"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[15541],{63329:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u7b2c\u4e00\u7ae0",content:(0,s.jsx)(s.Fragment,{children:"\u5f64\u4e91\u56db\u5408\uff0c\u6714\u98ce\u6012\u543c\uff01 \u3000\u3000\u662f\u5c81\u672b\uff0c\u4fdd\u5b9a\u57ce\u51fa\u5947\u7684\u51b7\uff0c\u8fde\u57ce\u5916\u90a3\u4e00\u9053\u62a4\u57ce\u6cb3\uff0c\u90fd\u7ed3\u4e86\u5c42\u539a\u539a\u7684\u51b0\uff0c\u539a\u5f97\u4f60\u751a\u81f3\u53ef\u4ee5 \u6beb\u4e0d\u8d39\u4e8b\u5730\u8d76\u7740\u5927\u8f66\u4ece\u4e0a\u9762\u9a76\u8fc7\u53bb\u3002 \u3000\u3000\u96ea\u505c\u4e86\uff0c\u4f46\u662f\u66ae\u8272\u5374\u4e3a\u5927\u5730\u5e26\u6765\u4e86\u66f4\u5927\u7684\u5bd2\u51b7\uff0c\u5929\u4e0a\u5f53\u7136\u6ca1\u6709\u661f\uff0c\u66f4\u4e0d\u4f1a\u6709\u6708\u4e86\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u5927\u5730\u663e\u5f97\u683c\u5916\u5730\u9ed1\u6697\uff0c\u5c31\u8fde\u96ea\uff0c\u4f60\u770b\u4e0a\u53bb\u90fd\u662f\u8ff7\u8499\u7684\u7070\u9ed1\u8272\u3002 \u3000\u3000\u4fdd\u5b9a\u57ce\u91cc\uff0c\u884c\u4eba\u4e5f\u8fd8\u4e0d\u5982\u5f80\u5e38\u5730\u591a\uff0c\u9664\u4e86\u8fbe\u5b98\u8d35\u4eba\u7684\u8c6a\u534e\u5927\u8f7f\u5916\uff0c\u8c01\u80af\u5192\u7740\u8fd9\u4e48\u5927\u7684\u5bd2 \u51b7\u5728\u8857\u4e0a\u8d70\uff0c\u5c31\u662f\u6709\u51e0\u8f86\u5927\u8f66\uff0c\u8f66\u4e0a\u7684\u5e18\u5e03\u4e5f\u662f\u653e\u5f97\u4e25\u4e25\u7684\uff0c\u53ea\u5269\u4e0b\u8d76\u8f66\u7684\u8f66\u628a\u5f0f\uff0c\u7f29\u7740\u5934 \u98a4\u6296\u5728\u51db\u51bd\u7684\u897f\u5317\u98ce\u91cc\uff0c\u5583\u5583\u5730\u62b1\u6028\u7740\u5929\u6c14\u7684\u5bd2\u51b7\u3002 \u3000\u3000\u4f46\u662f\u901a\u5f80\u5357\u57ce\u7684\u5357\u5927\u8857\u4e0a\uff0c\u6b64\u65f6\u7a81\u7136\u9a91\u6765\u4e00\u5339\u5168\u9ed1\u8272\u7684\u5065\u9a6c\uff0c\u9a6c\u4e0a\u662f\u4e2a\u5634\u4e0a\u5fae\u5fae\u7559\u7740\u4e9b \u77ed\u9aed\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u5934\u4e0a\u6234\u7740\u9876\u5173\u5916\u5e38\u89c1\uff0c\u6b64\u5730\u5374\u662f\u7f55\u89c1\u7684\u76ae\u5e3d\uff0c\u8fde\u8033\u6735\u90fd\u76d6\u4f4f\u4e86\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u4f60\u6839\u672c\u65e0\u6cd5\u5728\u8fd9\u79cd\u5149\u7ebf\u4e0b\u770b\u51fa\u4ed6\u7684\u9762\u5bb9\uff0c\u53ea\u89c9\u5f97\u4ed6\u5728\u8fd9\u4e48\u51b7\u7684\u5929\u6c14\u91cc\uff0c\u5750\u5728\u9a6c\u4e0a \u7684\u8eab\u8eaf\u4ecd\u662f\u633a\u5f97\u76f4\u76f4\u7684\uff0c\u4eff\u4f5b\u5bf9\u8fd9\u79cd\u523a\u9aa8\u5bd2\u51b7\uff0c\u5e76\u4e0d\u5927\u4ecb\u610f\u3002 \u3000\u3000\u8857\u65c1\u6709\u5bb6\u5e76\u4e0d\u592a\u5927\u7684\u9152\u94fa\uff0c\u6b64\u523b\u5374\u662f\u9ad8\u670b\u6ee1\u5ea7\u3002\u6709\u4e2a\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\uff0c\u7a81\u7136\u4ece\u91cc\u9762\u8d70\u4e86 \u51fa\u6765\uff0c\u88ab\u95e8\u5916\u7684\u51e4\u4e00\u5439\uff0c\u673a\u4f36\u4f36\u6253\u4e86\u5bd2\u6218\uff0c\u62b1\u6028\u7740\u8bf4\uff1a\u201c\u597d\u51b7\uff01\u201d\u9000\u4e86\u4e24\u6b65\u5c06\u8eab\u5b50\u7559\u5728\u95e8 \u91cc\uff0c\u4f38\u5934\u5728\u5916\u9762\uff0c\u201c\u5478\u201d\u5730\u4e00\u58f0\uff0c\u5410\u4e86\u56de\u6d53\u6d53\u7684\u75f0\u3002 \u3000\u3000\u4e00\u62ac\u5934\uff0c\u5374\u6b63\u597d\u671b\u89c1\u9a6c\u4e0a\u7684\u5947\u602a\u6c49\u5b50\uff0c\u7709\u6bdb\u5fae\u5fae\u4e00\u76b1\uff0c\u6697\u81ea\u4f4e\u8bed\u9053\uff1a\u201c\u5947\u602a\uff0c\u4ed6\u600e\u5730\u4f1a \u5728\u8fd9\u91cc\uff1f\u201d\u5934\u4e00\u7f29\uff0c\u53c8\u94bb\u56de\u95e8\u91cc\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u6c49\u5b50\u7f13\u7f13\u653e\u7740\u9a6c\uff0c\u4eff\u4f5b\u6ca1\u6709\u770b\u5230\u8fd9\u4e2a\u4eba\uff0c\u624b\u4e00\u6309\uff0c\u5c06\u6234\u7740\u7684\u76ae\u5e3d\u6309\u5f97\u66f4\u4f4e\u4e86\u4e9b\u3002 \u3000\u3000\u9152\u4f2a\u9999\u6c14\uff0c\u4ece\u539a\u91cd\u7684\u68c9\u95e8\u5e18\u91cc\u900f\u4e86\u51fa\u6765\uff0c\u9a6c\u4e0a\u7684\u4eba\u95fb\u89c1\u4e86\u8fd9\u79cd\u6c14\u5473\uff0c\u5634\u4e00\u62bf\uff0c\u50cf\u662f\u6781\u529b \u5730\u538b\u5236\u4f4f\u60f3\u8fdb\u53bb\u559d\u4e24\u676f\u7684\u6b32\u671b\u3002 \u3000\u3000\u9a6c\u8e44\u6572\u5728\u5df2\u7ecf\u7ed3\u51b0\u5757\u7684\u96ea\u4e0a\uff0c\u53d1\u51fa\u4e00\u79cd\u975e\u5e38\u60a6\u8033\u7684\u94ee\u94ee\u4e4b\u58f0\uff0c\u50cf\u662f\u91d1\u5668\u76f8\u51fb\u65f6\u6240\u53d1\u51fa\u7684 \u90a3\u79cd\u7279\u522b\u7684\u58f0\u97f3\u3002 \u3000\u3000\u9a6c\u4e5f\u662f\u5339\u9a8f\u9a6c\uff0c\u8fd9\u4e00\u5bf9\u9a6c\u548c\u4eba\uff0c\u8ba9\u4eba\u770b\u8d77\u6765\uff0c\u90fd\u6709\u4e00\u79cd\u96c4\u58ee\u7684\u611f\u89c9\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u5e26\u7740\u90a3\u79cd\u60a6\u8033\u7684\u94ee\u94ee\u4e4b\u58f0\uff0c\u8fd9\u4e00\u5bf9\u9a6c\u548c\u4eba\u9010\u6e10\u8fdc\u53bb\u3002 \u3000\u3000\u7ed5\u8fc7\u6587\u5e99\uff0c\u5c31\u662f\u5357\u95e8\u3002\u5b88\u95e8\u7684\u5352\u5b50\u501a\u7740\u7ea2\u7f28\u67aa\u7ad9\u5728\u57ce\u5185\u907f\u98ce\u7684\u9634\u5f71\u91cc\uff0c\u4e5f\u770b\u5230\u8fd9\u4e00\u4eba\u4e00 \u9a6c\u7f13\u7f13\u9a91\u51fa\u57ce\u53bb\uff0c\u770b\u7740\u9a6c\u4e0a\u7684\u9a91\u58eb\u7684\u82f1\u59ff\uff0c\u4e0d\u7981\u4f4e\u5934\u8d5e\u7f8e\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u53ef\u771f\u68d2\uff01\u201d \u3000\u3000\u9a6c\u51fa\u4e86\u57ce\uff0c\u5c31\u8d70\u5f97\u7a0d\u5fae\u5feb\u4e86\u4e9b\uff0c\u4f46\u662f\u4ecd\u4e0d\u662f\u4e00\u4e2a\u5728\u8fd9\u79cd\u5929\u6c14\u91cc\u8d76\u8def\u7684\u4eba\u5e94\u6709\u7684\u901f\u5ea6\uff0c\u6cbf \u7740\u6b63\u5b9a\u7684\u5927\u8def\u4e0a\u8d70\u4e86\u4e00\u6bb5\uff0c\u9a6c\u7adf\u505c\u4e86\u4e0b\u6765\uff0c\u5728\u4e00\u682a\u67af\u4e86\u7684\u8001\u6768\u6811\u4e0b\u5fae\u5fae\u8e22\u7740\u811a\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u4f3c\u4e4e\u82e5\u6709\u6240\u5f85\uff0c\u9762\u4e0a\u7684\u795e\u8272\uff0c\u9634\u6c89\u5f97\u5f88\u3002 \u3000\u3000\u5728\u4ed6\u6765\u8bf4\uff0c\u65f6\u95f4\u4eff\u4f5b\u8fc7\u5f97\u7279\u522b\u6162\uff0c\u9634\u6c89\u7684\u8138\u4e0a\uff0c\u4e5f\u9732\u51fa\u4e86\u4e9b\u7126\u6025\uff0c\u4ed6\u8f7b\u8f7b\u7528\u9a6c\u97ad\u7684\u540e\u67c4 \u51fb\u7740\u624b\u638c\uff0c\u81ea\u8bed\u9053\uff1a\u201c\u600e\u5730\u8fd8\u4e0d\u6765\uff1f\u201d \u3000\u3000\u53c8\u8fc7\u4e86\u7247\u523b\uff0c\u4ed6\u7b49\u5f97\u4e0d\u8010\u70e6\uff0c\u53c8\u60f3\u5f80\u524d\u8d70\uff0c\u56db\u987e\u4e00\u773c\uff0c\u770b\u5230\u4ed6\u7acb\u9a6c\u6240\u5728\u5730\uff0c\u56db\u5468\u6e3a\u65e0\u4eba \u8ff9\uff0c\u60f3\u4e86\u60f3\uff0c\u53c8\u52d2\u4f4f\u9a6c\u7f30\uff0c\u6253\u6d88\u4e86\u8981\u5f80\u524d\u8d70\u7684\u5ff5\u5934\u3002 \u3000\u3000\u591c\u9759\u5f97\u6015\u4eba\uff0c\u53ea\u6709\u98ce\u522e\u7740\u67af\u6811\u679d\uff0c\u4e0d\u65f6\u5730\u53d1\u51fa\u90a3\u79cd\u201c\u5237\u5237\u201d\u7684\u58f0\u97f3\uff0c\u662f\u8fd9\u4e2a\u65e0\u661f\u65e0\u6708\u7684 \u5bd2\u591c\u91cc\u552f\u4e00\u8ba9\u4eba\u4eec\u542c\u5f97\u89c1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\u795e\u60c5\u8d8a\u53d1\u4e0d\u8010\uff0c\u8df3\u4e0b\u9a6c\uff0c\u4f0f\u5728\u5730\u4e0a\uff0c\u7528\u8033\u6735\u8d34\u7740\u6ee1\u662f\u51b0\u96ea\u7684\u5730\u9762\u542c\u4e86\u534a\u664c\uff0c \u7a81\u7136\u8138\u4e0a\u9732\u51fa\u559c\u8272\uff0c\u8dc3\u4e86\u8d77\u6765\uff0c\u51b0\u96ea\u6cbe\u5f97\u4ed6\u4e00\u8138\u90fd\u662f\uff0c\u4ed6\u4e5f\u4e0d\u5728\u4e4e\uff0c\u968f\u624b\u62b9\u53bb\u4e86\uff0c\u4e5f\u4e0d\u89c9\u5f97 \u51b7\u3002 \u3000\u3000\u4ed6\u638f\u51fa\u4e86\u4e00\u4e2a\u6781\u5927\u7684\u624b\u5e15\uff0c\u624b\u5e15\u662f\u767d\u8272\u7684\uff0c\u548c\u4ed6\u8eab\u4e0a\u7684\u8863\u670d\u6781\u4e0d\u76f8\u79f0\uff0c\u4f46\u662f\u4ed6\u5374\u5c06\u8fd9\u5757 \u624b\u5e15\u8499\u5728\u8138\u4e0a\uff0c\u53ea\u6709\u4e00\u53cc\u70af\u70af\u53d1\u7740\u4eae\u7684\u773c\u775b\u3002 \u3000\u3000\u5728\u76ae\u5e3d\u548c\u624b\u4e2d\u4e4b\u95f4\u7684\u7a7a\u9699\u91cc\uff0c\u5168\u795e\u51dd\u89c6\u7740\u8fdc\u65b9\u3002 \u3000\u3000\u6ca1\u6709\u591a\u4e45\uff0c\u5927\u8def\u4e0a\u679c\u7136\u4f20\u6765\u4e00\u9635\u6025\u907d\u7684\u8e44\u58f0\uff0c\u8001\u6c5f\u6e56\u4ece\u8fd9\u79cd\u8e44\u58f0\u91cc\uff0c\u7acb\u523b\u53ef\u4ee5\u5224\u65ad\u5f97 \u51fa\uff0c\u8fd9\u4e00\u5b9a\u662f\u6709\u4eba\u56e0\u7740\u6025\u4e8b\u5728\u8def\u4e0a\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u8d76\u8def\uff0c\u800c\u4e14\u8d76\u8def\u7684\u4eba\u8fd8\u4e0d\u6b62\u4e00\u4e2a\u3002 \u3000\u3000\u8e44\u58f0\u8d8a\u5b8b\u8d8a\u8fd1\uff0c\u8fd9\u4e2a\u4ee5\u624b\u5e15\u8499\u6614\u8138\u7684\u6c49\u5b50\u7acb\u523b\u4ee5\u6700\u654f\u6377\u7684\u8eab\u6cd5\u53c8\u8df3\u4e0b\u9a6c\uff0c\u5c06\u7edc\u5fae\u5fae\u5411\u5de6 \u4e00\u5e26\uff0c\u662f\u4ee5\u9a6c\u8eab\u4fbf\u6070\u597d\u6a2a\u5728\u8def\u4e0a\u3002 \u3000\u3000\u8def\u7684\u90a3\u8fb9\uff0c\u98de\u5feb\u5730\u5f1b\u6765\u4e24\u5339\u5065\u9a6c\uff0c\u8fd9\u4e48\u51b7\u7684\u5929\uff0c\u5934\u4e0a\u8fd8\u4e0d\u65ad\u5192\u7740\u70ed\u6c14\uff0c\u9a6c\u4e0a\u7684\u4eba\u4e00\u8272\u9752 \u5e03\u77ed\u8884\uff0c\u5916\u9762\u7f69\u7740\u4e00\u4ef6\u98ce\u6c05\uff0c\u8fd9\u662f\u5f53\u65f6\u8d76\u8def\u7684\u65c5\u4eba\u6700\u5e38\u89c1\u7684\u6253\u626e\uff0c\u539f\u672c\u4e00\u70b9\u4e5f\u5f15\u4e0d\u8d77\u522b\u4eba\u5bb6 \u7684\u6ce8\u610f\uff0c\u53ea\u662f\u9a6c\u4e0a\u7684\u8fd9\u4e24\u4eba\u4ff1\u662f\u4e00\u8138\u7684\u7cbe\u660e\u4e4b\u8272\uff0c\u4e24\u53cc\u773c\u775b\u4e5f\u90fd\u662f\u70af\u70af\u6709\u795e\uff0c\u8ba9\u4eba\u89c1\u4e86\uff0c\u6709 \u4e00\u79cd\u4e0d\u51e1\u7684\u611f\u89c9\u7f62\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u5339\u98de\u5954\u7740\u7684\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u8fdc\u8fdc\u4e5f\u770b\u5230\u6709\u4e00\u5339\u9a6c\u6a2a\u5728\u8def\u4e0a\uff0c\u5176\u4e2d\u6709\u4e00\u4eba\u9894\u4e0b\u5df2\u6709\u5fae \u987b\uff0c\u5e74\u9f84\u4eff\u4f5b\u751a\u5927\uff0c\u89c1\u72b6\u76b1\u7709\u9053\uff1a\u201c\u524d\u9762\u7684\u50cf\u5c31\u662f\u90a3\u6d3b\u513f\uff1f\u201d\u8bed\u97f3\u4e2d\u6cb3\u5357\u5473\u6781\u91cd\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u6211\u4eec\u5c06\u9a6c\u653e\u6162\u4e00\u4e9b\u5427\u3002\u201d\u4f46\u662f\u4e3a\u4e86\u7231\u60dc\u9a6c\uff0c\u8fd9\u4e24\u4eba\u90fd\u4e0d\u80af\u592a\u7528\u529b\u5730\u53bb\u52d2\u7586 \u7ef3\uff0c\u8ba9\u9a6c\u53c8\u8dd1\u4e86\u4e00\u6bb5\u3002 \u3000\u3000\u8fd9\u6837\uff0c\u8fd9\u4e24\u5339\u9a6c\u505c\u7684\u65f6\u5019\uff0c\u8ddd\u79bb\u90a3\u8499\u9762\u7684\u9a91\u58eb\uff0c\u5df2\u7ecf\u6ca1\u6709\u591a\u8fdc\u4e86\u3002 \u3000\u3000\u5e74\u957f\u7684\u6c49\u5b50\u89c1\u4e86\u8fd9\u8499\u7740\u8138\u7684\u9a91\u58eb\uff0c\u8138\u4e0a\u795e\u60c5\u731b\u53d8\uff0c\u5fc3\u5934\u4e5f\u5728\u7830\u7136\u6253\u9f13\uff0c\u4f46\u662f\u4ed6\u95ef\u8361\u6c5f\u6e56 \u591a\u5e74\uff0c\u5728\u5200\u53e3\u4e0a\u7ffb\u6eda\u7684\u6b21\u6570\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\uff0c\u6b64\u65f6\u867d\u7136\u6709\u4e9b\u60ca\u5f02\uff0c\u4f46\u8fd8\u662f\u4ece\u5bb9\u4ed6\u8bf4\u9053\uff1a\u201c\u8001 \u54e5\u501f\u5149\uff0c\u8ba9\u4e2a\u9053\u7ed9\u6211\u4eec\u8d70\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u5f97\u5ba2\u6c14\u5f97\u5f88\u3002 \u3000\u3000\u8499\u9762\u9a91\u58eb\u7684\u773c\u775b\u77ac\u4e5f\u4e0d\u77ac\u5730\u77aa\u7740\u4ed6\uff0c\u4e00\u77ac\u95f4\uff0c\u7a7a\u6c14\u50cf\u662f\u51dd\u7ed3\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u5339\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u83ab\u6d4b\u9ad8\u6df1\uff0c\u53c8\u5fc3\u6000\u754f\u60e7\uff0c\u6115\u7136\u671b\u7740\u4ed6\u3002 \u3000\u3000\u8499\u9762\u9a91\u58eb\u7684\u7b11\u58f0\u53c8\u662f\u90a3\u4e48\u7a81\u7136\u5730\u987f\u4f4f\u4e86\uff0c\u6362\u4e86\u4e00\u79cd\u6beb\u65e0\u7b11\u610f\u7684\u58f0\u97f3\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d \u6562\u60c5\u5c31\u662f\u4e24\u6cb3\u95fb\u540d\u7684\u2018\u67aa\u5251\u65e0\u6545\u2019\u88f4\u6c0f\u53cc\u6770\u5427\uff1f\u201d\u8bf4\u8bdd\u7684\u6001\u5ea6\u91cc\uff0c\u6ee1\u662f\u6311\u8845\u7684\u610f\u5473\u3002 \u3000\u3000\u90a3\u8f83\u4e3a\u5e74\u957f\u7684\u4e00\u4e2a\u8003\u8651\u4e86\u534a\u664c\uff0c\u65b9\u60f3\u7b54\u8bdd\uff0c\u90a3\u5e74\u8f7b\u7684\u4e00\u4e2a\u5df2\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u597d\u5389\u5bb3\u7684\u773c \u5149\uff0c\u4e0d\u9519\uff0c\u5728\u4e0b\u5c31\u662f\u5f27\u5f62\u5251\u88f4\u5143\uff0c\u8fd9\u5c31\u662f\u5bb6\u5144\u94a9\u9570\u67aa\u88f4\u626c\u3002\u201d\u4ed6\u51b7\u7b11\u6570\u58f0\uff0c\u53c8\u9053\uff0c\u201c\u670b\u53cb\u6df1 \u591c\u5728\u6b64\u76f8\u5019\uff0c\u83ab\u975e\u5bf9\u6211\u5144\u5f1f\u6709\u4ec0\u4e48\u6307\u6559\u5417\uff1f\u201d \u3000\u3000\u8499\u9762\u9a91\u58eb\u6717\u58f0\u7b11\u9053\uff1a\u201c\u6211\u542c\u8bf4\u88f4\u4e8c\u4fa0\u6027\u60c5\u8c6a\u723d\uff0c\u5982\u4eca\u4e00\u89c1\uff0c\u679c\u7136\u662f\u5feb\u4eba\u5feb\u8bed\u3002\u201d\u4ed6\u7b11\u58f0 \u4e00\u4f4f\uff0c\u968f\u5373\u53c8\u662f\u4e00\u526f\u51b7\u51b7\u7684\u795e\u60c5\uff0c\u4f60\u867d\u7136\u770b\u4e0d\u900f\u5728\u4ed6\u624b\u5e15\u540e\u8138\u4e0a\u808c\u8089\u7684\u53d8\u5316\uff0c\u4f46\u662f\u4ece\u4ed6\u7684\u76ee \u5149\u91cc\uff0c\u4f60\u4ecd\u53ef\u4ee5\u53d1\u73b0\u4ed6\u7684\u8fd9\u79cd\u6151\u4eba\u7684\u5bd2\u610f\u3002 \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5728\u660e\u4eba\u9762\u524d\uff0c\u4e5f\u4e0d\u5fc5\u8bf4\u6697\u8bdd\uff0c\u4eca\u65e5\u6765\u6b64\uff0c\u6211\u4e5f\u6ca1\u6709\u4ec0\u4e48\u522b\u7684 \u7528\u610f\uff0c\u53ea\u4e0d\u8fc7\u662f\u60f3\u5411\u4e24\u4f4d\u8ba8\u4e00\u6837\u4e1c\u897f\u7f62\u4e86\u3002\u201d \u3000\u3000\u201c\u8981\u5411\u6211\u5144\u5f1f\u8981\u4e1c\u897f\uff0c\u8fd8\u4e0d\u7b80\u5355\u5f97\u5f88\u3002\u201d\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u51b7\u7b11\u9053\uff0c\u201c\u53ea\u8981\u670b\u53cb\u4e5f\u8be5\u4eae\u4e2a \u4e07\u513f\uff0c\u8981\u77e5\u9053\uff0c\u6211\u5144\u5f1f\u7684\u4e1c\u897f\uff0c\u4e0d\u662f\u968f\u4fbf\u8981\u5f97\u7684\u5462\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u53ef\u8bf4\u5f97\u6781\u4e3a\u4e0d\u5ba2\u6c14\uff0c\u50cf\u662f\u65e9\u5df2\u77e5\u9053\u8fd9\u8499\u9762\u9a91\u58eb\u5bf9\u81ea\u5df1\u975e\u4f46\u7edd\u65e0\u597d\u610f\uff0c\u800c\u4e14\u8fd8\u6709\u7740\u6781 \u574f\u7684\u56fe\u8c0b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fd9\u79cd\u4e0d\u5ba2\u6c14\u7684\u6001\u5ea6\uff0c\u5e76\u6ca1\u6709\u5f15\u8d77\u8499\u9762\u9a91\u58eb\u7684\u66b4\u6012\u3002 \u3000\u3000\u4ed6\u53cd\u800c\u7b11\u9053\uff1a\u201c\u6211\u8981\u7684\u4e0d\u662f\u522b\u7684\u3002\u201d\u4ed6\u7528\u624b\u5c06\u9762\u4e0a\u7684\u9762\u5e15\u66f4\u5411\u4e0a\u63d0\u4e86\u63d0\uff0c\u8bf4\uff1a\u201c\u5c31\u662f\u8d35 \u5144\u5f1f\u5934\u4e0a\u7684\u8111\u888b\uff0c\u548c\u4e24\u4f4d\u6000\u4e2d\u7684\u73a9\u610f\u3002\u2018\u201d\u5f27\u5f62\u5251\u201c\u88f4\u5143\u6012\u6781\u800c\u7b11\uff0c\u7b11\u58f0\u9ad8\u4ea2\u4eba\u4e91\uff0c\u663e\u89c1\u5f97 \u5185\u529f\u4e0d\u4f46\u5df2\u6709\u706b\u5019\uff0c\u800c\u4e14\u5df2\u53ef\u7b97\u662f\u767b\u5802\u4eba\u5ba4\u4e86\u3002\u90a3\u8499\u9762\u4eba\u7684\u773c\u775b\u77ac\u4e5f\u4e0d\u77ac\u5730\u76ef\u5728\u4ed6\u8138\u4e0a\uff0c\u51b7 \u51b7\u8bf4\u9053\uff1a\u201c\u88f4\u4e8c\u4fa0\u4e3a\u7740\u4ec0\u4e48\u4e8b\u8fd9\u6837\u597d\u7b11\uff1f\u201d \u3000\u3000\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u7b11\u58f0\u987f\u4f4f\uff0c\u9053\uff1a\u201c\u6211\u88f4\u5bb6\u5144\u5f1f\u51fa\u6765\u95ef\u8361\u6c5f\u6e56\u5341\u4f59\u5e74\uff0c\u8fd8\u6ca1\u6709\u4eba\u6562\u5728\u6211\u54e5 \u513f\u4fe9\u9762\u524d\u8bf4\u8fc7\u96be\u542c\u7684\u8bdd\u3002\u670b\u53cb\uff0c\u4f60\u51ed\u7740\u4e9b\u4ec0\u4e48\uff0c\u5c31\u6562\u5728\u6211\u54e5\u513f\u4fe9\u9762\u524d\u8fd9\u6837\u5356\u72c2\uff0c\u4f60\u6562\u60c5\u4e5f\u662f \u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u5427\uff1f\u201d \u3000\u3000\u94a9\u9570\u67aa\u88f4\u626c\u867d\u662f\u6db5\u517b\u529f\u6df1\uff0c\u6b64\u65f6\u4e5f\u4e0d\u89c9\u5fae\u5fae\u6709\u4e9b\u6012\u706b\u4e86\uff0c\u5389\u559d\u9053\uff1a\u201c\u670b\u53cb\uff01\u54b1\u4eec\u5e9f\u8bdd\u5c11 \u8bf4\uff0c\u8fd8\u662f\u624b\u5e95\u4e0b\u89c1\u4e2a\u771f\u7ae0\u5427\u3002\u201d \u3000\u3000\u90a3\u8499\u9762\u4eba\u4ef0\u5929\u800c\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u88f4\u6c0f\u53cc\u6770\u679c\u7136\u90fd\u662f\u597d\u6c49\uff0c\u5144\u5f1f\u4eca\u5929\u82e5\u4e0d\u6210\u5168\u4f60\u4eec\uff0c \u4ece\u6b64\u6b66\u6797\u4e2d\u5c31\u662f\u6ca1\u6709\u5144\u5f1f\u8fd9\u53f7\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u91cd\u91cd\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u50cf\u9601\u4e0b\u8fd9\u53f7\u85cf\u5934\u9732\u5c3e\u7684\u9f20\u8f88\u4e4b\u80fd\u79f0\u5f97\u4e0a\u662f \u2018\u4eba\u7269\u2019\u7684\u8bdd\uff0c\u90a3\u6b66\u6797\u4e2d\u7684\u4eba\u7269\uff0c\u4e5f\u672a\u514d\u592a\u591a\u4e86\u4e9b\u5427\uff01\u201d\u8a00\u4e0b\u5927\u6709\u4f60\u6839\u672c\u4e0d\u662f\u4eba\u7269\uff0c\u8fd8\u8bf4\u4ec0 \u4e48\u4ee5\u540e\u4e0d\u4ee5\u540e\u5462\uff01 \u3000\u3000\u90a3\u8499\u9762\u4eba\u7684\u773c\u775b\u500f\u7136\u5c04\u51fa\u51f6\u5149\uff0c\u4e00\u8bed\u672a\u53d1\uff0c\u53cc\u817f\u5fae\u5939\u9a6c\u8eab\uff0c\u90a3\u9a6c\u4fbf\u7f13\u7f13\u8d70\u5230\u8def\u8fb9\u7684\u8352\u5730 \u4e0a\u3002 \u3000\u3000\u7136\u540e\u4ed6\u56de\u8f6c\u8eab\uff0c\u51b7\u7136\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u8fc7\u6765\u5427\uff0c\u8fd9\u91cc\u6e05\u6e05\u9759\u9759\uff0c\u7528\u6765\u505a\u4e24\u4f4d\u7684\u846c\u8eab\u4e4b\u5730\uff0c\u90a3 \u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u79cd\u8bed\u6c14\uff0c\u5c31\u662f\u6839\u672c\u5c06\u8fd9\u4e24\u6cb3\u95fb\u540d\u7684\u3002\u67aa\u5251\u53cc\u7edd\u3001\u770b\u5f97\u4e00\u6587\u4e0d\u503c\uff0c\u8ba4\u4e3a\u4ed6\u4eec\u7b80\u76f4\u6ca1\u6709 \u4e00\u4e1d\u80fd\u80dc\u5f97\u4e86\u81ea\u5df1\u7684\u5e0c\u671b\u3002 \u3000\u3000\u88f4\u6c0f\u53cc\u6770\u4e45\u8d70\u6c5f\u6e56\uff0c\u6b64\u65f6\u867d\u662f\u6012\u706b\u9ad8\u6da8\uff0c\u4f46\u89c1\u4e86\u8fd9\u4eba\u8fd9\u79cd\u8d85\u4eba\u7684\u81ea\u4fe1\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u7981\u5fae\u5fae \u6253\u9f13\uff0c\u77e5\u9053\u6b64\u4eba\u51b3\u975e\u5584\u4e0e\u4e4b\u8f88\uff0c\u4f46\u4e8b\u60c5\u5df2\u53d1\u5c55\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u81ea\u5df1\u53c8\u600e\u80fd\u8bf4\u51fa\u4e86\u4e0d\u7b97\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u91cc\u90fd\u63d0\u9ad8\u4e86\u8b66\u89c9\uff0c\u53cc\u53cc\u4e00\u5e26\u9a6c\uff0c\u4e5f\u76f8\u7ee7\u8d70\u5230\u90a3\u7247\u8352\u5730\u4e4b\u4e0a\u3002 \u3000\u3000\u56db\u91ce\u82cd\u832b\uff0c\u4ed6\u4eec\u5f7c\u6b64\u90fd\u4e0d\u80fd\u770b\u5230\u5bf9\u65b9\u7684\u8138\u8272\uff0c\u5bd2\u6c14\u4fb5\u4eba\uff0c\u4e09\u5339\u9a6c\u51bb\u5f97\u6709\u4e9b\u4e0d\u8010\uff0c\u4e0d\u5b89\u5730 \u8e22\u97f3\u817f\uff0c\u53d1\u7740\u4f4e\u4f4e\u7684\u5636\u9e23\u3002 \u3000\u3000\u90a3\u8c6a\u9762\u4eba\u5237\u5730\u7ffb\u8eab\u843d\u4e86\u9a6c\uff0c\u8fd9\u4efd\u8f7b\u7075\u548c\u654f\u6377\uff0c\u4f7f\u5f97\u88f4\u6c0f\u53cc\u6770\u4e5f\u4e0d\u7981\u6697\u8d5e\u4e00\u58f0\uff1a\u201c\u597d\u8eab \u624b\uff01\u201d \u3000\u3000\u56e0\u4e3a\u4f60\u751a\u81f3\u65e0\u6cd5\u770b\u6e05\u4ed6\u4ece\u9a6c\u4e0a\u4e0b\u6765\u65f6\u6240\u7528\u7684\u662f\u54ea\u4e00\u79cd\u8eab\u6cd5\uff0c\u53ea\u89c9\u5f97\u4ed6\u672c\u662f\u5750\u5728\u9a6c\u4e0a\u7684\u8eab \u8eaf\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5df2\u7ad9\u5728\u5730\u4e0a\u4e86\u3002\uff5e\u2026 \u3000\u3000\u201c\u94a9\u9570\u67aa\u201d\u7a81\u7136\u53d1\u8bdd\u9053\uff1a\u201c\u670b\u53cb\u7aef\u7684\u597d\u8eab\u624b\uff0c\u6211\u59d3\u88f4\u7684\u8d70\u904d\u5927\u6c5f\u5357\u5317\uff0c\u53ef\u662f\u50cf\u670b\u53cb\u8fd9\u6837 \u7684\u8eab\u624b\uff0c\u6211\u59d3\u88f4\u7684\u5012\u771f\u8fd8\u5c11\u89c1\uff0c\u60f3\u5fc5\u670b\u53cb\u4e5f\u662f\u6b66\u6797\u4e2d\u6210\u540d\u7acb\u4e07\u7684\u597d\u6c49\uff0c\u6211\u59d3\u88f4\u7684\u8fd9\u6b21\u4fdd\u7684 \u9556\uff0c\u670b\u53cb\u65e2\u7136\u77e5\u9053\u4e86\uff0c\u4e5f\u8be5\u77e5\u9053\u672a\u8def\uff0c\u6211\u59d3\u88f4\u7684\u4e07\u4e07\u62c5\u4e0d\u8d77\u8fd9\u4e2a\u8d23\u4efb\uff0c\u670b\u53cb\u82e5\u770b\u5f97\u8d77\u6211\u59d3\u88f4 \u7684\uff0c\u4eae\u4e2a\u4e07\u513f\uff0c\u9ad8\u9ad8\u624b\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u65e5\u540e\u6211\u59d3\u88f4\u7684\u603b\u6709\u62a5\u7b54\u670b\u53cb\u4e4b\u5904\uff01\u201d \u3000\u3000\u4ed6\u8bed\u6c14\u4e2d\u5df2\u6709\u660e\u663e\u5730\u9732\u51fa\u602f\u654c\u4e4b\u610f\uff0c\u8fd9\u5012\u4e0d\u662f\u8bf4\u4ed6\u662f\u4e2a\u61e6\u4eba\uff0c\u4e16\u4e0a\u7684\u4eba\uff0c\u53c8\u6709\u8c01\u4e0d\u660e\u4e0d \u81ea\u5730\u4ee5\u751f\u547d\u4f5c\u8d4c\u6ce8\u6765\u548c\u4eba\u5bb6\u6bd4\u8bd5\uff0c\u800c\u6b66\u6797\u4e2d\u7684\u89c4\u77e9\uff0c\u53cc\u65b9\u5728\u4ea4\u624b\u4e4b\u524d\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u8be5\u4eae\u4e2a\u4e07 \u513f\u3002 \u3000\u3000\u4f46\u662f\u90a3\u8499\u9762\u4eba\u5374\u50cf\u662f\u5b8c\u5168\u4e0d\u61c2\u8fd9\u4e00\u5957\uff0c\u4e24\u6761\u817f\u4e0d\u4e01\u4e0d\u516b\uff0c\u6c14\u5b9a\u795e\u51dd\u5730\u7ad9\u5728\u96ea\u5730\u4e0a\uff0c\u50cf\u662f \u8c01\u4e5f\u65e0\u6cd5\u6765\u64bc\u52a8\u4ed6\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u8fd9\u79cd\u9a84\u505a\u7684\u795e\u6001\uff0c\u4f7f\u5f97\u672c\u6765\u6027\u60c5\u5c31\u8f83\u66b4\u8e81\u7684\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u4ed6\u66b4\u559d \u9053\uff1a\u201c\u5927\u54e5\uff0c\u548c\u8fd9\u79cd\u9f20\u8f88\u5e9f\u8bdd\u5e72\u4ec0\u4e48\uff1f\u201d\u53cc\u817f\u79bb\u978d\uff0c\u4e5f\u98d8\u8eab\u4e0b\u4e86\u9a6c\uff0c\u968f\u624b\u4e00\u6325\uff0c\u90a3\u9a6c\u5c31\u5f90\u5f90 \u8e31\u4e86\u5f00\u53bb\uff0c\u8fdc\u8fdc\u5730\u505c\u4e0b\u4e86\uff0c\u663e\u89c1\u8fd9\u9a6c\u662f\u53d7\u8fc7\u8bad\u7ec3\u7684\u826f\u9a79\u3002 \u3000\u3000\u201c\u94a9\u9570\u67aa\u201d\u88f4\u626c\u6697\u6697\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u5bb6\u6027\u547d\uff0c\u5168\u5728\u6b64\u4e00\u6597\uff0c\u81ea\u5df1\u82e5\u662f\u80dc\u4e86\uff0c \u8fde\u5bf9\u65b9\u7684\u59d3\u540d\u90fd\u4e0d\u77e5\u9053\uff1a\u81ea\u5df1\u82e5\u7136\u8d25\u4e86\uff0c\u90a3\u4e48\u81ea\u5df1\u5144\u5f1f\u7684\u4e24\u6761\u547d\u5c31\u7b97\u5168\u846c\u9001\u5728\u8fd9\u4fdd\u5b9a\u9053\u4e0a \u4e86\u3002 \u3000\u3000\u8fd9\u662f\u5168\u7136\u4e0d\u516c\u5e73\u7684\uff0c\u4f46\u662f\u4ed6\u4e5f\u77e5\u9053\u522b\u65e0\u9009\u62e9\u7684\u4f59\u5730\uff0c\u4ee5\u88f4\u6c0f\u53cc\u6770\u7684\u8eab\u4efd\uff0c\u52bf\u4e0d\u80fd\u4e00\u9003\u4e86 \u4e4b\uff0c\u4f55\u51b5\u4e5f\u672a\u5fc5\u80fd\u9003\u5f97\u6389\u5462\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u4e5f\u53ea\u5f97\u4e0b\u4e86\u9a6c\uff0c\u51dd\u795e\u7ad9\u5728\u5730\u4e0a\uff0c\u8fd9\u65f6\u4e09\u4eba\u6240\u7acb\u7684\u5730\u4f4d\uff0c\u6210\u4e86\u4e00\u4e2a\u4e09\u89d2\u4e4b\u52bf\uff0c\u4e09\u4eba \u5168\u90fd\u51dd\u795e\u6212\u5907\u7740\uff0c\u8c01\u4e5f\u4e0d\u6562\u6709\u4e00\u4e1d\u758f\u5ffd\u3002 \u3000\u3000\u88f4\u626c\u884c\u8d70\u6c5f\u6e56\uff0c\u4e00\u751f\u8c28\u614e\uff0c\u6b64\u523b\u7edd\u4e0d\u5148\u53d1\u96be\uff0c\u800c\u4e14\u4ed6\u5144\u5f1f\u4e24\u4eba\u5df1\u6709\u9ed8\u5951\uff0c\u6b64\u65f6\u6b64\u5730\uff0c\u4ed6 \u4eec\u4e5f\u987e\u4e0d\u5f97\u4ec0\u4e48\u6c5f\u6e56\u9053\u4e49\uff0c\u51c6\u5907\u53ea\u8981\u5bf9\u65b9\u4e00\u53d1\u52a8\uff0c\u81ea\u5df1\u5c31\u8054\u624b\u800c\u653b\uff0c\u7edd\u4e0d\u5355\u6253\u72ec\u6597\u3002 \u3000\u3000\u90a3\u8499\u9762\u4eba\u773c\u73e0\u4e00\u8f6c\uff0c\u51b7\u58f0\u8bf4\u9053\uff1a\u201c\u8d35\u5144\u5f1f\u8fd8\u662f\u4e00\u8d77\u4e0a\u5427\uff0c\u7701\u5f97\u6211\u4e00\u4e2a\u4e2a\u5730\u52a8\u624b\u8d39\u4e8b\u3002\u201d \u3000\u3000\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u4e5f\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5f53\u7136\uff0c\u6211\u5144\u5f1f\u548c\u9f20\u8f88\u52a8\u624b\uff0c\u4ece\u6765\u4e0d\u8bb2\u6b66\u6797\u89c4\u77e9\uff0c\u56e0\u4e3a\u4f60 \u4e0d\u914d\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u72c2\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u4e0d\u914d\uff01\u201d\u7b11\u58f0\u672a\u4f4f\uff0c\u8eab\u5f62\u500f\u7136\u800c\u52a8\uff0c\u98d2\u7136\u88ad\u5411\u201c\u5f27\u5f62\u5251\u201d \u88f4\u5143\u3002 \u3000\u3000\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u731b\u7136\u65cb\u8eab\u9519\u6b65\uff0c\u54ea\u77e5\u8499\u9762\u4eba\u7a81\u7136\u4e00\u8f6c\u6298\uff0c\u6539\u53d8\u4e86\u65b9\u5411\uff0c\u8eab\u5f62\u95ea\u7535\u822c\u51fb\u5411 \u88f4\u626c\u3002 \u3000\u3000\u8fd9\u79cd\u8eab\u6cd5\u548c\u901f\u5ea6\u679c\u7136\u662f\u60ca\u4eba\u7684\uff0c\u5230\u4e86\u8fd9\u65f6\u5019\uff0c\u5404\u4eba\u529f\u592b\u7684\u6df1\u6d45\u7acb\u523b\u5c31\u53ef\u4ee5\u5224\u65ad\u5f97\u51fa\u6765 \u4e86\u3002 \u3000\u3000\u201c\u94a9\u9570\u67aa\u201d\u88f4\u626c\u4e0d\u6127\u4e3a\u5317\u65b9\u6b66\u6797\u5065\u8005\uff0c\u201c\u5012\u8e29\u4e03\u661f\u6b65\u201d\uff0c\u8eab\u5f62\u5982\u884c\u4e91\u6d41\u6c34\u822c\u6e9c\u4e86\u5f00\u53bb\uff0c \u624b\u8155\u4e00\u7ffb\uff0c\u5df2\u5c06\u4e00\u6761\u6676\u5149\u95ee\u70c1\u7684\u94a9\u9570\u67aa\u64a4\u5728\u624b\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\uff0c\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u4e5f\u81ea\u64a4\u51fa\u5175\u5203\uff0c\u5bd2\u5149\u4e00\u95ee\uff0c\u201c\u7acb\u5288\u534e\u5cb3\u201d\uff0c\u5212\u5411\u8499\u9762 \u4eba\u7684\u540e\u80cc\u3002 \u3000\u3000\u8499\u9762\u4eba\u53cc\u638c\u4e00\u9519\uff0c\u7684\u6e9c\u6e9c\u5730\u4e00\u8f6c\u8eab\uff0c\u88f4\u5143\u7684\u5f27\u5f62\u5251\u521a\u597d\u9012\u7a7a\uff0c\u53f3\u624b\u4e00\u622a\uff0c\u5de6\u6307\u5982\u5251\uff0c \u4e00\uff1a\u62db\u4e24\u5f0f\uff0c\u75be\u5982\u95ea\u7535\uff0c\u7aef\u5730\u60ca\u4eba\u3002 \u3000\u3000\u201c\u94a9\u9570\u67aa\u201d\u88f4\u626c\u5e72\u8155\u4e00\u6296\uff0c\u638c\u4e2d\u94a9\u9570\u67aa\u7adf\u5f53\u505a\u5927\u67aa\u4f7f\u5e26\u8d77\u7897\u5927\u7684\u67aa\u82b1\uff0c\u7adf\u65bd\u5c55\u51fa\u201c\u5cb3\u5bb6 \u67aa\u6cd5\u201d\u91cc\u7684\u715e\u624b\uff0c\u523a\u5411\u8499\u9762\u4eba\u8170\u4e0b\u7684\u201c\u7b11\u8170\u7a74\u201d\u3002 \u3000\u3000\u8499\u9762\u4eba\u6697\u81ea\u70b9\u5934\uff0c\u6697\u5fd6\u8fd9\u201c\u67aa\u5251\u65e0\u654c\u201d\u88f4\u6c0f\u53cc\u6770\u6b66\u529f\u7684\u786e\u4e0d\u5f31\uff0c\u987b\u77e5\u94a9\u9570\u67aa\u8fdc\u6bd4\u5927\u67aa \u77ed\uff0c\u5728\u88f4\u626c\u624b\u4e0a\u7adf\u80fd\u6296\u8d77\u7897\u5927\u7684\u67aa\u82b1\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u90a3\u8499\u9762\u4eba\u7109\u6709\u4e0d\u8bc6\u8d27\u7684\u9053\u7406\u3002 \u3000\u3000\u5f53\u4e0b\u4ed6\u4e5f\u4e0d\u6562\u592a\u8fc7\u8f7b\u654c\uff0c\u8f7b\u5578\u4e00\u58f0\uff0c\u8fd0\u638c\u5982\u51e4\uff0c\u5ffd\u53c8\u5316\u638c\u4e3a\u62f3\uff0c\u5316\u62f3\u4e3a\u722a\uff0c\u7adf\u5c06\u201c\u5c11 \u6797\u201d\u7684\u201c\u7f57\u6c49\u62f3\u201d\uff0c\u201c\u6b66\u5f53\u201d\u7684\u201c\u4e03\u5341\u4e8c\u5f0f\u64d2\u62ff\u624b\u201d\uff0c\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u4ee5\u53ca\u201c\u5ce8\u5e3d\u201d\u7684\u201c\u795e \u9e64\u638c\u201d\u8fd0\u7528\u5728\u4e00\u5904\u4e86\u3002 \u3000\u3000\u8fd9\u51e0\u8def\u62db\u5f0f\u672c\u662f\u6c5f\u6e56\u4e60\u89c1\u7684\uff0c\u4f46\u80fd\u5c06\u8fd9\u51e0\u8def\u62db\u5f0f\u878d\u800c\u4e3a\u4e00\u4f53\uff0c\u6c5f\u6e56\u4e2d\u5374\u7edd\u65e0\u4ec5\u6709\uff0c\u751a\u81f3 \u8fde\u542c\u90fd\u6d5a\u6709\u542c\u8fc7\u3002 \u3000\u3000\u8fd9\u8499\u9762\u4eba\u4e0d\u4f46\u80fd\u5c06\u8fd9\u51e0\u8def\u62db\u5f0f\u6eb6\u800c\u4e3a\u4e00\uff0c\u914d\u5408\u4f73\u5999\uff0c\u66f4\u662f\u5999\u5230\u6beb\u98a0\u3002\u88f4\u6c0f\u53cc\u6770\u79f0\u96c4\u4e24\u6cb3 \u591a\u5e74\uff0c\u638c\u4e2d\u7684\u4e24\u4ef6\u5916\u95e8\u5175\u5203\u6240\u7528\u7684\uff0c\u53c8\u90fd\u662f\u6b66\u6797\u7f55\u89c1\u7684\u62db\u5f0f\uff0c\u4f46\u5728\u8fd9\u8499\u9762\u4eba\u7684\u4e00\u53cc\u7a7a\u624b\u4e4b \u4e0b\uff0c\u975e\u4f46\u6ca1\u6709\u5360\u5230\u534a\u5206\u4fbf\u5b9c\uff0c\u800c\u4e14\u5e94\u4ed8\u5f97\u5f88\u5403\u529b\u3002 \u3000\u3000\u8499\u9762\u4eba\u638c\u98ce\u864e\u864e\uff0c\u6bcf\u51fa\u4e00\u62db\uff0c\u90fd\u662f\u5411\u81f4\u4eba\u6b7b\u547d\u4e4b\u5904\u4e0b\u624b\uff0c\u9ed1\u6697\u4e4b\u4e2d\u8ba4\u7a74\u4e4b\u51c6\uff0c\u65f6\u95f4\u62ff\u634f \u4e4b\u7a33\uff0c\u4e34\u654c\u7ecf\u9a8c\u4e4b\u4e30\uff0c\u5b9e\u5728\u90fd\u9a87\u4eba\u542c\u95fb\u3002\u88f4\u626c\u6697\u5fd6\uff1a\u201c\u6b66\u6797\u4e2d\uff0c\u54ea\u91cc\u51fa\u6765\u8fd9\u4e48\u4e2a\u597d\u624b\uff1f\u201d \u3000\u3000\u987b\u77e5\u88f4\u626c\u5728\u6c5f\u6e56\u4e2d\u4ea4\u6e38\u9887\u5e7f\uff0c\u6b66\u6797\u4e2d\u7684\u9ad8\u624b\uff0c\u4ed6\u4e5f\u5927\u90fd\u6709\u4e2a\u8ba4\u8bc6\uff0c\u662f\u4ee5\u4ed6\u5144\u5f1f\u201c\u5144\u5f1f\u9556 \u5c40\u201d\u8d70\u9556\u5341\u4f59\u5e74\uff0c\u4ece\u6765\u672a\u66fe\u5931\u98ce\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u8499\u9762\u4eba\u7684\u6765\u8def\uff0c\u9976\u662f\u88f4\u626c\u6781\u529b\u601d\u7d22\uff0c\u53ef\u4e5f\u731c\u60f3\u4e0d\u900f\u3002 \u3000\u3000\u4f9d\u8fd9\u8499\u800c\u4eba\u7684\u53e3\u97f3\uff0c\u8be5\u662f\u6cb3\u5317\u4e00\u5e26\u7684\u4eba\u7269\uff0c\u770b\u8fd9\u8499\u800c\u4eba\u7684\u8eab\u6cd5\uff0c\u5374\u53c8\u50cf\u8eab\u517c\u4e2d\u539f\u6b66\u6797\u51e0 \u5927\u5b97\u6d3e\u4e4b\u957f\u3002 \u3000\u3000\u88f4\u6c0f\u53cc\u6770\u6210\u540d\u4e8e\u4e24\u6cb3\uff0c\u4e24\u6cb3\u6b66\u6797\u4e2d\u7684\u9ad8\u624b\uff0c\u4ed6\u5144\u5f1f\u53ef\u8bf4\u662f\u4e86\u5982\u6307\u638c\uff0c\u53ef\u662f\u4ed6\u4eec\u5374\u4e5f\u65e0\u6cd5 \u63e3\u6d4b\u5f97\u5230\u8fd9\u8499\u9762\u4eba\u7a76\u7adf\u662f\u8c01\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u5fc3\u4e2d\u867d\u7136\u6781\u529b\u63e3\u6d4b\uff0c\u624b\u4e0b\u53ef\u4e0d\u6562\u6709\u534a\u4e1d\u758f\u5ffd\uff0c\u4ee5\u4ed6\u4e24\u4eba\u7684\u6b66\u529f\uff0c\u5408\u529b\u5c1a\u4e14\u4e0d\u884c\uff0c \u90a3\u8499\u9762\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u53ef\u4ee5\u60f3\u89c1\uff0c\u800c\u4ee5\u8fd9\u8499\u9762\u4eba\u7684\u5e74\u9f84\u548c\u6b66\u529f\uff0c\u5728\u6b66\u6797\u672c\u8be5\u4e45\u8d1f\u76db\u540d\uff0c\u4f46\u88f4\u6c0f \u53cc\u6770\u5374\u65e0\u6cd5\u731c\u7834\u4eba\u5bb6\u7684\u6765\u5386\uff0c\u5c82\u975e\u6709\u4e9b\u5947\u602a\u4e86\u5417\uff1f \u3000\u3000\u591c\u66f4\u6df1\uff0c\u98ce\u96ea\u53c8\u8d77\uff0c\u96ea\u82b1\u7eb7\u98de\uff0c\u90a3\u4e09\u5339\u9a6c\u51bb\u5f97\u53d1\u6296\uff0c\u53ef\u662f\u5374\u5e76\u672a\u8dd1\u8fdc\u3002 \u3000\u3000\u96ea\u82b1\u98d8\u5230\u4e09\u4eba\u52a8\u624b\u4e4b\u5904\uff0c\u88ab\u4e09\u4eba\u6240\u53d1\u51fa\u7684\u771f\u529b\u4e00\u6fc0\uff0c\u8fdc\u8fdc\u98d8\u4e86\u5f00\u53bb\uff0c\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u638c \u4e2d\u5175\u5203\u957f\u4e0d\u53ca\u4e09\u5c3a\uff0c\u5168\u662f\u8fdb\u624b\u62db\u6570\uff0c\u6b63\u662f\u5175\u7ecf\u6240\u8bf4\u7684\uff1a\u201c\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\u3002\u201d\u201c\u5f27\u5f62\u753b\u201d\u88f4 \u5143\u5fc3\u4e2d\u6124\u6012\uff0c\u62db\u62db\u6b3a\u8eab\u76f4\u5165\uff0c\u7b80\u76f4\u6709\u4e9b\u50cf\u662f\u5728\u62fc\u547d\u4e86\u3002 \u3000\u3000\u8499\u9762\u4eba\u867d\u7136\u5df2\u5360\u4e0a\u98ce\uff0c\u4f46\u4e00\u65f6\u534a\u523b\u4e4b\u95f4\uff0c\u5374\u4e5f\u65e0\u6cd5\u4f24\u5f97\u5bf9\u65b9\uff0c\u50cf\u662f\u6709\u4e9b\u4e0d\u8010\uff0c\u500f\u7136\u4e00\u58f0 \u6e05\u5578\uff0c\u8eab\u5f62\u98d8\u7136\u800c\u8d77\u3002 \u3000\u3000\u88f4\u6c0f\u53cc\u6770\u65b9\u81ea\u4e00\u60ca\uff0c\u90a3\u8499\u9762\u4eba\u5728\u7a7a\u4e2d\u7adf\u53d8\u4e86\u8eab\u5f62\uff0c\u5fae\u4e00\u8f6c\u6298\u95f4\uff0c\u5934\u4e0a\u811a\u4e0b\uff0c\u53cc\u638c\u5e26\u7740\u51cc \u5389\u800c\u60ca\u4eba\u7684\u98ce\u58f0\uff0c\u5288\u5411\u5f27\u5f62\u5251\u7684\u5934\u9876\u3002 \u3000\u3000\u4ed6\u8fd9\u79cd\u8eab\u6cd5\u4e00\u4f7f\u51fa\uff0c\u88f4\u6c0f\u53cc\u6770\u4e0d\u7981\u5927\u60ca\uff0c\u8131\u53e3\u800c\u547c\uff1a\u201c\u662f\u4f60\uff01\u201d\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u638c\u4e2d\u5175 \u5203\u7531\u4e0b\u800c\u4e0a\uff0c\u201c\u9738\u738b\u4e3e\u9f0e\u201d\uff0c\u8eab\u5f62\u659c\u8f6c\u3002 \u3000\u3000\u54ea\u77e5\u8499\u9762\u4eba\u7a81\u7136\u5728\u7a7a\u4e2d\u4e00\u632b\u8170\uff0c\u4e0a\u8eab\u731b\u7136\u5347\u8d77\u5c3a\u8bb8\uff0c\u5de6\u817f\u5374\u6a2a\u626b\u800c\u51fa\uff0c\u7740\u7740\u5b9e\u5b9e\u8e39\u5728\u94a9 \u9570\u67aa\u88f4\u626c\u80cc\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u7684\u5947\u8be1\u53d8\u5316\uff0c\u76f4\u662f\u532a\u5937\u6240\u601d\uff0c\u8fd9\u4e00\u811a\u7684\u529b\u9053\u4f55\u6b62\u5343\u65a4\uff0c\u88f4\u626c\u60e8\u547c\u4e00\u58f0\uff0c\u80f8\u53e3\u4e00 \u751c\uff0c\u9c9c\u8fce\u5c1a\u672a\u53ca\u55b7\u51fa\uff0c\u5df2\u7136\u6c14\u7edd\u4e86\u3002 \u3000\u3000\u8499\u9762\u4eba\u8eab\u5f62\u4e5f\u98d8\u843d\u4e0b\u6765\uff0c\u66fc\u5999\u5df2\u6781\uff0c\u5b64\u5f62\u5251\u88f4\u5143\u53cc\u76ee\u8d64\u7ea2\uff0c\u5389\u547c\u9053\uff1a\u201c\u6211\u5144\u5f1f\u548c\u4f60\u6709\u4ec0 \u4e48\u4ec7\u6028\uff1f\u4f60\u7adf\u4e0b\u4e86\u5982\u6b64\u8fa3\u624b\uff01\u201d \u3000\u3000\u8eab\u5f62\u5f62\u5982\u75af\u864e\uff0c\u671d\u8499\u9762\u4eba\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8499\u9762\u4eba\u5fae\u5fae\u51b7\u7b11\uff0c\u88f4\u6c0f\u53cc\u6770\u5df2\u53bb\u5176\u4e00\uff0c\u4ed6\u66f4\u662f\u80dc\u7b97\u5728\u63e1\uff0c\u88f4\u5143\u867d\u7136\u4e0d\u8981\u547d\u5730\u731b\u653b\uff0c\u4f46\u4ed6 \u6280\u9ad8\u4e00\u7740\u4ece\u5bb9\u5316\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u8fd9\u79cd\u62fc\u547d\u7684\u62db\u5f0f\uff0c\u6700\u662f\u8017\u8d39\u771f\u529b\uff0c\u4f55\u6d88\u5341\u6570\u4e2a\u7167\u9762\uff0c\u4ed6\u5df2\u7ecf\u6c14\u5598\u54bb\u54bb \u4e86\u3002 \u3000\u3000\u8499\u9762\u4eba\u6c14\u5b9a\u795e\u95f2\uff0c\u7a81\u7136\u53cc\u624b\u7ffc\u5f20\uff0c\u80f8\u524d\u7a7a\u95e8\u5927\u5f20\uff0c\u201c\u5f27\u5f62\u5251\u201d\u88f4\u5143\u53ef\u6ca1\u60f3\u5230\u4eba\u5bb6\u4e3a\u4ec0\u4e48 \u7a81\u7136\u5728\u8eab\u6cd5\u4e0a\u6709\u8fd9\u4e48\u5927\u7684\u7834\u7efd\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u662f\u5f53\u5c40\u8005\u8ff7\uff0c\u88f4\u5143\u6b3a\u8eab\u76f4\u8fdb\uff0c\u5f27\u5f62\u5251\u76f4\u523a\u8499\u9762\u4eba\u7684\u80f8\u8179\u3002 \u3000\u3000\u8499\u9762\u4eba\u957f\u7b11\u95f4\uff0c\u731b\u4e00\u5438\u6c14\uff0c\u80f8\u819b\u500f\u7136\u7f29\u540e\u5c3a\u8bb8\uff0c\u7adf\u662f\u5185\u5bb6\u767b\u5cf0\u9020\u6781\u7684\u529f\u592b\uff0c\u201c\u5f27\u5f62\u5251\u201d \u88f4\u5143\u638c\u4e2d\u5175\u5203\uff0c\u521a\u597d\u591f\u4e0d\u4e0a\u90e8\u4f4d\u3002 \u3000\u3000\u4ed6\u4e45\u7ecf\u5927\u654c\uff0c\u6b64\u62db\u843d\u7a7a\uff0c\u4fbf\u77e5\u8981\u7cdf\uff0c\u8eab\u5f62\u731b\u5f80\u540e\u64a4\uff0c\u4f46\u8499\u9762\u4eba\u6b64\u65f6\u518d\u4e5f\u4e0d\u7ed9\u4ed6\u5598\u6c14\u7684\u673a \u4f1a\uff0c\u5de6\u53f3\u53cc\u638c\u9f50\u51fa\uff0c\u5f62\u540c\u95ee\u7535\uff0c\u4e00\u8d77\u5207\u5728\u88f4\u5143\u7684\u80a9\u5934\u4e0a\uff0c\u8fd9\u4e24\u638c\u662f\u4f55\u7b49\u529f\u529b\uff0c\u88f4\u5143\u53cc\u80a9\u4ff1 \u788e\uff0c\u72c2\u53eb\u4e00\u58f0\uff0c\u4e24\u6761\u817f\u88ab\u8fd9\u4e00\u51fb\u4e4b\u4e0b\uff0c\u7adf\u9677\u4e0b\u96ea\u5730\u51e0\u8fbe\u534a\u5c3a\uff0c\u54ea\u91cc\u8fd8\u6709\u6d3b\u547d\u7684\u5e0c\u671b\u3002 \u3000\u3000\u4f9d\u7136\u5728\u4e0b\u7740\u96ea\uff0c\u5927\u5730\u82cd\u7136\u2014\u2014\u201c\u67aa\u5251\u65e0\u654c\u201d\u88f4\u6c0f\u5144\u5f1f\u7684\u5c38\u4f53\uff0c\u5b89\u9759\u5730\u8eba\u5728\u96ea\u5730\u4e0a\uff0c\u4ed6\u5144 \u5f1f\u7684\u90a3\u4e24\u5339\u9a6c\uff0c\u4f3c\u4e4e\u61c2\u5f97\u4eba\u610f\uff0c\u53c8\u4f3c\u4e4e\u662f\u4e0d\u8010\u5bd2\u51b7\uff0c\u6602\u9996\u4e00\u58f0\u957f\u5636\uff0c\u7ade\u8dd1\u8d70\u4e86\u3002 \u3000\u3000\u8499\u9762\u4eba\u51dd\u7acb\u672a\u52a8\uff0c\u773c\u4e2d\u795e\u91c7\u66f4\u89c1\u593a\u4eba\uff0c\u8d70\u5230\u88f4\u626c\u7684\u5c38\u9996\u65c1\uff0c\u7f13\u7f13\u5f2f\u4e0b\u8170\u53bb\uff0c\u5728\u88f4\u626c\u7684\u5c38 \u4f53\u4e0a\u641c\u7d22\u4e86\u534a\u5929\uff0c\u5e76\u65e0\u6240\u5f97\uff0c\u53c8\u8d70\u5230\u88f4\u5143\u5c38\u7554\u641c\u7d22\u4e86\u4e00\u4f1a\uff0c\u773c\u4e2d\u6d41\u9732\u559c\u8272\uff0c\u81ea\u88f4\u5143\u6000\u4e2d\u53d6\u51fa \u4e00\u7269\uff0c\u6781\u8c28\u614e\u5730\u6536\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7565\u5fae\u62c2\u4e86\u62c2\u8eab\u4e0a\u7684\u96ea\u82b1\uff0c\u671d\u56db\u5468\u518d\u4e00\u6253\u91cf\uff0c\u56db\u91ce\u4ecd\u7136\u65e0\u4eba\uff0c\u7f13\u7f13\u8e31\u5230\u9a6c\u65c1\uff0c\u4ece\u5bb9 \u4e0a\u9a6c\uff0c\u626c\u97ad\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u8352\u5730\u4e0a\u811a\u6b65\u7684\u5370\u75d5\u96f6\u4e71\uff0c\u88f4\u6c0f\u53cc\u6770\u7684\u5c38\u8eab\uff0c\u5c31\u8eba\u5728\u8fd9\u96f6\u4e71\u7684\u811a\u5370\u4e0a\u3002 \u3000\u3000\u88f4\u6c0f\u53cc\u6770\u6b7b\u4e86\uff0c\u4ed6\u4eec\u6240\u5f97\u7684\u5f02\u5b9d\u78a7\u7389\u87fe\u870d\u4e5f\u5931\u4e86\u8e2a\uff0c\u8fd9\u6d88\u606f\u77ac\u5373\u4f20\u904d\u6b66\u6797\uff0c\u4f46\u6740\u6b7b\u88f4\u6c0f \u53cc\u6770\u7684\u51f6\u624b\u662f\u8c01\uff1f\u6c5f\u6e56\u4e0a\u8c01\u4e5f\u4e0d\u77e5\u3002 \u3000\u3000\u4f46\u662f\u5927\u5bb6\u5fc3\u4e2d\u90fd\u60f4\u60f4\u4e0d\u5b89\uff0c\u56e0\u6b64\u4ed6\u4eec\u77e5\u9053\u6b64\u4eba\u65e2\u80fd\u4ee5\u4e00\u4eba\u4e4b\u529b\u6740\u4e86\u4e24\u6cb3\u6b66\u6797\u4e2d\u6709\u540d\u7684\u9ad8 \u624b\u88f4\u6c0f\u53cc\u6770\uff0c\u90a3\u4e48\u6b64\u4eba\u7684\u529f\u529b\uff0c\u5c82\u975e\u4e0d\u53ef\u601d\u8bae\u4e86\u5417\uff1f \u3000\u3000\u4e8e\u662f\u4e24\u6cb3\u7684\u6bcf\u4e00\u95f4\u9556\u5c40\u90e8\u5f00\u59cb\u8b66\u6212\u4e86\uff0c\u4f46\u662f\u56e0\u4e3a\u6b64\u65f6\u9556\u5c40\u95f4\u7ade\u4e89\u975e\u5e38\u6fc0\u70c8\uff0c\u8c01\u4e5f\u4e0d\u80af\u5c06 \u81ea\u5df1\u8b66\u6212\u7684\u529b\u91cf\u53bb\u548c\u522b\u7684\u9556\u5c40\u7ed3\u5408\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u66f4\u7ed9\u4e86\u90a3\u795e\u79d8\u7684\u8499\u9762\u4eba\u4ee5\u540e\u8bb8\u591a\u6b21\u673a\u4f1a\u3002 \u3000\u3000\u4e0d\u51fa\u4e09\u4e2a\u6708\uff0c\u4e24\u6cb3\u7684\u5341\u516d\u5bb6\u9556\u5c40\u7684\u5341\u516d\u4f4d\u603b\u9556\u5934\uff0c\u7adf\u88ab\u8fd9\u795e\u79d8\u7684\u8499\u9762\u4eba\u51fb\u6bd9\u4e86\u5341\u4e09\u4e2a\u3002 \u3000\u3000\u8fd9\u5341\u4e09\u4e2a\u6b66\u6797\u597d\u624b\uff0c\u6709\u7684\u662f\u8d70\u9556\u5728\u8def\u4e0a\uff0c\u88ab\u8499\u9762\u4eba\u51fb\u6bd9\uff1b\u6709\u7684\u6839\u672c\u662f\u5728\u5bb6\u91cc\uff0c\u88ab\u8fd9\u8499\u9762 \u4eba\u8bf1\u51fa\u5b85\u5916\uff0c\u7532\u91cd\u624b\u6cd5\u51fb\u6bd9\u3002 \u3000\u3000\u8fd9\u8499\u9762\u4eba\u6c38\u8fdc\u662f\u5355\u4eba\u72ec\u9a91\uff0c\u65e2\u6ca1\u6709\u5e2e\u624b\uff0c\u4e5f\u4e0d\u5e26\u5175\u5203\uff0c\u4f46\u662f\u5374\u4ece\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u80fd\u5728\u4ed6 \u624b\u4e0b\u9003\u51fa\u6d3b\u547d\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4e0d\u4f46\u4e24\u6cb3\u6b66\u6797\u5927\u4e3a\u9707\u6050\uff0c\u5c31\u8fde\u6574\u4e2a\u4e2d\u539f\u6b66\u6797\uff0c\u4e5f\u4e3a\u8fd9\u4e8b\u6380\u8d77\u5de8\u6ce2\uff0c\u6b66\u6797\u4e2d\u4eba\u7eb7\u7eb7\u731c \u6d4b\u7740\u8fd9\u8499\u9762\u4eba\u7684\u6765\u8def\uff0c\u4f46\u6d3b\u5728\u8fd9\u4e16\u4e0a\u7684\u4eba\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u770b\u5230\u795e\u79d8\u7684\u8499\u9762\u4eba\u7684\u771f\u5b9e\u9762\u76ee\u3002 \u3000\u3000\u603b\u9556\u5934\u4e00\u6b7b\uff0c\u9556\u5c40\u7fa4\u9f99\u5143\u9996\uff0c\u540c\u65f6\u518d\u4e5f\u6ca1\u6709\u4eba\u80af\u51fa\u6765\u62c5\u5f53\u8fd9\u4e8b\uff0c\u9556\u5c40\u81ea\u7136\u5173\u4e86\u95e8\uff0c\u5269\u4e0b \u7684\u56db\u4e2a\u9556\u5c40\u4e2d\u7684\u6cb3\u5317\u201c\u9e3f\u8fdc\u9556\u5c40\u201d\uff0c\u6cb3\u5357\u7684\u201c\u94f6\u97ad\u9556\u5c40\u201d\u91cc\u7684\u4e24\u4f4d\u603b\u9556\u5934\u516b\u5366\u5200\u674e\u6807\uff0c\u94f6\u97ad \u53f8\u5f92\u660e\uff0c\u5e74\u4e8b\u5df2\u9ad8\uff0c\u6b66\u529f\u4e5f\u5f31\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5413\u5f97\u8d76\u7d27\u6d17\u624b\uff0c\u518d\u4e5f\u4e0d\u5e72\u8fd9\u5200\u5934\u8214\u8840\u7684\u52fe \u5f53\uff0c\u9690\u5c45\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u504c\u5927\u7684\u4e24\u6cb3\u5730\u65b9\uff0c\u5c31\u53ea\u5269\u4e0b\u4e86\u6cb3\u5357\u7684\u201c\u96c4\u98ce\u9556\u5c40\u201d\uff0c\u548c\u6cb3\u5317\u4eac\u57ce\u91cc\u7684\u201c\u98de\u9f99\u9556\u5c40\u201d \u4e86\u3002 \u3000\u3000\u539f\u6765\u4e24\u6cb3\u5730\u65b9\u6700\u5927\u7684\u4e24\u5bb6\u9556\u5c40\uff0c\u5c31\u662f\u8fd9\u201c\u96c4\u98ce\u9556\u5c40\u201d\u548c\u201c\u98de\u9f99\u9556\u5c40\u201d\u3002 \u3000\u3000\u201c\u96c4\u98ce\u9556\u5c40\u201d\u7684\u603b\u9556\u5934\uff0c\u4e2d\u5dde\u4e00\u5251\u6b27\u9633\u5e73\u4e4b\u5df2\u7ecf\u5feb\u4e03\u5341\u5c81\u4e86\uff0c\u4f46\u59dc\u662f\u8d8a\u8001\u8d8a\u8fa3\uff0c\u638c\u4e2d\u5251 \u5f97\u6709\u70b9\u82cd\u5fc3\u6cd5\uff0c\u4ed6\u6d78\u6deb\u4e8e\u6b64\u6570\u5341\u5e74\uff0c\u529f\u529b\u66f4\u89c1\u60ca\u4eba\u3002\u6b64\u523b\u4e24\u6cb3\u6b66\u6797\u867d\u7136\u98ce\u58f0\u9e64\u5533\uff0c\u4f46\u8fd9\u4e2a\u8001 \u5934\u5b50\u7980\u6027\u5014\u5f3a\uff0c\u58f0\u8a00\u8981\u4ee5\u638c\u4e2d\u5251\u6765\u548c\u8fd9\u8499\u9762\u4eba\u5468\u65cb\u5468\u65cb\u3002 \u3000\u3000\u201c\u98de\u9f99\u9556\u5c40\u201d\u7684\u603b\u9556\u5934\u5374\u66f4\u662f\u5927\u5927\u6709\u540d\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u521d\u51fa\u6c5f\u6e56\u65f6\uff0c\u624d\u4e8c\u5341\u4f59\u5c81\uff0c \u4fbf\u4ee5\u4e00\u53cc\u8089\u638c\u904d\u4f1a\u7fa4\u96c4\u3002 \u3000\u3000\u4ed6\u6b66\u529f\u867d\u9ad8\uff0c\u5374\u4e5f\u4ece\u4e0d\u7ed9\u4eba\u5bb6\u96be\u582a\uff0c\u4ea4\u624b\u65f6\u70b9\u5230\u4e3a\u6b62\uff0c\u65e0\u8bba\u5bf9\u65b9\u6b66\u529f\u9ad8\u4e0b\uff0c\u6c38\u8fdc\u662f\u6218\u4e2a \u5e73\u624b\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u4eba\u773c\u775b\u96ea\u4eae\uff0c\u8179\u4e2d\u4e5f\u6709\u6570\uff0c\u5bf9\u8fd9\u5e74\u8f7b\u597d\u624b\u4e0d\u4ec5\u66f4\u4e3a\u94a6\u4f69\uff0c\u5341\u5e74\u6765\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80 \u660e\u5728\u4e24\u6cb3\u6b66\u6797\u4e2d\u4eba\u671b\u4e4b\u4f73\uff0c\u66f4\u662f\u65e0\u51fa\u5176\u53f3\u8005\u3002 \u3000\u3000\u800c\u4e14\u6b66\u6797\u4e2d\u4eba\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u6b66\u529f\u7a76\u7adf\u5982\u4f55\uff0c\u5c31\u8fde\u4e2d\u5dde\u4e00\u5251\u90a3\u79cd\u4ece\u4e0d\u670d\u4eba\u7684\u4e2a\u6027\uff0c\u8bf4\u53ca\u6a80 \u660e\u65f6\uff0c\u4e5f\u4f1a\u6697\u6697\u4f38\u8d77\u5927\u62c7\u6307\u6765\u3002 \u3000\u3000\u6b64\u6b21\u4e24\u6cb3\u9556\u5c40\u5341\u4e09\u5bb6\u88ab\u6bc1\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u66f4\u505a\u4e86\u4ef6\u5927\u5927\u7684\u4e49\u4e3e\uff0c\u90a3\u5c31\u662f\u4ed6\u5c06\u8fd9\u5341\u56db\u4e2a\u603b \u9556\u5934\u7684\u9057\u5b64\uff0c\u5168\u6536\u517b\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u987b\u77e5\u8fd9\u4e9b\u6b66\u6797\u597d\u6c49\uff0c\u5927\u591a\u662f\u4e00\u63b7\u5343\u91d1\uff0c\u65e0\u602a\u541d\u7684\u6177\u6168\u6c49\u5b50\uff0c\u5e73\u65e5\u5f97\u6765\u7684\u94b1\u8d22\uff0c\u5230\u624b\u5373 \u6563\uff0c\u54ea\u91cc\u4f1a\u7559\u4e0b\u4ec0\u4e48\u79ef\u84c4\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u7684\u9057\u5b64\uff0c\u751f\u6d3b\u81ea\u7136\u5c31\u4f1a\u751f\u51fa\u95ee\u9898\uff0c\u5c24\u5176\u662f\u6709\u7684\u5e74\u9f84\u8fd8\u5c0f\uff0c\u66f4\u662f\u53ef\u601c\uff0c\u201c\u9f99\u5f62\u516b \u638c\u201d\u6b64\u4e00\u4e49\u4e3e\uff0c\u76f4\u53ef\u79f0\u5f97\u4e0a\u662f\u529f\u5fb7\u65e0\u91cf\uff0c\u4e24\u6cb3\u6b66\u6797\u4e2d\u63d0\u8d77\u201c\u9f99\u5f62\u516b\u638c\u201d\u6765\uff0c\u66f4\u662f\u4f69\u670d\u5f97\u4e94\u4f53 \u6295\u5730\u4e86\u3002 \u3000\u3000\u4f46\u201c\u9f99\u5f62\u516b\u638c\u201d\u5374\u7edd\u65e0\u9a84\u77dc\u4e4b\u8272\uff0c\u8fd9\u4e09\u4e2a\u6708\u6765\u4ed6\u65f6\u5e38\u60a3\u75c5\uff0c\u4e5f\u4e0d\u5927\u51fa\u6765\u8d70\u9556\uff0c\u5bf9\u4e8e\u90a3\u795e \u79d8\u7684\u8499\u9762\u4eba\uff0c\u4e5f\u4e0d\u4f5c\u4efb\u4f55\u8bc4\u8bba\uff0c\u6709\u4eba\u5728\u4ed6\u9762\u524d\u63d0\u53ca\u6b64\u4eba\uff0c\u4ed6\u4e5f\u53ea\u662f\u5fae\u5fae\u542b\u7b11\uff0c\u5374\u4e5f\u4e0d\u53d1\u4e00 \u8a00\u3002 \u3000\u3000\u4e8e\u662f\u5927\u5bb6\u5bf9\u4ed6\u7684\u6b66\u529f\u8d77\u4e86\u66f4\u5927\u7684\u4fe1\u5ff5\uff0c\u90fd\u5e0c\u671b\u4ed6\u80fd\u4e3a\u6b66\u6797\u9664\u53bb\u8fd9\u8499\u9762\u4eba\uff0c\u8fd9\u5c31\u662f\u6c89\u9ed8\u7684 \u597d\u5904\uff0c\u4f60\u4e0d\u8bf4\u8bdd\u6709\u65f6\u5f80\u5f80\u6bd4\u8bf4\u8bdd\u80fd\u6536\u5230\u66f4\u5927\u7684\u6548\u679c\u3002 \u3000\u3000\u4e25\u51ac\u5df2\u8fc7\uff0c\u6625\u65e5\u5df2\u4e34\uff0c\u5317\u4eac\u57ce\u91cc\u53c8\u6062\u590d\u4e86\u751f\u6c14\uff0c\u524d\u95e8\u697c\u5b50\u7684\u8336\u9986\u91cc\uff0c\u7a81\u7136\u6765\u4e86\u4e24\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u8d70\u8ff8\u8336\u9986\uff0c\u559d\u8336\u7684\u4eba\u5341\u4e2a\u5012\u6709\u4e5d\u4e2a\u7ad9\u4e86\u8d77\u6765\uff0c\u8eac\u8eab\u62db\u547c\u7740\uff0c\u663e\u89c1\u5bf9\u8fd9\u4e24\u4eba\u751a\u662f \u5c0a\u656c\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u4e2a\u5e74\u7eaa\u8f83\u957f\uff0c\u5df2\u6709\u4e03\u5341\u4e0a\u4e0b\u4e86\uff0c\u4f46\u7cbe\u795e\u5374\u4ecd\u6781\u4e3a\u5065\u6717\uff0c\u624b\u91cc\u63e1\u7740\u4e24\u4e2a\u94c1\u80c6\uff0c\u5f53 \u5f53\u4f5c\u54cd\uff0c\u5927\u8e0f\u6b65\u8d70\u4e86\u8fdb\u6765\uff0c\u4e00\u70b9\u4e5f\u672a\u663e\u8001\u6001\u3002 \u3000\u3000\u5e74\u8f7b\u7684\u4e00\u4e2a\u53ea\u6709\u4e09\u5341\u591a\u5c81\uff0c\u53cc\u76ee\u70af\u70af\uff0c\u9e70\u9f3b\u9614\u53e3\uff0c\u795e\u6001\u6781\u4e3a\u5a01\u731b\uff0c\u8336\u9986\u91cc\u559d\u8336\u7684\u4eba\u4eec\u606d \u656c\u62db\u547c\u7684\u5bf9\u8c61\uff0c\u4e5f\u662f\u6b64\u4eba\u3002 \u3000\u3000\u4e0d\u8ba4\u8bc6\u4ed6\u7684\u4eba\u4e5f\u6709\uff0c\u6697\u81ea\u5947\u602a\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u4f46\u89c1\u4e86\u8fd9\u7b49\u6c14\u6d3e\uff0c\u5fc3\u91cc\u4e5f\u5728\u6697\u5730\u8d5e\u4f69\u3002 \u3000\u3000\u90a3\u8001\u8005\u9009\u4e86\u5f20\u684c\u5b50\u5750\u4e86\u4e0b\u6765\uff0c\u671d\u90a3\u5a01\u731b\u7684\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u5317\u4eac\u57ce\u91cc\u679c\u7136\u662f\u4eba\u6770\u5730\u7075\uff0c\u4eca\u5929 \u6211\u8001\u5934\u5b50\u603b\u7b97\u5f00\u4e86\u773c\u5566\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u65f6\u58f0\u82e5\u6d2a\u949f\uff0c\u4e00\u53e3\u9053\u5730\u7684\u5ddd\u9ed4\u53e3\u97f3\u3002\u90a3\u6c49\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b27\u9633\u524d\u8f88\u7a0d\u4e3a\u6b47\u606f\u4e00 \u4e0b\uff0c\u7b49\u4f1a\u513f\u665a\u8f88\u518d\u966a\u60a8\u5230\u522b\u5904\u901b\u901b\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6a80\u8001\u5f1f\u5feb\u522b\u8fd9\u6837\u79f0\u547c\uff0c\u53ef\u628a\u6211\u8001\u5934\u6298\u715e\u4e86\u3002\u201d\u53e3\u4e2d\u867d\u7136\u8fd9\u4e48 \u8bf4\uff0c\u5fc3\u4e2d\u5bf9\u4ed6\u7684\u606d\u656c\u9ad8\u5174\u5f97\u5f88\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fdc\u6765\uff0c\u665a\u8f88\u771f\u60ed\u6127\u5f97\u5f88\uff0c\u672c\u6765\u665a\u8f88\u65e9\u8be5\u53bb\u62dc\u8bbf\u60a8\u7684\u2014 \u2014\u201d\u90a3\u8001\u97f3\u4e00\u6446\u624b\uff0c\u963b\u6b62\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u8fd9\u6709\u5565\u5b50\u5173\u7cfb\uff0c\u6211\u4e5f\u662f\u987a\u4fbf\u5230\u5317\u4eac\u57ce\u6765\u800d\u5b50\u7684\uff0c \u90a3\u5c0f\u5b50\u8fd9\u513f\u4e2a\u6708\u867d\u7136\u641e\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u53ef\u4e5f\u8fd8\u4e0d\u503c\u5f97\u6211\u8001\u5934\u5b50\u5df4\u5df4\u5730\u4ece\u6cb3\u5357\u8dd1\u6765\u3002\u201d \u3000\u3000\u8336\u9986\u91cc\u7684\u4eba\u5374\u7ad6\u8d77\u8033\u6735\u6765\u542c\u7740\uff0c\u6709\u7684\u719f\u6089\u6b66\u6797\u4e2d\u4e8b\u7684\uff0c\u4fbf\u5df2\u731c\u51fa\u8fd9\u8001\u5934\u5927\u6982\u5c31\u662f\u6cb3\u5357\u96c4 \u98ce\u9556\u5c40\u7684\u4e2d\u5dde\u4e00\u5251\u6b27\u9633\u5e73\u4e4b\u3002 \u3000\u3000\u201c\u4f46\u662f\u4ed6\u662f\u6cb3\u5357\u8c6a\u6770\uff0c\u600e\u4ed6\u8bf4\u8bdd\u5374\u662f\u8fd9\u79cd\u53e3\u97f3\u5462\uff1f\u201d\u6709\u4e9b\u4eba\u5728\u5947\u602a\uff1a\u201c\u4e5f\u8bb8\u4e0d\u662f\u4ed6 \u5427\uff1f\u201d \u3000\u3000\u4f46\u8fd9\u8001\u8005\u5374\u6b63\u662f\u201c\u4e2d\u5dde\u4e00\u5251\u201d\u6b27\u9633\u5e73\u4e4b\uff0c\u4ed6\u81ea\u5e7c\u751f\u957f\u5728\u4e91\u5357\uff0c\u53c8\u5728\u70b9\u82cd\u5b66\u5251\uff0c\u58ee\u5e74\u624d\u79fb \u5230\u6cb3\u5357\u7684\uff0c\u8bf4\u8bdd\u81ea\u7136\u662f\u5ddd\u9ed4\u4e00\u5e26\u7684\u53e3\u97f3\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u4e0d\u8a00\u53ef\u77e5\u5c31\u662f\u5a01\u9707\u6cb3\u6714\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4e86\u3002 \u3000\u3000\u539f\u6765\u4e2d\u5dde\u4e00\u5251\u6b27\u9633\u5e73\u4e4b\u7adf\u4e3a\u7740\u90a3\u795e\u79d8\u7684\u8499\u9762\u4eba\u8d76\u6765\u5317\u4eac\u548c\u9f99\u5f62\u516b\u638c\u5546\u8ba8\u5e94\u4ed8\u7684\u65b9\u6cd5\uff0c\u53ea \u662f\u4ed6\u7980\u6027\u521a\u5f3a\uff0c\u5634\u91cc\u4e0d\u80af\u627f\u8ba4\uff0c\u786c\u8bf4\u4ed6\u662f\u6765\u5317\u4eac\u57ce\u901b\u901b\u7684\u3002 \u3000\u3000\u4ed6\u4e24\u4eba\u795e\u4ea4\u5df2\u4e45\uff0c\u89c1\u4e86\u9762\u76f8\u8c08\u4ea6\u6b22\uff0c\u4e8e\u662f\u201c\u9f99\u5f62\u516b\u638c\u201d\u4fbf\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u966a\u7740\u8001\u5f53\u76ca\u58ee\u7684 \u201c\u4e2d\u5dde\u4e00\u5251\u201d\u6b27\u9633\u5e73\u4e4b\u901b\u8d77\u5317\u4eac\u57ce\u6765\u4e86\u3002 \u3000\u3000\u201c\u4e2d\u5dde\u4e00\u5251\u201d\u6b27\u9633\u5e73\u4e4b\u5174\u81f4\u9887\u9ad8\uff0c\u8fde\u901b\u4e86\u4e24\u5929\uff0c\u8fd8\u610f\u72b9\u672a\u5c3d\u3002 \u3000\u3000\u4f46\u662f\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u90a3\u795e\u79d8\u7684\u8499\u9762\u4eba\u5374\u5df2\u5149\u4e34\u5230\u98de\u9f99\u9556\u5c40\u91cc\u6765\u4e86\u3002 \u3000\u3000\u6b27\u9633\u5e73\u4e4b\u5230\u5e95\u662f\u4e0a\u4e86\u5e74\u7eaa\u7684\u4eba\uff0c\u65e5\u6765\u901b\u7d2f\u4e86\uff0c\u4e5f\u7761\u5f97\u719f\u4e9b\uff0c\u4f46\u8fd9\u4e2a\u51e0\u5341\u5e74\u7684\u8001\u6c5f\u6e56\uff0c\u4ecd \u7136\u4e0d\u662f\u5e38\u4eba\u53ef\u4ee5\u6bd4\u62df\uff0c\u4ed6\u7761\u68a6\u4e2d\u84e6\u7136\u60ca\u9192\u4e86\uff0c\u542c\u5230\u5c4b\u9876\u4e0a\u6709\u591c\u884c\u4eba\u96f6\u4e71\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u4ed6\u6781\u4e3a\u8fc5\u901f\u5730\u7a7f\u597d\u4e86\u8863\u88f3\uff0c\u51e0\u5341\u5e74\u7684\u8bad\u7ec3\uff0c\u4f7f\u5f97\u4ed6\u5728\u4e00\u6bb5\u5e38\u4eba\u65e0\u6cd5\u601d\u8bae\u7684\u6781\u7094\u65f6\u95f4\u91cc\u7ed3 \u675f\u597d\u4e86\u4e00\u5207\uff0c\u6084\u7136\u63a8\u5f00\u7a97\u6237\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u6709\u4e9b\u5947\u602a\uff0c\u8c01\u6709\u8fd9\u4e48\u5927\u7684\u80c6\u5b50\u8dd1\u5230\u98de\u9f99\u9556\u5c40\u91cc\u6765\u751f\u4e8b\uff0c\u4f46\u662f\u4ed6\u4e60\u6027\u4f7f\u7136\uff0c\u9047\u4e0a\u8fd9 \u79cd\u4e8b\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u8896\u624b\u4e0d\u7ba1\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7eb5\u4e00\u7eb5\u8eab\uff0c\u72f8\u732b\u822c\u5730\u63a0\u51fa\u4e86\u7a97\u6237\uff0c\u56db\u987e\u4e4b\u4e0b\uff0c\u679c\u7136\u53d1\u73b0\u5c4b\u9876\u4e0a\u6709\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u64a4\u4e0b\u4e86\u5251\uff0c\u8fd9\u5c31\u662f\u4ed6\u7684\u8c28\u614e\u4e4b\u5904\uff0c\u80fd\u5728\u6c5f\u6e56\u4e2d\u4eab\u6709\u5982\u8bb8\u591a\u5e74\u76db\u540d\u7684\u4eba\u7269\uff0c\u81ea\u7136\u662f\u884c\u52a8 \u8c28\u614e\u7684\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4e00\u957f\u8eab\uff0c\u55d6\u7136\u7a9c\u4e0a\u4e86\u623f\u9876\uff0c\u5374\u542c\u5230\u90a3\u591c\u884c\u4eba\u5fae\u5fae\u4e00\u58f0\u51b7\u7b11\uff0c\u6781\u5feb\u5730\u5411\u5c4b\u540e\u63a0\u53bb\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e5f\u6beb\u4e0d\u8fdf\u7591\u5730\u8ffd\u4e86\u4e0b\u53bb\uff0c\u4e00\u9762\u6697\u7b11\u6a80\u660e\uff1a\u201c\u8fd9\u5c0f\u5b50\u5230\u5e95\u662f\u5e74\u8f7b\u4e86\u4e9b\uff0c\u5c45\u7136\u7761\u5f97\u90a3 \u4e48\u6b7b\uff0c\u8fde\u6709\u4eba\u5149\u987e\u4ed6\uff0c\u4ed6\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9662\u5b50\u91cc\u53c8\u6062\u590d\u5bc2\u9759\uff0c\u8bb8\u4e45\uff0c\u4e00\u4e2a\u5341\u591a\u5c81\u7684\u7537\u5b69\u8dd1\u51fa\u9662\u5b50\uff0c\u7ad9\u5728\u5899\u89d2\u6492\u5c3f\uff0c\u5ffd\u7136\u770b\u5230\u4eba\u5f71 \u4e00\u6643\uff0c\u5413\u5f97\u4e00\u54c6\u568f\uff0c\u5c3f\u90fd\u5dee\u70b9\u6492\u5728\u88e4\u5b50\u4e0a\u4e86\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u80c6\u5b50\u50cf\u662f\u6bd4\u522b\u7684\u5b69\u5b50\u5927\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u8eb2\u5728\u5899\u89d2\u91cc\uff0c\u770b\u5230\u4e00\u6761\u4eba\u5f71\u4ee5\u6781\u5feb\u7684\u901f\u5ea6\u95ea \u5165\u5c4b\u4e2d\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u867d\u7136\u4e0d\u5927\uff0c\u5934\u8111\u5374\u6781\u7075\u654f\uff0c\u81ea\u5e7c\u4e5f\u5b66\u4e86\u4e9b\u6b66\u529f\uff0c\u53ea\u82e6\u4e8e\u672a\u5f97\u660e\u5e08\u800c\u5df2\uff0c\u6b64\u523b\u90a3\u4eba \u5f71\u867d\u7136\u53ea\u662f\u4e00\u95ea\u5373\u6ca1\uff0c\u4f46\u4ed6\u5df2\u770b\u51fa\u8fd9\u4eba\u5f71\u50cf\u662f\u6a80\u660e\uff0c\u4e0d\u7981\u5947\u602a\u7740\uff1a\u201c\u6a80\u5927\u53d4\u600e\u5730\u8fd9\u4e48\u665a\u624d\u56de \u6765\uff1f\u201d \u3000\u3000\u4f46\u90a3\u4eba\u5f71\u5374\u53c8\u6781\u5feb\u5730\u95ea\u8eab\u800c\u51fa\uff0c\u4e00\u7a9c\u800c\u81f3\u5c4b\u9876\uff0c\u901f\u5ea6\u66f4\u662f\u60ca\u4eba\uff0c\u4ee4\u4eba\u6839\u672c\u65e0\u6cd5\u770b\u6e05\u4ed6\u662f \u8c01\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u5bf9\u81ea\u5df1\u65b9\u624d\u7684\u5224\u65ad\uff0c\u53c8\u89c9\u5f97\u4e0d\u5927\u786e\u5b9a\u4e86\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u5927\u6982\u4e0d\u662f\u6a80\u5927\u53d4\uff0c\u600e\u4f1a\u521a\u56de \u6765\u9a6c\u4e0a\u5c31\u51fa\u53bb\uff1f\u201d \u3000\u3000\u4ed6\u5348\u591c\u68a6\u56de\uff0c\u5934\u8111\u53ef\u662f\u660f\u660f\u5730\uff0c\u4e5f\u4e0d\u53bb\u591a\u601d\u7d22\u4e86\uff0c\u53c8\u8d70\u56de\u623f\u91cc\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u5317\u4eac\u57ce\u91cc\u53ef\u6cb8\u817e\u8d77\u6765\u4e86\u3002 \u3000\u3000\u539f\u6765\u81ea\u6cb3\u5357\u8d76\u6765\u7684\u540d\u9556\u5934\u201c\u4e2d\u5dde\u4e00\u5251\u201d\u6b27\u9633\u5e73\u4e4b\u7adf\u5728\u8352\u90ca\u6bd9\u547d\uff0c\u80f8\u808b\u95f4\u4e2d\u4e86\u5bf9\u65b9\u4e00\u638c\uff0c \u8fde\u80f8\u9aa8\u90fd\u5b8c\u5168\u788e\u4e86\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4f4d\u8001\u9556\u5934\u6bd5\u7adf\u8d85\u4eba\u4e00\u7b49\uff0c\u4e34\u6b7b\u524d\u8fd8\u4e3a\u6b66\u6797\u9664\u53bb\u4e00\u5bb3\uff0c\u539f\u6765\u4ed6\u7684\u5bf9\u624b\u4e5f\u88ab\u4ed6\u4e00\u62f3\u51fb \u4e2d\u9762\u95e8\uff0c\u5c06\u8111\u888b\u6253\u5f97\u7a00\u70c2\uff0c\u800c\u4ed6\u7684\u5bf9\u624b\uff0c\u5374\u5c31\u662f\u6b66\u6797\u4e2d\u4eba\u4eba\u6b32\u5f97\u800c\u8bdb\u4e4b\u7684\u795e\u79d8\u8499\u9762\u5bb9\u3002 \u3000\u3000\u90a3\u662f\u4ece\u4ed6\u7684\u88c5\u675f\u3001\u8eab\u6750\uff0c\u4ee5\u53ca\u867d\u7136\u5df2\u88ab\u51fb\u70c2\uff0c\u4f46\u4ecd\u770b\u5f97\u51fa\u7684\u90a3\u5757\u8499\u5728\u9762\u4e0a\u7684\u9762\u4e2d\u63a8\u65ad\u800c \u51fa\u4ed6\u5c31\u662f\u90a3\u8499\u9762\u7684\u4eba\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u7684\u9762\u76ee\uff0c\u5374\u5df2\u5b8c\u5168\u65e0\u6cd5\u8fa8\u8ba4\u4e86\u3002 \u3000\u3000\u8499\u9762\u4eba\u867d\u6b7b\uff0c\u4f46\u4ed6\u7684\u8eab\u4efd\u3001\u6765\u8def\uff0c\u4ecd\u88ab\u6c5f\u6e56\u4e2d\u4eba\u4e0d\u65ad\u731c\u6d4b\u7740\uff0c\u81f3\u4e8e\u90a3\u8499\u9762\u4eba\u7a76\u7adf\u662f\u8c01\uff0c \u5374\u4f3c\u4e4e\u6c38\u8fdc\u6ca1\u6709\u4eba\u77e5\u9053\u4e86\u3002 \u3000\u3000\u201c\u4e2d\u5dde\u4e00\u5251\u201d\u8fd9\u4e00\u6b7b\uff0c\u9f99\u5f62\u516b\u638c\u7adf\u5f15\u4e3a\u81ea\u548e\uff0c\u4e0d\u65ad\u5730\u8c34\u8d23\u7740\u81ea\u5df1\uff0c\u4e3a\u4e2d\u5dde\u4e00\u5251\u5b89\u6392\u4e86\u6781 \u9686\u91cd\u7684\u846c\u793c\uff0c\u5317\u4eac\u57ce\u91cc\u6765\u53c2\u52a0\u8fd9\u846c\u793c\uff0c\u5c31\u6709\u51e0\u5343\u4eba\uff0c\u518d\u52a0\u4e0a\u8fdc\u65b9\u8d76\u6765\u7684\u6b66\u6797\u8c6a\u6770\uff0c\u4eba\u6570\u66f4\u4e3a \u60ca\u4eba\u4e86\u3002 \u3000\u3000\u201c\u4e2d\u5dde\u4e00\u5251\u201d\u4e00\u751f\u53f1\u54a4\u6c5f\u6e56\uff0c\u6b7b\u540e\u4ea6\u5907\u6781\u54c0\u8363\uff0c\u4ed6\u867d\u7136\u6ca1\u6709\u513f\u5b59\uff0c\u4f46\u4e24\u6cb3\u6b66\u6797\u9053\u7684\u9b41\u9996 \u201c\u9f99\u5f62\u516b\u638c\u201d\u7adf\u5f53\u7740\u5929\u4e0b\u8c6a\u6770\uff0c\u4e3a\u4ed6\u62ab\u9ebb\u6234\u5b5d\uff0c\u505a\u8d77\u5b5d\u5b50\u6765\u4e86\u3002 \u3000\u3000\u201c\u4e2d\u5dde\u4e00\u5251\u201d\u867d\u6b7b\uff0c\u4ed6\u7684\u58f0\u540d\u53cd\u800c\u6bd4\u751f\u524d\u66f4\u54cd\uff0c\u800c\u201c\u9f99\u5f62\u516b\u638c\u201d\u8fd9\u79cd\u98ce\u5ea6\uff0c\u4e5f\u640f\u5f97\u6c5f\u6e56 \u4e2d\u4eba\u4e00\u81f4\u7684\u79f0\u8d5e\u3002 \u3000\u3000\u4e8e\u662f\u201c\u9f99\u5f62\u516b\u638c\u201d\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u4e5f\u5c31\u66f4\u5d07\u9ad8\u4e86\uff0c\u4ed6\u201c\u98de\u9f99\u9556\u5c40\u201d\u6240\u4fdd\u7684\u9556\uff0c\u7531\u5357\u5230 \u5317\uff0c\u53ea\u8981\u201c\u98de\u9f99\u9556\u65d7\u201d\u4e00\u5230\uff0c\u518d\u4e5f\u4e0d\u4f1a\u6709\u4eba\u6562\u671b\u534a\u773c\uff0c\u5c31\u8fde\u6b66\u6797\u4e2d\u5176\u4ed6\u7684\u7ea0\u7eb7\uff0c\u89c1\u4e86\u201c\u98de\u9f99 \u9556\u65d7\u201d\uff0c\u4e5f\u662f\u7acb\u65f6\u4fbf\u89e3\u51b3\u4e86\u3002 \u3000\u3000\u4e24\u6cb3\u6b66\u6797\u4e2d\uff0c\u7adf\u6709\u5341\u56db\u4e2a\u9ad8\u624b\u4e27\u5728\u8fd9\u8499\u9762\u4eba\u624b\u91cc\uff0c\u8fd9\u8499\u9762\u4eba\u50cf\u662f\u548c\u9556\u5c40\u7ed3\u4e86\u4ec0\u4e48\u4ec7\u6068\uff0c \u56e0\u4e3a\u9664\u4e86\u9556\u5c40\u4e2d\u4eba\u4e4b\u5916\uff0c\u4efb\u4f55\u4ed6\u4eba\u5374\u4e00\u4e2a\u4e5f\u672a\u66fe\u906d\u4ed6\u7684\u6bd2\u624b\u3002 \u3000\u3000\u8fd9\u4e9b\u8eab\u6545\u7684\u9556\u5934\u7684\u540e\u4eba\uff0c\u7537\u5973\u4e0d\u540c\uff0c\u5e74\u9f84\u4ea6\u6709\u5dee\u522b\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u5374\u5c06\u4ed6\u4eec\u5168\u6536\u7559\u5728\u8eab \u7554\uff0c\u8fd8\u6089\u5fc3\u6559\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u6b66\u6797\u4e2d\u4eba\u4ea4\u53e3\u8d5e\u8a89\uff0c\u90fd\u8bf4\u9f99\u5f62\u516b\u638c\u4ec1\u4e49\u4e3a\u5148\uff0c\u662f\u4e2a\u4e86\u4e0d\u5f97\u7684\u597d \u6c49\u3002 \u3000\u3000\u65f6\u65e5\u5306\u5306\uff0c\u53c8\u662f\u8bb8\u591a\u5e74\u4e86\u3002 \u3000\u3000\u4eba\u4eec\u5bf9\u51e0\u5e74\u524d\u6240\u53d1\u751f\u7684\u4e8b\uff0c\u90fd\u5df2\u6e10\u6e10\u6de1\u5fd8\uff0c\u6614\u5e74\u6c5f\u6e56\u4fa7\u76ee\uff0c\u641e\u5f97\u6b66\u6797\u60f6\u60f6\u4e0d\u5b89\u7684\u795e\u79d8\u8499 \u9762\u5bb9\uff0c\u6b64\u65f6\u5c38\u9aa8\u5df2\u5bd2\uff0c\u5df2\u7ecf\u5f88\u5c11\u6709\u4eba\u518d\u63d0\u53ca\u4ed6\u3002 \u3000\u3000\u5c31\u8fde\u6614\u65e5\u58f0\u540d\u663e\u8d6b\u7684\u201c\u4e2d\u5dde\u4e00\u5251\u201d\uff0c\u4e5f\u5df2\u4e0d\u518d\u5b58\u7559\u5728\u4eba\u4eec\u5fc3\u4e2d\u4e86\u3002 \u3000\u3000\u53ea\u6709\u201c\u9f99\u5f62\u516b\u638c\u201d\uff0c\u4ed6\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u5374\u968f\u7740\u65f6\u65e5\u7684\u53d8\u8fc1\u3001\u800c\u65e5\u76ca\u5347\u9ad8\uff0c\u201c\u98de\u9f99\u9556 \u5c40\u201d\u4e0d\u4f46\u5728\u4e24\u6cb3\u9996\u5c48\u4e00\u6307\uff0c\u8fdc\u81f3\u6c5f\u5357\uff0c\u585e\u5916\uff0c\u90fd\u8bbe\u6709\u5206\u53f7\uff0c\u6c5f\u6e56\u4e0a\u81ea\u6709\u9556\u5c40\u4ee5\u6765\uff0c\u4ece\u6ca1\u6709\u4efb \u4f55\u4e00\u5bb6\u9556\u5c40\u4eab\u540d\u5982\u6b64\u76db\u7684\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u672c\u4eba\u4e5f\u5f88\u5c11\u51fa\u53bb\u8d70\u9556\u4e86\uff0c\u56e0\u4e3a\u8fd9\u6839\u672c\u4e0d\u9700\u8981\u4ed6\u4eb2\u81ea\u51fa\u9a6c\uff0c\u662f\u4ee5\u4ed6\u7ec8\u65e5 \u65e0\u4e8b\uff0c\u5c31\u5b89\u95f2\u5730\u5728\u5bb6\u91cc\u7eb3\u798f\u3002 \u3000\u3000\u5f53\u5e74\u88ab\u8499\u9762\u4eba\u6240\u6740\u7684\u90a3\u4e9b\u9556\u5ba2\u7684\u540e\u4eba\uff0c\u73b0\u5728\u5168\u90e8\u957f\u5927\uff0c\u6700\u5c0f\u7684\u4e5f\u6709\u5341\u4e09\u5c81\u4e86\uff0c\u201c\u9f99\u5f62\u516b \u638c\u201d\u65e0\u4e8b\u65f6\uff0c\u4e5f\u6559\u6559\u4ed6\u4eec\u6b66\u529f\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u81ea\u5df1\u7684\u72ec\u751f\u5973\u513f\uff0c\u6b64\u65f6\u4e5f\u6709\u5341\u4e94\u5c81\u4e86\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u5df2\u662f\u4e2d\u5e74\u4eba\uff0c\u5bf9\u6c5f\u6e56 \u4e0a\u7684\u52fe\u5f53\uff0c\u4f3c\u4e4e\u5df2\u4e0d\u592a\u611f\u5174\u8da3\uff0c\u4f46\u6b66\u6797\u4e2d\u82e5\u9047\u5230\u4e86\u4e9b\u4ec0\u4e48\u5316\u89e3\u4e0d\u5f00\u7684\u7ea0\u7eb7\uff0c\u8fd8\u662f\u4e0d\u8fdc\u5343\u91cc\u800c \u6765\u6c42\u4ed6\u76f8\u52a9\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u7b2c\u4e8c\u4ee3\uff0c\u4e5f\u5174\u8d77\u4e86\u4e0d\u5c11\u9ad8\u624b\uff0c\u4f46\u65e0\u8bba\u6b66\u529f\uff0c\u58f0\u671b\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u6bd4\u5f97\u4e0a\u201c\u9f99\u5f62\u516b \u638c\u201d\u6a80\u660e\u7684\uff0c\u90a3\u4e9b\u9556\u5ba2\u7684\u540e\u4eba\uff0c\u4e0d\u77e5\u662f\u5426\u5929\u8d44\u592a\u5dee\uff0c\u8fde\u201c\u9f99\u5f62\u516b\u638c\u201d\u5341\u6210\u529f\u592b\u91cc\u7684\u4e00\u6210\u90fd\u672a \u66fe\u5b66\u53bb\u3002 \u3000\u3000\u53c8\u662f\u6625\u5929\uff0c\u8fd9\u5df2\u662f\u201c\u4e2d\u5dde\u4e00\u5251\u201d\u6b7b\u540e\u7684\u7b2c\u516d\u4e2a\u6625\u5929\u4e86\u3002 \u3000\u3000\u6653\u8272\u65b9\u5f00\uff0c\u201c\u98de\u9f99\u9556\u5c40\u201d\u91cc\u7ec3\u6b66\u573a\u91cc\uff0c\u5df2\u6709\u4eba\u5728\u7ec3\u62f3\u4e86\uff0c\u90a3\u662f\u4e2a\u4e5f\u53ea\u6709\u5341\u4e94\u516d\u5c81\u7684\u5c11 \u5e74\uff0c\u7709\u957f\u800c\u79c0\uff0c\u4e24\u53ea\u773c\u775b\u795e\u91c7\u660e\u6717\uff0c\u8eab\u8eaf\u867d\u4e0d\u9ad8\uff0c\u4f46\u53d1\u5c55\u5f97\u6781\u4e3a\u5300\u79f0\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u53ef\u79f0\u5f97\u4e0a \u662f\u201c\u7f8e\u7537\u5b50\u201d\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u6c89\u8170\u5750\u9a6c\uff0c\u4f38\u62f3\u8e22\u817f\uff0c\u529b\u91cf\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u62f3\u6cd5\u4e5f\u4e00\u4e1d\u4e0d\u4e71\uff0c\u53ef\u60dc\u7684\u53ea\u662f\u8fd9\u5957\u62f3 \u6cd5\u4ec5\u662f\u6b66\u6797\u4e2d\u6781\u4e3a\u666e\u904d\u7684\u201c\u5927\u6d2a\u62f3\u201d\u800c\u5df2\u3002\u8fd9\u201c\u5927\u6d2a\u62f3\u201d\u62db\u5f0f\u5446\u677f\uff0c\u53ea\u80fd\u5f3a\u8eab\uff0c\u5374\u4e0d\u80fd\u9632\u8eab \u7684\uff0c\u66f4\u8c08\u4e0d\u4e0a\u653b\u654c\u4e86\uff0c\u7136\u800c\u8fd9\u5c11\u5e74\u5374\u5168\u795e\u8d2f\u6ce8\uff0c\u4e00\u4e1d\u4e0d\u82df\u5730\u7ec3\u4e86\u4e0b\u53bb\uff0c\u4e00\u8d9f\u62f3\u6253\u5b8c\uff0c\u989d\u9762\u4e0a \u5df2\u5fae\u5fae\u89c1\u6c57\u4e86\uff0c\u663e\u89c1\u5185\u529f\u4e5f\u6beb\u65e0\u6839\u57fa\u3002 \u3000\u3000\u4ed6\u6df1\u6df1\u547c\u5438\u4e86\u51e0\u6b21\uff0c\u6cbf\u7740\u56f4\u5899\u7f13\u7f13\u8e31\u7740\uff0c\u8138\u4e0a\u867d\u662f\u6ee1\u8138\u806a\u660e\u4f36\u4fd0\u7684\u6837\u5b50\uff0c\u4f46\u795e\u8272\u5374\u663e\u5f97 \u5341\u5206\u5fe7\u90c1\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u5c31\u662f\u5f53\u5e74\u201c\u67aa\u5251\u53cc\u7edd\u201d\u4e2d\u2018\u94a9\u9570\u67aa\u201c\u88f4\u626c\u7684\u72ec\u5b50\u88f4\u73cf\uff0c\u8fd9\u51e0\u5e74\u6765\u4ed6\u523b\u82e6\u81ea\u52b1\uff0c\u52e4 \u7ec3\u7740\u6b66\u529f\uff0c\u4f46\u7ec3\u4e86\u8fd9\u4e48\u4e45\uff0c\u4ed6\u4ecd\u662f\u6beb\u65e0\u8fdb\u5c55\uff0c\u8fde\u9556\u5c40\u91cc\u5bfb\u5e38\u7684\u4e00\u4e2a\u8d9f\u5b50\u624b\u90fd\u6253\u4e0d\u8fc7\uff0c\u4ed6\u4e0d\u7981 \u5f88\u7070\u5fc3\uff0c\u6697\u6068\u81ea\u5df1\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u7b28\uff0c\u6bcf\u9022\u201d\u9f99\u5f62\u516b\u638c\u201c\u4eb2\u81ea\u6559\u6b66\u529f\u7684\u65f6\u5019\uff0c\u4ed6\u66f4\u7559\u5fc3\u53bb\u5b66\uff0c\u4f46 \u5b66\u672a\u5b66\u53bb\uff0c\u5374\u4ecd\u662f\u90a3\u51e0\u5957\u529f\u592b\uff0c\u6a80\u660e\u5e73\u5e38\u8bf4\u4ed6\u4eec\u592a\u7b28\uff0c\u8fd9\u6837\u7ec3\u6cd5\u4e00\u8f88\u5b50\u4e5f\u65e0\u6cd5\u7ec3\u597d\u3002\u4e8e\u662f\u4ed6 \u5f00\u59cb\u6709\u4e9b\u6000\u7591\u201d\u9f99\u5f62\u516b\u638c\u201c\u4e0d\u80af\u6559\u4ed6\u4eec\u771f\u529f\u529b\uff0c\u4f46\u201d\u9f99\u5f62\u516b\u638c\u201c\u5bf9\u4ed6\u4eec\u5e76\u4e0d\u574f\uff0c\u4ed6\u4e5f\u4e0d\u6562\u5bf9 \u8fd9\u4f4d\u81ea\u5df1\u7684\u5927\u6069\u4eba\u6709\u4ec0\u4e48\u6000\u7591\u3002\u4f46\u5947\u602a\u7684\u662f\u522b\u7684\u9556\u5e08\u5728\u7ec3\u6b66\u65f6\uff0c\u201d\u9f99\u5f62\u516b\u638c\u201c\u4e5f\u4e0d\u51c6\u4ed6\u4eec\u53bb \u770b\uff0c\u8bf4\u662f\u6015\u4e71\u4e86\u4ed6\u4eec\u7684\u5fc3\u601d\uff0c\u88f4\u73cf\u5929\u6027\u6781\u5f3a\uff0c\u4eba\u5bb6\u4e0d\u613f\u610f\u4ed6\u505a\u7684\u4e8b\uff0c\u4ed6\u5c31\u51b3\u4e0d\u505a\uff0c\u4f46\u6b66\u529f\u5bf9 \u4ed6\u7684\u8bf1\u60d1\u53c8\u6781\u5927\uff0c\u662f\u4ee5\u4ed6\u7ec8\u65e5\u5fc3\u60c5\u5fe7\u90c1\uff0c\u5c06\u4ed6\u539f\u6765\u7684\u806a\u660e\u6d3b\u6cfc\u90fd\u6d88\u78e8\u6b86\u5c3d\u4e86\u3002\u6bcf\u5929\u65e9\u4e0a\u5929\u8fd8 \u672a\u4eae\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u6084\u7136\u722c\u8d77\u6765\u7ec3\u62f3\uff0c\u672c\u6765\u8ddf\u4ed6\u5728\u4e00\u8d77\u7684\uff0c\u4e00\u5171\u6709\u4e38\u4e2a\u4eba\uff0c\u90fd\u662f\u9556\u5c40\u7684\u540e\u4eba\uff0c \u4f46\u662f\u201d\u9f99\u5f62\u516b\u638c\u201c\u5374\u5c06\u4ed6\u4eec\u5206\u5f00\u4e86\uff0c\u6709\u7684\u88ab\u9001\u5230\u6cb3\u5357\uff0c\u6709\u7684\u88ab\u9001\u5230\u6c5f\u5357\uff0c\u8bf4\u662f\u8ba9\u4ed6\u4eec\u51fa\u53bb\u5386 \u7ec3\uff0c\u53ea\u7559\u4e0b\u88f4\u73cf\u548c\u53e6\u4e00\u4e2a\u6700\u5c0f\u7684\u5973\u5b69\u5b50\u5728\u5317\u4eac\u57ce\u91cc\u7684\u9556\u5c40\u91cc\u3002\u90a3\u4e2a\u5c0f\u5973\u5b69\u5b50\u53eb\u8881\u6caa\u73cd\uff0c\u662f\u65ad \u9b42\u9556\u8881\u4e00\u6881\u7684\u540e\u4eba\uff0c\u5e74\u7eaa\u867d\u5c0f\uff0c\u4eba\u5374\u806a\u660e\u5f97\u5f88\uff0c\u4e24\u53ea\u5927\u773c\u775b\u4e00\u8f6c\u4e00\u8f6c\u7684\uff0c\u50cf\u662f\u770b\u51fa\u4f60\u7684\u5fc3 \u4e8b\u3002\u88f4\u73cf\u5f88\u559c\u6b22\u5979\uff0c\u5e38\u5e38\u643a\u7740\u5979\u7684\u624b\u5230\u9556\u5c40\u5916\u9762\u53bb\u6563\u6b65\uff0c\u4ed6\u5fc3\u60c5\u4e0d\u597d\u7684\u65f6\u5019\uff0c\u4e5f\u5e38\u5e38\u62c9\u7740\u5979 \u804a\u5929\uff0c\u5176\u5b9e\u4ed6\u4eec\u90fd\u8fd8\u5c0f\uff0c\u5fe7\u90c1\u8fd8\u5acc\u592a\u65e9\u4e86\u4e9b\u3002\u201d\u94a9\u9570\u67aa\u201c\u88f4\u626c\u7684\u59bb\u5b50\u5728\u751f\u4e0b\u88f4\u73cf\u540e\u5c31\u53bb\u4e16 \u4e86\uff0c\u88f4\u73cf\u81ea\u5e7c\u7236\u6bcd\u53cc\u4ea1\uff0c\u73b0\u5728\u53c8\u5947\u4eba\u7bf1\u4e0b\uff0c\u4ed6\u5fc3\u9ad8\u6c14\u505a\uff0c\u65f6\u523b\u60f3\u81ea\u8c0b\u51fa\u8def\u3002\u4f46\u662f\u4ed6\u8eab\u65e0\u4e00\u6280 \u4e4b\u957f\uff0c\u6839\u672c\u4e0d\u77e5\u9053\u8be5\u600e\u6837\u53bb\u8c0b\u751f\uff0c\u4f55\u51b5\u9f99\u5f62\u516b\u638c\u4e5f\u65f6\u5e38\u5b89\u6170\u4ed6\uff0c\u53eb\u4ed6\u597d\u597d\u803d\u5728\u5bb6\u91cc\u3002\u8fd8\u6709\u4e00 \u70b9\u662f\u4ed6\u5fc3\u4e2d\u7684\u79d8\u5bc6\uff0c\u8fd9\u79d8\u5bc6\u5173\u7cfb\u7740\u9f99\u5f62\u516b\u638c\u7684\u72ec\u751f\u5973\u513f\u6a80\u6587\u745b\uff0c\u4e0d\u8fc7\u4ed6\u5c06\u8fd9\u79d8\u5bc6\u6df1\u6df1\u57cb\u85cf\u5728 \u5fc3\u5e95\uff0c\u5e76\u4e14\u65f6\u5e38\u538b\u5236\u7740\u81ea\u5df1\u4e0d\u8981\u53bb\u60f3\u5b83\uff0c\u4f46\u4eba\u7c7b\u7684\u5fc3\u7406\u5374\u53c8\u90a3\u4e48\u5947\u602a\uff0c\u4f60\u8d8a\u662f\u538b\u5236\u7684\u4e8b\uff0c\u5f80 \u5f80\u5374\u66f4\u5bb9\u6613\u7206\u53d1\u7684\u3002\u4ed6\u6cbf\u7740\u5899\u89d2\u8f6c\u4e86\u4e00\u5708\uff0c\u5929\u5df2\u5927\u4eae\u4e86\uff0c\u4ed6\u505c\u4f4f\u4e86\u811a\uff0c\u671b\u7740\u4e1c\u8fb9\u521d\u5230\u7684\u671d \u971e\uff0c\u6123\u4e86\u8bb8\u4e45\uff0c\u5fc3\u91cc\u4e0d\u77e5\u5728\u60f3\u7740\u4e9b\u4ec0\u4e48\u3002\u84e6\u5730\uff0c\u4e00\u7c92\u77f3\u5b50\u98de\u6765\uff0c\u51fb\u4e2d\u4ed6\u7684\u5934\uff0c\u4ed6\u4e00\u60ca\uff0c\u56de\u8fc7 \u5934\u53bb\uff0c\u5374\u770b\u5230\u4e00\u4e2a\u7a7f\u7740\u7d2b\u7f0e\u631f\u8884\u7684\u5c11\u5973\uff0c\u6b63\u501a\u7740\u653e\u5175\u5668\u7684\u67b6\u5b50\u5728\u51b2\u4ed6\u61a8\u7b11\u3002\u77f3\u5b50\u53d1\u51fa\u7684\u529b\u9053 \u867d\u7136\u4e0d\u91cd\uff0c\u4f46\u8fd8\u662f\u51fb\u5f97\u4ed6\u8111\u888b\u9690\u9690\u53d1\u75db\u3002\u4ed6\u4e0b\u610f\u8bc6\u5730\u6478\u4e86\u6478\u5934\uff0c\u90a3\u5c11\u5973\u5a07\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211 \u7239\u7239\u8bf4\u4f60\u7b28\uff0c\u4f60\u77a7\u4f60\uff0c\u7ec3\u4e86\u8fd9\u4e48\u4e45\u7684\u529f\u592b\uff0c\u6709\u4eba\u5728\u540e\u9762\u6697\u7b97\u4f60\uff0c\u4f60\u90fd\u4e0d\u77e5\u9053\uff0c\u8fd9\u5e78\u597d\u662f\u77f3 \u5934\uff0c\u8981\u4e0d\uff0c\u4f60\u8111\u888b\u4e0d\u5f00\u82b1\u624d\u602a\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5973\u6b63\u662f\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7684\u638c\u4e0a\u660e\u73e0\uff0c\u5a07\u7b11\u5982\u82b1\uff0c\u5410\u8bed\u5982\u73e0\uff0c\u7b11\u8d77\u6765\u4e24\u8fb9\u988a\u4e0a\u9732\u51fa \u4e24\u4e2a\u6df1\u6df1\u7684\u9152\u6da1\uff0c\u4ee4\u4eba\u6709\u767e\u5408\u521d\u653e\u7684\u611f\u89c9\u3002 \u3000\u3000\u88f4\u73cf\u4e00\u7b11\uff0c\u5e73\u65e5\u95f4\u8fd9\u79cd\u8bdd\u4ed6\u4e5f\u542c\u591a\u4e86\uff0c\u4e5f\u5c31\u6162\u6162\u5730\u4e60\u60ef\uff0c\u8fd9\u98de\u9f99\u9556\u5c40\u91cc\u9762\u7684\u4eba\u4e2a\u4e2a\u8bf4\u4ed6 \u7b28\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5f00\u59cb\u89c9\u5f97\u81ea\u5df1\u662f\u7b28\u7684\uff0c\u5e73\u65e5\u5c3d\u91cf\u7684\u5c11\u8bf4\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8bf4\u591a\u4e86\u8bdd\u4ed6\u5c31\u66f4\u7b28\u4e86\u3002 \u3000\u3000\u6a80\u6587\u742a\u59d7\u59d7\u8d70\u4e86\u8fc7\u6765\uff0c\u4e24\u53ea\u5927\u773c\u775b\u4e00\u7728\u4e00\u7728\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u62f3\u7ec3\u5b8c\u4e86\u6ca1\u6709\uff1f\u201d\u88f4\u73cf\u70b9\u70b9 \u5934\u3002 \u3000\u3000\u6a80\u6587\u742a\u4e00\u8dfa\u811a\uff0c\u5a07\u55d4\u9053\uff1a\u201c\u4f60\u5440\uff01\u771f\u6c14\u6b7b\u4eba\uff0c\u4eba\u5bb6\u8ddf\u4f60\u8bf4\u8bdd\uff0c\u4f60\u603b\u50cf\u54d1\u5427\u4f3c\u7684\u3002\u201d \u3000\u3000\u88f4\u73cf\u4ecd\u7136\u4e0d\u4f5c\u58f0\uff0c\u6a80\u6587\u742a\u6c14\u5f97\u5c0f\u5634\u561f\u8d77\u8001\u9ad8\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u4eec\u4e0d\u914d\u8ddf\u4f60\u8bf4\u8bdd\uff0c\u53ea\u6709 \u4f60\u7684\u8881\u59b9\u59b9\u624d\u914d\u8ddf\u4f60\u8bf4\u8bdd\u662f\u4e0d\u662f\uff1f\u597d\uff01\u201d\u5979\u53c8\u4e00\u8dfa\u811a\uff0c\u8f6c\u8fc7\u8eab\u53bb\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u4ee5\u540e\u4f60\u4e0d\u8981 \u7406\u6211\u597d\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u8138\u4e0a\u795e\u8272\u5947\u602a\u5f97\u5f88\uff0c\u50cf\u662f\u6781\u529b\u5728\u63a7\u5236\u7740\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u6a80\u6587\u742a\u8d70\u4e86\u4e24\u6b65\uff0c\u56de\u8fc7\u5934\u6084\u4fcf \u6765\u671b\u4ed6\uff0c\u4ed6\u5fc3\u91cc\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u742a\u59b9\u2014\u2014\u201d\u4e0b\u9762\u7684\u8bdd\u5374\u518d\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u53ea\u89c9\u5fc3\u91cc\u751c\u751c\u7684\u3002 \u3000\u3000\u6a80\u6587\u742a\u4e00\u7b11\u505c\u4f4f\u4e86\u811a\u6b65\uff0c\u5f97\u610f\u5730\u5a07\u7b11\u7740\u8bf4\uff1a\u201c\u771f\u8ba8\u538c\uff0c\u8c01\u6559\u4f60\u7406\u6211\u7684\uff1f\u201d\u56de\u8fc7\u5934\u6765\uff0c\u8fde \u4e24\u53ea\u5927\u773c\u775b\u91cc\u90fd\u5145\u6ee1\u4e86\u7b11\u610f\u3002 \u3000\u3000\u88f4\u73cf\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u6211\u8be5\u600e\u751f\u662f\u597d\uff1f\u5979\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u5bf9\u7537\u5973\u4e4b\u60c5\uff0c\u53ea\u6a21\u6a21\u7cca \u7cca\u6709\u4e2a\u6982\u5ff5\uff0c\u77e5\u9053\u5f97\u5e76\u4e0d\u6e05\u695a\uff0c\u89c1\u4e0d\u7740\u6a80\u6587\u742a\u65f6\uff0c\u6211\u65f6\u65f6\u523b\u523b\u60f3\u770b\u89c1\u5979\uff0c\u53ef\u662f\u82e5\u771f\u6b63\u89c1\u4e86 \u5979\uff0c\u53c8\u60f3\u9a6c\u4e0a\u8d70\u5f00\uff0c\u56e0\u4e3a\u6211\u4eff\u4f5b\u89c9\u5f97\u81ea\u5df1\u914d\u4e0d\u4e0a\u5979\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u7684\u8fd9\u4e9b\u77db\u76fe\uff0c\u6a80\u6587\u742a\u53ef\u4e0d\u77e5\u9053\uff0c\u5979\u5a07\u61a8\u5df2\u60ef\uff0c\u5634\u91cc\u867d\u5728\u9a82\u7740\u4ed6\u7b28\uff0c\u5fc3\u91cc\u53ef\u6ca1\u6709\u8fd9 \u79cd\u60f3\u6cd5\uff0c\u53ea\u89c9\u5f97\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u5c31\u9ad8\u5174\u5f97\u5f88\uff0c\u53ef\u662f\u4ed6\u813e\u6c14\u50cf\u662f\u6709\u4e9b\u9634\u9633\u602a\u6c14\u7684\uff0c\u5979\u4e5f\u4e0d\u77e5\u9053\u662f \u4ec0\u4e48\u539f\u56e0\u3002 \u3000\u3000\u5979\u770b\u5230\u4ed6\u548c\u8881\u6caa\u73cd\u5728\u4e00\u8d77\u65f6\u5c31\u6709\u8bf4\u6709\u7b11\u7684\uff0c\u5fc3\u91cc\u5c31\u751f\u6c14\uff0c\u4e0b\u6b21\u89c1\u4e86\u4ed6\u65f6\uff0c\u5c31\u6545\u610f\u9017\u4ed6\u751f \u6c14\uff0c\u53ef\u662f\u4ed6\u82e5\u771f\u7684\u751f\u6c14\u4e86\uff0c\u5979\u5fc3\u91cc\u53c8\u540e\u6094\u3002 \u3000\u3000\u88f4\u73cf\u5446\u5446\u5730\u7ad9\u7740\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u9633\u5149\u5347\u8d77\uff0c\u7167\u5f97\u4ed6\u8138\u4e0a\u7ea2\u7ea2\u7684\u3002 \u3000\u3000\u6a80\u6587\u742a\u5728\u4ed6\u9762\u524d\u8d70\u6765\u8d70\u53bb\uff0c\u5ffd\u7136\u81ea\u6000\u4e2d\u638f\u51fa\u4e00\u6837\u4e1c\u897f\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5730\u629b\u7740\uff0c\u9633\u5149\u7167\u5f97\u90a3\u4e1c \u897f\u95ea\u95ea\u53d1\u5149\uff0c\u539f\u6765\u662f\u4e00\u53ea\u9e21\u6bdb\u505a\u6210\u7684\u6bfd\u5b50\u3002 \u3000\u3000\u88f4\u73cf\u7684\u773c\u5149\u968f\u7740\u90a3\u6bfd\u5b50\u4e00\u4e0a\u4e00\u843d\uff0c\u5fc3\u91cc\u53eb\u82e6\uff1a\u201c\u53c8\u6765\u4e86\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u4fa7\u7740\u8138\u671b\u7740\u4ed6\u5a07\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u8981\u548c\u6211\u8e22\u6bfd\u5b50\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4e0d\u6562\u7b54\u8154\uff0c\u6a80\u6587\u745b\u5634\u4e00\u561f\uff0c\u62ff\u7740\u6bfd\u5b50\u8dd1\u8fc7\u6765\uff0c\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u5a07\u55d4\u9053\uff1a\u201c\u4f60\u8ddf\u4e0d\u8ddf\u6211 \u8e22\u6bfd\u5b50\uff1f\u201d\u4e00\u4e2a\u4fcf\u751f\u751f\u7684\u9762\u5b54\uff0c\u51e0\u4e4e\u8d34\u5230\u88f4\u73cf\u8138\u4e0a\u3002 \u3000\u3000\u88f4\u73cf\u9f3b\u5185\uff0c\u6ee1\u662f\u5c11\u5973\u7684\u5e7d\u9999\uff0c\u5fae\u5fae\u5411\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u8e22\uff01\u8e22\uff01\u201d\u6a80\u6587\u742a\u4e00\u7b11\u62cd \u4e86\u62cd\u4ed6\u80a9\u8180\uff0c\u9053\uff1a\u201c\u8fd9\u624d\u4e56\u3002\u201d\u88f4\u73cf\u5fc3\u91cc\u8df3\u5f97\u66f4\u5389\u5bb3\uff0c\u671b\u7740\u5979\u7684\u9152\u6da1\uff0c\u7adf\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u6a80\u6587\u742a\u62ff\u8d77\u6bfd\u5b50\u5411\u4e0a\u4e00\u629b\uff0c\u90a3\u6bfd\u5b50\u75be\u5730\u843d\u4e0b\u6765\uff0c\u5979\u811a\u4e00\u62db\uff0c\u6bfd\u5b50\u7adf\u5e73\u5e73\u7a33\u7a33\u5728\u5979\u811a\u9762 \u4e0a\u3002 \u3000\u3000\u5979\u53c8\u5f97\u610f\u5730\u671d\u88f4\u73cf\u4e00\u7b11\uff0c\u811a\u518d\u4e00\u62ac\uff0c\u6bfd\u5b50\u98de\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u90a3\u6bfd\u5b50\u4e00\u4e0a\u4e00\u843d\uff0c\u5979\u8e22\u4e86\u5341\u51e0\u4e2a\uff0c\u7a81\u7136\u5fae\u5fae\u4e00\u4fa7\u8eab\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u53f3\u811a\u4ece\u5de6\u811a\u540e\u9762\u7a7f\u51fa\uff0c \u5374\u8e22\u90a3\u6bfd\u5b50\uff0c\u4e00\u9762\u9053\uff1a\u201c\u5582\uff0c\u4f60\u600e\u4e48\u4e0d\u5e2e\u6211\u6570\u5440\uff1f\u201d \u3000\u3000\u5a40\u5a1c\u800c\u5a07\u5c0f\u7684\u8eab\u8eaf\uff0c\u50cf\u662f\u4e00\u53ea\u7a7f\u82b1\u7684\u8774\u8776\u3002 \u3000\u3000\u88f4\u73cf\u5634\u91cc\u6570\u7740\uff1a\u201c\u5341\u3001\u5341\u4e00\u2014\u2014\u201d\u773c\u91cc\u968f\u7740\u5979\u6253\u8f6c\u3002 \u3000\u3000\u6a80\u6587\u742a\u8d8a\u8e22\u8d8a\u9ad8\u5174\uff0c\u773c\u89d2\u4e00\u77ac\uff0c\u671b\u89c1\u88f4\u73cf\u5446\u5446\u5730\u671b\u7740\u81ea\u5df1\uff0c\u5634\u89d2\u4e00\u62bf\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u54ea\u77e5\u5979\u5fc3\u795e\u4e00\u5206\uff0c\u90a3\u6bfd\u5b50\u8fdc\u8fdc\u88ab\u8e22\u8d70\u4e86\uff0c\u5979\u8eab\u8eaf\u4e00\u626d\uff0c\u50cf\u662f\u98de\u7fd4\u7740\u7684\u71d5\u5b50\uff0c\u8ddf\u4e86\u8fc7\u53bb\uff0c \u8eab\u6cd5\u7684\u8f7b\u7075\u7f8e\u5999\uff0c\u662f\u96be\u4ee5\u5f62\u5bb9\u7684\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u91cc\u6697\u6697\u96be\u53d7\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u82e5\u6709\u5979\u90a3\u6837\u7684\u8eab\u6cd5\u8be5\u6709\u591a\u597d\uff1f\u53ef\u60dc\uff0c\u5509\uff01\u6211\u96be\u9053\u771f\u7684\u90a3 \u4e48\u7b28\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u79c0\u53d1\u98d8\u98d8\uff0c\u8863\u8882\u5fae\u626c\uff0c\u671b\u4e4b\u76f4\u5982\u51cc\u6ce2\u4ed9\u5b50\uff0c\u7a81\u5730\u8f7b\u5de7\u5730\u4e00\u8f6c\u8eab\uff0c\u53cc\u817f\u8fde\u73af\u8e22\u51fa\uff0c \u5c06\u6bfd\u5b50\u8e22\u5f97\u9ad8\u9ad8\u5730\uff0c\u624b\u4e00\u626c\uff0c\u63a5\u5728\u624b\u91cc\u3002 \u3000\u3000\u5979\u8fd9\u51e0\u4e2a\u52a8\u4f5c\uff0c\u5b8c\u5168\u662f\u4e00\u6c14\u5475\u6210\uff0c\u6ca1\u6709\u4e1d\u6beb\u52c9\u5f3a\uff0c\u4e5f\u6ca1\u6709\u4e1d\u6beb\u505a\u4f5c\uff0c\u66fc\u5999\u5730\u505c\u4f4f\u4e86\u8eab \u5f62\u3002 \u3000\u3000\u5979\u5fae\u5fae\u6709\u4e9b\u5a07\u5598\uff0c\u4f46\u90a3\u66f4\u6dfb\u4e86\u5979\u7684\u59a9\u5a9a\u3002 \u3000\u3000\u201c\u4e24\u767e\u4e2a\u8e22\u5b8c\u4e86\uff0c\u8be5\u8f6e\u5230\u4f60\u5566\uff01\u201d\u5979\u8d70\u5230\u88f4\u73cf\u8eab\u65c1\uff0c\u5c06\u6bfd\u5b50\u9012\u7ed9\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u4f60\u8e22 \u4e0d\u5230\u4e24\u767e\u4e2a\uff0c\u770b\u6211\u4eca\u5929\u53ef\u9976\u4f60\u3002\u201d \u3000\u3000\u88f4\u73cf\u8138\u4e0a\u7a81\u7136\u63a0\u8d77\u4e00\u4e1d\u5947\u602a\u7684\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u5047\u5982\u6211\u8e22\u5230\u4e86\u5462\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8111\u6d77\u4e2d\u6cdb\u8d77\u4e86\u4e0a\u4e00\u6b21\u4ed6\u8e22\u6bfd\u5b50\u90a3\u79cd\u7b28\u62d9\u7684\u6837\u5b50\uff0c\u8fde\u5341\u4e2a\u90fd\u6ca1\u6709\u8e22 \u5230\u3002 \u3000\u3000\u4e8e\u662f\u5979\u7b11\u7740\u8bf4\uff1a\u201c\u5537\uff0c\u6562\u60c5\u4f60\u8fd8\u80fd\u8e22\u4e24\u767e\u4e2a\u5440\uff01\u201d\u5979\u9762\u624b\u53c9\u7740\u8170\uff0c\u9762\u5b54\u7ea2\u7ea2\u7684\uff0c\u53c8\u9053\uff1a \u201c\u597d\uff0c\u4f60\u8e22\u5230\u4e24\u767e\u4e2a\u968f\u4fbf\u600e\u6837\u90fd\u884c\u3002\u201d \u3000\u3000\u201c\u968f\u4fbf\u600e\u6837\u90fd\u884c\uff1f\u201d\u88f4\u73cf\u968f\u53e3\u95ee\u9053\u3002 \u3000\u3000\u64c5\u6587\u742a\u8138\u4e00\u7ea2\uff0c\u5a07\u9a82\u9053\uff1a\u201c\u4f60\u574f\u6b7b\u4e86\uff01\u201d\u5fc3\u4e2d\u5374\u5947\u602a\u5730\u6cdb\u51fa\u4e00\u79cd\u96be\u4ee5\u5f62\u5bb9\uff0c\u65e0\u6cd5\u63cf\u8ff0\u7684 \u611f\u89c9\u3002 \u3000\u3000\u88f4\u73cf\u77ac\u5373\u4e5f\u4e86\u89e3\u4e86\u5979\u4e3a\u4ec0\u4e48\u5728\u9a82\u81ea\u5df1\uff0c\u8138\u7ea2\u5f97\u6bd4\u6a80\u6587\u742a\u66f4\u5389\u5bb3\uff0c\u4f4e\u7740\u5934\uff0c\u63a5\u8fc7\u4e86\u6bfd\u5b50\uff0c \u4e5f\u5728\u5730\u4e0a\u5f00\u59cb\u8e22\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6a80\u6587\u745b\u5174\u9ad8\u91c7\u70c8\u5730\u6570\u7740\uff1a\u201c\u4e00\u3001\u4e8c\u3001\u4e09\u2014\u2014\u201d\u4f46\u662f\u5979\u6570\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u5c0f\uff0c\u5230\u540e\u6765\uff0c\u50cf\u662f \u8fde\u6570\u7684\u529b\u6c14\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u539f\u6765\u88f4\u73cf\u8eab\u6cd5\u867d\u7136\u6ca1\u6709\u5979\u8f7b\u7075\uff0c\u59ff\u52bf\u66f4\u4e0d\u53ca\u5979\u66fc\u5999\uff0c\u4f46\u662f\u6bfd\u5b50\u5374\u50cf\u751f\u4e86\u773c\u775b\u4f3c\u7684\uff0c\u76f4\u4e0a \u76f4\u4e0b\uff0c\u7edd\u4e0d\u5f80\u522b\u7684\u5730\u65b9\u8dd1\u3002 \u3000\u3000\u662f\u4ee5\u88f4\u73cf\u53ea\u8981\u4e00\u62ac\u811a\uff0c\u90a3\u6bfd\u5b50\u4fbf\u6b63\u597d\u843d\u5728\u4ed6\u811a\u4e0a\uff0c\u53c8\u98de\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u6643\u773c\u4e4b\u95f4\uff0c\u88f4\u73cf\u53c8\u8e22\u4e86\u4e00\u767e\u591a\u4e2a\u4e86\u3002 \u3000\u3000\u6a80\u6587\u742a\u5fc3\u91cc\u65e2\u5947\u602a\uff0c\u53c8\u7740\u6025\uff0c\u5947\u602a\u7684\u662f\u4ed6\u600e\u4e48\u7a81\u7136\u8e22\u5f97\u8fd9\u4e48\u597d\uff1f \u3000\u3000\u7740\u6025\u7684\u662f\uff0c\u4ed6\u773c\u770b\u5df2\u8e22\u5230\u4e24\u767e\u4e2a\uff0c\u81ea\u5df1\u5c31\u8981\u8f93\u4e86\u3002 \u3000\u3000\u5979\u54ea\u91cc\u77e5\u9053\u88f4\u73cf\u7980\u6027\u5014\u5f3a\uff0c\u4e0a\u6b21\u8e22\u6bfd\u5b50\u65f6\uff0c\u88ab\u6a80\u6587\u742a\u7b11\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u5fc3\u91cc\u4e0d\u8212\u670d\uff0c\u5077\u5077 \u53bb\u505a\u4e86\u4e2a\u6bfd\u5b50\uff0c\u6bcf\u5929\u665a\u4e0a\u8fde\u89c9\u90fd\u4e0d\u7761\uff0c\u8dd1\u5230\u9662\u5b50\u91cc\u53bb\u8e22\u6bfd\u5b50\uff0c\u53d1\u8a93\u4e00\u5b9a\u8981\u8e22\u5f97\u6bd4\u5979\u597d\u3002 \u3000\u3000\u719f\u80fd\u751f\u5de7\uff0c\u8e22\u6bfd\u5b50\u4e00\u9053\uff0c\u672c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6280\u5de7\u53ef\u8a00\uff0c\u4f55\u51b5\u4ed6\u672c\u6781\u806a\u660e\uff0c\u53ea\u662f\u4ece\u5c0f\u88ab\u6291\u5236\uff0c \u81ea\u5df1\u5fc3\u91cc\u6709\u4e86\u81ea\u5351\u4e4b\u611f\u800c\u5df2\u3002 \u3000\u3000\u7ec3\u4e86\u6ca1\u591a\u4e45\uff0c\u4ed6\u8e22\u8d77\u6bfd\u5b50\u6765\u5df2\u80fd\u5f97\u5fc3\u5e94\u624b\u4e86\uff0c\u4ed6\u4e5f\u4e0d\u8bf4\uff0c\u53ea\u95f7\u5728\u5fc3\u91cc\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u7b49\u5230 \u4f60\u627e\u6211\u8e22\u6bfd\u5b50\u65f6\uff0c\u6211\u8981\u597d\u597d\u8ba9\u4f60\u5403\u4e00\u60ca\u3002\u201d \u3000\u3000\u73b0\u5728\u5979\u679c\u7136\u60ca\u5947\u4e86\uff0c\u5728\u65c1\u8fb9\u56b7\u9053\uff1a\u201c\u597d\uff0c\u4f60\u771f\u574f\uff0c\u5077\u5077\u5730\u53bb\u5b66\u4e86\u662f\u4e0d\u662f\uff1f\u4e5f\u4e0d\u544a\u8bc9\u6211\uff0c \u8ba9\u6211\u4e0a\u5f53\u3002\u201d \u3000\u3000\u88f4\u73cf\u4e5f\u4e0d\u7406\u5979\uff0c\u8138\u4e0a\u5374\u96be\u514d\u5f97\u610f\u5730\u7b11\u4e86\u8d77\u6765\uff0c\u53e3\u91cc\u4e00\u9762\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e00\u4e5d\u4e09\uff0c\u4e00\u4e5d\u56db\u2014 \u2014\u201d\u6a80\u6587\u742a\u7a81\u7136\u8df3\u4e86\u8fc7\u53bb\uff0c\u4e00\u628a\u62a2\u8fc7\u6bfd\u5b50\uff0c\u5a07\u58f0\u4e0d\u4f9d\u9053\uff1a\u201c\u4f60\u574f\uff0c\u4f60\u574f\uff01\u201d \u3000\u3000\u88f4\u73cf\u5927\u7b11\uff1a\u201c\u4f60\u8f93\u4e86\uff0c\u8fd8\u8d56\u3002\u201d\u6570\u5e74\u6765\u4ed6\u5fc3\u60c5\u4ece\u672a\u5982\u6b64\u597d\u8fc7\uff0c\u4ed6\u597d\u80dc\u4e4b\u5fc3\u6700\u5f3a\uff0c\u4f46\u5374\u5904 \u5904\u88ab\u4eba\u538b\u5236\uff0c\u5e73\u65e5\u81ea\u7136\u662f\u90c1\u90c1\u5be1\u6b22\u4e86\u3002 \u3000\u3000\u6a80\u6587\u742a\u4e00\u4e2a\u8eab\u5b50\u5df2\u51e0\u4e4e\u501a\u5728\u4ed6\u6000\u91cc\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u8f93\u4e86\uff0c\u4f60\u8981\u600e\u6837\uff1f\u201d\u88f4\u73cf\u5fc3\u4e2d\u4e00 \u8361\u3002 \u3000\u3000\u6b64\u523b\u9633\u5149\u521d\u5347\uff0c\u6b63\u662f\u5c11\u5e74\u4eba\u60c5\u604b\u6700\u76db\u4e4b\u9645\uff0c\u521d\u5347\u7684\u9633\u5149\u7167\u5f97\u6a80\u6587\u742a\u8138\u4e0a\u7684\u6beb\u6bdb\uff0c\u53d8\u6210\u4e86 \u4e00\u79cd\u68a6\u822c\u7684\u91d1\u9ec4\u8272\u3002 \u3000\u3000\u5979\u5a07\u5598\u4f9d\u4f9d\uff0c\u5374\u5410\u5728\u88f4\u73cf\u8138\u4e0a\uff0c\u88f4\u73cf\u5fc3\u8df3\u52a0\u901f\uff0c\u518d\u96be\u628a\u63e1\uff0c\u5fcd\u4e0d\u4f4f\u4f4e\u4e0b\u5934\u53bb\uff0c\u8f7b\u8f7b\u5730\u5728 \u5979\u9762\u988a\u4e0a\u543b\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u5f53\u4ed6\u7684\u5634\u5507\u63a5\u89e6\u5230\u6a80\u6587\u742a\u9762\u988a\u4e0a\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4e24\u4e2a\u4eba\u90fd\u5b9b\u5982\u89e6\u7535\uff0c\u5168\u8eab\u90fd\u9ebb\u6728\u4e86\uff0c\u6b64\u65f6 \u7eb5\u7136\u5929\u5d29\u5730\u88c2\uff0c\u4ed6\u4eec\u4e5f\u5168\u4e0d\u5728\u610f\u4e86\uff0c\u4e24\u4eba\u4f46\u89c9\u5929\u5730\u4e07\u7269\uff0c\u90fd\u4e0d\u8fc7\u662f\u4e3a\u4ed6\u4e24\u4eba\u8fd9\u4e00\u543b\u800c\u751f\u7684\u7f62 \u4e86\u3002 \u3000\u3000\u84e6\u5730\uff0c\u6709\u4eba\u91cd\u91cd\u5730\u54b3\u55fd\u4e86\u4e00\u58f0\u2014\u2014\u4ed6\u4e24\u4eba\u5927\u60ca\uff0c\u7acb\u523b\u5206\u4e86\u5f00\u6765\uff0c\u4e00\u671b\u4e4b\u4e0b\uff0c\u66f4\u662f\u5413\u5f97\u9b42 \u4e0d\u9644\u4f53\uff0c\u539f\u6765\u5728\u4ed6\u4eec\u4fe9\u8eab\u4fa7\u7ad9\u7740\u7684\uff0c\u6b63\u662f\u9762\u5982\u5bd2\u51b0\u7684\u9f99\u5f62\u516b\u638c\u3002 \u3000\u3000\u6a80\u6587\u742a\u7eb5\u7136\u5e73\u65f6\u6492\u5a07\u653e\u5201\uff0c\u6b64\u65f6\u5374\u662f\u5fc3\u5934\u9e7f\u649e\uff0c\u5413\u5f97\u9762\u5b54\u7ea2\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u4f4e\u7740\u5934\u518d\u4e5f \u4e0d\u6562\u62ac\u8d77\u6765\u3002 \u3000\u3000\u88f4\u73cf\u66f4\u662f\u624b\u8db3\u5931\u63aa\uff0c\u9762\u5b54\u7ea2\u5f97\u50cf\u8304\u5b50\u4e00\u6837\uff0c\u4e0d\u5b89\u5730\u626d\u52a8\u7740\u53cc\u624b\uff0c\u751f\u50cf\u8fd9\u4e24\u53ea\u624b\u4e0d\u77e5\u8be5\u5982 \u4f55\u653e\u6cd5\u624d\u597d\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u5982\u51b0\uff0c\u77aa\u5728\u4ed6\u4eec\u8138\u4e0a\uff0c\u7a81\u7136\u4e00\u8f6c\u5934\uff0c\u5389\u58f0\u9053\uff1a\u201c\u742a\u513f\uff0c\u56de\u623f\u53bb\u3002\u201d\u5927 \u8e0f\u6b65\u8d70\u4e86\u3002 \u3000\u3000\u6a80\u6587\u742a\u59d4\u59d4\u5c48\u5c48\u5730\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u8d70\u4e86\u4e24\u6b65\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u56de\u5934\u53bb\u671b\u4ed6\u4e00\u773c\uff0c\u6b64\u65f6\u5979\u4e00\u9897\u5c11\u5973 \u82b3\u5fc3\uff0c\u5df2\u4e0d\u81ea\u89c9\u5730\u653e\u5728\u4ed6\u8eab\u4e0a\u4e86\u3002 \u3000\u3000\u88f4\u73cf\u6115\u5728\u90a3\u91cc\uff0c\u6a80\u6587\u742a\u7684\u56de\u5934\u4e00\u987e\uff0c\u4ee4\u4ed6\u7ec8\u8eab\u96be\u5fd8\uff0c\u5c24\u5176\u662f\u5979\u773c\u4e2d\u6ee1\u76c8\u7740\u6cea\u6c34\uff0c\u66f4\u4f7f\u4ed6 \u96be\u5fcd\uff0c\u5fc3\u4e2d\u5b9b\u5982\u5200\u5272\u3002 \u3000\u3000\u4ed6\u6697\u5fd6\uff1a\u201c\u90fd\u662f\u6211\u4e0d\u597d\uff0c\u5bb3\u5f97\u5979\u53d7\u7f6a\u3002\u201d\u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u6a80\u5927\u53d4\u4e00\u5b9a\u8ba4\u4e3a\u6211\u592a\u7b28\uff0c\u4e0d\u914d\u4ed6 \u7684\u5973\u513f\uff0c\u6240\u4ee5\u751f\u5927\u6c14\uff0c\u5509\uff01\u8c01\u53eb\u6211\u81ea\u5df1\u8fd9\u4e48\u4e0d\u6210\u6750\uff0c\u8981\u662f\u6211\u80fd\u806a\u660e\u4e9b\uff0c\u90a3\u4e0d\u662f\u592a\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u6115\u4e86\u8bb8\u4e45\uff0c\u4f4e\u4e0b\u5934\u51dd\u89c6\u7740\u5730\u4e0a\uff0c\u5374\u770b\u5230\u4e00\u53ea\u8682\u8681\uff0c\u5728\u7b28\u62d9\u5730\u642c\u8fd0\u7740\u4e00\u5757\u4f53\u79ef\u6bd4\u5b83\u8fd8 \u5927\u7684\u6606\u866b\u7684\u5c38\u4f53\uff0c\u8f9b\u82e6\u800c\u8e52\u8dda\u5730\u5728\u722c\u884c\u7740\u3002 \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u8fd9\u8682\u8681\uff0c\u5fc3\u4e2d\u6cb9\u7136\u800c\u751f\u51fa\u4e00\u79cd\u4ed6\u4ece\u672a\u60f3\u5230\u7684\u611f\u89c9\u3002 \u3000\u3000\u201c\u6211\u867d\u7136\u7b28\u4e9b\uff0c\u4f46\u6211\u4e5f\u8be5\u6709\u6211\u81ea\u5df1\u7684\u524d\u9014\u5440\uff0c\u7ec8\u65e5\u56f0\u5728\u522b\u4eba\u7684\u5bb6\u91cc\u5403\u95f2\u996d\uff0c\u6211\u8fd8\u7b97\u4e2a\u4ec0 \u4e48\u7537\u5b50\u6c49\uff0c\u8fd9\u6837\u4e0b\u53bb\uff0c\u6211\u53c8\u600e\u5bf9\u5f97\u4f4f\u6211\u6b7b\u53bb\u7684\u53cc\u4eb2\uff0c\u600e\u5bf9\u5f97\u4f4f\u742a\u59b9\uff0c\u53c8\u600e\u5bf9\u5f97\u4f4f\u6211\u81ea\u5df1 \u5462\uff1f\u201d \u3000\u3000\u4ed6\u63e1\u7d27\u62f3\u5934\uff0c\u610f\u6c14\u7a81\u7136\u8c6a\u53d1\uff0c\u6697\u5fd6\uff1a\u201c\u6211\u8981\u51fa\u53bb\u95ef\u95ef\uff0c\u53bb\u78b0\u78b0\u8fd0\u6c14\uff0c\u5047\u5982\u6211\u6210\u529f\u4e86\uff0c\u6211 \u5c31\u53ef\u4ee5\u5149\u5f69\u5730\u56de\u5230\u8fd9\u91cc\u6765\uff0c\u90a3\u65f6\u5019\u6a80\u5927\u53d4\u4e5f\u4e0d\u4f1a\u518d\u8ba4\u4e3a\u6211\u6ca1\u51fa\u606f\uff0c\u4e5f\u8bb8\u5c31\u80af\u8ba9\u742a\u59b9\u548c\u6211\u5728\u4e00 \u8d77\u4e86\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u731b\u7136\u89c9\u5f97\u6d51\u8eab\u6d3b\u6cfc\u6cfc\u5730\u5145\u6ee1\u4e86\u751f\u6c14\uff0c\u751f\u50cf\u4e00\u523b\u4e5f\u65e0\u6cd5\u5728\u6b64\u5730\u803d\u4e0b\u53bb\uff0c\u81f3\u4e8e \u4ed6\u5b64\u8eab\u5916\u95ef\uff0c\u4e3e\u76ee\u65e0\u4eb2\uff0c\u5c06\u8981\u53d7\u5230\u600e\u4e48\u6837\u7684\u75db\u82e6\uff0c\u5374\u975e\u8fd9\u5e74\u8f7b\u6c14\u76db\u7684\u88f4\u73cf\u6b64\u65f6\u6240\u60f3\u5f97\u5230\u7684 \u4e86\u3002 \u3000\u3000\u201c\u53ef\u662f\u5c0f\u59b9\u77e5\u9053\u6211\u8d70\u4e86\uff0c\u4e00\u5b9a\u4f1a\u96be\u8fc7\u6b7b\u4e86\u3002\u201d\u4ed6\u53c8\u60f3\u8d77\u4e86\u8881\u6caa\u73cd\uff0c\u4f46\u4ed6\u77ac\u5373\u8f6c\u5ff5\u5fd6\u9053\uff1a \u201c\u53ef\u662f\u6211\u4ee5\u540e\u5149\u5f69\u5730\u56de\u6765\uff0c\u5979\u5c82\u975e\u8981\u66f4\u9ad8\u5174\u5341\u500d\uff1f\u201d \u3000\u3000\u4ed6\u6027\u683c\u6781\u4e3a\u5014\u5f3a\uff0c\u5fc3\u4e2d\u51b3\u5b9a\u7684\u4e8b\uff0c\u4e5f\u4ece\u4e0d\u66f4\u6539\u3002 \u3000\u3000\u4ed6\u4e0d\u518d\u8003\u8651\u4e00\u5207\uff0c\u4ee5\u540e\u4efb\u4f55\u5931\u8d25\uff0c\u4efb\u4f55\u632b\u6298\uff0c\u4ed6\u90fd\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u56e0\u4e3a\u4e00\u79cd\u66f4\u5f3a\u70c8\u7684\u5e0c\u671b\uff0c\u6b64\u523b\u6b63\u5145\u6c9b\u5728\u4ed6\u5fc3\u91cc\uff0c\u4ed6\u4e0d\u613f\u610f\u4ed6\u7684\u8ba1\u5212\u53d7\u5230\u4efb\u4f55\u963b\u788d\uff0c\u4ed6\u5fae \u5fae\u62ac\u8d77\u5934\uff0c\u671b\u7740\u90a3\u56f4\u5899\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u56f4\u5899\u5916\u9762\u5c31\u4e0d\u5c5e\u4e8e\u98de\u9f99\u9556\u5c40\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8dd1\u5230\u5899\u8fb9\uff0c\u52aa\u529b\u5730\u5411\u4e0a\u4e00\u7eb5\u8eab\uff0c\u60f3\u81ea\u5899\u5934\u8dc3\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u529b\u91cf\u4e0d\u591f\uff0c\u8f7b\u529f\u6839\u672c\u6beb\u65e0\u6839\u57fa\uff0c\u54ea\u91cc\u8dc3\u5f97\u4e0a\u8fd9\u4e08\u8bb8\u9ad8\u7684\u56f4\u5899\uff0c\u7830\u5730\u4e00\u54cd\uff0c\u91cd\u91cd\u6454 \u5728\u5730\u4e0a\uff0c\u8dcc\u5f97\u5c41\u80a1\u9690\u9690\u53d1\u75db\u3002 \u3000\u3000\u4ed6\u6beb\u4e0d\u6c14\u9981\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u8fde\u8eab\u4e0a\u7684\u5c18\u571f\u90fd\u4e0d\u62cd\u4e00\u4e0b\uff0c\u53c8\u7eb5\u8eab\u4e0a\u8dc3\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u4ed6\u53cc\u624b\u5df2\u6500\u4e0a\u4e86\u5899\u5934\uff0c\u4e8e\u662f\u4ed6\u7d27\u6293\u4e0d\u653e\uff0c\u5168\u8eab\u4e00\u8d77\u7528\u529b\uff0c\u52aa\u529b\u5730\u722c\u4e0a\u4e86\u56f4\u5899\u3002 \u3000\u3000\u56f4\u5899\u5916\u9762\u662f\u4e00\u6761\u5c0f\u5df7\u5b50\uff0c\u6b64\u65f6\u6b63\u6709\u4e2a\u83dc\u8d29\u5b50\uff0c\u6311\u7740\u62c5\u5b50\u4ece\u4e0b\u9762\u8d70\u4e86\u8fc7\u53bb\uff0c\u62ac\u8d77\u5934\u60ca\u5f02\u5730 \u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u4e5f\u5e76\u672a\u5341\u5206\u5728\u610f\u3002 \u3000\u3000\u4ed6\u4e00\u54ac\u7259\uff0c\u5899\u5934\u8ddd\u79bb\u5730\u9762\u867d\u7136\u8fd8\u6709\u8bb8\u591a\u8ddd\u79bb\uff0c\u4f46\u4ed6\u5374\u4e5f\u4e0d\u7ba1\uff0c\u53cc\u817f\u4e00\u5c48\uff0c\u671d\u5730\u4e0a\u8df3\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u88f4\u73cf\u51ed\u7740\u4e00\u65f6\u610f\u6c14\uff0c\u4e1d\u6beb\u6ca1\u6709\u8003\u8651\u5230\u540e\u679c\uff0c\u7adf\u4ece\u98de\u9f99\u9556\u5c40\u91cc\u8d8a\u5899\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u95ed\u7740\u773c\u81ea\u5899\u5934\u8df3\u5230\u5730\u4e0a\uff0c\u7830\u5730\u4e00\u58f0\uff0c\u9707\u5f97\u5168\u8eab\u9690\u9690\u53d1\u75db\uff0c\u4f46\u603b\u7b97\u8fd8\u6ca1\u6709\u8dcc\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u6761\u5e76\u4e0d\u592a\u5bbd\u7684\u5df7\u5b50\uff0c\u4e24\u7aef\u5374\u4f38\u5ef6\u5f97\u5f88\u957f\uff0c\u88f4\u73cf\u5fd6\u91cf\u4e00\u4e0b\uff0c\u77e5\u9053\u5f80\u5de6\u8d70\u662f\u98de\u9f99\u9556\u5c40 \u7684\u5927\u95e8\uff0c\u4e8e\u662f\u4ed6\u5c31\u671d\u957f\u5df7\u7684\u53f3\u7aef\u8d70\u53bb\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fc3\u60c5\u662f\u5174\u594b\u7684\uff0c\u5bf9\u672a\u6765\u867d\u662f\u832b\u65e0\u6240\u77e5\uff0c\u4f46\u5374\u5145\u6ee1\u4e86\u5e7b\u60f3\uff0c\u56e0\u4e3a\u8fd9\u65f6\u73b0\u5b9e\u7684\u95ee\u9898\u8fd8 \u672a\u66fe\u56f0\u6270\u8fc7\u4ed6\u3002 \u3000\u3000\u8d70\u51fa\u957f\u5df7\uff0c\u662f\u4e00\u6761\u8f83\u5bbd\u7684\u9752\u77f3\u677f\u8def\uff0c\u53c8\u662f\u5411\u5de6\u53f3\u4f38\u5c55\uff0c\u4ed6\u672c\u65e0\u76ee\u7684\uff0c\u4fe1\u6b65\u671d\u53f3\u65b9\u8d70\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u6b64\u65f6\u5929\u65f6\u5c1a\u65e9\uff0c\u8def\u4e0a\u7684\u884c\u4eba\u4e5f\u4e0d\u591a\uff0c\u6709\u4e00\u9876\u7eff\u5462\u5b98\u8f7f\u8d70\u8fc7\u6765\uff0c\u524d\u9762\u6709\u516b\u4e2a\u96b6\u5352\uff0c\u625b\u7740 \u201c\u8083\u9759\u201d\u3001\u201c\u56de\u907f\u201d\u7684\u724c\u5b50\uff0c\u60f3\u5fc5\u662f\u65e9\u671d\u56de\u6765\u7684\u4eac\u5b98\uff0c\u4ed6\u8fdc\u8fdc\u5c31\u907f\u5728\u8def\u65c1\uff0c\u8ba9\u5b98\u8f7f\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u5b98\u8f7f\u7684\u7a97\u5e18\u6df1\u5782\uff0c\u4ed6\u770b\u4e0d\u6e05\u91cc\u9762\u5750\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u4ed6\u597d\u5947\u5730\u731c\u60f3\uff1a\u201c\u91cc\u9762\u5750\u7684\u4eba\u6b64\u523b\u5728\u60f3 \u7740\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u6700\u540e\uff0c\u4ed6\u66ff\u81ea\u5df1\u4e0b\u4e86\u4e2a\u7ed3\u8bba\uff1a\u201c\u90a3\u603b\u4e0d\u5916\u662f\u2018\u540d\u2019\u4e0e\u2018\u5229\u2019\u5427\uff01\u201d \u3000\u3000\u4ed6\u6652\u7136\u4e00\u7b11\uff0c\u89c9\u5f97\u81ea\u5df1\u8fdc\u6bd4\u5750\u5728\u5b98\u8f7f\u91cc\u7684\u90a3\u4eba\u5feb\u4e50\u5f97\u591a\uff0c\u56e0\u4e3a\u81f3\u5c11\uff0c\u81ea\u5df1\u662f\u5b8c\u5168\u81ea\u7531 \u7684\uff0c\u6ca1\u6709\u4efb\u4f55\u7684\u62d8\u675f\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u50cf\u662f\u957f\u4e86\u7fc5\u8180\uff0c\u98de\u5230\u9065\u8fdc\u7684\u5730\u65b9\u4e86\u3002 \u3000\u3000\u4ed6\u7a7f\u7740\u662f\u4e00\u5957\u6c34\u6e56\u8272\u7684\u77ed\u886b\uff0c\u811a\u4e0b\u767b\u7740\u4e00\u53cc\u8584\u5e95\u5feb\u9774\uff0c\u8fd9\u662f\u4ed6\u7ec3\u6b66\u65f6\u7684\u88c5\u675f\uff0c\u8d70\u8d77\u8def \u6765\uff0c\u8f7b\u4fbf\u5f97\u5f88\u3002 \u3000\u3000\u8f6c\u51fa\u8fd9\u6761\u8def\uff0c\u662f\u4e00\u4e2a\u4e0d\u5c0f\u7684\u5e02\u573a\uff0c\u6b64\u523b\u5df2\u662f\u65e9\u5e02\uff0c\u4eba\u4eec\u62e5\u6324\u5728\u91cc\u9762\uff0c\u53d1\u51fa\u6742\u4e71\u7684\u5608\u58f0\u3002 \u3000\u3000\u4ed6\u65bd\u7136\u4fe1\u6b65\u800c\u8d70\uff0c\u5fc3\u60c5\u8f7b\u677e\u5f97\u5f88\uff0c\u4f46\u8d70\u4e86\u4e0d\u4e45\uff0c\u809a\u5b50\u5374\u997f\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u7b2c\u4e00\u4e2a\u6709\u5173\u73b0\u5b9e\u7684\u95ee\u9898\u56f0\u6270\u4ed6\uff0c\u5e02\u573a\u91cc\u7684\u4e1c\u897f\u5f88\u591a\uff0c\u5317\u4eac\u57ce\u91cc\u8457\u540d\u7684\u201c\u7cd6\u846b\u82a6\u201d\u3001 \u201c\u751c\u5c71\u6942\u201d\u3001\u201c\u67a3\u513f\u7cd5\u201d\uff0c\u90fd\u662f\u4ed6\u5e73\u65e5\u7231\u5403\u7684\uff0c\u6b64\u523b\u89c1\u4e86\uff0c\u66f4\u662f\u998b\u6d8e\u6b32\u6ef4\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u8981\u4e9b \u6765\u5403\u3002 \u3000\u3000\u4f46\u4ed6\u53e3\u888b\u91cc\u8fde\u4e00\u5206\u94f6\u5b50\u90fd\u6ca1\u6709\uff0c\u4ed6\u53ea\u80fd\u773c\u770b\u7740\uff0c\u8fd9\u65f6\u5019\uff0c\u4ed6\u7b2c\u4e00\u6b21\u4e86\u89e3\u5230\u201c\u91d1\u94b1\u201d\u7684\u529b \u91cf\uff0c\u4e5f\u4e86\u89e3\u5230\u4e86\u5b83\u7684\u53ef\u8d35\u3002 \u3000\u3000\u4ece\u8fd9\u4e2a\u95ee\u9898\u5f00\u59cb\uff0c\u5404\u79cd\u7684\u73b0\u5b9e\u95ee\u9898\u90fd\u5411\u4ed6\u4ea4\u76f8\u800c\u653b\u4e86\u3002 \u3000\u3000\u751f\u6d3b\uff0c\u8fd9\u662f\u4eba\u4eec\u6700\u91cd\u8981\u7684\u95ee\u9898\uff0c\u800c\u751f\u6d3b\u4e2d\u6700\u6700\u4e0d\u80fd\u7f3a\u5c11\u7684\uff0c\u5c31\u662f\u201c\u91d1\u94b1\u201d\uff0c\u56e0\u4e3a\u201c\u91d1 \u94b1\u201d\u51e0\u4e4e\u53ef\u4ee5\u4ee3\u8868\u4e86\u4e00\u5207\u3002 \u3000\u3000\u201c\u8be5\u600e\u4e48\u6837\u751f\u6d3b\u5462\uff1f\u201d\u88f4\u73cf\u56f0\u60d1\u4e86\uff0c\u9996\u5148\uff0c\u4ed6\u8fde\u4eca\u65e5\u7684\u5348\u996d\u90fd\u65e0\u6cd5\u89e3\u51b3\uff0c\u90a3\u66f4\u4e0d\u987b\u518d\u8c08 \u5230\u5176\u4ed6\u7684\u4e86\uff0c\u4e8e\u662f\u4ed6\u4e5f\u60f6\u6050\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5356\u5403\u98df\u7684\u644a\u8d29\u89c1\u5230\u4ed6\u8863\u7740\u4e0d\u9519\uff0c\u90fd\u62a2\u7740\u5411\u4ed6\u515c\u751f\u610f\uff0c\u4ed6\u90fd\u6447\u5934\u62d2\u7edd\u4e86\uff0c\u5176\u5b9e\u4ed6\u4f55\u5c1d\u4e0d\u60f3 \u4e70\u4e9b\u5403\u98df\uff0c\u53ea\u662f\u529b\u6709\u4e0d\u902e\u7f62\u4e86\u3002 \u3000\u3000\u968f\u7740\u8179\u4e2d\u9965\u997f\u7684\u7a0b\u5ea6\uff0c\u4ed6\u5185\u5fc3\u7684\u60f6\u6050\u4e5f\u5728\u589e\u52a0\uff1a\u201c\u4eca\u5929\u4e2d\u5348\u4e0d\u5403\uff0c\u665a\u4e0a\u4e5f\u8981\u5403\u5440\uff0c\u5c31\u662f \u4eca\u5929\u665a\u4e0a\u4e5f\u53ef\u4ee5\u4e0d\u5403\uff0c\u4f46\u660e\u5929\u5462\uff1f\u201d\u4ed6\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9664\u4e86\u4f1a\u4e00\u4e9b\u4e0d\u4e2d\u7528\u7684\u672c\u4e8b\u4e4b\u5916\uff0c\u8c0b\u751f\u7684 \u65b9\u6cd5\uff0c\u4ed6\u4e00\u7a8d\u4e0d\u901a\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u5f00\u59cb\u6709\u4e9b\u540e\u6094\uff0c\u4f46\u662f\u4ed6\u65e2\u5b9a\u51b3\u5fc3\uff0c\u5c31\u518d\u4e5f\u4e0d\u4f1a\u66f4\u6539\u4e86\u3002\u201c\u5b81\u53ef\u6b7b\u53bb\uff0c\u4e5f\u4e0d\u518d\u6539\u53d8 \u81ea\u5df1\u4e3b\u610f\u3002\u201d\u50bb\u52b2\u513f\uff0c\u4ed6\u662f\u6709\u7684\u3002 \u3000\u3000\u4ed6\u968f\u7740\u4eba\u6f6e\u8d70\u52a8\u7740\uff0c\u5fc3\u4e2d\u7684\u601d\u6f6e\uff0c\u5374\u6bd4\u4eba\u6f6e\u8fd8\u8981\u6df7\u4e71\u6570\u500d\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u6709\u4eba\u5728\u4ed6\u80cc\u540e\u8f7b\u8f7b\u62cd\u4e86\u4e00\u4e0b\uff0c\u4ed6\u832b\u7136\u56de\u8fc7\u8eab\uff0c\u4e00\u4e2a\u7325\u7410\u7684\u6c49\u5b50\u6b63\u671b\u7740\u4ed6\u7b11\uff0c\u5947\u602a\u7684\u4ed6\u6b64 \u65f6\u50cf\u662f\u8eab\u4e0d\u7531\u5df1\uff0c\u5c45\u7136\u8ddf\u7740\u90a3\u4eba\u8dd1\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u8d70\u5f97\u5feb\uff0c\u4ed6\u4e5f\u8d70\u5f97\u5feb\uff0c\u90a3\u4eba\u8d70\u5f97\u6162\uff0c\u4ed6\u4e5f\u5c31\u6162\u6162\u8d70\uff0c\u4ed6\u6f5c\u5728\u610f\u8bc6\u867d\u4e0d\u6e05\u9192\u7684\uff0c\u4f46\u8eab \u8eaf\u5374\u50cf\u662f\u5df2\u4e0d\u542c\u81ea\u5df1\u547d\u4ee4\u3002 \u3000\u3000\u90a3\u7325\u7410\u7684\u6c49\u5b50\u8d70\u51fa\u5e02\u573a\uff0c\u4e03\u8f6c\u516b\u8f6c\uff0c\u8d70\u5f80\u4e00\u6761\u66f4\u72ed\u7a84\u7684\u5df7\u5b50\uff0c\u90a3\u5df7\u5b50\u4e24\u65c1\u7684\u623f\u5b50\u5efa\u5f97\u5f88 \u4f4e\uff0c\u4f46\u5374\u662f\u697c\u623f\uff0c\u518d\u52a0\u4e0a\u5df7\u5b50\u592a\u7a84\uff0c\u5bf9\u9762\u5f53\u7a97\u653e\u7740\u7684\u4e1c\u897f\uff0c\u4ece\u8fd9\u91cc\u7a97\u6237\u4f38\u624b\u8fc7\u53bb\u90fd\u51e0\u4e4e\u53ef\u4ee5 \u62ff\u5230\u4e86\u3002 \u3000\u3000\u8d70\u5230\u5df7\u5b50\u7684\u6700\u540e\u51e0\u5bb6\uff0c\u90a3\u6c49\u5b50\u8d70\u8fdb\u4e00\u4e2a\u5c0f\u95e8\uff0c\u88f4\u73cf\u5df2\u662f\u7740\u9b54\uff0c\u4e5f\u8ddf\u7740\u8d70\u4e86\u8fdb\u53bb\u3002\u623f\u5b50\u91cc \u53c8\u81ed\u53c8\u5c0f\uff0c\u6709\u51e0\u4e2a\u5996\u5f62\u602a\u72b6\u7684\u5973\u4eba\u5750\u5728\u697c\u4e0b\uff0c\u9ad8\u58f0\u7b11\u9a82\u7740\uff0c\u5b8c\u5168\u6ca1\u6709\u4e00\u4e1d\u5973\u4eba\u7684\u5473\u9053\u3002 \u3000\u3000\u90a3\u4e9b\u5973\u4eba\u4e00\u770b\u89c1\u90a3\u6c49\u5b50\u5e26\u4e86\u88f4\u73cf\u8fdb\u6765\uff0c\u4e00\u6d8c\u5411\u524d\uff0c\u56f4\u5728\u88f4\u73cf\u8eab\u65c1\uff0c\u4e03\u624b\u516b\u811a\u5730\u5728\u4ed6\u8eab\u4e0a \u6478\u6765\uff0c\u6709\u7684\u8bf4\uff1a\u201c\u8fd9\u8d27\u8272\u771f\u4e0d\u574f\u3002\u201d\u6709\u7684\u4e00\u9762\u6478\u7740\u4ed6\u7684\u8138\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u770b\uff0c\u8fd9\u8d27\u8272\u7684 \u76ae\u80a4\u771f\u5ae9\uff0c\u8138\u8272\u513f\u50cf\u5439\u5f39\u5f97\u7834\u4f3c\u7684\uff0c\u6253\u626e\u8d77\u6765\uff0c\u5305\u7ba1\u50cf\u662f\u5973\u7684\u3002\u201d \u3000\u3000\u88f4\u73cf\u8ff7\u8ff7\u7cca\u7cca\u5730\u6709\u4e9b\u751f\u6c14\uff0c\u4f46\u4ed6\u8111\u6d77\u91cc\u6df7\u9968\u4e00\u7247\uff0c\u8fde\u8fd9\u751f\u6c14\u7684\u611f\u89c9\u90fd\u4e0d\u592a\u660e\u786e\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u542c\u4e86\u5f97\u610f\u5f97\u5f88\uff0c\u63a8\u5f00\u90a3\u91cc\u8d8a\u770b\u8d8a\u522b\u626d\u7684\u201c\u5973\u4eba\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0a\u697c\u53bb\u66ff\u4ed6\u6253\u626e\u6253 \u626e\u3002\u201d\u88c2\u5f00\u5634\u4e00\u7b11\uff0c\u5634\u91cc\u7684\u7259\u9f7f\u90fd\u53d8\u6210\u571f\u9ec4\u8272\u4e86\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u4e0a\u4e86\u697c\uff0c\u88f4\u73cf\u4e5f\u8ddf\u7740\u4e0a\u4e86\u697c\uff0c\u8d70\u8fdb\u4e00\u95f4\u623f\uff0c\u623f\u91cc\u9664\u4e86\u4e00\u5f20\u5927\u5e8a\u4e4b\u5916\uff0c\u5c31\u4ec0\u4e48\u4e5f\u6ca1 \u6709\u4e86\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4ece\u5e8a\u5e95\u7684\u4e00\u53e3\u6a1f\u6728\u7bb1\u5b50\u91cc\uff0c\u53d6\u51fa\u4e86\u51e0\u4ef6\u5973\u4eba\u7a7f\u7684\u8863\u670d\uff0c\u5728\u88f4\u73cf\u8eab\u4e0a\u6bd4\u4e86\u6bd4\uff0c\u9009\u4e86 \u4ef6\u5927\u7ea2\u7684\uff0c\u653e\u5728\u5e8a\u4e0a\uff0c\u5c06\u5176\u4f59\u7684\u53c8\u6536\u56de\u7bb1\u5b50\u91cc\u3002 \u3000\u3000\u4ed6\u53c8\u66ff\u88f4\u73cf\u6362\u4e0a\u4e86\u8fd9\u4ef6\u7ea2\u8863\u670d\uff0c\u7830\u5730\uff0c\u5c06\u88f4\u73cf\u63a8\u5728\u5e8a\u4e0a\uff0c\u8d70\u4e86\u51fa\u53bb\uff0c\u5173\u4e0a\u623f\u95e8\uff0c\u8fd8\u50cf\u662f \u5df2\u7ecf\u4e0b\u4e86\u9501\u3002 \u3000\u3000\u88f4\u73cf\u6b64\u523b\u5b8c\u5168\u50cf\u662f\u4e00\u5177\u5931\u53bb\u4e86\u7075\u9b42\u7684\u5c38\u4f53\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u53cd\u6297\uff0c\u8111\u6d77\u91cc\u4e5f\u662f\u8ff7\u7cca\u7684\uff0c\u53ea\u9690 \u9690\u7ea6\u7ea6\u5730\u89c9\u5f97\u8fd9\u4e8b\u5b9e\u5728\u592a\u5947\u602a\u4e86\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u88ab\u63a8\u5728\u5e8a\u4e0a\u65f6\u7684\u59ff\u6001\uff0c\u52a8\u4e5f\u672a\u52a8\uff0c\u4e5f\u4e0d\u77e5\u7b49\u4e86\u591a\u4e45\u3002 \u3000\u3000\u6700\u540e\uff0c\u623f\u95e8\u88ab\u6253\u5f00\u4e86\uff0c\u8d70\u8fdb\u6765\u4e00\u4e2a\u80d6\u5b50\uff0c\u671d\u88f4\u73cf\u770b\u4e86\u770b\uff0c\u53c8\u4f38\u51fa\u5934\u53bb\uff0c\u548c\u5916\u9762\u7684\u4eba\u8bb2\u4e86 \u51e0\u65ec\u8bdd\uff0c\u7830\u5730\uff0c\u53c8\u5c06\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u80d6\u5b50\u7792\u73ca\u5730\u8d70\u5230\u5e8a\u524d\uff0c\u9152\u6c14\u718f\u718f\uff0c\u4f38\u624b\u53bb\u89e3\u88f4\u73cf\u7684\u8863\u670d\uff0c\u539f\u6765\u6b64\u5730\u662f\u4e2a\u201c\u50cf\u59d1\u56e2\u5b50\u201d\u3002 \u90a3\u7325\u7410\u6c49\u5b50\uff0c\u4ee5\u6c5f\u6e56\u4e0b\u4e5d\u6d41\u7684\u201c\u62cd\u82b1\u624b\u6cd5\u201d\uff0c\u5c06\u88f4\u73cf\u62cd\u4e86\u6765\uff0c\u8fd9\u4e5f\u602a\u88f4\u73cf\u751f\u5f97\u592a\u6e05\u79c0\u4e86\u4e9b\u3002 \u3000\u3000\u53ef\u662f\u5bf9\u8fd9\u4e9b\uff0c\u88f4\u73cf\u5374\u4e00\u70b9\u4e5f\u4e0d\u61c2\uff0c\u4ed6\u867d\u7136\u795e\u667a\u4e0d\u6e05\uff0c\u4f46\u5df2\u5fae\u5fae\u89c9\u5f97\u6b64\u4e8b\u6709\u4e9b\u4e0d\u5bf9\uff0c\u53ef\u662f \u4ed6\u56db\u80a2\u65e0\u529b\uff0c\u6839\u672c\u65e0\u6cd5\u53cd\u6297\u3002 \u3000\u3000\u90a3\u80d6\u5b50\u662f\u4e2a\u201c\u8001\u73a9\u5bb6\u201d\uff0c\u4ed6\u7ec6\u770b\u4e86\u770b\u88f4\u73cf\uff0c\u53c8\u8e52\u8dda\u5730\u8dd1\u51fa\u95e8\u5916\uff0c\u62ff\u4e86\u676f\u6e05\u6c34\u56de\u6765\uff0c\u542b\u5728 \u5634\u91cc\uff0c\u5657\u5730\uff0c\u55b7\u5f97\u88f4\u73cf\u4e00\u5934\u4e00\u8138\u3002\u539f\u6765\u8fd9\u80d6\u5b50\u4e00\u770b\u4fbf\u77e5\u88f4\u73cf\u88ab\u8ff7\u3002\u4ed6\u5374\u5acc\u88ab\u8ff7\u4e86\u7684\u4e0d\u8fc7\u763e\uff0c \u60f3\u4ee5\u6e05\u6c34\u6765\u5c06\u88f4\u73cf\u5f04\u9192\u624d\u73a9\uff0c\u54ea\u77e5\u5374\u6551\u4e86\u88f4\u73cf\u3002\u201c\u88f4\u73cf\u88ab\u6c34\u4e00\u55b7\uff0c\u795e\u667a\u7acb\u523b\u6e05\u9192\u4e86\uff0c\u6c34\uff0c\u672c \u662f\u201d\u62cd\u82b1\u201c\u7684\u552f\u4e00\u89e3\u836f\u3002\u90a3\u80d6\u5b50\u53c8\u60f3\u4f38\u624b\u53bb\u89e3\u88f4\u73cf\u7684\u8863\u670d\uff0c\u88f4\u73cf\u6b64\u523b\u529b\u6c14\u4e5f\u6062\u590d\u4e86\uff0c\u867d\u7136\u4e0d \u77e5\u9053\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f46\u5374\u77e5\u9053\u5fc5\u5b9a\u4e0d\u662f\u597d\u4e8b\u3002\u3001\u8fd9\u80d6\u5b50\u9152\u610f\u91ba\u7136\uff0c\u4e00\u9762\u7b11\u7740\u8bf4\uff1a\u201c\u5c0f\u4e56 \u4e56\uff0c\u4e0d\u7528\u6015\uff0c\u6765\u2014\u2014\u201d\u3001\u88f4\u73cf\u5927\u6012\uff0c\u53cc\u65f6\u4e00\u7528\u529b\uff0c\u4ece\u5e8a\u4e0a\u7ffb\u4e86\u8d77\u6765\uff0c\u90a3\u80d6\u5b50\u563b\u5f00\u5927\u5634\u7b11\u9053\uff1a \u201c\u5c0f\u4e56\u4e56\uff0c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d\u8bdd\u672a\u8bb2\u5b8c\uff0c\u88ab\u88f4\u73cf\u7830\u5730\u4e00\u62f3\uff0c\u6253\u5728\u9f3b\u6881\u4e0a\uff0c\u75db\u5f97\u54ce\u54df\u4e00\u58f0\uff0c\u8fde\u773c \u6cea\u90fd\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u80d6\u5b50\u5927\u58f0\u9a82\u9053\uff1a\u201c\u5c0f\u81ed\u8d27\uff0c\u4f60\u75af\u4e86\u3002\u201d\u88f4\u73cf\u66f4\u6012\uff0c\u53c8\u671d\u80d6\u5b50\u9762\u4e0a\u6253\u4e86\u4e00\u62f3\uff0c\u4ed6\u6210\u529f\u867d \u4e0d\u4f73\uff0c\u4f46\u7ec3\u4e86\u8fd9\u4e48\u591a\u5e74\u6b66\u529f\u7684\u4eba\uff0c\u8eab\u8eaf\u81ea\u7136\u6bd4\u522b\u4eba\u5f3a\u4e9b\uff0c\u529b\u6c14\u4e5f\u81ea\u7136\u6bd4\u5e38\u4eba\u5927\u4e9b\uff0c\u8fd9\u4e00\u62f3\u90a3 \u80d6\u5b50\u600e\u4f1a\u518d\u6328\u5f97\u4f4f\uff1f \u3000\u3000\u88f4\u73cf\u6012\u6781\uff0c\u53c8\u671d\u90a3\u80d6\u5b50\u6253\u4e86\u51e0\u62f3\uff0c\u6253\u5f97\u90a3\u80d6\u5b50\u53eb\u82e6\u8fde\u5929\uff0c\u75db\u5f97\u9ad8\u58f0\u5927\u558a\uff1a\u201c\u5feb\u6551\u547d \u5440\uff01\u201d \u3000\u3000\u63a5\u7740\uff0c\u4e00\u9635\u96f6\u4e71\u7684\u697c\u68af\u58f0\uff0c\u8dd1\u4e0a\u6765\u4e24\u4e2a\u5f6a\u5f62\u5927\u6c49\uff0c\u60f3\u662f\u6b64\u5730\u7684\u6253\u624b\uff0c\u542c\u5230\u6402\u4e0a\u7684\u58f0\u97f3\uff0c \u8dd1\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u54ea\u77e5\u88f4\u73cf\u90a3\u623f\u95f4\u7684\u95e8\uff0c\u88ab\u90a3\u80d6\u5b50\u5728\u91cc\u9762\u6263\u4e0a\u4e86\uff0c\u662f\u4ee5\u90a3\u4e24\u4e2a\u6253\u5bfb\uff0c\u5728\u5916\u9762\u7a7a\u81ea\u7740\u6025\uff0c\u5374 \u8fdb\u4e0d\u6765\u3002 \u3000\u3000\u88f4\u73cf\u51fa\u62f3\u5982\u96e8\uff0c\u5c06\u90a3\u80d6\u5b50\u6253\u5f97\u6740\u732a\u822c\u4e71\u53eb\u3002\u4f46\u53eb\u58f0\u8d8a\u6765\u8d8a\u5fae\u5f31\uff0c\u60f3\u662f\u773c\u89c1\u4e0d\u884c\u4e86\uff0c\u90a3\u4e24 \u4e2a\u6253\u624b\u8d8a\u542c\u8d8a\u4e0d\u5bf9\uff0c\u518d\u987e\u4e0d\u5f97\u4ec0\u4e48\uff0c\u4e24\u4eba\u4e00\u8d77\u7528\u529b\uff0c\u60f3\u8fd9\u79cd\u623f\u5b50\uff0c\u600e\u7981\u5f97\u4e24\u4eba\u4e00\u63a8\uff0c\u54d7\u5566\u4e00 \u58f0\uff0c\u623f\u95e8\u7adf\u88ab\u63a8\u6563\u4e86\uff0c\u90a3\u4e24\u4e2a\u6253\u624b\u8dcc\u8dcc\u649e\u649e\u5730\u51b2\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u6b64\u65f6\u88f4\u73cf\u6b63\u9a91\u5728\u80d6\u5b50\u8eab\u4e0a\uff0c\u6253\u5f97\u90a3\u80d6\u5b50\u5df2\u7ecf\u53ea\u6709\u4eba\u6c14\u6ca1\u6709\u51fa\u6c14\u4e86\uff0c\u6253\u624b\u4eec\u6012\u9a82\u9053\uff1a\u201c\u5c0f \u5154\u5d3d\u5b50\uff0c\u6562\u60c5\u4f60\u6d3b\u5f97\u6709\u70b9\u4e0d\u8010\u70e6\u4e86\uff01\u201d\u4f38\u5f00\u84b2\u638c\u822c\u7684\u5927\u624b\uff0c\u4e00\u628a\u63ea\u4f4f\u88f4\u73cf\u7684\u9886\u5b50\uff0c\u5c31\u5f80\u4e0b \u62d6\u3002 \u3000\u3000\u60f3\u88f4\u73cf\u5e74\u7eaa\u5c1a\u8f7b\uff0c\u6b66\u529f\u53c8\u6ca1\u6709\u5f97\u8fc7\u771f\u4f20\uff0c\u518d\u52a0\u4e0a\u8eab\u6750\u5e76\u4e0d\u9ad8\u5927\uff0c\u600e\u662f\u8fd9\u4e24\u4e2a\u726f\u725b\u822c\u7684\u5927 \u6c49\u7684\u5bf9\u624b\uff0c\u88ab\u4ed6\u4eec\u62d6\u5f97\u76f4\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u623f\u95f4\u5927\u5c0f\uff0c\u4e24\u4e2a\u5927\u6c49\u5728\u623f\u91cc\u6839\u672c\u65bd\u5c55\u4e0d\u5f00\u624b\u811a\uff0c\u4e8e\u662f\u4ed6\u4eec\u62d6\u4e86\u88f4\u73cf\u51fa\u95e8\uff0c\u5f20\u5f00\u624b\u638c\uff0c\u5c31 \u8981\u53bb\u6247\u88f4\u73cf\u7684\u8033\u5149\uff0c\u4e00\u9762\u9a82\u9053\uff1a\u201c\u5c0f\u5154\u5d3d\u5b50\uff0c\u4f60\u4e5f\u4e0d\u6253\u542c\u6253\u542c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u5c31\u5728\u8fd9\u513f\u4f5c \u6b7b\uff01\u201d \u3000\u3000\u88f4\u73cf\u88ab\u8fd9\u4e24\u4e2a\u6c49\u5b50\u6293\u4f4f\uff0c\u52a8\u4e5f\u52a8\u4e0d\u4e86\uff0c\u4f46\u4ed6\u7a76\u7adf\u662f\u7ec3\u5bb6\u5b50\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u624b\u8098\u5f80\u5916\u731b\u649e\uff0c \u7830\u5730\uff0c\u5728\u8fd9\u4e24\u4e2a\u5927\u6c49\u808b\u4e0b\u51fb\u4e86\u91cd\u91cd\u7684\u4e00\u4e0b\uff0c\u90a3\u4e24\u4e2a\u5927\u6c49\u75db\u6781\u800c\u53eb\uff0c\u624b\u4e5f\u4e0d\u7981\u677e\u4e86\u5f00\u6765\u3002 \u3000\u3000\u88f4\u73cf\u593a\u8def\u5c31\u60f3\u5f80\u4e0b\u9003\uff0c\u90a3\u4e24\u4e2a\u6253\u624b\u600e\u80af\u653e\u8fc7\u4ed6\uff0c\u9a82\u9053\uff1a\u201c\u4eca\u5929\u5927\u7237\u975e\u597d\u597d\u6cbb\u6cbb\u4f60\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u77e5\u4e0d\u662f\u8fd9\u4e24\u4e2a\u6c49\u5b50\u7684\u5bf9\u624b\uff0c\u6697\u53eb\u8981\u7cdf\uff0c\u76ee\u5149\u56db\u626b\uff0c\u5374\u770b\u5230\u5eca\u8fb9\u7684\u7a97\u6237\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u5728\u4ed6\u6ca1\u6709\u6e05\u9192\u4ee5\u524d\uff0c\u4ed6\u6240\u7ecf\u5386\u8fc7\u7684\u4e8b\uff0c\u4ed6\u5168\u7136\u6726\u80e7\u4e00\u7247\uff0c\u53ea\u6709\u4e9b\u6de1\u6de1\u7684\u8f6e\u5ed3\uff0c\u4ed6\u5f53\u7136\u4e5f \u4e0d\u77e5\u9053\u662f\u697c\u4e0a\u8fd8\u662f\u697c\u4e0b\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6697\u5fd6\uff1a\u201c\u62fc\u7740\u6328\u8fd9\u4e24\u4e2a\u6c49\u5b50\u4e00\u62f3\uff0c\u5f80\u7a97\u53e3\u8df3\u51fa\u53bb\u624d\u8bf4\u3002\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u4e24\u4e2a\u6c49\u5b50\u53c8\u5411\u4ed6\u51b2\u4e86\u8fc7\u6765\uff0c\u4ed6\u5de6\u624b\u4e00\u6321\uff0c\u53f3\u62f3\u4f38\u51fa\u53bb\u6253\u90a3\u6c49\u5b50\u7684\u80f8\u819b\uff0c\u90a3\u6c49\u5b50\u65b9 \u624d\u7740\u4e86\u4ed6\u4e00\u8bb0\u65f6\u62f3\uff0c\u6328\u5f97\u4e0d\u8f7b\uff0c\u6b64\u523b\u5012\u4e5f\u4e0d\u6562\u5927\u610f\uff0c\u4e5f\u662f\u5de6\u624b\u4e00\u6321\uff0c\u53f3\u62f3\u7830\u5730\u6253\u5728\u88f4\u73cf\u80a9 \u4e0a\u3002 \u3000\u3000\u54ea\u77e5\u88f4\u73cf\u5fc3\u91cc\u65e9\u6709\u6253\u7b97\uff0c\u80a9\u5934\u867d\u7136\u6328\u4e86\u4e00\u8bb0\uff0c\u4ed6\u4e5f\u4e0d\u7406\uff0c\u5934\u4e00\u4f4e\uff0c\u4ece\u90a3\u6c49\u5b50\u7684\u5de6\u81c2\u5f2f\u4e0b \u94bb\u4e86\u51fa\u6765\uff0c\u7528\u529b\u4e00\u8df3\uff0c\u8df3\u5728\u7a97\u53f0\u4e0a\uff0c\u5934\u4e5f\u4e0d\u6562\u56de\uff0c\u671b\u4e5f\u4e0d\u6562\u671d\u4e0b\u671b\u4e00\u773c\uff0c\u7eb5\u8eab\u5c31\u5f80\u4e0b\u8df3\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u697c\u4e0d\u9ad8\uff0c\u4f46\u5373\u662f\u8fd9\u6837\uff0c\u5f53\u4ed6\u811a\u63a5\u89e6\u5230\u5730\u9762\u65f6\uff0c\u4ed6\u6d51\u8eab\u4e00\u9707\uff0c\u518d\u4e5f\u7a33\u4e0d\u4f4f\u8eab\u5f62\uff0c\u5c41 \u80a1\u7740\u7740\u5b9e\u5b9e\u5730\u8dcc\u5230\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u81ea\u7136\u8dcc\u5f97\u4e0d\u8f7b\uff0c\u4f46\u4ed6\u6b64\u523b\u9664\u4e86\u4e00\u5fc3\u60f3\u9003\u79bb\u6b64\u5730\u5916\uff0c\u4ec0\u4e48\u4e5f\u987e\u4e0d\u5f97\u4e86\uff0c\u722c\u4e86\u8d77\u6765\uff0c \u4e5f\u4e0d\u8fa8\u65b9\u5411\uff0c\u5c31\u62d4\u8db3\u800c\u5954\u3002 \u3000\u3000\u8fd9\u6761\u5df7\u5b50\u5927\u662f\u85cf\u6c61\u7eb3\u57a2\u4e4b\u5904\uff0c\u6b64\u65f6\u4e24\u8fb9\u5c0f\u697c\u7684\u95e8\u53e3\uff0c\u96f6\u96f6\u656c\u6563\u5730\u5750\u7740\u4e00\u4e9b\u6253\u626e\u5f97\u7537\u4e0d \u7537\uff0c\u5973\u4e0d\u5973\u7684\u201c\u50cf\u59d1\u201d\uff0c\u770b\u89c1\u6709\u4eba\u4ece\u697c\u4e0a\u8df3\u4e0b\u6765\u8dd1\u8d70\uff0c\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u65e2\u4e0d\u60ca\u614c\uff0c\u4e5f\u4e0d\u53bb\u963b\u62e6 \u4ed6\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u6f5c\u5728\u4e8e\u4eba\u6027\u91cc\u7684\u540c\u60c5\u4e4b\u5fc3\uff0c\u8fd9\u4e9b\u4eba\u867d\u7136\u5728\u4e8e\u7740\u8fd9\u4e9b\u89c1\u4e0d\u5f97\u4eba\u7684\u52fe\u5f53\uff0c\u4f46\u5fc3\u91cc\u53c8\u4f55 \u5c1d\u613f\u610f\uff0c\u53ea\u4e0d\u8fc7\u662f\u88ab\u73af\u5883\u6240\u903c\u800c\u5df2\u3002 \u3000\u3000\u88f4\u73cf\u4e24\u773c\u53d1\u9ed1\uff0c\u593a\u8def\u800c\u9003\uff0c\u4ed6\u4eec\u7adf\u6697\u6697\u8ba9\u51fa\u4e00\u6761\u8def\u6765\u3002 \u3000\u3000\u88f4\u73cf\u4e0d\u77e5\u8dd1\u4e86\u591a\u4e45\uff0c\u8def\u4e0a\u7684\u4eba\u90fd\u4ee5\u5947\u5f02\u7684\u76ee\u5149\u770b\u7740\u4ed6\uff0c\u4ee5\u4e3a\u4ed6\u662f\u4e2a\u201c\u5973\u75af\u5b50\u201d\uff0c\u4f46\u5317\u4eac \u57ce\u91cc\u4eba\u6027\u6df3\u6734\uff0c\u90fd\u4e5f\u4e0d\u613f\u591a\u4e8b\u3002 \u3000\u3000\u4ed6\u8dd1\u4e86\u8bb8\u4e45\uff0c\u5b9e\u5728\u8dd1\u4e0d\u52a8\u4e86\uff0c\u7559\u610f\u53bb\u542c\u540e\u9762\uff0c\u77e5\u9053\u6ca1\u6709\u4eba\u8ffd\u8d76\uff0c\u5c31\u6162\u6162\u505c\u4e86\u4e0b\u6765\uff0c\u5598\u7740 \u51e0\u53e3\u6c14\uff0c\u521a\u624d\u6240\u53d1\u751f\u7684\u4e8b\uff0c\u6b64\u65f6\u60f3\u6765\u771f\u50cf\u4e00\u573a\u8352\u5510\u800c\u79bb\u5947\u7684\u6076\u68a6\uff0c\u4ed6\u5e74\u8f7b\u7eaf\u6d01\uff0c\u600e\u4e48\u4f1a\u77e5\u9053 \u8fd9\u5230\u5e95\u662f\u4ec0\u4e48\u52fe\u5f53\u3002 \u3000\u3000\u4ed6\u5f00\u59cb\u518d\u5411\u524d\u8d70\uff0c\u6e10\u6e10\u5b9a\u8fc7\u795e\u6765\uff0c\u56db\u80a2\u6709\u4e9b\u53d1\u8f6f\uff0c\u4e0d\u77e5\u662f\u60ca\u5413\u8fc7\u5ea6\uff0c\u8fd8\u662f\u5927\u997f\u4e86\u3002 \u3000\u3000\u626b\u76ee\u56db\u671b\uff0c\u624d\u770b\u89c1\u8fd9\u91cc\u7adf\u662f\u5317\u4eac\u57ce\u90ca\u6700\u4f4e\u7ea7\u7684\u6240\u5728\uff0c\u56db\u5468\u90fd\u662f\u4e9b\u6728\u677f\u642d\u6210\u7684\u623f\u5b50\uff0c\u623f\u5b50 \u91cc\u4f4f\u7684\u4e5f\u4ff1\u591a\u662f\u4e9b\u5317\u4eac\u57ce\u91cc\u6700\u4f4e\u5c42\u7684\u4eba\u7269\u3002 \u3000\u3000\u88f4\u73cf\u89c9\u5f97\u6240\u6709\u7684\u4eba\u90fd\u5728\u671b\u7740\u81ea\u5df1\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u81ea\u5df1\u8eab\u4e0a\u7a7f\u7740\u7684\u662f\u5973\u5b50\u8863\u886b\uff0c\u811a\u4e0b\u5374\u7a7f\u4e86 \u4e00\u53cc\u7537\u5b50\u7528\u7684\u84b2\u5e95\u5feb\u9774\u3002 \u3000\u3000\u8fd9\u6253\u626e\u7684\u786e\u662f\u4e0d\u4f26\u4e0d\u7c7b\u5230\u4e86\u6781\u70b9\uff0c\u6b64\u523b\u6ca1\u6709\u955c\u5b50\uff0c\u4ed6\u65e0\u6cd5\u77e5\u9053\u81ea\u5df1\u9762\u4e0a\u7684\u5f62\u72b6\uff0c\u4f46\u72fc\u72c8 \u4e4b\u6001\uff0c\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u6709\u4e9b\u7ad9\u5728\u95e8\u53e3\u7684\u5987\u5b7a\u6307\u7740\u4ed6\u7a83\u7b11\uff0c\u4ed6\u8138\u4e00\u7ea2\uff0c\u4f4e\u7740\u5934\u5c31\u5f80\u8352\u50fb\u4e4b\u5904\u8d70\uff0c\u60f3\u9003\u5f00\u8fd9\u4e9b\u5632\u7b11 \u7684\u76ee\u5149\u3002 \u3000\u3000\u8fd9\u662f\u4eba\u4e4b\u5e38\u60c5\uff0c\u5f53\u81ea\u5df1\u89c9\u5f97\u81ea\u5df1\u89c1\u4e0d\u5f97\u4eba\u65f6\uff0c\u5c31\u60f3\u53bb\u65e0\u4eba\u4e4b\u5904\u3002 \u3000\u3000\u88f4\u73cf\u8d8a\u8d70\u4eba\u8d8a\u5c11\uff0c\u6b64\u523b\u65e9\u5df2\u4eba\u591c\uff0c\u6625\u5929\u7684\u665a\u98ce\u4ecd\u6709\u6599\u5ced\u4e4b\u610f\uff0c\u6625\u8349\u6e10\u751f\uff0c\u6625\u866b\u591c\u9e23\uff0c\u4ed6 \u5fae\u5fae\u89c9\u5f97\u6709\u4e9b\u51b7\uff0c\u5fc3\u4e2d\u7684\u601d\u6f6e\uff0c\u50cf\u6f6e\u6c34\u4e00\u6837\u5954\u817e\u800c\u751f\u3002 \u3000\u3000\u4eba\u6d77\u832b\u832b\uff0c\u4ed6\u7adf\u65e0\u4f9d\u5f52\u4e4b\u5904\uff0c\u4ed6\u6b64\u65f6\u82e5\u662f\u7a0d\u5fae\u8f6f\u5f31\u4e00\u4e9b\uff0c\u5c31\u4f1a\u7acb\u523b\u56de\u5230\u98de\u9f99\u9556\u5c40\u91cc\u53bb\uff0c \u56e0\u4e3a\u90a3\u91cc\u81f3\u5c11\u662f\u5b89\u5168\u7684\u3002 \u3000\u3000\u4f46\u662f\u5929\u8d4b\u7684\u5014\u5f3a\u6027\u683c\uff0c\u5374\u4f7f\u4ed6\u5b81\u613f\u6328\u51b7\uff0c\u53d7\u997f\uff0c\u4e5f\u8e48\u8e48\u800c\u884c\uff0c\u89c9\u5f97\u773c\u775b\u6709\u4e9b\u6e7f\u6da6\uff0c\u7adf\u7136 \u5feb\u6d41\u773c\u6cea\u4e86\uff0c\u4ed6\u8fde\u5fd9\u538b\u5236\u4f4f\u81ea\u5df1\u60f3\u54ed\u7684\u610f\u5ff5\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u8fd9\u4e0d\u662f\u7537\u5b50\u6c49\u5927\u4e08\u592b\u7684\u884c\u5f84\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u542c\u5230\u80cc\u540e\u4eff\u4f5b\u6709\u7a83\u7a83\u79c1\u8bed\u4e4b\u58f0\uff0c\u8d76\u7d27\u56de\u5934\u53bb\u770b\uff0c\u591c\u8272\u6de1\u832b\u4e2d\uff0c\u53ea\u770b\u5230\u6709\u51e0\u6761\u4eba \u5f71\u8ddf\u5728\u4ed6\u540e\u9762\uff0c\u4e5f\u4e0d\u77e5\u5728\u6253\u7740\u4ec0\u4e48\u7b97\u76d8\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u53c8\u5f00\u59cb\u8df3\u4e86\u8d77\u6765\uff0c\u6b64\u523b\u4ed6\u7adf\u6210\u4e86\u60ca\u5f13\u4e4b\u9e1f\uff0c\u5bf9\u4ec0\u4e48\u90fd\u6000\u6709\u754f\u60e7\u4e4b\u5fc3\uff0c\u4e8e\u662f\u4ed6\u8d70\u5f97 \u5feb\u4e86\u4e9b\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u51e0\u6761\u4eba\u5f71\u4e5f\u8ddf\u7740\u4ed6\u8d8a\u8d70\u8d8a\u5feb\uff0c\u4ed6\u6697\u5730\u53eb\u82e6\uff1a\u201c\u600e\u4e48\u6211\u8001\u78b0\u5230\u8fd9\u4e9b\u5012\u9709\u4e8b\uff1f\u201d\u811a\u4e0b \u4e00\u4e0d\u7559\u5fc3\uff0c\u78b0\u7740\u4e00\u5757\u77f3\u5b50\uff0c\u8dcc\u5012\u4e86\u3002 \u3000\u3000\u90a3\u51e0\u4eba\u5f71\u4e00\u9635\u54c4\u7b11\uff0c\u6d8c\u4e86\u4e0a\u6765\uff0c\u90fd\u662f\u4e9b\u8863\u886b\u4e0d\u6574\u7684\u6d41\u6c13\u5730\u75de\uff0c\u5e74\u7eaa\u90fd\u5f88\u8f7b\uff0c\u5934\u4e0a\u659c\u6234\u7740 \u74dc\u76ae\u5c0f\u5e3d\uff0c\u8896\u5b50\u633d\u5f97\u9ad8\u9ad8\u7684\u3002 \u3000\u3000\u90a3\u4e9b\u4eba\u6309\u4f4f\u88f4\u73cf\uff0c\u6709\u7684\u5c31\u5728\u4ed6\u8eab\u4e0a\u8138\u4e0a\u4e71\u6478\uff0c\u7b11\u8d77\u6765\u7684\u65f6\u5019\uff0c\u58f0\u97f3\u91cc\u9690\u9690\u542b\u7740\u8272\u60c5\u7684\u610f \u5473\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u4e00\u52a8\uff0c\u604d\u7136\u4e86\u89e3\u5230\u4ed6\u4eec\u7684\u7528\u610f\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u5c06\u6211\u5f53\u6210\u4e86\u5973\u4eba\u3002\u201d\u91cc\u4e0d \u7981\u53c8\u6c14\uff0c\u53c8\u597d\u7b11\uff0c\u53c8\u7740\u6025\uff0c\u624b\u811a\u62fc\u547d\u5730\u6323\u624e\u7740\uff0c\u600e\u5948\u90a3\u51e0\u4e2a\u5c0f\u5b50\u4ea6\u662f\u5e74\u8f7b\u529b\u58ee\uff0c\u518d\u52a0\u4e0a\u4eba\u53c8 \u591a\uff0c\u4ed6\u867d\u7136\u7528\u5c3d\u4e86\u5168\u8eab\u529b\u6c14\u6323\u624e\uff0c\u4f46\u662f\u4e5f\u6ca1\u6709\u7528\u3002 \u3000\u3000\u90a3\u51e0\u4e2a\u5730\u75de\u7b11\u58f0\u8d8a\u6765\u8d8a\u5927\uff0c\u6709\u7684\u4f38\u624b\u53bb\u89e3\u4ed6\u7684\u8863\u670d\uff0c\u4e00\u9762\u8bf4\uff1a\u201c\u8fd9\u51e0\u5929\u6b63\u6ca1\u94b1\uff0c\u53c8\u95f7\u5f97 \u614c\uff0c\u8fd9\u5c0f\u59d0\u771f\u662f\u5929\u4e0a\u9001\u6765\u7684\u5b9d\u8d27\u3002\u201d \u3000\u3000\u88f4\u73cf\u7740\u6025\u5f97\u53eb\u4e86\u8d77\u6765\uff0c\u6b64\u65f6\u5728\u8fd9\u6837\u7684\u60c5\u51b5\u4e0b\uff0c\u96be\u602a\u4ed6\u7740\u6025\uff0c\u8fd9\u65f6\uff0c\u4ed6\u53c8\u4e0d\u7981\u6697\u602a\u81ea\u5df1\u7684 \u7b28\uff1a\u201c\u5047\u5982\u6211\u6b66\u529f\u7ec3\u597d\u4e86\uff0c\u53c8\u6709\u8c01\u6562\u6765\u6b3a\u8d1f\u6211\uff1f\u201d \u3000\u3000\u811a\u4e00\u8e22\uff0c\u867d\u5c06\u4e00\u4eba\u8e22\u5012\u4e86\uff0c\u4f46\u53e6\u4e00\u4eba\u5374\u53c8\u538b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u66ae\u5730\uff0c\u8fdc\u8fdc\u6709\u8e44\u58f0\u4f20\u6765\uff0c\u5728\u9759\u591c\u91cc\u663e\u7136\u5206\u5916\u523a\u8033\uff0c\u90a3\u51e0\u4e2a\u5730\u75de\u4e92\u76f8\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\u3002\u201d \u90fd\u505c\u4f4f\u4e86\u624b\uff0c\u7559\u610f\u53bb\u542c\u3002 \u3000\u3000\u88f4\u73cf\u6697\u79f0\u4fa5\u5e78\uff0c\u53c8\u6015\u90a3\u4eba\u4e0d\u5230\u8fd9\u8fb9\u6765\uff0c\u626f\u5f00\u5589\u5499\u53c8\u53eb\u4e86\u51e0\u58f0\uff0c\u5374\u88ab\u4e00\u4e2a\u6c49\u5b50\u5c06\u53e3\u63a9\u4f4f \u4e86\uff0c\u4e00\u9762\u8bf4\uff1a\u201c\u4f60\u518d\u53eb\u5927\u7237\u5c31\u5bb0\u4e86\u4f60\u3002\u201d \u3000\u3000\u90a3\u8e44\u58f0\u7adf\u8d8a\u884c\u8d8a\u8fdc\uff0c\u4ece\u65c1\u8fb9\u8d70\u8fc7\u53bb\u4e86\uff0c\u8fd9\u4e9b\u65e0\u8d56\u6c49\u5b50\u53c8\u5f00\u59cb\u884c\u52a8\uff0c\u88f4\u73cf\u6025\u5f97\u4e0d\u77e5\u600e\u4e48 \u529e\uff0c\u624b\u811a\u518d\u7528\u529b\uff0c\u4e5f\u65e0\u529e\u6cd5\u6323\u5f00\u3002 \u3000\u3000\u54ea\u77e5\u8e44\u58f0\u7a81\u7136\u52a0\u6025\uff0c\u800c\u4e14\u662f\u671d\u8fd9\u4e2a\u65b9\u5411\u5954\u6765\u7684\uff0c\u65e0\u8d56\u4eec\u90fd\u7565\u663e\u60ca\u614c\uff0c\u4f46\u4ed6\u4eec\u4ed7\u7740\u4eba\u591a\uff0c \u4e5f\u4e0d\u6015\uff0c\u72e0\u58f0\u9053\uff1a\u201c\u6709\u4eba\u95ef\u6765\uff0c\u5927\u7237\u4eec\u5c31\u4e00\u5757\u513f\u4f5c\u7ffb\u4e86\u4ed6\u3002\u201d\u8bdd\u58f0\u672a\u7edd\uff0c\u5df2\u6709\u4e00\u9a91\u5954\u6765\uff0c\u90a3 \u901f\u5ea6\u4eff\u4f5b\u662f\u548c\u8e44\u58f0\u4e00\u8d77\u5230\u6765\u7684\uff0c\u786e\u5b9e\u60ca\u4eba\u3002 \u3000\u3000\u90a3\u9a6c\u901a\u4f53\u7eaf\u767d\uff0c\u5230\u4e86\u4ed6\u4eec\u9762\u524d\uff0c\u6253\u4e86\u4e2a\u76d8\u65cb\uff0c\u9a6c\u4e0a\u7684\u9a91\u58eb\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c01\uff1f\u5728\u8fd9\u91cc \u5e72\u4ec0\u4e48\uff1f\u201d\u88f4\u73cf\u5927\u559c\uff0c\u603b\u6709\u4eba\u6765\u6551\u4ed6\u4e86\u3002 \u3000\u3000\u90a3\u4e9b\u65e0\u8d56\u559d\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u662f\u4ec0\u4e48\u73a9\u610f\u513f\uff0c\u7adf\u6562\u6765\u7ba1\u5927\u7237\u7684\u95f2\u4e8b\uff0c\u8d81\u65e9\u5939\u7740\u5c3e\u5df4\u2014\u2014\u201d\u8bed \u58f0\u672a\u4e86\uff0c\u5237\u5730\u4e00\u58f0\uff0c\u8bf4\u8bdd\u7684\u90a3\u4eba\u5934\u4e0a\u5df2\u7740\u4e86\u4e00\u97ad\uff0c\u6253\u5f97\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4e9b\u65e0\u8d56\u987f\u65f6\u5927\u4e71\uff0c\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u6562\u6253\u4eba\u3002\u201d\u4e03\u624b\u516b\u811a\u5730\u56f4\u4e86\u4e0a\u53bb\uff0c\u60f3\u5c06\u9a6c\u4e0a\u7684 \u9a91\u58eb\u63ea\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u9a6c\u4e0a\u4eba\u4e00\u58f0\u6012\u53f1\uff0c\u9a6c\u97ad\u96e8\u70b9\u822c\u6253\u5728\u4ed6\u4eec\u8eab\u4e0a\uff0c\u6700\u602a\u7684\u662f\u90a3\u6761\u5c0f\u5c0f\u7684\u9a6c\u97ad\u4e0a\u7adf\u50cf\u6709\u7740\u4e0a\u767e \u65a4\u529b\u6c14\uff0c\u62bd\u5728\u8eab\u4e0a\uff0c\u5947\u75db\u5f7b\u9aa8\u3002 \u3000\u3000\u88f4\u73cf\u5750\u4e86\u8d77\u6765\uff0c\u501f\u7740\u661f\u5149\u4e00\u770b\uff0c\u9a6c\u4e0a\u4eba\u9690\u7ef0\u7ef0\u5730\u53ef\u770b\u5f97\u51fa\u662f\u4e00\u4e2a\u4e66\u751f\uff0c\u5e74\u7eaa\u4e5f\u4e0d\u5927\uff0c\u8fd9 \u4ece\u4ed6\u7684\u53e3\u97f3\u4e0a\u53ef\u4ee5\u542c\u51fa\u6765\uff0c\u4f46\u662f\u5750\u5728\u9a6c\u4e0a\uff0c\u97ad\u6325\u7fa4\u5c0f\uff0c\u5374\u50cf\u5929\u795e\u4e00\u6837\uff0c\u88f4\u73cf\u6697\u4e2d\u7fa1\u6155\uff0c\u77e5\u9053 \u6b64\u4eba\u4e00\u5b9a\u6709\u9ad8\u6df1\u7684\u6b66\u529f\u3002 \u3000\u3000\u90a3\u4e9b\u6c49\u5b50\u679c\u771f\u65e0\u8d56\uff0c\u88ab\u6253\u5728\u5730\u4e0a\uff0c\u8fd8\u4e0d\u80af\u8d70\uff0c\u9a82\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6253\uff0c\u4f60\u6253\u3002\u201d\u6eda\u5728\u5730\u4e0a\u53bb \u62b1\u9a6c\u811a\uff0c\u54ea\u77e5\u90a3\u9a6c\u975e\u51e1\u7269\uff0c\u811a\u4e00\u626c\uff0c\u5c06\u90a3\u4eba\u8e22\u5f97\u95ed\u8fc7\u6c14\u53bb\uff0c\u9a6c\u4e0a\u4eba\u4e5f\u5927\u6012\uff0c\u9a6c\u97ad\u5ffd\u5730\u6539\u6325\u4e3a \u70b9\uff0c\u8f6f\u8f6f\u7684\u9a6c\u97ad\u5230\u4e86\u4ed6\u624b\u4e0a\u7adf\u50cf\u662f\u68cd\u5b50\u4f3c\u7684\uff0c\u968f\u624b\u4e00\u70b9\uff0c\u98ce\u58f0\u98d3\u7136\uff0c\u7ade\u70b9\u5411\u4e00\u4eba\u7684\u201c\u80a9\u4e95 \u7a74\u201d\u3002 \u3000\u3000\u8fd9\u79cd\u4ee5\u8f6f\u5175\u5203\u70b9\u7a74\u7684\u624b\u6cd5\uff0c\u5df2\u662f\u6b66\u6797\u7f55\u7779\u7684\u4e86\uff0c\u4f55\u51b5\u4ed6\u6240\u4f7f\u7684\u53ea\u662f\u6761\u9a6c\u97ad\u3002\u90a3\u4e9b\u65e0\u8d56\u51e0 \u65f6\u9047\u5230\u8fc7\u8fd9\u79cd\u7edd\u9876\u7684\u8eab\u624b\uff0c\u6643\u773c\u4e4b\u95f4\uff0c\u5df2\u88ab\u4ed6\u70b9\u5012\u4e24\u4e2a\uff0c\u8eba\u5728\u5730\u4e0a\u518d\u4e5f\u722c\u4e0d\u8d77\u6765\u4e86\u3002 \u3000\u3000\u90a3\u4e9b\u65e0\u8d56\u8fd9\u624d\u5927\u9a87\uff0c\u9ad8\u558a\u9053\uff1a\u201c\u6740\u4eba\u5566\u3002\u201d\u843d\u8352\u800c\u9003\u3002 \u3000\u3000\u88f4\u73cf\u6b66\u529f\u867d\u4e0d\u597d\uff0c\u4f46\u4ed6\u751f\u957f\u5728\u6b66\u6797\u4e16\u5bb6\u3002\u5e73\u65e5\u8033\u6684\u76ee\u67d3\uff0c\u5374\u8bc6\u8d27\u5f97\u5f88\uff0c\u6b64\u523b\u89c1\u4e86\u9a6c\u4e0a\u9a91 \u58eb\u7684\u624b\u6cd5\uff0c\u5927\u60ca\u5fd6\u9053\uff1a\u201c\u8fd9\u4eba\u6b66\u529f\u771f\u9ad8\uff01\u201d \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\u671b\u7740\u90a3\u80a1\u4eba\u7684\u80cc\u5f71\uff0c\u5fae\u5fae\u51b7\u7b11\u3002\u88f4\u73cf\u7ad9\u4e86\u8d77\u6765\uff0c\u60f3\u53bb\u8c22\u8c22\u4eba\u5bb6\uff0c\u62ac\u5934\u4e00\u671b\uff0c \u770b\u89c1\u90a3\u4eba\u904d\u4f53\u7eaf\u767d\uff0c\u76ee\u5982\u6717\u661f\uff0c\u5728\u9ed1\u6697\u4e2d\u95ea\u95ea\u53d1\u5149\uff0c\u518d\u4f4e\u5934\u4e00\u770b\u81ea\u5df1\uff0c\u81ea\u5351\u4e4b\u611f\uff0c\u53c8\u6cb9\u7136\u800c \u751f\u3002 \u3000\u3000\u90a3\u4eba\u4e5f\u4f4e\u7740\u5934\uff0c\u4ed4\u7ec6\u770b\u4e86\u4ed6\u534a\u664c\uff0c\u7a81\u7136\u9053\uff1a\u201c\u4f60\u7684\u5bb6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4e00\u6115\uff0c\u5343\u6101\u4e07\u611f\uff0c\u9f50\u5730\u515c\u4e0a\u5fc3\u5934\uff0c\u6697\u5fd6\uff1a\u4eba\u5bb6\u5e74\u9f84\u548c\u6211\u5dee\u4e0d\u591a\uff0c\u6b66\u529f\u5374\u4e0d\u77e5\u6bd4\u6211\u5f3a \u591a\u5c11\u500d\uff0c\u5509\uff0c\u6211\u7b97\u4ec0\u4e48\uff1f\u6211\u6709\u4ec0\u4e48\uff1f\u6211\u4ec0\u4e48\u90fd\u6ca1\u6709\uff01\u8138\u4e0a\u7684\u795e\u8272\uff0c\u4e0d\u7981\u975e\u5e38\u9eef\u7136\u3002 \u3000\u3000\u90a3\u4eba\u89c1\u4ed6\u4e0d\u56de\u7b54\uff0c\u4f3c\u4e4e\u4e0d\u8010\u70e6\u5730\u95ee\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u5bb6\u5417\uff0c\u600e\u5730\u4e0d\u8bf4\u8bdd\uff1f\u201d\u88f4\u73cf\u70b9\u4e86\u70b9\u5934\uff0c \u5ffd\u5730\u6df1\u6df1\u5f2f\u4e86\u8170\u53bb\uff0c\u515c\u5934\u4e00\u63d6\uff0c\u6389\u5934\u4fbf\u8d70\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fc3\u91cc\u7684\u96be\u53d7\uff0c\u7edd\u975e\u4efb\u4f55\u8a00\u8bcd\u53ef\u4ee5\u5f62\u5bb9\u5f97\u51fa\u7684\uff0c\u5589\u7ba1\u91cc\u50cf\u662f\u5835\u585e\u7740\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e00\u53e5 \u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u53c8\u5f80\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u90a3\u4eba\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u672c\u6765\u6beb\u65e0\u8868\u60c5\u7684\u8138\u4e0a\uff0c\u6b64\u65f6\u5374\u50cf\u6d41\u9732\u51fa\u4e00\u4e1d\u601c \u60af\u7684\u795e\u8272\u3002 \u3000\u3000\u4ed6\u7528\u9a6c\u97ad\u7684\u67c4\u6572\u7740\u9a6c\u978d\uff0c\u5fc3\u91cc\u50cf\u662f\u975e\u5e38\u70e6\u8e81\uff0c\u7a81\u5730\uff0c\u4ed6\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5973\u5b69\u5b50\uff0c\u5feb\u56de \u6765\u3002\u201d \u3000\u3000\u88f4\u73cf\u505c\u4f4f\u4e86\u811a\uff0c\u4ed6\u77e5\u9053\u90a3\u4eba\u53e3\u4e2d\u7684\u201c\u5973\u5b69\u5b50\u201d\u5c31\u662f\u6307\u7684\u4ed6\uff0c\u4f46\u662f\u4ed6\u4e5f\u4e0d\u613f\u610f\u89e3\u91ca\uff0c\u56e0\u4e3a \u4ed6\u89c9\u5f97\u81ea\u5df1\u592a\u4e22\u4eba\uff0c\u4eba\u5bb6\u82e5\u95ee\u8d77\u4ed6\u7a7f\u4e0a\u5973\u886b\u7684\u539f\u56e0\u6765\uff0c\u8ba9\u4ed6\u600e\u751f\u53bb\u5206\u8bf4\uff0c\u4ed6\u597d\u80dc\u4e4b\u5fc3\u7edd\u5f3a\uff0c \u5bf9\u522b\u4eba\u7684\u601c\u60af\u4e0e\u540c\u60c5\uff0c\u4ed6\u90fd\u4e0d\u613f\u610f\u63a5\u53d7\uff0c\u5bf9\u522b\u7684\u4eba\u803b\u7b11\uff0c\u4ed6\u66f4\u75db\u6068\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u8fd8\u662f\u8d70\u4e86\u56de\u53bb\uff0c\u7ad9\u5728\u90a3\u4eba\u7684\u9a6c\u524d\uff0c\u90a3\u4eba\u4f4e\u4e0b\u5934\u6765\u770b\u4e86\u4ed6\u534a\u664c\uff0c\u8138\u4e0a\u4f3c\u4e4e\u6709\u60ca\u5947\u4e4b \u8272\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6ca1\u6709\u5bb6\uff0c\u8981\u4e0d\u8981\u8ddf\u7740\u6211\u8d70\uff1f\u201d\u4ed6\u4ef0\u5929\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u8bf4 \u9053\uff1a\u201c\u6211\u4e5f\u662f\u4e2a\u6ca1\u6709\u5bb6\u7684\u4eba\u3002\u201d\u4e00\u53e3\u7684\u6c5f\u5357\u53e3\u97f3\uff0c\u8bf4\u5f97\u53c8\u5feb\u3002 \u3000\u3000\u4f46\u58f0\u97f3\u5374\u4e5f\u542b\u7740\u51c4\u51c9\u7684\u5473\u9053\uff0c\u88f4\u73cf\u542c\u4e86\uff0c\u76f8\u601c\u4e4b\u5fc3\u5927\u8d77\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u8bf4\u8bdd\uff0c\u90a3\u4eba\u53c8\u9053\uff1a \u201c\u6211\u8fd8\u53ef\u4ee5\u4f20\u4e9b\u6b66\u529f\u7ed9\u4f60\uff0c\u8ba9\u4f60\u4e0d\u53d7\u522b\u4eba\u6b3a\u8d1f\uff0c\u81f3\u4e8e\u4f60\u80fd\u5b66\u5f97\u4e86\u591a\u5c11\uff0c\u90a3\u5c31\u8981\u770b\u4f60\u81ea\u5df1 \u4e86\u3002\u201d \u3000\u3000\u8a00\u4e0b\u5927\u6709\u81ea\u5df1\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u522b\u4eba\u8fde\u5b66\u90fd\u65e0\u6cd5\u5b66\u5b8c\u4e4b\u610f\u3002 \u3000\u3000\u88f4\u73cf\u8fd9\u4e00\u559c\uff0c\u771f\u662f\u51fa\u4e8e\u610f\u671b\u4e4b\u5916\uff0c\u4f46\u662f\u4ed6\u8f6c\u5ff5\u4e00\u60f3\uff0c\u602f\u602f\u4ed6\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u592a\u7b28\uff0c\u5b66\u6765 \u5b66\u53bb\u603b\u662f\u5b66\u4e0d\u597d\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7565\u73b0\u60ca\u5947\u4e4b\u5bb9\uff0c\u9053\uff1a\u201c\u4f60\u5b66\u8fc7\u6b66\u529f\uff1f\u201d\u88f4\u73cf\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90a3\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u4f60\u7b28\uff1f\u4f60\u4ee5\u524d\u8ddf\u8c01\u5b66\u8fc7\u6b66\u529f\u7684\uff1f\u201d\u88f4\u73cf\u57a3\uff1a\u201c\u9f99\u5f62\u516b\u638c\u6a80 \u660e\u3002\u201d \u3000\u3000\u201c\u4ed6\u6ee1\u4ee5\u4e3a\u81ea\u5df1\u6240\u8bf4\u7684\u8fd9\u540d\u4e00\u5b9a\u4f1a\u4f7f\u8fd9\u4eba\u5403\u4e00\u60ca\uff0c\u54ea\u77e5\u4eba\u5bb6\u542c\u4e86\uff0c\u9f3b\u5b54\u91cc\u51b7\u51b7\u54fc\u4e86\u4e00 \u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u7b97\u4ec0\u4e48\u4e1c\u897f\uff01\u201d\u88f4\u73cf\u4e0d\u89c9\u5927\u5947\uff0c\u987b\u77e5\u9f99\u5f62\u516b\u638c\u6b64\u65f6\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u53ef\u8bf4\u662f\u975e \u540c\u5c0f\u53ef\uff0c\u6b64\u4eba\u542c\u4e86\uff0c\u5374\u5927\u6709\u9119\u89c6\u4e4b\u610f\uff0c\u90a3\u4e48\u6b64\u4eba\u662f\u4f55\u6765\u8def\uff1f \u3000\u3000\u201c\u96be\u9053\u8fd9\u4eba\u7684\u6b66\u529f\u6bd4\u6a80\u5927\u53d4\u8fd8\u9ad8\uff1f\u201d\u88f4\u73cf\u5fc3\u4e2d\u6697\u6697\u5730\u601d\u5fd6\uff0c\u4f46\u770b\u8fd9\u4eba\u5e74\u7eaa\u8f7b\u8f7b\u7684\u6837\u5b50\uff0c \u5374\u53c8\u89c9\u5f97\u81ea\u5df1\u7684\u63a8\u6d4b\u6709\u4e9b\u4e0d\u5408\u7406\u3002 \u3000\u3000\u90a3\u4eba\u7684\u813e\u6c14\u4f3c\u751a\u66b4\u8e81\uff0c\u4e0d\u8010\u70e6\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u8ddf\u4e0d\u8ddf\u6211\u4e00\u8d77\u8d70\uff1f\u201d\u88f4\u73cf\u6697\u5fd6\uff1a\u201c\u65e0\u8bba\u5982 \u4f55\uff0c\u6211\u4e5f\u8981\u8ddf\u8fd9\u4eba\u53bb\u5b66\u5b66\u770b\uff0c\u5047\u5982\u771f\u80fd\u5b66\u597d\u4e86\u2014\u2014\u201d\u4e0b\u9762\u4ed6\u4e0d\u6562\u518d\u53bb\u60f3\uff0c\u56e0\u4e3a\u90a3\u5c31\u662f\u4ed6\u6574\u4e2a \u7684\u5e7b\u68a6\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u53c8\u70b9\u70b9\u5934\uff0c\u90a3\u4eba\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u9a6c\u97ad\u4e00\u6325\uff0c\u90a3\u9a6c\u4e00\u626c\u8e44\uff0c\u5f80\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u9a6c\u4e0a\u4eba\u4e00\u5f2f \u8170\uff0c\u7528\u624b\u53bb\u6284\u88f4\u73cf\u7684\u8170\u3002 \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u5f97\u8170\u4e00\u7d27\uff0c\u6574\u4e2a\u4eba\u817e\u7a7a\u4e86\u8d77\u6765\uff0c\u7136\u540e\u5750\u5230\u90a3\u4eba\u7684\u524d\u9762\uff0c\u4e5f\u662f\u4ed6\u5e74\u7eaa\u592a\u8f7b\uff0c\u6709\u8bb8 \u591a\u4e8b\u90fd\u6ca1\u6709\u8003\u8651\u5230\uff0c\u4ed6\u82e5\u4ed4\u7ec6\u4e00\u60f3\uff0c\u4ee5\u4ed6\u7684\u6253\u626e\u548c\u5f53\u65f6\u7684\u60c5\u51b5\uff0c\u8fd9\u4eba\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u4ed6\u662f\u5973\u7684\uff0c \u4f46\u5374\u8981\u4ed6\u548c\u81ea\u5df1\u4e00\u8d77\u8d70\uff0c\u53c8\u5c06\u4ed6\u62b1\u5728\u8eab\u4e0a\uff0c\u662f\u4e0d\u662f\u4e5f\u50cf\u662f\u5bf9\u201c\u4ed6\u201d\u6000\u6709\u91ce\u5fc3\u5462\uff1f \u3000\u3000\u88f4\u73cf\u5750\u5728\u524d\u9762\uff0c\u90a3\u9a6c\u8dd1\u8d77\u6765\u50cf\u817e\u4e91\u9a7e\u96fe\u4f3c\u7684\uff0c\u8fd9\u662f\u4ed6\u5e73\u751f\u6240\u672a\u66fe\u7ecf\u5386\u8fc7\u7684\u901f\u5ea6\uff0c\u4e0d\u7981\u89c9 \u5f97\u751a\u4e3a\u5174\u594b\u3002 \u3000\u3000\u987b\u77e5\u201c\u901f\u5ea6\u201d\u4e5f\u662f\u4eba\u4eec\u4e00\u79cd\u4eab\u53d7\uff0c\u5c24\u5176\u662f\u7231\u597d\u523a\u6fc0\u7684\u4eba\u4eec\u3002 \u3000\u3000\u88f4\u73cf\u95ed\u8d77\u773c\u6765\u9886\u7565\u8fd9\u5e73\u751f\u7b2c\u4e00\u6b21\u611f\u53d7\u5230\u7684\u611f\u89c9\uff0c\u9f3b\u7aef\u7a81\u7136\u95fb\u5230\u4e00\u4e1d\u6de1\u6de1\u7684\u9999\u6c14\uff0c\u5374\u662f\u4ece \u8eab\u540e\u7684\u90a3\u4eba\u8eab\u4e0a\u53d1\u51fa\u7684\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5947\u602a\uff1a\u201c\u8fd9\u4eba\u8eab\u4e0a\u7684\u6c14\u606f\u600e\u4e48\u50cf\u5973\u4eba\u4e00\u6837\uff1f\u201d\u54ea\u77e5\u90a3\u4eba\u5df2\u5728\u4ed6\u8eab\u540e\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4f60\u662f\u4e2a\u5973\u513f\u5bb6\uff0c\u505a\u4e8b\u8981\u8c28\u614e\u4e9b\uff0c\u4ee5\u540e\u5728\u6ca1\u6709\u5b66\u4f1a\u6b66\u529f\u4e4b\u524d\uff0c\u5343\u4e07\u4e0d\u8981\u51fa\u53bb\u4e00\u4eba\u4e71\u8dd1\u3002\u201d \u3000\u3000\u88f4\u73cf\u542c\u4e86\uff0c\u54ed\u7b11\u4e0d\u5f97\uff0c\u90a3\u4eba\u53c8\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u4f60\u968f\u4fbf\u5c31\u8ddf\u7740\u6211\u8d70\uff0c\u8fd9\u5e78\u597d\u662f\u6211\uff0c\u5982\u679c\u6362\u4e86 \u522b\u4eba\uff0c\u90a3\u4f60\u96be\u514d\u53c8\u8981\u5403\u4e8f\u3002\u201d \u3000\u3000\u88f4\u73cf\u6709\u53e3\u96be\u8a00\uff0c\u7ed3\u7ed3\u5df4\u5df4\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4e00--\u201d\u90a3\u4eba\u5389\u58f0\u9053\uff1a\u201c\u4e0d\u8981\u591a\u8bf4\uff01\u201d\u58f0\u97f3\u867d\u7136\u5f88 \u597d\u542c\uff0c\u4f46\u8bed\u58f0\u5374\u4e25\u5389\u5f97\u5f88\uff0c\u800c\u4e14\u91cc\u9762\u8fd8\u6709\u79cd\u51b7\u51b0\u51b0\u7684\u5473\u9053\uff0c\u4f7f\u4eba\u4e0d\u6562\u4e0d\u542c\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u90a3\u4eba\u53c8\u9053\uff1a\u201c\u4ee5\u540e\u5728\u5916\u9762\uff0c\u4f60\u5c31\u53eb\u6211\u51b7\u5927\u53d4\u597d\u4e86\u3002\u201d\u88f4\u73cf\u542c\u4e86\uff0c\u6697\u6697\u597d\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9 \u4eba\u7684\u5e74\u7eaa\u770b\u6765\u6bd4\u6211\u5927\u4e0d\u4e86\u591a\u5c11\uff0c\u5374\u8981\u6211\u53eb\u4ed6\u5927\u53d4\u3002\u201d\u4f46\u4ed6\u53e3\u4e2d\u8fd8\u662f\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u7b97\u662f\u7b54\u5e94 \u4e86\u3002 \u3000\u3000\u9a6c\u6781\u5feb\u5730\u5954\u8dd1\u4e86\u4e00\u6bb5\uff0c\u5929\u8d8a\u6765\u8d8a\u9ed1\uff0c\u5927\u7ea6\u5df2\u662f\u5b50\u591c\u4e86\u3002 \u3000\u3000\u88f4\u73cf\u4e5f\u4e0d\u77e5\u9053\u5df2\u8dd1\u5230\u4ec0\u4e48\u5730\u65b9\uff0c\u90a3\u4eba\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4ed6\u4e5f\u4e0d\u6562\u95ee\uff0c\u5ffd\u7136\u4ed6\u770b\u5230\u8fdc\u8fdc\u6709\u4e00\u7247\u706f \u706b\uff0c\u60f3\u5fc5\u90a3\u91cc\u6709\u4e2a\u5e02\u96c6\u3002 \u3000\u3000\u90a3\u9a6c\u5411\u524d\u98de\u5954\uff0c\u5230\u4e86\u524d\u9762\uff0c\u624d\u7f13\u7f13\u6536\u4e0b\u6b65\u5b50\u6765\u3002\u88f4\u73cf\u4e00\u770b\uff0c\u6b64\u5904\u679c\u7136\u662f\u4e2a\u5e02\u96c6\uff0c\u800c\u4e14\u8fd8 \u76f8\u5f53\u70ed\u95f9\uff0c\u56e0\u4e3a\u8fd9\u4e48\u665a\u4e86\uff0c\u6b64\u5730\u4ecd\u7136\u706f\u706b\u672a\u7edd\uff0c\u53ea\u662f\u4ed6\u81ea\u5230\u5317\u4eac\u4ee5\u6765\uff0c\u5c31\u6ca1\u6709\u518d\u51fa\u6765\u8fc7\uff0c\u81ea \u7136\u4e5f\u4e0d\u77e5\u9053\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\u3002 \u3000\u3000\u9a6c\u4eba\u4e86\u5e02\u96c6\uff0c\u5c31\u8d70\u5f97\u66f4\u6162\uff0c\u90a3\u4eba\u7684\u624b\u7531\u88f4\u73cf\u8eab\u540e\u6284\u8fc7\u6765\uff0c\u52d2\u4f4f\u9a6c\u57a6\u3002 \u3000\u3000\u88f4\u73cf\u7a81\u7136\u611f\u89c9\u5230\u4ed6\u8eab\u5b50\u8f6f\u8f6f\u7684\uff0c\u5fc3\u91cc\u4e0d\u7981\u5947\u602a\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4eba\u6b66\u529f\u8fd9\u4e48\u597d\uff0c\u600e\u5730\u8eab\u5b50 \u5374\u662f\u8fd9\u4e48\u8f6f\u5462\uff1f\u201d \u3000\u3000\u9a6c\u505c\u5728\u4e00\u5bb6\u6c14\u6d3e\u751a\u5927\u7684\u5ba2\u6808\u95e8\u53e3\uff0c\u90a3\u4eba\u4e0b\u4e86\u9a6c\uff0c\u88f4\u73cf\u4e45\u5c45\u5317\u56fd\uff0c\u81ea\u7136\u4e5f\u4f1a\u9a91\u9a6c\uff0c\u4e5f\u8ddf\u7740 \u8df3\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u4eba\u8138\u4e0a\u53c8\u6709\u60ca\u5947\u4e4b\u8272\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4f1a\u9a91\u9a6c\uff1f\u201d\u4f46\u5374\u5e76\u672a\u7b49\u88f4\u73cf\u7684\u7b54\u590d\uff0c\u5c31\u5148\u8d70\u4e86\u8fdb \u53bb\u3002 \u3000\u3000\u4ed6\u8863\u5c65\u751a\u662f\u534e\u8d35\uff0c\u6240\u9a91\u7684\u9a6c\u53c8\u662f\u5343\u4e2d\u9009\u4e00\u7684\u826f\u9a79\uff0c\u5ba2\u6808\u91cc\u7684\u5c0f\u4e8c\u9605\u4eba\u591a\u77e3\uff0c\u4ec0\u4e48\u4eba\u662f\u4ec0 \u4e48\u6837\u7684\u6765\u8def\uff0c\u4ed6\u4e00\u773c\u5c31\u770b\u5f97\u51fa\u6765\uff0c\u8fde\u5fd9\u8dd1\u8fc7\u6765\u5df4\u7ed3\u4ed6\u8bf4\u9053\uff1a\u201c\u5ba2\u5b98\u6562\u60c5\u662f\u8981\u623f\u95f4\u5417\uff1f\u201d \u3000\u3000\u90a3\u81ea\u79f0\u201c\u51b7\u5927\u53d4\u201d\u7684\u4eba\u4e0d\u8010\u70e6\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u592b\u4eba\u600e\u5730\u8fd8\u4e0d\u8fdb\u6765\u3002\u201d \u3000\u3000\u539f\u6765\u88f4\u73cf\u8fd8\u7ad9\u5728\u95e8\u53e3\uff0c\u6b64\u523b\u542c\u5230\u522b\u4eba\u53eb\u4ed6\u201c\u592b\u4eba\u201d\uff0c\u53ef\u6c14\u53ef\u4e50\uff0c\u4f46\u5374\u4e5f\u4e0d\u597d\u53d1\u4f5c\u51fa\u6765\uff0c \u53ea\u5f97\u6162\u6162\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5c0f\u4e8c\u60ca\u5947\u5730\u671b\u7740\u4ed6\u7684\u811a\uff0c\u539f\u6765\u4ed6\u811a\u4e0a\u4ecd\u7136\u8fd8\u7a7f\u7684\u662f\u90a3\u53cc\u8584\u5e95\u5feb\u9774\uff0c\u201c\u51b7\u5927\u53d4\u201d\u4e5f\u4e0d\u7981\u968f \u7740\u5c0f\u4e8c\u7684\u773c\u5149\u4e00\u671b\uff0c\u4e5f\u662f\u4e00\u76b1\u7709\u3002 \u3000\u3000\u88f4\u73cf\u671b\u7740\u4ed6\u65e0\u53ef\u5948\u4f55\u5730\u4e00\u7b11\uff0c\u6b64\u523b\u706f\u5149\u4e4b\u4e0b\uff0c\u88f4\u73cf\u624d\u5bf9\u4ed6\u770b\u4e2a\u6e05\u695a\uff0c\u4e0d\u7981\u6697\u8d5e\uff1a\u201c\u597d\u6f02 \u4eae\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u201c\u51b7\u5927\u53d4\u201d\u53cc\u7709\u957f\u5782\uff0c\u76ee\u5149\u4e2d\u95ea\u70c1\u7740\u5149\u91c7\uff0c\u5634\u867d\u4e0d\u5c0f\uff0c\u4f46\u4e5f\u5e76\u4e0d\u751a\u5927\uff0c\u9f3b\u5b50\u50cf\u662f \u4e00\u6839\u7389\u62c4\uff0c\u7b14\u76f4\u901a\u5411\u4e0a\u989d\uff0c\u7adf\u6bd4\u88f4\u73cf\u8fd8\u8981\u6f02\u4eae\u4e09\u5206\u3002 \u3000\u3000\u201c\u51b7\u5927\u53d4\u201d\u770b\u5230\u88f4\u73cf\u4e24\u53cc\u773c\u775b\u76f4\u52fe\u52fe\u5730\u770b\u7740\u4ed6\uff0c\u5fc3\u91cc\u4e5f\u5728\u5947\u602a\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u597d\u50cf\u6709\u4e9b\u53e4 \u602a\u3002\u201d \u3000\u3000\u4f46\u662f\u4ed6\u65e0\u8bba\u5982\u4f55\u4e5f\u672a\u66fe\u60f3\u5230\uff0c\u8fd9\u9669\u4e9b\u88ab\u4eba\u5f3a\u66b4\u7684\u201c\u5973\u5b50\u201d\u7adf\u4e0d\u662f\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u966a\u7740\u7b11\u9053\uff1a\u201c\u655d\u5e97\u5168\u5ba2\u6ee1\u4e86\uff0c\u53ea\u5269\u4e0b\u4e00\u95f4\u623f\uff0c\u4e24\u4f4d\u5c31\u5c06\u5c31\u7740\u4f4f\u4e0b\u5427\uff0c\u90a3\u91cc\u8fd8\u7b97\u5e72 \u51c0\u3002\u201d\u4ed6\u773c\u775b\u96ea\u4eae\uff1b\u5df2\u89c9\u8fd9\u4e24\u4eba\u6709\u4e9b\u4e0d\u5bf9\u8def\uff0c\u662f\u4ee5\u8bf4\u8bdd\u7684\u6001\u5ea6\uff0c\u4e5f\u8fdc\u4e0d\u53ca\u65b9\u624d\u90a3\u4e48\u6837\u5df4\u7ed3 \u4e86\u3002 \u3000\u3000\u201c\u51b7\u5927\u53d4\u201d\u4e00\u6447\u624b\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u5feb\u5e26\u6d45\u4eec\u53bb\u3002\u201d\u88f4\u73cf\u81ea\u5e7c\u5c31\u548c\u522b\u4eba\u540c\u623f\u800c\u7761\uff0c\u5f53\u7136\u4e0d \u4f1a\u89c9\u5f97\u6709\u4e9b\u4ec0\u4e48\u4e0d\u4fbf\uff0c\u4f46\u662f\u4ed6\u5374\u6ca1\u6709\u60f3\u5230\u81ea\u5df1\u548c\u4eba\u5bb6\u5916\u8868\u770b\u6765\uff0c\u603b\u662f\u4e00\u7537\u4e00\u5973\uff0c\u90a3\u4e48\u8fd9\u201c\u51b7 \u5927\u53d4\u201d\u600e\u5730\u5374\u53c8\u8981\u548c\u81ea\u5df1\u540c\u623f\u7761\u5462\uff1f\u96be\u9053\u8fd9\u201c\u51b7\u5927\u53d4\u201d\u5fc3\u91cc\uff0c\u4e5f\u6709\u7740\u4ec0\u4e48\u6bdb\u75c5\uff1f \u3000\u3000\u521a\u8d70\u8fdb\u623f\uff0c\u201c\u51b7\u5927\u53d4\u201d\u5c31\u6325\u624b\u53eb\u5c0f\u4e8c\u8d70\u5f00\uff0c\u4e00\u9762\u5173\u8d77\u623f\u95e8\u6765\uff0c\u8bf4\uff1a\u201c\u5feb\u8131\u8863\u670d\u4f11\u606f\uff0c\u660e \u5929\u6211\u4eec\u8fd8\u8981\u4e00\u65e9\u8d76\u8def\u3002\u201d \u3000\u3000\u88f4\u73cf\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u4ed6\u5012\u4e0d\u662f\u4e3a\u522b\u7684\uff0c\u800c\u4e14\u6050\u6015\u201c\u51b7\u5927\u53d4\u201d\u67e5\u95ee\u4ed6\u600e\u4e48\u4f1a\u7a7f\u4e0a\u5973\u5b50\u7684\u8863 \u670d\u3002 \u3000\u3000\u201c\u51b7\u5927\u53d4\u201d\u770b\u89c1\u4ed6\u5750\u5728\u6905\u5b50\u4e0a\u4e0d\u52a8\uff0c\u8138\u4e0a\u4e0d\u89c9\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u597d\u610f\u601d\u662f\u4e0d \u662f\uff1f\u7b49\u4e00\u4f1a\u4f60\u5c31\u77e5\u9053\u6ca1\u6709\u5173\u7cfb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7565\u5fae\u62ed\u4e86\u62ed\u8138\uff0c\u5c31\u89e3\u81ea\u5df1\u7684\u8863\u670d\uff0c\u8131\u53bb\u5916\u8863\uff0c\u8fde\u91cc\u9762\u7684\u77ed\u8902\u90ca\u8131\u4e0b\u4e86\u3002\u88f4\u73cf\u672c\u6765\u5fc3\u4e2d \u5728\u60f3\u7740\u8be5\u600e\u4e48\u6837\u5411\u201c\u51b7\u5927\u53d4\u201d\u8bf4\u81ea\u5df1\u6240\u9047\u5230\u7684\u4e8b\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u4e00\u9897\u5fc3\u51e0\u4e4e\u8981\u8df3\u5230\u8154\u53e3\u4e86\u3002 \u3000\u3000\u539f\u6765\u201c\u51b7\u5927\u53d4\u201d\u8131\u4e86\u8863\u670d\u540e\uff0c\u4e30\u4e73\u9686\u80a1\uff0c\u7adf\u7136\u662f\u4e2a\u5973\u7684\u3002 \u3000\u3000\u5979\u6839\u672c\u6ca1\u6709\u6ce8\u610f\u5230\u88f4\u73cf\u9762\u4e0a\u7684\u8868\u60c5\uff0c\u4e00\u9762\u5e26\u7740\u6559\u8bad\u7684\u53e3\u543b\u8bf4\uff1a\u201c\u4f60\u73b0\u5728\u8be5\u77e5\u9053\u6211\u521a\u624d\u6240 \u8bf4\u7684\u8bdd\u7684\u610f\u601d\u4e86\u5427\uff0c\u6211\u5176\u5b9e\u4e0d\u662f\u7537\u7684\u3002\u201d\u5979\u54fc\u4e86\u4e00\u58f0\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u6211\u8981\u662f\u4e2a\u7537\u4eba\uff0c\u4f60\u5c82\u4e0d\u662f \u53c8\u8981\u5012\u9709\u4e86\u5417\uff1f\u201d \u3000\u3000\u88f4\u73cf\u81ea\u51fa\u4e16\u4ee5\u6765\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u4e00\u4e2a\u5973\u5b50\u5728\u4ed6\u9762\u524d\u8131\u8863\uff0c\u6b64\u523b\u89c1\u4e86\u8fd9\u60c5\u5f62\uff0c\u5fc3\u8df3\u5f97\u50cf \u662f\u8981\u79bb\u8154\u800c\u51fa\uff0c\u9762\u5b54\u4e5f\u6da8\u5f97\u8d64\u7ea2\uff0c\u5413\u5f97\u8d76\u7d27\u4f4e\u4e0b\u5934\u53bb\uff0c\u4e0d\u6562\u518d\u591a\u671d\u201c\u51b7\u5927\u53d4\u201d\u770b\u4e00\u773c\u3002 \u3000\u3000\u201c\u51b7\u5927\u53d4\u201d\u7a81\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u548c\u4f60\u771f\u6709\u7f18\uff0c\u4e00\u770b\u89c1\u4f60\uff0c\u5c31\u89c9\u5f97\u4f60\u5b64\u82e6\u4f36\u4ec3\uff0c\u53d7\u4eba\u6b3a \u8d1f\uff0c\u602a\u53ef\u601c\u7684\uff0c\u6240\u4ee5\u624d\u6536\u4f60\u505a\u5f92\u5f1f\uff0c\u4f60\u522b\u4ee5\u4e3a\u8fd9\u4e48\u7b80\u5355\uff0c\u6050\u6015\u4ee5\u540e\u4f60\u8bf4\u7ed9\u522b\u4eba\u542c\uff0c\u522b\u4eba\u4e5f\u4e0d \u4f1a\u76f8\u4fe1\u5462\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4e00\u62fe\u5934\uff0c\u53ea\u89c9\u201c\u55e1\u201d\u7136\u4e00\u58f0\uff0c\u9762\u5b54\u66f4\u7ea2\u5f97\u50cf\u732a\u809d\u4e00\u6837\u3002 \u3000\u3000\u539f\u6765\u8fd9\u201c\u51b7\u5927\u53d4\u201d\u7adf\u8131\u5f97\u8eab\u65e0\u5bf8\u7f15\uff0c\u8eab\u8eaf\u4e0a\u7f8e\u5999\u7684\u66f2\u7ebf\u548c\u5f27\u5ea6\uff0c\u5728\u706f\u5149\u4e0b\u663e\u5f97\u66f4\u7a81\u51fa \u4e86\u3002 \u3000\u3000\u201c\u51b7\u5927\u53d4\u201d\u60f3\u5fc5\u4e5f\u770b\u5230\u88f4\u73cf\u7684\u7a98\u6001\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u5947\u602a\uff0c\u6211\u4ece\u5c0f\u5c31\u662f\u8fd9\u6837\u7761\u89c9\u7684\u3002\u201d \u3000\u3000\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u4f60\u591a\u5927\u4e86\uff0c\u600e\u5730\u8fd9\u6837\u5bb3\u8e81\uff1f\u5feb\u8131\u8863\u88f3\u7761\u5440\uff0c\u4f60\u770b\u89c1\u6211\u4e5f\u662f\u5973\u7684\uff0c\u8fd8\u6015\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u201c\u51b7\u5927\u4e00\u5927\u53d4\u3002\u201d\u88f4\u73cf\u7ed3\u7ed3\u5df4\u5df4\u4ed6\u8bf4\uff0c\u201c\u4f60\u5feb\u7a7f\u4e0a\u2014\u2014\u6211\u2014\u2014\u6211\u662f\u4e2a\u7537\u4eba\u3002\u201d \u3000\u3000\u201c\u51b7\u5927\u53d4\u201d\u4e00\u60ca\uff0c\u731b\u5730\u5411\u540e\u4e00\u9000\u6b65\uff0c\u5a07\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u786c\u7740\u5934\u76ae\u9053\uff1a\u201c\u6211\u662f\u4e2a\u7537\u4eba\uff0c\u6211\u2014\u2014\u201d\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u201c\u51b7\u5927\u53d4\u201d\u5df2\u4e00\u63a0\u81f3\u524d\uff0c\u88f4 \u73cf\u8fd8\u672a\u53ca\u770b\u6e05\uff0c\u9f3b\u7554\u4e00\u9ebb\uff0c\u5168\u8eab\u7adf\u5b9a\u4f4f\u4e86\u3002\u201c\u51b7\u5927\u53d4\u201d\u7389\u624b\u4e00\u4f38\uff0c\u5728\u4ed6\u80f8\u524d\u4e00\u6478\uff0c\u7389\u9762\u4e5f\u7acb \u523b\u98de\u7ea2\uff0c\u5427\u5730\u4e00\u5df4\u638c\uff0c\u6253\u5728\u88f4\u73cf\u8138\u4e0a\uff0c\u6068\u58f0\u9053\uff1a\u201c\u4f60\u662f\u627e\u6b7b\uff0c\u6562\u6b3a\u8d1f\u59d1\u5976\u5976\uff01\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u53eb\u82e6\uff1a\u201c\u8c01\u6b3a\u8d1f\u4f60\u4e86\uff1f\u201d\u60f3\u89e3\u91ca\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u7684\u539f\u56e0\uff0c\u4f46\u662f\u5374\u82e6\u4e8e\u53e3\u4e0d\u80fd \u8a00\u3002 \u3000\u3000\u201c\u51b7\u5927\u53d4\u201d\u4e00\u4f4e\u5934\uff0c\u770b\u89c1\u88f4\u73cf\u7684\u773c\u775b\u4ecd\u77aa\u4f4f\u81ea\u5df1\uff0c\u53cd\u624b\u53c8\u662f\u4e00\u5df4\u638c\uff0c\u8138\u66f4\u7ea2\u5f97\u50cf\u719f\u900f\u4e86 \u7684\u82f9\u679c\uff0c\u98de\u5feb\u5730\u7a7f\u4e0a\u4e86\u4ef6\u5916\u8863\uff0c\u4e00\u9762\u6068\u58f0\u9053\uff1a\u201c\u4eca\u5929\u6211\u82e5\u4e0d\u8ba9\u4f60\u75db\u5feb\uff0c\u6211\u5c31\u4e0d\u53eb\u51b7\u6708\u4ed9 \u5b50\u3002\u201d \u3000\u3000\u6b64\u60c5\u6b64\u666f\uff0c\u542c\u5230\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u56db\u5b57\uff0c\u6015\u4e0d\u5413\u5f97\u7acb\u523b\u660f\u8fc7\u53bb\u624d\u602a\u3002 \u3000\u3000\u539f\u6765\u6b66\u6797\u4e2d\uff0c\u8fd1\u5341\u5e74\u6765\u51fa\u4e86\u4e2a\u6781\u4e3a\u6709\u540d\u7684\u4eba\u7269\uff0c\u8fd9\u4eba\u53eb\u505a\u201c\u5343\u624b\u4e66\u751f\u201d\u884c\u8e2a\u8be1\u79d8\uff0c\u6b66\u529f \u5374\u9ad8\u5f97\u60ca\u4eba\uff0c\u884c\u4e8b\u53c8\u4ecb\u4e8e\u6b63\u90aa\u4e4b\u95f4\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u771f\u59d3\u540d\uff0c\u4e5f\u6ca1\u6709\u4eba\u89c1\u8fc7\u4ed6\u7684\u771f\u5bb9 \u8c8c\u3002 \u3000\u3000\u4f60\u82e5\u4e0d\u53bb\u72af\u4ed6\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u6765\u627e\u4f60\uff0c\u53ef\u662f\u53ea\u8981\u4ed6\u6211\u7740\u4f60\uff0c\u4f60\u518d\u4e5f\u4f11\u60f3\u9003\u51fa\u4ed6\u7684\u624b\u53bb\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u4eba\u63d0\u8d77\u201c\u5343\u624b\u4e66\u751f\u201d\u56db\u5b57\uff0c\u591a\u662f\u656c\u800c\u8fdc\u4e4b\uff0c\u8fd9\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u672c\u662f\u201c\u5343\u624b\u4e66\u751f\u201d\u4e4b \u59bb\uff0c\u884c\u4e8b\u5374\u6bd4\u201c\u5343\u624b\u4e66\u751f\u201d\u66f4\u8fa3\u3002 \u3000\u3000\u540e\u6765\u4e0d\u77e5\u4f55\u6545\uff0c\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u4e0e\u201c\u5343\u624b\u4e66\u751f\u201d\u592b\u59bb\u53cd\u76ee\uff0c\u5343\u624b\u4e66\u751f\u7a81\u7136\u5728\u6c5f\u6e56\u4e2d\u5931\u53bb\u8e2a \u8ff9\uff0c\u90a3\u51b7\u6708\u4ed9\u5b50\u5374\u5f00\u59cb\u884c\u8d70\u6c5f\u6e56\uff0c\u5979\u4ea6\u662f\u884c\u8e2a\u98d8\u5ffd\uff0c\u800c\u4e14\u559c\u505a\u7537\u88c5\uff0c\u5ffd\u7537\u5ffd\u5973\uff0c\u53ea\u8981\u6709\u4eba\u7a0d \u5fae\u5f97\u7f6a\u4e86\u5979\uff0c\u5c31\u662f\u4e0d\u4e86\u3002 \u3000\u3000\u4ee5\u201c\u9f99\u5f62\u516b\u638c\u201d\u90a3\u4e48\u7684\u8eab\u4efd\u6b66\u529f\uff0c\u63d0\u8d77\u8fd9\u592b\u59bb\u4e24\u4eba\uff0c\u4e5f\u662f\u9762\u76ee\u53d8\u8272\uff0c\u7edd\u5bf9\u4e0d\u6562\u53bb\u62db\u60f9\u4ed6 \u4eec\u3002 \u3000\u3000\u6b64\u65f6\u673a\u7f18\u51d1\u5de7\uff0c\u5374\u8ba9\u88f4\u73cf\u9047\u7740\u4e86\u5979\uff0c\u800c\u53d1\u751f\u7684\u4e8b\uff0c\u53c8\u662f\u90a3\u4e48\u96be\u4ee5\u89e3\u91ca\uff0c\u4ee5\u201c\u51b7\u6708\u4ed9\u5b50\u201d \u5f80\u5e38\u7684\u813e\u6c14\uff0c\u4e0d\u8981\u4e86\u88f4\u73cf\u7684\u547d\u624d\u602a\u3002 \u3000\u3000\u88f4\u73cf\u7684\u76ee\u5149\u91cc\uff0c\u81ea\u8d23\uff0c\u60ed\u6127\uff0c\u4e0d\u5b89\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u4f46\u5374\u7edd\u5bf9\u6ca1\u6709\u4e5e\u6c42\u4e4b\u795e\u8272\uff0c\u4ed6\u751f\u6027\u5982 \u6b64\uff0c\u5c31\u7b97\u5200\u67b6\u5728\u5934\u4e0a\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u5411\u4f60\u54c0\u6c42\u534a\u53e5\u7684\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u8138\u4e0a\u7684\u7ea2\u971e\uff0c\u4ecd\u7136\u672a\u9000\uff0c\u9664\u4e86\u5979\u4e08\u592b\u5916\uff0c\u4ece\u672a\u6709\u4eba\u770b\u5230\u8fc7\u5979\u7684\u8eab\u4f53\uff0c\u8fd1\u51e0 \u5e74\u6765\uff0c\u5c31\u8fde\u5979\u7684\u4e08\u592b\u90fd\u6ca1\u6709\u770b\u89c1\u8fc7\u4e86\u3002 \u3000\u3000\u6b64\u523b\u5979\u5374\u8ba9\u8fd9\u5c11\u5e74\u4eba\u770b\u4e86\u4e2a\u9971\uff0c\u5fc3\u4e2d\u56fa\u7136\u6124\u6012\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u5374\u8fd8\u6709\u53e6\u4e00\u79cd\u8bf4\u4e0d\u51fa\u6765\u7684\u611f \u89c9\u3002 \u3000\u3000\u7136\u800c\u8fd9\u611f\u89c9\u5374\u66f4\u4ee4\u5979\u4e0d\u5b89\uff0c\u4e5f\u66f4\u4fc3\u4f7f\u5979\u4e0b\u51b3\u5fc3\u8981\u5e9f\u6389\u88f4\u73cf\uff0c\u8fd9\u5728\u5979\u800c\u8a00\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3e\u624b \u4e4b\u52b3\u800c\u5df2\uff0c\u4f46\u5979\u5374\u5728\u8fdf\u7591\u7740\u3002 \u3000\u3000\u4ece\u88f4\u73cf\u7684\u76ee\u5149\u770b\uff0c\u5979\u53d1\u73b0\u4e86\u4e00\u79cd\u4ece\u672a\u9047\u5230\u8fc7\u7684\u201c\u7eaf\u771f\u201d\u3002\u5979\u7684\u81ea\u5e7c\u5b64\u50b2\uff0c\u5ac1\u7ed9\u201c\u5343\u624b\u4e66 \u751f\u201d\u540e\uff0c\u813e\u6c14\u66f4\u602a\uff0c\u54ea\u77e5\u201c\u5343\u624b\u4e66\u751f\u201d\u7528\u60c5\u4e0d\u4e13\uff0c\u88ab\u5979\u53d1\u89c9\u4e86\uff0c\u5979\u5c31\u4e00\u6012\u800c\u79bb\u5f00\u4e86\u4ed6\u3002 \u3000\u3000\u81ea\u6b64\uff0c\u5979\u5c06\u5929\u4e0b\u7684\u7537\u4eba\u90fd\u89c6\u4e3a\u4ec7\u654c\uff0c\u6b64\u523b\u5979\u4f4e\u5934\u4e00\u671b\uff0c\u88f4\u73cf\u7684\u76ee\u5149\u5374\u4f7f\u5979\u771f\u6b63\u7684\u5fc3\u52a8 \u4e86\u3002 \u3000\u3000\u987b\u77e5\u4e16\u95f4\u4efb\u4f55\u4eba\uff0c\u56fa\u7136\u53ef\u4ee5\u7528\u5404\u79cd\u65b9\u6cd5\u6765\u9a97\u5f97\u4ed6\u4eba\u7684\u60c5\u611f\uff0c\u7136\u800c\u90a3\u7edd\u5bf9\u53ea\u662f\u6682\u65f6\u7684\uff0c\u552f \u6709\u201c\u7eaf\u771f\u201d\u7684\u60c5\u611f\uff0c\u624d\u80fd\u6362\u5f97\u522b\u4eba\u7684\u7eaf\u771f\u60c5\u611f\uff0c\u4e5f\u552f\u6709\u201c\u7eaf\u771f\u201d\uff0c\u624d\u80fd\u611f\u52a8\u4e86\u522b\u4eba\uff0c\u8fd9\u662f\u81ea \u53e4\u4e0d\u53d8\u7684\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u7389\u624b\u4e00\u5f39\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u50cf\u662f\u80fd\u591f\u968f\u610f\u53d8\u5e7b\u65b9\u5411\uff0c\u7adf\u62cd\u5728\u88f4\u73cf\u8111\u540e\u7684\u201c\u7389\u6795 \u9aa8\u201d\u4e0a\u3002 \u3000\u3000\u88f4\u73cf\u677e\u4e86\u53e3\u6c14\uff1a\u4ed6\u4e5f\u77e5\u9053\u65b9\u624d\u662f\u88ab\u4eba\u5bb6\u70b9\u4e2d\u7a74\u9053\u4e86\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u76ee\u5149\u91cc\uff0c\u4ecd\u7136\u6ca1\u6709\u4e00\u4e1d\u597d\u610f\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u88f4\u73cf\u867d\u7136\u660e\u77e5\u81ea\u5df1\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u4f46\u5374\u5e76\u4e0d\u77e5\u9053\u81ea\u5df1\u9669\u6b7b\u8fd8\u751f\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u80fd\u5728 \u201c\u51b7\u6708\u4ed9\u5b50\u201d\u624b\u4e0b\u9003\u51fa\u547d\u6765\uff0c\u5b9e\u5728\u662f\u5f02\u6570\u4e86\u3002 \u3000\u3000\u5728\u7a74\u9053\u88ab\u89e3\u540e\uff0c\u4ed6\u6123\u4e86\u8bb8\u4e45\uff0c\u7136\u540e\u624d\u5c06\u81ea\u5df1\u7684\u51fa\u8eab\uff0c\u4ee5\u53ca\u65e5\u95f4\u6240\u7ecf\u5386\u8fc7\u7684\u4e8b\uff0c\u90fd\u8bf4\u4e86\u51fa \u6765\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u827e\u9752\uff0c\u867d\u7136\u5916\u8868\u4e0a\u51b7\u82e5\u51b0\u971c\uff0c\u800c\u4e14\u884c\u4e8b\u5fc3\u72e0\u624b\u8fa3\uff0c\u4f46\u5374\u662f\u4e2a\u6781\u5bcc\u60c5\u611f\u7684\u5973 \u4eba\uff0c\u53ea\u662f\u5979\u8fd9\u79cd\u60c5\u611f\uff0c\u4e0d\u8f7b\u6613\u8868\u9732\u800c\u5df2\u3002 \u3000\u3000\u4e16\u4e0a\u6709\u8bb8\u591a\u4eba\uff0c\u906d\u9047\u8fd8\u8fdc\u6bd4\u88f4\u73cf\u51c4\u60e8\u5f97\u591a\uff0c\u827e\u9752\u4e5f\u4ece\u672a\u8fc7\u95ee\uff0c\u4e5f\u4ece\u672a\u5173\u5fc3\uff0c\u6b64\u523b\u542c\u4e86\u88f4 \u73cf\u7684\u8bdd\uff0c\u60c5\u51b5\u5374\u5927\u4e3a\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u4eba\u7c7b\u7684\u60c5\u611f\uff0c\u5f80\u5f80\u4f1a\u968f\u7740\u5bf9\u8c61\u800c\u53d8\u8fc1\uff0c\u4e00\u4ef6\u540c\u6837\u7684\u4e8b\uff0c\u4f46\u53d1\u751f\u5728\u4e24\u4e2a\u4e0d\u540c\u7684\u4eba\u8eab\u4e0a\uff0c\u90a3 \u8fd9\u4ef6\u4e8b\u5728\u4f60\u5fc3\u4e2d\u9020\u6210\u7684\u5370\u8c61\uff0c\u4e5f\u4f1a\u8fe5\u7136\u800c\u5f02\u7684\u3002 \u3000\u3000\u88f4\u73cf\u5e76\u4e0d\u5584\u4e8e\u8a00\u8bcd\uff0c\u518d\u52a0\u4e0a\u81ea\u8eab\u53c8\u4e0d\u559c\u591a\u8a00\uff0c\u6240\u4ee5\u4ed6\u8bf4\u5f97\u5f88\u7b80\u77ed\uff0c\u4f46\u662f\u5f88\u627c\u8981\uff0c\u5f88\u52a8 \u4eba\u3002 \u3000\u3000\u5be1\u8a00\u8005\u7684\u8bf4\u8bdd\uff0c\u5f80\u5f80\u90fd\u662f\u627c\u8981\u52a8\u4eba\u7684\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u65b9\u624d\u5b58\u5728\u4ed6\u4eec\u4e4b\u95f4\u7684\u7f9e\u6127\u3001\u5c34\u5c2c\u548c\u4e0d\u5b89\uff0c\u90fd\u5df2\u4e0d\u518d\u5b58\u5728\u4e86\uff0c\u4ee3\u66ff\u7684\u5374\u662f\u5f7c\u6b64\u4e4b \u95f4\u7684\u4e86\u89e3\u548c\u540c\u60c5\u3002 \u3000\u3000\u867d\u7136\u827e\u9752\u5e76\u672a\u66fe\u5c06\u8be1\u79d8\u800c\u591a\u5f69\u7684\u4e00\u751f\u8bf4\u51fa\u6765\uff0c\u4f46\u662f\u5979\u8f7b\u53f9\u7740\u8bf4\uff1a\u201c\u4f60\u522b\u96be\u53d7\uff0c\u6211\u7684\u8eab\u4e16 \u4e5f\u548c\u4f60\u5dee\u4e0d\u591a\uff0c\u4f60\u5e76\u4e0d\u7b28\uff0c\u53ea\u8981\u80af\u7528\u5fc3\uff0c\u5c06\u6765\u6b66\u529f\u4e5f\u8bb8\u6bd4\u6211\u8fd8\u597d\uff0c\u8fd9\u4ee5\u540e\u6162\u6162\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u5c31\u662f\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5728\u88f4\u73cf\u5fc3\u4e2d\uff0c\u5df2\u80dc\u8fc7\u5343\u8a00\u4e07\u8bed\uff0c\u4ed6\u5bf9\u8fd9\u5e74\u7eaa\u6bd4\u4ed6\u5927\u4e86\u5c06\u8fd1\u4e00\u500d\u7684\u5973\u5b50\uff0c \u5fc3\u4e2d\u6b64\u523b\u867d\u65e0\u60c5\u6b32\u4e4b\u5ff5\uff0c\u4f46\u5374\u6709\u53e6\u4e00\u79cd\u96be\u8a00\u7684\u60c5\u611f\u3002 \u3000\u3000\u90a3\u51e0\u4e4e\u662f\u4e00\u79cd\u4e0e\u201c\u6bcd\u7231\u201d\u76f8\u4f3c\u7684\u60c5\u611f\uff0c\u800c\u8fd9\u79cd\u60c5\u611f\uff0c\u5df2\u6709\u591a\u5e74\u672a\u66fe\u5728\u88f4\u73cf\u5fc3\u4e2d\u51fa\u73b0\u8fc7 \u4e86\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u5fc3\u795e\u4ea4\u75b2\uff0c\u5979\u6b64\u6b21\u52ff\u5306\u5317\u6765\uff0c\u5b9e\u5728\u662f\u4e3a\u7740\u9003\u907f\u4e00\u4e2a\u6781\u4e3a\u5389\u5bb3\u7684\u5bf9\u5934\uff0c\u4e00\u8def \u4e0a\u9a6c\u4e0d\u505c\u8e44\uff0c\u53d7\u5c3d\u4e86\u5954\u6ce2\u4e4b\u82e6\u3002 \u3000\u3000\u800c\u660e\u5929\uff0c\u5979\u8fd8\u8981\u7ee7\u7eed\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u4f11\u6b62\u7684\u9003\u4ea1\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u5026\u773c\u60fa\u5fea\uff0c\u5a07\u6175\u4ed6\u8bf4\uff1a\u201c\u5feb\u7761\u5427\u3002\u201d\u8bdd\u4e00\u51fa\u53e3\uff0c\u53c8\u4e0d\u7981\u6ee1\u9762\u751f\u51fa\u7ea2 \u971e\uff0c\u84e6\u7136\u60f3\u8d77\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5bf9\u65b9\u603b\u662f\u4e2a\u7537\u4eba\u5440\u3002 \u3000\u3000\u827e\u9752\u5ffd\u5730\u4e00\u63a0\u81f3\u95e8\u53e3\uff0c\u63a9\u4e0a\u8863\u895f\uff0c\u500f\u7136\u62c9\u5f00\u623f\u95e8\uff0c\u95e8\u5916\u6084\u7136\u65e0\u4eba\uff0c\u5c31\u8fde\u95e8\u5916\u90a3\u4e00\u6761\u957f\u957f \u8d70\u5eca\u7684\u4e24\u7aef\uff0c\u6b64\u523b\u4e5f\u6e3a\u65e0\u4eba\u8ff9\u3002 \u3000\u3000\u6709\u98ce\u5439\u52a8\uff0c\u5979\u8863\u8882\u4e00\u98d8\uff0c\u8fde\u5fd9\u7528\u624b\u62c9\u4f4f\uff0c\u8138\u4e0a\u53c8\u4e0d\u7981\u4e00\u7ea2\uff0c\u56de\u5934\u53bb\u671b\u88f4\u73cf\uff0c\u773c\u5149\u77ac\u5904\uff0c \u53c8\u84e6\u5730\u4e00\u60ca\u3002 \u3000\u3000\u6b64\u523b\u88f4\u73cf\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u51b7\u2014\u2014\u51b7\u5927\u53d4\uff0c\u4f60\u7d2f\u4e86\uff0c\u8fd8\u662f\u5148\u7761\u5427\uff0c\u6211\u5230\u95e8\u5916\u7ad9 \u7ad9\uff0c\u53cd\u6b63\u5929\u5feb\u4eae\u4e86\u3002\u201d \u3000\u3000\u827e\u9752\u4f4e\u5934\u6c89\u601d\u7740\uff0c\u4eff\u4f5b\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u5ffd\u7136\u6068\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u4eec\uff0c\u6562\u60c5\u4f60\u4eec\u6d3b\u5f97\u771f \u4e0d\u8010\u70e6\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u4e00\u60ca\uff0c\u832b\u7136\u671b\u7740\u5979\uff0c\u5947\u602a\u5979\u600e\u5730\u7a81\u7136\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u827e\u9752\u4e5f\u81ea\u53d1\u89c9\uff0c\u770b\u7740\u4ed6\u90a3\u832b\u7136 \u7684\u795e\u8272\uff0c\u4e0d\u7981\u5fae\u5fae\u73b0\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u6307\u7740\u95e8\u6846\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u770b\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u88f4\u73cf\u4e00\u770b\uff0c\u4e5f\u5927\u5403\u4e00\u60ca\uff0c\u539f\u6765\u95e8\u6846\u4e0a\uff0c\u6574\u6574\u9f50\u9f50\u5730\u7528\u767d\u7c89\u753b\u4e86\u4e2a\u661f\u5f62\u7684\u56fe\u5f62\u3002\u4ed6\u4e45\u5c45\u9556 \u5c40\uff0c\u5e73\u65e5\u542c\u4eba\u95f2\u8c08\uff0c\u6c5f\u6e56\u4e0a\u7684\u52fe\u5f53\uff0c\u4ed6\u4e5f\u77e5\u9053\u4e0d\u5c11\uff0c\u6b64\u523b\u4e00\u89c1\uff0c\u4fbf\u5df2\u77e5\u9053\u8fd9\u662f\u6c5f\u6e56\u76d7\u515a\u505a\u6848 \u524d\u7684\u9884\u544a\u3002 \u3000\u3000\u8fd9\u610f\u601d\u4e5f\u5c31\u7b49\u4e8e\u8bf4\uff1a\u201c\u8fd9\u8d27\u8272\u5df2\u88ab\u6211\u4eec\u5b9a\u4e0b\u4e86\uff0c\u522b\u4eba\u4f11\u6765\u63d2\u624b\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u827e\u9752\u5fae\u4e00\u70b9\u5934\uff0c\u6307\u7740\u90a3\u661f\u5f62\u9053\uff1a\u201c\u4f60\u7559\u610f\u770b\uff0c\u8fd9\u9897\u661f\u53ef\u6709\u4ec0\u4e48\u53e4\u602a\u4e4b\u5904\uff1f\u201d \u3000\u3000\u88f4\u73cf\u8fde\u5fd9\u7559\u610f\u4e00\u4e0b\uff0c\u4ed6\u672c\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u591a\u5e74\u6765\u7684\u6291\u5236\uff0c\u867d\u7136\u4ed6\u5df2\u5931\u53bb\u4e86\u81ea\u4fe1\u4e4b\u5fc3\uff0c \u4f46\u672c\u6027\u5374\u4ecd\u672a\u6d88\u5931\uff0c\u8fd9\u6b63\u5982\u4e00\u7c92\u660e\u73e0\uff0c\u4ecd\u5728\u691f\u4e2d\uff0c\u672a\u7ecf\u4eba\u53d1\u73b0\uff0c\u4ed6\u4ecd\u7136\u8fd8\u4f1a\u53d1\u51fa\u5149\u5f69\u7684\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u4e00\u89c1\uff0c\u4fbf\u9053\uff1a\u201c\u666e\u901a\u7684\u661f\u53ea\u6709\u4e94\u89d2\uff0c\u4f46\u8fd9\u9897\u661f\u5374\u6709\u4e03\u4e2a\u89d2\u3002\u800c\u4e14\u516d\u4e2a\u89d2\u8f83\u5c0f\uff0c\u5176 \u4e2d\u53ea\u6709\u4e00\u89d2\u8f83\u5927\u3002\u201d \u3000\u3000\u827e\u9752\u8d5e\u8bb8\u5730\u4e00\u7b11\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u7684\u89c2\u5bdf\u529b\u5012\u654f\u9510\u5f97\u5f88\u3002\u201d\u7acb\u523b\u8f7b\u8f7b\u62f4\u4e0a\u623f\u95e8\uff0c\u8bf4 \u9053\uff1a\u201c\u5bf9\u4e86\u3001\u8fd9\u5c31\u662f\u6c5f\u6e56\u4e0a\u58f0\u540d\u6700\u6076\u7684\u4e03\u4e2a\u4eba\u6240\u7559\u4e0b\u7684\u6807\u8bb0\uff0c\u54fc\uff0c\u4ed6\u4eec\u627e\u5230\u6211\uff0c\u4e5f\u662f\u4ed6\u4eec\u9709 \u8fd0\u5230\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u827e\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e49\u7ed3\u5144\u5f1f\u4e03\u4eba\uff0c\u81ea\u79f0\u4e3a\u2018\u5317\u6597\u4e03\u715e\u2019\uff0c\u5e73\u65e5\u65e0\u6076\u4e0d\u4f5c\uff0c\u6b66\u529f\u60f3\u4e5f\u4e0d \u574f\uff0c\u522b\u7684\u4e8b\u4e0d\u8bf4\uff0c\u8fd9\u4e03\u715e\u91cc\u7684\u8001\u4e09\u548c\u8001\u4e03\uff0c\u662f\u6700\u597d\u8272\u2014\u2014\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u8138\u53c8\u662f\u4e00\u7ea2\u3002 \u3000\u3000\u88f4\u73cf\u7559\u610f\u5730\u503e\u542c\u7740\uff0c\u5374\u672a\u5bdf\u89c9\u5230\u5979\u7684\u9762\u8d64\u3002\u5979\u987f\u4e86\u987f\uff0c\u53c8\u63a5\u7740\u8bf4\uff1a\u201c\u521a\u521a\u72b9\u770b\u90a3\u56fe\u5f62\uff0c \u8f83\u5927\u7684\u4e00\u89d2\uff0c\u662f\u7531\u4ece\u4e0a\u5f80\u4e0b\u6570\u7684\u7b2c\u2014\u2014\u201d\u5979\u7a81\u7136\u53c8\u987f\u4f4f\u8bdd\uff0c\u5411\u88f4\u73cf\u95ee\u9053\uff1a\u201c\u4f60\u8bb0\u5f97\u7b2c\u51e0\u4e2a\u89d2 \u8f83\u5927\u5417\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6beb\u4e0d\u601d\u7d22\u7b54\u9053\uff1a\u201c\u6b63\u662f\u7b2c\u4e09\u4e2a\u3002\u201d \u3000\u3000\u827e\u9752\u53c8\u4e00\u7b11\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u4ee5\u4ed6\u7684\u5929\u4efd\uff0c\u5b66\u6b66\u529f\u600e\u4f1a\u65e0\u6210\uff0c\u60f3\u90a3\u9f99\u5f62\u516b\u638c\u5728\u6c5f\u6e56\u4e0a\u4ea6\u662f\u4ee5 \u6b66\u529f\u6210\u540d\u7684\u4eba\u7269\uff0c\u4ed6\u5728\u9f99\u5f62\u516b\u638c\u5904\u803d\u4e86\u90a3\u4e48\u4e45\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u81f3\u4e8e\u6b66\u529f\u5982\u6b64\u5f31\u5440\uff1f\u201d \u3000\u3000\u5979\u7591\u5ff5\u5927\u8d77\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u4e8b\u6709\u8e4a\u8df7\uff0c\u518d\u5fd6\u9053\uff1a\u201c\u4f55\u51b5\u4ed6\u5929\u8d44\u4e4b\u9ad8\uff0c\u5df2\u5c5e\u7edd\u9876\uff0c\u90a3\u9f99\u5f62\u516b \u638c\u4e3a\u4f55\u53c8\u4e00\u76f4\u8bf4\u4ed6\u7b28\u5462\uff1f\u201d \u3000\u3000\u5979\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u867d\u7136\u77e5\u9053\u5176\u4e2d\u5fc5\u5b9a\u6709\u4e9b\u53e4\u602a\uff0c\u4f46\u771f\u76f8\u5982\u4f55\uff0c\u5979\u4e5f\u4e0d\u6562\u5984\u52a0\u81c6\u6d4b\uff0c\u6697\u5fd6 \u9053\uff1a\u201c\u4ee5\u540e\u8fd9\u4e00\u5b9a\u8981\u67e5\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u88f4\u73cf\u89c1\u5979\u4e45\u672a\u8bf4\u8bdd\uff0c\u4ed6\u7a76\u7adf\u5c11\u5e74\u5fc3\u60c5\uff0c\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u9053\uff1a\u201c\u8fd9\u56fe\u5f62\u6240\u793a\uff0c\u662f\u4e0d\u662f\u5c31\u662f \u8bf4\u8fd9\u6765\u7684\u5c31\u662f\u4e03\u715e\u4e2d\u7684\u8001\u4e09\u5462\uff1f\u201d \u3000\u3000\u827e\u9752\u70b9\u9996\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u5979\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4ed6\u6765\u4e86\uff0c\u6050\u6015\u5c31\u518d\u4e5f\u8d70\u4e0d\u6389 \u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u9053\uff1a\u201c\u4ed6\u7559\u4e0b\u4e86\u8bb0\u53f7\uff0c\u662f\u4e0d\u662f\u5c31\u4e00\u5b9a\u4f1a\u6765\u5462\uff1f\u201d\u6b64\u65f6\u4ed6\u5bf9\u827e\u9752\u7684\u6b66\u529f\uff0c\u5df2\u6709\u4fe1\u5fc3\uff0c \u5012\u5e0c\u671b\u90a3\u201c\u5317\u6597\u4e03\u715e\u201d\u5168\u6765\uff0c\u8ba9\u81ea\u5df1\u770b\u770b\u70ed\u95f9\u3002 \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\u5317\u6597\u4e03\u715e\uff0c\u5728\u6c5f\u6e56\u4ea6\u975e\u6613\u4e0e\u4e4b\u8f88\uff0c\u82e5\u771f\u7684\u5168\u6765\u4e86\uff0c\u51b7\u6708\u4ed9\u5b50\u4e00\u4eba\uff0c\u6050\u6015\u8fd8\u4e0d \u597d\u5e94\u4ed8\u5462\u3002 \u3000\u3000\u827e\u9752\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6765\u662f\u4e00\u5b9a\u4f1a\u6765\u7684\uff0c\u53ea\u4e0d\u8fc7\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u65f6\u5019\u7f62\u4e86\u3002\u201d\u5979\u53c8\u53f9\u9053\uff1a\u201c\u522b \u7684\u4e0d\u8bf4\uff0c\u4eca\u591c\u6211\u770b\u6765\u89c9\u662f\u65e0\u6cd5\u7761\u7684\u4e86\u3002\u201d \u3000\u3000\u4f4e\u7740\u5934\uff0c\u5fae\u51fa\u4e86\u56de\u795e\uff0c\u7a81\u7136\u770b\u5230\u81ea\u5df1\u7a7f\u7740\u7684\u53ea\u662f\u4ef6\u6587\u58eb\u957f\u886b\uff0c\u6b64\u523b\u4e0b\u895f\u6563\u5f00\uff0c\u91cc\u9762\u7684\u80a4 \u8272\u5982\u7389\uff0c\u5979\u8fde\u5fd9\u4e00\u671b\u88f4\u73cf\uff0c\u5374\u89c1\u4ed6\u501a\u7740\u684c\u5b50\uff0c\u50cf\u662f\u5df2\u7ecf\u7761\u7740\u4e86\uff0c\u706f\u5149\u4e4b\u4e0b\uff0c\u671b\u4e4b\u771f\u5982\u5973\u5b50\u3002 \u3000\u3000\u5979\u53c8\u4e00\u7b11\uff0c\u60f3\u8d77\u65b9\u624d\u81ea\u5df1\u5728\u4ed6\u9762\u524d\u89e3\u8863\u65f6\u7684\u60c5\u666f\uff0c\u8138\u53c8\u4e0d\u7981\u4e00\u7ea2\u3002 \u3000\u3000\u5979\u5e73\u65e5\u5b64\u82b3\u72ec\u50b2\uff0c\u7b49\u95f2\u8c01\u4e5f\u89c1\u4e0d\u7740\u5979\u7684\u4e00\u7b11\uff0c\u6b64\u523b\u4e0d\u77e5\u600e\u5730\uff0c\u5fc3\u60c5\u5374\u50cf\u662f\u8d77\u4e86\u5f88\u5927\u7684\u53d8 \u5316\uff0c\u8fd9\u662f\u5979\u81ea\u5df1\u4e5f\u65e0\u6cd5\u4e86\u89e3\u7684\u4e8b\u3002 \u3000\u3000\u5979\u6084\u6084\u7ad9\u4e86\u8d77\u6765\uff0c\u60f3\u7a7f\u4e0a\u8863\u88f3\uff0c\u514d\u5f97\u7b49\u4f1a\u52a8\u624b\u65f6\u4e0d\u4fbf\uff0c\u54ea\u77e5\u8f7b\u8f7b\u4e00\u52a8\uff0c\u88f4\u73cf\u5df2\u7741\u5f00\u773c \u6765\uff0c\u539f\u6765\u4ed6\u6839\u672c\u5c31\u4e0d\u66fe\u7761\u7740\u3002 \u3000\u3000\u4ed6\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5df2\u7ecf\u6765\u4e86\uff1f\u201d \u3000\u3000\u827e\u9752\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u80cc\u8fc7\u8eab\u53bb\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u88f4\u73cf\u773c\u73e0\u4e00\u52a8\uff0c\u5df2\u77e5\u5979\u7684\u5fc3\u610f\uff0c\u5fd9\u5c06\u8eab\u5b50\u4e00\u8f6c\uff0c\u53cc\u773c\u7d27\u7d27\u76ef\u5728\u5899\u4e0a\uff0c\u54ea\u77e5\u706f\u5149\u53cd\u5c04\uff0c\u5374 \u53c8\u5c06\u827e\u9752\u89e3\u8863\u65f6\u7684\u8eab\u5f71\u6620\u5230\u5899\u4e0a\u4e86\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u8840\u6c14\u65b9\u521a\u7684\u5c11\u5e74\u4eba\u5185\u5fc3\u771f\u72b9\u5982\u5927\u6d77\u7ffb\u817e\uff0c\u4f46\u662f\u4ed6\u7ec8\u4e8e\u5fcd\u4f4f\u4e86\uff0c\u7d27\u7d27\u95ed\u8d77\u773c\u775b\uff0c\u518d \u4e5f\u4e0d\u60f3\u3002 \u3000\u3000\u970e\u65f6\uff0c\u827e\u9752\u5df2\u7ed3\u675f\u597d\u4e86\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u5c4b\u9876\u4e0a\u7a81\u7136\u6709\u4e00\u79cd\u5f88\u5947\u602a\u7684\u58f0\u97f3\uff0c\u975e\u5e38\u8f7b\u5fae\uff0c\u88f4\u73cf \u4e00\u4e1d\u4e5f\u6ca1\u6709\u5bdf\u89c9\u5230\uff0c\u827e\u9752\u5374\u9762\u8272\u4e00\u53d8\u3002 \u3000\u3000\u5979\u5fae\u4e00\u6325\u624b\uff0c\u684c\u4e0a\u7684\u706f\u4fbf\u500f\u7136\u800c\u706d\u3002 \u3000\u3000\u5979\u7684\u8fd9\u52a8\u4f5c\u50cf\u662f\u8f7b\u6613\u800c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\uff0c\u4f46\u82e5\u4e0d\u662f\u529f\u529b\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u53c8\u600e\u80fd\u81f4\u6b64\uff1f \u3000\u3000\u88f4\u73cf\u987f\u89c9\u773c\u524d\u4e00\u9ed1\uff0c\u706f\u5149\u5df2\u706d\uff0c\u4ed6\u65b9\u60f3\u51fa\u58f0\uff0c\u4f46\u77ac\u5373\u60f3\u5230\u53ef\u80fd\u662f\u90a3\u4eba\u5df2\u7ecf\u6765\u4e86\uff0c\u8fde\u5fd9\u6536 \u4f4f\uff0c\u501f\u7740\u7a97\u7eb8\u4e2d\u900f\u8fc7\u7684\u4e00\u4e1d\u5fae\u5f31\u7684\u5149\u7ebf\uff0c\u4e24\u53ea\u773c\u775b\u7741\u5f97\u8001\u5927\uff0c\u77ac\u4e5f\u4e0d\u77ac\u671b\u7740\u7a97\u524d\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u89c9\u5f97\u8eab\u7554\u4e00\u9635\u6e29\u99a8\uff0c\u4e00\u8f6c\u5934\uff0c\u8fd9\u79cd\u6e29\u99a8\u7684\u6c14\u606f\u66f4\u662f\u5f3a\u70c8\uff0c\u539f\u6765\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c \u827e\u9752\u5df2\u6765\u5230\u4ed6\u8eab\u4fa7\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u8981\u52a8\uff0c\u4e5f\u4e0d\u8981\u8bf4\u8bdd\uff0c\u5df2\u7ecf\u5feb\u6765\u4e86\u3002\u201d\u5410\u6c14\u5982\u5170\uff0c\u55c5\u4e4b\u9189 \u4eba\u3002 \u3000\u3000\u88f4\u73cf\u8d8a\u53d1\u5c4f\u4f4f\u58f0\u606f\uff0c\u8fde\u5927\u6c14\u90fd\u4e0d\u6562\u5598\uff0c\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u5fc3\u8df3\u5f97\u90a3\u4e48\u5389\u5bb3\uff0c\u751a\u81f3\u8fde\u827e\u9752\u90fd\u542c \u5230\u4e86\uff0c\u6084\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u6015\u5417\uff1f\u201d \u3000\u3000\u88f4\u73cf\u8138\u4e00\u7ea2\uff0c\u4ed6\u81ea\u5df1\u53ef\u77e5\u9053\u81ea\u5df1\u5fc3\u8df3\u7684\u539f\u56e0\uff0c\u4f46\u662f\u4ed6\u53c8\u600e\u80fd\u8bf4\u5f97\u51fa\u53e3\u3002 \u3000\u3000\u7a81\u5730\uff0c\u7a97\u6237\u65e0\u98ce\u81ea\u5f00\uff0c\u4e00\u6761\u4eba\u5f71\u5728\u7a97\u53e3\u4e00\u95ee\uff0c\u7565\u4e00\u8fdf\u7591\uff0c\u4fbf\u6478\u4e86\u8fdb\u6765\uff0c\u5206\u660e\u662f\u81ea\u4f8d\u8eab \u624b\uff0c\u6ca1\u6709\u5c06\u623f\u91cc\u7684\u4eba\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u8fd9\u4eba\u5f71\u8eab\u6750\u751a\u9ad8\uff0c\u8eab\u624b\u4e5f\u6781\u4e3a\u654f\u6377\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u5168\u7136\u4e0d\u987e\u5fcc\uff0c\u6b66\u529f\u5f53\u7136\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u5426 \u5219\uff0c\u4ed6\u600e\u4e48\u6562\u8fd9\u6837\u5730\u653e\u8086\u5462\uff1f \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u9f3b\u5b54\u91cc\u6697\u54fc\u4e00\u58f0\uff0c\u90a3\u4eba\u5f71\u662f\u4e2a\u8001\u6c5f\u6e56\uff0c\u5c31\u662f\u8fd9\u9f3b\u5b54\u91cc\u6240\u53d1\u51fa\u7684\u90a3\u4e00\u4e1d\u6781\u4e3a \u5fae\u5c0f\u7684\u58f0\u606f\uff0c\u5df2\u4f7f\u4ed6\u6709\u4e86\u8b66\u89c9\uff0c\u773c\u5149\u56db\u626b\uff0c\u53d1\u89c9\u623f\u91cc\u5750\u7740\u4e24\u6761\u9ed1\u9ed1\u7684\u4eba\u5f71\uff0c\u5fae\u5fae\u4e00\u60ca\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u53ef\u663e\u51fa\u4eba\u5bb6\u867d\u7136\u72c2\u5984\uff0c\u4f46\u771f\u9047\u4e0a\u4e86\u4e8b\uff0c\u53ef\u6709\u7cbe\u786e\u7684\u5224\u65ad\u3002 \u3000\u3000\u4ed6\u5fae\u4e00\u64a4\u624b\uff0c\u624b\u91cc\u4f3c\u5df2\u64a4\u4e0b\u5175\u5203\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u623f\u91cc\u7684\u53ef\u662f\u9053\u4e0a\u540c\u6e90\uff0c\u5144\u5f1f\u83ab\u897f\uff0c\u662f\u5408 \u5b57\uff0c\u4e5f\u8bf7\u4eae\u4e2a\u4e07\u513f\u3002\u201d \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u4e00\u62c9\u88f4\u73cf\u7684\u624b\uff0c\u793a\u610f\u4ed6\u4e0d\u8981\u51fa\u58f0\uff0c\u83ab\u897f\u53c8\u9053\uff1a\u201c\u670b\u53cb\u662f\u4f55\u65b9\u795e\u5723\uff0c\u518d\u4e0d\u5f00 \u53e3\uff0c\u53ef\u522b\u602a\u5144\u5f1f\u8981\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d\u987b\u77e5\u4ed6\u4e45\u7ecf\u5927\u654c\uff0c\u65b9\u624d\u867d\u7136\u8d38\u7136\u95ef\u5165\uff0c\u4f46\u90a3\u5374\u56e0\u4e3a\u5c06\u623f\u91cc\u7684 \u4eba\u770b\u5f97\u592a\u8fc7\u8f7b\u6613\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u662f\u4ed6\u7684\u758f\u5ffd\u4e4b\u5904\uff0c\u539f\u6765\u4ed6\u4e5f\u4f4f\u5728\u8fd9\u95f4\u6808\u623f\u91cc\uff0c\u65b9\u624d\u51b7\u6708\u4ed9\u5b50\u827e\u9752\u4e0e\u88f4\u73cf\u6295\u5e97\u7684\u65f6 \u5019\uff0c\u4ed6\u5df2\u671b\u89c1\u827e\u9752\uff0c\u8fd9\u79cd\u4eba\u7684\u773c\u5149\u53ef\u591a\u5389\u5bb3\uff0c\u4e00\u773c\u4fbf\u770b\u51fa\u827e\u9752\u662f\u5973\u626e\u7537\u88c5\u7684\uff0c\u4ed6\u597d\u8272\u95fb\u540d\uff0c \u624b\u4e0b\u4e0d\u77e5\u574f\u4e86\u591a\u5c11\u4e2a\u826f\u5bb6\u5987\u5973\uff0c\u6b64\u523b\u4e00\u89c1\u827e\u9752\u90a3\u79cd\u6210\u719f\u800c\u59a9\u5a9a\u7684\u5987\u4eba\u98ce\u81f4\uff0c\u867d\u662f\u7a7f\u7740\u7537\u88c5\uff0c \u5df2\u4f7f\u83ab\u897f\u8272\u4e0e\u9b42\u6388\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u591a\u671b\uff0c\u6015\u6253\u8349\u60ca\u86c7\uff0c\u6084\u6084\u8e51\u5728\u540e\u9762\uff0c\u5bf9\u88f4\u73cf\uff0c\u5012\u6ca1\u6709\u671b\u4e00\u773c\uff0c\u53ea\u5f71\u7ef0\u7ef0\u5730\u77e5\u9053\u53e6 \u5916\u8fd8\u6709\u4e00\u4e2a\u5973\u5b50\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u8272\u80c6\u5305\u5929\uff0c\u518d\u52a0\u4e0a\u6b66\u529f\u5b9e\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u518d\u4e5f\u6599\u60f3\u4e0d\u5230\u4ed6\u773c\u4e2d\u7684\u5bf9\u8c61\u7adf\u662f\u201c\u51b7\u6708\u4ed9 \u5b50\u201d\uff0c\u7b49\u4e0d\u5230\u4e09\u66f4\uff0c\u5c31\u95ef\u5165\u4e86\u4eba\u5bb6\u7684\u623f\u91cc\u3002 \u3000\u3000\u53ef\u662f\u827e\u9752\u90a3\u8f7b\u5fae\u7684\u4e00\u54fc\uff0c\u53ef\u4f7f\u4ed6\u60ca\u89c9\u4e86\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u60f3\u5230\uff1a\u201c\u8fd9\u5973\u4eba\u867d\u5973\u626e\u7537\u88c5\uff0c\u8bf4\u4e0d\u5b9a\u624b\u4e0b\u6709\u6b66\u529f\u4e5f\u672a\u53ef\u77e5\u3002\u201d\u8111\u6d77\u4e00\u8f6c\uff0c\u5bf9\u6b66\u6797 \u4e2d\u51e0\u4e2a\u559c\u6b22\u7a7f\u7537\u88c5\u7684\u5973\u5b50\u60f3\u4e86\u4e00\u904d\uff0c\u5fc3\u4e2d\u5927\u5b9a\uff0c\u56e0\u4e3a\u5979\u4eec\u7684\u6b66\u529f\u90fd\u4e0d\u53ca\u81ea\u5df1\uff0c\u58f0\u540d\u4e5f\u4e0d\u53ca\u81ea \u5df1\u9ad8\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6302\u4e07\u6f0f\u4e00\uff0c\u5374\u5fd8\u4e86\u201c\u51b7\u6708\u4ed9\u5b50\u201d\uff0c\u8fd9\u4e5f\u662f\u56e0\u4e3a\u51b7\u6708\u4ed9\u5b50\u58f0\u540d\u9ad8\uff0c\u4ed6\u518d\u4e5f\u4f30\u4e0d\u5230\u8fd9 \u5a07\u602f\u602f\u7684\u5973\u5b50\u7adf\u662f\u6c5f\u6e56\u4e2d\u95fb\u4e4b\u8272\u53d8\u7684\u5973\u715e\u661f\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u51ed\u4f60\u4e5f\u914d\u95ee\u6211\u59d1\u5976\u5976\u7684\u540d\u5b57\u3002\u201d\u624b\u5fae\u626c\uff0c\u7adf\u786c\u751f\u751f\u5c06\u684c \u5b50\u634f\u4e0b\u4e00\u89d2\uff0c\u5f53\u505a\u6697\u5668\u4f7f\u3002 \u3000\u3000\u83ab\u897f\u53ef\u4e0d\u77e5\u9053\u4eba\u5bb6\u7528\u7684\u662f\u4ec0\u4e48\u6697\u5668\uff0c\u53ea\u515a\u98ce\u58f0\u98d5\u7136\uff0c\u624b\u6cd5\u7684\u60ca\u4eba\uff0c\u7adf\u662f\u81ea\u5df1\u524d\u6240\u672a\u89c1 \u7684\u3002 \u3000\u3000\u4ed6\u5f53\u4e0b\u54ea\u91cc\u8fd8\u6562\u6020\u6162\uff0c\u75be\u5fd9\u4e00\u8f6c\u8eab\uff0c\u8eab\u5f62\u75be\u4fa7\uff0c\u90a3\u6697\u5668\u64e6\u80f8\u800c\u8fc7\uff0c\u201c\u593a\u201d\u5730\uff0c\u51fb\u5728\u5899 \u4e0a\u3002 \u3000\u3000\u83ab\u897f\u53ef\u7b97\u662f\u4e45\u7ecf\u5927\u654c\u4e86\uff0c\u89c1\u4e86\u8fd9\u53d1\u6697\u5668\u7684\u624b\u6cd5\uff0c\u5df2\u7ecf\u77e5\u9053\u4eba\u5bb6\u6b66\u529f\u7684\u6df1\u5999\uff0c\u7adf\u662f\u81ea\u5df1\u751f \u5e73\u672a\u7779\uff0c\u5fc3\u4e2d\u5927\u9a87\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5ff5\u5934\u4e5f\u6765\u4e0d\u53ca\u8f6c\u5b8c\uff0c\u53cc\u817f\u4e00\u987f\uff0c\u8eab\u5f62\u75be\u5730\u4ece\u7a97\u53e3\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u51b7\u51b7\u4e00\u7b11\uff0c\u56de\u5934\u5411\u88f4\u73cf\u9053\uff1a\u201c\u4f60\u7b49\u4e00\u4f1a\uff0c\u6211\u9a6c\u4e0a\u5c31\u6765\u3002\u201d \u3000\u3000\u88f4\u73cf\u65b9\u81ea\u7b54\u5e94\uff0c\u773c\u524d\u4e00\u82b1\uff0c\u51b7\u6708\u4ed9\u5b50\u5df2\u5931\u53bb\u8e2a\u8ff9\u4e86\u3002 \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff1a\u201c\u6211\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u5b66\u4f1a\u4eba\u5bb6\u90a3\u6837\u7684\u6b66\u529f\uff1f\u201d\u89c9\u5f97\u5f88\u75b2\u5026\uff0c\u53c8\u89c9\u5f97\u5f88\u997f\u3002 \u3000\u3000\u5c24\u5176\u662f\u201c\u997f\u201d\uff0c\u66f4\u4ee4\u4ed6\u96be\u53d7\uff0c\u987b\u77e5\u4ed6\u5df2\u4e00\u65e5\u672a\u66fe\u8fdb\u98df\u4e86\uff0c\u4f46\u6b64\u523b\u591c\u6df1\u4eba\u9759\uff0c\u53c8\u80fd\u5230\u54ea\u91cc \u627e\u4e1c\u897f\u5403\u5462\uff1f \u3000\u3000\u83ab\u897f\u8eab\u5f62\u731b\u7136\u51e0\u4e2a\u8d77\u843d\uff0c\u4e5f\u63a0\u51fa\u4e86\u6570\u4e08\u8fdc\u8fd1\uff0c\u201c\u5317\u6597\u4e03\u715e\u201d\u4e2d\uff0c\u4ee5\u4ed6\u8f7b\u529f\u6700\u9ad8\uff0c\u5728\u6b66\u6797 \u4e2d\uff0c\u4e09\u715e\u83ab\u897f\u7684\u8f7b\u529f\uff0c\u662f\u9887\u6709\u540d\u6c14\u7684\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u5168\u529b\u800c\u8d74\uff0c\u6697\u5fd6\u603b\u53ef\u4ee5\u9003\u8131\u4eba\u5bb6\u7684\u638c\u63e1\u4e86\uff0c\u4ed6\u4eba\u751a\u673a\u7075\uff0c\u89c1\u673a\u800c\u4f5c\uff0c\u53cd\u5e94\u6700\u5feb\uff0c \u53ea\u8981\u7a0d\u6709\u4e0d\u5bf9\uff0c\u4fbf\u7acb\u523b\u9003\u8d70\uff0c\u662f\u4ee5\u867d\u7136\u4f5c\u6076\u591a\u7aef\uff0c\u4f46\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u5374\u6ca1\u6709\u5403\u8fc7\u4ec0\u4e48\u5927\u4e8f\u3002 \u3000\u3000\u4ed6\u4ee5\u4e3a\u4eca\u65e5\u4e5f\u662f\u4e00\u6837\uff0c\u867d\u7136\u672a\u66fe\u5f97\u624b\uff0c\u4f46\u603b\u7b97\u4e5f\u6ca1\u6709\u5403\u4e8f\u3002 \u3000\u3000\u54ea\u77e5\u80cc\u540e\u500f\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u7b11\u58f0\u5c31\u50cf\u5728\u4ed6\u80cc\u540e\u53d1\u51fa\u7684\uff0c\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u8fde\u8eab\u90fd\u4e0d\u6562\u56de\u8f6c\u53bb\u770b \u4e00\u770b\uff0c\u811a\u5c16\u731b\u70b9\uff0c\u4eba\u5df2\u5411\u5de6\u524d\u65b9\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u51b7\u7b11\u4e4b\u58f0\uff0c\u8fde\u8fde\u4e0d\u7edd\uff0c\u4e5f\u59cb\u7ec8\u9644\u5728\u4ed6\u8eab\u540e\uff0c\u9976\u4ed6\u7528\u5c3d\u8eab\u6cd5\uff0c\u90a3\u51b7\u7b11\u4e4b\u58f0\uff0c\u4ecd\u7136\u8ddf\u5728 \u4ed6\u540e\u9762\u3002 \u3000\u3000\u4ed6\u9b42\u4e0d\u9644\u4f53\uff0c\u6c57\u73e0\u6d94\u6d94\u800c\u843d\uff0c\u77e5\u9053\u4eba\u5bb6\u8f7b\u529f\u9ad8\u51fa\u81ea\u5df1\u751a\u591a\uff0c\u731b\u4e00\u54ac\u7259\uff0c\u8eab\u5f62\u75be\u8f6c\uff0c\u638c\u4e2d \u5224\u5b98\u53cc\u7b14\u6cfc\u5f00\u540e\u6253\uff0c\u60c5\u6025\u800c\u62fc\u547d\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u8fd9\u4e00\u8f6c\u8eab\uff0c\u6240\u53d7\u5230\u7684\u60ca\u9a87\uff0c\u66f4\u975e\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9\u3002 \u3000\u3000\u539f\u6765\u8eab\u540e\u7a7a\u7a7a\uff0c\u9664\u4e86\u8fdc\u65b9\u7684\u5c4b\u9876\uff0c\u88ab\u661f\u5149\u7684\u7167\u5c04\uff0c\u5fae\u5fae\u6709\u4e9b\u767d\u5149\u4e4b\u5916\uff0c\u773c\u4e2d\u6240\u89c1\uff0c\u53ea\u662f \u4e00\u7247\u7a7a\u8361\u800c\u5df2\uff0c\u54ea\u6709\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u518d\u4e00\u8f6c\u8eab\uff0c\u90a3\u51b7\u7b11\u4e4b\u58f0\u7adf\u5982\u9644\u9aa8\u4e4b\u86c6\uff0c\u53c8\u5728\u4ed6\u80cc\u540e\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u83ab\u897f\u53cc\u817f\u53d1\u8f6f\uff0c\u8fd9\u79cd\u60ca\u9a87\uff0c\u7684\u786e\u662f\u4ed6\u5e73\u751f\u4ece\u672a\u7ecf\u5386\u8fc7\u7684\u3002 \u3000\u3000\u987b\u77e5\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u90a3\u65e0\u5f02\u8bf4\u81ea\u5df1\u7684\u6027\u547d\u5df2\u60ac\u5728\u4eba\u5bb6\u624b\u4e2d\uff0c\u53ea\u8981\u4eba\u5bb6\u9ad8\u5174\uff0c\u5c06\u81ea\u5df1\u7684\u8111 \u888b\u6458\u4e0b\uff0c\u4e5f\u662f\u5bb9\u6613\u5f97\u5f88\u3002 \u3000\u3000\u83ab\u897f\u60c5\u6025\u4e4b\u4e0b\uff0c\u5374\u88ab\u4ed6\u60f3\u51fa\u4e00\u6cd5\u6765\uff0c\u8fd9\u5f53\u7136\u4e5f\u662f\u4ed6\u4e45\u7ecf\u5927\u654c\uff0c\u4e34\u654c\u7ecf\u9a8c\u5df2\u4e30\uff0c\u662f\u4ee5\u5728\u60ca \u9a87\u4e4b\u4e2d\uff0c\u4ecd\u672a\u66fe\u5931\u53bb\u81ea\u6551\u7684\u672c\u80fd\u3002 \u3000\u3000\u4ed6\u731b\u7136\u8eab\u5b50\u5f80\u4e0b\u4e00\u5012\uff0c\u8098\u3001\u819d\u3001\u80a9\u5934\u3001\u811a\u817f\uff0c\u4e00\u8d77\u7528\u529b\uff0c\u7adf\u5728\u74e6\u9762\u4e0a\u65bd\u5c55\u51fa\u201c\u71d5\u9752\u5341\u516b \u7ffb\u201d\u7684\u5c0f\u5de7\u529f\u592b\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4f7f\u7528\u8fd9\u79cd\u529f\u592b\u7684\u786e\u662f\u6700\u597d\u7684\u529e\u6cd5\u4e86\u3002 \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u4e8c\u7ae0 \u3000\u3000\u4ed6\u7b97\u76d8\u6253\u5f97\u867d\u597d\uff0c\u54ea\u77e5\u4ed6\u811a\u5c16\u65b9\u4e00\u6cbe\u5730\uff0c\u80cc\u540e\u53c8\u662f\u5176\u5bd2\u6f88\u9aa8\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u4ed6\u60c5\u6025\u4e4b\u4e0b\uff0c \u53cd\u81c2\u4e00\u5200\u62a1\u53bb\uff0c\u98ce\u58f0\u864e\u864e\uff0c\u5012\u4e5f\u6709\u51e0\u5206\u529f\u529b\u3002 \u3000\u3000\u4f46\u4ed6\u4e5f\u77e5\u9053\u8fd9\u4e00\u5200\u7edd\u5bf9\u780d\u4e0d\u7740\u4eba\u5bb6\uff0c\u811a\u5c16\u5fae\u9519\uff0c\u9752\u84dd\u7684\u5200\u5149\u5212\u4e86\u4e2a\u534a\u5706\uff0c\u731b\u5730\u5411\u4e0a\u659c \u6311\uff0c\u5200\u82b1\u4e71\u98a4\uff0c\u201c\u7389\u5e26\u56f4\u5892\u201d\uff0c\u201c\u6885\u82b1\u9519\u843d\u201d\uff0c\u5237\u5237\u4e24\u62db\uff0c\u72e0\u3001\u6bd2\u3001\u5feb\u3001\u51c6\uff0c\u517c\u800c\u6709\u4e4b\u3002 \u3000\u3000\u4ed6\u5200\u5200\u72e0\u8fa3\uff0c\u5374\u4e5f\u5200\u5200\u843d\u7a7a\uff0c\u5200\u5149\u7f24\u7eb7\u4e2d\uff0c\u4ed6\u770b\u89c1\u4e00\u4e2a\u767d\u8272\u7684\u4eba\u5f71\u50cf\u9b3c\u9b45\u4f3c\u7684\u5728\u4ed6\u8eab\u4fa7 \u63a0\u52a8\uff0c\u4ed6\u624b\u638c\u7684\u51b7\u6c57\u5c06\u7f1a\u5728\u5200\u67c4\u4e0a\u7684\u7ef8\u5e03\u90fd\u6e17\u5f97\u6e7f\u900f\u4e86\uff0c\u5374\u8d8a\u53d1\u4e0d\u6562\u505c\u624b\uff0c\u5c06\u4e00\u67c4\u6298\u94c1\u5feb\u5200 \u821e\u5f97\u6ef4\u6c34\u4e0d\u900f\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u51b7\u7b11\u7740\uff0c\u5728\u4ed6\u8eab\u4fa7\u7ed5\u52a8\uff0c\u53cc\u624b\u5782\u5728\u80a9\u4e0b\uff0c\u5374\u4e0d\u8fd8\u624b\uff0c\u83ab\u897f\u7528\u5c3d\u4e86\u201c\u4e94\u864e\u65ad \u95e8\u5200\u201d\u91cc\u6240\u6709\u7cbe\u5999\u7684\u62db\u6570\uff0c\u5374\u8fde\u5979\u7684\u8863\u88f3\u90e8\u78b0\u4e0d\u5230\u4e00\u70b9\uff0c\u4ed6\u4eec\u52a8\u624b\u4e4b\u5904\u672c\u662f\u90a3\u5bb6\u5ba2\u6808\u540e\u9662\uff0c \u5f53\u65f6\u96be\u514d\u60ca\u52a8\u4e86\u4f4f\u5e97\u7684\u65c5\u5ba2\uff0c\u51fa\u95e8\u4eba\u54ea\u4e2a\u613f\u610f\u591a\u4e8b\uff0c\u90fd\u628a\u7a97\u5b50\u5173\u5f97\u7d27\u7d27\u7684\uff0c\u8fde\u770b\u90fd\u4e0d\u6562\u770b\u4e00 \u773c\u3002 \u3000\u3000\u6625\u5bd2\u9661\u5ced\uff0c\u591c\u51e4\u88ad\u4eba\uff0c\u4e09\u715e\u83ab\u897f\u989d\u4e0a\u7684\u6c57\u73e0\uff0c\u5374\u6d94\u6d94\u4e71\u843d\uff0c\u6e10\u6e10\uff0c\u4ed6\u771f\u529b\u8d8a\u53d1\u4e0d\u7ee7\uff0c \u5237\u3001\u5237\u3001\u5237\uff0c\u62fc\u7740\u6700\u540e\u4e4b\u529b\uff0c\u63a5\u8fde\u8fdb\u624b\u4e09\u5200\uff0c\u8eab\u5f62\u4e00\u77ee\uff0c\u55d6\u5730\uff0c\u5411\u540e\u5012\u7a9c\uff0c\u5c06\u8eab\u8eaf\u8d34\u5728\u5899\u58c1 \u4e0a\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u4e3e\u7740\u5200\uff0c\u671b\u7740\u51b7\u6708\u4ed9\u5b50\u6c14\u5598\u54bb\u54bb\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u59d3\u83ab\u7684\u62db\u5b50\u4e0d\u4eae\uff0c\u4e0d\u77e5\u9053\u670b\u53cb\u662f\u9ad8 \u4eba\uff0c\u4eca\u5929\u8ba4\u683d\u4e86\uff0c\u670b\u53cb\u5ff5\u5728\u540c\u662f\u6b66\u6797\u4e00\u6d3e\uff0c\u4eae\u4e2a\u4e07\u513f\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u5c71\u4e0d\u8f6c\u8def\u8f6c\uff0c \u4ee5\u540e\u89c1\u7740\u9762\uff0c\u6211\u59d3\u83ab\u7684\u5144\u5f1f\u4e03\u4eba\uff0c\u603b\u6709\u8865\u62a5\u670b\u53cb\u4e4b\u5904\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u8bf4\u5f97\u4e0d\u4ea2\u4e0d\u5351\uff0c\u867d\u7136\u8ba4\u683d\uff0c\u4f46\u4ecd\u4ea4\u5f85\u5f97\u573a\u9762\u5df2\u6781\uff0c\u679c\u7136\u662f\u8001\u6c5f\u6e56\u7684\u53e3\u543b\uff0c\u54ea\u77e5\u51b7\u6708 \u4ed9\u5b50\u827e\u9752\u4e00\u5411\u8f6f\u786c\u4e0d\u5403\uff0c\u9976\u4f60\u8bf4\u4e0b\u4e2a\u5927\u5929\u6765\uff0c\u5979\u4e5f\u4ecd\u662f\u65e0\u52a8\u4e8e\u8877\uff0c\u51b7\u7b11\u671b\u7740\u83ab\u897f\uff0c\u4e00\u6b65\u6b65\u5730 \u671d\u4ed6\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u5979\u4ecd\u7136\u7a7f\u7740\u7537\u7528\u7684\u6587\u58eb\u8863\u886b\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u8863\u886b\u91cc\u6210\u719f\u7684\u8eaf\u4f53\uff0c\u88ab\u665a\u98ce\u4e00\u5439\uff0c\u66f4\u4e3a\u52a8\u4eba\uff0c \u53ef\u662f\u5e73\u65e5\u597d\u8272\u5982\u547d\u7684\u83ab\u897f\uff0c\u6b64\u523b\u518d\u4e5f\u6ca1\u6709\u5fc3\u60c5\u6765\u6b23\u8d4f\u8fd9\u5a40\u5a1c\u7684\u4f53\u6001\u4e86\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u670b\u53cb\uff0c \u4f60\u672a\u514d\u4e5f\u592a\u4e0d\u8bb2\u6c5f\u6e56\u8fc7\u8282\u4e86\uff0c\u6211\u59d3\u83ab\u7684\u8fde\u6bdb\u90fd\u6ca1\u6709\u78b0\u7740\u4f60\u7684\uff0c\u4f60\u53c8\u4f55\u5fc5\u82e6\u82e6\u76f8\u903c\u3002\u201d\u8bed\u8c03 \u4e2d\uff0c\u5df2\u660e\u663e\u5730\u9732\u51fa\u602f\u610f\u3002 \u3000\u3000\u827e\u9752\u4ecd\u7136\u51b7\u7b11\u7740\uff0c\u50cf\u662f\u6839\u672c\u542c\u4e0d\u61c2\u4ed6\u7684\u8bdd\uff0c\u8fd9\u4e5f\u602a\u4e09\u715e\u83ab\u897f\u5e73\u65e5\u6076\u540d\u592a\u8457\uff0c\u624d\u60f9\u5f97\u8fd9\u4f4d \u8001\u9b54\u5934\u52a8\u4e86\u6740\u673a\uff0c\u800c\u5979\u6740\u673a\u4e00\u52a8\uff0c\u518d\u65e0\u66f4\u6539\u7684\u4e86\u3002 \u3000\u3000\u5979\u8d70\u5f97\u5f88\u6162\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\uff0c\u5374\u50cf\u662f\u90fd\u8e29\u5728\u83ab\u897f\u5fc3\u4e0a\uff0c\u83ab\u897f\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u670b\u53cb\u4f60\u770b \u770b\uff0c\u8be5\u600e\u4e48\u529e\u5427\uff01\u201d\u5f53\u5730\u4e00\u58f0\uff0c\u5c06\u624b\u4e2d\u6298\u94c1\u5feb\u5200\u629b\u5728\u5730\u4e0a\uff0c\u7a81\u5730\u624b\u4e00\u626c\uff0c\u5341\u6570\u70b9\u5bd2\u661f\u81ea\u4ed6\u8896 \u4e2d\u7535\u5c04\u800c\u51fa\uff0c\u6b63\u662f\u4ed6\u6210\u540d\u7edd\u6280\u4e4b\u4e00\uff0c\u201c\u4e03\u661f\u795e\u5f29\u201d\u3002 \u3000\u3000\u4e03\u661f\u795e\u5f29\u540d\u867d\u4e3a\u201c\u5f2f\u201d\uff0c\u5374\u662f\u6bd2\u9488\uff0c\u5e73\u65e5\u5b89\u88c5\u8896\u7ba1\u4e2d\uff0c\u673a\u82eb\u4e00\u52a8\uff0c\u4fbf\u7535\u5c04\u800c\u51fa\uff0c\u4e00\u7b52\u4e03 \u9488\uff0c\u83ab\u897f\u5de6\u53f3\u53cc\u624b\u90fd\u5b89\u7740\u4e00\u7b52\uff0c\u4e0d\u5230\u5371\u6025\u65f6\u7edd\u4e0d\u8f7b\u65bd\uff0c\u4e00\u7ecf\u65bd\u51fa\uff0c\u5bf9\u624b\u5374\u5f88\u5c11\u6709\u80fd\u907f\u5f00\u7684\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u53cc\u624b\u9f50\u626c\uff0c\u5341\u56db\u53e3\u6bd2\u9488\u500f\u5730\u5c04\u51fa\uff0c\u65b9\u5706\u4e24\u4e08\u4e4b\u5185\uff0c\u90fd\u5728\u4ed6\u6bd2\u9488\u7684\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u51b7\u6708 \u4ed9\u5b50\u548c\u4ed6\u76f8\u8ddd\u4e0d\u8fc7\u4e03\u516b\u5c3a\uff0c\u773c\u770b\u5c31\u5c06\u4e27\u5728\u4ed6\u8fd9\u6b79\u6bd2\u7684\u6697\u5668\u4e4b\u4e0b\uff0c\u83ab\u897f\u5f00\u59cb\u51b7\u7b11\uff0c\u5728\u4ed6\u6697\u5668\u51fa \u624b\u7684\u90a3\u4e00\u5239\u90a3\u91cc\uff0c\u4ed6\u5df2\u7ecf\u8ba4\u4e3a\u662f\u4e07\u65e0\u4e00\u5931\u7684\u4e86\u3002 \u3000\u3000\u83ab\u897f\u7ecf\u8fc7\u7684\u5927\u5c0f\u6218\u6597\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u6b21\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4e2a\u6b66\u6797\u7684\u6210\u540d\u82f1\u96c4\uff0c\u4f24\u5728\u4ed6\u8fd9\u5c0f\u5c0f \u7684\u5341\u56db\u53e3\u6bd2\u9488\u4e4b\u4e0b\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u51b7\u7b11\u672a\u7edd\uff0c\u7389\u624b\u8f7b\u62db\uff0c\u90a3\u5341\u56db\u53e3\u6025\u5982\u9aa4\u96e8\u822c\u7684\u98de\u9488\uff0c\u7adf\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u970e\u773c \u95f4\u5931\u53bb\u8e2a\u5f71\uff0c\u4e09\u715e\u83ab\u897f\u9762\u8272\u987f\u65f6\u60e8\u767d\uff0c\u60ca\u547c\u9053\uff1a\u201c\u5343\u624b\u4e66\u751f\uff01\u201d\u865a\u8f6f\u5730\u9760\u5728\u5899\u4e0a\uff0c\u8fde\u6323\u624e\u7684 \u529b\u6c14\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u987b\u77e5\u827e\u9752\u82e5\u4ee5\u5185\u5bb6\u5288\u7a7a\u638c\u529b\u9707\u98de\u8fd9\u4e9b\u6bd2\u9488\uff0c\u6216\u662f\u4ee5\u7edd\u9876\u8f7b\u529f\u907f\u5f00\uff0c\u83ab\u897f\u867d\u4e5f\u4f1a\u60ca\u5f02\uff0c\u5374 \u4e0d\u4f1a\u5413\u5f97\u5982\u6b64\u5389\u5bb3\uff0c\u800c\u827e\u9752\u6b64\u65f6\u6240\u7528\u7684\u624b\u6cd5\uff0c\u6b63\u662f\u5343\u624b\u4e66\u751f\u7684\u72ec\u95e8\u529f\u592b\u201c\u4e07\u6d41\u5f52\u5b97\u201d\uff0c\u4e5f\u5c31 \u662f\u6570\u5341\u5e74\u524d\u540d\u9707\u5929\u4e0b\u7684\u5947\u4eba\u2014\u2014\u4ec7\u5148\u751f\uff0c\u72ec\u6b65\u5929\u4e0b\u7684\u7edd\u6280\u3002 \u3000\u3000\u83ab\u897f\u4e45\u8d70\u6c5f\u6e56\uff0c\u8fd9\u79cd\u624b\u6cd5\u4ed6\u867d\u672a\u5f97\u89c1\uff0c\u5374\u542c\u5f97\u5df2\u4e45\u4e86\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u80fd\u5c06\u4ed6\u201c\u4e03\u661f\u795e\u5f29\u201d \u8fd9\u79cd\u6697\u5668\u6536\u53bb\u7684\uff0c\u4e5f\u53ea\u6709\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u8fd9\u79cd\u624b\u6cd5\uff0c\u800c\u666e\u5929\u4e4b\u4e0b\u7684\u6b66\u6797\u4e2d\u4eba\uff0c\u80fd\u591f\u5f97\u8fd9\u7edd\u9876\u5185 \u529f\u4f20\u6388\u7684\uff0c\u4e5f\u53ea\u6709\u5343\u624b\u4e66\u751f\u592b\u5987\u4e24\u4eba\u3002 \u3000\u3000\u83ab\u897f\u9a87\u60ca\u800c\u547c\uff0c\u4ed6\u5634\u91cc\u867d\u53eb\u7740\u201c\u5343\u624b\u4e66\u751f\u201d\uff0c\u5fc3\u91cc\u53ef\u60f3\u5230\u4e86\u5bf9\u624b\u5c31\u662f\u201c\u51b7\u6708\u4ed9\u5b50\u201d\uff0c\u827e \u9752\u53c8\u7f13\u7f13\u5411\u4ed6\u8d70\u8fd1\u4e86\u4e24\u6b65\uff0c\u4ed6\u66ae\u5730\u4e00\u58f0\u5389\u543c\uff0c\u53cc\u624b\u5341\u6307\u7b95\u5f20\uff0c\u7eb5\u8eab\u6251\u4e86\u4e0a\u53bb\uff0c\u65e0\u62db\u65e0\u5f0f\uff0c\u5c45 \u7136\u70c2\u6253\u4e86\u3002 \u3000\u3000\u827e\u9752\u4e00\u58f0\u51b7\u7b11\uff0c\u7389\u638c\u6325\u5904\uff0c\u4e5f\u662f\u5341\u56db\u70b9\u5bd2\u661f\u7535\u5c04\u800c\u51fa\uff0c\u4e24\u7b52\u201c\u4e03\u661f\u795e\u5f29\u201d\u7adf\u539f\u7269\u5949\u56de\uff0c \u83ab\u897f\u4e00\u58f0\u60e8\u547c\uff0c\u5341\u56db\u53e3\u6bd2\u9488\u5168\u5c04\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u5a07\u5a1c\u7684\u8eab\u8eaf\u4e00\u52a8\uff0c\u8f6c\u8eab\u63a0\u8d77\u3002\u5bf9\u83ab\u897f\u770b\u4e5f\u672a\u518d\u770b\u4e00\u773c\uff0c\u767d\u8272\u7684\u4eba\u5f71\u4e00\u95ea\uff0c \u53ea\u7559\u4e0b\u6fd2\u4e34\u6c14\u7edd\u7684\u83ab\u897f\u8eba\u5728\u5730\u4e0a\u54c0\u547c\u3002 \u3000\u3000\u827e\u9752\u4ee5\u6781\u5feb\u7684\u901f\u5ea6\u5728\u5c4b\u9876\u4e0a\u5de1\u89c6\u4e86\u4e00\u8f6c\uff0c\u8ba4\u6e05\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u7a97\u6237\u4ecd\u662f\u5f00\u7740\u7684\uff0c\u5979\u6beb\u4e0d\u8e0c \u8e87\u5730\u63a0\u4e86\u8fdb\u53bb\uff0c\u88f4\u73cf\u4ecd\u7a7f\u7740\u90a3\u4ef6\u5927\u7ea2\u5973\u5b50\u8863\u88f3\uff0c\u4f0f\u5728\u5e8a\u4e0a\uff0c\u597d\u50cf\u5df2\u7ecf\u7761\u7740\u4e86\u7684\u6837\u5b50\u3002 \u3000\u3000\u827e\u9752\u4e00\u7b11\uff0c\u8f7b\u8f7b\u95ee\u9053\uff1a\u201c\u5582\uff01\u4f60\u7761\u7740\u4e86\u5417\uff1f\u201d\u88f4\u73cf\u4ecd\u7136\u4f0f\u5728\u5e8a\u4e0a\uff0c\u52a8\u4e5f\u672a\u52a8\u4e00\u4e0b\uff0c\u827e\u9752 \u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u771f\u6709\u4e9b\u4e4f\u4e86\uff0c\u8f7b\u8f7b\u548c\u8863\u8eba\u5728\u5e8a\u89d2\uff0c\u4f46\u5374\u4e0d\u77e5\u600e\u5730\uff0c\u773c\u775b\u867d\u5408\u4e0a\u4e86\uff0c\u4eba\u4e5f\u75b2\u5026\u5f97 \u5f88\uff0c\u4f46\u5374\u4e00\u70b9\u513f\u7761\u610f\u4e5f\u6ca1\u6709\uff0c\u53ea\u662f\u95ed\u7740\u773c\u775b\u517b\u795e\u3002 \u3000\u3000\u623f\u91cc\u65e0\u706f\uff0c\u4f46\u7a97\u5916\u6709\u661f\u6708\u4e4b\u5149\u5c04\u8fdb\u6765\uff0c\u662f\u4ee5\u5149\u7ebf\u5e76\u4e0d\u5341\u5206\u9ed1\u6697\uff0c\u5979\u8eba\u5728\u5e8a\u4e0a\uff0c\u89c9\u5f97\u6709\u4e00 \u4e1d\u5bd2\u610f\uff0c\u6726\u80e7\u4e4b\u95f4\uff0c\u89c9\u5f97\u88f4\u73cf\u4f3c\u4e4e\u52a8\u5f39\u4e86\u4e00\u4e0b\uff0c\u7741\u5f00\u773c\u775b\u4e00\u770b\uff0c\u4ece\u7a97\u5b50\u91cc\u7167\u8fdb\u6708\u5149\uff0c\u521a\u597d\u7167 \u5728\u8eba\u5728\u5979\u65c1\u8fb9\u7684\u90a3\u4eba\u7684\u8138\u4e0a\uff0c\u5979\u7adf\u54ce\u5440\u4e00\u58f0\uff0c\u60ca\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4eba\u7adf\u4e0d\u662f\u88f4\u73cf\uff0c\u9634\u607b\u607b\u5730\u51b7\u7b11\u4e00\u4e0b\u3002\u827e\u9752\u9762\u8272\u5982\u571f\uff0c\u53cc\u65f6\u4e00\u8d77\u7528\u529b\uff0c\u8170\u4e00\u633a\uff0c\u60f3\u63a0\u8d77 \u6765\uff0c\u90a3\u4eba\u53f3\u65f6\u652f\u5728\u5e8a\u4e0a\uff0c\u5de6\u624b\u5fae\u4f38\uff0c\u90a3\u4e48\u6070\u5230\u597d\u5904\u5730\u70b9\u5728\u827e\u9752\u8170\u4e0a\uff0c\u751f\u50cf\u662f\u827e\u9752\u7684\u8170\u81ea\u5df1\u9001 \u4e0a\u6765\u88ab\u4ed6\u70b9\u7684\u4e00\u6837\uff0c\u827e\u9752\u8170\u4e00\u8f6f\uff0c\u5427\u5730\u53c8\u5012\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u90a3\u4eba\u8138\u4e0a\u9732\u51fa\u5f97\u610f\u7684\u795e\u8272\uff0c\u8eab\u5f62\u4e00\u52a8\uff0c\u50cf\u662f\u6709\u4eba\u5728\u4e0b\u9762\u6258\u7740\u4ed6\u4f3c\u7684\uff0c\u865a\u98d8\u98d8\u5730\u4ece\u5e8a\u4e0a\u63a0 \u4e86\u8d77\u6765\uff0c\u5c06\u8eab\u4e0a\u7684\u90a3\u4ef6\u706b\u7ea2\u7f0e\u5b50\u5973\u8863\u8131\u4e86\uff0c\u9732\u51fa\u91cc\u9762\u624b\u5de5\u6781\u5176\u7cbe\u81f4\uff0c\u8d28\u6599\u4e5f\u5f02\u5e38\u9ad8\u8d35\u7684\u77ed\u886b \u88e4\u6765\u3002 \u3000\u3000\u4ed6\u8f6c\u5230\u5e8a\u540e\uff0c\u671b\u4e86\u88ab\u4ed6\u70b9\u4e2d\u7a74\u9053\uff0c\u8eba\u5728\u5730\u4e0a\u7684\u88f4\u73cf\u4e00\u773c\uff0c\u5634\u89d2\u6cdb\u8d77\u4e00\u4e2a\u72e0\u6bd2\u7684\u7b11\u5bb9\uff0c\u5c06 \u6302\u5728\u5e8a\u540e\u7684\u4e00\u4ef6\u7070\u8272\u957f\u886b\u53d6\u6765\u7a7f\u4e0a\uff0c\u8eab\u5f62\u663e\u5f97\u6781\u4e3a\u51c4\u82cd\uff0c\u8d70\u56de\u5e8a\u524d\u5bf9\u827e\u9752\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u6765 \u4e86\u5427\uff1f\u201d\u8bed\u8c03\u4e2d\u5e26\u7740\u4e09\u5206\u8ba5\u8bee\u548c\u4e03\u5206\u6028\u6068\u7684\u610f\u5473\u3002 \u3000\u3000\u201c\u66f4\u60f3\u4e0d\u5230\u7684\uff0c\u603b\u7b97\u8ba9\u6211\u6293\u7740\u4e86\u4f60\u3002\u201d\u4ed6\u773c\u4e2d\u95ea\u52a8\u7740\u9e70\u96bc\u4e00\u6837\u7684\u5149\u8292\uff0c\u51b7\u7b11\u7740\u9053\uff1a\u201c\u4f60 \u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d\u4f38\u624b\u6293\u8d77\u4e86\u827e\u9752\uff0c\u4e5f\u5c31\u50cf\u9e70\u96bc\u652b\u8d77\u5c0f\u9e21\u90a3\u4e48\u6837\u8f7b\u6613\u548c\u5b89\u7965\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u63a0 \u5230\u7a97\u53e3\uff0c\u5ffd\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u63a0\u5230\u5e8a\u540e\uff0c\u9a84\u6307\u5982\u5251\uff0c\u5728\u88f4\u73cf\u8eab\u4e0a\u75be\u70b9\u4e86\u4e24\u4e0b\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u4ece\u540e\u7a97 \u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u662f\u90a3\u4e48\u8f7b\u7075\u800c\u66fc\u5999\uff0c\u50cf\u9053\u8f7b\u70df\u4f3c\u7684\uff0c\u5012\u8eba\u5728\u5e8a\u540e\u9634\u6697\u89d2\u843d\u540e\u7684\u88f4\u73cf\uff0c\u5fc3\u91cc\u89c9\u5f97\u8bf4 \u4e0d\u51fa\u6765\u7684\u59d4\u5c48\uff0c\u5bf9\u4e8e\u8fd9\u4e00\u5207\uff0c\u4ed6\u90fd\u89c9\u5f97\u6709\u4e9b\u832b\u7136\u3002 \u3000\u3000\u65b9\u624d\u7fa1\u6155\u5730\u770b\u7740\u827e\u9752\u63a0\u51fa\u623f\u53bb\uff0c\u4ed6\u53c8\u7d2f\u3001\u53c8\u997f\uff0c\u4f4e\u5934\u770b\u5230\u81ea\u5df1\u8eab\u4e0a\u4ecd\u7a7f\u7740\u90a3\u4ef6\u5927\u7ea2\u5973 \u886b\uff0c\u89c9\u5f97\u53c8\u7f9e\u3001\u53c8\u6127\uff0c\u7ad9\u8d77\u6765\uff0c\u65b9\u60f3\u8131\u6389\uff0c\u4ed6\u51fa\u6765\u624d\u4e00\u65e5\uff0c\u4f46\u8fd9\u4e00\u5929\u4e2d\u4ed6\u7ecf\u5386\u7684\u4e8b\u5374\u6bd4\u4ed6\u4e00 \u751f\u4e2d\u5176\u4ed6\u65e5\u5b50\u7684\u603b\u548c\u4eff\u4f5b\u8fd8\u591a\u4e9b\uff0c\u4ed6\u6709\u4e9b\u96be\u53d7\uff0c\u5374\u53c8\u5f88\u5174\u594b\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u89c9\u5f97\u50cf\u662f\u6709\u4e9b\u58f0\u97f3\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u5374\u770b\u89c1\u4e00\u4e2a\u7626\u957f\u7684\u4eba\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5df2\u6765\u5230\u8eab \u524d\uff0c\u4ed6\u60ca\u547c\u4e00\u58f0\uff0c\u5f80\u540e\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u90a3\u4eba\u7a7f\u7740\u7070\u8272\u7684\u6587\u58eb\u957f\u886b\uff0c\u88f4\u73cf\u770b\u4e0d\u6e05\u4ed6\u7684\u9762\u8c8c\uff0c\u58ee\u9752\u80c6\u5b50\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u51b7 \u51b7\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u88f4\u73cf\u89c9\u5f97\u6709\u80a1\u8bf4\u4e0d\u51fa\u6765\u7684\u5bd2\u610f\uff0c\u55eb\u5685\u7740\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u90a3\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u8eab\u8eaf\u7a0d\u5fae\u79fb\u52a8\u4e86 \u4e00\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u827e\u9752\u5462\uff1f\u201d \u3000\u3000\u6709\u5149\u4ece\u7a97\u5916\u5c04\u8fdb\u6765\uff0c\u90a3\u4eba\u4e00\u4fa7\u8138\uff0c\u88f4\u73cf\u770b\u5230\u90a3\u4eba\u7684\u4fa7\u5f71\uff0c\u5bbd\u989d\u9e70\u9f3b\uff0c\u7ebf\u6761\u6781\u5176\u7a81\u51fa\uff0c\u90a3 \u4eba\u8d70\u4e0a\u4e00\u6b65\uff0c\u7d27\u7d27\u8ffd\u95ee\u9053\uff1a\u201c\u827e\u9752\u5462\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4e0b\u610f\u8bc6\u5730\u4e00\u6307\u7a97\u53e3\uff0c\u9053\uff1a\u201c\u5979\u51fa\u53bb\u4e86\u3002\u201d\u90a3\u4eba\u773c\u73e0\u4e00\u8f6c\uff0c\u88f4\u73cf\u53ea\u89c9\u5f97\u8eab\u5f62\u50cf\u98ce\u4e00\u6837 \u5377\u4e86\u8fc7\u6765\uff0c\u81ea\u5df1\u8170\u4e0a\u4e00\u9ebb\uff0c\u5df2\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u90a3\u4eba\u4e00\u624b\u63d0\u8d77\u4e86\u4ed6\uff0c\u53e3\u4e2d\u5583\u5583\u4f4e\u8bed\u7740\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u627e\u4e0d\u7740\u5979\uff0c\u539f\u6765\u5979\u627e\u7740\u4e86\u6c49\u5b50\u3002\u201d \u3000\u3000\u4f4e\u5934\u53c8\u770b\u4e86\u88f4\u73cf\u4e00\u773c\uff0c\u5478\u4e86\u4e00\u53e3\uff0c\u9a82\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5979\u7adf\u770b\u4e0a\u4e86\u4f60\u8fd9\u7537\u4e0d\u7537\uff0c\u5973\u4e0d\u5973\u7684\u5154 \u5d3d\u5b50\u3002\u201d \u3000\u3000\u88f4\u73cf\u4e0d\u77e5\u9053\u4ed6\u5728\u8bb2\u4e9b\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\uff0c\u5bf9\u4ed6\u540e\u9762\u7684\u90a3\u53e5\u8bdd\uff0c\u4ed6\u5012\u6709\u4e9b\u4f1a\u610f\uff0c\u89c9\u5f97 \u4e00\u809a\u5b50\u7684\u51a4\u5c48\uff0c\u5374\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4eba\u7830\u5730\u5c06\u88f4\u73cf\u629b\u5728\u5e8a\u540e\uff0c\u88f4\u73cf\u53ea\u89c9\u5f97\u56db\u8170\u53d1\u8f6f\uff0c\u8f6f\u4e2d\u53c8\u5e26\u7740\u9ebb\u75f9\uff0c\u4e00\u52a8\u4e5f\u4e0d\u80fd\u52a8\u5730\u8eba \u5728\u5730\u4e0a\u3002\u6b64\u523b\u90a3\u4eba\u6293\u7740\u4ed6\uff0c\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u8fd8\u5728\u4ed6\u524d\u80f8\u3001\u989a\u4e0b\u75be\u5730\u70b9\u4e86\u4e00\u4e0b\u3002\u4ed6\u4e5f\u4f1a\u4e9b\u6b66\u529f\uff0c \u5bf9\u7a74\u9053\u5374\u662f\u4e00\u70b9\u4e5f\u4e0d\u61c2\uff0c\u4e0d\u77e5\u9053\u4eba\u5bb6\u7a76\u7adf\u70b9\u5728\u81ea\u5df1\u54ea\u4e00\u4e2a\u7a74\u9053\u4e0a\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u4e45\uff0c\u4e5f\u8bb8\u53ea\u662f\u7247\u523b\uff0c\u7136\u800c\u4ed6\u5374\u89c9\u5f97\u50cf\u4e00\u5e74\u822c\u90a3\u4e48\u957f\uff0c\u4ed6\u8033\u6735\u672c\u662f\u8d34\u5728\u5730 \u4e0a\uff0c\u6b64\u523b\u542c\u5230\u623f\u4e2d\u6709\u4e9b\u58f0\u97f3\uff0c\u4ed6\u5168\u8eab\u6c57\u6bdb\u90fd\u7acb\u4e86\u8d77\u6765\uff0c\u5589\u4e2d\u4e0d\u7981\u4f4e\u4f4e\u53d1\u51fa\u547b\u541f\u7684\u58f0\u97f3\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u89c9\u5f97\u773c\u524d\u53c8\u662f\u4e00\u82b1\uff0c\u4e00\u53cc\u7a7f\u7740\u7c89\u5e95\u6731\u5c65\u7684\u811a\u8d6b\u7136\u6765\u5230\u4ed6\u773c\u524d\uff0c\u4ed6\u8eab\u5b50\u4e0d\u80fd\u52a8\uff0c \u4e5f\u65e0\u6cd5\u770b\u5230\u90a3\u4eba\u7684\u4e0a\u8eab\u3002 \u3000\u3000\u63a5\u7740\uff0c\u90a3\u53cc\u7a7f\u7740\u7c89\u5e95\u6731\u5c65\u7684\u811a\u4e00\u52a8\uff0c\u671d\u4ed6\u8170\u773c\u8e22\u4e86\u4e24\u811a\uff0c\u4ed6\u89c9\u5f97\u5468\u8eab\u5927\u75db\uff0c\u5374\u4ecd\u7136\u4e0d\u80fd \u52a8\uff0c\u90a3\u4eba\u4f3c\u4e4e\u6781\u4e3a\u60ca\u5f02\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u539f\u6765\u662f\u4ed6\u7684\u72ec\u95e8\u70b9\u7a74\u3002\u201d\u642c\u8d77\u88f4\u73cf\u7684\u8eab \u5b50\uff0c\u5728\u88f4\u73cf\u540e\u5fc3\u6781\u5feb\u5730\u62cd\u4e86\u5341\u51e0\u638c\u3002 \u3000\u3000\u88f4\u73cf\u89c9\u5f97\u5468\u8eab\u7684\u9aa8\u8282\u50cf\u662f\u6563\u4e86\u4e00\u6837\uff0c\u731b\u5730\u5410\u51fa\u4e00\u53e3\u6d53\u75f0\uff0c\u8eab\u5b50\u867d\u7136\u6709\u75c5\uff0c\u4f46\u5374\u53ef\u4ee5\u52a8\u5f39 \u4e86\uff0c\u6162\u6162\u6323\u624e\u7740\u722c\u8d77\u6765\uff0c\u770b\u5230\u4e00\u4e2a\u7a7f\u7740\u94f6\u8272\u957f\u886b\u7684\u4eba\uff0c\u5e26\u7740\u4e00\u8138\u8f7b\u8511\u4e4b\u8272\uff0c\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u9894 \u4e0b\u5fae\u5fae\u84c4\u7740\u4e9b\u77ed\u9aed\uff0c\u795e\u60c5\u65e2\u6e05\u4fca\uff0c\u53c8\u9ad8\u50b2\uff0c\u88f4\u73cf\u770b\u8d77\u6765\uff0c\u7adf\u50cf\u5929\u795e\u4f3c\u7684\uff0c\u60f3\u5230\u81ea\u5df1\uff0c\u81ea\u5351\u4e4b \u611f\uff0c\u53c8\u4e0d\u89c9\u800c\u751f\u3002 \u3000\u3000\u6b64\u523b\u5df2\u7ecf\u6709\u4e9b\u66d9\u8272\u4e86\uff0c\u662f\u4ee5\u88f4\u73cf\u80fd\u591f\u770b\u5230\u4ed6\u8138\u4e0a\u7684\u795e\u8272\uff0c\u4ed6\u4e5f\u80fd\u770b\u5f97\u51fa\u88f4\u73cf\u7684\u8138\uff0c\u7709\u5934 \u4e00\u76b1\uff0c\u4f3c\u662f\u975e\u5e38\u4e0d\u5c51\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u91cc\u6709\u8bf4\u4e0d\u51fa\u7684\u96be\u53d7\uff0c\u4f4e\u4e0b\u5934\u53bb\uff0c\u4ed6\u89c9\u5f97\u6b64\u523b\u50cf\u662f\u7279\u522b\u5b89\u9759\uff0c\u8033\u7554\u7adf\u4ec0\u4e48\u58f0\u97f3\u4e5f\u542c \u4e0d\u5230\uff0c\u50cf\u662f\u5927\u5730\u90fd\u7761\u719f\u4e86\u4f3c\u7684\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u89c9\u5f97\u90a3\u4eba\u53c8\u8e22\u4e86\u4ed6\u4e00\u811a\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u770b\u5230\u90a3\u4eba\u7684\u5634\u671d\u4ed6\u52a8\u4e86\u51e0\u4e0b\uff0c\u4ed6\u5374\u4e00\u70b9\u58f0 \u97f3\u4e5f\u542c\u4e0d\u89c1\uff0c\u5fc3\u91cc\u4e0d\u7981\u5347\u8d77\u4e86\u6781\u5927\u7684\u6050\u60e7\uff0c\u5f20\u53e3\u60f3\u5450\u558a\uff0c\u54ea\u77e5\u5374\u53ea\u80fd\u53d1\u51fa\u6781\u4f4e\u5fae\u7684\u201c\u5440\u3001 \u5440\u201d\u4e4b\u58f0\uff0c\u4ed6\u7740\u6025\u5730\u6293\u7740\u81ea\u5df1\u7684\u5934\u53d1\uff0c\u5fc3\u91cc\u50cf\u662f\u7a81\u7136\u5835\u585e\u4f4f\u4e86\u51e0\u5341\u5757\u5de8\u77f3\uff0c\u538b\u5f97\u4ed6\u900f\u4e0d\u8fc7\u6c14 \u6765\u3002 \u3000\u3000\u90a3\u4eba\u5782\u7740\u5934\u671b\u7740\u4ed6\uff0c\u76ee\u5149\u4e2d\u7adf\u6ca1\u6709\u4e00\u4e1d\u601c\u60af\uff0c\u5bf9\u4e8e\u4e16\u4e0a\u4e00\u5207\u503c\u5f97\u601c\u60af\u7684\u4e8b\uff0c\u4ed6\u5374\u65bd\u4e4b\u4ee5 \u8f7b\u8511\uff0c\u4e00\u624b\u6293\u8d77\u4e86\u88f4\u73cf\u7684\u5934\u53d1\uff0c\u7aef\u8be6\u4e86\u51e0\u773c\uff0c\u500f\u7136\u677e\u624b\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u8fd9\u53ae\u7684\u624b\u6bb5\uff0c\u679c\u7136\u72e0\u6bd2 \u5df2\u5230\u6781\u5904\u3002\u201d\u671b\u4e86\u88f4\u73cf\u4e00\u773c\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u80fd\u602a\u4f60\u6ca1\u51fa\u606f\u3002\u201d\u811a\u6b65\u4e00\u9519\uff0c\u6084\u7136\u6e9c\u5f00\u4e86\u6570\u5c3a\uff0c\u8863 \u886b\u4e00\u98d8\uff0c\u94f6\u6ce2\u78f7\u78f7\uff0c\u88f4\u73cf\u773c\u5149\u968f\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u4ed6\u7684\u8eab\u5f62\u7adf\u50cf\u662f\u6bd4\u4eba\u5bb6\u7684\u773c\u5149\u8fd8\u5feb\uff0c\u970e\u773c\u4e4b \u95f4\uff0c\u4ed6\u5df2\u5931\u53bb\u4e86\u8e2a\u8ff9\u3002 \u3000\u3000\u88f4\u73cf\u773c\u4e2d\u6c69\u6c69\u6d41\u4e0b\u6cea\u6765\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u4e0d\u4f46\u804b\uff0c\u800c\u4e14\u4e5f\u54d1\u4e86\uff0c\u90a3\u94f6\u886b\u7684\u4e2d\u5e74\u4eba\u5634\u91cc\u8bb2\u7684\u8bdd \u4ed6\u867d\u7136\u542c\u4e0d\u5230\uff0c\u53ef\u662f\u8138\u4e0a\u90a3\u79cd\u8f7b\u8511\u7684\u795e\u8272\uff0c\u88f4\u73cf\u5374\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u4ed6\u5fc3\u9ad8\u6c14\u505a\uff0c\u5374\u5904\u5904\u53d7\u7740 \u538b\u5236\uff0c\u5904\u5904\u88ab\u4eba\u6b3a\u8d1f\uff0c\u9047\u5230\u51b7\u6708\u4ed9\u5b50\uff0c\u521a\u521a\u6709\u4e86\u4e00\u4e9b\u5b66\u6210\u6b66\u529f\u7684\u5e0c\u671b\uff0c\u54ea\u77e5\u53c8\u51fa\u4e86\u8fd9\u79cd\u4e8b\uff0c \u4ed6\u7684\u5e0c\u671b\u5b8c\u5168\u7834\u706d\u4e86\uff0c\u81ea\u5df1\u4e5f\u53d8\u6210\u4e00\u4e2a\u65e2\u804b\u4e14\u54d1\u7684\u6b8b\u5e9f\uff0c\u4ed6\u7d27\u7d27\u627c\u7740\u81ea\u5df1\u7684\u5589\u5499\uff0c\u6068\u4e0d\u5f97\u7acb \u65f6\u6b7b\u53bb\u3002 \u3000\u3000\u8fd9\u4e16\u754c\uff0c\u8fd9\u751f\u547d\uff0c\u5bf9\u4ed6\u8bf4\u6765\uff0c\u662f\u672a\u514d\u592a\u6b8b\u9177\u4e86\u4e9b\uff0c\u8fd9\u5e74\u8f7b\u4eba\u672c\u8be5\u50cf\u671d\u65e5\u4e00\u6837\u7684\u591a\u5f69\u800c\u7eda \u4e3d\uff0c\u7136\u800c\uff0c\u82cd\u5929\u5374\u8ba9\u4ed6\u6bd4\u96e8\u591c\u8fd8\u8981\u7070\u6697\u3002 \u3000\u3000\u6653\u8272\u65b9\u5f00\uff0c\u65ed\u65e5\u4e1c\u5347\uff0c\u6709\u5149\u4ece\u7a97\u53e3\u5c04\u4eba\uff0c\u5c06\u8fd9\u95ee\u6597\u5ba4\u7167\u5f97\u5149\u4eae\u5df2\u6781\u3002 \u3000\u3000\u5149\u7ebf\u7167\u8fc7\u7684\u5730\u65b9\uff0c\u5c06\u5ba4\u4e2d\u7684\u5c18\u57c3\uff0c\u7167\u6210\u4e00\u6761\u7070\u67f1\uff0c\u88f4\u73cf\u5446\u5446\u7684\u671b\u7740\uff0c\u95ee\u7740\u81ea\u5df1\uff1a\u201c\u4e3a\u4ec0 \u4e48\u5728\u6709\u5149\u7684\u5730\u65b9\u624d\u6709\u7070\u5c18\u5462\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u77ac\u5373\u4e3a\u81ea\u5df1\u627e\u5230\u4e86\u7b54\u6848\uff1a\u201c\u539f\u6765\u662f\u5149\u7ebf\u5c06\u7070\u5c18\u7167\u51fa\u6765\uff0c\u6ca1\u6709\u5149\u7684\u5730\u65b9\u4e5f\u6709\u7070\u5c18\uff0c\u53ea \u662f\u6211\u4eec\u770b\u4e0d\u5230\u7f62\u4e86\u3002\u201d\u4ed6\u5782\u4e0b\u5934\uff0c\u5fc3\u60c5\u66f4\u4e3a\u8427\u7d20\uff0c\u4ed6\u60f3\uff1a\u201c\u8fd9\u4e16\u754c\u591a\u4e48\u4e0d\u516c\u5e73\uff01\u5149\u7ebf\u4e3a\u4ec0\u4e48 \u4e0d\u628a\u6240\u6709\u7684\u7070\u5c18\u90fd\u7167\u51fa\u6765\u5462\uff1f\u4e3a\u4ec0\u4e48\u8ba9\u90a3\u4e9b\u7070\u5c18\u8eb2\u5728\u9ed1\u6697\u91cc\u5462\uff1f\u201d \u3000\u3000\u84e6\u5730\uff0c\u95e8\u5916\u6709\u5e97\u4f19\u7684\u53eb\u58f0\uff1a\u201c\u5ba2\u5b98\uff0c\u5929\u4eae\u4e86\uff0c\u8981\u8d76\u8def\u7684\u8be5\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u58f0\u97f3\u867d\u7136\u5b8f\u4eae\uff0c\u4f46\u88f4\u73cf\u5374\u4e00\u4e1d\u4e5f\u542c\u4e0d\u5230\uff0c\u7a97\u5916\u9633\u5149\u66f4\u76db\uff0c\u4ed6\u7684\u5fc3\u60c5\uff0c\u5374\u548c\u7a97\u5916\u7684\u5929\u6c14\u76f8 \u53cd\uff1a\u201c\u65e0\u4eae\u4e86\uff0c\u6211\u8be5\u8d70\u4e86\uff0c\u4f46\u662f\u6211\u8d70\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d\u867d\u7136\u5f3a\u5fcd\u7740\uff0c\u773c\u6cea\u4ecd\u7136\u6cbe\u6e7f\u4e86\u4ed6\u7684\u773c \u5e18\u3002 \u3000\u3000\u201c\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u5b81\u53ef\u6d41\u8840\uff0c\u4e5f\u4e0d\u80fd\u6d41\u6cea\u7684\u3002\u201d\u4ed6\u54ac\u7740\u7259\uff0c\u73af\u987e\u8fd9\u6597\u5ba4\u4e00\u6b21\uff0c\u84e6\u5730\u770b\u5230 \u51b7\u6708\u4ed9\u5b50\u6709\u4e2a\u5c0f\u5305\u88b1\u4ecd\u7136\u653e\u5728\u684c\u5b50\u4e0a\uff0c\u4ed6\u8003\u8651\u7740\uff0c\u8be5\u4e0d\u8be5\u53bb\u62ff\u8d70\uff1a\u201c\u522b\u4eba\u7684\u4e1c\u897f\uff0c\u6211\u80fd\u62ff \u5417\uff1f\u201d\u4ed6\u8111\u6d77\u4e2d\u4e0d\u505c\u5730\u8f6c\u52a8\u7740\uff0c\u84e6\u5730\u60f3\u8d77\uff1a\u201c\u4f46\u662f\u6211\u4f4f\u4e86\u5e97\uff0c\u8be5\u4ed8\u5e97\u94b1\u7684\u3002\u201d\u4e8e\u662f\u4ed6\u8d70\u8fc7 \u53bb\uff0c\u5c06\u90a3\u5305\u88b1\u89e3\u5f00\uff0c\u91cc\u9762\u679c\u7136\u6709\u4e00\u6574\u952d\u5143\u5b9d\u548c\u4e00\u4e9b\u6563\u788e\u94f6\u5b50\uff0c\u4ed6\u8fde\u5fd9\u62ff\u4e86\u4e00\u4e9b\uff0c\u5c06\u90a3\u5305\u88b1\u53c8 \u624e\u597d\uff0c\u6574\u4e86\u6574\u8eab\u4e0a\u7684\u77ed\u886b\u88e4\uff0c\u8d70\u51fa\u623f\u53bb\u3002 \u3000\u3000\u6628\u591c\u7684\u5267\u6597\uff0c\u4f7f\u5f97\u5e97\u4f19\u5bf9\u88f4\u73cf\u4e0d\u7981\u53e6\u773c\u76f8\u770b\uff0c\u6240\u4ee5\u4ed6\u867d\u7136\u5728\u5947\u602a\u6628\u591c\u8fdb\u6765\u4e86\u4e24\u4eba\uff0c\u4eca\u5929 \u5374\u53ea\u51fa\u6765\u4e00\u4e2a\uff0c\u800c\u4e14\u6628\u591c\u662f\u5973\u7684\uff0c\u4eca\u6668\u5374\u53d8\u4e86\u7537\u7684\uff0c\u4f46\u662f\u4ed6\u5374\u81ea\u5df1\u8b66\u544a\u81ea\u5df1\uff1a\u201c\u5c11\u591a\u4e8b\uff0c\u8bf4 \u4e0d\u5b9a\u8fd9\u4e5f\u662f\u6c5f\u6d0b\u5927\u76d7\uff0c\u4f60\u8981\u591a\u4e8b\uff0c\u4eba\u5bb6\u4e5f\u8bb8\u5c31\u4f1a\u7ed9\u4f60\u4e00\u5200\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u4e00\u58f0\u4e0d\u54cd\u5730\u8dd1\u8fc7\u53bb\uff0c\u88f4\u73cf\u7ed9\u4e86\u4ed6\u4e00\u4e9b\u94f6\u5b50\uff0c\u4e00\u6325\u624b\uff0c\u8868\u793a\u8bf4\uff1a\u201c\u591a\u7684\u4f60\u62ff\u53bb \u5427\uff01\u201d\u5e97\u4f19\u4e00\u770b\uff0c\u975e\u4f46\u4e0d\u591a\uff0c\u8fd8\u5c11\u4e86\u4e00\u70b9\uff0c\u4f46\u662f\u4e5f\u4e0d\u6562\u591a\u8bf4\uff0c\u5c06\u827e\u9752\u7684\u9a6c\u7275\u4e86\u51fa\u6765\uff0c\u966a\u7740\u7b11 \u9053\uff1a\u201c\u5ba2\u5b98\u591a\u5149\u987e\u3002\u201d\u5fc3\u91cc\u5374\u5728\u5492\u7740\u88f4\u73cf\u7684\u7956\u5b97\uff1a\u201c\u4f4f\u5e97\u4e0d\u7ed9\u94b1\uff0c\u8fd8\u8981\u94c1\u9752\u7740\u8138\u5145\u5927\u7237\uff0c\u770b \u4f60\u8fd9\u6837\u5b50\uff0c\u516b\u6210\u662f\u4e2a\u5154\u4e8c\u7237\u3002\u201d \u3000\u3000\u4f46\u88f4\u73cf\u8fde\u4ed6\u53e3\u4e2d\u8bb2\u7684\u8bdd\u90fd\u542c\u4e0d\u5230\uff0c\u5f53\u7136\u66f4\u4e0d\u4f1a\u77e5\u9053\u4ed6\u5fc3\u91cc\u60f3\u7684\u4e86\uff0c\u63a5\u7740\u9a6c\u7f30\uff0c\u5fc3\u91cc\u6709\u4e9b \u9ad8\u5174\uff1a\u201c\u6709\u4e86\u9a6c\uff0c\u6211\u5c31\u53ef\u4ee5\u5230\u5904\u8dd1\u4e86\u3002\u201d\u5f53\u7136\uff0c\u4ed6\u8fd9\u4e00\u4e1d\u9ad8\u5174\u6bd4\u8d77\u4ed6\u7684\u5fe7\u90c1\u6765\uff0c\u8fd8\u5dee\u5f97\u592a \u8fdc\u3002 \u3000\u3000\u7275\u7740\u9a6c\u8d70\u4e86\u4e24\u6b65\uff0c\u8fd9\u5931\u53bb\u89c6\u542c\u4e4b\u89c9\u7684\u5b64\u82e6\u7684\u5e74\u8f7b\u4eba\uff0c\u5728\u601d\u5fd6\u7740\u81ea\u5df1\u7684\u53bb\u8def\uff0c\u7a81\u5730\uff0c\u4e24\u4e2a \u62ab\u7740\u957f\u886b\u624b\u91cc\u62ff\u7740\u94c1\u7403\u7684\u6c49\u5b50\u671d\u4ed6\u7b14\u76f4\u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u4e00\u4e2a\u5fae\u62f1\u7740\u80cc\uff0c\u592a\u9633\u7a74\u4e0a\u8d34\u7740\u5757\u818f\u836f\u7684 \u6c49\u5b50\uff0c\u4e00\u4f38\u624b\uff0c\u63a8\u4e86\u4ed6\u4e00\u628a\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5339\u9a6c\u662f\u54ea\u91cc\u5077\u6765\u7684\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4e00\u6014\uff0c\u5168\u7136\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u90a3\u4e2a\u6c49\u5b50\u4e00\u626c\u94c1\u5c3a\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5feb\u8ddf\u592a\u7237\u5230\u8859\u95e8\u91cc \u53bb\uff01\u201d\u8def\u4eba\u542c\u4e86\u5fd6\u9053\uff1a\u201c\u539f\u6765\u662f\u516c\u5dee\u6293\u8d3c\u3002\u201d\u5374\u4e0d\u77e5\u8fd9\u4e24\u4e2a\u662f\u5728\u8859\u95e8\u91cc\u5403\u95f2\u996d\u7684\u89d2\u8272\uff0c\u6628\u591c \u8d4c\u4e86\u901a\u5bb5\u724c\u4e5d\uff0c\u5c06\u4e00\u4e2a\u6708\u5f04\u6765\u7684\u94f6\u5b50\u90fd\u8f93\u5149\u4e86\uff0c\u4e00\u65e9\u8dd1\u51fa\u6765\uff0c\u5230\u5904\u60f3\u89e6\u4eba\u5bb6\u7684\u9709\u5934\uff0c\u88f4\u73cf\u8fd9 \u4e00\u4e0d\u8bf4\u8bdd\uff0c\u4ed6\u8d8a\u53d1\u5f97\u610f\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4eba\u4e00\u5b9a\u662f\u8d3c\uff0c\u4f60\u770b\u4ed6\u7a7f\u5f97\u8fd9\u4e2a\u6837\u5b50\uff0c\u624b\u91cc\u5374\u7275\u7740\u8fd9\u4e48\u4e00 \u5339\u597d\u9a6c\u3002\u201d \u3000\u3000\u4ed6\u4f38\u624b\u5c31\u53bb\u593a\u9a6c\u7f30\uff0c\u88f4\u73cf\u5403\u60ca\u5730\u6293\u7740\uff0c\u5fc3\u91cc\u60f3\u8bf4\u8bdd\uff0c\u53e3\u4e2d\u5374\u8bf4\u4e0d\u51fa\u6765\uff0c\u90a3\u516c\u5dee\u201c\u5427\u201d \u5730\uff0c\u6253\u4e86\u4ed6\u4e00\u8033\u5149\uff0c\u9a82\u9053\uff1a\u201c\u5988\u62c9\u4e2a\u5df4\u5b50\uff0c\u4f60\u8fd9\u4e2a\u5c0f\u8d3c\u8fd8\u800d\u8d56\u3002\u201d\u53cd\u624b\u53c8\u662f\u4e00\u4e2a\u8033\u5149\u3002 \u3000\u3000\u88f4\u73cf\u53c8\u6c14\u53c8\u6012\uff0c\u8df3\u4e0a\u53bb\u5288\u9762\u4e00\u62f3\u6253\u53bb\uff0c\u90a3\u516c\u5dee\u73b0\u5728\u7cbe\u795e\u5168\u6765\u4e86\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\u8fd8\u6562 \u8fd8\u624b\uff01\u201d\u5de6\u624b\u4e00\u9886\u88f4\u73cf\u7684\u773c\u795e\uff0c\u53f3\u817f\u8d77\u5904\uff0c\u5c06\u88f4\u73cf\u8e39\u5728\u5730\u4e0a\uff0c\u8d76\u8fc7\u53bb\u53c8\u662f\u4e24\u811a\u3002\u88f4\u73cf\u8ddf\u7740 \u201c\u9f99\u5f62\u516b\u638c\u201d\u5b66\u4e86\u90a3\u4e48\u4e45\u7684\u6b66\u529f\uff0c\u6b64\u523b\u7adf\u88ab\u8fd9\u516c\u95e8\u91cc\u6700\u8d77\u7801\u7684\u628a\u5f0f\u6253\u5f97\u5728\u5730\u4e0a\u7ffb\u6eda\uff0c\u8fde\u8fd8\u624b \u7684\u529b\u91cf\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u201c\u6253\u5c0f\u8d3c\u201d\u539f\u662f\u8fd9\u4e9b\u4eba\u7684\u62ff\u624b\u597d\u620f\uff0c\u90a3\u4eba\u4e00\u9762\u8e22\uff0c\u4e00\u9762\u559d\u9a82\u7740\u3002\u53e6\u4e00\u4e2a\u772f\u7740\u773c\uff0c\u9888\u5b50\u7f29 \u5728\u8863\u670d\u91cc\uff0c\u9f3b\u6d95\u90fd\u5feb\u6d41\u51fa\u6765\u4e86\u7684\u7626\u5b50\u6253\u7740\u54c8\u6b20\u9053\uff1a\u201c\u8001\u5f20\uff0c\u7b97\u4e86\uff0c\u628a\u810f\u7269\u5e26\u56de\u53bb\u5c31\u7b97\u4e86\uff0c\u8fd9 \u5c0f\u8d3c\u602a\u53ef\u601c\u7684\uff0c\u5c31\u9a6c\u9a6c\u864e\u864e\u653e\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u8d34\u7740\u592a\u9633\u818f\u7684\u201c\u516c\u5dee\u201d\u773c\u73e0\u4e00\u8f6c\uff0c\u779f\u4e86\u90a3\u5339\u9a6c\u4e00\u773c\uff0c\u90a3\u8db3\u8db3\u62b5\u56de\u4ed6\u4eec\u6628\u591c\u8f93\u7684\u94b1\u8fd8\u6709 \u591a\uff0c\u6c14\u4e0d\u7981\u6d88\u4e86\u4e00\u5927\u534a\uff0c\u671d\u5730\u4e0a\u7684\u88f4\u73cf\u5550\u4e86\u4e00\u53e3\uff0c\u7275\u7740\u9a6c\u521a\u60f3\u8d70\uff0c\u90a3\u7626\u5b50\u5374\u53c8\u9053\uff1a\u201c\u8fd9\u5c0f\u8d3c \u8eab\u4e0a\u7684\u90a3\u4e2a\u5305\u88b1\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u4ec0\u4e48\u810f\u7269\uff0c\u4f60\u62ff\u6765\u770b\u770b\u3002\u201d \u3000\u3000\u4e8e\u662f\u88f4\u73cf\u6b7b\u547d\u6293\u7740\u7684\u5305\u88b1\u53c8\u88ab\u4ed6\u62a2\u53bb\uff0c\u90a3\u201c\u516c\u5dee\u201d\u7709\u5f00\u773c\u7b11\u5730\u5c06\u94f6\u5b50\u62ff\u4e86\u53bb\uff0c\u5374\u5c06\u90a3\u5305 \u88b1\u6254\u5230\u5730\u4e0a\uff0c\u7adf\u626c\u957f\u53bb\u4e86\u3002 \u3000\u3000\u88f4\u73cf\u6323\u624e\u7740\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u8eab\u4e0a\u7684\u75bc\u75db\uff0c\u5e76\u6ca1\u6709\u653e\u5728\u8fd9\u5014\u5f3a\u7684\u5c11\u5e74\u5fc3\u4e0a\uff0c\u4f46\u662f\u4ed6\u7684\u5fc3\u5374 \u56e0\u53d7\u4e86\u8fd9\u79cd\u59d4\u5c48\u548c\u4fae\u8fb1\uff0c\u51e0\u4e4e\u8981\u7206\u70b8\u4e86\u3002 \u3000\u3000\u4ed6\u65e0\u8a00\u5730\u671b\u7740\u82cd\u5929\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fd9\u4e9b\u4eba\u8981\u6b3a\u8d1f\u6211\uff0c\u96be\u9053\u6211\u751f\u6210\u5c31\u662f\u8be5\u53d7\u4eba\u5bb6\u7684\u6b3a\u51cc\u4e0e\u4fae\u8fb1 \u7684\u5417\uff1f\u201d\u4ed6\u6124\u6068\u90a3\u4e24\u4e2a\u5f3a\u62a2\u53bb\u4e86\u90a3\u672c\u5c5e\u4e8e\u4ed6\u7684\u4e1c\u897f\uff0c\u4ed6\u6068\u6ee1\u8857\u7684\u8def\u4eba\u773c\u770b\u7740\u8fd9\u4e0d\u5e73\u7684\u4e8b\uff0c\u975e \u4f46\u6ca1\u6709\u4e00\u4e2a\u4eba\u7ba1\uff0c\u800c\u4e14\u90fd\u8fd8\u7528\u8f7b\u8511\u7684\u773c\u5149\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4f46\u6124\u6068\u6c38\u8fdc\u662f\u4e8e\u4e8b\u65e0\u8865\u7684\uff0c\u4ed6\u8e09\u8dc4\u5730\u6361\u8d77\u4e86\u90a3\u201c\u5305\u88b1\u201d\uff0c\u5e0c\u671b\u5728\u91cc\u9762\u8fd8\u80fd\u627e\u5230\u4e00\u5206\u788e\u94f6 \u6765\u4e70\u4e9b\u70e7\u997c\u5145\u9965\uff0c\u4f46\u662f\u4ed6\u5931\u671b\u4e86\uff0c\u90a3\u4e2a\u5305\u88b1\u91cc\u9762\uff0c\u6b64\u523b\u6240\u5269\u7684\uff0c\u53ea\u6709\u4e24\u672c\u8584\u8584\u7684\u4e66\u3002\u4e66\u662f\u7528 \u9ed1\u6851\u76ae\u7eb8\u505a\u7684\u5c01\u9762\uff0c\u4e0a\u9762\u6ca1\u6709\u5199\u5b57\uff0c\u800c\u4ed6\u73b0\u5728\u4e5f\u6ca1\u6709\u770b\u4e66\u7684\u5fc3\u60c5\uff0c\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u809a\u5b50\u997f\u5f97\u8d8a \u53d1\u96be\u53d7\uff0c\u4ed6\u5929\u751f\u50b2\u9aa8\uff0c\u4e5e\u6c42\u7684\u4e8b\uff0c\u4ed6\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u505a\uff0c\u4e5f\u4e0d\u613f\u505a\u3002 \u3000\u3000\u4ed6\u5728\u8def\u4e0a\u8e2f\u8e85\u7740\uff0c\u4e00\u4e2a\u5356\u70e7\u997c\u7684\u80d6\u5b50\u770b\u7740\u4ed6\uff0c\u89c9\u5f97\u6709\u4e9b\u53ef\u601c\uff0c\u62ff\u4e86\u4e24\u5757\u997c\u7ed9\u4ed6\uff0c\u8138\u4e0a\u8fd8 \u5e26\u7740\u7b11\u5bb9\uff0c\u88f4\u73cf\u611f\u6fc0\u5f97\u5589\u5934\u90fd\u4fbf\u585e\u4f4f\uff0c\u63a5\u7740\u90a3\u4ed6\u6709\u751f\u4ee5\u6765\u6240\u63a5\u53d7\u5230\u7684\u6700\u73cd\u8d35\u7684\u8d60\u4e0e\uff0c\u5c06\u90a3\u80d6 \u5b50\u7684\u9762\u5bb9\uff0c\u5373\u65f6\u8bb0\u5728\u5fc3\u91cc\uff1a\u201c\u4f60\u6709\u4e09\u9897\u91d1\u7259\uff0c\u8033\u6735\u4e0a\u6709\u4e00\u7c92\u75e3\u3002\u201d\u4ed6\u6697\u5fd6\uff1a\u201c\u6211\u4e0d\u4f1a\u5fd8\u8bb0 \u4f60\uff0c\u603b\u6709\u4e00\u5929\u6211\u4f1a\u62a5\u7b54\u4f60\u7684\u3002\u201d \u3000\u3000\u90a3\u80d6\u5b50\u5728\u505a\u7740\u522b\u7684\u751f\u610f\uff0c\u62ff\u7740\u7834\u65e7\u7684\u7eb8\u5305\u70e7\u997c\u7ed9\u4eba\uff0c\u88f4\u73cf\u5634\u91cc\u56bc\u7740\u70e7\u997c\uff0c\u5fc3\u91cc\u5374\u4e00\u52a8\uff0c \u5c06\u5305\u88b1\u91cc\u7684\u90a3\u4e24\u672c\u8584\u4e66\u62ff\u51fa\u6765\uff0c\u4ea4\u7ed9\u90a3\u80d6\u5b50\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u201c\u6211\u5403\u4e86\u4f60\u7684\u70e7\u997c\uff0c\u73b0\u5728\u8fd8\u4f60\u4e24\u672c \u4e66\uff0c\u8ba9\u4f60\u5305\u70e7\u997c\u3002\u201d\u4ed6\u7adf\u4e0d\u613f\u610f\u767d\u5f97\u522b\u4eba\u4e00\u4e1d\u597d\u5904\u3002 \u3000\u3000\u90a3\u80d6\u5b50\u5c06\u90a3\u4e24\u672c\u4e66\u7ffb\u4e86\u7ffb\uff0c\u53c8\u56de\u7ed9\u88f4\u73cf\uff0c\u6447\u4e86\u6447\u624b\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u201c\u6211\u4e0d\u8981\u770b\u3002\u201d\u5374\u53c8\u62ff \u4e86\u4e2a\u70e7\u997c\u7ed9\u88f4\u73cf\u3002 \u3000\u3000\u88f4\u73cf\u62ff\u4e86\u90a3\u4e24\u672c\u4e66\uff0c\u8f6c\u5934\u5c31\u8dd1\uff0c\u4ed6\u77e5\u9053\u90a3\u80d6\u5b50\u4e00\u5b9a\u4ee5\u4e3a\u8fd8\u8981\u5403\u70e7\u997c\uff0c\u4ed6\u611f\u89c9\u5230\u88ab\u5c48\u8fb1\u4e86 \u7684\u60b2\u54c0\uff0c\u8dd1\u7740\u8dd1\u7740\uff0c\u773c\u775b\u53c8\u6f6e\u6e7f\u4e86\u3002 \u3000\u3000\u4e16\u4e0a\u6ca1\u6709\u4efb\u4f55\u4e00\u4ef6\u4e8b\u6bd4\u4e00\u4e2a\u5929\u751f\u505a\u9aa8\u7684\u4eba\uff0c\u5374\u504f\u504f\u5728\u53d7\u5230\u522b\u4eba\u59d4\u5c48\u7684\u65f6\u5019\uff0c\u65e2\u65e0\u6cd5\u53cd \u6297\uff0c\u4e5f\u65e0\u6cd5\u8fa8\u660e\u518d\u503c\u5f97\u60b2\u54c0\u7684\u4e86\u3002\u88f4\u73cf\u50cf\u4e00\u9897\u672a\u7ecf\u7422\u78e8\uff0c\u4e5f\u672a\u66fe\u53d1\u51fa\u5149\u5f69\u7684\u94bb\u77f3\uff0c\u6df7\u5728\u8def\u65c1 \u7684\u788e\u77f3\u91cc\u88ab\u4eba\u4eec\u8df5\u8e0f\u7740\uff0c\u6ca1\u6709\u4e00\u4e2a\u6ce8\u610f\u5230\u4ed6\u7684\u4ef7\u503c\uff0c\u8fd9\u9897\u94bb\u77f3\u7684\u547d\u8fd0\u662f\u6c38\u8fdc\u88ab\u4eba\u8df5\u8e0f\uff0c\u8fd8\u6709 \u80fd\u53d1\u51fa\u5149\u5f69\u7684\u4e00\u5929\u5417\uff1f \u3000\u3000\u8fd9\u5929\u665a\u4e0a\uff0c\u5ba2\u6808\u95e8\u53e3\u591a\u4e86\u4e2a\u6d17\u9a6c\u7684\u5c0f\u53ae\uff0c\u4ed6\u6d17\u7684\u9a6c\u6bd4\u4efb\u4f55\u4eba\u90fd\u5e72\u51c0\uff0c\u4f46\u62ff\u7684\u94b1\u5374\u6bd4\u4efb\u4f55 \u4eba\u90fd\u5c11\uff0c\u8fd9\u8fd8\u662f\u672c\u672a\u5728\u5ba2\u6808\u95e8\u53e3\u6d17\u9a6c\u7684\u90a3\u4e00\u7fa4\u65e0\u8d56\u4e2d\u7684\u201c\u8001\u5927\u201d\u53ef\u601c\u4ed6\uff0c\u624d\u5c06\u4e00\u4e9b\u4ed6\u4eec\u770b\u6765 \u6ca1\u6709\u4ec0\u4e48\u201c\u6cb9\u6c34\u201d\u7684\u5ba2\u4eba\u8ba9\u7ed9\u4ed6\u3002 \u3000\u3000\u4ed6\uff0c\u81ea\u7136\u5c31\u662f\u88f4\u73cf\uff0c\u4ed6\u8ba4\u4e3a\u9760\u52b3\u529b\u5403\u996d\uff0c\u5e76\u4e0d\u662f\u5c48\u8fb1\uff0c\u56e0\u6b64\u4ed6\u7adf\u4e5f\u5b89\u4e8e\u8fd9\u79cd\u5351\u8d31\u7684\u751f \u6d3b\uff0c\u665a\u4e0a\u5c31\u5728\u5ba2\u6808\u7684\u623f\u6a90\u4e0b\u4e00\u7761\uff0c\u7528\u90a3\u4e24\u672c\u7834\u4e66\u505a\u6795\u5934\uff0c\u8fd9\u662f\u4ed6\u552f\u4e00\u7684\u8d22\u4ea7\uff0c\u4e5f\u662f\u552f\u4e00\u6ca1\u6709 \u522b\u4eba\u62a2\u4ed6\u7684\u4e1c\u897f\u3002 \u3000\u3000\u6599\u5ced\u7684\u6625\u5bd2\u6709\u65f6\u4f7f\u4ed6\u534a\u591c\u60ca\u9192\uff0c\u4ed6\u5c31\u8d77\u6765\u6253\u4e00\u8d9f\u4ed6\u4e5f\u77e5\u9053\u6beb\u65e0\u7528\u5904\u7684\u201c\u5927\u6d2a\u62f3\u201d\uff0c\u4e00\u9762 \u5b89\u6170\u7740\u81ea\u5df1\uff1a\u201c\u590f\u5929\u5c31\u8981\u5230\u4e86\u3002\u201d \u3000\u3000\u4f46\u590f\u5929\u8fd8\u6ca1\u6709\u6765\u7684\u65f6\u5019\uff0c\u8fd9\u5c0f\u9547\u5374\u6765\u4e86\u4e2a\u5356\u628a\u5f0f\u7684\u8001\u5934\u5b50\uff0c\u5e26\u7740\u4e00\u5339\u75b2\u5f31\u7684\u8001\u9a6c\u548c\u4e00\u4e2a \u5341\u516d\u516b\u5c81\u7684\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u5ba2\u6808\u524d\u9762\u7684\u4e00\u5c0f\u5757\u7a7a\u5730\u4e0a\uff0c\u6253\u8d77\u9523\uff0c\u90a3\u5c0f\u59d1\u5a18\u800d\u7740\u82b1\u5200\uff0c\u88f4\u73cf\u770b\u5f97\u773c\u775b\u90fd\u76f4\u4e86\u3002 \u89c9\u5f97\u5979\u800d\u5f97\u771f\u597d\uff0c\u90a3\u8001\u5934\u5b50\u54b3\u7740\u55fd\uff0c\u53eb\u7740\u6c5f\u6e56\u7684\u573a\u9762\u8bdd\uff0c\u4f46\u4f7f\u4e86\u534a\u5929\uff0c\u770b\u7684\u4eba\u867d\u591a\uff0c\u7ed9\u94b1\u7684 \u4eba\u5374\u5c11\u3002 \u3000\u3000\u90a3\u8001\u5934\u5931\u671b\u4e86\uff0c\u5f2f\u7740\u8170\uff0c\u54b3\u7740\u55fd\u6536\u62fe\u7740\u573a\u5b50\uff0c\u90a3\u5c0f\u59d1\u5a18\u53f9\u7740\u6c14\uff0c\u5728\u65c1\u8fb9\u5e2e\u5fd9\u3002\u5929\u9ed1\u4e86\uff0c \u4ed6\u4eec\u7275\u7740\u90a3\u5339\u8001\u9a6c\u6765\u5230\u5ba2\u6808\u95e8\u53e3\uff0c\u5e97\u5c0f\u4e8c\u7231\u7406\u4e0d\u7406\u5730\u62db\u547c\u7740\uff0c\u88f4\u73cf\u5374\u53bb\u7275\u90a3\u5339\u8001\u9a6c\uff0c\u6bd4\u7740\u624b \u5f0f\uff0c\u610f\u601d\u662f\u8981\u66ff\u4ed6\u4eec\u5237\u4e00\u5237\uff0c\u90a3\u8001\u8005\u6447\u4e86\u6447\u5934\uff0c\u88f4\u73cf\u5374\u5728\u5730\u4e0a\u5212\u4e86\u201c\u4e0d\u8981\u94b1\u201d\u4e09\u4e2a\u5b57\uff0c\u90a3\u8001 \u5934\u4e00\u7b11\uff0c\u5c31\u5c06\u9a6c\u4ea4\u7ed9\u4ed6\u3002\u88f4\u73cf\u7ad9\u8d77\u6765\u7684\u65f6\u5019\uff0c\u770b\u5230\u90a3\u5c0f\u59d1\u5a18\u7684\u5927\u773c\u775b\u91cc\u4e5f\u5145\u6ee1\u4e86\u7b11\u610f\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4e00\u5bf9\u591a\u4e48\u6f02\u4eae\u7684\u773c\u6674\u5440\uff1f\u201d\u4f46\u662f\u4ed6\u7acb\u523b\u7981\u6b62\u81ea\u5df1\u518d\u60f3\u4e0b\u53bb\uff0c\u4ed6\u73b0\u5728\u751a\u81f3\u8fde\u6a80\u6587\u742a \u90fd\u4e0d\u6562\u60f3\uff0c\u56e0\u4e3a\u4e00\u60f3\u5979\uff0c\u4ed6\u5c31\u4f1a\u89c9\u5f97\u66f4\u96be\u53d7\u3002 \u3000\u3000\u665a\u4e0a\uff0c\u4ed6\u53c8\u6795\u7740\u90a3\u4e24\u672c\u7834\u4e66\u7761\u4e86\uff0c\u50cf\u4ee5\u524d\u7684\u90a3\u4e9b\u65e5\u5b50\u4e00\u6837\uff0c\u4ed6\u53c8\u88ab\u6625\u5bd2\u60ca\u9192\uff0c\u53ef\u662f\u4eca\u591c \u5f53\u4ed6\u5728\u661f\u7a7a\u4e0b\u4f7f\u7740\u62f3\u7684\u65f6\u5019\uff0c\u9664\u4e86\u6ee1\u5929\u7684\u661f\u661f\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u53cc\u773c\u775b\u5728\u671b\u7740\uff0c\u90a3\u5c31\u662f\u90a3\u800d\u628a\u5f0f \u7684\u8001\u5934\u5b50\u3002 \u3000\u3000\u90a3\u8001\u5934\u4ece\u5ba2\u6808\u91cc\u8d70\u51fa\u6765\uff0c\u62ff\u4e86\u5757\u767d\u7c89\u5728\u5730\u4e0a\u5199\u9053\uff1a\u201c\u4f60\u5b66\u8fc7\u6b66\uff1f\u201d \u3000\u3000\u88f4\u73cf\u70b9\u4e86\u70b9\u5934\uff0c\u90a3\u8001\u5934\u60f3\u4e86\u4f1a\uff0c\u53c8\u5199\u9053\uff1a\u201c\u4f60\u613f\u4e0d\u613f\u610f\u8ddf\u6211\u4eec\u53bb\u95ef\u6c5f\u6e56\uff0c\u867d\u7136\u6709\u65f6\u4e5f\u4f1a \u6328\u997f\uff0c\u4f46\u603b\u6bd4\u4f60\u5728\u8fd9\u91cc\u5237\u9a6c\u8981\u5f3a\u5f97\u591a\uff0c\u5c11\u5e74\u4eba\u4e5f\u8be5\u5728\u6c5f\u6e56\u4e0a\u95ef\u95ef\u5440\uff01\u201d \u3000\u3000\u88f4\u73cf\u5927\u559c\uff0c\u8fde\u8fde\u70b9\u7740\u5934\uff0c\u90a3\u8001\u5934\u5b50\u6ee1\u5e03\u76b1\u7eb9\u7684\u8138\u4e0a\uff0c\u4e5f\u9732\u51fa\u559c\u8272\uff0c\u4ed6\u5230\u5e95\u8001\u4e86\uff0c\u53e4\u94dc\u8272 \u7684\u76ae\u80a4\uff0c\u73b0\u5728\u4e5f\u6e10\u6e10\u677e\u5f1b\uff0c\u6709\u4e2a\u5e74\u8f7b\u529b\u58ee\u7684\u5c0f\u4f19\u5b50\u5e2e\u4ed6\u5fd9\uff0c\u603b\u662f\u4ef6\u597d\u4e8b\uff0c\u4f55\u51b5\u4ed6\u5bf9\u8fd9\u5c0f\u4f19\u5b50 \u8fd8\u9887\u5177\u597d\u611f\u3002 \u3000\u3000\u4e8e\u662f\u7b2c\u4e8c\u5929\uff0c\u88f4\u73cf\u5c31\u7531\u5237\u9a6c\u7684\u5c0f\u53ae\u53d8\u6210\u4e86\u8d70\u6c5f\u6e56\u7684\u5c0f\u4f19\u8ba1\uff0c\u4ed6\u968f\u7740\u8001\u5934\u5b50\u5728\u6c5f\u5357\u7684\u4e00\u4e9b \u5c0f\u9547\u91cc\u98d8\u6cca\u7740\uff0c\u767d\u5929\uff0c\u4ed6\u6253\u7740\u9523\uff0c\u62ff\u7740\u5bb6\u4f19\uff0c\u6709\u65f6\u4e5f\u4f7f\u4e00\u8d9f\u62f3\uff0c\u665a\u4e0a\uff0c\u4ed6\u62ff\u7740\u90a3\u6346\u5175\u5203\uff0c\u548c \u8001\u5934\u5b50\u7761\u5728\u4e00\u8d77\uff0c\u590f\u5929\u6765\u4e86\uff0c\u53ef\u662f\u4ed6\u5374\u53c8\u89c9\u5f97\u70ed\u4e86\u3002 \u3000\u3000\u4ee5\u5f80\u4ed6\u7684\u5e7b\u60f3\uff0c\u6b64\u523b\u5df2\u88ab\u73b0\u5b9e\u6298\u78e8\u5f97\u51e0\u4e4e\u5df2\u6ca1\u6709\u4e86\u5f71\u5b50\uff0c\u4f46\u591c\u6df1\u4eba\u9759\uff0c\u4ed6\u8fd8\u6ca1\u6709\u7761\u7740\u7684 \u65f6\u5019\uff0c\u4ed6\u4e5f\u4f1a\u5e7b\u60f3\u81ea\u5df1\u5b66\u6210\u4e86\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u4f7f\u6a80\u660e\u5927\u5403\u4e00\u60ca\u540e\uff0c\u5a36\u4e86\u4ed6\u7684\u5973\u513f\u3002 \u3000\u3000\u6709\u65f6\u5019\uff0c\u4ed6\u4e5f\u4f1a\u60f3\u8d77\u51b7\u6708\u4ed9\u5b50\uff0c\u60f3\u5230\u5979\u6620\u5728\u5899\u4e0a\u7684\u90a3\u4e2a\u7f8e\u4e3d\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4f46\u662f\u767d\u5929\uff0c\u5f53\u4ed6\u770b\u5230\u90a3\u4e00\u53cc\u660e\u5a9a\u800c\u5e26\u7740\u7b11\u610f\u7684\u773c\u775b\u7684\u5b59\u9526\u5e73\uff0c\u5374\u5bf9\u88f4\u73cf\u6709\u7740\u4eb2\u4eba\u822c\u7684\u6148 \u7231\uff0c\u8fd9\u5bf9\u81ea\u5e7c\u5931\u53bb\u4e86\u4eb2\u4eba\u7684\u88f4\u73cf\u6765\u8bf4\uff0c\u5df2\u8db3\u591f\u4f7f\u4ed6\u6ee1\u8db3\u4e86\uff0c\u4f55\u51b5\u90a3\u53cc\u5927\u773c\u775b\u671b\u7740\u4ed6\u65f6\uff0c\u8fd8\u5e26 \u7740\u751c\u871c\u7684\u7b11\u610f\u5462\u3002 \u3000\u3000\u4ed6\u4eec\u79bb\u6c5f\u9675\u8d8a\u6765\u8d8a\u8fdc\uff0c\u8fd9\u5929\u5230\u4e86\u9f99\u6f6d\uff0c\u5929\u6b63\u5728\u4e0b\u7740\u96e8\u3002 \u3000\u3000\u4e0b\u96e8\u5bf9\u6c5f\u6e56\u5356\u827a\u7684\u4eba\u4eec\u5f80\u6765\uff0c\u662f\u4e00\u79cd\u65e0\u6cd5\u8865\u6551\u7684\u78e8\u96be\uff0c\u82b1\u5200\u5b59\u658c\u8138\u4e0a\u7684\u76b1\u7eb9\u66f4\u6df1\u4e86\u3002 \u3000\u3000\u665a\u4e0a\uff0c\u88f4\u73cf\u4e2d\u591c\u800c\u9192\uff0c\u4ed6\u68a6\u5230\u6a80\u660e\u62ff\u7740\u4e00\u628a\u5200\u8981\u6740\u4ed6\uff0c\u64c5\u6587\u742a\u5728\u65c1\u8fb9\u62c9\u7740\u5979\u7239\u7239\u7684\u624b\uff0c \u6240\u4ee5\u4ed6\u9192\u6765\u65f6\uff0c\u90a3\u6781\u6050\u6016\u7684\u611f\u89c9\u4ecd\u4f7f\u4ed6\u60ca\u6817\uff0c\u4ed6\u4e00\u8f6c\u8138\uff0c\u770b\u5230\u5b59\u658c\u4e5f\u4e0d\u5728\u5e8a\u4e0a\uff0c\u4e8e\u662f\u4ed6\u5c31\u4ece \u90a3\u5f20\u6728\u677f\u642d\u6210\u7684\u5e8a\u4e0a\u722c\u8d77\u6765\uff0c\u6311\u4eae\u4e86\u6cb9\u706f\uff0c\u7a7f\u4e0a\u978b\u5b50\uff0c\u8d70\u51fa\u8fd9\u95f4\u8305\u8349\u642d\u6210\u7684\u5c0f\u5ba2\u6808\u91cc\u7684\u5c0f\u5ba2 \u623f\uff0c\u51fa\u53bb\u900f\u900f\u6c14\u3002 \u3000\u3000\u96e8\u5df2\u7ecf\u6b62\u4e86\uff0c\u8fd9\u4e48\u51c9\u5feb\u7684\u665a\u4e0a\uff0c\u88f4\u73cf\u5f88\u5c11\u5728\u590f\u5929\u9047\u5230\u8fc7\uff0c\u4ed6\u8d70\u5230\u5c0f\u9662\u5b50\u91cc\uff0c\u4ecd\u7136\u6ca1\u6709\u5b59 \u658c\u7684\u4eba\u5f71\uff0c\u4ed6\u4e0d\u7981\u5f00\u59cb\u5947\u602a\uff1a\u201c\u5b59\u8001\u7239\u8fd9\u4e48\u665a\u4e86\uff0c\u4f1a\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u722c\u5230\u90a3\u77ee\u5899\u4e0a\u4e00\u671b\u2014 \u2014\u5899\u5916\u90a3\u7247\u8352\u5730\u4e0a\u7684\u666f\u76f8\uff0c\u5374\u5413\u5f97\u4ed6\u5dee\u70b9\u4ece\u5899\u4e0a\u7ffb\u4e0b\u6765\u3002 \u3000\u3000\u539f\u6765\u5899\u5916\u6b64\u523b\u559d\u53f1\u8fde\u58f0\uff0c\u5200\u5149\u98de\u821e\uff0c\u201c\u82b1\u5200\u5b59\u658c\u201d\u638c\u4e2d\u9752\u94a2\u5200\u6d8c\u8d77\u4e00\u7247\u5149\u5f71\uff0c\u7adf\u4ee5\u540d\u5782 \u6c5f\u6e56\u7684\u201c\u4e94\u864e\u65ad\u9b42\u5200\u6cd5\u201d\u5bf9\u654c\u7740\u5bf9\u624b\u7684\u4e00\u679d\u4e27\u95e8\u5251\u548c\u4e24\u7ba1\u5224\u5b98\u7b14\uff0c\u5200\u98ce\u864e\u864e\uff0c\u62db\u6c89\u529b\u731b\uff0c\u663e \u89c1\u5f97\u5728\u8fd9\u67c4\u5200\u4e0a\uff0c\u81f3\u5c11\u6709\u7740\u56db\u5341\u5e74\u7684\u529f\u529b\uff0c\u54ea\u91cc\u8fd8\u662f\u4ed6\u5728\u800d\u628a\u5f0f\uff0c\u4f7f\u7684\u90a3\u79cd\u5200\u6cd5\uff0c\u88f4\u73cf\u7684\u773c \u775b\u90fd\u770b\u76f4\u4e86\u3002 \u3000\u3000\u4f7f\u4e27\u95e8\u5251\u7684\u662f\u4e2a\u7cbe\u7626\u6c49\u5b50\uff0c\u5de6\u8138\u4e0a\u5e26\u7740\u4e00\u9053\u957f\u957f\u7684\u5200\u75a4\uff0c\u4f7f\u5224\u5b98\u7b14\u7684\u77ed\u5c0f\u7cbe\u608d\uff0c\u62db\u5f0f\u72e0 \u8fa3\uff0c\u5c3d\u5f80\u5b59\u658c\u8eab\u4e0a\u53ef\u4ee5\u81f4\u547d\u7684\u5730\u65b9\u62db\u547c\u3002 \u3000\u3000\u201c\u82b1\u5200\u5b59\u658c\u201d\u96ea\u767d\u7684\u80e1\u53d1\u5728\u591c\u98ce\u91cc\u5e26\u7740\u5200\u5149\u98d8\u821e\uff0c\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u53cc\u7b14\u62a2\u51fa\uff0c\u4e00\u70b9\u201c\u671f \u95e8\u201d\uff0c\u4e00\u70b9\u201c\u4e73\u6cc9\u201d\uff0c\u5237\u5730\u4e00\u584c\u8170\uff0c\u53cc\u7b14\u76f4\u4e0a\u6311\u51fa\uff0c\u7adf\u70b9\u5411\u5b59\u658c\u7684\u54bd\u5934\uff0c\u62db\u5f0f\u5feb\u5982\u95ea\u7535\uff01 \u3000\u3000\u5b59\u658c\u51b7\u7b11\u4e00\u58f0\uff0c\u5fae\u4e00\u9519\u6b65\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u9707\u5f97\u90a3\u81ea\u4e0a\u659c\u524a\u800c\u4e0b\u7684\u4e27\u95e8\u5251\u53d1\u51fa\uff1a\u2018\u545b\u7136\u201c\u58f0 \u957f\u541f\uff0c\u5de6\u817f\u865a\u865a\u8e22\u51fa\uff0c\u53f3\u817f\u500f\u7136\u98de\u8d77\uff0c\u201d\u9e33\u9e2f\u53cc\u98de\u817f\u201c\uff0c\u8e22\u5f97\u90a3\u77ee\u5c0f\u6c49\u5b50\u4e0d\u5f97\u4e0d\u8d76\u7d27\u64a4\u62db\u540e \u9000\u3002\u4f7f\u4e27\u95e8\u5251\u7684\u7626\u957f\u6c49\u5b50\u51b7\u7b11\u53f1\u9053\uff1a\u201c\u5341\u5e74\u6765\u59d3\u5b59\u7684\u529f\u592b\u5012\u8fd8\u6ca1\u6709\u6401\u4e0b\uff0c\u53ef\u662f\u4eca\u5929\u6211\u59d3\u7a0b\u7684 \u4e0d\u53eb\u4f60\u8840\u6d12\u6b64\u95f4\uff0c\u4ece\u6b64\u6c5f\u6e56\u4e0a\u5c31\u7b97\u6ca1\u6709\u6211\u4eec\u2018\u6dee\u9633\u4e09\u715e\u2019\u8fd9\u5757\u5b57\u53f7\u3002\u201d\u5237\u5237\u4e24\u5251\uff0c\u5251\u5149\u5e26\u7740 \u9752\u84dd\u8272\u7684\u5149\u8292\uff0c\u5728\u9ed1\u591c\u91cc\u663e\u5f97\u5206\u5916\u5229\u660e\u3002 \u3000\u3000\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u6b64\u523b\u773c\u775b\u90fd\u7ea2\u4e86\uff0c\u4e5f\u662f\u8fb9\u6253\u8fb9\u559d\u9053\uff1a\u201c\u60f3\u4f60\u65ad\u9b42\u5200\u5728\u6b66\u6797\u4e2d\u8fd8\u7b97\u53eb\u5f97\u8d77\u4e07 \u513f\u7684\u4eba\u7269\uff0c\u6740\u4e86\u4eba\u7adf\u60f3\u4e00\u8eb2\uff0c\u90a3\u53ef\u529e\u4e0d\u5230\uff0c\u4eca\u5929\u6ca1\u522b\u7684\u8bf4\uff0c\u8d54\u6211\u4e8c\u54e5\u7684\u547d\u6765\u5427\uff01\u201d \u3000\u3000\u5b59\u658c\u4e00\u58f0\u4e0d\u54cd\uff0c\u201c\u4e94\u864e\u65ad\u9b42\u5200\u201d\u4f7f\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u529b\u654c\u8fd9\u540d\u9707\u6c5f\u6e56\u7684\u7eff\u6797\u5de8\u76d7\uff1a\u2018\u6dee\u9633\u4e09 \u715e\u201c\u4e2d\u7684\u5c0f\u4e27\u95e8\u7a0b\u745b\uff0c\u548c\u593a\u547d\u4e09\u90ce\u90d1\u6606\u708e\u7684\u4e09\u4ef6\u5175\u5203\uff0c\u5374\u4e5f\u5948\u4f55\u4ed6\u4e0d\u5f97\u3002\u4f0f\u5728\u77ee\u5899\u4e0a\u7684\u88f4\u73cf \u867d\u7136\u542c\u4e0d\u5230\u4ed6\u4eec\u7684\u8bdd\uff0c\u53ef\u662f\u4ed6\u8111\u4e2d\u7acb\u523b\u5c06\u8fd9\u4ef6\u4e8b\u731c\u51fa\u4e86\u4e5d\u5206\u3002\u201d\u8fd9\u5927\u6982\u662f\u6709\u4eba\u5411\u5b59\u8001\u7239\u5bfb \u4ec7\uff0c\u8fd9\u5b59\u8001\u7239\u4ee5\u524d\u4e00\u5b9a\u4e5f\u662f\u4e2a\u6210\u540d\u82f1\u96c4\uff0c\u4e3a\u4e86\u8eb2\u907f\u4ec7\u5bb6\uff0c\u5c31\u501f\u5356\u827a\u6765\u9690\u85cf\u8eab\u4efd\uff0c\u53ef\u662f\u4eca\u591c\uff0c \u8fd8\u662f\u8ba9\u4eba\u5bb6\u627e\u7740\u4e86\uff01\u201c\u4ed6\u6697\u53f9\u4e00\u58f0\uff0c\u53c8\u5fd6\u9053\uff1a\u201c\u53ef\u60dc\u6211\u5927\u4e0d\u4e2d\u7528\uff0c\u7adf\u8fde\u4e00\u70b9\u5fd9\u4e5f\u5e2e\u4e0d\u4e0a\uff0c\u4eba \u5bb6\u600e\u4e48\u6765\u7684\uff0c\u600e\u4e48\u52a8\u4e0a\u624b\u7684\u6211\u90fd\u4e0d\u77e5\u9053\uff0c\u6211\u662f\u4e2a\u7b28\u86cb\uff0c\u53c8\u662f\u4e2a\u6b8b\u5e9f\u3002\u201d \u3000\u3000\u4ed6\u7684\u5fc3\u66f4\u75bc\u4e86\u8d77\u6765\uff0c\u5934\u4e00\u62ac\uff0c\u5ffd\u7136\u770b\u5230\u51e0\u70b9\u5bd2\u661f\uff0c\u4f3c\u7535\u822c\u5730\u5411\u548c\u201c\u5b59\u8001\u7239\u201d\u52a8\u7740\u624b\u7684\u4e24 \u4e2a\u4eba\u8eab\u4e0a\u88ad\u53bb\uff0c\u4ed6\u77e5\u9053\u8fd9\u662f\u6697\u5668\uff0c\u671d\u7740\u65c1\u8fb9\u4e00\u770b\uff0c\u5b59\u9526\u5e73\u624b\u91cc\u4e5f\u63d0\u7740\u5200\uff0c\u6697\u5668\u5c31\u662f\u4ece\u5979\u624b\u4e0a \u53d1\u51fa\u7684\u3002 \u3000\u3000\u5c0f\u4e27\u95e8\u5251\u4e00\u9886\uff0c\u593a\u547d\u4e09\u90ce\u638c\u4e2d\u5224\u5b98\u7b14\u7ffb\u98de\u6cfc\u6253\uff0c\u5c06\u88ad\u6765\u7684\u94c1\u83b2\u5b50\u51fb\u98de\u4e86\uff0c\u53e3\u4e2d\u5927\u6012\u559d \u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6562\u6697\u7b97\u5927\u7237\uff1f\u201d \u3000\u3000\u8bed\u58f0\u65b9\u4e86\uff0c\u5b59\u9526\u5e73\u5df2\u50cf\u71d5\u5b50\u822c\u7684\u63a0\u4e86\u8fc7\u6765\uff0c\u624b\u4e2d\u4f7f\u7684\u662f\u67f3\u53f6\u5200\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u201c\u98ce\u864e\u4e91 \u9f99\u201d\uff0c\u4e0a\u524a\u5455\u54bd\uff0c\u4e0b\u5241\u53cc\u8db3\uff0c\u8d70\u7684\u4e5f\u662f\u201c\u4e94\u864e\u65ad\u9b42\u5200\u201d\u7684\u8def\u5b50\uff0c\u4f46\u662f\u8f7b\u7075\u5de7\u7094\uff0c\u548c\u5979\u7237\u7237\u7684 \u5200\u6c89\u529b\u731b\u5374\u53c8\u622a\u7136\u4e0d\u540c\uff0c\u5c0f\u4e27\u95e8\u51b7\u7b11\u4e00\u58f0\uff1a\u201c\u5c0f\u5ab3\u5987\u4e5f\u51fa\u6765\u4e86\uff01\u201d\u5251\u52bf\u4e00\u8f6c\uff0c\u5237\u5237\u4e24\u5251\uff0c\u523a \u5411\u5b59\u9526\u5e73\u3002 \u3000\u3000\u88f4\u73cf\u770b\u5f97\u51b7\u6c57\u76f4\u5192\uff0c\u4ed6\u60f3\u4e0d\u5230\u5b59\u9526\u5e73\u4e5f\u6709\u8fd9\u4e48\u597d\u7684\u529f\u592b\uff0c\u5bf9\u81ea\u5df1\u7684\u65e0\u7528\uff0c\u4e5f\u8d8a\u53d1\u60ed\u6127\u96be \u53d7\u3002 \u3000\u3000\u8fd9\u51e0\u4eba\u4e00\u52a8\u624b\uff0c\u8fd9\u9f99\u6f6d\u90ca\u5916\u5c0f\u4e61\u6751\u7684\u72d7\u90fd\u5420\u4e86\u8d77\u6765\uff0c\u5c0f\u4e27\u95e8\u6709\u4e9b\u5fc3\u865a\uff0c\u4f4e\u559d\u9053\uff1a\u201c\u8001\u4e09 \u536f\u4e0a\u52b2\uff0c\u5feb\u4e86\u7ed3\u8fd9\u4e24\u4e2a\u70b9\u5b50\u3002\u201d \u3000\u3000\u593a\u547d\u4e09\u90ce\u95f7\u54fc\u4e86\u4e00\u58f0\uff0c\u5224\u5b98\u53cc\u7b14\u76f4\u6b3a\u8fdb\u5b59\u658c\u6000\u91cc\u53bb\uff0c\u8fd9\u79cd\u77ed\u5175\u5668\uff0c\u8bb2\u7a76\u7684\u5c31\u662f\u201c\u4e00\u5bf8 \u77ed\uff0c\u4e00\u5bf8\u9669\u201d\uff0c\u53e6\u5916\u8fd8\u5f97\u52a0\u4e0a\u5feb\uff0c\u593a\u547d\u4e09\u90ce\u80fd\u626c\u540d\u6c5f\u5317\u7eff\u6797\uff0c\u8fd9\u4e00\u5bf9\u5224\u5b98\u7b14\u4e0a\uff0c\u786e\u5b9e\u6709\u8fc7\u4eba \u7684\u529f\u592b\uff0c\u5c31\u8fde\u5b59\u658c\u90a3\u4e48\u8001\u8fa3\u7684\u5200\u6cd5\uff0c\u5374\u88ab\u8feb\u5f97\u540e\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u4f46\u5341\u6570\u4e2a\u7167\u9762\u4e00\u8fc7\uff0c\u593a\u547d\u4e09\u90ce\u624b\u4e0a\u7684\u5224\u5b98\u53cc\u7b14\u5c31\u4e0d\u5f97\u4e0d\u7f13\u4e0b\u6765\uff0c\u5b59\u658c\u638c\u4e2d\u7684\u5200\uff0c\u5374\u4e00\u62db \u5feb\u4f3c\u4e00\u62db\uff0c\u5f88\u5feb\u5730\u5360\u4e86\u4e0a\u51e4\u3002 \u3000\u3000\u90a3\u8fb9\u5b59\u9526\u5e73\u638c\u4e2d\u7684\u67f3\u53f6\u5200\uff0c\u5374\u62b5\u654c\u4e0d\u4f4f\u5c0f\u4e27\u95e8\u638c\u4e2d\u7684\u4e09\u624d\u5251\u6cd5\uff0c\u4e00\u56e2\u5200\u5149\uff0c\u6e10\u6e10\u5df2\u88ab\u88f9 \u5728\u5c0f\u4e27\u95e8\u8f7b\u7075\u5de7\u7094\u7684\u5251\u62db\u91cc\u3002 \u3000\u3000\u88f4\u73cf\u81ea\u5bb6\u6b66\u529f\u867d\u4e0d\u884c\uff0c\u4f46\u603b\u7b97\u8fd8\u61c2\u5f97\u4e0d\u5c11\uff0c\u6b64\u523b\u5fc3\u4e2d\u7740\u6025\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6837\u5b50\u4ed6\u4eec\u4e00\u4e2a\u65f6 \u8fb0\u91cc\uff0c\u8fd8\u5206\u4e0d\u51fa\u80dc\u8d1f\u6765\uff0c\u82e5\u60ca\u52a8\u4e86\u522b\u4eba\uff0c\u600e\u751f\u662f\u597d\uff1f\u201d\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u6b64\u65f6\u65e9\u5df2\u60ca\u52a8\u522b\u4eba\u4e86\uff0c \u53ea\u662f\u5927\u5bb6\u90e8\u8eb2\u5728\u623f\u91cc\uff0c\u8c01\u80af\u51fa\u6765\u62db\u60f9\u8fd9\u79cd\u4e8b\u3002 \u3000\u3000\u5b59\u658c\u65e9\u5e74\u95ef\u8361\u6c5f\u6e56\uff0c\u89c1\u8fc7\u7684\u5927\u51e4\u5927\u6d6a\uff0c\u4e0d\u77e5\u9053\u591a\u5c11\uff0c\u6b64\u523b\u773c\u89d2\u52a8\u5904\uff0c\u5df2\u770b\u51fa\u4ed6\u5973\u513f\u60c5\u51b5 \u7684\u4e0d\u5999\uff0c\u8e6c\u3001\u8e6c\u3001\u8e6c\u5012\u9000\u4e09\u6b65\uff0c\u5237\u5730\uff0c\u53c8\u7a9c\u4e0a\u6765\uff0c\u7adf\u65bd\u5c55\u51fa\u201c\u4e94\u864e\u65ad\u9b42\u5200\u201d\u91cc\u7684\u201c\u8fdb\u6b65\u8fde\u73af \u593a\u547d\u4e09\u62db\u201d\u6765\uff0c\u987f\u65f6\u5c06\u593a\u547d\u4e09\u90ce\u7684\u8eab\u5f62\uff0c\u538b\u5728\u81ea\u5df1\u5200\u5149\u4e4b\u4e0b\u3002 \u3000\u3000\u593a\u547d\u4e09\u90ce\u5224\u5b98\u53cc\u7b14\uff0c\u8e66\u3001\u70b9\u3001\u67b6\uff0c\u5c06\u5b59\u658c\u7684\u201c\u8fdb\u6b65\u64a9\u9634\u201d\uff0c\u201c\u8fde\u524a\u5e26\u780d\u201d\uff0c\u72e0\u8fa3\u7684\u4e24\u62db \u907f\u4e86\u5f00\u53bb\uff0c\u5b59\u658c\u51b7\u7b11\u4e00\u58f0\uff0c\u5200\u5149\u5ffd\u5730\u4e00\u5708\uff0c\u593a\u547d\u4e09\u90ce\u53f3\u624b\u5224\u5b98\u7b14\u4e00\u67b6\uff0c\u5de6\u624b\u65b9\u52a8\uff0c\u5374\u88ab\u5b59\u658c \u5237\u5730\u4e00\u817f\uff0c\u8e22\u5728\u624b\u8155\u4e0a\uff0c\u4e00\u53ea\u7eaf\u94a2\u6253\u9020\u7684\u5224\u5b98\u7b14\u8131\u624b\u98de\u53bb\u3002 \u3000\u3000\u4ed6\u60ca\u547c\u4e00\u58f0\uff0c\u584c\u8170\u9519\u6b65\uff0c\u5b59\u658c\u5374\u600e\u4f1a\u518d\u7ed9\u4ed6\u5598\u606f\u7684\u673a\u4f1a\uff0c\u5200\u5149\u5982\u96ea\uff0c\u4e13\u627e\u4ed6\u5de6\u9762\u7684\u7a7a \u95e8\uff0c\u593a\u547d\u4e09\u90ce\u4e00\u679d\u5224\u5b98\u7b14\u624d\u67b6\u5f97\u4e24\u62db\uff0c\u4e00\u58f0\u60e8\u547c\uff0c\u5de6\u80a9\u4e0a\u53ef\u7740\u4e86\u4e00\u5200\uff0c\u75db\u5f97\u8fde\u53f3\u624b\u7684\u5224\u5b98\u7b14 \u90fd\u4e22\u4e86\u3002\u5b59\u658c\u9762\u5bd2\u5982\u6c34\uff0c\u6210\u5fc3\u5c06\u8fd9\u6c5f\u5317\u5de8\u76d7\u5e9f\u5728\u81ea\u5df1\u5200\u4e0b\uff0c\u5237\u5730\uff0c\u53c8\u662f\u4e00\u5200\uff0c\u593a\u547d\u4e09\u90ce\u75db\u5f97 \u51b7\u6c57\u76f4\u5192\uff0c\u4ecd\u672a\u5fd8\u4e86\u9003\u547d\uff0c\u6251\u5730\uff0c\u8eba\u4e86\u4e0b\u53bb\uff0c\u201c\u61d2\u9a74\u6253\u6eda\u201d\uff0c\u62db\u5f0f\u867d\u65e0\u8d56\uff0c\u4f46\u5374\u603b\u7b97\u8eb2\u5f00\u4e86 \u6b64\u62db\u3002 \u3000\u3000\u90a3\u8fb9\u5c0f\u4e27\u95e8\u4e00\u58f0\u5389\u547c\uff0c\u559d\u9053\uff1a\u201c\u59d3\u5b59\u7684\uff0c\u5149\u68cd\u4e0d\u6253\u8eba\u4e0b\u7684\uff0c\u597d\u670b\u53cb\u672a\u514d\u592a\u72e0\u4e86\u5427\uff01\u201d\u62bd \u8eab\u60f3\u8d76\u8fc7\u53bb\uff0c\u4f46\u5b59\u9526\u5e73\u7684\u5200\u5374\u4e0d\u8981\u547d\u7684\u7f20\u7740\u4ed6\uff0c\u4ed6\u5fc3\u8d8a\u6025\uff0c\u638c\u4e2d\u7684\u5251\u62db\u4e5f\u5c31\u8d8a\u4e71\uff0c\u731b\u542c\u5f97\u53c8 \u662f\u4e00\u58f0\u60e8\u547c\uff0c\u4ed6\u77e5\u9053\u593a\u547d\u4e09\u90ce\u516b\u6210\u513f\u662f\u5b8c\u4e86\u3002 \u3000\u3000\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u5b59\u658c\u5df2\u63a0\u4e86\u8fc7\u6765\uff0c\u5200\u5149\u4e00\u9886\uff0c\u76f4\u5241\u5c0f\u4e27\u95e8\u7684\u4e0a\u4e09\u8def\uff0c\u53e3\u4e2d\u5374\u559d\u9053\uff1a\u201c\u5e73 \u513f\u9000\u4e0b\u53bb\uff0c\u7528\u6697\u9752\u5b50\u5582\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u4e27\u95e8\u957f\u5251\u8d8a\u53d1\u4e0d\u6d4e\uff0c\u77ac\u773c\u4e4b\u95f4\uff0c\u80a9\u5934\u3001\u8170\u4e0b\uff0c\u53c8\u4e2d\u4e86\u4e24\u9897\u94c1\u83b2\u5b50\uff0c\u638c\u4e2d\u5251\u4e00\u62db\u201c\u5578\u96e8 \u8f6c\u98ce\u201d\u521a\u4f7f\u5230\u4e00\u534a\uff0c\u5c31\u75db\u5f97\u8fde\u5251\u62db\u90fd\u4f7f\u4e0d\u5b8c\uff0c\u773c\u524d\u5200\u5149\u4e00\u82b1\uff0c\u5de6\u817f\u4e0a\u53c8\u7740\u4e86\u4e00\u5200\u3002 \u3000\u3000\u8fd9\u79cd\u6d41\u8840\u7684\u573a\u9762\uff0c\u88f4\u73cf\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\uff0c\u4ed6\u5174\u594b\u5f97\u5168\u8eab\u53d1\u6296\uff0c\u6068\u4e0d\u5f97\u90a3\u8fd0\u5200\u5982\u98ce\uff0c\u8eab\u5f62\u5982 \u8c79\u4e0d\u662f\u5b59\u8001\u7239\uff0c\u800c\u662f\u81ea\u5df1\u624d\u5bf9\u5fc3\u601d\u3002 \u3000\u3000\u5b59\u658c\u81ea\u5df1\u77e5\u9053\u5288\u5728\u5c0f\u4e27\u95e8\u8eab\u4e0a\u7684\u4e00\u5200\uff0c\u5df2\u7528\u4e86\u516b\u6210\u52b2\uff0c\u5df2\u8db3\u591f\u53eb\u4ed6\u53bb\u89c1\u960e\u738b\u4e86\uff0c\u7528\u978b\u5e95 \u62b9\u4e86\u62b9\u5200\u53e3\u4e0a\u7684\u8840\uff0c\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u628a\u5730\u4e0a\u7684\u94c1\u83b2\u5b50\u62fe\u8d77\u6765\uff0c\u4e58\u7740\u5929\u6ca1\u4eae\uff0c\u8d76\u7d27\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5b59\u9526\u5e73\u55ef\u4e86\u4e00\u58f0\uff0c\u6643\u8d77\u706b\u6298\u5b50\uff0c\u5728\u5730\u4e0a\u62fe\u56de\u94c1\u83b2\u5b50\u2014\u2014\u90a3\u552f\u4e00\u53ef\u80fd\u9732\u4e86\u4ed6\u4eec\u8eab\u4efd\u7684\u4e1c \u897f\u3002 \u3000\u3000\u88f4\u73cf\u9ad8\u5174\u5f97\u4ece\u77ee\u5899\u4e0a\u8df3\u4e86\u4e0b\u6765\uff0c\u5b59\u658c\u671b\u7740\u4ed6\u4e00\u7b11\uff0c\u4e1d\u6beb\u6ca1\u6709\u56e0\u4e3a\u4ed6\u7aa5\u7834\u79d8\u5bc6\u800c\u6709\u4e0d\u6ee1\uff0c \u8fd9\u5f53\u7136\u662f\u56e0\u4e3a\u5df2\u5c06\u4ed6\u6ca1\u6709\u770b\u505a\u5916\u4eba\u7684\u7f18\u6545\u3002 \u3000\u3000\u4e09\u4eba\u56de\u5230\u623f\u91cc\uff0c\u5b59\u658c\u5c31\u5f00\u59cb\u68c0\u70b9\u884c\u88c5\uff0c\u88f4\u73cf\u77e5\u9053\u662f\u8981\u8d70\u4e86\uff0c\u5728\u65c1\u8fb9\u6346\u7740\u5175\u5668\uff0c\u65b9\u624d\u7684 \u4e8b\uff0c\u5b59\u658c\u4e00\u5b57\u4e0d\u63d0\uff0c\u88f4\u73cf\u867d\u7136\u5fc3\u91cc\u597d\u5947\uff0c\u5374\u4e5f\u4e0d\u95ee\uff0c\u6709\u65f6\u4e0d\u8fc7\u5077\u5077\u53bb\u7aa5\u671b\u5b59\u9526\u5e73\u7684\u773c\u8272\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u884c\u674e\uff0c\u5f53\u7136\u4e0d\u4f1a\u592a\u9ebb\u70e6\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u6536\u62fe\u597d\u4e86\uff0c\u6bcf\u5f53\u6536\u62fe\u884c\u674e\u7684\u65f6\u5019\uff0c\u88f4\u73cf\u5c31 \u4f1a\u975e\u5e38\u5174\u594b\uff0c\u56e0\u4e3a\u4ed6\u4eec\u53c8\u8981\u51fa\u53d1\u5230\u53e6\u4e00\u4e2a\u65b0\u7684\u73af\u5883\uff0c\u8fd9\u79cd\u5929\u6daf\u98d8\u6cca\u7684\u751f\u6d3b\uff0c\u662f\u6bcf\u4e2a\u5e74\u8f7b\u4eba\u90fd \u4e50\u4e8e\u5c1d\u8bd5\u7684\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u4e5f\u4e0d\u4f8b\u5916\u5730\u6709\u7740\u8fd9\u5174\u594b\u7684\u5fc3\u60c5\uff0c\u751a\u800c\u6bd4\u5f80\u5e38\u66f4\u8981\u5174\u594b\u4e9b\uff0c\u56e0\u4e3a\u4ed6\u53c8\u7ecf\u5386\u4e86\u4e00\u4ef6\u4ed6 \u4ee5\u524d\u6240\u672a\u66fe\u7ecf\u5386\u8fc7\u7684\u4e8b\u3002 \u3000\u3000\u5b59\u9526\u5e73\u4f4e\u7740\u5934\u5728\u6536\u62fe\u4e1c\u897f\uff0c\u5ffd\u5730\u770b\u5230\u88f4\u73cf\u7684\u4e24\u672c\u4e66\uff0c\u5979\u6beb\u672a\u5728\u610f\u5730\u629b\u5728\u88f4\u73cf\u8eab\u524d\uff0c\u88f4\u73cf \u4e5f\u6beb\u672a\u5728\u610f\u5730\u5c06\u5b83\u63d2\u5728\u5175\u5668\u62c5\u4e0b\u3002 \u3000\u3000\u4ed6\u4eec\u8fde\u591c\u901f\u884c\uff0c\u5929\u4eae\u7684\u65f6\u5019\uff0c\u5c31\u8d76\u5230\u4e00\u5ea7\u5c0f\u5c71\u7684\u5c71\u811a\u3002 \u3000\u3000\u90a3\u662f\u4eac\u9547\u5c71\u5730\u91cc\u4e00\u4e2a\u6bd4\u4e18\u9675\u7565\u4e3a\u5927\u4e9b\u7684\u5c0f\u5c71\uff0c\u4f46\u6ee1\u5c71\u9752\u7eff\uff0c\u5206\u5916\u89c9\u5f97\u73b2\u7403\u53ef\u7231\uff0c\u56e0\u4e3a\u8fd9 \u662f\u9547\u6c5f\u4e0e\u6c5f\u9675\u4e24\u5e9c\u4e4b\u95f4\u7684\u901a\u9053\uff0c\u56db\u5b63\u884c\u4eba\u9887\u591a\uff0c\u56e0\u6b64\u5728\u5c71\u811a\u4e0b\uff0c\u4f9d\u5c71\u67b6\u642d\u7740\u8336\u4ead\u548c\u9762\u9986\uff0c\u53c8 \u56e0\u4e3a\u6709\u597d\u51e0\u5bb6\u5728\u4e00\u8d77\uff0c\u56e0\u6b64\u5c31\u6709\u4e86\u7ade\u4e89\uff0c\u6bcf\u5bb6\u90fd\u6536\u62fe\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5012\u50cf\u662f\u8fd9\u65f6\u7a81\u7136\u591a\u4e86\u4e00\u4e2a \u5c0f\u9547\u7684\u6837\u5b50\u3002 \u3000\u3000\u867d\u662f\u51cc\u6668\uff0c\u4f46\u8fd9\u4e9b\u505a\u884c\u65c5\u751f\u610f\u7684\u5ba2\u5bb6\uff0c\u90fd\u5df2\u5f00\u5f20\u4e86\uff0c\u5b59\u658c\u770b\u4e86\u7d2f\u5f97\u54bb\u54bb\u5598\u6c14\u7684\u88f4\u73cf\u4e00 \u773c\uff0c\u4e5f\u8d70\u8fdb\u4e00\u5bb6\u5c0f\u9762\u9986\u53bb\u6253\u4e2a\u5c16\u3002 \u3000\u3000\u8fd9\u5c0f\u94fa\u5b50\u56db\u9762\u90fd\u662f\u680f\u6746\uff0c\u996d\u7897\u7c97\u7ec6\u7684\u6bdb\u7af9\u7f16\u7684\uff0c\u684c\u6905\u4e5f\u662f\u7af9\u5236\uff0c\u770b\u4e0a\u53bb\u65e2\u6e05\u723d\uff0c\u53c8\u5e72 \u51c0\uff0c\u88f4\u73cf\u5750\u5728\u6905\u4e0a\u5598\u6c14\uff0c\u6697\u6697\u611f\u6fc0\u6709\u8fd9\u6781\u597d\u53bb\u5904\u3002 \u3000\u3000\u5802\u500c\u9001\u4e0a\u4e9b\u5403\u98df\uff0c\u4e5f\u65e0\u975e\u662f\u70ed\u6c64\u9762\u3001\u5305\u5b50\u4e00\u7c7b\u7684\u4e1c\u897f\uff0c\u88f4\u73cf\u548c\u5b59\u9526\u5e73\u5374\u5403\u5f97\u6d25\u6d25\u6709\u5473\uff0c \u5b59\u658c\u5219\u6709\u4e9b\u98df\u4e0d\u4e0b\u54bd\u7684\u6837\u5b50\u3002 \u3000\u3000\u8fd9\u65f6\u8336\u68da\u91cc\u9664\u4e86\u4ed6\u4eec\u4e09\u4eba\u4e4b\u5916\uff0c\u5c31\u522b\u65e0\u5176\u4ed6\u7684\u5ba2\u4eba\uff0c\u84e6\u5730\uff0c\u9053\u4e0a\u5c18\u571f\u5927\u8d77\uff0c\u5954\u6765\u4e24\u5339\u5065 \u9a6c\uff0c\u500f\u7136\u5728\u8fd9\u95f4\u8336\u68da\u524d\u505c\u4f4f\u4e86\uff0c\u5237\u5730\u4e0b\u4e86\u9a6c\uff0c\u671b\u91cc\u56b7\u9053\uff1a\u201c\u5582\uff01\u5e97\u5bb6\uff0c\u5feb\u6765\u513f\u7897\u9762\uff0c\u7237\u4eec\u5403 \u4e86\u4e0a\u8def\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u8fd9\u4eba\u7ec6\u9ad8\u6311\u8eab\u6750\uff0c\u6ee1\u8138\u4e0a\u5374\u5e26\u7740\u75c5\u5bb9\uff0c\u4e24\u53ea\u773c\u775b\u9677\u4e0b\u53bb\u8001\u6df1\uff0c\u7709\u9aa8\u9ad8\u9ad8\u8038\u8d77\uff0c\u518d \u52a0\u4e0a\u4e24\u8fb9\u9f13\u8d77\u597d\u9ad8\u7684\u592a\u9633\u7a74\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u662f\u5177\u5185\u529f\u7684\u7ec3\u5bb6\u5b50\u3002\u53e6\u4e00\u4eba\u5374\u6b63\u597d\u76f8\u53cd\uff0c\u4e00\u8eab\u80d6\u8089 \u968f\u7740\u4ed6\u8d70\u8fdb\u6765\u7684\u811a\u6b65\u4e0d\u4f4f\u98a4\u52a8\u7740\uff0c\u4f46\u8170\u7554\u7684\u5927\u76ae\u56ca\uff0c\u5374\u53c8\u544a\u8bc9\u522b\u4eba\u8fd9\u662f\u4f4d\u4f7f\u6697\u5668\u7684\u884c\u5bb6\uff0c\u5f53 \u7136\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u80d6\u4e00\u7626\u7684\u4e24\u4e2a\u4eba\uff0c\u4e00\u8d70\u8fdb\u6765\u5c31\u7528\u773c\u775b\u4e0d\u4f4f\u5730\u6253\u91cf\u7740\u5b59\u658c\u4ed6\u4eec\uff0c\u5b59\u658c\u8d76\u7d27\u4f4e\u4e0b\u5934\u53bb\u5403 \u9762\uff0c\u4eff\u4f5b\u751a\u4e3a\u4e0d\u613f\u610f\u62db\u60f9\u4ed6\u4eec\u3002 \u3000\u3000\u88f4\u73cf\u8f6c\u8fc7\u4e86\u5934\u53bb\u671b\u4eba\u5bb6\uff0c\u89c9\u5f97\u4eba\u5bb6\u7684\u773c\u775b\u91cc\u4eff\u4f5b\u6709\u7535\u5149\u4f3c\u7684\uff0c\u4e5f\u8d76\u7d27\u8f6c\u8fc7\u5934\uff0c\u4e0d\u6562\u518d\u53bb \u6253\u91cf\u4eba\u5bb6\uff0c\u614c\u5f20\u4e4b\u4e0b\uff0c\u624b\u8098\u4e00\u78b0\uff0c\u5374\u628a\u9760\u5728\u684c\u5b50\u65c1\u8fb9\u7684\u90a3\u6346\u5175\u5668\u78b0\u5012\u4e86\uff0c\u53d1\u51fa\u54d7\u7136\u4e00\u58f0\u5de8 \u54cd\u3002 \u3000\u3000\u65b9\u624d\u4ed6\u6346\u5175\u5668\u7684\u65f6\u5019\uff0c\u5fc3\u6709\u4e9b\u614c\uff0c\u610f\u6709\u4e9b\u4e71\uff0c\u6839\u672c\u6ca1\u6709\u6346\u597d\uff0c\u6b64\u523b\u88ab\u4ed6\u4e00\u649e\uff0c\u7ea2\u7f28\u67aa\uff0c \u6298\u94c1\u5200\uff0c\u9f50\u7709\u68cd\uff0c\u6563\u5f97\u4e00\u5730\u3002 \u3000\u3000\u800c\u90a3\u4e24\u672c\u7528\u9ed1\u6851\u76ae\u7eb8\u505a\u5c01\u9762\u7684\u7834\u4e66\uff0c\u4e5f\u843d\u5728\u8fd9\u4e9b\u5175\u5668\u4e2d\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u521a\u521a\u8d70\u8fdb\u6765\u7684\u6c49\u5b50\uff0c\u76ee\u5149\u4e00\u843d\u5230\u8fd9\u4e24\u672c\u4e66\u4e0a\uff0c\u8138\u8272\u597d\u50cf\u90fd\u9aa4\u7136\u53d8\u4e86\u4e00\u4e0b\uff0c\u4e24\u4eba\u4e92 \u89c6\u4e86\u4e00\u773c\uff0c\u53c8\u671b\u4e86\u5750\u5728\u90a3\u91cc\u4f4e\u5934\u5403\u9762\u7684\u5b59\u8001\u7239\u548c\u5df2\u7ad9\u8d77\u6765\u51c6\u5907\u5e2e\u52a9\u88f4\u73cf\u62fe\u5175\u5668\u7684\u5b59\u9526\u5e73\u4e00 \u773c\uff0c\u76ee\u5149\u6700\u540e\u843d\u5728\u90a3\u8e72\u5728\u5730\u4e0a\u6b63\u624b\u5fd9\u811a\u4e71\u5730\u62fe\u7740\u5175\u5203\u7684\u88f4\u73cf\u8eab\u4e0a\u3002 \u3000\u3000\u88f4\u73cf\u5f53\u7136\u4e0d\u77e5\u9053\u4eba\u5bb6\u7684\u4e24\u53cc\u7535\u76ee\u6b63\u5229\u7bad\u4f3c\u7684\u77aa\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u6b63\u81ea\u6127\u607c\u7740\u81ea\u5df1\u7684\u83bd\u649e\uff0c\u54ea \u77e5\u773c\u524d\u4e00\u82b1\uff0c\u7a81\u7136\u770b\u5230\u4e86\u4e00\u4e2a\u4eba\u4e5f\u8e72\u4e86\u4e0b\u6765\uff0c\u7adf\u5e2e\u7740\u81ea\u5df1\u5c06\u6563\u843d\u5728\u6700\u8fdc\u7684\u4e00\u6746\u82b1\u67aa\u62fe\u4e86\u8fc7 \u6765\u3002 \u3000\u3000\u4ed6\u611f\u6fc0\u5730\u4e00\u7b11\uff0c\u4e00\u62fe\u5934\uff0c\u770b\u5230\u8fd9\u5e2e\u7740\u81ea\u5df1\u62fe\u5175\u5203\u7684\u4eba\u7adf\u7136\u5c31\u662f\u65b9\u624d\u8d70\u8fdb\u6765\uff0c\u90a3\u4e2a\u773c\u775b\u91cc \u4eff\u4f5b\u6709\u7535\u5149\u95ea\u52a8\u7684\u80d6\u5b50\u3002 \u3000\u3000\u4ed6\u53c8\u671b\u5230\u8fd9\u80d6\u5b50\u4e5f\u662f\u4e00\u8138\u7b11\u5bb9\uff0c\u90a3\u4e48\u81c3\u80bf\u7684\u8eab\u6750\uff0c\u8e72\u5728\u5730\u4e0a\u5c31\u50cf\u662f\u4e2a\u7403\u4f3c\u7684\uff0c\u8fd9\u65f6\u5019\uff0c \u8fd9\u80d6\u5b50\u6b63\u51c6\u5907\u53bb\u62fe\u90a3\u4e24\u672c\u9ed1\u6851\u76ae\u9762\u7684\u4e66\u2014\u2014\u4f46\u662f\u8fd9\u4e24\u672c\u4e66\u8ddd\u79bb\u88f4\u73cf\u8f83\u8fd1\uff0c\u88f4\u73cf\u4e00\u4f38\u624b\uff0c\u5c31\u5c06 \u90a3\u4e24\u672c\u4e66\u5148\u62fe\u5230\u624b\u91cc\u4e86\uff0c\u5e76\u4e14\u5bf9\u90a3\u5e2e\u81ea\u5df1\u5fd9\u7684\u80d6\u5b50\u5fae\u7b11\u4e86\u4e00\u4e0b\uff0c\u5fc3\u4e0b\u9887\u6709\u4e9b\u597d\u611f\uff0c\u56e0\u4e3a\u8fd9\u4e16 \u4e0a\u5bf9\u4ed6\u548c\u989c\u60a6\u8272\u7684\u4eba\uff0c\u5e76\u4e0d\u5927\u591a\u3002 \u3000\u3000\u54ea\u77e5\u9053\u90a3\u80d6\u5b50\u8138\u4e0a\u7684\u80a5\u8089\u5374\u4e0d\u77e5\u600e\u5730\u62bd\u52a8\u4e86\u4e00\u4e0b\uff0c\u5f20\u53e3\u8bf4\u4e86\u4e00\u53e5\u6d3b\uff0c\u8bf4\u7684\u662f\u4ec0\u4e48\uff0c\u88f4\u73cf \u6839\u672c\u542c\u4e0d\u5230\uff0c\u4f46\u662f\u5b59\u9526\u5e73\u5374\u542c\u5230\u4e86\u3002 \u3000\u3000\u90a3\u80d6\u5b50\u8bf4\u7684\u662f\uff1a\u201c\u5c0f\u54e5\uff0c\u4f60\u8fd9\u4e24\u672c\u4e66\u53ef\u4e0d\u53ef\u4ee5\u501f\u7ed9\u6211\u770b\u770b\uff1f\u201d\u4f46\u88f4\u73cf\u4ecd\u77aa\u7740\u5927\u773c\u775b\uff0c\u542b \u7b11\u671b\u7740\u4ed6\uff0c\u4ed6\u6b63\u6709\u4e9b\u83ab\u6d4b\u9ad8\u6df1\u7684\u611f\u89c9\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u5750\u5728\u684c\u4e0a\u7684\u5c0f\u59d1\u5a18\u5374\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u548c\u4ed6\u8bf4\u8bdd\uff0c\u4ed6\u662f\u4e2a\u804b\u5b50\uff0c\u53c8\u662f\u4e2a\u54d1\u5df4\uff0c\u4f60\u548c \u4ed6\u8bf4\u8bdd\u4ed6\u4e5f\u542c\u4e0d\u89c1\u3002\u201d \u3000\u3000\u8fd9\u80d6\u5b50\u201c\u54e6\u201d\u4e86\u4e00\u4e00\u58f0\uff0c\u50cf\u662f\u9887\u4e3a\u60ca\u5f02\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u773c\u73e0\u8f6c\u4e86\u4e24\u8f6c\uff0c\u7a81\u7136\u8138\u4e0a\u63a0\u8d77\u4e00\u4e2a \u8be1\u5f02\u7684\u7b11\u5bb9\uff0c\u6307\u7740\u90a3\u4e24\u672c\u4e66\u5411\u5b59\u9526\u5e73\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u4e24\u672c\u4e66\u5356\u4e0d\u5356\u7684\uff1f\u201d \u3000\u3000\u5b59\u9526\u5e73\u4e00\u76b1\u7709\uff0c\u5fae\u55d4\u9053\uff0c\u201c\u4e0d\u5356\uff0c\u4e0d\u5356\uff0c\u6211\u4eec\u662f\u5356\u628a\u5f0f\u7684\uff0c\u53ef\u4e0d\u662f\u5356\u4e66\u7684\uff0c\u4f60\u770b\u4e66\uff0c\u5356 \u4e66\u7684\u94fa\u5b50\u591a\u5f97\u5f88\u2014\u2014\u201d\u90a3\u80d6\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u7709\u68a2\u773c\u89d2\u6cdb\u8d77\u4e00\u79cd\u751f\u50cf\u662f\u6361\u7740\u5b9d\u8d1d\u7684\u795e\u8272\uff0c\u773c\u89d2\u4fcf \u6084\u4e00\u779f\u90a3\u59cb\u7ec8\u6c89\u7740\u8138\u7ad9\u5728\u65c1\u8fb9\u7684\u7626\u957f\u6c49\u5b50\u4e00\u773c\uff0c\u53c8\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u77e5\u9053\u4f60\u4e0d\u662f\u5356\u4e66\uff0c\u4f46\u662f\u6211 \u770b\u7740\u8fd9\u4e24\u672c\u4e66\u7684\u9762\u5b50\u5f88\u597d\u770b\uff0c\u5c31\u60f3\u4e70\u4e0b\u6765\uff0c\u5316\u4e2a\u5341\u4e24\u3001\u516b\u4e24\u94f6\u5b50\uff0c\u6211\u90fd\u4e0d\u5728\u4e4e\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u5b59\u9526\u5e73\u53ef\u5403\u4e86\u60ca\uff0c\u987b\u77e5\u201c\u5341\u4e24\u3001\u516b\u4e24\u201d\u94f6\u5b50\uff0c\u5728\u5f53\u65f6\u53ef\u662f\u4e00\u7b14\u9887\u4e3a\u60ca\u4eba\u7684\u6570\u76ee\uff0c \u5b59\u9526\u5e73\u4ed6\u4eec\u52b3\u82e6\u5954\u6ce2\u51e0\u4e2a\u6708\uff0c\u4e5f\u53ef\u80fd\u6ca1\u6709\u8fd9\u79cd\u6536\u5165\uff0c\u5979\u81ea\u7136\u4f1a\u5947\u602a\u4e3a\u4ec0\u4e48\u6709\u4eba\u4f1a\u5316\u8fd9\u4e48\u591a\u94f6 \u5b50\u6765\u4e70\u8fd9\u4e24\u672c\u975e\u4f46\u4e0d\u8d77\u773c\uff0c\u800c\u4e14\u7b80\u76f4\u5df2\u7ecf\u7834\u70c2\u4e86\u7684\u4e66\u3002 \u3000\u3000\u5979\u5403\u60ca\u800c\u6000\u7591\u5730\u6253\u91cf\u4e86\u8fd9\u80d6\u5b50\u51e0\u773c\uff0c\u5750\u5728\u90a3\u91cc\u5403\u9762\u7684\u201c\u5b59\u8001\u7239\u201d\u66f4\u662f\u6ee1\u8138\u5f02\u5bb9\uff0c\u56e0\u4e3a\u4ed6 \u6614\u5e74\u4e5f\u662f\u6b66\u6797\u4e2d\u9887\u6709\u4e9b\u201c\u4e07\u513f\u201d\u7684\u4eba\u7269\uff0c\u95ef\u8361\u6c5f\u6e56\u5df2\u6709\u591a\u5e74\uff0c\u56e0\u4e4b\u4ed6\u4e00\u773c\u4fbf\u731c\u51fa\u8fd9\u4e00\u80d6\u4e00\u7626 \u4e24\u4eba\u662f\u8c01\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u80d6\u5b50\u7adf\u662f\u6c5f\u6e56\u4e2d\u5927\u5927\u6709\u540d\u7684\u591a\u81c2\u4eba\u718a\u90b1\u6000\u4ec1\uff0c\u800c\u90a3\u4e2a\u9ec4\u9762\u7684\u7626\u957f\u6c49\u5b50\uff0c\u5374\u7adf\u662f\u540d \u6ee1\u6b66\u6797\u7684\u72ec\u884c\u5de8\u76d7\u3001\u91d1\u9762\u97e6\u9640\u53f6\u4e4b\u8f89\u3002 \u3000\u3000\u662f\u4ee5\u201c\u5b59\u8001\u7239\u201d\u6240\u60ca\u5f02\u7684\uff0c\u5e76\u4e0d\u662f\u4ed6\u5973\u513f\u6240\u60ca\u5f02\u7684\uff0c\u800c\u662f\u8fd9\u4e24\u4e2a\u5728\u6c5f\u6e56\u4e2d\u4e45\u8457\u51f6\u540d\u7684\u4eba \u7269\uff0c\u600e\u4f1a\u5ba2\u5ba2\u6c14\u6c14\u5730\u5411\u4e2a\u5973\u5b69\u5b50\u4e70\u4e24\u672c\u4e66\uff1f \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u6ca1\u6709\u6ce8\u610f\u5230\u8fd9\u4e24\u672c\u4e66\u7684\u4ef7\u503c\u2014\u2014\u5728\u4e00\u4e2a\u5237\u9a6c\u7684\u6b8b\u5e9f\u5b69\u5b50\u8eab\u4e0a\u7684\u4e66\uff0c\u6709 \u8c01\u4f1a\u53bb\u6ce8\u610f\u5230\u5b83\u7684\u4ef7\u503c\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\uff1a\u8fd9\u4e24\u672c\u4e66\u7adf\u662f\u6b66\u6797\u4e2d\u4eba\u68a6\u5bd0\u4ee5\u6c42\uff0c\u66fe\u5728\u6c5f\u6e56\u4e2d\u60f9\u51fa\u65e0\u6570\u98ce\u6ce2\uff0c\u53c8\u4f7f\u5f97\u5343 \u624b\u4e66\u751f\u548c\u51b7\u6708\u4ed9\u5b50\u4e3a\u4e4b\u53cd\u76ee\uff0c\u51b7\u6708\u4ed9\u5b50\u4e5f\u56e0\u4e4b\u5403\u5c3d\u82e6\u5934\uff0c\u8fd8\u9669\u4e9b\u4e27\u751f\u4e8e\u6b64\u7684\u6b66\u6797\u79d8\u7b08\u3002 \u3000\u3000\u8fd9\u4e24\u672c\u4e66\uff0c\u7adf\u662f\u767e\u5341\u5e74\u524d\u7684\u6b66\u6797\u7b2c\u4e00\u5f02\u4eba\uff0c\u5927\u540d\u81f3\u4eca\u4f20\u8bf5\u4e0d\u606f\u7684\u6d77\u5929\u5b64\u71d5\u6240\u9057\u7559\u7684\u79d8 \u7b08\uff0c\u6d77\u5929\u5b64\u71d5\u4e00\u8eab\u6df1\u4e0d\u53ef\u6d4b\u7684\u6b66\u529f\uff0c\u5c31\u5168\u8bb0\u8f7d\u5728\u8fd9\u4e0a\u9762\u3002 \u3000\u3000\u90a3\u591a\u81c2\u4eba\u718a\u548c\u91d1\u9762\u97e6\u9640\u9887\u5177\u773c\u529b\uff0c\u4ed6\u4eec\u4e00\u773c\u770b\u5230\u5728\u4e00\u4e2a\u8d70\u6c5f\u6e56\u5356\u827a\u7684\u5b69\u5b50\u8fd9\u91cc\uff0c\u7adf\u6709\u7740 \u548c\u4f20\u8bf4\u4e2d\u7684\u201c\u6d77\u5929\u79d8\u7b08\u201d\u6781\u76f8\u4f3c\u7684\u4e66\uff0c\u60ca\u5f02\u4e4b\u4e0b\uff0c\u81ea\u7136\u4e5f\u96be\u514d\u5947\u602a\uff0c\u5374\u81ea\u7136\u4e5f\u6709\u4e9b\u6000\u7591\u3002 \u3000\u3000\u56e0\u4e4b\u591a\u81c2\u4eba\u718a\u5c31\u6545\u610f\u8e72\u4e0b\u6765\u5e2e\u5fd9\uff0c\u60f3\u770b\u770b\u8fd9\u4e24\u672c\u4e66\u662f\u5426\u5c31\u662f\u4ed6\u4eec\u6240\u6599\u4e2d\u7684\uff0c\u4ed6\u867d\u7136\u6ca1\u6709 \u62ff\u7740\u8fd9\u4e24\u672c\u4e66\uff0c\u4f46\u662f\u88f4\u73cf\u62ff\u4e66\u7684\u65f6\u5019\uff0c\u4e66\u9875\u6563\u5f00\u4e86\u4e00\u4e0b\uff0c\u5df2\u88ab\u8fd9\u8001\u6c5f\u6e56\u4e00\u773c\u77a5\u89c1\u91cc\u9762\u679c\u7136\u50cf \u662f\u6709\u7740\u4e9b\u7ec3\u529f\u7684\u56fe\u5f62\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5e76\u4e0d\u6562\u7acb\u65f6\u4f38\u624b\u53bb\u62a2\uff0c\u8bd5\u60f3\u8fd9\u4e48\u4e00\u672c\u4e66\uff0c\u600e\u4f1a\u5728\u4e00\u4e2a\u6b66\u529f\u5e73\u5e38\u7684\u4eba\u8eab\u4e0a\u2014\u2014\u5c31\u662f \u6b66\u529f\u5e73\u5e38\u7684\u4eba\uff0c\u5f97\u4e86\u8fd9\u4e24\u672c\u4e66\u540e\uff0c\u6b66\u529f\u4e5f\u4e0d\u4f1a\u518d\u5e73\u5e38\u4e86\u5440\uff01\u8fd9\u672c\u662f\u6781\u5408\u7406\u7684\u6599\u60f3\u3002 \u3000\u3000\u56e0\u4e4b\u8fd9\u8eab\u5f62\u4f3c\u80a5\u732a\uff0c\u72e1\u733e\u5374\u5982\u72d0\u72f8\u7684\u8001\u6c5f\u6e56\uff0c\u5c31\u7528\u8a00\u8bed\u8bd5\u63a2\u7740\uff0c\u5b59\u9526\u5e73\u8fd9\u4e48\u4e00\u7b54\u8bdd\uff0c\u4ed6 \u8138\u4e0a\u7684\u5047\u7b11\u5c31\u53d8\u505a\u771f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u4f38\u624b\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u952d\u94f6\u5b50\u6765\uff0c\u8db3\u6709\u5341\u4e24\u91cd\uff0c\u62ff\u5728\u624b\u4e2d\u4e00\u6643\uff0c\u4e00\u9762\u53c8\u5e26\u7740\u8be1\u7b11\u8bf4 \u9053\uff1a\u201c\u6211\u6700\u559c\u6b22\u6536\u85cf\u5c01\u9762\u597d\u770b\u7684\u4e66\u4e86\uff0c\u4f60\u5356\u7ed9\u6211\uff0c\u8fd9\u952d\u94f6\u5b50\u5c31\u662f\u4f60\u7684\u3002\u201d \u3000\u3000\u4e00\u9762\u8bf4\u7740\uff0c\u4ed6\u8fd8\u4e00\u9762\u5411\u88f4\u73cf\u6253\u7740\u624b\u5f0f\uff0c\u4ed6\u548c\u5b59\u9526\u5e73\u7684\u773c\u775b\u90fd\u6709\u4e9b\u53d1\u76f4\u4e86\u3002 \u3000\u3000\u591a\u81c2\u4eba\u718a\u8138\u4e0a\u7684\u7b11\u5bb9\u6108\u89c1\u5f00\u6717\uff0c\u8fd9\u672c\u6b66\u6797\u4e2d\u4eba\u4eba\u68a6\u5bd0\u4ee5\u6c42\u7684\u79d8\u7b08\uff0c\u773c\u770b\u5c31\u8981\u843d\u5230\u4ed6\u7684\u624b \u4e0a\uff0c\u90a3\u4e48\u4e0d\u8981\u4e09\u5e74\uff0c\u90b1\u6000\u4ec1\u8fd9\u4e2a\u540d\u5b57\u5728\u6c5f\u6e56\u4e2d\u5c06\u8981\u6765\u5f97\u66f4\u5176\u54cd\u4eae\uff0c\u4ed6\u7684\u80a5\u8138\u4e0a\uff0c\u7efd\u5f00\u4e86\u82b1 \u6735\u3002 \u3000\u3000\u88f4\u73cf\u8fd8\u8e72\u5728\u5730\u4e0a\uff0c\u5b59\u9526\u5e73\u56de\u5934\u770b\u4e86\u5979\u7239\u7239\u4e00\u773c\uff0c\u5f81\u6c42\u5979\u7239\u7239\u7684\u610f\u89c1\uff0c\u662f\u4e0d\u662f\u8be5\u5c06\u8fd9\u4e24\u672c \u201c\u7834\u4e66\u201d\u5356\u7ed9\u8fd9\u4e2a\u201c\u75af\u5b50\u201d\u3002 \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u5219\u4e8e\u6697\u4e2d\u601d\u5fd6\u7740\uff0c\u8be5\u5982\u4f55\u5e94\u4ed8\u8fd9\u5c40\u9762\uff0c\u4ed6\u5230\u5e95\u4e5f\u662f\u4e45\u95ef\u6c5f\u6e56\uff0c\u6b64\u523b\u5df2\u731c\u51fa\u8fd9 \u4e2a\u201c\u5237\u9a6c\u6b8b\u5e9f\u5b69\u5b50\u201d\u8eab\u4e0a\u7684\u8fd9\u4e24\u672c\u4e66\u5fc5\u4e0d\u5bfb\u5e38\uff0c\u53ea\u662f\u4ed6\u4e3a\u907f\u5f3a\u4ec7\uff0c\u9690\u8ff9\u591a\u5e74\uff0c\u6b66\u6797\u4e2d\u7684\u4e8b\u4ed6 \u4e5f\u4e45\u5df2\u9694\u819c\uff0c\u662f\u4ee5\u6b64\u523b\u4ed6\u8fd8\u672a\u60f3\u51fa\u8fd9\u4e24\u672c\u4e66\uff0c\u5c31\u662f\u201c\u6d77\u5929\u79d8\u7b08\u201d\u6765\uff01 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u867d\u5df2\u77e5\u9053\u8fd9\u4e24\u672c\u4e66\u5fc5\u975e\u5bfb\u5e38\uff0c\u4ed6\u5f53\u7136\u4e5f\u4e0d\u613f\u610f\u88ab\u8fd9\u591a\u81c2\u4eba\u718a\u4ee5\u5341\u4e24\u94f6\u5b50\u4e70\u53bb\uff0c \u4f46\u662f\u4ed6\u5374\u60f3\u4e0d\u51fa\u4e00\u4e2a\u6cd5\u5b50\u6765\u62d2\u7edd\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e24\u4eba\u5728\u6c5f\u6e56\u4e2d\u90fd\u662f\u6709\u540d\u7684\u5fc3\u72e0\u624b\u8fa3\uff0c\u53cd\u76ee\u4fbf\u80fd\u6740\u4eba\uff0c\u201c\u5b59\u8001\u7239\u201d\u81ea\u5fd6\u6b66\u529f\uff0c\u65e0\u8bba \u5982\u4f55\u4e5f\u4e0d\u4f1a\u662f\u8fd9\u4e24\u4eba\u7684\u654c\u624b\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u6b63\u52a8\u7740\u5ff5\u5934\uff0c\u591a\u81c2\u4eba\u718a\u7684\u773c\u775b\u5374\u76ef\u5728\u5b59\u9526\u5e73\u8138\u4e0a\uff0c\u4ed6\u81ea\u4e5f\u65e9\u6709\u6253\u7b97\uff0c\u8fd9\u5973\u5b69\u4e00\u70b9 \u5934\uff0c\u4ed6\u81ea\u7136\u4e0d\u4f1a\u5728\u4e4e\u5341\u4e24\u94f6\u5b50\uff0c\u8fd9\u5973\u5b69\u4e00\u6447\u5934\uff0c\u90a3\u4ed6\u5c31\u8981\u4e0d\u5ba2\u6c14\u5730\u4ee5\u66b4\u529b\u6765\u62a2\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u59cb\u7ec8\u672a\u53d1\u4e00\u8a00\u7684\u91d1\u9762\u97e6\u9640\u53f6\u4e4b\u8f89\u6b64\u523b\u7a81\u5730\u51b7\u51b7\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u8fd9\u672c\u4e66\u5356\u7ed9\u6211\uff0c \u6211\u7ed9\u4f60\u4e00\u767e\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u5b59\u9526\u5e73\u53c8\u4e00\u60ca\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u76ee\u5149\u5728\u8fd9\u4e24\u4e2a\u201c\u75af\u5b50\u201d\u8138\u4e0a\u4e00\u626b\u3002 \u3000\u3000\u90a3\u591a\u81c2\u4eba\u718a\u6b64\u523b\u9762\u4e0a\u7684\u795e\u8272\uff0c\u5df2\u662f\u96be\u770b\u5df2\u6781\uff0c\u5f3a\u7b11\u7740\u4fa7\u76ee\u9053\uff1a\u201c\u53f6\u8001\u54e5\uff0c\u4f60\u8fd9\u53c8\u662f\u4f55 \u5fc5\uff0c\u4f60\u4e70\u548c\u6211\u4e70\uff0c\u8fd8\u4e0d\u662f\u4e00\u6837\u5417\uff1f\u201d \u3000\u3000\u91d1\u9762\u97e6\u9640\u8138\u4e0a\u5374\u4e00\u4e1d\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u505a\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4e70\u5f97\u96be\u9053\u6211\u5c31\u4e70\u4e0d\u5f97 \u5417\uff1f\u201d \u3000\u3000\u591a\u81c2\u4eba\u718a\u9762\u8272\u60e8\u53d8\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u597d\uff0c\u597d\u2026\u2026\u201d\u7a81\u5730\u4e00\u8f6c\u5934\uff0c\u54ac\u7259\u5411\u5b59\u9526\u5e73\u8bf4\u9053\uff1a\u201c\u5c0f\u59d1 \u5a18\uff0c\u6211\u51fa\u4e8c\u767e\u4e24\u94f6\u5b50\u3002\u201d\u4e00\u9762\u53c8\u4f38\u624b\uff0c\u638f\u51fa\u4e00\u53e0\u94f6\u7968\u6765\uff0c\u62bd\u4e86\u4e00\u5f20\uff0c\u5728\u5b59\u9526\u5e73\u7684\u8138\u4e0a\u4e00\u626c\uff0c \u63a5\u7740\u53c8\u8bf4\u9053\uff1a\u201c\u8fd9\u5f20\u7968\u5b50\u662f\u2018\u6e90\u88d5\u2019\u7684\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u4efb\u4ec0\u4e48\u5730\u65b9\u90fd\u80fd\u5151\u73b0\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u6b66\u6797\u4e2d\u7684\u51f6\u4eba\uff0c\u5e73\u65e5\u95f4\u76f8\u5904\uff0c\u9887\u4e3a\u81ed\u5473\u76f8\u6295\uff0c\u8fd8\u66fe\u7ecf\u8054\u624b\u4e0a\u8fc7\u7ebf\uff0c\u5f00\u8fc7\u6252\uff0c\u4e00\u642d \u4e00\u5f53\uff0c\u5f88\u505a\u4e86\u51e0\u4ef6\u6076\u4e8b\u3002 \u3000\u3000\u4f46\u6b64\u523b\u8fd9\u4e24\u4e2a\u4e92\u5938\u201c\u4e49\u6c14\u65e0\u5148\u201d\u7684\u89d2\u8272\uff0c\u4e66\u8fd8\u6ca1\u6709\u5f97\u5230\uff0c\u5374\u5df2\u95f9\u8d77\u5185\u54c4\u6765\uff0c\u800c\u8fd9\u4e48\u4e00 \u6765\uff0c\u4e24\u4eba\u4e92\u6709\u987e\u5fcc\uff0c\u8c01\u4e5f\u4e0d\u6562\u4f38\u624b\u5f3a\u62a2\u4e86\u3002 \u3000\u3000\u4f46\u662f\u5b59\u9526\u5e73\u53ef\u8d8a\u6765\u8d8a\u7cca\u6d82\u4e86\uff0c\u90a3\u7aef\u7740\u4e00\u53ea\u76d8\u5b50\u7ad9\u5728\u65c1\u8fb9\u7684\u5e97\u4f19\uff0c\u6b63\u662f\u76ee\u5b9a\u53e3\u5446\uff0c\u6068\u4e0d\u5f97 \u81ea\u5df1\u4e5f\u6709\u8fd9\u4e48\u672c\u4e66\uff0c\u4e00\u4e24\u94f6\u5b50\u5c31\u5356\u4e86\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u201c\u4e8c\u767e\u4e24\u94f6\u5b50\uff0c\u2018\u6e90\u88d5\u201d\u7684\u94f6\u7968\uff01\u2026\u2026\u59d1\u5a18\uff0c\u4f60\u5c31\u5356\u4e86\u5427\u3002\u201c\u4ed6\u53c8\u4fa7 \u8fc7\u5934\uff0c\u671d\u5b59\u658c\u4e0d\u80dc\u7fa1\u6155\u5730\u9053\uff1a\u201c\u8001\u7239\uff0c\u4e8c\u767e\u4e24\u94f6\u5b50\u5440\u2014\u2014\u201d\u91d1\u9762\u97e6\u9640\u6068\u6068\u5730\u671d\u4ed6\u4e00\u77aa\u773c\u775b\uff0c \u5413\u5f97\u4ed6\u4e0b\u9762\u7684\u8bdd\u518d\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u201c\u5b59\u8001\u7239\u201d\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u672c\u4e66 \u662f\u8fd9\u4e2a\u5b69\u5b50\u7684\uff0c\u6211\u4eec\u4e5f\u505a\u4e0d\u5f97\u4e3b\uff0c\u5176\u5b9e\u4e24\u4f4d\u53c8\u4f55\u5fc5\u82b1\u8fd9\u591a\u94f6\u5b50\uff0c\u6765\u4e70\u2014\u2014\u201d\u591a\u81c2\u4eba\u718a\u7a81\u7136\u6717 \u58f0\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u6307\u7740\u201c\u5b59\u8001\u7239\u201d\u8fde\u8fde\u7b11\u9053\uff1a\u201c\u5440\uff0c\u5440\uff0c\u6211\u90b1\u6000\u4ec1\u773c\u775b\u8d8a\u6765\u8d8a\u5dee\u4e86\uff0c\u76f4\u5230\u73b0\u5728 \u624d\u8ba4\u51fa\u4f60\u6765\uff0c\u4f60\u4e0d\u662f\u4e94\u864e\u65ad\u9b42\u5200\u5b59\u658c\u5417\uff1f\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\u2014\u2014\u2014\u4ed6\u54c8\u54c8\u5927\u7b11\u4e86\u4e00\u9635\uff0c\u53c8\u9053\uff1a \u201c\u65e2\u7136\u662f\u4f60\uff0c\u90a3\u8fd9\u4ef6\u4e8b\u53ef\u597d\u529e\u4e86\uff0c\u6211\u90b1\u6000\u4ec1\u8ddf\u4f60\u53ef\u662f\u8001\u670b\u53cb\u4e86\uff0c\u7d20\u6765\u6ca1\u6709\u4ec0\u4e48\u96be\u8fc7\uff0c\u6614\u5e74 \u2018\u4e09\u715e\u4e94\u9738\u2019\u90fd\u8981\u627e\u4f60\u7684\u9ebb\u70e6\uff0c\u6211\u8fd8\u66ff\u4f60\u8bf4\u8fc7\u8bdd\uff0c\u4eca\u5929\u2014\u2014\u54c8\uff0c\u4f60\u603b\u5f97\u5356\u6211\u4e2a\u9762\u5b50\u5427\u3002\u201d \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u8fd9\u65f6\u4e0d\u7981\u9762\u8272\u60e8\u53d8\uff0c\u4ed6\u77e5\u4e8c\u4eba\u5bb6\u5df2\u8ba4\u51fa\u81ea\u5df1\u6765\uff0c\u81ea\u5df1\u518d\u60f3\u4e0d\u8ba4\u5e10\u4e5f\u4e0d\u884c\u4e86\uff0c \u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u90a3\u91d1\u9762\u97e6\u9640\u8eab\u5f62\u4e00\u52a8\uff0c\u7a81\u5730\u5411\u524d\u8de8\u4e86\u4e00\u6b65\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f5c\u4e70\u5356\u53ef\u4e0d\u51c6\u8bb2\u4ea4\u60c5\uff0c\u5b59\u670b\u53cb\uff0c \u4f60\u5927\u6982\u4e5f\u77e5\u9053\u6211\u7684\u813e\u6c14\uff0c\u73b0\u5728\u627e\u51fa\u4e94\u767e\u4e24\u94f6\u5b50\u4e70\u4f60\u8fd9\u4e24\u672c\u4e66\uff0c\u2018\u4e09\u715e\u4e94\u9738\u2019\u8ddf\u4f60\u7ed3\u4ec7\u7684\u6881 \u5b50\uff0c\u6211\u53f6\u4e4b\u8f89\u4e5f\u5168\u66ff\u4f60\u5305\u63fd\u8fc7\u6765\uff0c\u4f60\u8bf4\uff0c\u4f60\u5356\u7ed9\u8c01\u5427\uff1f\u201d \u3000\u3000\u591a\u81c2\u4eba\u718a\u4e00\u62e7\u8eab\uff0c\u53f3\u624b\u5df2\u4f38\u8fdb\u4ed6\u8eab\u7554\u7684\u76ae\u56ca\u91cc\uff0c\u4e5f\u81ea\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u53f6\u7684\uff0c\u6211\u90b1\u67d0\u4eba\u8ddf\u4f60 \u603b\u7b97\u8fd8\u6709\u4e09\u5206\u4ea4\u60c5\uff0c\u4f60\u8981\u771f\u8fd9\u4e48\u4e0d\u8bb2\u4e49\u6c14\uff0c\u522b\u4eba\u6015\u4f60\u7684\u91d1\u521a\u638c\uff0c\u6211\u90b1\u67d0\u4eba\u53ef\u4e0d\u542b\u7cca\u4f60\u7684\u3002\u201d \u3000\u3000\u91d1\u9762\u97e6\u9640\u6df1\u6c89\u7684\u53cc\u76ee\u4e00\u51db\uff0c\u5200\u4e5f\u4f3c\u5730\u77aa\u4f4f\u90b1\u6000\u4ec1\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4f60\u8bf4\u7684\uff0c\u90a3\u4e48\u4f60\u5c31 \u522b\u602a\u6211\u5fc3\u72e0\u624b\u8fa3\uff0c\u8981\u600e\u4e48\u529e\u4f60\u5c31\u770b\u7740\u529e\u5427\u3002\u201d\u53c8\u4e00\u4fa7\u7768\u5b59\u658c\u4e00\u773c\uff0c\u6a2a\u7740\u773c\u775b\u8bf4\u9053\uff1a\u201c\u4f60\u5356\u8fd8 \u662f\u4e0d\u5356\uff0c\u4f60\u8981\u5356\u7ed9\u8c01\uff1f\u4f60\u53ef\u5f97\u5feb\u8bf4\uff0c\u8981\u4e0d\u7136\u4f60\u94f6\u5b50\u62ff\u4e0d\u5230\uff0c\u547d\u5374\u9001\u6389\u4e86\uff0c\u54fc\uff01\u90a3\u4f60\u5c31\u4e0d\u50cf\u662f \u5149\u68cd\u4e86\uff01\u201d \u3000\u3000\u8bed\u58f0\u65b9\u843d\uff0c\u84e6\u5730\u2014\u2014\u7adf\u53c8\u54cd\u8d77\u4e00\u9635\u51b7\u7b11\u7684\u58f0\u97f3\uff0c\u4e00\u4e2a\u751f\u51b7\u5df2\u6781\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4e24\u4e2a\u5168\u4e0d \u5356\uff0c\u4f60\u4eec\u4e24\u4e2a\u98de\u5feb\u7ed9\u6211\u6eda\u5427\uff01\u201d \u3000\u3000\u4f17\u4eba\u90e8\u9aa4\u7136\u4e00\u60ca\uff0c\u591a\u81c2\u4eba\u718a\u548c\u91d1\u9762\u97e6\u9640\u66f4\u662f\u9762\u76ee\u5927\u53d8\uff0c\u5237\u5730\uff0c\u4e24\u4eba\u540c\u65f6\u4e00\u62e7\u8170\uff0c\u4e00\u5de6\u4e00 \u53f3\uff0c\u5411\u5916\u8dc3\u5f00\u597d\u51e0\u5c3a\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u624d\u770b\u5230\uff0c\u4e00\u4e2a\u7626\u524a\u7684\u4e2d\u5e74\u6587\u58eb\uff0c\u7a7f\u7740\u4e00\u9886\u94f6\u7070\u8272\u7684\u957f\u886b\uff0c\u5374\u5e26\u7740\u4e00\u80a1\u8f7b\u8511\u7684\u7b11 \u5bb9\uff0c\u7ad9\u5728\u81ea\u5df1\u65b9\u624d\u6240\u7ad9\u7684\u5730\u65b9\u540e\u9762\u3002 \u3000\u3000\u8fd9\u8336\u68da\u5916\u9762\u662f\u4e00\u6761\u5b98\u9053\uff0c\u8336\u68da\u5916\u9762\u90fd\u662f\u7a7a\u8361\u8361\u7684\uff0c\u4e00\u773c\u671b\u51fa\u53bb\uff0c\u53ef\u4ee5\u671b\u7740\u8001\u8fdc\uff0c\u4f46\u662f\u8fd9 \u94f6\u886b\u6587\u58eb\u662f\u4f55\u65f6\u6765\u7684\uff0c\u4ece\u54ea\u91cc\u6765\u7684\uff0c\u7adf\u6ca1\u6709\u4e00\u4eba\u77e5\u9053\uff0c\u800c\u8fd9\u4e9b\u8fd8\u90fd\u662f\u9887\u5177\u6b66\u529f\u7684\u7ec3\u5bb6\u5b50\u3002 \u3000\u3000\u4f46\u662f\uff0c\u6700\u5403\u60ca\u7684\u8fd8\u662f\u88f4\u73cf\uff0c\u4ed6\u4e00\u76f4\u6367\u7740\u90a3\u4e24\u672c\u4e66\u8e72\u5728\u5730\u4e0a\uff0c\u8fd9\u4e9b\u4eba\u8bf4\u7684\u8bdd\uff0c\u4ed6\u4e00\u5b57\u4e5f\u6ca1 \u6709\u542c\u5230\uff0c\u4f46\u662f\u4ed6\u5374\u731c\u51fa\u8fd9\u4e9b\u4eba\u8bf4\u8bdd\u7684\u5185\u5bb9\u90fd\u662f\u4e3a\u7740\u81ea\u5df1\u624b\u91cc\u7684\u8fd9\u4e24\u672c\u4ed6\u4e00\u76f4\u6ca1\u6709\u6ce8\u610f\u7684\u4e66\u3002 \u3000\u3000\u4f46\u662f\uff0c\u6b64\u523b\u4ed6\u5fc3\u5ff5\u8f6c\u52a8\u4e0b\uff0c\u4e0d\u7981\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u672c\u4e66\u672c\u6765\u662f\u5728\u90a3\u2018\u51b7\u5927\u53d4\u2019\u7684\u5305\u88b1\u91cc \u7684\uff0c\u2018\u51b7\u5927\u53d4\u2019\u7684\u6b66\u529f\uff0c\u9ad8\u6df1\u65e0\u6bd4\uff0c\u73b0\u5728\u8fd9\u4e24\u4e2a\u6c49\u5b50\uff0c\u4e5f\u90a3\u4e48\u6ce8\u610f\u8fd9\u4e24\u672c\u4e66\uff0c\u96be\u9053\u8fd9\u4e24\u672c\u4e66 \u4e0a\u9762\uff0c\u6709\u7740\u4ec0\u4e48\u79d8\u5bc6\uff1f\u5509\uff01\u6211\u4ee5\u524d\u4e3a\u4ec0\u4e48\u4e0d\u770b\u770b\u5462\uff1f\u201d \u3000\u3000\u987b\u77e5\u88f4\u73cf\u672c\u662f\u806a\u660e\u7edd\u9876\u7684\u4eba\uff0c\u4ee5\u524d\u4ed6\u4e00\u76f4\u5fc3\u795e\u4ea4\u7601\uff0c\u4e3a\u751f\u6d3b\u800c\u6323\u624e\uff0c\u662f\u4ee5\u6ca1\u6709\u60f3\u5230\u8fd9\u4e0a \u9762\u6765\uff0c\u6b64\u523b\u5fc3\u5ff5\u52a8\u5904\uff0c\u5374\u5df2\u731c\u7740\u51e0\u5206\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5fc3\u91cc\u6b63\u5728\u4e3a\u8fd9\u4ef6\u4e8b\u7830\u7830\u8df3\u52a8\u7684\u65f6\u5019\uff0c\u4ed6\u7a81\u7136\u53d1\u73b0\u4e00\u53cc\u7a7f\u7740\u7c89\u5e95\u6b65\u5c65\u7684\u811a\uff0c\u51fa\u73b0\u5728 \u4ed6\u773c\u524d\uff0c\u800c\u8fd9\u53cc\u811a\u7adf\u662f\u8fd9\u4e48\u719f\u6089\u3002 \u3000\u3000\u4e8e\u662f\u4e00\u4e00-- \u3000\u3000\u90a3\u5c0f\u9547\u5ba2\u6808\uff0c\u81ea\u5df1\u88ab\u70b9\u4e2d\u7a74\u9053\u540e\uff0c\u8737\u4f0f\u5728\u5e8a\u89d2\u7684\u90a3\u4e00\u5e55\uff0c\u4fbf\u53c8\u7535\u4e5f\u4f3c\u7684\u95ea\u8fc7\u4ed6\u7684\u5fc3\u5934\u3002 \u3000\u3000\u4ed6\u7981\u4e0d\u4f4f\u6084\u6084\u62ac\u5934\u5411\u4e0a\u671b\u53bb\uff0c\u4ed6\u770b\u5230\u4e00\u88ad\u94f6\u7070\u8272\u7684\u957f\u886b\uff0c\u548c\u90a3\u5f20\u9894\u4e0b\u5fae\u5fae\u5e26\u7740\u4e9b\u77ed\u9aed\uff0c \u65e2\u6e05\u4fca\uff0c\u53c8\u9ad8\u50b2\uff0c\u53c8\u6ee1\u542b\u8f7b\u8511\u7684\u8138\u3002 \u3000\u3000\u88f4\u73cf\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c\u4ed6\u60f3\u8d77\u8fd9\u4eba\u5c31\u662f\u90a3\u66fe\u7ecf\u66ff\u81ea\u5df1\u89e3\u5f00\u8fc7\u7a74\u9053\u7684\u4eba\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u4ed6 \u518d\u53d1\u73b0\u9664\u4e86\u81ea\u5df1\u4e4b\u5916\uff0c\u522b\u4eba\u7684\u8138\u4e0a\uff0c\u4e5f\u662f\u6ee1\u9732\u60ca\u60f6\u7684\u989c\u8272\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u53c8\u8f6c\u4e86\u8f6c\uff0c\u4e3a\u81ea\u5df1\u6253 \u7740\u4e3b\u610f\u3002 \u3000\u3000\u5b59\u658c\u7236\u5973\u3001\u91d1\u9762\u97e6\u9640\u3001\u591a\u81c2\u4eba\u718a\uff0c\u51e0\u53cc\u773c\u775b\u90fd\u5e26\u7740\u60ca\u60f6\u671b\u7740\u8fd9\u94f6\u886b\u6587\u58eb\uff0c\u4f46\u4ed6\u5374\u4e1d\u6beb\u4e5f \u6ca1\u6709\u4e3a\u4e4b\u52a8\u5bb9\uff0c\u76ee\u5149\u51b7\u7136\u671b\u7740\u5929\u4e0a\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u7a81\u7136\u51fa\u73b0\uff0c\u4f7f\u6bcf\u4e2a\u4eba\u90fd\u60ca\u5f02\u4e8e\u4ed6\u8f7b\u529f\u7684\u795e\u5947\uff0c\u4f46\u591a\u81c2\u4eba\u718a\u548c\u91d1\u9762\u97e6\u9640\u90fd\u4e5f\u662f\u201c\u4e07 \u513f\u201d\u94ee\u9535\u4f5c\u54cd\u7684\u4eba\u7269\uff0c\u5374\u4e0d\u662f\u5c31\u6b64\u53ef\u4ee5\u5413\u8dd1\u7684\uff0c\u5c24\u5176\u662f\u8fd9\u4e24\u672c\u6b66\u6797\u79d8\u7b08\uff0c\u8fd8\u50cf\u662f\u4e00\u5757\u80a5\u8089\uff0c \u5728\u4ed6\u4eec\u5634\u524d\u9762\u6447\u6765\u6447\u53bb\u7684\u65f6\u5019\uff0c\u5c31\u662f\u5200\u67b6\u7684\u8116\u5b50\u4e0a\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u4e3a\u4ed6\u62fc\u4e00\u62fc\u7684\u3002 \u3000\u3000\u591a\u81c2\u4eba\u718a\u5f3a\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u670b\u53cb\u662f\u4f55\u65b9\u9ad8\u4eba\u2014\u2014\u201d\u54ea\u77e5\u8fd9\u94f6\u886b\u6587\u58eb\u4f3c\u4e4e\u6839\u672c\u4e0d\u613f\u610f\u591a \u5e9f\u8bdd\uff0c\u51b7\u53f1\u4e00\u58f0\uff0c\u622a\u4f4f\u4ed6\u8bdd\u9053\uff1a\u201c\u4f60\u4eec\u6eda\u4e0d\u6eda\uff1f\u201d \u3000\u3000\u91d1\u9762\u97e6\u9640\u9762\u5bd2\u5982\u6c34\uff0c\u53cc\u7709\u5fae\u6311\uff0c\u4e5f\u5389\u53f1\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u5356\u7684\u90a3\u95e8\u5b50\u72c2\uff0c\u4f60\u51ed\u4ec0\u4e48\u6562\u5728\u6211 \u91d1\u9762\u97e6\u9640\u9762\u524d\u8bf4\u8fd9\u79cd\u72c2\u8bdd-----\u201d\u90a3\u591a\u81c2\u4eba\u718a\u4e00\u542c\u91d1\u9762\u97e6\u9640\u5982\u6b64\u8bf4\uff0c\u4e5f\u4e0d\u7518\u793a\u5f31\uff0c\u4e00\u77aa\u773c\uff0c \u9053\uff1a\u201c\u4ed6\u4eec\u5356\u4e1c\u897f\uff0c\u6211\u4eec\u4e70\u4e1c\u897f\uff0c\u4f60\u7ba1\u5f97\u7740\u5417\uff1f\u201d \u3000\u3000\u90a3\u94f6\u886b\u6587\u58eb\u7a81\u5730\u4ef0\u5929\u5927\u7b11\u8d77\u6765\uff0c\u7b11\u58f0\u8d8a\u800c\u9ad8\u4ea2\u2014\u2014\u591a\u81c2\u4eba\u718a\u5fc3\u4e2d\u4e00\u9a87\uff0c\u4ed6\u8bc6\u8d27\u5f97\u5f88\uff0c\u51b2 \u4eba\u5bb6\u8fd9\u7b11\u58f0\u770b\u6765\uff0c\u5c31\u77e5\u9053\u8fd9\u4eba\u5185\u529f\u4e4b\u6df1\uff0c\u5df2\u662f\u4e0d\u53ef\u601d\u8bae\uff0c\u7edd\u5bf9\u5728\u81ea\u5df1\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u53cc\u7709\u6697\u76b1\uff0c\u76ee\u5149\u4e2d\u7a81\u7136\u9732\u51fa\u6740\u673a\u6765\uff0c\u7a81\u5730\u53cc\u624b\u9f50\u626c\uff0c\u5341\u6570\u70b9\u5bd2\u661f\u66b4\u5c04\u800c\u51fa\uff0c\u80a5\u80d6 \u7684\u8eab\u8eaf\u5feb\u5982\u6d41\u661f\uff0c\u63a0\u5411\u8e72\u5728\u5730\u4e0a\u7684\u88f4\u73cf\u3002 \u3000\u3000\u90a3\u94f6\u886b\u6587\u58eb\u7b11\u58f0\u672a\u4f4f\uff0c\u5bf9\u8fd9\u4ee5\u6697\u5668\u7684\u6b79\u6bd2\u95fb\u540d\u5929\u4e0b\u7684\u591a\u81c2\u4eba\u718a\u53d1\u6765\u7684\u5341\u51e0\u70b9\u5bd2\u661f\uff0c\u4f3c\u4e4e \u8fde\u773c\u89d2\u90fd\u6ca1\u6709\u779f\u4e00\u4e0b\u3002 \u3000\u3000\u5b59\u658c\u7236\u5973\u5374\u4e0d\u7981\u60ca\u5f97\u5524\u51fa\u58f0\u6765\uff0c\u91d1\u9762\u97e6\u9640\u76ee\u5149\u95ea\u52a8\u5904\uff0c\u5374\u63a0\u5411\u90a3\u51c6\u5907\u5148\u5c06\u201c\u6d77\u5929\u79d8\u7b08\u201d \u62a2\u5230\u624b\u91cc\u7684\u591a\u81c2\u4eba\u718a\u3002\u201c\u88f4\u73cf\u5927\u60ca\u4e4b\u4e0b\uff0c\u62b1\u7740\u4e24\u672c\u4e66\u5f80\u5730\u4e0b\u4e00\u6eda\uff0c\u4ed6\u6b66\u529f\u867d\u4e0d\u9ad8\u3001\u4f46\u8eab\u624b\u5230 \u5e95\u6bd4\u5e38\u4eba\u654f\u6377\u5f97\u591a\uff0c\u521a\u521a\u6eda\u5230\u684c\u4e0b\u3002\u90a3\u8fb9\u201d\u7830\u7136\u201c\u4e00\u58f0\uff0c\u63a5\u7740\u201d\u591a\u81c2\u4eba\u718a\u201c\u95f7\u54fc\u4e86\u4e00\u58f0\uff0c\u539f \u6765\u4ed6\u548c\u91d1\u9762\u97e6\u9640\u5df2\u5bf9\u4e86\u4e00\u638c\uff0c\u4ed6\u5374\u4e0d\u662f\u8fd9\u4ee5\u638c\u529b\u89c1\u957f\u7684\u91d1\u9762\u97e6\u9640\u7684\u654c\u624b\uff0c\u4e24\u638c\u76f8\u4ea4\u4e4b\u4e0b\uff0c\u4ed6 \u7981\u4e0d\u4f4f\u88ab\u9707\u5f97\u5411\u540e\u8fde\u9000\u51fa\u597d\u8fdc\uff0c\u5589\u5934\u4e00\u751c\uff0c\u80f8\u4e2d\u4e00\u70ed\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u5df2\u53d7\u4e86\u91cd\u4f24\u3002\u8fd9\u591a\u81c2\u4eba\u718a \u84e6\u5730\u53d1\u51fa\u6697\u5668\uff0c\u91d1\u9762\u97e6\u9640\u51b7\u53f1\u6325\u638c\uff0c\u5b59\u658c\u60ca\u9a87\u4e4b\u4e0b\uff0c\u80be\u5230\u4ed6\u4e24\u4eba\u5bf9\u638c\u4e4b\u540e\uff0c\u4e24\u6761\u4eba\u5f71\u4fbf\u500f\u7136 \u5206\u51fa\u3002\u4ed6\u8fd9\u65f6\u624d\u60f3\u8d77\u591a\u81c2\u4eba\u718a\u53d1\u51fa\u7684\u6697\u5668\uff0c\u624d\u8d76\u7d27\u53bb\u770b\u90a3\u94f6\u886b\u7684\u9752\u5e74\u6587\u58eb\uff0c\u53ea\u89c1\u4ed6\u7adf\u4ecd\u662f\u505a \u7136\u5353\u7acb\u5728\u539f\u5730\uff0c\u4e00\u4ed8\u6f47\u6d12\u7684\u6837\u5b50\uff0c\u591a\u81c2\u4eba\u718a\u84c4\u529b\u800c\u53d1\u7684\u5341\u51e0\u4ef6\u6697\u5668\uff0c\u7adf\u65e0\u5f71\u65e0\u8e2a\uff0c\u4e0d\u77e5\u5230\u54ea \u91cc\u53bb\u4e86\u3002\u8fd9\u79cd\u624b\u6cd5\uff0c\u7b80\u76f4\u9a87\u4eba\u542c\u95fb\uff0c\u591a\u81c2\u4eba\u718a\u767e\u5fd9\u4e4b\u4e2d\u6109\u773c\u4e00\u77a5\uff0c\u4e5f\u770b\u5230\u8fd9\u79cd\u60c5\u5f62\u4e86\uff0c\u4ed6\u8fd9 \u79cd\u8001\u6c5f\u6e56\u7acb\u523b\u5c31\u53d1\u89c9\u51fa\u81ea\u5df1\u7684\u60c5\u5f62\u4e0d\u5999\u3002\u8fd9\u94f6\u886b\u6587\u58eb\u7684\u6b66\u529f\uff0c\u7adf\u662f\u4e0d\u53ef\u601d\u8bae\uff0c\u518d\u52a0\u4e0a\u5df2\u7ecf\u548c \u81ea\u5df1\u53cd\u8138\u6210\u4ec7\u7684\u91d1\u9762\u97e6\u9640\uff0c\u66f4\u4f55\u51b5\u81ea\u5df1\u6b64\u523b\u5df2\u53d7\u4e86\u6781\u91cd\u7684\u9707\u4f24\u3002\u591a\u81c2\u4eba\u718a\u95ea\u7535\u4e4b\u95f4\uff0c\u5df2\u63a8\u65ad \u51fa\u81ea\u5df1\u6b64\u523b\u552f\u4e00\u53ef\u8d70\u7684\u8def\uff0c\u4fbf\u662f\u8d81\u65e9\u6e9c\u4e4b\u5927\u5409\uff0c\u7559\u5728\u8fd9\u91cc\uff0c\u4e66\u662f\u5f97\u4e0d\u5230\uff0c\u547d\u8fd8\u5f97\u8d54\u4e0a\u3002\u4ed6\u5728 \u6c5f\u6e56\u4e2d\u7ffb\u6eda\u8fd9\u4e48\u591a\u5e74\uff0c\u7ed3\u4e86\u8fd9\u4e48\u591a\u4ec7\u5bb6\uff0c\u8fd8\u80fd\u4e0d\u6b7b\uff0c\u7531\u6b64\u53ef\u77e5\u4ed6\u4e34\u4e8b\u7684\u5224\u65ad\uff0c\u81ea\u6709\u8fc7\u4eba\u4e4b \u5904\uff0c\u6b63\u662f\u5f53\u673a\u7acb\u65ad\u7684\u89d2\u8272\u3002\u5fc3\u5ff5\u4e00\u52a8\u4e4b\u4e0b\uff0c\u4ed6\u518d\u4e0d\u8fdf\u7591\uff0c\u731b\u4e00\u62e7\u6b65\uff0c\u53cc\u8db3\u4e00\u987f\uff0c\u5237\u5730\u659c\u7a9c\u51fa \u53bb\uff0c\u671d\u8fd9\u8336\u68da\u540e\u9762\u7684\u8352\u91ce\u63a0\u53bb\u3002\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u8fd9\u4e45\u95ef\u6c5f\u6e56\u7684\u5de8\u76d7\uff0c\u7adf\u7136\u5728\u8eab\u5f62\u5c55\u52a8\u65f6\uff0c\u8fd8 \u53cd\u624b\u4e00\u6325\uff0c\u7535\u4e5f\u4f3c\u5730\u6253\u51fa\u5341\u6570\u70b9\u5bd2\u661f\u6765\uff0c\u5206\u88ad\u5404\u4eba\uff0c\u8fd9\u4efd\u8001\u5230\u3001\u72e0\u8fa3\u3001\u5978\u72e1\uff0c\u7684\u786e\u4e0d\u6127\u662f\u5728 \u6b66\u6797\u4e2d\u4e45\u8457\u51f6\u540d\u7684\u4eba\u7269\u3002\u81ea\u4ece\u4ed6\u53d1\u6697\u5668\uff0c\u593a\u79d8\u7b08\uff0c\u548c\u91d1\u9762\u97e6\u9640\u5bf9\u638c\uff0c\u53d7\u4f24\u4e00\u76f4\u5230\u6b64\u523b\uff0c\u7b14\u4e0b \u5199\u6765\u867d\u6162\uff0c\u7136\u800c\u5728\u5f53\u65f6\u5374\u4ec5\u662f\u5728\u4eba\u4eec\u970e\u773c\u4e4b\u4e2d\u5b8c\u6210\u7684\uff0c\u8fdc\u8fdc\u7ad9\u5728\u65c1\u8fb9\u7684\u90a3\u9762\u5982\u6b7b\u7070\u7684\u5e97\u4f19\uff0c \u751a\u81f3\u6839\u672c\u6ca1\u6709\u770b\u6e05\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u6765\u3002\u4f46\u662f\u90a3\u505a\u7136\u5353\u7acb\u7684\u94f6\u886b\u6587\u58eb\uff0c\u51b7\u7b11\u58f0\u4e2d\uff0c\u8eab\u5f62\u500f\u7136\u800c \u52a8\uff0c\u5c31\u50cf\u4e00\u6761\u94f6\u9f99\u4f3c\u7684\uff0c\u5728\u7a7a\u4e2d\u5fae\u4e00\u76d8\u65cb\u2014\u2014\u591a\u81c2\u4eba\u718a\u4e34\u5371\u9003\u547d\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u5206\u5411\u53d1\u51fa\u501f\u4ee5 \u4fdd\u8eab\u7684\u5341\u51e0\u9053\u6697\u5668\uff0c\u7adf\u5728\u4ed6\u8fd9\u5fae\u4e00\u76d8\u65cb\u4e4b\u4e0b\uff0c\u5168\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u65e0\u5f71\u65e0\u8e2a\u3002\u8fd9\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\u7684 \u94f6\u886b\u6587\u58eb\uff0c\u957f\u8896\u6325\u52a8\u5904\uff0c\u500f\u7136\u53c8\u662f\u4e00\u58f0\u8f7b\u5578\uff0c\u8f6c\u6298\u7a7a\u4e2d\u7684\u8eab\u8eaf\uff0c\u7adf\u7a81\u53c9\u51ed\u7a7a\u62d4\u8d77\u6570\u5c3a\uff0c\u7136\u540e \u5f53\u5934\u671d\u91d1\u9762\u97e6\u9640\u51fb\u4e0b\u3002\u8fd9\u65f6\u7d20\u6765\u72c2\u50b2\u7684\u91d1\u9762\u97e6\u9640\uff0c\u4e5f\u88ab\u8fd9\u94f6\u886b\u6587\u58eb\u7684\u60ca\u4eba\u8eab\u6cd5\uff0c\u9a87\u5f97\u9762\u5982\u571f \u8272\uff0c\u6b63\u5f85\u4e5f\u5b66\u591a\u81c2\u4eba\u718a\u4e00\u6837\uff0c\u8d81\u65e9\u6e9c\u4e4b\u5927\u5409\u3002\u54ea\u77e5\u4f46\u95fb\u4e00\u58f0\u8f7b\u5578\uff0c\u90a3\u6761\u94f6\u8272\u7684\u4eba\u5f71\uff0c\u5df2\u5e26\u7740 \u4e00\u79cd\u81ea\u5df1\u4ece\u6ca1\u7ecf\u5386\u8fc7\u7684\u5f3a\u52b2\u638c\u98ce\uff0c\u5f53\u65f6\u671d\u81ea\u5df1\u538b\u4e86\u4e0b\u6765\u2014\u2014\u6f2b\u5929\u638c\u98ce\u4e4b\u4e2d\uff0c\u4ed6\u6839\u672c\u65e0\u6cd5\u5206\u8fa8 \u51fa\u4eba\u5bb6\u5411\u81ea\u5df1\u51fa\u62db\u7684\u90e8\u4f4d\uff0c\u800c\u4e14\u81ea\u5df1\u88ab\u8fd9\u6837\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\u7684\u638c\u98ce\u4e00\u538b\uff0c\u7adf\u597d\u50cf\u6c14\u90fd\u900f \u4e0d\u8fc7\u6765\u3002\u8fd9\u7d20\u79f0\u201d\u786c\u624b\u201c\u7684\u51f6\u4eba\uff0c\u6b64\u523b\u975e\u4f46\u4e0d\u80fd\u8fd8\u624b\uff0c\u7ade\u8fde\u8eb2\u90fd\u6ca1\u6709\u529e\u6cd5\u8eb2\uff0c\u53ea\u89c9\u773c\u524d\u4e00 \u9ed1\uff0c\u5f53\u80f8\u5df2\u7740\u4e86\u4e00\u638c\u3002\u76ee\u77aa\u53e3\u5446\u7684\u5b59\u658c\u7236\u5973\uff0c\u53ea\u89c9\u5f97\u6f2b\u5929\u94f6\u886b\u98de\u821e\u95f4\uff0c\u4e00\u58f0\u8f7b\u5578\uff0c\u4e00\u58f0\u60e8 \u547c\uff0c\u90a3\u6761\u94f6\u9f99\u822c\u7684\u4eba\u5f71\uff0c\u5df2\u5411\u591a\u81c2\u4eba\u718a\u9003\u8d70\u7684\u65b9\u5411\u7535\u5c04\u800c\u53bb\u3002\u518d\u4e00\u770b\uff0c\u90a3\u65b9\u624d\u67b6\u505a\u4e0d\u53ef\u4e00\u4e16 \u7684\u91d1\u9762\u97e6\u9640\uff0c\u6b64\u523b\u5df2\u5012\u5728\u5730\u4e0a\uff0c\u4e0d\u7528\u7ec6\u770b\uff0c\u5b59\u658c\u5c31\u5df2\u77e5\u9053\u8fd9\u6a2a\u884c\u4e00\u65f6\u7684\u72ec\u884c\u5de8\u76d7\u6b64\u523b\u5df2\u7ecf\u4e27 \u547d\u3002\u8fd9\u94f6\u886b\u6587\u58eb\u7684\u8eab\u624b\uff0c\u82e5\u975e\u4eb2\u76ee\u6240\u89c1\uff0c\u7b80\u76f4\u5c31\u4eca\u4eba\u96be\u4ee5\u7f6e\u4fe1\u3002\u4e94\u864e\u65ad\u9b42\u5200\u5b59\u658c\uff0c\u6614\u5e74\u672c\u662f \u4e2a\u9887\u4e3a\u5e72\u7ec3\u7684\u9556\u5ba2\uff0c\u6b66\u529f\u867d\u4e0d\u600e\u7684\u51fa\u8272\uff0c\u4f46\u773c\u76ae\u4e4b\u6742\uff0c\u81ea\u662f\u4e0d\u5728\u8bdd\u4e0b\uff0c\u4f46\u662f\u76f4\u5230\u4eca\u5929\uff0c\u4ed6\u624d \u7b97\u5f00\u4e86\u773c\u754c\uff0c\u77e5\u9053\u82b8\u82b8\u6b66\u6797\u4e4b\u4e2d\uff0c\u771f\u5730\u6709\u7740\u8fd9\u79cd\u5f02\u4eba\u3002\u4ed6\u957f\u53f9\u4e00\u751f\uff0c\u6115\u4e86\u534a\u664c\uff0c\u8111\u6d77\u4e4b\u4e2d\u4e71 \u7eb7\u7eb7\u7684\uff0c\u6574\u7406\u4e0d\u51fa\u4e00\u4e2a\u5934\u7eea\u6765\u3002\u5b59\u9526\u5e73\u81ea\u4e5f\u82b1\u5bb9\u5931\u8272\uff0c\u6d51\u8eab\u6218\u6817\uff0c\u90a3\u5e97\u4f19\u66f4\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1 \u7684\u773c\u775b\uff0c\u8fde\u53eb\u90fd\u53eb\u4e0d\u51fa\u58f0\u6765\u3002\u8fd9\u5176\u4e2d\u5230\u5e95\u5b59\u658c\u662f\u8001\u6c5f\u6e56\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u5730\u4e0a\u5012\u7740\u4e00\u5177\u6b7b\u5c38\uff0c\u8fd9 \u5bb6\u8336\u68da\u8fd8\u662f\u5728\u5b98\u9053\u4e0a\uff0c\u6b64\u523b\u5929\u5df2\u5927\u4eae\uff0c\u884c\u4eba\u9a6c\u4e0a\u5c31\u8981\u6765\u5f97\u591a\u4e86\u3002\u8fd9\u65f6\uff0c\u4ed6\u4e5f\u60f3\u8d77\u88f4\u73cf\u548c\u90a3\u4e24 \u672c\u5df2\u4f7f\u4e24\u4e2a\u6b66\u6797\u5de8\u76d7\u4e27\u547d\u7684\u4e66\u6765\u3002\u4e8e\u662f\u4ed6\u5411\u4ed6\u5973\u513f\u4f4e\u53f1\u4e00\u58f0\uff1a\u201c\u5e73\u513f\uff0c\u6536\u62fe\u4e1c\u897f\uff0c\u5feb\u8d70\u3002\u201d \u800c\u8fd9\u65f6\u88f4\u73cf\u5df2\u4ece\u684c\u4e0b\u94bb\u4e86\u51fa\u6765\uff0c\u624b\u91cc\u7684\u90a3\u4e24\u672c\u4e66\uff0c\u5df2\u7ecf\u7ffb\u4e86\u5f00\u6765\u3002 \u3000\u3000\u800c\u4ed6\u9762\u4e0a\u6b64\u523b\u7adf\u7136\u6ee1\u9732\u559c\u8272\uff0c\u5b59\u658c\u76ee\u5149\u5728\u4ed6\u8138\u4e0a\u4e00\u8f6c\uff0c\u5c31\u77e5\u9053\u8fd9\u5b69\u4e86\u5fc5\u5df2\u53d1\u73b0\u4e86\u6b64\u4e66\u7684 \u79d8\u5bc6\u3002 \u3000\u3000\u539f\u6765\u88f4\u73cf\u65e2\u804b\u4e14\u54d1\uff0c\u4e00\u94bb\u5230\u684c\u4e0b\u540e\uff0c\u7adf\u4efb\u4e8b\u4e0d\u7ba1\uff0c\u5148\u5c06\u8fd9\u4e24\u672c\u4e66\u7ffb\u4e86\u4e00\u672c\uff0c\u4ed6\u9a87\u7136\u53d1 \u73b0\uff0c\u8fd9\u4e24\u672c\u4e66\u4e0a\u6240\u5199\u7684\u4f55\u7136\u5168\u662f\u6b66\u529f\u4fee\u4e3a\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u5b59\u658c\u53cc\u7709\u7d27\u76b1\uff0c\u77e5\u9053\u6b64\u65f6\u81ea\u5df1\u975e\u8d70\u4e0d\u53ef\uff0c\u4f46\u662f\u5f80\u4f55\u5904\u53bb\u5462\uff1f \u3000\u3000\u4ed6\u5fc3\u4e2d\u53c8\u6781\u5feb\u5730\u8f6c\u4e86\u4e24\u8f6c\uff0c\u77e5\u9053\u90a3\u94f6\u886b\u6587\u58eb\u5c06\u8fd9\u4e24\u4e2a\u5de8\u76d7\u51fb\u6bd9\u7684\u76ee\u7684\uff0c\u65e0\u5f02\u4e5f\u5728\u8fd9\u4e24\u672c \u79d8\u7b08\u8eab\u4e0a\uff0c\u4ee5\u4ed6\u8fd9\u79cd\u8eab\u624b\uff0c\u7247\u523b\u95f4\u4fbf\u53ef\u4ee5\u5c06\u591a\u81c2\u4eba\u718a\u51fb\u6bd9\uff0c\u800c\u90a3\u65f6\u4ed6\u52bf\u5fc5\u4f1a\u8fd4\u56de\u6765\u53d6\u8fd9\u4e24\u672c \u5f02\u4e66\u3002 \u3000\u3000\u4ed6\u4e00\u4f38\u624b\uff0c\u4ece\u88f4\u73cf\u624b\u4e0a\u63a5\u8fc7\u8fd9\u4e24\u672c\u4e66\u6765\uff0c\u201c\u6d77\u5929\u79d8\u7b08\u201d\u56db\u5b57\uff0c\u4fbf\u8d6b\u7136\u6620\u5165\u4ed6\u773c\u4e2d\uff0c\u4ed6\u5fc3 \u4e2d\u731b\u5730\u4e00\u4e00\u9635\u5de8\u8df3\uff0c\u7adf\u7981\u4e0d\u4f4f\u8d2a\u5fc3\u5927\u8d77\u3002 \u3000\u3000\u4e94\u864e\u65ad\u9b42\u5200\u6614\u5e74\u8d70\u9556\u65f6\uff0c\u66fe\u5c06\u6c5f\u5357\u5e2e\u532a\u201c\u4e09\u715e\u4e94\u9738\u201d\u4e2d\u7684\u4e8c\u715e\u4f24\u5728\u624b\u4e0b\uff0c\u4ece\u6b64\u4ed6\u5c31\u4e3a\u8fd9 \u4efd\u4ec7\u6068\u800c\u533f\u8d77\u6765\uff0c\u4e1c\u9003\u897f\u8eb2\uff0c\u5c31\u50cf\u662f\u4e00\u53ea\u6c38\u8fdc\u89c1\u4e0d\u5f97\u5929\u5149\u7684\u8017\u5b50\uff0c\u5728\u9ed1\u6697\u4e2d\u9003\u7a9c\u7740\u3002 \u3000\u3000\u800c\u6b64\u523b\uff0c\u4ed6\u624b\u91cc\u62ff\u7740\u7684\u4e1c\u897f\uff0c\u5374\u53ef\u4ee5\u4f7f\u8fd9\u4e9b\u5b8c\u5168\u6539\u89c2\u8fc7\u6765\uff0c\u53ea\u8981\u4ed6\u5f97\u7740\u8fd9\u4e66\u4e0a\u6240\u8bb0\u8f7d \u7684\uff0c\u4ed6\u5c31\u53ef\u4ee5\u6c38\u8fdc\u4e0d\u518d\u754f\u60e7\u4efb\u4f55\u4eba\u3002 \u3000\u3000\u4ed6\u5634\u89d2\u7efd\u5f00\u4e00\u4e1d\u7b11\u5bb9\uff0c\u5fc3\u4e0b\u518d\u4e0d\u8fdf\u7591\uff0c\u4e00\u9762\u559d\u9053\uff1a\u201c\u5e73\u513f\uff0c\u5feb\u8d70\uff01\u201d\u4e00\u9762\u62c9\u7740\u88f4\u73cf\uff0c\u5954 \u51fa\u8fd9\u8336\u68da\uff0c\u8df3\u4e0a\u65b9\u624d\u591a\u81c2\u4eba\u718a\u548c\u91d1\u9762\u97e6\u9640\u9a91\u6765\u7684\u4e24\u5339\u5065\u9a6c\uff0c\u5148\u5728\u4ed6\u5973\u513f\u6240\u9a91\u7684\u9a6c\u8179\u4e0a\uff0c\u5237\u5730 \u6253\u4e86\u4e00\u97ad\uff0c\u7136\u540e\u81ea\u5df1\u4e00\u5939\u9a6c\u8179\uff0c\u4e24\u5339\u9a6c\u4fbf\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u672a\uff0c\u53ef\u5927\u5927\u51fa\u4e86\u88f4\u73cf\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u88ab\u8fd9\u4e94\u864e\u65ad\u9b42\u5200\u5b59\u658c\u534a\u631f\u534a\u62b1\u5730\u6a2a\u6208\u5728\u9a6c\u524d\uff0c \u671b\u7740\u8fd9\u201c\u5b59\u8001\u7239\u201d\u5df2\u5c06\u90a3\u4e24\u672c\u73b0\u5728\u4ed6\u5df2\u77e5\u9053\u4ef7\u503c\u7684\u5947\u4e66\uff0c\u7528\u53e6\u4e00\u53ea\u624b\u6396\u8fdb\u81ea\u5df1\u7684\u6000\u91cc\u3002 \u3000\u3000\u4ed6\u6709\u8bb8\u591a\u8bdd\u60f3\u95ee\uff0c\u4f46\u662f\u5374\u95ee\u4e0d\u51fa\u6765\uff0c\u4ed6\u6697\u6697\u6012\u6068\u81ea\u5df1\uff0c\u4e3a\u4ec0\u4e48\u81ea\u5df1\u7684\u547d\u8fd0\u5374\u8981\u8ba9\u4eba\u5bb6\u6765 \u6446\u5e03\uff0c\u81ea\u5df1\u751a\u81f3\u8fde\u4e00\u4e9b\u53cd\u6297\u7684\u529b\u91cf\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u7eb5\u7136\u5df2\u7ecf\u4e60\u60ef\u4e86\u88ab\u5c48\u8fb1\uff0c\u4f46\u6b64\u523b\u5fc3\u80f8\u4ecd\u4e0d\u7981\u60b2\u6ca7\u54fd\u5835\u3002 \u3000\u3000\u6b64\u523b\u5929\u867d\u5927\u4eae\uff0c\u4f46\u5b98\u9053\u4e0a\u4ecd\u5c11\u884c\u4eba\uff0c\u8fd9\u4e24\u5339\u9a6c\u653e\u8f94\u6025\u5954\uff0c\u9a6c\u8e44\u540e\u626c\u8d77\u7684\u6c99\u5c18\uff0c\u6709\u5982\u4e00\u6761 \u7070\u9f99\u3002 \u3000\u3000\u5b59\u9526\u5e73\u672c\u751a\u5584\u9a91\uff0c\u65b9\u624d\u6240\u9a91\u4e4b\u9a6c\u88ab\u5176\u7236\u5288\u4e86\u4e00\u638c\uff0c\u6b64\u523b\u8fd9\u5339\u9a6c\u4ecd\u8d1f\u75db\u6025\u7a9c\uff0c\u5979\u6839\u672c\u65e0\u6cd5 \u63a7\u5236\uff0c\u867d\u4ecd\u4e0d\u65f6\u626d\u5934\u56de\u671b\uff0c\u4f46\u9a6c\u884c\u592a\u6025\uff0c\u867d\u5c3d\u529b\u626d\uff0c\u5374\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\u6765\uff0c\u9669\u4e9b\u81ea\u5df1\u4e5f\u56e0\u4e4b\u5760 \u9a6c\u3002 \u3000\u3000\u8fd9\u4e24\u5339\u9a6c\u90fd\u662f\u5343\u4e2d\u9009\u4e00\u7684\u826f\u9a79\uff0c\u867d\u7ecf\u957f\u7a0b\u5954\u6765\uff0c\u4f46\u4e00\u70b9\u4e5f\u663e\u4e0d\u51fa\u75b2\u52b3\uff0c\u5065\u8e44\u7ffb\u98de\uff0c\u9a6c\u884c \u5982\u9f99\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u5954\u51fa\u8001\u8fdc\u3002 \u3000\u3000\u4e94\u864e\u65ad\u9b42\u5200\u5b59\u658c\u4e5f\u4e0d\u65f6\u626d\u5934\u56de\u671b\uff0c\u770b\u5230\u80cc\u540e\u6839\u672c\u6ca1\u6709\u4eba\u8ffd\u6765\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u4e24\u6761\u817f\u5230\u5e95\u8dd1 \u4e0d\u8fc7\u56db\u6761\u817f\u7684\u3002\u7528\u5de6\u624b\u629a\u4e86\u629a\u6000\u4e2d\u7684\u4e24\u672c\u201c\u6d77\u5929\u79d8\u7b08\u201d\uff0c\u770b\u4e86\u770b\u53f3\u624b\u6240\u6396\u6301\u7740\u7684\u88f4\u73cf\uff0c\u8d2a\u5ff5 \u4e00\u751f\uff0c\u826f\u5fc3\u4fbf\u6cef\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u8d77\u521d\u6536\u7559\u88f4\u73cf\uff0c\u867d\u4e5f\u6709\u4e9b\u607b\u9690\u4e4b\u5fc3\uff0c\u4f46\u4e5f\u662f\u56e0\u4e3a\u81ea\u5df1\u5df2\u9700\u8981\u8fd9\u4e48\u4e00\u4e2a\u53ea\u505a\u4e8b\u4e0d\u62ff \u94b1\u7684\u5e2e\u624b\uff0c\u5e76\u6ca1\u6709\u4ec0\u4e48\u771f\u6b63\u7684\u5584\u610f\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5ff5\u5934\u6570\u8f6c\uff0c\u5634\u89d2\u5fae\u5fae\u72de\u7b11\u4e00\u4e0b\uff0c\u671b\u4e86\u5954\u5728\u524d\u9762\u7684\u5b59\u9526\u5e73\u4e00\u773c\uff0c\u500f\u5730\u5c06\u53f3\u624b\u5f80\u5916\u4e00 \u63a8\u2014\u2014\u5b59\u9526\u5e73\u672c\u591a\u591a\u5c11\u5c11\u731c\u7740\u4e00\u4e9b\u5979\u7239\u7239\u7684\u7528\u610f\uff0c\u4f46\u662f\u5979\u5374\u7edd\u672a\u60f3\u5230\u81ea\u5df1\u7684\u7239\u7239\u8fde\u4e00\u4e2a\u5b64\u82e6 \u4f36\u4ec3\u7684\u6b8b\u5e9f\u5c11\u5e74\u90fd\u5bb9\u4e0d\u5f97\u3002 \u3000\u3000\u8e44\u58f0\u7eb7\u6c93\u4e4b\u4e2d\uff0c\u5979\u53ea\u542c\u5230\u540e\u9762\u4f3c\u4e4e\u6709\u91cd\u7269\u5760\u5730\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5979\u8fde\u5fd9\u626d\u5934\u53bb\u770b\uff0c\u4f46\u662f\u81ea\u5df1\u6240\u4e58\u7684\u9a6c\u540e\uff0c\u5374\u53c8\u88ab\u5288\u4e86\u4e00\u638c\uff0c\u8fd9\u5339\u9a6c\u65e7\u75db\u672a\u6108\uff0c\u65b0\u4f24\u53c8 \u8d77\uff0c\u4ef0\u9996\u4e00\u58f0\u957f\u5636\uff0c\u594b\u8e44\u524d\u5954\uff0c\u5176\u6025\u5982\u706b\u3002 \u3000\u3000\u4f46\u662f\u5b59\u9526\u5e73\u5374\u5df2\u770b\u5230\u5979\u7239\u7239\u7684\u9a6c\u4e0a\u5df2\u6ca1\u6709\u88f4\u73cf\u7684\u5f71\u5b50\u4e86\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u5979\u53c8\u8be5\u662f\u600e\u4e48\u4e00\u79cd\u5fc3\u60c5\u5462\uff1f \u3000\u3000\u53ea\u662f\uff0c\u8fd9\u5339\u9a6c\u5374\u4e0d\u77e5\u9053\u5979\u7684\u5fc3\u60c5\uff0c\u4e5f\u4e0d\u80af\u4e3a\u8fd9\u53ef\u601c\uff0c\u65e0\u52a9\uff0c\u82b3\u5fc3\u5df2\u5bf8\u65ad\u7684\u5c11\u5973\u505c\u7559\u4e00 \u523b\uff0c\u751a\u81f3\u6bd4\u5148\u524d\u5954\u9a70\u5f97\u66f4\u5feb\u4e86\u3002 \u3000\u3000\u8fd9\u6761\u7b14\u76f4\u7684\u5b98\u9053\u5728\u524d\u9762\u7565\u6709\u66f2\u6298\uff0c\u8fd9\u4e24\u5339\u9a6c\u4e5f\u632b\u773c\u5931\u53bb\u4e86\u8e2a\u8ff9\u3002 \u3000\u3000\u592a\u9633\uff0c\u4e5f\u50cf\u5f80\u5e38\u4e00\u6837\uff0c\u7f13\u7f13\u5730\uff0c\u4f46\u5374\u6709\u7740\u4e00\u5b9a\u7684\u89c4\u5f8b\u5347\u4e0a\u6765\uff0c\u7167\u4e0a\u4e86\u6811\u68a2\uff0c\u7167\u4e0a\u4e86\u5b98 \u9053\u3002 \u3000\u3000\u65b9\u624d\u4ed6\u88ab\u5b59\u658c\u4ece\u6025\u9a70\u7740\u7684\u9a6c\u4e0a\u7529\u4e0b\u6765\uff0c\u201c\u7830\u201d\u5730\uff0c\u5934\u649e\u5728\u575a\u786c\u7684\u77f3\u5b50\u8def\u4e0a\uff0c\u53c8\u7ffb\u4e86\u4e24\u4e2a \u7b4b\u6597\uff0c\u843d\u5728\u9053\u65c1\u7684\u4e1b\u751f\u8349\u77f3\u91cc\uff0c\u624d\u505c\u4e0b\u6765\uff0c\u800c\u8fd9\u5386\u5c3d\u60e8\u52ab\u7684\u5b64\u661f\uff0c\u81ea\u4e5f\u5931\u53bb\u4e86\u77e5\u89c9\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u60a0\u60a0\u5730\u9192\u8f6c\u4e86\u8fc7\u6765\u3002\u5f20\u76ee\u4f46\u89c9\u9633\u5149\u523a\u76ee\uff0c\u4e0b\u610f\u8bc6\u4ed6\u60f3\u4f38\u624b\u63c9\u7740\u773c\u775b\uff0c\u4f46\u56db\u811a\u5374 \u50cf\u5df2\u88ab\u6454\u6563\u4e86\u4f3c\u7684\uff0c\u4e00\u52a8\u5f39\u5c31\u53d1\u75db\u3002 \u3000\u3000\u4ed6\u53ea\u5f97\u52c9\u5f3a\u626d\u5934\uff0c\u907f\u5f00\u7531\u4e0a\u9762\u7167\u5230\u4ed6\u8138\u4e0a\u7684\u9633\u5149\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u53ea\u89c9\u5f97\u8111\u6d77\u4e2d\u4e00\u7247\u6df7 \u9968\uff0c\u4ec0\u4e48\u4e8b\u90fd\u60f3\u4e0d\u8d77\u6765\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u613f\u60f3\u3002 \u3000\u3000\u81ea\u4ece\u4ed6\u6709\u77e5\u8bc6\u90a3\u4e00\u5929\u5f00\u59cb\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u6240\u906d\u53d7\u7684\uff0c\u4f3c\u4e4e\u5374\u53ea\u6709\u4e0d\u5e78\uff0c\u4f46\u662f\u4ed6\u5374\u5e76\u4e0d\u6028 \u5929\u6068\u5730\uff0c\u66f4\u4e0d\u6028\u6068\u522b\u4eba\uff0c\u4ed6\u53ea\u662f\u6028\u6068\u81ea\u5df1\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u53ea\u602a\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e0d\u4e89\u6c14\uff0c\u4e3a\u4ec0\u4e48\u522b\u4eba\u80fd\u505a\u5230\u7684\u4e8b\u81ea\u5df1\u5374\u505a\u4e0d\u5230\uff0c\u4e8e\u662f\u4ed6\u53c8\u6028\u6068\u81ea\u5df1\u7684 \u611a\u8822\uff0c\u5bf9\u4e8e\u522b\u4eba\u6240\u65bd\u4e8e\u4ed6\u7684\u5c48\u8fb1\u548c\u4e0d\u5e73\uff0c\u4ed6\u5374\u53ea\u662f\u9ed8\u9ed8\u5730\u53bb\u627f\u53d7\u7740\uff0c\u53ea\u5e0c\u671b\u6709\u4e00\u5929\u80fd\u8ba9\u522b\u4eba \u770b\u5f97\u8d77\u3002 \u3000\u3000\u62a5\u590d\uff0c\u4ec7\u6068\uff0c\u8fd9\u4e9b\u5b57\u5728\u4ed6\u6765\u8bf4\u90fd\u662f\u90a3\u4e48\u751f\u758f\uff0c\u4ed6\u53ea\u8981\u522b\u4eba\u4e0d\u6765\u635f\u5bb3\u81ea\u5df1\uff0c\u4fbf\u5df2\u5fc3\u6ee1\u610f \u8db3\uff0c\u5bf9\u4e8e\u4ed6\u81ea\u5df1\uff0c\u5374\u7edd\u4e0d\u60f3\u53bb\u635f\u5bb3\u522b\u4eba\u3002 \u3000\u3000\u867d\u7136\u7ecf\u8fc7\u8fd9\u4e48\u591a\u65e5\u5b50\u7684\u78e8\u6298\uff0c\u8fd9\u4e48\u591a\u6b21\u51c4\u60e8\u7684\u906d\u9047\uff0c\u4ed6\u6e10\u6e10\u5df2\u77e5\u9053\u4e86\u4e9b\u4eba\u5fc3\u9669\u6076\uff0c\u4f46\u662f \u4ed6\u4ecd\u7136\u70ed\u7231\u7740\u4e16\u4eba\uff0c\u4e5f\u5e0c\u671b\u522b\u4eba\u80fd\u70ed\u7231\u81ea\u5df1\u3002 \u3000\u3000\u5bf9\u90a3\u201c\u5b59\u8001\u7239\u201d\uff0c\u88f4\u73cf\u5f53\u7136\u5df2\u77e5\u9053\u4ed6\u5c06\u81ea\u5df1\u63a8\u5728\u8def\u65c1\uff0c\u662f\u4e3a\u4e86\u90a3\u4e24\u672c\u4e66\u2014\u2014\u4ed6\u5e76\u4e0d\u662f\u7b28 \u4eba\uff0c\u4e86\u89e3\u5f97\u4e5f\u8bb8\u6bd4\u522b\u4eba\u90fd\u591a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u4e0d\u613f\u610f\u53bb\u8bb0\u4f4f\u8fd9\u4e9b\uff0c\u4ed6\u53ea\u613f\u610f\u8bb0\u4f4f\u4eba\u5bb6\u5bf9\u4ed6\u597d\u7684\u5730\u65b9\uff0c\u53ea\u613f\u610f\u8bb0\u4f4f\u201c\u5b59\u8001\u7239\u201d \u66fe\u7ecf\u6536\u7559\u8fc7\u4ed6\uff0c\u5e26\u4ed6\u7ecf\u5386\u8fc7\u4e00\u6bb5\u4ed6\u4ece\u672a\u7ecf\u5386\u7684\u751f\u6d3b\uff0c\u4f7f\u4ed6\u4eab\u53d7\u4e86\u4e00\u6bb5\u6709\u4eb2\u60c5\u7684\u751f\u6d3b\u2014\u2014\u90a3\u4e00 \u53cc\u660e\u4eae\u7684\u5927\u773c\u775b\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fd8\u611f\u6fc0\u4eba\u5bb6\u4e0d\u5c06\u81ea\u5df1\u6740\u6b7b\uff0c\u800c\u4ec5\u662f\u5c06\u81ea\u5df1\u63a8\u843d\u800c\u5df2\uff0c\u56e0\u4e3a\u4eba\u5bb6\u5047\u5982\u8981\u60f3\u6740\u4ed6\uff0c\u90a3 \u4e5f\u662f\u4e00\u6837\u5730\u975e\u5e38\u5bb9\u6613\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u9759\u9759\u5730\u5012\u5367\u5728\u8349\u5730\u4e0a\uff0c\u6709\u9a6c\u8e44\u7684\u58f0\u97f3\u4ece\u5b98\u9053\u4e0a\u5954\u8fc7\uff0c\u4ece\u5730\u5e95\u4f20\u8fc7\u6765\uff0c\u4f46\u662f\u4ed6\u5374\u4e00 \u70b9\u4e5f\u542c\u4e0d\u5230\u3002 \u3000\u3000\u540c\u65f6\u4ed6\u89c9\u5f97\u975e\u5e38\u5b81\u9759\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5df2\u4e0d\u5c5e\u4e16\u4eba\uff0c\u4e16\u4eba\u66f4\u4e0d\u5c5e\u4ed6\uff0c\u5929\u5730\u867d\u5927\uff0c\u4f46\u5374\u4eff \u4f5b\u53ea\u5269\u4e0b\u4ed6\u81ea\u5df1\u4e00\u4eba\uff0c\u65e0\u4eba\u7406\u4f1a\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u79cd\u591a\u4e48\u5bc2\u5bde\u7684\u611f\u89c9\uff0c\u4ed6\u4e0d\u7981\u6697\u6697\u611f\u6fc0\u4e0a\u82cd\uff0c\u8fd8\u8d50\u7ed9\u4ed6\u4e00\u53cc\u773c\u775b\uff0c\u8ba9\u4ed6\u80fd\u770b\u5230\u5927 \u5730\uff0c\u56e0\u4e3a\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u4ecd\u7136\u70ed\u7231\u7740\u751f\u547d\u2014\u2014\u5bf9\u4e8e\u4e00\u4e2a\u52c7\u6562\u7684\u4eba\u8bf4\u6765\uff0c\u751f\u547d\u662f\u6c38\u8fdc\u53ef\u7231\u7684\u3002 \u3000\u3000\u8349\u77f3\u95f4\u6709\u4e00\u6761\u86af\u8693\uff0c\u4ece\u5730\u4e0b\u94bb\u51fa\u6765\uff0c\u8815\u52a8\u7740\u8eab\u8eaf\uff0c\u6709\u4e00\u53ea\u8682\u8681\u722c\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u7adf\u5728\u4ed6\u8eab \u4e0a\u505c\u7559\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u88f4\u73cf\u4e0d\u7981\u6697\u4e2d\u5fae\u7b11\u4e00\u4e0b\uff0c\u4ed6\u77e5\u9053\u53ea\u8981\u8fd9\u6761\u86af\u8693\u7ffb\u4e2a\u8eab\uff0c\u90a3\u53ea\u8682\u8681\u4fbf\u5f97\u7acb\u523b\u88ab\u4ed6\u7529\u843d\uff0c\u751a \u81f3\u88ab\u4ed6\u538b\u5728\u4e0b\u9762\uff0c\u88f4\u73cf\u4e0d\u7981\u95ee\u81ea\u5df1\u3002 \u3000\u3000\u201c\u8fd9\u6761\u86af\u8693\u662f\u4e0d\u613f\u7ffb\u8eab\uff0c\u6291\u6216\u662f\u4e0d\u80fd\u7ffb\u8eab\uff0c\u8fd8\u662f\u5df2\u7ecf\u9ebb\u6728\u5230\u4e0d\u77e5\u9053\u8fd9\u53ea\u8682\u8681\u7684\u5b58\u5728\u3002\u201d \u3000\u3000\u53ef\u662f\u5728\u4ed6\u8fd9\u95ee\u9898\u8fd8\u6ca1\u6709\u5f97\u5230\u7b54\u6848\u7684\u65f6\u5019\uff0c\u90a3\u6761\u86af\u8693\u53c8\u94bb\u56de\u5730\u4e0b\u53bb\uff0c\u90a3\u53ea\u8682\u8681\u5374\u8fd8\u505c\u7559\u5728 \u5730\u9762\u4e0a\uff0c\u4f46\u662f\uff0c\u7a81\u7136\u2014\u2014\u5c31\u50cf\u4e00\u9635\u98ce\u6765\u65f6\u90a3\u6837\u7a81\u7136\uff0c\u4e00\u53ea\u811a\u7a81\u7136\u538b\u5230\u90a3\u53ea\u8682\u8681\u7684\u8eab\u4e0a\u2014\u2014\u90a3 \u662f\u4e00\u53cc\u7a7f\u7740\u7c89\u5e95\u6731\u5c65\u7684\u811a\uff0c\u968f\u7740\u90a3\u94f6\u7070\u957f\u886b\u7684\u4e0b\u6446\u8d6b\u7136\u53c8\u8fdb\u5165\u88f4\u73cf\u7684\u773c\u5e18\uff0c\u88f4\u73cf\u4e0d\u7528\u770b\uff0c\u5c31 \u77e5\u9053\u8fd9\u53cc\u811a\u662f\u5c5e\u4e8e\u4ec0\u4e48\u4eba\u7684\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4ecd\u7136\u5fcd\u4e0d\u4f4f\u6084\u6084\u626d\u56de\u5934\uff0c\u987a\u7740\u8fd9\u53cc\u811a\u5f80\u4e0a\u770b\uff0c\u4ecd\u7136\u662f\u94f6\u7070\u8272\u7684\u957f\u886b\uff0c\u843d\u62d3\u800c\u5028\u50b2 \u7684\u9762\u5b54\uff0c\u6f47\u6d12\u800c\u51b7\u6f20\u7684\u795e\u60c5\uff0c\u800c\u90a3\u4e00\u53cc\u51db\u7136\u5e26\u7740\u5bd2\u5149\u7684\u53cc\u773c\uff0c\u4e5f\u6b63\u5728\u671b\u7740\u88f4\u73cf\u3002 \u3000\u3000\u4ed6\u4e00\u4fef\u8eab\uff0c\u628a\u88f4\u73cf\u4ece\u5730\u4e0a\u62c9\u4e86\u8d77\u6765\uff0c\u968f\u5373\u653e\u5f00\u624b\uff0c\u88f4\u73cf\u867d\u7136\u88ab\u8fd9\u7a81\u6765\u4e00\u62c9\uff0c\u4f7f\u5f97\u672c\u5df2\u56e0 \u65b9\u624d\u90a3\u4e00\u8dcc\u800c\u6454\u5f97\u50cf\u6563\u4e86\u822c\u7684\u56db\u80a2\u66f4\u52a0\u75db\u695a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4ecd\u7136\u54ac\u7740\u7259\uff0c\u5f3a\u5fcd\u7740\u4f7f\u81ea\u5df1\u4e0d\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u662f\u56e0\u4e3a\u8fd9\u94f6\u886b\u4eba\u5634\u89d2\u6240\u5e26\u7684\u90a3\u4e00\u4efd\u8f7b\u8511\uff0c\u4f7f\u5f97\u4ed6\u5373\u4f7f\u5fcd\u53d7\u4e16\u95f4\u4efb\u4f55\u75db\u82e6\uff0c\u4e5f\u4e0d\u613f\u5728\u8fd9 \u4eba\u9762\u524d\u4e22\u8138\uff0c\u4ed6\u5b81\u613f\u88ab\u6b3a\u51cc\uff0c\u88ab\u8feb\u5bb3\uff0c\u4f46\u662f\u4ed6\u5374\u4e0d\u80fd\u5fcd\u53d7\u522b\u4eba\u7684\u8f7b\u89c6\uff0c\u4e0d\u80fd\u5fcd\u53d7\u522b\u4eba\u5c06\u4ed6\u770b \u6210\u4e2a\u65e0\u7528\u7684\u61e6\u592b\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u7ec8\u4e8e\u4e00\u62ac\u773c\u5c31\u80fd\u770b\u5230\u8fd9\u94f6\u886b\u4eba\u7684\u8138\u4e86\uff0c\u800c\u4e0d\u7528\u7531\u4e0b\u800c\u4ef0\u89c6\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u73b0\u5728\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u80fd\u591f\u9762\u5bf9\u9762\u5730\u548c\u8fd9\u4eba\u7ad9\u5728\u4e00\u8d77\uff0c\u73b0\u5728\u867d\u7136\u6709\u4e00\u53ea\u5343\u65a4\u94c1\u9524\u8981\u6253 \u5230\u4ed6\u7684\u5934\u4e0a\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u754f\u7f29\u5730\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u94f6\u886b\u4eba\u4e0a\u4e0a\u4e0b\u4e0b\u5730\u671d\u4ed6\u6253\u91cf\u7740\uff0c\u4ed6\u4e5f\u633a\u76f4\u4e86\u80f8\u819b\uff0c\u9762\u5bf9\u7740\u8fd9\u94f6\u886b\u4eba\u5b9b\u5982\u5229\u7bad\u7684\u76ee\u5149\uff0c \u4ed6\u65e0\u6240\u754f\u60e7\uff0c\u56e0\u4e3a\u4ed6\u6b64\u523b\u80f8\u4e2d\u5766\u8361\u3002 \u3000\u3000\u7136\u540e\u8fd9\u94f6\u886b\u4eba\u7a81\u5730\u4e00\u4f38\u624b\uff0c\u4fbf\u5df2\u6258\u4f4f\u4ed6\u7684\u624b\u8098\uff0c\u4ed6\u5c31\u89c9\u5f97\u81ea\u5df1\u7684\u8eab\u4e8e\u50cf\u662f\u7a81\u7136\u8f7b\u4e86\u5f88 \u591a\uff0c\u90a3\u94f6\u886b\u4eba\u4e00\u4e00\u8f6c\u8eab\uff0c\u4ed6\u7adf\u4e5f\u968f\u4e4b\u8f6c\u4e86\u4e2a\u65b9\u5411\u3002 \u3000\u3000\u90a3\u94f6\u886b\u4eba\u6f47\u6d12\u5730\u4e00\u8fc8\u6b65\uff0c\u4fbf\u5df2\u8de8\u5230\u8def\u4e0a\uff0c\u88f4\u73cf\u53ea\u89c9\u5f97\u81ea\u5df1\u7684\u8eab\u5b50\u98d8\u98d8\u8361\u8361\u5730\uff0c\u968f\u7740\u90a3\u4eba \u524d\u884c\uff0c\u5c31\u50cf\u662f\u81ea\u5df1\u7684\u8eab\u5b50\u5df2\u7ecf\u9644\u5728\u4eba\u5bb6\u8eab\u4e0a\uff0c\u81ea\u5df1\u7adf\u4e0d\u518d\u6709\u4e1d\u6beb\u63a7\u5236\u81ea\u5df1\u7684\u529b\u91cf\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u8fd9\u94f6\u886b\u4eba\u8981\u5e26\u81ea\u5df1\u5230\u54ea\u91cc\u53bb\uff0c\u4e5f\u4e0d\u77e5\u9053\u4eba\u5bb6\u5c06\u8981\u5bf9\u81ea\u5df1\u600e\u6837\uff0c\u4f46\u662f\u4ed6\u4ecd\u7136\u65e0\u6240 \u754f\u60e7\uff0c\u4ed6\u867d\u7136\u70ed\u7231\u751f\u547d\uff0c\u4f46\u5374\u4e0d\u754f\u6b7b\u4ea1\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4efb\u4f55\u4e00\u79cd\u6076\u52a3\u7684\u60c5\u51b5\u4e0b\uff0c\u4ed6\u53ea\u6709\u611f\u89c9\u5c48\u8fb1\uff0c\u800c\u4ece\u672a\u611f\u89c9\u8fc7\u754f\u60e7\uff0c\u4ed6\u4e0d\u77e5\u9053\u81ea\u5df1\u662f \u4e0d\u662f\u4e00\u4e2a\u4e50\u5929\u7684\u4eba\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5374\u77e5\u9053\u81ea\u5df1\u4ece\u672a\u7070\u5fc3\u8fc7\uff0c\u5728\u90a3\u72e0\u4eb5\u800c\u9ed1\u6697\u7684\u5c0f\u697c\u91cc\uff0c\u9762\u5bf9\u7740\u90a3\u8272\u60c5\u72c2\u7684\u80d6\u5b50\uff1b \u5728\u90a3\u8352\u51c9\u7684\u90ca\u5916\uff0c\u9762\u5bf9\u7740\u90a3\u4e00\u7fa4\u65e0\u8d56\u5c11\u5e74\uff1b\u5728\u5ba2\u6808\u7684\u5e97\u623f\u4e2d\uff0c\u9762\u5bf9\u7740\u201c\u51b7\u5927\u53d4\u201d\u7acb\u523b\u4fbf\u80fd\u5c06 \u81ea\u5df1\u5236\u6b7b\u7684\u624b\u638c\uff0c\u5728\u5c4b\u6a90\u4e0b\uff0c\u9762\u5bf9\u7740\u6765\u65e5\u7684\u7070\u9eef\u548c\u751f\u6d3b\u7684\u56f0\u82e6\u2014\u2014\u8fd9\u4e9b\u906d\u9047\uff0c\u867d\u7136\u51c4\u60e8\uff0c\u4f46 \u975e\u4f46\u6ca1\u6709\u4ee4\u4ed6\u7070\u5fc3\uff0c\u5931\u671b\uff0c\u53cd\u800c\u66f4\u6fc0\u8d77\u4e86\u4ed6\u751f\u547d\u7684\u52c7\u6c14\uff0c\u4ed6\u8981\u4e3a\u751f\u547d\u800c\u6323\u624e\uff0c\u4ed6\u66f4\u7edd\u672a\u56e0\u4e4b \u9893\u5e9f\u3002 \u3000\u3000\u6b64\u523b\uff0c\u50cf\u5f80\u5e38\u4e00\u6837\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u5c06\u6765\u964d\u4e34\u5230\u4ed6\u8eab\u4e0a\u7684\u662f\u4efb\u4f55\u4e00\u79cd\u906d\u9047\uff0c\u4ed6\u90fd\u6709\u4e00\u4efd\u52c7\u6c14 \u6765\u63a5\u53d7\uff0c\u90fd\u53ef\u4ee5\u51ed\u7740\u8fd9\u4efd\u52c7\u6c14\u6765\u6323\u624e\u7684\u3002 \u3000\u3000\u8f66\u9a6c\u751a\u591a\uff0c\u8fd9\u6761\u5b98\u9053\u672c\u662f\u901a\u8859\u8981\u9053\uff0c\u884c\u4eba\u770b\u5230\u88f4\u73cf\u548c\u8fd9\u7a7f\u7740\u94f6\u7070\u957f\u886b\u7684\u6587\u58eb\uff0c\u90fd\u4e0d\u7981\u6a2a \u7740\u773c\u775b\u6765\u770b\uff0c\u987b\u77e5\u7a7f\u7740\u8fd9\u79cd\u94f6\u7070\u957f\u886b\u7684\u4eba\u672c\u5c31\u6781\u5c11\uff0c\u518d\u52a0\u4e0a\u8fd9\u4eba\u795e\u60c5\u7684\u7279\u522b\uff0c\u522b\u4eba\u81ea\u7136\u96be\u514d \u6ce8\u610f\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u524d\u9762\u662f\u4e2a\u4e09\u5c94\u8def\u53e3\uff0c\u88f4\u73cf\u8eab\u4e0d\u7531\u5df1\u5730\u968f\u7740\u90a3\u94f6\u886b\u4eba\u8d70\u5230\u53f3\u9762\u90a3\u6761\u8def\uff0c\u4ed6\u4e5f \u4e0d\u77e5\u9053\u8fd9\u6761\u8def\u662f\u901a\u5f80\u54ea\u91cc\u53bb\u7684\u3002 \u3000\u3000\u54ea\u77e5\u65b9\u5f80\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u90a3\u94f6\u886b\u4eba\u5ffd\u5730\u53c8\u9000\u4e86\u56de\u53bb\uff0c\u7ad9\u5728\u90a3\u4e09\u5c94\u8def\u53e3\uff0c\u7adf\u4e0d\u8d70\uff0c\u88f4\u73cf\u5fc3\u91cc \u5947\u602a\uff0c\u53ef\u53c8\u4e0d\u80fd\u95ee\u53e5\u8bdd\uff0c\u5077\u773c\u4e00\u770b\u90a3\u94f6\u886b\u4eba\u7684\u8138\u8272\uff0c\u4ecd\u7136\u662f\u5e26\u7740\u4ed6\u60ef\u6709\u7684\u90a3\u79cd\u51b7\u6f20\u4e0e\u8f7b\u8511\uff0c \u8fd9\u4efd\u51b7\u6f20\u4e0e\u8f7b\u8511\uff0c\u5c31\u50cf\u662f\u4e00\u5c42\u5bd2\u51b0\u4f3c\u7684\uff0c\u5c06\u4ed6\u4e00\u5207\u60c5\u611f\u90fd\u57cb\u85cf\u5728\u4e0b\u9762\u3002 \u3000\u3000\u88f4\u73cf\u4e0d\u7981\u6697\u95ee\u81ea\u5df1\u3002 \u3000\u3000\u201c\u4ed6\u96be\u9053\u662f\u6ca1\u6709\u60c5\u611f\u7684\u5417\uff1f\u2026\u2026\u5509\uff01\u6211\u82e5\u80fd\u50cf\u4ed6\u591a\u597d\uff0c\u5982\u679c\u6211\u4ec0\u4e48\u90fd\u4e0d\u53bb\u60f3\uff0c\u90a3\u4e48\u6211\u5c82 \u4e0d\u662f\u4efb\u4f55\u70e6\u607c\u90fd\u6ca1\u6709\u4e86\u5417\uff1f\u201d \u3000\u3000\u4ed6\u5230\u5e95\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u4e0d\u77e5\u9053\u6709\u4e9b\u4eba\u5916\u8868\u6108\u662f\u51b7\u6f20\uff0c\u5185\u5fc3\u7684\u70e6\u607c\u5374\u8d8a\u591a\u3002 \u3000\u3000\u8fd9\u94f6\u886b\u4eba\u671b\u4e5f\u4e0d\u671b\u88f4\u73cf\uff0c\u4e24\u773c\u4e0a\u7ffb\uff0c\u671b\u7740\u5929\u4e0a\uff0c\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u4e48\uff0c\u88f4\u73cf\u4e5f\u53ea\u5f97\u62ac\u5934\u4ef0 \u671b\uff0c\u53ea\u89c1\u82cd\u5929\u78a7\u84dd\uff0c\u767d\u4e91\u82cd\u72d7\uff0c\u98de\u8f6c\u5954\u6d41\u2014\u2014\u201c\u591a\u597d\u7684\u5929\u6c14\u2014\u2014\u201d\u88f4\u73cf\u7684\u601d\u6f6e\uff0c\u60a0\u60a0\u5730\u53c8\u98de \u4e86\u5f00\u53bb\uff0c\u98de\u5230\u8fdc\u8fdc\u5730\u65b9\uff0c\u98de\u5230\u4ed6\u4eec\u719f\u6089\u7684\u4eba\u4eec\u8eab\u4e0a\uff0c\u5c11\u5e74\uff0c\u5c11\u5e74\u65f6\u7684\u65e5\u5b50\u672c\u8be5\u591a\u4e48\u53ef\u7231\uff0c\u7136 \u800c\u88f4\u73cf\u2026\u2026 \u3000\u3000\u8fdc\u5904\u84e6\u5730\u60f3\u4e86\u5639\u4eae\u7684\u547c\u558a\u58f0\uff01 \u3000\u3000\u201c\u9f99\u98de\uff0c\u5a01\u626c\u4e00--\u9f99\u98de\u2014\u2014\u201d\u662f\u8d9f\u5b50\u624b\u558a\u9556\u7684\u58f0\u97f3\uff0c\u82e5\u88f4\u73cf\u80fd\u591f\u542c\u89c1\uff0c\u8fd9\u558a\u9556\u7684\u58f0\u97f3\u4e5f \u662f\u4ed6\u6240\u719f\u6089\u7684\uff0c\u6c5f\u6e56\uff0c\u65e0\u8bba\u9ed1\u3001\u767d\u4e24\u9053\uff0c\u4e00\u542c\u8fd9\u558a\u9556\u7684\u58f0\u97f3\uff0c\u4e5f\u7acb\u523b\u5c31\u4f1a\u77e5\u9053\uff0c\u6b63\u662f\u76ee\u524d\u6c5f \u6e56\u4e0a\u9996\u5c48\u4e00\u6307\uff0c\u65e0\u53ef\u6bd4\u654c\u7684\u201c\u9f99\u98de\u9556\u5c40\u201d\u7684\u961f\u4f0d\u6765\u4e86\u3002 \u3000\u3000\u7247\u523b\uff0c\u9760\u5de6\u8fb9\u7684\u90a3\u6761\u8def\uff0c\u70df\u5c18\u5927\u8d77\uff0c\u8f66\u8f9a\u9a6c\u5636\u58f0\u4e2d\uff0c\u5f53\u5934\u9a70\u6765\u4e00\u5339\u5065\u9a6c\uff0c\u5230\u4e86\u8def\u53e3\uff0c\u9a6c \u4e0a\u7684\u9a91\u58eb\u4e00\u5e26\u7ecf\u7ef3\uff0c\u90a3\u9a6c\u957f\u5636\u4e00\u58f0\uff0c\u4e00\u626c\u8e44\uff0c\u5237\u5730\uff0c\u8f6c\u4e86\u4e2a\u5934\uff0c\u53c8\u5ffd\u5f8b\u5f8b\u5730\u8dd1\u4e86\u56de\u53bb\u3002 \u3000\u3000\u8fd9\u9a91\u8d9f\u9053\u7684\u8d9f\u5b50\u624b\u4e00\u8fc7\uff0c\u63a5\u7740\u5c31\u7f13\u7f13\u6765\u4e86\u4e24\u5339\u9a6c\uff0c\u9a6c\u4e0a\u4eba\u987e\u76fc\u4e4b\u95f4\uff0c\u9887\u4e3a\u81ea\u5f97\uff0c\u4e00\u773c\u671b \u53bb\uff0c\u5c31\u77e5\u9053\u662f\u62bc\u9556\u7684\u9556\u5934\u5230\u4e86\u3002 \u3000\u3000\u90a3\u94f6\u886b\u4eba\u9762\u8272\u4e1d\u6beb\u672a\u53d8\uff0c\u7b49\u5230\u8fd9\u4e24\u5339\u9a6c\u6765\u5230\u8fd1\u524d\uff0c\u624d\u6a2a\u8de8\u4e00\u6b65\uff0c\u6321\u5728\u8def\u4e2d\uff0c\u539f\u6765\u4ed6\u8001\u65e9 \u5c31\u542c\u5230\u6709\u558a\u9556\u7684\u58f0\u97f3\uff0c\u662f\u4ee5\u624d\u4ece\u53e6\u4e00\u6761\u8def\u4e0a\u56de\u5934\uff0c\u7b49\u5728\u8def\u4e2d\u53e3\uff0c\u4e3a\u7684\u5374\u53ea\u662f\u60f3\u95ee\u9556\u961f\u501f\u5339\u9a6c \u9a91\u3002\u8fd9\u5f53\u7136\u662f\u56e0\u4e3a\u4ed6\u8eab\u4fa7\u5e26\u7740\u88f4\u73cf\uff0c\u9a91\u9a6c\u81ea\u7136\u6bd4\u884c\u8def\u65b9\u4fbf\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u7a81\u73b0\u8eab\u5f62\uff0c\u9a91\u5728\u9a6c\u4e0a\u7684\u90a3\u4e24\u4e2a\u9556\u5e08\u5374\u4e0d\u7981\u4e3a\u4e4b\u9762\u8272\u9aa4\u53d8\uff0c\u987b\u77e5\u82e5\u975e\u4e0a\u7ebf\u5f00\u6252\uff0c\u6216 \u8005\u67b6\u6881\u751f\u4e8b\uff0c\u51b3\u4e0d\u4f1a\u6709\u4eba\u6321\u4f4f\u9556\u961f\u7684\u53bb\u8def\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u9556\u5e08\u81ea\u7136\u5927\u60ca\uff0c\u94f6\u886b\u4eba\u76ee\u5149\u51b7\u51b7\u5c06\u4ed6\u4eec\u6253\u91cf\u4e00\u773c\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u5c06\u8de8\u4e0b\u7684 \u9a6c\u501f\u7ed9\u5728\u4e0b\u4e00\u7528\uff0c\u4e00\u6708\u4e4b\u540e\uff0c\u5728\u4e0b\u51b3\u5b9a\u5c06\u8fd9\u5339\u9a6c\u9001\u56de\u8d35\u9556\u5c40\uff0c\u4e24\u4f4d\u81ea\u7ba1\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u9a6c\u4e0a\u7684\u4e24\u4e2a\u9556\u5e08\u4e5f\u6b63\u5728\u4e0a\u4e0b\u6253\u91cf\u7740\u4ed6\uff0c\u5ffd\u5730\u770b\u5230\u4ed6\u8eab\u4fa7\u7684\u88f4\u73cf\uff0c\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6123\u3002 \u3000\u3000\u88f4\u73cf\u81ea\u4e5f\u65e9\u5c31\u770b\u5230\u4ed6\u4eec\uff0c\u809a\u4e2d\u6b63\u6697\u6697\u53eb\u82e6\uff0c\u4ed6\u81ea\u9003\u51fa\u98de\u9f99\u9556\u5c40\u4e4b\u540e\uff0c\u5c31\u518d\u4e5f\u4e0d\u613f\u770b\u5230\u9556 \u5c40\u7684\u4eba\uff0c\u5c24\u5176\u662f\u5728\u8fd9\u6837\u843d\u9b44\u7684\u65f6\u5019\u3002 \u3000\u3000\u800c\u8fd9\u4e24\u4e2a\u9556\u5934\uff0c\u88f4\u73cf\u672c\u751a\u719f\u6089\uff0c\u539f\u6765\u8fd9\u4e24\u4eba\u5728\u98de\u9f99\u9556\u5c40\u91cc\u9887\u5f97\u9f99\u5f62\u516b\u638c\u6a80\u660e\u7684\u4eb2\u4fe1\uff0c\u5c24 \u5176\u5176\u4e2d\u4e00\u4e2a\u53eb\u5feb\u9a6c\u795e\u5200\u9f9a\u6e05\u6d0b\u7684\uff0c\u66f4\u662f\u6a80\u603b\u9556\u5934\u624b\u4e0b\u7684\u7ea2\u4eba\uff0c\u4ed6\u4eec\u51fa\u5165\u5185\u5b85\uff0c\u81ea\u7136\u4e5f\u8ba4\u5f97\u88f4 \u73cf\u3002 \u3000\u3000\u88f4\u73cf\u79c1\u9003\u51fa\u9556\u5c40\u7684\u4e8b\uff0c\u9f99\u5f62\u516b\u638c\u66fe\u5927\u4e3a\u9707\u6012\uff0c\u8fd9\u4e24\u4eba\u4e00\u89c1\u88f4\u73cf\uff0c\u60ca\u5f02\u4e4b\u4e0b\uff0c\u90a3\u94f6\u886b\u4eba\u8bf4 \u7684\u8bdd\uff0c\u5c31\u6839\u672c\u6ca1\u6709\u542c\u8fdb\u8033\u91cc\u3002 \u3000\u3000\u5feb\u9a6c\u795e\u5200\u9f9a\u6e05\u6d0b\u548c\u4ed6\u8eab\u65c1\u7684\u516b\u5366\u638c\u67f3\u8f89\u4e92\u89c6\u4e00\u773c\uff0c\u5237\u5730\uff0c\u8fd9\u5feb\u9a6c\u795e\u5200\u7adf\u8dc3\u4e0b\u9a6c\u6765\uff0c\u54c8\u54c8 \u4e00\u7b11\uff0c\u671d\u88f4\u73cf\u8d70\u4e86\u8fc7\u53bb\uff0c\u6717\u58f0\u9053\uff0c\u88f4\u8001\u5f1f\u600e\u5730\u8dd1\u5230\u8fd9\u91cc\u6765\uff0c\u6559\u6a80\u603b\u9556\u5934\u60f3\u5f97\u597d\u82e6\uff0c\u88f4\u8001\u5f1f\uff0c \u6211\u770b\u4f60\u8fd8\u662f\u56de\u53bb\u5427\uff01\u6c5f\u6e56\u9669\u6076\uff0c\u4f60\u8981\u662f\u4e0a\u4e86\u574f\u4eba\u7684\u5f53\uff0c\u90a3\u624d\u53eb\u82e6\u54e9\u3002\u201c\u88f4\u73cf\u5782\u7740\u5934\uff0c\u6839\u672c\u542c \u4e0d\u5230\u4ed6\u8bf4\u7684\u8bdd\uff0c\u82e5\u4e0d\u662f\u4ed6\u5de6\u65f6\u88ab\u90a3\u94f6\u886b\u4eba\u6240\u6258\uff0c\u751f\u50cf\u662f\u6709\u79cd\u5438\u529b\u5438\u4f4f\u4ed6\u4f3c\u7684\uff0c\u8ba9\u4ed6\u6839\u672c\u52a8\u5f39 \u4e0d\u5f97\uff0c\u5426\u5219\u4ed6\u65e9\u5c31\u6e9c\u5f97\u8fdc\u8fdc\u7684\u4e86\uff0c\u6b64\u523b\u4ed6\u5782\u7740\u5934\uff0c\u6b63\u597d\u671b\u7740\u811a\u4e0a\u7a7f\u7684\u90a3\u4e00\u53cc\u5df2\u7ecf\u7efd\u4e86\u7ebf\uff0c\u7a7f \u4e86\u6d1e\u7684\u7c97\u5e03\u978b\uff0c\u81ea\u60ed\u5f62\u79fd\u7684\u5fc3\u91cc\u4e0d\u7981\u66f4\u96be\u53d7\u3002\u90a3\u94f6\u886b\u4eba\u5251\u7709\u4e00\u8f69\uff0c\u811a\u6b65\u4e00\u9519\uff0c\u4ed6\u548c\u88f4\u73cf\u7684\u8eab \u8eaf\u4fbf\u540c\u65f6\u5f39\u5f00\u4e09\u5c3a\u3002\u662f\u4ee5\u4ed6\u4fbf\u53c8\u6b63\u597d\u6321\u5728\u8fd9\u5feb\u9a6c\u795e\u5200\u7684\u9762\u524d\uff0c\u51b7\u7136\u53f1\u9053\uff0c\u201d\u670b\u53cb\uff0c\u6211\u8bb2\u7684 \u8bdd\uff0c\u4f60\u542c\u5230\u6ca1\u6709\uff1f\u201c\u5feb\u9a6c\u795e\u5200\u773c\u795e\u4e00\u9519\uff0c\u9762\u524d\u5c31\u6362\u4e86\u4e2a\u4eba\u3002\u4ed6\u81ea\u7136\u53c8\u5fae\u5403\u4e00\u60ca\uff0c\u4f46\u662f\u8fd9\u8001\u6c5f \u6e56\u6bd5\u7adf\u6c89\u5f97\u4f4f\u6c14\uff0c\u671b\u7740\u8fd9\u94f6\u886b\u4eba\u54c8\u54c8\u53c8\u4e00\u7b11\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u9601\u4e0b\u60f3\u5fc5\u662f\u6211\u4eec\u8fd9\u4f4d\u88f4\u8001\u5f1f\u7684\u670b \u53cb\uff0c\u6211\u4eec\u8fd9\u4f4d\u8001\u5f1f\u5e74\u7eaa\u8f7b\uff0c\u4e0d\u61c2\u4e8b\uff0c\u591a\u627f\u9601\u4e0b\u7167\u987e\uff0c\u56de\u53bb\u655d\u9556\u5c40\u9f99\u5f62\u516b\u638c\u6a80\u603b\u9556\u5934\u77e5\u9053\u4e86\uff0c \u5fc5\u6709\u8865\u62a5\u9601\u4e0b\u4e4b\u5904\u3002\u201d\u4ed6\u4e00\u56de\u5934\uff0c\u7adf\u53c8\u6717\u58f0\u9053\uff1a\u201c\u67f3\u5144\uff0c\u4f60\u53eb\u540e\u9762\u817e\u51fa\u8f86\u8f66\u6765\uff0c\u4f60\u6211\u5144\u5f1f\u5c31 \u628a\u88f4\u8001\u5f1f\u9001\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u94f6\u886b\u4eba\u6b64\u523b\u9762\u5bd2\u5982\u6c34\uff0c\u76ee\u5149\u51db\u7136\u77aa\u5728\u8fd9\u5feb\u9a6c\u795e\u5200\u7684\u8138\u4e0a\uff0c\u9f9a\u6e05\u6d0b\u53ea\u89c9\u4ed6\u8fd9\u4e24\u9053\u76ee\u5149\u5c31 \u50cf\u4e24\u628a\u5200\u4e00\u6837\uff0c\u4e0d\u7981\u53c8\u5e72\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u5feb\u9a6c\u795e\u5200\u9f9a\u6e05\u6d0b\uff0c\u4fdd\u7684\u8fd9\u8d9f\u9556\uff0c\u6b63\u597d\u662f\u8981\u56de\u4eac \u57ce\u7684\uff0c\u4e0d\u77e5\u9601\u4e0b\u662f\u5426\u6709\u5174\uff0c\u548c\u5c0f\u53ef\u4e00\u8d77\u8d70\u4e00\u8d9f\uff0c\u8981\u4e0d\u7136\u7684\u8bdd\u2026\u2026\u54b3\uff01\u54b3\uff01\u201d \u3000\u3000\u4ed6\u53c8\u5e72\u7b11\u4e86\u4e24\u58f0\uff0c\u63a5\u7740\u9053\uff1a\u201c\u9601\u4e0b\u5982\u679c\u8eab\u4e0a\u4e0d\u4fbf\uff0c\u5c0f\u53ef\u591a\u591a\u5c11\u5c11\uff0c\u4e5f\u5f97\u9001\u9601\u4e0b\u4e9b\u76d8\u7f20\uff0c \u4e5f\u4e0d\u6789\u9601\u4e0b\u8001\u8fdc\u628a\u6211\u4eec\u8fd9\u4f4d\u88f4\u8001\u5f1f\u9001\u56de\u6765\u3002\u201d \u3000\u3000\u8fd9\u94f6\u886b\u4eba\u6709\u5982\u575a\u51b0\u7684\u9762\u8272\uff0c\u7a81\u5730\u7efd\u5f00\u4e00\u4e1d\u7b11\u5bb9\uff0c\u8fd9\u7b11\u5bb9\u8d8a\u5c55\u8d8a\u5f00\u6717\uff0c\u6700\u540e\u7adf\u7eb5\u58f0\u5927\u7b11\u8d77 \u6765\u3002 \u3000\u3000\u5feb\u9a6c\u795e\u5200\u5fc3\u4e5f\u4e00\u5b9a\uff0c\u987b\u77e5\u4ed6\u672c\u5bf9\u8fd9\u94f6\u886b\u4eba\u6765\u610f\u6709\u4e9b\u5600\u5495\uff0c\u6b64\u523b\u89c1\u8fd9\u94f6\u886b\u4eba\u4e00\u542c\u81ea\u5df1\u63d0\u5230 \u76d8\u7f20\uff0c\u5c31\u7b11\u4e86\u8d77\u6765\uff0c\u5fc3\u9042\u5927\u5b9a\uff0c\u4ee5\u4e3a\u8fd9\u4eba\u4e0d\u8fc7\u662f\u4e2a\u6253\u79cb\u98ce\uff0c\u6572\u7af9\u6760\u7684\u4eba\u7269\uff0c\u628a\u5148\u524d\u7684\u5600\u5495\u4e4b \u5fc3\uff0c\u5168\u629b\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u4e00\u4f38\u624b\uff0c\u638f\u51fa\u534a\u952d\u5341\u4e24\u91cd\u7684\u5143\u5b9d\u6765\uff0c\u6258\u5728\u638c\u5fc3\uff0c\u9001\u5230\u8fd9\u94f6\u886b\u4eba\u9762\u524d\uff0c \u53c8\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u51fa\u95e8\u5728\u5916\uff0c\u8eab\u4e0a\u4e5f\u5e26\u7740\u4e0d\u591a\uff0c\u76cf\u76cf\u4e4b\u6570\uff0c\u5c31\u8bf7\u670b\u53cb\u5c06\u5c31\u4e70\u4e9b\u9152\u559d\u3002\u201d\u8bcd\u8272\u4e4b \u4e2d\uff0c\u81ea\u4e5f\u5df2\u8fdc\u4e0d\u5982\u65b9\u624d\u7684\u5ba2\u6c14\u4e86\u3002 \u3000\u3000\u8fd9\u94f6\u886b\u4eba\u7b11\u58f0\u7a81\u655b\uff0c\u76ee\u5149\u8f6c\u5230\u4ed6\u7684\u624b\u4e0a\uff0c\u7a81\u53c8\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u7ed9\u6211\u7684\u5417\uff1f\u201d \u3000\u3000\u9f9a\u6e05\u6d0b\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8fde\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\u6210\u656c\u610f\uff0c\u4e0d\u6210\u656c\u610f\uff0c\u670b\u53cb\u5343\u4e07\u4e0d\u7528\u5ba2\u6c14\uff0c\u4e0d\u8fc7\u8db3\u591f \u4e0a\u77f3\u5bb6\u5e84\u7684\u9189\u6708\u697c\u53bb\u5403\u4e00\u987f\u4e86\u3002\u201d\u4e00\u56de\u5934\uff0c\u53c8\u671d\u4ed6\u8eab\u540e\u9a6c\u4e0a\u7684\u67f3\u8f89\u7b11\u9053\uff1a\u201c\u67f3\u5144\uff0c\u6628\u5929\u591c\u91cc \u6211\u4eec\u51e0\u4e2a\u5403\u7684\u90a3\u987f\uff0c\u6050\u6015\u8fd8\u4e0d\u5230\u4e94\u4e24\u94f6\u5b50\u5427\u3002\u201d \u3000\u3000\u88f4\u73cf\u773c\u89d2\u5077\u77a5\u8fd9\u94f6\u886b\u4eba\u4e00\u773c\uff0c\u770b\u89c1\u8fd9\u4ece\u672a\u9732\u8fc7\u7b11\u5bb9\u7684\u94f6\u886b\u4eba\uff0c\u6b64\u523b\u6ee1\u9762\u6625\u98ce\uff0c\u7adf\u50cf\u662f\u6362 \u4e86\u4e2a\u4eba\u4f3c\u7684\uff0c\u5fc3\u65f6\u4e0d\u7981\u5927\u4e3a\u5947\u602a\u3002 \u3000\u3000\u90a3\u5feb\u9a6c\u795e\u5200\u4f38\u7740\u624b\uff0c\u6258\u7740\u94f6\u5b50\uff0c\u7709\u68a2\u773c\u89d2\uff0c\u5df2\u9732\u51fa\u4e0d\u8010\u70e6\u7684\u795e\u60c5\u6765\uff0c\u5fc3\u91cc\u6697\u6697\u9a82\u9053\uff1a \u201c\u82e5\u4e0d\u662f\u5927\u7237\u5728\u8fd9\u5b98\u9053\u4e0a\u4e0d\u60f3\u751f\u4e8b\uff0c\u4e0d\u4e00\u811a\u8e22\u6241\u4e86\u4f60\u624d\u602a\uff01\u201d \u3000\u3000\u90a3\u94f6\u886b\u4eba\u53f3\u624b\u6258\u7740\u88f4\u73cf\u7684\u5de6\u8098\uff0c\u5de6\u624b\u6162\u6162\u4f38\u4e86\u51fa\u53bb\uff0c\u4e00\u9762\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u89c1\u8d50\uff0c\u90a3\u6211\u5c31 \u62dc\u9886\u4e86\u3002\u201d \u3000\u3000\u8bdd\u58f0\u4e00\u843d\uff0c\u4ed6\u5de6\u624b\u75be\u4f38\uff0c\u5df2\u5c06\u5feb\u9a6c\u795e\u5200\u90a3\u53ea\u6258\u7740\u94f6\u5b50\u7684\u624b\u4e00\u628a\u64d2\u4f4f\uff0c\u9762\u4e0a\u7b11\u5bb9\u4ecd\u81ea\u672a \u53d8\uff0c\u5de6\u624b\u4e00\u62e7\uff0c\u4e00\u6296\uff0c\u53ea\u542c\u5f97\u8fd9\u5feb\u9a6c\u795e\u5200\u4e00\u58f0\u60e8\u547c\uff0c\u4ed6\u7684\u4e00\u53ea\u53f3\u624b\uff0c\u7adf\u88ab\u8fd9\u94f6\u886b\u4eba\u4ee5\u95fb\u6240\u672a \u95fb\u7684\u624b\u6cd5\uff0c\u5728\u8fd9\u5feb\u5982\u95ea\u7535\u7684\u4e00\u523b\u91cc\uff0c\u4e00\u62e7\u4e00\u6296\u4e4b\u4e0b\uff0c\u7adf\u786c\u751f\u751f\u5c06\u4ed6\u8fd9\u53ea\u6258\u4f4f\u94f6\u5b50\u7684\u624b\u638c\u9f50\u8155 \u5730\u626f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5feb\u9a6c\u795e\u5200\u7eb5\u7136\u662f\u786c\u6c49\uff0c\u6b64\u523b\u53ef\u4e5f\u633a\u4e0d\u4f4f\u4e86\uff0c\u8155\u95f4\u7684\u9c9c\u8840\u76f4\u5916\u5192\uff0c\u4ed6\u60e8\u547c\u4e00\u58f0\uff0c\u53cc\u773c\u77aa\u5f97 \u8840\u7ea2\uff0c\u4e00\u54ac\u7259\uff0c\u7adf\u75bc\u5f97\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u88f4\u73cf\u4e0d\u7981\u673a\u4f36\u4f36\u6253\u4e86\u51b7\u98a4\uff0c\u90a3\u6cf0\u7136\u81ea\u5f97\u5730\u5750\u5728\u9a6c\u4e0a\u7684\u516b\u5366\u638c\u67f3\u8f89\uff0c\u4e5f\u4e0d\u7981\u9762\u8272 \u9aa4\u53d8\uff0c\u53d8\u5f97\u7070\u767d\uff0c\u5389\u559d\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u8fd9\u662f\u5e72\u4e48\uff1f\u201d\u4e00\u62ac\u811a\uff0c\u98d8\u8eab\u4e0b\u4e86\u9a6c\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u5230\u9f9a \u6e05\u6d0b\u8eab\u4fa7\uff0c\u5c06\u4ed6\u4ece\u5730\u4e0a\u6284\u4e86\u8d77\u6765\uff0c\u56de\u5934\u53c8\u5406\u559d\u9053\uff1a\u201c\u5feb\u6765\u4eba\u5440\uff01\u201d\u53c8\u53eb\u9053\uff1a\u201c\u6284\u5bb6\u4f19\u5b88\u4f4f\u9556 \u8f66\uff01\u201d \u3000\u3000\u90a3\u94f6\u886b\u4eba\u624b\u91cc\u62ff\u7740\u90a3\u53ea\u8840\u6dcb\u6dcb\u7684\u65ad\u638c\uff0c\u9c9c\u8840\u4e00\u6ef4\u4e00\u6ef4\u5730\u6ef4\u5728\u5730\u4e0a\uff0c\u5c06\u5730\u4e0a\u7684\u6c99\u77f3\u67d3\u4e86\u4e00 \u7247\u9eef\u7ea2\uff0c\u4ed6\u9762\u4e0a\u7adf\u4ecd\u5e26\u7740\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u7684\u539a\u8d50\uff0c\u6211\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff0c\u53ea\u5f97\u62dc\u9886\u4e86\uff0c\u81f3\u4e8e\u8fd9 \u952d\u94f6\u5b50\u561b\u2014\u2014\u54c8\u54c8\uff0c\u90a3\u8fd8\u662f\u8fd8\u7ed9\u9601\u4e0b\uff01\u201d\u4ed6\u624b\u638c\u4e00\u7ffb\uff0c\u55d6\u5730\uff0c\u4e00\u70b9\u94f6\u5149\u5fae\u95ea\uff0c\u4ed6\u7ade\u628a\u90a3\u53ea\u65ad \u638c\u4e0a\u7684\u534a\u952d\u94f6\u5b50\uff0c\u6253\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u534a\u952d\u94f6\u5b50\u5176\u53bb\u5982\u77e2\uff0c\u98ce\u58f0\u5fae\u51db\u95f4\uff0c\u516b\u5366\u638c\u67f3\u8f89\uff0c\u53ea\u89c1\u8fd9\u70b9\u94f6\u661f\u5df2\u6253\u5230\u773c\u524d\uff0c\u6b63\u662f\u5f80\u81ea \u5df1\u9f3b\u6881\u6b63\u4e2d\u6253\u6765\uff0c\u81ea\u5df1\u7adf\u8fde\u8eb2\u90fd\u65e0\u6cd5\u8eb2\uff0c\u8fd9\u534a\u5f99\u94f6\u5b50\u4ece\u8fd9\u94f6\u6749\u4eba\u624b\u91cc\u53d1\u51fa\u6765\uff0c\u7adf\u6bd4\u90a3\u79cd\u88c5\u6709 \u673a\u7c27\u7684\u94c1\u5f29\u8fd8\u6025\u3002 \u3000\u3000\u4ed6\u5fc3\u9b44\u4ff1\u4e27\u4e4b\u4e0b\uff0c\u54ea\u77e5\u8fd9\u70b9\u94f6\u661f\u8fd9\u4e48\u5feb\u7684\u6765\u52bf\uff0c\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u7adf\u7a81\u7136\u6389\u4e86\u4e0b\u53bb\u4e86\uff0c\u5c31\u50cf \u662f\u6709\u4eba\u7a81\u7136\u5728\u4e0b\u9762\u4e00\u62c9\u4f3c\u7684\uff0c\u8fd9\u534a\u952d\u94f6\u5b50\u5c31\u7a81\u7136\u6d88\u6cc4\u4e86\u529b\u9053\uff0c\u8f7b\u98d8\u98d8\u5730\u843d\u5728\u90a3\u5df2\u6655\u8fc7\u53bb\u7684\u5feb \u9a6c\u795e\u5200\u9f9a\u6e05\u6d0b\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u70b9\u94f6\u661f\u867d\u7136\u6ca1\u6709\u6253\u7740\u516b\u5366\u638c\u67f3\u8f89\uff0c\u53ef\u6bd4\u6253\u7740\u4ed6\u8fd8\u8ba9\u4ed6\u5403\u60ca\uff0c\u516b\u5366\u638c\u67f3\u8f89\u4eca\u5e74\u5e74\u5df2\u4e0d \u60d1\uff0c\u95ef\u8361\u6c5f\u6e56\u4e5f\u6709\u4e8c\u5341\u5e74\u4e86\uff0c\u6b66\u6797\u9ad8\u624b\uff0c\u4ed6\u4e5f\u89c1\u8fc7\u4e0d\u5c11\uff0c\u53ef\u662f\u50cf\u8fd9\u94f6\u886b\u4eba\u8fd9\u79cd\u53d1\u6697\u5668\u7684\u624b \u6cd5\uff0c\u4ed6\u53ef\u7b80\u76f4\u6ca1\u6709\u770b\u8fc7\uff0c\u751a\u81f3\u8fde\u542c\u90fd\u6ca1\u6709\u542c\u5230\u8fc7\u3002 \u3000\u3000\u8fd9\u94f6\u886b\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u5f20\u50cf\u662f\u6cb9\u7eb8\u822c\u7684\u4e1c\u897f\uff0c\u7adf\u5c06\u8fd9\u53ea\u65ad\u638c\u4ed4\u4ed4\u7ec6\u7ec6\u5305\u5728\u91cc \u9762\uff0c\u53c8\u4ed4\u4ed4\u7ec6\u7ec6\u6536\u8fdb\u6000\u91cc\u3002 \u3000\u3000\u90a3\u672c\u5df2\u9762\u5982\u4e0a\u8272\u7684\u516b\u5366\u638c\u67f3\u8f89\u89c1\u4e86\u8fd9\u4e00\u4e3e\u52a8\uff0c\u5fc3\u4e2d\u5fae\u52a8\uff0c\u7a81\u5730\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\uff0c\u624b\u4e00\u53d1 \u8f6f\uff0c\u7adf\u8fde\u4ed6\u6276\u6301\u7740\u7684\u9f9a\u6e05\u6d0b\u90fd\u628a\u6301\u4e0d\u4f4f\u4e86\uff0c\u5657\u5730\u4e00\u58f0\uff0c\u672c\u6765\u501a\u5728\u4ed6\u624b\u81c2\u4e0a\u7684\u9f9a\u6e05\u6d0b\uff0c\u6b64\u523b\u7adf \u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5df2\u6709\u4e24\u4e09\u4e2a\u8d9f\u4e8e\u624b\uff0c\u9556\u4f19\u8d76\u4e86\u8fc7\u6765\uff0c\u5fae\u52d2\u9a6c\u7f30\uff0c\u90fd\u7ffb\u8eab\u4e0b\u9a6c\u3002 \u3000\u3000\u8dd1\u5230\u8fd9\u91cc\u4e86\uff0c\u90a3\u94f6\u886b\u4eba\u542b\u7b11\u671b\u7740\u4ed6\u4eec\uff0c\u53ef\u662f\u4ed6\u6b64\u523b\u8138\u4e0a\u7684\u7b11\u5bb9\u6108\u662f\u5f00\u6717\uff0c\u90a3\u516b\u5366\u638c\u67f3\u8f89 \u5374\u50cf\u662f\u6015\u5f97\u66f4\u5389\u5bb3\u3002 \u3000\u3000\u4ed6\u6d51\u8eab\u7adf\u5fae\u5fae\u6709\u4e9b\u98a4\u6296\u8d77\u6765\uff0c\u7ad9\u5728\u4e00\u65c1\u7684\u88f4\u73cf\u53c8\u60ca\u53c8\u602a\uff0c\u5e73\u65e5\u4ed6\u6240\u89c1\u6240\u95fb\uff0c\u77e5\u9053\u4e0d\u4f46 \u201c\u9f99\u5f62\u516b\u638c\u201d\u5728\u6c5f\u6e56\u4e2d\u53ef\u7b97\u662f\u9886\u8896\u4eba\u7269\uff0c\u201c\u98de\u9f99\u9556\u5c40\u201d\u91cc\u6bcf\u4e00\u4e2a\u9556\u5e08\uff0c\u5728\u6b66\u6797\u4e2d\u4e5f\u662f\u54cd\u5f53\u5f53 \u7684\u4eba\u7269\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u516b\u5366\u638c\u67f3\u8f89\uff0c\u6b64\u523b\u5374\u9732\u51fa\u8fd9\u79cd\u60e7\u6015\u7684\u795e\u8272\u6765\uff0c\u751f\u50cf\u662f\u8fd9\u94f6\u886b\u4eba\u4e00\u62ac\u624b\uff0c\u5c31\u53ef\u4ee5\u5c06 \u4ed6\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u4f3c\u7684\u3002 \u3000\u3000\u8fd9\u94f6\u886b\u4eba\u5fae\u7b11\u4e4b\u95f4\uff0c\u53c8\u9053\uff1a\u201c\u65b9\u624d\u90a3\u4f4d\u9f9a\u5927\u9556\u5934\u7684\u539a\u8d50\uff0c\u5728\u4e0b\u5df2\u62dc\u9886\u4e86\uff0c\u9601\u4e0b\u662f\u5426\u4e5f\u6709 \u4e1c\u897f\u89c1\u8d50\u5462\uff1f\u201d \u3000\u3000\u90a3\u516b\u5366\u638c\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u53ef\u6709\u773c\u65e0\u73e0\uff0c\u65b9\u624d\u6ca1\u6709\u770b\u51fa \u8001\u524d\u8f88\u662f\u8c01\u6765\uff0c\u4e0d\u8fc7\u665a\u8f88\u4eec\u5b9e\u5728\u4e5f\u6ca1\u6709\u60f3\u5230\u8001\u524d\u8f88\u4f1a\u7a81\u7136\u5728\u8fd9\u6cb3\u6714\u9053\u4e0a\u73b0\u8eab\uff0c\u73b0\u5728\u665a\u8f88\u5df2\u7ecf \u77e5\u9053\u8001\u524d\u8f88\u662f\u8c01\u4e86\uff0c\u8001\u524d\u8f88\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u53ea\u7ba1\u8bf4\u5c31\u662f\uff0c\u665a\u8f88\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u94f6\u886b\u4eba\u7a81\u5730\u53c8\u957f\u7b11\u8d77\u6765\uff0c\u90a3\u51e0\u4e2a\u8d9f\u5b50\u624b\u6b64\u523b\u5374\u77a0\u76ee\u7ed3\u820c\uff0c\u4e0d\u77e5\u9053\u8fd9\u516b\u5366\u638c\u67f3\u9556\u5934\u600e\u5730\u4f1a \u8bf4\u51fa\u8fd9\u79cd\u6cc4\u6c14\u7684\u8bdd\u6765\u3002 \u3000\u3000\u94f6\u886b\u4eba\u957f\u7b11\u58f0\u4f4f\uff0c\u51b7\u7136\u9053\uff1a\u201c\u4f60\u65e2\u5df2\u8ba4\u51fa\u6211\u6765\u4e86\uff0c\u6211\u4e5f\u4e0d\u518d\u96be\u4e3a\u4f60\uff0c\u4e0d\u8fc7\u8fd9\u8fd8\u8981\u501f\u4f60\u4e4b \u53e3\uff0c\u4f20\u8a00\u6c5f\u6e56\uff0c\u5c31\u8bf4\u6211\u5343\u624b\u4e4b\u6570\uff0c\u5df2\u5c06\u51d1\u6ee1\uff0c\u53ef\u662f\u8fd8\u672a\u51d1\u6ee1\uff0c\u6c5f\u6e56\u4e2d\u624b\u4e0a\u8fd8\u67d3\u7740\u8840\u8165\u7684\u670b \u53cb\uff0c\u53ef\u8981\u7559\u610f\u4e9b\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4eca\u5929\u6211\u6682\u501f\u8d35\u9556\u5c40\u4e24\u5339\u5065\u9a6c\uff0c\u56de\u53bb\u544a\u8bc9\u59d3\u6a80\u7684\uff0c\u8fd9\u59d3\u88f4\u7684\u5c11\u5e74\uff0c \u6211\u4e5f\u8981\u5e26\u56de\u53bb\uff0c\u4ed6\u82e5\u6709\u4ec0\u4e48\u8bdd\uff0c\u53ea\u7ba1\u51b2\u7740\u6211\u6765\u8bf4\uff0c\u8fd9\u4e09\u4e2a\u6708\u91cc\uff0c\u6211\u90fd\u7559\u5728\u5e73\u5c71\u5916\u7684\u96c6\u8d24\u5c71 \u5e84\uff0c\u59d3\u6a80\u7684\u8981\u95ee\u6211\u8981\u4eba\u8981\u9a6c\uff0c\u6211\u90fd\u5728\u96c6\u8d24\u5e84\u606d\u5019\u5927\u9a7e\u3002\u201d \u3000\u3000\u8fd9\u94f6\u886b\u4eba\u51b7\u7136\u8bf4\u51fa\u8fd9\u4e9b\u8bdd\uff0c\u516b\u5366\u638c\u8fde\u58f0\u552f\u552f\uff0c\u4e00\u53e5\u8bdd\u90fd\u4e0d\u6562\u53cd\u9a73\uff0c\u90a3\u51e0\u4e2a\u8d9f\u5b50\u624b\u4e5f\u90fd\u662f \u8001\u6c5f\u6e56\uff0c\u4e00\u542c\u8fd9\u8bdd\uff0c\u4e5f\u8d76\u7d27\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u6b64\u523b\u90fd\u77e5\u9053\u4e86\u94f6\u886b\u4eba\u7adf\u7136\u5c31\u662f\u540d\u9707\u5929\u4e0b\u5343\u624b\u4e66\u751f\uff0c\u666e\u5929\u4e4b\u4e0b\u7684\u6b66\u6797\u4e2d\u4eba\uff0c\u5bf9\u5343 \u624b\u4e66\u751f\u8bf4\u51fa\u7684\u8bdd\uff0c\u5c31\u4ece\u672a\u6709\u8fdd\u6297\u8fc7\u7684\uff0c\u4ed6\u4eec\u5947\u602a\u7684\u53ea\u662f\uff0c\u6c5f\u6e56\u4e2d\u4e45\u5df2\u672a\u9732\u884c\u8e2a\u7684\u5343\u624b\u4e66\u751f\uff0c \u6b64\u523b\u600e\u5730\u4e00\u53cd\u5e38\u6001\uff0c\u7adf\u5c06\u81ea\u5df1\u843d\u811a\u5730\u65b9\u90fd\u8bf4\u51fa\u6765\u4e86\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u4eec\u5fc3\u91cc\u867d\u5947\u602a\uff0c\u53e3\u91cc\u53ef\u4e0d\u6562\u95ee\u51fa\u6765\uff0c\u516b\u5366\u638c\u67f3\u8f89\u548c\u65c1\u8fb9\u7684\u8d9f\u5b50\u624b\u4f4e\u8bed\u4e86\u4e24\u53e5\uff0c\u90a3 \u8d9f\u5b50\u624b\u5c31\u7acb\u523b\u8dd1\u4e86\u8fc7\u53bb\uff0c\u7275\u6765\u4e24\u5339\u5065\u9a6c\uff0c\u505c\u5728\u8fd9\u5343\u624b\u4e66\u751f\u9762\u524d\uff0c\u7136\u540e\u5012\u9000\u7740\u8d70\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u5343\u624b\u4e66\u751f\u624b\u638c\u5fae\u5fae\u4e00\u6258\uff0c\u88f4\u73cf\u53ea\u89c9\u5f97\u81ea\u5df1\u751f\u50cf\u662f\u51cc\u4e91\u9a7e\u96fe\u4f3c\u7684\uff0c\u4e0d\u77e5\u600e\u5730\u5df2\u843d\u5728\u9a6c\u4e0a\uff0c \u76f4\u5230\u6b64\u523b\u4ed6\u8fd8\u4e0d\u77e5\u9053\u8fd9\u94f6\u886b\u4eba\u7a76\u7adf\u662f\u8c01\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5bf9\u81ea\u5df1\u6709\u4f55\u7528\u610f\uff0c\u53ef\u662f\u4ed6\u5df2\u731c\u51fa\u8fd9\u94f6\u886b \u4eba\u5fc5\u5b9a\u548c\u90a3\u4e24\u672c\u5947\u4e66\u6709\u7740\u5173\u7cfb\uff0c\u4ed6\u770b\u4e86\u8fd9\u94f6\u886b\u4eba\u884c\u4e8b\u624b\u6bb5\u4e4b\u51b7\u9177\uff0c\u53ea\u5e0c\u671b\u5b59\u9526\u5e73\u548c\u5979\u7684\u7239\u7239 \u4e0b\u8981\u88ab\u8fd9\u94f6\u886b\u4eba\u6349\u4f4f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u7528\u63a8\u60f3\uff0c\u5c31\u77e5\u9053\u5047\u5982\u5b59\u9526\u5e73\u7236\u5973\u88ab\u6349\u4e4b\u540e\u7684\u60e8\u51b5\u3002 \u3000\u3000\u5343\u624b\u4e66\u751f\u76ee\u5149\u51b7\u6f20\u5730\u5728\u90a3\u516b\u5366\u638c\u548c\u8d9f\u5b50\u624b\u7684\u9762\u4e0a\u626b\u4e86\u4e00\u4e0b\uff0c\u8eab\u5f62\u4e00\u52a8\uff0c\u516b\u5366\u638c\u67f3\u8f89\u751a\u81f3 \u8fde\u770b\u90fd\u6ca1\u6709\u770b\u6e05\uff0c\u4ed6\u5df2\u500f\u7136\u5750\u5230\u9a6c\u4e0a\uff0c\u5176\u8f7b\u7075\u5de7\u5feb\uff0c\u7b80\u76f4\u4e0d\u662f\u4e16\u95f4\u4efb\u4f55\u8a00\u8bcd\uff0c\u53ef\u4ee5\u5f62\u5bb9\u7684\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u548c\u88f4\u73cf\u6240\u4e58\u7684\u4e24\u5339\u5065\u9a6c\u90fd\u5728\u53e6\u4e00\u6761\u9053\u4e0a\u6d88\u5931\u7684\u65f6\u5019\uff0c\u516b\u5366\u638c\u67f3\u8f89\u5bf8\u900f\u51fa\u4e00\u53e3\u6c14\uff0c \u5c06\u91cd\u4f24\u7684\u9f9a\u6e05\u6d0b\u6276\u5230\u4e00\u8f86\u8f66\u4e0a\u3002 \u3000\u3000\u4e8e\u662f\u9556\u8f66\u518d\u6b21\u524d\u884c\uff0c\u53ea\u662f\u90a3\u8d9f\u5b50\u624b\u558a\u9556\u7684\u58f0\u97f3\uff0c\u5df2\u8fdc\u4e0d\u5982\u5148\u524d\u54cd\u4eae\u4e86\u3002 \u3000\u3000\u9a91\u9a6c\uff0c\u5bf9\u4e8e\u88f4\u73cf\u6765\u8bf4\uff0c\u7684\u786e\u662f\u4e00\u4ef6\u82e6\u4e8b\uff0c\u4ed6\u867d\u7136\u5728\u9556\u5c40\u4e2d\u751f\u957f\uff0c\u5374\u4ece\u672a\u6ca1\u6709\u9a91\u8fc7\u9a6c\uff0c\u6b64 \u523b\uff0c\u4ed6\u54ac\u7740\u7259\uff0c\u5750\u5728\u9a6c\u4e0a\uff0c\u4e24\u6761\u817f\u7d27\u7d27\u5939\u7740\u9a6c\u8170\uff0c\u9a6c\u884c\u751a\u6025\uff0c\u4ed6\u53ea\u89c9\u8fd9\u4e24\u6761\u817f\u706b\u8fa3\u8fa3\u5730\u75db\uff0c \u5f80\u5e38\u4ed6\u770b\u5230\u522b\u4eba\u9a91\u9a6c\u7684\u6837\u5b50\uff0c\u603b\u89c9\u5f97\u975e\u5e38\u7fa1\u6155\uff0c\u73b0\u5728\u4ed6\u5374\u611f\u89c9\u5230\u8fd9\u5e76\u4e0d\u662f\u4e00\u4ef6\u503c\u5f97\u7fa1\u6155\u7684 \u4e8b\uff0c\u751a\u81f3\u5df2\u4e0d\u50cf\u4ed6\u5728\u9a91\u7740\u9a6c\uff0c\u800c\u50cf\u662f\u9a6c\u5728\u9a91\u7740\u4ed6\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4e1d\u6beb\u4e0d\u80fd\u63a7\u5236\u9a6c\uff0c\u53cd\u5f97\u8ba9\u9a6c\u63a7\u5236 \u7740\u4ed6\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u5c06\u4e00\u5207\u75db\u82e6\u90fd\u5fcd\u5728\u5fc3\u91cc\uff0c\u4ed6\u8eab\u4fa7\u7684\u94f6\u886b\u4eba\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\u8fd8\u6ca1\u6709\u5bf9\u4ed6\u8bf4\u8fc7\u4e00\u53e5\u8bdd\uff0c \u505a\u8fc7\u4e00\u4e2a\u624b\u5f0f\uff0c\u751a\u81f3\u8fde\u770b\u90fd\u6ca1\u6709\u5411\u4ed6\u770b\u4e00\u773c\uff0c\u4f46\u662f\u4ed6\u5374\u50cf\u5df2\u4e3b\u5bb0\u7740\u4ed6\u7684\u547d\u8fd0\uff0c\u8fd9\u79cd\u906d\u9047\uff0c\u5374 \u7684\u786e\u662f\u5927\u75db\u82e6\u4e86\u4e9b\u3002 \u3000\u3000\u4e24\u5339\u9a6c\u517c\u7a0b\u53c8\u9a70\u9a8b\u4e86\u4e00\u6bb5\uff0c\u7a81\u5730\u8def\u52bf\u4e00\u8f6c\uff0c\u8fd9\u6761\u8def\u5f80\u53f3\u9762\u7ed5\u4e86\u8fc7\u53bb\uff0c\u88f4\u73cf\u53ea\u89c9\u5f97\u8fd9\u6761\u8def \u8d8a\u6765\u8d8a\u5bbd\uff0c\u884c\u4eba\u5374\u8d8a\u6765\u8d8a\u5c11\u3002 \u3000\u3000\u5f80\u8fd9\u6761\u8def\u4e0a\u53ea\u8d70\u4e86\u534a\u76cf\u8336\u7684\u65f6\u5019\uff0c\u524d\u9762\u5c31\u662f\u4e2a\u5927\u6811\u6797\u5b50\uff0c\u8fd9\u65f6\u5019\u8fd8\u662f\u590f\u5929\uff0c\u6d51\u8eab\u5192\u7740\u6c57 \u7684\u88f4\u73cf\uff0c\u4e00\u8fdb\u4e86\u8fd9\u6811\u6797\u5b50\uff0c\u624d\u900f\u51fa\u53e3\u6c14\u3002 \u3000\u3000\u6811\u6797\u5b50\u91cc\u7adf\u4e5f\u6709\u4e00\u6761\u788e\u77f3\u5b50\u94fa\u6210\u7684\u8def\uff0c\u8fd9\u6761\u8def\u8d70\u4e86\u4e00\u534a\uff0c\u88f4\u73cf\u653e\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u91cc\u9762\u9690\u9690 \u7ea6\u5730\uff0c\u7adf\u9732\u51fa\u697c\u9601\u7684\u5f71\u5b50\u6765\u3002 \u3000\u3000\u88f4\u73cf\u81ea\u4ece\u90a3\u5929\u4ece\u9556\u5c40\u7684\u540e\u5899\u4e0a\u8dc3\u4e0b\u4e4b\u540e\uff0c\u6240\u9047\u7684\u4e8b\u53ef\u8bf4\u90fd\u662f\u6781\u4e3a\u79bb\u5947\u7684\uff0c\u4f46\u662f\u4ed6\u611f\u89c9\u5230 \u6700\u79bb\u5947\u7684\uff0c\u8fd8\u662f\u6b64\u523b\u3002 \u3000\u3000\u88f4\u73cf\u65e0\u6cd5\u731c\u51fa\u8fd9\u94f6\u886b\u4eba\u4e3a\u4f55\u8981\u8fd9\u6837\u5bf9\u5f85\u81ea\u5df1\uff0c\u82e5\u8bf4\u4ed6\u5bf9\u81ea\u5df1\u6709\u7740\u6076\u610f\uff0c\u4ed6\u6839\u672c\u65e0\u9700\u8d39\u8fd9 \u4e48\u591a\u9ebb\u70e6\uff0c\u53ea\u8981\u4e00\u62ac\u624b\uff0c\u4fbf\u53ef\u89e3\u51b3\u81ea\u5df1\uff0c\u82e5\u8bf4\u4ed6\u5bf9\u81ea\u5df1\u5e76\u65e0\u6076\u610f\uff0c\u5374\u4e5f\u4e07\u4e07\u4e0d\u4f1a\u5bf9\u81ea\u5df1\u8fd9\u822c \u505a\u6cd5\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u5c61\u7ecf\u60e8\u52ab\uff0c\u51e1\u4e8b\u90fd\u4e0d\u6562\u5f80\u597d\u5904\u53bb\u60f3\uff0c\u800c\u4e8b\u5b9e\u4e0a\u4ee5\u4ed6\u8fd9\u79cd\u5904\u5883\uff0c\u548c\u4ed6\u773c\u4e2d\u6240\u89c1\u7684\u8fd9 \u94f6\u886b\u4eba\u7684\u884c\u4e8b\uff0c\u4e5f\u4e0d\u5141\u8bb8\u4ed6\u5f80\u597d\u5904\u53bb\u60f3\u3002 \u3000\u3000\u5750\u9a6c\u4e0a\uff0c\u4ed6\u5fc3\u5ff5\u6570\u8f6c\uff0c\u4e0d\u7981\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4eba\u4e00\u5b9a\u662f\u5c06\u6211\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u8ffd\u95ee \u90a3\u4e24\u672c\u4e66\u7684\u4e0b\u843d\uff0c\u53ef\u662f\u8fd9\u4e24\u672c\u4e66\u73b0\u5728\u7a76\u7adf\u5df2\u88ab\u2018\u5b59\u8001\u7239\u2019\u5e26\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u6211\u4e5f\u4e0d\u77e5\u9053\u5440\u3002\u201d \u3000\u3000\u9a6c\u4e00\u8fdb\u4e86\u6811\u6797\uff0c\u5c31\u8d8a\u884c\u8d8a\u7f13\uff0c\u6b64\u523b\u7adf\u505c\u4e86\u4e0b\u6765\uff0c\u539f\u6765\u90a3\u94f6\u886b\u4eba\u7adf\u5c06\u8de8\u4e0b\u7684\u9a6c\u6a2a\u5728\u88f4\u73cf\u6240 \u9a91\u7684\u9a6c\u9996\u524d\u9762\uff0c\u76ee\u5149\u518d\u95ea\uff0c\u51db\u7136\u843d\u5728\u88f4\u73cf\u8eab\u4e0a\uff0c\u53f3\u624b\u7a81\u5730\u4e00\u5782\uff0c\u5bbd\u5927\u7684\u888d\u8896\u4e2d\uff0c\u968f\u5373\u843d\u51fa\u4e24 \u672c\u4e66\u6765\u3002 \u3000\u3000\u5343\u624b\u4e66\u751f\u7adf\u5c06\u8fd9\u4e24\u672c\u4e66\u9001\u5230\u88f4\u73cf\u773c\u524d\uff0c\u88f4\u73cf\u4e00\u773c\u671b\u53bb\uff0c\u8840\u6db2\u4e0d\u7981\u7acb\u523b\u4e3a\u4e4b\u51dd\u7ed3\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u94f6\u6749\u4eba\u624b\u4e2d\u6240\u6301\u4e4b\u4e66\uff0c\u7adf\u7136\u5c31\u662f\u90a3\u201c\u5b59\u8001\u7239\u201d\u4ece\u88f4\u73cf\u624b\u4e2d\u593a\u53bb\u7684\u4e24\u672c\uff0c\u8fd9\u4e24\u672c\u4e66\u7528\u9ed1 \u6851\u76ae\u7eb8\u505a\u7684\u5c01\u9762\uff0c\u88f4\u73cf\u4e0d\u77e5\u770b\u8fc7\u591a\u5c11\u904d\u4e86\uff0c\u6b64\u523b\u4ed6\u6839\u672c\u6bcb\u5eb8\u770b\u7b2c\u4e8c\u904d\uff0c\u5c31\u4e1d\u6beb\u518d\u4e5f\u6ca1\u6709\u7591 \u95ee\u3002 \u3000\u3000\u4ed6\u8111\u4e2d\u4e0d\u7981\u4e00\u9635\u6655\u7729\uff01\u8fd9\u4e24\u672c\u4e66\u5df2\u843d\u5230\u8fd9\u624b\u6bb5\u51b7\u9177\u7684\u94f6\u886b\u4eba\u624b\u4e0a\uff0c\u90a3\u4e48\u201c\u5b59\u8001\u7239\u201d\u7236\u5973 \u7684\u547d\u8fd0\uff0c\u81ea\u4e5f\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u5b59\u9526\u5e73\u7684\u90a3\u4e24\u53ea\u660e\u4eae\u800c\u59a9\u5a9a\u7684\u773c\u775b\uff0c\u4eb2\u5207\u800c\u6e29\u67d4\u7684\u773c\u6ce2\uff0c\u4f3c\u4e4e\u56db\u9762\u516b\u65b9\u5730\u6d41 \u5230\u4ed6\u8eab\u4e0a\uff0c\u6d41\u5165\u4ed6\u5fc3\u5e95\uff0c\u4ed6\u9a91\u5728\u9a6c\u4e0a\uff0c\u53ea\u89c9\u5f97\u8eab\u5b50\u865a\u98d8\u98d8\u5730\uff0c\u8111\u6d77\u7684\u601d\u6f6e\uff0c\u4e5f\u4e3a\u4e4b\u505c\u987f\u4e86\u3002 \u7b2c\u4e09\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u4e09\u7ae0 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u5927\u5730\u90fd\u4eff\u4f5b\u4e00\u8d77\u53d8\u4e86\u989c\u8272\uff0c\u90a3\u4e24\u672c\u4e66\u7684\u9ed1\u6851\u76ae\u7eb8\u5c01\u9762\u4e0a\uff0c\u4e5f\u4f3c\u4e4e\u90fd\u6cbe\u6ee1\u4e86\u6591 \u6591\u7684\u8840\u8ff9\uff0c\u90a3\u4e9b\u90fd\u662f\u66fe\u7ecf\u7231\u8fc7\u88f4\u73cf\uff0c\u4e5f\u66fe\u7ecf\u4e3a\u88f4\u73cf\u7231\u8fc7\u7684\u4eba\u8840\u8ff9\uff0c\u6240\u4e0d\u540c\u7684\u53ea\u662f\u4ed6\u4eec\u4f3c\u5df2\u4e0d \u518d\u7231\u88f4\u73cf\uff0c\u800c\u88f4\u73cf\u5374\u662f\u59cb\u7ec8\u7231\u7740\u4ed6\u4eec\u7684\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u6240\u53d7\u8fc7\u7684\u6298\u78e8\u5df2\u7ecf\u591f\u591a\u4e86\uff0c\u591a\u5f97\u5df2\u8db3\u591f\u4f7f\u4ed6\u7684\u60c5\u611f\u53d8\u5f97\u51b7\u9177\u4e00\u4e9b\uff0c\u4f46\u4e0d\u77e5\u662f\u4ed6\u6bd4 \u522b\u4eba\u90fd\u806a\u660e\u4e9b\u6291\u6216\u662f\u90fd\u7b28\u4e9b\uff0c\u8fd9\u4e9b\u632b\u6298\uff0c\u975e\u4f46\u672a\u80fd\u6d88\u78e8\u53bb\u4ed6\u751f\u547d\u7684\u52c7\u6c14\uff0c\u4e5f\u672a\u80fd\u51b7\u5374\u4ed6\u70ed \u60c5\uff0c\u751f\u547d\u867d\u7136\u574e\u5777\uff0c\u4eba\u4eec\u867d\u7136\u51b7\u9177\uff0c\u4ed6\u5374\u662f\u4ecd\u7136\u70ed\u7231\u7740\u4ed6\u4eec\u7684\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5750\u5728\u9a6c\u4e0a\uff0c\u5fc5\u987b\u975e\u5e38\u52aa\u529b\u5730\u652f\u6301\u7740\u81ea\u5df1\uff0c\u624d\u4e0d\u81f4\u4ece\u9a6c\u4e0a\u8dcc\u4e0b\u6765\u3002 \u3000\u3000\u6709\u98ce\u5439\u8fc7\uff0c\u5439\u5f97\u4ed6\u5bf9\u9762\u7684\u5343\u624b\u4e66\u751f\u8eab\u4e0a\u7684\u94f6\u7070\u8272\u8863\u8882\u98d8\u98d8\u626c\u8d77\uff0c\u4e5f\u5439\u5f97\u5343\u624b\u4e66\u751f\u6258\u5728\u638c \u5fc3\u7684\u90a3\u4e24\u672c\u4e66\u7684\u518c\u9875\u98d8\u98d8\u626c\u8d77\u3002 \u3000\u3000\u88f4\u73cf\u7684\u76ee\u5149\u4ece\u8fd9\u4e24\u672c\u5df2\u4e3a\u4ed6\u5e26\u6765\u8bb8\u591a\u707e\u7978\u7684\u4e66\uff0c\u5446\u6ede\u5730\u79fb\u5230\u90a3\u5728\u4ed6\u773c\u4e2d\u4f3c\u4e4e\u9ad8\u4e0d\u53ef\u6500\u7684 \u94f6\u886b\u4eba\u8eab\u4e0a\uff0c\u5374\u89c1\u5343\u624b\u4e66\u751f\u4e25\u5cfb\u7684\u9762\u5b54\uff0c\u6b64\u523b\u7adf\u50cf\u662f\u6cdb\u51fa\u4e00\u4e1d\u6e29\u6696\u7684\u7b11\u610f\u3002 \u3000\u3000\u201c\u6e29\u6696\u201d\uff0c\u662f\u88f4\u73cf\u591a\u4e48\u6025\u5207\u5730\u6e34\u671b\u7740\u7684\u4e1c\u897f\u5440\uff0c\u4e8e\u662f\u4ed6\u62ac\u8d77\u5934\u6765\uff0c\u52c7\u6562\u5730\u671b\u7740\u8fd9\u51b7\u9177\u7684 \u94f6\u886b\u4eba\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u88f4\u73cf\u53ea\u89c9\u5f97\u51b7\u9177\u7684\u4eba\u76ee\u5149\u4e2d\u539f\u6765\u4e5f\u662f\u6709\u7740\u4eba\u7c7b\u7684\u60c5\u611f\u7684\u3002\u53ea\u662f\uff0c\u4ed6 \u5374\u65e0\u6cd5\u4e86\u89e3\u8fd9\u79cd\u60c5\u611f\u7a76\u7adf\u662f\u5728\u8868\u793a\u7740\u4ec0\u4e48\u610f\u4e49\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u591a\u4e48\u5e0c\u671b\u81ea\u5df1\u80fd\u591f\u542c\u5f97\u89c1\uff0c\u8bf4\u5f97\u51fa\uff0c\u56e0\u4e3a\u6b64\u523b\u4ed6\u5fc3\u4e2d\u7591\u56e2\u91cd\u91cd\uff0c\u6068\u4e0d\u5f97\u7acb\u523b\u4fbf\u80fd\u5f97\u5230 \u89e3\u7b54\uff0c\u4e8e\u662f\u4ed6\u4f38\u51fa\u624b\uff0c\u6307\u4e86\u6307\u90a3\u4e24\u672c\u4e66\uff0c\u4f46\u662f\uff0c\u4ed6\u5374\u65e0\u6cd5\u6bd4\u51fa\u4e00\u4e2a\u80fd\u4ee3\u8868\u4ed6\u5fc3\u4e2d\u610f\u5ff5\u7684\u624b\u5f0f \u6765\u3002\u4e09\u4ed6\u65b9\u81ea\u6574\u987f\u7740\u81ea\u5df1\u7d0a\u4e71\u7684\u601d\u7eea\uff0c\u54ea\u77e5\u4e00\u9635\u65e0\u6bd4\u5f3a\u52b2\u7684\u52b2\u98ce\uff0c\u84e6\u5730\u81ea\u9053\u65c1\u53f3\u4fa7\u7684\u6811\u6728\u4e2d \u7a7f\u51fa\uff0c\u201c\u547c\u201d\u5730\u4e00\u58f0\uff0c\u7adf\u5c06\u5343\u624b\u4e66\u751f\u6258\u5728\u638c\u5fc3\u7684\u90a3\u4e24\u672c\u4e66\uff0c\u8fdc\u8fdc\u5439\u5230\u5730\u4e0a\uff0c\u5750\u5728\u9a6c\u4e0a\u7684\u88f4 \u73cf\uff0c\u8eab\u5f62\u6447\u4e86\u4e24\u6447\uff0c\u4fbf\u4e5f\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u7684\u8eab\u5f62\uff0c\u201c\u5657\u201d\u5730\uff0c\u7adf\u4ece\u9a6c\u978d\u4e0a\u8dcc\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c31\u5728\u88f4\u73cf\u8eab\u5f62\u843d\u5730\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u9053\u65c1\u5de6\u4fa7\u7684\u6797\u6728\u4e2d\uff0c\u500f\u7136\u63a0\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u7535\u4e5f\u4f3c\u5730\u7a9c\u5230 \u9a6c\u524d\uff0c\u4f38\u624b\u4e00\u6284\uff0c\u5c06\u521a\u521a\u843d\u5728\u5730\u4e0a\u7684\u4e66\u6284\u5728\u624b\u4e0a\uff0c\u8eab\u5f62\u4e00\u5f13\uff0c\u500f\u7136\u81ea\u9a6c\u8179\u4e0b\u7a7f\u8fc7\uff0c\u63a0\u5165\u53f3\u4fa7 \u6797\u6728\u91cc\u3002 \u3000\u3000\u503c\u5f97\u9057\u61be\u7684\u662f\uff1a\u4eba\u4eec\u6c38\u8fdc\u65e0\u6cd5\u5c06\u5728\u7535\u95ea\u800c\u8fc7\u7684\u90a3\u4e00\u5239\u90a3\u91cc\u540c\u65f6\u53d1\u751f\u7684\u4e8b\uff0c\u7528\u540c\u6837\u7684\u901f\u5ea6 \u63cf\u8ff0\u51fa\u6765\uff0c\u6b64\u523b\u8fd9\u5f3a\u98ce\u51fa\u6797\uff0c\u4e66\u518c\u843d\u5730\uff0c\u88f4\u73cf\u5760\u9a6c\uff0c\u4eba\u5f71\u63a0\u6765\uff0c\u4fbf\u51e0\u4e4e\u662f\u5728\u540c\u4e00\u5239\u90a3\u4e2d\u53d1\u751f \u7684\u3002 \u3000\u3000\u88f4\u73cf\u773c\u524d\u4eba\u5f71\u65b9\u81ea\u4e00\u82b1\uff0c\u90a3\u5343\u624b\u4e66\u751f\u9762\u5bb9\u4e5f\u4e3a\u4e4b\u9aa4\u53d8\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u7a81\u7136\u63a0\u8d77\uff0c\u51cc\u7a7a \u4e00\u4e2a\u7ffb\u8eab\uff0c\u4fbf\u4e5f\u7bad\u4e5f\u4f3c\u5730\u63a0\u5165\u6797\u4e2d\u3002 \u3000\u3000\u88f4\u73cf\u7684\u76ee\u5149\u867d\u5feb\uff0c\u5374\u7adf\u4e5f\u8ddf\u4e0d\u53ca\u6b64\u523b\u7684\u53d8\u5316\uff0c\u4ed6\u6323\u624e\u7740\u4ece\u5730\u4e0a\u722c\u4e86\u8d77\u6765\uff0c\u76ee\u5149\u56db\u626b\uff0c\u53ea \u89c1\u6797\u6728\u4f9d\u7136\uff0c\u679d\u53f6\u5fae\u7c38\uff0c\u4eba\u5f71\u5374\u6e3a\uff0c\u6797\u6728\u63a9\u6620\u4e2d\u7684\u697c\u9601\uff0c\u4e5f\u4ecd\u7136\u9759\u6084\u6084\u5730\u77d7\u7acb\u5728\u90a3\u91cc\uff0c\u8fd9\u53d8 \u5316\u867d\u7136\u6765\u5f97\u7a81\u7136\u800c\u5de8\u5927\uff0c\u7136\u800c\u5927\u5730\u5374\u5e76\u672a\u53d7\u5230\u4e1d\u6beb\u5f71\u54cd\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u629a\u6478\u4e00\u4e0b\u8eab\u4e0a\u88ab\u8dcc\u75db\u7684\u5730\u65b9\uff0c\u5fc3\u4e2d\u832b\u7136\u4e00\u7247\uff0c\u5bf9\u4e8e\u4e16\u95f4\u7684\u4e00\u5207\u53d8\u653e\uff0c\u4ed6\u65e2\u4e0d\u77e5\u4ece \u4f55\u800c\u6765\uff0c\u4ea6\u4e0d\u77e5\u4ece\u4f55\u800c\u53bb\uff0c\u8fd9\u4e9b\u53d8\u6545\u7eb5\u7136\u90fd\u6df1\u5207\u5730\u5f71\u54cd\u4e86\u4ed6\uff0c\u751a\u81f3\u4e25\u91cd\u7684\u635f\u5bb3\u4e86\u4ed6\uff0c\u4f46\u4ed6\u9664 \u4e86\u9ed8\u9ed8\u5730\u5fcd\u53d7\u4e4b\u5916\uff0c\u5c31\u4f3c\u4e4e\u518d\u65e0\u522b\u7684\u529e\u6cd5\u53ef\u60f3\u3002 \u3000\u3000\u91cd\u91cd\u7684\u7591\u56e2\uff0c\u5728\u4ed6\u5fc3\u80f8\u4e2d\u51dd\u7ed3\u6210\u4e00\u5757\u6c89\u91cd\u7684\u77f3\u5757\uff0c\u4ed6\u6068\u4e0d\u80fd\u6495\u88c2\u81ea\u5df1\u7684\u80f8\u819b\uff0c\u5c06\u8fd9\u77f3\u5757 \u53d6\u51fa\u6765\uff0c\u8fdc\u8fdc\u629b\u5230\u4e00\u8fb9\u53bb\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u5728\u4ed6\u5e74\u7eaa\u6781\u5e7c\u7684\u65f6\u5019\uff0c\u4ed6\u7239\u7239\u66fe\u7ecf\u5bf9\u4ed6\u8bf4\u8fc7\uff0c\u806a\u660e\u7684\u4eba\u6c38\u8fdc\u4e0d\u8981\u7737\u604b\u8fc7\u53bb\uff0c\u671f\u671b \u5c06\u6765\uff0c\u800c\u8f7b\u8f7b\u653e\u8fc7\u73b0\u5728\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u867d\u4e0d\u7737\u604b\u8fc7\u53bb\uff0c\u56e0\u4e3a\u4ed6\u4e00\u751f\u4e2d\u5e76\u6ca1\u6709\u4ec0\u4e48\u503c\u5f97\u7737\u604b\u7684\u4e8b\u3002 \u3000\u3000\u800c\u5c06\u6765\u7684\u4e8b\u5374\u4e5f\u662f\u832b\u7136\u4e00\u7247\uff0c\u4f46\u201c\u73b0\u5728\u201d\uff0c\u73b0\u5728\u4ed6\u4e0d\u4e5f\u662f\u7a7a\u7a7a\u8361\u8361\u7684\u5417\uff1f\u4e16\u95f4\u53ef\u6709\u4ec0\u4e48 \u4e8b\u662f\u4ed6\u80fd\u591f\u6539\u53d8\u7684\uff0c\u662f\u4ed6\u80fd\u591f\u521b\u9020\u7684\u5462\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u832b\u7136\u722c\u4e0a\u4e86\u9a6c\uff0c\u4ed6\u786e\u4fe1\u81ea\u5df1\uff0c\u53ea\u8981\u6709\u4e00\u4e2a\u76ee\u6807\u662f\u4ed6\u80fd\u591f\u8ffd\u5bfb \u7684\uff0c\u4ed6\u5c31\u4f1a\u6bd5\u751f\u5c3d\u5168\u529b\u53bb\u8ffd\u5bfb\u5b83\u3002\u7eb5\u7136\u5403\u5c3d\u4e86\u5343\u8f9b\u4e07\u82e6\uff0c\u53d7\u5c3d\u79cd\u79cd\u6298\u78e8\uff0c\u4ed6\u90fd\u4e0d\u4f1a\u76b1\u4e00\u76b1\u7709 \u5934\u3002 \u3000\u3000\u201c\u7236\u4ec7\u201d\uff0c\u5728\u4ed6\u5fc3\u4e2d\u867d\u7136\u4ecd\u5f88\u6df1\u523b\uff0c\u4f46\u5374\u5df2\u662f\u975e\u5e38\u9065\u8fdc\u7684\u4e86\uff0c\u56e0\u4e3a\uff0c\u4ed6\u77e5\u9053\u4ed6\u7684\u6740\u7236\u4ec7 \u4eba\uff0c\u5df2\u6b7b\u5728\u4e2d\u5dde\u4e00\u5251\u7684\u638c\u4e0b\uff0c\u4f46\u662f\u90a3\u4efd\u4e45\u88ab\u4eba\u4eec\u5c48\u8fb1\u548c\u8f7b\u8d31\u7684\u611f\u89c9\uff0c\u5374\u5728\u4ed6\u5fc3\u4e2d\u53d8\u6210\u4e86\u65e0\u6bd4 \u6c89\u91cd\u7684\u8d1f\u62c5\uff0c\u4ed6\u5bf9\u81ea\u5df1\u7684\u671f\u671b\uff0c\u6a80\u6587\u742a\u7684\u5a07\u7b11\uff0c\u5b59\u9526\u5e73\u7684\u773c\u6ce2\uff0c\u4f7f\u5f97\u4ed6\u8fd9\u4efd\u8d1f\u62c5\u66f4\u6c89\u91cd\u4e86 \u4e9b\u3002 \u3000\u3000\u7136\u800c\u8fd9\u4e00\u5207\u4e8b\u90fd\u4f3c\u4e4e\u90fd\u4e0d\u662f\u4ed6\u6b64\u523b\u80fd\u591f\u4f01\u53ca\u7684\uff0c\u90a3\u4e48\uff0c\u4ed6\u53c8\u80fd\u505a\u4e9b\u4ec0\u4e48\u6765\u6539\u53d8\u8fd9\u4e9b\u5462\uff1f \u3000\u3000\u9664\u4e86\u5bf9\u751f\u547d\u7684\u4fe1\u5ff5\u4e4b\u5916\uff0c\u8fd9\u5b64\u82e6\u7684\u5c11\u5e74\u5c31\u518d\u65e0\u5176\u4ed6\u7684\u4e1c\u897f\u4e86\u3002 \u3000\u3000\u7b56\u9a6c\u51fa\u6797\uff0c\u832b\u7136\u4e45\u4e4b\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u8be5\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff0c\u6cbf\u7740\u5927\u9053\u8d70\u4e86\u4e00\u4f1a\u513f\uff0c\u4ed6 \u53c8\u56de\u5230\u65b9\u624d\u90a3\u4e09\u5c94\u8def\u53e3\uff0c\u671b\u7740\u5206\u6b67\u5728\u4ed6\u9762\u524d\u7684\u4e24\u6761\u8def\uff0c\u4ed6\u6697\u4e2d\u4e00\u54ac\u7259\uff0c\u60f3\u7b14\u76f4\u5730\u5411\u524d\u8d70\u3002 \u3000\u3000\u4f46\u4ed6\u5750\u4e0b\u7684\u9a6c\uff0c\u5374\u4f3c\u4e0d\u542c\u4ed6\u7684\u4f7f\u5524\uff0c\u9a6c\u9996\u4e00\u504f\uff0c\u7adf\u5f80\u53e6\u4e00\u6761\u8def\u8d70\u53bb\uff0c\u88f4\u73cf\u53ea\u89c9\u5fc3\u80f8\u4e4b \u4e2d\uff0c\u6012\u706b\u4e0a\u51b2\uff0c\u731b\u5730\u4e00\u62c9\u7f30\u7ef3\uff0c\u60f3\u5c06\u9a6c\u62c9\u5230\u4e00\u6761\u4ed6\u81ea\u5df1\u60f3\u8d70\u7684\u8def\u4e0a\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u5339\u5065\u9a6c\u6602\u9996\u4e00\u58f0\u957f\u5636\uff0c\u5374\u5c06\u88f4\u73cf\u4ece\u9a6c\u80cc\u4e0a\u6380\u4e86\u4e0b\u6765\uff0c\u653e\u8e44\u5954\u53bb\uff0c\u88f4\u73cf\u7ffb\u8eab\u722c\u4e86\u8d77 \u6765\uff0c\u62fe\u8d77\u4e00\u5757\u77f3\u5934\uff0c\u624b\u81c2\u201c\u547c\u201d\u5730\u4e00\u62a1\uff0c\u63b7\u5411\u90a3\u5339\u9a6c\uff0c\u4f46\u6b6a\u9a6c\u5374\u65e9\u5df2\u8d70\u5f97\u8fdc\u4e86\uff0c\u5e72\u71e5\u4ec5\u80fd\u5230 \u9a6c\u540e\u626c\u8d77\u5f97\u6c99\u5c18\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u62cd\u4e86\u62cd\u8eab\u4e0a\u7684\u5c18\u571f\uff0c\u8f6c\u8eab\u8d70\u5411\u81ea\u5df1\u8981\u8d70\u7684\u90a3\u6761\u8def\uff0c\u4ed6\u5bf9\u547d\u8fd0\u7684\u53cd\u6297\uff0c\u7b2c\u4e00\u6b21\u5f97\u5230\u80dc \u5229\uff0c\u867d\u7136\u4ed6\u7684\u5bf9\u624b\u4ec5\u662f\u4e00\u5339\u9a6c\u800c\u5df2\u3002 \u3000\u3000\u9a84\u9633\u9690\u6ca1\u5728\u897f\u65b9\u7684\u7fa4\u5c71\u4e4b\u540e\uff0c\u5927\u5730\u7531\u9ec4\u660f\u8f6c\u5165\u9ed1\u591c\u3002 \u3000\u3000\u82cd\u82cd\u66ae\u972d\u4e4b\u4e2d\uff0c\u88f4\u73cf\u8e6d\u8e6d\u72ec\u884c\uff0c\u9965\u997f\u3001\u75b2\u52b3\uff0c\u4f7f\u5f97\u4ed6\u4e24\u6761\u817f\u5f2f\u5f97\u6709\u5982\u5343\u94a7\u822c\u6c89\u91cd\uff0c\u4f46 \u662f\uff0c\u4ed6\u5374\u5e76\u4e0d\u540e\u6094\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e0d\u9a91\u5728\u90a3\u5339\u9a6c\u4e0a\uff0c\u8fd9\u6b63\u5982\u4ed6\u4ece\u4e0d\u540e\u6094\u81ea\u5df1\u4ece\u90a3\u53ef\u83b7\u4e30\u8863\u8db3\u98df\u7684 \u98de\u9f99\u9556\u5c40\u9003\u51fa\u4e00\u6837\u3002 \u3000\u3000\u57ce\u5ed3\u7684\u5f71\u5b50\u8fd1\u4e86\uff0c\u88f4\u73cf\u7684\u811a\u6b65\u4e5f\u5feb\u4e86\uff0c\u8d70\u5230\u57ce\u95e8\u53e3\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u4e0a\u9762\u4f9d\u7a00\u5199\u7740\u201c\u9547\u6c5f\u201d \u4e24\u5b57\uff0c\u4e8e\u662f\u4ed6\u8fc8\u5f00\u5927\u6b65\uff0c\u8d70\u4eba\u57ce\u53bb\u3002 \u3000\u3000\u591c\u5e02\u5c06\u6536\uff0c\u4ed6\u867d\u7136\u6602\u9996\u800c\u884c\uff0c\u5176\u5b9e\u773c\u524d\u5df2\u7ecf\u997f\u5f97\u53d1\u9ed1\uff0c\u8033\u7554\u5ffd\u7136\u201c\u5f53\u201d\u5730\u4e00\u58f0\u8f7b\u54cd\uff0c\u8d70 \u5728\u4ed6\u524d\u9762\u7684\u6c49\u5b50\uff0c\u843d\u4e0b\u4e00\u4e2a\u50cf\u662f\u663e\u4e3a\u6c89\u91cd\u7684\u94b1\u888b\u6765\uff0c\u4ed6\u8d76\u524d\u4e24\u6b65\uff0c\u5c06\u94b1\u5305\u62fe\u5728\u624b\u4e0a\uff0c\u8ffd\u4e0a \u53bb\uff0c\u8fd8\u7ed9\u4e86\u90a3\u5927\u610f\u7684\u884c\u4eba\uff0c\u54ea\u77e5\u90a3\u4eba\u5374\u6076\u72e0\u72e0\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u5288\u624b\u5c06\u94b1\u888b\u593a\u4e86\u8fc7\u53bb\uff0c\u5634\u76ae\u52a8\u4e86 \u4e24\u52a8\u3002 \u3000\u3000\u6389\u9996\u4e0d\u987e\u800c\u53bb\u3002 \u3000\u3000\u88f4\u73cf\u6014\u4e86\u6014\uff0c\u4ed6\u4e0d\u77e5\u9053\u90a3\u4eba\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4ed6\u5982\u6b64\uff0c\u4f46\u662f\u4ed6\u5fc3\u80f8\u4e4b\u95f4\uff0c\u5374\u4ecd\u7136\u56e0\u6709\u6b64\u4e8b\u6709\u4e86 \u4e9b\u8bb8\u6109\u5feb\uff0c\u56e0\u4e3a\u4ed6\u5df2\u5e2e\u52a9\u4e86\u522b\u4eba\uff0c\u5df2\u4eab\u53d7\u5230\u52a9\u4eba\u7684\u6109\u5feb\uff0c\u81f3\u4e8e\u522b\u4eba\u5bf9\u4ed6\u7684\u6001\u5ea6\uff0c\u5e76\u4e0d\u653e\u5728\u4ed6 \u7684\u5fc3\u4e0a\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u4ece\u672a\u60f3\u5230\uff0c\u5047\u5982\u4ed6\u5c06\u90a3\u94b1\u888b\u653e\u8fdb\u81ea\u5df1\u6000\u91cc\uff0c\u90a3\u4e48\u4ed6\u81f3\u5c11\u4e0d\u5fc5\u518d\u56e0\u9965\u997f\u800c\u75db\u82e6\u4e86 \u5440\u3002 \u3000\u3000\u7ecf\u8fc7\u51e0\u6761\u8857\uff0c\u4ed6\u5728\u4e00\u4e2a\u9634\u6697\u7684\u89d2\u843d\u91cc\u8737\u4f0f\u4e86\u8d77\u6765\uff0c\u6e10\u6e10\uff0c\u4ed6\u77e5\u9053\u4ed6\u7684\u75b2\u52b3\u8fd8\u5728\u9965\u997f\u4e4b \u4e0a\uff0c\u56e0\u4e3a\u4ed6\u5f88\u5feb\u5c31\u7761\u7740\u4e86\u3002 \u3000\u3000\u7b49\u5230\u4ed6\u9192\u6765\u7684\u65f6\u5019\uff0c\u5929\u5df2\u5927\u4eae\uff0c\u56a3\u54d7\u7684\u5e02\u58f0\uff0c\u4ed6\u867d\u65e0\u6cd5\u542c\u5230\uff0c\u4f46\u62e5\u6324\u7684\u4eba\u7fa4\uff0c\u4ed6\u5374\u53ef\u770b \u89c1\uff0c\u539f\u6765\u4ed6\u6628\u591c\u5b58\u8eab\u4e4b\u5730\uff0c\u7adf\u662f\u4e00\u4e2a\u5e02\u96c6\uff0c\u6b64\u523b\u65e9\u5e02\u5df2\u5347\uff0c\u644a\u8d29\u67ff\u6bd4\uff0c\u6709\u7684\u8d29\u5356\u83dc\u852c\uff0c\u6709\u7684 \u8d29\u5356\u5e03\u5e1b\uff0c\u6709\u7684\u7528\u7af9\u679d\u5728\u5730\u4e0a\u56f4\u4e86\u5708\u5b50\uff0c\u8d29\u5356\u9e21\u9e2d\u725b\u7f8a\u3002 \u3000\u3000\u88f4\u73cf\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u6253\u91cf\u7740\u56de\u4e0b\u7684\u4eba\u7fa4\uff0c\u7a81\u7136\u770b\u5230\u5bf9\u9762\u4e00\u5757\u7a7a\u5730\u4e0a\uff0c\u6b63\u5750\u7740\u4e00\u4e2a\u548c\u81ea\u5df1\u5e74 \u7eaa\u4eff\u4f5b\uff0c\u8863\u886b\u4e5f\u4e00\u6837\u697c\u8934\u7684\u5c11\u5e74\u3002\u6b63\u5c0f\u5fc3\u5730\u4ece\u8eab\u4fa7\u4e00\u4e2a\u6781\u5927\u7684\u5e03\u888b\u91cc\uff0c\u53d6\u51fa\u4e00\u5757\u5757\u7816\u5934\uff0c\u8c28 \u614e\u5730\u653e\u5230\u5730\u4e0a\uff0c\u642d\u6210\u4e00\u4e2a\u5c0f\u7076\uff0c\u8fd9\u4e9b\u7816\u5934\u5df2\u88ab\u70df\u706b\u718f\u5f97\u53d1\u9ed1\uff0c\u7136\u800c\u90a3\u5c11\u5e74\u5374\u6781\u4e3a\u5c0f\u5fc3\u5730\u642c\u5f04 \u7740\u5b83\uff0c\u50cf\u662f\u751f\u6015\u78b0\u574f\u4e00\u4e9b\u4f3c\u7684\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u91cc\u5947\u602a\uff0c\u773c\u7741\u7741\u5730\u671b\u7740\u8fd9\u5c11\u5e74\uff0c\u5374\u89c1\u8fd9\u5c11\u5e74\u62ac\u8d77\u5934\u6765\uff0c\u4e5f\u671b\u4e86\u4e00\u773c\uff0c\u5e76\u4e14\u5fae\u7b11\u4e00 \u4e0b\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u9047\uff0c\u88f4\u73cf\u53ea\u89c9\u8fd9\u5c11\u5e74\u8863\u886b\u8934\u697c\uff0c\u4f46\u4e00\u53cc\u773c\u775b\uff0c\u5374\u70af\u7136\u53d1\u7740\u4eae\u5149\uff0c\u4f7f\u5f97\u4ed6\u770b\u8d77 \u6765\u6ca1\u6709\u4e00\u4e1d\u7325\u7410\u7684\u6837\u5b50\u3002 \u3000\u3000\u88f4\u73cf\u7ffb\u8eab\u5750\u8d77\u6765\uff0c\u66f4\u52a0\u7559\u610f\u5730\u671b\u7740\u4ed6\uff0c\u5374\u89c1\u4ed6\u53c8\u4ece\u5e03\u888b\u91cc\u9762\uff0c\u53d6\u51fa\u4e00\u4e9b\u5e72\u67f4\u67af\u679d\uff0c\u5728\u90a3 \u7816\u5934\u642d\u6210\u7684\u5c0f\u7076\u91cc\u9762\u751f\u8d77\u706b\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u706b\u751f\u7740\u4e86\uff0c\u4ed6\u53d6\u51fa\u4e00\u53e3\u6781\u5927\u7684\u94c1\u9505\uff0c\u67b6\u5728\u7076\u4e0a\uff0c\u53c8\u62ff\u4e86\u4e2a\u5c0f\u6c34\u6876\uff0c\u8dd1\u53bb\u5f04\u4e86 \u4e00\u6876\u6c34\uff0c\u5012\u5728\u94c1\u9505\u91cc\u3002 \u3000\u3000\u8fd9\u65f6\u4e0d\u4f46\u88f4\u73cf\u597d\u5947\u5730\u671b\u7740\u4ed6\uff0c\u4e00\u4e9b\u63d0\u7740\u83dc\u7bee\u7684\u8001\u59aa\u3001\u5987\u4eba\uff0c\u751a\u81f3\u4e00\u4e9b\u7231\u7ba1\u95f2\u4e8b\u7684\u6c49\u5b50\uff0c \u4e5f\u5728\u4ed6\u8eab\u65c1\u505c\u4e86\u4e0b\u6765\uff0c\u90fd\u60f3\u770b\u770b\u8fd9\u5c11\u5e74\u7a76\u7adf\u5f04\u7740\u4ec0\u4e48\u628a\u620f\uff0c\u4ed6\u5374\u50cf\u662f\u89c6\u82e5\u65e0\u7779\uff0c\u8f7b\u8f7b\u5730\u53f9\u4e86 \u53e3\u6c14\uff0c\u7f13\u7f13\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u84dd\u5e03\u5c0f\u5305\u6765\u3002 \u3000\u3000\u88f4\u73cf\u4e0d\u7981\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u4ed6\u8eab\u4fa7\uff0c\u53ea\u89c1\u8fd9\u5c11\u5e74\u6781\u4e3a\u5c0f\u5fc3\u800c\u8c28\u614e\u5730\u6253\u5f00\u90a3\u84dd\u5e03\u5c0f\u5305\uff0c\u91cc \u9762\u5305\u7684\u7adf\u662f\u4e00\u53ea\u94dc\u5236\u7684\u624b\u956f\u3002 \u3000\u3000\u4eba\u4eec\u4e0d\u7981\u5f00\u59cb\u4f4e\u8bed\u8d77\u6765\uff0c\u731c\u6d4b\u7740\u8fd9\u5c11\u5e74\u7a76\u7adf\u5728\u4e8e\u4ec0\u4e48\uff0c\u88f4\u73cf\u66f4\u662f\u5fc3\u91cc\u5947\u602a\uff0c\u51e0\u4e4e\u5c06\u81ea\u5df1 \u7684\u9965\u997f\u90fd\u5fd8\u4e86\uff0c\u773c\u775b\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u8fd9\u53ea\u94dc\u956f\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u5c11\u5e74\u7528\u4e24\u6839\u624b\u6307\u634f\u8d77\u94dc\u956f\uff0c\u653e\u5230\u773c\u524d\u4ed4\u7ec6\u5730\u770b\u4e86\u4e24\u773c\uff0c\u7136\u540e\u7f13\u7f13\u653e\u5728\u9505\u91cc\uff0c\u6c34\u9762 \u8d77\u4e86\u4e2a\u6f29\u6da1\uff0c\u94dc\u956f\u77ac\u5373\u6c89\u5230\u9505\u5e95\uff0c\u90a3\u5c11\u5e74\u773c\u671b\u5728\u9505\u91cc\uff0c\u6839\u672c\u671b\u4e5f\u4e0d\u671b\u56f4\u5728\u4ed6\u8eab\u524d\u7684\u4eba\u7fa4\u4e00 \u773c\u3002 \u3000\u3000\u4e00\u4e2a\u80a5\u7855\u5065\u58ee\u7684\u5987\u4eba\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u5fc3\u91cc\u7684\u597d\u5947\u5fc3\u201c\u5582\u201d\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u5c11\u5e74\u4eba\uff0c\u4f60\u8fd9 \u662f\u5728\u4e8e\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u76ee\u5149\u4e00\u62ac\uff0c\u5634\u89d2\u505a\u4e86\u4e2a\u975e\u5e38\u8f7b\u8511\u7684\u8868\u60c5\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u716e\u6c64\u3002\u201d \u3000\u3000\u5987\u4eba\u7684\u773c\u4e0a\u90fd\u77aa\u5706\u4e86\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u716e\u6c64\uff1f\u201d\u5979\u7528\u90a3\u53ea\u80a5\u539a\u7684\u624b\u638c\uff0c\u62b9\u4e86\u62b9\u81ea\u5df1\u7684\u773c\u775b\uff0c \u518d\u5411\u94c1\u9505\u77aa\u4e86\u4e24\u773c\uff0c\u60ca\u8be7\u5730\u63a5\u7740\u9053\uff1a\u201c\u7528\u8fd9\u53ea\u94dc\u956f\u716e\u6c64\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u524a\u8584\u7684\u5634\u5507\u5f80\u4e0b\u4e00\u6487\uff0c\u4f3c\u4e4e\u518d\u4e5f\u4e0d\u5c51\u56de\u7b54\u5979\u7684\u8bdd\uff0c\u8f7b\u8f7b\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u95ed\u8d77\u773c\u6765\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u56f4\u89c2\u7684\u4eba\u7fa4\u66f4\u60ca\u8bb6\u4e86\uff0c\u90fd\u8981\u770b\u8fd9\u4e2a\u94dc\u956f\u80fd\u716e\u51fa\u4ec0\u4e48\u6c64\u6765\u3002 \u3000\u3000\u88f4\u73cf\u867d\u7136\u542c\u4e0d\u5230\u4ed6\u4eec\u8bf4\u7684\u8bdd\uff0c\u4f46\u5fc3\u91cc\u7684\u597d\u5947\u5fc3\uff0c\u53cd\u800c\u66f4\u76db\u4e86\uff0c\u8d8a\u53d1\u820d\u4e0d\u5f97\u79bb\u5f00\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u9505\u91cc\u7684\u6c34\u6cb8\u4e86\uff0c\u90a3\u5c11\u5e74\u7741\u5f00\u773c\u6765\uff0c\u5f80\u7076\u91cc\u6dfb\u4e86\u51e0\u6bb5\u67af\u679d\uff0c\u7136\u540e\u53c8\u4ece\u5e03\u888b\u91cc \u53d6\u4e86\u4e2a\u6c64\u5319\u51fa\u6765\uff0c\u7528\u8863\u895f\u64e6\u4e86\u64e6\uff0c\u8200\u4e86\u5319\u9521\u91cc\u7684\u201c\u6c64\u201d\uff0c\u559d\u4e86\u4e00\u53e3\uff0c\u7136\u540e\u95ed\u8d77\u773c\u775b\uff0c\u8f7b\u8f7b\u53f9 \u4e86\u53e3\u6c14\uff0c\u81ea\u8bed\u9053\uff1a\u201c\u8981\u662f\u6709\u4e9b\u8471\u59dc\u5c31\u597d\u4e86\uff0c\u4e0d\u8fc7\u2014\u2014\u6ca1\u6709\u4e5f\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u4e00\u4e2a\u68b3\u7740\u4e24\u6839\u8fab\u5b50\u7684\u5c0f\u59d1\u5a18\uff0c\u7f9e\u6da9\u5730\u8d70\u51fa\u6765\uff0c\u624b\u91cc\u62ff\u7740\u4e9b\u8471\u59dc\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u653e\u5728\u8fd9\u5c11\u5e74 \u8eab\u4fa7\u7684\u5730\u4e0a\uff0c\u8138\u5df2\u7f9e\u5f97\u7ea2\u4e86\uff0c\u6389\u5934\u8d70\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u76ee\u5149\u4e00\u8f6c\uff0c\u773c\u4e2d\u6cdb\u8fc7\u4e00\u4e1d\u7b11\u610f\uff0c\u62ff\u8d77\u8471\u59dc\uff0c\u653e\u5728\u9505\u91cc\uff0c\u90a3\u80a5\u7855\u7684\u5987\u4eba\u5df2\u5fcd\u4e0d\u4f4f\u8dd1 \u4e86\u51fa\u6765\uff0c\u671f\u827e\u7740\u9053\uff1a\u201c\u6211\u60f3\u2026\u2026\u6211\u4e0d\u77e5\u9053\u2026\u2026\u518d\u653e\u4e00\u70b9\u9752\u83dc\u662f\u4e0d\u662f\u597d\u5403\u4e9b\uff1f\u201d\u624b\u91cc\u62ff\u7740\u4e00\u628a \u9752\u83dc\uff0c\u9001\u5230\u90a3\u5c11\u5e74\u7684\u9762\u524d\uff0c\u50cf\u662f\u552f\u6050\u4eba\u5bb6\u4e0d\u8981\u7684\u6837\u5b50\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e00\u8138\u5e76\u4e0d\u5341\u5206\u9ad8\u5174\u7684\u6837\u5b50\uff0c\u50cf\u662f\u4e0d\u9ad8\u5174\u6709\u4eba\u6765\u6253\u6270\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u65e0\u6240\u8c13\u3002\u201d\u7f13 \u7f13\u63a5\u8fc7\u90a3\u628a\u9752\u83dc\uff0c\u5341\u5206\u4e0d\u60c5\u613f\u5730\u653e\u5230\u9505\u91cc\u3002 \u3000\u3000\u9752\u83dc\u4e4b\u540e\uff0c\u597d\u5947\u7684\u4eba\u63a5\u8fde\u5c06\u8c46\u8150\u3001\u841d\u535c\uff0c\u751a\u81f3\u9e21\u86cb\u3001\u732a\u809d\uff0c\u9001\u5230\u8fd9\u5c11\u5e74\u7684\u9762\u524d\uff0c\u4ed6\u65e2\u4e0d \u8bf7\u6c42\uff0c\u4e5f\u4e0d\u62d2\u7edd\uff0c\u8138\u4e0a\u5e26\u7740\u4e00\u8138\u4e0d\u8010\u70e6\u7684\u795e\u60c5\uff0c\u5c06\u8fd9\u4e9b\u4e1c\u897f\u4e00\u8d77\u653e\u8fdb\u90a3\u53e3\u5927\u94c1\u9505\u91cc\u3002 \u3000\u3000\u4e0d\u7528\u7247\u523b\uff0c\u6d53\u90c1\u7684\u9999\u6c14\u4ece\u9505\u91cc\u5192\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e8e\u662f\u597d\u5947\u7684\u4eba\u4eec\u597d\u5947\u5fc3\u6ee1\u8db3\u4e86\uff0c\u4e00\u9762\u60ca\u53f9\u5730\u4f20\u8bed\u9053\uff1a\u201c\u4f60\u95fb\u95fb\uff0c\u8fd9\u5473\u9053\u591a\u9999\uff0c\u4f60\u77e5\u4e0d\u77e5 \u9053\uff0c\u8fd9\u662f\u94dc\u956f\u716e\u51fa\u6765\u7684\u6c64\u3002\u201d\u4e00\u9762\u6ee1\u8db3\u5730\u8d70\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4e8e\u662f\u88f4\u73cf\u7b11\u4e86\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u4f3c\u4e4e\u4e86\u89e3\u5230\u4e86\u4e00\u4e9b\u9053\u7406\u3002 \u3000\u3000\u90a3\u5c31\u662f\u4e16\u95f4\u6709\u4e9b\u4e1c\u897f\uff0c\u4f60\u82e5\u662f\u53bb\u8981\u6c42\uff0c\u4f60\u5c31\u6c38\u8fdc\u65e0\u6cd5\u5f97\u5230\uff0c\u4f46\u82e5\u4f60\u4e0d\u53bb\u8981\u6c42\uff0c\u53cd\u800c\u62d2 \u7edd\u2014\u2014\u81f3\u5c11\u88c5\u51fa\u62d2\u7edd\u7684\u6837\u5b50\uff0c\u90a3\u4e48\u4f60\u8981\u6c42\u4e0d\u5230\u7684\u4e1c\u897f\uff0c\u5c31\u53ef\u80fd\u9001\u5230\u4f60\u7684\u624b\u4e2d\u3002 \u3000\u3000\u987b\u77e5\u88f4\u73cf\u662f\u7edd\u9876\u806a\u660e\u4e4b\u4eba\uff0c\u6709\u4e9b\u4e8b\u4ed6\u5e76\u975e\u4e0d\u80fd\u4e86\u89e3\uff0c\u53ea\u662f\u4e0d\u613f\u610f\u4e86\u89e3\u800c\u5df2\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e5f\u7b11\u4e86\uff0c\u4e24\u4eba\u542b\u7b11\u4e92\u89c6\uff0c\u5f7c\u6b64\u5fc3\u4e2d\uff0c\u90fd\u6709\u4e00\u79cd\u53ef\u4ee5\u4e92\u76f8\u4f20\u9012\u7684\u60c5\u611f\uff0c\u800c\u8fd9\u79cd\u60c5 \u611f\uff0c\u5374\u662f\u88f4\u73cf\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u611f\u5230\u7684\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5411\u88f4\u73cf\u62db\u62db\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u6765\u5c1d\u5c1d\u6211\u8fd9\u9505\u94dc\u956f\u716e\u6210\u7684\u6c64\uff0c\u4fdd\u9669\u6bd4\u8001\u6bcd\u9e21\u716e \u7684\u6c64\u8fd8\u597d\u5403\u3002\u201d \u3000\u3000\u88f4\u73cf\u81ea\u7136\u542c\u4e0d\u5230\u4ed6\u8bf4\u7684\u8bdd\uff0c\u832b\u7136\u6447\u4e86\u6447\u5934\uff0c\u53c8\u6307\u4e86\u6307\u81ea\u5df1\u7684\u8033\u6735\u548c\u5634\uff0c\u4ed6\u4f3c\u4e4e\u6709\u4e00\u79cd\u611f \u89c9\uff0c\u90a3\u5c31\u662f\u4ed6\u5728\u8fd9\u5c11\u5e74\u9762\u524d\uff0c\u53ef\u4ee5\u8bf4\u51fa\u81ea\u5df1\u7684\u4e00\u5207\u5fc3\u4e8b\u6765\uff0c\u800c\u7528\u4e0d\u7740\u7f9e\u6da9\u4e5f\u4e0d\u4f1a\u4e0d\u5b89\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u9762\u4e0a\u9732\u51fa\u60ca\u8bb6\u4e4b\u8272\uff0c\u4f3c\u4e4e\u5728\u5947\u602a\u7740\u9762\u524d\u8fd9\u82f1\u4fca\u5c11\u5e74\uff0c\u600e\u4f1a\u662f\u4e2a\u53c8\u804b\u53c8\u54d1\u7684\u6b8b\u5e9f\uff0c \u76ee\u5149\u8f6c\u4e86\u4e24\u8f6c\uff0c\u7a81\u5730\u957f\u8eab\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u88f4\u73cf\u8eab\u524d\uff0c\u671b\u7740\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u624b\u62c9\u7740\u4e86\u4ed6\u7684\u81c2 \u8180\uff0c\u8d70\u5230\u90a3\u9505\u9999\u6c14\u56db\u6ea2\u7684\u70ed\u6c64\u65c1\u8fb9\uff0c\u4f60\u624b\u6307\u4e86\u6307\u81ea\u5df1\u7684\u5634\uff0c\u53c8\u4f38\u624b\u6307\u4e86\u6307\u88f4\u73cf\u7684\u5634\uff0c\u518d\u6307\u4e86 \u6307\u90a3\u9505\u70ed\u6c64\uff0c\u53c8\u662f\u4e00\u7b11\u3002 \u3000\u3000\u88f4\u73cf\u548c\u8fd9\u5c11\u5e74\u867d\u662f\u521d\u6b21\u8c0b\u9762\uff0c\u4f46\u5374\u5bf9\u4ed6\u5927\u6709\u597d\u611f\uff0c\u6b64\u523b\u89c1\u4e86\u4ed6\u5bf9\u81ea\u5df1\u7684\u795e\u60c5\uff0c\u65e2\u975e\u8f7b \u8511\uff0c\u4ea6\u975e\u601c\u60af\uff0c\u5374\u50cf\u662f\u4e00\u79cd\u6781\u613f\u548c\u81ea\u5df1\u4ea4\u670b\u53cb\u7684\u6837\u5b50\uff0c\u5fc3\u4e0b\u4e0d\u7981\u5927\u4e3a\u611f\u52a8\uff0c\u5374\u4e0d\u7981\u5fae\u5fae\u4e00 \u7b11\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u90a3\u5c11\u5e74\u9762\u4e0a\u9732\u51fa\u559c\u8272\uff0c\u65b9\u60f3\u628a\u88f4\u73cf\u4e00\u8d77\u62d6\u5230\u5730\u4e0a\u53bb\u5750\u3002 \u3000\u3000\u54ea\u77e5\u88f4\u73cf\u53c8\u6447\u4e86\u6447\u5934\uff0c\u4f38\u624b\u6307\u4e86\u6307\u5e02\u573a\u4e0a\u56a3\u56b7\u7684\u4eba\u7fa4\uff0c\u90a3\u5c11\u5e74\u806a\u660e\u7edd\u9876\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u5df2 \u77e5\u9053\u4e86\u4ed6\u7684\u7528\u610f\uff0c\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u5144\u53f0\u4e0d\u613f\u5728\u8fd9\u4e48\u591a\u4fd7\u4eba\u9762\u524d\uff0c\u548c\u2014\u2014\u201d\u8bdd\u65b9\u8bf4\u5230\u4e00 \u534a\uff0c\u84e6\u5730\u60f3\u5230\u5bf9\u65b9\u662f\u4e2a\u804b\u5b50\uff0c\u8bdd\u58f0\u4fbf\u81ea\u500f\u7136\u987f\u4f4f\uff0c\u56de\u76ee\u671b\u7740\u88f4\u73cf\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u88f4\u73cf\u53ea\u89c9\u90a3\u5c11\u5e74\u76ee\u5149\u4e4b\u4e2d\uff0c\u4f3c\u4e4e\u6d41\u9732\u51fa\u4e00\u79cd\u81ea\u759a\u7684\u795e\u60c5\uff0c\u50cf\u662f\u751f\u6015\u4ed6\u65b9 \u624d\u53c8\u8bf4\u51fa\u8bdd\u6765\uff0c\u56e0\u800c\u523a\u75db\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e0d\u7981\u70ed\u8840\u6cb8\u817e\uff0c\u53cd\u624b\u4e00\u628a\uff0c\u7d27\u7d27\u63e1\u4f4f\u90a3\u5c11\u5e74\u7684\u624b\u638c\u3002 \u3000\u3000\u987b\u77e5\u88f4\u73cf\u4e00\u751f\u4e4b\u4e2d\uff0c\u98a0\u6c9b\u5b64\u82e6\uff0c\u522b\u4eba\u5bf9\u4ed6\u4e0d\u662f\u8f7b\u8511\uff0c\u5c31\u662f\u4fae\u8fb1\uff0c\u7eb5\u7136\u9047\u7740\u51e0\u4e2a\u5bf9\u4ed6\u597d\u7684 \u4eba\uff0c\u4f46\u90a3\u4e5f\u4ec5\u662f\u51fa\u4e8e\u601c\u60af\u800c\u5df2\u3002 \u3000\u3000\u6b64\u523b\u89c1\u4e86\u8fd9\u5c11\u5e74\u7684\u795e\u6001\uff0c\u90fd\u662f\u5b8c\u5168\u5c06\u81ea\u5df1\u4ee5\u670b\u53cb\u76f8\u5f85\uff0c\u4ed6\u672c\u662f\u81f3\u60c5\u81f3\u6027\u4e4b\u4eba\uff0c\u53ea\u8981\u522b\u4eba \u5bf9\u4ed6\u7a0d\u5fae\u597d\u4e9b\uff0c\u4ed6\u7eb5\u7136\u4ee5\u6b7b\u62a5\u7b54\uff0c\u4ea6\u662f\u5728\u6240\u4e0d\u501f\uff0c\u4e00\u628a\u63e1\u4f4f\u90a3\u5c11\u5e74\u7684\u624b\uff0c\u773c\u4e2d\u7adf\u611f\u52a8\u5f97\u6d41\u4e0b \u6cea\u6765\u3002 \u3000\u3000\u5374\u4e0d\u77e5\u9053\u90a3\u5c11\u5e74\u4e5f\u662f\u751f\u6027\u5947\u7279\u4e4b\u4eba\uff0c\u4e00\u89c1\u88f4\u73cf\uff0c\u4e5f\u4e0d\u77e5\u600e\u5730\u4ece\u5fc3\u5e95\u5347\u51fa\u4e00\u4efd\u597d\u611f\uff0c\u6b64\u523b \u4e24\u4eba\u53cc\u624b\u7d27\u63e1\uff0c\u76ee\u5149\u76f8\u5bf9\uff0c\u867d\u662f\u521d\u6b21\u8c0b\u9762\uff0c\u4e00\u8bed\u672a\u901a\uff0c\u4f46\u5fc3\u91cc\u5374\u5404\u81ea\u6709\u7740\u4e00\u4efd\u8bf4\u4e0d\u51fa\u7684\u8212\u670d \u5feb\u6d3b\u7684\u611f\u89c9\uff0c\u5c31\u50cf\u662f\u79bb\u522b\u7ecf\u5e74\u7684\u8001\u53cb\uff0c\u4e00\u65e6\u5f02\u4e61\u91cd\u9022\u4f3c\u7684\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u5bf9\u51dd\u6ce8\uff0c\u90a3\u5c11\u5e74\u7a81\u5730\u8f69\u7709\u4e00\u7b11\uff0c\u677e\u5f00\u63e1\u4f4f\u88f4\u73cf\u7684\u624b\uff0c\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u5c06\u5730\u4e0a\u7684 \u6c64\u5319\u6742\u7269\uff0c\u53c8\u90fd\u629b\u5165\u5e03\u888b\uff0c\u7136\u540e\u5de6\u624b\u6284\u8d77\u888b\u5b50\uff0c\u5374\u5c06\u90a3\u76db\u6ee1\u4e00\u9505\u6cb8\u6c64\u94c1\u9505\uff0c\u7528\u53f3\u624b\u7684\u62c7\u3001 \u98df\u3001\u4e2d\u4e09\u6307\u631f\u4f4f\u9505\u8fb9\uff0c\u4e00\u628a\u63d0\u4e86\u8d77\u6765\uff0c\u671b\u7740\u88f4\u73cf\u7b11\u4e00\u7b11\uff0c\u8fc8\u5f00\u5927\u6b65\uff0c\u5411\u5e02\u96c6\u5916\u9762\u8d70\u53bb\uff0c\u8fde\u5730 \u4e0a\u7684\u90a3\u51e0\u5757\u7816\u5934\u4e5f\u4e0d\u8981\u4e86\u3002 \u3000\u3000\u5e02\u96c6\u4e0a\u7684\u4eba\u4eec\u867d\u662f\u6d41\u52a8\u4e0d\u606f\uff0c\u4f46\u90a3\u4e9b\u8d29\u5356\u83dc\u852c\u679c\u8089\u4ec0\u7269\u7684\u644a\u8d29\uff0c\u5bf9\u8fd9\u8863\u886b\u8934\u891b\u7684\u5c11\u5e74\uff0c \u672c\u5c31\u62b1\u7740\u4e00\u4efd\u597d\u5947\uff0c\u6b64\u523b\u89c1\u4ed6\u7adf\u4ee5\u4e09\u6307\u5c06\u90a3\u4e00\u9505\u76db\u5f97\u6ee1\u6ee1\u7684\u6cb8\u6c64\u631f\u5728\u624b\u91cc\uff0c\u5927\u6b65\u800c\u884c\uff0c\u4e0d\u89c9 \u90fd\u4e00\u4e2a\u4e2a\u60ca\u8bb6\u5f97\u8131\u53e3\u53eb\u51fa\u58f0\u6765\uff0c\u4e0d\u77e5\u8fd9\u5c11\u5e74\u7a76\u7adf\u662f\u4f55\u8bb8\u4eba\u7269\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u91cc\u4ea6\u662f\u4e00\u60ca\uff0c\u4ed6\u6b66\u529f\u867d\u5f31\uff0c\u4f46\u6709\u751f\u4ee5\u6765\uff0c\u63a5\u89e6\u5230\u7684\u4eba\u4ff1\u662f\u6b66\u6797\u4eba\u7269\uff0c\u5bf9\u6b66\u529f\u4e00 \u9053\uff0c\u5374\u662f\u8bc6\u8d27\u5f97\u5f88\uff0c\u6b64\u523b\u89c1\u4e86\u8fd9\u5c11\u5e74\u7684\u8fd9\u79cd\u60ca\u4eba\u6307\u529b\uff0c\u4e0d\u7981\u66f4\u662f\u60ca\u8bb6\uff0c\u5fc3\u4e2d\u6697\u53f9\uff0c\u5e38\u542c\u4eba\u8bf4 \u666e\u5929\u4e4b\u4e0b\uff0c\u4ff1\u662f\u5367\u864e\u85cf\u9f99\u4e4b\u5730\uff0c\u98ce\u5c18\u4e4b\u4e2d\uff0c\u5c24\u591a\u5f02\u4eba\uff0c\u8fd9\u5e74\u7eaa\u770b\u6765\u8fd8\u6bd4\u81ea\u5df1\u8f7b\u7684\u5c11\u5e74\uff0c\u7adf\u6709 \u5982\u6b64\u6b66\u529f\uff0c\u6b64\u8bdd\u679c\u662f\u4e0d\u865a\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u53c8\u60f3\u5230\u81ea\u5df1\uff0c\u4e0d\u7981\u6068\u8d77\u81ea\u5df1\u7684\u65e0\u7528\uff0c\u6697\u53f9\u4e00\u58f0\uff0c\u5374\u89c1\u90a3\u5c11\u5e74\u5df2\u9a7b\u8db3\u505c\u7740\uff0c \u56de\u5934\u542b\u7b11\u671b\u7740\u81ea\u5df1\uff0c\u76ee\u5149\u4e4b\u4e2d\uff0c\u6ee1\u542b\u7740\u771f\u631a\u7684\u8868\u60c5\uff0c\u4e0d\u7981\u4e5f\u4e3a\u4e4b\u8f69\u7709\u4e00\u7b11\uff0c\u5927\u6b65\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u624b\u91cc\u63d0\u7740\u90a3\u4e48\u6c89\u91cd\u7684\u94c1\u9505\uff0c\u811a\u4e0b\u5374\u4ecd\u7136\u4ece\u5bb9\u81ea\u5982\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u5403\u529b\u7684\u6837\u5b50\uff0c\u88f4\u73cf \u5168\u529b\u8fc8\u6b65\uff0c\u624d\u80fd\u7d27\u7d27\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u8def\u4e0a\u884c\u4eba\uff0c\u89c1\u4e86\u4ed6\u4eec\uff0c\u90fd\u4ee5\u60ca\u8be7\u7684\u76ee\u5149\u4fa7\u76ee\u800c\u671b\uff0c\u90a3\u5c11\u5e74\u5374\u6839\u672c\u6ca1\u6709\u770b\u5728\u773c\u91cc\uff0c\u5e26\u7740\u88f4 \u73cf\u7a7f\u8857\u5165\u5df7\uff0c\u88f4\u73cf\u4e5f\u4e0d\u77e5\u4ed6\u8981\u5230\u4ec0\u4e48\u5730\u65b9\uff0c\u54ea\u77e5\u8d70\u4e86\u534a\u664c\uff0c\u5374\u5df2\u8d70\u5230\u57ce\u5916\u4e86\u3002 \u3000\u3000\u51fa\u57ce\u4e4b\u5916\uff0c\u90a3\u5c11\u5e74\u5140\u81ea\u505c\u6b65\uff0c\u9505\u91cc\u7684\u6c64\uff0c\u70ed\u6c14\u8d8a\u6765\u8d8a\u5c11\uff0c\u9a6c\u4e0a\u5c31\u8981\u51b7\u4e86\uff0c\u90a3\u5c11\u5e74\u7528\u9f3b\u5b50 \u95fb\u4e86\u4e00\u4e0b\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5374\u53c8\u5411\u88f4\u73cf\u4e00\u7b11\uff0c\u53c8\u5f80\u524d\u8d70\u4e86\u534a\u664c\uff0c\u8d70\u5230\u4e00\u4e2a\u4e0a\u4e18\u4e0a\uff0c\u653e\u4e0b\u624b\u91cc\u7684\u94c1 \u9505\u548c\u5e03\u888b\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u56db\u4e0b\u5212\u4e86\u4e2a\u5708\u5b50\uff0c\u4ef0\u5929\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u88f4\u73cf\u56db\u4e0b\u4e00\u671b\uff0c\u53ea\u89c1\u56db\u91ce\u4e00\u7247\u9752\u8471\uff0c\u6797\u6728\u7530\u7574\uff0c\u4ff1\u6536\u773c\u5e18\uff0c\u5374\u4e0d\u89c1\u534a\u4e2a\u4eba\u5f71\uff0c\u4e0d\u89c9\u4ea6\u4e3a \u4e4b\u4e00\u7b11\uff0c\u80f8\u4e2d\u79ef\u90c1\uff0c\u6d88\u53bb\u4e0d\u5c11\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5c06\u5927\u9505\u653e\u5230\u77f3\u4e0a\uff0c\u53c8\u5f04\u4e86\u4e24\u5757\u77f3\u5934\uff0c\u548c\u88f4\u73cf\u4e00\u4eba\u5750\u4e86\u4e00\u5757\uff0c\u4ece\u5e03\u888b\u4e4b\u4e2d\uff0c\u62ff\u4e86\u4e00 \u5927\u4e00\u5c0f\u4e24\u53ea\u6c64\u5319\u6765\uff0c\u5c06\u5927\u7684\u4ea4\u7ed9\u88f4\u73cf\uff0c\u7528\u5c0f\u7684\u5728\u9505\u91cc\u8fde\u6c64\u5e26\u83dc\uff0c\u6ee1\u6ee1\u8200\u4e86\u4e00\u5319\uff0c\u987f\u65f6\u5927\u5403\u8d77 \u6765\u3002 \u3000\u3000\u88f4\u73cf\u65e9\u5c31\u9965\u706b\u4e2d\u70e7\uff0c\u6b64\u523b\u4e5f\u4e0d\u518d\u5ba2\u6c14\uff0c\u4e5f\u8200\u4e86\u4e00\u5319\uff0c\u653e\u5230\u53e3\u4e2d\uff0c\u4e00\u5c1d\u4e4b\u4e0b\uff0c\u53ea\u89c9\u82b3\u9999\u751c \u7f8e\uff0c\u65e0\u4e0e\u4f26\u6bd4\uff0c\u751f\u5e73\u7f8e\u5473\uff0c\u83ab\u8fc7\u4e8e\u6b64\u77e3\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5403\u4e86\u4e24\u5319\uff0c\u5ffd\u5730\u653e\u4e0b\u6c64\u5319\uff0c\u4ece\u5e03\u888b\u4e2d\u638f\u51fa\u4e00\u4e2a\u9152\u846b\u82a6\u6765\uff0c\u62d4\u5f00\u585e\u5b50\uff0c\u559d\u4e86\u4e24\u53e3\uff0c \u53c8\u4f38\u624b\u9012\u7ed9\u88f4\u73cf\u3002 \u3000\u3000\u88f4\u73cf\u6709\u751f\u81f3\u4eca\u6d93\u6ef4\u4e4b\u9152\uff0c\u90fd\u672a\u6cbe\u5507\uff0c\u6b64\u523b\u63a5\u8fc7\u9152\u846b\u82a6\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5374\u89c1\u90a3\u5c11\u5e74\u6b63\u542b\u7b11\u671b \u7740\u81ea\u5df1\uff0c\u5fc3\u91cc\u5ffd\u7136\u95ea\u8fc7\u4e24\u53e5\u4ed6\u5e7c\u65f6\u5ff5\u8fc7\u7684\u5510\u8bd7\u6765\uff0c\u4e3e\u8d77\u9152\u846b\u82a6\uff0c\u518d\u4e0d\u8fdf\u7591\uff0c\u4ef0\u5929\u559d\u4e86\u4e00\u5927 \u53e3\u3002 \u3000\u3000\u90a3\u9152\u4eba\u53e3\u4e4b\u9645\uff0c\u5e76\u4e0d\u8f9b\u8fa3\uff0c\u4f46\u4e00\u559d\u4e0b\u5589\u5499\uff0c\u6d41\u5165\u809a\u91cc\uff0c\u88f4\u73cf\u53ea\u89c9\u4e00\u80a1\u70ed\u6c14\uff0c\u987f\u65f6\u5728\u809a\u4e2d \u6269\u6563\u5f00\u6765\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u53ea\u89c9\u6d51\u8eab\u4e0a\u4e0b\uff0c\u5982\u6c90\u6625\u98ce\uff0c\u4ed6\u867d\u672a\u559d\u8fc7\u9152\uff0c\u4f46\u5728\u98de\u9f99\u9556\u5c40\u65f6\uff0c\u5374\u5e38\u542c \u4eba\u8bf4\u8d77\u9152\u8d28\u597d\u574f\u7684\u533a\u522b\u4e4b\u5904\uff0c\u800c\u4ed6\u4eec\u6240\u8bf4\u7684\u597d\u9152\uff0c\u996e\u4e0b\u53bb\u5c31\u662f\u6b64\u523b\u81ea\u5df1\u9886\u53d7\u5230\u7684\u5473\u9053\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e0d\u7981\u6697\u7b11\uff0c\u8fd9\u5c11\u5e74\u4e0d\u77e5\u53c8\u7528\u4ec0\u4e48\u624b\u6cd5\uff0c\u5f04\u6765\u5982\u6b64\u597d\u9152\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u8fd9\u9152\u4e0d \u4f46\u662f\u597d\u9152\uff0c\u800c\u4e14\u662f\u597d\u9152\u4e2d\u7684\u4e0a\u4e0a\u4e4b\u54c1\u54e9\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u4eba\u4e00\u53e3\uff0c\u559d\u4e86\u513f\u53e3\u9152\uff0c\u90a3\u4e24\u53e5\u5510\u8bd7\uff0c\u5374\u53c8\u5728\u88f4\u73cf\u5fc3\u5934\u95ea\u8fc7\uff0c\u4ed6\u7ec6\u4e00\u4f53\u4f1a\uff0c\u89c9\u5f97\u8fd9 \u4e24\u53e5\u4ee5\u540e\u770b\u6765\u4e95\u65e0\u4ec0\u4e48\u5999\u5904\u7684\u8bd7\u53e5\uff0c\u6b64\u523b\u5374\u662f\u5b57\u5b57\u73e0\u7391\uff0c\u7ec6\u4e00\u4f53\u5473\uff0c\u66f4\u662f\u5999\u4e0d\u53ef\u8a00\uff0c\u53ea\u662f\u5374 \u82e6\u4e8e\u53e3\u4e0d\u80fd\u8a00\uff0c\u65e0\u6cd5\u5c06\u8fd9\u4e24\u53e5\u8bd7\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u5728\u5fc3\u91cc\u53cd\u53cd\u590d\u590d\u5730\u4f4e\u8bf5\u7740\u90a3\u4e24\u53e5\u8bd7\uff0c\u7ec8\u4e8e\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u4ece\u5730\u4e0a\u6361\u8d77\u4e00\u5757\u77f3\u5934\uff0c\u5c31\u5728\u8fd9 \u5c71\u4e18\u7684\u6ce5\u5730\u4e0a\uff0c\u6781\u5feb\u5730\u5199\u9053\uff1a\u201c\u529d\u541b\u66f4\u8fdb\u4e00\u676f\u9152\uff0c\u4e0e\u5c14\u540c\u9500\u4e07\u53e4\u6101\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u76ee\u5149\u4e00\u626b\uff0c\u53c8\u5927\u5927\u559d\u4e86\u53e3\u9152\uff0c\u4ef0\u5929\u957f\u7b11\u8d77\u6765\uff0c\u62a2\u8fc7\u88f4\u73cf\u624b\u4e2d\u7684\u77f3\u5934\uff0c\u4ea6\u81ea\u5199\u9053\uff1a \u201c\u9152\u9022\u77e5\u5df1\u5343\u676f\u5c11\uff0c\u6765\uff0c\u518d\u559d\u4e00\u53e3\u3002\u201d\u4e00\u4ef0\u9996\u53c8\u559d\u4e86\u53e3\u9152\uff0c\u4f55\u6d88\u7247\u523b\uff0c\u8fd9\u4e24\u4e2a\u8eab\u4e16\u4e0d\u540c\uff0c\u6027 \u60c5\u8fe5\u5f02\uff0c\u4f46\u5374\u5404\u6709\u611f\u6000\u7684\u5c11\u5e74\u7adf\u5c06\u8fd9\u4e24\u846b\u82a6\u7684\u4e09\u65a4\u5973\u513f\u7ea2\u559d\u4e86\u4e00\u534a\u3002 \u3000\u3000\u88f4\u73cf\u751f\u5e73\u7b2c\u4e00\u6b21\u559d\u9152\uff0c\u867d\u5df2\u9886\u7565\u5230\u9152\u7684\u5999\u5904\uff0c\u4f46\u7ec8\u8fd8\u662f\u4e0d\u80dc\u9152\u529b\uff0c\u6b64\u523b\u65e9\u5df2\u9189\u4e86\uff0c\u53ea\u89c9 \u8111\u4e2d\u6df7\u6df7\u6c8c\u6c8c\u7684\uff0c\u6068\u4e0d\u5f97\u808b\u751f\u53cc\u7ffc\uff0c\u62cd\u7ffc\u800c\u98de\uff0c\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u90a3\u5c11\u5e74\u4e00\u624b\u62ff\u7740\u9152\u846b\u82a6\uff0c\u4e00 \u624b\u62ff\u7740\u6c64\u5319\u5728\u6572\u6253\u7740\uff0c\u53cc\u76ee\u4ef0\u89c6\uff0c\u50cf\u662f\u5728\u5f15\u542d\u9ad8\u6b4c\u3002 \u3000\u3000\u88f4\u73cf\u867d\u7136\u542c\u4e0d\u5230\u4ed6\u7684\u6b4c\u58f0\uff0c\u5374\u770b\u5f97\u89c1\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u53ea\u89c1\u4ed6\u76ee\u5149\u83b9\u7136\uff0c\u53cc\u76ee\u60b2\u6006\uff0c\u5531\u5230 \u540e\u6765\uff0c\u7a81\u5730\u626c\u624b\u629b\u53bb\u624b\u4e2d\u7684\u846b\u82a6\uff0c\u7f8e\u9152\u6cfc\u5f97\u4e00\u5730\uff0c\u4ed6\u4e5f\u4e0d\u7ba1\uff0c\u4e00\u628a\u6293\u7740\u88f4\u73cf\u7684\u624b\u8155\uff0c\u7adf\u7a81\u5730 \u653e\u58f0\u5927\u54ed\u8d77\u6765\uff0c\u88f4\u73cf\u867d\u7136\u5947\u602a\uff0c\u8fd9\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c11\u5e74\uff0c\u5fc3\u91cc\u600e\u5730\u4f1a\u6709\u8fd9\u4e48\u591a\u60b2\u6006\u7684\u4e8b\u3002 \u3000\u3000\u62c5\u5fc3\u5ff5\u8f6c\u5904\uff0c\u60f3\u5230\u81ea\u5df1\u53c8\u4f55\u5c1d\u4e0d\u662f\u5e74\u7eaa\u8f7b\u8f7b\uff1f\u53c8\u4f55\u5c1d\u4e0d\u662f\u4f24\u5fc3\u4eba\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u5f80\u4e8b\u4ff1\u5728 \u5fc3\u5934\u95ea\u8fc7\uff0c\u4e0d\u7531\u4e5f\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u867d\u662f\u4e00\u4e2a\u6709\u58f0\uff0c\u4e00\u4e2a\u65e0\u58f0\uff0c\u4f46\u5374\u5404\u5404\u54ed\u5f97\u4f24\u5fc3\u65e0\u6bd4\uff0c\u90a3\u5c11\u5e74\u7a81\u5730\u4e00\u628a\u63a8\u5f00\u88f4\u73cf\uff0c \u53c8\u62fe\u8d77\u4e00\u5757\u77f3\u5934\uff0c\u5199\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u6709\u90a3\u4e48\u4f24\u5fc3\u7684\u4e8b\uff1f\u201d\u88f4\u73cf\u4e00\u6014\uff0c\u6697\u60f3\u8fd9\u53e5\u8bdd\u6b63\u662f\u6211\u60f3\u95ee \u4f60\u7684\uff0c\u4f46\u4ed6\u6b64\u523b\u5fc3\u80f8\u5835\u585e\uff0c\u6b63\u6068\u4e0d\u5f97\u6709\u4eba\u503e\u5410\uff0c\u9042\u5c31\u62ff\u8fc7\u77f3\u5757\uff0c\u5c06\u81ea\u5df1\u7684\u4e00\u8eab\u906d\u9047\uff0c\u90fd\u5728\u5730 \u4e0a\u5199\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u64e6\u4e86\u53c8\u5199\uff0c\u5199\u4e86\u53c8\u64e6\uff0c\u4e5f\u4e0d\u77e5\u9053\u5199\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u53ea\u5199\u5f97\u5730\u4e0a\u7684\u6ce5\u4e0a\u90fd\u677e\u5f97\u5199\u4e0d\u51fa\u5b57\u6765 \u4e86\uff0c\u4ed6\u5c31\u53e6\u5916\u6362\u5757\u5730\u65b9\uff0c\u53ea\u5199\u5f97\u81ea\u5df1\u7684\u8180\u5b50\u90fd\u9178\u4e86\uff0c\u4ed6\u5c31\u6b47\u606f\u4e00\u4e0b\uff0c\u6b47\u606f\u7684\u65f6\u5019\uff0c\u4ed6\u53c8\u4e0d\u7981 \u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4ea6\u662f\u8fb9\u54ed\u8fb9\u770b\uff0c\u4e00\u4f1a\u513f\u8dd1\u5230\u522b\u5904\uff0c\u5374\u6361\u90a3\u53ea\u65b9\u624d\u88ab\u4ed6\u81ea\u5df1\u629b\u6389\u7684\u9152\u846b\u82a6\uff0c\u5c06\u91cc\u9762 \u7684\u5269\u9152\uff0c\u53c8\u548c\u88f4\u73cf\u4e00\u8d77\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u672c\u6765\u81ea\u60b2\u547d\u8fd0\uff0c\u6b64\u523b\u5374\u662f\u4e3a\u88f4\u73cf\u7684\u547d\u8fd0\u800c\u75db\u54ed\uff0c\u4f46\u9152\u6709\u559d\u5e72\u7684\u65f6\u5019\uff0c\u6cea\u4e5f\u6709\u6d41\u5c3d\u7684\u65f6 \u5019\uff0c\u592a\u9633\u4ece\u4e1c\u8fb9\u5347\u4e0a\u6765\uff0c\u5347\u5230\u4e2d\u95f4\uff0c\u6b64\u523b\u5374\u5c06\u8981\u56de\u897f\u8fb9\u843d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u88f4\u73cf\u7a81\u5730\u957f\u8eab\u800c\u8d77\uff0c\u5c06\u624b\u4e2d\u7684\u77f3\u5757\uff0c\u8fdc\u8fdc\u629b\u4e86\u5f00\u53bb\uff0c\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u4eff\u4f5b\u8212\u7545\u5f88\u591a\uff0c\u56e0\u4e3a\u591a \u5e74\u4ee5\u6765\uff0c\u4ed6\u7ec8\u4e8e\u627e\u5230\u4e00\u4e2a\u80fd\u591f\u503e\u8bc9\u60b2\u54c0\u7684\u4eba\u3002 \u3000\u3000\u79ef\u90c1\u4e00\u6d88\uff0c\u4ed6\u5fc3\u4e2d\u53ea\u89c9\u7a7a\u7a7a\u6d1e\u6d1e\u5730\uff0c\u4ec0\u4e48\u4e8b\u90fd\u518d\u4e5f\u60f3\u4e0d\u8d77\u6765\uff0c\u90a3\u79cd\u632f\u632f\u6b32\u98de\u7684\u611f\u89c9\uff0c\u5374 \u53c8\u81ea\u5fc3\u4e2d\u5347\u8d77\uff0c\u4ed6\u7b2c\u4e00\u6b21\u611f\u53d7\u5230\u9152\uff0c\u662f\u4e00\u79cd\u591a\u4e48\u5947\u5999\u7684\u4e1c\u897f\uff0c\u4e5f\u7b2c\u4e00\u6b21\u611f\u53d7\u5230\uff0c\u54ed\u662f\u4e00\u4ef6\u591a \u4e48\u5947\u5999\u7684\u4e8b\u3002 \u3000\u3000\u66ae\u8272\u5c06\u4e34\uff0c\u98ce\u4e2d\u5df2\u6709\u4e9b\u51c9\u610f\u4e86\uff0c\u4f46\u8fd9\u4e24\u4e2a\u5c11\u5e74\uff0c\u5fc3\u80f8\u5374\u4ecd\u7136\u6eda\u70eb\u7684\uff0c\u4e16\u95f4\u53ef\u6709\u4ec0\u4e48\u4e8b\u80fd \u51b7\u5374\u5c11\u5e74\u4eba\u5fc3\u4e2d\u7684\u70ed\u8840\u5462\uff1f \u3000\u3000\u4ed6\u4eec\u4ece\u5c71\u4e18\u8d70\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u592a\u9633\u5df2\u7ecf\u5b8c\u5168\u843d\u4e0b\u53bb\u4e86\uff0c\u56db\u9762\u7684\u5929\u7554\uff0c\u665a\u971e\u7eda\u4e3d\uff0c\u867d\u7136\u4e00\u5982 \u5f80\u65e5\uff0c\u4f46\u88f4\u73cf\u7684\u5fc3\u60c5\uff0c\u5374\u662f\u548c\u5f80\u65e5\u8fe5\u7136\u800c\u5f02\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6b64\u523b\u8eab\u4fa7\u5df2\u6709\u77e5\u5df1\u3002\u5fc3\u80f8\u4e0d\u518d\u5bc2\u5bde\uff0c\u867d\u7136\u4ed6\u8fde\u90a3\u5c11\u5e74\u59d3\u540d\u8fd8\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e00\u624b\u63d0\u7740\u5e03\u888b\uff0c\u4e00\u624b\u642d\u5728\u88f4\u73cf\u7684\u80a9\u4e0a\uff0c\u4e24\u4eba\u9152\u610f\u90fd\u672a\u6d88\uff0c\u811a\u6b65\u4e5f\u6709\u4e9b\u8e09\u8dc4\uff0c\u4f46\u5374 \u8d70\u5f97\u6781\u5feb\uff0c\u88f4\u73cf\u76f4\u89c9\u5f97\u4eff\u4f5b\u6709\u4e2a\u4eba\u5728\u80cc\u540e\u63a8\u7740\u81ea\u5df1\uff0c\u4f7f\u81ea\u5df1\u7684\u811a\u6b65\u4e0d\u7531\u81ea\u4e3b\u5730\u5feb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u5168\u662f\u90a3\u5c11\u5e74\u642d\u5728\u81ea\u5df1\u80a9\u4e0a\u7684\u4e00\u53ea\u624b\u7684\u529b\u91cf\uff0c\u5fc3\u91cc\u5bf9\u4ed6\u7684\u6b66\u529f\uff0c\u4e0d\u7981\u66f4\u52a0\u94a6\u4f69\u3002 \u3000\u3000\u4e24\u4eba\u4e5f\u4e0d\u8fa8\u8def\u5f84\uff0c\u8d70\u4e86\u4e5f\u4e0d\u77e5\u591a\u4e45\uff0c\u53ea\u89c1\u56db\u4e0b\u8d8a\u6765\u8d8a\u8352\u51c9\uff0c\u7ade\u8fde\u7530\u964c\u90fd\u6ca1\u6709\u4e86\uff0c\u8d70\u5230\u8fd9 \u79cd\u8352\u51c9\u7684\u5730\u65b9\u6765\uff0c\u4eca\u5929\u665a\u4e0a\u5230\u54ea\u91cc\u53bb\u6b47\uff1f \u3000\u3000\u54ea\u77e5\u76ee\u5149\u4e00\u62ac\uff0c\u5374\u89c1\u82cd\u832b\u7684\u66ae\u8272\u4e2d\uff0c\u77d7\u7acb\u7740\u4e00\u5e62\u697c\u9601\u7684\u5f71\u5b50\uff0c\u6b64\u523b\u4ed6\u9152\u610f\u4ecd\u5728\uff0c\u4e5f\u4e0d\u7ba1 \u90a3\u5e62\u697c\u9601\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u4e5f\u4e0d\u7ba1\u90a3\u697c\u9601\u7684\u4e3b\u4eba\u4f1a\u4e0d\u4f1a\u6536\u7559\u4e24\u4e2a\u8863\u886b\u8934\u891b\u7684\u5c11\u5e74\u8fc7\u591c\uff0c\u4e00\u62c9\u90a3\u5c11 \u5e74\u7684\u8896\u5b50\uff0c\u5c31\u5feb\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u8d70\u5230\u8ddf\u524d\u4e00\u770b\uff0c\u5fc3\u91cc\u66f4\u662f\u9ad8\u5174\uff0c\u539f\u6765\u90a3\u5e62\u697c\u9601\u5916\u9762\u7684\u5927\u95e8\uff0c\u7adf \u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u8fd9\u5e62\u697c\u9601\u77d7\u7acb\u5728\u65e0\u4eba\u7684\u8352\u90ca\uff0c\u5c45\u7136\u655e\u7740\u5927\u95e8\uff0c\u6b64\u4e8b\u82e5\u88ab\u4efb\u4f55\u4e00\u4e2a\u4eba\u770b\u5728\u773c\u91cc\uff0c\u90fd\u4f1a\u89c9\u5f97 \u6709\u4e9b\u5947\u602a\uff0c\u4f46\u8fd9\u4e24\u4e2a\u4ff1\u90fd\u5df2\u6709\u4e86\u4e03\u5206\u9152\u610f\u7684\u5c11\u5e74\uff0c\u5374\u5168\u7136\u4e0d\u7ba1\u8fd9\u4e9b\uff0c\u7b14\u76f4\u5730\u8d70\u4e0a\u77f3\u9636\uff0c\u63a2\u9996 \u4e00\u671b\uff0c\u53ea\u89c1\u95e8\u5185\u5ead\u9662\u6df1\u6df1\uff0c\u8fde\u4e00\u4e1d\u706f\u5149\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u66ae\u8272\u867d\u6df1\uff0c\u4f46\u65f6\u5df2\u4eba\u590f\uff0c\u767d\u663c\u751a\u957f\uff0c\u6b64\u523b\u5374\u8fd8\u6709\u4e9b\u819d\u9647\u4eae\u5149\uff0c\u800c\u4eba\u7a7f\u8fc7\u9662\u843d\uff0c\u8d70\u8fdb\u5927 \u5385\uff0c\u5374\u89c1\u53a2\u5e18\u56db\u5904\uff0c\u90fd\u7ed3\u7740\u86db\u7f51\uff0c\u5927\u5385\u91cc\u684c\u6905\u6b8b\u8d25\uff0c\u56db\u58c1\u8427\u7136\uff0c\u663e\u89c1\u8fd9\u5e62\u6c14\u6d3e\u751a\u5927\u7684\u5c4b\u5b57\u7adf \u662f\u4e00\u4e2a\u8352\u5b85\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u54c8\u54c8\u4e00\u7b11\uff0c\u5c06\u624b\u4e2d\u7684\u5e03\u888b\u91cd\u91cd\u5730\u653e\u5728\u4e00\u5f20\u516b\u4ed9\u684c\u4e0a\uff0c\u54ea\u77e5\u201c\u5580\u55e4\u201d\u4e00\u58f0\uff0c\u90a3\u5f20\u65b9 \u684c\u7adf\u7a81\u5730\u5012\u4e86\u4e0b\u53bb\uff0c\u88f4\u73cf\u54a7\u5634\u4e00\u7b11\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u8fd9\u4e2a\u5927\u53e3\u888b\u50cf\u4e2a\u767e\u5b9d\u56ca\uff0c\u91cc\u9762\u82b1\u6837\u5927\u591a\uff0c\u4e00 \u5b9a\u91cd\u5f97\u5413\u4eba\u3002\u201d\u4e00\u9762\u5f80\u65c1\u8fb9\u4e00\u5f20\u6905\u5b50\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u53c8\u662f\u201c\u5580\u55e4\u201d\u4e00\u58f0\uff0c\u90a3\u5f20\u6905\u5b50\u4e5f\u5012\u4e86\u4e0b\u53bb\uff0c\u88f4\u73cf\u91cd\u5fc3\u4e00\u5931\uff0c\u5657\u5730\uff0c\u8dcc\u5230\u5730\u4e0a\u3002\u90a3\u5c11 \u5e74\u5374\u54c8\u54c8\u7b11\u4e86\u8d77\u6765\u3002\u524d\u884c\u4e24\u6b65\uff0c\u51c6\u5907\u62c9\u8d77\u88f4\u73cf\uff0c\u54ea\u77e5\u4e00\u811a\u5411\u4e0b\uff0c\u811a\u5e95\u7adf\u50cf\u662f\u6574\u4e2a\u5d4c\u5165\u4e00\u4e2a\u6d1e \u91cc\uff0c\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u4fef\u8eab\u4f4e\u5934\u4e00\u770b\uff0c\u5fc3\u4e2d\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u6726\u80e7\u7684\u6708\u5149\u81ea\u95e8\u5916\u5c04\u4eba\uff0c\u521a\u597d\u7167\u5728\u8fd9\u4e00\u7247\u5730\u4e0a\uff0c\u53ea\u89c1\u5730\u9762\u4e0a\u7adf\u5370\u7740\u4e03\u516b\u4e2a\u6df1\u9677\u5730\u9762\u3001\u51e0 \u8fbe\u4e09\u5bf8\u7684\u811a\u5370\uff0c\u4ed6\u4e00\u811a\u521a\u597d\u8e0f\u5165\u811a\u5370\u91cc\u3002 \u3000\u3000\u88f4\u73cf\u4e00\u773c\u671b\u5230\uff0c\u90a3\u5c11\u5e74\u9762\u4e0a\u7b11\u5bb9\u7a81\u655b\uff0c\u5782\u7740\u5934\u6115\u6115\u5730\u671b\u7740\u5730\u4e0a\uff0c\u5fc3\u91cc\u4e00\u602a\uff0c\u722c\u4e86\u8d77\u6765\uff0c \u8d70\u5230\u8fd1\u524d\u4e00\u770b\uff0c\u5fc3\u5934\u4e5f\u4e0d\u7981\u4e00\u60ca\u3002 \u3000\u3000\u987b\u77e5\u8fd9\u680b\u5de8\u5b85\u867d\u7136\u7834\u65e7\uff0c\u5efa\u7b51\u5f97\u5374\u751a\u7262\u56fa\uff0c\u8fd9\u5927\u5385\u7684\u56db\u9762\u4e0a\u90fd\u94fa\u7740\u539a\u539a\u4e00\u5c42\u4e09\u5408\u4e0a\uff0c\u800c \u6b64\u523b\u8fd9\u4e9b\u811a\u5370\u6df1\u9677\u5165\u5730\u7adf\u6709\u4e09\u5bf8\uff0c\u90a3\u4e48\u8e0f\u4e0b\u8fd9\u811a\u5370\u7684\u4eba\u529f\u529b\u4e4b\u6df1\u539a\uff0c\u5c82\u975e\u9a87\u4eba\u542c\u95fb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5782\u7740\u5934\u6115\u4e86\u534a\u664c\uff0c\u8fc8\u6b65\u5230\u90a3\u5f20\u5df2\u88ab\u88f4\u73cf\u5750\u584c\u7684\u6905\u524d\uff0c\u4f38\u624b\u65b9\u5f85\u62fe\u8d77\u4e00\u6bb5\u6905\u811a\uff0c\u54ea \u77e5\u89e6\u624b\u4e4b\u5904\uff0c\u90a3\u4e48\u7ed3\u5b9e\u7684\u7ea2\u6728\u6905\u811a\u7adf\u7136\u4e00\u7247\u7247\u6563\u4e86\u5f00\u6765\uff0c\u4ed6\u53cc\u7709\u4e00\u76b1\uff0c\u987a\u624b\u4e00\u62c2\uff0c\u90a3\u5f20\u7ea2\u6728 \u6905\u5b50\uff0c\u7adf\u5168\u6563\u6210\u4e00\u5806\u6728\u7247\uff0c\u8fde\u4e00\u6bb5\u6574\u9f50\u7684\u6728\u5934\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6c5f\u6e56\u5386\u7ec3\u5374\u751a\u4e30\uff0c\u77e5\u9053\u8fd9\u79cd\u7ea2\u6728\u6905\u5b50\uff0c\u7edd\u4e0d\u53ef\u80fd\u56e0\u5e74\u4ee3\u4e45\u8fdc\u800c\u8150\u8680\u6210\u5982\u6b64 \u6a21\u6837\uff0c\u76ee\u5149\u4e00\u8f6c\u3002\u679c\u7136\u770b\u5230\u8fd9\u5f20\u7ea2\u6728\u6905\u5b50\u524d\uff0c\u4e5f\u6709\u4e24\u53ea\u6574\u6574\u9f50\u9f50\u7684\u811a\u5370\uff0c\u6df1\u9677\u5165\u5730\uff0c\u6709\u5982\u5200 \u51ff\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u9000\u540e\u51e0\u6b65\uff0c\u679c\u89c1\u521a\u624d\u90a3\u51e0\u4e2a\u811a\u5370\uff0c\u6247\u9762\u4f3c\u5730\u5728\u8fd9\u4e24\u4e2a\u811a\u5370\u524d\u5e03\u6210\u4e00\u9053\u5f27 \u7ebf\uff0c\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5fc5\u5b9a\u662f\u5185\u5bb6\u9ad8\u624b\u5728\u8fd9\u91cc\u8f83\u91cf\u5185\u529b\uff0c\u6240\u7559\u4e0b\u7684\u811a\u5370\uff0c\u800c\u4e14\u662f\u6709 \u4e09\u56db\u4eba\u8054\u624b\uff0c\u6765\u5bf9\u4ed8\u5750\u5728\u6905\u4e0a\u7684\u4eba\u2014\u2014\u201d\u5fc3\u5ff5\u65b9\u81ea\u8f6c\u52a8\uff0c\u5374\u89c1\u88f4\u73cf\u4e00\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u6307\u4e86\u6307\u5730 \u4e0a\u7684\u811a\u5370\uff0c\u53c8\u4f38\u51fa\u98df\u3001\u4e2d\u3001\u62c7\u4e09\u53ea\u624b\u6307\uff0c\u8f7b\u8f7b\u4e00\u637b\uff0c\u6447\u4e86\u6447\u5934\uff0c\u50cf\u662f\u5341\u5206\u5947\u602a\u7684\u6837\u5b50\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5fae\u5fae\u4e00\u6014\uff0c\u968f\u5373\u4f1a\u8fc7\u610f\u6765\uff0c\u77e5\u9053\u88f4\u73cf\u505a\u7684\u624b\u5f0f\uff0c\u662f\u8868\u793a\u201c\u4e03\u201d\u5b57\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u679c \u7136\u53d1\u73b0\u5730\u4e0a\u9664\u4e86\u6905\u524d\u7684\u4e24\u53ea\u811a\u5370\u5916\uff0c\u7adf\u53ea\u6709\u4e03\u4e2a\u811a\u5370\uff0c\u9760\u5728\u6700\u53f3\u7684\u4e00\u53ea\u811a\u5370\u65c1\uff0c\u5374\u6709\u4e00\u4e2a\u5706 \u6d1e\u3002 \u3000\u3000\u4ed6\u76b1\u7740\u7709\u53c8\u6c89\u541f\u4e86\u534a\u664c\uff0c\u7a81\u5730\u62ff\u8d77\u5e03\u888b\uff0c\u5728\u91cc\u9762\u627e\u4e86\u534a\u664c\uff0c\u62ff\u51fa\u4e00\u53ea\u8721\u70db\u548c\u4e00\u4e2a\u706b\u6298\u5b50 \u6765\uff0c\u6247\u8d77\u706b\u6298\uff0c\u70b9\u8d77\u8721\u70db\uff0c\u70db\u706b\u867d\u5f31\uff0c\u5374\u5df2\u4f7f\u5f97\u4ed6\u4eec\u773c\u524d\u4e00\u4eae\u3002 \u3000\u3000\u4ed6\u5c06\u90a3\u53ea\u8721\u70db\u62ff\u5728\u624b\u4e0a\uff0c\u76ee\u5149\u8f6c\u52a8\u5904\uff0c\u7a81\u5730\u8131\u53e3\u60ca\u547c\u51fa\u6765\uff0c\u811a\u6b65\u5fae\u9519\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230 \u65b9\u624d\u653e\u7740\u90a3\u7ea2\u6728\u6905\u5b50\u540e\u9762\u7684\u5899\u811a\uff0c\u88f4\u73cf\u76ee\u5149\u968f\u5373\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u9762\u5899\u4e0a\u6676\u5149\u95ea\u95ea\uff0c\u7adf\u5d4c\u7740\u4e03\u70b9 \u5bd2\u5149\uff0c\u6574\u6574\u9f50\u9f50\u5730\u6392\u6210\u4e00\u4e2a\u201c\u5317\u6597\u4e03\u661f\u201d\u7684\u5f62\u72b6\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e3e\u7740\u70db\u706b\uff0c\u5728\u5899\u4e0a\u4e00\u6620\uff0c\u53ea\u89c1\u4e03\u6839\u94a2\u9489\uff0c\u7adf\u90fd\u6df1\u5d4c\u5165\u5899\uff0c\u70db\u5149\u5f71\u6620\u5904\uff0c\u88f4\u73cf\u53ea\u89c9 \u4ed6\u7684\u9762\u5b54\u82cd\u767d\uff0c\u53c8\u81ea\u76b1\u7709\u6c89\u601d\u8d77\u6765\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u91cc\u867d\u4e5f\u5728\u5947\u602a\u8fd9\u4e9b\u811a\u5370\u548c\u5bd2\u661f\uff0c\u4f46\u5374\u53c8\u89c9\u5f97\u8fd9\u4e9b\u4e8b\u6839\u672c\u4e0e\u81ea\u5df1\u65e0\u5173\uff0c\u81ea\u5df1\u53c8\u4f55\u5fc5 \u767d\u767d\u82b1\u4e9b\u8111\u7b4b\u5728\u4e0a\u9762\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u56de\u5934\u8d70\u4e86\u51e0\u6b65\uff0c\u7a81\u5730\u770b\u5230\u8fbe\u95ee\u9893\u8d25\u7684\u5927\u5385\u7684 \u89d2\u843d\u91cc\uff0c\u7adf\u6302\u7740\u4e00\u5e45\u753b\u56fe\uff0c\u548c\u56db\u4e0b\u663e\u5f97\u6781\u4e0d\u76f8\u79f0\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u4ea6\u4e0d\u7981\u8d77\u4e86\u597d\u5947\u5fc3\uff0c\u56de\u76ee\u800c\u671b\uff0c\u90a3\u5c11\u5e74\u4ecd\u7136\u51fa\u795e\u5730\u671b\u7740\u5899\u4e0a\u7684\u5bd2\u661f\uff0c\u9042\u4e5f\u6ca1\u6709\u8fc7 \u53bb\u62db\u547c\u4ed6\uff0c\u5f84\u81ea\u8d70\u5230\u90a3\u89d2\u843d\u91cc\u3002 \u3000\u3000\u70db\u5149\u867d\u6781\u5f31\uff0c\u4ed6\u5374\u53ef\u4ee5\u770b\u5230\u90a3\u5e45\u753b\u4e0a\uff0c\u753b\u7684\u7adf\u662f\u4e00\u7247\u60ac\u5d16\uff0c\u58c1\u7acb\u5343\u4e08\uff0c\u4e0b\u9762\u7edd\u58d1\u6c89\u6c89\uff0c \u6df1\u4e0d\u89c1\u5e95\uff0c\u5d16\u4e0a\u5374\u753b\u7740\u4e00\u4e2a\u778e\u5b50\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u6839\u660e\u6756\uff0c\u53e6\u5916\u4e00\u4e2a\u957f\u886b\u6587\u58eb\uff0c\u501a\u5728\u4e00\u682a\u6811\u524d\uff0c \u6b63\u5728\u5439\u7740\u7b1b\u5b50\uff0c\u90a3\u778e\u5b50\u60f3\u5fc5\u542c\u5f97\u5341\u5206\u5165\u795e\uff0c\u7adf\u5fd8\u4e86\u53bb\u63a2\u6d4b\u524d\u9762\u7684\u8def\uff0c\u4e00\u811a\u773c\u770b\u5c31\u8981\u8e0f\u7a7a\uff0c\u5760 \u4eba\u90a3\u6df1\u4e0d\u89c1\u5e95\u7684\u7edd\u58d1\u4e0b\u3002 \u3000\u3000\u8fd9\u753b\u753b\u5f97\u975e\u5e38\u7ec6\u817b\uff0c\u5c06\u90a3\u778e\u5b50\u9762\u4e0a\u7684\u8868\u60c5\u90fd\u753b\u4e86\u51fa\u6765\uff0c\u53ea\u89c1\u5929\u84dd\u5982\u78a7\uff0c\u82b1\u7ea2\u5982\u7d2b\uff0c\u90a3\u778e \u5b50\u4ea6\u662f\u4e00\u4ed8\u5982\u75f4\u5982\u9189\u7684\u8868\u60c5\uff0c\u518d\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u8fd9\u4e00\u811a\u8e0f\u4e0b\u53bb\uff0c\u7acb\u5373\u4fbf\u5f97\u7c89\u8eab\u788e\u9aa8\u3002 \u3000\u3000\u88f4\u73cf\u770b\u4e86\u534a\u664c\uff0c\u8d8a\u770b\u8d8a\u89c9\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u5fc3\u60f3\u753b\u8fd9\u753b\u7684\u4eba\uff0c\u600e\u5730\u5982\u6b64\u6b8b\u5fcd\uff0c\u7adf\u5c06\u4e00\u4e2a\u778e\u5b50\u7f6e \u4e8e\u7edd\u5883\u3002 \u3000\u3000\u4ed6\u672c\u662f\u81f3\u60c5\u81f3\u6027\u4e4b\u4eba\uff0c\u773c\u4e2d\u770b\u7740\u8fd9\u5e45\u753b\uff0c\u5fc3\u4e2d\u5374\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u80fd\u81ea\u5df1\uff0c\u6068\u4e0d\u5f97\u81ea\u5df1\u8dd1 \u4e0a\u753b\u53bb\uff0c\u62c9\u90a3\u778e\u5b50\u4e00\u628a\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u53f9\u606f\u4e00\u58f0\uff0c\u8f6c\u8fc7\u5934\u53bb\uff0c\u4e0d\u5fcd\u518d\u770b\uff0c\u54ea\u77e5\u76ee\u5149\u52a8\u5904\uff0c\u5374\u770b\u5230\u5899\u8fb9\u4e00\u5f20\u5c0f\u51e0\u4e0a\uff0c\u7adf\u653e \u7740\u4e00\u526f\u7b14\u58a8\uff0c\u781a\u4e2d\u58a8\u6c41\u4ecd\u81ea\u672a\u5e72\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u559c\uff0c\u4e5f\u4e0d\u7ba1\u5728\u8fd9\u8352\u5b85\u91cc\uff0c\u600e\u4f1a\u653e\u7740\u7b14\u58a8\uff0c\u5927\u6b65\u8d70 \u4e86\u8fc7\u53bb\uff0c\u4e00\u624b\u62ff\u8d77\u77f3\u781a\uff0c\u4e00\u624b\u62ff\u8d77\u6bdb\u7b14\uff0c\u53c8\u8dd1\u5230\u9762\u524d\uff0c\u7adf\u5728\u90a3\u778e\u5b50\u8eab\u540e\uff0c\u52a0\u4e0a\u4e00\u4e2a\u4eba\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u6c89\u5fd6\u4e86\u534a\u664c\uff0c\u53e3\u4e2d\u5583\u5583\u5ff5\u9053\uff1a\u201c\u5317\u6597\u4e03\u661f\u9488\uff0c\u5317\u6597\u4e03\u661f\u9488\u2026\u2026\u96be\u9053\u2018\u5317\u6597\u4e03\u715e\u2019 \u4e5f\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u4f46\u90a3\u5750\u5728\u6905\u5b50\u4e0a\u7684\uff0c\u5374\u53c8\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d\u8f6c\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u88f4\u73cf\u7ad9\u5f97\u8fdc\u8fdc\u7684\uff0c \u624b\u91cc\u62ff\u7740\u4e00\u53ea\u7b14\uff0c\u5728\u5899\u4e0a\u7684\u4e00\u5e45\u753b\u4e0a\u753b\u7740\uff0c\u5fc3\u91cc\u53c8\u662f\u4e00\u6014\uff0c\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u5374\u89c1\u88f4\u73cf\u4e13\u5fc3\u51dd \u6ce8\uff0c\u5728\u753b\u4e0a\u753b\u4e86\u4e00\u5343\u3001\u8eab\u7a7f\u957f\u886b\u7684\u5c11\u5e74\uff0c\u6b63\u4f38\u51fa\u4e00\u53ea\u624b\uff0c\u53bb\u6293\u778e\u5b50\u7684\u80a9\u8180\u3002 \u3000\u3000\u88f4\u73cf\u867d\u672a\u4e60\u753b\uff0c\u4f46\u4ed6\u5929\u8d44\u7edd\u9876\uff0c\u753b\u5f97\u5e76\u4e0d\u79bb\u8c31\uff0c\u5012\u4e5f\u5c06\u90a3\u5c11\u5e74\u753b\u5f97\u6829\u6829\u5982\u751f\uff0c\u800c\u4e14\u9762\u76ee \u4e4b\u95f4\uff0c\u7adf\u6709\u51e0\u5206\u50cf\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e0d\u7981\u5931\u58f0\u4e00\u7b11\uff0c\u53ea\u89c1\u88f4\u73cf\u63d0\u7740\u7b14\uff0c\u5de6\u770b\u53f3\u770b\uff0c\u5634\u89d2\u6cdb\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u4f3c\u4e4e\u5fc3\u91cc\u9887\u4e3a \u6ee1\u610f\uff0c\u53c8\u5728\u753b\u4e0a\u90a3\u5c11\u5e74\u8eab\u7554\uff0c\u6dfb\u4e86\u4e00\u53e3\u957f\u5251\uff0c\u65b9\u81ea\u4e22\u4e0b\u7b14\uff0c\u957f\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5374\u4ecd\u7136\u7ad9\u5728\u753b \u524d\uff0c\u76ee\u5149\u51dd\u6ce8\uff0c\u6839\u672c\u6ca1\u6709\u53d1\u73b0\u90a3\u5c11\u5e74\u5df2\u6765\u5230\u8eab\u4fa7\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u65b9\u81ea\u4e22\u4e86\u753b\u7b14\uff0c\u8fd9\u5927\u5385\u7684\u5c4b\u9876\uff0c\u5ffd\u5730\u53d1\u51fa\u4e00\u9635\u5947\u5f02\u7684\u53e3\u54e8\u58f0\uff0c\u58f0\u97f3\u5c16\u9510\u800c\u9ad8\u4ea2\uff0c \u5728\u9759\u591c\u4e2d\u5206\u5916\u523a\u8033\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u84e6\u5730\u4e00\u60ca\uff0c\u5012\u9000\u4e09\u6b65\uff0c\u62ac\u76ee\u671b\u53bb\uff0c\u5c4b\u9876\u6ee1\u5e03\u86db\u7f51\u5c18\u57c3\uff0c\u770b\u4e0d\u89c1\u534a\u6761\u4eba\u5f71\uff0c\u4f46\u90a3\u5c16 \u9510\u800c\u9ad8\u4ea2\u7684\u54e8\u58f0\uff0c\u5374\u4ecd\u672a\u4e2d\u6b62\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u5c06\u624b\u4e2d\u7684\u8721\u70db\u7acb\u5728\u5730\u4e0a\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u65b9\u5f85\u817e\u8eab\u800c\u8d77\uff0c\u5230\u5c4b\u9876\u4e0a\u53bb\u770b\u4e2a\u7a76 \u7adf\uff0c\u54ea\u77e5\u2014\u2014\u5916\u9762\u7a81\u5730\u4f20\u6765\u4e00\u9635\u6e05\u6717\u7684\u7b11\u58f0\uff0c\u90a3\u7b11\u58f0\u8d77\u5904\u4eff\u4f5b\u8fd8\u6709\u751a\u8fdc\uff0c\u4f46\u7b11\u58f0\u672a\u7edd\uff0c\u90a3\u5c11 \u5e74\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u95e8\u53e3\u5df2\u591a\u4e86\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u95e8\u5916\u661f\u5149\u5982\u70db\uff0c\u95e8\u5185\u70db\u5149\u5982\u661f\uff0c\u5728\u8fd9\u661f\u70db\u4e4b\u5149\u4ea4\u6620\u4e4b\u4e0b\uff0c\u53ea\u89c1\u6b64\u4eba\u8eab\u6750\u9b41\u4f1f\uff0c\u80cc\u9614\u4e09 \u505c\uff0c\u5374\u7a7f\u7740\u4e00\u4ef6\u5b9d\u84dd\u4e1d\u888d\uff0c\u4e00\u624b\u6447\u7740\u4e00\u628a\u7d20\u9762\u6298\u6247\uff0c\u4e00\u624b\u634b\u7740\u9894\u4e0b\u6d53\u987b\uff0c\u7f13\u7f13\u8d70\u4e86\u8fdb\u6765\uff0c\u76ee \u5149\u56db\u4e0b\u4e00\u626b\uff0c\u5176\u5229\u6709\u5982\u95ea\u7535\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5fc3\u4e2d\u6697\u60ca\uff1a\u201c\u6b64\u4eba\u597d\u5feb\u7684\u8eab\u624b\u3002\u201d\u62ac\u76ee\u671b\u53bb\uff0c\u5374\u89c1\u6b64\u4eba\u4ea6\u6b63\u51dd\u76ee\u671b\u7740\u81ea\u5df1\uff0c\u7a81\u53c8 \u58f0\u82e5\u6d2a\u949f\u822c\u5730\u5927\u7b11\u8d77\u6765\u3002\u7b11\u5f97\u90a3\u5c11\u5e74\u8033\u4fa7\u201c\u55e1\u55e1\u201d\u4f5c\u54cd\uff0c\u4ed6\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u60ca\uff1a\u201c\u6b64\u4eba\u597d\u6df1\u7684 \u529f\u529b\u3002\u201d \u3000\u3000\u53ea\u6709\u88f4\u73cf\uff0c\u4ed6\u5374\u4ecd\u7136\u5168\u795e\u51dd\u6ce8\u5728\u90a3\u5e45\u753b\u4e0a\uff0c\u6839\u672c\u6ca1\u6709\u542c\u89c1\u8fd9\u7b11\u58f0\uff0c\u4e5f\u6839\u672c\u6ca1\u6709\u770b\u5230\u6b64 \u4eba\uff0c\u4ed6\u5fc3\u91cc\u53ea\u5728\u60f3\u7740\uff1a\u201c\u8981\u662f\u6211\u80fd\u5c06\u5929\u4e0b\u6fd2\u4e8e\u7edd\u5883\u7684\u4eba\uff0c\u90fd\u4e00\u4e00\u6551\u56de\u6765\uff0c\u90a3\u8be5\u6709\u591a\u597d\u3002\u201d \u3000\u3000\u4ed6\u6068\u4e0d\u5f97\u81ea\u5df1\u5c31\u662f\u753b\u4e0a\u90a3\u8170\u4f69\u957f\u5251\u7684\u6f47\u6f47\u5c11\u5e74\uff0c\u4e00\u5251\u5728\u624b\uff0c\u5feb\u610f\u6c5f\u6e56\u3002 \u3000\u3000\u90a3\u9ad8\u5927\u5a01\u731b\u8001\u8005\uff0c\u7f13\u6b65\u8d70\u8fdb\u5385\u6765\uff0c\u6717\u58f0\u7b11\u4e2d\uff0c\u7a81\u4ed6\u8bf4\u9053\uff1a\u201c\u8001\u592b\u6218\u98de\uff0c\u4e0d\u77e5\u5144\u53f0\u9ad8\u59d3\uff0c \u80fd\u5426\u89c1\u544a\uff1f\u201d\u90a3\u5c11\u5e74\u4e00\u6014\uff0c\u4e00\u60ca\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u96be\u9053\u6b64\u4eba\u5c31\u662f\u795e\u624b\u6218\u98de\u3002\u201d\u76ee\u5149\u62ac\u5904\uff0c\u5374\u89c1 \u8fd9\u6218\u98de\u7b11\u58f0\u7a81\u655b\uff0c\u76ee\u5149\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5230\u88f4\u73cf\u8eab\u4e0a\uff0c\u518d\u4e5f\u4e0d\u770b\u81ea\u5df1\u4e00\u773c\uff0c\u751a\u81f3\u8fde\u65b9\u624d\u95ee\u81ea\u5df1\u7684 \u8bdd\u90fd\u518d\u4e5f\u65e0\u987b\u56de\u7b54\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u6218\u98de\u4e00\u6447\u6298\u6247\uff0c\u53c8\u590d\u5927\u7b11\u8d77\u6765\uff0c\u5374\u8d70\u5411\u88f4\u73cf\u8eab\u4fa7\uff0c\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u9601\u4e0b\uff0c\u597d\u6781\uff0c \u597d\u6781\uff0c\u5148\u524d\u6211\u8fd8\u4ee5\u4e3a\u662f\u8d35\u53cb\u54e9\u3002\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u95ea\u7535\u822c\u5728\u90a3\u5e45\u753b\u4e0a\u4e00\u626b\uff0c\u4e0d\u4f4f\u70b9\u8d77\u5934\u6765\u3002 \u3000\u3000\u4ed6\u8bed\u58f0\u867d\u6d2a\u4eae\uff0c\u88f4\u73cf\u5374\u4ecd\u7136\u542c\u4e0d\u5230\u3002\u90a3\u5c11\u5e74\u5fc3\u5ff5\u8f6c\u5904\uff0c\u7a81\u5730\u4e00\u4e2a\u7bad\u6b65\uff0c\u63a0\u5230\u88f4\u73cf\u8eab\u524d\uff0c \u54ea\u77e5\u8863\u8882\u5e26\u98ce\uff0c\u5374\u5c06\u5730\u4e0a\u7684\u8721\u706b\u5f04\u706d\u4e86\u3002 \u3000\u3000\u5927\u5385\u5185\u9aa4\u7136\u4e00\u6697\uff0c\u7b49\u5230\u4ed6\u518d\u62ff\u51fa\u706b\u6298\uff0c\u70b9\u4eae\u8721\u70db\u7684\u65f6\u5019\uff0c\u5927\u5385\u95e8\u53e3\uff0c\u7adf\u53c8\u591a\u4e86\u56db\u6761\u4eba \u5f71\uff0c\u5e76\u80a9\u8d70\u4e86\u8fdb\u6765\uff0c\u9762\u4e0a\u5404\u81ea\u5e26\u7740\u5947\u5f02\u7684\u795e\u8272\u3002 \u3000\u3000\u88f4\u73cf\u6b64\u523b\u4ea6\u4ece\u51dd\u601d\u4e2d\u60ca\u9192\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u95e8\u5916\u8d70\u8fdb\u7684\u56db\u4eba\uff0c\u4e00\u4e2a\u8eab\u6750\u9880\u957f\uff0c\u9762\u76ee\u7626 \u524a\uff0c\u76ee\u5149\u5982\u9e70\uff0c\u4e00\u624b\u7f13\u7f13\u629a\u5f04\u7740\u8170\u95f4\u7684\u5251\u67c4\uff0c\u6ee1\u9762\u4ff1\u662f\u9634\u68ee\u6df1\u6c89\u7684\u6837\u5b50\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u751f\u50cf\u548c\u4ed6\u65e0\u5f02\uff0c\u53ea\u662f\u5e74\u7eaa\u8f83\u4e3a\u8f7b\u4e9b\uff0c\u8170\u95f4\u4e5f\u6ca1\u6709\u4f69\u5251\u3002 \u3000\u3000\u8d70\u5728\u4ed6\u4eec\u8eab\u4fa7\u7684\uff0c\u5374\u662f\u4e2a\u7626\u5c0f\u67af\u5e72\u7684\u77ee\u5b50\uff0c\u8170\u95f4\u6302\u7740\u4e00\u4e2a\u8c79\u76ae\u4f69\u56ca\uff0c\u51e0\u4e4e\u5360\u4e86\u4ed6\u8eab\u8eaf\u7684 \u4e00\u534a\uff0c\u53ea\u662f\u4ed6\u9762\u76ee\u4ea6\u662f\u6df1\u6c89\u65e0\u6bd4\uff0c\u4f7f\u4ed6\u770b\u6765\u672c\u751a\u6ed1\u7a3d\u7684\u6837\u5b50\uff0c\u53d8\u5f97\u534a\u70b9\u4e5f\u65e0\u53ef\u7b11\u4e4b\u610f\u3002 \u3000\u3000\u88f4\u73cf\u76ee\u5149\u518d\u8f6c\u5230\u6700\u53f3\u4e00\u4eba\u7684\u624b\u4e0a\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5927\u4e3a\u604d\u7136\uff1a\u201c\u602a\u4e0d\u5f97\u65b9\u624d\u53ea\u6709\u4e03\u53ea\u811a\u5370\uff0c \u60f3\u5fc5\u5c31\u662f\u8fd9\u56db\u4eba\u7559\u4e0b\u7684\u4e86\u3002\u201d\u539f\u6765\u6b64\u4eba\u7adf\u662f\u4e2a\u8ddb\u5b50\uff0c\u5de6\u808b\u6491\u7740\u4e00\u53ea\u94c1\u62d0\uff0c\u4f46\u8d70\u8d77\u8def\u6765\uff0c\u5374\u4ecd \u5b89\u7a33\u5f97\u5f88\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u7684\u516b\u53ea\u773c\u775b\uff0c\u6709\u5982\u516b\u9053\u5389\u7535\uff0c\u4e00\u8d77\u671b\u5728\u88f4\u73cf\u8eab\u4e0a\uff0c\u88f4\u73cf\u4e0d\u7981\u4e00\u4fa7\u76ee\uff0c\u5374\u89c1\u53e6\u4e00\u4e2a \u9ad8\u5927\u5a01\u731b\u7684\u8001\u4eba\uff0c\u76ee\u5149\u4ea6\u5728\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u88f4\u73cf\u4e0d\u89c9\u60ca\u5413\u4ea4\u96c6\uff0c\u4e0d\u77e5\u8fd9\u4e9b\u4eba\u4e3a\u4ec0\u4e48\u5982\u6b64\u671b\u7740\u81ea\u5df1\uff0c\u5374\u89c1\u90a3\u56db\u4eba\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u4e00\u8d77\u7ad9\u5728 \u81ea\u5df1\u9762\u524d\uff0c\u53c8\u4fa7\u76ee\u53bb\u671b\u90a3\u5899\u4e0a\u7684\u753b\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u88f4\u73cf\u867d\u4e0d\u8ba4\u5f97\uff0c\u90a3\u5c11\u5e74\u5374\u8ba4\u5f97\u4e24\u4e2a\uff0c\u8eab\u5f62\u4e00\u5c55\uff0c\u6321\u5728\u88f4\u73cf\u8eab\u524d\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211 \u5f53\u662f\u8c01\uff0c\u539f\u6765\u662f\u9601\u4e0b\u5144\u5f1f\uff0c\u771f\u662f\u5e78\u4f1a\u5f97\u5f88\uff0c\u5e78\u4f1a\u5f97\u5f88\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8eab\u8eaf\u9880\u957f\u7684\u6c49\u5b50\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u4e0d\u7981\u6697\u4e2d\u4e00\u76b1\u7709\u5934\uff0c\u751f\u50cf\u662f\u4e0a\u4e0d\u613f\u610f\u89c1\u5230\u8fd9\u5c11\u5e74\uff0c \u5374\u53c8\u4e0d\u5f97\u4e0d\u53d1\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u5434\u5c11\u4fa0\uff0c\u54c8\uff0c\u771f\u662f\u5de7\u9047\uff0c\u60f3\u4e0d\u5230\u5434\u5c11\u4fa0\u4e5f\u6709\u5174\u8da3\u8dd1\u5230\u6c5f\u5357 \u6765\u3002\u201d \u3000\u3000\u90a3\u7626\u5c0f\u67af\u7626\u7684\u6c49\u5b50\u4e0a\u4e0b\u6253\u91cf\u4e86\u4ed6\u51e0\u773c\uff0c\u7a81\u5730\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u5c31\u662f\u4e94\u5e74\u4ee5\u524d\uff0c\u5c31\u5df2 \u540d\u4f20\u6cb3\u6714\u7684\u4e03\u5de7\u7ae5\u5b50\u5434\u9e23\u4e16\u5434\u5c11\u4fa0\u5427\uff1f\u5c0f\u5f1f\u65e9\u95fb\u5927\u540d\uff0c\u5e38\u76fc\u4e00\u89c1\uff0c\u60f3\u4e0d\u5230\u5374\u5728\u6b64\u5904\u9047\u7740 \u4e86\u3002\u201d\u4ed6\u6697\u91cc\u867d\u5728\u5411\u90a3\u5c11\u5e74\u201c\u5434\u9e23\u4e16\u201d\u8bf4\u7740\u8bdd\uff0c\u773c\u775b\u5374\u671b\u7740\u5c4b\u9876\uff0c\u4e00\u624b\u6276\u5728\u90a3\u8c79\u76ae\u4f69\u56ca\u4e0a\uff0c \u5927\u6709\u76ee\u65e0\u4f59\u5b50\u4e4b\u6982\u3002 \u3000\u3000\u90a3\u8863\u886b\u69db\u697c\u7684\u5c11\u5e74\uff0c\u679c\u771f\u5c31\u662f\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\uff0c\u6570\u767e\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u4eba\u6210\u540d\u6700\u65e9\u7684\uff0c \u4e5f\u5c31\u662f\u6b64\u4eba\uff0c\u4ed6\u5341\u4e8c\u5c81\u51fa\u6c5f\u6e56\uff0c\u5341\u4e94\u5c81\u5c31\u540d\u6ee1\u5929\u4e0b\uff0c\u6c5f\u6e56\u4e0a\u82e5\u8bba\u7cbe\u7075\u8df3\u8131\uff0c\u5c31\u6ca1\u4e00\u4eba\u6bd4\u5f97\u4e0a \u8fd9\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u7684\uff0c\u53ea\u662f\u88f4\u73cf\u76f4\u5230\u6b64\u523b\u8fd8\u4e0d\u77e5\u9053\u4ed6\u7adf\u662f\u6b66\u6797\u540d\u4eba\u800c\u5df2\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u4e0d\u7981\u4e00\u6311\u5251\u7709\uff0c\u51b7\u51b7\u5411\u90a3\u7626\u5c0f\u67af\u5e72\u7684\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u5c0f\u53ef\u6b63\u662f\u5434\u9e23 \u4e16\uff0c\u9601\u4e0b\u2014\u2014\u201d\u8bed\u72b9\u672a\u4e86\uff0c\u90a3\u9880\u957f\u7684\u6c49\u5b50\u5374\u5df2\u8fde\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u2018\u4e03\u5de7\u8ffd\u9b42\uff0c\u90a3\u98de\u8679\uff0c \u6c5f\u6e56\u4eba\u79f0\u5357\u5317\u53cc\u5de7\uff0c\u9047\u4e0a\u4e0d\u4e86\uff0c\u5c31\u662f\u8bf4\u7684\u4f60\u4eec\u4e24\u4e2a\uff0c\u54c8\uff0c\u4e24\u4f4d\u771f\u8be5\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d\u90a3\u98de\u8679\u9f3b\u5b54 \u91cc\u91cd\u91cd\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5176\u5b9e\u2018\u4e03\u5de7\u2019\u4e24\u5b57\uff0c\u53ea\u6709\u5434\u5c11\u4fa0\u8fd9\u6837\u7684\u4eba\u624d\u914d\u79f0\u5f97\u4e0a\uff0c\u81f3 \u4e8e\u5c0f\u53ef\u4e48\u2014\u2014\u5374\u4e07\u4e07\u62c5\u5f53\u4e0d\u8d77\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u9601\u4e0b\u5c31\u6362\u4e2a\u540d\u5b57\u597d\u4e86\u3002\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u5927\u5bb6\u4ff1\u90fd\u4e00\u6014\uff0c\u90a3\u98de\u8679\u66f4\u662f\u9762\u5bb9\u9aa4\u53d8\uff0c\u5434\u9e23\u4e16\u9762\u4e0a\u867d\u662f\u7b11\u5bb9\u6ee1\u9762\uff0c\u5176\u5b9e\u5728\u672a \u8bf4\u8bdd\u524d\uff0c\u65e9\u5df2\u6212\u5907\uff0c\u987b\u77e5\u4ed6\u8fd9\u8bdd\u6b63\u662f\u72af\u4e86\u6b66\u6797\u5927\u5fcc\uff0c\u4ed6\u4e5f\u65e9\u5c31\u77e5\u9053\u90a3\u98de\u8679\u4e0d\u4f1a\u5584\u7f62\u7518\u4f11\u7684\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u98de\u8679\u671b\u4e86\u7ad9\u5728\u5434\u545c\u4e16\u8eab\u540e\u7684\u88f4\u73cf\u4e00\u773c\uff0c\u7adf\u5c06\u6012\u5bb9\u655b\u4e86\u4e0b\u53bb\uff0c\u5434\u9e23\u4e16\u76ee\u773a\u77ac\u5904\uff0c\u5fc3 \u91cc\u4e0d\u7981\u5927\u4e3a\u5947\u602a\uff1a\u201c\u96be\u9053\u4ed6\u7adf\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u7adf\u80fd\u4f7f\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\u754f\u60e7\u4e8e\u4ed6\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u8fd9\u4e9b\u53f1\u54a4\u6c5f\u5357\u6b66\u6797\u7684\u8349\u6cfd\u8c6a\u58eb\uff0c\u6b64\u523b\u7adf\u90fd\u5411\u88f4\u73cf\u8eac\u8eab\u884c\u793c\uff0c\u4e0d\u7981 \u53c8\u4e3a\u4e4b\u4e00\u6014\uff0c\u4ed6\u672c\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u4f46\u6b64\u523b\u5374\u4e5f\u5f04\u4e0d\u6e05\u8fd9\u4e9b\u4eba\u7684\u7528\u610f\u3002\u800c\u88f4\u73cf\u5462\uff0c\u4ed6\u6839\u672c\u4ece \u5934\u5230\u5c3e\u90fd\u542c\u4e0d\u61c2\u8fd9\u4e9b\u4eba\u7684\u8bdd\uff0c\u6b64\u523b\u81ea\u66f4\u4e3a\u832b\u7136\u3002 \u3000\u3000\u795e\u624b\u6218\u98de\u4e00\u8fde\u95ee\u4e86\u4e24\u53e5\uff0c\u5374\u89c1\u9762\u524d\u8fd9\u5c11\u5e74\u4ecd\u7136\u4e00\u8a00\u4e0d\u53d1\uff0c\u6d53\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u600e\u5730\u2014 \u2014\u201d\u5434\u9e23\u4e16\u5374\u5df2\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u655d\u53cb\u88f4\u73cf\uff0c\u6218\u5927\u4fa0\u6709\u4f55\u89c1\u6559\uff0c\u8ddf\u5c0f\u5f1f\u8bf4\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u53cc\u7709\u4e00\u8f69\uff0c\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff0c\u9707\u5f97\u5434\u9e23\u4e16\u8033\u65c1\u53c8\u662f\u201c\u55e1\uff0c\u7136\u4e00\u58f0\uff0c\u54ea\u77e5\u90a3\u98de \u8679\u4e00\u559d\u8fc7\u540e\uff0c\u5df2\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u8d35\u53cb\u662f\u4e2a\u804b\u5b50\uff0c\u6218\u5927\u4fa0\uff0c\u770b\u6765\u4f60\u6211\u65e5\u524d\u4e4b\u7ea6\uff0c\u6b64\u523b\u7b97\u4e0d\u5f97 \u4e86\u3002\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u6781\u4e3a\u5f97\u610f\uff0c\u4f46\u5434\u9e23\u4e16\u5374\u53c8\u4e0d\u7981\u4e00\u6014\u3002 \u3000\u3000\u5374\u89c1\u795e\u624b\u6218\u98de\u51b7\u7b11\u4e00\u58f0\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8c01\u8bf4\u7b97\u4e0d\u5f97\uff01\u201d\u8d70\u5230\u90a3\u59cb\u7ec8\u65e0\u52a8\u4e8e\u8877\u7684\u88f4\u73cf\u9762\u524d\uff0c \u4ed4\u7ec6\u4e00\u671b\uff0c\u7a81\u5730\u7adf\u4e5f\u5927\u559d\u4e00\u58f0\uff0c\u6709\u5982\u9739\u96f3\uff0c\u5434\u9e23\u4e16\u6d51\u8eab\u4e00\u9707\uff0c\u8fde\u9000\u4e09\u6b65\uff0c\u90a3\u98de\u8679\u3001\u83ab\u5357\u3001\u83ab \u5317\u3001\u5411\u4e00\u557c\uff0c\u4ea6\u662f\u9762\u5bb9\u5927\u53d8\uff0c\u53ea\u6709\u88f4\u73cf\uff0c\u5374\u4ecd\u662f\u76ee\u5149\u832b\u7136\uff0c\u6839\u672c\u4ec0\u4e48\u4e5f\u6ca1\u542c\u5230\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e9b\u4eba\u7a76\u7adf\u5728\u5f04\u4ec0\u4e48\u82b1\u6837\uff0c\u53c8\u4e3a\u4ec0\u4e48\u5411\u81ea\u5df1\u8eac\u8eab\u884c\u793c\uff0c\u4e0d\u7981\u6697\u53f9\u4e00 \u58f0\uff0c\u6697\u6068\u81ea\u5df1\u542c\u4e0d\u5230\u522b\u4eba\u7684\u8bdd\uff0c\u76ee\u5149\u6c42\u52a9\u5730\u4e00\u5170\u90a3\u5c11\u5e74\u2014\u2014\u5434\u9e23\u4e16\uff0c\u5374\u89c1\u4ed6\u7adf\u4e5f\u548c\u81ea\u5df1\u4e00 \u6837\uff0c\u9762\u76ee\u832b\u7136\uff0c\u76ee\u5149\u4e2d\u6ee1\u662f\u60ca\u8bb6\u4e4b\u8272\uff0c\u751f\u50cf\u662f\u4e5f\u5760\u5165\u4e94\u91cc\u96fe\u91cc\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u6218\u5144\u518d\u543c\u4e5f\u6ca1\u6709\u7528\uff0c\u6b64\u4eba\u679c\u771f\u662f\u4e2a\u804b\u5b50\uff0c\u96be\u9053\u6218\u5144\u8981 \u627e\u4e2a\u804b\u5b50\u6765\u62c5\u5f53\u5982\u6b64\u5927\u4e8b\u5417\uff1f\u201d \u3000\u3000\u90a3\u8eab\u8eaf\u9880\u957f\u7684\u6c49\u5b50\u6b63\u662f\u201c\u5317\u6597\u4e03\u715e\u201d\u4e2d\u7684\u201c\u4e8c\u715e\u201d\u83ab\u5357\uff0c\u6b64\u523b\u4e00\u624b\u4ecd\u81ea\u629a\u7740\u5251\u67c4\uff0c\u6c89\u58f0 \u9053\uff1a\u201c\u6211\u770b\u6218\u5144\u8fd8\u662f\u4e0d\u5fc5\u5982\u6b64\u56fa\u6267\u5427\uff0c\u5176\u5b9e\u4f60\u6211\u90fd\u662f\u6b66\u6797\u540c\u6e90\uff0c\u6709\u4ec0\u4e48\u4e8b\u4e0d\u597d\u8bf4\u7684\u3002\u201d\u76ee\u5149 \u4e00\u8f6c\uff0c\u53c8\u9053\uff1a\u201c\u5411\u5144\uff0c\u4f60\u8bf4\u53ef\u662f\uff1f\u201d \u3000\u3000\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e00\u6296\u624b\u4e2d\u94c1\u62d0\uff0c\u5389\u58f0\u9053\uff1a\u201c\u522b\u7684\u4e8b\u6211\u59d3\u5411\u7684\u90fd\u4e0d\u7ba1\uff0c\u53ea\u662f\u53eb\u6211\u59d3\u5411\u7684 \u542c\u547d\u4e8e\u4f60\u6218\u98de\uff0c\u90a3\u53ef\u4e0d\u6210\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u8f69\uff0c\u5389\u58f0\u9053\uff1a\u201c\u96be\u9053\u53eb\u6211\u6218\u98de\u542c\u547d\u4e8e\u4f60\u8fd9\u4e2a\u6b8b\u5e9f\u4e0d\u6210\u3002\u201d \u3000\u3000\u5411\u4e00\u557c\u5927\u559d\u4e00\u58f0\uff0c\u72ec\u811a\u5fae\u70b9\uff0c\u8eab\u5f62\u5df2\u63a0\u4e86\u8fc7\u53bb\uff0c\u53f3\u624b\u5fae\u6284\uff0c\u7adf\u5c06\u53f3\u808b\u631f\u7740\u7684\u94c1\u62d0\u201c\u547c\u201d \u5730\u62a1\u4e86\u8d77\u6765\uff0c\u201c\u7acb\u5288\u534e\u5cb3\u201d\uff0c\u5f53\u5934\u5411\u6218\u98de\u62a1\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u795e\u624b\u6218\u98de\u671b\u7740\u8fd9\u6709\u5982\u5c71\u5cb3\u822c\u538b\u4e0b\u7684\u62d0\u5f71\uff0c\u5634\u89d2\u9690\u542b\u51b7\u7b11\uff0c\u8eab\u5f62\u5374\u52a8\u4e5f\u4e0d\u52a8\uff0c\u773c\u770b\u8fd9\u52bf\u5982 \u5343\u94a7\u7684\u94c1\u62d0\uff0c\u5df2\u582a\u582a\u538b\u5230\u4ed6\u5934\u4e0a\uff0c\u54ea\u77e5\u65c1\u8fb9\u7a81\u7136\u98de\u8d77\u4e00\u6e9c\u9752\u5149\uff0c\u671d\u94c1\u62d0\u5934\u4e0a\u4e00\u70b9\uff0c\u4f46\u95fb \u201c\u6323\u201d\u5730\u4e00\u58f0\uff0c\u90a3\u94c1\u62d0\u52bf\u5934\u4e00\u504f\uff0c\u4fbf\u4ece\u6218\u98de\u8eab\u4fa7\u64e6\u4e86\u8fc7\u53bb\uff0c\u773c\u524d\u4e00\u9eef\uff0c\u70db\u706b\u53c8\u706d\u3002 \u3000\u3000\u5411\u4e00\u557c\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u83ab\u5144\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e8c\u715e\u83ab\u5357\u5fae\u5fae\u4e00\u7b11\uff0c\u5de6\u624b\u6cbf\u7740\u53f3\u624b\u6240\u6301\u7684\u957f\u5251\u5251\u810a\u4e00\u62b9\uff0c\u53c8\u5c06\u957f\u5251\u63d2\u5165\u9798\u91cc\uff0c\u7f13\u7f13\u7b11 \u9053\uff1a\u201c\u5411\u5144\u4e14\u83ab\u52a8\u6012\uff0c\u6b64\u4e8b\u65e2\u7136\u4e0d\u662f\u52a8\u624b\u53ef\u4ee5\u89e3\u51b3\u7684\uff0c\u5e73\u767d\u82b1\u4e9b\u529b\u6c14\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fae\u4e00\u8eac\u8eab\uff0c\u4ece\u5730\u4e0a\u5c06\u90a3\u6bb5\u8721\u70db\u62ff\u4e86\u8d77\u6765\uff0c\u5434\u9e23\u4e16\u4f38\u624b\u4e00\u6643\uff0c\u53c9\u6247\u7740\u4e86\u706b\u6298\u5b50\uff0c\u70b9\u4e0a \u706b\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u5404\u5e26\u7591\u95ee\uff0c\u88f4\u73cf\u6307\u4e86\u6307\u81ea\u5df1\uff0c\u6307\u4e86\u6307\u95e8\u5916\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u201c\u6211\u4eec\u8fd8\u662f\u8d70 \u5427\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u4e00\u9894\u9996\uff0c\u4ece\u6b63\u5728\u77aa\u76ee\u671b\u7740\u83ab\u6c0f\u5144\u5f1f\u53ca\u91d1\u9e21\u5411\u4e00\u557c\u7684\u795e\u624b\u6218\u98de\u8eab\u4fa7\u7ed5\u4e86\u8fc7\u53bb\uff0c\u4f38 \u624b\u62ff\u8d77\u90a3\u53e3\u5927\u5e03\u888b\u5b50\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u65e2\u7136\u6709\u4e8b\u5546\u91cf\uff0c\u5c0f\u53ef\u4eec\u5c31\u544a\u8f9e\u4e86\u3002\u201d\u88f4\u73cf\u8ddf\u5728\u540e \u9762\uff0c\u6b63\u5f85\u5f80\u5385\u5916\u8d70\u53bb\uff0c\u54ea\u77e5\u773c\u524d\u4e00\u82b1\uff0c\u5374\u89c1\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u624b\u6447\u6298\u6247\uff0c\u53c8\u81ea\u5f53\u95e8\u800c\u7acb\uff0c\u6321\u5728 \u81ea\u5df1\u9762\u524d\uff0c\u7adf\u4e0d\u8ba9\u81ea\u5df1\u51fa\u53bb\u3002 \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff0c\u53ea\u89c9\u81ea\u5df1\u7684\u906d\u9047\uff0c\u8d8a\u6765\u8d8a\u5947\uff0c\u5fc3\u91cc\u60f3\u95ee\u95ee\u9762\u524d\u8fd9\u9ad8\u5927\u5a01\u731b\u7684\u8001\u8005\uff0c\u5bf9\u81ea \u5df1\u7a76\u7adf\u6709\u4f55\u7528\u610f\uff0c\u5374\u53c8\u95ee\u4e0d\u51fa\u6765\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u53c8\u81ea\u6697\u6068\u7740\u81ea\u5df1\uff0c\u4e3a\u4ec0\u4e48\u5982 \u6b64\u65e0\u7528\uff0c\u5bf9\u4e00\u5207\u5c06\u8981\u964d\u4e34\u5230\u81ea\u5df1\u8eab\u4e0a\u7684\u4e8b\uff0c\u4e0d\u4f46\u65e0\u6cd5\u53cd\u6297\uff0c\u751a\u81f3\u8fde\u77e5\u9053\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5434\u9e23\u4e16\u4fa7\u76ee\u4e00\u671b\uff0c\u4ea6\u81ea\u671b\u5230\u4ed6\u9762\u4e0a\u8fd9\u79cd\u5982\u75f4\u5982\u679c\u7684\u795e\u60c5\uff0c\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u53e4\u4eba \u8bf4\u65e0\u5992\u7ea2\u989c\uff0c\u7ea2\u989c\u8584\u547d\uff0c\u8fd9\u88f4\u73cf\u867d\u975e\u7ea2\u989c\uff0c\u5374\u4e5f\u5982\u6b64\u8584\u547d\uff01\u9020\u5316\u5f04\u4eba\uff0c\u600e\u5730\u4e00\u81f3\u4e8e\u65af\uff0c\u660e\u660e \u9020\u4e86\u4e2a\u806a\u660e\u4fca\u79c0\u949f\u4e8e\u4e00\u8eab\u7684\u4eba\u7269\uff0c\u5374\u53c8\u504f\u504f\u8981\u4ee4\u4ed6\u53d7\u8bb8\u591a\u51e0\u4e4e\u4ee4\u4eba\u65e0\u6cd5\u5fcd\u53d7\u7684\u6298\u78e8\uff0c\u5509\u2014\u2014 \u6b64\u523b\u4ed6\u7adf\u8fde\u6211\u4eec\u6240\u8bf4\u7684\u8bdd\u90fd\u65e0\u6cd5\u542c\u5230\uff0c\u5fc3\u91cc\u7684\u611f\u89c9\uff0c\u7684\u786e\u662f\u4ee4\u4eba\u65e0\u6cd5\u5fcd\u53d7\u7684\u4e86\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4f46\u89c9\u8111\u4e2d\u5145\u6ee1\u4e0d\u5e73\u4e4b\u6c14\uff0c\u8de8\u524d\u4e00\u6b65\uff0c\u5927\u58f0\u53f1\u9053\u3002 \u3000\u3000\u201c\u5c0f\u53ef\u4e45\u95fb\u2018\u795e\u624b\u2019\u6218\u98de\u884c\u9053\u6c5f\u5357\uff0c\u662f\u6761\u54cd\u5f53\u5f53\u7684\u6c49\u5b50\uff0c\u53ea\u662f\u4eca\u65e5\u4e00\u89c1\uff0c\u5374\u53eb\u5728\u4e0b\u5931\u671b \u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u6545\u610f\u987f\u4f4f\u81ea\u5df1\u7684\u8bdd\u58f0\uff0c\u53ea\u89c1\u90a3\u795e\u624b\u6218\u98de\u9762\u5bb9\u679c\u7136\u4e3a\u4e4b\u4e00\u53d8\uff0c\u7528\u529b\u6447\u4e86\u6447\u624b\u4e2d\u7684\u6298\u6247\uff0c \u50cf\u4f3c\u8981\u5c06\u5fc3\u4e2d\u7684\u6012\u706b\u6247\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u5374\u5728\u65c1\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5434\u5144\u4eca\u65e5\u624d\u77e5\u9053\u5440\u2014\u2014\u563f\u563f\uff0c\u5728\u4e0b\u5374\u65e9\u5c31\u77e5\u9053 \u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u77aa\u76ee\u559d\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9e21\u5411\u4e00\u557c\u5140\u81ea\u563f\u563f\u51b7\u7b11\uff0c\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u5434\u9e23\u4e16\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9 \u2018\u795e\u624b\u2019\u6218\u98de\uff0c\u91d1\u9e21\u5411\u4e00\u557c\uff0c\u4e03\u5de7\u8ffd\u9b42\u90a3\u98de\u8679\uff0c\u83ab\u6c0f\u5144\u5f1f\uff0c\u4ff1\u662f\u6c5f\u5357\u6b66\u6797\u4e2d\u96c4\u8e1e\u4e00\u65b9\uff0c\u8d6b\u8d6b \u7684\u8349\u6cfd\u8c6a\u58eb\uff0c\u6b64\u523b\u90fd\u805a\u5728\u8fd9\u91cc\u6765\uff0c\u60f3\u5fc5\u90fd\u662f\u4e3a\u7740\u4e00\u4ef6\u6781\u4e3a\u91cd\u5927\u4e4b\u4e8b\uff0c\u800c\u7167\u6b64\u523b\u7684\u60c5\u51b5\u770b\u6765\uff0c \u4ed6\u4eec\u867d\u7ecf\u8fc7\u4e00\u756a\u5267\u6597\uff0c\u6b64\u4e8b\u5374\u4ecd\u672a\u89e3\u51b3\u2014\u2014\u4f46\u6b64\u4e8b\u5374\u7edd\u4e0d\u4f1a\u4e0e\u88f4\u73cf\u6709\u5173\uff0c\u90a3\u4e48\u4ed6\u4eec\u4e3a\u4f55\u5bf9\u4ed6 \u5982\u6b64\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5ff5\u5934\u5728\u4ed6\u5fc3\u4e2d\u4e00\u95ea\u800c\u8fc7\uff0c\u4ed6\u867d\u7136\u4ecd\u65e0\u6cd5\u4e86\u89e3\u6b64\u4e8b\u7684\u771f\u76f8\uff0c\u4f46\u5374\u5df2\u60f3\u51fa\u5bf9\u7b56\uff0c\u8be5\u5982\u4f55\u5e94 \u4ed8\u5f53\u4e0b\u8fd9\u79cd\u590d\u6742\u79bb\u5947\u7684\u5c40\u9762\u3002 \u3000\u3000\u4ed6\u5e72\u54b3\u4e00\u58f0\uff0c\u653e\u4e0b\u624b\u4e2d\u7684\u5e03\u888b\uff0c\u5fae\u5fae\u4e00\u6307\u88f4\u73cf\uff0c\u6717\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u60f3\u5fc5\u65e9\u5df2\u770b\u51fa\u655d\u53cb\u88f4\u73cf \u662f\u4e2a\u8eab\u7f79\u6b8b\u5e9f\u7684\u804b\u54d1\u4e4b\u4eba\uff0c\u4f55\u51b5\u4e0e\u9601\u4e0b\u7d20\u65e0\u7ea0\u845b\uff0c\u4e0d\u77e5\u9601\u4e0b\u62e6\u4f4f\u4ed6\u7684\u53bb\u8def\uff0c\u7a76\u662f\u4f55\u610f\uff1f\u201d \u3000\u3000\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u5fae\u5fae\u4e00\u6014\uff0c\u624b\u4e2d\u7684\u6298\u6247\uff0c\u8d8a\u6447\u8d8a\u7f13\uff0c\u60f3\u662f\u5728\u5bfb\u601d\u8be5\u5982\u4f55\u56de\u7b54\u4ed6\u7684\u8bdd\uff0c\u54ea \u77e5\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5728\u4e0b\u6b63\u662f\u8981\u8bf7\u8d35\u53cb\u6765\u505a\u6211\u7b49\u7684\u603b\u74e2\u628a\u5b50\u3002\u201d\u4e00\u624b\u53c8\u6447 \u8d77\u6298\u6247\uff0c\u6247\u98ce\u5439\u5f97\u4ecd\u7136\u6301\u5728\u88f4\u73cf\u624b\u4e2d\u7684\u8721\u70db\uff0c\u706b\u7130\u6447\u6447\u3002 \u3000\u3000\u5434\u9e23\u4e16\u867d\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u6b64\u523b\u5374\u4ecd\u4e0d\u7981\u4e00\u5934\u96fe\u6c34\uff0c\u5374\u542c\u201c\u7b03\uff0c\u7b03\u201d\u4e24\u58f0\uff0c\u90a3\u201c\u91d1\u9e21\u201d \u5411\u4e00\u557c\u62c4\u7740\u94c1\u62d0\uff0c\u8d70\u5230\u8fd1\u524d\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6b64\u523b\u51c9\u98ce\u4e60\u4e60\uff0c\u8925\u6691\u5168\u6d88\uff0c\u6b63\u662f\u5927\u597d\u826f\u5bb5\uff0c\u5434\u5144\u5982 \u4e0d\u5acc\u5f03\uff0c\u5728\u4e0b\u5012\u8981\u8bf4\u4e2a\u6781\u6709\u8da3\u5473\u7684\u6545\u4e8b\u7ed9\u5434\u5144\u542c\u542c\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fc3\u5ff5\u4e00\u52a8\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u53ef\u867d\u7136\u5b64\u964b\u5be1\u95fb\uff0c\u5374\u4e5f\u65e9\u95fb\u6c5f\u5357\u2018\u91d1\u9e21\u5e2e\u2019\u7684\u4ec1\u4e49\u5927 \u54e5\u2018\u91d1\u9e21\u2019\u5411\u4e00\u557c\u5411\u5927\u54e5\u7684\u58f0\u540d\uff0c\u53ea\u6068\u65e0\u7f18\u62dc\u8bc6\u800c\u5df2\uff0c\u5411\u5927\u54e5\u65e2\u7136\u8981\u5bf9\u5c0f\u53ef\u8bf4\u6545\u4e8b\uff0c\u5c0f\u53ef\u81ea \u7136\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u6717\u58f0\u4e00\u7b11\uff0c\u76ee\u5149\u659c\u8131\u6218\u98de\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u6b66\u6797\u795e\u7ae5\u7684\u5927 \u540d\uff0c\u5728\u4e0b\u4ea6\u662f\u542c\u5f97\u4e45\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5434\u5144\u3002\u4f60\u53ef\u77e5\u9053\uff0c\u4eca\u65e5\u6b66\u6797\u4e2d\u540d\u7b26\u5176\u5b9e\u7684\u4eba\u56fa\u7136\u5f88\u591a\uff0c\u6b3a\u4e16 \u76d7\u540d\u4e4b\u8f88\uff0c\u5374\u4e5f\u4e0d\u5c11\u54e9\u3002\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u6545\u610f\u518d\u4e5f\u4e0d\u671b\u6218\u98de\u4e00\u773c\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4ece\u524d\u6709\u4f4d\u4ec1 \u5144\uff0c\u5c31\u662f\u8fd9\u79cd\u6d6a\u5f97\u865a\u58f0\u7684\u89d2\u8272\uff0c\u4ed6\u5728\u6c5f\u6e56\u4e2d\u6df7\u4e86\u6570\u5341\u5e74\uff0c\u6b66\u529f\u867d\u4e0d\u574f\uff0c\u4eba\u7f18\u5374\u4e0d\u597d\uff0c\u4f46\u8fd9\u4f4d \u4ec1\u5144\u5374\u6709\u70b9\u4e0d\u81ea\u91cf\u529b\uff0c\u5c45\u7136\u60f3\u505a\u6c5f\u6e56\u4e2d\u597d\u4e9b\u6210\u540d\u7acb\u4e07\u7684\u670b\u53cb\u7684\u603b\u74e2\u628a\u5b50\uff0c\u5434\u5144\uff0c\u4f60\u60f3\u60f3\u770b\uff0c \u4ed6\u5fc3\u91cc\u60f3\u5f97\u867d\u5982\u610f\uff0c\u53ef\u662f\u4eba\u5bb6\u600e\u4f1a\u7b54\u5e94\u5462\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u54c8\u54c8\u4e00\u7b11\uff0c\u76ee\u5149\u76f4\u6ce8\u5230\u201c\u795e\u624b\u201d\u6218\u98de\u8eab\u4e0a\uff0c\u53ea\u89c1\u4ed6\u624b\u81c2\u6447\u7740\u6298\u6247\uff0c\u4e00\u9762\u9053\uff1a\u201c\u597d \u70ed\uff0c\u597d\u70ed\u3002\u201d\u4e00\u526f\u82e5\u65e0\u5176\u4e8b\u7684\u6837\u5b50\uff0c\u751f\u50cf\u8fd9\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u6240\u8bf4\u7684\u6545\u4e8b\uff0c\u6839\u672c\u4e0e\u81ea\u5df1\u65e0\u5173\u3002 \u3000\u3000\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u66f4\u662f\u773c\u89d2\u4e5f\u4e0d\u8198\u4ed6\u4e00\u773c\uff0c\u5140\u81ea\u7b11\u9053\uff1a\u201c\u4f46\u662f\u90a3\u4f4d\u4ec1\u5144\u8fd8\u4e0d\u6b7b\u5fc3\uff0c\u6545\u610f \u627e\u4e86\u4e2a\u501f\u53e3\uff0c\u5c06\u4e00\u4e9b\u6b66\u6797\u4e2d\u6700\u6709\u52bf\u529b\uff0c\u58f0\u540d\u4e5f\u6700\u54cd\u7684\u670b\u53cb\uff0c\u627e\u5230\u4e00\u4e2a\u8352\u5b85\u91cc\u53bb\uff0c\u60f3\u7528\u6b66\u529f\u6765 \u80c1\u8feb\u90a3\u4e9b\u670b\u53cb\u627f\u8ba4\u4ed6\u662f\u6c5f\u5357\u6b66\u6797\u7fa4\u8c6a\u7684\u603b\u74e2\u628a\u5b50\uff0c\u54ea\u77e5\u4ed6\u5982\u610f\u7b97\u76d8\u6253\u5f97\u86ee\u597d\uff0c\u5230\u4e86\u90a3\u65f6\u4ed6\u624d \u53d1\u73b0\u90a3\u4e9b\u6210\u540d\u7acb\u4e07\u7684\u670b\u53cb\uff0c\u6b66\u529f\u867d\u6ca1\u6709\u4ed6\u9ad8\uff0c\u4f46\u5927\u5bb6\u4e00\u8054\u624b\uff0c\u4ed6\u4e5f\u53ea\u6709\u5e72\u77aa\u773c\u7684\u4efd\u513f\uff0c\u65e0\u6cd5 \u5948\u4eba\u5bb6\u7684\u4f55\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u201c\u563f\u563f\u201d\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u671b\u7740\u9662\u4e2d\u7684\u661f\u5149\uff0c\u5434\u545c\u4e16\u5fc3\u4e2d\u6697\u7b11\uff0c\u4e00 \u4e00\u9762\u6697\u4ed8\uff1a\u201c\u539f\u6765\u8fd9\u2018\u795e\u624b\u2019\u6218\u98de\u60f3\u505a\u6c5f\u5357\u5f3a\u76d7\u5934\u5b50\uff0c\u6240\u4ee5\u624d\u5c06\u8fd9\u5411\u4e00\u786c\u6458\u786c\u62ff\u7684\u2018\u91d1\u9e21 \u5e2e\u2019\u7684\u8001\u5927\u2018\u91d1\u9e21\u2019\u5411\u4e00\u557c\uff0c\u4e13\u95e8\u9760\u8499\u6c57\u836f\uff0c\u8ffd\u9b42\u9999\u8d77\u5bb6\u7684\u98de\u8d3c\u5e2e\u7684\u603b\u74e2\u628a\u5b50\u2018\u4e03\u5de7\u8ffd\u9b42\u2019 \u90a3\u98de\u8679\uff0c\u548c\u6c5f\u5357\u9ed1\u9053\u4e2d\u624b\u628a\u5b50\u6700\u786c\u7684\u2018\u5317\u6597\u4e03\u715e\u2019\u4e2d\u7684\u8001\u5927\uff0c\u8001\u56db\u90fd\u627e\u5230\u8fd9\u91cc\u6765\uff0c\u5440\uff0c\u8fd9\u59d3 \u6218\u7684\u91ce\u5fc3\u53ef\u771f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u5374\u542c\u90a3\u201c\u91d1\u9e21\u201d\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u59d3\u5411\u7684\u8bb2\u8bdd\u4e00\u662f\u4e00\uff0c\u4e8c\u662f\u4e8c\uff0c\u4ece\u6765\u4e0d\u8981\u82b1\u62db\uff0c\u90a3\u4f4d \u4ec1\u5144\u624b\u5e95\u4e0b\u4e5f\u7684\u786e\u6709\u4e24\u4e0b\u5b50\uff0c\u5c24\u5176\u662f\u4ed6\u4e0d\u77e5\u4ece\u54ea\u91cc\u5b66\u6765\u7684\u4e00\u79cd\u50cf\u662f\u2018\u5148\u5929\u771f\u6c14\u2019\u4e00\u7c7b\u7684\u529f \u592b\uff0c\u90a3\u4e9b\u7d20\u6765\u5728\u6b66\u6797\u4e2d\u51ed\u7740\u771f\u672c\u4e8b\u6210\u540d\u7acb\u4e07\u7684\u670b\u53cb\uff0c\u867d\u7136\u56db\u4e2a\u8054\u624b\uff0c\u4e5f\u4e0d\u80fd\u628a\u4ed6\u600e\u4e48\u6837\uff0c\u5927 \u5bb6\u8c01\u4e5f\u5948\u4f55\u4e0d\u4e86\u8c01\uff0c\u672c\u5e94\u65e0\u4e8b\u4e86\uff0c\u563f\uff0c\u5434\u5144\uff0c\u4f60\u731c\u90a3\u4f4d\u4ec1\u5144\u600e\u5730\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u5434\u9e23\u4e16\u77e5\u9053\u81ea\u5df1\u82e5\u4e0d\u5e2e\u4e0a\u4e24\u53e5\u8154\uff0c\u8fd9\u5411\u4e00\u557c\u7684\u8bdd\u5c31\u65e0\u6cd5\u8bf4\u4e0b\u53bb\u4e86\uff0c\u65b9\u60f3\u6447 \u5934\u9053\uff1a\u201c\u731c\u4e0d\u5230\u3002\u201d\u54ea\u77e5\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u6027\u5b50\u6025\u5f97\u5f88\uff0c\u6839\u672c\u672a\u7b49\u4ed6\u8bf4\u8bdd\uff0c\u53f3\u638c\u62cd\u5927\u817f\uff0c\u5c31 \u53c8\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4f4d\u4ec1\u5144\u5c45\u7136\u5f02\u60f3\u5929\u5f00\uff0c\u53c8\u5f04\u4e86\u532a\u5937\u6240\u601d\u7684\u4e3b\u610f\u51fa\u6765\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u8d76\u7d27\u63a5\u53e3\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u59d3\u5411\u7684\u867d\u7136\u662f\u4e2a\u7c97\u6c49\u5b50\uff0c\u53ef\u662f\u4ee5\u524d\u5374\u4e5f\u8bfb\u8fc7\u4e24\u5929 \u4e66\uff0c\u77e5\u9053\u4ee5\u524d\u6709\u4e9b\u597d\u5b98\u5978\u81e3\u81ea\u5df1\u60f3\u505a\u7687\u5e1d\u505a\u4e0d\u4e0a\uff0c\u6216\u8bb8\u662f\u4e0d\u6562\u505a\u7684\u65f6\u5019\uff0c\u5c31\u5f04\u4e2a\u5c0f\u5b69\u5b50\uff0c\u6216 \u8005\u662f\u7cca\u6d82\u866b\u672a\u6302\u4e2a\u7687\u5e1d\u7684\u540d\uff0c\u5176\u5b9e\u771f\u6b63\u7684\u7687\u5e1d\uff0c\u5374\u8fd8\u662f\u4ed6\u81ea\u5df1\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u4e00\u987f\uff0c\u5c48\u7740\u4e00\u53ea\u624b\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\u66f9\u64cd\uff0c\u5c31\u662f\u8fd9\u79cd\u89d2\u8272\uff0c\u4ed6\u867d\u7136\u4e00\u8f88\u5b50\u6ca1\u6709 \u5f53\u7687\u5e1d\uff0c\u4f46\u5374\u5f04\u5f97\u8ba9\u7687\u5e1d\u542c\u4ed6\u7684\u8bdd\uff0c\u5434\u5144\uff0c\u4f60\u8bf4\uff0c\u8fd9\u548c\u7687\u5e1d\u6709\u4ec0\u4e48\u4e24\u6837\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u4e00\u9894\u9996\uff0c\u5fc3\u4e0b\u5df2\u81ea\u604d\u7136\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u8fd9\u2018\u795e\u624b\u2019\u6218\u98de\u81ea\u5df1\u5f53\u4e0d\u6210\u6c5f\u5357\u9ed1\u9053\u7fa4 \u96c4\u7684\u2018\u603b\u74e2\u628a\u5b50\u2019\uff0c\u5c31\u60f3\u968f\u4fbf\u5f04\u4e2a\u4eba\u51fa\u6765\u5f53\uff0c\u518d\u53eb\u8fd9\u4e2a\u4eba\u53d7\u81ea\u5df1\u7684\u631f\u6301\uff0c\u2018\u631f\u5929\u4e88\u4ee5\u4ee4\u8bf8 \u4faf\u2019\uff0c\u54c8\uff0c\u8fd9\u59d3\u6218\u7684\u60f3\u5f97\u5230\u8fd8\u771f\u4e0d\u9519\u2014\u2014\u201d\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u5374\u542c\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u51b7\u7b11\u4e00 \u58f0\uff0c\u679c\u7136\u8bf4\u9053\uff1a\u201c\u65b9\u624d\u6211\u8bf4\u7684\u90a3\u4f4d\u4ec1\u5144\uff0c\u5c45\u7136\u4e5f\u60f3\u5b66\u66f9\u64cd\uff0c\u773c\u89c1\u81ea\u5df1\u5f53\u603b\u74e2\u628a\u5b50\u5df2\u662f\u65e0\u671b\uff0c \u5c31\u8bf4\uff1a\u2018\u4eca\u65e5\u6c5f\u5357\u6b66\u6797\uff0c\u7406\u5e94\u540c\u5fc3\u4e00\u81f4\uff0c\u4e00\u5b9a\u8981\u6709\u4e2a\u7edf\u7b79\u4e00\u5207\u7684\u4eba\u7269\uff0c\u5404\u4f4d\u65e2\u7136\u4e0d\u8ba9\u5728\u4e0b\u6765 \u505a\u8fd9\u4e8b\uff0c\u90a3\u4e48\u8be5\u8c01\u6765\u505a\u5462\uff1f\u2019\u201c\u8fd9\u201d\u91d1\u9e21\u201c\u5411\u4e00\u557c\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5c06\u53f3\u624b\u6447\u6765\u6447\u53bb\uff0c\u5434\u9e23 \u4e16\u671b\u7740\u4ed6\u7684\u6837\u5b50\uff0c\u518d\u4e00\u60f3\u90a3\u201d\u795e\u624b\u201c\u6218\u98de\u6447\u6298\u6247\u8bf4\u8bdd\u7684\u795e\u6001\uff0c\u4e0d\u7981\u201d\u5657\u55e4\u201c\u4e00\u58f0\uff0c\u5931\u58f0\u7b11\u4e86 \u51fa\u6765\u3002\u201d\u795e\u624b\u201c\u6218\u98de\u9762\u5bd2\u5982\u6c34\uff0c\u5140\u81ea\u671b\u7740\u95e8\u5916\uff0c\u90a3\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u548c\u83ab\u6c0f\u5144\u5f1f\uff0c\u9762\u4e0a\u4e5f\u6ca1\u6709\u7b11 \u5bb9\uff0c\u53ea\u6709\u90a3\u5411\u4e00\u557c\u89c1\u5230\u5434\u9e23\u4e16\u7684\u8fd9\u4e00\u7b11\uff0c\u5fc3\u4e0b\u4eff\u4f5b\u9887\u4e3a\u5f97\u610f\uff0c\u54c8\u54c8\u5927\u7b11\u4e86\u51e0\u58f0\uff0c\u63a5\u7740\u5f80\u4e0b\u8bf4 \u9053\uff1a\u201c\u4ed6\u8bdd\u867d\u662f\u5982\u6b64\u8bf4\uff0c\u53ef\u662f\u4eba\u5bb6\u65e2\u7136\u4e0d\u8ba9\u4ed6\u5f53\u603b\u74e2\u628a\u5b50\uff0c\u4ed6\u5f53\u7136\u4e5f\u4e0d\u4f1a\u8ba9\u4eba\u5bb6\u6765\u5f53\uff0c\u5c31\u53c8 \u8bf4\u9053\uff1a\u2018\u4f9d\u5728\u4e0b\u4e4b\u610f\uff0c\u8fd9\u4e8b\u6700\u597d\u8ba9\u4e2a\u4e0e\u4f60\u6211\u65e0\u5173\u7684\u4eba\u6765\u505a\u3002\u2019\u5927\u5bb6\u5c31\u95ee\u4ed6\uff1a\u201c\u8c01\u5462\uff1f\u2018\u4ed6\u6545 \u610f\u60f3\u4e86\u534a\u5929\uff0c\u7a81\u7136\u627e\u4e86\u4e00\u526f\u7b14\u58a8\u6765\uff0c\u753b\u4e86\u4e00\u5e45\u753b\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u968f\u624b\u4e00\u6307\u6302\u5728\u5899\u89d2\u7684\u90a3 \u5e45\u753b\uff0c\u53c8\u9053\uff1a\u201c\u5c31\u662f\u90a3\u5e45\uff0c\u5434\u5144\u60f3\u5fc5\u4e5f\u770b\u5230\u4e86\uff0c\u5927\u5bb6\u770b\u4ed6\u7a81\u7136\u753b\u4e86\u5e45\u753b\u51fa\u6765\uff0c\u5fc3\u91cc\u90fd\u611f\u5230\u5947 \u602a\uff0c\u4ee5\u4e3a\u4ed6\u53c8\u8981\u5356\u5f04\u81ea\u5df1\u7684\u624d\u534e\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u7a81\u53c9\u4e00\u987f\uff0c\u4f46\u968f\u5373\u53c8\u9053\uff1a\u201c\u54e6\uff0c\u5434\u5144\uff0c\u6211\u8fd8\u5fd8\u4e86\u544a\u8bc9\u4f60\uff0c\u8fd9\u4f4d\u4ec1\u5144\u4e0d\u4f46\u6b66\u529f\u4e0d \u9519\uff0c\u800c\u4e14\u8fd8\u98ce\u96c5\u5f97\u5f88\uff0c\u5e73\u65e5\u8fd8\u559c\u6b22\u5199\u4e24\u7b14\u5b57\uff0c\u753b\u4e24\u5e45\u753b\uff0c\u4e0b\u4e24\u76d8\u68cb\uff0c\u4ed6\u81ea\u5df1\u5c31\u5f97\u610f\u5f97\u4e0d\u5f97 \u4e86\uff0c\u5e38\u5e38\u8bf4\u81ea\u5df1\u7684\u4e00\u53cc\u624b\u6bd4\u795e\u4ed9\u8fd8\u7075\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u54c8\u54c8\u4e00\u7b11\uff0c\u5fc3\u4e2d\u66f4\u662f\u604d\u7136\uff0c\u5374\u542c\u5411\u4e00\u557c\u53c8\u9053\uff1a\u201c\u4e8e\u662f\u5927\u5bb6\u5c31\u95ee\uff1a\u2018\u6b64\u753b\u4f55\u610f\uff1f\u2019 \u4ed6\u653e\u4e0b\u753b\u7b14\u6545\u610f\u88c5\u51fa\u4e00\u526f\u4ec1\u4e49\u9053\u5fb7\u7684\u6837\u5b50\uff0c\u8bf4\uff1a\u2018\u4eca\u65e5\u6c5f\u5357\u6b66\u6797\u4e0a\u7ebf\u5f00\u6252\u7684\u670b\u53cb\uff0c\u5c31\u597d\u50cf\u753b \u4e0a\u7684\u8fd9\u4e2a\u778e\u5b50\u4e00\u6837\uff0c\u53ea\u77e5\u542c\u5230\u7684\u7b1b\u58f0\u7f8e\u5999\u5f97\u5f88\uff0c\u5c31\u81ea\u5df1\u4ee5\u4e3a\u81ea\u5df1\u7684\u8033\u798f\u4e0d\u9519\uff0c\u5374\u60f3\u4e0d\u5230\u81ea\u5df1 \u5df2\u7ecf\u4e00\u811a\u8e0f\u7a7a\uff0c\u82e5\u6ca1\u6709\u4eba\u5373\u65f6\u8d76\u6765\u62c9\u4e0a\u4e00\u628a\uff0c\u5c31\u9a6c\u4e0a\u8981\u6389\u5230\u4e07\u4e08\u7edd\u58d1\u91cc\u53bb\u4e86\u3002\u201c\u201d\u4ed6\u8bf4\u4e86\u8fd9 \u8bdd\uff0c\u5c31\u628a\u8fd9\u5e45\u753b\u6302\u5230\u5899\u4e0a\u53bb\uff0c\u5927\u5bb6\u8fd8\u662f\u4e0d\u660e\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u54ea\u77e5\u4ed6\u53c8\u8bf4\u9053\uff1a\u2018\u73b0\u5728\u6211\u8fd9\u5e45\u753b\u6302 \u5728\u8fd9\u91cc\uff0c\u628a\u8fd9\u526f\u7b14\u58a8\u653e\u5728\u65c1\u8fb9\uff0c\u8981\u662f\u6709\u8c01\u80fd\u628a\u8fd9\u753b\u4e0a\u7684\u778e\u5b50\u6551\u4e0a\u4e00\u6551\uff0c\u5728\u8fd9\u5e45\u753b\u4e0a\u52a0\u4e0a\u51e0 \u7b14\uff0c\u90a3\u4ed6\u5c31\u662f\u6211\u4eec\u7684\u603b\u74e2\u628a\u5b50\u3002\u2019\u201c\u201d\u5927\u5bb6\u4e00\u542c\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u63d0\u51fa\u53cd\u5bf9\u7684\u610f\u601d\u6765\uff0c\u54ea\u77e5\u4ed6\u5374\u6709 \u4e00\u5957\u89e3\u91ca\u7684\u82b1\u8a00\u5de7\u8bed\uff0c\u4ed6\u8bf4\uff1a\u2018\u8fd9\u5ea7\u8352\u5b85\u662f\u6709\u540d\u7684\u9b3c\u5b85\uff0c\u5e73\u5e38\u6839\u672c\u6ca1\u6709\u4eba\u6765\uff0c\u8981\u662f\u6709\u4eba\u51d1\u5de7 \u6765\u66ff\u8fd9\u5e45\u753b\u52a0\u4e0a\u4e9b\u4e1c\u897f\uff0c\u90a3\u5c31\u662f\u65e0\u610f\uff0c\u662f\u8001\u5929\u8ba9\u4ed6\u6765\u505a\u6c5f\u5357\u7eff\u6797\u7684\u603b\u74e2\u628a\u5b50\u7684\u3002\u2019\u201c\u201d\u4ed6\u8fd8 \u8bf4\uff1a\u2018\u800c\u4e14\u8fd9\u4e2a\u4eba\u65e2\u7136\u6562\u5230\u9b3c\u5b85\u6765\uff0c\u4e00\u5b9a\u80c6\u5b50\u5f88\u5927\uff0c\u4ed6\u770b\u5230\u8fd9\u5e45\u753b\uff0c\u80fd\u591f\u60f3\u51fa\u4e00\u4e2a\u6551\u8fd9\u753b\u4e0a \u778e\u5b50\u7684\u529e\u6cd5\u6765\uff0c\u90a3\u8fd9\u4e2a\u4e0d\u4f46\u80c6\u5b50\u5927\uff0c\u8fd8\u4e00\u5b9a\u662f\u4e2a\u65e2\u806a\u660e\u3001\u53c8\u4ec1\u6148\u7684\u4eba\uff0c\u8fd9\u6837\u7684\u4eba\u6765\u505a\u6211\u4eec\u7684 \u603b\u74e2\u628a\u5b50\uff0c\u90a3\u4e48\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\uff0c\u5c31\u7b97\u4ed6\u4e0d\u4f1a\u6b66\u529f\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u53cd\u6b63\u4ed6\u53ea\u8981\u52a8\u52a8\u8111 \u7b4b\uff0c\u53d1\u53d1\u53f7\u4ee4\u5c31\u884c\u4e86\uff0c\u4e5f\u4e0d\u8981\u4ed6\u771f\u7684\u81ea\u5df1\u52a8\u624b\u3002\u2019\u201c\u8bf4\u5230\u8fd9\u91cc\uff0c\u201d\u91d1\u9e21\u201c\u5411\u4e00\u557c\u957f\u957f\u5598\u4e86\u53e3 \u6c14\uff0c\u800c\u672c\u6765\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\u7684\u5434\u545c\u4e16\uff0c\u6b64\u523b\u5374\u5df2\u5c06\u6b64\u4e8b\u7684\u524d\u56e0\u540e\u679c\uff0c\u5168\u90e8\u4e86\u7136\uff0c\u53ea\u662f\u4ed6\u5374\u4ecd\u7136 \u6709\u4e9b\u5947\u602a\uff0c\u6697\u4e2d\u5bfb\u601d\u9053\uff1a\u201c\u8fd9\u2018\u795e\u624b\u2019\u6218\u98de\u679c\u7136\u662f\u4e2a\u67ad\u96c4\u4e4b\u624d\uff0c\u80fd\u60f3\u51fa\u8fd9\u4e9b\u5343\u5947\u767e\u602a\uff0c\u95fb\u6240 \u672a\u95fb\u7684\u7406\u7531\u6765\uff0c\u8fbe\u5230\u81ea\u5df1\u2018\u631f\u5929\u5b50\u4ee5\u4ee4\u8bf8\u4faf\u2019\u7684\u76ee\u7684\uff0c\u53ef\u662f\u8fd9\u83ab\u6c0f\u5144\u5f1f\uff0c\u90a3\u98de\u8679\u7b49\u4eba\u5374\u4e5f\u4e0d \u662f\u5446\u5b50\uff0c\u4ed6\u4eec\u65e2\u7136\u731c\u51fa\u8fd9\uff0c\u795e\u624b\u2018\u6218\u98de\u7684\u7528\u610f\uff0c\u5374\u53c8\u600e\u4f1a\u7b54\u5e94\u4ed6\u8fd9\u63d0\u8bae\u5462\uff1f\u201d\u5374\u542c\u5411\u4e00\u557c\u4e00 \u6e05\u5589\u5499\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u8fd9\u8bdd\u8bf4\u5f97\u867d\u4f3c\u6781\u6709\u9053\u7406\uff0c\u4f46\u5927\u5bb6\u65e9\u5c31\u770b\u7834\u4ed6\u7684\u7528\u5fc3\uff0c\u672c\u5e94\u8fd8\u662f\u4e0d\u7b54\u5e94\uff0c \u54ea\u77e5\u5728\u8fd9\u4e9b\u4eba\u91cc\uff0c\u5374\u5df2\u6709\u4eba\u548c\u4ed6\u6709\u7740\u540c\u6837\u7684\u5fc3\u601d\uff0c\u4e5f\u60f3\u81ea\u5df1\u73a9\u73a9\u66f9\u64cd\u7684\u628a\u620f\uff0c\u662f\u4ee5\u4e09\u8a00\u4e24\u8bed \u4e4b\u540e\uff0c\u7adf\u7136\u5c31\u5c06\u6b64\u4e8b\u51fb\u638c\u6572\u5b9a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u773c\u89d2\u659c\u779f\u83ab\u6c0f\u5144\u5f1f\u4e00\u773c\u3002 \u3000\u3000\u4e8e\u662f\u5434\u9e23\u4e16\u5fc3\u4e2d\u6700\u540e\u4e00\u4e2a\u7591\u95ee\uff0c\u4fbf\u4e5f\u604d\u7136\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u76ee\u5149\u8f6c\u53d8\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u53c8\u81ea\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u4f4d\u4ec1\u5144\u89c1\u5230\u5927\u5bb6\u90fd\u65e0\u5f02\u8bae\uff0c \u81ea\u7136\u9ad8\u5174\u5f97\u5f88\uff0c\u987b\u77e5\u8fd9\u4e9b\u4eba\u90fd\u662f\u6c5f\u5357\u7eff\u6797\u4e2d\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u53ea\u8981\u4ed6\u4eec\u7b54\u5e94\u4e86\uff0c\u522b\u7684\u4eba\u5c31\u518d\u4e5f \u4e0d\u6210\u95ee\u9898\uff0c\u800c\u4e14\u4ed6\u4eec\u53ea\u8981\u6d3b\u4e00\u51fa\u53e3\uff0c\u4fbf\u4e0d\u4f1a\u66f4\u6539\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u5176\u4e2d\u53ea\u6709\u4e00\u4e2a\u4eba\u5bf9\u8fd9\u4ef6\u4e8b\u5927\u5927\u4e0d\u4ee5\u4e3a\u7136\uff0c\u53ea\u662f\u4ed6\u89c1\u5927\u5bb6\u90fd\u7b54\u5e94\uff0c\u81ea\u5df1\u4fbf\u4e5f\u65e0\u6cd5\u53cd \u5bf9\uff0c\u8fd9\u65f6\u5019\u90a3\u4f4d\u4e00\u5fc3\u60f3\u6548\u6cd5\u66f9\u64cd\u7684\u670b\u53cb\u7a81\u5730\u4e00\u62cd\u53cc\u638c\uff0c\u90a3\u5ea7\u8352\u5b85\u5916\u9762\uff0c\u7adf\u84e6\u5730\u63a0\u8fdb\u4e03\u516b\u4e2a\u52b2 \u88c5\u4f69\u5251\u7684\u6c49\u5b50\u6765\uff0c\u539f\u6765\u8fd9\u4eba\u65e9\u5df2\u8ba1\u5212\u5f97\u5468\u5468\u8be6\u8be6\uff0c\u7adf\u7136\u5148\u7559\u4e0b\u540e\u624b\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u53ea\u6015\u8fd9\u4e9b\u4eba\u90fd\u4e0d\u4f1a\u4ec5\u4ec5\u662f\u5b64\u8eab\u800c\u6765\u7684\u5427\uff1f\u201d\u5374\u89c1\u5411\u4e00\u557c\u53c8 \u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u8fdb\u6765\u4e4b\u540e\uff0c\u90a3\u4f4d\u4ec1\u5144\u5c31\u627e\u4e86\u4e00\u4eba\uff0c\u8eb2\u5728\u90a3\u623f\u5b50\u7684\u627f\u6881\u4e0a\u9762\uff0c\u544a\u8bc9\u4ed6\u53ea\u8981\u6709\u4eba\u5728 \u90a3\u5e45\u753b\u4e0a\u753b\u52a0\u4e0a\u51e0\u7b14\u5c31\u7acb\u523b\u4ee5\u54e8\u58f0\u901a\u77e5\u5927\u5bb6\u2014\u2014\u201d\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u76ee\u5149\u4e2d\u6ee1\u542b\u8ba5\u5632\u4e4b\u610f\uff0c\u53c8 \u9053\uff1a\u201c\u54ea\u77e5\u90a3\u4f4d\u4ec1\u5144\u7b97\u6765\u7b97\u53bb\uff0c\u8fd8\u662f\u7b97\u6f0f\u4e86\u4e00\u7740\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\uff0c\u6765\u5728\u90a3\u5e45\u753b\u4e0a\u52a8\u7b14\u7684\u4eba\uff0c \u7adf\u662f\u4e2a\u2014\u2014\u54fc\uff0c\u5434\u5144\uff0c\u4f60\u770b\u8fd9\u6545\u4e8b\u53ef\u8fd8\u6709\u8da3\u3002\u201d \u3000\u3000\u8bed\u58f0\u65b9\u843d\uff0c\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u7a81\u5730\u4ef0\u5929\u957f\u7b11\u8d77\u6765\uff0c\u7f13\u7f13\u626d\u56de\u5934\uff0c\u76ee\u5149\u51db\u7136\u671b\u7740\u5411\u4e00\u557c\uff0c\u6717 \u7b11\u4e4b\u58f0\u4fbf\u4e5f\u53d8\u4e3a\u51b7\u7b11\u9053\uff1a\u201c\u8001\u592b\u4e00\u5411\u53ea\u77e5\u9053\u2018\u91d1\u9e21\u2019\u5411\u4e00\u557c\u5411\u5927\u4fa0\u624b\u4e2d\u4e00\u6839\u5bd2\u94c1\u62d0\u6709\u7740\u60ca\u4eba \u7684\u62db\u6570\uff0c\u5374\u4e0d\u77e5\u9053\u5411\u5144\u820c\u5934\u4e0a\u7684\u62db\u6570\uff0c\u5374\u66f4\u662f\u5389\u5bb3\u54e9\u3002\u201d \u3000\u3000\u5411\u4e00\u557c\u5fae\u5fae\u51b7\u7b11\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff0c\u6bd4\u8d77\u9601\u4e0b\u6765\u2014\u2014\u563f\u563f\uff0c\u53ea\u6015\u8fd8\u5dee\u5f97\u8fdc\u54e9\u3002\u201d \u3000\u3000\u54ea\u77e5\u201c\u795e\u624b\u201d\u6218\u98de\u6389\u8f6c\u5934\u53bb\uff0c\u6839\u672c\u4e0d\u7406\u4ed6\uff0c\u5411\u5434\u9e23\u4e16\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u65b9\u624d\u542c\u8fd9\u4f4d\u5411\u5e2e \u4e3b\u8bf4\u4e86\u4e2a\u6545\u4e8b\uff0c\u53ef\u6709\u5174\u8da3\u518d\u542c\u5728\u4e0b\u8bf4\u4e2a\u6545\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u4e00\u7b11\u9053\uff1a\u201c\u81ea\u7136\u6d17\u8033\u606d\u542c\u3002\u201d\u4ed6\u5634\u91cc\u867d\u5728\u8bf4\u7740\u8bdd\uff0c\u5fc3\u91cc\u5374\u5728\u6697\u4e2d\u601d\u5fd6\uff1a\u201c\u5982\u6b64\u770b \u6765\uff0c\u6211\u8fd9\u88f4\u5144\u662f\u5154\u4e0d\u4e86\u8981\u5f53\u4e0a\u51e0\u5929\u6c5f\u5357\u9ed1\u9053\u7684\u76df\u4e3b\u4e86\uff0c\u8fd9\u4e8b\u5012\u7684\u786e\u6709\u8da3\u5f97\u5f88\u3002\u201d\u56de\u76ee\u4e00\u671b\u88f4 \u73cf\uff0c\u53ea\u89c1\u4ed6\u4e24\u773c\u671b\u7740\u5929\u82b1\u677f\uff0c\u4ecd\u7136\u662f\u4e00\u526f\u5982\u75f4\u5982\u5446\u7684\u6837\u5b50\uff0c\u50cf\u662f\u53c8\u9677\u4e8e\u6c89\u601d\u91cc\u3002 \u3000\u3000\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u4e00\u7b11\uff0c\u201c\u5587\u201d\u5730\uff0c\u5c06\u624b\u4e2d\u7684\u6298\u6247\u6536\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u670b\u53cb\u9762\u524d\u4e0d\u8bf4\u6697 \u8bdd\uff0c\u5728\u4e0b\u5728\u9601\u4e0b\u8fd9\u7b49\u806a\u660e\u4eba\u9762\u524d\uff0c\u4e5f\u4e0d\u5fc5\u5b66\u90a3\u79cd\u5c0f\u4eba\uff0c\u5c06\u5fc3\u91cc\u8981\u8bf4\u7684\u8bdd\uff0c\u8981\u9a7e\u7684\u4eba\uff0c\u90fd\u906e\u906e \u63a9\u63a9\uff0c\u62d0\u5f2f\u889c\u89d2\u4ed6\u8bf4\u51fa\u6765\u2014\u2014\u201d\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u5728\u5434\u5144\u8fd9\u7b49\u806a \u660e\u4eba\u9762\u524d\uff0c\u8bf4\u8d77\u8bdd\u6765\uff0c\u60f3\u5fc5\u5c31\u662f\u906e\u906e\u63a9\u63a9\uff0c\u62d0\u5f2f\u62b9\u89d2\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9f3b\u5b54\u91cc\u91cd\u54fc\u4e86\u4e00\u58f0\uff0c\u5934\u4e5f\u4e0d\u4fa7\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u867d\u7136\u4e45\u5728\u6cb3\u6714\uff0c\u5bf9\u6c5f\u5357 \u6b66\u6797\u60c5\u51b5\uff0c\u8f83\u4e3a\u751f\u758f\uff0c\u60f3\u5fc5\u4e5f\u4f1a\u77e5\u9053\uff0c\u4eca\u65e5\u6c5f\u5357\u6b66\u6797\u4e2d\uff0c\u4e5f\u6b63\u50cf\u6cb3\u6714\u4e00\u6837\uff0c\u51e0\u4e4e\u5168\u53d8\u6210\u4e86 \u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u5929\u4e0b\uff0c\u90a3\u9f99\u5f62\u516b\u638c\u6a80\u660e\uff0c\u8fd1\u5e74\u6765\u867d\u5c11\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\uff0c\u4f46\u904d\u5e03\u5357\u4e03\u5317\u516d\u5341\u4e09\u7701 \u7684\u4e8c\u5341\u4e09\u5bb6\u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u5206\u5c40\uff0c\u5374\u5904\u5904\u6709\u51e0\u4e2a\u5e73\u9762\u5b50\u5bbd\uff0c\u624b\u628a\u5b50\u786c\u7684\u624e\u624b\u4eba\u7269\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u5434\u9e23\u4e16\u4e0d\u7981\u4fa7\u76ee\u4e00\u671b\u88f4\u73cf\uff0c\u5fc3\u4e2d\u6697\u5730\u601d\u5fd6\uff1a\u201c\u4e0d\u77e5\u6211\u8fd9\u88f4\u5144\u542c\u5230\u6b64\u8bdd\uff0c\u5fc3 \u4e2d\u8be5\u6709\u5982\u4f55\u611f\u89c9\uff1f\u201d\u4f46\u88f4\u73cf\u5374\u6839\u672c\u542c\u4e0d\u5230\uff0c\u4ed6\u5446\u5446\u5730\u671b\u7740\u9edd\u9ed1\u7684\u5c4b\u9876\uff0c\u5fc3\u4e2d\u601d\u6f6e\u53cd\u8986\uff0c\u5374\u4e0d \u77e5\u81ea\u5df1\u7684\u547d\u8fd0\uff0c\u5728\u4e0d\u4e45\u4e4b\u540e\uff0c\u5c31\u5f00\u59cb\u8981\u6709\u4e2a\u91cd\u5927\u7684\u6539\u53d8\u4e86\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u624b\u634b\u7740\u957f\u987b\uff0c\u54c8\u54c8\u53c8\u662f\u4e00\u9635\u72c2\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u662f\u6211\u6218\u98de\u8bf4\u53e5\u72c2\u8bdd\uff0c\u8fd9\u4e9b \u98de\u9f99\u9556\u5e08\u4eec\uff0c\u624b\u628a\u5b50\u867d\u786c\uff0c\u4f46\u82e5\u8bf4\u5355\u6253\u72ec\u6597\uff0c\u8fd9\u4e9b\u4eba\u8fd8\u771f\u65e0\u4e00\u4eba\u5728\u6211\u59d3\u6218\u7684\u773c\u4e0b\u2014\u2014\u201d\u4ed6\u8bdd \u58f0\u5fae\u987f\uff0c\u659c\u779f\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e00\u773c\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u4eec\u4e09\u4e94\u4e2a\u8054\u624b\u4e00\u8d77\u4e0a\uff0c\u6211\u59d3\u6218\u7684 \u4e5f\u4e0d\u4f1a\u542b\u7cca\u4ed6\u4eec\uff0c\u53ea\u662f\u4ed6\u4eec\u4eba\u591a\u52bf\u4f17\uff0c\u662f\u4ee5\u2018\u98de\u9f99\u9556\u5c40\u2019\u4fbf\u5728\u6c5f\u6e56\u4e0a\u5f62\u6210\u4e00\u80a1\u5f3a\u5927\u7684\u529b \u91cf\u3002\u201d \u3000\u3000\u201c\u6570\u5341\u5e74\u524d\uff0c\u6c5f\u6e56\u4e0a\u5947\u4eba\u8f88\u51fa\uff0c\u90a3\u65f6\u66fe\u6709\u4eba\u66ff\u6b66\u6797\u4e2d\u9ed1\u767d\u4e24\u9053\u90e8\u5212\u4e0b\u9053\u6765\uff0c\u5f00\u5c71\u7acb\u5be8\u7684 \u7eff\u6797\u670b\u53cb\uff0c\u4e0d\u52ab\u5b64\u65c5\uff0c\u4e0d\u52ab\u660e\u9556\uff0c\u4e0d\u4e0a\u8def\u7684\u94f6\u5b50\uff0c\u5c31\u7b97\u662f\u6210\u5343\u6210\u4e07\u7684\u5f80\u4f60\u773c\u524d\u9001\uff0c\u4f60\u5374\u8fde\u4e00 \u5206\u4e00\u5398\u90fd\u4e0d\u80fd\u52a8\uff0c\u53ef\u662f\u9556\u5c40\u91cc\u4e5f\u4e0d\u80fd\u4fdd\u8d2a\u5b98\uff0c\u4e0d\u80fd\u4fdd\u6697\u9556\uff0c\u4e5f\u4e0d\u80fd\u4fdd\u4e0d\u4e49\u4e4b\u8d22\uff0c\u8fd9\u89c4\u77e9\u6570\u5341 \u5e74\uff0c\u53ef\u4ece\u672a\u6709\u4eba\u72af\u8fc7\u3002\u201d \u3000\u3000\u201c\u53ea\u662f\u8fd9\u2018\u98de\u9f99\u9556\u5c40\u2019\u5374\u5168\u4e0d\u7ba1\u8fd9\u4e00\u5957\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u5f04\u5f97\u5927\u6c5f\u5357\u5317\uff0c\u9ec4\u6cb3\u4e24\u5cb8\u7684\u7eff\u6797\u9053 \u51e0\u4e4e\u8fde\u53e3\u996d\u90fd\u5403\u4e0d\u6210\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u505a\u7eff\u6797\u751f\u6daf\u4e0d\u6210\u5417\uff1f\u201d\u5fc3\u4e2d\u867d\u5982\u6b64\u60f3\uff0c\u53e3\u4e2d\u5374\u672a\u8bf4 \u51fa\u7c73\uff0c\u5374\u542c\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u53c8\u9053\uff1a\u201c\u6b66\u6797\u60c5\u51b5\uff0c\u4e00\u81f4\u5982\u662f\uff0c\u6211\u6218\u98de\u5fdd\u4e3a\u6b66\u6797\u4e00\u6d3e\uff0c\u53c8\u672a\u80fd\u5750 \u89c6\uff0c\u662f\u4ee5\u624d\u5c06\u90a3\u5e2e\u4e3b\u3001\u5411\u5e2e\u4e3b\u3001\u548c\u83ab\u6c0f\u53cc\u4fa0\u7ea6\u5230\u8fd9\u91cc\u6765\uff0c\u4e5f\u65e0\u975e\u662f\u60f3\u5c06\u7eff\u6797\u4e2d\u5206\u6563\u5df2\u4e45\u7684\u529b \u91cf\uff0c\u805a\u5728\u4e00\u5904\uff0c\u4e5f\u514d\u5f97\u7eff\u6797\u670b\u53cb\u7ec8\u65e5\u53d7\u90a3\u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u6b3a\u8d1f\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u76f4\u89c6\u5434\u9e23\u4e16\uff0c\u8fd9\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u73b2\u73d1\u5254\u900f\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6218\u8001\u524d\u8f88\u96c4\u624d\u5927\u7565\uff0c \u786e\u975e\u5e38\u4eba\u80fd\u53ca\u3002\u201d \u3000\u3000\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4ea6\u54c8\u54c8\u4e00\u7b11\uff0c\u51b7\u7136\u9053\uff1a\u201c\u60f3\u5f53\u5e74\u5929\u4e0b\u4e09\u5206\uff0c\u72ec\u9b4f\u6700\u5f3a\uff0c\u90a3\u66f9\u64cd\u53c8\u4f55\u5c1d \u4e0d\u662f\u96c4\u624d\u5927\u7565\uff0c\u5e38\u4eba\u4e0d\u53ca\uff0c\u5475\u5475\u2014\u2014\u201d\u4ed6\u5e72\u7b11\u6570\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u5434\u5144\uff0c\u4f60\u8fd9\u8bdd\u7684\u786e\u8bf4\u5f97\u5999\u6781 \u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u54fc\u4e00\u58f0\uff0c\u8fd8\u662f\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u4e00\u634b\u957f\u987b\uff0c\u63a5\u9053\uff1a\u201c\u54ea\u77e5\u8001\u592b\u8fd9\u4e00\u756a\u597d\u610f\uff0c \u5374\u88ab\u4eba\u770b\u505a\u6076\u610f\uff0c\u8001\u592b\u5728\u5982\u6b64\u60c5\u51b5\u4e0b\uff0c\u624d\u8bf4\u51fa\u90a3\u610f\u89c1\u6765\uff0c\u83ab\u5927\u4fa0\u5148\u4fbf\u7acb\u523b\u8d5e\u6210\u4e86\uff0c\u90a3\u5e2e\u4e3b\u4e5f \u4e0d\u53cd\u5bf9\uff0c\u662f\u4ee5\u4fbf\u4e0e\u8001\u592b\u51fb\u638c\u4e3a\u7ea6\uff0c\u6b64\u4e8b\u5168\u7136\u662f\u5927\u5bb6\u540c\u610f\uff0c\u53c8\u4e0d\u662f\u8001\u592b\u4ee5\u5f3a\u8981\u80c1\u7684\u3002\u201d \u3000\u3000\u201c\u5434\u5144\uff0c\u4f60\u6211\u8d70\u52a8\u6c5f\u6e56\uff0c\u8bb2\u7a76\u7684\u662f\u4e00\u8bfa\u5343\u65a4\uff0c\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\uff0c\u83ab\u8bf4\u8d35\u53cb\u88f4 \u73cf\u4ec5\u662f\u8033\u4e0d\u80fd\u95fb\uff0c\u53e3\u4e0d\u80fd\u8a00\u800c\u5df2\uff0c\u5c31\u7b97\u4ed6\u662f\u4e2a\u778e\u5b50\u3001\u767d\u75f4\uff0c\u6b64\u7ea6\u4e5f\u662f\u4e07\u4e07\u4e0d\u80fd\u6539\u7684\u3002\u4f55\u51b5\u88f4 \u5144\u867d\u7136\u804b\u54d1\uff0c\u4f46\u5374\u76f8\u8c8c\u5802\u5802\uff0c\u8001\u592b\u95ef\u8361\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u81ea\u4fe1\u4e24\u773c\u4e0d\u778e\uff0c\u8fd8\u80fd\u89c6\u4eba\uff0c\u4e00\u773c\u671b\u53bb\uff0c \u4fbf\u77e5\u8fd9\u4f4d\u88f4\u5144\u5fc5\u5b9a\u5929\u59ff\u82f1\u53d1\uff0c\u8d85\u4e8e\u5e38\u4eba\uff0c\u5426\u5219\u50cf\u5434\u5144\u8fd9\u7b49\u4eba\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u6298\u8282\u4e0b\u4ea4\u7684\u4e86\u3002\u201d \u3000\u3000\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u6ed4\u6ed4\u800c\u8a00\uff0c\u58f0\u82e5\u6d2a\u949f\uff0c\u53cc\u76ee\u7684\u7684\uff0c\u795e\u5149\u7167\u4eba\uff0c\u6b64\u523b\u4e00\u5c55\u624b\u4e2d\u6298\u6247\uff0c\u53c8\u81ea \u6717\u58f0\u5927\u7b11\u8d77\u6765\uff0c\u5434\u9e23\u4e16\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed8\u9053\uff1a\u201c\u8fd9\u2018\u795e\u624b\u2019\u6218\u98de\u4e45\u5df2\u4eab\u8a89\u6c5f\u6e56\uff0c\u800c\u4e14\u6709\u540d\u7684\u5fc3\u667a \u6df1\u6c89\uff0c\u5fc3\u673a\u8fc7\u4eba\uff0c\u6b64\u523b\u5b9a\u8981\u6211\u8fd9\u88f4\u5144\u6765\u505a\u603b\u74e2\u628a\u5b50\uff0c\u60f3\u5fc5\u6709\u7740\u6df1\u610f--\u4e00\u201d\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u604d\u7136\u53c8 \u5fd6\u9053\uff1a\u201c\u662f\u4e86\uff0c\u60f3\u5fc5\u4ed6\u770b\u88f4\u5144\u8eab\u7f79\u6b8b\u5e9f\uff0c\u5c06\u6765\u5b9a\u597d\u5229\u7528\u4e9b\u3002\u201d\u5f53\u4e0b\u5fc3\u5ff5\u53c8\u81ea\u6570\u8f6c\uff1a\u201c\u88f4\u5144\u4e45 \u906d\u56f0\u82e6\u3001\u6b3a\u51cc\uff0c\u6b64\u523b\u6709\u4e86\u8fd9\u79cd\u673a\u4f1a\uff0c\u6211\u4f55\u4e0d\u5c06\u8ba1\u5c31\u8ba1\uff0c\u8ba9\u88f4\u5144\u5927\u5927\u5730\u626c\u7709\u5410\u6c14\u4e00\u756a\uff0c\u4e5f\u4e0d\u5728 \u4ed6\u548c\u6211\u4ea4\u53cb\u4e00\u573a\u3002\u201d \u3000\u3000\u8fd9\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\uff0c\u81ea\u9aeb\u9f84\u95ef\u8361\u6c5f\u6e56\uff0c\u5373\u51ed\u8fc7\u4eba\u7684\u5fc3\u673a\uff0c\u95ef\u4e0b\u4e00\u4efd\u201c\u4e07\u513f\u201d\uff0c\u4ed6\u9762 \u4e0a\u770b\u6765\u867d\u662f\u98de\u626c\u8df3\u8131\uff0c\u7b11\u9762\u8fce\u4eba\uff0c\u5176\u5b9e\u5374\u662f\u9762\u548c\u5fc3\u51b7\uff0c\u591a\u5e74\u6765\u72ec\u6765\u72ec\u5f80\uff0c\u975e\u4f46\u6ca1\u6709\u670b\u53cb\uff0c\u5c31 \u8fde\u4ed6\u7684\u5e08\u627f\u6765\u5386\uff0c\u6b66\u6797\u4e2d\u5374\u4ece\u672a\u6709\u4eba\u77e5\u9053\u3002 \u3000\u3000\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u4ed6\u4e00\u89c1\u88f4\u73cf\uff0c\u4fbf\u89c9\u6295\u7f18\uff0c\u8fd9\u79cd\u5fc3\u667a\u6df1\u6c89\u3001\u7d20\u6027\u6de1\u8584\u4e4b\u4eba\uff0c\u4e0d\u4ea4\u53cb\u5219\u5df1\uff0c\u4e00 \u4ea4\u53cb\u4ea6\u662f\u5168\u5fc3\u5168\u610f\uff0c\u4e0d\u4f1a\u534a\u70b9\u865a\u5047\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5fc3\u5ff5\u8f6c\u6765\u8f6c\u53bb\uff0c\u4fbf\u90fd\u662f\u4e3a\u7740\u88f4\u73cf\u7740\u60f3\uff0c\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u6b63\u548c\u201c\u91d1 \u9e21\u201d\u5411\u4e00\u557c\u4e92\u76f8\u77aa\u89c6\uff0c\u770b\u6765\u5f7c\u6b64\u90fd\u6068\u4e0d\u5f97\u5c06\u5bf9\u65b9\u4e00\u638c\u6253\u6b7b\u624d\u5bf9\u5fc3\u601d\uff0c\u6697\u4e2d\u4e00\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u6218\u8001\u524d\u8f88\u9ad8\u77e5\u5353\u89c1\uff0c\u5c0f\u53ef\u81ea\u662f\u5fc3\u6298\u4e0d\u5df2\uff0c\u4f46\u5411\u5e2e\u4e3b\u65b9\u624d\u6240\u8bf4\u7684\u8bdd\uff0c\u4e5f\u4e0d\u65e0\u9053\u7406\uff0c\u5c0f\u53ef\u5e74\u8f7b \u8bc6\u6d45\uff0c\u53c8\u662f\u5c40\u5916\u4eba\uff0c\u672c\u65e0\u63d2\u8a00\u4e4b\u4f59\u5730\uff0c\u4f46\u5404\u4f4d\u65e2\u7136\u770b\u5f97\u8d77\u5c0f\u53ef\uff0c\u90a3\u88f4\u5144\u53c8\u662f\u5c0f\u53ef\u4e4b\u81f3\u4ea4\uff0c\u5c0f \u53ef\u867d\u7136\u62d9\u611a\uff0c\u5374\u4e5f\u4e0d\u5f97\u4e0d\u8bf4\u51e0\u53e5\u8bdd\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6697\u4e2d\u4e00\u4f38\u5927\u62c7\u6307\uff0c\u5fd6\u9053\uff1a\u201c\u4e45\u95fb\u8fd9\u5434\u9e23\u4e16\u662f\u6b66\u6797\u795e\u7ae5\uff0c\u6b64\u523b\u4e00\u89c1\uff0c\u679c\u7136\u662f \u53e3\u624d\u4fbf\u6377\uff0c\u8a00\u8bed\u5f97\u4f53\uff0c\u5947\u602a\u7684\u662f\uff0c\u4e0d\u77e5\u4ed6\u600e\u4f1a\u548c\u8fd9\u804b\u54d1\u6b8b\u75be\u6709\u7740\u6df1\u4ea4\u2014\u2014\u201d\u5374\u542c\u201c\u91d1\u9e21\u201d\u5411 \u4e00\u557c\u5927\u58f0\u9053\uff1a\u201c\u5434\u5144\u6709\u4ec0\u4e48\u8bdd\uff0c\u53ea\u7ba1\u8bf4\u51fa\u6765\u4fbf\u662f\u3002\u201d\u4ed6\u5bf9\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u6b64\u523b\u5df2\u5927\u751f\u597d\u611f\uff0c\u4e00 \u5fc3\u4ee5\u4e3a\u4ed6\u5b9a\u4f1a\u5e2e\u7740\u81ea\u5df1\u8bf4\u4e0a\u4e24\u53e5\u8bdd\u7684\u3002 \u3000\u3000\u54ea\u77e5\u5434\u9e23\u4e16\u5fae\u5fae\u4e00\u7b11\uff0c\u5374\u9053\uff1a\u201c\u82e5\u5355\u4ee5\u6b64\u4e8b\u800c\u8a00\uff0c\u5c0f\u53ef\u662f\u7ad9\u5728\u6218\u8001\u524d\u8f88\u8fd9\u8fb9\u7684--\u4e00\u201d\u4ed6 \u6b64\u8bdd\u4e00\u51fa\uff0c\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e0d\u7981\u9762\u5bb9\u9aa4\u53d8\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u5374\u662f\u559c\u52a8\u989c\u8272\uff0c\u9053\uff1a\u201c\u5434\u5144\u5c3d\u7ba1 \u8bf4\u4e0b\u53bb\uff0c\u82e5\u6709\u4eba\u963b\u62e6\uff0c\u6211\u59d3\u6218\u7684\u5148\u628a\u4ed6\u5bb0\u4e86\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u6b64\u4e8b\u65e2\u6210\u5b9a\u5c40\uff0c\u53c8\u7ecf\u51fb\u638c\uff0c\u6309\u60c5\u6309\u7406\uff0c\u90fd\u4e07\u4e07\u53cd\u6094\u4e0d\u5f97\uff0c\u4f55\u51b5\u6211\u8fd9 \u88f4\u5144\u5929\u8d44\u8d85\u4eba\uff0c\u80f8\u6000\u5927\u5ea6\uff0c\u505a\u4e8b\u4e00\u5b9a\u6781\u4e3a\u516c\u6b63\uff0c\u4ed6\u8fd9\u6b8b\u75be\uff0c\u4e5f\u662f\u906d\u4eba\u6697\u7b97\uff0c\u88ab\u70b9\u4e86\u2018\u804b\u54d1\u2019 \u91cd\u7a74\u800c\u5df2\uff0c\u5e76\u975e\u5929\u804b\u5929\u54d1\u4e0d\u6cbb\u4e4b\u75c7\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u634b\u957f\u987b\uff0c\u9053\uff1a\u201c\u5434\u5144\u4ea6\u662f\u9ad8\u624b\uff0c\u5bf9\u70b9\u7a74\u4e00\u9053\uff0c\u60f3\u5fc5\u662f\u5341\u5206\u7cbe\u901a\u7684\u4e86\uff0c\u600e \u5730\u4e0d\u66ff\u8d35\u53cb\u5c06\u6b64\u7a74\u89e3\u5f00\u5462\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u7684\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u6218\u8001\u524d\u8f88\u6709\u6240\u4e0d\u77e5\uff0c\u70b9\u4e2d\u8fd9\u88f4\u5144\u7a74\u9053\u7684\uff0c\u5b9e\u662f\u975e\u5e38\u4e4b\u4eba\uff0c\u6240\u7528 \u7684\u4e5f\u662f\u72ec\u95e8\u624b\u6cd5\uff0c\u5c0f\u53ef\u867d\u6709\u5fc3\uff0c\u5374\u662f\u65e0\u80fd\u4e3a\u529b\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u634b\u987b\u7b11\u9053\uff1a\u201c\u6b67\u9ec4\u4e4b\u9053\uff0c\u8001\u592b\u81ea\u4fe1\u5c1a\u6709\u4e09\u5206\u628a\u63e1\uff0c\u8d35\u53cb\u4e4b\u75be\uff0c\u8001\u592b\u65e5\u540e\u5b9a \u8981\u8bbe\u6cd5\u5e2e\u4ed6\u6cbb\u4e0a\u4e00\u6cbb\uff0c\u53ea\u662f\u2014\u2014\u201d\u4ed6\u54c8\u54c8\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u5434\u5144\u65b9\u624d\u65e2\u5982\u6b64\u8bf4\uff0c\u90a3\u4e48\u6b64\u7ea6\u66f4\u662f \u5b9a\u8981\u9075\u884c\u7684\u4e86\uff1f\u6b64\u4e8b\u8bf4\u6025\u4e0d\u6025\uff0c\u8bf4\u7f13\u4e0d\u7f13\uff0c\u8001\u592b\u660e\u65e5\u6e05\u6668\u5c31\u8981\u64a4\u4e0b\u6b66\u6797\u5e16\uff0c\u4f20\u8bed\u6c5f\u6e56\uff0c\u5171\u8d3a \u6b64\u4e3e\u2014\u2014\u201d\u4ed6\u8bed\u72b9\u672a\u4e86\uff0c\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u7a81\u5730\u5c06\u624b\u4e2d\u94c1\u62d0\u4e00\u987f\uff0c\u602a\u53eb\u9053\uff1a\u201c\u6b64\u4e8b\u5c1a\u5f85\u8003 \u8651\uff0c\u201d\u56de\u9996\u671b\u7740\u83ab\u6c0f\u5144\u5f1f\uff0c\u201c\u4e07\u4e07\u4e0d\u80fd\u5982\u6b64\u8349\u7387\u3002\u201d \u3000\u3000\u83ab\u6c0f\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u76ee\u5149\u5404\u5404\u4e00\u52a8\uff0c\u5374\u672a\u7b54\u8bdd\uff0c\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u9762\u4e0a\u5ffd\u9634\u5ffd\u6674\uff0c\u60f3\u662f\u5728 \u601d\u8003\u7740\u4ec0\u4e48\uff0c\u4e5f\u6ca1\u6709\u53d1\u8a00\u3002 \u3000\u3000\u6b64\u523b\u5929\u867d\u672a\u4eae\uff0c\u4f46\u8fdc\u5904\u5df2\u6709\u9e21\u557c\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u7a81\u5730\u51b7\u54fc\u4e00\u58f0\uff0c\u5012\u7a9c\u800c\u8d77\uff0c\u51cc\u7a7a\u4e00\u4e2a\u7ffb \u8eab\uff0c\u5411\u9662\u5916\u5982\u98de\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8eab\u6cd5\u65e2\u662f\u5feb\u5982\u95ea\u7535\uff0c\u6b64\u4e3e\u53c8\u662f\u7a81\u7136\u800c\u6765\uff0c\u7b49\u5230\u83ab\u5357\u6025\u95ee\uff1a\u201c\u6218\u8001\u54ea\u91cc\u53bb\uff1f\u201d\u4ed6\u9ad8\u5927\u7684 \u8eab\u5f71\uff0c\u5374\u5df2\u6d88\u5931\u5728\u9ed1\u6697\u91cc\u4e86\u3002 \u3000\u3000\u5385\u4e2d\u7fa4\u8c6a\u9762\u9762\u76f8\u89d1\uff0c\u5fc3\u4e2d\u5404\u662f\u4e00\u6014\uff0c\u4e0d\u77e5\u9053\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u6b64\u4e3e\u7a76\u7adf\u662f\u4ec0\u4e48\u7528\u610f\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e00\u53cc\u773c\u6674\uff0c\u66f4\u662f\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u95e8\u5916\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u53ea\u542c\u8fdc\u5904\u9e21\u9e23\u4e4b\u58f0\uff0c \u4e00\u58f0\u8fde\u7740\u4e00\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\u7684\u53eb\u4e86\u8d77\u6765\uff0c\u4f46\u672a\u8fc7\u7247\u523b\uff0c\u8fd9\u4e9b\u6b64\u8d77\u5f7c\u843d\u7684\u9e21\u9e23\u58f0\uff0c\u53c8\u590d\u5bc2\u7136\u3002 \u3000\u3000\u5927\u5bb6\u6b64\u65f6\u66f4\u662f\u5947\u602a\uff0c\u59cb\u59cb\u672a\u4f5c\u4efb\u4f55\u8868\u793a\u7684\u201c\u5317\u6597\u4e03\u715e\u201d\u4e4b\u9996\u83ab\u5357\u3002 \u3000\u3000\u6b64\u523b\u53cc\u7709\u5fae\u76b1\uff0c\u53f3\u624b\u7d27\u63e1\u7740\u8170\u95f4\u7684\u5251\u67c4\uff0c\u6c89\u58f0\u9053\u3002 \u3000\u3000\u201c\u8fd9\u4f4d\u2018\u795e\u624b\u2019\u6218\u98de\uff0c\u884c\u4e8b\u771f\u662f\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\uff0c\u597d\u751f\u751f\u7684\u2014\u2014\u201d\u54ea\u77e5\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u201c\u795e \u624b\u201d\u6218\u98de\u7684\u7b11\u58f0\uff0c\u5374\u53c8\u5728\u95e8\u5916\u54cd\u8d77\uff0c\u5434\u9e23\u4e16\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4ed6\u53f3\u624b\u4ecd\u81ea\u6447\u7740\u6298\u6247\uff0c\u5de6\u624b\u5374\u63d0 \u7740\u4e00\u6761\u957f\u7d22\uff0c\u7d22\u4e0a\u7adf\u6346\u7740\u767e\u5341\u53ea\u9e21\uff0c\u957f\u957f\u5730\u62d6\u4e86\u4e0b\u53bb\uff0c\u4e00\u8def\u62d6\u5728\u8eab\u540e\uff0c\u4e00\u53ea\u8fde\u7740\u4e00\u53ea\uff0c\u4f46\u5374 \u4ff1\u90fd\u65e0\u58f0\u65e0\u606f\uff0c\u60f3\u5fc5\u90fd\u5df2\u6b7b\u4e86\u3002 \u3000\u3000\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u811a\u8de8\u5165\u5927\u5385\uff0c\u65e5\u5149\u51db\u7136\u56db\u626b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6211\u7545\u6de1\u751a\u6b22\uff0c\u8fd9\u4e9b\u9e21\u5374 \u53eb\u5f97\u8ba8\u538c\uff0c\u8001\u592b\u4e00\u6c14\u4e4b\u4e0b\uff0c\u5c31\u5c06\u5b83\u63d0\u6765\u6740\u4e86--\u4e00\u201d\u4ed6\u7b11\u58f0\u7a81\u655b\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u82e5\u8fd8\u6709 \u9e21\u6562\u6253\u65ad\u8001\u592b\u7684\u6e05\u8c08\uff0c\u54fc\u2014\u2014\u201d\u5de6\u624b\u4e00\u62ac\uff0c\u5c06\u90a3\u6761\u957f\u7d22\u4e0a\u6346\u7740\u7684\u4e00\u8fde\u4e32\u6b7b\u9e21\uff0c\u90fd\u5e26\u4e86\u8fdb\u6765\uff0c \u51b7\u7b11\u53c8\u9053\uff1a\u201c\u8fd9\u4e9b\u9e21\u5c31\u662f\u699c\u6837\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fc3\u4e2d\u6697\u7b11\uff0c\u77e5\u9053\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u6b64\u523b\u6b63\u662f\u6307\u6851\u9a82\u69d0\uff0c\u4ed6\u53e3\u53e3\u58f0\u58f0\u9a82\u7684\u662f\u9e21\uff0c\u5176\u5b9e \u9a82\u7684\u5374\u662f\u201c\u91d1\u9e21\u201d\u3002 \u3000\u3000\u90a3\u5411\u4e00\u557c\u4ea6\u975e\u5446\u5b50\uff0c\u6b64\u523b\u8179\u4e2d\u4ea6\u662f\u96ea\u4eae\uff0c\u5927\u6012\u4e4b\u4e0b\uff0c\u9762\u5bb9\u9aa4\u53d8\uff0c\u65b9\u5f85\u53cd\u5507\u76f8\u9a7e\uff0c\u76ee\u5149\u8f6c \u5904\uff0c\u5374\u89c1\u90a3\u767e\u5341\u53ea\u6b7b\u9e21\uff0c\u52a8\u4e5f\u4e0d\u52a8\u5730\u8eba\u5728\u5730\u4e0a\uff0c\u8eab\u4e0a\u534a\u70b9\u4f24\u75d5\u90fd\u6ca1\u6709\uff0c\u4f46\u9e21\u5934\u5374\u5168\u90e8\u6241\u4e86\uff0c \u663e\u89c1\u8fd9\u662f\u88ab\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u624b\u6cd5\u6240\u4f24\u3002\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\u3002\u5fc3\u60f3\u6b64\u5904\u672c\u662f\u8352\u90ca\uff0c\u56db\u4e0b\u5e76\u65e0\u4eba\u5bb6\uff0c \u800c\u8fd9\u6218\u98de\u7adf\u80fd\u5728\u7247\u523b\u4e4b\u5185\uff0c\u5c06\u8fd9\u4e9b\u663e\u89c1\u4e0d\u5728\u8fd1\u5904\uff0c\u800c\u4e14\u7edd\u975e\u4e00\u5bb6\u6240\u517b\u7684\u9e21\uff0c\u53ea\u53ea\u6740\u6b7b\uff0c\u8fd9\u79cd \u8eab\u624b\u4e4b\u60ca\u4eba\uff0c\u786e\u975e\u81ea\u5df1\u80fd\u53ca\uff0c\u53c8\u60f3\u5230\u4e09\u4e24\u6708\u524d\uff0c\u81ea\u5df1\u548c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u4ee5\u53ca\u83ab\u6c0f\u53cc\u715e\u8054\u624b\u5bf9\u4ed8 \u4ed6\uff0c\u90a3\u4e94\u715e\u83ab\u5317\u5c1a\u4e14\u65bd\u5c55\u51fa\u201c\u5317\u6597\u4e03\u715e\u201d\u4ed7\u4ee5\u6210\u540d\u3001\u6b66\u6797\u4e2d\u6700\u4e3a\u9738\u9053\u7684\u6697\u5668\u201c\u5317\u6597\u4e03\u661f\u9488\u201d \u6765\uff0c\u5374\u4e5f\u672a\u5360\u4e0a\u98ce\uff0c\u81ea\u5df1\u82e5\u662f\u4e00\u4eba\u60f9\u607c\u4e86\u4ed6\uff0c\u5c82\u975e\u8981\u5403\u773c\u524d\u4e4b\u4e8f\u3002 \u3000\u3000\u8fd9\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u867d\u7136\u6027\u60c5\u66b4\u8e81\u521a\u5f3a\uff0c\u4f46\u4ea6\u4e45\u8d70\u6c5f\u6e56\uff0c\u6b63\u662f\u773c\u91cc\u4e0d\u63c9\u6c99\u5b50\u7684\u5149\u68cd\uff0c\u773c\u524d \u4e8f\u662f\u4e07\u4e07\u4e0d\u80af\u5403\u7684\uff0c\u4e00\u5ff5\u81f3\u6b64\uff0c\u809a\u5185\u6697\u9a7e\u51e0\u58f0\uff0c\u5374\u5c06\u53e3\u4e2d\u7684\u8bdd\uff0c\u5fcd\u4e86\u56de\u53bb\uff0c\u5012\u9000\u4e00\u6b65\uff0c\u62ac\u5934 \u671b\u7740\u5c4b\u9876\uff0c\u4e5f\u5b66\u7740\u88f4\u73cf\u7684\u6837\u5b50\uff0c\u50cf\u662f\u53d8\u5f97\u65e2\u804b\u53c8\u54d1\u4e86\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u7b11\u4e00\u58f0\uff0c\u7765\u7768\u56db\u987e\uff0c\u53c8\u9053\uff1a\u201c\u65e2\u7136\u65e0\u4eba\u53cd\u5bf9\uff0c\u6b64\u4e8b\u4fbf\u6210\u5b9a\u5c40\uff0c\u6211\u6218\u98de\u6b64 \u523b\u5c31\u5148\u53c2\u8d1d\u672a\u3001\u6c5f\u5357\u7eff\u6797\u9053\u7684\u603b\u74e2\u628a\u5b50\u88f4\u73cf\u88f4\u5927\u5148\u751f\u4e86\u3002\u201d \u3000\u3000\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u8bed\u58f0\u4e00\u3001\u843d\uff0c\u53f3\u624b\u4e00\u62db\uff0c\u5c06\u624b\u4e2d\u7684\u6298\u6247\uff0c\u63d2\u5728\u9886\u53e3\u4e4b\u540e\uff0c\u957f\u8896\u5fae\u6296\uff0c\u7adf \u53c8\u6df1\u6df1\u5411\u88f4\u73cf\u5f53\u5934\u4e00\u63d6\u3002 \u3000\u3000\u54ea\u77e5\u88f4\u73cf\u6b64\u523b\u5fc3\u4e2d\u6b63\u662f\u601d\u6f6e\u7ffb\u6d8c\uff0c\u60f3\u5230\u81ea\u5df1\u4e00\u751f\u4e4b\u4e2d\u7684\u60c5\u3001\u4ec7\u3001\u6069\u3001\u6028\uff0c\u60f3\u5230\u90a3\u9a84\u7eb5\u4f46 \u53c8\u6e29\u67d4\uff0c\u6e29\u67d4\u5374\u53c8\u5201\u86ee\u7684\u6a80\u6587\u742a\uff0c\u53c8\u60f3\u5230\u5979\u7684\u7236\u4eb2\u201c\u9f99\u5f62\u516b\u638c\u201d\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u6211\u7239\u5988\u5168 \u6b7b\uff0c\u5b64\u82e6\u4f36\u4ec3\uff0c\u6a80\u5927\u53d4\u5c06\u6211\u6536\u7559\u4e86\uff0c\u6211\u672c\u8be5\u597d\u597d\u62a5\u4ed6\u6069\u624d\u662f\u3002\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u6211\u5374\u53c8\u4e3a\u4ec0\u4e48\u5bf9 \u4ed6\u5fc3\u4e2d\u603b\u6709\u4e9b\u96be\u8a00\u7684\u6076\u611f\uff0c\u5509\u2014\u2014\u4e0d\u8bba\u5982\u4f55\uff0c\u8fd9\u6b21\u6211\u5077\u8dd1\u51fa\u6765\uff0c\u603b\u662f\u6709\u8d1f\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u53c8\u60f3\u5230\u90a3\u5929\u771f\u53ef\u7231\u7684\u8881\u6caa\u73cd\uff1a\u201c\u6211\u5728\u8fd9\u4e16\u4e0a\u672c\u662f\u5bc2\u5bde\u5f97\u5f88\uff0c\u53ea\u6709\u73cd\u73cd\u7ed9\u6211\u90a3\u4e48\u591a\u5b89\u6170\uff0c \u4f46\u662f\u6211\u8d70\u4e86\uff0c\u5374\u8fde\u5979\u4e5f\u6ca1\u6709\u544a\u8bc9\u4e00\u58f0\uff0c\u5509\u2014\u2014\u5979\u4e0d\u77e5\u9053\u8981\u591a\u4e48\u4f24\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u53c8\u5f00\u59cb\u60f3\u8d77\u5b59\u9526\u5e73\uff1a\u201c\u5979\u5bf9\u6211\u4e5f\u662f\u90a3\u4e48\u597d\uff0c\u5e38\u5e38\u5e2e\u6211\u505a\u4e8b\uff0c\u4e5f\u6ca1\u6709\u56e0\u4e3a\u6211\u662f\u4e2a \u6b8b\u5e9f\u7684\u65e0\u7528\u4e4b\u4eba\u800c\u770b\u4e0d\u8d77\u6211\uff0c\u8fd8\u6709\u5b59\u8001\u7239\uff0c\u4ed6\u4e5f\u5bf9\u6211\u5f88\u597d\uff0c\u5509\uff0d\uff0d\u6211\u5374\u6ca1\u6709\u62a5\u7b54\u4ed6\u4eec\uff0c\u53cd\u800c \u5bb3\u4ed6\u4eec\u56e0\u4e3a\u90a3\u4e24\u672c\u4e66\u800c\u6b7b\u5728\u522b\u4eba\u624b\u4e0a\u3002\u201d \u3000\u3000\u8fd9\u53d7\u5c3d\u6b3a\u51cc\u3001\u5c1d\u904d\u708e\u51c9\u7684\u5c11\u5e74\uff0c\u6b64\u523b\u5374\u4e00\u5fc3\u4e00\u610f\u5730\u56de\u5fc6\u7740\u4eba\u5bb6\u5bf9\u4ed6\u7684\u597d\u5904\uff0c\u4e00\u5fc3\u4e00\u610f\u5730 \u8d23\u5907\u7740\u81ea\u5df1\uff0c\u4ee5\u4e3a\u81ea\u5df1\u8d1f\u4e86\u4eba\u5bb6\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u50cf\u662f\u53c8\u56de\u5230\u98de\u9f99\u9556\u5c40\u7684\u540e\u9662\u91cc\uff0c\u6a80\u6587\u742a\u6e29\u6696\u800c\u5a07\u5c0f\u7684\u8eab\u8eaf\uff0c\u6b64\u523b\u4eff\u4f5b\u53c8\u5728 \u4ed6\u6000\u4e2d\uff0c\u4ed6\u4eff\u4f5b\u53c8\u770b\u5230\u8fd9\u5c11\u5973\u88ab\u5979\u7239\u7239\u5e26\u8d70\u65f6\uff0c\u56de\u5934\u671b\u7740\u81ea\u5df1\u5e7d\u6028\u7684\u4e00\u77a5\uff1b\u53c8\u4eff\u4f5b\u56de\u5230\u90a3\u6761 \u957f\u957f\u7684\uff0c\u94fa\u7740\u788e\u77f3\u5b50\u7684\u8def\u4e0a\uff0c\u79cb\u98ce\u745f\u745f\uff0c\u843d\u53f6\u6ee1\u5929\uff0c\u4ed6\u6b63\u7262\u7740\u8881\u6cf8\u73cd\u7684\u5c0f\u624b\uff0c\u4e00\u9762\u5929\u771f\u5730\u7b11 \u7740\uff0c\u4e00\u9762\u90e8\u53c8\u8bf4\u4e9b\u5fe7\u4f24\u7684\u4e8b\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u5bf9\u90a3\u4e8e\u795e\u624b\u201c\u6218\u98de\u7684\u4e00\u63d6\uff0c\u6839\u672c\u6ca1\u6709\u770b\u5230\uff0c\u6218\u98de\u62ac\u5934\u4e00\u671b\u3002\u4ea6\u81ea\u770b\u5230\u4ed6\u9762\u4e0a\u8fd9\u79cd \u5982\u75f4\u5982\u9189\u7684\u795e\u60c5\uff0c\u4e0d\u89c9\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u968f\u5373\u5927\u7b11\u8d77\u6765\uff0c\u56de\u8fc7\u5934\u53bb\u5411\u90a3\u201d\u4e03\u5de7\u8feb\u9b42\u201c\u53ca\u83ab\u6c0f\u5144\u5f1f \u9053\uff1a\u201c\u4f60\u4eec\u600e\u5730\u4e0d\u6765\u53c2\u89c1\uff1f\u201d \u3000\u3000\u5374\u542c\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5e72\u54b3\u4e00\u58f0\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6b64\u4e8b\u56fa\u7136\u5df2\u6210\u5b9a\u5c40\uff0c\u4f46\u6218\u5144\u4f60\u5374\u5fd8\u4e86\u4e00 \u4e8b\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u5fd8\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u6b64\u201c\u4e8b\u4e43\u6218\u5144\u6240\u521b\uff0c\u6218\u5144\u81ea\u7136\u8d5e\u6210\uff0c\u83ab\u5927\u54e5\u5144\u5f1f\u4ea6 \u662f\u65e9\u5df2\u8d5e\u6210\uff0c\u5411\u5e2e\u4e3b\u6b64\u523b\u4ea6\u65e0\u53cd\u5bf9\u4e4b\u610f\u3002\u81f3\u4e8e\u5c0f\u5f1f\u4e48\uff0c\u81ea\u7136\u66f4\u65e0\u8bdd\u8bf4\uff0c\u53ea\u662f\u2014\u2014\u201d\u4ed6\u6545\u610f\u4e00 \u987f\u8bdd\u58f0\uff0c\u76ee\u5149\u5fae\u626b\uff0c\u53ea\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u4e0a\uff0c\u679c\u7136\u9732\u51fa\u7126\u6025\u800c\u53d1\u6115\u7684\u795e\u8272\uff0c\u50cf\u662f\u5728\u6025\u4e8e\u7b49\u5f85 \u7740\u81ea\u5df1\u7684\u4e0b\u6587\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u624b\u6307\u4e86\u6307\u7ad9\u5728\u65c1\u8fb9\u7684\u88f4\u73cf\uff0c\u63a5\u7740\u7b11\u9053\uff1a\u201c\u53ea\u662f\u6218\u5144\u5374\u5fd8\u4e86 \u95ee\u95ee\u4eba\u5bb6\u81ea\u5df1\uff0c\u662f\u5426\u4e5f\u8d5e\u6210\u6b64\u4e8b\u5462\uff1f\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u4e0d\u4ec5\u201c\u795e\u624b\u201d\u6218\u98de\u4e3a\u4e4b\u4e00\u6014\uff0c\u5434\u9e23\u4e16\u4e5f\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u4e0e\u8fd9\u88f4\u5144 \u867d\u4ec5\u662f\u4e00\u65e5\u4e4b\u4ea4\uff0c\u4f46\u5374\u5df2\u770b\u51fa\u4ed6\u662f\u4e2a\u78ca\u843d\u7537\u513f\uff0c\u82e5\u662f\u8ba9\u4ed6\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u7b54\u5e94\u6b64\u4e8b\uff0c\u4ed6\u662f\u4e07\u4e07 \u4e0d\u4f1a\u80af\u7684\u3002\u201d \u3000\u3000\u6b64\u4e8b\u4e00\u6210\u3001\u4ed6\u7531\u4e00\u4e2a\u9ed8\u9ed8\u65e0\u95fb\u7684\u5c11\u5e74\uff0c\u9661\u7136\u53d8\u4e3a\u6c5f\u5357\u7eff\u6797\u9053\u7684\u603b\u74e2\u628a\u5b50\uff0c\u81ea\u662f\u5e73\u6b65\u9752 \u4e91\uff0c\u4f46\u5fc3\u5ff5\u6570\u8f6c\uff0c\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u9762\u4e0a\u9732\u51fa\u5f97\u610f\u7684\u795e\u8272\u6765\uff0c\u83ab\u6c0f\u5144\u5f1f\u4ecd\u7136 \u662f\u9762\u76ee\u51b7\u6f20\uff0c\u65e0\u52a8\u4e8e\u8877\uff0c\u53ea\u6709\u6218\u98de\u5374\u5df2\u7126\u6025\u95ee\u9053\uff1a\u201c\u5434\u5144\uff0c\u8d35\u53cb\u88f4\u5144\u753b\u5f97\u4e00\u7b14\u4e39\u9752\uff0c\u60f3\u5fc5\u8bc6 \u5f97\u5b57\u5417\uff1f\u53ef\u5426\u4ee5\u7b14\u4ee3\u53e3\uff0c\u95ee\u4ed6\u4e00\u95ee\uff1f\u201d\u201c\u5434\u9e23\u4e16\u5fc3\u5ff5\u5df2\u5b9a\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5012\u65e0\u987b\u5982\u6b64\uff0c\u53ea\u8981 \u5c0f\u53ef\u4e00\u95ee\u4fbf\u77e5\u3002\u201d\u4f38\u624b\u4e00\u62cd\u88f4\u73cf\u7684\u80a9\u5934\uff0c\u88f4\u73cf\u9661\u7136\u4e00\u60ca\uff0c\u65b9\u4ece\u90a3\u6df7\u5408\u7740\u60b2\u4f24\u548c\u751c\u871c\u7684\u5f80\u4e8b\u4e2d \u9192\u6765\uff0c\u53ea\u89c1\u81ea\u5df1\u8eab\u524d\uff0c\u56f4\u7ed5\u7740\u90a3\u4e9b\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u6765\u610f\u7684\u4eba\uff0c\u800c\u81ea\u5df1\u90a3\u503e\u523b\u4fbf\u6210\u76f8\u77e5\u7684\u670b\u53cb\uff0c \u6b63\u5728\u6307\u624b\u5212\u811a\u5730\u5411\u81ea\u5df1\u6bd4\u7740\u624b\u5f0f\u3002 \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u4e86\u89e3\u8fd9\u4e9b\u624b\u5f0f\u7684\u610f\u601d\uff0c\u53ea\u89c1\u8fd9\u5c11\u5e74\u5ffd\u800c\u5c48\u8d77\u624b\u6307\uff0c\u5ffd\u800c\u644a\u5f00\u624b\u638c\uff0c\u5ffd\u800c\u4e24\u624b\u4e92 \u642d\uff0c\u5ffd\u53c8\u4f5c\u51fa\u62b1\u62f3\u4f5c\u63d6\u7684\u59ff\u52bf\u3002\u5fc3\u4e2d\u4e0d\u89c9\u5927\u4e3a\u5947\u602a\uff0c\u8f6c\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u6bcf\u4e2a\u4eba\u90fd\u5728\u51dd\u76ee\u671b\u7740\u81ea \u5df1\u3002 \u3000\u3000\u5434\u9e23\u4e16\u89c1\u4e86\u4ed6\u4e00\u8138\u832b\u7136\u7684\u795e\u8272\uff0c\u5fc3\u4e2d\u4e0d\u7981\u597d\u7b11\uff0c\u5176\u5b9e\u8fd9\u4e9b\u624b\u5f0f\u7684\u610f\u601d\uff0c\u4ed6\u81ea\u5df1\u4e5f\u6839\u672c\u4e0d \u77e5\u9053\uff0c\u53ea\u662f\u4ed6\u5929\u6027\u504f\u6fc0\uff0c\u6b63\u662f\u81f3\u60c5\u81f3\u6027\u4e4b\u4eba\uff0c\u77e5\u9053\u88f4\u73cf\u4e45\u906d\u6b3a\u51cc\uff0c\u4fbf\u5e0c\u671b\u88f4\u73cf\u5927\u5927\u5730\u626c\u7709\u5410 \u6c14\u4e00\u756a\uff0c\u6781\u613f\u88f4\u73cf\u80fd\u505a\u90a3\u6c5f\u5357\u7eff\u6797\u9053\u7684\u603b\u74e2\u628a\u5b50\uff0c\u662f\u4ee5\u6b64\u523b\u4ed6\u4fbf\u80e1\u4e71\u505a\u4e9b\u624b\u5f0f\uff0c\u53ea\u8981\u88f4\u73cf\u4e00 \u70b9\u5934\uff0c\u6b64\u4e8b\u4fbf\u624d\u6210\u529f\u3002 \u3000\u3000\u4ed6\u624b\u5f0f\u8d8a\u6bd4\u8d8a\u591a\uff0c\u88f4\u73cf\u4e5f\u8d8a\u6765\u8d8a\u6014\uff0c\u5ffd\u7136\u770b\u5230\u4ed6\u4e00\u6307\u5927\u5385\uff0c\u53c8\u4e00\u6307\u5730\u4e0a\u7684\u5e03\u888b\uff0c\u4fbf\u5728\u5fc3 \u4e2d\u6697\u5fd6\uff1a\u201c\u4ed6\u662f\u5426\u95ee\u6211\u8981\u4e0d\u8981\u5728\u8fd9\u91cc\u716e\u4e9b\u4e1c\u897f\u5403\uff1f\u201d\u8f6c\u76ee\u4e00\u671b\uff0c\u4fbf\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e00\u89c1\u5927\u559c\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u5374\u9762\u5bb9\u9aa4\u53d8\uff0c\u5434\u9e23\u4e16\u89c1\u4ed6\u5ffd\u7136\u6447\u8d77\u5934\u6765\uff0c\u5fc3\u4e2d \u4e00\u6025\uff0c\u4f46\u9762\u4e0a\u5374\u4e5f\u4e0d\u52a8\u58f0\u8272\uff0c\u5fc3\u5ff5\u6781\u5feb\u5730\u8f6c\u4e86\u51e0\u8f6c\uff0c\u65b9\u81ea\u5f00\u53e3\u89e3\u91ca\u9053\uff1a\u201c\u6211\u662f\u5728\u2014\u2014\u201d\u54ea\u77e5 \u5374\u89c1\u88f4\u73cf\u53c8\u7a81\u7136\u70b9\u8d77\u5934\u6765\uff0c\u539f\u6765\u4ed6\u65b9\u624d\u601d\u6f6e\u5982\u6d8c\uff0c\u4ec0\u4e48\u4e8b\u90fd\u5fd8\u8bb0\u4e86\uff0c\u6b64\u523b\u4e00\u89c1\u8fd9\u76f4\u5230\u6b64\u523b\u4ed6 \u8fd8\u4e0d\u77e5\u9053\u59d3\u540d\u7684\u201c\u77e5\u5df1\u201d\u4e00\u6307\u90a3\u5e03\u53e3\u888b\uff0c\u53c8\u60f3\u8d77\u65b9\u624d\u90a3\u9505\u201c\u94dc\u956f\u716e\u6210\u7684\u6c64\u201d\uff0c\u809a\u91cc\u5c31\u89c9\u5f97\u6709 \u4e9b\u997f\u4e86\uff0c\u662f\u5df2\u4fbf\u4e0d\u4f4f\u70b9\u5934\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\uff0c\u60f3\u5230\u90a3\u68b3\u7740\u8fab\u5b50\u7684\u59d1\u5a18\u7f9e\u7b54\u7b54\u9001\u53bb\u8471\u59dc\u7684\u6837 \u5b50\uff0c\u4ed6\u4e0d\u7981\u7b11\u5f97\u66f4\u52a0\u5389\u5bb3\u3002 \u3000\u3000\u5434\u9e23\u4e16\u957f\u957f\u677e\u4e86\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u513f\u8fd9\u4f4d\u88f4\u5144\u771f\u662f\u56fa\u6267\u5f97\u5f88\uff0c\u5c0f\u53ef\u5411\u4ed6\u89e3\u91ca\u534a\u5929\uff0c\u4ed6\u624d\u7b54\u5e94 \u4e86\u3002\u201c\u201d\u91d1\u9e21\u201c\u5411\u4e00\u557c\u91cd\u91cd\u54fc\u4e86\u4e00\u58f0\uff0c\u5c06\u624b\u4e2d\u94c1\u62d0\u4e00\u987f\uff0c\u4fbf\u5df2\u8d70\u5230\u95e8\u53e3\uff0c\u5ffd\u7136\u773c\u524d\u4e00\u82b1\uff0c\u201d \u795e\u624b\u201c\u6218\u98de\u5df2\u6321\u5728\u9762\u524d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6ca1\u6709\u53c2\u89c1\u603b\u74e2\u628a\u5b50\u7684\u4eba\uff0c\u8c01\u4e5f\u522b\u60f3\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u53cc\u76ee\u4e00\u5f20\uff0c\u53ea\u89c9\u4e00\u80a1\u6012\u6c14\uff0c\u76f4\u51b2\u5fc3\u80f8\uff0c\u4f46\u5374\u53c8\u81ea\u77e5\u4e0d\u662f\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de \u4e4b\u654c\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u77aa\u4e86\u534a\u664c\uff0c\u5411\u4e00\u557c\u52c9\u5f3a\u5c06\u8fd9\u80a1\u6012\u6c14\uff0c\u6309\u5728\u5fc3\u91cc\uff0c\u7f13\u7f13\u56de\u8f6c\u8eab\uff0c\u4e00\u9762\u8f6c \u7740\u5ff5\u5934\uff1a\u201c\u6211\u5c06\u8fd9\u5c0f\u5b50\u5bb0\u4e86\uff0c\u770b\u4f60\u8fd8\u627e\u8c01\u505a\u603b\u74e2\u628a\u5b50\u53bb\u3002\u201d\u6697\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u7f13\u7f13\u8d70\u5230\u88f4\u73cf\u8eab \u524d\uff0c\u53cc\u62f3\u4e00\u62b1\uff0c\u4ea6\u81ea\u6df1\u6df1\u4e00\u63d6\u3002 \u3000\u3000\u88f4\u73cf\u53c8\u662f\u4e00\u6014\uff0c\u626d\u8fc7\u8eab\u5b50\uff0c\u53bb\u671b\u5434\u9e23\u4e16\uff0c\u54ea\u77e5\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e00\u63d6\u4e4b\u540e\uff0c\u7a81\u5730\u53cc\u62f3\u9f50 \u51fa\uff0c\u5feb\u5982\u95ea\u7535\u5730\u6253\u5728\u88f4\u73cf\u8eab\u4e0a\uff0c\u94c1\u62d0\u4e00\u70b9\uff0c\u8eab\u5f62\u5012\u7a9c\uff0c\u51cc\u7a7a\u4e00\u4e2a\u7b4b\u6597\uff0c\u5c06\u624b\u4e2d\u7684\u94c1\u62d0\u501f\u52b2\u62a1 \u51fa\uff0c\u4e58\u7740\u201c\u795e\u624b\u201d\u6218\u98de\u4fa7\u8eab\u4e00\u8ba9\u4e4b\u65f6\uff0c\u4fbf\u5df2\u63a0\u51fa\u95e8\u5916\uff0c\u94c1\u62d0\u4e00\u70b9\u5385\u95e8\uff0c\u7bad\u4e5f\u4f3c\u5730\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u79f0\u96c4\u6b66\u6797\uff0c\u4e95\u975e\u5e78\u81f4\uff0c\u8fd9\u5168\u529b\u4e00\u51fb\uff0c\u529b\u9053\u4f55\u6b62\u4e94\u767e\u65a4\uff0c\u5e78\u597d\u65b9\u624d\u88f4\u73cf\u8eab \u8eaf\u4e00\u626d\uff0c\u662f\u4ee5\u8fd9\u4e00\u51fb\u6ca1\u6709\u51fb\u5728\u80f8\u4e0a\uff0c\u4f46\u4ed6\u4ea6\u662f\u5168\u8eab\u4e00\u9707\uff0c\u5929\u5730\u5b87\u5b99\u5728\u8fd9\u4e00\u5239\u90a3\u4e4b\u95f4\u4eff\u4f5b\u90fd\u4e3a \u4e4b\u8df3\u52a8\u8d77\u6765\uff0c\u4ed6\u6574\u4e2a\u8eab\u5b50\u4e5f\u88ab\u9707\u5f97\u76f4\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u6bb5\u5df2\u5c06\u71c3\u5c3d\u7684\u8721\u70db\uff0c\u8fdc\u8fdc\u843d\u5230\u8fd9\u5927\u5385\u7684\u89d2\u843d\u91cc\uff0c\u5149\u7ebf\u7acb\u523b\u4e00\u9eef\u3002 \u3000\u3000\u8fd9\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u7eb5\u8eab\u3001\u6325\u6756\u3001\u51fa\u95e8\u3001\u88f4\u73cf\u8eab\u98de\u3001\u70db\u706d\uff0c\u51e0\u4e4e\u662f\u5728\u540c\u4e00\u5239\u90a3\u4e2d\u53d1\u751f\uff0c \u201c\u795e\u624b\u201d\u6218\u98de\u5927\u559d\u4e00\u58f0\uff0c\u731b\u4e00\u957f\u8eab\uff0c\u6709\u5982\u79bb\u5f26\u4e4b\u7bad\u822c\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u7684\u8eab\u5f62\uff0c\u5df2\u5728\u5341\u4e08\u5f00\u5916\uff0c\u8fd9\u7f3a\u4e86\u4e00\u8db3\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u8eab\u624b\u4e4b\u5feb\uff0c\u7aef\u5f97 \u60ca\u4eba\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5168\u529b\u800c\u8ffd\uff0c\u500f\u7136\u5341\u6570\u4e2a\u8d77\u843d\uff0c\u4fbf\u5df2\u63a0\u51fa\u4e86\u767e\u4e08\uff0c\u4f46\u5374\u4ecd\u7136\u548c\u4ed6\u6709\u7740\u4e00\u6bb5\u8ddd \u79bb\uff0c\u6218\u98de\u77e5\u9053\u81ea\u5df1\u82e5\u60f3\u8ffd\u4e0a\u4ed6\uff0c\u5e76\u975e\u6613\u4e8b\uff0c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u60f3\u5230\u88f4\u73cf\u4ecd\u7136\u7559\u5728\u5385\u91cc\uff0c\u4e0d\u77e5\u751f\u6b7b\u5982 \u4f55\uff0c\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u7b49\u4eba\u82e5\u5728\u6b64\u523b\u6709\u4f55\u4e3e\u52a8\uff0c\u90a3\u4e48\u81ea\u5df1\u5c82\u975e\u524d\u529f\u5c3d\u5f03\u3002 \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u4fbf\u56de\u8eab\u63a0\u4e86\u56de\u53bb\uff0c\u4e00\u4eba\u5927\u5385\uff0c\u53ea\u89c1\u5385\u5185\u5149\u7ebf\u660f\u9eef\uff0c\u8fde\u534a\u6761\u4eba\u5f71\u90fd\u6ca1\u6709\u4e86\uff0c \u53ea\u6709\u5434\u9e23\u4e16\u7684\u4e00\u4e2a\u5927\u5e03\u888b\u548c\u4e00\u5806\u6b7b\u9e21\uff0c\u4ecd\u7136\u7559\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u968f\u5373\u51b7\u51b7\u4e00\u7b11\uff0c\u7a81\u5730\u62ac\u5934\u5927\u559d\u9053\uff1a\u201c\u987b\u65b0\uff0c\u4f60\u4e0b\u6765\u3002\u201d \u3000\u3000\u559d\u58f0\u65b9\u4f4f\uff0c\u5927\u5385\u627f\u6881\u4e4b\u4e0a\uff0c\u5df2\u8dc3\u4e0b\u4e00\u3001\u6761\u4eba\u5f71\u6765\uff0c\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u8fde\u8eab\u4e0a\u548c \u5934\u4e0a\u7684\u5c18\u4e0a\u90fd\u6ca1\u6709\u62cd\uff0c\u5c31\u8eac\u8eab\u7ad9\u5728\u201c\u795e\u98de\u201d\u6218\u98de\u8eab\u524d\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6b63\u5982\u548c\u4e16\u95f4\u6240\u6709\u7684\u5974\u624d\u89c1 \u7740\u4e3b\u5b50\u7684\u795e\u60c5\u4e00\u6837\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4fbf\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u65b9\u624d\u90a3\u4e9b\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u90a3\u987b\u65b0\u82e6\u7740\u8138\uff0c\u5450\u5450\u5730\u7b54\u4e0d\u51fa\u8bdd\u6765\uff0c\u539f\u6765\u4ed6\u5728\u627f\u6881\u4e0a\u8e72\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u65b9\u624d\u7adf\u7761\u7740\u4e86\uff0c\u76f4 \u5230\u6218\u98de\u5927\u58f0\u4e00\u559d\uff0c\u624d\u5c06\u4ed6\u60ca\u9192\u8fc7\u6765\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u76b1\uff0c\u76ee\u5149\u4e4b\u4e2d\uff0c\u6ee1\u542b\u6740\u673a\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u77aa\u5728\u987b\u65b0\u8138\u4e0a\u3002\u987b\u65b0\u53ea\u89c9\u6d51 \u8eab\u53d1\u51b7\uff0c\u51b7\u6c57\u76f4\u6d41\uff0c\u201c\u5657\u901a\u201d\u8dea\u4e86\u4e0b\u53bb\uff0c\u54c0\u58f0\u9053\uff1a\u201c\u5c0f\u4eba\u2014\u2014\u6ca1\u770b\u5230\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u54fc\u4e00\u58f0\uff0c\u5389\u58f0\u9053\uff1a\u201c\u517b\u7740\u4f60\u4eec\u8fd9\u4e9b\u5e9f\u6599\uff0c\u771f\u662f\u65e0\u7528\u3002\u201d\u7f13\u7f13\u4f38\u51fa\u624b\u638c\uff0c \u5411\u90a3\u987b\u65b0\u5934\u4e0a\u62cd\u53bb\uff0c\u987b\u65b0\u773c\u671b\u7740\u8fd9\u53cc\u624b\u638c\uff0c\u5168\u8eab\u4e0d\u4f4f\u5730\u98a4\u6296\uff0c\u5374\u8fde\u8eb2\u90fd\u4e0d\u6562\u8eb2\u3002 \u3000\u3000\u54ea\u77e5\u201c\u795e\u624b\u201d\u6218\u98de\u638c\u5230\u4e2d\u9014\uff0c\u7ade\u7a81\u5730\u653e\u4e86\u4e0b\u53bb\uff0c\u6325\u4e86\u6325\u624b\u548c\u58f0\u9053\uff1a\u201c\u4f60\u5446\u4e86\u4e00\u5929\uff0c\u5feb\u53bb \u6b47\u6b47\u5427\u3002\u201d\u53c8\u9053\uff1a\u4f60\u8eab\u4f53\u4e0d\u597d\uff0c\u5c06\u8fd9\u4e9b\u9e21\u62ff\u56de\u53bb\u716e\u6c64\u6765\u5403\uff0c\u4ee5\u540e\u5c31\u4e0d\u4f1a\u5e38\u5e38\u60f3\u7761\u89c9\u4e86\u3002\u201c\u90a3 \u987b\u65b0\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u54af\u54af\u5728\u5730\u4e0a\u53e9\u4e86\u51e0\u4e2a\u5934\uff0c\u62ac\u8d77\u90a3\u5806\u6b7b\u9e21\uff0c\u611f\u6fc0\u6d95\u96f6 \u5730\u53bb\u4e86\u3002\u987b\u77e5\u201d\u795e\u624b\u201c\u5fc3\u667a\u6df1\u6c89\uff0c\u57ce\u5e9c\u6781\u6df1\uff0c\u6b63\u662f\u67ad\u96c4\u4e4b\u624d\uff0c\u65b9\u624d\u5fc3\u4e2d\u867d\u662f\u6ee1\u809a\u6012\u706b\uff0c\u4f46\u8f6c \u5ff5\u4e4b\u95f4\uff0c\u60f3\u5230\u4e8b\u5df2\u81f3\u6b64\uff0c\u6740\u4e86\u4ed6\u53c8\u6709\u4f55\u7528\uff0c\u4e0d\u5982\u653e\u4ed6\u53bb\u4e86\uff0c\u8ba9\u4ed6\u4ee5\u540e\u66f4\u6b7b\u5fc3\u584c\u5730\u5730\u6548\u5fe0\u81ea \u5df1\u3002\u53e4\u5f80\u4eca\u6765\uff0c\u4e00\u5fc3\u60f3\u6210\u9738\u4e1a\u7684\u67ad\u96c4\u4ff1\u662f\u5982\u6b64\uff0c\u53c8\u5c82\u53ea\u6218\u98de\u4e00\u4eba\u800c\u5df2\u3002\u4ed6\u601d\u7d22\u534a\u664c\uff0c\u8fde\u8fde\u51b7 \u7b11\u9053\uff1a\u201c\u4f60\u82e5\u9003\u8fc7\u8001\u592b\u7684\u638c\u5fc3\uff0c\u54fc\u2014\u2014\u201d\u7f13\u7f13\u8d70\u5230\u90a3\u5e45\u753b\u524d\uff0c\u5c06\u90a3\u5e45\u753b\u4ed4\u7ec6\u5730\u5377\u4e86\u8d77\u6765\uff0c\u7f13 \u7f13\u56de\u8eab\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u500f\u89c1\u5385\u95e8\u4e4b\u524d\uff0c\u8d6b\u7136\u7ad9\u7740\u4e00\u4eba\uff0c\u7adf\u662f\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5012\u5927\u51fa\u201c\u795e\u624b\u201d\u6218\u98de\u610f\u6599\u4e4b\u5916\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6c89\u58f0\u53f1\u9053\uff1a\u201c\u4ed6\u4eec\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u9762\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u51b7\u51b7\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u56de\u8eab\u8d70\u51fa\uff0c\u4e00\u9762\u9053\uff1a\u201c\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6ee1\u8179\u6012\u6c14\u5374\u53ea\u5f97\u6309\u637a\u4f4f\uff0c\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u53ea\u89c1\u4ed6\u80a9\u5934\u4e0d\u52a8\uff0c\u8170\u8eab\u4e0d\u56de\uff0c\u811a\u4e0b \u5374\u8d70\u5f97\u98de\u5feb\uff0c\u50cf\u662f\u8fde\u811a\u5c16\u90fd\u4e0d\u6cbe\u5730\u4e00\u822c\u3002 \u3000\u3000\u4e24\u4eba\u5404\u5404\u94c1\u9752\u7740\u8138\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u8d70\u4e86\u534a\u664c\uff0c\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u7a81\u5730\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u83ab\u6c0f\u5144\u5f1f \u82e5\u662f\u6551\u5f85\u4e86\u90a3\u59d3\u88f4\u7684\uff0c\u5b9a\u7136\u5bf9\u4ed6\u611f\u6fc0\uff0c\u65e5\u540e\u83ab\u5357\u8981\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u4ed6\u4e5f\u4e0d\u597d\u610f\u601d\u4e0d\u542c\u3002\u201d \u3000\u3000\u8fd9\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5934\u4e5f\u4e0d\u56de\uff0c\u51b7\u7136\u8bf4\u51fa\u8fd9\u51e0\u53e5\u8bdd\u6765\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u4e0d\u7981\u5fc3\u4e2d\u5927\u52a8\uff0c\u4f46\u5374\u4ecd \u7136\u88c5\u7740\u65e0\u52a8\u4e8e\u8877\u7684\u6837\u5b50\u9053\uff1a\u201c\u542c\u53c8\u600e\u5730\uff1f\u4e0d\u542c\u53c8\u600e\u5730\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u542c\u4e0d\u542c\u83ab\u6c0f\u5144\u5f1f\u7684\u8bdd\uff0c\u81ea\u7136\u4e0e\u6211\u65e0\u5173\uff0c\u53ef\u662f\u2014\u2014\u54fc\uff0c \u8981\u77e5\u9053\u2018\u5317\u6597\u2019\u4e03\u715e\u5144\u5f1f\u4e03\u4eba\uff0c\u8bba\u5b9e\u529b\u4e5f\u4e0d\u5728\u9601\u4e0b\u4e4b\u4e0b\u54e9\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u52a8\uff0c\u6c89\u5fd6\u4e86\u534a\u664c\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f9d\u90a3\u5144\u4e4b\u89c1\uff0c\u53c8\u8be5\u5982\u4f55 \u5462\uff1f\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u51b7\u51b7\u51b0\u51b0\u7684\u5473\u9053\u5df2\u4e00\u626b\u800c\u7a7a\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u811a\u4e0b\u4e0d\u505c\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4f9d\u6211\u4e4b\u89c1\uff0c\u6211\u82e5\u662f\u4f60\uff0c\u4fbf\u627e\u4e00\u4e2a\u80fd\u52a9\u4f60\u4e00\u81c2\u7684\u5e2e \u624b\uff0c\u4e24\u4eba\u540c\u5fc3\uff0c\u529b\u80fd\u65ad\u91d1\uff0c\u2018\u795e\u624b\u2019\u6218\u98de\u806a\u660e\u4e00\u4e16\uff0c\u96be\u9053\u4f1a\u7cca\u6d82\u4e00\u65f6\u5417\uff1f\u201d \u3000\u3000\u201c\u795e\u98de\u201d\u6218\u98de\u4e00\u62cd\u524d\u989d\uff0c\u8fde\u8fde\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff01\u201d\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u5c0f\u5f1f\u65e9\u6709\u7ed3\u4ea4\u90a3\u5144\u4e4b \u610f\uff0c\u53ea\u662f\u96be\u4ee5\u542f\u53e3\u800c\u5df2\uff0c\u6b64\u523b\u90a3\u5144\u65e2\u5982\u6b64\u8bf4\uff0c\u60f3\u5fc5\u662f\u80af\u6298\u8282\u4e0b\u4ea4\u7684\u4e86\u3002\u201d\u5176\u5b9e\u8fd9\u201c\u4e03\u5de7\u8ffd \u9b42\u201d\u8bf4\u7b2c\u4e00\u53e5\u8bdd\u65f6\uff0c\u4ed6\u4fbf\u5df2\u7aa5\u7834\u771f\u610f\uff0c\u53ea\u662f\u4ed6\u57ce\u5e9c\u6781\u6df1\uff0c\u76f4\u5230\u6b64\u523b\u624d\u505a\u51fa\u604d\u7136\u5927\u609f\uff0c\u6b23\u559c\u65e0 \u6bd4\u7684\u6837\u5b50\u6765\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u7a81\u5730\u505c\u4e0b\u811a\u6b65\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u4f38\u51fa\u53f3\u624b\u6765\uff0c\u6218\u98de\u76ee\u5149\u4e00\u8f6c\uff0c\u4ea6\u81ea\u4f38\u51fa\u53f3\u624b\uff0c \u53ea\u542c\u201c\u62cd\u3001\u62cd\u3001\u62cd\u201d\u4e09\u58f0\uff0c\u4e24\u4eba\u5df2\u5bf9\u51fb\u4e86\u4e09\u638c\uff0c\u90a3\u98de\u8679\u51b0\u51b7\u7684\u76ee\u5149\u91cc\uff0c\u95ea\u8fc7\u4e00\u4e1d\u559c\u8272\uff0c\u4f46\u968f \u5373\u6de1\u6de1\u9053\uff1a\u201c\u90a3\u59d3\u88f4\u7684\u4f24\u52bf\u5e76\u4e0d\u751a\u91cd\uff0c\u7edd\u4e0d\u4f1a\u4f24\u4e86\u6027\u547d\uff0c\u53ef\u53ea\u5c31\u51ed\u90a3\u59d3\u5960\u7684\u5144\u5f1f\u4e24\u4eba\uff0c\u5374\u7edd \u5bf9\u6cbb\u4e0d\u597d\u4ed6\u3002\u4f9d\u6211\u4e4b\u89c1\uff0c\u6218\u5144\u4e5f\u4e0d\u5fc5\u592a\u5feb\u5c06\u4ed6\u7684\u4f24\u51b6\u6108\uff0c\u4e5f\u4e0d\u8981\u8bf4\u51fa\u4f24\u52bf\u7684\u8f7b\u91cd\u6765\uff0c\u5148\u62d6\u4e00 \u6bb5\u65f6\u671f\u518d\u8bf4\u3002\u82e5\u662f\u8fd9\u59d3\u88f4\u7684\u8868\u793a\u5f88\u4e70\u6211\u4eec\u7684\u8d26\u7684\u6837\u5b50\uff0c\u6218\u5144\u518d\u5c06\u4ed6\u6cbb\u6108\uff0c\u4e5f\u4e0d\u7b97\u8fdf\uff0c\u5426\u5219\u2014 \u2014\u54fc\u4ed6\u53c8\u662f\u51b7\u7b11\u4e00\u58f0\uff0c\u4f38\u51fa\u5de6\u638c\uff0c\u7acb\u638c\u5982\u5200\uff0c\u505a\u4e86\u4e2a\u5f80\u4e0b\u201d\u5207\u201c\u7684\u624b\u5f0f\uff0c\u4e00\u9762\u53c8\u9053\uff1a\u201c\u5c31\u60f3 \u6cd5\u628a\u4ed6\u5bb0\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u5934\u4e00\u51db\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u90a3\u98de\u8679\u624b\u6bb5\u4e4b\u72e0\uff0c\u5fc3\u80a0\u4e4b\u8fa3\uff0c\u770b\u6765\u7adf\u8fd8\u5728\u6211\u4e4b\u4e0a\uff0c \u65e5\u540e\u82e5\u4e0d\u5c06\u4ed6\u9664\u53bb\uff0c\u83ab\u8981\u6211\u4e5f\u7740\u4e86\u4ed6\u7684\u9053\u513f\u3002\u201d\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u90a3\u5144\u4e4b\u8ba1\uff0c\u771f\u662f\u5999\u7edd\u4eba\u8870\uff0c \u53ea\u6015\u5f20\u826f\u590d\u751f\uff0c\u8bf8\u845b\u5728\u4e16\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff0c\u5c0f\u5f1f\u4e00\u4ecb\u6b66\u592b\uff0c\u65e5\u540e\u8fd8\u8981\u90a3\u5144\u65f6\u5e38\u8d50\u6559\u624d\u662f\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u8f6c\u8eab\u53c8\u5f80\u524d\u8d70\uff0c\u5fc3\u4e2d\u5374\u5728\u60f3\u9053\uff1a\u201c\u8fd9\u59d3\u6218 \u7684\u8868\u9762\u4e0a\u770b\u6765\u867d\u662f\u4e2a\u76f4\u80a0\u6c49\u5b50\uff0c\u8bf4\u8d77\u8bdd\u6765\u4e5f\u597d\u542c\u5f97\u5f88\uff0c\u5176\u5b9e\u4ed6\u5fc3\u91cc\u60f3\u4ec0\u4e48\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u6b64 \u4eba\u57ce\u5e9c\u592a\u6df1\uff0c\u65e5\u540e\u82e5\u4e0d\u597d\u597d\u5bf9\u4ed8\u4ed6\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u5c31\u4f1a\u5148\u4e0b\u624b\u5c06\u6211\u9664\u53bb\u3002\u201d \u3000\u3000\u4e24\u4eba\u867d\u7136\u5fc3\u4e2d\u5404\u81ea\u8f6c\u7740\u5ff5\u5934\uff0c\u4f46\u811a\u4e0b\u5374\u90fd\u6781\u5feb\uff0c\u8d70\u4e86\u534a\u664c\uff0c\u6218\u98de\u53ea\u89c1\u524d\u9762\u4e00\u7247\u7a3b\u8349\u4e4b \u4e2d\uff0c\u76d6\u7740\u4e09\u4e94\u95f4\u623f\u5b50\uff0c\u6b64\u523b\u7a97\u5185\u706f\u706b\u8367\u8367\uff0c\u7167\u5f97\u7a97\u7eb8\u4e00\u7247\u660f\u9ec4\uff0c\u77e5\u9053\u4fbf\u662f\u90a3\u83ab\u6c0f\u5144\u5f1f\u5b58\u8eab\u4e4b \u5904\u4e86\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u679c\u7136\u4fa7\u9996\u9053\uff1a\u201c\u5230\u4e86\u3002\u201d\u8eab\u5f62\u52a0\u5feb\uff0c\u500f\u7136\u51e0\u4e2a\u8d77\u843d\uff0c\u63a0\u5230\u90a3\u680b\u623f\u5b50\u95e8\u524d\uff0c \u4f38\u624b\u4e00\u63a8\uff0c\u95ea\u8eab\u63a0\u4e86\u8fdb\u53bb\u3002\u8d70\u5165\u5ba4\u5185\uff0c\u53ea\u89c1\u8fce\u95e8\u4e00\u5f20\u5367\u69bb\u4e0a\uff0c\u7761\u7740\u5140\u81ea\u6655\u8ff7\u7740\u7684\u88f4\u73cf\uff0c\u5434\u9e23 \u4e16\u6ee1\u9762\u5173\u5207\u4e4b\u5bb9\uff0c\u5750\u5728\u5e8a\u4fa7\uff0c\u90a3\u83ab\u6c0f\u5144\u5f1f\u5374\u4e00\u4e2a\u4e3e\u7740\u6cb9\u706f\uff0c\u4e00\u4e2a\u4fef\u9996\u770b\u7740\u88f4\u73cf\u7684\u4f24\u52bf\uff0c\u624b\u91cc \u62ff\u7740\u4e00\u5305\u91d1\u521b\u836f\uff0c\u6b63\u7f13\u7f13\u5f80\u88f4\u73cf\u4f24\u5904\u503e\u5012\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u548c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u8d70\u8fdb\u623f\u91cc\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u4eba\u56de\u5934\u770b\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9f3b\u5b54\u91cc\u91cd\u91cd\u54fc\u4e86\u4e00\u58f0\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u5e8a\u524d\uff0c\u7a81\u7136\u5288\u624b\u4e00\u628a\u593a\u8fc7\u90a3\u83ab\u5357\u624b \u4e2d\u62ff\u7740\u7684\u91d1\u521b\u836f\uff0c\u770b\u4e5f\u4e0d\u770b\u5c31\u5f80\u5730\u4e0a\u4e00\u4e22\uff0c\u4e00\u9762\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u836f\u600e\u6cbb\u5f97\u4e86\u75c5\uff01\u201d\u4fef\u8eab\u4e00 \u671b\uff0c\u53ea\u89c1\u88f4\u73cf\u80a9\u80db\u4e0a\u7684\u8863\u8882\uff0c\u5df2\u88ab\u6495\u5f00\uff0c\u9732\u51fa\u91cc\u9762\u5df2\u7ecf\u9752\u80bf\u8001\u9ad8\u7684\u8089\u6765\uff0c\u4ed6\u7528\u624b\u6307\u8f7b\u8f7b\u4e00 \u6309\uff0c\u53c8\u81ea\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u9aa8\u5934\u788e\u4e86\u6ca1\u6709\uff1f\u201d\u6839\u672c\u518d\u4e5f\u4e0d\u671b\u83ab\u5357\u4e00\u773c\u3002 \u3000\u3000\u83ab\u5357\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u500f\u5ffd\u6362\u4e86\u597d\u51e0\u4e2a\u989c\u8272\uff0c\u7ec8\u4e8e\u4e00\u8a00\u4e0d\u53d1\u5730\u540e\u9000\u4e09\u6b65\uff0c\u56de\u5934\u4e00 \u671b\uff0c\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u67af\u7626\u7684\u9762\u5e9e\u4e0a\uff0c\u6b63\u81ea\u6cdb\u51fa\u4e00\u79cd\u5947\u602a\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u8179\u4e2d\u6697\u9a82\uff1a\u201c\u603b\u6709\u4e00\u5929\uff0c\u54fc\u2014\u2014\u201d\u54ea\u77e5\u4ed6\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u95e8\u5916\u7a81\u5730\u4f20\u6765\u4e00 \u58f0\u68ee\u51b7\u7b11\u58f0\uff0c\u4e00\u4e2a\u5a07\u67d4\u6e05\u8106\u7684\u53e3\u97f3\uff0c\u7528\u5341\u5206\u51b0\u51b7\u7684\u8bed\u6c14\uff0c\u4e00\u5b57\u4e00\u53e5\u4ed6\u8bf4\u9053\u3002 \u3000\u3000\u201c\u8c01\u662f\u2018\u5317\u6597\u4e03\u715e\u2019\u4e2d\u7684\u8001\u5927\u3001\u8001\u4e94\uff0c\u7edf\u7edf\u7ed9\u6211\u6eda\u51fa\u6765\uff01\u201d \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u9a87\u7136\u800c\u987e\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u8eaf\u5a40\u5a1c\uff0c\u9762\u76ee\u5982\u82b1\u7684\u5973\u5b50\uff0c\u4e00\u624b\u62b9\u7740\u95e8\u6846\uff0c\u4fcf\u751f\u751f \u5730\u7ad9\u5728\u95e8\u53e3\uff0c\u4e00\u53cc\u5a9a\u76ee\u4e4b\u4e2d\uff0c\u9732\u51fa\u9635\u9635\u4ee4\u4eba\u5fc3\u60b8\u7684\u5bd2\u5149\u6765\uff0c\u6b63\u81ea\u7f13\u7f13\u81ea\u6bcf\u4e2a\u4eba\u9762\u4e0a\u626b\u8fc7\u3002 \u3000\u3000\u5c4b\u4e2d\u4e4b\u4eba\uff0c\u9664\u4e86\u53d7\u4f24\u7684\u88f4\u73cf\u4e4b\u5916\uff0c\u53ef\u8bf4\u90fd\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u7684\u4e00\u7b49\u9ad8\u624b\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u4eba\u77e5\u9053 \u8fd9\u5973\u5b50\u662f\u4f55\u65f6\u800c\u6765\uff0c\u4ece\u4f55\u800c\u6765\u7684\u3002 \u7b2c\u56db\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u56db\u7ae0 \u3000\u3000\u8fd9\u5973\u5b50\u867d\u7136\u8eab\u8eaf\u5a40\u5a1c\uff0c\u8c8c\u7f8e\u5982\u82b1\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4ea6\u662f\u5a07\u67d4\u6e05\u8106\uff0c\u4efb\u4f55\u4eba\u89c1\u4e86\u8fd9\u79cd\u5973\u5b50\uff0c \u672c\u90fd\u4e0d\u5e94\u6709\u754f\u60e7\u4e4b\u5fc3\uff0c\u4f46\u5979\u8bf4\u8bdd\u7684\u8bed\u6c14\uff0c\u5374\u662f\u51b7\u524a\u65e0\u6bd4\uff0c\u6bcf\u5b57\u6bcf\u53e5\u4e4b\u4e2d\uff0c\u90fd\u751f\u50cf\u662f\u9690\u542b\u7740\u4e00 \u679d\u5229\u7bad\uff0c\u4e94\u715e\u83ab\u5317\u6301\u706f\u5728\u624b\uff0c\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u5fc3\u5934\u7a81\u5730\u4e00\u60ca\uff0c\u624b\u4e5f\u4e0d\u7981\u4e00\u98a4\uff0c\u624b\u4e2d \u7684\u6cb9\u706f\u7adf\u518d\u4e5f\u628a\u6301\u4e0d\u4f4f\uff0c\u7b14\u76f4\u5730\u843d\u5411\u5730\u4e0a\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u5fae\u8f6c\uff0c\u84e6\u5730\u53cd\u624b\u4e00\u6284\uff0c\u5c06\u90a3\u76cf\u773c\u770b\u5df2\u5c06\u843d\u5230\u5730\u4e0a\u7684\u6cb9\u706f\u6284\u5728\u624b\u91cc\uff0c\u706f \u7130\u6447\u4e86\u4e24\u6447\uff0c\u5c06\u7184\u672a\u7184\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u624b\u638c\u4e00\u6258\uff0c\u5e73\u5e73\u7a33\u7a33\u5730\u5c06\u706f\u6258\u4e86\u8d77\u6765\uff0c\u706f\u706b\u53c8\u590d\u8367 \u7136\u3002 \u3000\u3000\u5434\u9e23\u4e16\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u51fa\u624b\u679c\u7136\u5feb\u5f97\u60ca\u4eba\uff0c\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u5f53\u95e8\u9762 \u7acb\u7684\u7edd\u7f8e\u5973\u5b50\uff0c\u5634\u89d2\u4ecd\u81ea\u5e26\u7740\u4e00\u4e1d\u51b7\u524a\u7684\u7b11\u610f\uff0c\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u76ee\u5149\uff0c\u95ea\u7535\u822c\u5730\u51dd\u6ce8\u5728\u201c\u795e \u624b\u201d\u6218\u98de\u9762\u4e0a\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u53ef\u5c31\u662f\u2018\u5317\u6597\u4e03\u715e\u2019\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u4e00\u7b11\uff0c\u8f6c\u8eab\u800c\u7acb\uff0c\u76ee\u5149\u51db\u7136\u5411\u8fd9\u7edd\u7f8e\u5973\u5b50\u8eab\u4e0a\u4e00\u626b\uff0c\u6717\u58f0\u9053\uff1a\u201c\u59d1\u5a18 \u53c8\u662f\u8c01\uff1f\u90a3\u2018\u5317\u6597\u4e03\u715e\u2019\u65e2\u7136\u7d20\u4e0d\u76f8\u8bc6\uff0c\u5bfb\u4ed6\u4e8c\u4eba\uff0c\u53c8\u6709\u4f55\u5e72\uff1f\u201d\u76ee\u5149\u52a8\u5904\uff0c\u659c\u659c\u5411\u90a3\u83ab\u6c0f \u5144\u5f1f\u779f\u4e86\u4e00\u773c\uff0c\u5434\u9e23\u4e16\u51b7\u773c\u65c1\u89c2\uff0c\u4e0d\u7981\u53c8\u6697\u4e2d\u611f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u2018\u795e\u624b\u2019\u6218\u98de\u4e0d\u4f46\u6b66\u529f \u60ca\u4eba\uff0c\u5fc3\u667a\u4ea6\u786e\u975e\u5e38\u4eba\u80fd\u53ca\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u8bdd\u4e2d\u867d\u672a\u8bf4\u51fa\uff0c\u5374\u65e0\u5f02\u5df2\u5c06\u8c01\u662f\u2018\u5317\u6597\u4e03\u715e\u2019\u4e2d \u7684\u8001\u5927\u8001\u4e94\u544a\u8bc9\u4e86\u8fd9\u5973\u5b50\u3002\u201d\u987b\u77e5\u2018\u795e\u624b\u2019\u6218\u98de\u4e00\u770b\u8fd9\u5973\u5b50\u4e4b\u9762\uff0c\u5c31\u77e5\u9053\u6b64\u4eba\u5fc5\u5b9a\u5927\u6709\u6765 \u5934\uff0c\u5fc3\u4e2d\u65e9\u5c31\u5b58\u4e0b\u4e0d\u613f\u5f97\u7f6a\u4e4b\u610f\uff0c\u7b49\u5230\u90a3\u5973\u5b50\u51b7\u51b7\u4e00\u95ee\uff0c\u95ee\u5230\u4ed6\u81ea\u5df1\u5934\u4e0a\uff0c\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u81ea \u7136\u4e0d\u80fd\u8bf4\u51fa\u793a\u5f31\u7684\u8bdd\u6765\uff0c\u4e5f\u52bf\u4e0d\u80fd\u8bf4\u51fa\u8c01\u662f\u2018\u5317\u6597\u4e03\u715e\u2019\uff0c\u4f46\u4ed6\u4e45\u95ef\u6c5f\u6e56\uff0c\u662f\u4f55\u7b49\u5389\u5bb3\u7684\u89d2 \u8272\uff0c\u5fc3\u5ff5\u5fae\u8f6c\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8f7b\u63cf\u6de1\u5199\u4ed6\u8bf4\u51fa\u8fd9\u51e0\u53e5\u8bdd\u6765\uff0c\u4e0d\u4f46\u5df2\u544a\u8bc9\u4e86\u90a3\u5973\u5b50\u81ea\u5df1\u5e76\u975e\u5979\u6240 \u627e\u4e4b\u4eba\uff0c\u4e5f\u544a\u8bc9\u4e86\u5979\u8c01\u662f\u5979\u6240\u8981\u627e\u4e4b\u4eba\uff0c\u800c\u795e\u8272\u8bed\u6c14\uff0c\u5374\u662f\u4e0d\u4ea2\u4e0d\u5351\uff0c\u6b63\u662f\u6807\u6807\u51c6\u51c6\u7684\u8001\u6c5f \u6e56\u53e3\u543b\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u8fd9\u79cd\u5ff5\u5934\uff0c\u4e0d\u4f46\u90a3\u806a\u660e\u7edd\u9876\u7684\u5434\u9e23\u4e16\uff0c\u4e00\u773c\u4fbf\u81ea\u770b\u7834\uff0c\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u548c\u83ab\u6c0f\u5144 \u5f1f\u542c\u5230\u8033\u91cc\uff0c\u809a\u91cc\u4ea6\u90fd\u6709\u6570\uff0c\u83ab\u5357\u3001\u83ab\u5317\u5fc3\u4e2d\u6697\u54fc\u4e00\u58f0\uff0c\u6012\u6c14\u5927\u4f5c\uff0c\u4f46\u5fc3\u4e2d\u5374\u53c8\u4e0d\u7981\u53c8\u4e3a\u5947 \u602a\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u5fd6\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u4e0e\u6211\u7b49\u7d20\u4e0d\u76f8\u8bc6\uff0c\u66f4\u65e0\u51a4\u4ec7\uff0c\u5bfb\u627e\u4e8e\u6211\uff0c\u4e3a\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u76ee\u5149\u62ac\u5904\uff0c\u5374\u89c1\u90a3\u5973\u5b50\u51b0\u51b7\u7684\u76ee\u5149\uff0c\u679c\u7136\u7f13\u7f13\u79fb\u5230\u81ea\u5df1\u8eab\u4e0a\uff0c\u83ab\u5357\u53cc\u7709\u5fae\u76b1\uff0c\u80f8\u819b\u4e00 \u633a\uff0c\u5927\u6b65\u8de8\u524d\u4e00\u6b65\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5144\u5f1f\u4fbf\u662f\u83ab\u5357\uff0c\u4e0d\u77e5\u9053\u59d1\u5a18\u5bfb\u627e\u4e8e\u6211\uff0c\u4e3a\u7740\u4f55\u4e8b\uff1f\u201d\u4e94\u715e\u83ab \u5317\u62ac\u773c\u4e00\u671b\uff0c\u53ea\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u6b63\u4f3c\u7b11\u975e\u7b11\u5730\u671b\u7740\u81ea\u5df1\uff0c\u50cf\u662f\u5728\u6697\u4e2d\u8baa\u7b11\u81ea\u5df1\u65b9\u624d\u5931\u624b\u6389\u843d \u6cb9\u706f\u4e4b\u4e8b\uff0c\u5fc3\u91cc\u4e0d\u89c9\u7f9e\u6127\u4ea4\u96c6\uff0c\u7adf\u5c06\u81ea\u5df1\u5bf9\u8fd9\u6765\u5982\u9b3c\u9b45\uff0c\u884c\u8e2a\u8be1\u5f02\u7684\u5973\u5b50\u7684\u754f\u60e7\u4e4b\u5fc3\uff0c\u5fd8\u5f97 \u5e72\u5e72\u51c0\u51c0\uff0c\u80f8\u819b\u4e00\u633a\uff0c\u4ea6\u81ea\u6717\u58f0\u9053\uff1a\u201c\u5140\u90a3\u4f60\u8fd9\u5973\u5b50\uff0c\u6211\u5144\u5f1f\u4e0e\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f60\u6df1\u66f4\u534a\u591c\u5730 \u6765\u627e\u6211\u5e72\u4ec0\u4e48\uff1f\u8981\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u90a3\u5973\u5b50\u51b7\u51b7\u4e00\u7b11\uff0c\u8eab\u5f62\u7a81\u5730\u4e00\u63a0\uff0c\u83ab\u5317\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u90a3\u5973\u5b50\u4fbf\u7ad9\u5230\u81ea\u5df1\u9762\u524d\uff0c\u4ed6\u58f0\u540d \u9887\u54cd\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u53ef\u662f\u7adf\u8fde\u4eba\u5bb6\u5982\u4f55\u5c55\u52a8\u8eab\u5f62\u90fd\u672a\u770b\u51fa\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u80c6\u6c14\u4fbf\u9981\uff0c\u4e0b\u9762\u7684\u8bdd\u4fbf \u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u5ff5\u6570\u8f6c\uff0c\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u4e0e\u83ab\u6c0f\u53cc\u6770\u6709\u4f55\u8fc7\u8282\uff0c\u4e0d\u59a8\u8bf4 \u51fa\u6765\u5927\u5bb6\u542c\u542c\uff0c\u8001\u592b\u6218\u98de\u2026\u2026\u201d\u54ea\u77e5\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u5973\u5b50\u7a81\u5730\u51b7\u53f1\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e5f \u914d\u7ba1\u6211\u7684\u95f2\u4e8b\u3002\u201d\u731b\u4e00\u56de\u5934\uff0c\u76ee\u5149\u5728\u5434\u9e23\u4e16\u3001\u90a3\u98de\u8679\u548c\u6218\u98de\u8eab\u4e0a\u4e00\u626b\uff0c\u7ea4\u624b\u5fae\u62ac\uff0c\u5f80\u95e8\u5916\u4e00 \u6307\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u7edf\u7edf\u7ed9\u6211\u51fa\u53bb\u3002\u201d \u3000\u3000\u90a3\u98de\u8679\u3001\u5434\u9e23\u4e16\uff0c\u9762\u8272\u4e2a\u4e2a\u4e00\u53d8\uff0c\u5374\u542c\u201c\u795e\u624b\u201d\u6218\u98de\u53c8\u81ea\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5982\u679c\u5982\u6b64\u4e00 \u8d70\uff0c\u65e5\u540e\u4f20\u8a00\u51fa\u53bb\uff0c\u6c5f\u6e56\u4e2d\u4e0d\u77e5\u5185\u60c5\u4e4b\u4eba\uff0c\u8fd8\u9053\u5728\u4e0b\u7b49\u6015\u4e86\u59d1\u5a18\uff0c\u8fd9\u5374\u6709\u4e9b\u4e0d\u4fbf\uff0c\u4f55\u51b5\u2026\u2026 \u54c8\u54c8\uff0c\u5728\u4e0b\u867d\u662f\u65e0\u540d\u5c0f\u5e73\uff0c\u8fd9\u4e24\u4f4d\u5144\u53f0\uff0c\u5374\u662f\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u6050\u6015\u4e0d\u662f\u59d1\u5a18\u80fd\u591f\u547c \u4e4b\u5373\u6765\uff0c\u6325\u4e4b\u5373\u53bb\u7684\u54e9\uff01\u201d \u3000\u3000\u90a3\u98de\u8679\u5fc3\u4e2d\u6697\u9a82\u4e00\u58f0\uff1a\u201c\u8fd9\u6218\u98de\u679c\u7136\u662f\u53ea\u8001\u72d0\u72f8\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u65b9\u5f85\u7b54\u8bdd\uff0c\u54ea\u77e5\u5434\u9e23\u4e16 \u5374\u5df2\u957f\u8eab\u800c\u8d77\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53ea\u8981\u6218\u5144\u613f\u610f\u51fa\u53bb\uff0c\u5c0f\u53ef\u66f4\u65e0\u6240\u8c13\u4e86\u2026\u2026\u90a3\u5144\uff0c\u4f60\u8bf4\u53ef\u662f\uff1f\u201d \u3000\u3000\u90a3\u98de\u8679\u795e\u8272\u4e4b\u95f4\uff0c\u672c\u65e0\u8868\u60c5\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u53ea\u8981\u6218\u5144\u5e26\u5934\uff0c\u6211\u4fbf\u7acb\u523b\u51fa\u53bb\uff0c \u2018\u795e\u624b\u2019\u6218\u98de\u80fd\u591f\u5982\u6b64\uff0c\u6211\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\u90a3\u98de\u8679\u66f4\u65e0\u5173\u7cfb\u4e86\u3002\u201d\u5434\u9e23\u4e16\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b63 \u662f\uff0c\u6b63\u662f\u3002\u201d \u3000\u3000\u62ac\u5934\u4e00\u671b\u2014\u2014\u53ea\u89c1\u90a3\u5973\u5b50\u7684\u4e00\u53cc\u526a\u6c34\u53cc\u77b3\u4e4b\u4e2d\uff0c\u7adf\u9732\u51fa\u8be7\u5f02\u4e4b\u8272\uff0c\u4e0d\u7981\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6 \u9053\uff1a\u201c\u8fd9\u5973\u5b50\u60f3\u5fc5\u662f\u88ab\u6211\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\u5f04\u7cca\u6d82\u4e86\uff0c\u53ea\u6015\u5979\u518d\u4e5f\u60f3\u4e0d\u5230\u540c\u5728\u4e00\u95f4\u6597\u5ba4\u4e2d\u7684\u4eba\uff0c \u5176\u95f4\u5173\u7cfb\uff0c\u7adf\u4f1a\u5982\u6b64\u590d\u6742\u3002\u201d\u4e03\u5de7\u7ae5\u5b50\u4ee5\u5fc3\u667a\u7075\u5de7\uff0c\u540d\u95fb\u5929\u4e0b\uff0c\u4ed6\u8fd9\u4e00\u731c\uff0c\u6b63\u662f\u731c\u5f97\u4e00\u70b9\u4e5f \u4e0d\u9519\u3002 \u3000\u3000\u987b\u77e5\u201c\u795e\u624b\u201d\u6218\u98de\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\uff0c\u4ff1\u662f\u6c5f\u5357\u6b66\u6797\u4e2d\u6781\u8d1f\u76db\u540d\u7684\u4eba\u7269\uff0c\u90a3\u5973\u5b50\u81ea \u4e5f\u542c\u5230\u8fc7\u4ed6\u4eec\u7684\u540d\u5b57\uff0c\u539f\u672c\u4ee5\u4e3a\u8fd9\u4e9b\u4eba\u65e2\u7136\u548c\u90a3\u83ab\u6c0f\u5144\u5f1f\u540c\u5904\u4e00\u5ba4\uff0c\u4e00\u5b9a\u5fc5\u5b9a\u4f1a\u548c\u90a3\u83ab\u6c0f\u5144 \u5f1f\u4e00\u81f4\u8054\u624b\u5bf9\u4ed8\u81ea\u5df1\uff0c\u4ee5\u4ed6\u4eec\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u540d\u5730\u4f4d\uff0c\u83ab\u8bf4\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u8c01\uff0c\u5c31\u7b97\u77e5\u9053\u81ea\u5df1\u662f \u8c01\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u4f4e\u58f0\u4e0b\u6c14\u5730\u5c31\u6b64\u4e00\u8d70\u4e86\u4e4b\uff0c\u5979\u81ea\u662f\u4e0d\u77e5\u9053\u8fd9\u4e9b\u4eba\u4e4b\u95f4\u7684\u5e72\u7cfb\uff0c\u6b64\u523b\u89c1\u4e86\u8fd9\u79cd\u60c5 \u51b5\uff0c\u5fc3\u4e0b\u4e0d\u7981\u5927\u5947\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u5446\u5446\u5730\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u95f4\u6597\u5ba4\u4e4b\u4e2d\uff0c\u4eba\u4eba\u90fd\u6709\u4e0d\u540c\u7684\u5fc3\u601d\uff0c\u90a3\u98de\u8679\u5fc3\u4e2d\u5fd6\u9053\u3002 \u3000\u3000\u201c\u8fd9\u5973\u5b50\u8eab\u6cd5\u8be7\u5f02\uff0c\u5fc5\u5b9a\u5927\u6709\u6765\u5934\uff0c\u90a3\u2018\u795e\u624b\u2019\u6218\u98de\u8001\u597d\u5de8\u733e\uff0c\u4e0d\u613f\u610f\u62db\u60f9\u6b64\u4eba\uff0c\u6211\u53c8 \u4f55\u82e6\u6765\u8d9f\u8fd9\u6dcc\u6d51\u6c34\uff0c\u4f55\u51b5\u2018\u5317\u6597\u4e03\u715e\u2019\u4e0e\u6211\u7d20\u65e0\u4ea4\u60c5\uff0c\u4ed6\u4eec\u7684\u6b7b\u6d3b\uff0c\u4e0e\u6211\u534a\u5206\u5173\u7cfb\u90fd\u6ca1 \u6709\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u5374\u5728\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u8fd9\u2018\u795e\u624b\u2019\u6218\u98de\u60f3\u8131\u8eab\u4e8b\u5916\uff0c\u6211\u5374\u504f\u504f\u4e0d\u8ba9\u4ed6\u5b89\u9038\u3001\u54c8\u54c8\uff0c\u6b64 \u523b\u4ed6\u9762\u4e0a\u7684\u8868\u60c5\uff0c\u771f\u662f\u597d\u770b\u5f97\u5f88\uff0c\u4ee5\u4ed6\u7684\u58f0\u540d\u5730\u4f4d\uff0c\u6211\u5012\u8981\u770b\u770b\u4ed6\u5982\u4f55\u4e22\u5f97\u8d77\u8fd9\u4e2a\u4eba\uff0c\u5f53\u5934 \u8d70\u51fa\u53bb\u2026\u2026\u201d\u8f6c\u5ff5\u53c8\u5fd6\u9053\uff1a\u201c\u53ea\u662f\u4ed6\u82e5\u771f\u7684\u8d70\u4e86\uff0c\u6211\u4e5f\u4e0d\u80fd\u79bb\u5f00\u8fd9\u91cc\uff0c\u90a3\u88f4\u73cf\u4e0e\u6211\u867d\u662f\u521d \u4ea4\uff0c\u4f46\u5374\u6781\u4e3a\u6295\u5951\uff0c\u6211\u600e\u80fd\u8ba9\u4ed6\u4e00\u4eba\u7559\u5728\u8fd9\u91cc\uff1f\u4e07\u4e00\u8fd9\u5973\u4e8e\u548c\u83ab\u6c0f\u5144\u5f1f\u52a8\u624b\u4e4b\u9645\uff0c\u8bef\u4f24\u4e86 \u4ed6\uff0c\u6211\u5c82\u975e\u7ec8\u751f\u6709\u6127\u3002\u201d \u3000\u3000\u83ab\u6c0f\u5144\u5f1f\u9762\u9762\u76f8\u89d1\uff0c\u5fc3\u4e2d\u5404\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u8eab\u6cd5\u8be1\u5f02\uff0c\u6b66\u529f\u50cf\u662f\u6781\u9ad8\uff0c\u96be\u602a\u8fd9\u4e9b\u5bb6\u4f19 \u90fd\u4e0d\u613f\u62db\u60f9\u5979\u2026\u2026\u5947\u602a\u7684\u662f\uff0c\u5979\u7adf\u50cf\u662f\u548c\u6211\u7ed3\u6709\u6df1\u4ec7\uff0c\u6211\u5374\u8fde\u5979\u7684\u9762\u90fd\u6ca1\u6709\u89c1\u8fc7\uff0c\u5509\uff01\u4e8b\u5df2 \u81f3\u6b64\uff0c\u6211\u5144\u5f1f\u5b9a\u8981\u60f3\u4e2a\u529e\u6cd5\u5bf9\u4ed8\u5979\uff0c\u4e07\u4e00\u8d25\u5728\u5979\u624b\u4e0a\uff0c\u65e5\u540e\u4f20\u8bf4\u51fa\u53bb\uff0c\u2018\u5317\u6597\u4e03\u715e\u2019\u5c82\u975e\u5a01 \u540d\u626b\u5730\uff1f\u201d \u3000\u3000\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u5374\u5728\u5fc3\u4e2d\u51b7\u7b11\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u90a3\u98de\u8679\u65b9\u624d\u4e0e\u6211\u51fb\u638c\u4e3a\u76df\uff0c\u6b64\u523b\u7adf\u5c31\u548c \u90a3\u59d3\u5434\u7684\u5c0f\u5b50\u4e00\u8d77\u7528\u8a00\u8bed\u6324\u5151\u4e8e\u6211\uff0c\u4ed6\u4eec\u4ee5\u4e3a\u6211\u4e07\u4e07\u4e0d\u4f1a\u5f53\u5934\u8d70\u51fa\u8fd9\u95f4\u5c4b\u5b50\uff0c\u54fc\u54fc\uff0c\u6211\u5374\u504f \u504f\u8981\u8d70\u51fa\u53bb\u7ed9\u4ed6\u4eec\u770b\u770b\uff0c\u65e5\u540e\u7eb5\u7136\u4f20\u8bf4\u51fa\u53bb\uff0c\u6b66\u6797\u4e2d\u4eba\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u6211\u2018\u795e\u624b\u2019\u6218\u98de\u4f1a\u6015\u4e86\u4e00 \u4e2a\u4e09\u7efa\u68b3\u5934\uff0c\u4e24\u622a\u7a7f\u8863\u7684\u65e0\u540d\u5973\u5b50\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u5ff5\u5934\u5728\u5404\u4eba\u5fc3\u4e2d\u4ff1\u662f\u4e00\u95ea\u800c\u8fc7\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u51b7\u4e00\u7b11\uff0c\u5c06\u624b\u4e2d\u6cb9\u706f\uff0c\u653e\u5230\u684c\u4e0a\uff0c \u56de\u9996\u7b11\u9053\uff1a\u201c\u90a3\u5144\u4e0e\u5434\u5144\u65e2\u5982\u6b64\u8bf4\uff0c\u90a3\u4e48\u2026\u2026\u201d \u3000\u3000\u4e94\u715e\u83ab\u5317\u53cc\u7709\u4e00\u8f69\uff0c\u7a81\u7136\u63a5\u53e3\u9053\uff1a\u201c\u6210\u5144\u3001\u90a3\u5144\u4ff1\u90fd\u4e0d\u5fc5\u51fa\u53bb\uff0c\u8fd8\u662f\u6211\u5144\u5f1f\u51fa\u53bb\u7684\u597d\uff0c \u53cd\u6b63\u6b64\u5730\u5730\u65b9\u5927\u5c0f\uff0c\u8eab\u624b\u4e5f\u65bd\u5c55\u4e0d\u5f00\u3002\u201d\u4e00\u62c2\u8863\u8896\uff0c\u5927\u6b65\u5411\u95e8\u5916\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u7edd\u7f8e\u5973\u5b50\u5fae\u4e00\u5b9a\u795e\uff0c\u4ea6\u81ea\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u559c\u6b22\u5230\u5916\u9762\u53bb\u6b7b\uff0c\u4e5f\u672a\u5c1d\u4e0d\u53ef\u3002\u201d\u83ab\u5357\u4ea6\u81ea \u5927\u6b65\u524d\u884c\uff0c\u6b64\u523b\u7a81\u5730\u9a7b\u8db3\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u4e0e\u6211\u7b49\u7a76\u7adf\u6709\u4f55\u4ec7\u6068\uff0c\u4e0d\u59a8\u5148\u8bf4\u51fa\u6765\uff0c\u4e5f\u8bb8\u2026\u2026\u201d \u3000\u3000\u90a3\u5973\u5b50\u51b7\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u2018\u5317\u6597\u4e03\u715e\u2019\u4e0d\u662f\u8d2a\u6deb\u597d\u8272\uff0c\u5c31\u662f\u7a77\u51f6\u6781\u6076\uff0c\u6211\u65e9\u5c31\u60f3\u9664\u53bb\u4f60\u4eec \u8fd9\u6279\u7978\u5bb3\u4e86\uff0c\u54fc\uff01\u4f60\u4eec\u600e\u914d\u4e0e\u6211\u6709\u4ec0\u4e48\u4ec7\u6068\u3002\u201d \u3000\u3000\u4e94\u715e\u83ab\u5317\u4e00\u5c55\u53cc\u7709\uff0c\u51b7\u53f1\u9053\uff1a\u201c\u4f60\u53c8\u662f\u4ec0\u4e48\u4e1c\u897f\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u7a81\u5730\u53cc\u624b\u4e00\u626c\uff0c\u8eab\u5f62 \u5374\u7535\u4e5f\u4f3c\u7684\u7a9c\u51fa\u95e8\u53bb\u3002\u201c\u795e\u624b\u201d\u6218\u98de\u4f4e\u547c\u4e00\u58f0\uff0c\u5012\u9000\u4e09\u6b65\uff0c\u53ea\u89c1\u5341\u6570\u70b9\u94f6\u661f\u3001\u95ea\u7535\u822c\u81ea\u773c\u524d \u63a0\u8fc7\uff0c\u51fb\u5411\u90a3\u7edd\u7f8e\u5c11\u5973\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u91cc\uff0c\u83ab\u5357\u4ea6\u662f\u8dfa\u811a\u7eb5\u8eab\uff0c\u63a0\u51fa\u95e8\u5916\uff0c\u53cd\u624b\u3001\u6325\uff0c\u94f6\u661f\u7535\u5c04\uff0c\u8fd9\u201c\u5317\u6597\u4e03 \u715e\u201d\u4ed6\u4ee5\u540d\u626c\u5929\u4e0b\u7684\u2018\u5317\u6597\u4e03\u661f\u9488\u201c\uff0c\u7aef\u7684\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u5f1f\u5144\u4e8c\u4eba\u53d1\u51fa\u65f6\u867d\u6709\u5148\u540e\uff0c\u4f46\u4f17\u4eba \u773c\u524d\u53ea\u89c9\u5f97\u94f6\u5149\u767e\u6402\uff0c\u5374\u6839\u672c\u5206\u4e0d\u51fa\u5148\u540e\u6765\u3002\u90a3\u7edd\u7f8e\u5973\u5b50\u67f3\u7709\u4e00\u626c\uff0c\u7ea4\u8170\u8f7b\u6298\uff0c\u8f7b\u8f7b\u6ed1\u5f00\u4e94 \u5c3a\u3002\u5434\u9e23\u4e16\u65b9\u81ea\u6697\u53f9\u4e00\u58f0\uff1a\u201c\u597d\u5feb\u7684\u8eab\u624b\u3002\u201d\u76ee\u5149\u52a8\u5904\uff0c\u5374\u89c1\u8fd9\u6570\u5341\u70b9\u94f6\u661f\u4f59\u52bf\u4e30\u6b47\uff0c\u6b64\u523b \u7adf\u9f50\u5730\u51fb\u5411\u90a3\u5367\u5728\u5e8a\u4e0a\u3001\u5140\u81ea\u6655\u8ff7\u672a\u9192\u7684\u88f4\u73cf\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u8131\u53e3\u800c\u547c\uff0c\u4f46\u90a3\u201c\u5317\u6597\u4e03\u661f\u9488\u201d\u672c\u662f\u4ee5\u673a\u7c27\u5f29\u7b52\u5c04\u51fa\uff0c\u662f\u4f55\u7b49\u60ca\u4eba\u7684\u901f \u5ea6\uff0c\u83ab\u8bf4\u4ed6\u6b64\u523b\u8fdc\u8fdc\u7ad9\u5728\u65c1\u8fb9\uff0c\u5c31\u7b97\u4ed6\u7ad9\u5f97\u8fdc\uff0c\u8f83\u6b64\u523b\u8fd1\u4e9b\uff0c\u4e5f\u4e07\u4e07\u65e0\u6cd5\u5c06\u8fd9\u6570\u5341\u70b9\u94f6\u661f\u4e00 \u8d77\u6321\u4f4f\u3002 \u3000\u3000\u773c\u89c1\u8fd9\u4e09\u7b52\u4e8c\u5341\u4e00\u53e3\u201c\u5317\u6597\u4e03\u661f\u9488\u201d\uff0c\u4fbf\u8981\u9f50\u5730\u6253\u5728\u88f4\u73cf\u8eab\u4e0a\uff0c\u88f4\u73cf\u7eb5\u7136\u529f\u529b\u7edd\u4e16\uff0c\u4e5f \u65e0\u6cd5\u7981\u53d7\u5f97\u8d77\uff0c\u4f55\u51b5\u4ed6\u6839\u672c\u6b66\u529f\u5e73\u5e38\uff0c\u6b64\u523b\u66f4\u662f\u6655\u8ff7\u672a\u9192\uff0c\u8fd9\u4e8c\u5341\u4e00\u53e3\u94f6\u9488\u82e5\u662f\u51fb\u5728\u4ed6\u8eab \u4e0a\uff0c\u6015\u4e0d\u5c06\u4ed6\u51fb\u5f97\u6709\u5982\u8702\u5de2\u4e00\u822c\u3002 \u3000\u3000\u201c\u795e\u624b\u3001\u6218\u98de\u4ea6\u81ea\u5fc3\u4e0b\u5927\u60ca\uff0c\u6697\u9053\u4e00\u58f0\u201d\u7f62\u4e86\u3002\u201c\u5434\u545c\u4e16\u5df2\u5927\u53eb\u7740\u6251\u4e86\u8fc7\u53bb\u2014\u2014\u54ea\u77e5\u90a3 \u5973\u5b50\u76ee\u5149\u52a8\u5904\uff0c\u8138\u8272\u4ea6\u662f\u4e00\u53d8\uff0c\u8131\u53e3\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u3002\u201d\u8eab\u5f62\u5df2\u5728\u8fd9\u4e00\u53eb\u58f0\u4e4b\u4e2d\uff0c\u500f\u7136\u4e00 \u6298\uff0c\u540e\u9000\u7740\u7684\u8eab\u5f62\uff0c\u7ade\u53c8\u7a81\u5730\u5411\u524d\u4e00\u63a0\uff0c\u5fae\u62ac\u7ea4\u638c\uff0c\u53cc\u638c\u4e00\u5708\uff0c\u90a3\u6570\u5341\u53e3\u7535\u5c04\u800c\u524d\u7684\u94f6\u9488\u7adf \u4e5f\u7a81\u5730\u8f6c\u53d8\u4e86\u65b9\u5411\uff0c\u6295\u5165\u90a3\u7edd\u7f8e\u5973\u5b50\u7684\u4e00\u53cc\u7f57\u8896\u4e4b\u4e2d\uff0c\u6709\u5982\u6ce5\u725b\u4eba\u6d77\uff0c\u6643\u773c\u4fbf\u65e0\u8e2a\u8ff9\u3002 \u3000\u3000\u5434\u9e23\u4e16\u5168\u529b\u800c\u6251\uff0c\u8eab\u5f62\u5982\u79bb\u5f26\u4e4b\u7bad\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u63a7\u5236\u4e0d\u4f4f\uff0c\u201c\u7830\u201d\u5730\u6251\u5230\u88f4\u73cf\u8eab\u4e0a\uff0c\u5fc3 \u91cc\u53ea\u671b\u81ea\u5df1\u7684\u8eab\u5f62\u80fd\u6bd4\u90a3\u6570\u5341\u53e3\u94f6\u9488\u7a0d\u5feb\u4e00\u6b65\uff0c\u987b\u77e5\u4ed6\u867d\u7136\u751f\u6027\u98de\u626c\u8df3\u8131\uff0c\u7075\u5de7\u673a\u53d8\uff0c\u5374\u662f \u81f3\u60c5\u81f3\u6027\u4e4b\u4eba\uff0c\u6b64\u523b\u4f46\u6c42\u6551\u5f97\u88f4\u73cf\u6027\u547d\uff0c\u5374\u5df2\u5c06\u81ea\u8eab\u7684\u751f\u6b7b\uff0c\u7f6e\u4e4b\u5ea6\u5916\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u611f\u89c9\u4e4b\u4e2d\uff0c\u90a3\u4e9b\u94f6\u9488\u4e0d\u4f46\u6ca1\u6709\u51fb\u5728\u88f4\u73cf\u8eab\u4e0a\uff0c\u5374\u4e5f\u5e76\u672a\u51fb\u5728\u81ea\u5df1\u8eab\u4e0a\u3001\u5fc3\u4e2d\u65b9\u81ea \u4e00\u6123\uff0c\u8033\u7554\u4f46\u542c\u5f97\u201c\u795e\u624b\u201d\u6218\u98de\u4e0e\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u9f50\u58f0\u60ca\u547c\u9053\uff1a\u201c\u4e07\u6d41\u5f52\u5b97\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u53c8\u662f\u4e00\u6115\uff0c\u5fae\u4e00\u626d\u8170\uff0c\u56de\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u4e0e\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5e76\u80a9 \u800c\u7acb\uff0c\u76ee\u77aa\u53e3\u5446\u5730\u671b\u7740\u90a3\u7edd\u7f8e\u5973\u5b50\uff0c\u9762\u4e0a\u6ee1\u5e03\u60ca\u8bb6\u4e4b\u8272\uff0c\u800c\u90a3\u7edd\u7f8e\u5973\u5b50\u5374\u5446\u5446\u5730\u7acb\u5728\u5e8a\u5934\uff0c \u76ee\u5149\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u88f4\u73cf\u8eab\u4e0a\uff0c\u9762\u4e0a\u7adf\u4e5f\u6ee1\u5e03\u60ca\u8bb6\u4e4b\u8272\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u53d8\u5316\uff0c\u5728\u5f53\u65f6\u786e\u662f\u6709\u5982\u5728\u540c\u4e00\u5239\u90a3\u95f4\u53d1\u751f\uff0c\u987b\u77e5\u8fd9\u4e9b\u6b66\u6797\u9ad8\u624b\u7684\u52a8\u4f5c\u53cd\u5e94\uff0c\u4ff1\u662f \u5feb\u5982\u95ea\u7535\uff0c\u7edd\u975e\u5e38\u4eba\u80fd\u591f\u60f3\u8c61\u7684\u3002 \u3000\u3000\u4f46\u6b64\u523b\u4e00\u5207\u52a8\u4f5c\u7adf\u7a81\u5730\u5168\u90e8\u51dd\u7ed3\u4f4f\u4e86\u3001\u5434\u9e23\u4e16\u3001\u6218\u98de\u3001\u90a3\u98de\u8679\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u5730\u7acb\u5728\u5f53 \u5730\uff0c\u5446\u5446\u5730\u671b\u7740\u90a3\u7edd\u7f8e\u5973\u5b50\uff0c\u800c\u90a3\u7edd\u7f8e\u5973\u5b50\u5374\u4e5f\u662f\u4e00\u52a8\u4e0d\u52a8\u5730\u7acb\u5728\u5f53\u5730\uff0c\u5374\u662f\u5728\u5446\u5446\u5730\u671b\u7740 \u5367\u5728\u5e8a\u4e0a\u7684\u88f4\u73cf\uff0c\u5f7c\u6b64\u5fc3\u4e2d\uff0c\u5404\u5404\u60ca\u8bb6\u4ea4\u96c6\uff0c\u53ea\u662f\u5f7c\u6b64\u5fc3\u4e2d\u60ca\u5f02\u7684\u539f\u56e0\u4e0d\u540c\u800c\u5df2\u3002 \u3000\u3000\u5434\u9e23\u4e16\u3001\u6218\u98de\u3001\u90a3\u98de\u8679\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u8f7b\u5531\u4e00\u58f0\uff0c\u9f50\u5730\u8de8\u524d\u4e00\u6b65\uff0c\u9053\uff1a \u201c\u9601\u4e0b\u53ef\u662f\u51b7\u6708\u4ed9\u5b50\uff1f\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u7edd\u7f8e\u5973\u5b50\u5374\u4e5f\u8f7b\u5531\u4e00\u58f0\uff0c\u4f4e\u8bed\u9053\u3002 \u3000\u3000\u201c\u771f\u7684\u662f\u4f60\uff1f\u4f60\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d\u5434\u9e23\u4e16\u3001\u6218\u98de\u3001\u90a3\u98de\u8679\u4e0d\u7981\u53c8\u9f50\u5730\u4e00\u6123\uff0c\u5374\u89c1\u8fd9\u7edd\u7f8e \u5973\u5b50\u7f13\u7f13\u8f6c\u8fc7\u5934\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u53d7\u7684\u662f\u4ec0\u4e48\u4f24\uff1f\u600e\u4e48\u53d7\u7684\u4f24\uff1f\u4ed6\u662f\u4f60\u4eec\u7684\u4ec0\u4e48\u4eba\uff1f\u4f60\u4e3a \u4ec0\u4e48\u8981\u62fc\u6b7b\u6551\u4ed6\uff1f\u201d\u5979\u8bf4\u5934\u4e24\u53e5\u8bdd\u65f6\u76ee\u5149\u671b\u7740\u6218\u98de\u3001\u90a3\u98de\u8679\u4e24\u4eba\uff0c\u8bed\u6c14\u51b0\u51b7\uff0c\u540e\u4e24\u53e5\u8bdd\u5374\u8bf4 \u5f97\u6e29\u548c\u65e0\u6bd4\uff0c\u76ee\u5149\u4e5f\u5df2\u8f6c\u5230\u5434\u9e23\u4e16\u8eab\u4e0a\u3002 \u3000\u3000\u5434\u9e23\u4e16\u5b9a\u6674\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u8eab\u6000\u6b66\u6797\u4e4b\u4e2d\u65e0\u4e0a\u5185\u529f\u5fc3\u6cd5\u3002\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u3001\u201c\u6444\u91d1\u5438\u94c1\u201d\u7684 \u7edd\u7f8e\u5973\u5b50\u76ee\u5149\u4e4b\u4e2d\uff0c\u6b64\u523b\u7adf\u662f\u6ee1\u542b\u5173\u5207\u4e4b\u610f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u5947\uff01\u6697\u4e2d\u5fd6\u9053\uff1a\u201c\u6211\u8fd9\u88f4\u73cf\u5144\u867d\u7136 \u751f\u6027\u667a\u6167\uff0c\u90fd\u5927\u5f02\u5e38\u4eba\uff0c\u4f46\u5374\u662f\u4e2a\u5e7c\u906d\u5b64\u96f6\u7684\u5c11\u5e74\uff0c\u6b66\u529f\u53c8\u6781\u5e73\u5e38\uff0c\u5374\u53c8\u600e\u4f1a\u548c\u8fd9\u540d\u6ee1\u5929\u4e0b \u7684\u6b66\u6797\u5f02\u4eba\u51b7\u6708\u4ed9\u5b50\u6709\u7740\u5173\u7cfb\u3002\u201d\u987b\u77e5\u88f4\u73cf\u4ee5\u7b14\u4ee3\u53e3\uff0c\u5411\u4ed6\u81ea\u53d9\u8eab\u4e16\u4e4b\u65f6\uff0c\u4e95\u672a\u5c06\u81ea\u5df1\u548c\u51b7 \u6708\u4ed9\u5b50\u827e\u9752\u95f4\u7684\u4e00\u6bb5\u906d\u9047\u8bf4\u51fa\u6765\u2014\u2014\u4ed6\u53c8\u600e\u80fd\u8bf4\u51fa\u6765\u5462\uff1f \u3000\u3000\u662f\u4ee5\u5434\u9e23\u4e16\u6b64\u523b\uff0c\u5fc3\u4e2d\u81ea\u662f\u5927\u4e3a\u5947\u602a\uff0c\u7adf\u6123\u6123\u5730\u5fd8\u8bb0\u7b54\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u4e00\u8f6c\uff0c\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u5411\u8fd9\u7edd\u7f8e\u5973\u5b50\u5f53\u5934\u4e00\u63d6\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4e0d \u77e5\u9053\u9601\u4e0b\u5c31\u662f\u827e\u4ed9\u5b50\uff0c\u5374\u4e5f\u4e0d\u77e5\u9053\u827e\u4ed9\u5b50\u7adf\u662f\u6211\u5144\u5f1f\u7684\u76df\u4e3b\u5927\u54e5\u88f4\u5927\u5148\u751f\u7684\u670b\u53cb\uff0c\u8fd9\u771f\u662f\u5927 \u6c34\u51b2\u4e86\u9f99\u738b\u5e99\u2026\u2026\u54c8\u54c8\uff0c\u771f\u662f\u8be5\u6b7b\uff0c\u771f\u662f\u8be5\u6b7b\u3002\u201d\u90a3\u7edd\u7f8e\u5973\u5b50\u7a81\u5730\u4e00\u6115\uff0c\u4f4e\u8bed\u9053\u3002 \u3000\u3000\u201c\u76df\u4e3b\u5927\u54e5\u2026\u2026\u88f4\u5927\u5148\u751f\u2026\u2026\u201d\u76ee\u5149\u60ca\u5f02\u5730\u5728\u6218\u98de\u7b49\u4e09\u4eba\u9762\u4e0a\u4e00\u626b\uff0c\u7f13\u7f13\u8f6c\u56de\u5934\u53bb\uff0c\u671b \u7740\u88f4\u73cf\uff0c\u4ea6\u81ea\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8fd9\u7edd\u7f8e\u5973\u5b50\u6b63\u662f\u8349\u83bd\u6b66\u6797\u4e4b\u4e2d\uff0c\u552f\u4e00\u80fd\u5f97\u5230\u90a3\u201c\u4e07\u6d41\u5f52\u5b97\u201d\u5fc3\u6cd5\u4f20\u6388\uff0c\u5341\u6570\u5e74\u6765\uff0c\u88ab\u6b66 \u6797\u4e2d\u4eba\u79f0\u7fa1\u4e0d\u7edd\u7684\u795e\u4ed9\u4fa0\u4fa3\u4e2d\u7684\u51b7\u6708\u4ed9\u5b50\u827e\u9752\u3002 \u3000\u3000\u90a3\u65e5\u5979\u7389\u638c\u8f7b\u6325\uff0c\u5341\u56db\u53e3\u201c\u5317\u6597\u4e03\u661f\u9488\u201d\u539f\u7269\u5949\u56de\uff0c\u5c06\u201c\u5317\u6597\u4e03\u715e\u201d\u4e2d\u7684\u4e09\u715e\u83ab\u897f\u51fb\u6bd9 \u4e4b\u540e\uff0c\u56de\u5230\u623f\u91cc\u7684\u5e8a\u4e0a\uff0c\u8fd8\u4ee5\u4e3a\u5e8a\u4e0a\u7761\u7740\u7684\u662f\u88f4\u73cf\uff0c\u662f\u4ee5\u5fc3\u4e2d\u6beb\u65e0\u534a\u70b9\u9632\u8303\u4e4b\u5fc3\uff0c\u54ea\u77e5\u5979\u8eab \u4fa7\u7684\u4eba\u8f7b\u8f7b\u4e00\u52a8\uff0c\u5979\u7adf\u53d1\u73b0\u90a3\u4e0d\u662f\u88f4\u73cf\uff0c\u800c\u662f\u5979\u8fd9\u6570\u5e74\u4e4b\u4e2d\uff0c\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u9003\u907f\u7740\u7684\u4e00\u4eba\uff0c \u53ea\u662f\u5979\u53d1\u89c9\u5df2\u665a\uff0c\u4fbf\u5728\u60ca\u9a87\u4e4b\u4e2d\uff0c\u88ab\u90a3\u4eba\u70b9\u4e2d\u7a74\u9053\uff0c\u5e26\u7740\u5979\u63a0\u51fa\u7a97\u53bb\u3002 \u3000\u3000\u90a3\u65f6\u5929\u8272\u5c1a\u6697\uff0c\u5979\u88ab\u90a3\u4eba\u62b1\u5728\u6000\u4e2d\uff0c\u8fde\u534a\u5206\u6323\u624e\u4e4b\u529b\u90fd\u6ca1\u6709\uff0c\u5fc3\u4e2d\u6025\u607c\u4ea4\u96c6\uff0c\u5374\u4e5f\u65e0\u53ef \u5948\u4f55\u3002 \u3000\u3000\u7b49\u5230\u90a3\u4eba\u89e3\u5f00\u5979\u7a74\u9053\u7684\u65f6\u5019\uff0c\u5929\u5df2\u7ecf\u5b8c\u5168\u4eae\u4e86\uff0c\u5979\u6b66\u529f\u4e0d\u53ca\u90a3\u4eba\uff0c\u5fc3\u667a\u66f4\u4e0d\u53ca\u90a3\u4eba\u5978 \u72e1\uff0c\u4f46\u90a3\u4eba\u767e\u5bc6\u4e00\u758f\uff0c\u5374\u53c8\u88ab\u5979\u4e58\u9699\u9003\u8d70\u2014\u2014\u8bd5\u60f3\u80fd\u4f7f\u51b7\u6708\u4ed9\u5b50\u7ec8\u65e5\u9003\u907f\uff0c\u8fde\u62b5\u6297\u90fd\u65e0\u6cd5\u62b5 \u6297\u4e4b\u4eba\uff0c\u53c8\u8be5\u662f\u4f55\u7b49\u89d2\u8272\uff0c\u8fd9\u5176\u4e2d\u53c8\u8be5\u5305\u542b\u7740\u4e00\u4e2a\u4f20\u5947\u590d\u6742\u7684\u6545\u4e8b\uff0c\u53ea\u662f\u8fd9\u6545\u4e8b\u51b7\u6708\u4ed9\u5b50\u81ea \u5df1\u82e5\u4e0d\u8bf4\u51fa\uff0c\u522b\u4eba\u4e5f\u65e0\u6cd5\u77e5\u9053\u800c\u5df2\u3002 \u3000\u3000\u51b7\u6708\u4ed9\u5b50\u827e\u9752\u867d\u7136\u6b66\u529f\u7edd\u4e16\uff0c\u5bf9\u6b64\u4eba\u5374\u662f\u4e0d\u4f46\u538c\u6076\uff0c\u800c\u4e14\u754f\u60e7\uff0c\u9003\u8d70\u4e4b\u540e\uff0c\u663c\u4f0f\u591c\u51fa\uff0c \u751f\u6015\u81ea\u5df1\u53c8\u843d\u5230\u90a3\u4eba\u624b\u4e0a\uff0c\u8fd9\u6570\u6708\u4ee5\u6765\uff0c\u5979\u98df\u4e0d\u77e5\u5473\uff0c\u5bdd\u4e0d\u5b89\u5e2d\uff0c\u65f6\u65f6\u5e7d\u6028\u5730\u6697\u95ee\u81ea\u5df1\uff1a \u201c\u6211\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u4e0d\u6015\u4ed6\u7684\u7ea0\u7f20\u5462\uff1f\u201d\u53ea\u662f\u8fd9\u95ee\u9898\u5979\u5374\u8fde\u81ea\u5df1\u4e5f\u65e0\u6cd5\u7b54\u590d\uff0c\u53ea\u662f\u6697\u6697\u7977\u544a\u82cd \u5929\uff0c\u8ba9\u90a3\u4eba\u5feb\u4e9b\u6b7b\u53bb\u3002 \u3000\u3000\u9664\u4e86\u9003\u4ea1\u4e4b\u5916\uff0c\u5979\u8fd8\u60f3\u627e\u5230\u88f4\u73cf\uff0c\u90a3\u5374\u5e76\u4e0d\u662f\u5b8c\u5168\u4e3a\u4e86\u90a3\u4e24\u672c\u4eca\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u5782\u6d8e\u4e0d\u5df2 \u7684\u6b66\u529f\u79d8\u7b08\uff0c\u800c\u662f\u5979\u5bf9\u8fd9\u751f\u5177\u5929\u6027\u7684\u5b69\u5b50\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u7adf\u7136\u6709\u4e9b\u6000\u5ff5\uff0c\u53ea\u662f\u4eba\u6d77\u832b\u832b\uff0c\u5979\u53c8 \u600e\u80fd\u627e\u5230\u90a3\u50cf\u662f\u4e00\u7247\u6d6e\u840d\u822c\u5728\u4eba\u6d77\u4e2d\u98d8\u6cca\u7684\u88f4\u73cf\u5462\uff1f \u3000\u3000\u8fd9\u65e5\u5979\u6df1\u591c\u4e4b\u4e2d\uff0c\u5b64\u8eab\u800c\u7acb\uff0c\u770b\u5230\u524d\u9762\u7684\u4e00\u95f4\u623f\u5b50\uff0c\u5728\u591c\u5df2\u5982\u6b64\u6df1\u7684\u65f6\u5019\uff0c\u8fd8\u6709\u706f\u5149\uff0c \u5979\u5fc3\u4e2d\u6709\u4e9b\u5947\u602a\uff0c\u7eb5\u8eab\u63a0\u4e86\u8fc7\u53bb\uff0c\u4f46\u5fc3\u5ff5\u8f6c\u5904\uff0c\u4e0d\u7981\u6697\u9a82\u81ea\u5df1\uff1a\u201c\u827e\u9752\u5440\u827e\u9752\uff0c\u4f60\u6b64\u523b\u5df2\u843d \u5230\u5982\u6b64\u4e0b\u573a\uff0c\u600e\u5730\u8fd8\u60f3\u591a\u7ba1\u4eba\u5bb6\u7684\u95f2\u4e8b\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u5979\u4fbf\u500f\u7136\u987f\u4f4f\u8eab\u5f62\uff0c\u8f6c\u8eab\u6b32\u53bb\uff0c\u54ea\u77e5\u76ee\u5149\u52a8\u5904\uff0c\u5374\u7a81\u5730\u671b\u5230\u8fd9\u95ee\u8305\u820d\u7684\u67f3\u6728 \u95e8\u677f\u4e4b\u4e0a\uff0c\u7adf\u753b\u7740\u4e00\u4e2a\u767d\u7c89\u56fe\u8bb0\uff0c\u661f\u6708\u4e4b\u5149\uff0c\u659c\u659c\u5730\u7167\u5728\u8fd9\u95e8\u677f\u4e0a\uff0c\u5979\u4fbf\u6e05\u6e05\u695a\u695a\u5730\u671b\u89c1\u8fd9 \u56fe\u8bb0\u7adf\u662f\u4e00\u4e2a\u4e03\u89d2\u4e4b\u661f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u52a8\uff1a\u201c\u539f\u6765\u662f\u2018\u5317\u6597\u4e03\u715e\u2019\u5728\u8fd9\u91cc\u3002\u201d\u8f6c\u5ff5\u53c8\u5fd6\u9053\uff1a \u201c\u82e5\u4e0d\u662f\u90a3\u4e09\u715e\u83ab\u897f\uff0c\u6211\u600e\u4f1a\u843d\u5230\u90a3\u8be5\u6b7b\u7684\u4eba\u7684\u624b\u4e0a\u3002\u201d\u6697\u54ac\u94f6\u7259\uff0c\u7eb5\u8eab\u800c\u5165\u3002\u53ea\u662f\u5979\u5374\u518d \u4e5f\u60f3\u4e0d\u5230\u5979\u65e0\u5904\u53ef\u5bfb\u7684\u88f4\u73cf\u4e5f\u5728\u8fd9\u8305\u820d\u91cc\u9762\uff0c\u66f4\u60f3\u4e0d\u5230\u88f4\u73cf\u7adf\u4f1a\u53d8\u6210\u201c\u76df\u4e3b\u5927\u54e5\uff0c\u88f4\u5927\u5148 \u751f\u201d\u3002 \u3000\u3000\u6b64\u523b\u5979\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u60ca\u7591\u4ea4\u96c6\uff0c\u6115\u6115\u5730\u7ad9\u5728\u5e8a\u524d\uff0c\u7ade\u5c06\u90a3\u83ab\u6c0f\u5144\u5f1f\u90fd\u5fd8\u8bb0\u4e86\uff0c\u7f13\u7f13\u4fef\u4e0b\u8eab \u53bb\uff0c\u5728\u88f4\u73cf\u7684\u4f24\u5904\u4ed4\u7ec6\u671b\u4e86\u51e0\u773c\uff0c\u8f7b\u8f7b\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u4f24\u5f97\u600e\u4e48\u8fd9\u6837\u91cd\uff0c\u53ea\u6015\u8fde\u9aa8\u5934\u90fd\u788e \u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u4e00\u7b11\uff0c\u53cd\u624b\u53d6\u4e0b\u63d2\u5728\u80cc\u540e\u7684\u6298\u6247\uff0c\u5237\u5730\u5c55\u4e86\u5f00\u6765\uff0c\u8f7b\u8f7b\u6447\u4e86\u4e24\u6447\uff0c\u4e00 \u9762\u7b11\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u4f24\u52bf\u867d\u4e0d\u8f7b\uff0c\u6240\u5e78\u53ea\u662f\u5916\u4f24\u800c\u5df2\uff0c\u5728\u4e0b\u867d\u4e0d\u624d\uff0c\u5bf9\u6cbb\u8fd9\u7b4b\u9aa8\u4e4b\u4f24\uff0c\u8fd8\u6709 \u4e09\u5206\u628a\u63e1\uff0c\u827e\u4ed9\u5b50\u53ea\u7ba1\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u51b7\u6708\u4ed9\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u65b9\u7eaf\u767d\u624b\u5e15\uff0c\u8f7b\u8f7b\u62b9\u4e86\u62b9\u88f4\u73cf\u989d\u4e0a\u7684\u6c57\u73e0\uff0c\u4e00\u9762\u6447\u9996 \u5fae\u5531\u9053\uff1a\u201c\u4e16\u4e8b\u53d8\u5316\uff0c\u771f\u4e0d\u662f\u4eba\u4eec\u53ef\u4ee5\u9884\u6599\u5f97\u5230\u7684\uff0c\u6211\u521d\u6b21\u89c1\u5230\u4ed6\u65f6\uff0c\u4ed6\u8fd8\u662f\u4e2a\u5230\u5904\u53d7\u4eba\u6b3a \u51cc\u7684\u5c11\u5e74\uff0c\u60f3\u4e0d\u5230\u4ec5\u4ec5\u51e0\u4e2a\u6708\u7684\u65e5\u5b50\uff0c\u4ed6\u7adf\u53d8\u6210\u4e86\u4f60\u4eec\u8fd9\u4e9b\u6210\u540d\u4eba\u7269\u7684\u76df\u4e3b\u5927\u54e5\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u81ea\u8f6c\u9996\u5411\u5434\u9e23\u4e16\u542b\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u4e0d\u53ef\u4ee5\u544a\u8bc9\u6211\uff0c\u8fd9\u77ed\u77ed\u51e0\u4e2a\u6708\u91cc\uff0c\u4ed6\u5230 \u5e95\u6709\u7740\u4ec0\u4e48\u5947\u9047\uff1f\u201d \u3000\u3000\u8fd9\u771f\u662f\u6559\u4eba\u5fc3\u91cc\u5947\u602a\u5f97\u5f88\uff0c\u8bed\u6c14\u8f7b\u67d4\uff0c\u7adf\u548c\u65b9\u624d\u7684\u51b0\u51b7\u8083\u6740\uff0c\u622a\u7136\u800c\u5f02\u3002 \u3000\u3000\u5434\u9e23\u4e16\u5fae\u4e00\u5b9a\u795e\uff0c\u6c89\u541f\u534a\u664c\uff0c\u65b9\u5f85\u7b54\u8bdd\uff0c\u54ea\u77e5\u76ee\u5149\u8f6c\u5904\uff0c\u95e8\u5916\u7a81\u5730\u4eba\u5f71\u4e00\u95ea\uff0c\u4ed6\u8bed\u672a\u51fa \u53e3\uff0c\u90a3\u51b7\u6708\u4ed9\u5b50\u5df2\u81ea\u51b7\u53f1\u4e00\u58f0\uff1a\u201c\u4f60\u4eec\u8fd8\u6ca1\u6709\u8d70\u5440\uff1f\u201d\u67f3\u8170\u8f7b\u6298\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u5434\u9e23\u4e16\u53ea\u89c9\u773c \u524d\u4e00\u82b1\uff0c\u4fbf\u5df2\u5931\u53bb\u5979\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u6447\u624b\u4e2d\u7684\u6298\u6247\uff0c\u7f13\u6b65\u8d70\u5230\u95e8\u53e3\uff0c\u95e8\u5916\u591c\u8272\u5c06\u5c3d\uff0c\u6668\u66e6\u5fae\u660e\uff0c\u4e00\u7247\u9c7c\u9752\u4e4b \u8272\u4e2d\uff0c\u4e09\u6761\u4eba\u5f71\uff0c\u7535\u5c04\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u51b7\u51b7\u4e00\u7b11\u3001\u56de\u8fc7\u5934\u6765\u3001\u9053\u3002 \u3000\u3000\u201c\u8fd9\u83ab\u6c0f\u5144\u5f1f\u4e24\u4eba\u5012\u771f\u662f\u6d3b\u5f97\u6709\u4e9b\u4e0d\u8010\u70e6\u4e86\uff0c\u660e\u660e\u5df2\u53ef\u9003\u8d70\uff0c\u597d\u751f\u751f\u7684\u5374\u8dd1\u56de\u6765\u4f5c\u751a\uff0c \u6b64\u756a\u843d\u4eba\u8fd9\u4f4d\u5973\u9b54\u5934\u624b\u4e2d\uff0c\u53ea\u6015\u2014\u2014\u54fc\u54fc\uff01\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u76b1\u7709\u53c8\u9053\uff1a\u201c\u5434\u5144\uff0c\u4f60\u548c\u88f4\u5927\u5148\u751f \u65e2\u5c5e\u77e5\u4ea4\uff0c\u53ef\u77e5\u9053\u4ed6\u7a76\u7adf\u662f\u4f55\u6765\u5386\uff0c\u600e\u7684\u548c\u8fd9\u4f4d\u5973\u9b54\u5934\u4ea6\u662f\u7d20\u8bc6\uff1f\u201d\u4e03\u5de7\u8ffd\u9b42\u51b7\u7136\u63a5\u53e3\u9053\uff1a \u201c\u8fd9\u53ea\u6015\u8fde\u5434\u5c11\u4fa0\u4e5f\u4e0d\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u8bed\u58f0\u65b9\u843d\uff0c\u95e8\u5916\u7a81\u53c9\u4eba\u5f71\u4e00\u82b1\uff0c\u4f17\u4eba\u4e00\u8d77\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u51b7\u6708\u4ed9\u5b50\u827e\u9752\u7adf\u53c8\u95ee\u7535\u822c\u63a0 \u4e86\u8fdb\u6765\uff0c\u9762\u4e0a\u7adf\u7136\u6ee1\u5e26\u60ca\u60f6\u4e4b\u8272\uff0c\u5a07\u8eaf\u4e00\u8f6c\uff0c\u6781\u5feb\u5730\u5173\u4e0a\u623f\u95e8\uff0c\u7a81\u53c8\u53cd\u624b\u4e00\u6325\uff0c\u98ce\u58f0\u4e00\u51db\uff0c \u684c\u4e0a\u7684\u6cb9\u706f\u4fbf\u5e94\u624b\u800c\u706d\u3002 \u3000\u3000\u4f17\u4eba\u773c\u524d\u4e00\u6697\uff0c\u5fc3\u4e2d\u4e00\u6123\uff0c\u53ea\u542c\u95e8\u5916\u4e00\u4e2a\u68ee\u51b7\u7684\u53e3\u97f3\uff0c\u5e26\u7740\u51b7\u524a\u7684\u7b11\u610f\u4e00\u5b57\u4e00\u5b57\u4ed6\u8bf4 \u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5427\uff0c\u53c8\u8ba9\u6211\u6276\u7740\u4f60\uff0c\u5176\u5b9e\u4f60\u53c8\u4f55\u82e6\u5982\u6b64\u82e6\u82e6\u9003\u907f\uff0c\u5c31\u7b97\u4f60\u9003\u5230\u5929\u6daf\u6d77\u89d2\uff0c\u96be\u9053 \u6211\u8fd8\u4f1a\u627e\u4e0d\u7740\u4f60\u3002\u201d\u8bed\u97f3\u8d77\u5904\uff0c\u4eff\u4f5b\u8fd8\u5728\u5f88\u8fdc\uff0c\u8bf4\u5230\u4e00\u534a\u65f6\uff0c\u4f17\u4eba\u53ea\u542c\u5f97\u623f\u95e8\u201c\u7830\u201d\u5730\u4e00 \u54cd\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u7a7f\u5ba4\u800c\u8fc7\uff0c\u53ef\u662f\u7b49\u5230\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\u7684\u65f6\u5019\uff0c\u5374\u53c8\u5df2\u53bb\u5f97\u5f88\u8fdc\u3002\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c \u623f\u4e2d\u9759\u5f97\u8fde\u547c\u5438\u4e4b\u58f0\uff0c\u90fd\u6e05\u695a\u53ef\u95fb\uff0c\u51b7\u6708\u4ed9\u5b50\u548c\u90a3\u7a7f\u5ba4\u800c\u8fc7\u7684\u4eba\u5f71\uff0c\u5374\u90fd\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\u4e86\u3002 \u3000\u3000\u4e1c\u65b9\u867d\u5df2\u6cdb\u51fa\u9c7c\u9752\uff0c\u4f46\u623f\u4e2d\u5374\u4ecd\u6697\u5f97\u5f88\uff0c\u4f17\u4eba\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u5404\u81ea\u5fc3\u4e2d\uff0c\u8fd8\u662f\u601d\u6f6e\u500f \u4e71\uff0c\u60ca\u7591\u4ea4\u96c6\uff0c\u4e0d\u77e5\u9053\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5e72\u54b3\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u90a3\u5144\uff0c\u4f60\u53ef\u5e26\u7740\u706b\u6298\u5b50\uff0c\u5509\uff0c\u8fd1\u5e74\u6765\u6211\u7684\u786e\u8001\u4e86\uff0c \u76ee\u529b\u5df2\u975e\u6614\u65e5\u53ef\u6bd4\uff0c\u4f60\u5e74\u7eaa\u5c1a\u8f7b\uff0c\u4f60\u53ef\u770b\u6e05\u540e\u6765\u90a3\u4eba\u7684\u8eab\u5f62\u5417\uff1f\u201d \u3000\u3000\u53ea\u542c\u5434\u9e23\u4e16\u957f\u957f\u4e00\u53f9\uff0c\u534a\u664c\u6ca1\u6709\u56de\u7b54\u81ea\u5df1\u7684\u8bdd\uff0c\u90a3\u4e03\u5de7\u8ffd\u9b42\u5374\u5df2\u8d70\u5230\u684c\u65c1\uff0c\u5c06\u684c\u4e0a\u7684\u6cb9 \u706f\u70b9\u8d77\u6765\u4e86\uff0c\u53ea\u662f\u6b64\u523b\u6668\u5149\u5df2\u73b0\uff0c\u6cb9\u706f\u867d\u7136\u70b9\u8d77\uff0c\u5374\u5df2\u8fdc\u4e0d\u5982\u591c\u6df1\u4e4b\u65f6\u7684\u660e\u4eae\u4e86\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u5434\u9e23\u4e16\u53ea\u89c9\u8eab\u4e0a\u5fae\u6709\u5bd2\u610f\uff0c\u8f6c\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u623f\u95e8\u6d1e\u5f00\uff0c\u4e24\u6247\u95e8\u677f\uff0c\u4e00\u5de6\u4e00 \u53f3\u5730\u5012\u5728\u5730\u4e0a\uff0c\u95e8\u73af\u4e4b\u4e0a\uff0c\u6574\u6574\u9f50\u9f50\u5730\u5370\u7740\u4e00\u4e2a\u638c\u5370\uff0c\u6df1\u9677\u5165\u6728\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u624d\u77e5\u9053\u65b9\u624d\u90a3 \u4eba\u7adf\u5c06\u8fd9\u539a\u8fbe\u4e09\u5bf8\u7684\u67f3\u6728\u95e8\u677f\uff0c\u51fb\u5f97\u5bf9\u7a7f\uff0c\u6b64\u523b\u7559\u5728\u95e8\u677f\u4e4b\u4e0a\u7684\uff0c\u7adf\u662f\u4e2a\u638c\u6d1e\u3002 \u3000\u3000\u65b9\u624d\u90a3\u4eba\u58f0\u5230\u4eba\u5230\uff0c\u663e\u89c1\u811a\u4e0b\u7edd\u672a\u505c\u6b65\uff0c\u4e61\u4e0b\u4eba\u95e8\u6237\u6700\u662f\u8c28\u614e\uff0c\u8fd9\u95e8\u677f\u81ea\u662f\u6781\u4e3a\u539a\u91cd\uff0c \u6b64\u4eba\u811a\u4e0b\u672a\u505c\uff0c\u968f\u624b\u6325\u51fa\u4e00\u638c\uff0c\u5374\u5df2\u5c06\u8fd9\u539a\u91cd\u7684\u95e8\u677f\u51fb\u7a7f\uff0c\u8fd9\u79cd\u529f\u529b\u4e0d\u4f46\u60ca\u4e16\u9a87\u4fd7\uff0c\u5c31\u8fde\u5434 \u9e23\u4e16\u8fd9\u79cd\u6b66\u6797\u9ad8\u624b\u89c1\u4e86\uff0c\u5fc3\u4e0b\u90fd\u4e3a\u4e4b\u9a87\u7136\u3002 \u3000\u3000\u76ee\u5149\u8f6c\u5904\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u4e0a\u4ea6\u662f\u6ee1\u5e03\u60ca\u9a87\u4e4b\u8272\uff0c\u76ee\u5149\u518d\u4e00\u8f6c\uff0c\u4e03\u5de7\u8ffd\u9b42\u90a3\u98de\u8679\u4e00\u624b\u62ff \u706b\u6298\u5b50\uff0c\u624b\u4e2d\u7684\u706b\u6298\u5b50\u5374\u5728\u5fae\u5fae\u98a4\u6296\u7740\uff0c\u4e09\u4eba\u53e3\u4e2d\u867d\u7136\u90fd\u672a\u8bf4\u8bdd\uff0c\u5fc3\u91cc\u5374\u4e0d\u7ea6\u800c\u540c\u4ed6\u8bf4\u9053\uff1a \u201c\u6b64\u4eba\u662f\u8c01\uff1f\u7adf\u6709\u5982\u6b64\u60ca\u4eba\u7684\u6b66\u529f\u3002\u201d\u5fc3\u4e0b\u5404\u81ea\u60ca\u60b8\u4e0d\u5df2\u3002 \u3000\u3000\u53ea\u542c\u8eab\u4fa7\u5e8a\u677f\u8f7b\u8f7b\u4e00\u54cd\uff0c\u4e09\u4eba\u7406\u667a\u4e00\u6e05\uff0c\u9f50\u5730\u8f6c\u8fc7\u8eab\u53bb\uff0c\u8d70\u5230\u5e8a\u524d\uff0c\u5374\u89c1\u90a3\u5df2\u6655\u8ff7\u4e86\u5c06 \u8fd1\u534a\u4e2a\u65f6\u8fb0\u7684\u88f4\u73cf\uff0c\u6b64\u523b\u7adf\u7f13\u7f13\u5f20\u5f00\u773c\u6765\u3002 \u3000\u3000\u5434\u9e23\u4e16\u5fc3\u4e2d\u4e00\u559c\uff0c\u8131\u53e3\u9053\uff1a\u201c\u4f60\u5df2\u9192\u4e86\u3002\u201d\u4e24\u4eba\u76f8\u987e\u4e00\u7b11\uff0c\u54ea\u77e5\u90a3\u65b9\u81ea\u82cf\u9192\u7684\u88f4\u73cf\uff0c\u5634 \u89d2\u4ea6\u81ea\u6cdb\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u5634\u89d2\u52a8\u4e86\u4e24\u52a8\uff0c\u661f\u672a\u8bf4\u51fa\u8bdd\u6765\uff0c\u4f46\u5634\u89d2\u7684\u7b11\u5bb9\uff0c\u5374\u6781\u4e3a\u5f00\u6717\u3002\u5434\u9e23\u4e16 \u5fc3\u4e2d\u5947\u602a\uff1a\u201c\u600e\u5730\u4ed6\u4eba\u4e00\u82cf\u9192\u5c31\u7b11\u4e86\u8d77\u6765\uff1f\u201d\u5fc3\u5ff5\u6570\u8f6c\uff0c\u5374\u4e5f\u731c\u4e0d\u51fa\u88f4\u73cf\u7b11\u7684\u662f\u4e3a\u7740\u4ec0\u4e48\u3002 \u3000\u3000\u88f4\u73cf\u6084\u7136\u95ed\u8d77\u773c\u6765\uff0c\u8033\u7554\u5140\u81ea\u6405\u7ed5\u7740\uff1a\u201c\u4ed6\u5df2\u9192\u4e86\u2026\u2026\u4ed6\u5df2\u9192\u4e86\u3002\u201d\u8fd9\u867d\u662f\u6781\u5176\u7b80\u5355\u7684 \u56db\u4e2a\u5b57\uff0c\u5374\u662f\u88f4\u73cf\u4e00\u751f\u4e4b\u4e2d\u6240\u542c\u5230\u7684\u6700\u6700\u5965\u5999\u7684\u97f3\u4e50\uff0c\u56e0\u4e3a\uff0c\u4ed6\u7ec8\u4e8e\u53c8\u80fd\u542c\u5230\u4e16\u4e0a\u7684\u58f0\u97f3 \u4e86\uff0c\u90a3\u4e48\u8fd9\u56db\u4e2a\u5b57\u5bf9\u4ed6\u800c\u8a00\uff0c\u53c8\u662f\u591a\u4e48\u7f8e\u5999\u5462\uff1f \u3000\u3000\u201c\u6211\u7ec8\u4e8e\u53c8\u80fd\u542c\u5230\u58f0\u97f3\u4e86\u3002\u201d\u4ed6\u72c2\u559c\u5730\u6697\u5fd6\u9053\uff0c\u5f53\u4ed6\u7741\u5f00\u773c\u775b\u7684\u4e00\u5239\u90a3\uff0c\u8fd9\u56db\u4e2a\u5b57\u4fbf\u50cf \u4ed9\u4e50\u4e00\u822c\uff0c\u7531\u9065\u8fdc\u7684\u7a7a\u9645\uff0c\u98d8\u5165\u4ed6\u8033\u91cc\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u8111\u6d77\u4e2d\u4ec0\u4e48\u4e5f\u4e0d\u60f3\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u613f\u60f3\uff0c\u53ea\u662f\u5728\u53cd\u590d\u9ed8\u541f\u7740\uff1a\u201c\u4ed6\u5df2\u9192\u4e86\u2026\u2026\u4ed6\u5df2\u9192 \u4e86\u3002\u201d\u5fc3\u7075\u4eff\u4f5b\u5df2\u751f\u53cc\u7ffc\uff0c\u76f4\u6b32\u632f\u7ffc\u98de\u53bb\uff0c\u8fd9\u56db\u5b57\u4e5f\u5728\u8111\u6d77\u4e2d\u65cb\u8f6c\u7740\uff0c\u8d8a\u8f6c\u8d8a\u5feb\uff0c\u7ec8\u4e8e\u53c8\u53d8 \u6210\u4e00\u58f0\u6df7\u6c8c\u3002 \u3000\u3000\u5434\u9e23\u4e16\u6447\u9996\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u2018\u4ed6\u53c8\u6655\u4e86\u8fc7\u53bb\uff0c\u5509\u2014\u2014\u5947\u602a\u7684\u662f\u2026\u2026\u201d\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u6447\u6298 \u6247\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4ed6\u65b9\u4e00\u82cf\u9192\uff0c\u600e\u5730\u5c31\u7b11\u4e86\u8d77\u6765\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u4ff1\u662f\u5fc3\u667a\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u662f\u4ee5\u5434\u9e23\u4e16\u8bdd\u672a\u8bf4\u5b8c\uff0c\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u4fbf\u5df2\u77e5\u9053\u4ed6\u6240\u8981 \u8bf4\u7684\u662f\u4ec0\u4e48\uff0c\u4f46\u8fd9\u4e24\u4eba\u867d\u7136\u4e2a\u4e2a\u5fc3\u667a\u7edd\u9876\uff0c\u5374\u8c01\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u65b9\u624d\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u7684\u5168\u529b\u4e00 \u51fb\uff0c\u867d\u5c06\u88f4\u73cf\u51fb\u6210\u91cd\u4f24\uff0c\u5374\u4e5f\u5c06\u4ed6\u88ab\u90a3\u9526\u8863\u8be1\u79d8\u6587\u58eb\u6240\u70b9\u7684\u72ec\u95e8\u804b\u54d1\u91cd\u7a74\u9707\u5f97\u89e3\u5f00\u591a\u534a\uff0c\u8fd9 \u79cd\u532a\u5937\u6240\u601d\u4e4b\u4e8b\uff0c\u7684\u786e\u662f\u673a\u7f18\u51d1\u5de7\uff0c\u800c\u4e14\u5de7\u5230\u6781\u5904\uff0c\u81ea\u4e0d\u662f\u6218\u3001\u5434\u4e24\u4eba\u80fd\u4ee5\u9884\u6599\u7684\u4e86\u3002 \u3000\u3000\u4e03\u5de7\u8ffd\u9b42\u90a3\u98de\u8679\u5374\u59cb\u7ec8\u5728\u5782\u9996\u60f3\u7740\u5fc3\u4e8b\uff0c\u6b64\u523b\u7a81\u5730\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6b64\u523b\u5929\u5c06\u5927\u4eae\uff0c\u6211\u7b49\u4f55 \u53bb\u4f55\u4ece\uff0c\u6218\u5144\u60f3\u5fc5\u65e9\u6709\u6253\u7b97\u5427\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u76ee\u5149\u4e00\u8f6c\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u65e0\u8bba\u4f55\u53bb\u4f55\u4ece\uff0c\u4e5f\u8be5\u5148\u5c06\u6211\u8fd9\u88f4\u5144\u7684\u4f24\u52bf\u533b\u597d\u624d\u662f\uff01\u201d\u4ed6 \u8bdd\u58f0\u5fae\u987f\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u6b64\u523b\u88f4\u5144\u5df2\u662f\u6c5f\u5357\u7eff\u6797\u7684\u603b\u74e2\u628a\u5b50\uff0c\u4f24\u4e0d\u6cbb\u597d\uff0c\u4e8e\u6218\u3001\u90a3\u4e8c \u5144\u7684\u989c\u9762\uff0c\u4ea6\u5927\u6709\u59a8\u788d\u5427\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u8f69\u7709\u4e00\u7b11\uff0c\u624b\u4e2d\u9759\u6b62\u8bb8\u4e45\u7684\u6298\u6247\uff0c\u53c8\u5f00\u59cb\u6447\u4e86\u8d77\u6765\u4e00\u9762\u7b11\u9053\uff1a\u201c\u6781\u662f\uff0c\u6781 \u662f\uff0c\u65e0\u8bba\u6211\u7b49\u4f55\u53bb\u4f55\u4ece\uff0c\u88f4\u5927\u5148\u751f\u7684\u4f24\u52bf\uff0c\u662f\u8be5\u5148\u6cbb\u597d\u7684\uff0c\u53ea\u662f\u2026\u2026\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u5237\u5730\u6536\u8d77 \u624b\u4e2d\u6298\u6247\uff0c\u5411\u4e0b\u4e00\u6307\uff0c\u63a5\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u4f24\u52bf\u975e\u8f7b\uff0c\u6b64\u95ee\u4ea6\u975e\u7597\u4f24\u4e4b\u5730\uff0c\u5434\u5144\u5927\u53ef\u653e\u5fc3\uff0c\u88f4 \u5927\u5148\u751f\u7684\u4f24\u52bf\uff0c\u53ea\u7ba1\u5305\u5728\u5c0f\u5f1f\u8eab\u4e0a\uff0c\u54c8\u54c8\uff0c\u6218\u67d0\u867d\u7136\u4e0d\u624d\uff0c\u5374\u4e5f\u4e0d\u4f1a\u8ba9\u6211\u7b49\u4f17\u5bb6\u5144\u5f1f\u7684\u76df\u4e3b \u5927\u54e5\u4e45\u4e45\u8d1f\u4f24\u7684\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u5251\u7709\u4e00\u8f69\uff0c\u4ea6\u81ea\u7b11\u9053\uff1a\u201c\u2018\u795e\u624b\u2019\u6218\u98de\uff0c\u624b\u5999\u5982\u795e\uff0c\u5144\u53f0\u7eb5\u7136\u4e0d\u8bf4\uff0c\u5c0f\u5f1f\u4e5f\u653e\u5fc3 \u5f97\u5f88\uff0c\u6b64\u95f4\u65e2\u975e\u4e45\u7559\u4e4b\u5730\uff0c\u6211\u7b49\u4f55\u53bb\u4f55\u4ece\uff0c\u5c31\u5168\u51ed\u6218\u5144\u5429\u5490\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u77ac\u5373\u5c55\u989c\u4e00\u7b11\uff0c\u5411\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u9053\uff1a\u201c\u4f9d\u5728\u4e0b\u4e4b\u610f\u4e48\uff0c \u81ea\u662f\u5148\u5f97\u5c06\u88f4\u5927\u54e5\u9001\u5230\u4e00\u4e2a\u5b89\u9759\u6240\u5728\uff0c\u7597\u517b\u4f24\u52bf\uff0c\u4f60\u6211\u4e00\u9762\u4fbf\u5f97\u6492\u51fa\u8bf7\u67ec\uff0c\u904d\u9080\u6c5f\u5357\u6b66\u6797\u540c \u9053\uff0c\u8ba9\u5927\u5bb6\u53c2\u89c1\u6c5f\u5357\u7eff\u6797\u4e2d\u7684\u65b0\u8d77\u76df\u4e3b\uff0c\u90a3\u5144\u4e4b\u610f\uff0c\u53ef\u5426\u5982\u6b64\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u9762\u4e0a\u4ecd\u7136\u6728\u65e0\u8868\u60c5\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6218\u5144\u9ad8\u89c1\uff0c\u5c0f\u5f1f\u4e00\u5411\u662f\u62dc\u670d\u7684\uff0c\u82e5\u8bba\u8fd9 \u88f4\u2026\u2026\u88f4\u5927\u5148\u751f\u7684\u7597\u4f24\u4e4b\u5730\uff0c\u81ea\u7136\u5f97\u4ee5\u6218\u5144\u7684\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u6700\u4f73\uff0c\u6218\u5144\u5c31\u8fd1\u8bca\u6cbb\uff0c\u4e5f\u8981\u65b9\u4fbf \u4e9b\u3002\u81f3\u4e8e\u90a3\u904d\u9080\u6c5f\u5357\u540c\u9053\u4e00\u4e8b\u4e48\uff0c\u4e5f\u4e07\u4e07\u8fdf\u4e0d\u5f97\uff0c\u4f9d\u5c0f\u5f1f\u4e4b\u89c1\uff0c\u5c31\u5b9a\u5728\u4e94\u6708\u7aef\u9633\u5427\uff0c\u90a3\u65f6\u6625 \u65e5\u867d\u53bb\uff0c\u9177\u590f\u5374\u4ecd\u672a\u81f3\uff0c\u4e5f\u514d\u5f97\u5404\u8def\u82f1\u96c4\u5954\u6ce2\u4e8e\u70c8\u65e5\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u7b11\u9053\u3002 \u3000\u3000\u201c\u6781\u662f\uff0c\u6781\u662f\u3002\u4e94\u6708\u7aef\u9633\uff0c\u5c31\u662f\u4e94\u6708\u7aef\u9633\u6700\u4f73\uff01\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u5411\u5434\u9e23\u4e16\u5f53\u5934\u4e00\u63d6\uff0c \u9053\uff1a\u201c\u4e00\u65e5\u4ee5\u6765\uff0c\u6211\u7b49\u62dc\u53d7\u5434\u5144\u6559\u76ca\u826f\u591a\uff0c\u4e0d\u4f46\u6211\u6218\u67d0\u611f\u6fc0\u4e0d\u5c3d\uff0c\u6c5f\u5357\u9053\u4e0a\u7684\u4eba\u5bb6\u5144\u5f1f\u5f97 \u77e5\uff0c\u4e5f\u5b9a\u5fc5\u6df1\u611f\u5434\u5144\u9ad8\u4e49\u7684\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6218\u5144\u8a00\u91cd\u4e86\u3002\u201d\u5fc3\u4e2d\u5374\u5728\u6697\u4e2d\u601d\u5fd6\uff1a\u201c\u8fd9\u59d3\u6218\u7684\u6b64\u523b\u5fc5\u5b9a\u8981\u8d76 \u6211\u8d70\u4e86\u3002\u65e5\u540e\u4ed6\u63a7\u5236\u88f4\u5144\uff0c\u4e5f\u65b9\u4fbf\u4e9b\u3002\u563f\u563f\uff0c\u53ea\u662f\u4f60\u5982\u610f\u7b97\u76d8\u6253\u5f97\u867d\u5999\uff0c\u6211\u5374\u672a\u89c1\u8ba9\u4f60\u5982\u610f \u54e9\uff01\u201d \u3000\u3000\u53ea\u542c\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u679c\u7136\u542b\u7b11\u53c8\u9053\uff1a\u201c\u5434\u5144\u56db\u6d77\u6e38\u4fa0\uff0c\u95f2\u4e91\u91ce\u9e64\uff0c\u771f\u662f\u900d\u9065\u81ea\u5728\u5f97\u5f88\uff0c \u5c0f\u5f1f\u4e00\u4ecb\u4fd7\u4eba\uff0c\u9762\u5bf9\u5434\u5144\uff0c\u5b9e\u5728\u6c57\u989c\u5f97\u5f88\uff0c\u4f46\u671b\u65e5\u540e\u6709\u7f18\uff0c\u4e5f\u80fd\u6b65\u5434\u5144\u540e\u5c18\uff0c\u4f5c\u4e00\u5929\u6daf\u6e38 \u5ba2\uff0c\u5578\u505a\u5c71\u6c34\uff0c\u5c82\u4e0d\u5feb\u54c9\uff0c\u5c82\u4e0d\u5feb\u54c9\u2026\u2026\u201d \u3000\u3000\u4ed6\u5c55\u5f00\u6298\u6247\uff0c\u731b\u5730\u6247\u4e86\u4e24\u6247\uff0c\u4ef0\u5929\u957f\u7b11\u51e0\u58f0\uff0c\u63a5\u53e3\u53c8\u9053\u3002 \u3000\u3000\u201c\u81f3\u4e8e\u4eca\u65e5\u4e48\uff0c\u5c0f\u5f1f\u4e5f\u4e0d\u6562\u4ee5\u4e9b\u8bb8\u4fd7\u52a1\uff0c\u7f81\u7559\u5434\u5144\u5927\u9a7e\uff0c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u65e5\u540e\u518d \u89c1\uff0c\u5c0f\u5f1f\u5b9a\u8981\u548c\u5434\u5144\u591a\u76d8\u6853\u4e9b\u65f6\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u6697\u4e2d\u4e00\u7b11\uff0c\u9762\u4e0a\u5374\u4f5c\u51fa\u4e00\u672c\u6b63\u7ecf\u7684\u795e\u8272\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6218\u5144\u8c2c\u8bb8\uff0c\u771f\u6559\u5c0f\u5f1f\u65e0\u5730 \u81ea\u5bb9\u5f97\u5f88\uff0c\u5176\u5b9e\u5c0f\u5f1f\u4e0d\u4f46\u662f\u4e2a\u4fd7\u4eba\uff0c\u8fd8\u662f\u4e2a\u5927\u5927\u7684\u4fd7\u4eba\uff0c\u5e73\u751f\u6700\u559c\u4e4b\u4e8b\uff0c\u5c31\u662f\u770b\u770b\u70ed\u95f9\u3002\u4e0d \u7792\u6218\u5144\u8bf4\uff0c\u5c0f\u5f1f\u4e4b\u6240\u4ee5\u5230\u5904\u4e71\u8dd1\uff0c\u54ea\u91cc\u662f\u4e3a\u7740\u5578\u505a\u5c71\u6c34\uff0c\u5b9e\u5728\u5374\u662f\u4e3a\u4e86\u8981\u5230\u5904\u627e\u4e9b\u70ed\u95f9\u770b \u770b\uff0c\u6b64\u523b\u6211\u8fd9\u88f4\u5144\u8363\u4efb\u6c5f\u5357\u7eff\u6797\u7684\u603b\u74e2\u628a\u5b50\uff0c\u60f3\u90a3\u6c5f\u5357\u6b66\u6797\u8c6a\u6770\u5171\u805a\u4e00\u5802\uff0c\u540c\u8d3a\u76df\u4e3b\uff0c\u662f\u4f55 \u7b49\u98ce\u5149\u70ed\u95f9\u4e4b\u4e8b\uff0c\u83ab\u8bf4\u5c0f\u5f1f\u65e0\u4e8b\uff0c\u5c31\u7b97\u5c0f\u5f1f\u6709\u4e8b\uff0c\u4e5f\u4e07\u4e07\u4e0d\u80af\u9519\u8fc7\u7684\u3002\u6218\u5144\u82e5\u4e0d\u5acc\u5f03\uff0c\u5c0f\u5f1f \u4fbf\u671b\u80fd\u9644\u9aa5\u5c3e\uff0c\u5230\u90a3\u540d\u95fb\u5929\u4e0b\u6b66\u6797\u7684\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u53bb\u89c2\u5149\u2026\u2026\u201d\u4ed6\u8bdd\u58f0\u4e00\u987f\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u53c8 \u9053\uff1a\u201c\u5c31\u7b97\u6218\u5144\u5acc\u5f03\uff0c\u5c0f\u5f1f\u5374\u4e5f\u5c11\u4e0d\u5f97\u8981\u539a\u7740\u8138\u76ae\uff0c\u8ddf\u5728\u540e\u9762\u7684\u3002\u201d \u3000\u3000\u4ed6\u53e3\u82e5\u60ac\u6cb3\uff0c\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u53e3\u4e2d\u867d\u5728\u8bf4\u7740\u8bdd\uff0c\u773c\u6674\u5374\u59cb\u7ec8\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u8fd9\u795e\u624b\u6218\u98de\u8eab \u4e0a\uff0c\u53ea\u89c1\u4ed6\u9762\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u624b\u4e2d\u7684\u6298\u6247\uff0c\u6247\u4e2a\u4e0d\u505c\uff0c\u5c06\u9894\u4e0b\u7684\u4e00\u90e8\u6d53\u987b\uff0c\u90fd\u5439\u5f97\u4e1d\u4e1d \u98d8\u821e\u4e86\u8d77\u6765\uff0c\u55eb\u5685\u4e86\u534a\u664c\uff0c\u65b9\u81ea\u5f3a\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5434\u5144\u8fd9\u662f\u8bf4\u54ea\u91cc\u8bdd\u6765\uff0c\u540d\u6ee1\u4e2d\u539f\u7684\u4e03\u5de7\u7ae5 \u5b50\uff0c\u82e5\u662f\u5149\u4e34\u655d\u5e84\uff0c\u5c0f\u5f1f\u8fde\u6b22\u8fce\u90fd\u6765\u4e0d\u53ca\uff0c\u7109\u6709\u5acc\u5f03\u4e4b\u7406\uff01\u5434\u5144\u5982\u6b64\u8bf4\uff0c\u5c31\u662f\u89c1\u5916\u4e86\u3002\u201d\u8179 \u4e2d\u5374\u5728\u6697\u9a82\uff0c\u6068\u4e0d\u5f97\u5c06\u8fd9\u4e03\u5de7\u7ae5\u5b50\u4e00\u638c\u51fb\u5012\u5728\u9762\u524d\u3002 \u3000\u3000\u5434\u9e23\u4e16\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5982\u6b64\uff0c\u5c0f\u5f1f\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d\u4e24\u624b\u4e00\u80cc\uff0c\u7ad9\u5728\u5e8a\u524d\uff0c\u518d\u4e5f\u4e0d\u53d1 \u4e00\u8a00\uff0c\u5fc3\u4e2d\u5374\u53c8\u4e0d\u7981\u6697\u5fd6\uff1a\u201c\u8fd9\u2018\u795e\u624b\u2019\u6218\u98de\u5012\u771f\u662f\u4e2a\u4eba\u7269\uff0c\u4ed6\u5fc3\u4e2d\u867d\u7136\u5b9a\u5df2\u5c06\u6211\u6068\u5165\u5207 \u9aa8\uff0c\u56de\u4e0a\u5374\u4e00\u4e1d\u795e\u8272\u4e5f\u4e0d\u9732\u51fa\u6765\uff0c\u7684\u786e\u662f\u96be\u5f97\u5f97\u5f88\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u9762\u4e0a\u662f\u672c\u65e0\u8868\u60c5\uff0c\u751f\u50cf\u662f\u5728\u4ed6\u5fc3\u4e2d\u5168\u65e0\u559c\u3001\u6012\u3001\u54c0\u3001\u4e50 \u7b49\u4e03\u60c5\u516d\u6b32\u4e00\u822c\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6298\u6247\u4e00\u6447\uff0c\u53c8\u81ea\u5f3a\u7b11\u4e00\u58f0\uff0c\u62ac\u9996\u4e00\u671b\u7a97\u5916\uff0c\u9053\uff1a\u201c\u4e0e\u5434\u5144\u4e00\u5e2d\u5feb\u8c08\uff0c\u7adf\u4e0d \u77e5\u4e1c\u65b9\u4e4b\u65e2\u767d\uff0c\u54c8\u54c8\uff0c\u6b64\u523b\u5929\u7adf\u5df2\u5c06\u8fd1\u65e5\u51fa\u4e4b\u65f6\u4e86\u3002\u90a3\u5144\uff0c\u4f60\u6211\u662f\u5426\u4e5f\u8be5\u8d70\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u51b7\u7136\u5fae\u4e00\u9894\u9996\uff0c\u7f13\u6b65\u8d70\u5230\u7a97\u524d\uff0c\u4f38\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e00\u7269\uff0c\u987a\u624b\u4e00\u63b7\uff0c \u201c\u5427\u201d\u7684\u63b7\u5230\u5730\u4e0a\uff0c\u54ea\u77e5\u6b64\u7269\u4e00\u89e6\u5730\u9762\uff0c\u4fbf\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u7206\u51fa\u4e00\u6e9c\u706b\u82b1\uff0c\u7a81\u53c8\u51b2\u5929\u800c\u8d77\uff0c \u76f4\u5347\u5341\u4e08\uff0c\u5728\u7a7a\u4e2d\u53c8\u662f\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u7f15\u706b\u82b1\uff0c\u7adf\u7136\u6563\u6210\u4e03\u7f15\u9ed1\u70df\uff0c\u968f\u51e4\u8885\u8885\u800c\u8d77\uff0c\u4e45\u4e45 \u65b9\u81ea\u50b2\u6210\u4e00\u7247\u6de1\u70df\u3002 \u3000\u3000\u5434\u9e23\u4e16\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u602a\u4eba\u8a00\u6c5f\u5357\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\u4e4b\u2018\u4e03\u5de7\u2019\u5de7\u7edd\u5929\u4e0b\uff0c\u522b\u7684\u6211\u867d \u672a\u89c1\uff0c\u5c31\u5355\u53ea\u8fd9\u4fe1\u53f7\u70df\u82b1\u4e00\u7269\uff0c\u5236\u4f5c\u4e4b\u5999\uff0c\u5c31\u7edd\u975e\u5e38\u4eba\u6240\u80fd\u53ca\u7684\u4e86\u3002\u201d \u3000\u3000\u8f7b\u70df\u65b9\u6563\uff0c\u95e8\u5916\u7a81\u5730\u54cd\u8d77\u4e00\u9635\u6025\u907d\u7684\u9a6c\u8e44\u4e4b\u58f0\uff0c\u5230\u4e86\u95e8\u5916\u3002\u4fbf\u6e10\u6e10\u505c\u4f4f\uff0c\u6643\u773c\u4e4b\u95f4\uff0c\u95e8 \u5916\u5df2\u8d70\u5165\u4e00\u884c\u52b2\u88c5\u4f69\u5203\u7684\u7cbe\u608d\u6c49\u5b50\u6765\uff0c\u8170\u4e0b\u5404\u4f69\u7740\u4e00\u4e2a\u9769\u56ca\uff0c\u9ad8\u77ee\u867d\u4e0d\u4e00\uff0c\u6b65\u5c65\u4e4b\u95f4\uff0c\u5374\u4ff1 \u90fd\u77eb\u5065\u65e0\u6bd4\uff0c\u4e00\u5165\u95e8\u5185\uff0c\u4fbf\u9f50\u5730\u5411\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u8eac\u8eab\u884c\u793c\uff0c\u5782\u9996\u4fa7\u7acb\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u7adf\u7136\u606d\u8c28 \u5f02\u5e38\u3002 \u3000\u3000\u5434\u545c\u4e16\u4fa7\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u8fd9\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u9762\u4e0a\u867d\u4ecd\u4e00\u65e0\u8868\u60c5\uff0c\u4f46\u76ee\u5149\u4e4b\u4e2d\uff0c\u5374\u4e0d\u7981 \u6cdb\u51fa\u5f97\u610f\u7684\u795e\u91c7\u6765\uff0c\u663e\u89c1\u662f\u9887\u4ee5\u81ea\u5df1\u6709\u6b64\u90e8\u4e0b\u4e3a\u8363\u7684\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u9053\u90a3\u5e2e\u4e3b\u600e\u5730\u4f1a\u5b64\u8eab\u800c\u6765\uff0c\u5374\u539f\u6765\u8fd8\u5e26\u7740\u5982\u8bb8\u7cbe\u608d\u7684 \u5f1f\u5144\uff0c\u4fe1\u53f7\u4e00\u53d1\uff0c\u5f39\u6307\u4fbf\u81f3\uff0c\u54c8\u54c8\uff0c\u2018\u8ffd\u9b42\u98de\u6728\u4ee4\u2019\u540d\u503e\u6c5f\u5357\uff0c\u4ee4\u4e4b\u6240\u81f3\uff0c\u91d1\u77f3\u4e3a\u5f00\uff0c\u5374\u7684 \u786e\u4e0d\u662f\u5e78\u81f4\u54e9\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u9762\u8272\u4e00\u6c89\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u6015\u6211\u90a3\u2018\u4e03\u5de7\u4fe1\u9999\u2019\u4e00\u53d1\uff0c\u6218\u5144\u7684\u5f1f\u5144\u4eec\uff0c\u4e5f\u4f1a \u8d76\u6765\u54e9\uff01\u201d \u3000\u3000\u8a00\u72b9\u672a\u4e86\uff0c\u95e8\u5916\u679c\u7136\u53c8\u54cd\u8d77\u4e00\u9635\u6025\u8fd9\u7684\u9a6c\u8e44\u4e4b\u58f0\uff0c\u8fd9\u8e44\u58f0\u5230\u4e86\u95e8\u5916\uff0c\u7adf\u560e\u7136\u800c\u6b62\uff0c\u663e\u89c1 \u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u9a91\u672f\u66f4\u4e3a\u7cbe\u7edd\u3002 \u3000\u3000\u5434\u9e23\u4e16\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u540d\u5229\u4e8c\u5b57\uff0c\u751f\u50cf\u662f\u4e2a\u6781\u5927\u7684\u5708\u5957\uff0c\u53e4\u5f80\u4eca\u6765\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11 \u82f1\u96c4\u8c6a\u6770\u843d\u5165\u5708\u5957\u4e4b\u4e2d\uff0c\u8fd9\u2018\u795e\u624b\u2019\u6218\u98de\u4e0e\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\u4e24\u4eba\uff0c\u6325\u5203\u6b66\u6797\uff0c\u5feb\u610f\u6c5f\u6e56\uff0c\u94b1\u8d22 \u6765\u5f97\u751a\u6613\uff0c\u5bf9\u90a3\u2018\u5229\u2019\u5b57\u60f3\u5fc5\u4e0d\u4f1a\u770b\u5f97\u751a\u91cd\uff0c\u4f46\u5374\u8fd8\u662f\u514d\u4e0d\u4e86\u4e3a\u2018\u540d\u2019\u6240\u7d2f\uff0c\u7247\u523b\u4e4b\u524d\uff0c\u8fd9 \u4e24\u4eba\u8fd8\u662f\u540c\u5fc3\u5bf9\u4ed8\u4e8e\u6211\uff0c\u6b64\u523b\u5374\u5df2\u4e92\u76f8\u8ba5\u5632\u8d77\u6765\u3002\u8fd9\u4e24\u4eba\u624d\u5177\u4ff1\u90fd\u4e0d\u51e1\uff0c\u82e5\u771f\u662f\u540c\u5fc3\u534f\u529b\uff0c \u529b\u91cf\u5fc5\u5b9a\u4e0d\u5c0f\uff0c\u53ea\u662f\u4ed6\u4e8c\u4eba\u82e5\u662f\u5148\u5c31\u4e92\u76f8\u731c\u5fcc\uff0c\u563f\u563f\uff0c\u90a3\u5c31\u6210\u4e0d\u5f97\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u95e8\u5916\u5df2\u53c8\u8d70\u5165\u4e00\u884c\u52b2\u88c5\u4f69\u5203\u7684\u5f6a\u5f62\u5927\u6c49\u6765\uff0c\u8fd9\u4e9b\u6c49\u5b50\u4e0d\u4f46\u4e00\u8272\u9ed1\u8863\uff0c \u5c31\u8fde\u8eab\u8eaf\u7684\u9ad8\u77ee\uff0c\u7adf\u90fd\u5b8c\u5168\u4e00\u6837\uff0c\u751f\u50cf\u662f\u540c\u4e00\u6a21\u5b50\u4e2d\u94f8\u51fa\u4e00\u822c\u3002\u4e00\u5165\u95e8\u5185\uff0c\u7a81\u5730\u9f50\u58f0\u54c8\u559d\u4e00 \u58f0\uff0c\u201c\u6251\u201d\u5730\u8dea\u5230\u5730\u4e0a\uff0c\u52a8\u4f5c\u7adf\u4ea6\u6d51\u5982\u4e00\u4f53\uff0c\u8fd9\u5341\u4f59\u4e2a\u6c49\u5b50\u8dea\u4e0b\u7684\u65f6\u523b\uff0c\u7adf\u6ca1\u6709\u4e00\u4eba\u6709\u534a\u5206 \u53c2\u5dee\u7684\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u62a1\u987b\u4e00\u7b11\uff0c\u5fae\u4e00\u62ac\u624b\uff0c\u8fd9\u5341\u4f59\u5927\u6c49\u4fbf\u53c8\u5728\u540c\u4e00\u5239\u90a3\u91cc\u7ad9\u4e86\u8d77\u6765\uff0c\u663e\u89c1\u8fd9 \u201c\u795e\u624b\u201d\u6218\u98de\u7387\u4f17\u4e4b\u4e25\uff0c\u8fdc\u8fdc\u5728\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u4e4b\u4e0a\u3002 \u3000\u3000\u90a3\u98de\u8679\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96be\u602a\u6218\u5144\u540d\u6ee1\u5929\u4e0b\uff0c\u4e0d\u8bf4\u522b\u7684\uff0c\u5c31\u51ed\u624b\u4e0b\u7684\u8fd9\u4e9b\u5f1f\u5144\uff0c\u5df2\u8db3\u4ee5 \u505a\u89c6\u6b66\u6797\u4e86\u3002\u201d\u53e3\u4e2d\u867d\u5728\u8bf4\u8bdd\uff0c\u5374\u6545\u610f\u5c06\u76ee\u5149\u8fdc\u8fdc\u671b\u5728\u95e8\u5916\u3002 \u3000\u3000\u6218\u98de\u9762\u5bb9\u7a81\u5730\u4e00\u53d8\uff0c\u6ee1\u542b\u6028\u6bd2\u5730\u4e00\u8198\u90a3\u98de\u8679\uff0c\u4f46\u77ac\u5373\u54c8\u54c8\u7b11\u9053\u3002 \u3000\u3000\u201c\u662f\u6781\uff0c\u662f\u6781\u3002\u5c0f\u53ef\u4e4b\u6240\u4ee5\u80fd\u5728\u6c5f\u6e56\u6df7\u53e3\u996d\u5403\uff0c\u5168\u90fd\u662f\u4ed7\u7740\u8fd9\u4e9b\u5144\u5f1f\uff0c\u83bd\u83bd\u6b66\u6797\u4e4b\u4e2d\uff0c \u82e5\u8bba\u80fd\u4ee5\u771f\u5b9e\u529f\u592b\u505a\u89c6\u5929\u4e0b\u7684\uff0c\u9664\u4e86\u90a3\u5144\u4e4b\u5916\uff0c\u6050\u6015\u2014\u2014\u54c8\u54c8\u3002\u201d\u4ed6\u653e\u58f0\u4e00\u7b11\uff0c\u8bed\u58f0\u5fae\u987f\uff0c \u65b9\u81ea\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5c31\u518d\u65e0\u4ed6\u4eba\u4e86\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u62ac\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u6b64\u523b\u9762\u76ee\u4e4b\u4e0a\uff0c\u7531\u9752\u8f6c\u767d\uff0c\u7531\u767d\u8f6c\u7ea2\uff0c\u76ee \u5149\u66f4\u662f\u751f\u50cf\u8981\u55b7\u51fa\u706b\u6765\uff0c\u72e0\u72e0\u5730\u5728\u201c\u795e\u624b\u201d\u6218\u98de\u8eab\u4e0a\u77aa\u4e86\u4e24\u773c\uff0c\u7ec8\u4e8e\u4e00\u8a00\u4e0d\u53d1\u5730\u6389\u9996\u800c\u53bb\u3002 \u3000\u3000\u4e03\u5de7\u7ae5\u5b50\u5434\u9e23\u4e16\u4e0d\u7981\u4e3a\u4e4b\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u2018\u795e\u624b\uff0c\u6218\u98de\u4e0d\u4f46\u6b66\u529f\u8fdc\u80dc\u4e8e\u90a3\u2019\u4e03\u5de7 \u8ffd\u9b42\u2018\uff0c\u82e5\u8bba\u53e3\u89d2\u4e4b\u7280\u5229\uff0c\u5176\u4eba\u66f4\u5728\u90a3\u98de\u8679\u4e4b\u4e0a\uff0c\u90a3\u98de\u8679\u4e0e\u4ed6\u65e0\u8bba\u660e\u4e89\u6697\u6597\uff0c\u770b\u6765\u4ff1\u662f\u6ce8\u5b9a \u5403\u8e69\u7684\u4e86\u3002\u201d\u539f\u6765\u8fd9\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5728\u6b66\u6797\u4e2d\u58f0\u540d\u867d\u4e0d\u5f31\uff0c\u771f\u5b9e\u6b66\u529f\uff0c\u5374\u8fdc\u5728\u5176\u76db\u540d\u4e4b\u4e0b\uff0c\u4ed6 \u4e4b\u6240\u4ee5\u80fd\u5728\u6c5f\u6e56\u4e2d\u6210\u540d\u7acb\u4e07\uff0c\u5168\u662f\u4ed7\u7740\u4ed6\u8170\u7554\u9769\u56ca\u4e2d\u7684\u4e03\u4ef6\u6781\u5176\u9738\u9053\u7684\u5916\u95e8\u8ff7\u9b42\u6697\u5668\u800c\u5df2\uff0c \u201c\u795e\u624b\u201d\u6218\u98de\u8fd9\u822c\u6697\u4e2d\u8ba5\u8bbd\uff0c\u771f\u6bd4\u5f53\u9762\u9a7e\u4ed6\u8fd8\u8981\u96be\u582a\uff0c\u8fd9\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u7109\u6709\u542c\u4e0d\u51fa\u6765\u7684\u9053 \u7406\u3002\u201c\u795e\u624b\u201d\u6218\u98de\u4ef0\u9996\u5927\u7b11\u51e0\u58f0\uff0c\u76ee\u5149\u5374\u5168\u65e0\u7b11\u610f\uff0c\u51b7\u51b7\u5411\u90a3\u98de\u8679\u80cc\u5f71\u4e00\u779f\uff0c\u7b11\u58f0\u4fbf\u500f\u7136\u800c \u987f\uff0c\u8f6c\u8eab\u8d70\u5230\u88f4\u73cf\u5e8a\u524d\uff0c\u4fef\u9996\u6c89\u601d\u534a\u664c\uff0c\u7a81\u5730\u6c89\u58f0\u9053\uff1a\u201c\u51c6\u5907\u8f66\u9a6c\uff0c\u5373\u523b\u4e0a\u9053\u3002\u201d\u90a3\u4e9b\u9ed1\u8863 \u58ee\u6c49\u8f70\u7136\u7b54\u5e94\u4e00\u58f0\uff0c\u864e\u8170\u9f50\u65cb\uff0c\u6251\u51fa\u95e8\u5916\uff0c\u4ece\u80cc\u95e8\u8d1f\u624b\u800c\u7acb\u7684\u90a3\u98de\u8679\u8eab\u4fa7\u7ed5\u4e86\u8fc7\u53bb\u3002\u6625\u9633\u6656 \u6656\uff0c\u6625\u98ce\u4f9d\u4f9d\uff0c\u5434\u9e23\u4e16\u671b\u7740\u90a3\u98de\u8679\u5fae\u5fae\u98d8\u8d77\u7684\u8863\u8882\uff0c\u4f3c\u4e4e\u4e5f\u5728\u60f3\u7740\u4ec0\u4e48\u5fc3\u4e8b\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u8fd9\u6625\u65e5\u7684\u6e05\u6668\uff0c\u4fbf\u9661\u7136\u53d8\u5f97\u5bc2\u9759\u8d77\u6765\uff0c\u90a3\u4e9b\u8170\u4f69\u9769\u56ca\u7684\u527d\u608d\u6c49\u5b50\uff0c\u6c89\u9ed8\u5730\u4ea4\u6362\u7740 \u76ee\u5149\uff0c\u9021\u5de1\u7740\u9000\u51fa\u95e8\u5916\u2014\u2014\u76f4\u5230\u4e00\u9635\u6025\u907d\u7684\u8f66\u9a6c\u58f0\uff0c\u5212\u7834\u8fd9\u4f3c\u4e4e\u662f\u6c38\u65e0\u5c3d\u671f\u7684\u5bc2\u9759\u7684\u65f6\u5019\uff0c \u8fd9\u4e9b\u5404\u81ea\u60f3\u7740\u5fc3\u4e8b\u7684\u6b66\u6797\u8c6a\u5ba2\uff0c\u624d\u4ece\u6c89\u601d\u4e2d\u9192\u6765\u3002 \u3000\u3000\u53ea\u6709\u88f4\u73cf\uff0c\u6b64\u523b\u5374\u4ecd\u9677\u4e8e\u660f\u8ff7\u4e4b\u4e2d\uff0c\u4e00\u8fde\u4e32\u7684\u98a0\u6c9b\u56f0\u82e6\u7684\u65e5\u5b50\uff0c\u672c\u5df2\u4f7f\u5f97\u8fd9\u8eab\u4e16\u51c4\u82e6\u7684 \u5c11\u5e74\uff0c\u8eab\u4f53\u8106\u5f31\u5f97\u7981\u4e0d\u8d77\u4efb\u4f55\u91cd\u5927\u7684\u6253\u51fb\uff0c\u4f55\u51b5\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u90a3\u5f53\u80f8\u4e00\u51fb\uff0c\u672c\u662f\u5168\u529b\u800c \u4e3a\uff0c\u82e5\u4e0d\u662f\u4ed6\u53ca\u65f6\u5c06\u8eab\u8eaf\u8f6c\u4fa7\u4e00\u4e0b\uff0c\u53ea\u6015\u6b64\u523b\u65e9\u5df2\u9b42\u5f52\u79bb\u6068\u5929\u4e86\u3002 \u3000\u3000\u5347\u8d77\uff0c\u843d\u4e0b\uff0c\u8df3\u52a8\uff0c\u65cb\u8f6c\u4e00\u2014\u2014\u8fde\u4e32\u7d0a\u4e71\u3001\u660f\u8ff7\u3001\u6df7\u6c8c\uff0c\u800c\u65e0\u6cd5\u8fde\u7f00\u7684\u601d\u6f6e\u4e4b\u540e\uff0c\u88f4\u73cf \u7ec8\u4e8e\u53c8\u518d\u6b21\u5f20\u5f00\u773c\u6765\u3002 \u3000\u3000\u8033\u7554\u4f3c\u4e4e\u6709\u8f9a\u8f9a\u4e0d\u7edd\u7684\u8f66\u9a6c\u58f0\uff0c\u4ed6\u89c9\u5f97\u8fd9\u58f0\u97f3\u662f\u90a3\u4e48\u9065\u8fdc\uff0c\u5374\u53c8\u50cf\u662f\u90a3\u4e48\u8fd1\u3002\u5f20\u5f00\u773c\uff0c \u6709\u65cb\u8f6c\u7740\u7684\u82b1\u7eb9\uff0c\u7531\u8fd1\u800c\u8fdc\uff0c\u7531\u8fdc\u800c\u8fd1\uff0c\u7ec8\u4e8e\u51dd\u7ed3\u6210\u4e00\u70b9\u56fa\u5b9a\u7684\u5149\u5f71\uff0c\u51dd\u7ed3\u6210\u5434\u9e23\u4e16\u5173\u5207\u7684 \u9762\u5bb9\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5634\u89d2\u6cdb\u51fa\u4e00\u4e1d\u5b89\u6170\u7684\u7b11\u5bb9\uff0c\u5f53\u4ed6\u6700\u9700\u8981\u8bc1\u5b9e\u81ea\u5df1\u5e76\u975e\u5b64\u72ec\uff0c\u4e5f\u5e76\u672a\u88ab\u4eba\u9057\u5f03\u7684\u65f6 \u5019\uff0c\u80fd\u53d1\u73b0\u4e00\u5f20\u5c5e\u4e8e\u81ea\u5df1\u670b\u53cb\u7684\u9762\u5b54\uff0c\u8fd9\u5bf9\u4e00\u4e2a\u65b9\u4ece\u65e0\u52a9\u5730\u6655\u8ff7\u4e2d\u9192\u8fc7\u7684\u4eba\u8bf4\u6765\uff0c\u8be5\u662f\u4e00\u79cd \u591a\u5927\u7684\u5b89\u6170\u5440\u3002 \u3000\u3000\u4ed6\u867d\u7136\u89c9\u5f97\u773c\u76ae\u4ecd\u7136\u662f\u90a3\u4e48\u6c89\u91cd\uff0c\u4f46\u4ed6\u5374\u52aa\u529b\u5730\u4e0d\u8ba9\u81ea\u5df1\u6c89\u91cd\u7684\u773c\u76ae\u9616\u8d77\u6765\uff0c\u800c\u8ba9\u8fd9\u5f20 \u5173\u5207\u7684\u9762\u5bb9\uff0c\u5728\u81ea\u5df1\u773c\u4e2d\u9010\u6e10\u6e05\u6670\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u7adf\u4f3c\u4e4e\u53c8\u542c\u5230\u4e00\u4e2a\u58f0\u97f3\uff0c\u4ece\u6781\u9065\u8fdc\u7684\u5730\u65b9\u4f20\u6765\uff0c\u4ed6\u867d\u7136\u6ca1\u6709\u542c\u6e05\u8fd9\u58f0\u97f3\u662f\u5728\u8bf4 \u7684\u4ec0\u4e48\uff0c\u4f46\u4ed6\u7684\u5fc3\uff0c\u5374\u4e0d\u7981\u4e3a\u4e4b\u72c2\u559c\u5730\u8df3\u52a8\u4e86\u8d77\u6765\uff0c\u58f0\u97f3\uff01\u80fd\u591f\u542c\u5230\u58f0\u97f3\uff01\u8fd9\u5728\u4ed6\u5df2\u662f\u4e00\u79cd \u591a\u4e48\u751f\u758f\u7684\u611f\u89c9\u5440\uff01 \u3000\u3000\u5df2\u7ecf\u6709\u4e00\u6bb5\u60a0\u957f\u3001\u60a0\u957f\u5f97\u4eff\u4f5b\u65e0\u6cd5\u8bb0\u5fc6\u7684\u65e5\u5b50\uff0c\u4ed6\u8033\u4e2d\u65e0\u6cd5\u542c\u5230\u4efb\u4f55\u58f0\u97f3\uff0c\u98de\u626c\u3001\u9c9c \u660e\uff0c\u800c\u591a\u5f69\u7684\u751f\u547d\uff0c\u5728\u4ed6\u7684\u611f\u89c9\u4e2d\uff0c\u5374\u6709\u5982\u6b7b\u4e00\u822c\u9759\u5bc2\uff0c\u56e0\u4e3a\u4ed6\u4ec0\u4e48\u4e5f\u542c\u4e0d\u5230\uff0c\u4ec0\u4e48\u4e5f\u8bf4\u4e0d \u51fa\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u6b7b\u5bc2\u7684\u751f\u6d3b\uff0c\u5374\u53c8\u5f00\u59cb\u98de\u626c\u3001\u9c9c\u660e\uff0c\u800c\u591a\u5f69\u8d77\u6765\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u53c8\u80fd\u591f\u542c\u5230\u4e86\uff01 \u3000\u3000\u4e16\u4e0a\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u8a00\u8bed\uff0c\u80fd\u591f\u5f62\u5bb9\u4ed6\u6b64\u523b\u7684\u6b23\u559c\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u6587\u5b57\uff0c\u80fd\u591f\u63cf\u8ff0\u4ed6\u6b64 \u523b\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u8bc5\u5492\u8fc7\u751f\u547d\u7684\u6b8b\u9177\uff0c\u4e5f\u4ece\u672a\u57cb\u6028\u8fc7\u9020\u5316\u7684\u4e0d\u516c\u5e73\uff0c\u4f46\u4ed6\u6b64\u523b\uff0c\u5374\u5728\u6df1\u6df1\u5730\u611f\u6fc0 \u7740\uff0c\u4f46\u751a\u81f3\u5728\u611f\u6fc0\u7740\u53eb\u4ed6\u6781\u4e3a\u6b8b\u9177\u800c\u4e0d\u516c\u7684\u547d\u8fd0\u3002 \u3000\u3000\u5584\u826f\u7684\u4eba\u4eec\uff0c\u662f\u6c38\u8fdc\u4e0d\u4f1a\u8bc5\u5492\uff0c\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u57cb\u6028\u7684\uff0c\u4ed6\u4eec\u53ea\u77e5\u611f\u6fc0\uff0c\u56e0\u6b64\uff0c\u4ed6\u4eec\u7684\u751f \u547d\uff0c\u4e5f\u6c38\u8fdc\u6bd4\u522b\u4eba\u5feb\u4e50\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u8f86\u5954\u9a70\u5728\u6c5f\u5357\u9053\u4e0a\uff0c\u5bbd\u655e\u800c\u534e\u4e3d\u7684\u7bf7\u8f66\uff0c\u76d8\u7740\u817f\u5750\u5728\u4ed6\u8eab\u65c1\u7684\u5434\u9e23\u4e16\uff0c\u770b\u5230\u4ed6\u5634 \u89d2\u6cdb\u8d77\u7684\u7b11\u5bb9\uff0c\u4e0d\u7981\u4e3a\u4e4b\u72c2\u559c\u9053\uff1a\u201c\u4ed6\u9192\u8fc7\u6765\u4e86\uff01\u201d \u3000\u3000\u7b49\u5230\u4ed6\u770b\u5230\u5df2\u7ecf\u6655\u8ff7\u4e86\u8bb8\u591a\u65e5\u5b50\u7684\u88f4\u73cf\uff0c\u7adf\u7136\u7f13\u7f13\u7fd5\u52a8\u7740\u5634\u5507\uff0c\u5fae\u5f31\u4ed6\u8bf4\u9053\uff1a\u201c\u5434 \u5144\u2026\u2026\u6211\u9192\u8fc7\u6765\u4e86\u2026\u2026\u542c\u5230\u4f60\u8bf4\u7684\u8bdd\u4e86\u3002\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u867d\u7136\u5fae\u5f31\uff0c\u5374\u5df2\u4f7f\u5f97\u672c\u5df2\u72c2\u559c\u7740\u7684\u5434\u9e23\u4e16\u51e0\u4e4e\u4ece\u8f66\u57ab\u4e0a\u8df3\u4e86\u8d77\u6765\uff0c\u4ed6\u5446\u4e86\u4e00\u5446\uff0c \u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u773c\u4e2d\u6240\u89c1\uff0c\u8033\u4e2d\u6240\u542c\u7684\u4e8b\u662f\u771f\u5b9e\u7684\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u72c2\u559c\u5730\u5927\u558a\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u4ed6\u80fd\u591f\u8bf4\u8bdd\u4e86\uff0c\u4ed6\u80fd\u591f\u8bf4\u8bdd\u4e86\u3002\u201d\u4e3a\u670b\u53cb\u7684\u5e78\u8fd0\u800c\u72c2\u559c\uff0c\u548c\u4e3a\u670b\u53cb\u7684\u4e0d\u5e78\u800c\u60b2\u54c0\uff0c\u8fd9 \u4e24\u79cd\u60c5\u611f\u867d\u7136\u4e0d\u540c\uff0c\u4f46\u5374\u540c\u6837\u662f\u4e00\u4efd\u591a\u4e48\u7eaf\u771f\u800c\u4f1f\u5927\u7684\u60c5\u64cd\u5440\uff01\u96be\u602a\u6709\u4e9b\u667a\u6167\u7684\u54f2\u4eba\uff0c\u4f1a\u4e00 \u624b\u637b\u7740\u9894\u4e0b\u7684\u767d\u987b\uff0c\u4e00\u624b\u6cbd\u8d77\u534a\u676f\u9999\u51bd\u7684\u767d\u9152\uff0c\u9065\u671b\u7740\u5929\u8fb9\u7684\u81ea\u4e91\uff1a\u65e0\u9650\u611f\u6fc0\u4ed6\u8bf4\uff1a\u201c\u4e16\u95f4 \u9664\u4e86\u53cb\u8c0a\u4e4b\u5916\uff0c\u5c31\u518d\u6ca1\u6709\u4e00\u6735\u65e0\u523a\u7684\u73ab\u7470\u4e86\u3002\u201d \u3000\u3000\u8f66\u7a97\u5916\u63a2\u5165\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u5934\u6765\uff0c\u9510\u5229\u7684\u76ee\u5149\uff0c\u626b\u8fc7\u88f4\u73cf\u5634\u89d2\u7684\u7b11\u5bb9\uff0c\u4f3c\u4e4e\u6709\u4e9b\u60ca\u8be7\u4ed6 \u8bf4\u9053\uff1a\u201c\u4ed6\u80fd\u591f\u8bf4\u8bdd\u4e86\u5417\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u72c2\u559c\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u5446\u4e86\u4e00\u5446\uff0c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u2026\u2026 \u96be\u9053\u4ed6\u88ab\u4eba\u70b9\u4e2d\u7684\u7a74\u9053\uff0c\u7adf\u88ab\u5411\u4e00\u557c\u90a3\u4e00\u9707\u51fb\u5f00\uff1f\u201d\u4e8e\u662f\u8fd9\u51b7\u9177\u7684\u4eba\uff0c\u4e5f\u4e0d\u7981\u6697\u4e2d\u611f\u53f9\u7740\u547d \u8fd0\u7684\u5de7\u5999\u4e86\u3002 \u3000\u3000\u8f66\u9a6c\u5e26\u8d77\u4e00\u80a1\u9ec4\u5c18\uff0c\u4e8e\u662f\u4ed6\u4eec\u4fbf\u6d88\u5931\u5728\u81ea\u5df1\u626c\u8d77\u7684\u5c18\u6c99\u91cc\u3002 \u3000\u3000\u6c5f\u5357\u7684\u6625\u5929\uff0c\u6765\u5f97\u5f88\u65e9\uff0c\u53bb\u5f97\u5374\u5f88\u8fdf\u3002\u9752\u9752\u6cb3\u6c34\u7554\u7684\u5343\u7f15\u67f3\u4e1d\uff0c\u4ecd\u7136\u4e1d\u4e1d\u7fe0\u76f4\uff1b\u5462\u5583\u7740 \u7684\u71d5\u5b50\uff0c\u4e5f\u4ecd\u7136\u5728\u82cd\u78a7\u7684\u6f84\u7a7a\u4e0b\u98de\u6765\u98de\u53bb\u3002\u79e6\u6dee\u6cb3\u8fb9\u7684\u91d1\u7c89\u7b19\u6b4c\uff0c\u5f7b\u591c\u4e0d\u606f\uff1b\u4e4c\u8863\u5df7\u53e3\u7684\u9999 \u8f66\u5b9d\u9a6c\uff0c\u62c2\u6653\u672a\u5f52\uff1b\u9ad8\u6402\u6731\u680f\u65c1\u72ec\u81ea\u4f2b\u7acb\u7740\u7684\u5c11\u5987\uff0c\u66fc\u58f0\u541f\u5531\u7740\uff1a\u201c\u6253\u8d77\u9ec4\u83ba\u513f\uff0c\u83ab\u6559\u679d\u4e0a \u557c\u3002\u201d \u3000\u3000\u626c\u97ad\u5feb\u610f\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u6b64\u523b\u5374\u5728\u98ce\u5149\u7eee\u4e3d\u7684\u6c5f\u5357\u9053\u4e0a\uff0c\u4f20\u8bed\u7740\u4e00\u4ef6\u9707\u60ca\u6c5f\u5357\u6b66\u6797\u7684\u5927 \u4e8b\u3002 \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6218\u795e\u624b\uff0c\u5411\u91d1\u9e21\uff0c\u90a3\u98de\u8679\uff0c\u548c\u83ab\u6c0f\u5144\u5f1f\u8fd9\u51e0\u4f4d\u4e3b\u513f\uff0c\u5df2\u627e\u51fa\u4e00\u4f4d\u4eba\u6765\uff0c\u5f53 \u54b1\u4eec\u7684\u603b\u74e2\u628a\u5b50\uff0c\u563f\uff0c\u8fd9\u53ef\u662f\u6c5f\u5357\u6b66\u6797\u91cc\u51e0\u5341\u5e74\u6765\u4ece\u6765\u6ca1\u6709\u7684\u4e8b\u5440\uff01\u770b\u6837\u5b50\uff0c\u54b1\u4eec\u53c8\u5f97\u70ed\u95f9 \u70ed\u95f9\u4e86\u3002\u201d \u3000\u3000\u201c\u771f\u7684\uff1f\u5c31\u51ed\u2018\u795e\u624b\u2019\u6218\u98de\uff0c\u91d1\u9e21\u5411\u4e00\u557c\u8fd9\u4e9b\u89d2\u8272\uff0c\u8fd8\u4f1a\u670d\u6c14\u8c01\u5417\uff1f\u5582\uff01\u8001\u54e5\uff0c\u4f60\u77e5\u4e0d \u77e5\u9053\u8fd9\u4f4d\u8981\u5f53\u54b1\u4eec\u603b\u74e2\u628a\u5b50\u7684\u4eba\uff0c\u5230\u5e95\u662f\u600e\u4e48\u6837\u4e00\u4f4d\u4eba\u7269\u5440\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u5144\u5f1f\u6211\u4e5f\u4e0d\u5341\u5206\u6e05\u695a\uff0c\u53ea\u542c\u8bf4\u8fd9\u4f4d\u4e3b\u513f\u59d3\u88f4\uff0c\u5e74\u7eaa\u4e5f\u4e0d\u600e\u4e48\u5927\uff0c\u522b\u7684\u4e48\uff0c\u5144 \u5f1f\u6211\u53ef\u4e5f\u4e0d\u592a\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u201c\u59d3\u88f4\u7684\uff1f\u8fd9\u5012\u5947\u602a\u4e86\uff01\u6c5f\u752b\u6b66\u6797\u5730\u9762\u4e0a\u6210\u540d\u9732\u8138\u7684\uff0c\u5e76\u6ca1\u6709\u59d3\u88f4\u7684\u8fd9\u4e00\u53f7\u5440\uff1f\u8fd9\u5012\u662f \u8c01\u5462\uff1f\u2026\u2026\u636e\u5144\u5f1f\u6211\u77e5\u9053\u7684\uff0c\u522b\u8bf4\u6c5f\u5357\u4e86\uff0c\u5c31\u8fde\u4e24\u6cb3\uff0c\u53ef\u4e5f\u6ca1\u6709\u59d3\u88f4\u7684\u82f1\u96c4\u5440\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5012\u4e0d\u89c1\u5f97\uff0c\u4f60\u770b\u8fc7\u829c\u6e56\u57ce\u767d\u8001\u7237\u5b50\u8ba2\u4e0b\u7684\u6b66\u6797\u82f1\u96c4\u8c31\u6ca1\u6709\uff0c\u4e0a\u9762\u5199\u7684\u5c31\u6709\u4e24\u4f4d\u59d3\u88f4 \u7684\uff0c\u53eb\u505a\u4ec0\u4e48\u2018\u67aa\u5251\u65e0\u654c\u2019\uff0c\u4f7f\u4e00\u5bf9\u5f27\u5f62\u5251\u548c\u4e00\u67c4\u94a9\u9570\u67aa\uff0c\u6b66\u529f\u8bf4\u662f\u5168\u90fd\u662f\u786c\u628a\u5b50\u3002\u201d \u3000\u3000\u201c\u778e\uff0c\u8001\u54e5\uff0c\u4f60\u53ef\u5c31\u5dee\u4e86\uff0c\u767d\u8001\u7237\u5b50\u8ba2\u8fd9\u2018\u6b66\u6797\u82f1\u96c4\u8c31\u2019\uff0c\u8fd8\u662f\u4e8c\u5341\u591a\u5e74\u524d\u7684\u4e8b\u54a7\uff0c\u90a3 \u2018\u67aa\u5251\u65e0\u6545\u2019\u88f4\u6c0f\u5144\u5f1f\uff0c\u65e9\u5c31\u6b7b\u4e86\u5341\u51e0\u5e74\u5566\uff0c\u5c31\u662f\u5728\u5341\u591a\u5e74\u524d\uff0c\u9707\u52a8\u6b66\u6797\u7684\u8499\u9762\u4eba\u90a3\u8d9f\u5b50\u4e8b \u91cc\u9762\uff0c\u548c\u53e6\u5916\u597d\u51e0\u4f4d\u6210\u540d\u7acb\u4e07\u513f\u7684\u9556\u5934\uff0c\u4e00\u8d77\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u54e6\uff0c\u539f\u6765\u662f\u8fd9\u4e48\u6863\u5b50\u4e8b\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u4ed6\u4eec\u5144\u5f1f\u4e24\u4eba\u6ca1\u6709\u6b7b\uff0c\u4ed6\u4eec\u53ef\u662f\u4e24\u6cb3\u5730\u9762\u4e0a\u7684\u4eba\uff0c\u600e\u6837\u4e5f\u4e0d\u53ef\u80fd\u8dd1\u5230\u54b1\u4eec\u6c5f\u5357\u6765 \u5f53\u603b\u74e2\u628a\u5b50\u5440\uff1f\u201d \u3000\u3000\u201c\u54c8\uff0c\u8001\u54e5\uff0c\u60a8\u522b\u5fd8\u4e86\uff0c\u54b1\u4eec\u4e5f\u662f\u4ece\u4e24\u6cb3\u5730\u9762\u4e0a\u8fc7\u6765\u7684\u5440\uff1f\u8bf4\u4e0d\u5b9a\uff0c\u6709\u90a3\u4e48\u4e00\u5929\uff0c\u54b1\u4eec \u4e5f\u80fd\u5f53\u4e0a\u6c5f\u5357\u7684\u603b\u74e2\u628a\u5b50\u5462\u3002\u201d \u3000\u3000\u201c\u563f\uff0c\u4f60\u522b\u6328\u9a82\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u8bf4\u6b63\u7ecf\u7684\uff0c\u60a8\u8981\u77e5\u9063\u8fd9\u4f4d\u4e3b\u513f\u5230\u5e95\u662f\u600e\u6837\u7684\u4e00\u4f4d\u4eba\u7269\uff0c\u5230\u4e86\u4e94\u6708\u7aef\u9633\u90a3\u4e00\u5929\uff0c\u60a8\u5230\u6218 \u795e\u624b\u7684\u6d6a\u83bd\u5c71\u5e84\u53bb\u77a7\u77a7\u5c31\u884c\u4e86\uff0c\u542c\u8bf4\u8fd9\u6b21\u76db\u4f1a\uff0c\u628a\u6c5f\u5357\u5408\u5b57\u5f1f\u5144\u90fd\u8bf7\u904d\u4e86\uff0c\u4e3a\u7684\u5c31\u662f\u5bf9\u4ed8\u90a3 \u6761\u5b7d\u9f99\u2014\u2014\u201d\u201c\u5582\uff0c\u8001\u54e5\uff0c\u4f60\u8fd8\u662f\u4e0d\u8981\u8bf4\u957f\u9053\u77ed\u5427\uff0c\u8ba9\u4eba\u542c\u89c1\u4e86\uff0c\u54b1\u4eec\u53ef\u5c31\u5403\u4e0d\u4e86\uff0c\u5f97\u515c\u7740 \u8d70\u5566\u3002\u201d \u3000\u3000\u4e8e\u662f\u6c5f\u5357\u9053\u4e0a\uff0c\u5feb\u9a6c\u9a70\u9a8b\uff0c\u5251\u5f71\u97ad\u4e1d\uff0c\u4fa0\u8e2a\u9891\u73b0\uff0c\u4ff1\u90fd\u662f\u5230\u6d6a\u83bd\u5c71\u5e84\u53bb\u53c2\u52a0\u8fd9\u573a\u76db\u4f1a\uff0c \u62dc\u89c1\u8fd9\u4f4d\u795e\u79d8\u7684\u603b\u74e2\u628a\u5b50\u7684\u3002 \u3000\u3000\u9633\u5149\u751a\u70c8\uff0c\u884c\u4eba\u82e6\u70ed\uff0c\u9053\u65c1\u4e00\u682a\u5927\u6811\u7684\u7eff\u836b\u4e0b\uff0c\u6a2a\u653e\u7740\u4e00\u62c5\u65b0\u9c9c\u7684\u74dc\u679c\uff0c\u9e45\u9ec4\u5ae9\u7eff\uff0c\u6e05 \u9999\u88ad\u4eba\uff0c\u4e8e\u662f\u8fd9\u65b9\u5c0f\u5c0f\u7684\u7eff\u836b\uff0c\u5c31\u6210\u4e86\u6765\u5f80\u884c\u4eba\u7684\u7eff\u6d32\u4e86\u3002 \u3000\u3000\u4e09\u4e94\u5339\u978d\u8f94\u9c9c\u660e\u7684\u957f\u7a0b\u5065\u9a6c\uff0c\u5018\u4f6f\u5728\u8f83\u8fdc\u7684\u8349\u5730\u4e0a\uff0c\u5076\u7136\u5782\u4e0b\u5934\uff0c\u56bc\u4e00\u53e3\u6c5f\u5357\u7684\u9752\u8349\uff0c \u4e09\u4e94\u4e2a\u624b\u91cc\u6447\u7740\u9a6c\u8fde\u5761\u5927\u8349\u5e3d\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u7b95\u8e1e\u5728\u7eff\u836b\u4e0b\u7684\u74dc\u679c\u62c5\u65c1\uff0c\u4eab\u53d7\u7740\u65c5\u9014\u4e2d\u7684\u7247\u523b \u836b\u51c9\u3002 \u3000\u3000\u6b63\u5348\u65f6\u5206\uff0c\u8def\u4e0a\u7684\u884c\u4eba\uff0c\u90fd\u662f\u61d2\u6d0b\u6d0b\u5730\uff0c\u7a7a\u6c14\u4e2d\u98d8\u6563\u7740\u7684\u662f\u61d2\u6563\u95f2\u9038\u7684\u6c14\u6c1b\uff0c\u751a\u81f3\u8fde\u8fd9 \u513f\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u90fd\u534a\u95ed\u7740\u773c\u775b\uff0c\u8fde\u8eab\u65c1\u653e\u7740\u7684\uff0c\u90a3\u5e26\u7740\u91d1\u9ec4\u8272\u7684\u9999\u74dc\uff0c\u90fd\u61d2\u5f97\u518d\u4f38\u624b\u62ff\u8d77\u6765 \u5403\u4e00\u53e3\u3002 \u3000\u3000\u84e6\u5730\u2014\u2014 \u3000\u3000\u8def\u7684\u5c3d\u5934\u5904\uff0c\u4f20\u6765\u4e00\u9635\u5954\u9a70\u7684\u9a6c\u8e44\u58f0\uff0c\u9633\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u6570\u5339\u5065\u9a6c\uff0c\u7edd\u5c18\u800c\u6765\uff0c\u9a6c\u8e44\u98de \u5065\uff0c\u5954\u884c\u5982\u9f99\uff0c\u7adf\u7136\u4ff1\u662f\u6765\u81ea\u585e\u5916\u7684\u826f\u9a79\u3002 \u3000\u3000\u6811\u836b\u4e0b\u7684\u52b2\u88c5\u5927\u6c49\u7741\u5f00\u773c\u6765\uff0c\u4ea4\u6362\u4e86\u4e00\u4e2a\u6000\u7591\u7684\u773c\u8272\uff0c\u50cf\u662f\u5f7c\u6b64\u5728\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u7684\u95ee\u9898\uff0c\u970e\u773c\u95f4\u4fbf\u6709\u4e86\u7b54\u6848\uff0c\u8fd9\u51e0\u5339\u5065\u9a6c\u9a70\u5230\u5207\u8fd1\uff0c\u9a6c\u4e0a\u9a91\u58eb\u53e3\u4e2d\u9f50\u58f0\u201c\u7684\u5362\u201d \u547c\uff0c\u5065\u9a6c\u957f\u5636\u4e00\u58f0\uff0c\u560e\u7136\u6b62\u6b65\u3002 \u3000\u3000\u6811\u836b\u4e0b\u7684\u5927\u6c49\u4e0d\u7981\u5728\u5fc3\u4e2d\u6697\u559d\u4e00\u58f0\uff01 \u3000\u3000\u201c\u597d\u8eab\u624b\u3002\u201d\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u7edd\u5c18\u9a70\u6765\u7684\u8fd9\u4e94\u5339\u5065\u9a6c\u4e0a\uff0c\u9996\u5934\u7684\u4e00\u9a91\uff0c\u4e0a\u9762\u5750\u7740\u4e00\u4e2a\u8eab \u8eaf\u9880\u957f\uff0c\u9762\u5b54\u7626\u524a\uff0c\u9894\u4e0b\u5fae\u5fae\u7559\u7740\u4e9b\u77ed\u9aed\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u8863\u886b\u534e\u4e3d\uff0c\u795e\u91c7\u98de\u626c\uff0c\u4e00\u526f\u5fd7\u5f97\u610f\u6ee1 \u7684\u6837\u5b50\u3002 \u3000\u3000\u548c\u4ed6\u5e76\u80a9\u540c\u6765\u7684\u4e00\u9a91\u9a6c\u4e0a\u4eba\u9ad8\u98a7\u6df1\u816e\uff0c\u76ee\u5149\u5982\u9e70\uff0c\u6ee1\u9762\u7cbe\u608d\u4e4b\u8272\uff0c\u5de6\u624b\u5e26\u7740\u7f30\u7ef3\uff0c\u53f3\u624b \u7adf\u9f50\u8155\u65ad\u53bb\uff0c\u4ed6\u5de6\u638c\u5fae\u5e26\uff0c\u8de8\u4e0b\u5065\u9a6c\u4fbf\u81ea\u7eb9\u98ce\u4e0d\u52a8\uff0c\u9a91\u672f\u4e4b\u7cbe\u7edd\uff0c\u7adf\u662f\u65e0\u4e0e\u4f26\u6bd4\u3002 \u3000\u3000\u6811\u836b\u4e0b\u7684\u5927\u6c49\u53c8\u81ea\u4e92\u671b\u4e00\u773c\uff0c\u8f6c\u76ee\u671b\u5411\u7b2c\u4e09\u5339\u9a6c\u4e0a\uff0c\u9a6c\u4e0a\u5750\u7684\u7adf\u662f\u4e00\u4e2a\u5999\u9f84\u5c11\u5973\uff0c\u4e00\u8eab \u6de1\u9752\u8272\u7684\u7d27\u8eab\u8863\u88e4\uff0c\u6ee1\u5934\u7684\u9752\u4e1d\uff0c\u4e5f\u662f\u4e00\u65b9\u6de1\u9752\u4e1d\u4e2d\u4e00\u8d77\u5305\u7740\uff0c\u9762\u5982\u6843\u82b1\uff0c\u773c\u660e\u5982\u6c34\uff0c\u79cb\u6ce2 \u5fae\u626b\uff0c\u7fa4\u5c71\u5931\u8272\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u867d\u89c9\u8fd9\u5c11\u5973\u7f8e\u8273\u4e0d\u53ef\u65b9\u7269\uff0c\u4f46\u795e\u6001\u4e4b\u4e2d\uff0c\u5374\u53c8\u5e26\u7740\u4e03\u5206\u51db\u7136\u4e0d \u53ef\u4fb5\u72af\u7684\u9ad8\u534e\u4e4b\u6001\u3002 \u3000\u3000\u90a3\u72ec\u638c\u6c49\u5b50\u8eab\u8eaf\u5fae\u626b\uff0c\u201c\u5237\u201d\u7684\u8dc3\u4e0b\u9a6c\u6765\uff0c\u5927\u6b65\u8d70\u5230\u8fd9\u5c11\u5973\u8eab\u524d\uff0c\u5e26\u7740\u6ee1\u8138\u7b11\u5bb9\uff0c\u95ee \u9053\uff1a\u201c\u59d1\u5a18\uff0c\u60a8\u53ef\u8981\u4e0b\u6765\u6b47\u6b47\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u79cb\u6ce2\u4e00\u8f6c\uff0c\u5374\u56de\u9996\u671b\u4e86\u8eab\u540e\u7684\u4e8c\u4eba\u4e00\u773c\uff0c\u4fbf\u5fae\u5fae\u6447\u9996\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u4f60\u628a\u90a3\u9ec4\u91d1 \u74dc\u4e70\u51e0\u4e2a\uff0c\u5e26\u5728\u8def\u4e0a\u5403\u5c31\u884c\u4e86\u3002\u8bed\u97f3\u6e05\u67d4\u5a07\u8106\uff0c\u6709\u5982\u957f\u8349\u4e2d\u7684\u98de\u83ba\uff0c\u5374\u662f\u4e00\u53e3\u7eaf\u7cb9\u4eac\u7247\u5b50\u3002 \u72ec\u638c\u6c49\u5b50\u542b\u7b11\u5e94\u4e86\u4e00\u58f0\uff0c\u5fae\u4e00\u62e7\u8eab\uff0c\u7bad\u6b65\u7a9c\u5230\u74dc\u679c\u62c5\u65c1\uff0c\u638f\u51fa\u4e00\u952d\u4e24\u8bb8\u91cd\u7684\u94f6\u5b50\uff0c\u201d\u5427\u201c\u7684 \u4e00\u58f0\uff0c\u629b\u5728\u5730\u4e0a\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5356\u74dc\u7684\uff0c\u628a\u4f60\u4eec\u8fd9\u91cc\u4e0a\u597d\u7684\u74dc\u679c\uff0c\u5168\u7528\u7bd3\u5b50\u7ed9\u7237\u4eec\u88c5\u4e0a\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u67f3\u7709\u8f7b\u98a6\uff0c\u53c8\u56de\u9996\u671b\u4e86\u8eab\u540e\u7684\u4e24\u4eba\u4e00\u773c\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u9f9a\u4e09\u53d4\u8fd8\u662f\u8fd9\u6837\u7684\u813e \u6c14\u3002\u201d \u3000\u3000\u5979\u8eab\u540e\u4e24\u9a91\uff0c\u9a6c\u4e0a\u4eba\u7adf\u662f\u4e24\u4f4d\u9762\u8c8c\u5b8c\u5168\u4e00\u6837\uff0c\u8863\u9752\u4e5f\u5b8c\u5168\u76f8\u540c\u7684\u67af\u7626\u6c49\u5b50\uff0c\u9762\u571f\u6728\u7136\u6ca1 \u6709\u4efb\u4f55\u8868\u60c5\uff0c\u65e5\u5149\u5982\u7535\uff0c\u5374\u662f\u5f80\u6765\u6d41\u8f6c\uff0c\u542c\u4e86\u8fd9\u5c11\u5973\u7684\u8bdd\uff0c\u9762\u4e0a\u795e\u8272\uff0c\u4ecd\u7136\u4e1d\u6beb\u4e0d\u52a8\uff0c\u751f\u50cf \u662f\u4e16\u95f4\u4efb\u4f55\u8a00\u8bed\uff0c\u90fd\u4e0d\u8db3\u4ee5\u4ee4\u4ed6\u4eec\u5173\u5fc3\u4f3c\u7684\u3002 \u3000\u3000\u6811\u836b\u4e0b\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u89c1\u5230\u8fd9\u4e24\u4e2a\u67af\u7626\u6c49\u5b50\uff0c\u9762\u8272\u5374\u4e0d\u7981\u4e3a\u4e4b\u84e6\u7136\u4e00\u53d8\uff0c\u4e92\u671b\u4e00\u773c\uff0c\u5404\u81ea \u5782\u4e0b\u5934\u53bb\uff0c\u53d6\u4e86\u8eab\u65c1\u5c1a\u672a\u5403\u5b8c\u7684\u9999\u74dc\uff0c\u4f4e\u5934\u5927\u56bc\u8d77\u6765\uff0c\u76ee\u5149\u518d\u4e5f\u4e0d\u6562\u5f80\u4e0a\u8198\u4e00\u773c\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u90a3\u72ec\u638c\u6c49\u5b50\u4e70\u597d\u4e86\u74dc\u679c\uff0c\u8fd9\u4e94\u5339\u5065\u9a6c\uff0c\u4fbf\u53c8\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u6811\u836b\u4e0b\u7684\u5927\u6c49\uff0c\u8fd9\u65f6\u624d\u6562\u62ac\u8d77\u5934\u6765\uff0c\u5374\u4e0d\u7ea6\u800c\u540c\u5730\u957f\u8eab\u800c\u8d77\uff0c\u4e00\u4e2a\u9894\u4e0b\u957f\u7740\u63a9\u53e3\u6d53\u987b\u7684 \u5f6a\u5f62\u6c49\u5b50\uff0c\u76ee\u9001\u7740\u4ed6\u4eec\u7684\u540e\u5f71\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u679c\u7136\u4e0d\u51fa\u5e84\u4e3b\u6240\u6599\uff0c\u98de\u9f99\u9556\u5c40\u91cc\u5df2\u7ecf\u6709\u4eba\u6765\u54a7\uff0c \u54fc\uff0c\u4f60\u770b\u770b\u90a3\u5feb\u9a6c\u795e\u5200\u9f9a\u6e05\u6d0b\u7684\u90a3\u4efd\u72c2\u52b2\uff0c\u82e5\u4e0d\u662f\u2026\u2026\u5509\uff0c\u82e5\u4e0d\u662f\u4ed6\u8eab\u540e\u8fd8\u8ddf\u7740\u90a3\u4e24\u4f4d\uff0c\u6211 \u5f53\u65f6\u5c31\u60f3\u6559\u8bad\u6559\u8bad\u4ed6\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u5927\u6c49\u628a\u624b\u4e2d\u7684\u9a6c\u8fde\u5761\u5927\u8349\u5e3d\u5f80\u5934\u4e0a\u4e00\u6234\uff0c\u4e00\u9762\u9053\uff1a\u201c\u2018\u5feb\u9a6c\u795e\u5200\u2019\u9f9a\u6e05\u6d0b\u548c\u2018\u516b \u5366\u638c\u2019\u67f3\u8f89\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u6765\u4e86\u5012\u65e0\u6240\u8c13\uff0c\u540e\u9762\u90a3\u4e24\u4f4d\uff0c\u5012\u7684\u786e\u624e\u624b\u5f97\u5f88\uff0c\u8fd8\u6709\u90a3\u4e2a\u5c0f\u599e\u513f\uff0c\u5374 \u4e0d\u77e5\u662f\u8c01\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4eba\u53cc\u7709\u4e00\u8f69\uff0c\u547c\u54e8\u4e00\u58f0\uff0c\u62db\u6765\u90a3\u8fb9\u7684\u513f\u5339\u5065\u9a6c\uff0c\u4e00\u9762\u9053\uff1a\u201c\u6211\u770b\u90a3\u5c0f\u5a18\u4eec\u516b\u6210\u5c31\u662f \u90a3\u6761\u5b7d\u9f99\u7684\u5973\u513f\uff0c\u5979\u8001\u5b50\u65e2\u7136\u653e\u5fc3\u8ba9\u5979\u51fa\u6765\u8d70\u6c5f\u6e56\uff0c\u624b\u5e95\u4e0b\u4e5f\u7edd\u5bf9\u9519\u4e0d\u4e86\uff0c\u5509\uff01\u6211\u771f\u4e0d\u77e5\u9053 \u5e84\u4e3b\u6253\u7684\u662f\u4ec0\u4e48\u4e3b\u610f\uff0c\u5f04\u4e86\u90a3\u4e48\u4e2a\u602a\u5c0f\u5b50\u6765\u5f53\u603b\u74e2\u628a\u5b50\uff0c\u5230\u4e86\u90a3\u5929\uff0c\u4ed6\u4e0d\u5f04\u4e2a\u7b11\u8bdd\u51fa\u6765\u624d \u602a\uff01\u201d \u3000\u3000\u90a3\u6d53\u987b\u5927\u6c49\u201c\u54fc\u201d\u4e86\u4e00\u201c\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5e84\u4e3b\u7684\u4e3b\u610f\uff0c\u4e5f\u662f\u4f60\u968f\u4fbf\u80fd\u8912\u8d2c\u7684\u5417\uff1f\u6211\u770b\u4f60 \u5c0f\u5b50\u771f\u662f\u80c6\u5b50\u4e0a\u751f\u6bdb\u4e86\u3002\u201d\u5de8\u638c\u5fae\u7ffb\uff0c\u6293\u4f4f\u4e00\u5339\u9a6c\u7684\u7ec4\u7ef3\uff0c\u7ffb\u8eab\u8dc3\u4e86\u4e0a\u53bb\uff0c\u53c8\u9053\uff1a\u201c\u98de\u9f99\u9556 \u5c40\u7684\u4eba\u65e2\u7136\u5df2\u73b0\u5f62\u8e2a\uff0c\u54b1\u4eec\u4e5f\u7528\u4e0d\u7740\u518d\u53bb\u6253\u542c\u4e86\uff0c\u8fd8\u662f\u5feb\u56de\u5e84\u53bb\u5427\uff01\u201d\u53cc\u817f\u4e00\u5939\uff0c\u626c\u97ad\u800c \u53bb\u3002 \u3000\u3000\u53ea\u5269\u4e0b\u90a3\u8d29\u5356\u74dc\u679c\u7684\u5c0f\u8d29\uff0c\u5140\u81ea\u7ad9\u5728\u6811\u4e0b\uff0c\u671b\u7740\u8fd9\u4e9b\u5927\u6c49\u9010\u6e10\u8fdc\u53bb\u7684\u8eab\u5f71\uff0c\u5446\u5446\u5730\u51fa\u4e86 \u4e00\u4f1a\u513f\u795e\uff0c\u7a81\u5730\u6284\u8d77\u5730\u4e0a\u7684\u62c5\u5b50\uff0c\u5927\u6b65\u5411\u53e6\u4e00\u65b9\u5411\u8d70\u53bb\uff0c\u53ea\u662f\u90a3\u4e9b\u52b2\u88c5\u5927\u6c49\u6ca1\u6709\u770b\u5230\u4ed6\u6b64\u523b \u7684\u795e\u60c5\u800c\u5df2\u3002 \u3000\u3000\u7531\u4e0b\u5348\u800c\u9ec4\u660f\uff0c\u8fd9\u6761\u5927\u8def\u4e0a\u7531\u897f\u9762\u9a70\u5411\u4e1c\u9762\u7684\u6b66\u6797\u8c6a\u6770\uff0c\u4e00\u62e8\u63a5\u7740\u4e00\u62e8\uff0c\u4e00\u4e2a\u4e2a\u4ff1\u662f\u6ee1 \u9762\u7cbe\u608d\u4e4b\u8272\uff0c\u663e\u89c1\u5f97\u90fd\u662f\u8349\u6cfd\u4e2d\u6210\u540d\u7684\u8c6a\u58eb\u3002 \u3000\u3000\u4f46\u662f\u88f4\u73cf\uff0c\u4ed6\u77e5\u4e0d\u77e5\u9053\u81ea\u5df1\u5df2\u5728\u6b66\u6797\u4e2d\u9020\u6210\u8fd9\u4e48\u5927\u7684\u9a9a\u52a8\u5462\uff1f \u3000\u3000\u5929\u9ed1\u4e86\uff0c\u4e00\u53cc\u94dc\u70db\u53f0\u4e0a\u7684\u4e24\u652f\u5de8\u70db\uff0c\u5c06\u4e00\u95f4\u5e03\u7f6e\u5f97\u6781\u5176\u7cbe\u81f4\u7684\u4e66\u623f\uff0c\u6620\u5f97\u5341\u5206\u660e\u4eae\u3002 \u3000\u3000\u88f4\u73cf\u4ee5\u624b\u652f\u989d\uff0c\u659c\u659c\u5730\u5750\u5728\u4e66\u684c\u524d\u7684\u6905\u5b50\u4e0a\uff0c\u76ee\u5149\u51dd\u6ce8\u7740\u90a3\u53cc\u70db\u53f0\uff0c\u9ed8\u9ed8\u5730\u60f3\u7740\u5fc3\u4e8b\u3002 \u3000\u3000\u4ed6\u4fa7\u9996\u671b\u7740\u5750\u5728\u8eab\u4fa7\u7684\u5434\u9e23\u4e16\u4e00\u773c\uff0c\u7a81\u5730\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5434\u5144\uff0c\u6211\u603b\u89c9\u6b64\u4e8b\u6709\u4e9b\u4e0d\u59a5\uff0c\u6b64 \u523b\u8ddd\u79bb\u4f1a\u671f\u8d8a\u6765\u8d8a\u8fd1\uff0c\u6211\u7684\u5fc3\u4e5f\u5c31\u8d8a\u53d1\u4e71\u4e86\uff0c\u8bd5\u60f3\u50cf\u6211\u8fd9\u6837\u4e00\u4e2a\u65e0\u7528\u7684\u4eba\uff0c\u600e\u80fd\u62c5\u5f53\u8d77\u8fd9\u4e48 \u91cd\u7684\u62c5\u5b50\uff0c\u5509\u2014\u2014\u201d\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u5fae\u5fae\u53d8\u52a8\u4e86\u4e00\u4e0b\u81ea\u5df1\u5750\u7740\u7684\u59ff\u52bf\uff0c\u53cc\u7709\u4e0d\u7981\u4e3a\u4e4b\u4e00\u76b1\uff0c\u63a5 \u7740\u53c8\u9053\uff1a\u201c\u4f55\u51b5\u6211\u8eab\u4e0a\u6240\u53d7\u7684\u4f24\uff0c\u76f4\u5230\u6b64\u523b\u4ecd\u672a\u75ca\u6108\uff0c\u5434\u5144\uff0c\u4f60\u5929\u8d44\u7edd\u4e16\uff0c\u6211\u5374\u662f\u4e2a\u6700\u7b28\u7684 \u4eba\uff0c\u8fd9\u4e00\u5e74\u6765\u6211\u5728\u6c5f\u6e56\u4e2d\u6d41\u6d6a\uff0c\u66f4\u77e5\u9053\u6c5f\u6e56\u4e2d\u6709\u7740\u60ca\u4eba\u6b66\u529f\u7684\u5947\u4eba\u5f02\u58eb\uff0c\u5b9e\u5728\u5927\u591a\u4e86\uff0c\u8981\u6211 \u8fd9\u4e48\u4e2a\u7b28\uff0c\u7b28\u5f97\u8fde\u6b66\u529f\u90fd\u5b66\u4e0d\u4f1a\u7684\u4e00\u4e2a\u4eba\u6765\u5f53\u6c5f\u5357\u6b66\u6797\u7684\u9886\u8896\uff0c\u5c82\u4e0d\u8981\u88ab\u5929\u4e0b\u82f1\u96c4\u803b\u7b11\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u5fae\u4e00\u7b11\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u5728\u623f\u4e2d\u7f13\u7f13\u8e31\u7740\u6b65\u5b50\u3002 \u3000\u3000\u53ea\u542c\u88f4\u73cf\u76b1\u7709\u53c8\u9053\uff1a\u201c\u4f55\u51b5\u2026\u2026\u5509\uff0c\u6211\u53c8\u4f55\u5c1d\u4e0d\u77e5\u9053\u90a3\u2018\u795e\u624b\u2019\u6218\u98de\u7684\u7528\u5fc3\uff0c\u4ed6\u4e4b\u6240\u4ee5 \u8981\u8ba9\u6211\u6765\u5f53\u8fd9\u603b\u74e2\u628a\u5b50\uff0c\u8fd8\u4e0d\u662f\u5df2\u77e5\u9053\u6211\u662f\u4e2a\u65e0\u7528\u7684\u4eba\uff0c\u662f\u4ee5\u4fbf\u60f3\u53eb\u6211\u53bb\u505a\u4ed6\u7684\u5080\u5121\uff0c\u65e5\u540e \u4ed6\u82e5\u8981\u6211\u505a\u4ec0\u4e48\u8fdd\u80cc\u826f\u5fc3\u4e4b\u4e8b\uff0c\u6211\u53c8\u5f53\u5982\u4f55\uff1f\u5434\u5144\uff0c\u6211\u90a3\u65f6\u82e5\u77e5\u9053\u4f1a\u751f\u51fa\u8fd9\u4e9b\u9ebb\u70e6\uff0c \u5509\u2026\u2026\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u500f\u7136\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u968f\u53c8\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u600e\u5730\uff0c\u81ea\u4ece\u6211\u7a74\u9053\u88ab\u90a3\u53ae \u6070\u5de7\u9707\u5f00\u4e4b\u540e\uff0c\u6211\u7adf\u53d8\u5f97\u5982\u6b64\u559c\u6b22\u8bf4\u8bdd\uff0c\u5509\u2014\u2014\u4eba\u4eec\u80fd\u591f\u5c06\u5fc3\u4e2d\u60f3\u8bf4\u7684\u8bdd\u8bf4\u51fa\u6765\uff0c\u7684\u786e\u662f\u4ef6 \u75db\u5feb\u7684\u4e8b\uff0c\u8fc7\u53bb\u4e00\u5e74\u6765\u2014\u2014\u201d\u201c\u5434\u9e23\u4e16\u5251\u7709\u5fae\u5254\uff0c\u7a81\u5730\u987f\u4f4f\u811a\u6b65\uff0c\u9762\u5bf9\u88f4\u73cf\u6717\u58f0\u63a5\u9053\u3002\u201d\u88f4 \u5144\uff0c\u6211\u4e0e\u4f60\u76f8\u4ea4\u65f6\u65e5\u867d\u6d45\uff0c\u4f46\u6211\u4e00\u751f\u4e4b\u4e2d\uff0c\u5374\u53ea\u4ea4\u4e86\u4f60\u8fd9\u4e48\u4e00\u4e2a\u670b\u53cb\u3002\u201c\u88f4\u73cf\u5fae\u5531\u4e00\u58f0\uff0c\u63a5 \u53e3\u9053\uff1a\u201c\u9664\u4e86\u5144\u53f0\u4e4b\u5916\uff0c\u82b8\u82b8\u5929\u4e0b\uff0c\u4e5f\u518d\u65e0\u4e00\u4eba\u771f\u7684\u89c6\u6211\u4e3a\u53cb\u4e86\u3002\u201d\u5434\u9e23\u4e16\u5fae\u7b11\u4e00\u4e0b\uff0c\u77ac\u53c8 \u6b63\u8272\u9053\uff1a\u201c\u4f60\u6211\u65e2\u76f8\u4ea4\uff0c\u670b\u53cb\u8d35\u5728\u77e5\u5fc3\uff0c\u6211\u6709\u4e00\u53e5\u8bdd\u672c\u5f85\u4e0d\u8bf4\uff0c\u4f46\u5374\u6709\u5982\u9aa8\u9ca0\u5728\u5589\uff0c\u975e\u8bf4\u4e0d \u53ef\u3002\u201d \u3000\u3000\u88f4\u73cf\u76ee\u5149\u4e00\u62ac\uff0c\u9053\uff1a\u201c\u5434\u5144\u53ea\u7ba1\u8bf4\u51fa\u6765\u4fbf\u662f\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u9053\uff1a\u201c\u4f60\u6211\u4e00\u89c1\u5982\u6545\uff0c\u627f\u8499\u4f60\u4e0d\u5f03\uff0c\u5c06\u4f60\u4e00\u751f\u906d\u9047\uff0c\u90fd\u544a\u8bc9\u4e86\u6211\uff0c\u6211\u4e0e\u4f60\u4ee5\u524d\u867d \u4e0d\u76f8\u8bc6\uff0c\u4f46\u4e5f\u53ef\u77e5\u9053\u4f60\u4ee5\u524d\u5fc5\u5b9a\u4e0d\u4f1a\u662f\u4e2a\u61e6\u592b\uff0c\u4f46\u8fd9\u4e9b\u65e5\u5b50\uff0c\u81ea\u4ece\u4f60\u968f\u90a3\u795e\u624b\u6218\u98de\u6765\u5230\u6b64\u5730 \u4e4b\u540e\uff0c\u6211\u770b\u4f60\u4e00\u65e5\u4e4b\u95f4\uff0c\u81f3\u5c11\u8981\u957f\u5401\u77ed\u53f9\u767e\u6570\u5341\u6b21\uff0c\u8fd9\u5374\u4e0d\u662f\u5927\u4e08\u5927\u7684\u884c\u5f84\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5446\u4e86\u4e00\u5446\uff0c\u5374\u542c\u4ed6\u53c8\u9053\uff1a\u201c\u90a3\u2018\u795e\u624b\u2019\u6218\u98de\u6b64\u4e3e\uff0c\u56fa\u7136\u662f\u522b\u6709\u5c45\u5fc3\uff0c\u4f46\u4f60\u53c8\u4f55\u5c1d\u4e0d \u80fd\u5c06\u8ba1\u5c31\u8ba1\uff0c\u4e58\u7740\u8fd9\u4e2a\u673a\u4f1a\uff0c\u505a\u4e24\u4ef6\u540d\u9707\u5929\u4e0b\uff0c\u9020\u798f\u6b66\u6797\u7684\u4e8b\u6765\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u53ea\u89c1\u88f4\u73cf\u7f13\u7f13\u5782\u4e0b\u76ee\u5149\uff0c\u4fbf\u53c8\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u4e4b\u5929\u8d44\uff0c\u8fdc\u5728\u6211\u4e4b\u4e0a \u591a\u591a\uff0c\u53ea\u662f\u4f60\u81ea\u5df1\u8fd8\u4e0d\u77e5\u9053\u800c\u5df2\uff0c\u4f60\u82e5\u6d6a\u8d39\u4e86\u8fd9\u4efd\u5929\u8d44\uff0c\u5c06\u5b83\u57cb\u846c\u5728\u8fc7\u4efd\u7684\u3002\u8c26\u865a\u91cc\uff0c\u90a3\u5c31 \u592a\u53ef\u60dc\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u9ed8\u9ed8\u5730\u8f6c\u8fc7\u76ee\u5149\uff0c\u7167\u8fdb\u7a97\u5b50\u6765\u7684\u6708\u534e\uff0c\u53c8\u6e10\u6e10\u9000\u4e86\u56de\u53bb\uff0c\u4ed6\u77e5\u9053\u591c\u5df2\u66f4\u6df1\u4e86\u3002 \u3000\u3000\u201c\u6211\u7a76\u7adf\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u4ed6\u6697\u95ee\u7740\u81ea\u5df1\uff1a\u201c\u540d\u626c\u5929\u4e0b\u201d\uff0c\u672c\u662f\u4ed6\u68a6\u5e7b\u4ee5\u6c42\u7684\u4e8b\uff0c\u4f46\u6b64\u523b\u9762\u5bf9\u7740\u8fd9\u626c\u540d\u7684\u673a\u4f1a\uff0c\u4ed6 \u5374\u53c8\u4e0d\u7981\u6709\u4e9b\u80c6\u602f\u3002 \u3000\u3000\u56e0\u4e3a\u5927\u591a\u7684\u6298\u78e8\uff0c\u5df2\u4f7f\u5f97\u4ed6\u5931\u53bb\u539f\u6709\u7684\u81ea\u4fe1\u3002\u8fd9\u4e00\u5e74\u6765\uff0c\u547d\u8fd0\u5bf9\u4ed6\u7684\u5b89\u6392\uff0c\u6839\u672c\u4ece\u672a\u7ed9 \u4ed6\u81ea\u5df1\u9009\u62e9\u7684\u673a\u4f1a\uff0c\u5bf9\u4efb\u4f55\u4e8b\uff0c\u4ed6\u53ea\u6709\u9ed8\u9ed8\u987a\u4ece\uff0c\u800c\u4ece\u672a\u6709\u8fc7\u53cd\u6297\u7684\u4f59\u5730\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u6b64\u523b\uff0c\u5f53\u4ed6\u81ea\u5df1\u80fd\u4e3a\u81ea\u5df1\u7684\u547d\u8fd0\u4f5c\u4e00\u9009\u62e9\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u672a\u514d\u4e3a\u4e4b\u4e3e\u68cb\u4e0d\u5b9a\u4e86\u3002 \u3000\u3000\u5434\u9e23\u4e16\u76ee\u5149\u51dd\u6ce8\u5728\u4ed6\u8eab\u4e0a\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u770b\u4ed6\u4ecd\u7136\u5782\u7740\u5934\uff0c\u751a\u81f3\u8fde\u5750\u7684\u59ff\u52bf\u90fd\u6ca1\u6709\u6539\u53d8\u4e00 \u4e0b\uff0c\u4e0d\u7981\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u65b9\u6cd5\u80fd\u591f\u6fc0\u8d77\u4ed6\u7684\u52c7\u6c14\u5462\uff1f\u4ed6\u672c\u53ef\u53d8\u6210\u4e00\u53ea\u521a\u5f3a \u7684\u72ee\u5b50\uff0c\u4f46\u6b64\u523b\u4ed6\u5374\u4ec5\u4ec5\u662f\u4e00\u53ea\u5584\u826f\u7684\u7ef5\u7f8a\u800c\u5df2\u3002\u201d \u3000\u3000\u66f4\u6572\u4e4b\u58f0\uff0c\u4ece\u7a97\u5916\u4f20\u6765\uff0c\u5df2\u7ecf\u8fc7\u4e86\u4e24\u66f4\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u5434\u9e23\u4e16\u53f9\u606f\u7740\u8d70\u4e86\u51fa\u6765\uff0c\u4e00\u9762\u6697\u4e2d\u544a\u8bc9\u81ea\u5df1\uff1a\u201c\u7b49\u5230\u592a\u9633\u5347\u8d77\u6765\u7684\u65f6\u5019\uff0c\u6211\u518d\u60f3\u60f3 \u529e\u6cd5\u5427\uff0c\u5728\u8fd9\u6625\u5929\u7684\u665a\u4e0a\uff0c\u8fde\u72ee\u5b50\u90fd\u4f1a\u53d8\u6210\u7ef5\u7f8a\uff0c\u6211\u53c8\u600e\u80fd\u4f7f\u7ef5\u7f8a\u53d8\u6210\u72ee\u5b50\u5462\uff1f\u201d \u3000\u3000\u4e8e\u662f\u8fd9\u95f4\u539f\u6765\u5df2\u662f\u5341\u5206\u5e7d\u9759\u7684\u4e66\u623f\uff0c\u6b64\u523b\u5c31\u53d8\u5f97\u66f4\u4e3a\u5e7d\u9759\u4e86\uff0c\u5e7d\u9759\u5f97\u4ee4\u88f4\u73cf\u4e0d\u7981\u611f\u89c9\u5230 \u4e00\u79cd\u65e0\u6bd4\u96be\u582a\u7684\u5bc2\u5bde\u3002 \u3000\u3000\u7a97\u5916\u5ead\u9662\u6df1\u6c89\uff0c\u5fae\u98ce\u58f0\uff0c\u866b\u9e23\u58f0\uff0c\u6df7\u5408\u5728\u5e7d\u51b7\u51c4\u6e05\u7684\u6708\u5149\u91cc\uff0c\u4fbf\u6709\u5982\u60c5\u4eba\u7684\u773c\u6cea\u6ef4\u5728\u6ee1 \u5858\u6b8b\u8377\u7684\u5c0f\u6c60\u4e2d\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u5927\u5730\u4e0d\u4e5f\u53d8\u6210\u5c11\u5973\u7684\u9762\u988a\u4e86\u5417\uff1f \u3000\u3000\u88f4\u73cf\u8d39\u529b\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u51fa\u95e8\uff0c\u8d70\u5230\u8fd9\u6df1\u6c89\u7684\u5ead\u9662\u91cc\u3002 \u3000\u3000\u4ed6\u6e34\u671b\u7740\u6625\u591c\u7684\u6708\u5149\u7167\u5728\u4ed6\u8eab\u4e0a\uff0c\u66f4\u559c\u7231\u6625\u665a\u7684\u58f0\u97f3\u542c\u5230\u4ed6\u8033\u91cc\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u8fd8\u662f\u70ed \u7231\u7740\u751f\u547d\u7684\uff0c\u7eb5\u7136\u4ed6\u6b64\u523b\u6709\u7740\u4e00\u4efd\u6de1\u6de1\u7684\u5fe7\u90c1\u3002 \u3000\u3000\u4ed6\u4eec\u5c45\u4f4f\u7684\u5730\u65b9\uff0c\u662f\u8fd9\u6d6a\u83bd\u5c71\u5e84\u5e7d\u9759\u7684\u540e\u9662\u91cc\u7684\u4e00\u4e2a\u5e7d\u9759\u7684\u4fa7\u8f69\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u4f3c\u4e4e\u6709 \u610f\u5c06\u4ed6\u548c\u4e00\u5207\u4eba\u9694\u5f00\uff0c\u5c31\u8fde\u5434\u9e23\u4e16\uff0c\u90fd\u662f\u5b89\u7f6e\u5230\u524d\u9662\u897f\u53a2\u7684\u4e00\u95ee\u5ba2\u623f\u91cc\u3002 \u3000\u3000\u6cbf\u7740\u9662\u4e2d\u4e00\u6761\u788e\u77f3\u4e8e\u94fa\u6210\u7684\u5c0f\u8def\uff0c\u4ed6\u7f13\u7f13\u800c\u884c\uff0c\u6708\u5149\u7167\u5728\u8fd9\u6761\u5c0f\u5f84\u4e0a\uff0c\u5c06\u6ee1\u5f84\u7684\u788e\u77f3\uff0c \u90fd\u95ee\u70c1\u5f97\u6709\u5982\u94bb\u77f3\u822c\u5149\u4eae\u3002 \u3000\u3000\u4ed6\u968f\u624b\u62fe\u8d77\u4e00\u5757\uff0c\u53c8\u8d39\u529b\u629b\u4e86\u51fa\u53bb\uff0c\u6697\u4e2d\u81ea\u611f\u53f9\u7740\u81ea\u5df1\u4e00\u751f\u906d\u9047\u4e4b\u51c4\uff0c\u5374\u53c8\u4e0d\u7981\u6697\u81ea\u611f \u53f9\u7740\u81ea\u5df1\u4e00\u751f\u906d\u9047\u4e4b\u5947\u3002 \u3000\u3000\u8bb8\u591a\u5f20\u719f\u6089\u7684\u9762\u5b54\uff0c\u4fbf\u5f00\u59cb\u5728\u8111\u6d77\u4e2d\u6cdb\u6ee5\u8d77\u6765\u3002 \u3000\u3000\u53ea\u89c1\u9662\u5b50\u7684\u89d2\u843d\u91cc\uff0c\u6709\u4e00\u6247\u5c0f\u5c0f\u7684\u6728\u95e8\uff0c\u4ed6\u6f2b\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e3a\u4e4b\u731b \u70c8\u8df3\u52a8\u4e00\u4e0b\uff0c\u51e0\u4e4e\u8131\u53e3\u60ca\u547c\u8d77\u6765\uff0c\u5168\u529b\u5954\u4e86\u8fc7\u53bb\uff0c\u89d2\u95e8\u524d\u7adf\u5012\u5367\u7740\u4e24\u4e2a\u52b2\u88c5\u5927\u6c49\u7684\u8eab\u4f53\u3002 \u3000\u3000\u6708\u5df2\u5347\u81f3\u4e2d\u5929\uff0c\u6708\u5149\u7b14\u76f4\u5730\u7167\u4e0b\u6765\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u8eab\u5f62\u626d\u66f2\uff0c\u4ef0\u5929\u5012\u5367\u5728\u5730\u4e0a\uff0c\u53f3\u624b\u7d27\u7d27 \u634f\u7740\u8170\u95f4\u7684\u5200\u67c4\uff0c\u5200\u5df2\u51fa\u9798\u4e00\u534a\uff0c\u534a\u622a\u5200\u5149\uff0c\u9752\u84dd\u5982\u7535\uff0c\u8d70\u5230\u8fd1\u524d\u4e00\u770b\uff0c\u8fd9\u4e24\u4eba\u9762\u76ee\u4e4b\u4e0a\uff0c \u6ee1\u662f\u60ca\u6050\u4e4b\u8272\uff0c\u4f38\u624b\u4e00\u63a2\uff0c\u5374\u5df2\u6b7b\u53bb\u3002 \u3000\u3000\u665a\u6625\u7684\u98ce\uff0c\u672c\u5df2\u6e29\u6696\u5f97\u6709\u5982\u6148\u6bcd\u7684\u773c\u6ce2\uff0c\u4f46\u5439\u5230\u88f4\u73cf\u8eab\u4e0a\uff0c\u4ed6\u5374\u89c9\u5f97\u6709\u4e00\u9635\u4ee4\u4eba\u6817\u60ca\u7684 \u5bd2\u610f\uff0c\u671b\u7740\u8fd9\u4e24\u5177\u5c38\u8eab\uff0c\u4ed6\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u7a81\u5730\u4e00\u8f6c\u8eab\uff0c\u60f3\u8dd1\u56de\u623f\u5b50\u91cc\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u65b9\u4e00\u8f6c\u8eab\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u5374\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u5e76\u80a9\u7ad9\u5728\u81ea\u5df1\u8eab\u540e\u3002 \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8fd9\u4eba\u8eab\u8eaf\u67af\u7626\u5982\u67f4\uff0c\u5374\u7a7f\u7740\u4e00\u4ef6\u6781\u4e3a\u5bbd\u5927\u7684\u957f\u888d\uff0c\u968f\u7740\u665a\u98ce\uff0c\u98d8\u52a8\u4e0d \u5df2\uff0c\u5934\u4e0a\u4e4c\u7737\u9ad8\u9afb\uff0c\u9762\u76ee\u751f\u51b7\u5982\u94c1\uff0c\u6728\u7136\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u82e5\u4e0d\u662f\u4e00\u53cc\u70af\u7136\u6709\u5149\u7684\u773c\u775b\uff0c\u50cf\u95ea \u7535\u822c\u671b\u5728\u88f4\u73cf\u8eab\u4e0a\uff0c\u4fbf\u751f\u50cf\u4e00\u5177\u50f5\u5c38\uff0c\u54ea\u91cc\u50cf\u662f\u6d3b\u4eba\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u84e6\u5730\u4e00\u60ca\uff0c\u672c\u5df2\u731b\u70c8\u8df3\u52a8\u7740\u7684\u5fc3\uff0c\u6b64\u523b\u66f4\u50cf\u662f\u8981\u4ece\u8154\u5b50\u91cc\u8df3\u51fa\u6765\uff0c\u76ee\u5149\u4e00\u5782\uff0c \u518d\u4e5f\u4e0d\u6562\u770b\u4ed6\u4e00\u773c\uff0c\u4e0b\u610f\u8bc6\u5730\u4e00\u56de\u5934\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u76ee\u5149\u52a8\u5904\uff0c\u8eab\u524d\u7adf\u4e5f\u7ad9\u7740\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5bd2\uff0c\u5b9a\u775b\u671b\u53bb\uff0c\u8fd9\u4eba\u5f71\u7adf\u7136\u4ea6\u662f\u67af\u7626\u5982\u67f4\uff0c\u8863\u8896\u5bbd\u5927\uff0c\u4e4c\u7c2a\u9ad8\u9afb\uff0c \u9762\u76ee\u751f\u51b7\uff0c\u7adf\u548c\u65b9\u624d\u90a3\u4eba\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u4ee5\u4e3a\u81ea\u5df1\u770b\u82b1\u4e86\u773c\u775b\uff0c\u4f46\u8fd9\u4eba\u5f71\u5374\u662f\u771f\u771f\u5b9e\u5b9e\u5730\u7ad9\u5728\u4ed6\u773c\u524d\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u53c8\u662f\u4e00 \u5bd2\uff1a\u201c\u96be\u9053\u6211\u771f\u7684\u9047\u89c1\u4e86\u9b3c\uff1f\u201d\u56de\u5934\u518d\u4e00\u671b\uff0c\u8eab\u540e\u90a3\u6761\u4eba\u5f71\uff0c\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4ed6\u80c6\u5b50\u518d\u5927\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u76ee\u5149\u98de\u5feb\u5730\u5de6\u53f3\u4e00\u671b\uff0c\u81ea\u5df1\u8eab\u524d\u8eab\u540e\uff0c\u7adf \u5404\u5404\u7ad9\u7740\u4e00\u6761\u4eba\u5f71\uff0c\u4e0d\u4f46\u7a7f\u7740\u9762\u8c8c\u5b8c\u5168\u4e00\u6837\uff0c\u9762\u4e0a\u7684\u795e\u60c5\uff0c\u7adf\u7136\u4e5f\u662f\u5b8c\u5168\u76f8\u540c\uff0c\u6728\u7136\u6ca1\u6709\u4efb \u4f55\u8868\u60c5\uff01 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u88f4\u73cf\u7684\u8eab\u5f62\uff0c\u518d\u4e5f\u65e0\u6cd5\u52a8\u5f39\u4e00\u4e0b\uff0c\u53ea\u89c1\u5de6\u9762\u90a3\u67af\u7626\u6c49\u5b50\uff0c\u9762\u4e0a\u7684\u808c\u8089\u5fae\u5fae\u7275 \u52a8\u4e00\u4e0b\uff0c\u4e0d\u77e5\u662f\u5426\u5c31\u662f\u7b97\u505a\u7b11\u4e86\u4e00\u7b11\uff0c\u7136\u540e\u8eab\u8eaf\u7b14\u76f4\u5730\u4e00\u65cb\uff0c\u7535\u4e5f\u4f3c\u5730\u63a0\u5230\u90a3\u9053\u89d2\u95e8\u4e4b\u4e0a\uff0c \u4f38\u51fa\u624b\u638c\uff0c\u5728\u95e8\u4e0a\u4e00\u53ea\u5de8\u9501\u4e0a\u8f7b\u8f7b\u4e00\u634f\u3002 \u3000\u3000\u90a3\u53ea\u91cd\u903e\u767e\u65a4\uff0c\u575a\u56fa\u65e0\u6bd4\u7684\u5de8\u5927\u94c1\u9501\uff0c\u7adf\u5728\u4ed6\u8fd9\u53ea\u5e72\u67af\u5f97\u6709\u5982\u9e1f\u722a\u4e00\u822c\u7684\u624b\u638c\u8f7b\u8f7b\u4e00\u634f \u4e4b\u4e0b\uff0c\u50cf\u673d\u6728\u822c\u5e94\u624b\u800c\u88c2\u3002 \u3000\u3000\u53f3\u9762\u90a3\u67af\u7626\u6c49\u5b50\u9762\u4e0a\u7684\u808c\u8089\u4e5f\u81ea\u5fae\u5fae\u7275\u52a8\u4e00\u4e0b\uff0c\u53e3\u4e2d\u7adf\u6c89\u58f0\u9053\uff1a\u201c\u8bf7\uff01\u201d \u3000\u3000\u5de6\u9762\u7684\u67af\u7626\u6c49\u5b50\u6b64\u523b\u5df2\u6253\u5f00\u89d2\u95e8\uff0c\u624b\u5fae\u4e00\u4f38\uff0c\u53e3\u4e2d\u4ea6\u9053\uff1a\u201c\u8bf7\uff01\u201d \u3000\u3000\u8fd9\u4e24\u58f0\u201c\u8bf7\u201d\u5b57\uff0c\u8bed\u6c14\u4e4b\u51b0\u51b7\uff0c\u751f\u50cf\u662f\u53d1\u81ea\u4e38\u5e7d\uff0c\u54ea\u91cc\u6709\u534a\u5206\u6d3b\u4eba\u7684\u5473\u9053\uff0c\u88f4\u73cf\u53ea\u89c9\u4e00 \u80a1\u5bd2\u610f\uff0c\u7531\u811a\u5e95\u5347\u81f3\u80cc\u810a\uff0c\u7981\u4e0d\u4f4f\u53c8\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u7ad9\u5728\u8fd9\u4e24\u4e2a\u5f62\u5982\u9b3c\u9b45\u7684\u6c49\u5b50\u4e2d\u95f4\uff0c \u4e0d\u77e5\u600e\u751f\u662f\u597d\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u67af\u7626\u6c49\u5b50\u7684\u56db\u9053\u76ee\u5149\uff0c\u6709\u5982\u56db\u9053\u5389\u7535\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u4ed6\u8eab\u4e0a\uff0c\u4f7f\u5f97\u4ed6\u6709\u4e00\u79cd\u7f6e \u8eab\u5e7d\u51a5\u5730\u5e9c\u7684\u611f\u89c9\uff0c\u8fde\u81ea\u5df1\u7684\u8840\u6db2\uff0c\u90fd\u51b0\u51b7\u8d77\u6765\uff0c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u6697\u81ea\u5728\u5fc3\u4e2d\u5bfb\u601d\u9053\uff1a\u201c\u8fd9\u4e24\u4eba \u7a76\u7adf\u662f\u8c01\uff1f\u6765\u6b64\u7a76\u7adf\u662f\u4f55\u7528\u610f\uff1f\u6211\u4e0e\u4ed6\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u66f4\u65e0\u5bbf\u4ec7\u53ef\u8a00\uff0c\u4ed6\u4eec\u627e\u6211\u53c8\u4e3a\u7684\u4ec0\u4e48\uff1f \u53eb\u6211\u51fa\u6765\u53c8\u4e3a\u7684\u4ec0\u4e48\uff1f\u201d\uff0c\u4ed6\u867d\u7136\u65e0\u6cd5\u5f97\u5230\u8fd9\u4e9b\u95ee\u9898\u7684\u89e3\u7b54\uff0c\u4f46\u662f\u4e8b\u5df2\u81f3\u6b64\uff0c\u4ed6\u5374\u77e5\u9053\u81ea\u5df1 \u9664\u4e86\u8ddf\u7740\u4ed6\u4eec\u51fa\u53bb\u4e4b\u5916\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6697\u4e2d\u4e00\u54ac\u7259\u9f7f\uff0c\u5927\u6b65\u8d70\u51fa\u95e8\u5916\uff0c\u4e00\u9053\u5c0f\u6eaa\uff0c\u7531\u897f\u9762\u6d41\u6765\uff0c\u5a49\u8712\u5411\u4e1c\u6d41\u53bb\uff0c\u6c34\u58f0\u6f7a \u6f7a\uff0c\u6eaa\u65c1\u6709\u4e00\u7247\u7af9\u6797\uff0c\u4e3a\u98ce\u6240\u5439\uff0c\u98ce\u58f0\u7c0c\u7c0c\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u67af\u7626\u6c49\u5b50\uff0c\u4e00\u524d\u4e00\u540e\uff0c\u8d70\u5728\u88f4\u73cf\u8eab\u4fa7\u3002\u88f4\u73cf\u8033\u4e2d\u6240\u95fb\uff0c\u771f\u662f\u81ea\u5df1\u7684\u5fc3\u8df3\u4e4b\u58f0\uff0c\u8fde \u8fd9\u7f8e\u5999\u7684\u5929\u7c41\uff0c\u90fd\u65e0\u6cd5\u542c\u5230\u4e86\u3002 \u3000\u3000\u8d70\u5230\u7af9\u6797\u8fd1\u524d\uff0c\u524d\u884c\u7684\u67af\u7626\u6c49\u5b50\uff0c\u7a81\u5730\u56de\u8fc7\u5934\u6765\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u9601\u4e0b\u5c31\u662f\u5c06\u4efb\u6c5f\u5357\u7eff\u6797\u603b \u74e2\u628a\u5b50\u7684\u88f4\u5927\u5148\u751f\u5427\uff1f\u201d\u8fd9\u4e8c\u5341\u4f59\u5b57\u81ea\u4ed6\u53e3\u4e2d\u8bf4\u51fa\uff0c\u97f3\u8c03\u9ad8\u4f4e\uff0c\u7adf\u7136\u6beb\u65e0\u53d8\u5316\uff0c\u6b64\u65f6\u542c\u6765\uff0c \u66f4\u89c9\u6709\u5982\u51fa\u81ea\u5e7d\u51a5\u3002 \u3000\u3000\u88f4\u73cf\u5446\u4e86\u4e00\u5446\uff0c\u8111\u6d77\u4e2d\u95ea\u7535\u822c\u63a0\u8d77\u4e00\u4e2a\u5ff5\u5934\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u600e\u5730\u8fd9\u4e24\u4eba\u4e5f\u77e5\u9053\u6211\u7684\u540d \u5b57\uff0c\u96be\u9053\u4ed6\u4eec\u4ea6\u662f\u90a3\u795e\u624b\u6218\u98de\u7684\u5bf9\u5934\uff0c\u524d\u6765\u52a0\u5bb3\u4e8e\u6211\uff1f\u201d\u76ee\u5149\u62ac\u5904\uff0c\u53ea\u89c1\u8fd9\u67af\u7626\u6c49\u5b50\u4e24\u9053\u6444 \u4eba\u5fc3\u9b44\u7684\u9634\u51b7\u76ee\u5149\u4e4b\u4e2d\uff0c\u679c\u7136\u6ee1\u542b\u6076\u6bd2\u4e4b\u610f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u5bd2\uff0c\u51e0\u4e4e\u60f3\u5426\u8ba4\u6b64\u4e8b\uff0c\u4f46\u5fc3 \u5ff5\u4e00\u8f6c\uff0c\u53c8\u81ea\u5fd6\u9053\uff1a\u88f4\u73cf\u5440\u88f4\u73cf\uff0c\u4f60\u96be\u9053\u771f\u7684\u5df2\u7ecf\u5931\u53bb\u6614\u65e5\u7684\u52c7\u6c14\uff0c\u4f60\u96be\u9053\u771f\u7684\u5df2\u53d8\u6210\u4e00\u4e2a \u53ea\u4f1a\u53f9\u6c14\u7684\u61e6\u592b\uff0c\u4eca\u65e5\u4f60\u5c31\u7b97\u8981\u88ab\u8fd9\u4e24\u4eba\u6740\u6b7b\uff0c\u53c8\u600e\u80fd\u505a\u51fa\u6b64\u7b49\u6076\u52a3\u3001\u5351\u9119\u4e4b\u6001\uff01\u201c\u4e00\u5ff5\u81f3 \u6b64\uff0c\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u53c8\u590d\u70ed\u8840\u6cb8\u817e\uff0c\u4e00\u633a\u80f8\u819b\uff0c\u6602\u9996\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u6b63\u662f\u88f4\u73cf\uff0c\u4e0d\u77e5\u4e24 \u4f4d\u6df1\u5bb5\u76f8\u53ec\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d\u6b64\u523b\u4ed6\u5df2\u5c06\u751f\u6b7b\u4e4b\u4e8b\uff0c\u5168\u7136\u7f6e\u4e4b\u5ea6\u5916\uff0c\u662f\u4ee5\u4fbf\u518d\u65e0\u754f\u60e7\u4e4b\u5fc3\uff0c\u65b9 \u624d\u90a3\u79cd\u754f\u7f29\u4e4b\u6001\uff0c\u6b64\u523b\u4fbf\u4e5f\u4e00\u626b\u800c\u7a7a\u3002 \u3000\u3000\u524d\u884c\u7684\u67af\u7626\u7537\u5b50\u4e11\u6076\u800c\u51b7\u524a\u7684\u9762\u76ee\uff0c\u53c8\u81ea\u5fae\u5fae\u626d\u66f2\u4e00\u4e0b\uff0c\u5634\u89d2\u7adf\u7136\u6cdb\u8d77\u4e00\u4e1d\u68ee\u51b7\u7684\u7b11 \u610f\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u3002\u3002 \u3000\u3000\u201c\u9601\u4e0b\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5374\u5df2\u5c06\u8981\u6210\u4e3a\u6c5f\u6e56\u4e2d\u65e0\u6570\u6b66\u6797\u8c6a\u5927\u7684\u9b41\u9996\uff0c\u771f\u662f\u53ef\u559c\u53ef\u8d3a\u5f97\u5f88\u3002\u201d\u4ed6 \u53e3\u4e2d\u867d\u5728\u8bf4\u7740\u201c\u53ef\u559c\u53ef\u8d3a\u201d\uff0c\u8bed\u6c14\u4e4b\u4e2d\uff0c\u5374\u4ecd\u7136\u6ee1\u542b\u68ee\u51b7\u7684\u5bd2\u610f\uff0c\u54ea\u91cc\u6709\u534a\u5206\u5411\u4eba\u8d3a\u559c\u7684\u610f \u601d\u3002 \u3000\u3000\u4ed6\u8bdd\u58f0\u5fae\u987f\uff0c\u88f4\u73cf\u8fd8\u672a\u6765\u5f97\u53ca\u7b54\u8bed\uff0c\u5374\u89c1\u4ed6\u624b\u5fae\u4e00\u62db\uff0c\u53c8\u81ea\u8bf4\u9053\uff1a\u201c\u51b7\u8001\u5927\uff0c\u4f60\u8fd8\u4e0d\u6765 \u53c2\u89c1\u53c2\u89c1\u672a\u6765\u6c5f\u5357\u7eff\u6797\u7684\u603b\u74e2\u628a\u5b50\uff1f\u3001\u8bdd\u58f0\u672a\u4e86\uff0c\u88f4\u73cf\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u8fdc\u8fdc\u8d70\u5728\u81ea\u5df1\u8eab\u540e\u7684 \u53e6\u4e00\u67af\u7626\u6c49\u5b50\uff0c\u4fbf\u5df2\u7a81\u7136\u73b0\u8eab\u5728\u81ea\u5df1\u773c\u524d\uff0c\u5bd2\u7740\u9762\u5b54\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u9601\u4e0b\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5374\u5df2\u5c06\u8981 \u6210\u4e3a\u6c5f\u6e56\u4e2d\u65e0\u6570\u6b66\u6797\u8c6a\u58eb\u7684\u9b41\u9996\uff0c\u771f\u662f\u53ef\u559c\u53ef\u8d3a\u5f97\u5f88\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u671b\u5411\u53e6\u4e00\u67af\u7626\u6c49\u5b50\u3001\u53c8 \u9053\uff1a\u201c\u4f60\u6211\u5b9e\u5728\u5e94\u8be5\u53c2\u89c1\u53c2\u89c1\u8fd9\u4f4d\u672a\u6765\u6c5f\u752b\u7eff\u6797\u7684\u603b\u74e2\u628a\u5b50\u3002\u201d \u3000\u3000\u4ed6\u7adf\u5c06\u5148\u524d\u90a3\u67af\u7626\u6c49\u5b50\u6240\u8bf4\u7684\u8bdd\uff0c\u4e00\u5b57\u4e0d\u6f0f\u7684\u91cd\u8bf4\u4e86\u4e00\u904d\uff0c\u88f4\u73cf\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6123\uff0c\u4e0d\u77e5\u9053 \u8fd9\u4e24\u4e2a\u9762\u4e0a\u6728\u7136\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\u7684\u8be1\u5f02\u4eba\u7269\uff0c\u7a76\u7adf\u5728\u5f04\u4ec0\u4e48\u7384\u865a\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u60ca\u7591\u4ea4\u96c6\uff0c\u5374\u89c1\u8fd9\u201c\u51b7\u8001\u5927\u201d\u76ee\u5149\u53c8\u81ea\u7f13\u7f13\u8f6c\u5230\u81ea\u5df1\u9762\u4e0a\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u7792\u9601 \u4e0b\u8bf4\uff0c\u6211\u5144\u5f1f\u4e24\u4eba\uff0c\u8fdc\u9053\u800c\u6765\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u770b\u770b\u8fd9\u4f4d\u538b\u5012\u6c5f\u5357\u6240\u6709\u6b66\u6797\u8c6a\u58eb\u7684\u603b\u74e2\u628a\u4e8e\uff0c\u7a76 \u7adf\u662f\u4f55\u7b49\u4eba\u7269\uff1f\u201d \u3000\u3000\u53e6\u4e00\u67af\u7626\u6c49\u5b50\u51b7\u7136\u63a5\u9053\uff1a\u201c\u5982\u4eca\u4e00\u89c1\uff0c\u9601\u4e0b\u679c\u7136\u662f\u82f1\u59ff\u7115\u53d1\uff0c\u4eba\u4e2d\u9f99\u51e4\u3002\u201d\u8fd9\u4e24\u4eba\u8bf4\u8d77 \u8bdd\u6765\uff0c\u65e0\u8bba\u8bdd\u4e2d\u7684\u542b\u610f\u662f\u6b23\u559c\uff0c\u6291\u6216\u662f\u606d\u7ef4\uff0c\u8bed\u6c14\u5374\u5168\u7136\u662f\u51b7\u51b0\u51b0\u5730\u6ca1\u6709\u4e00\u4e1d\u53d8\u5316\uff0c\u662f\u4ee5\u4ed6 \u4eec\u65e0\u8bba\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u4eba\u5bb6\u542c\u6765\uff0c\u90fd\u4f1a\u4e0d\u7531\u81ea\u4e3b\u5730\u6cdb\u8d77\u4e00\u79cd\u96be\u8a00\u7684\u5bd2\u610f\u3002 \u3000\u3000\u88f4\u73cf\u867d\u7136\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u6b64\u523b\u5bf9\u8fd9\u4e24\u4eba\u7684\u6765\u610f\uff0c\u5374\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u832b\u7136\uff0c\u4e5f\u6839\u672c\u4e0d\u77e5\u9053\u8be5 \u5982\u4f55\u56de\u7b54\u4eba\u5bb6\u7684\u8bdd\u3002 \u3000\u3000\u90a3\u201c\u51b7\u8001\u5927\u201d\u5634\u89d2\u6302\u7740\u7684\u68ee\u51b7\u7b11\u610f\uff0c\u7a81\u5730\u4e00\u655b\uff0c\u9762\u8272\u8d8a\u53d1\u9634\u6c89\u4ed6\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u51b7\u67af \u6728\u2014\u2014\u201d\u4ed6\u6545\u610f\u5c06\u8bdd\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u76ee\u5149\u4e00\u779f\u88f4\u73cf\uff0c\u5374\u89c1\u88f4\u73cf\u9762\u4e0a\uff0c\u5e76\u672a\u56e0\u201c\u51b7\u67af\u6728\u201d\u4e09\u5b57\u800c \u751f\u51fa\u60ca\u5413\u4e4b\u610f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u4e3a\u5947\u602a\uff1a\u201c\u96be\u9053\u4ed6\u4ece\u672a\u542c\u8fc7\u6211\u7684\u540d\u5b57\uff0c\u8fd8\u662f\u4ed6\u771f\u7684\u8eab\u6000\u7edd\u6280\uff0c\u662f \u4ee5\u4fbf\u4e0d\u754f\u60e7\u4e8e\u6211\uff1f\u201d\u53e3\u4e2d\u4f7f\u53c8\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u51b7\u67af\u6728\u5374\u6709\u4e00\u4e8b\u60f3\u8981\u8bf7\u6559\uff0c\u9601\u4e0b\u6b64\u756a\u8363\u81ba\u6c5f\u5357 \u6b66\u6797\u9b41\u9996\uff0c\u4e0d\u77e5\u662f\u5426\u88ab\u6c5f\u5357\u6b66\u6797\u540c\u9053\u63a8\u9009\u800c\u51fa\u7684\uff1f\u201d\u4ed6\u6b64\u523b\u5df2\u88ab\u88f4\u73cf\u90a3\u79cd\u5937\u7136\u65e0\u754f\u7684\u6837\u5b50\u6240 \u52a8\uff0c\u662f\u4ee5\u8bf4\u8bdd\u4e4b\u8bed\u6c14\uff0c\u4fbf\u4e5f\u8fdc\u8f83\u548c\u7f13\u7684\u591a\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u88f4\u73cf\u521d\u5165\u6c5f\u6e56\uff0c\u53c8\u4f55\u66fe\u542c\u5230\u201c\u51b7\u67af \u6728\u201d\u4e09\u5b57\uff0c\u662f\u4ee5\u5bf9\u8fd9\u6c5f\u6e56\u4e2d\u4eba\u95fb\u800c\u8272\u53d8\u7684\u540d\u5b57\uff0c\u4fbf\u4e5f\u4e1d\u6beb\u6ca1\u6709\u754f\u60e7\u4e4b\u6001\u3002 \u3000\u3000\u88f4\u73cf\u4e3a\u4e4b\u4e00\u5446\uff0c\u5374\u542c\u53e6\u4e00\u67af\u7626\u6c49\u5b50\u4ea6\u81ea\u4e00\u655b\u5634\u89d2\u7b11\u5bb9\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u51b7\u5bd2\u7af9\u4ea6\u6709\u4e00\u4e8b \u60f3\u8981\u8bf7\u6559\uff0c\u9601\u4e0b\u6b64\u756a\u8363\u81ba\u6c5f\u5357\u6b66\u6797\u9b41\u9996\uff0c\u82e5\u4e0d\u662f\u88ab\u6c5f\u5357\u6b66\u6797\u540c\u9053\u63a8\u9009\u800c\u51fa\uff0c\u90a3\u4e48\u662f\u9601\u4e0b\u7684\u4e00 \u8eab\u827a\u4e1a\uff0c\u5df2\u4f7f\u6c5f\u5357\u6b66\u6797\u4e2d\u6240\u6709\u7684\u82f1\u96c4\u8c6a\u58eb\uff0c\u5fc3\u670d\u53e3\u670d\uff0c\u662f\u4ee5\u4e5f\u6bcb\u5eb8\u5f81\u6c42\u4ed6\u4eec\u7684\u540c\u610f\uff1f\u2018\u88f4\u73cf \u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u5176\u5b9e\u6211\u53c8\u4f55\u5c1d\u540c\u610f\u6b64\u4e8b\u3002\u201d\u53e3\u4e2d\u5450\u5450\u5730\uff0c\u7adf\u81ea\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u51b7\u67af\u6728\u4e0e\u51b7\u5bd2\u7af9\u4e24\u4eba\uff0c\u9f50\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u53cc\u624b\u4e00\u80cc\uff0c\u5fae\u4e00\u62ac\u5934\uff0c\u76ee\u5149\u4ff1\u90fd\u671b\u5728\u5929 \u4e0a\uff0c\u53e3\u4e2d\u5374\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u6211\u5144\u5f1f\u6240\u95ee\u4e4b\u8bdd\uff0c\u8bf7\u9601\u4e0b\u5feb\u4e9b\u7b54\u590d\uff0c\u4e5f\u597d\u8ba9\u6211\u5f1f\u5144\u4e48\u2026\u2026\u563f\u563f\uff0c\u5feb \u4e9b\u53c2\u62dc\u9601\u4e0b\u3002\u201d \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u88f4\u73cf\u53ea\u89c9\u81ea\u5df1\u9762\u988a\u4e4b\u4e0a\uff0c\u70ed\u70d8\u70d8\u5730\uff0c\u50cf\u662f\u53d1\u8d77\u70e7\u6765\uff0c\u624b\u8db3\u5374\u662f\u4e00\u7247\u51b0\u51c9\uff0c \u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u5fc3\u91cc\u6068\u4e0d\u5f97\u90a3\u5434\u9e23\u4e16\u6b64\u523b\u7ad9\u5728\u81ea\u5df1\u8eab\u7554\uff0c\u66ff\u81ea\u5df1\u6765\u53e3\u7b54\u8fd9\u4e24\u4eba\u7684\u8bdd\uff0c\u53c8\u6697 \u6068\u81ea\u5df1\u53e3\u820c\u7b28\u62d9\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u5145\u6ee1\u7f9e\u60ed\u4e4b\u610f\uff0c\u5fd6\u9053\uff1a\u201c\u88f4\u73cf\u5440\u88f4\u73cf\uff0c\u4f60\u6280\u4e0d\u60ca\u4eba\uff0c \u53c8\u65e0\u58f0\u540d\uff0c\u4f60\u662f\u51ed\u7740\u4ec0\u4e48\u8981\u6765\u505a\u6c5f\u5357\u6b66\u6797\u7684\u9b41\u9996\uff0c\u53c8\u600e\u602a\u5f97\u4e86\u4eba\u5bb6\u4f1a\u6765\u76d8\u95ee\u4e8e\u4f60\u3002\u201d \u3000\u3000\u4ed6\u672c\u662f\u751f\u6027\u6781\u4e3a\u5584\u826f\uff0c\u6b63\u76f4\u4e4b\u4eba\uff0c\u6b64\u523b\u5fc3\u4e2d\u53ea\u60f3\u5230\u81ea\u5df1\u5b9e\u5728\u4e0d\u8be5\u6765\u505a\u8fd9\u603b\u74e2\u628a\u5b50\uff0c\u5374\u672a \u60f3\u5230\u8fd9\u4e24\u4eba\u51ed\u7740\u4ec0\u4e48\u8d28\u95ee\u81ea\u5df1\uff0c\u662f\u4ee5\u5fc3\u4e2d\u4f46\u89c9\u7f9e\u6127\uff0c\u5374\u65e0\u607c\u6012\u4e4b\u610f\uff0c\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u624d\u5f85\u8bf4 \u8bdd\uff0c\u54ea\u77e5\u90a3\u51b7\u67af\u6728\u76ee\u5149\u7a81\u5730\u4e00\u5782\uff0c\u51b7\u7136\u53c8\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u4e0d\u613f\u56de\u7b54\u6211\u5144\u5f1f\u4e8c\u4eba\u7684\u8bdd\uff0c\u60f3\u5fc5\u662f \u56e0\u4e3a\u6211\u5f1f\u5144\u4e24\u4eba\u914d\u4e0d\u4e0a\u548c\u672a\u6765\u7684\u6c5f\u5357\u7eff\u6797\u7684\u603b\u74e2\u628a\u5b50\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u4ea6\u81ea\u7f13\u7f13\u5782\u4e0b\u76ee\u5149\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5176\u5b9e\u9601\u4e0b\u4e5f\u4e0d\u5fc5\u81ea\u89c6\u592a\u9ad8\uff0c\u6211\u5144\u5f1f\u4e8c\u4eba\uff0c\u867d\u7136\u65e2 \u975e\u6b66\u6797\u9b41\u9996\uff0c\u4ea6\u975e\u5f3a\u76d7\u5934\u5b50\uff0c\u4f46\u5374\u6bd4\u9601\u4e0b\u8fd9\u79cd\u4e73\u81ed\u672a\u5e72\uff0c\u4e0d\u77e5\u5929\u591a\u9ad8\u5730\u591a\u539a\uff0c\u5374\u53c8\u8981\u539a\u7740\u8138 \u76ae\uff0c\u5e76\u8d77\u623f\u95e8\uff0c\u81ea\u5c01\u4e3a\u6c5f\u5357\u7eff\u6797\u603b\u74e2\u628a\u5b50\u7684\u65e0\u77e5\u7a1a\u5b50\u800c\u7565\u80dc\u4e00\u7b79\u3002\u201d \u3000\u3000\u88f4\u73cf\u5251\u7709\u4e00\u8f69\uff0c\u4f46\u89c9\u5fc3\u4e2d\u6012\u706b\u5927\u4f5c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u4e2a\u4ec0\u4e48\u603b\u74e2\u628a\u5b50\u7684\u4f4d\u5b50\uff0c\u4f60\u4eec\u770b\u5f97\u5341 \u5206\u7a00\u7f55\uff0c\u6211\u5374\u6839\u672c\u672a\u89c1\u5f97\u60f3\u505a\uff0c\u4f60\u5374\u4e3a\u4f55\u5982\u6b64\u8fb1\u9a82\u4e8e\u6211\uff0c\u96be\u9053\u6211\u6709\u4ec0\u4e48\u5730\u65b9\u5f97\u7f6a\u4e86\u4f60\u4eec\u4e0d \u6210\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u5446\u5446\u5730\u671b\u7740\u4ed6\uff0c\u751f\u50cf\u662f\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\u4f3c\u7684\uff0c\u7a81\u5730\u8f6c\u8fc7\u5934\u53bb\uff0c\u9053\uff1a\u51b7\u8001\u5927\uff0c\u4f60\u53ef \u542c\u89c1\u8fd9\u4e0d\u77e5\u5929\u591a\u9ad8\uff0c\u5730\u591a\u539a\u7684\u72c2\u5f92\uff0c\u8bf4\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u8bdd\uff1f\u201c\u51b7\u67af\u6728\u5782\u4e0b\u5934\u53bb\uff0c\u6545\u4f5c\u6c89\u601d\u72b6\u5730 \u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u4ed6\u50cf\u662f\u5728\u8d28\u95ee\u4f60\uff0c\u65b9\u624d\u4e3a\u4f55\u5bf9\u4ed6\u8bf4\u51fa\u90a3\u822c\u65e0\u793c\u7684\u8bdd\u6765\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u76ee\u5149\u4e00\u8f6c\uff0c\u51db\u7136\u671b\u5411\u88f4\u73cf\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u662f\u5426\u5bf9\u5728\u4e0b\u65b9\u624d\u6240\u8bf4\u7684\u8bdd\uff0c\u4ecd\u4e3a\u4e0d\u6ee1\uff0c\u90a3 \u4e48\u2014\u2014\u9601\u4e0b\u60f3\u5fc5\u662f\u8981\u60e9\u6212\u60e9\u6212\u5728\u4e0b\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u867d\u89c9\u81ea\u5df1\u672c\u5c31\u4e0d\u5e94\u6765\u505a\u8fd9\u603b\u74e2\u628a\u5b50\uff0c\u4f46\u4ed6\u4e00\u751f\u4e4b\u4e2d\u6700\u4e0d\u80fd\u5fcd\u53d7\u522b\u4eba\u7684\u8f7b\u8d31\u8fb1\u9a82\uff0c\u6b64 \u523b\u4e0d\u7981\u6012\u706b\u9ad8\u5f20\uff0c\u8f69\u7709\u6012\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f60\u4eec\u6df1\u591c\u4e4b\u4e2d\uff0c\u5c06\u6211\u5f15\u81f3\u6b64\u5730\uff0c\u5982\u6b64\u620f \u5f04\u4e8e\u6211\uff0c\u7a76\u7adf\u662f\u4e3a\u7684\u4ec0\u4e48\uff1f\u54fc\u54fc\uff0c\u4f60\u4eec\u867d\u7136\u65e0\u804a\uff0c\u6211\u5374\u72af\u4e0d\u7740\u548c\u65e0\u804a\u4e4b\u4eba\u8bf4\u8bdd\u3002\u201d\u8eab\u8eaf\u4e00 \u8f6c\uff0c\u5927\u6b65\u8d70\u4e86\u56de\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u811a\u6b65\u65b9\u81ea\u79fb\u52a8\u534a\u6b65\uff0c\u773c\u524d\u4e00\u82b1\uff0c\u8fd9\u67af\u7626\u5982\u67f4\uff0c\u540d\u7b26\u5176\u5b9e\u7684\u51b7\u67af\u6728\u3001\u51b7\u5bd2\u7af9\uff0c\u7adf\u53c8 \u5e76\u80a9\u6321\u4f4f\u4ed6\u7684\u53bb\u8def\uff0c\u8eab\u5f62\u4e4b\u5feb\uff0c\u6709\u5982\u98d8\u98ce\u95ea\u7535\uff0c\u7ade\u4e0d\u77e5\u4ed6\u4eec\u7684\u8eab\u5f62\u662f\u5982\u4f55\u800c\u52a8\u7684\u3002 \u3000\u3000\u88f4\u73cf\u811a\u6b65\u987f\u5904\uff0c\u6012\u9053\uff1a\u201c\u4f60\u4eec\u5e74\u7eaa\u6709\u4e86\u4e00\u628a\uff0c\u505a\u8d77\u4e8b\u6765\uff0c\u5374\u6709\u5982\u987d\u7ae5\u4e00\u822c\uff0c\u65e2\u4e0d\u8bf4\u51fa\u6765 \u610f\uff0c\u6b64\u523b\u5374\u6321\u4f4f\u6211\u7684\u53bb\u8def\uff0c\u4f60\u4eec\u7a76\u7adf\u8981\u5bf9\u6211\u600e\u4e48\u6837\uff0c\u5c31\u8bf7\u2014\u2014\u201d\u51b7\u5bd2\u7af9\u51b7\u7b11\u4e00\u58f0\uff0c\u622a\u65ad\u4e86\u4ed6 \u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u5144\u5f1f\u65b9\u624d\u95ee\u4f60\u7684\u8bdd\uff0c\u4f60\u82e5\u4e0d\u5feb\u4e9b\u7b54\u590d\uff0c\u54fc\u54fc\uff0c\u53ea\u6015\u9601\u4e0b\u53c8\u8981\u9ad8\u5347\u4e00\u7ea7\u4e86\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u597d\u50cf\u4e0d\u89e3\u5730\u4e00\u76b1\u7709\u5934\uff0c\u95ee\u9053\uff1a\u201c\u4eba\u5bb6\u6b64\u523b\u5df2\u662f\u6c5f\u5357\u7eff\u6797\u7684\u603b\u74e2\u628a\u5b50\uff0c\u518d\u5347\u4e00\u7ea7\uff0c \u5374\u5347\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u518d\u5347\u4e00\u7ea7\uff0c\u5c31\u8981\u5347\u5230\u4e5d\u5e7d\u5730\u5e9c\u53bb\u5f53\u960e\u738b\u4e86\u3002\u201d\u8fd9\u67af\u6728\u3001\u5bd2\u7af9\u4e24 \u4eba\uff0c\u4e00\u6bcd\u5b6a\u751f\uff0c\u81ea\u5e7c\u5fc3\u610f\u76f8\u901a\uff0c\u6b64\u523b\u8bf4\u8d77\u8bdd\u6765\uff0c\u4e00\u5531\u4e00\u7b54\uff0c\u5982\u5728\u5531\u53cc\u7c27\u4e00\u822c\uff0c\u6709\u65f6\u8bf4\u8bdd\u51b7\u5cfb \u65e0\u6bd4\uff0c\u6709\u65f6\u5374\u53c8\u5b9b\u5982\u513f\u620f\uff0c\u5b9e\u5728\u662f\u4ee4\u4eba\u96be\u4ee5\u6349\u6478\u3002\u88f4\u73cf\u82e5\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\uff0c\u4ed6\u4fbf\u4f1a\u77e5\u9053\u8fd9\u5144\u5f1f \u4e8c\u4eba\u4e4b\u884c\u4e8b\u4e4b\u96be\u6d4b\uff0c\u5728\u6c5f\u6e56\u4e2d\u65e9\u5df2\u5927\u5927\u6709\u540d\uff0c\u6b66\u6797\u4e2d\u4eba\u6349\u8d77\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6765\uff0c\u8c01\u4e0d\u6697\u6697\u5927\u76b1 \u7709\u5934\uff0c\u53ea\u662f\u88f4\u73cf\u521d\u5165\u6c5f\u6e56\uff0c\u53c8\u4ece\u672a\u6709\u4eba\u5bf9\u4ed6\u8bf4\u8fc7\u8fd9\u4e9b\u6b66\u6797\u638c\u6545\uff0c\u6b64\u523b\u53ea\u89c9\u5f97\u8fd9\u4e24\u4eba\u53ef\u538c\u5df2 \u6781\uff0c\u5374\u4e0d\u77e5\u9053\u81ea\u5df1\u9762\u5bf9\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u6027\u547d\u5df2\u5982\u60ac\u5375\u3002 \u3000\u3000\u4ed6\u5251\u7709\u6012\u8f69\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u7684\u6b66\u529f\u65e2\u4e0d\u80fd\u4f7f\u6c5f\u5357\u6b66\u6797\u8c6a\u58eb\u5fc3\u670d\uff0c\u4eba\u5bb6\u4e5f\u6ca1 \u6709\u63a8\u9009\u6211\u6765\u505a\u8fd9\u603b\u74e2\u628a\u5b50\uff0c\u6211\u81ea\u5df1\u5fc3\u91cc\u4e5f\u4e0d\u613f\u505a\uff0c\u53ef\u662f\u5374\u504f\u504f\u6709\u4eba\u975e\u8981\u8bf7\u6211\u6765\u505a\u4e0d\u53ef\uff0c\u4f60\u4e24 \u4eba\u8981\u662f\u770b\u7740\u773c\u7ea2\uff0c\u4e0d\u59a8\u53eb\u2014\u2014\u201d\u51b7\u5bd2\u7af9\u53c8\u81ea\u9634\u51c4\u51c4\u4e00\u58f0\u51b7\u7b11\uff0c\u518d\u5ea6\u622a\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u5982\u6b64\u8bf4\uff0c\u90a3\u597d\u6781\u4e86\uff0c\u53ef\u662f\u2014\u2014\u201d\u4ed6\u53c8\u4e00\u987f\u8bdd\u58f0\uff0c\u8f6c\u9996\u9053\uff1a\u201c\u51b7\u8001\u5927\uff0c\u4f60\u4e5f\u662f \u6c5f\u5357\u6b66\u6797\u4e2d\u4eba\uff0c\u4f60\u8d5e\u4e0d\u8d5e\u6210\u8fd9\u4f4d\u2018\u88f4\u5927\u5148\u751f\u2019\u6765\u505a\u54b1\u4eec\u7684\u603b\u74e2\u628a\u5b50\u5462\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u6545\u610f\u5446\u4e86\u4e00\u5446\uff0c\u7136\u540e\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u6211\u6709\u70b9\u4e0d\u5927\u613f\u610f\u3002\u201d\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u90a3\u4e48\u53c9\u8be5 \u600e\u4e48\u529e\u5462\uff1f\u201d\u51b7\u67af\u6728\u53c8\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u90a3\u4e48\u8be5\u600e\u4e48\u529e\u5462\uff1f\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u51c4\u51c4\u5730\u5728\u5634\u89d2\u4f5c\u51fa\u4e00\u4e1d\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u613f\u610f\uff0c\u6211\u4e5f\u4e0d\u613f\u610f\uff0c\u53ef\u662f\u5374\u53c8\u6709\u4eba\u975e\u8981 \u4ed6\u505a\u4e0d\u53ef\uff0c\u8fd9\u4e8b\u786e\u662f\u6709\u4e9b\u96be\u529e\uff0c\u6211\u770b\u2014\u2014\u51b7\u8001\u5927\uff0c\u6211\u4eec\u53ea\u6709\u628a\u4ed6\u5f04\u6b7b\u7b97\u4e86\u3002\u201d\u8bed\u6c14\u5e73\u9759\uff0c\u58f0 \u8c03\u4e5f\u4ecd\u7136\u5168\u65e0\u9ad8\u4f4e\u987f\u632b\uff0c\u53e3\u4e2d\u867d\u5728\u8bf4\u7740\u6709\u5173\u53e6\u4e00\u4eba\u7684\u751f\u6b7b\u4e4b\u4e8b\uff0c\u53e3\u6c14\u5374\u50cf\u662f\u5728\u8bf4\u7740\u5bb6\u5e38\u4e00 \u6837\uff0c\u522b\u4eba\u7684\u751f\u547d\uff0c\u5728\u4ed6\u773c\u4e2d\uff0c\u751f\u50cf\u662f\u6ca1\u6709\u4efb\u95ee\u4ef7\u503c\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u4e00\u51db\uff0c\u54ea\u77e5\u90a3\u51b7\u67af\u6728\u7a81\u5730\u4e0d\u4f4f\u6447\u8d77\u624b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u6709\u4e9b\u4e0d\u59a5\u3002\u201d\u51b7\u5bd2\u7af9 \u9053\uff1a\u201c\u600e\u5730\uff1f\u201d\u51b7\u67af\u6728\u9053\uff1a\u201c\u4eba\u5bb6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4f60\u5c31\u5c06\u4eba\u5bb6\u5f04\u6b7b\uff0c\u4e0d\u662f\u592a\u53ef\u60dc\u4e86\u4e9b\u5417\uff1f\u201d\u51b7\u5bd2 \u7af9\u9053\uff1a\u90a3\u4e48\u53c8\u5f53\u600e\u5730\uff1f\u201c\u51b7\u67af\u6728\u6545\u610f\u6c89\u601d\u534a\u664c\uff0c\u7a81\u4ed6\u8bf4\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\uff0c\u6211\u8fd9\u5144\u5f1f\u60f3\u5f04\u6b7b \u4f60\uff0c\u4f60\u770b\u8be5\u600e\u4e48\u529e\u5462\uff1f\u6211\u770b\u4f60\u8fd8\u662f\u8d81\u65e9\u8d70\u4e86\u7b97\u4e86\uff0c\u4f60\u8981\u4e0d\u5f53\u90a3\u603b\u74e2\u628a\u5b50\uff0c\u6211\u5144\u5f1f\u4e5f\u5c31\u4e0d\u4f1a\u8981 \u5f04\u6b7b\u4f60\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u867d\u7136\u4e0d\u613f\u610f\u88ab\u90a3\u795e\u624b\u6218\u98de\u5229\u7528\uff0c\u6765\u5f53\u8fd9\u603b\u74e2\u628a\u5b50\uff0c\u4f46\u6b64\u523b\u542c\u4e86\u8fd9\u51b7\u67af\u6728\u7684\u8bdd\uff0c \u5374\u4e00\u633a\u80f8\u819b\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6b64\u8bdd\uff0c\u6211\u672c\u975e\u4e00\u5b9a\u8981\u6765\u5f53\u8fd9\u603b\u74e2\u628a\u5b50\uff0c\u4f46\u4f60\u8bf4\u4e86\u8fd9\u8bdd\uff0c\u6211 \u4eca\u65e5\u5374\u662f\u975e\u5f53\u4e0d\u53ef\u4e86\u3002\u201d\u53cc\u81c2\u4e00\u5206\uff0c\u60f3\u5206\u5f00\u4e24\u4eba\uff0c\u4ece\u4e2d\u95f4\u7a7f\u8fc7\u53bb\uff0c\u54ea\u77e5\u89e6\u624b\u4e4b\u5904\uff0c\u51b0\u51c9\u575a \u786c\uff0c\u7adf\u7136\u6709\u5982\u7cbe\u94a2\u3002\u4ed6\u5fc3\u4e2d\u6697\u5403\u4e00\u60ca\uff0c\u7f29\u624b\u9000\u6b65\uff0c\u5374\u542c\u90a3\u51b7\u67af\u6728\u53c8\u81ea\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b \u82e5\u80fd\u5c06\u6211\u5144\u5f1f\u4e8c\u4eba\u7684\u8eab\u5f62\u63a8\u5f00\u534a\u6b65\uff0c\u90a3\u4e48\u6211\u5f1f\u5144\u4e8c\u4eba\u4e0d\u4f46\u7acb\u523b\u8ba9\u9601\u4e0b\u56de\u53bb\u5b89\u606f\uff0c\u800c\u4e14\u5230\u4e86\u9601 \u4e0b\u6b63\u5f0f\u5145\u4efb\u6c5f\u5357\u7eff\u6797\u603b\u74e2\u628a\u5b50\u65f6\u5019\uff0c\u6211\u5f1f\u5144\u4e8c\u4eba\u5fc5\u5b9a\u9996\u5148\u524d\u6765\u9053\u8d3a\uff0c\u5426\u5219\u2014\u2014\u54fc\uff01\u201d \u3000\u3000\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u8fd9\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e2d\u7684\u67af\u6728\u51b7\u8001\u5927\uff0c\u7684\u786e\u4e0d\u6127\u4e3a\u4e45\u4eab\u76db\u540d \u7684\u6b66\u6797\u4eba\u7269\uff0c\u65b9\u624d\u88f4\u73cf\u4f38\u624b\u65b9\u81ea\u89e6\u53ca\u4ed6\u7684\u80a9\u8180\uff0c\u4ed6\u4fbf\u77e5\u9053\u8fd9\u5c11\u5e74\u6b66\u529f\u5e73\u5e38\uff0c\u751a\u81f3\u6beb\u65e0\u6b66\u529f\uff0c \u5fc3\u4e2d\u867d\u5728\u5947\u602a\uff0c\u6b64\u4eba\u600e\u4f1a\u505a\u8d77\u6c5f\u5357\u7eff\u6797\u9053\u7684\u603b\u74e2\u628a\u5b50\u6765\uff0c\u4f46\u5fc3\u4e2d\u5374\u5df2\u518d\u65e0\u65b9\u624d\u90a3\u79cd\u5bf9\u8fd9\u5c11\u5e74 \u7684\u6b66\u529f\u83ab\u6d4b\u9ad8\u6df1\u7684\u611f\u89c9\uff0c\u662f\u4ee5\u4ed6\u6b64\u523b\u65b9\u81ea\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u56e0\u4e3a\u4ed6\u5df2\u660e\u77e5\u88f4\u73cf\u7edd\u65e0\u63a8\u52a8\u81ea\u5df1\u7684 \u8eab\u5f62\u7684\u53ef\u80fd\u3002 \u3000\u3000\u88f4\u73cf\u65b9\u624d\u4e00\u89e6\u4e4b\u4e0b\uff0c\u53c8\u4f55\u5c1d\u4e0d\u77e5\u9053\u81ea\u5df1\u82e5\u60f3\u63a8\u5f00\u8fd9\u4e24\u4eba\uff0c\u7b80\u76f4\u6709\u5982\u873b\u8713\u53bb\u64bc\u77f3\u67f1\uff0c\u4f46\u4ed6 \u751f\u6027\u5b81\u6298\u6bcb\u5c48\uff0c\u53eb\u4ed6\u4fef\u9996\u8ba4\u8f93\uff0c\u5374\u662f\u4e07\u4e07\u505a\u4e0d\u5230\u7684\u4e8b\uff0c\u5f53\u4e0b\u5251\u7709\u8f69\u5904\uff0c\u53e3\u4e2d\u5927\u559d\u4e00\u58f0\uff0c\u75be\u4f38 \u53cc\u638c\uff0c\u5411\u8fd9\u51b7\u6c0f\u5144\u5f1f\u690e\u53bb\u3002 \u3000\u3000\u624b\u638c\u89e6\u5904\uff0c\u5fc3\u4e0b\u4e0d\u7981\u53c8\u4e3a\u4e00\u60ca\uff0c\u539f\u6765\u4ed6\u6b64\u756a\u7adf\u7136\u89c9\u5f97\u8fd9\u51b7\u6c0f\u5144\u5f1f\u4e8c\u4eba\u7684\u8eab\u8eaf\uff0c\u4e0d\u518d\u575a\u5982 \u7cbe\u94a2\uff0c\u53cd\u800c\u8f6f\u7ef5\u7ef5\u5730\u6709\u5982\u68c9\u82b1\u4e00\u822c\uff0c\u4f46\u5374\u4e1d\u6beb\u6ca1\u6709\u7740\u529b\u4e4b\u5904\uff0c\u81ea\u5df1\u867d\u5df2\u5c06\u5168\u8eab\u7684\u529b\u6c14\uff0c\u90fd\u8d2f \u6ce8\u5230\u53cc\u638c\u4e0a\uff0c\u4f46\u8fd9\u80a1\u529b\u6c14\u7528\u5230\u4eba\u5bb6\u8eab\u4e0a\uff0c\u5374\u50cf\u662f\u77f3\u6c89\u5927\u6d77\uff0c\u8fde\u4e00\u4e1d\u56de\u5e94\u90fd\u6ca1\u6709\u3002\u62ac\u76ee\u4e00\u671b\uff0c \u53ea\u89c1\u8fd9\u51b7\u6c0f\u5144\u5f1f\u4e8c\u4eba\uff0c\u9762\u4e0a\u4ecd\u7136\u6728\u65e0\u8868\u60c5\uff0c\u4e5f\u6ca1\u6709\u534a\u5206\u663e\u51fa\u8d39\u529b\u7684\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u4e00\u60ca\u4e4b\u4e0b\uff0c\u4fbf\u60f3\u7f29\u56de\u624b\u638c\uff0c\u4f46\u5c31\u5728\u4ed6\u624b\u638c\u89e6\u5230\u4eba\u5bb6\u8eab\u8eaf\u7684\u8fd9\u4e00\u5239\u90a3\uff0c\u51b7\u6c0f\u53cc\u6728\u7684\u8eab \u4e0a\uff0c\u7a81\u5730\u4f20\u51fa\u4e00\u80a1\u70ed\u529b\uff0c\u7adf\u5c06\u88f4\u73cf\u7684\u4e00\u53cc\u624b\u638c\u5438\u4f4f\u3002 \u3000\u3000\u88f4\u73cf\u5927\u60ca\u4e4b\u4e0b\uff0c\u53f3\u817f\u540e\u66f2\uff0c\u5de6\u817f\u633a\u76f4\uff0c\u524d\u63a8\u7684\u529b\u9053\uff0c\u6539\u4e3a\u540e\u64a4\u4e4b\u529b\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u80a1\u70ed\u529b\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u4fbf\u53c8\u52a0\u5f3a\u6570\u500d\uff0c\u88f4\u73cf\u4f46\u89c9\u81ea\u5df1\u7684\u4e00\u53cc\u624b\u81c2\uff0c\u7adf\u7136\u6709\u82e5\u7f6e\u4e8e\u6d2a \u7089\uff0c\u70ed\u8fa3\u8fa3\u5730\u70e7\u4eba\u5fc3\u91cc\uff0c\u81ea\u5df1\u7684\u5168\u8eab\u6c14\u529b\uff0c\u7adf\u4e5f\u968f\u7740\u8fd9\u80a1\u9010\u6e10\u52a0\u5f3a\u7684\u70ed\u529b\uff0c\u4e00\u5206\u4e00\u5206\u5730\u5728\u65e0 \u5f62\u4e2d\u6d88\u53bb\u3002 \u3000\u3000\u70ed\u529b\u8d8a\u5f3a\uff0c\u4ed6\u529b\u6c14\u8d8a\u5f31\uff0c\u751a\u81f3\u8fde\u53cc\u817f\u90fd\u53d8\u5f97\u865a\u98d8\u98d8\u5730\uff0c\u8fde\u7ad9\u90fd\u65e0\u6cd5\u7ad9\u7a33\uff0c\u53f3\u81c2\u4e4b\u4e0a\uff0c\u66f4 \u662f\u5176\u75db\u5f7b\u9aa8\uff0c\u751f\u50cf\u662f\u6709\u65e0\u6570\u6839\u81ea\u706b\u4e2d\u53d6\u51fa\u7684\u5c16\u9488\uff0c\u63d2\u5728\u81ea\u5df1\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u53f3\u81c2\u7684\u4f24\u52bf\uff0c\u672c\u6765\u75ca\u6108\uff0c\u65b9\u624d\u867d\u56e0\u60ca\u6050\u548c\u6124\u6068\uff0c\u662f\u4ee5\u5fd8\u53bb\u4e86\u81c2\u4e0a\u7684\u75bc\u75db\uff0c\u4f46\u6b64\u523b \u4ed6\u4e00\u6709\u611f\u89c9\uff0c\u4fbf\u89c9\u75db\u4eba\u5fc3\u9aa8\u3002 \u3000\u3000\u51b7\u67af\u6728\u68ee\u51b7\u7684\u76ee\u5149\uff0c\u65e0\u52a8\u4e8e\u8877\u5730\u5728\u4ed6\u9762\u4e0a\u4e00\u8f6c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u600e\u5730\u5373\u5c06\u8363\u4efb\u6c5f\u5357\u7eff\u6797\u9b41 \u9996\u7684\u88f4\u5927\u5148\u751f\uff0c\u8fde\u6211\u5144\u5f1f\u4e8c\u4eba\u7ad9\u7740\u7684\u8eab\u5f62\u90fd\u65e0\u6cd5\u63a8\u52a8\uff0c\u54fc\u54fc\uff0c\u6211\u770b\u4f60\u8fd9\u603b\u74e2\u628a\u5b50\uff0c\u4e0d\u5f53\u4e5f \u7f62\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u89c1\u5230\u88f4\u73cf\u9762\u76ee\u4e4b\u4e0a\uff0c\u6ee1\u662f\u75db\u6614\u4e4b\u8272\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u201c\u4e24\u6781\u7384 \u529f\u201d\uff0c\u5df2\u4f7f\u8fd9\u5c11\u5e74\u53d7\u5230\u65e0\u6bd4\u7684\u75db\u82e6\uff0c\u4fbf\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5bb6\u4e8c\u5f1f\u867d\u7136\u813e\u6c14\u8f83\u4e3a\u574f\u4e9b\uff0c\u6211\u51b7\u67af\u6728 \u5374\u662f\u4e16\u4e0a\u6700\u4ec1\u6148\u4e4b\u4eba\uff0c\u773c\u89c1\u9601\u4e0b\u5982\u6b64\u75db\u82e6\uff0c\u5b9e\u5728\u4e8e\u5fc3\u4e0d\u5fcd\uff0c\u5509\u4e00\u4e00\u5176\u5b9e\u9601\u4e0b\u53ea\u8981\u53d1\u8a93\u518d\u4e0d\u5b58 \u5f53\u90a3\u603b\u74e2\u628a\u5b50\u4e4b\u5fc3\uff0c\u6211\u4fbf\u7acb\u523b\u653e\u9601\u4e0b\u56de\u53bb\uff0c\u5509\u2014\u2014\u8fd9\u79cd\u706b\u70e7\u6bdb\u71ce\u7684\u6ecb\u5473\uff0c\u53ef\u771f\u4e0d\u597d\u53d7\u5440\u3002\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u53f9\u6c14\u4e24\u58f0\uff0c\u505a\u51fa\u4e00\u526f\u60b2\u5929\u60af\u4eba\u7684\u6837\u5b50\u6765\uff0c\u88f4\u73cf\u542c\u5728\u8033\u91cc\uff0c\u5374\u6709\u5982\u4e07\u5251\u94bb\u5fc3\u4e00\u822c\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4ecd\u7136\u54ac\u7d27\u7259\u6839\uff0c\u7edd\u4e0d\u547b\u541f\u534a\u664c\u3002\u8ba9\u8fd9\u5014\u5f3a\u7684\u5c11\u5e74\u8bf4\u53e5\u6c42\u9976\u7684\u8bdd\uff0c\u771f\u6bd4\u6740\u6b7b\u4ed6\u8fd8\u8981 \u56f0\u96be\u5341\u500d\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u51b7\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u51b7\u8001\u5927\u6015\u4f60\u70ed\u5f97\u96be\u53d7\uff0c\u6211\u51b7\u8001\u4e8c\u53c8\u4f55\u82e6\u6765\u505a\u6076\u4eba\uff0c\u8fd8\u662f\u8ba9\u4f60\u51c9 \u5feb\u51c9\u5feb\u5427\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u88f4\u73cf\u4fbf\u89c9\u5f97\u81ea\u5df1\u53cc\u624b\u89e6\u5904\uff0c\u500f\u7136\u70d8\u94c1\u53d8\u4e3a\u7384\u51b0\uff0c\u81ea\u5df1\u7684\u5168\u8eab\uff0c\u4e5f\u50cf \u662f\u7f6e\u8eab\u51b0\u7a96\u3002 \u3000\u3000\u6597\u7136\u4e4b\u95f4\uff0c\u4e00\u51b7\u4e00\u70ed\uff0c\u51b7\u70ed\u4e4b\u95f4\uff0c\u76f8\u53bb\u4e07\u500d\uff0c\u88f4\u73cf\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u5168\u8eab\u9aa8\u8282\u4ea4\u63a5\u4e4b \u5904\uff0c\u90fd\u4eff\u4f5b\u88ab\u4eba\u63d2\u4e0a\u4e00\u6280\u51b0\u9488\uff0c\u76f4\u6bd4\u4e16\u4e0a\u4efb\u4f55\u9177\u5211\uff0c\u8fd8\u8981\u75db\u82e6\u5343\u4e07\u500d\uff0c\u4f46\u4ed6\u5374\u4ecd\u7136\u54ac\u7259\u5fcd\u53d7 \u7740\uff0c\u867d\u7136\u4ed6\u77e5\u9053\u81ea\u5df1\u518d\u4e5f\u4e0d\u80fd\u5fcd\u53d7\u591a\u4e45\u4e86\u3002 \u3000\u3000\u51b7\u6c57\uff0c\u4e00\u6ef4\u4e00\u6ef4\u5730\u7531\u4ed6\u7684\u989d\u4e0a\u6d41\u4e86\u4e0b\u6765\uff0c\u63a5\u7740\uff0c\u4ed6\u5168\u8eab\u5f00\u59cb\u4e0d\u4f4f\u5730\u98a4\u6296\uff0c\u7259\u9f7f\u4e5f\u4e3a\u4e4b\u6253 \u8d77\u6218\u6765\uff0c\u4f46\u4ed6\u7684\u76ee\u5149\uff0c\u5374\u4ecd\u7136\u6beb\u4e0d\u754f\u60e7\u5730\u77aa\u5e84\u8fd9\u51b7\u6c0f\u5144\u5f1f\u7684\u8138\u4e0a\uff0c\u751f\u50cf\u662f\u5728\u544a\u8bc9\u4ed6\u4eec\uff1a\u201c\u4f60 \u7eb5\u7136\u80fd\u4ee4\u6211\u8eab\u4f53\u75db\u82e6\uff0c\u5374\u65e0\u6cd5\u4ee4\u6211\u5fc3\u7075\u75db\u82e6\u3002\u4f60\u7eb5\u7136\u80fd\u591f\u5c06\u6211\u7acb\u5373\u6740\u6b7b\uff0c\u53ef\u662f\u4f60\u82e5\u8981\u6211\u8bf4\u53e5 \u6c42\u9976\u7684\u8bdd\uff0c\u5374\u662f\u518d\u4e5f\u4f11\u60f3\uff01\u201d\u90a3\u51b7\u8c37\u53cc\u6728\u4ea6\u4e0d\u7981\u5728\u5fc3\u4e2d\u6697\u8d5e\u4e00\u58f0\uff1a\u201c\u597d\u786c\u7684\u6c49\u5b50\u3002\u201d\u4f46\u5fc3\u4e2d \u5374\u66f4\u5b58\u4e0b\u9664\u53bb\u6b64\u4eba\u4e4b\u5fc3\uff0c\u53d1\u51fa\u7684\u5185\u529b\uff0c\u4e5f\u66f4\u52a0\u91cd\u4e86\u3002 \u3000\u3000\u53c8\u6371\u8fc7\u7247\u523b\uff0c\u88f4\u73cf\u5fc3\u4e2d\u65b9\u81ea\u6697\u9053\u4e00\u58f0\uff1a\u201c\u7f62\u4e86\u3002\u201d\u773c\u524d\u4eff\u4f5b\u89c1\u5230\u6b7b\u4ea1\u7684\u8138\uff0c\u6b63\u5f53\u5934\u5411\u4ed6 \u538b\u4e86\u4e0b\u6765\u3002\u8fd9\u65f6\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u63a0\u8fc7\u4e00\u9635\u96be\u8a00\u7684\u60b2\u54c0\uff0c\u4e3a\u4e4b\u6084\u7136\u5408\u4e0a\u773c\u775b\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6587\u742a\uff0c \u6cf8\u73cd\uff0c\u4f60\u4eec\u4e0d\u77e5\u9053\uff0c\u6211\u518d\u4e5f\u770b\u4e0d\u5230\u4f60\u4eec\u4e86\u3002\u201d\u4ed6\u60b2\u54c0\u5730\u53f9\u606f\u7740\uff0c\u8fd9\u5014\u5f3a\u7684\u5c11\u5e74\uff0c\u5e76\u4e0d\u754f\u60e7\u6b7b \u4ea1\uff0c\u800c\u4ec5\u662f\u89c9\u5f97\u81ea\u5df1\u8fd9\u4e00\u751f\u7684\u751f\u547d\uff0c\u7adf\u662f\u5982\u6b64\u77ed\u4fc3\u800c\u5e73\u6de1\uff0c\u6ca1\u6709\u4e00\u4ef6\u80fd\u591f\u503c\u5f97\u81ea\u5df1\u9a84\u50b2\u7684 \u4e8b\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u5c31\u53ea\u8fd9\u4e00\u4e00\u526f\u50b2\u9aa8\uff0c\u5df2\u8db3\u591f\u4ee4\u4ed6\u81ea\u50b2\u7684\u4e86\u3002 \u3000\u3000\u518d\u4ee4\u4ed6\u96be\u4ee5\u7791\u76ee\u7684\u662f\uff0c\u4ed6\u89c9\u5f97\u4ed6\u6b20\u4e86\u8bb8\u591a\u4eba\u7684\u6069\u60c5\uff0c\u800c\u5c06\u6c38\u8fdc\u65e0\u6cd5\u62a5\u7b54\uff0c\u4ed6\u773c\u524d\u4f3c\u4e4e\u53c8 \u6cdb\u8d77\u90a3\u5634\u91cc\u9576\u7740\u4e09\u7c92\u91d1\u7259\u7684\u80d6\u5b50\u7684\u8eab\u5f71\uff0c\u8fd9\u4e00\u679a\u5927\u997c\u7684\u65bd\u4e0e\uff0c\u5df2\u4f7f\u4ed6\u6c38\u751f\u96be\u5fd8\uff0c\u4f46\u90a3\u4e9b\u66fe\u7ecf \u8feb\u5bb3\u8fc7\u4ed6\u7684\u4eba\uff0c\u4ed6\u5374\u5168\u7136\u6ca1\u6709\u8bb0\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u4eba\u4eec\u4e34\u6b7b\u4e4b\u524d\u7684\u611f\u89c9\uff0c\u8be5\u662f\u5341\u5206\u96be\u4ee5\u5fcd\u53d7\u7684\u5427\uff1f\u5c24\u5176\u5f53\u4ed6\u5728\u60cb\u501f\u8fc7\u751f\u547d\u7684\u77ed\u4fc3\uff0c\u548c\u60e6\u5ff5 \u7740\u4e16\u4eba\u7684\u60c5\u91cd\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u867d\u7136\u70ed\u7231\u751f\u547d\uff0c\u5374\u4e5f\u4e0d\u80af\u4e3a\u751f\u547d\u5c48\u670d\uff0c\u53cd\u800c\u9ed8\u9ed8\u63a5\u53d7\u6b7b\u4ea1\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u4ed6\u8eab\u540e\u84e6\u5730\u54cd\u8d77\u4e86\u4e00\u9635\u94f6\u94c3\u822c\u7684\u7b11\u58f0\u3001\u4e00\u4e2a\u5a07\u67d4\u65e0\u6bd4\u7684\u58f0\u97f3\u6e05\u6717\u4ed6\u8bf4\u9053\uff1a\u201c\u51b7\u5927\u53d4\uff0c\u51b7 \u4e8c\u53d4\uff0c\u4f60\u4eec\u5728\u8ddf\u8c01\u804a\u5929\u5440\uff1f\u82e5\u4e0d\u662f\u65b9\u624d\u6211\u8dc3\u8d77\u5728\u79f0\u68a2\u770b\u5230\u8fd9\u91cc\u6709\u4eba\uff0c\u6211\u8fd8\u771f\u4e0d\u77e5\u9053\u4f60\u4eec\u8dd1\u5230 \u8fd9\u91cc\u6765\u4e86\u3002\u201d\u5979\u5a07\u67d4\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u91cc\u98ce\u666f\u771f\u597d\uff0c\u53c8\u6709\u5c0f\u6eaa\uff0c\u53c8\u6709\u7af9\u6797\uff0c\u90a3\u8fb9\u8fd8\u6709 \u4e00\u5ea7\u5c0f\u6865\uff0c\u90a3\u65f6\u6211\u770b\u5230\u4eba\u5bb6\u5199\u7684\u4e00\u53e5\u2018\u5c0f\u6865\u6d41\u6c34\u4eba\u5bb6\u2019\u6211\u5c31\u5947\u602a\uff0c\u5c0f\u6865\uff0c\u6d41\u6c34\u5230\u5904\u90fd\u6709\uff0c\u6709 \u4ec0\u4e48\u503c\u5f97\u541f\u7684\uff0c\u54ea\u77e5\u6c5f\u5357\u7684\u5c0f\u6865\u6d41\u6c34\uff0c\u679c\u771f\u6709\u79cd\u4e0d\u53ef\u5f62\u5bb9\u7684\u7f8e\uff0c\u5440\uff01\u51b7\u5927\u53d4\uff0c\u4f60\u4eec\u771f\u4f1a\u4eab \u798f\uff0c\u5c45\u7136\u8dd1\u5230\u8fd9\u91cc\u6765\u804a\u5929\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5a07\u67d4\u7684\u58f0\u97f3\u53c8\u8bf4\u53c8\u7b11\uff0c\u5b9b\u5982\u73e0\u843d\u7389\u76d8\uff0c\u5600\u5600\u5471\u5471\u4ed6\u8bf4\u4e86\u4e00\u5927\u5957\u3002\u88f4\u73cf\u5c06\u8981\u660f\u8ff7\u7684\u795e \u667a\uff0c\u542c\u4e86\u8fd9\u58f0\u97f3\uff0c\u5374\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6e05\uff0c\u52aa\u529b\u5730\u626d\u8fc7\u5934\u53bb\u4e00\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u8eab\u540e\u4fcf\u7136\u7ad9\u7740\u4e00\u4e2a\u9752 \u7eb1\u5c11\u5973\uff0c\u9752\u5dfe\u633d\u5934\uff0c\u6625\u5c71\u4e3a\u7709\uff0c\u79cb\u6c34\u4e3a\u76ee\uff0c\u6625\u591c\u7684\u665a\u98ce\uff0c\u5439\u5f97\u5979\u7ea4\u7ea4\u8170\u80a2\uff0c\u6709\u5982\u6768\u67f3\uff0c\u4e00\u53cc \u660e\u5a9a\u7684\u773c\u775b\uff0c\u671b\u89c1\u626d\u8fc7\u5934\u6765\u7684\u88f4\u73cf\uff0c\u5374\u50cf\u662f\u7a81\u5730\u5403\u4e86\u4e00\u60ca\uff0c\u8131\u53e3\u9053\uff1a\u201c\u662f\u4f60\uff01\u201d \u3000\u3000\u8fd9\u5a07\u7f8e\u7684\u8eab\u5f71\uff0c\u4e00\u6620\u5165\u88f4\u73cf\u7684\u773c\u5e18\uff0c\u88f4\u73cf\u5b9b\u5982\u5f53\u80f8\u88ab\u4eba\u4e00\u51fb\uff0c\u8111\u6d77\u4e2d\u4e00\u9635\u6655\u7729\uff0c\u51e0\u4e4e\u8fde \u8eab\u53d7\u7684\u75db\u82e6\u90fd\u5fd8\u8bb0\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5728\u8fd9\u76ee\u5149\u76f8\u5bf9\u7684\u4e24\u4eba\u773c\u4e2d\uff1b\u5929\u5730\u90fd\u4eff\u4f5b\u5fd8\u4e86\u989c\u8272\uff0c\u5c0f\u6eaa\u4e2d\u7684\u6d41\u6c34\uff0c\u4e0d\u518d\u4e1c \u6d41\uff0c\u95ea\u70c1\u7684\u661f\u7fa4\uff0c\u4e0d\u518d\u95ea\u70c1\uff0c\u751a\u81f3\u8fde\u90a3\u4e00\u8f6e\u6e05\u8f89\u4e07\u91cc\u7684\u5a75\u5a1f\u660e\u6708\uff0c\u4e5f\u90fd\u5931\u53bb\u539f\u6709\u7684\u5149\u8f89\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5728\u5979\u773c\u4e2d\uff0c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u4fbf\u4ec0\u4e48\u4e5f\u770b\u4e0d\u5230\uff0c\u5728\u4ed6\u773c\u4e2d\uff0c\u9664\u4e86\u5979\u4e4b\u5916\uff0c\u4e5f\u770b\u4e0d\u5230\u522b \u7684\u3002 \u3000\u3000\u5c81\u6708\u7684\u60a0\u957f\uff0c\u60a0\u957f\u7684\u522b\u79bb\uff0c\u522b\u79bb\u7684\u75db\u82e6\uff0c\u75db\u82e6\u7684\u76f8\u601d\uff0c\u5728\u4ed6\u4eec\u76ee\u5149\u76f8\u5bf9\u7684\u8fd9\u4e00\u5239\u90a3\uff0c\u4e5f \u90fd\u6709\u4e86\u8865\u507f\uff0c\u751f\u547d\uff0c\u662f\u591a\u4e48\u5947\u5999\u7684\u4e1c\u897f\u5440\uff1f \u3000\u3000\u90a3\u51b7\u67af\u6728\u4e0e\u51b7\u5bd2\u7af9\u5bf9\u671b\u4e00\u773c\uff0c\u5404\u5404\u888d\u8896\u4e00\u6307\uff0c\u9000\u5f00\u4e09\u6b65\u3002\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u6587\u742a\uff0c\u4f60\u8ba4\u5f97 \u4ed6\uff1f\u201d \u3000\u3000\u4f46\u662f\u90a3\u5c11\u5973\u5374\u6839\u672c\u6ca1\u6709\u542c\u4ed6\u4eec\u7684\u8bdd\uff0c\u4e00\u53cc\u79cb\u6ce2\uff0c\u4ecd\u81ea\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u88f4\u73cf\u8138\u4e0a\u3002 \u3000\u3000\u88f4\u73cf\u4f46\u89c9\u5468\u8eab\u538b\u529b\u4e00\u677e\uff0c\u624b\u638c\u8f6f\u8f6f\u5730\u5782\u4e86\u4e0b\u6765\uff0c\u5168\u8eab\u7684\u9aa8\u8282\uff0c\u4e5f\u50cf\u662f\u5168\u90e8\u677e\u6563\uff0c\u51e0\u4e4e\u518d \u4e5f\u652f\u6301\u4e0d\u4f4f\u81ea\u5df1\u7684\u8eab\u8eaf\uff0c\u8981\u8dcc\u5728\u5730\u4e0a\uff0c\u4f46\u662f\uff0c\u4ed6\u5374\u5947\u8ff9\u822c\u5730\u652f\u6301\u4f4f\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u5c11\u5973\u7684\u4e00\u53cc\u79cb\u6ce2\u4e4b\u4e2d\uff0c\u4eff\u4f5b\u6709\u7740\u4e00\u79cd\u4ee4\u4ed6\u80fd\u591f\u751f\u51fa\u65e0\u6bd4\u52c7\u6c14\u7684\u529b\u91cf\uff0c\u4e3a\u4e86\u8fd9\u4e00\u5bf9 \u773c\u775b\uff0c\u4ed6\u7518\u613f\u5fcd\u53d7\u4e00\u5207\u75db\u82e6\uff0c\u4e5f\u5403\u5c3d\u4e86\u4e00\u5207\u75db\u82e6\uff0c\u4e00\u5e74\u591a\u7684\u98a0\u6c9b\u6d41\u79bb\u3001\u9965\u997f\u3001\u5bd2\u51b7\u3001\u6b3a\u51cc\u3001 \u5931\u671b\u2026\u2026\u4ed6\u90fd\u5fcd\u53d7\u4e86\uff0c\u56e0\u4e3a\uff0c\u4e3a\u7684\u662f\u5979\u3002 \u3000\u3000\u5979\uff0c\u4fbf\u662f\u65f6\u65f6\u523b\u523b\u6d3b\u5728\u88f4\u73cf\u5fc3\u91cc\uff0c\u4e5f\u8ba9\u88f4\u73cf\u65f6\u65f6\u523b\u523b\u6d3b\u5728\u81ea\u5df1\u5fc3\u91cc\u7684\u6a80\u6587\u742a\u3002 \u3000\u3000\u6708\u5149\uff0c\u50cf\u5b69\u5b50\u68a6\u4e2d\u7684\u9ec4\u91d1\uff0c\u8f7b\u67d4\u5730\u6620\u5728\u5979\u8eab\u4e0a\uff0c\u5979\u7f13\u7f13\u5730\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u5411 \u88f4\u73cf\uff0c\u5634\u91cc\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u662f\u4f60\uff0c\u662f\u4f60\uff0c\u771f\u7684\u662f\u4f60\u3002\u201d\u58f0\u97f3\u4e5f\u50cf\u6708\u5149\u4e00\u822c\u7684\u8f7b\u67d4\uff0c\u4e24\u6ef4\u6676\u83b9\u7684 \u773c\u6cea\uff0c\u593a\u76ee\u800c\u51fa\uff0c\u6cbf\u7740\u5979\u5a07\u7f8e\u5982\u82b1\u7684\u9762\u5e9e\u7f13\u7f13\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u773c\u6cea\uff0c\u6709\u65f6\u4e5f\u662f\u8868\u793a\u7740\u5927\u591a\u7684\u559c\u60a6\u5417\uff1f \u3000\u3000\u6708\u5149\uff0c\u5c06\u6a80\u6587\u742a\u7684\u5f71\u5b50\uff0c\u957f\u957f\u5730\u62d6\u5728\u5730\u4e0a\uff0c\u4e8e\u662f\uff0c\u8fd9\u9053\u957f\u957f\u7684\u5f71\u5b50\uff0c\u4fbf\u968f\u7740\u5979\u7f13\u7f13\u79fb\u52a8 \u7684\u811a\u6b65\uff0c\u6e29\u67d4\u5730\u7b3c\u76d6\u5230\u88f4\u73cf\u7684\u811a\u4e0a\uff0c\u817f\u4e0a\u2026\u2026\u201c\u88f4\u73cf\u7684\u817f\uff0c\u5374\u662f\u98a4\u6296\u7740\u7684\uff0c\u8fd9\u867d\u7136\u662f\u56e0\u4e3a\u65b9 \u624d\u90a3\u201d\u51b7\u8c37\u53cc\u6728\u201c\u4e2d\u7684\u67af\u6728\u3001\u5bd2\u7af9\u5728\u4ed6\u8eab\u4e0a\u6240\u65bd\u7684\u5185\u529b\uff0c\u4f7f\u5f97\u4ed6\u4f53\u5185\u5df2\u53d7\u4e86\u6781\u5927\u7684\u4fb5\u8680\uff0c\u800c \u51e0\u4e4e\u65e0\u6cd5\u7ad9\u7a33\u81ea\u5df1\u7684\u8eab\u5f62\uff0c\u5374\u4e5f\u662f\u56e0\u4e3a\u8fd9\u4e00\u4efd\u7a81\u7136\u800c\u6765\uff0c\u4ee4\u4ed6\u81ea\u5df1\u90fd\u513f\u5b50\u4e0d\u80fd\u7f6e\u4fe1\u7684\u559c\u60a6\u548c \u5e78\u798f\uff0c\u4f7f\u5f97\u4ed6\u90a3\u4e00\u9897\u9971\u7ecf\u5fe7\u60a3\u7684\u5fc3\uff0c\u90fd\u4e3a\u4e4b\u98a4\u6296\u8d77\u6765\u3002\u4ed6\u611f\u89c9\u5230\u6a80\u6587\u742a\u7684\u5f71\u5b50\uff0c\u5728\u4ed6\u8eab\u4e0a\u7b3c \u76d6\u7684\u5730\u65b9\u8d8a\u6765\u8d8a\u5927\u3002\u4ed6\u4e5f\u80fd\u770b\u5230\uff0c\u6a80\u6587\u742a\u5a07\u7f8e\u5982\u82b1\u7684\u9762\u989c\uff0c\u8ddd\u79bb\u81ea\u5df1\u8d8a\u6765\u8d8a\u8fd1\uff0c\u8fd9\u5a07\u7f8e\u7684\u9762 \u989c\uff0c\u5728\u4ed6\u6a21\u7cca\u7684\u53cc\u773c\u4e2d\uff0c\u6709\u5982\u70df\u4e2d\u828d\u836f\uff0c\u96fe\u91cc\u7261\u4e39\uff0c\u968f\u7740\u68a6\u822c\u8f7b\u67d4\u7684\u5fae\u98ce\uff0c\u5189\u5189\u5439\u5411\u81ea\u5df1\u7684 \u6000\u62b1\u3002\u4f46\u662f\uff0c\u4ed6\u5374\u4e0d\u6562\u4f38\u51fa\u53cc\u81c2\u53bb\u8fce\u63a5\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u6015\u8fd9\u4ec5\u4ec5\u662f\u4e00\u573a\u5e7b\u68a6\u3002\u53ea\u8981\u81ea\u5df1\u7a0d\u5fae\u79fb\u52a8 \u4e00\u4e0b\u8eab\u5f62\uff0c\u4fbf\u4f1a\u5c06\u8fd9\u573a\u5e78\u798f\u7684\u5e7b\u68a6\u60ca\u788e\u3002\u6f7a\u6f7a\u7684\u6d41\u6c34\u58f0\uff0c\u6b64\u523b\u542c\u6765\uff0c\u662f\u90a3\u4e48\u7ec6\u788e\u800c\u5a07\u67d4\uff0c\u50cf \u662f\u8fdc\u8fdc\u5929\u7554\u98d8\u6d8c\u7684\u7434\u58f0\uff0c\u4e3a\u8fd9\u51c4\u51c9\u7684\u591c\u8272\uff0c\u5e26\u6765\u4e00\u4e1d\u6e29\u67d4\u7684\u60c5\u610f\u3002\u98ce\uff0c\u4e5f\u50cf\u5f80\u5e38\u4e00\u6837\u5730\u5439 \u7740\uff0c\u5439\u5728\u90a3\u201d\u51b7\u8c37\u53cc\u6728\u201c\u4e2d\u7684\u67af\u6728\u5bd2\u7af9\u8eab\u4e0a\u7a7f\u7684\u5bbd\u5927\u888d\u5b50\u4e0a\uff0c\u4fbf\u5e26\u8d77\u4e00\u9635\u9635\u730e\u730e\u7684\u58f0\u54cd\u3002\u886b \u89d2\u626c\u8d77\uff0c\u895f\u62ce\u98de\u821e\u201d\u7136\u800c\u4ed6\u4eec\u7684\u8eab\u8eaf\uff0c\u5374\u4ecd\u7136\u662f\u7b14\u76f4\u50f5\u786c\u7684\uff0c\u53ea\u6709\u56db\u53ea\u51db\u7136\u53d1\u7740\u5149\u5f69\u7684\u773c \u775b\uff0c\u5728\u7f13\u7f13\u5730\u79fb\u52a8\u7740\uff0c\u4ece\u6a80\u6587\u742a\u7684\u9762\u989c\uff0c\u79fb\u5411\u88f4\u73cf\u7684\u773c\u775b\uff0c\u53c8\u4ece\u88f4\u73cf\u7684\u9762\u989c\u79fb\u5411\u64c5\u6587\u742a\u7684\u773c \u775b\u3002 \u7b2c\u4e94\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u4e94\u7ae0 \u3000\u3000\u8fd9\u4e00\u53cc\u9762\u4e0a\u6728\u7136\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u4e5f\u751f\u50cf\u662f\u6ca1\u6709\u4efb\u4f55\u60c5\u611f\u7684\u6b66\u6797\u9b54\u5934\uff0c\u6b64\u523b\u76ee\u5149\u4e4b\u4e2d\uff0c\u5374 \u663e\u7136\u5730\u6cdb\u51fa\u4e86\u4e9b\u8bb8\u60c5\u611f\u7684\u6ce2\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u5947\u602a\u5730\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u742a\u513f\u600e\u4f1a\u8ba4\u5f97\u4ed6\uff1f\u53c8\u600e\u4f1a\u5bf9\u4ed6\u4f5c\u51fa\u8fd9\u526f\u6837\u5b50\u6765\uff1f\u96be\u9053\u2026\u2026\u201d\u76ee \u5149\u8f6c\u5904\uff0c\u5374\u89c1\u6a80\u6587\u742a\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u6251\u5411\u88f4\u73cf\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u51b7\u9177\u7684\u6b66\u6797\u9b54\u5934\u4e0d\u7ea6\u800c\u540c\u5730\u53e3\u4e2d\u4f4e\u53f1\u4e00\u58f0\uff0c\u67af\u7626\u800c\u9880\u957f\u7684\u8eab\u8eaf\uff0c\u672a\u89c1\u4efb\u4f55\u4f5c\u52bf\uff0c \u4fbf\u50cf\u4e24\u53ea\u79bb\u5f26\u4e4b\u7bad\uff0c\u7535\u4e5f\u4f3c\u5730\u63a0\u4e86\u8fc7\u53bb\u2014\u2014\u6a80\u6587\u742a\u6162\u6162\u5730\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u5979\u7684\u8eab\u8eaf\u8ddd\u79bb\u88f4\u73cf\u8d8a \u8fd1\uff0c\u4ed6\u5fc3\u4e2d\u60c5\u611f\u7684\u6ce2\u6d9b\uff0c\u4e5f\u5c31\u6fc0\u52a8\u5f97\u8d8a\u5927\u3002\u6a21\u7cca\u7684\u773c\u6cea\uff0c\u6cea\u773c\u76f8\u5bf9\uff0c\u76f8\u5bf9\u7684\u6cea\u773c\uff0c\u60c5\u612b\u5982 \u6d41\uff0c\u4ed6\u4ece\u5979\u7684\u76ee\u5149\u4e2d\u5f97\u4e86\u81ea\u5df1\u68a6\u5bd0\u4ee5\u6c42\u7684\u60c5\u610f\uff0c\u5979\u53c8\u4f55\u5c1d\u4e0d\u662f\uff1f \u3000\u3000\u7ec8\u4e8e\uff0c\u5979\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u6251\u5411\u4ed6\uff0c\u60f3\u5c06\u81ea\u5df1\u7684\u8eab\u8eaf\uff0c\u57cb\u85cf\u5728\u4ed6\u5fae\u5fae\u8d77\u4f0f\u7684\u80f8\u819b\u91cc\uff0c\u8fd9\u9500 \u9b42\u8680\u9aa8\u7684\u4e00\u523b\uff0c\u4ed6\u4f01\u5f85\u5df2\u4e45\uff0c\u5979\u4f01\u5f85\u66f4\u4e45\uff0c\u4ed6\u7f13\u7f13\u4f38\u51fa\u53cc\u81c2\uff0c\u5979\u6084\u7136\u5408\u4e0a\u773c\u5e18\u3002 \u3000\u3000\u54ea\u77e5\u4e00-- \u3000\u3000\u4e00\u58f0\u4f4e\u53f1\uff0c\u4e00\u9635\u5fae\u98ce\u3002\u5979\u7741\u5f00\u773c\u6765\uff0c\u53ea\u89c9\u773c\u524d\u4eba\u5f71\u4e00\u82b1\uff0c\u90a3\u51b7\u67af\u6728\u4e0e\u51b7\u5bd2\u7af9\uff0c\u4fbf\u5df2\u6321\u5728 \u81ea\u5df1\u8eab\u524d\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u5a07\u8eaf\u534a\u626d\uff0c\u5728\u8fd9\u5feb\u5982\u7535\u95ea\u7684\u4e00\u5239\u90a3\u95f4\u91cc\uff0c\u8fd9\u5fc3\u4e2d\u5145\u6ee1\u6e29\u99a8\u4e4b\u610f\u7684\u5c11 \u5973\uff0c\u7adf\u5df2\u4f7f\u51fa\u5999\u5230\u6beb\u5dc5\u7684\u8f7b\u529f\u8eab\u6cd5\u6765\uff0c\u968f\u7740\u67f3\u8170\u7684\u8f7b\u8f7b\u4e00\u79fb\uff0c\u6ed1\u5f00\u4e09\u5c3a\u3002 \u3000\u3000\u5979\u7ea4\u8db3\u4e00\u6cbe\u5730\u9762\uff0c\u5374\u53c8\u817e\u8eab\u800c\u8d77\uff0c\u63a0\u56de\u8fd9\u201c\u67af\u6728\u5bd2\u7af9\u201d\u7684\u8eab\u4e0a\uff0c\u4e00\u53cc\u660e\u5a9a\u7684\u79cb\u6ce2\u4e2d\uff0c\u6cdb \u51fa\u60ca\u8be7\u3001\u8d23\u602a\u7684\u795e\u91c7\uff0c\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u53d4\uff0c\u4e8c\u53d4\uff0c\u60a8\u8fd9\u662f\u5e72\u5417\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u76ee\u5149\u4e00\u8f6c\uff0c\u548c\u51b7\u5bd2\u7af9\u5bf9\u671b\u4e00\u773c\uff0c\u7a81\u5730\u4e00\u8d77\u56de\u8f6c\u8eab\u8eaf\uff0c\u56db\u53ea\u624b\u638c\u95ea\u7535\u800c\u51fa\uff0c\u5e73\u5e73\u5730 \u8d34\u5728\u88f4\u73cf\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4f7f\u88f4\u73cf\u60ca\u8be7\u3001\u5947\u602a\u7684\uff0c\u5e76\u4e0d\u662f\u8fd9\u4e24\u4e2a\u51b7\u9177\u7684\u602a\u4eba\u600e\u4f1a\u7a81\u7136\u963b\u6321\u5728\u81ea\u5df1\u8eab\u524d\uff0c\u800c\u662f\u4ed6\u4eec\u600e \u7684\u53c8\u4f1a\u5bf9\u81ea\u5df1\u7a81\u65bd\u715e\u624b\uff0c\u4ed6\u773c\u770b\u7740\u4ed6\u4eec\u7684\u56db\u53ea\u624b\u638c\uff0c\u51fb\u5411\u81ea\u5df1\u7684\u53cc\u80a9\u3001\u4e24\u81c2\uff0c\u5374\u8fde\u8eb2\u907f\u4e4b\u529b \u90fd\u6ca1\u6709\uff0c\u66f4\u9051\u8bba\u8fd8\u51fb\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u56db\u53ea\u624b\u638c\uff0c\u6b64\u523b\u51fb\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u81ea\u5df1\u7eb5\u7136\u662f\u94c1\u70e7\u94a2\u94f8\uff0c\u4e5f\u4f1a\u88ab\u51fb\u788e\uff0c\u4f46\u662f\u5728\u8fd9 \u751f\u6b7b\u4ec5\u7cfb\u4e8e\u4e00\u7ebf\u7684\u65f6\u5019\uff0c\u4ed6\u5fc3\u4e2d\u4ecd\u672a\u5fd8\u5374\u7684\uff0c\u5374\u5e76\u975e\u81ea\u5df1\u7684\u751f\u6b7b\u4e4b\u4e8b\uff0c\u800c\u662f\u4ed6\u5bf9\u9762\u7684\u6a80\u6587 \u742a\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u751a\u81f3\u8fde\u6700\u540e\u671b\u5979\u4e00\u773c\u90fd\u4e0d\u80fd\u591f\uff0c\u56e0\u4e3a\u5728\u4ed6\u548c\u5979\u4e4b\u95f4\uff0c\u963b\u9694\u7740\u51b0\u5c71\u822c\u7684\u4e24\u4e2a\u602a \u4eba\uff0c\u4e8e\u662f\u4ed6\u4e5f\u53ea\u5f97\u957f\u53f9\u7740\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u5e38\u4eba\u51fb\u51fa\u4e00\u638c\uff0c\u901f\u5ea6\u4e5f\u4e0d\u8fc7\u5728\u970e\u773c\u4e4b\u95f4\uff0c\u8fd9\u201c\u67af\u6728\u5bd2\u7af9\u201d\u540d\u503e\u6b66\u6797\uff0c\u4ed6\u4eec\u51fb\u51fa\u7684\u638c\u52bf\uff0c \u5176\u5feb\u81ea\u66f4\u60ca\u4eba\uff0c\u4f46\u4e16\u95f4\u6700\u5feb\u7684\uff0c\u4ecd\u8fd8\u662f\u4eba\u7c7b\u7684\u601d\u60f3\uff0c\u5c31\u5728\u4ed6\u4eec\u51fb\u51fa\u624b\u638c\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u88f4\u73cf\u5fc3 \u4e2d\uff0c\u5df2\u95ea\u7535\u822c\u63a0\u8fc7\u8fd9\u51e0\u4e2a\u5ff5\u5934\uff0c\u7b49\u5230\u4ed6\u4eec\u7684\u624b\u638c\u4ec5\u662f\u5e73\u5e73\u8d34\u5728\u88f4\u73cf\u8eab\u4e0a\uff0c\u800c\u5e76\u975e\u201c\u51fb\u201d\u5728\u88f4 \u73cf\u8eab\u4e0a\u7684\u65f6\u5019\u3002 \u3000\u3000\u6a80\u6587\u742a\u5df2\u81ea\u7126\u6025\u5730\u6251\u4e86\u4e0a\u6765\uff0c\u4e00\u624b\u626f\u4e00\u4eba\u7684\u8863\u886b\uff0c\u547c\u558a\u9053\uff1a\u201c\u5927\u53d4\uff0c\u4e8c\u53d4\uff0c\u60a8\u5230\u5e95\u662f\u600e \u4e48\u56de\u4e8b\u5440\uff1f\u4ed6\u2026\u2026\u4ed6\u662f\u2026\u2026\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u51b7\u51b7\u56de\u987e\u4e00\u773c\uff0c\u9053\uff1a\u201c\u742a\u513f\uff0c\u8d70\u5f00\u4e9b\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u5374\u5fae\u5fae\u6cdb\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u56de\u987e\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u4f60\u6025\u4ec0\u4e48\uff0c\u6211\u4eec\u82e5\u662f\u8981\u4ed6\u7684\u5c0f\u547d\uff0c\u4ed6\u6709 \u5341\u6761\u547d\u4e5f\u65e9\u5c31\u9001\u7ec8\u4e86\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u4e0d\u7981\u4e00\u5446\uff0c\u8f6c\u65e5\u671b\u53bb\uff0c\u53ea\u89c1\u88f4\u73cf\u7d27\u7d27\u95ed\u7740\u53cc\u76ee\uff0c\u989d\u89d2\u50cf\u662f\u6b63\u5728\u6c81\u7740\u6c57\u73e0\uff0c\u5979\u65e2\u4e0d \u77e5\u9053\u8fd9\u201c\u51b7\u8c37\u53cc\u6728\u201d\u548c\u81ea\u5df1\u7684\u5173\u7cfb\uff0c\u66f4\u4e0d\u77e5\u9053\u4ed6\u4eec\u8fd9\u6837\u5bf9\u4ed6\u662f\u4e3a\u7740\u4ec0\u4e48\uff0c\u8fdf\u7591\u534a\u664c\uff0c\u67f3\u8170\u53c8 \u81ea\u4e00\u626d\uff0c\u7ed5\u8fc7\u8fd9\u51b7\u6c0f\u5144\u5f1f\u7684\u8eab\u8eaf\uff0c\u63a0\u5230\u88f4\u73cf\u8eab\u4fa7\u3002 \u3000\u3000\u5374\u542c\u51b7\u5bd2\u7af9\u53c8\u81ea\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u742a\u513f\uff0c\u53eb\u4f60\u7ad9\u8fdc\u4e9b\uff0c\u4f60\u542c\u5230\u6ca1\u6709\uff1f\u201d\u51b7\u67af\u6728\u63a5\u53e3\u9053\uff1a\u201c\u8fd9 \u59d3\u88f4\u7684\u65b9\u624d\u53d7\u4e86\u6211\u4eec\u7684\u4e24\u6781\u7384\u529f\uff0c\u867d\u7136\u5f3a\u81ea\u652f\u6491\u7740\uff0c\u5176\u5b9e\u53d7\u7684\u4f24\u5df2\u662f\u4e0d\u8f7b\uff0c\u53ea\u8981\u518d\u6709\u4e9b\u987b\u9707 \u52a8\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u771f\u8981\u545c\u547c\u4e86\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u9762\u5bb9\u9aa4\u7136\u4e00\u53d8\uff0c\u5ae3\u7ea2\u7684\u9762\u988a\uff0c\u4fbf\u7acb\u65f6\u53d8\u5f97\u82cd\u81ea\uff0c\u5df2\u6ca1\u6709\u8840\u8272\uff0c\u98a4\u6296\u7740\u9053\uff1a\u201c\u5927 \u53d4\uff0c\u60a8\u2026\u2026\u60a8\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u5bf9\u4ed6\u5462\uff1f\u4ed6\u4e0d\u662f\u60a8\u7684\u670b\u53cb\u5417\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u542c\u8fc7\u4f60\u5927\u53d4\u4e8c\u53d4\u6709\u670b\u53cb\uff1f\u201d\u6a80\u6587\u742a\u4e00\u53cc\u9edb\u7709\uff0c\u6df1\u98a6\u82e5 \u7ed3\uff0c\u4e0d\u4f4f\u4ed6\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u529e\u5462\uff1f\u201d\u4f38\u51fa\u7ea4\u638c\uff0c\u60f3\u53bb\u62b9\u64e6\u88f4\u73cf\u989d\u4e0a\u7684\u6c57\u73e0\uff0c\u54ea\u77e5\u51b7\u67af\u6728\u7a81\u53c8\u8f7b \u53f1\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8822\u4e2b\u5934\uff0c\u53eb\u4f60\u522b\u78b0\u4ed6\uff0c\u4f60\u770b\u5230\u6ca1\u6709\uff0c\u6211\u4eec\u73b0\u5728\u662f\u5728\u5e72\u4ec0\u4e48\uff1f\u201d\u6a80\u6587\u742a\u79cb\u6ce2\u4e00 \u8f6c\uff0c\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u8f7b\u53f9\u4e00\u58f0\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u5979\u6b64\u523b\u867d\u5df2\u770b\u51fa\uff0c\u8fd9\u51b7\u6c0f\u5144\u5f1f\u50cf\u662f\u5728\u4e3a\u88f4 \u73cf\u5185\u529b\u7597\u4f24\uff0c\u5374\u53c8\u4e0d\u80fd\u5341\u5206\u786e\u5b9e\uff0c\u53ea\u5f97\u7126\u6025\u5730\u5b88\u5728\u65c1\u8fb9\uff0c\u5e0c\u671b\u88f4\u73cf\u80fd\u591f\u7741\u5f00\u773c\u6765\uff0c\u5411\u81ea\u5df1\u8bf4 \u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u65f6\u95f4\uff0c\u5728\u7126\u6025\u7740\u7684\u4eba\u4eec\u5fc3\u91cc\uff0c\u8fc7\u5f97\u5206\u5916\u7f13\u6162\u3002 \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8fd9\u67af\u6728\u3001\u5bd2\u7af9\u6728\u7136\u7684\u9762\u76ee\uff0c\u6b64\u523b\u7adf\u53d8\u5f97\u5341\u5206\u51dd\u91cd\uff0c\u56db\u53ea\u7d27\u8d34\u5728\u88f4\u73cf\u524d\u80f8 \u7684\u624b\u638c\uff0c\u7a81\u5730\u4e00\u626c\uff0c\u6307\u5c16\u5fae\u6307\uff0c\u638c\u7f18\u4e00\u8f6c\uff0c\u88f4\u73cf\u50f5\u7acb\u7740\u7684\u8eab\u5f62\uff0c\u7adf\u4e3a\u4e4b\u7684\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u90a3\u56db\u53ea \u67af\u7626\u7684\u624b\u638c\uff0c\u4fbf\u5df2\u8d34\u5728\u4ed6\u7684\u80cc\u540e\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u53ea\u89c9\u8fd9\u4e24\u4e2a\u51b7\u9177\u7684\u602a\u4eba\u638c\u5fc3\u4e4b\u4e2d\uff0c\u4eff\u4f5b\u6709\u79cd\u4e0d\u53ef\u8a00\u4f20\u7684\u70ed\u529b\uff0c\u4f20\u4eba\u81ea\u5df1\u7684\u8eab\u4e0a\uff0c \u8fd9\u70ed\u529b\u65f6\u800c\u8f7b\u5fae\uff0c\u65f6\u800c\u6d53\u539a\uff0c\u968f\u7740\u81ea\u5df1\u7684\u547c\u5438\uff0c\u5728\u81ea\u5df1\u7684\u8eab\u8eaf\u4e2d\u6e38\u8d70\u6d41\u7a9c\u7740\u3002 \u3000\u3000\u4ed6\u867d\u5168\u7136\u4e0d\u660e\u6b66\u529f\u4e4b\u5965\u5999\uff0c\u4f46\u5374\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u6b64\u523b \u600e\u7684\u4e3a\u6211\u7597\u8d77\u4f24\u6765\uff0c\u96be\u9053\u4ed6\u4eec\u662f\u4e3a\u4e86\u6587\u742a\uff0c\u4f46\u662f\u4ed6\u4eec\u5374\u53c8\u548c\u6587\u742a\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d\u987b\u77e5\u4ed6\u81ea \u77e5\u548c\u6a80\u6587\u742a\u4e00\u8d77\u957f\u6210\uff0c\u6a80\u6587\u742a\u8ba4\u5f97\u7684\u4eba\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u8ba4\u5f97\uff0c\u6b64\u523b\u89c1\u5979\u548c\u8fd9\u4e24\u4e2a\u602a\u4eba\u50cf\u662f\u5341\u5206\u719f \u6089\uff0c\u800c\u81ea\u5df1\u4e00\u751f\u4e4b\u4e2d\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e24\u4eba\u4e4b\u9762\uff0c\u5fc3\u91cc\u81ea\u7136\u5947\u602a\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u8fd9\u4e00\u5e74\u4e4b\u4e2d\uff0c\u4ed6\u81ea\u8eab\u56fa\u7136\u906d\u9047\u5230\u5947\u602a\u4e4b\u4e8b\uff0c\u800c\u6a80\u6587\u742a\u7684\u906d\u9047\u4e4b\u5947\uff0c\u5374\u4e5f\u672a\u89c1 \u5728\u4ed6\u4e4b\u4e0b\u54e9\u3002 \u3000\u3000\u83ab\u7ea6\u53c8\u8fc7\u4e86\u76cf\u8336\u65f6\u523b\uff0c\u90a3\u67af\u6728\u5bd2\u7af9\u7a81\u7136\u8eab\u5f62\u4e00\u52a8\uff0c\u5728\u88f4\u73cf\u8eab\u524d\u8eab\u540e\uff0c\u8eab\u5de6\u8eab\u53f3\uff0c\u6709\u5982\u7a7f \u82b1\u8774\u8776\u822c\u98de\u821e\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u56db\u53ea\u67af\u7626\u7684\u624b\u638c\uff0c\u7adf\u968f\u7740\u4ed6\u4eec\u8f6c\u52a8\u7740\u7684\u8eab\u5f62\uff0c\u4e0d\u65ad\u5730\u5728\u88f4\u73cf\u8eab\u4e0a\u51fb\u6253\u3002 \u3000\u3000\u5239\u90a3\u95ee\uff0c\u88f4\u73cf\u53ea\u89c9\u81ea\u5df1\u7684\u8eab\u8eaf\uff0c\u4e5f\u4e0d\u7531\u81ea\u4e3b\u5730\u968f\u7740\u8fd9\u56db\u53ea\u624b\u638c\u7684\u51fb\u6253\uff0c\u6709\u5982\u9640\u87ba\u822c\u65cb\u8f6c \u8d77\u6765\uff0c\u5947\u602a\u7684\u662f\uff0c\u81ea\u5df1\u8eab\u4e0a\u88ab\u51fb\u6253\u4e4b\u5904\uff0c\u975e\u4f46\u4e0d\u89c1\u75bc\u75db\uff0c\u800c\u4e14\u8fd8\u6709\u4e00\u79cd\u96be\u4ee5\u63cf\u8ff0\u7684\u8212\u670d\u4e4b \u611f\u3002 \u3000\u3000\u7126\u6025\u5730\u7ad9\u5728\u65c1\u8fb9\u51dd\u795e\u800c\u6ce8\u7684\u6a80\u6587\u742a\uff0c\u6b64\u523b\u89c1\u4e86\u8fd9\u4e24\u4eba\u5947\u602a\u7684\u52a8\u4f5c\uff0c\u5374\u4e3a\u4e4b\u559c\u60a6\u5730\u4f4e\u547c\u4e00 \u58f0\uff0c\u4e00\u6735\u5a07\u7f8e\u7684\u7b11\u989c\uff0c\u5077\u5077\u6cdb\u4e0a\u9762\u988a\u3002 \u3000\u3000\u8fd9\u751f\u957f\u4e8e\u6b66\u6797\u4e16\u5bb6\uff0c\u53c8\u88ab\u5979\u7236\u4eb2\u6df1\u6df1\u75bc\u7231\u7740\u7684\u5c11\u5973\uff0c\u5bf9\u6b66\u529f\u4e00\u65b9\u9762\u7684\u77e5\u8bc6\uff0c\u5f53\u7136\u8fdc\u5728\u88f4 \u73cf\u4e4b\u4e0a\uff0c\u5979\u6b64\u523b\u5df2\u7ecf\u770b\u51fa\uff0c\u8fd9\u51b7\u6c0f\u5144\u5f1f\u5728\u88f4\u73cf\u8eab\u4e0a\u6240\u65bd\u7684\u52a8\u4f5c\uff0c\u7adf\u662f\u4e0d\u60dc\u4ee5\u81ea\u5bb6\u7684\u771f\u5143\u4e4b \u529b\uff0c\u6765\u4e3a\u88f4\u73cf\u6563\u5f00\u6d51\u8eab\u7684\u4e00\u767e\u96f6\u516b\u5904\u5927\u5c0f\u7a74\u9053\u3002 \u3000\u3000\u90a3\u4e48\u88f4\u73cf\u65b9\u624d\u867d\u7136\u53d7\u4e86\u4e9b\u5185\u4f24\uff0c\u7ecf\u8fd9\u540d\u9707\u6b66\u6797\u7684\u4e24\u4f4d\u5947\u4eba\u5148\u4ee5\u4e00\u70b9\u638c\u5fc3\u903c\u51fa\u7684\u771f\u706b\uff0c\u52a9 \u4ed6\u4f53\u5185\u8840\u6c14\u8fd0\u884c\u4e09\u5341\u516d\u5c40\u5929\uff0c\u5185\u4f24\u4fbf\u5df2\u75ca\u6108\u5341\u4e4b\u516b\u4e38\uff0c\u6b64\u523b\u518d\u4ece\u4ed6\u4eec\u4e0d\u60dc\u5185\u529b\u4e8f\u635f\u6572\u5f00\u7a74 \u9053\uff0c\u4e0d\u4f46\u5bf9\u4ed6\u8eab\u4f53\u5927\u6709\u88e8\u76ca\uff0c\u751a\u4e14\u7acb\u65f6\u4fbf\u53ef\u6613\u7b4b\u6362\u9aa8\uff0c\u5143\u6c14\u51dd\u56fa\u3002 \u3000\u3000\u8fd9\u79cd\u9047\u5408\uff0c\u5728\u6b66\u6797\u4e2d\u4eba\u8bf4\u6765\uff0c\u5df2\u6781\u96be\u80fd\u53ef\u8d35\uff0c\u4f55\u51b5\u88f4\u73cf\u6b64\u756a\u6240\u5f97\uff0c\u7adf\u662f\u53d7\u81ea\u6b66\u6797\u4e2d\u6700\u6700 \u9762\u51b7\u5fc3\u8fa3\u7684\u201c\u51b7\u8c37\u53cc\u6728\u201d\u3002 \u3000\u3000\u88f4\u73cf\u867d\u7136\u4e0d\u77e5\u81ea\u5df1\u7684\u5e78\u8fd0\uff0c\u4f46\u6a80\u6587\u742a\u5374\u5df2\u4e0d\u7981\u4e3a\u4e4b\u6b22\u547c\u96c0\u8dc3\u4e86\u3002 \u3000\u3000\u5979\u90a3\u4e00\u53cc\u6709\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u6ee1\u5145\u559c\u60a6\u5730\u968f\u7740\u8fd9\u4e24\u6761\u98de\u821e\u7740\u7684\u4eba\u5f71\u6253\u8f6c\uff0c\u5979\u7684\u5fc3\uff0c\u5374\u4e5f\u56e0 \u559c\u60a6\u800c\u98de\u626c\u65cb\u8f6c\uff0c\u6de1\u6de1\u7684\u6708\u5149\uff0c\u7167\u5728\u5979\u9752\u8272\u7684\u8863\u886b\u4e0a\uff0c\u8f7b\u8f7b\u7684\u665a\u98ce\uff0c\u5439\u8d77\u5979\u9752\u8272\u7684\u8863\u88fe\uff0c\u4f7f \u5f97\u8fd9\u5352\u5df2\u7f8e\u7edd\u5929\u4eba\u7684\u5c11\u5973\uff0c\u770b\u6765\u66f4\u6709\u4e00\u79cd\u51fa\u5c18\u7684\u7f8e\u3002 \u3000\u3000\u84e6\u5730\u2014\u2014 \u3000\u3000\u53c8\u662f\u4e24\u58f0\u8f7b\u53f1\u3002 \u3000\u3000\u98de\u821e\u7740\u7684\u4eba\u5f71\uff0c\u560e\u7136\u800c\u987f\uff0c\u6a80\u6587\u742a\u8f7b\u547c\u4e00\u58f0\uff0c\u83b2\u8db3\u5fae\u70b9\uff0c\u60ca\u9e3f\u822c\u5730\u63a0\u4e86\u4e0a\u6765\uff0c\u6276\u4f4f\u6447\u6447 \u6b32\u5760\u7684\u88f4\u73cf\uff0c\u5c0f\u5fc3\u5730\u5c06\u4ed6\u6276\u5230\u5730\u4e0a\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u88f4\u73cf\u5634\u89d2\uff0c\u6cdb\u7740\u4e00\u4e1d\u8212\u6cf0\u7684\u7b11\u5bb9\uff0c\u6717\u661f \u822c\u7684\u773c\u775b\uff0c\u6b64\u523b\u5374\u662f\u7d27\u7d27\u95ed\u7740\u7684\uff0c\u4e00\u6ef4\u6c57\u73e0\uff0c\u6cbf\u7740\u4ed6\u7684\u773c\u5e18\u6d41\u4e0b\u3002 \u3000\u3000\u5979\u638f\u51fa\u4e00\u65b9\u6de1\u9752\u7684\u624b\u5e15\uff0c\u6e29\u67d4\u5730\u66ff\u4ed6\u62ed\u53bb\u989d\u4e0a\u7684\u6c57\u73e0\uff0c\u5979\u77e5\u9053\u4e0d\u7528\u591a\u4e45\uff0c\u4ed6\u5c31\u4f1a\u7ad9\u8d77\u6765 \u7684\uff0c\u6bd4\u5f80\u6614\u66f4\u575a\u5f3a\u5730\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u4e8e\u662f\u5979\u6b22\u6109\u5730\u5fae\u53f9\u4e00\u58f0\uff0c\u56de\u8f6c\u5934\uff0c\u67af\u6728\u3001\u5bd2\u7af9\uff0c\u6b63\u5e76\u80a9\u7ad9\u5728\u5979\u8eab\u540e\uff0c\u67af\u7626\u9880\u957f\u7684\u8eab\u8eaf\uff0c \u6709\u5982\u4e24\u5ea7\u9ad8\u4e0d\u53ef\u6500\u7684\u51b0\u5cf0\u3002 \u3000\u3000\u4f46\u662f\u5979\u6b64\u523b\u5374\u4e5f\u4e0d\u77e5\u9053\uff0c\u5728\u8fd9\u4e24\u5ea7\u51b0\u5cf0\u91cc\u4e5f\u542b\u8574\u7740\u4eba\u7c7b\u7684\u70ed\u60c5\uff0c\u53ea\u662f\u8981\u53d1\u73b0\u8fd9\u79cd\u70ed\u60c5\uff0c \u53c8\u662f\u591a\u4e48\u56f0\u96be\u7684\u4e8b\u5440\uff01 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u91cc\uff0c\u5979\u4e0d\u7981\u60f3\u8d77\u81ea\u5df1\u8fd9\u4e00\u5e74\u6765\u7684\u906d\u9047\uff0c\u5979\u60f3\u8d77\u4e86\u81ea\u5df1\u5982\u4f55\u4e3a\u88f4\u73cf\u7684\u51fa\u8d70\u800c\u60b2 \u4f24\uff0c\u7ec8\u4e8e\u81ea\u5df1\u4e5f\u79bb\u5f00\u4e86\u6148\u7236\uff0c\u8d70\u5230\u6c5f\u6e56\u6d41\u6d6a\uff0c\u5e0c\u671b\u80fd\u591f\u627e\u5230\u4e3a\u81ea\u5df1\u51fa\u8d70\u7684\u88f4\u73cf\u3002 \u3000\u3000\u4f46\u662f\u4eba\u6d77\u832b\u832b\uff0c\u8981\u5728\u832b\u832b\u7684\u4eba\u6d77\u4e2d\uff0c\u6f2b\u65e0\u76ee\u7684\u53bb\u627e\u5bfb\u4e00\u4e2a\u4eba\uff0c\u8be5\u662f\u591a\u4e48\u56f0\u96be\u5440\uff0c\u5979\u81ea\u7136 \u5931\u671b\u4e86\uff0c\u5979\u79bb\u5f00\u7e41\u534e\u7684\u57ce\u9547\uff0c\u8d70\u5411\u8352\u51c9\u7684\u5c71\u91ce\u3002 \u3000\u3000\u90a3\u662f\u79cb\u5929\uff0c\u79cb\u98ce\u8427\u7d22\uff0c\u5728\u5979\u8fd8\u6ca1\u6709\u8d70\u5230\u6c5f\u5357\u7684\u65f6\u5019\uff0c\u5979\u7adf\u9047\u7740\u4e86\u540d\u4f20\u6c5f\u6e56\u7684\u201c\u51b7\u8c37\u53cc \u6728\u201d\u3002 \u3000\u3000\u201c\u5947\u9047\uff0c\u771f\u7684\u662f\u5947\u9047\uff1f\u201d \u3000\u3000\u5979\u6697\u4e2d\u601d\u7d22\u7740\uff0c\u518d\u6b21\u62ac\u8d77\u5934\uff0c\u51b7\u67af\u6728\u3001\u51b7\u5bd2\u7af9\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u5979\u9762\u524d\uff0c\u4e8e\u662f\u5979\u611f\u6fc0 \u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u5927\u53d4\uff0c\u4e8c\u53d4\uff0c\u6211\u771f\u4e0d\u77e5\u9053\u8be5\u600e\u6837\u8c22\u8c22\u4f60\u4eec\uff0c\u4e3a\u4e86\u6211\u2026\u2026\u201d \u3000\u3000\u6e29\u67d4\u800c\u5a07\u5f31\u7684\u8bed\u58f0\uff0c\u4f7f\u5f97\u51b7\u6c0f\u5144\u5f1f\u6728\u7136\u65e0\u52a8\u4e8e\u8877\u7684\u9762\u76ee\uff0c\u4e5f\u5f00\u59cb\u6fc0\u8d77\u4e00\u4e1d\u60c5\u611f\u7684\u6d9f\u7b4b\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u8f7b\u8f7b\u4e00\u76b1\u53cc\u7709\uff0c\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u4f60\u600e\u4e48\u4f1a\u8ba4\u5f97\u4ed6\u2014\u2014\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u4ed6\u5c31\u8981\u505a\u6c5f\u5357 \u9ed1\u9053\u7684\u603b\u74e2\u628a\u5b50\u4e86\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u6123\uff0c\u773c\u775b\u7741\u5f97\u5927\u5927\u7684\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u5374\u542c\u51b7\u5bd2\u7af9\u53c8\u9053\uff1a \u201c\u8fd9\u4e2a\u603b\u74e2\u628a\u5b50\uff0c\u5c31\u662f\u90a3\u4e9b\u63a8\u4e3e\u51fa\u6765\u4e0e\u4f60\u7239\u7239\u505a\u5bf9\u7684\uff0c\u6211\u548c\u4f60\u7239\u7239\u867d\u7136\u6ca1\u6709\u4ea4\u60c5\uff0c\u4f46\u662f\u4e3a\u4e86 \u4f60\uff0c\u6240\u4ee5\u624d\u7279\u5730\u534a\u591c\u5230\u8fd9\u91cc\u6765\u7ba1\u6559\u7ba1\u6559\u4ed6\uff0c\u54ea\u77e5\u9053\u8fd9\u4f4d\u5c31\u5f53\u603b\u74e2\u628a\u5b50\u7684\u4ec1\u5144\uff0c\u7adf\u8fde\u4e00\u4e1d\u6b66\u529f \u4e5f\u4e0d\u4f1a\u2014\u2014\u201d\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u500f\u7136\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\u3002 \u3000\u3000\u6a80\u6587\u742a\u5374\u5df2\u88ab\u60ca\u6115\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4e0d\u662f\u51b7\u5927\u53d4\u3001\u4e8c\u53d4\u7684\u65e7\u8bc6\uff0c\u800c\u662f \u56e0\u4e3a\u8fd9\u4e2a\u539f\u56e0\u624d\u5728\u8fd9\u91cc\u8bf4\u8bdd\uff0c\u4f46\u662f\u2026\u2026\u8fd9\u662f\u591a\u4e48\u5947\u602a\uff0c\u4ed6\u600e\u4f1a\u8981\u5f53\u8d77\u603b\u74e2\u628a\u5b50\u6765\u5462\uff1f\u201d\u56de\u7738 \u4e00\u671b\uff0c\u88f4\u73cf\u4ecd\u9759\u9759\u5730\u5750\u5728\u5730\u4e0a\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u5df2\u6bd4\u65b9\u624d\u5b89\u7965\u8bb8\u591a\uff0c\u5bbd\u9614\u7684\u80f8\u819b\uff0c\u968f\u7740\u547c\u5438\u6781\u6709 \u89c4\u5f8b\u5730\u8d77\u4f0f\u7740\u3002 \u3000\u3000\u5979\u653e\u5fc3\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u5374\u542c\u51b7\u5bd2\u7af9\u53c8\u9053\uff1a\u201c\u5341\u4f59\u5e74\u6765\uff0c\u6211\u8db3\u8ff9\u672a\u79bb\u51b7\u8c37\u4e00\u6b65\uff0c\u60f3\u4e0d\u5230\u4e3a\u4e86 \u4f60\u8fd9\u4e2b\u5934\uff0c\u5374\u53c8\u751f\u51fa\u5982\u6b64\u8bb8\u591a\u4e8b\u2014\u2014\u201d\u8fd9\u51b7\u9177\u7684\u602a\u4eba\u5c45\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211 \u4eec\u603b\u7b97\u53c8\u628a\u8fd9\u59d3\u88f4\u7684\u6cbb\u597d\u4e86\uff0c\u4f60\u6709\u4ec0\u4e48\u606c\uff0c\u5c3d\u7ba1\u548c\u4ed6\u8bf4\u5427\uff01\u201d\u6a80\u6587\u742a\u9762\u988a\u5fae\u5fae\u4e00\u7ea2\uff0c\u7f13\u7f13\u5782 \u4e0b\u5934\uff0c\u5f53\u4e00\u4e2a\u5c11\u5973\u7684\u5fc3\u4e8b\u88ab\u4eba\u5bb6\u731c\u900f\u7684\u65f6\u5019\uff0c\u5979\u4eec\u7684\u5fc3\u60c5\u662f\u7f9e\u6da9\u7684\uff0c\u5374\u4e5f\u662f\u6109\u5feb\u7684\u3002 \u3000\u3000\u7b49\u5979\u62ac\u8d77\u5934\u6765\u7684\u65f6\u5019\uff0c\u773c\u524d\u5374\u5df2\u53ea\u5269\u4e0b\u4e00\u7247\u7a7a\u8361\uff0c\u8fdc\u5904\u7684\u7af9\u6797\uff0c\u5728\u5fae\u98ce\u4e2d\u8885\u5a1c\u800c\u821e\uff0c\u6f7a \u6f7a\u7684\u6d41\u6c34\uff0c\u5728\u6708\u5149\u4e0b\u95ea\u70c1\u5982\u94f6\uff0c\u65b9\u624d\u7ad9\u5728\u5979\u773c\u524d\u7684\u51b7\u6c0f\u5144\u5f1f\uff0c\u6b64\u523b\u5374\u5df2\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u88f4\u73cf\u4e0d\u7531\u81ea\u4e3b\u5730\u88ab\u4eba\u5bb6\u5728\u8eab\u4e0a\u4e00\u9635\u51fb\u6253\uff0c\u53ea\u89c9\u8fd9\u4e24\u4e2a\u51b7\u9177\u7684\u602a\u4eba\u5728\u81ea\u5df1\u8eab\u4e0a\u6253\u5f97\u8d8a\u6765\u8d8a \u5feb\uff0c\u81ea\u5df1\u5374\u53cd\u800c\u89c9\u5f97\u66f4\u52a0\u8212\u6cf0\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u79cd\u4e16\u95f4\u4efb\u4f55\u8a00\u8bed\u90fd\u65e0\u6cd5\u5f62\u5bb9\uff0c\u4e16\u95f4\u4efb\u4f55\u6587\u5b57\u90fd\u65e0\u6cd5\u63cf\u8ff0\u7684\u611f\u89c9\uff0c\u4ed6\u65e0\u6cd5\u77e5\u9053\u8fd9\u79cd \u611f\u89c9\u7684\u7531\u6765\u3002 \u3000\u3000\u51fb\u6253\u4e00\u505c\uff0c\u4ed6\u53ea\u89c9\u81ea\u5df1\u7684\u8eab\u5b50\u98d8\u8361\u8361\u5730\uff0c\u4f3c\u4e4e\u7f6e\u8eab\u4e91\u7aef\uff0c\u811a\u4e0b\u4e5f\u662f\u865a\u8f6f\u7684\uff0c\u5374\u53c8\u4f3c\u5e76\u975e \u6ca1\u6709\u6c14\u529b\u652f\u6301\uff0c\u53ea\u662f\u4e0d\u613f\u5c06\u6c14\u529b\u4f7f\u51fa\u800c\u5df2\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8e72\u8eab\u5750\u4e0b\uff0c\u4ed6\u77e5\u9053\u6a80\u6587\u742a\u5728\u4ed6\u8eab\u4fa7\u4f9d\u504e\u7740\u4ed6\uff0c\u4ed6\u77e5\u9053\u5979\u6e29\u67d4\u5730\u4f38\u51fa\u624b\uff0c\u4e3a\u81ea\u5df1\u64e6 \u62ed\u989d\u4e0a\u7684\u6c57\u73e0\uff0c\u4f46\u662f\u4ed6\u5374\u8fde\u773c\u6674\u90fd\u4e0d\u613f\u7741\u5f00\u4e00\u4e0b\u3002 \u3000\u3000\u56e0\u4e3a\u6b64\u523b\uff0c\u4ed6\u4f53\u5185\u7684\u547c\u5438\u3001\u8840\u6db2\uff0c\u90fd\u6709\u4e00\u79cd\u98de\u626c\u7684\u611f\u89c9\uff0c\u8fd9\u79cd\u611f\u89c9\u548c\u524d\u4e9b\u65e5\u5b50\u4ed6\u548c\u5434\u9e23 \u4e16\u75db\u996e\u800c\u9189\u7684\u611f\u89c9\u6709\u4e9b\u76f8\u4f3c\uff0c\u4f46\u4ed4\u7ec6\u4f53\u6627\uff0c\u5374\u53c8\u5b8c\u5168\u4e0d\u4f3c\uff0c\u4ed6\u867d\u7136\u4e0d\u77e5\u9053\u65b9\u624d\u90a3\u4e00\u756a\u6572\u6253\uff0c \u5df2\u4f7f\u4ed6\u7531\u4e00\u4e2a\u5b8c\u5168\u6ca1\u6709\u4fee\u4e60\u8fc7\u5185\u5bb6\u5410\u7eb3\u7684\u5c11\u5e74\uff0c\u53d8\u6210\u4e00\u4e2a\u5185\u529b\u5df2\u6709\u76f8\u5f53\u6839\u57fa\u7684\u4eba--\u4e00\u8fd9\u662f\u4ed6 \u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u7684\u4e8b\u2014\u2014\u4f46\u662f\u4ed6\u5374\u80fd\u4ed4\u7ec6\u5730\u628a\u63e1\u7740\u8fd9\u79cd\u611f\u89c9\uff0c\u8ba9\u81ea\u5df1\u7684\u6c14\u8840\u7545\u901a\u5730\u8fd0\u8f6c\u7740\u3002 \u3000\u3000\u7ec8\u4e8e\u4e00\u4e00\u4e00 \u3000\u3000\u4e00\u5207\u53c8\u5f52\u4e8e\u5e73\u5b9a\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u5f20\u5f00\u773c\u6765\u3002\u6a80\u6587\u742a\u8737\u66f2\u7740\u8eab\u8eaf\uff0c\u534a\u8e72\u534a\u5750\u5730\u5728\u4ed6\u8eab\u4fa7\uff0c\u4e00\u624b\u659c\u659c\u5730\u5782\u5728\u5730\u4e0a\uff0c\u6625 \u8471\u822c\u7684\u624b\u6307\uff0c\u8f7b\u5212\u7740\u5730\u4e0a\u7684\u6c99\u77f3\uff0c\u53e6\u4e00\u53ea\u624b\u5374\u6309\u5728\u90a3\u4e00\u65b9\u5305\u5934\u7684\u9752\u4e2d\u4e0a\uff0c\u4e09\u6307\u5fae\u66f2\uff0c\u634f\u7740\u4e00 \u65b9\u5c0f\u5c0f\u7684\u624b\u5e15\u3002 \u3000\u3000\u5979\u76ee\u5149\u51dd\u795e\u5730\u6ce8\u89c6\u7740\u8fdc\u65b9\uff0c\u88f4\u73cf\u4ece\u4fa7\u9762\u671b\u8fc7\u53bb\uff0c\u5979\u90a3\u6709\u5982\u7389\u77f3\u96d5\u7422\u6210\u7684\u9f3b\u5b50\uff0c\u4fbf\u5206\u5916\u663e \u5f97\u633a\u76f4\u800c\u79c0\u9038\uff0c\u76ee\u5149\u4ece\u5de6\u9762\u5c04\u6765\uff0c\u6620\u5f97\u5979\u53f3\u8fb9\u7684\u9f3b\u6d3c\uff0c\u5f62\u6210\u4e00\u4e2a\u66fc\u5999\u7684\u9634\u5f71\uff0c\u9634\u5f71\u518d\u659c\u659c\u5782 \u843d\uff0c\u4e8e\u662f\u5979\u90a3\u5634\u89d2\u5fae\u5fae\u4e0a\u7ffb\u7684\u6a31\u5507\uff0c\u4fbf\u4e5f\u795e\u79d8\u5730\u843d\u5728\u8fd9\u9634\u5f71\u91cc\u3002 \u3000\u3000\u51c4\u6e05\u7684\u6625\u591c\uff0c\u6625\u591c\u7684\u8ff7\u8499\uff0c\u8ff7\u8499\u7684\u51dd\u601d\uff0c\u51dd\u601d\u7740\u7684\u800c\u4eba\u2014\u2014\u8fd9\u4e00\u5207\uff0c\u5f62\u6210\u4e00\u79cd\u4e0d\u53ef\u4f01\u53ca \u7684\u7f8e\uff0c\u4f7f\u5f97\u88f4\u73cf\u51e0\u4e4e\u4e0d\u6562\u53bb\u60ca\u52a8\u5979\uff0c\u4e0d\u6562\u53bb\u60ca\u52a8\u8fd9\u4efd\u5b89\u7965\u548c\u5b81\u9759\uff0c\u800c\u53ea\u662f\u5446\u5446\u5730\u671b\u7740\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5979\u5374\u4fcf\u7136\u56de\u8fc7\u5934\uff0c\u6e05\u6f88\u4e2d\u5fae\u5e26\u8ff7\u60d8\u7684\u76ee\u5149\uff0c\u68a6\u4e00\u6837\u5730\u6ce8\u89c6\u5230\u88f4\u73cf\u8eab\u4e0a\uff0c\u88f4\u73cf\u626d\u52a8 \u4e00\u4e0b\u8170\u8eab\uff0c\u5c06\u81ea\u5df1\u5750\u7740\u7684\u59ff\u52bf\u53d8\u4e86\u53d8\uff0c\u53d8\u5f97\u9760\u8fd1\u5979\u4e9b\uff0c\u7136\u540e\u8f7b\u8f7b\u4ed6\u8bf4\u9053\uff1a\u201c\u6587\u742a\u2026\u6587\u742a\uff0c\u4f60 \u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u4ed6\u5e76\u4e0d\u5341\u5206\u786e\u4fe1\u81ea\u5df1\u539f\u672c\u662f\u60f3\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u4f46\u662f\u4e00\u5207\u4ed6\u5fc3\u91cc\u60f3\u8bf4\u7684\u8bdd\u5230\u4e86\u5634\u8fb9\uff0c \u4ed6\u5374\u8fde\u4e00\u53e5\u90fd\u8bf4\u4e0d\u51fa\uff0c\u56e0\u4e4b\u4ed6\u4fbf\u6f2b\u65e0\u76ee\u7684\u4ed6\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u6a80\u6587\u742a\u7ea4\u624b\u5fae\u8212\uff0c\u63a0\u4e86\u63a0\u540e\u5305\u5934\u9752\u4e2d\u8fb9\u9732\u51fa\u7684\u79c0\u53d1\uff0c\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u6211\u5728\u60f3\uff0c\u4eba\uff0c\u771f\u662f\u5947 \u602a\u7684\u4e1c\u897f\uff0c\u6709\u4e9b\u4eba\u5916\u8868\u770b\u6765\u70ed\u60c5\uff0c\u4f46\u5185\u5fc3\u5374\u51b7\u9177\u5f97\u5f88\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u80fd\u6253\u52a8\u4ed6\uff0c\u8b6c\u5982\u6211\u7239\u7239 \u5427\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u8c01\u4e0d\u77e5\u9053\u4ed6\u8001\u4eba\u5bb6\u6025\u516c\u597d\u4e49\uff0c\u4f46\u662f\u6211\u5374\u77e5\u9053\uff0c\u4ed6\u8001\u4eba\u5bb6\u2026\u2026\u201d\u5979\u5e7d\u5e7d\u957f\u53f9\u4e00 \u58f0\uff0c\u8f6c\u8fc7\u8bdd\u9898\uff0c\u53c8\u9053\uff1a\u201c\u4f46\u662f\u53e6\u5916\u4e00\u4e9b\u4eba\u5462\uff1f\u4eba\u4eba\u90fd\u8bf4\u4ed6\u662f\u51b7\u9177\uff0c\u5fc3\u72e0\u7684\u9b54\u5934\uff0c\u5176\u5b9e\u4ed6\u7684\u5fc3 \u91cc\uff0c\u5374\u4e5f\u662f\u6709\u7740\u4eba\u7c7b\u7684\u6e29\u60c5\u7684\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u521a\u624d\u90a3\u4e24\u4e2a\u4eba\uff0c\u662f\u6b66\u6797\u4e2d\u4eba\u6700\u6700\u5934\u75db\u7684\u9b54\u5934\uff0c \u4f46\u4ed6\u4eec\u5bf9\u6211\uff0c\u5374\u53c8\u90a3\u4e48\u597d\uff0c\u6211\u5fc3\u91cc\u7684\u4e8b\uff0c\u4e0d\u7528\u8bf4\u51fa\u6765\uff0c\u4ed6\u4eec\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u662f\u90a3\u4e48\u8f7b\u67d4\uff0c\u8f7b\u67d4\u5f97\u6709\u5982\u5b69\u5b50\u68a6\u4e2d\u7684\u5453\u8bed\uff0c\u5728\u8fd9\u9759\u9759\u7684\u6625\u591c\u4e2d\u98d8\u6f3e\u7740\u3002 \u3000\u3000\u88f4\u73cf\u5fcd\u4e0d\u4f4f\u4f38\u51fa\u624b\u638c\uff0c\u6e29\u67d4\u5730\u63e1\u4f4f\u5979\u7684\u624b\uff0c\u4fcf\u58f0\u95ee\u9053\uff1a\u201c\u6211\u5462\uff1f\u201d \u3000\u3000\u5979\u9762\u988a\u53c8\u4e3a\u4e4b\u4e00\u7ea2\uff0c\u4f6f\u55d4\u9053\uff1a\u201c\u4f60\u592a\u72e0\u5fc3\u4e86\uff0c\u4e00\u4e2a\u4eba\u5077\u5077\u8dd1\u8d70\uff0c\u4e5f\u4e0d\u544a\u8bc9\u4eba\u5bb6\u4e00\u58f0\uff0c\u5bb3 \u5f97\u4eba\u5bb6\u2026\u2026\u201d\u5782\u4e0b\u5934\uff0c\u7ea2\u7740\u8138\uff0c\u4e0b\u9762\u7684\u8bdd\uff0c\u518d\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6eaa\u4e2d\u7684\u6d41\u6c34\uff0c\u8361\u8d77\u4e1d\u4e1d\u6ce2\u7eb9\uff0c\u88f4\u73cf\u7684\u5fc3\u91cc\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u8361\u8d77\u7247\u7247\u6d9f\u6d9f\uff0c\u4ed6\u5fd8\u60c5\u5730\u5c06\u638c\u4e2d\u7684 \u7ea4\u624b\u63e1\u5f97\u66f4\u7d27\u4e86\u4e9b\uff0c\u6e29\u8bed\u9053\uff1a\u201c\u5bb3\u5f97\u4eba\u5bb6\u600e\u6837\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u7684\u8138\u66f4\u7ea2\u4e86\uff0c\u751a\u81f3\u5728\u591c\u8272\u4e2d\uff0c\u90fd\u53ef\u4ee5\u770b\u5230\u90a3\u79cd\u5ae3\u7ea2\u7684\u989c\u8272\uff0c\u6b64\u523b\u5979\u4f3c\u4e4e\u5c06\u4e00\u5207\u4e8b \u90fd\u5fd8\u5374\u4e86\uff0c\u4ed6\u53c8\u4f55\u5c1d\u4e0d\u662f\uff1f \u3000\u3000\u8fdc\u5904\u7af9\u6797\u4e2d\u7c38\u7136\u4e00\u58f0\u5fae\u54cd\uff0c\u7ad9\u5728\u7af9\u6797\u91cc\u7684\u51b7\u67af\u6728\u4e0e\u51b7\u5bd2\u7af9\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5728\u8fd9\u65e0\u4eba\u770b\u89c1\u7684 \u5730\u65b9\uff0c\u4ed6\u4eec\u8138\u4e0a\u90fd\u6cdb\u7740\u6b23\u6170\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u51b7\u67af\u6728\u6084\u6084\u4e00\u626f\u4ed6\u5144\u5f1f\u7684\u8863\u8896\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e2b\u5934\u4e5f\u6709\u7231\u4eba\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u5fae\u7b11\u4e00\u4e0b\uff0c\u76ee\u5149\u5446\u5446\u5730\u671b\u7740\u6797\u5916\uff0c\u5fc3\u80f8\u4e4b\u95f4\uff0c\u4eff\u4f5b\u4e5f\u5145\u6ee1\u4e86\u7c89\u7ea2\u8272\u7684\u56de\u5fc6\uff0c\u4f4e\u4f4e \u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\uff0c\u4e09\u5341\u5e74\u4ee5\u524d\u2026\u2026\u201d \u3000\u3000\u51b7\u67af\u6728\u70b9\u4e86\u70b9\u5934\uff1a\u201c\u4e09\u5341\u5e74\uff0c\u4e09\u5341\u5e74\u7684\u65e5\u5b50\uff0c\u8fc7\u53bb\u5f97\u771f\u5feb\u5440\uff01\u73b0\u5728\u6211\u4eff\u4f5b\u8fd8\u80fd\u770b\u5230\u4f60\u5750 \u5728\u6cf0\u5c71\u7edd\u9876\u90a3\u5757\u7389\u7687\u724c\u4e0a\uff0c\u62c9\u7740\u5979\u7684\u624b\u770b\u65e5\u51fa\u3002\u201d\u4ed6\u68ee\u51b7\u7684\u76ee\u5149\uff0c\u6b64\u523b\u4e5f\u53d8\u5f97\u6e29\u67d4\u8d77\u6765\uff0c\u53c8 \u9053\uff1a\u201c\u592a\u9633\u5347\u8d77\u7684\u65f6\u5019\uff0c\u7eda\u4e3d\u7684\u9633\u5149\uff0c\u7167\u5728\u4f60\u8138\u4e0a\uff0c\u90a3\u65f6\u4f60\u8fd8\u5e74\u8f7b\uff0c\u53ef\u4e0d\u50cf\u73b0\u5728\u8fd9\u6837\u96be\u770b\uff0c \u6211\u548c\u829d\u59b9\u90fd\u770b\u5f97\u5446\u4e86\uff0c\u8bb0\u5f97\u829d\u59b9\u90a3\u65f6\u6084\u6084\u5730\u5bf9\u6211\u8bf4\uff1a\u4f60\u548c\u8335\u5b50\u53ef\u771f\u662f\u4e00\u5bf9\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u559c\u60a6\u5730\u7b11\u4e86\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u90a3\u65f6\u6211\u4eec\u4e5f\u5728\u770b\u4f60\uff0c\u8335\u59b9\u4e5f\u5bf9\u6211 \u8bf4\uff0c\u4f60\u548c\u829d\u5b50\u53ef\u771f\u662f\u4e00\u5bf9\u3002\u201d \u3000\u3000\u7af9\u6797\u91cc\u7684\u9634\u5f71\u4e2d\uff0c\u8fd9\u540d\u9547\u6c5f\u6e56\u7684\u9b54\u5934\u5144\u5f1f\u4e8c\u4eba\u90fd\u6b22\u60a6\u5730\u7b11\u4e86\uff0c\u53ea\u662f\u5728\u7b11\u5bb9\u4e2d\uff0c\u5374\u53c8\u5e26\u7740 \u4e9b\u8bb8\u60b2\u54c0\u7684\u60c6\u6005\uff0c\u56e0\u4e3a\u901d\u53bb\u7684\u65e5\u5b50\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u518d\u6765\uff0c\u901d\u53bb\u7684\u4eba\u513f\uff0c\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u590d\u751f\u4e86\u3002 \u3000\u3000\u51b7\u67af\u6728\u5fe7\u90c1\u5fae\u7b11\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5979\u4eec\u6b7b\u5f97\u90a3\u4e48\u65e9\uff0c\u6254\u4e0b\u6211\u4eec\u4e24\u4e2a\u8001\u5934\u5b50\u2014\u2014\u201d\u4ed6\u6c89 \u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u51b7\u5bd2\u7af9\u5374\u5fae\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u6709\u4ec0\u4e48\u597d\u53f9\u6c14\u7684\uff0c\u6211\u4eec\u603b\u7b97\u6709\u8fc7\u90a3\u4e48\u4e00\u6bb5\u5e78\u798f \u7684\u65e5\u5b50\uff0c\u6bd4\u90a3\u4e9b\u6574\u5929\u5230\u665a\u53ea\u77e5\u4e89\u540d\u593a\u5229\u7684\u8822\u624d\u5f3a\u5f97\u591a\uff0c\u6709\u65f6\u6211\u53ef\u601c\u4ed6\u4eec\uff0c\u6709\u65f6\u5374\u53c8\u4e0d\u7981\u75db\u6068 \u4ed6\u4eec\uff0c\u6068\u4e0d\u5f97\u53eb\u4ed6\u4eec\u4e00\u4e2a\u4e2a\u90fd\u6b7b\u5728\u6211\u7684\u638c\u4e0b\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u5374\u53c8\u5728\u5446\u5446\u671b\u7740\u6797\u5916\uff0c\u4e00\u7247\u94f6\u767d\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u88f4\u73cf\u548c\u6a80\u6587\u742a\u7684\u8eab\u5b50\u8d8a\u5750\u8d8a\u8fd1\uff0c\u5728 \u6708\u5149\u4e0b\u6e10\u6e10\u5408\u6210\u4e00\u4e2a\u5f71\u5b50\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u8001\u4eba\u5bb6\u53c8\u7b11\u4e86\uff0c\u4f38\u51fa\u67af\u7626\u7684\u624b\u6307\u8f7b\u8f7b\u5411\u5916\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u4e00\u5bf9\uff0c\u4e0d\u5c31\u751f\u50cf\u662f \u6211\u4eec\u5f53\u5e74\u7684\u5f71\u5b50\uff0c\u5509\u2014\u2014\u4f46\u613f\u6211\u7684\u83cc\u513f\uff0c\u548c\u4f60\u7684\u6885\u513f\u4e5f\u80fd\u597d\u597d\u5730\u627e\u4e00\u4e2a\u5bf9\u8c61\uff0c\u90a3\u4e48\u6211\u4eec\u6b7b\u4e86 \u4e5f\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5728\u8fd9\u5e7d\u9759\u7684\u6625\u591c\u91cc\uff0c\u5728\u8fd9\u5e7d\u9759\u7684\u91ce\u6797\u4e2d\uff0c\u8fd9\u4e24\u4e2a\u51b7\u9177\u7684\u8001\u4eba\uff0c\u4e0d\u7981\u628a\u5fc3\u91cc\u8574\u85cf\u7684\u8bb8\u4e45\u7684 \u60c5\u611f\uff0c\u90fd\u8d64\u88f8\u88f8\u5730\u8868\u767d\u51fa\u6765\u3002 \u3000\u3000\u53ea\u662f\u6b64\u523b\u56db\u91ce\u65e0\u4eba\uff0c\u4ed6\u4eec\u8bf4\u7684\u8bdd\uff0c\u8c01\u4e5f\u6ca1\u6709\u542c\u5230\uff0c\u4ed6\u4eec\u9762\u4e0a\u7684\u7b11\u5bb9\uff0c\u8c01\u4e5f\u6ca1\u6709\u770b\u5230\uff0c\u6b64 \u523b\u4ed6\u4eec\u5fc3\u4e2d\u7684\u60c5\u6d9b\uff0c\u4e0d\u7528\u591a\u4e45\u5c31\u4f1a\u5e73\u590d\uff0c\u90a3\u65f6\u4ed6\u4eec\u8fd8\u662f\u4e00\u526f\u51b7\u51b0\u51b0\u7684\u6837\u5b50\uff0c\u522b\u4eba\u518d\u4e5f\u4e0d\u4f1a\u77e5 \u9053\u4ed6\u4eec\u8fd8\u6709\u8fd9\u4e48\u4e00\u6bb5\u751c\u871c\u7684\u5f80\u4e8b\uff0c\u66f4\u4e0d\u4f1a\u77e5\u9053\u4ed6\u4eec\u8fd8\u6709\u5982\u6b64\u6e29\u60c5\u3002 \u3000\u3000\u4ed6\u4eec\u611f\u6168\u7cfb\u4e4b\u5730\u671b\u7740\u7af9\u6797\u5916\u5c0f\u6eaa\u65c1\u4e92\u76f8\u4f9d\u504e\u7740\u7684\u88f4\u73cf\u548c\u6a80\u6587\u742a\uff0c\u51b7\u5bd2\u7af9\u7a81\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c \u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u731c\u731c\u770b\u4ed6\u4eec\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u7b11\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u548c\u4f60\u4ee5\u524d\u5bf9\u8335\u5b50\u8bf4\u7684\u4e00\u6837\u3002\u201d\u54ea\u77e5\u4ed6\u8bdd\u58f0\u65b9\u4e86\uff0c\u4f9d\u504e\u5728\u88f4\u73cf\u6000\u91cc \u7684\u6a80\u6587\u742a\uff0c\u7a81\u5730\u4e00\u8dc3\u800c\u8d77\uff0c\u98de\u4e5f\u4f3c\u63a0\u4e86\u8fc7\u6765\u3002\u51b7\u67af\u6728\u3001\u51b7\u5bd2\u7af9\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6123\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u5374 \u89c1\u88f4\u73cf\u6123\u695e\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u50cf\u662f\u6839\u672c\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u7684\u6837\u5b50\u3002 \u3000\u3000\u970e\u773c\u4e4b\u95f4\uff0c\u6a80\u6587\u742a\u6de1\u9752\u7684\u4eba\u5f71\uff0c\u5df2\u63a0\u81f3\u7af9\u6797\uff0c\u811a\u6b65\u5fae\u987f\uff0c\u4f3c\u4e4e\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u7ec8\u4e8e\u4e00\u599e\u5a07 \u8eaf\uff0c\u5237\u5730\u98de\u4e0a\u6797\u68a2\u3002 \u3000\u3000\u51b7\u67af\u6728\uff0c\u51b7\u5bd2\u7af9\u6b64\u523b\u5fc3\u4e2d\u65e2\u60ca\u4e14\u5947\uff0c\u5c06\u671b\u4e00\u773c\uff0c\u888d\u8896\u5fae\u62c2\uff0c\u4e5f\u81ea\u7b14\u76f4\u5730\u62d4\u4e0a\u6797\u68a2\uff0c\u53ea\u542c \u7af9\u679d\u201c\u54d7\u201d\u7136\u4e00\u54cd\uff0c\u6a80\u6587\u742a\u4e00\u60ca\u56de\u7738\uff0c\u5374\u89c1\u4ed6\u4eec\u5df2\u7ad9\u5728\u81ea\u5df1\u8eab\u4fa7\uff0c\u5979\u4e0d\u7981\u4e5f\u4e3a\u4e4b\u4e00\u60ca\uff0c\u8131\u53e3 \u9053\uff1a\u201c\u5927\u53d4\uff0c\u4e8c\u53d4\uff0c\u60a8\u8fd8\u6ca1\u8d70\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u5fae\u4e00\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u600e\u4e48\u8c08\u7684\u597d\u597d\u7684\uff0c\u7a81\u7136\u5374\u53c8\u8d70\u4e86\u3002\u201d\u67af\u7626\u7684\u8eab \u8eaf\uff0c\u968f\u7740\u5fae\u98a4\u7684\u7af9\u679d\uff0c\u4e0d\u4f4f\u5730\u8d77\u4f0f\u7740\uff0c\u6a80\u6587\u742a\u79cb\u6ce2\u4e00\u8f6c\uff0c\u9762\u988a\u7ea2\u4e86\u8d77\u6765\uff0c\u5a07\u55d4\u7740\u9053\uff1a\u201c\u4e0d\u6765 \u4e86\uff0c\u60a8\u5077\u770b\u4eba\u5bb6\u3002\u201d\u5979\u8f7b\u529f\u867d\u5999\uff0c\u4f46\u4e00\u5410\u6c14\u53d1\u8a00\uff0c\u8eab\u8eaf\u4fbf\u751f\u50cf\u662f\u91cd\u4e86\u8d77\u6765\uff0c\u67d4\u5f31\u7684\u7af9\u679d\uff0c\u731b \u5730\u5f80\u4e0b\u4e00\u5f2f\uff0c\u5979\u4e0d\u5f97\u4e0d\u6697\u4e2d\u6362\u4e86\u53e3\u6c14\uff0c\u8f7b\u6298\u67f3\u8170\uff0c\u6a2a\u6ed1\u4e00\u6b65\uff0c\u660e\u4eae\u7684\u773c\u775b\uff0c\u5374\u4e58\u673a\u5411\u540e\u779f\u4e86 \u4e00\u773c\uff0c\u5374\u89c1\u88f4\u73cf\u4ecd\u7136\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u6839\u672c\u6ca1\u6709\u52a8\u5f39\u4e00\u4e0b\u3002 \u3000\u3000\u5979\u53e3\u4e2d\u54fc\u4e86\u4e00\u58f0\uff0c\u6a31\u5507\u4e00\u64a4\uff0c\u50cf\u662f\u5728\u8bf4\uff1a\u201c\u8c01\u7a00\u7f55\u4f60\u3002\u201d\u51b7\u5bd2\u7af9\u76ee\u5149\u52a8\u5904\uff0c\u4ea6\u81ea\u5fae\u5fae\u76b1 \u7709\u9053\uff1a\u201c\u742a\u513f\uff0c\u544a\u8bc9\u6211\uff0c\u662f\u4e0d\u662f\u90a3\u59d3\u88f4\u7684\u5c0f\u5b50\u6b3a\u8d1f\u4e86\u4f60\uff0c\u54fc\uff01\u4ed6\u82e5\u771f\u7684\u6b3a\u8d1f\u4e86\u4f60\u2026\u2026\u201d\u4ed6\u53c8 \u81ea\u51b7\u54fc\u4e00\u58f0\uff0c\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u54fc\u58f0\u4e4b\u4e2d\uff0c\u6ee1\u542b\u68ee\u51b7\u4e4b\u610f\uff0c\u54ea\u77e5\u6a80\u6587\u742a\u5374\u53c8\u5a07\u7b11\u4e86\u8d77\u6765\uff0c\u6309 \u53e3\u9053\uff1a\u201c\u4e8c\u53d4\uff0c\u60a8\u53d1\u4ec0\u4e48\u813e\u6c14\uff0c\u4eba\u5bb6\u53c8\u6ca1\u6709\u6b3a\u8d1f\u6211\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u7adf\u662f\u4f60\u8fd9\u813e\u6c14\u53d1\u9519\u4e86\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u4e0d\u7981\u4e00\u6123\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u53d1\u8fd9\u813e\u6c14\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4f60\uff0c\u4f60\u5374\u602a\u8d77\u6211\u6765\u4e86\u3002\u201d\u4ed6\u867d\u662f \u9605\u5386\u4e30\u5bcc\uff1a\u4f46\u5bf9\u8fd9\u5c11\u5973\u7684\u5fc3\u4e8b\uff0c\u7ec8\u7a76\u8fd8\u662f\u5f04\u4e0d\u6e05\u695a\uff0c\u5fc3\u4e2d\u4e00\u9762\u53d1\u6123\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4ed6\u82e5\u6ca1\u6709 \u6b3a\u8d1f\u4f60\uff0c\u90a3\u4e48\u5c31\u662f\u4f60\u8fd9\u4e2b\u5934\u75af\u4e86\u3002\u201d\u64c5\u6587\u742a\u201c\u5657\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u662f\u6545\u610f\u6c14\u6c14\u4ed6\uff0c\u8c01\u53eb\u4ed6\u603b \u662f\u90a3\u4e2a\u6837\u5b50\uff0c\u8fc7\u4e24\u592d\uff0c\u7b49\u6211\u6c14\u6d88\u4e86\uff0c\u6211\u518d\u6765\u6211\u4ed6\uff0c\u5927\u53d4\u4e8c\u53d4\uff0c\u6211\u4eec\u8d70\u5427\uff0c\u8fd8\u803d\u5728\u8fd9\u91cc\u5e72 \u5417\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5979\u5a07\u8eaf\u5fae\u8f6c\uff0c\u4fbf\u53c8\u63a0\u53bb\u6570\u5c3a\uff0c\u51b7\u5bd2\u7af9\u671b\u7740\u5979\u7a88\u7a95\u7684\u80cc\u5f71\uff0c\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u4f4e \u8bed\u51b7\u67af\u6728\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u73b0\u5728\u7684\u5973\u5b69\u5b50\uff0c\u6bd4\u4e09\u5341\u5e74\u524d\u8fd8\u8981\u5201\u86ee\u53e4\u602a\u3002\u201d\u4f38\u624b\u4e00\u62c9\u51b7\u67af\u6728\u7684\u8863 \u8896\uff0c\u4ea6\u81ea\u8ddf\u8e2a\u63a0\u53bb\uff0c\u7af9\u6797\u5fae\u7c38\u4e4b\u95f4\uff0c\u4eba\u8e2a\u4fbf\u5df2\u5168\u6773\uff0c\u53ea\u5269\u4e0b\u5446\u7acb\u5728\u6797\u5916\u7684\u88f4\u73cf\uff0c\u53ea\u81ea\u671b\u7740\u8fd9 \u8fb9\u51fa\u795e\u3002 \u3000\u3000\u4eba\u8e2a\u53bb\u4e86\uff0c\u6797\u68a2\u7a7a\u4e86\uff0c\u6708\u5149\u4ece\u4e1c\u65b9\u5347\u8d77\uff0c\u73b0\u5728\u5df2\u843d\u5230\u897f\u65b9\u4e86\u3002 \u3000\u3000\u4ed6\u9ed8\u9ed8\u5730\u5782\u4e0b\u5934\uff0c\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u5979\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u600e\u5730\u7a81\u7136\u8d70\u4e86\uff1f\u5509\u2014\u2014\u6211\u8fde\u5979\u843d\u811a\u7684 \u5730\u65b9\u90fd\u4e0d\u77e5\u9053\uff0c\u53c8\u600e\u80fd\u627e\u5979\uff0c\u76f8\u601d\u4e00\u8f7d\uff0c\u5374\u6362\u5f97\u5306\u5306\u62c2\u8896\u800c\u53bb\uff0c\u6587\u742a\uff0c\u4f60\u5230\u5e95\u600e\u4e48\u4e86\u5440\uff1f\u3001 \u4ed6\u60c6\u6005\u5730\u53f9\u606f\u7740\uff0c\u7ad9\u5728\u6708\u5149\u4e0b\uff0c\u751a\u81f3\u8fde\u811a\u6b65\u90fd\u4e0d\u613f\u62ac\u8d77\u3002\u65b9\u624d\u5979\u6e29\u67d4\u7684\u8a00\u8bed\uff0c\u6b64\u523b\u4ecd\u5728\u4ed6\u8033 \u8fb9\u8361\u6f3e\u7740\u2014\u2014\u201d\u4f60\u8d70\u4e86\u4e4b\u540e\uff0c\u6211\u54ed\u4e86\u597d\u51e0\u665a\u4e0a\uff0c\u53ea\u671b\u4f60\u5f88\u5feb\u5730\u5c31\u4f1a\u56de\u6765\uff0c\u54ea\u77e5\u9053\u4e00\u5929\u3001\u4e24 \u5929\u3001\u4e00\u4e2a\u6708\u3001\u4e24\u4e2a\u6708\uff0c\u4f60\u5374\u8fde\u4e00\u70b9\u6d88\u606f\u90fd\u6ca1\u6709\uff0c\u6211\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\uff0c\u4e5f\u5077\u5077\u5730\u8dd1\u4e86\u51fa\u6765\uff0c\u4f60\u77e5\u9053 \u5417\uff1f\u6211\u4e3a\u4f60\u5403\u4e86\u591a\u5c11\u6614\uff0c\u65e0\u8bba\u662f\u6709\u6708\u4eae\u7684\u665a\u4e0a\uff0c\u8fd8\u662f\u6ca1\u6709\u6708\u4eae\u7684\u665a\u4e0a\uff0c\u6211\u90fd\u4f1a\u671b\u7740\u9ed1\u6697\u7684 \u5929\uff0c\u4f4e\u4f4e\u5730\u5ff5\u7740\u4f60\u7684\u540d\u5b57\uff0c\u4f60\u53ef\u542c\u89c1\u4e86\uff1f\u201c\u4e8e\u662f\u4ed6\u7684\u5fc3\uff0c\u4fbf\u5728\u8fd9\u6e29\u67d4\u7684\u8a00\u8bed\u4e0b\uff0c\u5316\u505a\u4e00\u6c60\u8361 \u6f3e\u7684\u6625\u6c34\u3002\u4ed6\u9eef\u7136\u4f38\u51fa\u624b\uff0c\u8fd9\u53ea\u624b\u65b9\u624d\u8fd8\u5728\u5979\u7684\u638c\u63e1\u91cc\uff0c\u5979\u629a\u6478\u7740\u8fd9\u53ea\u624b\uff0c\u4f9d\u4f9d\u4ed6\u8bf4\uff1a\u201c\u8fd9 \u4e00\u5e74\u6765\u4f60\u6709\u6ca1\u6709\u60f3\u6211\u5440\uff1f\u201d\u4ed6\u5e78\u798f\u5730\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u4e0d\u4f4f\u70b9\u5934\uff0c\u5979\u53c8\u8bf4\uff1a\u201c\u5582\uff0c\u542c\u8bf4\u4f60\u8981\u5f53\u603b \u74e2\u628a\u5b50\u4e86\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u5440\uff1f\u201d \u3000\u3000\u4ed6\u82e6\u7b11\u4e86\uff0c\u6b63\u5f85\u8bf4\u51fa\u81ea\u5df1\u8fd9\u4e00\u5e74\u7684\u906d\u9047\uff0c\u5374\u53c8\u7a81\u7136\u60f3\u8d77\u90a3\u53ef\u7231\u7684\u8881\u6cf8\u73cd\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u5148\u95ee \u5979\uff1a\u201c\u73cd\u73cd\u5462\uff1f\u5979\u53ef\u597d\uff1f\u6211\u8d70\u4e86\u5979\u6709\u6ca1\u6709\u54ed\uff1f\u201d\u54ea\u77e5\u5979\u542c\u4e86\u8fd9\u8bdd\uff0c\u5c31\u7a81\u7136\u8d70\u4e86\u3002 \u3000\u3000\u201c\u5509\uff01\u5973\u5b69\u5b50\u7684\u5fc3\uff0c\u771f\u662f\u96be\u6d4b\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u6211\u53ea\u5f53\u5979\u5df2\u8fdc\u6bd4\u4ee5\u524d\u6e29\u67d4\u4e86\uff0c\u54ea\u77e5\u5979\u8fd8\u662f \u4ee5\u524d\u90a3\u6837\u5b50\uff0c\u65e2\u53ef\u7231\uff0c\u5374\u53c8\u5a07\u7eb5\u5201\u86ee\uff0c\u6587\u742a\uff0c\u4f60\u4e0d\u8be5\u5bf9\u6211\u8fd9\u6837\u5440\uff1f\u4f60\u8be5\u77e5\u9053\uff0c\u4f60\u8fd9\u6837\u591a\u4f24\u6211 \u7684\u5fc3\u3002\u201d \u3000\u3000\u5782\u4e0b\u5934\uff0c\u4ed6\u6478\u4e86\u6478\u81ea\u5df1\u7684\u8863\u895f\uff0c\u8863\u895f\u72b9\u6e29\uff0c\u6e29\u9999\u72b9\u5728\u2014\u2014\u7247\u523b\u4e4b\u524d\uff0c\u5979\u8fd8\u4f9d\u504e\u5728\u4ed6\u7684\u6000 \u62b1\u91cc\uff0c\u7136\u800c\u6b64\u523b\u5462\uff1f\u5374\u53ea\u5269\u4e86\u4ed6\u81ea\u5df1\u7684\u5f71\u5b50\uff0c\u957f\u957f\u5730\u62d6\u5728\u5730\u4e0a\u3002 \u3000\u3000\u54a6\u4e00\u4e00 \u3000\u3000\u6eaa\u65c1\u7684\u5730\u662f\u5e73\u5766\u7684\uff0c\u4ed6\u5b64\u96f6\u96f6\u5730\u4f4d\u7acb\u7740\uff0c\u6708\u5149\u4ece\u4ed6\u8eab\u540e\u5c04\u6765\uff0c\u8fd9\u5e73\u5766\u7684\u571f\u5730\uff0c\u5374\u600e\u5730\u6709 \u4e24\u4e2a\u957f\u957f\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\uff0c\u4e0d\u7981\u4e3a\u4e4b\u731b\u5730\u8df3\u52a8\u4e00\u4e0b\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u6240\u6709\u7684\u601d\u6f6e\uff0c\u5df2\u53d8\u4e3a\u60ca\u60e7\uff0c\u4ed6\u6765\u4e0d \u53ca\u518d\u60f3\u522b\u7684\uff0c\u84e6\u7136\u626d\u8f6c\u8eab\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u4ed6\u8eab\u5f62\u65b9\u8f6c\uff0c\u773c\u524d\u7a81\u5730\u4eba\u5f71\u4e00\u82b1\uff0c\u7adf\u6709\u4e24\u6761\u4eba\u5f71\uff0c\u4ece\u4ed6\u8eab\u8eaf\u7684\u4e24\u4fa7\u63a0\u8fc7\uff0c\u4ed6\u53ea\u89c9\u81ea\u5df1\u7684 \u5de6\u53f3\u53cc\u81c2\uff0c\u90fd\u88ab\u4eba\u8f7b\u8f7b\u6309\u4e86\u4e00\u4e0b\u3002\u7b49\u4ed6\u8eab\u5f62\u7ad9\u7a33\u7684\u65f6\u5019\uff0c\u773c\u524d\u5374\u53c8\u662f\u7a7a\u8361\u8361\u5730\uff0c\u534a\u6761\u4eba\u5f71\u90fd \u770b\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u811a\u6b65\u5fae\u9519\uff0c\u84e6\u7136\u518d\u4e00\u8f6c\u8eab\uff0c\u53e3\u4e2d\u5389\u58f0\u53f1\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u8eab\u540e\u4e00\u58f0\u51b7\u7b11\uff0c\u4ed6\u773c\u524d\u4eba\u5f71\u53c8\u81ea\u4e00\u82b1\uff0c\u53c8\u662f\u4e24\u6761\u4eba\u5f71\uff0c\u4ece\u4ed6\u8eab\u8eaf\u897f\u4fa7\u63a0\u8fc7\uff0c\u201c\u5427\u3001\u5427\u201d \u4e24\u54cd\uff0c\u4ed6\u5de6\u53f3\u53cc\u80a9\u53c8\u88ab\u62cd\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u2014\u2014 \u3000\u3000\u5730\uff0c\u4ecd\u7136\u662f\u5e73\u5766\u7684\uff0c\u5730\u4e0a\u7684\u4eba\u5f71\uff0c\u4ecd\u7136\u53ea\u6709\u4e24\u6761\uff0c\u4e00\u524d\uff0c\u4e00\u540e\u7684\uff0c\u524d\u9762\u7684\u5f71\u5b50\u662f\u4ed6\u81ea\u5df1 \u7684\uff0c\u540e\u9762\u7684\u5f71\u5b50\u662f\u8c01\u7684\u5462\uff1f\u96be\u9053\u8fd9\u4e24\u4eba\u5176\u4e2d\u4e4b\u4e00\u662f\u6ca1\u6709\u5f71\u5b50\u7684\uff1f\u4ed6\u4e00\u634f\u638c\u5fc3\uff0c\u638c\u5fc3\u6c81\u51fa\u51b7\u6c57 \u4e86\u3002\u665a\u98ce\u5439\u5230\u4ed6\u8eab\u4e0a\uff0c\u4e5f\u5f00\u59cb\u6709\u523a\u9aa8\u7684\u5bd2\u610f\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u65e2\u60ca\u4e14\u60e7\uff0c\u60f3\u8d77\u51e0\u65f6\u6240\u542c\u7684\u6545\u4e8b\uff1a\u201c\u4eba\u90fd\u6709\u5f71\u5b50\uff0c\u53ea\u6709\u9b3c\uff0c\u624d\u6ca1\u6709\u5f71 \u5b50\u7684\u3002\u201d\u4ed6\u4e0d\u7981\u66f4\u4e3a\u4e4b\u6817\u7136\u3002 \u3000\u3000\u4ed6\u60ca\u6817\u5730\u7ad9\u7740\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u540e\u9762\u7684\u5f71\u5b50\u7a76\u7adf\u662f\u8c01\uff1f\u4ed6\u60f3\u4e5f\u4e0d\u6562\u60f3\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u5730\u4e0a \u7684\u4e24\u6761\u5f71\u5b50\uff0c\u4e5f\u6ca1\u6709\u4e1d\u6beb\u52a8\u4f5c\uff0c\u4ed6\u6084\u6084\u54bd\u4e0b\u4e00\u53e3\u553e\u6cab\uff0c\u54ea\u77e5\u8eab\u540e\u7a81\u53c9\u4f20\u6765\u4e00\u9635\u51b7\u7b11\u3002 \u3000\u3000\u540e\u9762\u7684\u90a3\u6761\u5f71\u5b50\uff0c\u4e5f\u5f00\u59cb\u5f80\u524d\u79fb\u52a8\u8d77\u6765\uff0c\u8ddd\u79bb\u81ea\u5df1\u7684\u5f71\u5b50\uff0c\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4ed6\u673a\u4f36\u4f36\u6253\u4e86\u4e2a \u5bd2\u56b7\uff0c\u4e0b\u610f\u8bc6\u5730\u5f80\u524d\u8d70\u4e86\u4e00\u6b65\uff0c\u51b7\u7b11\u4e4b\u58f0\uff0c\u66f4\u523a\u9aa8\u4e86\u3002 \u3000\u3000\u62ac\u9996\u4e00\u671b\uff0c\u5929\u4e0a\u4ecd\u7136\u7fa4\u661f\u707f\u70c2\uff0c\u8ddd\u79bb\u5929\u660e\uff0c\u4f3c\u4e4e\u8fd8\u6709\u4e00\u6bb5\u5f88\u957f\u7684\u65f6\u5019\uff0c\u4ed6\u4e8e\u54b3\u4e00\u58f0\uff0c\u6697 \u4e2d\u5fd6\u9053\uff1a\u201c\u88f4\u73cf\u5440\u88f4\u73cf\uff0c\u4f60\u96be\u9053\u771f\u662f\u4e2a\u65e0\u7528\u7684\u61e6\u592b\uff0c\u600e\u5730\u5982\u6b64\u80c6\u5c0f\uff0c\u540e\u9762\u7eb5\u7136\u662f\u4e2a\u9b3c\u9b45\uff0c\u53ea \u8981\u4f60\u95ee\u5fc3\u65e0\u6127\uff0c\u53c8\u6709\u4f55\u60e7\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u80c6\u5b50\u4e0d\u7981\u4e00\u58ee\uff0c\u6545\u610f\u7406\u4e5f\u4e0d\u7406\u90a3\u6761\u5f71\u5b50\uff0c\u5927\u6b65\u5411\u5e84\u9662\u8d70\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u80cc\u540e\u51b7\u7b11\u4e4b\u58f0\u7a81\u5730\u4e00\u987f\uff0c\u4e00\u4e2a\u7ec6\u5ae9\u67d4\u8106\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u88f4\u73cf\uff0c\u7ad9\u4f4f\u3002\u201d \u3000\u3000\u88f4\u73cf\u80c6\u5b50\u518d\u5927\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u5fc3\u9b42\u7686\u843d\uff1a\u201c\u4ed6\u600e\u5730\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u5b9a\u4e86\u5b9a\u795e\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u662f\u88f4\u73cf\uff0c\u9601\u4e0b\u6709\u4f55\u89c1\u6559\uff1f\u201d\u4ed6\u867d\u7136\u88c5\u4f5c\u9547\u9759\uff0c\u4f46\u8bed\u6c14\u4e4b \u4e2d\uff0c\u5374\u4e5f\u4e0d\u7981\u5fae\u5e26\u98a4\u6296\u4e86\u3002\u8eab\u540e\u7684\u8bed\u58f0\u68ee\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u88f4\u73cf\uff0c\u6211\u6b63\u8981\u627e\u4f60\u3002\u201d\u8bed \u58f0\u7c97\u58ee\uff0c\u6709\u5982\u6d2a\u949f\uff0c\u54ea\u91cc\u8fd8\u662f\u65b9\u624d\u90a3\u79cd\u7ec6\u5ae9\u67d4\u8106\u7684\u58f0\u97f3\u3002 \u3000\u3000\u88f4\u73cf\u53c8\u4e3a\u4e4b\u60ca\u6115\u4f4f\u4e86\uff0c\u53e3\u4e2d\u6162\u6162\u8bf4\u9053\u3002 \u3000\u3000\u201c\u6709\u4f55\u8d35\u5e72\uff1f\u201d\u5fc3\u4e2d\u5374\u662f\u7591\u4e91\u5927\u8d77\uff0c\u4fef\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u81ea\u5df1\u7684\u5f71\u5b50\uff0c\u5728\u5730\u4e0a\u6620\u6210\u7b14\u76f4\u7684\u4e00 \u6761\uff0c\u4eff\u4f5b\u8fde\u624b\u811a\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u96be\u9053\u6211\u6ca1\u6709\u624b\u811a\u5417\uff1f\u53ea\u662f\u6620\u5728\u5730\u4e0a\u7684\u5f71\u5b50\u5206\u4e0d\u6e05\u7f62\u4e86\u3002\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6 \u5fc3\u4e2d\u7684\u60ca\u60e7\uff0c\u4e0d\u7981\u5927\u51cf\uff0c\u5374\u542c\u8eab\u540e\u7684\u8bed\u58f0\uff0c\u53c8\u6362\u4e86\u65b9\u624d\u90a3\u79cd\u7ec6\u5ae9\u800c\u67d4\u8106\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u5148 \u522b\u95ee\u6211\u627e\u4f60\u4f5c\u4ec0\u4e48\uff1f\u6211\u5148\u95ee\u4f60\uff0c\u6211\u7a76\u7adf\u662f\u4eba\u662f\u9b3c\uff1f\u563f\u563f\u2014\u2014\u201d\u4ed6\u53c8\u81ea\u51b7\u51c4\u51c4\u5730\u51b7\u7b11\u6570\u58f0\uff0c\u63a5 \u9053\uff1a\u201c\u4f60\u7740\u56de\u7b54\u4e0d\u51fa\uff0c\u6211\u5c31\u628a\u4f60\u5403\u4e86\u3002\u201d \u3000\u3000\u54ea\u77e5\u88f4\u73cf\u5374\u4e00\u633a\u80f8\u819b\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u5f53\u7136\u662f\u4eba\u3002\u201d \u3000\u3000\u8eab\u540e\u7684\u4eba\u5f71\u4f3c\u4e4e\u60ca\u5f02\u5730\u8f7b\u5531\u4e86\u4e00\u58f0\uff0c\u65b9\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u662f\u4eba\uff1f\u544a\u8bc9\u4f60\uff0c\u6211\u4e0d\u662f\u4eba\uff0c \u4eba\u54ea\u91cc\u4f1a\u5206\u6210\u4e24\u4e2a\u8eab\u4f53\uff0c\u4e24\u79cd\u58f0\u97f3\uff0c\u563f\u563f\u2026\u2026\u4f60\u731c\u9519\u4e86\uff0c\u6211\u8981\u628a\u4f60\u7684\u9aa8\u5934\u90fd\u5403\u6389\u3002\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u867d\u7136\u8bf4\u5f97\u66f4\u4e3a\u60ca\u4eba\uff0c\u4f46\u88f4\u73cf\u5fc3\u4e2d\uff0c\u6b64\u523b\u5374\u5df2\u5168\u65e0\u60e7\u610f\uff0c\u7adf\u81ea\u54c8\u54c8\u4e00\u7b11\uff0c\u5927\u58f0\u9053\uff1a \u201c\u6211\u975e\u4f46\u77e5\u9053\u4f60\u4eec\u662f\u4e24\u4e2a\u4eba\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u4e00\u5927\u4e00\u5c0f\uff0c\u4e24\u4e2a\u4eba\u7ad9\u5728\u4e00\u8d77\uff0c\u5730\u4e0a\u5f53\u7136\u53ea\u6709\u4e00\u6761\u5f71 \u5b50\uff0c\u54c8\u54c8\uff0c\u6211\u65b9\u624d\u90fd\u9669\u4e9b\u4e0a\u4e86\u4f60\u4eec\u7684\u5f53\u4e86\u3002\u201d \u3000\u3000\u987b\u77e5\u4ed6\u672c\u662f\u806a\u660e\u7edd\u9876\u7684\u4eba\uff0c\u65b9\u624d\u52a8\u5ff5\u4e4b\u95f4\uff0c\u5df2\u81ea\u60f3\u5230\u6b64\u4e00\u53ef\u80fd\uff0c\u4ed4\u7ec6\u4e00\u60f3\u8d8a\u89c9\u81ea\u5df1\u731c\u6d4b \u7edd\u4e0d\u4f1a\u9519\uff0c\u6b64\u523b\u8bf4\u4e86\u51fa\u6765\uff0c\u60f3\u5230\u81ea\u5df1\u65b9\u624d\u7684\u754f\u60e7\u4e4b\u610f\uff0c\u53ea\u89c9\u751a\u4e3a\u53ef\u7b11\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7b11\u58f0\u8d8a\u6765\u8d8a\u5927\uff0c\u5230\u540e\u6765\u751a\u4e14\u7b11\u5f97\u5f2f\u4e0b\u8170\u53bb\uff0c\u4e00\u9762\u9053\uff1a\u201c\u6211\u65b9\u624d\u771f\u7b28\uff0c\u600e\u4e48\u8fde\u8fd9\u4e2a \u9053\u7406\u90e8\u60f3\u4e0d\u51fa\u6765\uff0c\u8fd8\u53ea\u5f53\u4f60\u4eec\u5176\u4e2d\u80af\u4e2a\u662f\u9b3c\uff0c\u6839\u672c\u6ca1\u6709\u5f71\u5b50\u7684\u3002\u201d \u3000\u3000\u7b11\u58f0\u672a\u6b47\uff0c\u8eab\u540e\u7684\u4eba\u5f71\u7adf\u4e5f\u7b11\u4e86\u8d77\u6765\uff0c\u88f4\u73cf\u6ee1\u8033\u4ff1\u662f\u7b11\u58f0\uff0c\u53ea\u89c9\u5f97\u7b11\u58f0\u4ece\u8eab\u540e\u79fb\u81f3\u8eab \u524d\uff0c\u4e0d\u7981\u62ac\u76ee\u671b\u53bb\uff0c\u54ea\u77e5\u4ed6\u76ee\u5149\u52a8\u5904\uff0c\u5374\u53c8\u4e0d\u7981\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u6b64\u523b\u7ad9\u5728\u4ed6\u8eab\u524d\u7684\uff0c\u7adf\u662f\u4e00\u4e2a\u8eab\u8eaf\u9ad8\u5927\u65e0\u6bd4\u7684\u5973\u5b50\uff0c\u624b\u811a\u7c97\u58ee\uff0c\u5251\u7709\u864e\u76ee\uff0c\u82e5\u4e0d\u662f\u5979\u5934 \u4e0a\u4e91\u9b13\u9ad8\u633d\uff0c\u88f9\u7740\u4e00\u4ef6\u8f7b\u7f57\u81ea\u886b\u4e2d\u7684\u8170\u8eab\uff0c\u4e5f\u7565\u6709\u8d77\u4f0f\uff0c\u53ea\u6015\u4efb\u4f55\u4eba\u4e5f\u4e0d\u4f1a\u5c06\u5979\u770b\u4f5c\u662f\u5973 \u4eba\u3002 \u3000\u3000\u88f4\u73cf\u4e00\u773c\u671b\u53bb\uff0c\u518d\u4e5f\u60f3\u4e0d\u5230\u4e16\u95f4\u8fd8\u6709\u8fd9\u79cd\u7c97\u58ee\u5973\u5b50\uff0c\u4e00\u5446\u4e4b\u4e0b\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u4e0d\u7981\u53c8\u4e3a\u4e4b \u8fde\u9000\u6570\u6b65\uff0c\u7b11\u58f0\u4e5f\u4e3a\u4e4b\u500f\u7136\u987f\u4f4f\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u767d\u886b\u5973\u5b50\u7684\u524d\u80f8\uff0c\u4ea4\u7ec7\u7740\u4e24\u6761\u9ec4\u91d1\u8272\u7684\u5e26\u5b50\uff0c\u5e26\u5b50\u540e\u9762\uff0c\u7ed1\u6ce8\u4e00\u4e2a\u9ec4\u91d1\u8272\u7684\u85e4 \u7ba9\uff0c\u85e4\u7ba9\u4e4b\u4e2d\uff0c\u7ade\u5750\u7740\u4e00\u4e2a\u6ee1\u8eab\u91d1\u886b\u7684\u7537\u5b50\uff0c\u8eab\u8eaf\u7279\u5c0f\uff0c\u6709\u5982\u5e7c\u7ae5\uff0c\u4f46\u5374\u8863\u51a0\u5ce8\u7136\uff0c\u6b63\u81ea\u4e00 \u624b\u63a5\u7740\u9894\u4e0a\u957f\u987b\u653e\u58f0\u5927\u7b11\u7740\uff0c\u7b11\u58f0\u7c97\u6d2a\uff0c\u6709\u5982\u94dc\u949f\uff0c\u4e00\u53cc\u660e\u4eae\u7684\u773c\u775b\uff0c\u4ea6\u81ea\u671b\u5728\u88f4\u73cf\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u5e74\u4e4b\u4e2d\uff0c\u88f4\u73cf\u904d\u5386\u6c5f\u6e56\uff0c\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u89c1\u8fc7\u4e0d\u77e5\u6709\u591a\u5c11\uff0c\u6709\u7684\u5f88\u80d6\uff0c\u6709\u7684\u5f88\u7626\uff0c \u6709\u7684\u5f88\u9ad8\uff0c\u6709\u7684\u5f88\u77ee\uff0c\u4f46\u662f\u4ed6\u8fde\u505a\u68a6\u4e5f\u6ca1\u6709\u89c1\u8fc7\u50cf\u8fd9\u6837\u7684\u5973\u5b50\uff0c\u66f4\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u7537\u5b50\u4e86\u3002 \u3000\u3000\u8fd9\u7537\u5973\u4e24\u4eba\u4ef0\u5929\u800c\u7b11\uff0c\u90a3\u8eab\u8eaf\u9ad8\u5927\u7684\u5973\u5b50\u7a81\u7684\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u88f4\u73cf\uff0c\u4e0d\u602a\u4eba\u5bb6\u8bf4\u4f60\u806a\u660e\uff0c \u4f60\u679c\u7136\u806a\u660e\u5f97\u5f88\uff0c\u6211\u592b\u5987\u4e24\u4eba\u8fd9\u6837\u4e0d\u77e5\u5413\u8fc7\u591a\u5c11\u4eba\uff0c\u60f3\u4e0d\u5230\u8fd9\u6b21\u5374\u5413\u4e0d\u6b7b\u4f60\u3002\u201d\u8eab\u8eaf\u867d\u7c97 \u58ee\uff0c\u58f0\u97f3\u5374\u5a07\u67d4\uff0c\u76f8\u5f62\u4e4b\u4e0b\uff0c\u66f4\u89c9\u5947\u5f02\u3002 \u3000\u3000\u672c\u5df2\u60ca\u6115\u65e0\u6bd4\u7684\u88f4\u73cf\uff0c\u6b64\u523b\u4e0d\u7981\u4e3a\u4e4b\u53c8\u4e00\u6115\uff0c\u65e5\u5149\u4ece\u8fd9\u9ad8\u5927\u7c97\u58ee\u7684\u5973\u5b50\u8eab\u4e0a\uff0c\u8f6c\u5230\u5979\u8eab \u540e\u80cc\u7740\u7684\u90a3\u4f8f\u5112\u822c\u7684\u7537\u5b50\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u96be\u9053\u8fd9\u4e24\u4eba\u7adf\u662f\u592b\u59bb\uff1f\u201d\u4ed6\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\u548c\u8033\u6735\uff0c\u4f46\u8fd9\u4e24\u4eba\u53c8\u662f\u90a3\u4e48\u771f\u5207 \u7ad9\u5728\u81ea\u5df1\u9762\u524d\uff0c\u90a3\u4e48\u771f\u5207\u4ed6\u8bf4\u9053\uff1a\u201c\u2026\u2026\u6211\u592b\u5987\u4e24\u4eba\u2026\u2026\u201d \u3000\u3000\u5374\u542c\u90a3\u7537\u5b50\u7b11\u58f0\u7a81\u5730\u4e00\u987f\uff0c\u76ee\u5149\u6df1\u7136\u671b\u7740\u88f4\u73cf\uff0c\u7f13\u7f13\u8bf4\u9053\uff0c\u201c\u4f60\u600e\u5730\u7b11\u4e0d\u51fa\u6765\u4e86\uff0c\u770b\u7740 \u6211\u592b\u5987\u4e24\u4eba\u6709\u4e9b\u4e0d\u5927\u987a\u773c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u88f4\u73cf\u5440\u88f4\u73cf\uff0c\u4f60\u600e\u5730\u4f5c\u51fa\u8fd9\u79cd\u8868\u60c5\u6765\uff0c\u4e14\u592b\u5987\u4fe9\u7684\u6837\u5b50\u867d\u7136\u53ef \u60ca\u53ef\u7b11\uff0c\u4f46\u4ed6\u4eec\u4e4b\u95f4\uff0c\u5fc5\u5b9a\u5305\u542b\u7740\u4e00\u4e2a\u65e0\u6bd4\u52a8\u4eba\u7684\u6545\u4e8b\uff0c\u5982\u5176\u8fd9\u6837\uff0c\u624d\u66f4\u663e\u51fa\u8fd9\u4e24\u4eba\u7ed3\u5408\u7684 \u53ef\u4f69\uff0c\u4f60\u81ea\u5df1\u4e5f\u66fe\u6b8b\u5e9f\u8fc7\uff0c\u4e5f\u66fe\u4e86\u89e3\u6b8b\u5e9f\u4e4b\u4eba\u7684\u75db\u82e6\uff0c\u6b64\u523b\u4f60\u600e\u5730\u4f1a\u5bf9\u522b\u4eba\u7684\u75db\u82e6\u548c\u4e0d\u5e78\u5982 \u6b64\u5462\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u89c9\u6b49\u7136\uff0c\u5fd9\u81ea\u4e00\u6574\u9762\u4e0a\u7684\u8868\u60c5\uff0c\u957f\u63d6\u9053\uff1a\u201c\u5c0f\u5b50\u65e0\u77e5\uff0c\u8fd8\u8bf7\u6055 \u7f6a\u3002\u201d \u3000\u3000\u4ed6\u5e76\u4e0d\u66ff\u81ea\u5df1\u65b9\u624d\u7684\u884c\u4e3a\u89e3\u91ca\u548c\u63a9\u9970\uff0c\u53ea\u662f\u4f46\u767d\u5730\u627f\u8ba4\uff0c\u800c\u4e14\u7acb\u523b\u6539\u8fc7\uff0c\u90a3\u7537\u5b50\u7684\u76ee\u5149 \u5728\u4ed6\u9762\u4e0a\u51dd\u6ce8\u534a\u664c\uff0c\u88f4\u73cf\u53ea\u89c1\u4ed6\u770b\u6765\u867d\u7136\u53ef\u7b11\uff0c\u4f46\u76ee\u5149\u4e4b\u4e2d\uff0c\u5374\u6709\u79cd\u4e0d\u53ef\u4fb5\u72af\u7684\u795e\u91c7\uff0c\u800c\u4e14 \u9762\u76ee\u4e4b\u95f4\uff0c\u82f1\u633a\u4fca\u9038\uff0c\u4e1d\u6beb\u6ca1\u6709\u72fc\u72c8\u7684\u6837\u5b50\u3002 \u3000\u3000\u90a3\u767d\u886b\u5973\u5b50\u66f4\u662f\u7709\u76ee\u5f00\u9614\uff0c\u4ed4\u7ec6\u4e00\u671b\uff0c\u4ea6\u6709\u4e09\u5206\u59a9\u5a9a\u4e4b\u6001\uff0c\u82e5\u4e0d\u662f\u5973\u7684\u8eab\u8eaf\u592a\u8fc7\u7c97\u58ee\u9ad8 \u5927\uff0c\u7537\u7684\u5374\u53c8\u662f\u4f8f\u5112\uff0c\u8fd9\u4e00\u7537\u4e00\u5973\uff0c\u5012\u771f\u7684\u662f\u5bf9\u6781\u597d\u592b\u5987\u3002 \u3000\u3000\u90a3\u4f8f\u5112\u7537\u5b50\u51dd\u76ee\u534a\u664c\uff0c\u7a81\u53c8\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u6b3a\u4e0d\u8bc8\uff0c\u4e0d\u9a84\u4e0d\u9981\uff0c\u5374\u53c8\u806a\u660e\u7edd\u9876\uff0c\u5140\u81ea\u96be\u5f97 \u7684\u5f88\u3002\u201d\u85e4\u7ba9\u4e2d\u4f38\u5c71\u5a74\u513f\u822c\u7684\u624b\u81c2\uff0c\u8f7b\u8f7b\u4e00\u62cd\u90a3\u767d\u886b\u5973\u5b50\u7684\u80a9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u73ca\u73ca\uff0c\u6211\u8bf4\u5979\u4e0d \u4f1a\u770b\u9519\u4eba\u7684\u3002\u4f60\u770b\uff0c\u6211\u8bf4\u7684\u8bdd\u53ef\u6709\u9519\u8fc7\uff1f\u201d\u4e00\u634b\u9894\u4e0b\u67f3\u987b\uff0c\u4eff\u4f5b\u751a\u4e3a\u5f97\u610f\u3002 \u3000\u3000\u90a3\u767d\u886b\u5973\u5b50\u5a07\u58f0\u4e00\u7b11\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u88f4\u73cf\u9762\u4e0a\u867d\u7136\u606d\u8c28\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u201c\u5148\u524d\u6211\u53ea\u5f53\u90a3\u7c97\u8c6a\u7684\u58f0\u97f3\uff0c\u5fc5\u662f\u53d1\u81ea\u4e00\u5f6a\u5f62\u5927\u6c49\uff0c\u67d4\u8106\u7684\u58f0\u97f3\uff0c\u5219\u53d1\u81ea\u4e00\u4e2a\u5a07\u5f31\u5973\u5b50\uff0c\u54ea \u77e5\u5374\u662f\u6070\u6070\u76f8\u53cd\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u53c8\u81ea\u5fd6\u9053\uff1a\u201c\u6211\u4e0e\u8fd9\u4e24\u4eba\u4ece\u672a\u8c0b\u9762\uff0c\u4f46\u4ed6\u4eec\u8a00\u8bcd\u4e4b\u4e2d\uff0c\u5374\u50cf\u5bf9\u6211\u9887\u4e3a\u719f\u6089\uff0c \u800c\u4e14\u8fd8\u662f\u7279\u5730\u6765\u6b64\u5bfb\u8bbf\u4e8e\u6211\u7684\uff0c\u8fd9\u5374\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u4ed6\u767e\u601d\u4e0d\u89e3\uff0c\u53c8\u81ea\u957f\u63d6\u9053\uff1a\u201c\u4e24\u4f4d\u524d\u8f88\uff0c\u6765\u6b64\u5bfb\u8bbf\u5c0f\u53ef\uff0c\u50cf\u662f\u6709\u4e9b\u5429\u5490\uff0c\u4e0d\u77e5\u53ef\u5426\u544a\u8bc9 \u5c0f\u53ef\uff0c\u5982\u6709\u5dee\u9063\u2026\u2026\u201d \u3000\u3000\u90a3\u4f8f\u5112\u7537\u5b50\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5a03\u5a03\uff0c\u5012\u6709\u4e9b\u50cf\u6211\u5e7c\u65f6\u7684\u6027\u683c\uff0c\u5176\u5b9e\u81ea\u5df1\u9700\u4eba\u76f8\u52a9\u4e4b \u4e8b\u6781\u591a\uff0c\u4f46\u5374\u65f6\u65f6\u523b\u523b\u60f3\u53bb\u5e2e\u52a9\u522b\u4eba\uff0c\u55e4\u2014\u2014\u201d\u4ed6\u7a81\u5730\u5fae\u53f9\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u832b\u832b\u5929\u4e0b\uff0c\u50cf\u4f60 \u6211\u4e4b\u4eba\uff0c\u82e5\u662f\u591a\u4e0a\u4e24\u4e2a\uff0c\u4e5f\u8bb8\u5929\u4e0b\u5c31\u592a\u5e73\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u767d\u886b\u5973\u5b50\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u600e\u4e48\u603b\u60f3\u6740\u4eba\uff0c\u800c\u4e0d\u60f3\u52a9\u4eba\u5462\uff1f\u201d \u3000\u3000\u90a3\u4f8f\u5112\u6c49\u5b50\u4f38\u51fa\u624b\u638c\uff0c\u5728\u7ba9\u8fb9\u91cd\u91cd\u4e00\u51fb\uff0c\u8f69\u7709\u6012\u9053\uff1a\u201c\u4e16\u4e0a\u53ef\u6740\u4e4b\u4eba\u5927\u591a\uff0c\u53ef\u52a9\u4e4b\u4eba\u5374 \u53c8\u592a\u5c11\uff0c\u6211\u9047\u7740\u53ef\u6740\u4e4b\u4eba\uff0c\u81ea\u7136\u8981\u6740\uff0c\u8fd9\u96be\u9053\u53c8\u60dc\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6b64\u523b\u5df2\u5bf9\u8fd9\u5bf9\u7537\u5973\u4e8c\u4eba\uff0c\u5927\u8d77\u597d\u611f\uff0c\u6b64\u523b\u5fcd\u4e0d\u4f4f\u63a5\u53e3\u9053\uff1a\u201c\u524d\u8f88\u9047\u7740\u53ef\u6740\u4e4b\u4eba\uff0c\u82e5 \u5730\u4e0d\u6740\uff0c\u53cd\u800c\u52a9\u4ed6\u6539\u53bb\u53ef\u6740\u4e4b\u56e0\uff0c\u90a3\u5c82\u975e\u66f4\u597d\u3002\u201d \u3000\u3000\u5374\u89c1\u8fd9\u4f8f\u5112\u7537\u5b50\u53cc\u7709\u95f4\uff0c\u5fae\u5fae\u4e00\u8f6c\uff0c\u4f3c\u4e4e\u6012\u6c14\u6e10\u4f5c\uff0c\u77aa\u4e86\u88f4\u73cf\u534a\u664c\uff0c\u7a81\u53c8\u53f9\u9053\uff1a\u201c\u4f60\u5e74 \u7eaa\u5c1a\u8f7b\uff0c\u81ea\u8fd8\u4e0d\u77e5\u4e16\u4e0a\u53ef\u6740\u4e4b\u4eba\u7684\u53ef\u6068\uff0c\u7b49\u4f60\u5e74\u7eaa\u5927\u4e9b\uff0c\u53ea\u6015\u4e5f\u4f1a\u548c\u6211\u4e00\u6837\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u6697\u4e2d\u53f9\u606f\u4e00\u58f0\uff0c\u4e0d\u518d\u8bf4\u8bdd\uff0c\u5374\u542c\u90a3\u767d\u886b\u5973\u5b50\u5a07\u7b11\u7740\u9053\uff1a\u201c\u5b7a\u5b50\u679c\u7136\u53ef\u6559\uff0c\u4e5f\u4e0d\u5728\u6211 \u592b\u5987\u4e8c\u4eba\u5343\u5c71\u4e07\u6c34\u8dd1\u6765\u770b\u4f60\uff0c\u4f60\u8981\u662f\u4e2a\u4e0d\u6210\u6750\u7684\uff0c\u53ea\u6015\u6211\u4eec\u8fd9\u4f4d\u5148\u751f\u53c8\u8981\u628a\u4f60\u4e00\u5200\u6740\u4e86\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u4eec\u8dd1\u6765\u627e\u4f60\uff0c\u662f\u4e3a\u7740\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fae\u4e00\u6447\u9996\uff0c\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u6211\u81ea\u7136\u4e0d\u77e5\u9053\uff0c\u5426\u5219\u6211\u65b9\u624d\u95ee\u4f60\u4f5c\u4ec0\uff1f\u201d\u53ea\u662f\u4ed6\u5fc3\u4e2d\u867d\u5982 \u6b64\u60f3\uff0c\u53e3\u4e2d\u5374\u672a\u8bf4\u51fa\u6765\u800c\u5df2\u3002 \u3000\u3000\u88f4\u73cf\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u53ea\u89c9\u81ea\u5df1\u8fd9\u534a\u591c\u4e4b\u4e2d\uff0c\u6240\u9047\u4e4b\u4eba\uff0c\u65e0\u4e00\u4e0d\u662f\u5927\u5927\u51fa\u4e4e\u81ea\u5df1\u610f\u6599\u4e4b \u5916\u7684\uff0c\u90a3\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u51b7\u6f20\uff0c\u56fa\u7136\u5df2\u662f\u4e16\u4e0a\u5c11\u6709\uff0c\u800c\u8fd9\u592b\u5987\u4e24\u4eba\u7684\u5f62\u6001\uff0c\u66f4\u662f\u81ea\u5df1\u8fde\u505a\u68a6 \u90fd\u6ca1\u6709\u60f3\u5230\u4f1a\u770b\u89c1\u7684\uff0c\u4ed6\u60f3\u6765\u60f3\u53bb\uff0c\u4e5f\u731c\u4e0d\u900f\u8fd9\u4e24\u4eba\u600e\u4f1a\u7ed3\u6210\u8fde\u7406\uff0c\u7136\u800c\u4ed6\u5374\u731c\u51fa\uff0c\u8fd9\u5176\u4e2d \u5fc5\u5b9a\u53c8\u5305\u6db5\u7740\u4e00\u4e2a\u6781\u5176\u52a8\u4eba\u7684\u6545\u4e8b\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u767d\u886b\u5973\u5b50\u53c8\u81ea\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u79cb\u6ce2\u6d41\u8f6c\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8bf4\u4e86\u534a\u5929\u8bdd\uff0c\u4f60\u53ef\u77e5 \u9053\u6211\u4eec\u662f\u8c01\u5417\uff1f\u6765\u627e\u4f60\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fae\u4e00\u5b9a\u795e\uff0c\u6614\u58f0\u9053\uff1a\u201c\u5c0f\u53ef\u6b63\u60f3\u8bf7\u95ee\uff0c\u552f\u6050\u4e24\u4f4d\u524d\u8f88\u89c1\u602a\uff0c\u6240\u4ee5\u8fdf\u8fdf\u672a\u6562\u95ee\u51fa\u3002\u201d \u3000\u3000\u767d\u886b\u5973\u5b50\u53c8\u81ea\u5fae\u5fae\u4e00\u7b11\uff0c\u65b9\u5f85\u8bf4\u8bdd\uff0c\u90a3\u4f8f\u5112\u7537\u5b50\u5374\u5df2\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u8fd9\u5a03\u5a03\u4ec0\u4e48\u90fd\u597d\uff0c\u5c31 \u662f\u8bf4\u8bdd\u505a\u4e8b\uff0c\u8fd8\u5acc\u4e0d\u591f\u5766\u7387\uff0c\u5176\u5b9e\u4f60\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u6211\u8001\u4eba\u5bb6\u8fd8\u6709\u770b\u4e0d\u51fa\u6765\u7684\u9053\u7406\u4e48\uff1f\u201d \u3000\u3000\u767d\u886b\u5973\u5b50\u56de\u7738\u4e00\u7b11\uff0c\u79fb\u8fc7\u624b\u53bb\uff0c\u8f7b\u8f7b\u63e1\u4f4f\u8fd9\u4f8f\u5112\u7537\u5b50\u6276\u5728\u85e4\u7ba9\u8fb9\u7684\u624b\u638c\uff0c\u8f7b\u8f7b\u7b11\u9053\uff1a \u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u7a0d\u4e3a\u6709\u70b9\u73a9\u610f\u7684\u89d2\u8272\uff0c\u8c01\u4e0d\u77e5\u9053\u4f60\u662f\u767e\u5341\u5e74\u6765\u6c5f\u6e56\u4e4b\u4e2d\u6700\u6700\u806a\u660e\u7684\u4eba\uff0c\u8fd9\u4e48\u591a \u5e74\u6765\uff0c\u53c8\u6709\u8c01\u80fd\u5728\u4f60\u9762\u524d\u73a9\u8fc7\u534a\u70b9\u82b1\u6837\u7684\uff1f\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u5145\u6ee1\u4e86\u67d4\u60c5\u871c\u610f\uff0c\u4e5f\u5145\u6ee1\u4e86\u5f97\u610f\u81ea \u50b2\uff0c\u50cf\u662f\u6df1\u6df1\u5728\u4e3a\u81ea\u5df1\u80fd\u6709\u8fd9\u6837\u4e00\u4e2a\u4e08\u592b\u4e3a\u8363\u4f3c\u7684\u3002 \u3000\u3000\u88f4\u73cf\u671b\u7740\u4ed6\u4eec\u7d27\u7d27\u4e92\u63e1\u7740\u7684\u4e00\u5927\u4e00\u5c0f\u7684\u4e24\u53ea\u624b\u638c\uff0c\u671b\u7740\u4ed6\u4eec\u4e45\u4e45\u8fd8\u672a\u5206\u5f00\u7684\u56db\u9053\u773c\u6ce2\uff0c \u5fc3\u4e2d\u53ea\u89c9\u8fd9\u7537\u5973\u4e24\u4eba\uff0c\u975e\u4f46\u6ca1\u6709\u534a\u5206\u53ef\u7b11\uff0c\u800c\u4e14\u8fd8\u6781\u4e3a\u53ef\u656c\u3001\u53ef\u7fa1\uff0c\u8fd9\u7537\u5973\u4e24\u4eba\u5f62\u6001\u867d\u7136\u6781 \u4e0d\u76f8\u79f0\uff0c\u4f46\u4ed6\u4eec\u4e4b\u95f4\u7684\u60c5\u611f\u5374\u662f\u90a3\u4e48\u771f\u631a\u7eaf\u51c0\uff0c\u800c\u8fd9\u79cd\u60c5\u611f\u4fbf\u4e5f\u662f\u88f4\u73cf\u5fc3\u4e2d\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u6df1 \u6df1\u4f01\u6c42\u7740\u7684\u3002 \u3000\u3000\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u90a3\u767d\u886b\u5973\u5b50\u65b9\u81ea\u53e3\u8fc7\u5934\u6765\uff0c\u671b\u7740\u88f4\u73cf\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u770b\u6211\u4eec\u8001\u592b\u8001\u59bb\uff0c\u8fd8\u5f53 \u7740\u4f60\u9762\u4eb2\u70ed\uff0c\u662f\u4e0d\u662f\u89c9\u5f97\u6709\u70b9\u597d\u7b11\u5440\uff1f\u201d \u3000\u3000\u88f4\u73cf\u8fde\u5fd9\u6447\u4e86\u6447\u5934\uff0c\u8fd8\u672a\u53ca\u8bf4\u51fa\u5fc3\u4e2d\u60f3\u8bf4\u7684\u8bdd\uff0c\u90a3\u4f8f\u5112\u7537\u5b50\u5c31\u5df2\u8bf4\u9053\uff1a\u201c\u4ed6\u5fc3\u91cc\u5012\u6ca1\u6709 \u597d\u7b11\u7684\u610f\u601d\uff0c\u4f46\u662f\u4ed6\u5fc3\u91cc\u5374\u4e00\u5b9a\u5728\u5947\u602a\uff0c\u6211\u4eec\u4e24\u4eba\u600e\u4f1a\u7ed3\u6210\u592b\u5987\u7684\u3002\u201d\u4ed6\u653e\u58f0\u4e00\u7b11\uff0c\u88f4\u73cf\u5374 \u4e0d\u7981\u6697\u5403\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u679c\u7136\u806a\u660e\u7edd\u9876\uff0c\u6211\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff1f\u4ed6\u7adf\u7136\u4e86\u5982\u6307\u638c\uff0c\u6211\u5148\u524d\u53ea \u9053\u90a3\u9e23\u4e16\u5144\u5df2\u662f\u6700\u806a\u660e\u7684\u4eba\uff0c\u54ea\u77e5\u4e16\u4e0a\u7adf\u8fd8\u6709\u4eba\u6bd4\u4ed6\u66f4\u806a\u660e\u5341\u500d\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u65b9\u81ea\u6697\u6697\u60ca\u53f9\uff0c\u5374\u542c\u90a3\u767d\u886b\u5973\u5b50\u5df2\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5728\u6c5f\u6e56\u4e2d\u8fd8\u6ca1\u6709\u95ef\u8361\u591a \u4e45\uff0c\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\u4f60\u548c\u4ed6\u7684\u6545\u4e8b\uff0c\u4f46\u662f\uff0c\u7b49\u4f60\u5e74\u7eaa\u5927\u4e9b\uff0c\u4f60\u5c31\u81ea\u7136\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u76ee\u5149\u53c8\u81ea\u51dd\u6ce8\u88f4\u73cf\u534a\u664c\uff0c\u50cf\u662f\u8981\u5bf9\u88f4\u73cf\u7684\u751f\u6027\u4e3a\u4eba\u770b\u5f97\u66f4\u900f\u5f7b\u4e9b\uff0c\u4e00 \u65f6\u4e4b\u95f4\uff0c\u88f4\u73cf\u7ade\u88ab\u8fd9\u7537\u5973\u4e24\u4eba\u7684\u56db\u9053\u76ee\u5149\u770b\u5f97\u5782\u4e0b\u5934\u53bb\uff0c\u53ea\u89c9\u8fd9\u56db\u9053\u76ee\u5149\u4e4b\u4e2d\uff0c\u4eff\u4f5b\u542b\u8574\u7740 \u4e00\u79cd\u60ca\u4eba\u7684\u5149\u91c7\uff0c\u53ef\u4ee5\u6d1e\u6089\u4e16\u4e0a\u4efb\u4f55\u4eba\u7684\u4e00\u5207\u5fc3\u4e8b\u3002 \u3000\u3000\u201c\u4f46\u662f\u8fd9\u4e24\u4eba\u7a76\u7adf\u662f\u4e3a\u7740\u4ec0\u4e48\u6765\u5bfb\u627e\u4e8e\u6211\uff0c\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\u5982\u6b64\u770b\u6211\u5462\uff1f\u201d\u4ed6\u60f3\u4e86\u8bb8\u4e45\uff0c \u8fd8\u662f\u4e0d\u80fd\u731c\u6d4b\uff0c\u5374\u542c\u90a3\u767d\u886b\u5973\u5b50\u5df2\u81ea\u7b11\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u6211\u4eec\u662f\u4e3a\u7740\u4ec0\u4e48\u6765\u627e\u4f60 \u7684\u4e86\u3002\u201d\u88f4\u73cf\u5fc3\u4e2d\u5927\u559c\uff0c\u8fde\u5fd9\u7559\u610f\u503e\u542c\uff0c\u54ea\u77e5\u8fd9\u767d\u886b\u5973\u5b50\u795e\u8272\u7a81\u5730\u4e00\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6709\u4eba\u6765 \u4e86\u3002\u201d\u4f38\u624b\u4eba\u6000\uff0c\u50cf\u662f\u60f3\u638f\u51fa\u4ec0\u4e48\u4e1c\u897f\u6765\uff0c\u7a81\u53c8\u6b62\u4f4f\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u660e\u5929\u4e09\u66f4\uff0c\u4f60\u8fd8\u662f\u4ece\u90a3\u540e \u95e8\u91cc\u51fa\u6765\uff0c\u6211\u518d\u544a\u8bc9\u4f60\u3002\u201d\u90a3\u4f8f\u5112\u7537\u5b50\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u5bb6\u4f19\u504f\u504f\u5728\u6b64\u523b\u8dd1\u6765\u3002\u201d\u767d \u886b\u5973\u5b50\u56de\u7738\u7b11\u9053\uff1a\u201c\u4f60\u770b\u4f60\uff0c\u813e\u6c14\u53c8\u53d1\u8d77\u6765\u4e86\u3002\u201d\u8eab\u5f62\u5fae\u5fae\u4e00\u65cb\uff0c\u88f4\u73cf\u53ea\u89c1\u4e00\u6761\u6de1\u6de1\u7684\u767d \u5f71\uff0c\u50cf\u662f\u4e00\u9053\u8f7b\u70df\u4f3c\u5730\u500f\u7136\u63a0\u53bb\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u4fbf\u5df2\u968f\u98ce\u800c\u901d\u3002\u4ed6\u4e0d\u7981\u53c8\u81ea\u6697\u4e2d\u60ca\u53f9\u4e00\u58f0\uff0c\u8fd9 \u767d\u886b\u5973\u5b50\u8eab\u8eaf\u5982\u6b64\u7c97\u9880\uff0c\u4f46\u8f7b\u529f\u5374\u53c8\u5982\u6b64\u9ad8\u5999\uff0c\u82e5\u975e\u81ea\u5df1\u773c\u770b\uff0c\u771f\u662f\u4ee4\u4eba\u96be\u4ee5\u7f6e\u4fe1\uff0c\u56de\u9996\u671b \u5904\uff0c\u591c\u8272\u6df1\u6df1\uff0c\u54ea\u6709\u534a\u6761\u4eba\u5f71\uff0c\u4ed6\u5fc3\u4e2d\u53c8\u4e0d\u7981\u7591\u60d1\uff0c\u201c\u96be\u9053\u5979\u770b\u9519\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u8fdf\u7591\u5730\u56de\u8f6c\u8eab\uff0c\u8d70\u4e86\u4e24\u6b65\uff0c\u4e00\u9635\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\uff0c\u679c\u81ea\u5939\u6742\u7740\u6f7a\u6f7a\u6d41\u6c34\u58f0\u968f\u98ce\u4f20\u6765\uff0c\u63a5 \u7740\uff0c\u524d\u9762\u7684\u591c\u8272\u4e4b\u4e2d\uff0c\u4fbf\u73b0\u51fa\u4e94\u6761\u4eba\u5f71\uff0c\u6697\u4e2d\u5bf9\u90a3\u767d\u886b\u5973\u5b50\u7684\u8033\u76ee\u4e4b\u529b\uff0c\u53c8\u4e0d\u7981\u5927\u8d77\u656c\u670d\u4e4b \u5fc3\u3002 \u3000\u3000\u5374\u89c1\u524d\u9762\u7684\u4eba\u5f71\u8d8a\u884c\u8d8a\u8fd1\uff0c\u7adf\u7a81\u8d77\u8f7b\u5524\u4e00\u58f0\uff1a\u201c\u524d\u9762\u7684\u53ef\u662f\u88f4\u5144\uff1f\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u3001\u88f4\u73cf\u4e4b\u8033\uff0c\u4ed6\u6bcb\u5eb8\u518d\u770b\u5f85\u6b64\u4eba\u7684\u8eab\u5f62\uff0c\u4fbf\u77e5\u9053\u662f\u5434\u9e23\u4e16\u6765\u4e86\uff0c\u4e8e\u662f\u4ed6\u7acb\u523b\u5e94 \u9053\uff1a\u201c\u662f\u6211\uff01\u201d\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5434\u9e23\u4e16\u811a\u5c16\u8f7b\u70b9\uff0c\u500f\u7136\u4e00\u4e2a\u8d77\u843d\uff0c\u63a0\u5230\u88f4\u73cf\u8eab\u524d\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u88f4\u5144\uff0c\u8fd9\u4e48\u665a\u4e86\uff0c\u4f60\u600e \u7684\u8fd8\u803d\u5728\u8fd9\u91cc\uff0c\u5012\u6559\u5c0f\u5f1f\u62c5\u5fc3\u3002\u201d\u8bed\u58f0\u4e4b\u4e2d\uff0c\u5fae\u5e26\u57cb\u6028\uff0c\u4f46\u57cb\u6028\u4e4b\u4e2d\uff0c\u5374\u53c8\u5145\u6ee1\u5173\u5207\u4e4b\u60c5\u3002 \u3000\u3000\u88f4\u73cf\u6b49\u7136\u4e00\u7b11\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u4f46\u89c9\u53cb\u60c5\u4e4b\u6e29\u6696\u53ef\u8d35\uff0c\u5434\u9e23\u4e16\u4e00\u628a\u6293\u7740\u4ed6 \u7684\u81c2\u8180\uff0c\u4ed4\u7ec6\u5728\u4ed6\u9762\u4e0a\u7aef\u8be6\u534a\u664c\uff0c\u53ea\u89c1\u4ed6\u867d\u7136\u75b2\u5026\uff0c\u5374\u4ecd\u63a9\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u6fc0\u52a8\u4e4b\u610f\uff0c\u751f\u50cf\u662f\u5df2 \u7ecf\u8fc7\u4e00\u4e9b\u6781\u4e3a\u5174\u594b\u7684\u4e8b\u4f3c\u7684\uff0c\u6c89\u541f\u534a\u664c\uff0c\u4fbf\u53c8\u8bf4\u9053\uff01 \u3000\u3000\u201c\u4f60\u6df1\u591c\u7559\u5728\u8fd9\u91cc\uff0c\u96be\u9053\u662f\u9047\u7740\u4e86\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d\u4ed6\u867d\u662f\u5341\u5206\u7cbe\u7075\u8131\u8df3\u4e4b\u4eba\uff0c\u4f46\u5bf9\u88f4\u73cf\uff0c \u5374\u662f\u4e8b\u4e8b\u4ee5\u8bda\u5f85\u4e4b\uff0c\u662f\u4ee5\u4ed6\u6b64\u523b\u4e5f\u5e76\u6ca1\u6709\u7528\u4efb\u4f55\u6280\u5de7\u6765\u5957\u88f4\u73cf\u7684\u8bdd\uff0c\u53ea\u662f\u5c06\u5fc3\u4e2d\u6240\u7591\uff0c\u5766\u7387 \u5730\u95ee\u51fa\u6765\u3002 \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u4e00\u6014\uff0c\u7adf\u53c8\u534a\u664c\u6ca1\u6709\u8bf4\u51fa\u8bdd\u6765\uff0c\u5434\u9e23\u4e16\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u6df1\u591c\u8f6c\u4fa7\uff0c\u96be\u4ee5\u6210 \u7720\uff0c\u60f3\u518d\u627e\u4f60\u8c08\u8c08\uff0c\u54ea\u77e5\u8dd1\u5230\u4f60\u623f\u95f4\u4e00\u770b\uff0c\u4f60\u5df2\u4e0d\u5728\uff0c\u800c\u9662\u5b50\u91cc\u7adf\u53c8\u5012\u6bd9\u4e86\u4e24\u5177\u5c38\u8eab\uff0c\u88f4 \u5144\uff0c\u4f60\u6211\u6b64\u523b\u7684\u5904\u5883\uff0c\u90fd\u5728\u4eba\u5bb6\u7684\u638c\u63e1\u4e4b\u4e2d\uff0c\u4eca\u591c\u4e4b\u4e8b\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u5b9a\u4e0d\u5bfb\u5e38\uff0c\u4f60\u5982\u4ee5\u6211\u4e3a \u77e5\u5df2\uff0c\u5c31\u5f53\u5c06\u5b83\u8bf4\u51fa\u6765\uff0c\u4f60\u6211\u4e00\u8d77\u5546\u91cf\u4e2a\u5e94\u5bf9\u4e4b\u7b56\uff0c\u5426\u5219\u90a3\u201d\u795e\u624b\u201c\u6218\u98de\u600e\u4f1a\u4efb\u5f97\u81ea\u5df1\u7684\u624b \u4e0b\u6b7b\u5728\u81ea\u5df1\u7684\u9662\u5b50\u91cc\uff0c\u4f55\u51b5\u90a3\u4e24\u4e2a\u4eba\u672c\u662f\u4ed6\u7528\u6765\u6697\u4e2d\u76d1\u89c6\u4f60\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4f4e\u6c89\uff0c\u5b57\u5b57\u53e5\u53e5\uff0c\u90fd\u6781\u4e3a\u8bda\u6073\uff0c\u4e0e\u4ed6\u5e73\u65e5\u5bf9\u522b\u4eba\u8bf4\u8bdd\u7684\u6001\u5ea6\u622a\u7136\u4e0d\u540c\uff0c\u88f4\u73cf\u5fc3\u91cc \u53c8\u662f\u6fc0\u52a8\uff0c\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u4e0d\u7981\u5bf9\u81ea\u5df1\u65b9\u624d\u541e\u5410\u4e4b\u6001\u5927\u8d77\u60ed\u6127\u4e4b\u610f\uff0c\u89c9\u5f97\u4eba\u5bb6\u4ee5\u8bda\u5f85\u5df1\uff0c\u81ea\u5df1 \u7adf\u4e0d\u80fd\u4ee5\u8bda\u5f85\u4eba\u3002 \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u4e0d\u7981\u4ea6\u81ea\u957f\u53f9\u4e00\u58f0\uff0c\u5c06\u81ea\u5df1\u8fd9\u534a\u591c\u4e4b\u95f4\u6240\u9047\u4e4b\u4e8b\uff0c\u8be6\u8be6\u7ec6\u7ec6\u4ed6\u8bf4\u51fa\u6765\uff0c\u8bf4 \u5230\u90a3\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e4b\u65f6\uff0c\u5434\u9e23\u4e16\u795e\u8272\u5df2\u81ea\u4e00\u53d8\uff0c\u60ca\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u600e\u5730\u4e5f\u8dd1\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u8bf4\u5230 \u4ed6\u81ea\u5df1\u9047\u7740\u6a80\u6587\u742a\u7684\u65f6\u5019\uff0c\u5434\u9e23\u4e16\u53c8\u4e0d\u7981\u4e3a\u4e4b\u6b23\u559c\uff0c\u8bf4\u5230\u6a80\u6587\u742a\u7684\u8d70\uff0c\u5434\u545c\u4e16\u4fbf\u6447\u5934\u7b11\u9053\uff1a \u201c\u770b\u6765\u8fd9\u4f4d\u59d1\u5a18\uff0c\u4e5f\u662f\u4e2a\u5a07\u7eb5\u6210\u6027\u7684\u89d2\u8272\uff0c\u4e0d\u8fc7\u90a3\u53ea\u7ba1\u653e\u5fc3\u597d\u4e86\uff0c\u4e0d\u51fa\u4e09\u5929\uff0c\u5979\u53c8\u4f1a\u5343\u65b9\u767e \u8ba1\u5730\u6765\u627e\u4f60\u7684\u3002\u201d\u968f\u53c8\u76b1\u7709\u9053\uff1a\u201c\u90a3\u2018\u795e\u624b\u2019\u6218\u98de\u82e5\u77e5\u9053\u4e86\u4f60\u4e0e\u2018\u9f99\u5f62\u516b\u638c\uff0c\u5bb6\u65cf\u4e4b\u95f4\u7684\u5173 \u7cfb\uff0c\u53ea\u6015\u53c8\u8981\u751f\u51fa\u4e9b\u9ebb\u70e6\u4e86\u3002\u201d\u53c8\u5947\u9053\uff1a\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e00\u5411\u51b7\u505a\u5b64\u50fb\uff0c\u72ec\u6765\u72ec\u5f80\uff0c\u6b64\u523b\u7adf\u4f1a \u5bf9\u4e00\u4e2a\u5973\u5b69\u5b50\u5982\u6b64\u5173\u6ce8\uff0c\u5012\u4e5f\u786e\u662f\u5f02\u6570\u3002\u201c\u7b49\u5230\u88f4\u73cf\u5c06\u90a3\u53cc\u5947\u5f02\u7684\u592b\u5987\u8bf4\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5434\u9e23 \u4e16\u7adf\u81ea\u8131\u53e3\u60ca\u547c\u9053\uff1a\u201c\u91d1\u7ae5\u738b\u5973\uff01\u201d \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f60\u8ba4\u5f97\u4ed6\u4eec\uff1f\u201d\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u90a3\u592b\u5987\u4e8c\u4eba\u7684\u540d\u5b57\uff0c\u7adf\u662f\u201c\u91d1 \u7ae5\u7389\u5973\u201d\uff0c\u5374\u89c1\u5434\u9e23\u4e16\u5fae\u5fae\u6447\u5934\u9053\uff1a\u201c\u6211\u54ea\u91cc\u4f1a\u8ba4\u5f97\u4ed6\u4eec\uff0c\u53ea\u4e0d\u8fc7\u6211\u4ece\u4f60\u53e3\u4e2d\u7684\u63cf\u8ff0\uff0c\u4fbf\u77e5 \u9053\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u2018\u91d1\u7ae5\u7389\u5973\u2019\u4e4b\u5916\uff0c\u518d\u65e0\u4e00\u4eba\u6709\u6b64\u4f53\u5f62\uff0c\u6709\u6b64\u6b66\u529f\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u5782\u4e0b\u5934\u53bb\uff0c\u6c89\u601d\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u2018\u91d1\u7ae5\u7389\u5973\uff0c\u9690\u8ff9\u6c5f\u6e56\uff0c\u5df2\u6709\u8bb8\u591a\u5e74\uff0c\u4f60\u4eca\u5929 \u665a\u4e0a\u7adf\u4f1a\u9047\u7740\u4ed6\u4eec\uff0c\u90a3\u771f\u6bd4\u9047\u7740\u2019\u51b7\u8c37\u53cc\u6728\u2018\u8fd8\u8981\u5947\u602a\u5341\u500d\u3002\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6570\u5341\u5e74\u6765\uff0c\u6b66\u6797 \u4e4b\u4e2d\uff0c\u867d\u7136\u80fd\u4eba\u8f88\u51fa\uff0c\u5374\u4ece\u672a\u6709\u4e00\u4eba\u7684\u58f0\u540d\u80fd\u591f\u6bd4\u5f97\u4e0a\u90a3\u6b66\u6797\u4e2d\u4e09\u5bf9\u795e\u4ed9\u7737\u5c5e\u7684\u3002\u201d\u4ed6\u8bed\u58f0 \u4e00\u987f\uff0c\u4f38\u51fa\u4e09\u6839\u624b\u6307\uff0c\u53c8\u9053\uff1a\u201c\u5176\u4e2d\u4e00\u5bf9\uff0c\u6c5f\u6e56\u4eba\u79f0\u2018\u5987\u5531\u592b\u968f\u2019\uff0c\u4fbf\u662f\u8fd9\u2018\u91d1\u7ae5\u7389\u5973\u2019\u592b \u5987\u4e24\u4eba\u4e86\u3002\u201d\u88f4\u73cf\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u5bf9\u5462\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u5c48\u4e0b\u4e00\u6839\u624b\u6307\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u5bf9\u2018\u592b\u5531\u5987\u968f\u2019\uff0c\u8fd9\u4e24\u4eba\u4fbf\u662f\u2018\u5343\u624b\u4e66\u751f\u2019\u4e0e\u2018\u51b7 \u6708\u4ed9\u5b50\u2019\uff0c\u53e6\u4e00\u5bf9\u2018\u592b\u65e2\u4e0d\u5531\uff0c\u5987\u4e5f\u4e0d\u5531\u2019\u7684\u592b\u5987\u4fa0\u4fa3\u2014\u2014\u201d\u4ed6\u8bed\u6765\u8bf4\u5b8c\uff0c\u88f4\u73cf\u6b63\u81ea\u60ca\u53f9\u4e00 \u58f0\uff0c\u53f9\u9053\uff1a\u201c\u5434\u5144\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u2018\u592b\u5531\u5987\u968f\u2019\u7684\u4e00\u5bf9\u795e\u4ed9\u7737\u5c5e\uff0c\u6b64\u523b\u5374\u5df2\u52b3\u71d5\u5206\u98de\u4e86\u5462\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u5fae\u4e00\u6014\uff0c\u968f\u5373\u604d\u7136\u9053\uff1a\u201c\u96be\u602a\u90a3\u5929\u2018\u51b7\u6708\u4ed9\u5b50\u2019\u89c1\u5230\u4f60\u65f6\uff0c\u4f1a\u6709\u90a3\u79cd\u8868\u60c5\uff0c\u539f \u6765\u4f60\u662f\u8ba4\u5f97\u4ed6\u4eec\u7684\u3002\u201d\u5374\u89c1\u88f4\u73cf\u5782\u7740\u5934\uff0c\u6b63\u5728\u6c89\u601d\u4e4b\u4e2d\uff0c\u751f\u50cf\u662f\u6ca1\u6709\u542c\u5230\u81ea\u5df1\u7684\u8bdd\u4f3c\u7684\u3002 \u3000\u3000\u88f4\u73cf\u4fef\u9996\u9ed8\u7136\u826f\u4e45\uff0c\u7a81\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u2018\u91d1\u7ae5\u7389\u5973\u2019\u4e24\u4eba\uff0c\u5f62\u6001\u5982\u6b64\u4e0d\u79f0\uff0c\u5374\u600e \u4f1a\u7ed3\u4e3a\u592b\u5987\u7684\u5417\uff1f\u201d\u4ed6\u5fc3\u4e2d\u867d\u7136\u662f\u611f\u6168\u6781\u591a\uff0c\u4f46\u4ecd\u4e0d\u80fd\u904f\u6b62\u5bf9\u6b64\u4e8b\u7684\u597d\u5947\u4e4b\u5fc3\uff0c\u662f\u4ee5\u7ec8\u4e8e\u8fd8 \u662f\u5c06\u8fd9\u53e5\u8bdd\u95ee\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6708\u5df2\u897f\u6c89\u3001\u591c\u8272\u867d\u66f4\u8fdc\uff0c\u4f46\u8ddd\u79bb\u9ece\u660e\uff0c\u5374\u5df2\u5f88\u8fd1\u4e86\uff0c\u5434\u9e23\u4e16\u62ac\u5934\u671b\u4e86\u671b\u6ee1\u7f00\u7a79\u82cd\u7684\u661f \u7fa4\uff0c\u6c89\u58f0\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u6c5f\u6e56\u4e2d\u9887\u6709\u8c23\u4f20\uff0c\u4f46\u771f\u5b9e\u60c5\u5f62\uff0c\u5374\u662f\u4e00\u6bb5\u6781\u4e3a\u52a8\u4eba\u7684 \u6545\u4e8b\u3002\u201d\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u6697\u4e2d\u5fd6\u9053\uff1a\u201c\u6211\u679c\u7136\u6ca1\u6709\u731c\u9519\u3002\u201d\u5374\u542c\u5434\u9e23\u4e16\u63a5\u9053\u3002 \u3000\u3000\u201c\u6b64\u523b\u66d9\u8272\u5c06\u4e34\uff0c\u4f60\u6211\u7ad9\u5728\u8fd9\u91cc\uff0c\u82e5\u88ab\u6218\u98de\u89c1\u4e86\uff0c\u603b\u662f\u4e0d\u59a5\u3002\u201d\u4f38\u624b\u4e00\u62c9\u88f4\u73cf\uff0c\u5411\u5c71\u5e84 \u8d70\u53bb\uff0c\u4e00\u9762\u63a5\u9053\uff1a\u201c\u4f60\u6211\u8fb9\u8d70\u8fb9\u8c08\uff0c\u8d70\u5230\u623f\u95f4\u7684\u65f6\u5019\uff0c\u8fd9\u6bb5\u6545\u4e8b\u4e5f\u8be5\u8bf4\u5b8c\u4e86\u3002\u201d\u4ed6\u5fc3\u91cc\u614e \u601d\uff0c\u5904\u5904\u614e\u91cd\uff0c\u4e3a\u53cb\u70ed\u80a0\uff0c\u53ea\u671b\u88f4\u73cf\u80fd\u591f\u987a\u5229\u5730\u767b\u4e0a\u6c5f\u5357\u7eff\u6797\u603b\u74e2\u628a\u5b50\u7684\u738b\u5ea7\uff0c\u4e5f\u597d\u626c\u7709\u5410 \u6c14\u4e00\u756a\uff0c\u800c\u88f4\u73cf\u6ee1\u5fc3\u597d\u5947\uff0c\u5374\u53ea\u5e0c\u671b\u4ed6\u5feb\u4e9b\u5c06\u8fd9\u6bb5\u6545\u4e8b\u8bf4\u51fa\u6765\uff0c\u81f3\u4e8e\u522b\u7684\u4e8b\uff0c\u5374\u6839\u672c\u6ca1\u6709\u653e \u5728\u4ed6\u7684\u5fc3\u4e0a\u3002 \u3000\u3000\u5434\u9e23\u4e16\u5e72\u54b3\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u91d1\u7ae5\u7389\u5973\u8fd9\u4e00\u5bf9\u6b66\u6797\u5947\u4eba\uff0c\u672c\u662f\u4e2d\u8868\u5144\u59b9\uff0c\u751f\u957f\u5728\u6c5f\u5357 \u7684\u4e00\u4e2a\u6b66\u6797\u4e16\u5bb6\u91cc\uff0c\u90a3\u65f6\u6b66\u6797\u4e4b\u4e2d\u867d\u672c\u6781\u591a\u4e8b\uff0c\u4f46\u8fd9\u4e2a\u6b66\u6797\u4e16\u5bb6\u5374\u65e2\u4e0d\u4fdd\u9556\uff0c\u4ea6\u4e0d\u5165\u516d\u6247 \u95e8\uff0c\u5374\u4e5f\u4e0d\u843d\u9ed1\u9053\uff0c\u6c5f\u6e56\u4e4b\u4e2d\u7684\u6069\u6028\u4ec7\u6740\uff0c\u4ed6\u4eec\u66f4\u4e0d\u8fc7\u95ee\uff0c\u53ea\u662f\u5728\u5f53\u5730\u8bbe\u573a\u6388\u5f92\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u5fae\u987f\uff0c\u4fbf\u53c8\u63a5\u9053\uff1a\u201c\u8fd9\u6b66\u6797\u4e16\u5bb6\u7684\u5bb6\u4e3b\uff0c\u4e5f\u5c31\u662f\u90a3\u2018\u91d1\u7ae5\u2019\u7684\u7956\u7236\uff0c\u58ee\u5c81\u4e5f\u66fe\u95ef \u8fc7\u6c5f\u6e56\uff0c\u4ee5\u638c\u4e2d\u4e00\u53e3\u7d2b\u91d1\u5200\uff0c\u4ee5\u53ca\u5bb6\u4f20\u7684\u5200\u6cd5\uff0c\u5728\u6c5f\u6e56\u4e2d\u535a\u4e0b\u4e00\u4e2a\u4e0d\u5c0f\u7684\u540d\u5934\u4e4b\u540e\uff0c\u4fbf\u606f\u5f71 \u5bb6\u56ed\uff0c\u4ece\u6b64\u4e0d\u95ee\u6b66\u6797\u4e2d\u4e8b\u3002\u8fd9\u2018\u91d1\u7ae5\u2019\u81ea\u5e7c\u4fbf\u662f\u7edd\u9876\u7684\u806a\u660e\uff0c\u53c8\u662f\u8001\u4eba\u7684\u6700\u5e7c\u5b59\u513f\uff0c\u81ea\u7136\u4fbf \u6781\u5f97\u8001\u4eba\u7684\u5ba0\u7231\u3002\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u9053\u6765\uff0c\u5374\u5c3d\u662f\u4e00\u4e9b\u5bb6\u5e38\u4e00\u4e8b\uff0c\u88f4\u73cf\u5fc3\u4e2d\u5927\u611f\u4e0d\u8010\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u8bf4\u7b80\u5355\u4e9b\u7684 \u597d\uff01\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u5fae\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u53ea\u5f53\u4ed6\u662f\u4e2a\u6e29\u541e\u6c34\u7684\u813e\u6c14\uff0c\u54ea\u77e5\u4ed6\u4e5f\u6027\u6025\u5f97\u5f88\u3002\u201d\u53e3\u4e2d\u4fbf \u63a5\u9053\uff1a\u201c\u8fd9\u2018\u91d1\u7ae5\u2019\u81ea\u5e7c\u5a07\u7eb5\uff0c\u4e0e\u4ed6\u5e74\u5e7c\u4eff\u4f5b\u7684\u7ae5\u5b50\uff0c\u4ed6\u90fd\u4e0d\u770b\u5728\u773c\u91cc\uff0c\u53ea\u6709\u5bc4\u5c45\u5728\u4ed6\u5bb6\u4e2d \u7684\u4e00\u4e2a\u8fdc\u623f\u8868\u4eb2\u7684\u5e7c\u5973\uff0c\u6700\u5408\u4ed6\u7684\u813e\u80c3\uff0c\u4e24\u4eba\u53ea\u8981\u4e00\u5929\u4e0d\u89c1\uff0c\u4ed6\u4fbf\u50cf\u662f\u5931\u843d\u4e86\u4ec0\u4e48\u4f3c\u7684\uff0c\u518d \u4e5f\u9732\u4e0d\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u8fd9\u8001\u4eba\u770b\u5728\u773c\u91cc\uff0c\u5fc3\u75bc\u5e7c\u5b59\uff0c\u53c8\u89c1\u8fd9\u5973\u5b69\u5b50\u5e74\u7eaa\u867d\u5c0f\uff0c\u5374\u6781\u6e29\u67d4\u61c2\u4e8b\uff0c \u4fbf\u66ff\u4ed6\u4eec\u4e24\u4eba\u8ba2\u4e0b\u4eb2\u4e8b\u3002\u201d \u3000\u3000\u88f4\u73cf\u6697\u4e2d\u53f9\u606f\u4e00\u58f0\uff0c\u60f3\u5230\u81ea\u5df1\u548c\u6a80\u6587\u742a\uff0c\u82e5\u662f\u81ea\u5df1\u4e5f\u6709\u4e2a\u8fd9\u6837\u7684\u7956\u7236\uff0c\u90a3\u8be5\u591a\u597d\uff0c\u4f46\u81ea \u5df1\u7236\u6bcd\u53cc\u4ea1\uff0c\u5bc4\u4eba\u7bf1\u4e0b\uff0c\u53c8\u662f\u90a3\u4e48\u611a\u8822\uff0c\u8fde\u6700\u666e\u901a\u7684\u529f\u592b\u90fd\u5b66\u4e0d\u597d\uff0c\u53c8\u600e\u80fd\u914d\u5f97\u4e0a\u5bb6\u4e16\u663e\u8d6b \u7684\u6587\u742a\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u53ea\u89c9\u9178\u751c\u6614\u8fa3\uff0c\u4ea4\u76f8\u7eb7\u6c93\u800c\u6765\uff0c\u4e0d\u89c9\u53c8\u60f3\u5f97\u75f4\u4e86\uff0c\u8fde\u5730\u4e0a\u7684\u4e00\u5757\u77f3\u5b50 \u90fd\u672a\u770b\u5230\uff0c\u4e00\u811a\u8e22\u5728\u4e0a\u9762\uff0c\u51e0\u4e4e\u8dcc\u5012\uff0c\u5434\u9e23\u4e16\u659c\u659c\u779f\u4e86\u4ed6\u4e00\u773c\uff0c\u4f38\u624b\u4e00\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u65b9\u81ea\u63a5 \u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u867d\u7136\u4ff1\u5728\u9aeb\u9f84\uff0c\u8fd8\u4e0d\u61c2\u5f97\u7537\u5973\u95f4\u4e8b\uff0c\u4f46\u542c\u5230\u5bb6\u4eba\u8bf4\u7684\u8bdd\uff0c\u77e5\u9053\u81ea\u6b64\u4e24\u4eba\u53ef\u4ee5\u7ec8 \u751f\u53ae\u5b88\u5728\u4e00\u8d77\uff0c\u5fc3\u91cc\u81ea\u662f\u9ad8\u5174\uff0c\u4e24\u4eba\u8d8a\u53d1\u5f97\u4eb2\u7231\uff0c\u8d8a\u53d1\u5730\u5206\u4e0d\u5f00\u6765\uff0c\u53ea\u5e0c\u671b\u81ea\u5df1\u5feb\u4e9b\u957f\u5927\uff0c \u5feb\u4e9b\u7ed3\u4e3a\u592b\u5987\uff0c\u522b\u4eba\u6709\u65f6\u53d6\u7b11\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u88f4\u73cf\u201c\u5657\u55e4\u201d\u5931\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u542c\u4f60\u8bf4\u6765\uff0c\u5c31\u50cf\u4f60\u5f53\u65f6\u4e5f\u5728\u90a3\u91cc\u4f3c\u7684\uff0c\u7adf\u8fde\u4ed6\u4eec\u5fc3\u91cc\u5728 \u60f3\u4ec0\u4e48\uff0c\u4f60\u90fd\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u4e0d\u89c9\u4ea6\u5fae\u7b11\u4e00\u4e0b\uff0c\u4f46\u7b11\u5bb9\u672a\u655b\uff0c\u5374\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u54ea\u77e5\u2014\u2014\u5509\uff01\u4eba\u95f4\u7978\u798f \u65e0\u5e38\uff0c\u8fd9\u5b89\u9002\u5bcc\u8db3\u7684\u4e00\u5bb6\u4eba\uff0c\u6b63\u5728\u4e3a\u81ea\u5df1\u7684\u5feb\u4e50\u800c\u5f97\u610f\u7684\u65f6\u5019\uff0c\u5374\u4e0d\u77e5\u6709\u4e00\u4ef6\u5927\u7978\u5df2\u5c06\u964d\u4e34 \u5230\u4ed6\u4eec\u8eab\u4e0a\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u51db\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u7684\uff1f\u201d\u4ed6\u751f\u5177\u81f3\u6027\uff0c\u53ea\u613f\u666e\u5929\u4e4b\u4e0b\uff0c\u4eba\u4eba\u90fd\u5feb\u4e50\u65e0\u6bd4\uff0c \u53ea\u8981\u542c\u5230\u4eba\u95f4\u7684\u4efb\u4f55\u4e00\u4ef6\u60b2\u60e8\u4e4b\u4e8b\uff0c\u4ed6\u5fc3\u4e2d\u4fbf\u89c9\u4e0d\u5fcd\uff0c\u81f3\u4e8e\u4ed6\u81ea\u5df1\u7684\u60b2\u60e8\u8eab\u4e16\uff0c\u4ed6\u5374\u5f88\u5c11\u4f1a \u53bb\u81ea\u6028\u81ea\u827e\uff0c\u81ea\u60b2\u81ea\u53f9\u4e00\u4e0b\u3002 \u3000\u3000\u5434\u9e23\u4e16\u53f9\u606f\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u6b63\u662f\u6625\u5929\uff0c\u8fd9\u4e00\u53cc\u7537\u5973\u5f53\u65f6\u53ea\u6709\u4e5d\u5c81\uff0c\u4e24\u4eba\u5728\u540e\u56ed\u4e2d\u6355\u6349\u4e00\u53cc \u8774\u8776\uff0c\u773c\u770b\u51e0\u4e4e\u5df2\u5c06\u6349\u5230\uff0c\u54ea\u77e5\u5728\u5feb\u8981\u5230\u624b\u7684\u65f6\u5019\uff0c\u5374\u53c8\u98de\u6389\uff0c\u8fd9\u2018\u91d1\u7ae5\u2019\u81ea\u5e7c\u5014\u5f3a\uff0c\u53d1\u8a93 \u975e\u5c06\u8fd9\u53cc\u8774\u8776\u6349\u5230\u4e0d\u53ef\uff0c\u773c\u770b\u5b83\u4eec\u98de\u51fa\u5899\u5916\uff0c\u4fbf\u4e5f\u5f00\u4e86\u9662\u4e2d\u7684\u89d2\u95e8\uff0c\u8ffd\u4e86\u51fa\u53bb\uff0c\u90a3\u5973\u5b69\u5b50\u867d \u7136\u80c6\u5b50\u6bd4\u8f83\u5c0f\u4e9b\uff0c\u4f46\u89c1\u4ed6\u5982\u6b64\uff0c\u81ea\u5df1\u4e5f\u5c31\u8ddf\u4e86\u51fa\u53bb\uff0c\u8774\u8776\u8d8a\u98de\u8d8a\u8fdc\uff0c\u4ed6\u4eec\u4e5f\u5c31\u8d8a\u8ffd\u8d8a\u8fdc\uff0c \u2018\u7389\u5973\u2019\u51e0\u6b21\u4e09\u756a\u5730\u529d\u2018\u91d1\u7ae5\u2019\u56de\u53bb\uff0c\u4f46\u90a3\u53cc\u8774\u8776\u7adf\u751f\u50cf\u6545\u610f\u5f15\u9017\u4ed6\u4eec\u4f3c\u7684\uff0c\u53c8\u504f\u504f\u5728\u524d\u9762 \u51fa\u73b0\uff0c\u2014\u2014\u201d\u88f4\u73cf\u8d8a\u542c\u8d8a\u5947\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u8fd9\u4e00\u53cc\u6b66\u6797\u524d\u8f88\u4e4b\u4e8b\uff0c\u4f60\u600e\u5730\u77e5\u9053\u5f97\u8fd9 \u4e48\u8be6\u7ec6\uff0c\u96be\u9053\u4e00\u201d\u5434\u9e23\u4e16\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4ed6\u4eec\u4e8b\u540e\u66fe\u5c06\u6b64\u4e8b\u8bf4\u7ed9\u5bb6\u7956\u7236\u77e5\u9053\uff0c\u5bb6\u7956\u7236 \u53c8\u5c06\u6b64\u4e8b\u544a\u8bc9\u4e86\u6211\uff0c\u56e0\u4e4b\u6211\u4e5f\u5c31\u77e5\u9053\u5f97\u6bd4\u522b\u4eba\u6e05\u695a\u4e9b\u3002\u201d \u3000\u3000\u88f4\u73cf\u604d\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u52a8\uff0c\u6697\u4e2d\u5bfb\u6069\u9053\uff1a\u201c\u770b\u6765\u4ed6\u7684\u7956\u7236\u4e0e\u8fd9\u2018\u91d1\u7ae5 \u7389\u5973\u2019\u672c\u6709\u6781\u6df1\u7684\u6e0a\u6e90\uff0c\u90a3\u4e48\u4ed6\u4e00\u5bb6\u4e5f\u662f\u6b66\u6797\u4e16\u5bb6\u4e86\uff0c\u4f46\u4e3a\u4ec0\u4e48\u4ed6\u4e0e\u6211\u76f8\u4ea4\u5982\u6b64\u771f\u8bda\uff0c\u5374\u59cb \u7ec8\u4e0d\u5c06\u81ea\u5df1\u7684\u5bb6\u4e16\u8bf4\u51fa\u6765\uff1f\u201d\u62ac\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u5434\u9e23\u4e16\u62ac\u9996\u671b\u5929\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u4ed6\u6ee1\u9762\u4eff\u4f5b\u4ff1\u662f \u60b2\u6006\u611f\u6000\u4e4b\u6001\uff0c\u5446\u5446\u5730\u60f3\u7740\u5fc3\u4e8b\u3002 \u3000\u3000\u4ed6\u81ea\u4e0e\u88f4\u73cf\u76f8\u4ea4\u4ee5\u6765\uff0c\u4e00\u76f4\u6f47\u6f47\u6d12\u6d12\uff0c\u5fc3\u4e2d\u4f3c\u4e4e\u6beb\u65e0\u5fc3\u4e8b\uff0c\u6b64\u523b\u88f4\u73cf\u89c1\u4e86\u4ed6\u8fd9\u79cd\u795e\u6001\uff0c \u4e0d\u89c9\u53c8\u4e3a\u4e4b\u5fd6\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5fc3\u4e2d\u4ea6\u6709\u4ec0\u4e48\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u800c\u4e0d\u613f\u5bf9\u4eba\u8bf4\u51fa\u3002\u201d\u4e00\u4e00\u5ff5\u81f3\u6b64\uff0c\u4fbf\u53c8 \u5fd6\u9053\uff1a\u201c\u5509\u2014\u2014\u4f46\u613f\u6211\u80fd\u6709\u5c3d\u529b\u4e4b\u5904\uff0c\u5e2e\u4ed6\u5316\u5f00\u8fd9\u4ef6\u4f24\u5fc3\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u4fbf\u6697\u4e0b\u51b3\u5fc3\uff0c\u65e5\u540e\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u8981\u5c06\u5434\u9e23\u4e16\u5fc3\u4e2d\u7684\u79d8\u5bc6\u63a2\u542c\u51fa\u6765\u3002 \u3000\u3000\u53ea\u89c1\u5434\u9e23\u4e16\u4fef\u9996\u6c89\u601d\u534a\u664c\uff0c\u5df2\u5c06\u8d70\u5230\u95e8\u8fb9\uff0c\u65b9\u81ea\u832b\u7136\u62ac\u8d77\u5934\u6765\uff0c\u8bf4\u9053\u3002 \u3000\u3000\u201c\u6211\u4ee5\u2018\u7537\u5b69\u2019\u4e8c\u5b57\uff0c\u6765\u79f0\u547c\u8fd9\u4f4d\u524d\u8f88\uff0c\u5b9e\u5728\u5927\u5927\u4e0d\u656c\uff0c\u4f46\u8fd9\u4f4d\u524d\u8f88\u4e45\u4f5a\u771f\u540d\uff0c\u6211\u4e5f\u60f3 \u4e0d\u51fa\u4ec0\u4e48\u66f4\u597d\u7684\u79f0\u547c\uff0c\u5c31\u53ea\u5f97\u4ece\u6743\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u4ea6\u81ea\u4e00\u7b11\uff0c\u65b9\u5f85\u8bf4\u201c\u65e0\u59a8\u201d\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6b64\u4e8b\u6839\u672c\u4e0e\u5df1\u65e0\u5173\uff0c\u81ea\u5df1\u53c8\u6709\u4ec0\u4e48\u8d44\u683c \u6765\u8bf4\u201c\u65e0\u59a8\u201d\u4e24\u5b57\uff0c\u4fbf\u4e5f\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u53ea\u542c\u5434\u9e23\u4e16\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8774\u8776\u8ffd\u4e0d\u5230\uff0c\u5929\u53c8\u4eba\u9ed1\uff0c\u8fd9\u7537\u5b69\u867d\u7136\u5014\u5f3a\uff0c\u5230\u5e95\u5e74\u9f84\u5927\u5e7c\uff0c\u5fc3 \u91cc\u4e5f\u4e0d\u7981\u614c\u4e86\u8d77\u6765\uff0c\u56db\u987e\u4e00\u773c\uff0c\u624d\u53d1\u89c9\u81ea\u5df1\u8d8a\u8d70\u8d8a\u8fdc\uff0c\u6b64\u523b\u7adf\u8ff7\u4e86\u8def\u4e86\uff0c\u4e24\u4eba\u5bfb\u4e86\u5757\u77f3\u5934\uff0c \u5750\u5728\u4e00\u8d77\u53d1\u6123\uff0c\u90a3\u5973\u5b69\u80c6\u5b50\u66f4\u5c0f\uff0c\u8d8a\u60f3\u8d8a\u6025\uff0c\u7adf\u6025\u5f97\u54ed\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u53f9\u606f\u4e00\u58f0\uff0c\u50cf\u662f\u5bf9\u4ed6\u4eec\u5f53\u65f6\u7684\u5904\u5883\uff0c\u9887\u4e3a\u540c\u60c5\uff0c\u53c8\u9053\uff1a\u201c\u7537\u5b69\u89c1\u90a3\u5973\u5b69\u54ed\u4e86\uff0c\u80c6 \u6c14\u53cd\u5012\u4e00\u58ee\uff0c\u7275\u7740\u5979\u7684\u624b\u7ad9\u4e86\u8d77\u6765\uff0c\u767e\u822c\u5b89\u6170\u4e8e\u5979\uff0c\u5f53\u7136\u662f\u4e00\u526f\u4fdd\u62a4\u4eba\u7684\u6837\u5b50\uff0c\u4ed6\u867d\u4e5f\u4e0d\u8ba4 \u8bc6\u8def\uff0c\u4f46\u5374\u4e0d\u7ba1\u4e09\u4e03\u4e8c\u5341\u4e00\u5730\u5e26\u7740\u5979\u5c31\u5f80\u56de\u8d70\uff0c\u53ea\u8d70\u4e86\u5927\u534a\u591c\uff0c\u4ed6\u4eec\u53c8\u7d2f\u3001\u53c8\u997f\u3001\u53c8\u6015\u3001\u53c8 \u6094\u3001\u773c\u770b\u8fdc\u5904\u7684\u706f\u706b\u90fd\u5df2\u7184\u4e86\uff0c\u665a\u98ce\u8d8a\u6765\u8d8a\u91cd\uff0c\u4ed6\u4eec\u53ea\u89c9\u5168\u8eab\u90fd\u53c8\u51b0\u53c8\u51b7\uff0c\u53ea\u6709\u5f7c\u6b64\u63e1\u4f4f\u7684 \u4e00\u53cc\u624b\uff0c\u5374\u6e29\u6696\u5f97\u5f88\uff0c\u8fd9\u4efd\u6e29\u6696\u4e0d\u4f46\u7ed9\u4e86\u8fd9\u5973\u5b69\u4e00\u4efd\u5b89\u5168\u7684\u611f\u89c9\uff0c\u4e5f\u7ed9\u4e86\u8fd9\u7537\u5b69\u4e00\u4efd\u52c7 \u6c14\u3002\u201d \u3000\u3000\u4ed6\u6b47\u606f\u4e00\u4e0b\uff0c\u88f4\u73cf\u957f\u53f9\u4e00\u58f0\uff0c\u653e\u773c\u56db\u987e\uff0c\u591c\u8272\u6c89\u6c89\uff0c\u7e41\u661f\u70b9\u70b9\uff0c\u4ed6\u773c\u770b\u4f3c\u4e4e\u73b0\u51fa\u4e00\u5e45\u56fe \u753b\uff0c\u4e00\u4e2a\u7626\u5f31\u7684\u7537\u5b69\u5b50\uff0c\u7275\u7740\u4e00\u4e2a\u5973\u5b69\u5b50\u7684\u624b\uff0c\u5728\u591c\u8272\u4e4b\u4e2d\uff0c\u77a9\u9685\u800c\u884c\uff0c\u5fc3\u91cc\u867d\u7136\u5bb3\u6015\uff0c\u4f46 \u9762\u4e0a\u5374\u7edd\u4e0d\u9732\u51fa\u6765\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4e00\u4efd\u591a\u4e48\u7eaf\u771f\u7684\u60c5\u611f\u5440\uff01\u201d\u88f4\u73cf\u5728\u5fc3\u4e2d\u6697\u81ea\u53f9\u606f\u7740\uff1a\u201c\u4f46\u5e78\u597d\u4ed6\u4eec\u8fd8\u6709\u4e24\u4e2a\u4eba\uff0c \u53ef\u4ee5\u5f7c\u6b64\u5b89\u6170\uff0c\u800c\u6211\u5462\uff1f\u2026\u2026\u201d\u8f6c\u76ee\u800c\u671b\uff0c\u5434\u9e23\u4e16\u771f\u8bda\u7684\u76ee\u5149\uff0c\u6b63\u5728\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5fc3\u5e95\u4e5f\u5347\u51fa\u4e00\u4efd\u6e29\u6696\u7684\u611f\u89c9\uff0c\u8fd9\u4efd\u6e29\u6696\u7684\u611f\u89c9\uff0c\u867d\u548c\u90a3\u5c0f\u7537\u5b69\u7684\u611f\u53d7\u4e0d\u540c\uff0c\u4f46\u5374 \u4e5f\u5df2\u8db3\u591f\u4f7f\u4ed6\u5728\u8d70\u8fc7\u8fd9\u4e00\u6bb5\u6f2b\u957f\u800c\u8270\u82e6\u7684\u4eba\u751f\u65c5\u9014\u65f6\uff0c\u591a\u52a0\u4e00\u4efd\u52c7\u6c14\u4e86\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u4e2d\uff0c\u4ed6\u4eec\u5df2\u8d70\u8fdb\u89d2\u95e8\uff0c\u95e8\u524d\u7684\u5c38\u9996\uff0c\u4ecd\u7136\u9759\u9759\u5730\u5012\u5367\u5728\u90a3\u91cc\uff0c\u4eba\u4e16\u95f4\u7684\u4e00\u5207\u8363 \u8fb1\uff0c\u90fd\u518d\u4e5f\u4e0e\u4ed6\u4eec\u65e0\u5173\u3002\u90a3\u4e48\uff0c\u201c\u6b7b\u201d\uff0c\u5bf9\u4eba\u7c7b\u6765\u8bf4\uff0c\u6838\u7b97\u662f\u5e78\u8fd0\uff0c\u6291\u6216\u662f\u4e0d\u5e78\u5462\uff1f\u8fd9\u95ee\u9898 \u8c01\u4e5f\u4e0d\u80fd\u89e3\u7b54\uff0c\u4e5f\u6ca1\u6709\u8c01\u4f1a\u53bb\u5bfb\u6c42\u89e3\u7b54\u7684\u3002 \u3000\u3000\u5434\u9e23\u4e16\u6c89\u58f0\u53c8\u9053\uff1a\u201c\u5c31\u51ed\u7740\u8fd9\u4efd\u6e29\u6696\u4e0e\u52c7\u6c14\uff0c\u4ed6\u4eec\u7ec8\u4e8e\u627e\u5230\u4e86\u4ed6\u4eec\u7684\u5bb6\uff0c\u90a3\u65f6\u5929\u5df2\u5feb\u4eae \u4e86\uff0c\u90a3\u7537\u5b69\u7d27\u7d27\u63e1\u7740\u5973\u5b69\u7684\u624b\uff0c\u5feb\u4e50\u5f97\u9ad8\u547c\u4e00\u58f0\uff0c\u4ed6\u81ea\u5e7c\u4ece\u672a\u6709\u8fc7\u4efb\u4f55\u4e00\u523b\u7684\u5feb\u4e50\u80fd\u548c\u6b64\u523b \u6bd4\u62df\uff0c\u4e8e\u662f\u4ed6\u6697\u4e2d\u544a\u8bc9\u81ea\u5df1\uff1a\u201c\u4ee5\u540e\u6c38\u8fdc\u4e0d\u8981\u79bb\u5f00\u5bb6\u4e86\uff0c\u5916\u9762\u867d\u7136\u597d\u73a9\uff0c\u4f46\u5374\u90a3\u4e48\u51b7\uff0c\u5bb6\u91cc \u867d\u4e0d\u597d\u73a9\uff0c\u4f46\u5374\u662f\u6e29\u6696\u7684\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fcd\u4e0d\u4f4f\u53c8\u6df1\u957f\u5730\u53f9\u606f\u4e86\u8d77\u6765\uff0c\u4e00\u9762\u5728\u5fc3\u4e2d\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u4e16\u4e0a\u53c8\u6709\u4ec0\u4e48\u5730\u65b9\u80fd\u6bd4\u5f97\u4e0a \u2018\u5bb6\u2019\u7684\u6e29\u6696\u5462\uff1f\u201d\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u53ea\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u80fd\u65ad\u7edd\uff0c\u6068\u4e0d\u5f97\u7acb\u5373\u8dd1\u5230\u7236\u6bcd\u7684\u575f\u524d\u5927 \u54ed\u4e00\u573a\uff0c\u4e00\u9762\u5374\u53c8\u4e0d\u7981\u4e3a\u8fd9\u53cc\u5b69\u5b50\u9ad8\u5174\uff0c\u4ed6\u4eec\u7ec8\u4e8e\u627e\u5230\u81ea\u5df1\u7684\u5bb6\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u5bb6\u7684\u4eba\uff0c\u5bf9\u4e8e\u201c\u5bb6\u201d\uff0c\u4e0d\u603b\u662f\u6709\u7740\u4e00\u4efd\u6df1\u631a\u7684\u6000\u5ff5\u5417\uff1f \u3000\u3000\u4ed6\u4eec\u5e76\u80a9\u800c\u884c\uff0c\u811a\u6b65\u8e0f\u5728\u56ed\u4e2d\u7684\u788e\u77f3\u8def\u4e0a\uff0c\u53d1\u51fa\u9635\u9635\u8f7b\u54cd\uff0c\u88f4\u73cf\u9ed8\u7136\u826f\u4e45\uff0c\u5374\u89c1\u5434\u9e23\u4e16 \u4ea6\u4e45\u4e45\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5434\u9e23\u4e16\u7684\u76ee\u5149\u4f4e\u5782\uff0c\u671b\u7740\u811a\u6b65\u79fb\u52a8\uff0c\u4f3c\u4e4e\u5fc3\u60c5 \u4e5f\u548c\u81ea\u5df1\u4e00\u6837\u5730\u6c89\u91cd\uff0c\u4e00\u6837\u5730\u60b2\u54c0\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u53bb\u6253\u6270\u522b\u4eba\u7684\u6c89\u6069\uff0c\u6b63\u5982\u4e5f\u4e0d\u613f\u522b\u4eba\u6765\u6253\u6270\u4ed6\u4e00\u6837\uff0c\u4fbf\u4efb\u51ed\u8fd9\u4efd\u6c89\u91cd\u7684\u6c89\u9ed8\uff0c\u50cf \u662f\u6c38\u65e0\u6b62\u5883\u822c\u5730\u5ef6\u7eed\u4e0b\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u5434\u9e23\u4e16\u7a81\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u62ac\u8d77\u76ee\u5149\uff0c\u4ef0\u671b\u661f\u7fa4\uff0c\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u4e24\u4e2a\u7eaf\u771f\u7684\u5b69\u5b50 \u7b2c\u4e00\u6b21\u611f\u89c9\u5230\u5bb6\u7684\u6e29\u6696\uff0c\u800c\u5927\u6b65\u5411\u5bb6\u4e2d\u8dd1\u53bb\u7684\u65f6\u5019\uff0c\u5509\u2014\u2014\u4ed6\u4eec\u5374\u6c38\u8fdc\u4e0d\u518d\u6709\u5bb6\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u51db\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u2026\xb7\xb7\u201d\u5434\u9e23\u4e16\u4f38\u624b\u4e00\u62ed\u773c\u5e18\uff0c\u4f3c\u4e4e\u662f\u5728\u62b9\u7740 \u773c\u4e2d\u7684\u6cea\u73e0\uff0c\u4f46\u662f\u4ed6\u7eb5\u5df2\u6d41\u6cea\uff0c\u5374\u4e5f\u662f\u4e0d\u613f\u88ab\u4eba\u770b\u5230\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6781\u5feb\u5730\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u8dd1\u5230\u95e8\u53e3\uff0c\u5927\u95e8\u7adf\u662f\u865a\u63a9\u7740\u7684\uff0c\u90a3\u7537\u5b69\u867d\u4e0d\u6ce8\u610f\uff0c\u4f46\u5973 \u5b69\u5b50\u603b\u662f\u8f83\u4e3a\u7ec6\u5fc3\uff0c\u5374\u5df2\u89c9\u5bdf\u5230\u4e86\uff0c\u4e8e\u662f\u5979\u53e3\u53eb\u7740\u8dd1\u8fdb\u95e8\u53bb\uff0c\u54ea\u77e5\u95e8\u5185\u5374\u65e0\u5e94\u58f0\uff0c\u53ea\u6709\u5979\u547c \u58f0\u7684\u4f59\u97f3\uff0c\u5728\u56db\u58c1\u98d8\u8361\u7740\u3002\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7adf\u53c8\u91cd\u590d\u4e86\u53e5\uff1a\u201c\u5728\u56db\u58c1\u98d8\u8361\u7740\u3002\u201d\u5c3e\u58f0\u62d6\u5f97\u5f88 \u957f\uff0c\u957f\u957f\u7684\u5c3e\u58f0\u53c8\u662f\u90a3\u6837\u4f4e\u6c89\uff0c\u4f4e\u6c89\u5f97\u50cf\u662f\u81ea\u5df1\u5fc3\u623f\u7684\u8df3\u52a8\u3002 \u3000\u3000\u88f4\u73cf\u673a\u4f36\u601c\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c\u53ea\u89c9\u4e00\u79cd\u4e0d\u7965\u7684\u9634\u5f71\uff0c\u5728\u81ea\u5df1\u5fc3\u5934\u500f\u7136\u6cdb\u8d77\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u4f4e\u4f4e \u95ee\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u5bb6\u91cc\u7684\u4eba\u90fd\u7761\u7740\u4e86\u5417\uff1f\u201d\u4f46\u662f\u4ed6\u81ea\u5df1\u77e5\u9053\uff0c\u81ea\u5df1\u7684\u8fd9\u79cd\u95ee\u8bdd\uff0c\u95ee\u5f97\u53c8\u662f\u591a \u4e48\u53ef\u7b11\u54e9\u3002 \u3000\u3000\u5434\u9e23\u4e16\u957f\u53f9\u4e00\u58f0\uff0c\u4fa7\u987e\u4e00\u773c\uff0c\u7f13\u7f13\u6447\u4e86\u6447\u5934\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u90a3\u5973\u5b69\u58f0\u97f3\u8d8a\u558a\u8d8a\u5927\uff0c\u811a\u6b65 \u4e5f\u8d8a\u8dd1\u8d8a\u5feb\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u7531\u524d\u9662\u8dd1\u81f3\u5385\u5802\uff0c\u8fd9\u6b66\u6797\u4e16\u5bb6\u672c\u662f\u4e3e\u5bb6\u5c45\u6b64\uff0c\u5385\u623f\u5efa\u5f97\u751a\u662f\u5e7f \u9614\uff0c\u5385\u524d\u7684\u53f0\u9636\uff0c\u5c31\u6709\u5341\u6570\u7ea7\u4e4b\u591a\uff0c\u8fd9\u7537\u5b69\u4e0e\u5973\u5b69\u4e24\u4eba\u5927\u558a\u7740\u8dd1\u5230\u77f3\u9636\u524d\uff0c\u56db\u4e0b\u4ecd\u7136\u5bc2\u65e0\u5e94 \u58f0\uff0c\u5fc3\u91cc\u90fd\u4e0d\u7981\u53d1\u8d77\u614c\u6765\uff0c\u4e09\u811a\u4e24\u6b65\u5730\u8dd1\u4e86\u4e0a\u53bb\uff0c\u63a8\u5f00\u5385\u95e8\uff0c\u5f80\u91cc\u4e00\u671b\u4e00\u4e00\u201d\u88f4\u73cf\u53ea\u89c9\u5fc3\u4e2d \u201c\u7830\u7830\u201d\u8df3\u52a8\uff0c\u867d\u4e0d\u60f3\u6253\u65ad\u4ed6\u7684\u8bdd\uff0c\u5374\u4ecd\u7981\u4e0d\u4f4f\u8131\u53e3\u95ee\u9053\uff1a\u201c\u91cc\u9762\u600e\u6837\uff1f\u201d\u8f6c\u76ee\u671b\u53bb\uff0c\u4f9d\u7a00 \u89c1\u5f97\u5434\u9e23\u4e16\u9762\u65e5\u4e4b\u4e0a\uff0c\u4ea6\u81ea\u6ee1\u662f\u6fc0\u52a8\u4e4b\u8272\uff0c\u53cc\u62f3\u7d27\u63e1\uff0c\u76ee\u5149\u76f4\u89c6\uff0c\u63a5\u7740\u7f13\u7f13\u53c8\u9053\uff1a\u201c\u6b64\u523b\u5df2 \u662f\u6e05\u6668\uff0c\u6668\u5149\u867d\u71b9\u5fae\uff0c\u4f46\u5341\u6b65\u4e4b\u5185\uff0c\u5df2\u53ef\u8fa8\u4eba\u9762\u76ee\uff0c\u4ed6\u4eec\u63a8\u95e8\u4e00\u671b\u2014\u2014\u5509\uff01\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c \u7adf\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u65b9\u81ea\u63a5\u9053\uff1a\u201c\u83ab\u8bf4\u8fd9\u4e24\u4eba\u4ec5\u662f\u9aeb\u9f84\u5e7c\u7ae5\uff0c\u4fbf\u662f\u4f60\u6211\uff0c\u89c1\u4e86\u90a3\u5385\u4e2d\u7684\u666f\u8c61\uff0c\u53ea \u6015\u4e5f\u8981--\u4e00\u201d\u4ed6\u8bf4\u5f97\u672c\u5c31\u6781\u6162\uff0c\u518d\u52a0\u4e0a\u4e0d\u65f6\u957f\u53f9\uff0c\u4e0d\u65f6\u505c\u987f\uff0c\u88f4\u73cf\u53ea\u89c9\u81ea\u5df1\u5fc3\u80f8\u4e4b\u95f4\uff0c\u50cf\u662f \u7a81\u5730\u5835\u585e\u4e86\u4e00\u5757\u5927\u77f3\u5934\u822c\u5730\u96be\u53d7\uff0c\u5fc3\u623f\u4e2d\u7684\u201c\u7830\u7830\u201d\u8df3\u52a8\u4e4b\u58f0\uff0c\u5374\u66f4\u52a0\u54cd\u4e86\uff0c\u76ee\u5149\u51dd\u6ce8\u7740\u5434 \u9e23\u4e16\uff0c\u53ea\u671b\u4ed6\u5feb\u4e9b\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u54ea\u77e5\u6b64\u523b\u5434\u9e23\u4e16\u8bed\u58f0\u4e00\u987f\u4e4b\u540e\uff0c\u811a\u6b65\u7adf\u4e5f\u968f\u4e4b\u505c\u4e0b\uff0c\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u7a81\u5730\u957f\u53f9\u9053\uff1a \u201c\u90a3\u5385\u4e2d\u7684\u666f\u8c61\uff0c\u4e0d\u8bf4\u4e5f\u7f62\uff0c\u603b\u4e4b\u2014\u2014\u201d\u88f4\u73cf\u5fc3\u4e2d\u4e00\u6025\uff0c\u65b9\u5f85\u8ffd\u95ee\uff0c\u4f46\u8f6c\u5ff5\u5fd6\u9053\uff1a\u201c\u4e16\u4e0a\u60b2 \u60e8\u4e4b\u4e8b\u672c\u5df2\u6781\u591a\uff0c\u6211\u4f55\u82e6\u8981\u53bb\u591a\u542c\u4e00\u4e9b\u3002\u201d\u4ed6\u5fc3\u77e5\u8fd9\u5385\u4e2d\u666f\u8c61\u5fc5\u5b9a\u6781\u591a\u60b2\u60e8\u6b8b\u9177\uff0c\u5fc3\u4e2d\u867d\u7136 \u597d\u5947\uff0c\u5374\u4ecd\u80fd\u5fcd\u4f4f\u4e0d\u95ee\u3002 \u3000\u3000\u53ea\u542c\u5434\u9e23\u4e16\u63a5\u9053\uff1a\u201c\u8fd9\u7537\u5973\u4e24\u4f4d\u7ae5\u5b50\u7684\u4e00\u5bb6\u5927\u5c0f\u6570\u5341\u53e3\u4eba\uff0c\u7adf\u5728\u4ed6\u4eec\u8ff7\u9014\u7684\u4e00\u591c\u4e4b\u4e2d\uff0c \u5168\u6570\u8eab\u906d\u60e8\u6b7b\uff0c\u8fd9\u6570\u5341\u53e3\u5177\u5c38\u8eab\uff0c\u6b64\u523b\u7adf\u5168\u90e8\u5806\u5728\u8fd9\u95f4\u5bbd\u9614\u7684\u5385\u623f\u91cc\uff0c\u4e00\u7ebf\u7070\u767d\u7684\u5929\u5149\uff0c\u81ea \u95e8\u5916\u5c04\u5165\uff0c\u53ea\u89c1\u8fd9\u4e9b\u5c38\u8eab\u4e0a\uff0c\u8840\u8ff9\u4ecd\u9c9c\uff0c\u5c38\u9aa8\u672a\u5bd2\uff0c\u65e0\u8bba\u7537\u5973\u8001\u5e7c\uff0c\u9762\u4e0a\u4ff1\u90fd\u5e26\u7740\u60ca\u6050\u4e4b \u8272\uff0c\u663e\u7136\u662f\u4e34\u6b7b\u4e4b\u9645\uff0c\u906d\u53d7\u5230\u6781\u5927\u7684\u60ca\u6050\uff0c\u800c\u6b7b\u540e\u4e5f\u4e0d\u80fd\u5b89\u7136\u7791\u76ee\u3002\u201d \u3000\u3000\u4ed6\u867d\u672a\u5c06\u5385\u4e2d\u666f\u8c61\u8be6\u7ec6\u63cf\u8ff0\uff0c\u4f46\u5c31\u53ea\u8fd9\u5be5\u5be5\u6570\u8bed\uff0c\u5374\u5df2\u4f7f\u5f97\u88f4\u73cf\u542c\u6765\u51b7\u6c57\u6d94\u6d94\uff0c\u5fc3\u80f8\u51e0 \u4e4e\u4e3a\u4e4b\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u4ed6\u63e1\u62f3\u4e00\u51fb\uff0c\u77a0\u76ee\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u5e72\u7684\uff1f\u96be\u9053\u8fd9\u4eba\u7adf\u6ca1\u6709\u534a\u70b9\u4eba\u6027\uff1f\u4ed6\u7eb5\u7136\u4e0e\u8fd9\u5bb6\u4eba\u6709 \u4ec7\uff0c\u4f55\u82e6\u5c06\u8fd9\u5bb6\u4e2d\u7684\u5987\u5b7a\u4e5f\u4e00\u8d77\u5982\u6b64\u6b8b\u9177\u5730\u6740\u6b7b\u5462\uff1f\u201d\u5fc3\u4e2d\u60b2\u6124\u4ea4\u96c6\uff0c\u6068\u4e0d\u5f97\u5c06\u6740\u6b7b\u8fd9\u4e9b\u5987 \u5b7a\u7684\u4eba\uff0c\u6293\u8fc7\u6765\u72e0\u72e0\u75db\u51fb\u6570\u638c\uff0c\u53c8\u6068\u4e0d\u5f97\u7acb\u523b\u8dd1\u5230\u8fd9\u4e00\u53cc\u5e7c\u7ae5\u8eab\u4fa7\uff0c\u53bb\u5b89\u6170\u4ed6\u4eec\uff0c\u773c\u524d\u4f3c\u4e4e \u53c8\u6cdb\u8d77\u4e00\u5e45\u56fe\u753b\u3002 \u3000\u3000\u4e00\u53cc\u9aeb\u9f84\u5e7c\u7ae5\uff0c\u75db\u54ed\u7740\u5954\u5411\u8fd9\u4e9b\u5c38\u8eab\uff0c\u5954\u5411\u4ed6\u4eec\u7236\u6bcd\u5c38\u8eab\u7684\u65c1\u8fb9\uff0c\u5927\u58f0\u75c8\u54ed\u7740\uff0c\u4ed6\u4eec\u5f53 \u7136\u65e0\u80fd\u529b\u5c06\u8fd9\u4e9b\u5c38\u8eab\u4e8e\u662f\u57cb\u846c\uff0c\u66f4\u65e0\u80fd\u529b\u66ff\u4ed6\u4eec\u590d\u4ec7\uff0c\u9664\u4e86\u75db\u54ed\u4e4b\u5916\uff0c\u5c31\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u505a\u4e86\u3002 \u201c\u3002\u6e10\u6e10\uff0c\u8fd9\u5e45\u56fe\u5728\u4ed6\u773c\u524d\u6a21\u7cca\u8d77\u6765\uff0c\u4ed6\u7ec6\u7ec6\u4f53\u4f1a\u7740\u8fd9\u4e00\u53cc\u5e7c\u7ae5\u5f53\u65f6\u7684\u5fc3\u60c5\uff0c\u8d8a\u60f3\u8d8a\u89c9\u96be \u53d7\uff0c\u53ea\u6068\u4e0d\u5f97\u653e\u58f0\u75db\u54ed\u4e00\u573a\u3002\u5374\u89c1\u5434\u545c\u4e16\u4ea6\u81ea\u5782\u9996\u9ed8\u7136\u826f\u4e45\uff0c\u7a81\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u623f\u95f4\u5230 \u4e86\u3002\u201d\u88f4\u73cf\u62ac\u76ee\u4e00\u671b\uff0c\u81ea\u5df1\u623f\u4e2d\u7684\u706f\u5149\uff0c\u4ecd\u7136\u4eae\u7740\uff0c\u660f\u9ec4\u7684\u5149\u7ebf\uff0c\u6620\u5728\u60e8\u767d\u7684\u7a97\u7eb8\u4e0a\uff0c\u4f3c\u4e4e \u500d\u51c4\u51c9\u3002 \u3000\u3000\u5fc3\u60c5\u54c0\u75db\u7684\u4eba\uff0c\u773c\u4e2d\u6240\u89c1\uff0c\u65e0\u8bba\u662f\u4ec0\u4e48\uff0c\u90fd\u4f1a\u589e\u52a0\u4ed6\u7684\u54c0\u75db\u4e4b\u5fc3\uff0c\u5176\u5b9e\u4e16\u4e0a\u706f\u5149\u672c\u90fd\u660f \u9ec4\uff0c\u7a97\u7eb8\u4ea6\u90fd\u767d\u8272\uff0c\u53c8\u6709\u4ec0\u4e48\u51c4\u51c9\u4e4b\u610f\u5462\uff01 \u3000\u3000\u4ed6\u4eec\u9ed8\u7136\u8d70\u5165\u623f\u4e2d\uff0c\u88f4\u73cf\u4fbf\u81ea\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e24\u4f4d\u524d\u8f88\u5947\u4eba\u7684\u8eab\u4e16\uff0c\u7adf\u662f\u5982\u6b64\u51c4\u51c9\uff0c \u4f46\u662f\u2014\u2014\u90a3\u201d\u91d1\u7ae5\u201c\u524d\u8f88\u540e\u6765\u600e\u4f1a\u2026\u2026\u201d\u4ed6\u672c\u60f3\u95ee\u90a3\u91d1\u7ae5\u540e\u6765\u8eab\u8eaf\u600e\u4f1a\u53d8\u5f97\u5982\u6b64\u7578\u5c0f\uff0c\u4f46\u53c8 \u89c9\u5f97\u5982\u6b64\u95ee\u6cd5\uff0c\u5927\u4e3a\u4e0d\u656c\uff0c\u4fbf\u500f\u7136\u4f4f\u53e3\u3002\u5374\u542c\u5434\u9e23\u4e16\u5df2\u81ea\u7f13\u7f13\u53f9\u9053\uff1a\u201c\u4ed6\u4eec\u5e74\u5e7c\u529b\u5f31\uff0c\u9661\u7136 \u9677\u5165\u8fd9\u79cd\u60b2\u60e8\u7684\u72b6\u51b5\u4e2d\uff0c\u771f\u662f\u53eb\u5929\u4e0d\u5e94\uff0c\u547c\u5730\u4e0d\u7075\uff0c\u4e24\u4eba\u5728\u90a3\u5c38\u9996\u8fb9\u6574\u6574\u75db\u54ed\u4e86\u4e00\u65e5\uff0c\u624d\u6709 \u4e2a\u8fdc\u5728\u4e94\u91cc\u4e4b\u5916\u7684\u730e\u6237\u8dd1\u6765\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u89e3\u91ca\u7740\u9053\uff1a\u201c\u4ed6\u4eec\u9690\u5c45\u4e4b\u5730\uff0c\u672c\u5728\u4e00\u5904\u6781\u4e3a \u50fb\u9759\u7684\u5c71\u90ca\uff0c\u56db\u8fd1\u90fd\u6ca1\u6709\u90bb\u4eba\uff0c\u82e5\u975e\u8fd9\u4e9b\u730e\u6237\u5076\u7136\u6765\u6b64\uff0c\u542c\u5230\u91cc\u9762\u7684\u54ed\u58f0\uff0c\u624d\u8d70\u4eba\u4e00\u770b\uff0c\u53ea \u6015\u4e00\u4e2a\u6708\u540e\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u8fd9\u95f4\u5de8\u5b85\u4e2d\u53d1\u751f\u60e8\u6848\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5ff5\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u4f9d\u6211\u770b\u6765\uff0c\u8fd9\u5bb6\u4e2d\u4e4b\u4e3b\uff0c\u5728\u65e9\u5e74\u95ef\u8361\u6c5f\u6e56\u4e4b\u9645\uff0c\u5fc5\u662f\u7ed3\u4e0b\u4e0d\u5c11\u4ec7 \u5bb6\uff0c\u662f\u4ee5\u4ed6\u624d\u4f1a\u9009\u4e0b\u8fd9\u7b49\u6240\u5728\u6765\u505a\u9690\u5c45\u4e4b\u5730\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u5fae\u9894\u9996\uff0c\u968f\u53c8\u63a5\u9053\uff1a\u201c\u8fd9\u4e9b\u730e\u6237\u89c1\u4e86\u8fd9\u79cd\u60c5\u51b5\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\uff0c\u4f46\u4ed6\u4eec\u7ec8\u5e74 \u4f24\u751f\uff0c\u80c6\u5b50\u81ea\u6bd4\u5e38\u4eba\u5927\u4e9b\uff0c\u5fc3\u4e2d\u867d\u60ca\u4e0d\u4e71\uff0c\u5c31\u5c06\u8fd9\u4e9b\u5c38\u8eab\u5168\u90e8\u57cb\u846c\u8d77\u6765\u3002\u201d \u3000\u3000\u88f4\u73cf\u957f\u957f\u900f\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u771f\u662f\u5929\u65e0\u7edd\u4eba\u4e4b\u8def\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e9b\u730e\u6237\u5012\u90fd\u662f\u5584\u826f\u4e4b \u4eba\u3002\u201d \u3000\u3000\u4ed6\u65b9\u81ea\u6697\u4e2d\u4e3a\u8fd9\u4e00\u53cc\u5e7c\u7ae5\u5e86\u5e78\uff0c\u54ea\u77e5\u5434\u9e23\u4e16\u7a81\u5730\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u730e\u6237\u4e00\u770b\u8fd9 \u6837\u5de8\u5927\u7684\u5b85\u9662\u4e2d\uff0c\u9664\u4e86\u4e24\u4e2a\u5e7c\u7ae5\u4e4b\u5916\u90fd\u662f\u6b7b\u4eba\uff0c\u4ed4\u7ec6\u4e00\u95ee\uff0c\u53c8\u77e5\u9053\u4ed6\u4eec\u4e0e\u5916\u4eba\u90fd\u4e0d\u76f8\u5f80\u6765\uff0c \u6697\u4e2d\u65e9\u5df2\u8d77\u4e86\u6076\u5ff5\uff0c\u5c06\u5c38\u8eab\u57cb\u846c\u4e4b\u540e\uff0c\u7adf\u7136\u96c0\u5de2\u9e20\u5360\uff0c\u4e3e\u5bb6\u90fd\u8fc1\u5165\u8fd9\u680b\u5de8\u5b85\u4e2d\u6765\uff0c\u800c\u4e14\u5bf9\u8fd9 \u5e7c\u7ae5\u4e24\u4eba\u767e\u822c\u51cc\u8fb1\u3002\u8fd9\u5e7c\u7ae5\u4e24\u4eba\u5bb6\u906d\u60e8\u53d8\uff0c\u5b64\u82e6\u4f36\u8ba2\uff0c\u518d\u9047\u7740\u8fd9\u73ed\u6076\u4eba\uff0c\u5509\u2014\u2014\u201d\u88f4\u73cf\u5251\u7709 \u6012\u626c\uff0c\u624b\u638c\u7d27\u63e1\uff0c\u5728\u684c\u4e0a\u91cd\u91cd\u6253\u4e86\u4e00\u62f3\uff0c\u4ed6\u5bf9\u4eba\u5bf9\u4e8b\uff0c\u867d\u7136\u4ff1\u90fd\u5b58\u7740\u4e5d\u5206\u5bbd\u6055\u4e4b\u5fc3\uff0c\u4f46\u6b64\u523b \u5fc3\u4e2d\u4ea6\u4e0d\u89c9\u6012\u6c14\u5927\u4f5c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u79cd\u72fc\u5fc3\u72d7\u80ba\u4e4b\u4eba\uff0c\u771f\u8be5\u5200\u5200\u65a9\u5c3d\uff0c\u4e2a\u4e2a\u8bdb\u7edd\u624d\u5bf9\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u76ee\u5149\u8f6c\u5904\uff0c\u53ea\u89c1\u4ed6\u6ee1\u9762\u4ff1\u662f\u6012\u5bb9\uff0c\u6240\u8bf4\u4e4b\u8bdd\uff0c\u4ea6\u662f\u4ed6\u4ece\u672a\u8bf4\u8fc7\u7684\uff0c\u4e0d\u7981\u6697\u53f9\u4e00 \u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u5bbd\u4e8e\u5f85\u4eba\uff0c\u4e25\u4e8e\u5f85\u5df1\uff0c\u522b\u4eba\u65e0\u8bba\u5982\u4f55\u5bf9\u5f85\u4e8e\u4ed6\uff0c\u4ed6\u90fd\u751f\u50cf\u662f\u6ca1\u6709\u653e\u5728\u5fc3 \u4e0a\uff0c\u4f46\u542c\u4e86\u522b\u4eba\u7684\u4e0d\u5e73\u4e4b\u4e8b\uff0c\u5374\u53c8\u5982\u6b64\u6c14\u6124\u4e0d\u5e73\uff0c\u7adf\u7136\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u5509\u2014\u2014\u4ea4\u53cb\u5982\u6b64\uff0c\u592b \u590d\u4f55\u61be\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u5fae\u8f6c\uff0c\u4fbf\u53c8\u63a5\u9053\uff1a\u201c\u8fd9\u4e00\u53cc\u5e7c\u7ae5\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u81ea\u7136\u65e0\u6cd5\u518d\u5fcd\u53d7\u5f97\u4f4f\uff0c\u4fbf\u5077\u5077\u8dd1 \u4e86\u51fa\u6765\uff0c\u4eba\u6d77\u832b\u832b\uff0c\u5929\u4e0b\u867d\u5927\uff0c\u4f46\u53c8\u6709\u4f55\u5730\u662f\u4ed6\u4eec\u5bb9\u8eab\u4e4b\u5904\uff1f\u201d \u3000\u3000\u76ee\u5149\u518d\u6b21\u4e00\u8f6c\uff0c\u5374\u89c1\u88f4\u73cf\u9762\u4e0a\u6b64\u523b\u6012\u5bb9\u5df2\u655b\uff0c\u5374\u6362\u4e86\u6ee1\u8138\u7684\u60b2\u6006\u4e4b\u8272\uff0c\u4ed6\u77e5\u9053\u8fd9\u60c5\u611f\u4e30 \u5bcc\u7684\u5c11\u5e74\uff0c\u53c8\u88ab\u81ea\u5df1\u8fd9\u51e0\u53e5\u8bdd\u52fe\u8d77\u4e86\u5fc3\u4e2d\u7684\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u8bed\u58f0\u4fbf\u4e3a\u4e4b\u987f\u4f4f\u3002 \u3000\u3000\u88f4\u73cf\u679c\u7136\u672a\u51fa\u6240\u6599\uff0c\u5fc3\u4e2d\u6b63\u60f3\u5230\u81ea\u5df1\u6d41\u6d6a\u7684\u65f6\u5019\uff0c\u6240\u906d\u9047\u5230\u7684\u8f9b\u9178\u82e6\u8fa3\uff0c\u6240\u4f53\u4f1a\u5230\u7684\u51b7 \u6696\u4eba\u60c5\uff0c\u708e\u51c9\u4e16\u6001\uff0c\u800c\u8fd9\u4e00\u53cc\u5e7c\u7ae5\uff0c\u5e74\u9f84\u8fd8\u4e0d\u53ca\u81ea\u5df1\u5927\uff0c\u5728\u8fd9\u832b\u832b\u4eba\u6d77\u91cc\uff0c\u5176\u906d\u9047\u81ea\u66f4\u53ef\u53f9 \u4e86\u3002\u4e8e\u662f\u4ed6\u53c8\u4e0d\u7981\u957f\u53f9\u4e00\u58f0\uff0c\u5782\u76ee\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u540e\u6765\u4ed6\u4eec\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u6c89\u541f\u534a\u664c\uff0c\u5ffd\u5730\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u82e6\u6781\u4e4b\u5904\u5fc5\u6709\u7518\u6765\uff0c\u60b2\u6781\u4e4b\u5883\u5fc5\u6709\u4e50\u81f3\u3002\u8fd9\u4e00 \u53cc\u5e7c\u7ae5\u53ef\u601c\u7684\u906d\u9047\uff0c\u7adf\u5168\u7136\u6539\u89c2\uff0c\u4ed6\u4eec\u6d41\u6d6a\u4e4b\u4e2d\uff0c\u7adf\u9047\u7740\u4e24\u4e2a\u6b66\u6797\u5947\u4eba\uff0c\u5c06\u4ed6\u4eec\u5206\u522b\u5e26\u4e86\u56de \u53bb\uff0c\u4f20\u6388\u7ed9\u4ed6\u4eec\u4e00\u8eab\u6b66\u529f\uff0c\u4f7f\u5f97\u4ed6\u4eec\u4e24\u4eba\uff0c\u53d8\u6210\u6570\u5341\u5e74\u6765\u6b66\u6797\u672a\u6709\u7684\u76d6\u4e16\u5947\u4eba\u3002\u62a5\u590d\u4e86\u81ea\u8eab \u7684\u8840\u6d77\u6df1\u4ec7\uff0c\u5c06\u90a3\u73ed\u8d2a\u5fc3\u7684\u730e\u6237\uff0c\u5927\u5927\u60e9\u6212\u4e86\u4e00\u987f\u3002\u88f4\u5144\uff0c\u4f60\u53ef\u77e5\u9053\uff1a\u4e00\u4e2a\u4eba\u5c11\u5e74\u65f6\u7684\u5f97 \u610f\uff0c\u672a\u5fc5\u662f\u798f\uff0c\u800c\u5c11\u5e74\u7684\u6298\u78e8\uff0c\u5374\u5f80\u5f80\u4f7f\u5f97\u4ed6\u65e5\u540e\u80fd\u6709\u66f4\u5927\u7684\u6210\u5c31\u3002\u4e00\u5757\u7f8e\u7389\uff0c\u4e0d\u7ecf\u7422\u78e8\uff0c \u4e0d\u80fd\u6210\u5668\uff0c\u4eba\u4e4b\u4e00\u751f\uff0c\u4e0d\u4e5f\u50cf\u7f8e\u7389\u4e00\u6837\u7684\u5417\uff1f\u201d \u3000\u3000\u4ed6\u89c1\u4e86\u88f4\u73cf\u7684\u60b2\u6006\u4e4b\u6001\uff0c\u60f3\u5230\u88f4\u73cf\u7684\u8eab\u4e16\uff0c\u77e5\u9053\u4ed6\u6b64\u523b\u5fc3\u4e2d\u96be\u514d\u6c89\u90c1\uff0c\u4fbf\u8bf4\u51fa\u8fd9\u756a\u8bdd \u6765\uff0c\u6b63\u662f\u53d6\u745f\u800c\u6b4c\uff0c\u522b\u6709\u6240\u5bc4\uff0c\u88f4\u73cf\u7edd\u9876\u806a\u660e\uff0c\u7109\u6709\u542c\u4e0d\u51fa\u6765\u7684\u9053\u7406\u3002 \u3000\u3000\u4ed6\u611f\u6fc0\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u4f46\u662f\u2026\u2026\u4ed6\u4eec\u600e\u5730\u4f1a\u2026\u2026\u4f1a\u2026\u2026\u201d\u4ed6\u4e00\u8fde\u8bf4\u4e86\u4e24\u4e2a \u201c\u4f1a\u201d\u5b57\uff0c\u5374\u4ecd\u6ca1\u6709\u5c06\u5fc3\u4e2d\u60f3\u95ee\u7684\u8bdd\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4f46\u5434\u9e23\u4e16\u5374\u5df2\u4e86\u89e3\u4ed6\u8a00\u4e0b\u4e4b\u610f\uff0c\u4fbf\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u867d\u7136\u4eba\u5206\u4e24\u5730\uff0c\u4f46\u5fc3\u5374\u5e38\u5728\u4e00\u5904\uff0c\u4e24\u4eba \u523b\u82e6\u7ec3\u529f\u4e4b\u6687\uff0c\u4ed6\u56fa\u7136\u65f6\u65f6\u523b\u523b\u5728\u60f3\u7740\u5979\uff0c\u5979\u4e5f\u65f6\u65f6\u523b\u523b\u5730\u60f3\u7740\u4ed6\uff0c\u4e24\u4eba\u52ab\u540e\u9980\u751f\uff0c\u5e38\u5ff5\u5bb6 \u4ec7\uff0c\u5fc3\u4e2d\u867d\u7136\u591a\u662f\u60b2\u82e6\uff0c\u4f46\u5f7c\u6b64\u53ea\u6709\u4e00\u60f3\u5230\u5bf9\u65b9\u5fc3\u91cc\u5b9a\u6709\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u751f\u51fa\u4e00\u4e1d\u751c\u610f \u6765\u3002\u201d \u3000\u3000\u800c\u4e14\uff0c\u4ed6\u4eec\u4e5f\u77e5\u9053\u4f20\u6388\u81ea\u5df1\u6b66\u529f\u7684\u5e08\u5085\uff0c\u90fd\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u7684\u5947\u4eba\uff0c\u81ea\u5df1\u53ea\u8981\u5b66\u6210\u6b66\u529f\uff0c \u590d\u4ec7\u5fc5\u975e\u65e0\u671b\uff0c\u5fc3\u91cc\u81ea\u4e5f\u6ca1\u6709\u4ee5\u524d\u90a3\u4e48\u96be\u53d7\uff0c\u6bcf\u5929\u53ea\u5e0c\u671b\u81ea\u5df1\u6b66\u529f\u80fd\u5feb\u4e9b\u5b66\u6210\uff0c\u81ea\u5df1\u80fd\u5feb\u4e9b \u957f\u5927\uff0c\u4e0b\u5c71\u5bfb\u5f97\u4ec7\u4eba\uff0c\u62a5\u5374\u6df1\u4ec7\uff0c\u548c\u81ea\u5df1\u7ec8\u5e74\u5fc6\u5ff5\u7684\u4eba\u76f8\u4f1a\uff0c\u56e0\u4e4b\u4ed6\u4eec\u4e60\u6b66\u4e4b\u52e4\uff0c\u66f4\u662f\u65e6\u4e45 \u4e0d\u65ad\uff0c\u90a3\u4e24\u4e2a\u6b66\u6797\u5f02\u4eba\u89c1\u5230\u81ea\u5df1\u7684\u5f1f\u5b50\u5982\u6b64\u7528\u529f\uff0c\u5fc3\u91cc\u81ea\u7136\u4e5f\u662f\u9ad8\u5174\u7684\u3002\u201c\u54ea\u77e5\u5434\u9e23\u4e16\u8bed\u58f0 \u4e00\u987f\uff0c\u751f\u50cf\u662f\u63a7\u5236\u4e0d\u4f4f\u81ea\u5df1\u7684\u611f\u6168\u4f3c\u7684\uff0c\u7adf\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u662f\u6ca7\u6d77\u6851\u7530\uff0c\u4e16\u4e8b\u53d8 \u5e7b\uff0c\u6b63\u5982\u767d\u4e91\u82cd\u72d7\uff0c\u5374\u4e0d\u662f\u4ed6\u4eec\u9884\u6599\u5f97\u5230\u7684\uff0c\u90a3\u5973\u5b69\u65e5\u6e10\u957f\u6210\uff0c\u6b66\u529f\u4e5f\u65e5\u9ad8\uff0c\u5341\u5e74\u4e4b\u540e\uff0c\u5979 \u6b66\u529f\u5927\u6210\uff0c\u5e26\u7740\u6ee1\u8154\u7684\u5174\u594b\uff0c\u53bb\u627e\u5979\u5fc3\u4e2d\u7684\u604b\u4eba\u7684\u65f6\u5019\uff0c\u624d\u53d1\u89c9\u5979\u7684\u604b\u4eba\uff0c\u8fd9\u5341\u5e74\u4e4b\u95f4\uff0c\u4e0d \u4f46\u4e1d\u6beb\u6ca1\u6709\u957f\u5927\uff0c\u800c\u4e14\uff0c\u2026\u2026\u5509\uff01\u4ed6\u7684\u8eab\u8eaf\u7adf\u50cf\u662f\u4e2a\u4e03\u516b\u5c81\u7684\u5e7c\u7ae5\u3002\u201d \u3000\u3000\u88f4\u73cf\u867d\u7136\u65e9\u5df2\u77e5\u9053\u6b64\u4e8b\u7684\u53d1\u751f\uff0c\u5fc5\u7136\u662f\u8fd9\u6837\u7684\u7ed3\u679c\uff0c\u4f46\u6b64\u523b\u4ecd\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5446\uff0c\u60f3\u5230\u4ed6\u4eec \u4e24\u4eba\u5f53\u65f6\u89c1\u9762\u65f6\u7684\u60c5\u5f62\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u77e5\u662f\u611f\u6168\uff0c\u662f\u540c\u60c5\uff0c\u6291\u6216\u662f\u4e00\u79cd\u54ed\u7b11\u4e0d\u5f97\u7684\u611f\u89c9\uff0c\u5fcd\u4e0d\u4f4f \u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u524d\u8f88\uff0c\u5230\u5e95\u662f\u4e3a\u7740\u4ec0\u4e48\uff0c\u624d\u4f1a\u5982\u6b64\u7684\u5462\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u53f9\u9053\uff1a\u201c\u4ed6\u4eec\u5f53\u65f6\u81ea\u730e\u6237\u5bb6\u4e2d\u9003\u8d70\u4e4b\u540e\uff0c\u6d41\u6d6a\u4e86\u4e00\u5e74\uff0c\u8fd9\u4e00\u5e74\u4e4b\u4e2d\uff0c\u4ed6\u4eec\u6240\u906d\u53d7 \u7684\u56f0\u82e6\uff0c\u6211\u4e0d\u7528\u8bf4\uff0c\u88f4\u5144\u60f3\u5fc5\u4e5f\u80fd\u77e5\u9053\u3002\u201d \u3000\u3000\u88f4\u73cf\u9eef\u7136\u989d\u9996\u3002\u5434\u9e23\u4e16\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u56db\u5904\u6d41\u6d6a\uff0c\u751f\u6d3b\u65e0\u7740\uff0c\u90a3\u7537\u5b69\u53ea\u60f3\u81ea\u5df1\u662f\u4e2a\u7537\u7684\uff0c \u5e94\u8be5\u5904\u5904\u4fdd\u62a4\u90a3\u5973\u5b69\uff0c\u4ed6\u5e74\u9f84\u867d\u5c0f\uff0c\u4f46\u529b\u6c14\u5374\u4e0d\u5c0f\uff0c\u4fbf\u5728\u7801\u5934\u3001\u5ba2\u6808\u7b49\u5730\uff0c\u5e2e\u4eba\u5bb6\u642c\u8fd0\u4e9b\u884c \u674e\uff0c\u501f\u4ee5\u6362\u51e0\u4e2a\u94b1\u5403\u996d\u3002\u201d \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff0c\u60f3\u5230\u81ea\u5df1\u5728\u5ba2\u6808\u95e8\u524d\u4e3a\u4eba\u5237\u9a6c\u4e4b\u65f6\uff0c\u4e0d\u7981\u5bf9\u8fd9\u7537\u5b69\uff0c\u66f4\u751f\u51fa\u540c\u60c5\u4e4b\u5fc3\uff0c \u6c89\u541f\u534a\u664c\uff0c\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u7adf\u9047\u4e0d\u7740\u4e00\u4e24\u4e2a\u597d\u5fc3\u7684\u4eba\uff0c\u5c06\u4ed6\u4eec\u6536\u7559\u5417\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u4fbf\u63a5\u9053\uff1a\u201c\u4e16\u4e0a\u597d\u5fc3\u4e4b\u4eba\u5e76\u975e\u5b8c\u5168\u6ca1\u6709\uff0c\u4f46\u8fd9\u7537\u5b69\u751f\u6027\u5014\u5f3a\uff0c\u7edd\u4e0d\u80af\u5411\u4eba\u4e5e\u6c42\uff0c \u66f4\u4e0d\u80af\u53d7\u4eba\u6069\u60e0\uff0c\u90a3\u5973\u5b69\u8981\u5e2e\u4ed6\u5fd9\uff0c\u4ed6\u4e5f\u4e0d\u8bb8\uff0c\u53ea\u4ee5\u81ea\u5df1\u52b3\u529b\u6240\u5f97\uff0c\u6765\u517b\u6d3b\u8fd9\u5973\u5b69\uff0c\u4f46\u8fd9\u6837 \u8d5a\u6765\u7684\u94b1\uff0c\u53c8\u80fd\u6709\u591a\u5c11\uff0c\u6240\u5f97\u7684\u98df\u7269\uff0c\u4e24\u4eba\u90fd\u4e0d\u591f\u5403\uff0c\u8fd9\u7537\u5b69\u4fbf\u5c06\u81ea\u5df1\u7684\u4e00\u4efd\uff0c\u4e5f\u8ba9\u5973\u5b69\u5403 \u4e86\uff0c\u63a8\u8bf4\u81ea\u5df1\u5df2\u7ecf\u5403\u8fc7\uff0c\u5176\u5b9e\u4ed6\u5374\u6697\u4e2d\u675f\u7d27\u8170\u5e26\uff0c\u5509\u2014\u2014\u8fd9\u6837\u7684\u65e5\u5b50\uff0c\u88f4\u73cf\u4f60\u53ef\u2014\u2014\u201d\u4ed6\u8bdd \u672a\u8bf4\u5b8c\uff0c\u88f4\u73cf\u5df2\u81ea\u5782\u9996\u53f9\u9053\uff1a\u201c\u8fd9\u6837\u7684\u65e5\u5b50\uff0c\u6211\u4e5f\u751f\u6d3b\u8fc7\u7684\u3002\u201d \u3000\u3000\u4e24\u4eba\u4ff1\u662f\u66fe\u7ecf\u9965\u5bd2\u56f0\u82e6\u4e4b\u4eba\uff0c\u6b64\u523b\u5404\u4eba\u5fc3\u4e2d\u60f3\u5230\u81ea\u5df1\u751f\u547d\u4e2d\u90a3\u4e00\u6bb5\u6d41\u6d6a\u7684\u65e5\u5b50\uff0c\u4e0d\u7981\u76f8 \u5bf9\u557c\u5618\uff0c\u9ed8\u7136\u826f\u4e45\uff0c\u5434\u9e23\u4e16\u65b9\u53c8\u63a5\u9053\uff1a\u201c\u4ed6\u5e74\u9f84\u8fd8\u4e0d\u5230\u4e5d\u5c81\uff0c\u9aa8\u8fd8\u672a\u957f\u6210\uff0c\u54ea\u91cc\u7981\u5f97\u8d77\u5982\u6b64 \u6467\u6b8b\uff0c\u53d1\u80b2\u81ea\u7136\u8981\u56e0\u4e4b\u53d7\u963b\uff0c\u5230\u540e\u6765\u4ed6\u523b\u82e6\u4e60\u6b66\uff0c\u6240\u4e60\u53c8\u662f\u9634\u67d4\u4e00\u7c7b\u7684\u529f\u592b\uff0c\u518d\u52a0\u4e0a\u5fc3\u60c5\u6c89 \u90c1\uff0c\u601d\u7d22\u5927\u591a\uff0c\u5509\u2014\u2014\u4e5f\u8bb8\u4ed6\u751f\u6765\u4f53\u8d28\u4e4b\u4e2d\uff0c\u4e5f\u6709\u4e9b\u7f3a\u9677\uff0c\u662f\u4ee5\u4ed6\u8eab\u8eaf\u4fbf\u6c38\u8fdc\u65e0\u6cd5\u957f\u5927 \u4e86\u3002\u201d \u3000\u3000\u4ed6\u7a0d\u4e3a\u5598\u606f\uff0c\u53c8\u9053\uff1a\u201c\u4e24\u4eba\u89c1\u9762\u4e4b\u4e0b\uff0c\u5f7c\u6b64\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u90a3\u7537\u5b69\u5fc3\u4e2d\u66f4\u662f\u5927\u751f\u7f9e\u6127\u4e4b \u5fc3\uff0c\u6115\u4e86\u534a\u664c\uff0c\u8f6c\u8eab\u4fbf\u8d70\uff0c\u90a3\u5973\u5b69\u5927\u558a\u4e00\u58f0\uff0c\u8ffd\u4e86\u4e0a\u53bb\uff0c\u5374\u672a\u8ffd\u5230\u3002\u201d \u3000\u3000\u201c\u81ea\u540e\u5979\u4fbf\u53c8\u56db\u5904\u6d41\u6d6a\uff0c\u53bb\u8ffd\u5bfb\u90a3\u7537\u5b69\uff0c\u6d41\u6d6a\u4e4b\u4e2d\uff0c\u5979\u81ea\u7136\u4e0d\u4f1a\u5fd8\u5374\u81ea\u5df1\u7684\u6df1\u4ec7\uff0c\u5929\u7f51 \u6062\u6062\uff0c\u4f46\u758f\u800c\u4e0d\u6f0f\uff0c\u5979\u7ec8\u4e8e\u63a2\u51fa\u4e86\u81ea\u5df1\u7684\u4ec7\u5bb6\u662f\u8c01\uff0c\u4e8e\u662f\u5979\u53ea\u5f97\u6682\u65f6\u653e\u4e0b\u5bfb\u627e\u90a3\u7537\u5b69\u4e4b\u4e8b\uff0c \u800c\u53bb\u590d\u4ec7\u3002\u201d \u3000\u3000\u88f4\u73cf\u53f9\u9053\uff1a\u201c\u4eba\u9053\u6b64\u60c5\u6df1\u5904\uff0c\u4fbf\u662f\u6d77\u67af\u77f3\u70c2\uff0c\u4e5f\u4e0d\u80fd\u5c06\u4e4b\u79fb\u52a8\uff0c\u8fd9\u4f4d\u524d\u8f88\u7528\u60c5\u4e4b\u6df1\uff0c\u5b9e \u662f\u4ee4\u4eba\u53ef\u656c\u5f97\u5f88\u3002\u201d\u4ed6\u81ea\u5df1\u4ea6\u662f\u81f3\u60c5\u81f3\u6027\u4e4b\u4eba\uff0c\u542c\u5230\u8fd9\u79cd\u4f1f\u5927\u7684\u60c5\u611f\uff0c\u4fbf\u4e0d\u7981\u5927\u8d77\u8d5e\u4f69\u4e4b \u5fc3\uff0c\u4fbf\u4e0d\u7981\u53c8\u63d2\u53e3\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5374\u542c\u5434\u9e23\u4e16\u53c8\u9053\uff1a\u201c\u5c31\u5728\u5979\u53bb\u590d\u4ec7\u7684\u65f6\u5019\uff0c\u5374\u4e0d\u60f3\u7adf\u53d1\u73b0\u81ea\u5df1\u7684\u4ec7\u4eba\uff0c\u5df2\u6b7b\u4e86\u4e09\u4e2a\uff0c\u6700 \u540e\u4e00\u4e2a\uff0c\u6b63\u5728\u5f3a\u81ea\u6323\u7740\u547d\uff0c\u800c\u5c06\u4ed6\u4eec\u4e00\u8d77\u5236\u6b7b\u7684\uff0c\u5374\u6b63\u662f\u81ea\u5df1\u5bfb\u627e\u4e0d\u5230\u7684\u604b\u4eba\uff0c\u4e8e\u662f\u5979\u8dd1\u4e0a \u53bb\uff0c\u5c06\u6700\u540e\u4e00\u4e2a\u4ec7\u4eba\u6740\u6b7b\uff0c\u800c\u4e14\u544a\u8bc9\u90a3\u7537\u5b69\u8bf4\u65e0\u8bba\u4ed6\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\uff0c\u5979\u603b\u662f\u7231\u7740\u4ed6\u7684\uff0c\u5e0c\u671b \u548c\u4ed6\u6c38\u751f\u53ae\u5b88\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u7728\u52a8\u4e00\u4e0b\uff0c\u773c\u4e2d\u4f3c\u4e4e\u53c8\u6709\u6cea\u5149\u95ea\u52a8\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u65b9\u81ea\u63a5\u9053\uff1a\u201c\u8fd9\u4efd\u75f4\u60c5\uff0c\u76f4\u53ef\u60ca \u5929\u5730\u800c\u52a8\u9b3c\u795e\uff0c\u90a3\u7537\u5b69\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u611f\u52a8\uff0c\u4e8e\u662f\u8fd9\u4e00\u53cc\u5386\u5c3d\u6ca7\u6851\u7684\u7537\u5973\uff0c\u4fbf\u7ec8\u4e8e\u6210\u4e86\u7737\u5c5e\uff0c\u4ed6 \u4eec\u7684\u5916\u8c8c\u867d\u4e0d\u76f8\u79f0\uff0c\u4f46\u662f\u653e\u773c\u5929\u4e0b\uff0c\u53c8\u6709\u54ea\u4e00\u5bf9\u592b\u5987\u7684\u60c5\u611f\uff0c\u6bd4\u5f97\u4e0a\u4ed6\u4eec\u7684\u575a\u5b9a\u771f\u8bda\u5462\uff0c\u4eba \u7c7b\u7684\u8eaf\u58f3\uff0c\u5728\u4ed6\u4eec\u770b\u6765\uff0c\u662f\u592a\u6e3a\u5c0f\u800c\u4e0d\u8db3\u9053\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\uff0c\u4eba\u4e16\u95f4\u6700\u53ef\u8d35\u7684\u4e1c\u897f\uff0c\u4fbf\u662f \u5f7c\u6b64\u95f4\u771f\u7eaf\u7684\u60c5\u611f\uff0c\u8fd9\u4efd\u60c5\u611f\uff0c\u662f\u4ed6\u4eec\u7528\u81ea\u5df1\u7684\u8840\u6cea\u57f9\u517b\u6210\u7684\uff0c\u4ed6\u4eec\u4fbf\u73cd\u60dc\u8fd9\u4efd\u60c5\u611f\uff0c\u81f3\u6b7b \u4e0d\u6e1d\u3002\u201d \u3000\u3000\u88f4\u73cf\u5446\u5446\u5730\u542c\u7740\u4ed6\u7684\u8bdd\uff0c\u76f4\u5230\u4ed6\u8bdd\u5df2\u8bf4\u5b8c\uff0c\u76ee\u5149\u4ecd\u672a\u77ac\u52a8\u4e00\u4e0b\uff0c\u5446\u5446\u5730\u671b\u7740\u7a97\u5916\uff0c\u7a97\u5916 \u591c\u8272\u5c06\u5c3d\uff0c\u5df2\u6709\u4e00\u4e9b\u7070\u767d\u7684\u66d9\u8272\u4e86\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u53cd\u590d\u601d\u5fd6\u7740\uff1a\u201c\u5916\u8c8c\u867d\u4e0d\u76f8\u79f0\uff0c\u4f46\u653e\u773c\u5929\u4e0b\uff0c\u53c8\u6709\u54ea\u4e00\u5bf9\u592b\u5987\u7684\u60c5\u611f\uff0c\u6bd4\u5f97\u4e0a\u4ed6 \u4eec\u7684\u575a\u5b9a\u771f\u8bda\u2026\u2026\u5509\uff01\u5916\u8c8c\u76f8\u79f0\uff0c\u53c8\u6709\u4f55\u7528\u3002\u201d\u5fc3\u5ff5\u8f6c\u5904\uff0c\u4e0d\u7981\u60f3\u5230\u90a3\u5343\u624b\u4e66\u751f\u4e0e\u51b7\u6708\u4ed9 \u5b50\uff0c\u4ed6\u4eec\u7684\u5916\u8c8c\uff0c\u4e0d\u662f\u6781\u4e3a\u76f8\u79f0\u5417\uff1f \u3000\u3000\u4ed6\u65e9\u5df2\u77e5\u9053\u8fd9\u201c\u91d1\u7ae5\u7389\u5973\u201d\u7684\u7ed3\u5408\uff0c\u5fc5\u5b9a\u662f\u4e00\u6bb5\u6781\u5176\u52a8\u4eba\u7684\u6545\u4e8b\uff0c\u4fbf\u5374\u60f3\u4e0d\u5230\u5176\u4e2d\u7adf\u5305 \u6db5\u7740\u8fd9\u4e48\u591a\u7684\u66f2\u6298\u53d8\u5316\uff0c\u8fd9\u6bb5\u4e8b\u76f4\u5230\u5f88\u4e45\u5f88\u4e45\u4ee5\u540e\uff0c\u4ed6\u6bcf\u4e00\u60f3\u8d77\uff0c\u72b9\u81ea\u4e0d\u7981\u4e3a\u4e4b\u4f4e\u56de\u4e0d\u5df2\u3002 \u3000\u3000\u4ece\u6b64\uff0c\u4ed6\u4e5f\u5f00\u59cb\u77e5\u9053\uff0c\u4e0d\u7ecf\u78e8\u7ec3\u7684\u60c5\u611f\uff0c\u603b\u662f\u8106\u5f31\u7684\uff0c\u60c5\u611f\u7684\u82b1\uff0c\u662f\u8981\u7528\u81ea\u5df1\u771f\u5b9e\u7684\u8840 \u6cea\u683d\u57f9\uff0c\u624d\u4f1a\u7ed3\u679c\u7684\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u53c8\u843d\u5165\u6df1\u601d\u4e2d\uff0c\u4e00\u9762\u53c8\u4e0d\u7981\u601d\u5fd6\uff1a\u201c\u4ed6\u4eec\u6765\u627e\u6211\uff0c\u662f\u4e3a\u7684\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d\u5171\u8d3a\u6c5f \u5357\u7eff\u6797\u76df\u4e3b\u7684\u5927\u4f1a\u4f1a\u671f\u5df2\u4e0d\u8fdc\uff0c\u4f46\u4ed6\u5fc3\u91cc\u60f3\u7740\u7684\uff0c\u5374\u662f\u4e00\u4e9b\u4e8e\u6b64\u65e0\u5173\u7684\u4e8b\uff0c\u201c\u6587\u742a\u4f1a\u4e0d\u4f1a\u771f \u7684\u50cf\u4ed6\u4eec\u6240\u8bf4\uff0c\u4e0d\u51fa\u51e0\u5929\uff0c\u53c8\u4f1a\u6765\u627e\u6211\uff1f\u201d\u8fd9\u4e9b\u4e8b\u5360\u53bb\u4e86\u4ed6\u5fc3\u4e2d\u7684\u5927\u90e8\uff0c\u4f7f\u5f97\u4ed6\u4e5f\u6ca1\u6709\u7a7a\u9699 \u53bb\u60f3\u522b\u7684\u4e86\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u4e0d\u4e45\u5373\u5c06\u5230\u6765\u7684\u76df\u4e3b\u4e4b\u4f1a\uff0c\u5bf9\u4ed6\u8bf4\u6765\uff0c\u8be5\u662f\u5982\u4f55\u91cd\u8981\u7684\u4e8b\u5440\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u516d\u7ae0",content:(0,s.jsx)(s.Fragment,{children:"\u5434\u9e23\u4e16\u4ee5\u4f4e\u6c89\u800c\u7f13\u6162\u7684\u58f0\u97f3\uff0c\u8bf4\u4e86\u8fd9\u6bb5\u66f2\u6298\u800c\u52a8\u4eba\u7684\u6545\u4e8b\uff0c\u4ed6\u660e\u9510\u800c\u777f\u667a\u7684\u76ee\u5149\uff0c\u4fbf\u4e5f \u4f3c\u56e0\u7740\u8fd9\u6bb5\u6545\u4e8b\u800c\u8499\u4e0a\u4e86\u4e00\u5c42\u60b2\u54c0\u7684\u8584\u96fe\u3002 \u3000\u3000\u591c\u8272\uff0c\u66f4\u6df1\u4e86\uff0c\u9ece\u660e\u524d\u7684\u7247\u523b\uff0c\u6c38\u8fdc\u662f\u4e00\u591c\u4e2d\u6700\u9ed1\u6697\u7684\u65f6\u5019\uff0c\u4e5f\u6c38\u8fdc\u662f\u4e00\u591c\u4e2d\u6700\u5bd2\u51b7\u7684 \u65f6\u5019\uff0c\u4ed6\u7ad9\u8d77\u8eab\u6765\uff0c\u6296\u4e86\u6296\u8863\u8882\u4e0a\u7684\u7070\u5c18\uff0c\u50cf\u662f\u60f3\u5c06\u4ed6\u5fc3\u4e2d\u7684\u5fe7\u90c1\u4e5f\u4e00\u8d77\u6296\u843d\u4f3c\u7684\uff0c\u4f46\u662f\u8fd9 \u5c11\u5e74\u5fc3\u4e2d\u7684\u5fe7\u90c1\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f\u5374\u6c38\u8fdc\u90fd\u6ca1\u6709\u4eba\u77e5\u9053\uff01 \u3000\u3000\u5f53\u4eba\u4eec\u6781\u529b\u9690\u85cf\u7740\u81ea\u5df1\u8eab\u4e16\u7684\u65f6\u5019\uff0c\u4e0d\u4e5f\u662f\u975e\u5e38\u75db\u82e6\u7684\u5417\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u53c8\u81ea\u957f\u53f9\u4e00\u58f0\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u8981\u8d76\u5feb\u79bb\u5f00\u8fd9\u623f\u95f4\uff0c\u56e0\u4e3a\u4ed6\u751f\u6015\u81ea\u5df1\u5728\u8fd9\u91cc \u803d\u5f97\u8fc7\u4e45\uff0c\u4f1a\u4e0d\u7531\u81ea\u4e3b\u5730\u5c06\u5fc3\u91cc\u7684\u79d8\u5bc6\u544a\u8bc9\u88f4\u73cf\uff0c\u800c\u4ed6\u6709\u8fd9\u79cd\u51b2\u52a8\uff0c\u6b64\u523b\u5df2\u4e0d\u662f\u7b2c\u4e00\u6b21\u4e86\u3002 \u3000\u3000\u88f4\u73cf\u62ac\u8d77\u5934\uff0c\u671b\u7740\u4ed6\u7684\u8eab\u5f71\uff0c\u4f4e\u4f4e\u95ee\u9053\uff1a\u201c\u4f60\u8981\u8d70\u4e86\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u505c\u4f4f\u811a\u6b65\uff0c\u53ea\u542c\u88f4\u73cf\u957f\u53f9\u7740\u53c8\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e00\u5929\u7684\u65f6\u5149\u6709\u65f6\u5019\u663e \u5f97\u90a3\u4e48\u77ed\uff0c\u6709\u65f6\u5019\u5374\u53c8\u50cf\u662f\u65e0\u6bd4\u7684\u6f2b\u957f\uff0c\u5509\u2014\u2014\u6211\u771f\u5e0c\u671b\u8fd9\u9ed1\u591c\u5feb\u8fc7\u53bb\uff0c\u767d\u5929\u5feb\u4e9b\u6765\uff0c\u7136\u540e \u767d\u5929\u518d\u8d76\u5feb\u8fc7\u53bb\uff0c\u660e\u5929\u7684\u9ed1\u591c\u518d\u5feb\u4e9b\u6765\uff0c\u5509\u2014\u2014\u6211\u771f\u662f\u4e0d\u77e5\u9053\u2018\u7b49\u5f85\u2019\u539f\u6765\u662f\u8fd9\u6837\u4ee4\u4eba\u75db\u82e6 \u7684\u4e8b\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u7f13\u7f13\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u7a81\u5730\u56de\u8eab\u4e00\u7b11\uff0c\u4eb2\u5207\u5730\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u7b49\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u957f\u957f\u4e00\u53f9\uff0c\u65e5\u5149\u8fdc\u8fdc\u6295\u5411\u7a97\u5916\u7684\u65e0\u5c3d\u9ed1\u6697\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u2018\u91d1\u91cd\u7389\u5973\u2019\u8fd9\u4e24 \u4f4d\u8001\u524d\u8f88\u6b64\u6765\u627e\u6211\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u56e0\u6b64\u6211\u5e0c\u671b\u660e\u591c\u7684\u4e09\u66f4\u5feb\u4e9b\u6765\uff0c\u597d\u8ba9\u6211\u80fd\u89e3\u5f00\u8fd9\u95ee \u9898\uff0c\u540c\u65f6\u5434\u9e23\u4e16\u53c8\u4eb2\u5207\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u53ea\u662f\u8fd9\u6b21\u4ed6\u7b11\u5bb9\u4e2d\u5374\u50cf\u662f\u6709\u4e9b\u5947\u602a\u3002\u5f53\u8fd9\u4eb2\u5207\u800c\u5947\u602a\u7684 \u7b11\u5bb9\uff0c\u5728\u4ed6\u76ee\u5149\u4e2d\u53c8\u65cb\u8f6c\u6210\u4e00\u9635\u8f7b\u6de1\u7684\u5fe7\u90c1\u65f6\uff0c\u4ed6\u5374\u4ecd\u7136\u542b\u7740\u7b11\u9053\uff1a\u201c\u540c\u65f6\uff0c\u4f60\u8fd8\u671f\u5f85\u7740\u6a80 \u6587\u742a\u4f1a\u6765\u627e\u4f60\uff0c\u4f60\u77e5\u9053\u5979\u767d\u5929\u65f6\u7edd\u4e0d\u4f1a\u6765\uff0c\u6240\u4ee5\uff0c\u4f60\u5c31\u7b49\u5f85\u665a\u4e0a\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6c89\u91cd\u7684\u9762\u988a\uff0c\u98de\u626c\u8d77\u4e00\u9635\u8f7b\u5feb\u800c\u5e26\u7740\u8d5e\u4f69\u7684\u7b11\u5bb9\uff0c\u50cf\u662f\u5728\u8bf4\uff1a\u201c\u4f60\u4ec0\u4e48\u90fd\u77e5\u9053\u3002\u201d \u3000\u3000\u4f46\u662f\u4ed6\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u4ed6\u53ea\u662f\u9ed8\u8ba4\u4e86\u3002\u5434\u9e23\u4e16\u7f13\u7f13\u8d70\u5230\u4ed6\u8eab\u65c1\uff0c\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u7b11\u7740 \u9053\uff1a\u201c\u7b49\u5f85\u867d\u7136\u4ee4\u4eba\u5fc3\u7126\uff0c\u4f46\u4e5f\u662f\u4ef6\u975e\u5e38\u7f8e\u7684\u4e8b\uff0c\u6ca1\u6709\u7126\u6025\u7684\u7b49\u5f85\uff0c\u600e\u4e48\u4f1a\u6709\u76f8\u89c1\u6709\u5feb \u4e50\u3002\u201d\u7136\u540e\uff0c\u4ed6\u518d\u7f13\u7f13\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u88f4\u73cf\u518d\u6b21\u671b\u5411\u4ed6\u7684\u8eab\u5f71\uff0c\u53ea\u89c9\u4ed6\u8bf4\u7684\u8bdd\u662f\u591a\u4e48\u52a8\u4eba\u800c\u7f8e\u5999\uff0c\u867d\u7136\u6ca1\u6709\u97f5\u811a\uff0c\u4ea6\u4e0d\u5206\u5e73 \u538c\uff0c\u7136\u800c\u5374\u50cf\u8bd7\u53e5\u4e00\u6837\uff0c\u4ee4\u4eba\u5fc3\u52a8\uff01 \u3000\u3000\u4e8e\u662f\u4ed6\u7ec6\u7ec6\u5730\u9886\u609f\u7740\u8fd9\u7b49\u5f85\u7684\u75db\u82e6\uff0c\u5e7b\u60f3\u7740\u76f8\u89c1\u7684\u5feb\u4e50\uff0c\u53ea\u5230\u66d9\u8272\u67d3\u767d\u4e86\u4ed6\u660f\u9ec4\u7684\u7a97 \u7eb8\uff0c\u4ed6\u65b9\u81ea\u5728\u6726\u80e7\u4e2d\u7761\u53bb\u3002 \u3000\u3000\u66ae\u6625\u7684\u9633\u5149\uff0c\u50cf\u5f80\u5e38\u4e00\u6837\uff0c\u4ece\u4e1c\u65b9\u7b14\u76f4\u5730\u7167\u5c04\u8fc7\u6765\uff0c\u5c04\u8fdb\u4e86\u4ed6\u7684\u7a97\u6237\uff0c\u7167\u7740\u4ed6\u4fca\u79c0\u7684\u9762 \u5bb9\uff0c\u540c\u6837\u5730\uff0c\u4e5f\u7167\u8fdb\u4e86\u6a80\u6587\u742a\u7684\u7a97\u6237\uff0c\u7167\u7740\u6a80\u6587\u742a\u90a3\u5982\u82b1\u7684\u5a07\u989c\uff0c\u5979\u6ca1\u6709\u7761\uff0c\u5979\u53ea\u662f\u6084\u7136\u5408 \u4e0a\u773c\u5e18\uff0c\u8f6c\u52a8\u4e00\u4e0b\u5367\u7740\u7684\u59ff\u52bf\uff0c\u907f\u5f00\u8fd9\u523a\u76ee\u7684\u9633\u5149\u3002 \u3000\u3000\u5979\u6ca1\u6709\u7761\uff0c\u662f\u56e0\u4e3a\u5979\u5df2\u5f00\u59cb\u540e\u6094\uff0c\u4e3a\u4ec0\u4e48\u8981\u90a3\u4e48\u5306\u5306\u5730\u79bb\u5f00\u81ea\u5df1\u65e6\u65e6\u76f8\u601d\uff0c\u671d\u4e45\u6614\u5fc6\u7684 \u4eba\uff0c\u4e00\u65f6\u7684\u5a07\u55d4\uff0c\u5374\u6362\u6765\u6c38\u4e45\u7684\u6094\u759a\uff0c\u5979\u6697\u602a\u81ea\u5df1\uff0c\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u5b69\u5b50\u6c14\u3002 \u3000\u3000\u4e8e\u662f\u5979\u5f00\u59cb\u671f\u5f85\u9ed1\u591c\u7684\u6765\u4e34\u3002 \u3000\u3000\u201c\u7b49\u5230\u665a\u4e0a\uff0c\u6211\u518d\u53bb\u627e\u4ed6\uff0c\u4e0d\u77e5\u9053\u4ed6\u4f1a\u4e0d\u4f1a\u539f\u8c05\u6211\u6628\u5929\u665a\u4e0a\u7684\u5b69\u5b50\u6c14\u3002\u201d \u3000\u3000\u5979\u7791\u76ee\u7384\u60f3\u7740\uff0c\u5f53\u591c\u5e55\u518d\u6b21\u7b3c\u7f69\u7740\u5927\u5730\u7684\u65f6\u5019\uff0c\u4ed6\u4f1a\u8dd1\u5230\u6628\u591c\u7684\u5c0f\u6eaa\u65c1\uff0c\u7b49\u5f85\u7740\u5979\uff0c\u5f20 \u5f00\u4ed6\u58ee\u5065\u7684\u81c2\u8180\uff0c\u5c06\u5979\u62e5\u5728\u4ed6\u5bbd\u9614\u7684\u80f8\u819b\u4e0a\uff0c\u544a\u8bc9\u5979\uff0c\u4ed6\u6240\u7231\u7684\u4eba\uff0c\u53ea\u6709\u5979\u4e00\u4e2a\u3002 \u3000\u3000\u8fd9\u4e2a\u767d\u5929\uff0c\u5979\u5e0c\u671b\u80fd\u5728\u8fd9\u79cd\u751c\u871c\u7684\u5e7b\u60f3\u4e2d\u5ea6\u8fc7\uff0c\u4f46\u662f\uff0c\u5f53\u6b66\u6797\u4e2d\u4eba\u77e5\u9053\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e3b \u4eba\u201c\u9f99\u5f62\u516b\u638c\u201d\u7684\u638c\u73e0\u5728\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u5374\u4e0d\u8ba9\u5979\u5b89\u9759\u7684\u65f6\u523b\u6765\u5e7b\u60f3\u4e86\uff0c\u4ed6\u4eec\u7edc\u7ece\u4e0d\u7edd\u5730 \u5230\u8fd9\u91cc\u6765\u62dc\u8bbf\u5979\uff0c\u62dc\u8bbf\u6c5f\u6e56\u4e0a\u6210\u540d\u7684\u9556\u5934\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\uff0c\u4ee5\u53ca\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u4e5f\u4f1a \u5bf9\u90a3\u51b7\u9177\u800c\u5028\u505a\u7684\u4ee4\u5bb6\u5144\u5f1f\u5077\u5077\u671b\u4e0a\u4e24\u773c\uff0c\u5927\u5bb6\u90fd\u5728\u5947\u602a\uff0c\u8fd9\u4e24\u4e2a\u602a\u7269\u600e\u4f1a\u548c\u201c\u98de\u9f99\u9556\u5c40\u201d \u91cc\u7684\u4eba\u5728\u4e00\u8d77\uff0c\u53ea\u662f\u8c01\u4e5f\u6ca1\u6709\u95ee\u51fa\u6765\u800c\u5df2\u3002 \u3000\u3000\u201c\u4eca\u5929\u5df2\u7ecf\u662f\u521d\u4e8c\u4e86\uff0c\u8ddd\u79bb\u4e94\u6708\u7aef\u9633\uff0c\u5df2\u4e0d\u8fc7\u53ea\u6709\u77ed\u77ed\u7684\u4e09\u5929\uff01\u201d \u3000\u3000\u6b66\u6797\u4e2d\u4eba\uff0c\u4e5f\u5728\u7126\u6025\u5730\u7b49\u5f85\u7740\u4e09\u5929\u540e\u5171\u8d3a\u76df\u4e3b\u7684\u5927\u4f1a\u3002 \u3000\u3000\u5348\u540e\uff0c\u4e8c\u5341\u56db\u4e2a\u9ed1\u8863\u52b2\u88c5\u4f69\u5203\u7684\u5f6a\u5f62\u5927\u6c49\uff0c\u9a70\u7740\u5065\u9a6c\uff0c\u4ece\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u6025\u9a70\u5230\u8fd9\u5c71\u57ce\u4e2d \u6765\uff0c\u5230\u5904\u6563\u53d1\u7ea2\u5e95\u91d1\u5b57\u7684\u8bf7\u5e16\uff0c\u6b63\u5f0f\u9080\u8bf7\u6b66\u6797\u540c\u9053\uff0c\u5728\u201c\u4e94\u6708\u7aef\u9633\u201d\u7684\u6b63\u5348\uff0c\u5230\u201c\u6d6a\u83bd\u5c71 \u5e84\u201d\u4e2d\u53bb\u3002 \u3000\u3000\u8fd9\u4efd\u63cf\u91d1\u7ea2\u5e16\uff0c\u662f\u7531\u201c\u795e\u624b\u201d\u6218\u98de\u3001\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\uff0c\u4ee5\u53ca\u201c\u5317\u6597\u4e03\u715e\u201d\u540c\u65f6\u5177\u540d \u7684\uff0c\u5f53\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u63a5\u5230\u8fd9\u4efd\u7ea2\u5e16\u7684\u65f6\u5019\uff0c\u4ed6\u8d6b\u7136\u89c1\u5230\uff0c\u7ea2\u5e16\u9762\u4e0a\u7adf\u5199\u7740\uff1a\u8c28\u5448\uff0c\u98de \u9f99\u9556\u5c40\u201c\u795e\u5200\u9f9a\u3001\u795e\u638c\u67f3\u4e24\u5927\u9556\u5934\u3002\u201d \u3000\u3000\u800c\u53e6\u4e00\u4efd\u7ea2\u5e16\uff0c\u7adf\u4ea6\u5199\u7740\uff1a\u8c28\u5448\u201c\u51b7\u8c37\u4e8c\u8001\u3002\u201d \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u867d\u7136\u72c2\u50b2\u81ea\u5927\uff0c\u81f3\u6b64\u5374\u4e5f\u4e0d\u5f97\u4e0d\u6697\u60ca\u4eba\u5bb6\u6d88\u606f\u7684\u7075\u901a\uff0c\u81ea\u5df1\u5230\u8fd9\u91cc \u6765\u4e0d\u8fc7\u4e00\u65e5\uff0c\u4eba\u5bb6\u4fbf\u5df2\u77e5\u9053\u4e86\u81ea\u5df1\u884c\u8e2a\uff0c\u4ed6\u51dd\u601d\u7740\u638f\u51fa\u4e00\u952d\u94f6\u951e\uff0c\u8d4f\u7ed9\u8fd9\u9001\u5e16\u7684\u5927\u6c49\uff0c\u8fd9\u5927 \u6c49\u65e2\u4e0d\u62dc\u8c22\uff0c\u4ea6\u4e0d\u63a8\u8f9e\uff0c\u53ea\u662f\u5012\u9000\u4e09\u6b65\uff0c\u201c\u5237\u201d\u5730\uff0c\u53cd\u624b\u4e0a\u4e86\u5065\u9a6c\uff0c\u6025\u9a70\u800c\u53bb\uff0c\u53ea\u7559\u4e0b\u9f9a\u6e05 \u6d0b\u624b\u6301\u94f6\u951e\uff0c\u4ecd\u5728\u51fa\u795e\u3002 \u3000\u3000\u81ea\u4ece\u201c\u5343\u624b\u4e66\u751f\u201d\u4ee5\u5185\u5bb6\u91cd\u624b\uff0c\u9707\u65ad\u4e86\u4ed6\u7684\u624b\u638c\u4e4b\u540e\uff0c\u4ed6\u7684\u5fc3\u5883\uff0c\u5df2\u591a\u5c11\u548c\u6614\u65e5\u6709\u4e9b\u6539 \u53d8\u4e86\uff0c\u6b64\u523b\u4ed6\u5949\u4e86\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e4b\u547d\uff0c\u5230\u8fd9\u91cc\u6765\uff0c\u63a2\u6d4b\u6c5f\u5357\u7eff\u6797\u7684\u60c5\u4e8b\uff0c\u4ed6\u5fc3\u91cc\u591a\u5c11\u662f\u6709\u7740 \u4e9b\u6014\u5fe1\u4e0e\u4e0d\u5b89\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u8fd9\u5e76\u975e\u4e00\u4ef6\u8f7b\u6613\u4e4b\u4e8b\uff0c\u867d\u7136\u6709\u540d\u9707\u6b66\u6797\u7684\u51b7\u5bb6\u5144\u5f1f\u505a\u9760\u5c71\uff0c\u4f46\u76f4\u5230\u6b64\u523b\u4e3a \u6b62\uff0c\u4ed6\u4ecd\u7136\u4e0d\u77e5\u9053\u8fd9\u4e24\u4e2a\u602a\u7269\u7a76\u7adf\u662f\u5426\u4f1a\u5728\u5371\u6025\u65f6\u523b\u5e2e\u52a9\u81ea\u5df1\uff0c\u800c\u4ed6\u5374\u5f88\u6e05\u695a\u5730\u77e5\u9053\uff0c\u5230\u6b64 \u6765\u7684\u4eba\uff0c\u90fd\u662f\u7eff\u6797\u8c6a\u5ba2\uff0c\u800c\u7eff\u6797\u8c6a\u5ba2\u662f\u6c38\u8fdc\u548c\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e3a\u654c\u7684\u3002 \u3000\u3000\u5728\u957f\u6c5f\u6e21\u53e3\uff0c\u4ed6\u548c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u9047\u7740\u4e86\u79bb\u5bb6\u4e00\u4e00\u8f7d\u7684\u6a80\u6587\u742a\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\u6a80\u6587\u742a \u600e\u4f1a\u548c\u8fd9\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8d70\u5230\u4e00\u5904\uff0c\u4ed6\u4eec\u53ea\u662f\u8c28\u614e\u5730\u529d\u8fd9\u5a07\u7eb5\u7684\u5c11\u5973\uff0c\u5feb\u4e9b\u56de\u5bb6\uff0c\u4f46\u662f\u6a80\u6587\u742a \u5374\u62d2\u7edd\u4e86\uff0c\u5979\u53cd\u800c\u59a5\u548c\u4ed6\u4eec\u4e00\u8d77\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u53ea\u5f97\u5c06\u5df2\u5bfb\u83b7\u5979\u7684\u6d88\u606f\uff0c\u5feb\u9a6c\u62a5\u5230\u5317\u4eac\uff0c\u800c\u6b64\u523b\uff0c\u4ed6\u53c8\u53d1\u89c9\u4e86\u5979\u7684\u53d8\u5316\uff0c\u5f80\u6614 \u5929\u771f\u65e0\u90aa\u7684\u5c11\u5973\uff0c\u4eca\u65e5\u5374\u50cf\u662f\u6709\u4e86\u5927\u591a\u7684\u5fe7\u90c1\uff0c\u4ed6\u5f00\u59cb\u540e\u6094\uff0c\u4e0d\u8be5\u548c\u5979\u5728\u4e00\u8d77\uff0c\u4f7f\u5f97\u81ea\u5df1\u672c \u5df2\u6781\u4e3a\u6c89\u91cd\u7684\u80a9\u8180\u4e0a\uff0c\u53c8\u591a\u4e86\u4e00\u91cd\u8d1f\u62c5\u3002 \u3000\u3000\u80cc\u540e\u8f7b\u54b3\u4e00\u58f0\uff0c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u7f13\u6b65\u8e31\u4e86\u8fc7\u6765\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u77a5\u89c1\u4e86\u4ed6\u624b\u4e2d\u7684\u7ea2\u5e16\uff0c\u53cc\u7709 \u5fae\u76b1\uff0c\u6c89\u7740\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\uff1f\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u90a3\u8fb9\u9001\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u9f9a\u6e05\u6d0b\u7f13\u7f13\u9894\u7b52\uff0c\u67f3\u8f89\u63a5\u8fc7\u8fd9\u4e24\u5f20\u62dc\u5e16\uff0c\u5306\u5306\u4e00\u77a5\uff0c\u53cc\u7709\u4fbf\u76b1\u5f97\u66f4\u7d27\uff0c\u4fef\u9996\u6c89\u601d\u534a\u664c\uff0c \u5ffd\u5730\u95ee\u9053\uff1a\u201c\u53bb\uff0c\u8fd8\u662f\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u5e72\u54b3\u4e00\u58f0\uff1a\u201c\u81ea\u7136\u8981\u53bb\u7684\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u2018\u795e\u624b\u2019\u6218\u98de\u6b64 \u4e3e\uff0c\u4f3c\u4e4e\u52bf\u5728\u5fc5\u6210\uff0c\u6211\u771f\u4e0d\u77e5\u9053\u4ed6\u4eec\u63a8\u51fa\u6765\u7684\u603b\u74e2\u628a\u5b50\uff0c\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u67f3\u8f89\u7f13\u7f13\u53f9\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u5173\u952e\u6240\u5728\uff0c\u8981\u7d27\u7684\u662f\uff0c\u4ed6\u4eec\u5177\u5e16\u76f8\u9080\uff0c\u4e0d\u77e5\u7a76\u7adf\u662f\u4ec0\u4e48\u610f \u601d\uff0c\u5982\u679c\u4ed6\u4eec\u60f3\u5728\u90a3\u76df\u4e3b\u4e4b\u4f1a\u4e0a\uff0c\u6210\u5fc3\u6298\u8fb1\u6211\u4eec\uff0c\u654c\u4f17\u6211\u5be1\uff0c\u5509\u4e00\u6211\u53ea\u6015\u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u76db \u540d\uff0c\u4f1a\u2014\u2014\u201d\u4ed6\u542b\u84c4\u5730\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u9f9a\u6e05\u6d0b\u4ea6\u81ea\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u53c8\u600e\u80fd\u4e0d \u53bb\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u66fe\u7ecf\u5e76\u80a9\u626c\u5a01\u6c5f\u6e56\uff0c\u5e76\u80a9\u624b\u5203\u7fa4\u5bc7\uff0c\u62a4\u536b\u7740\u201c\u98de\u9f99\u9556\u5c40\u201d\u548c\u98de\u9f99\u9556\u65d7\uff0c\u4e0d\u77e5\u95ef\u8fc7 \u591a\u5c11\u5200\u5c71\u5251\u6797\u7684\u6b66\u6797\u8c6a\u5ba2\uff0c\u6b64\u523b\u9762\u9762\u76f8\u5bf9\uff0c\u5fc3\u91cc\u5404\u5404\u90e8\u6709\u4e9b\u62c5\u5fe7\uff0c\u201c\u98de\u9f99\u9556\u5c40\u201d\u8fd1\u5e74\u6765\u76db\u540d \u867d\u66f4\u76db\uff0c\u4f46\u5176\u5b9e\u624b\u628a\u5b50\u771f\u786c\u7684\u89d2\u8272\uff0c\u5374\u5e76\u4e0d\u591a\uff0c\u5c24\u5176\u662f\u5728\u6c5f\u5357\u9053\u4e0a\uff0c\u6c5f\u5357\u7eff\u6797\u4e2d\u4eba\uff0c\u7740\u771f\u80fd \u56e0\u6b64\u4e00\u4f1a\uff0c\u800c\u8d8b\u4e8e\u56e2\u7ed3\uff0c\u5bf9\u201c\u98de\u9f99\u9556\u5c40\u201d\u8bf4\u6765\uff0c\u5012\u7684\u786e\u662f\u4ef6\u503c\u5f97\u5fe7\u8651\u4e4b\u4e8b\u3002 \u3000\u3000\u5929\u53c8\u9ed1\u4e86\uff0c\u4eac\u53e3\u5357\u90ca\uff0c\u4eac\u9547\u5c71\u5730\u7684\u8fd9\u5c0f\u5c0f\u5c71\u57ce\uff0c\u706f\u706b\u8fdc\u8fdc\u8f83\u5e73\u65e5\u7e41\u76db\u5f97\u591a\uff0c\u7e41\u534e\u9526\u7ea4\u7684 \u4eac\u53e3\u57ce\u91cc\u201c\u98de\u9f99\u9556\u5c40\u201d\u7684\u5206\u5c40\uff0c\u5374\u4f4f\u5728\u8fd9\u5c71\u57ce\u4e2d\u7684\u8fd9\u5bb6\u867d\u6d01\u51c0\uff0c\u4f46\u5374\u7b80\u964b\u7684\u5ba2\u6808\uff0c\u4e00\u662f\u4e3a\u4e86 \u4eac\u53e3\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u7684\u4e09\u4f4d\u9556\u5934\uff0c\u90fd\u5df2\u8fdc\u8d74\u897f\u5ddd\uff0c\u518d\u6765\u5374\u662f\u4e3a\u4e86\u8981\u60f3\u907f\u5f00\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u7684\u8033 \u76ee\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u4eec\u5931\u8d25\u4e86\uff0c\u6c5f\u6e56\u7533\u6709\u6210\u540d\u7684\u4eba\u7269\u5230\u4e86\u67d0\u4e2a\u5730\u65b9\uff0c\u8fd9\u79cd\u6d88\u606f\u6709\u65f6\u4f1a\u6bd4\u761f\u75ab\u4f20\u64ad\u5f97 \u8fd8\u5feb\uff0c\u4f55\u51b5\u4ed6\u4eec\u662f\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u7684\u4eba\u3002 \u3000\u3000\u9ec4\u660f\u65f6\uff0c\u8fd9\u5c0f\u57ce\u4e2d\u4fbf\u5df2\u5feb\u9a6c\u5954\u9a70\uff0c\u51a0\u76d6\u4e91\u96c6\uff0c\u53ea\u662f\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u5927\u591a\u90fd\u662f\u6c5f\u5357\u6b66\u6797\u767d\u9053 \u4e2d\u7684\u8c6a\u6770\uff0c\u4ed6\u4eec\u6b64\u6765\u5e76\u4e0d\u5b8c\u5168\u662f\u4e3a\u4e86\u62dc\u8bbf\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u7684\u9556\u5934\uff0c\u4e3b\u8981\u7684\u5374\u662f\u60f3\u770b\u770b\uff0c\u540d\u9707 \u5929\u4e0b\u7684\u201c\u98de\u9f99\u9556\u5c40\u201d\u5bf9\u8fd9\u6c5f\u5357\u7eff\u6797\u5171\u8d3a\u76df\u4e3b\u7684\u5927\u4f1a\uff0c\u7a76\u7adf\u6709\u4ec0\u4e48\u53cd\u5e94\u3002 \u3000\u3000\u4f46\u662f\u9ec4\u660f\u540e\u65b9\u81ea\u6765\u5230\u6b64\u95f4\u7684\u4eba\uff0c\u51e0\u4e4e\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u770b\u5230\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7684\u638c\u73e0\u7684\u9762\u76ee \u4e86\uff0c\u56e0\u4e3a\u5929\u4e00\u5165\u9ed1\uff0c\u6a80\u6587\u742a\u5c31\u53cd\u95e9\u4e0a\u81ea\u5df1\u7684\u623f\u95e8\uff0c\u8bf4\u662f\uff1a\u201c\u65c5\u9014\u52b3\u987f\uff0c\u8981\u7761\u4e86\uff0c\u62b1\u6b49\u5f97 \u5f88\u3002\u201d \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\uff0c\u548c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u53ea\u5f97\u4e3a\u5979\u5411\u90a3\u4e9b\u4e45\u6155\u201c\u9f99\u5f62\u516b\u638c\u201d\u76db\u540d\uff0c\u4ee5 \u540e\u4e45\u6155\u201c\u9f99\u5973\u201d\u6a80\u6587\u742a\u8273\u540d\uff0c\u800c\u8d76\u6765\u62dc\u8bbf\u7684\u6b66\u6797\u8c6a\u58eb\u9053\u6b49\uff0c\u8981\u77e5\u9053\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5f53\u65f6\u6743 \u503e\u6c5f\u6e56\uff0c\u4ed6\u7684\u638c\u73e0\uff0c\u4fbf\u81ea\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\u6240\u89e6\u76ee\u7684\u4eba\u7269\uff0c\u5979\u867d\u7136\u6ca1\u6709\u5728\u6c5f\u6e56\u4e2d\u95ef\u8361\uff0c\u53ef\u662f\u6c5f\u6e56 \u4e2d\u4eba\u5374\u90fd\u5df2\u77e5\u9053\u5979\u7684\u7f8e\u8273\uff0c\u53c8\u6709\u4e9b\u597d\u4e8b\u4e4b\u4eba\uff0c\u6697\u4e2d\u66ff\u5979\u53d6\u4e86\u7ef0\u53f7\u3002 \u3000\u3000\u201c\u9f99\u5973\uff01\u201d \u3000\u3000\u201c\u55ef\u2026\u2026\u2018\u9f99\u5973\u2019\uff0c\u5012\u7684\u786e\u662f\u4e2a\u54cd\u4eae\u7684\u540d\u5b57\uff01\u201d\u5750\u5728\u6614\u5e74\u5c0f\u6234\u66fe\u7ecf\u9690\u5c45\u8fc7\u7684\u62db\u9690\u53e4\u5bfa\u897f \u53bb\u534a\u91cc\u7684\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e2d\u5927\u5385\u91cc\u7684\u201c\u795e\u624b\u201d\u6218\u98de\uff0c\u4e00\u624b\u6447\u9996\u6298\u6247\uff0c\u4e00\u624b\u634b\u7740\u957f\u987b\uff0c\u542b\u7b11\u5982\u6b64 \u8bf4\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e2b\u5934\u6b66\u529f\u7a76\u7adf\u600e\u6837\uff0c\u5230\u90a3\u5929\uff0c\u5979\u5982\u679c\u4e5f\u6765\uff0c\u8001\u592b\u5012\u8981\u4ed4\u7ec6\u770b\u770b \u5979\u3002\u201d\u4e00\u6447\u624b\u4e2d\u6298\u6247\uff0c\u53c8\u662f\u4e00\u9635\u72c2\u7b11\u3002 \u3000\u3000\u5750\u5728\u4ed6\u8eab\u65c1\u7684\u4e00\u4e2a\u9762\u8272\u60e8\u767d\uff0c\u4f46\u5374\u9762\u5bb9\u6e05\u79c0\uff0c\u8eab\u6750\u7626\u524a\uff0c\u4f46\u5374\u4e00\u8eab\u9526\u8863\u7684\u5c11\u5e74\uff0c\u6b63\u662f \u201c\u5317\u6597\u4e03\u715e\u201d\u4e2d\u95fb\u8baf\u8d76\u6765\u7684\u201c\u4e03\u715e\u201d\u83ab\u661f\uff01 \u3000\u3000\u6b64\u523b\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6614\u5e74\u5c0f\u6234\u98ce\u6d41\u62db\u9690\u53e4\u5bfa\u4e2d\uff0c\u2018\u53cc\u67d1\u6597\u9152\u542c\u9ec4\u9e4f\u2019\uff0c\u4f20\u4e3a\u5343\u53e4\u97f5 \u8bdd\uff0c\u4eca\u65e5\u6211\u5012\u98ce\u6d41\u4e0d\u8f93\u5c0f\u6234\uff0c\u8c6a\u6c14\u5374\u6709\u8fc7\u4e4b\uff0c\u5728\u8fd9\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u4e2d\uff0c\u2018\u53ea\u9e21\u6597\u9152\u8bba\u82f1\u96c4\u2019\uff0c \u54c8\u54c8\u2014\u2014\u60f3\u6765\u4e5f\u53ef\u6210\u4e3a\u6b66\u6797\u4f73\u8bdd\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u8f7b\u7ec6\u5fae\u5f31\uff0c\u6709\u5982\u5973\u5b50\uff0c\u795e\u6c14\u6d3b\u73b0\uff0c\u4e5f\u6709\u4e9b\u5973\u5b50\u4e4b\u8c61\uff0c\u4e0d\u8ba4\u5f97\u4ed6\u7684\u4eba\uff0c\u6709\u8c01 \u4f1a\u77e5\u9053\u6b64\u4eba\u4fbf\u662f\u201c\u5317\u6597\u4e03\u715e\u201d\u4e2d\u6700\u72e0\u3001\u6700\u8fa3\u3001\u6b66\u529f\u4ea6\u6700\u9ad8\uff0c\u58f0\u540d\u4ea6\u6700\u54cd\u7684\u201c\u4e03\u715e\u201d\u83ab\u661f\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6380\u987b\u7b11\u9053\uff1a\u201c\u662f\u6781\uff0c\u662f\u6781\uff0c\u98ce\u6d41\u4e0d\u8f93\u5c0f\u6234\uff0c\u54c8\u54c8\u2014\u2014\u90a3\u2018\u9f99\u5973\u2019\u6a80\u6587\u742a\u82e5 \u662f\u89c1\u4e86\u83ab\u5144\uff0c\u53ea\u6015\u2026\u2026\u54c8\u54c8\uff0c\u53ea\u6015\u83ab\u5144\u6b64\u540e\u6539\u4e2a\u540d\u5b57\u53eb\u505a\u2018\u9f99\u5a7f\u2019\u4e86\u3002\u201d \u3000\u3000\u5927\u5385\u4e2d\u6d4e\u6d4e\u7fa4\u8c6a\uff0c\u7acb\u523b\u90fd\u4e5f\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\uff0c\u5927\u7b11\u58f0\u4e2d\uff0c\u53ea\u6709\u5750\u5728\u4e00\u8fb9\u7684\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434 \u9e23\u4e16\uff0c\u9762\u8272\u4f3c\u4e4e\u4e00\u53d8\uff0c\u4f3c\u6b32\u957f\u8eab\u800c\u8d77\uff0c\u4f46\u76ee\u5149\u4e00\u8f6c\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u53ea\u542c\u201c\u795e \u624b\u201d\u6218\u98de\u53c8\u81ea\u63a5\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u90a3\u4f4d\u2018\u91d1\u9e21\u2019\u4e0d\u66fe\u524d\u6765\uff0c\u4e0d\u7136\u6211\u4eec\u684c\u4e0a\u7684\u8fd9\u53ea\u9e21\uff0c\u518d\u52a0\u4e0a\u2026\u2026 \u54c8\u54c8\uff0c\u90a3\u4e0d\u5c31\u6210\u4e3a\u2018\u53cc\u9e21\u6597\u9152\u8bba\u82f1\u96c4\u2019\u4e86\u5417\uff1f\u201d \u3000\u3000\u5385\u4e2d\u53c8\u54cd\u8d77\u4e00\u9635\u66f4\u6d2a\u4eae\u7684\u7b11\u58f0\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u4e5f\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u7b11\u58f0\uff0c\u540e\u56ed\u4e2d\u7684\u88f4\u73cf\u5374\u6ca1\u6709\u542c\u5230\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e2d\u6b64\u523b\u5df2\u662f\u98ce\u4e91\u805a\u96c6\uff0c\u4ece\u56db\u9762\u516b\u65b9\u8d76\u6765\u7684\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u81ea\u6043\u8eab\u4efd\u80fd\u591f \u53ca\u5f97\u4e0a\u7684\uff0c\u4fbf\u90fd\u5728\u4f1a\u671f\u4e4b\u524d\u5c31\u90fd\u5230\u4e86\u8fd9\u91cc\u6765\uff0c\u201c\u5317\u6597\u4e03\u715e\u201d\u4e2d\uff0c\u9664\u4e86\u201c\u4e09\u715e\u201d\u83ab\u897f\u4e0d\u77e5\u4e0b\u843d \u5916\uff0c\u5176\u4f59\u7684\u516d\u4f4d\uff0c\u5df2\u6765\u4e86\u56db\u4f4d\uff0c\u201c\u5927\u715e\u201d\u83ab\u5357\uff0c\u201c\u4e94\u715e\u201d\u83ab\u5317\uff0c\u90a3\u65e5\u88ab\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u83ab\u540d\u5176 \u5999\u5730\u7a77\u8ffd\u4e86\u4e00\u987f\uff0c\u5e78\u597d\u540e\u6765\u7a81\u7136\u6709\u4eba\u5728\u4e2d\u9014\u622a\u8d70\u827e\u9752\uff0c\u4ed6\u4eec\u65b9\u624d\u5e78\u5154\u4e8e\u96be\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u4eec\u4e5f\u90fd\u5230\u4e86\u8fd9\u91cc\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4e0d\u77e5\u591a\u5c11\u88f4\u73cf\u4e0d\u8ba4\u5f97\u7684\u8c6a\u5ba2\u82f1\u96c4\uff0c\u4ed6\u4e5f\u77e5\u9053\uff0c\u8fd9\u4e9b\u4eba\u5230\u8fd9\u91cc\u6765\uff0c\u90fd\u662f\u4e3a \u4e86\u81ea\u5df1\u3002 \u3000\u3000\u201c\u4f46\u662f\uff0c\u6211\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u5509\u2014\u2014\u201d\u4ed6\u60b2\u54c0\u5730\u4f4e\u53f9\u4e00\u58f0\uff0c\u671b\u7740\u548c\u6628\u591c\u4e00\u6837\u7684\u706f\u5149\uff0c\u4f4e \u8bed\u7740\u9053\uff1a\u4ed6\u5fc3\u4e2d\u53ea\u5e0c\u671b\u4e09\u66f4\u5feb\u4e9b\u5230\u6765\uff0c\u53ea\u5e0c\u671b\u80fd\u5728\u4e09\u66f4\u65f6\u5206\uff0c\u89c1\u5230\u201c\u91d1\u7ae5\u7389\u5973\u201d\u4e24\u4f4d\u8001\u524d \u8f88\uff0c\u66f4\u5e0c\u671b\u80fd\u5728\u6df1\u591c\u4e2d\u89c1\u7740\u6a80\u6587\u742a\u3002 \u3000\u3000\u8fd9\u671f\u95f4\uff0c\u4ed6\u4ecd\u7136\u53ea\u6709\u7126\u6025\u5730\u7b49\u5f85\u7740\uff0c\u6697\u4e2d\u4f4e\u53f9\u7740\u2014\u2014\u800c\u4ed6\u53f9\u606f\u7684\u58f0\u97f3\uff0c\u5c71\u57ce\u4e2d\u5ba2\u6808\u91cc\u7684 \u6a80\u6587\u742a\u81ea\u7136\u4e5f\u65e0\u6cd5\u542c\u5230\u3002 \u3000\u3000\u5979\u53ea\u80fd\u542c\u5230\u5c4b\u5916\u7684\u54d7\u7b11\u58f0\uff0c\u5979\u77e5\u9053\u81ea\u5df1\u5c4b\u5916\u7684\u4e00\u95f4\u5ba2\u5385\u91cc\uff0c\u6b64\u523b\u6b63\u9ad8\u5f20\u7b75\u5e2d\uff0c\u5927\u4f1a\u7fa4 \u96c4\u3002 \u3000\u3000\u54d7\u7b11\u58f0\u4e2d\uff0c\u5979\u4eff\u4f5b\u542c\u5230\u4ed6\u4eec\u8bf4\u8bdd\u7684\u5185\u5bb9\uff0c\u662f\u5728\u8ba8\u8bba\u90a3\u4f4d\u5373\u5c06\u8363\u81ba\u201c\u6c5f\u5357\u603b\u74e2\u628a\u5b50\u201d\u7684 \u4eba\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u6837\u7684\u4e00\u4f4d\u4eba\u7269\u3002 \u3000\u3000\u6709\u4eba\u8bf4\uff1a\u201c\u542c\u8bf4\u6b64\u4eba\u662f\u6606\u4ed1\u540d\u5bbf\u94c1\u68a7\u6850\u7684\u9ad8\u8db3\uff0c\u4e00\u8eab\u6606\u4ed1\u5251\u6cd5\uff0c\u5df2\u5c3d\u5f97\u53ca\u5e08\u771f\u4f20\uff0c\u5c24\u5176 \u5bf9\u4e8e\u2018\u8f7b\u529f\u2019\u4e00\u9053\uff0c\u66f4\u6709\u7279\u522b\u7684\u529f\u592b\u3002\u201d \u3000\u3000\u53c8\u6709\u4eba\u8bf4\uff1a\u201c\u5c0f\u5f1f\u5012\u542c\u5f97\u7684\u6709\u4e9b\u4e0d\u540c\uff0c\u5144\u53f0\u4f60\u53ef\u77e5\u9053\uff0c\u6570\u5341\u5e74\u524d\uff0c\u2018\u5f62\u610f\u95e8\u2019\u66fe\u4e00\u5ea6\u4e2d \u5174\uff0c\u800c\u4f20\u5f97\uff0c\u5f62\u610f\u95e8\uff0c\u91cd\u632f\u58f0\u5a01\u7684\uff0c\u5c31\u662f\u90a3\u4f4d\u602a\u6770\u3001\u5982\u610f\u638c\u2018\u91d1\u516b\u6b65\uff0c\u540e\u6765\u4ed6\u8001\u4eba\u5bb6\u867d\u7136\u56e0 \u4e3a\u95e8\u4eba\u4e0d\u8096\uff0c\u800c\u4e0d\u518d\u8fc7\u95ee\u2019\u5f62\u610f\u95e8\u2018\u4e2d\u7684\u4e8b\uff0c\u5176\u5b9e\u5374\u5728\u6697\u4e2d\u7269\u8272\u4f20\u4eba\uff0c\u800c\u8fd9\u4f4d\u4e3b\u513f\uff0c\u542c\u8bf4\u5c31 \u662f\u8fd9\u2019\u5982\u610f\u638c\u2018\u7684\u5f1f\u5b50\u3002\u201d\u8fd9\u8bdd\u7acb\u523b\u5f15\u8d77\u4e00\u9635\u60ca\u53f9\u58f0\uff0c\u4f46\u968f\u5373\u6709\u4eba\u53cd\u9a73\u7740\u8bf4\uff1a\u201c\u4e0d\u5bf9\uff0c\u4f60\u4eec \u90fd\u9519\u4e86\u3002\u201d\u4ed6\u505c\u4e86\u505c\uff0c\u5356\u4e86\u5356\u5173\u5b50\uff0c\u65b9\u81ea\u63a5\u7740\u9053\uff1a\u201c\u4f60\u4eec\u5927\u6982\u90fd\u77e5\u9053\uff0c\u7ea6\u6478\u5341\u5e74\u524d\uff0c\u6b66\u6797\u4e2d \u51fa\u4e86\u4e2a\u795e\u79d8\u96be\u6d4b\u7684\u8499\u9762\u4eba\uff0c\u628a\u6b66\u6797\u4e2d\u5341\u591a\u5bb6\u6210\u540d\u7684\u9556\u5c40\uff0c\u5168\u90e8\u6574\u6bc1\u4e86\uff0c\u8fde\u6b27\u9633\u5e73\u4e4b\u8001\u9556\u5934\uff0c \u90fd\u4e27\u4e86\u547d\uff0c\u563f\uff01\u8fd9\u4f4d\u4e3b\u513f\uff0c\u5c31\u662f\u8fd9\u8499\u9762\u4eba\u7684\u513f\u5b50\uff0c\u4ed6\u8fd9\u6b21\u51fa\u6765\uff0c\u662f\u4e3a\u4ed6\u8001\u4eba\u62a5\u4ec7\u6765\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u7acb\u523b\u53c8\u54cd\u8d77\u4e00\u9635\u66f4\u5927\u7684\u60ca\u53f9\uff0c\u8eab\u5728\u9556\u5c40\u4e2d\u7684\u4eba\uff0c\u66f4\u662f\u6101\u5bb9\u6ee1\u9762\uff0c\u53ea\u6709\u8eb2\u5728\u623f\u4e2d\u7684 \u6a80\u6587\u742a\uff0c\u5fc3\u91cc\u5374\u6709\u4e9b\u597d\u7b11\uff0c\u5979\u4e0d\u77e5\u9053\u5f53\u9f9a\u6e05\u6d0b\u548c\u67f3\u8f89\u53d1\u73b0\u8fd9\u4f4d\u201c\u4e3b\u513f\u201d\u5c31\u662f\u4ed6\u4eec\u7d20\u6765\u770b\u4e0d\u8d77 \u7684\u88f4\u73cf\u65f6\u5019\uff0c\u4ed6\u4eec\u8138\u4e0a\u4f1a\u6709\u600e\u6837\u4e00\u79cd\u8868\u60c5\u3002 \u3000\u3000\u5979\u591a\u4e48\u6e34\u671b\u80fd\u770b\u5230\u8fd9\u79cd\u8868\u60c5\uff0c\u5979\u5fc3\u4e2d\u7684\u70ed\u8840\uff0c\u4e5f\u4f3c\u4e4e\u8981\u6cb8\u817e\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4f46\u662f\uff0c\u6ca1\u6709\u591a\u4e45\uff0c\u5979\u98de\u626c\u8d77\u7684\u5fc3\uff0c\u53c8\u88ab\u4e00\u5c42\u6d53\u539a\u7684\u5fe7\u90c1\u7b3c\u7f69\u3002 \u3000\u3000\u201c\u4ed6\u4eca\u591c\u89c1\u7740\u6211\uff0c\u4f1a\u4e0d\u4f1a\u8fd8\u5728\u602a\u6211\u6628\u5929\u665a\u4e0a\u7684\u5b69\u5b50\u6c14\uff1f\u201d \u3000\u3000\u53c8\u5fd6\u9053\uff1a\u201c\u5047\u5982\u4ed6\u4eca\u5929\u4e0d\u5728\u5916\u9762\u7b49\u6211\uff0c\u90a3\u53eb\u6211\u600e\u4e48\u6837\u53bb\u627e\u4ed6\u5462\uff1f\u6211\u53c8\u4e0d\u77e5\u9053\u4ed6\u7a76\u7adf\u662f\u4f4f \u5728\u54ea\u95f4\u623f\u5b50\u91cc\u3002\u201d \u3000\u3000\u5979\u90a3\u4e00\u53cc\u6709\u5982\u6625\u6c34\u822c\u7684\u9edb\u7709\uff0c\u4fbf\u7d27\u7d27\u76b1\u5230\u4e00\u5904\uff0c\u60c5\u6f6e\uff0c\u53c8\u5f00\u59cb\u7d0a\u4e71\u8d77\u6765\uff0c\u5979\u7ad9\u8d77\u6765\u8d70\u52a8 \u4e00\u4e0b\uff0c\u5385\u4e2d\u867d\u7136\u54d7\u7b11\u5982\u6545\uff0c\u4f46\u5979\u9694\u58c1\u7684\u623f\u95f4\u91cc\uff0c\u5374\u9759\u5f97\u8fde\u534a\u70b9\u58f0\u97f3\u90e8\u6ca1\u6709\uff0c\u5979\u4e0d\u77e5\u9053\u5979\u7684\u4e24 \u4f4d\u201c\u51b7\u53d4\u53d4\u201d\uff0c\u6b64\u523b\u5728\u505a\u4ec0\u4e48\uff0c\u5979\u53ea\u662f\u6697\u4e2d\u611f\u6fc0\uff0c\u8fd9\u4e24\u4f4d\u6027\u60c5\u51b7\u50fb\u7684\u602a\u4eba\uff0c\u7adf\u4e3a\u5979\u5fcd\u53d7\u4e86\u8fd9 \u79cd\u8ba8\u538c\u7684\u54d7\u7b11\u58f0\u3002 \u3000\u3000\u591c\u8272\u2014\u2014 \u3000\u3000\u5c31\u5728\u4eba\u4eec\u7684\u7b49\u5f85\u4e4b\u4e2d\uff0c\u4e00\u5206\u4e00\u5bf8\u5730\u52a0\u91cd\u4e86\u3002 \u3000\u3000\u5927\u5730\uff0c\u4e5f\u5c31\u53d8\u5f97\u66f4\u52a0\u9ed1\u6697\u3002 \u3000\u3000\u201c\u7b03\uff01\u7b03\uff01\u201d \u3000\u3000\u201c\u5440\uff01\u6b64\u523b\u5df2\u7ecf\u4e24\u66f4\u4e86\uff01\u201d \u3000\u3000\u88f4\u73cf\u7d27\u4e86\u7d27\u81ea\u5df1\u8863\u88f3\u6084\u6084\u4ece\u540e\u9662\u4e2d\u8d70\u4e86\u51fa\u6765\uff0c\u4ed6\u6781\u529b\u4e0d\u4f7f\u81ea\u5df1\u7684\u8eab\u5f62\u884c\u52a8\u65f6\uff0c\u5e26\u51fa\u4efb\u4f55 \u58f0\u97f3\u3002 \u3000\u3000\u201c\u554a\uff01\u6b64\u523b\u5df2\u7ecf\u4e24\u66f4\u4e86\uff01\u201d \u3000\u3000\u6a80\u6587\u742a\u4ea6\u5728\u6697\u4e2d\u4f4e\u8bed\uff0c\u5979\u53c8\u7ad9\u8d77\u6765\uff0c\u7d27\u4e86\u7d27\u81ea\u5df1\u7684\u8863\u88f3\uff1a\u201c\u6211\u8be5\u53bb\u4e86\u3002\u201d\u7a7f\u4e0a\u8584\u5e95\u7684\u86ee \u9774\uff0c\u5728\u8170\u95f4\u7cfb\u4e0a\u4e00\u6761\u6c34\u8272\u7684\u7ef8\u5e26\uff0c\u518d\u7528\u53e6\u4e00\u6761\u8f83\u77ed\u7684\u7ef8\u5e26\uff0c\u5c06\u6ee1\u5934\u7684\u79c0\u53d1\u8f7b\u8f7b\u67ec\u4f4f\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u63a8\u5f00\u7a97\u5b50\uff0c\u7a97\u5916\u7e41\u661f\u6ee1\u5929\uff0c\u6625\u610f\u6b63\u6d53\uff0c\u4e00\u9635\u98ce\u5439\u6765\uff0c\u5979\u6014\u4e86\u6014\uff0c\u7a81\u5730\u53c8\u6697\u4e2d\u601d \u5fd6\uff1a\u201c\u5047\u5982\u6211\u53bb\u4e86\uff0c\u4ed6\u4e0d\u7406\u6211\uff0c\u90a3\u4e48\u6211\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5979\u7acb\u523b\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u7aef\u8d77\u7a97\u524d\u684c\u4e0a\u7684\u51b7\u8336\uff0c\u559d\u4e86\u53e3\uff1a\u201c\u4ed6\u4e0d\u4f1a\u4e0d\u7406\u6211\u5427\uff1f\u4ed6\u5bf9\u6211\u90a3\u4e48 \u597d\uff01\u201d \u3000\u3000\u5979\u5fae\u7b11\u4e86\uff0c\u751c\u751c\u7684\u7b11\u5bb9\uff0c\u4f7f\u5f97\u660e\u5a9a\u7684\u6625\u591c\uff0c\u66f4\u5e73\u6dfb\u4e86\u51e0\u5206\u6625\u610f\uff0c\u5979\u60f3\u8d77\u4ed6\u5bf9\u81ea\u5df1\u7684\u597d \u5904\uff0c\u4f46\u662f\u2014\u2014\u5979\u7a81\u5730\u91cd\u91cd\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff1a\u201c\u4ed6\u5bf9\u6211\u6709\u4ec0\u4e48\u597d\uff1f\u4ed6\u8d70\u7684\u65f6\u5019\uff0c\u8fde\u544a\u8bc9\u90fd\u4e0d\u544a\u8bc9 \u6211\u4e00\u58f0\uff0c\u6211\u5403\u5c3d\u4e86\u5343\u8f9b\u4e07\u82e6\uff0c\u624d\u627e\u5230\u4ed6\uff0c\u53ef\u662f\u4ed6\u5374\u53ea\u95ee\u6211\u2018\u73cd\u73cd\u5462\uff1f\u2019\u201d\u201c\u73cd\u73cd\u5462\uff1f\u201d\u5979\u91cd \u590d\u5730\u4f4e\u8bed\u7740\uff0c\u6124\u7136\u505a\u4e86\u4e2a\u9b3c\u8138\uff0c\u6124\u7136\u62c9\u4e0b\u5934\u4e0a\u7684\u4e1d\u5e26\uff0c\u201c\u73cd\u73cd\u5462\uff1f\u9b3c\u624d\u77e5\u9053\uff01\u201d\u5657\u5730\uff0c\u53c8\u5750 \u5230\u6905\u5b50\u4e0a\uff0c\u5c06\u811a\u4e0a\u7684\u4e24\u53ea\u978b\u5b50\u90fd\u8131\u4e86\u4e0b\u6765\uff0c\u624b\u638c\u4e00\u6325\uff0c\u4e24\u53ea\u6c34\u8272\u7684\u7ea4\u82b1\u7684\u5c0f\u86ee\u9774\uff0c\u4e00\u5de6\u4e00 \u53f3\uff0c\u8fdc\u8fdc\u5730\u843d\u5230\u5c4b\u5b50\u7684\u89d2\u843d\u91cc\uff0c\u53d1\u51fa\u201c\u7830\uff0c\u7830\u201d\u4e24\u58f0\u8f7b\u54cd\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u5979\u90fd\u6ca1\u6709\u51fa\u53bb\uff0c\u5979\u751a\u81f3\u6ca1\u6709\u79bb\u5f00\u8fc7\u8fd9\u623f\u95f4\u4e00\u6b65\uff0c\u56e0\u4e3a\u5979\u6574\u591c\u90e8\u5728\u77db\u76fe\u4e0e\u75db\u82e6 \u4e2d\uff0c\u5979\u7684\u5fc3\uff0c\u51e0\u4e4e\u5df2\u88ab\u6495\u6210\u4e24\u534a\uff1a\u201c\u53bb\uff0c\u4ed6\u4f1a\u7b49\u4f60\u7684\uff0c\u4ed6\u4f1a\u539f\u8c05\u4f60\u7684\u4e00\u5207\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u53bb\uff0c\u4f60\u6709\u4ec0\u4e48\u8981\u4ed6\u539f\u8c05\u7684\uff0c\u4f60\u4e3a\u4ed6\u53d7\u4e86\u90a3\u4e48\u591a\u82e6\uff0c\u800c\u4ed6\u5374\u5728\u7b2c\u4e00\u6b21\u89c1\u5230\u4f60\u7684\u65f6 \u5019\u5c31\u95ee\u8d77\u522b\u4eba\u3002\u201d \u3000\u3000\u5929\u53c8\u4eae\u4e86\u3002 \u3000\u3000\u4e24\u591c\u672a\u66fe\u5b89\u7720\u7684\u5979\uff0c\u50cf\u662f\u4e00\u4e2a\u9152\u540e\u521d\u9192\u7684\u9189\u6c49\u4f3c\u7684\uff0c\u5468\u8eab\u90fd\u90a3\u4e48\u75b2\u5026\uff0c\u90a3\u4e48\u4e4f\u529b\uff0c\u5012\u5367 \u5728\u5e8a\u4e0a\uff0c\u5979\u751a\u81f3\u8fde\u6307\u5c16\u90fd\u4e0d\u613f\u52a8\u5f39\u4e00\u4e0b\u3002 \u3000\u3000\u5348\u81b3\u7684\u65f6\u5019\uff0c\u5979\u65b9\u81ea\u6709\u4e9b\u6726\u80e7\u7684\u7761\u610f\uff0c\u5ffd\u7136\u542c\u5230\u4e00\u4e2a\u6e29\u67d4\u7684\u58f0\u97f3\u95ee\u5979\uff1a\u201c\u742a\u513f\uff0c\u4f60\u53ef\u662f \u75c5\u4e86\u3002\u201d \u3000\u3000\u7741\u5f00\u773c\uff0c\u5979\u770b\u5230\u4e24\u4e2a\u9880\u957f\u67af\u7626\u7684\u4eba\u5f71\uff0c\u5e76\u80a9\u7ad9\u5728\u5979\u5e8a\u524d\uff0c\u5979\u5fcd\u4e0d\u4f4f\u8981\u54ed\uff0c\u7ec8\u4e8e\uff0c\u6709\u4e24\u7c92 \u6676\u83b9\u7684\u6cea\u73e0\uff0c\u5077\u5077\u81ea\u773c\u7736\u6ed1\u4e0b\u3002 \u3000\u3000\u51b7\u67af\u6728\u53cc\u7709\u5fae\u76b1\uff0c\u4ed6\u867d\u4e0d\u4e86\u89e3\u5c11\u5927\u7684\u5fc3\u60c5\uff0c\u5374\u4e5f\u77e5\u9053\u5979\u5e76\u6ca1\u6709\u771f\u75c5\uff0c\u53ea\u662f\u201c\u5fc3\u75c5\u201d\u800c \u5df2\uff0c\u4ed6\u4fa7\u987e\u51b7\u5bd2\u7af9\u4e00\u773c\uff0c\u4e24\u4eba\u4ff1\u90fd\u77e5\u9053\uff0c\u5979\u662f\u4e3a\u4ec0\u4e48\u6d41\u6cea\u7684\uff0c\u53ea\u662f\u8fd9\u4e24\u4eba\u4e00\u751f\u65e0\u60c5\uff0c\u8c01\u4e5f\u4e0d \u77e5\u9053\u8be5\u600e\u6837\u5bf9\u4e00\u4e2a\u54c0\u4f24\u7740\u7684\u5c11\u5973\uff0c\u8bf4\u53e5\u529d\u89e3\u5b89\u6170\u7684\u8bdd\u3002 \u3000\u3000\u6a80\u6587\u742a\u6084\u7136\u5408\u4e0a\u773c\u5e18\uff0c\u5979\u60f3\u5c06\u773c\u7736\u4e2d\u6240\u6709\u7684\u6cea\u6c34\uff0c\u90fd\u9690\u85cf\u5728\u5408\u8d77\u7684\u773c\u5e18\u91cc\uff0c\u4f46\u662f\uff0c\u6cea\u6c34 \u5374\u53c8\u90fd\u4e0d\u542c\u8bdd\u5730\u6ed1\u843d\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5979\u53ea\u5f97\u60b2\u621a\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u75c5\uff0c\u51b7\u5927\u53d4\uff0c\u4e8c\u53d4\uff0c\u6211\u2026\u2026\u201d\u5979\u8bdd\u72b9\u672a \u4e86\uff0c\u8170\u7554\u7a81\u5730\u5fae\u5fae\u4e00\u9ebb\uff0c\u9ed1\u751c\u7684\u7761\u610f\u7acb\u523b\u4ece\u8fd9\u5fae\u5fae\u4e00\u9ebb\u7684\u5730\u65b9\uff0c\u5f25\u5e03\u5979\u5168\u8eab\u3002 \u3000\u3000\u5979\u7761\u7740\u4e86\u3002 \u3000\u3000\u7ad9\u5728\u5979\u5e8a\u524d\u7684\u67af\u6728\u5bd2\u7af9\u4ea6\u81ea\u540c\u58f0\u53f9\u606f\u4e00\u58f0\uff0c\u6084\u7136\u5e26\u4e0a\u623f\u95e8\uff0c\u8d70\u4e86\u51fa\u6765\uff0c\u8fce\u9762\u8d70\u6765\u5411\u4ed6\u4eec \u542b\u7b11\u4e3a\u793c\u7684\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u4ed6\u4eec\u5374\u8fde\u773c\u76ae\u90fd\u6ca1\u6709\u62ac\u8d77\u534a\u5206\uff0c\u8ff3\u81ea\u8d70\u8fdb\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u201c\u7830\u201d \u5730\u5173\u8d77\u623f\u95e8\uff0c\u623f\u95e8\u5916\u72b9\u81ea\u7ad9\u7740\u6ee1\u9762\u5e72\u7b11\u7684\u67f3\u8f89\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u867d\u7136\u5fc3\u4e2d\u4e0d\u5fff\uff0c\u5374\u4e5f\u65e0\u53ef\u5948\u4f55\uff0c\u671b\u7740\u5173\u8d77\u7684\u623f\u95e8\u6697\u9a82\u4e86\u4e00\u58f0\uff0c\u60bb\u60bb\u8d70\u4e86\u5f00\u53bb\uff0c\u65b9 \u81ea\u8d70\u5230\u5e97\u95e8\uff0c\u5ffd\u5730\u513f\u9a91\u5065\u9a6c\u6025\u9a70\u800c\u672a\uff0c\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u50cf\u9e21\u5b50\u4f3c\u7684\u8dc3\u4e0b\u4e86\u9a6c\u80cc\uff0c\u67f3\u8f89\u5b9a\u775b\u4e00 \u770b\uff0c\u4e0d\u7531\u5931\u671b\u9053\uff1a\u201c\u539f\u6765\u662f\u2018\u4e1c\u65b9\u4e94\u4fa0\u2019\u6765\u4e86\uff0c\u600e\u5730\u4e5f\u4e0d\u901a\u77e5\u5c0f\u5f1f\u4eec\u4e00\u58f0\uff0c\u4e5f\u8ba9\u5c0f\u5f1f\u80fd\u53ca\u65e9 \u8fdc\u8fce\u3002\u201d\u62a2\u6b65\u8d70\u5230\u95e8\u53e3\uff0c\u4e00\u63d6\u5230\u5730\uff0c\u8fde\u58f0\u53c8\u9053\uff1a\u201c\u4e0d\u66fe\u8fdc\u8fce\uff0c\u6055\u7f6a\u6055\u7f6a\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5065\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u5df2\u5168\u90fd\u8dc3\u4e0b\uff0c\u7adf\u662f\u4e94\u4e2a\u9e22\u80a9\u8702\u8170\uff0c\u9762\u76ee\u82f1\u633a\uff0c\u4ff1\u90fd\u7a7f\u7740\u6d45\u7d2b \u7f57\u886b\u7684\u534e\u670d\u5c11\u5e74\u3002 \u3000\u3000\u6628\u591c\u6b47\u606f\u5728\u8fd9\u5ba2\u6808\u4e2d\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u6709\u7684\u5728\u524d\u9662\u4e2d\u95f2\u7acb\uff0c\u6b64\u523b\u89c1\u4e86\u8fd9\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u8d6b\u8d6b \u6709\u540d\u7684\u9556\u5934\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u7adf\u5bf9\u8fd9\u4e94\u4e2a\u5c11\u5e74\u5982\u6b64\u606d\u656c\uff0c\u4e0d\u7531\u90fd\u5927\u4e3a\u60ca\u8be7\uff0c\u4e00\u8d77\u6d8c\u5230\u95e8\u53e3\uff0c \u5b9a\u6674\u4e00\u770b\uff0c\u4e0d\u8bba\u8bc6\u4e0e\u4e0d\u8bc6\uff0c\u89c1\u4e86\u8fd9\u5c11\u5e74\u4e94\u4eba\u7684\u88c5\u675f\u6c14\u6d3e\uff0c\u5fc3\u4e2d\u65b9\u81ea\u604d\u7136\uff1a\u201c\u539f\u6765\u662f\u2018\u864e\u90b1\u98de \u7075\u5821\u2019\u7684\u4e1c\u65b9\u4e94\u4fa0\uff01\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u4e94\u4eba\u7565\u4e00\u6574\u7406\u8863\u886b\uff0c\u4fbf\u90fd\u62a2\u6b65\u5230\u67f3\u8f89\u8eab\u524d\uff0c\u63e1\u624b\u5bd2\u6684\uff0c\u5341\u9063\u5982\u7535\u76ee\u5149\uff0c\u987e\u76fc\u4e4b \u95f4\uff0c\u53c8\u5411\u67f3\u8f89\u8eab\u540e\u7684\u76f8\u8bc6\u4e4b\u4eba\uff0c\u542b\u7b11\u62db\u547c\uff0c\u800c\u66fe\u7ecf\u88ab\u8fd9\u5c11\u5e74\u4e94\u4eba\u62db\u547c\u7740\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u8138\u4e0a\u4fbf \u7acb\u523b\u6cdb\u8d77\u5f97\u610f\u7684\u7b11\u5bb9\uff0c\u50cf\u662f\u89c9\u5f97\u81ea\u5df1\u80fd\u4e0e\u4ed6\u4eec\u62db\u547c\uff0c\u4e43\u5341\u5206\u8363\u5e78\u7684\u4e8b\u3002 \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u542c\u5230\u9662\u4e2d\u7684\u9a9a\u52a8\uff0c\u4ea6\u81ea\u5feb\u6b65\u8fce\u51fa\uff0c\u5927\u559c\u547c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c \u4e1c\u65b9\u4e94\u5144\u5f1f\u7adf\u4e00\u8d77\u6765\u4e86\u3002\u201d\u62a2\u6b65\u8d70\u5230\u5176\u4e2d\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\uff0c\u82f1\u59ff\u98d2\u723d\u7684\u5c11\u5e74\u8eab\u524d\uff0c\u5927\u559c\u53c8\u9053\uff1a \u201c\u5c24\u5176\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u8fdc\u5728\u5343\u91cc\u4e4b\u5916\u7684\u94c1\u5144\uff0c\u4eca\u65e5\u4e5f\u56de\u5230\u6c5f\u5357\u6765\uff0c\u5c0f\u5f1f\u4e00\u5165\u6c5f\u6e56\uff0c\u4fbf\u60f3\u5230\u864e\u90b1 \u53bb\u62dc\u8bbf\u8bf8\u5144\uff0c\u53ea\u662f\u751f\u6015\u8bf8\u5144\u4ff1\u4e0d\u5728\u5bb6\uff0c\u53c8\u4e0d\u6562\u53bb\u60ca\u52a8\u8001\u4eba\u5bb6\uff0c\u662f\u4ee5\u2014\u2014\u54c8\u54c8\uff0c\u5374\u60f3\u4e0d\u5230\u4eca\u65e5 \u5728\u6b64\u5904\u89c1\u7740\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u4e94\u4eba\u4e00\u4eba\u5e97\u95e8\u9762\u4e0a\u4ff1\u90fd\u542b\u7740\u5fae\u7b11\uff0c\u6b64\u523b\u76ee\u5149\u4e00\u626b\uff0c\u77a5\u89c1\u9f9a\u6e05\u6d0b\u7684\u65ad\u624b\uff0c\u4e0d\u7531\u5931\u58f0 \u9053\uff1a\u201c\u9f9a\u5144\uff0c\u8fd9\u662f\u600e\u4e48\u4e86\uff1f\uff0c\u9f9a\u6e05\u6d0b\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u5c0f\u5f1f\u5b9e\u89c9\u6c57\u989c\uff0c\u5509\u2014 \u2014\u7a0d\u7b49\u5c0f\u5f1f\u518d\u5949\u544a\u8bf8\u5144\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u8bf8\u5144\u6b64\u6765\uff0c\u53ef\u4e5f\u662f\u4e3a\u7740\u90a3\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u4e2d\u7684\u76df\u4e3b\u4e4b\u4f1a\u5417\uff1f\u201d \u3000\u3000\u5f53\u5148\u800c\u7acb\u7684\u534e\u670d\u5c11\u5e74\uff0c\u4e5f\u5c31\u662f\u88ab\u9f9a\u6e05\u6d0b\u79f0\u4f5c\u201c\u94c1\u201d\u5144\u7684\u4e00\u4eba\uff0c\u542b\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6211\u5144\u5f1f \u4e94\u4eba\uff0c\u672c\u6765\u90fd\u96be\u5f97\u56de\u5bb6\uff0c\u8fd9\u6b21\u6070\u5de7\u662f\u5728\u7aef\u9633\u8282\u6211\u5144\u5f1f\u56de\u5bb6\u7701\u4eb2\u4e4b\u65f6\uff0c\u542c\u5f97\u6c5f\u5357\u9053\u4e0a\uff0c\u4f20\u8a00\u3002 \u795e\u624b\u2018\u6218\u98de\u7684\u8fd9\u6b21\u76db\u4e3e\uff0c\u6211\u5144\u5f1f\u4fbf\u5fcd\u4e0d\u4f4f\u8981\u6765\u89c2\u5149\u89c2\u5149\uff0c\u5bb6\u4e25\u672c\u6765\u4e0d\u8bb8\uff0c\u540e\u6765\u542c\u5f97\u6211\u5927\u5e08\u5144 \u81ea\u897f\u6cb3\u8fd4\u6765\uff0c\u8bf4\u8d77\u5728\u6d4e\u5357\u5e9c\u66fe\u89c1\u5230\u2019\u9f99\u5f62\u516b\u638c\u2018\u6a80\u5927\u7237\u7684\u4fa0\u8e2a\uff0c\u50cf\u662f\u4e5f\u53d6\u9053\u6c5f\u752b\uff0c\u5bb6\u7236\u8fd9\u624d \u4ee4\u6211\u5144\u5f1f\u524d\u6765\uff0c\u4e00\u6765\u987a\u4fbf\u95ee\u5019\u6a80\u5927\u7237\u597d\uff0c\u518d\u6765\u4e5f\u53eb\u6211\u5144\u5f1f\u81f4\u610f\uff0c\u8bf4\u662f\u81ea\u4ece\u6a80\u5927\u7237\u4e0a\u6b21\u5230\u5bd2\u58f0 \u53bb\u8fc7\u4e4b\u540e\uff0c\u5bb6\u4e25\u4e00\u76f4\u8eab\u4f53\u4e0d\u8212\uff0c\u662f\u4ee5\u4e5f\u4e0d\u80fd\u53bb\u4eac\u57ce\u56de\u62dc\uff0c\u8bf7\u6a80\u5927\u7237\u4e0d\u8981\u89c1\u602a\u3002\u201d\u8fd9\u5c11\u5e74\u8bf4\u8d77 \u8bdd\u6765\uff0c\u4e0d\u4f46\u8bed\u58f0\u6e05\u6717\uff0c\u800c\u4e14\u4e0d\u6025\u4e0d\u5f90\uff0c\u8bed\u6c14\u4ece\u5bb9\uff0c\u4e00\u671b\u800c\u77e5\u662f\u51fa\u8eab\u4e16\u5bb6\u7684\u4fa0\u58eb\u3002\u4ed6\u76ee\u5149\u4e00 \u8f6c\uff0c\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u603b\u9556\u5934\u4e5f\u6765\u4e86\uff1f\u8fd9\u8fde\u5c0f\u5f1f\u7b49\u4e5f\u4e0d\u77e5\u9053\u5462\uff01\u201d \u3000\u3000\u8fdc\u8fdc\u7ad9\u5728\u897f\u53a2\u8de8\u9662\u95e8\u5916\u7684\u4e00\u8001\u4e00\u5c11\uff0c\u4e24\u4e2a\u6b66\u6797\u8c6a\u58eb\uff0c\u542c\u5230\u4ed6\u4eec\u7684\u8bdd\u58f0\uff0c\u90a3\u5c11\u5e74\u5fcd\u4e0d\u4f4f\u95ee \u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u4e94\u4e2a\u4eba\u662f\u8c01\u5440\uff1f\u600e\u5730\u8fde\u2018\u9f99\u5f62\u516b\u638c\u2019\u90fd\u8981\u5230\u4ed6\u5bb6\u91cc\u62dc\u8bbf\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5144\u5f1f\u4e94\u4eba\u4ff1\u662f\u4e00\u6bcd\u6240\u751f\uff0c\u4e16\u5c45\u6c5f\u5357\u2018\u864e\u90b1\u98de\u7075\u5821\u2019\uff0c\u58f0\u540d\u8d6b \u8d6b\uff0c\u9707\u52a8\u5929\u4e0b\uff0c\u4f60\u518d\u60f3\u60f3\u770b\uff0c\u4e3a\u5e08\u53ef\u66fe\u4e0e\u4f60\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6c89\u541f\u534a\u664c\uff0c\u65b9\u81ea\u604d\u7136\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e94\u4eba\u5c31\u662f\u97f3\u5e74\u4ee5\u4e00\u67c4\u94c1\u5251\uff0c\u4e09\u679a\u5251\u80c6\uff0c\u5a01\u9707\u7fa4 \u9b54\u7684\u2018\u94c1\u5251\u2019\u4e1c\u65b9\u5947\u7684\u4e94\u4f4d\u516c\u5b50\uff0c\u4e1c\u65b9\u94c1\u3001\u5251\u3001\u9707\u3001\u6c5f\u3001\u6e56\u5417\uff1f\u201d \u3000\u3000\u8001\u8005\u542b\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65b9\u624d\u90a3\u4e0e\u2018\u5feb\u9a6c\u795e\u5200\u2019\u8bf4\u8bdd\u7684\uff0c\u4fbf\u662f\u4e1c\u65b9\u957f\u516c\u5b50\uff0c\u4e60\u827a\u4e8e\u6606\u4ed1\u95e8 \u4e0b\u7684\u4e1c\u65b9\u94c1\uff1b\u7ad9\u5728\u4ed6\u53f3\u4fa7\u7684\uff0c\u90a3\u8eab\u6750\u8f83\u77ee\uff0c\u9762\u5982\u6ee1\u6708\u7684\uff0c\u4fbf\u662f\u62dc\u5728\u5ce8\u5d4b\u2018\u971c\u5bfb\u5927\u5e08\u2019\u95e8\u4e0b\u7684 \u4e8c\u516c\u5b50\u4e1c\u65b9\u5251\uff01\u7ad9\u5728\u4ed6\u5de6\u4fa7\uff0c\u90a3\u8eab\u91cf\u9880\u957f\uff0c\u51e4\u76ee\u957f\u7709\u7684\uff0c\u5c31\u662f\u4e09\u516c\u5b50\u4e1c\u65b9\u9707\uff0c\u636e\u8bf4\u8fd9\u4e09\u516c\u5b50 \u6027\u60c5\u6700\u70c8\uff0c\u6b66\u529f\u6700\u9ad8\uff0c\u4e43\u662f\u5c11\u6797\u5bfa\u5f53\u4eca\u638c\u95e8\u5927\u5e08\u7684\u552f\u4e00\u4fd7\u5bb6\u5f1f\u5b50\u3002\u201d \u3000\u3000\u4ed6\u6b47\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u5e76\u80a9\u7ad9\u5728\u4ed6\u4eec\u8eab\u540e\uff0c\u9762\u8c8c\u76f8\u540c\uff0c\u8eab\u6750\u4e00\u6837\u7684\uff0c\u662f\u4e00\u53cc\u5b6a\u751f\u5144 \u5f1f\uff0c\u4e00\u8d77\u62dc\u5728\u6b66\u5f53\u95e8\u4e0b\uff0c\u5c31\u662f\u8fd9\u4e94\u5144\u5f1f\u4e2d\u6700\u5e7c\u7684\u4e1c\u65b9\u6c5f\uff0c\u4e0e\u4e1c\u65b9\u6e56\u4e86\u3002\u201d\u4ed6\u8d5e\u4f69\u5730\u5fae\u5531\u4e00 \u58f0\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e94\u4eba\u51fa\u8eab\u6b66\u6797\u4e16\u5bb6\uff0c\u5bb6\u4e16\u679c\u7136\u663e\u8d6b\u65e0\u6bd4\uff0c\u5e08\u95e8\u66f4\u662f\u540d\u91cd\u5f53\u65f6\uff0c\u53ef\u662f\u4ed6\u4eec\u505a\u4eba \u884c\u4e8b\uff0c\u5374\u90fd\u53c8\u90a3\u4e48\u8c26\u865a\u6709\u793c\uff0c\u771f\u662f\u4eba\u6770\uff0c\u771f\u662f\u4eba\u6770\u2026\u2026\u4e07\u513f\uff0c\u4f60\u5c06\u6765\u80fd\u5b66\u7740\u4ed6\u4eec\uff0c\u90a3\u5c31\u597d \u4e86\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5251\u7709\u4e00\u626c\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u53c8\u500f\u7136\u4f4f\u53e3\uff0c\u8f6c\u53e3\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u7236\u4eb2\u5c31\u662f\u4e00\u4ee3\u5927 \u4fa0\uff0c\u53ef\u662f\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u5374\u90fd\u4e0d\u5728\u81ea\u5df1\u7236\u4eb2\u95e8\u4e0b\u5b66\u6b66\uff0c\u96be\u9053\u4ed6\u4e00\u96be\u9053\u4ed6\u4eec\u770b\u4e0d\u8d77\u81ea\u5df1\u7684\u7236\u4eb2 \u5417\uff1f\u201d \u3000\u3000\u8001\u8005\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u662f\u56e0\u4e3a\u2018\u94c1\u5251\u2019\u4e1c\u65b9\u8001\u4fa0\u5ba2\uff0c\u4e3a\u4e86\u6015\u81ea\u5df1\u7ba1\u6559\u4e0d\u4e25\uff0c\u4e0d\u613f\u610f\u4eb2\u6388\u4ed6 \u4eec\u7684\u6b66\u529f\uff0c\u624d\u53eb\u4ed6\u4eec\u62dc\u5012\u522b\u4eba\u95e8\u4e0b\uff0c\u4e0d\u8fc7\uff0c\u4e1c\u65b9\u8001\u4fa0\u5ba2\u81ea\u5df1\u4e5f\u6536\u4e86\u4e2a\u5f1f\u5b50\uff0c\u90a3\u5c31\u662f\u4f60\u53bb\u5e74\u66fe \u7ecf\u5728\u5c71\u4e1c\u89c1\u8fc7\u7684\u2018\u94c1\u9762\u4e13\u8bf8\u2019\u96f7\u771f\u3002\u201d \u3000\u3000\u8fd9\u5e08\u5f92\u4e24\u4eba\u95f2\u8bed\u4e4b\u4e2d\uff0c\u201c\u4e1c\u65b9\u4e94\u5144\u5f1f\u201d\u5df2\u88ab\u5f15\u4eba\u6b63\u5385\uff0c\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u7acb\u523b\u6446\u4e0b\u63a5 \u98ce\u76db\u5bb4\uff0c\u957f\u5144\u4e1c\u65b9\u94c1\u4e00\u9762\u8c26\u8c22\uff0c\u4e00\u9762\u53c8\u9053\uff1a\u201c\u6211\u5144\u5f1f\u8fd9\u6b21\u5fcd\u4e0d\u4f4f\u8981\u5230\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u6765\uff0c\u4e3b\u8981 \u8fd8\u662f\u4e3a\u4e86\u8981\u770b\u770b\u90a3\u4f4d\u2018\u603b\u74e2\u628a\u5b50\u2019\uff0c\u7a76\u7adf\u662f\u4f55\u7b49\u6837\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u65b9\u4e86\uff0c\u95e8\u5916\u7a81\u53c8\u5927\u6b65\u8d70\u4eba\u4e24\u4e2a\u9ed1\u8863\u5e26\u5200\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u8d70\u5230\u9662\u4e2d\uff0c\u53cc\u624b\u4e00\u626c\uff0c\u624b\u4e2d \u9ad8\u4e3e\u7740\u4e00\u5bf9\u63cf\u91d1\u7ea2\u5e16\uff0c\u6717\u58f0\u9053\uff1a\u201c\u655d\u5e84\u5e84\u4e3b\u7279\u547d\u5c0f\u7684\u4eec\u6765\u95ee\u5019\u2018\u4e1c\u65b9\u4e94\u4fa0\u2019\u7684\u4fa0\u9a7e\uff0c\u5e76\u4e14\u9001 \u4e0a\u62dc\u5e16\uff0c\u606d\u8bf7\u2018\u4e1c\u65b9\u4e94\u4fa0\u2019\u4e8e\u540e\u65e5\u6b63\u5348\uff0c\u5149\u4e34\u655d\u5e84\uff01\u201d \u3000\u3000\u4e1c\u65b9\u9707\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6218\u795e\u624b\u7684\u52a8\u4f5c\u5012\u5feb\u5f97\u5f88\uff01\u201d \u3000\u3000\u201c\u54c8\u54c8\uff0c\u4e0d\u662f\u8001\u592b\u81ea\u5938\uff0c\u90a3\u2018\u4e1c\u65b9\u4e94\u5144\u5f1f\u2019\u5230\u4e86\u8fd8\u4e0d\u53ca\u534a\u4e2a\u65f6\u8fb0\uff0c\u8001\u592b\u7684\u62dc\u5e16\u4fbf\u5df2\u9001 \u5230\uff0c\u8fd9\u79cd\u52a8\u4f5c\uff0c\u54c8\u54c8\u2014\u2014\u83ab\u5144\uff0c\u4f60\u8bf4\u5feb\u4e0d\u5feb\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6380\u987b\u5927\u7b11\uff0c\u5411\u5e76\u80a9\u7ad9\u5728\u4ed6\u8eab\u65c1\u7684\u201c\u4e03\u715e\u201d\u83ab\u661f\u54c8\u54c8\u7b11\u9053\u3002 \u3000\u3000\u201c\u4e03\u715e\u201d\u83ab\u661f\u56de\u8eab\u671b\u4e86\u671b\u4ecd\u5728\u5927\u5385\u4e2d\u5403\u559d\u7740\u7684\u7fa4\u8c6a\u4e00\u773c\uff0c\u968f\u624b\u629b\u5374\u4e86\u624b\u4e2d\u7684\u7259\u7b7e\uff0c\u5fae\u7b11 \u7740\u9053\uff1a\u201c\u7684\u786e\u5feb\u5f97\u5f88\uff0c\u7684\u786e\u5feb\u5f97\u5f88\uff0c\u53ea\u662f\u2014\u2014\u201d\u4ed6\u7684\u7709\u4e00\u76b1\uff0c\u53c8\u9053\uff1a\u201c\u5c0f\u5f1f\u5374\u6709\u51e0\u4ef6\u62c5\u5fc3\u4e4b \u4e8b\uff0c\u60f3\u5bf9\u6218\u5144\u4e00\u8a00\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u7acb\u523b\u9053\uff1a\u201c\u4f60\u6211\u81ea\u5df1\u5144\u5f1f\uff0c\u8bf4\u8bdd\u96be\u9053\u8fd8\u4f1a\u6709\u4e0d\u4fbf\u4e4b\u5904\uff0c\u83ab\u5144\uff0c\u4f60\u5feb\u4e9b\u8bf4\u51fa \u6765\u2014\u2014\u201d\u4ed6\u4e00\u6447\u624b\u4e2d\u6298\u6247\uff0c\u6380\u987b\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u8001\u592b\u6b63\u81ea\u6d17\u8033\u606d\u542c\u54e9\uff01\u201d \u3000\u3000\u83ab\u661f\u76ee\u5149\u4e00\u8f6c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u5f1f\u6240\u62c5\u5fc3\u7684\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u6218\u5144\u6b64\u4e3e\uff0c\u6b64\u523b\u53ef\u8bf4\u662f\u5df2\u9707\u52a8 \u6c5f\u752b\uff0c\u8fde\uff0c\u864e\u90b1\u98de\u7075\u5821\u2018\u7684\u4e1c\u65b9\u5144\u5f1f\u90fd\u88ab\u5f15\u4e86\u51fa\u6765\uff0c\u4ed6\u5144\u5f1f\u5bb6\u6559\u4e00\u76f4\u6781\u4e25\uff0c\u8f7b\u6613\u7edd\u4e0d\u6d89\u8db3\u6c5f \u6e56\u7684\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u6b63\u4e0d\u77e5\u8fd8\u6709\u591a\u5c11\u6b66\u6797\u5065\u8005\uff0c\u8981\u5230\u6218\u5144\u7684\u8fd9\u2019\u6d6a\u83bd\u5c71\u5e84\u2018\u6765\uff01\u201d\u201c\u795e\u624b\u201d\u6218 \u98de\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u591a\u591a\u76ca\u5584\uff0c\u96be\u9053\u83ab\u5144\u4f60\u662f\u5728\u62c5\u5fc3\u8001\u592b\u8d1f\u62c5\u4e0d\u8d77\u5417\uff1f\u201d \u3000\u3000\u83ab\u661f\u53cc\u7709\u4e00\u8f69\uff0c\u9053\uff1a\u201c\u6218\u5144\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u5c0f\u5f1f\u5c82\u975e\u6210\u4e86\u5446\u5b50\uff0c\u5c0f\u5f1f\u62c5\u5fc3\u7684\u662f\uff0c\u90a3\u4f4d\u59d3\u88f4 \u7684\u4ec1\u5144\uff0c\u7ec8\u65e5\u6101\u7709\u82e6\u8138\uff0c\u75f4\u75f4\u5446\u5446\uff0c\u65e2\u4e0d\u4f1a\u6b66\u529f\uff0c\u53c8\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u5230\u4e86\u4f1a\u671f\u90a3\u65e5\uff0c\u82e5\u5728\u5929\u4e0b\u7fa4 \u8c6a\u9762\u524d\u5f04\u51fa\u7b11\u8bdd\u6765\uff0c\u90a3\u2026\u2026\u5c82\u975e\u4f60\u6211\u5144\u5f1f\uff0c\u4e5f\u8981\u5927\u5931\u9762\u5b50\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u201c\u5237\u201d\u5730\u6536\u8d77\u624b\u4e2d\u6298\u6247\uff0c\u4e24\u9053\u6d53\u7709\uff0c\u4e5f\u7d27\u7d27\u76b1\u5230\u4e00\u5904\u3002 \u3000\u3000\u5374\u542c\u83ab\u661f\u53c8\u9053\uff1a\u201c\u5c0f\u5f1f\u7b2c\u4e8c\u4ef6\u62c5\u5fc3\u4e4b\u4e8b\uff0c\u4fbf\u662f\u90a3\u2018\u91d1\u9e21\u2019\u5411\u4e00\u557c\uff0c\u65e2\u7136\u5df2\u4e0e\u6218\u5144\u95f9\u7ffb\uff0c \u5230\u4e86\u90a3\u65e5\uff0c\u53ea\u6015\u4e5f\u4f1a\u524d\u6765\u9a9a\u6270\uff0c\u6218\u5144\u867d\u4e0d\u4f1a\u754f\u60e7\u4e8e\u4ed6\uff0c\u4f46\u4e5f\u603b\u662f\u60f9\u538c\u4e4b\u4e8b\uff0c\u4ee5\u5c0f\u5f1f\u6240\u89c1\uff0c\u8fd8 \u5e94\u53ca\u65e9\u9632\u5907\u624d\u662f\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u4e00\u8f6c\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u8fd9\u96be\u9053\u6211\u8fd8\u4e0d\u77e5\u9053\uff0c\u8fd8\u8981\u4f60\u8fd9\u6bdb\u5934\u5c0f\u5b50\u6765\u544a\u8bc9 \u6211\uff1a\u201c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\u3002\u201d \u3000\u3000\u83ab\u661f\u5634\u89d2\u4e00\u626c\uff0c\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e8b\uff0c\u4fbf\u662f\u5c0f\u5f1f\u770b\u90a3\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\u76ee\u5149\u4e0d\u6b63\uff0c\u6b64\u4eba\u5978\u72e1\u767e \u51fa\uff0c\u8bf4\u4e0d\u5b9a\u6697\u4e2d\u5df2\u5728\u56fe\u8c0b\u5bf9\u6218\u5144\u4e0d\u5229\uff0c\u6218\u5144\u4ea6\u8be5\u5c0f\u5fc3\u624d\u662f\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u7f13\u7f13\u9894\u9996\uff0c\u7a81\u5730\u5927\u7b11\u9053\uff1a\u201c\u83ab\u5144\u6b63\u5728\u8c08\u5230\u90a3\u5144\uff0c\u60f3\u4e0d\u5230\u90a3\u5144\u5c31\u5df2\u6765\u4e86\u3002\u201d \u83ab\u661f\u9762\u8272\u4e00\u53d8\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u65e9\u5df2\u7f13\u6b65\u8d70\u6765\uff0c\u800c\u6218\u98de\u53c8\u5fae\u7b11\u9053\uff1a\u201c\u83ab\u5144\u6b63\u8c08 \u8d77\u9601\u4e0b\u56ca\u4e2d\u7684\u2018\u4e03\u5de7\u2019\uff0c\u867d\u7136\u4e45\u5df2\u95fb\u540d\uff0c\u5374\u59cb\u7ec8\u672a\u66fe\u89c1\u8fc7\uff0c\u51e0\u65f6\u5b9a\u8981\u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u76ee\u5149\u4e00\u8f6c\uff0c\u4e0e\u83ab\u661f\u4e92\u89c6\u4e00\u773c\uff0c\u9634\u9634\u7b11\u9053\uff1a\u201c\u83ab\u5144\u8981\u770b\uff0c\u65e5\u540e\u603b\u6709\u673a \u4f1a\u7684\uff0c\u563f\u563f\uff0c\u6218\u98de\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u4e03\u715e\u201d\u5960\u661f\u9762\u8272\u53c8\u4e00\u53d8\uff0c\u4f46\u968f\u5373\u4ea6\u201c\u563f\u563f\u201d\u4e00\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u5c0f\u5f1f\u4ea6\u5728\u7fd8\u9996\u4ee5 \u671b\u54e9\uff01\u201d \u3000\u3000\u201c\u563f\u563f\uff01\u54c8\u54c8\uff01\u201d\u4e09\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u4ff1\u90fd\u4ef0\u65e0\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u201c\u54c8\u54c8\uff01\u563f\u563f\uff01\u201d\u91d1\u9e21\u5411\u4e00\u557c\u5f97\u610f\u5730\u5411\u9762\u524d\u4e00\u4e2a\u8863\u886b\u69db\u697c\uff0c\u5f62\u5bb9\u7325\u4eb5\u7684\u77ee\u5c0f\u6c49\u5b50\uff0c\u54c8\u54c8 \u72c2\u7b11\u9053\uff1a\u201c\u5230\u4e86\u90a3\u4e00\u5929\uff0c\u4f60\u53ea\u7ba1\u8d70\u5230\u90a3\u4f4d\u8981\u5f53\u2018\u603b\u74e2\u628a\u5b50\uff0c\u7684\u4eba\u9762\u524d\u53bb\uff0c\u671d\u4ed6\u8138\u4e0a\u5410\u53e3\u6d53 \u75f0\uff0c\u770b\u4ed6\u600e\u4e48\u5bf9\u4ed8\u4f60\uff0c\u54c8\u54c8\u2014\u2014\u6218\u98de\u5440\u6218\u98de\uff01\u6211\u770b\u4f60\u7684\u5982\u610f\u7b97\u76d8\uff0c\u7a76\u7adf\u80fd\u6253\u5230\u51e0\u65f6\uff1f\u201d\u4ed6\u5f97 \u610f\u5730\u72c2\u7b11\u7740\uff0c\u773c\u671b\u7740\u897f\u65b9\u7684\u665a\u971e\uff0c\u5728\u4ed6\u8eab\u4fa7\uff0c\u7fa4\u96c6\u7740\u201c\u91d1\u9e21\u5e2e\u201d\u7684\u5f1f\u5b50\uff0c\u770b\u5230\u4ed6\u4eec\u5e2e\u4e3b\u7684\u7b11 \u5bb9\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u90fd\u7b11\u4e86\u8d77\u6765\uff01\u53c8\u662f\u4e00\u4e2a\u661f\u5149\u95ea\u70c1\u7684\u6625\u591c\u3002\u98ce\uff0c\u50cf\u5f80\u5e38\u4e00\u6837\u548c\u6696\uff0c\u661f\u5149\uff0c\u50cf\u5f80\u5e38 \u4e00\u6837\u660e\u4eae\uff1b\u6d41\u6c34\uff0c\u50cf\u5f80\u5e38\u4e00\u6837\u6d41\u52a8\uff1b\u5927\u5730\uff0c\u50cf\u5f80\u5e38\u4e00\u6837\u9759\u5bc2\u2014\u2014\u6a80\u6587\u742a\u4ece\u9759\u5bc2\u4e2d\u9192\u6765\uff0c\u7a97\u5916 \u591c\u5bd2\u5982\u6c34\uff0c\u5979\u8ff7\u832b\u5730\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u624d\u60f3\u8d77\u81ea\u5df1\u65b9\u624d\u662f\u88ab\u90a3\u51b7\u6c0f\u5144\u5f1f\u70b9\u4e86\u8170\u7554\u7684\u201c\u7761\u7a74\u201d\u624d\u7761 \u7740\u7684\uff0c\u800c\u6b64\u523b\u7a74\u9053\u5374\u5df2\u89e3\u5f00\u4e86\u3002\u5979\u4e0d\u77e5\u9053\u6b64\u523b\u662f\u4ec0\u4e48\u65f6\u5019\uff0c\u7ad9\u8d77\u6765\uff0c\u7406\u4e86\u7406\u63c9\u4e71\u4e86\u7684\u8863\u886b\uff0c \u9759\u5bc2\u7684\u9ed1\u591c\u4e2d\uff0c\u7a81\u5730\u4f20\u6765\u4e00\u9635\u6025\u5267\u7684\u9a6c\u8e44\u58f0\uff0c\u5979\u79c0\u7709\u8f6f\u98a6\uff0c\u6697\u5fd6\uff1a\u201c\u662f\u8c01\u5728\u6df1\u591c\u4e2d\u8fd8\u5982\u6b64\u6025 \u9a70\uff1f\u201d\u968f\u53c8\u6697\u7b11\u81ea\u5df1\uff1a\u201c\u6211\u600e\u4e48\u5982\u6b64\u591a\u4e8b\uff1f\u201d\u4f38\u624b\u4e00\u63a0\u9b13\u89d2\uff0c\u53ea\u89c9\u811a\u5e95\u51c9\u51c9\u7684\uff0c\u539f\u6765\u4ecd\u672a\u7a7f \u4e0a\u978b\u5b50\u3002 \u3000\u3000\u7b49\u5230\u5979\u8bb0\u8d77\u81ea\u5df1\u7761\u68a6\u524d\u7684\u5fe7\u90c1\u7684\u5fc3\u4e8b\u7684\u65f6\u5019\uff0c\u8fdc\u5904\u4f20\u6765\u7684\u9a6c\u8e44\u58f0\uff0c\u5df2\u500f\u7136\u800c\u6b62\uff0c\u5979\u6ca1\u6709 \u7559\u610f\u9a6c\u8e44\u58f0\u662f\u505c\u987f\u5728\u90a3\u91cc\uff0c\u5374\u53c8\u4ecd\u5728\u60f3\u90a3\u7740\u90a3\u5df2\u56f0\u6270\u4e86\u5979\u4e24\u65e5\u7684\u95ee\u9898\uff0c\u5979\u5728\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u6211 \u7a76\u7adf\u8be5\u4e0d\u8be5\u53bb\uff1f\u201d \u3000\u3000\u7ec8\u4e8e\uff0c\u201c\u53bb\u89c1\u88f4\u73cf\u201d\uff0c\u53d8\u6210\u4e86\u4e00\u79cd\u4e0d\u53ef\u6291\u6b62\u7684\u51b2\u52a8\uff0c\u5979\u5de6\u624b\u63a0\u7740\u9b13\u53d1\uff0c\u53cc\u811a\u5957\u4e0a\u978b\u5b50\uff0c \u53f3\u624b\u5f00\u4e86\u95e8\uff0c\u5411\u5916\u4e00\u671b\u2014\u2014\u7a81\u5730\u2014\u2014\u95e8\u5916\u7684\u9662\u4e2d\uff0c\u50cf\u843d\u53f6\u822c\u98d8\u4e0b\u4e00\u6761\u4eba\u5f71\u6765\uff0c\u5979\u5fc3\u4e2d\u4e00\u60ca\uff0c \u7acb\u523b\u4f4e\u53f1\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u5f71\u4e00\u65cb\u8eab\uff0c\u77a5\u89c1\u4e86\u6a80\u6587\u6dc7\u7684\u9762\u5bb9\uff0c\u6a80\u6587\u742a\u4ea6\u5728\u661f\u5149\u4e0b\u77a5\u89c1\u4e86\u4ed6\u7684\u9762\u5bb9\uff0c\u4e24\u4eba\u76ee\u5149\u76f8 \u5bf9\uff0c\u5982\u4e0d\u65f6\u8131\u53e3\u9053\uff1a\u201c\u662f\u4f60\uff01\u201d \u3000\u3000\u4e24\u4eba\u5404\u81ea\u6123\u4e86\u4e00\u6123\uff0c\u5ba2\u95e8\u5916\u53c8\u54cd\u8d77\u4e86\u4e00\u4e2a\u6c89\u91cd\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u51af\u8001\u4e94\uff0c\u8fd8\u4e0d\u5feb\u5f00\u95e8\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u51af\u4e94\u53d4\uff0c\u5916\u9762\u662f\u4e0d\u662f\u7239\u7239\u6765\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u5f71\u70b9\u4e86\u70b9\u5934\uff0c\u4e00\u9762\u60ca\u9053\uff1a\u201c\u6765\u4e86\u2026\u2026\u201d\u8f7b\u8f7b\u4e00\u62ac\u6b65\uff0c\u4fbf\u5df2\u63a0\u5230\u95e8\u53e3\uff0c\u8f7b\u529f\u4e4b\u5999\uff0c\u8d85 \u7fa4\u8fc8\u4fd7\uff0c\u539f\u6765\u6b64\u4eba\u4fbf\u662f\u4e24\u6cb3\u6b66\u6797\u4e2d\u8f7b\u529f\u53ef\u79f0\u6570\u4e00\u6570\u4e8c\uff0c\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u5f97\u529b\u7684\u9556\u5934\uff0c\u201c\u585e\u4e0a \u98de\u70df\u201d\u51af\u5947\u5cf0\uff01 \u3000\u3000\u6a80\u6587\u742a\u5fae\u4e00\u8fdf\u7591\uff0c\u4ea6\u81ea\u63a0\u5230\u95e8\u53e3\uff0c\u5927\u95e8\u4e00\u5f00\uff0c\u95e8\u5916\u5f53\u5148\u8d70\u5165\u4e00\u4e2a\u9ad8\u5927\u5a01\u731b\u7684\u957f\u886b\u8001\u8005 \u6765\uff0c\u6a80\u6587\u742a\u7c89\u9888\u4e00\u5782\uff0c\u4f4e\u547c\u4e00\u58f0\uff1a\u201c\u7239\uff01\u201d \u3000\u3000\u8fd9\u8001\u8005\u6b63\u662f\u540d\u626c\u5929\u4e0b\uff0c\u6743\u503e\u6c5f\u6e56\uff0c\u58f0\u540d\u8d6b\u8d6b\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff01 \u3000\u3000\u4ed6\u76ee\u5149\uff0c\u4e00\u8f6c\uff0c\u9f3b\u5b54\u4e2d\u51b7\u51b7\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u50cf\u662f\u6ca1\u6709\u770b\u5230\u6a80\u6587\u742a\u4f3c\u7684\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u9f9a \u8001\u4e09\uff0c\u67f3\u8001\u65e6\uff0c\u600e\u5730\u8d8a\u6765\u8d8a\u4e0d\u6d4e\u4e8b\u4e86\uff0c\u5916\u9762\u6298\u817e\u6210\u8fd9\u526f\u6837\u5b50\uff0c\u4ed6\u4eec\u8fd8\u90fd\u4e0d\u77e5\u9053\uff0c\u54fc--\u4e00\u201d\u4e00 \u58f0\u51b7\u54fc\u8fc7\u540e\uff0c\u4eba\u5df2\u8d70\u5230\u9662\u4e2d\uff0c\u624d\u56de\u5934\u671b\u4e86\u64c5\u6587\u742a\u4e24\u773c\uff0c\u53c8\u9053\uff1a\u201c\u742a\u513f\uff0c\u8ddf\u6211\u6765\u3002\u201d\u5927\u6b65\u8d70\u4e0a \u53f0\u9636\uff0c\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u4e00\u638c\u9707\u5f00\u4e86\u4e00\u95f4\u5ba2\u623f\u7684\u623f\u95e8\uff0c\u559d\u9053\uff1a\u201c\u91cc\u9762\u4f4f\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u9762\u8272\u5927\u53d8\uff0c\u5979\u89c1\u5230\u5979\u7239\u7239\u4e00\u638c\u9707\u5f00\u7684\u623f\u95e8\uff0c\u7adf\u662f\u5979\u4e24\u4f4d\u51b7\u53d4\u53d4\u4f4f\u7684\uff0c\u5fc3\u91cc\u53c8\u60ca\u53c8 \u6025\uff0c\u83b2\u8db3\u8f7b\u70b9\uff0c\u500f\u7136\u63a0\u4e86\u8fc7\u53bb\uff0c\u95ea\u76ee\u800c\u671b\uff0c\u661f\u5149\u5f71\u6620\u4e4b\u4e0b\uff0c\u5374\u89c1\u623f\u5185\u7a7a\u7a7a\uff0c\u201c\u51b7\u5927\u53d4\u201d\u201c\u51b7 \u4e8c\u53d4\u201d\u7adf\u4e0d\u77e5\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u8d70\u5230\u90a3\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u201c\u7830\u201d\u7136\u4e00\u58f0\u638c\u9707\uff0c\u624d\u5c06\u4f4f\u5728\u4e1c\u8de8\u9662\u7684\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u4e0e\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u60ca\u52a8\uff0c \u4ed6\u4eec\u9152\u540e\u7761\u610f\u672c\u6d53\uff0c\u6b64\u523b\u7adf\u4e0d\u77e5\u9662\u4e2d\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u65b9\u81ea\u5306\u5fd9\u7ed3\u675f\uff0c\u63a0\u4e86\u51fa\u6765\uff0c \u5374\u5df2\u542c\u5230\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4f4e\u58f0\u51b7\u51b7\u9053\uff1a\u201c\u53c8\u559d\u9189\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5ba2\u6808\u4e2d\u7684\u706f\u5149\uff0c\u77ac\u5373\u5168\u90fd\u4eae\u8d77\uff0c\u7761\u773c\u60fa\u677e\u7684\u5e97\u5c0f\u4e8c\uff0c\u5fd9\u4e71\u5730\u5f20\u7f57\u7740\u8336\u6c34\uff0c\u9664\u4e86\u6765\u81ea\u2018\u864e \u90b1\u98de\u7075\u5821\u2019\u7684\u4e1c\u65b9\u5144\u5f1f\uff0c\u5df2\u5230\u4eac\u53e3\u53bb\u4e86\u4e4b\u5916\uff0c\u8fd9\u5e97\u91cc\u8fd8\u4f4f\u6709\u4e8c\u5341\u591a\u4e2a\u6b66\u6797\u8c6a\u58eb\uff0c\u6b64\u523b\u90fd\u6574\u8863 \u8d77\u5e8a\uff0c\u4ed6\u4eec\u90fd\u77e5\u9053\uff0c\u591a\u5e74\u8db3\u8ff9\u672a\u51fa\u4eac\u57ce\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\uff0c\u6b64\u523b\u5df2\u5230\u4e86\u6c5f\u5357\u7684\u8fd9\u5c0f\u57ce\u4e2d\u6765\uff01 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u5c45\u7136\u4e5f\u6765\u4e86\uff0c\u54fc\u54fc\uff0c\u8fd9\u5012\u662f\u602a\u4e8b\uff01\u201d\u65b9\u5f97\u8b66\u62a5\uff0c\u7acb\u523b\u8d77\u8eab\u7684\u201c\u795e\u624b\u201d\u6218\u98de\uff0c \u76ee\u5149\u5728\u90a3\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u5728\u5c71\u57ce\u5916\u4f0f\u4e0b\u7684\u6697\u5361\uff0c\u6b64\u523b\u5306\u5306\u8d76\u6765\u62a5\u8baf\u7684\u5927\u6c49\u8eab\u4e0a\u4e00\u626b\uff0c\u6c89\u58f0\u53c8 \u9053\uff1a\u201c\u4f60\u53ef\u770b\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u9ed1\u8863\u5927\u6c49\u4fef\u9996\u9053\uff1a\u201c\u5c0f\u7684\u82e5\u662f\u6ca1\u6709\u5f97\u5230\u786e\u8baf\uff0c\u4e5f\u4e0d\u6562\u6765\u60ca\u52a8\u5e84\u4e3b\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u624b\u6307\u4e0d\u65ad\u5730\u6572\u7740\u684c\u5b50\uff0c\u53d1\u51fa\u4e86\u8fde\u4e32\u201c\u7b03\u7b03\u201d\u7684\u58f0\u54cd\uff0c\u6697\u81ea \u4f4e\u8bed\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u8d76\u5230\u8fd9\u91cc\u6765\uff1f\u4ee5\u4ed6\u7684\u5730\u4f4d\uff0c\u4f3c\u4e4e\u4e0d\u8be5\u4e3a\u4e86\u6b64\u4e8b\u5982\u6b64\u7d27\u5f20\u5440\uff1f\u201d\u4ed6\u76ee\u5149 \u968f\u7740\u81ea\u5df1\u7684\u624b\u6307\u8df3\u52a8\uff0c\u6d53\u7709\u7d27\u76b1\uff0c\u5f00\u59cb\u6c89\u601d\u8d77\u6765\u3002 \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8d76\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u51db\u7136\u671b\u7740\u4ed6\u7684\u7231\u5973\uff1a\u201c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4f60\uff0c\u6211 \u95ee\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5077\u5077\u8dd1\u51fa\u6765\uff0c\u8fd9\u4e9b\u65e5\u5b50\uff0c\u4f60\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u600e\u4e48\u4f1a\u548c\u2018\u51b7\u8c37\u53cc\u6728\u2019\u4e4b\u4e2d\u7684 \u67af\u6728\u5bd2\u7af9\u8d70\u5230\u4e00\u8d77\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u4f4e\u5782\u7740\u5934\uff0c\u7ad9\u5728\u5979\u7239\u7239\u8eab\u524d\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\uff0c\u5979\u7239\u7239\u7684\u8bdd\uff0c\u5ba2\u6808\u4e2d\u7684\u706f\u5149\u5168\u90e8 \u4eae\u7740\uff0c\u4f46\u8fd9\u95f4\u5c4b\u91cc\uff0c\u5374\u53ea\u6709\u5979\u7236\u5973\u4e24\u4eba\uff0c\u5979\u53ea\u89c9\u5f97\u5979\u7239\u7239\u7684\u76ee\u5149\uff0c\u6a61\u7bad\u4e00\u6837\u76f4\u5c04\u5165\u5979\u5fc3\u91cc\uff0c \u5979\u4e0d\u6562\u8bf4\u8c0e\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d\u8bf4\u8c0e\uff0c\u5979\u55eb\u5685\u7740\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u5230\u6c5f\u5357\u770b\u770b\uff0c\u53c8\u6015\u7239\u7239\u4e0d\u8bb8\uff0c\u6240\u4ee5\u624d \u5077\u5077\u6e9c\u4e86\u51fa\u6765\uff0c\u672c\u6765\u6ca1\u6709\u9047\u7740\u4ec0\u4e48\u4e8b\uff0c\u4f46\u662f\u6709\u4e00\u5929\uff0c\u6211\u5728\u6d4e\u5357\u5e9c\u6700\u70ed\u95f9\u7684\u4e00\u6761\u5927\u8857\u4e0a\uff0c\u5ffd\u7136 \u770b\u5230\u4e24\u4e2a\u8863\u670d\u7a7f\u5f97\u5f88\u534e\u4e3d\u7684\u4eba\uff0c\u7ad9\u5728\u8def\u65c1\u8fb9\u6c42\u4eba\u65bd\u820d\uff0c\u53ea\u662f\u4ed6\u4eec\u8981\u4eba\u65bd\u820d\u7684\u4e1c\u897f\u5374\u5f88\u5947 \u602a\u3002\u201d \u3000\u3000\u6a80\u660e\u6c89\u58f0\u9053\uff1a\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u6c5f\u6e56\u4e2d\u6c42\u4eba\u65bd\u820d\u7684\u4eba\uff0c\u968f\u5904\u7686\u662f\uff0c\u4f60\u591a\u7ba1\u4ec0\u4e48\u95f2\u4e8b\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u7684\u5934\uff0c\u5782\u5f97\u66f4\u4f4e\u4e86\uff0c\u8f7b\u8f7b\u63a5\u7740\u9053\uff1a\u201c\u6211\u770b\u89c1\u5f88\u591a\u4eba\u56f4\u5728\u90a3\u91cc\uff0c\u90fd\u5728\u6697\u4e2d\u4f4e\u8bed\uff0c\u9a82 \u8fd9\u4e24\u4eba\u662f\u75af\u5b50\u3002\u6211\u5fc3\u91cc\u5947\u602a\uff0c\u5fcd\u4e0d\u4f4f\u8dd1\u8fc7\u53bb\u770b\uff0c\u53ea\u89c1\u6709\u4e2a\u5c11\u5e74\u4ece\u6000\u4e2d\u62ff\u4e86\u534a\u4e32\u94b1\u7ed9\u4ed6\u4eec\uff0c\u4ed6 \u4eec\u5374\u770b\u4e5f\u4e0d\u770b\u5c31\u4e22\u56de\u7ed9\u90a3\u5c11\u5e74\uff0c\u5e76\u4e14\u8bf4\uff1a\u2018\u4f60\u8981\u7ed9\u6211\u94b1\uff0c\u5c31\u628a\u8eab\u4e0a\u6240\u6709\u7684\u94b1\u5168\u7ed9\u6211\u3002\u2019\u90a3\u5c11 \u5e74\u6c14\u5f97\u5446\u4e86\u534a\u664c\uff0c\u624d\u5927\u9a82\u4e86\u4e24\u53e5\uff0c\u56de\u5934\u907f\u5f00\uff0c\u8fd9\u4e24\u4eba\u542c\u5230\u4eba\u5bb6\u9a82\u4ed6\u4eec\uff0c\u9762\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u8fc7\u4e86 \u4e00\u4f1a\uff0c\u5176\u4e2d\u4e00\u4e2a\u4eba\u7a81\u7136\u5bf9\u53e6\u4e00\u4e2a\u4eba\u8bf4\uff1a\u2018\u65f6\u5019\u5230\u4e86\u6ca1\u6709\uff1f\u2019\u53e6\u4e00\u4e2a\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u4e24\u4eba\u5c31\u8981\u8d70 \u4e86\uff0c\u6211\u542c\u5230\u90a3\u4e9b\u4eba\u9a82\u4ed6\u4eec\u7684\u8bdd\u6781\u4e3a\u96be\u542c\uff0c\u5fc3\u4e2d\u672c\u6765\u5c31\u6709\u4e9b\u4e0d\u5e73\uff0c\u770b\u5230\u4ed6\u4eec\u8981\u8d70\uff0c\u5c31\u5fcd\u4e0d\u4f4f \u8bf4\uff1a\u2018\u6211\u628a\u6211\u6240\u6709\u7684\u94b1\u5168\u90e8\u7ed9\u4f60\u3002\u2019\u8fd9\u65f6\u5019\uff0c\u522b\u4eba\u90fd\u770b\u7740\u6211\uff0c\u50cf\u662f\u4ee5\u4e3a\u6211\u4e5f\u75af\u4e86\u3002\u201c\u6a80\u660e \u51b7\u201d\u54fc\u201c\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u5927\u6982\u5c31\u662f\u90a3\u67af\u6728\u5bd2\u7af9\u4e86\u3002\u201d \u3000\u3000\u690d\u6587\u742a\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u6211\u5fc3\u91cc\u60f3\uff0c\u6211\u5c31\u7b97\u628a\u8eab\u4e0a\u7684\u94b1\u5168\u90e8\u7ed9\u4ed6\u4eec\u4e5f\u6ca1\u6709\u5173\u7cfb\uff0c \u53cd\u6b63\u6d4e\u5357\u5e9c\u7684\u674e\u5927\u53d4\u6211\u662f\u8ba4\u5f97\u7684\uff0c\u518d\u6765\u6211\u4e5f\u662f\u770b\u4ed6\u4eec\u53d7\u6c14\u53d7\u5f97\u592a\u591a\u4e86\uff0c\u6211\u5374\u4e0d\u77e5\u9053\u8fd9\u4e24\u4eba\u539f \u6765\u5c31\u662f\u7239\u7239\u66fe\u7ecf\u544a\u8bc9\u8fc7\u6211\u7684\u2018\u67af\u6728\u5bd2\u7af9\u2019\u3002\u201d \u3000\u3000\u6a80\u660e\u7684\u7709\u5fae\u76b1\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u602a\u7269\u8fd9\u4e48\u505a\u662f\u5e72\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u8f7b\u8f7b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u4ed6\u4eec\u5144\u5f1f\u539f\u6765\u662f\u5728\u6253\u8d4c\uff0c\u4e00\u4e2a\u8bf4\uff1a\u2018\u5c31\u7b97\u6211\u4eec \u5728\u6700\u70ed\u95f9\u7684\u5927\u8857\u4e0a\u7ad9\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4eba\u628a\u8eab\u4e0a\u7684\u94b1\u90fd\u7ed9\u6211\u3002\u2019\u53e6\u4e00\u4e2a\u5374\u4e0d\u540c\u610f\uff0c\u5176\u5b9e\u2014 \u2014\u201c\u5979\u5fcd\u4e0d\u4f4f\u53c8\u8f7b\u8f7b\u4e00\u7b11\uff1a\u201c\u5176\u5b9e\u50cf\u4ed6\u4eec\u8fd9\u6837\u8981\u94b1\uff0c\u9664\u4e86\u6211\uff0c\u4e5f\u771f\u4e0d\u4f1a\u6709\u4eba\u7ed9\u4ed6\u4eec\uff0c\u4ed6\u4eec\u770b \u5230\u6211\u628a\u5305\u88b1\u91cc\u7684\u51e0\u5341\u4e24\u94f6\u5b50\u5168\u7ed9\u4e86\u4ed6\u4eec\uff0c\u4e5f\u4e0d\u8c22\u8c22\uff0c\u62ff\u4e86\u5c31\u8d70\uff0c\u6211\u4e5f\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u5012\u89c9\u5f97 \u5f88\u6709\u610f\u601d\uff0c\u540e\u6765\u2014\u2014\u2018\u3001\u5979\u6b47\u4e86\u53e3\u6c14\uff0c\u5077\u671b\u5979\u7239\u7239\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u7239\u7239\u9762\u4e0a\u5e76\u65e0\u6012\u610f\uff0c\u65b9\u81ea\u63a5 \u9053\uff1a\u201c\u5230\u4e86\u665a\u4e0a\uff0c\u6211\u53c8\u4e0d\u60f3\u5230\u674e\u5927\u53d4\u90a3\u91cc\u53bb\u62ff\u94b1\u4e86\uff0c\u60f3\u4e86\u60f3\uff0c\u5c31\u627e\u4e86\u5bb6\u6700\u5927\u7684\u623f\u5b50\uff0c\u60f3\u2026\u2026 \u60f3\u8fdb\u53bb\u501f\u51e0\u4e24\u76d8\u7f20\u2026\u2026\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e25\u5cfb\u7684\u9762\u76ee\u4e0a\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u9732\u51fa\u4e00\u70b9\u7b11\u610f\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u5374\u4e0d\u77e5\u9053\u8fd9\u5bb6\u4eba\u5bb6 \u4e5f\u662f\u6b66\u6797\u4eba\u7269\uff0c\u7ed3\u679c\u5dee\u70b9\u7ed9\u4eba\u6293\u4f4f\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u79c0\u76ee\u4e00\u5f20\uff0c\u5947\u602a\u5730\u95ee\u9053\uff1a\u201c\u7239\u7239\uff0c\u60a8\u8001\u4eba\u5bb6\u600e\u4e48\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u6a80\u660e\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u4f60\u60f3\u5077\u4e1c\u897f\u7684\u4eba\u5bb6\uff0c\u5c31\u662f\u5c71\u4e1c\u5883\u5185\u6700\u6210\u540d\u7684\u82f1\u96c4 \u2018\u9739\u96f3\u5251\uff0c\u79e6\u5929\u8c6a\u4f4f\u7684\u5730\u65b9\uff0c\u8fd9\u6b21\u6211\u7ecf\u8fc7\u6d4e\u5357\uff0c\u4e5f\u5728\u4ed6\u5bb6\u91cc\u4f4f\u4e86\u4e00\u5929\uff0c\u542c\u4ed6\u8bf4\u6570\u6708\u4ee5\u524d\uff0c\u5bb6 \u91cc\u5c45\u7136\u95f9\u8d3c\uff0c\u6211\u5fc3\u91cc\u5c31\u5947\u602a\uff0c\u6709\u8c01\u6562\u5230\u2019\u9739\u96f3\u5251\u2018\u5bb6\u91cc\u53bb\u5f00\u6252\uff0c\u5374\u60f3\u4e0d\u5230\u662f\u4f60\u8fd9\u4e2b\u5934\u2026\u2026\u201d \u201c\u6211\u6ca1\u60f3\u5230\u662f\u4ed6\u8001\u4eba\u5bb6\u4f4f\u7684\u5730\u65b9\uff0c\u53ea\u5947\u602a\u8fd9\u5bb6\u4eba\u5bb6\u600e\u4e48\u8fd9\u6837\u8b66\u89c9\uff0c\u6211\u521a\u4e00\u8fdb\u9662\u5b50\uff0c\u5c31\u6709\u4eba\u51fa \u6765\u4e86\uff0c\u672c\u6765\u6211\u8fd8\u4e0d\u6015\uff0c\u54ea\u77e5\u51fa\u6765\u7684\u4eba\u7adf\u5168\u90fd\u662f\u9ad8\u624b\uff0c\u800c\u4e14\u8d8a\u6765\u8d8a\u591a\uff0c\u5341\u51e0\u53e3\u5251\u628a\u6211\u903c\u5f97\u8fde\u6c14 \u90fd\u5598\u4e0d\u8fc7\u6765\uff0c\u6211\u5fc3\u91cc\u5c31\u5728\u5bb3\u6015\uff0c\u54ea\u77e5\u9053\u8fd9\u65f6\u5019\u7a81\u7136\u7535\u95ea\u4f3c\u7684\u6765\u4e86\u4e24\u6761\u4eba\u5f71\uff0c\u53cc\u624b\u8fde\u6293\uff0c\u5239\u90a3 \u4e4b\u95f4\uff0c\u5c31\u88ab\u4ed6\u4eec\u6293\u8d70\u4e86\u4e09\u53e3\u957f\u5251\uff0c\u90a3\u4e9b\u4eba\u5c31\u5927\u60ca\u7740\u9053\uff1a\u2018\u8d3c\u4eba\u624e\u624b\uff0c\u5feb\u628a\u8001\u7237\u5b50\u8bf7\u51fa\u6765\uff01\u2019 \u54ea\u77e5\u521a\u5728\u4ed6\u4eec\u8bf4\u8fd9\u4e24\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u8fd9\u4e24\u4eba\u5c31\u6b63\u62c9\u7740\u6211\u7684\u624b\uff0c\u98de\u4e00\u6837\u5730\u8dd1\u8d70\u4e86\uff0c\u4ed6\u4eec\u867d\u7136\u5728\u540e \u9762\u7a77\u8ffd\uff0c\u53ef\u662f\u5374\u8fde\u6211\u4eec\u7684\u5f71\u5b50\u90fd\u8ffd\u4e0d\u7740\uff0c\u201c\u201d\u9f99\u5f62\u516b\u638c\u201c\u6a80\u660e\u53cc\u7709\u4e00\u8f69\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u60f3\u5fc5 \u5c31\u662f\u90a3\u2018\u67af\u6728\u5bd2\u7af9\u2019\u4e86\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u8f7b\u7b11\u9894\u9996\u9053\uff1a\u201c\u4ed6\u4eec\u628a\u6211\u6551\u4e86\u51fa\u53bb\uff0c\u6211\u4e00\u770b\u662f\u4ed6\u4eec\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\uff0c\u4ed6\u4eec\u5446 \u5446\u5730\u671b\u7740\u6211\uff0c\u5ffd\u7136\u5bf9\u6211\u8bf4\uff1a\u4e00\u5e74\u4e4b\u5185\uff0c\u65e0\u8bba\u6211\u6709\u4ec0\u4e48\u56f0\u96be\uff0c\u90fd\u53ef\u4ee5\u627e\u4ed6\u4eec\u3002\u6211\u5c31\u8bf4\uff1a\u6211\u5230\u54ea \u91cc\u53bb\u627e\u4f60\u4eec\uff1f\u4e0d\u5982\u4f60\u4eec\u5c31\u966a\u6211\u4e00\u5e74\u5427\uff01\u6211\u672c\u6765\u6709\u4e9b\u5f00\u73a9\u7b11\u7684\u610f\u601d\uff0c\u60f3\u4e0d\u5230\u4ed6\u4eec\u60f3\u4e86\u60f3\uff0c\u5c45\u7136 \u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u672c\u81ea\u6ee1\u6000\u6012\u610f\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u542c\u4e86\u4ed6\u7231\u5973\u7684\u8f7b\u7b11\u4f4e\u8bed\uff0c\u4ed6\u867d\u7136\u751f\u6027\u4e25\u5cfb\uff0c\u4f46\u5374\u4e0d \u7981\u4e5f\u5c06\u80f8\u4e2d\u7684\u6012\u6c14\uff0c\u6d88\u53bb\u5927\u534a\u3002 \u3000\u3000\u5f53\u4ed6\u56de\u5230\u90a3\u95f4\u5df2\u7279\u522b\u4e3a\u4ed6\u8bbe\u5907\u5468\u5168\u7684\u623f\u4e2d\u53bb\u65f6\uff0c\u4ed6\u7684\u6b65\u5c65\u662f\u5b89\u7965\u7684\uff0c\u53ea\u662f\u5b89\u7965\u4e2d\u5374\u53c8\u6709 \u4e9b\u6c89\u91cd\u3002 \u3000\u3000\u201c\u8001\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6697\u53f9\u4f4e\u8bed\u7740\uff0c\u7531\u6cb3\u5317\u81f3\u6c5f\u5357\u8fd9\u4e00\u987f\u52b3\u7d2f\u7684\u65c5\u9014\uff0c\u5df2\u4f7f\u4ed6\u6b64\u523b\u5168\u8eab\u90fd\u5f25\u5e03\u7740\u75b2\u60eb\u4e4b\u610f\uff0c \u4e8b\u4e1a\u7684\u6210\u5c31\uff0c\u5730\u4f4d\u7684\u5de9\u56fa\uff0c\u540d\u8a89\u7684\u589e\u957f\u2014\u2014\u8fd9\u4e9b\u90fd\u50cf\u662f\u5305\u7740\u7cd6\u8863\u7684\u6bd2\u836f\uff0c\u5728\u6162\u6162\u5730\u4fb5\u8680\u7740\u4ed6 \u7684\u96c4\u5fc3\u58ee\u5fd7\uff0c\u4e5f\u5728\u6162\u6162\u4fb5\u8680\u7740\u4ed6\u5bf9\u6b66\u529f\u953b\u70bc\u7684\u6052\u5fc3\uff0c\u4f7f\u5f97\u5341\u5e74\u524d\u8fd8\u4e0d\u77e5\u9053\u201c\u75b2\u60eb\u201d\u4e3a\u4f55\u4e8b\u7684 \u4ed6\uff0c\u4e8e\u4eca\u7adf\u6709\u4e86\u75b2\u60eb\u4e4b\u610f\u3002\u5f80\u6614\u5feb\u9a6c\u5954\u9a70\uff0c\u6325\u5e3d\u626c\u97ad\u7684\u65e5\u5b50\uff0c\u5982\u4eca\u5df2\u50cf\u662f\u957f\u6c5f\u5927\u6cb3\u4e2d\u6eda\u6eda\u7684 \u6d41\u6c34\uff0c\u4e00\u53bb\u6c38\u4e0d\u590d\u8fd4\u4e86\u3002 \u3000\u3000\u4ed6\u6c89\u91cd\u5730\u5c06\u81ea\u5df1\u9ad8\u5927\u7684\u8eab\u8eaf\uff0c\u6295\u5728\u67d4\u8f6f\u7684\u5e8a\u4e0a\uff0c\u5180\u6c42\u80fd\u5bfb\u5f97\u65e7\u65e5\u7684\u68a6\u5883\u3002\u201c\u8001\u4e86\uff0c\u8001 \u4e86\u2026\u2026\u201d\u4e34\u7761\u524d\uff0c\u4ed6\u8fd8\u5728\u53f9\u606f\u4f4e\u8bed\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5f53\u7b2c\u4e8c\u5929\u4ed6\u9192\u6765\u7684\u65f6\u5019\uff0c\u7761\u7720\uff0c\u5c06\u4ed6\u5df2\u5931\u53bb\u7684\u6d3b\u529b\uff0c\u53c8\u6084\u4fcf\u5730\u5e26\u56de\u7ed9\u4ed6\uff0c\u5f53\u4ed6\u5927 \u6b65\u8d70\u51fa\u623f\u95e8\u65f6\uff0c\u4eba\u4eec\u6240\u770b\u5230\u7684\uff0c\u53c8\u662f\u4e00\u4e2a\u54a4\u53f1\u6c5f\u6e56\uff0c\u5a01\u731b\u6df1\u6c89\u7684\u82f1\u96c4\uff0c\u800c\u4e0d\u662f\u6628\u591c\u75b2\u60eb\u7684\u8001 \u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\uff0c\u9762\u8c8c\u5a1f\u79c0\uff0c\u4f46\u5374\u53cc\u76ee\u65e0\u5149\uff0c\u5634\u5507\u8584\u524a\uff0c\u63a9\u53e3\u7684\u5fae\u987b\uff0c\u826f\u597d\u7684\u7b11\u5bb9\uff0c\u5374\u4ecd \u63a9\u4e0d\u4f4f\u4ed6\u6ee1\u9762\u8584\u524a\u4e4b\u50cf\u7684\u4e2d\u5e74\u58ee\u58eb\uff0c\u5feb\u6b65\u4ece\u4eba\u7fa4\u4e2d\u7a7f\u51fa\uff0c\u7b14\u76f4\u5730\u8d70\u5230\u4ed6\u8eab\u524d\uff0c\u8eac\u8eab\u4e00\u793c\uff0c\u9762 \u4e0a\u66f4\u5806\u6ee1\u4e86\u963f\u8bc0\u7684\u7b11\u5bb9\uff0c\u606d\u8eab\u9053\uff1a\u201c\u6a80\u8001\u7237\u5b50\uff0c\u957f\u8fdc\u6ca1\u89c1\u4e86\uff0c\u60a8\u8001\u4eba\u5bb6\u53ef\u597d\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u95ea\u52a8\uff0c\u6839\u672c\u671b\u6ca1\u6709\u671b\u8fd9\u4eba\u4e00\u773c\uff0c\u4ed6\u77e5\u9053\u8fd9\u5165\u4fbf\u662f\u6c5f\u6e56\u4e2d\u4e13\u95e8\u4ee5\u51fa\u5356\u6d88 \u606f\u4e3a\u751f\uff0c\u4e5f\u4ee5\u51fa\u5356\u6d88\u606f\u51fa\u540d\u7684\u201c\u51b3\u8baf\u201d\u82b1\u7389\uff0c\u6b64\u4eba\u6b66\u529f\u4e0d\u9ad8\uff0c\u4f46\u5374\u4e00\u8868\u4eba\u6750\uff0c\u6570\u767e\u5e74\u6765\u6c5f\u6e56 \u4e2d\u4ee5\u4ed6\u8fd9\u79cd\u65b9\u5f0f\u6765\u8ba8\u751f\u6d3b\u7684\uff0c\u4ed6\u5c1a\u662f\u7b2c\u4e00\u4e2a\u3002 \u3000\u3000\u662f\u4ee5\u6a80\u660e\u4ec5\u4ec5\u4e0d\u60a6\u5730\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4e5f\u6839\u672c\u6ca1\u56de\u7b54\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u4f46\u662f\u201c\u5feb\u8baf\uff0c\uff0c\u82b1\u7389\u5374\u4e45\u5df2\u4e60\u60ef\u4e86\u8fd9\u79cd\u8f7b\u8511\uff0c\u662f\u4ee5\u4e5f\u6839\u672c\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u9762\u4e0a\u4ecd\u81ea\u5806\u6ee1 \u4e86\u804c\u4e1a\u6027\u7684\u7b11\u5bb9\uff0c\u606d\u58f0\u53c8\u9053\uff1a\u201c\u660e\u5929\u5c31\u662f\u6c5f\u5357\u7eff\u6797\u9053\u5171\u8d3a\u76df\u4e3b\u7684\u65e5\u5b50\uff0c\u60a8\u8001\u4eba\u5bb6\u660e\u5929\u53ef\u51c6\u5907 \u5230\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u4e2d\u53bb\u5417\uff1f\u201d \u3000\u3000\u6a80\u660e\u53c8\u81ea\u4e0d\u7f6e\u53ef\u5426\u5730\u55ef\u4e86\u4e00\u58f0\uff0c\u90a3\u4e9b\u7fa4\u805a\u9662\u4e2d\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u672c\u6765\u89c1\u5230\u6709\u4eba\u4e0a\u53bb\u548c\u6a80\u660e\u8bf4 \u8bdd\uff0c\u4fbf\u8fdc\u8fdc\u5730\u7ad9\u5728\u4e00\u8fb9\uff0c\u6b64\u523b\u89c1\u6a80\u660e\u5bf9\u6b64\u4eba\u6839\u672c\u4e0d\u52a0\u7406\u4f1a\uff0c\u90fd\u8d70\u4e86\u8fc7\u6765\uff0c\u4e03\u5634\u516b\u820c\u5730\u95ee\u9053\uff1a \u201c\u6a80\u8001\u7237\u5b50\uff0c\u591a\u5e74\u672a\u7779\u98ce\u91c7\u4e86\u3002\u201d \u3000\u3000\u201c\u6a80\u8001\u524d\u8f88\uff0c\u665a\u8f88\u5411\u60a8\u8bf7\u5b89\u3002\u201d \u3000\u3000\u6a80\u660e\u4e00\u9762\u542b\u7b11\u4e3a\u793c\uff0c\u4e00\u9762\u6325\u624b\u5bf9\u90a3\u201c\u5feb\u8baf\u201d\u82b1\u7389\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u8bdd\uff0c\u53bb\u627e\u9f9a\u8001\u4e09\u8bf4 \u53bb\uff01\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u7b11\u5bb9\u672a\u6539\uff0c\u8bfa\u8bfa\u79f0\u662f\uff0c\u4f46\u5374\u53c8\u5e26\u7b11\u9053\uff1a\u201c\u60a8\u8001\u4eba\u5bb6\u53ef\u8981\u77e5\u9053\uff0c\u8fd9\u4f4d\u65b0\u51fa\u6765 \u7684\u2018\u603b\u74e2\u628a\u5b50\u2019\uff0c\u7a76\u7adf\u662f\u4f55\u4eba\u7269\u5417\uff1f\u201d \u3000\u3000\u6a80\u660e\u795e\u8272\u679c\u7136\u5fae\u5fae\u4e00\u53d8\uff0c\u201c\u5feb\u8baf\u201d\u82b1\u7389\u5bdf\u8a00\u89c2\u8272\uff0c\u7acb\u523b\u63a5\u9053\uff1a\u201c\u542c\u8bf4\u6b64\u4eba\u662f\u4e2a\u4e0d\u4f1a\u6b66\u529f \u7a9d\u7a9d\u56ca\u56ca\u7684\u89d2\u8272\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u53cc\u76ee\u4e00\u5f20\uff0c\u7a81\u5730\u8f6c\u8eab\u5411\u65e9\u5df2\u5782\u624b\u7ad9\u5728\u4e00\u65c1\u7684\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u6c89\u58f0\u8bf4 \u9053\uff1a\u201c\u62ff\u4e24\u5c01\u94f6\u5b50\u9001\u7ed9\u8fd9\u4f4d\u82b1\u58ee\u58eb\u505a\u76d8\u7f20\u3002\u201d\u888d\u8896\u4e00\u62c2\uff0c\u65b9\u5f85\u8d70\u4e0b\u53f0\u9636\uff0c\u53ea\u89c1\u4eba\u7fa4\u4e2d\u7a81\u7136\u53d1 \u51fa\u4e00\u9635\u9a9a\u52a8\uff0c\u63a5\u7740\u4fbf\u8ba9\u5f00\u4e00\u6761\u901a\u9053\uff0c\u4ed6\u95ea\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u4eba\u4e1b\u65cb\u7136\u8d70\u6765\u4e94\u4e2a\u9526\u8863\u534e\u670d\u7684\u5c11\u5e74\uff0c \u5374\u6b63\u662f\u201c\u864e\u90b1\u98de\u7075\u5821\u201d\u7684\u4e1c\u65b9\u5144\u5f1f\u3002 \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u591a\u65e5\u6765\u4e00\u76f4\u5b88\u5728\u8fd9\u5ba2\u6808\uff0c\u6628\u591c\u4ee5\u4e94\u5341\u4e24\u94f6\u5b50\u7684\u4ef7\u94b1\uff0c\u5356\u7ed9\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u5728 \u8fd9\u5c71\u57ce\u4f0f\u4e0b\u7684\u6697\u5361\u4e00\u4e2a\u6d88\u606f\u2014\u2014\u201c\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u6765\u4e86\u3002\u201d\u4eca\u65e5\uff0c\u4ed6\u53c8\u4ee5\u53e6\u4e00\u4e2a\u6d88\u606f\uff0c\u5411 \u6a80\u660e\u6362\u4e86\u4e00\u767e\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u9762\u4e0a\u542b\u7740\u6ee1\u610f\u7684\u7b11\u5bb9\uff0c\u8d70\u51fa\u5ba2\u6808\uff0c\u5ba2\u6808\u4e2d\u6b63\u55a7\u817e\u7740\u5bd2\u6684\u7b11\u8bed\uff0c\u4ed6\u8d70\u51fa\u95e8\u5916\uff0c\u897f\u884c \u51e0\u6b65\uff0c\u7acb\u523b\u6709\u4e00\u4e2a\u9ed1\u8863\u52b2\u88c5\u7684\u5927\u6c49\u8fce\u9762\u9762\u6765\uff0c\u4e24\u4eba\u4ea4\u6362\u4e86\u4e2a\u773c\u8272\uff0c\u4e00\u8d77\u8d70\u5230\u8857\u7684\u8f6c\u89d2\u5904\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u5927\u6c49\u5fcd\u4e0d\u4f4f\u7acb\u523b\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u82b1\u5927\u54e5\uff0c\u4eca\u5929\u53ef\u6709\u4ec0\u4e48\u8baf\u606f\uff1f\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u5c55\u989c\u4e00\u7b11\uff0c\u7f13\u7f13\u4f38\u51fa\u4e00\u4e2a\u98df\u6307\uff0c\u7b80\u77ed\u4ed6\u8bf4\u9053\uff1a\u201c\u4e00\u767e\uff01\u201d \u3000\u3000\u9ed1\u8863\u5927\u6c49\u9762\u5bb9\u53d8\u4e86\u4e00\u53d8\uff0c\u5fc3\u4e2d\u867d\u5728\u5acc\u8d35\uff0c\u4f46\u5374\u4e5f\u77e5\u9053\uff0c\u8fd9\u201c\u51b3\u8baf\u201d\u82b1\u7389\u4e4b\u6240\u4ee5\u80fd\u5403\u8fd9\u884c \u996d\uff0c\u800c\u4e14\u5403\u5f97\u975e\u5e38\u8212\u670d\uff0c\u5c31\u9760\u4e86\u6d88\u606f\u7684\u51c6\u786e\u53ef\u9760\u548c\u201c\u5feb\u201d\uff0c\u5c24\u5176\u662f\u8fd9\u201c\u5feb\u201d\u5b57\uff0c\u6709\u4e9b\u6d88\u606f\uff0c \u56fa\u7136\u4eba\u4eba\u90fd\u4f1a\u77e5\u9053\uff0c\u4f46\u4ed6\u5374\u6bd4\u522b\u4eba\u8981\u5feb\u4e0a\u51e0\u6b65\uff0c\u56e0\u4e4b\u624d\u80fd\u591f\u5356\u94b1\uff0c\u4e5f\u56e0\u4e4b\u4ed6\u63d0\u51fa\u7684\u4ef7\u94b1\uff0c\u4ece \u6765\u6ca1\u6709\u8fd8\u4ef7\u4f59\u5730\u3002 \u3000\u3000\u9ed1\u8863\u5927\u6c49\u4e00\u8a00\u4e0d\u53d1\u5730\u638f\u51fa\u4e24\u5c01\u94f6\u5b50\uff0c\u82b1\u7389\u62ff\u5728\u624b\u91cc\u62c8\u4e86\u62c8\uff0c\u4fbf\u9053\uff1a\u201c\u51b7\u5bb6\u5144\u5f1f\u6628\u591c\u8d70 \u4e86\uff0c\u4eca\u5929\u8fd8\u6ca1\u6709\u56de\u6765\uff0c\u660e\u5929\u6211\u62c5\u4fdd\u4ed6\u4eec\u4e0d\u4f1a\u5230\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u4e2d\u53bb\u3002\u201d \u3000\u3000\u9ed1\u8863\u5927\u6c49\u76ee\u5149\u4e00\u5f20\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u62c5\u4fdd\uff1f\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u5f97\u610f\u5730\u7b11\u9053\uff1a\u201c\u6211\u8981\u662f\u6ca1\u6709\u6cd5\u5b50\u77e5\u9053\uff0c\u8fd8\u80fd\u8981\u4f60\u7684\u94f6\u5b50\u5417\uff1f\u201d\u4ed6\u987f\u4e86\u987f\uff0c \u53c8\u9053\uff1a\u201c\u6709\u5173\u6b64\u4e8b\uff0c\u6211\u4e5f\u8bb8\u8fd8\u6709\u66f4\u91cd\u5927\u7684\u6d88\u606f\uff0c\u53ef\u662f\u73b0\u5728\u8fd8\u4e0d\u80fd\u786e\u5b9a\uff0c\u4eca\u591c\u56db\u66f4\uff0c\u6211\u518d\u5728\u8fd9 \u91cc\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u4e8e\u662f\u7247\u523b\u4e4b\u540e\uff0c\u7acb\u523b\u53c8\u6709\u4e00\u5339\u5065\u9a6c\uff0c\u5411\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e2d\u6025\u9a70\u800c\u53bb\u3002 \u3000\u3000\u6bcf\u4e00\u4ef6\u8db3\u4ee5\u9707\u52a8\u6b66\u6797\u7684\u5927\u4e8b\uff0c\u8868\u9762\u770b\u6765\uff0c\u867d\u7136\u8f70\u8f70\u70c8\u70c8\uff0c\u5149\u660e\u6b63\u5927\u7684\uff0c\u53ef\u662f\u6697\u4e2d\uff0c\u5374\u4e0d \u77e5\u6709\u591a\u5c11\u9634\u8c0b\u7684\u52fe\u5f53\uff0c\u597d\u72e1\u7684\u8be1\u8ba1\uff0c\u8f9b\u9178\u7684\u8840\u6cea\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u8bf8\u5982\u6b64\u7c7b\u7684\u4ea4\u6613\uff0c\u53ea\u662f\u4f60\u82e5 \u4e0d\u6df1\u6df1\u5730\u53bb\u4f53\u4f1a\uff0c\u4f60\u4fbf\u96be\u4ee5\u53d1\u89c9\u800c\u5df2\u3002 \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u5c06\u8eab\u4e0a\u4e94\u5c01\u94f6\u5b50\u5206\u5728\u4e09\u4e2a\u5730\u65b9\u6536\u7740\uff0c\u8fd9\u6837\u624d\u4e0d\u4f1a\u592a\u8fc7\u6c89\u91cd\uff0c\u7136\u540e\u4ed6\u4fbf\u5feb\u9a6c \u9a70\u5230\u4eac\u53e3\uff0c\u7eb5\u60c5\u73a9\u4e86\u4e00\u65e5\u3002 \u3000\u3000\u56de\u6765\u65f6\uff0c\u5df2\u7ecf\u8fc7\u4e86\u9ec4\u660f\u4e86\uff0c\u4ed6\u6000\u91cc\u7684\u94f6\u5b50\uff0c\u4e5f\u53ea\u5269\u4e0b\u4e86\u4e09\u5c01\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6709\u628a\u63e1\uff0c\u5230\u4e86\u4eca\u591c\u4e94\u66f4\uff0c\u8fd9\u4e09\u5c01\u94f6\u5b50\uff0c\u53c8\u4f1a\u589e\u52a0\u51e0\u500d\uff0c\u56e0\u4e3a\u4ed6\u786e\u4fe1\u81ea\u5df1\u5df2\u638c\u63e1\u5230 \u4e94\u4ef6\u79d8\u5bc6\u7684\u67a2\u94ae\u3002 \u3000\u3000\u7ecf\u8fc7\u8fd9\u5c71\u57ce\u7684\u65f6\u5019\uff0c\u4ed6\u505c\u4e0b\u9a6c\uff0c\u5411\u90a3\u5ba2\u6808\u4e2d\u671b\u4e86\u51e0\u773c\uff0c\u5ba2\u6808\u4e2d\u4ecd\u7136\u6709\u4eba\u58f0\uff0c\u4ed6\u5e7b\u60f3\u5f97 \u5230\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\uff0c\u6b64\u523b\u6b63\u56f4\u5728\u201c\u9f99\u5f62\u516b\u638c\u201d\u8eab\u65c1\uff0c\u5bf9\u8fd9\u4f4d\u540d\u503e\u6b66\u6797\u7684\u8c6a\u6770\uff0c\u4f5c\u5404\u5f0f\u5404\u6837\u7684 \u5949\u627f\uff0c\u5c31\u6b63\u5982\u81ea\u5df1\u4e00\u6837\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5634\u89d2\u6cdb\u8d77\u4e00\u4e2a\u81ea\u5632\u7684\u7b11\u5bb9\uff0c\u53cd\u624b\u4e00\u97ad\u9a6c\u80a1\uff0c\u8fd9\u5339\u9a6c\uff0c\u4fbf\u5411\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u7684\u65b9\u5411\u6025 \u9a70\u800c\u53bb\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u8d70\u7684\u4e0d\u662f\u6b63\u9053\u800c\u662f\u5c0f\u8def\uff0c\u800c\u4e14\u8ddd\u79bb\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u8fd8\u6709\u8bb8\u591a\u8def\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u5c06\u9a6c\u5bc4 \u5728\u4e00\u4e2a\u8d2b\u7a77\u7684\u519c\u5bb6\u91cc\uff0c\u5c31\u50cf\u6628\u591c\u4e00\u6837\uff0c\u4ed6\u7ed9\u4e86\u8fd9\u519c\u5bb6\u7684\u4e3b\u4eba\u4e00\u4e9b\u6563\u788e\u94f6\u5b50\uff0c\u6362\u6765\u4e00\u8fde\u4e32\u7684\u611f \u6fc0\u3002 \u3000\u3000\u542c\u5230\u8fd9\u79cd\u611f\u6fc0\u7684\u8bdd\uff0c\u5728\u4ed6\u8bf4\u6765\uff0c\u662f\u4e00\u4ef6\u7a00\u7f55\u7684\u7ecf\u9a8c\u3002 \u3000\u3000\u4ed6\u7684\u811a\u6b65\uff0c\u4e5f\u5c31\u53d8\u5f97\u66f4\u8f7b\u5feb\u4e86\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u9880\u957f\u7684\u8eab\u5f62\uff0c\u4fbf\u968f\u7740\u8fd9\u8f7b\u5feb\u7684\u811a\u6b65\uff0c\u6295\u5165\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u5de8\u5927\u7684\u9634\u5f71\u4e2d\uff0c\u8fd9\u60c5\u5f62 \u4e5f\u6b63\u548c\u6628\u591c\u4e00\u6837\u3002 \u3000\u3000\u6628\u591c\u2014\u2014 \u3000\u3000\u5f53\u90a3\u5c71\u57ce\u4e2d\u4f3c\u4e4e\u5df2\u4e0d\u518d\u6709\u4ec0\u4e48\u6709\u4ef7\u503c\u7684\u6d88\u606f\u53ef\u4f9b\u4ed6\u63a2\u6d4b\u7684\u65f6\u5019\uff0c\u4ed6\u4fbf\u6084\u6084\u5730\u8dd1\u5230\u201c\u6d6a\u83bd \u5c71\u5e84\u201d\u6765\uff0c\u6cbf\u7740\u548c\u4eca\u591c\u76f8\u540c\u7684\u8def\u7ebf\uff0c\u4ece\u5c71\u5e84\u56db\u5468\u9ad8\u5927\u7684\u5899\u89d2\uff0c\u7ed5\u5230\u5e84\u540e\uff0c\u6b63\u548c\u4e00\u4e2a\u7ecf\u5e38\u5728\u996d \u94fa\u540e\u95e8\u8e72\u4f0f\u7740\uff0c\u671f\u5f85\u7740\u5927\u4eba\u5148\u751f\u4eec\u996d\u540e\u5269\u5f90\u7684\u6e23\u6ed3\u6765\u585e\u6ee1\u81ea\u5df1\u80a0\u80c3\u7684\u4e5e\u4e10\u4e00\u6837\uff0c\u4ed6\u603b\u662f\u5e0c\u671b \u81ea\u5df1\u80fd\u5728\u4e00\u4e9b\u9634\u6697\u7684\u89d2\u843d\u91cc\uff0c\u62fe\u53d6\u4e00\u4e9b\u522b\u4eba\u65e0\u6cd5\u6cc4\u6f0f\u7684\u6d88\u606f\u3002 \u3000\u3000\u4f46\u662f\uff0c\u7eb5\u7136\u6709\u56f4\u5899\u7684\u9634\u5f71\u63a9\u62a4\u7740\u4ed6\u7684\u8eab\u5f62\uff0c\u4ed6\u7684\u5fc3\u60c5\uff0c\u5374\u4ecd\u7136\u662f\u7d27\u5f20\u7684\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u56f4 \u5899\u91cc\u4f4f\u7740\u7684\uff0c\u90fd\u662f\u968f\u65f6\u53ef\u4ee5\u593a\u53d6\u4ed6\u6027\u547d\u7684\u82f1\u96c4\u8c6a\u58eb\uff0c\u4ed6\u6781\u529b\u8e51\u8f7b\u81ea\u5df1\u7684\u8db3\u5c65\uff0c\u751f\u6015\u81ea\u5df1\u4f1a\u53d1 \u51fa\u4efb\u4f55\u4e00\u70b9\u8db3\u4ee5\u593a\u53d6\u81ea\u5df1\u6027\u547d\u7684\u58f0\u97f3\u548c\u54cd\u529f\u3002 \u3000\u3000\u540c\u65f6\uff0c\u4ed6\u4e5f\u5728\u7559\u610f\u503e\u542c\u7740\u56f4\u5899\u5185\u7684\u58f0\u97f3\u4e0e\u54cd\u52a8\uff0c\u7136\u800c\u56db\u4e0b\u662f\u90a3\u6837\u9759\u5bc2\uff0c\u4ed6\u751a\u81f3\u80fd\u542c\u5f97\u5230 \u81ea\u5df1\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u8f7b\u5fae\u548c\u89c4\u5f8b\u7684\uff0c\u4ed6\u7684\u6b65\u5c65\u4fbf\u4e5f\u968f\u7740\u8fd9\u8f7b\u5fae\u800c\u89c4\u5f8b\u7684\u5fc3\u8df3\u58f0\uff0c\u5411\u524d\u79fb\u52a8\u7740\u3002 \u7a81\u5730\u2014\u2014\u56f4\u5899\u5185\u6709\u4e86\u58f0\u54cd\uff01 \u3000\u3000\u4ed6\u673a\u8b66\u5730\u505c\u4e0b\u811a\u6b65\uff0c\u5c4f\u606f\u800c\u5f85\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u7f13\u7f13\u81ea\u56f4\u5899\u4e2d\u5347\u8d77\uff0c\u4f3c\u4e4e\u4e5f\u5728\u7559\u610f\u7740\u56db\u4e0b\u7684\u54cd\u52a8\uff0c\u662f\u4ee5\u5de6\u53f3\u5bdf\u770b\u4e86\u8bb8 \u4e45\uff0c\u65b9\u81ea\u7ffb\u8eab\u5230\u5899\u5934\uff0c\u7136\u540e\u201c\u6251\u201d\u5730\u8df3\u5230\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u770b\u5230\u8fd9\u4eba\u8dc3\u5230\u5730\u9762\u540e\uff0c\u7adf\u50cf\u662f\u7ad9\u7acb\u4e0d\u7a33\uff0c\u5411\u524d\u51b2\u51fa\u6570\u6b65\uff0c\u8eab\u5f62\u65b9\u81ea\u7ad9\u76f4\uff0c\u4ed6\u4e0d\u7981\u6697\u4e2d \u5947\u602a\u3002\u201c\u8fd9\u4eba\u662f\u8c01\uff0c\u7adf\u4e0d\u4f1a\u6b66\u529f\uff0c\u800c\u6562\u5728\u8fd9\u2018\u6d6a\u83bd\u5c71\u5e84\uff0c\u4e2d\u505a\u8fd9\u4e9b\u9b3c\u795f\u52fe\u5f53\u2014\u2014\u201d\u4ed6\u5ff5\u5934\u5c1a \u672a\u56f4\u8f6c\u5b8c\uff0c\u53ea\u542c\u56f4\u5899\u4e2d\u53c8\u662f\u4e00\u58f0\u8f7b\u559d\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u7136\u540e\u6709\u4e24\u6761\u4eba\u5f71\uff0c\u50cf\u79bb\u5f26\u4e4b\u7bad\u4f3c\u7684\uff0c\u4ece\u5899\u5185\u63a0\u51fa\uff0c\u98d8\u4f53\u843d\u5230\u65b9\u624d\u90a3\u4e0d\u4f1a\u6b66\u529f\u7684\u4eba\u5f71\u524d \u9762\uff0c\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u5fd9\u5c06\u81ea\u5df1\u7684\u8eab\u5f62\uff0c\u9690\u85cf\u4e00\u682a\u6811\u540e\uff0c\u51e0\u4e4e\u8fde\u547c\u5438\u90fd\u4e0d\u6562\u900f\u51fa\uff0c\u5fae\u9732\u534a\u53ea\u773c \u89d2\uff0c\u5411\u5916\u7aa5\u53bb\uff0c\u53ea\u89c1\u90a3\u4e0d\u4f1a\u6b66\u529f\u7684\u4eba\u5f71\uff0c\u89c1\u5230\u6709\u4eba\u62e6\u4f4f\u81ea\u5df1\u7684\u53bb\u8def\uff0c\u975e\u4f46\u4e1d\u6beb\u6ca1\u6709\u60ca\u614c\u4e4b \u6001\uff0c\u53cd\u800c\u633a\u8d77\u80f8\u819b\uff0c\u6717\u58f0\u9053\uff1a\u201c\u662f\u6211\uff01\u201d \u3000\u3000\u6b64\u540e\u4ed6\u5df2\u770b\u51fa\u8fd9\u4eba\u5f71\u662f\u4e2a\u9526\u8863\u534e\u670d\u7684\u5c11\u5e74\uff0c\u591c\u8272\u4e2d\u867d\u770b\u4e0d\u751a\u6e05\uff0c\u4f46\u4ed6\u4ecd\u89c9\u5f97\u8fd9\u5c11\u5e74\u7684\u4fca \u7f8e\u4e0e\u82f1\u633a\uff0c\u7ade\u662f\u81ea\u5df1\u751f\u5e73\u672a\u89c1\uff0c\u4ed6\u5fc3\u60c5\u7d27\u5f20\u5730\u671f\u5f85\u7740\uff0c\u7b49\u5f85\u90a3\u4e24\u4eba\u7684\u53cd\u5e94\uff0c\u54ea\u77e5\u90a3\u4e24\u4eba\u53cd\u800c \u9000\u540e\u4e00\u6b65\uff0c\u606d\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u88f4\u5927\u5148\u751f\uff0c\u5982\u6b64\u6df1\u591c\uff0c\u4e0d\u77e5\u88f4\u5927\u5148\u751f\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u88f4\u5927\u5148\u751f\u201d\u56db\u5b57\u4e00\u5165\u82b1\u7389\u4e4b\u8033\uff0c\u4ed6\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8131\u53e3\u60ca\u547c\u8d77\u6765\uff01 \u3000\u3000\u201c\u96be\u9053\u4ed6\u5c31\u662f\u90a3\u5c06\u8981\u88ab\u6c5f\u5357\u7eff\u6797\u8c6a\u58eb\u5949\u4e3a\u603b\u74e2\u628a\u5b50\u7684\u2018\u88f4\u5927\u5148\u751f\u2019\uff0c\u600e\u5730\u4ed6\u7adf\u4e0d\u4f1a\u6b66 \u529f\uff1f\u662f\u4e2a\u5982\u6b64\u5e74\u8f7b\u7684\u5c11\u5e74\uff1f\u201d \u3000\u3000\u6b64\u4e8b\u59d4\u5b9e\u592a\u8fc7\u79bb\u5947\uff0c\u4ed6\u8e72\u8eab\u4e0b\u53bb\uff0c\u4f0f\u5728\u6811\u811a\uff0c\u53ea\u542c\u8fd9\u201c\u88f4\u5927\u5148\u751f\u201d\u51b7\u51b7\u7b54\u9053\uff1a\u201c\u5982\u6b64\u826f \u5bb5\uff0c\u6211\u60f3\u5230\u5916\u9762\u6765\u8d70\u8d70\u2014\u2014\u53ef\u4ee5\u5417\uff1f\u201d \u3000\u3000\u90a3\u4e24\u6761\u4eba\u5f71\u4ff1\u662f\u7cbe\u608d\u5f6a\u58ee\u7684\u6c49\u5b50\uff0c\u76ee\u5149\u95ea\u95ea\uff0c\u8eab\u5f62\u8f7b\u7075\uff0c\u663e\u89c1\u5f97\u6b66\u529f\u90fd\u5f88\u6709\u6839\u57fa\uff0c\u5728 \u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e2d\u4ea6\u975e\u6cdb\u6cdb\u4e4b\u8f88\uff0c\u6b64\u523b\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u9f50\u5730\u4e00\u8d77\u5927\u5956\u8d77\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\u7b11\u7740\u8bf4 \u9053\uff1a\u201c\u5982\u6b64\u826f\u5bb5\uff0c\u96be\u5f97\u88f4\u5927\u5148\u751f\u6709\u6b64\u96c5\u5174\uff0c\u6211\u4e24\u4eba\u4e0d\u5acc\u5192\u6627\uff0c\u4e5f\u60f3\u966a\u2018\u88f4\u5927\u5148\u751f\u2019\u8d70\u8d70\u3002\u201d \u4ed6\u7b11\u58f0\u4e00\u987f\uff1a\u201c\u53ef\u4ee5\u5417\uff1f\u201d \u3000\u3000\u88ab\u79f0\u4e3a\u201c\u88f4\u5927\u5148\u751f\u201d\u7684\u88f4\u73cf\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u624d\u663e\u7136\u5403\u4e86\u4e00\u60ca\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u7adf\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002 \u8eb2\u5728\u6811\u540e\u7684\u201c\u5feb\u8baf\u201d\u82b1\u7389\u4e0d\u7981\u5927\u60d1\u4e0d\u89e3\uff0c\u4ed6\u65e0\u6cd5\u60f3\u8c61\uff0c\u600e\u5730\u5728\u201c\u88f4\u5927\u5148\u751f\u201d\u4e0e\u201c\u795e\u624b\u201d\u6218\u98de \u4e4b\u95f4\uff0c\u8fd8\u4f1a\u6709\u6b64\u7b49\u60c5\u4e8b\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u201c\u88f4\u5927\u5148\u751f\u201d\u4f3c\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u65b9\u81ea\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f46\u51ed\u5c0a\u610f\uff01\u201d\u201c\u6b64\u523b\u201d\u5feb\u8baf \u201c\u82b1\u7389\u7684\u5fc3\u60c5\uff0c\u867d\u7136\u7d27\u5f20\uff0c\u5374\u53ef\u5fcd\u4e0d\u4f4f\u6709\u4e9b\u5174\u594b\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u6b64\u4e8b\u5176\u4e2d\uff0c\u5fc5\u5b9a\u53c8\u9690\u85cf\u7740\u4e00 \u4e9b\u201d\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\u201c\u7684\u79d8\u5bc6\uff0c\u800c\u522b\u4eba\u7684\u201d\u79d8\u5bc6\u201c\u5728\u4ed6\u773c\u4e2d\uff0c\u4ea6\u662f\u5c31\u548c\u767d\u82b1\u82b1\u7684\u94f6\u5b50\u4e00\u6837\u3002\u4ed6 \u770b\u5230\u90a3\u4e24\u4e2a\u7cbe\u608d\u7684\u6c49\u5b50\u4e00\u5de6\u4e00\u53f3\uff0c\u5c06\u201d\u88f4\u5927\u5148\u751f\u201c\u631f\u5728\u4e2d\u592e\uff0c\u7f13\u7f13\u5411\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u8fd9\u4e24\u4eba\u811a \u5e95\u7adf\u540c\u65f6\u4e00\u4e2a\u8e09\u8dc4\uff0c\u4e24\u4eba\u540c\u65f6\u7ffb\u8eab\u559d\u9053\uff1a\u201c\u8c01\uff1f\u662f\u2026\u2026\u2026\u201d\u8fd9\u4e24\u4eba\u559d\u58f0\u65b9\u81ea\u51fa\u53e3\uff0c\u8eab\u5f62\u6447\u4e86 \u4e24\u6447\uff0c\u7adf\u540c\u65f6\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u53d8\u6545\u53d1\u751f\u4e4b\u7a81\u7136\uff0c\u4f7f\u5f97\u201c\u5feb\u8baf\u201d\u82b1\u7389\u5fcd\u4e0d\u4f4f\u4f38\u624b\u63a9\u4f4f\u81ea\u5df1\u7684\u5634\uff0c\u751f\u6015\u81ea\u5df1\u8131\u53e3\u60ca\u547c \u51fa\u58f0\u6765\u3002 \u3000\u3000\u5374\u89c1\u8fd9\u201c\u88f4\u5927\u5148\u751f\u201d\u4f3c\u4e4e\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u4fef\u8eab\u4e0b\u53bb\uff0c\u4f38\u624b\u4e00\u63a2\u8fd9\u4e24\u4eba\u7684\u80f8\u819b\uff0c\u7136\u540e\u7ad9\u8d77 \u6765\uff0c\u770b\u4e86\u770b\u81ea\u5df1\u7684\u624b\u638c\uff0c\u8eb2\u5728\u9634\u6697\u5904\u7684\u82b1\u7389\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c\u661f\u5149\u4e4b\u4e0b\uff0c\u8fd9\u201c\u88f4 \u5927\u5148\u751f\u201d\u7684\u624b\u4e0a\uff0c\u7adf\u53c8\u6cbe\u6ee1\u4e86\u9c9c\u8840\uff01 \u3000\u3000\u53ea\u89c1\u201c\u88f4\u5927\u5148\u751f\u201d\u4f38\u7740\u6cbe\u6ee1\u9c9c\u8840\u7684\u624b\u638c\uff0c\u56db\u4e0b\u8f6c\u52a8\u7740\u8eab\u8eaf\uff0c\u53e3\u4e2d\u5583\u5583\u9053\uff1a\u201c\u8c01\uff1f\u8c01\uff1f\u201d \u3000\u3000\u591c\u8272\u6df1\u6c89\uff0c\u6625\u5bd2\u5982\u6c34\uff0c\u5439\u5f97\u56db\u4e0b\u7684\u6728\u53f6\uff0c\u7c0c\u7c0c\u4f5c\u54cd\u3002 \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u4e00\u751f\u4e4b\u4e2d\uff0c\u867d\u7136\u4e5f\u66fe\u7ecf\u5386\u8fc7\u8bb8\u591a\u51f6\u9669\u4e4b\u4e8b\uff0c\u4e5f\u867d\u7136\u660e\u77e5\u9053\u4ed6\u773c\u524d\u6240\u89c1\u7684\uff0c \u5fc5\u5b9a\u6709\u5173\u4e00\u4ef6\u7edd\u5927\u7684\u9690\u79d8\uff0c\u53ef\u662f\u4ed6\u51e0\u4e4e\u4ecd\u7136\u5fcd\u4e0d\u5728\u8981\u7ffb\u8eab\u63a0\u8d75\uff0c\u8fdc\u8fdc\u9003\u5f00\u8fd9\u91cc\uff0c\u8fd9\u5145\u6ee1\u4e86\u68ee \u68ee\u5bd2\u610f\u7684\u5730\u65b9\uff01 \u3000\u3000\u53ef\u662f\u2014\u2014 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u773c\u5e18\u5fae\u4e00\u5f20\u9616\uff0c\u518d\u6b21\u62ac\u76ee\u671b\u53bb\u7684\u65f6\u5019\u2026\u2026 \u3000\u3000\u201c\u88f4\u5927\u5148\u751f\u201d\u8eab\u4fa7\uff0c\u53c8\u5df2\u591a\u4e86\u4e24\u6761\u4eba\u5f71\uff0c\u8fd9\u4e24\u6761\u4eba\u5f71\u662f\u90a3\u4e48\u9ad8\uff0c\u90a3\u4e48\u7626\uff0c\u5c31\u50cf\u662f\u9b3c\u9b45\u7a81 \u7136\u81ea\u5730\u5e95\u6d8c\u51fa\u4f3c\u7684\uff0c\u6f2b\u65e0\u58f0\u97f3\u5730\u51fa\u73b0\u5728\u4ed6\u773c\u524d\uff0c\u4ed6\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5f3a\u81fc\u6309\u637a\u7740\u5fc3\u4e2d \u7684\u60ca\u6050\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u518d\u51dd\u795e\u671b\u53bb\uff01 \u3000\u3000\u201c\u5440\uff01\u539f\u6765\u662f\u4ed6\u4eec\uff01\u201d \u3000\u3000\u8fd9\u4e24\u6761\u4eba\u5f71\u7adf\u662f\u4ed6\u65b9\u624d\u79bb\u53bb\u4e4b\u65f6\uff0c\u4ecd\u5728\u5c71\u57ce\u5ba2\u6808\u4e2d\u9ad8\u5367\u7684\u201c\u67af\u6728\u5bd2\u7af9\u201d\uff01 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u8fd9\u4e24\u4e2a\u602a\u4eba\u4e3a\u4f55\u7a81\u7136\u5728\u6b64\u5730\u51fa\u73b0\uff0c\u66f4\u4e0d\u77e5\u9053\u4ed6\u4eec\u4e0e\u8fd9\u201c\u88f4\u5927\u5148\u751f\u201d\u6709\u4ec0\u4e48\u5173 \u8fde\uff0c\u53ea\u89c1\u4ed6\u4eec\u51b7\u51b7\u671b\u7740\u201c\u88f4\u5927\u5148\u751f\u201d\uff0c\u51b7\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u742a\u513f\u75c5\u4e86\u3002\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u6123\uff1a\u201c\u742a\u513f\u662f\u8c01\uff1f\u600e\u5730\u8fd9\u2018\u67af\u6728\u5bd2\u7af9\u2019\u6df1\u591c\u4e4b\u95f4\uff0c\u8dd1\u5230\u8fd9\u91cc \u6765\uff0c\u53c8\u4e0d\u60dc\u4ee5\u6bd2\u624b\u6740\u6b7b\u4e24\u4e2a\u4eba\uff0c\u5374\u53ea\u4e3a\u4e86\u8981\u544a\u8bc9\u522b\u4eba\u3002\u742a\u513f\u75c5\u4e86\u2018\uff1f\u201d\u4ed6\u5fc3\u4e2d\u5927\u5947\uff0c\u5b9a\u775b\u671b \u53bb\uff0c\u5374\u89c1\u90a3\u201c\u88f4\u5927\u5148\u751f\u201d\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u795e\u60c5\u7adf\u7136\u4e00\u53d8\uff0c\u7adf\u6ee1\u5e26\u60f6\u6025\u4e4b\u8272\u4ed6\u8bf4\u9053\uff1a\u201c\u5979\u600e\u5730\u75c5 \u4e86\uff1f\u4ec0\u4e48\u75c5\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u53c8\u81ea\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5979\u662f\u4e3a\u4f60\u75c5\u7684\uff01\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u4ea6\u81ea\u51b7\u7136\u63a5\u53e3\u9053\uff1a\u201c\u53bb\u770b\u770b\u5979\uff01\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u6b64\u523b\u66f4\u6709\u5982\u5760\u4eba\u4e94\u91cc\u96fe\u4e2d\uff0c\u7eb5\u7136\u7528\u5c3d\u5fc3\u667a\u3001\u4e5f\u60f3\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u4ed6\u6b64\u523b \u867d\u5df2\u9690\u7ea6\u731c\u5230\uff0c\u8fd9\u201c\u67af\u6728\u5bd2\u7af9\u201d\u53e3\u4e2d\u7684\u201c\u742a\u513f\u201d\uff0c\u53ef\u80fd\u5c31\u662f\uff1a\u2018\u9f99\u5f62\u516b\u638c\u201c\u7684\u7231\u5973\u201d\u9f99\u5973 \u201c\u6a80\u6587\u742a\uff0c\u53ef\u662f\u8fd9\u6837\u4e00\u6765\uff0c\u4ed6\u53cd\u800c\u66f4\u52a0\u7cca\u6d82\uff01\u201d\u8fd9\u2019\u88f4\u5927\u5148\u751f\u2018\u663e\u7136\u5c31\u8981\u6210\u4e3a\u6c5f\u5357\u7eff\u6797\u7684\u603b \u74e2\u628a\u5b50\uff0c\u800c\u6b66\u6797\u4e2d\u4eba\u4eba\u77e5\u9053\uff0c\u2019\u795e\u624b\u2018\u6218\u98de\u6b64\u4e3e\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u56e2\u7ed3\u6c5f\u5357\u7eff\u6797\uff0c\u672a\u548c\u2019\u98de\u9f99\u9556 \u5c40\uff0c\u4f5c\u5bf9\uff0c\u4f46\u5374\u53c8\u600e\u4f1a\u548c\u2018\u9f99\u5973\u2019\u6a80\u6587\u742a\u6709\u7740\u5173\u8fde\uff0c\u800c\u90a3\u6a80\u6587\u742a\u7adf\u4f1a\u4e3a\u4ed6\u75c5\u4e86\u3002\u201c\u6709\u4e9b\u4e8b\u8ba9 \u5f53\u5c40\u8005\u6216\u662f\u6df1\u77e5\u5185\u60c5\u7684\u4eba\u89c1\u4e86\uff0c\u56fa\u7136\u5e73\u5e73\u65e0\u5947\uff0c\u4f46\u5c40\u5916\u4eba\u5374\u4e0d\u7981\u83ab\u540d\u5176\u5999\uff0c\u6b64\u523b\u661f\u5149\u751a\u660e\uff0c \u6620\u5728\u5730\u4e0a\u7684\u4e24\u5177\u5c38\u8eab\uff0c\u6237\u8eab\u65c1\u7684\u4e24\u4e2a\u602a\u4eba\uff0c\u4ee5\u53ca\u4e00\u4e2a\u770b\u6765\u4f3c\u662f\u5931\u9b42\u843d\u9b44\u7684\u5c11\u5e74\uff0c\u8ba9\u8fd9\u8ff7\u8499\u7684 \u9ed1\u591c\uff0c\u5e73\u6dfb\u4e86\u4e0d\u77e5\u51e0\u8bb8\u51c4\u6e05\u4e4b\u610f\u3002\u98de\u89c1\u201d\u88f4\u5927\u5148\u751f\u201c\u53c8\u81ea\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u7a81\u5728\u957f\u53f9\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u6211\u4e0d\u80fd\u53bb\u3002\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u6697\u4e2d\u70b9\u4e86\u70b9\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u6362\u4e86\u662f\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u53bb\u7684\u3002\u201d\u5374\u89c1\u201c\u67af\u6728\u5bd2\u7af9\u201d \u95fb\u58f0\u53c8\u6012\u8d77\u6765\uff01 \u3000\u3000\u51b7\u67af\u6728\u51b7\u7b11\u4e00\u58f0\uff0c\u68ee\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u5979\u4e3a\u4f60\u75c5\u4e86\uff0c\u4f60\u8fde\u770b\u90fd\u4e0d\u53bb\u770b\u5979\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u63a5\u53e3\u51b7\u7b11\u9053\uff1a\u201c\u6709\u4e9b\u4eba\u603b\u662f\u656c\u9152\u4e0d\u5403\uff0c\u8981\u5403\u7f5a\u9152\uff0c\u4f60\u53ef\u60f3\u5230\uff0c\u4eca\u65e5\u4f60\u80fd\u4e0d\u53bb \u5417\uff1f\u201d \u3000\u3000\u8fd9\u201c\u67af\u6728\u5bd2\u7af9\u201d\u8bf4\u8d77\u8bdd\u6765\uff0c\u58f0\u97f3\u4e4b\u51b7\u524a\u68ee\u5bd2\uff0c\u6709\u5982\u53d1\u81ea\u575f\u5893\uff0c\u201c\u5feb\u8baf\u201d\u82b1\u7389\u867d\u7136\u660e\u77e5\u6b64 \u8bed\u4e0d\u662f\u7531\u4ed6\u800c\u53d1\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u5168\u8eab\u60ca\u60b8\u8d77\u6765\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u5c31\u5728\u51b7\u5bd2\u7af9\u8bed\u58f0\u65b9\u4e86\u7684\u90a3\u5239\u90a3\u4e4b\u95f4\uff0c\u8fdc\u5904\u6797\u68a2\u7a81\u5730\u4f20\u6765\u4e00\u4e2a\u5a07\u67d4\u6e05\u8106\u7684\u58f0\u97f3\uff0c\u4e00\u5b57\u4e00\u5b57 \u5730\u5e26\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u53bb\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u201c\u4e0d\u53bb\u201d\u4e24\u5b57\u65b9\u81ea\u4eba\u8033\uff0c\u591c\u8272\u4e4b\u4e2d\uff0c\u5df2\u98d8\u98d8\u63a0\u6765\u4e00\u6761\u4eba\u5f71\uff0c\u6765\u52bf\u4f3c\u4e0d\u751a \u6025\uff0c\u4f46\u5f85\u5230\u201c\u53c8\u600e\u6837\u201d\u4e09\u5b57\u8bf4\u5b8c\u4e4b\u9645\uff0c\u8fd9\u4eba\u5f71\u5df2\u63a0\u5230\u8fd1\u524d\uff0c\u5c31\u50cf\u662f\u5189\u5189\u4e58\u4e91\u800c\u6765\uff0c\u4e0d\u5e26\u4e00\u4e1d \u70df\u706b\u6c14\u3002 \u3000\u3000\u4ed6\u4e45\u8d70\u6c5f\u6e56\uff0c\u81ea\u8eab\u7684\u6b66\u529f\u867d\u4e0d\u9ad8\uff0c\u4f46\u6240\u63a5\u89e6\u5230\u7684\uff0c\u5374\u90fd\u662f\u6b66\u6797\u4e2d\u54cd\u5f53\u5f53\u7684\u4eba\u7269\uff0c\u53ea\u662f\u4ed6 \u6709\u751f\u4ee5\u6765\uff0c\u7adf\u4ece\u672a\u89c1\u8fc7\u4e00\u4eba\uff0c\u8f7b\u529f\u6709\u5982\u6b64\u9ad8\u5999\u7684\uff0c\u5fc3\u5934\u65b9\u81ea\u6697\u9a87\uff0c\u5374\u5df2\u542c\u5f97\u201c\u67af\u6728\u5bd2\u7af9\u201d\u5fae \u5e26\u60ca\u8be7\u5730\u8131\u53e3\u9053\uff1a\u201c\u91d1\u7ae5\u7389\u5973\uff01\u201d \u3000\u3000\u672c\u5df2\u7d27\u5f20\u5f97\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\u7684\u201c\u5feb\u8baf\u201d\u82b1\u7389\uff0c\u9aa4\u7136\u542c\u5230\u8fd9\u4e2a\u9707\u52a8\u6b66\u6797\u7684\u540d\u5b57\uff0c\u4e0d\u7981\u53c8\u4e3a \u4e4b\u4e00\u9707\uff0c\u5b9a\u6674\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4f20\u8bf4\u4e2d\u7684\u6b66\u6797\u5947\u4eba\uff0c\u7adf\u662f\u4e2a\u8eab\u6750\u9ad8\u5927\u65e0\u6bd4\u7684\u5973\u5b50\uff0c\u4e00\u8eab\u8f7b\u7f57\u957f \u886b\uff0c\u5374\u4e5f\u63a9\u4e0d\u4f4f\u5979\u8eab\u6750\u7684\u7c97\u58ee\uff0c\u6700\u602a\u7684\u662f\uff0c\u5979\u8eab\u540e\u7adf\u80cc\u7740\u4e00\u4e2a\u9ec4\u8272\u7684\u85e4\u7ba9\uff0c\u85e4\u7ba9\u4e2d\u659c\u501a\u7740\u4e00 \u4e2a\u6ee1\u8eab\u91d1\u886b\u7684\u6709\u5982\u5e7c\u7ae5\u822c\u7684\u7537\u5b50\uff0c\u591c\u8272\u4e2d\u8fdc\u8fdc\u671b\u53bb\uff0c\u4ed6\u867d\u7136\u770b\u4e0d\u57fa\u6e05\uff0c\u4f9d\u7a00\u4ecd\u53ef\u770b\u51fa\uff0c\u8fd9\u6709 \u5982\u5e7c\u7ae5\u822c\u7684\u7537\u5b50\uff0c\u4e0d\u4f46\u8863\u51a0\u5ce8\u7136\uff0c\u800c\u4e14\u9894\u4e0b\u5df2\u6709\u80e1\u987b\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u7b2c\u4e00\u6b21\u89c1\u4e86\u8fd9\u201c\u91d1\u7ae5\u7389\u5973\u201d\u4e4b\u9762\uff0c\u90fd\u4f1a\u6709\u4e9b\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u201c\u5feb\u8baf\u201d\u82b1\u7389\u81ea \u4e5f\u5982\u662f\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e00\u53cc\u540d\u9707\u6b66\u6797\u7684\u5947\u4eba\uff0c\u751f\u50cf\u7adf\u662f\u5982\u6b64\u6a21\u6837\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u201c\u67af\u6728 \u5bd2\u7af9\u201d\u6b64\u523b\u5df2\u5e76\u80a9\u7ad9\u5728\u4e00\u8d77\uff0c\u56db\u53ea\u773c\u775b\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u201c\u91d1\u7ae5\u7389\u5973\u201d\u8eab\u4e0a\uff0c\u8eab\u8eaf\u50f5\u7acb\uff0c\u795e \u60c5\u6728\u7136\uff0c\u82e5\u4e0d\u662f\u591c\u98ce\u5439\u5f97\u4ed6\u4eec\u7684\u8863\u8882\u5fae\u5fae\u98d8\u8d77\uff0c\u76f4\u6709\u5982\u4e00\u53cc\u6ce5\u5851\u6728\u96d5\u7684\u795e\u50cf\u3002 \u3000\u3000\u82b1\u7389\u91cd\u91cd\u54bd\u4e0b\u4e00\u53e3\u553e\u6cab\uff0c\u50cf\u662f\u8981\u5c06\u4ed6\u5df2\u5feb\u8981\u8dc3\u51fa\u53e3\u8154\u7684\u5fc3\uff0c\u4e5f\u4e00\u8d77\u54bd\u56de\u8179\u91cc\uff0c\u6708\u5149\u5f80\u897f \u6c89\u4e0b\uff0c\u4e8e\u662f\u4ed6\u5904\u8eab\u7684\u4e00\u65b9\u4e5f\u66f4\u9634\u6697\uff0c\u4f46\u662f\uff0c\u5728\u8fd9\u7fa4\u661f\u6f2b\u5929\uff0c\u591c\u5bd2\u5982\u6c34\u7684\u665a\u4e0a\uff0c\u4ed6\u5b81\u613f\u81ea\u5df1\u5728 \u4e16\u4e0a\u5176\u4ed6\u4efb\u4f55\u4e00\u5904\u5730\u65b9\uff0c\u4e5f\u4e0d\u613f\u5728\u8fd9\u91cc\uff0c\u53ea\u542c\u90a3\u201c\u7389\u5973\u201d\u53c8\u662f\u8f7b\u8f7b\u4e00\u7b11\uff0c\u4f38\u624b\u4e00\u6307\u8eab\u4fa7\u7684 \u201c\u88f4\u5927\u5148\u4e3b\u201d\uff0c\u542b\u7b11\u53c8\u9053\uff1a\u201c\u4eba\u5bb6\u4e0d\u613f\u610f\u8ddf\u4f60\u4eec\u8d70\uff0c\u4f60\u51ed\u4ec0\u4e48\u8981\u5f3a\u8feb\u4eba\u5bb6\uff0c\u4f55\u51b5\u2014\u2014\u4f60\u77e5\u4e0d \u77e5\u9053\uff0c\u4ed6\u8ddf\u6211\u4eec\u662f\u6709\u7ea6\u4f1a\uff0c\u8fd8\u8f6e\u4e0d\u4e0a\u4f60\u4eec\u54e9\uff01\u201d \u3000\u3000\u201c\u67af\u6728\u5bd2\u7af9\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u4ece\u201c\u91d1\u7ae5\u7389\u5973\u201d\u9762\u4e0a\u500f\u7136\u779f\u5411\u88f4\u73cf\uff0c\u4ed6\u4eec\u9762\u4e0a\u867d\u4ecd\u6728\u65e0\u8868\u60c5\uff0c \u4f46\u5fc3\u4e2d\u5374\u4e5f\u5728\u5947\u602a\uff1a\u201c\u8fd9\u59d3\u88f4\u7684\u5c0f\u5b50\u600e\u4f1a\u548c\u2018\u91d1\u7ae5\u7389\u5973\u2019\u6709\u7740\u5173\u7cfb\uff1f\u201d\u5fc3\u610f\u65b9\u8f6c\uff0c\u5374\u542c\u4e00\u58f0 \u6717\u7b11\uff0c\u63a5\u7740\u773c\u524d\u4e00\u82b1\uff0c\u90a3\u201c\u91d1\u7ae5\u201d\u7adf\u5df2\u4ece\u7ba9\u4e2d\u63a0\u51fa\uff0c\u201c\u5feb\u8baf\u201d\u82b1\u7389\u5fc3\u4e2d\u65b9\u81ea\u6697\u7b11\uff0c\u8fd9\u201c\u91d1 \u7ae5\u201d\u8eab\u8eaf\u4e4b\u77ee\uff0c\u5b9e\u5728\u6709\u5982\u4f8f\u5112\uff0c\u5374\u89c1\u201c\u67af\u6728\u5bd2\u7af9\u201d\u7adf\u4e0d\u58f0\u4e0d\u54cd\u5730\u500f\u7136\u75be\u4f38\u53cc\u624b\uff0c\u95ea\u7535\u822c\u5411 \u201c\u91d1\u7ae5\u201d\u5f53\u5934\u5288\u4e0b\u3002 \u3000\u3000\u201c\u67af\u6728\u5bd2\u7af9\u201d\u8eab\u8eaf\u7279\u9ad8\uff0c\u201c\u91d1\u7ae5\u201d\u8eab\u8eaf\u5374\u53c8\u7279\u77ee\uff0c\u201c\u67af\u6728\u5bd2\u7af9\u201d\u8fd9\u56db\u638c\u5288\u4e0b\uff0c\u6708\u5149\u4e0b\u53ea \u89c1\u4e00\u7247\u5de8\u5927\u7684\u9ed1\u5f71\uff0c\u6709\u5982\u6cf0\u5c71\u538b\u9876\u822c\u5411\u4ed6\u5f53\u5934\u51fb\u4e0b\u3002 \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u53ea\u89c1\u8fd9\u201c\u91d1\u7ae5\u201d\u7684\u5168\u8eab\u4e0a\u4e0b\uff0c\u4f3c\u4e4e\u90fd\u5df2\u5728\u8fd9\u4e24\u53cc\u624b\u638c\u7684\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u773c\u770b\u662f \u907f\u65e0\u53ef\u907f\uff0c\u95ea\u65e0\u53ef\u95ea\u3002 \u3000\u3000\u89c1\u201c\u88f4\u5927\u5148\u751f\u201d\u5931\u58f0\u60ca\u547c\uff0c\u800c\u90a3\u201c\u7389\u5973\u201d\u5374\u4ecd\u9762\u5e26\u7b11\u5bb9\uff0c\u8896\u624b\u65c1\u89c2\uff0c\u751f\u50cf\u662f\u6ca1\u6709\u770b\u89c1 \u201c\u67af\u6728\u5bd2\u7af9\u201d\u7684\u7a81\u7136\u52a8\u624b\u4e00\u6837\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u8fd9\u56db\u53ea\u624b\u638c\u582a\u582a\u51fb\u5728\u201c\u91d1\u7ae5\u201d\u8eab\u4e0a\u7684\u5239\u90a3\u4e4b\u95f4\u3001\u201c\u91d1\u7ae5\u201d\u7a81\u5730\u5fae\u5fae\u4e00\u7b11\uff0c\u624b\u81c2 \u4e5f\u4e0d\u4f5c\u52bf\uff0c\u7a81\u5730\u53cc\u638c\u4e00\u8d77\u7ffb\u8d77\uff0c\u5411\u201c\u67af\u6728\u5bd2\u7af9\u201d\u7531\u4e0a\u800c\u4e0b\u7684\u56db\u53ea\u624b\u638c\u6258\u53bb\uff0c\u201c\u5feb\u8baf\u201d\u82b1\u7389\u89c1 \u5230\u201c\u67af\u6728\u5bd2\u7af9\u201d\u8fd9\u56db\u638c\u91cd\u5982\u5c71\u5cb3\uff0c\u800c\u201c\u91d1\u7ae5\u201d\u5411\u4e0a\u63a5\u67b6\u7684\u53cc\u638c\uff0c\u5374\u662f\u8f7b\u98d8\u98d8\u7684\uff0c\u751f\u50cf\u662f\u4e00\u4e1d \u529b\u91cf\u90fd\u6ca1\u6709\uff0c\u5fc3\u4e2d\u65b9\u81ea\u66ff\uff1a\u2018\u91d1\u7ae5\u201c\u6697\u53eb\u4e00\u58f0\u201d\u8981\u7cdf\u201c\uff0c\u54ea\u77e5\u7a81\u5730\u542c\u5230\u201d\u6ce2\u3001\u6ce2\u3001\u6ce2\u3001\u6ce2 \u201c\u4e00\u8fde\u4e32\u56db\u58f0\u638c\u54cd\uff0c\u201d\u91d1\u7ae5\u201c\u77ee\u5c0f\u7684\u8eab\u8eaf\uff0c\u4ecd\u81ea\u5c79\u7acb\u4e0d\u52a8\uff0c\u800c\u201d\u67af\u6728\u5bd2\u7af9\u201c\u5374\u5df2\u5404\u5404\u540e\u9000\u4e86 \u4e00\u6b65\u3002\u4ed6\u5fc3\u4e2d\u5927\u5947\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u2018\u91d1\u7ae5\u2019\u58f0\u540d\u5982\u6b64\u4e4b\u54cd\uff0c\u83ab\u4e0d\u662f\u4f1a\u4ec0\u4e48\u90aa\u6cd5\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u201c\u91d1\u7ae5\u201d\u65b9\u624d\u90a3\u53cc\u638c\u4e00\u6258\u4e4b\u52bf\uff0c\u770b\u6765\u867d\u7136\u8f7b\u98d8\u65e0\u529b\uff0c\u5176\u5b9e\u5374\u662f\u5185\u5bb6\u7edd\u9876\u91cd \u624b\uff0c\u53ea\u662f\u4ed6\u6b66\u529f\u7ec3\u7684\u662f\u9634\u5947\u4e00\u6d3e\uff0c\u662f\u4ee5\u5916\u4eba\u770b\u6765\uff0c\u4e0d\u89c1\u5a01\u529b\uff0c\u5176\u5b9e\u4e3e\u624b\u6295\u8db3\u95ee\uff0c\u90fd\u542b\u8574\u7740\u6781 \u96c4\u6d51\u7684\u5185\u529b\u3002 \u3000\u3000\u65b9\u624d\u4ed6\u624b\u638c\u4e00\u67b6\uff0c\u4fbf\u5df2\u5728\u51b7\u5bd2\u7af9\u53f3\u638c\uff0c\u51b7\u67af\u6728\u5de6\u638c\u4e0a\u4e00\u51fb\uff0c\u638c\u638c\u76f8\u51fb\uff0c\u201c\u6ce2\u201d\u7684\u56db\u58f0\u8f7b \u54cd\uff0c\u201c\u67af\u6728\u5bd2\u7af9\u201d\u53ea\u89c9\u638c\u5fc3\u4e00\u70ed\uff0c\u5468\u8eab\u4e00\u9707\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u540e\u9000\u4e00\u6b65\uff0c\u88f4\u73cf\u5bf9\u8fd9\u53d1\u80b2\u4e0d\u5168\uff0c\u8eab \u5982\u4f8f\u5112\u7684\u6b66\u6797\u5947\u4eba\uff0c\u672c\u6765\u5b58\u6709\u4e09\u5206\u601c\u60af\u4e4b\u5fc3\uff0c\u6b64\u523b\u89c1\u4ed6\u6325\u624b\u4e4b\u95f4\uff0c\u4fbf\u5df2\u51fb\u9000\u5f3a\u654c\uff0c\u4e0d\u7981\u53c8\u5c06 \u5fc3\u4e2d\u7684\u601c\u60af\uff0c\u5316\u4e3a\u656c\u4f69\u3002 \u3000\u3000\u53ea\u542c\u201c\u91d1\u7ae5\u201d\u53c8\u81ea\u6717\u58f0\u4e00\u7b11\uff0c\u6717\u7b11\u58f0\u4e2d\uff0c\u8eab\u5f62\u5ffd\u8d77\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u4fbf\u5df2\u98d8\u98d8\u51fb\u51fa\u6570\u638c\uff0c\u638c \u52bf\u672a\u5230\uff0c\u201c\u67af\u6728\u5bd2\u7af9\u201d\u5df2\u89c9\u4e00\u9635\u9634\u68ee\u6cdb\u9aa8\u7684\u5bd2\u98ce\uff0c\u8fce\u9762\u9762\u6765\uff0c\u5fc3\u4e2d\u5fae\u614e\uff0c\u773c\u89d2\u659c\u779f\uff0c\u4e24\u4eba\u76ee \u5149\u76f8\u63a5\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u8eab\u5f62\u4e00\u77ee\uff0c\u51b7\u5bd2\u7af9\u53f3\u638c\u81ea\u53f3\u800c\u5de6\uff0c\u5de6\u638c\u81ea\u4e0b\u800c\u4e0a\uff0c\u51b7\u67af\u6728\u5de6\u638c\u81ea\u5de6\u800c \u53f3\uff0c\u53f3\u638c\u7b14\u76f4\u51fb\u51fa\uff0c\u201c\u7830\u201d\u5730\uff0c\u53c8\u662f\u56db\u638c\uff0c\u4ed6\u4e24\u4eba\u8eab\u9ad8\u81c2\u957f\uff0c\u8fd9\u56db\u53ea\u624b\u81c2\u50cf\u662f\u53c8\u5c06\u201c\u91d1\u7ae5\u201d \u5939\u5728\u4e2d\u592e\u3002\u4ed6\u8eab\u5df2\u51cc\u7a7a\uff0c\u773c\u770b\u53c8\u662f\u65e0\u6cd5\u95ea\u907f\uff0c\u54ea\u77e5\u4ed6\u624b\u8155\u4e00\u53cd\uff0c\u201c\u6ce2\u201d\u5730\u53c8\u662f\u56db\u58f0\u8f7b\u54cd\uff0c\u5728 \u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u7adf\u53c8\u51cc\u7a7a\u786c\u63a5\u4e86\u8fd9\u201c\u67af\u6728\u5bd2\u7684\u201d\u56db\u638c\uff0c\u77ee\u5c0f\u7684\u8eab\u8eaf\uff0c\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u7adf\u63a0\u5230 \u201c\u67af\u6728\u5bd2\u7af9\u201d\u8eab\u540e\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u53cc\u638c\u659c\u5206\uff0c\u5e76\u6307\u5982\u5251\uff0c\u75be\u5730\u5411\u201c\u67af\u6728\u5bd2\u7af9\u201d\u7684\u5de6\u53f3\u201c\u80a9\u4e95\u201d \u4e00\u5927\u7a74\u70b9\u53bb\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u98d8\u5ffd\uff0c\u6709\u5982\u9b3c\u566c\uff0c\u53cc\u638c\u8fd0\u8f6c\u4e4b\u75be\uff0c\u66f4\u662f\u9a87\u4eba\u542c\u95fb\u3002 \u3000\u3000\u201c\u67af\u6728\u5bd2\u4f2f\u201d\u5012\u5438\u4e00\u53e3\u51b7\u6c14\uff0c\u7529\u80a9\u3001\u62e7\u8170\u3001\u9519\u6b65\uff0c\u51b7\u67af\u6728\u53f3\u638c\uff0c\u51b7\u5bd2\u7af9\u5de6\u638c\uff0c\u9f50\u5730\u5212\u4e86 \u4e2a\u534a\u5708\uff0c\u5374\u7528\u53e6\u4e00\u53ea\u624b\u638c\uff0c\u500f\u7136\u7a7f\u51fa\uff0c\u8fd9\u4e00\u62db\u201c\u5708\u4e2d\u5c04\u6708\u201d\uff0c\u770b\u6765\u867d\u7136\u5e73\u5e73\u65e0\u5947\uff0c\u4f46\u5374\u653b\u5b88 \u517c\u5907\uff0c\u5b88\u52bf\u5bc6\u4e0d\u900f\u51e4\uff0c\u653b\u52bf\u5f00\u5c71\u88c2\u77f3\uff0c\u6b63\u662f\u5185\u5bb6\u638c\u6cd5\u4e2d\u7684\u7edd\u5999\u9ad8\u62db\u3002 \u3000\u3000\u54ea\u77e5\u201c\u91d1\u7ae5\u201d\u53cc\u638c\u843d\u7a7a\uff0c\u8eab\u8eaf\u51cc\u7a7a\u53c8\u662f\u4e00\u7ffb\uff0c\u5934\u4e0a\u811a\u4e0b\uff0c\u5374\u7528\u53cc\u811a\u811a\u5c16\u8e22\u5411\u51b7\u67af\u6728\u53f3 \u638c\uff0c\u51b7\u5bd2\u7af9\u5de6\u638c\uff0c\u638c\u7f18\u5916\u4fa7\u7684\u201c\u540e\u6eaa\u201d\u7a74\uff0c\u9ed1\u6697\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u4ee5\u811a\u8ba4\u7a74\uff0c\u7ade\u4ea6\u5982\u6b64\u4e4b\u51c6\uff0c\u201c\u67af\u6728\u5bd2\u7af9\u201d\u5fc3\u5934\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u61d4\uff0c\u624b\u638c\u4e00\u66f2\uff0c\u518d\u6b21\u95ea\u7535 \u822c\u51fb\u51fa\uff0c\u6a2a\u5207\u201c\u91d1\u7ae5\u201d\u8db3\u8e1d\u5185\u7f18\u3002 \u3000\u3000\u8981\u77e5\u9053\u201c\u2018\u67af\u6728\u5bd2\u7af9\u201d\u4eab\u540d\u6b66\u6797\u591a\u5e74\uff0c\u6b66\u529f\u5b9e\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u201c\u91d1\u7ae5\u201d\u811a\u4e0a\u7eb5\u7136\u7a7f\u7740\u94c1\u978b \u94a2\u9774\uff0c\u82e5\u88ab\u4ed6\u4eec\u53cc\u638c\u626b\u4e2d\uff0c\u7acb\u65f6\u4ea6\u5f97\u9aa8\u65ad\u7b4b\u6298\uff0c\u800c\u6b64\u523b\u4ed6\u8eab\u5f62\u51cc\u7a7a\u5df2\u4e45\uff0c\u8eab\u8eaf\u5e73\u606f\u4e0b\u843d\u4e4b \u52bf\uff0c\u4ed6\u82e5\u5411\u540e\u98d8\u9000\uff0c\u811a\u4e0b\u81ea\u7136\u8eb2\u5f97\u8fc7\u8fd9\u4e00\u638c\u4e4b\u5371\uff0c\u4f46\u524d\u80f8\u5374\u7a7a\u95e8\u5927\u9732\uff0c\u67af\u6728\u5de6\u638c\uff0c\u5bd2\u7af9\u53f3 \u638c\uff0c\u867d\u5df2\u51fb\u51fa\uff0c\u4f46\u5168\u8eab\u529b\u9053\uff0c\u5374\u5206\u4e86\u4e00\u534a\u7ed9\u53e6\u4e00\u53ea\u624b\u638c\u4e0a\uff0c\u53ea\u7b49\u4ed6\u524d\u80f8\u7a7a\u95e8\u4e00\u9732\uff0c\u7acb\u523b\u51fb \u51fa\u3002\u54ea\u77e5\u201c\u91d1\u7ae5\u201d\u6709\u5982\u6210\u4eba\u624b\u81c2\u7684\u53cc\u817f\uff0c\u7a81\u5730\u5411\u540e\u4e00\u7785\uff0c\u51cc\u7a7a\u7adf\u53c8\u7a81\u4e00\u7ffb\u8eab\uff0c\u53c8\u662f\u5934\u4e0a\u811a \u4e0b\uff0c\u638c\u5c16\u95ea\u7535\u822c\u6325\u51fa\uff0c\u201c\u67af\u6728\u5bd2\u7af9\u201d\u518d\u4e5f\u60f3\u4e0d\u5230\u4ed6\u773c\u770b\u5df2\u662f\u5f3a\u5f29\u4e4b\u672a\u7684\u8eab\u8eaf\uff0c\u8fd8\u80fd\u518d\u51cc\u7a7a\u53d8 \u52bf\uff0c\u8981\u60f3\u6536\u638c\uff0c\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff0c\u4e24\u4eba\u624b\u638c\u65b9\u89c9\u4e00\u9ebb\uff0c\u201c\u91d1\u7ae5\u201d\u53cd\u8155\u4e00\u6293\uff0c\u5374\u53c8\u6263\u4f4f\u4e86\u4ed6\u4eec\u7684 \u8109\u95e8\uff0c\u201c\u67af\u6728\u5bd2\u7af9\u201d\u767b\u65f6\u5168\u8eab\u65e0\u529b\uff0c\u201c\u91d1\u7ae5\u201d\u957f\u7b11\u4e00\u58f0\uff0c\u53cc\u811a\u500f\u7136\u843d\u4e0b\uff0c\u811a\u5c16\u95ea\u7535\u822c\u5728\u4ed6\u4eec \u8170\u7554\u201c\u8f6f\u9ebb\u201d\u7a74\u4e0a\u8f7b\u8f7b\u4e00\u70b9\u3002\u201c\u5feb\u8baf\u201d\u82b1\u7389\u53ea\u89c1\u8fd9\u201c\u91d1\u7ae5\u201d\u7684\u8eab\u8eaf\u51cc\u7a7a\u7ffb\u98de\uff0c\u50cf\u662f\u80c1\u751f\u53cc\u7fc5 \u4e00\u822c\uff0c\u8f6c\u6298\u81ea\u5982\uff0c\u500f\u800c\u51fa\u638c\uff0c\u500f\u800c\u8e22\u817f\uff0c\u7ade\u4e0d\u77e5\u662f\u4f55\u95e8\u4f55\u6d3e\u7684\u6b66\u529f\uff1f\u4ed6\u5fc3\u5934\u65b9\u81ea\u5927\u9a87\uff0c\u5374\u89c1 \u201c\u91d1\u7ae5\u201d\u4e00\u58f0\u957f\u7b11\uff0c\u957f\u7b11\u58f0\u4e2d\uff0c\u201c\u67af\u6728\u5bd2\u7af9\u201d\u7684\u8eab\u8eaf\uff0c\u4fbf\u5df2\u865a\u8f6f\u5730\u5012\u5728\u5730\u4e0a\u3002\u53c8\u542c\u5f97\u201c\u7389 \u5973\u201d\u8f7b\u8f7b\u4e00\u7b11\uff0c\u4f38\u51fa\u7389\u638c\uff0c\u8f7b\u62cd\u4e24\u4e0b\uff0c\u5e26\u7740\u65e0\u6bd4\u8d5e\u8d4f\u656c\u4f69\u7684\u8bed\u6c14\uff0c\u62cd\u638c\u7b11\u9053\uff1a\u201c\u5341\u5e74\u4e0d\u89c1\u5927 \u54e5\u52a8\u624b\uff0c\u4eca\u6b21\u4e00\u6765\uff0c\u54c8\u54c8\u2014\u2014\u5a01\u98ce\u4ecd\u7136\u4e0d\u51cf\u3002\u201d\u8f6c\u5411\u88f4\u73cf\uff1a\u201c\u4f60\u770b\uff0c\u6211\u5927\u54e5\u8fd9\u4e24\u624b\uff0c\u7b97\u4e0d\u7b97 \u5f97\u4e0a\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u201d \u3000\u3000\u5979\u8eab\u9ad8\u4f53\u5927\uff0c\u53c8\u7c97\u53c8\u58ee\uff0c\u4f46\u8bf4\u8d77\u8bdd\u6765\uff0c\u5374\u50cf\u662f\u4e2a\u5929\u771f\u5a07\u61a8\u7684\u5c11\u5973\u3002 \u3000\u3000\u201c\u5feb\u8baf\u3001\u82b1\u7389\u53ea\u89c9\u5fc3\u91cc\u597d\u7b11\uff0c\u5374\u53c8\u7b11\u4e0d\u51fa\u6765\u3002\u53ea\u89c1\u201d\u91d1\u7ae5\u201c\u671b\u7740\u5730\u4e0b\u7684\u201d\u67af\u6728\u5bd2\u7af9\u201c\uff0c \u53c8\u671b\u4e86\u671b\u4e00\u8fb9\u7684\u53e6\u4e24\u5177\u5c38\u8eab\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u5411\u201d\u7389\u5973\u201c\u8bf4\u9053\uff1a\u201c\u9ebb\u70e6\u4f60\u628a\u8fd9\u4e24\u6839\u6728\u5934\u5e26\u8d70\uff0c\u770b \u6765\u8981\u59d4\u5c48\u4ed6\u4eec\u51e0\u5929\uff0c\u514d\u5f97\u4ed6\u4eec\u591a\u5634\u3002\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u51b7\u98a4\uff01 \u3000\u3000\u201c\u514d\u5f97\u4ed6\u4eec\u591a\u5634\u3002\u201d\u4ed6\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u82e5\u662f\u4ed6\u4eec\u77e5\u9053\u8fd8\u6709\u522b\u4eba\u770b\u5230\uff0c\u5c82\u975e\u4e00\u2014\u2014\u201d\u4ed6\u6697\u53f9 \u4e00\u58f0\uff0c\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\uff0c\u53ea\u89c1\u201c\u7389\u5973\u201d\u4e00\u624b\u4e00\u4e2a\uff0c\u5c06\u201c\u67af\u6728\u5bd2\u7af9\u201d\u7684\u8eab\u8eaf\uff0c\u631f\u5728\u80c1\u4e0b\uff0c\u53c8\u5bf9\u90a3 \u201c\u88f4\u5927\u5148\u751f\u201d\u4e00\u7b11\u9053\uff1a\u201c\u8ddf\u6211\u6765\uff0c\u6211\u6709\u597d\u4e1c\u897f\u7ed9\u4f60\u3002\u201d\u5f53\u5148\u8d70\u53bb\u3002 \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u770b\u5230\u4ed6\u4eec\u7684\u8eab\u5f62\uff0c\u6e10\u6e10\u5728\u591c\u8272\u4e2d\u6d88\u5931\uff0c\u65b9\u81ea\u900f\u4e86\u53e3\u957f\u6c14\uff0c\u54ea\u77e5\u5934\u4e0a\u7a81\u5730\u4f3c \u662f\u88ab\u4eba\u8f7b\u8f7b\u4e00\u5f39\uff0c\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u7ffb\u8eab\u8dc3\u8d77\uff0c\u4ea1\u547d\u72c2\u5954\uff0c\u5954\u51fa\u6570\u5341\u4e08\uff0c\u5077\u5077\u56de\u5934\u4e00\u770b\uff0c\u8eab\u540e\u7a7a \u7a7a\uff0c\u8fde\u4e2a\u9b3c\u5f71\u5b50\u90fd\u6ca1\u6709\uff0c\u4f38\u624b\u4e00\u6478\uff0c\u5934\u4e0a\u8fd8\u6302\u7740\u534a\u622a\u6811\u679d\uff0c\u4ed6\u53c8\u900f\u4e86\u53e3\u957f\u6c14\uff0c\u8eab\u4e0a\u5374\u5df2\u51fa\u4e86 \u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u4eca\u591c\u2014\u2014 \u3000\u3000\u867d\u7136\u5df2\u8fc7\u6f2b\u957f\u7684\u4e00\u540c\uff0c\u4f46\u4ed6\u56de\u60f3\u8d77\u6628\u591c\u7684\u906d\u9047\uff0c\u5fc3\u4e2d\u4ecd\u4e0d\u7981\u60ca\u6050\u4ea4\u96c6\uff0c\u5374\u53c8\u4e0d\u7981\u4e3a\u81ea\u5df1 \u7684\u201c\u8349\u6728\u7686\u5175\u201d\u7684\u60ca\u614c\u4e4b\u60c5\uff0c\u6697\u6697\u597d\u7b11\uff0c\u4ed6\u7684\u751f\u6d3b\u4e0e\u804c\u4e1a\uff0c\u672c\u60ef\u4e8e\u5728\u60ca\u6050\u4e2d\u8ba8\u751f\u6d3b\uff0c\u4e3a\u4e86\u63a2 \u542c\u522b\u4eba\u7684\u9690\u79c1\u4e0e\u79d8\u5bc6\uff0c\u6709\u65f6\u4ed6\u4e0d\u5f97\u4e0d\u4ed8\u51fa\u6781\u9ad8\u7684\u4ee3\u4ef7\uff0c\u662f\u4ee5\u6628\u591c\u7684\u906d\u9047\u867d\u7136\u4f7f\u4ed6\u60ca\u9a87\uff0c\u4eca\u591c \u4ed6\u4ecd\u7136\u4e0d\u60dc\u5192\u9669\uff0c\u8d70\u5230\u6628\u591c\u4ed6\u6f5c\u4f0f\u7684\u5730\u65b9\u6765\u3002 \u3000\u3000\u6b64\u523b\u2014\u2014 \u3000\u3000\u4ed6\u53c8\u7ad9\u5728\u6628\u591c\u7684\u6811\u4e0b\uff0c\u6708\u4eae\uff0c\u4ecd\u7136\u662f\u9ad8\u6302\u5728\u6628\u591c\u7684\u5730\u65b9\uff0c\u662f\u4ee5\u8fd9\u682a\u6811\u4e0b\uff0c\u4e5f\u4ecd\u7136\u662f\u90a3\u4e48 \u9634\u6697\u800c\u9690\u79d8\uff0c\u5c31\u50cf\u662f\u5927\u5730\u4e0a\u6700\u9634\u6697\u7684\u5730\u65b9\u4e00\u6837\uff0c\u4ed6\u653e\u5fc3\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u5373\u5c0f\u5fc3\u5730\u518d\u56db\u987e\u4e00\u773c\uff0c \u6811\u5e72\u662f\u7c97\u5927\u7684\uff0c\u4e71\u679d\u7ea0\u7ed3\u7684\u679d\u53f6\uff0c\u6709\u5982\u9999\u8364\u7684\u76d6\u5b50\u4f3c\u7684\uff0c\u6d53\u5bc6\u5730\u8986\u76d6\u7740\u6811\u5e72\uff0c\u5730\u4e0a\u957f\u8349\u4e1b \u751f\uff0c\u518d\u52a0\u4e0a\u7531\u5730\u5e95\u751f\u51fa\u7684\u5de8\u5927\u7684\u6811\u6839\uff0c\u4ed6\u518d\u6b21\u653e\u5fc3\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u771f\u662f\u4e2a\u5b89\u5168\u7684\u5730 \u65b9\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u4fbf\u53c8\u5728\u8fd9\u8db3\u591f\u4ee4\u4ed6\u81ea\u5df1\u653e\u5fc3\u7684\u5730\u65b9\u4f0f\u4e86\u4e0b\u6765\uff0c\u76ee\u5149\u5374\u56db\u4e0b\u8f6c\u52a8\u7740\uff0c\u5bfb\u627e\u7740\u4efb\u4f55 \u4e00\u4ef6\u503c\u5f97\u4ed6\u63a7\u6d4b\u7684\u76ee\u6807\uff5e\u98ce\u5439\u6797\u6728\uff0c\u7fa4\u661f\u95ea\u70c1\u3002 \u3000\u3000\u4ecd\u7136\u548c\u6628\u591c\u4e00\u6837\uff0c\u7f8e\u597d\u800c\u5b89\u9759\uff0c\u6625\u5929\u7684\u665a\u4e0a\uff0c\u672c\u5c31\u5927\u591a\u5982\u662f\u3002 \u3000\u3000\u826f\u4e45\uff0c\u826f\u4e45\u2026\u2026 \u3000\u3000\u4ed6\u5728\u5730\u4e0a\u4e0d\u5b89\u5730\u8f6c\u52a8\u7740\u8eab\u8eaf\uff01 \u3000\u3000\u201c\u600e\u5730\u6ca1\u6709\u4efb\u4f55\u4e8b\u53d1\u751f\uff1f\u201d\u4ed6\u8010\u5fc3\u7b49\u5f85\u7740\uff0c\u4f46\u56db\u4e0b\u4ecd\u7136\u662f\u90a3\u4e48\u5b89\u9759\uff0c\u4ed6\u5f00\u59cb\u4e0d\u8010\u70e6\uff1a \u201c\u4e5f\u8bb8\u4eca\u591c\u6ca1\u6709\u4e8b\u53d1\u751f\u5462\uff01\u6211\u53c8\u4f55\u82e6\u5728\u8fd9\u91cc\u50bb\u7b49\uff1f\u201d \u3000\u3000\u4f46\u4e00\u9762\u53c8\u5b89\u6170\u81ea\u5df1\uff1a\u201c\u518d\u7b49\u4e00\u4e0b\uff0c\u7b49\u5230\u6708\u4eae\u5782\u5230\u90a3\u9762\u7684\u6811\u68a2\uff0c\u6211\u5c31\u8d70\u3002\u201d \u3000\u3000\u7389\u5154\u897f\u6c89\uff0c\u6e10\u6e10\u5df2\u5782\u5230\u5c0f\u6eaa\u90a3\u9762\u7684\u4e00\u682a\u6768\u67f3\u68a2\u5934\u4e86\u3002 \u3000\u3000\u4ed6\u62ac\u5934\u770b\u4e86\u4e00\u773c\uff0c\u5931\u671b\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u5f00\u59cb\u89c9\u5f97\u81ea\u5df1\u771f\u662f\u4e2a\u5446\u5b50\uff0c\u7adf\u5c06\u5982\u6b64\u7f8e\u5999\u7684\u6625\u591c\uff0c \u6d6a\u8d39\u5728\u8fd9\u65e0\u7528\u7684\u7b49\u5f85\u91cc\u3002 \u3000\u3000\u201c\u5440\uff0c\u6211\u5e94\u8be5\u77e5\u9053\u4eca\u591c\u4f1a\u6709\u4ec0\u4e48\u4e8b\u53d1\u751f\u7684\uff0c\u96be\u9053\u522b\u4eba\u5c31\u4e3a\u8981\u7ed9\u6211\u770b\uff0c\u662f\u4ee5\u5c31\u975e\u8981\u5c06\u4e00\u5207 \u4e8b\u7684\u53d1\u751f\uff0c\u90fd\u642c\u5230\u8fd9\u91cc\u6765\u2014\u2014\u54fc\uff01\u6211\u771f\u662f\u4e2a\u5446\u5b50\uff0c\u4eac\u53e3\u5c0f\u7fe0\u8f69\u7684\u5e8a\uff0c\u4e0d\u6bd4\u8fd9\u91cc\u8212\u670d\u591a\u4e86 \u5417\uff1f\u201d \u3000\u3000\u4ed6\u6697\u81ea\u57cb\u6028\u7740\u81ea\u5df1\uff0c\u6b63\u60f3\u4ece\u5730\u4e0a\u722c\u8d77\u6765\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u4ed6\u76ee\u5149\u52a8\u5904\uff0c\u7a81\u5730\u77a5\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u7531\u8fdc\u5904\u884c\u6765\uff0c\u5b9a\u775b\u4e00\u671b\uff0c\u7adf\u662f\u90a3\u201c\u88f4\u5927\u5148\u751f\u201d\uff0c\u6b64\u523b \u4ed6\u4e00\u8def\u884c\u6765\uff0c\u4e00\u8def\u6325\u821e\u7740\u624b\u811a\uff0c\u7adf\u50cf\u662f\u75af\u5b50\u4e00\u6837\uff0c\u201c\u5feb\u8baf\u201d\u82b1\u7389\u5fc3\u795e\u672c\u81ea\u4e00\u60ca\uff0c\u4f46\u89c1\u4ed6\u7f13\u7f13 \u884c\u6765\uff0c\u53ea\u6709\u4e00\u4eba\uff0c\u5fc3\u91cc\u53c8\u4e0d\u89c9\u4e00\u5b9a\uff0c\u5c4f\u4f4f\u547c\u5438\uff0c\u5728\u5730\u4e0a\u671b\u4e86\u534a\u664c\uff0c\u53ea\u89c1\u4ed6\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u624b\u811a\u5374 \u4ecd\u4e0d\u505c\u5730\u821e\u52a8\u7740\uff0c\u9aa4\u773c\u671b\u53bb\uff0c\u4ecd\u662f\u6f2b\u65e0\u89c4\u5f8b\uff0c\u4f46\u770b\u4e86\u534a\u664c\uff0c\u53ea\u89c1\u4ed6\u5de6\u638c\u6c38\u8fdc\u662f\u7531\u5de6\u5411\u53f3\u5212\u4e2a \u5708\u5b50\uff0c\u7136\u540e\u7a81\u5730\u6536\u56de\uff0c\u53f3\u638c\u6c38\u8fdc\u662f\u7531\u5185\u5411\u5916\u5212\u4e2a\u5708\u5b50\uff0c\u7136\u540e\u4e2d\u5fc3\u4e00\u62f3\u6363\u51fa\uff0c\u8170\u8eab\u5411\u53f3\u4e00\u62e7\uff0c \u5de6\u65f6\u4e58\u52bf\u4e00\u649e\uff0c\u53f3\u817f\u5374\u53c8\u7a81\u5730\u8e22\u51fa\u3002 \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u5446\u5446\u770b\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u4ed6\u624b\u811a\u6325\u6765\u821e\u53bb\uff0c\u5374\u6c38\u8fdc\u53ea\u6709\u8fd9\u4e00\u5957\uff0c\u82b1\u7389\u8d8a\u770b\u8d8a\u89c9 \u597d\u7b11\uff0c\u6697\u91cc\u5bfb\u601d\u9053\uff1a\u201c\u8fd9\u96be\u9053\u4e5f\u7b97\u662f\u4ec0\u4e48\u62f3\u62db\u4e0d\u6210\uff1f\u771f\u4e8f\u4ed6\u4e0d\u77e5\u662f\u4ece\u54ea\u91cc\u5b66\u6765\u7684\uff1f\u8fd9\u6837\u7684\u62db \u5f0f\u82e5\u4e5f\u80fd\u4f24\u4eba\uff0c\u563f\u563f\u2014\u2014\u9664\u975e\u90a3\u4eba\u662f\u4e2a\u5446\u5b50\u3002\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u201c\u88f4\u5927\u5148\u751f\u201d\u5374\u4ecd\u5931\u9b42\u843d\u9b44\u5730\u6325\u821e\u7740\u624b\u811a\uff0c\u5df2\u81ea\u8d70\u5230\u4ed6\u773c\u524d\uff0c\u4ed6\u5fc3\u4e2d\u7a81\u5730\u4e00\u52a8\uff1a \u201c\u6211\u82e5\u662f\u5c06\u4ed6\u64d2\u83b7\uff0c\u9001\u5230\u201d\u9f99\u5f62\u516b\u638c\u2018\u90a3\u91cc\uff0c\u5c82\u975e\u6bd4\u4ec0\u4e48\u6d88\u606f\u90fd\u8981\u4ee4\u4ed6\u9ad8\u5174\uff0c\u81f3\u5c11\u2014\u2014\u81f3\u5c11 \u4e5f\u5f97\u6572\u4ed6\u51e0\u5343\u4e24\u94f6\u5b50\uff0c\u54c8\u54c8\u2014\u2014\u8fd9\u53ae\u624b\u5446\u811a\u7aff\uff0c\u53c8\u4e0d\u4f1a\u6b66\u529f\uff0c\u6211\u8fd8\u4e0d\u662f\u624b\u5230\u64d2\u6765\u3002\u201c\u4e8e\u662f\u4ed6 \u7a81\u5730\u8f7b\u53f1\u4e00\u58f0\uff1a\u201c\u505c\u4f4f\uff01\u201d \u3000\u3000\u88f4\u73cf\u6b63\u81ea\u6c89\u8ff7\u4e8e\u4e00\u79cd\u65b0\u5947\u7684\u5883\u754c\u4e2d\uff0c\u7a81\u5730\u542c\u5f97\u4e00\u58f0\u559d\u53f1\u4e4b\u58f0\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u53ea \u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u81ea\u8def\u8fb9\u6797\u4e2d\u9634\u6697\u4e4b\u5904\u63a0\u51fa\uff0c\u8fde\u5954\u5e26\u8df3\u5730\u8dd1\u4e86\u8fc7\u6765\uff0c\u53e3\u4e2d\u4e00\u9762\u559d\u9053\uff1a\u201c\u9601\u4e0b\u53ef\u662f \u88f4\u5927\u5148\u751f\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u60ca\uff0c\u53ea\u9053\u6b64\u4eba\u662f\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u624b\u4e0b\uff0c\u4f46\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u6b64\u4eba\u957f \u8eab\u7389\u7acb\uff0c\u8863\u88f3\u534e\u4e3d\uff0c\u800c\u6709\u8f7b\u529f\u4e0d\u9ad8\uff0c\u5374\u662f\u81ea\u5df1\u4ece\u672a\u89c1\u8fc7\u7684\u4eba\uff0c\u4ed6\u8fdf\u7591\u534a\u664c\uff0c\u7ec8\u4e8e\u6717\u58f0\u7b54\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u6b63\u662f\u88f4\u73cf\uff0c\u4e0d\u77e5\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u6697\u7b11\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u4ed6\u53eb\u88f4\u73cf\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5728\u4e0b\u9648\u5b50 \u5e73\uff0c\u4e45\u6155\u88f4\u5927\u5148\u751f\u82f1\u540d\uff0c\u53ea\u6068\u65e0\u7f18\u8bc6\u8346\uff0c\u5374\u60f3\u4e0d\u5230\u4eca\u65e5\u5728\u6b64\u5904\u5f97\u89c1\uff0c\u54c8\u54c8\u2014\u2014\u5728\u4e0b\u5b9e\u662f\u4e09\u751f \u6709\u5e78\u3002\u201d \u3000\u3000\u4ed6\u6df1\u8c0b\u8fdc\u8651\uff0c\u867d\u60f3\u4ee5\u8fd9\u201c\u88f4\u5927\u5148\u751f\u201d\u53bb\u5411\u201c\u9f99\u5f62\u516b\u638c\u201d\u6362\u94f6\u5b50\uff0c\u5374\u53c8\u4e0d\u60f3\u5f97\u7f6a\u201c\u795e\u624b\u201d \u6218\u98de\uff0c\u662f\u4ee5\u4fbf\u80e1\u4e71\u8c04\u4e86\u4e2a\u540d\u5b57\uff0c\u7eb5\u7136\u4ee5\u540e\u8fd9\u201c\u88f4\u5927\u5148\u751f\u201d\u4e0d\u6b7b\uff0c\u5374\u4e5f\u4e0d\u4f1a\u77e5\u9053\u81ea\u5df1\u7a76\u7adf\u662f \u8c01\u3002\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u81ea\u7136\u66f4\u4e0d\u4f1a\u77e5\u9053\u6b64\u4e8b\u662f\u8c01\u5e72\u7684\u3002 \u3000\u3000\u88f4\u73cf\u95fb\u8a00\u5374\u4e0d\u7981\u4e00\u6115\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u82f1\u540d\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u7591\u60d1\uff0c\u4f46\u89c1\u8fd9\u201c\u9648\u5b50\u5e73\u201d\u9762\u8c8c\u82f1\u4fca\uff0c\u8a00\u8bed\u4e0d\u4fd7\uff0c\u5fc3\u4e2d\u4ea6\u65e0\u6076\u611f\uff0c\u968f\u53e3\u6577\u884d\u9053\uff1a \u201c\u9601\u4e0b\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u4e00\u9762\u7f13\u7f13\u8d70\u5411\u88f4\u73cf\uff0c\u4e00\u9762\u56db\u987e\u5de6\u53f3\uff0c\u53ea\u89c1\u591c\u8272\u6df1\u6c89\uff0c\u518d\u65e0\u522b\u4eba\uff0c\u4ed6\u5fc3\u4e2d\u6697 \u6697\u9ad8\u5174\uff0c\u53e3\u4e2d\u5374\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u660e\u65e5\u6e05\u6668\uff0c\u4fbf\u662f\u9601\u4e0b\u626c\u540d\u5929\u4e0b\u4e4b\u65e5\uff0c\u9601\u4e0b\u4eca\u591c\u4ecd\u6709\u5174\u4f5c\u79c9\u70db\u4e4b \u591c\u6e38\uff0c\u54c8\uff01\u9601\u4e0b\u771f\u662f\u4e2a\u96c5\u4eba\u2026\u2026\u771f\u662f\u4e2a\u96c5\u4eba\u3002\u201d\u8bed\u58f0\u65b9\u4e86\uff0c\u7a81\u5730\u8fce\u9762\u4e00\u62f3\uff0c\u7b14\u76f4\u5730\u5411\u88f4\u73cf\u9f3b \u6881\u6b63\u4e2d\u6253\u53bb\uff0c\u4ed6\u6b66\u529f\u867d\u4e0d\u9ad8\uff0c\u5374\u4e5f\u7ec3\u8fc7\u4e09\u4e94\u5e74\u628a\u5f0f\uff0c\u8fd9\u4e00\u62f3\u6b63\u662f\u5f53\u65f6\u6c5f\u6e56\u6d41\u4f20\u6700\u5e7f\u7684\u5c11\u6797\u5916 \u5bb6\u201c\u5927\u6d2a\u62f3\u201d\u4e2d\u7684\u4e00\u62db\u201c\u5c01\u95e8\u95ed\u6237\u201d\uff0c\u5e38\u4eba\u82e5\u88ab\u8fd9\u4e00\u62f3\u51fb\u4e2d\u9f3b\u6881\uff0c\u767b\u65f6\u4f7f\u5f97\u5934\u660f\u773c\u82b1\uff0c\u518d\u4e5f \u6ca1\u6709\u62db\u67b6\u4e4b\u529b\uff0c\u662f\u4ee5\u8fd9\u4e00\u62f3\u624d\u6709\u201c\u5c01\u95e8\u201d\u4e4b\u79f0\u3002 \u3000\u3000\u88f4\u73cf\u89c1\u4ed6\u7b11\u541f\u541f\u5730\u5411\u81ea\u5df1\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u8fd8\u5728\u5947\u602a\uff0c\u81ea\u5df1\u4e0e\u8fd9\u4eba\u7d20\u4e0d\u76f8\u8bc6\uff0c\u600e\u5730\u4ed6\u7adf\u5982\u6b64\u606d \u7ef4\u81ea\u5df1\uff0c\u54ea\u77e5\u4ed6\u7adf\u7136\u7a81\u5730\u4e00\u62f3\u6253\u6765\uff0c\u88f4\u73cf\u5927\u60ca\u4e4b\u4e0b\uff0c\u5ff5\u52a8\u638c\u53d1\uff0c\u5de6\u638c\u7a81\u5730\u5411\u4e0a\u4e00\u53cd\uff0c\u5411\u5de6\u4e00 \u5708\u2014\u2014\u4ed6\u8fd9\u4e24\u591c\u4ee5\u6765\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u5728\u7ec3\u4e60\u8fd9\u4e00\u817f\u53cc\u62f3\uff0c\u6b64\u523b\u5fc3\u5ff5\u52a8\u5904\uff0c\u7ade\u987a\u7406\u6210\u7ae0\u5730\u65bd\u5c55\u4e86 \u51fa\u6765\uff0c\u53ea\u662f\u4ed6\u5fc3\u4e2d\u4ecd\u4e0d\u7981\u6709\u4e9b\u6000\u7591\uff0c\u4e0d\u77e5\u9053\u81ea\u5df1\u638c\u52bf\u8fd9\u8f7b\u8f7b\u4e00\u5708\uff0c\u80fd\u4e0d\u80fd\u62db\u67b6\u5f97\u4f4f\u4eba\u5bb6\u8fd9\u731b \u529b\u4e00\u62f3\uff1f \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u4e00\u62f3\u51fb\u51fa\uff0c\u5fc3\u4e2d\u53ea\u9053\u5c31\u51ed\u8fd9\u4e00\u62f3\uff0c\u4fbf\u5df2\u8db3\u591f\u5c06\u5bf9\u65b9\u8fd9\u4e2a\u770b\u6765\u5f31\u4e0d\u7981\u98ce\u7684\u5c11 \u5e74\u51fb\u5012\u3002 \u3000\u3000\u54ea\u77e5\u5bf9\u65b9\u624b\u638c\u8f7b\u8f7b\u4e00\u5708\uff0c\u5c31\u5df2\u5c06\u81ea\u5df1\u5c3d\u529b\u51fb\u51fa\u7684\u4e00\u62f3\uff0c\u5c01\u5728\u5916\u95e8\uff0c\u4ed6\u8fd9\u624d\u5927\u5403\u4e00\u60ca\uff0c\u5de6 \u62f3\u7acb\u523b\u968f\u52bf\u51fb\u51fa\uff0c\u54ea\u77e5\u88f4\u73cf\u6b64\u523b\u53f3\u638c\u7531\u5185\u5411\u5916\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u6b63\u81ea\u5c06\u4ed6\u8fd9\u4e00\u62f3\u6258\u4f4f\uff0c\u800c\u4e14\u6258\u7684 \u90e8\u4f4d\u5999\u5230\u6beb\u5dc5\uff0c\u7adf\u7136\u6b63\u597d\u6258\u7740\u4ed6\u7684\u8109\u95e8\u3002 \u3000\u3000\u82b1\u7389\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e0d\u7981\u6697\u9a82\u81ea\u5df1\u7cca\u6d82\uff0c\u660e\u77e5\u4eba\u5bb6\u6709\u8fd9\u4e00\u624b\uff0c\u600e\u5730\u5374\u8fd8\u8981\u5c06\u81ea\u5df1\u7684\u62f3\u5934\u9001\u4e0a \u53bb\uff0c\u7a81\u53c8\u60f3\u5bf9\u65b9\u4e0b\u4e00\u62db\u4e43\u662f\u4e00\u62f3\u81ea\u4e2d\u5fc3\u6363\u51fa\u2014\u2014\u8fd9\u5ff5\u5934\u5728\u4ed6\u7684\u5fc3\u4e2d\u4e00\u95ee\u800c\u8fc7\uff0c\u4ed6\u60ca\u9a87\u4ea4\u96c6\u4e4b \u4e0b\uff0c\u7acb\u523b\u4e3e\u624b\u62db\u67b6\uff0c\u54ea\u77e5\u81ea\u5df1\u7684\u624b\u638c\u4e00\u53ea\u88ab\u4eba\u5bb6\u5c01\u5728\u5916\u95e8\uff0c\u4e00\u53ea\u88ab\u4eba\u5bb6\u6258\u5728\u638c\u5fc3\uff0c\u660e\u77e5\u5bf9\u65b9 \u4e00\u62f3\u5373\u5c06\u5f53\u80f8\u51fb\u6765\uff0c\u81ea\u5df1\u4e0d\u4f46\u4e0d\u80fd\u62db\u67b6\uff0c\u751a\u81f3\u8fde\u62bd\u8eab\u800c\u9000\u90fd\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u970e\u773c\u4e4b\u95f4\uff0c\u4ed6\u53ea\u89c9\u5f97\u8033\u65c1\u8f70\u7136\u4e00\u58f0\uff0c\u80f8\u53e3\u4e00\u9707\uff0c\u5589\u5934\u4e00\u751c\uff0c\u773c\u524d\u4e00\u82b1\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u8eab\u8eaf \u604d\u604d\u60da\u60da\u5730\u79bb\u5730\u800c\u8d77\uff0c\u7136\u540e\u201c\u7830\u201d\u5730\u843d\u5230\u5730\u4e0a\u3002 \u3000\u3000\u88f4\u73cf\u53f3\u638c\u6258\u4f4f\u4ed6\u7684\u8109\u95e8\uff0c\u7136\u540e\u638c\u52bf\u4fbf\u6781\u81ea\u7136\u5730\u7531\u5916\u5411\u5185\u5708\u56de\uff0c\u5374\u6b63\u597d\u5c06\u4ed6\u7684\u5de6\u638c\u6258\u8d77\uff0c \u7b49\u5230\u88f4\u73cf\u4e00\u62f3\u6363\u51fa\uff0c\u5374\u89c1\u5bf9\u65b9\u773c\u7741\u7741\u5730\u671b\u7740\u81ea\u5df1\uff0c\u7adf\u50cf\u662f\u5446\u4e86\u4e00\u6837\uff0c\u63a5\u7740\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u5bf9 \u65b9\u9880\u957f\u7684\u8eab\u8eaf\uff0c\u7adf\u79bb\u5730\u98de\u8d77\u8fdc\u8fdc\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u6115\u4e86\u6115\uff0c\u751a\u81f3\u65e0\u6cd5\u60f3\u8c61\u81ea\u5df1\u8fd9\u4e00\u62db\u600e\u5730\u6709\u5982\u6b64\u5a01\u529b\uff0c\u62db\u5f0f\u65b9\u81ea\u4f7f\u5230\u4e00\u534a\uff0c\u5374\u5df2\u5c06\u522b\u4eba \u51fb\u5012\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u5374\u89c1\u90a3\u201c\u9648\u5b50\u5e73\u201d\u843d\u5230\u5730\u4e0a\u4e4b\u540e\uff0c\u7adf\u52a8\u4e5f\u4e0d\u518d\u52a8\u4e00\u4e0b\uff0c\u4ed6\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u5fd6 \u9053\uff1a\u201c\u96be\u9053\u6b64\u4eba\u88ab\u6211\u4e00\u62f3\u5c31\u51fb\u5f97\u660f\u4e86\uff1f\u201d\u5927\u6b65\u8dd1\u4e86\u8fc7\u53bb\uff0c\u4fef\u8eab\u4e00\u770b\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u8fd9\u201c\u9648\u5b50 \u5e73\u201d\u53cc\u775b\u7a81\u51fa\uff0c\u5634\u89d2\u6d41\u8840\uff0c\u9762\u76ee\u72f0\u72de\uff0c\u6709\u5982\u5389\u9b3c\uff0c\u4f38\u624b\u4e00\u63a2\u9f3b\u606f\uff0c\u5440\uff01\u8fd9\u201c\u9648\u5b50\u5e73\u201d\u7adf\u5df2\u6b7b \u4e86\u3002\u4ed6\u5446\u5446\u5730\u7ad9\u8d77\u6765\uff0c\u8111\u6d77\u4e2d\u4f46\u89c9\u6655\u7136\u4e00\u7247\uff0c\u4ec0\u4e48\u4e5f\u60f3\u4e0d\u8d77\u6765\uff0c\u53ea\u60f3\u5230\uff1a\u201c\u6211\u6740\u4e86\u4eba\uff01\u6211\u7adf \u7136\u6740\u4e86\u4eba\uff01\u201d\u76ee\u5149\u4e00\u5782\uff0c\u8fd9\u6b7b\u5c38\u65e0\u52a9\u5730\u5012\u5367\u7740\uff0c\u4fee\u957f\u7684\u56db\u80a2\uff0c\u4e11\u6076\u5730\u5206\u5728\u4e24\u65c1\uff0c\u6563\u843d\u7684\u8863 \u895f\uff0c\u843d\u4e0b\u4e00\u5c01\u5df2\u7ecf\u62c6\u5f00\u8fc7\u7684\u94f6\u5b50\uff0c\u5728\u6708\u5149\u4e0b\u95ea\u70c1\u7740\u7729\u76ee\u7684\u5149\u8292\u3002 \u3000\u3000\u201c\u7247\u523b\u4e4b\u524d\uff0c\u4ed6\u8fd8\u8c08\u7b11\u98ce\u751f\uff0c\u8a00\u8bed\u81ea\u82e5\uff0c\u4ed6\u8eab\u4f53\u5185\u8fd8\u5145\u6ee1\u7740\u751f\u547d\u7684\u6d3b\u529b\uff0c\u53ef\u662f\u2014\u2014\u6b64\u523b \u4ed6\u7adf\u7136\u6b7b\u4e86\uff0c\u8fd9\u5927\u597d\u7684\u751f\u547d\uff0c\u7adf\u662f\u5728\u6211\u7684\u624b\u4e2d\u6bc1\u53bb\u7684\u3002\u201d\u88f4\u73cf\u60b2\u54c0\u5730\u53f9\u606f\u7740\uff0c\u7f13\u7f13\u62ac\u8d77\u81ea\u5df1 \u7684\u624b\u638c\uff0c\u6b66\u529f\uff0c\u6b66\u529f\u539f\u6765\u662f\u4ef6\u8fd9\u4e48\u53ef\u6015\u7684\u4e8b\u5440\uff01 \u3000\u3000\u591c\u8272\u66f4\u6df1\uff0c\u4ed6\u5b50\u7136\u4ec1\u7acb\u5728\u6df1\u6c89\u7684\u591c\u8272\u91cc\uff0c\u671b\u7740\u9762\u524d\u7684\u5c38\u8eab\uff0c\u5fc3\u60c5\u662f\u6c89\u91cd\u800c\u60b2\u54c0\uff0c\u6c89\u91cd\u5f97 \u5c31\u6709\u5982\u8fd9\u6df1\u591c\u7684\u5bd2\u610f\u3002 \u3000\u3000\u76f4\u5230\u4e1c\u65b9\u7684\u7b2c\u4e00\u7ebf\u66d9\u5149\uff0c\u6084\u4fcf\u5730\u7167\u5c04\u5230\u4ed6\u7684\u80cc\u4e0a\uff0c\u4ed6\u4ecd\u7136\u60b2\u54c0\u5730\u7ad9\u5728\u8fd9\u91cc\uff0c\u4e5f\u8bb8\u4ed6\u8fd8\u5927 \u5e74\u8f7b\u4e86\u4e9b\uff0c\u4ed6\u8fd8\u4e0d\u77e5\u9053\u6c5f\u6e56\u4e2d\u7684\u4e89\u6597\uff0c\u6c38\u8fdc\u662f\u8fd9\u4e48\u6b8b\u9177\uff0c\u4ed6\u66f4\u4e0d\u77e5\u9053\u6b64\u523b\u8eba\u5728\u4ed6\u9762\u524d\u7684\u5c38 \u8eab\uff0c\u672c\u6765\u662f\u5c06\u4ed6\u770b\u6210\u4e00\u4ef6\u53ef\u4ee5\u4ea4\u6362\u94f6\u5b50\u7684\u8d27\u7269\uff0c\u4ed6\u82e5\u662f\u6ca1\u6709\u6bc1\u53bb\u522b\u4eba\u7684\u6027\u547d\uff0c\u90a3\u4e48\u522b\u4eba\u5c31\u4f1a \u6bc1\u53bb\u4ed6\u7684\uff0c\u800c\u4e14\u4e1d\u6beb\u4e0d\u4f1a\u89c9\u5f97\u60b2\u54c0\u548c\u6b49\u759a\u3002 \u3000\u3000\u4ed6\u82e5\u662f\u77e5\u9053\u8fd9\u4e9b\uff0c\u800c\u4e14\u80fd\u6df1\u5207\u4f53\u4f1a\u5230\u5176\u4e2d\u7684\u6df1\u610f\uff0c\u90a3\u4e48\uff0c\u4ed6\u6b64\u523b\u4e5f\u8bb8\u4f1a\u53d8\u5f97\u597d\u53d7\u4e9b\uff0c\u4f46 \u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u6b64\u523b\u4ecd\u7136\u662f\u5e78\u798f\u7684\uff0c\u56e0\u4e3a\u4ed6\u8fd8\u5e74\u8f7b\uff0c\u800c\u4e14\u5e74\u8f7b\u4eba\u6c38\u8fdc\u53ea\u4f1a\u61a7\u61ac\u7f8e\u4e3d\uff0c\u4e0d\u4f1a\u4f53\u9a8c \u4e11\u6076\uff0c\u6ca1\u6709\u4f53\u9a8c\u8fc7\u4e11\u6076\u4e0e\u6b8b\u9177\u7684\u4eba\uff0c\u4e0d\u662f\u5e38\u5e38\u90fd\u975e\u5e38\u5e78\u798f\u5417\uff1f \u7b2c\u4e03\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u4e03\u7ae0 \u3000\u3000\u7fcc\u65e5\u6e05\u6668\u3002 \u3000\u3000\u6587\u91c7\u98ce\u6d41\uff0c\u98ce\u7269\u9526\u7ea4\u7684\u540d\u57ce\uff0c\u201c\u4eac\u53e3\u201d\u2014\u2014\u57ce\u95e8\u65b9\u542f\uff0c\u4fbf\u4e0d\u65ad\u5730\u6709\u4e09\u4e94\u9a91\u58eb\uff0c\u626c\u97ad\u800c \u8fc7\u3002\u8fd9\u4e9b\u9a91\u58eb\u5e74\u9f84\u5404\u5f02\uff0c\u5f62\u6001\u76f8\u6b8a\uff0c\u8863\u5c65\u88c5\u675f\uff0c\u4ea6\u662f\u5404\u4e0d\u76f8\u540c\uff0c\u4f46\u9762\u4e0a\u5374\u6cf0\u534a\u900f\u8457\u7cbe\u608d\u7684\u795e \u8272\uff0c\u76ee\u4e2d\u66f4\u90fd\u662f\u795e\u5149\u5955\u5955\u3002\u51fa\u57ce\u5357\u53bb\uff0c\u4e00\u8def\u7b14\u76f4\u7684\u788e\u77f3\u8def\u4e0a\uff0c\u66f4\u53ef\u89c1\u5230\u8fd9\u4e9b\u9a91\u58eb\u7eb5\u9a91\u72c2\u7b11\uff0c \u6325\u5e3d\u6247\u51e4\u7684\u8c6a\u6001\uff0c\u53ea\u662f\u5728\u4ed6\u4eec\u7ecf\u8fc7\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5c71\u57ce\u8fb9\u4e00\u5bb6\u5c0f\u5c0f\u7684\u5ba2\u6808\u65f6\uff0c\u4ed6\u4eec\u7684\u72c2\u7b11\u8c6a\u6001\uff0c \u5374\u7a81\u5730\u6536\u655b\u4e86\u4e0d\u5c11\uff0c\u6709\u7684\u751a\u81f3\u505c\u4e0b\u9a6c\u6765\uff0c\u9a7b\u8db3\u9053\u65c1\uff0c\u5411\u8fd9\u5bb6\u5ba2\u6808\uff0c\u6295\u4ee5\u8be7\u5f02\u7684\u76ee\u5149\u3002 \u3000\u3000\u66ae\u6625\u521d\u590f\uff0c\u6e05\u6668\u7684\u9633\u5149\uff0c\u5b89\u7965\u5730\u6620\u7167\u5728\u8fd9\u5bb6\u5ba2\u6808\u9eef\u7070\u8272\u7684\u5c4b\u9876\u4e0a\uff0c\u4e00\u4e2a\u5e73\u51e1\u7684\u5e97\u4f19\uff0c\u7f13 \u7f13\u5730\u81ea\u90a3\u65b9\u81ea\u5f00\u4e86\u4e00\u534a\u7684\u5ba2\u95e8\u4e2d\u8d70\u4e86\u51fa\u6765\uff0c\u61c8\u6020\u5730\u6253\u626b\u7740\u95e8\u524d\u77f3\u9636\u4e0a\u7684\u7070\u57a2\uff0c\u4e24\u53ea\u65e9\u5df2\u7184\u706d \u4e86\u70db\u706b\u7684\u706f\u7b3c\uff0c\u9ad8\u6302\u5728\u95e8\u4e0a\uff0c\u4e0d\u4f4f\u5730\u968f\u7740\u5fae\u98ce\u6447\u66f3\u7740\u3002 \u3000\u3000\u8fd9\u5bb6\u5ba2\u6808\uff0c\u4fbf\u662f\u5982\u6b64\u5e73\u51e1\u800c\u5b89\u9759\u5730\u4ec1\u7acb\u5728\u8fd9\u6e05\u6668\u7684\u659c\u9633\u91cc\uff0c\u5c0f\u5c0f\u7684\u5c71\u57ce\u8fb9\uff0c\u6ca1\u6709\u4e1d\u6beb\u60f9 \u773c\u7684\u5730\u65b9\uff0c\u66f4\u6ca1\u6709\u4e1d\u6beb\u5f02\u5e38\u7684\u60c5\u4e8b\u3002 \u3000\u3000\u201c\u4f46\u662f\uff0c\u8fd9\u91cc\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u9759\uff1f\u201d \u3000\u3000\u626c\u97ad\u7eb5\u9a6c\u800c\u6765\u7684\u6c5f\u6e56\u8c6a\u58eb\uff0c\u8349\u6cfd\u82f1\u8c6a\uff0c\u5374\u5728\u6697\u4e2d\u5947\u602a\uff1a\u201c\u9f99\u5f62\u516b\u638c\u65e2\u7136\u6765\u4e86\uff0c\u800c\u4e14\u6536\u4e0b \u4e86\u2018\u795e\u624b\u2019\u6218\u98de\u7684\u62dc\u5e16\uff0c\u5374\u4e3a\u4ec0\u4e48\u76f4\u5230\u6b64\u523b\uff0c\u8fd8\u6ca1\u6709\u4e1d\u6beb\u52a8\u9759\uff1f\u201d \u3000\u3000\u4e8e\u662f\u805a\u96c6\u5728\u8fd9\u5bb6\u5ba2\u6808\u524d\u7684\u4eba\uff0c\u4fbf\u8d8a\u6765\u8d8a\u591a\uff0c\u5927\u5bb6\u90fd\u5728\u6697\u4e2d\u4f4e\u8bed\uff0c\u731c\u6d4b\u7740\u8fd9\u540d\u6ee1\u5929\u4e0b\u7684\u6b66 \u6797\u5927\u8c6a\u201c\u9f99\u5f62\u516b\u638c\u201d\u7684\u610f\u5411\uff0c\u597d\u5947\u5730\u7b49\u5f85\u7740\u8fd9\u5ba2\u6808\u4e2d\u7684\u53d8\u5316\uff0c\u4f46\u662f\uff0c\u76f4\u5230\u592a\u9633\u5df2\u5347\u8d77\u5f88\u9ad8\uff0c \u8fd9\u5ba2\u6808\u5374\u4ecd\u7136\u6ca1\u6709\u4e00\u4e1d\u53d8\u5316\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u8d70\u51fa\u6765\uff0c\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u6562\u8d70\u8fdb\u53bb\u3002\u7a81\u5730\u90a3\u5e97\u5c0f\u4e8c\u53ef \u8d70\u4e86\u51fa\u6765\uff0c\u7830\u5730\u4e00\u58f0\u5c06\u5e97\u95e8\u5173\u4e86\uff0c\u5ba2\u6808\u4e2d\u8d8a\u53d1\u6ca1\u6709\u58f0\u606f\uff0c\u7fa4\u8c6a\u5bf9\u671b\u51e0\u773c\u3002\u4e00\u4eba\u5ffd\u8f7b\u547c\u9053\uff1a \u201c\u91d1\u9e21\u5e2e\uff01\u201d \u3000\u3000\u4f17\u4eba\u4e0d\u7ea6\u800c\u540c\u5730\u599e\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u8fb9\u4e00\u6761\u7ebf\u4f3c\u5730\u5954\u6765\u83ab\u7ea6\u5341\u5339\u5065\u9a6c\uff0c\u9a6c\u4e0a\u9a91\u58eb\uff0c\u4ff1\u7a7f\u7740 \u4e94\u989c\u516d\u8272\u7684\u9526\u8863\uff0c\u5c31\u50cf\u662f\u516c\u9e21\u7684\u5c3e\u5df4\u4f3c\u7684\uff0c\u4e00\u4e2a\u4e2a\u633a\u80f8\u51f8\u809a\u5730\u9a70\u9a6c\u800c\u6765\uff0c\u9a70\u8fc7\u5ba2\u6808\u65f6\uff0c\u5634\u89d2 \u4e00\u6487\uff0c\u5237\u5730\u4e00\u6325\u9a6c\u97ad\uff0c\u5c31\u5954\u4e86\u8fc7\u53bb\u3002\u6700\u540e\u7684\u4e00\u9a91\uff0c\u5374\u662f\u4e00\u5339\u9a74\u5b50\uff0c\u9a74\u4e0a\u4e4b\u4eba\u5f62\u5bb9\u67af\u7626\u5e73\u51e1\uff0c \u7a7f\u7684\u8863\u670d\u66f4\u662f\u5e73\u5e73\u5e38\u5e38\uff0c\u8fd8\u65ad\u4e86\u4e00\u6761\u817f\uff0c\u4e00\u6761\u4e4c\u9ed1\u7684\u94c1\u62d0\uff0c\u6a2a\u653e\u5728\u978d\u524d\uff0c\u624b\u91cc\u6709\u6c14\u65e0\u529b\u5730\u6325 \u7740\u97ad\u5b50\uff0c\u8fdc\u8fdc\u8ddf\u5728\u540e\u9762\uff0c\u5c31\u50cf\u662f\u524d\u884c\u8fd9\u4e9b\u9526\u519c\u9a91\u58eb\u7684\u8ddf\u73ed\u4f3c\u7684\uff0c\u4f46\u9053\u65c1\u7fa4\u8c6a\u89c1\u4e86\u6b64\u4eba\uff0c\u5374\u6709 \u7684\u5782\u4e0b\u5934\u53bb\uff0c\u76ee\u4e0d\u659c\u89c6\uff0c\u6709\u7684\u5806\u4e0a\u6ee1\u8138\u7b11\u5bb9\uff0c\u8fdc\u8fdc\u547c\u9053\uff1a\u201c\u5411\u5927\u54e5\uff0c\u53ef\u597d\uff01\u201d \u3000\u3000\u6709\u7684\u4e0d\u8bc6\u6b64\u4eba\uff0c\u6b64\u523b\u5fc3\u4e2d\u65b9\u81ea\u4e00\u60ca\uff1a\u201c\u539f\u6765\u6b64\u4eba\u4fbf\u662f\u2018\u91d1\u9e21\u2019\u5411\u4e00\u557c\uff01\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u5750\u5728\u9a74\u80cc\u4e0a\uff0c\u4e24\u773c\u534a\u5f00\u534a\u95ed\uff0c\u50cf\u662f\u591a\u65e5\u672a\u66fe\u7761\u8fc7\u89c9\u4f3c\u7684\uff0c\u770b\u89c1\u6709 \u4eba\u62db\u547c\uff0c\u9762\u4e0a\u65b9\u81ea\u61d2\u6d0b\u6d0b\u5730\u9732\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u6709\u6c14\u65e0\u529b\u5730\u70b9\u9996\u79f0\u597d\uff0c\u4f38\u51fa\u624b\u4e2d\u97ad\u5b50\uff0c\u6307\u7740\u90a3\u5ba2 \u6808\u9053\uff1a\u201c\u8001\u6a80\u53ef\u662f\u5c31\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u4ed6\u867d\u5728\u95ee\u8bdd\uff0c\u5374\u6839\u672c\u4e0d\u5f85\u522b\u4eba\u56de\u7b54\uff0c\u70b9\u4e86\u70b9\u5934\u53c8\u9053\uff1a\u201c\u5404\u4f4d\u60f3\u5fc5\u5c31\u662f\u5728\u8fd9\u91cc\u7b49\u7740\u770b\u70ed\u95f9 \u7684\u5427\uff0c\u5509\uff01\u82e5\u6362\u4e86\u662f\u6211\uff0c\u5230\u6d6a\u83bd\u5c71\u5e84\u53bb\u770b\u8fd8\u4e0d\u662f\u4e00\u6837\u3002\u201d\u4e00\u6325\u9a6c\u97ad\uff0c\u5f97\u5f97\u5730\u8dd1\u8fc7\u53bb\u4e86\u3002\u7fa4\u8c6a \u4e0d\u7531\u5f97\u5bf9\u671b\u4e00\u773c\uff0c\u6709\u7684\u7acb\u523b\u968f\u540e\u8ddf\u53bb\uff0c\u6709\u7684\u53c8\u7b49\u4e86\u534a\u664c\uff0c\u5fc3\u91cc\u867d\u8fd8\u5947\u602a\uff0c\u600e\u5730\u8fd9\u201c\u9f99\u5f62\u516b \u638c\u201d\u76f4\u5230\u6b64\u523b\u8fd8\u6ca1\u6709\u52a8\u9759\uff0c\u5374\u4e5f\u59cb\u7ec8\u8010\u4e0d\u4f4f\uff0c\u7eb5\u9a91\u800c\u53bb\u3002 \u3000\u3000\u8fc7\u5c71\u57ce\u524d\u884c\u4e0d\u8fdc\uff0c\u524d\u9762\u5ffd\u5730\u73b0\u51fa\u4e00\u7247\u7eff\u6797\uff0c\u6797\u6728\u63a9\u6620\u4e2d\uff0c\u4e00\u7247\u7247\u5de8\u5b85\u5c4b\u5f71\uff0c\u9690\u7ea6\u53ef\u89c1\uff0c \u8fdc\u671b\u8fd8\u4e0d\u89c9\u5f97\uff0c\u8d70\u5230\u8fd1\u524d\uff0c\u53ea\u89c1\u8fd9\u7247\u5e84\u9662\u4e00\u9053\u9ad8\u5899\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u957f\uff0c\u56f4\u5899\u4e2d\u7684\u5c4b\u9876\uff0c\u66f4\u662f\u6809 \u6bd4\u9cde\u6b21\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\uff0c\u4e00\u6761\u788e\u77f3\u8def\u7a7f\u6797\u800c\u51fa\uff0c\u5374\u6709\u6570\u5341\u4e2a\u5f6a\u5f62\u5927\u6c49\u8083\u7acb\u5728\u6797\u5916\uff0c\u89c1\u4e86\u7fa4\u8c6a \u7b56\u9a6c\u800c\u6765\uff0c\u5c31\u5954\u8fc7\u6765\u63a5\u8fc7\u9a6c\u7586\uff0c\u89c1\u5230\u6709\u4eba\u5f92\u6b65\u800c\u6765\uff0c\u4ed6\u4eec\u4e5f\u5954\u8fc7\u6765\u63a5\u5f15\u3002\u7a7f\u8fc7\u7eff\u6797\uff0c\u91cc\u9762\u7684 \u5e84\u95e8\u524d\uff0c\u5374\u7acb\u7740\u51e0\u4e2a\u957f\u886b\u6c49\u5b50\uff0c\u542b\u7b11\u62f1\u624b\u8083\u5bb9\uff0c\u5e84\u95e8\u5185\u4e00\u7247\u504c\u5927\u7684\u9662\u5b50\uff0c\u6b64\u523b\u5df2\u6ee1\u5145\u4eba\u8bed\u7b11 \u58f0\uff0c\u9662\u5b50\u524d\u7684\u4e00\u95f4\u5927\u5385\uff0c\u4e24\u95f4\u504f\u5385\uff0c\u6b64\u523b\u4ea6\u662f\u4eba\u5934\u62e5\u6324\uff0c\u4f3c\u4e4e\u6c5f\u5357\u9053\u4e0a\u6240\u6709\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u4eca \u65e5\u4e0d\u5206\u9ed1\u767d\uff0c\u4e0d\u5206\u7537\u5973\uff0c\u90e8\u5df2\u5230\u4e86\u8fd9\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e2d\u6765\u3002 \u3000\u3000\u5ffd\u5730\u2014\u2014 \u3000\u3000\u6811\u6797\u5916\u201c\u5288\u5288\u5265\u5265\u201d\u5730\u54cd\u8d77\u4e00\u4e32\u97ad\u70ae\u3002 \u3000\u3000\u8fd9\u4e32\u70ae\u58f0\u65b9\u4f4f\uff0c\u5e84\u95e8\u524d\uff0c\u4fbf\u7acb\u523b\u63a5\u7740\u63a5\u8d77\u4e00\u4e32\uff0c\u8fd9\u79cd\u7cbe\u5236\u7684\u201c\u767e\u5b50\u5357\u97ad\u201d\uff0c\u58f0\u54cd\u5947\u5927\uff0c \u76f4\u9707\u5f97\u7fa4\u8c6a\u8033\u9f13\u9690\u9690\u53d1\u75db\uff0c\u63a5\u7740\u5927\u5385\u4e2d\u8d70\u51fa\u4e00\u6392\u6ee1\u8eab\u7ea2\u886b\u7684\u5927\u6c49\uff0c\u626c\u8d77\u624b\u4e2d\u6676\u5149\u95ea\u4eae\u7684\u5587 \u53ed\uff0c\u5927\u58f0\u5439\u594f\u8d77\u6765\uff0c\u53f7\u58f0\u4e00\u6b47\uff0c\u4e00\u4e2a\u771f\u7684\u662f\u201c\u8170\u5927\u5341\u56f4\uff0c\u80a9\u9614\u4e09\u505c\u201d\u7684\u5927\u6c49\uff0c\u5f80\u5385\u95e8\u4e00\u7ad9\uff0c \u5927\u559d\u9053\uff1a\u201c\u91d1\u9e21\u5e2e\u5411\u5e2e\u4e3b\u5230\uff01\u201d \u3000\u3000\u70ae\u58f0\u4e00\u6b47\uff0c\u4f17\u4eba\u8033\u6735\u65b9\u5f97\u4e00\u9759\uff0c\u4e00\u542c\u5230\u8fd9\u58f0\u5927\u559d\uff0c\u7981\u4e0d\u4f4f\u53c8\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u89c1\u5927\u5385\u4e2d\u53c8\u81ea \u8d70\u51fa\u4e00\u7fa4\u4eba\u6765\uff0c\u4e00\u4eba\u7d2b\u9762\u4fee\u9aef\uff0c\u4e00\u4eba\u8eab\u6750\u7626\u5c0f\uff0c\u4f46\u5374\u795e\u91c7\u5955\u5955\uff0c\u8fd8\u6709\u56db\u4e2a\u4e2d\u5e74\u8c6a\u58eb\uff0c\u4e00\u4e2a\u9762 \u8272\u60e8\u767d\u7684\u5c11\u5e74\uff0c\u5e76\u80a9\u7acb\u5728\u9636\u524d\uff0c\u7fa4\u8c6a\u6697\u4e2d\u4f20\u8bed\u3002 \u3000\u3000\u201c\u5411\u91d1\u9e21\u5f53\u771f\u6709\u51e0\u5206\u529b\u91cf\uff0c\u6218\u795e\u624b\u3001\u90a3\u98de\u8679\u3001\u83ab\u5bb6\u5144\u5f1f\u4eec\uff0c\u4e00\u8d77\u8fce\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u65b9\u843d\uff0c\u5e84\u95e8\u5916\u5df2\u6709\u4e00\u7fa4\u9526\u8863\u6c49\u5b50\uff0c\u62e5\u7740\u4e00\u4e2a\u65ad\u8db3\u6c49\u5b50\uff0c\u6162\u541e\u541e\u5730\u7f13\u6b65\u800c\u5165\uff0c\u6162\u541e\u541e \u5730\u7a7f\u8fc7\u4eba\u7fa4\uff0c\u8d70\u5230\u9636\u524d\uff0c\u90a3\u65ad\u8db3\u6c49\u5b50\u4e24\u773c\u4e00\u7ffb\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u6218\u5e84\u4e3b\u5c45\u7136 \u8fd8\u628a\u533a\u533a\u5728\u4e0b\u5f53\u505a\u4eba\u770b\uff0c\u4e0d\u8fc7\u52b3\u52a8\u5927\u9a7e\uff0c\u59d3\u5411\u7684\u5fc3\u91cc\u771f\u6709\u70b9\u4e0d\u5b89\u3002\u201d\u4e00\u3001\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149 \u4e00\u8f6c\uff0c\u634b\u987b\u5927\u7b11\u9053\uff1a\u201c\u5411\u5927\u54e5\u8a00\u91cd\u4e86\uff0c\u8bf7\u8fdb\uff01\u8bf7\u8fdb\uff01\u201d \u3000\u3000\u201c\u4e03\u715e\u201d\u83ab\u661f\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u6218\u5144\u5bf9\u5411\u5144\u5012\u771f\u662f\u7279\u522b\u4f18\u5f85\uff0c\u8fd8\u51c6\u5907\u4e86\u4e2a\u7279\u522b\u8212\u670d\u7684\u6905\u5b50\u7ed9 \u5411\u5144\u5750\u54e9\u3002\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u9762\u5bb9\u4e00\u53d8\uff0c\u76ee\u5149\u518d\u8f6c\uff0c\u5374\u4e5f\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6905\u5b50\u6bcb\u9700\uff0c\u6218\u5144\u5012 \u8981\u51c6\u5907\u51e0\u4e2a\u6f02\u4eae\u7684\u59d1\u5a18\u7ed9\u83ab\u5144\u5012\u662f\u771f\u7684\u3002\u201d\u62d0\u6756\u4e00\u70b9\uff0c\u8f7b\u8f7b\u63a0\u4e0a\u9636\u53bb\uff0c\u7fa4\u8c6a\u9762\u9762\u76f8\u89d1\uff0c\u90fd\u4e0d \u7981\u5947\u602a\uff01 \u3000\u3000\u8fd9\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e0e\u201c\u795e\u624b\u201d\u6218\u98de\u3001\u201c\u5317\u6597\u4e03\u715e\u201d\uff0c\u600e\u5730\u50cf\u6709\u4e9b\u4e0d\u5bf9\u52b2\u8d77\u6765\uff0c\u6c5f\u6e56\u98ce \u6ce2\uff0c\u6ce2\u8c32\u4e91\u8be1\uff0c\u4e0d\u662f\u5f53\u4e8b\u4eba\u5b9e\u5728\u662f\u96be\u4ee5\u731c\u6d4b\u7684\u3002 \u3000\u3000\u8fd9\u5176\u95f4\u7edc\u7ece\u4e0d\u7edd\u5730\u53c8\u6765\u4e86\u4e9b\u4eba\uff0c\u5ffd\u5730\u4e00\u5339\u5065\u9a6c\uff0c\u76f4\u9a70\u5927\u5385\uff0c\u9a6c\u4e0a\u4e00\u4e2a\u77ed\u886b\u9a91\u58eb\uff0c\u53cc\u624b\u5fae \u6309\u9a6c\u978d\uff0c\u5237\u5730\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u7b14\u76f4\u5730\u8d70\u4eba\u5927\u5385\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\u2014\u2014 \u3000\u3000\u53c8\u662f\u4e00\u4e32\u201c\u767e\u5b50\u5357\u97ad\u201d\u54cd\u8d77\uff0c\u9707\u8033\u7684\u97ad\u70ae\u58f0\u4e2d\uff0c\u201c\u795e\u624b\u201d\u6210\u98de\u3001\u201c\u5317\u6597\u4e03\u715e\u201d\u3001\u201c\u4e03\u5de7 \u8ffd\u9b42\u201d\u7adf\u53c8\u4e00\u8d77\u62a2\u6b65\u800c\u51fa\uff0c\u4e0d\u4f46\u8d70\u51fa\u5385\u5916\uff0c\u800c\u4e14\u4e00\u8d77\u8d70\u51fa\u5e84\u95e8\uff0c\u201c\u6218\u795e\u624b\u7adf\u7136\u4eb2\u8fce\u51fa\u5e84\u3002\u201d \u7fa4\u8c6a\u5fc3\u4e2d\u6b63\u81ea\u5927\u5947\uff1a\u201c\u8fd9\u53c8\u662f\u4ec0\u4e48\u4eba\u6765\u4e86\u5e7f\u53ea\u542c\u5385\u95e8\u524d\u7684\u5f6a\u5f62\u5de8\u6c49\u4e00\u58f0\u5927\u559d\uff1a\u201c\u98de\u9f99\u9556\u5c40\u5357 \u4e03\u5317\u516d\u5341\u4e09\u7701\u603b\u9556\u5934\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u5230\u2014\u2014\u6c5f\u5357\u2018\u864e\u90b1\u98de\u7075\u5821\u2019\u4e1c\u65b9\u4e94\u4fa0\u5230\u2014\u2014\u201d\u7fa4\u8c6a\u4e00 \u8d77\u76f8\u987e\u5931\u8272\uff1a\u201c\u539f\u6765\u662f\u2018\u9f99\u5f62\u516b\u638c\u2019\u6765\u4e86\u3002\u201d \u3000\u3000\u6b66\u6797\u4e2d\u4eba\u7684\u58f0\u540d\u5730\u4f4d\uff0c\u5f53\u771f\u662f\u7acb\u7aff\u89c1\u5f71\uff0c\u4e1d\u6beb\u4e0d\u80fd\u52c9\u5f3a\uff0c\u8fd9\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e0e\u201c\u4e1c\u65b9\u5144 \u5f1f\u201d\u4e00\u5230\uff0c\u5728\u573a\u7fa4\u8c6a\uff0c\u867d\u7136\u4ff1\u662f\u4e45\u8d70\u6c5f\u6e56\uff0c\u4e0d\u81f3\u8702\u62e5\u5230\u95e8\u53e3\uff0c\u4f46\u4e00\u4e2a\u4e2a\u4e5f\u4ff1\u90fd\u662f\u5f15\u9888\u800c\u671b\u3002 \u53ea\u89c1\u5e84\u95e8\u5916\u4e00\u9635\u4eba\u58f0\u7b11\u8bed\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u62f1\u624b\u8083\u5bb9\uff0c\u4e00\u4e2a\u8eab\u6750\u867d\u4e0d\u751a\u9ad8\uff0c\u4f46\u6c14\u52bf\u5374\u6781\u8f69\u6602\u7684 \u8001\u8005\uff0c\u4e0e\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\uff0c\u76ee\u5149\u70af\u70af\u7684\u5c11\u5e74\uff0c\u5f53\u5148\u8d70\u4e86\u8fdb\u6765\uff0c\u56fd\u5149\u56db\u4e0b\u4e00\u8f6c\uff0c\u7acb\u523b\u6717\u58f0\u7b11\u9053\uff1a \u201c\u6a80\u660e\u4e00\u6b65\u6765\u8fdf\uff0c\u6709\u52b3\u5404\u4f4d\u4e45\u5019\uff0c\u6055\u7f6a\uff01\u6055\u7f6a\uff01\u201d \u3000\u3000\u7ad9\u5728\u524d\u9762\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u81ea\u7136\u7acb\u523b\u542b\u7b11\u8c26\u8c22\uff0c\u7ad9\u5728\u540e\u9762\u7684\u4eba\uff0c\u83ab\u4e0d\u4e00\u4f38\u5927\u59c6\u6307\uff0c\u6697\u4e2d\u8d5e \u9053\uff1a\u201c\u4e0d\u7ba1\u59d3\u6a80\u7684\u4e3a\u4eba\u5230\u5e95\u600e\u6837\uff0c\u5c31\u770b\u4eba\u5bb6\u8fd9\u4efd\u6c14\u6d3e\uff0c\u5c31\u4e0d\u6127\u662f\u5927\u4eba\u7269\uff0c\u54ea\u91cc\u50cf\u90a3\u59d3\u5411\u7684\uff0c \u4eba\u5bb6\u53ea\u8981\u4e00\u6367\u4ed6\uff0c\u4ed6\u5c31\u4e0a\u4e86\u5929\u4f3c\u7684\uff0c\u8fde\u773c\u775b\u90fd\u751f\u5230\u989d\u89d2\u9876\u4e0a\u53bb\u4e86\u3002\u201d \u3000\u3000\u6709\u7684\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6a80\u660e\u65c1\u8fb9\u90a3\u4e2a\u4e0d\u4f4f\u62f1\u624b\uff0c\u6ee1\u9762\u542b\u7b11\u7684\u5c0f\u4f19\u5b50\uff0c\u5c31\u662f\u2018\u98de\u7075\u5821\u2019\u7684 \u4e1c\u65b9\u94c1\uff0c\u4f60\u770b\u4eba\u5bb6\uff0c\u4e0d\u8bf4\u4ed6\u5e08\u5085\u4e0d\u662f\u6606\u4ed1\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u5c31\u8bf4\u4ed6\u7239\u7239\u5427\u3002\u563f\uff01\u4f60\u770b\u4eba\u5bb6\uff0c\u8fd8\u4e0d \u662f\u5ba2\u5ba2\u6c14\u6c14\uff0c\u65af\u65af\u6587\u6587\u7684\uff0c\u5582\uff01\u6211\u8bf4\u54b1\u4eec\u90a3\u4f4d\u2018\u88f4\u5927\u5148\u751f\u2019\uff0c\u53ef\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u6837\u4e00\u4f4d\u89d2 \u8272\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u7b49\u4eba\uff0c\u5df2\u966a\u7740\u201c\u9f99\u5f62\u516b\u638c\u201d\u3001\u201c\u4e1c\u65b9\u5144\u5f1f\u201d\u4ee5\u53ca\u201c\u5feb\u9a6c\u795e \u5200\u201d\uff0c\u201c\u5366\u638c\u201d\u7b49\u4eba\u8d70\u5165\u4e86\u5927\u5385\uff0c\u7f13\u6b65\u8d70\u5165\u6b63\u5385\uff0c\u77f3\u9636\u4e0a\u90a3\u4e00\u6392\u7ea2\u886b\u5927\u6c49\uff0c\u5de6\u624b\u53c9\u8170\uff0c\u53f3\u624b \u4e00\u65cb\uff0c\u638c\u4e2d\u91d1\u53f7\uff0c\u5728\u9633\u5149\u4e0b\u95ea\u95ea\u751f\u5149\uff0c\u8fde\u9000\u4e09\u6b65\uff0c\u9000\u5230\u6a90\u4e0b\u8ba9\u5f00\u4e00\u6761\u901a\u8def\uff0c\u7136\u540e\u201c\u545c\u201d\u5730\u4e00 \u58f0\uff0c\u53f7\u89d2\u4e4b\u58f0\uff0c\u53c8\u518d\u5927\u4f5c\uff0c\u90a3\u5f6a\u5f62\u5de8\u6c49\u9762\u4e0a\u6728\u7136\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u53e3\u4e2d\u53c8\u81ea\u5927\u559d\u9053\uff1a\u201c\u5404\u4f4d\u4eba \u5e2d\u3002\u201d \u3000\u3000\u53f7\u58f0\u4e94\u54cd\uff0c\u4e24\u4fa7\u504f\u5385\u4e2d\uff0c\u62a2\u6b65\u8d70\u51fa\u5341\u4f59\u4e2a\u957f\u886b\u6c49\u5b50\uff0c\u5230\u5904\u8083\u5ba2\u4eba\u5ea7\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u5237\u5730 \u6247\u5f00\u624b\u4e2d\u957f\u6247\uff0c\u6247\u9762\u6c34\u5e73\uff0c\u81ea\u5de6\u81f3\u53f3\uff0c\u7f13\u7f13\u5212\u4e86\u4e2a\u534a\u5706\uff0c\u5ea7\u4e0a\u7b11\u8bed\u4eba\u58f0\uff0c\u987f\u65f6\u4ff1\u5bc2\u3002 \u3000\u3000\u53ea\u89c1\u3002\u2018\u795e\u624b\u201c\u6218\u98de\u7f13\u7f13\u8f6c\u8fc7\u8eab\u53bb\uff0c\u5728\u8eab\u524d\u7684\u4e00\u5f20\u4f9b\u684c\u524d\u606d\u606d\u656c\u656c\u884c\u8fc7\u5927\u793c\uff0c\u4e00\u4e3e\u8d77\u684c \u4e0a\u7684\u4e00\u676f\u96c4\u9ec4\u827e\u9152\uff0c\u53cc\u624b\u7aef\u676f\uff0c\u9ad8\u4e3e\u8fc7\u9876\uff0c\u8f6c\u8eab\u9053\uff1a\u201c\u8bf7\uff01\u201d\u4ef0\u9996\u4e00\u4e8e\u800c\u5c3d\u3002 \u3000\u3000\u6b63\u5385\u5185\u5916\uff0c\u504f\u5385\u524d\u540e\uff0c\u91cc\u91cc\u5916\u5916\u56db\u5341\u4f59\u684c\u4e0a\u7684\u9752\u74f7\u9152\u676f\uff0c\u7acb\u523b\u5168\u88ab\u7aef\u8d77\uff0c\u559d\u5f97\u6d93\u6ef4\u4e0d \u5269\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u4e00\u7b11\uff0c\u518d\u6b21\u659f\u6ee1\u676f\u4e2d\u4e4b\u9152\uff0c\u4e00\u4e3e\u676f\u9053\uff1a\u201c\u4eca\u65e5\u6b23\u9022\u4f73\u8282\uff0c\u4f60\u6211\u5144\u5f1f\u6b22 \u805a\u4e00\u5802\uff0c\u5144\u5f1f\u6211\u6709\u4e2a\u5929\u5927\u7684\u559c\u8baf\uff0c\u8981\u544a\u8bc9\u5404\u4f4d\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u56db\u5385\u53c8\u590d\u54cd\u8d77\u4f4e\u8bed\u4e4b\u58f0\u3002 \u201c\u9f99\u5f62\u516b\u638c\u201d\u7aef\u5750\u5982\u5c71\uff0c\u76ee\u5149\u56db\u626b\uff0c\u5634\u89d2\u9690\u6cdb\u7b11\u5bb9\uff0c\u76ee\u5149\u4e2d\u5374\u65e0\u4e1d\u6beb\u7b11\u610f\u3002 \u3000\u3000\u53ea\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u5e72\u54b3\u4e24\u58f0\uff0c\u56db\u4e0b\u53c8\u590d\u5bc2\u7136\uff0c\u8fd9\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u7684\u4e3b\u4eba\uff0c\u4eca\u65e5\u9022\u559c\u4e8b\uff0c\u7cbe \u795e\u50cf\u662f\u5206\u5916\u723d\u6717\uff0c\u63a5\u7740\u6717\u58f0\u7b11\u9053\uff1a\u201c\u6c5f\u5357\u6b66\u6797\uff0c\u8fd1\u6570\u5341\u5e74\u6765\uff0c\u7fa4\u96c4\u7eb7\u8d77\uff0c\u82f1\u8c6a\u8f88\u51fa\uff0c\u5927\u6709\u6614 \u5e74\u6625\u79cb\u6218\u56fd\u65f6\uff0c\u7fa4\u96c4\u5272\u636e\u4e4b\u52bf\uff0c\u6b64\u79cd\u60c5\u52bf\uff0c\u867d\u53ef\u6fc0\u4eba\u5411\u4e0a\uff0c\u4e00\u4e89\u96c4\u957f\uff0c\u4f46\u5374\u7a0d\u5acc\u6563\u4e71\uff0c\u662f\u4ee5 \u5185\u4e0d\u80fd\u606f\u5185\u4e71\uff0c\u5916\u4e0d\u80fd\u5fa1\u5916\u4fae\uff0c\u4ee5\u81f4\u2026\u2026\u563f\u563f\uff01\u201d \u3000\u3000\u4ed6\u563f\u563f\u5e72\u7b11\u6570\u58f0\uff0c\u76ee\u5149\u4e00\u8198\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4eca\u5929\u5230\u6b64\u95f4\u6765\u7684\u5168\u90fd\u4e0d\u662f \u5916\u4eba\uff0c\u4f11\u602a\u5144\u5f1f\u6211\u53e3\u6ca1\u906e\u62e6\uff0c\u8981\u8bf4\u51e0\u53e5\u80ba\u8151\u4e4b\u8a00\u3002\u201d \u3000\u3000\u4ed6\u7a81\u5730\u9762\u8272\u4e00\u6b63\uff0c\u6b63\u8272\u9053\uff1a\u201c\u4eca\u65e5\u6b66\u6797\u60c5\u52bf\uff0c\u5317\u91cd\u4e8e\u5357\uff0c\u6b64\u4e43\u65e0\u5eb8\u8bb3\u8a00\u4e4b\u4e8b\uff0c\u4f60\u6211\u5144\u5f1f \u5982\u518d\u4e0d\u77e5\u632f\u4f5c\uff0c\u53ea\u6015\u6b64\u540e\u60c5\u51b5\u66f4\u52a3\u3002\u8fd9\u4e95\u975e\u662f\u8bf4\u6c5f\u5357\u6c5f\u6e56\u8c6a\u6770\u4e0d\u5982\u4e24\u6cb3\u6b66\u6797\u5065\u8005\uff0c\u800c\u53ea\u662f\u4f60 \u6211\u5144\u5f1f\u4e0d\u77e5\u56e2\u7ed3\u800c\u5df2\uff0c\u6709\u9053\u662f\u4e24\u4eba\u540c\u5fc3\uff0c\u5176\u5229\u65ad\u91d1\uff0c\u662f\u4ee5\u5144\u5f1f\u6211\u4e45\u9274\u4e8e\u6b64\uff0c\u4fbf\u548c\u2018\u4e03\u5de7\u8ffd \u9b42\u2019\u90a3\u5927\u54e5\u3001\u83ab\u5bb6\u8bf8\u5144\u5f1f\uff0c\u82e6\u5fc3\u5bfb\u8bbf\uff0c\u60f3\u627e\u4e00\u4e2a\u667a\u5fb7\u517c\u5907\u4e4b\u4eba\uff0c\u6765\u505a\u6c5f\u5357\u6b66\u6797\u7fa4\u9f99\u4e4b\u9996\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u653e\u4e0b\u9152\u676f\uff0c\u4fa7\u9996\u5411\u8eab\u65c1\u7684\u4e1c\u65b9\u5144\u5f1f\u4f4e\u8bed\u9053\uff1a\u201c\u4eba\u9053\u2018\u795e\u624b\u2019\u6218 \u98de\u6587\u6b66\u5168\u624d\uff0c\u662f\u4e2a\u89d2\u8272\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u5f53\u771f\u662f\u76db\u540d\u4e4b\u4e0b\u65e0\u865a\u58eb\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u8bf4\u5f97\u867d\u8f7b\uff0c\u4f46\u5374\u6545 \u610f\u8ba9\u201c\u795e\u624b\u201d\u6218\u98de\u80fd\u591f\u542c\u89c1\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u4e0a\u5fae\u5fae\u4e00\u7b11\uff0c\u50cf\u662f\u9887\u4e3a\u5f97\u610f\uff0c\u5fc3\u4e2d\u5374\u6697\u5fd6\uff1a\u201c\u4eca\u65e5\u4e4b\u4f1a\uff0c\u8fd9\u2018\u9f99\u5f62\u516b \u638c\u2019\u5c45\u7136\u6562\u6765\uff0c\u5f53\u771f\u662f\u6709\u51e0\u5206\u80c6\u6c14\uff0c\u53ea\u662f\u4ed6\u65e2\u6562\u95ef\u5230\u8fd9\u91cc\u6765\uff0c\u5fc5\u975e\u5168\u65e0\u4ed7\u6043\u2014\u2014\u201d\u4e00\u5ff5\u81f3 \u6b64\uff0c\u7a81\u5730\u5411\u8eab\u540e\u4e00\u4e2a\u957f\u886b\u6c49\u5b50\u4f4e\u8bed\u4e24\u53e5\uff0c\u8f6c\u8eab\u63a5\u9053\uff1a\u201c\u5144\u5f1f\u6211\u867d\u7136\u624d\u80fd\u9c9c\u8584\uff0c\u4f46\u83ab\u5bb6\u5144\u5f1f\uff0c \u90a3\u6c0f\u5927\u54e5\uff0c\u5374\u90fd\u662f\u5929\u7eb5\u5947\u624d\u7684\u7edd\u9876\u4eba\u7269\uff0c\u987b\u7ecf\u4ed6\u4eec\u5bfb\u5f97\u4e4b\u4eba\uff0c\u5fc5\u5b9a\u4e0d\u81f4\u4ee4\u5404\u4f4d\u5144\u5f1f\u5931\u671b\uff0c\u4eca \u65e5\u5144\u5f1f\u6211\u5728\u6b64\u95f4\u8bf7\u5404\u4f4d\u524d\u6765\uff0c\u4e00\u6765\u662f\u8bb8\u4e45\u672a\u4e0e\u5404\u4f4d\u89c1\u9762\uff0c\u9887\u4e3a\u60f3\u5ff5\uff0c\u518d\u8005\u5374\u662f\u8981\u5404\u4f4d\u6765\u89c1\u89c1 \u6211\u4eec\u672a\u6765\u7684\u76df\u4e3b\u2018\u88f4\u5927\u5148\u751f\u2019\u3002\u201d \u3000\u3000\u8bed\u58f0\u65b9\u4e86\uff0c\u56db\u4e0b\u7acb\u523b\u62a5\u4ee5\u5982\u96f7\u638c\u58f0\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u5e26\u5fae\u7b11\uff0c\u8f6c\u8eab\u4e00\u62db\u624b\uff0c\u95e8\u5916\u7684\u7ea2\u8863 \u5927\u6c49\u8eab\u8eaf\u4e00\u62e7\uff0c\u53f7\u89d2\u5bf9\u5411\u5385\u95f4\uff0c\u7a81\u5730\u5439\u594f\u8d77\u6765\uff0c\u5341\u4f59\u4e2a\u957f\u886b\u6c49\u5b50\u6025\u6b65\u800c\u51fa\uff0c\u5341\u4f59\u4e32\u201c\u767e\u5b50\u5357 \u97ad\u201d\u540c\u65f6\u71c3\u8d77\uff0c\u4f46\u89c1\u706b\u5149\u70b9\u70b9\uff0c\u7eb8\u5c51\u98de\u821e\uff0c\u53f7\u89d2\u4e4b\u58f0\uff0c\u66f4\u662f\u9707\u8033\u6b32\u804b\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u7f13\u7f13\u56de \u8f6c\u8eab\u6765\uff0c\u4f38\u51fa\u5de6\u624b\uff0c\u6307\u5411\u5927\u5385\u540e\u7684\u4e00\u6247\u95e8\u6237\uff0c\u6717\u7b11\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u2014\u2014\u201d\u76ee\u4e0b\u4f17\u8c6a\u7684\u6570\u767e\u9053\u76ee \u5149\uff0c\u4e0d\u7981\u968f\u7740\u4ed6\u7684\u624b\u6307\uff0c\u4e00\u8d77\u5411\u90a3\u6247\u95e8\u6237\u671b\u53bb\u3002 \u3000\u3000\u97ad\u70ae\u53f7\u89d2\u4e4b\u58f0\u66f4\u54cd\uff0c\u6de1\u9752\u8272\u7684\u95ee\u5e18\u5f80\u4e0a\u4e00\u6380----\u6218\u98de\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u95e8\u53e3\uff0c\u5782\u9996\u6717\u58f0 \u9053\uff1a\u201c\u6c5f\u5357\u6b66\u6797\u540c\u9053\uff0c\u606d\u8fce\u88f4\u5927\u5148\u751f\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u3001\u4e1c\u65b9\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e2d\u4e0d\u7ea6\u800c\u540c\u5730\u6697\u4e2d\u731c\u6d4b\uff1a\u201c\u4e0d\u77e5\u8fd9\u2018\u88f4\u5927\u5148\u751f\u2019 \u7a76\u7adf\u662f\u4ec0\u4e48\u5927\u4eba\u7269\uff1f\u201d\u4e00\u8d77\u8f6c\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u95e8\u5e18\u6380\u5f00\u540e\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u95e8\u5916\u65b9\u81ea\u5f2f\u5904\u8d70\u51fa\u4e00\u4e2a \u4eba\u6765\uff0c\u4f17\u8c6a\u76ee\u5149\u671b\u5904\uff0c\u53ea\u89c1\u6b64\u4eba\u76ee\u5149\u6d41\u8f6c\uff0c\u9f3b\u633a\u7709\u626c\uff0c\u6ee1\u9762\u4ff1\u662f\u7cbe\u7075\u8df3\u8131\u4e4b\u8272\uff0c\u201c\u516b\u5366\u638c\u201d \u67f3\u8f89\u5fc3\u4e2d\u4e00\u60ca\uff0c\u76b1\u7709\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u662f\u90a3\u2018\u4e03\u5de7\u7ae5\u5b50\u2019\u5434\u9e23\u4e16\u4e48\uff1f\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u8bed\u58f0\u672a\u4e86\uff0c\u5434\u545c\u4e16\u8eab\u8eaf\u5df2\u81ea\u5411\u95e8\u8fb9\u4e00\u95ea\uff0c\u95e8\u5185\u53c8\u81ea\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u53f7\u89d2\u3001\u97ad\u70ae\u4e4b \u58f0\uff0c\u500f\u7136\u987f\u4f4f\uff0c\u90a3\u5f6a\u5f62\u5de8\u6c49\u653e\u5f00\u5589\u5499\uff0c\u5927\u558a\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u2014\u2014\u5230\uff01\u201d\u7fa4\u8c6a\u5fc3\u5934\u4ff1\u90fd\u4e00\u51db\uff0c \u4e0d\u7531\u81ea\u4e3b\u5730\u957f\u8eab\u800c\u8d77\uff0c\u4e00\u8d77\u5b9a\u6674\u5411\u8fd9\u6c5f\u5357\u7eff\u6797\u7684\u76df\u4e3b\u671b\u53bb\u2014\u2014\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u4ea6\u81ea \u7ad9\u8d77\u8eab\u5b50\uff0c\u56de\u9996\u671b\u53bb\u2014\u2014\u8fd9\u4e00\u671b\u4e4b\u4e0b\uff0c\u4ed6\u5fc3\u5934\u5374\u4e0d\u7981\u4e3a\u4e4b\u6817\u7136\u4e00\u9707\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u8131\u53e3\u5524\u51fa \u58f0\u6765\uff0c\u4ed6\u867d\u7136\u9634\u83ba\u6df1\u6c89\uff0c\u4f46\u6b64\u523b\u5374\u4ecd\u4e0d\u7981\u9762\u5bb9\u5927\u53d8\uff01 \u3000\u3000\u4e1c\u65b9\u94c1\u76ee\u5149\u4e00\u8f6c\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u6b64\u4eba\u795e\u91c7\u7167\u4eba\uff0c\u800c\u4e14\u66f4\u662f\u4fca\u7f8e\uff0c\u770b\u6765\u5f53\u662f\u4e2a\u4eba\u624d\uff0c\u53ea\u662f\u2014 \u2014\u5e74\u7eaa\u5374\u4f3c\u592a\u8f7b\u4e86\u4e9b\u3002\u201d \u3000\u3000\u53ea\u89c1\u201c\u88f4\u5927\u5148\u751f\u201d\u5728\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u6276\u6301\u4e4b\u4e0b\uff0c\u5f97\u5f97\u8d70\u5165\u5927\u5385\uff0c\u76ee\u5149\u51dd\u7136\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730 \u671b\u7740\u524d\u65b9\uff0c\u9762\u4e0a\u66f4\u662f\u6728\u65e0\u8868\u60c5\uff0c\u53ea\u6709\u773c\u89d2\u7709\u5cf0\uff0c\u4f3c\u4e4e\u5fae\u5fae\u542b\u655b\u7740\u51e0\u5206\u5fe7\u90c1\u4e4b\u6001\u3002 \u3000\u3000\u53f7\u89d2\u97ad\u70ae\u4e4b\u58f0\u5df2\u606f\uff0c\u6b64\u523b\u5927\u5385\u4e2d\u7ade\u5bc2\u65e0\u4eba\u8bed\uff0c\u9759\u5f97\u8fde\u5f7c\u6b64\u547c\u5438\u4e4b\u58f0\u90fd\u4e92\u76f8\u53ef\u95fb\uff0c\u5385\u5185\u7fa4 \u8c6a\uff0c\u6b64\u523b\u5fc3\u4e2d\u65e2\u662f\u60ca\u5f02\uff0c\u53c8\u89c9\u5947\u602a\uff0c\u6570\u767e\u9053\u76ee\u5149\uff0c\u773c\u7741\u7741\u5730\u671b\u7740\u88f4\u73cf\uff0c\u800c\u88f4\u73cf\u5374\u50cf\u662f\u5168\u90e8\u4e0d \u77e5\u9053\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e0e\u4e1c\u65b9\u5144\u5f1f\u4e2d\u7684\u4e1c\u65b9\u94c1\u3001\u91d1\u9e21\u5411\u4e00\u557c\uff0c\u4ee5\u53ca\u4e03\u5de7\u8ffd\u9b42\u3001\u83ab\u6c0f\u56db\u715e\u3001\u201c\u795e \u624b\u201d\u6218\u98de\uff0c\u5750\u5728\u5f53\u4e2d\u4e3b\u5e2d\uff0c\u6b64\u523b\u53ea\u89c1\u8fd9\u201c\u88f4\u5927\u5148\u751f\u201d\uff0c\u7adf\u5df2\u8d70\u5230\u81ea\u5df1\u8eab\u4fa7\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u8f7b\u54b3\u4e00 \u58f0\uff0c\u5fc3\u4e2d\u5ffd\u7136\u4e00\u52a8\uff0c\u7acb\u523b\u5782\u4e0b\u5934\u53bb\uff0c\u53ea\u542c\u201c\u795e\u624b\u201d\u6218\u98de\u4e3e\u676f\u9053\uff1a\u201c\u4f60\u6211\u5144\u5f1f\u4e14\u656c\u88f4\u5927\u5148\u751f\u4e00 \u676f\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u62ff\u4e86\u9152\u676f\uff0c\u4ea4\u5230\u88f4\u73cf\u624b\u4e0a\uff0c\u88f4\u73cf\u832b\u7136\u63a5\u8fc7\uff0c\u4ef0\u9996\u4e00\u5343\u800c\u5c3d\uff0c\u5434\u545c\u4e16\u6697\u53f9\u4e00\u58f0\uff0c\u4ed6 \u8fd9\u4e24\u5929\u6765\uff0c\u603b\u89c9\u5f97\u88f4\u73cf\u50cf\u662f\u6709\u4e9b\u795e\u4e0d\u5b88\u820d\uff0c\u4eca\u65e5\u6e05\u6668\uff0c\u89c1\u5230\u88f4\u73cf\u7684\u6837\u5b50\uff0c\u66f4\u50cf\u662f\u832b\u7136\u4e00\u7247\uff0c \u4ed6\u5fc3\u4e2d\u65e2\u662f\u63d0\u5fc3\uff0c\u53c8\u89c9\u7740\u6025\uff0c\u751f\u6015\u88f4\u73cf\u4e00\u4e2a\u4e0d\u597d\uff0c\u51fa\u4e86\u5dee\u9519\uff0c\u4fbf\u65e0\u6cd5\u5f25\u8865\uff0c\u4ed6\u6b64\u523b\u5012\u6709\u4e9b\u540e \u6094\uff0c\u4e0d\u8be5\u6002\u607f\u6765\u505a\u6b64\u4e8b\u4e86\u3002 \u3000\u3000\u56db\u5ea7\u7fa4\u96c4\u8f70\u7136\u4e00\u58f0\uff0c\u996e\u4e0b\u676f\u4e2d\u4e4b\u9152\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u7f13\u7f13\u653e\u4e0b\u9152\u676f\uff0c\u76ee\u5149\u5982\u9e70\uff0c\u81ea\u7fa4\u8c6a\u6bcf \u4eba\u7684\u9762\u4e0a\u626b\u8fc7\uff0c\u7a81\u5730\u53cc\u638c\u4e00\u62cd\uff0c\u4e24\u4e2a\u957f\u886b\u6c49\u5b50\uff0c\u81ea\u5385\u540e\u62a2\u6b65\u800c\u51fa\uff0c\u624b\u91cc\u6367\u7740\u4e00\u65b9\u9c9c\u7ea2\u5f69\u7f0e\u5f80 \u201c\u795e\u624b\u201d\u6218\u98de\u8eab\u4e0a\u4e00\u62ab\u3002 \u3000\u3000\u6218\u98de\u9762\u5bd2\u5982\u6c34\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u53c8\u53cc\u638c\u4e00\u62cd\u3002 \u3000\u3000\u5927\u5385\u524d\u7a81\u5730\u4e00\u58f0\u725b\u545c\uff0c\u53ea\u89c1\u56db\u4e2a\u7cbe\u8d64\u7740\u4e0a\u8eab\u7684\u5f6a\u5f62\u5927\u6c49\uff0c\u8170\u95f4\u56f4\u7740\u4e00\u6761\u8840\u7ea2\u5f69\u5e26\uff0c\u56db\u4eba \u624b\u4e2d\u7adf\u5404\u6301\u4e00\u53ea\u725b\u817f\uff0c\u5c06\u4e00\u6761\u4ea6\u662f\u8eab\u62ab\u5f69\u5e26\uff0c\u89d2\u6302\u7ea2\u4e2d\u7684\u7261\u725b\uff0c\u9ad8\u4e3e\u8fc7\u9876\uff0c\u62ac\u4e86\u8fdb\u6765\uff0c\u90a3\u6761 \u725b\u867d\u7136\u6012\u543c\u8fde\u8fde\uff0c\u4f46\u88ab\u8fd9\u56db\u4eba\u9ad8\u9ad8\u62ac\u8d77\uff0c\u7adf\u662f\u4e1d\u6beb\u52a8\u5f39\u4e0d\u5f97\uff0c\u65e5\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8fd9\u56db\u4eba\u8eab\u4e0a\u7684 \u808c\u8089\uff0c\u6709\u5982\u7c9f\u7c73\u4e00\u822c\uff0c\u7c92\u7c92\u51f8\u8d77\uff0c\u6d41\u8f6c\u4e0d\u606f\uff0c\u811a\u4e0b\u4e0d\u505c\uff0c\u5c06\u8fd9\u6761\u7261\u725b\u7b14\u76f4\u5730\u62ac\u5165\u5927\u5385\uff0c\u505c\u5728 \u90a3\u6905\u684c\u4e4b\u524d\u3002\u8eab\u62ab\u5f69\u7f0e\u7684\u201c\u795e\u624b\u201d\u6218\u98de\u7f13\u7f13\u8f6c\u8eab\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684\u96c4\u9ec4\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u53e6\u4e24\u4e2a\u8d64\u8eab\u7cfb\u5f69\u7684\u5927\u6c49\uff0c\u624b\u6367\u4e00\u4e2a\u9880\u5927\u91d1\u76c6\uff0c\u98de\u6b65\u800c\u51fa\uff0c\u5355\u819d\u70b9\u5730\uff0c\u66f2\u8db3\u8dea\u5728\u6218\u98de\u8eab \u524d\uff0c\u6218\u98de\u4e00\u624b\u7f13\u7f13\u81ea\u4f9b\u684c\u524d\u62ff\u8d77\u4e00\u628a\u7cbe\u5149\u53d1\u4eae\u7684\u89e3\u8155\u5c16\u5200\uff0c\u84e6\u7136\u201c\u6251\u201d\u5730\u4e00\u58f0\uff0c\u7adf\u5c06\u65b9\u624d\u542b \u5728\u53e3\u4e2d\u7684\u96c4\u9ec4\u70c8\u9152\uff0c\u5f20\u53e3\u4e00\u55b7\u800c\u51fa\uff0c\u55b7\u5728\u725b\u9996\u4e4b\u4e0a\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u51fa\u624b\u5982\u98ce\uff0c\u624b\u4e2d\u5c16\u5200\uff0c\u95ea \u7535\u822c\u5728\u725b\u9888\u4e0b\u4e00\u5212\u2014\u2014\u5239\u90a3\u4e4b\u95f4\uff0c\u53ea\u89c1\u9c9c\u8840\u5982\u6cc9\uff0c\u6f02\u6d8c\u800c\u51fa\uff0c\u90a3\u4e24\u4e2a\u5927\u6c49\u56db\u624b\u4e00\u62ac\uff0c\u62ac\u8d77\u624b \u4e2d\u91d1\u76c6\uff0c\u63a5\u4f4f\u725b\u8840\u3002\u8fd9\u6761\u5176\u58ee\u65e0\u6bd4\u7684\u7261\u725b\uff0c\u6b64\u523b\u54c0\u9e23\u4e0d\u7edd\uff0c\u5168\u8eab\u4e0d\u4f4f\u5730\u6323\u624e\u3002\u53ea\u89c1\u90a3\u56db\u6761\u5927 \u6c49\u795e\u529b\u60ca\u4eba\uff0c\u6b64\u523b\u7adf\u4ecd\u5c79\u7acb\u5982\u5c71\uff0c\u4e1d\u7eb9\u4e0d\u52a8\uff0c\u53ea\u89c1\u4ed6\u4eec\u9762\u65e5\u795e\u60c5\u4e4b\u4e2d\uff0c\u5374\u4e5f\u4e0d\u7981\u900f\u51fa\u4e86\u51e0\u5206 \u5403\u529b\u4e4b\u6001\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6210\u98de\u624b\u8155\u4e00\u626c\uff0c\u624b\u4e2d\u5c16\u5200\uff0c\u7adf\u81ea\u7535\u5c04\u800c\u51fa\uff0c\u8fd9\u67c4\u5c16\u5200\uff0c\u5200\u8eab\u7565\u5f2f\uff0c\u6b64\u523b\u88ab\u201c\u795e \u624b\u201d\u6218\u98de\u968f\u624b\u629b\u51fa\uff0c\u5728\u7a7a\u4e2d\u5212\u4e86\u4e2a\u5706\u5f27\uff0c\u7adf\u53c8\u95ee\u7535\u822c\u8f6c\uff0c\u6251\u5730\u4e00\u58f0\u56de\u5934\u6765\uff0c\u63d2\u5728\u725b\u8eab\u540e\u80a1 \u4e0a\u3002 \u3000\u3000\u7261\u725b\u53c8\u4e00\u58f0\u54c0\u9e23\u3002 \u3000\u3000\u4f46\u5239\u90a3\u4e4b\u95f4\uff0c\u8fd9\u54c0\u9e23\u4e4b\u58f0\uff0c\u4fbf\u53c8\u88ab\u97ad\u70ae\u53f7\u89d2\u4ee5\u53ca\u559d\u91c7\u4e4b\u58f0\u6df9\u6ca1\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5634\u89d2\u4e00\u626c\uff0c\u7f13\u7f13\u8f6c\u8eab\uff0c\u5fae\u4e00\u62ac\u624b\u2014\u2014\u5239\u90a3\u4e4b\u95f4\uff0c\u97ad\u70ae\u3001\u53f7\u89d2\uff0c\u4ee5\u53ca\u559d\u91c7\u4e4b \u58f0\uff0c\u4fbf\u53c8\u4e00\u8d77\u987f\u4f4f\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u4e0a\u5fae\u5e26\u7b11\u5bb9\uff0c\u867d\u4ecd\u662f\u65e0\u52a8\u4e8e\u8877\u4e4b\u6001\uff0c\u4f46\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u6697\u6697 \u5403\u60ca\u3002 \u3000\u3000\u53ea\u542c\u201c\u795e\u624b\u201d\u6218\u98de\u6717\u58f0\u9053\uff1a\u201c\u51e1\u6211\u6c5f\u5357\u540c\u9053\uff0c\u4e14\u6765\u996e\u6211\u8840\u9152\uff0c\u8d3a\u6211\u76df\u4e3b\u4e4b\u751f\u3002\u201d\u4e3e\u8d77\u9152 \u676f\uff0c\u5728\u91d1\u76c6\u52fa\u4e86\u4e00\u676f\u8840\u9152\uff0c\u53cc\u624b\u6367\u81f3\u88f4\u73cf\u8eab\u524d\uff0c\u7b49\u5230\u88f4\u73cf\u4e00\u996e\u800c\u5c3d\uff0c\u4ed6\u53c8\u81ea\u996e\u4e86\u4e00\u676f\uff0c\u7136\u540e \u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u3001\u201c\u5317\u6597\u4e03\u715e\u201d\uff0c\u4ea6\u5404\u5404\u79bb\u5ea7\u800c\u8d77\uff0c\u52fa\u4e86\u4e00\u676f\u8840\u9152\uff0c\u4ef0\u9996\u4e00\u5e72\u800c\u5c3d\uff0c\u56db\u4e0b\u7fa4 \u8c6a\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u6709\u7684\u65e9\u5df2\u79bb\u5ea7\u800c\u51fa\uff0c\u6392\u6210\u4e00\u5217\uff0c\u7b49\u996e\u8840\u9152\uff0c\u6709\u7684\u5fc3\u4e2d\u867d\u8fd8\u6709\u51e0\u5206\u8fdf\u7591\uff0c\u4f46\u5fc3 \u4e0b\u8fde\u8fde\u6570\u8f6c\uff0c\u4ea6\u662f\u5404\u65e0\u5f02\u8bc6\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7aef\u5750\u4e0d\u52a8\uff0c\u773c\u89d2\u779f\u5904\uff0c\u53ea\u89c1\u88f4\u73cf\u76ee\u5149\u4e4b\u4e2d\uff0c\u7adf\u4ecd\u7136\u662f\u832b\u7136\u4e00\u7247\uff0c\u76f4\u5230 \u6b64\u523b\uff0c\u8fd8\u6ca1\u6709\u53d1\u73b0\u4ed6\u7684\u5b58\u5728\uff0c\u6a80\u660e\u5fc3\u4e2d\u65e2\u60ca\u4e14\u5947\uff0c\u4ed6\u65e0\u6cd5\u60f3\u8c61\u8fd9\u5c11\u5e74\u5728\u79bb\u5bb6\u4e00\u8f7d\u4e4b\u4e2d\uff0c\u600e\u5730 \u6709\u5982\u6b64\u5947\u9047\uff0c\u4eca\u65e5\u7adf\u505a\u4e86\u6c5f\u5357\u7eff\u6797\u9053\u7684\u76df\u4e3b\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u6570\u8f6c\uff0c\u5374\u4ecd\u7136\u7aef\u5750\u5982\u6545\uff0c\u4efb\u4f55\u4eba\u90fd\u96be\u4ee5\u4ece\u4ed6\u9762\u76ee\u4e4b\u4e0a\uff0c\u770b\u900f\u4ed6\u7684\u5fc3\u610f\u3002\u53ea\u89c1\u5927\u5385 \u4e4b\u4e2d\uff0c\u7fa4\u8c6a\u591a\u534a\u5df2\u79bb\u5ea7\u800c\u8d77\uff0c\u90a3\u6761\u7261\u725b\u60f3\u5fc5\u662f\u56e0\u6d41\u8840\u8fc7\u591a\uff0c\u6b64\u523b\u5df2\u505c\u6b62\u4e86\u6323\u624e\uff0c\u53ea\u662f\u9ed8\u7136\u5782 \u7740\u5934\uff0c\u9759\u7b49\u7740\u5b83\u6b8b\u4f59\u7684\u751f\u547d\u548c\u7740\u9c9c\u8840\u6d41\u51fa\u2026\u2026\u76f4\u5230\u6d41\u5c3d\uff01 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u80a9\u62ab\u5f69\u7f0e\uff0c\u8d1f\u624b\u800c\u7acb\uff0c\u5634\u89d2\u5fae\u626c\uff0c\u76ee\u5149\u8f6c\u4e86\u4e24\u8f6c\uff0c\u7a81\u5730\u95ea\u7535\u822c\u76f4\u77aa\u5230\u4e86 \u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u8eab\u4e0a\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5411\u5927\u54e5\u4eca\u65e5\u6765\u6b64\uff0c\u662f\u4ee5\u6c5f\u5357\u9053\u4e0a\u540c\u6e90\u7684\u8eab\u4efd\u53c2\u4e0e\u6b64\u4e8b\u7684 \u5462\uff0c\u6291\u6216\u662f\u2014\u2014\u54fc\u54fc\uff01\u5144\u5f1f\u6211\u5012\u8981\u5411\u5927\u54e5\u7ed9\u6211\u4e00\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u6d53\u7709\u4e00\u626c\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u4eca\u65e5\u6765\u6b64\uff0c\u53ea\u662f\u6765\u770b\u770b\u70ed\u95f9\u7684\uff0c\u96be\u9053 \u4e0d\u53ef\u4ee5\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4eca\u65e5\u6211\u6c5f\u752b\u6b66\u6797\u540c\u9053\uff0c\u6b43\u8840\u4e3a\u76df\uff0c\u8a93\u5171\u751f\u6b7b\uff0c\u5411\u5144\u4ea6\u662f \u6c5f\u5357\u9053\u4e0a\u540c\u6e90\uff0c\u5374\u53ea\u662f\u6765\u770b\u770b\u70ed\u95f9\uff0c\u563f\u563f\uff01\u8fd9\u5374\u4f7f\u5144\u5f1f\u6211\u6709\u4e9b\u4e0d\u61c2\u4e86\u3002\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u607b\u607b\u4e00\u58f0\u51b7\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u96be\u9053\u51e1\u662f\u6c5f\u5357\u6b66\u6797\u9053\uff0c\u5c31\u5168\u90fd\u8981\u52a0\u76df\u6b64\u4f1a\u7684 \u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u540c\u68ee\u5bd2\u5982\u94c1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f17\u5bb6\u5144\u5f1f\uff0c\u8a93\u5171\u751f\u6b7b\uff0c\u662f\u53cb\u4fbf\u975e\u654c\uff0c\u662f\u654c\u4fbf\u975e \u53cb\uff0c\u8fd9\u5176\u95ee\u7edd\u65e0\u9009\u62e9\u4f59\u5730\u3002\u662f\u53cb\u662f\u654c\uff0c\u4f46\u51ed\u5411\u5144\u4e00\u8a00\u800c\u62e9\u3002\u5411\u5144\u82e5\u8bf4\u4eca\u65e5\u6b64\u6765\u53ea\u662f\u770b\u770b\u70ed \u95f9\uff0c\u8bf4\u6765\u4fbf\u6765\uff0c\u8bf4\u53bb\u4fbf\u53bb\u2014\u2014\u54c8\u54c8\uff0c\u563f\u563f\uff0c\u54fc\u54fc\uff01\u8fd9\u5374\u672a\u514d\u5c06\u8fd9\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u770b\u5f97\u592a\u4e0d\u503c\u94b1 \u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u5230\u4e2d\u9014\uff0c\u7a81\u5730\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\uff0c\u81ea\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u8eab\u4e0a\uff0c\u63a0\u5411\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u8bdd \u58f0\u4e00\u4e86\uff0c\u53cc\u624b\u4e00\u8d1f\uff0c\u76ee\u5149\u51dd\u6ce8\uff0c\u5b9b\u5982\u5229\u526a\uff0c\u9759\u5f85\u5411\u4e00\u557c\u7684\u4e0b\u6587\u3002 \u3000\u3000\u5385\u4e0a\u7fa4\u8c6a\uff0c\u6570\u767e\u9053\u76ee\u5149\uff0c\u6b64\u523b\u4e0d\u7981\u53c8\u90fd\u96c6\u4e2d\u5230\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u8eab\u4e0a\uff0c\u4f46\u89c1\u5411\u4e00\u557c\u624b\u629a\u94c1 \u62d0\uff0c\u6b63\u895f\u800c\u5750\uff0c\u9762\u5bd2\u5982\u6c34\uff0c\u773c\u5e18\u5fae\u5782\u3002\u7fa4\u8c6a\u7684\u76ee\u5149\uff0c\u4e5f\u50cf\u662f\u5982\u4e2d\u5e7b\u9b54\uff0c\u968f\u7740\u4ed6\u9edd\u9ed1\u7684\u624b\u638c\uff0c \u5728\u90a3\u9edd\u9ed1\u7684\u94c1\u62d0\u4e0a\u79fb\u52a8\u7740\uff0c\u7531\u5de6\u81f3\u53f3\uff0c\u7531\u53f3\u81f3\u5de6\u2026\u2026 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u5927\u5385\u4e2d\u9634\u6697\u7684\u4e00\u89d2\u91cc\uff0c\u7f13\u7f13\u8d70\u51fa\u4e00\u4e2a\u5f62\u5bb9\u7325\u7410\u7684\u7626\u5c0f\u6c49\u5b50\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u7adf\u7a81\u5730\u4ef0\u9996\u72c2\u7b11 \u8d77\u6765\u3002 \u3000\u3000\u6b64\u523b\u5f53\u771f\u662f\u5251\u62e8\u5f29\u5f20\uff0c\u4e00\u89e6\u5373\u53d1\u4e4b\u9645\uff0c\u7fa4\u8c6a\u9aa4\u7136\u542c\u5230\u8fd9\u7b49\u7b11\u58f0\uff0c\u5fc3\u4e2d\u4e0d\u7981\u9f50\u90fd\u4e00\u60ca\uff0c\u8f6c \u76ee\u671b\u53bb\uff0c\u53ea\u53f2\u8fd9\u6c49\u5b50\u4e00\u6447\u4e09\u6446\u5730\u8d70\u4e86\u51fa\u6765\uff0c\u72c2\u9053\uff1a\u201c\u662f\u654c\u4fbf\u975e\u53cb\uff0c\u662f\u53cb\u4fbf\u975e\u654c\u2014\u2014\u54c8\u54c8\uff0c\u6218 \u5e84\u4e3b\uff0c\u96be\u9053\u6c5f\u5357\u6b66\u6797\u4e2d\u3002\u4e0d\u613f\u5949\u8fd9\u201d\u88f4\u5927\u5148\u751f\u201c\u4e3a\u76df\u4e3b\u7684\u4eba\uff0c\u4fbf\u5168\u90fd\u662f\u654c\u800c\u975e\u53cb\u4e48\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u5fc3\u4e2d\u53c8\u4e00\u60ca\uff1a\u201c\u6b64\u4eba\u662f\u8c01\uff1f\u7adf\u6562\u5728\u6218\u795e\u624b\u9762\u524d\u5982\u6b64\u653e\u8086\u72c2\u8a00\uff01\u201d\u53ea\u8d1d\u6b64\u4eba\u8c8c\u4e0d\u60ca \u4eba\uff0c\u795e\u6001\u7325\u7410\uff0c\u5728\u5ea7\u7fa4\u8c6a\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u4eba\u8ba4\u5f97\u6b64\u4eba\u662f\u8c01\u7684\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u8f69\uff0c\u5fc3\u5ff5\u6570\u8f6c\uff0c\u7a81\u5730\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u96be\u9053\u9601\u4e0b\u8fd8\u6709\u4ec0\u4e48\u5f02\u8bae\u4e0d\u6210\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5144\u5f1f\u6df7\u996d\u5403\uff0c\u8bb2\u7a76\u7684\u662f\u767d\u5200\u5b50\u8fdb\uff0c\u7ea2\u5200\u5b50\u51fa\uff0c\u67aa\u5c16\u4e0a\u62fc \u9aa8\uff0c\u5200\u5934\u4e0b\u8214\u8840\uff0c\u7eb5\u7136\u662f\u5200\u5c71\u706b\u6d77\uff0c\u67aa\u6797\u5251\u6811\uff0c\u8981\u4f60\u51fa\u51fa\u5165\u4eba\u4f60\u4e5f\u4e0d\u80fd\u6709\u4e00\u4e1d\u4e00\u6beb\u7684\u542b\u7cca\uff0c \u6218\u5e84\u4e3b\uff0c\u4f60\u8bf4\u8fd9\u8bdd\u53ef\u662f\uff1f\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u5f62\u5bb9\u867d\u7325\u7410\uff0c\u8a00\u8bed\u5374\u6781\u7075\u4fbf\uff0c\u4e00\u53e3\u6c14\u8bf4\u4e0b\u6765\uff0c\u8fde\u7599\u7629\u90fd\u6ca1\u6709\u4e00\u4e2a\uff0c\u201c\u795e\u624b\u201d\u6218\u98de \u53c8\u7709\u5fae\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u6309\u8bf4\u6218\u5e84\u4e3b\u66ff\u6211\u4eec\u9009\u51fa\u7684\u76df\u4e3b\uff0c\u7edd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5dee \u9519\uff0c\u53ef\u662f\u6211\u9648\u56fd\u826f\u4e0d\u5ea6\u5fb7\uff0c\u4e0d\u91cf\u529b\uff0c\u5374\u8981\u6765\u8bd5\u8bd5\u8fd9\u4f4d\u88f4\u5927\u5148\u751f\uff0c\u662f\u4e0d\u662f\u6709\u60ca\u4eba\u7684\u827a\u4e1a\uff0c\u8d85\u7fa4 \u7684\u672c\u4e8b\uff0c\u80fd\u538b\u5f97\u4f4f\u6211\u4eec\u8fd9\u4e9b\u5200\u5934\u8214\u8840\u7684\u670b\u53cb\u3002\u8981\u662f\u8fd9\u4f4d\u88f4\u5927\u5148\u751f\u7684\u6b66\u529f\u8fd8\u4e0d\u5982\u6211\u2014\u2014\u54c8\u54c8\uff1a \u201c\u4ed6\u72c2\u7b11\u4e00\u58f0\uff0c\u500f\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u53cc\u624b\u4e00\u53c9\uff0c\u5168\u7136\u662f\u4e00\u526f\u5e02\u4e95\u6cfc\u76ae\u5bfb\u4eba\u6253\u67b6\u7684\u59ff\u6001\u3002\u201d\u795e\u624b \u201c\u6218\u98de\u6d53\u7709\u4e00\u8f69\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u53d7\u4e86\u8c01\u7684\u6307\u4f7f\uff0c\u7adf\u6562\u5728\u8fd9\u91cc\u6492\u91ce\uff0c\u8fd9\u6d6a\u83bd\u5c71\u5e84\uff0c\u5c82\u662f \u4f60\u8fd9\u4e0b\u4e94\u95e8\u7684\u72c2\u5f92\u6492\u91ce\u4e4b\u5730\u2014\u2014\u6765\u4eba\u5440\uff01\u7ed9\u6211\u5c06\u8fd9\u5927\u80c6\u72c2\u5f92\u6293\u51fa\u53bb\u3002\u201d \u3000\u3000\u4ed6\u559d\u58f0\u4e00\u4e86\uff0c\u7acb\u523b\u6709\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u8d8a\u4f17\u800c\u51fa\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u500f\u7136\u957f\u8eab\u800c\u8d77\uff0c\u5927\u559d \u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6266\u7709\u9053\uff1a\u201c\u600e\u5730\uff1f\u2018\u201d\u91d1\u9e21\u201c\u5411\u4e00\u557c\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u9648\u5144\u5f1f\u8bf4\u7684\u8bdd\uff0c\u4e00\u53e5 \u4e5f\u672a\u66fe\u8bf4\u9519\uff0c\u8981\u60f3\u5f53\u6c5f\u752b\u6b66\u6797\u76df\u4e3b\u7684\u4eba\uff0c\u4e0d\u9732\u4e2a\u4e09\u62db\u4e24\u5f0f\uff0c\u563f\u563f\u2014\u2014\u6c5f\u5357\u9053\u4e0a\u7684\u6570\u4e07\u4e2a\u5f1f \u5144\uff0c\u600e\u80fd\u5fc3\u670d\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fae\u5fae\u4e00\u6014\uff0c\u77ac\u5373\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u2018\u88f4\u5927\u5148\u751f\uff0c\u4e43\u5144\u5f1f\u6211\u4e0e\u83ab\u6c0f\u5144\u5f1f\uff0c\u90a3\u5927\u54e5 \u4e00\u8d77\u8bf7\u6765\u7684\uff0c\u6709\u8c01\u4e0d\u670d\u7684\u8bdd\uff0c\u54fc\u54fc\uff01\u201d\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u4e0d\u5982\u8ba9\u6218\u5144 \u4f60\u81ea\u5df1\u505a\u76df\u4e3b\u597d\u4e86\uff0c\u53c8\u4f55\u5fc5\u2014\u2014\u54fc\u54fc\uff0c\u505a\u51fa\u8fd9\u7b49\u5f20\u81f4\u6765\u6b3a\u4eba\u8033\u76ee\u3002\u201d \u3000\u3000\u9648\u56fd\u826f\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff0c\u8981\u662f\u6218\u5e84\u4e3b\u6765\u505a\u76df\u4e3b\uff0c\u6211\u9648\u56db\u5012\u6ca1\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u51b7\u773c\u65c1\u89c2\uff0c\u6b64\u523b\u7a81\u7136\u5e72\u54b3\u4e00\u58f0\uff0c\u62ca\u638c\u7b11\u9053\uff1a\u201c\u662f\u6781\uff01\u662f\u6781\uff01\u201d \u3000\u3000\u5728\u573a\u7fa4\u8c6a\u7684\u6570\u767e\u9053\u76ee\u5149\uff0c\u500f\u5730\u5c3d\u90fd\u8f6c\u5411\u6a80\u660e\u3002\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\uff0c\u6b63\u90fd\u662f\u773c\u4e2d\u4e0d\u63c9\u534a\u7c92\u6c99\u5b50 \u7684\u5149\u68cd\uff0c\u77e5\u9053\u6b64\u65f6\u6b64\u5730\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u5c45\u7136\u53d1\u8a00\uff0c\u5fc5\u975e\u8f7b\u6613\u4e4b\u4e8b\u3002\u88f4\u73cf\u4e00\u5165\u5927\u5385\uff0c\u5fc3\u4e2d\u5343\u5934 \u4e07\u7eea\uff0c\u6b63\u5728\u832b\u7136\u6c89\u601d\uff0c\u6b64\u523b\u542c\u4e86\u8fd9\u53e5\u8bdd\u58f0\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u6b63\u597d\u4e0e\u6a80\u660e\u7684\u76ee\u5149\u9047\u5230\u4e86 \u4e00\u5904\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u88f4\u73cf\u4f46\u89c9\u5168\u8eab\u4e00\u9707\uff0c\u53ea\u89c1\u6a80\u660e\u9762\u5411\u81ea\u5df1\uff0c\u5fae\u5e26\u7b11\u5bb9\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u7a81\u5730\u60f3\u8d77 \u5e74\u4f59\u4ee5\u524d\uff0c\u5728\u90a3\u201c\u9f99\u98de\u9556\u5c40\u201d\u7684\u540e\u9662\u4e4b\u4e2d\u7684\u79cd\u79cd\u60c5\u4e8b\uff0c\u4ed6\u4e5f\u500f\u7136\u60f3\u8d77\uff0c\u81ea\u5df1\u51b3\u5fc3\u51fa\u6765\u95ef\u8361\u6c5f \u6e56\u65f6\uff0c\u6240\u7acb\u4e0b\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5389\u76ee\u671b\u7740\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u6b63\u5f85\u7b54\u8bdd\uff0c\u5374\u89c1\u88f4\u73cf\u7a81\u5730\u633a\u80f8\u8d70\u51fa\uff0c\u7b14\u76f4\u5730\u8d70 \u5411\u90a3\u201c\u9648\u56fd\u826f\u201d\u9762\u524d\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4f60\u662f\u51c6\u5907\u8981\u8bd5\u4e00\u8bd5\u6211\u7684\u6b66\u529f\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u9648\u56fd\u826f\u672c\u662f\u6c5f\u6e56\u5bb5\u5c0f\u4e4b\u8f88\uff0c\u65b9\u624d\u4e0d\u8fc7\u662f\u5949\u4e86\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e4b\u547d\uff0c\u6545\u610f\u6765\u6363\u4e71\u800c\u5df2\uff0c \u5176\u5b9e\u4ed6\u54ea\u91cc\u771f\u5f97\u6709\u5728\u6d6a\u83bd\u5c71\u5e84\u6492\u91ce\u7684\u80c6\u5b50\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u89c1\u8fd9\u5373\u5c06\u6210\u4e3a\u6c5f\u5357\u7eff\u6797\u7684\u76df\u4e3b\u7684\u5c11\u5e74\u7ad9\u5728\u81ea\u5df1\u524d\u9762\uff0c\u795e\u6001\u8f69\u6602\uff0c\u8a00\u8bed\u6e05\u6717\uff0c\u53cc\u76ee \u4e4b\u4e2d\uff0c\u66f4\u662f\u95ea\u95ea\u751f\u5149\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u5927\u751f\u602f\u754f\u4e4b\u5fc3\uff0c\u7adf\u4e0d\u6562\u7b54\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u5374\u6df1\u77e5\u88f4\u73cf\u7684\u5e95\u7ec6\uff0c\u77e5\u9053\u4ed6\u4e0d\u4f1a\u6b66\u529f\uff0c\u6b64\u523b\u5fd9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4f4d\u59d3\u9648 \u7684\u670b\u53cb\uff0c\u6b63\u662f\u627e\u88f4\u5927\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u5ffd\u7136\u60f3\u8d77\uff0c\u8fd9\u88f4\u59d3\u5c11\u5e74\uff0c\u672c\u662f\u53c8\u804b\u53c8\u54d1\u7684\uff0c\u81ea\u5df1\u53c8 \u66fe\u91cd\u91cd\u51fb\u4e86\u4ed6\u4e00\u62f3\uff0c\u800c\u6b64\u523b\u4ed6\u4e0d\u4f46\u8eab\u4e0a\u5168\u65e0\u534a\u5206\u4f24\u75d5\uff0c\u800c\u4e14\u5c45\u7136\u80fd\u8bf4\u80fd\u542c\u8d77\u6765\u3002 \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fc3\u4e2d\u5927\u5947\uff0c\u4e0d\u89c9\u500f\u7136\u4f4f\u53e3\uff0c\u53ea\u542c\u88f4\u73cf\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8981\u627e\u6211\u8f83\u91cf\u6b66\u529f\uff0c \u90a3\u4e48\u4f60\u5c31\u5feb\u4e9b\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u4e03\u5de7\u7ae5\u5b50\u5434\u9e23\u4e16\u76ee\u5149\u52a8\u5904\uff0c\u89c1\u4ed6\u7adf\u7a81\u5730\u633a\u8eab\u800c\u51fa\uff0c\u5fc3\u4e2d\u4e0d\u7981\u60ca\u5947\u4ea4\u96c6\uff0c\u8981\u77e5\u9053\u4ed6\u4e0e\u88f4\u73cf \u76f8\u4ea4\u4ee5\u6765\uff0c\u4f46\u89c9\u88f4\u73cf\u4ec1\u4e49\u4e3a\u6000\uff0c\u950b\u8292\u4e0d\u9732\uff0c\u4ee5\u5fb7\u62a5\u6028\uff0c\u5c11\u5e74\u8001\u6210\u2026\u2026\u4f18\u70b9\u867d\u591a\uff0c\u4f46\u5374\u603b\u5c11\u4e86 \u4e00\u79cd\u6c5f\u6e56\u4fa0\u58eb\u5e94\u6709\u7684\u8c6a\u52c7\u4e4b\u6c14\uff0c\u4f46\u4ed6\u77e5\u9053\u88f4\u73cf\u5c11\u5e74\u5b64\u82e6\uff0c\u53d7\u5c3d\u6298\u78e8\uff0c\u4ee5\u81f4\u5982\u6b64\uff0c\u81ea\u4e5f\u4e0d\u8db3\u4e3a \u602a\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u89c1\u88f4\u73cf\u5982\u6b64\u795e\u60c5\uff0c\u8fd9\u6b63\u5982\u56ca\u7834\u950b\u9732\uff0c\u7761\u72ee\u7a81\u9192\uff0c\u60ca\u5947\u4e4b\u4f59\uff0c\u53c8\u4e0d\u7981\u4e3a\u4e4b\u6b23\u559c\uff0c\u4f46 \u5374\u53c8\u6709\u51e0\u5206\u63d0\u5fc3\uff0c\u62c5\u5fc3\u88f4\u73cf\u7684\u6b66\u529f\uff0c\u4e0d\u662f\u8fd9\u9648\u56fd\u826f\u7684\u654c\u624b\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u90a3\u201c\u9f99\u5f62\u516b\u638c\u201d \u9762\u5e26\u5fae\u7b11\uff0c\u76ee\u6ce8\u88f4\u73cf\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u53cc\u62f3\u7d27\u63e1\uff0c\u5c79\u7acb\u5982\u5c71\uff0c\u201c\u5317\u6597\u4e03\u715e\u201d\u9762\u8272\u51dd\u91cd\uff0c\u76ee\u5149\u5982 \u526a\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u53cc\u7709\u5fae\u76b1\uff0c\u4f3c\u5728\u6c89\u601d\uff0c\u800c\u88f4\u73cf\u4ece\u5bb9\u8d1f\u624b\uff0c\u5374\u7adf\u4f3c\u6839\u672c\u6ca1\u6709\u5c06\u9762\u524d\u8fd9\u7325\u7410\u7684 \u6c49\u5b50\u9648\u56fd\u826f\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u8fd9\u5927\u5385\u4e4b\u4e2d\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u6709\u7684\u662f\u7eaf\u7cb9\u4e3a\u7740\u89c2\u793c\u800c\u6765\uff0c\u6709\u7684\u662f\u5949\u53ec\u6b43\u8840\u4e3a\u76df\u6c5f\u5357\u7eff\u6797\uff0c\u6709 \u7684\u662f\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u79c1\u4eba\u5fc3\u8179\uff0c\u6709\u7684\u662f\u5b58\u5fc3\u4e3a\u96be\u7684\u201c\u91d1\u9e21\u201d\u5e2e\u4f17\uff0c\u6709\u7684\u662f\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e \u7684\u95e8\u4eba\u624b\u4e0b\uff0c\u6709\u7684\u4e00\u5fc3\u60f3\u770b\u8fd9\u201c\u88f4\u73cf\u5927\u5148\u751f\u201d\u4e22\u4eba\u73b0\u773c\uff0c\u6709\u7684\u5374\u53c8\u5e0c\u671b\u4ed6\u80fd\u6210\u540d\u9732\u8138\u2026\u2026\u8fd9 \u5176\u4e2d\u60c5\u51b5\u4e4b\u590d\u6742\uff0c\u5f53\u771f\u662f\u8a00\u8bed\u96be\u4ee5\u63cf\u6479\uff0c\u4f46\u4f17\u4eba\u5fc3\u610f\u867d\u4e0d\u540c\uff0c\u76ee\u5149\u5374\u4e00\u8d77\u671b\u5728\u88f4\u73cf\u8eab\u4e0a\uff0c\u7eb5 \u7136\u662f\u201c\u5317\u6597\u4e03\u715e\u201d\u3001\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u3001\u201c\u795e\u624b\u201d\u6218\u98de\u3001\u201c\u9f99\u5f62\u516b\u638c\u201d\u8fd9\u4e9b\u4e45\u5df2\u6210\u540d\uff0c\u58f0\u5a01\u8fdc\u9707 \u7684\u4eba\u7269\uff0c\u6b64\u523b\u6bd4\u8d77\u88f4\u73cf\u7684\u5149\u91c7\uff0c\u4e5f\u8981\u9eef\u7136\u5931\u8272\u3002 \u3000\u3000\u88f4\u73cf\u8bed\u58f0\u8fc7\u540e\uff0c\u5927\u5385\u4e2d\u7acb\u523b\u53d8\u6210\u4e00\u7247\u6c89\u5bc2\uff0c\u90a3\u9648\u56fd\u826f\u76ee\u5149\u5230\u5904\u4e71\u8f6c\uff0c\u4f3c\u4e4e\u5728\u4e5e\u601c\uff0c\u53c8\u4f3c \u4e4e\u5728\u6c42\u52a9\uff0c\u6700\u540e\u7b14\u76f4\u5730\u671b\u5411\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u54ea\u77e5\u5411\u4e00\u557c\u6b64\u523b\u6b63\u81ea\u6697\u5730\u601d\u5fd6\uff1a\u201c\u770b\u6765\u8fd9\u88f4\u59d3 \u5c11\u5e74\uff0c\u4f3c\u4e4e\u6709\u4e9b\u53e4\u602a\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5148\u53eb\u8fd9\u9648\u56fd\u826f\u8bd5\u4e00\u8bd5\u4e5f\u597d\u3002\u201d\u4e8e\u662f\u51b7\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13 \u8bf4\u9053\uff1a\u201c\u670b\u53cb\u65e2\u6709\u5fc3\u4e00\u8bd5\u88f4\u5927\u5148\u751f\u7684\u6b66\u529f\uff0c\u6b64\u523b\u8fd8\u4e0d\u52a8\u624b\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d \u3000\u3000\u53cc\u624b\u4e00\u8d1f\uff0c\u53cc\u76ee\u4e00\u7ffb\uff0c\u518d\u4e5f\u4e0d\u671b\u9648\u56fd\u826f\u4e00\u773c\u3002\u90a3\u4e9b\u4e00\u8eab\u5f69\u8863\u7684\u91d1\u9e21\u4f17\u5e2e\uff0c\u89c1\u4e86\u5e2e\u4e3b\u8fd9\u7b49 \u795e\u8272\uff0c\u4e5f\u90fd\u8ddf\u7740\u8d77\u54c4\uff0c\u6709\u7684\u53e3\u4e2d\u5f00\u59cb\u4e86\u51fa\u201c\u5618\u201d\u58f0\uff0c\u6709\u7684\u8086\u53e3\u6bc1\u5632\uff1a\u201c\u770b\u4ed6\u50cf\u662f\u4e2a\u6c49\u5b50\uff0c\u54ea \u77e5\u5374\u662f\u8fd9\u6837\u7684\u6ca1\u79cd\uff01\u201d\u5927\u5385\u4e2d\u7684\u6c89\u5bc2\uff0c\u987f\u65f6\u6362\u4f5c\u54c4\u95f9\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u4f9d\u7136\u542b\u7b11\u7aef\u5750\uff0c\u51b7\u773c\u65c1 \u89c2\uff0c\u9648\u56fd\u826f\u5fc3\u4e2d\u602f\u754f\uff0c\u53c8\u662f\u61ca\u6094\uff0c\u4f46\u6b64\u523b\u9a91\u864e\u96be\u4e0b\uff0c\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u4e00\u4e2a \u864e\u6251\uff0c\u7eb5\u8eab\u6251\u5411\u88f4\u73cf\uff0c\u5385\u4e2d\u7fa4\u8c6a\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u4e00\u58f0\u60e8\u547c\uff0c\u751a\u81f3\u8fd8\u672a\u770b\u6e05\u88f4\u73cf\u7684\u52a8\u4f5c\uff0c\u9648\u56fd \u826f\u5df2\u81ea\u5e73\u7a7a\u98de\u4e86\u51fa\u53bb\uff0c\u201c\u53ed\u201d\u5730\u4e00\u58f0\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u518d\u4e5f\u52a8\u5f39\u4e0d\u5f97\u4e86\u3002 \u3000\u3000\u4f17\u8c6a\u9762\u9762\u76f8\u89d1\uff0c\u7fa4\u76f8\u5931\u8272\u3002\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u66f4\u662f\u6cae\u7136\u8272\u53d8\uff0c\u8fde\u9000\u4e09\u6b65\u501a\u5899\u800c\u7acb\uff0c\u5446\u5446\u5730 \u671b\u7740\u88f4\u73cf\uff0c\u51e0\u4e4e\u76f8\u4fe1\u4e0d\u81ea\u5df1\u7684\u773c\u775b\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u4e00\u626c\uff0c\u7a81\u5730\u957f\u8eab\u800c\u8d77\uff0c\u201c\u795e\u624b\u201d\u6210\u98de \u4e0b\u610f\u8bc6\u5730\u62d4\u51fa\u80cc\u540e\u6298\u6247\uff0c\u201c\u5237\u201d\u5730\u5c55\u5f00\uff0c\u201c\u5317\u6597\u4e03\u715e\u201d\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u53ea\u89c1\u5f7c\u6b64\u9762\u4e0a\uff0c\u4ea6\u90fd \u53d8\u4e86\u989c\u8272\uff01 \u3000\u3000\u6b64\u523b\u4f17\u4eba\u5fc3\u4e2d\u6240\u601d\uff0c\u66f4\u662f\u4e2a\u4e2a\u4e0d\u540c\uff0c\u590d\u6742\u4e07\u5206\uff0c\u8fd9\u5176\u4e2d\u53ea\u6709\u201c\u9f99\u5f62\u516b\u638c\u201d\u201c\u795e\u624b\u201d\u6218 \u98de\u3001\u201c\u5317\u6597\u4e03\u715e\u201d\u3001\u5411\u4e00\u557c\u3001\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u3001\u4e1c\u65b9\u5144\u5f1f\u3001\u5434\u9e23\u4e16\u7b49\u4eba\uff0c\u770b\u51fa\u88f4\u73cf\u65bd\u5c55\u7684\u62db \u5f0f\uff0c\u53ea\u89c1\u4ed6\u62db\u5f0f\u867d\u7136\u7b80\u5355\uff0c\u4f46\u51fa\u624b\u4e4b\u5947\u8be1\u3001\u90e8\u4f4d\u4e4b\u51c6\u786e\u3001\u52b2\u529b\u4e4b\u5206\u914d\u3001\u8fd0\u7528\u4e4b\u7eaf\u719f\uff0c\u5374\u7aef\u7684 \u662f\u4ee4\u4eba\u53f9\u4e3a\u89c2\u6b62\uff0c\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u867d\u90fd\u662f\u5185\u5916\u517c\u4fee\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u4f46\u6b64\u523b\u5fc3\u4e2d\u6697\u95ee\u81ea\u5df1\uff0c\u7adf\u6ca1\u6709 \u4e00\u4eba\u77e5\u9053\u8fd9\u4e00\u62db\u7684\u6765\u5386\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u6570\u8f6c\uff0c\u7a81\u5730\u6325\u624b\u53f1\u9053\uff1a\u201c\u62ac\u4e0b\u53bb\u2026\u2026\u201d\u53ea\u89c1\u88f4\u73cf\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u4f3c \u4e4e\u53c8\u56de\u590d\u4e86\u7247\u523b\u524d\u7684\u8fdf\u949d\u795e\u8272\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u4e2d\u60ca\u7591\u4e4b\u6781\uff0c\u4f46\u9762\u4e0a\u5374\u4e0d\u9732\u534a\u5206\u795e\u8272\uff0c\u6d53\u7709 \u4e00\u626c\uff0c\u9762\u5411\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u81ea\u95ee\u4e24\u773c\u4e0d\u76f2\uff0c\u5404\u4f4d\u5bf9\u88f4\u5927\u5148\u751f \u7684\u6b66\u529f\u82e5\u662f\u8fd8\u6709\u6000\u7591\u4e4b\u5904\uff0c\u4e0d\u59a8\u51fa\u6765\u8bd5\u8bd5\u3002\u201d\u4f46\u89c1\u5385\u4e2d\u7fa4\u8c6a\uff0c\u4e2a\u4e2a\u5664\u5982\u5bd2\u8749\uff0c\u4ff1\u5df2\u88ab\u88f4\u73cf\u65b9 \u624d\u90a3\u4e00\u62db\u5947\u5965\u7edd\u4f26\u7684\u6b66\u529f\u6240\u60ca\uff0c\u54ea\u6709\u4e00\u4eba\u518d\u6562\u53d1\u8a00\uff0c\u4e0d\u7981\u518d\u6b21\u72c2\u7b11\u4e00\u58f0\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u5ffd\u5730\u671b \u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e00\u624b\u7aef\u8d77\u9152\u676f\uff0c\u7b14\u76f4\u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u7adf\u5bf9\u88f4\u73cf\u7b11\u9053\uff1a\u201c\u73cf\u513f\uff0c\u4e00\u5e74\u4e0d\u89c1\uff0c\u60f3\u4e0d \u5230\u4f60\u6b66\u529f\u5c45\u7136\u7cbe\u8fdb\u5982\u65af\uff0c\u771f\u6b63\u4ee4\u4eba\u53ef\u559c\uff0c\u6211\u4e14\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u7acb\u523b\u9762\u8272\u5927\u53d8\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u88f4\u73cf\u7adf\u662f\u6a80\u660e\u7d20\u8bc6\uff0c\u800c\u4e14\u6a80\u660e\u8a00\u8bed\u4e4b\u4e0b\uff0c\u7adf \u4f3c\u8fd8\u6bd4\u88f4\u73cf\u957f\u7740\u4e00\u8f88\uff0c\u7fa4\u8c6a\u66f4\u662f\u5fc3\u4e2d\u5927\u5947\uff0c\u201c\u600e\u5730\u6218\u795e\u624b\u63a8\u51fa\u7684\u7eff\u6797\u76df\u4e3b\uff0c\u7ade\u662f\u4ed6\u51a4\u5bb6\u5bf9\u5934 \u7684\u6545\u4eba\uff1f\u201d \u3000\u3000\u53ea\u89c1\u88f4\u73cf\u7f13\u7f13\u79fb\u8fc7\u76ee\u5149\uff0c\u5411\u6a80\u660e\u5fae\u5fae\u4e00\u7b11\uff0c\u55eb\u5685\u534a\u664c\u7f13\u7f13\u9053\uff1a\u201c\u5927\u53d4\u4f60\u8fd9\u4e00\u5411\u53ef\u597d\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u4ef0\u9996\u559d\u5e72\u676f\u4e2d\u4e4b\u9152\uff0c\u6717\u58f0\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u8fd8\u597d\uff01\u201d\u4e00\u624b\u642d\u4e0a\u88f4\u73cf \u80a9\u8180\uff0c\u7f13\u7f13\u8d70\u56de\u5ea7\u4e2d\uff0c\u6218\u98de\u6115\u6115\u5730\u671b\u7740\u4ed6\u4eec\uff0c\u5fc3\u4e2d\u7684\u5f97\u610f\u4e4b\u60c5\uff0c\u65e9\u5df2\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u6115\u4e86\u534a \u664c\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u539f\u6765\u6a80\u5927\u4fa0\u7adf\u662f\u88f4\u5927\u5148\u751f\u7d20\u8bc6\u2026\u2026\u201d \u3000\u3000\u6a80\u660e\u6717\u7b11\u9053\uff1a\u201c\u73cf\u513f\u81ea\u5e7c\u4fbf\u548c\u6211\u4f4f\u5728\u4e00\u8d77\uff0c\u2018\u7d20\u8bc6\u2019\u4e8c\u5b57\uff0c\u4f3c\u4e4e\u8fd8\u4e0d\u8db3\u4ee5\u5f62\u5bb9\u54e9\u3002\u201d\u8f6c \u9996\u88f4\u73cf\uff1a\u201c\u73cf\u513f\uff0c\u4f60\u8bf4\u53ef\u662f\uff1f\u201d \u3000\u3000\u88f4\u73cf\u65e0\u8a00\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u53ea\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u4e0a\u9635\u9752\u9635\u767d\uff0c\u4ed6\u4e00\u5fc3\u60f3\u5c06\u88f4\u73cf\u63a8\u4e3a\u7eff\u6797\u76df \u4e3b\uff0c\u7136\u540e\u201c\u631f\u5929\u5b50\u4ee5\u4ee4\u8bf8\u4faf\u201d\uff0c\u53ea\u8981\u88f4\u73cf\u5728\u4ed6\u638c\u63e1\u4e4b\u4e2d\uff0c\u90a3\u4ed6\u53c8\u4e0e\u76df\u4e3b\u4f55\u5f02\uff0c\u65b9\u624d\u88f4\u73cf\u9732\u51fa \u60ca\u4eba\u6b66\u529f\uff0c\u4ed6\u5fc3\u4e2d\u867d\u5947\u602a\uff0c\u4f46\u5374\u5f97\u610f\uff0c\u54ea\u77e5\u6b64\u523b\u60c5\u52bf\u6025\u8f6c\u76f4\u4e0b\uff0c\u7ade\u662f\u5927\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u4ed6 \u6240\u8d39\u7684\u4e00\u756a\u6614\u5fc3\uff0c\u5230\u6b64\u523b\u7adf\u4f3c\u90fd\u4e3a\u4e86\u5bf9\u5934\u6240\u8d39\uff0c\u8fd9\u6d6a\u83bd\u5c71\u5e84\u4e3b\u867d\u7136\u9634\u9e37\u6df1\u6c89\uff0c\u6db5\u517b\u529f\u592b\uff0c\u5230 \u6b64\u523b\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u60f6\u7136\u8272\u53d8\u4e86\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u592b\u53ea\u987e\u7740\u81ea\u5df1\u6b22\u559c\uff0c\u5374\u5fd8\u4e86\u5404\u4f4d\u8fd8\u6709\u6b63\u4e8b\u3002\u73cf \u513f\uff0c\u4eca\u65e5\u6b66\u6797\u7fa4\u8c6a\u6b64\u6765\uff0c\u5168\u662f\u4e3a\u7740\u4f60\u4e00\u4eba\u800c\u5df2\uff0c\u6b64\u540e\u4f60\u505a\u4e86\u6c5f\u5357\u7eff\u6797\u76df\u4e3b\uff0c\u5207\u5207\u4e0d\u53ef\u8f9c\u8d1f\u4e86 \u522b\u4eba\u7684\u96c5\u7231\uff0c\u53bb\u53bb\u2014\u2014\u5feb\u53bb\u7167\u987e\u5ba2\u4eba\uff0c\u5509\uff01\u6545\u4eba\u6709\u540e\uff0c\u771f\u53eb\u8001\u592b\u9ad8\u5174\u5f97\u2026\u2026\u201d \u3000\u3000\u4ed6\u4ef0\u9996\u72c2\u7b11\u4e00\u9635\uff0c\u53c8\u9053\uff1a\u201c\u6218\u5e84\u4e3b\uff0c\u65b9\u624d\u6b43\u8840\u4e4b\u8a93\uff0c\u88ab\u90a3\u5339\u592b\u4e00\u6270\uff0c\u9669\u4e9b\u5f04\u5f97\u4e0d\u6b22\uff0c\u6240 \u5e78\u6b64\u523b\u5df2\u81ea\u65e0\u4e8b\uff0c\u5728\u5ea7\u7fa4\u8c6a\uff0c\u8fd8\u6709\u8bb8\u591a\u672a\u66fe\u996e\u5f97\u8840\u9152\uff0c\u6b64\u523b\u8fd8\u4e0d\u8d76\u5feb\u5b8c\u6210\u5927\u5178\uff0c\u8001\u592b\u867d\u662f\u5c40 \u5916\u4eba\uff0c\u5374\u5df2\u7b49\u5f97\u6709\u4e9b\u5fc3\u6025\u4e86\u54e9\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6ee1\u9762\u82e6\u7b11\uff0c\u8bfa\u8bfa\u9053\uff1a\u201c\u6b63\u662f\u2026\u2026\u6b63\u662f\u2026\u2026\u201d\u4ed6\u6b64\u523b\u5fc3\u91cc\u54ea\u91cc\u8fd8\u6709\u534a\u5206\u8981\u88f4 \u73cf\u6765\u5f53\u76df\u4e3b\u4e4b\u610f\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u5f53\u7740\u5929\u4e0b\u82f1\u96c4\uff0c\u4ed6\u5374\u53c8\u600e\u80fd\u81ea\u5df1\u6765\u6253\u81ea\u5df1\u7684\u5634\u5df4\uff0c\u8bf4\u51fa\u53cd\u5bf9 \u7684\u8bdd\uff0c\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u7a81\u5730\u5927\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\uff0c\u4e0d\u4f46\u5c11\u5e74\u82f1\u4fca\uff0c\u60f3\u4e0d\u5230\u6b66\u529f\u66f4\u662f \u5982\u6b64\u60ca\u4eba\uff0c\u8fd9\u79cd\u4eba\u6765\u5f53\u6c5f\u5357\u76df\u4e3b\uff0c\u6211\u59d3\u5411\u7684\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\u2014\u2014\u6765\u5144\u5f1f\u4eec\uff0c\u4e14\u996e\u4e00\u676f\u8840\u9152\uff0c\u8d3a \u6211\u76df\u4e3b\u4e4b\u751f\uff01\u201d\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u8200\u8d77\u4e00\u676f\u8840\u9152\uff0c\u9894\u9996\u4e00\u5e72\u800c\u5c3d\uff0c\u8d70\u5230\u88f4\u73cf\u8eab\u524d\uff0c\u8eac\u8eab\u4e00\u793c\uff0c \u5ffd\u5730\u5389\u58f0\u9053\uff1a\u201c\u81ea\u6b64\u4ee5\u540e\uff0c\u88f4\u5927\u5148\u751f\u5c31\u662f\u6211\u76df\u4e3b\u5927\u54e5\uff0c\u82e5\u6709\u4ec0\u4e48\u4eba\u5bf9\u6211\u5927\u54e5\u65e0\u793c\uff0c\u6211\u59d3\u5411\u7684 \u7b2c\u4e00\u4e2a\u627e\u4ed6\u62fc\u4e86\u3002\u201d\u4e00\u624b\u62c4\u7740\u94c1\u62d0\uff0c\u94c1\u62d0\u89e6\u5730\uff0c\u5f53\u5f53\u4f5c\u54cd\uff0c\u91d1\u9e21\u4f17\u5e2e\u89c1\u4e86\u5e2e\u4e3b\u5982\u6b64\uff0c\u81ea\u4e5f\u4e89 \u7740\u53bb\u996e\u8840\u9152\uff0c\u8fd9\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u65b9\u624d\u867d\u4e00\u5fc3\u60f3\u6765\u6270\u4e71\u8fd9\u201c\u76e5\u4e3b\u4e4b\u4f1a\u201d\uff0c\u4f46\u89c1\u4e86\u6218\u98de\u7684\u795e\u5ea7\u4ed6 \u4e0e\u6218\u98de\u4ec7\u6028\u5df2\u6df1\uff0c\u6b64\u523b\u4fbf\u4e0d\u4f46\u4e0d\u518d\u6270\u4e71\uff0c\u53cd\u800c\u6781\u529b\u8d5e\u6210\u4e86\u3002 \u3000\u3000\u8fd9\u5176\u95f4\u7684\u4eba\u4e16\u53d8\u5316\uff0c\u5f53\u771f\u662f\u6ce2\u8c32\u4e91\u8be1\uff0c\u77ac\u606f\u4e4b\u95f4\u65b9\u624d\u4e00\u5fc3\u60f3\u6765\u6270\u4e71\u4e4b\u4eba\uff0c\u6709\u5982\u6a80\u660e\u3001\u91d1 \u9e21\uff0c\u6b64\u523b\u4ff1\u90fd\u662f\u4e00\u529b\u8d5e\u6210\uff0c\u552f\u6050\u4e0d\u53ca\uff0c\u800c\u65b9\u624d\u4e00\u529b\u8d5e\u6210\u4e4b\u4eba\u6b64\u523b\u5374\u4e00\u529b\u53cd\u5bf9\uff0c\u4f46\u4ed6\u4eec\u5374\u90fd\u53c8 \u662f\u4e3b\u76df\u4e4b\u4eba\uff0c\u5fc3\u4e2d\u867d\u53cd\u5bf9\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u8bf4\u5c06\u51fa\u6765\u3002 \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u89c1\u4e86\u201c\u795e\u624b\u201d\u6218\u98de\u3001\u201c\u5317\u6597\u4e03\u715e\u201d\u3001\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u7b49\u4eba\u9762\u4e0a\u7684\u795e\u8272\uff0c\u5fc3\u4e2d \u867d\u5728\u597d\u7b11\uff0c\u4f46\u5374\u4e0d\u7981\u53c8\u6709\u4e9b\u62c5\u6270\u4e86\u3002 \u3000\u3000\u8981\u77e5\u9053\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u4e0d\u4f46\u806a\u660e\u7edd\u9876\uff0c\u800c\u4e14\u6d89\u4e16\u9887\u6df1\uff0c\u6b64\u523b\u51b7\u773c\u65c1\u89c2\uff0c\u66f4\u662f\u5c06\u8fd9\u4e9b\u4eba\u7684\u5fc3 \u4e8b\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u201c\u9f99\u5f62\u516b\u638c\u201d\u672c\u662f\u751f\u6015\u6c5f\u5357\u7eff\u6797\u76df\u4e3b\u4ea7\u751f\u4e4b\u540e\uff0c\u6c5f\u5357\u7eff\u6797\u56e0\u800c\u56e2\u7ed3\uff0c\u4fbf\u5bf9\u4ed6\u6781\u4e3a \u4e0d\u5229\uff0c\u662f\u4ee5\u4ed6\u81ea\u7136\u8981\u6765\u52a0\u4ee5\u963b\u6270\u7834\u574f\uff0c\u6b64\u6765\u4e0d\u8fc7\u662f\u4f3a\u673a\u800c\u52a8\uff0c\u4f46\u540e\u6765\u89c1\u4e86\u8fd9\u4f4d\u201c\u76df\u4e3b\u201d\u7adf\u662f \u88f4\u73cf\uff0c\u4ed6\u5fc3\u4e0b\u6570\u8f6c\uff0c\u4fbf\u5c06\u4ee5\u524d\u7684\u4e3b\u610f\u5168\u90e8\u63a8\u7ffb\uff0c\u53cd\u800c\u4e00\u5fc3\u60f3\u5e2e\u7740\u88f4\u73cf\u767b\u4e0a\u76df\u4e3b\u5b9d\u5ea7\uff0c\u56e0\u4e3a\u4ee5 \u4ed6\u548c\u88f4\u73cf\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u81ea\u7136\u6bd4\u201c\u795e\u624b\u201d\u6218\u98de\u6df1\u4e9b\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u88f4\u73cf\u4e3b\u76df\u6c5f\u5357\u7eff\u6797\uff0c\u5c31\u53cd\u800c\u53d8 \u6210\u4e0e\u4ed6\u6781\u4e3a\u6709\u5229\u4e4b\u4e8b\u4e86\u3002 \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u5fc3\u4e2d\u62c5\u5fe7\u7684\u662f\uff0c\u4ed6\u4ece\u88f4\u73cf\u53e3\u4e2d\uff0c\u77e5\u9053\u6a80\u660e\u4e4b\u5bf9\u4e8e\u88f4\u73cf\uff0c\u4e95\u975e\u771f\u7684\u5168 \u662f\u5584\u610f\uff0c\u8fd9\u5176\u4e2d\u7684\u5185\u5e55\u7a76\u7adf\u5982\u4f55\uff0c\u4ed6\u867d\u4e0d\u5341\u5206\u660e\u4e86\uff0c\u4f46\u4e5f\u731c\u7740\u51e0\u5206\uff0c\u88f4\u73cf\u5982\u6b64\u88ab\u4eba\u5229\u7528\uff0c\u8bf4 \u4e0d\u5b9a\u6bd4\u88ab\u201c\u795e\u624b\u201d\u6218\u98de\u5229\u7528\u66f4\u574f\uff0c\u5434\u9e23\u4e16\u5fc3\u5ff5\u6570\u8f6c\uff0c\u4ed6\u867d\u7136\u806a\u660e\u7edd\u9876\uff0c\u4f46\u60f3\u6765\u60f3\u53bb\uff0c\u5374\u4e5f\u60f3 \u4e0d\u51fa\u4e00\u4e2a\u59a5\u5584\u4e4b\u8ba1\u6765\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u9648\u56fd\u826f\u65e9\u5df2\u88ab\u6218\u98de\u624b\u4e0b\uff0c\u62ac\u51fa\u5385\u53bb\uff0c\u751f\u6b7b\u4e0d\u77e5\uff0c\u5385\u4e2d\u7fa4\u8c6a\uff0c\u4e00\u4e2a\u4e2a\u5fc3\u4e2d\u867d\u90fd\u6709\u7740 \u4e2a\u95f7\u846b\u82a6\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u4ecd\u7136\u4f9d\u6b21\u5f80\u996e\u8840\u9152\u3002\u201c\u4f38\u624b\u201d\u6218\u98de\u770b\u5728\u773c\u91cc\uff0c\u6614\u5728\u5fc3\u91cc\uff0c\u76f4\u6025\u5f97\u6ee1 \u5934\u6c57\u73e0\uff0c\u6d94\u6d94\u800c\u843d\u3002\u4f46\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u53e3\u89d2\u542b\u7b11\uff0c\u4e00\u9762\u4ecb\u7ecd\u4e1c\u65b9\u5144\u5f1f\u4e0e\u88f4\u73cf\u62db\u7ec7\uff0c\u4e00\u9762\u53c8\u4e0d \u4f4f\u542b\u7b11\u8be2\u95ee\u88f4\u73cf\u8fd9\u4e00\u5e74\u522b\u6765\u7ecf\u8fc7\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u7adf\u662f\u5341\u5206\u5173\u5207\u3002 \u3000\u3000\u5434\u9e23\u4e16\u51b7\u773c\u65c1\u89c2\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u53f9\uff0c\u4ed6\u77e5\u9053\u88f4\u73cf\u751f\u5177\u81f3\u6027\uff0c\u4e00\u5fc3\u53ea\u5ff5\u7740\u6a80\u660e\u7684\u517b\u80b2\u4e4b\u6069\uff0c \u6839\u672c\u4e1d\u6beb\u6ca1\u6709\u5bf9\u6a80\u660e\u6000\u7591\u4e4b\u5904\uff0c\u90a3\u6a80\u660e\u7eb5\u7136\u5bf9\u4ed6\u6709\u4e9b\u4e0d\u597d\u4e4b\u5904\uff0c\u4ed6\u4e5f\u5b8c\u5168\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\u3002\u6b64 \u523b\u4e0e\u6a80\u660e\u5bf9\u9762\u76f8\u5750\uff0c\u4eff\u4f5b\u53c8\u56de\u5230\u4e00\u5e74\u4f59\u524d\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u7684\u5149\u666f\uff0c\u6a80\u660e\u95ee\u4ed6\u4e00\u53e5\uff0c\u4ed6\u4fbf\u7b54\u4e0a \u4e00\u53e5\uff0c\u6240\u5e78\u6b64\u65f6\u6b64\u5730\uff0c\u8a00\u8bed\u4e0d\u4fbf\uff0c\u662f\u4ee5\u6a80\u660e\u6ca1\u6709\u591a\u95ee\uff0c\u4ed6\u4e5f\u6ca1\u6709\u591a\u8bf4\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u88f4\u73cf\u5fc3\u4e2d\u5b9e\u5728\u5fcd\u8010\u4e0d\u4f4f\uff0c\u55eb\u5685\u7740\u9053\uff1a\u201c\u5927\u53d4\uff0c\u4e0d\u77e5\u6587\u742a\u59b9\u5b50\u53ef\u8fd8\u597d\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u5bb9\u4e00\u6c89\uff0c\u7a81\u5730\u53f9\u9053\uff1a\u201c\u5509\uff0c\u6211\u77e5\u9053\u4f60\u4e0e\u742a\u513f\u9752\u6885\u7af9\u9a6c\uff0c\u5df2\u7ecf\u2014\u2014\u4f46\u6211\u4eec \u867d\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u2018\u793c\u6559\u2019\u4e24\u5b57\uff0c\u5374\u4e5f\u4e07\u4e07\u4e0d\u53ef\u5fd8\u8bb0\uff0c\u662f\u4ee5\u4f60\u90a3\u65e5\u5728\u540e\u82b1\u56ed\u4e2d\u7684\u60c5\u51b5\uff0c\u6211\u6781\u4e3a \u4e0d\u6ee1\uff0c\u53ea\u662f\u60f3\u4e0d\u5230\u4f60\u6027\u60c5\u90a3\u822c\u521a\u70c8\uff0c\u7adf\u7136\u4e0d\u8f9e\u800c\u522b\uff0c\u6211\u5fc3\u91cc\u867d\u7136\u751f\u6c14\uff0c\u4f46\u89c1\u4f60\u8d70\u4e86\uff0c\u5374\u8fd8\u662f \u62c5\u5fc3\u7684\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u66fe\u53eb\u8fc7\u8bb8\u591a\u4eba\u51fa\u6765\u627e\u4f60\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u60c5\u4e00\u9635\u6fc0\u52a8\uff0c\u60f3\u5230\u81ea\u5df1\u4e00\u751f\u4e4b\u4e2d\uff0c\u53c8\u6709\u51e0\u4e2a\u4eba\u66fe\u50cf\u6a80\u5927\u53d4\u8fd9\u822c\u5173\u5fc3\u81ea\u5df1\uff0c\u5fcd\u4e0d\u4f4f \u773c\u7736\u4e00\u7ea2\uff0c\u5782\u4e0b\u5934\u53bb\uff0c\u5fc3\u91cc\u60f3\u8bf4\u51e0\u53e5\u611f\u6fc0\u7684\u8bdd\uff0c\u5374\u53c8\u8bf4\u4e0d\u51fa\u6765\uff0c\u53ea\u542c\u6a80\u660e\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u81ea\u63a5 \u9053\uff1a\u201c\u5176\u5b9e\u4f60\u53ea\u8981\u597d\u597d\u505a\u4eba\uff0c\u6211\u4fbf\u5c06\u742a\u513f\u8bb8\u914d\u4e8e\u4f60\uff0c\u6709\u4f55\u4e0d\u53ef\u3002\u201d\u88f4\u73cf\u5fc3\u4e2d\u4e00\u98a4\uff0c\u62ac\u8d77\u5934 \u6765\uff0c\u53ea\u89c1\u6a80\u660e\u76ee\u5149\u7684\u7684\uff0c\u671b\u5411\u81ea\u5df1\uff0c\u4e0d\u7981\u53c8\u5782\u4e0b\u5934\u53bb\uff0c\u8fd9\u201c\u53d4\u4f84\u201d\u4e24\u4eba\uff0c\u8f7b\u8a00\u7ec6\u8bed\uff0c\u7adf\u4f3c\u5fd8 \u4e86\u8fd9\u91cc\u662f\u4ec0\u4e48\u6240\u5728\u3002\u90a3\u201c\u795e\u624b\u201d\u6218\u98de\u770b\u5728\u773c\u91cc\uff0c\u5fc3\u4e2d\u66f4\u662f\u6025\u6012\u4ea4\u96c6\uff0c\u6084\u6084\u8d70\u5230\u201c\u5317\u6597\u4e03\u715e\u201d \u8eab\u4fa7\uff0c\u9644\u8033\u4f4e\u8bed\u51e0\u53e5\uff0c\u4f46\u201c\u5317\u6597\u4e03\u715e\u201d\u5144\u5f1f\u9762\u4e0a\u5374\u9732\u51fa\u96be\u8272\uff0c\u6115\u4e86\u534a\u664c\uff0c\u4e0d\u4f4f\u6447\u5934\uff0c\u201c\u795e \u624b\u201d\u6218\u98de\u957f\u53f9\u4e00\u58f0\uff0c\u53ea\u89c1\u5385\u5185\u7fa4\u8c6a\uff0c\u6b64\u523b\u5168\u5df2\u996e\u8fc7\u8840\u9152\uff0c\u6709\u7684\u901e\u81ea\u8d70\u56de\u5ea7\u4e2d\uff0c\u6709\u7684\u7adf\u8d70\u5230\u88f4 \u73cf\u8eab\u524d\uff0c\u8eac\u8eab\u4e3a\u793c\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u6ee1\u8154\u6012\u6c14\uff0c\u5374\u542c\u5230\u5385\u5916\u201c\u5288\u62cd\u201d\u8fde\u58f0\uff0c\u53c8\u662f\u4e00\u4e32\u767e\u5b50\u5357\u97ad\u54cd\u8d77\uff0c\u90a3\u5f6a\u5f62\u5927\u6c49 \u5f53\u95e8\u800c\u7acb\uff0c\u53c8\u81ea\u5927\u58f0\u559d\u9053\uff1a\u201c\u5927\u5178\u5b8c\u6210\u2014\u2014\u201d\u201c\u795e\u624b\u201d\u6218\u98de\u706b\u5192\u4e09\u4e08\uff0c\u7f13\u7f13\u8e31\u8fc7\u53bb\uff0c\u4e58\u522b\u4eba \u672a\u89c1\uff0c\u7a81\u5730\u4e00\u4e2a\u201c\u65f6\u62f3\u201d\u6253\u5728\u90a3\u5de8\u6c49\u809a\u4e0a\uff0c\u90a3\u5de8\u6c49\u559d\u58f0\u672a\u4e86\uff0c\u5f53\u809a\u4e00\u51fb\uff0c\u76f4\u75db\u5f97\u5f2f\u4e0b\u8170\u53bb\uff0c \u51b7\u6c57\u76f4\u6d41\uff0c\u4ed6\u56db\u80a2\u53d1\u8fbe\uff0c\u5934\u8111\u8fdf\u949d\uff0c\u54ea\u77e5\u8fd9\u5176\u4e2d\u7684\u53d8\u5316\uff0c\u518d\u4e5f\u60f3\u4e0d\u901a\u5e84\u4e3b\u4e3a\u4f55\u4f1a\u7a81\u7136\u7ed9\u81ea\u5df1 \u4e00\u62f3\uff0c\u53ea\u89c1\u6218\u98de\u9762\u5e26\u7b11\u5bb9\uff0c\u82e5\u65e0\u5176\u4e8b\u5730\u53c8\u8d70\u4e86\u5f00\u53bb\u3002\u4ed6\u809a\u5b50\u867d\u75db\uff0c\u600e\u6562\u53eb\u51fa\u58f0\u6765\uff0c\u629a\u7740\u809a \u5b50\uff0c\u8fde\u9000\u51e0\u6b65\uff0c\u6e9c\u5230\u540e\u9762\u517b\u4f24\u53bb\u4e86\uff01 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u867d\u7136\u6253\u4e86\u522b\u4eba\u4e00\u62f3\uff0c\u4f46\u662f\u4ed6\u5fc3\u4e2d\u95f7\u6c14\uff0c\u5374\u53c8\u600e\u662f\u8fd9\u4e00\u62f3\u53ef\u4ee5\u51fa\u6389\u7684\uff0c\u7f13\u6b65 \u8e31\u56de\u5385\u4e0a\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u65e0\u7cbe\u6253\u91c7\u4ed6\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u65e2\u662f\u996e\u8fc7\u8840\u9152\uff0c\u4fbf\u5168\u662f\u81ea\u5df1\u5144\u5f1f\uff0c\u4f46\u8bf7\u968f\u610f \u5403\u559d\uff0c\u4e0d\u8981\u518d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6b64\u523b\u8bed\u58f0\u4f4e\u5fae\uff0c\u5750\u5728\u8fdc\u4e9b\u7684\u4eba\uff0c\u751a\u81f3\u8fde\u542c\u90fd\u542c\u4e0d\u6e05\u695a\uff0c\u54ea\u91cc\u8fd8\u6709\u534a\u5206\u5148\u524d\u90a3\u79cd\u5174\u9ad8\u91c7 \u70c8\u7684\u6837\u5b50\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u6697\u7b11\uff0c\u4e3e\u676f\u9053\u3002 \u3000\u3000\u201c\u6218\u5e84\u4e3b\u5f53\u771f\u662f\u4f17\u671b\u6240\u624b\uff0c\u767b\u9ad8\u4e00\u547c\uff0c\u6c5f\u5357\u6b66\u6797\u4e2d\u4e45\u672a\u80fd\u51b3\u4e4b\u4e8b\uff0c\u4e8e\u5179\u4fbf\u544a\u89e3\u51b3\uff0c\u6211\u5411 \u4e00\u557c\u5b9e\u4f69\u670d\u5f97\u5f88\uff0c\u4e14\u656c\u6218\u5e84\u4e3b\u4e00\u676f\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u54fc\u4e00\u58f0\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u6545\u610f\u7709\u5934\u4e00\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u503c\u6b64\u5927\u559c\u4e4b\u65e5\uff0c\u6218 \u5e84\u4e3b\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u4e0d\u9ad8\u5174\u7684\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5e72\u7b11\u4e00\u58f0\uff0c\u4e3e\u676f\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u9ad8\u5174\u6781\u4e86\u2026\u2026\u9ad8\u5174\u6781\u4e86\u3002\u201d\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff0c \u201c\u5427\u201d\u5730\u4e00\u58f0\u5c06\u9152\u676f\u91cd\u91cd\u653e\u5728\u684c\u4e0a\uff0c\u76f4\u6068\u4e0d\u5f97\u7ed9\u5411\u4e00\u557c\u809a\u5b50\u4e0a\u4e5f\u6765\u4e00\u62f3\uff0c\u624d\u5bf9\u5fc3\u601d\u3002 \u3000\u3000\u4e8e\u662f\u9152\u7b75\u76db\u5f00\uff0c\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e2d\u7684\u6267\u4e8b\u4e4b\u4eba\u3001\u7a7f\u6d41\u4e0d\u606f\u5730\u5f80\u4e0a\u9001\u4e0a\u9152\u83dc\uff0c\u915d\u917f\u591a\u65f6\u7684 \u201c\u76df\u4e3b\u5927\u4f1a\u201d\uff0c\u6b64\u523b\u5927\u5178\u65e2\u6210\uff0c\u5728\u6b66\u6797\u4e2d\u9ed8\u9ed8\u65e0\u95fb\u7684\u88f4\u73cf\uff0c\u4ece\u6b64\u4e0d\u4f46\u767b\u4e0a\u6c5f\u5357\u7eff\u6797\u7684\u76df\u4e3b\u5b9d \u5ea7\uff0c\u800c\u4e14\u4ed6\u7684\u6b66\u529f\uff0c\u4e5f\u4ece\u6b64\u6210\u4e86\u5929\u4e0b\u6b66\u6797\u7684\u8bdd\u9898\u4e2d\u5fc3\uff0c\u4f46\u5374\u4ece\u672a\u6709\u4e00\u4eba\u770b\u51fa\u8fd9\u201c\u88f4\u5927\u5148\u751f\u201d \u7684\u6b66\u529f\u7a76\u7adf\u662f\u4f55\u95e8\u4f55\u6d3e\u7684\uff0c\u66f4\u6ca1\u6709\u4e00\u4eba\u770b\u51fa\u201c\u88f4\u5927\u5148\u751f\u201d\u7684\u6b66\u529f\u7a76\u7adf\u6df1\u6d45\u5982\u4f55\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u65e0\u7cbe\u6253\u91c7\u5730\u559d\u4e86\u4e24\u676f\u95f7\u9152\uff0c\u5374\u89c1\u90a3\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u7a81\u5730\u8dd1\u4e86\u8fc7\u6765\uff0c\u5728 \u4ed6\u8033\u7554\u4f4e\u8bed\u6570\u53e5\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u59cb\u800c\u6d53\u7709\u6df1\u76b1\uff0c\u4f46\u542c\u5b8c\u4e86\u5434\u9e23\u4e16\u7684\u8bdd\uff0c\u7cbe\u795e\u5374\u7a81\u5730\u4e00\u9707\u3002 \u3000\u3000\u88f4\u73cf\u76ee\u5149\u52a8\u5904\uff0c\u5ffd\u5730\u89c1\u5230\u5434\u9e23\u4e16\uff0c\u8fde\u5fd9\u542b\u7b11\u62db\u547c\u9053\uff1a\u201c\u5434\u5144\uff0c\u4f60\u53ef\u8ba4\u8bc6\u6a80\u5927\u53d4\u4e48\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u6b65\u8e31\u8fc7\uff0c\u9053\uff1a\u201c\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u5927\u4fa0\u7684\u82f1\u540d\uff0c\u5929\u4e0b\u7686\u95fb\uff0c\u5c0f\u53ef\u6b63\u662f \u95fb\u540d\u5df2\u4e45\uff0c\u53ea\u60dc\u65e0\u7f18\u62dc\u8bc6\u800c\u5df2\u3002\u201d\u88f4\u73cf\u9053\uff1a\u201c\u6a80\u5927\u53d4\uff0c\u8fd9\u4f4d\u662f\u6211\u7684\u597d\u53cb\u5434\u9e23\u4e16\uff0c\u4ed6\u5728\u6b66\u6797\u4e2d \u4e5f\u6709\u4e9b\u58f0\u540d\uff0c\u4e0d\u77e5\u6a80\u5927\u53d4\u53ef\u66fe\u542c\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u95ea\u52a8\uff0c\u5728\u5434\u9e23\u4e16\u8138\u4e0a\u8fde\u8f6c\u6570\u8f6c\uff0c\u7a81\u5730\u50cf\u662f\u60f3\u8d77\u4ec0\u4e48\uff0c\u9762\u8272\u7adf\u968f\u4e4b\u4e00 \u53d8\uff0c\u4f46\u77ac\u5373\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5434\u9e23\u4e16\u2014\u2014\u5434\u5144\u60f3\u5fc5\u5c31\u662f\u4eba\u79f0\u2018\u4e03\u5de7\u7ae5\u5b50\u2019\u7684\u6b66\u6797\u795e\u7ae5\u5427\uff0c\u8001\u592b\u4e5f \u95fb\u540d\u5df2\u4e45\u4e86\uff0c\u54c8\u54c8\uff01\u5374\u60f3\u4e0d\u5230\u7adf\u662f\u73cf\u513f\u7684\u597d\u53cb\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u9762\u4e0a\u867d\u4ea6\u542b\u7b11\uff0c\u4f46\u76ee\u5149\u4e4b\u4e2d\uff0c\u5374\u4f3c\u95ea\u52a8\u7740\u4e00\u5206\u950b\u9510\u7684\u5149\u8292\uff0c\u4e0e\u6a80\u660e\u76ee\u5149\u76f8\u5bf9\uff0c\u826f \u4e45\u826f\u4e45\uff0c\u65b9\u81ea\u4e00\u7b11\uff0c\u9053\u3002 \u3000\u3000\u201c\u6a80\u5927\u4fa0\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u751f\u5177\u81f3\u6027\uff0c\u4e00\u5fc3\u60f3\u671b\u7740\u81ea\u5df1\u7684\u552f\u4e00\u77e5\u5df2\u80fd\u4e0e\u81ea\u5df1\u7684\u6700\u5927\u6069\u4eba\u8c08\u5f97\u6295\u673a\uff0c\u54ea\u77e5\u4ed6\u4e24\u4eba \u8a00\u8bed\u4e4b\u95f4\uff0c\u867d\u7136\u5404\u81ea\u90fd\u5e26\u7740\u7b11\u5bb9\uff0c\u4f46\u4e00\u660e\u826f\u671b\u53bb\uff0c\u4fbf\u77e5\u9053\u4e0d\u8fc7\u662f\u5047\u7b11\u800c\u5df2\uff0c\u5fc3\u91cc\u4e0d\u89c9\u6781\u4e3a\u5931 \u671b\uff0c\u4f46\u5374\u6ca1\u6709\u60f3\u5230\u522b\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u4e09\u65e5\u6765\uff0c\u4ed6\u5947\u9047\u6781\u591a\uff0c\u53c8\u542c\u4e86\u522b\u4eba\u7684\u529d\u544a\uff0c\u662f\u4ee5\u5e76\u4e0d\u63a8\u8f9e\u76df\u4e3b\u4e4b\u4f4d\uff0c\u518d\u52a0\u4e0a\u4e0e\u201c\u9f99 \u5f62\u516b\u638c\u201d\u7a81\u7136\u76f8\u89c1\uff0c\u4f7f\u4ed6\u6fc0\u8d77\u5fc3\u4e2d\u8c6a\u6c14\uff0c\u6e10\u9732\u950b\u8292\uff0c\u4f46\u662f\u4ed6\u672c\u6027\u96be\u6539\uff0c\u4ecd\u7136\u662f\u76f4\u80a0\u76f4\u8179\uff0c\u82e5 \u8981\u8ba9\u4ed6\u50cf\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u4e00\u822c\u673a\u8b66\u8df3\u8131\uff0c\u5374\u662f\u4e07\u4e07\u96be\u4ee5\u505a\u5230\u3002 \u3000\u3000\u4ed6\u89c1\u4e86\u6a80\u660e\u4e0e\u5434\u9e23\u4e16\u4e24\u4eba\u5bf9\u7b54\u4e24\u53e5\uff0c\u4fbf\u5df2\u4f4f\u53e3\u4e0d\u8a00\uff0c\u5fc3\u91cc\u96be\u53d7\uff0c\u53c8\u81ea\u7eb3\u95f7\uff0c\u4ed6\u6df1\u77e5\u5434\u9e23 \u4e16\u7684\u4e3a\u4eba\uff0c\u4e0d\u7ba1\u5fc3\u91cc\u600e\u6837\uff0c\u5bf9\u4eba\u5374\u603b\u662f\u9762\u5e26\u7b11\u5bb9\uff0c\u5373\u4f7f\u5bf9\u201c\u795e\u624b\u201d\u6218\u98de\u3001\u4e03\u5de7\u8ffd\u9b42\u7b49\u4eba\uff0c\u4e5f \u4ece\u6765\u6ca1\u6709\u9732\u51fa\u50cf\u6b64\u523b\u4e00\u822c\u7684\u795e\u8272\uff0c\u5251\u7709\u5fae\u76b1\uff0c\u6b63\u60f3\u8bf4\u51e0\u53e5\u8bdd\uff0c\u6765\u6253\u5f00\u4ed6\u4e24\u4eba\u4e4b\u95f4\u7684\u50f5\u5c40\u3002 \u3000\u3000\u54ea\u77e5\u201c\u795e\u624b\u201d\u6218\u98de\u7a81\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u88f4\u5927\u5148\u751f\u8363\u767b\u76df\u4e3b\u4e4b\u4f4d\uff0c\u672c\u5df2\u662f\u5927\u559c\u4e4b\u4e8b\uff0c\u5374 \u60f3\u4e0d\u5230\u88f4\u5927\u5148\u751f\u53c8\u662f\u6a80\u5927\u4fa0\u7684\u6545\u4eba\uff0c\u90a3\u66f4\u662f\u559c\u4e0a\u52a0\u559c\uff0c\u6b64\u540e\u6211\u6c5f\u5357\u6b66\u6797\u540c\u9053\uff0c\u6cbe\u7740\u88f4\u5927\u5148\u751f \u7684\u5149\uff0c\u4e5f\u5fc5\u80fd\u5728\u6a80\u5927\u4fa0\u624b\u4e0b\u8ba8\u53e3\u996d\u5403\u4e86\u3002\u7fa4\u8c6a\u95fb\u8a00\u4e00\u6115\uff1a\u201c\u2018\u795e\u624b\u2019\u6218\u98de\u600e\u4ed6\u8bf4\u51fa\u8fd9\u822c\u6cc4\u6c14 \u7684\u8bdd\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u5fae\u76b1\uff0c\u6b63\u5f85\u5f00\u53e3\uff0c\u5374\u542c\u201c\u795e\u624b\u201d\u6218\u98de\u53c8\u81ea\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u6b64\u523b \u867d\u7136\u5df2\u662f\u5144\u5f1f\u4eec\u7684\u76df\u4e3b\u5927\u54e5\uff0c\u4f46\u5374\u548c\u5144\u5f1f\u4eec\u76f8\u8bc6\u4e0d\u4e45\uff0c\u5144\u5f1f\u4eec\u53ea\u77e5\u88f4\u5927\u5148\u751f\u6b66\u529f\u6781\u9ad8\uff0c\u5374\u4e0d \u6df1\u77e5\u662f\u4f55\u6d3e\u9ad8\u4eba\uff0c\u4eca\u65e5\u4e00\u542c\u6a80\u5927\u4fa0\u4e4b\u8a00\uff0c\u624d\u77e5\u9053\u88f4\u5927\u5148\u751f\u539f\u6765\u81ea\u5e7c\u5373\u548c\u6a80\u5927\u4fa0\u5728\u4e00\u8d77\uff0c\u90a3\u4e48 \u88f4\u5927\u5148\u751f\u7684\u6b66\u529f\uff0c\u60f3\u5fc5\u4e5f\u662f\u548c\u6a80\u5927\u4fa0\u540c\u6e90\u7684\u4e86\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u53c8\u81ea\u4e00\u76b1\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u4e5f\u5fd9\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u636e\u5c0f\u53ef\u6240\u77e5\uff0c\u88f4\u5144 \u867d\u5728\u6a80\u5927\u4fa0\u95e8\u4e0b\u591a\u5e74\uff0c\u6b66\u529f\u5374\u662f\u79bb\u5f00\u6a80\u5927\u4fa0\u4e4b\u540e\u6240\u4e60\u7684\u54e9\uff0c\u6a80\u5927\u4fa0\uff0c\u4e0d\u77e5\u662f\u4e5f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u4e00\u52a8\uff0c\u81ea\u5e7c\u53ca\u957f\u7684\u5b66\u6b66\u7ecf\u8fc7\uff0c\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u7a81\u5730\u81ea\u4ed6\u5fc3\u5934\u4e00\u95ea\u800c\u8fc7\uff0c\u4ed6\u60f3\u8d77 \u4ed6\u5728\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e4b\u4e2d\uff0c\u5982\u4f55\u4e60\u6b66\uff0c\u5982\u4f55\u88ab\u4eba\u79f0\u4e3a\u8822\u624d\uff0c\u5982\u4f55\u8fde\u4e2a\u666e\u901a\u7684\u8d9f\u5b50\u624b\u90fd\u6253\u4e0d\u8fc7\uff0c \u4f7f\u5f97\u4ed6\u81ea\u5df1\u4e5f\u6df1\u4fe1\u81ea\u5df1\u4e0d\u662f\u7ec3\u6b66\u7684\u6750\u6599\u3002 \u3000\u3000\u4f46\u662f\u76f4\u5230\u4eca\u65e5\uff0c\u4ed6\u7684\u5931\u53bb\u7684\u81ea\u4fe1\u4e4b\u5fc3\uff0c\u5374\u53c8\u590d\u56de\u8f6c\uff0c\u4ed6\u5728\u6628\u3001\u524d\u65e5\u4e24\u65e5\u4e4b\u95f4\uff0c\u5728\u201c\u91d1\u7ae5 \u7389\u5973\u201d\u95e8\u4e0b\uff0c\u4ec5\u4ec5\u5b66\u4e86\u4e24\u65e5\u6b66\u529f\uff0c\u4f46\u4e4d\u4e00\u51fa\u624b\uff0c\u4fbf\u5df2\u9707\u60ca\u7fa4\u8c6a\u2026\u2026 \u3000\u3000\u4ed6\u867d\u7136\u76f4\u80a0\u76f4\u8179\uff0c\u4f46\u6b64\u523b\u5fc3\u5ff5\u52a8\u5904\uff0c\u4ea6\u4e0d\u7981\u7591\u4e91\u5927\u8d77\u3002 \u3000\u3000\u201c\u96be\u9053\u4ee5\u524d\u4ea6\u4e0d\u662f\u6211\u7b28\uff0c\u800c\u53ea\u662f\u6a80\u5927\u53d4\u4e0d\u613f\u6559\u6211\u6b66\u529f\uff0c\u662f\u4ee5\u6545\u610f\u9a97\u6211\u4e48\uff1f\u201d\u62ac\u5934\u671b\u53bb\uff0c \u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u6781\u4e3a\u96be\u770b\uff0c\u4ed6\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u53c8\u81ea\u5fd6\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u5982\u4e0d\u662f\u6a80\u5927 \u53d4\u5c06\u6211\u6536\u517b\uff0c\u6b64\u523b\u53ea\u6015\u65e9\u5df2\u51bb\u6b7b\u997f\u6b7b\uff0c\u4ed6\u7eb5\u7136\u4e0d\u4f20\u6388\u6211\u6b66\u529f\uff0c\u4e5f\u4e00\u5b9a\u662f\u4e00\u756a\u597d\u610f\u3002\u201d\u60f3\u5230\u8fd9 \u91cc\uff0c\u4fbf\u4e0d\u518d\u60f3\uff0c\u4ed6\u5b85\u5fc3\u4ec1\u539a\uff0c\u751f\u6015\u81ea\u5df1\u518d\u60f3\u4e0b\u53bb\uff0c\u53c8\u5bf9\u6a80\u5927\u53d4\u6000\u7591\u3002 \u3000\u3000\u5374\u542c\u201c\u795e\u624b\u201d\u6218\u98de\u53c8\u9053\uff1a\u201c\u5144\u5f1f\u6211\u76f4\u5230\u4eca\u65e5\uff0c\u624d\u77e5\u9053\u88f4\u5927\u5148\u751f\u539f\u6765\u662f\u6614\u5e74\u540d\u9707\u6cb3\u6714\u5927\u82f1 \u96c4\u2014\u2014\u2018\u67aa\u5251\u65e0\u6545\u2019\u88f4\u6c0f\u53cc\u4fa0\u7684\u540e\u4eba\uff0c\u5173\u4e8e\u88f4\u6c0f\u53cc\u4fa0\u751f\u524d\u7684\u79cd\u79cd\u82f1\u98ce\u8c6a\u8ff9\uff0c\u5144\u5f1f\u867d\u6068\u672a\u80fd\u773c \u89c1\uff0c\u5374\u542c\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5bf9\u201c\u795e\u624b\u201d\u6218\u98de\u672c\u65e0\u597d\u611f\uff0c\u6b64\u523b\u542c\u4ed6\u5ffd\u7136\u8c08\u8d77\u81ea\u5df1\u7684\u4ea1\u7236\uff0c\u5fc3\u5934\u4e00\u98a4\uff0c\u70ed\u8840\u4e0a\u6d8c\uff0c \u4f46\u89c9\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u7eb5\u6709\u5343\u822c\u4e0d\u597d\uff0c\u4f46\u5bf9\u81ea\u5df1\u603b\u662f\u597d\u7684\uff0c\u773c\u7736\u53c8\u4e00\u7ea2\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u7ad9\u8d77\u6765\uff0c \u5411\u6218\u98de\u6df1\u6df1\u4e00\u672d\uff0c\u53c8\u4e00\u8a00\u4e0d\u53d1\u5730\u5750\u4e86\u4e0b\u53bb\uff0c\u4ed6\u53ea\u89c9\u5589\u5934\u54fd\u54bd\uff0c\u5fc3\u91cc\u7eb5\u6709\u5343\u767e\u53e5\u60f3\u8bf4\u7684\u8bdd\uff0c\u5374 \u4e00\u53e5\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u8fde\u5fd9\u957f\u8eab\u800c\u7acb\uff0c\u8eac\u8eab\u8fd8\u793c\uff0c\u4e00\u800c\u6c89\u58f0\u9053\uff1a\u201c\u76df\u4e3b\u82e5\u5bf9\u5c0f\u5f1f\u8fd9\u822c\u5ba2\u6c14\uff0c\u5c82\u975e \u6298\u715e\u5c0f\u5f1f\u4e86\u3002\u201d\u8981\u77e5\u9053\u6b66\u6797\u4e2d\u7684\u73ed\u8f88\uff0c\u5168\u4e0e\u5e74\u9f84\u65e0\u5173\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u867d\u7136\u5df2\u8001\u5f97\u53ef\u505a\u88f4\u73cf\u7684 \u53d4\u4f2f\uff0c\u4f46\u88f4\u73cf\u6b64\u523b\u5df2\u662f\u76df\u4e3b\u8eab\u4efd\uff0c\u662f\u4ee5\u6218\u98de\u81ea\u79f0\u201c\u5c0f\u5f1f\u201d\uff0c\u522b\u4eba\u4e5f\u89c9\u523a\u8033\uff0c\u53ea\u6709\u201c\u91d1\u9e21\u201d\u5411 \u4e00\u557c\u7b49\u4eba\u5fc3\u4e2d\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u4ed6\u8fd9\u822c\u505a\u4f5c\uff0c\u7a76\u7adf\u53c8\u5728\u5f04\u4e9b\u4ec0\u4e48\u7384\u865a\uff1f \u3000\u3000\u53ea\u89c1\u6218\u98de\u7a81\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u88f4\u6c0f\u53cc\u4fa0\u7684\u751f\u524d\u4e8b\u8ff9\uff0c\u5144\u5f1f\u56fa\u662f\u542c\u5f97\u591a\u4e86\uff0c\u88f4\u6c0f\u53cc\u4fa0\u7684 \u6b7b\u56e0\uff0c\u5144\u5f1f\u542c\u5f97\u4e5f\u4e0d\u5c11\uff0c\u672c\u6765\u6b64\u4e8b\u4e0e\u5144\u5f1f\u6beb\u65e0\u5e72\u7cfb\uff0c\u4f46\u6b64\u523b\u88f4\u5927\u5148\u751f\uff0c\u65e2\u7136\u5df2\u662f\u5144\u5f1f\u7684\u76df\u4e3b \u5927\u54e5\uff0c\u90a3\u4e48\u88f4\u5927\u5148\u751f\u7684\u4e8b\uff0c\u4fbf\u5c31\u662f\u5144\u5f1f\u7684\u4e8b\uff0c\u5144\u5f1f\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u5f97\u4e3a\u88f4\u5927\u5148\u751f\u590d\u4ec7\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u9f50\u90fd\u4e00\u6115\uff0c\u8981\u77e5\u9053\u6614\u5e74\u90a3\u8499\u9762\u9ed1\u8863\u4eba\u6740\u5c3d\u6b66\u6797\u9556\u5934\uff0c\u6700\u540e\u4e0e\u201c\u4e2d\u5dde\u4e00\u5251\u201d\u6b27\u9633\u5e73\u4e4b \u540c\u5f52\u4e8e\u5c3d\u4e4b\u4e8b\uff0c\u5f53\u771f\u662f\u9707\u52a8\u5929\u4e0b\uff0c\u6c5f\u6e56\u4e2d\u65e0\u4eba\u4e0d\u77e5\uff0c\u6b64\u523b\u542c\u6218\u98de\u65e7\u4e8b\u91cd\u63d0\uff0c\u7fa4\u8c6a\u5fc3\u4e2d\u4ff1\u90fd\u5927 \u5947\uff01 \u3000\u3000\u201c\u90a3\u9ed1\u8863\u602a\u4eba\u660e\u660e\u5df2\u7ecf\u6b7b\u4e86\uff0c\u96be\u9053\u6218\u795e\u624b\u8fd8\u8981\u627e\u6b7b\u4e86\u7684\u4eba\u590d\u4ec7\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u66f4\u662f\u5fc3\u60c5\u6fc0\u52a8\uff0c\u6da9\u58f0\u9053\uff1a\u201c\u5148\u7236\u4e4b\u4ec7\uff0c\u6211\u65f6\u523b\u672a\u5fd8\uff0c\u4f46\u4ec7\u4eba\u5df2\u6b7b\uff0c\u800c\u4e14\u2026\xb7\xb7\u6211\u90a3 \u4ec7\u4eba\u59d3\u540d\u4e0d\u8be6\uff0c\u8fde\u4e2a\u540e\u4eba\u90fd\u6ca1\u6709\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u9893\u7136\u5730\u5750\u56de\u6905\u4e0a\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u626c\uff0c\u7a81\u7136\u201c\u5427\u201d\u5730\u4e00\u58f0\uff0c\u4ee5\u638c\u51fb\u684c\uff0c\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4eba\u5c3d\u9053\u90a3\u9ed1\u8863\u8499 \u9762\u4eba\u5df2\u6b7b\uff0c\u4f46\u2014\u2014\u54fc\u54fc\uff0c\u6709\u8c01\u771f\u7684\u89c1\u7740\uff0c\u5317\u5e73\u57ce\u5916\u6b7b\u5728\u6b27\u9633\u8001\u9556\u5934\u8eab\u65c1\u4e4b\u4eba\uff0c\u9762\u76ee\u5df2\u88ab\u51fb \u6bc1\uff0c\u53c8\u6709\u8c01\u80fd\u65ad\u8a00\u4ed6\u5c31\u662f\u90a3\u9ed1\u8863\u51f6\u624b\u7684\u6b63\u8eab\u2026\u2026\u54fc\u54fc\uff0c\u6b64\u4e8b\u5176\u4e2d\u5fc5\u5b9a\u5927\u6709\u8e4a\u8df7\uff0c\u8bf4\u4e0d\u5b9a\u90a3\u9ed1 \u8863\u51f6\u624b\u6b64\u523b\u4e0d\u4f46\u8fd8\u5728\u4eba\u4e16\uff0c\u800c\u4e14\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u84e6\u5730\u4e00\u987f\uff0c\u773c\u89d2\u779f\u5904\uff0c\u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u5bd2\u5982\u6c34\uff0c\u96be\u770b\u5df2\u6781\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u81ea\u5f97 \u610f\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u6a80\u5927\u4fa0\uff0c\u4f60\u4e43\u5f53\u4e8b\u4e4b\u4eba\uff0c\u4e0d\u77e5\u5bf9\u6b64\u4e8b\u770b\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u6df1\u6c89\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6b64\u4e8b\u771f\u76f8\uff0c\u672c\u6765\u6781\u4e3a\u7b80\u5355\uff0c\u4f46\u7ecf\u6218\u5e84\u4e3b\u4e00\u8bf4\uff0c\u5374\u53cd \u4f3c\u53d8\u5f97\u590d\u6742\u8d77\u6765\u4e86\uff0c\u6218\u5e84\u4e3b\u5982\u679c\u2026\u2026\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u622a\u65ad\u4e86\u4ed6\u7684\u6d3b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6b64\u4e8b\u771f\u76f8\u7a76\u7adf\u5982\u4f55\uff0c\u65e5\u4e45\u81ea \u77e5\uff0c\u53cd\u6b63\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\uff0c\u4e16\u95f4\u6ca1\u6709\u80fd\u5305\u706b\u4e4b\u7eb8\uff0c\u4e5f\u6ca1\u6709\u6c38\u8fdc\u53ef\u4ee5\u9690\u85cf\u4e4b\u4e8b\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u5730\u5927\u559d\u9053\uff1a\u201c\u51e1\u6211\u6c5f\u5357\u540c\u76df\uff0c\u6b64\u540e\u4eba\u4eba\u90e8\u5f97\u5c06\u76df\u4e3b\u5927\u54e5\u8fd9\u4ef6\u8840\u6d77\u6df1\u4ec7\uff0c \u6709\u5982\u81ea\u5df1\u8840\u6d77\u6df1\u4ec7\u4e00\u6837\u5730\u6df1\u94ed\u5fc3\u4e0a\uff0c\u65f6\u65f6\u523b\u523b\uff0c\u90fd\u5f97\u4e3a\u63a2\u5bfb\u6b64\u4e8b\u7684\u771f\u76f8\u52aa\u529b\u3002\u201d\u8bf4\u7f62\uff0c\u4e3e\u8d77 \u9152\u676f\uff0c\u5927\u559d\u9053\uff1a\u201c\u4e3a\u6b64\u76ee\u6807\uff0c\u4e14\u4e8e\u4e00\u676f\uff01\u201d \u3000\u3000\u5385\u4e0a\u7fa4\u8c6a\uff0c\u7a81\u7136\u4e00\u5f81\uff0c\u4f46\u90fd\u4e3e\u676f\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u76ee\u5149\u95ea\u95ea\uff0c\u987e\u76fc\u4e4b\u95f4\uff0c\u795e\u91c7\u6bd5 \u9732\uff0c\u201c\u9f99\u5f62\u516b\u638c\u2018\u867d\u4ecd\u9762\u8272\u6df1\u6c89\uff0c\u4e00\u65e0\u8868\u60c5\uff0c\u4f46\u4ed6\u5fc3\u91cc\u7a76\u7adf\u5728\u60f3\u4e9b\u4ec0\u4e48\uff0c\u5374\u518d\u65e0\u4e00\u4eba\u80fd\u591f\u731c \u5230\uff01\u88f4\u73cf\u66f4\u662f\u5fc3\u4e2d\u6fc0\u52a8\uff0c\u5589\u5934\u54fd\u54bd\uff0c\u4e3e\u676f\u996e\u5c3d\u676f\u4e2d\u4e4b\u9152\uff0c\u9152\u5165\u80a0\u4e2d\uff0c\u66f4\u5316\u4e3a\u6ee1\u8154\u70ed\u8840\uff0c\u65e5\u5149 \u52a8\u5904\u2014\u2014\u4ed6\u8fd9\u6ee1\u8154\u70ed\u8840\uff0c\u7adf\u4e0d\u7531\u81ea\u4e3b\u5730\u51dd\u7ed3\u4f4f\u4e86\u3002\u5927\u5385\u4e00\u7247\u559d\u58f0\u4e4b\u4e2d\uff0c\u5385\u5916\u7a81\u5730\u7f13\u7f13\u8d70\u5165\u4e00 \u4e2a\u4eba\u6765\u3002\u53ea\u89c1\u6b64\u4eba\u79c0\u53d1\u62ab\u80a9\uff0c\u957f\u886b\u66f3\u5730\uff0c\u9762\u8272\u82cd\u81ea\uff0c\u6709\u5982\u83b9\u738b\uff0c\u4e00\u53cc\u660e\u4eae\u7684\u773c\u775b\uff0c\u5374\u53c8\u4f3c\u5728 \u8fd9\u4e00\u7247\u83b9\u7389\u95ee\u5d4c\u5165\u7684\u4e24\u9897\u660e\u73e0\u3002\u5979\u6765\u5f97\u867d\u7136\u6f2b\u65e0\u58f0\u606f\uff0c\u4f46\u5385\u4e2d\u7fa4\u8c6a\uff0c\u5374\u90fd\u4f3c\u53d7\u4e86\u5979\u65e0\u5f62\u7684\u5438 \u5f15\uff0c\u4e00\u4e2a\u4e2a\u8f6c\u8fc7\u5934\u6765\u3002\u201d\u2019\u9f99\u5973\u2018\u6a80\u6587\u742a\uff01\u201c\u201d\u4e0d\u77e5\u662f\u8c01\uff0c\u5728\u89d2\u843d\u4e2d\u4f4e\u547c\u4e00\u58f0\uff0c\u4e8e\u662f\u6ee1\u5385\u4e4b \u4e2d\uff0c\u4f46\u542c\u4f4e\u547c\u201c\u9f99\u5973\u201d\u4e4b\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\u3002\u4f46\u8fd9\u4e00\u5207\u58f0\u97f3\uff0c\u6a80\u6587\u742a\u5374\u90fd\u6839\u672c\u6ca1\u6709\u542c\u5728\u8033\u91cc\uff0c\u50cf \u4ee5\u5f80\u90a3\u6b21\u4e00\u6837\uff0c\u6b64\u523b\u5979\u773c\u4e2d\u6240\u89c1\uff0c\u53ea\u6709\u88f4\u73cf\u7684\u58f0\u5f62\uff0c\u8033\u4e2d\u6240\u542c\uff0c\u53ea\u6709\u88f4\u73cf\u7684\u58f0\u97f3\uff0c\u5979\u4e5f\u4e0d\u77e5 \u9053\u8fd9\u7a76\u7adf\u662f\u4ec0\u4e48\u529b\u91cf\uff0c\u8fd9\u529b\u91cf\u7684\u6765\u5904\u50cf\u662f\u90a3\u4e48\u9065\u8fdc\uff0c\u4f46\u5374\u53c8\u90a3\u4e48\u7684\u771f\u5b9e\uff0c\u9065\u8fdc\u7684\u5c31\u50cf\u6b64\u523b\u6620 \u5728\u5979\u79c0\u53d1\u4e0a\u7684\u9633\u5149\uff0c\u771f\u5b9e\u5730\u4e5f\u6b63\u5982\u9633\u5149\uff0c\u5979\u751a\u81f3\u4e0d\u7528\u611f\u89c9\uff0c\u5c31\u77e5\u9053\u8fd9\u529b\u91cf\u7684\u5b58\u5728\uff0c\u6b63\u5982\u5979\u77e5 \u9053\u9633\u5149\u7684\u5b58\u5728\u4e00\u6837\u3002\u9633\u5149\uff0c\u5c06\u5979\u7684\u5f71\u5b50\uff0c\u957f\u957f\u5730\u6620\u5728\u5730\u4e0a\u3002\u5730\u4e0a\u5979\u957f\u957f\u7684\u5f71\u5b50\uff0c\u7f13\u7f13\u5411\u524d\u79fb \u52a8\u7740\uff0c\u88f4\u73cf\u4e5f\u7f13\u7f13\u8f6c\u8fc7\u5e2d\u9762\uff0c\u6e10\u6e10\uff0c\u5979\u7684\u5f71\u5b50\u89e6\u5230\u4ed6\u7684\u811a\u5c16\uff0c\u4e5f\u6b63\u5982\u5979\u7684\u56fa\u5149\u65e9\u5df2\u89e6\u7740\u4ed6\u7684 \u76ee\u5149\u4e00\u6837\u3002\u76ee\u5149\uff0c\u50cf\u56db\u6761\u65e0\u5f62\u7684\u7ebf\uff0c\u7d27\u7d27\u5730\u7ea0\u7f20\u4e00\u8d77\uff0c\u5979\u5fd8\u4e86\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u4ed6\u4e5f\u5fd8\u4e86\u8fd9\u662f \u4ec0\u4e48\u5730\u65b9\uff0c\u5979\u542c\u4e0d\u89c1\u4efb\u4f55\u58f0\u97f3\uff0c\u4ed6\u53c8\u4f55\u5e38\u542c\u5f97\u89c1\uff0c\u5979\u5f20\u5f00\u53e3\uff0c\u6ca1\u6709\u8bf4\u51fa\u8bdd\uff0c\u4ed6\u5f20\u5f00\u53e3\uff0c\u4e5f\u6ca1 \u6709\u58f0\u97f3\uff01\u65e0\u58f0\u5730\u60c5\u611f\u4ea4\u6d41\uff0c\u65e0\u58f0\u4e2d\u4e24\u5fc3\u76f8\u6295\u3002\u201c\u9f99\u5f62\u516b\u638c\u201d\u5e72\u54b3\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u742a\u513f\uff0c\u4f60\u600e\u4e48 \u4e5f\u6765\u4e86\u2026\u2026\u742a\u513f\uff0c\u4f60\u600e\u4e48\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u8bf4\u4e86\u4e24\u6b21\uff0c\u7b2c\u4e8c\u6b21\u7684\u58f0\u97f3\u8bf4\u5f97\u6bd4\u7b2c\u4e00\u6b21\u66f4\u5927\u3002 \u3000\u3000\u4e8e\u662f\u6a80\u6587\u742a\u4f4e\u5e94\u4e00\u58f0\u3002 \u3000\u3000\u201c\u6211\u6765\u4e86\u3002\u201d\u4f46\u5979\u7684\u76ee\u5149\uff0c\u5374\u8fd8\u505c\u7559\u5728\u88f4\u73cf\u7684\u8138\u4e0a\u3002 \u3000\u3000\u5385\u5185\u7fa4\u8c6a\uff0c\u6b64\u523b\u5343\u767e\u76ee\u5149\uff0c\u5ffd\u800c\u671b\u5411\u201c\u9f99\u5973\u201d\u6a80\u6587\u742a\uff0c\u5ffd\u800c\u671b\u5411\u88f4\u73cf\uff0c\u4f46\u89c9\u8fd9\u4e00\u7537\u4e00 \u5973\uff0c\u5973\u7684\u56fa\u662f\u767e\u5a9a\u5343\u5a07\uff0c\u7f8e\u8273\u4e0d\u53ef\u65b9\u7269\uff0c\u7537\u7684\u66f4\u662f\u82f1\u59ff\u633a\u79c0\uff0c\u5b9b\u5982\u4e34\u98ce\u7389\u6811\uff0c\u518d\u89c1\u4e86\u4ed6\u4eec\u7684 \u795e\u60c5\uff0c\u5fc3\u4e2d\u5404\u5404\u867d\u90fd\u6697\u7b11\u4ed6\u4eec\u7684\u75f4\uff0c\u81ea\u5df1\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u7adf\u4e5f\u770b\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u6b64\u523b\u5385\u5916\u7a81\u53c8\u95ef\u5165\u4e00\u4e2a\u4eba\u6765\uff0c\u76ee\u5149\u56db\u4e0b\u4e00\u8f6c\uff0c\u779f\u4e86\u4f17\u4eba\u4e00\u773c\uff0c\u6084\u6084\u7ed5\u8fc7\u201c\u9f99\u5f62\u516b\u638c\u201d\u8eab \u4fa7\uff0c\u8d70\u5230\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u524d\uff0c\u6218\u98de\u672c\u4e5f\u5728\u5446\u5446\u5730\u671b\u7740\uff0c\u90a3\u6c49\u5b50\u4f4e\u54b3\u4e00\u58f0\uff0c\u6218\u98de\u8f6c\u8fc7\u76ee\u5149\uff0c\u6d53 \u7709\u4e00\u626c\uff0c\u6084\u7136\u8d77\u7acb\uff0c\u9000\u540e\u6570\u6b65\uff0c\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u90a3\u59d3\u6a80\u7684\u5728\u5e84\u5916\u53ef\u6709\u57cb\u4f0f\uff1f\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u6b63\u662f\u6218\u98de\u65b9\u624d\u6d3e\u51fa\u5e84\u5916\u6253\u63a2\u654c\u60c5\u4e4b\u4eba\uff0c\u76ee\u5149\u53c8\u659c\u779f\u6a80\u660e\u4e00\u773c\uff0c\u5fae\u5fae\u6447\u9996\uff0c\u6218\u98de\u6d53 \u7709\u4e00\u626c\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u59d3\u6a80\u7684\u4f60\u8001\u5403\u8001\u505a\uff0c\u6709\u6043\u65e0\u6050\uff0c\u82e5\u4e0d\u662f\u6b64\u523b\u4f60\u5df2\u53e6\u6709\u6253\u7b97\uff0c\u6211 \u5012\u8981\u53eb\u4f60\u5c1d\u5c1d\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u7684\u5389\u5bb3\u3002\u201d\u888d\u8896\u4e00\u62c2\uff0c\u6b63\u5f85\u8d70\u56de\u5ea7\u4e2d\uff0c\u5374\u89c1\u90a3\u6c49\u5b50\u76ee\u5149\u4e00\u8f6c\uff0c\u6084 \u58f0\u9053\uff0c\u201c\u5e84\u5916\u867d\u65e0\u5f02\u52a8\uff0c\u4f46\u5c0f\u4eba\u5374\u5728\u5e84\u540e\u89c1\u5230\u4e00\u5904\u6d6e\u677e\u6ce5\u4e0a\uff0c\u4f3c\u662f\u65b0\u6398\u7684\u575f\u5893\u2026\u2026\u201d\u6218\u98de\u6d53 \u7709\u53c8\u4e00\u626c\uff0c\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u65b0\u575f\uff0c\u5e84\u540e\u600e\u4f1a\u6709\u65b0\u575f\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u4f4e\u8bed\u6309\u9053\uff1a\u201c\u5c0f\u4eba\u5fc3\u91cc\u4e5f\u5728\u5947\u602a\uff0c\u4fbf\u5524\u4e86\u4e09\u4e24\u4e2a\u5144\u5f1f\uff0c\u6398\u5f00\u4e00\u770b\u2014\u2014\u201d\u6218\u98de\u76b1\u7709 \u9053\uff1a\u201c\u91cc\u9762\u662f\u4ec0\u4e48\uff1f\u201d\u90a3\u6c49\u5b50\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u91cc\u9762\u679c\u662f\u4e00\u5177\u5c38\u9996\uff0c\u5c0f\u4eba\u867d\u4e0d\u8ba4\u5f97\uff0c\u4f46\u636e\u5916\u5e84\u7684 \u4faf\u5174\u6c11\u8bf4\uff0c\u8fd9\u5c38\u9996\u5c31\u662f\u90a3\u4e13\u95e8\u51fa\u5356\u6d88\u606f\u7684\u2018\u5feb\u8baf\u2019\u82b1\u7389\uff0c\u4ed6\u5c38\u8eab\u867d\u5df2\u63a9\u57cb\uff0c\u4f46\u5c38\u9996\u672a\u50f5\uff0c\u663e \u89c1\u5f97\u6b7b\u53bb\u4e0d\u4e45\uff0c\u5c24\u5176\u5947\u602a\u7684\u662f\uff0c\u5168\u8eab\u4e0a\u4e0b\u4e00\u65e0\u6bd4\u75d5\uff0c\u6253\u5f00\u957f\u670d\u4e00\u770b\uff0c\u53ea\u6709\u5f53\u80f8\u4e00\u4e2a\u7d2b\u9ed1\u638c \u5370\uff0c\u7adf\u662f\u88ab\u4eba\u5bb6\u4e00\u638c\u51fb\u6b7b\uff0c\u5374\u4e0d\u77e5\u4ed6\u5c38\u8eab\u662f\u8c01\u63a9\u57cb\u7684\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u6df1\u76b1\uff0c\u201c\u5440\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u542c\u8fd9\u6c49\u5b50\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4ef6\u5947\u602a\u4e4b\u4e8b\u2026\u2026\u201d \u6218\u98de\u53f1\u9053\uff1a\u201c\u5feb\u8bf4\uff01\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u9053\uff1a\u201c\u5728\u90a3\u65b0\u575f\u4e0d\u8fdc\u4e4b\u5904\uff0c\u5730\u4e0a\u7adf\u88ab\u4eba\u7528\u6307\u7532\u5212\u4e86\u56db\u4e2a\u5b57\u8ff9\uff0c\u5199\u7684\u662f\u2018\u53ea\u4f1a\u4e00 \u62db\u2019\u3002\u8fd9\u5b57\u65e0\u5934\u65e0\u5c3e\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u5c0f\u4eba\u518d\u5c06\u2018\u5feb\u8baf\u2019\u82b1\u7389\u7684\u5c38\u4f53\u4ed4\u7ec6\u68c0\u67e5\u4e00\u904d\uff0c \u53d1\u73b0\u4ed6\u53f3\u624b\u98df\u4e2d\u4e8c\u6307\u7684\u6307\u7532\u7f1d\u4e2d\uff0c\u6ee1\u8eab\u6ce5\u571f\uff0c\u663e\u89c1\u8fd9\u56db\u5b57\u4e5f\u662f\u4ed6\u4e34\u6b7b\u4e4b\u524d\u5199\u7684\u3002\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u672c\u662f\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u4e00\u4e2a\u5f97\u529b\u624b\u4e0b\uff0c\u6b66\u529f\u867d\u4e0d\u9ad8\uff0c\u4f46\u89c2\u5bdf\u4e8b\u7269\uff0c\u4ed4\u7ec6\u8c28\u614e\uff0c\u5374\u662f \u4e00\u628a\u597d\u624b\uff0c\u662f\u4ee5\u6218\u98de\u624d\u4f1a\u6d3e\u4ed6\u51fa\u53bb\u6253\u63a2\u3002 \u3000\u3000\u6218\u98de\u95fb\u8a00\u6c89\u601d\u534a\u664c\uff0c\u7a81\u5730\u4f38\u51fa\u53f3\u638c\uff0c\u98df\u6307\u5fae\u66f2\uff0c\u5176\u4f59\u56db\u6307\u5374\u4f38\u5f97\u7b14\u76f4\uff0c\u6709\u5982\u731c\u62f3\u884c\u4ee4\u65f6 \u6240\u65bd\u7684\u624b\u52bf\u4e00\u822c\uff0c\u987a\u624b\u4e00\u6325\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u9762\u4e0a\u7acb\u65f6\u9732\u51fa\u559c\u8272\uff0c\u5012\u9000\u4e09\u6b65\u8eac\u8eab\u4e00\u793c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u591a\u8c22\u5e84\u4e3b\u6069\u8d50\u3002\u201d\u518d\u9000\u4e09 \u6b65\uff0c\u8f6c\u8eab\u800c\u53bb\uff0c\u539f\u6765\u201c\u795e\u624b\u201d\u6218\u98de\u4eba\u867d\u9634\u9e37\u51f6\u72e1\uff0c\u5374\u662f\u67ad\u96c4\u4e4b\u624d\uff0c\u7edf\u4ee4\u4e4b\u4e0b\uff0c\u8d4f\u7f5a\u6781\u660e\uff0c\u65b9 \u624d\u90a3\u4e00\u4e2a\u624b\u5f0f\uff0c\u4fbf\u662f\u4ee4\u4ed6\u8d50\u8d4f\u4e4b\u610f\uff0c\u8d4f\u7684\u662f\u4ed6\u89c2\u5bdf\u4ed4\u7ec6\uff0c\u82e5\u6362\u4e86\u4e00\u4e2a\u7c97\u5fc3\u5927\u610f\u4e4b\u4eba\uff0c\u83ab\u8bf4\u770b \u4e0d\u51fa\u90a3\u5730\u4e0a\u5b57\u8ff9\u4e0e\u6307\u7f1d\u4e2d\u6ce5\u571f\uff0c\u4fbf\u662f\u90a3\u4e00\u5806\u65b0\u575f\uff0c\u53ea\u6015\u4e5f\u4f1a\u5ffd\u7565\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4fef\u9996\u6c89\u601d\u534a\u664c\uff0c\u5634\u89d2\u7a81\u5730\u73b0\u51fa\u4e00\u4e1d\u68ee\u51b7\u7684\u7b11\u5bb9\uff0c\u6697\u4e2d\u4f4e\u8bed\u9053\uff1a\u201c\u82b1\u7389\u5440\uff01 \u82b1\u7389\uff01\u4f60\u4e00\u751f\u51fa\u5356\u79d8\u5bc6\uff0c\u4e34\u6b7b\u524d\u5374\u8fd8\u5c06\u4e00\u4ef6\u79d8\u5bc6\u76f8\u544a\u4e8e\u6211\uff0c\u53ea\u53ef\u60dc\u6211\u867d\u6709\u5fc3\u7ed9\u4f60\u8d4f\u8d50\uff0c\u4f60\u5374 \u6c38\u8fdc\u65e0\u6cd5\u62ff\u5230\u4e86\u3002\u201d\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u90a3\u201c\u9f99\u5973\u201d\u6a80\u6587\u742a\u6b64\u523b\u5df2\u7ad9\u5230\u5979\u7239\u7239\u8eab\u4fa7\uff0c\u53ea\u89c1\u5979\u90a3\u4e00 \u53cc\u5982\u5e7d\u5982\u6155\u7684\u79cb\u6ce2\uff0c\u5374\u8fd8\u671b\u5728\u88f4\u73cf\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u672c\u5c31\u7ad9\u5728\u88f4\u73cf\u8eab\u65c1\uff0c\u6b64\u523b\u88f4\u73cf\u7f13\u7f13\u8d70\u56de\u5ea7\u4e2d\uff0c\u811a\u6b65\u661f\u79fb\uff0c\u76ee\u5149\u5374\u672a \u66fe\u79fb\u52a8\u534a\u5206\uff0c\u5434\u9e23\u4e16\u8f7b\u54b3\u4e00\u58f0\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u76df\u4e3b\u5927\u54e5\uff0c\u8fd9\u4f4d\u60f3\u5fc5\u5c31\u662f\u6a80\u59d1\u5a18\u5427\uff1f\u201d \u3000\u3000\u88f4\u73cf\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u4e2d\u5374\u5927\u4e3a\u5947\u602a\u3002 \u3000\u3000\u201c\u8fd9\u5927\u5385\u4e2d\u6240\u6709\u6b66\u6797\u8c6a\u58eb\uff0c\u83ab\u4e0d\u77e5\u5979\u5c31\u662f\u6a80\u6587\u742a\uff0c\u4ed6\u81ea\u5df1\u77e5\u9053\uff0c\u5374\u53c8\u4e3a\u4f55\u518d\u95ee\uff1f\u201d\u7eed\u53c8 \u60f3\u5230\uff1a\u201c\u5947\u602a\uff01\u4ed6\u4e00\u5411\u4e0e\u6211\u4eb2\u8fd1\uff0c\u4f46\u8fd9\u4e00\u58f0\u2018\u76df\u4e3b\u5927\u54e5\uff0c\u5374\u53c8\u4e3a\u4f55\u53eb\u5f97\u5982\u6b64\u751f\u4efd\uff1f\u201d\u4e00\u5ff5\u81f3 \u6b64\uff0c\u4ed6\u5fc3\u5934\u4e00\u51db\uff0c\u8f6c\u56de\u76ee\u5149\uff0c\u6b63\u895f\u5371\u5750\u8d77\u6765\uff0c\u8981\u77e5\u9053\u5434\u9e23\u4e16\u90a3\u4e00\u53e5\u95ee\u8bdd\uff0c\u91cd\u70b9\u539f\u5728\u524d\u9762\u201c\u76df \u4e3b\u5927\u54e5\u2019\u56db\u5b57\uff0c\u6b64\u523b\u88f4\u73cf\u5fc3\u5ff5\u6570\u8f6c\uff0c\u4ed6\u867d\u7136\u6027\u60c5\u62d7\u76f4\uff0c\u5374\u6781\u4e3a\u806a\u660e\uff0c\u5fc3\u4e0b\u4fbf\u5df2\u604d\u7136\uff0c\u77e5\u9053\u5434 \u9e23\u4e16\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5e76\u975e\u95ee\u4ed6\u6a80\u6587\u742a\uff0c\u800c\u662f\u63d0\u9192\u4ed6\u81ea\u5df1\u6b64\u523b\u7684\u8eab\u4efd\uff0c\u4f46\u76ee\u5149\u5782\u4e0b\u534a\u664c\uff0c\u5fc3\u91cc\u5374\u4ecd \u7981\u4e0d\u4f4f\u8981\u62ac\u5934\u671b\u5979\u4e24\u773c\uff0c\u5434\u9e23\u4e16\u6697\u53f9\u4e00\u58f0\uff0c\u77e5\u9053\u4ed6\u949f\u60c5\u5df2\u6df1\uff0c\u4e16\u4e0a\u7684\u4efb\u4f55\u4e8b\u5728\u4ed6\u773c\u4e2d\uff0c\u4f3c\u4e4e \u90fd\u5df2\u4e0d\u53ca\u6a80\u6587\u742a\u7684\u4e00\u77a5\u91cd\u8981\u3002\u5434\u9e23\u4e16\u8eab\u4e16\u5947\u8be1\uff0c\u81ea\u5e7c\u95ef\u8361\u6c5f\u6e56\uff0c\u591a\u5e74\u7684\u78e8\u7ec3\uff0c\u4f7f\u5f97\u4ed6\u6027\u60c5\u9010 \u6e10\u53d8\u6210\u6de1\u8584\uff0c\u6b64\u523b\u89c1\u88f4\u73cf\u4e0e\u6a80\u6587\u742a\u7684\u5982\u6b64\u6df1\u60c5\uff0c\u60f3\u5230\u81ea\u5df1\u80f8\u4e2d\u7684\u5bc2\u5bde\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u53ea\u89c9\u5fc3\u4e2d \u7a7a\u7a7a\u6d1e\u6d1e\uff0c\u5168\u65e0\u4e00\u4e1d\u5bc4\u60c5\u4e4b\u5904\u3002\u201d\u795e\u624b\u201c\u6218\u98de\u56de\u5230\u5ea7\u4e2d\uff0c\u8fd9\u4e00\u5e2d\u672c\u662f\u5c45\u4e2d\u800c\u6446\uff0c\u5ea7\u4e0a\u7684\u5341\u56db \u4e2a\u4eba\uff0c\u9664\u4e86\u201d\u5317\u6597\u4e03\u715e\u201c\u3001\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u3001\u201d\u91d1\u9e21\u201c\u5411\u4e00\u557c\u7b49\u516d\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u7684\u4e43\u662f\u4e1c\u65b9\u5144 \u5f1f\u3001\u201d\u9f99\u5f62\u516b\u638c\u201c\uff0c\u4ee5\u53ca\u4ed6\u81ea\u5df1\u548c\u88f4\u73cf\uff0c\u6b64\u523b\u518d\u52a0\u4e0a\u7ad9\u5728\u65c1\u8fb9\u7684\u5434\u9e23\u4e16\u4e0e\u6a80\u6587\u742a\uff0c\u4fbf\u5c06\u8fd9\u4e00 \u5f20\u7279\u5927\u7684\u5e2d\u9762\uff0c\u6324\u5f97\u6ee1\u65e0\u7a7a\u9699\uff0c\u53ea\u662f\u8fd9\u4e00\u5341\u516d\u4eba\uff0c\u6b64\u523b\u5fc3\u4e2d\u5404\u6709\u5fc3\u4e8b\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u4e3e\u676f\u52a8 \u7bb8\uff0c\u66f4\u6ca1\u6709\u4e00\u4eba\u8bf4\u51fa\u8bdd\u6765\u3002\u65c1\u5e2d\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u89c1\u4e86\u4e3b\u4eba\u5982\u6b64\uff0c\u60c5\u51b5\u81ea\u4e5f\u53d8\u5f97\u5341\u5206\u843d\u5bde\uff0c\u8fd9\u4e00 \u573a\u672c\u8be5\u8f70\u8f70\u70c8\u70c8\uff0c\u70ed\u70ed\u95f9\u95f9\uff0c\u6b66\u6797\u8c6a\u58eb\u7fa4\u96c6\u7684\u201d\u76df\u4e3b\u76db\u4f1a\u201c\u6b64\u523b\u4e3a\u4e86\u60c5\u52bf\u4e4b\u79cd\u79cd\u53d8\u5316\uff0c\u7adf\u53d8 \u5f97\u50cf\u4e2a\u65af\u65af\u6587\u6587\uff0c\u6587\u6587\u9759\u9759\u7684\u6587\u4eba\u96c5\u96c6\uff0c\u53ea\u662f\u5374\u8fde\u4e00\u53e5\u541f\u54e6\u4e4b\u58f0\u90fd\u6ca1\u6709\u3002\u201d\u2018\u795e\u624b\u201c\u6218\u98de\u76ee \u5149\u8f6c\u5904\uff0c\u5927\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6a80\u59d1\u5a18\u8fdc\u9053\u800c\u6765\uff0c\u7adf\u8fde\u4e2a\u5ea7\u4f4d\u90fd\u6ca1\u6709\uff0c\u8001\u592b\u771f\u662f\u5931\u793c\u7684\u5f88\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u76ee\u5149\u4e00\u5782\uff0c\u8f7b\u8bed\u9053\uff1a\u201c\u4e0d\u7528\u2026\u2026\u6211\u662f\u6765\u770b\u770b\u2026\u2026\u5c31\u8981\u8d70\u7684\u3002\u201d\u5ffd\u5730\u77a5\u89c1\u5ea7\u4e2d\u6709\u4e2a \u9762\u8272\u60e8\u767d\uff0c\u76ee\u5149\u72e1\u733e\u7684\u5c11\u5e74\uff0c\u6b63\u81ea\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u81ea\u5df1\uff0c\u8fd9\u5927\u5385\u4e4b\u4e2d\u671b\u7740\u5979\u7684\u4eba\u867d\u591a\uff0c\u4f46\u8fd9 \u5c11\u5e74\u7684\u76ee\u5149\u4e4b\u4e2d\uff0c\u5374\u4f3c\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u90aa\u6076\u4e4b\u610f\uff0c\u7adf\u770b\u5230\u6a80\u6587\u742a\u4e0d\u7531\u81ea\u4e3b\u7684\u9762\u988a\u4e00\u7ea2\uff0c\u5fc3\u4e2d\u65b9 \u81ea\u6697\u751f\u6012\u610f\uff0c\u54ea\u77e5\u8fd9\u5c11\u5e74\u89c1\u5979\u4e5f\u671b\u4e86\u81ea\u5df1\u4e00\u773c\uff0c\u5f97\u610f\u5730\u5927\u7b11\u4e24\u58f0\uff0c\u4e3e\u8d77\u9152\u676f\uff0c\u7b11\u9053\uff1a\u201c\u6a80\u59d1 \u5a18\u65e2\u7136\u6765\u4e86\uff0c\u4e0d\u5403\u676f\u9152\u5c31\u8d70\uff0c\u4f3c\u4e4e\u6709\u4e9b\u8bf4\u4e0d\u8fc7\u53bb\u5427\uff01\u201d \u3000\u3000\u6a80\u6587\u742a\u4e0d\u77e5\u9053\u6b64\u4eba\u4fbf\u662f\u6c5f\u6e56\u4e2d\u6709\u540d\u7684\u8272\u9b54\u201c\u4e03\u715e\u201d\u83ab\u661f\uff0c\u5fc3\u4e2d\u867d\u6697\u607c\u6b64\u4eba\u65e0\u793c\uff0c\u4f46\u6b64\u65f6 \u6b64\u523b\uff0c\u5979\u7ad9\u5728\u7239\u7239\u8eab\u4fa7\uff0c\u5374\u4e5f\u4e0d\u4fbf\u53d1\u4f5c\u3002 \u3000\u3000\u83ab\u661f\u89c1\u5979\u7c89\u9888\u4f4e\u5782\uff0c\u5a07\u989c\u5982\u82b1\uff0c\u534a\u5e26\u5a07\u7f9e\uff0c\u534a\u5e26\u8f7b\u55d4\uff0c\u90a3\u6a21\u6837\u5f53\u771f\u662f\u7b14\u58a8\u96be\u63cf\uff0c\u5fc3\u4e2d\u4e0d \u89c9\u5947\u75d2\u96be\u6293\uff0c\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u90fd\u4e0d\u662f\u5916\u4eba\uff0c\u59d1\u5a18\u4f55\u5fc5\u5bb3\u81ca\uff0c\u6765\u6765\u6765\u2014\u2014\u201d\u8bdd\u58f0\u4e4b\u4e2d\uff0c \u5c45\u7136\u79bb\u5ea7\u800c\u8d77\uff0c\u8fd9\u201c\u4e03\u715e\u201d\u83ab\u661f\u4eba\u6781\u673a\u8b66\uff0c\u6b66\u529f\u4e5f\u9887\u9ad8\uff0c\u672c\u662f\u9ed1\u9053\u4e2d\u4e00\u628a\u597d\u624b\uff0c\u4f46\u5e73\u751f\u89c1\u4e0d \u5f97\u7f8e\u8c8c\u5973\u5b50\uff0c\u4e00\u89c1\u4e86\u7f8e\u8c8c\u5973\u5b50\uff0c\u4ed6\u7684\u673a\u8b66\u6df1\u6c89\uff0c\u5c31\u5168\u90fd\u8dd1\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u6bd4\u4e4b\u5e02\u4e95\u8f7b\u8584\u65e0\u8d56\uff0c \u8fd8\u8981\u8f7b\u8584\u4e09\u5206\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u5bd2\u5982\u6c34\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5c0f\u5973\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u4e0d\u4f1a\u996e\u9152\uff0c\u83ab\u4e03\u4fa0\u8fd8\u662f\u514d\u4e86\u7684 \u597d\uff0c\u201d\u83ab\u661f\u4e24\u773c\u772f\u6210\u4e00\u7ebf\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u65e0\u59a8\u65e0\u59a8\uff0c\u53ea\u8981\u559d\u4e0a\u4e00\u70b9\uff0c\u610f\u601d\u610f\u601d\u5c31\u597d\u4e86\u3002\u201d\u8bf4 \u7f62\uff0c\u4e00\u53ea\u624b\u4f38\u4e86\u8fc7\u53bb\uff0c\u5c06\u9152\u676f\u9001\u5230\u6a80\u6587\u742a\u9762\u524d\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u8fd9\u4e00\u53ea\u624b\u65b9\u4f38\u51fa\u53bb\uff0c\u624b\u4e2d\u9152\u676f\uff0c\u7a81\u5730\u201c\u5f53\u201d\u5730\u4e00\u58f0\uff0c\u7adf\u88ab\u51fb\u5f97\u7247\u7247\u788e\u843d\uff0c\u676f\u4e2d\u4e4b \u9152\uff0c\u98de\u6e85\u800c\u51fa\uff0c\u6e85\u5f97\u4ed6\u4e00\u5934\u4e00\u8138\uff1a\u83ab\u661f\u9762\u8272\u4e00\u53d8\uff0c\u62e7\u8eab\u9000\u6b65\uff0c\u5927\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e00\u4eba\u51b7\u51b7\u7b54\u9053\uff1a\u201c\u662f\u6211\uff01\u201d \u3000\u3000\u83ab\u661f\u95ea\u7535\u822c\u626d\u8fc7\u5934\uff0c\u5374\u89c1\u7b54\u8bdd\u4e4b\u4eba\uff0c\u7adf\u662f\u88f4\u73cf\uff0c\u4ed6\u6123\u4e86\u4e00\u6123\uff0c\u53d8\u8272\u9053\uff1a\u201c\u6211\u597d\u610f\u656c\u9152\uff0c \u4f60\u2026\u2026\u201d\u4ed6\u5fc3\u4e2d\u867d\u7136\u607c\u6012\uff0c\u4f46\u5f53\u7740\u6b66\u6797\u7fa4\u8c6a\uff0c\u5bf9\u8fd9\u201c\u76df\u4e3b\u5927\u54e5\u201d\uff0c\u4ecd\u4e0d\u514d\u8fd8\u6709\u51e0\u5206\u987e\u5fcc\u3002 \u3000\u3000\u88f4\u73cf\u5929\u6027\u5bbd\u539a\uff0c\u522b\u4eba\u7eb5\u7136\u6b3a\u51cc\u4e8e\u4ed6\uff0c\u4ed6\u4e5f\u5f88\u5c11\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u65b9\u624d\u89c1\u4e86\u83ab\u661f\u5bf9\u6a80\u6587\u742a\u65e0 \u793c\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u70ed\u8840\u4e0a\u6d8c\uff0c\u6293\u8d77\u684c\u4e0a\u94f6\u7b77\uff0c\u5411\u9152\u676f\u63b7\u53bb\uff0c\u4ed6\u539f\u672a\u4e60\u8fc7\u6697\u5668\uff0c\u8fd9\u63b7\u53c8\u662f \u5fc3\u60c5\u6fc0\u52a8\u4e4b\u4e0b\u987a\u624b\u63b7\u51fa\uff0c\u54ea\u77e5\u83ab\u661f\u624b\u4e2d\u9152\u676f\u5374\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5e94\u624b\u800c\u788e\uff0c\u6b64\u523b\u83ab\u57a6\u51b7\u8a00\u76f8 \u8be2\uff0c\u4ed6\u6123\u4e86\u4e00\u6123\uff0c\u6717\u7b11\u9053\uff1a\u201c\u522b\u4eba\u4e0d\u559d\uff0c\u4f60\u52c9\u5f3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u83ab\u661f\u76ee\u5149\u8f6c\u4e86\u4e00\u8f6c\uff0c\u53ea\u89c1\u6a80\u6587\u742a\u7684\u76ee\u5149\uff0c\u4f3c\u4e4e\u53c8\u5728\u779f\u5411\u81ea\u5df1\uff0c\u5e38\u8a00\u9053\u201c\u8272\u80c6\u5305\u65e0\u201d\uff0c\u8fd9 \u83ab\u661f\u8272\u5fc3\u4e00\u8d77\uff0c\u522b\u7684\u4ec0\u4e48\u90fd\u518d\u4e5f\u4e0d\u987e\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u79fb\u52a8\u811a\u6b65\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u5411\u88f4\u73cf\u8d70\u53bb\u3002 \u3000\u3000\u7fa4\u8c6a\u4ff1\u90fd\u4e3a\u4e4b\u8038\u7136\u52a8\u5bb9\uff0c\u6a80\u6587\u742a\u79c0\u7709\u4e00\u8f69\uff0c\u811a\u6b65\u65b9\u52a8\uff0c\u5374\u88ab\u5979\u7239\u7239\u4e00\u624b\u62c9\u4f4f\uff0c\u5979\u4e0d\u6562\u6323 \u624e\uff0c\u5fc3\u4e2d\u5374\u6781\u4e3a\u4e0d\u613f\uff0c\u56de\u7738\u671b\u53bb\uff0c\u5374\u89c1\u4ed6\u7239\u7239\u5634\u5507\u5411\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u5476\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u7528\u4e0d\u7740 \u4f60\u51fa\u624b\uff01\u201d \u3000\u3000\u83ab\u661f\u9762\u5e26\u51b7\u7b11\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u5411\u88f4\u73cf\u3002\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u7684\u76df\u4e3b\uff0c \u4e0d\u5f53\u4e5f\u7f62\u3002\u201d \u3000\u3000\u4ed6\u8a00\u4e0b\u4e4b\u610f\uff0c\u610f\u81ea\u662f\u6697\u9a7e\u83ab\u661f\u600e\u5730\u7adf\u8981\u5411\u76df\u4e3b\u52a8\u7c97\uff0c\u83ab\u6c0f\u5144\u5f1f\u4e4b\u4e2d\uff0c\u539f\u4ee5\u83ab\u661f\u6700\u5f3a\u6700 \u72e0\uff0c\u4ed6\u5144\u5f1f\u867d\u4e5f\u77e5\u9053\uff0c\u4ed6\u6b64\u4e3e\u4e0d\u5f53\uff0c\u4f46\u90fd\u6df1\u77e5\u4ed6\u813e\u6c14\uff0c\u7adf\u65e0\u4e00\u4eba\u51fa\u8a00\u963b\u62e6\uff0c\u54ea\u77e5\u5960\u661f\u8d70\u4e86\u4e24 \u6b65\uff0c\u9762\u524d\u5b9e\u5730\u4eba\u5f71\u4e00\u82b1\uff0c\u53ea\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u5df2\u7ad9\u5728\u8eab\u524d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u83ab\u5144\u4f60\u8fd9\u662f\u8981\u505a\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u83ab\u661f\u51b7\u7b11\u4e00\u58f0\uff0c\u65b9\u5f85\u542f\u53e3\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u77e5\u9053\u4ed6\u6b64\u65f6\u6b64\u523b\uff0c\u53ea\u6015\u8981\u8bf4\u51fa\u96be\u542c\u7684\u8bdd\uff0c\u63a5\u53e3 \u9053\uff1a\u201c\u83ab\u5144\u4f60\u96be\u9053\u5fd8\u4e86\u88f4\u5927\u5148\u751f\u662f\u4f60\u6211\u7684\u4ec0\u4e48\u4eba\u4e48\uff1f\u83ab\u8bf4\u4ed6\u6ca1\u6709\u51fb\u788e\u4f60\u7684\u9152\u676f\uff0c\u5c31\u7b97\u2026\u2026\u201d \u3000\u3000\u83ab\u661f\u53cc\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u6218\u98de\u4ef0\u5929\u4e00\u7b11\uff0c\u5fae\u5fae\u62db\u624b\uff0c\u5385\u95e8\u4e4b\u4fa7\uff0c\u7a81\u5730\u5feb\u6b65\u8d70\u4eba\u4e00\u4e2a\u957f\u886b\u6c49\u5b50\uff0c\u53cc\u624b\u4ea4\u7ed9\u6218\u98de\u4e00 \u7269\uff0c\u4f17\u4eba\u5b9a\u775b\u4e00\u770b\uff0c\u5374\u662f\u4e00\u53ea\u94f6\u7b77\u3002 \u3000\u3000\u6218\u98de\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u53ea\u94f6\u7b77\uff0c\u4fbf\u662f\u88f4\u5927\u5148\u751f\u63b7\u51fa\u4e4b\u7269\uff0c\u4f46\u5374\u7edd\u672a\u51fb\u4e2d\u83ab\u5144\u624b\u4e2d\u9152\u676f\u3002\u201d \u3000\u3000\u88f4\u73cf\u3001\u83ab\u661f\u9f50\u90fd\u4e00\u6123\uff0c\u5374\u89c1\u90a3\u4e1c\u65b9\u9707\u7a81\u5730\u957f\u8eab\u800c\u8d77\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u6218\u5e84\u4e3b\u679c\u7136\u597d\u773c\u529b\uff0c \u4e0d\u9519\uff0c\u83ab\u5144\u638c\u4e2d\u9152\u676f\uff0c\u662f\u6211\u4e1c\u65b9\u9707\u51fb\u788e\u3002\u201d\u5374\u89c1\u6a80\u660e\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u5730\u4e0a\u62fe\u8d77\u4e00\u53ea\u7259\u7b7e\uff0c\u7f13\u7f13 \u653e\u5728\u684c\u4e0a\uff0c\u7fa4\u8c6a\u53c8\u662f\u53d8\u8272\uff0c\u8fd9\u4e1c\u65b9\u9707\u7adf\u80fd\u4ee5\u4e00\u53ea\u7259\u7b7e\u5728\u4f17\u4eba\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u51fb\u788e\u522b\u4eba\u9152\u676f\uff0c\u8fd9\u52b2 \u529b\u4e0e\u624b\u6cd5\uff0c\u5f53\u771f\u53ef\u4ee5\u60ca\u4e16\u9a87\u4fd7\u3002 \u3000\u3000\u83ab\u661f\u51b7\u7b11\u4e00\u58f0\uff0c\u7a81\u5730\u8f6c\u8eab\u9762\u5411\u4e1c\u65b9\u5144\u5f1f\uff0c\u5927\u5385\u4e2d\u6c89\u95f7\u4e4b\u6c14\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4fbf\u5df2\u53d8\u5f97\u5251\u62d4\u5f29 \u5f20\uff0c\u4e00\u89e6\u5373\u53d1\uff0c\u54ea\u77e5\u201c\u9f99\u5f62\u516b\u638c\u201d\u7a81\u5730\u53c8\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4e1c\u65b9\u5144\u6682\u8bf7\u5750\u4e0b\uff0c\u8fd9\u676f\u9152\u5374\u4e5f\u4e0d\u662f\u4f60 \u51fb\u788e\u7684\u54e9\uff1f\uff0c\u4f17\u4eba\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u6123\uff0c\u201d\u795e\u624b\u201c\u6218\u98de\u7a81\u5730\u4ef0\u5929\u5927\u7b11\u8d77\u6765\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u679c\u7136\u8fd8 \u662f\u6a80\u5927\u4fa0\u7684\u597d\u773c\u529b\uff01\u201d\u7a81\u5730\u4f38\u624b\u6293\u8d77\u4e00\u53ea\u9152\u676f\uff0c\u987a\u624b\u63b7\u5728\u5730\u4e0a\uff0c\u9152\u676f\u89e6\u5730\uff0c\u201c\u5f53\u201d\u5730\u4e00\u54cd\uff0c \u5374\u4ecd\u7136\u4e1d\u6beb\u672a\u788e\u3002 \u3000\u3000\u6218\u98de\u5927\u7b11\u9053\uff1a\u201c\u82e5\u4ee5\u4e1c\u65b9\u4e09\u4fa0\u7684\u624b\u529b\uff0c\u51ed\u90a3\u5c0f\u5c0f\u4e00\u53ea\u7259\u7b7e\uff0c\u51fb\u788e\u9152\u676f\uff0c\u672c\u975e\u96be\u4e8b\uff0c\u4f46\u533a \u533a\u8fd9\u4e9b\u9152\u676f\uff0c\u5374\u662f\u540d\u7a91\u7cbe\u5236\uff0c\u575a\u56fa\u5f02\u5e38\uff0c\u4e1c\u65b9\u4e09\u4fa0\u5982\u82e5\u4e0d\u4fe1\uff0c\u4e0d\u59a8\u518d\u8bd5\u4e0a\u4e00\u8bd5\u3002\u201d\u987a\u624b\u53c8\u62ff \u8d77\u4e00\u53ea\u9152\u676f\uff0c\u4e1c\u65b9\u9707\u53cc\u7709\u4e00\u626c\uff0c\u5374\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u7a81\u5730\u4f38\u51fa\u624b\u4e2d\u94f6\u7b77\uff0c\u5728\u684c\u4e0a\u4e00\u76d8\u6e05\u84b8\u56e2\u7fc5 \u4e2d\u62e8\u4e86\u4e24\u62e8\uff0c\u94f6\u7b77\u4e00\u7ffb\uff0c\u53d6\u51fa\u4e00\u7269\uff0c\u5f53\u5730\u4e00\u58f0\uff0c\u629b\u5728\u684c\u4e0a\uff0c\u7fa4\u8c6a\u9f50\u5730\u4e00\u6123\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u5374\u89c1 \u4ed6\u624b\u4e2d\u94f6\u7b77\uff0c\u7adf\u5df2\u53d8\u5f97\u4e4c\u9ed1\uff0c\u4e0d\u7981\u66f4\u4e3a\u4e4b\u7fa4\u76f8\u53d8\u8272\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u8f7b\u8f7b\u5c06\u94f6\u7b77\u653e\u5728\u684c\u4e0a\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u83ab\u5144\u624b\u4e2d\u9152\u676f\uff0c\u65e2\u975e\u4e1c\u65b9\u4e16\u5144\u51fb\u788e\uff0c \u66f4\u975e\u73cf\u513f\u6240\u4e3a\uff0c\u83ab\u5144\u5fc3\u4e2d\u5982\u4e0d\u5fff\uff0c\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\uff0c\u81fc\u7ba1\u5bfb\u51fa\u90a3\u4eba\u4fbf\u662f\uff0c\u5374\u53c8\u4f55\u6614\u62ff\u522b\u4eba\u51fa \u6c14\u3002\u201d\u888d\u8896\u4e00\uff3d\u62c2\uff0c\u7f13\u7f13\u5750\u4e0b\uff0c\u6ee1\u9762\u4ff1\u662f\u4e0d\u5c51\u4e4b\u610f\u3002 \u3000\u3000\u201c\u5317\u6597\u4e03\u715e\u201d\u672c\u662f\u6697\u5668\u540d\u5bb6\uff0c\u6b64\u523b\u83ab\u661f\u624b\u4e2d\u9152\u676f\u88ab\u4eba\u51fb\u788e\uff0c\u83ab\u6c0f\u5144\u5f1f\uff0c\u7adf\u6ca1\u6709\u4e00\u4eba\u770b\u51fa \u6697\u5668\u662f\u6765\u81ea\u4f55\u65b9\uff0c\u8fd9\u81ea\u662f\u6781\u4e3a\u4e22\u4eba\u4e4b\u4e8b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u201c\u4e03\u715e\u201d\u83ab\u661f\u9762\u5bb9\u7531\u767d\u8f6c\u7ea2\uff0c\u7531\u7ea2\u8f6c \u767d\uff0c\u607c\u7f9e\u6210\u6012\u4e4b\u4e0b\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5317\u6597\u4e03\u715e\u201d\u4e4b\u9996\u83ab\u5357\uff0c\u65b9\u624d\u867d\u56e0\u56db\u9762\u4ff1\u662f\u6709\u5173\u4e4b\u4eba\uff0c\u662f\u4ee5\u4e0d\u4fbf\u8bf4\u8bdd\uff0c\u4f46\u6b64\u523b\u89c1\u6839\u672c\u4e0e \u88f4\u73cf\u65e0\u5173\uff0c\u957f\u8eab\u7ad9\u8d77\uff0c\u63a5\u53e3\u559d\u9053\uff1a\u201c\u670b\u53cb\u65e2\u7136\u6709\u5fc3\u4e0e\u6211\u5144\u5f1f\u4e3a\u96be\uff0c\u4f3c\u8fd9\u822c\u85cf\u5934\u9732\u5c3e\uff0c\u9b3c\u9b3c\u795f \u795f\uff0c\u5374\u7b97\u4e0d\u5f97\u6c49\u5b50\u3002\u201d\u4ed6\u5144\u5f1f\u4e24\u4eba\u867d\u7136\u8fde\u58f0\u6012\u559d\uff0c\u4f46\u6839\u672c\u4e0d\u77e5\u9053\u6697\u5668\u6765\u81ea\u4f55\u65b9\uff0c\u81ea\u4e5f\u4e0d\u77e5\u9053 \u654c\u4eba\u8eb2\u5728\u54ea\u91cc\uff0c\u662f\u4ee5\u76ee\u5149\u56db\u8f6c\uff0c\u5230\u5904\u641c\u7d22\uff0c\u4f46\u6b64\u523b\u5927\u5385\u4e4b\u4e0a\u4eba\u5934\u62e5\u6324\uff0c\u4ed6\u4ec0\u4e48\u4e5f\u65e0\u6cd5\u770b\u5230\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u6df1\u6c89\u8d1f\u624b\u800c\u7acb\uff0c\u76ee\u5149\u5374\u9634\u68ee\u68ee\u671b\u5411\u5385\u53f3\u9762\u7a97\u5b50\uff0c\u65b9\u624d\u90a3\u53d1\u73b0\u82b1\u7389\u5c38\u8eab \u7684\u6c49\u5b50\uff0c\u9886\u8fc7\u8d4f\u8d50\uff0c\u867d\u5df2\u56de\u5230\u5385\u4e2d\uff0c\u6b64\u523b\u76ee\u5149\u4e00\u8f6c\uff0c\u6084\u6084\u8d70\u4e86\u51fa\u53bb\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u5634\u89d2\u5fae\u9732 \u7b11\u5bb9\uff0c\u4f3c\u4e4e\u9887\u4ee5\u81ea\u5df1\u6709\u5982\u6b64\u76ee\u5149\u654f\u9510\u7684\u624b\u4e0b\u4e3a\u50b2\uff0c\u53ea\u89c1\u90a3\u6c49\u5b50\u65b9\u81ea\u8d70\u5230\u5385\u95e8\uff0c\u5385\u53f3\u7a97\u5916\uff0c\u7a81 \u5730\u4f20\u6765\u9634\u68ee\u68ee\u4e00\u58f0\u51b7\u7b11\uff0c\u7b11\u58f0\u867d\u7136\u8f6c\u5fae\uff0c\u4f46\u4eba\u8033\u5374\u6781\u6e05\u6670\u3002\u7fa4\u8c6a\u4e00\u8d77\u61d4\u7136\u8272\u53d8\uff0c\u4eff\u4f5b\u90a3\u53d1\u7b11 \u4e4b\u4eba\uff0c\u5c31\u5728\u81ea\u5df1\u8033\u7554\u4e00\u822c\u3002 \u3000\u3000\u7b11\u58f0\u672a\u4e86\u2014\u2014 \u3000\u3000\u5385\u53f3\u7a97\u6237\uff0c\u7a81\u5730\u65e0\u98ce\u81ea\u5f00\uff0c\u7f13\u7f13\u5f00\u4e86\u4e00\u7ebf\u3002 \u3000\u3000\u83ab\u661f\u9762\u5bb9\u60e8\u767d\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u624b\u8155\u6025\u626c\uff0c\u201c\u4e03\u715e\u201d\u5bd2\u661f\uff0c\u7535\u5c04\u800c\u51fa\u3002 \u3000\u3000\u201c\u5317\u6597\u4e03\u715e\u201d\u4ed7\u4ee5\u6210\u540d\u7684\u201c\u5317\u6597\u4e03\u661f\u9488\u201d\uff0c\u94a2\u7b52\u673a\u7c27\uff0c\u5747\u7ecf\u5de7\u624b\u6240\u5236\uff0c\u4e00\u53d1\u4e03\u9488\uff0c\u4e00\u7b52 \u53ef\u8fde\u53d1\u4e09\u6b21\uff0c\u5171\u662f\u4e09\u4e03\u4e8c\u5341\u4e00\u9488\uff0c\u83ab\u5bb6\u5144\u5f1f\u5747\u662f\u53cc\u624b\u88c5\u7b52\uff0c\u5de6\u53f3\u8fde\u53d1\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5c04\u7a0b\u53c8\u7279 \u8fdc\uff0c\u51e0\u53ef\u8fbe\u4e94\u4e08\u5f00\u5916\uff0c\u5f53\u4eca\u6b66\u6797\u6697\u5668\u4e4b\u4e2d\uff0c\u82e5\u8bba\u5a01\u529b\u4e4b\u9738\u9053\uff0c\u8fd9\u201c\u5317\u6597\u4e03\u661f\u9488\u201d\u867d\u975e\u9996\u4f4d\uff0c \u4f46\u4e5f\u8ddd\u4e4b\u4e0d\u8fdc\u3002 \u3000\u3000\u6b64\u523b\u4f46\u89c1\u8fd9\u4e03\u70b9\u5bd2\u661f\uff0c\u7535\u5c04\u800c\u51fa\uff0c\u4f46\u8fd9\u5927\u5385\u65b9\u5706\u6781\u5927\uff0c\u8fd9\u4e03\u70b9\u5bd2\u661f\u5230\u4e86\u5385\u53f3\u7a97\u524d\uff0c\u5374\u5df2 \u53d8\u6210\u5f3a\u5f29\u4e4b\u672a\uff0c\u52bf\u9053\u6e10\u7f13\u6e10\u8870\uff0c\u7a97\u5916\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u4e00\u58f0\u98ce\u58f0\u7a7f\u7a97\u800c\u51fa\uff0c\u8fd9\u4e03\u70b9\u5bd2\u661f\u7adf\u6089\u6570 \u88ab\u5288\u843d\u5730\u4e0a\u3002\u53ea\u5bb3\u5f97\u7a97\u524d\u6240\u5750\u7684\u4e00\u5e2d\u4eba\u58eb\uff0c\u4e00\u4e2a\u4e2a\u60f6\u7136\u8d70\u907f\uff0c\u552f\u6050\u6697\u5668\u843d\u5728\u81ea\u5df1\u8eab\u4e0a\u3002 \u3000\u3000\u98ce\u58f0\u65b9\u606f\uff0c\u51b7\u7b11\u672a\u7edd\uff0c\u4e24\u6761\u6de1\u7070\u4eba\u5f71\uff0c\u4fbf\u5df2\u7a7f\u7a97\u800c\u51fa\uff0c\u4f46\u542c\u8863\u8882\u5e26\u98ce\u4e4b\u58f0\uff0c\u547c\u5730\u4e00\u54cd\uff0c \u8fd9\u4e24\u6761\u4eba\u5f71\u8eab\u5f62\u4e00\u63a0\u4e09\u4e08\uff0c\u773c\u770b\u52bf\u9053\u5c06\u8870\uff0c\u4e24\u4eba\u7a81\u5730\u5404\u5404\u4f38\u51fa\u4e00\u638c\uff0c\u4e24\u638c\u76f8\u4ea4\uff0c\u62cd\u5730\u4e00\u58f0\uff0c \u4e24\u4eba\u8eab\u5f62\u4e00\u4eba\u5fae\u5fae\u504f\u5de6\uff0c\u4e00\u4eba\u5fae\u5fae\u504f\u53f3\uff0c\u7adf\u53c8\u501f\u52bf\u659c\u63a0\u4e24\u4e08\uff0c\u98d8\u98d8\u843d\u5728\u5f53\u4e2d\u4e00\u5e2d\u5de6\u53f3\u4e24\u8fb9\u7a7a \u9699\u5730\u4e0a\uff0c\u5f53\u771f\u662f\u70b9\u6cd5\u4e0d\u8c05\uff0c\u5bf8\u4e0a\u4e0d\u626c\uff0c\u7fa4\u8c6a\u76f8\u987e\u4e00\u773c\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u60ca\uff1a\u4e00\u8fd9\u4e24\u4eba\u662f\u8c01\uff1f\u8f7b\u529f \u7adf\u7136\u5982\u6b64\u60ca\u4eba\uff01\u201c\u83ab\u6c0f\u5144\u5f1f\u6697\u5668\u51fa\u624b\uff0c\u4eba\u5f71\u5df2\u81ea\u98de\u5165\uff0c\u4ed6\u5144\u5f1f\u4eba\u867d\u72c2\u50b2\uff0c\u5374\u4e5f\u4e0d\u7981\u4e3a\u8fd9\u4e24\u4eba \u8eab\u6cd5\u6240\u60ca\uff0c\u5b9a\u6674\u671b\u53bb\uff0c\u53ea\u89c1\u684c\u53f3\u4e00\u4eba\u8eab\u5f62\u7279\u9ad8\uff0c\u9aa8\u7626\u5982\u67f4\uff0c\u4e4c\u7c2a\u9ad8\u9afb\uff0c\u9762\u5bb9\u50f5\u6728\uff0c\u8eab\u7a7f\u4e00\u4ef6 \u9f50\u819d\u7070\u888d\uff0c\u5374\u662f\u53c8\u5bbd\u53c8\u5927\uff0c\u76ee\u5149\u8f6c\u52a8\u4e4b\u95f4\uff0c\u5b9b\u5982\u5389\u7535\u4e00\u822c\u3002\u83ab\u6c0f\u5144\u5f1f\u4e00\u60ca\uff0c\u8f6c\u76ee\u518d\u671b\uff0c\u5374\u89c1 \u684c\u5de6\u4e00\u4eba\uff0c\u7adf\u4ea6\u662f\u67af\u7626\u5982\u67f4\uff0c\u4e4c\u7c2a\u9ad8\u5bb3\uff0c\u9762\u76ee\u751f\u51b7\uff0c\u76ee\u5149\u5982\u7535\uff0c\u7adf\u548c\u684c\u53f3\u4e00\u4eba\u4e00\u6a21\u4e00\u6837\u3002\u8fd9 \u4e24\u4eba\u7a7f\u7a97\u201d\u63a0\u4eba\u201c\u843d\u5730\uff0c\u4e0d\u8fc7\u4ec5\u5728\u5239\u90a3\u4e4b\u95f4\u2014\u2014\u201d\u795e\u624b\u201c\u6218\u98de\u6d53\u7709\u4e00\u626c\uff0c\u8131\u53e3\u9053\uff1a\u201c\u539f\u6765\u662f \u51b7\u6c0f\u53cc\u4fa0\u5230\u4e86\uff01\u201d \u3000\u3000\u6a80\u6587\u742a\u5374\u5a07\u547c\u4e00\u58f0\uff0c\u7ea4\u8170\u5fae\u626d\uff0c\u63a0\u5230\u684c\u53f3\u7684\u51b7\u67af\u6728\u8eab\u65c1\u3002 \u3000\u3000\u83ab\u6c0f\u5144\u5f1f\u56db\u4eba\u5fc3\u5934\u9f50\u90fd\u4e00\u51db\uff0c\u9f50\u5730\u957f\u8eab\u800c\u8d77\uff0c\u53ea\u89c1\u8fd9\u201c\u51b7\u8c37\u53cc\u6728\u201d\u50f5\u6728\u51b0\u51b7\u7684\u9762\u8272\uff0c\u89c1 \u5230\u6a80\u6587\u742a\u65f6\uff0c\u7ade\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u6a80\u6587\u742a\u5a07\u58f0\u9053\uff1a\u201c\u51b7\u5927\u53d4\uff0c\u8fd9\u4e24\u5929\uff0c\u4f60\u4eec\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u67af\u6728\u3001\u5bd2\u7af9\u7b11\u5bb9\u4e00\u655b\uff0c\u4ed6\u4eec\u4e24\u4eba\u7b11\u5bb9\u6765\u5f97\u867d\u5feb\uff0c\u53bb\u5f97\u5374\u66f4\u5feb\uff0c\u6b64\u523b\u4e24\u4eba\u9762\u4e0a\uff0c\u53c8\u5b9b\u5982\u7f69 \u4e0a\u4e00\u5c42\u5bd2\u971c\uff0c\u51b0\u51b7\u7684\u76ee\u5149\uff0c\u5411\u83ab\u6c0f\u5144\u5f1f\u4e00\u626b\uff0c\u6b64\u523b\u867d\u662f\u5348\u95f4\uff0c\u6237\u5916\u6625\u9633\u6b63\u70c8\uff0c\u4f46\u83ab\u6c0f\u5144\u5f1f\u88ab \u8fd9\u76ee\u5149\u4e00\u626b\uff0c\u7ade\u5b9b\u5982\u5bd2\u98ce\u62c2\u96e8\uff0c\u51b7\u51b0\u6dcb\u8eab\uff0c\u5fcd\u4e0d\u4f4f\u6253\u4e86\u4e2a\u5bd2\u5509\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5f3a\u7b11\u4e00\u58f0\uff1a\u201c\u51b7\u6c0f\u53cc\u4fa0\uff0c\u4fa0\u8e2a\u96be\u5230\u6c5f\u5357\uff0c\u4eca\u65e5\u4e0d\u77e5\u662f\u90a3\u9635\u51e4\u5c06\u9601\u4e0b\u4e24\u4f4d\u5439 \u5230\u6b64\u95f4\uff0c\u597d\u6559\u6218\u98de\u9ad8\u5174\uff01\u201d\u4ed6\u4e0e\u201c\u5317\u6597\u4e03\u715e\u201d\u867d\u662f\u670b\u53cb\uff0c\u4f46\u5374\u66f4\u4e0d\u613f\u6811\u4e0b\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8fd9\u822c \u5f3a\u654c\uff0c\u6b64\u523b\u62a2\u5148\u53d1\u6d3b\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e0d\u8fc7\u662f\u65e0\u8bba\u4f60\u5144\u5f1f\u6765\u610f\u5982\u4f55\uff0c\u90fd\u4e0e\u6211\u6218\u98de\u65e0\u5173\uff0c\u6211\u6218\u98de\u5374 \u662f\u6b22\u8fce\u5f97\u5f88\u3002 \u3000\u3000\u51b7\u6c0f\u5144\u5f1f\u53cc\u540c\u4e00\u7ffb\uff0c\u67af\u6728\u51b7\u51b7\u9053\uff1a\u201c\u4e03\u661f\u6bd2\u9488\u89c1\u8840\u5c01\u5589\uff0c\u96be\u9053\u8fd9\u5c31\u662f\u6d6a\u83bd\u5c71\u5e84\u7684\u5f85\u5ba2\u4e4b \u9053\u4e48\uff1f\u201d\u76ee\u5149\u500f\u7136\u4e00\u8f6c\uff0c\u95ea\u7535\u822c\u5c04\u5230\u83ab\u661f\u8eab\u4e0a\u3002 \u3000\u3000\u4e03\u715e\u83ab\u661f\u51b7\u51b7\u4e00\u7b11\uff0c\u9ad8\u4e3e\u8d77\u9762\u524d\u94f6\u7b77\uff0c\u631f\u8d77\u684c\u4e0a\u90a3\u65b9\u624d\u88ab\u6a80\u660e\u653e\u4e0b\u7684\u4e00\u7c92\u5fae\u5e26\u8292\u523a\u7684\u4e4c \u9ed1\u94c1\u73e0\uff0c\u51b7\u51b7\u63a5\u9053\u3002 \u3000\u3000\u201c\u5317\u6597\u4e03\u715e\u4e0e\u51b7\u8c37\u53cc\u6728\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u8fd9\u53ef\u7b97\u662f\u4ec0\u4e48\uff1f\u533a\u533a\u5728\u4e0b\u5012\u8981\u95ee\u4e24\u4f4d\u8981\u70b9\u516c \u9053\uff01\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u76ee\u5149\u6709\u5982\u5bd2\u7bad\u51b0\u9489\uff0c\u7262\u7262\u76ef\u5728\u83ab\u661f\u9762\u4e0a\uff0c\u7f13\u7f13\u5730\u9053\uff1a\u201c\u8981\u70b9\u516c\u9053\u2014\u2014\u54fc\u54fc\uff01\u201d\u53cc \u76ee\u4e00\u7ffb\uff0c\u500f\u7136\u4f4f\u53e3\uff0c\u201c\u4e03\u715e\u201d\u83ab\u661f\u6c14\u5f80\u4e0a\u649e\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u4e45\u95fb\u2018\u51b7\u8c37\u53cc\u6728\u2019\u4e4b\u80fd\uff0c\u53ef\u4e0e \u4f60\u8fdc\u65e0\u51a4\u8fd1\u65e0\u4ec7\uff0c\u662f\u4ee5\u624d\u8ba9\u4f60\u534a\u5206\uff0c\u4f60\u5982\u4eca\u8fd9\u822c\u8138\u8272\uff0c\u96be\u9053\u6211\u2018\u5317\u6597\u4e03\u715e\u2019\u5c31\u6015\u4e86\u4f60\u2018\u51b7\u8c37 \u53cc\u6728\u2019\u4e48\uff1f\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u9762\u4e0a\u7a81\u5730\u5fae\u5fae\u4e00\u7b11\uff0c\u5ff5\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4e24\u4f4d\u5f97\u9ad8\u671b\u91cd\uff0c\u672c\u662f\u5728\u4e0b\u7b49\u7684 \u524d\u8f88\uff0c\u5728\u4e0b\u65e2\u7136\u6ca1\u6709\u5403\u4e8f\u2014\u2014\u201d\u4ed6\u542b\u7b11\u800c\u8a00\uff0c\u8bf4\u58f0\u6781\u4e3a\u548c\u7f13\uff0c\u7fa4\u8c6a\u4ff1\u90fd\u5927\u5947\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u201d \u4e03\u715e\u201c\u83ab\u661f\u539f\u6765\u4e5f\u662f\u4e2a\u5916\u5f3a\u4e2d\u5e72\uff0c\u6b3a\u8f6f\u6015\u786c\u7684\u89d2\u8272\uff0c\u65b9\u624d\u795e\u6c14\u51f6\u5982\u731b\u864e\uff0c\u5982\u4eca\u89c1\u4e86\u51b7\u6c0f\u5144 \u5f1f\uff0c\u5c45\u7136\u53d8\u5f97\u8f6f\u5982\u7ef5\u7f8a\u4e86\u3002\uff0c\u53ea\u542c\u83ab\u5357\u63a5\u7740\u9053\uff1a\u201c\u5728\u4e0b\u5144\u5f1f\u5176\u5b9e\u2014\u2014\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u5730\u53cc\u624b \u4e00\u626c\uff0c\u5341\u6570\u70b9\u5bd2\u661f\uff0c\u95ea\u7535\u822c\u5de6\u53f3\u5c04\u51fa\uff0c\u4e03\u70b9\u51fb\u5411\u67af\u6728\uff0c\u4e03\u70b9\u51fb\u5411\u5bd2\u7af9\u3002 \u3000\u3000\u4ed6\u8fd9\u201c\u6597\u4e03\u661f\u9488\u201d\u5a01\u529b\u672c\u6781\u9738\u9053\uff0c\u6b64\u523b\u8ddd\u79bb\u53c8\u8fd1\uff0c\u7fa4\u8c6a\u9f50\u5730\u60ca\u547c\u4e00\u58f0\uff0c\u53ea\u9053\u51b7\u6c0f\u5144\u5f1f\u7eb5 \u7136\u6b66\u529f\u9ad8\u5f3a\uff0c\u4f46\u5728\u731d\u4e0d\u53ca\u9632\u4e4b\u4e0b\uff0c\u54ea\u8fd8\u8eb2\u5f97\u5f00\uff0c\u54ea\u77e5\u773c\u770b\u8fd9\u5341\u6570\u70b9\u5bd2\u661f\uff0c\u5df2\u5c06\u51fb\u5230\u51b7\u6c0f\u5144\u5f1f \u8eab\u4e0a\uff0c\u51b7\u6c0f\u5144\u5f1f\u8eab\u5f62\u7adf\u8fd8\u65e0\u4e1d\u6beb\u95ea\u907f\u4e4b\u610f\uff0c\u7ad9\u5728\u67af\u6728\u65c1\u8fb9\u7684\u6a80\u6587\u742a\u6b64\u523b\u4ea6\u4e0d\u7981\u5a07\u547c\u4e00\u58f0\uff0c\u5927 \u60ca\u5931\u8272\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u67af\u6728\u5bd2\u7af9\u8eab\u4e0a\u90a3\u4ef6\u5bbd\u5927\u7684\u7070\u888d\uff0c\u7adf\u7a81\u5730\u5f80\u5916\u4e00\u6da8\uff0c\u5c31\u4f3c\u91cc\u9762\u7a81\u7136 \u88ab\u4eba\u5439\u4e86\u6c14\u4e00\u822c\uff0c\u53c8\u4f3c\u4e00\u5f20\u7a81\u7136\u5f20\u8d77\u7684\u5e10\u7bf7\uff0c\u53ea\u89c1\u5657\u5657\u51e0\u58f0\uff0c\u5341\u6570\u70b9\u94f6\u661f\uff0c\u867d\u90fd\u7740\u7740\u5b9e\u5b9e\u5730 \u6253\u5728\u4ed6\u4eec\u8eab\u4e0a\uff0c\u4f46\u5374\u534a\u70b9\u4e5f\u6cbe\u4e0d\u7740\u4ed6\u4eec\u7684\u76ae\u8089\uff0c\u88f4\u73cf\u5fc3\u4e2d\u6697\u9a87\uff0c\u77e5\u9053\u8fd9\u53c8\u662f\u4ed6\u4eec\u201c\u4e24\u6781\u7384 \u529f\u201d\u7684\u52b2\u6c14\u4e4b\u529f\u3002\u5385\u4e2d\u7fa4\u8c6a\u867d\u90fd\u5f97\u77e5\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6b66\u529f\u8d85\u4eba\uff0c\u5374\u518d\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4eec\u7684\u5185\u5bb6\u771f\u6c14 \u7adf\u5df2\u7ec3\u5230\u5982\u6b64\u5730\u6b65\u3002\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u52a8\u5904\uff0c\u9762\u4e0a\u4ea6\u4e0d\u7981\u5fae\u5fae\u5931\u8272\uff0c\u83ab\u6c0f\u5144\u5f1f\u66f4\u662f\u9762\u5982\u9752 \u94c1\uff0c\u53ea\u89c1\u67af\u6728\u5bd2\u7af9\u52b2\u6c14\u4e00\u6536\uff0c\u7070\u888d\u6536\u7f29\uff0c\u53ee\u53ee\u4e00\u9635\u58f0\u54cd\uff0c\u5341\u56db\u53e3\u94a2\u9488\uff0c\u5168\u90e8\u843d\u5230\u5730\u4e0a\u3002 \u3000\u3000\u83ab\u6c0f\u5144\u5f1f\u5927\u60ca\u4e4b\u4e0b\uff0c\u5bf9\u671b\u4e00\u773c\uff0c\u8eab\u5f62\u79fb\u52a8\uff0c\u5144\u5f1f\u56db\u4eba\u5e76\u80a9\u7ad9\u5230\u4e00\u5904\uff0c\u51dd\u795e\u5f85\u654c\uff0c\u7fa4\u8c6a\u5fc3 \u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4e00\u4e0b\u4e0d\u51fa\u5239\u90a3\u4e4b\u95f4\uff0c\u5b9a\u6709\u4e00\u756a\u6076\u6597\u3002\u201d\u8ddd\u79bb\u5728\u4ed6\u4eec\u8fd1\u4e9b\u7684\uff0c\u6b64\u523b\u65e9\u5df2\u6084\u6084\u7ad9\u4e86 \u8d77\u6765\uff0c\u751f\u6015\u57ce\u95e8\u4e4b\u708e\uff0c\u6b81\u53ca\u6c60\u9c7c\uff0c\u5404\u90fd\u8fdc\u8fdc\u8d70\u5230\u4e00\u8fb9\u3002 \u3000\u3000\u201c\u54ea\u77e5\u67af\u6728\u5bd2\u7af9\u888d\u8896\u5fae\u62c2\uff0c\u7adf\u8fde\u671b\u90fd\u4e0d\u518d\u53bb\u671b\u83ab\u6c0f\u5144\u5f1f\u4e00\u773c\uff0c\u8fd9\u6b21\u9177\u5947\u8be1\u7684\u5144\u5f1f\u4e24\u4eba\uff0c \u6b64\u523b\u7adf\u4e00\u8d77\u8d70\u5230\u88f4\u73cf\u8eab\u524d\u51b7\u51b7\u9053\uff1a\u201c\u6211\u5144\u5f1f\u6765\u6b64\uff0c\u4e3a\u7684\u4ec0\u4e48\uff0c\u4f60\u53ef\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4e00\u6123\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f46\u8bf7\u4e24\u4f4d\u8001\u524d\u8f88\u76f8\u544a\uff01\u201d \u3000\u3000\u51b7\u67af\u6728\u51b7\u7b11\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u5144\u5f1f\u6765\u6b64\uff0c\u5c31\u662f\u4e3a\u4e86\u8ba8\u6559\u8ba8\u6559\u9601\u4e0b\u7684\u6b66\u529f\uff0c\u4f60\u96be\u9053\u4e0d\u77e5 \u9053\u4e48\uff1f\u201d \u3000\u3000\u5728\u5ea7\u8bf8\u4eba\uff0c\u95fb\u8a00\u9f50\u90fd\u5927\u6123\uff0c\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u5fc3\u4e2d\u90fd\u89c9\u8fd9\u5144\u5f1f\u4e24\u4eba\u5f53\u771f\u6709\u4e9b\u6bdb\u75c5\uff0c\u83ab\u6c0f\u5144 \u5f1f\u6697\u5bb3\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5374\u53bb\u627e\u88f4\u73cf\u7684\u9ebb\u70e6\uff0c\u8fd9\u5c82\u975e\u5929\u5927\u5947\u4e8b\u3002\u5960\u6c0f\u5144\u5f1f\u4ea6\u662f\u5927\u60d1\u4e0d\u89e3\uff0c\u6728\u7acb\u5730 \u4e0a\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u53ea\u89c1\u6a80\u6587\u742a\u6123\u4e86\u4e00\u695e\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u5a07\u547c\u9053\uff1a\u201c\u5927\u53d4\uff0c\u4e8c\u53d4\uff0c\u4f60\u8001\u4eba\u5bb6\u8fd9\u662f\u5e72 \u4ec0\u4e48\uff1f\u4eba\u5bb6\u548c\u4f60\u65e0\u51a4\u65e0\u4ec7\u2014\u2014\uff0c\u51b7\u67af\u6728\u500f\u7136\u8f6c\u8fc7\u5934\u6765\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u600e\u5730\u77e5\u9053\u4ed6\u4e0e\u6211\u65e0\u51a4\u65e0 \u4ec7\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u53c8\u81ea\u4e00\u5446\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u7a81\u5730\u5782\u9996\u9053\uff1a\u201c\u96be\u9053\u4f60\u8001\u4eba\u5bb6\u8fd8\u5c06\u90a3\u5929\u665a\u4e0a\u7684\u4e8b\u653e\u5728\u5fc3\u4e0a \u4e48\uff0c\u5176\u5b9e\u6211\u53c8\u4e0d\u662f\u771f\u7684\u602a\u4ed6\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b64\u4e8b\u4e0e\u4f60\u65e0\u5173\uff0c\u4f60\u5feb\u7ad9\u8fdc\u4e9b\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u9053\uff1a\u201c\u4ed6\u5e08\u7236\u4e0e\u6211\u6709\u4ec7\uff0c\u6211\u627e\u5e08\u7236\u4e0d\u5230\uff0c\u5148\u627e\u5f92\u5f1f\u4e5f\u662f\u4e00\u6837\uff0c\u54fc\u54fc\u4e00\u6253\u4e86\u5f92\u5f1f\uff0c \u8fd8\u6015\u5e08\u7236\u4e0d\u51fa\u6765\u4e48\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u6025\u9053\uff1a\u201c\u4ed6\u54ea\u91cc\u6709\u4ec0\u4e48\u5e08\u7236\uff0c\u4ed6\u5e08\u7236\u600e\u4f1a\u5f97\u7f6a\u4e86\u4f60\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u76ee\u5149\u4e00\u51db\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u518d\u7b11\u9053\uff1a\u201c\u4ed6\u82e5\u6ca1\u6709\u5e08\u7236\u8c01\u6709\u5e08\u7236\uff0c\u4ed6\u5e08\u7236\u82e5\u6ca1\u6709\u5f97\u7f6a\u6211\u8c01\u5f97\u7f6a\u4e86\u6211\u2014\u2014\u54fc\uff0c\u59d3 \u88f4\u7684\uff0c\u4f60\u6709\u6ca1\u6709\u5e08\u7236\uff0c\u4f60\u5e08\u7236\u662f\u5426\u5f97\u7f6a\u4e86\u6211\uff1f\u4f60\u4e14\u8bf4\u7ed9\u8fd9\u7b28\u4e2b\u5934\u542c\u542c\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u60c5\u6025\u5173\u5fc3\uff0c\u82b1\u5bb9\u5931\u8272\uff0c\u76ee\u5149\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u88f4\u73cf\uff0c\u53ea\u671b\u4ed6\u6447\u5934\u5426\u8ba4\uff0c\u54ea\u77e5\u88f4\u73cf\u957f \u53f9\u4e00\u58f0\uff0c\u7adf\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0f\u53ef\u662f\u6709\u5e08\u7236\uff0c\u5bb6\u5e08\u7684\u786e\u662f\u5f97\u7f6a\u4e86\u4e24\u4f4d\u524d\u8f88\uff0c\u4f46\u662f\u2026\u2026\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u91cd\u91cd\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4e0e\u6211\u6709\u4ec7\uff0c\u6211\u627e\u5f92\u5f1f\u7b97\u5e10\uff0c\u8bf7\u95ee\u5404\u4f4d\uff0c\u8fd9\u9053\u7406\u96be\u9053\u8bf4\u4e0d\u901a\u4e48\uff1f\u201d\u8981 \u77e5\u9053\u5343\u767e\u5e74\u6765\u6b66\u6797\u4e4b\u4e2d\uff0c\u5bfb\u4ec7\u4e4b\u98ce\uff0c\u59cb\u7ec8\u6700\u70c8\uff0c\u83ab\u8bf4\u4e0e\u5e08\u7236\u6709\u4ec7\u7684\u53ef\u627e\u5f92\u5f1f\uff0c\u4fbf\u662f\u518d\u8fdc\u4e9b\u7684 \u5173\u7cfb\uff0c\u4e5f\u7167\u6837\u4f1a\u7275\u8fde\u7684\u4e0a\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u6a80\u6587\u742a\u771f\u6025\u5f97\u5446\u7acb\u5f53\u5730\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\uff0c\u5979\u77e5\u9053\u88f4\u73cf\u7684\u6b66\u529f\uff0c\u4e07\u4e07\u4e0d\u662f\u8fd9 \u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u653e\u624b\uff0c\u4f46\u5979\u5374\u53c8\u4e0d\u80fd\u5e2e\u7740\u88f4\u73cf\u6765\u4e0e\u51b7\u6c0f\u5144\u5f1f\u4e3a\u654c\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u671b\u5411\u6218\u98de\uff0c\u5fc3 \u4e2d\u6697\u9053\uff1a\u201c\u88f4\u73cf\u662f\u4f60\u4eec\u7684\u76df\u4e3b\u5927\u54e5\uff0c\u96be\u9053\u4f60\u4eec\u7adf\u4e0d\u4f38\u624b\u7ba1\u7ba1\u6b64\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5374\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u624b\u4e2d\u4e0d\u4f4f\u6447\u7740\u6298\u6247\uff0c\u7adf\u662f\u4e0d\u53d1\u4e00\u58f0\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u51b7\u51b7\u9053\uff1a\u201c\u59d3\u88f4\u7684\uff0c\u6211\u5144\u5f1f\u770b\u5728\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u4e0d\u5f97\u4e0d\u8ba9\u4f60\u51e0\u5206\uff0c\u600e\u5730\u52a8\u624b\xb7\u54ea\u91cc \u52a8\u624b\uff0c\u90fd\u7531\u4f60\u6765\u9009\u62e9\u597d\u4e86\uff01\u201d \u3000\u3000\u6a80\u6587\u742a\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5927\u53d4\uff0c\u4e8c\u53d4\uff0c\u4f60\u8001\u4eba\u5bb6\u660e\u660e\u77e5\u9053\u4ed6\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u53ef\u6bd4\u4f60\u8001\u4eba\u5bb6\u665a\u7740\u4e00 \u8f88\uff0c\u4f55\u82e6\u2026\u2026\u201d \u3000\u3000\u51b7\u67af\u6728\u7a81\u5730\u63a5\u53e3\u9053\uff1a\u201c\u59d3\u88f4\u7684\u82e5\u4ee3\u4ed6\u5e08\u7236\u5411\u6211\u5144\u5f1f\u53e9\u5934\u966a\u793c\uff0c\u6211\u5144\u5f1f\u4fbf\u53ef\u4e0d\u96be\u4e3a\u4ed6\uff0c\u6ec7 \u513f\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u522b\u65e0\u4ed6\u6cd5\uff0c\u4f60\u518d\u8bf4\u4e5f\u6ca1\u6709\u7528\u4e86\u3002\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u7a81\u5730\u4ef0\u5929\u72c2\u7b11\u8d77\u6765\uff0c\u51b7\u67af\u6728\u9762\u8272\u4e00\u6c89\u9053\uff1a\u201c\u4f60\u7b11\u4ec0 \u4e48\uff1f\u201d\u5434\u9e23\u4e16\u72c2\u7b11\u7740\u9053\uff1a\u201c\u6211\u7b11\u7684\u662f\u4e45\u95fb\u2018\u51b7\u8c37\u53cc\u6728\u2019\u4e0d\u4f46\u6210\u529f\u9ad8\u5f3a\uff0c\u800c\u4e14\u806a\u660e\u7edd\u4e16\uff0c\u54ea\u77e5 \u4eca\u65e5\u5374\u505a\u51fa\u8fd9\u822c\u5446\u4e8b\u51fa\u6765\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u9762\u8272\u9634\u6c89\uff0c\u58f0\u8272\u4e0d\u52a8\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u5144\u5f1f\u5446\u7684\u4ec0\u4e48\uff1f\u4f60\u4e14\u8bf4\u6765\u542c\u542c\uff01\u201d \u3000\u3000\u5434\u9e23\u4e16\u72c2\u7b11\u672a\u7edd\uff0c\u968f\u624b\u4e00\u6307\uff0c\u6307\u5411\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u4e00\u9762\u72c2\u7b11\u7740\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u8c01 \u4e48\uff1f\u6b64\u4eba\u4fbf\u662f\u540d\u58f0\u9707\u52a8\u6b66\u6797\uff0c\u5357\u4e03\u5317\u516d\u2014\u2014\u5341\u4e09\u7701\u65e0\u4eba\u4e0d\u77e5\uff0c\u65e0\u4eba\u4e0d\u6653\u7684\u2018\u98de\u9f99\u9556\u5c40\u2019\u5c40\u4e3b \u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u6a80\u5927\u4fa0\uff0c\u6a80\u5927\u4fa0\u4e0e\u88f4\u73cf\u4e24\u4ee3\u6df1\u4ea4\uff0c\u8bf4\u5f97\u4e0a\u662f\u5173\u7cfb\u975e\u6d45\uff01\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u624b \u6307\u8f6c\u5411\u201c\u795e\u624b\u201d\u6218\u98de\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\u4e48\uff1f\u6b64\u4eba\u4fbf\u662f\u6c5f\u752b\u6b66\u6797\u4e2d\u7684\u4e00\u4ee3\u8c6a\u6770\uff0c\u6d6a\u83bd\u5c71\u5e84\u7684\u5e84 \u4e3b\u2018\u795e\u624b\u2019\u6218\u98de\u6218\u5927\u4fa0\u3002\u201d\u624b\u6307\u518d\u6b21\u4e00\u8f6c\uff0c\u8f6c\u5411\u90a3\u98de\u8679\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u53c8\u662f\u8c01\u4e48\uff1f\u6b64\u4eba\u56ca\u4e2d\u4e03 \u4ef6\u6697\u5668\uff0c\u5929\u4e0b\u95fb\u540d\uff0c\u4eba\u79f0\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\uff0c\u5f53\u771f\u662f\u58f0\u540d\u8d6b\u8d6b\u3002\u201d\u53c8\u6307\u5411\u5411\u4e00\u557c\uff1a\u201c\u4f60\u53ef\u542c\u8fc7\u6c5f \u5357\u2018\u91d1\u9e21\u5e2e\u2019\u4e00\u557c\u60ca\u5929\uff0c\u518d\u557c\u52a8\u5730\uff0c\u8bfa\u8bfa\uff01\u6b64\u4eba\u4fbf\u662f\u2018\u91d1\u9e21\u5e2e\u2019\u5e2e\u4e3b\u5411\u4e00\u557c\u5411\u5927\u4fa0\u3002\u201d\u624b\u6307 \u4e00\u5708\uff0c\u7f13\u7f13\u6307\u5411\u88f4\u73cf\uff1a\u201c\u6218\u5e84\u4e3b\u3001\u90a3\u5e2e\u4e3b\u3001\u5411\u5927\u4fa0\u4e0e\u4ed6\u6b43\u8840\u4e3a\u76df\uff0c\u8a93\u5171\u751f\u6b7b\uff0c\u54c8\u54c8\u2014\u2014\u8fd9\u5173 \u7cfb\u4e4b\u6df1\uff0c\u66f4\u662f\u975e\u540c\u5c0f\u53ef\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u7a81\u5730\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u5230\u6b64\u5bfb\u4ec7\u4e4b\u524d\uff0c\u96be\u9053\u5c31\u672a\u66fe\u6253\u542c\u4e00\u4e0b\uff0c\u8fd9\u4e9b\u540d\u9707\u6c5f\u6e56\u7684\u82f1 \u96c4\u8c6a\u58eb\uff0c\u5c82\u5bb9\u4f60\u5bf9\u88f4\u5927\u5148\u751f\u4e0b\u624b\uff0c\u2018\u51b7\u8c37\u53cc\u6728\u2019\u867d\u7136\u6b66\u529f\u9ad8\u5f3a\uff0c\u54fc\u54fc\u2014\u2014\u53ea\u6015\u4e5f\u672a\u89c1\u5f97\u6bd4\u4ed6 \u4eec\u5f3a\u5230\u54ea\u91cc\u53bb\u5427\uff01\u201d \u3000\u3000\u51b7\u6c0f\u5144\u5f1f\u76ee\u5149\u4e00\u8f6c\uff0c\u9762\u4e0a\u663e\u89c1\u5df2\u51c4\u7136\u52a8\u5bb9\uff0c\u5144\u5f1f\u4e24\u4eba\uff0c\u5bf9\u671b\u4e00\u773c\uff0c\u6a80\u6587\u742a\u82b3\u5fc3\u5927\u5b9a\uff0c\u54ea \u77e5\u88f4\u73cf\u7a81\u5730\u80f8\u819b\u4e00\u633a\uff0c\u6717\u58f0\u9053\uff1a\u201c\u7236\u503a\u5b50\u8fd8\uff0c\u5144\u503a\u5f1f\u8fd8\uff0c\u5e08\u5f92\u4e4b\u95f4\uff0c\u672c\u5982\u5144\u5f1f\u7236\u5b50\uff0c\u662f\u4ee5\u5e08 \u503a\u5f92\u8fd8\uff0c\u4ea6\u662f\u5929\u7ecf\u5730\u4e49\u4e4b\u4e8b\uff0c\u5bb6\u5e08\u65e2\u7136\u5f97\u7f6a\u4e86\u4e24\u4f4d\u524d\u8f88\uff0c\u5c0f\u53ef\u867d\u7136\u65e0\u80fd\uff0c\u4f46\u81ea\u4e5f\u5e94\u4ee3\u5bb6\u5e08\u4e00 \u529b\u627f\u5f53\uff0c\u4e24\u4f4d\u524d\u8f88\u4f46\u8bf7\u653e\u5fc3\uff0c\u5c0f\u53ef\u7edd\u4e0d\u4f1a\u5411\u4ed6\u4eba\u6c42\u5206\u6beb\u4e4b\u52a9\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u79c0\u76ee\u4e00\u5f20\uff0c\u6025\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u5979\u4e00\u8fde\u8bf4\u4e86\u4e09\u4e2a\u201c\u4f60\u201d\u5b57\uff0c\u867d\u672a\u8bf4\u51fa \u4e0b\u6587\uff0c\u4f46\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e0d\u8a00\u53ef\u77e5\u3002 \u3000\u3000\u88f4\u73cf\u957f\u53f9\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6587\u742a\uff0c\u4f60\u5fc3\u91cc\u7684\u610f\u601d\uff0c\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\u2014\u2014\u5434\u5144\uff0c\u4f60\u5bf9\u6211\u7684\u597d \u610f\uff0c\u6211\u5fc3\u91cc\u66f4\u662f\u611f\u6fc0\uff0c\u4f46\u6211\u4e00\u751f\u4e4b\u4e2d\uff0c\u5b64\u6614\u65e0\u4f9d\uff0c\u76f4\u5230\u524d\u65e5\uff0c\u624d\u8499\u6069\u5e08\u6536\u7559\u95e8\u4e0b\uff0c\u6211\u4fbf\u662f\u7acb \u65f6\u6b7b\u4e86\uff0c\u5374\u4e5f\u4e0d\u80fd\u66ff\u6069\u5e08\u4e22\u4eba\uff0c\u6211\u4e00\u751f\u61e6\u5f31\uff0c\u65e2\u4e0d\u80fd\u5c3d\u5b5d\u4e8e\u7236\u6bcd\uff0c\u4ea6\u4e0d\u80fd\u884c\u4fa0\u4e8e\u5929\u4e0b\u2026\u2026\u201d \u8bf4\u5230\u540e\u6765\u4e24\u5308\uff0c\u4ed6\u8bed\u58f0\u4f4e\u5fae\uff0c\u5df2\u4f3c\u5583\u5583\u81ea\u8bed\uff0c\u8bed\u58f0\u5fae\u987f\uff0c\u7a81\u53c8\u6717\u58f0\u9053\uff1a\u201c\u6b64\u5730\u7fa4\u8c6a\u6b22\u5bb4\uff0c\u4e0d \u662f\u6d41\u8840\u52a8\u624b\u4e4b\u5730\uff0c\u4e24\u4f4d\u65e2\u8981\u52a8\u624b\uff0c\u5c0f\u53ef\u5916\u9762\u5949\u966a\u3002\u201d\u4ed6\u5e73\u65e5\u5bbd\u539a\u5f85\u4eba\uff0c\u4ee5\u5fb7\u62a5\u6028\uff0c\u522b\u4eba\u5584\u610f \u5f85\u4ed6\uff0c\u4ed6\u5fc3\u91cc\u611f\u6fc0\uff0c\u522b\u4eba\u6b3a\u51cc\u4e8e\u4ed6\uff0c\u4ed6\u5374\u4e0d\u77e5\u6000\u6068\u3002\u8fd9\u6b63\u662f\u4ed6\u5b85\u5fc3\u4ec1\u539a\u4e4b\u5904\uff0c\u4f46\u522b\u4eba\u770b\u6765\uff0c \u5374\u4f3c\u61e6\u5f31\u65e0\u80fd\uff0c\u76f4\u5230\u4eca\u65e5\uff0c\u4ed6\u4e00\u8fde\u9047\u7740\u6570\u4ef6\u4e0e\u4ed6\u672c\u8eab\u5e76\u65e0\u76f4\u63a5\u5173\u7cfb\u4e4b\u4e8b\uff0c\u4ed6\u5374\u663e\u9732\u4e86\u4ed6\u5916\u548c \u5185\u521a\u7684\u82f1\u96c4\u672c\u8272\uff0c\u5f53\u771f\u662f\u5934\u53ef\u65ad\uff0c\u8840\u53ef\u6d41\uff0c\u5fd7\u5374\u4e0d\u53ef\u5c48\uff0c\u6b64\u523b\u8fd9\u51e0\u53e5\u8bdd\uff0c\u66f4\u662f\u8bf4\u5f97\u622a\u9489\u65ad \u94c1\uff0c\u63b7\u5730\u6210\u58f0\u3002 \u3000\u3000\u6a80\u6587\u742a\u79cb\u6ce2\u51dd\u6ce8\uff0c\u5fc3\u4e2d\u4f46\u89c9\u53c8\u662f\u54c0\u75db\uff0c\u53c8\u662f\u96be\u8fc7\uff0c\u5374\u53c8\u4e3a\u4ed6\u5f97\u610f\uff0c\u9a84\u505a\uff0c\u5434\u9e23\u4e16\u5fc3\u4e2d\u6fc0 \u52a8\uff0c\u6b32\u8bed\u65e0\u58f0\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u4e4b\u4e2d\uff0c\u9732\u51fa\u60ca\u5947\u4e4b\u8272\uff0c\u6ee1\u5385\u7fa4\u8c6a\u4ea6\u662f\u6697\u4e2d\u5927\u751f\u8d5e\u4f69\u4e4b\u5fc3\uff0c \u800c\u90a3\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e25\u5cfb\u7684\u9762\u76ee\u4e4b\u4e0a\uff0c\u4e5f\u597d\u4f3c\u9732\u51fa\u4e00\u4e1d\u7b11\u5bb9\u3002 \u3000\u3000\u67af\u6728\u3001\u5bd2\u7af9\u5bf9\u671b\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u5916\u9762\u9886\u6559\u3002\u201d\u8f6c\u8eab\u5e76\u80a9\u8d70\u51fa\uff0c\u4f17\u4eba\u76ee\u9001 \u4ed6\u4e24\u4eba\u7684\u8eab\u5f71\u8f6c\u8fc7\u5706\u684c\uff0c\u7ecf\u8fc7\u83ab\u6c0f\u5144\u5f1f\u8eab\u4fa7\uff0c\u8d70\u5411\u5385\u5916\u3002 \u3000\u3000\u88f4\u73cf\u6717\u58f0\u9053\uff1a\u201c\u6211\u6b64\u53bb\u65e0\u8bba\u80dc\u8d1f\u751f\u6b7b\uff0c\u4ff1\u662f\u6211\u4e00\u4eba\u4e4b\u4e8b\uff0c\u82e5\u662f\u6709\u4eba\u8981\u76f8\u52a9\u4e8e\u6211\uff0c\u4fbf \u662f\u2026\u2026\u201d\u8bf8\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u201c\u4e03\u715e\u201d\u83ab\u661f\u7a81\u5730\u4e00\u58f0\u60e8\u547c\uff0c\u524a\u7626\u7684\u8eab\u8eaf\uff0c\u968f\u7740\u8fd9\u4e00\u58f0\u60e8\u547c\uff0c\u76f4\u7a9c \u4e24\u4e08\uff0c\u201c\u81a8\u201d\u5730\u4e00\u58f0\u78b0\u5230\u5c4b\u9876\uff0c\u201c\u53ed\u201d\u5730\u843d\u4e86\u4e0b\u6765\uff0c\u843d\u5728\u90a3\u9152\u7b75\u5706\u684c\u4e4b\u4e0a\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u63a5\u7740 \u53c8\u662f\u81a8\u7136\u4e00\u58f0\uff0c\u5706\u684c\u574d\u4e0b\uff0c\u5706\u684c\u4e0a\u7684\u201c\u4e03\u715e\u201d\u83ab\u661f\uff0c\u5374\u662f\u56db\u80a2\u50f5\u786c\uff0c\u52a8\u5f39\u4e0d\u5f97\u4e86\u3002 \u7b2c\u516b\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u516b\u7ae0 \u3000\u3000\u8fd9\u4e00\u4e2a\u7a81\u751f\u7684\u4fee\u53d8\uff0c\u4f7f\u5f97\u56db\u5ea7\u7fa4\u8c6a\u4e0d\u7981\u4e00\u8d77\u4e3a\u4e4b\u8038\u7136\u5927\u60ca\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u53ea\u89c1\u56db\u4e0b\u4eba\u5f71\u95ea\u52a8\uff0c\u7eb7\u7eb7\u8d70\u907f\uff0c\u53ea\u542c\u5f97\u60ca\u547c\u4e0e\u788e\u74f7\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u201c\u5317\u6597 \u4e03\u715e\u201d\u83ab\u6c0f\u5144\u5f1f\u4e00\u8d77\u5927\u559d\uff1a\u201c\u4e03\u5f1f\uff0c\u4f60\u600e\u5730\u4e86\uff1f\u201d \u3000\u3000\u8bed\u58f0\u65b9\u4e86\uff0c\u4e00\u5207\u5df2\u5f52\u4e8e\u9759\u6b62\u3002\u83ab\u6c0f\u5144\u5f1f\u4e09\u4eba\uff0c\u5404\u81ea\u60ca\u547c\u4e00\u58f0\uff0c\u4e00\u8d77\u6251\u5230\u83ab\u661f\u8eab\u4e0a\u65f6\uff0c \u201c\u795e\u624b\u201d\u6218\u98de\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\uff0c\u4ee5\u53ca\u201c\u98de\u7075 \u5821\u201d\u4e1c\u65b9\u5144\u5f1f\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7236\u5973\uff0c\u4ff1\u5df2\u81ea\u56db\u4fa7\u7f13\u6b65\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u624d\u90a3\u53d8\u6545\u53d1\u751f\u5f97\u662f\u90a3\u4e48\u7a81\u7136\uff0c\u4f46\u4ed6\u4eec\u5374\u65e0\u4e00\u4eba\u8eab\u4e0a\u6cbe\u6709\u534a\u6ef4\u679c\u6c41\u6c34\u73e0\uff0c\u6b64\u523b\u6b65\u5c65\u4e4b \u95f4\uff0c\u4ea6\u662f\u90a3\u822c\u4ece\u5bb9\u800c\u5b89\u8be6\uff0c\u76f4\u5982\u4efb\u4f55\u4e8b\u4ff1\u672a\u53d1\u751f\u4e00\u822c\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\uff0c\u987f\u4f4f\u811a\u6b65\uff0c\u7f13\u7f13\u8f6c\u8eab\uff0c\u5e76\u80a9\u7acb\u5728\u95e8\u7554\uff0c\u4e24\u4eba\u540c\u65f6\u5f20\u53e3\uff0c\u540c\u65f6\u95ed\u53e3\uff0c\u4e00\u5b57\u4e00 \u5b57\u5730\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u4fbf\u662f\u516c\u9053\uff01\u201d \u3000\u3000\u56db\u5ea7\u7fa4\u8c6a\uff0c\u5341\u4e2d\u6709\u4e5d\uff0c\u90fd\u672a\u770b\u6e05\u201c\u4e03\u715e\u201d\u83ab\u661f\u662f\u88ab\u4f55\u4eba\u505a\u4e86\u624b\u811a\uff0c\u6b64\u523b\u5fc3\u4e2d\u65b9\u59cb\u604d\u7136\uff0c \u201c\u539f\u6765\u662f\u2018\u51b7\u8c37\u53cc\u6728\u2019\uff01\u201d \u3000\u3000\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7adf\u80fd\u5728\u4eba\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5c06\u4e00\u4e2a\u5728\u6b66\u6797\u4e2d\u751a\u8d1f\u76db\u540d\u7684\u9ad8 \u624b\u6bd9\u4e8e\u638c\u4e0b\uff01\u7fa4\u8c6a\u4e0d\u7981\u4e3a\u4e4b\u6697\u4e2d\u9a87\u7136\uff0c\u6570\u767e\u9053\u76ee\u5149\uff0c\u4e00\u8d77\u4e0b\u610f\u8bc6\u5730\u671b\u5728\u88f4\u73cf\u9762\u4e0a\uff0c\u6709\u7684\u867d\u5728 \u4e3a\u4ed6\u62c5\u5fe7\uff0c\u6709\u7684\u5374\u5728\u51b7\u773c\u65c1\u89c2\uff0c\u770b\u4ed6\u662f\u5426\u5df2\u6709\u80c6\u602f\u4e4b\u610f\u3002 \u3000\u3000\u6a80\u6587\u742a\u6084\u6084\u8d70\u5230\u88f4\u73cf\u8eab\u4fa7\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u9ed8\u7136\u800c\u5782\u9996\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u9762\u8272\u51dd\u91cd\uff0c\u6ca1\u6709\u534a\u5206\u8868\u60c5\uff0c\u51b7\u51b7\u671b\u4e86\u6218\u98de\u4e00\u773c\uff0c\u4e1c\u65b9\u5144\u5f1f\u66f4\u662f\u4e0d\u52a8\u58f0 \u8272\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u626c\uff0c\u5389\u58f0\u9053\uff1a\u201c\u51b7\u8c37\u53cc\u6728\u867d\u7136\u540d\u626c\u5929\u4e0b\uff0c\u4f46\u662f\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c \u56db\u6307\u63e1\u62f3\uff0c\u59c6\u6307\u4e0a\u626c\uff0c\u5f80\u5730\u4e0a\u4e00\u6307\uff0c\u5389\u58f0\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4eca\u65e5\u4f60\u65e2\u5728\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u901e\u51f6\uff0c\u6218\u67d0 \u5c82\u80fd\u518d\u8ba9\u4f60\u751f\u79bb\u6b64\u95f4\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u7b80\u77ed\u800c\u6709\u5a01\u529b\uff0c\u76ee\u5149\u51db\u51db\uff0c\u987b\u53d1\u7686\u5f20\uff0c\u663e\u7136\u5df2\u52a8\u4e86\u771f\u6012\u3002\u8bdd\u58f0\u65b9\u4e86\uff0c\u53ea\u542c\u56db\u4e0b\u4e00 \u9635\u53f7\u89d2\u9f50\u9e23\uff0c\u54cd\u5f7b\u4e91\u9704\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9762\u5bb9\u51b7\u6f20\uff0c\u795e\u8272\u4e0d\u53d8\uff0c\u4ecd\u7136\u5e76\u80a9\u8d1f\u624b\u800c\u7acb\uff0c\u5c31\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u89c1\u4ed6\u7684\u8bdd\u4f3c \u7684\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\u2026\u2026 \u3000\u3000\u53ea\u542c\u89c1\u5916\u9662\u4e2d\uff0c\u7a81\u7136\u6d8c\u81f3\u767e\u5341\u6761\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u80cc\u540e\u659c\u63d2\u539a\u80cc\u8584\u5203\u7684\u9b3c\u5934\u5feb\u5200\uff0c\u624b\u4e2d\u5374 \u62ff\u7740\u6b66\u6797\u4e2d\u4eba\u6700\u4e3a\u80c6\u5bd2\u7684\u5f3a\u5f13\u786c\u5f29\uff0c\u8fd9\u767e\u5341\u6761\u5927\u6c49\u7a81\u5730\u81ea\u9662\u4e2d\u51fa\u73b0\uff0c\u7adf\u65e0\u4e00\u4eba\u53d1\u51fa\u534a\u70b9\u58f0 \u606f\u3002 \u3000\u3000\u56db\u5ea7\u7fa4\u8c6a\uff0c\u6709\u7684\u6276\u6848\u800c\u7acb\uff0c\u6709\u7684\u7aef\u5750\u5982\u6545\uff0c\u4f46\u4ea6\u65e0\u4efb\u4f55\u4e00\u4eba\uff0c\u53d1\u51fa\u534a\u70b9\u58f0\u606f\uff0c\u53ea\u6709\u6c89\u91cd \u7684\u547c\u5438\u4e0e\u5fc3\u8df3\u4e4b\u58f0\uff0c\u5355\u8c03\u5730\u6b64\u8d77\u5f7c\u843d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4ee4\u4eba\u7a92\u606f\u7684\u6c89\u5bc2\u4e4b\u4e2d\uff0c\u83ab\u6c0f\u5144\u5f1f\uff0c\u7f13\u7f13\u957f\u8eab\u800c\u8d77\uff0c\u4e09\u4eba\u4e00\u8d77\u9762\u5411\u6218\u98de\uff0c\u4e09\u4eba\u4e00\u8d77 \u6447\u4e86\u6447\u5934\uff0c\u4ed6\u4eec\u5df2\u65e0\u8a00\u5730\u5ba3\u5e03\u4e86\u83ab\u661f\u7684\u6b7b\u8baf\uff0c\u7136\u540e\u8fd9\u516d\u9053\u51b0\u51b7\u7684\u76ee\u5149\uff0c\u4fbf\u4e00\u8d77\u671b\u5230\u201c\u51b7\u8c37\u53cc \u6728\u201d\u5144\u5f1f\u4e24\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u8038\u52a8\uff0c\u7eb5\u6b65\u8d70\u5230\u201c\u4e03\u715e\u201d\u83ab\u661f\u7684\u5c38\u8eab\u524d\uff0c\u4fef\u9996\u51dd\u6ce8\u4e86\u534a\u664c\uff0c\u624b\u638c\u4e00 \u6325\uff0c\u7acb\u523b\u6709\u4e24\u6761\u5927\u6c49\uff0c\u5c06\u5c38\u8eab\u62ac\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u76ee\u5149\u4ea6\u4f3c\u5229\u5251\u822c\u671b\u5411\u201c\u51b7\u8c37\u53cc\u6728\u201d\uff0c\u7a81\u5730\u5927\u559d\u9053\uff1a\u201c\u51e1\u6211\u6c5f\u5357\u540c\u76df\uff0c\u4eca\u65e5\u4e0e\u4f60 \u2018\u51b7\u8c37\u53cc\u6728\u2019\u4ff1\u5df2\u52bf\u4e0d\u4e24\u7acb\uff0c\u4f60\u5144\u5f1f\u8fd8\u60f3\u9003\u5f97\u6389\u4e48\uff1f\u201d \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9762\u4e0a\u65e2\u65e0\u60ca\u5bb9\uff0c\u4ea6\u65e0\u60e7\u8272\uff0c\u5bf9\u5f53\u524d\u7684\u60c5\u52bf\uff0c\u4e1d\u6beb\u65e0\u52a8\u4e8e\u8877\uff0c\u8981\u77e5\u4ed6\u5144\u5f1f\u4e24 \u4eba\u80fd\u5728\u6b66\u6797\u4e2d\u4eab\u6709\u76db\u540d\uff0c\u81ea\u7136\u7edd\u975e\u795e\u667a\u9ebb\u6728\uff0c\u66f4\u975e\u72c2\u505a\u5f97\u5931\u53bb\u7406\u667a\uff0c\u800c\u662f\u4ed6\u4eec\u6df1\u77e5\u4efb\u4f55\u60ca\u614c \u4e4b\u6001\uff0c\u4ff1\u90fd\u4f1a\u52a9\u957f\u5bf9\u65b9\u7684\u51f6\u7130\uff0c\u662f\u4ee5\u4fbf\u4ee5\u4e0d\u53d8\u5e94\u4e07\u53d8\uff0c\u4ee5\u9759\u5236\u52a8\u3002 \u3000\u3000\u88f4\u73cf\u76ee\u5149\u51dd\u6ce8\u7740\u201c\u4e03\u715e\u201d\u5960\u661f\u7684\u5c38\u8eab\uff0c\u76ee\u9001\u7740\u8fd9\u66fe\u7ecf\u663e\u8d6b\u4e00\u65f6\u7684\u4eba\u7269\uff0c\u800c\u4eca\u4e5f\u53ea\u80fd\u51b0\u51b7 \u800c\u65e0\u52a9\u5730\u88ab\u56db\u53ea\u4ed6\u66fe\u7ecf\u8f7b\u8d31\u8fc7\u7684\u624b\u638c\uff0c\u9c81\u83bd\u5730\u62ac\u51fa\u5927\u5385\uff0c\u800c\u8fd9\u671f\u95f4\u7684\u8fc7\u7a0b\uff0c\u7ade\u53c8\u662f\u5982\u6b64\u77ed \u6682\uff0c\u751f\u547d\u4e0e\u6b7b\u4ea1\u7684\u5883\u754c\uff0c\u5c31\u5b9b\u5982\u5927\u5385\u5916\u90a3\u77ed\u77ed\u7684\u95e8\u69db\uff0c\u4f60\u53ea\u8981\u8f7b\u8f7b\u5f80\u5916\u8df3\u51fa\u4e00\u6b65\u2026\u2026 \u3000\u3000\u8fd9\u9635\u601d\u6f6e\u662f\u6c89\u91cd\u800c\u5bd2\u51b7\u5730\uff0c\u4f46\u5374\u6e05\u51bd\u5f97\u5982\u540c\u4e00\u9053\u6708\u591c\u4e2d\u7684\u6eaa\u6d41\uff0c\u6f7a\u6f7a\u5730\u81ea\u88f4\u73cf\u6df7\u4e71\u7684\u601d \u6f6e\u6d41\u8fc7\u3002\u4ed6\u7f13\u7f13\u62ac\u8d77\u5934\uff0c\u671b\u4e86\u8fd9\u5927\u5385\u4e2d\u56db\u4e0b\u7684\u4eba\u7fa4\u4e00\u773c\uff0c\u4ed6\u4eec\u867d\u7136\u4ff1\u90fd\u5341\u5206\u7d27\u5f20\uff0c\u4f46\u5374\u65e0\u4e00 \u4eba\u6709\u4e1d\u6beb\u60b2\u54c0\u4e0e\u60cb\u501f\u4e4b\u610f\uff0c\u5c31\u50cf\u65b9\u624d\u6240\u6b7b\u7684\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u964c\u751f\u800c\u5e73\u51e1\u7684\u4eba\u800c\u5df2\uff1b\u65e2\u4e0d\u662f \u65b9\u624d\u4e0e\u4ed6\u4eec\u5171\u540c\u996e\u8fc7\u8840\u9152\u7684\u540c\u76df\u5144\u5f1f\uff0c\u4ea6\u4e0d\u662f\u4e00\u4e2a\u66fe\u5728\u6c5f\u6e56\u4e2d\u4eab\u8fc7\u76db\u540d\u7684\u6b66\u6797\u8c6a\u58eb\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u53cc\u62f3\u7d27\u63e1\uff0c\u9759\u7acb\u4e0d\u52a8\uff0c\u4ed6\u867d\u4e5f\u5728\u9759\u5019\u7740\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u53cd\u5e94\uff0c\u4f46\u8c01\u90fd\u80fd\u770b \u5f97\u51fa\u4ed6\u7684\u7b49\u5f85\u5e76\u4e0d\u80fd\u6301\u4e45\uff0c\u56e0\u4e3a\u4ed6\u6b64\u523b\u5168\u8eab\u4ff1\u5df2\u6ee1\u8574\u7740\u6124\u6012\uff0c\u800c\u4e14\u4ed6\u53c8\u660e\u663e\u5730\u5360\u7740\u4f18\u52bf\u2014\u2014 \u5360\u7740\u4f18\u52bf\u7684\u4eba\uff0c\u901a\u5e38\u90fd\u60ef\u4e8e\u653b\u51fb\uff0c\u800c\u4e0d\u60ef\u4e8e\u7b49\u5f85\uff0c\u53ea\u662f\uff0c\u4ed6\u7684\u6124\u6012\u4e5f\u4e0d\u8fc7\u53ea\u662f\u56e0\u4e3a\u201c\u51b7\u8c37\u53cc \u6728\u201d\u635f\u4f24\u4e86\u4ed6\u7684\u989c\u9762\u800c\u5df2\uff0c\u4e0e\u201c\u4e03\u715e\u201d\u83ab\u661f\u7684\u6b7b\uff0c\u6839\u672c\u6beb\u65e0\u5173\u7cfb\uff0c\u82e5\u4e0d\u662f\u5728\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\uff0c \u82e5\u4e0d\u662f\u5f53\u7740\u8fd9\u4e48\u591a\u4ed6\u6781\u6b32\u63a7\u5236\u7684\u4eba\uff0c\u82e5\u4e0d\u662f\u4ed6\u6df1\u4fe1\u81ea\u5df1\u662f\u5360\u7740\u4f18\u52bf\uff0c\u4fbf\u662f\u201c\u5317\u6597\u4e03\u715e\u201d\u4e00\u8d77 \u88ab\u4eba\u6740\u6b7b\u4e86\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u6124\u6012\uff0c\u800c\u52a8\u5bb9\u7684\u2014\u2014\u56e0\u4e3a\u4ed6\u7eb5\u7136\u6124\u6012\uff0c\u4ed6\u4e5f\u4f1a\u5c06\u90a3\u4efd\u4e0d\u5fc5\u8981\u7684\u6124\u6012 \u5f88\u8c28\u614e\u5730\u9690\u85cf\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u84e6\u7136\u4e86\u89e3\u4e86\u751f\u547d\u7684\u4ef7\u503c\uff0c\u5e76\u4e0d\u4ec5\u5728\u4e8e\u751f\u524d\u7684\u8363\u8000\u800c\u663e\u8d6b\uff0c\u800c\u8fd8\u8be5\u6709 \u8bb8\u591a\u5176\u4ed6\u8bb8\u591a\u79cd\u5e94\u5f53\u88ab\u73cd\u60dc\u7684\u4e1c\u897f\u3002 \u3000\u3000\u8fd9\u4e9b\u4e1c\u897f\u5728\u201c\u795e\u624b\u201d\u6218\u98de\uff0c\u201c\u5317\u6597\u4e03\u715e\u201d\uff0c\u751a\u81f3\u6ee1\u5385\u7684\u6b66\u6797\u8c6a\u58eb\u5fc3\u4e2d\uff0c\u90fd\u662f\u6c38\u8fdc\u4e5f\u4e0d\u4f1a \u88ab\u73cd\u60dc\u7684\uff0c\u800c\u6b64\u523b\u5374\u968f\u7740\u90a3\u4e00\u9053\u6e05\u51bd\u7684\u6eaa\u6d41\uff0c\u5e73\u9759\u800c\u5b89\u8be6\u5730\u6ce8\u5165\u5230\u88f4\u73cf\u4ed6\u672c\u5df2\u5145\u6ee1\u4ec1\u6148\u800c\u5bbd \u6055\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u4ed6\u9762\u5bb9\u7a81\u5730\u53d8\u5f97\u51fa\u5947\u5730\u5b89\u8be6\u800c\u9547\u5b9a\uff0c\u4ed6\u5b89\u8be6\u800c\u9547\u5b9a\u5730\u8d70\u5230\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8eab\u524d\uff0c\u6c89\u58f0\u9053\uff1a \u201c\u51fa\u53bb\uff01\u201d \u3000\u3000\u4e00\u9635\u60ca\u547c\u58f0\u4e2d\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u5389\u53f1\u4e00\u58f0\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u88f4\u73cf\u5b89\u8be6\u5730\u8f6c\u8fc7\u8eab\u6765\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5389\u58f0\u9053\uff1a\u201c\u96be\u9053\u4f60\u6ca1\u6709\u542c\u5230\u6211\u65b9\u624d\u6240\u8bf4\u7684\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u867d\u4ecd\u7b80\u77ed\u800c\u6709\u5a01\u529b\uff0c\u4f46\u5374\u663e\u7136\u5df2\u88ab\u88f4\u73cf\u8fd9\u4efd\u51fa\u5947\u5730\u5b89\u8be6\u4e0e\u9547\u9759\u523a\u4f24\u4e86\u4e00\u4e9b\uff0c\u662f\u4ee5 \u4ed6\u5a01\u4e25\u7684\u8bed\u58f0\uff0c\u7adf\u7a7a\u524d\u5730\u66b4\u9732\u51fa\u4e00\u4e1d\u5f31\u70b9\uff0c\u4ed6\u7eb5\u60f3\u63a9\u9970\uff0c\u5374\u529b\u4e0d\u80fd\u902e\uff0c\u5c31\u6b63\u5982\u4e00\u53ea\u731b\u864e\u5728\u72fc \u7fa4\u4e2d\u53d1\u73b0\u81ea\u5df1\u7684\u5f31\u70b9\uff0c\u4e5f\u6b63\u5982\u731b\u864e\u4e0d\u613f\u7fa4\u72fc\u55c5\u5230\u81ea\u5df1\u7684\u8840\u8165\u4e00\u6837\u3002\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60 \u65b9\u624d\u6240\u8bf4\u7684\u8bdd\uff0c\u6211\u6bcf\u4e00\u53e5\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u2014\u2014\u201d\u201c\u795e\u624b\u201d\u6218\u98de\u80f8\u819b\u4e00\u633a\uff0c\u663e\u7136\u4e3a\u81ea\u5df1\u7684\u8a00\u8bf4 \u80fd\u88ab\u91cd\u89c6\u800c\u6cbe\u6cbe\u81ea\u559c\u3002 \u3000\u3000\u4f46\u88f4\u73cf\u5374\u53c8\u63a5\u53e3\u9053\uff1a\u201c\u4f46\u662f\uff0c\u96be\u9053\u4f60\u5df2\u5fd8\u4e86\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u6211\u4ecd\u662f\u6c5f\u5357\u540c\u76df\u7684\u76df\u4e3b\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u5934\u4e00\u9707\uff0c\u88f4\u73cf\u53e3\u4e2d\u8fd9\u5b89\u8be6\u7684\u8bed\u58f0\uff0c\u7adf\u4eff\u4f5b\u662f\u97ad\u5b50\u4e00\u6837\u97ad\u9791\u5728\u4ed6\u8eab\u4e0a\uff0c\u4f7f \u5f97\u4ed6\u4e0d\u7531\u81ea\u4e3b\u5730\u9000\u7f29\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u88f4\u73cf\u76ee\u5149\u4e00\u626b\uff0c\u5fae\u7b11\u53c8\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u51e1\u6211\u6c5f\u5357\u540c\u76df\uff0c\u90fd\u8be5\u5c0a\u91cd\u76df\u4e3b\u4e4b\u610f\u89c1\u7684\uff0c\u82e5\u6709 \u8fdd\u6297\u4e4b\u8a00\uff0c\u4f60\u201d\u795e\u624b\u201c\u4fbf\u662f\u76df\u4e3b\u7684\u62a4\u6cd5\u4e4b\u4eba\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u5e73\u65e5\u88ab\u751f\u547d\u7684\u4e0d\u5e78\u4e0e\u6ce2\u6298\uff0c\u751f\u6d3b\u7684\u8270\u82e6\u4e0e\u5c48\u8fb1\uff0c\u7d27\u7d27\u63a9\u57cb\u8d77\u4e86\u7684\u667a\u6167\uff0c\u5728\u8fd9\u5239\u90a3\u4e4b \u95f4\uff0c\u5df2\u50cf\u662f\u4e00\u67c4\u9525\u5b50\u523a\u7834\u5e03\u56ca\u4e00\u822c\u5730\u9732\u4e86\u51fa\u6765\uff0c\u6709\u4e86\u667a\u6167\u7684\u8a00\u8bed\uff0c\u81ea\u7136\u4e5f\u5c31\u53d8\u5f97\u51fa\u5947\u7684\u950b \u9510\uff0c\u6b63\u5f53\u8fd9\u950b\u9510\u7684\u8a00\u8bed\u81ea\u5b89\u8be6\u800c\u5fae\u7b11\u7740\u7684\u53e3\u4e2d\u8bf4\u51fa\u6765\u65f6\uff0c\u5b83\u4fbf\u6709\u4e86\u97ad\u5b50\u822c\u7684\u529b\u9053\uff0c\u76f4\u63a5\u97ad\u9791 \u5230\u522b\u4eba\u5fc3\u5e95\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u663e\u7136\u88ab\u51fb\u5012\uff0c\u4ed6\u7070\u9edd\u5374\u53c8\u5e26\u7740\u60e8\u7eff\u76ee\u5149\u2014\u2014\u90a3\u5374\u662f\u997f\u72fc\u5e38\u5e26\u7684\u76ee\u5149\u2014\u2014\u56db \u4e0b\u4e00\u626b\u3002 \u3000\u3000\u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u5fae\u76b1\uff0c\u5634\u89d2\u5374\u4ecd\u5fae\u5fae\u542b\u7b11\uff0c\u4e1c\u65b9\u5144\u5f1f\u76ee\u5149\u95ee\u70c1\uff0c\u5bf9\u88f4\u73cf\u4f3c\u4e4e\u6709\u4e86 \u4e9b\u60fa\u60fa\u76f8\u501f\u4e4b\u610f\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u6ee1\u9762\u60ca\u5947\uff0c\u76ee\u5149\u4e2d\u5374\u53c8\u4ea4\u70c1\u7740\u4e00\u4e9b\u5e78\u707e\u4e50\u7978\u4e4b\u610f\u2014\u2014\u5176\u4ed6\u7684\u6b66\u6797\u7fa4 \u8c6a\uff0c\u4e5f\u5dee\u4e0d\u591a\u662f\u8fd9\u79cd\u795e\u60c5\uff0c\u53ea\u6709\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\uff0c\u5374\u5728\u76fc\u6ce8\u7740\u83ab\u6c0f\u5144\u5f1f\uff0c\u5728\u60f3\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u83ab\u6c0f\u5144\u5f1f\uff0c\u65e2\u662f\u6124\u6012\uff0c\u53c8\u662f\u60b2\u54c0\uff0c\u4f46\u4e5f\u6709\u7740\u66f4\u591a\u60ca\u5947\u3002 \u3000\u3000\u6a80\u6587\u742a\u79cb\u6ce2\u84e6\u5730\u660e\u4eae\u4e86\u8d77\u6765\uff0c\u5979\u662f\u5149\u8363\u3001\u9a84\u505a\uff0c\u800c\u6b23\u6170\u7684\uff1b\u4f46\u5374\u53c8\u6709\u4e00\u4e9b\u62c5\u5fc3\uff0c\u201c\u4e03\u5de7 \u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u63a9\u4e0d\u4f4f\u4ed6\u5fc3\u4e2d\u7684\u6b23\u6170\u4e4b\u60c5\uff0c\u4ed6\u773c\u770b\u7740\u4ed6\u7684\u597d\u53cb\u81ea\u88ab\u5c48\u4fae\uff0c\u800c\u88ab\u5c0a\u656c\uff0c\u4ed6\u4e5f\u6df1\u77e5 \u8fd9\u5386\u7a0b\u770b\u6765\u867d\u8f7b\u6613\uff0c\u5176\u5b9e\u5374\u4e0d\u77e5\u6709\u591a\u4e48\u957f\u800c\u8270\u8f9b\u3002 \u3000\u3000\u8fd9\u8bb8\u591a\u4eba\u9762\u4e0a\u8868\u60c5\u7684\u53d8\u5316\uff0c\u5728\u4e00\u5239\u90a3\u4e4b\u95f4\uff0c\u4fbf\u4e00\u8d77\u6536\u56de\u201c\u795e\u624b\u201d\u773c\u5e95\uff0c\u7b49\u5230\u4ed6\u9510\u5229\u7684\u76ee \u5149\u56de\u5230\u88f4\u73cf\u9762\u4e0a\uff0c\u4ed6\u5fc3\u4e2d\u5df2\u6709\u4e86\u51b3\u5b9a\u3002 \u3000\u3000\u8fd9\u6b66\u6797\u4e2d\u7684\u67ad\u96c4\u4eba\u7269\u7adf\u7a81\u5730\u6717\u58f0\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u634b\u987b\u7b11\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u5df2\u662f\u6c5f\u5357\u540c\u76df\u4e4b \u9996\uff0c\u6218\u67d0\u5c82\u4f1a\u5fd8\u8bb0\uff0c\u4e0d\u4f46\u6218\u67d0\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u800c\u4e14\u82e5\u662f\u6709\u8c01\u5fd8\u8bb0\u4e86\uff0c\u6218\u67d0\u4e5f\u4f1a\u63d0\u9192\u4e00\u2014\u2014\u201d\u4ed6\u7b11 \u58f0\u4e00\u987f\uff0c\u7a81\u5730\u95ea\u7535\u822c\u4f38\u51fa\u624b\u638c\uff0c\u6a2a\u638c\u4e00\u626b\uff0c\u53ea\u542c\u201c\u547c\u201d\u5730\u4e00\u58f0\uff0c\u4e00\u9053\u5f3a\u52b2\u65e0\u6bd4\u7684\u638c\u98ce\uff0c\u7b14\u76f4 \u5730\u5411\u4ed6\u8eab\u65c1\u7684\u4e00\u5f20\u6728\u6905\u51fb\u53bb\uff0c\u201c\u5580\u5587\u201d\u4e00\u54cd\uff0c\u6728\u6905\u4fbf\u5df2\u88ab\u9707\u5f97\u56db\u6563\u98de\u843d\u3002 \u3000\u3000\u6218\u98de\u6d53\u7709\u4e00\u6311\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u63a5\u53e3\u9053\uff1a\u201c\u975e\u5e38\u5c0f\u5fc3\u5730\u63d0\u9192\u4ed6\u4e00\u58f0\uff0c\u76f4\u5230\u4ed6\u4e34\u6b7b\u524d\u90fd\u4e0d\u4f1a\u5fd8 \u8bb0\uff01\u201d \u3000\u3000\u4ed6\u6b64\u523b\u773c\u795e\u4e2d\u867d\u4ecd\u5e26\u201c\u72fc\u201d\u7684\u5149\u8292\uff0c\u4f46\u795e\u6001\u95f4\u5374\u5df2\u6062\u590d\u4e86\u201c\u864e\u201d\u7684\u5a01\u4e25\uff0c\u201c\u795e\u624b\u201d\u6218 \u98de\uff0c\u6bd5\u7adf\u662f\u6b66\u6797\u4e4b\u96c4\uff01 \u3000\u3000\u88f4\u73cf\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u5728\u6211\u4e0e\u2018\u51b7\u8c37\u53cc\u6728\u2019\u4e4b\u95f4\u7684\u4e8b\u8fd8\u672a\u89e3\u51b3\u4e4b\u524d\uff0c\u4e00\u5207\u4e8b\u90fd\u5f97\u6682\u7f13 \u5904\u7406\uff0c\u800c\u6211\u4e0e\u2018\u51b7\u8c37\u53cc\u6728\u2019\u4e4b\u95f4\u7684\u6881\u5b50\uff0c\u4e5f\u53ea\u80fd\u7531\u6211\u4e0e\u4ed6\u4eec\u5355\u72ec\u89e3\u51b3\u3002\u201d\u4ed6\u8bed\u58f0\u4e0d\u4f46\u5b89\u8be6\uff0c \u800c\u4e14\u7a81\u5730\u663e\u9732\u51fa\u4e00\u79cd\u8d85\u4eba\u7684\u5a01\u4e25\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u56db\u671b\u4e00\u773c\uff0c\u56db\u4e0b\u201c\u7fa4\u8c6a\u53c8\u6e10\u6e10\u5f00\u59cb\u9a9a\u4e71\uff0c\u6a80\u6587\u742a\u5fcd\u4e0d\u4f4f\u5a07\u5524\u4e00\u58f0\uff0c\u83ab\u6c0f\u5144 \u5f1f\u5374\u5df2\u66b4\u6012\u8d77\u6765\u3002\u9a9a\u52a8\u4e2d\u54cd\u8d77\u4e00\u58f0\u5927\u559d\uff1a\u201c\u76df\u4e3b\u4e4b\u4ee4\uff0c\u8fdd\u4ee4\u8005\u65a9\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u3001\u6218\u98de\u624b\u638c\u4e00\u6325\uff0c\u9662\u5916\u7a81\u5730\u81ea\u56db\u5468\u6d8c\u73b0\u51fa\u7684\u9ed1\u8863\u5927\u6c49\uff0c\u4fbf\u53c8\u50cf\u4ed6\u4eec\u6765\u65f6\u90a3\u4e48\u7a81 \u7136\uff0c\u50cf\u65e0\u58f0\u606f\u7684\u9000\u4e86\u4e0b\u53bb\uff0c\u4f46\u4ed6\u4eec\u80cc\u540e\u9b3c\u5934\u5200\u5200\u67c4\u4e0a\u7684\u7ea2\u4e2d\uff0c\u5374\u4ecd\u4e0d\u65f6\u5730\u5728\u5fae\u98ce\u4e2d\uff0c\u81ea\u56db\u4e0b \u7684\u5899\u5934\u540e\uff0c\u5c71\u77f3\u8fb9\u98de\u626c\u8d77\u6765\uff01\u8fd9\u5176\u95f4\u53ea\u6709\u201d\u51b7\u8c37\u53cc\u6728\u201c\u9762\u4e0a\u7684\u8868\u60c5\uff0c\u5374\u4ecd\u7136\u662f\u51b7\u5982\u7384\u51b0\uff0c\u4eff \u4f5b\u8fd9\u4e00\u5207\u7684\u53d1\u751f\uff0c\u4ff1\u90fd\u4e0e\u4ed6\u4eec\u65e0\u5173\u3002\u83ab\u6c0f\u5144\u5f1f\u7684\u516d\u9053\u76ee\u5149\uff0c\u6076\u6bd2\u5730\u5728\u201d\u795e\u624b\u201c\u6218\u98de\u4ee5\u53ca\u88f4\u73cf \u9762\u4e0a\u8f6c\u6765\u8f6c\u53bb\uff0c\u6218\u98de\u5374\u4e5f\u89c6\u5982\u65e0\u7779\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u5982\u6709\u4e8b\u6599\u7406\uff0c\u6218\u67d0\u5728\u6b64\u606d\u5019\u5927 \u9a7e\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u6765\u4eff\u4f5b\u6b64\u53bb\u4e0d\u8fc7\u662f\u53bb\u4e0e\u4e24\u4e2a\u987d\u7ae5\u563b\u620f\u4e00\u6837\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4fbf\u4f1a\u5b89\u7136\u56de\u6765\uff0c\u5176\u5b9e\u4ed6\u5374\u5f97 \u77e5\u88f4\u73cf\u6b64\u53bb\uff0c\u5b9a\u5fc5\u4e0d\u4f1a\u91cd\u8fd4\uff0c\u662f\u4ee5\u4ed6\u624d\u5982\u6b64\u505a\u6cd5\uff0c\u56e0\u4e3a\u4ed6\u6b64\u523b\u5df2\u5bf9\u8fd9\u201c\u5e73\u51e1\u800c\u5446\u7b28\u201d\u7684\u5c11 \u5e74\uff0c\u7a81\u5730\u751f\u51fa\u4e00\u79cd\u754f\u60e7\u4e4b\u5fc3\uff0c\u751f\u6015\u81ea\u5df1\u517b\u864e\u8d3b\u60a3\uff0c\u662f\u4ee5\u6b63\u597d\u5047\u501f\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e4b\u624b\uff0c\u5c06\u4ed6\u9664 \u53bb\u3002 \u3000\u3000\u88f4\u73cf\u5fae\u4e00\u62b1\u62f3\uff0c\u8f6c\u8fc7\u8eab\u53bb\uff0c\u518d\u6b21\u5411\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\uff01\u201d \u3000\u3000\u4ed6\u76ee\u5149\u867d\u7136\u4e00\u65e0\u6240\u754f\uff0c\u4f46\u5374\u518d\u4e5f\u4e0d\u6562\u4e0e\u6a80\u6587\u742a\u90a3\u6e29\u67d4\u7684\u773c\u6ce2\u63a5\u89e6\u4e00\u4e0b\uff0c\u751f\u50cf\u662f\u4ed6\u5bf9\u5979\u5df2 \u4e00\u65e0\u6240\u604b\u3002 \u3000\u3000\u6a80\u6587\u742a\u5446\u5446\u5730\u671b\u7740\u4ed6\uff0c\u76f4\u5230\u4ed6\u8eab\u5f62\u6b65\u4e0b\u53f0\u9636\uff0c\u7a81\u5730\u4e00\u54ac\u6a31\u5507\uff0c\u5728\u5979\u7239\u7239\u8eab\u4fa7\u5750\u4e86\u4e0b\u6765\uff0c \u4ea6\u81ea\u518d\u4e5f\u4e0d\u53bb\u671b\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u6068\u4e0e\u7231\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u672c\u4ec5\u76f8\u9694\u4e00\u7ebf\uff0c\u7231\u5f97\u8d8a\u6df1\uff0c\u6068\u5f97\u4e5f\u5c31\u66f4\u5f3a\u70c8\uff0c\u8fd9\u591a\u60c5\u7684\u5c11\u5973\u6b64\u523b\u6b63 \u5728\u5fc3\u4e2d\u53cd\u590d\u5730\u6697\u4e2d\u4f4e\u8bed\uff1a\u201c\u4f60\u5bf9\u6211\u4e00\u65e0\u6240\u604b\uff0c\u96be\u9053\u6211\u5b9a\u8981\u82e6\u6614\u5730\u7559\u604b\u7740\u4f60\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u4fa7\u76ee\u671b\u4e86\u81ea\u5df1\u7684\u7231\u5973\u4e00\u773c\uff0c\u4f3c\u4e4e\u6697\u6697\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u7136\u540e\uff0c\u4ed6\u70af\u7136\u7684\u65e5\u5149\uff0c \u4fbf\u53c8\u8f6c\u5230\u88f4\u73cf\u7684\u540e\u5f71\u4e0a\u3002 \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u4ed6\u867d\u7136\u806a\u660e\u7edd\u9876\uff0c\u4f46\u6b64\u523b\u4ea6\u662f\u5168\u65e0\u4e3b\u610f\uff0c\u53ea\u6709\u50cf\u522b\u4eba\u4e00 \u6837\uff0c\u76ee\u9001\u7740\u88f4\u73cf\u7684\u8eab\u5f71\u8fdc\u53bb\u3002 \u3000\u3000\u76f4\u5230\u88f4\u73cf\u8d70\u5230\u9662\u4e2d\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u65b9\u81ea\u7f13\u7f13\u5f00\u59cb\u79fb\u52a8\u811a\u6b65\uff0c\u8fd9\u5176\u95ee\u4ed6\u4eec\u7684\u773c\u795e\uff0c\u59cb\u7ec8\u672a \u66fe\u79bb\u5f00\u8fc7\u83ab\u6c0f\u5144\u5f1f\u7684\u773c\u775b\u3002 \u3000\u3000\u83ab\u6c0f\u5144\u5f1f\u7684\u624b\u638c\u7d27\u63e1\u7740\uff0c\u4ed6\u4eec\u7d27\u63e1\u7740\u7684\u624b\u638c\uff0c\u5df2\u7531\u8840\u7ea2\uff0c\u53d8\u4e3a\u94c1\u9752\uff0c\u53ea\u89c1\u201c\u51b7\u8c37\u53cc\u6728\u201d \u51b7\u51b7\u5730\u671b\u7740\u4ed6\u4eec\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u9762\u4e0a\u7a81\u5730\u6cdb\u8d77\u4e86\u4e00\u4e1d\u8f7b\u8511\u7684\u51b7\u7b11\uff0c\u9f50\u5730\u4e00\u62c2\u888d\u8896\u968f\u7740\u88f4\u73cf\u8d70 \u53bb\u3002 \u3000\u3000\u83ab\u6c0f\u5144\u5f1f\u4e0d\u662f\u5446\u5b50\uff0c\u5f53\u7136\u770b\u5f97\u51fa\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8fd9\u8f7b\u8511\u7b11\u5bb9\u7684\u542b\u610f\uff0c\u56e0\u4e3a\u81ea\u5df1\u5144\u5f1f\u4e09\u4eba\uff0c \u867d\u7136\u9762\u5bf9\u7740\u4e0e\u81ea\u5df1\u6709\u7740\u8840\u4ec7\u7684\u654c\u4eba\uff0c\u7adf\u6ca1\u6709\u4e00\u4eba\u6562\u51fa\u6765\u590d\u4ec7\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6df1\u77e5\u81ea\u5df1\u5fc3\u4e2d\u7684\u754f \u60e7\uff0c\u8981\u8fdc\u6bd4\u6124\u6012\u4e0e\u4ec7\u6068\u6765\u5f97\u5f3a\u70c8\u7684\u591a\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4efd\u8f7b\u8511\uff0c\u5374\u53c8\u662f\u8fd9\u4e48\u5f3a\u70c8\uff0c\u5f3a\u70c8\u5f97\u4ee4\u83ab\u6c0f\u5144\u5f1f\u65e0\u6cd5\u5fcd\u53d7\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u8f6c\u5904\uff0c\u4e00\u6b65\u62a2\u5230\u4ed6\u4eec\u8eab\u524d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u2018\u51b7\u8c37\u53cc\u6728\u2019\u82e5\u662f\u6ca1\u6709\u6b7b\u5728\u88f4 \u5927\u5148\u751f\u624b\u4e0b\uff0c\u5144\u5f1f\u7acb\u8a93\uff0c\u4e00\u5b9a\u4ee3\u83ab\u5149\u590d\u4ec7\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u76ee\u5149\u4e2d\u6cdb\u8d77\u4e00\u4e1d\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u82e5\u662f\u88f4\u5927\u5148\u751f\u80dc\u4e86\uff0c\u83ab\u5144\u6709\u76df\u4e3b \u4ee3\u4e3a\u590d\u4ec7\uff0c\u8fd8\u4e0d\u662f\u4e00\u6837\u4e48\uff1f\u201d \u3000\u3000\u83ab\u6c0f\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u4e2a\u4e2a\u957f\u53f9\u4e00\u58f0\uff0c\u5782\u4e0b\u5934\u53bb\uff0c\u5bf9\u4e8e\u88f4\u73cf\uff0c\u4ed6\u4eec\u4e0d\u7981\u751f\u51fa\u4e86\u4e00\u4e1d\u656c\u610f\uff0c \u56e0\u4e3a\u4ed6\u4eec\u5df2\u5f00\u59cb\u5bf9\u81ea\u5df1\u7684\u61e6\u5f31\u60b2\u54c0\uff0c\u4ed6\u6599\u4e0d\u5230\u4eba\u7c7b\u4e2d\u7adf\u6709\u4eba\u80fd\u5c06\u751f\u6b7b\u4e4b\u4e8b\uff0c\u770b\u5f97\u5982\u6b64\u8f7b\u8d31\uff0c \u201c\u5317\u6597\u4e03\u715e\u201d\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u540d\uff0c\u4ece\u6b64\u4e00\u6485\u4e0d\u632f\uff0c\u56e0\u4e3a\u6b64\u523b\u5df2\u6709\u6570\u767e\u9053\u76ee\u5149\uff0c\u770b\u5230\u4e86\u4ed6\u4eec\u5144\u5f1f \u7684\u61e6\u5f31\u3002 \u3000\u3000\u4e8e\u662f\u201c\u795e\u624b\u201d\u6218\u98de\u795e\u91c7\u98de\u626c\u5730\u8f6c\u8fc7\u8eab\u6765\uff0c\u5429\u5490\u624b\u4e0b\uff0c\u91cd\u6446\u9152\uff0c\u4f46\u83ab\u6c0f\u5144\u5f1f\u5374\u53ea\u80fd\u9893\u4e27\u5730 \u8d77\u8eab\u8d70\u51fa\u5385\u5916\uff0c\u7167\u6599\u4ed6\u6b7b\u53bb\u5f1f\u5144\u7684\u540e\u4e8b\u4e86\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u6c89\u58f0\u9053\uff1a\u201c\u83ab\u4e03\u4e0e\u6211\u4ea4\u60c5\u4e0d\u9519\uff0c\u6211\u5f97\u53bb\u770b\u770b\u4ed6\u7684\u540e \u4e8b\u3002\u201d\u4e0d\u5f85\u6218\u98de\u7b54\u8bdd\uff0c\u968f\u7740\u83ab\u6c0f\u5144\u5f1f\u8d70\u51fa\uff0c\u6b64\u4eba\u5fc3\u673a\u6df1\u6c89\uff0c\u662f\u5728\u6b64\u523b\u53ea\u6709\u4ed6\u624d\u4f1a\u5229\u7528\u65f6\u673a\uff0c \u6536\u62e2\u83ab\u6c0f\u5144\u5f1f\u7684\u4eba\u5fc3\uff0c\u56e0\u4e3a\u4ed6\u6df1\u77e5\u8fd9\u5144\u5f1f\u4e09\u4eba\uff0c\u867d\u7136\u61e6\u5f31\uff0c\u4f46\u4ecd\u6709\u7740\u4e0d\u53ef\u5ffd\u89c6\u7684\u529b\u91cf\u3002 \u3000\u3000\u6c5f\u5357\u540c\u76df\u5df2\u6210\uff0c\u88f4\u73cf\u5b9a\u7136\u6c38\u4e0d\u590d\u8fd4\uff0c\u90a3\u4e48\u201c\u795e\u624b\u201d\u6218\u98de\u5c82\u975e\u987a\u7406\u6210\u7ae0\u5730\u6210\u4e86\u6c5f\u5357\u7684\u76df \u4e3b\u3002\u662f\u4ee5\u4ed6\u89c1\u5230\u90a3\u98de\u8679\u7684\u884c\u52a8\uff0c\u53ea\u662f\u8f7b\u8511\u7684\u6697\u7b11\u4e00\u58f0\uff0c\u4e1d\u6beb\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5f97\u610f\uff0c\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u6b63\u9762\u5e26\u5fae\u7b11\u5730\u671b\u7740\u4ed6\uff0c\u4f3c\u4e4e\u65e9\u5df2\u770b\u51fa \u4ed6\u7684\u5fc3\u610f\u3002 \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u5446\u4e86\u534a\u664c\uff0c\u7a81\u5730\u5927\u6b65\u5954\u51fa\u5385\u5916\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u8f7b\u54b3\u4e00\u58f0\uff0c\u9662\u4e2d\u4eba\u5f71\u95ea\u52a8\uff0c\u9ed1\u8863\u6c49\u5b50\u4e00\u8d77\u6d8c\u51fa\uff0c\u5f3a\u5f13\u786c\u5f29\uff0c\u6c89\u9ed8\u5730\u5bf9\u7740 \u4ed6\uff0c\u5434\u9e23\u4e16\u76ee\u5149\u4e00\u51db\uff0c\u56de\u9996\u559d\u9053\uff1a\u201c\u8fd9\u7b97\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u7b11\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u65b9\u624d\u6240\u4e0b\u7684\u547d\u4ee4\uff0c\u4f60\u96be\u9053\u4e0d\u66fe\u542c\u5230\uff1f\u76df\u4e3b \u65e2\u5df2\u6709\u4ee4\uff0c\u4e0d\u5bb9\u522b\u4eba\u63d2\u624b\uff0c\u5434\u5144\u8fd8\u662f\u803d\u5728\u8fd9\u91cc\u7684\u597d\u3002\u201d \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u76ee\u5149\u5149\u8292\u66f4\u70bd\uff0c\u663e\u6709\u4e0d\u5e73\u4e4b\u610f\uff0c\u54ea\u77e5\u5434\u9e23\u4e16\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u957f\u53f9\u4e00 \u58f0\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u51fa\u53bb\uff0c\u4e5f\u4e0d\u8fc7\u662f\u8981\u5bf9\u4ed6\u8bf4\u4e00\u53e5\u2018\u73cd\u91cd\u2019\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u76df\u4e3b\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u96be\u9053\u8fd8\u4f1a\u4e0d\u77e5\u73cd\u91cd\uff1f\u5434\u5144\uff0c\u4f60\u4e14\u770b\u76df\u4e3b\u65b9 \u624d\u51fa\u624b\u7684\u6b66\u529f\uff0c\u2018\u51b7\u8c37\u53cc\u6728\u2019\u5f3a\u715e\uff0c\u4e5f\u672a\u89c1\u80fd\u6321\u5f97\u4f4f\u5341\u62db\uff0c\u6765\u6765\u6765\u2026\u2026\u4f60\u6211\u5144\u5f1f\uff0c\u4e14\u6765\u5171\u996e \u4e00\u676f\uff0c\u9884\u795d\u76df\u4e3b\u7684\u6210\u529f\uff01\u201d \u3000\u3000\u4ed6\u867d\u5148\u7aef\u8d77\u9152\u676f\uff0c\u56db\u4e0b\u4e00\u7167\uff0c\u4ef0\u9996\u5e72\u4e86\u4e00\u676f\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u6697\u601d\u5fd6\uff1a\u201c\u82b1\u7389\u5440\u82b1\u7389\uff0c\u4f60\u4e00 \u751f\u51fa\u5356\u7684\u6d88\u606f\uff0c\u603b\u52a0\u8d77\u6765\uff0c\u5bf9\u6211\u800c\u8a00\uff0c\u90fd\u4e0d\u5982\u4f60\u6b7b\u540e\u6240\u51fa\u5356\u7684\u4e00\u4e2a\u91cd\u8981\uff0c\u56e0\u4e3a\u4f60\u5df2\u8bf4\u51fa\u4e86\u4e00 \u4ef6\u79d8\u5bc6\uff0c\u4fbf\u662f\u88f4\u73cf\u867d\u6709\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u4f46\u4ec5\u53ea\u4f1a\u4e00\u62db\uff0c\u54c8\u54c8\u2014\u2014\u4ed6\u82e5\u662f\u518d\u591a\u4f1a\u51e0\u62db\uff0c\u6211\u4fbf\u5f53\u771f \u8981\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u7b49\u5230\u4ed6\u7684\u6012\u624d\u4e3a\u4ed6\u659f\u6ee1\u4e86\u7a7a\u676f\uff0c\u4fbf\u53c8\u4ef0\u9996\u4e00\u5343\u800c\u5c3d\uff0c\u5f97\u610f\u5730\u5728\u5fc3\u4e2d\u6697\u6697\u81ea\u8bed\u9053\uff0c \u201c\u82b1\u7389\u5440\u82b1\u7389\uff01\u4f60\u53ef\u77e5\u9053\uff0c\u8fd9\u4e00\u676f\u9152\uff0c\u6211\u662f\u5728\u656c\u4f60\u7684\uff01\u201d \u3000\u3000\u201c\u5feb\u8baf\u201d\u82b1\u7389\u7684\u4e00\u751f\uff0c\u662f\u5351\u8d31\u800c\u5e73\u51e1\u7684\uff1b\u4f46\u662f\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u5374\u6709\u4e00\u4ef6\u503c\u5f97\u81ea\u8c6a\u4e4b\u5904\u2014\u2014 \u4ed6\u82e5\u662f\u6b7b\u540e\u6709\u77e5\uff0c\u4e5f\u8be5\u4e3a\u6b64\u9a84\u50b2\uff0c\u56e0\u4e3a\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u51fa\u5356\u7684\u6d88\u606f\uff0c\u867d\u7136\u6709\u4e9b\u5e76\u4e0d\u91cd\u8981\uff0c\u4f46\u662f \u5374\u7edd\u65e0\u4e00\u4ef6\u865a\u5047\uff0c\u4ef6\u4ef6\u4ff1\u90fd\u771f\u5b9e\u5f97\u4e00\u5982\u522b\u4eba\u4ed8\u7ed9\u4ed6\u7684\u94f6\u5b50\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u806a\u660e\u7edd\u9876\u7684\u4eba\uff0c\u5426\u5219\u4ed6\u53c8\u600e\u4f1a\u9009\u62e9\u4e86\u8fd9\u6837\u5947\u7279\u7684\u804c\u4e1a\uff1f \u3000\u3000\uff08\u5343\u767e\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u4ece\u6765\u672a\u6709\u7684\u804c\u4e1a\uff09\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u867d\u806a\u660e\uff0c\u5374\u7edd\u672a\u60f3\u5230\uff0c\u4ed6\u81ea\u5df1\u4e34\u6b7b\u524d\u6240\u5212\u51fa\u7684\u56db\u5b57\uff0c\u7adf\u4f1a\u88ab\u6b66\u6797\u4e2d\u7684\u5927\u8c6a\u201c\u795e \u624b\u201d\u6218\u98de\u5982\u6b64\u770b\u91cd\uff0c\u800c\u4ec5\u662f\u7531\u4e8e\u4ed6\u751f\u524d\u804c\u4e1a\u7684\u4e60\u60ef\uff0c\u2014\u2014\u6cc4\u9732\u79d8\u5bc6\u7684\u4e60\u60ef\u800c\u5df2\u3002\u4e00\u79cd\u4e60\u60ef\u80fd \u5728\u4e34\u6b7b\u524d\u8fd8\u4e0d\u6539\u53d8\uff0c\u8fd9\u8bf4\u660e\u4e86\u4ed6\u5bf9\u804c\u4e1a\u7684\u5fe0\u8bda\uff0c\u662f\u4ee5\u4ed6\u6b7b\u540e\uff0c\u4fbf\u4e5f\u5f97\u5230\u4e86\u4ed6\u8fd9\u79cd\u5c0f\u4eba\u7269\u5e94\u5f97 \u7684\u5c0a\u91cd\u3002 \u3000\u3000\u201c\u53ea\u4f1a\u4e00\u62db\u201d\uff01 \u3000\u3000\u8fd9\u56db\u5b57\u4e5f\u662f\u771f\u5b9e\uff0c\u771f\u5b9e\u5f97\u4e5f\u4e00\u5982\u4ed6\u751f\u524d\u51fa\u552e\u7684\u6d88\u606f\uff0c\u4f46\u662f\u4ed6\u5374\u4e0d\u77e5\u9053\u88f4\u73cf\u600e\u4f1a\u5b66\u5230\u8fd9\u4e00 \u62db\u8db3\u4ee5\u9707\u60ca\u6b66\u6797\u7684\u7edd\u5b66\u7ecf\u8fc7\u3002 \u3000\u3000\u8981\u660e\u4e86\u8fd9\u4e9b\uff0c\u5404\u4f4d\u5fc5\u987b\u539f\u8c05\u6211\u5c06\u6545\u4e8b\u7a0d\u4e3a\u62c9\u540e\u4e00\u4e9b\uff0c\u6765\u63d0\u9192\u5404\u4f4d\u7684\u8bb0\u5fc6\uff0c\u201c\u6211\u662f\u90a3\u4e48\u8363 \u5e78\uff0c\u7adf\u80fd\u4f7f\u65f6\u5149\u5012\u6d41\uff0c\u867d\u7136\u4ec5\u662f\u5728\u4e66\u672c\u4e0a\uff0c\u5374\u5df2\u8db3\u591f\u4f7f\u6211\u611f\u5230\u5149\u5927\u3002\u201d \u3000\u3000\u8ff7\u8499\u7684\u6625\u591c\uff0c\u8ff7\u6f3e\u7684\u591c\u98ce\uff0c\u51c4\u6e05\u7684\u6708\u8272\uff0c\u51c4\u6e05\u7684\u5927\u5730\u2026\u2026 \u3000\u3000\u8fd9\u662f\u88f4\u73cf\u5728\u9047\u5230\u201c\u51b7\u8c37\u53cc\u6728\u201d\u3001\u6a80\u6587\u742a\u4ee5\u53ca\u201c\u91d1\u7ae5\u7389\u5973\u201d\u90a3\u5947\u5f02\u7684\u4e00\u591c\u7684\u7b2c\u4e8c\u5929\u665a\u4e0a\u3002 \u3000\u3000\u4e09\u66f4\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u56e0\u4e3a\u6a80\u6587\u742a\u7684\u5e7d\u6028\u4e0e\u76f8\u601d\uff0c\u6124\u6012\u5730\u6765\u5bfb\u88f4\u73cf\u3002 \u3000\u3000\u88f4\u73cf\u5374\u7d27\u8bb0\u7740\u201c\u91d1\u7ae5\u7389\u5973\u201d\u4e34\u53bb\u524d\u7684\u5141\u8bfa\uff0c\u800c\u53c8\u6084\u6084\u8d70\u51fa\u540e\u56ed\u5916\uff0c\u4ed6\u4eec\u7684\u76f8\u9047\uff0c\u81ea\u7136\u662f \u4e0d\u6109\u5feb\u7684\uff0c\u88f4\u73cf\u6115\u7136\u542c\u7740\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8d23\u9a82\u4ed6\u8d1f\u5fc3\uff0c\u5374\u4e0d\u80fd\u8ddf\u968f\u4ed6\u4eec\u4e00\u8d77\u53bb\u63a2\u89c6\u6a80\u6587\u742a\u7684 \u75c5\uff0c\u56e0\u4e3a\u4ed6\u4e0e\u201c\u91d1\u7ae5\u7389\u5973\u201d\u7684\u9080\u7ea6\u5728\u524d\uff1b\u4f46\u662f\u4ed6\u5b88\u7ea6\u7684\u5fb7\u6027\uff0c\u5374\u66f4\u6fc0\u8d77\u4e86\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u6124 \u6012\uff01 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u662f\u5b64\u50fb\u800c\u5014\u505a\u7684\uff0c\u4e0d\u80fd\u5fcd\u53d7\u4efb\u4f55\u4eba\u5bf9\u4ed6\u4eec\u7684\u8fdd\u6297\uff0c\u76db\u6012\u4e4b\u4e0b\uff0c\u4ed6\u4eec\u4fbf\u8981\u4ee5 \u6b66\u529b\u76f8\u5f3a\u3002 \u3000\u3000\u54ea\u77e5\uff0c\u4ed6\u4eec\u7684\u6b66\u529b\uff0c\u5374\u88ab\u53e6\u4e00\u79cd\u6b66\u529b\u963b\u6b62\u4e86\uff0c\u56e0\u4e3a\u201c\u91d1\u7ae5\u7389\u5973\u201d\u6bd4\u4ed6\u4eec\u66f4\u5f3a\uff0c\u4e8e\u662f\u4ed6\u4eec \u88ab\u201c\u91d1\u7ae5\u7389\u5973\u201d\u7981\u95ed\u5728\u4e00\u4e2a\u5e7d\u6e05\u7684\u5c71\u7a9f\u91cc\uff0c\u5c31\u5728\u90a3\u5c71\u7a9f\u91cc\uff0c\u201c\u91d1\u7ae5\u7389\u5973\uff0c\u5b9e\u884c\u4e86\u4ed6\u4eec\u5bf9\u201d\u51b7 \u6708\u4ed9\u5b50\u201c\u7684\u5141\u8bfa\uff0c\u4ea4\u7ed9\u88f4\u73cf\u4e00\u672c\u8584\u8584\u7684\u518c\u5b50\uff0c\u4e5f\u4f20\u6388\u7ed9\u88f4\u73cf\u4e03\u62db\u6b66\u529f\uff01\u53ea\u662f\u8fd9\u4e09\u79cd\u5947\u5965\u7684\u6b66 \u6797\u7edd\u5b66\uff0c\u5bf9\u4e8e\u4e00\u65e0\u6839\u57fa\u7684\u88f4\u73cf\uff0c\u6bd5\u7adf\u662f\u592a\u8270\u6df1\u4e86\u4e9b\uff0c\u662f\u4ee5\u4ed6\u5728\u201d\u76df\u4e3b\u5927\u4f1a\u201c\u4e4b\u524d\uff0c\u4ec5\u4ec5\u5b66\u4f1a \u4e86\u4e00\u62db\uff0c\u800c\u884c\u8ff9\u98d8\u5ffd\u7684\u201d\u91d1\u7ae5\u7389\u5973\u201c\uff0c\u4e5f\u56e0\u4e3a\u4e00\u4ef6\u91cd\u8981\u7684\u4e8b\uff0c\u8981\u79bb\u5f00\u6c5f\u5357\u4e86\u3002\u4ed6\u4eec\u867d\u7136\u59cb\u7ec8 \u6ca1\u6709\u6b63\u5f0f\u5c06\u88f4\u73cf\u6536\u4e3a\u5f92\u5f1f\uff0c\u4f46\u60c5\u611f\u4e30\u5bcc\u7684\u88f4\u73cf\uff0c\u5bf9\u8fd9\u592b\u5987\u5f02\u4eba\u7684\u60c5\u611f\uff0c\u5374\u751a\u4e8e\u4e00\u822c\u5f92\u5f1f\u5bf9\u5e08 \u5085\u7684\u611f\u6fc0\u4e0e\u5c0a\u91cd\u3002\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u88f4\u73cf\u5fcd\u4e0d\u4f4f\u95ee\u8d77\u201d\u51b7\u6708\u4ed9\u5b50\u201c\u7684\u884c\u8e2a\uff0c\u4f46\u201d\u51b7\u6708\u4ed9\u5b50\u201c\u7684\u884c \u8e2a\uff0c\u5374\u98d8\u6e3a\u5f97\u54cd\u5982\u5f53\u65f6\u7684\u6625\u96fe\u4e00\u6837\uff0c\u4fbf\u8fde\u201d\u91d1\u7ae5\u7389\u5973\u201c\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e8e\u662f\u88f4\u73cf\u53c8\u95ee\u8d77\u5979\u7684\u8eab\u4e16 \u4e0e\u6069\u6028\uff0c\u8fd9\u95ee\u9898\u5374\u4f7f\u5f97\u76f4\u7387\u800c\u5feb\u4e50\u7684\u3001\u7389\u5973\u201d\uff0c\u76ee\u5149\u4e2d\u4e5f\u9732\u51fa\u75db\u82e6\u7684\u795e\u8272\uff0c\u65e0\u6cd5\u5f00\u53e3\u3002 \u3000\u3000\u201c\u603b\u6709\u4e00\u5929\uff0c\u4f60\u4f1a\u77e5\u9053\u7684\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u8fd9\u4e24\u4f4d\u7537\u5973\u5f02\u4eba\uff0c\u4fbf\u6d88\u5931\u5728\u90a3\u4e73\u767d\u8272\u7684\u6668\u96fe\u91cc\uff0c\u53ea\u56fd\u4e0b\u7a74\u9053\u88ab\u70b9\u7684\u201c\u51b7 \u8c37\u53cc\u6728\u201d\u4e0e\u6ee1\u5fc3\u7591\u60d1\u7684\u88f4\u73cf\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u7a74\u9053\uff0c\u4e0d\u4e45\u4fbf\u4f1a\u81ea\u89e3\uff0c\u88f4\u73cf\u5fc3\u4e2d\u7684\u7591\u60d1\u4e0e\u601d\u5ff5\uff0c\u5374\u4e0d\u77e5\u4f55\u65f6\u624d\u80fd\u89e3\u5f00\uff0c \u4f46\u662f\u4e00\u79cd\u5bf9\u6b66\u529f\u7684\u72c2\u70ed\uff0c\u5374\u4f7f\u5f97\u4ed6\u5728\u56de\u53bb\u7684\u8def\u4e0a\u8fd8\u4e00\u76f4\u5728\u7ec3\u4e60\u7740\u65b9\u81ea\u5b66\u4f1a\u7684\u6b66\u529f\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u201c\u5feb\u8baf\u201d\u82b1\u4e3b\u4fbf\u4ee5\u81ea\u5df1\u7684\u6b7b\u4ea1\uff0c\u6362\u53d6\u4e86\u4ed6\u6700\u540e\u4e00\u4e2a\u503c\u5f97\u51fa\u552e\u7684\u6d88\u606f\u2014\u2014\u201c\u53ea\u4f1a\u4e00 \u62db\u201d\uff01 \u3000\u3000\u4f46\u8fd9\u4e00\u5207\u90fd\u662f\u79d8\u5bc6\u7684\uff0c\u6b64\u523b\u5728\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e2d\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u9664\u4e86\u201c\u795e\u624b\u201d\u6218\u98de\u5916\uff0c\u8c01\u4e5f \u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6b64\u523b\uff0c\u7eb7\u7eb7\u7684\u79c1\u8bed\u4e2d\uff0c\u8fd8\u6709\u4eba\u5728\u731c\u6d4b\u4ed6\u4eec\u76df\u4e3b\u201c\u88f4\u5927\u5148\u751f\u201d\u7684\u5e08\u627f\uff0c\u66f4\u6709\u4eba\u5728\u684c\u5e95\u4e0b\u6697 \u4e2d\u4ea4\u6362\u7740\u91d1\u94f6\uff0c\u4f5c\u4e3a\u4e00\u9879\u5947\u7279\u7684\u8d4c\u6ce8\uff0c\u6765\u535a\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e0e\u201c\u88f4\u5927\u5148\u751f\u201d\u4e4b\u95f4\u6bd4\u6597\u7684\u751f\u6b7b\u80dc \u8d1f\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u773c\u65c1\u89c2\uff0c\u4e0d\u7981\u6697\u4e2d\u51b7\u7b11\u3002 \u3000\u3000\u201c\u53ea\u4f1a\u4e00\u62db\u7684\u88f4\u73cf\uff0c\u53ea\u6015\u5728\u2018\u51b7\u8c37\u53cc\u6728\u2019\u4efb\u4f55\u4eba\u7684\u624b\u4e0b\uff0c\u90fd\u662f\u4e0d\u8fc7\u4e00\u62db\uff0c\u8981\u535a\u88f4\u73cf\u80dc \u7684\uff0c\u4e0d\u662f\u5446\u5b50\uff0c\u4fbf\u662f\u767d\u75f4\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u5fae\u8f6c\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u7a81\u5730\u634b\u987b\u5927\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u600e\u5730\u4e0d\u5feb\u4e9b\u996e\u9152\uff0c\u96be\u9053\u6697\u4e2d\u5728\u4e3a\u2018\u88f4\u5927 \u5148\u751f\u2019\u62c5\u5fc3\u4e48\uff1f\u9519\u4e86\uff0c\u9519\u4e86\uff0c\u9519\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u8fde\u547c\u4e09\u58f0\u201c\u9519\u4e86\u201d\uff0c\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u76f4\u5230\u4eca\u65e5\u4e3a\u6b62\uff0c\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u540d\uff0c \u6216\u8005\u4e0d\u5982\u51b7\u8c37\u53cc\u6728\u7684\u54cd\u4eae\uff0c\u4f46\u5404\u4f4d\u4e14\u770b\u88f4\u5927\u5148\u751f\u65b9\u624d\u6240\u663e\u9732\u7684\u6b66\u529f\uff0c\u54c8\u54c8\u2014\u2014\u4fbf\u662f\u533a\u533a\u5728 \u4e0b\uff0c\u4e5f\u6321\u4e0d\u4f4f\u8fd9\u4e48\u4e09\u62db\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u7565\u6697\u597d\u7b11\uff0c\u5f97\u610f\uff01 \u3000\u3000\u201c\u53ef\u60dc\u4ed6\u53ea\u4f1a\u4e00\u62db\uff0c\u82e5\u662f\u8fde\u53d1\u516d\u3001\u4e03\u62db\uff0c\u53ea\u6015\u6211\u771f\u7684\u65e0\u6cd5\u62b5\u6321\u4e86\uff01\u201d \u3000\u3000\u80f8\u819b\u4e00\u633a\uff0c\u5927\u7b11\u9053\uff1a\u201c\u82e5\u6709\u8c01\u4e0d\u4fe1\u88f4\u5927\u5148\u751f\u7684\u6b66\u529f\uff0c\u6211\u6218\u67d0\u613f\u610f\u548c\u4ed6\u535a\u4e0a\u4e00\u535a\u3002\u201d \u3000\u3000\u8bed\u58f0\u65b9\u4e86\uff0c\u4ed6\u8eab\u540e\u8083\u7acb\u7740\u4e00\u4e2a\u9ed1\u8863\u5927\u6cc5\uff0c\uff0c\u4fbf\u8f6c\u8eab\u5954\u4e86\u51fa\u53bb\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4fbf\u624b\u6367\u4e00\u76d8\u91d1 \u5149\u95ea\u95ea\u7684\u5143\u5b9d\uff0c\u98de\u6b65\u8d70\u56de\u6765\uff0c\u653e\u5230\u6218\u98de\u9762\u524d\uff0c\u7fa4\u8c6a\u773c\u89d2\u5077\u7aa5\uff0c\u53ea\u89c1\u76d8\u4e2d\u6c89\u6c89\u7538\u7538\uff0c\u4ff1\u662f\u5341\u4e24 \u4e00\u952d\u7684\u9ec4\u91d1\uff0c\u770b\u6765\u7adf\u4f3c\u6709\u4e8c\u3001\u4e09\u5341\u952d\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e3a\u4e4b\u8d6b\u7136\uff1b\u4f46\u4ed6\u7eb5\u7136\u660e\u77e5\u5fc5\u8d25\uff0c\u5374\u4e5f\u4e0d\u6562\u4e0e \u201c\u795e\u624b\u201d\u6218\u98de\u76f8\u535a\uff0c\u4f55\u51b5\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u4eec\u8fd8\u65e0\u4e00\u4eba\u731c\u5f97\u51fa\u201c\u88f4\u5927\u5148\u751f\u201d\u6b66\u529f\u7684\u6df1\u6d45\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u53c8\u81ea\u4e00\u626b\uff0c\u65e9\u5df2\u770b\u900f\u4e86\u8fd9\u73ed\u4eba\u7684\u5fc3\u5ff5\uff0c\u4ef0\u5929\u72c2\u7b11\u6570\u58f0\uff0c\u634b\u987b\u7b11\u9053\uff1a \u201c\u8be5\u6b7b\u8be5\u6b7b\uff0c\u5c0f\u4eba\u65e0\u77e5\uff0c\u7adf\u4ee5\u8fd9\u533a\u533a\u4e4b\u6570\uff0c\u6765\u6253\u6270\u5404\u4f4d\u7684\u9152\u5174\uff01\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u8f6c\u8eab\u53f1\u9053\uff1a\u201c\u8be5\u6b7b\u7684\u5974\u624d\uff0c\u8fd8\u4e0d\u518d\u53bb\u53d6\u4e00\u4e9b\u6765\uff0c\u4f5c\u4e3a\u5404\u4f4d\u82f1\u5ae9\u9152\u540e\u6d88\u9063\u7684\u8d22 \u7269\u3002\u201d \u3000\u3000\u8eab\u540e\u7684\u9ed1\u8863\u6c49\u5b50\u606d\u5e94\u4e00\uff0c\u58f0\uff0c\u53c8\u81ea\u5954\u51fa\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u3001\u4e1c\u65b9\u5144\u5f1f\u51b7\u773c\u65c1\u89c2\uff0c\u6a80\u6587\u742a\u3001 \u5434\u9e23\u4e16\u90fd\u662f\u76ee\u5149\u5446\u6ede\uff0c\u9762\u8272\u6728\u7136\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u53ea\u89c1\u56db\u4e2a\u9ed1\u8863\u5927\u6c49\u4e00\u8d77\u98de\u6b65\u5954\u6765\uff0c\u624b\u4e2d\u5404\u5404\u6367\u7740\u4e00\u76d8\u9ec4\u91d1\uff0c\u5728\u660e\u5982\u767d\u663c\u7684\u706f \u5149\u4e0b\u95ea\u8000\u7740\u70ab\u76ee\u7684\u5149\u8292\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u634b\u987b\u7b11\u9053\uff1a\u201c\u533a\u533a\u4e4b\u7269\uff0c\u4e0d\u8fc7\u804a\u535a\u5404\u4f4d\u4e00\u7b11\u800c\u5df2\uff01\u201d \u3000\u3000\u8bed\u58f0\u65b9\u4e86\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7a81\u5730\u8f7b\u54b3\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6e05\u6d0b\uff0c\u8fc7\u6765\uff01\u201d \u3000\u3000\u65c1\u8fb9\u4e00\u5e2d\u5750\u5728\u4e1c\u65b9\u5251\u3001\u4e1c\u65b9\u9707\u3001\u4e1c\u65b9\u6c5f\u3001\u4e1c\u65b9\u6e56\u5144\u5f1f\u56db\u4eba\u4e0b\u9996\u76f8\u966a\u7684\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05 \u6d0b\uff0c\u7acb\u523b\u4e00\u6b65\u8d76\u6765\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u7f13\u7f13\u9053\uff1a\u201c\u6e05\u6d0b\uff0c\u4f60\u8eab\u4e0a\u53ef\u66fe\u5e26\u5f97\u6709\u94f6\u5b50\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u7f13\u6162\u800c\u8f7b\u5fae\uff0c\u4f46\u5df2\u8db3\u4ee5\u4eca\u56db\u5ea7\u7fa4\u8c6a\u4ff1\u90fd\u4e3a\u4e4b\u4e00\u9707\u3002 \u3000\u3000\u6240\u6709\u7684\u9a9a\u52a8\uff0c\u60ca\u53f9\uff0c\u79c1\u8bae\u2026\u2026\u5239\u90a3\u4e4b\u95f4\uff0c\u7acb\u523b\u5e73\u606f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5446\u4e86\u4e00\u5446\uff0c\u54c8\u54c8\u5e72\u7b11\u7740\u9053\uff1a\u201c\u6a80\u8001\u9556\u5934\u96be\u9053\u4e5f\u6709\u8fd9\u822c\u96c5\u5174\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u6218\u5e84\u4e3b\u662f\u5426\u4fef\u5141\u5728\u4e0b\u8fd9\u5c40\u5916\u4eba\u53c2\u4e0e\u8fd9\u7cbe\u91c7\u7684\u6e38 \u620f\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5f3a\u7b11\u7740\u9053\uff1a\u201c\u81ea\u7136\uff0c\u81ea\u7136\u2026\u2026\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u201c\u9f99\u5f62\u516b\u638c\u201d\u7adf\u6765\u53c2\u4e0e\u6b64\u4e8b\uff0c\u53ea\u662f\u4ed6\u867d\u7136\u611f\u89c9\u60ca\u5f02\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u6697\u5fd6\u9053\uff1a \u201c\u7eb5\u7136\u8f93\u4e86\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u4e0d\u7981\u7559\u604b\u5730\u671b\u4e86\u684c\u4e0a\u7684\u4e94\u76d8\u9ec4\u91d1\u4e00\u773c\uff0c\u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u81ea\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u4ec5\u4f59\u7684 \u4e00\u53ea\u5de6\u624b\u4e0a\u63a5\u8fc7\u4e86\u4e00\u53e0\u94f6\u7968\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u9664\u610f\u62bd\u4e86\u4e24\u5f20\uff0c\u53c8\u671b\u4e86\u671b\u684c\u4e0a\u7684\u9ec4\u91d1\uff0c\u542b\u7b11\u8bf4\u9053\uff1a \u201c\u5982\u4eca\u91d1\u94f6\u4e4b\u6bd4\u5352\uff0c\u53ef\u662f\u4ee5\u4e94\u6613\u4e00\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5fae\u5fae\u4e00\u7b11\uff0c\u9f9a\u6e05\u6d0b\u5374\u5df2\u8eac\u8eab\u9053\uff1a\u201c\u6218\u5e84\u4e3b\u9762\u524d\u7684\u9ec4\u91d1\uff0c\u5927\u7ea6\u662f\u6bcf\u76d8\u4e24 \u767e\u56db\u5341\u4e24\uff0c\u6298\u5408\u767d\u94f6\u516d\u5343\u4e24\u6574\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u7565\u7565\u4e8e\u7b11\u7740\u9053\uff1a\u201c\u9f9a\u9556\u5934\u597d\u5389\u5bb3\u7684\u773c\u529b\uff0c\u597d\u7cbe\u660e\u7684\u7b97\u76d8\uff0c\u7eb5\u7136\u4e0d\u4f5c\u5200\u67aa\u4e0a \u7684\u4e70\u5356\u2026\u2026\u563f\u563f\uff0c\u563f\u563f\uff0c\u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u5927\u638c\u67dc\uff0c\u53ea\u6015\u975e\u9f9a\u5144\u83ab\u5c5e\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8ba5\u5632\u5730\u671b\u4e86\u9f9a\u6e05\u6d0b\u7684\u65ad\u624b\u4e00\u773c\uff0c\u4e0d\u4f4f\u634b\u987b\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u9762\u8272\u5fae\u53d8\uff0c\u5782\u624b\u9000\u56de\u5e2d\u4e0a\uff0c\u4ece\u6b64\u4e0e\u201c\u795e\u624b\u201d\u6218\u98de\u7ed3\u4e0b\u6df1\u4ec7\uff0c\u201c\u9f99\u5f62 \u516b\u638c\u201d\u6a80\u660e\u5374\u5fae\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6218\u5144\u9ad8\u89c1\uff0c\u786e\u662f\u4e0d\u51e1\uff0c\u8eab\u624b\u6b8b\u5e9f\u7684\u4eba\uff0c\u603b\u8981\u6bd4\u5934\u8111\u75f4\u5446\u7684\u4eba\u597d \u5f97\u591a\uff0c\u6e05\u6d0b\uff0c\u4f60\u6b63\u8be5\u8c22\u8c22\u6218\u5e84\u4e3b\u7684\u5938\u5956\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5927\u7b11\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\u2026\u2026\u201d\u8fd8\u5f85\u53cd\u51fb\u4e24\u53e5\uff0c\u5374\u504f\u504f\u4e00\u201c\u65f6\u95f4\u60f3\u4e0d\u51fa\u8bdd \u6765\u3002\u201d\u9f99\u5f62\u516b\u638c\u201c\u6a80\u660e\u542b\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u2018\u6c47\u4e30\u2019\u7684\u94f6\u7968\uff0c\u5171\u662f\u516d\u5343\u4e94\u767e\u4e24\uff0c\u8bf7\u6218\u5e84\u4e3b\u8fc7\u76ee \u8fc7\u76ee\u3002\u201d\u968f\u610f\u5c06\u4e24\u5f20\u94f6\u7968\uff0c\u653e\u5230\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u524d\u3002\u56db\u4e0b\u6b64\u523b\u65e9\u5df2\u9e26\u96c0\u65e0\u58f0\uff0c\u53ea\u6709\u5385\u5916\u7684\u5fae \u98ce\u5439\u7740\u5d2d\u65b0\u7684\u94f6\u7968\uff0c\u6c99\u6c99\u4f5c\u54cd\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u7b11\u9053\uff1a\u201c\u4e0d\u4f1a\u9519\u7684\uff0c\u4e0d\u4f1a\u9519\u7684\uff01\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u53ea\u6015\u4e0d\u4f1a\u518d\u6709\u522b\u4eba\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4f38\u624b\u5c06\u4e24\u5f20\u94f6\u7968\u538b\u5728\u4e00\u76d8\u9ec4\u91d1\u4e4b\u4e0b\uff0c\u751f\u50cf\u662f\u5bf9\u6b64\u6b21\u8d4c\u535a\uff0c\u6781\u6709\u81f4\u80dc\u7684\u628a\u63e1\u3002\u53c8\u81ea\u6717\u58f0 \u9053\uff1a\u201c\u9664\u4e86\u6a80\u8001\u9556\u5934\u6709\u6b64\u96c5\u5174\u5916\uff0c\u8fd8\u6709\u54ea\u4f4d\u5144\u5f1f\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u4e1c\u65b9\u94c1\u7a81\u5730\u542b\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u6b63\u89c9\u624b\u75d2\u7684\u5f88\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u53c8\u81ea\u4e00\u6123\uff0c\u4e8e\u7b11\u9053\uff1a\u201c\u4e1c\u65b9\u5927\u4fa0\u2026\u2026\u54c8\u54c8\uff0c\u597d\u6781\u4e86\uff0c\u597d\u6781\uff01\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u7b11\u9053\uff1a\u201c\u4f46\u5c0f\u5f1f\u8eab\u8fb9\u672a\u5e26\u91d1\u94f6\uff0c\u53ea\u662f\u4ee5\u533a\u533a\u4e4b\u7269\uff0c\u804a\u4ee5\u52a9\u5174\u7f62\u4e86\u3002\u201d \u3000\u3000\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u81ea\u8170\u95f4\u7684\u4e1d\u7ee6\u4e0a\uff0c\u53d6\u4e0b\u4e00\u65b9\u5f62\u5f0f\u53e4\u62d9\uff0c\u989c\u8272\u82cd\u7fe0\u7684\u53e4\u7389\uff0c\u201c\u795e\u624b\u201d\u6218\u98de \u81ea\u662f\u8bc6\u8d27\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u6b64\u4e43\u65e0\u4ef7\u4e4b\u5b9d\uff0c\u5c0f\u5f1f\u600e\u6562\u5984\u4f5c\u8bc4\u4ef7\u3002\u201d\u53cc\u624b\u4e00\uff0c\u62cd\uff0c \u8f6c\u8eab\u4f4e\u4f4e\u5631\u5490\u4e86\u51e0\u53e5\uff0c\u54ea\u77e5\u4ed6\u8eab\u540e\u7684\u9ed1\u8863\u5927\u6c49\u65b9\u5f85\u4e3e\u6b65\uff0c\u65b9\u624d\u8c26\u865a\u5730\u5750\u5230\u53e6\u4e00\u684c\u4e0a\u53bb\u7684\u4e1c\u65b9 \u56db\u5144\u5f1f\uff0c\u5df2\u4e00\u8d77\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u5144\u5f1f\u4e94\u4e2a\uff0c\u751f\u957f\u4fa0\u4e49\u4e4b\u5bb6\uff0c\u65e2\u4e0e\u201c\u9f99\u5f62\u516b\u638c\u201d\u540c\u6765\uff0c\u4fbf\u89c9\u5f97\u81ea\u5df1\u5144\u5f1f\u4e94\u4eba\uff0c\u4ff1\u8be5\u7ad9\u5728 \u201c\u9f99\u5f62\u516b\u638c\u201d\u4e00\u8fb9\uff0c\u6b64\u523b\u89c1\u5230\u6a80\u660e\u7684\u4e3e\u52a8\uff0c\u8c01\u90fd\u4e0d\u77e5\u9053\u6b64\u4e3e\u7684\u6df1\u610f\uff0c\u4f46\u5374\u4e0d\u7ea6\u800c\u540c\u5730\u4e3a\u4e4b\u81c2 \u52a9\uff0c\u4ff1\u90fd\u53d6\u4e86\u4e00\u7269\uff0c\u6765\u4e0e\u6218\u98de\u76f8\u535a\uff0c\u4ed6\u5144\u5f1f\u51fa\u8eab\u4e16\u5bb6\uff0c\u867d\u662f\u5c0f\u5c0f\u513f\u4ef6\u73a9\u7269\uff0c\u4ef7\u503c\u5374\u5df2\u8d85\u8fc7\u684c \u4e0a\u6240\u6709\u7684\u91d1\u94f6\u6570\u500d\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u4e0a\u867d\u4ecd\u5e26\u7740\u7b11\u5bb9\uff0c\u4f46\u7b11\u58f0\u5df2\u66f4\u52c9\u5f3a\uff0c\u5fc3\u5934\u4e5f\u6e10\u6e10\u7740\u6025\uff0c\u8fd9\u672c\u662f\u73a9\u7b11\u4e4b \u4e3e\uff0c\u6b64\u523b\u7adf\u53d8\u5f97\u6e10\u6e10\u4e25\u91cd\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5f3a\u7b11\u7740\u77a5\u4e86\u684c\u4e0a\u7684\u4e94\u4ef6\u73cd\u5b9d\u4e00\u773c\uff0c\u4ea6\u81ea\u5df1\u8d70\u4eba\u5185\u5ba4\uff0c\u6258\u51fa\u4e00\u76d8\u91d1\u73e0\uff0c\u6ee1\u5385\u7fa4\u8c6a\uff0c\u9759\u5bc2 \u5982\u6b7b\uff0c\u773c\u770b\u7740\u4ed6\u7684\u811a\u6b65\u7f13\u7f13\u8d70\u4eba\uff0c\u53c8\u7f13\u7f13\u8d70\u51fa\u2026\u2026 \u3000\u3000\u7a81\u5730\u4e00\u9635\u5927\u7b11\uff0c\u5212\u7834\u9759\u5bc2\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u7ade\u4e5f\u54af\u54af\u5927\u7b11\u8d77\u6765\uff0c\u51fb\u6848\u5927\u7b11\u9053\uff1a\u201c\u6709\u8da3\u6709 \u8da3\uff0c\u6709\u8da3\u5df2\u6781\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5411\u5144\u96be\u9053\u4e5f\u6709\u6b64\u96c5\u5174\u4e48\uff1f\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u5927\u7b11\u7740\u9053\uff1a\u201c\u5982\u6b64\u70ed\u95f9\u7684\u8d4c\u535a\uff0c\u6211\u5411\u67d0\u4eba\u82e5\u4e0d\u51d1\u4e0a\u4e00\u89d2\uff0c\u5c82\u975e\u8981\u7ec8\u751f\u9057 \u61be\uff01\u201d \u3000\u3000\u5fae\u4e00\u6325\u624b\uff0c\u90a3\u8fb9\u4fbf\u8d70\u6765\u4e5d\u6761\u8eab\u7a7f\u4e03\u5f69\u9526\u8863\u7684\u5f6a\u5f62\u5927\u6c49\uff0c\u8083\u7acb\u5728\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u8eab\u524d\u3002 \u3000\u3000\u8fd9\u4e5d\u4eba\u4e00\u4e2a\u4e2a\u8eab\u8eaf\u5f6a\u58ee\uff0c\u9762\u5bb9\u6c89\u6bc5\uff0c\u76ee\u5149\u5149\u8292\u70af\u70af\uff0c\u884c\u52a8\u95f4\u66f4\u662f\u5341\u5206\u527d\u608d\u77eb\u5065\uff0c\u867d\u7136\u79f0 \u4e0d\u4e0a\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u4f46\u663e\u7136\u8eab\u624b\u4ff1\u90fd\u4e0d\u5f31\u3002\u4e5d\u4eba\u4e00\u8d77\u5411\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u606d\u8eab\u4e00\u793c\uff0c\u795e\u60c5\u4ff1\u90e8\u6781 \u4e3a\u606d\u8c28\uff0c\u4f46\u5bf9\u522b\u4eba\u795e\u6001\u95f4\u5374\u663e\u5f97\u5341\u5206\u50b2\u6162\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5411\u67d0\u4eba\u4e00\u751f\u53e4\u602a\u5b64\u50fb\uff0c\u4eca\u65e5\u4e5f\u8981\u4e0e\u6218\u5e84\u4e3b\u8d4c\u4e00\u4e2a\u5947\u5f02 \u7684\u8d4c\u6ce8\u3002\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\uff0c\u7b11\u58f0\u4ea6\u987f\uff0c\u7a81\u5730\u8f6c\u8eab\u9762\u5411\u8fd9\u4e38\u6761\u9526\u8863\u5927\u6c49\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u4e5d\u4eba\u8eab\u4f53\u6027\u547d\u4ece\u4f55 \u800c\u6765\uff1f\u201d \u3000\u3000\u4e5d\u6761\u9526\u8863\u5927\u6c49\u9f50\u58f0\u559d\u9053\uff1a\u201c\u8eab\u5c5e\u91d1\u9e21\uff0c\u547d\u5c5e\u91d1\u9e21\uff0c\u91d1\u9e21\u6709\u4ee4\uff0c\u767e\u6b7b\u65e0\u61be\uff01\u201d \u3000\u3000\u4ed6\u4e38\u4eba\u4e00\u8d77\u5f20\u53e3\uff0c\u4e00\u8d77\u95ed\u53e3\uff0c\u558a\u58f0\u5639\u4eae\uff0c\u5f53\u771f\u662f\u58f0\u9707\u5c4b\u74e6\uff0c\u9762\u524d\u4e00\u684c\u7684\u676f\u76d8\u7897\u76cf\uff0c\u4f3c\u4e4e \u5df2\u90fd\u6ce2\u9707\u5f97\u53ee\u5f53\u4f5c\u54cd\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u54c8\u54c8\u4e00\u7b11\uff0c\u8f6c\u8fc7\u8eab\u6765\u6717\u58f0\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u6211\u5411\u4e00\u557c\u8981\u4e0e\u6218\u5e84\u4e3b\u4f60\u4e00\u8d4c\u7684\uff0c \u4fbf\u662f\u8fd9\u4e5d\u4eba\u7684\u8eab\u4f53\u6027\u547d\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u5934\u4e00\u9707\uff0c\u7fa4\u8c6a\u66f4\u662f\u609a\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u53ea\u542c\u5411\u4e00\u557c\u60a0\u7136\u63a5\u53e3\u9053\uff1a\u201c\u6218\u5e84\u4e3b\u4ec1\u4e49\u5f85\u4eba\uff0c\u4e00\u4ee3\u4e4b\u96c4\uff0c\u624b\u4e0b\u60f3\u5fc5\u591a\u7684\u662f\u80fd\u4e3a\u6218\u5e84\u4e3b\u5356 \u547d\u7684\u5144\u5f1f\uff0c\u53ea\u8981\u968f\u610f\u9009\u51fa\u4e5d\u4eba\u6765\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u6ee1\u5802\u4eba\u58f0\uff0c\u53c8\u590d\u5bc2\u7136\uff0c\u6570\u767e\u9053\u773c\u795e\uff0c\u4ff1\u90fd\u7d27\u5f20\u800c\u671f\u5f85\u5730\u671b\u5728\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u4e0a\uff0c\u4e0d\u77e5\u4ed6 \u8be5\u5982\u4f55\u5e94\u7b54\u8fd9\u5947\u5f02\u7684\u6311\u6218\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u540c\u5149\u4e00\u8f6c\uff0c\u4f9d\u6b21\u5f80\u8fd9\u4e5d\u6761\u9526\u8863\u5927\u6c49\u9762\u4e0a\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e5d\u4eba\u9762\u5bb9\u4ecd\u662f\u90a3\u822c\u6c89 \u6bc5\uff0c\u76ee\u5149\u4ecd\u662f\u90a3\u822c\u575a\u5b9a\uff0c\u7ade\u65e0\u4e00\u4eba\u6709\u4e1d\u6beb\u60ca\u614c\u6050\u60e7\u4e4b\u8272\u3002\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6d53\u7709\u5fae\u76b1\uff0c\u7a81\u5730 \u957f\u8eab\u800c\u8d77\uff0c\u7f13\u7f13\u8d70\u5230\u8fd9\u4e5d\u4eba\u8eab\u524d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4eba\u547d\u592b\u5929\uff0c\u7ec8\u975e\u513f\u620f\uff0c\u4f60\u4e5d\u4eba\u53ef\u662f\u5f53\u771f\u5fc3\u7518\u60c5 \u613f\uff1a\u201c\u4e5d\u6761\u9526\u8863\u5927\u6c49\uff0c\u5341\u516b\u9053\u76ee\u5149\u671b\u4e5f\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u751f\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\u4f3c\u7684\uff0c\u53c8\u50cf \u662f\u4ee5\u6c89\u9ed8\u6765\u8ba5\u5632\u6a80\u660e\u7684\u591a\u4e8b\uff01\u201d\u91d1\u9e21\u201c\u5411\u4e00\u557c\u9762\u8272\u4e00\u6c89\uff0c\u5389\u53f1\u9053\uff1a\u201c\u6a80\u603b\u9556\u5934\u7684\u8bdd\uff0c\u4f60\u4eec\u96be \u9053\u6ca1\u6709\u542c\u5230\u4e48\uff1f\u201d\u94c1\u62d0\u4e94\u70b9\uff0c\u8eab\u5f62\u5982\u98de\u63a0\u8d77\uff0c\u53ea\u542c\u4e00\u9635\u6e05\u8106\u7684\u201c\u5288\u62cd\u201d\u4e4b\u58f0\uff0c\u63a5\u8fde\u54cd\u8d77\uff0c\u4f46 \u5355\u638c\u52a8\u5904\uff0c\u7adf\u5728\u8fd9\u4e00\u6392\u4e38\u4eba\u9762\u4e0a\uff0c\u5404\u5404\u6253\u4e86\u5341\u516b\u8bb0\u6b63\u53cd\u8033\u5149\uff01 \u3000\u3000\u7fa4\u8c6a\u8f7b\u547c\u4e00\u58f0\uff0c\u54ea\u77e5\u8fd9\u4e38\u4eba\u5404\u5404\u6328\u4e86\u4e24\u8bb0\u8033\u5149\uff0c\u4e0d\u4f46\u4ecd\u81ea\u884c\u6240\u65e0\u4e8b\uff0c\u795e\u8272\u4e0d\u53d8\uff0c\u800c\u4e14\u7acb \u523b\u9f50\u5730\u8eac\u8eab\u5e94\u9053\uff1a\u201c\u542c\u5230\u4e86\uff01\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\xb7\u557c\u5389\u53f1\u9053\uff1a\u201c\u542c\u5230\u4e86\u600e\u4e0d\u56de\u7b54\u6a80\u8001\u9556\u5934\u7684\u8bdd\uff1f\u201d \u3000\u3000\u4e38\u6761\u5927\u6c49\u4e00\u8d77\u4fa7\u8fc7\u8eab\u6765\uff0c\u5411\u6a80\u660e\u8eac\u8eab\u4e00\u793c\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u541b\u8d50\u81e3\u6b7b\uff0c\u81e3\u4e0d\u6562\u4e0d\u6b7b\uff0c\u7236\u4ee4\u5b50 \u4ea1\uff0c\u5b50\u4e0d\u6562\u4e0d\u4ea1\u3002\u5411\u5927\u54e5\u4e8e\u6211\u7b49\u6069\u60c5\u6709\u5982\u541b\u7236\uff0c\u662f\u4ee5\u6211\u4e5d\u4eba\u5b9e\u662f\u5fc3\u7518\u60c5\u613f\uff0c\u4e07\u6b7b\u4e0d\u8f9e\uff01\u201d \u3000\u3000\u8fd9\u4e5d\u4eba\u6ed4\u6ed4\u8bf4\u6765\uff0c\u4ecd\u662f\u4e00\u8d77\u5f20\u53e3\uff0c\u4e00\u8d77\u95ed\u53e3\uff0c\u663e\u89c1\u662f\u65e9\u5df2\u8bad\u7ec3\u6709\u7d20\u3002\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fae\u5fae \u4e00\u7b11\uff0c\u62b1\u62f3\u5411\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u9053\uff1a\u201c\u5411\u5e2e\u4e3b\u8bf7\u6055\u5728\u4e0b\u591a\u4e8b\uff01\u201d \u3000\u3000\u5fae\u4e00\u637b\u987b\uff0c\u7f13\u6b65\u8d70\u56de\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u53f9\uff0c\u5fd6\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6b64\u4eba\u770b\u6765\u504f\u6fc0\u53e4\u602a\uff0c\u5374\u80fd\u5f97\u8fd9 \u822c\u4eba\u7684\u6b7b\u529b\uff01\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u795e\u60c5\u4e4b\u95f4\uff0c\u66f4\u662f\u5f97\u610f\uff0c\u76ee\u5149\u51dd\u6ce8\u7740\u6c89\u541f\u672a\u7edd\u7684\u201c\u795e\u624b\u201d\u6218\u98de\uff0c\u6717\u7b11\u9053\uff1a \u201c\u6218\u5e84\u4e3b\u662f\u5426\u5728\u5acc\u6211\u2018\u9e21\u5c3e\u4e5d\u5144\u5f1f\u2019\u592a\u8fc7\u611a\u9c81\u5446\u7b28\uff0c\u662f\u4ee5\u89c9\u5f97\u8fd9\u4e38\u6761\u8d31\u547d\uff0c\u4e0d\u503c\u4e0e\u6218\u5e84\u4e3b\u624b \u4e0b\u7684\u6d4e\u6d4e\u957f\u624d\u76f8\u63d0\u5e76\u8bba\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5f3a\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5411\u5e2e\u4e3b\u8a00\u91cd\u4e86\uff0c\u4f46\u2026\u2026\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e0d\u7b49\u6218\u98de\u8bf4\u5b8c\uff0c\u5df2\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u5982\u662f\u8fd9\u822c\uff0c\u90a3\u4e48\u5728\u4e0b\u53ea\u6709\u4ee4\u6211\u8fd9\uff0c\u9e21\u5c3e \u4e5d\u5144\u5f1f\u2018\u5728\u6218\u5e84\u4e3b\u9762\u524d\u732e\u4e00\u732e\u4e11\u4e86\uff01\u201d\u970d\u7136\u8f6c\u8fc7\u8eab\u53bb\uff0c\u6325\u624b\u5927\u559d\u9053\uff1a\u201c\u53bb\uff01\u201d \u3000\u3000\u4e5d\u6761\u9526\u8863\u5927\u6c49\u8eac\u8eab\u4e00\u8bfa\uff0c\u5239\u90a3\u95f4\u4f46\u89c1\u9526\u8863\u95ea\u52a8\uff0c\u6ee1\u9662\u98de\u8dc3\uff0c\u8eab\u5f62\u6709\u5982\u7a7f\u82b1\u4e4b\u8774\u8776\uff0c\u7fa4\u8c6a \u65b9\u81ea\u4ee5\u4e3a\u8fd9\u4e38\u4eba\u662f\u5728\u5356\u5f04\u8eab\u6cd5\u4e4b\u5a07\u5065\uff0c\u5374\u542c\u4e00\u58f0\u8f7b\u53f1\uff0c\u4e5d\u4eba\u5df2\u4e00\u8d77\u56de\u5230\u5385\u524d\uff0c\u53ea\u662f\u4e3a\u9996\u4e00 \u4eba\uff0c\u638c\u4e2d\u591a\u4e86\u4e00\u6839\u9152\u76cf\u7c97\u7ec6\uff0c\u4e00\u4e08\u957f\u77ed\u7684\u94c1\u68cd\u3002 \u3000\u3000\u4eba\u5f71\u53c8\u81ea\u4e00\u5206\uff0c\u4e5d\u6761\u5927\u6c49\uff0c\u5df2\u81ea\u5404\u5404\u5206\u6301\u4e86\u8fd9\u94c1\u68cd\u7684\u4e24\u7aef\uff0c\u56db\u4eba\u5728\u5de6\uff0c\u56db\u4eba\u5728\u53f3\uff0c\u5f53\u4e2d \u4e00\u4e2a\u518d\u6b21\u8f7b\u53f1\uff0c\u5de6\u9762\u56db\u4eba\u8eab\u5f62\u5de6\u4fa7\uff0c\u53f3\u9762\u56db\u4eba\u8eab\u5f62\u53f3\u503e\uff0c\u5f53\u4e2d\u7684\u94c1\u68cd\uff0c\u5374\u6e10\u7ec6\u6e10\u957f\uff0c\u5b9b\u5982\u9ea6 \u6761\u4e00\u822c\uff0c\u88ab\u8fd9\u516b\u4eba\u7684\u60ca\u4eba\u795e\u529b\uff0c\u62c9\u4e86\u5f00\u6765\u3002 \u3000\u3000\u7acb\u5728\u4e2d\u592e\u4e4b\u4eba\uff0c\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5f00\uff01\u201d\u7acb\u638c\u4e00\u5207\uff0c\u5c06\u90a3\u5df2\u53d8\u5f97\u7af9\u7b77\u822c\u7c97\u7684\u94c1\u68cd\uff0c\u4e00\u638c \u5207\u65ad\uff01 \u3000\u3000\u4e00\u9635\u70ed\u70c8\u7684\u559d\u91c7\u4e4b\u58f0\uff0c\u7acb\u65f6\u6709\u5982\u8f70\u96f7\u822c\u54cd\u8d77\uff0c\u4e5d\u6761\u9526\u8863\u5927\u6c49\u5fae\u4e00\u8eac\u8eab\uff0c\u9c7c\u8d2f\u8d70\u56de\u5411\u4e00\u557c \u8eab\u524d\uff0c\u9762\u8272\u4ecd\u662f\u90a3\u822c\u575a\u6bc5\u800c\u9547\u5b9a\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u5934\u5fae\u51db\uff0c\u8fd9\u4e38\u4eba\u7684\u6b66\u529f\uff0c\u867d\u662f\u5916\u95e8\u5de6\u9053\uff0c\u65e0\u6cd5\u4e0e\u5185\u5bb6\u9ad8\u624b\u76f8\u63d0\u5e76\u8bba\uff0c\u4f46 \u73af\u987e\u81ea\u5df1\u624b\u4e0b\uff0c\u80fd\u6709\u8fd9\u822c\u6b66\u529f\u4e4b\u4eba\uff0c\u5374\u5df2\u5be5\u5be5\u53ef\u6570\u3002 \u3000\u3000\u4ed6\u867d\u7136\u81ea\u5927\uff0c\u5374\u4ecd\u672a\u5927\u5f97\u5931\u53bb\u7406\u667a\uff0c\u5f53\u7136\u4e0d\u613f\u5c06\u81ea\u5df1\u4e38\u4e2a\u5f97\u529b\u7684\u90e8\u4e0b\uff0c\u846c\u9001\u5728\u4e00\u6b21\u6beb\u65e0 \u5f97\u80dc\u5e0c\u671b\u7684\u8d4c\u6ce8\u4e0a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u867d\u7136\u7406\u667a\uff0c\u5374\u53c8\u592a\u8fc7\u987e\u5fcc\u81ea\u5df1\u7684\u5730\u4f4d\u4e0e\u5c0a\u4e25\uff0c\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u4ed6\u5b9e\u5728\u65e0\u6cd5\u5fcd\u53d7 \u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u8fd9\u534a\u5e26\u72c2\u505a\uff0c\u534a\u5e26\u8ba5\u5632\u7684\u6311\u6218\uff0c\u77db\u76fe\u4e4b\u95f4\uff0c\u7a81\u542c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5fae\u5fae\u4e00\u7b11 \u8f7b\u8f7b\u9053\uff1a\u201c\u6218\u5e84\u4e3b\u5982\u5df2\u7a33\u64cd\u80dc\u7b97\uff0c\u8fd9\u8d4c\u6ce8\u7eb5\u4e0d\u5408\u7406\uff0c\u63a5\u53d7\u4e86\u5b83\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u867d\u8f7b\uff0c\u5374\u5df2\u8db3\u591f\u8ba9\u8bb8\u591a\u4eba\u90fd\u80fd\u542c\u5230\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u4e00\u7b11\uff1a\u201c\u6b63\u662f\uff0c\u6b63 \u662f\u2026\u2026\u201d\u53cc\u638c\u4e00\u62cd\uff0c\u56de\u9996\u9053\uff1a\u201c\u4e8e\u5e73\uff0c\u4f60\u4e14\u66ff\u6211\u51fa\u53bb\u770b\u770b\uff0c\u6709\u54ea\u51e0\u4f4d\u5144\u5f1f\u613f\u610f\u524d\u6765\uff1f\u201d \u3000\u3000\u59cb\u7ec8\u7ad9\u5728\u4ed6\u8eab\u540e\u7684\u9ed1\u8863\u5927\u6c49\u4e8e\u5e73\u66b4\u5e94\u4e00\u58f0\uff0c\u9762\u4e0a\u5374\u4e5f\u5fae\u5fae\u53d8\u4e86\u989c\u8272\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u8f6c\u8eab\u5954 \u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u5411\u67d0\u4eba\u5e73\u751f\u55dc\u8d4c\uff0c\u4f46\u76f4\u5230\u4eca\u65e5\uff0c\u624d\u7b97\u9047\u7740\u4e86\u5bf9\u624b\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u8fde\u75db\u996e\u4e86\u4e09\u676f\u70c8\u9152\uff0c\u6708\u5149\u53c8\u6e10\u6e10\u6062\u590d\u9547\u5b9a\uff0c\u6b64\u523b\u5927\u5385\u4e0a\u9152\u7b75\u867d\u4ecd\u6446\u5f97\u6574 \u6574\u9f50\u9f50\uff0c\u4f46\u770b\u6ee1\u5385\u7fa4\u8c6a\uff0c\u5374\u518d\u65e0\u4e00\u4eba\u80fd\u5b89\u7a33\u5730\u5750\u5728\u5ea7\u4e0a\uff0c\u6b64\u523b\u4ed6\u5012\u5fc3\u4e2d\u867d\u8fd8\u4e0d\u77e5\u4eca\u65e5\u5230\u5e95\u8c01 \u80dc\u8c01\u8d1f\u3001\u4f46\u5374\u5df2\u4e0d\u7981\u6697\u4e2d\u4e3a\u201c\u795e\u624b\u201d\u6218\u98de\u7d27\u5f20\u4e86\u8d77\u6765\uff0c\u6709\u7684\u5728\u5fc3\u4e2d\u6697\u6697\u601d\u5fd6\uff1a\u201c\u88f4\u5927\u5148\u751f\u6b66 \u529f\u5fc5\u975e\u540c\u5c0f\u53ef\uff0c\u5426\u5219\u8fd9\u6218\u795e\u624b\u4e00\u5411\u806a\u660e\uff0c\u600e\u4f1a\u5728\u4ed6\u8eab\u4e0a\u4e0b\u4e86\u8fd9\u4e48\u5927\u7684\u8d4c\u6ce8\uff1f\u201d \u3000\u3000\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u4f3c\u4e4e\u81ea\u5df1\u4e5f\u53c2\u4e0e\u4e86\u8fd9\u5947\u5f02\u7684\u8c6a\u8d4c\u4e4b\u4e2d\uff0c\u53ea\u89c9\u5fc3\u623f\u8df3\u52a8\u52a0\u5267\uff0c\u8840\u6db2\u51b2\u5411\u9762 \u988a\uff0c\u76ee\u5149\u4e0d\u7531\u81ea\u4e3b\u5730\u9f50\u90fd\u671b\u7740\u5385\u95e8\u3002\u4e0d\u77e5\u9053\u518d\u8fc7\u4e00\u6bb5\u65f6\u5019\uff0c\u201c\u88f4\u5927\u5148\u751f\u201d\u662f\u5426\u80fd\u518d\u5165\u6b64\u5385\u3002 \u3000\u3000\u8fd9\u5176\u4e2d\u53ea\u6709\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u76ee\u5149\u7edd\u672a\u5411\u5385\u95e8\u671b\u4e0a\u4e00\u773c\uff0c\u56e0\u4e3a\u4ed6\u6df1\u77e5\u53ea\u8981\u671f\u5f85\u88f4\u73cf\u751f\u5165\u6b64 \u95e8\uff0c\u8fd8\u4e0d\u5982\u671f\u671b\u4e00\u6761\u9cb8\u9c7c\u9a91\u5728\u9a6c\u8eab\u4e0a\u5954\u8fdb\u6765\uff0c\u56e0\u4e3a\u540e\u8005\u867d\u7136\u8352\u8c2c\uff0c\u8fd8\u8fdc\u6bd4\u524d\u8005\u6709\u5e0c\u671b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u7d27\u6263\u5fc3\u5f26\u7684\u6c89\u9ed8\u4e4b\u4e2d\uff0c\u591c\u8272\u4f3c\u4e4e\u6765\u5f97\u5947\u5feb\uff0c\u5385\u4e2d\u5df2\u71c3\u8d77\u706f\u706b\u3002 \u3000\u3000\u4f46\u5374\u89c1\u90a3\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\uff0c\u4ed6\u4e00\u811a\u8de8\u4eba\u5927\u5385\uff0c\u4fbf\u6717\u58f0\u7b11\u9053\uff1a\u201c\u597d\u9669\u597d\u9669\uff0c\u5144\u5f1f\u6211\u9669 \u4e9b\u9519\u8fc7\u4e86\u4e00\u573a\u597d\u620f\uff01\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u957f\u8eab\u800c\u8d77\uff0c\u5927\u7b11\u9053\uff1a\u201c\u6b63\u662f\u6b63\u662f\uff0c\u4eca\u65e5\u6218\u5e84\u4e3b\u8c6a\u5174\u9038\u98de\uff0c\u90a3\u5144\u4f60\u82e5\u4e0d\u4e0e \u4ed6\u8d4c\u4e0a\u4e00\u8d4c\uff0c\u4ee5\u540e\u4f60\u518d\u4e5f\u4f11\u60f3\u9047\u7740\u8fd9\u822c\u8c6a\u8d4c\u3002\u201d \u3000\u3000\u90a3\u98de\u8679\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u867d\u975e\u55dc\u8d4c\u4e4b\u4eba\uff0c\u4f46\u542c\u5230\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u811a\u4e0b\u4fbf\u50cf\u751f\u7fc5\u8180\u4f3c\u7684\uff0c\u8eab\u4e0d\u7531 \u4e3b\u5730\u5954\u4e86\u8fc7\u6765\u2026\u2026\u201d \u3000\u3000\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u6ee1\u9762\u4ff1\u662f\u5f3a\u7b11\uff0c\u4ed6\u7b11\u58f0\u4fbf\u4e0d\u89c9\u66f4\u662f\u5f97\u610f\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6218 \u98de\u5440\u6218\u98de\uff0c\u4f60\u806a\u660e\u4e86\u4e00\u4e16\uff0c\u5374\u7cca\u6d82\u4e86\u4e00\u65f6\uff0c\u5728\u8fd9\u822c\u4eba\u9762\u4e0a\uff0c\u4f60\u600e\u53ef\u73a9\u8d77\u2018\u5e05\u2019\u6765\uff0c\u4eca\u65e5\u6211\u82e5 \u4e0d\u8981\u4f60\u503e\u5bb6\u8361\u4ea7\uff0c\u4ece\u6b64\u4e5f\u7b97\u4e0d\u5f97\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\u4e86\u3002\u201d\u9762\u4e0a\u5374\u662f\u6ee1\u9762\u7b11\u5bb9\uff0c\u6717\u58f0\u9053\uff1a\u201c\u65b9\u624d\u7ba1 \u5bb6\u5728\u5916\u9762\u5f81\u52df\u6562\u6b7b\u7684\u82f1\u96c4\uff0c\u5144\u5f1f\u6211\u624d\u77e5\u9053\u5411\u5144\u60f3\u51fa\u8fd9\u822c\u5947\u5999\u7684\u8d4c\u6ce8\uff0c\u4f46\u5c0f\u5f1f\u5374\u65e0\u8fd9\u822c\u624b\u7b14\uff0c \u53ea\u80fd\u4ee5\u65b0\u8fdb\u5230\u7684\u4e94\u767e\u9798\u94f6\u5b50\u4e0e\u6218\u5144\u8d4c\u4e0a\u4e00\u8d4c\uff0c\u6218\u5144\u5982\u5acc\u5c11\u4e86\uff0c\u5c0f\u5f1f\u82cf\u5dde\u8fd8\u6709\u4e00\u7247\u5e84\u9662\uff0c\u867d\u65e0 \u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u8fd9\u822c\u8c6a\u9614\uff0c\u4f46\u4e5f\u5c0f\u5177\u89c4\u6a21\uff0c\u5c31\u4e00\u8d77\u51d1\u4e0a\u597d\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u968f\u968f\u4fbf\u4fbf\uff0c\u5c31\u4eff\u4f5b\u987d\u7ae5\u8d4c\u8c46\u4e00\u822c\u7684\u8f7b\u6613\uff1b\u4f46\u4ed6\u8bed\u58f0\u672a\u4e86\uff0c\u7fa4\u8c6a\u5df2\u5fcd\u4e0d\u4f4f\u60ca\u547c\u51fa \u58f0\uff0c\u4fbf\u8fde\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4e5f\u4e0d\u7981\u52a8\u5bb9\u3002 \u3000\u3000\u8981\u77e5\u4e94\u767e\u9798\u94f6\u5b50\u5df2\u6709\u4e94\u4e07\u4e24\u4e4b\u591a\uff0c\u518d\u52a0\u4e0a\u4ed6\u65e9\u5df2\u4f20\u540d\u6c5f\u6e56\u7684\u201c\u4e03\u5de7\u5c71\u5e84\u201d\uff0c\u5176\u4ef7\u503c\u5b9e\u662f \u9a87\u4eba\u542c\u95fb\u3002 \u3000\u3000\u90a3\u98de\u8679\u76ee\u5149\u4e00\u626b\uff0c\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u6211\u5e73\u751f\u4e0d\u8d4c\uff0c\u4eca\u65e5\u8d4c\u8d77\u6765\uff0c\u4fbf\u5b9a\u8981\u597d\u597d\u8d4c\u4e0a\uff5e\u8d4c\uff0c \u7eb5\u7136\u8f93\u5f97\u503e\u5bb6\u8361\u4ea7\uff0c\u5144\u5f1f\u4e5f\u662f\u5fc3\u7518\u60c5\u613f\uff0c\u6700\u591a\u518d\u82b1\u4e0a\u5341\u5e74\u5de5\u592b\u2026\u2026\u54c8\u54c8\uff0c\u6218\u5144\u2026\u2026\u6218\u5144\uff0c\u4f60 \u600e\u5730\u4e0d\u8bf4\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6014\u4e86\u4e00\u6014\uff0c\u4eff\u4f5b\u81ea\u68a6\u4e2d\u60ca\u9192\uff0c\u56de\u671b\u4e00\u773c\uff0c\u54c8\u54c8\u5f3a\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4eca\u65e5\u7684\u8d4c \u5c40\uff0c\u867d\u4e43\u6e38\u620f\uff0c\u4f46\u5927\u5bb6\u5374\u8d4c\u5f97\u6b63\u6b63\u5f53\u5f53\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u96be\u9053\u5144\u5f1f\u6211\u8d4c\u5f97\u4e0d\u6b63\u5f53\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u4e0a\u867d\u4ecd\u6ee1\u9762\u5f3a\u7b11\uff0c\u4f46\u76ee\u5149\u5374\u6ee1\u662f\u6068\u6bd2\u4e4b\u610f\uff0c\u82e5\u662f\u76ee\u5149\u4e5f\u80fd\u4f24\u4eba\uff0c\u90a3\u98de\u8679 \u65e9\u5df2\u6b7b\u4e86\u6570\u5341\u6b21\u4e86\u3002 \u3000\u3000\u8981\u77e5\u65b9\u624d\u7684\u8d4c\u6ce8\u4e8e\u6218\u98de\u7eb5\u6709\u4f24\u635f\uff0c\u72b9\u8fd8\u7f62\u4e86\uff0c\u4f46\u90a3\u98de\u8679\u6b64\u523b\u7684\u8d4c\u6ce8\uff0c\u5374\u8db3\u4ee5\u4ee4\u4efb\u4f55\u4eba\u503e \u5bb6\u8361\u4ea7\u3002\u201c\u795e\u624b\u201d\u6218\u98de\u867d\u7136\u662f\u7eff\u6797\u5927\u8c6a\uff0c\u4f46\u5e73\u65e5\u624b\u7b14\u751a\u5927\uff0c\u4e95\u65e0\u591a\u5c11\u79ef\u84c4\uff0c\u5e93\u4e2d\u6700\u591a\u4e5f\u4e0d\u8fc7 \u53ea\u5269\u4e86\u4e94\u4e07\u4e24\u94f6\u5b50\uff0c\u8fd9\u90a3\u98de\u8679\u7adf\u50cf\u662f\u7b97\u51c6\u4e86\u4ed6\u7684\u8eab\u5bb6\uff0c\u624d\u63d0\u51fa\u8fd9\u8d4c\u6ce8\u6765\uff0c\u81ea\u7136\u662f\u8981\u773c\u89c1\u6218\u98de \u7834\u4ea7\u800c\u5f15\u4ee5\u4e3a\u5feb\u3001\u4ed6\u751a\u81f3\u8fde\u6218\u98de\u7684\u533f\u5904\u90fd\u8981\u8d62\u6765\uff0c\u6068\u4e0d\u5f97\u7acb\u523b\u8981\u4ed6\u9732\u5bbf\u8857\u5934\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u81ea\u7136\u4e0d\u4f1a\u4e0d\u4e86\u89e3\u4ed6\u7684\u7528\u5fc3\uff0c\u4e0d\u7981\u6697\u4e2d\u6068\u6068\u9a82\u9053\uff1a\u201c\u90a3\u98de\u8679\u5440\u90a3\u98de\u8679\uff0c\u6211\u4e0e \u4f60\u6709\u4f55\u51a4\u4ec7\uff0c\u4f60\u8981\u5982\u6b64\u5bf9\u5f85\u4e8e\u6211\uff0c\u6709\u671d\u4e00\u65e5\uff0c\u4f60\u82e5\u662f\u72af\u5728\u6211\u7684\u624b\u91cc\uff0c\u54fc\u54fc\u2026\u2026\u201d \u3000\u3000\u53e3\u4e2d\u5927\u7b11\u4e09\u58f0\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u5e76\u65e0\u6b64\u610f\uff0c\u66f4\u975e\u4fe1\u4e0d\u8fc7\u90a3\u5144\uff0c\u4f46\u8d4c\u573a\u5982\u6218\u573a\uff0c\u4e00\u4e0a\u8d4c\u53f0\uff0c\u4fbf \u662f\u4eb2\u5144\u5f1f\uff0c\u4e5f\u5f97\u660e\u7b97\u5e10\u4e86\uff0c\u800c\u4e14\u2026\u2026\u8d4c\u53f0\u4e4b\u4e0a\uff0c\u8bb2\u7a76\u7684\u662f\u771f\u5200\u771f\u67aa\uff0c\u7eb8\u4e0a\u8c08\u5175\uff0c\u603b\u662f\u2026\u2026\u603b \u662f\xb7\u3002\u2026\xb7\u7b97\u4e0d\u5f97\u6570\u7684\u2026\u2026\u201d \u3000\u3000\u4ed6\u7a81\u5730\u60f3\u8d77\u4e00\u4e2a\u53ef\u4ee5\u63a8\u6258\u7684\u7406\u7531\uff0c\u5f3a\u7b11\u58f0\u4e2d\uff0c\u4fbf\u6709\u4e86\u4e9b\u771f\u5b9e\u7684\u7b11\u610f\uff0c\u4ef0\u5929\u5927\u7b11\u4e0d\u7edd\u3002\u90a3 \u98de\u8679\u51b7\u51b7\u671b\u7740\u4ed6\uff0c\u76f4\u5230\u4ed6\u7b11\u58f0\u987f\u4f4f\uff0c\u65b9\u81ea\u6717\u58f0\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u5fae\u76b1\uff0c\u9053\uff1a\u201c\u90a3\u5144\u867d\u7136\u8c6a\u9614\uff0c\u603b\u4e0d\u81f3\u5c06\u4e94\u4e07\u4e24\u94f6\u5b50\uff0c\u4e00\u8d77\u5e26\u5728\u8eab\u8fb9 \u5427\uff01\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u6070\u5de7\u5c06\u4e94\u4e07\u4e24\u94f6\u5b50\u4ff1\u90fd\u5e26\u6765\u4e86\uff0c\u867d\u7136\u672a\u5728\u8eab\u8fb9\uff0c\u4f46\u4e00 \u4e2a\u65f6\u8fb0\u4e4b\u4e2d\uff0c\u4fbf\u53ef\u53d6\u5230\uff0c\u800c\u4e14\u65b9\u624d\u5144\u5f1f\u542c\u5230\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u5df2\u5148\u4ee4\u624b\u4e0b\u7684\u5f1f\u5144\u53bb\u53d6\u4e86\uff0c\u56e0\u4e3a\u5144 \u5f1f\u5f97\u77e5\u6218\u5144\u5bcc\u7532\u6c5f\u5357\uff0c\u8fd9\u533a\u533a\u4e94\u4e07\u4e24\u94f6\u5b50\u7684\u8d4c\u6ce8\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u56de\u7edd\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u63a5\u7740\u9053\uff1a\u201c\u81f3\u4e8e\u90a3\u5ea7\u5e84\u9662\u4e48\uff0c\u5144\u5f1f\u6211\u6b64\u523b\u53ef\u7acb\u4e0b\u5b57\u636e\uff0c\u9664\u4e86\u5728\u573a\u7684\u8fd9\u8bb8\u591a \u6b66\u6797\u540c\u9053\u4ff1\u53ef\u4f5c\u4e3a\u89c1\u8bc1\u5916\uff0c\u5144\u5f1f\u8fd8\u60f3\u8bf7\u6a80\u8001\u9556\u5934\u3001\u5411\u5e2e\u4e3b\u4f5c\u4e2a\u4e2d\u4eba\uff0c\u82e5\u8c01\u8f93\u4e86\uff0c\u534a\u6708\u4e4b\u5185\uff0c \u4fbf\u5c06\u5e84\u9662\u62f1\u624b\u8ba9\u51fa\u2026\u2026\u54c8\u54c8\uff0c\u6218\u5144\u8bf4\u5f97\u662f\uff0c\u8d4c\u573a\u4e4b\u4e2d\uff0c\u4fbf\u662f\u4eb2\u5144\u5f1f\uff0c\u4e5f\u8981\u660e\u7b97\u5e10\u7684\u2026\u2026\u54c8 \u54c8\u2026\u2026\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u9053\uff1a\u201c\u5c0f\u5f1f\u867d\u975e\u591a\u4e8b\u4e4b\u4eba\uff0c\u4f46\u4eca\u65e5\u8fd9\u4e2a\u4e2d\u4eba\uff0c\u5374\u662f\u5b9a\u8981\u505a\u7684\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u65e2\u627f\u5927\u4fa0\u62ac\u7231\uff0c\u8001\u592b\u6562\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6728\u7acb\u5f53\u5730\uff0c\u5ffd\u7684\u62d4\u51fa\u6298\u6247\uff0c\u62fc\u547d\u6247\u4e86\u51e0\u4e0b\uff1b\u5ffd\u53c8\u6536\u56de\u6298\u6247\uff0c\u75db\u996e\u4e86\u51e0\u676f\u70c8 \u9152\u3002\u4ed6\u7eb5\u662f\u67ad\u96c4\uff0c\u7eb5\u7136\u8c6a\u8fc8\uff0c\u4f46\u591a\u5e74\u6765\u8f9b\u82e6\u6323\u6765\u7684\u5bb6\u4e16\uff0c\u5df2\u5c06\u5168\u90e8\u846c\u9001\u5728\u8fd9\u7edd\u65e0\u80dc\u671b\u4e4b\u8d4c\u6ce8 \u4e0a\uff0c\u5374\u4ecd\u4ee4\u4ed6\u5fcd\u4e0d\u4f4f\u5931\u4e86\u5e38\u6001\u3002 \u3000\u3000\u7fa4\u8c6a\u5c4f\u606f\u9759\u6c14\u5730\u671b\u7740\u4ed6\uff0c\u751a\u81f3\u8fde\u7a83\u7a83\u79c1\u8bed\u4e4b\u58f0\uff0c\u4ff1\u5df2\u5168\u90e8\u6d88\u5bc2\u3002 \u3000\u3000\u7a81\u5730\u6218\u98de\u5927\u7b11\u6570\u58f0\uff0c\u9053\uff1a\u201c\u597d\u597d\uff0c\u90a3\u5144\u65e2\u7136\u6709\u6b64\u8c6a\u5174\uff0c\u6218\u98de\u81ea\u5f53\u5949\u966a\u3002\u201d \u3000\u3000\u624b\u638c\u4e00\u6325\uff0c\u5927\u559d\u9053\uff1a\u201c\u62ff\u7b14\u781a\u6765\u3002\u201d \u3000\u3000\u4e00\u4e2a\u9887\u6709\u6587\u540d\u7684\u9556\u5934\uff0c\u88ab\u63a8\u51fa\u6765\u5199\u8fd9\u5f20\u5b57\u636e\uff0c\u4f46\u4ed6\u62ff\u8d77\u7b14\u65f6\uff0c\u624b\u638c\u5374\u4e0d\u7981\u7c0c\u7c0c\u53d1\u6296\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6728\u7136\u65c1\u89c2\uff0c\u70c8\u9152\u867d\u4f7f\u4ed6\u52c9\u5f3a\u63a7\u5236\u4e86\u81ea\u5df1\u7684\u9762\u5bb9\uff0c\u5374\u65e0\u6cd5\u80fd\u4f7f\u4ed6\u63a7\u5236\u4f4f\u989d\u4e0a \u7684\u6c57\u73e0\uff0c\u7b49\u5230\u63d0\u7b14\u5177\u540d\u65f6\uff0c\u6ee1\u5934\u5927\u6c57\uff0c\u4e0d\u7981\u6d94\u6d94\u800c\u843d\uff0c\u7fa4\u8c6a\u4e0d\u7981\u6697\u4e2d\u5947\u602a\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u5fd6 \u9053\uff1a\u201c\u6218\u795e\u624b\u4e00\u5411\u9547\u9759\uff0c\u600e\u5730\u6b64\u523b\u7adf\u5927\u5931\u5e38\u6001\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u4eec\u82e5\u80fd\u77e5\u9053\u201c\u795e\u624b\u201d\u6218\u98de\u6b64\u523b\u7684\u611f\u89c9\uff0c\u53ea\u6015\u518d\u65e0\u4eba\u4f1a\u751f\u51fa\u8fd9\u822c\u89c2\u5ff5\u6765\u3002\u201c\u9f99\u5f62\u516b \u638c\u201d\u51b7\u773c\u65c1\u89c2\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u79f0\u5947\u3002 \u3000\u3000\u5b57\u636e\u7acb\u8fc7\uff0c\u5206\u6210\u4e24\u4efd\uff0c\u5e76\u4e0e\u90a3\u4e24\u5f20\u94f6\u7968\uff0c\u4e00\u8d77\u538b\u5728\u91d1\u76d8\u4e4b\u4e0b\uff0c\u56db\u58c1\u7684\u706f\u706b\uff0c\u6620\u7740\u684c\u4e0a\u8fd9 \u4efd\u7a7a\u524d\u7684\u8d4c\u6ce8\uff0c\u4f7f\u5f97\u5b83\u4eec\u4f3c\u4e4e\u4e5f\u6709\u4e86\u7a7a\u524d\u7684\u5149\u5f69\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u5ffd\u5730\u5750\u4e0b\uff0c\u5ffd\u5730\u7ad9\u8d77\uff0c\u5b9e\u5df2 \u6709\u4e9b\u5750\u7acb\u4e0d\u5b89\u3002 \u3000\u3000\u7fa4\u8c6a\u7684\u76ee\u5149\uff0c\u66f4\u662f\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u5385\u95e8\uff0c\u65b9\u624d\u5954\u51fa\u7684\u7ba1\u5bb6\u4e8e\u5e73\uff0c\u6b64\u523b\u5306\u5306\u5954\u4eba\uff0c\u7fa4\u8c6a\u867d \u7136\u660e\u660e\u770b\u6e05\u662f\u4ed6\uff0c\u5fc3\u5934\u4ecd\u4e0d\u7981\u4ff1\u90fd\u4e3a\u4e4b\u4e00\u8df3\uff0c\u6b64\u523b\u95e8\u524d\u53ea\u6709\u4eba\u5f71\u95ee\u52a8\uff0c\u4f17\u4eba\u4fbf\u4e0d\u7981\u4e00\u8d77\u7d27\u5f20 \u8d77\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4e8e\u5e73\u5927\u6b65\u5954\u4eba\u4e4b\u540e\uff0c\u4fbf\u626c\u58f0\u9053\uff1a\u201c\u5916\u9762\u7684\u5144\u5f1f\uff0c\u4ff1\u60f3\u4e3a\u5e84\u4e3b\u5356\u547d\uff0c\u4f46\u5c0f\u5f1f\u4e00\u770b\u4eba\u5927 \u591a\u4e86\uff0c\u53ea\u80fd\u968f\u610f\u9009\u51fa\u4e5d\u4f4d\u2026\u2026\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u6218\u5144\u5b9e\u662f\u6df1\u5f97\u4eba\u5fc3\u2026\u2026\u6df1\u5f97\u4eba\u5fc3\uff01\u563f\u563f\uff01\u201d \u3000\u3000\u4ed6\u65b9\u624d\u773c\u89c1\u5230\u5f53\u65f6\u7684\u60c5\u51b5\uff0c\u5b9e\u5728\u5e76\u4e0d\u8e0a\u8dc3\uff0c\u751a\u81f3\u8fd8\u5e26\u7740\u52c9\u5f3a\u3002\u201c\u201d\u795e\u624b\u201c\u6218\u98de\u9762\u988a\u5fae \u7ea2\uff0c\u5927\u558a\u9053\uff1a\u201c\u5524\u5c06\u8fdb\u6765\uff01\u201d \u3000\u3000\u4e5d\u6761\u9ed1\u8863\u5927\u6c49\u5e94\u58f0\u800c\u5165\uff0c\u6070\u5de7\u9762\u5bf9\u7740\u90a3\u4e5d\u6761\u9526\u8863\u5927\u6c49\uff0c\u5341\u516b\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u4f60\u671b\u7740\u6211\uff0c\u6211 \u671b\u7740\u4f60\uff0c\u5fc3\u91cc\u5374\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u76ee\u5149\u4e00\u626b\uff0c\u4fbf\u77e5\u9053\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u4e0d\u6127\u4e00\u65b9\u5927\u8c6a\uff0c\u5e76\u672a\u4ee5\u8001\u5f31\u6b8b\u5175\u6df7\u5145 \u4eba\u6570\uff0c\u8fd9\u4e5d\u6761\u9ed1\u8863\u5927\u6c49\u4ea6\u662f\u7cbe\u795e\u9971\u6ee1\uff0c\u884c\u6b62\u77eb\u5065\uff0c\u53ea\u89c1\u795e\u6001\u4e4b\u95f4\uff0c\u5374\u8fdc\u4e0d\u5982\u81ea\u5df1\u624b\u4e0b\u7684\u4ece\u5bb9 \u9547\u5b9a\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u8fde\u8fde\u987f\u9996\u9053\uff1a\u201c\u597d\uff0c\u597d\u2026\u2026\u201d\u5ffd\u5730\u56de\u8fc7\u5934\u53bb\uff0c\u5728\u4e8e\u5e73\u8033\u8fb9\u4f4e\u4f4e\u5429\u5490\u4e86\u51e0 \u53e5\u3002\u2018\u201c\u91d1\u9e21\u201d\u5411\u4e00\uff0c\u557c\u76ee\u5149\u4e00\u8f6c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u5144\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u4eca\u65e5\u4f60\u6211\u82e5\u662f\u8f93\u4e86\u4e5f\u4fbf \u7f62\uff1b\u82e5\u662f\u8d62\u4e86\u4e48\u2026\u2026\uff1f\u563f\u563f\uff0c\u53ea\u6015\u51fa\u53bb\u65f6\u4fbf\u8fdc\u4e0d\u53ca\u8fdb\u6765\u65f6\u5bb9\u6613\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u4e00\u53d8\uff0c\u4ea6\u81ea\u51b7\u7b11\u9053\uff1a\u201c\u5411\u5144\u5f53\u771f\u5c06\u5144\u5f1f\u5982\u6b64\u8f7b\u8d31\u4e48\uff1f\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u60a0\u60a0\u9053\uff1a\u201c\u5bb3\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u6709\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff0c\u53e4\u6709\u660e\u8bad\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u8f69\uff0c\u5927\u559d\u9053\uff1a\u201c\u4e8e\u5e73\uff0c\u65b9\u624d\u6211\u4e0e\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u5e73\u5782\u9996\u9053\uff1a\u201c\u5e84\u4e3b\u4ee4\u5c0f\u7684\u5b89\u629a\u8fd9\u4e5d\u4f4d\u5144\u5f1f\u7684\u5bb6\u5c5e\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u54c8\u54c8\u7b11\u9053\uff1a\u300a\u6b64\u523b\u80dc\u8d1f\u672a\u5206\uff0c\u6218\u5144\u600e\u5730\u5c31\u957f\u8d77\u4e86\u4ed6\u4eba\u7684\u5fd7\u6c14\uff0c\u706d\u6389\u4e86\u81ea\u5df1 \u7684\u5a01\u98ce\uff1f\u201c\u4ef0\u8d77\u5934\u6765\uff0c\u4e0d\u4f4f\u5927\u7b11\u3002\u5fc3\u601d\u91cd\u91cd\uff0c\u6ee1\u5fc3\u5fe7\u6124\u7684\u201d\u4e03\u5de7\u7ae5\u5b50\u201c\u5434\u9e23\u4e16\uff0c\u4e5f\u65e9\u88ab\u8fd9\u9635 \u8c6a\u8d4c\u60ca\u52a8\uff1b\u6b64\u523b\u89c1\u5230\u8fd9\u822c\u60c5\u52bf\uff0c\u77e5\u9053\u8fd9\u201d\u795e\u624b\u201c\u6218\u98de\u5df2\u88ab\u4f17\u4eba\u56f4\u653b\uff0c\u5f53\u771f\u5df2\u662f\u56de\u9762\u695a\u6b4c\uff0c\u5fc3 \u4e2d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u53f9\u606f\uff01\u4ed6\u867d\u7136\u4e0d\u503c\u6218\u98de\u4e4b\u4e3a\u4eba\uff0c\u6b64\u523b\u5374\u4e5f\u9887\u4e3a\u611f\u6168\uff0c\u671b\u4e86\u671b\u684c\u4e0a\u7684\u8d4c\u6ce8\uff0c\u53c8\u671b \u4e86\u671b\u90a3\u5341\u516b\u6761\u6d3b\u751f\u751f\u7684\u5927\u6c49\uff0c\u7a81\u5730\u53f9\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u8d4c\uff0c\u65e0\u8bba\u6dee\u80dc\u8c01\u8d1f\uff0c\u4f46\u6218\u98de\u5e84\u4e3b\u4e00\u751f\u4e4b \u4e2d\uff0c\u80fd\u6709\u6b64\u8c6a\u8d4c\uff0c\u4ea6\u53ef\u8db3\u4ee5\u81ea\u50b2\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u4e2d\u5927\u662f\u611f\u6fc0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5434\u5c11\u4fa0\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u624d\u51fa\uff0c\u7a81\u542c\u81ea\u5df1\u8eab\u4fa7\uff0c\u54cd\u8d77\u4e00\u58f0\u6781\u5176\u8f7b\u8511\u5c16\u9510\u7684\u51b7\u7b11\u3002 \u3000\u3000\u8fd9\u8f7b\u8511\u7684\u51b7\u7b11\u58f0\uff0c\u5728\u8fd9\u9759\u5bc2\u7684\u5385\u5802\u4e2d\uff0c\u81ea\u663e\u5f97\u51fa\u5947\u7684\u54cd\u4eae\uff0c\u7fa4\u8c6a\u76ee\u5149\uff0c\u4e00\u8d77\u81ea\u5385\u95e8\u8f6c\u4e86 \u8fc7\u6765\u2014\u2014\u53ea\u89c1\u8fd9\u6b21\u51b7\u7b11\u4e4b\u58f0\uff0c\u7ade\u662f\u90a3\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u8eab\u4fa7\u7684\u201c\u9f99\u5973\u201d\u6a80\u6587\u742a\u53d1\u51fa\u6765\u7684\uff0c\u6ee1 \u5385\u7684\u706f\u5149\uff0c\u6b64\u523b\u4fbf\u4e00\u8d77\u5730\u6c47\u96c6\u5230\u5979\u90a3\u79c0\u7f8e\u7edd\u4f26\uff0c\u4f46\u5374\u4e1d\u6beb\u6ca1\u6709\u4e00\u4e1d\u8840\u8272\u7684\u5a07\u5bb9\u4e4b\u4e0a\uff0c\u4f7f\u5f97\u5979 \u4e00\u53cc\u79cb\u6ce2\uff0c\u4e5f\u6709\u4e86\u51fa\u5947\u7684\u660e\u4eae\u3002 \u3000\u3000\u5979\u76ee\u5149\u5446\u5446\u5730\u51dd\u6ce8\u7740\u81ea\u5df1\u7684\u7ea4\u7ea4\u7389\u624b\uff0c\u5bf9\u6570\u767e\u9053\u7b14\u76f4\u7684\u773c\u795e\uff0c\u7adf\u662f\u4e0d\u95fb\u4e0d\u89c1\uff0c\u53ea\u662f\u51b7\u51b7 \u8bf4\u9053\uff1a\u201c\u5047\u5982\u8fd9\u4e5f\u7b97\u505a\u8c6a\u8d4c\uff0c\u4e16\u4e0a\u7684\u8c6a\u8d4c\u4e5f\u4e0d\u514d\u5927\u591a\u4e86\u4e9b\u5427\uff01\u201d \u3000\u3000\u5979\u795e\u60c5\u4e4b\u95f4\u3001\u4eff\u4f5b\u662f\u81ea\u8a00\u81ea\u8bed\uff0c\u751f\u50cf\u662f\u4e0d\u77e5\u9053\u81ea\u5df1\u8fd9\u7b80\u7b80\u5355\u5355\u7684\u4e24\u53e5\u8bdd\uff0c\u4f1a\u5982\u4f55\u9707\u52a8\u4eba \u5fc3\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u5927\u53d8\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u76ee\u5149\u4e00\u4eae\uff0c\u5bf9\u671b\u4e00\u773c\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u53cc\u7709\u7acb\u76b1\u3002 \u3000\u3000\u5979\u8bed\u58f0\u4e00\u843d\uff0c\u8fd9\u4e9b\u4eba\u7adf\u4e00\u8d77\u5f00\u53e3\u9053\uff1a\u201c\u2026\u2026\u201d\u8bf4\u4e86\u4e00\u5b57\uff0c\u624d\u53d1\u89c9\u7adf\u6709\u4eba\u5728\u4e00\u8d77\u62a2\u7740\u8bf4 \u8bdd\uff0c\u8c01\u4e5f\u6ca1\u6709\u542c\u6e05\u522b\u4eba\u90a3\u4e00\u5b57\u662f\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u7ec8\u4e8e\u8fd8\u662f\u8ba9\u201c\u9f99\u5f62\u516b\u638c\u201d\u6c89\u58f0\u9053\uff1a\u201c\u742a\u513f\uff0c\u4f11\u5f97\u80e1\u8a00\u4e71\u8bed\uff01\u201d \u3000\u3000\u4ed6\u5bf9\u6a80\u6587\u742a\u59cb\u7ec8\u6781\u4e3a\u75db\u7231\uff0c\u6b64\u523b\u5f53\u7740\u6ee1\u5385\u7fa4\u8c6a\uff0c\u8d23\u9a82\u4e86\u5979\u8fd9\u4e00\u53e5\uff0c\u81ea\u5df1\u53c8\u89c9\u5f97\u8bf4\u5f97\u592a\u91cd \u4e86\u4e9b\u3002 \u3000\u3000\u54ea\u77e5\u64c5\u6587\u742a\u9762\u8272\u6728\u7136\u3001\u7389\u5bb9\u5982\u51b0\uff0c\u7adf\u4f3c\u6839\u672c\u6ca1\u6709\u542c\u5230\u8fd9\u53e5\u8bdd\u4f3c\u7684\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u65e5\u5149\u95ea\u70c1\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u96be\u9053\u6a80\u59d1\u5a18\u8fd8\u6709\u4ec0\u4e48\u66f4\u8d35\u91cd\u7684 \u8d4c\u6ce8\u4e48\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u51b7\u51b7\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u5979\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u518d\u6b21\u4f4e\u53f1\u4e00\u58f0\uff1a\u201c\u5750\u4e0b\uff01\u201d \u3000\u3000\u4f46\u6a80\u6587\u742a\u6b64\u523b\u5374\u4eff\u4f5b\u53ea\u5269\u4e0b\u4e00\u5177\u7f8e\u7edd\u4eba\u5bf0\u7684\u8eaf\u58f3\uff0c\u7075\u9b42\u4e0e\u795e\u667a\uff0c\u4eff\u4f5b\u5374\u5df2\u98d8\u6e3a\u5730\u79bb\u53bb \u4e86\u3002 \u3000\u3000\u5979\u51b0\u51b7\u7684\u79cb\u6ce2\uff0c\u53ea\u5230\u6b64\u523b\u624d\u5f00\u59cb\u8f6c\u52a8\uff0c\u95ea\u7535\u822c\u56db\u671b\u4e00\uff0c\u773c\uff0c\u7f13\u7f13\u8d70\u5230\u201c\u795e\u624b\u201d\u6218\u98de\u9762 \u524d\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6b64\u523b\u7adf\u4e0d\u89c9\u88ab\u5979\u8fd9\u5947\u5f02\u7684\u795e\u60c5\u9707\u6151\uff0c\u5450\u5450\u9053\uff1a\u201c\u6a80\u59d1\u5a18\u6709\u4f55\u2026\u2026\u201d \u3000\u3000\u6a80\u6587\u742a\u51b7\u51b7\u9053\uff1a\u201c\u6211\u8981\u4e0e\u4f60\u8d4c\u7684\u4e1c\u897f\uff0c\u6bd4\u8fd9\u4e9b\u90fd\u8d35\u91cd\u5f97\u591a\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u4f60\u662f\u5426\u6709\u6b64\u52c7 \u6c14\uff1f\u6562\u4e0d\u6562\u63a5\u53d7\uff1f\u201d \u3000\u3000\u90a3\u98de\u8679\u3001\u5411\u4e00\u557c\uff0c\u518d\u6b21\u5bf9\u671b\u4e00\u773c\uff0c\u76ee\u4e2d\u8fde\u8fde\u95ea\u52a8\u7740\u5174\u594b\u7684\u5149\u8292\uff0c\u6ee1\u5385\u7fa4\u8c6a\u66f4\u662f\u4e00\u8d77\u98de\u8eab \u800c\u8d77\uff0c\u5c31\u8fde\u7f6e\u8eab\u4e8b\u5916\uff0c\u8896\u624b\u65c1\u89c2\u7684\u4e1c\u65b9\u4e94\u5144\u5f1f\uff0c\u4e5f\u7ad9\u8d77\u8eab\u6765\uff0c\u6570\u767e\u9053\u773c\u795e\uff0c\u4e00\u8d77\u76ef\u4f4f\u8fd9\u5947\u5f02 \u7684\u5c11\u5973\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u534a\u5e26\u8be2\u95ee\uff0c\u534a\u5e26\u6c42\u52a9\u5730\u77a7\u4e86\u6a80\u660e\u4e00\u773c\uff1b\u4f46\u6a80\u660e\u6b64\u523b\u4e5f\u4e0d\u80fd\u5f3a\u8feb\u4ed6\u7231\u5973\u79bb \u53bb\uff0c\u4f55\u51b5\u4ed6\u4e5f\u60f3\u6218\u98de\u503e\u5bb6\u8361\u4ea7\uff0c\u53ea\u8981\u5bf9\u6218\u98de\u4e0d\u5229\u7684\u4e8b\uff0c\u591a\u4e9b\u4e5f\u65e0\u59a8\uff0c\u4f55\u51b5\u4ed6\u4ea6\u77e5\u6218\u98de\u7edd\u65e0\u5f97 \u80dc\u4e4b\u671b\uff0c\u662f\u4ee5\u6b64\u523b\u7adf\u4e5f\u4e0d\u95fb\u4e0d\u95ee\u8d77\u6765\u3002 \u3000\u3000\u6a80\u6587\u742a\u79cb\u6ce2\u51b0\u51b7\u5730\u671b\u7740\u6218\u98de\uff0c\u7adf\u751f\u50cf\u662f\u4e00\u53ea\u591c\u884c\u7684\u732b\uff0c\u8f7b\u8511\u800c\u8ba5\u5632\u5730\u671b\u7740\u9762\u524d\u7684\u8001\u9f20\u3002 \u201c\u795e\u624b\u201d\u6218\u98de\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u59a8\u5148\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u6a80\u6587\u742a\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u82e5\u63a5\u53d7\uff0c\u6211\u518d\u8bf4\u51fa\u3002\u201d \u3000\u3000\u6218\u98de\u5450\u5450\u9053\uff1a\u201c\u59d1\u5a18\u5982\u4e0d\u8bf4\u51fa\uff0c\u6218\u67d0\u600e\u80fd\u5984\u8a00\u7b54\u5e94\u4e0e\u5426\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f60\u7adf\u65e0\u52c7\u6c14\u6765\u63a5\u53d7\u4e00\u4e2a\u5973\u5b50\u7684\u8d4c\u6ce8\uff1f\u201d \u3000\u3000\u6218\u98de\u4f38\u624b\u4e00\u62b9\u989d\u4e0a\u6c57\u73e0\uff0c\u8fd9\u53f1\u54a4\u4e00\u65f6\u7684\u6b66\u6797\u67ad\u96c4\uff0c\u6b64\u523b\u4e0d\u77e5\u600e\u5730\uff0c\u7adf\u4f1a\u5728\u5fc3\u5e95\u5347\u8d77\u4e86\u4e00 \u9635\u5bd2\u610f\uff0c\u56e0\u4e3a\u9762\u524d\u8fd9\u7edd\u8272\u5c11\u5973\u51b7\u5982\u7384\u51b0\u7684\u795e\u6001\uff0c\u7684\u786e\u5df2\u6df1\u6df1\u5730\u60ca\u6151\u4e86\u4ed6\uff0c\u6c89\u541f\u534a\u664c\uff0c\u5450\u5450 \u9053\uff1a\u201c\u5728\u4e0b\u82e5\u65e0\u6b64\u7269\uff1f\u2026\u2026\u201d \u3000\u3000\u6a80\u6587\u742a\u7b80\u77ed\u800c\u751f\u51b7\u5730\u622a\u53e3\u9053\uff1a\u201c\u4f60\u6709\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u53ea\u89c9\u5fc3\u623f\u8df3\u52a8\uff0c\u51e0\u6b32\u79bb\u8154\u800c\u51fa\uff01 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u633a\u8d77\u80f8\u6765\uff0c\u6697\u601d\u81ea\u5df1\uff0c\u600e\u4f1a\u5728\u81ea\u5df1\u5bf9\u5934\u4e4b\u5927\u9762\u524d\u5982\u6b64\u754f \u7f29\u3002 \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u6717\u58f0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u65e0\u8bba\u59d1\u5a18\u8981\u8d4c\u4ec0\u4e48\uff0c\u5728\u4e0b\u65e0\u4e0d\u63a5\u4ed6\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u53cd\u6b63 \u4eca\u65e5\u4e4b\u8d4c\uff0c\u5df2\u8db3\u4ee5\u4ee4\u6211\u503e\u5bb6\uff01\u518d\u52a0\u4e0a\u4e00\u4e9b\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u662f\u4ee5\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u4fbf\u53c8\u6062\u590d\u4e86\u51e0\u5206\u5f80\u6614\u7684\u96c4\u98ce\u3002 \u3000\u3000\u6a80\u6587\u742a\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8981\u4e0e\u4f60\u8d4c\u7684\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u58f0\u8f7b\u8f7b\u987f\u4f4f\uff0c\u51b0\u51b7\u7684\u79cb\u6ce2\uff0c\u518d\u6b21\u95ea\u7535\u822c\u56db\u4e0b\u4e00\u626b\u2026\u2026 \u3000\u3000\u7fa4\u8c6a\u51e0\u4e4e\u8fde\u547c\u5438\u4e5f\u4e00\u8d77\u505c\u4f4f\uff0c\u53ea\u542c\u5979\u4e00\u5b57\u4e00\u5b57\u5730\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u4e00\u53cc\u773c\u775b\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u4e45\u5df2\u5c4f\u4f4f\u547c\u5438\uff0c\u6b64\u523b\u90fd\u4e0d\u7981\u9f50\u58f0\u60ca\u547c\uff01 \u3000\u3000\u6a80\u6587\u742a\u82cd\u767d\u800c\u7edd\u7f8e\u7684\u9762\u5bb9\uff0c\u4ecd\u662f\u6728\u7136\u4e0d\u53d8\uff0c\u51b7\u51b7\u63a5\u53e3\u9053\uff1a\u201c\u6211\u4eec\u7684\u8d4c\u6ce8\uff0c\u4ee5\u660e\u65e5\u6b63\u5348\u4e3a \u671f\uff0c\u90a3\u91cc\u88f4\u73cf\u4e0e\u2018\u51b7\u8c37\u53cc\u6728\u2019\u65e0\u8bba\u8c01\u80dc\u8c01\u8d1f\uff0c\u90fd\u5fc5\u5b9a\u5df2\u53ef\u5206\u51fa\u7ed3\u679c\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u65b9\u81ea\u6062\u590d\u7684\u8c6a\u6c14\uff0c\u6b64\u523b\u53c8\u4e3a\u4e4b\u6240\u6151\u3002 \u3000\u3000\u4f46\u7fa4\u8c6a\u76ee\u5149\u5df2\u81ea\u6a80\u6587\u742a\u8f6c\u5411\u4ed6\uff0c\u4f7f\u4ed6\u4e0d\u5f97\u4e0d\u5450\u5450\u9053\uff1a\u201c\u60f3\u5fc5\u5982\u6b64\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u76ee\u5149\uff0c\u4e00\u8d77\u56de\u5411\u6a80\u6587\u742a\uff0c\u53ea\u542c\u5979\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u65f6\u88f4\u73cf\u82e5\u5df2\u56de\u8f6c\uff0c\u6211\u7acb\u523b\u4fbf\u6458\u4e0b\u6211\u7684 \u773c\u775b\uff0c\u53cc\u624b\u5949\u9001\u5230\u4f60\u9762\u524d\uff1b\u5426\u5219\u7684\u8bdd\uff0c\u6211\u4e0d\u8bf4\u4f60\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u8bf4\u5f97\u4ecd\u662f\u51b0\u51b0\u51b7\u51b7\uff0c\u65e0\u52a8\u4e8e\u8877\uff0c\u751f\u50cf\u662f\u6839\u672c\u6ca1\u6709\u5c06\u81ea\u5df1\u7684\u4e00\u53cc\u773c\u775b\u770b\u505a\u81ea\u5df1\u7684\u3002 \u3000\u3000\u6ee1\u5385\u7fa4\u8c6a\uff0c\u867d\u7136\u4ff1\u90fd\u662f\u5200\u53e3\u4e0b\u8ba8\u751f\u6d3b\u7684\u89d2\u8272\uff0c\u90fd\u4e5f\u672a\u66fe\u89c1\u8fc7\u5982\u6b64\u51b7\u5ced\u7684\u5973\u5b50\uff0c\u4e0d\u7981\u4e3a\u4e4b \u5012\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u6709\u7684\u5fcd\u4e0d\u4f4f\u5077\u5077\u53bb\u671b\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e00\u773c\uff0c\u53ea\u5f53\u4ed6\u542c\u5230\u81ea\u5df1\u7231\u5973\u4e0b\u8fd9\u822c\u7684\u8d4c \u6ce8\uff0c\u4e5f\u5b9a\u8981\u5fc3\u60ca\u80c6\u98a4\u3002 \u3000\u3000\u54ea\u77e5\u6a80\u660e\u4e00\u624b\u637b\u987b\uff0c\u5374\u4ecd\u662f\u795e\u8272\u4e0d\u52a8\uff0c\u4ed6\u4eec\u81ea\u7136\u731c\u4e0d\u51fa\u8fd9\u9886\u8896\u7fa4\u4f26\u7684\u6b66\u6797\u5927\u8c6a\u4e4b\u5fc3\u610f\u3002 \u3000\u3000\u6a80\u660e\u81ea\u7136\u6df1\u77e5\u88f4\u73cf\u7edd\u975e\u2018\u51b7\u8c37\u53cc\u6728\u2019\u7684\u654c\u624b\uff0c\u90a3\u4e48\u4ed6\u53c8\u4f55\u5c1d\u4e0d\u5e0c\u671b\u6316\u4e0b\u4ed6\u5bf9\u5934\u7684\u4e00\u53cc\u773c \u775b\uff1f\u662f\u4ee5\u4ed6\u5bf9\u81ea\u5df1\u7231\u5973\u7684\u4e3e\u52a8\uff0c\u53cd\u800c\u6ca1\u6709\u9707\u60ca\u8d23\u602a\uff0c\u53cd\u800c\u6697\u6697\u6709\u7740\u4e9b\u8d5e\u8bb8\u4e4b\u610f\uff0c\u4e3a\u5979\u80fd\u5229\u7528 \u65f6\u673a\uff0c\u5934\u8111\u7075\u6d3b\uff0c\u7adf\u4e0d\u900a\u4e8e\u5df1\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u53f1\u54a4\u98ce\u4e91\u7684\u6b66\u6797\u5927\u8c6a\uff0c\u53c8\u4f55\u5c1d\u731c\u51fa\u4e86\u4ed6\u7231\u5973\u7684\u5fc3\u610f\uff1f \u3000\u3000\u53ea\u6709\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u5728\u6697\u4e2d\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\u6211\u90a3\u88f4\u5144\u65b9\u624d\u79bb\u53bb\u65f6\uff0c\u5df2\u6df1\u6df1 \u4f24\u4e86\u8fd9\u5c11\u5973\u7684\u5fc3\uff0c\u4ed6\u82e5\u4e07\u4e00\u80dc\u4e86\uff0c\u5979\u771f\u7684\u60c5\u613f\u6316\u4e0b\u81ea\u5df1\u7684\u773c\u775b\uff0c\u56e0\u4e3a\u5979\u518d\u4e5f\u4e0d\u613f\u89c1\u5230\u4ed6 \u4e86\uff01\u201d \u3000\u3000\u53ea\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u5931\u9b42\u843d\u9b44\u5730\u6014\u4e86\u534a\u664c\uff0c\u7a81\u5730\u54af\u54af\u5e72\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u59d1\u5a18\u53c8\u4f55\u82e6\u4e0e\u5728\u4e0b\u6765 \u8d4c\u773c\u775b\uff0c\u5728\u4e0b\u7684\u8fd9\u53cc\u773c\u775b\uff0c\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4f46\u88f4\u5927\u5148\u751f\u82e5\u662f\u80dc\u4e86\uff0c\u59d1\u5a18\u7684\u8fd9\u4e00\u53cc\u526a\u6c34\u79cb\u6ce2\uff0c\u8840 \u6dcb\u6dcb\u5730\u6316\u5c06\u51fa\u6765\uff0c\u5374\u5f53\u771f\u53eb\u5728\u4e0b\u770b\u4e86\u4e0d\u5fcd\uff01\u563f\u563f\u2014\u2014\u5404\u4f4d\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u5984\u60f3\u4ee5\u8fd9\u756a\u8f7b\u677e\u7684\u8a00\u8bed\u6765\u63a9\u9970\u81ea\u5df1\u7684\u7d27\u5f20\uff0c\u66f4\u671f\u671b\u80fd\u4ee5\u8fd9\u756a\u8a00\u8bed\u6765\u6253\u52a8\u6a80\u6587\u742a\u7684\u5fc3\uff0c \u540c\u65f6\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5e0c\u671b\u80fd\u4ee5\u6b64\u6765\u5085\u53d6\u522b\u4eba\u540c\u60c5\u7684\u7b11\u58f0\u3002 \u3000\u3000\u4f46\u7fa4\u8c6a\u6b64\u523b\u4eba\u4eba\u5fc3\u5f26\u7d27\u6263\uff0c\u90a3\u6709\u5fc3\u60c5\u7b11\u5f97\u51fa\u6765\uff0c\u6a80\u6587\u742a\u51b7\u51b7\u9053\uff1a\u201c\u662f\u4e48\u2026\u2026\u201d\u7a81\u5730\u9762\u5bb9 \u5927\u53d8\uff0c\u653e\u58f0\u9053\uff1a\u201c\u88f4\u73cf\u82e5\u662f\u80dc\u4e86\uff0c\u6211\u4e0d\u4f46\u6316\u51fa\u773c\u775b\uff0c\u8fd8\u8981\u5272\u4e0b\u820c\u5934\uff0c\u56e0\u4e3a\u6211\u518d\u4e5f\u4e0d\u613f\u89c1\u5230 \u4ed6\uff0c\u518d\u4e5f\u4e0d\u613f\u4e0e\u4ed6\u8bf4\u8bdd\u2026\u2026\u201d \u3000\u3000\u7fa4\u8c6a\u4e00\u6123\uff0c\u4ff1\u90fd\u5927\u5947\uff0c\u4e0d\u77e5\u5979\u4e3a\u4f55\u7a81\u5730\u53d8\u4e86\u795e\u6001\uff0c\u53d8\u4e86\u8bed\u6c14\uff0c\u751a\u81f3\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16 \u5374\u53c8\u4e0d\u7981\u53f9\u606f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u5a07\u7eb5\u800c\u4efb\u6027\u7684\u5c11\u5973\uff0c\u7ec8\u4e8e\u4e0d\u7981\u9732\u51fa\u4e86\u81ea\u5df1\u7684\u771f\u60c5\u3002 \u3000\u3000\u6b64\u523b\u5385\u5185\u7fa4\u8c6a\uff0c\u56fa\u662f\u4eba\u4eba\u6ce8\u610f\u7740\u6a80\u6587\u742a\uff1b\u9662\u5916\u7684\u4eba\uff0c\u4e5f\u4ff1\u90fd\u8702\u6d8c\u5230\u5385\u95e8\uff0c\u6570\u767e\u9053\u76ee\u5149\uff0c \u5168\u90e8\u88ab\u5979\u5438\u5f15\uff0c\u8c01\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u9662\u4e2d\u5df2\u6084\u6084\u8d70\u5165\u4e86\u4e00\u6761\u4eba\u5f71\uff0c\u5c31\u50cf\u662f\u4e00\u6761\u6de1\u7070\u8272\u7684\u5e7d\u7075\uff01 \u3000\u3000\u4ed6\u4e3a\u4e86\u6a80\u6587\u742a\u7684\u8bed\u58f0\u800c\u987f\u4f4f\u811a\u6b65\uff0c\u53c8\u4e3a\u6a80\u6587\u742a\u7684\u8a00\u8bed\u800c\u9eef\u7136\u8f7b\u53f9\uff0c\u5929\u4e0a\u7684\u661f\u5149\uff0c\u5385\u5185\u7684 \u706f\u5149\uff0c\u6620\u7740\u4ed6\u7684\u9762\u5bb9\u3002 \u3000\u3000\u4ed6\u7684\u9762\u5bb9\u7adf\u4e5f\u6709\u5982\u5e7d\u7075\u7684\u60e8\u767d\u3002 \u3000\u3000\u4ed6\u8e2f\u8e85\u5728\u95e8\u5916\uff0c\u8bb8\u4e45\u8bb8\u4e45\u2026 \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u633a\u4e00\u633a\u80f8\u819b\uff0c\u5206\u5f00\u8702\u6d8c\u5728\u95e8\u53e3\u7684\u4eba\u7fa4\uff0c\u7f13\u6b65\u8d70\u4eba\u5927\u5385\u3002 \u3000\u3000\u5385\u5185\u7fa4\u8c6a\uff0c\u8fd8\u5728\u5446\u5446\u5730\u671b\u7740\u6a80\u6587\u742a\uff0c\u4e0d\u77e5\u662f\u8c01\uff0c\u7a81\u5730\u60ca\u547c\u4e00\u58f0\uff01 \u3000\u3000\u201c\u88f4\u2026\u2026\u88f4\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e00\u4e2a\u5b57\u5728\u6b64\u65f6\u5f53\u771f\u6bd4\u5f20\u5929\u5e08\u7684\u4f5b\u6cd5\u8fd8\u6709\u9b54\u529b\uff0c\u6bcf\u4e00\u4e2a\u4eba\u7684\u76ee\u5149\u2014\u2014\u5305\u62ec\u6a80\u6587\u742a\u7684\u5728 \u5185\uff0c\u90fd\u7740\u4e86\u9b54\u4f3c\u5730\u5411\u5385\u95e8\u671b\u53bb\u3002 \u3000\u3000\u5385\u95e8\u524d\u7684\u4eba\u7fa4\uff0c\u6b64\u523b\u5374\u50cf\u662f\u7740\u4e86\u9b54\u4f3c\u7684\u8fdc\u8fdc\u907f\u4e86\u5f00\u53bb\uff0c\u7559\u4e0b\u4e00\u6761\u6781\u5bbd\u6781\u5bbd\u7684\u9053\u8def\uff0c\u5c31\u50cf \u662f\u8fd9\u8fdb\u6765\u7684\u4eba\u6709\u7740\u76d8\u53e4\u90a3\u6837\u9876\u5929\u7acb\u5730\u7684\u8eab\u4f53\u4f3c\u7684\u3002 \u3000\u3000\u9053\u8def\u4e2d\uff0c\u4e00\u4e2a\u4eba\u7f13\u6b65\u800c\u5165\uff01 \u3000\u3000\u4ed6\u811a\u6b65\u867d\u7136\u8f7b\u5fae\uff0c\u4f46\u6b64\u523b\u6b64\u65f6\uff0c\u8fd9\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\uff0c\u5374\u50cf\u662f\u5de8\u65a7\u6572\u5c71\u4f3c\u7684\uff0c\u4e00\u58f0\u58f0\u76f4\u9707\u5230 \u4eba\u4eec\u5fc3\u5e95\u3002 \u3000\u3000\u2014\u2014\u9635\u96be\u4ee5\u5f62\u5bb9\u7684\u9759\u5bc2\u4e4b\u540e\uff0c\u4e00\u58f0\u60ca\u5929\u52a8\u5730\u7684\u60ca\u547c\u7ec8\u4e8e\u54cd\u8d77\u3002 \u3000\u3000\u7136\u540e\uff0c\u6570\u767e\u9053\u58f0\u97f3\u4e00\u8d77\u6b22\u547c\u7740\uff1a\u201c\u88f4\u5927\u5148\u751f\uff01\u201d \u3000\u3000\u8fc7\u5ea6\u7684\u9707\u60ca\uff0c\u5374\u4f7f\u5f97\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u5fd8\u4e86\u5931\u671b\uff0c\u4f7f\u5f97\u201c\u795e\u624b\u201d\u6218 \u98de\u5fd8\u4e86\u6b22\u547c\uff0c\u4f7f\u5f97\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u4e5f\u5fd8\u4e86\u9ad8\u5174\uff0c\u4e5f\u4f7f\u5f97\u6a80\u6587\u742a\u5fd8\u4e86\u81ea\u5df1\u7684\u8d4c\u6ce8\u2026\u2026 \u3000\u3000\u88f4\u73cf\u7684\u9762\u5bb9\u662f\u82cd\u81ea\u7684\uff0c\u5931\u671b\u7684\uff0c\u5c31\u6b63\u5982\u6a80\u6587\u742a\u65b9\u624d\u7684\u9762\u5bb9\u4e00\u6837\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u76ee\u5149\uff0c\u5374\u8fdc\u4e0d\u5982\u6a80\u6587\u742a\u7684\u660e\u4eae\uff0c\u7528\u4e3a\u6a80\u6587\u742a\u7684\u90a3\u65f6\u7684\u60c5\u611f\u662f\u6124\u6012\u4e0e\u6068\uff0c\u800c\u6b64\u523b \u7684\u60c5\u611f\u5374\u53ea\u6709\u5931\u671b\uff0c\u5931\u671b\u3001\u2026\u2026 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5446\u671b\u7740\u4ed6\uff0c\u5374\u4e0d\u77e5\u81ea\u5df1\u662f\u8be5\u9ad8\u5174\uff0c\u6291\u6216\u662f\u8be5\u5931\u671b\uff0c\u65b9\u624d\u7684\u8d4c\u6ce8\u7eb5\u7136\u60ca\u4eba\uff0c \u4f46\u76f4\u5230\u6700\u540e\uff0c\u4ed6\u5374\u4ecd\u672a\u6709\u4e1d\u6beb\u5e0c\u671b\u88f4\u73cf\u5f97\u80dc\u7684\u5fc3\u5ff5\uff0c\u5c31\u6b63\u5982\u4e1c\u65b9\u5144\u5f1f\u7edd\u4e0d\u5e0c\u671b\u4ed6\u5931\u8d25\u800c\u6b7b\u4e00 \u6837\u3002 \u3000\u3000\u7ec8\u4e8e\u2026\u2026 \u3000\u3000\u6218\u98de\u7206\u51fa\u4e00\u58f0\u6b22\u547c\u3002 \u3000\u3000\u90a3\u98de\u8679\u3001\u5411\u4e00\u557c\u76f8\u5bf9\u4e00\u53f9\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u957f\u8eab\u800c\u8d77\uff01 \u3000\u3000\u5434\u9e23\u4e16\u98de\u8eab\u63a0\u5230\u88f4\u73cf\u8eab\u65c1\u3002 \u3000\u3000\u6a80\u6587\u742a\u98a4\u6296\u7740\u4f38\u51fa\u4e00\u53ea\u7ea4\u7ea4\u7389\u624b\uff0c\u4e24\u53ea\u9752\u8471\u7684\u738b\u6307\uff0c\u70b9\u5411\u5979\u81ea\u5df1\u7684\u4e00\u53cc\u526a\u6c34\u79cb\u6ce2\u2026\u2026 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u7709\u6307\u6311\u5904\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u742a\u513f\uff01\u201d \u3000\u3000\u624b\u638c\u4e00\u62c2\uff0c\u70b9\u4e2d\u4ed6\u7231\u5973\u8170\u95f4\u7684\u7a74\u9053\u3002\u201c\u6a80\u6587\u742a\u201d\u5624\u549b\u201c\u4e00\u58f0\uff0c\u7f13\u7f13\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u5979\u7239 \u7239\u6000\u91cc\u3002\u88f4\u73cf\u5c31\u6b63\u5982\u4e00\u9897\u660e\u661f\u7684\u964d\u843d\uff0c\u5438\u5f15\u4e86\u5168\u90e8\u7684\u76ee\u5149\uff0c\u76f4\u5230\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u4e00\u58f0\u5624\u549b\uff0c\u7fa4 \u8c6a\u65b9\u81ea\u8f6c\u8fc7\u5934\u6765\u3002\u201d\u795e\u624b\u201c\u6218\u98de\u76ee\u5149\u4e00\u626b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u65b9\u624d\u7684\u8d4c\u6ce8\uff0c\u53ef\u4e0d\u662f\u5144\u5f1f\u63d0\u51fa\u6765\u7684\uff0c \u6a80\u8001\u9556\u5934\u4f11\u8981\u5fd8\u4e86\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u5bb9\u9aa4\u53d8\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4ef0\u5929\u4e00\u7b11\u9053\uff1a\u201c\u96be\u9053\u4ec1\u4e49\u4e3a\u5148\u7684\u6a80\u5927\u82f1\u96c4\uff0c\u4e5f\u4e0d\u6015\u6c5f\u6e56\u4e2d\u7684\u803b\u7b11\uff1f\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u8f6c\u9996\u9053\uff1a\u201c\u88f4\u5144\uff0c\u6709\u4e9b\u4eba\u5f53\u771f\u662f\u6709\u773c\u65e0\u73e0\uff0c\u7adf\u4e0d\u4fe1\u5144\u53f0\u4f1a\u80dc\u5f97\u2018\u51b7\u8c37\u53cc \u6728\u2019\u2026\u2026\u201d\u88f4\u73cf\u4e00\u6b65\u4e00\u6b65\u5730\u5411\u524d\u79fb\u52a8\u7740\uff0c\u4ed6\u9762\u4e0a\u6beb\u65e0\u4efb\u4f55\u60c5\u611f\u7684\u8868\u9732\uff0c\u53ea\u662f\u7a81\u5730\u51b7\u51b7\u622a\u53e3 \u9053\uff1a\u201c\u8c01\u8bf4\u6211\u80dc\u4e86\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u5934\u4e00\u9707\uff0c\u8131\u53e3\u9053\uff1a\u201c\u88f4\u5144\u96be\u9053\u8d25\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u6b64\u523b\u5fc3\u4e2d\u7684\u60c5\u611f\uff0c\u5f53\u771f\u662f\u8c01\u4e5f\u63cf\u5199\u4e0d\u51fa\uff0c\u542c\u5230\u88f4\u73cf\u80dc\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u81ea\u662f\u5931\u671b\uff0c\u4f46\u5931\u671b\u4e2d \u53c8\u4e0d\u7981\u6709\u4e9b\u9ad8\u5174\uff1b\u542c\u5230\u88f4\u73cf\u8d25\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u5931\u671b\uff0c\u4f46\u5931\u671b\u4e2d\u5374\u4e5f\u6709\u4e9b\u9ad8\u5174\uff0c\u662f\u559c\u662f\u60b2\uff0c \u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6ee1\u5385\u7fa4\u8c6a\u7684\u5fc3\u60c5\uff0c\u6b64\u523b\u4ea6\u662f\u5ffd\u5fe7\u5ffd\u559c\uff0c\u53ea\u6709\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u542c\u5230\u88f4\u73cf\u672a\u80dc\uff0c\u4e0d\u7981\u6697\u4e2d \u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u518d\u6b21\u5bf9\u671b\u4e00\u8ddf\uff0c\u9762\u4e0a\u4ea6\u6709\u559c\u8272\uff0c\u54ea\u77e5\u88f4\u73cf\u51b7\u51b7\u53c8 \u9053\uff1a\u201c\u8c01\u8bf4\u6211\u8d25\u4e86\uff1f\u201d \u3000\u3000\u53c8\u662f\u4e00\u9635\u54c4\u4e71\uff01 \u3000\u3000\u54c4\u4e71\uff0c\u54c4\u4e71\u2026\u2026\u8fd9\u65b9\u624d\u5bc2\u9759\u5982\u6b7b\u7684\u5927\u5385\uff0c\u6b64\u523b\u7adf\u54c4\u52a8\u5f97\u6709\u5982\u5343\u519b\u4e07\u9a6c\u6b63\u5728\u53ae\u6740\u7740\u7684\u6218 \u573a\u3002\u201c\u795e\u624b\u201d\u6218\u98de\u53cc\u81c2\u4e00\u626c\uff0c\u5927\u559d\u9053\uff1a\u201c\u9759\uff0c\u5404\u4f4d\u9759\u4e00\u9759\u597d\u4e0d\u597d\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5927\u559d\u867d\u7136\u6709\u4e9b\u6548\u7528\uff0c\u4f46\u6548\u7528\u5374\u4e5f\u4e0d\u751a\u663e\u8457\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u7b49\u4e86\u8bb8\u4e45\uff0c\u7ec8\u4e8e\u53ea\u5f97\u957f \u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u5230\u5e95\u662f\u80dc\u4e86\uff0c\u6291\u662f\u8d25\u4e86\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6728\u7136\u9053\uff1a\u201c\u80dc\u4e86\uff0c\u80dc\u4e86\uff01\u201d \u3000\u3000\u6a80\u660e\u3001\u5411\u4e00\u557c\u3001\u90a3\u98de\u8679\uff0c\u5fc3\u5934\u4e00\u6c89\u2026\u2026 \u3000\u3000\u88f4\u73cf\u6728\u7136\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u8d25\u4e86\uff0c\u8d25\u4e86\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u7709\u5934\u4e00\u626c\uff0c\u5fc3\u4e2d\u6697\u9a82\uff1a\u201c\u6b64\u4eba\u96be\u9053\u7740\u75c5\u4e86\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u63a5\u53e3\u9053\uff1a\u201c\u80dc\u4e86\uff0c\u8d25\u4e86\u2026\u2026\u201d\u9762\u4e0a\u5ffd\u5730\u6cdb\u8d77\u4e00\u4e1d\u96be\u6d4b\u7684\u5fae\u7b11\u3002 \u3000\u3000\u539f\u6765\u88f4\u73cf\u65b9\u624d\u5934\u4e5f\u4e0d\u53e3\u5730\u5954\u51fa\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e4b\u5916\uff0c\u4ed6\u4e5f\u4e0d\u7ba1\u201c\u51b7\u8c37\u53cc\u6728\u201d\u662f\u5426\u6765\u4e86\uff0c\u53ea \u7ba1\u7f13\u6b65\u5782\u9996\u800c\u884c\uff0c\u751f\u50cf\u662f\u90ca\u6e38\u8e0f\u9752\uff0c\u5bfb\u89c5\u4f73\u53e5\u7684\u5e74\u8f7b\u58eb\u5b50\u4f3c\u7684\uff0c\u504f\u6fc0\u53e4\u602a\u7684\u201c\u2018\u51b7\u8c37\u53cc\u6728\u201d \u6b64\u523b\u7adf\u5bb9\u5fcd\u5730\u8ddf\u968f\u5728\u4ed6\u8eab\u540e\uff0c\u4e1d\u6beb\u6ca1\u6709\u50ac\u4fc3\u4e4b\u610f\u3002\u7ed5\u8fc7\u5e84\u95e8\u524d\u6742\u4e71\u7684\u8f66\u9a6c\uff0c\u4ed6\u53c8\u56de\u5230\u4e86\u90a3\u51b7 \u50fb\u7684\u6811\u6797\uff0c\u6668\u96fe\u65e9\u5df2\u892a\u5c3d\uff0c\u6728\u53f6\u5374\u66f4\u82cd\u7fe0\u3002\u201c\u4e94\u6708\u5929\u6c14\uff0c\u7684\u786e\u662f\u8ff7\u4eba\u7684\uff01\u201d\u4ed6\u671b\u7740\u679d\u5934\u5b9b\u556d \u7684\u9e23\u79bd\uff0c\u6697\u4e2d\u54fa\u54fa\u81ea\u8bed\uff0c\u5fc3\u5883\u663e\u5f97\u7a7a\u524d\u7684\u5e73\u9759\uff0c\u65e2\u6ca1\u6709\u9891\u4e34\u751f\u6b7b\u65f6\u7684\u60ca\u614c\uff0c\u4ea6\u4e0d\u662f\u4ece\u5bb9\u5c31\u4e49 \u65f6\u90a3\u79cd\u6177\u6168\u7684\u9547\u5b9a\uff0c\u53ea\u662f\u5e73\u9759\uff0c\u51fa\u5947\u5730\u5e73\u9759\u3002\u6b64\u523b\u82e5\u6709\u4e00\u4f4d\u5f97\u9053\u7684\u9ad8\u50e7\u770b\u5230\u4ed6\u6676\u83b9\u7684\u9762\u5bb9\uff0c \u4e00\u5b9a\u4f1a\u5f88\u6b22\u559c\u5730\u529d\u4ed6\u7688\u4f9d\u4f5b\u95e8\uff0c\u56e0\u4e3a\u4ed6\u867d\u7136\u6ca1\u6709\u53c2\u900f\u6b66\u529f\u7684\u6cd5\u95e8\uff0c\u5374\u5df2\u53c2\u900f\u4eba\u751f\u7684\u771f\u8c1b\uff0c\u5982 \u679c\u771f\u7684\u8ba9\u4ed6\u6b64\u523b\u6b7b\u53bb\uff0c\u4ed6\u5b9a\u4f1a\u53d8\u6210\u4e00\u4e2a\u6f47\u6d12\u800c\u5e38\u5e26\u5fae\u7b11\u7684\u5e7d\u7075\u3002\u201c\u51b7\u8c37\u53cc\u6728\u3001\u5bf9\u671b\u4e00\u773c\uff0c\u773c \u795e\u4e2d\u660e\u663e\u5730\u9732\u51fa\u4e86\u5fc3\u4e2d\u7684\u60ca\u5947\uff0c\u53ea\u89c1\u88f4\u73cf\u7f13\u7f13\u8f6c\u8fc7\u8eab\u6765\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u52a8\u624b\uff0c\u4e24\u4f4d\u53ef\u7b97 \u5f97\u6ee1\u610f\u4e48\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u5e72\u54b3\u4e00\u58f0\uff0c\u5411\u51b7\u5bd2\u7af9\u5fae\u4e00\u793a\u610f\uff0c\u9053\uff1a\u201c\u6b64\u5904\u5927\u4f73\uff01\u201d \u3000\u3000\u88f4\u73cf\u542b\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4e24\u4f4d\u6b64\u523b\u5df2\u53ef\u52a8\u624b\u4e86\uff01\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u5446\u4e86\u4e00\u5446\uff0c\u5450\u5450\u9053\uff1a\u201c\u6211\u53bb\u4e48\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u9053\uff1a\u201c\u81ea\u7136\u662f\u4f60\u53bb\u3002\u201d \u3000\u3000\u8fd9\u5144\u5f1f\u4e24\u4eba\u6b64\u523b\u7adf\u662f\u8c01\u4e5f\u4e0d\u613f\u53bb\u6267\u884c\u8fd9\u5728\u4ed6\u4eec\u773c\u4e2d\u770b\u6765\uff0c\u672c\u662f\u5929\u7ecf\u5730\u4e49\u7684\u590d\u4ec7\u5de5\u4f5c\uff0c\u867d \u7136\u4ed6\u4eec\u77e5\u9053\u6b64\u4e3e\u662f\u90a3\u4e48\u8f7b\u6613\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u65e0\u53ef\u5948\u4f55\u5730\u6697\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u53bb\uff0c\u6211\u53bb\uff01\u201d \u3000\u3000\u7f13\u6b65\u8d70\u5230\u88f4\u73cf\u9762\u524d\uff0c\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf7\uff01\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u76ee\u5149\u62ac\u5904\uff0c\u53ea\u89c1\u8fd9\u5c11\u5e74\u9762\u4e0a\u7684\u5fae\u7b11\u7adf\u662f\u90a3\u4e48\u6f47\u6d12\u800c\u81ea\u7136\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u6b66\u529f\u7edd\u597d\u7684 \u6b66\u6797\u9ad8\u624b\uff0c\u5728\u9762\u5bf9\u7740\u4e00\u4e2a\u65e0\u8db3\u8f7b\u91cd\u7684\u5bf9\u624b\uff1b\u82e5\u975e\u4ed6\u65e9\u5df2\u77e5\u9053\u8fd9\u5c11\u5e74\u7684\u6b66\u529f\uff0c\u6b64\u523b\u4ed6\u5fc5\u5b9a\u4f1a\u5341 \u4e8c\u4e07\u5206\u5c0f\u5fc3\u5730\u51dd\u795e\u5f85\u654c\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6b64\u523b\uff0c\u5374\u4e1d\u6beb\u6ca1\u6709\u4e0e\u4eba\u52a8\u624b\u7684\u5fc3\u60c5\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u4f60\u600e\u5730\u4e0d\u5148\u51fa\u624b\uff1f\u201d \u3000\u3000\u88f4\u73cf\u542b\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5e76\u65e0\u4e0e\u4e24\u4f4d\u52a8\u624b\u4e4b\u610f\uff0c\u800c\u662f\u4e24\u4f4d\u5411\u5728\u4e0b\u6311\u6218\u7684\uff0c\u81ea\u7136\u5148\u5e94\u8ba9\u9601\u4e0b\u5148 \u51fa\u4e8e\u624d\u662f\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u5fae\u5fae\u9894\u9996\uff0c\u4f3c\u4e4e\u6781\u4e3a\u540c\u610f\u5bf9\u65b9\u7684\u89c1\u89e3\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5c31\u5148\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u5e72\u54b3\u4e00\u58f0\uff0c\u5411\u524d\u8de8\u51fa\u4e00\u6b65\uff0c\u4e3e\u624b\u4e00\u638c\uff0c\u5411\u88f4\u73cf\u62cd\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u62cd\u51fa\u65e2\u65e0\u4e1d\u6beb\u771f\u529b\uff0c\u4ea6\u65e0\u65f6\u95f4\u90e8\u4f4d\uff0c\u7b80\u76f4\u50cf\u662f\u4e2a\u65e0\u7cbe\u6253\u91c7\u7684\u6bcd\u4eb2\uff0c\u8981\u52a8\u624b\u53bb\u6253\u4ed6 \u5e76\u4e0d\u60f3\u6253\u7684\u5b50\u5973\u3002 \u3000\u3000\u88f4\u73cf\u6115\u4e86\u4e00\u6115\uff0c\u8f7b\u8f7b\u4e3e\u624b\u683c\u53bb\uff0c\u51b7\u5bd2\u7af9\u7acb\u523b\u6536\u56de\u624b\u638c\uff0c\u653e\u4e0b\u624b\u638c\uff0c\u53c8\u62ac\u8d77\u624b\u638c\uff0c\u65e0\u7cbe\u6253 \u91c7\u7684\u53c8\u662f\uff1d\u62f3\u51fb\u53bb\u3002 \u3000\u3000\u88f4\u73cf\u540e\u9000\u4e00\u6b65\uff0c\u7adf\u7136\u8fde\u62db\u67b6\u90fd\u4e0d\u62db\u67b6\u4e86\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u5927\u58f0\u9053\uff1a\u201c\u4f60\u600e\u5730\u4e0d\u8fd8\u624b\uff1f\u201d \u3000\u3000\u88f4\u73cf\u9053\uff1a\u201c\u6211\u8fd9\u4e0d\u662f\u8fd8\u624b\uff1f\u201d \u3000\u3000\u968f\u7740\u8bdd\u58f0\uff0c\u4ed6\u4e5f\u51fb\u51fa\u4e00\u62f3\uff0c\u51b7\u5bd2\u7af9\u624b\u638c\u4e00\u53cd\uff0c\u53ea\u8981\u8f7b\u8f7b\u4e00\u642d\uff0c\u4fbf\u53ef\u642d\u4f4f\u88f4\u73cf\u7684\u8109\u95e8\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u4ec5\u4ec5\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u56de\u5934\u5c31\u8d70\uff0c\u8d70\u5230\u51b7\u67af\u6728\u9762\u524d\uff0c\u6728\u7acb\u534a\u664c\u3001\u5927\u58f0\u9053\uff1a \u201c\u4f60\u82e5\u8981\u62a5\u65e0\u7aef\u53d7\u4fae\u4e4b\u4ec7\uff0c\u4f60\u81ea\u5df1\u53bb\u52a8\u624b\u597d\u4e86\uff1a\u6211\u2026\u2026\u6211\u75b2\u5026\u5f97\u5f88\u2026\u2026\u6c14\u529b\u4e0d\u6d4e\u4e86\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u51b7\u5ced\u7684\u76ee\u5149\u4e2d\uff0c\u4f3c\u4e4e\u95ea\u8fc7\u4e00\u4e1d\u7b11\u610f\uff0c\u9894\u9996\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u53bb\uff0c\u6211\u53bb\uff01\u201d \u3000\u3000\u5927\u54e5\u8d70\u5230\u88f4\u73cf\u9762\u524d\uff0c\u7f13\u7f13\u5377\u7740\u81ea\u5df1\u7684\u8863\u8896\uff0c\u4e5f\u4e1d\u6beb\u6ca1\u6709\u51fa\u624b\u4e4b\u610f\uff0c\u88f4\u73cf\u773c\u7741\u7741\u5730\u671b\u7740\u8fd9 \u5144\u5f1f\u4e24\u4eba\uff0c\u5fc3\u5934\u4e0d\u7981\u6cdb\u8d77\u4e00\u9635\u6e29\u6696\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u7adf\u4f1a\u5728\u8fd9\u4e24\u4e2a\u51b7\u9177\u7684\u602a\u4eba\u8eab\u4e0a\uff0c\u53d1\u73b0\u4eba\u7c7b \u7684\u6e29\u60c5\uff01 \u3000\u3000\u51b7\u67af\u6728\u5377\u4e86\u534a\u5929\u8896\u5b50\uff0c\u4f3c\u4e4e\u5377\u8896\u5b50\u8fd9\u4ef6\u5de5\u4f5c\uff0c\u8fdc\u6bd4\u505a\u4ec0\u4e48\u4e8b\u90fd\u56f0\u96be\u4e9b\uff0c\u51b7\u5bd2\u7af9\u76ee\u5149\u4e2d\u5df2 \u95ee\u8fc7\u4e00\u4e1d\u7b11\u610f\uff0c\u53e3\u4e2d\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u5377\u8896\u5b50\uff0c\u4e5f\u4e00\u6837\u53ef\u4ee5\u52a8\u624b\u7684\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u56de\u5934\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u7ec8\u4e8e\u4e3e\u8d77\u624b\u638c\uff0c\u4e00\u638c\u62cd\u53bb\uff0c\u88f4\u73cf\u5446\u5446\u5730\u671b\u7740\u8fd9\u53ea\u624b\u638c\u62cd \u6765\u2026\xb7\xb7\xb7\u54ea\u77e5\u638c\u5230\u4e2d\u9014\uff0c\u51b7\u67af\u6728\u7a81\u5730\u7f29\u56de\u638c\u53bb\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u6211\u5144\u5f1f\u5b81\u613f\u5c06 \u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u4e2d\u7684\u4eba\u5168\u90fd\u6740\u6b7b\uff0c\u4e5f\u4e0d\u613f\u78b0\u4f60\u8fd9\u79cd\u4e0d\u4f1a\u6b66\u529f\u7684\u4eba\u4e00\u4e0b\uff0c\u8001\u4e8c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u8d76\u4e0a\u524d\u6765\uff0c\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff01\u201d \u3000\u3000\u51b7\u67af\u6728\u6115\u4e86\u534a\u664c\uff0c\u7a81\u5730\u53c8\u5927\u58f0\u9053\uff1a\u201c\u4f46\u2018\u51b7\u8c37\u53cc\u6728\u2019\u4e00\u4e16\u79f0\u96c4\uff0c\u4e5f\u4e0d\u80fd\u65e0\u7aef\u88ab\u4eba\u6b3a\u4fae\uff0c \u5e08\u5085\u627e\u4e0d\u5230\u627e\u5f92\u5f1f\uff0c\u6b63\u662f\u5929\u7ecf\u5730\u4e49\u4e4b\u4e8b\uff0c\u8001\u4e8c\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u4e0d\u4f4f\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\u2026\u2026\u90a3\u4e48\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u53c8\u81ea\u5446\u5446\u5730\u6115\u4e86\u51fa\u795e\uff0c\u8f6c\u5411\u88f4\u73cf\u5927\u58f0\u9053\uff1a\u201c\u4f60\u867d\u7136\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f46\u522b\u7684\u4e8b\u4f60\u603b\u4f1a\u7684 \u5427\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4e0b\u610f\u8bc6\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u51b7\u67af\u6728\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u968f\u610f\u8bf4\u51fa\u4e00\u4ef6\u4f60\u53ef\u4ee5\u6bd4\u8bd5\u7684\u4e1c\u897f\u6765\uff0c\u65e0\u8bba \u662f\u7434\u68cb\u4e66\u753b\uff0c\u6587\u6b66\u4e24\u9053\uff0c\u4ec0\u4e48\u90fd\u53ef\u4ee5\u3002\u201d \u3000\u3000\u8fd9\u5144\u5f1f\u4e24\u4eba\u6b64\u523b\u5b9e\u5df2\u6ca1\u6709\u4f24\u5bb3\u88f4\u73cf\u4e4b\u5fc3\uff0c\u662f\u4ee5\u4fbf\u63d0\u51fa\u8fd9\u79cd\u65b9\u6cd5\u6765\u3002\u5176\u5b9e\u8fd9\u5144\u5f1f\u4e24\u4eba\u751f\u6027 \u51b7\u50fb\uff0c\u9664\u4e86\u6b66\u529f\u4e4b\u5916\uff0c\u522b\u7684\u4e8b\u4e5f\u4f1a\u5f97\u4e0d\u591a\u3002 \u3000\u3000\u4f46\u88f4\u73cf\u4fef\u9996\u6c89\u601d\u4e86\u534a\u664c\uff0c\u5374\u53d1\u89c9\u81ea\u5df1\u9664\u4e86\u4e0d\u4f1a\u6b66\u529f\u4e4b\u5916\uff0c\u522b\u7684\u6280\u80fd\u4ea6\u662f\u4e00\u7a8d\u4e0d\u80fd\u901a\uff0c\u4ed6 \u5e7c\u906d\u5b64\u601c\uff0c\u6258\u5e87\u5728\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e4b\u4e2d\uff0c\u7ec8\u65e5\u4e0e\u6b66\u592b\u4e3a\u4f0d\uff0c\u81ea\u7136\u4e0d\u4f1a\u5b66\u5230\u7434\u3001\u68cb\u3001\u4e66\u3001\u753b\uff0c\u8fd9 \u4e9b\u6587\u96c5\u4e4b\u4e8b\uff0c\u53ea\u4e0d\u8fc7\u5ff5\u8fc7\u4e09\u4e24\u672c\u542f\u8499\u7684\u4e66\u7c4d\u800c\u5df2\uff0c\u7ec8\u65e5\u9664\u4e86\u505a\u4e9b\u7c97\u7b28\u7684\u5de5\u4f5c\uff0c\u4fbf\u662f\u5750\u5728\u77f3\u9636 \u4e0a\uff0c\u671b\u7740\u78a7\u7a7a\u51dd\u601d\u3002 \u3000\u3000\u5230\u540e\u6765\u79bb\u5f00\u201c\u98de\u9f99\u9556\u5c40\u201d\u540e\uff0c\u66f4\u662f\u5dc5\u7c38\u56f0\u82e6\uff0c\u6d41\u79bb\u5931\u6240\uff0c\u54ea\u91cc\u6709\u65f6\u95f4\u53bb\u5b66\u4e60\u4efb\u4f55\u77e5\u8bc6\uff0c \u54ea\u91cc\u6709\u4eba\u6559\u4ed6\uff1f \u3000\u3000\u4ed6\u5446\u5446\u5730\u51dd\u601d\u4e86\u8bb8\u4e45\uff0c\u7a81\u5730\u60f3\u5f97\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002\u4ed6\u75db\u6068\u81ea\u5df1\u7684\u65e0\u77e5\uff0c\u76f4\u6068\u5f97\u5fc3\u5934 \u9635\u9635\u53d1\u75db\u3002 \u3000\u3000\u65e0\u77e5\uff0c\u65e0\u77e5\u2026\u2026\u8fd9\u7684\u786e\u662f\u4ef6\u53ef\u6015\u7684\u4e8b\uff01\u4e5f\u96be\u602a\u8fd9\u5c11\u5e74\u75db\u6068\u81ea\u5df1\uff0c\u4f46\u662f\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u4ed6\u867d \u7136\u6ca1\u6709\u522b\u4eba\u90fd\u6709\u7684\u4e1c\u897f\u4e0e\u77e5\u8bc6\uff0c\u4f46\u662f\u4ed6\u5374\u6709\u4e00\u9897\u4f1f\u5927\u800c\u5584\u826f\u7684\u5fc3\u2014\u2014\u8fd9\u662f\u5927\u591a\u6570\u4eba\u90fd\u975e\u5e38\u6b20 \u7684\uff0c\u8fd9\u4e5f\u53ef\u8865\u5c1d\u4ed6\u6240\u6709\u7684\u7f3a\u70b9\uff0c\u4f46\u4eba\u4eec\u9762\u5bf9\u4e00\u9897\u4f1f\u5927\u800c\u6e29\u6696\u7684\u5fc3\u4e4b\u65f6\uff0c\u4fbf\u5f88\u5c11\u518d\u53bb\u7559\u5fc3\u522b \u7684\u3002 \u3000\u3000\u4ed6\u60b2\u54c0\u800c\u75db\u6068\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e0d\u7792\u4e24\u4f4d\uff0c\u5728\u4e0b\u4e00\u751f\u4e4b\u4e2d\uff0c\u5b9e\u5728\u2026\u2026\u5b9e\u5728\u2026\u2026\u201d \u7a81\u89c9\u6cea\u73e0\u5df2\u8981\u593a\u7736\u800c\u51fa\uff0c\u6e10\u6e10\u8bed\u4e0d\u6210\u58f0\u3002 \u3000\u3000\u51b7\u6854\u6728\u5446\u4e86\u4e00\u5446\uff0c\u5450\u5450\u9053\uff1a\u201c\u4f60\u96be\u9053\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u52c9\u5f3a\u6291\u5236\u4f4f\u773c\u6cea\u2014\u2014\u4e16\u4e0a\u6240\u6709\u7684\u6050\u60e7\u548c\u75db\u82e6\uff0c\u90fd\u4e0d\u4f1a\u4f7f\u8fd9\u5c11\u5e74\u5982\u6b64\u4f24\u5fc3\uff01\u6b64\u523b\u4ed6\u4f24 \u5fc3\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u63a5\u53d7\u4e86\u8fd9\u53ef\u6015\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5bf9\u671b\u4e00\u773c\uff0c\u76ee\u5149\u518d\u8f6c\u5411\u88f4\u73cf\u65f6\uff0c\u9664\u4e86\u5148\u524d\u539f\u6709\u7684\u60ca\u5947\u4e0e\u94a6\u4f69\u5916\uff0c\u53c8\u591a\u4e86\u4e00 \u4efd\u6e29\u6696\u7684\u601c\u60af\u3002 \u3000\u3000\u5fae\u98ce\u8f7b\u62c2\uff0c\u4ed6\u5144\u5f1f\u4e24\u4eba\u7a81\u5730\u76d8\u819d\u5750\u4e86\u4e0b\u6765\uff0c\u671b\u7740\u6797\u4e2d\u6d3b\u52a8\u7684\u9ed1\u5f71\uff0c\u9ed8\u9ed8\u5730\u51fa\u8d77\u795e\u6765\u3002 \u3000\u3000\u4ed6\u5144\u5f1f\u4e24\u4eba\u751f\u5e73\u6781\u4e3a\u4e0d\u5e78\uff0c\u662f\u4ee5\u4ed6\u624d\u6028\u5929\u5c24\u4eba\uff0c\u624d\u4f1a\u517b\u6210\u8fd9\u822c\u5b64\u50fb\u800c\u51b7\u9177\u7684\u4e2a\u6027\u3002 \u3000\u3000\u4f46\u4ed6\u6b64\u523b\u7a81\u7136\u53d1\u73b0\uff0c\u8fd9\u5c11\u5e74\u7684\u751f\u6027\u906d\u9047\uff0c\u7adf\u50cf\u662f\u6bd4\u4ed6\u4eec\u8fd8\u8981\u503c\u5f97\u60b2\u54c0\uff1b\u4f46\u662f\u4ed6\u5374\u9ed8\u9ed8\u5730 \u627f\u53d7\u4e86\u2014\u2014\u4ed6\u81ea\u5df1\u4e3a\u81ea\u5df1\u4f24\u5fc3\uff0c\u800c\u4e1d\u6beb\u6ca1\u6709\u5bf9\u522b\u4eba\u62b1\u6028\uff0c\u800c\u5b9e\u9645\u4e0a\uff0c\u4ed6\u5374\u662f\u5e94\u8be5\u62b1\u6028\u7684\u3002 \u3000\u3000\u88f4\u73cf\u4ea6\u81ea\u4ef0\u671b\u7740\u82cd\u7a79\uff0c\u8fd9\u523a\u9aa8\u7684\u60b2\u54c0\uff0c\u5df2\u5927\u5927\u6270\u4e71\u4e86\u4ed6\u5148\u524d\u5e73\u9759\u7684\u5fc3\u60c5\uff0c\u751f\u6b7b\uff0c\u6210\u8d25\uff0c \u5728\u4ed6\u773c\u4e2d\u770b\u6765\u867d\u662f\u90a3\u4e48\u6de1\u6cca\uff0c\u4f46\u662f\u5bf9\u81ea\u5df1\u751f\u547d\u7684\u65e0\u77e5\u2026\u2026\u5509\uff01\u4ed6\u8981\u591a\u4e48\u75db\u6614\u624d\u80fd\u63a5\u53d7\u8fd9\u4e00\u4e8b \u5b9e\uff1f \u3000\u3000\u4e00\u7247\u8fd8\u672a\u6210\u719f\u7684\u6811\u53f6\uff0c\u968f\u98ce\u98d8\u843d\u5230\u5730\u4e0a\uff0c\u4ed6\u671b\u7740\u8fd9\u7247\u6811\u53f6\uff0c\u7a81\u5730\u89c9\u5f97\u81ea\u5df1\u7684\u751f\u547d\u4e5f\u5982\u8fd9 \u7247\u6811\u53f6\u4e00\u6837\u3002 \u3000\u3000\u53ea\u8981\u8ba9\u6211\u4eab\u53d7\u4e00\u5927\u77e5\u8bc6\uff0c\u8ba9\u6211\u80fd\u4ece\u77e5\u8bc6\u7684\u5883\u57df\u5185\u53bb\u91cd\u65b0\u89c2\u5bdf\u4eba\u7c7b\u7684\u53ef\u7231\uff0c\u5b87\u5b99\u7684\u4f1f\u5927\uff0c \u90a3\u4e48\u6211\u4fbf\u53ef\u542b\u7b11\u7791\u76ee\u4e86\u3002 \u3000\u3000\u4ed6\u4ece\u5fc3\u5e95\u75db\u82e6\u5730\u5636\u558a\u7740\uff0c\u8fd9\u6c42\u77e5\u7684\u6b32\u671b\uff0c\u7adf\u662f\u90a3\u4e48\u5f3a\u70c8\uff0c\u7adf\u8fdc\u8f83\u4e16\u4e0a\u4efb\u4f55\u4e8b\u90fd\u5f3a\u70c8\u5f97 \u591a\uff0c\u5b83\u6270\u4e71\u4e86\u4ed6\u7684\u5fc3\u5883\uff0c\u4e5f\u523a\u6fc0\u8d77\u4ed6\u751f\u547d\u7684\u52c7\u6c14\u2014\u2014\u5e73\u9759\u7684\u5fc3\u5883\uff0c\u5230\u5e95\u4e0d\u662f\u5c11\u5e74\u4eba\u5e94\u6709\u7684\u5fc3 \u5883\uff0c\u5c11\u5e74\u4eba\u5e94\u6709\u7684\u662f\u98de\u626c\u7684\u751f\u547d\uff0c\u4e0e\u751f\u6d3b\u7684\u52c7\u6c14\uff01 \u3000\u3000\u66ae\u8272\u6e10\u6e10\u964d\u4e34\u2026\u2026 \u3000\u3000\u8fd9\u8001\u5c11\u4e09\u4eba\uff0c\u5728\u8fd9\u9759\u5bc2\u7684\u6797\u6728\u4e2d\u4ed4\u7ec6\u5480\u56bc\u751f\u547d\u7684\u6ecb\u5473\uff0c\u7adf\u5fd8\u4e86\u65f6\u5149\u5df2\u5728\u6084\u6084\u6d41\u53bb\u3002 \u3000\u3000\u4e00\u58f0\u5f52\u9e26\u5531\u665a\uff0c\u51b7\u5bd2\u7af9\u5fc3\u5934\u7a81\u7136\u7075\u5149\u4e00\u95ea\uff0c\u51b7\u5ced\u7684\u9762\u5bb9\uff0c\u4e5f\u7a81\u5730\u9732\u51fa\u4e86\u6ee1\u9762\u7684\u559c\u8272\u3002 \u3000\u3000\u4ed6\uff0c\u6bd5\u7adf\u60f3\u8d77\u4e86\u4e00\u4ef6\u503c\u5f97\u5174\u594b\u7684\u4e8b\uff01 \u7b2c\u4e5d\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u4e5d\u7ae0 \u3000\u3000\u65e0\u7a77\u7684\u78a7\u7a79\uff0c\u5df2\u95ea\u8000\u8d77\u4e94\u6708\u7684\u661f\u7fa4\u3002 \u3000\u3000\u4e94\u6708\u7684\u661f\u7a7a\uff0c\u6e29\u67d4\u5730\u7b3c\u7f69\u7740\u5927\u5730\uff0c\u4e00\u53cc\u6148\u6bcd\u7684\u773c\u6ce2\uff0c\u7b3c\u7f69\u7740\u5979\u6df1\u4ee5\u4e3a\u505a\u7684\u5b50\u5973\u4f3c\u7684\u3002 \u3000\u3000\u5927\u5730\u4e0a\u867d\u7136\u6709\u4e9b\u60b2\u60e8\u7684\u4e8b\uff0c\u4f46\u751f\u547d\u6bd5\u7adf\u662f\u53ef\u7231\u7684\uff0c\u5c24\u5176\u662f\u5728\u8fd9\u6e29\u6696\u800c\u53ef\u7231\u7684\u4e94\u6708\u7684\u661f\u7a7a \u4e4b\u4e0b\uff0c\u5b83\u70b9\u5316\u4e86\u4e00\u4e9b\u4e11\u964b\u7684\u5fc3\uff01\u4eba\u7c7b\uff0c\u5df2\u7ecf\u5f88\u8be5\u77e5\u8db3\u4e86\u3002\u4e3a\u4e86\u4ed6\u4eec\u53ef\u7231\u7684\u751f\u547d\u4e00\u751f\u547d\u5176\u672c\u8eab \u4e4b\u4ef7\u503c\uff0c\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u662f\u4e11\u6076\u7684\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u76ee\u5149\u4e00\u8f6c\uff0c\u6ee1\u9762\u4ff1\u662f\u559c\u8272\uff0c\u51b7\u67af\u6728\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u9ad8\u5174\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u5927\u58f0\u9053\uff1a\u201c\u6211\u4eec\u65e2\u4e0d\u80fd\u4e0e\u4ed6\u6bd4\u8bd5\u6b66\u529f\uff0c\u4e5f\u4e0d\u80fd\u767d\u767d\u9976\u8fc7\u4ed6\uff0c\u504f\u504f\u4ed6\u9664\u4e86\u4e0d\u4f1a\u6b66\u529f \u4e4b\u5916\uff0c\u522b\u7684\u4e5f\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u65e0\u7cbe\u6253\u91c7\u5730\u6c89\u58f0\u5e94\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u5fcd\u4e0d\u4f4f\u63f4\u4e86\u6447\u5934\uff0c\u5583\u5583\u81ea\u8bed\u7740\u9053\uff1a\u201c\u6211\u771f\u4e0d\u61c2\u8fd9\u6709\u4ec0\u4e48\u503c\u5f97\u9ad8\u5174\u4e4b\u5904\uff01\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u5634\u89d2\u6cdb\u8d77\u4e00\u4e1d\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4f46\u6211\u6b64\u523b\u5374\u60f3\u8d77\u4e86\u4e00\u4e2a\u4e24\u5168\u4e4b\u7b56\u3002\u201d \u3000\u3000\u4ed6\u970d\u7136\u7ad9\u8d77\u8eab\u6765\uff0c\u8f7b\u8f7b\u4e00\u62cd\u88f4\u73cf\u7684\u80a9\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u770b\u4f60\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u8a00\u8bed\u751a\u662f\u8bda \u6073\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u9a97\u4eba\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6115\u7136\u62ac\u8d77\u5934\u6765\uff0c\u5450\u5450\u9053\uff1a\u201c\u5728\u4e0b\u5e73\u751f\u672a\u8bf4\u4e00\u53e5\u865a\u8a00\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u9894\u9996\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u662f\u771f\u7684\u4ec0\u4e48\u4e5f\u4e0d\u4f1a\u7684\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u9eef\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u4f46\u662f\u6211\u5144\u5f1f\u8fd8\u662f\u8981\u4e0e\u4f60\u8d4c\u4e00\u8d4c\uff0c\u4f60\u82e5\u8f93\u4e86\uff0c\u4fbf\u5f97\u4ee3\u4f60 \u90a3\u5306\u5306\u800c\u6765\uff0c\u5306\u5306\u800c\u53bb\u7684\u5e08\u5085\uff0c\u507f\u8fd8\u6211\u4eec\u7684\u5c48\u88f4\u73cf\u80f8\u819b\u4e00\u633a\uff0c\u8fd8\u672a\u7b54\u8bdd\uff0c\u51b7\u67af\u6728\u53cc\u7709\u5fae\u76b1\uff0c \u51b7\u5bd2\u7af9\u7a81\u5730\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e14\u6162\u70b9\u9ad8\u5174\uff0c\u6b64\u4e8b\u4e5f\u672a\u89c1\u8f7b\u6613\uff0c\u65e0\u8bba\u6587\u6b66\u4e24\u9014\uff0c\u7434\u68cb\u4e66\u753b\uff0c \u4e09\u6559\u4e5d\u6d41\u7684\u6280\u80fd\uff0c\u53ea\u8981\u6211\u5144\u5f1f\u6559\u4e86\u4f60\uff0c\u800c\u4f60\u5374\u4e0d\u80fd\u5728\u6700\u77ed\u65f6\u95f4\u4e2d\u5b66\u4f1a\uff0c\u90a3\u4e48\u4f60\u53d7\u7684\u7f6a\uff0c\u7edd\u5bf9 \u8981\u6bd4\u4f60\u60f3\u50cf\u4e2d\u91cd\u5f97\u591a\u3002\u201d \u3000\u3000\u88f4\u73cf\u76ee\u5149\u4e00\u8f6c\uff0c\u77e5\u9053\u8fd9\u5144\u5f1f\u4e8c\u4eba\uff0c\u5185\u5fc3\u5b9e\u5728\u8fdc\u4e0d\u53ca\u5916\u8868\u7684\u51b7\u9177\uff0c\u6b64\u523b\u4ed6\u4eec\u7adf\u501f\u7740\u6b64\u4e8b\uff0c \u6765\u6fc0\u8d77\u81ea\u5df1\u5411\u4e0a\u7684\u5fd7\u6c14\uff0c\u8fd9\u79cd\u6e29\u60c5\uff0c\u53c8\u6709\u8c01\u80fd\u60f3\u50cf\u662f\u7531\u51b7\u9177\u6bd2\u8fa3\u7684\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5fc3\u4e2d\u53d1\u51fa\uff1f \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u65e2\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u9ad8\u5174\u3001\u5374\u53c8\u6709\u6de1\u6de1\u4e00\u4e1d\u60f6\u6050\uff0c\u4e0d\u77e5\u9053\u201c\u611a\u7b28\u201d\u7684\u81ea \u5df1\uff0c\u80fd\u4e0d\u80fd\u5b66\u5f97\u90a3\u4e9b\u65b0\u5947\u7684\u77e5\u8bc6\uff1f \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5bf9\u671b\u4e00\u773c\uff0c\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u8fd9\u79cd\u6bd4\u8bd5\u7684\u65b9\u6cd5\uff0c\u4f60\u53ef\u613f\u610f\u63a5\u53d7\uff1f\u201d \u3000\u3000\u88f4\u73cf\u63a9\u9970\u7740\u81ea\u5df1\u5fc3\u4e2d\u7684\u5404\u79cd\u60c5\u611f\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u8fd8\u4e0d\u613f\u5728\u8fd9\u4e24\u4f4d\u602a\u4eba\u9762\u524d\uff0c\u8868\u9732\u81ea\u5df1\u7684\u611f\u6fc0 \u4e0e\u6b23\u559c\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u53ea\u662f\u7f13\u7f13\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u4f46\u4ec5\u662f\u8fd9\u77ed\u77ed\u4e00\u5b57\uff0c\u5374\u5df2\u6709\u7740\u8bb8\u591a\u60c5\u611f\u6d41\u9732\u3002 \u3000\u3000\u51b7\u67af\u6728\u53cc\u81c2\u4e00\u4f38\uff0c\u8eab\u5f62\u7acb\u8d77\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4ece\u6b64\u4ee5\u540e\u4fbf\u8981\u8ddf\u7740\u6211\u4eec\u8d70\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u9894\u9996\u9053\uff1a\u201c\u5728\u4e0b\u77e5\u9053\uff01\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u6d6a\u83bd\u5c71\u5e84\u4e4b\u4e2d\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4e8b\u9700\u8981\u6599\u7406\u7684\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5b64\u8eab\u6f02\u6cca\uff0c\u65e0\u7275\u65e0\u6302\uff0c\u672c\u5f85\u8bf4\uff1a\u201c\u6ca1\u6709\uff01\u201d\u4f46\u4ed6\u8f6c\u5ff5\u4e00\u60f3\uff0c\u60f3\u8d77\u4e86\u5434\u9e23\u4e16\u4e0e\u6a80\u6587\u742a \u5173\u5207\u7684\u76ee\u5149\uff0c\u4fbf\u62b1\u62f3\u9053\uff1a\u201c\u90a3\u4e48\u4fbf\u8bf7\u4e24\u4f4d\u5728\u6b64\u7a0d\u4faf\uff0c\u5728\u4e0b\u53bb\u53bb\u5c31\u6765\u3002\u201d \u3000\u3000\u4ed6\u6025\u6b65\u5954\u53bb\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u671b\u7740\u4ed6\u7684\u8eab\u5f71\uff0c\u9762\u4e0a\u65b9\u81ea\u9732\u51fa\u4e00\u4e1d\u6e29\u60c5\u7684\u7b11\u5bb9\uff0c\u50cf\u662f\u7a81\u5730\u88ab \u6625\u98ce\u878d\u5316\u4e86\u7684\u51b0\u6cb3\u3002 \u3000\u3000\u51b7\u67af\u6728\u5fae\u7b11\u9053\uff1a\u201c\u6211\u603b\u89c9\u5f97\u8fd1\u6765\u6211\u4eec\u5b9e\u5728\u592a\u5bc2\u5bde\u4e86\u4e9b\uff0c\u5e26\u7740\u8fd9\u5b69\u5b50\u8d70\uff0c\u5b9e\u5728\u4e0d\u9519\u3002\u4ed6\u65e0 \u7275\u65e0\u6302\uff0c\u53c8\u662f\u4e2a\u7537\u5b69\uff0c\u6587\u742a\u867d\u7136\u662f\u4e2a\u597d\u5b69\u5b50\uff0c\u53ea\u53ef\u60dc\u987e\u5fcc\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u4ea6\u81ea\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u4f46\u5982\u6b64\uff0c\u6211\u4eec\u8fd8\u53ef\u4ee5\u5c06\u4ed6\u81ea\u90a3\u2018\u795e\u624b\u2019\u6218\u98de\u7684\u9634\u8c0b\u4e2d\u6551 \u51fa\u3002\u4f60\u4e14\u60f3\u60f3\uff0c\u4ed6\u4eec\u5c06\u8fd9\u6837\u4e00\u4e2a\u5c11\u5e74\u63a8\u51fa\u6765\u505a\u6c5f\u5357\u76df\u4e3b\uff0c\u8fd9\u5176\u4e2d\u5c82\u4f1a\u6ca1\u6709\u9634\u8c0b\uff1f\u6211\u770b\u8fd9\u5b69\u5b50 \u662f\u4e2a\u53ef\u9020\u4e4b\u624d\uff0c\u8ddf\u7740\u6211\u4eec\uff0c\u5fc5\u5b9a\u53ef\u4ee5\u5b66\u4f1a\u5f88\u591a\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u51dd\u601d\u534a\u664c\u9053\uff1a\u201c\u5176\u5b9e\u82e5\u8bba\u8fd9\u5b69\u5b50\u7684\u751f\u6027\uff0c\u548c\u5f85\u4eba\u5904\u4e16\u7684\u65b9\u6cd5\uff0c\u4ed6\u505a\u8d77\u7eff\u6797\u76df\u4e3b \u6765\uff0c\u5b9e\u5728\u6bd4\u522b\u4eba\u90fd\u597d\u5f97\u591a\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u957f\u53f9\u9053\uff1a\u201c\u53ea\u662f\u4ed6\u592a\u5584\u826f\u4e86\u4e9b\uff0c\u600e\u6bd4\u5f97\u8fc7\u90a3\u4e9b\u4eba\u7684\u5978\u72e1\uff01\u201d \u3000\u3000\u51b7\u67af\u6728\u7a81\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6709\u8bb8\u591a\u8be1\u8ba1\u4e0e\u9634\u8c0b\uff0c\u5bf9\u522b\u4eba\u4e5f\u8bb8\u6709\u7528\uff1b\u4f46\u662f\u5728\u4ec1\u6148 \u4e0e\u5584\u826f\u9762\u524d\uff0c\u53cd\u800c\u4f1a\u4e00\u7b79\u83ab\u5c55\uff01\u8fd9\u5c31\u50cf\u2026\u2026\u8fd9\u5c31\u50cf\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u4f3c\u4e4e\u6781\u529b\u5728\u601d\u7d22\u7740\u4e00\u4e2a\u6070\u5f53\u7684\u6bd4\u55bb\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u50cf\u51b0\u96ea\u9047\u7740\u592a\u9633\u4e00\u6837\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u5fae\u7b11\u7740\u9894\u9996\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u8fd9\u5c31\u50cf\u51b0\u96ea\u9047\u7740\u592a\u9633\u4e00\u6837\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u5230\u81ea\u5df1\u5f1f\u5144\u4e24\u4eba\uff0c\u9047\u7740\u88f4\u73cf\uff0c\u4e0d\u4e5f\u662f\u88ab\u8fd9\u5c11\u5e74\u5c06\u81ea\u5df1\u51b0\u51b7\u7684\u5fc3\u80a0\u878d\u5316\u7684\u4e48\uff1f\u4ed6 \u9762\u4e0a\u7684\u7b11\uff0c\u4e0d\u7981\u66f4\u663e\u8457\u4e86\u3002 \u3000\u3000\u8fd9\u5144\u5f1f\u4e24\u4eba\u5728\u65e0\u4eba\u65f6\u7684\u8c08\u8bdd\uff0c\u4e0e\u6709\u4eba\u65f6\u7684\u8a00\u8bed\u5b9e\u5927\u4e0d\u76f8\u540c\uff0c\u53ea\u53ef\u60dc\u88f4\u73cf\u5df2\u81ea\u53bb\u8fdc\uff0c\u6839\u672c \u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u4ed6\u5927\u6b65\u800c\u884c\uff0c\u6ee1\u6000\u5174\u594b\uff0c\u60f3\u5230\u6709\u90a3\u4e48\u591a\u65b0\u7684\u77e5\u8bc6\u4e0e\u6280\u80fd\uff0c\u4e0d\u4e45\u4f7f\u8981\u586b\u6ee1\u4ed6\u5bf9\u77e5\u8bc6\u7684\u9965 \u6e34\uff0c\u4ed6\u811a\u4e0b\u4e0d\u7981\u50cf\u751f\u4e86\u7fc5\u8180\u4e00\u6837\uff0c\u8d8a\u8d70\u8d8a\u662f\u8f7b\u5feb\u3002\u4e94\u6708\u91cc\u7684\u665a\u98ce\u5728\u4ed6\u611f\u89c9\u4e2d\u662f\u90a3\u4e48\u6e05\u65b0\uff0c\u6240 \u6709\u4e0d\u6109\u5feb\u7684\u4e8b\uff0c\u90fd\u4f3c\u4e4e\u6e10\u6e10\u53d8\u5f97\u6de1\u4e86\uff0c\u7ec8\u4e8e\u50cf\u4e00\u7f15\u8f7b\u70df\u822c\uff0c\u88ab\u665a\u98ce\u5439\u6563\u3002 \u3000\u3000\u5bf9\u4e8e\u60b2\u54c0\u3001\u4e0d\u5e78\u4e0e\u4ec7\u6068\uff0c\u4ed6\u7279\u522b\u5bb9\u6613\u5fd8\u6000\uff0c\u8fd9\u6216\u8bb8\u56e0\u4e3a\u4ed6\u8fd8\u5e74\u8f7b\uff0c\u53c8\u6709\u7740\u4e00\u9897\u4e50\u89c2\u3001\u5584 \u826f\u800c\u4ec1\u6148\u7684\u5fc3\u3002 \u3000\u3000\u4ed6\u6084\u6084\u8d70\u5165\u4e86\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\uff0c\u4ee4\u4ed6\u60ca\u5f02\u7684\u662f\uff0c\u5e84\u95e8\u5916\u6742\u4e71\u7684\u8f66\u9a6c\uff0c\u6b64\u523b\u7adf\u90fd\u7740\u4e86\u9b54\u4f3c\u7684 \u5b89\u9759\uff0c\u5927\u5385\u7684\u95e8\u524d\uff0c\u53c8\u62e5\u6324\u7740\u90a3\u4e48\u4e9b\u4eba\u3002\u4ed6\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u8fd9\u5927\u5385\u4e4b\u4e2d\uff0c\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e0d\u5e78 \u7684\u53d8\u6545\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5fc3\u4e2d\u65b9\u81ea\u6709\u4e00\u4e1d\u4e0d\u5e78\u7684\u611f\u89c9\u5347\u8d77\u7684\u65f6\u5019\uff0c\u4ed6\u7a81\u7136\u542c\u5230\u4e86\u6a80\u6587\u742a\u7684\u8bed\u58f0\uff0c\u4ed6\u867d\u662f\u5bbd \u5bb9\u800c\u5fcd\u8010\u7684\uff0c\u4f46\u6a80\u6587\u742a\u90a3\u4e9b\u65e0\u60c5\u8a00\u8bed\uff0c\u5374\u50cf\u662f\u65e0\u6570\u6839\u5c16\u9488\uff0c\u4e00\u6839\u6839\u8840\u6dcb\u6dcb\u5730\u63d2\u5728\u4ed6\u5fc3\u91cc\uff01 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u832b\u7136\u8d70\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u832b\u7136\u7ad9\u5728\u5927\u5385\u91cc\uff0c\u53ea\u89c9\u81ea\u5df1\u7684\u60c5\u611f\u5e73\u751f\u7b2c\u4e00\u6b21\u771f\u6b63\u5730\u88ab\u522b\u4eba\u4f24\u5bb3\u4e86\u2014\u2014\u56e0\u4e3a\u7231 \u60c5\u523a\u4f24\u4eba\u5fc3\uff0c\u8fdc\u6bd4\u5176\u4ed6\u4efb\u4f55\u4e8b\u90fd\u6765\u5f97\u5bb9\u6613\u2014\u2014\u8fd9\u79cd\u5185\u5fc3\u7684\u521b\u75db\uff0c\u548c\u65b9\u624d\u4ed6\u5bf9\u81ea\u8eab\u7684\u60b2\u54c0\u53c8\u7edd \u4e0d\u540c\u2014\u2014\u867d\u7136\u8fd9\u4e24\u79cd\u4ff1\u90fd\u662f\u523b\u9aa8\u94ed\u5fc3\u7684\u75db\u82e6\u3002 \u3000\u3000\u7fa4\u8c6a\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\u4ed6\u5185\u5fc3\u7684\u60c5\u611f\uff0c\u53ea\u662f\u773c\u7741\u7741\u5730\u671b\u7740\u4ed6\uff0c\u671b\u7740\u4ed6\u98a4\u6296\u7740\u7684\u5634\u5507\uff0c\u671f\u5f85\u4ed6 \u8bf4\u51fa\u7a76\u7adf\u662f\u201c\u80dc\u4e86\u201d\uff1f\u6291\u6216\u662f\u201c\u8d25\u4e86\u201d\uff01\u8fd9\u79cd\u671f\u5f85\u7684\u5fc3\u60c5\uff0c\u5728\u5411\u4e00\u557c\u3001\u6218\u98de\u7b49\u4eba\u5fc3\u91cc\uff0c\u81ea\u7136 \u66f4\u52a0\u6025\u5207\u3002 \u3000\u3000\u201c\u80dc\u4e86\uff1f\u2026\u2026\u8d25\u4e86\u2026\u2026\u201d \u3000\u3000\u88f4\u73cf\u76ee\u5149\u4e00\u8f6c\uff0c\u671b\u89c1\u4e86\u8fd9\u4e9b\u4eba\u9762\u4e0a\u7684\u6025\u5207\uff0c\u5728\u4ed6\u5fc3\u5e95\u6df1\u5904\uff0c\u7a81\u7136\u4ece\u6765\u672a\u6709\u5730\u6cdb\u8d77\u4e00\u9635\u5bf9 \u4eba\u7c7b\u8f7b\u8511\u4e0e\u8ba5\u5632\u3002 \u3000\u3000\u201c\u5927\u7ea6\u4e09\u5e74\u4e4b\u5185\uff0c\u4f60\u4eec\u8fd8\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u6115\uff0c\u53ea\u542c\u88f4\u73cf\u53c8\u81ea\u6728\u7136\u63a5\u53e3\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e5f\u4e0d\u77e5\u9053\uff01\u201d\u811a\u6b65\u79fb\u52a8\uff0c\u4f3c\u4e4e\u8981\u8f6c\u8eab\u8d70 \u51fa\u95e8\u5916\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u201c\u4e03\u5de7\u8feb\u9b42\u201d\u90a3\u98de\u8679\u9f50\u5730\u5927\u559d\u4e00\u58f0\uff0c\u4ed6\u4eec\u7b80\u77ed\u800c\u6025\u4fc3 \u5730\u95ee\u9053\uff1a\u201c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u88f4\u73cf\u7b80\u77ed\u800c\u7f13\u6162\u5730\u544a\u8bc9\u4e86\u4ed6\u4eec\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u6b21\u5947\u5f02\u7684\u8d4c\u6ce8\u540e\uff0c\u8fd9\u4e9b\u4eba\u90fd\u6709\u6743 \u77e5\u9053\u2014\u2014\u4ed6\u662f\u516c\u6b63\u7684\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u6ee1\u5385\u4e2d\u7684\u4eba\uff0c\u5168\u90e8\u5446\u4e86\uff01\u4ed6\u4eec\u7684\u8d4c\u6ce8\u867d\u7136\u5947\u5f02\uff0c\u4f46\u8fd9\u79cd\u6bd4\u8bd5\u80dc\u8d1f\u7684\u65b9\u6cd5\uff0c\u5374 \u66f4\u5947\u5999\u3002 \u3000\u3000\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u8c01\u90fd\u4e0d\u77e5\u8be5\u5982\u4f55\u5904\u7406\u6b64\u4e8b\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u5fae\u76b1\uff0c\u671b\u4e86\u671b\u684c\u4e0a\u7684\u8d4c\u6ce8\uff0c\u53c8\u671b\u4e86\u671b\u6000\u4e2d\u7684\u7231\u5973\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u6c89\u58f0 \u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4e0d\u5982\u5c06\u8fd9\u8d4c\u7ea6\u53d6\u6d88\u4e86\u5427\uff01\u684c\u4e0a\u7684\u90a3\u4e9b\u94f6\u5b50\uff0c\u5c31\u7b97\u4f5c\u6211\u9001\u4e0e\u6218\u5e84\u4e3b\u7684\u95e8\u4e0b\u597d \u4e86\u3002\u201d \u3000\u3000\u4ed6\u53c8\u671b\u4e86\u671b\u88f4\u73cf\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u90a3\u79cd\u5947\u602a\u7684\u6bd4\u8bd5\uff0c\u4e0d\u5982\u4e5f\u53d6\u6d88\u4e86\u5427\uff01\u4e0e\u6211\u4e00\u8d77\u2026\u2026\u201d \u3000\u3000\u88f4\u73cf\u9762\u5bb9\u6728\u7136\uff0c\u7f13\u7f13\u622a\u53e3\u9053\uff1a\u201c\u4e00\u8a00\u65e2\u51fa\uff0c\u65e0\u6cd5\u53d6\u6d88\u4e86\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u4e00\u8f6c\uff0c\u671b\u5230\u6a80\u660e\u6000\u4e2d\u7684\u6a80\u6587\u742a\u65f6\uff0c\u4ed6\u7684\u773c\u795e\u7a81\u5730\u53d8\u5f97\u86c7\u4e00\u822c\u7684\u72e0\u6bd2\u4e0e \u6b8b\u9177\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4e00\u8a00\u65e2\u51fa\uff0c\u600e\u53ef\u81ea\u6094\uff01\u201d \u3000\u3000\u5411\u4e00\u557c\u3001\u90a3\u98de\u8679\u4ea4\u6362\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4e07\u4e07\u53cd\u6094\u4e0d\u5f97\u201d\u9f99\u5f62\u516b\u638c\u201c\u9762\u5bb9\u5fae \u53d8\uff0c\u5434\u9e23\u4e16\u5374\u4e0e\u88f4\u73cf\u4f4e\u8bed\u8d77\u6765\uff01\u7fa4\u8c6a\u53c8\u590d\u9a9a\u4e71\u3002\u7eb7\u7eb7\u8bae\u8bba\u4e4b\u58f0\uff0c\u6709\u5982\u96f7\u9e23\u3002\u201d\u4e03\u5de7\u8ffd\u9b42\uff0c\u90a3 \u98de\u8679\u6c89\u601d\u534a\u664c\uff0c\u7a81\u5730\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u80dc\u8d1f\u672a\u5206\u4e4b\u524d\uff0c\u8d4c\u7ea6\u4e2d\u7684\u73cd\u5b9d\uff0c\u8d22\u7269\u4e0e\u5b57\u636e\uff0c\u5374\u5e94\u4ea4\u4e0e\u4e00 \u4eba\u6536\u5b58\uff0c\u8c01\u4e5f\u4e0d\u5f97\u5984\u52a8\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u659c\u779f\u5411\u4e00\u557c\u4e00\u773c\uff0c\u53c8\u9053\uff1a\u201c\u4fbf\u8fde\u8d4c\u7ea6\u4e2d\u90a3\u5341\u516b\u4f4d\u5144\u5f1f\uff0c\u4e5f\u4e0d\u80fd\u968f\u610f\u8d70\u52a8\uff0c\u5fc5\u9700\u4e0e \u73cd\u5b9d\u8d22\u7269\uff0c\u540c\u88ab\u76d1\u89c6\uff0c\u76f4\u5230\u80dc\u8d1f\u5206\u51fa\u4e4b\u540e\u3002\u201d \u3000\u3000\u4ed6\u62b1\u62f3\u56db\u63d6\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u670b\u53cb\uff0c\u5728\u4e0b\u8fd9\u4e2a\u610f\u89c1\uff0c\u53ef\u7b97\u516c\u5e73\u4e48\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u53c8\u662f\u4e00\u9635\u79c1\u8bed\uff0c\u6709\u7684\u4fbf\u4fdd\u6301\u7f04\u9ed8\uff0c\u6709\u7684\u5927\u58f0\u9053\uff1a\u201c\u5982\u6b64\u8d4c\u6cd5\uff0c\u624d\u6709\u610f\u601d\uff01\u201d \u3000\u3000\u6709\u7684\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u53ef\u7b97\u5f97\u662f\u6700\u6700\u516c\u5e73\u7684\u610f\u89c1\u4e86\u3002\u201d \u3000\u3000\u53c8\u6709\u4eba\u95f4\uff1a\uff1a\u2018\u53ea\u662f\u8fd9\u4e9b\u73cd\u5b9d\u4e4b\u7269\uff0c\u5e94\u5f53\u7f6e\u4e8e\u4f55\u5904\u5462\uff1f\u201c\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u76ee\u5149\u53c8\u81ea\u4e00\u8f6c\uff0c \u7a81\u5730\u671b\u89c1\u9ed8\u7136\u7aef\u5750\u7684\u4e1c\u65b9\u4e94\u5144\u5f1f\uff0c\u7acb\u523b\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e1c\u65b9\u94c1\u6606\u4ef2\u5a01\u9707\u6c5f\u6e56\uff0c\u2018\u98de\u7075\u5821\u2019\u66f4\u662f \u6b66\u6797\u5723\u5730\u4e4b\u4e00\uff0c\u4e1c\u65b9\u8001\u5821\u4e3b\u5a01\u9707\u5929\u4e0b\uff0c\u4ed6\u4e94\u4f4d\u5176\u4e2d\u8c01\u4e5f\u6709\u8d4c\u6ce8\uff0c\u4f46\u53ea\u662f\u968f\u610f\u6e38\u620f\u800c\u5df2\uff0c\u8fd9\u4e9b \u8d4c\u6ce8\u7f6e\u4e8e\u2018\u98de\u7075\u5821\u2019\uff0c\u4e5f\u7b97\u5f97\u662f\u6700\u5b89\u5168\u800c\u516c\u5e73\u4e86\uff0c\u5404\u4f4d\u53ef\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u95ee\u6a80\u660e\u3001\u6218\u98de\u7b49\u4eba\uff0c\u800c\u53bb\u95ee\u6ee1\u5385\u7fa4\u8c6a\uff0c\u56e0\u4e3a\u4ed6\u6df1\u77e5\u4f17\u610f\u6240\u5f52\uff0c\u5373\u4f7f\u4e0d\u540c\uff0c\u8fd9\u4e9b\u4eba\u4fbf \u4e5f\u65e0\u6cd5\u53cd\u5bf9\u4e86\u3002 \u3000\u3000\u7fa4\u8c6a\u679c\u771f\u54c4\u7136\u4f20\u8bae\uff0c\u4e1c\u65b9\u4e94\u5144\u5f1f\u957f\u8eab\u800c\u8d77\uff0c\u4f3c\u5f85\u8c22\u7edd\uff0c\u4f46\u671b\u4e86\u5174\u594b\u4e2d\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u53ea\u5f97 \u5fae\u4e00\u62f1\u624b\uff0c\u65e0\u8a00\u5730\u627f\u53d7\u4e0b\u6765\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6b64\u523b\u4ecd\u662f\u77db\u76fe\u5df2\u6781\uff0c\u4ed6\u4e0d\u7981\u6697\u7b97\u81ea\u5df1\u5f53\u771f\u662f\u5728\u4f5c\u8327\u81ea\u7f1a\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u4ed6 \u53cc\u638c\u4e00\u62cd\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u4e48\u8fd9\u4f4d\u6a80\u59d1\u5a18\u53c8\u5f53\u600e\u5730\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u4e00\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5979\u5e74\u7eaa\u5c1a\u8f7b\uff0c\u65b9\u624d\u80e1\u4e71\u6240\u8bf4\u7684\u8bdd\uff0c\u7b97\u4e0d\u5f97\u51c6\u7684\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u5bb9\u4e00\u6c89\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5979\u65e2\u662f\u80e1\u4e71\u8bf4\u8bdd\uff0c\u6a80\u8001\u9556\u5934\u65b9\u624d\u4e3a\u4f55\u4e0d\u52a0\u963b\u6b62\uff1f\u96be \u9053\u662f\u56e0\u4e3a\u6a80\u8001\u9556\u5934\u65b9\u624d\u6709\u80dc\u7b97\u5728\u63e1\uff0c\u800c\u6b64\u523b\u5df2\u65e0\u628a\u63e1\uff0c\u662f\u4ee5\u4fbf\u60f3\u63a8\u8d56\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5927\u6012\u9053\uff1a\u201c\u6570\u5341\u5e74\u6765\uff0c\u5c1a\u65e0\u4e00\u4eba\u6562\u5bf9\u8001\u592b\u5982\u6b64\u8bf4\u8bdd\uff0c\u6218\u5e84\u4e3b\uff0c\u4f60\u83ab\u8981 \u5fd8\u4e86\u8001\u592b\u5bf9\u4f60\u7684\u5ba2\u6c14\u3002\u201d \u3000\u3000\u65b9\u624d\u6218\u98de\u7684\u8a00\u8bed\uff0c\u5b9e\u5df2\u8bf4\u51fa\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u8fd9\u6b66\u6797\u4e00\u4ee3\u5927\u8c6a\u5728\u65e0\u8a00\u53ef\u5bf9\u7684\u60c5\u51b5\u4e0b\uff0c\u607c\u7f9e\u6210 \u6012\uff0c\u610f\u4ee5\u5a01\u529b\u538b\u8d77\u4eba\u6765\u3002 \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\uff0c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u8eab\u5f62\u4e2b\u52a8\uff0c\u5df2\u7ad9\u5728\u4ed6\u8eab\u4fa7\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4ef0\u592d\u7b11\u9053\uff1a\u201c\u5ba2\u6c14\uff0c\u5ba2\u6c14\u2026\u2026\u54c8\u54c8\uff0c\u563f\u563f\uff0c\u5404\u4f4d\u53ef\u66fe\u542c\u5230\u8fd9\u4f4d\u4ec1\u4e49\u4e3a\u5148\uff0c \u4e00\u8bfa\u5343\u91d1\u7684\u6a80\u8001\u9556\u5934\uff0c\u65b9\u624d\u6240\u8bf4\u7684\u662f\u4ec0\u4e48\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u8f7b\u52a8\u58f0\u4e2d\uff0c\u7acb\u523b\u4fbf\u54cd\u8d77\u4e00\u7247\u9965\u5632\u3001\u51b7\u7b11\uff0c\u8981\u77e5\u4eca\u65e5\u6765\u5230\u6b64\u95f4\u7684\uff0c\u5927\u591a\u4ff1\u662f\u7eff\u6797\u8c6a \u58eb\uff0c\u81ea\u7136\u4e0e\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u7ad9\u5728\u654c\u5bf9\u5730\u65b9\u3002\u6a80\u660e\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u53c8\u4f55\u5c1d\u4e0d\u77e5\u9053\u6b64\u95f4\u7684\u60c5 \u52bf\u3002 \u3000\u3000\u4ed6\u6d53\u7709\u76b1\u5904\uff0c\u65b9\u5f85\u7b54\u8bdd\uff0c\u54ea\u77e5\u5411\u4e00\u557c\u3001\u90a3\u98de\u8679\u53c8\u9f50\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u88f4\u5144\u7559\u6b65\uff01\u201d \u3000\u3000\u539f\u6765\u88f4\u73cf\u5728\u54c4\u4e71\u58f0\u4e2d\uff0c\u7565\u5fae\u4e0e\u5434\u545c\u4e16\u503e\u8bc9\u4e86\u4e00\u4e9b\u5fc3\u4e8b\uff0c\u81ea\u89c9\u5728\u6b64\u95f4\u5df2\u65e0\u7559\u604b\uff0c\u4fbf\u4fcf\u7136\u8f6c \u8eab\uff0c\u8d70\u5411\u5385\u95ee\u3002 \u3000\u3000\u5411\u4e00\u557c\u94c1\u62d0\u201c\u5f53\u201d\u5730\u4e00\u70b9\uff0c\u8eab\u5f62\u5c55\u5904\uff0c\u6321\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u88f4\u73cf\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u8981\u505a\u4ec0\u4e48\uff1f\u201d\u4ed6\u751f\u6027\u867d\u7136\u5bbd\u5bb9\uff0c\u4f46\u4e5f\u672a\u5fd8\u5411\u4e00\u557c\u90a3\u4e00\u62f3\u4e4b\u6068\uff0c\u53ea \u662f\u4ed6\u6781\u529b\u60f3\u4f7f\u81ea\u5df1\u6de1\u5fd8\u6b64\u4e8b\u800c\u5df2\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u6b64\u65f6\u6b64\u523b\uff0c\u795e\u60c5\u5df2\u4e0d\u6562\u9732\u51fa\u4e0d\u606d\u4e4b\u8272\uff0c\u5fae\u4e00\u547b\u541f\uff0c\u7adf\u62b1\u62f3\u4e00\u6307\uff0c\u6717\u58f0 \u9053\uff1a\u201c\u9601\u4e0b\u82e5\u662f\u8d70\u4e86\uff0c\u5728\u4e0b\u7b49\u600e\u80fd\u77e5\u9053\u80dc\u8d1f\u5982\u4f55\uff1f\u201d \u3000\u3000\u88f4\u73cf\u51b7\u51b7\u9053\uff1a\u201c\u6211\u82e5\u662f\u4e0d\u8d70\uff0c\u53c8\u600e\u80fd\u5206\u5f97\u51fa\u80dc\u8d1f\uff1f\u201d \u3000\u3000\u5411\u4e00\u557c\u5446\u4e86\u4e00\u5446\uff0c\u88f4\u73cf\u5df2\u81ea\u4ed6\u8eab\u65c1\u6084\u7136\u8d70\u51fa\u95e8\u5916\uff0c\u53ea\u6709\u4e00\u201c\u9635\u7f13\u6162\u800c\u6e05\u6670\u7684\u8bed\u58f0\uff0c\u81ea\u95e8 \u5916\u4f20\u6765\u3002\u201d\u80dc\u8d1f\u5206\u51fa\u540e\uff0c\u4f60\u4eec\u81ea\u4f1a\u5f97\u5230\u8baf\u606f\u3002\u201c\u8fd9\u5176\u95ee\u81ea\u7136\u6709\u4eba\u8981\u53bb\u963b\u62e6\uff0c\u4f46\u201d\u795e\u624b\u201c\u6218\u98de \u5374\u5389\u58f0\u559d\u9053\uff1a\u201c\u8c01\u6562\u5bf9\u76df\u4e3b\u65e0\u793c\uff1f\u201d \u3000\u3000\u4ed6\u559d\u58f0\u867d\u7136\u5a01\u4e25\u5802\u7687\uff0c\u5176\u5b9e\u5fc3\u91cc\u5374\u662f\u6068\u4e0d\u5f97\u88f4\u73cf\u65e9\u8d70\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u5446\u4e86\u534a\u664c\uff0c\u7a81\u5730\u5927\u58f0\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u603b\u8981\u5dee\u4eba\u53bb\u8ddf\u7740\u4ed6\u7684\u884c\u8e2a \u7684\uff01\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5c0f\u5f1f\u4e5f\u6709\u6b64\u610f\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u4e0d\u5982\u4f60\u6211\u5404\u6d3e\u4e00\u4eba\u8ddf\u968f\u4e8e\u4ed6\uff0c\u4e5f\u597d\u65e9\u4e9b\u77e5\u9053 \u80dc\u8d1f\u3002\u201d\u4ed6\u8f6c\u9996\u4ee5\u76ee\u5149\u53bb\u8be2\u95ee\u4e1c\u65b9\u5144\u5f1f\u7684\u610f\u89c1\uff0c\u4ed6\u5144\u5f1f\u4e94\u4eba\u53ea\u662f\u6de1\u6de1\u5730\u5fae\u5fae\u9894\u9996\uff0c\u4f46\u201c\u795e \u624b\u201d\u6218\u98de\u89c1\u5230\u4ed6\u5144\u5f1f\u5e76\u672a\u7ad9\u5728\u6a80\u660e\u8eab\u8fb9\uff0c\u5fc3\u4e2d\u5927\u5b9a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6a80\u8001\u9556\u5934\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u6a80\u660e\u51b7\u7b11\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8a00\u8bed\u5982\u6b64\u50b2\u6162\uff0c\u5f53\u771f\u4ee5\u4e3a\u8001\u592b\u5df2\u5728\u4f60\u52bf\u529b\u4e4b\u4e0b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u867d\u4e0d\u6562\u6709\u6b64\u610f\uff0c\u4f46\u76ee\u524d\u60c5\u51b5\uff0c\u5374\u4f3c\u5df2\u5982\u6b64\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u76ee\u5149\u4e00\u8f6c\uff0c\u4ea6\u81ea\u4ef0\u5929\u5927\u7b11\u8d77\u6765\uff0c\u5927\u7b11\u7740\u9053\uff1a\u201c\u8001\u592b\u8d70\u5357\u95ef\u5317\uff0c\u6570\u5341\u5e74 \u884c\u8d70\u6c5f\u6e56\uff0c\u4eca\u65e5\u96be\u9053\u4f1a\u6beb\u65e0\u51c6\u5907\u5730\u6765\u5230\u4f60\u8fd9\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u65b9\u8d77\uff0c\u6218\u98de\u7b11\u58f0\u7acb\u987f\uff0c\u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u8bed\u58f0\u987f\u8d77\uff0c\u76ee\u4e2d\u7cbe\u8292\u95ea\u52a8\uff0c\u5389\u58f0\u9053\uff1a \u201c\u6218\u98de\u4f60\u4eca\u65e5\u5982\u4f55\u5c06\u8001\u592b\u8fce\u4eba\u5e84\u6765\uff0c\u4f7f\u5f97\u5982\u4f55\u5c06\u8001\u592b\u606d\u9001\u51fa\u53bb\uff0c\u5426\u5219\u8001\u592b\u4fbf\u8981\u4f60\u8fd9\u2018\u6d6a\u83bd\u5c71 \u5e84\u2019\u8840\u6d41\u6210\u6cb3\uff0c\u5316\u4e3a\u74e6\u783e\uff01\u201d \u3000\u3000\u8fd9\u79f0\u96c4\u4e00\u4e16\u7684\u6b66\u6797\u5927\u8c6a\uff0c\u65b9\u624d\u867d\u4ee5\u8be2\u60af\u957f\u8005\u7684\u59ff\u6001\u51fa\u73b0\uff0c\u4f46\u6b64\u523b\u8bed\u58f0\u9abc\u7136\uff0c\u63b7\u5730\u6210\u58f0\uff0c \u795e\u6001\u5a01\u72c2\u65e0\u4fe6\uff0c\u7684\u786e\u6709\u5176\u6151\u4eba\u7684\u5a01\u529b\uff01 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u5bd2\u5982\u6c34\uff01 \u3000\u3000\u9662\u5916\u4eba\u5f71\u95ea\u52a8\uff01 \u3000\u3000\u4e1c\u65b9\u4e94\u5144\u5f1f\u7f13\u7f13\u957f\u8eab\u800c\u8d77\u3002 \u3000\u3000\u5927\u5385\u4e2d\u53c8\u590d\u9759\u5bc2\u5982\u6b7b\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u53ea\u624b\u638c\uff0c\u5df2\u6084\u6084\u63e1\u5728\u8170\u7554\u7684\u5251\u9798\u4e4b\u4e0a\u3002\u8fd9\u5176\u95f4\u53ea\u6709 \u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u5fae\u5fae\u4e00\u7b11\uff0c\u4e58\u4e71\u4e2d\u98d8\u7136\u800c\u53bb\uff01 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6000\u62b1\u7740\u88ab\u4ed6\u70b9\u4e2d\u201c\u9ed1\u751c\u7761\u7a74\u201d\uff0c\u5df2\u7ecf\u6c89\u6c89\u7761\u53bb\u7684\u7231\u5973\uff0c\u76ee\u5149\u51b0\u51b7\u5730\u671b \u7740\u9762\u524d\u7684\u4eba\u7fa4\uff0c\u5c06\u6ee1\u5385\u4e2d\u8fd9\u6570\u767e\u4e2a\u8349\u83bd\u7fa4\u8c6a\uff0c\u4f3c\u4e4e\u4ff1\u90fd\u672a\u66fe\u770b\u5728\u773c\u91cc\u3002\u4ed6\u51b0\u51b7\u7684\u76ee\u5149\uff0c\u53ea\u6709 \u5076\u5c14\u6295\u843d\u5728\u4ed6\u7231\u5973\u5a07\u5bb9\u4e0a\u65f6\uff0c\u624d\u5fae\u5fae\u73b0\u51fa\u67d4\u548c\u4e4b\u610f\uff1b\u4ed6\u96c4\u58ee\u7684\u8eab\u8eaf\uff0c\u867d\u7136\u7ecf\u8fc7\u8fd9\u8bb8\u591a\u5e74\u7684\u5c81 \u6708\u6d88\u78e8\uff0c\u4ecd\u7136\u6709\u5982\u94dc\u6d47\u94c1\u94f8\uff0c\u8c01\u4e5f\u731c\u4e0d\u900f\u5728\u8fd9\u7b14\u633a\u5f97\u6709\u5982\u6807\u67aa\u822c\u7684\u8eaf\u4f53\u5185\uff0c\u7a76\u7adf\u6709\u591a\u5c11\u6f5c \u529b\uff1f \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u5bb9\u9634\u6c89\uff0c\u4f46\u76ee\u5149\u4e2d\u5374\u5df2\u660e\u663e\u5730\u9732\u51fa\uff0c\u4ed6\u5fc3\u4e2d\u6b63\u5728\u601d\u8003\u7740\u4e00\u4ef6\u91cd\u5927\u7684\u51b3 \u5b9a\uff0c\u662f\u201c\u6218\u201d\uff1f\u662f\u201c\u98de\u201d\uff1f\u4ed6\u6ca1\u6709\u51b3\u5b9a\u524d\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u4e0a\u867d\u9547\u9759\uff0c\u5fc3\u4e2d\u5374\u8d77\u4f0f\u4e0d\u5b9a\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u52bf\uff0c\u654c\u5be1\u6211\u4f17\uff0c\u90a3\u4e1c \u65b9\u5144\u5f1f\u867d\u7136\u7ad9\u5728\u4ed6\u8eab\u540e\uff0c\u5374\u672a\u89c1\u4f1a\u5168\u529b\u52a9\u4ed6\u3002\u6211\u5982\u80fd\u5c06\u2018\u9f99\u5f62\u516b\u638c\u2019\u5728\u4eca\u65e5\u4e00\u6218\u4e2d\u6740\u6b7b\uff0c\u90a3 \u4e48\u6211\u4fbf\u6bcb\u5eb8\u518d\u501f\u65c1\u4eba\u4e4b\u529b\uff0c\u4fbf\u9690\u53ef\u6210\u4e3a\u6c5f\u5357\u4e4b\u76df\u4e3b\uff0c\u2018\u98de\u9f99\u9556\u5c40\u2019\u4e5f\u52bf\u5fc5\u74e6\u89e3\uff0c\u4f55\u51b5\u6211\u4eca\u65e5 \u6740\u4ed6\uff0c\u6b63\u6709\u7740\u6781\u597d\u7684\u501f\u53e3\uff0c\u6559\u5929\u4e0b\u6b66\u6797\u540c\u9053\uff0c\u90fd\u65e0\u6cd5\u975e\u96be\u4e8e\u6211\u3002\u4eca\u65e5\u6211\u82e5\u8fdf\u7591\uff0c\u518d\u8981\u5bfb\u5f97\u5982 \u6b64\u673a\u4f1a\uff0c\u53ea\u6015\u5df2\u662f\u96be\u5982\u767b\u5929\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53cc\u638c\u6e10\u6e10\u63e1\u7d27\uff0c\u76ee\u5149\u6e10\u6e10\u7280\u5229\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5374\u53c8\u4e0d\u7981\u5fd6\u9053\uff1a\u201c\u4f46\u76f4\u5230\u6b64\u523b\uff0c\u8fd9\u2018\u9f99 \u5f62\u516b\u638c\u2019\u795e\u6001\u4ecd\u5982\u6b64\u9547\u5b9a\uff0c\u4ed6\u6b66\u529f\u7eb5\u7136\u9ad8\u5f3a\uff0c\u9762\u5bf9\u7740\u8fd9\u8bb8\u591a\u654c\u624b\uff0c\u4ee5\u53ca\u9662\u5916\u7684\u5f3a\u5f13\u786c\u5f29\uff0c\u4ea6 \u662f\u96be\u64cd\u6697\u7b97\uff0c\u83ab\u975e\u4ed6\u771f\u7684\u662f\u6709\u5907\u800c\u6765\uff0c\u5728\u5e84\u5916\u65e9\u6709\u4eba\u9a6c\u57cb\u4f0f\uff1f\u201d \u3000\u3000\u4ed6\u53cc\u638c\u6e10\u6e10\u677e\u5f00\uff0c\u76ee\u5149\u4e5f\u6de1\u6de1\u9eef\u6de1\uff0c\u63a5\u7740\u53c8\u81ea\u5fd6\u9053\uff1a\u201c\u6c5f\u6e56\u4f20\u8a00\uff0c\u2018\u9f99\u5f62\u516b\u638c\u2019\u6b66\u529f\u4e4b \u6df1\uff0c\u6df1\u4e0d\u53ef\u6d4b\uff0c\u5373\u4f7f\u4ed6\u4eca\u65e5\u547d\u4e27\u6b64\u5730\uff0c\u4ed6\u82e5\u5b58\u5fc3\u4e0e\u6211\u540c\u5f52\u4e8e\u5c3d\uff0c\u6211\u53ea\u6015\u4e5f\u96be\u9003\u6bd2\u624b\uff01\u201d \u3000\u3000\u8f6c\u5ff5\u81f3\u6b64\uff0c\u4ed6\u6597\u5fd7\u66f4\u4e27\uff0c\u65b9\u5f85\u4ee5\u8a00\u8bed\u8f6c\u56de\u5c40\u52bf\uff0c\u54ea\u77e5\u5c31\u5728\u4ed6\u8a00\u8bed\u5c06\u8bf4\u672a\u8bf4\uff0c\u8fd8\u672a\u51fa\u53e3\u4e4b \u65f6\u2026\u2026\u90a3\u8fb9\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u76ee\u5149\u79fb\u52a8\uff0c\u4e00\u9762\u84c4\u52bf\u5f85\u654c\uff0c\u4e00\u9762\u89c2\u5bdf\u60c5\u52bf\uff0c\u4e00\u9762\u5728\u5fc3\u4e2d\u6697\u5730 \u601d\u5fd6\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u52bf\uff0c\u770b\u6765\u867d\u662f\u2018\u795e\u624b\u2019\u6218\u98de\u4f30\u5f3a\uff0c\u4f46\u2018\u9f99\u5f62\u516b\u638c\u2019\u4ea6\u81ea\u4e0d\u5f31\uff0c\u662f\u4ee5\u50f5\u6301\u81f3 \u6b64\uff0c\u6218\u98de\u4ecd\u4e0d\u6562\u5984\u52a8\uff0c\u4e3a\u7684\u662f\u65e2\u6015\u2018\u9f99\u5f62\u516b\u638c\u2019\u6709\u7740\u57cb\u4f0f\uff0c\u53c8\u6015\u2018\u9f99\u5f62\u516b\u638c\uff0c\u4ee5\u6df1\u4e0d\u53ef\u6d4b\u7684 \u6b66\u529f\uff0c\u5728\u52bf\u4e0d\u4e24\u7acb\u7684\u60c5\u51b5\u4e0b\uff0c\u4e0e\u4ed6\u540c\u5f52\u4e8e\u5c3d\u3002\u4f46\u662f\u6211\u5462\uff1f\u6211\u5b9e\u529b\u5168\u90fd\u6ca1\u6709\u653e\u5728\u6b64\u5904\uff0c\u5bf9\u65b9\u7684 \u76ee\u6807\u4ea6\u4e0d\u5728\u6211\uff0c\u6211\u968f\u65f6\u90fd\u53ef\u4e58\u4e71\u800c\u8d70\u3002\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u5fc3\u4e2d\u51b7\u7b11\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u6211\u4f55\u4e0d\u6311\u8d77\u6218 \u7aef\uff0c\u8ba9\u4ed6\u4eec\u6740\u5f97\u843d\u82b1\u6d41\u6c34\uff1f\u65e0\u8bba\u8c01\u80dc\u8c01\u8d1f\uff0c\u5bf9\u6211\u800c\u8a00\uff0c\u4ff1\u662f\u6709\u767e\u5229\u800c\u65e0\u4e00\u5bb3\uff01\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e2d\u7a81\u5730\u73b0\u51fa\u4e86\u6740\u673a\uff0c\u5fc3\u4e2d\u4e5f\u5df2\u4e0b\u4e86\u51b3\u5b9a\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4e00\u624b\u73af\u6301\u7231\u5973\uff0c\u4e00\u624b\u770b\u4f3c\u968f\u610f\u5782\u4e0b\uff0c\u5b9e\u5df2\u6ee1\u8574\u771f\u529b\uff0c\u76ee\u5149\u575a\u5b9a\u5f97\u6709\u5982 \u5ca9\u77f3\uff0c\u601d\u6f6e\u5374\u6709\u5982\u5ca9\u8fb9\u7684\u6d6a\u82b1\u3002 \u3000\u3000\u8fd9\u5a01\u731b\u7684\u8001\u4eba\u4ea6\u5728\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6211\u7b97\u6765\u7b97\u53bb\uff0c\u2018\u795e\u624b\u2019\u6218\u98de\u51b3\u4e0d\u6562\u5728\u6b64 \u5730\u5984\u52a8\u4e8e\u6211\uff0c\u56e0\u4e3a\u6b64\u4eba\u5fc3\u673a\u9887\u6df1\uff1b\u4e5f\u4e0d\u613f\u6210\u4e3a\u6b66\u6797\u4e2d\u7684\u7f6a\u4eba\uff01\u662f\u4ee5\u7edd\u4e0d\u6562\u5984\u505a\u7978\u9996\uff0c\u4f55\u51b5\u53c8 \u6709\u2018\u4e1c\u65b9\u4e94\u5251\u2019\u4e0e\u6211\u5171\u6765\uff1f\u8fd9\u5144\u5f1f\u4e94\u4eba\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u51fa\u8eab\u6b66\u6797\u4e16\u5bb6\uff0c\u53c8\u5404\u5f97\u771f\u4f20\uff0c\u6b66\u529f\u4e0d \u5f31\uff0c\u5728\u6b66\u6797\u4e2d\u5b9e\u6709\u4e3e\u8db3\u8f7b\u91cd\u4e4b\u52bf\u3002\u201d \u3000\u3000\u6697\u53f9\u4e00\u58f0\uff0c\u63a5\u7740\u5fd6\u9053\uff1a\u201c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u60c5\u52bf\u5374\u5927\u4e0d\u76f8\u540c\uff0c\u4ed6\u5df2\u627e\u7740\u4e86\u6781\u597d\u7684\u501f\u53e3\uff0c\u53c8\u6311 \u8d77\u4e86\u4f17\u6012\uff0c\u770b\u6765\u6211\u539f\u5148\u7684\u6253\u7b97\u5df2\u7ecf\u9519\u4e86\uff01\u53ea\u602a\u6211\u672a\u66fe\u51c6\u5907\uff0c\u4fbf\u968f\u610f\u5b64\u8eab\u800c\u6765\uff0c\u6b64\u523b\u867d\u7136\u4ee5\u8a00 \u8bed\u5413\u4f4f\u4e86\u6218\u98de\uff0c\u4f46\u5374\u600e\u80fd\u5413\u5f97\u4f4f\u2018\u4e03\u5de7\u8ffd\u9b42\uff0c\u90a3\u98de\u8679\u4e0e\u2019\u91d1\u9e21\u2018\u5411\u4e00\u557c\u5462\uff1f\u4ed6\u4e24\u4eba\u5728\u6b64\u60c5\u51b5 \u4e4b\u4e2d\uff0c\u5750\u6536\u6e14\u4eba\u4e4b\u5229\uff0c\u81ea\u7136\u662f\u5e0c\u671b\u6211\u4eec\u5927\u6740\u4e00\u573a\u7684\uff01\u201d\u4ed6\u6084\u7136\u8f6c\u76ee\u671b\u4e86\u8eab\u65c1\u7684\u201c\u5feb\u9a6c\u795e\u5200\u201d \u9f9a\u6e05\u6d0b\u4e0e\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u4e00\u773c\uff0c\u53c8\u81ea\u63a5\u7740\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u867d\u7136\u4e0d\u662f\u5eb8\u624d\uff0c\u800c\u4e14\u5fe0\u5fc3\u4e8e\u6211\uff0c \u4f46\u6b66\u529f\u5374\u975e\u597d\u624b\uff0c\u4eca\u65e5\u4e8e\u6211\uff0c\u4e95\u65e0\u5e2e\u52a9\uff0c\u51ed\u6211\u4e00\u4eba\u4e4b\u6b66\u529f\uff0c\u4eca\u65e5\u6740\u51fa\u6b64\u95f4\uff0c\u867d\u7136\u7edd\u975e\u95ee\u9898\uff0c \u4f46\u2026\u2026\u201d \u3000\u3000\u4ed6\u53c8\u5782\u9996\u671b\u4e86\u671b\u6000\u4e2d\u7684\u7231\u5973\uff0c\u6a80\u6587\u742a\u7684\u9f3b\u606f\u6c89\u6c89\uff0c\u7761\u5f97\u6b63\u719f\uff0c\u82cd\u767d\u7684\u9762\u5bb9\uff0c\u4e5f\u5df2\u6cdb\u51fa\u4e86 \u5ae3\u7ea2\u7684\u989c\u8272\u3002 \u3000\u3000\u6a80\u660e\u4e0d\u7981\u6697\u53f9\u9053\uff1a\u201c\u4f46\u662f\u8fd9\u5b69\u5b50\u2026\u2026\u5509\uff0c\u82e5\u975e\u8fd9\u5b69\u5b50\uff0c\u6211\u65e2\u4e0d\u4f1a\u6765\u6c5f\u5357\uff0c\u4e5f\u4e0d\u4f1a\u843d\u4eba\u6b64 \u523b\u7684\u5c40\u9762\uff01\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u51db\u7136\u5fd6\u9053\uff1a\u201c\u6b64\u523b\u770b\u6765\uff0c\u90a3\u98de\u8679\u5df2\u6709\u51fa\u624b\u6311\u8d77\u6218\u7aef\u4e4b\u610f\uff0c\u6d41\u8840\u5df2\u5728\u76ee\u524d\uff01 \u55ef\uff0c\u6709\u4e86\uff01\u6218\u7aef\u4e00\u8d77\uff0c\u6211\u4fbf\u601c\u742a\u513f\u4ea4\u7ed9\u4e1c\u65b9\u5144\u5f1f\uff0c\u8ba9\u4ed6\u4eec\u4e0d\u5f97\u51fa\u624b\u4fdd\u62a4\u4e8e\u5979\u3002\u54fc\u54fc\uff0c\u6b64\u95f4\u4e4b \u4eba\uff0c\u53ea\u6015\u8fd8\u6ca1\u6709\u4e00\u4eba\u613f\u610f\u62db\u60f9\u2018\u98de\u7075\u5821\u2019\u7684\uff01\u201d \u3000\u3000\u5c31\u5728\uff1a\u2018\u795e\u624b\u201c\u6218\u98de\u5984\u60f3\u8f6c\u56de\u5c40\u52bf\u7684\u8a00\u8bed\u5c06\u8bf4\u672a\u8bf4\uff0c\u8fd8\u672a\u51fa\u53e3\u4e4b\u9645\u2026\u2026\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u90a3 \u98de\u8679\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f17\u5bb6\u5144\u5f1f\uff0c\u8fd8\u4e0d\u5feb\u5c06\u8fd9\u6ee1\u53e3\u4ec1\u4e49\uff0c\u6ee1\u5fc3\u5978\u8bc8\u4e4b\u8001\u5339\u592b\u9664\u53bb\uff0c \u4e3a\u6211\u6218\u5927\u54e5\u51fa\u4e00\u53e3\u6076\u6c14\uff01\u201d \u3000\u3000\u4ed6\u5de7\u5999\u800c\u8f7b\u6613\u5730\uff0c\u5c06\u8fd9\u7b14\u5e10\u53c8\u5212\u5728\u6218\u98de\u5934\u4e0a\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u5934\u4e00\u51db\uff0c\u5239\u90a3\u95f4\u5927\u4e71\u5df2\u8d77\uff0c\u53f1\u54a4\u58f0\uff0c\u62d4\u5251\u58f0\uff0c\u63a8\u5012\u684c\u6905\u58f0\uff0c\u676f\u76cf\u7c89\u788e \u58f0\u2026\u2026\u8fd8\u6709\u4eba\u5927\u559d\u9053\uff1a\u201c\u5835\u4f4f\u95e8\u6237\uff0c\u83ab\u6559\u70b9\u5b50\u626f\u6d3b\uff01\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5927\u559d\u8fc7\u540e\uff0c\u624b\u638c\u7acb\u626c\uff0c\u4e09\u9053\u4e4c\u5149\uff0c\u95ea\u7535\u822c\u51fb\u5411\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff01 \u3000\u3000\u51e0\u4e4e\u5c31\u5728\u4ed6\u52a8\u624b\u7684\u540c\u4e00\u5239\u90a3\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u94c1\u62d0\u4e00\u70b9\uff0c\u547c\u5730\u4e00\u6756\uff0c\u5e26\u7740\u52b2\u98ce\u5411\u201c\u5feb\u9a6c \u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u5f53\u5934\u51fb\u4e0b\uff01 \u3000\u3000\u8fd9\u4e24\u4eba\u907f\u91cd\u5c31\u8f7b\uff0c\u5206\u4f5c\u4e24\u5bf9\uff0c\u53ae\u6740\u8d77\u6765\uff01\u5374\u5c06\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u7559\u4e0b\u7ed9\u201c\u795e\u624b\u201d\u6218\u98de \u4e86\u3002 \u3000\u3000\u4e5f\u5728\u8fd9\u540c\u4e00\u5239\u90a3\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u5251\u7709\u4e00\u5254\uff0c\u970d\u7136\u8f6c\u8eab\uff0c\u5c06\u6000\u4e2d\u7684\u7231\u5973\uff0c\u63a8\u5728\u4e1c\u65b9\u9707\u624b \u91cc\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8001\u592b\u5c06\u5c0f\u5973\u4ea4\u4e0e\u4e16\u5144\u4eec\u4e86\uff01\u201d \u3000\u3000\u4e0d\u5f85\u7b54\u8bdd\uff0c\u624b\u638c\u53cd\u6325\uff0c\u51fb\u843d\u4e86\u4e09\u679a\u4e71\u98de\u6765\u7684\u8896\u7bad\u3002 \u3000\u3000\u4e1c\u65b9\u9707\u6123\u4e86\u4e00\u6123\uff0c\u6696\u7389\u6e29\u9999\u5df2\u81ea\u4eba\u6000\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u5251\u7709\u5fae\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8001\u4e09\u597d\u751f\u7167\u987e\u7740\u6a80\u59d1\u5a18\uff0c\u4eca\u65e5\u4e4b\u6218\uff0c\u53ea\u6015\u6211\u5144\u5f1f\u4e5f\u4e0d\u5f97\u4e0d \u51fa\u624b\u4e86\uff01\u201d \u3000\u3000\u8fd9\u8bdd\u58f0\u4f20\u5165\u201c\u9f99\u5f62\u516b\u638c\u201d\u8033\u91cc\uff0c\u4ed6\u7cbe\u795e\u4e00\u9707\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u5927\u559d\u9053\uff1a\u201c\u6a80\u660e\u5728\u6b64\uff0c\u8c01\u6562\u524d \u6765\u52a8\u624b\uff01\u6218\u98de\uff01\u6218\u98de\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u5f53\u771f\u6709\u5982\u5929\u5916\u9739\u96f3\uff0c\u5927\u5385\u4e2d\u6570\u767e\u8c6a\u58eb\uff0c\u53ea\u89c9\u8033\u7554\u201c\u55e1\u55e1\u201d\u4f5c\u54cd\uff0c\u7adf\u65e0\u4e00\u4eba \u6562\u5984\u81ea\u51fa\u624b\u7684\uff01 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6697\u53f9\u4e00\u58f0\uff0c\u5bf9\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5df2\u6068\u5165\u523a\u9aa8\u3002\u53ea\u89c1\u4ed6\u867d\u5728\u4e0e\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u52a8 \u624b\uff0c\u4f46\u62db\u5f0f\u677e\u61c8\uff0c\u663e\u89c1\u672a\u4f7f\u51fa\u5168\u8eab\u529f\u529b\uff0c\u800c\u4e14\u6b65\u5c65\u4e4b\u95f4\uff0c\u6e10\u6e10\u5411\u95e8\u7a97\u5904\u79fb\u52a8\uff0c\u6218\u98de\u76ee\u5149\u52a8 \u5904\uff0c\u5207\u9f7f\u9053\uff1a\u201c\u597d\u4e2a\u90a3\u98de\u8679\uff0c\u4f60\u6311\u8d77\u6218\u7aef\uff0c\u7adf\u8981\u6e9c\u8d70\u4e86\uff01\u201d \u3000\u3000\u62d4\u4e86\u6298\u6247\u3001\u91cd\u91cd\u63b7\u5728\u5730\u4e0a\uff0c\u96f7\u58f0\u5927\u559d\u9053\uff1a\u201c\u5404\u4f4d\u5144\u5f1f\uff0c\u4eca\u65e5\u4e00\u6218\uff0c\u4e8b\u5173\u6c5f\u5357\u540c\u9053\u6210\u8d25\uff0c \u6c5f\u5357\u540c\u76df\uff0c\u8c01\u4e5f\u4e0d\u5f97\u5148\u8d70\u3002\u597d\u670b\u53cb\u53ea\u8981\u628a\u4f4f\u95e8\u7a97\uff0c\u4fbf\u662f\u5e2e\u4e86\u6218\u67d0\u7684\u5927\u5fd9\uff0c\u9662\u5916\u7684\u5f1f\u5144\u542c\u4e86\uff01 \u5385\u4e2d\u65e0\u8bba\u662f\u8c01\u63a0\u51fa\uff0c\u7acb\u523b\u5c06\u4e4b\u4e71\u7bad\u6740\u6b7b\uff01\u201d \u3000\u3000\u5927\u559d\u58f0\u4e2d\uff0c\u4ed6\u5df2\u7529\u53bb\u957f\u886b\uff0c\u7259\u5173\u4e00\u54ac\uff0c\u5411\u201c\u9f99\u5f62\u516b\u638c\u201d\u6251\u53bb\uff0c\u4ed6\u5df2\u51b3\u5b9a\u5fc3\u610f\uff0c\u4eca\u65e5\u65e0\u8bba \u6210\u8d25\uff0c\u4e5f\u8981\u5c06\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u62d6\u5728\u4e00\u8d77\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u8033\u4e2d\u542c\u5230\u4ed6\u7684\u559d\u58f0\uff0c\u5fc3\u5934\u5df2\u5728\u6697\u6697\u53d1\u614c\uff0c\u626c\u624b\u5288\u51fa\u4e00\u638c\uff0c\u6697\u5fd6\u9053\uff1a \u201c\u770b\u6765\u6218\u98de\u662f\u8981\u4e0e\u6211\u5e76\u80a9\u4e86\uff01\u201d \u3000\u3000\u601d\u5fd6\u4e4b\u95f4\uff0c\u62db\u5f0f\u4e00\u7f13\uff0c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u4f4e\u53f1\u4e00\u58f0\uff0c\u8eab\u5f62\u5c55\u52a8\uff0c\u95ea\u7535\u822c\u51fb\u51fa\u56db\u638c\uff01 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5fc3\u5934\u4e00\u60ca\uff0c\u95ea\u8eab\u9519\u6b65\uff0c\u540e\u9000\u4e09\u5c3a\uff0c\u7a81\u5730\u8eab\u5f62\u4e00\u7f29\uff0c\u903c\u8fdb\u201c\u516b\u5366\u638c\u201d\u6000\u91cc\u3002 \u8981\u77e5\u67f3\u8f89\u4ed7\u4ee5\u6210\u540d\u7684\u201c\u516b\u5366\u638c\u201d\u6cd5\uff0c\u4e43\u662f\u5927\u5f00\u5927\u960e\u7684\u6b63\u5b97\u62db\u5f0f\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u6b64\u523b\u4ee5\u5de7\u80dc \u76f4\uff0c\u4ee5\u67d4\u514b\u521a\uff0c\u7adf\u6070\u597d\u6210\u4e86\u201c\u516b\u5366\u638c\u201d\u7684\u514b\u661f\u3002 \u3000\u3000\u52bf\uff0c\u4ecd\u662f\u9510\u4e0d\u53ef\u5f53\u3002 \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u6b66\u529f\u672c\u4e0d\u9ad8\uff0c\u6b64\u523b\u5931\u4e86\u53f3\u624b\uff0c\u53c8\u65e0\u5175\u5203\uff0c\u6570\u62db\u8fc7\u540e\uff0c\u5df2\u662f\u9669\u8c61\u73af\u751f\uff1b\u53ea\u89c9 \u8eab\u524d\u8eab\u540e\uff0c\u4e0a\u4e0b\u5de6\u53f3\uff0c\u4ff1\u662f\u8fd9\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u7684\u62d0\u98ce\u6756\u5f71\uff0c\u5b9b\u5982\u65e0\u6570\u5ea7\u9ad8\u5c71\uff0c\u5c06\u4ed6\u538b\u5728\u4e2d \u592e\u3002 \u3000\u3000\u518d\u8fc7\u6570\u62db\uff0c\u4ed6\u751a\u81f3\u8fde\u62db\u67b6\u90fd\u65e0\u6cd5\u62db\u67b6\u4e86\uff01\u4ed7\u7740\u8eab\u5f62\u7684\u95ee\u52a8\uff0c\u867d\u4ecd\u53ef\u52c9\u5f3a\u652f\u6301\uff0c\u4f46\u5239\u90a3\u4e4b \u95f4\uff0c\u4fbf\u662f\u7c89\u8eab\u788e\u9aa8\u4e4b\u7978\uff0c\u5df2\u6210\u4e0d\u53ef\u907f\u514d\u4e4b\u4e8b\uff0c\u53ea\u89c1\u4ed6\u9762\u8272\u8d64\u7ea2\uff0c\u547c\u5438\u6025\u4fc3\uff0c\u6ee1\u5934\u6c57\u73e0\uff0c\u66f4\u662f \u65e9\u5df2\u6d94\u6d94\u800c\u843d\u3002 \u3000\u3000\u5385\u4e2d\u4e4b\u4eba\u867d\u591a\uff0c\u4f46\u5728\u8fd9\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4ecd\u662f\u53ea\u6709\u8fd9\u516d\u4eba\u52a8\u624b\u3002 \u3000\u3000\u5385\u4e2d\u684c\u6905\uff0c\u65e9\u5df2\u63a8\u5012\uff0c\u6709\u7684\u88ab\u629b\u51fa\u7a97\u5916\uff0c\u6709\u7684\u72fc\u85c9\u5730\u63a8\u5728\u56db\u4fa7\uff0c\u6ee1\u5385\u7fa4\u8c6a\uff0c\u6709\u7684\u624b\u6301\u5200 \u5251\uff0c\u5728\u4e00\u65c1\u63a0\u6218\uff0c\u6709\u7684\u7ad9\u5728\u684c\u6905\u7a97\u628a\u4e0a\uff0c\u5835\u585e\u7740\u95e8\u6237\uff0c\u53ea\u8981\u201c\u795e\u624b\u201d\u6218\u98de\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00 \u557c\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u8fd9\u4e09\u4eba\u4e4b\u4e2d\uff0c\u6709\u4efb\u4f55\u4e00\u4e2a\u9732\u51fa\u8d25\u8c61\uff0c\u4fbf\u4e0d\u77e5\u8981\u6709\u591a\u5c11\u4eba\u7acb\u523b\u51fa\u624b\uff01 \u3000\u3000\u90a3\u4e5d\u6761\u9526\u8863\u5927\u6c49\uff0c\u4e00\u9e21\u5c3e\u4e38\u5144\u5f1f\uff0c\u4ee5\u53ca\u6218\u98de\u95e8\u4e0b\u7684\u4e38\u6761\u9ed1\u8863\u5927\u5974\uff0c\u4e95\u80a9\u6324\u5728\u5927\u5385\u4e2d\u7684\u4e00 \u4e2a\u89d2\u843d\u91cc\uff0c\u8fd9\u5341\u516b\u4eba\u4f3c\u4e4e\u77e5\u9053\u81ea\u5df1\u5df2\u6210\u4e86\u201c\u8d4c\u6ce8\u201d\uff0c\u5df2\u662f\u8eab\u4e0d\u7531\u5df1\uff0c\u662f\u4ee5\u5e76\u65e0\u4e00\u4eba\u6709\u4e1d\u6beb\u51fa \u624b\u4e4b\u610f\u2014\u2014\u81ea\u7136\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u8fd9\u5341\u516b\u4eba\u51fa\u624b\u4e0e\u5426\uff0c\u4e0e\u6218\u5c40\u4ea6\u65e0\u4e1d\u6beb\u4e4b\u5f71\u54cd\u3002 \u3000\u3000\u6570\u767e\u9053\u76ee\u5149\u6700\u6ce8\u610f\u7684\uff0c\u81ea\u7136\u8fd8\u662f\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4e0e\u201c\u795e\u624b\u3001\u6218\u98de\u8fd9\u4e00\u5bf9\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684 \u80dc\u8d1f\uff0c\u4e0d\u4f46\u8981\u5f71\u54cd\u5230\u4eca\u65e5\u4e4b\u6218\uff0c\u800c\u4e14\u5fc5\u5c06\u5f71\u54cd\u5230\u5929\u4e0b\u6b66\u6797\uff0c\u8fd9\u4e00\u6218\u4e4b\u6210\u8d25\u80dc\u8d1f\uff0c\u5b9e\u662f\u6df1\u6df1\u5173 \u7cfb\u7740\u4eca\u540e\u5929\u4e0b\u6b66\u6797\u7684\u5927\u5c40\u3002\u201d\u795e\u624b\u201c\u6218\u98de\u672a\u4e0e\u6a80\u660e\u4ea4\u624b\u4e4b\u524d\uff0c\u4fbf\u5df2\u5148\u5b58\u4e86\u4e09\u5206\u754f\u60e7\u4e4b\u5fc3\u3002\u8981 \u77e5\u201d\u9f99\u5f62\u516b\u638c\u201c\u4ee5\u638c\u6cd5\u6210\u540d\uff0c\u5c11\u5e74\u95f4\u4fbf\u5d1b\u8d77\u6c5f\u6e56\uff0c\u81f3\u4eca\u5782\u4e09\u5341\u4f59\u5e74\uff0c\u58f0\u540d\u4e4b\u76db\uff0c\u4e00\u65f6\u65e0\u4e24\uff0c \u4ed6\u4eb2\u81ea\u4e0e\u4eba\u52a8\u624b\u7684\u6b21\u6570\u5e76\u4e0d\u591a\uff0c\u4f46\u5374\u672a\u5c1d\u8d25\u8fc7\u4e00\u6b21\uff0c\u800c\u201d\u795e\u624b\u201c\u6218\u98de\u867d\u5df2\u6210\u540d\u9887\u4e45\uff0c\u4e0e\u4e4b\u76f8 \u6bd4\uff0c\u5374\u4ecd\u4e0d\u8fc7\u662f\u540e\u8f88\u800c\u5df2\u3002\u8fd9\u79cd\u6839\u6df1\u8482\u56fa\u7684\u89c2\u5ff5\uff0c\u4fbf\u4f7f\u5f97\u201d\u795e\u624b\u201c\u6218\u98de\u4e0d\u7531\u81ea\u4e3b\u5730\u201d\u957f\u4e86\u4ed6 \u4eba\u5fd7\u6c14\uff0c\u706d\u4e86\u81ea\u5df1\u5a01\u98ce\u201c\u3002\u4f46\u8fd9\u8349\u83bd\u5927\u8c6a\u5374\u5df2\u6709\u4e0d\u77e5\u591a\u5c11\u6b21\u7684\u4ea4\u624b\u7ecf\u9a8c\uff0c\u5c06\u8fd9\u4efd\u754f\u60e7\u9690\u85cf\u8d77 \u6765\uff0c\u4ed6\u53ea\u662f\u4efd\u5916\u5730\u52a0\u4e86\u51e0\u5206\u8c28\u614e\uff0c\u6682\u65f6\u4e0d\u6c42\u6709\u529f\uff0c\u4f46\u6c42\u65e0\u8fc7\u3002\u662f\u4ee5\u4e24\u4eba\u752b\u4e00\u4ea4\u624b\uff0c\u201d\u795e\u624b \u201c\u6218\u98de\u4fbf\u4ee5\u4e25\u5bc6\u7684\u5b88\u52bf\uff0c\u62a4\u4f4f\u5168\u8eab\uff0c\u4f46\u662f\u6570\u5341\u9053\u638c\u98ce\uff0c\u88f9\u7740\u4e00\u4e2a\u7d2b\u8272\u7684\u4eba\u5f71\uff0c\u5f53\u771f\u53ef\u79f0\u662f\u6ef4 \u6c34\u96be\u5165\u3002\u201d\u9f99\u5f62\u516b\u638c\u201c\u8eab\u5f62\u98d8\u52a8\uff0c\u638c\u52bf\u8f7b\u7075\uff0c\u4ed6\u9ad8\u5927\u7684\u8eab\u6750\uff0c\u7adf\u6709\u7740\u51fa\u5947\u5730\u7075\u5de7\uff0c\u4f46\u638c\u52bf\u95f4 \u7684\u529b\u9053\uff1a\u5374\u8fdc\u4e0d\u5982\u201d\u795e\u624b\u201c\u6218\u98de\u60f3\u8c61\u4e2d\u7684\u96c4\u6d51\u6c89\u91cd\uff0c\u62db\u5f0f\u95f4\u7684\u53d8\u5316\uff0c\u4e5f\u8fdc\u4e0d\u5982\u795e\u624b\u610f\u5ff5\u4e2d\u7684 \u7280\u5229\u5947\u8be1\u3002\u8fd9\u6b66\u6797\u5927\u8c6a\u7684\u638c\u6cd5\uff0c\u4ec5\u4ec5\u4e0d\u8fc7\u53ea\u5360\u4e86\u201d\u8f7b\u7075\u201c\u4e24\u5b57\u800c\u5df2\u3002\u8fd9\u5b9e\u662f\u5927\u51fa\u6218\u98de\u610f\u6599\u4e4b \u5916\uff0c\u4e5f\u5927\u51fa\u7fa4\u8c6a\u4e4b\u5916\u3002\u53ea\u89c1\u8fd9\u4e24\u6761\u8eab\u5f71\u6e38\u79bb\u95ea\u52a8\uff0c\u867d\u7136\u751a\u662f\u597d\u770b\uff0c\u4f46\u5374\u672a\u6709\u4e00\u62db\u771f\u6b63\u7684\u63a5 \u89e6\u2014\u2014\u90a3\u79cd\u5728\u7fa4\u8c6a\u610f\u5ff5\u95f4\u5fc5\u6709\u7684\u3001\u60ca\u5fc3\u52a8\u9b44\u7684\u63a5\u89e6\u3002\u201d\u76db\u540d\u4e4b\u4e0b\u7684\u2018\u9f99\u5f62\u516b\u638c\u2019\uff0c\u96be\u9053\u53ea\u662f \u5f92\u62e5\u865a\u540d\u800c\u5df2\u4e48\uff1f\u201c\u201d\u795e\u624b\u201c\u6218\u98de\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u80c6\u6c14\u9aa4\u76db\uff0c\u6c89\u8170\u5750\u9a6c\uff0c\u53cc\u638c\u84e6\u5730\u7ffb\u51fa\uff0c\u5de6\u638c\u5728 \u524d\uff0c\u53f3\u638c\u5728\u540e\uff0c\u4f46\u638c\u5230\u4e2d\u9014\u53f3\u638c\u5374\u5ffd\u5730\u81ea\u5de6\u638c\u4e0b\u7a7f\u51fa\uff0c\u547c\u5730\u4e00\u80a1\u638c\u98ce\uff0c\u62cd\u5411\u6a80\u660e\u53f3\u4e0b\u808b\u8110\u4e0b \u4e8c\u5bf8\u95f4\u7684\u6c14\u95e8\u201d\u5546\u66f2\u5927\u7a74\u201c\u3002\u8fd9\u4e00\u638c\u4e0d\u4f46\u529b\u9053\u5a01\u731b\uff0c\u800c\u4e14\u65f6\u95f4\u3001\u90e8\u4f4d\u3001\u53d8\u5316\uff0c\u4ff1\u662f\u4e0a\u4e58\u4e4b \u4f5c\uff0c\u6b63\u662f\u201d\u795e\u624b\u201c\u6218\u98de\u5f97\u610f\u638c\u6cd5\u201d\u98ce\u840d\u638c\u201c\u4e2d\u7684\u715e\u7740\u3002\u539f\u6765\u6218\u98de\u4e4b\u638c\u6cd5\uff0c\u4ea6\u662f\u672c\u7740\u6c5f\u752b\u6b66\u529f \u6e90\u6d41\uff0c\u4ee5\u8f7b\u7075\u53d8\u5316\u4e3a\u4e3b\uff0c\u53ea\u662f\u4ed6\u7980\u8d4b\u6781\u9ad8\uff0c\u662f\u4ee5\u638c\u529b\u4ea6\u6781\u6c89\u539a\u3002\u201d\u9f99\u5f62\u516b\u638c\u201c\u6a80\u660e\u8eab\u5f62\u4e00 \u8f6c\uff0c\u659c\u98d8\u6570\u5c3a\uff0c\u7adf\u4f3c\u4e0d\u6562\u6321\u5176\u950b\u9510\uff0c\u6218\u98de\u5927\u559d\u4e00\u58f0\uff0c\u8fde\u73af\u8fdb\u6b65\uff0c\u5de6\u638c\u76f4\u5288\uff0c\u53f3\u638c\u6a2a\u5207\uff0c\u5237\u5237 \u53c8\u81ea\u62cd\u51fa\u4e24\u638c\uff0c\u201d\u5f31\u6c34\u53cc\u840d\u201c\uff0c\u5206\u51fb\u6a80\u660e\u201d\u5206\u6c34\u201c\u3001\u201d\u80a9\u4e95\u201c\u4e8c\u7a74\u3002\u6a80\u660e\u8eab\u8eaf\u4e00\u62e7\uff0c\u7684\u6e9c\u6e9c \u8f6c\u81f3\u6218\u98de\u53f3\u4fa7\uff0c\u987b\u53d1\u98d8\u52a8\uff0c\u5e76\u6307\u5982\u5251\uff0c\u8f7b\u70b9\u6218\u98de\u53f3\u4e73\u4e4b\u4e0a\u4e00\u5bf8\u516d\u5206\u95f4\u7684\u201d\u4e0a\u8840\u6d77\u7a74\u201c\u3002\u8fd9\u4e00 \u62db\u867d\u4e5f\u662f\u8fde\u62db\u5e26\u6253\u7684\u5999\u7740\uff0c\u4f46\u5374\u4ecd\u4f3c\u4e0d\u6562\u6b63\u9762\u4ea4\u624b\u3002\u201d\u795e\u624b\u201c\u6218\u98de\u80c6\u6c14\u66f4\u76db\uff0c\u96c4\u5fc3\u5927\u8d77\uff0c\u62e7 \u8170\u7529\u624b\uff0c\u638c\u98ce\u5982\u5c71\uff0c\u7adf\u65bd\u51fa\u201d\u5927\u6454\u7891\u624b\u201c\u8fd9\u79cd\u4ee5\u786c\u6253\u786c\uff0c\u4ee5\u5f3a\u6253\u5f3a\u7684\u62db\u5f0f\u3002\u201d\u9f99\u5f62\u516b\u638c\u201c\u66f2 \u65f6\u7f29\u624b\uff0c\u80f8\u8179\u4e00\u5438\uff0c\u201d\u5530\u201c\u5730\uff0c\u53c8\u81ea\u540e\u9000\u4e09\u5c3a\uff01\u8fd9\u4e09\u62db\u4e00\u8fc7\uff0c\u7fa4\u8c6a\u4e0d\u7981\u9707\u5929\u822c\u559d\u8d77\u5f69\u6765\u3002\u6709 \u4eba\u770b\u5f97\u5174\u9ad8\u91c7\u70c8\uff0c\u7adf\u653e\u58f0\u5927\u547c\u9053\uff1a\u201c\u6218\u513f\u5b50\u786c\u662f\u8981\u5f97\uff01\u201d\u539f\u6765\u6b64\u4eba\u6b63\u662f\u5ddd\u4e2d\u7684\u72ec\u884c\u5927\u76d7\u201c\u5df4 \u5c71\u864e\u201d\u9ec4\u5927\u65a7\u3002\u4ed6\u65e9\u5df2\u770b\u5f97\u6280\u75d2\uff0c\u6068\u4e0d\u5f97\u81ea\u5df1\u4e0b\u53bb\u4e0e\u8fd9\u5f92\u6709\u865a\u540d\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\u8fc7\u8fc7\u624b\uff0c\u4ee5 \u81ea\u5df1\u5927\u6740\u5927\u780d\u7684\u62db\u5f0f\uff0c\u5c06\u6a80\u660e\u903c\u5f97\u6ee1\u5385\u4e71\u7a9c\u3002 \u3000\u3000\u6309\u5251\u800c\u7acb\u7684\u4e1c\u65b9\u5144\u5f1f\uff0c\u5404\u81ea\u4ea4\u6362\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u7d20\u6027\u4e0d\u518d\u53bb\u770b\u8fd9\u4e00\u5bf9\u4ea4\u624b\uff0c\u4f3c\u4e4e\u662f\u5bf9\u201c\u9f99 \u5f62\u516b\u638c\u201d\u6a80\u660e\u751a\u4e3a\u5931\u671b\uff0c\u53c8\u4f3c\u4e4e\u662f\u660e\u77e5\u201c\u9f99\u5f62\u516b\u638c\uff0c\u5df2\u81ea\u7a33\u64cd\u80dc\u7b97\uff0c\u65e9\u4ee5\u518d\u4e5f\u4e0d\u9700\u770b\u4e86\u3002\u4e1c \u65b9\u5251\u76ee\u5149\u52a8\u5904\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u9f9a\u6e05\u6d0b\u53ea\u6015\u5df2\u4e0d\u884c\u4e86\uff0c\u6211\u53bb\u4ee3\u4ed6\u63a5\u4e0b\u6765\uff01\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u8f7b\u8f7b\u6447\u5934\uff0c\u8033\u8bed\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u5c40\uff0c\u5207\u5207\u4e0d\u53ef\u8f7b\u4e3e\u5984\u52a8\uff0c\u514d\u5f97\u5f15\u8d77\u6df7\u4e71\u3002\u4f60\u770b\u6a80\u5927 \u53d4\u6b64\u523b\u660e\u660e\u53ef\u4ee5\u5728\u6570\u62db\u4e4b\u5185\u89e3\u51b3\u2018\u795e\u624b\u2019\u6218\u98de\uff0c\u4f46\u4ed6\u8001\u4eba\u5bb6\u4e5f\u4e0d\u6562\u65bd\u5c55\u51fa\u771f\u5b9e\u6b66\u529f\uff0c\u5c31\u662f\u6015 \u6218\u98de\u8d25\u4e86\uff0c\u8fd9\u4e9b\u4eba\u4fbf\u8981\u4e00\u8d77\u51fa\u624b\uff0c\u90a3\u65f6\u6218\u5c40\u4e00\u8d25\uff0c\u4e0d\u4f46\u6b7b\u4f24\u5fc5\u591a\uff0c\u5c31\u8fde\u6a80\u5927\u53d4\u8fd9\u79cd\u6b66\u529f\uff0c\u90fd \u672a\u89c1\u80fd\u5168\u8eab\u800c\u9000\uff0c\u4f55\u51b5\u4f60\u6211\uff01\u201d \u3000\u3000\u4e1c\u65b9\u5251\u5fae\u4e00\u6c89\u541f\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u96be\u9053\u4ed6\u7684\u6b66\u529f\uff0c\u6bd4\u4f60\u6211\u8fd8\u9ad8\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u6b64\u4eba\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4e0e\u4eba\u8fc7\u624b\uff0c\u4ece\u672a\u65bd\u51fa\u5168\u529b\uff0c\u5c31\u8fde \u7239\u7239\u90fd\u770b\u4e0d\u51fa\u4ed6\u771f\u5b9e\u6b66\u529f\u7684\u6df1\u6d45\uff01\u201d \u3000\u3000\u8bed\u58f0\u987f\u5904\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u8f7b\u8f7b\u53c8\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u4ee5\u8fd9\u4e00\u62db\u2018\u8131\u888d\u6361\u7532\u2019\u907f\u5f00\u6218\u98de\u4e00\u7740\u62a2\u653b\u7684 \u8eab\u6cd5\uff0c\u4e0e\u666e\u901a\u8eab\u6cd5\u53ef\u6709\u4ec0\u4e48\u4e0d\u540c\u4e4b\u6cd5\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u5251\u5fae\u4e00\u601d\u7d22\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f3c\u4e4e\u6ca1\u6709\u4ec0\u4e48\u4e24\u6837\uff0c\u53ea\u662f\u4eff\u4f5b\u6bd4\u522b\u4eba\u8f7b\u5feb\u5f97\u591a\uff01\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u8fd1\u5e74\u6b66\u529f\u5927\u6709\u8fdb\u5883\u3002\u4f46\u4f60\u773c\u5149\u3001\u7ecf\u9a8c\uff0c\u6bd5\u7adf\u8fd8\u662f\u5dee\u5f97 \u591a\uff01\u4f60\u7adf\u770b\u4e0d\u51fa\u4ed6\u5728\u8fd9\u4e00\u62db\u666e\u901a\u4e0b\u4e58\u7684\u62db\u5f0f\u4e2d\uff0c\u5df2\u53c2\u5408\u4e86\u6700\u6700\u4e0a\u4e58\u7684\u6b66\u529f\u2018\u79fb\u5f62\u6362\u4f4d\u2019\u4e48\uff01 \u80fd\u5c06\u4e0a\u4e58\u6b66\u529f\u53c2\u5408\u5230\u4e0b\u4e58\u6b66\u529f\u4f5c\u7528\uff0c\u800c\u53c8\u7528\u5f97\u5982\u6b64\u4e0d\u7740\u75d5\u8ff9\uff0c\u5929\u8863\u65e0\u7f1d\u7684\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6015 \u53ea\u6709\u4ed6\u4e00\u4eba\u4e86\uff01\u201d \u3000\u3000\u4e1c\u65b9\u5251\u9762\u4e0a\u5fae\u73b0\u60ca\u5947\u4e4b\u8272\uff0c\u76f4\u7740\u4e24\u773c\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u81ea\u95ee\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6cd5\uff0c\u4ed6\u96be\u9053\u662f\u5929\u4e0b\u6b66 \u529f\u7b2c\u4e00\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u6447\u5934\u9053\uff1a\u201c\u6211\u5e76\u672a\u8bf4\u4ed6\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u53ea\u662f\u5355\u6307\u8fd9\u4e00\u9879\u6210\u5c31\u800c\u8a00\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u6d77 \u5185\u6d77\u5916\uff0c\u6b66\u529f\u9ad8\u8fc7\u4ed6\u7684\u4eba\uff0c\u867d\u6709\u4e0d\u5c11\uff0c\u4f46\u5374\u65e0\u4eba\u7814\u7a76\u8fd9\u79cd\u5c06\u4e0a\u4e58\u6b66\u529f\u9690\u85cf\u4e8e\u4e0b\u4e58\u62db\u5f0f\u4e4b\u4e2d\u7684 \u529f\u592b\uff0c\u56e0\u6b64\u5f7c\u6b64\u4e4b\u95f4\u7684\u5904\u5883\u4e0d\u540c\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u7ad9\u5728\u4ed6\u8eab\u65c1\u7684\u4e1c\u65b9\u9707\u5374\u7a81\u5730\u51b7\u51b7\u622a\u53e3\u9053\uff1a\u201c\u8fd9\u79cd\u4f5c\u4f2a\u7684\u529f\u592b\uff0c\u4e0d\u5b66\u4e5f\u7f62\uff01\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u542b\u7b11\u671b\u4e86\u4ed6\u4e09\u5f1f\u4e00\u773c\uff0c\u53c8\u671b\u4e86\u671b\u4ed6\u4e09\u5f1f\u6000\u4e2d\u7684\u6a80\u6587\u742a\uff0c\u53cc\u76ee\u4e4b\u4e2d\uff0c\u7a81\u5730\u95ea\u8fc7\u4e00\u4e1d \u5947\u5f02\u7684\u5149\u8292\uff0c\u53e3\u4e2d\u6df1\u5e94\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u4f60\u8a00\u8bed\u4e2d\u4ee5\u540e\u5207\u5207\u4e0d\u53ef\u8f7b\u6162\u4e86\u6b66\u6797\u524d\u8f88\uff01\u201d \u3000\u3000\u8fd9\u51fa\u81ea\u540d\u95e8\u3001\u950b\u8292\u521d\u9732\u7684\u5144\u5f1f\u4e09\u4eba\u79c1\u8bed\u4e4b\u95f4\uff0c\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u53f3\u80a9\u5df2\u88ab\u5411\u4e00\u557c\u94c1\u62d0 \u626b\u4e2d\u3002 \u3000\u3000\u4ed6\u5fcd\u75db\u8f7b\u547c\u4e00\u58f0\uff0c\u4ecd\u7136\u6323\u624e\u5730\u82e6\u6597\u4e0b\u53bb\u3002 \u3000\u3000\u4e1c\u65b9\u5251\u5251\u7709\u5fae\u5254\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u518d\u8fdf\u5239\u90a3\uff0c\u9f9a\u6e05\u6d0b\u4fbf\u8981\u906d\u90a3\u2018\u91d1\u9e21\u2019\u5411\u4e00\u557c\u7684\u6bd2\u624b\u4e86\uff01\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u8f7b\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u770b\u6765\u6211\u5144\u5f1f\u53ea\u6709\u51fa\u624b\u4e86\uff0c\u65e0\u8bba\u60c5\u52bf\u5982\u4f55\uff0c\u6211\u4eec\u603b\u4e0d\u80fd\u773c\u89c1 \u9f9a\u6e05\u6d0b\u6b7b\u5728\u522b\u4eba\u7684\u624b\u4e0b\uff01\u201d \u3000\u3000\u7ad9\u5728\u4ed6\u8eab\u540e\u7684\u4e1c\u65b9\u6e56\u65e9\u5df2\u4e0d\u8010\uff0c\u6b64\u523b\u542c\u5230\u4ed6\u5927\u54e5\u7684\u8a00\u201c\u8bed\uff0c\u7cbe\u795e\u4e0d\u89c9\u4e00\u632f\uff0c\u4e1c\u65b9\u5251\u4ea6\u81ea \u5174\u594b\u4ed6\u8bf4\u9053\uff1a\u201c\u82e5\u8981\u51fa\u624b\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\u3002\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u9762\u8272\u4e00\u6c89\uff0c\u5251\u7709\u8f69\u52a8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e0a\uff01\u201d \u3000\u3000\u201c\u4e0a\u201d\u5b57\u4e4b\u58f0\u672a\u843d\uff0c\u4e00\u9635\u201c\u545b\u561f\u201d\u9f99\u541f\u4e4b\u58f0\u5df2\u81ea\u54cd\u8d77\u3002 \u3000\u3000\u5251\u5149\u7f2d\u7ed5\uff0c\u5251\u6c14\u68ee\u68ee\uff0c\u4e1c\u65b9\u94c1\u3001\u4e1c\u65b9\u5251\u3001\u4e1c\u65b9\u6c5f\u3001\u4e1c\u65b9\u6e56\uff0c\u5144\u5f1f\u56db\u4eba\uff0c\u4e00\u8d77\u62d4\u51fa\u5251\u6765\uff01 \u3000\u3000\u5de6\u9762\u89d2\u843d\u95f4\uff0c\u53e6\u5341\u4f59\u5927\u6c49\uff0c\u8eab\u5f62\u5c55\u52a8\uff0c\u5404\u6301\u5200\u5251\uff0c\u8dc3\u4e86\u51fa\u6765\uff01 \u3000\u3000\u53f3\u9762\u89d2\u843d\u91cc\uff0c\u4ea6\u6709\u5341\u6570\u9053\u5bd2\u5149\u66b4\u957f\uff01 \u3000\u3000\u201c\u5df4\u5c71\u864e\u201d\u9ec4\u5927\u65a7\u73af\u773c\u4e00\u5f20\uff0c\u53cd\u624b\u81ea\u8170\u95f4\u62d4\u51fa\u4e00\u67c4\u6448\u9547\u5de8\u65a7\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u95ea\u52a8\uff0c\u89c1\u5230\u6df7\u4e71\u4e4b\u5c40\uff0c\u5df2\u6210\u4e86\u4e0d\u53ef\u907f\u514d\u4e4b\u52bf\uff0c\u7a81\u5730\u4e00\u58f0\u6e05\u5578\uff0c\u53cc\u638c\u4e00 \u6487\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u62db\u201c\u53cc\u649e\u624b\u201d\u65b9\u81ea\u51fb\u51fa\uff0c\u53ea\u89c1\u6a80\u660e\u53cc\u638c\u5df2\u81ea\u8fce\u6765\uff1b\u638c\u51e4\u4e4b\u5f3a\u70c8\uff0c\u65e0\u4e0e\u4f26 \u6bd4\u3002\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u6697\u9053\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d \u3000\u3000\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u604d\u7136\u77e5\u9053\u4e86\u6a80\u660e\u65b9\u624d\u4e0d\u8fc7\u53ea\u662f\u505a\u4f5c\u800c\u5df2\uff0c\u53ea\u662f\u4ed6\u77e5\u9053\u5f97\u5374\u5df2\u5acc\u592a\u8fdf \u4e86\u3002 \u3000\u3000\u201c\u556a\u201d\u5730\u4e00\u58f0\uff0c\u56db\u638c\u76f8\u4ea4\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u53ea\u89c9\u5168\u8eab\u4e00\u9707\uff0c\u518d\u4e5f\u7ad9\u4e0d\u7a33\u8eab\u5f62\uff0c\u7adf\u88ab\u6a80\u660e\u8fd9 \u4e00\u638c\u9707\u5f97\u6a2a\u98de\u4e94\u5c3a\uff01 \u3000\u3000\u4ed6\u8fde\u9000\u6570\u5c3a\uff0c\u867d\u7136\u7ad9\u7a33\u4e86\u8eab\u5f62\uff0c\u4f46\u5634\u89d2\u5374\u5df2\u6709\u4e00\u4e1d\u9c9c\u8840\u6d41\u51fa\uff0c\u773c\u89c1\u6a80\u660e\u5df2\u4e58\u52bf\u6251\u6765\uff0c\u81ea \u5df1\u4f53\u5185\u5374\u5df2\u6c14\u8840\u7ffb\u6d8c\uff0c\u53ea\u6015\u518d\u4e5f\u63a5\u4e0d\u4f4f\u4ed6\u4e00\u62db\u4e86\uff01 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u94c1\u62d0\u7eb5\u6a2a\uff0c\u4ea6\u662f\u5728\u5239\u90a3\u4e4b\u95f4\uff0c\u4fbf\u53ef\u5c06\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u6bd9\u5728\u624b\u4e0b\u3002 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u95ea\u8eab\u800c\u51fa\u3002 \u3000\u3000\u7fa4\u8c6a\u5404\u6301\u5200\u5251\uff0c\u7fa4\u6d8c\u800c\u4e0a\u3002 \u3000\u3000\u7a81\u5730\uff01 \u3000\u3000\u5e84\u9662\u5916\u54cd\u8d77\u4e00\u9635\u9a6c\u8e7f\u5954\u817e\u4e4b\u58f0\uff0c\u4e00\u4e2a\u5639\u4eae\u7684\u58f0\u97f3\u5927\u558a\u9053\uff1a\u201c\u603b\u9556\u5934\uff0c\u5144\u5f1f\u4eec\u5df2\u5168\u90fd\u6765 \u4e86\uff0c\u4f60\u8001\u53ef\u66fe\u51fa\u4e8b\uff1f\u53ef\u8981\u6211\u4eec\u8fdb\u6765\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u547c\u559d\u4e4b\u58f0\u4e2d\u6c14\u5f25\u8db3\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u4f20\u9001\u8fdb\u6765\uff0c\u6ee1\u5385\u7fa4\u8c6a\uff0c\u5fc3\u5934\u4ff1\u90fd\u4e3a\u4e4b\u4e00\u9707\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u4e0d\u51fa\u6211\u4e4b\u6240\u6599\uff01\u6a80\u660e\u679c\u6709\u51c6\u5907\uff01\u201d \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u60f3\u4e0d\u5230\u6a80\u5927\u53d4\u884c\u4e8b\u7adf\u8fd9\u822c\u8c28\u614e\uff0c\u4eca\u65e5\u7adf\u662f\u6709\u5907\u800c\u6765\uff0c\u770b\u6765\u6211\u5144\u5f1f \u56db\u4eba\u662f\u6bcb\u9700\u51fa\u624b\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fc3\u4e2d\u5374\u4e0d\u7981\u5927\u5947\uff01 \u3000\u3000\u201c\u662f\u8c01\u6765\u4e86\uff1f\u6211\u6b64\u6765\u6839\u672c\u672a\u66fe\u901a\u77e5\u6c5f\u5357\u7684\u9556\u5c40\uff0c\u8fd9\u53e3\u97f3\u4e5f\u751f\u758f\u5f97\u5f88\uff01\u201d\u5f53\u7136\uff0c\u4ed6\u8868\u9762\u4e0a \u81ea\u4e0d\u4f1a\u6709\u4e1d\u6beb\u60ca\u5947\u4e4b\u8272\u9732\u51fa\uff01 \u3000\u3000\u6ee1\u5385\u7fa4\u8c6a\u7684\u8eab\u5f62\uff0c\u6b64\u523b\u4ff1\u50cf\u662f\u51b0\u96ea\u4e00\u822c\u5730\u51dd\u7ed9\u4e86\u8d77\u6765\uff0c\u8c01\u90fd\u518d\u4e5f\u4e0d\u6562\u5984\u81ea\u51fa\u624b\u3002 \u3000\u3000\u53ea\u89c1\u5e84\u5916\u9a6c\u8e44\u4e4b\u58f0\uff0c\u5f80\u590d\u5954\u817e\uff0c\u4e5f\u4e0d\u77e5\u6765\u4e86\u591a\u5c11\u4eba\uff0c\u4e5f\u4e0d\u77e5\u6765\u4e86\u591a\u5c11\u5339\u9a6c\u3002\u8e44\u58f0\u4e2d\uff0c\u5076 \u800c\u8fd8\u5939\u6742\u7740\u51e0\u58f0\u4e2d\u6c14\u6781\u8db3\u7684\u53f1\u54a4\u4e4b\u58f0\uff0c\u663e\u89c1\u4eca\u65e5\u201c\u98de\u9f99\u9556\u5c40\u201d\u6d3e\u6765\u6b64\u95f4\u7684\u4eba\uff0c\u8eab\u624b\u4ff1\u90fd\u4e0d \u5f31\uff01\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u5982\u5251\uff0c\u56db\u4e0b\u4e00\u8f6c\uff0c\u7fa4\u8c6a\u7adf\u65e0\u4e00\u4eba\u6562\u63a5\u89e6\u4ed6\u8fd9\u79cd\u9510\u5229\u7684\u76ee\u5149\uff0c\u9f50\u90fd\u5782\u4e0b \u5934\u53bb\u3002 \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u672c\u6765\u867d\u60f3\u4e58\u4e71\u5750\u6536\u6e14\u4eba\u4e4b\u5229\uff0c\u4f46\u89c1\u4e86\u8fd9\u822c\u60c5\u52bf\uff0c \u53c8\u542c\u4e86\u65b9\u624d\u7684\u5927\u559d\uff0c\u6df1\u6015\u81ea\u5df1\u4e0d\u80fd\u5168\u8eab\u800c\u9000\uff0c\u662f\u4ee5\u6b64\u523b\u8fd9\u4e24\u4eba\u4ea6\u662f\u5664\u5982\u5bd2\u8749\uff0c\u4e0d\u6562\u51fa\u53e3\uff1a \u201c\u795e\u624b\u201d\u6218\u98de\u8eab\u8eaf\u867d\u4ecd\u633a\u5f97\u7b14\u76f4\uff0c\u4f46\u4ed6\u9762\u4e0a\u94c1\u9752\u7684\u989c\u8272\uff0c\u5634\u89d2\u9c9c\u7ea2\u7684\u8840\u6e0d\uff0c\u5728\u8fd9\u98d8\u6447\u7684\u706f\u5149 \u4e0b\uff0c\u4ee4\u4eba\u770b\u6765\uff0c\u6b63\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u672b\u8def\u82f1\u96c4\uff01 \u3000\u3000\u9662\u5916\u4e4b\u4eba\uff0c\u867d\u7136\u4eba\u4eba\u7bad\u4e0a\u5f26\uff0c\u5200\u51fa\u9798\uff0c\u4f46\u542c\u5f97\u5899\u5916\u8fd9\u4e00\u9635\u5954\u817e\u7684\u9a6c\u8e44\u58f0\uff0c\u4ea6\u662f\u8c01\u4e5f\u4e0d\u6562 \u52a8\u5f39\uff0c\u6709\u4e9b\u7acb\u5728\u5899\u4e0b\u4e4b\u4eba\uff0c\u6b64\u523b\u90fd\u6084\u6084\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u5f80\u4e2d\u592e\u56f4\u4e86\u8fc7\u6765\u3002\u7adf\u65e0\u4e00\u4eba\u6562\u63a2\u9996\u5899\u5916 \u53bb\u671b\u4e0a\u4e00\u773c\uff01 \u3000\u3000\u53c8\u662f\u4e00\u9635\u5927\u558a\uff1a\u201c\u6a80\u603b\u9556\u5934\uff0c\u53ef\u8981\u6211\u4eec\u8fdb\u6765\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fc3\u4e2d\u84e6\u5730\u4e00\u60ca\uff0c\u542c\u51fa\u4e86\u8fd9\u559d\u58f0\u4e2d\u7684\u7834\u7efd\u3002\u201c\u98de\u9f99\u9556\u5c40\u201d\u6240\u6709\u5206\u5c40\u4e2d\u5927\u5c0f\u9556 \u5934\u4e4b\u4e2d\uff0c\u518d\u65e0\u4e00\u4eba\u4f1a\u79f0\u81ea\u5df1\u4e3a\u201c\u6a80\u603b\u9556\u5934\u201d\u7684\uff0c\u5899\u5916\u7684\u9a6c\u8e44\u4eba\u58f0\uff0c\u5fc5\u6709\u6eaa\u8df7\u3002 \u3000\u3000\u4f46\u8fd9\u6b66\u6797\u5927\u8c6a\u9762\u4e0a\u4ecd\u662f\u9634\u6c89\u5982\u51b0\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u53ea\u89c1\u6ee1\u5385\u7fa4\u8c6a\uff0c\u4ecd\u662f\u6728\u7acb\u5982\u6b7b\uff0c\u4ed6\u5fc3\u5ff5\u4e00 \u8f6c\uff0c\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u592b\u4e3a\u4eba\uff0c\u4ece\u4e0d\u8d76\u5c3d\u6740\u7edd\uff0c\u4eca\u65e5\u4e5f\u9976\u4f60\u8fd9\u4e00\u906d\uff01\u201d \u3000\u3000\u8f6c\u9996\u559d\u9053\uff1a\u201c\u4e1c\u65b9\u4e16\u5144\uff0c\u6e05\u6d0b\uff0c\u6211\u4eec\u2014\u2014\u9000\uff01\u201d \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u6697\u94a6\u4f69\u8fd9\u201c\u9f99\u5f62\u516b\u638c\u201d\u7684\u4ec1\u539a\uff0c\u5144\u5f1f\u591a\u4eba\uff0c\u4e00\u8d77\u7f13\u6b65\u8d70\u4e86\u51fa \u53bb\uff01\u201c\u9f99\u5f62\u516b\u638c\u201d\u6602\u7136\u800c\u51fa\uff0c\u56db\u9762\u7fa4\u8c6a\uff0c\u65e0\u8a00\u5730\u8ba9\u5f00\u4e00\u6761\u901a\u8def\uff0c\u4ed6\u4eec\u4ff1\u90fd\u5782\u7740\u9996\uff0c\u65e0\u4eba\u6562\u62ac \u5934\u53bb\u671b\u4e00\u773c\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u957f\u53f9\u4e00\u58f0\uff0c\u9762\u5bb9\u82cd\u767d\u5982\u6b7b\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u80cc\u8fc7\u8eab\u53bb\uff0c\u76ee\u5149\u9ed8\u7136\u51dd\u6ce8\u7740\u5899\u4e0a\u7684 \u4e00\u526f\u5bf9\u8054\u2026\u2026 \u3000\u3000\u826f\u4e45\uff0c\u4ed6\u76ee\u5149\u4e0d\u7981\u6cdb\u8d77\u4e86\u4e00\u7247\u6cea\u5149\uff0c\u7ec8\u4e8e\uff0c\u4e24\u6ef4\u6cea\u73e0\uff0c\u593a\u7736\u800c\u51fa\uff0c\u987a\u8138\u6d41\u4e0b\uff0c\u548c\u7740\u4ed6\u5634 \u89d2\u7684\u9c9c\u8840\uff0c\u843d\u5230\u4ed6\u9894\u4e0b\u7684\u957f\u9aef\u4e0a\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u811a\u6b65\u6c89\u7a33\uff0c\u8d70\u5165\u9662\u4e2d\uff0c\u7a81\u5730\u6c89\u58f0\u9053\uff1a\u201c\u4e1c\u65b9\u4e16\u5144\u8fd9\u8fb9\u8d70\uff01\u201d \u3000\u3000\u8eab\u5f62\u4e00\u62e7\uff0c\u7a81\u5730\u95ea\u7535\u822c\u63a0\u51fa\u5899\u5916\uff0c\u4e1c\u65b9\u5144\u5f1f\u6115\u4e86\u4e00\u6115\uff0c\u4ea6\u81ea\u968f\u4e4b\u63a0\u51fa\u3002 \u3000\u3000\u5899\u5916\u70df\u4e3b\u6eda\u6eda\uff0c\u9a6c\u5339\u5954\u817e\uff01 \u3000\u3000\u4f46\u662f\uff0c\u6240\u6709\u7684\u9a6c\u978d\u4e0a\u5374\u4ff1\u90fd\u662f\u7a7a\u978d\u65e0\u4eba\uff0c\u53ea\u89c1\u8fdc\u8fdc\u6709\u4e09\u6761\u7070\u5f71\uff0c\u8d76\u52a8\u7740\u9a6c\u5339\uff0c\u9aa4\u773c\u4e00 \u671b\uff0c\u7adf\u4f3c\u4e4e\u662f\u201c\u5317\u6597\u4e03\u715e\u201d\u4e2d\u7684\u83ab\u6c0f\u5144\u5f1f\uff01 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u4e5f\u4e0d\u613f\u518d\u52a0\u8fdf\u7591\uff0c\u201c\u5587\u201d\u5730\uff0c\u5404\u81ea\u63a0\u4e0a\u4e86\u4e00\u5339\u7a7a\u978d\u4e4b\u9a6c\uff0c\u65e7\u4e2d\u4f4e\u53f1\u4e00\u58f0\uff0c\u7ecf\u7ef3 \u4e00\u5e26\uff0c\u6012\u9a6c\u626c\u8e44\uff0c\u75be\u9a70\u800c\u53bb\uff01 \u3000\u3000\u6d6a\u83bd\u5c71\u5e84\uff0c\u7aef\u9633\u4e00\u4f1a\uff0c\u5728\u5f53\u65f6\u770b\u6765\uff0c\u867d\u672a\u505a\u51fa\u4ec0\u4e48\u5341\u5206\u5177\u6709\u51b3\u5b9a\u6027\u7684\u4e8b\u60c5\uff0c\u90a3\u60ca\u5fc3\u52a8\u9b44 \u7684\u4e00\u6218\uff0c\u5728\u5f53\u65f6\u4ea6\u65e0\u6210\u8d25\u4e4b\u5206\uff0c\u4f46\u90a3\u4e00\u6218\u56fa\u662f\u4e00\u65e9\u5df2\u9707\u52a8\u6b66\u6797\uff0c\u90a3\u4e00\u4f1a\u5bf9\u6b66\u6797\u5f71\u54cd\u4e4b\u5de8\uff0c\u66f4 \u662f\u9a87\u4eba\u542c\u95fb\uff01 \u3000\u3000\u81ea\u4ece\u6614\u5e74\u4e4b\u795e\u79d8\u8499\u9762\u5ba2\uff0c\u4ee5\u72ec\u529b\u641e\u6563\u5927\u6c5f\u5357\u5317\u5341\u4f59\u5bb6\u6210\u540d\u7684\u9556\u5c40\u540e\uff0c\u5e73\u9759\u7684\u6b66\u6797\uff0c\u5df2\u7531 \u8fd9\u4e00\u4f1a\u5c55\u5f00\u4e86\u4e00\u4e9b\u6c5f\u6e56\u4e2d\u81ea\u53e4\u672a\u6709\u3001\u4ece\u6765\u5c11\u89c1\u3001\u6781\u7aef\u5947\u5f02\u7684\u98ce\u6d6a\uff0c\u800c\u8fd9\u4e9b\u98ce\u6d6a\uff0c\u5374\u7adf\u7136\u662f\u4e0e \u4e00\u4e2a\u6781\u4e3a\u5e73\u51e1\uff0c\u800c\u53c8\u6781\u4e3a\u4e0d\u5e73\u51e1\u7684\u5f31\u51a0\u5c11\u5e74\uff0c\u6709\u7740\u5bc6\u5207\u4e4b\u5173\u7cfb\u7684\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u6b66\u529f\u6d45\u8584\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f46\u5728\u6c5f\u6e56\u4f20\u8a00\u4e2d\uff0c\u4ed6\u5012\u662f\u4e2a\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\u7684 \u4eba\u7269\u3002\uff0c\u8fd9\u5c11\u5e74\u7684\u751f\u8eab\u5b64\u82e6\uff0c\u51fa\u8eab\u5e73\u51e1\uff0c\u4f46\u5728\u6c5f\u6e56\u4f20\u8a00\u4e2d\uff0c\u4ed6\u5374\u662f\u6b66\u6797\u540d\u95e8\u4e16\u5bb6\u7684\u95e8\u4eba\uff0c\u6216 \u662f\u4e2a\u4e45\u5df2\u9690\u8ff9\uff0c\u50fb\u5c45\u6d77\u5916\u7684\u7edd\u4ee3\u9ad8\u4eba\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u751f\u6027\u5584\u826f\uff0c\u5bbd\u539a\u4ec1\u6148\uff0c\u4f46\u5728\u6c5f\u6e56\u4f20\u8a00\u4e2d\uff0c\u4ed6\u5374\u662f\u4e2a\u5fc3\u673a\u6df1\u6c89\u7684\u4eba\u7269\uff0c\u56e0\u4e3a\u4ed6\u5e74\u7eaa \u8f7b\u8f7b\uff0c\u4fbf\u5df2\u505a\u4e86\u6c5f\u5357\u7eff\u6797\u7684\u76df\u4e3b\uff01 \u3000\u3000\u8fd9\u5c11\u5e74\u53eb\u505a\u88f4\u73cf\uff0c\u4f46\u6c5f\u6e56\u4e2d\u4eba\u5374\u4ece\u4e0d\u79f0\u547c\u4ed6\u7684\u59d3\u540d\uff0c\u800c\u5c0a\u656c\u5730\u79f0\u4ed6\u4e3a\u201c\u88f4\u5927\u5148\u751f\u201d\u3002\u5c31 \u8fd9\u6837\uff0c\u5584\u826f\uff0c\u5e73\u51e1\uff0c\u800c\u5e74\u8f7b\u7684\u88f4\u73cf\uff0c\u4fbf\u88ab\u6c5f\u6e56\u4e2d\u4eba\uff0c\u6e32\u67d3\u8d77\u79cd\u79cd\u795e\u79d8\u800c\u79bb\u5947\u7684\u5f69\u8272\u3002\u6d6a\u83bd\u4e00 \u6218\u540e\uff0c\u201c\u4e1c\u65b9\u4e94\u5251\u201d\uff0c\u517c\u7a0b\u8fd4\u56de\u201c\u98de\u7075\u5821\u201d\u2014\u2014\u5728\u4ed6\u4eec\u56de\u5821\u540e\u7684\u7b2c\u4e8c\u5929\uff0c\u4fbf\u6709\u5341\u516b\u6761\u5927\u6c49\u5e26 \u7740\u5341\u65b9\u4e24\u4ee5\u4e0a\u7684\u91d1\u94f6\u73cd\u5b9d\uff0c\u6c42\u89c1\u201c\u98de\u7075\u5821\u201d\u7684\u5c11\u5821\u4e3b\u3002\u867d\u7136\u7ecf\u8fc7\u4e86\u90a3\u6fc0\u70c8\u7684\u4e00\u6218\uff0c\u4f46\u201c\u6d6a\u83bd \u5c71\u5e84\u201d\uff0c\u201c\u91d1\u9e21\u5e2e\u201d\uff0c\u201c\u4e03\u5de7\u5c71\u5e84\u201d\uff0c\u81ea\u672a\u5fd8\u4e86\u8fd9\u4e00\u6b21\u5947\u5f02\u7684\u8d4c\u6ce8\uff01 \u3000\u3000\u6d6a\u83bd\u4e00\u6218\u540e\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u4ea6\u517c\u7a0b\u8fd4\u56de\u4e2d\u539f\uff0c\u4ed6\u6682\u65f6\u65e0\u4ec0\u4e48\u4e3e\u52a8\uff0c\u4f46\u6b66\u6797\u4e2d\u4eba\u8c01 \u90fd\u77e5\u9053\uff0c\u8fd9\u6b66\u6797\u4e2d\u7684\u4e00\u4ee3\u4e4b\u96c4\uff0c\u662f\u51b3\u4e0d\u4f1a\u653e\u8fc7\u201c\u795e\u624b\u201d\u6218\u98de\u7684\uff0c\u800c\u8fd9\u5fc5\u5c06\u53d1\u751f\u7684\u7b2c\u4e8c\u6b21\u53cc\u96c4 \u4e4b\u6218\uff0c\u4fbf\u7edd\u4e0d\u4f1a\u6709\u5982\u7b2c\u4e00\u6b21\u90a3\u822c\u4e0d\u5206\u80dc\u8d1f\uff0c\u800c\u4e14\u9664\u4e86\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e0e\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u5916\uff0c\u5927\u5385 \u4e24\u5cb8\uff0c\u957f\u6c5f\u5357\u5317\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u4e5f\u52bf\u5fc5\u8981\u5728\u8fd9\u4e00\u6218\u4e4b\u4e2d\uff0c\u5c3d\u6570\u51fa\u52a8\uff0c\u6b66\u6797\u4e2d\u4eba\u5bf9\u8fd9\u6b21\u6709\u51b3\u5b9a\u6027 \u7684\u4e00\u6218\uff0c\u4ff1\u90fd\u5728\u7d27\u5f20\u4e0e\u671f\u5f85\u4e2d\u89c2\u671b\u7740\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5728\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e2d\u4e4b\u6240\u4ee5\u80fd\u5168\u8eab\u800c\u9000\uff0c\u5728\u6b66\u6797\u4e2d\u4e5f\u6709\u8bb8\u591a\u79cd\u4f20\u8bf4\uff0c\u4f46\u771f\u8c61 \u7a76\u7adf\u5982\u4f55\uff0c\u5230\u73b0\u5728\u4ecd\u672a\u63ed\u7a7f\uff0c\u4e8e\u662f\u201c\u9f99\u5f62\u516b\u638c\u201d\u8fd9\u540d\u5b57\uff0c\u5728\u6c5f\u6e56\u4e2d\u4eba\u7684\u53e3\u91cc\uff0c\u4e5f\u5e73\u6dfb\u4e86\u51e0\u5206 \u795e\u5947\u7684\u8272\u5f69\uff01 \u3000\u3000\u8fd9\u4e00\u5207\u90fd\u662f\u503c\u5f97\u5174\u594b\u3001\u8db3\u4ee5\u8f70\u52a8\u7684\u4e8b\uff0c\u4f46\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u771f\u6b63\u7684\u5174\u8da3\uff0c\u7adf\u5927\u591a\u4e0d\u5728\u8fd9\u4e9b\u4e8b \u4e0a\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5174\u8da3\u5728\u2026\u2026 \u3000\u3000\u4e5d\u6708\u5df2\u81f3\uff0c\u76db\u6691\u5374\u4ecd\u672a\u53bb\uff01 \u3000\u3000\u79cb\u98ce\u4e4d\u8d77\uff0c\u4e07\u91cc\u82cd\u7a79\uff0c\u4e00\u78a7\u5982\u6d17\u3002 \u3000\u3000\u81ea\u7941\u95e8\uff0c\u81f3\u9ec4\u5c71\uff0c\u4e00\u6761\u867d\u4e0d\u5341\u5206\u51b7\u50fb\uff0c\u4f46\u5e73\u65e5\u884c\u4eba\u5374\u6781\u5c11\u7684\u9ec4\u6ce5\u8def\u4e0a\uff0c\u6b64\u523b\u7adf\u7136\u6cbf\u8def\u4ff1 \u662f\u4eba\u8e2a\uff0c\u800c\u4e14\u5927\u591a\u662f\u4f69\u5200\u6302\u5251\u7684\u6c5f\u6e56\u597d\u6c49\uff0c\u4ed6\u4eec\u6709\u7684\u7275\u7740\u9aa1\u9a6c\uff0c\u6709\u7684\u7a7a\u624b\u800c\u884c\uff0c\u8fd9\u8bb8\u591a\u6c5f\u6e56 \u8c6a\u58eb\u540c\u8def\u800c\u884c\uff0c\u4e0d\u7981\u4ee4\u4eba\u5947\u602a\u3002\u83ab\u662f\u9ec4\u5c71\u4e4b\u4e0a\uff0c\u53c8\u53d1\u751f\u4e86\u4ec0\u4e48\u8db3\u4ee5\u9707\u52a8\u6b66\u6797\u7684\u5927\u4e8b\uff1f\u4f46\u770b\u4ed6 \u4eec\u60a0\u95f2\u7684\u795e\u60c5\uff0c\u5374\u53c8\u4e0d\u50cf\uff0c\u4ed6\u4eec\u5f7c\u6b64\u7b11\u8bed\uff0c\u4e92\u76f8\u62db\u547c\uff0c\u884c\u8d70\u5f97\u4ff1\u90fd\u5341\u5206\u7f13\u6162\uff0c\u7adf\u4eff\u4f5b\u662f\u4e00\u7fa4 \u8336\u4f59\u996d\u540e\uff0c\u4e00\u8d77\u53bb\u89c2\u5267\u542c\u6b4c\u7684\u95f2\u4eba\uff0c\u53c8\u50cf\u662f\u4e00\u7fa4\u9526\u8863\u7389\u98df\uff0c\u4e00\u8d77\u53bb\u54c1\u82b1\u996e\u9152\u7684\u7ea8\u7ed4\u5c11\u5e74\u3002 \u3000\u3000\u6700\u5947\u602a\u7684\u662f\u8fd8\u6709\u4e00\u7fa4\u884c\u811a\u5c0f\u8d29\uff0c\u6709\u7684\u62c5\u7740\u9152\u8089\uff0c\u6709\u7684\u62c5\u7740\u8336\u98df\uff0c\u81ea\u6210\u4e00\u5e2e\uff0c\u4ea6\u81ea\u975e\u5e38\u60a0 \u95f2\u5730\u8ddf\u5728\u4ed6\u4eec\u8eab\u65c1\uff0c\u8d29\u5356\u7740\u9152\u8089\u8336\u98df\uff0c\u751a\u81f3\u8fd8\u6709\u4e00\u4e9b\u5c0f\u8d29\uff0c\u5356\u7684\u7adf\u662f\u8863\u5c65\u978b\u889c\uff0c\u751f\u610f\u4e5f\u4e0d \u6076\u3002\u663e\u89c1\u8fd9\u4e00\u4e2a\u5947\u5f02\u7684\u56e2\u4f53\uff0c\u5df2\u7ed3\u6210\u4e86\u8bb8\u4e45\uff0c\u800c\u4e14\u8d70\u4e86\u4e0d\u5c11\u8def\u9014\uff0c\u624d\u5230\u8fd9\u91cc\u3002 \u3000\u3000\u4ed6\u4eec\u505c\u505c\u6b47\u6b47\uff0c\u7f13\u6b65\u800c\u884c\uff0c\u4f3c\u4e4e\u662f\u4e00\u65e0\u76ee\u7684\uff0c\u4f46\u540e\u9762\u7684\u4eba\u5374\u53c8\u4e0d\u65f6\u6781\u4e3a\u7d27\u5f20\u5730\u8d76\u5230\u524d \u9762\uff0c\u7d27\u5f20\u5730\u95ee\u4e00\u95ee\u8d70\u5728\u524d\u9762\u7684\u4eba\u3002 \u3000\u3000\u201c\u600e\u6837\u4e86\uff0c\u6709\u6ca1\u6709\u6d88\u606f\uff1f\u201d \u3000\u3000\u6d88\u606f\uff1f\u751a\u4e48\u6d88\u606f\uff01 \u3000\u3000\u662f\u4ec0\u4e48\u6d88\u606f\u503c\u5f97\u8fd9\u4e00\u7fa4\u6b66\u6797\u8c6a\u58eb\u5982\u6b64\u91cd\u89c6\uff0c\u4e0d\u501f\u629b\u5f00\u4e86\u81ea\u5df1\u5e94\u505a\u7684\u4e8b\uff0c\u6709\u7684\u751a\u81f3\u662f\u4ece\u4e2d \u539f\u8d76\u5230\u8fd9\u91cc\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u7fa4\u4eba\u524d\u9762\u7ea6\u83ab\u6570\u4e08\u4e4b\u5904\uff0c\u53c8\u6709\u4e00\u5e2e\u6b66\u6797\u8c6a\u58eb\uff0c\u4ed6\u4eec\u4eba\u6570\u4e0d\u201c\u771f\u662f\u5012\u9709\uff0c\u7adf\u88ab\u5dee\u6765 \u5e72\u8fd9\u8d9f\u82e6\u5dee\uff01\u201d\u5176\u5b9e\u8fd9\u4e00\u8d9f\u201c\u82e6\u5dee\u201d\uff0c\u5374\u662f\u4ed6\u81ea\u5df1\u8ba8\u5f97\u6765\u7684\u3002 \u3000\u3000\u6709\u65f6\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u4fbf\u8f6c\u8eab\u5954\u5230\u540e\u9762\u7684\u9152\u8089\u644a\u8d29\u5904\uff0c\u75db\u996e\u51e0\u676f\u70c8\u9152\uff0c\u90a3\u65f6\u4e00\u5b9a\u6709\u8bb8\u591a\u4eba\u4f1a \u62a2\u7740\u4e3a\u4ed6\u4ed8\u5e10\uff0c\u4e3a\u7684\u53ea\u662f\u8981\u95ee\u4ed6\uff01 \u3000\u3000\u201c\u5305\u8001\u5927\uff0c\u600e\u6837\u4e86\uff0c\u6709\u6ca1\u6709\u6d88\u606f\uff1f\u201d \u3000\u3000\u8fd9\u7ea2\u8863\u5927\u6c49\u5c31\u4f1a\u201c\u5427\u201d\u5730\u4e00\u58f0\uff0c\u5c06\u9152\u7897\u6454\u5230\u644a\u6848\u4e0a\uff0c\u5927\u9a82\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u6d88\u606f\uff01\u54fc\uff0c\u5c41\u4e5f \u6ca1\u6709\uff0c\u53ea\u6015\u8981\u7b49\u4e0a\u4e09\u5e74\u4e94\u8f7d\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u8d70\u7740\u77a7\u5427\uff01\u6211\u978b\u5b50\u90fd\u6362\u4e86\u4e24\u53cc\u4e86\uff01\u201d \u3000\u3000\u522b\u7684\u4eba\u6709\u7684\u5931\u7b11\u9053\uff1a\u201c\u5012\u662f\u771f\u7684\uff0c\u5305\u8001\u5927\u978b\u7834\u4e86\uff0c\u8fd8\u771f\u4e0d\u597d\u4e70\u3002\u201d \u3000\u3000\u54ea\u77e5\u65c1\u8fb9\u7acb\u523b\u6709\u4e00\u4f4d\u5c0f\u8d29\u63a5\u53e3\u558a\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u5c0f\u7684\u5df2\u4e3a\u60a8\u8001\u51c6\u5907\u4e86\u597d\u51e0\u53cc\u7ea2\u978b\u5b50\uff0c\u5927 \u5c0f\u5305\u7ba1\u5408\u811a\u3002\u201d \u3000\u3000\u4e8e\u662f\u56db\u4e0b\u7acb\u523b\u54c4\u4f20\u8d77\u4e00\u9635\u7b11\u58f0\uff0c\u8fd9\u7ea2\u8863\u5927\u6c49\u5df2\u4e0d\u7981\u5e26\u7b11\u9a7e\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u86ee\u4f1a\u505a\u751f \u610f\uff01\u201d \u3000\u3000\u7136\u540e\u60bb\u60bb\u7136\u5927\u6b65\u8d70\u4e86\u56de\u53bb\uff0c\u53ea\u662f\u4ed6\u795e\u60c5\u867d\u7136\u6781\u4e3a\u72c2\u50b2\uff0c\u5374\u5bf9\u8fd9\u516d\u4eba\u4e4b\u4e2d\u7684\u4e00\u4e2a\u957f\u886b\u6c49\u5b50 \u9887\u4e3a\u606d\u656c\uff1b\u53c8\u4f3c\u5bf9\u4e00\u4e2a\u5f62\u5bb9\u5e72\u67af\u3001\u8eab\u6750\u7626\u5c0f\u7684\u6c49\u5b50\u9887\u4e3a\u754f\u60e7\uff0c\u4e0d\u65f6\u53bb\u5077\u671b\u4ed6\u51e0\u773c\uff0c\u4f46\u7b49\u5230\u4ed6 \u76ee\u5149\u5e26\u7b11\u8f6c\u8fc7\u6765\u65f6\uff0c\u4f46\u7acb\u523b\u671b\u5230\u522b\u5904\u53bb\u3002 \u3000\u3000\u8fd9\u7ea2\u8863\u5927\u6c49\u5728\u6b66\u6797\u4e2d\u201c\u4e07\u513f\u201d\u9887\u54cd\uff0c\u6b63\u662f\u5728\u201c\u91d1\u9e21\u5e2e\u201d\u4e2d\u4ec5\u6b21\u4e8e\u5e2e\u4e3b\u5411\u4e00\u557c\u7684\u5927\u5934\u9886\uff0c \u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\uff1a\u90a3\u957f\u886b\u6c49\u5b50\uff0c\u662f\u8fd9\u4e9b\u4eba\u4e2d\u552f\u4e00\u7a7f\u7740\u957f\u886b\u7684\u4eba\uff0c\u4ed6\u795e\u6001\u4e4b\u95f4\uff0c\u6781\u4e3a\u8c26\u606d\uff0c\u4f46 \u522b\u4eba\u5374\u53c8\u90fd\u5bf9\u4ed6\u5341\u5206\u606d\u656c\u3002 \u3000\u3000\u6b64\u4eba\u8eab\u8eaf\u7626\u524a\uff0c\u9762\u5bb9\u9887\u4e3a\u6e05\u77cd\uff0c\u5fae\u5fae\u7559\u7740\u4e00\u4e9b\u6e05\u987b\uff0c\u83ab\u7ea6\u56db\u5341\u5c81\u5e74\u7eaa\uff0c\u770b\u6765\u4f3c\u4e4e\u662f\u4e2a\u4e0d \u7b2c\u79c0\u624d\uff0c\u53c8\u4f3c\u4e4e\u662f\u4e2a\u5546\u53f7\u4e2d\u7684\u638c\u67dc\u7684\uff0c\u4f46\u4e00\u8def\u6f47\u6d12\u800c\u884c\uff0c\u5728\u5982\u6b64\u70c8\u65e5\u4e4b\u4e0b\uff0c\u5374\u5e76\u672a\u663e\u51fa\u52b3 \u7d2f\u3002 \u3000\u3000\u6709\u65f6\uff0c\u4ed6\u53e3\u4e2d\u8fd8\u4f1a\u4f4e\u54fc\u4e00\u4e24\u58f0\u8bd7\u53e5\uff0c\u60f3\u5fc5\u90fd\u662f\u4ed6\u5728\u8fd9\u591a\u4f59\u7684\u9ec4\u5c71\u9053\u4e0a\u62fe\u6765\u7684\u4f73\u53e5\uff0c\u5374\u6781 \u4e0d\u4e0e\u8eab\u65c1\u8fd9\u4e9b\u4eba\u8bf4\u8bdd\uff0c\u795e\u8272\u5728\u8c26\u606d\u4e2d\u53c8\u5e26\u7740\u4e9b\u50b2\u6162\uff0c\u53ea\u56e0\u4ed6\u672c\u8eab\u867d\u7136\u65e0\u751a\u58f0\u540d\uff0c\u5176\u6765\u5386\u5374\u662f \u8d6b\u8d6b\u4e0d\u51e1\u3002 \u3000\u3000\u4ed6\u4fbf\u662f\u6c5f\u5357\u201c\u98de\u7075\u5821\u201d\u4e2d\u7684\u6267\u4e8b\u4e4b\u4e00\uff0c\u5728\u5821\u4e2d\u4eba\u4eba\u79f0\u4ed6\u201c\u7ba1\u4e8c\u201d\uff0c\u4f46\u6b64\u523b\u522b\u4eba\u5374\u5c0a\u4ed6\u4e00 \u53e5\u201c\u7ba1\u4e8c\u7237\u201d\uff0c\u5c31\u8fde\u4ed6\u8eab\u65c1\u90a3\u67af\u7626\u7684\u6c49\u5b50\u90fd\u4e0d\u4f8b\u5916\uff0c\u662f\u4ee5\u4ed6\u795e\u8272\u4e4b\u95f4\uff0c\u4fbf\u4e0d\u7981\u663e\u5f97\u6709\u4e9b\u6cbe\u6cbe \u81ea\u559c\u3002 \u3000\u3000\u8fd9\u67af\u7626\u6c49\u5b50\u5bf9\u522b\u7684\u4eba\u5374\u6ee1\u9762\u4ff1\u662f\u8f7b\u8511\u7684\u51b7\u7b11\uff0c\u4eff\u4f5b\u6781\u4e3a\u4e0d\u5c51\uff0c\u6709\u65f6\u751a\u81f3\u4e0d\u613f\u4e0e\u4ed6\u4eec\u8d70\u5728 \u4e00\u8d77\uff0c\u72ec\u81ea\u9a91\u7740\u4ed6\u7684\u9ed1\u9a74\u7f13\u7f13\u800c\u884c\uff0c\u5374\u4e5f\u4e0d\u6562\u8d70\u5230\u592a\u524d\u9762\u53bb\uff0c\u90a3\u7ea2\u8863\u5927\u6c49\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u672c \u6765\u60f3\u627e\u4e9b\u6614\u5934\u4e0e\u4ed6\u5403\uff0c\u54ea\u77e5\u6b64\u4eba\u5fc3\u667a\u7075\u5de7\uff0c\u968f\u673a\u5e94\u53d8\uff0c\u53cd\u6559\u90a3\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u5403\u4e86\u82e6\u5934\u53bb\u3002 \u3000\u3000\u4ed6\u8f7b\u529f\u4f3c\u4e4e\u6781\u9ad8\uff0c\u8d70\u8d77\u8def\u6765\uff0c\u4e00\u98d8\u4e00\u98d8\u5730\uff0c\u4eff\u4f5b\u968f\u65f6\u90fd\u4f1a\u88ab\u98ce\u5439\u8d70\uff0c\u5c31\u8fde\u4ed6\u7275\u7740\u7684\u9ed1 \u9a74\uff0c\u4e5f\u662f\u5e72\u67af\u7626\u5c0f\uff0c\u4e00\u4eba\u4e00\u9a74\uff0c\u6070\u597d\u76f8\u4e92\u8f89\u6620\uff0c\u8fd9\u4eba\u9a74\u4e4b\u95f4\u7684\u795e\u6001\uff0c\u4e5f\u597d\u50cf\u662f\u5144\u5f1f\u4f3c\u7684\uff0c\u751a \u81f3\u8fde\u5403\u996d\u90fd\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4f46\u6b64\u4eba\u5374\u662f\u5927\u5927\u6709\u540d\uff0c\u4e43\u662f\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u6709\u540d\u7684\u9556\u5934\uff0c\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\uff0c\u4ed6\u4e4b\u6240\u4ee5 \u53c2\u52a0\u8fd9\u516d\u4eba\u4e4b\u4e2d\uff0c\u53ea\u4e0d\u8fc7\u662f\u81ea\u613f\u800c\u5df2\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u5bf9\u8fd9\u4ef6\u201c\u6d88\u606f\u201d\uff0c\u6709\u7740\u6d53\u539a\u7684\u5174\u8da3\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u9762\u8c8c\u5374\u6781\u719f\u6089\uff0c\u6b63\u662f\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e2d\u7684\u5f97\u529b\u4eba\u7269\u201c\u94c1\u7b97\u76d8\u201d\u4e8e\u5e73\u3002\u4ed6\u8eab\u540e\u8fd8\u8ddf\u7740 \u4e00\u4e2a\u5341\u516d\u4e03\u5c81\u3001\u770b\u4f3c\u5c0f\u53ae\u7684\u6e05\u79c0\u5c11\u5e74\uff0c\u53ea\u662f\u8fd9\u5c0f\u53ae\u5374\u53c8\u4e0d\u5927\u613f\u610f\u505a\u4e8b\uff0c\u4e8e\u5e73\u5524\u4ed6\u4e3a\u201c\u8317 \u4e66\u201d\uff0c\u663e\u89c1\u662f\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u4e66\u7ae5\u4e86\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u4eba\uff0c\u8eab\u8eaf\u81c3\u80bf\uff0c\u6c14\u5598\u6dcb\u6dcb\uff0c\u4e0d\u65f6\u81ea\u6000\u4e2d\u638f\u51fa\u4e00\u4e9b\u8089\u812f\uff0c\u653e\u5230\u53e3\u4e2d\u5927\u56bc\uff0c\u89c1\u4e86\u4eba\u603b \u662f\u563b\u563b\u54c8\u54c8\uff0c\u4f60\u95ee\u4ed6\u4ec0\u4e48\uff0c\u4ed6\u603b\u662f\u4e0d\u77e5\u9053\uff0c\u4ed6\u82e5\u95ee\u4f60\uff0c\u90a3\u6ee1\u9762\u7684\u7b11\u5bb9\uff0c\u5374\u6559\u4f60\u65e0\u6cd5\u4e0d\u56de\u7b54 \u4ed6\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u5947\u602a\uff0c\u60c5\u660e\u7ec3\u8fbe\u7684\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\uff0c\u600e\u4f1a\u6d3e\u4e86\u4e2a\u8fd9\u6837\u7684\u201c\u8822\u624d\u201d\u6765\u505a\u8fd9\u4ef6 \u4e8b\uff1f\u4ed6\u81ea\u79f0\u201c\u738b\u5f97\u9ad8\u201d\uff0c\u522b\u4eba\u90fd\u5524\u4ed6\u505a\u201c\u738b\u80d6\u5b50\u201d\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u65e0\u8bba\u8d70\u5230\u54ea\u91cc\uff0c\u4fbf\u5373\u662f\u7a77\u4e61\u50fb\u58e4\uff0c\u4e5f\u4f1a\u7a81\u7136\u53d8\u5f97\u7e41\u8363\u8d77\u6765\u3002\u4f46\u8fd9\u4e9b\u4eba\u7684\u811a\u6b65\uff0c\u4ea6 \u662f\u8eab\u4e0d\u7531\u5df1\u7684\u3002 \u3000\u3000\u540e\u9762\u90a3\u4e00\u7fa4\u4eba\uff0c\u8ddf\u7740\u524d\u9762\u8fd9\u516d\u4eba\uff1b\u8fd9\u516d\u4eba\u7684\u811a\u6b65\uff0c\u5374\u662f\u8ddf\u7740\u3002 \u3000\u3000\u518d\u524d\u9762\u5341\u4f59\u4e08\u5904\uff0c\u7f13\u6b65\u800c\u884c\u7684\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e0e\u88f4\u73cf\uff01 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e00\u8def\u89c2\u8d4f\u7740\u98ce\u666f\uff0c\u2014\u2014\u4ed6\u4eec\u672c\u662f\u4e3a\u4e86\u6e38\u5c71\u73a9\u6c34\u624d\u51fa\u201c\u51b7\u8c37\u201d\u7684\u2014\u2014\u6709\u65f6\u4e24 \u4eba\u4e5f\u4f1a\u4f4e\u8bed\u4e24\u53e5\u3002 \u3000\u3000\u88f4\u73cf\u5374\u5927\u534a\u4ff1\u5728\u6c89\u601d\uff0c\u6709\u65f6\u81ea\u6000\u4e2d\u53d6\u51fa\u4e00\u518c\u4e66\u5377\uff0c\u770b\u4e0a\u534a\u664c\uff0c\u76f4\u5230\u9762\u4e0a\u73b0\u51fa\u7b11\u610f\uff0c\u4ed6\u4fbf \u53c8\u6536\u56de\u6000\u91cc\u3002 \u3000\u3000\u4ed6\u4eec\u4f3c\u4e4e\u4e0d\u77e5\u9053\u81ea\u5df1\u5728\u6c5f\u6e56\u4e2d\u5df2\u9020\u6210\u5982\u6b64\u8f70\u52a8\u4e4b\u52bf\uff0c\u53ea\u8981\u4ed6\u811a\u6b65\u6240\u53ca\uff0c\u7a77\u4e61\u7acb\u5bcc\uff0c\u5e9f\u589f \u6210\u5e02\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u6708\u6765\uff0c\u4ed6\u5fc3\u7075\u4f3c\u4e4e\u5df2\u8fdb\u5165\u5230\u53e6\u4e00\u4e2a\u9886\u57df\u4e2d\u53bb\uff0c\u5bf9\u8eab\u5916\u7684\u4e00\u5207\u4e8b\u7269\uff0c\u4ff1\u90fd\u4e0d\u95fb\u4e0d \u95ee\uff1b\u5b66\u4e86\u4e00\u6837\uff0c\u518d\u5b66\u4e00\u6837\uff0c\u4ed6\u751a\u81f3\u4e0d\u77e5\u9053\u81ea\u5df1\u5b66\u4e60\u7684\u901f\u5ea6\u662f\u591a\u4e48\u5f97\u60ca\u4eba\u3002\u6295\u5e97\u7684\u65f6\u5019\uff0c\u201c\u51b7 \u8c37\u53cc\u6728\u201d\u4f1a\u5728\u623f\u4e2d\u4f20\u6388\u4ed6\u4e00\u4e9b\u6b66\u529f\u7684\u8bc0\u7a8d\uff0c\u884c\u8def\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u5374\u8981\u4ed6\u53bb\u8bfb\u4e00\u4e9b\u4e66\u5377\u3002 \u3000\u3000\u4ed6\u4eec\u751a\u81f3\u4e0d\u613f\u7ed9\u4ed6\u4e00\u4e9b\u7a7a\u95f2\uff0c\u800c\u4ed6\uff0c\u4e5f\u5168\u7136\u6ca1\u6709\u60f3\u5230\u81ea\u5df1\u9700\u8981\u7a7a\u95f2\uff0c\u56e0\u4e3a\u4ed6\u53ea\u8981\u601d\u6f6e\u4e00 \u7a7a\uff0c\u6a80\u6587\u742a\u7684\u5029\u5f71\uff0c\u4fbf\u7acb\u523b\u4f1a\u586b\u6ee1\u8fd9\u7a7a\u7f3a\uff01 \u3000\u3000\u6709\u65f6\uff0c\u4ed6\u4e2d\u591c\u53cd\u4fa7\uff0c\u4e0d\u80fd\u6210\u7720\uff0c\u671b\u7740\u7a97\u5916\u7684\u661f\u7a7a\uff0c\u4ed6\u4f1a\u6697\u95f4\u81ea\u5df1\uff1a\u201c\u6211\u662f\u8be5\u80dc\uff0c\u6291\u6216\u662f \u8be5\u8d25\u5462\uff1f\u201d \u3000\u3000\u56e0\u4e3a\u4ed6\u82e5\u80dc\u4e86\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u4fbf\u4f1a\u4ee5\u5168\u529b\u53bb\u4e89\u53d6\u6a80\u6587\u742a\u7684\u4e00\u53cc\u7f8e\u3001\u76ee\uff0c\u6709\u65f6\uff0c\u4ed6\u5fcd\u4e0d\u4f4f \u8981\u727a\u7272\u81ea\u5df1\uff0c\u56e0\u4e3a\u4ed6\u5bf9\u5979\u867d\u7136\u662f\u90a3\u6837\u75db\u5fc3\uff0c\u4f46\u662f\u4ed6\u5374\u4ecd\u7136\u4e0d\u613f\u8ba9\u4efb\u4f55\u4eba\u4f24\u5bb3\u5230\u5979\u2014\u2014\u65e0\u8bba\u662f \u8eab\u4f53\u4e0a\uff0c\u6291\u6216\u662f\u60c5\u611f\u4e0a\u7684\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u53c8\u65e0\u6cd5\u62b5\u6321\u81ea\u5df1\u6c42\u77e5\u7684\u6b32\u671b\uff0c\u76f4\u5230\u6b64\u65f6\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6240\u6559\u7ed9\u4ed6\u7684\uff0c\u867d\u7136\u8fd8\u90fd \u662f\u4e9b\u6d45\u8fd1\u7684\u6b66\u529f\u4e0e\u77e5\u8bc6\uff0c\u4f46\u5374\u5df2\u662f\u4ed6\u4ece\u672a\u9886\u53d7\u8fc7\u7684\u3002\u4ed6\u4ee5\u5341\u500d\u4e8e\u4e00\u4e2a\u5b69\u5b50\u63a5\u53d7\u65b0\u8863\u7f8e\u98df\u7684\u6b23 \u559c\uff0c\u672a\u63a5\u53d7\u8fd9\u4e9b\u3002 \u3000\u3000\u4ed6\u795e\u60c5\u4e0e\u9762\u8c8c\uff0c\u4ff1\u5df2\u6e10\u6e10\u6709\u4e86\u6539\u53d8\uff0c\u53ea\u662f\u8fd8\u4e0d\u751a\u663e\u7740\u800c\u5df2\uff01\u4ed6\u81ea\u5df1\u9887\u4e3a\u60ca\u5f02\u4e8e\u81ea\u5df1\u7684\u6539 \u53d8\uff0c\u56e0\u4e3a\u4ed6\u8fd8\u4e0d\u77e5\u9053\u4e16\u95f4\u6700\u6700\u5947\u5999\u4e4b\u7269\uff0c\u4fbf\u662f\u201c\u77e5\u8bc6\u201d\u3002\u5b83\u867d\u7136\u65e0\u5f62\uff0c\u4f46\u5374\u4e0d\u4f46\u80fd\u6539\u53d8\u4eba\u4eec \u7684\u5fc3\u7075\uff0c\u8fd8\u80fd\u6539\u53d8\u4eba\u4eec\u7684\u795e\u60c5\u4e0e\u9762\u8c8c\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5bf9\u88f4\u73cf\u7684\u5b66\u4e60\u80fd\u529b\uff0c\u8fd8\u5e76\u4e0d\u5341\u5206\u60ca\u5f02\uff0c\u56e0\u4e3a\u4eba\u4eec\u5b66\u8d77\u6d45\u8fd1 \u7684\u4e8b\u7269\u65f6\uff0c\u5927\u591a\u90fd\u662f\u5f88\u5feb\u7684\u3002 \u3000\u3000\u5bf9\u4e8e\u540e\u9762\u8ddf\u7740\u7684\u8fd9\u4e00\u7fa4\u201c\u5c3e\u5df4\u201d\uff0c\u4ed6\u4eec\u5e76\u4e0d\u5341\u5206\u538c\u6076\uff0c\u53cd\u800c\u6709\u4e00\u4efd\u6b23\u559c\u4e0e\u597d\u5947\uff0c\u751a\u81f3\u4f1a \u53bb\u5077\u5077\u5730\u89c2\u5bdf\u4ed6\u4eec\u7684\u52a8\u6001\uff0c\u6709\u65f6\u51b7\u5bd2\u7af9\u6545\u610f\u4f1a\u95ee\uff1a\u201c\u600e\u5730\u4e0d\u907f\u5f00\u8fd9\u4e9b\u538c\u7269\uff01\u201d \u3000\u3000\u51b7\u67af\u6728\u4fbf\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u907f\u6211\u4eec\uff0c\u96be\u9053\u8fd8\u8981\u6211\u4eec\u907f\u4ed6\u4eec\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u662f\u88f4\u73cf\u6e10\u6e10\u66f4\u4e86\u89e3\u8fd9\u4e24\u4e2a\u51b7\u50fb\u7684\u8001\u4eba\u7684\u5fc3\u6027\u3002\u5728\u4ed6\u4eec\u5b64\u50fb\u800c\u51b7\u505a\u7684\u8868\u9762\u4e0b\uff0c\u662f\u4e00\u9897\u70ed \u70c8\u7684\u8d64\u5b50\u4e4b\u5fc3\u3002 \u3000\u3000\u4ed6\u4eec\u60a0\u95f2\u5730\u4e0a\u4e86\u5929\u4e0b\u95fb\u540d\uff0c\u666f\u8272\u7edd\u7f8e\u7684\u9ec4\u5c71\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u51c6\u5907\u5728\u8fd9\u540d\u5c71\u4e0a\u5bfb\u4e00\u5e7d\u9759\u4e4b \u5904\uff0c\u6765\u6559\u7ed9\u88f4\u73cf\u4e00\u4e9b\u8f83\u4e3a\u8270\u6df1\u7684\u6b66\u529f\u3002 \u3000\u3000\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u7acb\u5728\u9a6c\u80cc\u4e0a\uff0c\u9065\u9065\u5411\u524d\u89c2\u671b\uff0c\u5fc3\u4e2d\u6781\u662f\u5f97\u610f\uff0c\u56e0\u4e3a\u4ed6\u542c\u5230\u8fdc\u8fdc\u6709\u4eba\u559d\u5f69 \u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5305\u8001\u5927\u7adf\u6709\u8fd9\u4e48\u4fca\u7684\u9a6c\u4e0a\u529f\u592b\uff01\u201d \u3000\u3000\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\u51b7\u51b7\u4e00\u7b11\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u5173\u5916\u7684\u9a6c\u8d3c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u5305\u6653\u5929\u5fc3\u4e2d\u6697\u9a82\u4e00\u58f0\uff01\u7a81\u5730\u77a5\u89c1\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e0e\u88f4\u73cf\u5df2\u4e0a\u5c71\u5341\u6570\u4e08\u4e86\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4e0a \u5c71\u4e86\uff01\u201d \u3000\u3000\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u8f7b\u8f7b\u8dc3\u4e0b\u9a6c\u6765\uff0c\u4ed6\u8eab\u8eaf\u867d\u9ad8\u5927\uff0c\u8f7b\u529f\u5374\u4e0d\u5f31\uff0c\u4ed6\u4e5f\u9887\u4e3a\u6b64\u800c\u6cbe\u6cbe\u81ea \u559c\u3002 \u3000\u3000\u201c\u7ba1\u4e8c\u7237\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u56de\u987e\u540e\u9762\u7684\u4eba\u7fa4\u4e00\u773c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4e00\u6765\u522b\u7684\u4e8b\u8fd8\u5c0f\uff0c\u540d\u5c71\u5374\u8981 \u906d\u52ab\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4e0d\u6562\u60f3\u8c61\u8fd9\u4e9b\u4eba\u4e00\u9f50\u6d8c\u4e0a\u9ec4\u5c71\u65f6\u662f\u4f55\u7b49\u60c5\u51b5\u3002 \u3000\u3000\u201c\u94c1\u7b97\u76d8\u201d\u4e8e\u5e73\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u5fc5\u4e00\u8d77\u4e0a\u5c71\uff0c\u53ea\u8981\u4e09\u4e24\u4eba\u968f\u4e4b\u4e0a\u5c71\u4fbf\u53ef\u4ee5\u4e86\uff0c \u5176\u4f59\u7684\u7b49\u5728\u5c71\u4e0b\u4ea6\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u7ba1\u4e8c\u7237\u5927\u559c\u9053\uff1a\u201c\u6b63\u662f\u6b63\u662f\uff0c\u4e8e\u5144\u9ad8\u89c1\uff0c\u679c\u662f\u4e0d\u51e1\uff0c\u90a3\u4e48\u2014\u2014\u8bf7\u54ea\u4f4d\u4e0a\u5c71\u4e00\u884c\uff1f\u201d \u3000\u3000\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u7b11\u9053\uff1a\u201c\u6211\u5b81\u613f\u5728\u5c71\u4e0b\u5403\u9152\uff0c\u5012\u843d\u5f97\u5feb\u6d3b\u4e9b\u3002\u201d \u3000\u3000\u201c\u94c1\u7b97\u76d8\u201d\u4e8e\u5e73\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u5176\u4e2d\u53ea\u6709\u5305\u5144\u4e0e\u8d3e\u5144\u8f7b\u529f\u8fd8\u9ad8\uff0c\u5c11\u4e0d\u5f97\u8fd8\u662f\u8981\u52b3\u52a8\u4e24\u4f4d\u4e00 \u4e0b\u7684\u3002\u201d \u3000\u3000\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u76ee\u4e2d\u9732\u51fa\u5f97\u610f\u7684\u5149\u91c7\uff0c\u4f46\u53e3\u4e2d\u5374\u6545\u610f\u957f\u53f9\u4e00\u58f0\uff0c\u65e2\u672a\u6e9c\u8d70\uff0c\u96be\u9053\u4eca\u65e5\u4f1a \u6e9c\u8d70\u4e48\uff1f\u201c\u4ed6\u5e72\u67af\u7626\u5c0f\uff0c\u4f46\u5b66\u8d77\u5305\u6653\u5929\u7684\u6837\u5b50\u6765\uff0c\u5374\u5b66\u5f97\u60df\u8096\u60df\u5999\uff0c\u5c31\u8fde\u201d\u7ba1\u4e8c\u7237\u201c\u4e5f\u4e0d\u7981 \u4e3a\u4e4b\u5931\u7b11\u3002\u201d\u2018\u9e21\u51a0\u201c\u5305\u6653\u5929\u53cc\u76ee\u6012\u5f20\uff0c\u773c\u4e2d\u51e0\u4e4e\u8981\u55b7\u51fa\u706b\u6765\u3002\u53ea\u89c1\u8fd9\u201d\u9ed1\u9a74\u8ffd\u98ce\u201c\u7275\u7740\u4ed6 \u7684\u9ed1\u9a74\uff0c\u7f13\u7f13\u8d70\u5230\u4e00\u5904\u6811\u9634\u4e0b\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u53c8\u53eb\u6765\u4e00\u4e9b\u8336\u98df\u9152\u8089\uff0c\u7b11\u9053\uff1a\u201c\u7ba1\u4e8c\u7237\uff0c\u4f60\u6211\u4e0d \u59a8\u6765\u5feb\u6d3b\u5feb\u6d3b\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u8f7b\u629a\u6478\u7740\u9ed1\u9a74\u7684\u9b03\u6bdb\uff0c\u7b11\u7740\u5bf9\u8fd9\u9a74\u5b50\u9053\uff1a\u201c\u8d3e\u513f\uff0c\u6709\u4e9b\u4eba\u771f\u7684\u6ca1\u6709\u4f60\u806a\u660e\uff0c\u4f60\u77e5\u9053 \u4e48\uff1f\u8fd9\u4e48\u70ed\u7684\u5929\uff0c\u4e00\u5b9a\u8981\u8dd1\u4e0a\u5c71\u53bb\uff0c\u4f60\u770b\uff0c\u6211\u4eec\u5728\u8fd9\u91cc\u591a\u8212\u670d\uff0c\u591a\u51c9\u5feb\u3002\u201d \u3000\u3000\u8fd9\u9ed1\u9a74\u7adf\u4f3c\u4e5f\u61c2\u5f97\u4eba\u610f\uff0c\u4f4e\u55e5\u4e86\u4e00\u58f0\uff0c\u4e0d\u4f4f\u5730\u70b9\u5934\uff0c\u770b\u5230\u8fd9\u60c5\u51b5\u7684\u4eba\uff0c\u4ff1\u90ce\u5fcd\u4e0d\u4f4f\u7b11 \u4e86\u3002 \u3000\u3000\u53ea\u6709\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u672a\u7b11\uff0c\u4ed6\u9762\u4e0a\u4e86\u4e00\u9635\u9752\u4e00\u9635\u767d\uff0c\u76ee\u4e2d\u51e0\u4e4e\u8981\u55b7\u51fa\u706b\u6765\uff0c\u4f46\u662f\uff0c\u4e3a\u4e86 \u8868\u793a\u4ed6\u5e76\u4e0d\u6bd4\u90a3\u9a74\u5b50\u7b28\u5f97\u592a\u591a\uff0c\u4ed6\u561f\u6d53\u7740\u5927\u58f0\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u8981\u4e0a\u5c71\uff1f\u6211\u672c\u6765\u5c31\u8981\u7559\u5728\u5c71\u4e0b \u7684\uff01\u201d\u5927\u6b65\u8d70\u5230\u9152\u644a\u524d\uff0c\u4e70\u4e86\u4e9b\u9152\u8089\uff0c\u75db\u6b21\u8d77\u6765\u3002 \u3000\u3000\u2018\u94c1\u7b97\u76d8\u201c\u4e8e\u5e73\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u6697\u4e2d\u5fd6\u9053\uff1a\u201c\u8fd9\u8d3e\u658c\u8a00\u6765\u9887\u6709\u9053\u7406\u2014\u2014\u201d\u4ed6\u5fc3\u673a\u6df1\u6c89\uff0c\u89c1\u4e8b \u6781\u660e\uff0c\u662f\u4ee5\u624d\u4f1a\u53d1\u73b0\u90a3\u201c\u5feb\u8baf\u201d\u82b1\u7389\u7684\u5c38\u8eab\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u6240\u4ee5\u6d3e\u4ed6\u524d\u6765\uff0c\u4ea6\u662f\u6b64\u7406\uff0c\u82e5\u6362 \u4e86\u522b\u4eba\uff0c\u53ea\u6015\u65e9\u5df2\u4e0e\u6765\u65e5\u201c\u98de\u9f99\u9556\u5c40\u201d\u8d3e\u658c\u51b2\u7a81\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u4ea6\u81ea\u5750\u5230\u6811\u4e0b\uff0c\u7565\u4f5c\u6b47\u606f\uff0c\u90a3\u201c\u738b\u80d6\u5b50\u201d\u9762\u4e0a\u59cb\u7ec8\u5e26\u7740\u4e0d\u7f6e\u53ef\u5426\u7684\u7b11\u5bb9\uff0c \u6b64\u523b\u65e9\u5df2\u5750\u5230\u6811\u4e0b\uff0c\u5927\u5403\u5927\u559d\u8d77\u6765\u3002 \u3000\u3000\u4e8e\u662f\u9ec4\u5c71\u811a\u4e0b\uff0c\u65e0\u5f62\u4e2d\u4fbf\u6210\u4e86\u4e00\u5ea7\u589f\u5e02\u3002 \u3000\u3000\u591c\u8272\u6e10\u4e34\uff0c\u8fd9\u91cc\u7adf\u53c8\u51fa\u73b0\u4e86\u8d29\u5356\u706f\u7b3c\u706b\u628a\u7684\u5c0f\u8d29\uff0c\u9152\u8089\u8d29\u5b50\uff0c\u66f4\u662f\u6e90\u6e90\u81ea\u7941\u95e8\u8d76\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u4e09\u4e94\u6210\u7fa4\uff0c\u56f4\u7740\u706f\u7b3c\u706b\u628a\uff0c\u996e\u9152\u5403\u8089\uff0c\u4e5d\u6708\u7684\u665a\u98ce\uff0c\u4e00\u9635\u9635\u5439\u5230\u4ed6\u4eec\u8eab \u4e0a\uff0c\u5f53\u771f\u662f\u201c\u5feb\u6d3b\u201d\u5f97\u5f88\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e00\u5929\u2026\u2026\u4e24\u5929\u2026\u2026\u4e09\u5929\u2026\u2026 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e0e\u88f4\u73cf\u5374\u59cb\u7ec8\u6ca1\u6709\u4e0b\u6765\uff01 \u3000\u3000\u9ec4\u5c71\uff0c\u4e0d\u4f46\u6709\u4e91\uff0c\u800c\u4e14\u6709\u677e\u3001\u6709\u77f3\u3001\u6709\u6cc9\u3002 \u3000\u3000\u9ec4\u5c71\u7684\u4e91\u6d77\uff0c\u662f\u51c4\u8ff7\u800c\u53c8\u7eda\u4e3d\uff0c\u7ef0\u7ea6\u800c\u53c8\u58ee\u89c2\u7684\u3002 \u3000\u3000\u9ec4\u5c71\u7684\u677e\u6d77\uff0c\u5374\u4eff\u4f5b\u6bd4\u4e91\u6d77\u66f4\u6df1\u3001\u66f4\u539a\uff0c\u53c8\u662f\u90a3\u4e48\u591a\uff0c\u90a3\u4e48\u540d\u8d35\u3002 \u3000\u3000\u4f46\u6bcf\u4e00\u682a\uff0c\u5374\u53c8\u90fd\u6709\u7740\u5b83\u72ec\u7279\u7684\u98ce\u683c\u4e0e\u795e\u97f5\u3002 \u3000\u3000\u9ec4\u5c71\u7684\u77f3\uff0c\u66f4\u662f\u7433\u7405\u800c\u591a\u5f69\uff0c\u90a3\u591a\u5f69\u7684\u5c71\u5cf0\u4e0e\u5ca9\u77f3\uff0c\u4e0d\u77e5\u8ff7\u60d1\u4e86\u591a\u5c11\u53e4\u5f80\u4eca\u6765\u753b\u5bb6\u8bd7\u4eba \u7684\u5fc3\u3002 \u3000\u3000\u9ec4\u5c71\u7684\u5bb6\uff0c\u4e0d\u591a\uff0c\u4f46\u4e00\u6761\u4eba\u5b57\u6fe0\uff0c\u4fbf\u5df2\u8981\u5e7b\u51fa\u98d8\u6e3a\u5982\u6e38\u4e1d\u7684\u8ff7\u79bb\u61a7\u61ac\uff0c\u66f4\u4f55\u51b5\u8fd8\u6709\u5929\u77eb \u5982\u9f99\uff0c\u4e5d\u53e0\u58ee\u89c2\u7684\u4e38\u9f99\u6f6d\uff1f\u4ee5\u53ca\u522b\u7684\u6cc9\u4e4b\u6e05\u6f88\u6f84\u660e\uff0c\u73e0\u7802\u6e29\u6cc9\u7684\u7eda\u70c2\u7ea2\u6da6\uff01 \u3000\u3000\u9ec4\u5c71\uff0c\u662f\u8bd7\u4eba\u7b14\u4e0b\u7684\u201c\u7edd\u4ee3\u4f73\u4eba\u201d\uff0c\u800c\u4eca\u8fd9\u201c\u7edd\u4ee3\u4f73\u4eba\u201d\uff0c\u4fbf\u4e5f\u6beb\u4e0d\u4f8b\u5916\u7684\uff0c\u8ff7\u60d1\u4e86 \u201c\u51b7\u8c37\u53cc\u6728\u201d\u4ee5\u53ca\u88f4\u73cf\u7684\u5fc3\u3002 \u3000\u3000\u5a07\u9633\u53e4\u843d\uff0c\u9ec4\u660f\u6e10\u4e34\uff0c\u665a\u971e\u63a9\u6620\u4e0b\u7684\u9ec4\u5c71\u7684\u677e\uff0c\u9ec4\u5c71\u7684\u77f3\uff0c\u9ec4\u5c71\u7684\u6cc9\uff0c\u5728\u6726\u80e7\u4e2d\u53d8\u5f97\u66f4 \u7f8e\u4e86\u3002 \u3000\u3000\u521d\u4e0a\u540d\u5c71\u7684\u88f4\u73cf\uff0c\u72c2\u559c\u5728\u8fd9\u65b0\u7684\u5929\u5730\u91cc\uff0c\u4ed6\u4e00\u8def\u4e0a\u5c71\uff0c\u6bcf\u4e0a\u4e00\u6b65\uff0c\u90fd\u66f4\u8c26\u5351\u5730\u627f\u8ba4\u4e86\u5929 \u5730\u7684\u6d69\u5927\uff0c\u4e0e\u81ea\u8eab\u7684\u6e3a\u5c0f\uff0c\u4ed6\u53ea\u6068\u81ea\u5df1\u6ca1\u6709\u8bd7\u4eba\u7684\u9526\u7b3a\u4e0e\u753b\u5bb6\u7684\u5f69\u7b14\uff0c\u5199\u4e0d\u51fa\u5fc3\u4e2d\u6240\u611f\u89c9\u7684 \u591a\u5f69\u4e0e\u7eda\u4e3d\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u51b7\u5ced\u7684\u9762\u5bb9\uff0c\u4e5f\u6709\u7740\u6bd4\u5e73\u65e5\u8f83\u591a\u7684\u60c5\u611f\u4e4b\u6d41\u9732\u3002 \u3000\u3000\u7acb\u5728\u59cb\u4fe1\u5cf0\u524d\uff0c\u9669\u5cfb\u800c\u7075\u5947\u7684\u63a5\u5f15\u677e\u4e0b\uff0c\u51b7\u5bd2\u7af9\u6781\u76ee\u56db\u671b\uff0c\u7a81\u5730\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u7f13\u9053\uff1a \u201c\u90a3\u73ed\u5e9f\u7269\uff0c\u600e\u5730\u6ca1\u6709\u9650\u4e0a\u6765\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u53ea\u6015\u4ee5\u4e3a\u6211\u7b49\u5b9a\u4f1a\u5faa\u539f\u8def\u4e0b\u5c71\uff0c\u662f\u4ee5\u4fbf\u4e50\u5f97\u8212\u8212\u670d\u670d\u5730\u7b49\u5728\u5c71\u4e0b\uff0c \u5176\u5b9e\uff0c\u6211\u7b49\u6a2a\u7a7f\u2018\u94c1\u76d8\u5934\u2019\u8d8a\u8fc7\u2018\u59cb\u4fe1\u5cf0\u2019\uff0c\u4ece\u90a3\u8fb9\u4e0b\u5c71\u6709\u4f55\u4e0d\u53ef\uff1f\u4e5f\u597d\u8ba9\u8fd9\u822c\u5e9f\u7269\uff0c\u597d\u597d \u5730\u7740\u6025\u4e00\u6bb5\u65e5\u5b50\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u76ee\u5149\u95ea\u52a8\uff0c\u8fd9\u51b7\u50fb\u7684\u602a\u4eba\u4e0d\u77e5\u662f\u4e0d\u662f\u56e0\u4e3a\u8fd9\u540d\u5c71\u80dc\u5883\u7684\u6f5c\u79fb\u9ed8\u5316\uff0c\u6b64\u523b\u7ade\u653e\u58f0\u5927 \u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u201d \u3000\u3000\u7b11\u58f0\u4e00\u987f\uff0c\u4ed6\u7a81\u5730\u53d1\u89c9\u5c71\u95f4\u7684\u6e05\u9759\u2014\u2014\u6d41\u6c34\u58f0\u4e0e\u677e\u6d9b\u867d\u7136\u4e5f\u662f\u58f0\u97f3\uff0c\u4f46\u8fd9\u79cd\u58f0\u97f3\u90e8\u80fd\u4f7f \u201c\u6e05\u9759\u201d\u53d8\u5f97\u66f4\u52a0\u201c\u6e05\u9759\u201d\u2014\u2014\u4ed6\u653e\u773c\u56db\u671b\uff0c\u5f69\u971e\u5df2\u843d\uff0c\u7fa4\u5c71\u5bc2\u5bc2\uff0c\u591c\uff0c\u7adf\u5df2\u5f88\u6df1\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u5144\u5f1f\u65e2\u5df2\u7acb\u4e0b\u7684\u4e3b\u610f\uff0c\u662f\u7edd\u5bf9\u4e0d\u4f1a\u53d8\u66f4\u7684\uff0c\u4ed6\u4eec\u7b14\u76f4\u8d8a\u4e0a\u59cb\u4fe1\u5cf0\uff0c\u4e00\u8def\u4e0a\uff0c\u8fd9\u4e24\u4e2a \u602a\u4eba\u4fbf\u4e58\u673a\u6559\u7740\u88f4\u73cf\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u8fd9\u6bb5\u5c71\u8def\u662f\u5d0e\u5c96\u800c\u9669\u5cfb\u7684\uff0c\u5bf9\u88f4\u73cf\u6765\u8bf4\uff0c\u6bd5\u7adf\u662f\u592a\u8270\u82e6\u4e86 \u4e9b\u3002 \u3000\u3000\u4f46\u5174\u594b\u7740\u7684\u88f4\u73cf\uff0c\u5374\u6beb\u4e0d\u5728\u610f\uff0c\u4ed6\u9aa4\u7136\u89c9\u5f97\u81ea\u5df1\u7684\u8eab\u6cd5\uff0c\u5df2\u6709\u4e86\u6bd4\u5f80\u65e5\u6570\u500d\u7684\u8f7b\u7075\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u51b7\u51b7\u9053\uff1a\u201c\u5230\u4e86\u2018\u59cb\u4fe1\u5cf0\u2019\u5dc5\uff0c\u4f60\u5f97\u597d\u751f\u51c6\u5907\u7740\u5b66\u4e00\u5957\u638c\u6cd5\uff0c\u54fc\u54fc\uff01\u6211\u770b\u4f60\u672a\u89c1 \u5c31\u80fd\u5b66\u5f97\u4f1a\u7684\u3002\u201d \u3000\u3000\u4ed6\u5bf9\u88f4\u73cf\u8bf4\u8bdd\u65f6\u7684\u8bed\u58f0\uff0c\u59cb\u7ec8\u90fd\u662f\u51b7\u51b0\u51b0\u7684\uff0c\u4f46\u88f4\u73cf\u5374\u65e9\u5df2\u4e60\u60ef\uff0c\u800c\u4e14\u6109\u5feb\u5730\u63a5\u53d7\u4e86\u3002 \u4ed6\u5174\u594b\u5730\u5e94\u627f\u7740\uff0c\u7a81\u7136\u53d1\u73b0\u201c\u59cb\u4fe1\u5cf0\u201d\u5dc5\u5df2\u5728\u773c\u524d\uff0c\u4e5f\u7a81\u7136\u53d1\u73b0\uff0c\u81ea\u5df1\u4e0e\u70c1\u70c1\u7684\u661f\u7a7a\uff0c\u7adf\u662f \u5982\u6b64\u63a5\u8fd1\u3002 \u3000\u3000\u5c24\u5176\u6709\u4e00\u70b9\u661f\u5149\uff0c\u4eff\u4f5b\u5c31\u5728\u4ed6\u7684\u5934\u9876\u2014\u2014\u8fd9\u4e00\u70b9\u661f\u5149\u662f\u5fae\u5f31\u800c\u95ee\u52a8\u7740\u7684\uff0c\u5174\u594b\u5730\u6062\u590d\u4e86 \u7ae5\u5e74\u7684\u5e7b\u60f3\uff1a\u201c\u5440\uff01\u6211\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u5c06\u8fd9\u70b9\u661f\u5149\u6458\u5728\u624b\u91cc\uff01\u201d \u3000\u3000\u4f46\u51b7\u5bd2\u7af9\u7684\u4e00\u58f0\u8f7b\u547c\uff0c\u5374\u7c89\u788e\u4e86\u4ed6\u7684\u51a5\u60f3\uff0c\u591c\u8272\u4e2d\uff0c\u53ea\u89c1\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6ee1\u9762\u4ff1\u662f\u60ca\u8bb6\u4e4b \u8272\u3002 \u3000\u3000\u51b7\u67af\u6728\u8eab\u5f62\u4e00\u987f\uff0c\u76ee\u5149\u51dd\u6ce8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u4f60\u770b\u90a3\u662f\u5426\u662f\u706f\u5149\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u70b9\u4e86\u70b9\u5934\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u706f\u5149\uff01\u201d \u3000\u3000\u8981\u4f7f\u8fd9\u5144\u5f1f\u4e8c\u4eba\u9732\u51fa\u60ca\u8bb6\u4e4b\u8272\uff0c\u786e\u975e\u6613\u4e8b\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u8fd9\u9669\u5cfb\u7684\u201c\u59cb\u4fe1\u5cf0\u201d\u5dc5\uff0c\u7ade\u4f1a \u6709\u706f\u5149\u95ea\u70c1\uff0c\u5374\u5b9e\u5728\u4ee4\u4eba\u60ca\u5f02\u3002 \u3000\u3000\u5c71\u98ce\u5f3a\u52b2\uff0c\u88f4\u73cf\u7a81\u5730\u89c9\u5f97\u4e00\u9635\u5bd2\u610f\uff0c\u81ea\u811a\u5e95\u6d8c\u8d77\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8eab\u5f62\u5c55\u52a8\uff0c\u5df2\u8f7b\u8f7b\u5411\u90a3 \u706f\u706b\u4eae\u5904\u6251\u53bb\u3002\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u624d\u53d1\u89c9\u81ea\u5df1\u662f\u5b64\u96f6\u8212\u8212\u670d\u670d\u5730\u7b49\u5728\u5c71\u4e0b\uff0c\u5176\u5b9e\uff0c\u6211\u7b49\u6a2a\u7a7f\u2018\u94c1 \u76d8\u5934\u2019\u8d8a\u8fc7\u2018\u59cb\u4fe1\u5cf0\u2019\uff0c\u4ece\u90a3\u8fb9\u4e0b\u5c71\u6709\u4f55\u4e0d\u53ef\uff1f\u4e5f\u597d\u8ba9\u8fd9\u822c\u5e9f\u7269\uff0c\u597d\u597d\u5730\u7740\u6025\u4e00\u6bb5\u65e5\u5b50\u3002 \u201c\u51b7\u5bd2\u7af9\u76ee\u5149\u95ea\u52a8\uff0c\u8fd9\u51b7\u50fb\u7684\u602a\u4eba\u4e0d\u77e5\u662f\u4e0d\u662f\u56e0\u4e3a\u8fd9\u540d\u5c71\u80dc\u5883\u7684\u6f5c\u79fb\u9ed8\u5316\uff0c\u6b64\u523b\u7ade\u653e\u58f0\u5927\u7b11 \u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u201d \u3000\u3000\u7b11\u58f0\u4e00\u987f\uff0c\u4ed6\u7a81\u5730\u53d1\u89c9\u5c71\u95f4\u7684\u6e05\u9759\u2014\u2014\u6d41\u6c34\u58f0\u4e0e\u677e\u6d9b\u867d\u7136\u4e5f\u662f\u58f0\u97f3\uff0c\u4f46\u8fd9\u79cd\u58f0\u97f3\u90e8\u80fd\u4f7f \u201c\u6e05\u9759\u201d\u53d8\u5f97\u66f4\u52a0\u201c\u6e05\u9759\u201d\u2014\u2014\u4ed6\u653e\u773c\u56db\u671b\uff0c\u5f69\u971e\u5df2\u843d\uff0c\u7fa4\u5c71\u5bc2\u5bc2\uff0c\u591c\uff0c\u7adf\u5df2\u5f88\u6df1\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u5144\u5f1f\u65e2\u5df2\u7acb\u4e0b\u7684\u4e3b\u610f\uff0c\u662f\u7edd\u5bf9\u4e0d\u4f1a\u53d8\u66f4\u7684\uff0c\u4ed6\u4eec\u7b14\u76f4\u8d8a\u4e0a\u59cb\u4fe1\u5cf0\uff0c\u4e00\u8def\u4e0a\uff0c\u8fd9\u4e24\u4e2a \u602a\u4eba\u4fbf\u4e58\u673a\u6559\u7740\u88f4\u73cf\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u8fd9\u6bb5\u5c71\u8def\u662f\u5d0e\u5c96\u800c\u9669\u5cfb\u7684\uff0c\u5bf9\u88f4\u73cf\u6765\u8bf4\uff0c\u6bd5\u7adf\u662f\u592a\u8270\u82e6\u4e86 \u4e9b\u3002 \u3000\u3000\u4f46\u5174\u594b\u7740\u7684\u88f4\u73cf\uff0c\u5374\u6beb\u4e0d\u5728\u610f\uff0c\u4ed6\u9aa4\u7136\u89c9\u5f97\u81ea\u5df1\u7684\u8eab\u6cd5\uff0c\u5df2\u6709\u4e86\u6bd4\u5f80\u65e5\u6570\u500d\u7684\u8f7b\u7075\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u51b7\u51b7\u9053\uff1a\u201c\u5230\u4e86\u2018\u59cb\u4fe1\u5cf0\u2019\u5dc5\uff0c\u4f60\u5f97\u597d\u751f\u51c6\u5907\u7740\u5b66\u4e00\u5957\u638c\u6cd5\uff0c\u54fc\u54fc\uff01\u6211\u770b\u4f60\u672a\u89c1 \u5c31\u80fd\u5b66\u5f97\u4f1a\u7684\u3002\u201d \u3000\u3000\u4ed6\u5bf9\u88f4\u73cf\u8bf4\u8bdd\u65f6\u7684\u8bed\u58f0\uff0c\u59cb\u7ec8\u90fd\u662f\u51b7\u51b0\u51b0\u7684\uff0c\u4f46\u88f4\u73cf\u5374\u65e9\u5df2\u4e60\u60ef\uff0c\u800c\u4e14\u6109\u5feb\u5730\u63a5\u53d7\u4e86\u3002 \u4ed6\u5174\u594b\u5730\u5e94\u627f\u7740\uff0c\u7a81\u7136\u53d1\u73b0\u201c\u59cb\u4fe1\u5cf0\u201d\u5dc5\u5df2\u5728\u773c\u524d\uff0c\u4e5f\u7a81\u7136\u53d1\u73b0\uff0c\u81ea\u5df1\u4e0e\u70c1\u70c1\u7684\u661f\u7a7a\uff0c\u7adf\u662f \u5982\u6b64\u63a5\u8fd1\u3002 \u3000\u3000\u5c24\u5176\u6709\u4e00\u70b9\u661f\u5149\uff0c\u4eff\u4f5b\u5c31\u5728\u4ed6\u7684\u5934\u9876\u2014\u2014\u8fd9\u4e00\u70b9\u661f\u5149\u662f\u5fae\u5f31\u800c\u95ee\u52a8\u7740\u7684\uff0c\u5174\u594b\u5730\u6062\u590d\u4e86 \u7ae5\u5e74\u7684\u5e7b\u60f3\uff1a\u201c\u5440\uff01\u6211\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u5c06\u8fd9\u70b9\u661f\u5149\u6458\u5728\u624b\u91cc\uff01\u201d \u3000\u3000\u4f46\u51b7\u5bd2\u7af9\u7684\u4e00\u58f0\u8f7b\u547c\uff0c\u5374\u7c89\u788e\u4e86\u4ed6\u7684\u51a5\u60f3\uff0c\u591c\u8272\u4e2d\uff0c\u53ea\u89c1\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6ee1\u9762\u4ff1\u662f\u60ca\u8bb6\u4e4b \u8272\u3002 \u3000\u3000\u51b7\u67af\u6728\u8eab\u5f62\u4e00\u987f\uff0c\u76ee\u5149\u51dd\u6ce8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u4f60\u770b\u90a3\u662f\u5426\u662f\u706f\u5149\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u70b9\u4e86\u70b9\u5934\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u706f\u5149\uff01\u201d \u3000\u3000\u8981\u4f7f\u8fd9\u5144\u5f1f\u4e8c\u4eba\u9732\u51fa\u60ca\u8bb6\u4e4b\u8272\uff0c\u786e\u975e\u6613\u4e8b\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u8fd9\u9669\u5cfb\u7684\u201c\u59cb\u4fe1\u5cf0\u201d\u5dc5\uff0c\u7ade\u4f1a \u6709\u706f\u5149\u95ea\u70c1\uff0c\u5374\u5b9e\u5728\u4ee4\u4eba\u60ca\u5f02\u3002 \u3000\u3000\u5c71\u98ce\u5f3a\u52b2\uff0c\u88f4\u73cf\u7a81\u5730\u89c9\u5f97\u4e00\u9635\u5bd2\u610f\uff0c\u81ea\u811a\u5e95\u6d8c\u8d77\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8eab\u5f62\u5c55\u52a8\uff0c\u5df2\u8f7b\u8f7b\u5411\u90a3 \u706f\u706b\u4eae\u5904\u6251\u53bb\u3002\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u624d\u53d1\u89c9\u81ea\u5df1\u662f\u5b64\u96f6\u96f6\u5730\u7ad9\u5728\u4e00\u65b9\u7a81\u51fa\u5c71\u77f3\u4e0a\uff0c\u4eff\u4f5b\u7acb\u5728\u5929\u5730\u7684 \u4e2d\u592e\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u65e0\u6cd5\u8ffd\u53ca\u201c\u51b7\u8c37\u53cc\u6728\u201d\u90a3\u95ea\u7535\u822c\u7684\u8eab\u5f62\uff0c\u53ea\u5f97\u76d8\u819d\u5728\u8fd9\u65b9\u5c71\u77f3\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u5c71\u98ce \u5439\u52a8\uff0c\u4ed6\u4e0d\u5b89\u5730\u6574\u7406\u4e00\u4e0b\u8863\u886b\u3002 \u3000\u3000\u7a81\u5730\uff01\u4ed6\u53d1\u89c9\u811a\u4e0b\u7684\u5c71\u77f3\u4e5f\u968f\u4e4b\u8f7b\u8f7b\u6447\u52a8\u4e86\u4e00\u4e0b\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u7eb5\u7136\u662f\u8fd9\u79cd\u6781\u4e3a\u8f7b\u5fae\u7684\u6447 \u52a8\uff0c\u5df2\u8db3\u4ee5\u4ee4\u4ed6\u5fc3\u5934\u9707\u8361\uff0c\u4e8e\u662f\u4ed6\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8dc3\u4e86\u4e0b\u53bb\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u7a81\u53c8\u53d1\u89c9\u5728\u8fd9\u65b9\u5c71\u77f3\u7684 \u6839\u90e8\uff0c\u7adf\u4e5f\u6709\u4e00\u4e9b\u5fae\u5149\uff01 \u3000\u3000\u4ed6\u5fc3\u5934\u4e00\u60ca\uff0c\u56de\u9996\u671b\u53bb\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u8eab\u5f62\uff0c\u5df2\u88ab\u9ed1\u8272\u4e0e\u5c71\u77f3\u9690\u6ca1\u2014\u2014\u4ed6\u5012\u501f\u7740\u9ed1\u8272 \u4e0e\u5c71\u77f3\u6765\u9690\u85cf\u81ea\u5df1\u7684\u8eab\u5f62\uff0c\u8fc2\u56de\u7740\u5411\u90a3\u5fae\u5149\u6251\u53bb\uff0c\u8fd9\u4e00\u70b9\u70b9\u5149\u770b\u6765\u867d\u8fd1\uff0c\u5176\u5b9e\u5374\u8fdc\u6bd4\u4ed6\u4eec\u60f3 \u8c61\u4e2d\u9065\u8fdc\uff01 \u3000\u3000\u88f4\u73cf\u5fae\u4e00\u601d\u5fd6\uff0c\u5fcd\u4e0d\u4f4f\u4fef\u4e0b\u8eab\u53bb\uff0c\u5c1d\u8bd5\u7740\u53bb\u8f7b\u8f7b\u63a8\u52a8\u8fd9\u65b9\u5c71\u77f3\u2014\u2014\u5440\uff0c\u5c71\u77f3\u679c\u7136\u968f\u7740\u4ed6 \u7684\u624b\u52bf\uff0c\u8f7b\u8f7b\u79fb\u52a8\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e00\u9053\u5149\u7ebf\uff0c\u968f\u7740\u5c71\u77f3\u7684\u79fb\u52a8\uff0c\u76f4\u5c04\u4ed6\u773c\u5e18\uff0c\u5149\u7ebf\u867d\u5fae\u5f31\uff0c\u4f46\u5728\u8fd9\u51c4\u6e05\u7684\u51b7\u591c\u91cc\uff0c\u5374\u4f3c\u4e4e \u6bd4\u6570\u5341\u9053\u706b\u70ac\u7684\u5149\u8292\u8fd8\u8981\u660e\u4eae\uff01 \u3000\u3000\u4ed6\u95fa\u4e0a\u4e86\u773c\u5e18\uff0c\u7acb\u523b\u7741\u5f00\uff0c\u5fae\u5fae\u98a4\u6296\u7684\u624b\u638c\uff0c\u518d\u6b21\u5411\u5916\u4e00\u63a8\uff0c\u5c71\u77f3\u4e0b\u4fbf\u9732\u51fa\u4e86\u4e00\u6761\u79d8\u9053 \u7684\u4eba\u53e3\u3002 \u3000\u3000\u4e00\u9635\u6f6e\u6e7f\u800c\u5fae\u5e26\u9709\u81ed\u7684\u51b7\u6c14\uff0c\u6251\u9762\u51fb\u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u4ed6\u56de\u8fc7\u5934\uff0c\u53ea\u542c\u81ea\u5df1\u7684\u5fc3\u623f\uff0c\u6709\u5982\u51fb \u9f13\u822c\u5730\u8df3\u52a8\u7740\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4ecd\u672a\u51fa\u73b0\u8e2a\u5f71\uff0c\u661f\u7a7a\u5374\u4eff\u4f5b\u9aa4\u7136\u79bb\u4ed6\u8fdc\u5f97\u591a\uff0c\u591c\u98ce\u4e2d\u7684\u5bd2\u610f\u66f4\u91cd\u4e86\uff01\u4ed6\u6ca1 \u6709\u60ca\u547c\u51fa\u58f0\uff0c\u4e0d\u77e5\u662f\u56e0\u4e3a\u4ed6\u6709\u8db3\u591f\u7684\u52c7\u6c14\uff0c\u6291\u6216\u662f\u5f3a\u70c8\u7684\u81ea\u5c0a\uff0c\u4ed6\u53ea\u662f\u6728\u7136\u7ad9\u5728\u79d8\u9053\u7684\u4eba\u53e3 \u8fb9\uff0c\u76f4\u5230\u5730\u9053\u4e2d\u4f20\u51fa\u4e00\u58f0\u54c0\u547c\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u5fae\u5f31\u3001\u75db\u82e6\u3001\u60b2\u54c0\u3001\u98a4\u6296\u7684\u54c0\u547c\uff0c\u5b9b\u5982\u4e00\u6839\u51b0\u51b7\u7684\u5c16\u9488\uff0c\u7b14\u76f4\u5730\u523a\u4eba\u4ed6\u5fc3\u91cc\uff01 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u673a\u4f36\u601c\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u53cc\u62f3\u7d27\u63e1\uff0c\u638c\u5fc3\u5374\u5df2\u6d41\u51fa\u4e86\u51b7\u6c57\u535c\u63a5\u7740\uff0c\u53c8\u662f\u4e00\u58f0\u60b2\u54c0\u800c \u75db\u82e6\u7684\u54c0\u547c\uff0c\u8f7b\u5fae\u800c\u98a4\u6296\u5730\u98d8\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u54c0\u547c\uff0c\u4f7f\u5f97\u4ed6\u547c\u5438\u4e0e\u8840\u6db2\uff0c\u90fd\u50cf\u662f\u51b0\u96ea\u4e00\u822c\u5730\u51dd\u7ed3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6050\u60e7\uff01\u6050\u60e7\u5374\u53c8\u52a0\u4e0a\u4e86\u60ca\u5f02\uff0c\u8fd9\u54c0\u58f0\u5728\u4ed6\u8033\u4e2d\u542c\u6765\uff0c\u7adf\u662f\u8fd9\u822c\u719f\u6089\u2014\u2014\u523b\u9aa8\u94ed\u5fc3\uff0c\u65e0\u6cd5 \u5fd8\u6000\u5730\u719f\u6089\uff0c\u4f46\u4ed6\u5374\u53c8\u504f\u504f\u60f3\u4e0d\u8d77\u7a76\u7adf\u662f\u5c5e\u4e8e\u8c01\u7684\uff1f\u5c31\u50cf\u662f\u7ae5\u5e74\u7684\u68a6\u9b54\uff0c\u662f\u90a3\u4e48\u6a21\u7cca\uff0c\u5374\u53c8 \u662f\u90a3\u822c\u6e05\u6670\u3002 \u3000\u3000\u4ed6\u7259\u5173\u4e00\u54ac\uff0c\u773c\u5e18\u5fae\u9616\uff0c\u7791\u76ee\u5411\u79d8\u9053\u4eba\u53e3\u8df3\u4e0b\u53bb\uff0c\u8fd9\u5947\u5f02\u7684\u5c11\u5e74\uff0c\u5e38\u5e38\u4f1a\u6709\u4e00\u79cd\u5947\u5f02\u7684 \u52c7\u6c14\uff0c\u53bb\u63a5\u53d7\u522b\u4eba\u90fd\u65e0\u6cd5\u63a5\u53d7\u7684\u75db\u82e6\uff0c\u53bb\u5c1d\u8bd5\u522b\u4eba\u90fd\u4e0d\u6562\u5c1d\u8bd5\u7684\u6050\u60e7\uff0c\u5c31\u662f\u8fd9\u4efd\u52c7\u6c14\uff0c\u4f7f\u5f97 \u4ed6\u4e0d\u6b62\u4e00\u6b21\u5730\u505a\u51fa\u4e86\u522b\u4eba\u90fd\u4e0d\u6562\u505a\u7684\u4e8b\uff01 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5e76\u975e\u4e0d\u77e5\u6050\u60e7\uff0c\u751a\u81f3\u4ed6\u7684\u53cc\u817f\uff0c\u90fd\u56e0\u6050\u60e7\u800c\u53d8\u5f97\u8f6f\u5f31\u800c\u53c8\u9ebb\u6728\u8d77\u6765\uff01\u56e0\u4e3a\uff0c\u5f53 \u9762\u4e34\u5371\u96be\u4e4b\u9645\uff0c\u6050\u60e7\u672c\u8eab\uff0c\u672c\u662f\u4e00\u79cd\u201c\u5065\u7f8e\u201d\u800c\u201c\u660e\u667a\u201d\u7684\u53cd\u5e94\uff0c\u662f\u4e0d\u5fc5\u8bb3\u8a00\uff0c\u4e5f\u4e0d\u5fc5\u6291\u5236 \u7684\uff0c\u53ea\u662f\u5e94\u5f53\u5c06\u5176\u8f6c\u5316\u4e3a\u201c\u52c7\u201d\u800c\u5df2\uff0c\u800c\u201c\u52c7\u201d\uff0c\u4e5f\u5c31\u662f\u5e94\u4ed8\u5371\u9669\u7684\u667a\u6167\uff01 \u3000\u3000\u4ed6\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u8dcc\u843d\u5728\u575a\u786c\u800c\u51b0\u51b7\u7684\u77f3\u5730\u4e0a\uff0c\u4ed6\u53cc\u624b\u4e00\u6491\uff0c\u7acb\u5f85\u817e\u8eab\u8dc3\u8d77\uff0c\u4f46\u662f\u4ed6\u624b \u638c\u63a5\u89e6\u5230\u7684\uff0c\u5374\u5df2\u4e0d\u662f\u575a\u786c\u7684\u77f3\u5730\uff0c\u800c\u662f\u2014\u2014\u7adf\u662f\u4e00\u53ea\u51b0\u51b7\u7684\u3001\u5e72\u67af\u800c\u50f5\u786c\u7684\u624b\u638c\uff01 \u3000\u3000\u4e00\u79cd\u96be\u4ee5\u63cf\u6479\u7684\u611f\u89c9\uff0c\u5239\u90a3\u95ee\u7531\u6307\u5c16\u76f4\u8fbe\u4ed6\u5fc3\u623f\uff0c\u4f7f\u5f97\u4ed6\u8eab\u8eaf\u4e00\u9707\uff0c\u95ea\u7535\u822c\u8df3\u8d77\uff0c\u76ee\u5149 \u754f\u7f29\u5730\u8f6c\u5411\u4ed6\u65b9\u624d\u624b\u638c\u6240\u6491\u4e4b\u5904\uff0c\u660f\u9ec4\u7684\u5149\u7ebf\u4e0b\uff0c\u58a8\u9ed1\u7684\u77f3\u5730\u4e0a\uff0c\u7adf\u6709\u4e00\u53ea\u4e11\u6076\u3001\u6b7b\u7070\u7684\u65ad \u638c\uff01 \u3000\u3000\u65ad\u638c\u65c1\uff0c\u662f\u4e00\u53ea\u4e11\u6076\u7684\u9ed1\u6f06\u6728\u5323\uff0c\u53e6\u6709\u4e09\u4e94\u53ea\u76f8\u540c\u7684\u65ad\u638c\uff0c\u6563\u843d\u5728\u6728\u5323\u8fb9\uff0c\u8fd9\u4e9b\u624b\u638c\u5df2 \u53d8\u5f97\u5e72\u67af\u800c\u50f5\u786c\u840e\u7f29\uff0c\u663e\u7136\u662f\u81ea\u4eba\u4f53\u5272\u4e0b\u5df2\u4e45\uff0c\u638c\u7aef\u7684\u6307\u7532\uff0c\u5728\u660f\u9eef\u4e2d\u5448\u73b0\u7740\u6b7b\u7070\u8272\u7684\u51b7 \u5149\u3002 \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u4e00\u9635\u5455\u5410\u4e4b\u610f\uff0c\u81ea\u80f8\u810f\u7ffb\u6d8c\u800c\u4e0a\uff0c\u4e00\u624b\u6342\u4f4f\u5634\u5507\uff0c\u659c\u659c\u5411\u524d\u51b2\u51fa\u6570\u6b65\uff0c\u7ec8\u4e8e\u8fd8 \u662f\u5fcd\u4e0d\u4f4f\u5455\u51fa\u4e00\u6ee9\u7eff\u6c34\u3002 \u3000\u3000\u62ac\u76ee\u671b\u53bb\uff0c\u524d\u9762\u662f\u4e00\u6761\u72ed\u7a84\u7684\u79d8\u9053\uff0c\u4e00\u4e2a\u5df2\u5c06\u70e7\u6b8b\u7684\u706b\u628a\uff0c\u659c\u63d2\u5728\u5c71\u5ca9\u4e0a\uff0c\u706b\u628a\u4e0b\u8d6b\u7136 \u6709\u4e00\u201c\u67c4\u65ad\u5251\uff0c\u5251\u67c4\u5728\u5de6\uff0c\u5251\u5c16\u5374\u8fdc\u8fdc\u843d\u5728\u53f3\u8fb9\uff0c\u65e0\u60c5\u5730\u6307\u7740\u4e00\u675f\u65ad\u53d1\uff0c\u518d\u8fc7\u53bb\uff0c\u6709\u4e00\u65b9\u9526 \u5e03\uff0c\u4eff\u4f5b\u662f\u88ab\u5200\u5251\u5272\u4e0b\u7684\u888d\u89d2\u3002\u5730\u9053\u7684\u5c3d\u5904\uff0c\u53f3\u9762\u4f3c\u4e4e\u6709\u4e00\u5904\u6d1e\u7a9f\uff0c\u4e00\u7247\u8f83\u4e3a\u660e\u4eae\u7684\u5149\u7ebf\uff0c \u81ea\u91cc\u9762\u6295\u843d\u51fa\u6765\uff0c\u5149\u5f71\u4e2d\u7adf\u8d6b\u7136\u6709\u7740\u4e00\u6761\u9ed1\u8272\u7684\u4eba\u5f71\uff0c\u88ab\u95ea\u52a8\u7684\u5149\u5f71\u957f\u957f\u5730\u5370\u5728\u7070\u9eef\u7684\u77f3 \u5730\u3002\u5947\u602a\u7684\u662f\uff0c\u88f4\u73cf\u65b9\u81ea\u90a3\u4e00\u9635\u5455\u5410\u4e4b\u58f0\u8fc7\u540e\uff0c\u90a3\u91cc\u9762\u4ecd\u7136\u5bc2\u65e0\u53cd\u5e94\uff0c\u751f\u50cf\u662f\u91cc\u9762\u7684\u4eba\u5168\u5df2 \u6b7b\u4e86\u4e00\u6837\u3002\u88f4\u73cf\u4f38\u624b\u4e00\u62b9\u5634\u89d2\uff0c\u7a81\u542c\u201d\u54d7\u5265\u201c\u4e00\u58f0\uff0c\u706b\u628a\u71c3\u5c3d\uff0c\u79d8\u9053\u4e2d\u9aa4\u7136\u9ed1\u5750\u3002\u201d\u90a3\u54c0\u547c \u58f0\u96be\u9053\u5c31\u662f\u8fd9\u4eba\u5f71\u53d1\u51fa\u7684\u4e48\uff1f\u6b64\u4eba\u83ab\u975e\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201c\u4ed6\u84e6\u5730\u4e00\u6b65\u51b2\u4e86\u8fc7\u53bb\uff0c\u4e00\u6761\u7eaf\u767d\u7684\u80cc \u5f71\uff0c\u7acb\u523b\u95ee\u7535\u822c\u6620\u73b0\u5728\u4ed6\u773c\u4e2d\uff0c\u7eaf\u767d\u7684\u8863\u886b\uff0c\u6f06\u9ed1\u7684\u5934\u53d1\u2026\u2026\u4ed6\u7684\u53cc\u817f\u4e00\u8f6f\uff0c\u518d\u4e5f\u65e0\u6cd5\u5411\u524d \u79fb\u52a8\u534a\u6b65\uff0c\u53ea\u89c1\u6b64\u4eba\u84e6\u5730\u56de\u8fc7\u5934\u6765\uff0c\u8d6b\u7136\u73b0\u51fa\u4e00\u5f20\u75db\u82e6\u3001\u60b2\u54c0\uff0c\u5374\u53c8\u719f\u6089\u7684\u9762\u5bb9\uff0c\u5c31\u50cf\u662f\u9ed1 \u591c\u4e2d\u7684\u4e00\u9053\u95ea\u7535\uff0c\u9739\u96f3\u4e00\u58f0\uff0c\u51fb\u5728\u4ed6\u8eab\u4e0a\uff01\u56e0\u4e3a\uff0c\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5448\u73b0\u5728\u4ed6\u773c\u4e2d\u7684\u9762\u5e9e\uff0c\u7adf\u662f \u90a3\u4e48\u82cd\u767d\u3001\u60b2\u54c0\uff0c\u800c\u53c8\u523b\u9aa8\u94ed\u5fc3\u7684\u719f\u6089\uff0c\u8fd9\u9762\u5e9e\u5c31\u50cf\u662f\u4e00\u6839\u65e0\u5f62\u7684\u97ad\u4e8e\uff0c\u201d\u5427\u201c\u5730\u4e00\u54cd\uff0c\u97ad \u631e\u5728\u88f4\u73cf\u5fc3\u5e95\uff0c\u97ad\u631e\u5728\u4ed6\u7075\u9b42\u7684\u6df1\u5904\u3002\u4ed6\u5403\u60ca\u5730\u201d\u5440\u201c\u4e86\u4e00\u58f0\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u600e\u4f1a\u662f \u4f60\uff1f\u201d \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u5728\u8fd9\u51c4\u6e05\u7684\u9ec4\u5c71\u4e4b\u5cad\uff0c\u5728\u8fd9\u795e\u79d8\u800c\u9634\u68ee\u7684\u6d1e\u7a9f\u91cc\uff0c\u8fd9\u5e7d\u7075\u822c\u76d8\u819d\u800c\u5750\u7684 \u4eba\uff0c\u7adf\u4f1a\u662f\u201c\u51b7\u6708\u4ed9\u5b50\u201d\uff01 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u827e\u9752\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u5149\u7ebf\u5916\u9ed1\u6697\u7684\u5730\u9053\u4e2d\uff0c\u4f2b\u7acb\u7740\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u5979\u9aa4\u773c\u4e4b\u4e0b\uff0c\u8fd8\u672a\u770b\u6e05\u4ed6\u7684\u9762\u5bb9\uff0c\u4f46\u8fd9\u4e00\u58f0\u60ca\u5524\uff0c\u5374\u5524\u8d77\u5979\u7684\u8bb0\u5fc6\uff0c\u5979\u4e0d\u7981\u4e5f\u4e3a\u4e4b\u5931\u58f0 \u60ca\u547c\uff1a\u201c\u4f60\u2026\u2026\u600e\u4f1a\u662f\u4f60\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4e00\u6b65\u51b2\u4e86\u8fc7\u6765\uff0c\u4f46\u970e\u773c\u4e4b\u95f4\uff0c\u4ed6\u811a\u6b65\u5374\u53c8\u500f\u7136\u987f\u4f4f\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u6df1\u9083\u7684\u6d1e\u7a9f\uff0c\u5012\u5782\u7740\u949f\u4e73\uff0c\u88ab\u4e00\u76cf\u6cdb\u7eff\u7684\u94dc\u706f\u4e2d\u7684\u660f\u9ec4\u706f\u5149\uff0c\u6620\u5f97\u591a\u5f69\u800c\u7f24 \u7eb7\u3002 \u3000\u3000\u591a\u5f69\u800c\u7f24\u7eb7\u7684\u949f\u4e73\u4e0b\uff0c\u76d8\u819d\u7aef\u5750\u7740\u4e24\u4eba\uff1a\u5de6\u9762\u4e00\u4eba\uff0c\u9762\u5bb9\u82cd\u767d\u800c\u6e05\u77cd\uff0c\u5bbd\u9614\u7684\u989d\u89d2\u4e0a\uff0c \u5374\u5df2\u5e03\u6ee1\u4e86\u6c57\u73e0\uff0c\u4e4c\u9ed1\u7684\u53d1\u9afb\uff0c\u5df2\u84ec\u4e71\u800c\u96f6\u843d\uff0c\u6574\u6d01\u7684\u8863\u886b\uff0c\u4e5f\u5df2\u6c61\u79fd\u800c\u72fc\u72c8\uff0c\u53ea\u6709\u76ee\u5149\u5374 \u4ecd\u7136\u6709\u7740\u5200\u5251\u822c\u7684\u9510\u5229\uff0c\u9510\u5229\u5730\u51dd\u6ce8\u5728\u5bf9\u9762\u4e00\u4eba\u7684\u8eab\u4e0a\uff0c\u53cc\u638c\u5408\u5341\u5f53\u80f8\uff0c\u638c\u4e2d\u5374\u5939\u7740\u4e00\u67c4\u957f \u5251\u7684\u5251\u5c16\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u7ae0",content:(0,s.jsx)(s.Fragment,{children:"\u96ea\u4eae\u7684\u5251\u5c16\uff0c\u8ddd\u79bb\u4ed6\u80f8\u819b\u4e0d\u8fc7\u4ec5\u4ec5\u4e00\u5bf8\uff0c\u5730\u4e0a\u575a\u786c\u7684\u5c71\u77f3\uff0c\u5374\u5df2\u88ab\u4ed6\u7684\u8eab\u5b50\u5750\u5f97\u9677\u843d\u534a \u5c3a\u3002 \u3000\u3000\u4ed6\u52a8\u4e5f\u4e0d\u52a8\u5730\u5750\u5728\u90a3\u8fb9\uff0c\u8fde\u773c\u89d2\u4e5f\u6ca1\u6709\u659c\u8198\u88f4\u73cf\u4e00\u773c\uff0c\u9ec4\u660f\u7684\u706f\u5149\u4e0b\uff0c\u9aa4\u773c\u671b\u53bb\uff0c\u5c31\u4eff \u4f5b\u662f\u4e00\u5177\u8fde\u5728\u5c71\u77f3\u5730\u4e0a\u5851\u6210\u7684\u77f3\u50cf\u3002 \u3000\u3000\u4ed6\uff0c\u5728\u88f4\u73cf\u773c\u4e2d\u4e5f\u662f\u90a3\u822c\u719f\u6089\u3002 \u3000\u3000\u4ed6\uff0c\u8d6b\u7136\u7adf\u662f\u90a3\u540d\u9707\u6b66\u6797\u7684\u5f02\u4eba\u2014\u2014\u201c\u5343\u624b\u4e66\u751f\u201d\uff01 \u3000\u3000\u53f3\u9762\u4e00\u4eba\uff0c\u9762\u5bb9\u4ea6\u662f\u82cd\u767d\u800c\u6e05\u77cd\uff0c\u5bbd\u9614\u7684\u989d\u89d2\uff0c\u4e5f\u5df2\u5e03\u6ee1\u4e86\u6c57\u73e0\u3002 \u3000\u3000\u84ec\u4e71\u800c\u96f6\u843d\u7684\u987b\u8b6c\uff0c\u6c61\u79fd\u800c\u72fc\u72c8\u7684\u8863\u886b\uff0c\u5200\u5251\u822c\u9510\u5229\u7684\u76ee\u5149\uff0c\u751f\u4e86\u6839\u4f3c\u5730\u51dd\u6ce8\u7740\u5bf9\u65b9\uff0c \u53cc\u638c\u4ea6\u662f\u5408\u5341\u5f53\u80f8\uff0c\u638c\u4e2d\u4ea6\u662f\u5939\u7740\u4e00\u67c4\u5251\u5c16\uff0c\u5251\u5c16\u5b54\u5df2\u582a\u582a\u89e6\u7740\u4e86\u4ed6\u81ea\u5df1\u7684\u8863\u886b\u2026\u2026 \u3000\u3000\u4ed6\uff0c\u5728\u88f4\u73cf\u773c\u4e2d\u7adf\u4e5f\u662f\u90a3\u822c\u719f\u6089\u3002 \u3000\u3000\u4ed6\uff0c\u8d6b\u7136\u7adf\u4e5f\u662f\u90a3\u540d\u9707\u6b66\u6797\u7684\u5f02\u4eba\u2014\u2014\u201c\u5343\u624b\u4e66\u751f\u201d\uff01 \u3000\u3000\u8fd9\u4e24\u4eba\u5bf9\u9762\u9762\u5750\uff0c\u4e24\u67c4\u957f\u5251\u7684\u5251\u67c4\uff0c\u7d27\u7d27\u7f1a\u5728\u4e00\u8d77\uff0c\u4efb\u4f55\u4e00\u4eba\u638c\u4e0a\u7684\u771f\u529b\u7a0d\u4e00\u677e\u61c8\uff0c\u7acb \u523b\u4fbf\u6709\u7a7f\u80f8\u4e4b\u7978\u3002 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u4e24\u4eba\u6b63\u662f\u4ee5\u65e0\u4e0a\u7684\u5185\u529b\uff0c\u5728\u4f5c\u751f\u6b7b\u7684\u640f\u6597\uff0c\u8fd9\u5176\u95f4\u751a\u81f3\u6ca1\u6709\u59a5\u534f\u7684\u4f59\u5730\uff0c\u8c01\u4e5f \u4e0d\u80fd\u6709\u4e1d\u6beb\u7684\u677e\u5f1b\u4e0e\u758f\u5ffd\u3002\u81ea\u53e4\u4ee5\u6765\uff0c\u6b66\u6797\u4e2d\u4ec7\u5bb6\u7684\u640f\u6597\uff0c\u4f3c\u4e4e\u90fd\u6ca1\u6709\u8fd9\u4e24\u4eba\u5982\u6b64\u7d27\u5f20\u800c\u4e25 \u91cd\uff0c\u9664\u975e\u4ed6\u4eec\u4e24\u4eba\u540c\u65f6\u64a4\u9500\u638c\u529b\uff0c\u540c\u65f6\u98de\u8eab\u9000\u540e\u2014\u2014\u8fd9\u671f\u95f4\u8fd8\u4e0d\u80fd\u6709\u4e1d\u6beb\u7684\u5dee\u9519\u2014\u2014\u5426\u5219\uff0c \u8fd9\u4e24\u4eba\u4e4b\u4e2d\u82e5\u662f\u6709\u4efb\u4f55\u4e00\u4eba\u9000\u7f29\u6216\u677e\u5f1b\uff0c\u5bf9\u65b9\u4e00\u4eba\u638c\u4e2d\u7684\u957f\u5251\uff0c\u4fbf\u7acb\u523b\u4f1a\u9001\u8fdb\u4ed6\u8d77\u4f0f\u7684\u80f8\u819b \u4e2d\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u4e24\u4eba\u7684\u9762\u5bb9\u4e0e\u8eab\u6750\uff0c\u5374\u53c8\u7adf\u7136\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\uff0c\u4e16\u4eba\u867d\u591a\uff0c\u4f46\u9664\u4e86\u5b6a\u751f\u5144\u5f1f\u4e4b \u5916\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6709\u8fd9\u822c\u76f8\u540c\u7684\u9762\u8c8c\uff0c\u5947\u602a\u7684\u662f\u2014\u2014\u65e2\u662f\u5b6a\u751f\u5144\u5f1f\uff0c\u4e3a\u4f55\u53c8\u4f1a\u6709\u8fd9\u822c\u4e0d\u53ef\u5316\u89e3\u7684 \u523b\u9aa8\u6df1\u4ec7\uff1f \u3000\u3000\u88f4\u73cf\u4e00\u773c\u626b\u8fc7\uff0c\u4ed6\u51e0\u4e4e\u4e0d\u80fd\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u7adf\u4f1a\u770b\u5230\u5982\u6b64\u60ca\u4eba\u7684\u666f\u8c61\uff0c\u4ed6\u8eab\u5f62\u6709\u5982\u4e00 \u6761\u88ab\u51bb\u5728\u51b0\u4e2d\u7684\u9c7c\uff0c\u65e0\u6cd5\u52a8\u5f39\u5730\u51dd\u7ed3\u5728\u7a7a\u6c14\u4e2d\uff0c\u706f\u5149\u6620\u7740\u957f\u5251\uff0c\u4e00\u95ea\u4e00\u95ea\u5730\u53d1\u7740\u9752\u5149\uff0c\u50cf\u662f \u4eba\u7fa4\u8f7b\u8511\u7684\u773c\u795e\uff0c\u5728\u4e00\u95ea\u4e00\u95ea\u5730\u5632\u7b11\u7740\u4ed6\u7684\u795e\u6001\uff01\u518d\u52a0\u4ee5\u7f24\u7eb7\u800c\u591a\u5f69\u7684\u949f\u4e73\uff0c\u4ed6\u51e0\u4e4e\u4ee5\u4e3a\u81ea \u5df1\u8fd9\u4e0d\u8fc7\u4ec5\u662f\u505a\u4e86\u4e00\u573a\u6076\u68a6\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u79fb\u52a8\u4e86\u76ee\u5149\u2014\u2014\u5728\u4ed6\u672a\u66fe\u79fb\u52a8\u76ee\u5149\u7684\u8fd9\u4e00\u5239\u90a3\uff0c\u4eff\u4f5b\u662f\u6c38\u6052\u7684\u6f2b\u957f\u2014\u2014\u4ed6\u76ee\u5149 \u60ca\u8be7\u5730\u79fb\u5411\u827e\u9752\u8eab\u4e0a\uff0c\u7a81\u5730\uff01 \u3000\u3000\u4ed6\u4e0d\u7981\u53c8\u81ea\u60ca\u547c\u4e00\u58f0\u2026\u2026 \u3000\u3000\u827e\u9752\u90a3\u96ea\u767d\u7684\u8863\u886b\u4e0a\uff0c\u7adf\u7136\u5e03\u6ee1\u4e86\u6591\u73ed\u7684\u8840\u6e0d\uff0c\u6bcf\u4e00\u6ee9\u8840\u6e0d\u4e4b\u4e0a\uff0c\u90fd\u63d2\u7740\u4e00\u6839\u96ea\u4eae\u7684\u94a2 \u9488\u3002 \u3000\u3000\u94a2\u9488\uff01\u5728\u706f\u5149\u4e0b\u95ea\u52a8\u7740\u5fae\u5149\uff01 \u3000\u3000\u88f4\u73cf\u7684\u773c\u4e2d\uff0c\u5374\u50cf\u662f\u5e03\u6ee1\u4e86\u91d1\u661f\u3002\u91d1\u661f\u95ea\u70c1\uff0c\u4ed6\u53cc\u817f\u4e00\u8f6f\uff0c\u201c\u6251\u201d\u5730\u865a\u5f31\u5730\u5750\u5230\u4e86\u5730 \u4e0a\u3002 \u3000\u3000\u4ed6\u518d\u60f3\u4e0d\u51fa\u8fd9\u9634\u68ee\u7684\u6d1e\u7a9f\u4e4b\u4e2d\uff0c\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u60ca\u4eba\u7684\u60e8\u53d8\uff0c\u4ed6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e09\u4eba\u4e4b\u95f4\uff0c \u7a76\u7adf\u7ea0\u7f20\u7740\u4ec0\u4e48\u523b\u9aa8\u7684\u6069\u6028\u2014\u2014\u9664\u4e86\u201c\u6b7b\u4ea1\u201d\u4e4b\u5916\uff0c\u4e16\u4e0a\u4f3c\u4e4e\u518d\u4e5f\u6ca1\u6709\u4e00\u79cd\u529b\u91cf\uff0c\u80fd\u5c06\u8fd9\u6069 \u6028\u5316\u89e3\u5f97\u5f00\u3002 \u3000\u3000\u4ed6\u84e6\u7136\u5fc6\u8d77\u4e86\u4ed6\u4ece\u201c\u98de\u9f99\u9556\u5c40\u201d\u9003\u51fa\u7684\u90a3\u5929\u665a\u4e0a\u3002 \u3000\u3000\u90a3\u662f\u4ed6\u81f3\u4eca\u6bcf\u4e00\u60f3\u5230\uff0c\u4ecd\u4e0d\u7981\u4e3a\u4e4b\u60ca\u5fc3\u52a8\u9b44\u7684\u4e00\u591c\uff01 \u3000\u3000\u4ed6\u4e5f\u5ffd\u7136\u60f3\u8d77\uff0c\u5728\u4ed6\u4eec\u8c08\u53ca\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u7684\u8eab\u4e16\u65f6\uff0c\u201c\u91d1\u7ae5\u7389\u5973\u201d\u9762\u4e0a\u6240\u663e\u793a\u7684\u90a3\u79cd\u795e \u8272\u3002 \u3000\u3000\u8fd9\u4e00\u5207\uff0c\u975e\u4f46\u4e0d\u80fd\u89e3\u91ca\u6b64\u523b\u7684\u60c5\u51b5\uff0c\u5374\u53cd\u800c\u589e\u52a0\u4e86\u5b83\u7684\u9634\u68ee\u3001\u6050\u6016\uff0c\u4ee5\u53ca\u795e\u79d8\u3001\u5947\u8be1\u4e4b \u610f\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u6240\u63aa\u5730\u5750\u5728\u5730\u4e0a\uff0c\u4e0d\u80fd\u81ea\u6551\u5730\u8ff7\u4e71\u4e86\uff01 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u60b2\u54c0\u800c\u5e7d\u6028\u7684\u76ee\u5149\uff0c\u5446\u5446\u77a7\u4e86\u4ed6\u51e0\u773c\u3002 \u3000\u3000\u5979\u4e30\u6ee1\u7684\u80f8\u819b\uff0c\u5267\u70c8\u5730\u8d77\u4f0f\u7740\uff0c\u98a4\u52a8\u4e86\u63d2\u5728\u5979\u80f8\u819b\u4e0a\u7684\u9488\u5c16\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u970d\u7136\u56de\u8fc7\u5934\u53bb\uff0c\u671b\u5411\u5979\u9762\u524d\u6323\u624e\u4e8e\u751f\u6b7b\u8fb9\u7f18\u7684\u4e24\u5c0a\u201c\u77f3\u50cf\u201d\uff0c\u6b64\u523b\uff0c\u4e16\u4e0a\u518d\u65e0 \u4efb\u4f55\u4e00\u4eba\uff0c\u518d\u65e0\u4efb\u4f55\u4e00\u79cd\u529b\u91cf\uff0c\u80fd\u5f15\u5f00\u5979\u7684\u6ce8\u610f\uff0c\u80fd\u5206\u53bb\u5979\u7684\u5173\u5fc3\uff0c\u56e0\u4e3a\uff0c\u5979\u4e0e\u9762\u524d\u8fd9\u4e24\u4eba \u4e2d\u7684\u4efb\u4f55\u4e00\u4eba\uff0c\u90fd\u6709\u7740\u76f8\u4e92\u7ea0\u7f20\uff0c\u4e0d\u53ef\u5316\u89e3\uff1a\u94ed\u5fc3\u523b\u9aa8\uff0c\u7ec8\u8eab\u96be\u5fd8\u7684\u60c5\uff01\u4ec7\uff01\u6069\uff01\u6028\uff01 \u3000\u3000\u7f24\u7eb7\u7684\u5f69\u5149\u6d3b\u52a8\u7740\uff0c\u8fd9\u4e24\u4eba\u7684\u9762\u5bb9\uff0c\u5ffd\u800c\u6beb\u65e0\u8840\u8272\u7684\u82cd\u767d\uff0c\u5ffd\u800c\u52a8\u4eba\u5fc3\u5f26\u7684\u8840\u7ea2\uff0c\u5ffd\u800c \u53c8\u5448\u73b0\u51fa\u4e00\u79cd\u7edd\u671b\u7684\u7070\u7eff\u8272\u3002 \u3000\u3000\u4ee4\u4eba\u7a92\u606f\u7684\u6c89\u5bc2\uff0c\u51e0\u4e4e\u8fde\u547c\u5438\u4e4b\u58f0\u90fd\u6ca1\u6709\uff0c\u4ec5\u6709\u7684\u4e00\u4e1d\u98ce\u58f0\uff0c\u4e5f\u662f\u90a3\u822c\u5fae\u5f31\u800c\u9065\u8fdc\uff1b\u82e5 \u65ad\u82e5\u7eed\uff0c\u4f3c\u6709\u4f3c\u65e0\uff01 \u3000\u3000\u7a81\u5730\uff0c\u957f\u5251\u6e10\u6e10\u5411\u5de6\u9762\u79fb\u52a8\uff01 \u3000\u3000\u6e10\u6e10\uff01\u957f\u5251\u89e6\u7740\u4e86\u5de6\u9762\u4e00\u4eba\u7684\u8863\u886b\u2014\u2014\u4ed6\u989d\u4e0a\u9690\u9690\u6cdb\u51fa\u4e86\u9752\u7b4b\uff0c\u76ee\u4e2d\u9690\u9690\u6cdb\u51fa\u4e86\u8840\u4e1d\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u53cc\u76ee\u4e00\u5f20\uff0c\u76ee\u4e2d\u4e0d\u53ef\u63a9\u9970\u5730\u6d41\u9732\u51fa\u60ca\u6050\u4e0e\u5173\u5207\u4e4b\u8272\uff0c\u8eab\u8eaf\u4e0d\u53ef\u63a9\u9970\u5730\u8d77\u4e86 \u4e00\u9635\u98a4\u6296\u3002 \u3000\u3000\u5979\u662f\u90a3\u4e48\u5173\u5207\u4ed6\u7684\u5b89\u5168\u4e0e\u751f\u6b7b\uff0c\u8fd9\u79cd\u6df1\u9042\u6d53\u539a\u7684\u5173\u5207\uff0c\u751a\u81f3\u8fde\u5979\u8eab\u540e\u7684\u88f4\u73cf\u90fd\u611f\u89c9\u5230 \u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u53ef\u907f\u514d\u5730\u6697\u4e2d\u601d\u5fd6\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\uff0c\u53ea\u8981\u5979\u8f7b\u8f7b\u4e00\u4e3e\u624b\uff0c\u53f3\u9762\u90a3 \u4eba\uff0c\u7acb\u523b\u4fbf\u6709\u4e0d\u53ef\u907f\u514d\u5730\u6740\u8eab\u4e4b\u7978\uff01\u201d \u3000\u3000\u4ed6\u6df1\u77e5\u8fd9\u4e24\u4eba\u4e2d\u4efb\u4f55\u4e00\u4eba\uff0c\u90fd\u65e0\u6cd5\u518d\u62b5\u6321\u4efb\u4f55\u4e00\u4e2a\u7b2c\u4e09\u8005\u6240\u51fb\u6765\u7684\u529b\u9053\uff0c\u5373\u4fbf\u662f\u4e00\u4e2a\u4e09 \u5c3a\u5e7c\u7ae5\u7684\u62f3\u5934\uff0c\u90fd\u53ef\u4ee5\u8f7b\u800c\u6613\u4e3e\u5730\u5c06\u4ed6\u7f6e\u4e4b\u6b7b\u5730\uff01 \u3000\u3000\u4ed6\u5fc3\u4e2d\u65e2\u662f\u60ca\u7591\uff0c\u53c8\u89c9\u5947\u602a\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u7f13\u7f13\u957f\u8eab\u800c\u8d77\uff0c\u8981\u60f3\u5728\u53f3\u9762\u4e00\u4eba\u8eab\u4e0a\u8f7b\u8f7b\u51fb\u4e0a\u4e00 \u638c\u3002 \u3000\u3000\u53ea\u8981\u8f7b\u8f7b\u4e00\u638c\uff0c\u4fbf\u53ef\u89e3\u53bb\u5de6\u9762\u4e00\u4eba\u5f53\u524d\u7684\u5371\u673a\uff01 \u3000\u3000\u4ed6\u4e0e\u8fd9\u4e8c\u4eba\u867d\u6709\u6069\u6028\uff0c\u4f46\u4ed6\u5374\u5206\u4e0d\u51fa\u8fd9\u4e24\u4eba\u7a76\u7adf\u8c01\u662f\u66fe\u7ecf\u4ee5\u72ec\u95e8\u624b\u6cd5\u70b9\u4e2d\u4ed6\u201c\u804b\u54d1\u6b8b \u7a74\u201d\u7684\u4e00\u4eba\uff0c\u4ed6\u5982\u6b64\u505a\uff0c\u53ea\u662f\u4e3a\u4e86\u201c\u51b7\u6708\u4ed9\u5b50\u201d\uff0c\u56e0\u4e3a\u4ed6\u5bf9\u5979\u6709\u7740\u96be\u4ee5\u5fd8\u6000\u7684\u611f\u6fc0\u3002 \u3000\u3000\u90a3\u65f6\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u957f\u5251\u5374\u53c8\u6e10\u6e10\u5411\u53f3\u79fb\u52a8\uff0c\u6e10\u6e10\u89e6\u7740\u4e86\u53f3\u9762\u90a3\u4eba\u7684\u8863\u886b\u3002 \u3000\u3000\u5de6\u9762\u4e00\u4eba\uff0c\u795e\u8272\u6e10\u6e10\u5e73\u5b9a\uff0c\u53f3\u9762\u4e00\u4eba\uff0c\u795e\u8272\u5374\u6e10\u6e10\u60ca\u6050\u3002 \u3000\u3000\u88f4\u73cf\u6697\u4e2d\u677e\u4e86\u53e3\u6c14\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u5374\u89c1\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u7684\u5a07\u8eaf\uff0c\u4ecd\u5728\u4e0e\u65b9\u624d\u4e00\u6837\u5730\u5173\u5207\u5730\u98a4 \u6296\u7740\u3002 \u3000\u3000\u5979\u7adf\u4ee5\u540c\u6837\u6d53\u539a\u3001\u540c\u6837\u6df1\u5207\u7684\u4e00\u4efd\u5173\u5207\uff0c\u8f6c\u79fb\u5230\u53f3\u9762\u8fd9\u4eba\u7684\u8eab\u88f4\u73cf\u5446\u4e86\u4e00\u5446\uff0c\u65e0\u52a9\u5730\u5750 \u56de\u5730\u4e0a\uff01 \u3000\u3000\u8fd9\u5176\u95f4\u5173\u7cfb\u7684\u590d\u6742\u4e0e\u5fae\u5999\uff0c\u66f4\u4ee4\u8fd9\u5c11\u5e74\u65e0\u6cd5\u60f3\u8c61\u3002 \u3000\u3000\u706f\u5149\u4e0e\u5f69\u5149\uff0c\u4ecd\u5728\u95ea\u70c1\u3002 \u3000\u3000\u8fd9\u4e0d\u6b7b\u4e0d\u4f11\u7684\u640f\u6597\uff0c\u7adf\u4f3c\u8981\u6c38\u65e0\u4f11\u6b62\u5730\u7ee7\u7eed\u4e0b\u53bb\uff0c\u6c89\u91cd\u800c\u903c\u4eba\u7684\u6c14\u6c1b\uff0c\u5c71\u5cb3\u822c\u538b\u5728\u88f4\u73cf \u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u4e0d\u5b89\u5730\u8f6c\u52a8\u4e86\u4e00\u4e0b\u50f5\u6728\u7684\u8eab\u8eaf\uff0c\u5fc3\u4e2d\u7684\u60ca\u6050\u4e0e\u7591\u60d1\uff0c\u968f\u7740\u65f6\u5149\u4e4b\u8fc7\u53bb\uff0c\u53d8\u5f97\u8d8a\u53d1\u96be\u4ee5 \u5fcd\u53d7\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u827e\u9752\uff0c\u5374\u50cf\u662f\u6839\u672c\u5df2\u5fd8\u5374\u4e86\u4ed6\u7684\u5b58\u5728\uff0c\u5979\u7684\u76ee\u5149\uff0c\u4ecd\u662f\u60b2\u54c0\u5e7d\u6028\u800c\u5173\u5207\u5730 \u671b\u5728\u9762\u524d\u4e24\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u8fdc\u5904\uff0c\u7a81\u5730\u54cd\u8d77\u4e86\u4e00\u9635\u547c\u58f0\uff01 \u3000\u3000\u201c\u88f4\u73cf\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8fd9\u98d8\u6e3a\u7684\u547c\u58f0\u867d\u7136\u6781\u5176\u9065\u8fdc\u800c\u5fae\u5f31\uff0c\u5c31\u4eff\u4f5b\u662f\u5730\u9053\u4e2d\u90a3\u82e5\u65ad\u82e5\u7eed\u3001\u4f3c\u6709\u4f3c\u65e0\u7684\u98ce\u58f0\u4e00 \u6837\uff0c\u4f46\u4ed6\u5165\u8033\u4fbf\u77e5\uff0c\u53d1\u51fa\u8fd9\u547c\u58f0\u7684\u4eba\uff0c\u4e2d\u6c14\u6781\u8db3\uff0c\u4e0d\u53ef\u6000\u7591\u7684\u5b9a\u662f\u4e00\u4e2a\u8eab\u6000\u4e0a\u4e58\u5185\u529f\u7684\u6b66\u6797 \u9ad8\u624b\uff01 \u3000\u3000\u5979\u5fc3\u5934\u4e00\u9707\uff0c\u970d\u7136\u8f6c\u9996\uff0c\u53d8\u8272\u8f7b\u53f1\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u88f4\u73cf\u76ee\u5149\u4f4e\u5782\uff0c\u4e0d\u5fcd\u4e5f\u4e0d\u6562\u518d\u671b\u5979\u7684\u9762\u5bb9\u4e00\u773c\uff0c\u5782\u9996\u9053\uff1a\u201c\u662f\u548c\u6211\u540c\u4e0a\u9ec4\u5c71\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u7684\u9762\u5bb9\u66f4\u662f\u82cd\u767d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u53d1\u73b0\u4e86\u6d1e\u7a9f\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fae\u4e00\u6c89\u541f\uff0c\u5450\u5450\u9053\uff1a\u201c\u53ef\u80fd\u2026\u2026\u201d \u3000\u3000\u827e\u9752\u76ee\u5149\u5446\u6ede\u5730\u79fb\u52a8\u4e86\u4e00\u4e0b\uff0c\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u90a3\u6ee1\u63d2\u7740\u94a2\u9488\u7684\u8eab\u8eaf\uff0c\u50cf\u662f\u98d8\u626c\u5728\u5fae\u98ce\u4e2d \u4f3c\u7684\u6643\u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u88f4\u73cf\u6006\u7136\u957f\u8eab\u800c\u8d77\uff0c\u53d8\u8272\u9053\uff1a\u201c\u4f60\u2026\u2026\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u5c1d\u8bd5\u7740\u53bb\u6400\u6276\u5979\uff0c\u4f46\u5979\u5374\u53c8\u9893\u7136\u5750\u4e86\u4e0b\u53bb\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u53bb\u544a\u8bc9\u4ed6\u4eec\uff0c\u53eb\u4ed6\u4eec\u4e0d\u8981\u8fdb \u6765\uff01\u201d \u3000\u3000\u88f4\u73cf\u5782\u9996\u671b\u4e86\u671b\u5979\u82cd\u767d\u7684\u9762\u5bb9\uff0c\u671b\u4e86\u671b\u5979\u8eab\u4e0a\u9c9c\u7ea2\u7684\u8840\u6e0d\uff0c\u96ea\u4eae\u7684\u94a2\u9488\u2014\u2014\u4efb\u4f55\u4e00\u4e2a\u6709 \u5fc3\u80a0\u7684\u4eba\u90fd\u4e0d\u4f1a\u62d2\u7edd\u5982\u6b64\u60b2\u54c0\u800c\u53ef\u601c\u7684\u5973\u5b50\u7684\u8bf7\u6c42\uff0c\u4f55\u51b5\u662f\u5bf9\u5979\u6df1\u6df1\u611f\u6fc0\u7740\u7684\u3001\u5584\u826f\u800c\u4ec1\u6148 \u7684\u88f4\u73cf\uff1f \u3000\u3000\u4ed6\u6beb\u4e0d\u72b9\u7591\u5730\u8f6c\u8eab\u98de\u6b65\u5954\u4e86\u51fa\u53bb\uff0c\u751a\u81f3\u6ca1\u6709\u95ee\u5979\u4e00\u53e5\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u65e0\u8bba\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6 \u90fd\u4f1a\u4e3a\u5979\u53bb\u505a\u4efb\u4f55\u4e8b\u7684\u3002 \u3000\u3000\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\uff0c\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u9010\u6e10\u8fdc\u53bb\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u6df1\u6df1\u8f6c\u8fc7\u8eab\uff0c\u4e24\u7c92\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u6084\u7136\u6d41\u843d\uff0c\u7f13\u7f13\u6ef4\u843d\u5728\u5979\u8eab\u4e0a\u96ea\u4eae\u7684\u9488\u5c16 \u4e0a\u3002 \u3000\u3000\u5979\u60b2\u54c0\u5730\u8f7b\u547c\u7740\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u2026\u2026\u4e3a\u4ec0\u4e48\u4f60\u4eec\u8981\u8fd9\u6837\uff01\u201d \u3000\u3000\u5176\u5b9e\uff0c\u5979\u662f\u6781\u4e3a\u6e05\u695a\u5730\u77e5\u9053\uff0c\u9762\u524d\u8fd9\u4e24\u4eba\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\uff0c\u90a3\u662f\u4e3a\u4e86\u5979\u3002\u4e3a\u4e86\u4e00\u79cd\u4ee5\u8840\u6cea \u4ea4\u7ec7\u6210\u7684\u6069\u6028\uff0c\u4e3a\u4e86\u90a3\u4e0d\u53ef\u8fdd\u6297\u7684\u5929\u547d\uff0c\u4e3a\u4e86\u90a3\u4e0e\u751f\u4ff1\u6765\u7684\u4eba\u6027\uff01 \u3000\u3000\u8fd9\u51c4\u695a\u800c\u54c0\u6028\u7684\u547c\u58f0\uff0c\u751a\u81f3\u6ca1\u6709\u4f7f\u9762\u524d\u8fd9\u4e24\u4eba\u7684\u76ee\u5149\u8f6c\u52a8\u4e00\u4e0b\uff0c\u751f\u4e0e\u6b7b\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u5728 \u4ed6\u4eec\u4e24\u4eba\u4e4b\u95f4\uff0c\u4f3c\u5982\u957f\u5251\u8fb9\u7f18\u7684\u5203\u53e3\u4e00\u6837\u3002 \u3000\u3000\u5979\u7edd\u671b\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u5782\u9996\u671b\u7740\u8eab\u4e0a\u7684\u9488\u5c16\u3002 \u3000\u3000\u8fd9\u4e9b\u94a2\u9488\uff0c\u90fd\u662f\u5979\u4e00\u6839\u4e00\u6839\u5730\u63d2\u5728\u81ea\u5df1\u8eab\u4e0a\u7684\uff0c\u4f46\u662f\uff0c\u8fd9\u53ef\u6016\u800c\u60ca\u4eba\u7684\u4e3e\u52a8\uff0c\u5374\u662f\u4e1d\u6beb \u4e0d\u80fd\u963b\u6b62\u5979\u9762\u524d\u8fd9\u4e24\u4eba\u751f\u6b7b\u7684\u640f\u6597\uff0c\u800c\u8fd9\u79cd\u8089\u4f53\u4e0a\u5f3a\u70c8\u7684\u75db\u82e6\uff0c\u4e5f\u4e1d\u6beb\u4e0d\u80fd\u4f7f\u5979\u5fc3\u4e2d\u7684\u75db\u82e6 \u8f6c\u79fb\u3002 \u3000\u3000\u5979\u7edd\u671b\u5730\u4fef\u9996\u51dd\u601d\u7740\uff0c\u7a81\u5730\uff0c\u5979\u9762\u4e0a\u6cdb\u8d77\u4e86\u4e00\u9635\u5fae\u7b11\uff01 \u3000\u3000\u56e0\u4e3a\uff0c\u5979\u6df1\u77e5\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5c31\u5728\u4eca\u65e5\uff0c\u90a3\u79cd\u75db\u82e6\u800c\u60b2\u60e8\u7684\u751f\u547d\uff0c\u4ee5\u53ca\u5979\u4e0e\u8fd9\u4e24\u4eba\u7ea0\u7f20\u96be \u7ed3\u7684\u60c5\u3001\u4ec7\u3001\u6069\u3001\u6028\uff0c\u5fc5\u5c06\u83b7\u5f97\u6c38\u8fdc\u7684\u89e3\u8131\uff01 \u3000\u3000\u88f4\u73cf\u98de\u6b65\u800c\u5954\uff0c\u8fd9\u4e00\u6bb5\u4ed6\u8d70\u5165\u65f6\u4eff\u4f5b\u6709\u7740\u4e0d\u53ef\u4f01\u53ca\u5730\u6f2b\u957f\u8ddd\u79bb\u7684\u79d8\u9053\uff0c\u6b64\u523b\u7adf\u50cf\u662f\u7a81\u5730 \u53d8\u6210\u5f02\u6837\u5730\u77ed\u6682\u3002 \u3000\u3000\u970e\u773c\u95f4\uff0c\u4ed6\u4fbf\u5df2\u5954\u5230\u4e86\u5c3d\u5934\uff0c\u4ed6\u770b\u5230\u6709\u4e00\u4e1d\u5fae\u5f31\u7684\u5929\u5149\uff0c\u81ea\u90a3\u5730\u9053\u7684\u4eba\u53e3\u5904\u6295\u843d\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u677e\u4e86\u53e3\u6c14\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5730\u9053\u4e2d\u6b64\u523b\u5df2\u662f\u5982\u6b64\u9ed1\u6697\uff0c\u96be\u602a\u90a3\u51b7\u6c0f\u5144\u5f1f\u4e8c\u4eba\uff0c\u76f4\u5230\u6b64 \u523b\u8fd8\u672a\u53d1\u73b0\u90a3\u77f3\u5757\u4e0b\u7684\u4eba\u53e3\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u53c8\u81ea\u5fd6\u9053\uff1a\u201c\u65b9\u624d\u4ed6\u4eec\u6240\u671b\u89c1\u7684\u90a3\u4e00\u4e1d\u706f\u5149\uff0c\u60f3\u5fc5\u662f\u4ece\u2018\u51b7\u6708\u4ed9\u5b50\uff0c\u5b58\u8eab\u4e4b \u6d1e\u7a9f\u91cc\u7684\u88c2\u9699\u4e2d\u900f\u51fa\u53bb\u7684\uff0c\u800c\u90a3\u91cc\u6839\u672c\u6ca1\u6709\u5165\u53e3\uff01\u201d\u5fc3\u5ff5\u4e00\u95ea\u800c\u8fc7\uff0c\u4ed6\u594b\u8eab\u4e00\u8dc3\uff0c\u624b\u638c\u6500\u4f4f \u4e86\u4eba\u53e3\u7684\u8fb9\u7f18\uff0c\u6b64\u523b\u4ed6\u6b66\u529f\u5df2\u5927\u5f02\u4e8e\u5f80\u6614\uff0c\u8eab\u8eaf\u4e00\u7ffb\uff0c\u4fbf\u7ffb\u4e86\u4e0a\u53bb\uff0c\u53ea\u89c9\u4e00\u53ea\u51b0\u51c9\u7684\u624b\u638c\uff0c \u7a81\u5730\u642d\u4f4f\u4e86\u4ed6\u7684\u8155\u8109\uff0c\u4e00\u80a1\u5927\u529b\uff0c\u5c06\u4ed6\u63d0\u8d77\u3002\u4ed6\u8f7b\u547c\u4e00\u58f0\uff1a\u201c\u662f\u6211\uff01\u201d \u3000\u3000\u53cc\u8db3\u8e0f\u4e0a\u5b9e\u5730\uff0c\u661f\u5149\u4e0b\uff0c\u4ed6\u7a81\u5730\u77a5\u89c1\u7acb\u5728\u4ed6\u8eab\u524d\u7684\u201c\u51b7\u8c37\u53cc\u6728\u201d\u90a3\u51b7\u524a\u7684\u9762\u5bb9\uff0c\u6b64\u523b\u7adf \u5145\u6ee1\u7740\u5173\u5207\u4e4b\u8272\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u83ab\u975e\u9047\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51b0\u51b7\u7684\u8bed\u58f0\u4e2d\uff0c\u4e5f\u9690\u9690\u542b\u8574\u7740\u5173\u5207\u7684\u60c5\u611f\uff0c\u88f4\u73cf\u53ea\u89c9\u5fc3\u5e95\u7a81\u5730\u6cdb\u8d77\u4e86\u4e00\u9635\u6e29\u6696\u3002\u6b64\u523b\uff0c \u4ed6\u89c1\u7740\u8fd9\u4e24\u4e2a\u201c\u51b7\u9177\u201d\u7684\u201c\u602a\u4eba\u201d\uff0c\u7adf\u4f3c\u9047\u7740\u5bb6\u4eba\u4e00\u822c\u4eb2\u5207\u3002 \u3000\u3000\u4ed6\u5306\u5fd9\u800c\u7b80\u77ed\u5730\u5740\u51fa\u4e86\u81ea\u5df1\u65b9\u624d\u90a3\u4e00\u6bb5\u79bb\u54e5\u800c\u50da\u4eba\u7684\u906d\u9047\uff0c\u6073\u6c42\u4ed6\u4e24\u4eba\uff0c\u5343\u4e07\u4e0d\u8981\u5230\u8fd9 \u79d8\u7a9f\u4e2d\u53bb\u3002 \u3000\u3000\u4ed6\u6c38\u8fdc\u4e0d\u4f1a\u6b3a\u9a97\u522b\u4eba\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u4ee5\u6b3a\u9a97\u7684\u624b\u6bb5\uff0c\u8fbe\u5230\u81ea\u5df1\u7684\u76ee\u7684\uff0c\u5f80\u6614\u5982\u6b64\uff0c\u6b64\u523b\u4e5f\u5982 \u6b64\uff0c\u4ed6\u53ea\u662f\u7387\u76f4\u4ed6\u8bf4\u51fa\u81ea\u5df1\u7684\u8bf7\u6c42\u2014\u2014\u800c\u8fd9\u79cd\u8bda\u6073\u800c\u7387\u76f4\u7684\u8bf7\u6c42\uff0c\u901a\u5e38\u90fd\u4f1a\u4f7f\u5bf9\u65b9\u96be\u4ee5\u62d2 \u7edd\u3002 \u3000\u3000\u53d9\u8ff0\u4e2d\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u795e\u8272\uff0c\u662f\u60ca\u5947\u800c\u53d8\u6362\u7740\u7684\u3002 \u3000\u3000\u751a\u81f3\u5728\u51b7\u9177\u3001\u50b2\u6162\u7684\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u5fc3\u4e2d\uff0c\u201c\u5343\u624b\u4e66\u751f\u201d\u4e0e\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u8fd9\u51e0\u4e2a\u5b57\uff0c\u4e5f \u662f\u4e2a\u54cd\u4eae\u7684\u540d\u5b57\u3002 \u3000\u3000\u4ed6\u4eec\u60ca\u5947\u5730\u5bf9\u671b\u4e00\u773c\uff0c\u51b7\u5bd2\u7af9\u7a81\u5730\u5c55\u989c\u5931\u7b11\uff0c\u9053\uff1a\u201c\u6709\u8c01\u76f8\u4fe1\uff0c\u6709\u8c01\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u88f4\u73cf\u832b\u7136\u95ee\u9053\uff1a\u201c\u76f8\u4fe1\u4ec0\u4e48\uff1f\u6211\u6240\u8bf4\u7684\uff0c\u4ff1\u662f\u5343\u771f\u4e07\u786e\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u4e00\u7b11\u622a\u53e3\u9053\uff1a\u201c\u6709\u8c01\u4f1a\u77e5\u9053\u4e00\u4e2a\u4e0e\u2018\u9f99\u5f62\u516b\u638c\u2019\u3001\u2018\u51b7\u6708\u4ed9\u5b50\u2019\u3001\u2018\u91d1\u7ae5\u7389 \u5973\u2019\uff0c\u8fd9\u822c\u4eba\u90fd\u6709\u7740\u6781\u597d\u5bc6\u5207\u5173\u7cfb\u7684\u5c11\u5e74\uff0c\u7adf\u7136\u53ef\u8bf4\u662f\u4e1d\u6beb\u4e0d\u4f1a\u6b66\u529f\uff01\u800c\u8fd9\u4e1d\u6beb\u4e0d\u4f1a\u6b66\u529f\u7684 \u5c11\u5e74\uff0c\u5374\u53c8\u5728\u77ed\u77ed\u4e00\u5e74\u4e4b\u95f4\uff0c\u540d\u6ee1\u6c5f\u6e56\uff01\u201d \u3000\u3000\u51b7\u67af\u6728\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u53ea\u6015\u5df2\u53ef\u7b97\u505a\u6b66\u6797\u4e2d\u81ea\u53e4\u672a\u6709\u7684\u5947\u95fb\u5f02\u4e8b\u4e86\uff01\u201d \u3000\u3000\u8fd9\u5144\u5f1f\u4e24\u4eba\u81ea\u4e0e\u88f4\u73cf\u76f8\u5904\u4e4b\u540e\uff0c\u9762\u4e0a\u6cdb\u51fa\u5fae\u7b11\uff0c\u5df2\u4e0d\u518d\u662f\u4e00\u4ef6\u503c\u5f97\u60ca\u5f02\u7684\u4e8b\uff0c\u4ec1\u6148\u800c\u5584 \u826f\u7684\u5fc3\uff0c\u6709\u65f6\u7684\u786e\u4f1a\u548c\u6625\u98ce\u4e00\u6837\uff0c\u80fd\u6e29\u548c\u5730\u878d\u5316\u5bd2\u51b7\u7684\u51b0\u96ea\u3002 \u3000\u3000\u88f4\u73cf\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u662f\u5728\u5947\u602a\u6211\u6240\u8bf4\u7684\u4e8b\u2026\u2026\u201d\u51b7\u5bd2\u7af9\u5fae\u7b11\u9053\uff1a\u201c\u540d\u9707\u6b66 \u6797\u7684\u2018\u5343\u624b\u4e66\u751f\u2019\u7adf\u4f1a\u6709\u4e24\u4e2a\u4eba\uff1f\u2018\u51b7\u6708\u4ed9\u5b50\u2019\u8eab\u4e0a\u7adf\u7136\u4f1a\u63d2\u6ee1\u4e86\u94a2\u9488\uff0c\u8fd9\u4e9b\u867d\u7136\u90fd\u662f\u4ee4\u4eba \u60ca\u5fc3\u52a8\u9b44\u7684\u5947\u5f02\u4e4b\u4e8b\uff0c\u4f46\u8fd9\u4e9b\u4e8b\u6bd4\u8d77\u4f60\u81ea\u5df1\u7684\u906d\u9047\u6765\uff0c\u5374\u53c8\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u53ea\u662f\u4f60\u81ea\u5df1\u4e0d\u77e5\u9053 \u800c\u5df2\uff01\u201d \u3000\u3000\u51b7\u67af\u6728\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u8981\u4e0b\u53bb\uff0c\u5c31\u5feb\u4e9b\u4e0b\u53bb\uff0c\u6211\u4eec\u5728\u8fd9\u91cc\u7b49\u4f60\u3002\u201d \u3000\u3000\u88f4\u73cf\u6728\u7136\u6014\u4e86\u534a\u664c\uff0c\u4f3c\u4e4e\u5728\u56de\u5473\u8fd9\u5144\u5f1f\u4e24\u4eba\u7684\u8a00\u8bed\uff0c\u53c8\u4f3c\u4e4e\u5728\u5947\u602a\u4ed6\u5144\u5f1f\u4e24\u4eba\u7684\u8bf4\u8bdd\uff0c \u7adf\u4f1a\u53d8\u5f97\u5982\u6b64\u6e29\u67d4\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u611f\u6fc0\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u518d\u6b21\u8dc3\u4e0b\u79d8\u7a9f\u3002 \u3000\u3000\u51b7\u67af\u6728\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u2014\u2014\u4e00\u4ed6\u5bf9\u522b\u4eba\u7684\u4e8b\uff0c\u603b\u662f\u6bd4\u5bf9\u81ea\u5df1\u7684\u4e8b\u70ed\u5fc3\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u5fae\u5fae\u4e00\u7b11\uff0c\u7a81\u5730\u76b1\u7709\u9053\uff1a\u60f3\u4e0d\u5230\u2018\u5343\u624b\u4e66\u751f\u2019\uff0c\u7adf\u6709\u4e24\u4eba\uff0c\u96be\u602a\u6c5f\u6e56\u4f20\u8a00\uff0c\u201c\u5343 \u624b\u4e66\u751f\u2018\u7684\u884c\u4e8b\uff0c\u603b\u662f\u5ffd\u5584\u5ffd\u6076\uff0c\u2019\u5343\u624b\u4e66\u751f\u2018\u7684\u884c\u8e2a\uff0c\u603b\u662f\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u4eca\u65e5\u5728\u6c5f\u5357\u505a\u4e86\u4ef6 \u5584\u4e8b\uff0c\u660e\u65e5\u5374\u53c8\u5728\u6cb3\u5317\u505a\u51fa\u6076\u884c\u3002\u201d\u51b7\u67af\u6728\u60a0\u7136\u53f9\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u672c\u6709\u8bb8\u591a\u795e\u8bdd\u822c\u7684\u4eba\u7269\uff0c\u795e \u8bdd\u822c\u7684\u6545\u4e8b\uff0c\u4f46\u662f\u5728\u8fd9\u4e9b\u4eba\u7269\u4e0e\u6545\u4e8b\u80cc\u540e\uff0c\u5374\u53c8\u603b\u662f\u9690\u85cf\u7740\u4e00\u4e9b\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\u7684\u4e8b\u5b9e\uff0c\u8fd9\u4e9b \u4e8b\u5b9e\u6709\u5927\u534a\u90fd\u6c38\u8fdc\u6ca1\u4eba\u77e5\u9053\uff0c\u5c31\u50cf\u662f\u2026\u2026\u5c31\u50cf\u662f\u2026\u2026\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u622a\u53e3\u9053\uff1a\u201c\u5c31\u50cf\u662f\u6211\u4eec\u5144\u5f1f\u4e00\u6837\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u4e24\u4eba\u76f8\u89c6\u4e00\u7b11\uff0c\u5c31\u8fde\u9ec4\u5c71\u4e4b\u5dc5\u8fd9\u5f3a\u70c8\u7684\u591c\u98ce\uff0c\u90fd\u5439\u4e0d\u6563\u6b64\u523b\u7559\u5728\u4ed6\u5144\u5f1f\u4e24\u4eba\u9762\u4e0a\u7684\u7b11 \u5bb9\u3002 \u3000\u3000\u661f\u5149\u819d\u9647\u4e86\uff0c\u56e0\u4e3a\u6709\u6d53\u96fe\u5728\u5c71\u5dc5\u5347\u8d77\u3002 \u3000\u3000\u79d8\u9053\u4e2d\u6b63\u8361\u6f3e\u7740\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u90a3\u60b2\u54c0\u51c4\u695a\u7684\u8bed\u97f3\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u7b97\u4e86\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\uff1f\u56db\u5341\u5e74\u524d\u7684\u6028\u6bd2\uff0c\u96be\u9053\u4eca\u65e5\u8fd8\u4e0d\u80fd\u5316\u89e3\uff1f \u4f55\u51b5\u4ed6\u2026\u2026\u4ed6\u65e9\u5df2\u77e5\u9053\u9519\u4e86\uff01\u201d \u3000\u3000\u88f4\u73cf\u4e0d\u7531\u81ea\u4e3b\u5730\u653e\u8f7b\u4e86\u811a\u6b65\uff0c\u53ea\u542c\u5979\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u5fcd\u53d7\u4e86\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u5fcd\u8010\u7684\u75db\u82e6\u4e0e\u5c48 \u8fb1\uff0c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4f60\uff0c\u8fd9\u4e9b\u4e8b\uff0c\u96be\u9053\u8fd8\u4e0d\u80fd\u8865\u507f\u4ed6\u5e7c\u65f6\u7684\u8fc7\u9519\uff1f\u4f60\u603b\u4e0d\u8be5\u5c06\u4ed6\u903c\u5f97\u65e0\u8def\u53ef\u8d70 \u5440\uff01\u662f\u4e48\uff1f\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u96be\u9053\u771f\u5fcd\u5fc3\u5c06\u4f60\u5ae1\u4eb2\u7684\u5144\u5f1f\u6740\u6b7b\uff1f\u201d \u3000\u3000\u51c4\u695a\u7684\u8bed\u58f0\uff0c\u5c31\u50cf\u662f\u9ec4\u660f\u65f6\u675c\u9e43\u7684\u54c0\u557c\u3002 \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u4e00\u9635\u6c89\u91cd\u7684\u60b2\u5fe7\uff0c\u6d8c\u4e0a\u5fc3\u5934\u3002 \u3000\u3000\u4ed6\u811a\u6b65\u66f4\u8f7b\uff0c\u66f4\u8f7b\u4e86\u3002 \u3000\u3000\u51c4\u7136\u7684\u8bed\u58f0\u5fae\u5fae\u4e2d\u65ad\uff0c\u53c8\u5f00\u59cb\u7ee7\u7eed\u7740\uff0c\u201c\u4ef2\u5fcd\uff0c\u4f60\u5df2\u7ecf\u5fcd\u53d7\u4e86\u90a3\u4e48\u591a\uff0c\u96be\u9053\u5c31\u4e0d\u80fd\u518d\u5fcd \u53d7\u4e00\u4e9b\u4e48\uff1f\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u603b\u662f\u9519\u4e86\u5440\uff01\u4f60\u603b\u662f\u5148\u5bf9\u4ed6\u4e0d\u8d77\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u8bed\u58f0\u4e2d\u7684\u4f4d\u58f0\u6e10\u91cd\uff1a\u201c\u6211\u77e5\u9053\u2026\u2026\u8fd9\u90fd\u662f\u4e3a\u4e86\u6211\uff0c\u6ca1\u6709\u6211\uff0c\u4f60\u4eec\u539f\u672c\u53ef\u4ee5\u2026\u2026\u53ef\u4ee5\u591a\u5fcd \u53d7\u4e00\u4e9b\u7684\uff0c\u4f46\u662f\uff0c\u4f60\u4eec\u8981\u77e5\u9053\uff0c\u6211\u4e5f\u662f\u4eba\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u600e\u4e48\u80fd\u76ee\u7779\u8fd9\u4e9b\u4e8b\uff1f\u6211\u53ef\u4ee5\u7acb\u523b \u6b7b\u5728\u4f60\u4eec\u9762\u524d\uff0c\u4f46\u662f\u2026\u2026\u4f46\u662f\u6211\u5374\u4e0d\u5fcd\u89c1\u5230\u4f60\u4eec\u4e4b\u4e2d\u4efb\u4f55\u4e00\u4eba\u6b7b\u5728\u5bf9\u65b9\u624b\u91cc\uff0c\u8840\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u4e8e\u662f\u9634\u68ee\u7684\u5730\u9053\u4e2d\u4fbf\u53ea\u6709\u201c\u8840\u201d\u8fd9\u4e00\u4e2a\u5b57\u7684\u4f59\u97f3\u5728\u6447\u66f3\u7740\uff0c\u8361\u6f3e \u7740\u2026\u2026 \u3000\u3000\u5979\u62bd\u6ce3\u7740\u63a5\u53e3\u9053\uff1a\u201c\u8840\uff0c\u6bd5\u7adf\u662f\u6d53\u4e8e\u6c34\u7684\u5440\uff01\u6c42\u6c42\u4f60\u2026\u2026\u4f60\u4eec\u4e00\u8d77\u653e\u5f00\u624b\uff0c\u597d\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u751a\u81f3\u4e0d\u6562\u547c\u5438\uff0c\u4ed6\u4e00\u6b65\u4e00\u6b65\u5730\uff0c\u7ec8\u7a76\u8d70\u5230\u5c3d\u5934\u3002 \u3000\u3000\u706f\u5149\uff0c\u4ecd\u662f\u660f\u9ec4\u7684\uff0c\u4ed6\u8270\u96be\u5730\u79fb\u52a8\u7740\u81ea\u5df1\u7684\u76ee\u5149\uff0c\u79fb\u5411\u90a3\u4e00\u5e55\u60e8\u7edd\u4eba\u5bf0\u3001\u4ee4\u4eba\u4e0d\u5fcd\u5352\u7779 \u7684\u666f\u8c61\u3002 \u3000\u3000\u54ea\u77e5\uff0c\u5c31\u5728\u4ed6\u76ee\u5149\u79fb\u52a8\u7684\u8fd9\u4e00\u77ac\u95f4\u3002 \u3000\u3000\u5de6\u9762\u4e00\u4eba\uff0c\u575a\u5b9a\u5f97\u6709\u5982\u77f3\u50cf\u822c\u7684\u9762\u5bb9\uff0c\u7a81\u5730\u8d77\u4e86\u4e00\u9635\u53d8\u5316\uff0c\u4e00\u9635\u6781\u5176\u8f7b\u5fae\u3001\u51e0\u4e4e\u4e0d\u6613\u89c9 \u5bdf\u7684\u53d8\u5316\u3002 \u3000\u3000\u7136\u800c\uff0c\u5728\u8fd9\u4e00\u8f7b\u5fae\u800c\u4e0d\u6613\u89c9\u5bdf\u7684\u53d8\u5316\u4e4b\u540e\uff0c\u4ed6\u7d27\u5408\u7740\u7684\u624b\u638c\uff0c\u7a81\u5730\u677e\u5f00\u4e86\uff01\u677e\u5f00\u4e86\uff01 \u201c\u51b7\u6708\u4ed9\u5b50\u201d\u9762\u8272\u60e8\u767d\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4ef2\u5fcd\u2014\u2014\u201d\u559d\u58f0\u672a\u4e86\uff0c\u53f3\u9762\u4e00\u4eba\u9762\u4e0a\u7a81\u5730\u95ea\u8fc7\u4e00\u4e1d\u5fae \u7b11\uff0c\u7d27\u5408\u7740\u7684\u53cc\u638c\uff0c\u4e5f\u7a81\u5730\u5411\u5916\u4e00\u5206\u2026\u2026 \u3000\u3000\u96ea\u4eae\u7684\u5251\u950b\uff0c\u201c\u5657\u3001\u5730\u63d2\u5165\u4e86\u80f8\u819b\u2014\u2014\u51e0\u4e4e\u5728\u540c\u4e00\u5239\u90a3\u95f4\uff0c\u63d2\u5165\u4e86\u4ed6\u4eec\u4e24\u4eba\u7684\u80f8\u819b\u3002\u9c9c \u7ea2\u7684\u70ed\u8840\uff0c\u98de\u6e85\u4e86\u8d77\u6765\u2014\u2014\u5f7c\u6b64\u95f4\u7684\u70ed\u8840\uff0c\u98de\u6e85\u5230\u53e6\u4e00\u4eba\u8eab\u4e0a\u3002\u4ed6\u4eec\u7684\u70ed\u8840\u4ea4\u6d41\u4e86\uff0c\u4ed6\u4eec\u7684 \u8eab\u8eaf\uff0c\u4e5f\u7d27\u7d27\u9760\u5728\u4e00\u5904\uff0c\u4ed6\u4eec\u518d\u4e5f\u89c1\u4e0d\u5230\u827e\u9752\u7684\u60b2\u6ce3\u4e0e\u6b22\u7b11\uff0c\u53ea\u6709\u5979\u6b64\u523b\u5c16\u9510\u800c\u51c4\u60e8\u7684\u4e00\u58f0 \u60ca\u547c\uff0c\u5c06\u6c38\u8fdc\u7559\u5728\u4ed6\u4eec\u8033\u7554\uff0c\u966a\u4f34\u7740\u4ed6\u4eec\uff0c\u76f4\u5230\u6c38\u6052\xb7\xb7\u2026\xb7\uff01\u5de6\u9762\u4e00\u4eba\u5fc3\u623f\u7684\u8df3\u52a8\u505c\u6b62 \u4e86\uff0c\u4ed6\u662f\u54e5\u54e5\uff0c\u4ed6\u6bd4\u53e6\u4e00\u4eba\u5148\u4e00\u523b\u5f00\u59cb\u4ed6\u7684\u751f\u547d\uff0c\u4e5f\u6bd4\u53e6\u4e00\u4e2a\u5148\u4e00\u523b\u7ed3\u675f\uff01\u53f3\u9762\u4e00\u4eba\u773c\u5e18\u5782 \u843d\u4e86\uff0c\u4ed6\u5589\u95f4\u8fd8\u6709\u4e00\u4e1d\u58f0\u97f3\uff01\u201d\u4ed6\u2026\u2026\u6bd5\u2026\u2026\u7ade\u2026\u2026\u662f\u2026\u2026\u7231\u2026\u2026\u6211\u2026\u2026\u7684\uff01\u201c\u8fd9\u4e00\u9635\u7ec6\u5982 \u6e38\u4e1d\u822c\u7684\u58f0\u97f3\uff0c\u7ec8\u7a76\u4e5f\u968f\u7740\u4ed6\u7684\u751f\u547d\u6d88\u5931\uff01\u640f\u6597\u505c\u6b62\u4e86\uff0c\u751f\u547d\u7ed3\u675f\u4e86\uff01\u60c5\u3001\u4ec7\u3001\u6069\u3001\u6028\uff0c\u7ec8 \u4e8e\u6c38\u8fdc\u5730\u89e3\u8131\uff01\u4e00\u5207\u7ea0\u7f20\u4ea4\u7ed3\uff0c\u96be\u4ee5\u5316\u89e3\uff0c\u523b\u9aa8\u94ed\u5fc3\u7684\u4ec7\u6068\u3001\u75db\u82e6\u4e0e\u6b22\u4e50\uff0c\u5728\u201d\u6b7b\u4ea1\u201c\u9762 \u524d\uff0c\u4ff1\u90fd\u8c26\u5351\u5730\u5782\u4e0b\u5934\u53bb\u3002\u53ea\u6709\u9c9c\u8840\uff0c\u4ecd\u5728\u6ef4\u843d\u7740\u3002\u7136\u800c\u4ed6\u4eec\u4e24\u4eba\u7684\u9c9c\u8840\uff0c\u6b64\u523b\u5374\u5df2\u6ef4\u843d\u5728 \u4e00\u5904\uff0c\u6d53\u6d53\u5730\u878d\u5408\u5728\u4e00\u5904\uff0c\u518d\u4e5f\u96be\u4ee5\u5206\u89e3\u3002\u8fd9\u5144\u5f1f\u4e24\u4eba\uff0c\u4e00\u751f\u79bb\u5947\u800c\u8f89\u714c\uff0c\u8f89\u714c\u800c\u75db\u82e6\u4f2a\u751f \u547d\uff0c\u51e0\u4e4e\u5728\u540c\u65f6\u5f00\u59cb\uff0c\u6b64\u523b\uff0c\u5374\u4e5f\u540c\u65f6\u8fd9\u822c\u51c4\u6e05\u800c\u60b2\u60e8\u5730\u7ed3\u675f\u4e86\uff01\u201d\u51b7\u6708\u4ed9\u5b50\u201c\u6bd5\u7adf\u4e0d\u662f\u4ed9 \u5b50\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u7684\u7075\u9b42\u4e0e\u611f\u60c5\uff0c\u4f3c\u4e4e\u4ff1\u90fd\u5df2\u7ecf\u53d8\u4f5c\u9ebb\u6728\uff01\u5979\u90a3\u4e00\u58f0\u5c16\u9510\u7684\u54c0\u547c\uff0c\u6b64\u523b\u4ecd \u7136\u8361\u6f3e\u5728\u5730\u9053\u4e2d\uff0c\u8361\u6f3e\u5728\u88f4\u73cf\u8033\u7554\uff01\u4ed6\u65e0\u52a9\u5730\u773c\u671b\u7740\u8fd9\u4e00\u5e55\u60b2\u5267\u7684\u7ed3\u675f\uff0c\u65e0\u6cd5\u963b\u6b62\uff0c\u4e0d\u77e5\u6240 \u63aa\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u8fd9\u60b2\u5267\u7684\u5f00\u59cb\u2014\u2014\u4f55\u65f6\u5f00\u59cb\uff1f\u4e3a\u4f55\u5f00\u59cb\uff1f\u4ed6\u6728\u7136\u4f01\u7acb\u7740\uff0c\u76f4\u5230\u827e\u9752\u518d \u6b21\u54c0\u547c\u4e00\u58f0\uff0c\u6251\u5728\u4ed6\u4e24\u4eba\u7684\u8eab\u4e0a\u3002\u591a\u5f69\u7684\u949f\u4e73\uff0c\u4ecd\u5728\u7f24\u7eb7\u5730\u95ee\u70c1\u7740\uff0c\u9664\u4e86\u8fb9\u65e0\u60c5\u7684\u5ca9\u77f3\uff0c\u53c8 \u6709\u8c01\u80fd\u5982\u6b64\u6b8b\u9177\u5730\u65e0\u89c6\u4e8e\u4eba\u4eec\u7684\u751f\u6b7b\uff1f\u88f4\u73cf\u6728\u7acb\u5f53\u5730\uff0c\u53ea\u89c9\u56db\u4e0b\u9759\u5bc2\u5982\u6b7b\uff0c\u8fde\u539f\u6765\u7684\u60b2\u6ce3\u4e4b \u58f0\uff0c\u90fd\u5df2\u6e10\u6e10\u6d88\u5931\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u4e00\u52a8\uff01\u201d\u60b2\u54c0\u5982\u6b64\u7684\u2018\u51b7\u6708\u4ed9\u5b50\u2019\uff0c\u4e3a\u4f55\u6ca1\u6709\u54ed\u6ce3\uff1f\u201c\u4ed6\u6bd5 \u7adf\u662f\u7edd\u9876\u806a\u660e\u7684\uff0c\u77e5\u9053\u8fd9\u95ee\u9898\u53ea\u6709\u4e24\u79cd\u7b54\u6848\uff1a\u82e5\u975e\u662f\u90a3\u79cd\u5f3a\u70c8\u7684\u60b2\u54c0\u5df2\u4f7f\u5979\u5168\u7136\u9ebb\u6728\uff0c\u4fbf\u662f \u5979\u5df2\u6e0a\u60b2\u54c0\uff0c\u56e0\u4e3a\u5979\u5df2\u7acb\u4e0b\u51b3\u5fc3\u3002\u6709\u4e86\u4ee5\u8eab\u76f8\u6b89\u4e4b\u610f\u3002\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u5927\u9a87\uff0c\u751a\u81f3\u8fde \u4ed6\u7684\u7075\u9b42\u6df1\u5904\uff0c\u90fd\u8d77\u4e86\u4e00\u9635\u9884\u6599\uff0c\u4ed6\u4e0d\u7531\u81ea\u4e3b\u5730\u8fc8\u52a8\u7740\u811a\u6b65\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u827e\u9752\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u7f13\u7f13\u8f6c\u8fc7\u5934\u6765\uff0c\u5979\u82cd\u767d\u7684\u9762\u5bb9\u4e0a\uff0c\u867d\u7136\u6ee1\u5e03\u6cea\u75d5\uff0c\u4f46\u662f\u5979\u660e\u5a9a\u7684\u79cb\u6ce2\uff0c\u5374 \u662f\u575a\u5b9a\u7684\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u77a5\u4e86\u88f4\u73cf\u51e0\u773c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u73cf\u513f\uff0c\u6211\u4eec\u7ec8\u4e8e\u53c8\u76f8\u89c1\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u672c\u5e94\u65e9\u5df2\u8bf4\u8fc7\u7684\u8bdd\uff0c\u76f4\u5230\u6b64\u523b\u5979\u6728\u8bf4\u51fa\u53e3\u6765\uff0c\u5176\u4e2d\u7684\u610f\u5473\uff0c\u7adf\u5df2\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u88f4\u73cf\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4e9b\u65e5\u5b50\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u672c\u60f3\u95ee\u4e00\u53e5\uff1a\u201c\u4f60\u597d\u5417\uff1f\u201d\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u7a81\u7136\u60f3\u8d77\u81ea\u5df1\u8fd9\u95ee\u9898\uff0c\u5b9e\u5728\u662f\u6bcb\u5eb8\u95ee\u51fa\u6765 \u7684\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u6697\u53f9\u4e00\u58f0\uff0c\u6539\u53e3\u9053\uff1a\u201c\u524d\u51e0\u4e2a\u6708\uff0c\u6211\u89c1\u7740\u4e86\u2026\u2026\u201d \u3000\u3000\u827e\u9752\u7f13\u7f13\u4e00\u70b9\u5934\uff0c\u622a\u65ad\u4e86\uff0c\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u90a3\u662f\u6211\u53eb\u4ed6\u4eec\u53bb\u7684\uff0c\u73cf\u513f\u2026\u2026\u4f60\u5e94 \u77e5\u9053\uff0c\u6211\u4e00\u76f4\u662f\u559c\u6b22\u4f60\u7684\uff0c\u56e0\u4e3a\u8fd9\u4e16\u4e0a\u5584\u826f\u7684\u4eba\u5b9e\u5728\u592a\u5c11\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5f3a\u5fcd\u7740\u5fc3\u5934\u7684\u60b2\u54c0\uff0c\u4f46\u79ef\u805a\u5728\u4ed6\u80f8\u8179\u95ee\u7684\u60b2\u54c0\uff0c\u5374\u50cf\u662f\u4e00\u5757\u6c89\u91cd\u7684\u5c71\u77f3\uff0c\u538b\u5f97\u4ed6\u8bf4 \u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u7f24\u7eb7\u7684\u5f69\u5149\u4e0b\uff0c\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u7a81\u7136\u8f7b\u8f7b\u4e00\u7b11\uff0c\u8fd9\u4e00\u4e1d\u60b2\u54c0\u7684\u7b11\u5bb9\uff0c\u5b9e\u5728\u6bd4\u54ed\u6ce3\u8fd8\u8981\u4ee4\u4eba \u5fc3\u52a8\u3002 \u3000\u3000\u5979\u5c31\u5e26\u7740\u8fd9\u4e00\u4e1d\u7b11\u5bb9\uff0c\u53c8\u4ed4\u7ec6\u5730\u77a7\u4e86\u88f4\u73cf\u51e0\u773c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u80fd\u518d\u89c1\u7740\u4f60\u4e00\u9762\uff0c\u6211\u5f88\u9ad8 \u5174\uff0c\u4f60\u2026\u2026\u4f60\u53d8\u4e86\u8bb8\u591a\uff0c\u4e5f\u957f\u5927\u4e86\u8bb8\u591a\uff0c\u73b0\u5728\uff0c\u4f60\u770b\u8d77\u6765\u5df2\u662f\u4e00\u4e2a\u7537\u4eba\uff0c\u4e0d\u518d\u662f\u4e2a\u5b69\u5b50\u4e86\uff0c \u5509\u2026\u2026\u80fd\u591f\u89c1\u5230\u4f60\u957f\u5927\u6210\u4eba\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u5f88\u597d\u5f88\u597d\u7684\u4e8b\u3002\u201d \u3000\u3000\u5979\u76ee\u5149\u60a0\u60a0\u671b\u5411\u8fdc\u5904\u7684\u9ed1\u6697\uff0c\u90a3\u662f\u4e00\u79cd\u591a\u4e48\u51c4\u695a\u3001\u591a\u4e48\u5e7d\u6028\uff0c\u800c\u591a\u4e48\u7f8e\u4e3d\u52a8\u4eba\u7684\u76ee\u5149\uff0c \u50cf\u6d77\u6c34\u4e00\u822c\u6df1\u8fed\uff0c\u50cf\u6676\u661f\u4e00\u822c\u660e\u4eae\uff01 \u3000\u3000\u88f4\u73cf\u5782\u4e0b\u5934\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u4f60\u4ee5\u540e\u53ef\u4ee5\u65f6\u5e38\u89c1\u7740\u6211\u7684\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u5ffd\u7136\u6539\u53e3\u8bf4\uff1a\u201c\u6211\u2026\u2026\u6211\u66ff\u4f60\u5c06\u8eab\u4e0a\u7684\u94a2\u9488\u62d4\u6389\u597d\u5417\uff1f\u201d \u3000\u3000\u827e\u9752\u7684\u76ee\u5149\u4ecd\u7136\u51dd\u6ce8\u7740\u8fdc\u65b9\uff0c\u751f\u50cf\u662f\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u534f\uff0c\u5979\u4eff\u4f5b\u5df2\u6c89\u6d78\u4e8e\u5f80\u4e8b\u7684\u6b22\u4e50\u4e0e\u75db \u82e6\u4e2d\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u5979\u8f7b\u8f7b\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5df2\u957f\u6210\u4e86\u5927\u4eba\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u662f\u4e0d\u662f\u8fd8\u50cf\u4ee5\u524d\u4e00 \u6837\u542c\u6211\u7684\u8bdd\uff1f\u201d \u3000\u3000\u88f4\u73cf\u60f6\u58f0\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u2026\u2026\u6211\u90fd\u4e00\u5b9a\u4f1a\u505a\u7684\u3002\u201d \u3000\u3000\u827e\u9752\u9762\u4e0a\u53c8\u662f\u7efd\u5f00\u4e00\u4e1d\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u90a3\u4e48\u2026\u2026\u4f60\u8dea\u4e0b\u6765\uff0c\u53d1\u8a93\uff0c\u8981\u7b54\u5e94\u6211\u4e09\u4ef6 \u4e8b\uff0c\u65e0\u8bba\u600e\u4e48\u6837\uff0c\u4f60\u90fd\u8981\u7167\u6211\u7684\u8bdd\u53bb\u505a\uff0c\u6c38\u8fdc\u4e5f\u4e0d\u80fd\u66f4\u6539\uff01\u201d \u3000\u3000\u82e5\u662f\u522b\u4eba\u5bf9\u88f4\u73cf\u8bf4\u51fa\u6b64\u8bdd\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u8003\u8651\u7684\uff0c\u56e0\u4e3a\u4ed6\u751f\u6015\u522b\u4eba\u6559\u4ed6\u505a\u4e00\u4e9b\u4e0d\u613f\u610f\u505a\u7684 \u4e8b\u3002 \u3000\u3000\u4f46\u662f\uff0c\u827e\u9752\uff0c\u5374\u50cf\u662f\u6709\u4e00\u79cd\u795e\u79d8\u7684\u529b\u91cf\uff0c\u88f4\u73cf\u60f3\u4e5f\u4e0d\u60f3\uff0c\u4fbf\u8f7b\u8f7b\u8dea\u4e86\u4e0b\u53bb\uff0c\u5927\u58f0\u9053\uff1a \u201c\u6211\u4e00\u88f4\u73cf\uff0c\u82e5\u662f\u2026\u2026\u82e5\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u4f1a\u53d1\u8a93\uff0c\u827e\u9752\u8f7b\u8f7b\u9053\uff1a\u201c\u82e5\u662f\u6ca1\u6709\u4f9d\u7167\u827e\u9752\u7684\u8bdd\u53bb\u505a\uff0c\u4fbf\u2026\u2026\u4fbf\u53d7\u5929\u6253\u96f7\u51fb\uff0c \u4e07\u52ab\u4e0d\u590d\uff01\u201d \u3000\u3000\u88f4\u73cf\u5927\u58f0\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u6837\uff0c\u88f4\u73cf\u82e5\u4e0d\u7167\u827e\u9752\u7684\u8bdd\u53bb\u505a\uff0c\u4fbf\u53d7\u5929\u6253\u96f7\u51fb\uff0c\u4e07\u52ab\u4e0d\u590d\uff01\u201d \u3000\u3000\u5ffd\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u827e\u9752\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\uff0c\u4f60\u4ece\u4eca\u4ee5\u540e\uff0c\u6709\u751f\u4e4b\u5e74\uff0c\u6c38\u8fdc\u4e0d\u8981\u53bb\u6b3a\u9a97\u4efb\u4f55\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c \u6c38\u8fdc\u4e0d\u8981\u53eb\u4efb\u4f55\u4e00\u4e2a\u5973\u5b69\u5b50\u4f24\u5fc3\uff0c\u4e0d\u7ba1\u4f60\u7231\u4e0d\u7231\u5979\uff0c\u53ea\u8981\u5979\u5bf9\u4f60\u597d\uff0c\u4f60\u5c31\u8be5\u597d\u597d\u5730\u53bb\u4fdd\u62a4\u7740 \u5979\uff0c\u65e0\u8bba\u4e3a\u4e86\u4ec0\u4e48\u539f\u56e0\uff0c\u90fd\u4e0d\u8981\u8ba9\u5979\u53d7\u5230\u522b\u4eba\u7684\u4f24\u5bb3\uff0c\u4f60\u7b54\u5e94\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u7acb\u523b\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u4f1a\u8ba9\u4e00\u4e2a\u5bf9\u6211\u597d\u7684\u5973\u5b69\u5b50\u53d7\u5230\u522b\u4eba\u4f24\u5bb3\u7684\u3002\u201d \u3000\u3000\u827e\u9752\u76ee\u5149\u4e2d\u95ea\u8fc7\u4e00\u4e1d\u5f3a\u70c8\u7684\u54c0\u6028\u4e4b\u5149\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4e8b\u8bf4\u6765\u5bb9\u6613\uff0c\u5176\u5b9e\u2026\u2026\u5509\uff0c\u5374\u662f\u5f88 \u96be\u7684\uff0c\u56e0\u4e3a\u4e16\u4e0a\u603b\u6709\u591a\u5c11\u5947\u602a\u7684\u539f\u56e0\uff0c\u8ba9\u4f60\u4e0d\u5f97\u4e0d\u53bb\u4f24\u5bb3\u4e00\u4e2a\u7231\u4f60\u7684\u4eba\uff01\u201d \u3000\u3000\u88f4\u73cf\u80f8\u819b\u4e00\u633a\uff0c\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u6c38\u8fdc\u4e0d\u4f1a\u7684\u3002\u201d \u3000\u3000\u827e\u9752\u5b89\u6170\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u8bb0\u4f4f\u4eca\u5929\u7684\u8bdd\u2026\u2026\u7b2c\u4e8c\u4ef6\uff0c\u6211\u8981\u4f60\u5728\u8fd9\u4e2a\u6d1e\u7a9f\u91cc \u966a\u6211\u4e09\u5929\uff0c\u65e0\u8bba\u53d7\u5230\u4ec0\u4e48\u75db\u82e6\uff0c\u90fd\u4e0d\u8981\u79bb\u5f00\u3002\u5509\u2026\u2026\u90a3\u5c06\u662f\u975e\u5e38\u75db\u6614\u7684\u4e09\u5929\uff0c\u56e0\u4e3a\u9ed1\u6697\u3001\u9965 \u6e34\u3001\u75b2\u5026\uff0c\u8fd9\u8bb8\u591a\u79cd\u81ea\u53e4\u4ee5\u6765\u4eba\u7c7b\u6700\u5927\u7684\u654c\u4eba\uff0c\u9a6c\u4e0a\u5c31\u8981\u6765\u4e86\uff0c\u4f60\u80fd\u5fcd\u53d7\u5f97\u4f4f\u8fd9\u4e9b\u75db\u82e6\u4e48\uff1f \u4f60\u7b54\u5e94\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u9894\u9996\u9053\uff1a\u201c\u6211\u7b54\u5e94\uff0c\u4ec0\u4e48\u75db\u82e6\uff0c\u6211\u90fd\u80fd\u5fcd\u53d7\u7684\u3002\u201d\u4ed6\u5ffd\u7136\u60f3\u8d77\u5b88\u5019\u5728\u5916\u9762\u7684\u201c\u51b7\u8c37 \u53cc\u6728\u201d\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u6cdb\u8d77\u4e00\u4e1d\u6b49\u610f\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u8f7b\u53f9\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u6211\u77e5\u9053\u4f60\u80fd\u4e3a\u6211\u5fcd\u53d7\u75db\u82e6\u7684\uff0c\u4f46\u662f\u6211\u7b54\u5e94\u4f60\uff0c\u4f60\u6240\u5fcd \u53d7\u7684\u4e00\u5207\u75db\u82e6\uff0c\u90fd\u5c06\u4f1a\u5f97\u5230\u5341\u500d\u7684\u62a5\u507f\u3002\u201d \u3000\u3000\u88f4\u73cf\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u8981\u62a5\u507f\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u6587\u9752\u5e7d\u54c0\u5730\u4e00\u7b11\uff0c\u76ee\u5149\u66f4\u5145\u6ee1\u4e86\u5b89\u6170\u4e0e\u8d5e\u8d4f\u4e4b\u610f\uff0c\u5979\u5583\u5583\u7740\u9053\uff1a\u201c\u6211\u80fd\u4e3a\u8fd9\u5b69\u5b50\u5c3d\u6700\u540e \u7684\u4e00\u4efd\u5fc3\u529b\uff0c\u8ba9\u4ed6\u597d\u597d\u505a\u4eba\uff0c\u4e3a\u6b66\u6797\u589e\u5149\uff0c\u6211\u6b7b\u4e86\u4e5f\u8be5\u4f1a\u7b11\u4e86\u5427\uff1f\u201d \u3000\u3000\u5979\u8bed\u58f0\u6a21\u7cca\uff0c\u88f4\u73cf\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u827e\u9752\u9053\uff1a\u201c\u5728\u6211\u8bf4\u7b2c\u4e09\u4ef6\u4e8b\u4e4b\u524d\uff0c\u6211\u8981\u5bf9\u4f60\u8bf4\u4e00\u4e2a\u6545\u4e8b\uff0c\u4f46\u662f\u4f60\u6c38\u8fdc\u4e0d\u80fd \u518d\u5c06\u5b83\u8bf4\u51fa\u53bb\uff0c\u6211\u53ea\u662f\u2026\u2026\u6211\u53ea\u662f\u5fc5\u987b\u5bf9\u4eba\u8bf4\u8fd9\u4e2a\u6545\u4e8b\uff0c\u5509\u2026\u2026\u82cd\u5929\u6bd5\u7adf\u662f\u4ec1\u6148\u7684\uff0c\u5b83\u8ba9\u6211 \u80fd\u5728\u8fd9\u4e2a\u65f6\u5019\u89c1\u7740\u4f60\u3002\u201d \u3000\u3000\u5979\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u5c06\u90a3\u94dc\u706f\u4e2d\u7684\u706b\u7130\u62e8\u5f97\u66f4\u5c0f\u4e86\u4e9b\uff0c\u4e8e\u662f\u5979\u5931\u8840\u7684\u9762\u8272\uff0c\u5c31\u66f4\u51c4\u695a\u3002\u5979 \u8f7b\u8f7b\u81ea\u8bed\u7740\u9053\uff1a\u201c\u706b\u7130\u5c0f\u4e9b\uff0c\u5c31\u4f1a\u4eae\u5f97\u4e45\u4e9b\uff0c\u751f\u547d\u2026\u2026\u751f\u547d\u4e0d\u4e5f\u662f\u4e00\u6837\u4e48\uff1f\u4efb\u4f55\u4e00\u79cd\u5f3a\u70c8\u7684 \u5149\u8f89\uff0c\u90fd\u4e0d\u4f1a\u957f\u4e45\u7684\uff0c\u9664\u975e\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u671b\u4e86\u88f4\u73cf\u4e00\u773c\uff1a\u201c\u9664\u975e\u4ed6\u6709\u4e00\u9897\u5584\u826f\u7684\u5fc3\u2026\u2026\u201d \u3000\u3000\u4e8e\u662f\u5979\u7f13\u7f13\u53d6\u51fa\u4e00\u65b9\u7f57\u4e2d\uff0c\u8f7b\u8f7b\u64e6\u5e72\u4e86\u90a3\u5df2\u6b7b\u53bb\u7684\u4e24\u4f4d\u6b66\u6797\u5f02\u4eba\u9762\u4e0a\u7684\u8840\u8ff9\uff0c\u5c06\u4ed6\u4eec\u73af \u62b1\u7740\u5bf9\u65b9\u80a9\u5934\u7684\u624b\u81c2\uff0c\u56f4\u5f97\u66f4\u7d27\u4e86\u4e9b\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u518d\u6b21\u5750\u4e86\u4e0b\u6765\uff0c\u9762\u5bf9\u7740\u88f4\u73cf\uff0c\u5f00\u59cb\u4e86\u5979\u7684\u6545\u4e8b\uff1a\u201c\u4ece\u524d\uff0c\u6709\u4e00\u4e00\u4e2a\u5e73\u51e1\u7684\u5987 \u4eba\uff0c\u4e0d\u77e5\u662f\u5e78\u8fd0\u6291\u6216\u662f\u4e0d\u5e78\uff0c\u5979\u751f\u4e86\u4e00\u53cc\u5b6a\u751f\u5b69\u5b50\uff0c\u4e0d\u5e73\u51e1\u7684\u5b69\u5b50\uff0c\u5979\u90a3\u5e73\u51e1\u7684\u751f\u547d\uff0c\u50cf\u662f \u5b8c\u5168\u4e3a\u4e86\u8981\u5b8c\u6210\u8fd9\u4f7f\u547d\u800c\u751f\u5b58\u7684\uff0c\u56e0\u4e3a\u5979\u751f\u51fa\u8fd9\u4e00\u5bf9\u5b69\u5b50\u540e\uff0c\u7acb\u523b\u5c31\u6b7b\u4e86\u3002\u201d\u5b63\u8282\u53d8\u5316\uff0c\u5c81 \u6708\u6d88\u901d\uff0c\u8fd9\u4e00\u53cc\u5b69\u5b50\uff0c\u6e10\u6e10\u5730\u957f\u5927\u4e86\uff0c\u4ed6\u4eec\u7684\u9762\u8c8c\u3001\u8eab\u6750\uff0c\u751a\u81f3\u58f0\u97f3\u3001\u4e3e\u52a8\uff0c\u90fd\u662f\u90a3\u4e48\u76f8 \u50cf\uff1b\u6709\u65f6\u8fde\u4ed6\u4eec\u7684\u7236\u4eb2\u90fd\u65e0\u6cd5\u5206\u8fa8\u51fa\u4ed6\u4eec\u7a76\u7adf\u8c01\u662f\u54e5\u54e5\uff0c\u8c01\u662f\u5f1f\u5f1f\u3002\u2026 \u3000\u3000\u201c\u4f46\u662f\uff0c\u82cd\u5929\u5374\u504f\u504f\u5728\u8fd9\u4e00\u53cc\u5b69\u5b50\u8eab\u4f53\u91cc\uff0c\u653e\u8fdb\u4e86\u4e24\u9897\u4e0d\u540c\u7684\u5fc3\uff0c\u54e5\u54e5\u662f\u65e2\u806a\u660e\u3001\u53c8\u9a84 \u505a\u3001\u53c8\u5f3a\u6a2a\uff0c\u5f1f\u5f1f\u5374\u662f\u53c8\u8f6f\u5f31\u3001\u53c8\u5584\u826f\uff0c\u65e0\u8bba\u662f\u5728\u5bb6\u91cc\u6291\u6216\u5728\u79c1\u587e\u91cc\uff0c\u4e00\u5207\u7684\u5149\u8363\uff0c\u90fd\u662f\u54e5 \u54e5\u7684\uff0c\u751a\u81f3\u8fde\u4ed6\u4eec\u7684\u7236\u4eb2\uff0c\u4e5f\u4e0d\u559c\u6b22\u8fd9\u53ef\u601c\u7684\u5f1f\u5f1f\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u5982\u82e5\u6ca1\u6709\u8fd9\u4e2a\u5f1f\u5f1f\uff0c\u90a3\u4e48\u4ed6 \u7684\u59bb\u5b50\u6216\u8bb8\u5c31\u4e0d\u4f1a\u5728\u751f\u4ea7\u4e2d\u6b7b\u53bb\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u662f\u90a3\u4e48\u6e29\u67d4\u800c\u7f8e\u4e3d\uff0c\u4f46\u662f\uff0c\u5979\u6240\u53d9\u8bf4\u7684\u8fd9\u4e2a\u6545\u4e8b\u5374\u662f\u8fd9\u822c\u60b2\u5207\uff01\u88f4\u73cf\u76d8\u819d\u5750\u5728 \u5730\u4e0a\uff0c\u51e0\u4e4e\u542c\u5f97\u5446\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5979\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u751f\u957f\u7684\u5f1f\u5f1f\uff0c\u81ea\u7136\u5c31\u517b\u6210\u4e86\u4e00\u79cd\u9634\u90c1\u7684\u4e2a\u6027\uff0c\u5bf9\u4e8e\u4efb \u4f55\u6479\uff0c\u4ed6\u90fd\u9006\u6765\u987a\u53d7\u5730\u5fcd\u53d7\uff0c\u4f46\u662f\u4ed6\u7684\u5fc3\u91cc\uff0c\u5374\u4e00\u6b21\u53c8\u4e00\u6b21\u5730\u544a\u8bc9\u81ea\u5df1\uff0c\u62a5\u590d\u2026\u62a5\u590d\u2026\u2026\u603b \u6709\u4e00\u5929\u8981\u62a5\u590d\u7684\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u7f8e\u4e3d\u7684\u8bed\u58f0\u7adf\u7a81\u5730\u8d77\u4e86\u4e00\u9635\u98a4\u6296\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u60ca\uff0c\u53ea\u89c9\u5979\u53e3\u5236\u8bb6\u8bf4\u7684\u8fd9\u201c\u62a5\u590d\u201d\u4e8c\u5b57\uff0c\u5176\u4e2d\u7adf\u542b\u8574\u7740\u90a3\u4e48\u6df1\u9083\u7684\u6076\u6bd2\u4e0e\u6050 \u60e7\uff0c\u751f\u8c6b\u5267\u53d8\u62a5\u590d\u7684\u5bf9\u8c61\u4e0d\u662f\u90a3\u9a84\u50b2\u7684\u54e5\u54e5\uff0c\u800c\u662f\u5979\u81ea\u5df1\u3002 \u3000\u3000\u98a4\u6296\u7740\u7684\u8bed\u58f0\u6e10\u6e10\u5e73\u590d\uff0c\u5979\u63a5\u7740\u9053\uff1a\u201c\u6709\u4e00\u5929\uff0c\u54e5\u54e5\u5931\u624b\u6253\u788e\u4e86\u4ed6\u7239\u7239\u6700\u7231\u7684\u53e4\u74f6\uff0c\u5374 \u5c06\u8d23\u4efb\u63a8\u5230\u5f1f\u5f1f\u8eab\u4e0a\uff0c\u800c\u90a3\u504f\u5fc3\u7684\u7239\u7239\uff0c\u5374\u76f8\u4fe1\u4e86\u54e5\u54e5\u7684\u8bdd\u3002\u201d\u53d7\u4e86\u51a4\u5c48\u4e0e\u8d23\u9a7e\u7684\u5f1f\u5f1f\uff0c\u4e58 \u7740\u9ed1\u591c\uff0c\u9003\u4e86\u51fa\u53bb\uff0c\u4f46\u7239\u7239\u4e0e\u54e5\u54e5\u4e1d\u6beb\u4e5f\u4e0d\u7740\u6025\uff0c\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u8f6f\u5f15\u7684\u5f1f\u5f1f\u4e00\u5b9a\u4f1a\u56de\u6765\u7684\u3002 \u3000\u3000\u201c\u7b2c\u4e09\u5929\uff0c\u5f1f\u5f1f\u679c\u7136\u56de\u6765\u4e86\uff0c\u4ed6\u9762\u4e0a\u662f\u4f3c\u4e4e\u5e26\u7740\u4e00\u79cd\u5947\u5f02\u800c\u5feb\u6d3b\u7684\u5149\u8f89\uff0c\u5bf9\u4e8e\u4efb\u4f55\u8d23 \u9a82\uff0c\u5374\u50cf\u662f\u6ca1\u6709\u542c\u89c1\uff0c\u806a\u660e\u7684\u54e5\u54e5\u770b\u51fa\u4e86\u5f1f\u5f1f\u7684\u5947\u602a\u5fc3\u60c5\uff0c\u4e0d\u65ad\u5730\u903c\u7740\u4ed6\uff0c\u95ee\u4ed6\u5230\u5e95\u4e3a\u4e86\u4ec0 \u4e48\uff1f\u201d\u5f1f\u5f1f\u4f3c\u4e4e\u4e0d\u613f\u8bf4\u51fa\u6765\uff0c\u4f46\u7ec8\u4e8e\u8bf4\u51fa\u6765\u4e86\uff01\u4ed6\u8bf4\uff1a\u201c\u5728\u4ed6\u51fa\u8d70\u7684\u5730\u65b9\uff0c\u9047\u7740\u4e86\u4ed9\u4eba\uff0c\u90a3 \u4ed9\u4eba\u544a\u8bc9\u4ed6\uff0c\u53eb\u4ed6\u65e5\u540e\u518d\u5230\u90a3\u91cc\u53bb\uff0c\u8981\u4f20\u6388\u7ed9\u4ed6\u4e00\u79cd\u795e\u5947\u7684\u4ed9\u6cd5\uff0c\u6536\u4ed6\u4e3a\u5f92\u5f1f\u3002\u201d\u4e8e\u662f\u54e5\u54e5 \u5f00\u59cb\u5ac9\u5992\u8d77\u5f1f\u5f1f\u6765\u4e86\uff0c\u4ed6\u51e0\u4e4e\u65e0\u6cd5\u5b89\u7761\uff0c\u5230\u540e\u6765\uff0c\u4ed6\u7adf\u60f3\u51fa\u4e86\u4e00\u6761\u6076\u6bd2\u7684\u8ba1\u5212\u3002 \u3000\u3000\u201c\u5230\u4e86\u7b2c\u4e09\u5929\uff0c\u4ed6\u8fd8\u5047\u88c5\u8981\u9001\u5f1f\u5f1f\uff0c\u5e76\u4e14\u518d\u4e09\u95ee\u4ed6\u7684\u5f1f\u5f1f\uff0c\u90a3\u4ed9\u4eba\u7a76\u7adf\u662f\u4f4f\u5728\u4ec0\u4e48\u5730 \u65b9\uff1f\u201d\u5f1f\u5f1f\u5e26\u7740\u4e00\u79cd\u5947\u5f02\u7684\u795e\u8272\uff0c\u5f88\u8be6\u7ec6\u5730\u544a\u8bc9\u4e86\u4ed6\uff0c\u4ed6\uff0c\u5fc3\u91cc\u6697\u6697\u5730\u7b11\uff0c\u4ee5\u4e3a\u5f1f\u5f1f\u4e2d\u4e86 \u8ba1\uff0c\u56e0\u4e3a\u4ed6\u65e9\u5df2\u60f3\u597d\u8ba1\u5212\uff0c\u8981\u5c06\u5f1f\u5f1f\u5bb3\u6b7b\uff0c\u7136\u540e\uff0c\u4ed6\u518d\u88c5\u6210\u5f1f\u5f1f\u7684\u6837\u5b50\uff0c\u5230\u4ed9\u4eba\u90a3\u91cc\u53bb\uff0c\u53cd \u6b63\u4ed6\u5144\u5f1f\u4e24\u4eba\u9762\u8c8c\u5b8c\u5168\u4e00\u6837\uff0c\u5373\u4f7f\u662f\u4ed9\u4eba\uff0c\u4e5f\u672a\u89c1\u5f97\u80fd\u5206\u8fa8\u5f97\u51fa\u3002 \u3000\u3000\u201c\u4f46\u662f\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u5f1f\u5f1f\u6839\u672c\u6ca1\u6709\u9047\u5230\u4ed9\u4eba\uff0c\u4ed6\u53ea\u662f\u4ece\u5c71\u4e0a\u7684\u730e\u6237\u53e3\u4e2d\u6253\u542c\u51fa\u4e00\u4e2a\u91ce\u517d \u6700\u5e38\u51fa\u6ca1\u3001\u751a\u81f3\u8fde\u730e\u4eba\u90fd\u4e0d\u6562\u53bb\u7684\u5730\u65b9\uff0c\u4ed6\u6545\u610f\u88c5\u51fa\u4e86\u90a3\u526f\u795e\u8272\uff0c\u4fbf\u77e5\u9053\u4ed6\u54e5\u54e5\u4e00\u5b9a\u4f1a\u62a2\u7740 \u53bb\u7684\u3002\u201d\u4f46\u662f\uff0c\u4ed6\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u4ed6\u54e5\u54e5\u7adf\u8981\u5b58\u5fc3\u5bb3\u6b7b\u4ed6\u3002\u201c\u5979\u8f7b\u53f9\u4e00\u58f0\uff0c\u987f\u4f4f\u8bed\u58f0\uff0c\u53ea\u542c\u5f97 \u5584\u826f\u7684\u88f4\u73cf\u638c\u5fc3\u90fd\u4e0d\u7981\u6c81\u51fa\u4e86\u51b7\u6266\u3002\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u4eba\u4e0e\u4eba\u4e4b\u95f4\uff0c\u7adf\u4f1a\u4f7f\u51fa\u8fd9\u822c\u6076\u6bd2\u7684\u5fc3\u8ba1\u6765 \u4e92\u76f8\u6b8b\u5bb3\uff01\u51b5\u4e14\u662f\u5ae1\u4eb2\u7684\u5b6a\u751f\u5144\u5f1f\uff1f\u201d\u51b7\u6708\u4ed9\u5b50\u201c\u4e0d\u81ea\u89c9\u5730\u56de\u9996\u671b\u4e86\u671b\u90a3\u4e24\u5177\u4e92\u76f8\u62e5\u62b1\u7740\u7684 \u5c38\u8eab\uff0c\u53c8\u81ea\u5e7d\u5e7d\u957f\u53f9\u4e86\u58f0\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e24\u4eba\u8c01\u4e5f\u6ca1\u6709\u544a\u8bc9\u7239\u7239\uff0c\u5c31\u4e00\u8d77\u5077\u5077\u4e0a\u4e86\u5c71\uff0c\u54e5\u54e5 \u5728\u6697\u4e2d\u5f97\u610f\uff0c\u5f1f\u5f1f\u4e5f\u5728\u6697\u4e2d\u5f97\u610f\uff0c\u5230\u4e86\u4e00\u4e2a\u9669\u5cfb\u7684\u5ced\u58c1\u5904\uff0c\u54e5\u54e5\u8bf4\uff1a\u2018\u4eca\u65e5\u4e00\u522b\uff0c\u4e0d\u77e5\u5728\u4f55 \u65f6\u624d\u80fd\u76f8\u89c1\u4e86\u3002\u2019\u5f1f\u5f1f\u4e5f\u8bf4\uff1a\u2018\u4eca\u65e5\u4e00\u522b\uff0c\u771f\u4e0d\u77e5\u4f55\u65f6\u624d\u80fd\u76f8\u89c1\u4e86\u3002\u2019\u4ed6\u6b64\u523b\u5fc3\u91cc\u5374\u5728\u5947 \u602a\uff0c\u54e5\u54e5\u4e3a\u4ec0\u4e48\u6ca1\u6709\u62a2\u7740\u53bb\uff01 \u3000\u3000\u201c\u54ea\u77e5\u4ed6\u5ff5\u5934\u8fd8\u6ca1\u6709\u8f6c\u5b8c\uff0c\u54e5\u54e5\u7a81\u5730\u7528\u5c3d\u5e73\u751f\u6c14\u529b\uff0c\u5c06\u4ed6\u63a8\u843d\u4e86\u60ac\u5d16\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fcd\u4e0d\u4f4f\u60ca\u547c\u4e86\u4e00\u58f0\uff0c\u827e\u9752\u53f9\u9053\uff1a\u201c\u7ad9\u5728\u5d16\u4e0a\u7684\u54e5\u54e5\uff0c\u53ea\u542c\u5f97\u5f1f\u5f1f\u60e8\u547c\u7740\u8dcc\u4e0b\u4e86\u60ac \u5d16\uff0c\u5fc3\u91cc\u4e5f\u6709\u4e9b\u5bb3\u6015\uff0c\u4fbf\u653e\u8db3\u72c2\u5954\u8d77\u6765\uff0c\u4e00\u76f4\u8dd1\u5230\u4e86\u5f1f\u5f1f\u6240\u8bf4\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u4ed6\u6ca1\u6709\u627e\u7740\u4ed9\u4eba\uff0c\u4fbf\u5df2\u9047\u7740\u4e86\u4e00\u53ea\u767d\u989d\u731b\u864e\uff0c\u90a3\u65f6\u4ed6\u624d\u4e0d\u8fc7\u5341\u4e8c\u5c81\uff0c\u4f46\u5374\u5df2\u6709\u4e86 \u8fc7\u4eba\u7684\u52c7\u6c14\uff0c\u7adf\u80fd\u4ee5\u5f02\u5e38\u7684\u9547\u9759\uff0c\u6765\u5e94\u4ed8\u8fd9\u7a81\u6765\u7684\u5371\u673a\uff0c\u4f46\u662f\u5341\u4e8c\u5c81\u7684\u5e7c\u7ae5\u600e\u80fd\u6bd4\u5f97\u4e0a\u51f6\u6076 \u7684\u731b\u864e\uff1f\u773c\u770b\u4ed6\u5c31\u8981\u6b7b\u5728\u90a3\u731b\u864e\u7684\u5229\u722a\u4e0b\u3002\u201d \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u81ea\u5df1\u7684\u547c\u5438\u5df2\u6e10\u6e10\u6c89\u91cd\u8d77\u6765\uff0c\u827e\u9752\u63a5\u7740\u9053\uff1a\u201c\u54ea\u77e5\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u731b\u864e\u7684\u543c\u58f0\uff0c \u7adf\u60ca\u52a8\u4e86\u4e00\u4f4d\u6b66\u6797\u4e2d\u524d\u8f88\u5f02\u4eba\uff0c\u5c06\u54e5\u54e5\u6551\u51fa\u4e86\u864e\u722a\u3002\u201d\u8fd9\u4f4d\u6b66\u6797\u5f02\u4eba\u6df1\u559c\u8fd9\u5b69\u5b50\u7684\u9547\u9759\u4e0e\u806a \u660e\uff0c\u4fbf\u95ee\u4ed6\u613f\u4e0d\u613f\u610f\u505a\u81ea\u5df1\u7684\u5f92\u5f1f\uff1f\u806a\u660e\u7684\u54e5\u54e5\u798f\u81f3\u5fc3\u7075\uff0c\u81ea\u7136\u5c31\u7acb\u523b\u62dc\u5012\u5728\u4ed6\u819d\u4e0b\u3002 \u3000\u3000\u201c\u4e8e\u662f\u4ed6\u56e0\u7978\u5f97\u798f\uff0c\u4e0d\u5230\u5341\u5e74\uff0c\u4fbf\u4f20\u5f97\u4e86\u90a3\u5f02\u4eba\u7684\u4e00\u8eab\u7edd\u6280\uff0c\u53ea\u662f\u5728\u6df1\u591c\u68a6\u56de\u7684\u65f6\u5019\uff0c \u4ed6\u671b\u7740\u7a97\u5916\u7684\u591c\u8272\uff0c\u8033\u7554\u4eff\u4f5b\u603b\u662f\u4f1a\u54cd\u8d77\u4ed6\u5f1f\u5f1f\u8dcc\u843d\u60ac\u5d16\u65f6\u90a3\u79cd\u51c4\u5389\u7684\u60e8\u547c\uff01\u4ed6\u5fc3\u5e95\u4e5f\u4e0d\u7981 \u4f1a\u6cdb\u8d77\u4e00\u9635\u9635\u96be\u8a00\u7684\u60ca\u6817\u3002\u201d \u3000\u3000\u65e0\u98ce\u7684\u79d8\u7a9f\u91cc\uff0c\u7a81\u5730\u5e73\u6dfb\u4e86\u51e0\u5206\u5bd2\u51b7\u4e4b\u610f\u3002 \u3000\u3000\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u827e\u9752\u7ee7\u7eed\u53d9\u8bf4\u5979\u7684\u6545\u4e8b\uff1a\u201c\u5341\u5e74\u4e4b\u540e\uff0c\u90a3\u6b66\u6797\u5f02\u4eba\u7ec8\u4e8e\u6b7b\u4e86\uff01\u4ed6\u5144\u5f1f\u4fe9\u7684 \u7239\u7239\uff0c\u4e5f\u56e0\u7a81\u7136\u5931\u53bb\u4e86\u4e24\u4e2a\u513f\u5b50\uff0c\u90c1\u90c1\u800c\u7ec8\u3002\u201d\u5b66\u5f97\u4e86\u4e00\u8eab\u7edd\u6280\u7684\u54e5\u54e5\uff0c\u81ea\u7136\u4e0d\u4f1a\u57cb\u6ca1\u5728\u8352 \u5c71\u91cc\uff01\u4ed6\u4ed7\u5251\u4e0b\u5c71\uff0c\u51fa\u9053\u6c5f\u6e56\uff0c\u4e0d\u5230\u4e09\u5e74\uff0c\u4fbf\u5728\u6b66\u6797\u4e2d\u4f20\u5f97\u4e86\u60ca\u4eba\u7684\u540d\u58f0\u3002\u6709\u4e00\u6b21\uff0c\u4ed6\u5728\u7518 \u51c9\u9053\u4e0a\uff0c\u4ece\u4e00\u7fa4\u5927\u76d7\u624b\u4e2d\uff0c\u6551\u51fa\u4e86\u4e00\u4e2a\u5e74\u8f7b\u7684\u5973\u5b50\uff0c\u8fd9\u5973\u5b50\u611f\u6fc0\u4ed6\u7684\u5927\u6069\uff0c\u53c8\u503e\u7fa1\u4ed6\u90a3\u4e00\u8eab \u7edd\u6280\uff0c\u518d\u52a0\u4e0a\u4ed6\u4e3a\u4edf\u6094\u81ea\u5df1\u5e7c\u5e74\u7684\u7f6a\u6076\uff0c\u6240\u505a\u4e0b\u7684\u82f1\u98ce\u4fa0\u884c\uff0c\u4e5f\u6df1\u6df1\u6253\u52a8\u4e86\u90a3\u5973\u5b50\uff0c\u7ec8\u4e8e\uff0c \u4ed6\u4eec\u975e\u5e38\u81ea\u7136\u5730\u7ed3\u6210\u4e86\u592b\u59bb\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u6bb5\u65e5\u5b50\uff0c\u5728\u4ed6\u4eec\u4e24\u4eba\u4e00\u751f\u4e2d\uff0c\u90fd\u662f\u6700\u7f8e\u4e3d\u7684\u3002\u4ed6\u4eec\u5728\u4e00\u8d77\u8bfb\u4e66\uff0c\u5728\u4e00\u8d77\u5b66\u5251\uff0c\u4ed6 \u5c06\u5f97\u81ea\u4ed6\u5e08\u5085\u7684\u4e00\u672c\u6b66\u6797\u79d8\u7b08\u2018\u6d77\u5929\u79d8\u7b08\u2019\u4e0a\u7684\u6b66\u6280\uff0c\u5168\u90e8\u4ea4\u7ed9\u4e86\u4ed6\uff0c\u5979\u5374\u6559\u7ed9\u4ed6\u8bd7\u8bcd\u4e0e\u6b4c \u8d4b\u3002\u201d \u3000\u3000\u88f4\u73cf\u7a81\u7136\u53d1\u73b0\u4e86\u5979\u8bed\u58f0\u4e2d\u6709\u4e86\u5f02\u6837\u7684\u6e29\u67d4\uff0c\u773c\u6ce2\u4e2d\u4e5f\u6709\u4e86\u5f02\u6837\u7684\u5149\u5f69\uff0c\u6b63\u5982\u6bcf\u4e00\u4e2a\u4eba\u56de \u5fc6\u5f80\u65e5\u6b22\u4e50\u65f6\u7684\u9762\u5bb9\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5df2\u7ecf\u77e5\u9053\u5979\u8fd9\u6545\u4e8b\u4e2d\u6240\u53d9\u8bf4\u7684\u4eba\u7269\u662f\u8c01\uff0c\u60c5\u4e0d\u81ea\u7981\u5730\u671b\u4e86\u90a3\u4e24\u5177\u4e92\u76f8\u62e5\u62b1 \u7740\u7684\u5c38\u8eab\u4e00\u773c\uff0c\u5374\u53d1\u73b0\u5979\u7684\u76ee\u5149\uff0c\u4e5f\u5728\u671b\u7740\u90a3\u91cc\u3002 \u3000\u3000\u827e\u9752\u6084\u7136\u671b\u4e86\u51e0\u773c\uff0c\u6781\u5feb\u5730\u56de\u8fc7\u5934\u6765\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4e00\u5bf9\u592b\u59bb\uff0c\u5728\u6b66\u6797\u4e2d\u662f\u6700\u5e78\u798f\u7684\u4e00 \u5bf9\uff0c\u76f4\u5230\u4e00\u5929\u2026\u2026\u4e00\u5929\u665a\u4e0a\u2026\u2026\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u61d4\uff0c\u5df2\u6709\u4e86\u4e0d\u7965\u7684\u9884\u611f\u3002 \u3000\u3000\u53ea\u542c\u827e\u9752\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u90a3\u4e00\u5929\u665a\u4e0a\uff0c\u662f\u591a\u96e8\u7684\u9ec4\u6885\u5929\u6c14\uff0c\u6211\u542c\u7740\u7a97\u5916\u7684\u96e8\u58f0\uff0c\u4e0d\u77e5\u600e \u5730\uff0c\u5fc3\u4e2d\u7adf\u4f3c\u7a81\u5730\u6709\u4e00\u79cd\u4e0d\u7965\u7684\u9884\u611f\u3002\u201d \u3000\u3000\u5979\u7a81\u7136\u53d1\u89c9\u81ea\u5df1\u7ec8\u4e8e\u8bf4\u6f0f\u4e86\u5634\uff0c\u51c4\u7136\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u90a3\u65f6\u6211\u5ac1\u7ed9\u2018\u5343\u624b\u4e66\u751f\u2019\u8427\u4ef2\u5fcd \u5df2\u6709\u4e03\u5e74\uff0c\u4f46\u8fd9\u79cd\u4e0d\u5e78\u7684\u9884\u611f\uff0c\u5374\u662f\u521d\u6b21\u53d1\u751f\uff0c\u6211\u5b88\u5728\u4ed6\u8eab\u8fb9\uff0c\u50cf\u662f\u53c8\u56de\u5230\u7ae5\u5e74\u3002\u201d\u8fd8\u672a\u5230 \u5b50\u591c\uff0c\u4ed6\u8fdc\u5728\u897f\u5317\u8fb9\u9672\u7684\u4e00\u4e2a\u670b\u53cb\u7a81\u7136\u5dee\u4eba\u98de\u9a6c\u8d76\u6765\u544a\u6025\uff0c\u8bf4\u662f\u53d1\u73b0\u4e86\u60ca\u4eba\u7684\u53d8\u6545\uff0c\u5e0c\u671b\u4ed6 \u80fd\u5373\u65f6\u8d76\u53bb\uff0c\u6211\u2026\u2026\u5509\uff0c\u6211\u672c\u6765\u4e5f\u8981\u8ddf\u4ed6\u4e00\u8d77\u53bb\u7684\uff0c\u4f46\u662f\u4ed6\u5374\u5bf9\u6211\u8bf4\uff0c\u6015\u6211\u8eab\u5b50\u4e0d\u8212\u670d\uff0c\u8981 \u6211\u7559\u5728\u5bb6\u91cc\u3002 \u3000\u3000\u4e0d\u51fa\u4e00\u6708\uff0c\u4ed6\u5c31\u4f1a\u56de\u6765\u7684\uff0c\u56e0\u4e3a\u6b66\u6797\u4e2d\u65e0\u8bba\u6709\u4ec0\u4e48\u7ea0\u7eb7\uff0c\u53ea\u8981\u2018\u5343\u624b\u4e66\u751f\uff0c\u4e00\u5230\uff0c\u65e0\u4e0d \u8fce\u5203\u800c\u89e3\u3002\u201c\u6211\u5fc3\u91cc\u5bb3\u6015\uff0c\u4e00\u5b9a\u8981\u8ddf\u7740\u4ed6\u53bb\uff0c\u4ed6\u7b11\u6211\u662f\u5b69\u5b50\u813e\u6c14\u3002\u201d\u5979\u6df1\u6df1\u5730\u5438\u4e86\u53e3\u6c14\uff0c\u50cf \u662f\u751f\u6015\u81ea\u5df1\u4f1a\u7a81\u7136\u7a92\u606f\uff0c\u7136\u540e\u63a5\u7740\u9053\uff1a\u201c\u8fd8\u4e0d\u5230\u4e00\u4e2a\u6708\uff0c\u4ed6\u679c\u7136\u56de\u6765\u4e86\uff0c\u867d\u7136\u770b\u8d77\u6765\u6bd4\u4ee5\u524d \u7626\u524a\u5f97\u591a\uff0c\u4f46\u662f\u7cbe\u795e\u5374\u66f4\u597d\u4e86\uff0c\u6211\u5fc3\u91cc\u5f88\u9ad8\u5174\uff0c\u4f46\u662f\u2026\u2026\u4e0d\u77e5\u600e\u5730\uff0c\u81ea\u4ece\u90a3\u4e00\u5929\u4e4b\u540e\uff0c\u6211\u603b \u662f\u89c9\u5f97\u6709\u4e00\u79cd\u5f02\u5e38\u7684\u6c14\u6c1b\uff0c\u7b3c\u7f69\u5728\u6211\u56db\u5468\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u6e10\u6e10\u6c89\u91cd\uff0c\u6bcf\u5410\u51fa\u4e00\u4e2a\u5b57\uff0c\u90fd\u50cf\u662f\u8d39\u4e86\u8bb8\u591a\u6c14\u529b\u3002 \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u5979\u8bed\u6c14\u4e2d\u4e5f\u50cf\u662f\u6709\u4e86\u4e00\u79cd\u5f02\u5e38\u7684\u6c14\u6c1b\uff0c\u4f7f\u5f97\u4ed6\u7684\u5fc3\u5e95\u6cdb\u8d77\u4e86\u4e00\u9635\u4e0d\u53ef\u6297\u62d2\u7684\u5bd2 \u51b7\u3002 \u3000\u3000\u4ed6\u632f\u4e86\u632f\u8863\u895f\uff0c\u542c\u5979\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u6837\u7684\u65e5\u5b50\uff0c\u4e00\u76f4\u8fc7\u4e86\u4e00\u5e74\uff0c\u6211\u89c9\u5f97\u4e00\u5207\u4e8b\u90fd\u4f3c\u4e4e\u53d8\u4e86 \u6837\u5b50\uff0c\u4f46\u5374\u53c8\u8bf4\u4e0d\u51fa\u539f\u56e0\u6765\uff0c\u8fd9\u4e00\u5e74\u4e2d\uff0c\u6211\u548c\u4ed6\u751a\u81f3\u5f88\u5c11\u8bf4\u8bdd\uff0c\u4ee5\u524d\u8bfb\u4e66\u3001\u5b66\u5251\u7684\u529f\u8bfe\uff0c\u4e5f \u90fd\u505c\u6b62\u4e86\uff0c\u56e0\u4e3a\u4ed6\u8bf4\u4ed6\u53d7\u4e86\u4e00\u70b9\u5185\u4f24\uff0c\u4f46\u662f\u6211\u5374\u53c8\u770b\u4e0d\u51fa\u6765\u3002\u201d\u4e00\u5e74\u8fc7\u53bb\uff0c\u53c8\u5230\u4e86\u9ec4\u6885\u5929 \u6c14\uff0c\u53c8\u5230\u4e86\u4e00\u4e2a\u96e8\u4e1d\u8fde\u7ef5\u7684\u665a\u4e0a\uff0c\u6211\u7761\u4e86\uff0c\u5374\u5728\u4e2d\u591c\u60ca\u9192\uff0c\u6211\u53d1\u89c9\u4ed6\u7b14\u76f4\u5730\u5750\u5728\u5e8a\u8fb9\uff0c\u4f3c\u4e4e \u5728\u671b\u7740\u7a97\u5b50\u51fa\u795e\uff0c\u6211\u6ca1\u6709\u60ca\u52a8\u4ed6\uff1a\u53ea\u662f\u6084\u4fcf\u5f20\u5f00\u773c\u775b\uff0c\u987a\u7740\u4ed6\u7684\u76ee\u5149\u671b\u53bb\u2026\u2026\u201c\u5979\u8bed\u58f0\u7531\u6c89 \u91cd\u800c\u53d8\u5f97\u4e0d\u53ef\u63a9\u9970\u7684\u60ca\u6050\u3001\u98a4\u6296\u800c\u60b2\u5207\u3002\u5979\u98a4\u6296\u7740\u9053\uff1a\u201c\u90a3\u4e00\u773c\u2026\u2026\u90a3\u4e00\u773c\u6240\u89c1\u5230\u7684\u666f\u8c61\uff0c \u6211\u6c38\u751f\u4e5f\u65e0\u6cd5\u5fd8\u6000\uff0c\u6211\u2026\u2026\u6211\u7adf\u5728\u90a3\u7a97\u5b50\u4e0a\uff0c\u770b\u5230\u4e86\u53e6\u4e00\u4e2a\uff0c\u5343\u624b\u4e66\u751f\u201d\u8427\u4ef2\u5fcd\u7684\u773c\u775b\uff0c\u5728 \u5446\u5446\u5730\u51dd\u6ce8\u7740\u6211\uff0c\u6211\u7684\u4e00\u9897\u5fc3\u7acb\u523b\u6d8c\u5411\u7a97\u8d1d\uff0c\u5fcd\u4e0d\u4f4f\u653e\u53e3\u60ca\u547c\u8d77\u6765\u3002\u201c\u88f4\u73cf\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2 \u5664\uff0c\u51e0\u4e4e\u4e0d\u5fcd\u518d\u542c\u4e0b\u53bb\u3002\u4ed6\u5168\u8eab\u4e0a\u4e0b\u7684\u808c\u80a4\uff0c\u5374\u5df2\u5192\u51fa\u4e86\u4e00\u7c92\u7c92\u51b7\u6c57\uff0c\u6084\u4fcf\u62ac\u773c\u671b\u53bb\uff0c\u53ea\u89c1 \u827e\u9752\u7684\u9762\u5bb9\uff0c\u5df2\u9ebb\u6728\u5f97\u6ca1\u6709\u4e00\u4e1d\u60c5\u611f\u3002\u5979\u5c31\u50cf\u662f\u5728\u53d9\u8bf4\u7740\u53e6\u4e00\u4ef6\u4e8b\u4e00\u6837\uff0c\u4f46\u8bed\u58f0\u5374\u4ecd\u4e0d\u7531\u81ea \u4e3b\u5730\u98a4\u6296\u7740\uff1a\u201c\u6211\u4e00\u58f0\u60ca\u547c\u8fc7\u540e\uff0c\u7a97\u5916\u7684\u4eba\u5f71\u7acb\u523b\u5982\u98de\u63a0\u8d70\uff0c\u6211\u5fcd\u4e0d\u4f4f\u4ece\u5e8a\u4e0a\u8df3\u4e86\u8d77\u6765\uff0c\u60f3 \u8ffd\u51fa\u53bb\uff0c\u4f46\u662f\u5750\u5728\u6211\u2026\u2026\u6211\u5e8a\u4fa7\u7684\u2026\u2026\u7684\u4eba\uff0c\u5374\u7a81\u5730\u53cd\u624b\u70b9\u4e2d\u4e86\u6211\u7684\u7a74\u9053\uff0c\u4f7f\u6211\u4e1d\u6beb\u52a8\u5f39\u4e0d \u5f97\uff01\u201d \u3000\u3000\u7a81\u5730\uff0c\u6cb9\u5c3d\u706f\u67af\uff0c\u706b\u5149\u7184\u706d\u5c0f\u4e00\u77ac\u95f4\uff0c\u9634\u68ee\u7684\u6d1e\u7a9f\uff0c\u4fbf\u5168\u88ab\u9ed1\u6697\u7b3c\u7f69\u3002 \u3000\u3000\u5bd2\u610f\u66f4\u91cd\u4e86\uff0c\u9ed1\u6697\u4e2d\uff0c\u4eff\u4f5b\u6709\u65e0\u6570\u4e2a\u9b3c\u602a\u7cbe\u7075\uff0c\u5728\u4f5c\u72c2\u6b22\u7684\u4e71\u821e\u3002 \u3000\u3000\u90fd\u4eff\u5ae9\u662f\u201c\u5343\u624b\u4e66\u751f\u201d\u7684\u5f71\u5b50\u3002 \u3000\u3000\u88f4\u73cf\u4e0d\u81ea\u89c9\u5730\u8737\u66f2\u4e86\u8eab\u8eaf\uff0c\u5728\u8fd9\u9634\u68ee\u9ed1\u6697\u7684\u5730\u65b9\uff0c\u542c\u8fd9\u79cd\u9634\u68ee\u9ed1\u6697\u7684\u6545\u4e8b\uff0c\u672c\u5df2\u8db3\u4ee5\u4ee4 \u4eba\u60b2\u54c0\u60ca\u6817\uff0c\u4f55\u51b5\u8fd9\u6545\u4e8b\u4e2d\u60b2\u60e8\u7684\u4e3b\u4eba\uff0c\u6b64\u523b\u6b63\u5750\u5728\u4ed6\u5bf9\u9762\uff1f\u4ed6\u751a\u81f3\u770b\u5230\u5979\u773c\u4e2d\u7684\u6cea\u6c34\uff0c\u5728 \u9ed1\u6697\u4e2d\u95ea\u70c1\u3002 \u3000\u3000\u53ea\u542c\u5979\u63a5\u7740\u9053\uff1a\u201c\u76f4\u5230\u90a3\u65f6\u4e3a\u6b62\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053\u4ed6\u5144\u5f1f\u7684\u5f80\u4e8b\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u8fd9\u2026\u2026\u8fd9\u5750\u5728 \u6211\u2026\u2026\u5e8a\u8fb9\u2026\u2026\u66fe\u7ecf\u2026\u2026\u548c\u6211\u2026\u2026\u5171\u540c\u751f\u6d3b\u4e86\u4e00\u5e74\u7684\u4eba\uff0c\u7adf\u2026\u2026\u7adf\u4e0d\u662f\u2018\u5343\u624b\u4e66\u751f\u2019\u8427\u4ef2 \u5fcd\uff0c\u800c\u2026\u2026\u800c\u662f\u4ed6\u7684\u2026\u2026\u5f1f\u5f1f\u2026\u2026\u8427\u4f2f\u8d24\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fcd\u4e0d\u4f4f\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9ed1\u6697\u4e2d\u7ec8\u4e8e\u6709\u4e86\u60b2\u6ce3\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5979\u4e5f\u4e0d\u77e5\u54ed\u4e86\u591a\u4e45\uff0c\u65b9\u81ea\u98a4\u58f0\u63a5\u7740\u9053\uff1a\u201c\u90a3\u65f6\u2026\u2026\u6211\u6728\u7136\u50f5\u5367\u5728\u5e8a\uff0c\u542c\u7740\u8427\u4f2f\u8d24\u5728\u6211\u8eab \u8fb9\uff0c\u8bf4\u51fa\u4e86\u6574\u4e2a\u6545\u4e8b\uff0c\u4ed6\u5760\u4e0b\u60ac\u5d16\u540e\uff0c\u7adf\u7136\u4e5f\u6ca1\u6709\u6b7b\uff0c\u5728\u5c1d\u53d7\u4e86\u8bb8\u591a\u82e6\u96be\u4e4b\u540e\uff0c\u7adf\u7136\u4e5f\u5b66\u5f97 \u4e86\u4e00\u8eab\u7edd\u6280\uff0c\u7adf\u56de\u5230\u4eba\u95f4\u6765\u590d\u4ec7\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u60b2\u5636\u7740\u9053\uff1a\u201c\u6211\u5374\u662f\u65e0\u8f9c\u7684\u5440\uff0c\u6211\u53c8\u6709\u4ec0\u4e48\u7f6a\u5b7d\uff0c\u8981\u53d7\u5230\u8fd9\u79cd\u975e\u4eba\u53ef\u4ee5\u5fcd\u53d7\u7684\u4fae\u8fb1\u4e0e \u75db\u82e6\uff1f\u201d\u6211\u542c\u7740\u4ed6\u5728\u8eab\u8fb9\uff0c\u72de\u7b11\u7740\u544a\u8bc9\u6211\uff1a\u2018\u4f60\u662f\u4ed6\u5fc3\u7518\u60c5\u613f\u5730\u8ba9\u7ed9\u6211\u7684\uff0c\u56e0\u4e3a\u4ed6\u81ea\u89c9\u5bf9\u6211 \u4e0d\u8d77\uff0c\u4eca\u5929\uff0c\u6211\u4e0d\u8fc7\u662f\u8ba9\u4ed6\u6765\u770b\u4f60\u4e00\u773c\uff0c\u540e\u4f1a\u6709\u671f\uff0c\u4f60\u5df2\u662f\u8427\u4f2f\u8d24\u7684\u59bb\u5b50\uff0c\u4f60\u4e0d\u4f46\u8981\u8ddf\u6211\u4e00 \u5e74\uff0c\u4f60\u8fd8\u8981\u8ddf\u6211\u4e00\u751f\u3002\u2019\u201c\u201d\u5440\u2026\u2026\u201c\u5979\u7edd\u671b\u5730\u54c0\u547c\u4e00\u58f0\uff0c\u8fd9\u4e00\u58f0\u54c0\u547c\uff0c\u4eff\u4f5b\u662f\u4e00\u6839\u5f2f\u66f2\u7684 \u9488\uff0c\u523a\u5165\u88f4\u73cf\u7684\u795e\u7ecf\uff0c\u4f7f\u5f97\u4ed6\u5168\u8eab\u90fd\u7c0c\u7c0c\u5730\u53d1\u8d77\u6296\u6765\uff0c\u7259\u9f7f\u4e5f\u6296\u5f97\u54af\u54af\u4f5c\u54cd\u3002\u9ed1\u6697\u4e2d\u90a3\u60e8\u7edd \u4eba\u5bf0\u7684\u53d9\u8ff0\u4ecd\u5728\u7ee7\u7eed\u7740\uff1a\u201c\u4f60\u2026\u2026\u4f60\u60f3\u60f3\uff0c\u6211\u2026\u2026\u966a\u7740\u4e00\u4e2a\u964c\u751f\u4eba\u7761\u4e86\u4e00\u5e74\uff0c\u5374\u2026\u2026\u59cb\u7ec8\u8ba4 \u4e3a\u4ed6\u662f\u6211\u4e08\u592b\u2026\u2026\u201d\u6211\u542c\u7740\u4ed6\u7684\u8bdd\uff0c\u5fc3\u91cc\u7a81\u5730\u8d77\u4e86\u4e00\u79cd\u75db\u6068\uff0c\u4e00\u79cd\u65e0\u6bd4\u5f3a\u70c8\u7684\u75db\u6068\uff0c\u6211\u6068\u4ed6 \u4eec\u5144\u5f1f\u4e24\u4eba\uff0c\u6211\u53d1\u8a93\u8981\u7ec3\u6210\u66f4\u9ad8\u6df1\u7684\u6b66\u529f\uff0c\u5c06\u4ed6\u4eec\u5144\u5f1f\u4e24\u4eba\u4e00\u8d77\u6740\u6b7b\u3002 \u3000\u3000\u201c\u5c31\u662f\u8fd9\u79cd\u4ec7\u6068\u652f\u6301\u7740\u6211\uff0c\u6211\u90a3\u65f6\u624d\u6ca1\u6709\u6b7b\u5728\u4ed6\u9762\u524d\u3002\u201d\u81ea\u4ece\u90a3\u4e00\u5929\u4e4b\u540e\uff0c\u8427\u4f2f\u8d24\u7adf\u4e00 \u76f4\u6ca1\u6709\u89e3\u5f00\u6211\u7684\u7a74\u9053\uff0c\u4ed6\u70b9\u4e86\u6211\u8eab\u4e0a\u6c14\u8840\u76f8\u901a\u7684\u4e09\u5904\u7a74\u9053\uff0c\u4f7f\u5f97\u6211\u867d\u80fd\u884c\u8d70\uff0c\u5374\u9003\u4e0d\u5f00\u4ed6\u7684 \u638c\u63e1\u3002 \u3000\u3000\u201c\u8fd9\u6837\uff0c\u7adf\u2026\u7adf\u7136\u53c8\u2026\u2026\u8fc7\u4e86\u4e00\u5e74\uff0c\u8fd9\u4e00\u5e74\u91cc\uff0c\u6211\u2026\u2026\u5fcd\u53d7\u7684\u4fae\u8fb1\u4e0e\u75db\u82e6\uff0c\u4e16\u4e0a\u6ca1\u6709\u4e00 \u4e2a\u4eba\u80fd\u591f\u60f3\u8c61\u5f97\u51fa\u3002\u201d\u8427\u6015\u8d24\u4e0d\u505c\u5730\u5728\u6211\u8eab\u8fb9\u4fae\u8fb1\u7740\u6211\uff0c\u53c8\u4e0d\u65f6\u5728\u6b66\u6797\u4e2d\u505a\u4e00\u4e9b\u4ee4\u4eba\u53d1\u6307\u7684 \u6076\u884c\uff0c\u4f7f\u5f97\u2018\u5343\u624b\u4e66\u751f\u2019\u5728\u6c5f\u6e56\u4e0a\u6210\u4e86\u4e00\u4e2a\u5ffd\u5584\u5ffd\u6076\u7684\u602a\u7269\u3002\u201c\u201d\u8fd9\u4e00\u5e74\u4e2d\uff0c\u6211\u53c8\u53d1\u73b0\uff0c\u4ed6 \u65e9\u5df2\u8ddf\u8e2a\u7740\u6211\u4eec\uff0c\u76f4\u5230\u8427\u4ef2\u5fcd\u8001\u4e86\uff0c\u4ed6\u4fbf\u6709\u8ba1\u5212\u5730\u6765\u5360\u6709\u4e86\u6211\u3002 \u3000\u3000\u201c\u8427\u4ef2\u5fcd\u56de\u6765\u7684\u65f6\u5019\uff0c\u89c1\u5230\u4e86\u8fd9\u60c5\u51b5\uff0c\u4e0d\u5fcd\u4f24\u6211\u7684\u5fc3\uff0c\u53ea\u5f97\u6084\u4fcf\u907f\u5f00\u4e86\uff0c\u4ed6\u4e3a\u4e86\u5bf9\u5f1f\u5f1f \u7684\u6b49\u759a\uff0c\u5374\u5c06\u8671\u727a\u7272\u4e86\uff01\u6211\u2026\u2026\u6211\u7adf\u505a\u4e86\u4ed6\u4eec\u5144\u5f1f\u7f6a\u6076\u7684\u727a\u7272\u8005\uff0c\u6211\u2026\u2026\u66f4\u6068\u4ed6\u4eec\uff01\u201d \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff0c\u604d\u7136\u4e86\u89e3\u4e86\uff0c\u5979\u8981\u81ea\u5df1\u505a\u7684\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u539f\u6765\u8fd8\u6709\u8fd9\u6837\u4e00\u4e2a\u75db\u82e6\u800c\u590d\u6742\u7684 \u539f\u56e0\u3002\u4ed6\u8f7b\u8f7b\u52a8\u4e86\u4e00\u4e0b\u8eab\u8eaf\uff0c\u5bf8\u53d1\u89c9\u81ea\u5df1\u5168\u8eab\u7684\u8863\u886b\uff0c\u5df2\u5168\u88ab\u51b7\u6c57\u6e7f\u900f\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u4e00\u6478\u53cc\u988a\uff0c\u624d\u53d1\u89c9\u81ea\u5df1\u9762\u4e0a\uff0c\u65e9\u5df2\u5e03\u6ee1\u4e86\u540c\u60c5\u7684\u6cea\u75d5\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u751a\u81f3\u5728\u6697\u4e2d\u611f\u8c22\u8fd9\u9ed1\u6697\u7684\u6765\u4e34\uff0c\u56e0\u4e3a\u4ed6\u5b9e\u5728\u4e0d\u5fcd\u518d\u89c1\u5230\u9762\u524d\u8fd9\u88ab\u4fae\u8fb1\u4e0e\u635f\u5bb3\u4e86 \u7684\u5973\u5b50\u7684\u9762\u5bb9\u3002 \u3000\u3000\u4e00\u9635\u6c89\u9ed8\uff0c\u7ec8\u4e8e\u53c8\u6709\u4e86\u58f0\u97f3\uff1a\u201c\u540e\u6765\u2026\u2026\u8427\u4f2f\u8d24\u6e10\u6e10\u758f\u5ffd\u4e86\uff0c\u6211\u60f3\u5c3d\u65b9\u6cd5\uff0c\u89e3\u5f00\u4e86\u7a74 \u9053\uff0c\u6109\u4e86\u90a3\u672c\u2018\u6d77\u5929\u79d8\u7b08\u2019\uff0c\u4ea1\u547d\u4e00\u822c\u9003\u4e86\u51fa\u6765\u3002\u201d\u6211\u4e0d\u6562\u5230\u6df1\u5c71\u4e2d\u53bb\uff0c\u56e0\u4e3a\u6211\u6015\u4ed6\u5bfb\u7740 \u6211\uff0c\u6211\u53ea\u6709\u5973\u626e\u7537\u88c5\uff0c\u9690\u85cf\u5728\u4eba\u7fa4\u91cc\u2026\u2026\u6240\u4ee5\u6211\u624d\u4f1a\u9047\u7740\u4f60\u3002 \u3000\u3000\u201c\u6211\u53c8\u5c06\u90a3\u2018\u6d77\u5929\u79d8\u7b08\u2019\u7684\u5c01\u9762\u62c6\u4e0b\uff0c\u505a\u4e86\u4e24\u672c\u5047\u7684\uff0c\u653e\u5728\u5305\u88b1\u91cc\uff0c\u540c\u65e5\u591c\u591c\u5730\u52e4\u7ec3\u6b66 \u529f\u3002\u201d\u4f46\u662f\uff0c\u6211\u7ec8\u4e8e\u88ab\u4ed6\u627e\u7740\u4e86\uff0c\u90a3\u5929\u665a\u4e0a\uff0c\u6211\u6740\u4e86\u2018\u5317\u6597\u4e03\u715e\u2019\u4e2d\u7684\u83ab\u897f\uff0c\u5c31\u88ab\u4ed6\u6349\u4f4f\uff0c \u4ed6\u767e\u822c\u5730\u5bf9\u6211\u5632\u7b11\uff0c\u4ee5\u4e3a\u2026\u2026\u4ee5\u4e3a\u2026\u2026\u5509\uff0c\u90a3\u65f6\u6211\u4ee5\u4e3a\u4ed6\u4f1a\u6740\u4e86\u6211\uff0c\u54ea\u77e5\u4ed6\u7b11\u6211\uff0c\u9a82\u6211\u4e4b \u540e\uff0c\u53c8\u8dea\u5728\u5730\u4e0a\u6c42\u6211\uff0c\u6c42\u6211\u4e0d\u8981\u79bb\u5f00\u4ed6\uff01 \u3000\u3000\u201c\u4ed6\u2026\u2026\u4ed6\u7adf\u50cf\u75af\u4e86\u4f3c\u7684\uff0c\u4e00\u4f1a\u513f\u5c06\u6211\u7d27\u7d27\u6346\u8d77\u6765\uff0c\u4e00\u4f1a\u53c8\u89e3\u5f00\u4e86\u6211\uff0c\u4ed6\u65e5\u65e5\u591c\u591c\u3001\u65f6 \u65f6\u523b\u523b\u5730\u5b88\u5728\u6211\u8eab\u8fb9\uff0c\u5341\u5929\u5341\u591c\uff0c\u7adf\u6ca1\u6709\u5408\u4e00\u5408\u773c\u775b\u3002\u201d\u5230\u540e\u6765\uff0c\u4ed6\u7ec8\u4e8e\u75b2\u5026\u4e86\uff0c\u6211\u624d\u9003\u4e86 \u51fa\u6765\uff0c\u4f46\u662f\u4ed6\u5374\u50cf\u6076\u9b54\u4e00\u6837\uff0c\u603b\u662f\u80fd\u627e\u7740\u6211\uff0c\u6211\u4ea1\u547d\u5730\u9003\uff0c\u5374\u603b\u662f\u9003\u4e0d\u5f00\u4ed6\u7684\u9634\u5f71\u3002\u201c\u9ed1\u6697 \u4e2d\u53d1\u51fa\u4e00\u58f0\u65e0\u6bd4\u6c89\u91cd\u7684\u53f9\u606f\u3002\u3001\u5979\u957f\u53f9\u7740\u9053\uff1a\u201c\u6211\u7ec8\u4e8e\u538c\u5026\u4e86\uff0c\u800c\u4e14\u6211\u5ffd\u7136\u53d1\u73b0\uff0c\u6211\u7eb5\u7136\u518d \u7ec3\u5341\u5e74\u3001\u767e\u5e74\uff0c\u6211\u7684\u6b66\u529f\u4ecd\u7136\u65e0\u6cd5\u80dc\u8fc7\u4ed6\u4eec\u3001\u201d\u6709\u4e00\u5929\uff0c\u6211\u9047\u5230\u4e86\u2018\u91d1\u7ae5\u7389\u5973\u2019\u592b\u5987\u4e24\u4eba\uff0c \u4ed6\u4eec\u544a\u8bc9\u6211\u4e00\u4e2a\u91cd\u5927\u5927\u7684\u6d88\u606f\uff0c\u8bf4\u662f\u53d1\u73b0\u4e86\u2018\u5343\u624b\u4e66\u751f\u2019\u7684\u884c\u8ff9\uff0c\u9690\u85cf\u5728\u9ec4\u5c71\u2018\u59cb\u4fe1\u5cf0\uff0c\u7684 \u4e00\u5904\u79d8\u7a9f\u91cc\uff0c\u6211\u624d\u77e5\u9053\u8427\u4ef2\u5fcd\u79bb\u5f00\u6211\u540e\uff0c\u539f\u6765\u662f\u8eb2\u5728\u8fd9\u91cc\u3002\u201c\u4ed6\u4eec\u592b\u5987\u4e24\u4eba\uff0c\u662f\u6211\u6700\u597d\u7684\u670b \u53cb\uff0c\u4ed6\u4eec\u5173\u5fc3\u6211\uff0c\u4f46\u662f\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u80fd\u89e3\u51b3\u6211\u7684\u75db\u82e6\u3002\u201d\u6211\u8003\u8651\u4e86\u8bb8\u4e45\uff0c\u51b3\u5b9a\u8981\u5230\u9ec4\u5c71\u6765\u5bfb\u8427 \u4ef2\u5fcd\uff0c\u4e8e\u662f\u6211\u5c31\u5c06\u90a3\u8ba2\u5728\u4e00\u8d77\u7684\u4e00\u672c\u771f\u7684\u2019\u6d77\u5929\u79d8\u7b08\u2018\uff0c\u6258\u4ed6\u4eec\u4ea4\u7ed9\u4f60\u3002\u201c\u88f4\u73cf\u81ea\u79ef\u90c1\u5728\u5fc3 \u4e2d\u4e4b\u540c\u60c5\u4e0e\u60b2\u54c0\u7684\u7a7a\u9699\u4e2d\uff0c\u900f\u51fa\u4e00\u53e3\u957f\u6c14\uff0c\u76f4\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u4ed6\u624d\u77e5\u9053\u88ab\u5b59\u6c0f\u7236\u5b50\u62a2\u53bb\u7684\u4e24 \u672c\u201d\u6d77\u5929\u79d8\u7b08\u201c\uff0c\u539f\u6765\u662f\u5047\u7684\uff0c\u4ed6\u4e5f\u77e5\u9053\u90a3\u672c\u4e00\u76f4\u653e\u5728\u81ea\u5df1\u6000\u4e2d\u7684\u4e66\u518c\uff0c\u7adf\u662f\u540d\u9707\u5929\u4e0b\u7684\u6b66 \u6797\u79d8\u7b08\u3002\u201d\u51b7\u6708\u4ed9\u5b50\u201c\u827e\u9752\u63a5\u7740\u9053\uff1a\u201c\u6211\u4ea4\u5f85\u4e86\u4e00\u5207\uff0c\u4fbf\u5230\u4e86\u9ec4\u5c71\uff0c\u627e\u5230\u4e86\u8fd9\u79d8\u7a9f\uff0c\u90a3\u65f6\u8427 \u4ef2\u5fcd\u5374\u8fd8\u6ca1\u6709\u56de\u6765\uff0c\u6211\u5728\u8fd9\u91cc\u7b49\u7740\u4ed6\uff0c\u7b49\u4e86\u4e00\u5929\u3002\u201d\u8427\u4ef2\u5fcd\u4e00\u5165\u79d8\u7a9f\uff0c\u4fbf\u770b\u89c1\u6211\u6728\u7136\u7ad9\u5728\u4ed6 \u9762\u524d\uff0c\u4ed6\u60ca\u547c\u4e00\u58f0\uff0c\u8fde\u624b\u4e2d\u7684\u5323\u5b50\uff0c\u90fd\u8dcc\u5230\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u201c\u6211\u4e00\u628a\u62c9\u4f4f\u4ed6\uff0c\u671b\u89c1\u4e86\u4ed6\uff0c\u6211\u624d\u53d1\u89c9\u6211\u867d\u7136\u6068\u4ed6\uff0c\u5374\u4e5f\u662f\u7231\u7740\u4ed6\u7684\uff0c\u6211\u54ed\u7740\u95ee\u4ed6\uff0c\u4ed6 \u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u5f85\u6211\uff1f\u201d\u54ea\u77e5\u4ed6\u7a81\u7136\u5927\u7b11\u4e86\u8d77\u6765\uff01\u539f\u6765\u2026\u2026\u539f\u6765\u6211\u53c8\u8ba4\u9519\u4e86\uff0c\u4ed6\u2026\u2026\u4ed6\u7adf\u7136\u4e0d \u662f\u8427\u4ef2\u5fcd\uff0c\u800c\u662f\u8427\u4f2f\u8d24\u3002 \u3000\u3000\u201c\u6211\u60ca\u6016\u5730\u72c2\u53eb\u8d77\u6765\uff0c\u5c31\u5728\u8fd9\u65f6\u8427\u4ef2\u5fcd\u7ec8\u4e8e\u56de\u6765\u4e86\u3002\u201d\u4ed6\u4eec\u4e24\u4eba\u4e00\u8d77\u51fa\u73b0\u5728\u6211\u7684\u773c\u524d\uff0c \u4e92\u76f8\u51dd\u89c6\u7740\uff0c\u6570\u5341\u5e74\u6765\u7ea0\u7f20\u7740\u7684\u6069\u6028\uff0c\u4f7f\u5f97\u4ed6\u4eec\u4e24\u4eba\u7684\u773c\u775b\u4e2d\u90fd\u50cf\u662f\u8981\u5192\u51fa\u706b\u6765\u3002 \u3000\u3000\u201c\u7136\u540e\uff0c\u4ed6\u4eec\u4e24\u4eba\u4e00\u8d77\u671b\u7740\u6211\uff0c\u6211\u4e0d\u81ea\u89c9\u5730\u9000\u7f29\u4e86\u3002\u4e00\u76f4\u8ffd\u5230\u51b0\u51b7\u7684\u5c71\u58c1\u4e0a\uff0c\u8427\u4f2f\u8d24\u7a81 \u7136\u8bf4\uff1a\u2018\u8fd9\u4e16\u754c\u592a\u6324\u4e86\uff0c\u4f60\u6211\u4e24\u4eba\u4e4b\u4e2d\uff0c\u603b\u6709\u4e00\u4eba\u8be5\u9000\u51fa\u53bb\u3002\u2019\u8427\u4ef2\u5fcd\u5446\u4e86\u8bb8\u4e45\uff0c\u4e5f\u6c89\u58f0 \u9053\uff1a\u2018\u8fd9\u4e16\u754c\u7684\u786e\u5927\u5c0f\u4e86\u3002\u2019 \u3000\u3000\u201c\u4e8e\u662f\u4ed6\u4eec\u4e24\u4eba\u4e00\u8d77\u62d4\u51fa\u5251\u6765\uff0c\u5509\u2026\u2026\u9020\u5316\u7684\u5f04\u4eba\uff0c\u6709\u65f6\u7684\u786e\u5927\u6b8b\u9177\u4e86\u4e9b\uff0c\u4ed6\u4eec\u4e24\u4eba\u7684 \u795e\u6001\u3001\u4e3e\u6b62\u8a00\u8bed\uff0c\u7adf\u7136\u662f\u90a3\u4e48\u76f8\u50cf\uff0c\u6211\u770b\u7740\u4ed6\u4eec\u4e24\u4eba\u52a8\u8d77\u624b\u6765\uff0c\u7a81\u7136\u53d1\u89c9\u6211\u5bf9\u8fd9\u5144\u5f1f\u4e24\u4eba\u7684 \u5173\u5207\uff0c\u7adf\u662f\u4e00\u6a21\u4e00\u6837\uff01\u201d\u8fd9\u5ff5\u5934\u51e0\u4e4e\u4f7f\u6211\u53d1\u89c9\uff0c\u4f46\u5374\u662f\u4e8b\u5b9e\uff0c\u6b8b\u9177\u5730\u903c\u7740\u6211\uff0c\u4e0d\u5bb9\u6211\u9003\u907f\uff0c \u6211\u2026\u2026\u6211\u5f00\u59cb\u54c0\u6c42\u4ed6\u4eec\uff0c\u8981\u4ed6\u4eec\u4e0d\u8981\u52a8\u624b\u4e86\u3002 \u3000\u3000\u201c\u6211\u54ed\u558a\u7740\uff0c\u54c0\u6c42\u7740\uff01\u4f46\u662f\u4ed6\u4eec\u5374\u50cf\u6839\u672c\u6ca1\u6709\u542c\u5230\uff0c\u4ed6\u4eec\u5c31\u5728\u8fd9\u72ed\u7a84\u7684\u5730\u9053\u91cc\uff0c\u53ae\u6740\u4e86 \u4e00\u591c\uff0c\u4ed6\u4eec\u7684\u8eab\u4e0a\uff0c\u90fd\u53d7\u4e86\u4f24\uff0c\u6d41\u4e86\u8840\uff0c\u5509\u2026\u2026\u82cd\u5929\u7adf\u53c8\u5c06\u4ed6\u4eec\u4e24\u4eba\u7684\u6b66\u529f\u5b89\u6392\u6210\u4e00\u6a21\u4e00 \u6837\u3002\u201d \u3000\u3000\u88f4\u73cf\u53cd\u624b\u4e00\u62b9\u989d\u4e0a\u6c57\u73e0\uff0c\u4ed6\u82e5\u975e\u81ea\u5df1\u4eb2\u773c\u76ee\u7779\uff0c\u5426\u5219\u4ed6\u771f\u4e0d\u6562\u76f8\u4fe1\u8fd9\u51c4\u60e8\u800c\u79bb\u5947\u7684\u6545\u4e8b \u7adf\u662f\u771f\u7684\u3002 \u3000\u3000\u5916\u9762\uff0c\u5929\u4f3c\u5df2\u9ece\u660e\u4e86\uff0c\u7531\u90a3\u88c2\u9699\u4e2d\u5c04\u5165\u7684\u5fae\u5149\uff0c\u4f7f\u5f97\u4ed6\u5df2\u80fd\u819d\u9647\u5730\u770b\u5230\u827e\u9752\u7684\u8eab\u5f62\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u4e0d\u6562\u53bb\u770b\uff0c\u4ed6\u53ea\u662f\u5782\u7740\u5934\uff0c\u542c\u5979\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u540e\u6765\uff0c\u4ed6\u4eec\u7ade\u820d\u5f03\u4e86\u5251\u6cd5\u7684\u6bd4 \u6597\uff0c\u800c\u60f3\u51fa\u4e86\u8fd9\u4e0d\u6b7b\u4e0d\u4f11\u7684\u6bd4\u8bd5\u65b9\u6cd5\uff0c\u6211\u66f4\u60ca\u614c\u4e86\uff0c\u867d\u7136\u6211\u4e5f\u77e5\u9053\uff0c\u4ed6\u4eec\u4e24\u4eba\u82e5\u662f\u540c\u65f6\u6d3b\u5728 \u4e16\u4e0a\uff0c\u90a3\u4e48\u60b2\u5267\u662f\u6c38\u8fdc\u4e0d\u4f1a\u7ed3\u675f\uff0c\u56f0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211\u2026\u2026\u6211\u7231\u7740\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u7231\u7740\u6211\uff01\u201d\u4f46 \u662f\uff0c\u6211\u4ecd\u7136\u4e0d\u5fcd\u89c1\u5230\u4ed6\u4eec\u7684\u6b7b\u4ea1\uff0c\u6211\u4ee5\u8fd9\u96ea\u4eae\u7684\u94a2\u9488\uff0c\u4e00\u9488\u4e00\u9488\u5730\u523a\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u5e0c\u671b\u4ed6\u4eec \u80fd\u4e3a\u6211\u75db\u82e6\u800c\u4f4f\u624b\u3002\u201c\u201d\u4f46\u662f\u4ed6\u4eec\u5374\u4ecd\u7136\u50cf\u662f\u6ca1\u6709\u770b\u5230\uff01\u201c\u8bed\u58f0\u987f\u5904\uff0c\u4f59\u97f3\u8885\u8885\u3002\u7ec8\u4e8e\uff0c\u56db \u4e0b\u53d8\u5f97\u6b7b\u4e00\u822c\u7684\u9759\u5bc2\u3002\u88f4\u73cf\u6728\u7136\u50f5\u5750\u7740\uff0c\u601d\u6f6e\u5374\u4f3c\u4e4e\u505c\u6b62\u4e86\u8f6c\u52a8\u3002\u826f\u4e45\u826f\u4e45\uff0c\u53ea\u542c\u5979\u5e7d\u5e7d\u957f \u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u60b2\u5267\uff0c\u7ec8\u4e8e\u7ed3\u675f\u4e86\uff01\u6545\u4e8b\uff0c\u4e5f\u7ed3\u675f\u4e86\uff01\u4ed6\u4eec\u5144\u5f1f\uff0c\u7ec8\u4e8e\u89e3\u5f00\u4e86\u7ea0\u7f20\u7684\u6069 \u6028\u60c5\u4ec7\uff0c\u800c\u6211\u5462\uff1f\u201d \u3000\u3000\u5979\u7a81\u5730\u8f7b\u7b11\u4e86\u4e00\u58f0\uff0c\u7b11\u58f0\u4e2d\u63ba\u63c9\u7740\u7684\u90a3\u79cd\u5bf9\u751f\u547d\u7684\u8ba5\u5632\u4e0e\u60b2\u5207\uff0c\u4f7f\u5f97\u8fd9\u7b11\u58f0\u542c\u672a\u6709\u5982\u66ae \u6625\u675c\u9e43\u7684\u557c\u8840\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u63a5\u7740\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u95ee\u4f60\uff0c\u6211\u662f\u5426\u8be5\u7ee7\u7eed\u6d3b\u4e0b\u53bb\uff1f\u6211\u80fd\u7ee7\u7eed\u6d3b\u4e0b\u53bb\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5168\u8eab\u4e00\u98a4\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u827e\u9752\u4e00\u53f9\u622a\u53e3\u9053\uff1a\u201c\u6211\u8981\u4f60\u4e3a\u6211\u505a\u7684\u7b2c\u4e09\u4ef6\u4e8b\uff0c\u4fbf\u662f\u7b49\u6211\u6b7b\u540e\uff0c\u4f60\u518d\u5c06\u6211\u4eec\u4e09\u4eba\u7684\u5c38 \u8eab\uff0c\u846c\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u79ef\u538b\u5728\u88f4\u73cf\u5fc3\u4e2d\u7684\u60b2\u54c0\uff0c\u6b64\u523b\u7a81\u5730\u4e00\u8d77\u7ffb\u6d8c\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u60b2\u54c0\u5730\u5927\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u6b7b\uff01\u201d\u3001\u827e\u9752\u51c4\u7136\u4e00\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u5fd8\u4e86\u4f60\u65b9\u624d\u66fe\u7ecf\u7b54\u5e94\u6211 \u7684\u8bdd\u4e48\uff1f\u4f55\u51b5\u2026\u2026\u4ee5\u4f60\u7684\u529b\u91cf\uff0c\u4f60\u53c8\u600e\u80fd\u963b\u6b62\u6211\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6014\u4e86\u534a\u664c\uff0c\u4e24\u6ef4\u6cea\u73e0\u593a\u7736\u800c\u51fa\uff0c\u773c\u524d\u8fd9\u819d\u9647\u7684\u60c5\u5f71\uff0c\u53d8\u5f97\u66f4\u52a0\u6a21\u7cca\uff0c\u4ed6\u60b2\u6ce3\u7740\u9053\uff1a \u201c\u4f46\u662f\u2026\u2026\u4f46\u662f\u2026\u2026\u201d \u3000\u3000\u827e\u9752\u53f9\u9053\uff1a\u201c\u4f46\u662f\u6211\u73b0\u5728\u8fd8\u4e0d\u4f1a\u6b7b\uff0c\u6211\u8981\u4ee5\u6211\u4ec5\u5b58\u7684\u4e00\u70b9\u529b\u91cf\uff0c\u4e3a\u4f60\u505a\u4e00\u4e9b\u4e8b\uff0c\u4e09 \u5929\u2026\u2026\u4e09\u5929\u4e4b\u540e\uff0c\u8c01\u4e5f\u4e0d\u80fd\u963b\u6b62\u6211\u2026\u2026\u53bb\u6b7b\uff0c\u4e09\u5929\u2026\u2026\u201d \u3000\u3000\u5979\u5583\u5583\u5730\u4f4e\u8bed\u7740\uff0c\u53c8\u81ea\u8f6c\u8fc7\u8eab\u53bb\uff0c\u671b\u5411\u90a3\u4e00\u53cc\u9760\u5408\u7740\u7684\u4eba\u5f71\uff01\u5509\uff0c\u547d\u8fd0\uff01\u547d\u8fd0\u5bf9\u5979\u7684\u786e \u592a\u6b8b\u9177\u4e86\u4e9b\uff0c\u7adf\u4f7f\u5979\u5bf9\u751f\u547d\u5df2\u4e00\u65e0\u4f9d\u604b\uff01 \u3000\u3000\u88f4\u73cf\u6728\u7136\u6123\u4e86\u534a\u664c\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4e09\u5929\u2026\u2026\u4e09\u5929\u540e\uff0c\u6211\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u5f97\u963b\u6b62\u5979\u81ea\u5df1\u6765\u4f24 \u5bb3\u81ea\u5df1\u7684\u6027\u547d\uff01\u7eb5\u7136\u6211\u8981\u8fdd\u80cc\u6211\u7684\u8a93\u8a00\uff0c\u7eb5\u7136\u6211\u8981\u88ab\u5929\u6253\u96f7\u51fb\uff0c\u4f46\u662f\u6211\u4e5f\u8981\u6551\u5979\u4e00\u547d\uff0c\u6211\u8fd8 \u8981\u5e2e\u52a9\u5979\uff0c\u8ba9\u5979\u53bb\u5bfb\u627e\u53e6\u4e00\u79cd\u751f\u547d\u7684\u610f\u4e49\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u65b9\u8f6c\uff0c\u7a81\u89c1\u827e\u9752\u957f\u8eab\u800c\u8d77\uff0c\u5979\u6726\u80e7\u7684\u8eab\u5f62\u5fae\u5fae\u4e00\u6447\uff0c\u4e00\u53cc\u7ea4\u638c\uff0c\u4fbf\u5df2\u95ea\u7535\u822c\u51fb\u5728\u88f4 \u73cf\u8eab\u4e0a\u3002 \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u8033\u7554\u55e1\u7136\u4e00\u54cd\uff0c\u4e00\u9053\u70bd\u70ed\u7684\u706b\u7130\uff0c\u5df2\u7a7f\u5165\u4ed6\u5fc3\u91cc\u3002 \u3000\u3000\u7136\u540e\u706b\u7130\u6e10\u6e10\u6269\u6563\uff0c\u7531\u4ed6\u7684\u5fc3\uff0c\u904d\u8eab\u5230\u80a9\u3001\u81c2\u3001\u80a1\u3001\u8154\u2026\u2026 \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u7684\u56db\u80a2\u767e\u9ab8\uff0c\u90fd\u50cf\u662f\u5df2\u7ecf\u71c3\u70e7\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u6655\u8ff7\u800c\u65e0\u52a9\u5730\u4efb\u51ed\u8fd9\u706b\u7130\u71c3\u70e7\u7740\uff0c\u4e00\u79cd\u4f3c\u662f\u6495\u88c2\u822c\u7684\u75db\u82e6\uff0c\u4f7f\u5f97\u4ed6\u4e0d\u80fd\u5fcd\u53d7\u5730\u53d1\u51fa\u547b \u541f\u4e4b\u58f0\u3002 \u3000\u3000\u75db\u82e6\u7ee7\u7eed\u7740\uff0c\u4eff\u4f5b\u5343\u767e\u5e74\u90a3\u822c\u6f2b\u957f\u3002 \u3000\u3000\u7136\u540e\uff0c\u706b\u7130\u7a81\u5730\u7184\u706d\uff0c\u4ed6\u56db\u80a2\u762b\u6563\u5730\u4f38\u5f20\u5728\u56db\u8fb9\uff0c\u53ea\u89c9\u6709\u4e00\u4e2a\u6e29\u51c9\u7684\u8eaf\u4f53\uff0c\u7d27\u4f9d\u5728\u4ed6\u6000 \u4e2d\u3002 \u3000\u3000\u75db\u82e6\u8fc7\u540e\uff0c\u7adf\u662f\u4e00\u9635\u65e0\u6cd5\u5f62\u5bb9\u7684\u8212\u9002\uff0c\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u7a81\u7136\u4e71\u4e86\uff0c\u6240\u6709\u4e00\u5207\u4ed6\u4ece\u672a\u6562\u60f3\u7684\u6deb \u6076\u5ff5\u5934\uff0c\u7adf\u4e00\u8d77\u5728\u4ed6\u5fc3\u4e2d\u6d8c\u8d77\u3002 \u3000\u3000\u4ed6\u8270\u82e6\u5730\u514b\u5236\u7740\uff0c\u7136\u540e\uff0c\u53c8\u662f\u4e00\u9635\u706b\u7130\u822c\u7684\u71c3\u70e7\uff01 \u3000\u3000\u53c8\u662f\u5343\u767e\u5e74\u7684\u6f2b\u957f\u7684\u75db\u6614\uff01 \u3000\u3000\u4ed6\u547b\u541f\u7740\uff0c\u7ffb\u6eda\u7740\uff0c\u7a81\u5730\uff0c\u4e00\u9635\u5e73\u9759\u50cf\u95ea\u7535\u822c\u5230\u6765\uff0c\u4ed6\u75b2\u5026\u5730\u5012\u5367\u7740\uff0c\u534a\u664c\uff0c\u4ed6\u7a81\u7136\u89c9 \u5f97\u9965\u6e34\u2014\u2014\u4e0d\u53ef\u5fcd\u53d7\u7684\u9965\u6e34\uff0c\u4ed6\u751a\u81f3\u5b81\u613f\u4ee5\u81ea\u5df1\u7684\u751f\u6b7b\u53bb\u6362\u53d6\u4e00\u676f\u6e05\u6c34\u6216\u662f\u4e00\u4e9b\u98df\u7269\u3002 \u3000\u3000\u865a\u7a7a\u2026\u2026\u4ed6\u89c9\u5f97\u81ea\u5df1\u50cf\u662f\u5df2\u8981\u88ab\u98ce\u5439\u4e86\u8d77\u6765\uff0c\u6240\u6709\u7684\u7cbe\u529b\u4e0e\u8840\u8089\uff0c\u90fd\u50cf\u662f\u5df2\u968f\u7740\u6c57\u73e0\u6d41 \u51fa\u3002 \u3000\u3000\u75db\u82e6\u3001\u8212\u9002\u3001\u5fc3\u9b54\u3001\u6b32\u5ff5\u3001\u865a\u7a7a\u2026\u2026\u50cf\u662f\u6c38\u65e0\u4f11\u6b62\u4f3c\u7684\uff0c\u4e0d\u65ad\u5730\u4ea4\u66ff\u7740\uff0c\u4ed6\u8111\u6d77\u4e2d\u6a21\u6a21 \u7cca\u7cca\u5730\u6709\u4e00\u4e2a\u601d\u60f3\uff1a\u201c\u4e09\u5929\u2026\u2026\u4e09\u5929\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u5374\u5df2\u5fd8\u4e86\u4ec0\u4e48\u662f\u201c\u4e09\u5929\u201d\uff0c\u4ed6\u50cf\u662f\u5df2\u7ecf\u5386\u4e86\u5343\u767e\u5e74\uff01 \u3000\u3000\u5ffd\u7136\uff0c\u4e00\u5207\u90fd\u505c\u6b62\u4e86\u3002 \u3000\u3000\u4ed6\u6025\u5267\u5730\u5598\u606f\u7740\uff1a\u826f\u4e45\u826f\u4e45\u2026\u2026\u5ffd\u7136\uff0c\u4ed6\u8bb0\u8d77\u201c\u4e09\u5929\u201d\uff0c\u4ed6\u8bb0\u8d77\u4e86\u201c\u4e09\u5929\u201d\u7684\u542b\u610f\uff0c\u4ed6 \u5927\u559d\u4e00\u58f0\uff0c\u8dc3\u4e86\u8d77\u672a\u3002 \u3000\u3000\u6d1e\u7a9f\u4e2d\u7684\u5149\u7ebf\u4ecd\u662f\u6726\u80e7\u7684\uff0c\u5c31\u50cf\u662f\u4efb\u4f55\u4e8b\u90fd\u672a\u66fe\u53d1\u751f\u8fc7\u4e00\u6837\uff0c\u4f46\u662f\u2026\u2026\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u827e \u9752\u5462\uff1f \u3000\u3000\u4ed6\u5fc3\u5934\u4e00\u61d4\uff0c\u547c\u9053\uff1a\u201c\u827e\u2026\u2026\u592b\u4eba\uff0c\u827e\u9752\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\u7684\u56de\u54cd\uff0c\u81ea\u79d8\u9053\u4e2d\u4f20\u6765\uff0c\u4f46\u56db\u4e0b\u5374\u5bc2\u65e0\u56de\u5e94\u3002 \u3000\u3000\u4ed6\u6728\u7acb\u5f53\u5730\uff0c\u5fc3\u4e71\u5982\u9ebb\uff0c\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u81ea\u5df1\u7a76\u7adf\u7ecf\u5386\u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u56de\u58f0\u5bc2\u7edd\u3002 \u3000\u3000\u4ed6\u7a81\u5730\u542c\u5230\u4e00\u58f0\u5fae\u5f31\u7684\u58f0\u54cd\uff0c\u53d1\u81ea\u5730\u4e0a\u3002 \u3000\u3000\u201c\u73cf\u513f\u2026\xb7\xb7\xb7\u201d\u4ed6\u5fc3\u5934\u4e00\u60ca\uff0c\u5ffd\u5730\u4fef\u4e0b\u8eab\u53bb\uff0c\u819d\u9647\u7684\u5149\u7ebf\u4e2d\uff0c\u827e\u9752\u67d4\u8f6f\u5730\u5367\u5728\u5730\u4e0a\uff0c \u90a3\u660e\u4eae\u7684\u76ee\u5149\uff0c\u6b64\u523b\u5df2\u5b8c\u5168\u6d88\u5931\uff0c\u90a3\u4e4c\u9ed1\u7684\u53d1\u4e1d\uff0c\u6b64\u523b\u7adf\u4e5f\u53d8\u5f97\u7070\u767d\u3002 \u3000\u3000\u4ed6\u60ca\u60f6\u800c\u8ff7\u4e71\u5730\u6276\u8d77\u4e86\u5979\uff0c\u60ca\u60f6\u800c\u8ff7\u4e71\u5730\u6697\u4e2d\u601d\u5fd6\uff1a\u201c\u96be\u9053\u2026\u2026\u96be\u9053\u6211\u5df2\u6655\u8ff7\u4e86\u8bb8\u591a \u5e74\uff1f\u5979\u2026\u2026\u5979\u7adf\u7136\u5df2\u7ecf\u8001\u4e86\u2026\u2026\u5440\uff0c\u8fd9\u2026\u2026\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u67d4\u8f6f\u800c\u65e0\u529b\u5730\u501a\u5728\u4ed6\u6000\u4e2d\u7684\u827e\u9752\uff0c\u7a81\u53c8\u53d1\u51fa\u4e00\u4e1d\u58f0\u97f3\uff0c\u4e5f\u4e0d\u77e5\u662f\u5fae\u7b11\u6291\u6216\u662f\u53f9\u606f\uff0c\u547b \u541f\u2026\u2026 \u3000\u3000\u53ea\u542c\u5979\u8f7b\u8f7b\u9053\uff1a\u201c\u4e09\u5929\u2026\u2026\u5df2\u7ecf\u8fc7\u4e86\uff01\u201d \u3000\u3000\u88f4\u73cf\u5927\u9a87\u9053\uff1a\u201c\u4e09\u5929\uff0c\u624d\u53ea\u4e09\u5929\uff0c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u8001\u4e86\uff1f\u201d \u3000\u3000\u827e\u9752\u547b\u541f\u7740\u9053\uff1a\u201c\u4f60\u57cb\u846c\u4e86\u6211\u4eec\uff0c\u4fbf\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5927\u558a\u7740\u9053\uff1a\u201c\u57cb\u846c\u2026\u2026\u6211\u4e3a\u4ec0\u4e48\u8981\u57cb\u846c\u4f60\uff1f\u4f60\u2026\u2026\u8fd8\u662f\u6cbf\u7740\u7684\uff0c\u4f60\u8fd8\u8981\u6d3b\u4e0b\u53bb\uff01\u6c38 \u8fdc\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u558a\u58f0\u662f\u90a3\u4e48\u5639\u4eae\uff0c\u4f46\u827e\u9752\u5374\u4f3c\u6839\u672c\u542c\u4e0d\u5230\u4e86\uff01 \u3000\u3000\u5979\u53ea\u662f\u81ea\u8bed\u7740\u9053\uff1a\u201c\u6211\u5168\u8eab\u7684\u6c14\u529b\u3001\u7cbe\u8840\uff0c\u5df2\u7ecf\u5b8c\u5168\u7ed9\u4e86\u4f60\uff0c\u4f60\u2026\u2026\u4f60\u8981\u597d\u597d\u7684\u505a\u4eba\uff0c \u597d\u597d\u7684\u505a\u4eba\u2026\u2026\u6211\u80fd\u591f\u5e2e\u52a9\u4f60\u2026\u2026\u6211\u9ad8\u5174\u7684\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u5730\u4e2d\u65ad\u4e86\u3002 \u3000\u3000\u88f4\u73cf\u6ee1\u9762\u6cea\u75d5\uff0c\u60b2\u5636\u7740\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u4ed6\u7ec8\u4e8e\u4f0f\u5728\u5979\u8eab\u4e0a\uff0c\u653e\u58f0\u75db\u54ed\u4e86\u8d77\u6765\uff01\u4ed6 \u77e5\u9053\uff0c\u6df1\u6df1\u5730\u77e5\u9053\uff0c\u5979\u5df2\u6b7b\u4e86\uff01 \u3000\u3000\u4ece\u5979\u4e34\u6b7b\u524d\u7684\u8a00\u8bed\uff0c\u4ed6\u77e5\u9053\u5979\u5df2\u5c06\u5979\u4e00\u8eab\u7684\u529f\u529b\uff0c\u4ee5\u4e00\u79cd\u5947\u5999\u7684\u65b9\u6cd5\uff0c\u5168\u90e8\u7ed9\u4e86\u81ea\u5df1\uff0c \u800c\u4e14\u56e0\u6c14\u8840\u67af\u7aed\u800c\u6b7b\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u6b64\u523b\u5012\u5728\u4ed6\u6000\u4e2d\u7684\u8eaf\u4f11\uff0c\u662f\u8fd9\u4e48\u8f7b\uff0c\u8f7b\u5f97\u51e0\u4e4e\u63a5\u8fd1\u865a\u7a7a\uff0c\u7136\u800c\uff0c\u6b64\u523b\u538b\u5728\u4ed6\u5fc3\u5934 \u7684\u8d1f\u62c5\uff0c\u5374\u662f\u6c89\u91cd\u7684\u3002 \u3000\u3000\u65e0\u6bd4\u7684\u6069\u60c5\uff0c\u65e0\u6bd4\u7684\u611f\u6fc0\uff0c\u65e0\u6bd4\u7684\u60b2\u54c0\uff0c\u65e0\u6bd4\u7684\u75db\u82e6\u2026\u2026\u4f0d\u5f97\u4ed6\u7684\u5fc3\u623f\u90fd\u4f3c\u5df2\u505c\u6b62\u4e86\u8df3 \u52a8\u3002 \u3000\u3000\u4f46\u662f\uff0c\u6b7b\u4ea1\uff0c\u5374\u662f\u6ca1\u6709\u4efb\u4f55\u529b\u91cf\u53ef\u4ee5\u633d\u56de\u7684\uff01 \u3000\u3000\u60b2\u5267\uff0c\u7ec8\u7ed3\u4e86\uff01 \u3000\u3000\u79d8\u9053\u4e2d\u7684\u8db3\u58f0\uff0c\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u5411\u5916\u8d70\u53bb\uff0c\u8db3\u97f3\u662f\u5b64\u5355\u800c\u51c4\u6e05\u7684\uff0c\u88f4\u73cf\u7684\u5fc3\u60c5\uff0c\u4e5f\u662f\u5b64 \u5355\u800c\u51c4\u6e05\u7684\uff01 \u3000\u3000\u4ed6\u8f7b\u8f7b\u5730\u5c06\u90a3\u4e09\u5177\u5c38\u4f53\uff0c\u5e76\u6392\u653e\u5728\u4e00\u8d77\uff0c\u4ed6\u53d1\u8a93\u8981\u4ee5\u4e00\u4e2a\u65e0\u6bd4\u9686\u91cd\u7684\u846c\u793c\uff0c\u4f7f\u4ed6\u4eec\u80fd\u591f \u5b89\u606f\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u7acb\u5728\u5730\u9053\u7684\u5c3d\u5934\uff0c\u4ecd\u4e0d\u7981\u4f9d\u604b\u5730\u56de\u8fc7\u5934\u53bb\uff0c\u5411\u90a3\u9634\u68ee\u9edd\u9eef\u7684\u6d1e\u7a97\uff0c\u6295\u4ee5\u6700\u540e\u4e4b \u4e00\u77a5\u3002 \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u4ed6\u6839\u672c\u770b\u4e0d\u5230\u5979\uff0c\u4ed6\u6c38\u8fdc\u518d\u4e5f\u65e0\u6cd5\u770b\u5230\u5979\u90a3\u660e\u4eae\u7684\u773c\u6ce2\uff0c\u4f46\u662f\uff0c\u4ed6\u5374\u6df1\u4fe1\uff0c\u4ed6 \u82e5\u662f\u4ee5\u81ea\u5df1\u7684\u5fc3\u53bb\u770b\uff0c\u90a3\u4e48\u5979\u968f\u65f6\u90fd\u4f1a\u5448\u73b0\u5728\u81ea\u5df1\u773c\u524d\u7684\uff01 \u3000\u3000\u5730\u9053\u4e0a\u6709\u5f3a\u5149\u5c04\u4e0b\uff0c\u4ed6\u5583\u5583\u7740\u9053\uff1a\u201c\u73b0\u5728\u662f\u767d\u5929\u4e86\uff01\u201d \u3000\u3000\u4ed6\u867d\u7136\u5df2\u6709\u4e09\u5929\u4e09\u591c\u672a\u8fdb\u6c34\u7c73\uff0c\u4f46\u4ed6\u5374\u4e1d\u6beb\u4e0d\u89c9\u9965\u6e34\u75b2\u5026\u3002\u4ed6\u4e0d\u77e5\u9053\u662f\u60b2\u54c0\u4f24\u5bb3\u4e86\u4ed6\u7684 \u98df\u6b32\uff0c\u6291\u6216\u662f\u5947\u8ff9\u9020\u6210\u7684\u529b\u91cf\uff1b\u4ed6\u53ea\u662f\u4fcf\u7136\u5408\u4e0a\u773c\u5e18\uff0c\u594b\u529b\u4e00\u8dc3\uff0d\u2018\u4ed6\u53d1\u89c9\u81ea\u5df1\u7adf\u4f3c\u71d5\u5b50\u4f3c \u7684\u98d8\u4e86\u4e0a\u53bb\uff01\u5cf0\u5dc5\uff0c\u4ecd\u7136\u6c16\u6c22\u7740\u7ec8\u5e74\u4e0d\u6563\u7684\u4e91\u96fe\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u76d8\u819d\u5bf9\u5750\u5728\u5c0f\u77f3\u4e0a\uff0c\u88f4\u73cf\u4e00 \u63a0\u800c\u51fa\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u53ea\u89c1\u8fd9\u5144\u5f1f\u4e24\u4eba\u8eab\u5f62\u4f3c\u5df2\u50f5\u6728\uff0c\u987b\u53d1\u4e4b\u4e0a\uff0c\u6cbe\u6ee1\u4e86\u6c34\u73e0\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u4e3a \u4e4b\u5927\u9a87\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u4e5f\u2026\u2026\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u5fc3\u5ff5\u65b9\u8f6c\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5374\u5df2\u5f20\u5f00\u773c\u6765\uff0c\u5144\u5f1f\u4e8c\u4eba\uff0c\u5bf9\u671b\u4e00\u773c\uff0c\u51b7\u67af\u6728\u7f13\u7f13\u9053\uff1a \u201c\u4f60\u7684\u4e8b\u529e\u5b8c\u4e86\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u957f\u53f9\u4e00\u58f0\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d \u3000\u3000\u5144\u5f1f\u4e24\u4eba\uff0c\u9f50\u5730\u4e00\u632f\u8863\u886b\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u5f53\u76f4\u5411\u5c71\u4e0b\u8d70\u53bb\uff0c\u7adf\u4f3c\u88f4\u73cf\u5728\u4e0b\u9762\u53ea\u4e0d\u8fc7\u803d\u4e86\u4e09 \u4e24\u4e2a\u65f6\u8fb0\u800c\u5df2\uff0c\u65e2\u4e0d\u60ca\u5947\uff0c\u4ea6\u4e0d\u8be2\u95ee\u3002 \u3000\u3000\u88f4\u73cf\u6014\u4e86\u4e00\u6014\uff0c\u5feb\u6b65\u8ddf\u968f\u800c\u53bb\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u8981\u7ffb\u5c71\u800c\u884c\u4e86\u4e48\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u5934\u4e5f\u4e0d\u56de\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e09\u65e5\u4e09\u591c\u672a\u8fdb\u996e\u98df\uff0c\u54ea\u91cc\u8fd8\u6709\u7ffb\u5c71\u7684\u4e03\u9489\u201d\u88f4\u73cf\u6697\u53f9\u4e00 \u58f0\uff0c\u77e5\u9053\u8fd9\u5144\u5f1f\u4e24\u4eba\uff0c\u9762\u4e0a\u867d\u4f3c\u6f20\u4e0d\u5173\u5fc3\uff0c\u5176\u5b9e\u5374\u4e0d\u77e5\u5982\u4f55\u5730\u5728\u5173\u5fc3\u81ea\u5df1\uff01 \u3000\u3000\u4ed6\u5144\u5f1f\u4e24\u4eba\u8fd9\u4e09\u65e5\u4e09\u591c\u4e2d\uff0c\u7adf\u4e00\u76f4\u5b88\u5728\u90a3\u91cc\uff0c\u5bf8\u6b65\u672a\u79bb\u3002 \u3000\u3000\u5c71\u8def\u4ecd\u662f\u5d0e\u5c96\u7684\uff0c\u4f46\u5728\u88f4\u73cf\u773c\u4e2d\uff0c\u5374\u4f3c\u5df2\u53d8\u5f97\u6781\u4e3a\u5e73\u5766\uff0c\u53ea\u89c1\u4ed6\u6ee1\u5fc3\u7d0a\u4e71\uff0c\u6839\u672c\u6ca1\u6709\u6ce8 \u610f\u5230\u81ea\u8eab\u7684\u53d8\u5316\uff0c\u53ea\u662f\u4ea6\u6b65\u4ea6\u8d8b\u5730\u8ddf\u5728\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u8eab\u540e\u3002\u201c\u51b7\u8c37\u53cc\u6728\u201d\u53c8\u81ea\u5bf9\u671b\u4e00\u773c\uff0c \u5fc3\u4e2d\u5927\u662f\u60ca\u5947\uff0c\u9ed8\u7136\u8d70\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u56de\u8f6c\u8eab\u53bb\uff0c\u5446\u5446\u5730\u51dd\u6ce8\u7740\u88f4\u73cf\u7684\u811a\u6b65\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u9762\u4e0a\u7684\u60ca\u5947\u4e4b\u8272\u66f4\u660e\u663e\u4e86\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u5730\u626c\u624b\u4e00\u638c\uff0c\u5411\u88f4\u73cf\u62cd\u53bb\u3002 \u3000\u3000\u88f4\u73cf\u66ae\u7136\u4e00\u60ca\uff0c\u4e0d\u7b49\u4ed6\u601d\u8def\u8fd0\u8f6c\uff0c\u4ec5\u5728\u5fae\u4e00\u63d0\u6c14\u4e4b\u95f4\uff0c\u4ed6\u8eab\u5f62\u4f7f\u5df2\u540e\u9000\u4e09\u5c3a\u3002\u51b7\u67af\u6728\u76ee \u5149\u4e00\u4eae\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u4e86\uff01\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u5927\u662f\u832b\u7136\uff0c\u8be7\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u9762\u6c89\u5982\u51b0\uff0c\u9053\uff1a\u201c\u51b7\u6708\u4ed9\u5b50\u827e\u9752\uff0c\u53ef\u662f\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u88f4\u73cf\u9ed8\u7136\u5782\u9996\uff0c\u957f\u53f9\u9053\uff1a\u201c\u5343\u624b\u4e66\u751f\u548c\u51b7\u6708\u4ed9\u5b50\u4ff1\u5df2\u4ed9\u53bb\u3002\u201d \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9762\u4e0a\u5404\u5404\u95ea\u8fc7\u4e00\u4e1d\u5947\u5f02\u7684\u795e\u8272\u3002\u88f4\u73cf\u5fc3\u4e2d\u4ecd\u662f\u832b\u7136\u4e0d\u89e3\uff0c\u53ea\u542c\u51b7\u5bd2\u7af9\u53f9 \u9053\uff1a\u201c\u6b66\u6797\u4e2d\u65e9\u6709\u4f20\u8a00\uff0c\u4f5b\u9053\u4e24\u5bb6\u4e4b\u4e2d\uff0c\u4ff1\u6709\u4e00\u79cd\u795e\u5947\u7684\u6b66\u529f\uff0c\u80fd\u5728\u4e09\u65e5\u4e4b\u5185\uff0c\u6253\u901a\u4e00\u4eba\u7684 \u751f\u6b7b\u7384\u5173\uff0c\u5316\u8150\u673d\u4e3a\u795e\u5947\uff0c\u60f3\u4e0d\u5230\u4f60\u7adf\u6709\u5982\u6b64\u5947\u9047\uff0c\u53ea\u662f\u2026\u2026\u51b7\u6708\u4ed9\u5b50\u4e43\u662f\u4e3a\u4f60\u800c\u6b7b\uff0c\u4f60\u53ef \u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5f3a\u5fcd\u7740\u5fc3\u4e2d\u7684\u60b2\u54c0\uff0c\u5782\u9996\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u906d\u9047\u3002\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9762\u5bb9\u5fae\u53d8\uff0c\u7ec8\u4e8e\u5404\u81ea\u957f \u53f9\u4e00\u58f0\uff0c\u76f4\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u8fd9\u5144\u5f1f\u4e24\u4eba\uff0c\u65b9\u624d\u5728\u7b2c\u4e09\u8005\u9762\u524d\u53d1\u51fa\u53f9\u606f\uff0c\u5374\u4e0d\u77e5\u4ed6\u5fc3\u4e2d\u662f\u5728\u4e3a\u88f4 \u73cf\u7684\u5947\u9047\u800c\u5e86\u5e78\uff0c\u6291\u6216\u662f\u4e3a\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u7684\u547d\u8fd0\u800c\u60b2\u54c0\u3002 \u3000\u3000\u4e09\u6761\u4eba\u5f71\uff0c\u6709\u5982\u6d41\u661f\u98de\u5760\u822c\u63a0\u4e0b\u9ec4\u5c71\uff0c\u88f4\u73cf\u7684\u6b65\u5c65\uff0c\u7adf\u80fd\u4e0e\u8fd9\u4e24\u4e2a\u4e45\u5df2\u6210\u540d\u7684\u6b66\u6797\u9ad8\u624b \u5e76\u9a7e\u9f50\u9a71\uff0c\u8fd9\u4e00\u6765\u56fa\u662f\u56e0\u4e3a\u51b7\u6c0f\u5144\u5f1f\u4e24\u4eba\u56f0\u4e8e\u9965\u6e34\uff0c\u4f53\u529b\u9510\u51cf\uff0c\u518d\u8005\u81ea\u7136\u4fbf\u662f\u56e0\u4e3a\u90a3\u8584\u547d\u7684 \u4e00\u4ee3\u7ea2\u989c\uff0c\u5728\u4e34\u6b7b\u524d\u9020\u6210\u7684\u5947\u8ff9\u3002 \u3000\u3000\u5b87\u5b99\u4e4b\u95f4\uff0c\u672c\u6709\u8bb8\u591a\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\uff0c\u5c24\u5176\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u8fd9\u79cd\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\u66f4\u591a\u3002\u5c31\u8fde\u88f4 \u73cf\u81ea\u5df1\uff0c\u90fd\u51e0\u4e4e\u4e0d\u80fd\u76f8\u4fe1\u8fd9\u5947\u8ff9\u7adf\u662f\u771f\u7684\uff0c\u82e5\u4e0d\u662f\u4ed6\u5fc3\u4e2d\u4ecd\u5b58\u7740\u8fd9\u6df1\u8fed\u7684\u60b2\u54c0\u4e0e\u611f\u6fc0\uff0c\u53ea\u6015 \u4ed6\u771f\u5f97\u5174\u594b\u5f97\u96c0\u8dc3\u800c\u8d77\u3002 \u3000\u3000\u8fd9\u6b63\u5982\u4e45\u76f2\u4e4b\u4eba\u7a81\u83b7\u5149\u660e\uff0c\u4e45\u8d2b\u4e4b\u4eba\u7a81\u83b7\u8d22\u5bcc\uff0c\u4e45\u6e34\u4e4b\u4eba\u7a81\u83b7\u7518\u9716\uff1b\u4ed6\u7adf\u5728\u8fd9\u5d0e\u5c96\u66f2\u6298 \u7684\u4eba\u751f\u4e4b\u8def\u4e0a\uff0c\u9aa4\u7136\u8de8\u8fdb\u4e00\u6b65\uff0c\u4f7f\u5f97\u4ed6\u7684\u751f\u547d\u7acb\u523b\u4e3a\u4e4b\u6539\u89c2\uff0c\u4ec5\u4ec5\u662f\u4e09\u65e5\u77ed\u6682\u7684\u65f6\u5149\uff0c\u4ed6\u7adf \u5df2\u8d85\u8fc7\u4e86\u4e00\u4e2a\u5e38\u4eba\u51e0\u4e4e\u4e00\u751f\u90fd\u65e0\u6cd5\u8d85\u8fc8\u7684\u9636\u5c42\u3002 \u3000\u3000\u201c\u4f46\u662f\uff0c\u6211\u7b54\u5e94\u4f60\uff0c\u4f60\u6240\u5fcd\u53d7\u7684\u4e00\u5207\u75db\u82e6\uff0c\u90fd\u5c06\u4f1a\u5f97\u5230\u5341\u500d\u7684\u62a5\u507f\u2026\u2026\u201d \u3000\u3000\u5239\u90a3\u95f4\uff0c\u8fd9\u6e29\u67d4\u800c\u60b2\u54c0\u7684\u8bed\u58f0\uff0c\u4f3c\u4e4e\u53c8\u5728\u4ed6\u8033\u7554\u54cd\u8d77\uff0c\u6b63\u5982\u4e00\u4e2a\u79bb\u5bb6\u7684\u6e38\u5b50\uff0c\u7a81\u7136\u60f3\u8d77 \u4e86\u6545\u4e61\u7684\u4e61\u97f3\uff0c\u4f46\u4e61\u97f3\u72b9\u53ef\u91cd\u95fb\uff0c\u8fd9\u6e29\u67d4\u7684\u8bed\u58f0\u5462\uff1f \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5c3d\u91cf\u63a9\u9970\u7740\u5fc3\u4e2d\u7684\u559c\u60a6\uff0c\u4f46\u559c\u60a6\u4ecd\u6084\u6084\u5730\u4ece\u4ed6\u4eec\u7684\u76ee\u5149\u4e2d\u6e9c\u4e86\u51fa\u6765\uff0c\u56e0\u4e3a \u4ed6\u4eec\u786e\u4fe1\u88f4\u73cf\u662f\u503c\u5f97\u6709\u8fd9\u79cd\u5947\u9047\u7684\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u4fa7\u76ee\u671b\u4e86\u671b\u88f4\u73cf\u7684\u795e\u8272\uff0c\u77e5\u9053\u8fd9\u5584\u826f\u7684\u5c11\u5e74\u4ecd\u7136\u6c89\u6d78\u4e8e\u60b2\u54c0\u4e4b\u4e2d\uff0c\u4ed6\u4e0d\u613f\u5927\u591a\u60b2 \u75db\u4f24\u5bb3\u8fd9\u5c11\u5e74\u7684\u5fc3\u2014\u2014\u56e0\u4e3a\u4ed6\u81ea\u5df1\u7684\u5fc3\u4fbf\u662f\u66fe\u7ecf\u88ab\u60b2\u54c0\u4f24\u5bb3\u4e86\u7684\u2014\u2014\u4ed6\u5fae\u4e00\u6c89\u541f\uff0c\u7f13\u7f13\u9053\uff1a \u201c\u88f4\u73cf\uff0c\u4f60\u60f3\u90a3\u73ed\u538c\u7269\u6b64\u523b\u662f\u5426\u8fd8\u5728\u5c71\u4e0b\uff1f\u201d \u3000\u3000\u88f4\u73cf\u795e\u601d\u4e0d\u5c5e\uff0c\u832b\u7136\u5e94\u9053\uff1a\u201c\u6211\u4eec\u4e0a\u5c71\u5df2\u6709\u56db\u5929\uff0c\u53ea\u6015\u4ed6\u4eec\u65e9\u5df2\u8d70\u4e86\uff01\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u7a81\u5730\u4e00\u7b11\u9053\uff1a\u201c\u6211\u5012\u5e0c\u671b\u4ed6\u4eec\u672a\u8d70\uff0c\u6709\u8fd9\u4e9b\u4eba\u966a\u7740\u6211\u4eec\uff0c\u65c5\u9014\u4e2d\u5f53\u771f\u5c11\u4e86\u8bb8\u591a\u5bc2 \u5bde\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u4e00\u52a8\uff0c\u201c\u5bc2\u5bde\u201d\u8fd9\u4e24\u4e2a\u5b57\uff0c\u7adf\u4f1a\u51fa\u81ea\u51b7\u9177\u7684\u201c\u51b7\u8c37\u53cc\u6728\u201d\u53e3\u4e2d\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u4ee4 \u4eba\u60ca\u5f02\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u62ac\u8d77\u5934\uff0c\u53c8\u770b\u5230\u4e86\u4ed6\u4eec\u9762\u4e0a\u7684\u7b11\u5bb9\uff0c\u4e8e\u662f\u4ed6\u672c\u5df2\u5bd2\u900f\u7684\u5fc3\u91cc\uff0c\u4fbf\u4e0d\u7981\u5347\u8d77\u4e00\u9635\u6e29\u6696\uff0c \u6697\u6697\u5fd6\u9053\uff1a\u201c\u5440\uff0c\u2018\u51b7\u8c37\u53cc\u6728\u2019\u7adf\u7136\u53d8\u4e86\uff01\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u9762\u4e0a\u4fbf\u4e5f\u4e0d\u7981\u6cdb\u8d77\u4e00\u4e1d\u7b11\u5bb9\uff0c\u76f4\u5230\u5c71\u4e0b\uff1a\u8d70\u5230\u5c71\u4e0b\uff0c\u5df2\u6709\u4e00\u9635\u9635\u5608\u4e71\u4e4b\u58f0\u968f\u98ce\u98d8 \u6765\uff0c\u8fd9\u4e09\u4eba\u4e0d\u7981\u5927\u4e3a\u5947\u602a\u3002\u63a0\u5230\u4e00\u65b9\u5c71\u77f3\u4e4b\u4e0a\u6781\u76ee\u4e0b\u671b\uff0c\u53ea\u89c1\u5c71\u811a\u524d\u4eba\u5934\u8702\u6d8c\uff0c\u7b11\u8bed\u55a7\u54d7\uff0c \u4f3c\u4e4e\u6bd4\u4ed6\u4eec\u4e0a\u5c71\u65f6\u8fd8\u8981\u70ed\u95f9\uff0c\u4e00\u9635\u9635\u9152\u8089\u7684\u9999\u6c14\uff0c\u968f\u7740\u7b11\u8bed\u4e4b\u58f0\u98d8\u8d77\u3002 \u3000\u3000\u4e09\u4eba\u76ee\u5149\u4e92\u4e00\u4ea4\u9519\uff0c\u7a81\u89c9\u9965\u80a0\u8f98\u8f98\uff0c\u96be\u4ee5\u5fcd\u8010\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u98de\u5954\u4e0b\u5c71\u53bb\uff0c\u4f46\u5230\u4e86\u5c71\u811a\uff0c \u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u811a\u6b65\u4fbf\u7a81\u7136\u548c\u7f13\uff0c\u9762\u4e0a\u7684\u7b11\u5bb9\uff0c\u4e5f\u65e9\u5df2\u6536\u655b\uff0c\u88f4\u73cf\u76ee\u5149\u8f6c\u52a8\uff0c\u4e0d\u7981\u6697\u53f9\u4e00 \u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u5144\u5f1f\u4e24\u4eba\uff0c\u4e3a\u4ec0\u4e48\u5bf9\u4e16\u4eba\u603b\u8981\u5982\u6b64\u51b7\u9177\u5462\uff1f\u201d \u3000\u3000\u9633\u5149\u666e\u7167\uff0c\u5927\u5730\u4e0a\u6d0b\u6ea2\u7740\u4e00\u79cd\u65b0\u751f\u7684\u671d\u6c14\uff0c\u88f4\u73cf\u4e00\u633a\u80f8\u819b\uff0c\u5927\u6b65\u800c\u884c\uff0c\u4ed6\u8eab\u5f62\u65b9\u73b0\uff0c\u5c71 \u524d\u7acb\u523b\u66b4\u8d77\u4e00\u9635\u5f02\u6837\u7684\u6b22\u547c\uff1a\u201c\u88f4\u5927\u5148\u751f\uff01\u201d \u3000\u3000\u8fd9\u9707\u8033\u7684\u547c\u58f0\uff0c\u7ade\u662f\u7531\u6570\u767e\u4e2a\u6b66\u6797\u8c6a\u58eb\u53e3\u4e2d\u4e00\u8d77\u558a\u51fa\uff0c\u88f4\u73cf\u6014\u4e86\u4e00\u6014\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u81ea \u5df1\u5728\u6c5f\u6e56\u4e2d\u7adf\u6709\u8fd9\u79cd\u529b\u91cf\u2014\u2014\u4ed6\u6c38\u8fdc\u662f\u8c26\u606d\u7684\u3002\u4ed6\u7adf\u4e0d\u77e5\u9053\u4e16\u4e0a\u552f\u6709\u8c26\u606d\uff0c\u624d\u80fd\u5f97\u5230\u4eba\u4eec\u7684 \u6b22\u547c\uff1b\u800c\u9a84\u50b2\u81ea\u5927\u6240\u80fd\u5f97\u5230\u7684\uff0c\u5374\u53ea\u6709\u4e0d\u5c51\u4e0e\u8fb1\u9a82\u3002 \u3000\u3000\u56f4\u7ed5\u7740\u7684\u4eba\u7fa4\uff0c\u7acb\u523b\u9a9a\u52a8\u4e86\u8d77\u6765\uff0c\u4eba\u7fa4\u4e2d\u5374\u6709\u4e8c\u4eba\uff0c\u5bf9\u9762\u800c\u5750\uff0c\u5bc2\u7136\u4e0d\u52a8\uff0c\u4e00\u4eba\u8eab\u8eaf\u9ad8 \u5927\uff0c\u6ee1\u8eab\u7ea2\u8863\uff0c\u81ea\u7136\u662f\u90a3\u7c97\u8c6a\u7684\u83bd\u6c49\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\uff0c\u53e6\u4e00\u4eba\u8eab\u5f62\u67af\u7626\uff0c\u53cc\u76ee\u6df1\u9677\uff0c\u6b63\u662f\u4ed6 \u7684\u5bf9\u5934\uff0c\u201c\u9ed1\u9a74\u8ffd\u51e4\u201d\u8d3e\u658c\uff01 \u3000\u3000\u547c\u58f0\u4ecd\u5728\u7ee7\u7eed\u7740\uff0c\u88f4\u73cf\u5fae\u5e26\u60f6\u6050\uff0c\u8d70\u5165\u4e86\u4eba\u7fa4\uff0c\u201c\u98de\u7075\u5821\u201d\u7684\u7ba1\u4e8c\u5148\u751f\uff0c\u201c\u6d6a\u83bd\u5c71\u5e84\u201d \u7684\u4e8e\u5e73\u9f50\u5730\u8fce\u4e86\u4e0a\u53bb\uff0c\u4e24\u4eba\u5404\u4ee5\u4e0d\u540c\u7684\u5e0c\u5180\u795e\u8272\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u63a2\u95ee\uff1a\u201c\u80dc\u8d1f\u5206\u51fa\u4e86\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u66fe\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u6709\u60b2\u54c0\uff0c\u4f46\u4ed6\u5374\u4e0d\u613f\u8ba9\u522b\u4eba\u4e5f\u6765\u8d1f\u62c5\u4ed6\u7684\u60b2\u54c0\u7684\u75db\u82e6\u2014\u2014\u60b2\u54c0\uff0c\u6c38\u8fdc\u53ea\u9002\u4e8e\u72ec \u81ea\u5480\u56bc\u7684\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u5fae\u7b11\u9053\uff1a\u201c\u6211\u539f\u672c\u4ee5\u4e3a\u5404\u4f4d\u5df2\u662f\u8d70\u4e86\uff0c\u5374\u4e0d\u60f3\u5404\u4f4d\u7adf\u6709\u5982\u6b64\u8010\u5fc3\u3002\u201d \u3000\u3000\u201c\u7ba1\u4e8c\u7237\u201d\u7cbe\u795e\u4e00\u632f\uff0c\u4ed6\u4f3c\u4e4e\u7b97\u5f97\u201c\u88f4\u5927\u5148\u751f\u201d\u7adf\u4e0e\u81ea\u5df1\u8c08\u7b11\u5f97\u8fd9\u822c\u4eb2\u5207\uff0c\u7684\u786e\u662f\u4e00\u4ef6 \u5149\u8363\u7684\u4e8b\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u70ed\u7231\u7740\u4eba\u7c7b\u7684\u88f4\u73cf\u4e5f\u662f\u591a\u4e48\u613f\u610f\u4e0e\u4eba\u5e73\u7b49\u76f8\u4ea4\uff0c\u53ea\u662f\u5728\u4ee5\u5f80\u90a3\u4e00\u6bb5\u65e5 \u5b50\u91cc\uff0c\u522b\u4eba\u90fd\u4e0d\u613f\u4e0e\u4ed6\u5e73\u7b49\u76f8\u4ea4\u800c\u5df2\uff01 \u3000\u3000\u4e8e\u5e73\u56de\u9996\u671b\u4e86\u90a3\u6728\u7136\u7aef\u5750\u7740\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u4e00\u773c\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u5c0f\u7684\u4eec\u672c\u4e5f\u8981\u8d70\u4e86\uff0c\u53ea \u662f\u2026\u2026\u53ea\u662f\u90a3\u4f4d\u8d3e\u9556\u5934\u5374\u8bf4\u4e09\u4f4d\u4e00\u5b9a\u4f1a\u7531\u539f\u8def\u4e0b\u5c71\u7684\uff0c\u662f\u4ee5\u5c0f\u4eba\u7b49\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4ed6\u5351\u5fae\u5730\u81ea\u79f0\u201c\u5c0f\u7684\u201d\uff0c\u88f4\u73cf\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u6697\u53f9\u606f\uff1a\u201c\u4e3a\u4ec0\u4e48\u8bb8\u591a\u4eba\u90fd\u8fd9\u822c\u5947\u602a\uff0c\u4ed6\u4eec \u4e0d\u662f\u8981\u538b\u5728\u522b\u4eba\u7684\u5934\u4e0a\uff0c\u4fbf\u662f\u60c5\u613f\u88ab\u8e42\u5728\u811a\u4e0b\uff0c\u96be\u9053\u4ed6\u4e0d\u77e5\u9053\u4eba\u7c7b\u751f\u6765\u672c\u8be5\u662f\u5e73\u7b49\u7684\u4e48\uff1f\u201d \u4ed6\u5374\u4e0d\u77e5\u9053\u4ed6\u81ea\u5df1\u90a3\u795e\u5947\u7684\u201c\u4e00\u6b65\u201d\u3001\u7684\u786e\u8de8\u5f97\u592a\u5927\u4e86\u4e9b\u3002 \u3000\u3000\u4ed6\u987a\u7740\u624b\u6307\u7684\u65b9\u5411\uff0c\u8d70\u5230\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\u9762\u524d\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u65b9\u5f85\u8bf4\u8bdd\uff0c\u54ea\u77e5\u201c\u9e21\u51a0\u201d \u5305\u6653\u5929\u7a81\u5730\u626c\u81c2\u5927\u559d\u9053\uff1a\u201c\u62ff\u9152\u6765\uff0c\u62ff\u9152\u4e3a\u2014\u2014\u8001\u5b50\u75db\u75db\u5feb\u5feb\u5730\u7aed\u4e0a\u51e0\u7897\uff0c\u4fbf\u8981\u548c\u960e\u738b\u8001\u7237 \u53bb\u6253\u4ea4\u9053\u4e86\uff01\u201d \u3000\u3000\u88f4\u73cf\u53cc\u7709\u4e00\u76b1\uff0c\u6697\u9053\uff1a\u201c\u600e\u5730\u53c8\u662f\u4e00\u4e2a\u8981\u6b7b\u7684\u4eba\uff1f\u201d\u4ed6\u8f6c\u8eab\u8d70\u5411\u201c\u9e21\u51a0\u201d\u5305\u6653\u5927\uff0c\u548c\u60a6 \u5730\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u5fc3\u4e2d\u6709\u4f55\u5316\u89e3\u4e0d\u5f00\u4e4b\u4e8b\uff0c\u8981\u5982\u6b64\u4e00\u4e00\u4e00\u201d\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u53cc\u76ee\u4e00\u5f20\uff0c\u5927 \u58f0\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u6709\u4ec0\u4e48\u5316\u89e3\u4e0d\u5f00\u4e4b\u4e8b\uff1f\u6211\u5fc3\u91cc\u5feb\u6d3b\u5f97\u5f88\uff0c\u53ea\u662f\u4e0e\u8fd9\u59d3\u8d3e\u7684\u8d4c\u8f93\u4e86\uff0c\u662f\u4ee5\u975e\u6b7b \u4e0d\u53ef\uff01\u563f\u563f\uff0c\u548c\u960e\u738b\u8001\u7237\u6253\u6253\u4ea4\u9053\uff0c\u60f3\u6765\u4e5f\u86ee\u6709\u5473\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u867d\u7136\u54cd\u54cd\u5f53\u5f53\uff0c\u5176\u5b9e\u5fc3\u91cc\u53c8\u4f55\u5c1d\u4e0d\u5bf9\u6b7b\u4ea1\u6709\u7740\u754f\u60e7\uff0c\u5c31\u8fde\u4ed6\u5e73\u65e5\u90a3\u79cd\u5f97\u610f\u7684\u7b11 \u58f0\uff0c\u6b64\u523b\u90fd\u53d8\u5f97\u5341\u5206\u52c9\u5f3a\u3002 \u3000\u3000\u88f4\u73cf\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u53c8\u662f\u6253\u8d4c\uff0c\u4e3a\u4ec0\u4e48\u8d4c\u7684\uff1f\u201d \u3000\u3000\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u9053\uff1a\u201c\u59d3\u8d3e\u7684\u8bf4\u4f60\u4eec\u4e00\u5b9a\u4f1a\u4ece\u539f\u8def\u4e0b\u5c71\uff0c\u6211\u7b49\u4e86\u4e24\u5929\uff0c\u4f60\u4eec\u5374\u8fde\u5f71\u5b50\u4e5f \u770b\u4e0d\u89c1\uff0c\u8a00\u6765\u8bed\u53bb\uff0c\u6211\u4eec\u5c31\u6253\u8d77\u8d4c\u6765\uff0c\u4ed6\u8bf4\u4f60\u4eec\u4e94\u65e5\u4e4b\u5185\uff0c\u5fc5\u5b9a\u4f1a\u6765\uff0c\u6211\u95ee\u4ed6\u8d4c\u4ec0\u4e48\uff0c\u4ed6\u8bf4 \u2018\u8d4c\u8111\u888b\u2019\uff01\u597d\uff0c\u8d4c\u8111\u888b\u5c31\u8d4c\u8111\u888b\uff0c\u563f\u563f\u2026\u2026\u8111\u888b\u6389\u4e86\uff0c\u4e5f\u4e0d\u8fc7\u53ea\u662f\u7897\u5927\u7684\u4e00\u4e2a\u7a9f\u7abf\u800c\u5df2\uff0c \u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff1f\u563f\u563f\u2026\u2026\u62ff\u9152\u6765\uff0c\u62ff\u9152\u6765\uff01\u201d \u3000\u3000\u4ed6\u8a00\u8bed\u7c97\u76f4\uff0c\u6b63\u662f\u8349\u83bd\u8c6a\u96c4\u7684\u672c\u8272\uff0c\u88f4\u73cf\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u5012\u662f\u6761\u6c49\u5b50\uff01\u201d\u5fc3\u4e0b\u5df2\u52a8\u4e86\u601c\u60dc \u4e4b\u610f\u3002\u53ea\u89c1\u90a3\u201c\u7ba1\u4e8c\u7237\u201d\u51d1\u4e86\u8fc7\u6765\uff0c\u5e26\u7740\u7b11\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4ed6\u4e24\u4eba\u53c8\u5728\u6253\u8d4c\uff0c\u8fd9\u56db\u4e0b\u7684\u597d\u6c49\u4eec \u53ea\u6015\u65e9\u5df2\u8d70\u4e86\uff01\u5509\u2026\u2026\u8d3e\u9556\u5934\u5f53\u771f\u6709\u672a\u535c\u5148\u77e5\u4e4b\u80fd\uff0c\u8d77\u5148\u8fde\u6211\u90fd\u65e0\u6cd5\u76f8\u4fe1\u3002\u201d \u3000\u3000\u88f4\u73cf\u4e00\u7b11\uff0c\u8f6c\u5411\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\uff0c\u53ea\u89c1\u6b64\u4eba\u867d\u662f\u5e72\u67af\u7626\u5c0f\uff0c\u5176\u8c8c\u4e0d\u626c\uff0c\u4f46\u53cc\u76ee\u70af\u70af\u6709 \u5149\uff0c\u6b64\u523b\u542b\u7b11\u7ad9\u4e86\u8d77\u6765\uff0c\u88f4\u73cf\u5f53\u5934\u4e00\u63d6\uff0c\u4ed6\u4e5f\u8fde\u5fd9\u8fd8\u793c\u3002\u88f4\u73cf\u9053\uff1a\u201c\u9601\u4e0b\u60f3\u5fc5\u5c31\u662f\u8d3e\u9556\u5934 \u4e86\uff0c\u5728\u4e0b\u88f4\u73cf\uff0c\u6614\u65e5\u672c\u5728\u2018\u98de\u9f99\u9556\u5c40\u2019\u957f\u5927\uff0c\u5374\u672a\u66fe\u89c1\u5230\u8d3e\u9556\u5934\uff0c\u5b9e\u662f\u9057\u61be\u5f97\u5f88\u3002\u201d \u3000\u3000\u8d3e\u658c\u62b1\u62f3\u9053\uff1a\u201c\u5144\u5f1f\u4e00\u76f4\u5728\u6c5f\u5357\u5206\u5c40\uff0c\u516c\u5b50\u81ea\u7136\u89c1\u4e0d\u5230\u4e86\u3002\u201d \u3000\u3000\u56db\u4e0b\u4f17\u8c6a\uff0c\u5927\u591a\u4e0d\u77e5\u201c\u88f4\u5927\u5148\u751f\u201d\u4e0e\u201c\u98de\u9f99\u9556\u5c40\u201d\u6709\u7740\u6e0a\u6e90\uff0c\u6b64\u523b\u4e0d\u89c9\u4ff1\u90fd\u5927\u5947\uff0c\u542c\u88f4 \u73cf\u9053\uff1a\u201c\u6a80\u8001\u9556\u5934\uff0c\u5728\u4e0b\u4e00\u76f4\u4ee5\u7236\u6267\u76f8\u79f0\uff0c\u9601\u4e0b\u81ea\u7136\u4e5f\u662f\u5c0f\u53ef\u7684\u524d\u8f88\uff01\u201d \u3000\u3000\u201c\u88f4\u5927\u5148\u751f\u201d\u8a00\u8bed\u7adf\u662f\u5982\u6b64\u8c26\u606d\uff0c\u4f17\u8c6a\u53c8\u4e0d\u7981\u5927\u5947\u3002\u8d3e\u658c\u66f4\u662f\u8fde\u79f0\u201c\u4e0d\u6562\u201d\u3002\u88f4\u73cf\u957f\u53f9 \u4e00\u58f0\u9053\uff0c\u201c\u5c0f\u53ef\u5e73\u65e5\u65e0\u6743\u5e72\u9884\u9601\u4e0b\u4e4b\u4e8b\uff0c\u4f46\u5c0f\u53ef\u603b\u8ba4\u4e3a\u4eba\u547d\u5173\u5929\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u53ea\u671b\u9601\u4e0b\u80fd\u770b \u5728\u5c0f\u53ef\u7684\u8584\u9762\uff0c\u5c06\u90a3\u8d4c\u6ce8\u4e00\u7b11\u7f6e\u4e4b\uff0c\u5c0f\u53ef\u5f53\u771f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u4e0d\u7981\u53c8\u662f\u4e00\u9635\u79c1\u8bed\u3001\u559d\u5f69\u3002\u4ed6\u4eec\u8c01\u4e5f\u6ca1\u6709\u60f3\u5230\u201c\u88f4\u5927\u5148\u751f\u201d\u7adf\u4f1a\u4e3a\u7740\u6b64\u4e8b\u5411\u522b\u4eba\u5982 \u6b64\u8c26\u524d\u8bda\u6073\u5730\u8bf7\u6c42\u3002\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u66f4\u662f\u76ee\u77aa\u53e3\u5446\uff0c\u5fc3\u4e2d\u60ed\u6127\uff0c\u6df1\u6127\u65b9\u624d\u81ea\u5df1\u7adf\u5bf9\u4ed6\u8a00\u8bed\u90a3 \u822c\u65e0\u793c\uff01 \u3000\u3000\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\u76ee\u5149\u95ea\uff0c\u52a8\uff0c\u5fc3\u4e2d\u4f3c\u4e5f\u6df1\u53d7\u611f\u52a8\u3002\u6c89\u541f\u826f\u4e45\uff0c\u7a81\u5730\u5927\u7b11\u51e0\u58f0\uff0c\u8d70\u5230 \u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u9762\u524d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u771f\u7684\u8981\u53bb\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u4e8e\u54b3\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u81ea\u7136\u3002\u201d \u3000\u3000\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u8981\u53bb\u6b7b\uff0c\u90a3\u4e48\u4f60\u7b97\u5f97\u662f\u4e2a\u5446\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u6211 \u867d\u4e0e\u4f60\u6253\u8d4c\uff0c\u5176\u5b9e\u5fc3\u91cc\u4e5f\u6beb\u65e0\u628a\u63e1\uff0c\u65e9\u5df2\u51c6\u5907\u597d\u4e86\uff0c\u8f93\u4e86\u4e4b\u540e\uff0c\u4fbf\u4e00\u8d70\u4e86\u4e4b\uff0c\u53cd\u6b63\u4f60\u4e5f\u8feb\u4e0d \u4e0a\u6211\u2014\u2014\u54c8\u54c8\uff0c\u65b9\u624d\u6211\u89c1\u5230\u88f4\u516c\u5b50\u4e0b\u5c71\u4e4b\u9645\uff0c\u51e0\u4e4e\u559c\u6b22\u5f97\u8df3\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u5446\u5446\u5730\u671b\u7740\u4ed6\uff0c\u7a81\u5730\u5927\u58f0\u9053\uff1a\u201c\u597d\u597d\uff0c\u4f60\u65e2\u7136\u8001\u5b9e\u4e0d\u5ba2\u6c14\u4ed6\u8bf4\u51fa\u6765\uff0c\u6211 \u4e5f\u53ea\u597d\u8001\u5b9e\u4e0d\u5ba2\u6c14\u5730\u4e0d\u6b7b\u4e86\uff0c\u83ab\u8981\u6b7b\u4e86\u4e4b\u540e\uff0c\u8fd8\u88ab\u4f60\u9a82\u505a\u5446\u5b50\u3002\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u867d\u7136\u5f3a\u786c\uff0c\u76ee\u5149\u4e2d\u5374\u6ee1\u662f\u611f\u6fc0\u4e4b\u610f\uff0c\u8fd9\u4e2a\u4ed6\u6240\u75db\u6068\u7684\u4eba\uff0c\u6b64\u523b\u7684\u8fd9\u756a\u8a00\u8bed\uff0c\u4e0d\u4f46 \u4fdd\u4f4f\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u4e5f\u4fdd\u5168\u4e01\u4ed6\u7684\u989c\u9762\u4e00\u5c24\u5176\u662f\u540e\u8005\uff0c\u66f4\u4ee4\u8fd9\u7c97\u8c6a\u7684\u83bd\u6c49\u6c38\u8fdc\u611f\u6fc0\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff0c\u6b64\u523b\u4ed6\u66f4\u786e\u500c\uff0c\u4eba\u95f4\u6bd5\u7adf\u662f\u5145\u6ee1\u4e86\u4eba\u60c5\u4e0e\u6e29\u6696\uff0c\u4ed6\u4e0d\u7981\u53c8\u5728\u6697\u4e2d\u5e0c\u5180\uff0c \u201c\u795e\u624b\u201d\u6218\u98de\u7684\u8d4c\u7ea6\uff0c\u4e5f\u80fd\u50cf\u6b64\u523b\u4e00\u6837\u5730\u8f7b\u8f7b\u5316\u89e3\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u8eab\u4efd\u7684\u4e0d\u540c\uff0c\u5730\u4f4d\u7684\u60ac\u6b8a\uff0c\u5df2\u4f7f\u5f97\u8fd9\u4e24\u4ef6\u8d4c\u7ea6\u4e4b\u95f4\u6709\u4e86\u4e0d\u53ef\u6500\u8d8a\u7684 \u8ddd\u79bb\u3002 \u7b2c\u5341\u4e00\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u5341\u4e00\u7ae0 \u3000\u3000\u5947\u5f02\u7684\u8d4c\u7ea6\uff0c\u4ecd\u5728\u7ee7\u7eed\u7740\uff1b\u5947\u5f02\u7684\u884c\u5217\uff0c\u4e5f\u4ecd\u5728\u7ee7\u7eed\u7740\u4ed6\u4eec\u5947\u5f02\u7684\u884c\u7a0b\uff0c\u4ed6\u4eec\u7ecf\u8fc7\u7684\u5730 \u65b9\uff0c\u5e9f\u589f\u53d8\u4e3a\u95f9\u5e02\uff0c\u8ddf\u968f\u7740\u4ed6\u4eec\u7684\u884c\u5546\u5c0f\u8d29\uff0c\u4e5f\u6e10\u6e10\u805a\u5408\u6210\u4e00\u4e2a\u5947\u5f02\u7684\u5546\u56e2\uff0c\u4f9b\u5e94\u7740\u4eba\u7c7b\u751f \u6d3b\u4e0a\u5404\u79cd\u5fc5\u9700\u7684\u7269\u54c1\u3002 \u3000\u3000\u8fd9\u5947\u5f02\u7684\u56e2\u4f53\u4e4b\u4e2d\uff0c\u5145\u6ee1\u4e86\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u6597\u4e89\u2014\u2014\u60c5\u3001\u4ec7\u3001\u6069\u3001\u6028\u3001\u5229\u3001\u6b32\u2026\u2026\u2026\u5404\u79cd \u6597\u4e89\u3002 \u3000\u3000\u8fd9\u65e0\u6570\u79cd\u6597\u4e89\u4e4b\u4e2d\uff0c\u53c8\u5145\u6ee1\u4e86\u8bb8\u591a\u79cd\u4e50\u8da3\uff1b\u56fa\u7136\u6709\u8bb8\u591a\u523b\u9aa8\u96be\u5fd8\u7684\u4ec7\u5bb6\uff0c\u5728\u8fd9\u91cc\u72ed\u8def\u76f8 \u9022\uff0c\u4f46\u4e5f\u6709\u8bb8\u591a\u7ecf\u5e74\u672a\u89c1\u7684\u826f\u53cb\uff0c\u5728\u8fd9\u91cc\u628a\u81c2\u91cd\u6664\uff1b\u540c\u7136\u6709\u8bb8\u591a\u7d20\u65e0\u5173\u8fde\u7684\u4eba\uff0c\u56e0\u7ec6\u6545\u800c\u751f \u52c3\u8c41\uff0c\u751a\u81f3\u53cd\u76ee\u6210\u4ec7\uff0c\u62d4\u5200\u76f8\u5411\uff0c\u4f46\u4e5f\u6709\u8bb8\u591a\u7d20\u6765\u964c\u751f\u7684\u4eba\uff0c\u7531\u4e8e\u8fd9\u79cd\u805a\u5408\uff0c\u800c\u7ed3\u4e3a\u76f8\u77e5\u3002 \u3000\u3000\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u7c97\u8c6a\u7684\u7b11\u58f0\uff0c\u4ecd\u7136\u4e00\u5982\u5f80\u6614\uff0c\u4f46\u4ed6\u5bf9\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\u7684\u6001\u5ea6\uff0c\u5374\u5df2\u7531 \u4ec7\u89c6\u800c\u53d8\u4e3a\u4eb2\u5bc6\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u5f00\u59cb\u4e86\u89e3\uff0c\u5728\u8fd9\u8eab\u8eaf\u7626\u5c0f\uff0c\u9762\u5bb9\u51b7\u5ced\u7684\u7537\u5b50\u5fc3\u4e2d\uff0c\u672a\u514d\u5c31\u6ca1\u6709\u4e00\u9897\u548c\u81ea\u5df1\u4e00\u6837 \u8c6a\u723d\u7684\u5fc3\uff0c\u751a\u81f3\u8fd8\u8fdc\u6bd4\u81ea\u5df1\u8c6a\u723d\u5f97\u591a\uff0c\u800c\u4ed6\u4e5f\u5f00\u59cb\u4e86\u89e3\uff0c\u7531\u4eba\u4eec\u7684\u5916\u9762\u53bb\u63a2\u6d4b\u5176\u5185\u5fc3\uff0c\u8fd9\u8be5 \u662f\u4e00\u4ef6\u591a\u4e48\u611a\u8822\u7684\u4e8b\u3002 \u3000\u3000\u800c\u88f4\u73cf\uff0c\u4ed6\u5e73\u65e5\u7684\u8a00\u8bed\u5374\u66f4\u5c11\u4e86\uff0c\u8fd9\u5e76\u4e0d\u662f\u7531\u4e8e\u4ed6\u4e0d\u559c\u6b22\u4e0e\u4e16\u4eba\u63a5\u7eb3\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u5df2\u65e0 \u65f6\u95f4\u8bf4\u8bdd\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u65e5\u65e5\u4e0d\u540c\uff0c\u6240\u4f20\u6388\u7ed9\u4ed6\u7684\u65b0\u77e5\u65b0\u5b66\u2014\u2014\u90a3\u771f\u662f\u4e00\u79cd\u4ee4\u4eba\u5341\u5206\u56f0\u60d1\u7684\u8bfe\u7a0b\uff0c \u4e5f\u662f\u4e00\u79cd\u5341\u5206\u8270\u82e6\u7684\u5b66\u4e60\uff0c\u5176\u4e2d\u5305\u62ec\u4e86\u7434\u3001\u68cb\u3001\u4e66\u3001\u753b\u3001\u8bd7\u3001\u8bcd\uff0c\u5305\u62ec\u4e86\u533b\u3001\u535c\u3001\u661f\u3001\u76f8\u3001 \u5f39\u3001\u5531\uff0c\u4e5f\u5305\u62ec\u4e86\u4e09\u575f\u4e94\u5178\uff0c\u661f\u4e66\u516d\u7ecf\uff0c\u66f4\u5305\u62ec\u4e86\u6697\u5668\u3001\u8f7b\u529f\u3001\u5251\u672f\u3001\u638c\u6cd5\uff0c\u6240\u6709\u4eba\u7c7b\u7684\u77e5 \u8bc6\uff0c\u7adf\u51e0\u4e4e\u5747\u6709\u6d89\u730e\u2014\u2014\u5df2\u8db3\u591f\u4ee4\u88f4\u73cf\u8d39\u5c3d\u5fc3\u667a\uff0c\u66f4\u4f55\u51b5\u4ed6\u81ea\u8eab\u8fd8\u8981\u5728\u90a3\u5999\u7edd\u4eba\u5bf0\u7684\u6b66\u529f\u79d8 \u5f55\u201c\u6d77\u5929\u79d8\u7b08\u201d\u4e0a\uff0c\u52a0\u4ee5\u5b66\u4e60\u548c\u63a2\u8ba8\uff0c\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u90a3\u4e00\u756a\u795e\u5947\u7684\u6539\u9020\uff0c\u5c31\u50cf\u662f\u4e00\u628a\u5947\u5f02\u7684 \u94a5\u5319\uff0c\u7a81\u7136\u4e3a\u4ed6\u6253\u5f00\u4e86\u6b66\u5b66\u7684\u5b9d\u5e93\u3002 \u3000\u3000\u4ed6\u81f3\u6b64\u65b9\u81ea\u77e5\u9053\uff0c\u6709\u5173\u6b66\u5b66\u7684\u77e5\u8bc6\uff0c\u662f\u4f55\u7b49\u6df1\u5965\u548c\u535a\u5927\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u65f6\u95f4\u53bb\u8bf4\u8bdd\u5462\uff1f \u3000\u3000\u5b66\u7684\u4eba\u56fa\u7136\u8270\u82e6\uff0c\u6559\u7684\u4eba\u4e5f\u672a\u89c1\u8f7b\u677e\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6e10\u6e10\u5f00\u59cb\u60ca\u5f02\u4e8e\u88f4\u73cf\u5b66\u4e60\u7684\u901f\u5ea6\uff0c \u4e5f\u6e10\u6e10\u5f00\u59cb\u53d1\u73b0\u81ea\u5df1\u5b66\u8bc6\u7684\u4e0d\u591f\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u4eec\u81ea\u5df1\u4e5f\u5f00\u59cb\u53bb\u5b66\u4e60\u4e86\uff0c\u4ed6\u4eec\u5f00\u59cb\u8d2d\u4e70\u5404\u79cd\u4e66\u7c4d\uff0c\u5f00\u59cb\u8bbe\u6cd5\u53bb\u5b66\u4e60\u5404\u79cd\u6280\u80fd\u3002 \u3000\u3000\u90a3\u8ddf\u7740\u4ed6\u4eec\u7684\u5947\u5f02\u884c\u5217\u4e4b\u4e2d\uff0c\u6709\u8bb8\u591a\u8eab\u6000\u4e00\u6280\u4e4b\u957f\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u5728\u6df1\u591c\u4e4b\u9645\uff0c\u4e5f\u5e38\u5e38\u4f1a \u8d6b\u7136\u89c1\u5230\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6765\u5230\u81ea\u5df1\u8eab\u65c1\uff0c\u800c\u6b63\u5f53\u4ed6\u4eec\u5fc3\u80c6\u7686\u4e27\uff0c\u9b42\u4e0d\u9644\u4f53\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e24\u4e2a\u51b7\u9177 \u7684\u5947\u4eba\uff0c\u4e3a\u4ec0\u4e48\u5bfb\u7740\u81ea\u5df1\u7684\u65f6\u5019\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5c31\u4f1a\u6e29\u548c\u5730\u544a\u8bc9\u4ed6\u4eec\uff0c\u5e0c\u671b\u4ed6\u4eec\u80fd\u5c06\u5b66\u4e60\u4ed6 \u4eec\u90a3\u79cd\u7edd\u6280\u7684\u65b9\u6cd5\u8bf4\u51fa\uff0c\u53c8\u4f1a\u5f88\u4e25\u5389\u5730\u8b66\u544a\u4ed6\u4eec\uff0c\u4e0d\u8981\u5c06\u6b64\u4e8b\u5ba3\u6cc4\u3002 \u3000\u3000\u4e8e\u662f\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7b2c\u4e8c\u5929\u5c31\u5c06\u5b66\u4e60\u8fd9\u79cd\u7edd\u6280\u7684\u65b9\u6cd5\uff0c\u4e00\u53e5\u4e0d\u6f0f\u5730\u544a\u8bc9\u88f4\u73cf\uff0c\u65f6\u5e38\u4ed6\u5144\u5f1f \u4e24\u4eba\u81ea\u5df1\u5c1a\u672a\u5b66\u4f1a\uff0c\u88f4\u73cf\u5374\u5df2\u5b66\u4f1a\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u65f6\u65e5\u8d8a\u4e45\uff0c\u5b66\u7684\u4eba\u6e10\u6e10\u8f7b\u677e\uff0c\u6559\u7684\u4eba\u5374\u6e10\u6e10\u56f0\u96be\u3002 \u3000\u3000\u8fd9\u4e00\u5207\uff0c\u4e5f\u4ff1\u90fd\u662f\u591a\u4e48\u5947\u5f02\u7684\u4e8b\uff0c\u5f53\u771f\u662f\u6b66\u6797\u6709\u53f2\u4ee5\u6765\uff0c\u4ece\u672a\u6709\u4e00\u5165\u5b66\u827a\u7684\u5386\u7a0b\u5982\u6b64\u5947 \u602a\u3002 \u3000\u3000\u5728\u90a3\u4e9b\u53e4\u8001\u76f8\u4f20\u7684\u6b66\u6797\u4f20\u8bf4\u4e2d\uff0c\u867d\u7136\u6709\u4e00\u4e9b\u6b66\u6797\u6210\u540d\u7684\u82f1\u96c4\u5b66\u827a\u7684\u7ecf\u8fc7\uff0c\u662f\u6781\u4e3a\u5947\u5f02 \u7684\uff0c\u751a\u81f3\u5947\u5f02\u5f97\u8fd1\u4e4e\u795e\u8bdd\u2014\u2014\u8fd9\u5176\u4e2d\u6709\u4eba\u662f\u5728\u65e0\u610f\u95ee\u8eab\u53d7\u91cd\u4f24\u540e\uff0c\u53c8\u5de7\u670d\u5f02\u679c\uff0c\u5076\u9047\u4ed9\u5e08\uff0c \u5f97\u4f20\u7edd\u6280\u3002 \u3000\u3000\u4e5f\u6709\u4eba\u662f\u56e0\u7f18\u51d1\u5de7\uff0c\u8eab\u6d89\u79d8\u5883\uff0c\u5f97\u5230\u524d\u8f88\u5947\u4eba\u7559\u4e0b\u7684\u795e\u5251\u79d8\u7b08\u3002 \u3000\u3000\u751a\u6216\u6709\u90a3\u524d\u8f88\u7559\u4e0b\u7684\u7075\u79bd\u5f02\u517d\uff0c\u800c\u5386\u7ecf\u9b54\u52ab\uff0c\u7ec8\u6210\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u53c8\u6709\u4eba\u662f\u8eab\u4e16\u5b64\u82e6\uff0c\u7236\u6bcd\u5144\u59b9\u4ff1\u90fd\u4e3a\u4ec7\u5bb6\u6240\u5bb3\uff0c\u81ea\u5df1\u51d1\u5de7\u88ab\u4e00\u4e2a\u5148\u4eba\u7684\u81f3\u53cb\u6551\u51fa\uff0c\u4e00\u8def \u8eb2\u907f\u4ec7\u5bb6\u7684\u6740\u5bb3\uff0c\u7ec8\u4e8e\u5c06\u4e4b\u9001\u5230\u4e00\u4f4d\u524d\u8f88\u5f02\u4eba\u7684\u5c45\u5904\uff0c\u53c8\u5fcd\u53d7\u4e86\u8bb8\u591a\u79cd\u8bd5\u63a2\uff0c\u624d\u62dc\u5728\u90a3\u5f02\u4eba \u95e8\u4e0b\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u4e9b\u4eba\u751f\u6027\u7edd\u9876\u806a\u660e\uff0c\u751a\u81f3\u4ee5\u5077\u3001\u9a97\u3001\u4ece\u5f3a\u8feb\u3001\u4ee5\u4ea4\u6362\u7b49\u65b9\u6cd5\u53bb\u5b66\u4e60\u6b66\u5bb6\u7edd\u6280\uff0c\u800c \u81f3\u5927\u6210\u3002 \u3000\u3000\u4f46\u662f\uff0c\u6240\u6709\u7684\u8fd9\u4e00\u5207\u5947\u9047\uff0c\u6bd4\u4e4b\u88f4\u73cf\u6240\u9047\uff0c\u4f3c\u4e4e\u90fd\u6709\u4e9b\u5931\u8272\u3002 \u3000\u3000\u4f46\u662f\uff0c\u88f4\u73cf\u8fd9\u4ece\u6765\u672a\u6709\u7684\u5947\u5999\u906d\u9047\uff0c\u5176\u5386\u7a0b\u4e5f\u662f\u56f0\u82e6\u800c\u8270\u96be\u7684\u2014\u2014\u4ed6\u601d\u7d22\u3001\u82e6\u5e72\u3001\u594b \u6597\u3001\u6325\u6c57\u3001\u6da6\u8272\u3001\u8eab\u4f53\u529b\u884c\u3001\u4e0d\u7720\u4e0d\u4f11\u3001\u52aa\u529b\u518d\u52aa\u529b\uff0c\u8270\u82e6\u5730\u5728\u81ea\u5df1\u5fc3\u4e2d\u5efa\u7b51\u8d77\u4e00\u4e2a\u8db3\u4ee5\u5bb9 \u7eb3\u6240\u6709\u77e5\u8bc6\u7684\u5b9d\u6bbf\u3002 \u3000\u3000\u65f6\u65e5\u7684\u6d41\u8f6c\uff0c\u5728\u4ed6\u7684\u773c\u4e2d\uff0c\u751a\u81f3\u5df2\u6beb\u65e0\u611f\u89c9\uff0c\u79cb\u53bb\u51ac\u81f3\uff0c\u51ac\u6b8b\u6625\u5f52\u2026\u2026\u8986\u5730\u7684\u51b0\u96ea\uff0c\u53d8 \u4e3a\u707f\u70c2\u7684\u6625\u82b1\uff0c\u86f0\u7720\u7684\u86c7\u866b\u82cf\u9192\uff0c\u65b0\u751f\u7684\u751f\u547d\u6210\u957f\uff0c\u539a\u91cd\u7684\u68c9\u8884\u53d8\u4e3a\u9002\u4f53\u7684\u8f7b\u886b\u2026\u2026 \u3000\u3000\u8fd9\u4e00\u5207\uff0c\u90fd\u5728\u4ed6\u4e0d\u7ecf\u610f\u95f4\u6d41\u53bb\u4e86\uff0c\u53d8\u5316\u4e86\uff0c\u53e4\u5f80\u4eca\u6765\uff0c\u4ece\u672a\u6709\u4e00\u4eba\u5b66\u4e60\u7684\u6001\u5ea6\uff0c\u6709\u4ed6\u8fd9 \u6837\u5fe0\u8bda\uff0c\u4e5f\u4ece\u672a\u6709\u4eba\u5982\u4ed6\u8fd9\u822c\u8270\u82e6\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u6240\u5b66\u4e60\u7684\uff0c\u662f\u4ed6\u6e34\u671b\u4e86\u8bb8\u591a\u8bb8\u591a\u5e74\u7684\u4e8b\uff0c\u4ed6\u5bf9\u77e5\u8bc6\u7684\u5d07\u656c\uff0c\u5c31\u6b63\u5982\u4e00\u4e2a\u4e5e\u4e10\u5bf9 \u91d1\u94b1\u7684\u5d07\u656c\u4e00\u6837\u54fa\u54fa\uff0c\u90a3\u751a\u81f3\u6bd4\u58eb\u4eba\u4e4b\u5bf9\u540d\u8a89\u5d07\u656c\uff0c\u7f8e\u4eba\u5bf9\u9752\u6625\u5d07\u656c\uff0c\u540d\u5c06\u4e4b\u5bf9\u6218\u529f\u5d07\u656c\u8fd8 \u8981\u5f3a\u70c8\u3002 \u3000\u3000\u4ed6\u5bb9\u8c8c\u4e0e\u6c14\u8d28\u4e0a\u7684\u53d8\u6362\uff0c\u4e5f\u6e10\u6e10\u66f4\u663e\u8457\u4e86\uff0c\u4ed6\u81ea\u5df1\u867d\u7136\u5e76\u672a\u611f\u89c9\u5230\uff0c\u4f46\u5728\u8ddf\u968f\u7740\u4ed6\u4eec\u7684 \u4e00\u4e9b\u6b66\u6797\u8c6a\u58eb\u773c\u4e2d\uff0c\u8fd9\u6539\u53d8\u662f\u6781\u4e3a\u89e6\u76ee\u7684\u2014\u2014\u8fd9\u4e9b\u4eba\u4e4b\u6240\u4ee5\u8fd8\u5728\u8ddf\u7740\u4ed6\uff0c\u9664\u4e86\u5bf9\u80dc\u8d1f\u7684\u5173\u5fc3 \u5916\uff0c\u8fd8\u662f\u4e3a\u4e86\u672c\u8eab\u7684\u8da3\u5473\u3002 \u3000\u3000\u9664\u4e86\u8fd9\u79cd\u5947\u5f02\u7684\u73b0\u8c61\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u80fd\u805a\u5408\u8fd9\u4e48\u591a\u670b\u53cb\u6216\u4ec7\u654c\uff1f\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u80fd\u65e5\u65e5 \u591c\u591c\uff0c\u968f\u65f6\u968f\u5730\uff0c\u89c1\u5230\u4e00\u4e9b\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u7684\u4eba\uff0c\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u7684\u4e8b\uff0c\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u80fd\u6bd4\u8fd9\u91cc \u66f4\u80fd\u6392\u9063\u65f6\u65e5\u4e0e\u5bc2\u5bde\uff1f \u3000\u3000\u6240\u4ee5\uff0c\u6709\u4e9b\u6839\u672c\u5bf9\u6b64\u4e8b\u7684\u80dc\u8d1f\u5e76\u65e0\u5341\u5206\u5174\u8da3\u7684\u4eba\uff0c\u4e5f\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\uff0c\u53c2\u52a0\u4e86\u8fd9\u5947\u5f02\u800c\u6709 \u8da3\u7684\u884c\u5217\u3002 \u3000\u3000\u8fd8\u6709\u4e9b\u6839\u672c\u4e0d\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u4e5f\u6765\u5230\u8fd9\u91cc\u770b\u770b\u70ed\u95f9\u2014\u2014\u4ed6\u4eec\u53c8\u6210\u4e86\u4e00\u4e2a\u96c6\u56e2\u3002 \u3000\u3000\u5546\u4eba\uff0c\u4e5f\u8d77\u4e86\u6597\u4e89\uff0c\u539f\u6709\u7684\uff0c\u6392\u65a5\u65b0\u6765\u7684\uff0c\u672c\u5730\u7684\uff0c\u6392\u65a5\u5916\u5730\u7684\uff0c\u4e8e\u662f\u5546\u4eba\u4e4b\u95f4\uff0c\u4e5f\u5f62 \u6210\u4e86\u4e00\u4e2a\u96c6\u56e2\u3002 \u3000\u3000\u8fd9\u884c\u5217\u81ea\u7136\u4f1a\u60ca\u52a8\u5b98\u5e9c\uff0c\u4f46\u662f\u53c8\u6709\u8c01\u80fd\u53d6\u7f14\uff1f\u4ed6\u4eec\u5e76\u6ca1\u6709\u72af\u6cd5\u5440\uff1f\u4f46\u662f\uff0c\u5374\u6709\u8bb8\u591a\u72af\u6cd5 \u7684\u4eba\uff0c\u60f3\u9690\u85cf\u5230\u8fd9\u5176\u4e2d\u6765\u3002 \u3000\u3000\u4e8e\u662f\u5404\u5730\u7684\u516c\u5dee\uff0c\u4e5f\u7ed3\u5408\u6210\u4e00\u4e2a\u96c6\u56e2\uff0c\u7f09\u6355\u7f6a\u72af\uff0c\u9632\u6b62\u53d8\u4e71\u2014\u2014\u540c\u65f6\uff0c\u987a\u4fbf\u770b\u770b\u8fd9\u7f55\u6709 \u7684\u70ed\u95f9\u3002 \u3000\u3000\u5947\u5f02\u2026\u2026\u5947\u5f02\u2026\u2026\u4e00\u5207\u4ff1\u662f\u5947\u5f02\u7684\uff0c\u5947\u5f02\u5f97\u7b80\u76f4\u4e0d\u662f\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9\uff0c\u66f4\u4e0d\u662f\u7b14\u58a8\u6240\u80fd\u63cf \u8ff0\u7684\u3002 \u3000\u3000\u751a\u81f3\u8fde\u5b63\u8282\u7684\u53d8\u5f02\u90fd\u6beb\u4e0d\u5173\u5fc3\u7684\u88f4\u73cf\uff0c\u81ea\u7136\u66f4\u4e0d\u4f1a\u7559\u610f\u5230\u6c5f\u6e56\u95f4\u7684\u98ce\u6ce2\uff0c\u6b66\u6797\u4e2d\u7684\u6d88 \u606f\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u5df2\u6e10\u6e10\u5f00\u59cb\u786e\u5b9a\u4e86\u5bf9\u88f4\u73cf\u7684\u89c2\u5ff5\uff1a\u201c\u88f4\u5927\u5148\u751f\uff0c\u7684\u786e\u6709\u60ca\u4eba\u7684\u7edd\u6280\uff0c\u56e0\u4e3a\u4ed6\u8a00\u8c08 \u4e3e\u6b62\uff0c\u4e00\u4e3e\u4e00\u52a8\uff0c\u90fd\u6709\u7740\u4e00\u79cd\u8d85\u5c18\u7edd\u4fd7\u7684\u6c14\u5ea6\uff0c\u76ee\u5149\u4e2d\u4e5f\u6709\u4e86\u95ea\u7535\u822c\u7684\u795e\u5149\uff0c\u6b65\u5c65\u95f4\u5374\u6709\u4e86 \u6cf0\u5c71\u822c\u7684\u575a\u5b9a\u4e0e\u6c89\u7a33\uff0c\u82e5\u975e\u8eab\u6000\u7edd\u6280\uff0c\u600e\u80fd\u5982\u6b64\uff1f\u201d \u3000\u3000\u8fd9\u4f20\u8a00\u4f7f\u5f97\u201c\u795e\u624b\u201d\u6218\u98de\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\uff0c\u65e2\u662f\u6697\u4e2d\u597d\u7b11\uff0c\u5374 \u53c8\u60ca\u7591\u4e0d\u5b9a\u3002 \u3000\u3000\u4e00\u5e74\u500f\u5ffd\u8fc7\u53bb\uff0c\u8fd9\u4e00\u5e74\u591a\u7684\u65f6\u65e5\uff0c\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e0e\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u4e4b\u95f4\uff0c\u8868\u9762\u770b\u6765\uff0c\u4f3c\u4e4e \u4e00\u65e0\u52a8\u9759\uff0c\u5176\u5b9e\u53cc\u65b9\u4ff1\u5728\u8c03\u52a8\u5b9e\u529b\uff0c\u517b\u7cbe\u84c4\u9510\uff0c\u51c6\u5907\u51fa\u624b\u4e00\u51fb\uff0c\u800c\u6210\u8d25\u80dc\u8d1f\u4e4b\u5206\uff0c\u4fbf\u5728\u8fd9\u4e00 \u51fb\u4e4b\u4e0a\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u626c\u8a00\u5929\u4e0b\uff0c\u8d4c\u7ea6\u5df2\u5b9a\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u66f4\u6539\uff0c\u201c\u6c5f\u5357\u540c\u76df\u201d\u968f\u65f6\u968f\u5730\u90fd\u4e3a\u6b64 \u4e8b\u51c6\u5907\u5168\u529b\u4e00\u4e89\u3002 \u3000\u3000\u4ed6\u6240\u9488\u5bf9\u7684\uff0c\u81ea\u7136\u5c31\u662f\u201c\u9f99\u5f62\u516b\u638c\u201d\u7684\u7231\u5973\u6a80\u6587\u742a\uff0c\u867d\u7136\u201c\u6c5f\u5357\u540c\u76df\u201d\u81ea\u8eab\u5df2\u8d77\u4e86\u5185 \u4e89\uff0c\u4f46\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u5b9e\u529b\uff0c\u4ecd\u662f\u4e0d\u5bb9\u5ffd\u89c6\u7684\uff0c\u957f\u6c5f\u4ee5\u5357\uff0c\u6cbf\u6c5f\u4e00\u5e26\uff0c\u6c5f\u9634\u3001\u6e56\u53e3\u3001\u9547\u6c5f\u3001 \u5357\u4eac\u3001\u829c\u6e56\u3001\u8d35\u6c60\u3001\u9a6c\u5f53\u3001\u6b66\u660c\u2026\u2026\u8fd9\u4e9b\u5927\u57ce\u5927\u9547\uff0c\u4ff1\u90fd\u6709\u201c\u795e\u5bf8\u201d\u6218\u98de\u9690\u85cf\u7740\u7684\u529b\u91cf\uff0c\u5374 \u4e5f\u4e0d\u77e5\u9053\u8fd9\u529b\u91cf\u7684\u6df1\u6d45\uff01 \u3000\u3000\u201c\u98de\u9f99\u9556\u5c40\u201d\u7a81\u5730\u51cf\u5c11\u4e86\u8d70\u9556\u7684\u6b21\u6570\uff0c\u8fd9\u5176\u4e2d\u4e14\u6709\u4e0d\u5c11\u7684\u65b0\u7684\u9556\u5c40\u5174\u8d77\uff0c\u201c\u98de\u9f99\u9556\u5c40\u201d \u4e2d\u7684\u9556\u5e08\uff0c\u4e5f\u6e10\u6e10\u6781\u5c11\u5728\u6c5f\u6e56\u9732\u9762\uff0c\u8001\u8c0b\u6df1\u7b97\u7684\u201c\u9f99\u5f62\u4eba\u638c\u201d\u6a80\u603b\u9556\u5934\u7a76\u7adf\u5728\u505a\u4ec0\u4e48\uff0c\u4ea6\u662f \u8c01\u4e5f\u65e0\u6cd5\u77e5\u9053\u3002 \u3000\u3000\u6709\u5173\u201c\u9f99\u5973\u201d\u6a80\u6587\u742a\u7684\u6d88\u606f\uff0c\u5728\u6c5f\u6e56\u4e2d\u66f4\u662f\u4e00\u8d77\u7edd\u8ff9\uff0c\u5979\u5728\u54ea\u91cc\uff1f \u3000\u3000\u5979\u5728\u505a\u4ec0\u4e48\uff1f\u8c01\u4e5f\u4e0d\u77e5\u9053\uff01 \u3000\u3000\u4f46\u662f\uff0c\u53cc\u65b9\u5251\u62d4\u5f29\u5f20\uff0c\u4e00\u89e6\u5373\u53d1\u7684\u60c5\u52bf\uff0c\u5374\u662f\u5c3d\u4eba\u7686\u77e5\u4e4b\u4e8b\uff0c\u8fd9\u9ed1\u3001\u81ea\u9053\u4e0a\uff0c\u5404\u636e\u4e00\u65b9 \u7684\u96c4\u4e3b\uff0c\u867d\u7136\u4e45\u5df2\u5404\u4e0d\u76f8\u5bb9\uff0c\u4f46\u5982\u6b64\u5c16\u9510\u7684\u5bf9\u7acb\uff0c\u76f4\u5230\u5982\u4eca\uff0c\u5374\u4ecd\u7136\u662f\u7b2c\u4e00\u6b21\u53d1\u751f\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u5176\u95f4\u7684\u6210\u4e0e\u8d25\uff0c\u5df2\u7edd\u65e0\u9009\u62e9\u7684\u4f59\u5730\uff0c\u662f\u4ee5\u8fd9\u66b4\u98ce\u96e8\u524d\u7684\u5e73\u9759\uff0c\u4e5f\u5c31\u66f4\u4ee4\u6c5f\u6e56\u4e2d\u4eba\u4e3a\u4e4b \u6ce8\uff01 \u3000\u3000\u6df1\u79cb\uff0c\u4e5d\u6708\u3002 \u3000\u3000\u96b6\u5c5e\u201c\u6c5f\u5357\u540c\u76df\u201d\u7684\u90fd\u9633\u5927\u8c6a\u201c\u5206\u6c34\u795e\u7280\u201d\u5218\u5f97\u7389\uff0c\u7a81\u5730\u5728\u6e56\u53e3\u88ab\u523a\u8eab\u6b7b\uff0c\u8eab\u4e2d\u4e03\u5904\u5200 \u4f24\uff0c\u6b7b\u72b6\u60e8\u4e0d\u5fcd\u7779\uff0c\u636e\u8bf4\u662f\u4e09\u4e2a\u9ed1\u8863\u5251\u5ba2\u4e0e\u201c\u5206\u6c34\u795e\u7280\u201d\u9152\u540e\u51b2\u7a81\uff0c\u5f15\u8d77\u51b3\u6218\uff0c\u5218\u5f97\u7389\u4e0d\u654c \u800c\u6b7b\u3002 \u3000\u3000\u4f46\u8fd9\u4e09\u4e2a\u9ed1\u8863\u5251\u5ba2\u662f\u8c01\uff1f\u5374\u662f\u6c5f\u6e56\u4e2d\u4eba\u8a00\u4eba\u6b8a\u7684\u8bdd\u9898\u3002 \u3000\u3000\u4e8b\u51fa\u4e0d\u4e45\uff0c\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u7684\u4e00\u7ea7\u9556\u5934\u201c\u864e\u5934\u94a9\u201d\u5510\u70c8\uff0c\u81ea\u6000\u5b81\u4e58\u8239\u6e21\u6c5f\uff0c\u7adf\u4e00\u53bb\u4e0d \u8fd4\u3002 \u3000\u3000\u4e09\u65e5\u4e4b\u540e\u5510\u70c8\u7684\u5c38\u8eab\uff0c\u5374\u5728\u5c0f\u5b64\u5c71\u4e0b\u7684\u6c5f\u6ee9\u4e0a\u88ab\u4eba\u53d1\u73b0\uff0c\u8179\u5927\u5982\u9f13\uff0c\u8179\u4e2d\u6da8\u6ee1\u4e86\u6cb3\u6c34\u3002 \u3000\u3000\u8fd9\u4e24\u4ef6\u4e8b\u63a5\u8fde\u53d1\u751f\uff0c\u6b66\u6797\u4e2d\u4eba\u4fbf\u4e00\u8d77\u7d27\u5f20\u8d77\u6765\uff0c\u4eba\u4eba\u4ff1\u5728\u6697\u4e2d\u731c\u6d4b\uff1a\u201c\u8fd9\u4e24\u4ef6\u4e8b\u662f\u5426\u4f1a \u6210\u4e3a\u6b66\u6797\u4e89\u9738\u4e4b\u6218\u7684\u5bfc\u706b\u7ebf\uff1f\u201d \u3000\u3000\u5c31\u5728\u6b66\u6797\u4e2d\u4eba\u5c4f\u606f\u671f\u5f85\u4e4b\u4e2d\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u7a81\u5730\u6563\u53d1\u4e00\u4e07\u516b\u5343\u5f20\u6b66\u6797\u98de\u67ec\uff0c\u626c\u8a00\u5929\u4e0b\uff1a \u201c\u51e1\u6709\u2018\u98de\u9f99\u9556\u5c40\u2019\u9556\u65d7\u4e4b\u8f66\u9a6c\uff0c\u5728\u2018\u6c5f\u5357\u540c\u76df\u2019\u6240\u5c5e\u9053\u4e0a\u884c\u8d70\uff0c\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u4e0d\u8d1f\u5b89\u5168\u4e4b \u8d23\u3002\u201d \u3000\u3000\u8fd9\u5c31\u50cf\u662f\u4e00\u65b9\u5de8\u77f3\uff0c\u7a81\u5730\u6295\u4e0b\u4e86\u672c\u5df2\u751f\u4e86\u6d9f\u6f2a\u7684\u6c60\u6c34\u4e2d\uff0c\u4e5f\u4f7f\u5f97\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\uff0c\u4ff1\u90fd\u4e3a \u4e4b\u4e00\u9707\u3002 \u3000\u3000\u4e00\u65e5\u4e4b\u540e\uff0c\u201c\u91d1\u9e21\u5e2e\u201d\u9996\u9886\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u7a81\u4e5f\u6563\u51fa\u53f7\u79f0\u4e00\u4e07\u516b\u5343\u5f20\u7684\u201c\u6b66\u6797\u98de\u67ec\u201d\uff0c \u4ed6\u626c\u8a00\u5929\u4e0b\uff1a\u201c\u91d1\u9e21\u5e2e\uff0c\u98ce\u96e8\u5982\u6666\u5802\uff0c\u5728\u8d4c\u7ea6\u4e2d\u867d\u4e0e\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u6811\u7acb\uff0c\u4f46\u4e00\u5207\u201d\u6c5f\u5357\u540c\u76df \u2018\u7684\u51b3\u8bae\uff0c\u2019\u91d1\u9e21\u5e2e\u2018\u4ff1\u90fd\u670d\u4ece\uff01\u201c\u4e8e\u662f\u201d\u4e03\u5de7\u5c71\u5e84\u201c\u7acb\u523b\u4e5f\u4e0d\u6708\u201d\u7f04\u9ed8\uff0c\u53d1\u51fa\u4e86\u540c\u6837\u7684\u5ba3 \u8a00\uff0c\u672c\u5df2\u5206\u6563\u7684\u201c\u6c5f\u5357\u540c\u76df\u201d\u4e8e\u662f\u53c8\u544a\u590d\u5408\uff01\u6b66\u6797\u7fa4\u8c6a\u7684\u76ee\u5149\uff0c\u4fbf\u4e00\u8d77\u8f6c\u5411\u201c\u98de\u9f99\u9556\u5c40\u201d\uff0c \u54ea\u77e5\u201c\u98de\u9f99\u9556\u5c40\u201d\u7adf\u4ecd\u662f\u4e00\u65e0\u52a8\u9759\uff0c\u5c31\u50cf\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u4f3c\u7684\u3002\u8fd9\u79cd\u7279\u6b8a\u7684\u6c89\u9759\uff0c\u4f7f\u5f97 \u201c\u9f99\u5f62\u516b\u638c\u201d\u5728\u6b66\u6797\u4e2d\u4eba\u773c\u4e2d\u66f4\u52a0\u795e\u79d8\uff1b\u800c\u5728\u8fd9\u79cd\u5f02\u6837\u7684\u7d27\u5f20\u72b6\u6001\u4e4b\u4e2d\uff0c\u201c\u6c5f\u5357\u540c\u76df\u201d\u201c\u76df \u4e3b\u201d\u201c\u88f4\u5927\u5148\u751f\u201d\u7684\u6210\u8d25\uff0c\u751a\u81f3\u4ed6\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u4e5f\u5c31\u5206\u5916\u5730\u5f15\u4eba\u6ce8\u76ee\u3002\u51ac\u6b8b\uff0c\u96ea\u878d\uff0c\u6625\u98ce\u53c8 \u8d77\u3002\u88f4\u73cf\u7684\u884c\u5217\uff0c\u7531\u91c7\u77f3\u6e21\u6c5f\u540e\uff0c\u8fc7\u6b63\u9633\u5173\uff0c\u5df2\u5165\u4f0f\u725b\u5c71\u533a\u3002\u6625\u5bd2\u6599\u5ced\uff0c\u665a\u98ce\u66f4\u5bd2\uff0c\u4f0f\u725b\u5c71 \u9e93\uff0c\u4e00\u7247\u8424\u706b\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u6709\u5982\u6ee1\u5929\u7e41\u661f\uff0c\u660e\u706d\u95ea\u70c1\uff0c\u95ea\u52a8\u7684\u706b\u5149\uff0c\u5374\u53c8\u50cf\u662f\u76db\u590f\u6c60\u5858\u4e2d\u7684 \u6ee1\u6c60\u7ea2\u83b2\u3002\u9ed1\u8272\u4e4b\u4e2d\uff0c\u7a81\u6709\u4e09\u5339\u5feb\u9a6c\uff0c\u72c2\u5954\u800c\u5165\u4f0f\u725b\u5c71\u533a\uff0c\u9a6c\u4e0a\u4eba\u75be\u88c5\u52b2\u670d\uff0c\u6ee1\u9762\u98ce\u5c18\uff0c\u8d8a \u8fc7\u5341\u4f59\u5806\u91ce\u706b\uff0c\u65b9\u81ea\u7ffb\u8eab\u4e0b\u9a6c\u3002\u5239\u90a3\u95f4\uff0c\u4ed6\u4eec\u56db\u4fa7\u4fbf\u5df2\u56f4\u6ee1\u4e86\u4eba\u7fa4\uff0c\u4f17\u53e3\u7eb7\u7eb7\u5728\u95ee\u9053\uff1a\u201c\u60c5 \u52bf\u5982\u4f55\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u4e09\u9a91\u4e2d\u7684\u4e00\u4e2a\u7626\u957f\u6c49\u4e88\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u5148\u81ea\u53cd\u95ee\uff1a\u201c\u6b64\u523b\u7684\u60c5\u51b5\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u7acb\u523b\u6709\u4eba\u4e89\u5148\u56de\u7b54\uff1a\u201c\u8fd8\u662f\u5206\u4e0d\u51fa\u80dc\u8d1f\uff0c\u53ea\u662f\u2018\u88f4\u5927\u5148\u751f\u2019\u795e\u6001\u66f4\u52a0\u6c89\u7a33\uff0c\u2018\u51b7\u8c37\u53cc \u6728\u2019\u5374\u4f3c\u5df2\u6709\u4e9b\u614c\u5f20\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u53c8\u6709\u4eba\u7126\u6025\u4ed6\u8bf4\u9053\uff1a\u201c\u67f3\u8001\u5927\uff0c\u8fd8\u662f\u5feb\u8bf4\u4f60\u7684\u5427\u3002\u201d \u3000\u3000\u7626\u957f\u6c49\u5b50\u201c\u67f3\u8001\u5927\u201d\u89e3\u4e0b\u4e86\u8eab\u4e0a\u7684\u98ce\u6c05\uff0c\u5bfb\u4e86\u4e00\u5806\u5927\u706b\u5750\u4e0b\uff0c\u4ef0\u9996\u559d\u4e86\u51e0\u53e3\u70c8\u9152\uff0c\u53c8\u6495 \u5f00\u4e00\u53ea\u9e21\u817f\uff0c\u4ed4\u7ec6\u5480\u56bc\uff0c\u65b9\u81ea\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u6e56\u5317\u5b9c\u660c\u5e9c\u7684\u2018\u98de\u9f99\u9556\u5c40\u2019\uff0c\u53c8\u5728\u6df1\u591c\u4e4b\u4e2d\uff0c \u88ab\u4eba\u62c6\u6bc1\uff0c\u5c40\u91cc\u4e00\u5341\u516d\u53e3\u7537\u5973\u8001\u5e7c\uff0c\u88ab\u6740\u5f97\u9e21\u5927\u4e0d\u7559\uff0c\u95e8\u53e3\u7684\u91d1\u5b57\u62db\u724c\uff0c\u4e5f\u88ab\u653e\u706b\u70e7\u4e86\uff0c\u8fde \u4e0a\u6b21\u8944\u9633\u548c\u6c49\u9633\u4e24\u4ef6\u4e8b\uff0c\u2018\u98de\u9f99\u9556\u5c40\u2019\u5df2\u6b7b\u4e86\u4e94\u5341\u4e94\u6761\u4eba\u547d\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u9635\u60ca\u559f\uff0c\u53ea\u542c\u4ed6\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u5e72\u4e0b\u8fd9\u6863\u4e8b\u7684\u4eba\uff0c\u624b\u6cd5\u5e72\u51c0\u5229\u843d\uff0c\u975e\u4f46\u4e0d\u7559\u4e00\u6761\u6d3b \u53e3\uff0c\u4e5f\u4e0d\u7559\u4e00\u70b9\u75d5\u8ff9\uff0c\u663e\u89c1\u662f\u9ed1\u9053\u4e0a\u7684\u9ad8\u624b\uff0c\u6c5f\u6e56\u4e2d\u8c01\u4e5f\u731c\u4e0d\u51fa\u662f\u8c01\u6709\u8fd9\u4e48\u5927\u7684\u624b\u7b14\u2014\u2014\u201d \u8bf4\u5230\u8fd9\u91cc\uff0c\u8bed\u58f0\u5ffd\u7136\u8f6c\u8f7b\u3002 \u3000\u3000\u9053\uff1a\u201c\u6709\u4eba\u731c\u662f\u2018\u6218\u795e\u624b\u2019\u5df2\u5230\u4e86\u6c5f\u5317\uff0c\u4eb2\u81ea\u5e72\u51fa\u6765\u7684\u4e8b\u3002\u201d \u3000\u3000\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u60ca\u547c\uff0c\u7eb7\u4e71\uff0c\u6709\u4eba\u53f9\u606f\u7740\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u5c82\u4e0d\u662f\u5feb\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u67f3\u8001\u5927\u201d\u70b9\u5934\u9053\uff1a\u201c\u5feb\u4e86\uff0c\u5feb\u4e86\uff0c\u4f46\u662f\u2026\u2026\u636e\u4e00\u822c\u4eba\u63a8\u6d4b\uff0c\u65e0\u8bba\u600e\u4e48\u5feb\uff0c\u4e5f\u8981\u7b49\u8fd9\u91cc \u7684\u5206\u51fa\u80dc\u8d1f\u6765\uff0c\u4ed6\u4eec\u624d\u4f1a\u52a8\u624b\u3002\u201d \u3000\u3000\u4f17\u4eba\u8bae\u8bba\u7eb7\u7eb7\uff0c\u53c8\u56de\u5230\u539f\u6765\u6240\u5750\u7684\u5730\u65b9\u3002\u53ea\u542c\u4eba\u4e1b\u4e4b\u4e2d\u7684\u8bed\u58f0\uff0c\u53c8\u5728\u4e92\u76f8\u4f4e\u58f0\u95ee\u9053\uff1a \u201c\u662f\u4e0d\u662f\u2018\u6545\u795e\u624b\u2019\uff1f\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u5feb\u4e86\uff1f\u201d\u201c\u4f60\u731c\u731c\uff0c\u88f4\u5927\u5148\u751f\u7a76\u7adf\u662f\u80dc\uff1f\u662f\u8d25\uff1f\u201d \u3000\u3000\u8f83\u8fdc\uff0c\u4e5f\u8f83\u9ad8\u7684\u4e00\u5757\u5c71\u5730\u4e0a\uff0c\u5b64\u96f6\u96f6\u5730\u5347\u7740\u4e00\u5806\u70c8\u706b\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5144\u5f1f\u4e8c\u4eba\uff0c\u5bf9\u5750\u5728\u70c8\u706b\u65c1\uff0c\u9065\u89c6\u7740\u8fd9\u6ee1\u5c71\u7684\u4eba\u5f71\uff0c\u4e00\u9635\u9635\u7b11\u8bed\u4eba\u58f0\uff0c\u968f\u98ce \u800c\u6765\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u4e34\u98ce\u628a\u76cf\uff0c\u5ffd\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u6211\u5144\u5f1f\uff0c\u8001\u4eba\u7adf\u4e0d\u5bc2\u5bde\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u4ea6\u81ea\u7b11\u9053\uff1a\u201c\u4eba\u751f\u767e\u5e74\uff0c\u80fd\u9047\u5230\u8fd9\u822c\u76db\u4e8b\uff0c\u603b\u8be5\u4e5f\u7b97\u4e0d\u865a\u6b64\u751f\u4e86\u5427\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u54bd\u4e0b\u4e00\u53e3\u767d\u9152\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4e4b\u4e2d\uff0c\u53ea\u6015\u5b9a\u6709\u8bb8\u591a\u4eba\u4f1a\u6697\u4e2d\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u6211\u5144\u5f1f\u4e24 \u4eba\uff0c\u4e3a\u4ec0\u4e48\u65e2\u4e0d\u56de\u5bb6\uff0c\u4e5f\u65e0\u6446\u8131\u8fd9\u7fa4\u2018\u5c3e\u5df4\u2019\u4e4b\u610f\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u4e00\u4e0b\uff0c\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u53ea\u6015\u771f\u6ca1\u6709\u4eba\u4f1a\u731c\u5230\uff0c\u6211\u4eec\u5144\u5f1f\u800c\u4eba\u7adf\u662f\u4e3a\u4e86\u8d2a\u56fe\u70ed \u95f9\u3002\u201d \u3000\u3000\u4e24\u4eba\u76f8\u89c6\u4e00\u7b11\uff0c\u76ee\u5149\u9f50\u5730\u8f6c\u5411\u4e94\u4e08\u5916\u76d8\u819d\u5782\u76ee\u7aef\u5750\u7684\u88f4\u73cf\u3002 \u3000\u3000\u591c\u8272\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4ed6\u795e\u6001\u7aef\u5e84\uff0c\u4e00\u5fc3\u5411\u5929\uff0c\u9762\u8272\u4e4b\u95f4\uff0c\u795e\u6c14\u6676\u7136\uff0c\u6839\u672c\u542c\u4e0d\u5230\u8fd9\u6742\u4e71\u7684\u8bed \u58f0\uff0c\u4e5f\u672a\u66fe\u611f\u89c9\u5230\u8fd9\u88ad\u4eba\u7684\u5bd2\u610f\uff1b\u53cd\u800c\u4f3c\u4e4e\u6709\u4e00\u7f15\u70ed\u6c14\uff0c\u81ea\u4ed6\u5934\u9876\u4e4b\u4e0a\uff0c\u8885\u8885\u5347\u8d77\uff0c\u968f\u98ce\u56db \u6563\u3002 \u3000\u3000\u51b7\u67af\u6728\u5fae\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4f20\u8a00\uff0c\u66fe\u8bf4\u6709\u4e9b\u5947\u624d\u5f02\u80fd\u4e4b\u58eb\u7684\u6b66\u529f\uff0c\u80fd\u65e5\u8fdb\u5343\u91cc\uff0c\u6211\u8d77 \u5148\u8fd8\u4e0d\u6df1\u4fe1\u3002\u4f46\u5982\u4eca\u2026\u2026\u5509\uff0c\u89c1\u4e86\u4ed6\u7684\u6b66\u529f\u8fdb\u5883\uff0c\u53c8\u4f55\u6b62\u4e00\u65e5\u5343\u91cc\uff01\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e14\u83ab\u9ad8\u5174\uff0c\u518d\u8fc7\u4e00\u9635\uff0c\u6211\u770b\u4f60\u518d\u62ff\u4ec0\u4e48\u53bb\u6559\u4ed6\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u4ea6\u81ea\u542b\u7b11\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u8fd9\u6b21\u8d4c\u7ea6\uff0c\u6211\u5012\u5b81\u613f\u843d\u8d25\uff0c\u4f60\u6211\u82e5\u662f\u8d25\u4e86\uff0c\u672c\u662f\u6211\u9ad8\u5174 \u4e4b\u6781\u7684\u4e8b\u3002\u53ea\u662f\u2026\u2026\u5509\uff01\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u76ee\u5149\u56db\u626b\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6b64\u60c5\u6b64\u666f\uff0c\u96be\u4ee5\u518d\u89c1\uff0c\u662f\u4ee5\u6211\u7684\u5e0c\u671b\u53ea\u80fd\u62d6\u4e9b\u65f6\u65e5 \u800c\u5df2\u3002\u201d \u3000\u3000\u5144\u5f1f\u4e24\u4eba\uff0c\u53c8\u81ea\u76f8\u89c6\u4e00\u7b11\uff0c\u9065\u89c6\u5c71\u4e0b\u4eba\u5f71\uff0c\u9ed8\u9ed8\u5730\u4eab\u53d7\u7740\u8fd9\u79cd\u5947\u5f02\u7684\u60c5\u8da3\uff0c\u82cd\u7a79\u95f4\u5347\u8d77 \u51e0\u9897\u660e\u661f\uff0c\u4e5f\u53ea\u6709\u8fd9\u51e0\u9897\u660e\u661f\uff0c\u624d\u80fd\u7aa5\u7834\u4ed6\u5144\u5f1f\u7684\u771f\u60c5\u3002 \u3000\u3000\u5fae\u98ce\u5439\u62c2\uff0c\u5239\u90a3\u95f4\u5c71\u4e0b\u4eba\u5f71\uff0c\u7a81\u5730\u4e00\u9635\u5927\u4e71\uff0c\u5750\u7740\u7684\u4eba\uff0c\u5168\u90e8\u7ad9\u4e86\u8d77\u6765\uff0c\u51b7\u67af\u6728\u795e\u8272\u4e00 \u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53ea\u542c\u5c71\u4e0b\u60ca\u547c\u4e4b\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u6e10\u6e10\u6e05\u6670\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4ed4\u7ec6\u51dd\u542c\u4e00\u9635\uff0c\u795e\u8272\u66f4\u662f\u5927\u53d8\uff0c\u539f\u6765\u5c71\u4e0b\u7684\u60ca\u547c\u4e4b\u58f0\uff0c\u558a\u7684\u7adf\u7136\u662f\u201c\u2018\u9f99 \u5f62\u516b\u638c\u2019\u6765\u4e86\u201d\uff01 \u3000\u3000\u201c\u6a80\u603b\u9556\u5934\u6765\u4e86\uff01\u201d \u3000\u3000\u706b\u5149\u4e00\u95ee\uff0c\u4e24\u6761\u4eba\u5f71\uff0c\u6025\u7a9c\u800c\u4e0a\uff0c\u4e00\u4e2a\u662f\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\uff0c\u4e00\u4e2a\u7adf\u662f\u90a3\u201c\u516b\u5366\u638c\u201d\u67f3 \u8f89\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8fdc\u8fdc\u7acb\u5728\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e94\u4e08\u4e4b\u5916\uff0c\u5fae\u4e00\u62b1\u62f3\uff0c\u9f50\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\uff0c\u2018\u98de\u9f99\u9556\u5c40\u2019 \u6a80\u603b\u9556\u5934\uff0c\u7279\u6765\u7ea6\u5019\uff01\u201d \u3000\u3000\u51b7\u6c0f\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u77e5\u662f\u9ad8\u5174\u6291\u6216\u662f\u96be\u53d7\uff0c\u77ed\u77ed\u4e00\u5e74\u4e4b\u95f4\uff0c\u201c\u88f4\u5927\u5148\u751f\u201d\u7684\u540d \u5934\uff0c\u7ade\u5df2\u6bd4\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8fd8\u8981\u54cd\u4eae\u3002\u8fd9\u5b9e\u5728\u662f\u8c01\u4e5f\u6ca1\u6709\u60f3\u5230\u7684\u4e8b\uff0c\u5176\u5b9e\u6c5f\u6e56\u4e2d\u7684\u4eba\u4e8b\u53d8\u8fc1\uff0c \u53c8\u6709\u8c01\u80fd\u60f3\u5f97\u5230\u5462\uff1f \u3000\u3000\u559d\u58f0\u843d\u5904\uff0c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\u4e24\u6761\u4eba\u5f71\u4e00\u5206\uff0c\u5782\u624b\u8083\u7acb\uff0c\u795e\u8272\u95f4\u5341\u5206 \u606d\u8c28\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7aef\u5750\u4e0d\u52a8\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u88f4\u73cf\u7adf\u4e5f\u6709\u5982\u672a\u89c1\u672a\u95fb\uff0c\u4f9d\u7136\u7aef\u5750\uff0c\u4ed6\u6b64\u523b\u795e\u6c14\u5408 \u4e00\u201c\uff0c\u53cd\u749e\u5f52\u771f\uff0c\u4fbf\u662f\u6cf0\u5c71\u5d29\u5728\u4ed6\u9762\u524d\uff0c\u4ed6\u795e\u6001\u4e5f\u4e0d\u4f1a\u4e3a\u4e4b\u751f\u53d8\u3002\u5c71\u4e0b\u7684\u7fa4\u8c6a\uff0c\u4ea6\u81ea\u5782\u624b\u5206 \u7acb\u4e24\u65c1\uff0c\u8ba9\u5f00\u4e00\u6761\u9053\u8def\u3002\u706b\u5149\u95ea\u70c1\u4e2d\uff0c\u54b8\u9707\u5929\u4e0b\u7684\u201d\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u8eab\u62ab\u91d1\u6c05\uff0c\u6b65\u5c65\u6c89 \u91cd\uff0c\u4e00\u6b65\u4e00\u6b65\uff0c\u7a7f\u8fc7\u4eba\u4e1b\u3002\u4ed6\u5634\u89d2\u867d\u7136\u5e26\u7740\u90a3\u4e00\u4efd\u8c26\u865a\u7684\u5fae\u7b11\uff0c\u4e0d\u4f4f\u5411\u4e24\u4fa7\u7684\u6b66\u6797\u7fa4\u8c6a\u9894\u9996 \u4e3a\u793c\uff0c\u4f46\u76ee\u5149\u4e2d\u5374\u542b\u8574\u7740\u4e00\u79cd\u6151\u4eba\u7684\u54b8\u4eea\uff0c\u4f7f\u5f97\u4efb\u4f55\u4eba\u90fd\u4e0d\u6562\u5bf9\u8fd9\u58f0\u9707\u6b66\u6797\u7684\u4e00\u4ee3\u5927\u8c6a\uff0c\u751f \u51fa\u4e1d\u6beb\u8f7b\u89c6\u3001\u4e0d\u656c\u4e4b\u5fc3\u3002\u4e09\u6761\u9ed1\u8863\u75be\u670d\uff0c\u8170\u60ac\u5229\u5203\u7684\u6c49\u5b50\uff0c\u4ea6\u6b65\u4ea6\u8d8b\uff0c\u7d27\u8ddf\u5728\u4ed6\u8eab\u540e\u4e94\u6b65\u4e4b \u5185\u3002\u4e00\u4e2a\u8eab\u8eaf\u9880\u957f\u3001\u98a7\u9aa8\u9ad8\u8038\u3001\u76ee\u4e2d\u51cc\u51cc\u548c\u5149\u3001\u8170\u60ac\u5947\u5f62\u957f\u5251\u7adf\u6709\u56db\u5c3a\u957f\u77ed\uff0c\u7fa4\u8c6a\u4ff1\u90fd\u8ba4 \u5f97\uff0c\u6b64\u4eba\u6b63\u662f\u4eac\u57ce\u201d\u98de\u9f99\u9556\u5c40\u201c\u7684\u9996\u5ea7\u9556\u5934\uff0c\u6b66\u6797\u4e2d\u58f0\u540d\u5353\u8457\u7684\u786c\u624b\uff0c\u201d\u957f\u8679\u5251\u201c\u8fb9\u5c11\u884d\u3002 \u53e6\u4e00\u4eba\u8eab\u8eaf\u867d\u77ed\u5c0f\uff0c\u4f46\u6b65\u5c65\u5374\u5206\u5916\u77eb\u5065\uff0c\u77ed\u9894\u73af\u65e5\uff0c\u6ee1\u53e3\u866c\u987b\uff0c\u624b\u957f\u51e0\u8fbe\u819d\u4e0a\uff0c\u8170\u95f4\u659c\u63d2\u7740 \u4e00\u67c4\u201d\u4e38\u73af\u9b3c\u5934\u5927\u5200\u201c\uff0c\u7a7a\u5203\u65e0\u9798\uff0c\u5200\u5149\u8000\u76ee\uff0c\u6bcf\u8d70\u4e00\u6b65\uff0c\u5200\u4e0a\u94a2\u73af\u53ee\u5f53\u4f5c\u54cd\uff0c\u5b9b\u5982\u6444\u9b42\u4e4b \u94c3\u3002\u6b64\u4eba\u5728\u6c5f\u6e56\u4e2d\u4ea6\u662f\u5927\u5927\u6709\u540d\uff0c\u4e43\u662f\u4e24\u6cb3\u5200\u6cd5\u540d\u5bb6\uff0c\u4ee5\u201d\u4e03\u5341\u4e8c\u8def\u6444\u9b42\u593a\u547d\u5200\u201c\u8d70\u904d\u5929\u4e0b \u7684\u201d\u6444\u9b42\u5200\u201c\u7f57\u4e49\u3002\u8fd9\u5176\u4e2d\u6700\u6700\u5f15\u4eba\u6ce8\u76ee\u7684\uff0c\u5374\u662f\u7d27\u8ddf\u5728\u201d\u9f99\u5f62\u516b\u638c\u201c\u8eab\u540e\u7684\u4e00\u4e2a\u8eab\u957f\u4e03 \u5c3a\u3001\u9762\u5982\u9505\u5e95\u7684\u5f6a\u5f62\u5c11\u5e74\uff01\u4ed6\u4e0d\u4f46\u8eab\u5f62\u5f6a\u58ee\uff0c\u8fe5\u5f02\u7fa4\u6d41\uff0c\u751f\u50cf\u66f4\u662f\u4ee4\u4eba\u5fc3\u60ca\uff0c\u9614\u53e3\u6df1\u8170\uff0c\u9e70 \u76ee\u9e5e\u9f3b\uff0c\u9edd\u9ed1\u7684\u9762\u76ee\uff0c\u5168\u65e0\u4e00\u4e1d\u8868\u60c5\uff0c\u4eff\u4f5b\u7b3c\u7f69\u7740\u4e00\u5c42\u5bd2\u971c\uff0c\u8170\u7554\u4e45\u6302\u7740\u4e00\u53ea\u5f62\u72b6\u5947\u7279\u3001\u7eff \u9ca8\u9c7c\u76ae\u7684\u957f\u5957\uff0c\u76ee\u4e0b\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u5374\u65e0\u4e00\u4eba\u731c\u5f97\u51fa\u8fd9\u91cc\u9762\u85cf\u7684\u662f\u4ec0\u4e48\u5175\u5203\uff0c\u66f4\u65e0\u4e00\u4eba\u731c\u5f97\u4e86 \u8fd9\u5c11\u5e74\u7684\u6765\u5386\u3002\u4ed6\u8eab\u8eaf\u867d\u91cd\uff0c\u6b65\u5c65\u5374\u6781\u8f7b\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u79fb\u52a8\uff0c\u5b9b\u5982\u811a\u4e0b\u6709\u4eba\u6258\u7740\u4f3c\u7684\uff0c\u5f53\u771f \u5168\u65e0\u4e00\u4e1d\u811a\u6b65\u4e4b\u58f0\u3002\u7fa4\u8c6a\u53c8\u4e0d\u7981\u5728\u6697\u4e2d\u7a83\u7a83\u79c1\u8bae\u3002\u201d\u6b64\u4eba\u662f\u8c01\uff1f\u96be\u9053\u4e5f\u662f\u2018\u98de\u9f99\u9556\u5c40\u2019\u65b0\u624e \u8d77\u7684\u9556\u5e08\uff1f\u201c\u8fd9\u56db\u4eba\u811a\u6b65\u4e0d\u505c\uff0c\u7b14\u76f4\u8d70\u4e0a\u4e86\u88f4\u73cf\u4e0e\u201d\u51b7\u8c37\u53cc\u6728\u201c\u505c\u7559\u7684\u5c0f\u5c0f\u5c71\u5761\u3002\u201d\u9f99\u5f62\u516b \u638c\u201c\u6a80\u660e\u5a01\u76ee\u4e00\u626b\uff0c\u89c1\u5230\u7aef\u5750\u5982\u6545\u7684\u201d\u51b7\u8c37\u53cc\u6728\u201c\uff0c\u6d53\u7709\u5fae\u5fae\u4e00\u8038\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u7a81\u5730\u89c1\u5230\u4e86 \u72b9\u5728\u9759\u5750\u8c03\u606f\u7684\u88f4\u73cf\u3002\u4ed6\u9875\u4e0a\u90a3\u79cd\u5b89\u7965\u800c\u53c8\u5b81\u9759\u7684\u795e\u6001\uff0c\u7adf\u4f7f\u5f97\u8fd9\u6b66\u6797\u5927\u8c6a\u9762\u8272\u4e3a\u4e4b\u4e00\u53d8\uff0c \u4f46\u77ac\u5373\u6062\u590d\u81ea\u7136\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u88f4\u8d24\u4f84\uff0c\u4f60\u597d\u4e48\uff1f\u201d \u3000\u3000\u7b11\u58f0\u9ad8\u4ea2\u6e05\u6717\uff0c\u51e0\u53ef\u76f4\u51b2\u9704\u6c49\uff0c\u76f4\u9707\u5f97\u5c71\u4e0b\u7fa4\u8c6a\u7684\u8033\u4e2d\uff0c\u90fd\u4e3a\u4e4b\u201c\u55e1\u55e1\u201d\u4f5c\u54cd\uff0c\u56db\u5c71\u56de \u5e94\uff0c\u66f4\u662f\u8fde\u7ef5\u4e0d\u7edd\u3002 \u3000\u3000\u54ea\u77e5\u88f4\u73cf\u5374\u4ecd\u662f\u4e0d\u95fb\u4e0d\u95ee\uff0c\u9759\u5750\u5982\u6545\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u8eab\u540e\u7684\u5f6a\u5f62\u5c11\u5e74\u7a81\u5730\u76ee\u5149\u4e00\u4eae\uff0c\u5634\u89d2\u7275\u52a8\uff0c\u9732\u51fa\u4e00\u53e3\u68ee\u68ee\u767d\u7259\uff0c\u8eab\u5f62\u4e00 \u95ea\uff0c\u6025\u5730\u5411\u88f4\u73cf\u63a0\u53bb\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u9762\u8272\u4e00\u6c89\uff0c\u80a9\u5934\u5fae\u8038\uff0c\u6a2a\u98de\u800c\u8d77\uff0c\u65b9\u5f85\u6321\u4f4f\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5e74\u8eab\u5f62\u4e4b\u5feb\uff0c\u7ade\u662f\u9a87\u4eba\u542c\u95fb\uff0c\u53ea\u89c9\u201c\u55d6\u201d\u5730\u4e00\u9635\u98ce\u54cd\uff0c\u5df2\u81ea\u51b7\u5bd2\u7af9\u8eab\u4fa7\u5982\u98de\u63a0 \u8fc7\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u5fae\u5fae\u4e00\u60ca\uff0c\u970d\u7136\u8f6c\u8eab\uff0c\u53ea\u89c1\u4ed6\u63a0\u5230\u88f4\u73cf\u8eab\u524d\uff0c\u4e3e\u8d77\u624b\u638c\uff0c\u6b63\u5f85\u8981\u5411\u88f4\u73cf\u5f53\u5934\u62cd \u53bb\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e0d\u89c9\u9f50\u5730\u8f7b\u53f1\u4e00\u58f0\uff0c\u5404\u5404\u5c55\u52a8\u8eab\u5f62\uff0c\u65bd\u5c55\u5168\u529b\uff0c\u5411\u8fd9\u8eab\u6cd5\u5947\u5feb\u7684\u5c11\u5e74\u8eab\u540e \u6251\u53bb\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u4e00\u4e00\u626c\uff0c\u6c89\u58f0\u53f1\u9053\uff1a\u201c\u8c79\u5b50\uff0c\u4e0d\u5f97\u65e0\u793c\uff01\u201d \u3000\u3000\u5f6a\u5f62\u5c11\u5e74\u624b\u638c\u65b9\u4e3e\uff0c\u4e00\u542c\u53f1\u58f0\uff0c\u500f\u7136\u56de\u624b\uff0c\u6b64\u523b\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5df2\u6765\u5230\u4ed6\u8eab\u540e\uff0c\u53ea\u89c1\u4ed6\u8eab \u5f62\u4e00\u95ea\uff0c\u7a81\u6c60\u6e9c\u5f00\u4e94\u5c3a\uff0c\u4e00\u53cc\u6709\u5982\u91ce\u517d\u4e00\u822c\u7684\u773c\u775b\uff0c\u8fd8\u4ecd\u7136\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8eab \u4e0a\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u624b\u638c\u4e00\u6325\uff0c\u201c\u957f\u8679\u5251\u201d\u8fb9\u5c11\u884d\u3001\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\u3001\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u3001\u201c\u9ed1 \u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\uff0c\u8eab\u5f62\u7a81\u5730\u6563\u5f00\uff0c\u56db\u4eba\u5404\u636e\u4e00\u65b9\uff0c\u4f3c\u4e4e\u5728\u9632\u5907\u7740\u4ec0\u4e48\u4eba\u4f1a\u7a81\u7136\u9003\u53bb\u4f3c\u7684\u3002 \u3000\u3000\u6a80\u660e\u8fc8\u6b65\u8d70\u5411\u88f4\u73cf\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5fae\u4e00\u6ed1\u6b65\uff0c\u5b88\u5019\u5728\u88f4\u73cf\u7684\u8eab\u7554\uff0c\u771f\u6c14\u5185\u8574\uff0c\u968f\u65f6\u51c6\u5907 \u51fa\u624b\u4e00\u51fb\u3002 \u3000\u3000\u665a\u98ce\u4e2d\u5bd2\u610f\u66f4\u91cd\uff0c\u8fd9\u5c0f\u5c0f\u7684\u5c71\u5761\u4e0a\uff0c\u7a81\u5730\u7b3c\u7f69\u8d77\u4e00\u7247\u6740\u6c14\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5e72\u54b3\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u88f4\u8d24\u4f84\uff0c\u4f60\u96be\u9053\u2014\u2014\u201d\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u89c1\u88f4\u73cf\u9762\u8272\u4e4b \u4e0a\uff0c\u6cdb\u51fa\u4e00\u7247\u7d2b\u6c14\u3002 \u3000\u3000\u6a80\u660e\u5fc3\u5934\u4e00\u61d4\uff0c\u77e5\u9053\u88f4\u73cf\u7684\u5185\u529f\uff0c\u6b64\u523b\u7adf\u5df2\u4e0a\u8fbe\u7d2b\u5e9c\uff0c\u8840\u6c14\u4ea4\u5408\uff0c\u795e\u5f62\u5408\u4e00\uff0c\u4e43\u662f\u5185\u529f \u4fee\u4e3a\u4e0a\u4e58\u7684\u6700\u9ad8\u5883\u754c\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u901a\u9762\u524d\u8fd9\u5c11\u5e74\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u6b65\u5165\u4e86\u8fd9\u5185\u5bb6\u65e0\u4e0a\u5fc3\u6cd5\u4e2d\u7684\u79d8\u5f84\uff0c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u76ee \u4e2d\u795e\u5149\u7a81\u76db\uff0c\u7f13\u7f13\u4e3e\u8d77\u624b\u638c\uff0c\u4f3c\u4e4e\u8981\u62cd\u5411\u88f4\u73cf\u7684\u5934\u9876\u3002\u8981\u77e5\u88f4\u73cf\u6b64\u523b\u6b63\u503c\u6027\u547d\u4ea4\u4fee\u4e4b\u5883\uff0c\u4ed6 \u8fd9\u4e00\u638c\u7eb5\u662f\u8f7b\u8f7b\u62cd\u4e0b\uff0c\u88f4\u73cf\u4e0d\u4f46\u8981\u524d\u529f\u5c3d\u5f03\uff0c\u800c\u4e14\u6c14\u8840\u9006\u6d41\uff0c\u7acb\u523b\u4fbf\u6709\u6740\u8eab\u4e4b\u7978\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u56db\u9053\u76ee\u5149\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u51dd\u6ce8\u5728\u4ed6\u8fd9\u53ea\u624b\u638c\u4e4b\u4e0a\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u624b\u638c\u7a0d\u6709\u4e0b\u843d\u4e4b\u52bf\uff0c\u8fd9\u5144\u5f1f\u4e24\u4eba\u4fbf\u4f1a\u5168\u529b\u51fa\u624b\uff01 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u88f4\u73cf\u7a81\u5730\u5f20\u5f00\u773c\u6765\uff0c\u76ee\u4e2d\u7684\u795e\u5149\uff0c\u5b9b\u5982\u4e24\u67c4\u5229\u5203\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d \u624b\u638c\u4e00\u98a4\uff0c\u56de\u624b\u634b\u987b\u7b11\u9053\uff1a\u201c\u597d\u597d\uff0c\u606d\u559c\u606d\u559c\uff0c\u60f3\u4e0d\u5230\u4e00\u5e74\u4e0d\u89c1\uff0c\u8d24\u4f84\u4f60\u7684\u6b66\u529f\u8fdb\u5883\uff0c\u4e00\u81f3 \u5982\u65af\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u5411\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6295\u4ee5\u611f\u6fc0\u7684\u4e00\u77a5\uff0c\u4f3c\u4e4e\u65e9\u5df2\u77e5\u9053\u8fd9\u5144\u5f1f\u4e24\u4eba \u65b9\u624d\u5bf9\u81ea\u5df1\u7684\u9632\u62a4\u4e4b\u60c5\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4fbf\u5411\u201c\u9f99\u5f62\u516b\u638c\u201d\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u6a80\u5927\u53d4\u522b\u6765\u53ef\u597d\uff1f\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u8f6c\u8fc7\u8eab\u53bb\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u6015\u6709\u4eba\u518d\u4e5f\u60f3\u4e0d\u5230\uff0c\u4e00\u4e2a\u5929\u8d44\u611a\u9c81\u7684\u5c11 \u5e74\uff0c\u7adf\u4f1a\u7ec3\u6210\u5982\u6b64\u7cbe\u5999\u7684\u6b66\u529f\u5427\uff1a\u563f\u563f\u2026\u2026\u201d\u51b7\u7b11\u8fde\u8fde\uff0c\u518d\u4e5f\u4e0d\u671b\u6a80\u660e\u4e00\u773c\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u867d\u7136\u8001\u7ec3\uff0c\u6b64\u523b\u9762\u988a\u4ea6\u4e0d\u7981\u5fae\u5fae\u4e00\u7ea2\u3002 \u3000\u3000\u88f4\u73cf\u89c1\u4e86\u4ed6\u8fd9\u79cd\u5c34\u5c2c\u7684\u795e\u8272\uff0c\u5fc3\u4e0b\u5927\u662f\u4e0d\u5b9a\uff0c\u4ed6\u5929\u6027\u9187\u539a\uff0c\u60f3\u8d77\u4ee5\u524d\u5728\u201c\u98de\u9f99\u9556\u5c40\u201d\u5b66 \u827a\u7684\u7ecf\u8fc7\uff0c\u4ee5\u53ca\u6a80\u660e\u66fe\u7ecf\u8d23\u9a82\u4ed6\u201c\u5929\u8d44\u611a\u9c81\u201d\u7684\u8bdd\uff0c\u5fc3\u4e2d\u867d\u7136\u6709\u4e9b\u6000\u7591\uff0c\u4f46\u4ed6\u5374\u4e00\u76f4\u53ea\u5f53\u4f5c \u662f\u4ed6\u7684\u201c\u6a80\u5927\u53d4\u201d\u4e0d\u613f\u4ed6\u7ec3\u6210\u6b66\u529f\uff0c\u518d\u6b65\u4ed6\u7236\u4eb2\u7684\u540e\u5c18\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u81ea\u59cb\u81f3\u7ec8\uff0c\u5fc3\u4e2d\u4e1d\u6beb\u6ca1\u6709\u5bf9\u6a80\u660e\u751f\u51fa\u6028\u6068\u4e4b\u610f\u3002 \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u5c71\u4e0b\u7fa4\u8c6a\uff0c\u5df2\u6e10\u6e10\u56f4\u4e86\u4e0a\u6765\uff0c\u4f46\u56db\u4e0b\u5374\u5bc2\u65e0\u5608\u58f0\uff0c\u663e\u89c1\u8fd9\u96c4\u8e1e\u6c5f\u6e56\u7684\u6b66 \u6797\u5927\u8c6a\u4e4b\u58f0\u5a01\uff0c\u5df2\u5c06\u4f17\u4eba\u4e00\u8d77\u9707\u4f4f\u3002 \u3000\u3000\u88f4\u73cf\u6697\u4e2d\u611f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u8fd9\u6a80\u5927\u53d4\u5f53\u771f\u662f\u4e00\u4ee3\u4eba\u6770\uff0c\u4e00\u4e3e\u3002\u4e00\u52a8\uff0c\u90fd\u5e26\u7740\u4e00\u79cd\u8bf4 \u4e0d\u51fa\u7684\u5a01\u4eea\u3002\u201d\u4ed6\u5374\u4e0d\u77e5\u9053\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u5bf9\u4ed6\u7684\u656c\u91cd\u4e4b\u5fc3\uff0c\u6bd4\u4e4b\u5bf9\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u5176\u95f4 \u5df2\u65e0\u60ac\u6b8a\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u606d\u58f0\u9053\uff1a\u201c\u6a80\u5927\u53d4\u8fdc\u9053\u6765\u6b64\uff0c\u4e0d\u77e5\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd1\u65e5\u6765\u6211\u542c\u5f97\u6c5f\u6e56\u4f20\u8a00\uff0c\u4f60\u5df2\u8131\u9896\u800c\u51fa\uff0c\u5fc3\u91cc\u65e2\u662f\u6b22 \u559c\uff0c\u53c8\u662f\u5173\u5fc3\uff0c\u5fcd\u4e0d\u4f4f\u8981\u6765\u770b\u770b\u4f60\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u4e00\u9635\u611f\u6fc0\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u5c0f\u4f84\u8499\u6a80\u5927\u53d4\u629a\u517b\u6210\u4eba\uff0c\u5929\u9ad8\u5730\u539a\u4e4b\u6069\uff0c\u5c0f\u4f84\u771f\u4e0d\u77e5\u5982 \u4f55\u62a5\u7b54\u624d\u597d\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u53e5\u53e5\u5747\u662f\u53d1\u81ea\u80ba\u8151\uff0c\u4e1d\u6beb\u6ca1\u6709\u505a\u4f5c\u4e4b\u5904\uff0c\u4e00\u53e5\u8bdd\u672a\u66fe\u8bf4\u5b8c\uff0c\u8bed\u58f0\u4e2d\u5df2\u6709\u54fd\u54bd\u4e4b \u610f\uff0c\u76ee\u4e2d\u51e0\u4e4e\u8981\u6d41\u51fa\u611f\u6fc0\u7684\u773c\u6cea\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u690d\u660e\u4e00\u624b\u634b\u987b\uff0c\u795e\u8272\u95f4\u4f3c\u88ab\u8fd9\u756a\u771f\u8bda\u611f\u52a8\uff0c\u5fae\u559d\u4e00\u58f0\uff0c\u5634\u89d2\u6cdb\u8d77\u4e00\u4e1d\u6148\u7965 \u7684\u7b11\u5bb9\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4ee4\u5c0a\u65e9\u6545\uff0c\u8001\u592b\u81ea\u7136\u8981\u4e3a\u6545\u4eba\u5c3d\u4e00\u4efd\u5fc3\u610f\uff0c\u53ea\u6068\u8001\u592b\u7ec8\u5e74\u5fd9\u4e8e\u6742\u52a1\uff0c\u672a \u66fe\u5bf9\u4f60\u4eec\u591a\u52a0\u5173\u7167\u2026\u2026\u5509\uff01\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u500f\u7136\u4f4f\u53e3\uff0c\u76ee\u5149\u95ea\u52a8\u4e4b\u610f\uff0c\u4f3c\u4e4e\u5341\u5206\u53f9\u7136\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u66f4\u662f\u611f\u6fc0\uff0c\u53cc\u76ee\u6cea\u5149\u83b9\u7136\uff0c\u8bb7\u8bb7\u4ed6\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5374\u89c1\u6a80\u660e\u9762\u4e0a\u7b11\u5bb9\u7a81\u5730\u4e00\u655b\uff0c\u7acb\u523b\u6362\u4f5c\u4e00\u7247\u51b0\u51b7\u7684\u6740\u6c14\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u9707\uff0c\u8131\u56de\u9053\uff1a\u201c\u6a80\u5927\u53d4\u6b64\u6765\uff0c\u96be\u9053\u8fd8\u6709\u4e9b\u4ec0\u4e48\u522b\u7684\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5389\u7535\u822c\u7684\u76ee\u5149\uff0c\u5728\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u80cc\u5f71\u4e0a\u4e00\u626b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u8bdd\u58f0\u843d\u540e\uff0c\u4ed6\u624b\u638c\u7a81\u5730\u4e00\u6325\uff0c\u53ea\u542c\u201c\u545b\u201d\u5730\u4e24\u58f0\u6e05\u541f\uff0c\u90a3\u201c\u957f\u8679\u5251\u201d\u8fb9\u5c11\u884d\u3001\u201c\u6444\u9b42 \u5200\u201d\u7f57\u4e49\uff0c\u5df2\u53cc\u53cc\u62d4\u51fa\u5175\u5203\u3002 \u3000\u3000\u706b\u5149\u95ea\u52a8\uff0c\u5251\u5149\u5982\u78a7\uff0c\u7fa4\u8c6a\u5fc3\u5934\u5404\u81ea\u4e00\u60ca\uff0c\u88f4\u73cf\u8bb7\u8bb7\u9053\uff1a\u201c\u690d\u5927\u53d4\u83ab\u975e\u2014\u2014\u201d\u201c\u9f99\u5f62\u516b \u638c\u201d\u6a80\u660e\u9762\u6c89\u5982\u6c34\uff0c\u6c89\u58f0\u622a\u53e3\u9053\uff1a\u201c\u8001\u592b\u6b64\u6765\u9664\u4e86\u63a2\u671b\u4f60\u4e4b\u5916\uff0c\u8fd8\u8981\u4ee3\u6b66\u6797\u4e3b\u6301\u4e00\u4e9b\u6b63\u4e49\uff0c \u4e3a\u4e00\u4e2a\u6b7b\u53bb\u7684\u6c5f\u6e56\u540c\u9053\u590d\u4ec7\u3002\u201d \u3000\u3000\u88f4\u73cf\u9762\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u4f46\u5c0f\u4f84\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u6545\u610f\u4f24\u4eba\u2014\u2014\u201d\u201c\u9f99\u5f62\u516b\u638c\u201d\u622a\u53e3\u9053\uff1a \u201c\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u4ed6\u7a81\u5730\u8f6c\u8fc7\u8eab\u53bb\uff0c\u53cc\u62f3\u4e00\u62f1\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5317\u6597\u4e03\u715e\u4e2d\u7684\u4e09\u715e\u83ab\u661f\uff0c\u5404\u4f4d\u6c5f\u6e56\u670b\u53cb\uff0c\u60f3\u5fc5 \u90fd\u662f\u8ba4\u5f97\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5df2\u4e00\u8d77\u8f6c\u8fc7\u5934\uff0c\u51b0\u51b7\u7684\u76ee\u5149\uff0c\u56db\u4e0b\u4e00\u626b\uff0c\u4fbf\u505c\u7559\u5728\u201c\u957f\u8679 \u5251\u201d\u3001\u201c\u6444\u9b42\u5200\u201d\u638c\u4e2d\u7684\u4e24\u4ef6\u5175\u5203\u4e4b\u4e0a\u3002 \u3000\u3000\u7fa4\u8c6a\u5fcd\u4e0d\u4f4f\u53d1\u51fa\u4e00\u7247\u60ca\u559f\u4e4b\u58f0\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u624b\u638c\u4e00\u6325\uff0c\u56db\u4e0b\u5608\u58f0\u5c3d\u7686\u5bc2\u7136\uff0c\u98ce\u5439\u706b\u7130\uff0c\u54d7\u5265\u4f5c\u54cd\uff0c\u5728\u6b66\u6797\u7fa4\u8c6a\u773c\u4e2d\uff0c \u8fd9\u4e00\u4ee3\u6cc9\u96c4\u9ad8\u5927\u5a01\u731b\u7684\u8eab\u5f62\uff0c\u7adf\u6709\u5982\u6cf0\u5c71\u5317\u6597\u4e00\u822c\uff0c\u4ee4\u4eba\u4e0d\u6562\u4ef0\u89c6\u3002 \u3000\u3000\u53ea\u542c\u6a80\u660e\u6717\u58f0\u53c8\u9053\uff1a\u201c\u4e03\u715e\u83ab\u661f\u4e4b\u4e3a\u4eba\u884c\u4e8b\uff0c\u59d1\u4e14\u4e0d\u8bba\uff0c\u4f46\u6b64\u4eba\u6b7b\u65f6\uff0c\u8001\u592b\u6070\u5728\u5f53\u573a\uff0c \u76ee\u7779\u4e00\u5207\uff0c\u6df1\u89c9\u6b64\u4e8b\u6709\u5931\u516c\u9053\uff0c\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u4e00\u4e9b\u6781\u5fae\u5c0f\u7684\u4e89\u6267\uff0c\u4ee5\u51b7\u9177\u6bd2\u8fa3\u8457\u79f0\u6c5f\u6e56\u7684\u2018\u51b7 \u8c37\u53cc\u6728\u2019\u4fbf\u5c06\u4ed6\u6740\u6b7b\u3002\u201d \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9f50\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u6728\u7acb\u5f53\u5730\uff0c\u7adf\u4ecd\u672a\u6709\u4e1d\u6beb\u963b\u6b62\u201c\u9f99\u5f62\u516b\u638c\u201d\u8bf4\u8bdd\u4e4b\u610f\u3002 \u3000\u3000\u88f4\u73cf\u9762\u8272\u5927\u53d8\uff0c\u7fa4\u8c6a\u8bae\u8bba\u7eb7\u7eb7\uff0c\u53ea\u542c\u6a80\u660e\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u8001\u592b\u4e0e\u2018\u5317\u6597\u4e03\u715e\u2019\u975e\u4eb2\u975e\u6545\uff0c \u4f46\u4e3a\u4e86\u6b66\u6797\u9053\u4e0a\u7684\u4e00\u70b9\u6b63\u4e49\uff0c\u5374\u4e0d\u80fd\u7f6e\u8eab\u6b64\u4e8b\u4e4b\u5916\uff0c\u4e3a\u4e86\u8fd9\u70b9\u6b66\u6797\u6b63\u4e49\uff0c\u6570\u5341\u5e74\u6765\uff0c\u8001\u592b\u5954 \u6ce2\u5c3d\u7601\uff0c\u5404\u4f4d\u6709\u76ee\u5171\u7779\uff0c\u4eca\u65e5\u8001\u592b\u6765\u5230\u6b64\u95f4\uff0c\u4ea6\u662f\u4e3a\u4e86\u8fd9\u540c\u6837\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u4ed6\u624b\u638c\u4e00\u6325\uff0c\u5389\u58f0\u63a5\u9053\uff1a\u201c\u4eca\u65e5\u6211\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\uff0c\u4e3a\u4e86\u2018\u5317\u6597\u4e03\u715e\u2019\uff0c\u6765\u5bfb\u2018\u51b7\u8c37 \u53cc\u6728\u2019\u590d\u4ec7\u2014\u2014\u201d\u4ed6\u76ee\u5149\u5982\u7535\uff0c\u56db\u4e0b\u4e00\u626b\uff0c\u53ea\u89c1\u7fa4\u8c6a\u679c\u5df2\u4e3a\u4ed6\u58f0\u5a01\u6240\u6151\uff0c\u518d\u65e0\u4e00\u4eba\u9ad8\u58f0\u8bf4 \u8bdd\uff0c\u76ee\u4e2d\u4e0d\u7981\u9690\u9690\u9732\u51fa\u5f97\u610f\u4e4b\u8272\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u6218\uff0c\u65e0\u8bba\u8c01\u80dc\u8c01\u8d25\uff0c\u90fd\u8bf7\u5404\u4f4d\u8896\u624b\u65c1 \u89c2\uff0c\u4e0d\u8981\u51fa\u624b\uff0c\u5404\u4f4d\u82e5\u662f\u5e2e\u4e86\u6211\u6a80\u660e\u4e00\u62f3\u4e00\u8db3\uff0c\u5c31\u4e0d\u662f\u6a80\u660e\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u756a\u8bdd\u8bf4\u5f97\u5149\u660e\u78ca\u843d\uff0c\u6f02\u4eae\u5df2\u6781\uff0c\u6697\u4e2d\u867d\u662f\u6559\u4eba\u4e0d\u8981\u4f38\u624b\u591a\u4e8b\uff0c\u4e3a\u201c\u51b7\u8c37\u53cc\u6728\u201d\u52a9 \u62f3\uff0c\u4f46\u9762\u4e0a\u5374\u662f\u6559\u4eba\u4e0d\u8981\u5e2e\u52a9\u81ea\u5df1\uff0c\u7fa4\u8c6a\u5927\u591a\u5217\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6beb\u65e0\u597d\u611f\uff0c\u6b64\u523b\u54c4\u7136\u4e00\u58f0\uff0c\u7b54 \u5e94\u5f97\u7ade\u662f\u5341\u5206\u70ed\u70c8\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u634b\u987b\u4e00\u7b11\uff0c\u7f13\u7f13\u8f6c\u8eab\uff0c\u88f4\u73cf\u5fc3\u4e2d\u5927\u60d1\u4e0d\u89e3\uff0c\u4e0d\u77e5\u4ed6\u8fd9\u201c\u6a80\u5927\u53d4\u201d\u7adf\u4f1a\u4e3a \u201c\u5317\u6597\u4e03\u715e\u201d\u590d\u8d77\u4ec7\u6765\uff0c\u8d76\u4e0a\u4e09\u6b65\uff0c\u8fd8\u672a\u8bf4\u8bdd\uff0c\u53ea\u89c1\u6a80\u660e\u624b\u638c\u4e00\u6c89\uff0c\u201c\u957f\u8679\u5251\u201d\u3001\u201c\u6444\u9b42 \u5200\u201d\u8eab\u5f71\u9aa4\u8d77\uff0c\u4e24\u9053\u96ea\u4eae\u7684\u5bd2\u5149\uff0c\u5f53\u5934\u5411\u201c\u51b7\u8c37\u53cc\u6728\u201d\u51fb\u4e0b\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9762\u4e0a\u867d\u7136\u4e0d\u52a8\u58f0\u8272\uff0c\u6697\u4e2d\u65e9\u5df2\u6ee1\u84c4\u771f\u529b\uff0c\u6b64\u523b\u9f50\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u51b7\u67af\u6728\u5782\u7709 \u655b\u76ee\uff0c\u76f4\u5f85\u8fb9\u5c11\u884d\u638c\u4e2d\u90a3\u67c4\u5947\u5f62\u957f\u5251\uff0c\u5e26\u7740\u4e00\u6e9c\u9752\u5149\uff0c\u582a\u582a\u524a\u5728\u4ed6\u8eab\u4e0a\uff0c\u7a81\u5730\u5411\u5de6\u6ed1\u5f00\u4e09 \u5c3a\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u62cd\u5411\u8fb9\u5c11\u884d\u8170\u7554\u7684\u201c\u7ae0\u95e8\u201d\u5927\u7a74\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4ee5\u9759\u5236\u52a8\uff0c\u9759\u5982\u6cf0\u5c71\uff0c\u52a8\u5982\u8131\u5154\uff0c\u867d\u662f\u7b80\u7b80\u5355\u5355\u7684\u4e00\u62db\uff0c\u4f46\u65f6\u95f4\u4e4b\u8fc5\u5feb\uff0c\u90e8\u4f4d\u4e4b \u51c6\u786e\uff0c\u5374\u5f53\u771f\u4e0d\u6127\u4e3a\u6b66\u6797\u4e00\u6d41\u9ad8\u624b\u3002 \u3000\u3000\u7fa4\u8c6a\u660e\u660e\u770b\u5230\u201c\u957f\u8679\u5251\u201d\u8fb9\u5c11\u884d\u4e00\u5251\u5df2\u81f3\u51b7\u67af\u6728\u54bd\u5589\uff0c\u54ea\u77e5\u970e\u773c\u4e4b\u95f4\uff0c\u51b7\u67af\u6728\u4e00\u53cc\u94c1\u638c \u5374\u5df2\u5230\u4e86\u8fb9\u5c11\u884d\u808b\u4e0b\u3002 \u3000\u3000\u201c\u957f\u8679\u5251\u201d\u8fb9\u5c11\u884d\u62e7\u8eab\u9519\u6b65\uff0c\u624b\u8155\u4e00\u6296\uff0c\u638c\u4e2d\u957f\u5251\u7acb\u523b\u75be\u626b\u800c\u51fa\uff0c\u5251\u8292\u95ea\u95ea\uff0c\u6a2a\u524a\u51b7\u67af \u6728\u7684\u53f3\u8155\u3002 \u3000\u3000\u51b7\u67af\u6728\u8f7b\u53f1\u4e00\u58f0\uff0c\u8ba9\u5f00\u957f\u5251\uff0c\u7a81\u5730\u98de\u8d77\u4e00\u817f\uff0c\u75be\u8e22\u4ed6\u6301\u5251\u7684\u624b\u8155\uff0c\u8fb9\u5c11\u884d\u4e00\u6c89\uff0c\u53ea\u89c1\u51b7 \u67af\u6728\u4ee5\u5de6\u817f\u4e3a\u8f74\uff0c\u8eab\u8eaf\u4e00\u65cb\uff0c\u5de6\u624b\u98df\u3001\u4e2d\u4e24\u6307\uff0c\u5e76\u8d77\u5982\u5251\uff0c\u75be\u5730\u70b9\u5411\u4ed6\u808b\u68a2\u9aa8\u4e0b\u7684\u201c\u8179\u7ed3\u201d \u8981\u7a74\u3002 \u3000\u3000\u201c\u957f\u8679\u5251\u201d\u8fb9\u5c11\u884d\u811a\u6b65\u79fb\u52a8\uff0c\u903c\u5f00\u4e00\u5c3a\uff0c\u957f\u5251\u4e00\u5c55\uff0c\u6d12\u51fa\u4e00\u7247\u5149\u7f51\uff0c\u65bd\u5c55\u51fa\u51cc\u5389\u7684\u653b \u52bf\u3002 \u3000\u3000\u4ed6\u9762\u6c89\u5982\u6c34\uff0c\u76ee\u8574\u6740\u673a\uff0c\u8eab\u8eaf\u867d\u7855\u957f\uff0c\u8eab\u624b\u5374\u77eb\u6d3b\uff0c\u8fd9\u4e00\u67c4\u8f83\u957f\u5251\u51e0\u4e4e\u591a\u51fa\u4e00\u5c3a\u7684\u5229 \u5203\uff0c\u5728\u4ed6\u624b\u4e2d\u7528\u6765\uff0c\u7ade\u6709\u5982\u522b\u4eba\u638c\u4e2d\u7684\u5315\u9996\u4e00\u822c\u7075\u6d3b\uff0c\u5251\u62db\u8fc5\u5feb\u72e0\u8fa3\uff0c\u62db\u62db\u4e0d\u79bb\u51b7\u67af\u6728\u7684\u8981 \u5bb3\u3002 \u3000\u3000\u51b7\u67af\u6728\u867d\u7136\u8eab\u8eaf\u7626\u957f\uff0c\u4f46\u6bd4\u4e4b\u8fb9\u5c11\u884d\u6765\uff0c\u7adf\u77ee\u4e86\u4e00\u5934\u3002 \u3000\u3000\u5239\u90a3\u95f4\uff0c\u4f46\u89c1\u4ed6\u67af\u7626\u7684\u8eab\u8eaf\uff0c\u4f3c\u4e4e\u5df2\u88ab\u8fb9\u5c11\u884d\u6cf0\u5c71\u538b\u9876\u822c\u7684\u62db\u5f0f\u56f4\u4f4f\uff0c\u51fa\u624b\u5341\u62db\u4e4b\u4e2d\uff0c \u5012\u6709\u4e5d\u62db\u662f\u5b88\u975e\u653b\u3002 \u3000\u3000\u201c\u957f\u8679\u5251\u201d\u8fb9\u5c11\u884d\u7cbe\u795e\u4e00\u632f\uff0c\u5251\u62db\u66f4\u89c1\u72e0\u8fa3\uff0c\u6068\u4e0d\u5f97\u957f\u5251\u4e00\u6325\uff0c\u4fbf\u5c06\u51b7\u683c\u6728\u7684\u9996\u7ea7\u5272 \u4e0b\u3002 \u3000\u3000\u90a3\u8fb9\u51b7\u5bd2\u7af9\u8eab\u6cd5\u5374\u662f\u5feb\u5982\u98d8\u98ce\uff0c\u8eab\u968f\u638c\u8d70\uff0c\u638c\u968f\u8eab\u6e38\uff0c\u987b\u53d1\u98d8\u62c2\uff0c\u957f\u886b\u98de\u626c\uff0c\u5c06\u201c\u6444\u9b42 \u5200\u201d\u7f57\u4e49\u56f0\u5728\u4e2d\u592e\u3002 \u3000\u3000\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\u5200\u6c89\u529b\u731b\uff0c\u62db\u5f0f\u6c89\u5b9e\uff0c\u6bcf\u51fa\u4e00\u62db\uff0c\u4fbf\u6709\u4e00\u80a1\u52b2\u98ce\u968f\u7740\u9707\u8033\u7684\u94dc\u73af\u4e4b\u58f0\uff0c \u81ea\u5200\u950b\u6325\u51fa\uff0c\u9707\u5f97\u65c1\u8fb9\u90a3\u4e00\u5806\u706b\u7130\u95ea\u70c1\u98de\u821e\u3002 \u3000\u3000\u4ed6\u62db\u5f0f\u867d\u7f13\u6162\uff0c\u4f46\u5200\u5149\u4e4b\u95f4\uff0c\u5374\u65e0\u534a\u4e1d\u7834\u9699\uff0c\u76ee\u5149\u4e0b\u5782\uff0c\u5bf9\u51b7\u5bd2\u7af9\u6e38\u8d70\u7684\u8eab\u5f62\u76f4\u5982\u672a \u7779\uff0c\u53ea\u662f\u4ee5\u6c89\u5b9e\u7684\u62db\u5f0f\uff0c\u62a4\u4f4f\u5168\u8eab\uff0c\u5076\u5c14\u5288\u51fa\u4e00\u5200\uff0c\u5b9a\u662f\u653b\u5411\u51b7\u5bd2\u7af9\u5fc5\u6551\u4e4b\u5904\uff0c\u5341\u6570\u62db\u4e00 \u8fc7\uff0c\u4ed6\u5200\u6cd5\u6e10\u5feb\uff0c\u653b\u52bf\u6e10\u591a\uff0c\u4e00\u5200\u63a5\u7740\u4e00\u5200\uff0c\u4e00\u62db\u63a5\u7740\u4e00\u62db\uff0c\u4f46\u89c1\u5200\u82b1\u98de\u821e\uff0c\u5200\u98ce\u6fc0\u8361\uff0c\u5200 \u4e0a\u94dc\u73af\uff0c\u53ee\u54e8\u9707\u8033\uff0c\u5df2\u4f3c\u4e4e\u6e10\u6e10\u62a2\u5f97\u5148\u673a\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u64c5\u660e\u634b\u987b\u65c1\u89c2\uff0c\u6c14\u5ea6\u4ece\u5bb9\uff0c\u770b\u5230\u201c\u957f\u8679\u5251\u201d\u201c\u6444\u9b42\u5200\u201d\u65bd\u51fa\u5999\u62db\uff0c\u4fbf\u4e0d\u4f4f \u70b9\u9996\u5fae\u7b11\uff0c\u8fde\u58f0\u8bf4\u9053\uff0c\u201c\u597d\uff0c\u597d\uff01\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u7b11\u5bb9\u4e00\u73b0\uff0c\u7acb\u5728\u4ed6\u8eab\u65c1\u7684\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u4e5f\u7acb\u523b\u51fb\u638c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u9ad8\u62db\uff01\u201d \u3000\u3000\u7acb\u5728\u8f83\u8fd1\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u6709\u7684\u4fbf\u54c4\u7136\u559d\u8d77\u5f69\u6765\uff0c\u5176\u5b9e\u8fd9\u52a8\u624b\u56db\u4eba\u4ff1\u662f\u4ee5\u653b\u8fd8\u653b\uff0c\u8eab\u6cd5\u90e8 \u5feb\uff0c\u771f\u80fd\u770b\u51fa\u4ed6\u4eec\u62db\u5f0f\u4e4b\u53d8\u5316\u7684\u4eba\uff0c\u5e76\u65e0\u51e0\u4e2a\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u5f6a\u58ee\u5c11\u5e74\uff0c\u76ee\u5c04\u7cbe\u5149\uff0c\u5353\u7136\u800c\u7acb\uff0c\u9762\u4e0a\u867d\u65e0\u8868\u60c5\uff0c\u76ee\u5149\u4e2d\u5374\u9690\u542b\u8f7b\u8511\u4e4b\u610f\uff0c\u4eff\u4f5b \u6839\u672c\u672a\u5c06\u8fd9\u56db\u4eba\u7684\u6b66\u529f\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u88f4\u73cf\u6ee1\u5fc3\u60ca\u60f6\uff0c\u67ec\u624b\u65e0\u7b56\uff0c\u989d\u4e0a\u5df2\u6025\u51fa\u8c46\u5927\u7684\u6c57\u73e0\uff0c\u4ed6\u867d\u7136\u6709\u5fc3\u53bb\u52a9\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e00\u81c2 \u4e4b\u529b\uff0c\u4f46\u5374\u53c8\u4e0d\u613f\u4e0e\u4ed6\u7684\u201c\u6069\u4eba\u201d\u6a80\u5927\u53d4\u4e3a\u654c\uff0c\u773c\u770b\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4f3c\u4e4e\u4ff1\u5df2\u843d\u5728\u4e0b\u98ce\uff0c\u5fcd\u4e0d \u4f4f\u906d\u5de1\u8d70\u5230\u6a80\u660e\u8eab\u4fa7\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u5c1a\u672a\u5f00\u53e3\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5df2\u542b\u7b11\u9053\uff1a\u201c\u6211\u4e45\u95fb\u2018\u51b7\u8c37\u53cc\u6728\u2019\u4e4b\u540d\uff0c\u4eca\u65e5\u4e00 \u89c1\uff0c\u5374\u662f\u95fb\u540d\u4e0d\u5982\u89c1\u9762\uff0c\u73cf\u513f\uff0c\u4f60\u770b\u6211\u624b\u4e0b\u7684\u4e24\u4e2a\u5144\u5f1f\uff0c\u6b66\u529f\u53ef\u8fd8\u8fc7\u5f97\u53bb\uff1f\u201d \u3000\u3000\u88f4\u73cf\u8bb7\u8bb7\u9053\uff1a\u201c\u597d\u6781\u597d\u6781\uff0c\u4f46\u2014\u2014\u201d\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u542b\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\uff0c \u770b\u6765\u867d\u662f\u5404\u6709\u5de7\u5999\u4e0d\u540c\uff0c\u5176\u5b9e\u5374\u90fd\u662f\u62d9\u4e2d\u85cf\u5de7\uff0c\u4ee5\u4ed6\u4e24\u4eba\u7684\u5175\u5203\u770b\u6765\uff0c\u6b66\u529f\u4f3c\u4e4e\u8be5\u8d70\u5a01\u731b\u4e00 \u8def\uff0c\u4f46\u4ed6\u4eec\u5374\u662f\u8d70\u7684\u8f7b\u7075\u53d8\u5e7b\u4e00\u8def\uff0c\u5c24\u5176\u662f\u2018\u6444\u9b42\u5200\u2019\u7f57\u4e49\uff0c\u5200\u6cd5\u8d8a\u6765\u8d8a\u5feb\uff0c\u62db\u5f0f\u8d8a\u6765\u8d8a \u5de7\uff0c\u4f60\u770b\u2014\u2014\u4ed6\u8fd9\u4e00\u62db\u2018\u5206\u82b1\u62c2\u7a74\u2019\uff0c\u7528\u5f97\u662f\u4f55\u7b49\u5de7\u5999\u3002\u201d \u3000\u3000\u88f4\u73cf\u8bb7\u8bb7\u9053\uff1a\u201c\u662f\u6781\u662f\u6781\uff0c\u4f46\u2014\u2014\u201d\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4e00\u7b11\uff0c\u622a\u53e3\u9053\uff1a\u957f\u8679\u5251\u8fb9\u5c11\u884d\u4e5f \u8fd8\u4e0d\u9519\uff0c\u4ed6\u8fd9\u4e00\u62db\u2018\u957f\u8679\u8d2f\u65e5\u2019\u4f7f\u7684\u867d\u662f\u957f\u5251\uff0c\u7528\u7684\u5374\u662f\u6ca7\u62db\uff0c\u4f60\u770b\u2014\u2014\u8fd9\u4e00\u62db\u5c82\u4e0d\u662f\u2018\u68a8 \u82b1\u5927\u67aa\u2019\u4e2d\u7684\u5999\u7740\u2018\u98ce\u70b9\u5934\u2019\u4e48\uff0c\u5e78\u597d\u51b7\u67af\u6728\u95ea\u5f97\u5feb\uff0c\u5426\u5219\u53ea\u8981\u8fd9\u4e00\u62db\uff0c\u4fbf\u53ef\u4ee5\u89e3\u51b3\u4e86\u3002 \u201c\u4ed6\u9762\u542b\u5fae\u7b11\uff0c\u4f83\u4f83\u800c\u8c08\uff0c\u7adf\u50cf\u6559\u6b66\u7684\u8001\u5e08\u5085\uff0c\u5728\u573a\u5916\u8bb2\u89e3\u8d77\u5f1f\u5b50\u4eec\u62db\u5f0f\u6765\u4e86\u3002\u88f4\u73cf\u4e00\u9762\u9894 \u9996\uff0c\u76ee\u5149\u5374\u4e0d\u79bb\u51b7\u6c0f\u5144\u5f1f\u4e24\u4eba\u8eab\u4e0a\uff0c\u53ea\u89c1\u4ed6\u5144\u5f1f\u4e24\u4eba\u4f3c\u5df2\u6e10\u6e10\u88ab\u8feb\u5f97\u5c3d\u5931\u5148\u673a\uff0c\u90a3\u4e24\u67c4\u5947\u5f62 \u5200\u5251\uff0c\u62db\u5f0f\u5374\u8d8a\u6253\u8d8a\u731b\uff0c\u5c24\u5176\u662f\u5200\u4e0a\u94dc\u73af\u7684\u201d\u53ee\u54e8\u201c\u4e4b\u58f0\uff0c\u5f53\u771f\u662f\u58f0\u58f0\u52a8\u4eba\u5fc3\u9b44\u3002\u7fa4\u8c6a\u65e9\u5df2 \u88ab\u201d\u9f99\u5f62\u516b\u638c\u201c\u58f0\u5a01\u6240\u6151\uff0c\u6b64\u523b\u4fbf\u4e0d\u4f4f\u4e3a\u201d\u957f\u8679\u5251\u201c\u3001\u201d\u6444\u9b42\u5200\u201c\u4e24\u4eba\u559d\u5f69\u52a9\u54b8\u3002\u201d\u9f99\u5f62\u516b \u638c\u201c\u8bed\u58f0\u5fae\u987f\uff0c\u542b\u7b11\u53c8\u9053\uff1a\u201c\u4e0e\u4eba\u4ea4\u624b\uff0c\u4ee5\u5175\u5203\u6765\u5bf9\u4ed8\u8d64\u624b\u7a7a\u62f3\u4e4b\u4eba\uff0c\u867d\u7136\u6709\u5931\u516c\u9053\uff0c\u4f46\u6b64 \u523b\u4e0d\u662f\u6bd4\u6b66\uff0c\u800c\u662f\u590d\u4ec7\uff0c\u60c5\u51b5\u4fbf\u5927\u5927\u76f8\u540c\uff0c\u73cf\u513f\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6728\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u8bb7\u8bb7\u9053\uff1a\u201c\u662f\u6781\u662f\u6781\uff0c\u4f46\u2014\u2014\u201d\u201c\u9f99\u5f62\u516b\u638c\u201d\u5c55\u989c\u4e00\u7b11\uff0c\u53c8\u60f3\u622a\u65ad \u88f4\u73cf\u7684\u8bdd\u5934\uff0c\u4f46\u88f4\u73cf\u5df2\u5927\u58f0\u63a5\u7740\u9053\uff1a\u201c\u6a80\u5927\u53d4\u4e3a\u4eba\u590d\u4ec7\uff0c\u5c0f\u4f84\u672c\u6765\u4e0d\u8be5\u591a\u8bdd\uff0c\u4f46\u8fd9\u51b7\u6c0f\u5144\u5f1f \u6b64\u523b\u4e0e\u5c0f\u4f84\u8d4c\u7ea6\u672a\u7ec8\uff0c\u6a80\u5927\u53d4\u4f3c\u4e4e\u4e0d\u8be5\u2014\u2014\u2018\u201d\u9f99\u5f62\u516b\u638c\u201c\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4e0d\u8be5\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5446\u4e86\u4e00\u5446\uff0c\u900f\u4e86\u53e3\u957f\u6c14\uff0c\u7ec8\u4e8e\u7f13\u7f13\u9053\uff1a\u201c\u4f3c\u4e4e\u4e0d\u8be5\u5728\u6b64\u65f6\u6b64\u523b\u52a8\u624b\u3002\u201d \u3000\u3000\u88f4\u73cf\u7ecf\u9a8c\u7684\u78e8\u7ec3\uff0c\u6b66\u529f\u7684\u589e\u957f\uff0c\u5fc3\u667a\u7684\u6210\u719f\uff0c\u867d\u7136\u5df2\u4f7f\u8f6f\u5f31\u7684\u88f4\u538b\u53d8\u5f97\u575a\u5f3a\u8d77\u6765\uff0c\u4f46\u662f \u4ed6\u81ea\u5e7c\u53ca\u957f\uff0c\u4e45\u5904\u6a80\u660e\u79ef\u54b8\u4e4b\u4e0b\uff0c\u5bf9\u4e8e\u6a80\u660e\u4ecd\u6709\u754f\u60e7\u4e4b\u5fc3\uff0c\u6b64\u523b\u8fd9\u4e00\u756a\u8bdd\u8bf4\u5c06\u51fa\u6765\uff0c\u5b9e\u5df2\u8d39 \u5c3d\u6c14\u529b\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4eca\u65e5\u4e4b\u4e3e\uff0c\u867d\u662f\u4e3a\u4e86\u201c\u5317\u6597\u4e03\u715e\u201d\u83ab\u6c0f\u5144\u5f1f\u5728\u201d\u6d6a\u83bd\u5c71 \u5e84\u201d\u4e4b\u5916\uff0c\u8bbe\u4e0b\u7591\u5175\u4e4b\u8ba1\uff0c\u6551\u4ed6\u8131\u51fa\u91cd\u56f4\uff0c\u4e5f\u662f\u4e3a\u4e86\u4e0d\u613f\u8ba9\u88f4\u73cf\u4e0e\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u8d4c\u7ea6\u7ee7\u7eed \u4e0b\u53bb\u3002 \u3000\u3000\u88f4\u73cf\u76ee\u5149\u4e00\u8f6c\uff0c\u4f46\u89c1\u6a80\u660e\u9762\u5bd2\u5982\u6c34\uff0c\u4e00\u4e0b\u634b\u987b\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u867d\u6709\u4e9b\u60ca\u60e7\uff0c\u4f46\u4ecd\u9f13\u8db3 \u52c7\u6c14\u8bf4\u9053\uff1a\u201c\u6a80\u5927\u53d4\uff0c\u4f60\u8bf4\u5c0f\u4f84\u7684\u8bdd\uff0c\u53ef\u6709\u9053\u7406\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u95f4\u4e8b\uff0c\u975e\u4f60\u80fd\u77e5\uff0c\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u8fd8\u8be5\u2014\u2014 \u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u5730\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9f50\u5730\u4e00\u58f0\u6e05\u5578\uff0c\u4e24\u4eba\u8eab\u5f62\u4e00\u95ee\uff0c\u8eab\u6cd5\u5927\u53d8\uff0c\u5404\u81ea\u5288\u51fa\u4e09\u638c\uff0c \u5c06\u201c\u957f\u8679\u5251\u201d\u3001\u201c\u6444\u9b42\u5200\u201d\u903c\u5f00\u4e94\u6b65\uff0c\u51b7\u67af\u6728\u811a\u6b65\u4e00\u6ed1\uff0c\u53cc\u638c\u4ff1\u98de\uff0c\u5de6\u622a\u53f3\u5288\uff0c\u7adf\u7a81\u5730\u5411 \u201c\u957f\u8679\u5251\u201d\u653b\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u4e4b\u95f4\uff0c\u51b7\u5bd2\u7af9\u4e5f\u5df2\u5411\u201c\u6444\u9b42\u5200\u201d\u653b\u51fa\u4e24\u638c\u3002 \u3000\u3000\u5239\u90a3\u95f4\uff0c\u4f46\u89c1\u4ed6\u4e24\u4eba\u731b\u5a01\u7edd\u4f26\u7684\u8fde\u73af\u51fb\u51fa\u4e03\u638c\uff0c\u7ade\u5c06\u201c\u957f\u8679\u5251\u201d\u3001\u201c\u6444\u9b42\u5200\u201d\u4e24\u4eba\u903c\u5f97 \u6ca1\u6709\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u4e03\u62db\u4e00\u8fc7\uff0c\u201c\u957f\u8679\u5251\u201d\u3001\u201c\u6444\u9b42\u5200\u201d\u4e24\u4eba\u4fbf\u5df2\u9677\u8eab\u5371\u5883\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u7d27\u76b1\uff0c\u7fa4\u8c6a \u96c5\u96c0\u65e0\u58f0\uff0c\u53ea\u6709\u88f4\u73cf\u9762\u4e0a\u6e10\u6e10\u9732\u51fa\u7b11\u5bb9\uff0c\u77e5\u9053\u4ed6\u5144\u5f1f\u4e24\u4eba\uff0c\u65b9\u624d\u53ea\u4e0d\u8fc7\u7528\u7684\u662f\u8bf1\u654c\u4e4b\u8ba1\u3002 \u3000\u3000\u201c\u957f\u8679\u5251\u201d\u3001\u201c\u6444\u9b42\u5200\u201d\u8eab\u5f62\u6e10\u6e10\u6563\u4e71\uff0c\u770b\u60c5\u52bf\u518d\u6253\u4e0b\u53bb\uff0c\u5341\u5408\u4e4b\u5185\uff0c\u4fbf\u8981\u4f24\u5728\u201c\u51b7\u8c37 \u53cc\u6728\u201d\u7684\u4e00\u53cc\u94c1\u638c\u4e4b\u4e0b\u3002 \u3000\u3000\u88f4\u73cf\u6697\u4e2d\u677e\u4e86\u53e3\u6c14\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u9762\u8272\u66f4\u52a0\u6c89\u91cd\uff0c\u6d53\u7709\u76b1\u5f97\u66f4\u7d27\uff0c \u4ed6\u4e0d\u7528\u518d\u770b\uff0c\u4fbf\u77e5\u9053\u201c\u957f\u8679\u5251\u201d\u3001\u201c\u6444\u9b42\u5200\u201d\u4e24\u4eba\u5904\u5883\u66f4\u9669\uff0c\u5ffd\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6d53\u7709\u4e00 \u626c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8c79\u5b50\uff01\u201d \u3000\u3000\u559d\u58f0\u521a\u843d\uff0c\u5bf9\u9762\u90a3\u9ed1\u8863\u52b2\u670d\u7684\u5c11\u5e74\uff0c\u4e24\u81c2\u4e00\u632f\uff0c\u5df2\u4ece\u201c\u957f\u8679\u5251\u201d\u201c\u6444\u9b42\u5200\u201d\u4ee5\u53ca\u201c\u51b7\u8c37 \u53cc\u6728\u201d\u5934\u9876\u4e4b\u4e0a\uff0c\u98de\u8d8a\u800c\u6765\uff0c\u8eab\u5f62\u4e4b\u5feb\uff0c\u6709\u5982\u4e00\u53ea\u5212\u7a7a\u800c\u8fc7\u7684\u5927\u9e4f\uff0c\u8f7b\u8f7b\u843d\u5230\u6a80\u660e\u8eab\u524d\uff0c\u5782 \u624b\u9053\uff1a\u201c\u8c79\u5b50\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u76ee\u5c04\u5bd2\u5149\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u81ea\u89c9\u53ef\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5f6a\u5f62\u5c11\u5e74\u5934\u4e5f\u4e0d\u56de\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u80fd\u4e00\u4eba\uff01\u201d \u3000\u3000\u6a80\u660e\u6df1\u6df1\u9053\uff1a\u201c\u53eb\u5c11\u884d\u4e0e\u7f57\u4e49\u4ee5\u4e8c\u51fb\u4e00\uff0c\u4f60\u53bb\u5e94\u4ed8\u4e00\u4eba\uff0c\u8d25\u4e86\u4f11\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u9ed1\u8863\u5f6a\u5f62\u5c11\u5e74\u4e00\u8a00\u4e0d\u53d1\uff0c\u7f13\u7f13\u89e3\u5f00\u4e86\u8170\u7554\u90a3\u5947\u5f62\u76ae\u56ca\uff0c\u81ea\u56ca\u4e2d\u53d6\u51fa\u4e00\u6bb5\u94f6\u5149\u95ea\u95ea\uff0c\u957f\u7ea6 \u4e00\u5c3a\uff0c\u7c97\u5982\u7897\u53e3\u7684\u94f6\u68cd\uff0c\u7f13\u7f13\u8f6c\u8fc7\u8eab\u53bb\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u8f7b\u53f1\u4e00\u58f0\uff1a\u201c\u5c11\u884d\uff0c\u95ea\u8fc7\u53f3\u8fb9\u3002\u201d \u3000\u3000\u201c\u957f\u8679\u5251\u3001\u8fb9\u5c11\u884d\u6b64\u523b\u62db\u5f0f\u5c55\u52a8\u95f4\uff0c\u5df2\u6e10\u529b\u4e0d\u4ece\u5fc3\uff0c\u95fb\u8a00\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u957f\u5251\u75be\u5730\u626b \u5730\uff0c\u4e00\u62db\u201d\u6a2a\u626b\u5343\u519b\u201c\uff0c\u5c06\u51b7\u67af\u6728\u903c\u5f00\u4e24\u6b65\uff0c\u8eab\u5f62\u8f6c\u5904\uff0c\u4e00\u4e2a\u7bad\u6b65\u63a0\u5230\u201d\u6444\u9b42\u5200\u201c\u90a3\u8fb9\uff0c\u6296 \u624b\u4e00\u5251\uff0c\u5411\u51b7\u5bd2\u7af9\u523a\u53bb\u3002\u51b7\u67af\u6728\u600e\xb7\u80af\u653e\u4ed6\u8131\u8d70\uff0c\u8f7b\u53f1\u4e00\u58f0\uff0c\u65b9\u5f85\u8ddf\u8e2a\u6251\u4e0a\uff0c\u54ea\u77e5\u8fd9\u9ed1\u8863\u5c11 \u5e74\u7a81\u5730\u6709\u5982\u4e00\u9053\u8f7b\u70df\u822c\u76f4\u7a9c\u8fc7\u53bb\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u51b7\u67af\u6728\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65e0\u77e5\u91ce\u4eba\uff0c\u4e5f\u8981\u6765\u52a8\u624b\u4e48\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u7259\u5173\u4e00\u54ac\uff0c\u4e24\u816e\u808c\u8089\uff0c\u7c92\u7c92\u575f\u8d77\uff0c\u76ee\u4e2d\u6563\u51fa\u91ce\u517d\u4e00\u822c\u7684\u5149\u8292\uff0c\u76f4\u5c04\u51b7\u67af\u6728\uff0c\u7f13 \u7f13\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u662f\u91ce\u4eba\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u6570\u5341\u5e74\u6765\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u4ece\u6765\u4ee5\u51b7\u9177\u68ee\u5bd2\u593a\u4eba\u5fc3\u795e\uff0c\u6b64\u523b\u89c1\u8fd9\u5c11\u5e74\u7684\u76ee\u5149\uff0c\u5fc3\u4e2d\u7adf\u5fae \u5fae\u4e00\u98a4\uff0c\u53e3\u4e2d\u5374\u4ecd\u7136\u51b7\u51b7\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u6728\u7136\u7684\u9762\u5bb9\u5ffd\u5730\u6cdb\u8d77\u4e00\u4e1d\u72de\u7b11\uff0c\u5de6\u638c\u4e00\u6325\uff0c\u8fce\u9762\u4e00\u638c\uff0c\u4e94\u6307\u7b95\u5f20\uff0c\u5411\u51b7\u67af\u6728 \u201c\u8fce\u9999\u201d\u3001\u201c\u56de\u767d\u201d\u3001\u201c\u4e0b\u4ed3\u201d\u4e09\u5904\u5927\u7a74\u6293\u53bb\u3002 \u3000\u3000\u51b7\u67af\u6728\u53cc\u638c\u4e00\u7ffb\uff0c\u53f3\u638c\u75be\u70b9\u4ed6\u8109\u95e8\uff0c\u5de6\u638c\u6a2a\u622a\u4ed6\u80f8\u53e3\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u602a\u7b11\u4e00\u58f0\uff0c\u53f3\u638c\u4e2d\u7684\u94f6\u68cd\uff0c\u7a81\u5730\u95ea\u7535\u822c\u51fb\u53bb\uff0c\u8fd9\u94f6\u68cd\u5728\u4ed6\u624b\u4e2d\u4ec5\u53ea\u77ed\u77ed\u4e00\u5c3a\uff0c \u4f46\u6b64\u4e00\u62db\u51fb\u51fa\uff0c\u7adf\u957f\u6709\u4e00\u4e08\u3002 \u3000\u3000\u51b7\u67af\u6728\u5fc3\u795e\u4e00\u9707\uff0c\u85cf\u5934\u7f29\u80f8\uff0c\u8eab\u8eaf\u65cb\u8f6c\uff0c\u62fc\u5c3d\u5168\u529b\uff0c\u659c\u659c\u51b2\u51fa\u4e94\u6b65\uff0c\u65b9\u81ea\u52c9\u5f3a\u8eb2\u5f00\u8fd9\u4e00 \u62db\u3002 \u3000\u3000\u4f46\u9ed1\u8863\u5c11\u5e74\u600e\u80af\u7ed9\u4ed6\u5598\u606f\u4e4b\u673a\u4f1a\uff0c\u624b\u8155\u6325\u5904\uff0c\u94f6\u5149\u95ea\u95ea\uff0c\u6709\u5982\u5343\u767e\u9053\u60ca\u8679\u5389\u7535\uff0c\u4e00\u76f4\u51fb \u5411\u51b7\u67af\u6728\u8eab\u4e0a\u3002 \u3000\u3000\u4e00\u62db\u4e4b\u4e0b\uff0c\u51b7\u67af\u6728\u4fbf\u5df2\u5c3d\u5931\u5148\u673a\uff0c\u4f46\u89c1\u8eab\u524d\u8eab\u540e\uff0c\u8eab\u5de6\u8eab\u53f3\uff0c\u4ff1\u662f\u90a3\u95ea\u52a8\u7740\u7684\u94f6\u5149\uff0c\u6ee1 \u8eab\u4ff1\u662f\u90a3\u5f3a\u52b2\u7684\u98ce\u58f0\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u9664\u4e86\u95ea\u907f\u62db\u67b6\u4e4b\u5916\uff0c\u7adf\u65e0\u6cd5\u8fd8\u51fb\u4e00\u62db\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u7709\u5b87\u95f4\u4e00\u7247\u6740\u673a\uff0c\u773c\u795e\u4e2d\u4e00\u7247\u51f6\u5149\uff0c\u5ffd\u5730\u6296\u624b\u4e00\u62a1\uff0c\u94f6\u68cd\u5df2\u53d8\u4e86\u4e03\u8282\u94f6\u97ad\uff0c\u4ee5 \u201c\u6cf0\u5c71\u538b\u9876\u201d\u4e4b\u52bf\u538b\u4e0b\u3002 \u3000\u3000\u51b7\u67af\u6728\u518d\u9000\u4e09\u6b65\uff0c\u53ea\u542c\u201c\u53ed\u201d\u5730\u4e00\u58f0\u5de8\u54cd\uff0c\u94f6\u97ad\u51fb\u5728\u706b\u7130\u4e4b\u4e0a\uff0c\u706b\u661f\u56db\u4e0b\u98de\u6fc0\uff0c\u4e00\u6bb5\u70e7 \u5f97\u900f\u7ea2\u7684\u67f4\u6728\uff0c\u5e26\u7740\u6570\u5341\u70b9\u706b\u661f\uff0c\u4e00\u8d77\u98de\u5230\u51b7\u67af\u6728\u8eab\u4e0a\uff0c\u9ed1\u8863\u5c11\u5e74\u97ad\u52bf\u56de\u5e26\uff0c\u5df2\u62e6\u8170\u626b\u81f3\uff0c \u51b7\u67af\u6728\u53cc\u81c2\u4e00\u632f\uff0c\u201c\u9ec4\u9e64\u51b2\u5929\u201d\uff0c\u62d4\u8d77\u4e00\u4e08\uff0c\u9ed1\u8863\u5c11\u5e74\u97ad\u68a2\u56de\u5e26\uff0c\u201c\u671d\u5929\u4e00\u67f1\u9999\u201d\uff0c\u76f4\u70b9\u51b7 \u67af\u6728\u811a\u5e95\u201c\u6d8c\u6cc9\u201d\u8981\u7a74\uff0c\u51b7\u67af\u6728\u7529\u811a\u62e7\u8170\uff0c\u51cc\u7a7a\u4e00\u4e2a\u8f6c\u6298\uff0c\u8fdc\u8fdc\u843d\u5230\u5730\u4e0a\uff0c\u65b9\u81ea\u5598\u606f\u4e00\u4e0b\uff0c \u4f46\u8eab\u4e0a\u7684\u706b\u661f\uff0c\u5374\u5df2\u6e10\u6e10\u5c06\u4ed6\u8863\u886b\u987b\u53d1\u71c3\u8d77\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u9762\u5e26\u72de\u7b11\uff0c\u4e00\u6b65\u5411\u524d\uff0c\u638c\u4e2d\u94f6\u97ad\u8fde\u6325\u5e26\u6253\uff0c\u8fde\u653b\u4e03\u62db\uff0c\u51b7\u67af\u6728\u867d\u7136\u95ea\u8fc7\uff0c\u4f46\u706b \u661f\u5374\u5df2\u71c3\u5f97\u66f4\u5927\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5de6\u95ea\u53f3\u907f\uff0c\u795e\u60c5\u72fc\u72c8\u4e0d\u582a\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u4e0a\u53c8\u81ea\u6cdb\u8d77\u51b7\u7b11\uff0c\u7fa4\u8c6a\u60ca\u547c\u4e4b\u58f0\uff0c\u4e0d \u7edd\u4e8e\u8033\uff0c\u8c01\u4e5f\u672a\u66fe\u60f3\u5230\u8fd9\u521d\u6b21\u5728\u6c5f\u6e56\u9732\u9762\u7684\u5c11\u5e74\uff0c\u7adf\u6709\u8fd9\u822c\u60ca\u4eba\u7684\u795e\u529b\u4e0e\u6b66\u529f\uff0c\u7adf\u80fd\u5c06\u201c\u51b7 \u8c37\u53cc\u6728\u201d\u903c\u5f97\u5982\u6b64\u72fc\u72c8\u3002 \u3000\u3000\u90a3\u8fb9\u201c\u957f\u8679\u5251\u201d\u3001\u201c\u6444\u9b42\u5200\u201d\u4ee5\u4e8c\u51fb\u4e00\uff0c\u4e5f\u6e10\u6e10\u5360\u5f97\u4e0a\u98ce\uff0c\u4e24\u9053\u96ea\u4eae\u7684\u5200\u5149\uff0c\u6709\u5982\u4ea4\u526a \u98de\u8679\uff0c\u4e00\u4e0a\u4e00\u4e0b\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u7740\u7740\u72e0\u8fa3\uff0c\u62db\u6c89\u529b\u731b\uff0c\u51b7\u5bd2\u7af9\u867d\u80fd\u6682\u65f6\u5e94\u4ed8\uff0c\u4f46\u9762\u76ee\u95ee\u4e5f\u5df2\u6709 \u4e86\u60f6\u6050\u4e4b\u6001\u3002 \u3000\u3000\u8981\u77e5\u201c\u957f\u8679\u5251\u201d\u8fb9\u5c11\u884d\u3001\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\uff0c\u4ff1\u662f\u6b66\u6797\u95f4\u7684\u4e00\u6d41\u786c\u624b\uff0c\u6b66\u529f\u7edd\u975e\u201c\u516b\u5366 \u638c\u201d\u67f3\u8f89\u3001\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u4e4b\u6d41\u53ef\u6bd4\uff0c\u5176\u4e2d\u4efb\u4f55\u4e00\u4eba\u4e4b\u529f\uff0c\u5df2\u8db3\u4ee5\u79f0\u96c4\u4e00\u65b9\uff0c\u6b64\u756a\u8054\u624b \u76f8\u653b\uff0c\u51b7\u5bd2\u7af9\u6b66\u529f\u518d\u9ad8\uff0c\u4ea6\u975e\u5176\u654c\u3002 \u3000\u3000\u88f4\u73cf\u9762\u4e0a\u9635\u9752\u9635\u767d\uff0c\u5fc3\u623f\u7830\u7830\u8df3\u52a8\uff0c\u4ed6\u89c1\u5230\u51b7\u6c0f\u5144\u5f1f\u8fd9\u79cd\u72fc\u72c8\u4e4b\u6001\uff0c\u60f3\u5230\u4ed6\u5144\u5f1f\u4e8c\u4eba\u5bf9 \u81ea\u5df1\u7684\u6069\u60c5\uff0c\u4fbf\u518d\u4e5f\u65e0\u6cd5\u5fcd\u8010\uff0c\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f4f\u624b\uff01\u201d\u8eab\u5f62\u75be\u5730\u5411\u524d\u6251\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u4ed6\u65e9\u5df2\u8574\u52b2\u5f85\u53d1\uff0c\u6b64\u523b\u559d\u5c06\u51fa\u6765\uff0c\u5f53\u771f\u6709\u5982\u6d2a\u949f\u521d\u9e23\uff0c\u58f0\u9707\u9704\u6c49\u3002 \u3000\u3000\u7fa4\u8c6a\u53ea\u89c9\u8033\u4e2d\u4e00\u9707\uff0c\u201c\u957f\u8679\u5251\u201d\u8fb9\u5c11\u884d\u3001\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\u4e0d\u7531\u81ea\u4e3b\u5730\u987f\u4f4f\u5200\u5251\u3002\u201c\u9f99\u5f62 \u516b\u638c\u201d\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u53ea\u4f5c\u672a\u95fb\uff0c\u5411\u201c\u957f\u8679\u5251\u201d\u3001\u201c\u6444\u9b42\u5200\u201d\u5fae\u4e00\u62f1\u624b\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u8d4f\u6211\u8584\u9762\uff0c\u6682\u8bf7\u4f4f \u624b\u3002\u201d \u3000\u3000\u8fb9\u3001\u7f57\u4e24\u4eba\u867d\u662f\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e00\u6d41\u9556\u5934\uff0c\u4f46\u7ec8\u662f\u5954\u8d70\u56db\u65b9\uff0c\u4ece\u672a\u4e0e\u88f4\u73cf\u8c0b\u9762\uff0c\u53ea\u77e5\u88f4\u73cf \u4e0e\u6a80\u603b\u9556\u5934\u6709\u65e7\uff0c\u6b64\u523b\u53c8\u662f\u201c\u6c5f\u5357\u540c\u76df\u201d\u7684\u76df\u4e3b\u3002 \u3000\u3000\u5982\u4eca\u89c1\u4ed6\u5982\u6b64\u8c26\u606d\u5ba2\u6c14\uff0c\u4e24\u4eba\u4ff1\u7b97\u5f88\u662f\u610f\u5916\uff0c\u8fde\u5fd9\u606d\u624b\u8fd8\u793c\u3002 \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u76ee\u5149\u5411\u90a3\u9ed1\u8863\u5c11\u5e74\u626b\u53bb\uff0c\u53ea\u89c1\u4ed6\u94f6\u97ad\u6325\u52a8\uff0c\u4e1d\u6beb\u6ca1\u6709\u4f4f\u624b\u4e4b\u610f\uff0c\u51f6\u6076\u5df2 \u6781\uff0c\u7ade\u6709\u5982\u4e00\u53ea\u53d1\u4e86\u72c2\u6027\u7684\u731b\u864e\u4e00\u822c\uff0c\u5168\u65e0\u534a\u5206\u4eba\u6027\uff0c\u88f4\u73cf\u53cc\u7709\u5fae\u5254\uff0c\u6717\u58f0\u9053\uff1a\u201c\u8c79\u5144\u2014\u2014 \u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u9ed1\u8863\u5c11\u5e74\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff0c\u94f6\u97ad\u6325\u52a8\u66f4\u6025\uff0c\u51b7\u67af\u6728\u987b\u53d1\u5df2\u71c3\u7740\u706b\uff0c\u795e\u60c5\u66f4\u662f\u72fc\u72c8 \u4e0d\u582a\u3002 \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u4e5f\u4e0d\u987e\u81ea\u5df1\u662f\u5426\u662f\u8fd9\u9ed1\u8863\u5c11\u5e74\u7684\u654c\u624b\uff0c\u84e6\u5730\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u76ee\u5c04\u51f6\u5149\uff0c\u5389\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u8981\u6765\u9001\u6b7b\uff01\u201d \u3000\u3000\u94f6\u97ad\u4e00\u632f\uff0c\u4e0d\u51fb\u51b7\u67af\u6728\uff0c\u53cd\u5411\u88f4\u73cf\u51fb\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u97ad\u52bf\u9053\u60ca\u4eba\uff0c\u98ce\u58f0\u864e\u864e\uff0c\u51b7\u6c0f\u5144\u5f1f\u9f50\u5730\u4e00\u60ca\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u4ea6\u81ea\u5fae\u5fae\u53d8\u8272\uff0c\u7fa4\u8c6a\u66f4 \u662f\u60ca\u547c\u51fa\u58f0\uff0c\u53ea\u9053\u6587\u8d28\u5f6c\u5f6c\u3001\u8d64\u624b\u7a7a\u62f3\u7684\u88f4\u73cf\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u4f1a\u662f\u8fd9\u52bf\u5982\u75af\u864e\u7684\u527d\u608d\u5c11\u5e74 \u7684\u654c\u624b\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4ea6\u81ea\u4e00\u9707\uff0c\u94f6\u5149\u95ea\u95ea\uff0c\u5df2\u5f53\u5934\u51fb\u6765\uff0c\u4ed6\u65e0\u6687\u8003\u8651\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u53f3\u638c\u659c\u5212\u534a\u5708\uff0c \u75be\u5730\u7ffb\u51fa\uff0c\u7adf\u4e00\u628a\u6293\u4f4f\u4e86\u97ad\u68a2\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e43\u662f\u201c\u6d77\u5929\u79d8\u7b08\u201d\u4e0a\u7684\u7edd\u62db\u4e4b\u4e00\uff0c\u6b66\u6797\u4e2d\u5df2\u6709\u6570\u5341\u5e74\u672a\u7779\uff0c\u7fa4\u8c6a\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c \u94f6\u97ad\u97ad\u68a2\u5df2\u5728\u88f4\u73cf\u638c\u4e2d\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u76ee\u5149\u4e00\u4eae\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u5927\u53d8\uff0c\u9ed1\u8863\u5c11\u5e74\u5927\u559d\u4e00 \u58f0\uff1a\u201c\u5f00\uff01\u201d \u3000\u3000\u53cc\u8db3\u5982\u6869\uff0c\u9489\u5728\u5730\u4e0a\uff0c\u8eab\u5f62\u540e\u4ef0\uff0c\u5168\u529b\u540e\u64a4\u3002 \u3000\u3000\u88f4\u73cf\u6839\u672c\u4e0d\u77e5\u81ea\u5df1\u6b66\u529f\u6df1\u6d45\uff0c\u4e00\u62db\u5f97\u624b\uff0c\u4ed6\u81ea\u5df1\u7adf\u7136\u5148\u6123\u4f4f\u4e86\uff0c\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u81ea\u97ad\u68a2\u4f20 \u6765\uff0c\u94f6\u97ad\u4fbf\u53c8\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u7fa4\u8c6a\u53c8\u662f\u4e00\u58f0\u60ca\u547c\uff0c\u9ed1\u8863\u5c11\u5e74\u9762\u9732\u5f97\u8272\uff0c\u624b\u8155\u4e00\u6296\uff0c\u53c8\u662f\u4e00\u97ad\u6325\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u6709\u524d\u8f66\u4e4b\u9274\uff0c\u6b64\u523b\u751f\u6015\u97ad\u68a2\u518d\u88ab\u5bf9\u65b9\u6293\u4f4f\uff0c\u662f\u4ee5\u8fd9\u4e00\u62db\u673a\u7075\u53d8\u5e7b\uff0c\u97ad\u68a2\u98a4\u52a8\uff0c\u6ee1\u84c4 \u771f\u529b\u3002 \u3000\u3000\u54ea\u77e5\u88f4\u73cf\u5de6\u638c\u4e00\u6325\uff0c\u53f3\u638c\u75be\u5730\u7ffb\u51fa\uff0c\u4e00\u6d88\u4e00\u5e26\uff0c\u7adf\u53c8\u4ee5\u539f\u5f0f\u5c06\u97ad\u68a2\u6293\u4f4f\uff0c\u800c\u4e14\u8f7b\u6613\u5730\u5316 \u53bb\u4e86\u97ad\u4e0a\u7684\u771f\u529b\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u4e0d\u4f46\u7fa4\u8c6a\u5927\u4e3a\u9707\u60ca\uff0c\u90a3\u9ed1\u8863\u5c11\u5e74\u5fc3\u5934\u4ea6\u662f\u832b\u7136\u4e0d\u89e3\uff0c\u518d\u4e5f\u60f3\u4e0d\u901a\u4e3a\u4f55\u8fd9\u5c11\u5e74\u65bd\u51fa \u5982\u6b64\u7b80\u5355\u7684\u4e00\u62db\uff0c\u7adf\u80fd\u4e24\u6b21\u6293\u4f4f\u4e86\u81ea\u5df1\u7684\u957f\u97ad\uff0c\u7adf\u5982\u63a2\u56ca\u53d6\u7269\u4e00\u822c\u8f7b\u6613\uff1f\u4ed6\u5374\u4e0d\u77e5\u88f4\u73cf\u8fd9\u4e00 \u62db\uff0c\u6b63\u662f\u5524\u4f5c\u201c\u63a2\u56ca\u53d6\u7269\u201d\uff0c\u4e43\u662f\u6b66\u529f\u4e2d\u7684\u65e0\u4e0a\u5999\u7740\uff0c\u4fbf\u662f\u4ed6\u518d\u60f3\u5c3d\u4e86\u82b1\u6837\uff0c\u51fb\u51fa\u5341\u97ad\uff0c\u88f4 \u73cf\u8fd8\u662f\u4e00\u6837\u80fd\u8f7b\u800c\u6613\u4e3e\u5730\u5c06\u4ed6\u97ad\u68a2\u6293\u4f4f\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u4e00\u6123\u4e4b\u540e\uff0c\u7d27\u54ac\u7259\u5173\uff0c\u518d\u6b21\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5f00\uff01\u201d \u3000\u3000\u88f4\u73cf\u8fd9\u4e00\u6b21\u5374\u65e9\u6709\u9632\u5907\uff0c\u771f\u6c14\u5185\u6c89\uff0c\u8eab\u5f62\u5982\u6869\uff0c\u624b\u8155\u5411\u540e\u4e00\u5e26\uff0c\u53ea\u542c\u201c\u5623\u201d\u5730\u4e00\u58f0\uff0c\u6709 \u5982\u7434\u5f26\u4e4d\u65ad\uff0c\u9ed1\u8863\u5c11\u5e74\u638c\u4e2d\u7684\u4e03\u8282\u957f\u97ad\uff0c\u7adf\u5206\u4f5c\u4e24\u622a\uff0c\u9ed1\u8863\u5c11\u5e74\u5168\u529b\u540e\u62e8\uff0c\u6b64\u523b\u7adf\u7a33\u4e0d\u4f4f\u8eab \u5f62\uff0c\u4e00\u8fde\u5411\u540e\u9000\u4e86\u4e94\u6b65\u3002 \u3000\u3000\u7fa4\u8c6a\u5fcd\u4e0d\u4f4f\u54c4\u7136\u559d\u8d77\u5f69\u6765\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u76ee\u4e2d\u5927\u9732\u559c\u8272\uff0c\u6700\u602a\u7684\u201c\u957f\u8679\u5251\u201d\u3001\u201c\u6444\u9b42 \u5200\u201d\u4e24\u4eba\uff0c\u795e\u8272\u95f4\u4e5f\u4f3c\u4e4e\u5728\u6697\u6697\u9ad8\u5174\u3002 \u3000\u3000\u539f\u6765\u8fd9\u9ed1\u8863\u5c11\u5e74\u540d\u53eb\u201c\u82d7\u8c79\u201d\uff0c\u4e43\u662f\u82d7\u7586\u5b64\u513f\uff0c\u81ea\u5e7c\u7ec3\u5f97\u4e00\u8eab\u86ee\u529b\uff0c\u53c8\u96f6\u788e\u5730\u5b66\u4e86\u4e0d\u5c11 \u6b66\u529f\uff0c\u65e0\u610f\u95f4\u88ab\u201c\u9f99\u5f62\u516b\u638c\u201d\u53d1\u73b0\u4ed6\u60ca\u4eba\u7684\u7ec3\u6b66\u7980\u8d4b\uff0c\u4fbf\u5c06\u4e4b\u6536\u5f52\u95e8\u4e0b\uff0c\u7565\u4e00\u6307\u70b9\uff0c\u6b66\u529f\u679c \u7136\u4e00\u65e5\u5343\u91cc\u3002 \u3000\u3000\u4ed6\u81ea\u77e5\u5728\u201c\u9f99\u5f62\u516b\u638c\u201d\u773c\u524d\u6781\u4e3a\u5f97\u5ba0\uff0c\u5e73\u65e5\u5c31\u6839\u672c\u672a\u5c06\u8fb9\u5c11\u884d\u3001\u7f57\u4e49\u4e00\u822c\u9556\u5e08\u770b\u5728\u773c \u91cc\uff0c\u522b\u4eba\u754f\u60e7\u4ed6\u5929\u751f\u7684\u795e\u529b\u4e0e\u5947\u5f02\u7684\u7980\u8d4b\uff0c\u4e5f\u53ea\u5f97\u8ba9\u4ed6\u4e09\u5206\uff0c\u5e73\u65e5\u79ef\u6028\u5df2\u6df1\uff0c\u6b64\u523b\u4ed6\u53d7\u632b\u4e8e \u4eba\uff0c\u522b\u4eba\u81ea\u7136\u6697\u4e2d\u9ad8\u5174\u3002 \u3000\u3000\u4f46\u201c\u9f99\u5f62\u516b\u638c\u201d\u5374\u662f\u9762\u8272\u5927\u53d8\uff0c\u53ea\u89c1\u90a3\u9ed1\u8863\u5c11\u5e74\u82d7\u8c79\u7ad9\u7a33\u8eab\u5f62\uff0c\u671b\u4e86\u671b\u638c\u4e2d\u7684\u65ad\u97ad\uff0c\u4f3c \u4e4e\u8fd8\u4e0d\u76f8\u4fe1\u81ea\u5df1\u6240\u5411\u65e0\u6545\u7684\u795e\u529b\uff0c\u4eca\u65e5\u4f1a\u9047\u7740\u5bf9\u624b\uff0c\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u7a81\u53c8\u5927\u558a\u4e00\u58f0\uff0c\u5411\u524d \u6251\u53bb\u3002 \u3000\u3000\u88f4\u73cf\u4e00\u62db\u5f97\u624b\uff0c\u4fe1\u5fc3\u5df2\u751f\uff0c\u811a\u6b65\u4e00\u8f6c\uff0c\u8f7b\u8f7b\u8ba9\u5f00\u4e86\u8fd9\u9ed1\u8863\u5c11\u5e74\u7684\u6765\u52bf\uff0c\u968f\u624b\u5c06\u638c\u4e2d\u534a\u622a \u65ad\u97ad\u6325\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u97ad\u867d\u662f\u968f\u624b\u6325\u51fa\uff0c\u5374\u662f\u5999\u7740\u5929\u6210\uff0c\u82d7\u8c79\u7ffb\u8eab\u4e00\u8ba9\uff0c\u4f46\u8863\u8882\u7adf\u53c8\u88ab\u88f4\u73cf\u7684\u65ad\u97ad\u97ad\u68a2\u6253 \u4e2d\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u6b66\u529f\u867d\u900a\u4e8e\u88f4\u73cf\u4e00\u7b79\uff0c\u4f46\u4ea4\u624b\u7ecf\u9a8c\u5374\u80dc\u8fc7\u88f4\u73cf\u8bb8\u591a\uff0c\u53ea\u8981\u6c89\u7740\u5e94\u6218\uff0c\u672a\u5c1d\u4e0d\u80fd\u652f \u6301\u4e00\u9635\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6b64\u523b\u9762\u4e0a\u867d\u51f6\u72e0\uff0c\u5b9e\u5728\u5df2\u88ab\u88f4\u73cf\u90a3\u5947\u5965\u7684\u7edd\u5b66\u6240\u6151\uff0c\u5fc3\u795e\u65e2\u8e81\uff0c\u80c6\u6c14\u53c8\u4e27\uff0c\u7eb5\u7136 \u60c5\u6025\u62fc\u547d\uff0c\u53c8\u6709\u4f55\u7528\uff1f \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u4e00\u5254\uff0c\u6c89\u58f0\u53f1\u9053\uff1a\u201c\u8c79\u5b50\uff0c\u4f4f\u624b\uff01\u201d \u3000\u3000\u559d\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u4ed6\u8f7b\u8f7b\u8fc8\u51fa\u4e00\u6b65\uff0c\u9ad8\u5927\u7684\u8eab\u5f62\uff0c\u4f7f\u5df2\u5230\u4e86\u9ed1\u8863\u5c11\u5e74\u82d7\u8c79\u7684\u8eab\u4fa7\uff0c\u5288\u624b\u593a \u8fc7\u4e86\u82d7\u8c79\u638c\u4e2d\u7684\u534a\u622a\u94f6\u97ad\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8fd8\u4e0d\u4e0b\u53bb\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6b65\u3001\u4e00\u593a\uff0c\u8eab\u6cd5\u4e4b\u7094\uff0c\u624b\u6cd5\u4e4b\u5999\uff0c\u4ea6\u662f\u9a87\u4eba\u542c\u95fb\uff0c\u7fa4\u8c6a\u53c8\u662f\u54c4\u7136\u4e00\u9635\u7b11\u58f0\uff0c\u82d7\u8c79\u9762 \u8272\u94c1\u9752\uff0c\u8fde\u9000\u6570\u6b65\uff0c\u7a81\u5730\u7ffb\u8eab\u98de\u5954\u800c\u53bb\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u624b\u6301\u65ad\u97ad\uff0c\u671b\u4e5f\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u5374\u5411\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\u3002\u8fd9\u2500\u2500\u70b9\u5728\u522b\u4eba\u773c\u91cc\uff0c \u56fa\u662f\u5e73\u5e73\u548c\u548c\uff0c\u4f46\u88f4\u73cf\u5374\u4e0d\u7981\u5fc3\u5934\u4e00\u5bd2\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u81ea\u5df1\u5e7c\u5e74\u65f6\u751f\u6d3b\u5728\u201d\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u7684\u60c5 \u666f\u3002 \u3000\u3000\u90a3\u65f6\u8fd9\u201c\u6a80\u5927\u53d4\u201d\u9762\u4e0a\uff0c\u5c31\u65f6\u5e38\u5e26\u7740\u8fd9\u79cd\u5fae\u7b11\uff0c\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u4ed6\u603b\u89c9\u5f97\u5728\u8fd9\u5e73\u548c\u7684\u7b11\u5bb9 \u4e2d\uff0c\u4eff\u4f5b\u9690\u85cf\u7740\u4e00\u4efd\u5bd2\u610f\uff0c\u6bcf\u5f53\u4ed6\u4e0e\u6a80\u6587\u742a\u8bf4\u8bdd\u6216\u6e38\u620f\u7684\u65f6\u5019\uff0c\u201c\u6a80\u5927\u53d4\u201d\u5c31\u4f1a\u5e26\u7740\u8fd9\u4efd\u7b11 \u5bb9\u5c06\u5979\u5524\u8d70\u3002 \u3000\u3000\u6709\u4e00\u6b21\u4ed6\u65e0\u610f\u95f4\u8d70\u5230\u201c\u6a80\u5927\u53d4\u201d\u7684\u4e66\u623f\u4e2d\u53bb\uff0c\u201c\u6a80\u5927\u53d4\u201d\u6b63\u5728\u6848\u8fb9\u628a\u73a9\u7740\u4e00\u6837\u4e1c\u897f\uff0c\u89c1 \u5230\u4ed6\u8d70\u8fdb\u53bb\u540e\uff0c\u9762\u4e0a\u4e5f\u5c55\u5f00\u4e86\u4e00\u4efd\u8fd9\u6837\u7684\u7b11\u5bb9\uff0c\u4f46\u5374\u544a\u8bc9\u4ed6\uff0c\u4ece\u6b64\u4ee5\u540e\uff0c\u4e0d\u51c6\u4ed6\u518d\u5230\u4e66\u623f\u4e2d \u53bb\u3002 \u3000\u3000\u4ed6\u82e5\u662f\u5f97\u5230\u4e86\u4e00\u4ef6\u5fc3\u7231\u7684\u4e1c\u897f\uff0c\u201c\u6a80\u5927\u53d4\u201d\u5c31\u4f1a\u5e26\u7740\u8fd9\u4efd\u7b11\u5bb9\u5c06\u4ed6\u7684\u4e1c\u897f\u62ff\u53bb\uff0c\u5e76\u4e14\u544a \u8bc9\u4ed6\uff0c\u5c11\u5e74\u4eba\u4e0d\u53ef\u73a9\u7269\u4e27\u5fd7\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u6ca1\u6709\u5bf9\u8fd9\u4e9b\u4e8b\u6000\u6068\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u8fd9\u662f\u201c\u6a80\u5927\u53d4\u201d\u5bf9\u4ed6\u7684\u6559\u8bad\uff0c\u8981\u4ed6\u5b66\u597d\uff0c\u4f46\u4e0d\u77e5 \u600e\u5730\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u53c8\u89c1\u5230\u8fd9\u4efd\u7b11\u5bb9\u7684\u65f6\u5019\uff0c\u8fd9\u4efd\u5f80\u4e8b\u5374\u5ffd\u7136\u4ff1\u90fd\u5728\u4ed6\u5fc3\u4e2d\u95ea\u8fc7\uff0c\u4f7f\u5f97\u4ed6\u5fc3 \u91cc\u53c8\u751f\u51fa\u5e7c\u5e74\u65f6\u540c\u6837\u7684\u5bd2\u610f\u3002 \u3000\u3000\u4ed6\u60c5\u4e0d\u81ea\u7981\u5730\u5411\u540e\u9000\u4e86\u534a\u6b65\uff0c\u53ea\u542c\u201c\u9f99\u5f62\u516b\u638c\u201d\u542b\u7b11\u9053\uff1a\u201c\u4eba\u9053\u96cf\u51e4\u4e4b\u58f0\uff0c\u5fc5\u5b9a\u6e05\u4e8e\u8001 \u51e4\uff0c\u8d24\u4f84\u4f60\u4e00\u545c\u60ca\u4eba\uff0c\u6a80\u5927\u53d4\u5fc3\u91cc\u81ea\u7136\u6b22\u559c\uff0c\u4f46\u6b64\u523b\u4f60\u8fd8\u662f\u8d70\u5f00\u4e9b\u7684\u597d\u3002\u201d \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u7b49\u88f4\u73cf\u7b54\u8bdd\uff0c\u4fbf\u8f6c\u8fc7\u8eab\u53bb\uff0c\u9762\u5bf9\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u638c\u4e2d\u64ad\u5f04\u7740\u90a3\u534a\u622a\u94f6 \u97ad\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u8d24\u6606\u4ef2\u7edd\u6280\u60ca\u4eba\uff0c\u8001\u592b\u770b\u5f97\u4e5f\u89c9\u6280\u75d2\uff0c\u82e5\u662f\u8d24\u6606\u4ef2\u5e76\u4e0d\u5b8c\u5168\u4f9d\u4ed7\u7740\u6211\u90a3\u88f4 \u8d24\u4f84\u7684\u8bdd\u2014\u2014\u201d\u4ed6\u7b11\u5bb9\u7a81\u7136\u4e00\u655b\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8001\u592b\u8c28\u5411\u4e24\u4f4d\u6311\u6218\uff01\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u7fa4\u8c6a\u4ff1\u90fd\u5927\u60ca\uff0c\u53c8\u4e0d\u7981\u5728\u6697\u4e2d\u81ea\u53f9\u773c\u798f\u4e0d\u6d45\uff0c\u5728\u540e\u9762\u7684\u4eba\uff0c\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u4e5f \u4e00\u8d77\u6d8c\u4e0a\u524d\u6765\u3002 \u3000\u3000\u5341\u4f59\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u4eba\u4ece\u672a\u89c1\u8fc7\u8fd9\u540d\u9707\u5929\u4e0b\u7684\u6b66\u6797\u5927\u8c6a\u4eb2\u81ea\u51fa\u4e8e\uff0c\u8c01\u4e5f\u65e0\u6cd5\u4f30\u91cf\u4ed6\u6b66\u529f\u7684 \u6df1\u6d45\u3002 \u3000\u3000\u6b64\u523b\u7fa4\u8c6a\u6697\u4e2d\u7a83\u7a83\u79c1\u8bed\uff0c\u53c8\u5728\u6253\u8d77\u8d4c\u6765\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u2018\u9f99\u5f62\u516b\u638c\u2019\u80fd\u5728\u591a\u5c11\u62db\u4e4b\u95f4\u51fb\u8d25\u51b7\u5bb6\u5144\u5f1f\uff1f\u201d \u3000\u3000\u201c\u4e94\u5341\u62db\uff01\u201d \u3000\u3000\u201c\u4e09\u5341\u62db\uff01\u201d \u3000\u3000\u201c\u6211\u8d4c\u5341\u4e94\u4e24\u3002\u201d\u201c\u4e09\u5341\u62db\uff01\u201d \u3000\u3000\u201c\u6211\u8d4c\u4e00\u5339\u5ddd\u9a6c\uff0c\u4e94\u5341\u62db\uff01\u201d\u7adf\u65e0\u4e00\u4eba\u672a\u8d4c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u80dc\u7684\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9762\u8272\u4ecd\u662f\u9634\u6c89\u5982\u6b64\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5144\u5f1f\u4e24\u4eba\u5fc3\u91cc\u7a76\u7adf\u5728\u60f3\u4e9b\u4ec0\u4e48\u3002\u751f\u6b7b\u5173 \u5934\uff0c\u4ecd\u6709\u8fd9\u4efd\u5f02\u5e38\u7684\u9547\u9759\uff0c\u7fa4\u8c6a\u53c8\u4e0d\u7981\u5728\u6697\u4e2d\u559d\u5f69\u3002 \u3000\u3000\u4ed6\u5144\u5f1f\u4e8c\u4eba\u53ea\u662f\u6de1\u6de1\u5411\u88f4\u73cf\u671b\u4e86\u4e00\u773c\uff0c\u7136\u540e\u4e00\u6574\u8863\u886b\uff0c\u5e76\u80a9\u8d70\u5230\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u524d\uff0c\u51b7 \u51b7\u9053\uff1a\u201c\u662f\u6bd4\u6b66\u6291\u6216\u662f\u2014\u2014\u201d\u201c\u9f99\u5f62\u516b\u638c\u201d\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u662f\u5426\u6bd4\u6b66\uff0c\u4f60\u5144\u5f1f\u4e24\u4eba\u53ea\u7ba1\u4e00 \u8d77\u4e0a\u6765\u597d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u624b\u638c\u4e00\u6325\uff0c\u53ea\u89c1\u4e00\u9053\u94f6\u5149\uff0c\u8131\u624b\u98de\u51fa\uff0c\u6709\u5982\u6d41\u661f\u4e00\u822c\u6ca1\u4eba\u9ed1\u6697\u91cc\uff0c\u970e\u773c\u95f4\u4fbf\u6d88\u5931\u5f97\u65e0 \u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u8fd9\u822c\u60ca\u4eba\u7684\u624b\u529b\uff0c\u81ea\u7136\u53c8\u5f15\u8d77\u7fa4\u8c6a\u4e00\u9635\u60ca\u53f9\uff0c\u201c\u957f\u8679\u5251\u201d\u201c\u6444\u9b42\u5200\u201d\u9f50\u5730\u540e\u9000\u5341\u6b65\u3002 \u3000\u3000\u60ca\u53f9\u4e4b\u58f0\uff0c\u54cd\u904d\u539f\u91ce\uff0c\u4f46\u88f4\u73cf\u5374\u662f\u5145\u8033\u4e0d\u95fb\uff0c\u4ed6\u5fc3\u91cc\u72b9\u5728\u60f3\u7740\u65b9\u624d\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6de1\u6de1\u5730 \u671b\u4ed6\u90a3\u4e00\u773c\u4e4b\u4e2d\u7684\u542b\u610f\u3002 \u3000\u3000\u53ea\u6709\u4ed6\u80fd\u4e86\u89e3\u8fd9\u4e24\u4e2a\u51b7\u9177\u5b64\u72ec\u7684\u8001\u4eba\u5fc3\u4e2d\u7684\u6c89\u91cd\uff0c\u53ea\u6709\u4ed6\u80fd\u4f53\u4f1a\u51fa\u90a3\u4e00\u773c\u4e4b\u4e2d\u7684\u54c0\u75db\u3002 \u3000\u3000\u90a3\u4e00\u773c\u4e4b\u4e2d\uff0c\u5305\u542b\u4e86\u5bf9\u751f\u547d\u7684\u8bc0\u522b\uff0c\u4e5f\u5305\u542b\u4e86\u5bf9\u88f4\u73cf\u7684\u60c5\u611f\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u5728\u9057\u61be\u7740\u81ea\u5df1\u4e0d \u80fd\u773c\u89c1\u88f4\u73cf\u7684\u5149\u8292\uff0c\u7167\u8000\u6b66\u6797\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5267\u6218\u65b9\u4f11\uff0c\u81ea\u5fd6\u6b66\u529f\u3001\u771f\u529b\uff0c\u4ff1\u90fd\u4e07\u4e07\u4e0d\u4f1a\u662f\u201c\u9f99 \u5f62\u516b\u638c\u201d\u7684\u654c\u624b\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u88f4\u73cf\u53ea\u89c9\u5fc3\u4e2d\u601d\u7eea\u5176\u4e71\u5982\u9ebb\u3002\u8bba\u6069\u60c5\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u56fa\u7136\u629a\u517b\u81ea\u5df1\u6210\u4eba\uff0c\u4f46 \u6ca1\u6709\u201c\u51b7\u8c37\u53cc\u6728\u201d\uff0c\u81ea\u5df1\u7109\u6709\u4eca\u65e5\uff1f\u8bba\u611f\u60c5\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u867d\u7136\u51b7\u9177\uff0c\u4f46\u5bf9\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u5374 \u8fde\u90a3\u51b7\u9177\u7684\u9762\u8c8c\u4e5f\u63a9\u9970\u4e0d\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u201c\u9f99\u5f62\u516b\u638c\u201d\u7a81\u5730\u53cc\u624b\u4e00\u62cd\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u6211\u64c5\u660e\u8d64\u624b\u7a7a\u62f3\uff0c\u82e5\u662f\u4e0d\u80fd\u53d6\u4e24\u4f4d\u7684\u6027 \u547d\uff0c\u65b0\u4ec7\u65e7\u6068\uff0c\u4fbf\u4ece\u6b64\u4e00\u7b14\u52fe\u6d88\u3002\u6765\uff0c\u6765\uff0c\u6765\uff01\u201d \u3000\u3000\u5639\u4eae\u7684\u7b11\u58f0\uff0c\u58f0\u9707\u56db\u91ce\uff0c\u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u5728\u8fd9\u9707\u8033\u7684\u7b11\u58f0\u4e4b\u4e2d\uff0c\u7f13\u6b65\u5411\u201c\u51b7\u8c37\u53cc\u6728\u201d \u8d70\u53bb\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8eab\u5f62\u4e00\u5206\uff0c\u5de6\u53f3\u5bf9\u7acb\uff0c\u76ee\u5149\u70af\u70af\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u201c\u9f99\u5f62\u516b\u638c\u201d\u7684\u811a\u6b65\u4e4b \u4e0a\u3002 \u3000\u3000\u77ac\u773c\u4e4b\u95f4\uff0c\u4ed6\u9ad8\u5927\u7684\u8eab\u5f62\uff0c\u8ddd\u79bb\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5df2\u4e0d\u53ca\u4e09\u6b65\uff0c\u53ea\u8981\u4ed6\u624b\u638c\u4e00\u62ac\uff0c\u4fbf\u53ef\u89e6\u53ca \u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u7a74\u9053\u3002 \u3000\u3000\u88f4\u73cf\u62ac\u773c\u4e00\u671b\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u51b0\u51b7\u7684\u76ee\u5149\uff0c\u53c8\u6070\u5de7\u5411\u4ed6\u779f\u4e86\u4e00\u773c\uff0c\u8fd9\u4e00\u773c\u4e4b\u4e0b\uff0c\u88f4\u73cf\u53ea \u89c9\u5fc3\u5934\u70ed\u8840\u4e0a\u6d8c\uff0c\u7a81\u53c8\u8f7b\u559d\u4e00\u58f0\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u559d\u58f0\u867d\u4e0d\u54cd\u4eae\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u88f4\u73cf\u5728\u4f17\u4eba\u5fc3\u76ee\u4e2d\u7684\u8eab\u4efd\u5730\u4f4d\u5374\u5df2\u5927\u4e0d\u76f8\u540c\uff0c\u9f50\u5730 \u8038\u7136\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u811a\u6b65\u4e00\u6ed1\uff0c\u6ed1\u5728\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8eab\u65c1\uff0c\u5f20\u5f00\u53cc\u624b\u3002\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u4e00\u6c89\uff0c \u5389\u58f0\u9053\uff1a\u201c\u4f60\u7fbd\u6bdb\u5df2\u4e30\uff0c\u96be\u9053\u5c31\u60f3\u6765\u4e0e\u6a80\u5927\u53d4\u8f83\u91cf\u8f83\u91cf\u4e86\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5782\u9996\u9063\uff1a\u2018\u4e0d\u6562\uff01\u201c\u201d\u9f99\u5f62\u516b\u638c\u201c\u6a80\u660e\u5fae\u7b11\u4e00\u4e0b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u9000\u5f00\u53bb\uff01\u201d \u3000\u3000\u88f4\u73cf\u970d\u7136\u62ac\u8d77\u5934\u6765\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5c0f\u4f84\u6597\u80c6\uff0c\u4eca\u65e5\u8bf7\u6a80\u5927\u53d4\u6682\u4e14\u653e\u624b\uff0c\u7b49\u5230\u6b64\u95f4\u8d4c\u7ea6\u5206\u51fa \u80dc\u8d1f\u540e\u2026\u2026\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u4ef0\u5929\u51b7\u7b11\u51e0\u58f0\uff0c\u622a\u53e3\u9053\uff1a\u201c\u597d\u6781\u597d\u6781\uff01\u83ab\u975e\u6b64\u523b\u6211\u7684\u884c\u52a8\uff0c\u5df2\u8981\u53d7\u4f60\u7684\u9650 \u5236\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u4e86\uff0c\u7a81\u5730\u4f38\u51fa\u4e00\u638c\uff0c\u5411\u88f4\u73cf\u80a9\u5934\u63a8\u53bb\uff0c\u53e3\u4e2d\u5927\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d \u3000\u3000\u88f4\u73cf\u76ee\u5149\u70af\u70af\uff0c\u4e0d\u907f\u4e0d\u95ea\uff0c\u4ed6\u672c\u60f3\u62fc\u7740\u8eab\u53d7\u6a80\u660e\u4e00\u638c\uff0c\u54ea\u77e5\u4ed6\u751f\u6b7b\u7384\u5173\u4e00\u901a\u4e4b\u540e\uff0c\u5168\u8eab \u7cbe\u529b\u5185\u8574\uff0c\u5bf9\u522b\u4eba\u7684\u62f3\u8db3\u62db\u5f0f\uff0c\u81ea\u6709\u4e00\u79cd\u53cd\u6297\u4e4b\u52bf\uff0c\u6b63\u5982\u5e38\u4eba\u4f38\u624b\u89e6\u7740\u706b\u661f\uff0c\u8111\u7b4b\u5c1a\u672a\u601d \u7d22\uff0c\u624b\u638c\u4f1a\u81ea\u52a8\u5f39\u5f00\u4e00\u6837\u3002 \u3000\u3000\u6a80\u660e\u4e00\u638c\u62cd\u6765\uff0c\u4ed6\u867d\u60f3\u4e0d\u8fce\u4e0d\u67b6\uff0c\u4e0d\u907f\u4e0d\u95ea\uff0c\u4f46\u638c\u98ce\u89e6\u4f53\u4e4b\u540e\uff0c\u4ed6\u5de6\u638c\u7a81\u5730\u4e0b\u610f\u8bc6\u5730\u5411 \u4e0a\u4e00\u7ffb\uff0c\u6070\u5de7\u622a\u5411\u6a80\u660e\u8155\u8109\u4e4b\u5904\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u4e00\u626c\uff0c\u624b\u8155\u4e00\u6296\uff0c\u638c\u52bf\u7a81\u53d8\u65b9\u5411\uff0c\u54ea\u77e5\u88f4\u73cf\u7684\u624b\u638c\u4e4b\u4e0a\uff0c\u4eff\u4f5b\u751f\u4e86\u773c \u775b\u4e00\u822c\uff0c\u7adf\u4e5f\u987a\u52bf\u8f6c\u53bb\uff0c\u4e94\u6307\u6307\u5c16\uff0c\u59cb\u7ec8\u4e0d\u79bb\u4ed6\u624b\u638c\u8155\u8109\u4e4b\u95f4\u3002 \u3000\u3000\u5176\u5b9e\u88f4\u73cf\u4e5f\u4e0d\u77e5\u81ea\u5df1\u624b\u638c\u7adf\u4f1a\u5982\u6b64\u8f6c\u52a8\u3001\u50cf\u662f\u987a\u7406\u6210\u7ae0\uff0c\u6c34\u5230\u6e20\u620d\uff0c\u624b\u638c\u81ea\u7136\u800c\u7136\u5730\u8f6c \u52a8\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u90a3\u201c\u6d77\u5929\u79d8\u7b08\u201d\uff0c\u539f\u662f\u6614\u5e74\u6b66\u5b66\u7684\u4e00\u4ee3\u5947\u624d\u201c\u6d77\u592d\u5b64\u71d5\u201d\u8017\u8d39\u4e00\u751f\u7cbe\u529b\u6240\u8457\uff0c \u201c\u6d77\u5929\u5b64\u71d5\u201d\u58ee\u5c81\u95ef\u8361\u6c5f\u6e56\uff0c\u5929\u4e0b\u5404\u95e8\u5404\u6d3e\u7684\u6b66\u529f\uff0c\u4ed6\u5747\u6709\u6d89\u730e\u3002\u662f\u4ee5\u8fd9\u672c\u201c\u6d77\u5929\u79d8\u7b08\u201d\u4e0a \u6240\u8f7d\uff0c\u5b9e\u662f\u5929\u4e0b\u6b66\u5b66\u7684\u603b\u7ec4\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u4e0d\u8bba\u5185\u5bb6\u5916\u5bb6\uff0c\u62f3\u6cd5\u5251\u672f\uff0c\u5404\u79cd\u6cd5\u95e8\u8bc0\u7a8d\uff0c\u4ff1\u5728\u8fd9 \u672c\u6b66\u529f\u79d8\u5f55\u4e4b\u4e0a\u3002 \u3000\u3000\u88f4\u73cf\u4e00\u5e74\u4ee5\u6765\uff0c\u5df2\u5c06\u8fd9\u672c\u6b66\u529f\u79d8\u7b08\u7ec3\u5f97\u6eda\u74dc\u70c2\u719f\uff0c\u800c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6b64\u523b\u6240\u4f7f\u7684\u624b\u6cd5\uff0c\u6b63 \u5305\u6db5\u5728\u201c\u6d77\u5927\u79d8\u7b08\u201d\u4e4b\u4e2d\uff0c\u88f4\u73cf\u4e0d\u77e5\u4e0d\u89c9\u5730\u4f7f\u51fa\u4e00\u62db\uff0c\u4fbf\u6070\u5de7\u662f\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u8fd9\u4e00\u638c\u7684 \u514b\u661f\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e24\u4eba\u624b\u638c\u6e38\u8d70\uff0c\u5b9b\u5982\u592a\u6781\u62f3\u4e2d\u7684\u63a8\u624b\uff0c\u7fa4\u8c6a\u54ea\u91cc\u77e5\u9053\u8fd9\u5176\u95f4\u7684\u5965\u5999\uff0c\u5404\u5404\u770b\u5f97\u76ee \u77aa\u53e3\u5446\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u6c89\u5982\u6c34\uff0c\u5fc3\u4e0b\u5927\u662f\u60ca\u5f02\uff0c\u624b\u638c\u4e09\u8f6c\u4e4b\u540e\uff0c\u7a81\u5730\u638c\u52bf\u4e00\u6536\uff0c\u51dd\u76ee\u77a7\u4e86\u88f4\u73cf \u4e24\u773c\uff0c\u54c8\u54c8\u5f3a\u7b11\u9053\uff1a\u201c\u73cf\u513f\uff0c\u4f60\u96be\u9053\u771f\u5730\u8981\u6211\u5927\u53d4\u52a8\u624b\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u80f8\u819b\u4e00\u633a\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f46\u613f\u6a80\u5927\u53d4\u624b\u4e0b\u7559\u60c5\uff0c\u653e\u8fc7\u4eca\u65e5\uff01\u201d \u3000\u3000\u4ed6\u672c\u89c9\u6a80\u660e\u8eab\u5f62\u751a\u662f\u9ad8\u5927\uff0c\u6b64\u523b\u80f8\u819b\u4e00\u633a\uff0c\u7a81\u5730\u53d1\u89c9\u81ea\u5df1\u7adf\u662f\u548c\u6a80\u660e\u4e00\u822c\u9ad8\u77ee\uff0c\u5239\u90a3\u95f4 \u4ed6\u5bf9\u6a80\u660e\u7684\u754f\u60e7\u4e4b\u5fc3\uff0c\u4fbf\u4e5f\u7a81\u7136\u6d88\u53bb\u8bb8\u591a\u3002 \u3000\u3000\u6a80\u660e\u76ee\u5149\u4e00\u95ea\uff0c\u5fc3\u4e2d\u601d\u5ff5\u987f\u8f6c\uff0c\u5341\u5e74\u4e4b\u524d\uff0c\u4ed6\u4fbf\u9690\u9690\u6210\u4e3a\u6c5f\u6e56\u8c6a\u6770\u4e2d\u7684\u9886\u8896\u4eba\u7269\uff0c\u6b64\u523b \u82e5\u771f\u4e0e\u8fd9\u4e0d\u6ee1\u4e8c\u5341\u7684\u5c11\u5e74\u52a8\u624b\uff0c\u4e0d\u8bba\u80dc\u8d1f\uff0c\u4ff1\u4e0d\u5149\u5f69\uff0c\u4ed6\u80f8\u4e2d\u867d\u5df2\u6d8c\u8d77\u6740\u673a\u6012\u706b\uff0c\u4f46\u9762\u4e0a\u4ecd \u662f\u6ee1\u9762\u7b11\u5bb9\uff0c\u542b\u7b11\u9053\uff1a\u201c\u82e5\u4ee5\u4f60\u6211\u7684\u60c5\u4efd\uff0c\u4f60\u6240\u6c42\u6211\u4e4b\u4e8b\uff0c\u6211\u672c\u4e0d\u4f1a\u592a\u8fc7\u4f7f\u4f60\u96be\u53d7\uff0c\u4f46\u4eca\u65e5 \u8fc7\u540e\uff0c\u4f60\u82e5\u518d\u2014\u2014\u201d\u88f4\u73cf\u622a\u53e3\u9053\uff1a\u201c\u53ea\u8981\u7b49\u5230\u5c0f\u4f84\u80dc\u8d1f\u5206\u51fa\u4e4b\u540e\uff0c\u6a80\u5927\u53d4\u5c3d\u53ef\u518d\u4e0e\u4e24\u4f4d\u51b7\u8001 \u524d\u8f88\u4e00\u51b3\u96cc\u96c4\uff0c\u5230\u90a3\u65f6\u5c0f\u4f84\u600e\u6562\u591a\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u8a00\u8bed\u4e4b\u95f4\uff0c\u4e1d\u6beb\u6ca1\u6709\u4e3a\u201c\u51b7\u8c37\u53cc\u6728\u201d\u793a\u5f31\u4e4b\u610f\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6728\u7acb\u5f53\u5730\uff0c\u5fc3\u4e2d\u5927\u662f\u611f \u6fc0\u3002 \u3000\u3000\u8981\u77e5\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6210\u540d\u5df2\u4e45\uff0c\u4ea6\u662f\u6b66\u6797\u4e2d\u54cd\u5f53\u5f53\u7684\u4eba\u7269\uff0c\u82e5\u662f\u88f4\u73cf\u4ee5\u4fdd\u62a4\u4ed6\u4e24\u4eba\u7684\u59ff\u52bf \u51fa\u73b0\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u62fc\u5374\u4e00\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u5728\u8fd9\u8bb8\u591a\u6b66\u6797\u8c6a\u58eb\u9762\u4e0a\u793a\u5f31\uff1b\u4f46\u88f4\u73cf\u5982\u6b64\u8bf4\u8bdd\uff0c\u4fbf\u4f7f \u5f97\u4ed6\u4eca\u65e5\u6b64\u4e3e\uff0c\u770b\u6765\u5168\u662f\u4e3a\u4e86\u8d4c\u7ea6\uff0c\u5e76\u975e\u660e\u77e5\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5b9a\u8981\u843d\u8d25\uff0c\u800c\u52a0\u4fdd\u62a4\u3002 \u3000\u3000\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u62a4\u4eba\u81ea\u5c0a\uff0c\u771f\u662f\u8fdc\u6bd4\u6551\u4eba\u6027\u547d\u8fd8\u8981\u91cd\u8981\uff0c\u88f4\u73cf\u867d\u65e0\u6c5f\u6e56\u5386\u7ec3\uff0c\u4f46\u4ed6\u751f\u6027\u4ec1 \u539a\uff0c\u81ea\u4e0d\u613f\u4f24\u4eba\u5c0a\u4e25\uff0c\u4e5f\u5c31\u662f\u8fd9\u79cd\u4ec1\u539a\u5bbd\u5927\u7684\u5929\u6027\uff0c\u4f7f\u5f97\u4ed6\u65e5\u540e\u7ec8\u4e8e\u6210\u4e3a\u6c5f\u6e56\u4e2d\u9ed1\u767d\u4e24\u9053\u7684 \u9886\u8896\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u76ee\u5149\u95ea\u52a8\uff0c\u7a81\u5730\u8f6c\u8eab\u5927\u559d\u9053\u3002\u201c\u4f60\u4eec\u53ef\u66fe\u542c\u5230\u88f4\u5927\u5148\u751f\u7684\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u7a81\u5730\u95ee\u51fa\u6b64\u8bdd\uff0c\u7fa4\u8c6a\u4ff1\u90fd\u4e00\u6014\uff0c\u53ea\u542c\u4ed6\u63a5\u53e3\u559d\u9053\uff1a\u201c\u8d4c\u7ea6\u80dc\u8d1f\u672a\u5206\u4e4b\u524d\uff0c\u82e5\u6709\u4eba\u8981\u5bf9 \u2018\u51b7\u8c37\u53cc\u6728\u2019\u4e0d\u5229\uff0c\u4fbf\u4e5f\u662f\u770b\u6211\u2018\u9f99\u5f62\u516b\u638c\u2019\u4e0d\u8d77\u3002\u201d \u3000\u3000\u4ed6\u81ea\u627e\u53f0\u9636\u4e0b\u5802\uff0c\u4f46\u8bf4\u8bdd\u4ecd\u662f\u51a0\u5195\u5802\u7687\uff0c\u7fa4\u8c6a\u54c4\u7136\u7b54\u5e94\u4e00\u58f0\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u4e0a\u53c8\u81ea\u6062 \u590d\u4e86\u90a3\u81ea\u4fe1\u7684\u7b11\u5bb9\uff0c\u655e\u58f0\u9053\uff1a\u201c\u6211\u4e0e\u88f4\u5927\u5148\u751f\u4e24\u4ee3\u76f8\u4ea4\uff0c\u51e1\u662f\u88f4\u5927\u5148\u751f\u8bf4\u51fa\u7684\u8bdd\uff0c\u4e5f\u5c31\u7b49\u4e8e \u6211\u8bf4\u7684\u4e00\u6837\uff0c\u51e1\u662f\u6211\u6a80\u660e\u7684\u670b\u53cb\uff0c\u6b64\u540e\u4e5f\u5c31\u662f\u88f4\u5927\u5148\u751f\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u4e3a\u4e86\u81ea\u6043\u8eab\u4efd\uff0c\u4fbf\u4e5f\u6781\u529b\u62ac\u9ad8\u88f4\u73cf\u7684\u8eab\u4ef7\uff0c\u7fa4\u8c6a\u53c8\u662f\u4e00\u9635\u54c4\u7136\u559d\u5f69\uff0c\u88f4\u73cf\u5fc3\u4e2d\u5374\u5927\u662f \u611f\u6fc0\uff0c\u5fd6\u9053\uff1a\u201c\u6a80\u5927\u53d4\u5bf9\u6211\u6bd5\u7adf\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u5e26\u5fae\u7b11\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u7d27\u7d27\u63e1\u4f4f\u4ed6\u7684\u624b\u638c\uff0c\u9053\uff1a\u201c\u73cf\u513f\uff0c\u4f60\u4eca\u65e5\u80fd\u6709\u6b64\u6210 \u5c31\uff0c\u6211\u6a80\u5927\u53d4\u5b9e\u5728\u9ad8\u5174\u5f97\u5f88\uff0c\u5c31\u662f\u4f60\u7236\u4eb2\u7684\u5728\u5929\u4e4b\u7075\uff0c\u60f3\u5fc5\u4e5f\u4f1a\u9ad8\u5174\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bed\u6c14\u4e4b\u95f4\uff0c\u60c5\u611f\u4eff\u4f5b\u751a\u662f\u6fc0\u52a8\uff0c\u88f4\u73cf\u53ea\u89c9\u4e00\u9635\u6e29\u60c5\u4e0e\u70ed\u529b\uff0c\u81ea\u4ed6\u5bbd\u5927\u7684\u624b\u638c\u4e4b\u4e2d\u4f20\u5230 \u81ea\u5df1\u5fc3\u91cc\uff0c\u542c\u5230\u4ed6\u63d0\u8d77\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u88f4\u73cf\u4e0d\u89c9\u66f4\u662f\u60c5\u611f\u5954\u817e\uff0c\u5782\u9996\u5446\u4e86\u534a\u664c\uff0c\u8bb7\u8bb7\u4ed6\u8bf4\u9053\uff1a \u201c\u6a80\u5927\u53d4\u5bf9\u6211\u7684\u5929\u9ad8\u5730\u539a\u4e4b\u6df1\u6069\uff0c\u5c0f\u4f84\u662f\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u8bb0\u7684\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u6211\u4eca\u65e5\u770b\u6765\u867d\u7136\u662f\u5728\u654c\u5bf9\u4e4b\u65b9\uff0c\u4f46\u90a3\u4e0d\u8fc7\u53ea \u662f\u4e9b\u5c0f\u4eba\u5728\u5176\u4e2d\u4f5c\u795f\u800c\u5df2\uff0c\u4f46\u671b\u4f60\u4ee5\u540e\u5bf9\u6211\uff0c\u4ecd\u548c\u4ee5\u524d\u4e00\u6837\uff0c\u82e5\u662f\u89c9\u5f97\u5916\u9762\u4eba\u60c5\u51b7\u9177\uff0c\u4e0d\u59a8 \u4f60\u518d\u642c\u56de\u5bb6\u53bb\uff0c\u6a80\u5927\u53d4\u6c38\u8fdc\u662f\u6b22\u8fce\u4f60\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e00\u756a\u8a00\u8bed\u4e4b\u4e2d\uff0c\u66f4\u662f\u5145\u6ee1\u4e86\u6e29\u60c5\uff0c\u88f4\u73cf\u53ea\u89c9\u773c\u524d\u6e10\u6e10\u6726\u80e7\uff0c\u4e00\u7247\u6cea\u5149\u6676\u83b9\uff0c\u8fd9\u7eaf\u826f\u7684 \u5c11\u5e74\uff0c\u7adf\u5df2\u6d41\u51fa\u4e86\u611f\u52a8\u7684\u6cea\u73e0\u3002 \u3000\u3000\u7fa4\u8c6a\u89c1\u5230\u201c\u98de\u9f99\u9556\u5c40\u201d\u7684\u6a80\u603b\u9556\u5934\uff0c\u4e0e\u201c\u6c5f\u5357\u540c\u76df\u201d\u7684\u76df\u4e3b\uff0c\u5728\u4e00\u9635\u4e89\u6218\u4e4b\u540e\uff0c\u7adf\u53d8\u5f97 \u5982\u6b64\u4eb2\u5bc6\uff0c\u4ff1\u90fd\u4e0d\u7981\u5927\u5947\u3002 \u3000\u3000\u7acb\u5728\u4eba\u4e1b\u4e2d\u7684\u4e8e\u5e73\u4e0e\u8eab\u4fa7\u7684\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u4ea4\u6362\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u53c8\u4fa7\u76ee\u671b \u4e86\u201c\u7ba1\u4e8c\u7237\u201d\u4e00\u773c\u3002 \u3000\u3000\u201c\u7ba1\u4e8c\u7237\u201d\u5374\u9894\u9996\u53f9\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5168\u662f\u4e00\u5bb6\uff0c\u6a80\u603b\u9556\u5934\u82e5\u662f\u80fd\u4e0e\u88f4\u5927\u5148\u751f\u5408\u4f5c\uff0c\u90a3 \u771f\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u5927\u76db\u4e8b\uff01\u201d \u3000\u3000\u5305\u6653\u5929\u3001\u4e8e\u5e73\u9f50\u5730\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u201c\u7ba1\u4e8c\u7237\u201d\u5374\u6839\u672c\u672a\u66fe\u542c\u89c1\u3002 \u3000\u3000\u6b64\u523b\u6218\u5c40\u4e00\u4e86\uff0c\u4ed6\u4e3a\u4e86\u8868\u793a\u81ea\u5df1\u5bb6\u95e8\u4e0e\u6a80\u603b\u9556\u5934\u7684\u4eb2\u8fd1\uff0c\u6b63\u5f85\u6324\u5c06\u51fa\u53bb\uff0c\u4e0e\u6a80\u603b\u9556\u5934\u5bd2 \u6684\u51e0\u53e5\uff0c\u4e5f\u597d\u589e\u52a0\u81ea\u5df1\u7684\u5149\u5f69\u3002 \u3000\u3000\u54ea\u77e5\u6b64\u523b\u201c\u9f99\u5f62\u516b\u638c\u201d\u64c5\u660e\u5374\u5c06\u88f4\u73cf\u624b\u638c\u7d27\u7d27\u4e00\u63e1\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u591a\u65e5\u4e0d\u89c1\uff0c\u6211\u672c\u60f3\u4e0e\u4f60 \u591a\u805a\u4e00\u9635\uff0c\u600e\u5948\u6211\u6b64\u523b\u8fd8\u6709\u8981\u4e8b\uff0c\u4e0d\u5f97\u4e0d\u8d70\u4e86\uff01\u7b49\u5230\u4e00\u5207\u4e8b\u4e86\uff0c\u6211\u5b9a\u4f1a\u4e0e\u4f60\u597d\u597d\u8c08\u8c08\u3002\u201d \u3000\u3000\u88f4\u73cf\u6b64\u523b\u5df2\u662f\u5589\u5934\u54fd\u54bd\uff0c\u96be\u4ee5\u51fa\u58f0\uff0c\u53ea\u662f\u611f\u6fc0\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u4e00\u626b\uff0c\u51b7\u51b7\u8198\u4e86\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e00\u773c\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u500f\u53c8\u4f4f\u53e3\uff0c\u53ea\u662f \u6c89\u58f0\u9053\uff1a\u201c\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u518d\u6b21\u4e00\u63e1\u88f4\u73cf\u624b\u638c\uff0c\u8f6c\u8eab\u800c\u884c\uff0c\u8d70\u4e86\u51e0\u6b65\uff0c\u7a81\u53c9\u56de\u9053\uff1a\u201c\u6587\u742a\u5f88\u597d\uff0c\u5979\u8fd8\u5e38\u5e38\u8c08\u8d77 \u4f60\u3002\u201d \u3000\u3000\u88f4\u73cf\u7f13\u7f13\u8ddf\u5728\u8eab\u540e\u9001\u884c\uff0c\u542c\u5230\u8fd9\u8bdd\uff0c\u811a\u6b65\u4e00\u987f\uff0c\u7adf\u5446\u5446\u5730\u6115\u4f4f\u4e86\uff0c\u5fc3\u5934\u84e6\u5730\u515c\u8d77\u4e00\u8154\u65b0 \u6101\u65e7\u60c5\u3002 \u3000\u3000\u201c\u5979\u8fd8\u5728\u60f3\u6211\uff1f\u2026\u2026\u5979\u8fd8\u8bb0\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u662f\u6240\u6709\u7684\u58f0\u97f3\u4e0e\u4eba\u5f71\uff0c\u5239\u90a3\u95f4\u4fbf\u5df2\u79bb\u4ed6\u8fdc\u53bb\uff0c\u4ed6\u5fc3\u5934\u5c31\u53ea\u5269\u4e0b\u64c5\u6587\u742a\u7684\u7247\u7247\u8eab\u5f71\u3002 \u3000\u3000\u4e0d\u7ba1\u591a\u4e48\u75db\u82e6\uff0c\u4e0d\u7ba1\u591a\u4e48\u60b2\u54c0\uff0c\u4e0d\u7ba1\u4ed6\u591a\u4e48\u60f3\u5c06\u5979\u5fd8\u53bb\uff0c\u4f46\u90a3\u6bd5\u7adf\u662f\u4e00\u6bb5\u523b\u9aa8\u94ed\u5fc3\u7684\u611f \u60c5\u3002 \u3000\u3000\u53bb\u65e5\u867d\u7136\u5df2\u4e45\uff0c\u4f46\u5f80\u4e8b\u8bb0\u5fc6\u72b9\u65b0\uff0c\u4ed6\u4eff\u4f5b\u53c8\u56de\u5230\u90a3\u66ae\u6625\u7684\u5ead\u56ed\u4e2d\uff0c\u8349\u6728\u6b23\u6b23\uff0c\u767e\u82b1\u6012 \u653e\uff0c\u4ed6\u6b63\u5728\u4e0e\u64c5\u6587\u742a\u8e22\u7740\u6bfd\u5b50\u3002 \u3000\u3000\u90a3\u662f\u591a\u4e48\u7eaf\u6d01\u7684\u60c5\u611f\uff0c\u7eb5\u7136\u4ed6\u5c06\u80fd\u5f97\u5230\u4e00\u5207\uff0c\u4f46\u8fd9\u4e00\u6bb5\u751c\u871c\u7684\u65e5\u5b50\u5374\u52bf\u5fc5\u4e00\u53bb\u65e0\u8fd4\uff0c\u7eb5 \u7136\u4ed6\u80fd\u5b66\u4f1a\u4e00\u5207\u77e5\u8bc6\uff0c\u4f46\u662f\u5374\u518d\u4e5f\u5f97\u4e0d\u5230\u5982\u6b64\u7eaf\u6d01\u7684\u60c5\u611f\uff0c\u4eba\u751f\u7684\u521d\u604b\uff0c\u53ea\u6709\u4e00\u6b21\uff0c\u5c31\u6b63\u5982 \u6b7b\u4ea1\u4e5f\u53ea\u6709\u4e00\u6b21\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u51e0\u4e4e\u5fd8\u8bb0\u4e86\u81ea\u5df1\u8eab\u5728\u4f55\u5904\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u2018\u51b7\u8c37\u53cc\u6728\u201d\u6b63\u5e76\u80a9\u7acb \u5728\u4ed6\u8eab\u524d\uff0c\u800c\u201d\u9f99\u5f62\u516b\u638c\u201c\u5374\u65e9\u5df2\u8d70\u4e86\u3002\u51b7\u5bd2\u7af9\u9ed8\u7136\u51dd\u6ce8\u7740\u4ed6\u7684\u773c\u775b\u2014\u2014\u773c\u7736\u4e2d\u7684\u6cea\u75d5\uff0c\u6c89 \u58f0\u53f9\u9053\uff1a\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u51c4\u7136\u4e00\u7b11\u3002\u7f13\u7f13\u9053\uff1a\u201c\u5f80\u4e8b\uff0c\u6211\u6b63\u5728\u60f3\u7740\u5f80\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u66fe\u4e5f\u56de\u60f3\u8fc7\u5f80\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u51b7\u6c0f\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u5404\u81ea\u9ed8\u7136\u70b9\u4e86\u70b9\u5934\u3002\u88f4\u6c6a\u7f13\u7f13\u9053\uff0c\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u5f80\u4e8b\uff0c\u6709\u7684\u4eba\u5f80\u4e8b \u751c\u871c\uff0c\u6709\u7684\u4eba\u5f80\u4e8b\u75db\u82e6\uff1b\u800c\u751c\u871c\u7684\u5f80\u4e8b\uff0c\u5c31\u50cf\u4e00\u5b97\u8d22\u5b9d\u4e00\u6837\uff0c\u53ea\u662f\u8d22\u5bcc\u53ef\u4ee5\u6563\u5c3d\uff0c\u800c\u5f80\u4e8b\u5374 \u8c01\u4e5f\u65e0\u6cd5\u593a\u8d70\uff1b\u8d2b\u8d31\u8005\u7684\u5f80\u4e8b\uff0c\u4e5f\u6709\u65f6\u4f1a\u6bd4\u5bcc\u8d35\u8005\u7684\u5f80\u4e8b\u503c\u5f97\u73cd\u60dc\u5f97\u591a\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u51b7\u6c0f\u5144\u5f1f\u9f50\u5730\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u88f4\u73cf\u832b\u7136\u63a5\u7740\u9053\uff1a\u201c\u6709\u7684\u4eba\u594b\u6597\u4e00\u751f\uff0c\u7ec8\u4e8e\u5f97\u5230\u4e86\u4ed6\u6240\u9700\u8981\u7684\u58f0\u540d\u3001\u5730\u4f4d\u4e0e\u8d22\u5bcc\uff0c\u4f46\u56de \u9996\u524d\u5c18\uff0c\u5f80\u4e8b\u5374\u5145\u6ee1\u4e86\u5351\u8d31\u4e0e\u75db\u82e6\uff0c\u6709\u7684\u4eba\u788c\u788c\u7ec8\u751f\uff0c\u4f46\u7b49\u5230\u8001\u5e74\u65f6\u5c06\u8981\u6b7b\u53bb\u7684\u65f6\u5019\uff0c\u5374\u6709 \u8bb8\u591a\u751c\u871c\u7684\u5f80\u4e8b\u503c\u5f97\u56de\u5fc6\u3002\u8fd9\u4e24\u79cd\u4eba\uff0c\u5230\u5e95\u662f\u54ea\u4e00\u79cd\u8f83\u4e3a\u5e78\u798f\u5462\uff1f\u201d \u3000\u3000\u51b7\u6c0f\u5144\u5f1f\u9ed8\u7136\u534a\u664c\uff0c\u7a81\u542c\u4e00\u9635\u6717\u7b11\u4e4b\u58f0\uff0c\u5212\u7a7a\u800c\u6765\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u9707\uff0c\u970d\u7136\u8f6c\u8eab\uff0c\u6c89\u58f0\u53f1\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e00\u9ad8\u6717\u7684\u53e3\u97f3\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\uff0c\u522b\u6765\u65e0\u6059\uff1f\u201d \u3000\u3000\u7b11\u58f0\u672a\u6b47\uff0c\u8fdc\u5904\u9ed1\u6697\u7684\u5c71\u77f3\u95ee\uff0c\u5df2\u75be\u5730\u5954\u6765\u4e00\u6761\u4eba\u5f71\uff0c\u8eab\u6cd5\u77eb\u5065\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u6643\u773c\u95f4\u4fbf \u5df2\u5230\u4e86\u8fd1\u524d\u3002 \u3000\u3000\u88f4\u73cf\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u6b64\u4eba\u8eab\u5f62\u9ad8\u5927\uff0c\u957f\u9aef\u98de\u821e\uff0c\u867d\u5df2\u6df1\u79cb\uff0c\u4f46\u4ed6\u5de6\u638c\u4e4b\u4e2d\uff0c\u4ecd\u62ff\u7740\u4e00\u67c4 \u6298\u6247\uff0c\u7adf\u662f\u90a3\u4e00\u522b\u7ecf\u5e74\u7684\u201c\u795e\u624b\u201d\u6218\u98de\u3002 \u3000\u3000\u706b\u5149\u95ea\u70c1\u4e2d\uff0c\u4ed6\u8eab\u5f62\u5927\u73b0\uff0c\u7fa4\u8c6a\u65b9\u81ea\u9001\u8d70\u4e86\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u6b64\u523b\u89c1\u5230\u201c\u795e\u624b\u201d\u6218\u98de \u7adf\u7fe9\u7136\u800c\u6765\uff0c\u4e0d\u7981\u53c8\u662f\u4e00\u9635\u60ca\u547c\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u7684\u4e3b\u4eba\uff0c\u7adf\u771f\u7684\u7531\u6c5f\u5357\u6765\u5230\u4e2d \u539f\u3002 \u3000\u3000\u8fd9\u4eff\u4f5b\u5df2\u8bc1\u5b9e\u4e86\u65b9\u624d\u4ed6\u4eec\u5728\u6697\u4e2d\u731c\u6d4b\u7684\u4e8b\u3002 \u3000\u3000\u6c5f\u5357\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e94\u5341\u4e94\u6761\u4eba\u547d\uff0c\u7684\u786e\u662f\u6b7b\u5728\u201c\u795e\u624b\u201d\u6218\u98de\u624b\u4e0a\uff01 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u8eab\u5f62\u987f\u5904\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u70ed\u95f9\uff0c\u597d\u70ed\u95f9\uff0c\u5144\u5f1f\u771f\u60f3\u4e0d\u5230\u5728\u8fd9 \u8352\u91ce\u4e4b\u5730\uff0c\u80fd\u89c1\u5230\u8fd9\u8bb8\u591a\u670b\u53cb\uff0c\u5f53\u771f\u6559\u5144\u5f1f\u9ad8\u5174\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u4ef0\u5929\u5927\u7b11\u6570\u58f0\uff0c\u76ee\u5149\u5ffd\u5730\u91cd\u843d\u5230\u88f4\u73cf\u8eab\u4e0a\uff0c\u4e0a\u4e0b\u6253\u91cf\u4e86\u88f4\u73cf\u51e0\u773c\uff0c\u53c8\u81ea\u6717\u7b11\u4e0d\u7edd\u5730\u63a5 \u53e3\u8bf4\u9053\uff1a\u201c\u6700\u4ee4\u5144\u5f1f\u9ad8\u5174\u7684\uff0c\u8fd8\u662f\u65b9\u624d\u4eb2\u773c\u89c1\u5230\u6211\u2018\u6c5f\u5357\u540c\u76df\u2019\u7684\u76df\u4e3b\u5927\u54e5\uff0c\u5728\u4e09\u8a00\u4e24\u8bed\u4e4b \u95f4\uff0c\u60ca\u9000\u4e86\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\uff0c\u54c8\u54c8\u2014\u2014\u8fd9\u5f53\u771f\u662f\u5927\u5feb\u4eba\u5fc3\u4e4b\u4e8b\uff0c\u2018\u9f99\u5f62\u516b\u638c\u2019\u7adf\u5728\u4e2d\u539f\u5730 \u4e0a\uff0c\u683d\u4e86\u8ddf\u6597\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u4e4b\u4e2d\uff0c\u7684\u786e\u662f\u5145\u6ee1\u4e86\u9ad8\u5174\u5f97\u610f\u4e4b\u60c5\u3002 \u3000\u3000\u88f4\u73cf\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u539f\u6765\u6218\u5e84\u4e3b\u65e9\u5df2\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u7684\u786e\u65e9\u5df2\u6765\u4e86\uff0c\u4f46\u662f\u4e0d\u5fcd\u89c1\u5230\u2018\u9f99\u5f62\u516b\u638c\uff0c\u90a3\u526f\u7a98\u6001\uff0c \u662f\u4ee5\u4e00\u76f4\u6ca1\u6709\u73b0\u8eab\uff0c\u54c8\u54c8\u2014\u2014\u4ece\u6b64\u4ee5\u540e\uff0c\u6211\u2019\u6c5f\u5357\u540c\u76df\u2018\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u7684\u786e\u53ef\u4ee5\u626c\u7709\u5410\u6c14 \u4e86\uff0c\u56e0\u4e3a\u6211\u4eec\u6709\u5982\u6b64\u4e00\u4e2a\u76df\u4e3b\u5927\u54e5\u3002\u201d\u4ed6\u5f3a\u8c03\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e43\u662f\u88ab\u88f4\u73cf\u6b66\u529f\u60ca\u9000\uff0c\u65c1\u89c2\u7fa4\u8c6a \u4e4b\u4e2d\uff0c\u6b63\u6709\u8bb8\u591a\u201c\u6c5f\u5357\u540c\u76df\u201d\u4e2d\u4eba\uff0c\u6b64\u523b\u4fbf\u9f50\u5730\u53d1\u51fa\u4e00\u9635\u6b22\u547c\u3002\u547c\u58f0\u6fc0\u8361\uff0c\u58f0\u9707\u56db\u91ce\uff0c\u53ea\u542c \u547c\u58f0\u4e4b\u4e2d\u9690\u9690\u5939\u6742\u7740\u201c\u88f4\u5927\u5148\u751f\uff0c\u626c\u5a01\u5929\u4e0b\uff0c\u6c5f\u5357\u540c\u76df\uff0c\u6c38\u9738\u6b66\u6797\uff01\u201d\u8fd9\u547c\u58f0\u5c31\u6b63\u5982\u91ce\u706b\u4e00 \u822c\uff0c\u6643\u773c\u4fbf\u70e7\u904d\u4e86\u6b66\u6797\u2026\u2026 \u7b2c\u5341\u4e8c\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u5341\u4e8c\u7ae0 \u3000\u3000\u591c\u98ce\u6fc0\u8361\uff0c\u70c8\u706b\u98de\u626c\uff0c\u6ee1\u5c71\u4e00\u7247\u6b22\u547c\uff0c\u88f4\u73cf\u5fc3\u5934\u4e5f\u4e0d\u7981\u5347\u8d77\u4e00\u79cd\u70ed\u8840\u5954\u817e\u4e4b\u611f\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u58f0\u6b22\u547c\uff0c\u5c31\u6709\u5982\u6d6a\u6f6e\u51b2\u51fb\u7740\u5c71\u5ca9\u4f3c\u7684\uff0c\u51b2\u51fb\u7740\u88f4\u73cf\u7684\u5fc3\u623f\uff0c\u6d6a\u82b1\u51b2\u8d70\u4e86\u5c71\u5ca9\u4e0a\u7684 \u6c61\u79fd\u4e0e\u9752\u82d4\uff1b\u6b22\u547c\u51b2\u53bb\u4e86\u5374\u662f\u88f4\u73cf\u5fc3\u4e2d\u7684\u9634\u973e\u4e0e\u60b2\u54c0\uff0c\u4ed6\u773c\u4e2d\u6e10\u6e10\u9732\u51fa\u4e86\u7729\u76ee\u7684\u5149\u5f69\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51dd\u6ce8\u7740\u4ed6\u9762\u4e0a\u8868\u60c5\u7684\u53d8\u5316\uff0c\u5c31\u6b63\u5982\u4e00\u53ea\u6b63\u5f85\u6251\u4eba\u800c\u566c\u7684\u91ce\u517d\uff0c\u7a81\u7136\u53d1\u89c9\u81ea \u5df1\u7684\u76ee\u6807\u5df2\u53d8\u6210\u4e2a\u4f18\u79c0\u800c\u8001\u7ec3\u7684\u730e\u4eba\u65f6\u4e00\u6837\uff0c\u4e00\u4e1d\u4e00\u6beb\u4e5f\u4e0d\u6562\u653e\u8fc7\u88f4\u73cf\u9762\u4e0a\u8868\u60c5\u7684\u53d8\u5316\u3002 \u3000\u3000\u6b22\u547c\u4e4b\u58f0\uff0c\u6e10\u6e10\u5e73\u606f\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u4ee5\u624b\u634b\u987b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ee5\u4eca\u65e5\u6b22\u547c\u96f7\u52a8\u4e4b\u52bf\u770b \u6765\uff0c\u4ed6\u65e5\u4e4b\u6b66\u6797\uff0c\u4f55\u6101\u4e0d\u662f\u88f4\u5144\u4e4b\u5929\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u4ef0\u5929\u800c\u7b11\uff0c\u8ba9\u4eba\u65e0\u6cd5\u770b\u51fa\u4ed6\u76ee\u4e2d\u7684\u542b\u610f\u3002\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6b64\u523b\u867d\u7136\u4e5f\u66ff\u88f4\u73cf\u9ad8\u5174\uff0c\u4f46\u610f \u6c14\u4f3c\u4e4e\u5341\u5206\u6d88\u6c89\uff0c\u5144\u5f1f\u4e24\u4eba\u9ed8\u7136\u8d70\u4e86\u5f00\u53bb\uff0c\u5230\u5bf9\u9762\u897f\u65b9\u5c71\u77f3\u4e0a\u3002\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6218\u5144 \u4e4b\u8a00\uff0c\u5728\u4e0b\u5b9e\u5728\u6127\u4e0d\u6562\u5f53\uff0c\u6a80\u603b\u9556\u5934\u4e4b\u79bb\u53bb\uff0c\u4e0d\u8fc7\u53ea\u662f\u5ff5\u5728\u6614\u65e5\u4e0e\u6211\u7684\u60c5\u4e49\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4ef0\u5929\u72c2\u7b11\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u5927\u5927\u5730\u9519\u4e86\uff0c\u60f3\u90a3\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\uff0c\u662f\u4f55\u7b49\u4eba \u7269\uff0c\u5373\u4f7f\u771f\u4e0e\u88f4\u5144\u6709\u6240\u60c5\u4e49\uff0c\u4eca\u65e5\u4e4b\u5c40\uff0c\u4ed6\u4e5f\u65ad\u4e0d\u4f1a\u4e3a\u4e86\u60c5\u4e49\u800c\u81ea\u635f\u5a01\u671b\uff0c\u6b64\u4eba\u505a\u4e8b\u4e00\u5411\u6709 \u59cb\u6709\u7ec8\uff0c\u8d76\u5c3d\u6740\u7edd\uff0c\u82e5\u662f\u4ed6\u4e5f\u6709\u6240\u60c5\u4e49\u2026\u2026\xb7\xb7\u54fc\u54fc\uff01\u201d \u3000\u3000\u4ed6\u51b7\u54fc\u4e24\u58f0\uff0c\u7b11\u58f0\u7a81\u987f\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u88f4\u5144\uff0c\u5728\u4e0b\u4eca\u65e5\u4f7f\u662f\u4e3a\u4e86\u8981\u544a\u8bc9\u88f4\u5144\uff0c\u90a3\u2018\u9f99\u5f62\u516b \u638c\u2019\u6a80\u660e\uff0c\u5230\u5e95\u4e0e\u88f4\u5144\u6709\u4f55\u2018\u60c5\u4e49\u2019\uff0c\u53ea\u662f\u6b64\u523b\u4ecd\u672a\u5230\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u518d\u8fc7\u534a\u664c\uff0c\u88f4\u5144\u5c31\u4f1a\u77e5\u9053 \u90a3\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u5bf9\u5144\u53f0\u662f\u5982\u4f55\u7684\u6709\u60c5\u6709\u4e49\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u53cc\u7709\u5fae\u76b1\uff0c\u5fc3\u4e2d\u5927\u662f\u7591\u60d1\uff0c\u8be7\u58f0\u9053\uff1a\u201c\u6218\u5144\uff0c\u4f60\u65b9\u624d\u6240\u8bf4\u7684\u8bdd\uff0c\u5728\u4e0b\u5b9e\u5728\u672a\u80fd\u5b8c\u5168 \u660e\u4e86\uff0c\u4e0d\u77e5\u2014\u2014\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u626c\uff0c\u5389\u58f0\u53f1\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u51e0\u4eba\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u968f\u7740\u4ed6\u7684\u76ee\u5149\u671b\u53bb\uff0c\u53ea\u89c1\u5c71\u4e0b\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u7a81\u5730\u6d8c\u51fa\u4e86\u6570\u5341\u6761\u9ed1\u8863\u5927\u6c49\uff0c\u6709\u7684\u624b\u5f85\u5200 \u5251\uff0c\u6709\u7684\u5374\u4ee5\u64d2\u62ff\u624b\u6cd5\uff0c\u626d\u7740\u4e00\u4e9b\u6b66\u6797\u8c6a\u58eb\u7684\u624b\u8155\uff0c\u524d\u547c\u540e\u62e5\u5730\u5c06\u4ed6\u4eec\u81ea\u6b22\u547c\u7740\u7684\u4eba\u7fa4\u4e2d\u63a8 \u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u6709\u7684\u5c1a\u5728\u4e0d\u4f4f\u6323\u624e\uff0c\u6709\u7684\u53ea\u662f\u9ed8\u7136\u5782\u9996\u800c\u884c\uff0c\u9762\u4e0a\u5404\u5404\u5e26\u7740\u60ca\u5947\u3001\u6124\u6012\u4e0e \u754f\u60e7\u4e4b\u8272\uff0c\u8fd8\u6709\u7684\u8fd8\u5728\u60f6\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\uff0c\u4ea6\u5728\u8fd9\u7fa4\u4eba\u4e4b\u4e2d\uff0c\u4ed6\u9762\u5bb9\u4e00\u7247\u82cd\u81ea\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u4f3c\u4e4e\u5df2\u53d7\u4e86\u6697\u4f24\u3002 \u3000\u3000\u4e24\u4e2a\u9762\u8272\u9634\u6c89\u3001\u8eab\u8eaf\u9ad8\u7626\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u4e00\u4eba\u53cc\u624b\u5206\u7279\u7740\u4e00\u5bf9\u201c\u5224\u5b98\u53cc\u7b14\u201d\uff0c\u7d27\u7d27\u8ddf\u5728\u8d3e \u658c\u8eab\u7554\uff0c\u53e6\u4e00\u4eba\u8170\u60ac\u8c79\u56ca\uff0c\u811a\u6b65\u8f7b\u5065\uff0c\u53cc\u624b\u865a\u63e1\uff0c\u638c\u4e2d\u4f3c\u4e4e\u4e0d\u77e5\u6263\u4e86\u4e9b\u4ec0\u4e48\u6697\u5668\uff0c\u5374\u8fdc\u8fdc\u7ad9 \u5728\u8fd9\u7fa4\u4eba\u4e94\u5c3a\u5f00\u5916\u4e4b\u5904\uff0c\u76ee\u5149\u70af\u70af\uff0c\u7d27\u7d27\u76d1\u89c6\u7740\u4ed6\u4eec\u7684\u811a\u6b65\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u9762\u76ee\u5341\u5206\u751f\u758f\uff0c\u4f3c\u975e\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u7684\u95e8\u4e0b\uff0c\u7709\u5b87\u95f4\u4e00\u7247\u527d\u608d\u9634\u9e37\u4e4b\u6c14\uff0c\u5374\u663e\u89c1\u4ff1 \u662f\u67b6\u505a\u4e0d\u7fa4\u7684\u6b66\u6797\u8c6a\u5f3a\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u52a8\uff0c\u5927\u60d1\u4e0d\u89e3\uff0c\u4ea6\u4e0d\u7981\u8131\u53e3\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u4ff1\u90fd\u672a\u5c06\u2018\u88f4\u5927\u5148\u751f\uff0c\u770b\u5728\u773c\u91cc\uff0c\u5144\u5f1f\u597d\u6b79\u4e5f\u5f97 \u7ed9\u4ed6\u4eec\u4e00\u4e9b\u6559\u8bad\uff0c\u6559\u6b66\u6797\u4e2d\u4eba\u4e0d\u8981\u5fd8\u4e86\u2019\u88f4\u5927\u5148\u751f\u2018\u7684\u5a01\u671b\u3002\u201d\u88f4\u73cf\u76b1\u7709\u9053\uff1a\u201c\u4f46\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u672a\u843d\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u7b11\u5bb9\u5df2\u655b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u9646\u8001\u5f1f\uff0c\u518d\u65e0\u522b\u4eba\u4e86\u4e48\uff1f\u201d \u3000\u3000\u90a3\u8170\u60ac\u8c79\u56ca\u7684\u7626\u957f\u6c49\u5b50\uff0c\u811a\u6b65\u4e00\u62ac\uff0c\u8f7b\u8f7b\u63a0\u4e86\u8fc7\u6765\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u5c71\u4e0b\u5343\u767e\u4eba\u4e2d\uff0c\u4e00\u542c\u88f4 \u5927\u5148\u751f\u4e4b\u540d\uff0c\u4ff1\u5728\u62cd\u624b\u9ad8\u547c\uff0c\u9664\u4e86\u8fd9\u5341\u4f59\u4eba\u4e4b\u5916\uff0c\u518d\u4e5f\u6ca1\u6709\u522b\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u9894\u9996\u9053\uff1a\u201c\u90a3\u59d3\u8d3e\u7684\u9556\u5934\u600e\u5730\u4e86\uff1f\u201d \u3000\u3000\u8170\u60ac\u8c79\u56ca\u7684\u7626\u957f\u6c49\u5b50\u6c89\u58f0\u9053\uff1a\u201c\u4ed6\u4e00\u89c1\u5e84\u4e3b\u73b0\u8eab\u4e4b\u540e\uff0c\u4fbf\u60f3\u81ea\u4eba\u7fa4\u4e2d\u4e58\u4e71\u6e9c\u8d70\uff0c\u6211\u548c\u4e8c \u5f1f\u7acb\u523b\u8ffd\u4e86\u8fc7\u53bb\uff0c\u4ed6\u8fd8\u60f3\u52a8\u624b\uff0c\u88ab\u6211\u4ee5\u2018\u4e03\u5349\u6885\u82b1\u2019\uff0c\u51fb\u5728\u4ed6\u540e\u80a1\u4e0a\uff0c\u4e8c\u5f1f\u4e5f\u8d76\u5c06\u8fc7\u53bb\uff0c\u8d4f \u4e86\u4ed6\u4e00\u638c\u2018\u7384\u9e1f\u5212\u6c99\u2019\uff0c\u4ed6\u624d\u4e56\u4e56\u675f\u624b\u3002\u201d \u3000\u3000\u4ed6\u795e\u6001\u867d\u5bf9\u201c\u795e\u624b\u201d\u6218\u98de\u9887\u4e3a\u606d\u656c\uff0c\u4f46\u8a00\u8bed\u95f4\u5374\u4ecd\u5e26\u7740\u4e00\u79cd\u6840\u50b2\u4e4b\u6c14\uff0c\u663e\u89c1\u662f\u5bf9\u81ea\u5df1\u7684 \u8eab\u624b\u6b66\u529f\uff0c\u6781\u4e3a\u5f97\u610f\u3002 \u3000\u3000\u88f4\u73cf\u6ee1\u5fc3\u60ca\u7591\uff0c\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u96be\u9053\u4ec5\u662f\u4e3a\u4e86\u4e0d\u80af\u5bf9\u6211\u62cd\u638c\u6b22\u547c\uff0c\u5c31\u88ab\u6218\u5144\u57cb\u4f0f\u5728\u4eba \u4e1b\u4e2d\u7684\u5144\u5f1f\u6293\u51fa\u6765\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u53ea\u4f5c\u672a\u95fb\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6211\u7adf\u5fd8\u4e86\u4e3a\u88f4\u5144\u5f15\u89c1\u4e24\u4f4d\u5f97\u529b\u7684\u5f1f\u5144\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u5411\u8fd9\u8170\u4f69\u8c79\u56ca\u7684\u6c49\u5b50\u4e00\u6307\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u4f4d\u4fbf\u662f\u6210\u540d\u5ddd\u3001\u6ec7\u4e00\u5e26\u7684\u72ec\u884c\u4fa0\u9053 \u2018\u5df4\u5c71\u53cc\u715e\u2019\u4e2d\u7684\u5927\u54e5\uff0c\u4eba\u79f0\u2018\u65e0\u5f71\u6885\u82b1\u9b3c\u89c1\u6101\u2019\u7684\u9646\u5929\u9a7f\u3002\u8fd9\u4f4d\u2018\u88f4\u5927\u5148\u751f\u2019\uff0c\u60f3\u5fc5\u4f60\u4e5f \u5df2\u4e45\u4ef0\u76db\u540d\uff0c\u6bcb\u5eb8\u6211\u518d\u591a\u52a0\u5439\u5618\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u867d\u6709\u6ee1\u8154\u60ca\u7591\u6124\u6168\uff0c\u5374\u4e5f\u4e0d\u613f\u626b\u4eba\u9762\u5b50\uff0c\u53ea\u5f97\u5fcd\u4f4f\u8bdd\u5934\uff0c\u5fae\u4e00\u62b1\u62f3\uff0c\u5fae\u5fae\u542b\u7b11 \u9053\uff1a\u201c\u4e45\u4ef0\u4e45\u4ef0\u3002\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u867d\u8fde\u79f0\u201c\u4e45\u4ef0\u201d\uff0c\u5176\u5b9e\u5374\u4ece\u672a\u542c\u8fc7\u8fd9\u201c\u5df4\u5c71\u53cc\u715e\u201d\u4e24\u5b57\uff0c\u81ea\u4e0d\u77e5\u9053\u8fd9\u5144\u5f1f\u4e24\u4eba\u5728 \u9ed1\u9053\u4e2d\u5b9e\u5728\u4e45\u6709\u76db\u540d\uff0c\u4e24\u4eba\u72ec\u6765\u72ec\u5f80\uff0c\u4ff1\u662f\u5fc3\u72e0\u624b\u8fa3\uff0c\u67d3\u4e0b\u6ee1\u624b\u8840\u8165\uff0c\u6700\u8fd1\u5ffd\u7136\u88ab\u4e00\u4e2a\u6781\u4e3a \u5389\u5bb3\u7684\u4ec7\u5bb6\u82e6\u82e6\u8ffd\u8e2a\uff0c\u4ed6\u4e24\u4eba\u867d\u7136\u51f6\u72e1\u72e0\u8fa3\uff0c\u600e\u5948\u8fd9\u4ec7\u5bb6\u4ea6\u662f\u673a\u8b66\u65e0\u6bd4\uff0c\u6b66\u529f\u5c24\u9ad8\uff0c\u7adf\u5c06\u4ed6 \u5144\u5f1f\u4e24\u4eba\u903c\u5f97\u65e0\u5904\u5bb9\u8eab\uff0c\u624d\u4e0d\u5f97\u4e0d\u53bb\u6295\u5165\u201c\u6d6a\u83bd\u5c71\u5e84\u201d\u95e8\u4e0b\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u6b63\u503c\u7528\u4eba\u4e4b\u9645\uff0c \u81ea\u7136\u662f\u5927\u4e3a\u6b22\u8fce\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u201c\u65e0\u5f71\u6885\u82b1\u9b3c\u89c1\u6101\u201d\u9634\u9e37\u7684\u9762\u5bb9\u4e0a\uff0c\u9732\u51fa\u4e00\u4e1d\u865a\u4f2a\u800c\u51b7\u524a\u7684\u7b11\u5bb9\uff0c\u5fae\u4e00\u8eac\u8eab\uff0c \u9053\uff1a\u201c\u6211\u5144\u5f1f\u4e24\u4eba\u5df2\u6295\u5165\u2018\u6c5f\u5357\u540c\u76df\u2019\uff0c\u4ece\u4eca\u4ee5\u540e\uff0c\u4fbf\u662f\u2018\u88f4\u5927\u5148\u751f\u2019\u7684\u5c5e\u4e0b\uff0c\u2018\u88f4\u5927\u5148 \u751f\u2019\u53ea\u8981\u5429\u5490\u4e00\u53e5\uff0c\u6211\u5144\u5f1f\u4e24\u4eba\u8d74\u6c64\u8e48\u706b\uff0c\u5728\u6240\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u634b\u987b\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u88f4\u5144\uff0c\u4e0d\u4f46\u8fd9\u4f4d\u9646\u5144\u5f1f\uff0c\u662f\u6761\u4e49\u6c14\u6c49\u5b50\uff0c\u4fbf\u662f\u90a3 \u8fb9\u7684\u9646\u4e8c\u5f1f\uff0c\u4e5f\u662f\u6761\u6c5f\u6e56\u5c11\u89c1\u7684\u70ed\u8840\u7537\u513f\u3002\u201d\u4ed6\u5411\u90a3\u8fb9\u624b\u6301\u5224\u5b98\u53cc\u7b14\u7684\u6c49\u5b50\uff0c\u6307\u70b9\u7740\u9053\uff1a \u201c\u90a3\u4f4d\u4fbf\u662f\u2018\u5df4\u5c71\u53cc\u715e\u2019\u4e2d\u7684\u2018\u8ffd\u98ce\u94c1\u7b14\u9707\u6c5f\u6e56\u2019\u9646\u5929\u9aa5\u9646\u4e8c\u4fa0\uff0c\u8fd9\u5144\u5f1f\u4e24\u4eba\u4e0d\u4f46\u4ff1\u662f\u4e00\u8eab \u786c\u8f6f\u529f\u529b\uff0c\u800c\u4e14\u8fd8\u6709\u4e00\u638c\u795e\u9b3c\u7686\u60ca\u7684\u72ec\u95e8\u6697\u5668\uff0c\u6b64\u756a\u4ea6\u6295\u5728\u88f4\u5144\u624b\u4e0b\uff0c\u2018\u6c5f\u5357\u540c\u76df\uff0c\u4f55\u60a3\u5927 \u4e8b\u4e0d\u6210\uff1f\u201d\u88f4\u73cf\u65e0\u53ef\u5948\u4f55\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u4ecd\u672a\u5fd8\u8f6c\u56de\u606c\u5934\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6218\u5144\uff0c\u8fd9\u4e9b\u4eba\u82e5\u771f\u662f\u2014 \u2014\u201d\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u6b64\u4e3e\uff0c\u81ea\u6709\u6df1\u610f\uff0c\u88f4\u5144\u7a0d\u7b49\u4e00\u4f1a\u513f\uff0c\u4fbf\u53ef\u77e5\u9053\u4e86\u3002 \u535c\u201d\u9ed1\u9a74\u8ffd\u98ce\u201c\u8d3e\u658c\u76ee\u5149\u4e00\u76f4\u72e0\u72e0\u671b\u7740\u8fd9\u8fb9\uff0c\u6b64\u523b\u653e\u58f0\u5927\u558a\u9053\uff1a\u201c\u59d3\u6218\u7684\uff0c\u4f60\u8981\u62ff\u4f60\u8d3e\u8001\u5b50 \u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u7b11\u4e00\u58f0\uff0c\u7f13\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e14\u731c\u731c\u6211\u8981\u5c06\u4f60\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u867d\u5df2\u53d7\u4f24\uff0c\u4f46\u527d\u608d\u4e4b\u6c14\uff0c\u5374\u4e1d\u6beb\u4e0d\u51cf\uff0c\u867d\u7136\u75db\u5f97\u9f87\u7259\u54a7\u5634\uff0c\u4f46\u4ecd\u72c2\u7b11\u7740 \u9053\uff1a\u201c\u6211\u5012\u770b\u770b\u4f60\u6562\u5c06\u4f60\u8d3e\u5927\u9556\u5934\u600e\u6837\uff0c\u9664\u975e\u4f60\u4ee5\u540e\u4e0d\u60f3\u5728\u6c5f\u6e56\u4e0a\u6df7\u4e86\uff01\u54c8\u54c8\uff0c\u2018\u98de\u9f99\u9556 \u5c40\u2019\u9556\u5e08\u4eec\uff0c\u4fbf\u662f\u88ab\u4eba\u780d\u4e0b\u8111\u888b\uff0c\u4e5f\u4e0d\u4f1a\u76b1\u4e00\u76b1\u7709\u5934\uff01\u53ea\u662f\uff0c\u59d3\u6218\u7684\uff0c\u4f60\u6562\u5417\uff1f\u4f60\u6562\u2014\u2014\u201d \u8bdd\u58f0\u672a\u4e86\uff0c\u201c\u8ffd\u98ce\u94c1\u7b14\u9707\u6c5f\u6e56\u201d\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u6389\u8f6c\u7b14\u950b\uff0c\u4ee5\u90a3\u5224\u5b98\u7b14\u7684\u94c1\u67c4\uff0c\u5728\u4ed6\u80a9\u4e0a\u8f7b \u8f7b\u6572\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u8d3e\u658c\u4e00\u58f0\u60e8\u547c\uff0c\u6251\u5730\u5012\u5728\u5730\u4e0a\uff0c\u9646\u5929\u9a87\u8fd9\u4e00\u7b14\u5b9e\u5df2\u7528\u516d\u5206\u771f\u529b\u3002 \u3000\u3000\u770b\u6765\u867d\u8f7b\uff0c\u5176\u5b9e\u5df2\u5c06\u4ed6\u80a9\u80bf\u5927\u9aa8\u51fb\u788e\u3002 \u3000\u3000\u8fd9\u79cd\u5176\u75db\u5f7b\u9aa8\u7684\u75db\u6614\uff0c\u4fbf\u662f\u94c1\u6c49\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u201c\u9ed1\u9a74\u8ffd\u98ce\u201d\u8d3e\u658c\u66f4\u662f\u75db\u5f97\u6ee1\u5934\u51b7\u6c57\u6d94\u6d94 \u800c\u843d\uff0c\u6323\u624e\u7740\u5750\u4e86\u8d77\u6765\uff0c\u53e3\u4e2d\u4ecd\u5927\u9a82\u9053\uff1a\uff1a\u2018\u59d3\u6218\u7684\uff0c\u4f60\u6709\u79cd\u5c31\u5c06\u8d3e\u5927\u7237\u5207\u4e86\uff0c\u82e5\u518d\u8981\u8fd9\u6837 \u96f6\u96f6\u788e\u788e\u5730\u6298\u78e8\u4f60\u8d3e\u8001\u5927\uff0c\u83ab\u602a\u5927\u7237\u6211\u8981\u9a82\u4f60\u7956\u5b97\u516b\u4ee3\u4e86\u3002\u201c\u4ed6\u5230\u5e95\u662f\u81ea\u5e7c\u4fbf\u662f\u6df7\u8ff9\u6c5f\u6e56\uff0c \u8033\u6fe1\u76ee\u67d3\uff0c\u6c5f\u6e56\u4e60\u6027\u9887\u91cd\uff0c\u6b64\u523b\u6025\u75db\u4e4b\u4e0b\uff0c\u53e3\u4e2d\u4fbf\u5fcd\u4e0d\u4f4f\u8bf4\u51fa\u6c5f\u6e56\u4e2d\u7684\u7c97\u91ce\u4e4b\u8bdd\u3002\u201d\u795e\u624b \u201c\u6218\u98de\u9762\u5bd2\u5982\u6c34\uff0c\u76ee\u5149\u5982\u5200\uff0c\u51b7\u7136\u671b\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4eec\u8981\u770b\u770b\u4f60\u5982\u4f55\u9a82\u6cd5\uff01\u201d \u3000\u3000\u638c\u4e2d\u6298\u6247\u4e00\u6447\uff0c\u201c\u8ffd\u98ce\u94c1\u7b14\u201d\u9646\u5929\u9aa5\u53cc\u7709\u626c\u5904\uff0c\u53c8\u662f\u4e00\u7b14\u6572\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u7b14\u7528\u7684\u529b\u9053\u66f4\u5927\uff0c\u4e0d\u504f\u4e0d\u501a\u5730\u6572\u5728\u4ed6\u53e6\u4e00\u8fb9\u7684\u80a9\u80db\u9aa8\u4e0a\uff0c\u8d3e\u658c\u53c8\u662f\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u201c\u8ffd\u98ce\u94c1\u7b14\u9707\u6c5f\u6e56\u201d\u9646\u5929\u9a87\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u9a82\u5427\u3002\u201d \u3000\u3000\u4f46\u8d3e\u658c\u53cc\u80a9\u4ff1\u788e\uff0c\u75db\u5f7b\u5fc3\u8151\uff0c\u65e9\u5df2\u6655\u4e86\u8fc7\u53bb\uff0c\u54ea\u91cc\u8fd8\u9a82\u5f97\u51fa\u534a\u4e2a\u5b57\u6765\u3002 \u3000\u3000\u56db\u4e0b\u7fa4\u8c6a\uff0c\u4ff1\u90fd\u88ab\u201c\u795e\u624b\u201d\u6218\u98de\u5982\u6b64\u72e0\u6bd2\u7684\u624b\u6cd5\u6240\u60ca\uff0c\u4eba\u4eba\u9762\u8272\u5927\u53d8\uff0c\u88f4\u73cf\u66f4\u662f\u770b\u5f97\u65e0 \u6cd5\u5fcd\u8010\u3002 \u3000\u3000\u4ed6\u4e00\u6b65\u63a0\u4e86\u8fc7\u53bb\uff0c\u65b9\u5f85\u51fa\u624b\u529d\u963b\uff0c\u54ea\u77e5\u4eba\u4e1b\u4e2d\u7a81\u5730\u5927\u53eb\u4e00\u58f0\uff0c\u5982\u98de\u5954\u51fa\u4e00\u6761\u7ea2\u8863\u5927\u6c49 \u6765\uff0c\u7adf\u662f\u90a3\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u3002\u4ed6\u5982\u98de\u63a0\u5230\u8d3e\u658c\u8eab\u4fa7\uff0c\u53cc\u624b\u4e00\u5f20\uff0c\u5411\u201c\u795e\u624b\u201d\u6218\u98de\u77a0\u76ee\u5927\u559d \u9053\uff1a\u201c\u6218\u5e84\u4e3b\uff0c\u4f60\u96be\u9053\u771f\u8981\u6253\u6b7b\u4eba\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u51b7\u626b\u4e86\u4ed6\u4e00\u773c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u662f\u2018\u91d1\u9e21\u5e2e\u2019\u4e2d\u7684\u2018\u9e21\u51a0\u2019\u5305\u6653\u5929\uff1f\u201d \u3000\u3000\u201c\u9e21\u51a0\u201d\u5305\u6653\u592d\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6218\u5e84\u751f\uff0c\u8fd9\u59d3\u8d3e\u7684\u4eba\u5e76\u4e0d\u574f\uff0c\u4e5f\u6ca1\u6709\u505a\u4ec0\u4e48\u9519\u4e8b\uff0c\u8fd8\u5bf9\u6211 \u6709\u4e9b\u6069\u60e0\uff0c\u4f60\u600e\u4e48\u80fd\u5982\u6b64\u5bf9\u5f85\u4ed6\uff1f\u201d \u3000\u3000\u4ed6\u6027\u60c5\u9c81\u83bd\uff0c\u8a00\u8bed\u7c97\u8c6a\uff0c\u7adf\u6562\u5bf9\u201c\u795e\u624b\u201d\u6218\u98de\u5982\u6b64\u8bf4\u8bdd\uff0c\u201c\u5df4\u5c71\u53cc\u715e\u201d\u9762\u4e0a\u9f50\u5730\u6cdb\u51fa\u4e00 \u7247\u6740\u673a\uff0c\u7fa4\u8c6a\u4ea6\u81ea\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u88f4\u73cf\u5374\u5728\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u5012\u662f\u6761\u70ed\u8840\u6c49\u5b50\uff0c\u53d7\u4eba\u6069\u60e0\uff0c\u81f3\u6b7b\u4e0d\u5fd8\u3002\u201d \u3000\u3000\u53e3\u5149\u8f6c\u5904\uff0c\u53ea\u89c1\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u4e0a\u4ecd\u662f\u4e00\u7247\u51b0\u51b7\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8eab\u4e3a\u2018\u6c5f\u5357\u540c\u76df\u2019\u7684\u5144 \u5f1f\uff0c\u7adf\u6562\u65e0\u7aef\u53d7\u522b\u4eba\u6069\u60e0\uff0c\u5df2\u662f\u4e00\u7f6a\uff1b\u6b64\u523b\u5c45\u7136\u6562\u5728\u2018\u88f4\u5927\u5148\u751f\u2019\u53ca\u6211\u9762\u524d\u5f20\u7259\u821e\u722a\uff0c\u6211\u5012 \u8981\u95ee\u95ee\u4f60\uff0c\u4f60\u81ea\u4ee5\u4e3a\u4f55\u4eba\uff1f\u6562\u5728\u8fd9\u91cc\u5982\u6b64\u8bf4\u8bdd\uff0c\u96be\u9053\u4e5f\u4e0d\u6015\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8a00\u8bed\u4e2d\u4ea6\u542b\u84c4\u7740\u4e00\u7247\u6740\u673a\uff0c\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u5927\u58f0\u9053\uff1a\u201c\u6211\u8fd8\u8981\u600e\u6837\u8bf4\u8bdd\uff1f\u96be\u9053\u8981\u6211\u8dea \u4e0b\u6765\u4e48\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u9646\u592d\u9aa5\u638c\u4e2d\u53cc\u7b14\u4e00\u95ea\uff0c\u5305\u6653\u5929\u53ea\u89c9\u819d\u5f2f\u4e00\u9ebb\uff0c\u5176\u75db\u5f7b\u9aa8\uff0c\u8eab\u4e0d\u7531\u4e3b\u5730\u8dea\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u52a8\uff0c\u6697\u8e0f\u5403\u60ca\uff1a\u201c\u8fd9\u59d3\u9646\u7684\u597d\u5feb\u624b\u6cd5\u3002\u201d \u3000\u3000\u53ea\u542c\u201c\u9e21\u51a0\u201d\u5305\u6653\u5929\u5927\u559d\u4e00\u58f0\uff0c\u4f3c\u5f85\u7ad9\u8d77\uff0c\u201c\u8ffd\u98ce\u94c1\u7b14\u201d\u51fa\u624b\u5982\u98ce\uff0c\u53cc\u7b14\u9f50\u98de\uff0c\u5728\u4ed6 \u201c\u80a9\u4e95\u201d\u3001\u201c\u66f2\u6c60\u201d\u4e24\u5904\u5927\u7a74\u4e4b\u4e0a\uff0c\u5404\u5404\u70b9\u4e86\u4e00\u4e0b\uff0c\u5305\u6653\u5929\u559d\u58f0\u8fd8\u672a\u5b8c\u5168\u51fa\u53e3\uff0c\u4fbf\u5df2\u6709\u5982\u77f3 \u50cf\u822c\u88ab\u4eba\u70b9\u5728\u5730\u4e0a\uff0c\u8eab\u8eaf\u4e0d\u80fd\u52a8\u5f39\uff0c\u8fde\u5634\u5df4\u90fd\u5408\u4e0d\u62e2\u6765\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u91d1\u9e21\u5e2e\u2018\u4e2d\uff0c\u4e5f\u6709\u8fd9\u822c\u8d25\u7c7b\uff0c\u4eca\u65e5\u6211\u5012\u8981\u66ff\u5411 \u4e00\u557c\u7ba1\u6559\u7ba1\u6559\u4f60\u8fd9\u5974\u624d\u4e86\u3002\u201d\u4ed6\u6d53\u7709\u626c\u5904\uff0c\u5411\u201c\u5df4\u5c71\u53cc\u715e\u201d\u5fae\u4e00\u793a\u610f\uff0c\u201d\u5df4\u5c71\u53cc\u715e\u201d\u5144\u5f1f\u4e24 \u4eba\uff0c\u7acb\u523b\u5c06\u8d3e\u658c\u3001\u5305\u6653\u5929\u81ea\u5730\u4e0a\u631f\u8d77\u3002\u6b64\u523b\u7fa4\u8c6a\u4ff1\u662f\u5404\u5404\u53d8\u8272\uff0c\u90a3\u5341\u4f59\u4e2a\u88ab\u9ed1\u8863\u5927\u6c49\u5236\u4f4f\u7684 \u4eba\uff0c\u66f4\u662f\u9a87\u5f97\u9762\u5bb9\u82cd\u81ea\uff0c\u5664\u5982\u5bd2\u8749\u3002\u4e00\u4e2a\u5f62\u5bb9\u5a01\u731b\u7684\u6c49\u5b50\uff0c\u9a87\u5f97\u6ee1\u5934\u4ff1\u662f\u51b7\u6c57\uff0c\u7a81\u7136\u5657\u5730\u8dea \u4e86\u4e0b\u6765\uff0c\u6323\u624e\u7740\u5728\u5730\u4e0a\u722c\u4e86\u51e0\u6b65\uff0c\u5927\u558a\u7740\uff1a\u201c\u6211\u521a\u624d\u53ea\u662f\u5fd8\u4e86\u6b22\u547c\uff0c\u4e0d\u662f\u6545\u610f\u7684\uff0c\u6218\u5e84\u4e3b\uff0c \u4f60\u9976\u4e86\u6211\u5427\uff01\u88f4\u5927\u5148\u751f\uff0c\u4f60\u9976\u4e86\u6211\u5427\uff01\u6211\u548c\u2018\u98de\u9f99\u9556\u5c40\u2019\u6beb\u65e0\u5173\u7cfb\uff0c\u6211\u662f\u6700\u62e5\u62a4\u2018\u6c5f\u5357\u540c \u76df\u2019\u7684\u2026\u2026\u6218\u5e84\u4e3b\u4e07\u5c81\uff0c\u88f4\u5927\u5148\u751f\u4e07\u5c81\uff01\u201d \u3000\u3000\u88f4\u73cf\u53cc\u7709\u76b1\u5904\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6218\u5144\uff0c\u4f60\u600e\u80fd\u5982\u6b64\u505a\u6cd5\uff1f\u201d \u3000\u3000\u53e3\u5149\u8f6c\u5904\uff0c\u7a81\u89c1\u201c\u5df4\u5c71\u53cc\u715e\u201d\u5df2\u631f\u7740\u8d3e\u3001\u5305\u4e24\u4eba\u5411\u9ed1\u6697\u7684\u8352\u91ce\u4e4b\u5904\u8d70\u53bb\uff0c\u4e0d\u7981\u8131\u53e3\u5927\u559d \u4e00\u58f0\uff01 \u3000\u3000\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u8eab\u5f62\u4e00\u98d8\uff0c\u8f7b\u8f7b\u98d8\u5728\u201c\u5df4\u5c71\u53cc\u715e\u201d\u7684\u9762\u524d\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u5bb9\u7a81\u53d8\uff0c\u4fbf\u77ac\u5373\u5fae\u7b11\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u822c\u5974\u624d\u2026\u2026\u201d \u3000\u3000\u88f4\u73cf\u9762\u8272\u4e00\u6c89\uff0c\u6717\u58f0\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u4e24\u4eba\u65e2\u65e0\u6740\u8eab\u4e4b\u7f6a\uff0c\u4f60\u4fbf\u4e0d\u8be5\u5984\u4e0b\u6bd2\u624b\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5e72\u7b11\u6570\u58f0\u9053\uff1a\u201c\u5e2e\u6709\u5e2e\u89c4\uff0c\u5bb6\u6709\u5bb6\u6cd5\uff0c\u88f4\u5144\u2014\u2014\u201d\u88f4\u73cf\u6b64\u523b\u5df2\u5168\u65e0\u4ed6\u6614\u5e74 \u7684\u67d4\u5f31\u4e4b\u6001\uff0c\u76ee\u4e2d\u70af\u70af\u5c04\u51fa\u7cbe\u5149\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e2e\u6709\u5e2e\u89c4\uff0c\u4f46\u4f60\u83ab\u975e\u5fd8\u4e86\u8c01\u662f\u6c5f\u5357\u540c\u76df \u7684\u76df\u4e3b\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u5bb9\u53c8\u662f\u4e00\u53d8\uff0c\u8f6c\u76ee\u671b\u4e86\u56db\u4e0b\u7684\u7fa4\u8c6a\u4e00\u773c\uff0c\u7a81\u5730\u5927\u559d\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u65e2\u5df2 \u6709\u547d\uff0c\u8fd8\u4e0d\u5c06\u8fd9\u4e24\u4eba\u5e26\u4e0b\u53bb\u597d\u597d\u4e3a\u4ed6\u4eec\u7597\u6cbb\u4f24\u52bf\uff0c\u89e3\u5f00\u7a74\u9053\uff0c\u96be\u9053\u4f60\u4eec\u90fd\u5df2\u5fd8\u4e86\u8c01\u662f\u2018\u6c5f\u5357 \u540c\u76df\u2019\u7684\u76df\u4e3b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4ed6\u76ee\u5149\u6de1\u6de1\u5411\u9646\u6c0f\u5144\u5f1f\u505a\u4e86\u4e2a\u773c\u8272\uff0c\u201c\u5df4\u5c71\u53cc\u715e\u201d\u8eac\u8eab\u5e94\u547d\uff0c\u5c0f\u5fc3\u5730\u62d6\u7740 \u8d3e\u3001\u5305\u4e24\u4eba\u7684\u8eab\u8eaf\uff0c\u7f13\u6b65\u8d70\u5165\u4eba\u4e1b\u3002 \u3000\u3000\u88f4\u73cf\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6218\u5144\uff0c\u6211\u5e76\u975e\u6709\u610f\u5982\u6b64\uff0c\u53ea\u662f\u2014\u2014\u201d\u201c\u795e\u624b\u201d\u6218\u98de\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u6211\u4e0e\u76df\u4e3b\u76f8\u8bc6\u5df2\u4e45\uff0c\u76df\u4e3b\uff0c\u4f60\u90a3\u4ec1\u6148\u5bbd\u539a\u7684\u6027\u60c5\uff0c\u96be\u9053\u5c0f\u5f1f\u6211\u8fd8\u4f1a\u4e0d\u77e5\u9053\u4e48\uff1f\u5176\u5b9e\u5c0f\u5f1f\u6211 \u53c8\u4f55\u5c1d\u6709\u53d6\u4ed6\u4eec\u6027\u547d\u4e4b\u610f\uff0c\u4e0d\u8fc7\u53ea\u662f\u5413\u5413\u4ed6\u4eec\uff0c\u6559\u4ed6\u4eec\u6709\u6240\u8b66\u6212\uff0c\u4e0d\u8981\u5fd8\u4e86\u6c5f\u5357\u540c\u76df\u7684\u89c4\u77e9 \u800c\u5df2\u3002\u201d \u3000\u3000\u8dea\u5728\u5730\u4e0a\u7684\u5f62\u5bb9\u72e0\u7410\u7684\u7070\u8863\u6c49\u4e8e\uff0c\u6b64\u523b\u7a81\u5730\u6323\u8131\u4e86\u9ed1\u8863\u5927\u6c49\u7684\u624b\u638c\uff0c\u98de\u6b65\u5954\u5230\u88f4\u73cf\u8eab \u524d\uff0c\u6251\u5730\u8dea\u4e86\u4e0b\u6765\uff0c\u54c0\u547c\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\uff0c\u9976\u4e86\u6211\u5427\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fae\u4e00\u6446\u624b\uff0c\u4e24\u6761\u9ed1\u8863\u5927\u6c49\u968f\u4e4b\u63a0\u53bb\uff0c\u8fd9\u7070\u8863\u6c49\u5b50\u4e00\u4e0b\u6251\u5230\u88f4\u73cf\u8eab\u4e0a\uff0c\u54c0\u547c \u9053\uff1a\u201c\u6551\u6551\u6211\u2026\u2026\u6551\u6551\u6211\uff01\u201d \u3000\u3000\u88f4\u73cf\u53cc\u7709\u5fae\u76b1\uff0c\u867d\u89c9\u6b64\u4eba\u884c\u4e3a\u6709\u4e9b\u5351\u9119\uff0c\u8d2a\u751f\u60dc\u547d\uff0c\u7adf\u4e00\u81f3\u5982\u6b64\uff0c\u4f46\u5fc3\u4e2d\u4ecd\u4e0d\u89c9\u5347\u51fa\u4fa7 \u9690\u4e4b\u5fc3\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6839\u672c\u65e0\u4eba\u8981\u53d6\u4f60\u6027\u547d\uff0c\u4f60\u4f55\u82e6\u5982\u6b64\u2014\u2014\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u89c9\u4e00\u80a1\u5927\u529b\uff0c\u81ea \u8fd9\u7070\u8863\u6c49\u5b50\u53cc\u638c\u4e0a\u88ad\u6765\uff01 \u3000\u3000\u8fd9\u5f62\u5bb9\u7325\u7410\u7684\u7070\u8863\u6c49\u5b50\uff0c\u6b64\u523b\u76ee\u5149\u4e00\u51db\uff0c\u53cc\u81c2\u73af\u62b1\u7740\u88f4\u73cf\u7684\u8eab\u8eaf\uff0c\u7adf\u4ee5\u5185\u5bb6\u638c\u529b\uff0c\u731b\u51fb \u88f4\u73cf\u7684\u540e\u80cc\uff0c\u53e3\u4e2d\u5927\u559d\u9053\uff1a\u201c\u59d3\u88f4\u7684\uff0c\u62ff\u547d\u6765\u5427\uff01\u201d \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u7fa4\u8c6a\u4ff1\u7686\u53d8\u8272\u60ca\u547c\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u53cc\u53cc\u817e\u8eab\u800c\u8d77\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u95ea\u52a8\uff0c \u4ea6\u4e0d\u77e5\u662f\u60ca\u3001\u662f\u6012\u3001\u662f\u559c\uff1f \u3000\u3000\u773c\u89c1\u88f4\u73cf\u5168\u8eab\u4ff1\u90fd\u5728\u8fd9\u7070\u8863\u6c49\u5b50\u53cc\u81c2\u73af\u62b1\u4e4b\u4e2d\uff0c\u6709\u5982\u88ab\u4eba\u4ee5\u94c1\u94fe\u531d\u4f4f\uff0c\u624b\u8db3\u96be\u4ee5\u65bd\u5c55\uff0c \u7fa4\u8c6a\u60ca\u547c\u672a\u5df2\uff0c\u4ed6\u5df2\u201c\u5657\u201d\u5730\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c9\u5fc3\u5934\u4e00\u9707\uff0c\u6709\u5982\u5f53\u80f8\u88ab\u4eba\u51fb\u4e86\u4e00\u62f3\uff0c\u98de\u63a0\u7684\u8eab\u5f62\uff0c\u4e5f\u4e0d\u7981 \u7a81\u7136\u505c\u987f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u5144\u5f1f\u4e24\u4eba\u5bf9\u88f4\u73cf\u4e4b\u5173\u5fc3\uff0c\u5df2\u975e\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9\u3002 \u3000\u3000\u56db\u4e0b\u7fa4\u8c6a\u9707\u5929\u4ef7\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u53cc\u80a9\u4e00\u6311\uff0c\u5927\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u5974\u624d\uff0c\u8fd8 \u4e0d\u66ff\u6211\u62ff\u4e0b\u6765\u3002\u201d \u3000\u3000\u90a3\u5f62\u5bb9\u7325\u7410\u7684\u7070\u8863\u6c49\u5b50\u4e00\u770b\u5f97\u624b\uff0c\u8eab\u5f62\u8dc3\u8d77\uff0c\u8f7b\u7075\u77eb\u5065\uff0c\u65e0\u4e0e\u4f26\u6bd4\uff0c\u54ea\u91cc\u8fd8\u6709\u534a\u4e1d\u65b9\u624d \u90a3\u79cd\u7325\u7410\u5351\u9119\u4e4b\u6001\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u56db\u4e0b\u4e00\u8f6c\uff0c\u4fbf\u5df1\u63e3\u91cf\u597d\u5730\u52bf\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u5411\u9ed1\u6697\u4e2d\u5982\u98de\u63a0\u53bb\uff0c\u90e8\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\u867d \u5df2\u5230\u4e86\u8fd1\u524d\uff0c\u7ade\u4ecd\u7136\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u773c\u770b\u4ed6\u8eab\u5f62\u53ea\u8981\u518d\u4e00\u8d77\u843d\uff0c\u4fbf\u8981\u6ca1\u4eba\u8fdc\u65b9\u9ed1\u6697\u7684\u5c71\u91ce\u91cc\u2026\u2026 \u3000\u3000\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u5239\u90a3\u4e4b\u95f4\u2014\u2014 \u3000\u3000\u60ca\u547c\u8fde\u8fde\uff0c\u4eba\u5f71\u95ea\u52a8\uff0c\u8fdc\u65b9\u9ed1\u6697\u4e4b\u4e2d\uff0c\u7a81\u5730\u63a0\u51fa\u6570\u6761\u9ed1\u8863\u5927\u6c49\uff0c\u4f3c\u4e4e\u8981\u6321\u4f4f\u8fd9\u7070\u8863\u4eba\u7684 \u53bb\u8def\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u957f\u9aef\u4e00\u7529\uff0c\u8eab\u5f62\u7a81\u8d77\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5927\u60ca\u4e4b\u4e0b\uff0c\u5fae\u4e00\u8fdf\u7591\uff0c\u7acb\u523b\u8ddf\u7740\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u4eec\u7684\u8eab\u5f62\u8fd8\u672a\u63a0\u51fa\u4e09\u6b65\uff0c\u5e73\u5367\u5728\u5730\u4e0a\u7684\u88f4\u73cf\u7a81\u5730\u6709\u5982\u98de\u77e2\u4e00\u822c\u63a0\u8d77\uff0c\u5934\u524d\u811a\u540e\uff0c \u6025\u5c04\u4e00\u4e08\uff0c\u53cc\u81c2\u4e00\u62a1\uff0c\u8eab\u5f62\u62e7\u8f6c\uff0c\u5927\u559d\u9053\uff1a\u201c\u54ea\u91cc\u53bb\uff01\u201d \u3000\u3000\u7070\u8863\u4eba\u8eab\u5f62\u672c\u6765\u6781\u4e3a\u5feb\u901f\uff0c\u4f46\u542c\u5230\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u5fc3\u5934\u5374\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\uff0c\u51e0\u4e4e\u8981\u60ca\u547c\u51fa\u58f0 \u6765\u3002 \u3000\u3000\u4ed6\u65b9\u624d\u660e\u660e\u4ee5\u5185\u5bb6\u638c\u529b\uff0c\u7740\u7740\u5b9e\u5b9e\u5730\u51fb\u5728\u88f4\u73cf\u7684\u540e\u80cc\u4e0a\uff0c\u4ed6\u6b66\u529f\u4e0d\u5f31\uff0c\u81ea\u4fe1\u4ee5\u8fd9\u4e8c\u638c\u4e4b \u529b\uff0c\u4fbf\u662f\u6b66\u6797\u9ad8\u624b\u4e5f\u96be\u4ee5\u62c5\u5f53\u5f97\u8d77\uff0c\u88f4\u73cf\u62db\u5f0f\u4e4b\u5999\uff0c\u867d\u80dc\u4e8e\u5df1\uff0c\u4f46\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u4f1a\u53d7\u5f97\u4e0b\u81ea \u5df1\u8fd9\u4e00\u53cc\u94c1\u638c\uff1f \u3000\u3000\u800c\u6b64\u523b\u88f4\u73cf\u7684\u547c\u58f0\u5374\u53c8\u660e\u660e\u81ea\u540e\u4f20\u6765\uff0c\u771f\u529b\u5145\u6c9b\uff0c\u9707\u4eba\u8033\u9f13\uff0c\u663e\u7136\u975e\u4f46\u672a\u66fe\u8eab\u6b7b\uff0c\u800c\u4e14 \u4e1d\u6beb\u672a\u53d7\u4f24\u5bb3\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u811a\u6b65\u5fae\u4e00\u987f\u632b\uff0c\u88f4\u73cf\u8eab\u8eaf\u4e00\u957f\uff0c\u5de6\u638c\u95ea\u7535\u822c\u4f38\u51fa\uff0c\u4e94\u6307\u7b95\u5f20\uff0c\u5df2\u4e00\u628a\u6293\u4f4f \u4ed6\u80a9\u5934\u8863\u886b\u3002 \u3000\u3000\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u60ca\u4e71\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u53c8\u662f\u4e00\u53d8\uff0c\u4ed6\u89c1\u5230\u88f4\u73cf\u672a\u6b7b\uff0c\u5fc3\u91cc\u4ea6\u4e0d\u77e5\u662f\u9ad8 \u5174\uff0c\u6291\u6216\u662f\u5931\u671b\uff1f \u3000\u3000\u53ea\u89c1\u90a3\u7070\u8863\u6c49\u5b50\u8eab\u5b50\u5411\u524d\u4e00\u6251\uff0c\u201c\u5636\u201d\u7684\u4e00\u58f0\uff0c\u540e\u80cc\u8863\u886b\uff0c\u6495\u4e0b\u4e00\u7247\uff0c\u4ed6\u5374\u659c\u659c\u5411\u5de6\u4e00 \u51b2\uff0c\u53c8\u81ea\u51b2\u51fa\u4e00\u4e08\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u957f\u9aef\u62c2\u52a8\uff0c\u7a81\u5730\u66b4\u559d\u4e00\u58f0\u3002 \u3000\u3000\u201c\u6253\uff01\u201d \u3000\u3000\u53ea\u542c\u4e00\u7f15\u9510\u98ce\uff0c\u5212\u7a7a\u800c\u53bb\uff0c\u4ed6\u7adf\u5c06\u638c\u4e2d\u6298\u6247\u5f53\u505a\u6697\u5668\uff0c\u201c\u7529\u624b\u7bad\u201d\u7684\u624b\u6cd5\uff0c\u51fb\u4e2d\u4e86\u90a3\u7070 \u8863\u6c49\u5b50\u8eab\u540e\u7684\u201c\u6c14\u6d77\u4fde\u7a74\u201d\u3002 \u3000\u3000\u56db\u6761\u9ed1\u8863\u6c49\u5b50\u4e00\u62e5\u800c\u4e0a\uff0c\u5c06\u4ed6\u7d27\u7d27\u6309\u5728\u5730\u4e0a\uff0c\u53e6\u4e00\u4eba\u53d6\u8d77\u5730\u4e0a\u7684\u6298\u6247\uff0c\u53cc\u624b\u4ea4\u56de\u7ed9\u201c\u795e \u624b\u201d\u6218\u98de\u3002 \u3000\u3000\u88f4\u73cf\u4f38\u624b\u4e00\u62c2\u8863\u8882\uff0c\u9762\u8272\u5982\u5e38\uff0c\u7adf\u4e1d\u6beb\u6ca1\u6709\u60ca\u60f6\u5931\u8272\u4e4b\u6001\uff0c\u65b9\u624d\u90a3\u4ef6\u53d8\u6545\uff0c\u4f3c\u4e4e\u6839\u672c\u4e0d \u662f\u53d1\u751f\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u89c1\u5230\u4ed6\u8fd9\u822c\u9547\u9759\u7684\u795e\u6001\uff0c\u9762\u5bb9\u53c8\u4e0d\u7981\u5fae\u5fae\u4e00\u53d8\uff0c\u4f38\u624b\u63a5\u8fc7\u6298\u6247\uff0c\u8fde\u58f0\u53f9 \u9053\uff1a\u201c\u597d\u9669\u597d\u9669\uff0c\u88f4\u5144\uff0c\u4f60\u53ef\u53d7\u60ca\u4e86\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65b9\u624d\u4ed6\u53cc\u638c\u62cd\u4e0b\u4e4b\u9645\uff0c\u6211\u4e5f\u89c9\u5168\u8eab\u4e3a\u4e4b\u4e00\u9707\uff0c\u6211\u751f\u6015\u4ed6\u624b\u638c\u8f6c\u5230 \u6211\u8eab\u540e\u7684\u2018\u547d\u95e8\u2019\u3001\u2018\u5fd7\u5802\u2019\u7b49\u7a74\u4e4b\u4e0a\uff0c\u6240\u4ee5\u4fbf\u5012\u4e86\u4e0b\u53bb\uff0c\u4f46\u662f\u6211\u6697\u4e2d\u5c06\u771f\u6c14\u8fd0\u884c\u4e00\u904d\uff0c\u53d1 \u89c9\u4f3c\u4e4e\u6beb\u65e0\u4f24\u635f\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u542b\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e0d\u8fc7\u662f\u4e00\u573a\u865a\u60ca\u800c\u5df2\uff0c\u5012\u7d2f\u5f97\u5404 \u4f4d\u5982\u6b64\u60ca\u52a8\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u6697\u4e2d\u8bae\u8bba\u7eb7\u7eb7\uff0c\u6709\u7684\u60ca\u5f02\uff0c\u6709\u7684\u611f\u53f9\uff0c\u6709\u7684\u5e86\u5e78\uff0c\u65e0\u8bba\u662f\u8c01\uff0c\u5bf9\u88f4\u73cf\u7684\u6b66\u529f\u90fd\u4e0d\u7981 \u5b58\u4e0b\u51e0\u5206\u754f\u60e7\u4e4b\u5fc3\u3002 \u3000\u3000\u8981\u77e5\u8fd9\u7070\u8863\u4eba\u8eab\u624b\u77eb\u5065\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u6b64\u6709\u76ee\u5171\u7779\u4e4b\u4e8b\uff0c\u800c\u88f4\u73cf\u7ade\u80fd\u884c\u6240\u65e0\u4e8b\u5730\u63a5\u4e0b\u4ed6\u8d34 \u8eab\u53d1\u51fa\u7684\u4e24\u638c\uff0c\u8fd9\u7b49\u5185\u529b\u4e4b\u542b\u8574\uff0c\u5c82\u975e\u9a87\u4eba\u542c\u95fb\uff1f \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5fc3\u5934\u4e5f\u4e0d\u7981\u5347\u51fa\u4e00\u822c\u5bd2\u610f\uff0c\u5bf9\u88f4\u73cf\u66f4\u52a0\u91cd\u4e86\u4e09\u5206\u6212\u5fc3\uff0c\u4f46\u53e3\u4e2d\u5374\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u5e78\u597d\u662f\u573a\u865a\u60ca\uff0c\u5426\u5219\u5c0f\u5f1f\u6211\u771f\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\u4e86\uff01\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u987f\u5904\uff0c\u9762\u8272\u4e00\u6c89\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f46\u8fd9\u53ae\u7684\u6765\u5386\uff0c\u5374\u59d4\u5b9e\u8d39\u4eba\u731c\u7591\uff0c\u5b9a\u5f97\u597d\u597d\u67e5\u95ee\u4e00 \u4e0b\u3002\u201d \u3000\u3000\u88f4\u73cf\u542b\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u65e2\u65e0\u53d7\u4f24\uff0c\u4e5f\u5c31\u7b97\u4e86\uff0c\u60f3\u6765\u6b64\u4eba\u4e5f\u4e0d\u8fc7\u662f\u60c5\u6025\u62fc\u547d\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u957f\u53f9\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u4e3a\u4eba\u5b9e\u5728\u592a\u8fc7\u5fe0\u539a\uff0c\u96be\u9053\u4f60\u8fd8\u6ca1\u6709\u770b\u51fa\u6b64\u4eba\u4e0d\u4f46\u65e9\u6709 \u9884\u8c0b\uff0c\u800c\u4e14\u82e6\u5fc3\u79ef\u8651\uff0c\u6545\u610f\u4f5c\u51fa\u90a3\u526f\u53ef\u601c\u7684\u6837\u5b50\uff1f\u54fc\u54fc\u4e00\u4e00\uff0c\u201d\u4ed6\u51b7\u201c\u54fc\u201d\u4e24\u58f0\uff0c\u8bed\u58f0\u7a81 \u8f7b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u800c\u4e14\u6b64\u4eba\u5e55\u540e\u5fc5\u6709\u4e3b\u8c0b\u4e4b\u4eba\uff0c\u4f9d\u5c0f\u5f1f\u770b\u6765\uff0c\u5341\u4e2d\u516b\u4e5d\uff0c\u5b9a\u7136\u4fbf\u662f\u90a3\u2018\u9f99\u5f62\u516b \u638c\u2019\u6a80\u660e\uff01\u201d \u3000\u3000\u88f4\u73cf\u53cc\u7709\u5fae\u76b1\uff0c\u9053\uff1a\u201c\u6210\u5144\u5fc3\u4e2d\u6210\u89c1\u592a\u6df1\uff0c\u662f\u4ee5\u624d\u4f1a\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u5176\u5b9e\u2014\u2014\u201d\u201c\u795e\u624b\u201d \u6218\u98de\u51b7\u7136\u622a\u53e3\u9053\uff1a\u201c\u5176\u5b9e\u771f\u76f8\u5982\u4f55\uff0c\u88f4\u5144\u4e0d\u4e45\u4fbf\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u4ed6\u624b\u638c\u4e00\u62db\uff0c\u90a3\u56db\u6761\u9ed1\u8863\u6c49\u5b50\u4fbf\u7acb\u523b\u5c06\u90a3\u7070\u8863\u4eba\u62fe\u4e86\u8fc7\u6765\uff0c\u6b64\u523b\u201c\u4ed6\u5df2\u88ab\u9ed1\u8863\u5927\u6c49\u4ee5\u725b \u7b4b\u7d27\u7d27\u7f1a\u4f4f\u8eab\u8eaf\u3002\u201d\u795e\u624b\u201c\u6218\u98de\u624b\u638c\u4e00\u4f38\uff0c\u89e3\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u59d3\u751a\u540d\u8c01\uff1f \u53d7\u4e86\u4ec0\u4e48\u4eba\u7684\u6307\u4f7f\uff1f\u8fd8\u4e0d\u8d76\u5feb\u4ece\u5b9e\u62db\u6765\uff0c\u96be\u9053\u8fd8\u60f3\u518d\u591a\u5403\u70b9\u82e6\u5934\u4e48\uff1f\u201d \u3000\u3000\u7070\u8863\u4eba\u9762\u4e0a\u7a81\u5730\u6cdb\u8d77\u4e86\u4e00\u9635\u5947\u5f02\u7684\u5fae\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e3b\u4f7f\u6211\u6765\u505a\u6b64\u4e8b\u7684\u4eba\uff0c\u4fbf\u662f\u2018\u795e \u624b\u2019\u6218\u98de\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5927\u559d\u4e00\u58f0\uff0c\u65b9\u5f85\u4e00\u62f3\u51fb\u53bb\uff0c\u54ea\u77e5\u8fd9\u7070\u8863\u4eba\u53cc\u76ee\u7a81\u7136\u4e00\u5f20\uff0c\u5149\u5f69\u5c3d\u5931\uff0c\u77b3\u4ec1 \u56db\u6563\uff0c\u9762\u4e0a\u7684\u7b11\u5bb9\uff0c\u4e5f\u53d8\u6210\u4e86\u4e00\u79cd\u5947\u5f02\u7684\u626d\u66f2\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u5fd8\u2026\u2026\u8bb0\u2026\u2026\u2026\u4e86\u2026\u2026 \u4e48\uff1f\u2026\u2026\xb7\xb7\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u4ed6\u773c\u3001\u8033\u3001\u9f3b\u3001\u53e3\u4e03\u7a8d\u4e4b\u4e2d\uff0c\u5df2\u6c69\u7136\u6d41\u4e0b\u9c9c\u8840\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6012\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b64\u4eba\u7adf\u7136\u4ee5\u6b7b\u5b88\u53e3\uff01\u201d \u3000\u3000\u53cc\u624b\u75be\u4f38\uff0c\u95ed\u4f4f\u4e86\u4ed6\u5fc3\u8109\u9644\u8fd1\u7684\u516d\u5904\u7a74\u9053\uff0c\u638c\u52bf\u4e00\u8f6c\uff0c\u634f\u4f4f\u4ed6\u7684\u4e0b\u989a\uff0c\u53ea\u89c1\u4ed6\u731b\u4e86\u5f20 \u53e3\uff0c\u81ea\u53e3\u843d\u4e0b\u4e24\u534a\u8d64\u7ea2\u8721\u4e38\uff0c\u8721\u4e38\u4e2d\u6240\u85cf\u7684\u6bd2\u836f\uff0c\u5374\u5df2\u88ab\u4ed6\u5403\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u6b64\u4eba\u7adf\u5df2\u65e9\u84c4\u6b7b \u5fd7\uff0c\u9884\u85cf\u4e86\u8fd9\u7c92\u5185\u88c5\u7acb\u53ef\u5c01\u5589\u593a\u547d\u7684\u6bd2\u836f\u4e4b\u8721\u4e38\uff0c\u8fd9\u90fd\u662f\u201c\u795e\u624b\u201d\u6218\u98de\u4e5f\u672a\u66fe\u60f3\u5230\u7684\u4e8b\u3002 \u3000\u3000\u88f4\u73cf\u9762\u5bb9\u5927\u53d8\uff0c\u4ed6\u672c\u4e0d\u4fe1\u6b64\u4eba\u65e9\u6709\u9884\u8c0b\uff0c\u4f46\u6b64\u523b\u770b\u6765\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u8bdd\u7adf\u662f\u5343\u771f\u4e07\u786e \u4e4b\u4e8b\u3002 \u3000\u3000\u6218\u98de\u624b\u91cc\u6258\u7740\u90a3\u4e24\u74e3\u7834\u788e\u7684\u8721\u4e38\uff0c\u51dd\u6ce8\u534a\u664c\uff0c\u51b7\u7b11\u7740\u9053\uff1a\u201c\u4f60\u7eb5\u7136\u5982\u6b64\uff0c\u662f\u8c01\u6307\u4f7f\u4f60 \u7684\uff0c\u96be\u9053\u6211\u6218\u67d0\u4eba\u8fd8\u731c\u4e0d\u51fa\u6765\u4e48\uff1f\u201d \u3000\u3000\u7a81\u5730\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u8fd9\u7070\u8863\u4eba\u7684\u5c38\u8eab\u8fdc\u8fdc\u8e22\u5f00\u4e00\u4e08\uff0c\u56db\u4e0b\u7fa4\u8c6a\u53c8\u5f00\u59cb\u4e86\u7eb7\u7eb7\u7684\u8bae\u8bba\uff0c\u4ff1\u5728 \u731c\u6d4b\u7740\u8fd9\u7070\u8863\u4eba\u662f\u4f55\u6765\u5386\u3002 \u3000\u3000\u5269\u4e0b\u7684\u90a3\u4e00\u4e9b\u88ab\u9ed1\u8863\u5927\u6c49\u626d\u4f4f\u624b\u8155\u7684\u6c49\u5b50\uff0c\u6b64\u523b\u66f4\u662f\u9762\u8272\u5982\u571f\uff0c\u5176\u4e2d\u4e00\u4eba\u5f53\u5373\u5927\u558a\u9053\uff1a \u201c\u6211\u77e5\u9053\u6b64\u4eba\u662f\u8c01\uff0c\u53ea\u8981\u4f60\u653e\u6211\u8d70\uff0c\u6211\u5c31\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u4e00\u4eae\uff0c\u9053\uff1a\u201c\u4f60\u771f\u7684\u77e5\u9053\u4e48\uff1f\u8bf4\u51fa\u6765\u6211\u5c31\u653e\u4f60\u8d70\uff01\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u4ea6\u662f\u4e00\u8eab\u7070\u8863\uff0c\u5c0f\u58f0\u9053\uff1a\u201c\u6211\u4eec\u90fd\u662f\u6a80\u603b\u9556\u5934\u4f0f\u4e0b\u7684\u6697\u6869\uff0c\u53ef\u662f\u6211\u4eec\u90fd\u4e0d\u8fc7\u662f\u5c0f \u55bd\u7f57\u800c\u5df2\uff0c\u53ea\u6709\u6b64\u4eba\u662f\u4e2a\u9556\u5934\uff0c\u800c\u4e14\u5728\u6c5f\u6e56\u4e2d\u9887\u6709\u540d\u58f0\uff0c\u53eb\u505a\u2018\u6bd2\u624b\u59dc\u7ef4\u2019\u6c5f\u5927\u77f3\uff0c\u53ea\u662f\u4ed6 \u9762\u4e0a\u6d82\u4e86\u4e00\u5c42\u9762\u836f\uff0c\u662f\u4ee5\u4f60\u4eec\u8c01\u90fd\u6ca1\u6709\u8ba4\u51fa\u4ed6\u6765\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u9707\uff0c\u5012\u9000\u4e09\u6b65\u3002 \u3000\u3000\u7fa4\u8c6a\u81ea\u7136\u53c8\u662f\u4e00\u9635\u60ca\u52a8\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u6a80\u660e\u5440\u6a80\u660e\uff0c\u4f60\u867d\u7136\u5fc3\u72e0\u624b\u8fa3\uff0c \u5978\u72e1\u51f6\u6076\uff0c\u4e5f\u5c45\u7136\u6709\u80af\u4e3a\u4f60\u5356\u547d\u7684\u670b\u53cb\uff0c\u4f46\u662f\u4f60\u667a\u8005\u5343\u8651\uff0c\u5374\u60f3\u4e0d\u5230\u4f60\u624b\u4e0b\u8fd8\u6709\u5982\u6b64\u4e0d\u6210\u6750 \u7684\u4eba\u7269\u5427\uff01\u201d \u3000\u3000\u201c\u72c2\u7b11\u672a\u7edd\uff0c\u624b\u638c\u4e00\u6325\uff0c\u5927\u559d\u9053\uff1a\u201c\u653e\u4ed6\u8d70\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u9ed1\u8863\u6c49\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u7ec8\u4e8e\u677e\u5f00\u624b\u638c\uff0c\u8fd9\u7070\u8863\u6c49\u5b50\u5982\u9022\u5927\u8d66\uff0c\u5206\u5f00\u4eba\u4e1b\uff0c\u653e\u6b65\u72c2\u5954 \u800c\u53bb\uff0c\u6643\u773c\u4fbf\u6d88\u5931\u4e86\u4eba\u5f71\u3002 \u3000\u3000\u4f17\u4eba\u4e0d\u7981\u4ff1\u90fd\u6697\u4e2d\u5947\u602a\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u201c\u795e\u624b\u201d\u6218\u98de\u771f\u5730\u653e\u8d70\u4e86\u6b64\u4eba\uff0c\u53c8\u6709\u4eba\u4e0d\u7981\u5728\u6697\u4e2d \u79f0\u8d5e\uff1a\u201c\u6218\u795e\u624b\u867d\u7136\u624b\u6bb5\u6bd2\u8fa3\uff0c\u4f46\u8a00\u51fa\u5982\u5c71\uff0c\u5f53\u771f\u662f\u6761\u6c49\u5b50\uff0c\u5982\u6b64\u770b\u6765\uff0c\u201d\u9f99\u5f62\u516b\u638c\u201c\u5c31\u4eff \u4f5b\u663e\u5f97\u8fdc\u4e0d\u5982\u4ed6\u4e86\u3002\u201d \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6b64\u523b\u53c8\u4ee5\u8fdc\u8fdc\u5750\u5728\u4e00\u8fb9\uff0c\u8fd9\u5144\u5f1f\u4e24\u4eba\u51b7\u773c\u65c1\u89c2\uff0c\u6b64\u523b\u9762\u4e0a\u53c8\u5df2\u6302\u51fa\u4e86\u4ed6\u4eec \u60ef\u6709\u7684\u51b7\u7b11\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6218\u795e\u624b\u4e3a\u4ec0\u4e48\u5c06\u6b64\u4eba\u653e\u8d70\u4e48\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u6cc4\u9732\u4e86\u2018\u9f99\u5f62\u516b\u638c\u2019\u7684\u673a\u5bc6\uff0c\u2018\u98de\u9f99\u9556\u5c40\u2019\u600e\u4f1a\u653e\u8fc7\u4ed6\uff1f \u53ea\u6015\u4ed6\u8d70\u4e0d\u51fa\u8fd9\u5c71\u533a\u4e4b\u5916\uff0c\u5c31\u8981\u6a2a\u5c38\u5c31\u5730\u4e86\uff0c\u800c\u4e14\u6b7b\u5f97\u5fc5\u5b9a\u5f88\u60e8\uff0c\u6218\u795e\u624b\u4e50\u5f97\u4f5c\u51fa\u5bbd\u5b8f\u5927 \u5ea6\uff0c\u8a00\u51fa\u5fc5\u8df5\u7684\u6837\u5b50\uff0c\u8ba9\u522b\u4eba\u6765\u52a8\u624b\uff0c\u8fd8\u4e0d\u662f\u4e00\u6837\u4e48\uff1f\u201d \u3000\u3000\u5144\u5f1f\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u76f8\u89c6\u4e00\u7b11\uff0c\u51b7\u5bd2\u7af9\u53c8\u53f9\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u73cf\u513f\u53ea\u6015\u771f\u4e0e\u90a3\u6a80\u660e\u6709\u7740 \u8840\u6d77\u822c\u7684\u6df1\u4ec7\u4e86\uff01\u6211\u8d77\u5148\u4e5f\u5728\u6000\u7591\uff0c\u90a3\u6a80\u660e\u4e3a\u4f55\u4e0d\u80af\u4f20\u6388\u73cf\u513f\u7684\u6b66\u529f\uff0c\u5982\u4eca\u624d\u77e5\u9053\u59d3\u6a80\u7684\u679c \u7136\u662f\u4e2a\u5978\u733e\u51f6\u72e1\u7684\u89d2\u8272\uff0c\u4ed6\u5c06\u4ec7\u4eba\u7684\u5b50\u5973\u7559\u5728\u8eab\u8fb9\uff0c\u53c8\u4e0d\u4f20\u4ed6\u6b66\u529f\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u522b\u4eba\u81ea\u7136\u4f1a \u79f0\u8d5e\u4ed6\u7684\u4ec1\u6148\u535a\u7231\uff0c\u601c\u60af\u5b64\u72ec\uff0c\u4ed6\u5374\u6c38\u8fdc\u4e0d\u7528\u987e\u8651\u4ec7\u4eba\u7684\u5f1f\u5b50\u4f1a\u6765\u590d\u4ec7\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u957f\u53f9\u9053\uff1a\u201c\u4f46\u667a\u8005\u5343\u8651\uff0c\u5fc5\u6709\u4e00\u5931\uff0c\u4eba\u7b97\u7ec8\u662f\u4e0d\u5982\u5929\u7b97\u7684\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u51b7\u7b11\u9053\uff1a\u201c\u81ea\u7136\uff0c\u6211\u5c31\u4e0d\u4fe1\u4e16\u4e0a\u6709\u6c38\u8fdc\u53ef\u4ee5\u9690\u85cf\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u4e24\u4eba\u51b7\u773c\u65c1\u89c2\uff0c\u6697\u4e2d\u79c1\u8bed\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4ff1\u90fd\u751f\u51fa\u8bb8\u591a\u611f\u6168\u3002 \u3000\u3000\u90a3\u8fb9\u7684\u88f4\u73cf\uff0c\u5fc3\u4e2d\u66f4\u662f\u611f\u6168\u4e07\u5343\uff0c\u4ed6\u5446\u5446\u5730\u6014\u4e86\u534a\u664c\uff0c\u53f9\u9053\uff1a\u201c\u679c\u7136\u662f\u6a80\u5927\u53d4\u6d3e\u6765\u7684 \u4eba\uff0c\u4f46\u662f\u2026\u2026\u4f46\u662f\u2026\u2026\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u505a\u5462\uff1f\u4ed6\u8981\u6740\u6211\uff0c\u4ee5\u524d\u4e0d\u662f\u5bb9\u6613\u5f97\u5f88\u4e48\uff1f\u4f55\u5fc5\u7b49\u5230\u4eca \u65e5\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u5bf9\u4ed6\u6beb\u65e0\u5a01\u80c1\uff0c\u4ed6\u4e5f\u60f3\u4e0d\u5230\u4f60\u4eca\u65e5\u6709\u5982\u6b64\u6210\u5c31\uff0c \u662f\u4ee5\u2014\u2014\u201d\u88f4\u73cf\u957f\u53f9\u622a\u53e3\u9053\uff1a\u201c\u6211\u4eca\u65e5\u4e5f\u4e0d\u4f1a\u5bf9\u4ed6\u6709\u4efb\u4f55\u5a01\u80c1\u5440\uff01\u4ed6\u4e8e\u6211\u6709\u6069\u65e0\u4ec7\uff0c\u6211\u5bf9\u4ed6 \u53ea\u6709\u62a5\u6069\u4e4b\u5fc3\uff0c\u4ed6\u4e3a\u4f55\u8981\u6765\u6697\u7b97\u4e8e\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u957f\u53f9\u9053\uff1a\u201c\u88f4\u5144\uff0c\u6709\u65f6\u5c0f\u5f1f\u6211\u771f\u4e3a\u4f60\u53ef\u60b2\u53ef\u53f9\uff0c\u76f4\u5230\u4eca\u65e5\uff0c\u54c8\u2014\u2014\u4f60\u7adf\u7136 \u8fd8\u88ab\u8fd9\u6076\u8d3c\u8499\u5728\u9f13\u91cc\uff01\u201d \u3000\u3000\u88f4\u73cf\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u6ee1\u76b1\uff0c\u6ee1\u9762\u4ff1\u662f\u60b2\u54c0\u6c89\u91cd\u4e4b\u8272\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u5341\u5e74\u4e4b \u524d\uff0c\u5f00\u5c01\u57ce\u5916\uff0c\u4ee4\u5c0a\u4e0e\u4ee4\u53d4\uff0c\u7a76\u7adf\u662f\u6b7b\u5728\u4ec0\u4e48\u4eba\u7684\u624b\u4e0a\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u9707\uff0c\u9762\u8272\u5927\u53d8\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u96be\u9053\u4f60\u662f\u8bf4\u4ed6\u2026\u2026\u4f46\u90a3\u9ed1\u8863\u51f6\u624b\uff0c\u4e0d\u662f\u8fdc\u5728\u5341\u5e74 \u4e4b\u524d\uff0c\u4fbf\u5df2\u4e8e\u6b27\u9633\u8001\u9556\u5934\u540c\u5f52\u4e8e\u5c3d\uff0c\u6b7b\u5728\u5317\u4eac\u57ce\u5916\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9053\uff1a\u201c\u5317\u4eac\u57ce\u5916\u7684\u4e24\u5177\u5c38\u8eab\uff0c\u4e0d\u8fc7\u662f\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u7684\u91d1\u8749\u8131\u58f3\u4e4b\u8ba1\u800c \u5df2\uff01\u53ea\u53ef\u601c\u6b63\u76f4\u4ec1\u6148\u7684\u6b27\u9633\u8001\u9556\u5934\uff0c\u7adf\u4e3a\u4e86\u8fd9\u6076\u8d3c\u800c\u727a\u7272\uff0c\u66f4\u53ef\u53f9\u83bd\u83bd\u6b66\u6797\u4e4b\u95f4\uff0c\u7adf\u6ca1\u6709\u4e00 \u4e2a\u4eba\u770b\u51fa\u8fd9\u6076\u8d3c\u7684\u5978\u8ba1\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u950b\u4e00\u8f6c\uff0c\u7adf\u8f6c\u5230\u4e86\u90a3\u4ef6\u5341\u5e74\u4ee5\u524d\uff0c\u9707\u52a8\u5929\u4e0b\u6b66\u6797\u7684\u5947\u6848\u4e4b\u4e0a\uff0c\u7fa4\u8c6a\u66f4\u4e0d\u7981\u4e3a\u4e4b\u8038\u7136 \u52a8\u5bb9\u3002 \u3000\u3000\u8981\u77e5\u5341\u4f59\u5e74\u524d\uff0c\u90a3\u8499\u9762\u9ed1\u8863\u5947\u4eba\uff0c\u4ee5\u4e00\u4eba\u4e4b\u529b\uff0c\u8fde\u4f24\u5357\u4e03\u5317\u516d\u5341\u4e09\u7701\u5927\u5c0f\u9556\u5c40\u4e2d\u6240\u6709\u6210 \u540d\u7684\u9556\u5934\uff0c\u4f7f\u5f97\u6c5f\u6e56\u4e2d\u6240\u6709\u7684\u9556\u5c40\u4e0d\u662f\u88ab\u4ed6\u6bc1\uff0c\u4fbf\u662f\u81ea\u52a8\u6b47\u4e1a\uff0c\u4ece\u6b64\u4e00\u8e76\u4e0d\u632f\uff0c\u800c\u201c\u98de\u9f99\u9556 \u5c40\u201d\u65b9\u80fd\u79f0\u96c4\u4e8e\u5929\u4e0b\u3002 \u3000\u3000\u6b64\u4e8b\u4e0d\u4f46\u5f53\u65f6\u9707\u52a8\u6b66\u6797\uff0c\u76f4\u5230\u4eca\u65e5\uff0c\u4ecd\u662f\u6c5f\u6e56\u4e2d\u4e00\u4ef6\u810d\u7099\u4eba\u53e3\u4e4b\u4e8b\uff0c\u662f\u4ee5\u6b64\u523b\u56db\u4e0b\u7fa4\u8c6a \u4ff1\u90fd\u9e26\u96c0\u65e0\u58f0\uff0c\u542c\u4ed6\u53d9\u8ff0\u8fd9\u4ef6\u6b66\u6797\u79d8\u95fb\u3002 \u3000\u3000\u88f4\u73cf\u66f4\u662f\u9762\u5bb9\u82cd\u767d\uff0c\u5fc3\u5934\u72c2\u8df3\uff0c\u53cc\u638c\u7d27\u63e1\uff0c\u6307\u7532\u90fd\u5df2\u5d4c\u5165\u8089\u91cc\u3002 \u3000\u3000\u53ea\u542c\u201c\u795e\u624b\u201d\u6218\u98de\u63a5\u7740\u9053\uff1a\u201c\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\uff0c\u4e3a\u4e86\u72ec\u9738\u6c5f\u6e56\uff0c\u6267\u9556\u5c40\u754c\u4e4b\u725b\u8033\uff0c\u4e54 \u88c5\u6539\u626e\uff0c\u6740\u4e86\u90a3\u4e48\u591a\u6210\u540d\u7684\u9556\u5934\uff0c\u4ed6\u81ea\u4ee5\u4e3a\u5978\u8ba1\u5f97\u901e\uff0c\u505a\u5f97\u795e\u9b3c\u4e0d\u77e5\uff0c\u800c\u4e14\u7792\u5c3d\u5929\u4e0b\u4eba\u8033 \u76ee\uff0c\u76f4\u8fbe\u5341\u4f59\u5e74\u4e4b\u4e45\uff0c\u4f46\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u6211\u6218\u67d0\u4eca\u65e5\u7adf\u4f1a\u63ed\u7a7f\u4e86\u4ed6\u7684\u79d8 \u5bc6\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5f00\u5c01\u57ce\u5916\u6240\u6b7b\u7684\u90a3\u9ed1\u8863\u8499\u9762\u602a\u5ba2\uff0c\u4e0d\u77e5\u662f\u54ea\u4e2a\u65e0\u8f9c\u4e4b\u4eba\uff0c\u505a\u4e86 \u6a80\u660e\u90a3\u6076\u8d3c\u7684\u66ff\u6b7b\u51a4\u9b3c\u3002\u4ed6\u7adf\u5c06\u6b64\u4eba\u9762\u76ee\u5b8c\u5168\u51fb\u6bc1\uff0c\u4f7f\u5f97\u666e\u5929\u4e4b\u4e0b\uff0c\u90fd\u4ee5\u4e3a\u8499\u9762\u602a\u5ba2\u5df2\u6b7b\uff0c \u90a3\u4e48\u2018\u98de\u9f99\u9556\u5c40\u2019\u6c38\u65e0\u53d8\u6545\uff0c\u81ea\u7136\u662f\u5929\u7ecf\u5730\u4e49\u4e4b\u4e8b\uff0c\u4e5f\u65e0\u4eba\u4f1a\u6000\u7591\u5230\u4ed6\u8eab\u4e0a\uff0c\u4f46\u4ed4\u7ec6\u60f3\u6765\uff0c \u5176\u4e2d\u5c82\u65e0\u53ef\u7591\u4e4b\u5904\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u65b9\u81ea\u6b47\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u7fa4\u8c6a\u4e00\u9635\u60ca\u559d\u4e4b\u540e\uff0c\u53c8\u590d\u9e26\u96c0\u65e0\u58f0\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u8499\u9762\u602a\u5ba2\u4ee5\u4e00\u4eba\u4e4b\u529b\uff0c\u505a\u4e0b\u65e0\u6570\u5947\u6848\uff0c\u5c31\u8fde\u2018\u67aa\u5251\u65e0\u654c\u2019\u88f4\u6c0f\u53cc\u96c4 \u90a3\u822c\u6b66\u529f\uff0c\u4ff1\u975e\u5176\u4eba\u4e4b\u653e\u624b\uff1b\u6b27\u9633\u8001\u9556\u5934\u5e74\u4e8b\u5df2\u9ad8\uff0c\u6b66\u529f\u53c8\u975e\u7edd\u9876\u9ad8\u660e\uff0c\u600e\u4f1a\u662f\u5176\u4eba\u4e4b\u654c\uff0c \u600e\u4f1a\u4e0e\u4ed6\u5171\u5f52\u4e8e\u5c3d\uff1f\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u6570\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u6b27\u9633\u8001\u9556\u5934\u90a3\u591c\u5bbf\u4e8e\u2018\u98de\u9f99\u9556\u5c40\u2019\uff0c\u82e5\u6709\u591c\u884c\u4eba\u8fdb\u5165\u9556\u5c40\uff0c\u2018\u9f99 \u5f62\u516b\u638c\u2019\u600e\u4f1a\u6beb\u4e0d\u77e5\u60c5\uff0c\u800c\u8ba9\u6b27\u9633\u5e73\u4e4b\u4e00\u4e2a\u6d89\u9669\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u60ca\uff0c\u7a81\u5730\u60f3\u9053\uff0c\u90a3\u591c\u4ed6\u51fa\u6765\u4fbf\u6eba\uff0c\u4f3c\u4e4e\u89c1\u5230\u201c\u6a80\u5927\u53d4\u201d\u7684\u8eab\u5f71\u5728\u9662\u4e2d\u4e00\u95ea\u3002 \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u65e2\u60ca\u53c8\u9a87\uff0c\u5374\u53c8\u4e0d\u5fcd\u6000\u7591\u4ed6\u7684\u201c\u6a80\u5927\u53d4\u201d\u4f1a\u662f\u5982\u6b64\u4e07\u6076\u7684\u51f6\u624b\uff0c \u53e3\u4e2d\u8bb7\u8bb7\u9053\uff1a\u201c\u4f46\u2014\u2014\u8fd9\u4e9b\u4e0d\u8fc7\u90fd\u662f\u4f60\u7684\u731c\u6d4b\u800c\u5df2\uff0c\u5e76\u65e0\u4e00\u4eba\u4eb2\u773c\u76ee\u7779\uff0c\u8001\u2014\u2014\u201d\u201c\u795e\u624b\u201d \u6218\u98de\u957f\u53f9\u4e00\u58f0\uff0c\u622a\u53e3\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u76f4\u5230\u6b64\u523b\u96be\u9053\u8fd8\u4e0d\u660e\u767d\u4e48\uff1f\u4ed6\u6545\u4f5c\u5927\u4ec1\u5927\u4e49\u4e4b\u6001\uff0c\u5c06\u90a3 \u4e9b\u9556\u5e08\u6b7b\u540e\u7684\u5b64\u513f\u5b64\u5973\u5168\u90fd\u6536\u517b\u5728\u8eab\u8fb9\uff0c\u4f7f\u5f97\u6b66\u6797\u4e2d\u4eba\uff0c\u4eba\u4eba\u90fd\u79f0\u8d5e\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u662f\u4e2a \u5927\u5927\u7684\u597d\u4eba\uff0c\u4f46\u2014\u2014\u201d\u4ed6\u53c8\u81ea\u51b7\u7b11\u4e24\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u53ef\u66fe\u60f3\u5230\uff0c\u6a80\u660e\u53ef\u66fe\u4f20\u6388\u8fc7\u4f60\u4eec \u6b66\u529f\uff1f\u54fc\u54fc\u2014\u2014\u4ed6\u4e0d\u4f46\u672a\u66fe\u4f20\u6388\u8fc7\u4f60\u4eec\u6b66\u529f\uff0c\u800c\u4e14\u8fd8\u5c06\u4f60\u4eec\u9694\u79bb\u5f00\u6765\uff0c\u4f7f\u5f97\u4f60\u4eec\u6c38\u8fdc\u65e0\u6cd5\u7ed9 \u5728\u4e00\u5904\uff0c\u4e8e\u662f\u4ed6\u4fbf\u6c38\u8fdc\u9ad8\u6795\u65e0\u4f18\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u62c5\u5fc3\u6709\u4eba\u5411\u4ed6\u590d\u4ec7\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u5bd2\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u53c8\u81ea\u5012\u9000\u4e09\u6b65\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u6697\u6697\u5fd6\u9053\uff0c\u201c\u6211\u82e5\u662f\u771f\u7684\u611a\u8822\uff0c\u4eca\u65e5\u4fbf\u4e0d\u4f1a\u6709\u6b64\u6b66\u529f\u6210\u5c31\uff0c\u4ed6\u82e5\u662f\u771f\u7684\u4e0d\u613f\u6211\u4eec \u5b66\u6b66\uff0c\u800c\u8e48\u5148\u4eba\u4e4b\u8986\u8f99\uff0c\u4e3a\u4f55\u5374\u6559\u4ed6\u5973\u513f\u4e60\u6b66\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u51dd\u6ce8\u7740\u4ed6\uff0c\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u867d\u7136\u4ff1\u662f\u731c\u6d4b\uff0c\u4f46\u88f4\u5144\u4f60\u4e14\u4ed4\u7ec6\u4e00 \u60f3\uff0c\u5176\u4e2d\u53ef\u662f\u5b8c\u5168\u5408\u60c5\u5408\u7406\uff0c\u4f55\u51b5\u2014\u2014\u54fc\u54fc\uff01\u201d \u3000\u3000\u4ed6\u53c8\u81ea\u51b7\u201c\u54fc\u201d\u4e24\u58f0\uff0c\u4e00\u6325\u624b\u638c\uff0c\u9053\uff1a\u201c\u4ed6\u81ea\u4ee5\u4e3a\u505a\u4e8b\u9690\u79d8\uff0c\u5374\u7ec8\u7a76\u8fd8\u6709\u4eba\u770b\u5230\u4e86\u4ed6\u7684 \u79d8\u5bc6\u2026\u2026\u201d\u3002 \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u65b9\u624d\u81ea\u90a3\u8fb9\u9ed1\u6697\u5c71\u91ce\u4e2d\u6d8c\u51fa\u6765\u7684\u6570\u540d\u9ed1\u8863\u5927\u6c49\uff0c\u6b64\u523b\u7a81\u5730\u81ea\u5c71\u77f3\u540e\u6276\u51fa\u4e00\u4e2a \u4eba\u6765\u3002 \u3000\u3000\u88f4\u73cf\u51dd\u76ee\u671b\u5904\uff0c\u53ea\u89c1\u6b64\u4eba\u8eab\u8eaf\u867d\u7136\u4e0d\u77ee\uff0c\u4f46\u5374\u67af\u7626\u5df2\u6781\uff0c\u4eff\u4f5b\u4e00\u9635\u5c71\u98ce\u4fbf\u4f1a\u5c06\u4ed6\u5439\u5012\uff0c \u9762\u5bb9\u4e4b\u82cd\u767d\uff0c\u66f4\u50cf\u662f\u7ec8\u65e5\u4e0d\u89c1\u65e5\u8272\uff0c\u76ee\u5149\u95ea\u70c1\uff0c\u9762\u4e0a\u6c38\u8fdc\u5e26\u7740\u4e00\u79cd\u60ca\u614c\u6050\u60e7\u4e4b\u610f\uff0c\u751f\u50cf\u662f\u4e00 \u53ea\u7ec8\u5e74\u88ab\u730e\u6237\u8ffd\u9010\u7684\u91ce\u517d\u3002 \u3000\u3000\u4ed6\u811a\u6b65\u4e5f\u50cf\u662f\u8bb8\u4e45\u6ca1\u6709\u8d70\u8fc7\u8def\u4f3c\u7684\uff0c\u8e52\u8dda\u6c89\u91cd\uff0c\u8d70\u5230\u8fd1\u524d\uff0c\u66f4\u53ef\u770b\u51fa\u4ed6\u9762\u4e0a\u4e4b\u76b1\u7eb9\uff0c\u6bcf \u4e00\u6761\u90fd\u523b\u753b\u51fa\u6b64\u4eba\u5fc5\u5b9a\u7ecf\u5386\u4e86\u4e00\u6bb5\u6781\u4e3a\u8270\u82e6\u5fe7\u6101\u7684\u5c81\u6708\uff0c\u4f7f\u5f97\u6bcf\u4e00\u4e2a\u89c1\u5230\u4ed6\u7684\u4eba\u90fd\u4e0d\u7981\u8981\u4e3a \u4ed6\u53f9\u606f\u4e0d\u5df2\u3002 \u3000\u3000\u4e00\u6761\u9ed1\u8863\u5927\u6c49\uff0c\u642c\u6765\u4e00\u65b9\u5c71\u77f3\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u6276\u7740\u4ed6\u8f7b\u8f7b\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7fa4\u8c6a\u6b64\u523b\u5df2\u4ff1\u90fd\u77e5\u9053\u6b64\u4eba\u5fc5\u5b9a\u4e0e\u5341\u4f59\u5e74\u524d\u90a3\u4ef6\u9707\u52a8\u6b66\u6797\u7684\u5947\u6848\uff0c\u6709\u7740\u4e0d\u5bfb\u5e38\u7684\u5173\u7cfb\uff0c\u6b64 \u523b\u90fd\u4e0d\u7981\u6084\u6084\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u8d70\u5230\u8fd1\u524d\u3002 \u3000\u3000\u5c31\u8fde\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u8038\u7136\u52a8\u5bb9\uff0c\u800c\u9732\u51fa\u7559\u610f\u503e\u542c\u7684\u795e\u8272\u3002 \u3000\u3000\u53ea\u89c1\u6b64\u4eba\u76ee\u5149\u95ea\u7f29\uff0c\u56db\u4e0b\u4e71\u8f6c\uff0c\u8eab\u5b50\u4e5f\u5750\u4e0d\u5b89\u7a33\uff0c\u4eff\u4f5b\u9ed1\u6697\u4e4b\u4e2d\uff0c\u968f\u65f6\u90fd\u6709\u4eba\u4f1a\u98de\u5c06\u51fa \u6765\uff0c\u6765\u53d6\u4ed6\u6027\u547d\u4f3c\u7684\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5e72\u54b3\u6570\u58f0\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4f60\u59d3\u751a\u540d\u8c01\uff1f\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u8fd9\u9762\u5bb9\u82cd\u767d\u7684\u6c49\u5b50\u5782\u9996\u9053\uff1a\u201c\u5c0f\u4eba\u59d3\u8fc7\uff0c\u56e0\u4e3a\u751f\u5728\u5830\u9f99\u6e20\u65c1\uff0c\u6240\u4ee5\u53eb\u505a\u8fc7\u5927\u6e20\uff1b\u53c8\u56e0\u4e3a \u5c0f\u4eba\u662f\u4e2a\u8d76\u8f66\u7684\uff0c\u559c\u6b22\u559d\u9152\uff0c\u9047\u7740\u9152\u94fa\uff0c\u5c31\u4e0d\u60f3\u518d\u5f80\u524d\u8d76\u8f66\u5b50\uff0c\u6240\u4ee5\u6211\u7684\u540c\u884c\u670b\u53cb\uff0c\u90fd\u53eb\u6211 \u2018\u8fc7\u4e0d\u53bb\u2019\uff0c\u53cd\u800c\u6ca1\u6709\u4eba\u53eb\u6211\u8fc7\u5927\u6e20\u4e86\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u7aed\u529b\u63d0\u9ad8\u5589\u5499\uff0c\u4f46\u8bed\u58f0\u5374\u4ecd\u662f\u5341\u5206\u754f\u4ff1\u800c\u95ea\u7f29\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9053\uff1a\u201c\u4f60\u662f\u5426\u8ba4\u5f97\u90a3\u2018\u9f99\u5f62\u516b\u638c\u2019\u64c5\u660e\uff0c\u53c8\u662f\u5982\u4f55\u8ba4\u5f97\u4ed6\u7684\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u542c\u5230\u201c\u9f99\u5f62\u516b\u638c\u201d\u7684\u540d\u5b57\uff0c\u5168\u8eab\u4f3c\u4e4e\u90fd\u4e3a\u4e4b\u98a4\u6296\u4e86\u4e00\u4e0b\uff0c\u76ee\u5149\u56db\u4e0b\u8f6c\u4e86\u4e00 \u8f6c\uff0c\u65b9\u81ea\u7b54\u9053\uff1a\u201c\u5c0f\u4eba\u662f\u8ba4\u5f97\u6a80\u5927\u7237\u7684\uff0c\u56e0\u4e3a\u2018\u98de\u9f99\u9556\u5c40\u2019\u66fe\u7ecf\u96c7\u8fc7\u5c0f\u4eba\u7684\u5927\u8f66\uff0c\u90a3\u4e00\u6b21\u5c31 \u662f\u6a80\u5927\u7237\u4eb2\u81ea\u62bc\u7684\u9556\uff0c\u800c\u4e14\u8fd8\u548c\u5c0f\u4eba\u8bf4\u8fc7\u4e00\u53e5\u7b11\u8bdd\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6c89\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u7b11\u8bdd\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u7f29\u7740\u8116\u5b50\uff0c\u9053\uff1a\u201c\u4ed6\u95ee\u5c0f\u4eba\u4e3a\u4ec0\u4e48\u53eb\u505a\u2018\u8fc7\u4e0d\u53bb\u2019\uff1f\u4ed6\u8001\u4eba\u5bb6\u8bf4\uff1a\u4e16\u754c\u4e0a\u6ca1 \u6709\u4ec0\u4e48\u8fc7\u4e0d\u53bb\u7684\u4e8b\uff0c\u53eb\u6211\u628a\u8fd9\u540d\u5b57\u6539\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u5341\u4f59\u5e74\u524d\uff0c\u4e00\u4e2a\u5927\u96ea\u7eb7\u98de\u7684\u591c\u665a\uff0c\u4f60\u53ef\u662f\u5728\u5f00\u5c01 \u57ce\uff1f\u4f60\u5728\u5f00\u5c01\u57ce\u95e8\u5916\uff0c\u53c8\u770b\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u7a81\u5730\u53c8\u662f\u4e00\u9635\u98a4\u6296\uff0c\u76ee\u4e2d\u7684\u60ca\u6050\u754f\u60e7\u4e4b\u8272\uff0c\u66f4\u52a0\u660e\u663e\u3002 \u3000\u3000\u7fa4\u8c6a\u4ff1\u90fd\u77e5\u9053\u4ed6\u8fd9\u53e5\u8bdd\u5fc5\u5b9a\u5173\u7cfb\u751a\u5927\uff0c\u662f\u4ee5\u5c4f\u606f\u9759\u542c\uff0c\u53ea\u662f\u4ed6\u4e45\u4e45\u90fd\u672a\u8bf4\u51fa\u8bdd\u6765\uff0c\u7259\u9f7f \u5374\u5728\u4e0d\u4f4f\u5730\u201c\u54af\u54af\u201d\u4f5c\u54cd\uff0c\u50cf\u662f\u751f\u6015\u81ea\u5df1\u4e00\u8bf4\u51fa\u8fd9\u756a\u8bdd\u6765\uff0c\u7acb\u523b\u4fbf\u4f1a\u6709\u6740\u8eab\u4e4b\u7978\uff01 \u3000\u3000\u591c\u66f4\u6df1\uff0c\u98ce\u66f4\u6025\uff0c\u56db\u4e0b\u7684\u706b\u7130\uff0c\u4e5f\u56e0\u65e0\u4eba\u7167\u987e\uff0c\u800c\u6e10\u6e10\u9eef\u6de1\u8870\u5f31\uff0c\u751a\u81f3\u7ec8\u4e8e\u7184\u706d\u3002 \u3000\u3000\u4e8e\u662f\u5927\u5730\u53d8\u5f97\u66f4\u52a0\u5bd2\u51b7\uff0c\u66f4\u52a0\u9ed1\u6697\uff0c\u7ed9\u56db\u4e0b\u7684\u6b66\u6797\u7fa4\u8c6a\u5fc3\u4e2d\uff0c\u53c8\u5e73\u6dfb\u4e86\u51e0\u5206\u60ca\u6817\u7684\u5bd2 \u610f\u3002 \u3000\u3000\u88f4\u73cf\u9762\u5bb9\u82cd\u767d\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u51dd\u671b\u5728\u201c\u8fc7\u4e0d\u53bb\u201d\u8eab\u4e0a\uff0c\u5fc3\u623f\u8df3\u52a8\u66f4\u5267\uff0c\u53cc\u62f3\u4e5f\u63e1\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u5982\u70ac\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u56db\u4e0b\u4ff1\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u6709\u4efb\u4f55\u4eba\u6562\u6765\u4f24 \u5bb3\u4e8e\u4f60\uff0c\u4f60\u53ea\u7ba1\u653e\u80c6\u8bf4\u51fa\u4fbf\u662f\u2014\u2014\u201d\u4ed6\u4f38\u624b\u5411\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u2018\u88f4\u5927\u5148\u751f\u2019\u5c31 \u662f\u6614\u5e74\u2018\u67aa\u5251\u65e0\u6545\u2019\u88f4\u6c0f\u53cc\u96c4\u7684\u540e\u4eba\uff0c\u4ed6\u7684\u6b66\u529f\u6bd4\u2018\u9f99\u5f62\u516b\u638c\u2019\u66f4\u9ad8\uff0c\u4f60\u8bf4\u51fa\u6765\u540e\uff0c\u4ed6\u81ea\u4f1a \u4fdd\u62a4\u4f60\u3002\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u62ac\u5934\u671b\u4e86\u88f4\u73cf\u4e00\u773c\uff0c\u77ac\u5373\u5782\u4e0b\u5934\uff0c\u4f3c\u4e4e\u5446\u5446\u5730\u60f3\u4e86\u8bb8\u4e45\uff0c\u53c8\u81ea\u8f7b\u54b3\u6570\u58f0\u3002 \u3000\u3000\u4ed6\u8eab\u65c1\u7684\u4e00\u6761\u9ed1\u8863\u5927\u6c49\uff0c\u9012\u7ed9\u4ed6\u4e00\u74f6\u767d\u9152\uff0c\u4ed6\u63a5\u5728\u624b\u91cc\uff0c\u62d4\u5f00\u74f6\u76d6\uff0c\u53c8\u5173\u8d77\uff0c\u5173\u8d77\u74f6 \u76d6\uff0c\u53c8\u62d4\u5f00\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u4ef0\u5929\u559d\u4e86\u51e0\u53e3\u70c8\u9152\uff0c\u52c7\u6c14\u4f3c\u5df2\u5927\u589e\uff0c\u53c8\u62ac\u5934\u671b\u4e86\u88f4\u73cf\u4e00\u773c\uff0c\u53c8\u8f7b\u54b3\u6570\u58f0\uff0c\u65b9\u81ea \u5f90\u5f90\u9053\uff1a\u201c\u90a3\u4e00\u5929\uff0c\u5929\u6c14\u5f88\u51b7\uff0c\u5927\u96ea\u7eb7\u98de\uff0c\u5730\u4e0a\u7684\u96ea\uff0c\u79ef\u5f97\u5f88\u539a\uff0c\u6211\u8d76\u7740\u8f66\u5b50\uff0c\u5230\u4e86\u5f00\u5c01\uff0c \u5b9e\u5728\u8fc7\u4e0d\u53bb\u4e86\u3002\u201d \u3000\u3000\u6709\u51e0\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u542c\u5230\u201c\u8fc7\u4e0d\u53bb\u201d\u4e09\u5b57\uff0c\u4f3c\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u7b11\u4e86\u8d77\u6765\uff0c\u4f46\u4e00\u671b\u56db\u4e0b\u4f17\u4eba\u7684\u795e \u8272\uff0c\u90a3\u79cd\u6c89\u91cd\u8083\u7a46\u4e4b\u6c14\uff0c\u5374\u53c8\u5c06\u4ed6\u4eec\u7684\u7b11\u58f0\u538b\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u542c\u201c\u8fc7\u4e0d\u53bb\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u5230\u4e86\u5f00\u5c01\u57ce\uff0c\u6211\u5c31\u6b47\u4e0b\u6765\uff0c\u5728\u57ce\u95e8\u9644\u8fd1\uff0c\u627e\u4e86\u5bb6\u5c0f\u9152 \u94fa\uff0c\u559d\u8d77\u9152\u6765\uff0c\u559d\u5230\u4e00\u534a\uff0c\u6211\u8d70\u5230\u95e8\u53e3\u5410\u75f0\uff0c\u54ea\u77e5\u4e00\u6380\u5e18\u5b50\uff0c\u5c31\u770b\u5230\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u6a80\u5927 \u7237\u9a91\u7740\u5339\u9a6c\u81ea\u8857\u4e0a\u8d70\u8fc7\u201d\u795e\u624b\u201c\u6218\u98de\u6c89\u58f0\u622a\u53e3\u9053\uff1a\u201c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4f60\u662f\u5426\u770b\u5f97\u6e05\u695a\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u900f\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u65f6\u867d\u662f\u9ed1\u591c\uff0c\u4f46\u6ee1\u5730\u7684\u96ea\uff0c\u5149\u7ebf\u5e76\u4e0d\u5927\u6697\uff0c\u662f\u4ee5\u6211\u5b9e\u662f \u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u7edd\u4e0d\u4f1a\u6709\u534a\u5206\u5dee\u9519\uff0c\u90a3\u65f6\u6211\u8fd8\u5728\u5947\u602a\uff0c\u6a80\u5927\u7237\u5b64\u8eab\u4e00\u4eba\uff0c\u600e\u4e48\u4f1a\u8dd1\u5230\u5f00\u5c01\u57ce \u6765\uff1f\u4f46\u662f\u6211\u60e6\u5ff5\u7740\u559d\u9152\uff0c\u4e5f\u6ca1\u6709\u5341\u5206\u5728\u610f\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7acb\u523b\u53c8\u63a5\u9053\uff1a\u201c\u6a80\u5927\u7237\u672c\u6765\u5c06\u5e3d\u6a90\u538b\u5f97\u5f88\u4f4e\uff0c\u82e5\u4e0d\u662f\u6070\u5de7\u4e00\u9635\u98ce\uff0c\u5439\u5f00\u6a80 \u5927\u7237\u7684\u5e3d\u5b50\uff0c\u6211\u4e5f\u4e0d\u4f1a\u770b\u5f97\u51fa\u4ed6\u662f\u8001\u4eba\u5bb6\u7684\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u61d4\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u96be\u9053\u771f\u662f\u2018\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\u2019\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u70b9\u4e86\u70b9\u5934\uff0c\u6c89\u58f0\u53c8\u9053\uff1a\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u5c06\u8116\u5b50\u7f29\u5f97\u66f4\u6df1\uff0c\u63a5\u7740\u9053\uff1a\u201c\u540e\u6765\u6211\u9152\u559d\u5b8c\u4e86\uff0c\u5df2\u6709\u4e03\u516b\u5206\u9189\u610f\uff0c\u89c9\u5f97\u751a\u662f \u8212\u670d\uff0c\u4eff\u4f5b\u5929\u6c14\u4e5f\u4e0d\u751a\u51b7\u4e86\uff0c\u4e58\u7740\u9152\u5174\uff0c\u95ef\u4e0a\u4e86\u5f00\u5c01\u5e9c\u7684\u57ce\u697c\uff0c\u5f80\u4e0b\u4e00\u770b\uff0c\u53ea\u89c1\u8fdc\u8fdc\u7684\u96ea\u5730 \u4e0a\uff0c\u4f3c\u4e4e\u6709\u4e09\u4e24\u6761\u4eba\u5f71\u5728\u6765\u56de\u8df3\u52a8\u7740\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u5df2\u6709\u4e03\u516b\u5206\u9152\u610f\uff0c\u8fd8\u80fd\u770b\u5f97\u90a3\u4e48\u8fdc\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u9053\uff1a\u201c\u57ce\u697c\u4e0a\u98ce\u5f88\u5927\uff0c\u6211\u4e0a\u53bb\u540e\u9152\u610f\u5c31\u50cf\u662f\u9192\u4e86\u4e09\u5206\uff0c\u57ce\u697c\u5916\u4e00\u7247\u767d\u96ea\uff0c\u90a3 \u4eba\u5f71\u53c8\u8df3\u52a8\u5f97\u5f88\u6025\u5267\uff0c\u662f\u4ee5\u6211\u624d\u770b\u5f97\u89c1\uff0c\u90a3\u65f6\u6211\u89c9\u5f97\u8fd9\u4e09\u4eba\u4f3c\u4e4e\u662f\u5728\u62fc\u547d\u640f\u6597\uff0c\u7b49\u4e86\u4e00\u4f1a \u513f\uff0c\u4ed6\u4eec\u7a81\u7136\u505c\u6b62\u4e86\uff0c\u53ea\u5269\u4e0b\u4e86\u4e00\u6761\u4eba\u5f71\uff0c\u53c8\u9a91\u4e0a\u4e86\u9a6c\uff0c\u7adf\u5411\u8fd9\u8fb9\u98de\u5f1b\u800c\u6765\uff0c\u6211\u7531\u4e0a\u800c\u4e0b\uff0c \u770b\u5f97\u8bf7\u6e05\u695a\u695a\uff0c\u9a6c\u4e0a\u4eba\u7adf\u7136\u8fd8\u662f\u90a3\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u6a80\u5927\u7237\uff01\u201d \u3000\u3000\u88f4\u73cf\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u770b\u5f97\u662f\u5426\u5f53\u771f\u6e05\u695a\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u754f\u602f\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u770b\u8fc7\u6a80\u5927\u7237\u90a3\u5929\u6240\u7a7f\u7684\u8863\u670d\uff0c\u6211\u60f3\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u9519\u7684\u3002\u201d \u3000\u3000\u88f4\u73cf\u8eab\u5f62\u6447\u4e86\u4e24\u6447\uff0c\u4fbf\u50cf\u77f3\u50cf\u822c\u6728\u7136\u800c\u7acb\uff0c\u76ee\u5149\u76f4\u89c6\u7740\u8fdc\u65b9\uff0c\u8fdc\u65b9\u7684\u9ed1\u6697\u4e2d\uff0c\u4f3c\u4e4e\u6709\u4e00 \u5f20\u201c\u9f99\u5f62\u516b\u638c\u201d\u72de\u7b11\u7740\u7684\u9762\u5bb9\u3002 \u3000\u3000\u7fa4\u8c6a\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u9a9a\u4e71\u4e86\u8d77\u6765\uff0c\u6709\u7684\u65e5\u77aa\u53e3\u5446\uff0c\u6709\u7684\u4e92\u76f8\u79c1\u8bed\uff01 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4ec1\u4e49\u4e3a\u5148\u7684\u2018\u9f99\u5f62\u516b\u638c\u2019\u7adf\u662f\u4e2a\u8863\u51a0\u79bd\u517d\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u624b\u634b\u987b\uff0c\u76f4\u5230\u8fd9\u4e00\u9635\u9a9a\u4e71\u5b8c\u5168\u5e73\u606f\uff0c\u7a81\u5730\u5389\u58f0\u9053\uff1a\u201c\u5341\u4f59\u5e74\u524d\uff0c\u4f60\u4fbf\u5df2 \u77e5\u9053\u6b64\u4e8b\uff0c\u600e\u5730\u76f4\u5230\u4eca\u65e5\u65b9\u81ea\u8bf4\u51fa\uff1f\u96be\u9053\u4f60\u5df2\u53d7\u8fc7\u4ec0\u4e48\u4eba\u7684\u54b8\u8feb\u5229\u8bf1\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u98a4\u58f0\u9053\uff1a\u201c\u90a3\u592d\u665a\u4e0a\u6211\u8fd8\u4e0d\u77e5\u9053\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u76f4\u5230\u7b2c\u4e8c\u5929\u6e05\u6668\uff0c\u6211\u624d \u542c\u5230\u2018\u67aa\u5251\u65e0\u6545\u2019\u4e24\u4f4d\u82f1\u96c4\u88ab\u4eba\u6740\u6b7b\u7684\u6d88\u606f\uff01\u201d \u3000\u3000\u201c\u6211\u5f53\u65f6\u5fc3\u91cc\u53c8\u60ca\u53c8\u6015\uff0c\u800c\u4e14\u8d8a\u60f3\u8d8a\u6015\uff0c\u6211\u77e5\u9053\u6a80\u5927\u7237\u529e\u4e0b\u8fd9\u4ef6\u4e8b\u4e00\u5b9a\u4e0d\u613f\u610f\u8ba9\u4eba\u77e5 \u9053\uff0c\u4ed6\u8001\u4eba\u5bb6\u82e5\u662f\u77e5\u9053\u6211\u770b\u5230\u4e86\u4ed6\uff0c\u4e00\u5b9a\u4f1a\u5c06\u6211\u6740\u6b7b\u706d\u53e3\uff0c\u6211\u60f3\u6c42\u4eba\u4fdd\u62a4\uff0c\u4f46\u90a3\u65f6\u6b66\u6797\u6709\u8c01 \u80fd\u4e0e\u6a80\u5927\u7237\u5bf9\u62db\u5462\uff1f\u6709\u8c01\u4f1a\u76f8\u4fe1\u4e00\u4e2a\u8d76\u8f66\u4eba\u7684\u8bdd\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6c89\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4fbf\u5982\u4f55\u5904\u7406\u4e86\u6b64\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u53ef\u4e0d\u77e5\u6211\u90a3\u4e00\u63a2\u5934\u7684\u65f6\u5019\uff0c\u6a80\u5927\u7237\u6709\u6ca1\u6709\u770b \u5230\u6211\uff1f\u6211\u6015\u5f97\u8981\u6b7b\uff0c\u6211\u5c06\u5927\u8f66\u5356\u4e86\uff0c\u8fdc\u8fdc\u5730\u8eb2\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9053\uff1a\u201c\u800c\u4e14\u4e00\u8eb2\u5c31\u662f\u5341\u5e74\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u76ee\u5149\u9eef\u7136\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u672c\u60f3\u7b49\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u5927\u7237\u4e8b\u60c5\u53d1\u4f5c\u4e4b \u540e\u518d\u51fa\u6765\uff0c\u54ea\u77e5\u8fd9\u4ef6\u4e8b\u7adf\u771f\u7684\u505a\u5f97\u795e\u4e0d\u77e5\uff0c\u9b3c\u4e0d\u89c9\uff0c\u6211\u53c8\u5e0c\u671b\u4ed6\u5feb\u4e9b\u2026\u2026\u5feb\u4e9b\u6b7b\uff0c\u54ea\u77e5\u4ed6\u53c8 \u4e0d\u6b7b\uff0c\u6240\u4ee5\u2026\u2026\uff0c\u5509\uff0c\u6211\u4e00\u8eb2\u5c31\u662f\u5341\u51e0\u5e74\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u626c\uff0c\u5389\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u6b64\u523b\u4f60\u4e3a\u4f55\u53c8\u8bf4\u51fa\u6765\uff0c\u96be\u9053\u4f60\u5df2\u4e0d\u6015\u6b7b\u4e86 \u4e48\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u9ed8\u7136\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u201c\u6211\u672c\u4e0d\u60f3\u51fa\u6765\uff0c\u4f46\u662f\u2026\u2026\u5509\uff0c\u8fd9\u5341\u51e0\u5e74\u7684\u65e5\u5b50\uff0c\u7684\u786e \u96be\u8fc7\uff0c\u6211\u65e2\u6ca1\u6709\u50a8\u84c4\uff0c\u53c8\u6ca1\u6709\u6052\u4ea7\uff0c\u53ea\u9760\u7740\u6211\u8001\u5a46\u4e00\u53cc\u624b\u4e3a\u4eba\u5bb6\u6d17\u8863\u517b\u7740\u6211\uff0c\u6211\u5374\u8eb2\u5728\u5bb6 \u91cc\uff0c\u4e0d\u6562\u51fa\u95e8\u4e00\u6b65\uff0c\u6574\u5929\u5728\u7095\u4e0a\uff0c\u6211\u51e0\u4e4e\u8fde\u8def\u90fd\u4e0d\u4f1a\u8d70\u4e86\uff0c\u53c8\u5bc2\u5bde\u3001\u53c8\u5bb3\u6015\uff0c\u751f\u6015\u6a80\u5927\u7237\u4f1a \u7a81\u7136\u4ece\u95e8\u91cc\u6478\u8fdb\u6765\uff0c\u4e00\u5200\u5c06\u6211\u6740\u6b7b\uff01\u201d \u3000\u3000\u4ed6\u5446\u4e86\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u4f46\u662f\u6700\u8fd1\uff0c\u6211\u8001\u5a46\u6b7b\u4e86\uff0c\u6211\u8fde\u996d\u90fd\u6ca1\u5f97\u5403\uff0c\u6709\u4e00\u5929\u534a\u591c\u51fa\u6765\uff0c\u95ee \u4eba\u8981\u4e86\u4e9b\u51b7\u996d\uff0c\u4f46\u662f\u6211\u5403\u5b8c\u4e86\u56de\u53bb\u7684\u65f6\u5019\uff0c\u7a81\u7136\u53d1\u73b0\u6709\u4e00\u4e2a\u62ff\u7740\u5200\u7684\u4eba\u5728\u6211\u95e8\u4e0a\u8d70\u8fc7\uff0c\u6211\u8fde \u90a3\u72d7\u7a9d\u4f3c\u7684\u5bb6\u90fd\u4e0d\u6562\u56de\u4e86\uff0c\u4e58\u591c\u8dd1\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u53ea\u542c\u4ed6\u6c89\u91cd\u63a5\u7740\u9053\uff1a\u201c\u4f46\u662f\u6211\u53c8\u80fd\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u6211\u8fde\u8def\u90fd\u4e0d\u5927\u4f1a\u8d70\u4e86\uff0c\u53c8\u6ca1\u6709\u94b1\uff0c\u767d\u5929 \u53c8\u4e0d\u6562\u9732\u9762\uff0c\u53ea\u6709\u5728\u665a\u4e0a\u6398\u4e9b\u8349\u6839\u3001\u6811\u76ae\u5145\u9965\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8fc7\u4e86\u51e0\u5929\uff0c\u6211\u5b9e\u5728\u65e0\u6cd5\u518d\u5fcd\u53d7\u4e86\uff0c\u6709\u4e00\u5929\u665a\u4e0a\u6211\u7761\u5728\u4e00\u6761\u5f04\u5802\u7684\u5783\u573e\u7bb1\u65c1\u8fb9\uff0c\u770b \u5230\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u60ca\u6050\u5730\u77a7\u4e86\u6218\u98de\u4e00\u773c\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u98a4\u58f0\u9053\uff1a\u201c\u6211\u53c8\u51b7\u53c8\u997f\uff0c\u5b9e\u5728\u7761\u4e0d\u7740\uff0c\u7a81\u7136\u542c\u5230\u4e00\u95f4\u5c4b\u5b50\u91cc\u53d1\u51fa\u597d\u591a\u58f0\u60e8 \u547c\uff0c\u6211\u4e00\u60ca\u4e4b\u4e0b\uff0c\u7ffb\u8eab\u5c31\u8dd1\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9053\uff1a\u201c\u8dd1\u4e86\u6ca1\u51e0\u6b65\uff0c\u6211\u624b\u4e0b\u7684\u4e00\u4e2a\u5144\u5f1f\u5c31\u4e00\u628a\u6293\u4e86\u4f60\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u98a4\u6296\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u672c\u5df2\u5413\u5f97\u51e0\u4e4e\u6655\u8fc7\u53bb\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53c8\u53d1\u73b0\u81ea\u5df1\u7adf \u662f\u5728\u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u95e8\u53e3\uff0c\u6211\u4ee5\u4e3a\u90a3\u4f4d\u5927\u54e5\u5c31\u662f\u6a80\u5927\u7237\u7684\u624b\u4e0b\uff0c\u5c31\u6251\u5730\u4e00\u58f0\u8dea\u4e86\u4e0b\u53bb\uff0c\u54c0\u547c \u7740\u8bf4\uff1a\u2018\u5927\u7237\uff0c\u6211\u6ca1\u6709\u770b\u5230\uff0c\u8bf7\u5927\u7237\u884c\u884c\u597d\uff0c\u653e\u6211\u8d70\uff0c\u544a\u8bc9\u6a80\u5927\u7237\uff0c\u90a3\u5929\u665a\u4e0a\u6211\u867d\u5728\u5f00\u5c01 \u57ce\uff0c\u4f46\u662f\u6211\u4ec0\u4e48\u4e5f\u6ca1\u6709\u770b\u5230\u3002\u2019\u201c\u201d\u795e\u624b\u201c\u6218\u98de\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u90a3\u5144\u5f1f\u4ee5\u4e3a\u4f60\u662f\u4e2a\u75af\u5b50\uff0c \u672c\u60f3\u5c06\u4f60\u653e\u8d70\uff0c\u4f46\u6211\u542c\u5230\u4f60\u7684\u8bdd\uff0c\u89c9\u5f97\u5176\u4e2d\u5927\u6709\u9690\u79d8\uff0c\u5c31\u903c\u7740\u95ee\u4f60\u662f\u8c01\uff01\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u4e0d\u4f4f\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\u6b63\u662f\uff0c\u6211\u77e5\u9053\u4e86\u5927\u7237\u4f60\u4e0d\u662f\u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u4eba\uff0c\u53c8\u770b\u5230 \u5927\u7237\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u51b7\u9053\uff1a\u201c\u544a\u8bc9\u4ed6\u65e0\u59a8\uff0c\u53ea\u7ba1\u8bf4\u4e0b\u53bb\u4fbf\u662f\u3002\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u62bd\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u4eba\u53c8\u770b\u5230\u5927\u7237\u4f60\u5c06\u90a3\u95f4\u2018\u98de\u9f99\u9556\u5c40\u2019\u5168\u90e8\u62c6\u6bc1\u4e86\uff0c \u5c31\u77e5\u9053\u5927\u7237\u4f60\u4e00\u5b9a\u662f\u2018\u9f99\u5f62\u516b\u638c\u2019\u7684\u4ec7\u4eba\uff0c\u800c\u4e14\u4e00\u70b9\u4e5f\u4e0d\u6015\u2018\u9f99\u5f62\u516b\u638c\u2019\uff0c\u6240\u4ee5\u5c31\u5c06\u8fd9\u4ef6\u4e8b \u4ece\u5934\u5230\u5c3e\uff0c\u6e90\u6e90\u672c\u672c\u4ed6\u8bf4\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u4e00\u626b\uff0c\u7a81\u5730\u6717\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u670b\u53cb\uff0c\u53ef\u662f\u90fd\u542c\u5230\u4e86\u8fd9\u4f4d\u670b\u53cb\u7684\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u6709\u7684\u4ecd\u7136\u76ee\u77aa\u53e3\u5446\uff0c\u6709\u7684\u4e0d\u4f4f\u70b9\u5934\uff0c\u4e5f\u6709\u7684\u7eb7\u7eb7\u60ca\u53f9\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u6311\uff0c\u6717\u58f0\u53c8\u9053\uff1a\u201c\u5404\u4f4d\u5230\u4e86\u6b64\u523b\uff0c\u60f3\u5fc5\u5df2\u77e5\u9053\u90a3\u2018\u9f99\u5f62\u516b\u638c\u2019\u5230\u5e95 \u662f\u600e\u6837\u7684\u89d2\u8272\u4e86\uff0c\u8fd9\u4f4d\u8fc7\u670b\u53cb\u90a3\u65e5\u5728\u5f00\u5c01\u57ce\uff0c\u6b63\u662f\u2018\u67aa\u5251\u65e0\u6545\u2019\u88f4\u6c0f\u53cc\u96c4\u4fdd\u7740\u4e00\u8d9f\u7ea2\u8d27\uff0c\u5c31 \u662f\u90a3\u7f55\u4e16\u4e4b\u73cd\u2018\u78a7\u7389\u8d61\u870d\u2019\u5230\u6cb3\u5317\u53bb\u7684\u2014\u2014\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u4e00\u76f4\u672c\u6728\u800c\u7acb\u7684\u88f4\u73cf\u7a81\u5730\u5927\u559d\u9053\uff1a \u201c\u78a7\u7389\u8d61\u870d\uff1f\u6211\u7239\u7239\u53d7\u5bb3\u90a3\u65e5\uff0c\u4fdd\u7684\u5c31\u662f\u78a7\u7389\u8d61\u870d\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u89c1\u4e86\u4ed6\u7684\u795e\u60c5\uff0c\u4e0d\u89c9\u4e00\u6115\uff0c\u9894\u9996\u9053\uff1a\u201c\u6b63\u662f\u2018\u78a7\u7389\u87fe\u870d\u2019\uff0c\u6b64\u4e8b\u5929\u4e0b\u5c3d\u4eba \u7686\u77e5\uff0c\u600e\u5730\u88f4\u5144\u8fd8\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5012\u9000\u4e09\u6b65\uff0c\u53cc\u62f3\u7d27\u63e1\uff0c\u76ee\u4e2d\u6c69\u6c69\u6d41\u4e0b\u4e86\u6cea\u73e0\uff0c\u6d41\u8fc7\u4ed6\u82cd\u767d\u800c\u5931\u8840\u7684\u9762\u988a\uff0c\u4ed6\u4ef0\u5929\u54c0 \u547c\u9053\uff1a\u201c\u82cd\u5927\u5440\u82cd\u5929\uff0c\u90a3\u51b7\u8840\u7684\u51f6\u624b\uff0c\u5f53\u771f\u5c31\u662f\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e86\u81ea\u5df1\u95ef\u5165\u6a80\u660e\u4e66\u623f\u90a3\u65e5\uff0c\u6a80\u660e\u5728\u638c\u4e2d\u628a\u73a9\u7740\u7684\u4e1c\u897f\uff0c\u6b63\u662f\u4e00\u53ea\u78a7\u7389\u7684\u73a9 \u7269\u3002 \u3000\u3000\u4ed6\u4e5f\u5ffd\u7136\u53d1\u89c9\u4e86\uff0c\u4e3a\u4ec0\u4e48\u6a80\u660e\u770b\u5230\u81ea\u5df1\u65f6\uff0c\u4f1a\u90a3\u822c\u60ca\u614c\u5730\u5c06\u90a3\u4ef6\u73a9\u7269\u6536\u8d77\uff0c\u800c\u4e14\u6c38\u8fdc\u4e0d \u8bb8\u81ea\u5df1\u518d\u5230\u4e66\u623f\u4e2d\u53bb\u3002 \u3000\u3000\u4e00\u4e9b\u4ed6\u4ee5\u524d\u4e0d\u89e3\u91ca\uff0c\u4e5f\u4e0d\u613f\u89e3\u91ca\u7684\u4e8b\uff0c\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u7adf\u5df2\u5168\u90e8\u6709\u7b54\u6848\u4e86\u2014\u2014\u800c\u8fd9\u7b54\u6848 \u53c8\u5982\u6b64\u4ee4\u4eba\u75db\u82e6\u3002 \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u754f\u602f\u5730\u671b\u7740\u4ed6\uff0c\u7fa4\u8c6a\u4e5f\u540c\u60c5\u5730\u671b\u7740\u4ed6\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9f50\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u51b7\u5bd2\u7af9 \u4f4e\u4f4e\u53f9\u9053\uff1a\u201c\u6587\u742a--\u4e00\u5509\uff0c\u5979\u771f\u53ef\u601c\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u6c89\u91cd\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u624b\u634b\u987b\uff0c\u5927\u559d\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u82e5\u662f\u8fd8\u6709\u516c\u9053\uff0c\u8fd8\u80fd\u8ba9\u2018\u9f99\u5f62\u516b\u638c\u2019\u90a3 \u6076\u8d3c\u6d3b\u5728\u4e16\u4e0a\u4e48\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u54c4\u7136\u4e00\u9635\u5927\u559d\uff0c\u6b64\u523b\u4eba\u4eba\u4ff1\u662f\u6124\u6012\u586b\u81ba\uff0c\u82e5\u662f\u201c\u9f99\u5f62\u516b\u638c\u201d\u8eab\u5728\u6b64\u95f4\uff0c\u4ed6\u6b66\u529f\u518d \u9ad8\uff0c\u53ea\u6015\u4e5f\u8981\u88ab\u8fd9\u80a1\u6012\u6c14\u51fb\u5012\uff01 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u5927\u559d\u9053\uff1a\u201c\u2018\u67aa\u5251\u65e0\u6545\u2019\u60e8\u6b7b\u5728\u2018\u9f99\u5f62\u516b\u638c\u2019\u90a3\u6076\u8d3c\u4e4b\u624b\uff0c\u6211\u2018\u6c5f\u5357\u540c \u76df\u2019\uff0c\u5df2\u51b3\u5fc3\u8981\u4e3a\u88f4\u5927\u5148\u751f\u590d\u4ec7\uff0c\u5404\u4f4d\u4ff1\u662f\u6ee1\u6000\u6b63\u4e49\u7684\u70ed\u8840\u7537\u513f\uff0c\u867d\u975e\u2018\u6c5f\u5357\u540c\u76df\u2019\uff0c\u4e5f\u5e94 \u8be5\u8981\u52a9\u6211\u5144\u5f1f\u4e00\u81c2\u4e4b\u529b\uff0c\u5404\u4f4d\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u53c8\u662f\u4e00\u9635\u5927\u559d\uff0c\u76f4\u9707\u5f97\u56db\u91ce\u5c06\u843d\u672a\u843d\u7684\u8427\u8427\u6728\u53f6\uff0c\u90fd\u4e3a\u4e4b\u7c0c\u850c\u98de\u843d\uff0c\u4e25\u51ac\u4f3c\u4e4e\u5728\u8fd9 \u5239\u90a3\u95f4\uff0c\u4fbf\u5df2\u964d\u4e34\u5927\u5730\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u6709\u8fd9\u8bb8\u591a\u670b\u53cb\u4e3a\u4f60\u540e\u76fe\uff0c\u8fd8\u6015\u5927\u4ec7\u4e0d\u62a5\uff1f\u201d \u3000\u3000\u88f4\u73cf\u832b\u7136\u671b\u4e86\u671b\u201c\u8fc7\u4e0d\u53bb\u201d\uff0c\u53e3\u4e2d\u54fa\u54fa\u9053\uff1a\u201c\u8fc7\u4e0d\u53bb\uff0c\u8fc7\u4e0d\u53bb\u2026\u2026\u4e16\u4e0a\u6bd5\u7adf\u6709\u4e9b\u4e8b\u662f\u8fc7 \u4e0d\u53bb\u7684\uff0c\u6a80\u660e\u5440\u6a80\u660e\uff0c\u4f60\u6bd5\u7adf\u662f\u9519\u4e86\uff0c\u9519\u4e86\uff01\u201d \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5bf9\u671b\u4e00\u773c\uff0c\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u82e5\u662f\u6a80\u660e\u77e5\u9053\u4ed6\u6614\u5fc3\u79ef\u8651\u7684\u6076\u8ba1\uff0c\u7792\u8fc7\u4e86\u5929\u4e0b\u4eba \u8033\u76ee\uff0c\u5374\u6bc1\u5728\u8fd9\u5c0f\u5c0f\u4e00\u4e2a\u8f66\u592b\u624b\u4e0a\uff0c\u771f\u4e0d\u77e5\u4ed6\u5fc3\u91cc\u8981\u6709\u4ec0\u4e48\u611f\u89c9\uff1f\u201d \u3000\u3000\u98ce\u66f4\u6025\uff0c\u5439\u6563\u4e86\u5927\u5730\u4e0a\u7684\u6012\u559d\u3001\u60ca\u8bed\uff0c\u4e0e\u53f9\u606f\uff01 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4e00\u5343\u8f7b\u5e26\u9a6c\u50f5\uff0c\u4e00\u624b\u5fae\u637b\u957f\u9aef\uff0c\u4efb\u51ed\u4ed6\u80ef\u4e0b\u7684\u5065\u9a6c\uff0c\u5728\u591c\u8272\u4e4b\u4e2d\u7f13\u6b65 \u800c\u884c\u3002 \u3000\u3000\u8fd9\u96c4\u8e1e\u6b66\u6797\uff0c\u53f1\u54a4\u6c5f\u6e56\u7684\u6b66\u6797\u5927\u8c6a\uff0c\u6b64\u523b\u7709\u5b57\u95f4\u4e00\u7247\u51dd\u91cd\u4e4b\u8272\uff0c\u4f3c\u4e4e\u5728\u5fc3\u4e2d\u51b3\u5b9a\u7740\u4ec0\u4e48 \u91cd\u5927\u7684\u51b3\u7b56\u3002 \u3000\u3000\u7f57\u4e49\u3001\u8fb9\u5c11\u884d\uff0c\u4ee5\u53ca\u90a3\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u7f13\u7f13\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u518d\u540e\u9762\u4fbf\u662f\u56db\u6761\u75be\u88c5\u52b2\u670d\u7684 \u6c49\u5b50\uff0c\u88c5\u675f\u6253\u626e\uff0c\u4f3c\u4e4e\u662f\u9556\u5c40\u4e2d\u7684\u8d9f\u5b50\u624b\u6a21\u5b66\u3002 \u3000\u3000\u516b\u5339\u9a6c\u884c\u8d70\u5728\u9759\u591c\u91cc\uff0c\u65e2\u65e0\u4eba\u58f0\uff0c\u4ea6\u65e0\u9a6c\u5636\uff0c\u53ea\u6709\u94c1\u8e44\u8e0f\u5728\u9053\u8def\u4e0a\uff0c\u53d1\u51fa\u4e00\u8fde\u4e32\u201c\u7684 \u5f97\u201d\u58f0\u54cd\u3002 \u3000\u3000\u6e05\u51b7\u7684\u665a\u98ce\uff0c\u5439\u62c2\u7740\u6a80\u660e\u7684\u80e1\u987b\uff0c\u4ed6\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u79cb\u6b8b\u51ac\u81f3\uff0c\u4e00\u5230\u4e25\u51ac\uff0c\u6b66\u6797 \u4e2d\u7684\u8bb8\u591a\u4eba\uff0c\u5c31\u90fd\u8be5\u89e3\u51b3\u4e86\uff01\u89e3\u51b3\u4e86\u8fd9\u4e9b\u4e8b\u540e\uff0c\u6211\u60f3\u6211\u4e5f\u8be5\u597d\u597d\u5730\u4f11\u606f\u4e00\u9635\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c \u5509\u2014\u2014\u201d\u4ed6\u5fae\u559f\u4e00\u58f0\uff0c\u987f\u4f4f\u8bdd\u5934\uff0c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u4e00\u62cd\u9a6c\u80a1\u8d76\u5230\u4ed6\u8eab\u4fa7\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u603b\u9556\u5934\u867d\u7136\u7d2f\u4e86\uff0c\u4f46\u7cbe\u795e\u5374\u4f3c\u8d8a\u53d1\u77cd\u70c1\uff0c\u5904\u7406\u4e8b\u60c5\uff0c\u4ed6\u8d8a\u53d1\u6559\u4eba\u4f69\u670d\u2014\u2014 \u201d\u4ed6\u5fae\u4e00\u6c89\u541f\uff0c\u4f3c\u4e4e\u5728\u8003\u8651\u81ea\u5df1\u8be5\u5982\u4f55\u8bf4\u8bdd\u624d\u80fd\u5f97\u5230\u4ed6\u4e3b\u5b50\u7684\u6b22\u5fc3\uff0c\u7ec8\u4e8e\u53c8\u81ea\u5fae\u7b11\u4e00\u4e0b\uff0c\u8f7b \u8f7b\u9053\uff1a\u201c\u5c31\u62ff\u65b9\u624d\u7684\u4e8b\u8bf4\u5427\uff0c\u6211\u5b9e\u5728\u5c31\u4ece\u5fc3\u91cc\u4f69\u670d\uff0c\u4e09\u8a00\u4e24\u8bed\uff0c\u5c31\u5c06\u6c5f\u5927\u77f3\u8bf4\u5f97\u670d\u670d\u8d34\u8d34\uff0c \u60c5\u613f\u53bb\u8d74\u6c64\u8e48\u706b\uff0c\u4e0d\u8fc7\u2014\u2014\u603b\u9556\u5934\u5373\u4f7f\u4e0d\u8bf4\u90a3\u4e9b\u8bdd\uff0c\u6211\u4eec\u8fd9\u73ed\u4eba\u4e5f\u90fd\u7518\u5fc3\u4e3a\u603b\u9556\u5934\u5356\u547d \u7684\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u637b\u987b\u4e00\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u505a\u5f85\u4eba\u5904\u4e8b\u7684\u65b9\u6cd5\uff0c\u8d24\u5f1f\uff0c\u6709\u671d\u4e00\u65e5\u6211\u82e5\u9000 \u4f11\u5f52\u9690\uff0c\u4f60\u5c31\u8be5\u5b66\u7740\u6211\u7684\u65b9\u6cd5\u53bb\u505a\u4eba\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u76ee\u5149\u4e00\u4eae\uff0c\u4f46\u9762\u8272\u5374\u4f5c\u51fa\u5341\u5206\u60ca\u8bb6\u6c89\u91cd\u4e4b\u6001\uff0c\u9053\uff1a\u201c\u603b\u9556\u5934\u65e0\u8bba\u8eab\u4f53\u3001 \u6b66\u529f\u3001\u5fc3\u667a\uff0c\u90fd\u6b63\u5728\u5dc5\u5cf0\u72b6\u51b5\u4e4b\u4e2d\uff0c\u600e\u5730\u5c31\u8bf4\u51fa\u4e86\u9000\u9690\u7684\u8bdd\u6765\uff0c\u603b\u9556\u5934\u82e5\u771f\u7684\u9000\u9690\u4e86\uff0c\u8fd9\u4e48 \u5927\u4e00\u4efd\u4e8b\u4e1a\u6709\u8c01\u62c5\u5f53\u5f97\u8d77\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u7b11\u5bb9\u66f4\u662f\u5f00\u6717\uff0c\u542b\u7b11\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u5c81\u6708\u54ea\u80af\u9976\u4eba\uff0c\u867d\u662f\u7edd\u4e16\u82f1\u96c4\uff0c \u4e5f\u5f53\u4e0d\u5f97\u5c81\u6708\u7684\u6d88\u78e8\uff0c\u5509\uff0c\u6211\u53ea\u671b\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u8eab\u540e\u7684\u9053\u8def\u4e0a\uff0c\u7a81\u5730\u54cd\u8d77\u4e00\u9635\u6025\u5267\u7684\u9a6c\u8e44\u58f0\uff0c\u4e00\u5339\u5065\u9a6c\uff0c\u6025\u9a70\u800c\u6765\uff0c\u6a80\u660e\u9762 \u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u516b\u5339\u5065\u9a6c\uff0c\u4e00\u8d77\u52d2\u4f4f\u9a6c\u7ec4\u3002\u6025\u9a70\u800c\u6765\u7684\u9a91\u58eb\uff0c\u7ffb\u8eab\u63a0\u4e0b\u9a6c\u6765\uff0c\u53cc\u624b\u4e00\u5782\uff0c\u8eac \u8eab\u9053\uff1a\u201c\u6218\u795e\u624b\u5df2\u5230\u4e2d\u539f\uff0c\u65b9\u624d\u5df2\u5728\u4f0f\u725b\u5c71\u73b0\u8eab\uff0c\u4f46\u770b\u6765\u53ea\u6709\u4ed6\u5b64\u8eab\u4e00\u4eba\uff0c\u4e0d\u77e5\u8981\u4e0e\u88f4\u5927\u5148 \u751f\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5c0f\u7684\u4e0d\u6562\u505c\u7559\uff0c\u5148\u6765\u901a\u62a5\u603b\u9556\u5934\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6d53\u7709\u4e00\u76b1\uff0c\u6c89\u541f\u534a\u664c\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u597d\u597d\uff0c\u679c\u7136\u6765\u4e86\uff0c\u6b64\u4eba\u65e2\u6765\u4e2d \u539f\uff0c\u60f3\u5fc5\u65e9\u6709\u5b89\u6392\uff0c\u7edd\u4e0d\u4f1a\u53ea\u6709\u5b64\u8eab\u4e00\u4eba\uff0c\u6c5f\u8d24\u5f1f\u82e5\u8981\u52a8\u624b\uff0c\u53ea\u6015\u5f88\u96be\u4e86\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9762\u8272\u4ea6\u81ea\u5fae\u5fae\u4e00\u53d8\uff0c\u966a\u7b11\u9053\uff1a\u201c\u4ed6\u5c45\u7136\u6562\u79bb\u5f00\u81ea\u5df1\u7684\u5730\u76d8\uff0c\u6765\u5230\u4e2d\u539f\uff0c \u5927\u6982\u5df2\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u8fd9\u6b63\u662f\u603b\u9556\u5934\u7684\u6d2a\u798f\uff0c\u5c0f\u5f1f\u5b9e\u5728\u5e94\u5f53\u5411\u603b\u9556\u5934\u9053\u559c\u7684\u4e86\uff0c\u81f3\u4e8e\u90a3\u6c5f \u8001\u5f1f\uff0c\u4e0d\u4f46\u4e00\u8eab\u6b66\u529f\uff0c\u800c\u4e14\u673a\u667a\u767e\u51fa\uff0c\u6211\u60f3\u4e8b\u60c5\u4e5f\u4e0d\u4f1a\u56f0\u96be\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5fae\u559f\u9053\uff1a\u201c\u77e5\u5df1\u77e5\u5f7c\uff0c\u624d\u80fd\u767e\u6218\u767e\u80dc\uff0c\u4f60\u4eec\u8fd9\u4e9b\u4eba\uff0c\u592a\u8fc7\u4f4e\u4f30\u4e86\u2018\u795e \u624b\u2019\u6218\u98de\uff0c\u5176\u5b9e\u6b64\u4eba\u4e5f\u7b97\u5f97\u4e0a\u662f\u4e2a\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u966a\u7b11\u9053\uff1a\u201c\u6218\u795e\u624b\u867d\u7136\u4e5f\u6709\u4e9b\u795e\u901a\uff0c\u4f46\u600e\u4e48\u80fd\u4e0e\u603b\u9556\u5934\u76f8\u6bd4\uff1f\u603b\u9556\u5934\u96c4 \u8e1e\u5929\u4e0b\u6570\u5341\u5e74\uff0c\u96be\u9053\u8fd8\u4f1a\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fae\u7b11\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u4e5f\u603b\u8be5\u5c0f\u5fc3\u4e3a\u662f\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u987f\u5904\uff0c\u76ee\u4e2d\u6740\u673a\u5927 \u9732\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u6218\u98de\u5440\u6218\u98de\uff0c\u4f60\u867d\u7136\u51fa\u4eba\u610f\u6599\uff0c\u4f46\u8001\u592b\u5374\u65e9\u5df2\u5728\u90a3\u8fb9\u4f0f\u4e0b\u773c\u7ebf\uff0c\u4f60\u7684\u4e00\u4e3e \u4e00\u52a8\uff0c\u53c8\u4f55\u5c1d\u80fd\u9003\u5f97\u8fc7\u8001\u592b\u7684\u8033\u76ee\uff1f\u6b64\u756a\u4f60\u65e2\u5df2\u5230\u4e86\u4e2d\u539f\uff0c\u8001\u592b\u82e5\u4e0d\u597d\u597d\u5730\u62db\u5f85\u62db\u5f85\u4f60\uff0c\u4e5f \u5728\u4e3a\u4e2d\u539f\u7684\u5730\u4e3b\u4e86\uff01\u201d \u3000\u3000\u7b11\u58f0\u4e4b\u4e2d\uff0c\u6ee1\u542b\u5f97\u610f\u4e4b\u60c5\u3002\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u4ea6\u81ea\u5927\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u662f\u8c01\uff0c\u82e5\u662f\u4e0e\u603b\u9556\u5934\u4e3a \u654c\uff0c\u6b64\u4eba\u5f53\u771f\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u3002\u201d \u3000\u3000\u7b11\u58f0\u672a\u843d\uff0c\u9053\u4e0a\u7a81\u5730\u53c8\u6709\u4e00\u5339\u5065\u9a6c\u72c2\u5954\u800c\u6765\uff0c\u9a6c\u4e0a\u4eba\u53d1\u820d\u84ec\u4e71\uff0c\u201c\u795e\u8272\u5f20\u60f6\uff0c\u5982\u6b64\u5bd2 \u591c\uff0c\u4ed6\u4ecd\u662f\u6ee1\u5934\u5927\u6c57\u6dcb\u6f13\u3002\u4ed6\u672a\u7b49\u52d2\u4f4f\u7f30\u7ef3\uff0c\u4fbf\u7ffb\u8eab\u63a0\u4e0b\u9a6c\u6765\u3002\u201d\u9f99\u5f62\u516b\u638c\u201c\u6a80\u660e\u500f\u7136\u987f\u4f4f \u7b11\u58f0\uff0c\u6d53\u7709\u76b1\u5904\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e8e\u4e03\uff0c\u5982\u6b64\u5f20\u60f6\u4f5c\u4ec0\uff1f\u64e6\u5e72\u5934\u4e0a\u7684\u6c57\uff0c\u518d\u4e0e\u6211\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u7b56\u9a6c\u72c2\u5954\u800c\u6765\u7684\u201c\u4e8e\u4e03\u201d\u679c\u7136\u4f38\u624b\u4e00\u62b9\u989d\u4e0a\u7684\u6c57\u73e0\uff0c\u4f46\u6c57\u6e0d\u672a\u5e72\uff0c\u4ed6\u4fbf\u5df2\u60f6\u7136\u8eac\u8eab\u8bf4 \u9053\uff1a\u201c\u7980\u544a\u603b\u9556\u5934\uff0c\u4f0f\u725b\u5c71\u90a3\u8fb9\uff0c\u5df2\u751f\u53d8\u6545\uff0c\u5c0f\u4eba\u5feb\u9a6c\u8d76\u6765\uff0c\u6b64\u523b\u90a3\u8fb9\u60c5\u52bf\uff0c\u8fd8\u4e0d\u77e5\u9053\u600e\u4e48 \u6837\u4e86\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6c89\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u53d8\u6545\uff1f\u201d \u3000\u3000\u201c\u4e8e\u4e03\u201d\u5598\u606f\u672a\u5b9a\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6218\u795e\u624b\u4e00\u81f3\uff0c\u72c2\u8a00\u8bcb\u6bc1\u4e86\u603b\u9556\u5934\u4e24\u53e5\uff0c\u5c71\u4e0b\u7684\u4eba\u7adf\u4e00\u8d77 \u4e3a\u88f4\u5927\u5148\u751f\u559d\u8d77\u91c7\u6765\uff0c\u603b\u9556\u5934\u4f0f\u5728\u90a3\u8fb9\u7684\u5144\u5f1f\uff0c\u5fc3\u4e2d\u4e0d\u6124\uff0c\u672a\u5c1d\u6b22\u547c\uff0c\u7adf\u88ab\u2018\u795e\u624b\u2019\u6218\u98de\u57cb \u4f0f\u5728\u90a3\u91cc\u7684\u4eba\u62d6\u4e86\u51fa\u53bb\uff0c\u6c5f\u9556\u5934\u773c\u770b\u60c5\u52bf\u4e0d\u5bf9\uff0c\u65b9\u5f85\u8131\u8eab\uff0c\u54ea\u77e5\u7adf\u88ab\u6218\u98de\u624b\u4e0b\u7684\u4e24\u6761\u6c49\u5b50\u64d2 \u83b7\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u4e00\u626c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6218\u98de\u624b\u4e0b\uff0c\u5c45\u7136\u6709\u4eba\u80fd\u5c06\u6c5f\u5927\u77f3\u64d2\u83b7\u3001\u8fd9\u5012\u602a \u4e86\u3002\u201d \u3000\u3000\u7f57\u4e49\u3001\u8fb9\u5c11\u884d\u5bf9\u671b\u4e00\u773c\uff0c\u9762\u4e0a\u4ea6\u6709\u60ca\u5947\u4e4b\u8272\uff0c\u8981\u77e5\u201c\u6bd2\u624b\u59dc\u7ef4\u201d\u6c5f\u5927\u77f3\u672c\u662f\u9556\u5c40\u7684\u4e00\u6d41 \u9ad8\u624b\uff0c\u662f\u4ee5\u6a80\u660e\u624d\u4f1a\u5c06\u90a3\u4e8b\u4ea4\u6258\u4e8e\u4ed6\u3002 \u3000\u3000\u201c\u4e8e\u4e03\u201d\u5598\u4e86\u53e3\u6c14\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5c0f\u7684\u770b\u60c5\u52bf\u4e0d\u5999\uff0c\u5c31\u62fc\u547d\u6b22\u547c\u8d77\u6765\uff0c\u7b49\u5230\u4ed6\u4eec\u4e00\u6d8c\u51fa \u53bb\uff0c\u5c0f\u7684\u5c31\u4ea1\u547d\u98de\u5954\u800c\u6765\uff0c\u6b64\u523b\u2014\u2014\u201d\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u51b7\u51b7\u63a5\u53e3\u9053\uff1a\u201c\u9664\u4e86\u6c5f\u5927\u77f3\u4e4b\u5916\uff0c \u8fd8\u6709\u591a\u5c11\u4eba\u88ab\u4ed6\u4eec\u53d1\u89c9\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e8e\u4e03\u201d\u5fae\u4e00\u6c89\u541f\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u603b\u6709\u5341\u4e94\u4eba\u4e0a\u4e0b\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u672a\u4e86\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7a81\u53c8\u4ef0\u5929\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u7b11\u58f0\u6e05\u6717\u9ad8\u4ea2\uff0c\u4ecd\u7136\u662f\u5145\u6ee1\u4e86\u5f97\u610f\u4e4b\u60c5\u3002 \u3000\u3000\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u4f46\u90fd\u6123\u4f4f\uff0c\u53ea\u542c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u72c2\u7b11\u9053\uff1a\u201c\u6218\u98de\u5440\u6218\u98de\uff0c\u4f60\u679c\u7136\u662f\u4e2a \u4eba\u7269\uff0c\u4e0d\u6127\u80fd\u505a\u4e3a\u8001\u592b\u7684\u654c\u624b\uff0c\u4f46\u662f\u4f60\u9009\u8001\u592b\u4e3a\u654c\u624b\uff0c\u5374\u662f\u5927\u5927\u5730\u9519\u4e86\uff0c\u4f60\u5c06\u8981\u6c38\u751f\u540e\u6094\u8fd9 \u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u72c2\u7b11\u4e0d\u7edd\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u67f3\u8d24\u5f1f\uff0c\u4f60\u53ef\u77e5\u8001\u592b\u5728\u90a3\u8fb9\u5e03\u4e0b\u4e86\u591a\u5c11\u773c\u7ebf\uff0c\u4ed6\u7eb5\u7136\u518d\u53d1\u73b0 \u5341\u4e94\u4eba\uff0c\u4ed6\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u5374\u4ecd\u7136\u9003\u4e0d\u8fc7\u8001\u592b\u7684\u8033\u76ee\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u966a\u7b11\u9053\uff1a\u201c\u603b\u9556\u5934\u4eba\u4e2d\u4e4b\u6770\uff0c\u4eba\u4e0d\u80fd\u53ca\uff0c\u90a3\u59d3\u6218\u7684\u7b97\u5f97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u77f3\u806a\u660e\u4e00\u4e16\uff0c\u7cca\u6d82\u4e00\u65f6\uff0c\u5176\u5b9e\u4ed6\u6839\u672c\u6bcb\u5eb8\u9003\u7684\uff0c\u53ea \u8981\u4f2a\u88c5\u4e00\u4e0b\uff0c\u53c8\u6709\u8c01\u80fd\u53d1\u73b0\u4ed6\uff1f\u53ea\u662f\u2014\u2014\u54c8\u54c8\uff0c\u4ed6\u7eb5\u7136\u88ab\u6349\uff0c\u5374\u4ecd\u4e8e\u6211\u5927\u8ba1\u65e0\u788d\uff0c\u4ee5\u88f4\u73cf\u5bf9 \u6211\u7684\u611f\u6fc0\uff0c\u4ed6\u600e\u4f1a\u4e0e\u6211\u4e3a\u654c\uff1f\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9053\uff1a\u201c\u6b63\u662f\uff0c\u90a3\u88f4\u73cf\u5c0a\u656c\u603b\u9556\u5934\u6709\u5982\u7236\u5144\uff0c\u600e\u4f1a\u5bf9\u603b\u9556\u5934\u65e0\u793c\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u7b11\u6218\u98de\u8f9b\u8f9b\u82e6\u82e6\u5730\u9020\u6210\u4e86\u88f4\u73cf\u58f0\u540d\u5730\u4f4d\uff0c\u5374\u6ca1\u6709\u5f04\u6e05\u88f4 \u73cf\u4e0e\u6211\u7684\u5173\u7cfb\uff0c\u8fd9\u4e00\u6765\uff0c\u4ed6\u53cd\u5012\u6210\u4e86\u4f5c\u8327\u81ea\u7f1a\uff0c\u4f5c\u6cd5\u81ea\u6bd9\uff0c\u6211\u603b\u6709\u4e00\u65e5\u4f7f\u5f97\u88f4\u73cf\u5bf9\u4ed6\u5012\u6208\u76f8 \u5411\uff0c\u90a3\u65f6\u4ed6\u8f9b\u8f9b\u82e6\u82e6\u9020\u6210\u7684\u2018\u6c5f\u5357\u540c\u76df\u2019\uff0c\u5c31\u7b49\u4e8e\u662f\u4e3a\u6211\u591a\u6dfb\u4e86\u51e0\u5206\u52bf\u529b\uff0c\u54c8\u54c8\u2014\u2014\u201d\u4ed6\u8f6c \u9996\u5411\u67f3\u8f89\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u4e00\u76f4\u8fdf\u8fdf\u672a\u66fe\u5bf9\u2018\u6c5f\u5357\u540c\u76df\u2019\u51fa\u624b\uff0c\u4fbf\u662f\u4e3a\u4e86\u8fd9\u4e2a\u7f18\u6545\uff0c\u4f46\u6211\u7684\u5fc3 \u8ba1\uff0c\u6c5f\u6e56\u4e2d\u53c8\u6709\u8c01\u80fd\u731c\u5230\uff1f\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u6ee1\u9762\u4f5c\u51fa\u94a6\u4f69\u4e4b\u6001\uff0c\u53f9\u9053\uff1a\u201c\u603b\u9556\u5934\u5999\u7b97\uff0c\u5c82\u662f\u4eba\u4eec\u80fd\u6d4b\uff1f\u88f4\u73cf\u8fd1\u6765\u6b66 \u529f\u5927\u8fdb\uff0c\u8bf4\u4e0d\u5b9a\u65e5\u540e\u53c8\u662f\u603b\u9556\u5934\u7684\u4e00\u6761\u5f97\u529b\u81c2\u8180\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u9894\u9996\u9053\uff1a\u201c\u53ea\u8981\u6211\u7a0d\u4f7f\u624b\u8155\uff0c\u4f55\u60a3\u4ed6\u4e0d\u6b7b\u5fc3\u584c\u5730\u5730\u8ddf\u7740\u6211\u3002\u201d\u4ed6\u7b11\u58f0 \u65b9\u987f\uff0c\u6b64\u523b\u9762\u4e0a\u5374\u4e0d\u7981\u6cdb\u8d77\u4e00\u9635\u6df1\u6c89\u800c\u5f97\u610f\u7684\u5fae\u7b11\u3002 \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u5fcd\u4e0d\u4f4f\u53f9\u9053\uff1a\u201c\u4f46\u662f\uff0c\u6211\u59cb\u7ec8\u65e0\u6cd5\u76f8\u4fe1\uff0c\u77ed\u77ed\u4e00\u4e24\u5e74\u4e4b\u5185\uff0c\u4ed6\u4e0d\u4f46\u7ec3\u6210 \u8fd9\u822c\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u800c\u4e14\u8a00\u8bed\u3001\u795e\u6001\uff0c\u4e5f\u50cf\u662f\u53e6\u5916\u53d8\u4e86\u4e00\u4e2a\u4eba\u4f3c\u7684\uff0c\u8fd9\u79cd\u4e8b\u6211\u5b9e\u5728\u95f4\u6240\u672a\u95fb\uff0c \u82e5\u975e\u662f\u6211\u4eb2\u773c\u6240\u7779\uff0c\u6211\u624d\u4e0d\u4f1a\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7b11\u5bb9\u4e00\u655b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5b9e\u5728\u662f\u4e2a\u806a\u660e\u7edd\u9876\u7684\u4eba\u7269\uff0c\u6211\u65e9\u5c31\u770b\u51fa \u4e86\uff0c\u662f\u4ee5\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u56db\u671b\u4e00\u773c\uff0c\u7b56\u9a6c\u8d70\u4e86\u51e0\u6b65\uff0c\u65b9\u81ea\u653e\u4f4e\u58f0\u97f3\uff0c\u5bf9\u968f\u540e\u8ddf\u6765\u7684\u201c\u516b \u5366\u638c\u201d\u67f3\u8f89\u4f4e\u8bed\u7740\u9053\uff1a\u201c\u6211\u4ece\u5c0f\u5c31\u6298\u78e8\u4ed6\uff0c\u4f24\u5bb3\u4ed6\u7684\u81ea\u5c0a\u5fc3\uff0c\u6253\u51fb\u4ed6\u7684\u81ea\u4fe1\uff0c\u8981\u4f7f\u4ed6\u53d8\u6210\u4e00 \u4e2a\u61e6\u5f31\u65e0\u7528\u7684\u4eba\u3002\u54ea\u77e5\u4ed6\u6bd5\u7adf\u4e0d\u662f\u6c60\u4e2d\u4e4b\u7269\uff0c\u4ed6\u7684\u5fc3\u667a\u3001\u80fd\u529b\uff0c\u867d\u7136\u88ab\u6211\u7d27\u7d27\u5730\u538b\u5236\u4e86\uff0c\u4f46 \u53ea\u8981\u7a0d\u653e\u4e00\u70b9\uff0c\u4fbf\u5168\u90e8\u6fc0\u6d8c\u800c\u51fa\uff0c\u662f\u4ee5\u4ed6\u624d\u80fd\u5728\u77ed\u671f\u5185\u6709\u6b64\u6210\u5c31\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5c31\u6b63\u5982\uff0c\u4ee5\u5824\u963b\u6c34\uff0c\u5824\u9632\u4e00\u51b3\uff0c\u6c34\u52bf\u4fbf\u5c31\u66f4\u5927\uff0c\u8fd9\u6b63\u662f\u5343\u53e4\u4e0d\u79fb \u4e4b\u7406\uff0c\u53ea\u602a\u6211\u672a\u66fe\u60f3\u5230\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u4e4b\u4e2d\uff0c\u5145\u6ee1\u540e\u6094\u611f\u53f9\u4e4b\u610f\u3002 \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u966a\u7b11\u9053\uff1a\u201c\u867d\u7136\u5982\u6b64\uff0c\u4f46\u603b\u9556\u5934\u76f4\u5230\u4eca\u65e5\uff0c\u4ecd\u7136\u6709\u5236\u4ed6\u4e4b\u529b\uff0c\u867d\u7136\u8981\u591a \u8d39\u4e9b\u5fc3\u529b\uff0c\u4f46\u6b64\u4eba\u8fd8\u4e0d\u7167\u6837\u662f\u603b\u9556\u5934\u7684\u56ca\u4e2d\u4e4b\u7269\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u4f38\u624b\u4e00\u62cd\u4ed6\u80a9\u5934\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u67f3\u8d24\u5f1f\uff0c\u4f60\u771f\u662f\u6211\u7684\u5f97\u529b\u81c2\u52a9\uff01\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u6b66\u529f\u65e2\u4e0d\u9ad8\uff0c\u5fc3\u667a\u4ea6\u672a\u89c1\u51fa\u8272\uff0c\u4f46\u5728\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\uff0c\u5374\u662f\u201d\u9f99\u5f62\u516b \u638c\u201d\u6700\u4eb2\u4fe1\u7684\u4eba\u7269\u3002 \u3000\u3000\u662f\u4ee5\u4f17\u4eba\u4ff1\u90fd\u5947\u602a\uff0c\u4e00\u5411\u7cbe\u660e\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\u600e\u4e48\u4e5f\u4f1a\u770b\u8d70\u4e86\u773c\uff0c\u5c06\u5eb8\u624d\u5f15\u4e3a\u4eb2\u4fe1\uff1f\u4f46 \u4ed6\u5374\u4e0d\u77e5\u9053\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u867d\u7136\u6837\u6837\u65e0\u80fd\uff0c\u4f46\u5949\u627f\u62cd\u9a6c\u4e4b\u529f\uff0c\u5374\u662f\u8d85\u4eba\u4e00\u7b49\uff0c\u800c\u666e\u5929\u4e4b\u4e0b\uff0c \u82b8\u82b8\u4f17\u751f\uff0c\u65e0\u8bba\u4e0a\u667a\u4e0b\u611a\uff0c\u5bf9\u8fd9\u5949\u627f\u62cd\u9a6c\u4e4b\u529f\uff0c\u90fd\u662f\u6700\u53d7\u7528\u4e0d\u8fc7\u7684\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7b11\u58f0\u4e00\u987f\uff0c\u970d\u7136\u8f6c\u56de\u9a6c\u5934\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6218\u795e\u624b\u5982\u6b64\u505a\u6cd5\uff0c\u5fc5\u6709\u6240\u4e3a\uff0c \u4e8e\u4e03\u4f60\u5feb\u9a6c\u8d76\u5230\u5357\u9633\uff0c\u627e\u5230\u90a3\u91cc\u7684\u805a\u8d24\u5ba2\u6808\uff0c\u5bfb\u5f97\u5f90\u660e\u3001\u5411\u98de\u65d7\u3001\u516c\u5b59\u5927\u8def\u4e09\u4f4d\u9556\u5934\uff0c\u53eb\u4ed6 \u4eec\u5728\u7834\u6653\u4ee5\u524d\uff0c\u8d76\u5230\u4f0f\u725b\u5c71\u53bb\uff0c\u5c31\u8bf4\u6211\u6709\u6025\u4e8b\u9700\u8981\u52a9\u624b\u3002\u201d \u3000\u3000\u201c\u4e8e\u4e03\u201d\u6c57\u6e0d\u65b9\u5e72\uff0c\u6b64\u523b\u606d\u8bfa\u4e00\u58f0\uff0c\u53c8\u590d\u98de\u8eab\u4e0a\u9a6c\uff0c\u72c2\u5954\u800c\u53bb\u3002\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6c89\u58f0 \u53c8\u9053\uff1a\u201c\u4f60\u6211\u7acb\u523b\u8d76\u5230\u90a3\u8fb9\u53bb\uff0c\u8001\u592b\u5012\u8981\u770b\u770b\uff0c\u90a3\u2018\u795e\u624b\u2019\u6218\u98de\u7a76\u7adf\u80fd\u5f04\u51fa\u4ec0\u4e48\u4e86\u4e0d\u5f97\u7684\u82b1 \u6837\u3002\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u4ed6\u4e00\u63d0\u7586\u7ef3\uff0c\u5f53\u5148\u5411\u524d\u9a70\u53bb\u3002 \u3000\u3000\u7f57\u4e49\u53cc\u7709\u5fae\u76b1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6218\u795e\u624b\u5fc5\u5b9a\u6709\u5907\u800c\u6765\uff0c\u5374\u4e0d\u77e5\u4f3c\u624b\u4e0b\u53c8\u6dfb\u4e86\u4ec0\u4e48\u9ad8\u624b\uff1f\u201d \u3000\u3000\u8fb9\u5c11\u884d\u6c89\u541f\u534a\u664c\uff0c\u671b\u7740\u201c\u9f99\u5f62\u516b\u638c\u201d\u7684\u80cc\u5f71\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u53ea\u8981\u603b\u9556\u5934\u4eb2\u81ea\u51fa\u624b\uff0c\u4eca\u65e5\u5929 \u4e0b\u6b66\u6797\u4e4b\u4e2d\uff0c\u53ea\u6015\u8fd8\u6ca1\u6709\u4efb\u4f55\u4e00\u4eba\u80fd\u5728\u4ed6\u5b50\u4e0b\u8d70\u8fc7\u4e94\u5341\u62db\u6cd5\u3002\u201d \u3000\u3000\u7f57\u4e49\u76b1\u7709\u9053\uff1a\u201c\u53ea\u6015\u2014\u2014\u201d\u8fb9\u5c11\u884d\u5fae\u5fae\u4e00\u7b11\uff0c\u622a\u53e3\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8bdd\u7edd\u65e0\u5dee\u9519\uff0c\u6709\u4e00\u65e5\u6211 \u4eb2\u773c\u770b\u5230\u603b\u9556\u5934\u5728\u51cc\u6668\u7ec3\u529f\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u5408\u6211\u4e24\u4eba\u4e4b\u529b\uff0c\u4e5f\u672a\u89c1\u80fd\u6321\u5f97\u4f4f\u4ed6\u7684\u4e09\u5341\u62db\u53bb\u3002\u201d \u3000\u3000\u7f57\u4e49\u9762\u8272\u5fae\u53d8\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u7b56\u9a6c\u800c\u53bb\uff0c\u5bf9\u8fb9\u5c11\u884d\u7684\u8a00\u8bed\uff0c\u867d\u7136\u4e0d\u80fd\u4e0d\u4fe1\uff0c\u5374\u53c8\u4e0d\u80fd\u5c3d \u4fe1\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5728\u9a6c\u4e0a\u7aef\u5750\u5982\u5c71\uff0c\u8c01\u4e5f\u65e0\u6cd5\u770b\u51fa\u8fd9\u8001\u4eba\u5df2\u5728\u9a6c\u4e0a\u5954\u9a70\u4e86\u4e00\u53e3\u4e00\u591c\uff0c\u672a \u66fe\u4f11\u606f\u3002 \u3000\u3000\u4ed6\u795e\u6001\u4ecd\u662f\u90a3\u822c\u9547\u5b9a\u800c\u4ece\u5bb9\uff0c\u9a6c\u884c\u534a\u523b\uff0c\u591c\u8272\u4e2d\uff0c\u524d\u9762\u7a81\u53c8\u5c18\u5934\u5927\u8d77\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u51b7 \u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff0c\u2018\u795e\u624b\u2019\u6218\u98de\u679c\u7136\u53c8\u51fa\u4e86\u82b1\u6837\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e4b\u4e2d\uff0c\u5145\u6ee1\u81ea\u4fe1\u81ea\u50b2\uff0c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u542b\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u82b1\u6837\uff0c\u53ea\u6015\u4e5f\u65e0\u6cd5\u5728 \u603b\u9556\u5934\u624b\u4e0b\u65bd\u5c55\u5427\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4fa7\u76ee\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5df2\u5bf9\u4f60\u8bf4\u8fc7\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u4e07\u65e0\u771f\u6b63\u8fc7\u4e0d\u53bb\u7684\uff0c \u8bb0\u5f97\u82e5\u5e72\u5e74\u524d\uff0c\u6211\u66fe\u7ecf\u89c1\u8fc7\u4e00\u4e2a\u9a6c\u5e73\u8f66\u592b\uff0c\u4ed6\u7684\u540d\u5b57\u7adf\u53eb\u505a\u2018\u8fc7\u4e0d\u53bb\u2019\u2026\u2026\u2026\u201d \u3000\u3000\u4ed6\u8a00\u8bed\u4e4b\u95f4\uff0c\u524d\u9762\u5df2\u9a70\u6765\u4e00\u5339\u5e62\u9a6c\uff0c\u9a6c\u4e0a\u4eba\u542c\u5230\u201c\u9f99\u5f62\u516b\u638c\u201d\u968f\u98ce\u98d8\u6765\u7684\u8bed\u58f0\uff0c\u9762\u8272\u7a81 \u5730\u5927\u53d8\uff0c\u4e00\u63a0\u4e0b\u9a6c\uff0c\u8131\u53e3\u9053\uff1a\u201c\u603b\u9556\u5934\u83ab\u975e\u5df2\u77e5\u9053\u201d\u9f99\u5f62\u516b\u638c\u201c\u9762\u8272\u4e00\u6c89\uff0c\u53f1\u9053\uff1a\u201c\u77e5\u9053\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u9a6c\u4e0a\u63a0\u4e0b\u7684\u9a91\u58eb\uff0c\u4e0d\u4f46\u795e\u8272\u5f20\u60f6\uff0c\u66f4\u80dc\u524d\u8005\uff0c\u5927\u6c57\u6dcb\u6f13\uff0c\u66f4\u5df2\u6e7f\u900f\u5168\u8863\uff0c\u95fb\u8a00\u4e00\u6014\uff0c\u5450 \u5450\u9053\uff1a\u201c\u8fc7\u4e0d\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u76b1\u7709\u53f1\u9053\uff1a\u201c\u4ec0\u4e48\u8fc7\u4e0d\u53bb\uff01\u738b\u70c8\uff0c\u4f60\u600e\u5730\u8d8a\u6765\u8d8a\u50cf\u6d3b\u56de\u53bb\u4f3c\u7684\uff0c\u8fde\u8bdd \u90fd\u8bf4\u4e0d\u6e05\u4e86\uff01\u201d \u3000\u3000\u795e\u8272\u5f20\u60f6\u3001\u5927\u6c57\u6dcb\u6f13\u7684\u201c\u738b\u70c8\u201d\u8bda\u60f6\u8bda\u6050\uff0c\u4e0d\u6562\u4ef0\u9996\uff0c\u5c06\u4f0f\u725b\u5c71\u9e93\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u5168\u90fd\u8bf4 \u4e86\u51fa\u6765\u3002 \u3000\u3000\u8bf4\u5230\u201c\u6bd2\u624b\u59dc\u7ef4\u201d\u8bc8\u505a\u8d2a\u751f\u4e5e\u547d\uff0c\u638c\u51fb\u88f4\u73cf\u5012\u5730\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u626c\u5904\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u6211\u65e9\u77e5\u6c5f\u8d24\u5f1f\u7edd\u975e\u90a3\u4e48\u7cca\u6d82\uff0c\u539f\u6765\u4ed6\u6b64\u4e3e\u679c\u6709\u6df1\u610f\uff0c\u6b64\u4eba\u5012\u662f\u4e2a\u4e0d\u53ef\u591a\u5f97\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9762\u4e0a\u867d\u6697\u7b11\u79f0\u662f\uff0c\u5fc3\u91cc\u5374\u6697\u6697\u5fcc\u5ac9\uff0c\u4f46\u738b\u70c8\u7acb\u523b\u4fbf\u53c8\u8bf4\u5230\u88f4\u73cf\u4e00\u8dc3\u800c \u8d77\uff0c\u6c5f\u5927\u77f3\u4ef0\u836f\u81ea\u6740\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u7acb\u53d8\uff0c\u8fde\u8fde\u957f\u53f9\uff0c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9762\u4e0a\u81ea\u4e5f\u505a\u4e86\u60cb\u60dc\u4e4b\u621a\u5bb9\uff0c\u5fc3\u91cc\u5374 \u4e0d\u7981\u6697\u6697\u6b22\u559c\u3002 \u3000\u3000\u7b49\u5230\u738b\u70c8\u8bf4\u5230\u6709\u4eba\u53db\u53d8\uff0c\u5410\u51fa\uff1a\u201c\u6bd2\u624b\u59dc\u7ef4\u201d\u7684\u5e95\u7ec6\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u7acb\u523b\u52c3\u7136\u5927\u6012\uff0c\u5389 \u53f1\u9053\uff1a\u201c\u6b64\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u53ef\u66fe\u88ab\u6218\u98de\u6740\u6b7b\uff1f\u201d \u3000\u3000\u738b\u70c8\u6447\u5934\u9053\uff1a\u201c\u4e0d\u66fe\uff0c\u4f46\u5c0f\u7684\u4e0e\u8d75\u5947\u3001\u5f20\u80dc\u4e00\u8d77\u6e9c\u51fa\uff0c\u4ed6\u4e24\u4eba\u7acb\u523b\u5df2\u6697\u5730\u8ffd\u8e2a\u4e86\u53bb\uff0c\u5c0f \u7684\u4e00\u4eba\u524d\u6765\u7980\u62a5\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u51b7\u7b11\u6570\u58f0\uff0c\u9053\uff1a\u201c\u6218\u98de\u660e\u77e5\u6211\u4e0d\u4f1a\u653e\u8fc7\u4ed6\uff0c\u81ea\u7136\u4e50\u5f97\u6545\u4f5c\u5927\u65b9\uff0c\u67f3\u8d24\u5f1f\uff0c \u8fd9\u2018\u795e\u624b\u2019\u6218\u98de\u82e5\u4e0d\u9664\u53bb\uff0c\u4f60\u6211\u5c06\u6c38\u65e0\u5b81\u65e5\u4e86\u3002\u201d \u3000\u3000\u4ed6\u9762\u8272\u867d\u53d8\uff0c\u4f46\u795e\u6001\u5374\u4ecd\u672a\u614c\u5f20\uff0c\u76f4\u5230\u738b\u70c8\u8bf4\u51fa\u4e86\u201c\u8fc7\u4e0d\u53bb\u201d\uff0c\u8bf4\u51fa\u4e86\u90a3\u4ef6\u6b66\u6797\u79d8\u95fb\uff0c \u8fd9\u79f0\u96c4\u4e00\u4e16\u7684\u6b66\u6797\u5927\u8c6a\u795e\u8272\u624d\u771f\u6b63\u614c\u5f20\u8d77\u6765\uff0c\u634b\u7740\u957f\u987b\u7684\u624b\u638c\uff0c\u4f3c\u4e5f\u5fae\u5fae\u8d77\u4e86\u98a4\u6296\uff0c\u9ed8\u7136\u534a \u664c\u65b9\u81ea\u6c89\u58f0\u9053\uff1a\u201c\u53ea\u51ed\u90a3\u4e00\u4e2a\u8f66\u592b\u7684\u8a00\u8bed\uff0c\u96be\u9053\u522b\u7684\u4eba\u5c31\u90fd\u76f8\u4fe1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u738b\u70c8\u4e0d\u6562\u56de\u7b54\uff0c\u53ea\u662f\u8f7b\u8f7b\u9894\u9996\u3002\u7f57\u4e49\u3001\u8fb9\u5c11\u884d\u3001\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u6b64\u523b\u4ea6\u5df2\u795e\u8272\u5927\u53d8\uff0c \u8fd9\u4ef6\u6b66\u6797\u79d8\u95fb\u5176\u4e2d\u771f\u6b63\u7684\u66f2\u6298\u9690\u79d8\uff0c\u5c31\u8fde\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u4e5f\u662f\u76f4\u5230\u4eca\u65e5\u624d\u7b2c\u4e00\u6b21\u542c\u5230\u3002 \u3000\u3000\u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e00\u624b\u634b\u9aef\uff0c\u7aef\u5750\u9a6c\u4e0a\uff0c\u9664\u4e86\u987b\u53d1\u88ab\u98ce\u5439\u8d77\u4e4b\u5916\u3002 \u3000\u3000\u5168\u8eab\u4e00\u65e0\u52a8\u5f39\uff0c\u4eff\u4f5b\u8001\u50e7\u4eba\u5b9a\u4e00\u822c\u3002 \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u55eb\u5685\u7740\u9053\uff1a\u201c\u603b\u9556\u5934\u2026\u2026\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u624b\u638c\u4e00\u6325\uff0c\u622a\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u653e\u9a6c\u7f13\u7f13\u524d\u884c\u4e86\u4e24\u6b65\uff0c\u53c8\u7a81\u5730\u515c\u8f6c\u9a6c\u5934\uff0c\u4e00\u8a00 \u4e0d\u53d1\uff0c\u62cd\u9a6c\u800c\u53bb\u3002 \u3000\u3000\u7f57\u4e49\u3001\u8fb9\u5c11\u884d\u3001\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9f50\u5730\u8ba9\u5f00\u9a6c\u8eab\uff0c\u8ba9\u4ed6\u4eba\u9a6c\u9a70\u8fc7\uff0c\u4e09\u4eba\u5404\u5404\u5bf9\u671b\u4e86\u4e00\u773c\uff0c \u8f6c\u9a6c\u968f\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u4eba\u9a6c\u8d8a\u5954\u8d8a\u6025\uff0c\u82b1\u767d\u7684\u987b\u53d1\uff0c\u968f\u98ce\u98de\u626c\u800c\u8d77\uff0c\u7adf\u9a6c\u4e0d\u505c\u8e44\u5730\u5954\u884c\u4e86\u534a\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u770b\u4e0d\u5230\u4ed6\u9762\u4e0a\u7684\u8868\u60c5\uff0c\u5374\u731c\u5f97\u51fa\u4ed6\u6b64\u523b\u5fc3\u60c5\u7684\u7d0a\u4e71\uff0c\u662f\u4ee5\u5404\u5404\u9762\u8272\u51dd\u91cd\uff0c\u8c01\u4e5f \u4e0d\u6562\u51fa\u58f0\u8bf4\u8bdd\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5954\u52bf\u7a81\u5730\u4e00\u987f\uff0c\u5065\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u4eba\u7acb\u800c\u8d77\uff0c\u540e\u9762\u7684\u4e5d\u5339\u5065\u9a6c\u7acb\u523b\u987f\u4f4f\u5954\u884c\u4e4b\u52bf\u3002 \u3000\u3000\u9a6c\u5636\u8fde\u8fde\uff0c\u8e44\u58f0\u987f\u6b62\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u56de\u8fc7\u5934\u6765\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u67f3\u8d24\u5f1f\uff0c\u4eac\u57ce\u603b\u5c40\u4e4b\u4e2d\uff0c\u8fd8 \u6709\u591a\u5c11\u53ef\u7528\u7684\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u975e\u4f46\u6ca1\u6709\u56e0\u72c2\u5954\u800c\u5598\u606f\uff0c\u795e\u8272\u53cd\u800c\u6062\u590d\u4e86\u9547\u5b9a\uff0c\u8fd9\u96c4\u8e1e\u6c5f\u6e56\u7684\u6b66\u6797\u5927\u8c6a\uff0c\u884c\u4e8b\u786e\u6709\u8fc7 \u4eba\u4e4b\u5904\u3002 \u3000\u3000\u201c\u516b\u5366\u62ff\u2018\u67f3\u8f89\u5fae\u4e00\u6c89\u541f\uff0c\u9063\uff1a\u201c\u7ea6\u7565\u56db\u5341\u4f59\u4eba\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9894\u9996\u9053\uff1a\u201c\u4e09\u6708\u4e4b\u5185\uff0c\u5404\u5730\u9556\u5c40\u7acb\u523b\u53ef\u4ee5\u8c03\u7528\u7684\u82f1\u96c4\u6709\u51e0\u4eba\uff1f\u201d\u201c\u516b\u5366 \u638c\u201d\u67f3\u8f89\u5fc3\u623f\u7830\u7830\u8df3\u52a8\uff0c\u77e5\u9053\u4ed6\u5df2\u51c6\u5907\u5168\u529b\u4e0e\u201c\u795e\u624b\u201d\u6218\u98de\u4e00\u51b3\u96cc\u96c4\uff0c\u7f57\u4e49\u3001\u8fb9\u5c11\u884d\uff0c\u4ea6\u662f \u70ed\u8840\u5954\u817e\u3002 \u3000\u3000\u53ea\u542c\u67f3\u8f89\u6c89\u541f\u9053\uff1a\u201c\u82e5\u4ee5\u98de\u9e3d\u4f20\u4e66\uff0c\u4e09\u65e5\u4e4b\u5185\uff0c\u53ef\u8c03\u52a8\u9556\u5e08\u4e8c\u5341\u4e5d\u4eba\uff0c\u8d9f\u5b50\u624b\u7ea6\u7565\u767e\u4f59 \u4eba\uff0c\u5176\u4f59\u2026\u2026\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6c89\u58f0\u9053\uff1a\u201c\u597d\u4e86\uff01\u5c11\u884d\uff0c\u4f60\u7acb\u523b\u8d76\u5230\u65f6\u65d7\u9547\uff0c\u4ee5\u98de\u9e3d\u4f20\u4e66\uff0c\u4ee4\u5404\u5730\u9556 \u5c40\u53ef\u4ee5\u52a8\u624b\u4e4b\u4eba\uff0c\u7acb\u523b\u542f\u7a0b\u8d76\u8d74\u6c5f\u5357\uff0c\u6709\u9556\u7684\u62bc\u9556\uff0c\u6ca1\u6709\u751f\u610f\u4e5f\u4ee5\u77f3\u5757\u88c5\u8f66\uff0c\u4f2a\u4f5c\u9556\u8f66\u6a21 \u6837\uff0c\u805a\u96c6\u5728\u6b66\u6c49\u4e00\u5e26\u6e21\u6c5f\u3002\u201d \u3000\u3000\u8fb9\u5c11\u884d\u7cbe\u795e\u4e00\u632f\uff0c\u6717\u58f0\u9053\uff1a\u201c\u9075\u547d\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u53cc\u7709\u4e00\u76b1\uff0c\u7a81\u53c8\u6c89\u58f0\u9053\uff1a\u201c\u65e0\u8bba\u6709\u9556\u65e0\u9556\uff0c\u90fd\u4ee5\u77f3\u5757\u88c5\u8f66\u597d\u4e86\u3002\u201d \u3000\u3000\u8fb9\u5c11\u884d\u5728\u9a6c\u4e0a\u62b1\u62f3\u4e00\u793c\uff0c\u9a6c\u97ad\u4e00\u626c\uff0c\u72c2\u5954\u800c\u53bb\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u76ee\u5149\u4e00\u51db\uff0c\u6c89\u58f0\u53c8\u9053\uff1a\u201c\u7f57\u8d24\u5f1f\uff0c\u4f60\u5373\u523b\u8d76\u53bb\u5357\u9633\uff0c\u622a\u4f4f\u5f90\u660e\u3001\u5411\u98de \u65d7\u3001\u516c\u5b59\u5927\u8def\u4e09\u4eba\uff0c\u9a6c\u4e0d\u505c\u8e44\u5730\u5954\u5230\u6e21\u53e3\uff0c\u6e21\u6c5f\u5357\u4e0b\uff0c\u5230\u4e86\u6c5f\u5357\uff0c\u518d\u805a\u96c6\u7559\u5728\u7941\u95e8\u5f85\u547d\u7684\u4e8c \u5341\u4e2a\u5144\u5f1f\uff0c\u8fde\u591c\u8d76\u5230\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\uff0c\u9664\u4e86\u8001\u5f31\u5987\u4eba\u4e4b\u5916\uff0c\u89c1\u4e86\u7537\u5b50\u58ee\u4e01\uff0c\u4e00\u8d77\u4e0e\u6211\u6349\u4e86\uff0c\u6700 \u597d\u751f\u64d2\uff0c\u6740\u6b7b\u4ea6\u53ef\uff0c\u7136\u540e\u518d\u5c06\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u70e7\u4e3a\u5e73\u5730\u3002\u201d \u3000\u3000\u7f57\u4e49\u5fc3\u5934\u5fae\u98a4\uff0c\u53e3\u4e2d\u4ea6\u81ea\u6717\u58f0\u9053\uff1a\u201c\u9075\u547d\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u4e2d\u6ee1\u542b\u6740\u673a\uff0c\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u6b64\u4e8b\u82e5\u4e0d\u529e\u6210\uff0c\u5c31\u4f11\u8981\u5148\u6765\u89c1\u6211\uff0c\u6b64\u4e8b\u529e\u5b8c \u4e4b\u540e\uff0c\u4f60\u7b49\u53ef\u5728\u6d6e\u6881\u6b47\u606f\u4e00\u65e5\uff0c\u9759\u5f85\u6211\u7684\u98de\u9e3d\u4f20\u4e66\u3002\u201d \u3000\u3000\u7f57\u4e49\u8bdd\u4e5f\u4e0d\u6562\u591a\u8bf4\uff0c\u4e00\u626c\u9a6c\u97ad\uff0c\u4ea6\u81ea\u72c2\u5954\u800c\u53bb\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6beb\u4e0d\u601d\u7d22\uff0c\u6c89\u58f0\u53c8\u9053\uff1a\u201c\u738b\u70c8\uff0c\u4f60\u6f5c\u56de\u4f0f\u725b\u5c71\u53bb\uff0c\u65e0\u8bba\u2018\u795e\u624b\u2019\u6218\u98de \u6709\u4f55\u52a8\u9759\uff0c\u7acb\u523b\u8bbe\u6cd5\u544a\u8bc9\u6211\uff0c\u82e5\u662f\u9057\u6f0f\u4e86\u4e00\u4ef6\u6d88\u606f\uff0c\u4f60\u4e5f\u83ab\u8981\u89c1\u6211\u4e86\u3002\u201d \u3000\u3000\u738b\u70c8\u53cd\u624b\u4e00\u62b9\u989d\u3002\u4e0a\u51b7\u6c57\uff0c\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u53e3\u4e2d\u5e94\u9053\uff1a\u201c\u9075\u547d\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u53c8\u9053\uff1a\u201c\u89c1\u5230\u8d75\u5947\u3001\u5f20\u80dc\u4e24\u4eba\uff0c\u82e5\u662f\u4ed6\u4eec\u5df2\u5c06\u90a3\u53db\u8d3c\u64d2\u83b7\uff0c\u4f60\u4fbf\u4ee4\u8d75 \u5947\u5c06\u53db\u8d3c\u523b\u65e5\u62bc\u8fd4\u4eac\u57ce\u3002\u201d \u3000\u3000\u738b\u70c8\u5e94\u58f0\u79f0\u662f\uff0c\u65b9\u5f85\u6253\u9a6c\u800c\u53bb\uff0c\u53ea\u89c1\u6a80\u660e\u6d53\u7709\u4e00\u6311\uff0c\u7a81\u53c8\u8bf4\u9053\uff1a\u201c\u82e5\u662f\u4ed6\u4e24\u4eba\u64d2\u4e0d\u4f4f \u5230\uff1b\u53db\u8d3c\uff0c\u4f60\u5c31\u62bd\u5200\u5c06\u4ed6\u4e24\u4eba\u6740\u6b7b\uff0c\u4e8b\u503c\u975e\u5e38\uff0c\u6211\u2018\u98de\u9f99\u9556\u5c40\u2019\u7528\u4e0d\u7740\u8fd9\u6837\u7684\u8822\u624d\u3002\u201d \u3000\u3000\u738b\u70c8\u5fc3\u5934\u4e00\u5bd2\uff0c\u5e26\u8f6c\u7f30\u7ef3\uff0c\u5982\u98de\u5954\u53bb\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u65b9\u81ea\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u67f3\u8d24\u5f1f\uff0c\u4f60\u968f\u6211\u56de\u4eac\u57ce\u53bb\uff0c\u8fd9 \u4e9b\u65e0\u4f60\u4e5f\u7d2f\u4e86\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u5fd9\u9053\uff1a\u201c\u603b\u9556\u5934\u6709\u4ec0\u4e48\u4e8b\u8981\u505a\uff0c\u53ea\u7ba1\u5429\u5490\u6211\u4fbf\u662f\uff0c\u6211\u2014\u2014\u201d\u690d\u660e\u5fae\u5fae\u4e00 \u7b11\uff0c\u622a\u53e3\u9053\uff1a\u201c\u4f60\u6211\u4e00\u8def\u4e4b\u4e0a\uff0c\u53ea\u6709\u4e00\u4ef6\u4e8b\u8981\u505a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7f13\u7f13\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u4fbf\u662f\u4f20\u8a00\u5929\u4e0b\u6b66\u6797\uff0c\u5c31\u8bf4\u6587\u742a\u5df2\u4e0e\u6c5f\u5357\u864e\u90b1\u4e4b\u4e1c\u65b9\u5144\u5f1f \u2018\u94c1\u5251\u9707\u6c5f\u6e56\u2019\u4e2d\u7684\u4e1c\u65b9\u9707\u7ed3\u4e0b\u4eb2\u4e86\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u5fae\u5fae\u4e00\u6014\uff0c\u5450\u5450\u9053\uff1a\u201c\u7ed3\u2026\u2026\u7ed3\u4e0b\u4eb2\u4e86\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u6b63\u662f\uff0c\u7ed3\u4e0b\u4eb2\u5bb6\u3002\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u4e00\u7b11\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u534a\u5e74\u4e4b\u524d\uff0c\u4e1c\u65b9\u94c1\u5c31\u66fe\u793a\u610f\u4e0e\u6211\uff0c\u8981\u6211\u5c06\u6587\u742a\u5339\u914d\u7ed9\u4ed6\u7684\u4e09 \u5f1f\uff0c\u90a3\u65f6\u6211\u5fc3\u91cc\u8fd8\u6709\u4e9b\u72b9\u8c6b\uff0c\u4e00\u6765\u751f\u6015\u6fc0\u5f97\u6587\u742a\u751f\u53d8\uff0c\u4e8c\u6765\u4e5f\u4e0d\u613f\u523a\u6fc0\u90a3\u88f4\u73cf\uff0c\u662f\u4ee5\u6211\u5f53\u65f6 \u53ea\u662f\u865a\u4e0e\u59d4\u86c7\u4e86\u4e00\u4e0b\uff0c\u672a\u66fe\u771f\u7684\u7b54\u5e94\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u6014\u4e86\u4e00\u6014\uff0c\u6c89\u541f\u9063\uff1a\u201c\u90a3\u4e48\u2014\u2014\u6b64\u523b\u53ef\u80fd\u2026\u2026\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5b9e\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u5230\u5e95\u8fd8\u662f\u5dee\u4e9b\uff0c\u8fd9\u8baf\u606f\u4e00\u3001\u7ecf\u4f20\u51fa\uff0c\u5fc5\u5b9a\u9707\u52a8 \u6c5f\u6e56\uff0c\u4e1c\u65b9\u5144\u5f1f\u542c\u4e86\uff0c\u81ea\u7136\u53c8\u60ca\u53c8\u7591\uff0c\u4ed6\u4eec\u5373\u4f7f\u4e0d\u6765\u6c42\u4eb2\uff0c\u5fc5\u5b9a\u8981\u627e\u6211\u6765\u6253\u542c\u4e00\u4e0b\uff0c\u90a3\u65f6\u6211 \u4fbf\u53ef\u91cd\u63d0\u65e7\u4e8b\uff0c\u4eb2\u4e8b\u81ea\u7136\u6c34\u5230\u6e20\u6210\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u601d\u7d22\u534a\u664c\uff0c\u65b9\u624d\u4f1a\u610f\u8fc7\u6765\uff0c\u5373\u9053\uff1a\u201c\u603b\u9556\u5934\u795e\u673a\u5999\u7b97\uff0c\u5f53\u771f\u4e0d\u900a\u8bf8\u845b\uff0c \u5982\u6b64\u4e00\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5982\u6b64\u4e00\u6765\uff0c\u4e0d\u4f46\u4e1c\u65b9\u4e16\u5bb6\u6210\u4e86\u6211\u7684\u5e2e\u624b\uff0c\u5c31\u8fde\u90a3\u4e1c\u65b9\u4e94\u5144\u5f1f \u7684\u5e08\u95e8\uff0c\u4e5f\u90fd\u6210\u4e86\u6211\u7684\u540e\u76fe\uff0c\u6211\u6709\u4e86\u8fd9\u4e9b\u63f4\u52a9\uff0c\u8fd8\u8981\u6015\u4ec0\u4e48\uff1f\u90a3\u533a\u533a\u4e00\u4e2a\u8f66\u592b\u6240\u8bf4\u7684\u8bdd\uff0c\u7eb5 \u53ef\u6253\u52a8\u90a3\u822c\u4e0d\u5b66\u65e0\u672f\u7684\u8822\u624d\uff0c\u4f46\u600e\u80fd\u4f7f\u5f97\u4e1c\u65b9\u4e16\u5bb6\uff0c\u4ee5\u53ca\u6b66\u5f53\u3001\u6606\u4ed1\u8fd9\u4e9b\u540d\u95e8\u6b63\u6d3e\u76f8\u4fe1\uff0c\u54c8 \u54c8\uff0c\u5341\u5e74\u5c81\u6708\uff0c\u6bd5\u7adf\u4e0d\u77ed\uff0c\u5df2\u8db3\u4ee5\u5c06\u4eba\u4eec\u7684\u5fd8\u8bb0\u4e0e\u4ec7\u6068\u6d88\u78e8\u8bb8\u591a\uff0c\u6218\u98de\u5440\u6218\u98de\uff0c\u4f60\u6bd5\u7adf\u662f\u9009 \u9519\u4e86\u5bf9\u624b\uff01\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u4ea6\u968f\u4e4b\u5927\u7b11\u4e00\u58f0\uff0c\u7a81\u53c8\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u6b64\u4e8b\u4e00\u53d1\uff0c\u6211\u4eec\u5012\u4e0d\u4fbf\u518d\u5bf9\u88f4\u73cf\u5982 \u4f55\u4e86\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u72b9\u81ea\u72c2\u7b11\u9053\uff1a\u201c\u6211\u6709\u4e86\u8fd9\u4e9b\u540e\u76fe\uff0c\u4fbf\u662f\u518d\u591a\u5341\u4e2a\u88f4\u73cf\u8fd9\u6837\u4e73\u81ed\u672a\u5e72\u7684 \u89d2\u8272\u6765\u4e0e\u6211\u4e3a\u654c\uff0c\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u66f4\u662f\u5f97\u610f\uff0c\u66f4\u89c1\u9ad8\u4ea2\uff0c\u53ea\u662f\u8fd9\u4e00\u751f\u5584\u79f0\u77e5\u4eba\u7684\u6b66\u6797\u5927\u8c6a\uff0c\u5374\u53c8\u4e00\u6b21\u4f4e\u4f30\u4e86\u88f4\u73cf\u7684 \u80fd\u529b\uff0c\u9020\u6210\u4e86\u4e00\u6b21\u9519\u8bef\uff01\u8fd9\u9519\u8bef\u6b63\u5982\u4ed6\u6240\u8bf4\uff0c\u662f\u4ed6\u8981\u4ee5\u6c38\u751f\u7684\u65f6\u65e5\u6765\u540e\u4fae\u7684\u3002 \u7b2c\u5341\u4e09\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u5341\u4e09\u7ae0 \u3000\u3000\u5bd2\u51ac\u7ec8\u81f3\uff01 \u3000\u3000\u5e7f\u9614\u7684\u6b66\u6c49\u5e73\u539f\uff0c\u964d\u4e0b\u4e86\u7b2c\u4e00\u6b21\u5927\u96ea\u3002 \u3000\u3000\u96ea\u5730\u4e0a\u8f66\u8f99\u5f80\u590d\uff0c\u9a6c\u8e44\u7eb5\u6a2a\uff0c\u65e7\u7684\u8f66\u8f99\u8e44\u75d5\u5c1a\u672a\u88ab\u65b0\u96ea\u6240\u63a9\uff0c\u65b0\u7684\u8f66\u8f99\u8e44\u75d5\u4fbf\u53c8\u5728\u65e7\u96ea \u4e0a\u6dfb\u8ff9\u3002 \u3000\u3000\u6f2b\u5929\u7684\u98ce\u96ea\u4e0b\uff0c\u5e3d\u5f71\u97ad\u4e1d\uff0c\u5904\u5904\u53ef\u89c1\uff0c\u72c2\u6b4c\u7b11\u8bed\uff0c\u5904\u5904\u53ef\u95fb\uff0c\u5076\u800c\u8fd8\u6709\u4e00\u4e24\u9053\u5bd2\u5149\u5251 \u5f71\uff0c\u7ed9\u5927\u5730\u66f4\u6dfb\u4e86\u51e0\u5206\u5bd2\u610f\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u8fd9\u6240\u6709\u7684\u4e00\u5207\uff0c\u4fbf\u90fd\u7ed9\u672c\u5df2\u7e41\u76db\u7684\u201c\u6b66\u6c49\u4e09\u9547\u201d\uff0c\u6dfb\u4e0a\u51e0\u5206\u7e41\u76db\uff0c\u7ed9\u672c\u5df2\u52a8\u4e71\u7684 \u6b66\u6797\uff0c\u6dfb\u4e0a\u51e0\u5206\u52a8\u4e71\uff0c\u6c5f\u6e56\u4e2d\u5e73\u9759\u7684\u5c81\u6708\uff0c\u5df2\u6210\u8fc7\u53bb\uff0c\u6b66\u6797\u4e2d\u4eba\u4ff1\u5728\u5954\u8d70\u76f8\u544a\uff0c\u6697\u4e2d\u4f20 \u8bed\u2026\u2026 \u3000\u3000\u201c\u6b8b\u5e74\u5c06\u53bb\uff0c\u65b0\u5e74\u5c06\u81f3\uff0c\u6709\u5fd7\u626c\u540d\u7684\u670b\u53cb\uff0c\u4e0d\u59a8\u7094\u4e9b\u78e8\u4eae\u5200\u5251\uff0c\u4e58\u6b64\u98ce\u4e91\u9645\u4f1a\u4e4b\u65f6\uff0c\u5728 \u6b64\u98ce\u4e91\u9645\u4f1a\u4e4b\u5730\uff0c\u901e\u4e00\u901e\u82f1\u96c4\uff0c\u5c55\u4e00\u5c55\u8eab\u624b\uff0c\u5728\u6c5f\u6e56\u4e2d\u4e89\u4e00\u5e2d\u5730\uff0c\u5728\u6b66\u6797\u4e2d\u535a\u4e07\u540d\u3002\u201d \u3000\u3000\u6574\u88c5\u5f85\u53d1\u7684\u9556\u8f66\uff0c\u7fa4\u96c6\u5728\u957f\u6c5f\u5317\u5cb8\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u4f1a\u6e21\u6c5f\u752b\u53bb\uff0c\u9556\u8f66\u4e0a\u9c9c\u660e\u7684\u65d7\u5e1c\uff0c\u8fce\u7740 \u51db\u51bd\u7684\u5317\u98ce\uff0c\u8212\u5377\u62db\u5c55\u3002 \u3000\u3000\u65d7\u5e1c\u4e0a\u6240\u7ee3\u90a3\u516b\u6761\u6829\u6829\u5982\u751f\u7684\u98de\u9f99\uff0c\u66f4\u50cf\u662f\u4ff1\u5c06\u4e58\u98ce\u7834\u4e91\u800c\u53bb\u3002 \u3000\u3000\u6cbf\u6c5f\u7684\u5927\u9053\u4e0a\uff0c\u4e0d\u65f6\u6709\u52b2\u88c5\u75be\u773c\u7684\u201c\u9f99\u98de\u9556\u5c40\u201d\u65d7\u4e0b\u7684\u6b66\u58eb\uff0c\u8170\u4f69\u957f\u5251\uff0c\u4e09\u4e94\u6210\u7fa4\uff0c\u547c \u5578\u6765\u53bb\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u51dd\u91cd\u7684\u9762\u5bb9\u4e0a\uff0c\u4e0d\u65f6\u900f\u9732\u51fa\u7d27\u5f20\u4e4b\u8272\uff0c\u76ee\u5149\u70af\u70af\uff0c\u6709\u5982\u730e\u72ac\u822c\u56db\u4e0b\u641c\u5bfb\u7740\uff0c\u7c97\u7cd9 \u575a\u5b9e\u7684\u624b\u638c\uff0c\u968f\u65f6\u7d27\u63e1\u7740\u5200\u5251\u4e4b\u67c4\uff0c\u4eff\u4f5b\u65e0\u8bba\u5728\u4efb\u4f55\u65f6\u523b\uff0c\u4ed6\u4eec\u90fd\u4f1a\u62bd\u51fa\u8170\u7554\u7684\u5200\u5251\uff0c\u4e0e\u4eba \u4f5c\u751f\u6b7b\u7684\u640f\u6597\u3002 \u3000\u3000\u575a\u5b9e\u7684\u76ae\u9774\uff0c\u8e0f\u5728\u575a\u5b9e\u7684\u96ea\u5730\u4e0a\uff0c\u94dc\u7247\u642d\u53e3\uff0c\u7cbe\u5149\u96ea\u4eae\u7684\u5200\u5251\u4e4b\u9798\uff0c\u8f7b\u62cd\u7740\u6697\u9ed1\u8272\u7684\u957f \u88e4\u3002 \u3000\u3000\u8840\u7ea2\u7684\u4e1d\u7a57\uff0c\u8fce\u98ce\u98d8\u821e\u7740\uff0c\u5c31\u50cf\u662f\u4ed6\u4eec\u5fc3\u91cc\u5954\u817e\u7740\u7684\u70ed\u8840\u4e00\u6837\u3002 \u3000\u3000\u5728\u6b66\u6797\u4e2d\uff0c\u7a33\u5c45\u76df\u4e3b\u4e4b\u52bf\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\uff0c\u5728\u9556\u5c40\u4e2d\u7a33\u6267\u725b\u8033\u4e4b\u4f4d\u7684\u201c\u98de\u9f99\u9556\u5c40\u201d\u2026\u2026 \u3000\u3000\u5341\u5e74\u6765\u5b89\u5982\u78d0\u77f3\u4e00\u822c\u7684\u5730\u4f4d\uff0c\u6b64\u523b\u7ec8\u4e8e\u5f00\u59cb\u52a8\u6447\u4e86\u8d77\u6765\u3002\u3001\u8fd9\u6700\u4e3b\u8981\u7684\u539f\u56e0\uff0c\u662f\u201c\u9f99\u5f62 \u516b\u638c\u201d\u5728\u4eba\u4eec\u5fc3\u76ee\u4e2d\u90a3\u6b63\u76f4\u3001\u6177\u6168\u3001\u4ec1\u539a\u7684\u82f1\u96c4\u5730\u4f4d\uff0c\u6839\u672c\u5f00\u59cb\u4e86\u52a8\u6447\uff0c\u56e0\u4e3a\u5341\u5e74\u524d\u7684\u65e7\u6848 \u91cd\u7ffb\uff0c\u4e00\u4e2a\u6bd2\u8fa3\u3001\u9634\u9669\u3001\u5978\u72e1\u7684\u201c\u51f6\u624b\u201d\u6076\u540d\uff0c\u5df2\u52a0\u5728\u8fd9\u96c4\u8e1e\u6b66\u6797\u7684\u4e00\u4ee3\u5927\u8c6a\u8eab\u4e0a\u3002 \u3000\u3000\u98ce\u5c18\u4ec6\u4ec6\u7684\u6c5f\u6e56\u5ba2\uff0c\u81ea\u56db\u9762\u516b\u65b9\u8d76\u6765\u8fd9\u98ce\u4e91\u9645\u5408\u7684\u201c\u6b66\u6c49\u4e09\u9547\u201d\u4e0a\uff0c\u6bcf\u4e2a\u4eba\u7684\u76ee\u5149\uff0c\u90fd \u6ce8\u610f\u7740\u6cbf\u6c5f\u805a\u96c6\u7684\u9556\u8f66\uff0c\u626c\u7709\u77aa\u76ee\u7684\u201c\u98de\u9f99\u201d\u6b66\u58eb\uff0c\u53c8\u4e0d\u65f6\u7559\u610f\u7740\u81ea\u6c5f\u5357\u90a3\u8fb9\u4f20\u6765\u7684\u52a8\u9759\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u4e0d\u7981\u5728\u6697\u4e2d\u60cb\u501f\uff0c\u82e5\u662f\u201c\u5feb\u8baf\u201d\u82b1\u7389\u672a\u6b7b\uff0c\u53ea\u6015\u4e5f\u4e0d\u4f1a\u6709\u90a3\u4e48\u591a\u6bb4\u6597\u3001\u4e89\u5435\u3001\u51f6 \u6740\u4e4b\u4e8b\u3002 \u3000\u3000\u201c\u6e05\u6668\uff0c\u672c\u8be5\u662f\u4e00\u65e5\u4e2d\u6700\u6700\u9759\u5bc2\u7684\u65f6\u5019\u3002\u4f46\u5927\u96ea\u7eb7\u98de\u4e0b\u7684\u6b66\u6c49\u4e09\u9547\uff0c\u5374\u8fdc\u6bd4\u5728\u8fd9\u540c\u6837\u7684 \u65f6\u5019\u4efb\u4f55\u4e00\u4e2a\u5176\u4ed6\u7684\u5730\u65b9\u66f4\u4e0d\u9759\u5bc2\uff0c\u7ed3\u7740\u51b0\u67f1\u7684\u5c4b\u6a90\u4e0b\uff0c\u5df2\u6709\u4e09\u4e94\u6210\u7fa4\uff0c\u4e92\u76f8\u4f4e\u8bed\u7740\u7684\u4eba \u4eec\uff0c\u521a\u4e0b\u95e8\u677f\u7684\u9762\u5e97\u8336\u8086\uff0c\u66f4\u65e9\u5df2\u4f4d\u65e0\u865a\u5e2d\u3002\u7a81\u5730\uff0c\u56db\u5339\u5065\u9a6c\uff0c\u72c2\u5954\u800c\u6765\uff0c\u9a6c\u8e44\u540e\u626c\u8d77\u4e00\u8fde \u4e32\u51b0\u96ea\u3002\u9a6c\u4e0a\u4eba\u91cd\u8863\u6be1\u7b20\uff0c\u659c\u62ab\u98ce\u6c05\uff0c\u4e00\u5165\u5e02\u533a\uff0c\u4fbf\u626c\u97ad\u5927\u547c\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u5348\u524d\u53ef\u5230\uff01\u201d \u3000\u3000\u8fd9\u547c\u58f0\uff0c\u4e00\u58f0\u8fde\u7740\u4e00\u58f0\uff0c\u7acb\u523b\u4f20\u8ffd\u4e86\u6b66\u6c49\u4e09\u9547\uff0c\u4eff\u4f5b\u90a3\u5c1a\u672a\u7ed3\u51bb\u7684\u6c5f\u6c34\u4e2d\uff0c\u6f8e\u6e43\u8d77\u4f0f\u7684 \u6ce2\u6d6a\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\uff0c\u8fd9\u4e24\u4e2a\u4f17\u76ee\u6240\u77a9\u7684\u6b66\u6797\u5927\u8c6a\uff0c\u867d\u7136\u81ea\u4eca\u5c1a\u672a\u9732\u9762\uff0c \u4f46\u201c\u88f4\u5927\u5148\u751f\u201d\u6bd5\u7adf\u6765\u4e86\uff0c\u8fd9\u5df2\u662f\u503c\u5f97\u4eba\u4eec\u5174\u594b\uff0c\u6fc0\u52a8\u7684\u6d88\u606f\u3002 \u3000\u3000\u53e6\u4e00\u4e9b\u4eba\uff0c\u6d8c\u96c6\u5728\u957f\u6c5f\u6e21\u5934\uff0c\u6709\u7684\u6491\u7740\u539a\u539a\u7684\u6cb9\u7eb8\u5927\u4f1e\uff0c\u6709\u7684\u6234\u7740\u539a\u539a\u7684\u6be1\u7b20\uff0c\u53ea\u89c1\u6ed4 \u6ed4\u6c5f\u6c34\u95f4\uff0c\u7f13\u7f13\u9a76\u6765\u4e00\u8258\u6c5f\u8239\u3002 \u3000\u3000\u201c\u662f\u8c01\uff1f\u201d\u662f\u8c01\u6765\u4e86\uff1f\u201c\u65e0\u8bba\u662f\u8c01\uff0c\u53ea\u8981\u81ea\u6c5f\u5357\u6765\uff0c\u90fd\u4f1a\u5f15\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u7684\u4e00\u9635\u6fc0\u52a8\uff0c \u8fd9\u4e00\u9053\u6d4a\u9ec4\u7684\u6c5f\u6c34\uff0c\u867d\u7136\u963b\u4f4f\u4e86\u8bb8\u591a\u6d88\u606f\uff0c\u4f46\u5374\u963b\u4e0d\u4f4f\u8fd9\u4e00\u573a\u5373\u5c06\u5230\u6765\u7684\u4e89\u6740\u640f\u6597\u2014\u2014\u6570\u5341 \u5e74\u6765\uff0c\u6c5f\u6e56\u4ec5\u89c1\u7684\u640f\u6597\u3002\u6c5f\u8239\u6e10\u6e10\u8fd1\u4e86\u3002\u9547\u7684\u90a3\u4e00\u8fb9\uff0c\u7a81\u5730\u9a9a\u52a8\u4e86\u8d77\u6765\u3002\u9760\u9760\u7684\u96ea\u82b1\u4e2d\uff0c\u4e00 \u4e2a\u5251\u7709\u6717\u76ee\uff0c\u4e00\u8eab\u9752\u8863\u7684\u5c11\u5e74\uff0c\u624b\u6309\u8f94\u5934\uff0c\u5f90\u6d82\u9a70\u8fd1\u4e86\u90a3\u4e00\u6761\u7b14\u76f4\u7684\u957f\u8857\u3002\u5728\u4ed6\u8eab\u4fa7\u662f\u4e24\u5339 \u9ed1\u9a6c\uff0c\u9a6c\u4e0a\u7070\u8863\u5927\u8884\uff0c\u9762\u8272\u51b7\u6f20\u7684\u9a91\u58eb\uff0c\u4fbf\u662f\u90a3\u540d\u58f0\u4e45\u5df2\u54cd\u904d\u6b66\u6797\uff0c\u81f3\u4eca\u540d\u58f0\u5374\u66f4\u54cd\u7684\u201d\u51b7 \u8c37\u53cc\u6728\u201c\u3002\u5728\u4ed6\u8eab\u540e\uff0c\u662f\u4e00\u7247\u4eba\u58f0\uff0c\u4e00\u7247\u9a6c\u5636\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u9a91\u58eb\uff0c\u9a91\u7740\u591a\u5c11\u9a74\u9a6c\uff0c\u8ddf\u5728\u4ed6 \u8eab\u540e\u7ea6\u83ab\u4e00\u4e08\u5f00\u5916\u5904\u3002\u4e00\u773c\u671b\u53bb\uff0c\u4f46\u89c1\u4eba\u5934\u8702\u6d8c\uff0c\u6c47\u96c6\u6210\u4e00\u9053\u7070\u9ed1\u7684\u6d6a\u6f6e\u3002\u201d\u88f4\u5927\u5148\u751f\uff01 \u201c\u56db\u4e0b\u7acb\u523b\u54cd\u8d77\u4e00\u7247\u9707\u8033\u7684\u547c\u58f0\u3002\u547c\u58f0\uff0c\u81ea\u6bcf\u4e2a\u4eba\u53e3\u4e2d\u53d1\u51fa\u65f6\uff0c\u672c\u662f\u8c28\u614e\u800c\u8f7b\u5fae\u7684\uff0c\u4f46\u8fd9\u8bb8 \u591a\u4eba\u540c\u65f6\u53d1\u51fa\uff0c\u542c\u6765\u4fbf\u4eff\u4f5b\u5929\u8fb9\u9e23\u96f7\u7684\u58f0\u97f3\u3002\u88f4\u73cf\uff0c\u9762\u5bb9\u4ecd\u662f\u8c26\u865a\u800c\u5b89\u8be6\uff0c\u5634\u89d2\uff0c\u4e5f\u4ecd\u7136\u6302 \u7740\u90a3\u8c26\u865a\u800c\u5b89\u7965\u7684\u5fae\u7b11\uff0c\u4f46\u662f\uff0c\u5728\u4ed6\u7684\u4e00\u53cc\u70af\u7136\u6709\u5149\u7684\u773c\u775b\u4e2d\uff0c\u5374\u4f3c\u4e4e\u9690\u85cf\u7740\u4e00\u4efd\u60b2\u54c0\uff0c\u4e00 \u4efd\u6c89\u91cd\uff0c\u4ee5\u53ca\u4e00\u4efd\u60b2\u5929\u60af\u4eba\u7684\u601c\u60dc\u4e0e\u5fe7\u8651\u3002\u65b9\u624d\u8fd8\u7316\u72c2\u5730\u5927\u6b65\u884c\u8d70\u7684\u201d\u98de\u9f99\u201c\u6b66\u58eb\u4eec\uff0c\u6b64\u523b \u65e9\u5df2\u6536\u655b\u4e86\u4ed6\u4eec\u6570\u65e5\u6765\u4e00\u76f4\u5e26\u5728\u9762\u4e0a\u7684\u72c2\u5984\u4e4b\u6001\u3002\u76ae\u9774\u8e0f\u5730\u7684\u6c89\u91cd\u811a\u6b65\uff0c\u9aa4\u7136\u8f7b\u5fae\u4e86\u4e0b\u6765\uff0c \u7d27\u63e1\u5251\u9798\u7684\u624b\u638c\uff0c\u6b64\u523b\u4e5f\u677e\u843d\u3001\u5782\u4e0b\uff0c\u5782\u5230\u53cc\u819d\u65c1\u3002\u88f4\u73cf\u76ee\u5149\u4e00\u626b\uff0c\u9000\u4e0b\u9a6c\u8e6c\uff0c\u8f7b\u8f7b\u63a0\u4e0b\u4e86 \u9a6c\u3002\u4ed6\u4e0d\u613f\u5728\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u4e2d\u95f4\uff0c\u9a91\u9a6c\u800c\u884c\uff0c\u56e0\u4e3a\u4ed6\u672c\u4e0d\u613f\u5728\u4f17\u4eba\u4e4b\u95f4\uff0c\u51fa\u4eba\u5934\u5730\uff0c\u4ed6\u53ea\u613f \u505a\u4e00\u4e2a\u5e73\u51e1\u7684\u4eba\u3002\u4f46\u547d\u8fd0\u5374\u5c06\u4ed6\u9020\u6210\u4e3a\u4e00\u4e2a\u82f1\u96c4\uff0c\u65f6\u52bf\u4e5f\u5c06\u4ed6\u9020\u6210\u4e3a\u4e00\u4e2a\u82f1\u96c4\uff0c\u4e00\u4e00\u4e2a\u51fa\u7c7b \u62d4\u8403\uff0c\u4e0d\u540c\u51e1\u54cd\u7684\u82f1\u96c4\u3002\u5c31\u5728\u8fd9\u540c\u65f6\uff0c\u957f\u8857\u7684\u90a3\u4e00\u5934\uff0c\u6c5f\u8239\u5df2\u9760\u5cb8\u3002\u8e0f\u677f\uff0c\u642d\u4e0a\u4e86\u6e21\u5934\u3002\u95e8 \u7a97\u7d27\u95ed\u7684\u8239\u8231\u4e2d\uff0c\u7f13\u7f13\u8d70\u51fa\u4e94\u4e2a\u9526\u8863\u5c11\u5e74\uff0c\u5251\u7709\u661f\u76ee\uff0c\u8170\u4f69\u957f\u5251\uff0c\u6c5f\u98ce\u5439\u821e\u7740\u4ed6\u4eec\u7684\u8863\u886b\uff0c \u4f7f\u4ed6\u4eec\u7684\u795e\u91c7\u671b\u6765\u66f4\u89c1\u6f47\u6d12\u3002\u6c5f\u5cb8\u8fb9\u7684\u4eba\u7fa4\uff0c\u7acb\u523b\u7206\u51fa\u4e00\u9635\u547c\u58f0\uff1a\u201c\u4e1c\u65b9\u4e94\u5251\uff01\u201d \u3000\u3000\u6e21\u5934\u4e0a\u7684\u4eba\u7fa4\uff0c\u98de\u5feb\u5730\u9000\u4e86\u5f00\u53bb\uff0c\u4e1c\u65b9\u94c1\u9762\u5e26\u5fae\u7b11\uff0c\u4e0d\u4f4f\u62f1\u624b\uff0c\u5e26\u7740\u4ed6\u540d\u9707\u6b66\u6797\u7684\u56db\u4f4d \u5144\u5f1f\uff0c\u4e0b\u4e86\u6e21\u8239\u3002 \u3000\u3000\u957f\u8857\u4e0a\uff0c\u7acb\u523b\u50cf\u716e\u6cb8\u4e86\u7684\u6c34\u9505\u4e00\u822c\uff0c\u6cb8\u817e\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u6b65\u4e0b\u6e21\u5934\uff0c\u6b65\u4e0a\u957f\u8857\uff0c\u7b14\u76f4\u7684\u4e00\u6761\u5927\u8857\u4e0a\uff0c\u54c4\u52a8\u4e0e\u6270\u4e71\uff0c\u867d\u7136\u5df2\u53ef\u9707\u4eba\u8033 \u9f13\uff0c\u4f46\u5982\u6b64\u5bbd\u9614\u7684\u8857\u9762\u4e0a\u7adf\u6ca1\u6709\u4e00\u4eba\u6765\u5f80\u884c\u8d70\uff0c\u53ea\u6709\u5c4b\u6a90\u4e0b\u3001\u8336\u8086\u4e2d\u7684\u4eba\u7fa4\u5374\u66f4\u62e5\u6324\u4e86\u3002 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u5251\u7709\u5fae\u76b1\uff0c\u5fc3\u4e2d\u5404\u5404\u6709\u4e9b\u8be7\u5f02\u3001\u6000\u7591\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u4eec\u7ec8\u4e8e\u542f\u6b65\u5411\u8857\u7684\u90a3\u4e00\u5934\u8d70\u53bb\u3002 \u3000\u3000\u8857\u7684\u90a3\u4e00\u8fb9\uff0c\u88f4\u73cf\u7684\u811a\u6b65\u4ecd\u662f\u5b89\u7965\u800c\u7f13\u6162\u7684\uff0c\u4ed6\u5782\u76ee\u655b\u7709\uff0c\u4e0d\u613f\u5411\u56db\u4e0b\u7fa4\u8c6a\u671b\u4e0a\u4e00\u773c\u3002 \u3000\u3000\u5c4b\u6a90\u4e0b\uff0c\u8336\u8086\u4e2d\uff0c\u7a81\u7136\u53d8\u5f97\u5bc2\u65e0\u58f0\u606f\uff0c\u6b66\u6797\u4e2d\u6b64\u523b\u65e9\u5df2\u8f70\u52a8\u5f00\u4e00\u4ef6\u76db\u4e8b\uff0c\u6b66\u6797\u4e2d\u4eba\u4eba\u90fd \u77e5\u9053\uff0c\u201c\u6c5f\u5357\u864e\u90b1\u4e1c\u65b9\u4e16\u5bb6\uff0c\u5df2\u4e0e\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u7ed3\u4e3a\u59fb\u4eb2\uff01\u201d\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7684\u638c\u4e0a \u660e\u73e0\u201c\u9f99\u5973\u201d\u64c5\u6587\u742a\uff0c\u5373\u5c06\u4e0b\u5ac1\u7ed9\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u4e2d\u7684\u4e09\u4fa0\u4e1c\u65b9\u9707\uff01 \u3000\u3000\u800c\u53e6\u4e00\u4e2a\u6d88\u606f\u867d\u7136\u8f83\u4e3a\u79d8\u5bc6\uff0c\u4f46\u5374\u5df2\u662f\u516c\u5f00\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u8fd9\u6d88\u606f\u4e0d\u77e5\u7531\u8c01\u4f20\u51fa\uff0c\u4f46\u5728\u7b2c\u4e00\u4eba\u53e3\u4e2d\u4f20\u51fa\u4e4b\u540e\uff0c\u4fbf\u5728\u65e0\u6570\u4eba\u7684\u53e3\u4e2d\u4f20\u4e86\u51fa\u53bb\uff0c\u867d\u7136\u5927 \u591a\u662f\u9644\u8033\u4f4e\u8bed\uff0c\u4f46\u901f\u5ea6\u5374\u4f3c\u6bd4\u516c\u5f00\u4f20\u64ad\u7684\u8fd8\u8981\u8fc5\u901f\u3002 \u3000\u3000\u6b64\u523b\u6b66\u6797\u4e4b\u4e2d\uff0c\u4eba\u4eba\u4e5f\u5df2\u4ff1\u90fd\u77e5\u9053\uff01 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7684\u638c\u4e0a\u660e\u73e0\u201c\u9f99\u5973\u201d\u6a80\u6587\u742a\uff0c\u672c\u6765\u662f\u201c\u88f4\u5927\u5148\u751f\u201d\u9752\u6885\u7af9\u9a6c\u7684\u7ae5\u5e74\u7231 \u4fa3\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u8fd8\u5728\u6697\u4e2d\u4f20\u8bf4\uff1a\u201c\u88f4\u5927\u5148\u751f\u4e0e\u2018\u9f99\u5973\u2019\u6a80\u6587\u742a\uff0c\u65e9\u5df2\u6697\u4e2d\u79c1\u8ba2\u4e86\u7ec8\u8eab\uff0c\u53ea\u662f\u56e0\u4e3a \u2018\u9f99\u5f62\u516b\u638c\u2019\u4ece\u4e2d\u4f5c\u6897\uff0c\u4ed6\u53ea\u662f\u4e3a\u4e86\u8981\u6500\u4e0a\u2018\u4e1c\u65b9\u4e16\u5bb6\u2019\u7684\u52bf\u529b\u6765\u5bf9\u4ed8\u2018\u6c5f\u5357\u540c\u76df\u2019\uff0c\u624d\u5c06 \u4ed6\u7684\u7231\u5973\u8bb8\u914d\u7ed9\u4e1c\u65b9\u9707\uff01\u201d \u3000\u3000\u867d\u7136\u5927\u591a\u6570\u4eba\u4e0d\u77e5\u9053\u8fd9\u6d88\u606f\u7684\u6765\u6e90\uff0c\u5374\u4ecd\u6709\u5c11\u6570\u4eba\u731c\u5230\u8fd9\u6d88\u606f\u5fc5\u5b9a\u662f\u201c\u795e\u624b\u201d\u6218\u98de\u4f20\u51fa \u6765\u7684\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u662f\u5426\u77e5\u9053\u8fd9\u6d88\u606f\u6765\u6e90\u7684\u4eba\uff0c\u5bf9\u8fd9\u6d88\u606f\u7684\u771f\u5b9e\u6027\u5374\u90fd\u786e\u4fe1\u4e0d\u7591\u3002 \u3000\u3000\u800c\u6b64\u523b\uff0c\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u4e0e\u88f4\u5927\u5148\u751f\u7adf\u5373\u5c06\u5728\u8fd9\u957f\u8857\u4e0a\u76f8\u9047\uff0c\u8fd9\u5f53\u771f\u6bd4\u4efb\u4f55\u4e8b\u90fd\u8981\u6444\u4eba\u5fc3 \u5f26\u3002 \u3000\u3000\u88f4\u73cf\u8eab\u540e\u90a3\u4e00\u7fa4\u6b66\u6797\u8c6a\u58eb\u4e5f\u90fd\u4e0b\u4e86\u9a6c\uff0c\u5343\u767e\u53ea\u4e0d\u540c\u7684\u978b\u9774\u8e0f\u5728\u540c\u6837\u7684\u96ea\u5730\u4e0a\uff0c\u53d1\u51fa\u4e86\u540c \u6837\u7684\u58f0\u54cd\u3002 \u3000\u3000\u4e00\u7247\u6c99\u6c99\u7684\u811a\u6b65\u58f0\uff0c\u81ea\u5317\u800c\u897f\u3002 \u3000\u3000\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u9762\u4e0a\u867d\u4e5f\u5e26\u7740\u7b11\u5bb9\uff0c\u4f46\u5fc3\u5934\u5374\u514d\u4e0d\u4e86\u6709\u4e00\u4efd\u60ca\u8bb6\u4e0e\u6000\u7591\uff0c\u9759\u5bc2\u4e4b\u4e2d\uff0c\u4ed6\u4eec \u4e5f\u542c\u5230\u4e86\u8fd9\u4e00\u7247\u811a\u6b65\u58f0\uff0c\u8ddd\u79bb\u81ea\u5df1\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4ed6\u4eec\u76ee\u5149\u4e00\u626b\uff0c\u53ea\u89c1\u897f\u4fa7\u7684\u4eba\u7fa4\uff0c\u4e5f\u8d8a\u6765\u8d8a\u662f \u7d27\u5f20\u3002 \u3000\u3000\u8fd9\u5144\u5f1f\u4e94\u4eba\u4e0d\u7ea6\u800c\u540c\u5730\u8f7b\u8f7b\u62ac\u8d77\u624b\u638c\uff0c\u63e1\u4f4f\u5251\u67c4\uff0c\u76ee\u5149\u5982\u5200\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u51dd\u6ce8\u5728\u524d\u9762\u7684 \u8857\u9053\u4e0a\u3002 \u3000\u3000\u9759\u5bc2\u7684\u5c4b\u6a90\u4e0b\uff0c\u6709\u6570\u5341\u4e2a\u9ed1\u8863\u6c49\u5b50\u5728\u4fcf\u4fcf\u79fb\u52a8\u7740\u5206\u6563\u5f00\uff0c\u5bfb\u627e\u7740\u9690\u50fb\u7684\u5730\u52bf\uff0c\u4f46\u6b64\u523b\u4f17 \u4eba\u81ea\u7136\u8c01\u4e5f\u4e0d\u4f1a\u5c06\u6ce8\u610f\u4e4b\u529b\u653e\u5230\u4ed6\u4eec\u8eab\u4e0a\uff0c\u66f4\u6ca1\u6709\u4e00\u4eba\u80fd\u8ba4\u51fa\u4ed6\u4eec\u7a76\u7adf\u662f\u8c01\u4eba\u7684\u624b\u4e0b\u3002 \u3000\u3000\u88f4\u73cf\u811a\u6b65\u672a\u505c\u3002 \u3000\u3000\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u811a\u6b65\u4ea6\u672a\u505c\uff01 \u3000\u3000\u4ed6\u4eec\u5f7c\u6b64\u8d70\u5f97\u66f4\u8fd1\u4e86\uff0c\u5f7c\u6b64\u5373\u5c06\u671b\u89c1\u5bf9\u65b9\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u76f8\u9047\u540e\u4f1a\u600e\u4e48\u6837\u5462\uff1f\u4ed6\u4eec\u4f1a\u6709\u4ec0\u4e48\u8868\u60c5\uff1f\u201d \u3000\u3000\u4eba\u4eba\u5fc3\u4e2d\u4ff1\u5728\u6697\u95ee\u81ea\u5df1\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\u8fd9\u95ee\u9898\u7684\u7b54\u6848\u3002 \u3000\u3000\u7ec8\u4e8e\u4e00\u4e00\u4e00 \u3000\u3000\u88f4\u73cf\u62ac\u8d77\u5934\u6765\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u53ea\u89c1\u524d\u9762\u6709\u4e94\u4e2a\u9526\u8863\u5c11\u5e74\u7f13\u6b65\u800c\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u811a\u6b65\u6574\u9f50\u800c\u5212\u4e00\uff0c\u4ed6\u4eec\u7684\u8863\u5c65\u795e\u6001\u662f\u8fd9\u822c\u76f8\u4f3c\u3002 \u3000\u3000\u88f4\u73cf\u4e00\u773c\u4e4b\u4e0b\uff0c\u4fbf\u5df2\u786e\u5b9a\u8fd9\u4e94\u4eba\u4fbf\u662f\u4e1c\u65b9\u5144\u5f1f\uff0c\u4ed6\u5fc3\u5934\u5fae\u5fae\u4e00\u8df3\uff0c\u4f46\u9762\u4e0a\u5374\u4ecd\u7136\u672a\u52a8\u58f0 \u8272\u3002 \u3000\u3000\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u5bf9\u671b\u4e00\u773c\uff0c\u4e1c\u65b9\u6c5f\u8f7b\u8f7b\u9053\uff1a\u201c\u524d\u9762\u7684\u662f\u88f4\u73cf\uff01\u201d \u3000\u3000\u5144\u5f1f\u3002\u4e94\u4eba\u9f50\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u4ed6\u5144\u5f1f\u4e94\u4eba\uff0c\u672c\u6765\u4e0e\u88f4\u73cf\u6beb\u65e0\u4ec7\u6028\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u5728\u5982\u6b64\u7684 \u60c5\u52bf\u4e0b\uff0c\u4ed6\u4eec\u5374\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u4e0e\u88f4\u73cf\u4e4b\u95f4\uff0c\u4f3c\u4e4e\u6709\u4e9b\u82a5\u8482\u4f3c\u7684\uff0c\u4ed6\u4eec\u9762\u8272\u867d\u672a\u53d8\uff0c\u4f46\u5fc3\u4e2d\u4e5f \u6709\u4e86\u4e9b\u5c34\u5c2c\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5bf9\u671b\u4e00\u773c\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u5374\u89c1\u90a3\u8fb9\u4e1c\u65b9\u5144\u5f1f\u5df2\u81ea\u5927\u6b65\u884c\u6765\uff0c\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c \u62b1\u62f3\u9053\uff1a\u201c\u5e78\u4f1a\u5e78\u4f1a\uff01\u201d \u3000\u3000\u4e1c\u65b9\u4e94\u5251\u4e00\u8d77\u62b1\u62f3\u4e3e\u624b\uff0c\u9053\uff1a\u201c\u5e78\u4f1a\u5e78\u4f1a\uff01\u201d \u3000\u3000\u4e1c\u65b9\u9707\u795e\u8272\u867d\u7136\u6700\u662f\u5c34\u5c2c\uff0c\u4f46\u9762\u4e0a\u5374\u4ecd\u7136\u5e26\u7740\u7b11\u5bb9\uff0c\u6a90\u4e0b\u4eba\u7fa4\u4e0d\u7981\u6697\u4e2d\u4ea4\u6362\u4e86\u4e00\u4e2a\u5931\u671b \u7684\u773c\u8272\u3002 \u3000\u3000\u773c\u770b\u4ed6\u4eec\u5306\u5306\u5bd2\u55a7\u4e86\u4e00\u53e5\uff0c\u4fbf\u5c06\u4ea4\u81c2\u800c\u53bb\uff0c\u65e2\u4e0d\u7d27\u5f20\uff0c\u66f4\u4e0d\u523a\u6fc0\uff0c\u5c31\u597d\u50cf\u8def\u4e0a\u4efb\u4f55\u4eba\u9047 \u7740\u53e6\u4e00\u4eba\u90a3\u6837\u5e73\u51e1\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u53c8\u81ea\u5bf9\u671b\u4e00\u773c\uff0c\u7a81\u542c\u957f\u8857\u90a3\u8fb9\uff0c\u54cd\u8d77\u4e00\u58f0\u547c\u558a\uff1a\u201c\u88f4\u5927\u5148\u751f\uff0c\u4f60\u7684\u7ae5\u5e74\u7231 \u4fa3\u88ab\u4eba\u62a2\u8d70\u4e86\uff0c\u4f60\u5fc3\u91cc\u96be\u9053\u4e00\u70b9\u4e5f\u4e0d\u96be\u53d7\uff1f\u96be\u9053\u4e00\u70b9\u4e5f\u4e0d\u6124\u6012\uff1f\u201d \u3000\u3000\u88f4\u73cf\u3001\u201c\u51b7\u8c37\u53cc\u6728\u201d\u3001\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u4e00\u8d77\u987f\u4f4f\u811a\u6b65\uff0c\u5446\u5446\u5730\u671b\u4e86\u51e0\u773c\uff0c\u8fd9\u5176\u95ee\u4ed6\u4eec\u9762\u4e0a \u795e\u8272\u7684\u53d8\u5316\uff0c\u5f53\u771f\u8c01\u4e5f\u65e0\u6cd5\u5f62\u5bb9\u3002 \u3000\u3000\u4e1c\u65b9\u9707\u5251\u7709\u7a81\u5730\u4e00\u6311\uff0c\u5389\u53f1\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u53f1\u58f0\u672a\u4e86\uff0c\u8857\u7684\u53e6\u4e00\u8fb9\u53c8\u6709\u4eba\u5927\u547c\u9053\uff1a\u201c\u4e1c\u65b9\u9707\uff0c\u6a80\u6587\u742a\u867d\u7136\u5ac1\u7ed9\u4f60\uff0c\u4f46\u5979\u7684\u5fc3\u91cc\u8fd8\u662f \u7231\u7740\u88f4\u5927\u5148\u751f\u7684\uff0c\u4f60\u89c9\u5f97\u8fd9\u6ecb\u5473\u597d\u53d7\u4e48\uff1f\u201d \u3000\u3000\u56db\u4e0b\u7acb\u523b\u4e00\u9635\u54c4\u4e71\uff0c\u4e1c\u65b9\u5144\u5f1f\u9762\u8272\u5267\u53d8\uff0c\u4e1c\u65b9\u9707\u66f4\u662f\u9762\u5bb9\u82cd\u767d\uff0c\u8fdc\u8fdc\u8ddf\u5728\u88f4\u73cf\u8eab\u540e\u7684\u4eba \u7fa4\uff0c\u4e00\u8d77\u6d8c\u4e86\u4e0a\u6765\uff0c\u7adf\u5c06\u4ed6\u5e7b\u5305\u56f4\u4e86\u8d77\u6765\uff0c\u8981\u5728\u8fd9\u8bb8\u591a\u4eba\u4e4b\u4e2d\u5bfb\u51fa\u4e00\u4e2a\u547c\u558a\u7684\u4eba\uff0c\u90a3\u5f53\u771f\u6bd4 \u5927\u6d77\u635e\u9488\u8fd8\u8981\u56f0\u96be\u3002 \u3000\u3000\u4e1c\u65b9\u9707\u5f3a\u7b11\u4e00\u58f0\uff0c\u6717\u58f0\u9053\uff1a\u201c\u88f4\u5144\u522b\u672a\u53ef\u597d\uff1f\u95fb\u9053\u8fd1\u6765\u88f4\u5144\u6280\u827a\u5927\u8fdb\uff0c\u5c0f\u5f1f\u60f3\u6765\u4e5f\u9ad8\u5174 \u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u6545\u610f\u8bf4\u5f97\u5341\u5206\u9ad8\u4ea2\uff0c\u4e00\u6765\u662f\u8868\u793a\u81ea\u5df1\u5fc3\u4e2d\u65e0\u79c1\uff0c\u518d\u6765\u4e5f\u662f\u60f3\u8f6c\u5f00\u8bdd\u9898\uff0c\u8fd9\u6b63\u662f\u4ed6 \u5584\u4e8e\u4e3a\u4eba\u4e4b\u5904\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u8bdd\u58f0\u624d\u4e86\uff0c\u7acb\u523b\u53c8\u6709\u4eba\u558a\u9053\uff1a\u201c\u4f60\u9ad8\u5174\u4ec0\u4e48\uff01\u88f4\u5927\u5148\u751f\u54ea\u70b9\u4e0d\u6bd4\u4f60\u5144\u5f1f\u4e94\u4eba\u5f3a\uff1f \u53ea\u53ef\u53f9\u6a80\u660e\u7adf\u4e3a\u4e86\u8981\u5df4\u7ed3\u4f60\u5bb6\uff0c\u5374\u5c06\u4ed6\u5973\u513f\u5f53\u505a\u4e86\u793c\u7269\uff0c\u727a\u7272\u4e86\u4ed6\u5973\u513f\u7684\u4e00\u751f\u5e78\u798f\uff0c\u4e1c\u65b9\u9707 \u5440\u4e1c\u65b9\u9707\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\u8fd9\u4e48\u56de\u4e8b\u5440\uff01\u201d \u3000\u3000\u4e1c\u65b9\u9707\u9762\u4e0a\u500f\u9752\u500f\u767d\uff0c\u7d27\u63e1\u7740\u5251\u67c4\u7684\u624b\u638c\uff0c\u4e5f\u9690\u9690\u66b4\u51fa\u4e86\u9752\u7b4b\uff0c\u56db\u4e0b\u7684\u4eba\u7fa4\uff0c\u4e00\u5c42\u4e00\u5c42 \u5730\u5c06\u4ed6\u4eec\u56f4\u5728\u4e2d\u95f4\uff0c\u4ed6\u4eec\u8d70\u4e5f\u4e0d\u662f\uff0c\u4e0d\u8d70\u4e5f\u4e0d\u662f\uff0c\u6012\u4e5f\u4e0d\u662f\uff0c\u4e0d\u6012\u4e5f\u4e0d\u662f\uff0c\u5f53\u771f\u4e0d\u77e5\u8be5\u5982\u4f55 \u662f\u597d\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u76ee\u5149\u4e00\u95ee\uff0c\u5fc3\u4e2d\u5df2\u77e5\u9053\u8fd9\u4e9b\u547c\u558a\u5fc5\u5b9a\u4e5f\u662f\u201c\u795e\u624b\u201d\u6218\u98de\u5b89\u6392\u5e03\u7f6e\u4e0b\u7684\u624b \u6bb5\uff0c\u8981\u4f7f\u88f4\u73cf\u4e0e\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u7ed3\u4e0b\u4ec7\u6028\uff0c\u751a\u81f3\u5c31\u5728\u6b64\u5730\u62fc\u6597\u4e00\u4e0b\uff0c\u9e6c\u868c\u76f8\u4e89\uff0c\u81ea\u7136\u662f\u6e14\u7fc1\u5f97 \u5229\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5144\u5f1f\u5374\u4e5f\u60f3\u4e0d\u51fa\u4efb\u4f55\u65b9\u6cd5\u6765\u6253\u5f00\u6b64\u523b\u7684\u50f5\u5c40\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u751f\u6027\u6700\u662f\u6c89\u7a33\uff0c\u6b64\u523b\u5374\u4e5f\u4e0d\u7981\u4e71\u4e86\u65b9\u5bf8\uff0c\u5fae\u4e00\u6c89\u541f\uff0c\u5927\u559d\u9053\uff1a\u201c\u54ea\u4f4d\u670b\u53cb\u8981\u60f3\u8bf4 \u8bdd\uff0c\u4e0d\u59a8\u5230\u8fd9\u91cc\u6765\u8bf4\u4e2a\u660e\u767d\uff0c\u8fd9\u6837\u2014\u2014\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53c8\u6709\u4eba\u559d\u9053\uff1a\u201c\u4f60\u5144\u5f1f\u4e94\u4eba\u6709\u4e2a\u597d\u7238 \u7238\uff0c\u53c8\u90fd\u6709\u4e2a\u597d\u5e08\u5085\uff0c\u6211\u4eec\u5fc3\u91cc\u867d\u7136\u6c14\u6124\uff0c\u53ef\u4e5f\u60f9\u4e0d\u8d77\u4f60\u4eec\u3002\u201d \u3000\u3000\u7acb\u523b\u6709\u4eba\u63a5\u7740\u558a\u9053\uff1a\u201c\u8fde\u2018\u9f99\u5f62\u516b\u638c\u2019\u90fd\u8981\u62cd\u4f60\u4eec\u7684\u9a6c\u5c41\uff0c\u53ea\u53ef\u60dc\u88f4\u5927\u5148\u751f\u4e00\u8868\u4eba\u6750\uff0c \u6587\u6b66\u53cc\u5168\uff0c\u5c31\u56e0\u4e3a\u6ca1\u6709\u540e\u53f0\uff0c\u7ade\u88ab\u4eba\u62c6\u6563\u9e33\u9e2f\u3002\u201d \u3000\u3000\u53c8\u6709\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u2018\u98de\u7075\u5821\u2019\u4e00\u5411\u4ee5\u4fa0\u4e49\u81ea\u5c45\uff0c\u60f3\u4e0d\u5230\u7adf\u505a\u51fa\u4e86\u8fd9\u6837\u7684\u4e8b\u6765\uff01\u201d \u3000\u3000\u4e1c\u65b9\u4e94\u5251\u95f0\u5149\u5a01\u5bd2\uff0c\u88f4\u73cf\u9762\u4e0a\u4e5f\u6536\u655b\u4e86\u7b11\u5bb9\u3002 \u3000\u3000\u7a81\u89c1\u4e1c\u65b9\u6c5f\u3001\u4e1c\u65b9\u6e56\u8fd9\u5b6a\u751f\u5144\u5f1f\u4e24\u4eba\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u63a0\u5230\u88f4\u73cf\u8eab\u524d\uff0c\u5e74\u7eaa\u6700\u8f7b\uff0c\u706b\u6c14\u6700\u76db \u7684\u4e1c\u65b9\u6e56\u51b7\u7b11\u4e00\u58f0\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u4e9b\u65e0\u803b\u7684\u5c0f\u4eba\uff0c\u53ef\u662f\u9601\u4e0b\u5b89\u6392\u5728\u8def\u4e0a\u7684\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u4f4e\u53f1\u4e00\u58f0\uff1a\u201c\u4e94\u5f1f\uff01\u201d \u3000\u3000\u4f46\u4ed6\u963b\u6b62\u5df2\u81ea\u4e0d\u53ca\uff0c\u88f4\u73cf\u9762\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5144\u53f0\u7684\u8bdd\uff0c\u5144\u5f1f\u6709\u4e9b\u542c\u4e0d\u61c2\u3002\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u4ef0\u5929\u51b7\u7b11\u6570\u58f0\uff0c\u7a81\u5730\u201c\u545b\u54ea\u201d\u62d4\u51fa\u5251\u6765\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u4e1c\u65b9\u6e56\u4e0d\u51ed\u5e08\u95e8\uff0c\u4e0d\u4ed7\u7236 \u5144\uff0c\u5012\u8981\u5355\u72ec\u4e0e\u4f60\u8fd9\u88f4\u5927\u5148\u751f\u6597\u4e0a\u4e00\u6597\uff0c\u770b\u4f60\u5230\u5e95\u6709\u4ec0\u4e48\u60ca\u4eba\u7684\u6587\u624d\u6b66\u827a\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u5251\u7709\u6df1\u76b1\uff0c\u53f9\u9053\uff1a\u201c\u4e94\u5f1f\uff0c\u4f60\u8fd9\u662f\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u56db\u4e0b\u5df2\u54cd\u8d77\u4e00\u7247\u66b4\u559d\u4e4b\u58f0\uff1a\u201c\u6253\uff01\u6253\uff01\u5c31\u6253\u6b7b\u8fd9\u5c0f\u5b50\uff0c\u770b\u4ed6\u7684\u5e08\u5085\u3001\u7236\u5144\u600e \u4e48\u6837\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u76ee\u5149\u4e00\u626b\uff0c\u53ea\u89c1\u88f4\u73cf\u6728\u7acb\u5f53\u5730\uff0c\u65e2\u4e0d\u56de\u7b54\uff0c\u4ea6\u4e0d\u89e3\u91ca\uff0c\u4ed6\u5fc3\u5934\u4ea6\u4e0d\u7981\u6cdb\u8d77\u4e00\u9635\u6000 \u7591\u4e0e\u6012\u6c14\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff0c\u201c\u88f4\u5144\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u4f46\u8bf8\u5144\u53f0\u89e3\u91ca\u4e00\u53e5\u3002\u201d \u3000\u3000\u88f4\u73cf\u7a81\u7136\u5730\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5144\u53f0\u8981\u6211\u89e3\u91ca\uff0c\u6211\u5374\u8fd8\u4e0d\u77e5\u9053\u8981\u8c01\u89e3\u91ca\u5462\u5e7f\u4e1c\u65b9\u6e56\u624b\u8155\u4e00 \u6296\uff0c\u5251\u5149\u7acb\u957f\uff0c\u51e0\u4e4e\u8981\u523a\u5230\u88f4\u73cf\u9762\u4e0a\u3002\u88f4\u73cf\u53d8\u8272\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u613f\u4e0e\u5144\u53f0\u4eec\u76f8\u4e89\uff0c\u4e00\u6765\u662f\u4e3a\u4e86 \u4e0e\u5144\u53f0\u4eec\u7d20\u65e0\u4ec7\u6028\uff0c\u518d\u6765\u5374\u662f\u4e0d\u613f\u88ab\u8fd9\u822c\u6697\u4e2d\u7834\u574f\u4e4b\u4eba\u5982\u613f\uff0c\u4f46\u5144\u53f0\u5374\u4e0d\u53ef\u6b3a\u4eba\u592a\u751a\uff0c\u81f3\u5c11 \u4e5f\u8be5\u5c06\u4e8b\u60c5\u5224\u65ad\u6e05\u695a\u624d\u662f\u3002\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u4e00\u628a\u62c9\u5f00\u4e86\u4ed6\u7684\u4e94\u5f1f\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u5144\u5f1f\u6b64\u6b21\u6e21\u6c5f\u5317\u6765\uff0c\u4ea6\u4e0d\u8fc7\u662f\u4e3a\u4e86\u8981\u5c06\u4e8b\u60c5 \u67e5\u95ee\u6e05\u695a\uff0c\u5e76\u975e\u7edd\u5bf9\u8981\u4e0e\u6a80\u5bb6\u7ed3\u4e0b\u4eb2\u4e8b\uff0c\u4f46\u5144\u53f0\u2014\u2014\u201d\u88f4\u73cf\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5144\u53f0 \u4eec\u662f\u5426\u8981\u4e0e\u6a80\u6587\u742a\u7ed3\u4eb2\uff0c\u4e0e\u6211\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u51b7\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u5173\u7cfb\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u5f97\u5fc3\u5934\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u53ea\u56e0\u8fd9\u51b2\u52a8\u7684\u5c11\u5e74\uff0c\u5b9e\u5728\u89e6\u53ca\u4e86\u4ed6\u5fc3\u4e2d\u7684\u4f24\u5fc3\u4e4b\u4e8b\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u76ee\u5149\u4e00\u626b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e5f\u8981\u6559\u597d\u4eba\u5f97\u610f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u51db\uff0c\u633a\u8d77\u7684\u80f8\u819b\uff0c\u4fbf\u53c8\u5f2f\u843d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u542c\u4eba\u4e1b\u5916\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff1a\u201c\u88f4\u5927\u5148\u751f\uff0c\u4f60\u600e\u5730\u5982\u6b64\u8f6f\u5f31\uff0c\u88ab\u4eba\u5982\u6b64\u6b3a\u8d1f\u4e86\uff0c\u8fd8\u4e0d\u6562\u52a8 \u624b\uff0c\u96be\u9053\u4f60\u4e5f\u6015\u4e86\u4ed6\u4eec\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u51b7\u7b11\u9053\uff1a\u201c\u6709\u8fd9\u8bb8\u591a\u85cf\u5934\u9732\u5c3e\uff0c\u89c1\u4e0d\u5f97\u4eba\u7684\u9f20\u8f88\u5728\u4e3a\u4f60\u5450\u558a\uff0c\u4f60\u8fd8\u6015\u8c01\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff0c\u56de\u9996\u671b\u4e86\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e00\u773c\uff0c\u811a\u6b65\u7f13\u7f13\u79fb\u52a8\uff0c\u4f3c\u4e4e\u8981\u5411\u4eba\u4e1b\u4e2d\u8d70\u53bb\u3002 \u3000\u3000\u7a81\u542c\u4e00\u58f0\u5927\u559d\uff1a\u201c\u5927\u516c\u5b50\uff0c\u4e8c\u516c\u5b50\uff0c\u662f\u8fd9\u4eba\u5728\u8fd9\u91cc\u4e71\u53eb\uff0c\u5feb\u2014\u2014\u201d\u559d\u58f0\u672a\u4e86\uff0c\u53c8\u662f\u4e00\u58f0 \u60e8\u547c\uff01 \u3000\u3000\u4e1c\u65b9\u94c1\u53d8\u8272\u9053\uff1a\u201c\u7ba1\u4e8c\uff01\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u957f\u5251\u4e00\u6325\uff0c\u8eab\u5f62\u63a0\u8d77\uff0c\u4f46\u91cc\u91cc\u5916\u5916\u4ff1\u662f\u4eba\u7fa4\uff0c\u4ed6\u957f\u5578\u4e00\u58f0\uff0c\u957f\u5251\u518d\u6b21\u4e00\u65cb\uff0c\u5e73\u7a7a \u81ea\u4eba\u5934\u4e0a\u98de\u63a0\u4e86\u8fc7\u53bb\u3002\u8fd9\u51fa\u8eab\u6b66\u6797\u4e16\u5bb6\uff0c\u53c8\u5f97\u660e\u5e08\u4f20\u6388\u7684\u5c11\u5e74\uff0c\u679c\u7136\u6000\u6709\u4e00\u8eab\u6c5f\u6e56\u7f55\u89c1\u7684\u7edd \u6280\u3002 \u3000\u3000\u88f4\u73cf\u987f\u4f4f\u811a\u6b65\uff0c\u4e1c\u65b9\u9707\u4f3c\u4e4e\u4ea6\u5f85\u63a0\u8d77\uff0c\u4e1c\u65b9\u94c1\u9053\uff1a\u201c\u6709\u4e94\u4e2a\u4eba\u4e2d\u4e00\u4eba\u53bb\u8ffd\u8db3\u591f\u4e86\uff01\u201d \u3000\u3000\u4e1c\u65b9\u6c5f\u5389\u58f0\u9053\uff1a\u201c\u82e5\u662f\u6349\u4f4f\u4e86\u90a3\u4eba\uff0c\u6211\u5012\u8981\u770b\u770b\u4ed6\u7a76\u7adf\u662f\u4ec0\u4e48\u53d8\u7684\uff1f\u201d \u3000\u3000\u4eba\u7fa4\u53c8\u89c1\u7eb7\u4e71\uff0c\u53ea\u542c\u56db\u4e0b\u7684\u811a\u6b65\u58f0\uff0c\u5f80\u6765\u5954\u8d70\u4e0d\u7edd\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4eba\u4e1b\u5206\u5f00\u4e00\u6761\u901a\u8def\uff0c\u4e1c\u65b9\u6e56\u9762\u5bd2\u5982\u51b0\uff0c\u4e00\u6b65\u4e00\u6b65\u8d70\u4e86\u8fdb\u6765\uff0c\u957f\u5251\u5df2\u7136\u5165\u9798\uff0c\u53cc\u624b \u5374\u5e73\u628a\u7740\u4e00\u5177\u5c38\u8eab\u3002 \u3000\u3000\u4e1c\u65b9\u5251\u60ca\u547c\u9053\uff1a\u201c\u7ba1\u4e8c\uff1f\u662f\u7ba1\u4e8c\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u4e00\u8a00\u4e0d\u53d1\u5730\u5c06\u90a3\u5177\u5c38\u8eab\u653e\u5728\u5730\u4e0a\uff0c\u5374\u5728\u5c38\u8eab\u7684\u80f8\u819b\u4e4b\u4e0a\uff0c\u62d4\u51fa\u4e86\u4e00\u67c4\u5315\u9996\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u53f9\u9053\uff1a\u201c\u679c\u7136\u662f\u7ba1\u4e8c\uff0c\u4ed6\u5fc5\u5b9a\u662f\u53d1\u73b0\u4e86\u547c\u558a\u4e4b\u4eba\uff0c\u60f3\u4e0d\u5230\u5374\u906d\u4e86\u90a3\u4eba\u7684\u6bd2\u624b\u3002\u201d \u3000\u3000\u4e1c\u65b9\u6c5f\u4e00\u6b65\u8d76\u5230\u4e1c\u65b9\u6e56\u8eab\u524d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u51f6\u624b\u5462\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u9707\u51b7\u51b7\u9053\uff1a\u201c\u6b64\u65f6\u6b64\u5730\uff0c\u4fbf\u6709\u4e00\u5343\u4e2a\u51f6\u624b\u4e5f\u5c3d\u53ef\u5728\u4eba\u4e1b\u4e2d\u9690\u85cf\u8d77\u6765\u3002\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u4e00\u76f4\u7559\u5fc3\u5bdf\u770b\u90a3\u67c4\u5315\u9996\uff0c\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff0c\u624b\u8155\u4e00\u626c\uff0c\u4e00\u9053\u5bd2\u5149\uff0c\u96f7\u5c04\u800c\u51fa\uff0c\u76f4\u51fb \u88f4\u73cf\u7684\u80f8\u819b\u3002 \u3000\u3000\u88f4\u73cf\u5251\u7709\u5fae\u8f69\uff0c\u8eab\u5f62\u4e0d\u52a8\uff0c\u51fa\u624b\u5982\u98ce\uff0c\u98df\u3001\u4e2d\u4e24\u6307\u5e76\u6307\u4e00\u5939\uff0c\u5c06\u5315\u9996\u5939\u5728\u638c\u4e2d\uff0c\u8f7b\u53f1 \u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u53cc\u76ee\u5706\u7741\uff0c\u6ee1\u9762\u6012\u6c14\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u4e14\u770b\u770b\u90a3\u4e0a\u9762\u7684\u5b57\u8ff9\uff0c\u4f60\u4e14\u770b\u770b\u90a3\u662f\u4e0d\u662f\u4f60 \u2018\u6c5f\u5357\u540c\u76df\u2019\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u6c5f\u5927\u559d\u4e00\u58f0\uff0c\u957f\u5251\u51fa\u9798\uff0c\u5237\u5730\u6325\u8d77\u4e00\u9053\u5251\u5149\uff0c\u524a\u5411\u88f4\u73cf\u80a9\u5934\u3002 \u3000\u3000\u88f4\u73cf\u5fae\u4e00\u9519\u6b65\uff0c\u53f3\u638c\u4e09\u6307\u634f\u7740\u5315\u9996\u5203\u5c16\uff0c\u8f7b\u8f7b\u5411\u4e0a\u4e00\u70b9\uff0c\u53ea\u89c1\u201c\u55e1\u201d\u7136\u4e00\u58f0\uff0c\u957f\u5251\u5f39\u8d77 \u6570\u5bf8\u3002 \u3000\u3000\u4e1c\u65b9\u6c5f\u5389\u53f1\u9053\uff1a\u201c\u597d\uff01\u518d\u63a5\u6211\u8fd9\u4e00\u62db\uff01\u201d \u3000\u3000\u5237\u5730\u53c8\u662f\u4e00\u5251\u524a\u53bb\uff0c\u4e1c\u65b9\u94c1\u51fa\u624b\u5982\u51e4\uff0c\u75be\u5730\u6258\u4f4f\u4e86\u4ed6\u56db\u5f1f\u7684\u624b\u8155\uff0c\u8f7b\u53f1\u9053\uff1a\u201c\u4e0d\u53ef\u5984 \u52a8\uff0c\u6559\u670b\u53cb\u4eec\u803b\u7b11\uff01\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u65b9\u81ea\u4eba\u9798\u7684\u957f\u5251\uff0c\u91cd\u53c8\u62d4\u51fa\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u803b\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5251\u5149\u9563\u7ed5\uff0c\u5de6\u524a\u53f3\u5241\uff0c\u5237\u5730\u4e24\u5251\uff0c\u51fb\u5411\u88f4\u73cf\u7684\u5de6\u80a9\u53f3\u9888\u3002\u4ed6\u6027\u60c5\u521a\u66b4\uff0c\u7528\u7684\u5251\u6cd5\u4ea6\u662f\u70ed \u82e5\u96f7\u9706\uff0c\u56db\u4e0b\u4eba\u4e1b\u60ca\u5524\u4e00\u58f0\uff0c\u524d\u9762\u7684\u4eba\u5411\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u4f46\u540e\u9762\u7684\u4eba\u7fa4\u5374\u53c8\u5c06\u4ed6\u4eec\u6d8c\u4e0a\u524d\u6765\u3002 \u3000\u3000\u88f4\u73cf\u8eab\u8eaf\u4e00\u95ea\uff0c\u907f\u5f00\u4e86\u8fd9\u4e00\u62db\u4e24\u5f0f\uff0c\u4e1c\u65b9\u6e56\u5251\u52bf\u4e00\u8f6c\uff0c\u523a\u5411\u4ed6\u524d\u5fc3\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u53d8\u52bf\u4e4b\u5feb\uff0c\u66f4\u662f\u5feb\u5982\u95ea\u7535\uff0c\u4f46\u89c1\u4e00\u7f15\u9752\u5149\u4e4d\u8d77\uff0c\u4fbf\u5df2\u582a\u582a\u89e6\u7740\u88f4\u73cf\u80f8\u524d\u7684\u8863\u886b\u3002 \u3000\u3000\u88f4\u73cf\u80f8\u8179\u4e00\u7f29\uff0c\u84e6\u7136\u5411\u540e\u79fb\u5f00\u534a\u5c3a\uff0c\u4e1c\u65b9\u6e56\u5389\u53f1\u9053\uff1a\u201c\u8fd8\u624b\uff01\u4f60\u96be\u9053\u4e0d\u6562\u8fd8\u624b\u4e48\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u4e4b\u4e2d\uff0c\u53c8\u662f\u8fde\u73af\u4e09\u5251\uff0c\u523a\u5411\u88f4\u73cf\u201c\u5929\u67a2\u201d\u3001\u201c\u91cd\u8840\u201d\uff0c\u201c\u5c06\u53f0\u201d\u4e09\u5904\u5927\u7a74\uff0c\u5b9b\u5982\u4e09 \u67c4\u957f\u5251\uff0c\u540c\u65f6\u523a\u51fa\u3002 \u3000\u3000\u88f4\u73cf\u51b7\u7b11\u4e00\u58f0\uff0c\u811a\u6b65\u4e00\u6e9c\uff0c\u659c\u659c\u5411\u524d\u51b2\u5f00\u4e09\u5c3a\uff0c\u4e1c\u65b9\u94c1\u987f\u8db3\u9053\uff1a\u201c\u7531\u5f97\u4f60\u4eec\uff0c\u7531\u5f97\u4f60 \u4eec\uff01\u201d\u677e\u5f00\u4e1c\u65b9\u6c5f\u7684\u624b\u8155\uff0c\u8fdc\u8fdc\u9000\u5230\u4e00\u8fb9\u3002\u201c\u51b7\u8c37\u53cc\u6728\u201d\u888d\u8896\u4e00\u62c2\uff0c\u75be\u5730\u6321\u5728\u88f4\u73cf\u8eab\u524d\u3002 \u3000\u3000\u4e1c\u65b9\u6c5f\u3001\u4e1c\u65b9\u6e56\u53cc\u5251\u4e00\u9519\uff0c\u559d\u9053\uff1a\u201c\u95ea\u5f00\uff01\u201d \u3000\u3000\u4e24\u9053\u9752\u5149\u4ea4\u526a\u800c\u81f3\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8eab\u5f62\u4e00\u4fa7\uff0c\u4ed6\u4eec\u4fbf\u53c8\u51b2\u5230\u88f4\u73cf\u8eab\u524d\u3002\u7a81\u542c\u4eba\u4e1b\u5916\u4e00\u58f0 \u51b7\u7b11\uff0c\u9053\uff1a\u201c\u597d\u611a\u8822\u7684\u5974\u624d\uff01\u201d \u3000\u3000\u8fd9\u8bed\u58f0\u867d\u4e0d\u751a\u9ad8\uff0c\u4f46\u58f0\u97f3\u7ef5\u7ef5\u5bc6\u5bc6\uff0c\u7adf\u4f3c\u5728\u4e1c\u65b9\u5144\u5f1f\u4e94\u4eba\u7684\u8033\u7554\u53d1\u51fa\uff0c\u4e1c\u65b9\u5144\u5f1f\u51fa\u8eab\u540d \u95e8\uff0c\u4eba\u8033\u4fbf\u77e5\u8bf4\u8bdd\u4e4b\u4eba\u5b9a\u662f\u5185\u529f\u4fee\u4e3a\u5df2\u5165\u5316\u5883\u7684\u6b66\u6797\u7edd\u9876\u9ad8\u624b\uff0c\u5144\u5f1f\u4e94\u4eba\u4e0d\u7981\u9f50\u5730\u4e3a\u4e4b\u4e00 \u60ca\u3002 \u3000\u3000\u4e1c\u65b9\u6c5f\u3001\u4e1c\u65b9\u6e56\u5251\u5149\u4e00\u632b\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u7a81\u89c1\u4e00\u56e2\u9ed1\u5f71\uff0c\u81ea\u4eba\u4e1b\u5916\u6a2a\u98de\u800c\u81f3\uff0c\u6765\u52bf\u4e4b\u5feb\uff0c \u6709\u5982\u5954\u96f7\u3002 \u3000\u3000\u4eba\u7fa4\u4e00\u58f0\u60ca\u547c\uff0c\u4e1c\u65b9\u5144\u5f1f\u4ea6\u4e0d\u7981\u8ba9\u5f00\u4e09\u6b65\uff0c\u53ea\u89c1\u8fd9\u56e2\u9ed1\u5f71\uff0c\u201c\u6251\u201d\u5730\u843d\u5230\u5730\u4e0a\uff0c\u7adf\u662f\u88ab \u4eba\u70b9\u4e2d\u4e86\u7a74\u9053\u7684\u9ed1\u8863\u5927\u6c49\u3002 \u3000\u3000\u8fd9\u9ed1\u8863\u5927\u6c49\u88ab\u4eba\u81ea\u4eba\u4e1b\u4e4b\u5916\u8fdc\u8fdc\u63b7\u6765\uff0c\u6765\u52bf\u90a3\u822c\u60ca\u4eba\u8fc5\u5feb\uff0c\u4f46\u843d\u5730\u4e4b\u540e\uff0c\u5374\u6beb\u65e0\u4f24\u635f\uff0c \u88ab\u63b7\u51fa\u8fd9\u9ed1\u8863\u5927\u6c49\u4e4b\u4eba\u5185\u529b\u4e4b\u5f3a\u52b2\uff0c\u624b\u6cd5\u4e4b\u5de7\u5999\uff0c\u53c8\u5c82\u662f\u6c5f\u6e56\u4e2d\u4e00\u822c\u6b66\u6797\u9ad8\u624b\u6240\u80fd\u671b\u5176\u80cc \u9879\uff01 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u5fc3\u5934\u66f4\u662f\u5927\u60ca\uff0c\u88f4\u73cf\u3001\u201c\u51b7\u8c37\u53cc\u6728\u201d\u9762\u4e0a\u4e5f\u4e3a\u4e4b\u53d8\u8272\uff0c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e2d\uff0c\u6709\u6b64\u5185 \u529b\uff0c\u6709\u6b64\u624b\u6cd5\u4e4b\u4eba\uff0c\u5b9e\u662f\u5be5\u5be5\u65e0\u51e0\uff0c\u4e1c\u65b9\u5251\u6c89\u58f0\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u5251\u7709\u5fae\u76b1\uff0c\u62b1\u62f3\u9ad8\u547c\u9053\uff1a\u201c\u662f\u54ea\u4f4d\u524d\u8f88\u9ad8\u4eba\u5149\u4e34\u6b64\u95f4\uff0c\u4e0d\u59a8\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u65b9\u624d\u90a3\u5185\u529b\u60a0\u957f\u3001\u4e2d\u6c14\u7ef5\u5bc6\u7684\u8bed\u58f0\u4fbf\u53c8\u5728\u4ed6\u5144\u5f1f\u4e94\u4eba\u7684\u8033\u7554\u54cd\u8d77\uff0c\u4e00\u5b57\u4e00\u5b57 \u5730\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u5206\u662f\u8c01\uff0c\u4e0d\u67e5\u7a76\u7adf\uff0c\u59d4\u5b9e\u660f\u5eb8\u611a\u8822\u4e4b\u6781\uff0c\u6211\u4e14\u5c06\u90a3\u4e9b\u5450\u558a\u4e4b\u4eba\u6293\u6765\u7ed9\u4f60\uff0c \u8ba9\u4f60\u770b\u770b\u4ed6\u4eec\u7a76\u7adf\u662f\u4f55\u4eba\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u6b21\u8bed\u58f0\u8fdc\u8f83\u4e0a\u6b21\u54cd\u4eae\uff0c\u6709\u5982\u9ed1\u591c\u4e4b\u4e2d\u65f7\u91ce\u4e0a\u539f\u59cb\u7684\u9f13\u58f0\uff0c\u56db\u4e0b\u4eba\u4e1b\u4e00\u9635\u5927\u4e71\uff0c\u90a3\u4e9b \u5728\u6697\u4e2d\u5450\u558a\u4e4b\u4eba\u90fd\u4e0d\u7981\u88ab\u8fd9\u8bed\u58f0\u6240\u60ca\uff0c\u5fc3\u865a\u80c6\u602f\u4e4b\u4e0b\uff0c\u60c5\u4e0d\u81ea\u7981\u5730\u6295\u8db3\u98de\u5954\uff0c\u5411\u56db\u9762\u516b\u65b9\u9003 \u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u811a\u6b65\u65b9\u52a8\uff0c\u5c4b\u6a90\u4e0b\u4fbf\u7a81\u5730\u98de\u8d77\u4e24\u6761\u4eba\u5f71\uff0c\u6709\u5982\u7ecf\u5929\u957f\u8679\u822c\u56db\u4e0b\u4e00\u8f6c\uff0c\u957f\u8857\u4e0a\u7684\u6570 \u5343\u53cc\u773c\u775b\uff0c\u7adf\u65e0\u4e00\u4eba\u80fd\u770b\u51fa\u8fd9\u4e24\u4e2a\u8eab\u5f62\u9762\u8c8c\uff0c\u4f46\u89c1\u4ed6\u4e24\u4eba\u8eab\u5f71\u5230\u5904\uff0c\u4fbf\u6709\u4e00\u58f0\u60ca\u547c\uff0c\u4fbf\u6709\u4e00 \u6761\u9ed1\u5f71\u6a2a\u7a7a\u98de\u8d77\uff0c\u843d\u5165\u4eba\u7fa4\u5305\u56f4\u7740\u7684\u90a3\u4e00\u56e2\u7a7a\u5730\u91cc\u3002 \u3000\u3000\u4eba\u4e1b\u4e2d\u7684\u88f4\u73cf\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u3001\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u60ca\u6115\u4e0d\u5df2\u3002\u53ea\u89c1\u5341\u6570\u6761\u9ed1\u5f71\u56db\u9762\u516b\u65b9\u7684\u63a0 \u7a7a\u98de\u6765\uff0c\u201c\u7830\u201d\u5730\u843d\u5230\u5730\u4e0a\uff0c\u8fd9\u4e9b\u9ed1\u5f71\u88ab\u63b7\u6765\u7684\u65b9\u5411\u90fd\u4e0d\u4e00\u6837\uff0c\u4f46\u5374\u51e0\u4e4e\u5728\u540c\u65f6\u843d\u4e86\u4e0b\u6765\uff01 \u3000\u3000\u4e1c\u65b9\u6c5f\u3001\u4e1c\u65b9\u6e56\u80a9\u5934\u4e00\u8038\uff0c\u8dc3\u8d77\u4e00\u4e08\uff0c\u4f46\u89c1\u4e24\u6761\u7070\u5f71\u51cc\u7a7a\u4e00\u95ea\uff0c\u4fbf\u6ca1\u5165\u8fdc\u65b9\uff0c\u6709\u5982\u5929\u9645 \u795e\u9f99\u4e00\u822c\uff0c\u89c1\u5176\u9996\u800c\u4e0d\u89c1\u5176\u5c3e\u3002 \u3000\u3000\u8fd9\u79cd\u9a87\u4eba\u542c\u95fb\u7684\u8f7b\u529f\u8eab\u624b\uff0c\u56db\u4e0b\u7fa4\u8c6a\u5f53\u771f\u662f\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\u3002\u4e1c\u65b9\u5144\u5f1f\u867d\u7136\u51fa\u8eab\u540d \u95e8\u4e16\u5bb6\uff0c\u5e08\u957f\u7236\u5144\u4ff1\u662f\u5f53\u65f6\u6b66\u6797\u4e2d\u7684\u7edd\u9876\u4eba\u7269\uff0c\u4f46\u89c1\u4e86\u8fd9\u4e24\u4eba\u7684\u8f7b\u529f\u8eab\u624b\uff0c\u4ea6\u4e0d\u7981\u6697\u6697\u5fc3 \u60ca\u3002 \u3000\u3000\u88f4\u73cf\u76ee\u5149\u95ea\u52a8\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u7684\u8eab\u5f71\u5fae\u5fae\u4e00\u95ea\uff0c\u7136\u800c\u4ed6\u5fc3\u6cd5\u52a8\u5904\uff0c\u5374\u7a81\u5730\u60f3\u8d77\u4e24\u4e2a\u4eba\u6765\uff0c \u9762\u4e0a\u4e0d\u7981\u6cdb\u8d77\u4e00\u4e1d\u7b11\u5bb9\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u4e00\u628a\u6293\u4f4f\u4e00\u6761\u9ed1\u8863\u5927\u6c49\u7684\u8863\u895f\uff0c\u51fa\u624b\u4e09\u638c\uff0c\u62cd\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u53ea\u89c1\u8fd9\u5927\u6c49\u9762\u4e0a\u6ee1 \u662f\u60ca\u9a87\u4e4b\u5bb9\uff0c\u76ee\u5149\u95fd\u7f29\u4e0d\u5b9a\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u9976\u547d\u2026\u2026\u5c0f\u4eba\u6ca1\u2026\u2026\u6709\u8bf4\u4ec0\u4e48\u3002\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u51b7\u7b11\u4e00\u58f0\uff0c\u5e73\u5251\u4e00\u62cd\uff0c\u62cd\u5728\u4ed6\u80a9\u80bf\u9aa8\u4e0a\uff0c\u53ea\u75db\u5f97\u8fd9\u5927\u6c49\u60e8\u547c\u4e00\u58f0\uff0c\u6ee1\u5934\u51b7\u6c57\u6d94\u6d94 \u843d\u4e0b\u3002 \u3000\u3000\u4e1c\u65b9\u6c5f\u5251\u7709\u6012\u8f69\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8c01\u7684\u95e8\u4e0b\uff0c\u53d7\u4e86\u8c01\u7684\u6307\u4f7f\uff1f\u5728\u6211\u6570\u5230\u2018\u4e09\u2019\u5b57\u4ee5\u524d\uff0c \u5feb\u4e9b\u4e0e\u6211\u4e56\u4e56\u8bf4\u51fa\u6765\uff0c\u5426\u5219\u6211\u5c31\u523a\u7a7f\u4f60\u7684\u7435\u7436\u53cc\u9aa8\uff0c\u523a\u778e\u4f60\u7684\u4e00\u53cc\u773c\u6674\u3002\u201d \u3000\u3000\u4ed6\u5251\u5149\u4e00\u5c55\uff0c\u98a4\u52a8\u7684\u5251\u5c16\uff0c\u4fbf\u62b5\u5728\u8fd9\u5927\u6c49\u7684\u7709\u4e0b\u776b\u4e0a\uff0c\u53ea\u8981\u4ed6\u624b\u8155\u5fae\u5fae\u4e00\u6296\uff0c\u8fd9\u5927\u6c49\u7acb \u65f6\u4fbf\u6709\u76ee\u76f2\u8840\u6e85\u4e4b\u7978\u3002 \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff0c\u4f3c\u4e4e\u60f3\u5230\u4ec0\u4e48\uff0c\u5374\u53c8\u7ec8\u4e8e\u5fcd\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u4e1c\u65b9\u6c5f\u51b7\u51b7\u9053\uff1a\u201c\u4e00\uff01\u201d \u3000\u3000\u9ed1\u8863\u5927\u6c49\u4f46\u89c9\u6ee1\u9762\u5bd2\u6c14\uff0c\u5168\u8eab\u98a4\u6296\uff0c\u52a8\u4e5f\u4e0d\u6562\u52a8\u4e00\u4e0b\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5c0f\u4eba\u6ca1\u2026\u2026\u6ca1 \u6709\u2026\u2026\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u671b\u4e5f\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e8c\uff01\u201d \u3000\u3000\u9ed1\u8863\u5927\u6c49\u9762\u5bb9\u66f4\u52a0\u82cd\u767d\uff0c\u7a81\u5730\u5927\u558a\u9053\uff1a\u201c\u6211\u8bf4\uff0c\u6211\u8bf4\u2026\u2026\u201d \u3000\u3000\u4e1c\u65b9\u6c5f\u51b7\u7b11\u4e00\u58f0\uff0c\u6536\u56de\u957f\u5251\uff0c\u8fd9\u9ed1\u8863\u5927\u6c49\u6251\u5730\u5750\u5230\u5730\u4e0a\uff0c\u98a4\u6296\u7740\u4f38\u51fa\u624b\u638c\uff0c\u62b9\u4e86\u62b9\u989d\u4e0a \u6c57\u73e0\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u5c0f\u4eba\u2026\u2026\u5c0f\u4eba\u662f\u2018\u4e03\u5de7\u5c71\u5e84\u2019\u90a3\u5e84\u4e3b\u7684\u624b\u4e0b\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e00\u8bf4\u51fa\u6765\uff0c\u88f4\u73cf\u3001\u201c\u51b7\u8c37\u53cc\u6728\u201d\u3001\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u4ff1\u90fd\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\uff0c\u8be7\u58f0\u9053\uff1a \u201c\u539f\u6765\u662f\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\u7684\u624b\u4e0b\uff1a\u201c\u56db\u4e0b\u7fa4\u8c6a\uff0c\u7acb\u65f6\u4e3a\u4e4b\u5927\u54d7\uff0c\u4eba\u4eba\u4ff1\u90fd\u4ee5\u4e3a\uff0c\u8fd9\u5fc5\u5b9a\u662f\u201d\u795e \u624b\u201c\u6218\u98de\u6240\u5b9a\u4e0b\u7684\u79bb\u95f4\u6311\u62e8\u4e4b\u7b56\uff0c\u5374\u60f3\u4e0d\u5230\u8fd9\u662f\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u90a3\u98de\u8679\u7684\u4e00\u77f3\u56db\u9e1f\u7684\u8fde\u73af\u6bd2 \u8ba1\uff0c\u88f4\u73cf\u4e0e\u201d\u4e1c\u65b9\u4e94\u5251\u201c\u82e5\u662f\u706b\u62fc\u8d77\u6765\uff0c\u5b9a\u7136\u8981\u4e24\u8d25\u4ff1\u4f24\u3002\u90a3\u4e48\u201d\u9f99\u5f62\u516b\u638c\u201c\u56fa\u7136\u53d7\u5bb3\u9887 \u6df1\uff0c\u4f46\u5b9a\u4f1a\u4ee5\u4e3a\u8fd9\u662f\u201d\u795e\u624b\u201c\u6218\u98de\u7684\u624b\u6bb5\uff0c\u6c5f\u6e56\u4e2d\u4eba\u4e5f\u4f1a\u4e0d\u803b\u4e8e\u201d\u795e\u624b\u201c\u6218\u98de\u7684\u5351\u9119\u3002\u51b7\u5bd2 \u7af9\u53cc\u7709\u4e00\u626c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e00\u77f3\u56db\u9e1f\uff0c\u4f24\u4eba\u65e0\u5f62\uff0c\u563f\u563f\uff0c\u597d\u5389\u5bb3\u7684\u8fde\u73af\u6bd2\u8ba1\uff01\u201d \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u5446\u5446\u5730\u8bc1\u4e86\u534a\u664c\uff0c\u659c\u76ee\u8198\u4e86\u88f4\u73cf\u4e00\u773c\uff0c\u4e00\u8d77\u907f\u5f00\u76ee\u5149\uff0c\u4e0d\u6562\u518d\u5411\u88f4\u73cf\u671b\u4e0a\u4e00 \u773c\u3002 \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u5ffd\u7136\u4fef\u4e0b\u8eab\u53bb\uff0c\u5411\u5730\u4e0a\u7684\u8fd9\u5341\u6570\u6761\u9ed1\u8863\u5927\u6c49\u8eab\u4e0a\uff0c\u5404\u5404\u62cd\u4e86\u4e09\u638c\uff0c\u4e1c\u65b9 \u6e56\u5fcd\u4e0d\u4f4f\u6c89\u58f0\u9053\uff1a\u201c\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u73ed\u4eba\u4ea6\u662f\u53d7\u4eba\u6307\u4f7f\uff0c\u8eab\u4e0d\u7531\u4e3b\uff0c\u6b64\u523b\u5927\u5bb6\u65e2\u7136\u77e5\u9053\u4e3b\u4f7f\u4e4b\u4eba\u662f\u8c01\uff0c \u5144\u53f0\u4e0e\u5c0f\u5f1f\u4ea6\u5404\u65e0\u4f24\u635f\uff0c\u4e0d\u5982\u5c06\u4ed6\u4eec\u653e\u4e86\u5427\uff01\u201d \u3000\u3000\u4e1c\u65b9\u6c5f\u9762\u988a\u4e00\u7ea2\uff0c\u518d\u4e0d\u8bf4\u8bdd\uff0c\u88f4\u73cf\u6325\u624b\u9053\uff1a\u201c\u53bb\uff01\u201d \u3000\u3000\u8fd9\u5341\u6570\u6761\u9ed1\u8863\u5927\u6c49\u5982\u9022\u5927\u8d66\uff0c\u9f50\u5730\u8dc3\u8d77\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u5411\u88f4\u73cf\u8eac\u8eab\u4e00\u793c\uff0c\u72fc\u72c8\u5730\u5411\u4eba\u4e1b\u4e2d \u9003\u7a9c\u800c\u53bb\uff0c\u6709\u4e9b\u597d\u4e8b\u4e4b\u5f92\u4e58\u673a\u5728\u4ed6\u4eec\u80cc\u4e0a\u6253\u4e86\u51e0\u638c\uff0c\u9a82\u4e0a\u51e0\u53e5\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u8fd8\u624b\u8fd8\u53e3\uff0c\u751a\u81f3 \u4e0d\u6562\u671b\u4e0a\u4e00\u773c\u3002 \u3000\u3000\u56db\u4e0b\u4eba\u7fa4\u4ecd\u5728\u6fc0\u52a8\uff0c\u4f46\u4eba\u4e1b\u4e2d\u7684\u4e1c\u65b9\u4e94\u5251\u53ca\u88f4\u73cf\u5374\u6709\u5982\u6728\u5851\u77f3\u96d5\u4e00\u822c\u6115\u5728\u5f53\u5730\uff0c\u8c01\u4e5f\u627e \u4e0d\u51fa\u4e00\u53e5\u8bdd\u6765\u8bf4\u3002 \u3000\u3000\u8fd9\u65f6\u5728\u62e5\u6324\u7684\u4eba\u4e1b\u4e2d\uff0c\u6b63\u6709\u4e00\u4e2a\u773c\u775b\u5927\u5927\u7684\u5973\u5b69\u5b50\uff0c\u95ea\u7f29\u5728\u9634\u6697\u4e4b\u5904\uff0c\u7559\u610f\u7740\u88f4\u73cf\u7684\u52a8 \u9759\u3002\u88f4\u73cf\u76ee\u5149\u4e00\u95ea\uff0c\u7a81\u5730\u77a5\u89c1\u4e86\u8fd9\u53cc\u773c\u775b\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u52a8\uff0c\u5306\u5306\u5411\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u62b1\u4e86\u62b1\u62f3\uff0c \u9053\uff1a\u201c\u5e78\u4f1a\uff01\u5e78\u4f1a\uff01\u201d \u3000\u3000\u4e1c\u65b9\u4e94\u5251\u9f50\u5730\u4e00\u6115\uff0c\u4e0b\u610f\u8bc6\u5730\u62f1\u4e86\u62f1\u624b\uff0c\u9053\uff1a\u201c\u5e78\u4f1a\uff01\u5e78\u4f1a\uff1a\u88f4\u73cf\u5374\u5df2\u50cf\u662f\u7a81\u7136\u53d1\u73b0\u4e86 \u4ec0\u4e48\uff0c\u62e5\u5165\u4eba\u4e1b\u91cc\u3002\u201d\u4e1c\u65b9\u4e94\u5251\u201c\u5bf9\u671b\u4e00\u773c\uff0c\u76ee\u5149\u4e2d\u65e2\u662f\u60ca\u7591\uff0c\u53c8\u89c9\u60ed\u6127\uff0c\u5fae\u5fae\u5411\u201d\u51b7\u8c37\u53cc \u6728\u201c\u62b1\u62f3\u4e00\u63d6\uff0c\u5206\u5f00\u4eba\u4e1b\uff0c\u8d70\u4e86\u51fa\u53bb\u3002\u51b7\u5bd2\u7af9\u76b1\u7709\u9053\uff1a\u201c\u73cf\u513f\u770b\u5230\u4e86\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u6447\u4e86\u6447\u5934\uff0c\u4e24\u4eba\u9f50\u5730\u8ddf\u5728\u88f4\u73cf\u8eab\u540e\uff0c\u6324\u5165\u4eba\u4e1b\u3002 \u3000\u3000\u88f4\u73cf\u6bcb\u5eb8\u5206\u5f00\u4f17\u4eba\uff0c\u4f17\u4eba\u81ea\u7136\u4e3a\u4ed6\u8ba9\u5f00\u4e86\u4e00\u6761\u9053\u8def\uff0c\u4f46\u6b64\u523b\u90a3\u773c\u775b\u5927\u5927\u7684\u5973\u5b69\u5b50\u5374\u5df2\u8d70 \u5f00\u4e86\uff0c\u53ea\u770b\u5230\u5979\u4e00\u53ea\u4e4c\u9ed1\u7684\u957f\u8fab\u5b50\uff0c\u5728\u4eba\u4e1b\u4e2d\u6447\u6643\u4e86\u4e00\u4e0b\uff0c\u88f4\u73cf\u66f4\u662f\u60ca\u8bb6\uff0c\u811a\u6b65\u653e\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u7a81\u542c\u8eab\u540e\u4e00\u58f0\u5927\u559d\uff1a\u201c\u88f4\u73cf\u5728\u8fd9\u91cc\u4e48\uff1f\u88f4\u73cf\u4f60\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fae\u4e00\u8fdf\u7591\uff0c\u987f\u4f4f\u811a\u6b65\uff0c\u53ea\u542c\u4e00\u8fde\u4e32\u94c1\u5668\u76f8\u51fb\u7684\u201c\u53ee\u53ee\u201d\u4e4b\u58f0\uff0c\u81ea\u8fdc\u800c\u8fd1\uff0c\u4e24\u65c1\u4eba\u4e1b \u4e00\u5206\uff0c\u8d70\u51fa\u4e00\u4e2a\u624b\u63e1\u94c1\u62d0\uff0c\u6ee1\u9762\u6012\u5bb9\u7684\u6c49\u5b50\uff0c\u7adf\u6b63\u662f\u90a3\u6b66\u6797\u201c\u91d1\u9e21\u5e2e\u201d\u4e4b\u9996\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00 \u557c\u3002 \u3000\u3000\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u65b9\u53bb\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u53c8\u6765\uff0c\u800c\u4e14\u4ed6\u795e\u8272\u4e4b\u95f4\uff0c\u6ee1\u9762\u5bfb\u8845\u751f\u4e8b\u4e4b\u610f\uff0c\u56db\u4e0b \u65b9\u5f85\u6563\u53bb\u7684\u4eba\u7fa4\uff0c\u6b64\u523b\u53c8\u805a\u62e2\u8fc7\u6765\u3002 \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u662f\u5979\u6765\u4e86\u4e48\uff1f\u5979\u600e\u5730\u4e0d\u89c1\u6211\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u5374\u62b1\u62f3\u9053\uff1a\u201c\u5411\u5e2e\u4e3b\u522b\u6765\u65e0\u6059\uff1f\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u5389\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6115\u4e86\u4e00\u6115\uff0c\u4e0d\u77e5\u5982\u4f55\u63a5\u53e3\uff0c\u53ea\u542c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u5389\u58f0\u53c8\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u4f60\u662f\u5982\u4f55\u767b \u4e0a\u2018\u6c5f\u5357\u540c\u76df\u2019\u76df\u4e3b\u5b9d\u5ea7\u7684\u4e48\uff1f\u60f3\u4e0d\u5230\u4f60\u6b64\u523b\u7adf\u771f\u7684\u4f5c\u5a01\u4f5c\u798f\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u88f4\u73cf\u5251\u7709\u5fae\u5254\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5411\u5e2e\u4e3b\u81ea\u7ba1\u8bf7\u4fbf\uff0c\u5728\u4e0b\u6055\u4e0d\u5949\u966a\u4e86\uff01\u201d \u3000\u3000\u888d\u8896\u4e00\u62c2\uff0c\u8f6c\u8eab\u800c\u884c\uff0c\u53ea\u542c\u201c\u5f53\u201d\u5730\u4e00\u58f0\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u6a2a\u7a7a\u98de\u8d77\uff0c\u8dc3\u5230\u4ed6\u9762\u524d\uff0c\u5927\u559d \u9053\uff1a\u201c\u4f60\u60f3\u8d70\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u51b7\u51b7\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u8d70\u4e0d\u5f97\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8a00\u8bed\u795e\u6001\u4e4b\u4e2d\uff0c\u5df2\u81ea\u6709\u4e00\u79cd\u6c89\u9759\u800c\u81ea\u4fe1\u7684\u5a01\u4eea\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u5446\u4e86\u4e00\u5446\uff0c\u60f3\u4e0d\u5230\u5e74 \u4f59\u4e0d\u89c1\uff0c\u8fd9\u61e6\u5f31\u7684\u5c11\u5e74\uff0c\u7adf\u5df2\u953b\u70bc\u6210\u94a2\uff0c\u5fae\u4e00\u6c89\u541f\uff0c\u65b9\u81ea\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u8d70\u4e5f\u884c\uff0c\u4e0d\u8fc7\u6211\u5148\u8981 \u95ee\u4f60\uff0c\u6211\u624b\u4e0b\u7684\u2018\u9e21\u51a0\u2019\u5305\u6653\u5929\uff0c\u7a76\u7adf\u72af\u4e0b\u4e86\u4ec0\u4e48\u5927\u7f6a\uff0c\u4f60\u8981\u5c06\u4ed6\u7f6e\u4e4b\u6b7b\u5730\uff01\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u88f4\u73cf\u53cd\u5012\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\uff0c\u5450\u5450\u9053\uff1a\u201c\u5305\u6653\u5929\u5df2\u6b7b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u5389\u559d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5df2\u88ab\u4f60\u5047\u501f\u2018\u6c5f\u5357\u540c\u76df\u2019\u7684\u5e2e\u52a9\uff0c\u6740\u6b7b\u5728\u4f0f\u725b\u5c71\u7684 \u8352\u90ca\uff0c\u82e5\u975e\u6211\u53d1\u73b0\u5f97\u65e9\uff0c\u4ed6\u5c38\u8eab\u90fd\u8981\u88ab\u86c7\u517d\u6240\u566c\u2014\u2014\u201d\u88f4\u73cf\u5fc3\u5934\u4e00\u60ca\uff0c\u622a\u53e3\u9053\uff1a\u201c\u5728\u4ed6\u8eab\u65c1 \u662f\u5426\u8fd8\u6709\u90a3\u2018\u9ed1\u9a74\u8ffd\u98ce\u2019\u7684\u5c38\u8eab\uff1f\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u82e5\u4e0d\u4e0e\u6211\u8bf4\u4e2a\u660e\u767d\uff0c\u4eca\u65e5\u5c31\u53eb\u4f60\u4e3a\u4ed6\u507f\u547d\uff1a \u201c\u4ed6\u53cc\u7709\u626c\u5904\uff0c\u94c1\u62d0\u5728\u5730\u4e0a\u91cd\u91cd\u4e00\u987f\uff0c\u5730\u4e0a\u51b0\u96ea\uff0c\u56db\u4e0b\u98de\u6fc0\uff0c\u7ade\u6e85\u5728\u88f4\u73cf\u90a3\u4e00\u8eab\u9752\u5e03\u957f\u886b\u4e4b \u4e0a\u3002\u88f4\u73cf\u957f\u53f9\u4e00\u58f0\uff0c\u6709\u5982\u672a\u89c1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u2018\u795e\u624b\u2019\u6218\u98de\u6bd5\u7adf\u8fd8\u662f\u5c06\u4ed6\u4eec\u6740\u6b7b\u4e86\uff01\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u8fde\u8fde\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u5c06\u7f6a\u8fc7\u63a8\u5728\u6218\u98de\u8eab\u4e0a\u4e48\uff1f\u4f60\u4ee5\u4e3a\u6211\u8fd8\u6015\u4e86\u6218\u98de\u4e0d \u6210\uff1f\u6211\u4eca\u65e5\u5148\u5bb0\u4e86\u4f60\uff0c\u518d\u627e\u6218\u98de\u7b97\u5e10\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u4ed6\u5df2\u626c\u624b\u4e00\u62d0\u631f\u7740\u4e00\u80a1\u52b2\u98ce\uff0c\u5411\u88f4\u73cf\u5f53\u5934\u51fb\u4e0b\u3002 \u3000\u3000\u56db\u4e0b\u7fa4\u8c6a\uff0c\u53c8\u662f\u4e00\u9635\u5927\u54d7\uff0c\u4e0d\u77e5\u8fd9\u8eab\u5c5e\u201c\u6c5f\u5357\u540c\u76df\u201d\u7684\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\uff0c\u600e\u6562\u5411\u4ed6\u7684\u76df \u4e3b\u52a8\u624b\uff1f \u3000\u3000\u88f4\u73cf\u8eab\u8eaf\u4e00\u8f6c\uff0c\u500f\u7136\u6e9c\u5230\u4ed6\u8eab\u540e\uff0c\u6c89\u58f0\u53f1\u9053\uff1a\u201c\u4f60\u75af\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u5927\u58f0\u559d\u9053\uff0c\u201c\u4e0d\u7ba1\u6211\u662f\u5426\u75af\u4e86\uff0c\u4eca\u65e5\u4e5f\u8981\u4f60\u6765\u4e0e\u5305\u6653\u5929\u7eb3\u547d\uff01\u201d \u3000\u3000\u98ce\u58f0\u6fc0\u8361\u4e4b\u4e0b\uff0c\u53c8\u662f\u4e09\u62d0\u51fb\u6765\uff0c\u4e0a\u51fb\u5929\u7075\uff0c\u4e2d\u62d0\u80f8\u8170\uff0c\u4e0b\u626b\u53cc\u8db3\uff0c\u4e00\u62d0\u6bd4\u4e00\u62d0\u7280\u5229\uff0c\u4e00 \u62d0\u6bd4\u4e00\u62d0\u6c89\u91cd\uff0c\u5f53\u771f\u662f\u7acb\u523b\u5c31\u60f3\u5c06\u88f4\u73cf\u6bd9\u6b7b\u4e8e\u62d0\u4e0b\u3002 \u3000\u3000\u88f4\u73cf\u8eab\u8eaf\u98d8\u98d8\uff0c\u8863\u8882\u62c2\u52a8\uff0c\u4ece\u5bb9\u5730\u907f\u8fc7\u4e86\u4ed6\u8fd9\u4e09\u62d0\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u2018\u91d1\u9e21\u2019\u5411\u4e00 \u557c\u5012\u662f\u6761\u8840\u6027\u6c49\u5b50\uff0c\u4e3a\u4e86\u4ed6\u624b\u4e0b\u4e00\u4e2a\u5144\u5f1f\u7684\u6027\u547d\uff0c\u7adf\u4e0d\u60dc\u4e0e\u4eba\u62fc\u547d\u52a8\u624b\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fc3\u4e2d\u5012\u5bf9\u6b64\u4eba\u751f\u4e86\u51e0\u5206\u597d\u611f\uff0c\u8eab\u5f62\u6e38\u8d70\u4e4b\u95f4\uff0c\u4fbf\u8d8a\u53d1\u4e0d\u613f\u8fd8\u624b\u52a8\u62db\uff0c\u53ea\u671b \u4ed6\u77e5\u96be\u800c\u9000\u3002 \u3000\u3000\u54ea\u77e5\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u62db\u5f0f\u5374\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u4e00\u62db\u5feb\u8fc7\u4e00\u62db\uff0c\u56db\u4e0b\u7fa4\u8c6a\u6709\u7684\u4e0d\u7981\u5927\u58f0\u547c\u559d \u6012\u9a82\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u2018\u5411\u91d1\u9e21\u2019\u7adf\u662f\u4e2a\u75af\u5b50\uff01\u4e3a\u4e86\u4ed6\u4e00\u4e2a\u624b\u4e0b\uff0c\u7adf\u6562\u5411\u4ed6\u7684\u76df\u4e3b\u52a8\u624b\u3002\u201d\u4f46\u6c5f \u6e56\u4e2d\u4eba\u660e\u54f2\u4fdd\u8eab\u7684\u591a\uff0c\u8c01\u4e5f\u4e0d\u613f\u591a\u7ba1\u95f2\u4e8b\uff0c\u4f55\u51b5\u4f17\u4eba\u65e9\u5df2\u770b\u51fa\uff0c\u201c\u88f4\u5927\u5148\u751f\u201d\u53ea\u662f\u5b58\u5fc3\u5bb9\u8ba9 \u800c\u5df2\uff0c\u82e5\u662f\u4ed6\u771f\u7684\u51fa\u624b\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u600e\u80fd\u5728\u4ed6\u624b\u4e0b\u8d70\u8fc7\u5341\u62db\uff01 \u3000\u3000\u62d0\u98ce\u8fc7\u5904\uff0c\u51b0\u96ea\u98de\u6fc0\uff0c\u7136\u800c\u6b64\u523b\u5374\u8fde\u8fd9\u98de\u6fc0\u7740\u7684\u51b0\u96ea\uff0c\u4e5f\u6cbe\u4e0d\u5230\u88f4\u73cf\u7684\u4e00\u70b9\u8863\u89d2\uff0c\u4ed6\u6f47 \u6d12\u5730\u5728\u90a3\u9635\u9635\u62d0\u98ce\u6756\u5f71\u4e2d\u76d8\u65cb\u6e38\u8d70\uff0c\u53ea\u56e0\u6b64\u523b\u7684\u8eab\u5206\u4e0e\u5730\u4f4d\uff0c\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5df2\u4e0d\u5bb9\u4ed6\u95ea \u907f\uff0c\u5426\u5219\u4ed6\u771f\u4e0d\u613f\u4e0e\u8fd9\u6709\u5982\u75af\u72c2\u4e4b\u4eba\u4e00\u822c\u89c1\u8bc6\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u8896\u624b\u9762\u89c2\uff0c\u51b7\u5bd2\u7af9\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u4f4e\u8bed\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u5982\u66ff\u73cf\u513f\u5c06\u8fd9\u53ae\u89e3\u51b3\u4e86 \u5427\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u5982\u8ba9\u4ed6\u5c06\u6b64\u4eba\u6536\u670d\uff0c\u5c06\u6765\u4e5f\u597d\u505a\u4ed6\u7684\u4e00\u6761\u81c2\u8180\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u53c8\u81ea\u653b\u51fa\u4e09\u62db\uff0c\u6b64\u523b\u4ed6\u4f3c\u5df2\u81ea\u77e5\u4e0d\u884c\uff0c\u9762\u4e0a\u4e0d\u7981\u9732\u51fa\u60ca\u8bb6\u4e0e \u7126\u6025\u4e4b\u8272\uff0c\u4f46\u76ee\u5149\u4e2d\u5374\u4f3c\u671f\u5f85\u7740\u4ec0\u4e48\uff0c\u4e0d\u4f4f\u5411\u56db\u4e0b\u641c\u5bfb\uff0c\u663e\u7136\u4ed6\u65e9\u5df2\u7ea6\u597d\u5e2e\u624b\uff0c\u5374\u4e0d\u77e5\u4ed6\u7684 \u7684\u662f\u8c01\uff1f \u3000\u3000\u4eba\u4e1b\u5916\u7a81\u53c8\u4e71\u4e86\u8d77\u6765\uff0c\u6ce2\u6d6a\u7684\u5411\u4e24\u65c1\u5206\u5f00\u3002 \u3000\u3000\u6709\u4eba\u5728\u6697\u4e2d\u4f4e\u8bed\uff1a\u201c\u90a3\u98de\u8679\u600e\u5730\u6765\u4e86\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4eba\u6f6e\u4e00\u5206\u53c8\u5408\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u5df2\u8d6b\u7136\u73b0\u8eab\uff0c\u4ed6\u4e00\u8eab\u52b2\u88c5\u75be\u670d\uff0c\u8170\u7554\u4f69\u7740\u4e00\u53ea \u9769\u56ca\uff0c\u56ca\u4e2d\u60f3\u5fc5\u5c31\u662f\u4ed6\u6210\u540d\u6c5f\u6e56\u7684\u6697\u5668\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4e86\u4ed6\u7684\u88c5\u675f\u884c\u8272\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u52a8\uff0c\u77e5\u9053\u4ed6\u5fc5\u5b9a\u662f\u51c6\u5907\u4e0e\u4eba\u52a8\u624b\u800c\u6765\uff0c\u51b7\u5bd2\u7af9\u53cc\u7709 \u4e00\u6311\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u82e5\u662f\u6b64\u4eba\u6709\u51fa\u624b\u4e4b\u610f\u2026\u2026\u201d \u3000\u3000\u51b7\u67af\u6728\u51b7\u51b7\u63a5\u53e3\u9053\uff1a\u201c\u6211\u600e\u4e48\u5bb9\u4ed6\u51fa\u624b\uff01\u201d \u3000\u3000\u53ea\u89c1\u90a3\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u9762\u4e0a\u679c\u7136\u9732\u51fa\u559c\u8272\uff0c\u8fde\u653b\u4e09\u62d0\uff0c\u5927\u58f0\u9053\uff1a\u201c\u90a3\u5927\u54e5\uff0c\u4f60\u6765\u4e86\u4e48\uff0c \u597d\u6781\u597d\u6781\uff0c\u8fd9\u79cd\u66b4\u53d1\u7684\u5c0f\u4eba\uff0c\u600e\u80fd\u5bb9\u4ed6\u5f53\u2018\u6c5f\u5357\u540c\u76df\u201d\u7684\u76df\u4e3b\uff0c\u8fd8\u662f\u5feb\u4e9b\u5c06\u4ed6\u9664\u53bb\u7b97\u4e86\uff01 \u201c\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u53ea\u5f53\u4ed6\u662f\u6761\u70ed\u8840\u6c49\u5b50\uff0c\u4e3a\u4e86\u4ed6\u624b\u4e0b\u5f1f\u5144\u4e4b\u6545\u800c\u6124\u6012\u4f24\u5fc3\uff0c\u54ea\u77e5 \u4ed6\u8fd9\u4e0d\u8fc7\u662f\u501f\u9898\u53d1\u6325\u800c\u5df2\u3002\u5509\uff01\u8fd9\u822c\u4eba\u7684\u4eba\u6027\uff0c\u4e3a\u4f55\u5982\u6b64\u5351\u52a3\uff01\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u9762\u5bd2\u5982\u6c34\uff0c\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u7f13\u7f13\u8d70\u5411\u6218\u5c40\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u8fd9\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\u679c\u7136\u662f\u4ed6\u7ea6\u597d\u7684\u5e2e\u624b\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u9ed8\u7136\u51dd\u6ce8\u7740\u90a3\u98de\u8679\u7684\u8eab\u5f62\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u7a81\u89c9\u5bf9\u65b9\u638c\u4e0a\u5df2\u6709\u771f\u529b\u53d1\u51fa\uff0c\u5fc3\u5934\u4e00 \u51db\uff0c\u5927\u559d\u9053\uff1a\u201c\u90a3\u5927\u54e5\u2026\u2026\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u51b7\u51b7\u622a\u53e3\u9053\uff1a\u201c\u4f60\u4e0d\u613f\u2018\u88f4\u5927\u5148\u751f\u2019\u505a\u2018\u6c5f\u5357\u540c\u76df\u2019\u7684\u76df\u4e3b\u662f \u4e48\uff1f\u201d \u3000\u3000\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u4e00\u9762\u52a8\u624b\uff0c\u4e00\u9762\u559d\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4ed6\u4e0d\u914d\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u2018\u51b7\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\u3002\u201d \u3000\u3000\u7a81\u5730\u624b\u8155\u4e00\u626c\uff0c\u4e00\u84ec\u94f6\u5149\uff0c\u66b4\u5c04\u800c\u51fa\uff0c\u51b7\u67af\u6728\u6c89\u58f0\u559d\u9053\uff1a\u201c\u7559\u5fc3\u6697\u5668\uff01\u201d \u3000\u3000\u4ed6\u65b9\u5f85\u7eb5\u8eab\u63a0\u51fa\uff0c\u53ea\u542c\u4e00\u58f0\u60e8\u547c\uff0c\u4eba\u5f71\u4e4d\u5206\uff0c\u76ee\u4e0b\u7fa4\u8c6a\uff0c\u4ea4\u76f8\u53d8\u8272\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u2018\u66f4\u662f \u60f6\u7136\u5931\u8272\u3002\u53ea\u89c1\u201d\u91d1\u9e21\u201c\u5411\u4e00\u557c\u4e0e\u88f4\u73cf\u5bf9\u9762\u800c\u7acb\uff0c\u4e24\u4eba\u8c01\u4e5f\u4e0d\u52a8\u4e00\u52a8\u3002\u7ec8\u4e8e\u2026\u2026\u201d\u91d1\u9e21\u201c\u5411 \u4e00\u557c\u9762\u4e0a\u6cdb\u8d77\u4e00\u4e1d\u51c4\u60e8\u7684\u72de\u7b11\uff0c\u98a4\u6296\u5730\u4f38\u51fa\u624b\u638c\u3002\u98a4\u6296\u7740\u6307\u5411\u90a3\u98de\u8679\uff0c\u98a4\u6296\u7740\u9053\uff1a\u201c\u4f60\u2026\u2026 \u4f60\u2026\u2026\u4f60\u2026\u2026\u72e0\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u201c\u5f53\u201d\u5730\u4e00\u58f0\uff0c\u94c1\u62d0\u843d\u5230\u5730\u4e0a\uff0c\u4ed6\u8eab\u8eaf\u6447\u4e86\u4e24\u6447\uff0c\u4f3c\u4e4e\u8981\u5411\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u6251 \u53bb\u3002 \u3000\u3000\u90a3\u98de\u8679\u51b7\u7b11\u4e00\u58f0\u3001\u5389\u559d\u9053\uff1a\u201c\u4e0d\u5b88\u5e2e\u89c4\uff0c\u53cd\u53db\u76df\u4e3b\uff0c\u7f6a\u4e0d\u5bb9\u8bdb\uff0c\u4f60\u8fd8\u5728\u8fd9\u91cc\u60f3\u4f3a\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u7a81\u5730\u626c\u624b\u4e00\u638c\uff0c\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c\u8eab\u5f62\u65b9\u52a8\uff0c\u4f46\u88ab\u4ed6\u8fd9\u4e00\u638c\u5288\u5230\u5730\u4e0a\uff0c\u60e8\u547c\u4e00\u58f0\uff0c\u6eda\u4e86\u4e24 \u6eda\uff0c\u4fbf\u518d\u4e5f\u4e0d\u4f1a\u52a8\u5f39\u4e86\u3002 \u3000\u3000\u5c40\u9762\u4e00\u53d8\u5982\u6b64\uff0c\u5df2\u5927\u51fa\u6bcf\u4e2a\u4eba\u7684\u610f\u6599\u4e4b\u5916\uff0c\u76ee\u4e0b\u7fa4\u8c6a\u7adf\u90fd\u88ab\u60ca\u5f97\u5446\u4e86\uff0c\u6ca1\u6709\u4e00\u4eba\u53d1\u51fa\u58f0 \u6765\u3002 \u3000\u3000\u88f4\u73cf\u66f4\u662f\u76ee\u77aa\u53e3\u5446\uff0c\u53ea\u89c1\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u53cc\u624b\u4e00\u62cd\uff0c\u5728\u5411\u4e00\u557c\u7684\u5c38\u8eab\u4e0a\u8e22\u4e86\u4e00\u811a\uff0c \u5fae\u7b11\u9053\uff1a\u201c\u76df\u4e3b\u4f60\u53ef\u53d7\u60ca\u4e86\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5450\u5450\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8fd9\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u6c89\u58f0\u9053\uff1a\u201c\u53db\u5e2e\u4e0e\u53db\u5e08\u540c\u7f6a\uff0c\u6c5f\u6e56\u4e0a\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff0c\u76df\u4e3b\u4f60\u867d\u7136\u5b58 \u5fc3\u4ec1\u539a\uff0c\u4f46\u5728\u4e0b\u5374\u4e0d\u80fd\u8ba9\u8fd9\u79cd\u4ee5\u4e0b\u72af\u4e0a\u7684\u4e07\u6076\u4e4b\u5f92\u900d\u9065\u6cd5\u5916\u3002\u201d \u3000\u3000\u88f4\u73cf\u6123\u4e86\u534a\u664c\uff0c\u5b9e\u662f\u65e0\u8bcd\u53ef\u5bf9\uff0c\u957f\u53f9\u9053\uff1a\u201c\u4f46\u4f60\u53c8\u4f55\u6614\u5982\u6b64\u5fc3\u6025\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u8f6c\u8fc7\u5934\u53bb\uff0c\u5fae\u4e00\u62db\u624b\uff0c\u4eba\u4e1b\u4e2d\u4fbf\u5df2\u5954\u6765\u4e24\u6761\u5927\u6c49\uff0c\u62ac\u53bb\u4e86\u201c\u91d1\u9e21\u201d\u5411\u4e00\u557c \u7684\u5c38\u8eab\u3002 \u3000\u3000\u8fd9\u4e00\u751f\u6027\u5b64\u50fb\u72c2\u505a\u3001\u597d\u9ad8\u559c\u529f\u7684\u6c5f\u6e56\u8c6a\u6770\uff0c\u7adf\u843d\u5230\u5982\u6b64\u4e0b\u573a\uff0c\u4f17\u4eba\u4e0d\u7981\u4e3a\u4e4b\u60cb\u501f\uff0c\u4f46\u5374 \u65e0\u4e00\u4eba\u6562\u8bf4\u51fa\u53e3\u6765\uff0c\u53ea\u56e0\u6b64\u523b\u82e5\u6709\u8c01\u5e2e\u4ed6\u8bf4\u4e86\u53e5\u8bdd\uff0c\u4fbf\u7b49\u4e8e\u548c\u6b64\u523b\u55a7\u8d6b\u4e00\u201c\u65f6\u7684\u201d\u6c5f\u752b\u540c\u76df \u201c\u4e3a\u654c\u3002\u6709\u4e9b\u201d\u98de\u9f99\u9556\u5c40\u201c\u7684\u9556\u4f19\u6216\u670b\u53cb\u89c1\u4e86\uff0c\u5374\u4e0d\u7981\u4e3a\u4e4b\u6697\u4e2d\u5f97\u610f\uff0c\u201d\u6c5f\u752b\u540c\u76df\u201c\u5982\u6b64\u81ea \u76f8\u6b8b\u6740\uff0c\u5c82\u975e\u5bf9\u201d\u98de\u9f99\u9556\u5c40\u201c\u5927\u662f\u6709\u5229\u3002\u201d\u51b7\u8c37\u53cc\u6728\u201d\u53c8\u81ea\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e2d\u5927\u662f\u7591\u60d1\uff0c\u4ed6\u4e24 \u4eba\u5df2\u770b\u51fa\u8fd9\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u5fc5\u5b9a\u662f\u53e6\u6709\u56fe\u8c0b\uff0c\u53ea\u662f\u4ed6\u4e24\u4eba\u5374\u4e5f\u4e0d\u4fbf\u8fc7\u95ee\u201d\u6c5f\u5357\u540c\u76df\u201c\u7684\u201d\u5bb6\u52a1 \u4e8b\u201c\u3002\u521d\u96ea\u65b9\u6b47\uff0c\u4f46\u5bd2\u98ce\u5374\u66f4\u51db\u51bd\u3002\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u9762\u5e26\u5fae\u7b11\uff0c\u671b\u7740\u4ed6\u7684\u624b\u4e0b\u62ac\u53bb\u201d\u91d1\u9e21\u201c\u5411 \u4e00\u557c\u7684\u5c38\u8eab\uff0c\u4eba\u7fa4\u6e10\u6e10\u6563\u53bb\uff0c\u7a81\u5730\u4e00\u67c4\u957f\u5251\uff0c\u6f2b\u65e0\u58f0\u606f\u5730\u523a\u4e86\u8fc7\u6765\uff0c\u5374\u4ec5\u5728\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u80a9 \u5934\u8089\u539a\u4e4b\u5904\u8f7b\u8f7b\u4e00\u70b9\uff0c\u90a3\u98de\u8679\u4e00\u60ca\u8f6c\u8eab\uff0c\u559d\u9053\uff1a\u201c\u8c01\uff01\u201d \u3000\u3000\u76ee\u5149\u52a8\u5904\uff0c\u4e1c\u65b9\u6c5f\u3001\u4e1c\u65b9\u6e56\u4e24\u4eba\u624b\u6301\u957f\u5251\uff0c\u9762\u5e26\u51b7\u7b11\uff0c\u6b63\u8d6b\u7136\u4e95\u80a9\u7acb\u5728\u4ed6\u8eab\u540e\u4e00\u5c3a\u5f00 \u5916\u3002 \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u8eab\uff0c\u77e5\u9053\u4eca\u65e5\u4e4b\u4e8b\uff0c\u8fd8\u672a\u4e86\u7ed9\uff0c\u53ea\u5f97\u9a7b\u8db3\u4e0d\u8d70\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u9762\u4e0a\u795e\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\uff1f\u539f\u6765\u662f\u4e24\u4f4d\u65b9\u4e1c\u5c11\u4fa0\uff0c\u5374\u4e0d\u77e5 \u4e24\u4f4d\u4f55\u65f6\u5b66\u4f1a\u4e86\u5728\u6697\u4e2d\u4f24\u4eba\u7684\u672c\u9886\uff1f\u5012\u6559\u5728\u4e0b\u4f69\u670d\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u8a00\u8bcd\u7280\u5229\uff0c\u679c\u7136\u4e0d\u6127\u662f\u8001\u6c5f\u6e56\u7684\u53e3\u543b\u3002 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u5374\u4ecd\u7136\u9762\u7b3c\u5bd2\u971c\uff0c\u4ecd\u4e0d\u4e3a\u6240\u52a8\uff0c\u4e1c\u65b9\u6c5f\u51b7\u51b7\u9053\uff1a\u201c\u6211\u5982\u6b64\u5bf9\u5f85\u60ef\u4e8e\u6697\u4e2d\u4f24\u4eba\u4e4b \u8f88\uff0c\u8fd8\u771f\u5ba2\u6c14\u5f97\u5f88\uff0c\u5426\u5219\u4f60\u6b64\u523b\u8fd8\u80fd\u4e0e\u6211\u5144\u5f1f\u4e24\u4eba\u8bf4\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u4ef0\u5929\u72c2\u7b11\u6570\u58f0\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u5012\u8981\u611f\u6fc0\u4e24\u4f4d\u624d\u662f\u4e86\uff01\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u51b7\u51b7\u9053\uff1a\u201c\u5c11\u5728\u5c11\u7237\u9762\u524d\u901e\u4e00\u65f6\u53e3\u820c\u4e4b\u5229\uff0c\u4f60\u5506\u4f7f\u624b\u4e0b\uff0c\u6563\u53d1\u72c2\u8a00\uff0c\u82e5\u4e0d\u8d76\u7d27\u8bf4 \u4e2a\u6e05\u695a\uff0c\u6211\u7acb\u65f6\u4fbf\u8981\u4f60\u4f24\u5728\u5251\u4e0b\uff0c\u53ef\u6ca1\u6709\u65b9\u624d\u90a3\u822c\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u4eff\u4f5b\u6123\u4e86\u4e00\u6123\uff0c\u4f5c\u51fa\u832b\u7136\u4e0d\u89e3\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4ead\uff0c\u8fd9\u5012\u6559\u5728\u4e0b\u4e0d \u61c2\u4e86\u3002\u201d \u3000\u3000\u4e1c\u65b9\u6c5f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u624b\u4e0b\u5df2\u5728\u4f17\u76ee\u6240\u89c6\u4e4b\u4e0b\u62db\u8ba4\u4e86\uff0c\u4f60\u96be\u9053\u8fd8\u60f3\u72e1\u8d56\u4e48\uff1f\u6211\u5012\u8981\u95ee\u95ee \u4f60\uff0c\u65b9\u624d\u90a3\u4e9b\u5728\u6697\u4e2d\u8fb1\u9a82\u6211\u5144\u5f1f\u7684\u4eba\uff0c\u83ab\u975e\u4e0d\u662f\u4f60\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u7136\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e9b\u4eba\u90fd\u662f\u6211\u7684\u624b\u4e0b\uff0c\u662f\u6211\u5728\u6697 \u4e2d\u6307\u4f7f\u4ed6\u4eec\uff01\u201d \u3000\u3000\u4ed6\u5982\u6b64\u75db\u5feb\u5730\u627f\u8ba4\uff0c\u4f17\u4eba\u53cd\u89c9\u4e00\u6123\uff0c\u4e1c\u65b9\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\u3002\u4e1c\u65b9\u6c5f\u957f\u5251\u4e00\u6296\uff0c\u5251\u7709\u6012\u8f69\uff0c \u6c89\u58f0\u9053\uff1a\u201c\u65e2\u7136\u662f\u4f60\u4e3b\u4f7f\uff0c\u4f60\u6216\u662f\u5728\u6211\u5144\u5f1f\u9762\u524d\u8dea\u4e0b\u8ba4\u9519\uff0c\u6216\u662f\u62d4\u51fa\u5175\u5203\uff0c\u4e0e\u6211\u5144\u5f1f\u4e00\u4e00\u51b3 \u751f\u6b7b\uff01\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u795e\u8272\u4e0d\u53d8\uff0c\u9053\uff1a\u201c\u90a3\u822c\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u83ab\u975e\u90fd\u5df2\u6b7b\u5728\u8d24\u6606\u4ef2\u7684\u5251\u4e0b\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u6c5f\u6c89\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u4ff1\u662f\u53d7\u547d\u4e8e\u4eba\uff0c\u81ea\u7136\u602a\u4e0d\u4e86\u4ed6\u4eec\uff01\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u9053\uff1a\u201c\u4f46\u6211\u4ea6\u662f\u53d7\u547d\u4e8e\u4eba\uff0c\u5c82\u80fd\u602a\u5f97\u4e86\u6211\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u6c5f\u76ee\u5149\u4e00\u51db\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8c01\uff1f\u6307\u4f7f\u4f60\u7684\u662f\u8c01\uff1f\u5960\u975e\u662f\u2018\u795e\u624b\u2019\u6218\u98de\uff0c\u6291\u6216\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u4e24\u58f0\uff0c\u500f\u7136\u4f4f\u53e3\uff0c\u76ee\u5149\u5374\u659c\u659c\u779f\u4e86\u88f4\u73cf\u4e00\u773c\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u4ef0\u5929\u9053\uff1a\u201c\u6307\u4f7f\u6211\u7684\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u4fbf\u662f\u4ee4\u5c0a\u4e1c\u65b9\u8001\u5821\u4e3b\uff01\u201d \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u9f50\u5730\u4e00\u6123\uff0c\u53cc\u5251\u4e00\u5c55\uff0c\u5927\u6012\u9053\uff1a\u201c\u597d\u4e2a\u5927\u80c6\u7684\u72c2\u5f92\uff0c\u5c45\u7136\u6562\u6765\u6349\u5f04\u6211\u5144\u5f1f\uff0c\u5feb \u4e9b\u62d4\u5251\u53d7\u6b7b\uff01\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u522b\u4eba\u53e3\u4e2d\u7684\u8bdd\uff0c\u4e24\u4f4d\u6df1\u4fe1\u4e0d\u7591\uff0c\u5728\u4e0b\u53e3\u4e2d\u7684\u8bdd\uff0c\u4e24 \u4f4d\u4e3a\u4f55\u5c31\u4e0d\u76f8\u4fe1\u4e86\u5462\uff1f\u8fd9\u5012\u602a\u4e86\uff01\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u4e00\u987f\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u7247\u9762\u4e4b\u8bcd\uff0c\u4e24\u4f4d\u600e\u80fd\u6df1\u4fe1\uff1f\u6211\u90a3\u98de\u8679\u5c82\u662f\u90a3\u79cd\u4eba\u7269\uff01\u201d \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u53cc\u53cc\u4e0d\u7981\u6014\u7136\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u638c\u4e2d\u7684\u5229\u5251\uff0c\u4e5f\u7f13\u7f13\u5782\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u51b7\u5bd2\u7af9\u51b7\u7b11\u4e00\u58f0\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u597d\u4e2a\u4f36\u5634\u5229\u53e3\u7684\u8001\u6c5f\u6e56\uff01\u201d \u3000\u3000\u51b7\u67af\u6728\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u6210\u4e8b\u4e0d\u8db3\uff0c\u8d25\u4e8b\u5374\u6709\u4f59\uff0c\u6700\u96be\u60f9\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u6e10\u9ad8\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5374\u53ea\u4f5c\u672a\u95fb\u3002 \u3000\u3000\u53ea\u89c1\u4e1c\u65b9\u5144\u5f1f\u4e24\u4eba\u8baa\u8baa\u5730\u6536\u56de\u957f\u5251\uff0c\u56db\u671b\u4e00\u773c\uff0c\u4e00\u8bed\u4e0d\u53d1\u5730\u8f6c\u8eab\u800c\u53bb\uff0c\u90a3\u98de\u8679\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u4e24\u4f4d\u5c11\u4fa0\u4ee5\u540e\u82e5\u8981\u5ba1\u95ee\u72af\u4eba\uff0c\u4e0d\u59a8\u6765\u901a\u77e5\u5728\u4e0b\u4e00\u58f0\u3002\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u970d\u7136\u56de\u8fc7\u5934\u6765\uff0c\u5374\u88ab\u4e1c\u65b9\u6c5f\u62c9\u4e86\u56de\u53bb\uff0c\u8fd9\u5144\u5f1f\u4e24\u4eba\u6bd5\u7adf\u662f\u4fa0\u4e49\u95e8\u5f92\uff0c\u53ea\u662f\u6c5f\u6e56\u5386 \u7ec3\u7565\u5acc\u4e0d\u591f\u800c\u5df2\u3002 \u3000\u3000\u90a3\u98de\u8679\u7b11\u58f0\u4e00\u987f\uff0c\u8f6c\u65e5\u9053\uff1a\u201c\u76df\u4e3b\u5728\u8fd9\u91cc\u53ef\u6709\u843d\u811a\u4e4b\u5904\uff0c\u8fd8\u662f\u5373\u523b\u5c31\u8981\u52a8\u8eab\uff01\u201d \u3000\u3000\u88f4\u73cf\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u6211\u51c6\u5907\u968f\u610f\u5bfb\u5bb6\u5ba2\u6808\u3002\u201d \u3000\u3000\u90a3\u98de\u8679\u5fae\u5fae\u4e00\u7b11\uff0c\u622a\u53e3\u9053\uff1a\u201c\u6b64\u523b\u4e0d\u4f46\u6c49\u53e3\u57ce\u4e2d\u5bb6\u5bb6\u5ba2\u6808\u4ff1\u5df2\u65e0\u6cd5\u63d2\u8db3\uff0c\u4fbf\u662f\u6c49\u9633\u9547 \u91cc\uff0c\u4e5f\u6ca1\u6709\u4e00\u5bb6\u5ba2\u6808\u53ef\u4ee5\u5bb9\u8eab\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u671b\u4e86\u51b7\u6c0f\u5144\u5f1f\u4e00\u773c\uff0c\u76b1\u7709\u9053\uff1a\u201c\u90a3\u4e48\u2026\u2026\u201d \u3000\u3000\u90a3\u98de\u8679\u542b\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5728\u57ce\u90ca\u9644\u8fd1\uff0c\u5012\u6709\u4e00\u5904\u7a7a\u5c4b\uff0c\u4e0d\u77e5\u76df\u4e3b\u53ef\u5426\u5c48\u9a7e\uff0c\u53cd\u6b63\u53ea\u4e0d\u8fc7\u662f \u6570\u5929\u7684\u65f6\u65e5\uff0c\u4e00\u5207\u4e8b\u90fd\u80fd\u89e3\u51b3\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fae\u7b11\u9053\uff1a\u201c\u90a3\u662f\u6700\u597d\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u89c1\u56db\u5339\u5065\u9a6c\uff0c\u6025\u9a70\u800c\u6765\uff0c\u8def\u4e0a\u4eba\u7fa4\uff0c\u7eb7\u7eb7\u95ee\u907f\uff0c\u9a6c\u4e0a\u56db\u4eba\uff0c\u4ff1\u90fd\u662f\u795e\u8272\u527d \u608d\uff0c\u9a91\u672f\u7cbe\u7edd\u7684\u9a91\u58eb\uff0c\u9996\u5339\u9a6c\u4e0a\u4e00\u4e2a\u8eab\u8eaf\u7279\u957f\u7684\u5927\u6c49\uff0c\u53f3\u81c2\u5fae\u56de\uff0c\u652f\u7740\u4e00\u9762\u9ed1\u5e95\u9ec4\u5b57\u7684\u5927 \u65d7\uff0c\u8fce\u98ce\u62db\u5c55\u4e0d\u5df2\u3002 \u3000\u3000\u88f4\u73cf\u5012\u9000\u6570\u6b65\uff0c\u53ea\u89c1\u65d7\u4e0a\u7ee3\u7684\u8d6b\u7136\u7adf\u662f\u516b\u6761\u91d1\u9f99\uff0c\u9996\u5c3e\u76f8\u63a5\uff0c\u56f4\u7740\u4e00\u4e2a\u6597\u5927\u7684\u201c\u6a80\u201d \u5b57\uff01 \u3000\u3000\u4ed6\u4e0d\u7981\u6123\u7136\u5fd6\u9053\uff1a\u201c\u96be\u602a\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u5c45\u7136\u90fd\u80af\u8ba9\u8def\uff0c\u539f\u6765\u662f\u2018\u9f99\u5f62\u516b\u638c\u2019\u7684\u624b\u4e0b\u4eb2\u4fe1 \u5230\u4e86\u3002\u201d \u3000\u3000\u8fd9\u56db\u5339\u5065\u9a6c\u4e00\u7ecf\u8e0f\u4e0a\u957f\u8859\uff0c\u9996\u5339\u9a6c\u4e0a\u7684\u9a91\u58eb\u7acb\u523b\u5f15\u542d\u547c\u9053\uff1a\u201c\u6a80\u603b\u9556\u5934\u6709\u4ee4\uff0c\u2018\u98de\u9f99 \u65d7\u2019\u4e0b\u6240\u5c5e\u7684\u6240\u6709\u5144\u5f1f\u4eec\uff0c\u7acb\u523b\u68c0\u70b9\u884c\u88c5\uff0c\u968f\u65f6\u968f\u5730\uff0c\u5f85\u547d\u800c\u53d1\uff01\u201d \u3000\u3000\u547c\u58f0\u5639\u4eae\uff0c\u54cd\u5f7b\u56db\u91ce\uff01 \u3000\u3000\u957f\u8857\u4e0a\u7acb\u523b\u53c8\u662f\u4e00\u9635\u9a9a\u52a8\uff0c\u6709\u7684\u4eba\u81ea\u8857\u4e0a\u5954\u56de\u5c4b\u53bb\uff0c\u6709\u7684\u4eba\u81ea\u5c4b\u4e2d\u5954\u4e0a\u8857\u6765\uff0c\u7b2c\u4e00\u904d\u547c \u58f0\u672a\u4e86\uff0c\u7b2c\u4e8c\u8ffd\u547c\u58f0\u53c8\u81ea\u54cd\u8d77\u2026\u2026 \u3000\u3000\u8fd9\u547c\u58f0\u4e00\u904d\u63a5\u7740\u4e00\u904d\uff0c\u81ea\u8857\u5934\u558a\u5230\u8857\u5c3e\uff0c\u7136\u540e\u8f6c\u8fc7\u4e86\u957f\u8857\uff0c\u4ecd\u6709\u4e00\u58f0\u58f0\u7684\u547c\u558a\uff0c\u8fdc\u8fdc\u4f20 \u6765\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u76ee\u5149\u4e00\u95ee\uff0c\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u53ef\u77e5\u9053\u6218\u795e\u624b\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u88f4\u73cf\u56db\u671b\u4e00\u773c\uff0c\u53ea\u89c1\u6ee1\u8857\u4e4b\u4eba\u7684\u76ee\u5149\uff0c\u53c8\u90fd\u8f6c\u5230\u81ea\u5df1\u8fd9\u8fb9\uff0c\u4e0d\u7981\u6c89\u541f\u534a\u664c\uff0c\u65b9\u81ea\u8f7b\u58f0 \u9053\uff1a\u201c\u6218\u5144\u53ea\u6015\u5df2\u8fd4\u6c5f\u5357\uff0c\u56e0\u4ed6\u7b97\u5b9a\u4e86\u6a80\u660e\u5fc5\u662f\u8981\u5bf9\u4ed6\u5bb6\u5b85\u4e0d\u5229\uff0c\u518d\u6765\u4e5f\u662f\u5728\u6c5f\u5357\u5e03\u7f6e\u4e00 \u4e0b\uff0c\u4e13\u7b49\u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u9556\u8f66\u6e21\u6c5f\u5357\u4e0b\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u76ee\u5149\u53c8\u81ea\u4e00\u95ea\uff0c\u7a81\u7136\u9644\u5728\u88f4\u73cf\u8033\u7554\uff0c\u4f4e\u4f4e\u9053\uff1a\u201c\u8fd1\u6765\u6c5f\u6e56\u4f20\u8a00\uff0c\u8bf4\u662f\u76df\u4e3b \u4e0e\u6a80\u660e\u6000\u6709\u4e0d\u5171\u6234\u5929\u4e4b\u4ec7\uff0c\u4e0d\u77e5\u76df\u4e3b\u5982\u4f55\u6253\u7b97\uff0c\u53ef\u6709\u8981\u5c0f\u5f1f\u6548\u52b3\u4e4b\u5904\uff1f\u201d \u3000\u3000\u88f4\u73cf\u9762\u8272\u4e00\u6c89\uff0c\u76ee\u5149\u51b0\u51b7\u5730\u51dd\u6ce8\u5728\u8fdc\u65b9\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u65b9\u81ea\u7f13\u7f13\u9053\uff1a\u201c\u6a80\u660e\u53ef\u662f\u4e5f\u8981\u5230\u8fd9 \u91cc\u6765\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u9053\uff1a\u201c\u60f3\u5fc5\u5982\u6b64\uff01\u201d \u3000\u3000\u88f4\u73cf\u76ee\u5149\u4e0d\u52a8\uff0c\u7f13\u7f13\u53c8\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u4e3a\u4ec0\u4e48\u8981\u7559\u5728\u6b64\u5730\u7684\u7f18\u7531\u4e86\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u9762\u4e0a\u7a81\u5730\u6cdb\u8d77\u4e86\u4e00\u9635\u5947\u5f02\u7684\u795e\u8272\uff0c\u4f46\u4e00\u95ea\u5373\u8fc7\uff0c\u659c\u76ee\u779f\u4e86\u201c\u51b7\u8c37\u53cc \u6728\u201d\u4e00\u773c\uff0c\u4f4e\u58f0\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u2026\u2026\u76df\u4e3b\uff0c\u4f60\u4e0e\u51b7\u6c0f\u5144\u5f1f\u7684\u8d4c\u7ea6\u2026\u2026\u201d \u3000\u3000\u88f4\u73cf\u622a\u53e3\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u80dc\u8d1f\u5168\u5df2\u65e0\u59a8\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u8fd8\u6709\u6bd4\u7236\u53d4\u4e4b\u4ec7\u66f4\u91cd\u8981\u7684\u4e8b \u4e48\uff1f\u201d \u3000\u3000\u4ed6\u53e3\u6c14\u662f\u5982\u6b64\u6c89\u7a33\uff0c\u53ef\u662f\u5982\u6b64\u5145\u6ee1\u4e86\u81ea\u4fe1\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5fc3\u5934\u5ffd\u5730\u4e00\u9635\u98a4\u6296\uff0c\u6df1\u6df1\u51dd\u6ce8 \u4e86\u81ea\u5df1\u9762\u524d\u8fd9\u5c11\u5e74\u4e00\u773c\uff0c\u4eff\u4f5b\u662f\u76f4\u5230\u4eca\u65e5\uff0c\u4ed6\u624d\u771f\u5730\u770b\u6e05\u4e86\u88f4\u73cf\u4f3c\u7684\uff0c\u5e72\u7b11\u4e24\u58f0\uff0c\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8ba9\u5c0f\u5f1f\u5e26\u76df\u4e3b\u5230\u90a3\u843d\u811a\u4e4b\u5904\u53bb\u624d\u662f\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u65b9\u4e86\uff0c\u56db\u4e0b\u5df2\u6709\u6570\u5341\u6761\u5927\u6c49\u56f4\u4e86\u4e0a\u6765\uff0c\u4e00\u8d77\u8eac\u8eab\u9053\uff1a\u201c\u5c0f\u7684\u4eec\u4ff1\u662f\u2018\u6c5f\u5357\u540c\u76df\uff0c \u4e2d\u4eba\uff0c\u53ea\u662f\u8eab\u4efd\u60ac\u6b8a\uff0c\u662f\u4ee5\u4e00\u76f4\u4e0d\u6562\u4e0e\u76df\u4e3b\u8bf4\u8bdd\uff0c\u4f46\u76df\u4e3b\u5728\u6b64\u5730\u65e0\u843d\u811a\u4e4b\u5904\uff0c\u5c0f\u7684\u5012\u53ef\u5c06\u4f4f \u7684\u5ba2\u6808\u5148\u8ba9\u51fa\u6765\u3002\u201d\u8fd9\u4e9b\u4eba\u4e0d\u4f46\u795e\u6001\u606d\u606d\u656c\u656c\uff0c\u8bed\u6c14\u66f4\u662f\u60f6\u60f6\u6050\u6050\uff0c\u5c31\u50cf\u662f\u80c6\u602f\u7684\u5f1f\u5b50\uff0c\u5728 \u4e25\u5e08\u9762\u524d\u8bf4\u8bdd\u4f3c\u7684\u3002\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u76ee\u5149\u53c8\u662f\u4e00\u9635\u95ea\u52a8\uff0c\u4f3c\u4e4e\u5728\u5947\u602a\u8fd9\u822c\u4eba\u600e\u4f1a\u5bf9\u88f4\u73cf\u5982\u6b64\u606d \u656c\uff0c\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u5728\u4e0b\u5df2\u4e3a\u76df\u4e3b\u5927\u54e5\u51c6\u5907\u5bbf\u5904\u3002\u201d \u3000\u3000\u8fd9\u6570\u5341\u6761\u6c49\u5b50\u9f50\u5730\u4e00\u9635\u53f9\u606f\uff0c\u4f3c\u4e4e\u6df1\u4ee5\u81ea\u5df1\u4e0d\u80fd\u4e3a\u201c\u88f4\u5927\u5148\u751f\u201d\u6548\u52b3\u800c\u5931\u671b\uff0c\u88f4\u73cf\u53ea\u89c9 \u5fc3\u4e2d\u4e00\u9635\u611f\u6fc0\u4e0a\u6d8c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u591a\u8c22\u5404\u4f4d\u7684\u5173\u5fc3\uff0c\u6211\u2026\u2026\u6211\u5b9e\u5728\u611f\u6fc0\u5f97\u5f88\u3002\u201d \u3000\u3000\u867d\u7136\u4ecd\u662f\u8fd9\u666e\u666e\u901a\u901a\u7684\u4e24\u65ec\u5ba2\u5957\u8bed\uff0c\u4f46\u5728\u88f4\u73cf\u53e3\u4e2d\u8bf4\u51fa\uff0c\u8ba9\u4eba\u542c\u4e86\uff0c\u5374\u662f\u53e6\u4e00\u79cd\u4e0d\u540c\u7684 \u6ecb\u5473\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u5b57\u5b57\u53e5\u53e5\u4ff1\u662f\u51fa\u81ea\u771f\u5fc3\uff0c\u4e1d\u6beb\u6ca1\u6709\u52c9\u5f3a\u7684\u505a\u4f5c\uff0c\u8fd9\u5c31\u6b63\u5982\u4ed6\u5e73\u65e5\u7684\u4e3a\u4eba\u4e00\u6837\uff0c\u8fd9 \u6837\u7684\u4eba\uff0c\u600e\u4f1a\u4e0d\u4ee4\u4eba\u8083\u7136\u8d77\u656c\uff0c\u8877\u5fc3\u4f69\u670d\uff1f \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u6697\u53f9\u4e00\u58f0\uff0c\u5fc3\u4e2d\u65e2\u662f\u5f97\u610f\uff0c\u53c8\u662f\u9ad8\u5174\uff0c\u4ed6\u4e24\u4eba\u4e00\u751f\u65e0\u5b50\u65e0\u5973\uff0c\u4ea6\u65e0\u95e8\u5f92\uff0c \u66f4\u65e0\u670b\u53cb\uff0c\u5b9e\u5c06\u88f4\u73cf\u770b\u6210\u81ea\u5df1\u5b50\u5973\u3001\u95e8\u5f92\u3001\u4eb2\u4eba\u3001\u670b\u53cb\u7684\u6df7\u5408\uff0c\u89c1\u5230\u522b\u4eba\u5bf9\u88f4\u73cf\u5982\u6b64\u5c0a\u656c\u7231 \u6234\uff0c\u5fc3\u4e2d\u81ea\u662f\u9ad8\u5174\uff0c\u4f46\u60f3\u5230\u81ea\u5df1\u4e00\u751f\u6c38\u672a\u53d7\u5230\u8fd9\u79cd\u60c5\u611f\uff0c\u53c8\u4e0d\u7981\u751f\u51fa\u611f\u89e6\u3002 \u3000\u3000\u88f4\u73cf\u8bed\u58f0\u65b9\u4e86\uff0c\u90a3\u6570\u5341\u6761\u6c49\u5b50\u5df2\u4e00\u8d77\u8eac\u8eab\u4e0b\u53bb\uff0c\u6ee1\u9762\u6fc0\u52a8\u4e4b\u8272\uff0c\u4e45\u4e45\u4e0d\u80fd\u5e73\u590d\uff0c\u88f4\u73cf\u5fc3 \u4e2d\u4ea6\u662f\u70ed\u8840\u6cb8\u817e\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002 \u3000\u3000\u7a81\u542c\u51b7\u5bd2\u7af9\u5927\u559d\u4e00\u58f0\uff1a\u201c\u95ee\u5f00\uff01\u201d \u3000\u3000\u559d\u58f0\u672a\u4e86\uff0c\u5f13\u5f26\u9aa4\u54cd\uff0c\u6570\u5341\u53ea\u9e1f\u7fbd\u957f\u7bad\uff0c\u66b4\u96e8\u4e00\u822c\u6fc0\u5c04\u800c\u81f3\uff0c\u6709\u7684\u5c04\u5411\u88f4\u73cf\uff0c\u6709\u7684\u5c04\u5411 \u90a3\u98de\u8679\uff0c\u6709\u7684\u7adf\u662f\u5c04\u5411\u90a3\u4e9b\u8eac\u8eab\u800c\u7acb\uff0c\u4e0d\u6562\u62fe\u5934\u7684\u5927\u6c49\u3002 \u3000\u3000\u88f4\u73cf\u76ee\u5149\u4e00\u51db\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u4e0d\u907f\u53cd\u8fdb\uff0c\u7adf\u5411\u8fd9\u4e00\u84ec\u98de\u7bad\u8fce\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8981\u77e5\u4ed6\u81ea\u8eab\u907f\u5f00\uff0c\u56fa\u7136\u5bb9\u6613\uff0c\u4f46\u8fd9\u4e9b\u6c49\u5b50\u5374\u4e0d\u514d\u8981\u4f24\u5728\u7bad\u4e0b\uff0c\u6b64\u523b\u4ed6\u98de\u63a0\u8fce\u4e0a\uff0c\u81ea\u8eab\u5374 \u662f\u5371\u9669\u5df2\u6781\uff0c\u4f46\u662f\u5feb\u5982\u95ea\u7535\uff0c\u773c\u89c1\u5df2\u6709\u5341\u6570\u679d\u5f29\u7bad\u3001\u5373\u5c06\u5c04\u5728\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u3001\u4e0d\u5047\u601d\u7d22\uff0c\u7acb\u523b\u968f\u4e4b\u6251\u4e0a\uff0c\u90a3\u4e9b\u6c49\u5b50\u6709\u7684\u7ffb\u6eda\u5012\u5730\uff0c\u6709\u7684\u7adf\u60f3\u4ee5\u81ea\u8eab\u4e3a\u88f4 \u73cf\u6321\u4f4f\u5f29\u7bad\u3002\u88f4\u73cf\u5578\u58f0\u672a\u7edd\uff0c\u968f\u624b\u64a4\u4e0b\u957f\u886b\uff0c\u53ea\u542c\u4e24\u80a1\u9510\u98ce\uff0c\u547c\u5578\u800c\u8d77\uff0c\u7adf\u5c06\u8fd9\u84ec\u5f29\u7bad\uff0c\u626b \u843d\u5927\u534a\uff0c\u4f59\u4e0b\u7684\u52bf\u9053\u4ea6\u53d7\u5f71\u54cd\uff0c\u8f7b\u6613\u5730\u4fbf\u88ab\u907f\u5f00\u3002\u8fd9\u53d8\u5316\u53d1\u751f\uff0c\u4e8b\u524d\u6beb\u65e0\u5f81\u5146\uff0c\u53d1\u751f\u540e\u970e\u773c \u4fbf\u8fc7\uff0c\u76f4\u5230\u6b64\u523b\u6ee1\u8857\u4e4b\u4eba\u65b9\u81ea\u53d1\u51fa\u4e00\u9635\u60ca\u547c\u4e4b\u58f0\u3002\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u9762\u4e0a\u4ea6\u4e0d\u7981\u95ea\u8fc7\u4e00\u4e1d\u611f\u52a8\u7684 \u795e\u8272\uff0c\u53ea\u89c1\u5bf9\u65b9\u5c4b\u6a90\u4e4b\u4e0a\uff0c\u4f0f\u7740\u6570\u5341\u6761\u6c49\u5b50\uff0c\u5176\u4e2d\u4e24\u4eba\u7a7f\u7740\u4e00\u8eab\u78a7\u7eff\u7684\u8863\u886b\uff0c\u5176\u4f59\u7684\u5374\u662f\u6ee1 \u8eab\u9ec4\u8863\uff0c\u624b\u4e2d\u72b9\u81ea\u62ff\u7740\u957f\u5f13\u5927\u7bad\uff0c\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u7adf\u6ca1\u6709\u4eba\u5c06\u7b2c\u4e8c\u7bad\u5c04\u5c06\u51fa\u6765\uff0c\u53ea\u662f\u5446\u5446\u5730\u671b \u7740\u88f4\u73cf\uff0c\u6ee1\u9762\u4ff1\u662f\u611f\u52a8\u4e4b\u8272\u3002\u88f4\u73cf\u6b64\u523b\u5f62\u72b6\u5374\u6781\u662f\u72fc\u72c8\uff0c\u4ed6\u4e0d\u4f46\u957f\u886b\u5df2\u88ab\u81ea\u5df1\u6495\u7834\uff0c\u7528\u505a\u6325 \u9000\u6697\u5668\uff0c\u957f\u886b\u5185\u7684\u7d27\u8eab\u8863\u886b\uff0c\u4ea6\u88ab\u4ed6\u60c5\u6025\u4e4b\u4e0b\u6495\u7834\u3002\u4ed6\u638c\u4e2d\u7684\u4e24\u7247\u8863\u886b\uff0c\u4e0d\u4f4f\u968f\u98ce\u98d8\u821e\uff0c\u4ed6 \u9762\u4e0a\u7684\u795e\u8272\uff0c\u72b9\u81ea\u60ca\u60b8\u672a\u5b9a\uff0c\u4f46\u5728\u4eba\u4eec\u773c\u4e2d\u770b\u6765\uff0c\u4e16\u4e0a\u5374\u518d\u65e0\u4e00\u4eba\u6709\u4ed6\u8fd9\u822c\u5e84\u4e25\u9ad8\u8d35\u3002\u90a3\u98de \u8679\u5389\u53f1\u4e00\u58f0\uff0c\u65b9\u5f85\u98de\u63a0\u4e0a\u5c4b\uff0c\u54ea\u77e5\u90a3\u5c4b\u6a90\u4e0a\u7684\u6c49\u5b50\uff0c\u5374\u5df2\u4e00\u8d77\u8dc3\u4e86\u4e0b\u6765\uff0c\u201d\u6251\u201c\u5730\u8dea\u5230\u5730 \u4e0a\u3002\u88f4\u73cf\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u5373\u4f7f\u4e0e\u6211\u6709\u4ec7\uff0c\u53c8\u4f55\u82e6\u4f24\u53ca\u4ed6\u4eba\uff01\u201d \u3000\u3000\u90a3\u98de\u8679\u4e00\u6b65\u8d76\u4e0a\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u2018\u91d1\u9e21\u5e2e\u2019\u4f17\u4eba\uff0c\u8eab\u7a7f\u78a7\u886b\u7684\u4e24\u4eba\uff0c\u4fbf\u662f\u5411\u4e00\u557c \u624b\u4e0b\u7684\u5927\u5c06\uff0c\u2018\u9e21\u76ee\u2019\u65b9\u5bb6\u5144\u5f1f\uff01\u201d \u3000\u3000\u88f4\u73cf\u604d\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u957f\u53f9\u9053\uff1a\u201c\u4f60\u4eec\u539f\u6765\u662f\u4e3a\u4e86\u66ff\u5e2e\u4e3b\u590d\u4ec7\uff0c\u6211\u4e0d\u602a\u4f60\uff0c\u4eca\u65e5\u4f60\u4eec\u867d\u7136 \u529f\u8d25\u5782\u6210\uff0c\u4f46\u2026\u2026\u5509\uff0c\u4f60\u4eec\u5feb\u53bb\u5427\uff0c\u4ee5\u540e\u603b\u4f1a\u6709\u590d\u4ec7\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u91d1\u9e21\u5e2e\u5374\u65e0\u4e00\u4eba\u62ac\u8d77\u5934\u6765\uff0c\u6ee1\u9762\u60f6\u6050\u540e\u6094\u4e4b\u5bb9\uff0c\u6709\u7684\u4eba\u751a\u81f3\u76ee\u4e2d\u5df2\u662f\u70ed\u6cea\u76c8\u7736\uff0c\u4f0f\u5728\u5730 \u4e0a\uff0c\u4e0d\u4f4f\u5730\u53e9\u9996\u8bf7\u7f6a\u3002 \u3000\u3000\u201c\u9e21\u76ee\u201d\u65b9\u6c0f\u5144\u5f1f\u4e2d\u7684\u65b9\u4e00\u5947\u4f0f\u9996\u9053\uff1a\u201c\u5c0f\u7684\u4eec\u4e0d\u77e5\u88f4\u5927\u5148\u751f\u7adf\u7136\u5982\u6b64\u4ec1\u4e49\u6177\u6168\uff0c\u662f\u4ee5 \u624d\u505a\u51fa\u8fd9\u7b49\u4e8b\u60c5\uff01\u6b64\u523b\u4f46\u51ed\u76df\u4e3b\u4f60\u8d23\u7f5a\uff0c\u5c0f\u7684\u4eec\u6ca1\u6709\u534a\u53e5\u6028\u8a00\u3002\u201d \u3000\u3000\u201c\u9e21\u76ee\u201d\u65b9\u4e00\u5076\u4ea6\u81ea\u4f0f\u9996\u9053\uff1a\u201c\u76df\u4e3b\u5982\u6b64\u4ec1\u4e49\uff0c\u5c0f\u7684\u4eec\u4ee5\u540e\u600e\u6562\u518d\u6709\u53cd\u53db\u4e4b\u5fc3\uff1f\u4eca\u65e5\u53d7 \u8fd9\u8d23\u7f5a\uff0c\u7eb5\u7136\u76df\u4e3b\u4e0d\u613f\uff0c\u5c0f\u7684\u4eec\u4e5f\u8981\u8ddf\u5728\u76df\u4e3b\u8eab\u540e\uff0c\u4e3a\u76df\u4e3b\u6548\u52b3\u3002\u201d \u3000\u3000\u88f4\u73cf\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5404\u4f4d\u5c31\u8bf7\u5feb\u4e9b\u8d77\u6765\uff0c\u96ea\u5730\u4e25\u5bd2\uff0c\u5404\u4f4d\u4f11\u8981\u51bb\u574f\u4e86\u8eab \u4f53\u3002\u201d \u3000\u3000\u4e25\u98ce\u51c9\u51bd\uff0c\u5439\u5f97\u4ed6\u6495\u88c2\u4e86\u7684\u8863\u886b\u4e2d\u4e1d\u697c\u68c9\u7d6e\uff0c\u6709\u5982\u96ea\u82b1\u822c\u56db\u6563\u98de\u843d\uff0c\u4e00\u6761\u5927\u6c49\u6084\u6084\u89e3\u5f00 \u81ea\u5df1\u7684\u957f\u886b\uff0c\u53cc\u624b\u6367\u5728\u88f4\u73cf\u8eab\u524d\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u4f46\u5374\u4e00\u8a00\u4e0d\u53d1\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5fc3\u4e2d\u7684\u611f\u6fc0\u5df2\u975e\u8a00\u8bed\u6240\u80fd\u8868\u8fbe\uff0c\u6b64\u523b\u83ab\u8bf4\u8981\u4ed6\u4eec\u89e3\u4e0b\u957f \u886b\uff0c\u4f46\u662f\u6559\u4ed6\u4eec\u629b\u5934\u9885\uff0c\u6d12\u70ed\u8840\uff0c\u4e5f\u65e0\u4e00\u4eba\u4f1a\u72b9\u8c6b\u4e00\u4e0b\u3002 \u3000\u3000\u88f4\u73cf\u5446\u5446\u5730\u671b\u7740\u8fd9\u4e9b\u70ed\u8840\u98de\u626c\u7684\u6c49\u5b50\uff0c\u4ee5\u53ca\u90a3\u4e9b\u72b9\u81ea\u8dea\u5728\u5730\u4e0a\u4e0d\u6562\u8d77\u8eab\u7684\u91d1\u9e21\u5e2e\u4f17\uff0c\u5450 \u5450\u9053\uff1a\u201c\u5404\u2026\u2026\u5404\u2026\u2026\u4f4d\u2026\u2026\u201d \u3000\u3000\u4f46\u662f\u4ed6\u53ea\u89c9\u5589\u5934\u54fd\u54bd\uff0c\u4ea6\u81ea\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u6ee1\u8857\u4e4b\u4eba\u773c\u671b\u7740\u8fd9\u4e00\u5e55\u611f\u4eba\u7684\u60c5\u666f\uff0c\u5404\u5404\u5fc3 \u4e2d\uff0c\u4ff1\u662f\u611f\u53f9\u4e0d\u5df2\uff0c\u53ea\u6709\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5374\u4fcf\u6084\u5782\u4e0b\u5934\u53bb\uff0c\u5374\u4e0d\u77e5\u4ed6\u662f\u5728\u611f\u53f9\u557c\u5618\uff0c\u6291\u6216\u662f\u5728 \u81ea\u759a\u60ed\u6127\uff01 \u3000\u3000\u96ea\u52bf\u505c\u505c\u6b47\u6b47\uff0c\u5730\u4e0a\u7684\u79ef\u96ea\uff0c\u5374\u66f4\u539a\u4e86\u3002 \u3000\u3000\u57ce\u90ca\u7684\u79ef\u96ea\uff0c\u66f4\u539a\u4e8e\u57ce\u5185\uff0c\u5927\u5730\u4e00\u7247\u94f6\u767d\uff0c\u9ec4\u660f\u540e\uff0c\u8fd9\u4e00\u7247\u94f6\u767d\u7684\u4e16\u754c\uff0c\u4fbf\u8f6c\u53d8\u6210\u4e00\u79cd \u6d45\u7070\u7684\u989c\u8272\uff0c\u5230\u4e86\u6df1\u591c\uff0c\u53ea\u89c1\u5929\u5730\u95f4\u4ff1\u662f\u4e00\u7247\u7070\u9eef\uff0c\u4e5f\u5206\u4e0d\u51fa\u54ea\u91cc\u662f\u539f\u91ce\uff0c\u54ea\u91cc\u662f\u6811\u6728\uff0c\u54ea \u91cc\u662f\u5c4b\u5b57\u3002 \u3000\u3000\u56db\u91ce\u5bc2\u65e0\u4eba\u8ff9\uff0c\u4e00\u95f4\u5c0f\u5c0f\u7684\u571f\u5730\u67ef\u524d\uff0c\u5374\u5353\u7acb\u7740\u4e00\u4e2a\u5341\u56db\u4e94\u5c81\u3002 \u3000\u3000\u8eab\u6750\u7ea4\u5f31\uff0c\u8863\u886b\u5355\u8584\u7684\u5973\u5b69\uff0c\u5728\u8fd9\u51c4\u6e05\u7684\u5bd2\u591c\u91cc\uff0c\u66f4\u663e\u5f97\u4f36\u6c40\u5b64\u82e6\u3002 \u3000\u3000\u7960\u5802\u5185\u6709\u4e00\u76cf\u5c0f\u5c0f\u7684\u957f\u660e\u4e4b\u706f\uff0c\u660f\u9ec4\u7684\u706f\u706b\uff0c\u6620\u7740\u5979\u4f36\u4ec3\u7684\u8eab\u5f71\uff0c\u4f46\u96ea\u5730\u4e0a\u7684\u5f71\u5b50\uff0c\u5374 \u53c8\u600e\u80fd\u89e3\u9664\u5979\u7684\u9965\u5bd2\u4e0e\u5bc2\u5bde\uff01 \u3000\u3000\u53ea\u6709\u5979\u90a3\u4e00\u53cc\u7075\u6d3b\u7684\u5927\u773c\u775b\uff0c\u7adf\u6709\u5982\u79cb\u591c\u7a79\u82cd\u4e2d\u7684\u660e\u661f\u4e00\u822c\u70c1\u8000\u7740\uff0c\u5979\u660e\u4eae\u7684\u76ee\u5149\u4e2d\uff0c \u663e\u9732\u51fa\u7684\u662f\u7126\u6025\u4e0e\u7b49\u5f85\u3002 \u3000\u3000\u5979\u5728\u7b49\u5f85\u7740\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u5bf9\u65b9\u7684\u4e00\u680b\u5c4b\u5b87\uff0c\u5979\u773c\u770b\u7740\u8fd9\u680b\u5c4b\u5b87\u4e2d\u6742\u4e71\u7684\u4eba\u58f0\uff0c\u6e10\u6e10\u9759\u5bc2\uff0c\u660e\u4eae \u7684\u706f\u706b\uff0c\u6e10\u6e10\u7a00\u843d\u2026\u2026 \u3000\u3000\u4e00\u9635\u5bd2\u98ce\u5439\u6765\uff0c\u5979\u673a\u601c\u4f36\u6253\u4e86\u4e2a\u5bd2\u6218\uff0c\u50cf\u662f\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u8f7b\u8f7b\u54ac\u4e86\u54ac\u7259\uff0c\u56de\u8eab\u8eac\u4e86\u4e00 \u8eac\uff0c\u7ec6\u8bed\u9053\uff1a\u201c\u571f\u5730\u516c\u516c\uff0c\u8c22\u8c22\u4f60\u3002\u201d \u3000\u3000\u7136\u540e\u5979\u8c28\u614e\u800c\u5c0f\u5fc3\u5730\u5411\u90a3\u680b\u5c4b\u5b57\u5954\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u8eab\u5f62\u5e76\u4e0d\u8f7b\u7075\uff0c\u66f4\u4e0d\u8fc5\u7094\uff0c\u663e\u89c1\u5979\u5e76\u6ca1\u6709\u7ec3\u8fc7\u4ec0\u4e48\u6b66\u529f\uff0c\u4f46\u662f\u5979\u660e\u4eae\u800c\u5584\u826f\u7684\u76ee\u5149 \u4e2d\uff0c\u5374\u6709\u4e00\u79cd\u575a\u97e7\u4e4b\u8272\u3002 \u3000\u3000\u5979\u5954\u5230\u5899\u8fb9\uff0c\u671b\u4e86\u671b\u9ad8\u7ea6\u4e00\u4e08\u4e09\u56db\u7684\u5899\u58c1\uff0c\u594b\u8eab\u4e00\u8dc3\uff0c\u53cc\u624b\u65b9\u81ea\u642d\u5728\u5899\u5934\uff0c\u5374\u53c8\u6ed1\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u4f46\u662f\u5979\u7edd\u4e0d\u7070\u5fc3\uff0c\u7acb\u523b\u518d\u6b21\u4e00\u8dc3\uff0c\u6ed1\u4e0b\u53bb\u53c8\u4e00\u8dc3\u2026\u2026 \u3000\u3000\u7ec8\u4e8e\uff0c\u5979\u624b\u8db3\u5e76\u7528\u5730\u722c\u4e86\u4e0a\u53bb\uff0c\u5979\u8f7b\u8f7b\u5618\u4e86\u4e00\u53e3\u6c14\uff0c\u660e\u4eae\u7684\u76ee\u5149\uff0c\u56db\u4e0b\u4e00\u8f6c\uff0c\u53ea\u89c1\u6ee1\u9662 \u6df1\u6c89\uff0c\u591c\u9759\u5982\u6c34\u3002 \u3000\u3000\u5979\u4e0d\u7981\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u8bed\u7740\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u79ef\u96ea\u7684\u591c\u9662\u4e2d\uff0c\u7ecf\u8fc7\u4e00\u5929\u5174\u594b\u540e\u7684\u88f4\u73cf\uff0c\u6b63\u6beb\u65e0\u75b2\u5026\u4e4b\u610f\u5730\u5b64\u7acb\u5728\u4e00\u682a\u67af\u840e\u4e86\u7684\u767d\u6768\u6811 \u4e0b\u3002 \u3000\u3000\u82cd\u7a79\uff0c\u662f\u7070\u9eef\u7684\uff0c\u6ca1\u6709\u661f\u5149\uff0c\u66f4\u6ca1\u6709\u6708\u8272\uff0c\u4ed6\u51dd\u6ce8\u7740\u56db\u4e0b\u7684\u7691\u7691\u767d\u96ea\uff0c\u5fc3\u4e2d\u601d\u6f6e\uff0c\u5c31\u6b63 \u5982\u539f\u91ce\u4e0a\u7684\u72c2\u98ce\u4e00\u6837\uff0c\u72c2\u5578\u6765\u53bb\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002 \u3000\u3000\u5728\u8fd9\u540c\u6837\u7684\u5bd2\u591c\u4e2d\uff0c\u4ed6\u66fe\u5b64\u7acb\u5728\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u7684\u67af\u6728\u4e0b\uff0c\u75db\u6068\u7740\u81ea\u5df1\u7684\u611a\u8822\uff0c\u75db\u6068\u7740 \u81ea\u5df1\u4e3a\u4ec0\u4e48\u6c38\u8fdc\u5b66\u4e0d\u4f1a\u6b66\u529f\uff0c\u5b66\u4e0d\u4f1a\u4e00\u5207\u2026\u2026 \u3000\u3000\u90a3\u65f6\uff0c\u4ed6\u4f1a\u75db\u82e6\u5730\u6697\u81ea\u6d41\u7740\u773c\u6cea\u611f\u6000\uff0c\u770b\u81ea\u5df1\u5b64\u82e6\u7684\u8eab\u4e16\uff0c\u4e0d\u5e78\u7684\u906d\u9047\uff0c\u671b\u7740\u53e6\u4e00\u91cd\u9662 \u843d\uff0c\u7fa1\u6155\u7740\u90a3\u4e00\u91cd\u9662\u843d\u4e2d\u7684\u5e78\u798f\uff0c\u4e5f\u5fc6\u5ff5\u7740\u90a3\u4e00\u91cd\u9662\u843d\u4e2d\u6a80\u6587\u742a\u505c\u87c0\u7684\u8eab\u5f71\uff0c\u7075\u6d3b\u7684\u773c\u6ce2\u3002 \u3000\u3000\u90a3\u65f6\uff0c\u4ed6\u8eab\u540e\u5e38\u5e38\u4f1a\u6709\u4e00\u53ea\u6e29\u6696\u7684\u5c0f\u624b\uff0c\u7a81\u7136\u4f38\u51fa\u6765\u4e3a\u4ed6\u8f7b\u62ed\u6cea\u73e0\uff0c\u4e8e\u662f\u4ed6\u5c31\u4f1a\u5b89\u6170\u5730 \u88ab\u8fd9\u53ea\u5c0f\u624b\u62c9\u56de\u5c4b\u91cc\u3002 \u3000\u3000\u4f46\u662f\uff0c\u8fd9\u53cc\u5c0f\u624b\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u662f\u4e0d\u662f\u8fd8\u5728\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e2d\u5fcd\u53d7\u7740\u75db\u82e6\uff0c\u8f7b\u8511\u4e0e\u5bc2\u5bde\uff1f \u3000\u3000\u4ed6\u75db\u82e6\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u53d1\u8a93\u8981\u4ee5\u81ea\u5df1\u7684\u624b\uff0c\u6765\u64e6\u62ed\u8fd9\u53cc\u5c11\u5e74\u4eba\u7684\u6cea\u6b8a\uff0c\u4ece\u4e00\u53cc\u660e\u4eae\u7684\u5927\u773c \u775b\u4e2d\u6d41\u4e0b\u7684\u6cea\u73e0\u3002 \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u53c8\u60f3\u8d77\u4eca\u65e5\u5728\u4eba\u4e1b\u4e2d\u7684\u90a3\u4e00\u53cc\u660e\u4eae\u7684\u773c\u775b\uff0c\u4f46\u662f\u4ed6\u7acb\u523b\u53f9\u606f\u4e00\u58f0\uff0c\u5583\u5583\u81ea\u8bed\u7740 \u9053\uff1a\u201c\u4e0d\u4f1a\u662f\u5979\uff0c\u82e5\u662f\u5979\u600e\u4f1a\u907f\u5f00\u6211\uff1f\u201d \u3000\u3000\u4e5f\u662f\u5728\u8fd9\u540c\u6837\u7684\u5bd2\u591c\u91cc\uff0c\u4ed6\u66fe\u5c48\u8fb1\u5730\u5367\u5728\u90a3\u964c\u751f\u7684\u5c4b\u6a90\u4e0b\uff0c\u5e26\u7740\u4e00\u5929\u5351\u8d31\u5de5\u4f5c\u540e\u7684\u52b3\u82e6 \u75b2\u5026\uff0c\u9ed8\u9ed8\u5730\u5fcd\u53d7\u5bd2\u51b7\u3001\u9965\u997f\u3001\u75db\u6614\u3001\u5931\u671b\u2026\u2026 \u3000\u3000\u4ee5\u53ca\u4ed6\u6700\u6700\u4e0d\u80fd\u5fcd\u53d7\u7684\u3001\u90a3\u523b\u9aa8\u94ed\u5fc3\u7684\u76f8\u601d\u3002 \u3000\u3000\u90a3\u523b\u9aa8\u94ed\u5fc3\u7684\u76f8\u601d\uff0c\u6b64\u523b\u8fd8\u7559\u5728\u4ed6\u5fc3\u5e95\uff0c\u4f46\u662f\u5374\u53c8\u52a0\u6df1\u4e86\u51e0\u5206\u75db\u82e6\uff0c\u56e0\u4e3a\u4ed6\u76f8\u601d\u7684\u5bf9 \u8c61\uff0c\u4e0e\u4ed6\u4e4b\u95f4\u5b9e\u5728\u9694\u79bb\u7740\u4e00\u91cd\u65e0\u6cd5\u6500\u8d8a\u7684\u95e8\u6237\uff0c\u4ed6\u53ea\u80fd\u6068\u9020\u5316\u7684\u6349\u5f04\uff0c\u4e3a\u4ec0\u4e48\u53eb\u4ed6\u7231\u4e0a\u4e00\u4e2a \u81ea\u5df1\u4e0d\u80fd\u7231\u7684\u5973\u5b50\u3002 \u3000\u3000\u4ed6\u601d\u6f6e\u7a81\u7136\u53c8\u56de\u5230\u8bb8\u4e45\u8bb8\u4e45\u4ee5\u524d\uff0c\u90a3\u4e5f\u662f\u4e00\u4e2a\u548c\u4eca\u591c\u76f8\u540c\u7684\u5bd2\u591c\uff0c\u4ed6\u88ab\u4e00\u9635\u5669\u68a6\u60ca\u9192 \u540e\uff0c\u4fbf\u518d\u4e5f\u65e0\u6cd5\u4eba\u7761\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u4fbf\u542c\u5230\u4ed6\u7684\u7236\u4eb2\u4e0e\u53d4\u7236\u7684\u6076\u8017\uff0c\u5f53\u65f6\u7684\u60b2\u54c0\u4e0e\u75db\u82e6\uff0c\u6b64\u523b\u4f3c\u4e4e\u53c8\u4e00\u8d77\u56de\u5230\u4ed6\u5fc3 \u5e95\u3002 \u3000\u3000\u6240\u6709\u7684\u4e00\u5207\uff0c\u79bb\u6b64\u523b\u867d\u7136\u90fd\u5df2\u9065\u8fdc\uff0c\u4f46\u5374\u53c8\u4f3c\u4ff1\u5728\u773c\u524d\uff0c\u4e16\u4e0a\u5404\u5730\u7684\u5bd2\u591c\u867d\u7136\u4ff1\u90fd\u76f8 \u540c\uff0c\u79ef\u96ea\u7684\u989c\u8272\u4e5f\u90fd\u4e00\u6837\uff0c\u4f46\u662f\u2026\u2026 \u3000\u3000\u4e16\u4e8b\u7684\u53d8\u5e7b\u5374\u662f\u591a\u4e48\u79bb\u5947\uff0c\u591a\u4e48\u5de8\u5927\u5462\uff1f \u3000\u3000\u90a3\u5b64\u82e6\u3001\u61e6\u5f31\uff0c\u53d7\u5c3d\u6b3a\u51cc\uff0c\u53d7\u5c3d\u767d\u773c\u7684\u5c11\u5e74\uff0c\u771f\u7684\u5c31\u662f\u4eca\u65e5\u7684\u81ea\u5df1\u4e48\uff1f\u4ed6\u4e0d\u80fd\u76f8\u4fe1\uff0c\u4e0d \u6562\u76f8\u4fe1\uff0c\u5374\u53c8\u600e\u80fd\u4e0d\u76f8\u4fe1\u5462\uff1f \u3000\u3000\u5e78\u798f\u4e0e\u5149\u8363\uff0c\u5c31\u50cf\u662f\u4e00\u9053\u95ee\u7535\u4e00\u6837\uff0c\u7a81\u7136\u70b9\u4eae\u4e86\uff0c\u662f\u6765\u5f97\u592a\u5feb\u4e86\u4e48\uff1f\u4f46\u5374\u6709\u4eba\u66ff\u4ed6\u60cb\u501f \u6765\u5f97\u592a\u6162\u4e86\u54e9\uff01 \u3000\u3000\u4ed6\u53ea\u89c9\u9762\u4e0a\u4e00\u7247\u5bd2\u51c9\uff0c\u539f\u6765\u4e0d\u77e5\u5728\u4f55\u65f6\u4ed6\u5df2\u6d41\u4e0b\u4e86\u6ee1\u9762\u6cea\u73e0\u3002 \u3000\u3000\u4ed6\u770b\u4e0d\u5230\u660f\u51a5\u7684\u591c\u9662\u4e2d\uff0c\u6b63\u6709\u4e00\u6761\u4f36\u4ec3\u7684\u4eba\u5f71\uff0c\u7f13\u7f13\u5411\u4ed6\u8d70\u4e86\u8fc7\u6765\uff0c\u505c\u4e0b\uff0c\u884c\u8d70\uff0c\u53c8\u505c \u4e0b\u2026\u2026 \u3000\u3000\u7ec8\u4e8e\u8d70\u5230\u4ed6\u8eab\u4fa7\u3002 \u3000\u3000\u4ed6\u84e6\u7136\u8b66\u89c9\uff0c\u970d\u7136\u56de\u9996\uff0c\u4e00\u53ea\u7ea4\u67d4\u7684\u5c0f\u624b\uff0c\u6b63\u98a4\u6296\u7740\u4e3e\u5728\u4ed6\u9762\u524d\uff0c\u5c31\u50cf\u5f80\u6614\u65f6\uff0c\u5bd2\u591c \u4e2d\uff0c\u90a3\u6c38\u96be\u5fd8\u6000\u7684\u60c5\u666f\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u7a81\u7136\u800c\u6765\u7684\u8c05\u559c\uff0c\u4f7f\u5f97\u4ed6\u50cf\u5446\u5b50\u4e00\u6837\u5730\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u7ea4\u67d4\u7684\u5c0f\u624b\uff0c\u98a4\u6296\u5f97\u66f4\u52a0\u5267\u70c8\u3002 \u3000\u3000\u660e\u4eae\u7684\u773c\u775b\uff0c\u73cd\u73e0\u822c\u6d41\u4e0b\u4e00\u8fde\u4e32\u6b22\u559c\u800c\u53c8\u60b2\u4f24\u3001\u60b2\u4f24\u800c\u53c8\u6b22\u4e50\u7684\u6cea\u73e0\uff0c\u4e00\u8fde\u4e32\u6d41\u5728\u96ea\u5730 \u4e0a\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u7ec8\u4e8e\uff0c\u88f4\u73cf\u5927\u559d\u4e00\u58f0\uff1a\u201c\u73cd\u73cd\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u54e5\u54e5\u2026\u2026\u5927\u54e5\u54e5\u2026\u2026\u5927\u54e5\u54e5\u2026\u2026\u201d \u3000\u3000\u4e5f\u4e0d\u77e5\u5979\u5524\u4e86\u591a\u5c11\u58f0\u201c\u5927\u54e5\u54e5\u201d\uff0c\u53ea\u77e5\u5979\u7ec8\u4e8e\u6251\u5728\u5979\u7684\u5927\u54e5\u54e5\u8eab\u4e0a\uff0c\u653e\u58f0\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u53c8\u6709\u4e24\u6761\u4eba\u5f71\u95ea\u8fc7\uff0c\u90a3\u6b63\u662f\u4e0e\u88f4\u73cf\u4e00\u8d77\u4f4f\u5728\u540e\u9662\u4e2d\u7684\u201c\u51b7\u8c37\u53cc\u6728\u201d\uff0c\u4ed6\u5144\u5f1f\u4e24\u4eba \u51fa\u795e\u5730\u5411\u8fd9\u8fb9\u5446\u671b\u4e86\u534a\u664c\uff0c\u4e24\u4eba\u9f50\u5730\u8f7b\u53f9\u4e00\u58f0\uff0c\u8e51\u7740\u811a\u6b65\uff0c\u56de\u5230\u5c4b\u91cc\uff0c\u51b7\u5bd2\u7af9\u5fcd\u4e0d\u4f4f\u8f7b\u8f7b\u8bf4 \u9053\uff1a\u201c\u8fd9\u4e2a\u5973\u5b69\u5b50\u5927\u7ea6\u5c31\u662f\u73cf\u513f\u66fe\u7ecf\u8bf4\u8d77\u8fc7\u7684\u8881\u6cf8\u73cd\u5427\uff1f\u201d\u60f3\u4e0d\u5230\u51b7\u67af\u6728\u9053\uff1a\u201c\u5618\uff0c\u8ba9\u4ed6\u4eec \u53bb\u6b22\u559c\uff0c\u53bb\u6d41\u6cea\uff0c\u73cf\u513f\u2026\u2026\u5509\uff0c\u4ed6\u4e5f\u8be5\u88ab\u4eba\u5b89\u6170\u4e00\u4e0b\u4e86\uff0c\u4ed6\u4e5f\u503c\u5f97\u88ab\u4eba\u5b89\u6170\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u5144\u5f1f\u800c\u4eba\u4e00\u8d77\u6ca1\u4eba\u9ed1\u6697\uff0c\u53ea\u7559\u4e0b\u4e00\u4e1d\u4ecd\u7136\u8361\u6f3e\u7740\u7684\u53f9\u606f\u58f0\u3002 \u3000\u3000\u88f4\u73cf\u7d27\u7d27\u5730\u5c06\u8881\u6cf8\u73cd\u62e5\u5728\u6000\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u8bb8\u4e45\uff0c\u4ed6\u624d\u677e\u5f00\u5979\uff0c\u8ba9\u5979\u770b\u4ed6\u4e00\u773c\uff0c\u8ba9\u4ed6\u4e5f \u770b\u5979\u4e00\u773c\u3002 \u3000\u3000\u4ed6\u75db\u82e6\u5730\u6b22\u7b11\u7740\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u957f\u5927\u4e86\u3002\u201d \u3000\u3000\u5979\u5782\u4e0b\u5934\uff0c\u8ba9\u957f\u957f\u7684\u776b\u6bdb\u8986\u76d6\u7740\u773c\u5e18\uff0c\u5979\u8f7b\u8f7b\u8bf4\uff1a\u201c\u4eca\u5929\u767d\u5929\uff0c\u6211\u5c31\u770b\u5230\u4e86\u4f60\uff0c\u6211\u60f3\u4e0d \u5230\u4f60\u5df2\u53d8\u6210\u4e86\u4e00\u4e2a\u82f1\u96c4\uff0c\u5c31\u50cf\u6211\u4eec\u90a3\u65f6\u505a\u68a6\u65f6\u5e38\u5e38\u4f1a\u68a6\u5230\u7684\u4e00\u6837\uff0c\u4f46\u662f\u6211\u4e0d\u6562\u73b0\u8eab\uff0c\u8857\u4e0a \u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u4eba\u90a3\u4e48\u591a\uff0c\u6211\u6015\u4ed6\u4eec\u6293\u6211\u56de\u53bb\uff0c\u53c8\u6015\u4ed6\u4eec\u53bb\u544a\u8bc9\u6a80\u2026\u2026\u5927\u53d4\uff01\u201d \u3000\u3000\u5979\u867d\u7136\u4e0d\u613f\u8bf4\u51fa\u201c\u5927\u53d4\u201d\u4e24\u5b57\uff0c\u4f46\u591a\u5e74\u6765\u7684\u4e60\u60ef\u53c8\u5c82\u662f\u9aa4\u7136\u53ef\u6539\uff1f \u3000\u3000\u88f4\u73cf\u771f\u7684\u7b11\u4e86\uff0c\u4f46\u7b11\u4e2d\u4ecd\u7136\u6709\u6cea\uff0c\u4ed6\u8bf4\uff1a\u201c\u4ece\u6b64\u4ee5\u540e\uff0c\u4f60\u53ef\u4ee5\u518d\u4e5f\u4e0d\u7528\u6015\u4e86\uff0c\u65e0\u8bba\u4ec0\u4e48 \u4e8b\uff0c\u6211\u90fd\u53ef\u4ee5\u4fdd\u62a4\u4f60\u3002\u201d \u3000\u3000\u8881\u6cf8\u73cd\u4ef0\u8d77\u5934\uff0c\u51dd\u671b\u7740\u4ed6\uff0c\u5c31\u50cf\u4efb\u4f55\u4e00\u4e2a\u5973\u5b69\u5b50\u51dd\u6ce8\u7740\u81ea\u5df1\u7684\u68a6\u4e2d\u7684\u738b\u5b50\u4e00\u6837\uff0c\u65e2\u6b23 \u559c\uff0c\u53c8\u503e\u6155\u3002 \u3000\u3000\u4ed6\u7d6e\u7d6e\u5730\u95ee\u7740\u5979\u8fd9\u4e24\u5e74\u6765\u7684\u751f\u6d3b\u3002 \u3000\u3000\u5979\u548c\u7740\u6cea\uff0c\u5e26\u7740\u7b11\u544a\u8bc9\u4ed6\uff0c\u5e73\u51e1\u7684\u751f\u6d3b\uff0c\u75db\u82e6\u7684\u751f\u6d3b\uff0c\u5bc2\u5bde\u7684\u751f\u6d3b\u2026\u2026\u6b64\u523b\u4f3c\u4e4e\u90fd\u5df2\u6210 \u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u53c8\u7d6e\u7d6e\u5730\u544a\u8bc9\u5979\u8fd9\u4e9b\u5e74\u6765\uff0c\u81ea\u5df1\u90a3\u795e\u5947\u800c\u7384\u5999\u7684\u7ecf\u5386\u3001\u75db\u82e6\u800c\u53c9\u60b2\u60e8\u7684\u7ecf\u5386\u3002 \u3000\u3000\u5979\u7741\u5927\u7740\u773c\u775b\uff0c\u9ed8\u9ed8\u5730\u542c\u7740\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u5979\u660e\u4eae\u7684\u773c\u775b\u9732\u51fa\u4e00\u9635\u4ec7\u6068\u4e0e\u6124\u6012\uff0c\u5979\u63e1\u7d27\u4e86\u53cc\u62f3\uff0c\u4ef0\u7740\u5934\u9885\uff0c\u6c89\u91cd\u4ed6\u8bf4\uff1a\u3001\u6211 \u5077\u5077\u5730\u542c\u4e86\u8bb8\u591a\u4eba\u7684\u8bdd\uff0c\u5728\u8def\u4e0a\uff0c\u5728\u9556\u5c40\u91cc\uff0c\u6211\u90fd\u542c\u5230\u8fc7\uff0c\u6211\u4eec\u7684\u7239\u7239\uff0c\u771f\u5730\u662f\u88ab\u2026\u2026\u88ab\u90a3 \u4e2a\u4eba\u5bb3\u6b7b\u7684\u4e48\uff1f\u201c\u88f4\u73cf\u54ac\u7d27\u7259\u5173\uff0c\u6c89\u91cd\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u4ed6\u54ac\u7259\u54ac\u5f97\u90a3\u4e48\u7d27\uff0c\u751a\u81f3\u6709\u4e00\u4e1d\u6de1\u6de1\u7684 \u9c9c\u8840\uff0c\u81ea\u4ed6\u5634\u89d2\u6c81\u51fa\u3002\u8881\u6cf8\u73cd\u53c8\u75db\u54ed\u4e86\uff0c\u4f0f\u5728\u4ed6\u8eab\u4e0a\uff0c\u75db\u54ed\u7740\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u2026\u2026\u4f60\u8981\u4e3a \u6211\u4eec\u7684\u7239\u7239\u590d\u4ec7\u5440\uff01\u201d \u3000\u3000\u88f4\u73cf\u8f7b\u62cd\u7740\u5979\u7684\u80a9\u5934\uff0c\u5583\u5583\u7740\u9053\uff1a\u201c\u590d\u4ec7\uff0c\u590d\u4ec7\uff0c\u590d\u4ec7\uff01\u201d \u3000\u3000\u5ffd\u7136\uff0c\u5979\u53c8\u987f\u4f4f\u54ed\u58f0\uff0c\u4ef0\u8d77\u4e86\u5934\uff0c\u90a3\u660e\u4eae\u7684\u773c\u775b\u4e2d\uff0c\u7adf\u6d41\u9732\u51fa\u4e00\u9635\u601c\u60af\uff0c\u540c\u60c5\u4e0e\u60b2\u54c0\uff0c \u75db\u82e6\uff01 \u3000\u3000\u5979\u76b1\u7d27\u4e86\u53cc\u7709\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u53ef\u601c\uff0c\u53ef\u601c\u2026\u2026\u6700\u53ef\u601c\u7684\u5c31\u662f\u6a80\u59d0\u59d0\u4e86\uff01\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u5979\u4e3a \u4e86\u4f60\uff0c\u662f\u591a\u4e48\u75db\u82e6\uff0c\u5979\u4e00\u4e2a\u4eba\u8eb2\u8fdb\u623f\u91cc\uff0c\u4e00\u4f1a\u513f\u54ed\uff0c\u4e00\u4f1a\u513f\u7b11\uff0c\u4e00\u4f1a\u513f\u8bf4\u4f60\u5bf9\u4e0d\u8d77\u5979\uff0c\u4e00\u4f1a \u513f\u53c8\u8bf4\u5979\u5bf9\u4e0d\u8d77\u4f60\uff0c\u5e38\u5e38\u628a\u6211\u62c9\u5230\u5979\u623f\u91cc\u53bb\u8c08\u5929\uff0c\u4f46\u662f\u9664\u4e86\u4f60\uff0c\u5979\u4ec0\u4e48\u90fd\u4e0d\u8c08\uff0c\u8c08\u4e86\u53c8\u54ed\uff0c \u54ed\u4e86\u53c8\u8c08\uff01\u201d \u3000\u3000\u5979\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff1a\u5782\u4e0b\u5934\u53bb\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u88f4\u73cf\u53ea\u89c9\u4e00\u9635\u70ed\u8840\u6d8c\u4e0a\u5fc3\u5934\uff0c\u7ade\u53c8\u5446\u5446\u5730\u6014\u4f4f \u4e86\u3002 \u3000\u3000\u826f\u4e45\uff0c\u53ea\u542c\u8881\u6cf8\u73cd\u53c8\u9053\uff1a\u201c\u540e\u6765\uff0c\u542c\u8bf4\u5979\u7239\u7239\u6709\u610f\u8981\u628a\u5979\u5ac1\u7ed9\u4ec0\u4e48\u4e1c\u65b9\u5144\u5f1f\uff0c\u5979\u5c31\u9003\u4e86 \u51fa\u6765\uff0c\u4f46\u53c8\u88ab\u5979\u7239\u7239\u6349\u4e86\u56de\u53bb\uff0c\u5979\u8981\u6b7b\u8981\u6d3b\uff0c\u76f4\u5230\u5979\u7239\u7239\u56de\u7edd\u4e86\u4e1c\u65b9\u5144\u5f1f\uff0c\u4f46\u662f\u2026\u2026\u6211\u8dd1\u51fa \u6765\u540e\uff0c\u53c8\u542c\u5230\u5979\u8981\u5ac1\u7ed9\u4e1c\u65b9\u5144\u5f1f\u7684\u6d88\u606f\uff0c\u5509\uff01\u5979\u542c\u5230\u4e4b\u540e\uff0c\u53c8\u4e0d\u77e5\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6728\u7acb\u5f53\u5730\uff0c\u5583\u5583\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u662f\u7231\u6211\u7684\u4e48\uff1f\u201d \u3000\u3000\u8881\u6cf8\u73cd\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u8033\u7554\u201c\u55e1\u201d\u7136\u4e00\u58f0\uff0c\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u827e\u9752\u4e34\u6b7b\u524d\u7684\u8bdd\uff0c\u4eff\u4f5b\u53c8\u5728\u4ed6\u8033\u7554\u54cd\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u201c\u2026\u2026\u4f60\u4ece\u4eca\u4ee5\u540e\uff0c\u6709\u751f\u4e4b\u5e74\uff0c\u6c38\u8fdc\u4e0d\u8981\u518d\u53bb\u6b3a\u9a97\u4efb\u4f55\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u6c38\u8fdc\u4e0d\u8981\u53eb\u4e00\u4e2a\u5973 \u5b69\u5b50\u4f24\u5fc3\uff0c\u4e0d\u7ba1\u4f60\u7231\u4e0d\u7231\u5979\uff0c\u53ea\u8981\u5979\u5bf9\u4f60\u597d\uff0c\u4f60\u5c31\u8be5\u597d\u597d\u4fdd\u62a4\u7740\u5979\uff0c\u65e0\u8bba\u4e3a\u4e86\u4ec0\u4e48\u539f\u56e0\uff0c\u90fd \u4e0d\u8981\u4f24\u5bb3\u5979\uff0c\u4e5f\u4e0d\u8981\u8ba9\u5979\u53d7\u5230\u522b\u4eba\u7684\u4f24\u5bb3\uff01\u201d \u3000\u3000\u4ed6\u76ee\u5149\u51dd\u6ce8\u7740\u51b0\u96ea\uff0c\u53c8\u81ea\u5583\u5583\u4f4e\u8bed\uff0c\u201c\u65e2\u5df2\u53d1\u4e0b\u4e86\u91cd\u8a93\uff0c\u6211\u600e\u80fd\u4f24\u5bb3\u5979\u5462\uff1f\u5979\u2026\u2026\u5979\u6bd5 \u7adf\u662f\u7231\u6211\u7684\u5440\uff01\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u75db\u82e6\u5730\u54ac\u4f4f\u81ea\u5df1\u7684\u5634\u5507\uff1a\u201c\u4f46\u662f\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u6211\u80fd\u4e0d\u62a5\u4e48\uff1f\u4f46\u662f\uff0c\u6211\u82e5\u662f\u62a5\u4e86\u4ec7\uff0c \u6740\u4e86\u5979\u7239\u7239\uff0c\u4fbf\u662f\u4f24\u5bb3\u4e86\u5979\uff0c\u4fbf\u662f\u8fdd\u53cd\u4e86\u8a93\u8a00\u3002\u201d \u3000\u3000\u7236\u4ec7\u3001\u8a93\u8a00\uff0c\u5f80\u6765\u51b2\u51fb\uff0c\u6069\u60c5\u3001\u4ec7\u6068\u96be\u89e3\u96be\u5206\uff0c\u4ed6\u4e0d\u7981\u53c8\u60f3\u8d77\u201c\u51b7\u6708\u4ed9\u5b50\u201d\u90a3\u54c0\u6028\u800c\u98a4 \u6296\u7684\u8bed\u58f0\uff1a\u201c\u8fd9\u4e8b\u8bf4\u6765\u5bb9\u6613\uff0c\u5176\u5b9e\u5374\u662f\u5f88\u96be\u7684\uff0c\u56e0\u4e3a\u4e16\u4e0a\u603b\u6709\u8bb8\u591a\u5947\u602a\u7684\u539f\u56e0\uff0c\u8ba9\u4f60\u4e0d\u5f97\u4e0d \u53bb\u4f24\u5bb3\u4e00\u4e2a\u7231\u4f60\u7684\u4eba\uff01\u201d \u3000\u3000\u8bb8\u591a\u79cd\u5947\u602a\u7684\u539f\u56e0\u2026\u2026\u8bb8\u591a\u79cd\u5947\u602a\u7684\u539f\u56e0\u2026\u2026\u7231\u4f60\u7684\u4eba\u2026\u2026\u7231\u4f60\u7684\u4eba\u2026\u2026 \u3000\u3000\u8881\u6cf8\u73cd\u7a81\u5730\u60ca\u5524\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u2026\u2026\u4f60\u600e\u4e48\u6837\u4e86\uff0c\u4f60\u2026\u2026\u8840\u2026\u2026\u201d \u3000\u3000\u5979\u4f38\u51fa\u7ea4\u67d4\u7684\u624b\u638c\uff0c\u4e3a\u88f4\u73cf\u62b9\u53bb\u4e86\u5507\u4e0a\u7684\u9c9c\u8840\uff0c\u867d\u7136\u662f\u5bd2\u591c\uff0c\u4f46\u88f4\u73cf\u7684\u9c9c\u8840\uff0c\u5374\u6709\u5982\u706b \u4e00\u822c\u7684\u70bd\u70ed\u3002 \u3000\u3000\u88f4\u73cf\u611f\u52a8\u5730\u629a\u7740\u5979\u7684\u624b\u638c\uff0c\u957f\u53f9\u9053\uff1a\u201c\u4f60\u6bd5\u7adf\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u6709\u4e9b\u4e8b\u2026\u2026\u5509\uff0c\u4f60\u662f\u4e0d\u4f1a\u61c2 \u7684\u3002\u201d \u3000\u3000\u8881\u6cf8\u73cd\u987a\u4ece\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u5979\u5fc3\u91cc\u867d\u7136\u4e0d\u613f\u610f\u627f\u53d7\u81ea\u5df1\u5e74\u7eaa\u8f7b\uff0c\u4f46\u53ea\u8981\u201c\u5927\u54e5\u54e5\u201d\u8bf4\u7684 \u8bdd\uff0c\u5374\u6c38\u8fdc\u662f\u5bf9\u7684\u3002 \u3000\u3000\u5979\u5446\u4e86\u8bb8\u4e45\uff0c\u50cf\u662f\u5ffd\u7136\u60f3\u5230\u4ec0\u4e48\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4eca\u5929\u6700\u540e\u548c\u4f60\u5728\u4e00\u8d77\u7684\u90a3\u4e2a\u4eba\uff0c\u662f\u4e0d\u662f\u53eb \u505a\u2018\u4e03\u5de7\u8ffd\u9b42\u2019\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u8881\u6cf8\u73cd\u8f7b\u8f7b\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u53ef\u4e0d\u662f\u597d\u4eba\uff01\u6211\u66fe\u7ecf\u5728\u3002\u98de\u9f99\u9556\u5c40\uff0c\u91cc\u770b\u5230\u8fc7\u4ed6\uff0c\u770b\u5230\u4ed6\u9b3c\u9b3c \u795f\u795f\u5730\u6e9c\u8fdb\u4e86\u540e\u9662\uff0c\u4e0d\u77e5\u548c\u6a80\u2026\u2026\u6a80\u660e\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4e00\u76f4\u5230\u7b2c\u4e8c\u665a\u4e0a\uff0c\u624d\u53c8\u6109\u5077\u6478\u6478\u5730\u6e9c\u8d70\uff0c \u8fde\u9a6c\u90fd\u4e0d\u6562\u9a91\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u60ca\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u4f60\u53ef\u770b\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u8881\u6cf8\u73cd\u575a\u5b9a\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u7a81\u542c\u8fdc\u5904\u5c71\u77f3\u540e\u4e00\u58f0\u53f9\u606f\uff0c\u4e00\u4e2a\u6c89\u91cd\u7684\u8bed\u58f0\uff0c\u4e00\u5b57\u4e00\u987f\u4ed6\u8bf4 \u9053\uff1a\u201c\u90fd\u2014\u2014\u662f\u2014\u2014\u771f\u2014\u2014\u7684\uff01\u201d \u3000\u3000\u8881\u6cf8\u73cd\u9762\u8272\u5927\u53d8\uff0c\u88f4\u73cf\u4ea6\u662f\u5fc3\u5934\u4e00\u61d4\uff0c\u4f4e\u53f1\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u65b9\u5f85\u98de\u63a0\u800c\u8d77\uff0c\u54ea\u77e5\u5c71\u540e\u4eba\u5f71\u4e00\u95ea\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u5df2\u8f7b\u8f7b\u8d70\u4e86\u51fa\u6765\uff0c\u53e3\u4e2d\u5583\u5583 \u9053\uff1a\u201c\u771f\u7684\uff0c\u771f\u7684\uff0c\u90fd\u662f\u771f\u7684\uff01\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u6cdb\u8d77\u4e86\u4e00\u4e1d\u60ed\u6127\u7684\u7b11\u5bb9\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u76df\u4e3b\u5927\u54e5\uff0c\u8bf7\u6055\u6211\u5077\u542c\u4e4b\u7f6a\uff0c\u4f46\u662f\u8fd9\u4f4d\u5c0f\u59b9 \u59b9\u4e00\u8fdb\u5230\u9662\u4e2d\uff0c\u6211\u5c31\u89c9\u5bdf\u4e86\uff0c\u662f\u4ee5\u624d\u8d70\u51fa\u623f\u6765\u3002\u201d \u3000\u3000\u8881\u6cf8\u73cd\u5fc3\u5934\u4e00\u8df3\uff0c\u5979\u81ea\u4ee5\u4e3a\u884c\u52a8\u6781\u4e3a\u5c0f\u5fc3\uff0c\u4e0d\u6599\u4ecd\u7136\u60ca\u52a8\u4e86\u522b\u4eba\uff0c\u5979\u4e5f\u5f00\u59cb\u4e86\u89e3\uff0c\u8fd9\u73ed \u6b66\u6797\u8c6a\u58eb\u7684\u8033\u76ee\u662f\u4f55\u7b49\u7075\u654f\uff01\u8fd9\u662f\u5979\u4ee5\u524d\u4ece\u4e0d\u4f1a\u76f8\u4fe1\u7684\uff0c\u4f46\u662f\u5979\u53c8\u4e0d\u7981\u5f00\u59cb\u4e3a\u4ed6\u4eec\u60b2\u54c0\uff1a \u201c\u4e00\u4e2a\u4eba\u5728\u5916\uff0c\u4ec7\u5bb6\u7ed3\u5f97\u5927\u591a\uff0c\u60f3\u5fc5\u5c31\u50cf\u4ed6\u4eec\u4e00\u6837\uff0c\u8fde\u7761\u89c9\u90fd\u7761\u4e0d\u5b89\u7a33\uff0c\u65f6\u65f6\u523b\u523b\u5730\u9632\u5907\u7740 \u522b\u4eba\u3002\u201d \u3000\u3000\u88f4\u73cf\u76ee\u5149\u70af\u7136\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u51dd\u6ce8\u7740\u90a3\u98de\u8679\uff0c\u8fd9\u7d20\u6765\u5fc3\u72e0\u624b\u8fa3\uff0c\u5978\u72e1\u51f6\u6076\u7684\u201c\u4e03\u5de7\u8ffd \u9b42\u201d\uff0c\u6b64\u523b\u7adf\u7136\u6ee1\u9762\u4ff1\u662f\u6127\u5bb9\uff0c\u5450\u5450\u9053\uff1a\u201c\u4e0d\u7792\u76df\u4e3b\u8bf4\uff0c\u6211\u672c\u5df2\u4e0e\u2018\u9f99\u5f62\u516b\u638c\u2019\u6697\u4e2d\u8ba2\u597d\u4e86 \u5bc6\u8c0b\uff0c\u6211\u52a9\u4ed6\u6d88\u706d\u2018\u6c5f\u5357\u540c\u76df\u2019\uff0c\u6740\u6b7b\u2018\u91d1\u9e21\u2019\u5411\u4e00\u557c\uff0c\u2018\u795e\u624b\u2019\u6218\u98de\uff0c\u4ee5\u53ca\u2026\u2026\u54b3\u54b3\uff0c\u4ee5 \u53ca\u76df\u4e3b\u4f60\uff0c\u4ed6\u4e8b\u6210\u4e4b\u540e\uff0c\u52a9\u6211\u91cd\u7ec4\u540c\u76df\uff0c\u62e5\u6211\u4e3a\u76df\u4e3b\u3002\u201d \u3000\u3000\u88f4\u73cf\u4ecd\u662f\u4e00\u8a00\u4e0d\u53d1\u5730\u51dd\u6ce8\u7740\u4ed6\uff0c\u65e2\u4e0d\u6124\u6012\uff0c\u4e5f\u4e0d\u6028\u6068\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5e72\u54b3\u4e24\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u65b9\u624d\u90a3\u5411\u4e00\u557c\u7684\u6b7b\u2014\u2014\u5509\uff0c\u5b9e\u5728\u662f\u6211\u4e00\u624b\u9020\u6210\u7684\uff0c\u6211 \u9f13\u52a8\u7740\u4ed6\u6765\u4e0e\u76df\u4e3b\u4f60\u4e89\u6740\uff0c\u7b54\u5e94\u4ed6\u6211\u4e00\u5b9a\u8d76\u6765\u5e2e\u52a9\u4ed6\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fcd\u4e0d\u4f4f\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u771f\u7684\u592a\u72e0\u4e86\u3002\u201d \u3000\u3000\u90a3\u98de\u8679\u9ed8\u7136\u5782\u4e0b\u5934\u53bb\uff0c\u88f4\u73cf\u5ffd\u53c8\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u4e9b\u5728\u6697\u4e2d\u5bf9\u4e1c\u65b9\u5144\u5f1f\u8fb1\u9a7e\u7684\u6c49 \u5b50\uff0c\u5927\u7ea6\u771f\u7684\u4e0d\u662f\u4f60\u6307\u4f7f\u7684\u4e86\uff01\u5426\u5219\u90a3\u4e9b\u4eba\u600e\u4f1a\u9a82\u51fa\u5bf9\u6a80\u660e\u4e0d\u5229\u7684\u8bdd\u6765\u3002\u201d \u3000\u3000\u90a3\u98de\u8679\u5782\u9996\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u4e5f\u90fd\u662f\u6211\u6307\u4f7f\u7684\uff0c\u56e0\u4e3a\u6211\u6015\u6a80\u660e\u4e0e\u4e1c\u65b9\u5144\u5f1f\u7ed3\u6210\u59fb\u4eb2\u540e\uff0c\u52bf\u529b \u592a\u5927\uff0c\u90a3\u65f6\u4ed6\u8981\u6bc1\u7ea6\uff0c\u751a\u81f3\u8981\u6740\u6b7b\u6211\uff0c\u6211\u4e5f\u6ca1\u6709\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u5bd2\uff0c\u957f\u53f9\u9053\uff1a\u201c\u6c5f\u6e56\u4e4b\u4e2d\uff0c\u4e3a\u4ec0\u4e48\u4eba\u4eba\u90fd\u8981\u4e92\u76f8\u6b3a\u9a97\u5462\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u53f9\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u672c\u5c31\u662f\u5f31\u8089\u5f3a\u98df\u7684\u4e16\u754c\uff0c\u6211\u672c\u6765\u4ee5\u4e3a\u5728\u8fd9\u4e2a\u4e16 \u754c\u4e2d\uff0c\u5584\u826f\u7684\u4eba\u6c38\u8fdc\u65e0\u6cd5\u751f\u5b58\uff0c\u4f46\u662f\u2014\u2014\u5509\uff0c\u6211\u73b0\u5728\u624d\u77e5\u9053\u6211\u7684\u60f3\u6cd5\u9519\u4e86\uff0c\u65e0\u8bba\u5728\u4ec0\u4e48\u5730 \u65b9\uff0c\u597d\u4eba\u90fd\u6c38\u8fdc\u4e0d\u4f1a\u5bc2\u5bde\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u5782\u9996\u53c8\u9053\uff1a\u201c\u8fd9\u5168\u662f\u56e0\u4e3a\u76df\u4e3b\u4f60\u7684\u4e3a\u4eba\uff0c\u5b9e\u5728\u611f\u52a8\u4e86\u6211\uff01\u6211\u2026\u2026\u6211\u672c\u60f3\u5c06 \u76df\u4e3b\u8bf1\u6765\u6b64\u5730\u540e\uff0c\u5728\u9152\u83dc\u4e2d\u52a0\u4e0a\u6bd2\u836f\uff0c\u6211\u6bd2\u836f\u751a\u81f3\u90fd\u5df2\u51c6\u5907\u597d\u4e86\uff0c\u662f\u4e00\u79cd\u65e0\u8272\u800c\u53c8\u65e0\u5473\u7684\u6bd2 \u836f\uff0c\u4f46\u662f\u2026\u2026\uff0c\u5509\uff0c\u6211\u5b9e\u5728\u4e0b\u4e0d\u4e86\u624b\uff01\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u60ca\uff0c\u77e5\u9053\u81ea\u5df1\u5df2\u5728\u751f\u6b7b\u8fb9\u7f18\u5f80\u8fd8\u4e86\u4e00\u906d\uff0c\u4ed6\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u65b9\u5f85\u8bf4\u8bdd\uff0c\u5ffd \u542c\u5ead\u9662\u4e2d\uff0c\u9ed1\u6697\u4e2d\uff0c\u7a81\u5730\u54cd\u8d77\u4e86\u4e00\u9635\u9707\u8033\u7684\u957f\u7b11\uff01 \u7b2c\u5341\u56db\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u5341\u56db\u7ae0 \u3000\u3000\u591c\u5df2\u5c06\u53bb\uff0c\u5bd2\u98ce\u66f4\u9177\uff0c\u8fd9\u4e00\u58f0\u51b7\u7b11\u4e4b\u4e2d\uff0c\u66f4\u662f\u5145\u6ee1\u4e86\u68ee\u5bd2\u4e4b\u610f\u3002 \u3000\u3000\u88f4\u73cf\u3001\u90a3\u98de\u8679\u3001\u8881\u6cf8\u73cd\u84e6\u5730\u4e00\u60ca\uff0c\u66b4\u559d\u4e00\u58f0\uff01 \u3000\u3000\u201c\u8c01\uff01\u201d \u3000\u3000\u53ea\u542c\u9ed1\u6697\u4e2d\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u77e5\u8fc7\u80fd\u6539\uff0c\u5c1a\u5c5e\u53ef\u6559\uff0c\u4f60\u82e5\u5984\u65bd\u6bd2\u8ba1\uff0c\u6b64\u523b\u8fd8\u6709\u547d \u4e48\uff1f\u201d \u3000\u3000\u8bed\u58f0\u6fc0\u8361\uff0c\u6fc0\u8361\u4e8e\u51db\u51bd\u7684\u5bd2\u98ce\u4e2d\uff0c\u4ea6\u4e0d\u77e5\u662f\u8fdc\u662f\u8fd1\uff0c\u4eff\u4f5b\u662f\u5728\u4ed6\u4eec\u8033\u7554\u7684\u58f0\u97f3\uff0c\u4f46\u5ead\u9662 \u5341\u4e08\u4ee5\u5185\u54ea\u6709\u201c\u4eba\u5f71\uff1f\u5355\u638c\u4e00\u7a7f\uff0c\u4eba\u968f\u8eab\u8d77\uff0c\u5237\u5730\u6a2a\u98de\u4e09\u4e08\uff0c\u811a\u5c16\u4e00\u8e0f\u79ef\u96ea\u7684\u67af\u679d\uff0c\u500f\u7136\u4e09 \u4e2a\u8d77\u843d\uff0c\u4fbf\u5df2\u63a0\u5728\u8fd9\u4e00\u7247\u5ead\u9662\u4e4b\u5916\u3002\u98ce\u5439\u56db\u91ce\uff0c\u79ef\u96ea\u51c4\u8ff7\uff0c\u65e0\u8fb9\u7684\u9759\u5bc2\uff0c\u6c89\u91cd\u5730\u7b3c\u7f69\u5927\u5730\uff0c \u751f\u50cf\u662f\u7ec8\u53e4\u4ee5\u6765\u4fbf\u6ca1\u6709\u4eba\u8ff9\u3002\u88f4\u73cf\u6781\u76ee\u56db\u987e\uff0c\u5f15\u542d\u5927\u558a\u9053\uff1a\u201c\u5e08\u5085\uff01\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u9ad8\u4ea2\u7684\u547c\u58f0\uff0c\u9707\u5f97\u67af\u679d\u4e0a\u7684\u79ef\u96ea\uff0c\u6709\u5982\u5c71\u5dc5\u7684\u4e71\u4e91\u822c\u56db\u4e0b\u98de\u843d\uff0c\u4e00\u53ea\u5b64\u5bbf\u7684\u5bd2\u9e1f\u60b2\u9e23\u4e00 \u58f0\uff0c\u632f\u7ffc\u98de\u8d77\uff0c\u970e\u773c\u4fbf\u6ca1\u5165\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u88f4\u73cf\u5446\u5446\u5730\u6123\u4e86\u534a\u54cd\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u63a0\u56de\u5ead\u9662\uff0c\u4f46\u89c1\u8881\u6cf8\u73cd\u4e00\u53cc\u660e\u4eae\u7684\u773c\u775b\uff0c\u6b63\u6ee1\u542b\u7740\u4ef0 \u6155\u4e0e\u70ed\u671b\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u53cc\u624b\u5782\u819d\uff0c\u6728\u7acb\u5f53\u5730\uff0c\u9762\u5bb9\u82cd\u767d\uff0c\u76ee\u77aa\u53e3\u5446\uff0c\u6ee1\u989d\u4ff1\u662f\u9ec4\u8c46\u822c\u5927\u5c0f \u7684\u6c57\u73e0\u3002 \u3000\u3000\u88f4\u73cf\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u653e\u4e0b\u5c60\u5200\uff0c\u7acb\u5730\u6210\u4f5b\uff0c\u5c0f\u5f1f\u771f\u8be5\u606d\u559c\u90a3\u5144\u2026\u2026\u201d \u3000\u3000\u8881\u6cf8\u73cd\u5ffd\u7136\u5a07\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u60f3\u5fc5\u4f60\u7761\u89c9\u4e5f\u53ef\u7761\u5f97\u5b89\u7a33\u4e9b\u4e86\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u4f38\u624b\u4e00\u62b9\u989d\u4e0a\u51b7\u6c57\uff0c\u5fc3\u623f\u5374\u4ecd\u7136\u5728\u7830\u7830\u8df3\u52a8\uff0c\u4ed6\u5fc3\u4e2d\u6b63\u5728\u6697\u4e2d\u81ea \u8bed\uff1a\u201c\u653e\u4e0b\u5c60\u5200\uff0c\u7acb\u5730\u6210\u4f5b\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u4ef0\u5929\u5927\u7b11\u6570\u58f0\uff0c\u6717\u58f0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e3a\u5584\u6bd5\u7adf\u6bd4\u4f5c\u6076\u6109\u5feb\u5f97\u591a\uff01\u201d \u3000\u3000\u4ed6\u51fa\u8eab\u8349\u83bd\uff0c\u81ea\u7136\u4e0d\u77e5\u9053\u4ed6\u81ea\u5df1\u6240\u8bf4\u51fa\u7684\u8fd9\u53e5\u7b80\u7b80\u5355\u5355\u7684\u8bdd\u4e2d\uff0c\u5305\u542b\u7740\u591a\u4e48\u4e0d\u7b80\u5355\u7684\u54f2 \u7406\u3002 \u3000\u3000\u88f4\u73cf\u6697\u53f9\u5fd6\u9053\uff1a\u201c\u4ed6\u4e0d\u77e5\u7ecf\u5386\u4e86\u591a\u5c11\u5931\u7720\u7684\u591c\u665a\uff0c\u8d1f\u62c5\u8fc7\u591a\u5c11\u826f\u5fc3\u7684\u75db\u82e6\uff0c\u624d\u80fd\u8bf4\u51fa\u8fd9 \u53e5\u5e73\u51e1\u800c\u53c8\u6781\u4e0d\u5e73\u51e1\u7684\u8bdd\u6765\uff0c\u4f46\u613f\u4e16\u4e0a\u7684\u4f5c\u6076\u4e4b\u5f92\uff0c\u6b64\u523b\u90fd\u80fd\u7ad9\u5728\u8fd9\u91cc\uff0c\u542c\u542c\u4ed6\u8fd9\u4e00\u53e5\u81ea\u5fc3 \u5e95\u8bf4\u7684\u8bdd\u3002\u201d \u3000\u3000\u4e09\u4eba\u76ee\u5149\u4ea4\u6d41\uff0c\u4f46\u89c9\u8fd9\u5bd2\u51b7\u800c\u5bc2\u5bde\u7684\u5ead\u9662\uff0c\u6b64\u523b\u7a81\u7136\u53d8\u5f97\u6e29\u6696\u800c\u5145\u5b9e\u8d77\u6765\uff0c\u56e0\u4e3a\u8fd9\u5ead\u9662 \u4e4b\u4e2d\uff0c\u6b64\u523b\u6b63\u5145\u5b9e\u7740\u5584\u826f\u7684\u4eba\u6027\u3002 \u3000\u3000\u6c49\u53e3\u57ce\u5185\u7684\u591c\u8857\uff0c\u6b64\u523b\u5374\u4ecd\u7136\u662f\u5bd2\u51b7\u800c\u5bc2\u5bde\u7684\u3002 \u3000\u3000\u867d\u7136\u6709\u8bb8\u591a\u52b2\u88c5\u4f69\u5203\u7684\u5927\u6c49\uff0c\u4ee5\u6c89\u91cd\u7684\u76ae\u9774\uff0c\u4e0d\u65ad\u5730\u8e29\u7740\u5730\u4e0a\u7684\u79ef\u96ea\uff0c\u5de1\u89c6\u7740\u6c5f\u5cb8\u8fb9\u7684 \u9556\u8f66\u3002 \u3000\u3000\u867d\u7136\u6709\u8bb8\u591a\u597d\u5947\u800c\u597d\u9152\u7684\u4eba\u4eec\uff0c\u4e3a\u4e86\u63a2\u6d4b\u8fd9\u4e00\u573a\u5fc5\u751f\u7684\u66b4\u98ce\u96e8\u7684\u5f00\u7aef\uff0c\u4ecd\u7559\u604b\u5728\u8d2a\u5229\u7684 \u9152\u5e97\u91cc\uff0c\u4f5c\u901a\u5bb5\u4e4b\u996e\u3002 \u3000\u3000\u4f46\u662f\uff0c\u56db\u4e0b\u7684\u5bd2\u51b7\u4e0e\u5bc2\u5bde\uff0c\u5374\u4ecd\u662f\u90a3\u4e48\u6c89\u91cd\uff0c\u6c89\u91cd\u5730\u538b\u5728\u6bcf\u4e2a\u4eba\u7684\u5fc3\u5934\uff0c\u538b\u5f97\u4ed6\u4eec\u900f\u4e0d \u8fc7\u6c14\u6765\u3002 \u3000\u3000\u5076\u800c\u6709\u4e00\u58f0\u7206\u53d1\u7684\u72c2\u7b11\uff0c\u5212\u7834\u4e86\u9ed1\u6697\u7684\u5c91\u5bc2\uff0c\u4f46\u65e0\u8bba\u591a\u5c11\u58f0\u72c2\u7b11\uff0c\u5374\u90fd\u5212\u4e0d\u5f00\u4eba\u4eec\u5fc3\u4e2d \u7684\u6c89\u91cd\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u8857\u7684\u90a3\u5934\u4f20\u6765\u4e00\u58f0\u5c16\u9510\u7684\u60e8\u547c\uff01 \u3000\u3000\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\uff0c\u7acb\u523b\u72c2\u5954\u5230\u60e8\u547c\u4e4b\u58f0\u53d1\u51fa\u7684\u5730\u65b9\uff0c\u4f46\u89c1\u60e8\u767d\u7684\u96ea\u5730\u4e0a\uff0c\u6d41\u843d\u7740\u4e00\u6ee9\u9c9c \u8840\u3002 \u3000\u3000\u9c9c\u7ea2\u7684\u8840\u8ff9\u5916\uff0c\u4e00\u4e2a\u201c\u98de\u9f99\u9556\u5c40\u201d\u7684\u624b\u4e0b\uff0c\u56db\u80a2\u5206\u5c55\uff0c\u4ef0\u5367\u5728\u6c89\u90c1\u7684\u82cd\u7a79\u4e0b\uff0c\u6ee1\u9762\u4ff1\u662f \u60ca\u60e7\u4e0e\u6050\u6016\uff0c\u76ee\u5149\u7a7a\u6d1e\u5730\u51dd\u89c6\u7740\u65e0\u661f\u65e0\u6708\u7684\u82cd\u7a79\u3002 \u3000\u3000\u4e00\u67c4\u96ea\u4eae\u7684\u5315\u9996\uff0c\u659c\u63d2\u5728\u4ed6\u575a\u5b9e\u7684\u80f8\u819b\u4e0a\uff0c\u9c9c\u8840\uff0c\u5728\u5982\u6b64\u5bd2\u51b7\u7684\u591c\u665a\uff0c\u867d\u7136\u4ec5\u5239\u90a3\u4e4b\u95f4 \u4fbf\u5df2\u548c\u5730\u9762\u4e0a\u7684\u60ca\u60e7\u4e0e\u6050\u6016\u4e00\u8d77\u51dd\u7ed3\u4e86\uff0c\u4ece\u6b64\u523b\u76f4\u5230\u6c38\u8fdc\uff0c\u5374\u518d\u4e5f\u65e0\u6cd5\u518d\u878d\u5408\u5316\u89e3\u7684\u5f00\u3002 \u3000\u3000\u201c\u6218\u795e\u624b\u5df2\u5f00\u59cb\u884c\u52a8\u4e86\uff01\u201d \u3000\u3000\u5174\u594b\u800c\u7d27\u5f20\u7684\u547c\u58f0\uff0c\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u5728\u5bd2\u51b7\u7684\u591c\u8857\u4e0a\u6563\u7740\u3002 \u3000\u3000\u53c8\u662f\u4e00\u58f0\u60e8\u547c\uff0c\u5728\u957f\u8857\u7684\u53e6\u4e00\u5934\u7206\u53d1\u51fa\u6765\u3002 \u3000\u3000\u516b\u5339\u957f\u811a\u5065\u9a6c\uff0c\u7a81\u5730\u81ea\u8857\u65c1\u7684\u4e00\u95f4\u5927\u5b85\u4e2d\u51b2\u51fa\uff0c\u5f53\u5934\u4e24\u4eba\uff0c\u624b\u6301\u53f7\u89d2\uff0c\u54cd\u8d77\u4e00\u8fde\u4e32\u9707\u8033 \u7684\u60b2\u9e23\uff01 \u3000\u3000\u53f7\u89d2\u4e0d\u65ad\uff0c\u5065\u9a6c\u5f00\u59cb\u5728\u9ed1\u6697\u7684\u57ce\u5e02\uff0c\u9634\u6697\u7684\u89d2\u843d\u91cc\u5954\u9a70\u3002 \u3000\u3000\u968f\u7740\u6025\u8fd9\u7684\u9a6c\u8e44\u58f0\uff0c\u4e00\u4e2a\u4e2d\u6c14\u6781\u8db3\uff0c\u8bed\u58f0\u5639\u4eae\u7684\u6c49\u5b50\uff0c\u5f15\u542d\u5927\u559d\u9053\uff1a\u201c\u51e1\u5c5e\u2018\u98de\u9f99\u2019\u65d7 \u4e0b\u7684\u5144\u5f1f\uff0c\u4e00\u8d77\u805a\u96c6\u5728\u957f\u6c5f\u6e21\u5934\uff0c\u4e0d\u5f97\u5206\u6563\uff01\u201d \u3000\u3000\u8fd9\u547c\u58f0\u4e5f\u662f\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u54cd\u904d\u4e86\u9ed1\u6697\u7684\u57ce\u5e02\u3002 \u3000\u3000\u6574\u4e2a\u7684\u57ce\u5e02\uff0c\u5374\u5df2\u5927\u4e71\u4e86\uff0c\u5931\u53bb\u4e86\u5b81\u9759\uff0c\u4e5f\u5931\u53bb\u4e86\u6cbb\u5b89\u3002 \u3000\u3000\u867d\u7136\u6709\u4e00\u4e9b\u5e26\u5200\u7684\u5b98\u5dee\uff0c\u65e0\u53ef\u5948\u4f55\u5730\u56db\u5904\u5de1\u67e5\u7740\uff0c\u4f46\u4ed6\u4eec\u7684\u773c\u775b\uff0c\u6b64\u523b\u5374\u5df2\u4f3c\u770b\u4e0d\u5230\u5200 \u5149\u4e0e\u9c9c\u8840\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u5c06\u8fd9\u4e00\u5207\u5f53\u4f5c\u4e00\u573a\u761f\u75ab\u2014\u2014\u761f\u75ab\uff0c\u662f\u4eba\u529b\u96be\u4ee5\u62b5\u6321\u7684\uff0c\u4f46\u761f\u75ab\uff0c\u5374\u603b\u6709\u79bb\u53bb\u7684\u4e00 \u5929\u3002 \u3000\u3000\u4f46\u60e8\u547c\u4e4b\u58f0\uff0c\u4ecd\u7136\u4e0d\u65ad\uff0c\u6709\u65f6\u5728\u4e1c\uff0c\u6709\u65f6\u5728\u897f\u3002\u4e00\u4e2a\u9189\u540e\u7684\u6c49\u5b50\uff0c\u8e0f\u97f3\u8e09\u8dc4\u7684\u811a\u6b65\uff0c\u53bb \u5bfb\u4e2a\u65b9\u4fbf\uff0c\u4e0d\u5e78\u4ed6\u8170\u65c1\u63d2\u7740\u7684\u4e00\u67c4\u65e0\u9798\u7684\u5c16\u5200\uff0c\u66f4\u4e0d\u5e78\u90a3\u516b\u5339\u5065\u9a6c\u6b64\u523b\u6070\u5de7\u5728\u4ed6\u8eab\u65c1\u5954\u9a70\u800c \u8fc7\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5065\u9a6c\u4e0a\u7684\u9a91\u58eb\u66b4\u53f1\u4e00\u58f0\uff0c\u5200\u5149\u4e00\u95ea\u3002 \u3000\u3000\u8e09\u8dc4\u7684\u9189\u6c49\u53ea\u89c9\u5934\u4e0a\u4e00\u9635\u51c9\u7684\u9ebb\u6728\uff0c\u4fbf\u53ef\u601c\u53c8\u53ef\u803b\u5730\u5728\u96ea\u5730\u4e0a\uff0c\u4efb\u51ed\u5954\u817e\u7684\u9a6c\u8e44\uff0c\u5728\u4ed6 \u8eab\u4e0a\u8e0f\u8fc7\u3002 \u3000\u3000\u98ce\u66f4\u6025\u2026\u2026 \u3000\u3000\u4e00\u8258\u4e4c\u7bf7\u7684\u6c5f\u8239\uff0c\u81ea\u9ed1\u6697\u4e2d\u6e21\u6c5f\u800c\u6765\uff0c\u505c\u6cca\u5728\u4e00\u5904\u8352\u51c9\u7684\u5cb8\u8fb9\u3002 \u3000\u3000\u8239\u672a\u5230\u5cb8\uff0c\u8239\u4e0a\u4fbf\u6709\u6570\u6761\u9ed1\u5f71\uff0c\u6a2a\u98de\u800c\u4e0b\uff0c\u811a\u6b65\u4e0d\u505c\uff0c\u970e\u773c\u95f4\u4fbf\u6ca1\u5165\u9ed1\u6697\u91cc\uff0c\u50cf\u662f\u8be1\u79d8 \u7684\u5e7d\u7075\u4e00\u822c\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u8c01\uff1f \u3000\u3000\u4e94\u5339\u5065\u9a6c\uff0c\u6d8c\u51fa\u4e00\u8f86\u4e4c\u7bf7\u5927\u8f66\uff0c\u81ea\u9ed1\u6697\u4e2d\u51b2\u51fa\uff0c\u72c2\u5954\u8fc7\u591c\u57ce\u4e2d\u7684\u957f\u8857\uff0c\u5f53\u5934\u4e00\u4eba\uff0c\u767d\u53d1 \u767d\u9aef\uff0c\u76ee\u5149\u5982\u5200\uff0c\u987e\u76fc\u751f\u5a01\u3002 \u3000\u3000\u4e0d\u77e5\u662f\u8c01\uff0c\u5728\u8857\u65c1\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\uff01 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u6765\u4e86\uff01\u201d \u3000\u3000\u547c\u58f0\u672a\u843d\uff0c\u5df2\u6709\u4e00\u53ea\u7ed3\u5b9e\u7684\u624b\u638c\uff0c\u63a9\u4f4f\u4ed6\u7684\u5634\u5507\uff0c\u5c06\u4ed6\u65e0\u52a9\u5730\u62d6\u5728\u5c4b\u6a90\u540e\u7edd\u671b\u7684\u9634\u5f71 \u91cc\u3002 \u3000\u3000\u4e8e\u662f\u518d\u6ca1\u6709\u60ca\u547c\uff01 \u3000\u3000\u8f66\u9a6c\u505c\u5728\u8857\u65c1\u90a3\u4e00\u5ea7\u5927\u5b85\u65c1\uff0c\u5927\u95e8\u524d\u672c\u6765\u6302\u7740\u7684\u4e00\u65b9\u6a2a\u533e\uff1a\u201c\u98de\u9f99\u652f\u5c40\uff01\u201d \u3000\u3000\u6b64\u523b\u65e9\u5df2\u4e0d\u77e5\u5728\u4f55\u65f6\u88ab\u4eba\u6458\u843d\u4e86\u4e0b\u6765\u3002\u5f53\u5934\u9a6c\u4e0a\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u80a9\u5934\u5fae\u8038\uff0c\u4fbf\u5df2 \u8dc3\u4e0a\u9a6c\u978d\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u4e00\u6b65\uff0c\u63a0\u5230\u8f66\u524d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u742a\u513f\uff0c\u4e0b\u6765\u3002\u201d \u3000\u3000\u8f66\u5e18\u4e00\u6380\uff0c\u9762\u8272\u82cd\u767d\uff0c\u76ee\u5149\u6563\u6f2b\u7684\u6a80\u6587\u742a\uff0c\u832b\u7136\u8d70\u4e86\u51fa\u6765\u5c0f\u5979\u9762\u4e0a\u4e00\u65e0\u8868\u60c5\uff0c\u5c31\u8fde\u5979\u660e \u4eae\u7684\u79cb\u6ce2\uff0c\u90fd\u5df2\u5931\u53bb\u4e86\u795e\u91c7\u3002 \u3000\u3000\u5979\u832b\u7136\u8e29\u8fc7\u4e0e\u5979\u9762\u4e00\u822c\u60e8\u767d\u7684\u96ea\u5730\uff0c\u8d70\u5165\u90a3\u4e00\u680b\u5927\u5b85\uff0c\u5bf9\u5979\u8eab\u65c1\u7684\u7239\u7239\uff0c\u7adf\u8fde\u770b\u4e5f\u6ca1\u6709 \u770b\u4e0a\u4e00\u773c\u3002\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u76ee\u5149\u4e00\u9635\u9eef\u7136\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u968f\u7740\u5979\u8d70\u5165\u5b85\u95e8\u3002 \u3000\u3000\u4e4c\u6f06\u7684\u5b85\u95e8\uff0c\u7830\u5730\u4e00\u58f0\uff0c\u91cd\u91cd\u5173\u8d77\uff0c\u622a\u65ad\u4e86\u4eba\u4eec\u7684\u76ee\u5149\uff0c\u4f46\u5374\u622a\u4e0d\u65ad\u65e0\u6570\u4eba\u53e3\u4e2d\u7684\u8033 \u8bed\uff0c\u201c\u2018\u9f99\u5f62\u516b\u638c\u2019\u5230\u4e86\uff01\u201d\u2026\u2026\xb7\xb7\u201c\u2018\u9f99\u5f62\u516b\u638c\u2019\u5230\u4e86\uff01\u201d\u2026\u2026 \u3000\u3000\u5929\u8272\uff0c\u53d8\u5f97\u66f4\u6c89\u91cd\u4e86\uff0c\u4e5f\u4e0d\u77e5\u8ddd\u79bb\u9ece\u660e\u8fd8\u6709\u591a\u8fdc\u3001\u9634\u6c89\u7684\u5927\u5b85\u4e2d\uff0c\u7acb\u523b\u4eae\u8d77\u4e86\u65e0\u6570\u76cf\u706f \u706b\u3002 \u3000\u3000\u4f46\u7eb7\u4e71\u7684\u811a\u6b65\u58f0\uff0c\u5374\u662f\u8f7b\u5fae\u7684\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u9762\u6c89\u5982\u51b0\uff0c\u5306\u5306\u8d70\u4eba\u4e86\u897f\u9762\u7684\u4e00\u95ee\u8de8 \u9662\u3002 \u3000\u3000\u4ed6\u4e00\u6b65\u65b9\u624d\u8fc8\u5165\u9662\u95e8\uff0c\u53a2\u623f\u4e2d\u4fbf\u5df2\u54cd\u8d77\u4e86\u4e00\u9635\u4f4e\u53f1\uff01 \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u6a80\u660e\u5e72\u54b3\u4e00\u58f0\uff0c\u53a2\u623f\u4e2d\u706f\u706b\u5254\u4eae\uff0c\u672a\u5fa1\u8863\u5c65\u7684\u201c\u4e1c\u65b9\u4e94\u5251\u201d\uff0c\u4e00\u8d77\u8fce\u51fa\u4e86\u95e8\u5916\uff0c\u4e1c\u65b9\u94c1 \u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6a80\u5927\u53d4\u600e\u5730\u4e58\u591c\u8d76\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6c89\u91cd\u7684\u9762\u4e0a\uff0c\u7acb\u523b\u6324\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6628\u65e5\u5df2\u5e94\u5728\u6b64\u7b49\u5019\u8d24\u4f84 \u4eec\u5927\u9a7e\uff0c\u4e00\u6b65\u6765\u8fdf\uff0c\u5374\u53eb\u4f60\u4eec\u65e0\u7aef\u53d7\u5230\u4e86\u8bb8\u591a\u72c2\u5f92\u7684\u80e1\u8a00\u4e71\u8bed\u3002\u201d \u3000\u3000\u51bb\u65b9\u94c1\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6a80\u5927\u53d4\u7684\u6d88\u606f\u5012\u7075\u901a\u5f97\u5f88\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e2d\u4ed6\u4eec\u4e00\u8d77\u4eba\u4e86\u53a2\u623f\uff0c\u4f46\u8fd9\u7b11\u58f0\u662f\u5426\u4ff1\u662f\u771f\u5fc3\u53d1\u51fa\u6765\u7684\u5462\uff1f \u3000\u3000\u4e2a\u4e2a\u5fc3\u4e0d\u5728\u7109\u7684\u5bd2\u55a7\u6570\u8bed\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7a81\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u5c06\u8bdd\u5934\u8f6c\u5165\u6b63\u9898\uff0c\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u5e74\u524d\u627f\u8499\u8d24\u4f84\u4eec\u4e0d\u5f03\uff0c\u800c\u6709\u62db\u4eb2\u4e4b\u610f\uff0c\u4f46\u8001\u592b\u90a3\u65f6\u53ea\u89c9\u5c0f\u5973\u5e74\u7eaa\u592a\u8f7b\uff0c\u53c8\u6050\u9ad8\u6500\u4e0d \u4e0a\uff0c\u662f\u4ee5\u672a\u6562\u4ed3\u4fc3\u51b3\u5b9a\u3002\u201d \u3000\u3000\u4e1c\u65b9\u6e56\u5fae\u5fae\u4e00\u7b11\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u88ab\u5927\u54e5\u626f\u8863\u89d2\uff0c\u622a\u4f4f\u4e86\u4ed6\u7684\u8bdd\u5934\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u4ea6\u4e0d\u77e5\u6709\u6ca1\u6709\u770b\u89c1\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f46\u81ea\u4ece\u2018\u6d6a\u83bd\u5c71\u5e84\u2019\u4ee5\u540e\uff0c\u5c0f \u5973\u5f97\u8499\u9707\u4e16\u5144\u5927\u529b\u7ef4\u62a4\u4e4b\u540e\uff0c\u60f3\u4e0d\u5230\u5bf9\u9707\u4e16\u5144\u2026\u2026\u5509\uff0c\u7adf\u5df2\u52a8\u4e86\u75f4\u5fc3\u3002\u201d \u3000\u3000\u4e1c\u65b9\u9707\u9762\u5bb9\u50f5\u6728\uff0c\u4e00\u65e0\u8868\u60c5\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u542b\u7b11\u9053\uff1a\u201c\u4e09\u5f1f\u5f53\u771f\u6709\u798f\u4e86\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u53cc\u7709\u4e00\u5c55\uff0c\u9053\uff1a\u201c\u8001\u592b\u4eba\u751f\u95ef\u8361\u6c5f\u6e56\uff0c\u53ea\u5f97\u6b64\u5973\uff0c\u662f\u4ee5\u2026\u2026\u5509\uff01\u65e2\u662f\u5979\u5fc3 \u91cc\u613f\u610f\uff0c\u8001\u592b\u4e5f\u53ea\u5f97\u539a\u989c\u6765\u5411\u4e16\u5144\u4eec\u91cd\u63d0\u65e7\u8bae\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u4e4e\u7279\u522b\u5f3a\u8c03\u201c\u91cd\u63d0\u65e7\u8bae\u201d\u56db\u5b57\uff0c\u8868\u793a\u8fd9\u95e8\u4eb2\u4e8b\u65e0\u8bba\u5982\u4f55\u603b\u662f\u4f60\u4eec\u81ea\u5df1\u5148\u63d0\u51fa\u6765\u7684\u3002 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u53c8\u9053\uff1a\u201c\u53ea\u662f\u2026\u2026\u5509\uff0c\u8001\u592b\u95e8\u6237\u592a\u4f4e\uff0c\u4e0d\u77e5\u662f\u5426\u9ad8\u6500 \u5f97\u4e0a\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u9707\u9762\u4e0a\u4ecd\u662f\u6beb\u65e0\u8868\u60c5\uff0c\u4ea6\u65e0\u53e3\u907f\u4e4b\u610f\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6a80\u5927\u53d4\u540d\u6ee1\u5929\u4e0b\uff0c\u9886\u8896\u6b66\u6797\uff0c\u5341\u5e74\u6765\u6c5f\u6e56\u82f1\u96c4\uff0c\u4ece\u672a\u6709\u4e00\u4eba\u4e4b\u58f0 \u540d\u80fd\u4e0e\u6a80\u5927\u53d4\u76f8\u576a\u3002\u6a80\u5927\u53d4\u82e5\u662f\u518d\u8bf4\u95e8\u6237\u592a\u4f4e\uff0c\u5c0f\u4f84\u4eec\u4fbf\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u4e86\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u634b\u987b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d24\u4f84\u8fc7\u8a89\u4e86\u2026\u2026\u5982\u6b64\u8bf4\u6765\uff0c\u4e0d\u77e5\u9707\u5144\u8eab\u8fb9\u53ef\u66fe\u5e26\u5f97\u6709\u6587 \u5b9a\u4e4b\u7269\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u622a\u53e3\u9053\uff1a\u201c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fcd\u4e0d\u4f4f\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u56fd\u5149\u4e00\u95ea\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u6a80\u5927\u53d4\u4e0d\u5acc\u4eca\u591c\u4ed3\u4fc3\u4e86\u4e9b\uff1f\u8fd9\u662f\u4e09\u5f1f\u7ec8\u8eab\u4e4b\u559c\uff0c\u6211\u5144\u5f1f\u65e0 \u8bba\u5982\u4f55\u4e5f\u8be5\u4e3a\u4ed6\u505a\u5f97\u90d1\u91cd\u4e9b\u624d\u662f\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u8f6c\u52a8\uff0c\u5fc3\u5ff5\u4ea6\u5728\u8f6c\u52a8\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6b64\u2026\u2026\u4e8b\u2026\u2026\u8bf4\u2026\u2026\u6765\u867d\u7136\u4e0d\u9519\uff0c \u4f46\u6b64\u523b\u4e8b\u6001\u975e\u5e38\uff0c\u51e1\u4e8b\u53ea\u597d\u4ece\u6743\uff0c\u597d\u5728\u4f60\u6211\u4ff1\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u4e5f\u4e0d\u5fc5\u6765\u62d8\u8fd9\u4e9b\u865a\u793c\u2026\u2026\u54c8\u54c8\uff0c \u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u601d\u7d22\uff0c\u4e00\u9762\u8bf4\u8bdd\uff0c\u662f\u4ee5\u5f00\u5934\u56db\u5b57\uff0c\u8bf4\u5f97\u6781\u6162\uff0c\u4f46\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u8a00\u8bed\u4fbf\u6ed4\u6ed4\u4e0d\u7edd\u800c \u51fa\u3002 \u3000\u3000\u4e1c\u65b9\u6c5f\u6545\u4f5c\u4e0d\u61c2\uff0c\u9053\uff1a\u201c\u4e8b\u6001\u975e\u5e38\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5fc3\u5ff5\u53c8\u81ea\u6570\u8f6c\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u7792\u8d24\u4f84\u4eec\u8bf4\uff0c\u6211\u2018\u98de\u9f99\u9556\u5c40\u2019\uff0c\u4eca\u65e5 \u5b9e\u5df2\u9047\u7740\u4e86\u52b2\u654c\uff0c\u8001\u592b\u53ea\u6b64\u4e00\u5973\uff0c\u603b\u8981\u5979\u5148\u6709\u4e86\u5f52\u5bbf\uff0c\u624d\u80fd\u653e\u5fc3\u3002\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6a80\u5927\u53d4\u7231\u5973\u5fc3\u5207\uff0c\u6b64\u8bdd\u4e5f\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u4ed6\u751f\u6027\u8c26\u606d\u4ec1\u539a\uff0c\u8a00\u8bed\u81ea\u4e5f\u5341\u5206\u6709\u672d\u3002 \u3000\u3000\u4e1c\u65b9\u6e56\u7a81\u5730\u5251\u7709\u4e00\u626c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8fd1\u65e5\u542c\u5f97\u6b66\u6797\u4f20\u8a00\uff0c\u8bf4\u662f\u6a80\u5927\u53d4\u4e0e\u5341\u516b\u5e74\u524d\u90a3\u4e00\u6bb5\u65e0 \u5934\u516c\u6848\u6709\u4e9b\u5173\u7cfb\uff0c\u4e0d\u77e5\u6b64\u4e8b\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u4ed6\u5e74\u5c11\u6c14\u76db\uff0c\u5fc3\u4e2d\u82e5\u6709\u4e8b\u60c5\uff0c\u4fbf\u518d\u4e5f\u7559\u4e0d\u4f4f\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u53c8\u81ea\u5fae\u5fae\u4e00\u53d8\uff0c\u7a81\u5730\u4ef0\u5929\u72c2\u7b11\u9053\uff1a\u201c\u8349\u83bd\u5339\u592b\u7684\u6076\u610f\u4e2d\u4f24\uff0c\u8001\u592b\u4ece\u672a \u653e\u5728\u5fc3\u4e0a\uff0c\u8d24\u4f84\u4eec\u5374\u4fe1\u4ee5\u4e3a\u771f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u6c5f\uff0c\u4e1c\u65b9\u6e56\u5bf9\u671b\u4e00\u773c\uff0c\u4e1c\u65b9\u94c1\u62a2\u53e3\u7b11\u9053\uff1a\u201c\u6a80\u5927\u53d4\u6e38\u4fa0\u6c5f\u6e56\uff0c\u5c11\u4e0d\u5f97\u8981\u7ed3\u4e0b\u8bb8\u591a\u4ec7 \u5bb6\uff0c\u4e94\u5f1f\uff0c\u4f60\u600e\u80fd\u2014\u2014\u201d\u201c\u9f99\u5f62\u516b\u638c\u201d\u7b11\u9053\uff1a\u201c\u6e56\u4e16\u5144\u70ed\u8840\u76f4\u80a0\uff0c\u6b63\u662f\u6211\u5c11\u5e74\u65f6\u7684\u5fc3\u6027\uff0c\u6211 \u600e\u4f1a\u602a\u4ed6\uff1f\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u7b14\u76f4\u5730\u671b\u5411\u4e1c\u65b9\u9707\uff0c\u53e3\u4e2d\u5411\u4e1c\u65b9\u94c1\u9053\uff1a\u201c\u94c1\u4e16\u5144\uff0c\u957f\u5144\u4e3a\u7236\uff0c\u53e4\u6709\u540d\u8bad\uff0c \u4eca\u65e5\u4e4b\u4e8b\uff0c\u82e5\u662f\u94c1\u4e16\u5144\u4e00\u53e3\u627f\u62c5\u4e0b\u6765\uff0c\u60f3\u5fc5\u8001\u7237\u5b50\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u542c\u4e00\u9635\u6742\u4e71\u7684\u811a\u6b65\u58f0\u81ea\u5916\u5954\u6765\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u4e00\u626c\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u6012\u53f1 \u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53ea\u89c1\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u5782\u9996\u8083\u7acb\u5728\u5385\u524d\u9636\u4e0b\uff0c\u9053\uff1a\u201c\u524d\u9762\u6709\u4eba\u9001\u6765\u4e09\u53a6\u793c\u7269\uff0c\u4e0d\u77e5\u603b\u9556\u5934 \u53ef\u8981\u770b\u4e0a\u4e00\u770b\uff1f\u201d \u3000\u3000\u4ed6\u6ee1\u9762\u4ff1\u662f\u60ca\u6050\u4e4b\u8272\uff0c\u9762\u4e0a\u4e5f\u5927\u5927\u5931\u4e86\u5e38\u6001\uff0c\u6a80\u660e\u77e5\u9053\u6b64\u4eba\u884c\u4e8b\u7d20\u6765\u9547\u9759\uff0c\u6b64\u523b\u5982\u6b64\u6a21 \u6837\uff0c\u4e8b\u60c5\u5fc5\u5b9a\u6709\u53d8\u3002 \u3000\u3000\u4ed6\u5fae\u4e00\u6c89\u541f\u4e4b\u95f4\uff0c\u65b9\u5f85\u4e3e\u6b65\u800c\u51fa\uff0c\u53ea\u542c\u4e1c\u65b9\u6c5f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u6709\u4e0d\u4fbf\uff0c\u6a80\u5927\u53d4\u81ea\u7ba1 \u51fa\u53bb\u4fbf\u662f\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u5e72\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5728\u8d24\u4f84\u4eec\u9762\u524d\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e0d\u65b9\u4fbf\u7684\u4e8b\uff01\u67f3\u5144\uff0c\u4fbf\u8bf7\u4f60\u5c06 \u90a3\u4e09\u5323\u793c\u7269\u53d6\u6765\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9762\u4e0a\u5fae\u5fae\u9732\u51fa\u96be\u8272\uff0c\u5450\u5450\u9053\uff1a\u201c\u4f46\u2026\u2026\u201d \u3000\u3000\u6a80\u660e\u9762\u8272\u4e00\u6c89\u9053\uff1a\u201c\u542c\u5230\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u5e72\u54b3\u4e00\u58f0\uff0c\u8f6c\u8eab\u800c\u51fa\uff0c\u5239\u90a3\u95f4\u4fbf\u9886\u7740\u4e09\u6761\u624b\u6367\u7ea2\u6728\u62dc\u5323\u7684\u5927\u6c49\uff0c\u5feb\u6b65\u8d70 \u4e86\u56de\u6765\u3002 \u3000\u3000\u4e1c\u65b9\u6e56\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u6a80\u5927\u53d4\u7a76\u7adf\u6709\u4ec0\u4e48\u53ef\u559c\u53ef\u8d3a\u4e4b\u4e8b\uff0c\u5982\u6b64\u6df1\u591c\uff0c\u8fd8\u6709\u4eba\u9001\u793c\u8fc7\u6765\uff1f\u201d \u3000\u3000\u53ea\u89c1\u90a3\u4e09\u6761\u5927\u6c49\u5c06\u638c\u4e2d\u62dc\u5323\u8f7b\u8f7b\u653e\u5728\u684c\u4e0a\uff0c\u5782\u9996\u655b\u7709\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u5012\u9000\u8d70\u56de\u5385\u5916\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u4e00\u626b\uff0c\u9762\u8272\u5927\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u9001\u793c\u7684\u4eba\u54ea\u91cc\u53bb\u4e86\uff1f\u8001\u592b\u5012\u8981\u597d\u597d\u916c\u8c22 \u4ed6\u4e00\u756a\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u606d\u8eab\u9053\uff1a\u201c\u65b9\u624d\u53ea\u542c\u5230\u95e8\u5916\u4e00\u9635\u54cd\u52a8\uff0c\u5f00\u95e8\u4e00\u770b\uff0c\u8fd9\u4e09\u5323\u4e1c\u897f\u5df2\u653e\u5728\u95e8 \u524d\u7684\u77f3\u9636\u4e0a\uff0c\u9001\u793c\u7684\u4eba\u5374\u65e9\u5df2\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u9762\u8272\u4e00\u7247\u94c1\u9752\u3002 \u3000\u3000\u4e1c\u65b9\u4e94\u5251\u4e00\u8d77\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u4e09\u4e2a\u7ea2\u6728\u62dc\u76d2\u4e4b\u4e0a\uff0c\u6574\u6574\u9f50\u9f50\u5730\u8d34\u7740\u4e09\u65b9\u767d\u7eb8\uff0c\u4e0a\u9762\u8d6b \u7136\u5199\u7684\u7adf\u662f\uff0c\u201c\u606d\u8d3a\u2018\u9f99\u5f62\u516b\u638c\u201d\u6a80\u603b\u9556\u5934\u8eab\u8d25\u540d\u88c2\u4e4b\u559c\u3002\u201c\u4e0b\u9762\u65e2\u65e0\u5177\u540d\uff0c\u4ea6\u65e0\u82b1\u62bc\u3002\u201d \u9f99\u5f62\u516b\u638c\u201c\u6d53\u7709\u5012\u8f69\uff0c\u4f4e\u53f1\u4e00\u58f0\uff0c\u201d\u89c1\u4e0d\u5f97\u4eba\u7684\u9f20\u8f88\uff01\u201c\u4e1c\u65b9\u5144\u5f1f\u60c5\u4e0d\u81ea\u7981\u5730\u56f4\u5728\u684c\u65c1\uff0c\u53ea \u89c1\u4ed6\u624b\u638c\u4e00\u626c\uff0c\u6380\u8d77\u4e00\u4e2a\u5323\u76d6\u3002\u4f17\u4eba\u5fcd\u4e0d\u4f4f\u4e00\u8d77\u60ca\u547c\u4e00\u58f0\uff0c\u8fd9\u5236\u4f5c\u5f97\u6781\u4e3a\u7cbe\u81f4\u7684\u7ea2\u6728\u62dc\u76d2\u4e4b \u5185\uff0c\u7adf\u653e\u7684\u662f\u4e00\u9897\u7528\u77f3\u7070\u56f4\u8d77\u7684\u4eba\u5934\u3002\u660f\u9ec4\u7684\u706f\u5149\u4e0b\uff0c\u53ea\u89c1\u8fd9\u4eba\u5934\u8840\u8ff9\u5df2\u88ab\u6d17\u53bb\uff0c\u800c\u4e14\u6829\u6829 \u5982\u751f\uff0c\u4e0a\u4e0b\u773c\u5e18\u4e4b\u95f4\uff0c\u5374\u4f3c\u88ab\u4e00\u6839\u6781\u7ec6\u7684\u94c1\u4e1d\u6491\u4e86\u8d77\u6765\uff0c\u4e00\u53cc\u7a7a\u6d1e\u800c\u6050\u6016\u7684\u773c\u775b\uff0c\u4fbf\u77ac\u4e5f\u4e0d \u77ac\u5730\u671b\u5728\u201d\u9f99\u5f62\u516b\u638c\u201c\u6a80\u660e\u9762\u4e0a\uff0c\u6a80\u660e\u5927\u559d\u4e00\u58f0\uff0c\u5012\u9000\u4e09\u6b65\uff0c\u4ed6\u4eba\u76ee\u4e4b\u4e0b\uff0c\u4fbf\u77e5\u9053\u8fd9\u9897\u4eba\u5934 \u6b63\u662f\u4ed6\u624b\u4e0b\u7684\u5f97\u529b\u9556\u5e08\u516c\u5b59\u5927\u8def\u3002\u5fae\u4e00\u5b9a\u795e\uff0c\u4ed6\u4fbf\u5c06\u53e6\u4e24\u4e2a\u5323\u76d6\u6380\u5f00\uff0c\u91cc\u9762\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u7136 \u4ea6\u662f\u4e24\u9897\u4eba\u5934\uff0c\u6b63\u662f\u4ed6\u624b\u4e0b\u7684\u5f97\u529b\u9556\u5e08\u5411\u98de\u65d7\u4e0e\u5f90\u660e\u6240\u6709\uff0c\u8fd9\u4e09\u4eba\u88ab\u4ed6\u8fde\u591c\u9063\u81f3\u6c5f\u752b\uff0c\u53bb \u53d6\u201d\u795e\u624b\u201c\u6218\u98de\u4e00\u5bb6\u5927\u5c0f\u7684\u9996\u7ea7\uff0c\u5374\u4e0d\u60f3\u4ed6\u4e09\u4eba\u7684\u9996\u7ea7\uff0c\u7ade\u5148\u88ab\u522b\u4eba\u65a9\u4e86\u4e0b\u6765\u3002\u82b1\u5385\u4e4b\u4e2d\u84e6 \u5730\u88ab\u4e00\u9635\u9634\u68ee\u4e4b\u6c14\u7b3c\u7f69\uff0c\u201d\u9f99\u5f62\u516b\u638c\u201c\u6a80\u660e\u6728\u7acb\u5728\u8fd9\u4e09\u9897\u9996\u7ea7\u4e4b\u524d\uff0c\u82cd\u767d\u7684\u987b\u53d1\uff0c\u968f\u7740\u5385\u5916 \u7684\u5bd2\u98ce\u4e0d\u4f4f\u98a4\u6296\u3002\u540d\u9707\u4e00\u65f6\u7684\u201d\u98de\u9f99\u4e09\u6770\u201c\uff0c\u7adf\u7136\u843d\u5f97\u5982\u6b64\u4e0b\u573a\uff01\u50f3\u662f\u4e1c\u65b9\u5c3a\u5f1f\uff0c\u4e5f\u4e0d\u7981\u5174 \u8d77\u4e00\u9635\u5154\u6b7b\u72d0\u60b2\uff0c\u7269\u4f24\u5176\u7c7b\u7684\u8427\u7d22\u4e4b\u611f\u3002\u201d\u9f99\u5f62\u516b\u638c\u201c\u6a80\u660e\u5fc3\u5934\u66f4\u662f\u6cdb\u8d77\u4e00\u9635\u9707\u60ca\u4e4b\u610f\uff0c\u4ed6 \u6df1\u77e5\u8fd9\u4e09\u4eba\u7684\u6b66\u529f\uff0c\u5728\u6b66\u6797\u4e2d\u5df2\u53ef\u7b97\u662f\u4e00\u6d41\u8eab\u624b\uff0c\u90a3\u4e48\u201d\u6d6a\u83bd\u5c71\u5e84\u201c\u7684\u6f5c\u529b\uff0c\u5c82\u975e\u66f4\u662f\u60ca \u4eba\uff1f\u4ed6\u81ea\u7136\u4e0d\u77e5\u9053\u201d\u795e\u624b\u201c\u6218\u98de\u53d6\u4e0b\u8fd9\u4e09\u4eba\u7684\u9996\u7ea7\uff0c\u5374\u4e5f\u82b1\u4e86\u6781\u5927\u7684\u4ee3\u4ef7\uff01\u4e00\u65f6\u4e4b\u95f4\uff0c\u4f17\u4eba \u5fc3\u5934\u4ff1\u89c9\u5341\u5206\u6c89\u91cd\uff0c\u201d\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u624b\u638c\u867d\u5df2\u7d27\u63e1\u6210\u62f3\uff0c\u5374\u4ecd\u5728\u4e0d\u4f4f\u98a4\u6296\u3002\u4e5f\u4e0d\u77e5\u9053\u8fc7\u4e86 \u591a\u4e45\uff0c\u4e1c\u65b9\u5251\u7a81\u5730\u60ca\u55a7\u4e00\u58f0\uff1a\u201c\u4e09\u5f1f\u5462\uff1f\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4f17\u4eba\u4e00\u60ca\uff0c\u8f6c\u8fc7\u76ee\u5149\uff0c\u90a3\u4e00\u76f4\u4e0d\u8a00\u4e0d\u52a8\u7684\u4e1c\u65b9\u9707\uff0c\u679c\u7136\u5df2\u4e0d\u77e5\u53bb\u5411\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272 \u5927\u53d8\uff0c\u9ad8\u547c\u9053\uff1a\u201c\u9707\u4e16\u5144\uff0c\u4e1c\u65b9\u9707\u2026\u2026\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u5fae\u4e00\u8dfa\u811a\uff0c\u53ea\u89c1\u5385\u540e\u7a97\u6237\u6d1e\u5f00\uff0c\u4ed6\u7bad\u6b65\u63a0\u5230\u7a97\u524d\uff0c\u7a97\u5916\u5bd2\u98ce\u51db\u51bd\uff0c\u54ea\u6709\u4eba\u5f71\uff1f \u3000\u3000\u4e1c\u65b9\u5374\u5df2\u9732\u51fa\u4e00\u4e1d\u8f7b\u6de1\u7684\u9c7c\u809a\u81ea\u8272\uff0c\u8ddd\u79bb\u9ece\u660e\uff0c\u4f3c\u4e4e\u5df2\u7ecf\u4e0d\u8fdc\u4e86\u3002 \u3000\u3000\u6a80\u6587\u742a\u5e7d\u5e7d\u5730\u72ec\u5750\u5728\u4e00\u76cf\u5b64\u706f\u4e4b\u4e0b\uff0c\u68a6\u4e00\u6837\u706f\u5149\uff0c\u6620\u7740\u5979\u68a6\u4e00\u6837\u7684\u773c\u775b\uff0c\u548c\u5979\u7684\u9b13\u53d1\u3002 \u3000\u3000\u5979\u7684\u8eab\u4f53\u3001\u5fc3\u667a\u3001\u7075\u9b42\uff0c\u90fd\u4f3c\u4e4e\u5728\u68a6\u4e2d\u4e00\u6837\uff0c\u4f46\u8fd9\u5374\u662f\u4e2a\u591a\u4e48\u5fe7\u6101\uff0c\u591a\u4e48\u75db\u82e6\u7684\u5669\u68a6 \u554a\uff01 \u3000\u3000\u5f80\u6614\u7684\u6b22\u4e50\u4e0e\u7b11\u5bb9\uff0c\u60b2\u54c0\u4e0e\u54ed\u6ce3\uff0c\u6b64\u523b\u4ff1\u90fd\u5df2\u7ecf\u79bb\u5979\u8fdc\u53bb\uff0c\u56e0\u4e3a\u5979\u7684\u8eab\u4f53\u4e0e\u7075\u9b42\uff0c\u4ff1\u5df2 \u53d8\u5f97\u6709\u5982\u767d\u75f4\u7684\u9ebb\u6728\u3002 \u3000\u3000\u5979\u65e9\u5df2\u7acb\u4e0b\u51b3\u5fc3\uff0c\u4eca\u751f\u4eca\u4e16\uff0c\u5979\u6c38\u8fdc\u4e0d\u8981\u518d\u52a8\u4efb\u4f55\u60c5\u611f\uff0c\u56e0\u4e3a\u201c\u60c5\u611f\u201d\u8fd9\u4e0d\u662f\u4e00\u4ef6\u6781\u4e3a \u53ef\u6015\u7684\u4e8b\u4e48\uff1f \u3000\u3000\u5979\u62d2\u7edd\u56de\u5fc6\uff0c\u62d2\u7edd\u601d\u5ff5\uff0c\u5979\u53ea\u8981\u50cf\u50f5\u5c38\u4e00\u822c\u6d3b\u4e0b\u53bb\uff0c\u5979\u7239\u7239\u51e0\u65f6\u4e3a\u5979\u5b89\u6392\u4e0b\u5a5a\u671f\uff0c\u5979\u5c31 \u51e0\u65f6\u7a7f\u4e0a\u5409\u670d\uff01\u7136\u540e\u2026\u2026 \u3000\u3000\u7136\u540e\u5462\uff1f\u5979\u4e5f\u62d2\u7edd\u53bb\u60f3\uff0c\u5979\u6df1\u4fe1\u8fd9\u4e00\u4efd\u9ebb\u6728\u4f1a\u4f7f\u5979\u6781\u5feb\u5730\u6b7b\u53bb\uff0c\u6216\u8005\u5979\u4e0d\u7b49\u9ebb\u6728\u5c06\u81ea\u5df1 \u6740\u6b7b\uff0c\u4fbf\u5148\u6740\u6b7b\u81ea\u5df1\u3002 \u3000\u3000\u7a81\u5730\uff01\u7a97\u5916\u4e00\u9635\u8f7b\u54cd\u3002 \u3000\u3000\u5979\u4e0d\u95ee\u4e0d\u52a8\uff0c\u6709\u5982\u672a\u95fb\uff0c\u4f46\u7a97\u5916\u5374\u53c8\u54cd\u8d77\u4e86\u4e00\u4e2a\u6c89\u91cd\u7684\u8bed\u58f0\u3002 \u3000\u3000\u201c\u6a80\u59d1\u5a18\uff01\u201d \u3000\u3000\u5979\u832b\u7136\u8d70\u5230\u7a97\u524d\uff0c\u652f\u5f00\u7a97\u5b50\uff0c\u6b64\u523b\u5979\u5fc3\u60c5\u867d\u6709\u4e00\u4e1d\u5fae\u52a8\uff0c\u4f46\u662f\u5979\u62d2\u7edd\u53bb\u60f3\uff0c\u62d2\u7edd\u53bb\u60f3\u4e00 \u5207\u60b2\u54c0\u6216\u8005\u6b22\u4e50\u3002 \u3000\u3000\u7a97\u5916\u9ed1\u5f71\u4e00\u95ea\uff0c\u5411\u5979\u62db\u4e86\u62db\u624b\uff0c\u53c8\u5411\u5979\u62db\u4e86\u62db\u624b\u2026\u2026 \u3000\u3000\u5f53\u7a97\u5916\u7684\u4eba\u5f71\u7b2c\u4e09\u6b21\u62db\u624b\u7684\u65f6\u5019\uff0c\u5979\u4e0b\u610f\u8bc6\u5730\u8f7b\u8f7b\u63a0\u51fa\u7a97\u5916\u3002 \u3000\u3000\u5979\u8f7b\u529f\u4ecd\u7136\u662f\u7f8e\u597d\u7684\uff0c\u5728\u5bc2\u9759\u7684\u5bd2\u591c\u4e2d\uff0c\u8f7b\u8f7b\u5730\u6e9c\u4e86\u51fa\u6765\uff0c\u597d\u50cf\u662f\u5929\u9e45\u6ed1\u884c\u5728\u51b0\u9762\u4e0a\u4e00 \u6837\u3002 \u3000\u3000\u4f46\u524d\u9762\u90a3\u4eba\u5f71\u7684\u8f7b\u529f\uff0c\u5374\u66f4\u52a0\u9ad8\u5999\uff0c\u5979\u5fc3\u5934\u6709\u4e9b\u5403\u60ca\u2014\u2014\u4f46\u662f\u5979\u62d2\u7edd\u53bb\u60f3\u3002 \u3000\u3000\u5239\u773c\u95f4\uff0c\u4ed6\u4eec\u4e24\u4eba\u4e00\u524d\u4e00\u540e\u63a0\u51fa\u4e86\u540e\u9662\uff0c\u63a0\u8fc7\u4e86\u9cde\u6b21\u67ff\u6bd4\u7684\u5c4b\u810a\uff0c\u63a0\u5230\u4e00\u89d2\u57ce\u5e02\u4e2d\u7684\u8352 \u91ce\u3002 \u3000\u3000\u6a80\u6587\u742a\u8f7b\u8f7b\u4e24\u63a0\uff0c\u63a0\u5230\u4ed6\u8eab\u524d\uff0c\u53ea\u89c1\u4ed6\u957f\u8eab\u7389\u7acb\uff0c\u76ee\u5149\u70af\u70af\uff0c\u82cd\u767d\u7684\u8138\uff0c\u6f06\u9ed1\u7684\u7709\uff0c\u7709 \u5b57\u95f4\u5374\u5e26\u7740\u4e00\u4efd\u6c89\u91cd\u7684\u5fe7\u90c1\u3002 \u3000\u3000\uff0c\u5979\u8ba4\u5f97\u4ed6\uff0c\u5979\u77e5\u9053\u6b64\u4eba\u4fbf\u662f\u6b66\u6797\u4e2d\u7684\u9a84\u5b50\uff0c\u201c\u4e1c\u65b9\u4e94\u5251\u201d\u4e2d\u7684\u4e1c\u65b9\u9707\uff0c\u5979\u4e5f\u77e5\u9053\u6b64\u4eba \u4fbf\u662f\u7239\u7239\u4e3a\u81ea\u5df1\u8ba2\u4e0b\u7684\u592b\u5a7f\u3002 \u3000\u3000\u4f46\u662f\u5979\u9762\u5bb9\u4ecd\u662f\u832b\u7136\uff0c\u65e2\u4e0d\u60ca\u8bb6\uff0c\u4e5f\u4e0d\u7f9e\u6da9\uff0c\u53ea\u662f\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8fd9\u79cd\u51fa\u5947\u7684\u51b7\u9759\uff0c\u4f7f\u5f97\u672c\u5df2\u51b7\u9759\u7684\u4e1c\u65b9\u9707\u90fd\u4e3a\u4e4b\u4e00\u6014\u3002 \u3000\u3000\u4ed6\u6728\u7acb\u4e86\u8bb8\u4e45\uff0c\u60f3\u662f\u8981\u5c06\u81ea\u5df1\u5fc3\u91cc\u7684\u8bb8\u591a\u79cd\u60c5\u611f\u90fd\u5316\u505a\u51b7\u9759\u7684\u529b\u91cf\uff0c\u76f4\u5230\u4ed6\u9762\u4e0a\u518d\u65e0\u4e00 \u4e1d\u8868\u60c5\uff0c\u4ed6\u624d\u81ea\u7f13\u7f13\u9053\uff1a\u201c\u6211\u53ea\u60f3\u95ee\u4f60\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u9053\uff1a\u201c\u8bf4\uff01\u201d \u3000\u3000\u4e1c\u65b9\u9707\u53cc\u62f3\u4e00\u7d27\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u662f\u7b54\u5e94\u5ac1\u7ed9\u6211\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u9053\uff1a\u201c\u662f\u2026\u2026\u201d \u3000\u3000\u4e1c\u65b9\u9707\u7d27\u7d27\u54ac\u7740\u81ea\u5df1\u7684\u7259\u9f7f\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u53ef\u662f\u81ea\u5df1\u613f\u610f\u7684\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d \u3000\u3000\u4e1c\u65b9\u9707\u5fc3\u5934\u4e00\u51c9\uff0c\u4e00\u9635\u5bd2\u610f\uff0c\u81ea\u811a\u5e95\u76f4\u8fbe\u5fc3\u623f\uff0c\u671b\u7740\u773c\u524d\u7684\u4e00\u7247\u9ed1\u6697\uff0c\u4ed6\u53c8\u6728\u7136\u826f\u4e45\uff0c \u7f13\u7f13\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4e8b\u4f7f\u4f60\u7b54\u5e94\u7684\u5462\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u76ee\u5149\u4e0a\u4e0b\u79fb\u52a8\uff0c\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u8fd9\u76ee\u5149\u50cf\u662f\u5df2\u5b8c\u5168\u5c06\u4ed6\u5f53\u505a\u4e00\u5757\u6728\u5934\u4e00\u6837\uff0c\u7136\u540e\u5979 \u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u5ac1\u7ed9\u4f60\uff0c\u7239\u7239\u5c31\u6c38\u8fdc\u4e0d\u4f24\u88f4\u73cf\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u5fae\u843d\uff0c\u5634\u89d2\u7a81\u5730\u6cdb\u8d77\u4e00\u4e1d\u8f7b\u8511\u800c\u8ba5\u5632\u7684\u5fae\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4e86\u4e48\uff1f\u4f60\u6ee1\u610f\u4e86 \u4e48\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u9707\u6728\u7acb\u534a\u664c\uff0c\u6709\u5982\u88ab\u4eba\u5728\u8138\u4e0a\u63cd\u4e86\u6570\u5341\u4e2a\u8033\u5149\u4e00\u6837\uff0c\u9762\u662f\u9635\u9752\u9635\u81ea\uff0c\u5fc3\u5934\u601d\u6f6e\u7ffb \u6d8c\uff0c\u7a81\u5730\u5927\u559d\u9053\uff1a\u201c\u597d\uff1a\u597d\uff0c\u4f60\u6bcb\u5eb8\u5ac1\u6211\uff0c\u6211\u8d70\uff0c\u6211\u8d70\uff01\u201d \u3000\u3000\u7ffb\u8eab\u4e00\u8dc3\uff0c\u6709\u5982\u75af\u72c2\u7684\u5411\u9ed1\u6697\u4e2d\u5954\u53bb\uff0c\u53ea\u7559\u4e0b\u4ed6\u98a4\u6296\u7684\u8bed\u58f0\uff0c\u4ecd\u5728\u9ed1\u6697\u4e2d\u968f\u98ce\u98d8\u8361\u3002 \u3000\u3000\u591c\u8272\uff0c\u7b3c\u7f69\u7740\u6a80\u6587\u742a\u82cd\u767d\u7684\u9762\u5bb9\uff0c\u5979\u76ee\u4e2d\u4f3c\u4e4e\u5fae\u5fae\u6709\u4e00\u4e9b\u6676\u83b9\u7684\u5149\u8292\uff0c\u5979\u6df1\u77e5\u81ea\u5df1\u5df2\u4f24 \u5bb3\u4e86\u4e00\u4e2a\u5c11\u5e74\u7684\u5fc3\uff0c\u5979\u5f97\u77e5\u81ea\u5df1\u65b9\u624d\u90a3\u7b80\u77ed\u51b0\u51b7\u7684\u8bed\u53e5\uff0c\u5df2\u50cf\u5343\u4e07\u679d\u5229\u7bad\uff0c\u5c06\u8fd9\u5c11\u5e74\u7684\u7075\u9b42 \u6253\u5f97\u767e\u5b54\u5343\u75ae\uff0c\u2014\u2014\u4f46\u662f\u5979\u62d2\u7edd\u53bb\u60f3\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u4ece\u6b64\u4f1a\u5c11\u4e86\u4e00\u4e2a\u524d\u9014\u65e0\u9650\u7684\u82f1\u96c4\uff0c\u5979\u7239\u7239\u671f\u671b\u4e2d\u7684\u5a5a\u793c\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u4e3e\u884c\uff0c\u505a\u597d\u7684 \u5409\u670d\u5c06\u6c38\u8fdc\u7f6e\u4e4b\u9ad8\u9601\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\uff0c\u4e0e\u5979\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f \u3000\u3000\u5979\u62d2\u7edd\u53bb\u60f3\u3002 \u3000\u3000\u5979\u4ec0\u4e48\u4e5f\u4e0d\u60f3\uff0c\u50cf\u662f\u4ec0\u4e48\u90fd\u672a\u66fe\u7ecf\u53d1\u751f\u8fc7\u4f3c\u7684\uff0c\u9759\u9759\u5730\u5411\u6765\u8def\u63a0\u56de\u3002 \u3000\u3000\u7a81\u5730\uff0c\u5979\u53d1\u89c9\u6709\u4e00\u6761\u4eba\u5f71\u6321\u5728\u5979\u9762\u524d\u3002 \u3000\u3000\u8fd9\u4eba\u5f71\u6765\u5f97\u662f\u5982\u6b64\u7a81\u7136\uff0c\u5c31\u50cf\u662f\u4e00\u7247\u7a81\u7136\u98d8\u6765\u7684\u5bd2\u96fe\uff0c\u6a80\u6587\u742a\u4e00\u63d0\u771f\u6c14\uff0c\u987f\u4f4f\u8eab\u5f71\uff0c\u53ea \u89c1\u81ea\u5df1\u9762\u524d\u4e0d\u77e5\u4f55\u65f6\u5df2\u591a\u4e86\u4e00\u4f4d\u81ea\u8863\u5982\u96ea\uff0c\u4e91\u9afb\u9ad8\u633d\uff0c\u4f46\u8eab\u5f62\u4e4b\u9ad8\u5927\u5374\u662f\u9a87\u4eba\u542c\u95fb\u7684\u5973\u5b50\u3002 \u3000\u3000\u6700\u602a\u7684\u662f\uff0c\u5728\u8fd9\u5973\u5b50\u8eab\u540e\uff0c\u7adf\u7136\u8fd8\u8d1f\u7740\u4e00\u53ea\u9ec4\u91d1\u8272\u7684\u85e4\u841d\uff0c\u85e4\u841d\u4e4b\u4e2d\uff0c\u7adf\u5750\u7740\u4e00\u4e2a\u6ee1\u8eab \u91d1\u886b\u7684\u7537\u5b50\u3002 \u3000\u3000\u4ed6\u8eab\u8eaf\u4e4b\u5c0f\uff0c\u6709\u5982\u5e7c\u7ae5\uff0c\u4f46\u8863\u51a0\u5ce8\u7136\uff0c\u5374\u4eff\u4f5b\u738b\u4faf\u3002\u9894\u4e0b\u957f\u987b\u98d8\u62c2\uff0c\u4e1d\u4e1d\u7f15\u7f15\uff0c\u8f7b\u8f7b\u62c2 \u5728\u8fd9\u96ea\u8863\u5973\u4e8e\u9ad8\u633d\u7684\u96ea\u5bb3\u4e4b\u4e0a\uff0c\u4e00\u53cc\u4eff\u4f5b\u53ef\u4ee5\u76f4\u900f\u4eba\u5fc3\u7684\u76ee\u5149\uff0c\u5374\u77ac\u4e5f\u4e0d\u77ac\u5730\u5728\u671b\u7740\u6a80\u6587 \u742a\u3002 \u3000\u3000\u64c5\u6587\u742a\u5fc3\u5934\u5fae\u9707\uff0c\u5df2\u81ea\u60f3\u8d77\u8fd9\u4e24\u4eba\u662f\u4ec0\u4e48\u4eba\u6765\uff01\u5979\u5fc3\u5934\u4e00\u7247\u51b0\u51c9\uff0c\u9762\u4e0a\u7adf\u4e5f\u65e0\u52a8\u4e8e\u8877\uff0c \u53ea\u662f\u8f7b\u8f7b\u4e00\u63d6\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u201c\u91d1\u7ae5\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u53ea\u6015\u9664\u4e86\u73cf\u513f\u6b7b\u5728\u5979\u9762\u524d\u4e4b\u5916\uff0c\u4e16\u4e0a\u7684\u4efb\u4f55\u4e8b\u90fd\u4e0d\u4f1a\u8ba9 \u5979\u5fc3\u52a8\u7684\u4e86\uff01\u201d \u3000\u3000\u201c\u7389\u5973\u201d\u9762\u4e0a\u4e00\u7247\u4f36\u60af\u5173\u5fc3\u4e4b\u8272\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6765\u65e5\u65b9\u957f\uff0c\u4e3a\u4ec0\u4e48\u8fd9 \u6837\u60f3\u4e0d\u5f00\u5462\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u51c4\u7136\u4e00\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8695\u5df2\u6210\u8327\uff0c\u552f\u7b49\u62bd\u4e1d\uff0c\u8721\u70db\u6210\u7070\uff0c\u6cea\u65e9\u6d41\u5e72\uff0c\u4e16\u4e0a\u4e07\u7269\u4e07 \u4e8b\uff0c\u4fbf\u5982\u955c\u82b1\u6c34\u6708\u800c\u5df2\uff0c\u665a\u8f88\u5b9e\u5728\u60f3\u5f97\u592a\u5f00\u4e86\u5305\u201d\u91d1\u7ae5\u201c\u4f38\u624b\u4e00\u634b\u957f\u9aef\uff0c\u542b\u7b11\u9053\uff1a\u201c\u771f\u7684 \u4e48\uff1f\u201d \u3000\u3000\u201c\u7389\u5973\u201d\u56de\u9996\u671b\u4ed6\u4e00\u773c\uff0c\u5fae\u55d4\u9053\uff1a\u201c\u4eba\u5bb6\u5df2\u662f\u8fd9\u79cd\u5fc3\u60c5\uff0c\u96be\u9053\u8bf4\u8bdd\u8fd8\u4f1a\u9a97\u4f60\u4e48\uff1f\u201d \u3000\u3000\u91d1\u7ae5\u201c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u544a\u8bc9\u4f60\uff0c\u4f60\u7684\u8695\u65e2\u6ca1\u6709\u6210\u8327\uff0c\u4f60\u7684\u8721\u4e5f\u6ca1\u6709\u6210\u7070\uff0c\u53ea\u8981\u6709\u6211 \u8001\u5934\u5b50\u592b\u59bb\u4fe9\u4eba\u5728\uff0c\u4e16\u4e0a\u5c31\u6ca1\u6709\u8865\u4e0d\u597d\u7684\u591a\u60c5\u5e38\u6068\u4e4b\u5929\u3002\u201d \u3000\u3000\u6a80\u6587\u742a\u79cb\u6ce2\u4e00\u4eae\uff0c\u5fcd\u4e0d\u4f4f\u62ac\u5934\u671b\u4e86\u8fd9\u4e24\u4f4d\u6b66\u6797\u5f02\u4eba\u4e00\u773c\u3002 \u3000\u3000\u201c\u7389\u5973\u201d\u8f7b\u8f7b\u4e00\u7b11\uff0c\u4f38\u624b\u629a\u5f04\u7740\u5979\u7684\u9b13\u53d1\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u7cbe\u8bda\u6240\u81f3\uff0c\u91d1\u77f3\u4e3a\u5f00\uff0c\u4e16\u4e0a\u6c38 \u8fdc\u6ca1\u6709\u771f\u60c5\u6240\u4e0d\u80fd\u611f\u52a8\u7684\u4e8b\uff0c\u60f3\u8d77\u4ee5\u524d\uff0c\u6211\u548c\u4ed6\u2026\u2026\u201d \u3000\u3000\u5979\u60c5\u6df1\u5982\u6d77\u5730\u56de\u9996\u671b\u4e86\u201c\u91d1\u7ae5\u201d\u4e00\u773c\uff0c\u5979\u7c97\u8c6a\u7684\u9762\u5bb9\uff0c\u7a81\u5730\u5448\u73b0\u51fa\u4e00\u79cd\u65e0\u6bd4\u7684\u6e29\u67d4\uff0c\u7f13 \u7f13\u63a5\u9053\uff1a\u201c\u6211\u548c\u4ed6\u90a3\u65f6\u6240\u9047\u7740\u7684\u963b\u788d\u4e0e\u56f0\u96be\uff0c\u771f\u4e0d\u77e5\u6bd4\u4f60\u4eec\u8fd8\u8981\u591a\u82e5\u5e72\u500d\uff0c\u4f46\u662f\u2026\u2026\u4f60\u770b\uff0c \u6211\u4eec\u73b0\u5728\u8fd8\u4e0d\u662f\u5728\u4e00\u8d77\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u671b\u7740\u8fd9\u4e24\u4f4d\u6b66\u6797\u5f02\u4eba\u60ac\u6b8a\u7684\u8eab\u5f71\uff0c\u671b\u7740\u4ed6\u4eec\u4e24\u4eba\u4e4b\u95f4\u6e29\u67d4\u7684\u60c5\u610f\uff0c\u7a81\u7136\u89c9\u5f97\u81ea\u5df1 \u51b0\u51b7\u800c\u9ebb\u6728\u7684\u5fc3\u623f\uff0c\u53c8\u6709\u4e86\u4e00\u4e1d\u60c5\u611f\u4e0e\u6e29\u6696\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u53cc\u6b66\u6797\u5f02\u4eba\u9762\u524d\uff0c\u4e16\u4e0a\u6240\u6709\u7684\u201c\u4e0d\u53ef\u80fd\u201d\u4f3c\u4e4e\u90fd\u53d8\u4f5c\u4e86\u201c\u53ef\u80fd\u201d\uff0c\u4e16\u4e0a\u6240\u6709\u7684 \u201c\u60c5\u75f4\u201d\u4f3c\u4e4e\u90fd\u53d8\u4f5c\u4e86\u201c\u4fe1\u4ef0\u201d\uff0c\u4e16\u4e0a\u6240\u6709\u7684\u201c\u68a6\u201d\u4f3c\u4e4e\u90fd\u53d8\u4f5c\u4e86\u201c\u771f\u5b9e\u201d\uff0c\u4e16\u4e0a\u6240\u6709\u7684 \u201c\u773c\u6cea\u201d\u5374\u53ef\u80fd\u53d8\u4f5c\u201c\u5fae\u7b11\u201d\u3002 \u3000\u3000\u5979\u5583\u5583\u4f4e\u8bed\uff1a\u201c\u7cbe\u8bda\u6240\u81f3\uff0c\u91d1\u77f3\u4e3a\u5f00\uff0c\u662f\u771f\u7684\u4e48\uff1f\u201d \u3000\u3000\u201c\u91d1\u7ae5\u201d\u7b11\u5bb9\u4e00\u655b\uff0c\u6b63\u8272\u9053\uff1a\u201c\u81ea\u7136\u662f\u771f\u7684\uff0c\u53ea\u8981\u4f60\u7684\u60c5\u611f\u80fd\u7ecf\u5f97\u8d77\u75db\u6614\u7684\u8003\u9a8c\uff0c\u90a3\u4e48 \u4f60\u7684\u771f\u60c5\uff0c\u4fbf\u603b\u4f1a\u5f97\u5230\u62a5\u507f\u7684\u4e00\u5929\u3002\u201d \u3000\u3000\u201c\u7389\u5973\u201d\u67d4\u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u6709\u4e86\u771f\u60c5\uff0c\u4f46\u662f\u4f60\u6ca1\u6709\u4fe1\u5ff5\uff0c\u6240\u4ee5\u4f60\u5c31\u53d8\u5f97\u75db\u82e6\u800c\u9ebb\u6728\uff0c \u5b69\u5b50\uff0c\u4f60\u613f\u610f\u542c\u6211\u4eec\u7684\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u6a80\u6587\u742a\u7a81\u89c9\u5fc3\u5934\u4e00\u9635\u771f\u60c5\u6fc0\u8361\uff0c\u9762\u4e0a\u5df2\u6d41\u4e0b\u4e45\u672a\u6d41\u843d\u7684\u6cea\u73e0\u3002 \u3000\u3000\u5979\u4ef0\u9762\u5411\u5929\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u201c\u91d1\u7ae5\u201d\u6717\u58f0\u7b11\u9053\uff1a\u201c\u597d\uff0c\u53ea\u8981\u4f60\u6709\u771f\u60c5\u4e0e\u4fe1\u5ff5\uff0c\u6211\u5c31\u70bc\u5f97\u51fa\u8865\u5929\u7684\u91c7\u77f3\u3002\u201d \u3000\u3000\u201c\u7389\u5973\u201d\u67d4\u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u8ddf\u6211\u4eec\u8d70\uff0c\u5728\u4f60\u524d\u9762\u867d\u7136\u8fd8\u6709\u4e00\u6bb5\u9065\u8fdc\u800c\u8270\u96be\u7684\u8def\u9014\uff0c\u4f46\u662f \u4e0d\u8981\u6015\uff0c\u4f60\u770b\uff0c\u9ed1\u6697\u867d\u957f\uff0c\u9ece\u660e\u4e0d\u662f\u4e5f\u5230\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u6a80\u6587\u742a\u518d\u6b21\u70b9\u4e86\u70b9\u5934\uff0c\u8ddf\u7740\u8fd9\u4e00\u53cc\u6b66\u6797\u5f02\u4eba\uff0c\u5411\u4e1c\u65b9\u7b2c\u4e00\u4e1d\u66d9\u5149\u8d70\u53bb\u3002\u9ed1\u6697\u867d\u957f\uff0c\u9ece \u660e\u7ec8\u4e8e\u5230\u4e86\u3002\u98ce\u4ecd\u6025\uff0c\u96ea\u53c8\u843d\uff0c\u51ac\uff0c\u66f4\u5bd2\u4e86\u3002\u4f46\u6b66\u6c49\u9547\u4e0a\u7684\u4e00\u7fa4\uff0c\u5374\u4e1d\u6beb\u4e0d\u907f\u5bd2\u51b7\uff0c\u4ecd\u7136\u62e5 \u6324\u5728\u90a3\u4e00\u6761\u957f\u8857\u4e0a\u3002\u6628\u591c\u901a\u5bb5\u672a\u7720\u7684\u4eba\uff0c\u4eca\u6668\u4ecd\u7136\u662f\u7cbe\u795e\u5955\u5955\u3002\u201d\u9f99\u5f62\u516b\u638c\u201c\u5230\u4e86\uff0c\u66b4\u98ce\u96e8 \u8fd8\u4f1a\u8fdc\u4e48\uff1f\u591a\u6570\u7684\u76ee\u5149\uff0c\u6216\u8fdc\u6216\u8fd1\u5730\u90fd\u805a\u96c6\u5728\u90a3\u6247\u7d27\u95ed\u7740\u7684\u9ed1\u6f06\u5927\u95e8\u4e0a\u3002\u6d41\u8a00\u3001\u8033\u8bed\uff0c\u4e0d\u65ad \u5730\u5728\u57ce\u4e2d\u4f20\u64ad\u7740\uff01\u201d\u4f60\u53ef\u77e5\u9053\uff0c\u6218\u795e\u624b\u4e5f\u5230\u4e86\u8fd9\u91cc\uff1f\u201c\u201d\u6628\u591c\u6211\u770b\u89c1\u6709\u4eba\u9001\u4e86\u4e09\u4e2a\u7ea2\u6728\u62dc \u76d2\uff0c\u5230\u2018\u9f99\u5f62\u516b\u638c\u2019\u90a3\u91cc\uff0c\u91cc\u9762\u8bf4\u4e0d\u5b9a\u88c5\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201c\u201d\u2018\u9f99\u5973\u2019\u6a80\u6587\u742a\u4e5f\u6765\u4e86\uff0c\u5927\u6982 \u5c31\u8981\u548c\u2018\u4e1c\u65b9\u4e94\u5251\u2019\u4e2d\u7684\u9707\u4e09\u7237\u6210\u4eb2\u4e86\uff0c\u8fd9\u4e00\u6765\uff0c\u563f\uff0c\u2018\u9f99\u5f62\u516b\u638c\u2019\u53ef\u66f4\u662f\u5982\u864e\u6dfb\u7ffc\u4e86\u3002 \u201c\u201d\u6211\u548c\u4f60\u6253\u8d4c\uff0c\u4e0d\u5230\u5348\u95f4\uff0c\u88f4\u5927\u5148\u751f\u5c31\u4f1a\u6765\u6211\u6a80\u660e\u62a5\u4ec7\u3002\u201c\u201d\u4f60\u5012\u8bf4\u8bf4\u770b\uff0c\u4ed6\u4eec\u4e24\u4f4d\u5230\u5e95 \u662f\u8c01\u6b66\u529f\u9ad8\u4e9b\uff1f\u201c\u201d\u795e\u624b\u201c\u6218\u98de\u7684\u624b\u4e0b\uff0c\u4e5f\u6df7\u6742\u5728\u4eba\u7fa4\u4e2d\uff0c\u4f20\u64ad\u7740\u6216\u771f\u6216\u5047\u7684\u6d41\u8a00\u3002\u201d\u4f60\u53ef \u77e5\u9053\uff0c\u2018\u98de\u9f99\u4e09\u6770\u2019\u516c\u5b59\u5927\u8def\u3001\u5411\u98de\u65d7\u3001\u5f90\u660e\u4e09\u4f4d\u4e3b\u513f\uff0c\u90fd\u88ab\u2018\u6218\u795e\u624b\u2019\u5207\u4e0b\u4e86\u8111\u888b\uff0c\u6628\u591c \u90a3\u4e09\u4e2a\u62dc\u76d2\uff0c\u91cc\u9762\u88c5\u7684\u5c31\u662f\u4ed6\u4eec\u7684\u4eba\u5934\u3002\u201c\u201d\u4f60\u53ef\u77e5\u9053\uff0c\u6a80\u660e\u867d\u7136\u5c06\u5973\u513f\u5e26\u6765\uff0c\u4f46\u4eba\u5bb6\u4e1c\u65b9 \u5144\u5f1f\u5374\u672a\u5fc5\u80af\u8ddf\u5979\u6210\u4eb2\uff0c\u574f\u4e86\u81ea\u5df1\u7684\u540d\u5934\u3002\u201c\u201d\u88f4\u5927\u5148\u751f\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u6b66\u529f\u53ef\u771f\u662f\u9ad8\u5f97\u4e0d\u53ef \u601d\u8bae\uff0c\u53ea\u8981\u4ed6\u4e00\u51fa\u624b\uff0c\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u53ef\u771f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff1a\u201c\u6d41\u8a00\u3001\u8033\u8bed\uff0c\u6ee1\u57ce\u98ce\u96e8\u3002\u65f6 \u95f4\uff0c\u8fc7\u5f97\u751f\u50cf\u662f\u5206\u5916\u7f13\u6162\uff0c\u5c06\u5230\u5348\u95f4\uff0c\u6b66\u6c49\u9547\u4e0a\uff0c\u6c49\u53e3\u57ce\u91cc\uff0c\u5374\u4ecd\u672a\u51fa\u73b0\u8fc7\u201d\u88f4\u5927\u5148\u751f \u201c\u3001\u201d\u795e\u624b\u201c\u6218\u98de\u3001\u201d\u4e1c\u65b9\u4e94\u5251\u201c\u3001\u201d\u9f99\u5f62\u516b\u638c\u201c\u201d\u4e03\u5de7\u8ffd\u9b42\u201c\u8fd9\u4e00\u4e9b\u4e07\u4eba\u77a9\u76ee\u7684\u4eba\u3002\u57ce\u5185 \u867d\u672a\u843d\u96ea\uff0c\u90ca\u5916\u5374\u6709\u96ea\u82b1\u3002\u88f4\u73cf\u7acb\u5728\u6a90\u4e0b\uff0c\u671b\u7740\u7eb7\u98de\u7684\u96ea\u82b1\uff0c\u5fc3\u5934\u601d\u7eea\uff0c\u5df2\u5982\u96ea\u82b1\u4e00\u822c\u7eb7 \u4e71\u3002\u4ed6\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u5c31\u5728\u6c49\u53e3\u57ce\u91cc\uff0c\u4f46\u662f\u6700\u6700\u6df1\u7231\u7740\u4ed6\u7684\u5973\u5b50\uff0c\u5374\u5c31\u5728\u4ed6\u4ec7\u4eba\u7684\u8eab \u4fa7\u3002\u201d\u2026\u2026\u201c\u201d\u4f60\u4ece\u4eca\u4ee5\u540e\uff0c\u6709\u751f\u4e4b\u5e74\uff0c\u6c38\u8fdc\u4e0d\u8981\u53eb\u4efb\u4f55\u4e00\u4e2a\u7231\u4f60\u7684\u5973\u5b69\u5b50\u4f24\u5fc3\u2026\u2026\u201c\u4ed6\u53cd \u590d\u9ed8\u5ff5\u7740\u8fd9\u53e5\u8bdd\uff0c\u773c\u524d\u7eb7\u7eb7\u7684\u96ea\u82b1\uff0c\u6bcf\u4e00\u6735\u4f3c\u4e4e\u90fd\u53d8\u6210\u201d\u51b7\u6708\u4ed9\u5b50\u201c\u90a3\u82cd\u767d\u3001\u60b2\u54c0\uff0c\u800c\u53c8\u523b \u9aa8\u94ed\u5fc3\u7684\u719f\u6089\u7684\u9762\u5bb9\u3002\u4ed6\u4e0d\u5fcd\u8fdd\u80cc\u5979\u4e34\u7ec8\u524d\u7684\u8bdd\uff0c\u4f46\u4ed6\u5374\u53c8\u600e\u80fd\u5fd8\u8bb0\u90a3\u4e0d\u5171\u6234\u5929\u7684\u6df1\u4ec7\uff1f\u4ed6 \u4e0d\u80fd\u5fd8\u8bb0\u90a3\u4e0d\u5171\u6234\u5929\u7684\u6df1\u4ec7\uff0c\u4f46\u4ed6\u53c8\u600e\u80fd\u5fd8\u8bb0\u6a80\u6587\u742a\u90a3\u5982\u6d77\u7684\u6df1\u60c5\uff1f\u201d\u65e0\u8bba\u600e\u6837\uff0c\u6211\u603b\u4e0d\u80fd \u8ba9\u7239\u7239\u4e0e\u53d4\u53d4\u542b\u6068\u4e8e\u4e5d\u6cc9\u4e4b\u4e0b\uff01\u201c\u4ed6\u5fc3\u4e2d\u7ec8\u4e8e\u4e0b\u4e86\u51b3\u5b9a\uff0c\u970d\u7136\u8f6c\u8eab\uff0c\u5750\u5728\u7a97\u524d\u7684\u8881\u6cf8\u73cd\u7a81\u5730 \u5e7d\u5e7d\u5730\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u96ea\u8fd9\u4e48\u5927\uff0c\u6587\u742a\u59d0\u59d0\u4e0d\u77e5\u9053\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u9635\u98a4\u6296\uff0c\u201c\u4e03\u5de7\u8ffd\u9b42\uff0c\u201d\u90a3\u98de\u8679\u9053\uff1a\u201c\u5509\uff0c\u2018\u9f99\u5f62\u516b\u638c\u2019\u4e00\u76f4\u5230\u6b64\u523b\u4ecd\u6ca1\u6709 \u52a8\u9759\uff0c\u8fd9\u6837\u7b49\u5f85\u771f\u6bd4\u4ec0\u4e48\u4e8b\u90fd\u8981\u4ee4\u4eba\u96be\u53d7\uff1a\u6211\u2026\u2026\u53cd\u6b63\u4ed6\u4e5f\u4e0d\u77e5\u9053\u6211\u5df2\u53d8\u5fd7\uff0c\u5982\u679c\u6211\u53bb\u63a2\u6d4b \u4e00\u4e0b\uff0c\u5fc5\u5b9a\u53ef\u4ee5\u5c06\u4ed6\u4eec\u7684\u865a\u5b9e\u60c5\u51b5\u63a2\u6d4b\u51fa\u6765\u3002\u201d \u3000\u3000\u88f4\u73cf\u8f7b\u53f9\u4e00\u58f0\uff0c\u6447\u5934\u9053\uff1a\u201c\u90a3\u5144\uff0c\u6b3a\u4eba\u4e4b\u4e8b\uff0c\u5fc5\u4e0d\u53ef\u4e45\uff0c\u6211\u4eec\u65e2\u4e0d\u613f\u4eba\u5bb6\u4ee5\u597d\u8ba1\u6b3a\u9a97\u6211 \u4eec\uff0c\u6211\u4eec\u53c8\u4f55\u82e6\u4ee5\u597d\u8ba1\u53bb\u611a\u5f04\u522b\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u89c9\u6b64\u8bdd\u4e49\u6b63\u8bcd\u4e25\uff0c\u5b9e\u662f\u4e0d\u53ef\u53cd\u9a73\u3002\u201d\u51b7\u8c37\u53cc\u6728\u201c\u7aef\u5750\u5728\u7a97\u7684 \u897f\u4fa7\uff0c\u51b7\u5bd2\u7af9\u5ffd\u7136\u9053\uff1a\u201c\u6d88\u606f\u6765\u4e86\uff01\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u4e00\u4e2a\u52b2\u88c5\u75be\u670d\u7684\u6c49\u5b50\uff0c\u5306\u5306\u5954\u5165\uff0c\u9762\u4e0a\u7684\u795e\u8272\uff0c\u50cf\u662f\u7a81\u7136\u5bfb\u7740\u4e86\u5b9d\u85cf\u4f3c \u7684\u2026\u2026 \u3000\u3000\u90a3\u98de\u8679\u4e00\u58f0\u53f1\u95f4\uff0c\u4ed6\u4fbf\u6025\u6025\u9053\uff1a\u201c\u57ce\u91cc\u9762\u73b0\u5728\u5df2\u7ecf\u66f4\u4e71\u4e86\uff0c\u6d41\u8a00\u7eb7\u98de\uff0c\u6ee1\u57ce\u98ce\u96e8\uff0c\u4ece \u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u624b\u4e0b\u4f20\u51fa\u6765\u7684\u6d88\u606f\uff0c\u2018\u98de\u9f99\u4e09\u6770\u2019\u786e\u5df2\u6bd9\u547d\u3002\u201d \u3000\u3000\u90a3\u98de\u8679\u6de1\u6de1\u5e94\u4e86\u4e00\u58f0\uff0c\u53ea\u542c\u4ed6\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u6700\u8981\u7d27\u7684\u662f\uff0c\u5728\u6628\u5929\u591c\u665a\uff0c\u2018\u4e1c\u65b9\u4e94\u5251\u2019\u4e2d \u7684\u4e1c\u65b9\u9707\uff0c\u4ee5\u53ca\u90a3\u2018\u9f99\u5973\u2019\u6a80\u6587\u742a\u7adf\u4e00\u8d77\u5931\u8e2a\u4e86\uff0c\u6240\u6709\u7684\u4eba\u904d\u5bfb\u4e0d\u83b7\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u5230\u4e86\u54ea \u91cc\uff1f\u76f4\u5230\u6b64\u523b\uff0c\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u8fd8\u5728\u7126\u6025\u4e4b\u4e2d\uff0c\u662f\u4ee5\u59cb\u7ec8\u6ca1\u6709\u52a8\u9759\u3002\u201d \u3000\u3000\u8881\u6cf8\u73cd\u60ca\u53f9\u4e00\u58f0\uff01 \u3000\u3000\u88f4\u73cf\u9762\u8272\u5927\u53d8\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u5446\u5446\u5730\u6123\u5728\u5f53\u5730\uff0c\u4e0d\u77e5\u662f\u60ca\u662f\u559c\u3002 \u3000\u3000\u5c31\u8fde\u201c\u51b7\u8c37\u53cc\u6728\u201d\u90fd\u88ab\u8fd9\u60ca\u4eba\u7684\u6d88\u606f\u9707\u5f97\u957f\u8eab\u800c\u7acb\u3002 \u3000\u3000\u90a3\u98de\u8679\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u662f\u5426\u53ef\u9760\uff1f\u201d \u3000\u3000\u52b2\u88c5\u6c49\u5b50\u5598\u606f\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u54ea\u77e5\u8fd9\u4e00\u9635\u60ca\u5f02\u8fd8\u672a\u8fc7\u53bb\uff0c\u4f17\u4eba\u8fd8\u6728\u7acb\u5f53\u5730\uff0c\u9662\u5916\u7a81\u5730\u53c8\u6709 \u4e00\u4eba\u98de\u5954\u800c\u5165\uff0c\u563f\u58f0\u9053\uff1a\u201c\u95e8\u5916\u7a81\u6709\u4e2a\u2018\u98de\u9f99\u9556\u5c40\uff0c\u4e2d\u7684\u8d9f\u5b50\u624b\u6765\u6c42\u89c1\u2019\u88f4\u5927\u5148\u751f\u2018\u3002\u6b64\u4eba \u6b66\u529f\u751a\u9ad8\uff0c\u8d75\u5e73\u98de\u3001\u738b\u5f97\u5fd7\u60f3\u4e0a\u53bb\u5c06\u4ed6\u64d2\u6765\u53e9\u89c1\u76df\u4e3b\uff0c\u54ea\u77e5\u4ed6\u8f7b\u8f7b\u4e00\u7f8a\u624b\uff0c\u5c31\u5c06\u8d75\u5e73\u98de\u3001\u738b \u5f97\u5fd7\u51fb\u5012\u5728\u5730\uff01\u201d\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u9762\u8272\u66f4\u662f\u94c1\u9752\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u770b\u6e05\u4e86\u6b64\u4eba\u662f\u4ec0\u4e48\u6a21 \u6837\uff1f\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u5fae\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u6b64\u4eba\u9762\u8272\u4e00\u7247\u8721\u9ec4\uff0c\u770b\u6765\u4eff\u4f5b\u6709\u91cd\u75c5\u5728\u8eab\uff0c\u7a7f\u7684\u662f\u2018\u98de\u9f99\u9556 \u5c40\u2019\u8d9f\u5b50\u624b\u7684\u8863\u886b\uff0c\u5934\u4e0a\u6234\u7740\u4e00\u9876\u8303\u9633\u6be1\u7b20\uff0c\u7d27\u7d27\u538b\u5728\u7709\u6bdb\u4e0a\uff0c\u522b\u4eba\u5f88\u96be\u770b\u5230\u4ed6\u7684\u76ee\u5149\uff0c\u811a \u4e0a\u7a7f\u7684\u4ec0\u4e48\u978b\u5b50\uff0c\u5c0f\u7684\u5374\u6ca1\u6709\u770b\u6e05\uff01\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u51b7\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u8eab\u4e0a\u53ef\u5e26\u6709\u5175\u5203\uff1f\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u5782\u9996\u9053\uff1a\u201c\u4ed6\u8eab\u6750\u4e0e\u6211\u8fd9\u822c\u6a21\u6837\uff0c\u8eab\u4e0a\u6ca1\u6709\u5175\u5203\uff0c\u4f46\u8170\u95f4\u5374\u4f3c\u6697\u85cf\u7740\u4e00\u6761\u7ec3\u5b50 \u67aa\uff0c\u4e03\u661f\u97ad\u4e4b\u7c7b\u7684\u8f6f\u5175\u5668\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u53cc\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u98de\u9f99\u9556\u5c40\u4e2d\uff0c\u54ea\u91cc\u6709\u8fd9\u6837\u7684\u89d2\u8272\uff1f\u76df\u4e3b\uff0c\u5c0f\u5f1f\u5148\u53bb\u770b \u770b\u3002\u201d \u3000\u3000\u88f4\u73cf\u9762\u6c89\u5982\u6c34\uff0c\u622a\u53e3\u9053\uff1a\u201c\u6b64\u4eba\u5bfb\u7684\u65e2\u7136\u662f\u6211\uff0c\u81ea\u7136\u662f\u6211\u51fa\u53bb\uff0c\u201d\u8bed\u58f0\u672a\u4e86\uff0c\u4ed6\u5df2\u8d70\u51fa \u95e8\u5916\uff0c\u6781\u5feb\u5730\u7a7f\u8fc7\u5ead\u9662\uff0c\u7a7f\u8fc7\u5927\u5385\uff0c\u53ea\u89c1\u655e\u5f00\u7684\u5927\u95e8\u5916\uff0c\u4e00\u7247\u5608\u6742\uff0c\u5341\u6570\u6761\u6c49\u5b50\uff0c\u62e5\u6324\u5728\u95e8 \u524d\uff0c\u6321\u4f4f\u4e86\u90a3\u4eba\u7684\u8eab\u5f71\uff0c\u88f4\u73cf\u53cc\u624b\u4e00\u5206\uff0c\u5927\u6b65\u800c\u51fa\uff0c\u53ea\u89c1\u4e00\u6761\u6c49\u5b50\uff0c\u679c\u5982\u65b9\u624d\u5f62\u5bb9\u7684\u6a21\u6837\uff0c \u5782\u624b\u7acb\u5728\u9636\u524d\uff0c\u4e00\u526f\u6f2b\u4e0d\u7ecf\u5fc3\u7684\u795e\u6001\uff0c\u4f3c\u4e4e\u6839\u672c\u5c31\u672a\u5c06\u9762\u524d\u8fd9\u5341\u4f59\u6761\u6c49\u5b50\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u88f4\u73cf\u5251\u7709\u5fae\u626c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u670b\u53cb\u662f\u8c01\uff1f\u5bfb\u88f4\u73cf\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u4ecd\u7136\u4f4e\u5782\u7740\u5934\uff0c\u4e5f\u4e0d\u671b\u88f4\u73cf\u4e00\u773c\u3002 \u3000\u3000\u88f4\u73cf\u76b1\u7709\u9053\uff1a\u201c\u5728\u4e0b\u7684\u8bdd\uff0c\u4f60\u96be\u9053\u6ca1\u6709\u542c\u89c1\u4e48\uff1f\u201d \u3000\u3000\u53ea\u542c\u8fd9\u6c49\u5b50\u5e72\u54b3\u4e00\u58f0\uff0c\u5636\u54d1\u7740\u58f0\u97f3\u9053\uff1a\u201c\u6a80\u603b\u9556\u5934\u6709\u4ee4\uff0c\u8981\u6211\u6765\u529d\u4f60\u5f52\u964d\u2018\u98de\u9f99\u9556 \u5c40\u2019\uff0c\u5426\u5219\u2026\u2026\u54fc\u54fc\uff01\u201d \u3000\u3000\u88f4\u73cf\u9762\u8272\u4e00\u6c89\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u56de\u53bb\u2014\u2014\u201d\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u89c1\u8fd9\u6c49\u5b50\u7adf\u4ef0\u5929\u5927\u7b11\u8d77\u6765\uff0c\u4e3e\u624b \u4e00\u6325\uff0c\u6325\u53bb\u4e86\u5934\u4e0a\u7684\u8303\u9633\u6be1\u7b20\uff0c\u9732\u51fa\u4e00\u53cc\u96ea\u4eae\u7684\u773c\u775b\u3002 \u3000\u3000\u88f4\u73cf\u51dd\u76ee\u671b\u53bb\uff0c\u7a81\u5730\u5927\u558a\u4e00\u58f0\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u4e00\u6b65\u63a0\u4e86\u8fc7\u53bb\uff0c\u7d27\u7d27\u62b1\u4f4f\u8fd9\u4e2a\u4eba\u7684\u80a9\u5934\uff0c\u7adf\u5728\u8fd9\u7ed3\u51b0\u7684\u96ea\u5730\u4e0a\uff0c\u7eb7\u98de\u7684\u5927\u96ea\u4e2d\uff0c\u72c2\u7b11\u96c0 \u8dc3\u8d77\u6765\u3002 \u3000\u3000\u65b9\u81ea\u51fa\u95e8\u7684\u201c\u51b7\u8c37\u53cc\u6728\u201d\u3001\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u4ee5\u53ca\u8881\u6cf8\u73cd\u89c1\u5230\u8fd9\u822c\u60c5\u51b5\uff0c\u90fd\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6123\uff0c \u5927\u7b11\u58f0\u4e2d\uff0c\u53ea\u542c\u88f4\u73cf\u9053\uff1a\u201c\u8fd9\u4e9b\u65e5\u5b50\u4f60\u53bb\u4e86\u54ea\u91cc\uff0c\u600e\u5730\u4e5f\u4e0d\u901a\u4e00\u4e0b\u4fe1\u606f\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5927\u7b11\u9053\uff1a\u201c\u6211\u5f53\u771f\u884c\u52a8\u795e\u79d8\u5f97\u5f88\uff0c\u600e\u80fd\u8d70\u6f0f\u6d88\u606f\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u5927\u7b11\uff0c\u4e00\u9762\u6276\u7740\u88f4\u73cf\u7684\u81c2\u8180\uff0c\u8d70\u4e0a\u77f3\u9636\u3002 \u3000\u3000\u8881\u6cf8\u73cd\u79cb\u6ce2\u8f6c\u5904\uff0c\u8f7b\u547c\u9053\uff1a\u201c\u674e\u8000\u6c11\uff0c\u4f60\u600e\u4e48\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4e00\u6014\uff0c\u505c\u6b65\u9053\uff1a\u201c\u674e\u8000\u6c11\uff0c\u8c01\u662f\u674e\u8000\u6c11\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u76ee\u5149\u51dd\u6ce8\uff0c\u53ea\u89c9\u5f97\u6b64\u4eba\u7684\u4e00\u53cc\u773c\u775b\u597d\u751f\u719f\u6089\uff0c\u6c89\u541f\u8bb8\u4e45\uff0c\u7ec8\u4e8e\u60f3\u8d77\uff0c\u8131\u53e3 \u9053\uff1a\u201c\u4e03\u5de7\u7ae5\u5b50\uff0c\u600e\u5730\u6765\u4e86\uff1f\u201d \u3000\u3000\u8881\u6cf8\u73cd\u5927\u5947\u9053\uff1a\u201c\u8c01\u662f\u4e03\u5de7\u7ae5\u5b50\uff1f\u4ed6\u660e\u660e\u662f\u2018\u98de\u9f99\u9556\u5c40\u2019\u91cc\u7684\u8d9f\u5b50\u624b\u674e\u8000\u6c11\uff0c\u4f60\u4eec\u5207\u83ab \u8981\u4e0a\u4e86\u4ed6\u7684\u5f53\uff01\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u60f3\u5fc5\u4f60\u8fd9\u4e9b\u65e5\u5b50\u91cc\u53c8\u5f04\u4e86\u4e9b\u4ec0\u4e48\u82b1\u6837\uff1f\u4f46\u2018\u4e03\u5de7\u7ae5\u5b50\u2019\u5434 \u9e23\u4e16\u600e\u5730\u4f1a\u53d8\u6210\u674e\u8000\u6c11\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\uff0c\u5434\u9e23\u4e16\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u674e\u8000\u6c11\u8005\u4f60\u8981\u547d\uff0c\u8981\u4f60\u547d\uff0c\u8981\u6a80\u660e\u7684\u547d\u4e4b\u610f\u4e86\uff01\u54c8 \u54c8\u2014\u2014\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u5feb\u4e9b\u6446\u9152\uff0c\u5f85\u6211\u8be6\u8c08\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5927\u7b11\u7740\u643a\u624b\u8d70\u5165\u540e\u9662\uff0c\u8fd9\u4e00\u53cc\u60a3\u96be\u76f8\u5171\u7684\u751f\u6b7b\u670b\u53cb\uff0c\u867d\u7136\u8bb8\u4e45\u672a\u89c1\uff0c\u4f46\u60c5\u611f\u4e0a\u5374\u6beb \u672a\u751f\u758f\u3002 \u3000\u3000\u53ea\u662f\u4ed6\u4eec\u89c9\u5f97\u5f7c\u6b64\u95f4\u90fd\u6709\u4e9b\u53d8\u4e86\u3002 \u3000\u3000\u4eba\u5385\u4e4b\u540e\uff0c\u90a3\u98de\u8679\u7acb\u523b\u62db\u547c\u6446\u9152\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u7b11\u9053\uff1a\u201c\u606d\u559c\u90a3\u5144\uff0c\u7ec8\u4e8e\u5927\u5f7b\u5927 \u609f\uff0c\u7a0d\u540e\u5c0f\u5f1f\u8981\u656c\u90a3\u5144\u4e09\u676f\u3002\u201d \u3000\u3000\u88f4\u73cf\u3001\u90a3\u98de\u8679\u9f50\u5730\u4e00\u201c\u6123\uff0c\u8131\u53e3\u9053\uff1a\u201c\u4f60\u600e\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u542b\u7b11\u9053\uff1a\u201c\u65b9\u624d\u88ab\u6211\u6253\u5012\u5728\u5730\u4e0a\u7684\u8d75\u5e73\u98de\u4e0e\u738b\u5f97\u5fd7\uff0c\u4fbf\u662f\u6211\u4e00\u624b\u5b89\u6392\u5728\u90a3\u5144\u624b\u5382 \u7684\u5185\u5e94\uff0c\u56e0\u4e3a\u90a3\u5144\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u5c0f\u5f1f\u90fd\u5173\u5207\u5f97\u5f88\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u6014\u4e86\u4e00\u6014\uff0c\u638c\u5fc3\u4e0d\u89c9\u53c8\u6697\u4e2d\u6c81\u51fa\u51b7\u6c57\uff0c\u4ed6\u672c\u6765\u4ee5\u4e3a\u81ea\u5df1\u5fc3\u667a\u53ef\u79f0\u4e00\u65f6\u4e4b \u9009\uff0c\u6b64\u523b\u5fc3\u4e2d\u4e0d\u7981\u60ca\u6050\u3001\u60ed\u6127\u4ea4\u96c6\u3002 \u3000\u3000\u9152\u65b9\u6446\u8d77\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u4fbf\u5f00\u59cb\u53d9\u8ff0\u4ed6\u8fd9\u4e9b\u65e5\u5b50\u6765\u66f2\u6298\u79bb\u5947\u7684\u6545\u4e8b\uff0c\u4ed6\u6700\u5148\u8bf4\uff1a \u201c\u6211\u6700\u521d\u542c\u88f4\u5144\u8bb2\u8d77\u4ed6\u7684\u8eab\u4e16\uff0c\u4fbf\u77e5\u9053\u2018\u9f99\u5f62\u516b\u638c\uff0c\u5fc5\u662f\u5bf9\u4ed6\u85cf\u7740\u6781\u5927\u7684\u9634\u8c0b\uff0c\u8c01\u8981\u8bf4\u4ed6\u8fd9 \u6837\u7684\u4eba\u5929\u8d44\u611a\u9c81\uff0c\u90a3\u4e9b\u4eba\u4e0d\u662f\u75af\u5b50\u4fbf\u662f\u767d\u75f4\uff0c\u6a80\u660e\u65e2\u975e\u75af\u4e8e\u4ea6\u975e\u767d\u75f4\uff0c\u81ea\u7136\u662f\u53e6\u6709\u5c45\u5fc3\u3002\u201d \u662f\u4ee5\u6211\u4e00\u5f00\u59cb\u4fbf\u7528\u6613\u5bb9\u836f\u6539\u6362\u4e86\u5bb9\u8c8c\uff0c\u6295\u5165\u2019\u98de\u9f99\u9556\u5c40\u2018\u60f3\u5728\u6697\u4e2d\u7559\u610f\u6a80\u660e\u7684\u7834\u7efd\uff0c\u540e\u6765\u6211 \u53c8\u5728\u65e0\u610f\u4e2d\u5bfb\u7740\u4e86\u90a3\u8f66\u592b\u2019\u8fc7\u4e0d\u53bb\u2018\uff0c\u542c\u5230\u4e86\u4ed6\u68a6\u4e2d\u7684\u5453\u8bed\uff0c\u6211\u5c31\u4ee5\u5404\u79cd\u65b9\u6cd5\uff0c\u903c\u5f97\u4ed6\u5fc3\u7518 \u60c5\u613f\u4ed6\u8bf4\u51fa\u8fd9\u4ef6\u9690\u79d8\uff01\u201c\u4ed6\u8bf4\u5f97\u751a\u662f\u7b80\u5355\u5306\u5fd9\uff0c\u4eff\u4f5b\u8fd8\u6709\u4ec0\u4e48\u5927\u4e8b\u8981\u7b49\u7740\u4ed6\u53bb\u505a\u4f3c\u7684\u3002\u4f46\u8fd9 \u7b80\u5355\u800c\u5306\u5fd9\u7684\u8a00\u8bed\uff0c\u5374\u5df2\u8db3\u591f\u4f7f\u5f97\u4f17\u4eba\u4e3a\u4e4b\u611f\u53f9\u60ca\u5947\u3002\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6211\u66fe\u542c\u88f4\u5144 \u8bf4\u8d77\u8fc7\u8fd9\u4f4d\u8881\u59d1\u5a18\uff0c\u662f\u4ee5\u6211\u5e38\u5728\u6697\u4e2d\u7559\u610f\u7740\u5979\uff0c\u501f\u6545\u548c\u5979\u8bf4\u4e24\u5411\u8bdd\uff0c\u53c8\u5728\u6709\u610f\u65e0\u610f\u95f4\uff0c\u544a\u8bc9 \u4e86\u5979\u8bb8\u591a\u4e8b\uff01\u201d \u3000\u3000\u8881\u6cf8\u73cd\u53cc\u76ee\u5706\u7741\uff0c\u8f7b\u5524\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5440\uff01\u96be\u602a\u2014\u2014\u771f\u60f3\u4e0d\u5230\uff0c\u4f60\u2026\u2026\u4f60\u771f\u7684\u662f\u806a\u660e\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u7b11\u4e00\u4e0b\uff0c\u5411\u88f4\u73cf\u9053\uff1a\u201c\u90a3\u6b21\u8fd9\u4f4d\u90a3\u5144\u5230\u2018\u98de\u9f99\u9556\u5c40\u2019\u65f6\u5c31\u662f\u6211\u5f15\u7740\u8881\u59d1\u5a18\u5728\u6709 \u610f\u65e0\u610f\u95f4\u89c1\u4ed6\u4e00\u9762\uff0c\u540e\u6765\u6211\u53c8\u5c06\u6a80\u660e\u548c\u5341\u4f59\u5e74\u524d\u90a3\u4e00\u6bb5\u65e0\u5934\u516c\u6848\u7684\u5173\u7cfb\uff0c\u544a\u8bc9\u4e86\u8881\u59d1\u5a18\uff0c\u7136 \u540e\u518d\u5f15\u53d1\u5979\u51fa\u6765\u627e\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u88f4\u73cf\u4f38\u624b\u4e00\u62cd\u524d\u989d\uff0c\u611f\u53f9\u9053\uff1a\u201c\u6211\u90a3\u65f6\u4fbf\u89c9\u5947\u602a\uff0c\u5979\u4e00\u4e2a\u5c0f\u5973\u5b69\u5b50\uff0c\u600e\u4f1a\u63a2\u51fa\u90a3\u4e48\u591a\u79d8 \u5bc6\uff1f\u539f\u6765\u2026\u2026\u5509\uff0c\u4e03\u5de7\u7ae5\u5b50\uff0c\u4f60\u771f\u8be5\u6539\u540d\u53eb\u505a\u5341\u5de7\u7ae5\u5b50\u624d\u662f\u3002\u201d \u3000\u3000\u8881\u6cf8\u73cd\u7741\u7740\u5979\u90a3\u4e00\u53cc\u660e\u4eae\u7684\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u9003\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5dee\u70b9\u88ab\u4ed6\u4eec\u6293\u56de\u53bb\uff0c\u662f\u4e0d\u662f \u53c8\u662f\u4f60\u5728\u6697\u4e2d\u5e2e\u6211\u5c06\u4ed6\u4eec\u5f15\u5f00\u7684\uff1f\u201d \u3000\u3000\u5434\u9e23\u4e16\u5fae\u7b11\u9894\u9996\u9053\uff1a\u201c\u90a3\u6b21\u6211\u4e5f\u5341\u5206\u5371\u9669\uff0c\u5dee\u70b9\u88ab\u4ed6\u4eec\u53d1\u89c9\u771f\u76f8\uff0c\u5e78\u597d\u90a3\u822c\u4eba\u90fd\u662f\u8822 \u9a74\uff01\u201d \u3000\u3000\u201c\u2018\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u957f\u53f9\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u5e76\u4e0d\u592a\u8822\uff0c\u53ea\u662f\u5434\u5144\u4f60\u2026\u2026\u5509\uff0c\u5f53\u771f\u6709\u7ecf\u4e16\u4e4b \u624d\uff0c\u8fc7\u4eba\u4e4b\u667a\u3002\u201d \u3000\u3000\u5434\u9e23\u4e16\u9053\uff1a\u201c\u90a3\u5144\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u7a81\u7136\u6cdb\u8d77\u4e00\u9635\u5f97\u610f\u7684\u5149\u8f89\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u5012\u8fd8\u4e0d\u8db3\u4e3a\u5947\uff0c\u6b64\u523b\u5728\u6c49\u53e3\u57ce\u91cc\uff0c \u5c0f\u5f1f\u5012\u786e\u5b9e\u5199\u4e0b\u4e86\u4e00\u4e9b\u5f97\u610f\u4e4b\u7b14\uff0c\u65e5\u843d\u4ee5\u524d\uff0c\u6211\u4eec\u5fc5\u5b9a\u8981\u8d76\u5230\u6c49\u53e3\u57ce\u53bb\uff0c\u5230\u90a3\u65f6\u2026\u2026\u54c8 \u54c8\u3002\u201d\u4ed6\u5f97\u610f\u5730\u5927\u7b11\u6570\u58f0\uff0c\u4e3e\u8d77\u9762\u524d\u9152\u676f\uff0c\u4ef0\u9996\u4e00\u5e72\u800c\u5c3d\u3002 \u3000\u3000\u8881\u6cf8\u73cd\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u6211\u5df2\u4e0d\u77e5\u9053\u4f60\u662f\u5982\u4f55\u505a\u51fa\u6765\u7684\u4e86\uff1f\u4f60\u5374\u8bf4\u90fd\u4e0d\u8db3\u4e3a\u5947\uff0c\u5927\u54e5 \u54e5\uff0c\u6211\u771f\u60f3\u4e0d\u5230\u4f60\u6709\u5982\u6b64\u806a\u660e\u7684\u670b\u53cb\uff0c\u770b\u6765\u6bd4\u4f60\u8fd8\u8981\u806a\u660e\u5f97\u591a\u3002\u201d \u3000\u3000\u88f4\u73cf\u542b\u7b11\u9053\uff1a\u201c\u4ed6\u4e00\u76f4\u5c31\u6bd4\u6211\u806a\u660e\u5f97\u591a\u3002\u201d \u3000\u3000\u5982\u6b64\u4fd7\u6c14\u800c\u5bb9\u5957\u7684\u79f0\u8d5e\u4e4b\u8a00\uff0c\u5728\u4ed6\u53e3\u4e2d\u8bf4\u51fa\uff0c\u5374\u662f\u90a3\u6837\u5730\u771f\u5fc3\u800c\u8bda\u6073\uff0c\u5434\u9e23\u4e16\u6447\u5934\u9053\uff1a \u201c\u9519\u4e86\u9519\u4e86\uff0c\u6211\u518d\u806a\u660e\uff0c\u4e5f\u4e0d\u8fc7\u662f\u7eff\u53f6\u800c\u5df2\uff0c\u53ea\u80fd\u4e3a\u8f85\uff0c\u4e0d\u80fd\u4e3a\u4e3b\u3002\u201d \u3000\u3000\u4ed6\u7b11\u5bb9\u4e00\u655b\uff0c\u5ffd\u5730\u6b63\u8272\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u8981\u77e5\u9053\uff0c\u771f\u6b63\u7684\u7261\u4e39\u662f\u4f60\uff0c\u5f53\u4eca\u6c5f\u6e56\u4e2d\u5927\u4e71\u5df2 \u8d77\uff0c\u6536\u62fe\u6b8b\u5c40\u7684\uff0c\u4e5f\u5fc5\u5c06\u662f\u4f60\uff0c\u4e0a\u5929\u751f\u4f60\uff0c\u4e43\u4e3a\u2018\u516c\u2019\uff0c\u4f60\u5207\u5207\u4e0d\u53ef\u4e3a\u4e86\u4e00\u4e9b\u513f\u5973\u60c5\u4ec7\uff0c\u6d88 \u78e8\u4e86\u81ea\u5df1\u7684\u5fd7\u6c14\uff0c\u6211\u65b9\u624d\u770b\u4f60\u610f\u5fd7\u6d88\u6c89\uff0c\u5fc3\u91cc\u5b9e\u5728\u96be\u53d7\u5f97\u5f88\uff0c\u4f60\u8981\u77e5\u9053\u6b64\u523b\u6b66\u6797\u4e2d\u5343\u5343\u4e07\u4e07 \u7684\u773c\u775b\uff0c\u4ff1\u90fd\u6ce8\u76ee\u5728\u4f60\u8eab\u4e0a\uff0c\u5343\u5343\u4e07\u4e07\u4e2a\u5e0c\u671b\uff0c\u4e5f\u90fd\u5bc4\u6258\u5728\u4f60\u8eab\u4e0a\uff0c\u4f60\u82e5\u662f\u81ea\u66b4\u81ea\u5f03\uff0c\u5c82\u975e \u53eb\u5929\u4e0b\u6b66\u6797\u670b\u53cb\u4f24\u5fc3\uff01\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u9707\uff0c\u5b9b\u5982\u4e00\u6876\u6e05\u6c34\uff0c\u704c\u9876\u800c\u4e0b\uff0c\u5fc3\u5934\u987f\u89c9\u4e00\u7247\u6e05\u660e\uff0c\u5239\u90a3\u95f4\u4fbf\u5c06\u6240\u6709\u7684 \u201c\u79c1\u201d\u60c5\u3001\u201c\u79c1\u3002\u6028\u4e00\u8d77\u629b\u5f00\uff0c\u5fc3\u4e2d\u6697\u9a82\u81ea\u5df1\uff1a\u201c\u88f4\u73cf\u5440\u88f4\u73cf\uff0c\u4f60\u5f53\u771f\u8be5\u6b7b\uff0c\u5929\u4e0b\u6b66\u6797\u670b\u53cb \u7684\u524d\u9014\u6c14\u8fd0\uff0c\u96be\u9053\u4e0d\u6bd4\u4f60\u79c1\u4eba\u7684\u4e00\u4e9b\u60c5\u4ec7\u601d\u6028\u91cd\u8981\u5f97\u591a\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fc3\u5934\u65e2\u662f\u60f6\u6050\uff0c\u53c8\u662f\u611f\u6fc0\uff0c\u5fcd\u4e0d\u4f4f\u957f\u8eab\u800c\u8d77\uff0c\u5411\u5434\u9e23\u4e16\u5f53\u5934\u4e00\u63d6\uff0c\u5374\u4e0d\u77e5 \u8be5\u8bf4\u4e9b\u4ec0\u4e48\uff01 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u5bf9\u671b\u4e00\u773c\uff0c\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u597d\u670b\u53cb\uff01\u201d \u3000\u3000\u51b7\u67af\u6728\u53f9\u9053\uff1a\u201c\u5f53\u771f\u662f\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u957f\u53f9\u9053\uff1a\u201c\u8c01\u82e5\u662f\u4ea4\u4e86\u4f60\u4eec\u4e24\u4eba\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u6b64\u4eba\u5f53\u771f\u8d70\u8fd0\u5f97 \u5f88\u3002\u201d \u3000\u3000\u8fc7\u4e86\u5348\u65f6\uff0c\u5bc6\u5e03\u9634\u973e\u7684\u82cd\u7a79\uff0c\u7a81\u7136\u5c04\u51fa\u4e00\u7247\u9633\u5149\uff0c\u7b14\u76f4\u5730\u5c04\u5728\u6c49\u53e3\u57ce\u91cc\u7684\u957f\u8857\u4e0a\u3002 \u3000\u3000\u957f\u8857\u4e0a\u7684\u4eba\u7fa4\uff0c\u6b64\u523b\u51e0\u4e4e\u5df2\u6cb8\u817e\u4e86\u8d77\u6765\uff0c\u9664\u4e86\u9152\u5bb6\u8336\u6808\uff0c\u6240\u6709\u7684\u5e97\u94fa\u4ff1\u5df2\u6b47\u4e1a\uff0c\u6c49\u53e3\u57ce \u5185\u6240\u6709\u7684\u670b\u53cb\u7ea6\u4f1a\u3001\u559c\u5e86\u4e27\u4e8b\u3001\u751f\u610f\u6765\u5f80\u3001\u94f6\u94b1\u4ea4\u6613\u2026\u2026\u6b64\u523b\u4e5f\u90fd\u65e9\u5df2\u5b8c\u5168\u505c\u987f\u3002 \u3000\u3000\u4e0a\u63d2\u201c\u98de\u9f99\u9556\u5c40\u201d\u65d7\u5b50\u7684\u9556\u8f66\uff0c\u4ecd\u505c\u7559\u5728\u6c5f\u8fb9\uff0c\u4f46\u9556\u8f66\u65c1\u7684\u9556\u4f19\u4eec\uff0c\u795e\u8272\u5374\u5df2\u90fd\u6709\u4e86\u4e9b \u6cae\u4e27\u3002 \u3000\u3000\u6240\u6709\u7684\u6d41\u8a00\u4e0e\u8033\u8bed\uff0c\u90fd\u662f\u5bf9\u201c\u9f99\u5f62\u516b\u638c\u201d\u5982\u6b64\u4e0d\u5229\uff0c\u8fd9\u5f53\u771f\u4f7f\u6b66\u6797\u4e2d\u4eba\u5927\u4e3a\u60ca\u8bb6\uff0c\u672c\u81ea \u5360\u5c3d\u4f18\u52bf\u7684\u201c\u98de\u9f99\u9556\u5c40\u201d\uff0c\u60c5\u52bf\u600e\u4f1a\u53d8\u5f97\u5982\u6b64\u6076\u52a3\uff1f \u3000\u3000\u957f\u8857\u4e0a\u4eba\u8bed\u55a7\u817e\u7740\uff0c\u672c\u6765\u6709\u7740\u987e\u5fcc\u7684\u4eba\uff0c\u6b64\u523b\u7adf\u90fd\u653e\u58f0\u800c\u6765\uff0c\u6574\u4e2a\u7684\u6c49\u53e3\u57ce\uff0c\u6b64\u523b\u5c31\u50cf \u4e00\u9505\u716e\u6cb8\u7684\u5f00\u6c34\u4e00\u6837\u3002 \u3000\u3000\u90a3\u4e00\u6247\u9ed1\u6f06\u7684\u5927\u95e8\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u8fd8\u672a\u542f\u5f00\uff0c\u4e8e\u662f\u805a\u96c6\u5728\u95e8\u53e3\u7684\u4eba\uff0c\u4fbf\u8d8a\u6765\u8d8a\u591a\uff0c\u50cf\u662f\u4e00 \u7fa4\u7b49\u7740\u770b\u8d5b\u795e\u4f1a\u5f00\u9523\u7684\u89c2\u4f17\u4e00\u6837\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u771f\u7684\u6709\u4e00\u9635\u9523\u58f0\u54cd\u8d77\uff01 \u3000\u3000\u5343\u767e\u9053\u76ee\u5149\u4e00\u8d77\u8f6c\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u767e\u5341\u6761\u9ed1\u8863\u5927\u6c49\uff0c\u7ed3\u961f\u800c\u6765\uff0c\u5f53\u5934\u56db\u4eba\uff0c\u624b\u6572\u94dc\u9523\uff0c\u540e \u9762\u6570\u5341\u4eba\uff0c\u624b\u6301\u96ea\u4eae\u957f\u5200\uff0c\u518d\u540e\u6570\u5341\u4eba\uff0c\u624b\u7279\u957f\u5f13\uff0c\u540e\u80cc\u957f\u5251\uff0c\u62e5\u7740\u4e00\u4e2a\u9ebb\u8863\u5b5d\u670d\uff0c\u6ee1\u9762\u60b2 \u5bb9\u7684\u5c11\u5e74\uff0c\u8d70\u5165\u957f\u8857\u3002 \u3000\u3000\u4f17\u4eba\u60ca\u5947\u4ea4\u96c6\uff0c\u53ea\u89c1\u8fd9\u4e9b\u9ed1\u8863\u5927\u6c49\u5c06\u8fd9\u5c11\u5e74\u62e5\u4e0a\u4e86\u5c4b\u6a90\u4e0b\u7684\u4e00\u5f20\u65b9\u684c\uff0c\u7136\u540e\u94a2\u5200\u624b\u56f4\u5728 \u56db\u4fa7\uff0c\u5f13\u7bad\u624b\u53c8\u56f4\u5728\u94a2\u5200\u624b\u4e4b\u5916\u3002 \u3000\u3000\u94dc\u9523\u518d\u6b21\u54cd\u8d77\uff0c\u90a3\u9ebb\u8863\u5b5d\u670d\u7684\u5c11\u5e74\u4fbf\u5e26\u7740\u773c\u6cea\u4e0e\u6124\u6012\uff0c\u53d9\u8bf4\u8d77\u81ea\u5df1\u60b2\u60e8\u7684\u906d\u9047\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u5c31\u662f\u5341\u4f59\u5e74\u524d\u4e27\u8eab\u5728\u90a3\u4ef6\u60e8\u6848\u4e2d\u9556\u5e08\u7684\u540e\u4ee3\uff0c\u4ed6\u6c89\u75db\u5730\u53d9\u8bf4\u7740\u81ea\u5df1\u8eab\u4e16\u7684\u60b2\u54c0\u3002 \u3000\u3000\u8fd9\u6c89\u75db\u7684\u60b2\u54c0\uff0c\u7acb\u523b\u4fbf\u535a\u53d6\u4e86\u5343\u767e\u4eba\u7684\u540c\u60c5\u4e0e\u6124\u6012\u3002 \u3000\u3000\u8bf4\u5230\u6700\u540e\uff0c\u8fd9\u9ebb\u8863\u5b5d\u670d\u7684\u5c11\u5e74\u5ffd\u7136\u8dea\u5230\u5730\u4e0a\uff0c\u58f0\u5636\u529b\u7aed\u5730\u5927\u558a\u9053\uff1a\u201c\u5c0f\u5b50\u5e7c\u906d\u5b64\u82e6\uff0c\u8eab \u62ab\u5947\u51a4\uff0c\u53c8\u88ab\u90a3\u6076\u8d3c\u597d\u8c0b\u6240\u5bb3\uff0c\u4ee5\u81f3\u76f4\u5230\u4eca\u65e5\u5c1a\u662f\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u5c0f\u5b50\u7684\u8840\u6d77\u6df1\u4ec7\uff0c\u53ea\u6709\u4ef0 \u4ed7\u5404\u4f4d\u7236\u8001\u3001\u53d4\u6015\u3001\u5144\u5f1f\u4eec\u4e3a\u5c0f\u5b50\u4e3b\u6301\u6b63\u4e49\uff0c\u4e3a\u6b66\u6797\u4e3b\u6301\u516c\u9053\uff01\u201d \u3000\u3000\u4f17\u4eba\u7acb\u523b\u5927\u54d7\uff0c\u4e5f\u4e0d\u77e5\u662f\u8c01\u5728\u7fa4\u4f17\u4e2d\u5927\u559d\u9053\uff1a\u201c\u597d\u8d3c\uff0c\u6253\u6b7b\u6a80\u660e\u8fd9\u5047\u4ec1\u5047\u4e49\u7684\u597d\u8d3c\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u6709\u5982\u661f\u661f\u4e4b\u706b\uff0c\u7acb\u523b\u5f15\u8d77\u4e86\u71ce\u539f\u4e4b\u52bf\u3002 \u3000\u3000\u5239\u90a3\u95f4\u6574\u6761\u957f\u8857\u4ff1\u5df2\u88ab\u6012\u559d\u58f0\u541e\u6ca1\u3002 \u3000\u3000\u6c49\u53e3\u57ce\u7684\u56db\u9762\u516b\u65b9\u4e4b\u5904\uff0c\u4e5f\u6709\u540c\u6837\u7684\u9ebb\u8863\u5c11\u5e74\uff0c\u5728\u53d9\u8ff0\u7740\u540c\u6837\u7684\u6545\u4e8b\uff0c\u5f15\u53d1\u8d77\u540c\u6837\u7684\u6012 \u559d\u3002 \u3000\u3000\u8981\u77e5\u8fd9\u822c\u6b66\u6797\u7fa4\u8c6a\u4ff1\u662f\u70ed\u8840\u51b2\u52a8\u4e4b\u8f88\uff0c\u7ecf\u8fc7\u8fd9\u8bb8\u591a\u65e5\u6c89\u95f7\u7684\u5f85\u5019\uff0c\u6b64\u523b\u65e9\u5df2\u538b\u5236\u4e0d\u4f4f\uff0c \u54ea\u91cc\u7ecf\u5f97\u8d77\u8fd9\u6837\u7684\u5f15\u53d1\uff01 \u3000\u3000\u672c\u6765\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u770b\u770b\u70ed\u95f9\u800c\u6765\u7684\u4eba\u4eec\uff0c\u6b64\u523b\u65e9\u5df2\u653e\u5f03\u4e86\u8896\u624b\u65c1\u89c2\u7684\u7acb\u573a\uff0c\u6124\u6012\u5730\u5927\u559d \u8d77\u6765\u3002 \u3000\u3000\u751a\u81f3\u8fde\u201c\u98de\u9f99\u9556\u5c40\u201d\u65d7\u4e0b\u7684\u4e00\u4e9b\u9556\u4f19\uff0c\u4e5f\u88ab\u8fd9\u4e00\u756a\u8a00\u8bed\u6240\u52a8\uff0c\u7adf\u53d8\u5f97\u8896\u624b\u65c1\u89c2\u8d77\u6765\u3002 \u3000\u3000\u53e6\u4e00\u4e9b\u4eba\u867d\u7136\u5bf9\u6a80\u660e\u5fe0\u5fc3\u803f\u803f\uff0c\u4f46\u89c1\u4e86\u8fd9\u4e00\u7fa4\u6124\u6012\u7684\u4eba\u7fa4\uff0c\u54ea\u91cc\u8fd8\u6562\u51fa\u624b\uff1f\u4ed6\u4eec\u53ea\u5e0c\u671b \u90a3\u6f06\u9ed1\u7684\u5927\u95e8\u5feb\u4e9b\u542f\u5f00\u3002 \u3000\u3000\u7a81\u7136\uff0c\u6709\u5341\u6570\u4eba\u8702\u6d8c\u5230\u6c5f\u8fb9\uff0c\u51b2\u5f00\u4e86\u90a3\u4e00\u5e2e\u6cae\u4e27\u7684\u9556\u4f19\uff0c\u5c06\u9556\u8f66\u63a8\u4e0b\u6c5f\u5cb8\uff0c\u6251\u901a\uff0c\u843d\u4eba \u6d4a\u9ec4\u7684\u6c5f\u6c34\u91cc\u3002 \u3000\u3000\u8fd9\u4e00\u4e2a\u60ca\u4eba\u7684\u4e3e\u52a8\u7acb\u523b\u4fbf\u5f15\u8d77\u4e86\u5343\u767e\u4eba\u7684\u6548\u6cd5\uff0c\u5343\u767e\u4eba\u4e00\u8d77\u8702\u6d8c\u800c\u4e0a\uff0c\u5c06\u767e\u5341\u8f86\u9556\u8f66\u4e00 \u9f50\u63a8\u4e0b\u4e86\u6c5f\u5cb8\uff0c\u98de\u6e85\u8d84\u7684\u6c5f\u6c34\uff0c\u6e85\u6e7f\u4e86\u8fdc\u5728\u6c5f\u5cb8\u65c1\u6570\u4e08\u5f00\u5916\u4eba\u4eec\u7684\u8863\u886b\uff0c\u4f46\u662f\u8fd9\u51b0\u51b7\u7684\u6c5f\u6c34 \u53cd\u800c\u6ca1\u6709\u6d47\u706d\u4eba\u4eec\u7684\u6012\u706b\uff0c\u53cd\u4f3c\u5728\u706b\u4e0a\u53c8\u52a0\u4e86\u4e9b\u6cb9\uff0c\u4f7f\u5f97\u4eba\u4eec\u7684\u6124\u6012\u71c3\u70e7\u5f97\u66f4\u52a0\u5267\u70c8\u3002 \u3000\u3000\u4ed6\u4eec\u53c8\u8702\u6d8c\u7740\u56de\u5230\u90a3\u6f06\u9ed1\u7684\u5927\u95e8\u524d\uff0c\u4e00\u58f0\u6012\u9a82\u54cd\u8d77\uff0c\u201c\u6a80\u660e\uff0c\u4f60\u51fa\u6765\uff0c\u8fd8\u6211\u4eec\u4e00\u4e2a\u516c \u9053\u3002\u201d \u3000\u3000\u5343\u767e\u58f0\u6012\u9a82\u968f\u4e4b\u54cd\u8d77\u3002 \u3000\u3000\u4e00\u5757\u77f3\u5757\uff0c\u7830\u5730\uff0c\u51fb\u5728\u90a3\u9ed1\u6f06\u7684\u5927\u95e8\u4e0a\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u77f3\u5757\uff0c\u6c34\u679c\uff0c\u751a\u81f3\u8336\u676f\u3001\u7897\u76cf\uff0c\u4fbf\u50cf\u662f\u66b4\u96e8\u4e00\u822c\u6295\u5728\u90a3\u9ed1\u6f06\u7684\u5927\u95e8\u4e0a\uff0c\u7070\u9eef\u7684\u56f4 \u5899\u5185\u5916\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u7fa4\u4f17\u7684\u5fc3\u7406\uff0c\u53e4\u5f80\u4eca\u6765\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u5c31\u5229\u7528\u4e86\u7fa4\u4f17\u7684\u5fc3\u7406\u800c\u6210\u5c31\u4e86\u9738\u738b\u4e4b\u4e1a\u3002 \u3000\u3000\u4f46\u82e5\u4e0d\u662f\u806a\u660e\u7edd\u9876\u7684\u4eba\uff0c\u5374\u4e07\u4e07\u4e0d\u4f1a\u5229\u7528\u8fd9\u7fa4\u4f17\u7684\u5fc3\u7406\u4e0e\u70ed\u8840\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u8ba1\u5212\uff0c\u81ea\u7136\u4ff1\u90fd\u662f\u806a\u660e\u7edd\u9876\u7684\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u5b89\u6392\u7684\uff0c\u4ed6\u8054\u7edc\u4e86\u6240\u6709\u88ab\u5bb3\u9556 \u5e08\u7684\u9057\u5c5e\uff0c\u5c06\u4ed6\u4eec\u4e00\u8d77\u9001\u5230\u6b66\u6c49\uff0c\u518d\u8bbe\u6cd5\u4e0e\u201c\u795e\u624b\u201d\u6218\u98de\u53d6\u5f97\u4e86\u5bc6\u5207\u7684\u8054\u7edc\uff0c\u8ba9\u201c\u6c5f\u5357\u540c \u76df\u201d\u7684\u624b\u4e0b\u7684\u7fa4\u4f17\u4e2d\u9f13\u626c\u8d77\u4e00\u9635\u65e0\u6cd5\u7184\u706d\u7684\u6012\u706b\u3002 \u3000\u3000\u6240\u6709\u7684\u4e8b\u60c5\u7684\u53d1\u751f\uff0c\u4ff1\u90fd\u5728\u4ed6\u5468\u5bc6\u7684\u5b89\u6392\u4e0e\u8ba1\u5212\u4e4b\u4e2d\uff0c\u800c\u6240\u6709\u7684\u5b89\u6392\u4e0e\u8ba1\u5212\uff0c\u4ff1\u90fd\u83b7\u5f97 \u4e86\u7a7a\u524d\u7684\u6210\u529f\uff01 \u3000\u3000\u81ea\u90ca\u5916\u5165\u57ce\u7684\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\uff0c\u4e00\u8def\u4e0a\u8be6\u7ec6\u5730\u53d9\u51fa\u4e86\u4ed6\u7684\u5b89\u6392\u4e0e\u8ba1\u5212\uff0c\u7136\u540e\u5fae\u5fae\u7b11 \u9053\uff1a\u201c\u8fd9\u5c31\u662f\u7fa4\u4f17\u7684\u5fc3\u7406\uff01\u201d \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u957f\u53f9\u4e00\u58f0\uff0c\u51fb\u638c\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u7fa4\u4f17\u7684\u5fc3\u7406\uff01\u201d \u3000\u3000\u88f4\u73cf\u4e00\u8a00\u4e0d\u53d1\uff0c\u9762\u5bd2\u5982\u6c34\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u65b9\u81ea\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u5c82\u4e0d\u592a\u8fc7\u4efd\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8881\u6cf8\u73cd\u5e7d\u5e7d\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u89c9\u5f97\u592a\u8fc7\u4efd\u4e86\u4e9b\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u60c5\u975e\u5f97\u5df2\uff0c\u4e8b\u5b9c\u4ece\u6743\uff0c\u6211\u8fd9\u6837\u7684\u505a\u6cd5\uff0c\u867d\u7136\u5931\u4e4b\u4ec1 \u539a\uff0c\u4f46\u5bf9\u6a80\u660e\u8fd9\u6837\u7684\u4eba\u6765\u7528\u8fd9\u6837\u7684\u65b9\u6cd5\uff0c\u5374\u662f\u518d\u6070\u5f53\u4e5f\u6ca1\u6709\u3002\u4eca\u65e5\u4e00\u5f79\uff0c\u6a80\u660e\u82e5\u80dc\uff0c\u4ed6\u7684\u950b \u8292\u5fc5\u5b9a\u66f4\u76db\uff0c\u59d1\u4e14\u4e0d\u8bba\u90a3\u4e00\u6bb5\u8840\u6d77\u6df1\u4ec7\uff0c\u4ee5\u6b66\u6797\u60c5\u52bf\u800c\u8a00\uff0c\u4e5f\u662f\u60b2\u60e8\u4e4b\u6781\u7684\u4e8b\uff0c\u4ed6\u4e00\u751f\u4ee5\u5978 \u72e1\u4e4b\u6743\u672f\u5bf9\u4eba\uff0c\u6211\u6b64\u523b\u4e5f\u4ee5\u597d\u72e1\u4e4b\u6743\u672f\u5bf9\u4ed6\uff0c\u8fd9\u5c82\u975e\u516c\u9053\u5df2\u6781\u7684\u4e8b\uff01\u88f4\u5144\uff0c\u82f1\u96c4\u5904\u4e16\uff0c\u5207\u5fcc \u6709\u5987\u4eba\u4e4b\u4ec1\uff0c\u4ee5\u5c0f\u4ec1\u4e71\u4e86\u5927\u8c0b\uff01\u201d \u3000\u3000\u88f4\u73cf\u9ed8\u7136\u826f\u4e45\uff0c\u957f\u53f9\u9053\uff1a\u201c\u82f1\u96c4\uff0c\u82f1\u96c4\u2026\u2026\u201d \u3000\u3000\u201c\u82f1\u96c4\uff0c\u82f1\u96c4\u2026\u2026\u201d \u3000\u3000\u7aef\u5750\u5728\u5ba2\u5385\u7684\u7ea2\u6728\u5927\u6905\u4e0a\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4e5f\u6b63\u5728\u5583\u5583\u81ea\u8bed\uff1a\u2018\u82f1\u96c4\uff1f\u82f1\u96c4\uff0c\u8c01\u662f\u82f1 \u96c4\uff0c\u82f1\u96c4\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201c\u8fd9\u4e00\u4e16\u82f1\u96c4\uff0c\u96c4\u8e1e\u6b66\u6797\u7684\u4e00\u4ee3\u5927\u8c6a\uff0c\u6b64\u523b\u5fc3\u5e95\u7684\u843d\u5bde\u4e0e\u8427\u7d22\uff0c\u4e16 \u95f4\u53c8\u6709\u54ea\u4e00\u679d\u7b14\u80fd\u591f\u63cf\u6479\uff1f\u7531\u5e73\u6de1\u800c\u7eda\u70c2\uff0c\u7531\u7eda\u70c2\u800c\u6781\u76db\uff0c\u6b64\u523b\uff0c\u4ed6\u4eff\u4f5b\u5df2\u611f\u89c9\u5230\u65e5\u843d\u540e\u7684 \u8427\u7d22\u3002\u6a80\u6587\u742a\u7684\u7a81\u7136\u79bb\u53bb\uff0c\u6240\u7ed9\u4e88\u8fd9\u8001\u4eba\u7684\u75db\u82e6\u4e0e\u523a\u6fc0\uff0c\u5f53\u771f\u6bd4\u6cf0\u5c71\u8fd8\u8981\u6c89\u91cd\uff0c\u4ed6\u53ea\u89c9\u96c4\u5fc3 \u6e10\u5931\uff0c\u4e07\u5ff5\u4ff1\u7070\uff01\u4e1c\u65b9\u94c1\u3001\u4e1c\u65b9\u5251\u3001\u4e1c\u65b9\u6c5f\u3001\u4e1c\u65b9\u6e56\u5144\u5f1f\u56db\u4eba\uff0c\u9762\u8272\u94c1\u9752\uff0c\u7aef\u5750\u5728\u5385\u5802\u4e2d \u592e\uff0c\u95e8\u5916\u7684\u6012\u9a82\uff0c\u5df2\u4f7f\u5f97\u4ed6\u4eec\u96be\u582a\uff0c\u843d\u5728\u9662\u4e2d\u7684\u77f3\u5757\u3001\u676f\u76cf\uff0c\u66f4\u4f7f\u4ed6\u4eec\u96be\u4ee5\u5fcd\u8010\uff0c\u4f46\u4ed6\u5144\u5f1f \u56db\u4eba\u4fa0\u4e49\u4f20\u5bb6\uff0c\u6b64\u523b\u5374\u53c8\u4e0d\u5fcd\u653e\u624b\u4e00\u8d70\u3002\u4ed6\u4eec\u8c01\u4e5f\u731c\u4e0d\u51fa\u6765\uff0c\u4e1c\u65b9\u9707\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u4e3a\u4ec0\u4e48\u7a81 \u7136\u51fa\u8d70\uff1f\u4e3a\u4ec0\u4e48\u7adf\u4f1a\u548c\u6a80\u6587\u742a\u4e00\u8d77\u5931\u8e2a\uff1f\u5927\u5385\u4fa7\u7684\u8033\u623f\u4e2d\uff0c\u201d\u516b\u5366\u638c\u201c\u67f3\u8f89\u3001\u201d\u5feb\u9a6c\u795e\u5200 \u201c\u9f9a\u6e05\u6d0b\uff0c\u4ee5\u53ca\u8fb9\u5c11\u884d\u3001\u7f57\u4e49\u7b49\uff0c\u6b63\u5728\u7a83\u7a83\u79c1\u8bed\u7740\u3002\u4ed6\u4eec\u5728\u5bc6\u8c0b\u8ba1\u5212\u7740\u4ec0\u4e48\uff1f\u201d\u795e\u624b\u201c\u6218\u98de \u7684\u884c\u8e2a\u662f\u96be\u4ee5\u88ab\u4eba\u5bfb\u51fa\u7684\u3002\u4ed6\u6b64\u523b\u6b63\u659c\u501a\u5728\u201d\u957f\u4e50\u91cc\u201c\uff0c\u201d\u767d\u5170\u9662\u201c\uff0c\u6b66\u6c49\u540d\u5993\u201d\u5c0f\u767d\u5170 \u201c\u7684\u9999\u95fa\u4e2d\u3002\u7d2b\u91d1\u94a9\u6302\u6d41\u82cf\u5e10\uff0c\u9e33\u9e2f\u6795\u53e0\u7fe0\u88f3\uff0c\u201d\u795e\u624b\u201c\u6218\u98de\u659c\u501a\u5728\u6d41\u82cf\u5e10\u4e0b\uff0c\u9e33\u9e2f\u6795\u4e0a\uff0c \u64ad\u5f04\u7740\u5e10\u8fb9\u7684\u91d1\u94a9\u3002\u91d1\u94a9\u53ee\u5f53\uff0c\u9ed8\u5750\u5728\u4ed6\u5bf9\u9762\u7684\u201d\u5c0f\u767d\u5170\u201c\u5706\u7741\u79cb\u6ce2\uff0c\u597d\u5947\u800c\u8be7\u5f02\u5730\u671b\u7740\u9762 \u524d\u8fd9\u4e2a\u8c6a\u5ba2\u3002\u5979\u4ece\u672a\u89c1\u8fc7\u8fd9\u6837\u7684\u5ba2\u4eba\uff0c\u5728\u5979\u4e00\u9897\u88ab\u98ce\u5c18\u67d3\u5f97\u53d8\u4e86\u8272\u7684\u82b3\u5fc3\u4e2d\uff0c\u8fd9\u7c97\u65f7\u4e2d\u5e26\u7740 \u5fe7\u6101\uff0c\u968f\u4fbf\u4e2d\u5e26\u7740\u5a01\u4e25\u7684\u8c6a\u5ba2\uff0c\u5bf9\u5979\u7ade\u6709\u4e00\u79cd\u4e0d\u53ef\u6297\u62d2\u7684\u5438\u5f15\u4e4b\u529b\uff0c\u4f46\u662f\u4ece\u6628\u591c\u7684\u6df1\u591c\uff0c\u76f4 \u5230\u6b64\u523b\uff0c\u4ed6\u5374\u53ea\u662f\u5446\u5446\u5730\u5750\u7740\uff0c\u76b1\u7709\u5730\u6df1\u601d\u7740\uff0c\u5076\u800c\u5230\u95e8\u7554\u53bb\u53d1\u4e00\u4e2a\u7b80\u77ed\u7684\u547d\u4ee4\uff0c\u5076\u800c\u4ece\u5979 \u83b9\u767d\u5982\u7389\u7684\u7ea4\u624b\u4e0a\u559d\u4e00\u676f\u8f9b\u8fa3\u7684\u70c8\u9152\u3002\u5979\u5fcd\u4e0d\u4f4f\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5728\u60f3\u4ec0 \u4e48\uff1f\u2018\u201d\u795e\u624b\u201c\u6218\u98de\u968f\u53e3\u6f2b\u5e94\u4e86\u4e00\u58f0\uff0c\u4ed6\u5fc3\u4e2d\u7684\u786e\u6709\u7740\u8bb8\u591a\u5fc3\u4e8b\u3002\u7167\u76ee\u524d\u7684\u60c5\u52bf\u6765\u770b\uff0c\u201d\u6c5f \u5357\u540c\u76df\u201c\u7684\u786e\u5df2\u7a33\u64cd\u80dc\u7b97\uff0c\u4f46\u662f\u8fd9\u79cd\u80dc\u5229\u5bf9\u4ed6\u800c\u8a00\uff0c\u5374\u662f\u6beb\u65e0\u5229\u76ca\u7684\u3002\u4ed6\u5ffd\u7136\u53d1\u89c9\uff0c\u4ed6\u8ba1\u5212 \u4e2d\u6240\u57f9\u517b\u7684\u201d\u5080\u5121\u201c\uff0c\u81f3\u4eca\u5df2\u6210\u4e86\u4e00\u4e2a\u5149\u8292\u4e07\u4e08\u7684\u201d\u82f1\u96c4\u201c\uff0c\u201d\u82f1\u96c4\u201c\u662f\u4efb\u4f55\u4eba\u65e0\u6cd5\u63a7\u5236 \u7684\uff0c\u4ed6\u8ba1\u5212\u4e2d\u7684\u6743\u52bf\u4e0e\u5149\u8363\uff0c\u81f3\u4eca\u53ef\u8bf4\u662f\u6beb\u65e0\u5e0c\u671b\u843d\u5728\u4ed6\u81ea\u5df1\u624b\u4e2d\u3002\u4ed6\u4ed4\u7ec6\u5730\u5206\u6790\u7740\u60c5\u52bf\uff0c \u4ed6\u603b\u7b97\u662f\u4e2a\u201d\u67ad\u96c4\u201c\uff0c\u5bf9\u4e8e\u60c5\u5f62\u7684\u5224\u65ad\uff0c\u662f\u90a3\u4e48\u7c97\u7ec6\u800c\u777f\u667a\uff0c\u4ed6\u660e\u786e\u5224\u5b9a\u4e86\u81ea\u5df1\u5728\u4e00\u573a\u80dc\u5229 \u4e2d\u6240\u80fd\u5f97\u5230\u7684\u6536\u83b7\uff0c\u4e0e\u4ed6\u5148\u524d\u8ba1\u5212\u7684\u5b9e\u5728\u76f8\u5dee\u5f97\u5927\u591a\u3002\u201d\u5c0f\u767d\u5170\u201d\u867d\u7136\u4e45\u7ecf\u98ce\u5c18\uff0c\u5374\u53c8\u600e\u4f1a \u731c\u5f97\u4e2d\u9762\u524d\u8fd9\u8349\u6cfd\u4e4b\u96c4\u7684\u5fc3\u4e8b\uff1f\u5979\u8f7b\u8f7b\u62ac\u8d77\u8d64\u88f8\u7684\u7ea4\u8db3\uff0c\u5728\u201d\u795e\u624b\u201c\u6218\u98de\u80f8\u819b\u4e0a\u70b9\u4e86\u4e00\u4e0b\uff0c \u5a07\u5524\u9053\uff1a\u201c\u5582\uff0c\u4f60\u2014\u2014\u201d\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u4e00\u626c\uff0c\u53cc\u76ee\u500f\u7741\uff0c\u5389\u53f1\u9053\uff1a\u201c\u4f60\u8981\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c0f\u767d\u5170\u201d\u82b3\u5fc3\u4e00\u51db\uff0c\u53ea\u89c9\u4ed6\u7684\u76ee\u5149\u50cf\u662f\u5200\u4e00\u6837\uff0c\u4f7f\u5f97\u81ea\u5df1\u4e0d\u6562\u903c\u89c6\uff0c\u4f46\u98ce\u5c18\u4e2d\u7684\u7ecf\u5386 \u5374\u4f7f\u5979\u53d1\u51fa\u4e0e\u5e38\u4eba\u4e0d\u540c\u7684\u53cd\u5e94\u3002 \u3000\u3000\u5979\u53cd\u800c\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u6251\u5230\u201c\u795e\u624b\u201d\u6218\u98de\u7684\u8eab\u4e0a\uff0c\u6492\u7740\u5a07\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd9\u6837\u51f6\uff1f\u6211\u662f\u770b \u4f60\u6101\u5f97\u53d1\u614c\uff0c\u624d\u60f3\u66ff\u4f60\u89e3\u89e3\u95f7\uff0c\u6211\u662f\u559c\u6b22\u4f60\u5440\uff01\u201d \u3000\u3000\u6e29\u67d4\u800c\u7eee\u4e3d\u7684\u5a07\u8bed\uff0c\u4f7f\u5f97\u201c\u795e\u624b\u201d\u6218\u98de\u5931\u53bb\u4e86\u96c4\u5fc3\u7a81\u7136\u8d77\u4e86\u4e00\u9635\u6fc0\u8361\u3002 \u3000\u3000\u4ed6\u7d27\u76b1\u7684\u53cc\u7709\u6e10\u6e10\u653e\u5bbd\uff0c\u76ee\u5149\u4e5f\u6e10\u6e10\u67d4\u548c\uff0c\u8fd9\u4e00\u751f\u98ce\u5c18\u5954\u6ce2\uff0c\u4e3a\u58f0\u540d\u4e8b\u4e1a\u6323\u624e\u3001\u594b\u6597\uff0c \u751a\u81f3\u6b3a\u9a97\u3001\u62a2\u63a0\u7684\u6b66\u6797\u6cc9\u96c4\uff0c\u5982\u4eca\u9aa4\u7136\u843d\u5165\u6e29\u67d4\u4e61\u4e2d\uff0c\u9aa4\u7136\u5c1d\u5230\u4e86\u6e29\u67d4\u4e61\u4e2d\u7684\u6e29\u67d4\u6ecb\u5473\uff0c\u8fd9 \u5bf9\u4ed6\u5931\u671b\u3001\u843d\u5bde\u3001\u800c\u6e10\u6e10\u8001\u53bb\u7684\u96c4\u5fc3\uff0c\u662f\u4e00\u79cd\u591a\u4e48\u5927\u7684\u8bf1\u60d1\u3002 \u3000\u3000\u201c\u5c0f\u767d\u5170\u201d\u611f\u89c9\u5230\u4ed6\u60c5\u7eea\u4e0a\u7684\u53d8\u5316\u4e0e\u6ce2\u52a8\uff0c\u5979\u8f7b\u8f7b\u4f38\u51fa\u6625\u8471\u822c\u7684\u7389\u624b\uff0c\u4e3a\u4ed6\u8f7b\u8f7b\u6574\u7740\u9894 \u4e0b\u7684\u957f\u9aef\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u8bf4\u51fa\u6765\u7ed9\u6211\u542c\u542c\uff0c\u597d\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u61c2\u7684\uff01\u201d \u3000\u3000\u201c\u5c0f\u767d\u5170\u201d\u4ee5\u660e\u5a9a\u7684\u79cb\u6ce2\u6e29\u67d4\u5730\u671b\u7740\u4ed6\uff0c\u8f7b\u8f7b\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u2026\u2026\u6211\u5531\u4e00\u53ea\u66f2\u5b50\uff0c\u66ff\u4f60\u89e3 \u6101\u597d\u4e48\uff1f\u201d \u3000\u3000\u5979\u5a40\u5a1c\u5730\u7ad9\u8d77\u6765\uff0c\u5979\u8d64\u88f8\u7684\u79c0\u8db3\uff0c\u8e0f\u8fc7\u539a\u539a\u7684\u5730\u6be1\uff0c\u5979\u83b9\u767d\u7684\u7ea4\u624b\uff0c\u53d6\u4e0b\u4e86\u5899\u89d2\u7684\u7435 \u7436\u3002 \u3000\u3000\u8f7b\u8f7b\u8c03\u5f04\uff0c\u6162\u6162\u7406\u5f26\uff0c\u8f7b\u8f7b\u54b3\u55fd\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u6162\u58f0\u8f7b\u5531\uff0c\u5979\u7684\u6b4c\u58f0\u662f\u90a3\u4e48\u7eee\u4e3d\u800c\u6e29\u67d4\u3002 \u3000\u3000\u5728\u8fd9\u6e29\u67d4\u800c\u7eee\u4e3d\u7684\u6b4c\u58f0\u4e2d\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u7a81\u7136\u53d1\u89c9\u8fd9\u91cc\u7684\u6e29\u67d4\u6ecb\u5473\uff0c\u6216\u8005\u7adf\u5c06\u662f\u4ed6\u5c06\u6765 \u6700\u5927\u7684\u5b89\u6170\u3002 \u3000\u3000\u4ed6\u51dd\u6ce8\u7740\u9762\u524d\u8fd9\u7f8e\u4e3d\u7684\u5973\u5b50\uff0c\u5fc3\u91cc\u5ffd\u7136\u6cdb\u8d77\u4e86\u4e00\u9635\u4ece\u6765\u672a\u6709\u7684\u8361\u6f3e\u3001\u6e29\u67d4\u3002\u6e29\u67d4\u4e0d\u662f\u6700 \u6700\u5bb9\u6613\u6d88\u78e8\u96c4\u5fc3\u7684\u4e48\uff1f \u3000\u3000\u4f46\u662f\u4ed6\u6b64\u523b\u5fc5\u987b\u51fa\u53bb\uff0c\u4e3a\u81ea\u5df1\u7684\u6743\u52bf\u4f5c\u6700\u540e\u7684\u6323\u624e\u3002 \u3000\u3000\u4ed6\u4e00\u632f\u8863\u88f3\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u5916\u9762\u7684\u6012\u559d\u4e0e\u66b4\u52a8\u4e4b\u58f0\uff0c\u5df2\u9690\u9690\u4f20\u5230\u4e86\u8fd9\u7eee\u4e3d\u800c\u6e29\u67d4\u7684\u9999\u95fa\u4e2d \u6765\u3002 \u3000\u3000\u957f\u8857\u4e0a\u66f4\u4e71\u4e86\u3002 \u3000\u3000\u6f06\u9ed1\u5927\u95e8\u5916\u7684\u4eba\u7fa4\uff0c\u50cf\u662f\u75af\u72c2\u4e86\u4f3c\u7684\uff0c\u4f46\u662f\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7684\u4f59\u5a01\u4ecd\u5728\uff0c\u4ed6\u4eec\u7adf\u6ca1 \u6709\u4eba\u6562\u51b2\u4e0a\u90a3\u77f3\u9636\u4e00\u6b65\u3002 \u3000\u3000\u201c\u98de\u9f99\u9556\u5c40\u201d\u7684\u9556\u4f19\uff0c\u6709\u7684\u5df2\u5077\u5077\u8131\u4e0b\u4e86\u201c\u98de\u9f99\u9556\u5c40\u201d\u7684\u8863\u88f3\uff0c\u6df7\u6742\u5728\u6124\u6012\u7684\u4eba\u7fa4\uff0c\u6709 \u7684\u751a\u81f3\u5df2\u5077\u5077\u6e9c\u8d70\uff01 \u3000\u3000\u51ac\u65e5\u867d\u5df2\u897f\u659c\uff0c\u4f46\u6bd5\u7adf\u5df2\u4ece\u9634\u973e\u4e2d\u6323\u624e\u800c\u51fa\uff0c\u4e5f\u6bd5\u7adf\u8fd8\u6709\u7740\u5b83\u4e98\u53e4\u672a\u53d8\u7684\u5a01\u529b\uff0c\u5c06\u5730\u4e0a \u7684\u79ef\u96ea\uff0c\u878d\u5316\u6210\u4e00\u7247\u7247\u9ed1\u7684\u6ce5\u6cde\u3002 \u3000\u3000\u5343\u767e\u53cc\u8db3\uff0c\u5728\u6ce5\u6cde\u4e0a\u8df5\u8e0f\u7740\u3002 \u3000\u3000\u897f\u659c\u7684\u9633\u5149\uff0c\u6620\u5f97\u9ed1\u6f06\u7684\u5927\u95e8\u6563\u53d1\u7740\u4e4c\u9ed1\u7684\u5149\u6cfd\u3002 \u3000\u3000\u5b9e\u5730\uff01\u5927\u95e8\u970d\u7136\u5f00\u542f\uff01 \u3000\u3000\u96c4\u8e1e\u6b66\u6797\u53f1\u54a4\u6c5f\u6e56\u7684\u4e00\u4ee3\u5927\u8c6a\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u4e00\u624b\u634b\u987b\uff0c\u9762\u5bd2\u5982\u94c1\uff0c\u7f13\u6162\u4f46\u5374\u6709\u529b \u5730\u5927\u6b65\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u5389\u7535\u822c\u7684\u76ee\u5149\u56db\u4e0b\u4e00\u626b\uff0c\u957f\u8857\u4e0a\u7684\u55a7\u4e71\u7acb\u523b\u9759\u5bc2\u4e0b\u8bb8\u591a\u3002 \u3000\u3000\u8fd9\u4e00\u4e16\u4e4b\u96c4\u679c\u7136\u8fd8\u6709\u7740\u4ed6\u7684\u5a01\u4eea\uff0c\u8fd9\u6210\u4eea\u65e9\u5df2\u6df1\u5165\u6b66\u6797\u4e2d\u4eba\u7684\u5fc3\u76ee\u4e2d\uff0c\u5f53\u4ed6\u5389\u7535\u822c\u7684\u76ee \u5149\u626b\u81f3\u7b2c\u4e09\u6b21\u65f6\uff0c\u6cb8\u817e\u7740\u7684\u957f\u8857\uff0c\u5df2\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7531\u6781\u4e71\u800c\u6781\u9759\uff0c\u8fd9\u957f\u8857\u4e0a\u6b64\u523b\u4fbf\u50cf\u662f\u6b7b\u4e00\u6837\u5b89\u5b81\uff0c\u5076\u800c\u6709\u81ea\u522b\u5904\u72c2\u5954\u800c\u6765\u7684\u4eba\u4eec\uff0c\u4f46\u6b64 \u523b\u5374\u4e5f\u4e0d\u81ea\u89c9\u5730\u653e\u8f7b\u4e86\u811a\u6b65\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u76ee\u5149\u7f13\u7f13\u626b\u8fc7\u8fd9\u4e00\u7fa4\u88ab\u4ed6\u58f0\u5a01\u6240\u61d4\u7684\u4eba\u7fa4\uff0c\u7709\u5b87\u95f4\u7684\u5fe7\u90c1\u5e76\u672a\u4e1d\u6beb\u51cf \u5c11\uff0c\u4ed6\u653e\u4e0b\u624b\u638c\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u867d\u7136\u662f\u5982\u6b64\u9547\u5b9a\u800c\u4ece\u5bb9\uff0c\u4f46\u662f\u4ed6\u5fc3\u4e2d\u5374\u9690\u85cf\u7740\u8bb8\u591a\u7126\u8651\uff0c\u5fe7\u60a3\u548c\u4e0d\u5b89\uff0c\u800c\u6b64\u523b\u4ed6 \u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u5374\u662f\u795e\u5b9a\u6c14\u8db3\uff0c\u7ef5\u7ef5\u5bc6\u5bc6\uff0c\u6709\u5982\u6cd5\u949f\u5de8\u9f13\u540c\u65f6\u9707\u8361\uff0c\u53c8\u6709\u5982\u6625\u96f7\u7a81\u7136\u66b4\u53d1\uff0c \u5c31\u8fde\u897f\u65b9\u7684\u659c\u9633\uff0c\u4f3c\u4e4e\u90fd\u4e5f\u88ab\u4ed6\u8fd9\u6210\u731b\u6c89\u91cd\u7684\u8bed\u58f0\u9707\u5f97\u66f4\u843d\u4e0b\u53bb\u4e86\u4e9b\u3002 \u3000\u3000\u7acb\u5728\u6700\u6700\u524d\u9762\u7684\u4eba\u60c5\u4e0d\u81ea\u7981\u5730\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u6b64\u523b\u4e1c\u65b9\u5144\u5f1f\u5df2\u81ea\u7f13\u6b65\u800c\u51fa\uff0c\u770b\u5230\u8fd9\u756a\u60c5 \u51b5\uff0c\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u9f50\u5730\u5fd6\u9053\uff1a\u201c\u767e\u8db3\u4e4b\u866b\uff0c\u6b7b\u800c\u4e0d\u50f5\uff0c\u60f3\u4e0d\u5230\u2018\u9f99\u5f62\u516b\u638c\u2019\u4e00\u8def\u5982\u6b64\uff0c\u6b64 \u523b\u5374\u8fd8\u6709\u5982\u6b64\u60ca\u4eba\u7684\u58f0\u5a01\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u4e00\u626c\uff0c\u5389\u58f0\u53c9\u9053\uff1a\u201c\u5982\u679c\u65e0\u4e8b\uff0c\u7ad9\u5728\u8fd9\u91cc\u80e1\u95f9\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u8d76\u5feb\u9000\u4e0b \u53bb\uff01\u201d \u3000\u3000\u7acb\u5728\u524d\u9762\u7684\u4eba\uff0c\u60c5\u4e0d\u81ea\u7981\u5730\u53c8\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u4f46\u540e\u9762\u7684\u4eba\u5374\u5bf8\u6b65\u672a\u79fb\uff0c\u4e8e\u662f\u4eba\u7fa4\u4e2d\u95f4\u53c8 \u8d77\u4e86\u4e00\u9635\u9a9a\u4e71\u3002 \u3000\u3000\u9a9a\u4e71\u65b9\u8d77\uff0c\u7acb\u523b\u6709\u4eba\u5927\u547c\u9053\uff1a\u201c\u8840\u503a\u8840\u8fd8\uff0c\u59d3\u6a80\u7684\uff0c\u5341\u4f59\u5e74\u524d\uff0c\u4f60\u505a\u4e0b\u7684\u6ee1\u8eab\u8840\u503a\uff0c\u4f60 \u82e5\u4e0d\u4ee5\u8840\u6d17\u6e05\uff0c\u4f11\u60f3\u8fc7\u5f97\u53bb\u4eca\u65e5\uff01\u201d \u3000\u3000\u547c\u58f0\u8fc7\u540e\uff0c\u5927\u4e71\u53c8\u8d77\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u53cc\u76ee\u4e00\u5f20\uff0c\u6d53\u7709\u5251\u98de\uff0c\u5389\u53f1\u4e00\u58f0\uff01 \u3000\u3000\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5389\u53f1\u66f4\u6709\u5982\u6674\u5929\u4e4b\u9739\u96f3\uff0c\u5f53\u7a7a\u51fb\u4e0b\uff0c\u540c\u65f6\u5728\u5343\u767e\u4eba\u8033\u4e2d\u54cd\u8d77\uff0c\u5343\u767e\u4e2a\u6742\u4e71\u7684\u58f0 \u97f3\uff0c\u7adf\u4e00\u8d77\u88ab\u8fd9\u4e00\u58f0\u5389\u53f1\u9707\u4f4f\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u53cc\u62f3\u7d27\u63e1\uff0c\u5389\u58f0\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u8bf4\u8bdd\uff1f\u53ea\u7ba1\u5230\u524d\u9762\u6765\u8bf4\uff01\u201d \u3000\u3000\u4eba\u7fa4\u4e2d\u4f60\u671b\u6211\uff0c\u6211\u671b\u4f60\uff0c\u7adf\u65e0\u4eba\u6562\u5411\u524d\u8d70\u52a8\u4e00\u6b65\u3002 \u3000\u3000\u53c8\u662f\u4e00\u9635\u6b7b\u822c\u7684\u9759\u5bc2\uff0c\u6a80\u660e\u6c89\u58f0\u9053\uff1a\u201c\u5341\u4f59\u5e74\u524d\u90a3\u4e00\u6bb5\u65e0\u5934\u516c\u6848\uff0c\u5404\u4f4d\u672a\u66fe\u5fd8\u8bb0\uff0c\u6a80\u660e \u4e5f\u672a\u66fe\u5fd8\u8bb0\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u60f3\u63a2\u6d4b\u51fa\u5176\u4e2d\u7684\u771f\u76f8\uff0c\u4f46\u771f\u76f8\u81f3\u4eca\u8fd8\u9690\u4e8e\u6d53\u96fe\uff0c\u5404\u4f4d\u77e5\u6211\u6a80\u660e\u5df2 \u4e45\uff0c\u5c82\u53ef\u968f\u4fbf\u542c\u4fe1\u4e00\u4e9b\u5c0f\u4eba\u7684\u8840\u53e3\u55b7\u4eba\uff0c\u5c31\u6307\u6211\u6a80\u660e\u4e3a\u51f6\u624b\uff1f\u201d \u3000\u3000\u4ed6\u53cc\u81c2\u4e00\u6325\uff0c\u5927\u559d\u9053\uff1a\u201c\u6211\u6a80\u660e\u53ef\u50cf\u662f\u51f6\u624b\u4e48\uff1f\u201d \u3000\u3000\u4f17\u4eba\u4ef0\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u5353\u7acb\u5982\u5c71\uff0c\u6ee1\u9762\u5a01\u4eea\uff0c\u6709\u7684\u4eba\u5df2\u4e0d\u7981\u5728\u5fc3\u4e2d\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u4ed6\u50cf\u662f \u51f6\u624b\u4e48\uff1f\u201d \u3000\u3000\u7acb\u5f97\u8fdc\u7684\uff0c\u4e5f\u5df2\u4e0d\u7981\u5f00\u59cb\u4e86\u7a83\u7a83\u79c1\u8bae\uff0c\u4eba\u7fa4\u4e2d\u7a81\u6709\u51e0\u4eba\u79fb\u52a8\uff0c\u7136\u540e\u56db\u9762\u516b\u65b9\u53c8\u540c\u65f6\u54cd\u8d77 \u4e86\u4e00\u9635\u6124\u6012\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u4e8b\u5b9e\u4ff1\u5728\uff0c\u4f60\u8fd8\u60f3\u72e1\u8d56\u4e48\uff1f\u201d \u3000\u3000\u201c\u597d\u6c49\u505a\u4e8b\u597d\u6c49\u5f53\uff0c\u6a80\u660e\u5440\uff0c\u60f3\u4e0d\u5230\u4f60\u7adf\u662f\u8fd9\u4e48\u4e00\u4e2a\u61e6\u592b\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u987b\u53d1\u9f50\u626c\uff0c\u5927\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5b9e\uff0c\u4ec0\u4e48\u8bc1\u636e\uff0c\u6709\u8c01\u80fd\u6307\u51fa\u4e00\u4ef6\u6765\u4e48\uff1f \u82e5\u6709\u4eba\u80fd\u4e3e\u51fa\u4e00\u4ef6\uff0c\u6211\u6a80\u660e\u7acb\u523b\u6a2a\u5200\u81ea\u520e\u5728\u5929\u4e0b\u9ad8\u660e\u4eba\u4e4b\u524d\uff0c\u4e0d\u52b3\u522b\u4eba\u52a8\u624b\uff0c\u82e5\u662f\u4ec5\u8fd9\u6837\u51ed \u7a7a\u8bf4\u8bdd\uff0c\u542b\u8840\u55b7\u4eba\uff0c\u600e\u80fd\u53eb\u5929\u4e0b\u4eba\u5fc3\u670d\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7acb\u523b\u63a5\u53e3\u9053\uff1a\u201c\u82e5\u662f\u771f\u6709\u771f\u51ed\u5b9e\u636e\u4e4b\u4eba\uff0c\u53ea\u7ba1\u51fa\u6765\u3002\u6211\u6a80\u660e\u7edd\u4e0d\u635f\u4f24\u4ed6\u4e00 \u6839\u6bdb\u53d1\uff01\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u4e1c\u65b9\u94c1\u5ffd\u7136\u5927\u6b65\u5411\u524d\u884c\u8d70\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6211\u4e1c\u65b9\u94c1\u4ee5\u2018\u98de\u7075\u5821\u2019\u6570\u5341\u5e74\u6765\u5728\u6b66 \u6797\u4e2d\u4e4b\u5730\u4f4d\u62c5\u4fdd\u6b64\u523b\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u6240\u8bf4\u7684\u8a00\u8bed\uff0c\u82e5\u662f\u6a80\u660e\u4eca\u65e5\u52a8\u4e86\u62ff\u51fa\u771f\u51ed\u5b9e\u636e\u4e4b\u4eba\u4e00 \u6839\u6bdb\u53d1\uff0c\u6211\u2018\u98de\u7075\u5821\u2019\u4fbf\u5148\u5411\u4ed6\u8981\u4e2a\u516c\u9053\uff0c\u82e5\u662f\u65e0\u4eba\u80fd\u62ff\u51fa\u771f\u51ed\u5b9e\u636e\uff0c\u53ea\u662f\u51ed\u7a7a\u634f\u9020\uff0c\u542b\u8840 \u55b7\u4eba\uff0c\u6211\u2018\u98de\u7075\u5821\u2019\u4e5f\u8981\u4ee3\u6a80\u660e\u5411\u5404\u4f4d\u8981\u4e2a\u516c\u9053\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u6e05\u660e\uff0c\u58f0\u5982\u91d1\u77f3\uff0c\u51e0\u53ef\u4e0a\u51b2\u4e91\u9704\uff01 \u3000\u3000\u6a80\u660e\u4e0d\u7981\u6df1\u542b\u611f\u6fc0\u5730\u671b\u4e86\u8fd9\u6b63\u76f4\u800c\u4fa0\u4e49\u7684\u5c11\u5e74\u4e00\u773c\uff0c\u53ea\u542c\u4ed6\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u7acb\u523b\u63a5\u53e3\u53c8 \u9053\uff1a\u201c\u5404\u4f4d\u6b66\u6797\uff0c\u670b\u53cb\u6709\u8c01\u4e0d\u76f8\u4fe1\u2018\u98de\u7075\u5821\u2019\u7684\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7684\u864e\u90b1\u201c\u98de\u7075\u5821\u201d\u201c\u4e1c\u65b9\u4e16\u5bb6\u201d\uff0c\u5728\u6b66\u6797\u4e2d\u4e4b\u5730\u4f4d\u5f53\u771f\u975e\u540c\u5c0f\u53ef\uff0c\u6b64\u523b\u8fd9\u4e1c\u65b9\u5c11\u5821 \u4e3b\u8bdd\u8bf4\u51fa\u6765\uff0c\u7acb\u523b\u4fbf\u53c8\u5c06\u7fa4\u8c6a\u4e00\u8d77\u9707\u4f4f\u3002 \u3000\u3000\u4eff\u4f5b\u6709\u4e2a\u4eba\u5728\u4eba\u7fa4\u4e2d\u4f4e\u8bed\u4e86\u53e5\uff1a\u201c\u4f60\u4eec\u662f\u4eb2\u5bb6\uff0c\u4f60\u5f53\u7136\u5e2e\u4ed6\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u4f46\u662f\u4ed6\u8bed\u58f0\u672a\u4e86\uff0c\u5374\u53c8\u5df2\u88ab\u4e1c\u65b9\u94c1\u626b\u8fc7\u7684\u773c\u795e\u9707\u4f4f\u3002 \u3000\u3000\u53c8\u4e00\u6b21\uff0c\u957f\u8857\u4e0a\u6b7b\u4e00\u822c\u5730\u9759\u5bc2\u3002 \u3000\u3000\u9759\u5bc2\u4e4b\u4e2d\uff0c\u7a81\u6709\u4e00\u58f0\u9707\u8033\u7684\u72c2\u7b11\uff0c\u81ea\u957f\u8857\u7684\u5c3d\u5934\u4f20\u6765\u3002 \u7b2c\u5341\u4e94\u7ae0>> \u53e4\u9f99\u300a\u5b64\u661f\u4f20\u300b \u7b2c\u5341\u4e94\u7ae0 \u3000\u3000\u8fd9\u4e00\u5207\u53d8\u5316\uff0c\u52a8\u4e71\u3001\u60ca\u547c\u3001\u6012\u559d\uff0c\u4ee5\u53ca\u8fd9\u4e00\u5207\u53d8\u5316\u4e2d\u7684\u5e73\u9759\u4e0e\u6c89\u5bc2\uff0c\u88f4\u73cf\u4ff1\u90fd\u770b\u5728\u773c \u91cc\uff0c\u542c\u5728\u8033\u91cc\u3002 \u3000\u3000\u4ed6\u4f2b\u7acb\u5728\u4e00\u5ea7\u9152\u697c\u4e0a\u7684\u7a97\u6237\u524d\uff0c\u65e0\u8a00\u5730\u770b\u7740\u8fd9\u4e00\u5207\u4e8b\u6545\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u77e5\u662f\u6124\u6012\uff0c\u6291\u6216\u662f\u601c \u60af\u4e0e\u60b2\u54c0\u3002 \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u5374\u4ee5\u51dd\u76ee\u671b\u7740\u4ed6\u9762\u4e0a\u7684\u8868\u60c5\uff0c\u4e0d\u65f6\u5f97\u610f\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u663e\u7136\u5bf9\u81ea\u5df1\u5b89 \u6392\u4e0b\u7684\u6548\u679c\uff0c\u751a\u4e3a\u6ee1\u610f\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u7684\u4e00\u756a\u8a00\u8bed\uff0c\u53ea\u4e0d\u8fc7\u5f15\u8d77\u4e86\u4ed6\u4e00\u58f0\u51b7\u7b11\uff0c\u88f4\u73cf\u4fa7\u76ee\u9053\uff1a\u201c\u6b64\u65f6\u6b64\u523b\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d \u7b11\u7684\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u5fae\u7b11\u4e0d\u5df2\uff0c\u7a81\u53c8\u957f\u53f9\u9053\uff1a\u201c\u6211\u5728\u7b11\u8fd9\u4e9b\u5c11\u5e74\u5f97\u5fd7\u7684\u5c11\u5e74\uff0c\u51ed\u7740\u7236\u5144 \u5e08\u95e8\u7684\u4f59\u836b\uff0c\u5728\u6b66\u6797\u4e2d\u535a\u5f97\u4e86\u4e00\u4efd\u58f0\u540d\uff0c\u5374\u4e1d\u6beb\u4e0d\u77e5\u9053\u6b66\u6797\u4e2d\u7684\u5978\u8bc8\uff0c\u2018\u9f99\u5f62\u516b\u638c\u2019\u773c\u89c1\u5df2 \u662f\u4f17\u53db\u4eb2\u79bb\uff0c\u7a77\u9014\u65e5\u66ae\uff0c\u8fd9\u4e1c\u65b9\u94c1\u7adf\u8fd8\u5728\u4e3a\u4ed6\u8bf4\u8bdd\u2026\u2026\u5509\uff01\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u4f4f\u53e3\u4e0d\u8bed\uff0c\u4f3c\u4e4e\u5bf9\u4e1c\u65b9\u94c1\u5982\u6b64\u4f5c\u98ce\uff0c\u751a\u662f\u60cb\u501f\u3002 \u3000\u3000\u88f4\u73cf\u9ed8\u7136\u4e0d\u54cd\uff0c\u5fcd\u4e0d\u4f4f\u53f9\u9053\uff1a\u201c\u552f\u5176\u5982\u6b64\uff0c\u6211\u624d\u89c9\u5f97\u4e1c\u65b9\u5144\u5f1f\u6bd5\u7adf\u4e0d\u5931\u4e3a\u540d\u95e8\u4e4b\u540e\uff0c\u70ed \u8840\u7537\u513f\uff0c\u4f60\u600e\u80fd\u5982\u6b64\u8f7b\u8511\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u65e5\u4e2d\u4e00\u9635\u5149\u8292\u95ea\u52a8\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u6b64\u523b\u201c\u795e\u624b\u201d\u6218\u98de\u5374\u5df2\u7a81\u7136\u5728\u957f\u8857 \u4e0a\u51fa\u73b0\u3002 \u3000\u3000\u8fd9\u6b63\u5982\u4e00\u65b9\u7855\u5927\u7684\u5c71\u77f3\uff0c\u7a81\u7136\u6295\u843d\u5728\u672c\u5df2\u6ce2\u6d6a\u91cd\u91cd\u7684\u6e56\u6c34\u91cc\uff0c\u201c\u5657\u901a\u201d\u4e00\u58f0\uff0c\u6d6a\u82b1\u56db \u6e85\u3002 \u3000\u3000\u6cb8\u817e\u4e86\u7684\u4eba\u7fa4\uff0c\u6b64\u523b\u66f4\u6cb8\u817e\u5230\u4e86\u9876\u70b9\uff0c\u4e1c\u65b9\u5144\u5f1f\u9762\u8272\u5fae\u53d8\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u795e\u8272\u8083\u7136\uff0c\u671b \u7740\u201c\u795e\u624b\u201d\u6545\u98de\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u8fd1\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u6bcf\u8de8\u4e00\u6b65\uff0c\u6684\u817e\u7684\u4eba\u7fa4\u4fbf\u6291\u6b62\u4e00\u4e9b\uff0c\u76f4\u5230\u4ed6\u8d70\u5230\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u9762\u524d\uff0c\u55a7\u817e\u7684\u4eba\u58f0 \u4fbf\u53c8\u5bc2\u7edd\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u5fae\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u6218\u5e84\u4e3b\u53ef\u662f\u6709\u4ec0\u4e48\u771f\u5b9e\u7684\u8bc1\u636e\u4e48\uff1f\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u51b7\u4e00\u7b11\uff0c\u76ee\u5149\u95ea\u7535\u822c\u626b\u5411\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u662f\u771f\u5730\u8981 \u8bc1\u636e\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6652\u7136\u4e00\u7b11\uff0c\u6d53\u7709\u8038\u52a8\uff0c\u7a81\u5730\u5389\u53f1\u4e00\u58f0\uff1a\u201c\u62ff\u8bc1\u636e\u6765\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u624b\u638c\u4e00\u6325\uff0c\u53ea\u89c1\u4e24\u6761\u5927\u6c49\uff0c\u631f\u6301\u7740\u4e00\u4e2a\u754f\u7f29\u7684\u6c49\u5b50\u81ea\u4eba\u4e1b\u4e2d\u8d70\u4e86\u51fa\u6765\uff0c \u201c\u795e\u624b\u201d\u6218\u98de\u5927\u559d\u9053\uff1a\u201c\u8fc7\u4e0d\u53bb\uff0c\u4f60\u53ef\u8ba4\u5f97\u6b64\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u754f\u7f29\u5730\u671b\u4e86\u201c\u9f99\u5f62\u516b\u638c\u201d\u4e00\u773c\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u5927 \u7237\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u3001\u6218\u98de\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u4e14\u7ad9\u5728\u8fd9\u91cc\uff0c\u5c06\u4f60\u4eb2\u773c\u6240\u89c1\u4e4b\u4e8b\uff0c\u5f53\u7740\u5929\u4e0b\u82f1\u96c4\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u5168\u8eab\u5267\u70c8\u5730\u98a4\u6296\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5c0f\u2026\u2026\u5c0f\u4eba\u2026\u2026\u4e0d\u2026\u2026\u6562\uff1b\u2026\u2026\u4ed6\u53ea\u89c9\u201d\u9f99\u5f62 \u516b\u638c\u201c\u6a80\u660e\u7684\u4e24\u9053\u773c\u795e\uff0c\u6709\u5982\u4e24\u67c4\u5229\u5251\u822c\u671b\u5230\u81ea\u5df1\u5fc3\u91cc\u3002\u201d\u795e\u624b\u201c\u6218\u98de\u9762\u8272\u4e00\u95ea\uff0c\u8f6c\u5411\u4e1c\u65b9 \u94c1\u9053\uff1a\u201c\u4e1c\u65b9\u5c11\u5821\u4e3b\u53ef\u80fd\u8d1f\u8d23\u6b64\u4eba\u7684\u5b89\u5168\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u6c89\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u4ee5\u8eab\u5bb6\u540d\u8a89\u4e3a\u4fdd\uff0c\u6b64\u4eba\u82e5\u6709\u534a\u5206\u635f\u4f24\uff0c\u552f\u6211\u4e1c\u65b9\u94c1\u662f\u95ee\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u56de\u9996\u9053\uff1a\u201c\u6709\u4e86\u4e1c\u65b9\u5c11\u5821\u4e3b\u4fdd\u62a4\uff0c\u4f60\u8fd8\u4e0d\u6562\u653e\u5fc3\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fc7\u4e0d\u53bb\u201d\u7ec8\u4e8e\u9f13\u8d77\u4e86\u52c7\u6c14\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u5c06\u90a3\u4e00\u756a\u8a00\u8bed\u53c8\u8bf4\u4e86\u4e00\u904d\uff0c\u4ed6\u8bed\u58f0\u867d\u4e0d\u5927\uff0c\u4f46 \u6ee1\u8857\u4e4b\u4eba\u5374\u90fd\u5bc2\u9759\u5982\u6b7b\uff0c\u51dd\u795e\u503e\u542c\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u59cb\u7ec8\u9762\u6c89\u5982\u6c34\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u4ece\u4ed6\u9762\u5bb9\u4e0a\u770b\u51fa\u4e00\u4e1d\u4ed6\u5fc3\u5e95 \u7684\u601d\u60f3\u4e0e\u610f\u5ff5\u3002 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u9762\u9762\u76f8\u89d1\uff0c\u9762\u5bb9\u7070\u767d\u3002 \u3000\u3000\u4f46\u5c0f\u697c\u4e0a\u7684\u88f4\u73cf\uff0c\u9762\u5bb9\u5374\u6bd4\u4ed6\u66f4\u7070\u767d\u51e0\u5206\u3002 \u3000\u3000\u5434\u9e23\u4e16\u4f4e\u58f0\u9053\uff1a\u201c\u518d\u8fc7\u7247\u523b\uff0c\u88f4\u5144\u4f60\u4fbf\u53ef\u6b65\u4e0b\u697c\u53bb\uff0c\u4e3a\u4eb2\u590d\u4ec7\u4e86\u3002\u201d \u3000\u3000\u88f4\u73cf\u5782\u9996\u9ed8\u7136\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u65b9\u81ea\u7f13\u7f13\u9053\uff1a\u201c\u6211\u53ea\u613f\u65e0\u4eba\u52a9\u6211\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u76ee\u4e2d\u53c8\u662f\u4e00\u7247\u5149\u8292\u95ea\u52a8\uff0c\u4ed6\u4e24\u4e2a\u8eab\u540e\u7684\u8881\u6cf8\u73cd\u5374\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u6211\u4e5f \u4e0d\u613f\u770b\u5230\u8fd9\u4e48\u591a\u4eba\u6765\u56f4\u6bb4\u4e00\u8001\u4eba\uff0c\u5373\u4f7f\u2026\u2026\u5509\uff0c\u5373\u4f7f\u4ed6\u662f\u6211\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\u3002\u201d \u3000\u3000\u88f4\u73cf\u56de\u89c2\u4e00\u773c\uff0c\u53ea\u89c9\u81ea\u5df1\u53ea\u6709\u5728\u8fd9\u5c0f\u5c0f\u7684\u5973\u5b69\u8eab\u4e0a\uff0c\u624d\u80fd\u5bfb\u83b7\u4e00\u4efd\u771f\u8bda\u7684\u4e86\u89e3\u4e0e\u540c\u60c5\u3002 \u53ea\u542c\u201c\u795e\u624b\u201d\u6218\u98de\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5404\u4f4d\u670b\u53cb\uff0c\u4f60\u4eec\u53ef\u66fe\u542c\u5230\u4ed6\u7684\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u4eba\u4e1b\u4e2d\u4e00\u9635\u6012\u559d\uff0c\u6218\u98de\u8f6c\u9996\u9053\uff1a\u201c\u6a80\u660e\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u5341\u4f59\u5e74\u524d\u90a3\u5927\u96ea\u4e4b\u591c\uff0c\u4f60\u53ef \u662f\u5230\u4e86\u4fdd\u5b9a\u57ce\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u6c89\u5982\u6b7b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4eba\u7fa4\u4e2d\u6012\u559d\u58f0\uff0c\u51e0\u53ef\u5c06\u4e24\u8fb9\u7684\u697c\u623f\u4ff1\u90fd\u4e3a\u4e4b\u9707\u574d\u3002 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u9762\u8272\u5927\u53d8\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u5374\u4e0d\u7981\u4e00\u6115\uff0c\u77ac\u53c8\u559d\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u5df2\u627f\u8ba4\u2018\u67aa \u5251\u65e0\u654c\u2019\u88f4\u6c0f\u53cc\u6770\u4e43\u662f\u88ab\u4f60\u6bd2\u624b\u6740\u6b7b\uff1f\u201d \u3000\u3000\u5c0f\u697c\u4e0a\u7684\u88f4\u73cf\u5fc3\u623f\u98a4\u6296\uff0c\u624b\u8db3\u51b0\u51b7\u3002 \u3000\u3000\u53ea\u542c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7f13\u7f13\u9053\uff1a\u201c\u5341\u4f59\u5e74\u524d\uff0c\u90a3\u5927\u96ea\u4e4b\u591c\uff0c\u5728\u4fdd\u5b9a\u57ce\u4e2d\u7684\u4eba\uff0c\u4f55\u6b62\u5343\u5343 \u4e07\u4e07\uff0c\u96be\u9053\u5c31\u5168\u90fd\u662f\u5bb3\u6b7b\u88f4\u6c0f\u53cc\u6770\u7684\u51f6\u624b\u5417\uff1f\u201d \u3000\u3000\u4eba\u4e1b\u4e2d\u7684\u6012\u559d\u53d8\u6210\u6f2b\u9a82\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u4ef0\u5929\u72c2\u7b11\u7740\u9053\uff1a\u201c\u597d\u65e0\u803b\u7684\u72e1\u8fa9\uff0c\u96be\u9053\u4f60\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7adf\u5df2\u4ef0\u5929\u72c2\u7b11\u8d77\u6765\uff0c\u4ed6\u8fd9\u4ee5\u5145\u6ee1\u771f\u6c14\u6240\u53d1\u51fa\u7684\u7b11\u58f0\u7acb\u523b\u5c06 \u201c\u795e\u624b\u201d\u6218\u98de\u7684\u7b11\u58f0\u538b\u5012\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6012\u559d\u9053\uff1a\u201c\u4f60\u7b11\u4e9b\u8d77\u4ec0\u4e48\uff1f\u54fc\u54fc\uff0c\u771f\u4e8f\u4f60\u76f4\u5230\u6b64\u523b\u8fd8\u7b11\u5f97\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7b11\u58f0\u560e\u7136\u800c\u987f\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u51ed\u7740\u4e00\u4e2a\u8d29\u592b\u8d70\u5352\u7684\u8bed\u8a00\uff0c\u4f60\u4fbf\u8bf4\u662f\u771f\u51ed \u5b9e\u636e\uff0c\u8001\u592b\u771f\u4e0d\u77e5\u4f60\u662f\u597d\u72e1\u6291\u6216\u662f\u611a\u8822\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u76ee\u5149\u56db\u626b\uff0c\u5927\u58f0\u9053\uff1a\u201c\u50cf\u8fd9\u6837\u7684\u8bc1\u4eba\uff0c\u8001\u592b\u968f\u65f6\u968f\u5730\u90fd\u53ef\u4ee5\u6536\u4e70\u6570\u5341\u4e2a\uff0c \u5404\u4f4d\u670b\u53cb\u4ff1\u662f\u660e\u773c\u4eba\uff0c\u96be\u9053\u5c31\u4fe1\u4e86\u4ed6\u7684\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u6012\u559d\u4e0e\u6f2b\u9a7e\u6e10\u6e10\u5e73\u606f\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u76ee\u5149\u4e00\u8f6c\uff0c\u76b1\u7709\u9053\uff1a\u201c\u51ed\u5fc3\u800c\u8bba\uff0c\u4f60\u7684\u786e\u7b97\u4e0d\u5f97\u662f\u771f\u51ed\u5b9e\u636e\uff0c\u6218\u5e84\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u622a\u53e3\u51b7\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u7b97\u4e0d\u5f97\u771f\u51ed\u5b9e\u636e\uff0c\u5982\u6b64\u8bf4\u6765\u4e8b\u9694\u7ecf\u5e74\uff0c\u9664\u4e86\u2018\u67aa\u5251\u65e0 \u654c\u2019\u4eba\u6b7b\u590d\u751f\uff0c\u4fbf\u518d\u65e0\u4eba\u80fd\u8bc1\u660e\u8fd9\u59d3\u6a80\u7684\u4fbf\u662f\u6740\u4eba\u7684\u51f6\u624b\u4e86\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u94c1\u6115\u4e86\u4e00\u6115\uff0c\u56de\u9996\u671b\u4e86\u671b\u4ed6\u7684\u5144\u5243\u4e1c\u65b9\u866c\u4e1c\u65b9\u6c5f\u3001\u4e1c\u65b9\u6e56\uff0c\u4e09\u4eba\u9762\u4e0a\u5404\u6709\u8868\u60c5\uff0c\u5374 \u4e5f\u90fd\u4e0d\u77e5\u9053\u8be5\u76f8\u4fe1\u8c01\u7684\u8bdd\u624d\u662f\u3002 \u3000\u3000\u5c0f\u697c\u4e0a\u7684\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u72e1\u733e\u7684\u8001\u4eba\uff01\u201d \u3000\u3000\u88f4\u73cf\u5ffd\u7136\u53f9\u9053\uff1a\u201c\u4e0d\u8fc7\u4ee5\u4e8b\u8bba\u4e8b\uff0c\u76f4\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u4eec\u5f53\u771f\u6ca1\u6709\u4e00\u4ef6\u771f\u6b63\u53ef\u4ee5\u5b9a\u4eba\u7f6a\uff0c \u5df2\u7684\u8bc1\u636e\uff0c\u82e5\u51ed\u8fd9\u4e9b\u83ab\u987b\u6709\u4e4b\u4e8b\uff0c\u4fbf\u8981\u7f6e\u4eba\u6b7b\u5730\uff0c\u5f53\u975e\u2026\u2026\u201d \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u51b7\u51b7\u622a\u53e3\u9053\uff1a\u201c\u88f4\u5144\uff0c\u4f60\u5fc3\u80a0\u4e5f\u672a\u514d\u592a\u4ec1\u539a\u4e86\u4e9b\uff0c\u5987\u4eba\u4e4b\u4ec1\uff0c\u5c82\u8db3 \u6210\u4e8b\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5446\u4e86\u534a\u664c\uff0c\u5fc3\u4e2d\u7a81\u5730\u5bf9\u5434\u9e23\u4e16\u7684\u8a00\u8bed\uff0c\u8d77\u4e86\u4e00\u9635\u8f7b\u5fae\u7684\u53cd\u611f\uff0c\u76ee\u5149\u4e0b\u671b\uff0c\u53ea\u89c1\u201c\u9f99 \u5f62\u516b\u638c\u201d\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u5f53\u5730\uff0c\u4f3c\u4e4e\u8d70\u4e0a\u4efb\u4f55\u9669\u6076\u7684\u98ce\u6d6a\uff0c\u90fd\u4e0d\u8db3\u4ee5\u5c06\u8fd9\u8001\u4eba\u51fb\u5012\u3002 \u3000\u3000\u826f\u4e45\u826f\u4e45\uff0c\u4e1c\u65b9\u94c1\u65b9\u81ea\u6c89\u58f0\u9053\uff0c\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6211\u5144\u5f1f\u867d\u662f\u5c40\u5916\u4e4b\u4eba\uff0c\u4f46\u4e5f\u4e0d\u5f97\u4e0d\u8bf4\u65ec\u516c \u9053\u4e4b\u8a00\uff0c\u82e5\u65e0\u771f\u51ed\u5b9e\u636e\uff0c\u8fd8\u671b\u5404\u4f4d\u4e09\u601d\uff0c\u83ab\u8981\u51a4\u6789\u4e86\u597d\u4eba\uff01\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u51b7\u7b11\u4e00\u58f0\uff0c\u65b9\u5f85\u8bf4\u8bdd\uff0c\u7a81\u542c\u4e00\u58f0\u5927\u559d\uff1a\u201c\u6211\u6709\u771f\u51ed\u5b9e\u636e\u3002\u201d \u3000\u3000\u4f17\u4eba\u9f50\u90fd\u4e00\u60ca\uff0c\u5343\u767e\u9053\u76ee\u5149\u968f\u4e4b\u671b\u53bb\uff0c\u53ea\u89c1\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\uff0c\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\uff0c\u4ee5 \u53ca\u7f57\u4e49\uff0c\u8fb9\u5c11\u884d\u56db\u4eba\uff0c\u5927\u6b65\u800c\u6765\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u4ff1\u662f\u201c\u5c24\u5f62\u516b\u638c\u201d\u7684\u4eb2\u4fe1\u5fc3\u8179\uff0c\u6b64\u523b\u7adf\u7136\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u4e0d\u4f46\u4f17\u4eba\u60ca\u5947\u8be7\u5f02\uff0c\u5c31 \u8fde\u201c\u795e\u624b\u201d\u6218\u98de\u4ea6\u89c9\u5927\u51fa\u610f\u5916\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u5927\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u67f3\u8f89\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u516b\u5366\u638c\uff0c\uff0c\u67f3\u8f89\u5374\u8fde\u671b\u90fd\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u81ea\u7ba1\u8d70\u5230\u201d\u795e\u624b\u201c\u6218\u98de\u8eab\u4fa7\uff0c\u53cc\u81c2\u4e00\u626c\uff0c\u6717\u58f0 \u5927\u547c\u9053\uff1a\u201c\u5404\u4f4d\u670b\u53cb\uff0c\u6211\u67f3\u8f89\u867d\u7136\u8ddf\u7740\u6a80\u660e\u5341\u6570\u5e74\uff0c\u4f46\u5374\u8fd8\u6709\u4e00\u4efd\u826f\u5fc3\uff0c\u4e8b\u5230\u4eca\u65e5\uff0c\u6211\u4e0d\u5f97 \u4e0d\u8bf4\u51e0\u53e5\u516c\u9053\u8bdd\u4e86\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u5251\u8f69\uff0c\u987b\u53d1\u7686\u5c55\uff0c\u4e1c\u65b9\u94c1\u6c89\u58f0\u9053\uff1a\u201c\u5927\u53d4\u6682\u4e14\u606f\u6012\uff0c\u4e14\u542c\u4ed6\u8bf4\u4e9b\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u8fd9\u540d\u95e8\u5c11\u5e74\u81f3\u4eca\u8a00\u8bed\u95f4\u5c1a\u4e0d\u80af\u5931\u793c\uff0c\u6a80\u660e\u5fc3\u4e2d\u4e0d\u7981\u5927\u662f\u611f\u6fc0\u3002 \u3000\u3000\u5239\u90a3\u95f4\u4eba\u7fa4\u9a9a\u52a8\u66f4\u5267\uff0c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u6717\u58f0\u9053\uff1a\u201c\u8fd9\u5341\u4f59\u5e74\u95f4\uff0c\u6a80\u660e\u867d\u662f\u4eab\u5c3d\u8363\u534e\uff0c\u4f46 \u662f\u4ed6\u4ea6\u662f\u98df\u4e0d\u77e5\u5473\uff0c\u7761\u4e0d\u5b89\u5bdd\uff0c\u867d\u7136\u662f\u505a\u8d3c\u5fc3\u865a\uff0c\u81ea\u4ece\u4ed6\u542c\u5230\u8fd9\u4f4d\u8d76\u8f66\u7684\u670b\u53cb\u2018\u8fc7\u4e0d\u53bb\u2019\u9732 \u9762\u4e4b\u540e\uff0c\u4ed6\u5c31\u60f3\u51fa\u5404\u79cd\u6076\u6bd2\u7684\u8ba1\u5212\uff0c\u6765\u5bf9\u4ed8\u2018\u6c5f\u5357\u540c\u76df\u2019\u2026\u2026\u201d \u3000\u3000\u4e00\u79cd\u88ab\u66f2\u4fae\u4e0e\u6b3a\u9a97\u7684\u611f\u89c9\uff0c\u4f7f\u5f97\u9547\u9759\u800c\u4ece\u5bb9\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\u6c14\u5f97\u8fde\u987b\u53d1\u90fd\u4e3a\u4e4b\u98a4\u6296\u8d77 \u6765\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u6ca1\u60f3\u5230\u81ea\u5df1\u5e73\u65e5\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u6b64\u523b\u7adf\u4f1a\u51fa\u5356\u81ea\u5df1\u3002 \u3000\u3000\u76db\u6012\u4e4b\u4e0b\uff0c\u8fd9\u4e00\u4ee3\u5927\u8c6a\u6012\u559d\u9053\uff1a\u201c\u5fd8\u6069\u8d1f\u4e49\u7684\u5974\u624d\uff01\u201d \u3000\u3000\u53cc\u81c2\u4e00\u4f38\uff0c\u5341\u6307\u5e76\u5c55\uff0c\u4fbf\u5f85\u5411\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u6251\u53bb\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u8eab\u5f62\u4e00\u52a8\uff0c\u6321\u5728\u4ed6\u8eab\u524d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u53ef\u5984\u52a8\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u98a4\u58f0\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u95f4\uff0c\u672c\u5df2\u5145\u6ee1\u52fe\u5fc3\u6597\u89d2\uff0c\u4e92\u76f8\u6b3a\u9a97\u4e4b\u4e8b\uff0c\u2018\u98de\u9f99\u9556 \u5c40\u2019\u4e0e\u2018\u6c5f\u5357\u540c\u76df\u2019\u52bf\u5df2\u4e0d\u80fd\u5e76\u5b58\uff0c\u6211\u8981\u60f3\u51fa\u5404\u79cd\u65b9\u6cd5\u6765\u5c06\u4e4b\u6d88\u706d\uff0c\u8fd9\u70b9\u6211\u7edd\u4e0d\u5426\u8ba4\uff0c\u4f46\u8c01 \u8981\u8bf4\u6211\u6a80\u660e\u5c31\u662f\u90a3\u6740\u4eba\u7684\u51f6\u624b\uff0c\u6211\u6a80\u660e\u4e0d\u501f\u4ee5\u6027\u547d\u4e0e\u4e4b\u76f8\u62fc\uff01\u201d \u3000\u3000\u4ed6\u795e\u60c5\u6fc0\u52a8\uff0c\u8a00\u8bed\u6fc0\u52a8\uff0c\u8bf4\u7684\u7adf\u4f3c\u4e4e\u5e76\u4e0d\u662f\u865a\u4f2a\u7684\u8a00\u8bed\u3002 \u3000\u3000\u5c0f\u697c\u4e0a\u7684\u88f4\u73cf\u5fc3\u4e2d\u4e3a\u4e4b\u4e00\u52a8\uff0c\u5434\u9e23\u4e16\u5374\u51b7\u7b11\u9053\uff1a\u201c\u597d\u4f1a\u505a\u4f5c\u7684\u5978\u5f92\uff0c\u60f3\u4e0d\u5230\u2018\u9f99\u5f62\u516b \u638c\u2019\u7adf\u662f\u5982\u6b64\u89d2\u8272\uff01\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u5411\u4ed6\u5fae\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u53ea\u8db3\u4ee5\u8bc1\u660e\u6a80\u660e\u6b64\u4eba\u7684\u597d\u72e1\u51f6\u6bd2\uff0c\u5374\u4e0d\u80fd \u8bc1\u660e\u4ed6\u4fbf\u662f\u5341\u4f59\u5e74\u524d\u90a3\u85cf\u5934\u85cf\u5c3e\u7684\u8499\u9762\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u76ee\u5149\u56db\u626b\uff0c\u4f17\u4eba\u4ff1\u90fd\u5c4f\u58f0\u9759\u6c14\uff0c\u51dd\u795e\u503e\u542c\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u7f13\u7f13\u63a5\u53e3\u9053\uff1a\u201c\u4f46\u6709\u4e00\u4e8b\uff0c\u5374\u53ef\u8bc1\u660e\u4ed6\u4fbf\u662f\u6740\u6b7b\u90a3\u2018\u67aa\u5251\u65e0\u654c\u2019\u88f4\u6c0f\u53cc\u6770\u7684\u51f6 \u624b\u3002\u201d \u3000\u3000\u4f17\u4eba\u5fcd\u4e0d\u4f4f\u7eb7\u7eb7\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u2026\u2026\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u9762\u8272\u51dd\u91cd\uff0c\u5c0f\u697c\u4e0a\u7684\u88f4\u73cf\u51e0\u5df2\u4e0d\u80fd\u81ea\u6301\u3002 \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9053\uff1a\u201c\u5404\u4f4d\u53ef\u8fd8\u8bb0\u5f97\uff0c\u6614\u5e74\u2018\u67aa\u5251\u65e0\u654c\u2019\u8eab\u6b7b\u90a3\u65e5\u6240\u4fdd\u7684\u73cd\u5b9d\u7ea2\u8d27\uff0c\u662f \u4e00\u4ef6\u4ec0\u4e48\u4e1c\u897f\uff1f\u6709\u4f55\u73cd\u8d35\u4e4b\u5904\uff1f\u201d \u3000\u3000\u4f17\u4eba\u6709\u7684\u832b\u7136\u4e0d\u77e5\u6240\u7b54\uff0c\u6709\u7684\u5374\u5df2\u4e71\u58f0\u9053\uff1a\u201c\u78a7\u7389\u87fe\u870d\u3002\u201d \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6c89\u58f0\u9053\uff1a\u201c\u67f3\u5144\u6240\u8bf4\uff0c\u53ef\u662f\u90a3\u80fd\u591f\u9884\u77e5\u5929\u6c14\u9634\u6674\u7684\u5f02\u5b9d\u2018\u78a7\u7389\u87fe\u870d\u2019\uff1f\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u2018\u78a7\u7389\u8d61\u870d\u2019\uff0c\u800c\u8fd9\u2018\u78a7\u7389\u87fe\u870d\u2019\uff0c\u6b64\u523b\u4fbf\u662f\u5728 \u8fd9\u2018\u9f99\u5f62\u516b\u638c\u2019\u6a80\u660e\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u56db\u4e0b\u7206\u8d77\u4e86\u4e00\u9635\u60ca\u5929\u52a8\u5730\u7684\u60ca\u547c\uff0c\u88f4\u73cf\u5fc3\u5934\u4e00\u61d4\uff0c\u53cc\u62f3\u7d27\u63e1\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5634\u89d2\u5374\u6cdb\u8d77 \u4e86\u4e00\u4e1d\u5f97\u610f\u7684\u5fae\u7b11\u3002 \u3000\u3000\u60ca\u8bb6\u8fc7\u540e\uff0c\u6012\u559d\u7acb\u8d77\u3002 \u3000\u3000\u201c\u641c\u4ed6\u8eab\u4e0a\u3002\u201d \u3000\u3000\u201c\u53eb\u4ed6\u5c06\u2018\u78a7\u7389\u87fe\u870d\u2019\u62ff\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u59d3\u6a80\u7684\uff0c\u4f60\u8eab\u4e0a\u82e5\u662f\u6ca1\u6709\u2018\u78a7\u7389\u87fe\u870d\u2019\uff0c\u4eca\u65e5\u6211\u4eec\u5c31\u653e\u8fc7\u4f60\uff0c\u5426\u5219\u6211\u4eec\u5c31\u5c06\u4f60\u6d3b\u6d3b\u6253 \u6b7b\uff0c\u4e3a\u5341\u4f59\u5e74\u524d\u90a3\u4e9b\u82f1\u96c4\u7684\u82f1\u7075\u590d\u4ec7\u3002\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9762\u5e26\u8be1\u7b11\uff0c\u51b7\u773c\u65c1\u89c2\uff0c\u51b7\u7b11\u7740\u9053\uff1a\u201c\u59d3\u6a80\u7684\u3001\u4f60\u663e\u7136\u4e0d\u662f\u51f6\u624b\uff0c\u4f60\u6562\u8ba9 \u4ed6\u4eec\u641c\u4e00\u641c\u8eab\u4e0a\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5446\u4e86\u534a\u664c\uff0c\u6012\u6781\u53cd\u7b11\uff0c\u5583\u5583\u9053\uff1a\u201c\u641c\u6211\u2026\u2026\u641c\u6211\u8eab\u4e0a\u2026\u2026\u201d \u3000\u3000\u7a81\u5730\u987b\u53d1\u7686\u626c\uff0c\u5389\u53f1\u9053\uff1a\u201c\u8c01\u6562\u641c\u6211\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5389\u559d\uff0c\u66f4\u662f\u6709\u5982\u6674\u5929\u9739\u96f3\uff0c\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u5f53\u771f\u6ca1\u6709\u4e00\u4eba\u6562\u5411\u4ed6\u8d70\u8fd1\u4e00\u6b65\u534a\u6b65\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u5251\u7709\u5fae\u76b1\uff0c\u5374\u89c1\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\u7a81\u5730\u4e00\u6b65\u63a0\u51fa\u3001\u76ee\u5149\u4e00\u626b\uff0c\u62b1\u62f3\u56db\u63d6\uff0c\u6717\u58f0\u9053\uff1a \u201c\u5404\u4f4d\u4e0e\u8fd9\u59d3\u6a80\u7684\u867d\u6709\u6df1\u4ec7\u5927\u6068\uff0c\u4f46\u8fd9\u2018\u78a7\u7389\u87fe\u870d\u2019\uff0c\u5374\u662f\u4e0e\u6211\u7f57\u4e49\u5173\u7cfb\u6700\u6df1\uff0c\u8fd9\u4e00\u4ef6\u6b66\u6797 \u7684\u9690\u79d8\u3001\u5404\u4f4d\u53ea\u662f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u59cb\u7ec8\u672a\u53d1\u4e00\u8a00\u7684\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\uff0c\u5374\u5728\u6b64\u523b\u8bf4\u51fa\u4e86\u8fd9\u60ca\u4eba\u4e4b\u8bed\uff0c\u4f17\u4eba\u5fc3\u4e2d\u4e0d\u89c9\u5927\u5947\u3002 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u76ee\u5149\u626b\u5904\uff0c\u53ea\u89c1\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u4e0a\u795e\u8272\u679c\u53c8\u4e00\u53d8\u3002 \u3000\u3000\u4e1c\u65b9\u6e56\u6717\u58f0\u9053\uff1a\u201c\u5144\u53f0\u53ea\u7ba1\u8bf4\u51fa\uff0c\u5728\u4e0b\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\u9053\uff1a\u201c\u8fd9\u2018\u78a7\u7389\u87fe\u870d\u2019\uff0c\u672c\u662f\u6dee\u5357\u4e00\u4f4d\u5de8\u5546\uff0c\u59d4\u6258\u6211\u4e49\u5144\uff0c\u2018\u65ad\u9b42\u5200\u2019 \u5b59\u658c\u62a4\u9001\u4e4b\u7269\uff0c\u201d\u6211\u4e49\u5144\u4e3a\u4e86\u6b64\u7269\uff0c\u4e0e\u6614\u5e74\u540d\u9707\u6c5f\u6e56\u7684\u7eff\u6797\u5de8\u76d7\u2018\u6dee\u9633\u4e09\u715e\u2019\u7ed3\u4e0b\u6df1\u4ec7\uff0c\u867d \u7136\u5200\u4f24\u8ffd\u547d\u8d75\u8001\u4e8c\uff0c\u5374\u88ab\u201c\u5c0f\u4e27\u95e8\u2018\u7a0b\u82f1\uff0c\u548c\u2019\u593a\u547d\u4e09\u90ce\u2018\u90d1\u6606\u708e\u903c\u5f97\u65e0\u5904\u5bb9\u8eab\uff0c\u8fd9\u624d\u5c06\u2019 \u78a7\u7389\u87fe\u870d\u2018\u8f6c\u4ea4\u7ed9\u2019\u67aa\u5251\u65e0\u6545\u2018\u62a4\u9001\uff01\u201d\u4ed6\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u6211\u4e49\u5144\u81f3\u4eca\u6d6a\u843d\u6c5f\u6e56\uff0c\u4e0d\u77e5\u751f\u6b7b \u4e0b\u843d\uff0c\u8ffd\u6839\u7a76\u5e95\uff0c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u6b64\u7269\uff1f\u662f\u4ee5\u6b64\u7269\u4e0e\u6211\u5e72\u7cfb\u5b9e\u662f\u6700\u6df1\uff0c\u662f\u4ee5\u2026\u2026\u201d \u3000\u3000\u4f17\u4eba\u51dd\u795e\u800c\u542c\uff0c\u4e00\u7247\u9759\u5bc2\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4ed6\u7f13\u7f13\u8f6c\u8fc7\u8eab\u5b50\u4e00\u9762\u5411\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u5389\u58f0 \u9053\uff1a\u201c\u4eca\u65e5\u6211\u5012\u8981\u641c\u4e00\u641c\u4f60\u7684\u8eab\u4e0a\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u4ed6\u5df2\u4e00\u4e2a\u7bad\u6b65\u5411\u6a80\u660e\u7a9c\u53bb\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u4e00\u626c\uff0c\u5288\u624b\u4e00\u638c\uff0c\u51fb\u5411\u4ed6\u80f8 \u819b\u3002 \u3000\u3000\u201c\u2018\u6444\u9b42\u5200\u201d\u7f57\u4e49\u53ea\u89c9\u524d\u80f8\u4e00\u80a1\u52b2\u98ce\u88ad\u6765\uff0c\u8eab\u4e0d\u7531\u4e3b\u5730\u8fde\u9000\u4e09\u6b65\uff0c\u8eab\u8eaf\u4e00\u633a\uff0c\u518d\u6b21\u6251 \u4e0a\u3002\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u5389\u53f1\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u8981\u547d\u4e86\uff1f\u201d \u3000\u3000\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\u8ddf\u968f\u4ed6\u5df2\u6709\u591a\u5e74\uff0c\u6b64\u523b\u4ed6\u867d\u5728\u6781\u6012\u4e4b\u4e0b\uff0c\u624b\u811a\u5fc5\u5b9a\u8fd8\u7559\u4e86\u513f\u5206\u60c5\u610f\uff0c\u888d \u8896\u4e00\u62c2\uff0c\u518d\u6b21\u9707\u9000\u4e86\u7f57\u4e49\u7684\u8eab\u5f62\u3002 \u3000\u3000\u4f17\u4eba\u5df2\u662f\u4e00\u7247\u55a7\u817e\uff0c\u7f57\u4e49\u8e09\u8dc4\u5730\u968f\u7740\u811a\u6b65\uff0c\u8f6c\u8eab\u9053\uff1a\u201c\u8fd9\u59d3\u6a80\u7684\u5c45\u7136\u8fd8\u6562\u52a8\u624b\uff0c\u5404\u4f4d\u670b \u53cb\uff0c\u8c01\u7ed9\u6211\u4e00\u4e2a\u516c\u9053\uff1f\u201d \u3000\u3000\u4f17\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u5df2\u6709\u6570\u5341\u4eba\u5411\u77f3\u9636\u4e0a\u51b2\u51fa\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u58f0\u97f3\u6012\u9a82\u7740\uff1a\u201c\u6253\u6b7b\u4ed6\uff0c\u518d\u6b21 \u641c\u4ed6\u8eab\u4e0a\uff01\u201d \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u867d\u7136\u65e9\u5df2\u5bf9\u6a80\u660e\u7684\u884c\u4e3a\u53d1\u751f\u6000\u7591\uff0c\u4f46\u89c1\u4e86\u8fd9\u79cd\u60c5\u51b5\uff0c\u5fc3\u5934\u5374\u4e0d\u7981\u6fc0\u8d77\u4e86\u4e00\u4efd\u4fa0 \u4e49\u4e4b\u6c14\uff0c\u53ea\u5915\u9633\u4f59\u8f89\u4e2d\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7684\u8eab\u8eaf\u867d\u7136\u4ecd\u662f\u90a3\u4e48\u5a01\u6b66\u800c\u633a\u76f4\uff0c\u4f46\u662f\u5728\u8fd9\u5df2\u662f \u4f17\u53db\u4eb2\u79bb\u3001\u65e5\u66ae\u9014\u7a77\u7684\u6b66\u6797\u5927\u8c6a\u7709\u5b57\u4e4b\u95f4\uff0c\u5374\u5df2\u663e\u9732\u51fa\u4e00\u79cd\u60b2\u54c0\u4e0e\u67aa\u51c9\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u5b81\u53ef\u8eab\u6b7b\uff0c\u4e5f\u4e0d\u613f\u8fd9\u4e9b\u4eba\u7684\u624b\u638c\u89e6\u53ca\u81ea\u5df1\u7684\u8863\u886b\uff0c\u6b64\u523b\u4ed6\u5b9e\u5df2\u62b1\u5b9a\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u53ea\u8981\u8fd9 \u4e9b\u4eba\u51b2\u4e0a\u53f0\u9636\u4e00\u6b65\uff0c\u4ed6\u4fbf\u8981\u4ee5\u522b\u4eba\u7684\u9c9c\u8840\uff0c\u6765\u704c\u6e89\u81ea\u5df1\u80f8\u4e2d\u7684\u6124\u6012\uff0c\u4ee5\u522b\u4eba\u7684\u5c38\u8eab\uff0c\u6765\u4f5c\u81ea \u5df1\u7684\u575f\u5893\u3002 \u3000\u3000\u5c0f\u697c\u4e0a\u7684\u88f4\u73cf\uff0c\u6b64\u523b\u4ea6\u662f\u70ed\u8840\u6cb8\u817e\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u9053\uff1a\u201c\u88f4\u5144\uff0c\u6b64\u523b\u5df2\u662f\u5077\u6838\u73b0 \u8eab\u7684\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u8bed\u72b9\u672a\u4e86\uff0c\u7a81\u89c1\u4eba\u4ece\u4e2d\u98de\u8d77\u4e00\u6761\u9ed1\u5f71\uff0c\u5ea7\u9e70\u96bc\u822c\u98de\u8fc7\u90a3\u6570\u5341\u4e2a\u6124\u6012\u7684\u4eba\u7fa4\uff0c\u843d\u5728\u201c\u9f99\u5f62 \u516b\u638c\u201d\u8eab\u524d\uff0c\u53e3\u4e2d\u5389\u559d\u4e00\u58f0\uff0c\u51fa\u624b\u5982\u51e4\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u4e00\u628a\u62e7\u4f4f\u4e86\u5f53\u5148\u51b2\u6765\u4e4b\u4eba\u7684\u81c2\u8180\uff0c\u624b\u81c2 \u4e00\u626c\uff0c\u968f\u7740\u8fd9\u4e00\u58f0\u5389\u559d\uff0c\u5c06\u6b64\u4eba\u76f4\u629b\u4e86\u51fa\u53bb\uff0c\u201c\u7830\u201d\u5730\u629b\u5728\u7b2c\u4e8c\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u8d77\u5411\u540e\u51b2\u51fa\u5341\u6570\u6b65\uff0c\u7acb\u523b\u5c06\u540e\u9762\u7684\u4eba\u6f6e\u4e5f\u649e\u5f97\u968f\u4e4b\u5411\u540e\u8dcc\u5012\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6d53\u7709\u4e00\u5c55\uff0c\u5927\u559c\u9053\uff1a\u201c\u8c79\u513f\uff0c\u4f60\u2026\u2026\u4f60\u7adf\u6765\u4e86\uff01\u201d \u3000\u3000\u4f17\u53db\u4eb2\u79bb\uff0c\u65e5\u66ae\u7a77\u9014\u4e4b\u4e2d\uff0c\u4ed6\u6bd5\u7adf\u770b\u5230\u4e86\u4e00\u4e2a\u4eb2\u4eba\uff0c\u4e00\u79cd\u6fc0\u52a8\uff0c\u4f7f\u5f97\u8fd9\u8001\u4eba\u51e0\u4e4e\u843d\u4e0b\u6cea \u6765\uff0c\u5fc3\u5934\u4ea6\u4e0d\u77e5\u662f\u6b23\u559c\uff0c\u662f\u611f\u6fc0\uff0c\u6291\u6216\u662f\u60b2\u54c0\uff01 \u3000\u3000\u8fd9\u5e7f\u989d\u6df1\u816e\uff0c\u76ee\u5149\u5982\u9e70\uff0c\u884c\u52a8\u5374\u77eb\u5065\u5982\u8c79\u7684\u5c11\u5e74\uff0c\u9762\u8272\u4ecd\u662f\u4e00\u7247\u6df1\u6c89\uff0c\u5de6\u624b\u75be\u4f38\uff0c\u95ee\u7535 \u822c\u634f\u4f4f\u4e86\u53e6\u4e00\u4eba\u7684\u65f6\u95f4\u7684\u201c\u66f2\u6c60\u201d\u5927\u7a74\uff0c\u53f3\u624b\u659c\u6284\uff0c\u6284\u8d77\u4e86\u6b64\u4eba\u7684\u819d\u76d6\uff0c\u53e3\u4e2d\u518d\u6b21\u5927\u559d\u4e00 \u58f0\uff0c\u7adf\u5c06\u6b64\u4eba\u7b14\u76f4\u4e3e\u8d77\u3002 \u3000\u3000\u4f17\u4eba\u4e00\u9635\u5927\u4e71\uff0c\u60c5\u4e0d\u81ea\u7981\u5730\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u77eb\u5065\u7684\u5c11\u5e74\uff0c\u201c\u82d7\u8c79\u201d\u5389\u559d\u9053\uff1a\u201c\u8c01\u6562\u518d\u52a8\u4e00\u52a8\uff01\u201d \u3000\u3000\u5915\u9633\u4e4b\u4e0b\uff0c\u6620\u7740\u4ed6\u5145\u6ee1\u4e86\u529b\u91cf\u7684\u8eab\u8eaf\uff0c\u6ee1\u542b\u6740\u673a\u7684\u9762\u5bb9\uff0c\u6563\u53d1\u7740\u91ce\u517d\u4e00\u822c\u5149\u8292\u7684\u773c \u775b\u2026\u2026\u5f53\u771f\u6709\u5982\u4e00\u53ea\u5486\u54ee\u5728\u6df1\u5c71\u4e2d\u7684\u731b\u517d\u3002 \u3000\u3000\u4e1c\u65b9\u94c1\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u597d\u4e00\u6761\u6c49\u5b50\uff01\u201d \u3000\u3000\u201c\u2018\u6444\u9b42\u5200\u201d\u7f57\u4e49\u8f69\u7709\u5927\u9a82\u9053\uff1a\u201c\u755c\u7272\uff0c\u4f60\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82d7\u8c79\u5927\u559d\u4e00\u58f0\uff0c\u7a81\u5730\u98de\u8d77\u4e00\u817f\uff0c\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\u5fc3\u5934\u4e00\u8df3\uff0c\u659c\u8eab\u9519\u6b65\uff0c\u54ea\u77e5\u82d7\u8c79\u7b2c\u4e00\u817f \u5c1a\u672a\u4e0b\uff0c\u7b2c\u4e8c\u817f\u5df2\u8ddf\u7740\u8e22\u51fa\uff0c\u8eab\u8eaf\u6709\u5982\u98ce\u8f66\u4e00\u822c\uff0c\u8eab\u53f3\u4e00\u8f6e\uff0c\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\u60e8\u547c\u4e00\u58f0\uff0c\u8eab \u8eaf\u6709\u5982\u65ad\u7ebf\u7684\u98ce\u7b5d\uff0c\u5411\u5916\u98de\u51fa\u4e00\u4e08\uff0c\u6251\u5730\u843d\u5728\u5730\u4e0a\uff01 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u9762\u8272\u5fae\u53d8\uff0c\u5927\u58f0\u9053\uff1a\u201c\u597d\u529f\u592b\uff0c\u6211\u6218\u98de\u9886\u6559\u9886\u6559\uff01\u201d \u3000\u3000\u82d7\u8c79\u53e3\u4e2d\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u53cc\u624b\u4e00\u6c89\uff0c\u628a\u638c\u4e2d\u90a3\u5df2\u88ab\u4ed6\u5236\u4f4f\u7684\u4eba\u8eab\uff0c\u5411\u6218\u98de\u7b14\u76f4\u5730\u7838\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u8eab\u5f62\u4fa7\u8ba9\uff0c\u5de6\u638c\u4e0a\u6258\uff0c\u63a5\u8fbe\u6b64\u4eba\uff0c\u53cd\u624b\u629b\u5728\u80cc\u540e\uff0c\u53f3\u638c\u659c\u659c\u6325\u51fa\uff0c\u6070\u5de7\u63a5 \u82d7\u8c79\u7684\u4e00\u638c\u3002 \u3000\u3000\u4e24\u638c\u76f8\u63a5\uff0c\u82d7\u8c79\u53ea\u89c9\u638c\u5fc3\u4e00\u70ed\uff0c\u8eab\u8eaf\u5927\u9707\uff0c\u6251\u5730\u5750\u5230\u5730\u4e0a\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6545\u98de\u5374\u53ea\u89c9\u6709\u4e00\u80a1\u6d2a\u6c34\u822c\u7684\u5927\u529b\uff0c\u5728\u4ed6\u624b\u638c\u4e0a\u4e00\u51fb\uff0c\u4f7f\u5f97\u4ed6\u8eab\u4e0d\u7531\u4e3b\u8981\u5411\u540e\u9000 \u53bb\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u638c\u529b\u4e00\u521a\u4e00\u67d4\uff0c\u201c\u795e\u624b\u201d\u6545\u98de\u867d\u7136\u5185\u529b\u7ef5\u5bb9\uff0c\u4f46\u8fd9\u5c11\u5e74\u8eab\u8eaf\u4e4b\u4e2d\uff0c\u5374\u542b\u8574\u7740\u4e00\u79cd \u91ce\u517d\u822c\u7684\u539f\u59cb\u4e4b\u529b\uff0c\u8eab\u8eaf\u65b9\u5012\uff0c\u7acb\u523b\u633a\u8170\u7ad9\u8d77\u3002 \u3000\u3000\u201c\u9f99\u5f62\u4eba\u638c\u201d\u76b1\u7709\u6c89\u58f0\u9053\uff1a\u201c\u8c79\u513f\uff0c\u4f60\u53ef\u53d7\u4e86\u5185\u4f24\uff1f\u201d \u3000\u3000\u82d7\u8c79\u6c89\u58f0\u9053\uff1a\u201c\u65e0\u59a8\uff01\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u547c\u547c\u4e24\u638c\uff0c\u5206\u51fb\u6218\u98de\u80f8\u819b\u4e0e\u8170\u80ef\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u957f\u9aef\u4e00\u98d8\uff0c\u8fd8\u51fb\u4e00\u62db\uff0c\u4ed6\u65b9\u624d\u672c\u5f85\u4e00\u62db\u4e4b\u4e0b\uff0c\u4fbf\u5c06\u8fd9\u5c11\u5e74\u7f6e\u4e4b\u6b7b\u5730\uff0c\u54ea\u77e5 \u8fd9\u5c11\u5e74\u7ade\u6709\u5982\u6b64\u7684\u6f5c\u529b\uff01 \u3000\u3000\u970e\u773c\u4e4b\u4e2d\uff0c\u4e94\u62db\u7acb\u8fc7\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u5149\u626b\u8fc7\uff0c\u53ea\u671b\u6709\u4eba\u4e3a\u4ed6\u63a5\u624b\uff0c\u8981\u77e5\u4ee5\u201c\u795e\u624b\u201d\u6218 \u98de\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u4e0e\u2018\u9f99\u5f62\u516b\u638c\u201c\u4e00\u62fc\u5c1a\u53ef\uff0c\u4e0e\u8fd9\u540d\u4e0d\u89c1\u76ce\u4f20\u7684\u5c11\u5e74\u52a8\u624b\uff0c\u5373\u4f7f\u4ed6\u80dc\u4e86\uff0c\u4e5f\u4e0d \u5149\u8363\uff0c\u4f55\u51b5\u4ed6\u6b64\u523b\u4ea4\u624b\u4e4b\u4e0b\uff0c\u8fd8\u6ca1\u6709\u4ec0\u4e48\u5236\u80dc\u7684\u628a\u63e1\u3002\u54ea\u77e5\u4ed6\u56fd\u5149\u626b\u52a8\u4e4b\u4e0b\uff0c\u7adf\u53d1\u89c9\u4eba\u4eba\u4ff1 \u5728\u8896\u624b\u65c1\u89c2\uff0c\u5c31\u8fde\u65b9\u624d\u90a3\u80a1\u51b2\u52a8\u7684\u4eba\u7fa4\uff0c\u6b64\u523b\u90fd\u5df2\u9759\u4e0b\u6765\u3002\u4ed6\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u5728\u6b66\u6797\u4e2d\u7684\u5730 \u4f4d\uff0c\u7ade\u662f\u5982\u6b64\u5b64\u72ec\uff0c\u6ca1\u6709\u670b\u53cb\uff0c\u6709\u7684\u4ff1\u662f\u5974\u624d\uff0c\u826f\u5df1\u82e5\u662f\u5230\u4e86\u7a77\u9014\u65e5\u66ae\u4e4b\u65f6\uff0c\u8fd9\u4e9b\u5974\u624d\u5bf9\u5f85 \u81ea\u5df1\uff0c\u8fd8\u4e0d\u662f\u6b63\u662f\u548c\u201d\u516b\u5366\u638c\u201c\u67f3\u8f89\u7b49\u4eba\u5bf9\u5f85\u6a80\u660e\u4e00\u6837\uff01\u4ed6\u5de6\u624b\u4e00\u62db\u201d\u5206\u82b1\u62c2\u67f3\u201c\uff0c\u53f3\u624b\u4e00 \u62db\u201d\u6a2a\u626b\u5343\u519b\u201c\uff0c\u8fd9\u4e24\u62db\u4e00\u521a\u4e00\u67d4\uff0c\u4e00\u62d9\u4e00\u5de7\uff0c\u529b\u91cf\u3001\u62db\u5f0f\uff0c\u4ff1\u662f\u5927\u4e0d\u76f8\u540c\uff0c\u4f46\u4ed6\u7adf\u5728\u540c\u65f6 \u53d1\u51fa\uff0c\u7528\u5f97\u679c\u7136\u5a01\u98ce\u516b\u9762\uff0c\u4f46\u662f\u5728\u4ed6\u5fc3\u5e95\u6df1\u5904\uff0c\u5374\u5df2\u5347\u8d77\u4e86\u4e00\u9635\u8427\u7d22\u843d\u5bde\u4e4b\u611f\u3002\u82d7\u8c79\u76ee\u5c04\u7cbe \u5149\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u8f6c\u77ac\u95f4\u4fbf\u4e0e\u201d\u795e\u624b\u201c\u6218\u98de\u529b\u62fc\u4e86\u6570\u5341\u62db\u3002\u8fd9\u5c11\u5e74\u62db\u5f0f\u5e76\u4e0d\u5341\u5206\u7cbe\u5999\uff0c\u5185\u529b\u66f4 \u4e0d\u5341\u5206\u6df1\u539a\uff0c\u4f46\u662f\u4ed6\u5374\u6709\u4e00\u79cd\u522b\u4eba\u6ca1\u6709\u7684\u527d\u608d\u800c\u731b\u9e37\u7684\u529b\u91cf\uff0c\u53ea\u8981\u4ed6\u4e00\u548c\u4eba\u5bb6\u52a8\u624b\uff0c\u90a3\u4e48\u4ed6 \u7684\u8eab\u4f53\u3001\u5fc3\u667a\u3001\u7075\u9b42\u3001\u6027\u547d\uff0c\u751a\u81f3\u6bdb\u53d1\uff0c\u5374\u50cf\u662f\u4ec5\u4ec5\u4e3a\u4e86\u8fd9\u6b21\u4ea4\u624b\u800c\u751f\uff0c\u518d\u6ca1\u6709\u4e00\u4e1d\u4e00\u6beb\u7684 \u4fdd\u7559\u3002\u8fd9\u79cd\u5148\u5929\u7684\u539f\u59cb\u529b\u91cf\uff0c\u4e0d\u4f46\u5f25\u8865\u4e86\u4ed6\u6b66\u529f\u7684\u4e0d\u8db3\uff0c\u800c\u4e14\u8fd8\u4f7f\u5f97\u4ed6\u7684\u654c\u4eba\uff0c\u5fc3\u4e2d\u65e0\u6cd5\u4e0d \u751f\u51fa\u4e00\u79cd\u754f\u60e7\u4e4b\u611f\uff01\u7fa4\u8c6a\u8d8a\u770b\u8d8a\u89c9\u60ca\u5947\uff0c\u201d\u516b\u5366\u638c\u201c\u67f3\u8f89\u3001\u201d\u5feb\u9a6c\u795e\u5200\u201c\u9f9a\u6e05\u6d0b\u3001\u8fb9\u5c11\u884d\u4ff1 \u90fd\u8fdc\u8fdc\u8d70\u5230\u4e00\u65c1\uff0c\u552f\u6050\u4ed6\u4f1a\u627e\u5230\u81ea\u5df1\u5934\u4e0a\u3002\u90a3\u201d\u8fc7\u4e0d\u53bb\u201c\u66f4\u662f\u5df2\u88ab\u9a87\u5f97\u56db\u80a2\u53d1\u8f6f\uff0c\u8e72\u5728\u77f3\u9636 \u65c1\uff0c\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u4e86\u3002\u5929\u8272\u6e10\u9eef\uff0c\u665a\u98ce\u6e10\u5bd2\uff0c\u201d\u795e\u624b\u201c\u6218\u98de\u7684\u76ee\u5149\u8d8a\u626b\u201d\u8d8a\u662f\u8427\u7d22\uff0c\u82d7\u8c79\u7684 \u76ee\u5149\u8d8a\u6253\u8d8a\u662f\u5c16\u9510\u660e\u4eae\u3002\u53ea\u89c1\u4ed6\u4e00\u638c\u51fb\u51fa\uff0c\u5168\u8eab\u7684\u529b\u9053\u968f\u4e4b\u51fb\u51fa\uff0c\u5168\u529b\u7684\u610f\u5fd7\u4e5f\u968f\u4e4b\u51fb\u51fa\uff0c \u6709\u65f6\u7eb5\u7136\u662f\u8981\u540c\u5f52\u4e8e\u5c3d\u7684\u62db\u5f0f\uff0c\u4ed6\u51fb\u51fa\u65f6\u4e5f\u4e1d\u6beb\u6ca1\u6709\u8003\u8651\uff0c\u4eff\u4f5b\u53ea\u8981\u80fd\u5c06\u5bf9\u624b\u6253\u6b7b\uff0c\u81ea\u5df1\u7eb5 \u7136\u966a\u7740\u6b7b\u53bb\uff0c\u4e5f\u6ca1\u6709\u5173\u7cfb\u3002\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u6e10\u6e10\u76b1\u8d77\uff0c\u7a81\u5730\u5927\u559d\u4e00\u58f0\uff0c\u53f3\u638c\u5168\u529b\u51fb\u51fa\uff0c\u5168 \u65e0\u82b1\u62db\u5de7\u5f0f\uff0c\u4ec5\u662f\u521a\u731b\u771f\u529b\uff0c\u5de6\u624b\u4e00\u634b\uff0c\u5374\u5c06\u81ea\u5df1\u9894\u4e0b\u7684\u957f\u9aef\u5377\u8d77\u54ac\u5728\u7259\u91cc\uff0c\u5de6\u817f\u968f\u4e4b\u8e22 \u51fa\uff0c\u5de6\u638c\u7acb\u5373\u51fb\u53bb\uff01\u82d7\u8c79\u4fa7\u8eab\u4e00\u8ba9\uff0c\u7fa4\u8c6a\u76ee\u5149\u52a8\u5904\uff0c\u77e5\u9053\u8fd9\u201c\u795e\u624b\u201d\u6218\u98de\u6b64\u523b\u4e5f\u52a8\u4e86\u62fc\u547d\u4e4b \u610f\uff0c\u6709\u4e9b\u4eba\u8f83\u4e3a\u51b7\u9759\uff0c\u65e9\u5df2\u5f04\u6765\u4e00\u4e9b\u706b\u628a\u706f\u7b3c\uff0c\u9ad8\u9ad8\u6311\u8d77\uff0c\u6b64\u523b\u5915\u9633\u8fd8\u672a\u5168\u843d\uff0c\u8fd9\u4e9b\u706f\u7b3c\u706b \u628a\u770b\u6765\u4e5f\u751a\u662f\u660f\u9eef\uff0c\u5c31\u4e00\u5982\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u7684\u9762\u8272\u4e00\u6837\u3002\u4e94\u5341\u62db\u867d\u8fc7\uff0c\u4f46\u4e5f\u4e0d\u8fc7\u53ea\u662f\u7247\u523b \u95f4\u4e8b\uff0c\u524d\u9762\u7684\u4eba\u7fa4\u4e2d\u867d\u5728\u5c4f\u606f\u800c\u89c2\uff0c\u540e\u9762\u7684\u4eba\u7fa4\u5374\u8d77\u4e86\u4e00\u9635\u9a9a\u52a8\u3002\u8fd9\u9a9a\u52a8\u8513\u5ef6\u5f02\u5e38\u4e4b\u5feb\uff0c\u4e0d \u77e5\u662f\u8c01\uff0c\u84e6\u5730\u5927\u58f0\u547c\u558a\u9053\uff1a\u201c\u88f4\u5927\u5148\u751f\u6765\u4e86\uff01\u201d \u3000\u3000\u7acb\u523b\u6709\u65e0\u6570\u58f0\u6b22\u547c\u968f\u4e4b\u54cd\u8d77\u3002 \u3000\u3000\u201c\u88f4\u5927\u5148\u751f\u6765\u4e86\u2026\u2026\u88f4\u5927\u5148\u751f\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u3001\u4e1c\u65b9\u5144\u5f1f\u3001\u751a\u81f3\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u7b49\u4eba\uff0c\u9762\u5bb9\u4ff1\u90fd\u4e00\u53d8\uff0c\u76ee\u5149\u50cf\u662f\u53d7\u4e86\u4ec0 \u4e48\u9b54\u529b\u7684\u5438\u5f15\u822c\uff0c\u4e00\u8d77\u968f\u4e4b\u671b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u7fa4\u867d\u5728\u52a8\u4e71\uff0c\u5374\u6e10\u6e10\u5411\u4e24\u8fb9\u5206\u5f00\uff0c\u8ba9\u4e86\u4e00\u6761\u901a\u9053\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u4e0e\u82d7\u8c79\u7684\u640f\u6597\u518d\u731b\u70c8\uff0c\u6b64\u523b\u4e5f\u6ca1\u6709\u4eba\u518d\u53bb\u770b\u4e0a\u4e00\u773c\u3002 \u3000\u3000\u4eba\u7fa4\u6f6e\u6c34\u822c\u5206\u5f00\u4e00\u6761\u901a\u8def\uff0c\u7b14\u76f4\u5730\u901a\u5411\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4ee5\u53ca\u4e1c\u65b9\u5144\u5f1f\u4ec1\u7acb\u7684\u77f3\u9636\u3002 \u3000\u3000\u5915\u9633\u4e00\u9eef\uff0c\u706b\u5149\u6e10\u4eae\u3002 \u3000\u3000\u665a\u98ce\u95ea\u52a8\u7740\u706b\u5149\uff0c\u706b\u5149\u70ab\u8000\u7740\u91d1\u9ec4\u800c\u5fae\u7ea2\u7684\u5f69\u8272\u3002 \u3000\u3000\u8fd9\u95ea\u52a8\u7740\u91d1\u9ec4\u800c\u5fae\u7ea2\u7684\u5f69\u8272\uff0c\u6b64\u523b\uff0c\u4fbf\u7167\u5230\u4e86\u88f4\u73cf\u7684\u8138\u4e0a\u3002 \u3000\u3000\u5343\u767e\u9053\u76ee\u5149\uff0c\u968f\u7740\u4ed6\u811a\u6b65\u79fb\u52a8\u7740\u3002 \u3000\u3000\u4ed6\u811a\u6b65\u6c89\u91cd\u800c\u7f13\u6162\u3002 \u3000\u3000\u671f\u5f85\uff0c\u4e5f\u4e0d\u8fc7\u53ea\u662f\u4e3a\u4e86\u8fd9\u4e00\u5239\u90a3\u7684\u5230\u6765\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u5bf9\u6728\u7acb\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u2014\u2014\u4e5f\u8bb8\u4ec5\u4ec5\u5728\u970e\u65f6\u4e4b\u95f4\u3002 \u3000\u3000\u7a81\u5730\uff0c\u56db\u4e0b\u7206\u51fa\u4e00\u58f0\u9707\u8033\u7684\u547c\u558a\uff0c\u878d\u5408\u7740\u6124\u6012\u3001\u5174\u594b\u3001\u52a9\u5a01\u4e0e\u5f97\u610f\u7684\u547c\u558a\uff0c\u8fd9\u547c\u558a\u4e4b \u58f0\uff0c\u6839\u672c\u6bcb\u9700\u5b57\u53e5\uff0c\u522b\u4eba\u4e5f\u542c\u5f97\u51fa\u6765\u3002 \u3000\u3000\u7acb\u5728\u88f4\u73cf\u8eab\u540e\u7684\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\uff0c\u76ee\u4e2d\u5149\u7130\u4e00\u95ea\uff0c\u6025\u884c\u513f\u6b65\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6a80\u660e\uff0c\u4f60 \u53ef\u77e5\u9053\u6b64\u523b\u7acb\u5728\u4f60\u9762\u524d\u7684\u4eba\u662f\u8c01\u4e48\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u76ee\u5149\u4e0d\u77ac\uff0c\u671b\u4e5f\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u53ea\u7ba1\u6c89\u58f0\u9053\uff1a\u201c\u597d\u597d\uff0c\u4f60\u6765\u4e86\uff0c\u4f60\u7ec8\u4e8e\u6765 \u4e86\uff01\u201d\u88f4\u73cf\u6697\u4e2d\u4e00\u54ac\u7259\u5173\uff0c\u7d27\u54ac\u7740\u7259\u9f7f\uff0c\u4f7f\u5f97\u4ed6\u9762\u4e0a\u7684\u808c\u8089\u4e00\u9635\u98a4\u52a8\uff0c\u4ed6\u4e00\u5b57\u4e00\u5b57\u5730\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u6211\u7ec8\u4e8e\u6765\u4e86\uff01\u201d \u3000\u3000\u6a80\u660e\u6d53\u7709\u4e00\u626c\uff0c\u7a81\u5730\u5927\u559d\u9053\uff1a\u201c\u4f60\u6765\u4f5c\u4ec0\u4e48\u3001\u4f60\u662f\u8981\u6765\u5bfb\u6211\u590d\u4ec7\u7684\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u76ee\u5149\u575a\u5b9a\u5730\u671b\u7740\u4ed6\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u53ea\u95ee\u4f60\u4e00\u53e5\uff0c\u6211\u7239\u7239\u53ef\u662f\u6b7b\u5728\u4f60\u624b\u4e0a\uff1f\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u53cc\u62f3\u7d27\u63e1\uff0c\u80f8\u819b\u8d77\u4f0f\uff0c\u82b1\u767d\u7684\u957f\u9aef\uff0c\u4e0d\u4f4f\u968f\u98ce\u98d8\u62c2\u3002 \u3000\u3000\u88f4\u73cf\u4ecd\u5728\u671b\u7740\u4ed6\uff0c\u76ee\u5149\u66f4\u6df1\u8fdc\uff0c\u66f4\u575a\u5b9a\u3002 \u3000\u3000\u5608\u4e71\u518d\u4e00\u6b21\u5e73\u606f\uff0c\u957f\u957f\u7684\u8857\u9053\uff0c\u5343\u767e\u4eba\u5934\uff0c\u53ea\u542c\u4e00\u9635\u547c\u5438\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u5343\u767e\u9053\u76ee\u5149\uff0c \u5ffd\u800c\u671b\u7740\u88f4\u73cf\uff0c\u5ffd\u800c\u671b\u7740\u201c\u9f99\u5f62\u516b\u638c\u201d\u3002 \u3000\u3000\u9759\u5bc2\u3001\u9759\u5bc2\u3001\u9759\u5bc2\u2026\u2026 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u547c\u5438\u7a81\u5730\u505c\u6b62\uff0c\u80f8\u819b\u5411\u524d\u4e00\u633a\uff0c\u81ea\u7259\u7f1d\u4e2d\u5410\u51fa\u4e24\u4e2a\u5b57\uff0c\u4e24\u4e2a\u60ca\u5fc3\u52a8\u9b44\u7684 \u5b57\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u88f4\u73cf\u5168\u8eab\u4e00\u9707\uff0c\u53ea\u89c9\u4eff\u4f5b\u6709\u4e00\u67c4\u5343\u94a7\u5de8\u9524\uff0c\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u78b0\u5730\uff0c\u51fb\u5728\u4ed6\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u56db\u4e0b\u9739\u96f3\u53c8\u8d77\uff0c\u5341\u91cc\u4ee5\u5916\u7684\u4eba\uff0c\u90fd\u53ef\u4ee5\u542c\u5230\u8fd9\u4e00\u9635\u6012\u543c\u3002 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u795e\u8272\u4e00\u53d8\uff0c\u5012\u9000\u4e09\u6b65\u3002 \u3000\u3000\u82d7\u8c79\u4e00\u6b65\u63a0\u5230\u6a80\u660e\u8eab\u4fa7\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u53cc\u76ee\u4e00\u4eae\uff0c\u201c\u795e\u624b\u201d\u6218\u98de\u6d53\u7709\u7acb\u626c\u3002 \u3000\u3000\u88f4\u73cf\u7a81\u5730\u8f6c\u8fc7\u8eab\u6765\uff0c\u624b\u638c\u7f13\u7f13\u4e00\u626c\uff0c\u8f7b\u8f7b\u4e00\u6325\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u8bf7\u9759\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9762\u4e0a\u7684\u795e\u8272\uff0c\u6709\u5982\u78d0\u77f3\u575a\u5b9a\uff0c\u4ed6\u76ee\u5149\u4e2d\u4f3c\u4e4e\u6709\u4e00\u79cd\u5947\u5f02\u7684\u529b\u91cf\uff0c\u538b\u4e0b\u4e86\u8fd9\u9739\u96f3\u7684\u547c\u558a\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u6697\u53f9\u4e00\u58f0\uff0c\u84e6\u7136\u53c8\u4e00\u6b21\u89c9\u51fa\u81ea\u5df1\u7684\u6ca1\u843d\u4e0e\u82cd\u8001\uff01 \u3000\u3000\u53ea\u89c1\u88f4\u73cf\u56de\u8f6c\u8eab\uff0c\u76ee\u5149\u56de\u5411\u6a80\u660e\uff0c\u5728\u8fd9\u4e00\u56de\u76ee\u4e4b\u95f4\uff0c\u4ed6\u660e\u786e\u5730\u770b\u5230\u6a80\u660e\u7709\u5b87\u95f4\uff0c\u7adf\u4f3c\u9690 \u85cf\u7740\u4e00\u79cd\u5341\u5206\u6df1\u9083\u7684\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u8d70\u4e0a\u4e00\u6b65\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8d70\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4e0d\u7981\u4e00\u6123\u9053\uff1a\u201c\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u88f4\u73cf\u6c89\u58f0\u9053\uff1a\u201c\u7236\u53d4\u4e4b\u4ec7\uff0c\u4e0d\u5171\u6234\u5929\uff0c\u6211\u8981\u4e0e\u4f60\u5bfb\u4e2a\u50fb\u9759\u4e4b\u5904\uff0c\u4e00\u51b3\u751f\u6b7b\uff0c\u65e0\u8bba\u80dc\u8d1f\uff0c \u4f60\u6211\u4e24\u5bb6\u7684\u4ec7\u6068\uff0c\u90fd\u53ef\u4ee5\u4e00\u7b14\u52fe\u6d88\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u53cc\u76ee\u4e00\u5f20\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u9762\u5bb9\u5927\u53d8\uff0c\u7fa4\u8c6a\u5374\u90fd\u6123\u4f4f\u4e86\uff0c\u4e1c\u65b9\u5144\u5f1f\u5374\u53c8\u4e0d\u7981 \u53f9\u4ed8\u9053\uff1a\u201c\u597d\u6c49\u5b50\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u7a81\u5730\u4ef0\u5929\u72c2\u7b11\u8d77\u6765\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u9644\u5728\u88f4\u73cf\u8eab\u4fa7\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u88f4\u5144\uff0c\u6211\u5927 \u52bf\u5b89\u6392\u5df2\u6210\uff0c\u53ea\u8981\u4f60\u4e00\u58f0\u4eca\u4e0b\uff0c\u6a80\u660e\u4fbf\u6b7b\u65e0\u5176\u6240\uff0c\u4f60\u4f55\u82e6\u2026\u2026\u201d \u3000\u3000\u6a80\u660e\u7b11\u58f0\u7a81\u5730\u4e00\u987f\uff0c\u622a\u53e3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4e0e\u6211\u5355\u72ec\u62fc\u6597\uff0c\u4f60\u6b66\u529f\u600e\u4f1a\u662f\u6211\u7684\u654c\u624b\uff1f\u201d \u3000\u3000\u88f4\u73cf\u4ecd\u7136\u9762\u6c89\u5982\u6c34\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u8d70\u51fa\u6b64\u5730\uff0c\u82e5\u6709\u4e00\u4eba\u5728\u6697\u4e2d\u8ddf\u968f\uff0c\u4fbf\u662f\u5bf9\u6211\u88f4\u73cf \u7684\u83ab\u5927\u7684\u7f9e\u8fb1\uff0c\u4fbf\u662f\u8ba4\u4e3a\u6211\u88f4\u73cf\u4e0d\u80fd\u4e3a\u81ea\u5df1\u7684\u7236\u4eb2\u590d\u4ec7\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u72e0\u5fc3\u4e00\u8dfa\u811a\uff0c\u6b66\u6797\u7fa4\u8c6a\u7684\u76ee\u5149\uff0c\u5374\u6e10\u6e10\u832b\u7136\u800c\u53d8\u6210\u94a6\u4f69\uff0c\u8981\u77e5\u8fd9\u822c \u8840\u6027\u7537\u513f\uff0c\u5fc3\u4e2d\u656c\u4f69\u7684\u5c31\u662f\u8fd9\u79cd\u65e0\u754f\u7684\u82f1\u96c4\uff0c\u867d\u7136\u66f4\u6709\u4e9b\u4eba\u773c\u4e2d\uff0c\u8fd9\u79cd\u82f1\u96c4\u672a\u514d\u592a\u8fc7\u611a\u8822\u3002 \u3000\u3000\u5176\u5b9e\u88f4\u73cf\u7684\u672c\u610f\u53c8\u4f55\u5c1d\u662f\u5982\u6b64\uff1f\u4f46\u5230\u4e86\u6b64\u65f6\u6b64\u5730\uff0c\u4ed6\u5fc3\u4e2d\u4fbf\u6709\u4e00\u9635\u70ed\u8840\u6d8c\u8d77\uff0c\u8fd9\u82f1\u96c4\u7684 \u70ed\u8840\uff0c\u4f7f\u5f97\u4ed6\u5fd8\u4e86\u8bb8\u591a\u4e8b\uff0c\u53e4\u5f80\u4eca\u6765\uff0c\u8fd9\u79cd\u82f1\u96c4\u7684\u70ed\u8840\u4e0d\u77e5\u6210\u5c31\u4e86\u591a\u5c11\u810d\u7099\u4eba\u53e3\u3001\u7559\u4f20\u5343\u53e4 \u7684\u96c4\u98ce\u70c8\u8ff9\uff0c\u4f20\u5f97\u58ee\u58eb\u4eec\u51fb\u8282\u9ad8\u6b4c\uff0c\u4f7f\u5f97\u7f8e\u4eba\u5012\u6697\u5f39\u73e0\u6cea\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9ed8\u7136\u534a\u664c\uff0c\u4ed6\u76ee\u4e2d\u7684\u795e\u8272\u7adf\u7136\u4e5f\u662f\u65e2\u75db\u6614\uff0c\u53c8\u77db\u76fe\uff0c\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16 \u7a81\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6211\u4eec\u4e0d\u80fd\u8ba9\u88f4\u5148\u751f\u8d70\uff0c\u6211\u4eec\u8981\u5148\u5c06\u8fd9\u5978\u8d3c\u6740\u6b7b\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u7acb\u523b\u88ab\u9f13\u52a8\u8d77\u6765\uff0c\u88f4\u73cf\u9762\u8272\u4e00\u6c89\uff0c\u4f46\u5927\u4e71\u52bf\u5df1\u5c06\u8d77\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u55a7\u77ac\u4e0d\u5bb9\u53d1\u7684\u5239\u90a3\u4e4b\u95f4\uff0c\u5929\u5916\u7a81\u5730\u4f20\u6765\u4e00\u9635\u6e05\u5578\u3002 \u3000\u3000\u8fd9\u5578\u58f0\u5b9b\u5982\u9f99\u541f\uff0c\u53c8\u5982\u51e4\u9e23\uff0c\u7a7f\u4e91\u88c2\u77f3\uff0c\u4e0a\u51b2\u9704\u6c49\u3002 \u3000\u3000\u7fa4\u8c6a\u53ea\u89c9\u5fc3\u5934\u4e00\u51db\uff0c\u6709\u7684\u5df2\u5fcd\u4e0d\u4f4f\u62b1\u4f4f\u8033\u6735\u3002 \u3000\u3000\u63a5\u7740\u5c4b\u810a\u4e0a\u5377\u4e0b\u4e00\u9635\u72c2\u98ce\uff0c\u5439\u7184\u4e86\u6240\u6709\u7684\u706b\u628a\u706f\u7b3c\u3002 \u3000\u3000\u5915\u9633\u65b9\u843d\uff0c\u661f\u6708\u672a\u5347\uff0c\u5927\u5730\u9aa4\u7136\u4e00\u9635\u660f\u6697\uff0c\u53ea\u542c\u957f\u5578\u58f0\u7531\u8fdc\u800c\u8fd1\uff0c\u7531\u8fd1\u53c8\u8fdc\uff0c\u970e\u773c\u95f4\u4fbf \u4f3c\u79bb\u53bb\u767e\u4e08\u3002 \u3000\u3000\u7b49\u5230\u7fa4\u8c6a\u76ee\u5149\u80fd\u591f\u8fa8\u7269\u65f6\uff0c\u8fd9\u957f\u5578\u5df2\u53ea\u5269\u4e0b\u4e86\u4e1d\u4e1d\u7f15\u7f15\u7684\u4f59\u97f3\u3002 \u3000\u3000\u505c\u7559\u5728\u6e05\u51b7\u7684\u591c\u7a7a\u91cc\uff0c\u800c\u53f0\u9636\u4e0a\u7684\u201c\u9f99\u5f62\u516b\u638c\u201d\u5374\u5df2\u4e0d\u89c1\u8e2a\u5f71\u3002 \u3000\u3000\u7acb\u523b\uff0c\u662f\u4e00\u9635\u66f4\u60ca\u9a87\u7684\u5927\u4e71\u3002 \u3000\u3000\u6709\u7684\u4eba\u5fd9\u7740\u53bb\u70b9\u706f\u7b3c\u706b\u628a\uff0c\u6709\u7684\u4eba\u5728\u65e0\u7528\u5730\u547c\u558a\u3002 \u3000\u3000\u201c\u8ffd\uff0c\u8ffd\uff0c\u9003\u4e86\uff0c\u9003\u4e86\u3002\u201d \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u76ee\u77aa\u53e3\u5446\uff0c\u9762\u5bb9\u53d1\u9752\uff0c\u5446\u5446\u4ef0\u89c6\u7740\u82cd\u7a79\u3002 \u3000\u3000\u4e1c\u65b9\u5144\u5f1f\u4ea6\u662f\u6ee1\u9762\u60ca\u5413\u4e4b\u8272\uff0c\u4ed6\u4eec\u4ff1\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u5c11\u5e74\u540d\u4fa0\uff0c\u6b66\u529f\u4ff1\u5f97\u6709\u4e00\u6d41\u4f20\u6388\uff0c \u4f46\u662f\u4ee5\u4ed6\u4eec\u7684\u771f\u529b\u7adf\u4f3c\u4e5f\u7981\u4e0d\u5f97\u90a3\u4e00\u58f0\u957f\u5578\uff0c\u4ee5\u4ed6\u4eec\u7684\u76ee\u529b\u7adf\u4e5f\u6ca1\u6709\u770b\u6e05\u8fd9\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff01 \u3000\u3000\u4ed6\u4eec\u53ea\u770b\u5230\u4e00\u6761\u4eba\u5f71\uff0c\u968f\u7740\u4e00\u9635\u72c2\u98ce\uff0c\u95ea\u7535\u822c\u6251\u4e86\u4e0b\u6765\uff0c\u4e00\u628a\u6284\u8d77\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c \u8eab\u5f62\u6beb\u65e0\u505c\u7559\uff0c\u4fbf\u53c8\u6377\u98de\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u671f\u95f4\u53ea\u6709\u88f4\u73cf\u5fc3\u4e2d\u66f4\u662f\u60ca\u7591\uff0c\u4ed6\u4e0d\u987b\u7528\u773c\u53bb\u770b\uff0c\u5df1\u53ef\u5927\u7ea6\u731c\u5230\u8fd9\u4ee5\u7edd\u9876\u5185\u529b\u4e0e\u8f7b\u529f\u6551 \u8d70\u6a80\u660e\u7684\u662f\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u4f7f\u4ed6\u65e0\u6cd5\u731c\u6d4b\u7684\uff0c\u662f\u8fd9\u4e24\u4f4d\u6b66\u6797\u5f02\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u6551\u8d70\u6a80\u660e\u3002 \u3000\u3000\u4ed6\u671b\u7740\u8fdc\u5904\u7684\u9ed1\u6697\uff0c\u76f4\u5230\u6240\u6709\u7684\u706f\u7b3c\u706b\u628a\u4ff1\u5df2\u4eae\u8d77\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7f13\u6b65\u8d70\u4e0a\u53f0\u9636\u2014\u2014\u7acb\u523b\uff0c\u6240\u6709\u7684\u58f0\u97f3\u90fd\u53d8\u505a\u4e86\u6b22\u547c\u3002 \u3000\u3000\u88f4\u73cf\u53cc\u624b\u4e00\u626c\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u670b\u53cb\u2026\u2026\u6a80\u660e\u5df2\u53bb\u2026\u2026\u4f46\u671b\u5404\u4f4d\u2026\u2026\u5404\u56de\u672c\u4f4d\u2026\u2026\u4e3a\u4eba \u95f4\u4f38\u5f20\u6b63\u4e49\u2026\u2026\u4e3a\u4eba\u7fa4\u670d\u52a1\u2026\u2026\u4f46\u5374\u8bf7\u5207\u8bb0\u4e00\u4e8b\u2026\u2026\u51e1\u4e8b\u4e07\u4e07\u4e0d\u53ef\u5982\u6b64\u51b2\u52a8\u2026\u2026\u79c1\u4ec7\u975e\u6bd4\u516c \u6124\u2026\u2026\u5728\u4e0b\u4e07\u4e07\u4e0d\u6562\u4ee5\u8ba1\u8c0b\u5c06\u79c1\u4ec7\u53d8\u4e3a\u516c\u6124\u2026\u2026\u4f46\u65e5\u540e\u5728\u4e0b\u82e5\u662f\u53d1\u73b0\u6709\u5371\u5bb3\u6b66\u6797\u6b63\u4e49\u4e4b \u4e8b\u2026\u2026\u8fd8\u671b\u5404\u4f4d\u80fd\u4e0e\u4eca\u65e5\u4e00\u6837\u2026\u2026\u4e0e\u6211\u540c\u5728\u2026\u2026\u4e3a\u6b66\u6797\u4f38\u5f20\u6b63\u4e49\u2026\u2026\u4e3b\u6301\u516c\u9053\uff01\u201d \u3000\u3000\u4ed6\u8a00\u8bed\u7b80\u76f4\u65e0\u6cd5\u7ee7\u7eed\uff0c\u56e0\u4e3a\u4ed6\u6bcf\u8bf4\u4e00\u53e5\uff0c\u4fbf\u6709\u4e00\u9635\u9707\u8033\u7684\u6b22\u547c\u3002 \u3000\u3000\u7b49\u5230\u4ed6\u5c06\u8bdd\u8bf4\u5b8c\uff0c\u56db\u9762\u7684\u6b22\u547c\uff0c\u5df2\u4f3c\u6012\u6f6e\u822c\u5c06\u5927\u5730\u90fd\u51e0\u4e4e\u6df9\u6ca1\uff0c\u201c\u6c5f\u5357\u540c\u76df\u201d\u4e2d\u4eba\uff0c\u66f4 \u662f\u4eba\u4eba\u5174\u594b\u6b32\u72c2\uff0c\u5927\u558a\u9053\uff1a\u201c\u76df\u4e3b\u4e07\u5c81\uff01\u2026\u2026\u62e5\u62a4\u6211\u4eec\u7684\u88f4\u5927\u5148\u751f\u91cd\u8fd4\u6c5f\u5357\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u6012\u6f6e\u822c\u7684\u6b22\u547c\u58f0\u4e2d\u2026\u2026 \u3000\u3000\u8881\u6cf8\u73cd\u70ed\u6cea\u76c8\u7736\uff0c\u7c92\u7c92\u73e0\u6cea\uff0c\u5374\u95ea\u70c1\u7740\u5f97\u610f\u7684\u5149\u91c7\u3002 \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u542b\u7b11\u4e92\u89c6\uff0c\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u4ed6\u7ec8\u7a76\u957f\u6210\u4e86\u3002\u201d \u3000\u3000\u51b7\u67af\u6728\u6b22\u559c\u5730\u53f9\u9996\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u8be5\u5411\u5bb6\u4e2b\u3002\u201d \u3000\u3000\u51b7\u5bd2\u7af9\u9053\uff1a\u201c\u8d4c\u7ea6\u5462\uff1f\u201d \u3000\u3000\u51b7\u67af\u6728\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u8d4c\u7ea6\uff0c\u80dc\u80dc\u8d1f\u8d1f\uff0c\u8fd8\u6709\u4ec0\u4e48\u5173\u7cfb\u4e48\uff1f\u201d \u3000\u3000\u4e24\u4eba\u76f8\u89c6\u4e00\u7b11\uff0c\u5411\u4eba\u4e1b\u4e2d\u98d8\u7136\u5f15\u53bb\u3002 \u3000\u3000\u201c\u795e\u624b\u201d\u6218\u98de\u76ee\u4e2d\u89c1\u5230\u8fd9\u79cd\u573a\u9762\uff0c\u8033\u4e2d\u542c\u5230\u8fd9\u58f0\u58f0\u6b22\u547c\uff0c\u75be\u7136\u82e5\u5931\uff0c\u5782\u4e0b\u4e86\u5934\uff0c\u5fc3\u4e2d\u66f4 \u5145\u6ee1\u4e86\u5bc2\u5bde\u8083\u7d22\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u4eba\u751f\uff0c\u4eba\u751f\u2026\u2026\u5509\uff01\u53bb\u4e86\u2026\u2026\u53bb\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u8fd9\u66fe\u7ecf\u53f1\u54a4\u4e00\u65f6\u7684\u6b66\u6797\u5927\u8c6a\uff0c\u4fbf\u4e5f\u5728\u8fd9\u6b22\u547c\u58f0\u4e2d\uff0c\u843d\u5bde\u5730\u8d70\u4e86\uff0c\u53ea\u662f\u4ed6\u5fc3\u91cc\u6bd5\u7adf\u8fd8\u6709\u4e00 \u4e1d\u751c\u751c\u7684\u5b89\u6170\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u5728\u4e0d\u8fdc\u7684\u4e00\u4e2a\u5730\u65b9\uff0c\u8fd8\u6709\u4e00\u6735\u751c\u751c\u7684\u5fae\u7b11\u5728\u7b49\u7740\u4ed6\uff0c\u4ed6\u5fc3\u4e0a\u7684 \u98ce\u5c18\u4e0e\u521b\u75d5\uff0c\u4e5f\u5f53\u771f\u9700\u8981\u90a3\u4e00\u53cc\u83b9\u767d\u7684\u7ea4\u624b\u7684\u6d17\u6da4\u4e0e\u5b89\u6170\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u662f\u82f1\u96c4\u7684\u672b\u8def\uff0c\u4f46\u8fd9\u53c8\u4f55\u5c1d\u4e0d\u662f\u4eba\u751f\u7684\u8d77\u59cb\u5462\uff1f\u4ed6\u66fe\u7ecf\u5f81\u670d\u8fc7\u8bb8\u591a\u4eba\uff0c\u4f46\u4ed6\u53c8\u4f55 \u5c1d\u5f81\u670d\u8fc7\u4e00\u4e2a\u5973\u4eba\u7684\u5fc3\uff1f \u3000\u3000\u5feb\u4e50\u4e0e\u6210\u529f\u53ef\u4ee5\u5206\u8bb8\u591a\u79cd\u3002\u53ea\u662f\u8981\u770b\u4f60\u4ece\u54ea\u4e00\u4e2a\u89d2\u5ea6\u53bb\u5224\u65ad\uff0c\u4ed6\u811a\u6b65\u867d\u6c89\u91cd\uff0c\u4f46\u662f\u5728\u843d \u5bde\u7684\u9762\u5bb9\u4e0a\uff0c\u5374\u6bd5\u7adf\u6709\u4e00\u4e1d\u5fae\u7b11\u3002 \u3000\u3000\u201c\u4e03\u5de7\u8ffd\u9b42\u201d\u90a3\u98de\u8679\u7ad9\u5f97\u4e0e\u88f4\u73cf\u6700\u8fd1\uff0c\u8fd9\u6539\u90aa\u5f52\u6b63\u7684\u7eff\u6797\u67ad\u96c4\uff0c\u4f3c\u4e4e\u5df2\u4ece\u8fd9\u6b22\u547c\u58f0\u4e2d\u5206 \u5f97\u4e00\u5206\u6b22\u5077\u4e0e\u5149\u8363\u3002 \u3000\u3000\u56e0\u6b64\u4ed6\u67af\u7626\u7684\u9762\u5bb9\u4e0a\uff0c\u6b64\u523b\u6b63\u7115\u53d1\u7740\u4ece\u6765\u672a\u6709\u7684\u5149\u91c7\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u65ad\u53cd\u590d\u9ed8\u5ff5\uff1a\u201c\u884c\u5584\u6bd5\u7adf\u662f\u6bd4\u4f5c\u6076\u5feb\u4e50\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u6444\u9b42\u5200\u201d\u7f57\u4e49\uff0c\u80f8\u819b\u524d\u4e00\u7247\u9c9c\u8840\uff0c\u5367\u5728\u4e00\u5904\u50fb\u9759\u7684\u5c4b\u6a90\u4e0b\uff0c\u8fd9\u4e00\u58f0\u58f0\u6b22\u547c\uff0c\u6d6a\u6f6e\u822c\u51b2 \u6fc0\u7740\u4ed6\u7684\u5fc3\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6709\u8bb8\u591a\u611f\u6168\uff0c\u4e5f\u6709\u8bb8\u591a\u60b2\u54c0\uff0c\u8fd9\u4e00\u4efd\u611f\u6168\u4e0e\u60b2\u54c0\uff0c\u6216\u8bb8\u80fd\u5e2e\u4ed6\u51b3\u5b9a\u4ee5\u540e\u4eba\u751f\u65cb\u9014 \u7684\u65b9\u5411\u3002 \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u3001\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\uff0c\u4ee5\u53ca\u8fb9\u5c11\u884d\u4e09\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u5077\u5077 \u5411\u4eba\u4e1b\u4e2d\u6e9c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u8f7b\u8f7b\u5730\u4ee5\u5feb\u6b65\u8d70\u51fa\u8fd9\u6761\u957f\u8857\uff0c\u5982\u98de\u63a0\u51fa\u6c49\u53e3\u57ce\u5916\uff0c\u8fb9\u5c11\u884d\u5fcd\u4e0d\u4f4f\u5410\u4e86\u53e3\u957f\u6c14\uff0c\u9053\uff1a \u201c\u64c5\u660e\u8dd1\u4e86\uff0c\u6211\u4eec\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u8dd1\u5f97\u6389\u4e48\uff1f\u201d \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u63a5\u53e3\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u81ea\u8ba4\u6740\u6b7b\u2018\u67aa\u5251\u65e0\u6545\u2019\uff0c\u88f4\u73cf\u600e\u4f1a\u653e\u8fc7\u4ed6\uff0c\u8fdf\u65e9 \u662f\u6b7b\u8def\u4e00\u6761\uff01\u201d \u3000\u3000\u57ce\u5916\u4e00\u7247\u65f7\u91ce\uff0c\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u4ef0\u5929\u5927\u7b11\u51e0\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u8981\u6a80\u660e\u4e00\u6b7b\uff0c\u54c8\u2026\u2026\u2018\u98de\u9f99\u9556 \u5c40\u2019\u7684\u5e10\u7c3f\u3001\u5b58\u6298\u3001\u8425\u4e1a\u60c5\u51b5\uff0c\u5168\u90e8\u634f\u5728\u6211\u624b\u91cc\uff0c\u6211\u4eec\u4e09\u4eba\u53ef\u771f\u8981\u626c\u7709\u5410\u6c14\u4e86\u3002\u201d \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u4f55\u51b5\u6211\u4eec\u8fd9\u756a\u5df2\u4e0e\u2018\u6c5f\u5357\u540c\u76df\uff0c\u62c9\u4e0a\u4e86\u4ea4\u60c5\uff0c\u6539\u7ec4\u540e \u7684\u2019\u98de\u9f99\u9556\u5c40\u2018\uff0c\u5c06\u6765\u60f3\u5fc5\u662f\u4e00\u7247\u5766\u9014\u4e86\u3002\u201d\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u9f9a\u5144\uff0c\u5c06\u6765 \u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u603b\u9556\u5934\u4f4d\u5b50\uff0c\u60f3\u6765\u8981\u5f52\u4e8e\u9f9a\u5144\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9762\u4e0a\u65b9\u81ea\u6cdb\u8d77\u4e86\u4e00\u4e1d\u7b11\u5bb9\uff0c\u4f46\u4e00\u77a5\u67f3\u8f89\u7684\u9762\u8272\uff0c\u7b11\u5bb9\u7acb\u655b\uff0c\u4e8e\u7b11\u6570\u58f0\uff0c \u9053\uff1a\u201c\u67f3\u5144\u8bf4\u54ea\u91cc\u8bdd\uff1f\u603b\u9556\u5934\u4e00\u4f4d\uff0c\u81ea\u7136\u662f\u67f3\u5144\u7684\u4e86\uff01\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u9762\u5bb9\u7565\u9701\uff0c\u7a81\u542c\u8fb9\u5c11\u884d\u51b7\u7b11\u4e00\u58f0\uff0c\u4e24\u4eba\u4e00\u8d77\u56de\u8f6c\u5934\u6765\uff0c\u5446\u5446\u5730\u671b\u7740\u8fb9\u5c11 \u884d\u3002 \u3000\u3000\u8fb9\u5c11\u884d\u7f13\u7f13\u629a\u5f04\u7740\u8170\u95f4\u7684\u5251\u67c4\uff0c\u9053\uff1a\u201c\u67f3\u603b\u9556\u5934\uff0c\u5c06\u6765\u2018\u98de\u9f99\u9556\u5c40\u2019\uff0c\u8fd8\u6709\u5c0f\u5f1f\u5bb9\u8eab\u4e4b \u5730\u4e48\uff1f\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u4ea6\u81ea\u5e72\u7b11\u6570\u58f0\uff0c\u9053\uff1a\u201c\u8fb9\u5144\uff0c\u8bf4\u54ea\u91cc\u8bdd\uff0c\u65e0\u8bba\u4ee5\u58f0\u540d\u6291\u6216\u6b66\u529f\u6765\u8bf4\uff0c\u5c06 \u6765\u2018\u98de\u9f99\u9556\u5c40\u2019\u7684\u603b\u9556\u5934\u4e00\u4f4d\uff0c\u5374\u8be5\u662f\u8fb9\u5144\u7684\u3002\u201d \u3000\u3000\u8fb9\u5c11\u884d\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\u2026\u2026\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u624d\u8d77\uff0c\u7a81\u542c\u201c\u5feb\u9a6c\u795e\u5200\u201d\u9f9a\u6e05\u6d0b\u4e00\u58f0\u60e8\u547c\uff0c\u8fb9\u5c11\u884d\u3001\u67f3\u8f89\u5927\u60ca\u4e4b\u4e0b\uff0c\u56de\u9996\u671b\u53bb\u2014 \u2014\u53ea\u89c1\u9f9a\u6e05\u6d0b\u9762\u4e0a\u808c\u8089\u4e00\u9635\u626d\u66f2\uff0c\u53cc\u80a9\u4e00\u9635\u6447\u6643\uff0c\u5ffd\u7136\u201c\u6251\u201d\u5730\u4ef0\u9762\u5012\u4e86\u4e0b\u53bb\uff0c\u80cc\u810a\u4e4b\u4e0a\uff0c \u8d6b\u7136\u63d2\u7740\u4e00\u53e3\u5229\u5203\uff0c\u2014\u2014\u4e0d\u5e38\u770b\u89c1\u7684\u67f3\u53f6\u98de\u5200\uff01 \u3000\u3000\u8fb9\u5c11\u884d\u3001\u67f3\u8f89\u9762\u5bb9\u9f50\u5730\u60e8\u53d8\uff0c\u75be\u53f1\u9053\uff1a\u201c\u8c01\uff01\u201d \u3000\u3000\u56de\u9996\u671b\u53bb\uff0c\u9ed1\u6697\u4e2d\u7f13\u7f13\u8d70\u51fa\u4e86\u4e00\u6761\u4eba\u5f71\uff0c\u6709\u5982\u5e7d\u7075\u4e00\u822c\uff0c\u98d8\u98d8\u5728\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u4e00\u5b57\u4e00\u5b57 \u5730\u51b7\u51b7\u9053\uff1a\u201c\u4e24\u4f4d\u6253\u5f97\u597d\u5982\u610f\u7684\u7b97\u76d8\uff01\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u5fc3\u5934\u4e00\u5bd2\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u8c79\u5144\uff0c\u4f60\u2026\u2026\u4f60\u600e\u5730\u6765\u4e86\uff1f\u201d \u3000\u3000\u82d7\u8c79\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fde\u6a80\u5927\u7237\u90fd\u4e0d\u8ba4\u5f97\u4e86\uff0c\u8fd8\u8ba4\u5f97\u6211\u5427\uff1f\u201d \u3000\u3000\u201c\u516b\u5366\u638c\u201d\u67f3\u8f89\u6ee1\u5934\u5927\u6c57\uff0c\u8fde\u9000\u4e09\u6b65\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u8eab\u5f62\u4e00\u8f6c\uff0c\u7ade\u8981\u63a0\u8d70\u3002 \u3000\u3000\u82d7\u8c79\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u54ea\u91cc\u53bb\uff1f\u201d\u2026 \u3000\u3000\u624b\u638c\u4e00\u7a7f\uff0c\u8eab\u5f62\u95ea\u52a8\u95f4\uff0c\u4fbf\u5df2\u6321\u5728\u67f3\u8f89\u9762\u524d\u3002 \u3000\u3000\u67f3\u8f89\u9053\uff1a\u201c\u82d7\u5144\uff0c\u4f60\u8fd9\u2026\u2026\u8fd9\u662f\u8981\u505a\u4ec0\u4e48\uff1f\u563f\u563f\uff0c\u8001\u5f1f\u5144\u597d\u4e45\u4e0d\u89c1\uff0c\u6211\u8bf7\u4f60\u2014\u2014\u201d\u82d7\u8c79 \u9762\u8272\u4e00\u6c89\uff0c\u6740\u673a\u5df2\u73b0\uff0c\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5f1f\u5144\uff1f\u6211\u6b63\u662f\u6765\u8981\u4f60\u7684\u72d7\u547d\uff01\u201d \u3000\u3000\u8fb9\u5c11\u884d\u638c\u52bf\u4e00\u626c\uff0c\u53ea\u542c\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u5251\u5149\u66b4\u73b0\uff0c\u957f\u5251\u5e26\u7740\u4e00\u6e9c\u9752\u84dd\u8272\u7684\u5149\u8292\uff0c\u95ea\u7535\u822c \u5411\u82d7\u8c79\u524a\u53bb\u3002 \u3000\u3000\u82d7\u8c79\u8d64\u624b\u7a7a\u62f3\uff0c\u4ee5\u4e00\u5bf9\u4e24\uff0c\u5374\u4e1d\u6beb\u4e0d\u60e7\uff0c\u4e24\u638c\u4e00\u5f15\uff0c\u76f4\u51fb\u67f3\u8f89\u524d\u80f8\uff0c\u53f3\u9762\u98de\u8d77\u4e00\u817f\uff0c\u76f4 \u8e22\u8fb9\u5c11\u884d\u6301\u5251\u7684\u624b\u8155\u3002 \u3000\u3000\u8fb9\u5c11\u884d\u867d\u7136\u77e5\u9053\u81ea\u5df1\u6b66\u529f\u4e0d\u662f\u82d7\u8c79\u4e4b\u654c\uff0c\u4f46\u6b64\u523b\u4ee5\u4e8c\u654c\u4e00\uff0c\u5fc3\u4e2d\u4ea6\u65e0\u754f\u60e7\u4e4b\u5fc3\uff0c\u53e3\u4e2d\u51b7 \u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u6765\u8981\u547d\uff0c\u6211\u5374\u770b\u4f60\u662f\u672a\u9001\u6b7b\u7684\uff1a\u201c\u8bf4\u8bdd\u58f0\u4e2d\uff0c\u4ed6\u957f\u5251\u7ffb\u8f6c\uff0c\u5251\u5149\u98de\u821e\uff0c\u8fde\u73af\u653b \u51fa\u4e09\u5251\uff01\u54ea\u77e5\u201d\u516b\u5366\u638c\u201c\u67f3\u8f89\u5374\u4e58\u8fd9\u5239\u90a3\u95f4\uff0c\u6454\u8f6c\u8eab\u8eaf\u8dd1\u4e86\uff01\u8fb9\u5c11\u884d\u5230\u4e86\u6b64\u523b\uff0c\u5fc3\u5934\u65b9\u5927 \u9a87\uff0c\u53ea\u89c1\u82d7\u8c79\u51b7\u7b11\u4e00\u58f0\uff0c\u5de6\u638c\u63a5\u4e86\u4e09\u62db\uff0c\u53f3\u638c\u4e00\u6325\u4e00\u626c\uff0c\u4e09\u53e3\u78a7\u7eff\u7684\u82d7\u5200\uff0c\u5e26\u7740\u6781\u4e3a\u8f7b\u5fae\u7684 \u98ce\u58f0\uff0c\u5411\u67f3\u8f89\u51fb\u53bb\uff0c\u8981\u77e5\u751f\u957f\u82d7\u7586\uff0c\u5bf9\u4e8e\u82d7\u4eba\u7684\u7edd\u6280\u98de\u5200\uff0c\u65e9\u5df2\u7ec3\u5f97\u5f97\u5fc3\u5e94\u624b\uff0c\u518d\u52a0\u4e0a\u6b66\u529f \u7684\u4fee\u4e3a\uff0c\u5185\u529b\u7684\u589e\u8fdb\uff0c\u624b\u6cd5\u66f4\u662f\u5de7\u5999\u3002\u201d\u516b\u5366\u638c\u201c\u67f3\u8f89\u65b9\u81ea\u5954\u51fa\u4e00\u4e08\uff0c\u53ea\u95fb\u8eab\u540e\u98ce\u58f0\u5df2\u81f3\u3002 \u4ee5\u4ed6\u7684\u6b66\u529f\u8eab\u6cd5\uff0c\u672c\u6765\u4e0d\u96be\u5c06\u8fd9\u4e09\u53e3\u98de\u5200\u907f\u5f00\uff0c\u600e\u5948\u4ed6\u6b64\u523b\u65e9\u5df2\u5fc3\u614c\u610f\u4e71\uff0c\u5de6\u907f\u53f3\u95ea\u4e4b\u4e0b\uff0c \u4e00\u53e3\u98de\u5200\u5df2\u81ea\u8d2f\u80cc\u800c\u5165\uff0c\u76f4\u6ca1\u81f3\u67c4\uff0c\u201d\u516b\u5366\u638c\u201c\u67f3\u8f89\u60e8\u547c\u4e00\u58f0\uff0c\u6070\u5de7\u5012\u5728\u201d\u7094\u9a6c\u795e\u5200\u201c\u9f9a\u6e05 \u6d0b\u7684\u8eab\u65c1\u3002\u8fb9\u5c11\u884d\u76ee\u5149\u626b\u5904\uff0c\u6ee1\u5fc3\u60ca\u60f6\uff0c\u5251\u6cd5\u5df2\u89c1\u7efc\u4e71\uff0c\u7a81\u89c1\u5251\u5149\u4e2d\u6b3a\u5165\u4e00\u6761\u4eba\u5f71\uff0c\u4ed6\u5927\u60ca \u4e4b\u4e0b\uff0c\u5389\u53f1\u4e00\u58f0\uff0c\u5251\u5149\u4e0b\u524a\uff0c\u53ea\u89c1\u767d\u5149\u4e00\u6d8c\uff0c\u4ed6\u5f53\u80f8\u5374\u5df2\u88ab\u82d7\u8c79\u51fb\u4e2d\u4e00\u638c\uff0c\u6709\u5982\u88ab\u5343\u65a4\u5de8\u77f3 \u51fb\u4e2d\u4e00\u6837\u3002\u5239\u90a3\u95f4\u4ed6\u53ea\u89c9\u5343\u4e07\u9897\u91d1\u661f\uff0c\u540c\u65f6\u5728\u4ed6\u773c\u524d\u73b0\u51fa\uff0c\u5589\u95ee\u4e00\u751c\uff0c\u4e00\u53e3\u9c9c\u8840\u55b7\u5728\u5730\u4e0a\uff0c \u82d7\u8c79\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u5728\u4ed6\u201d\u9f20\u6eaa\u201c\u8981\u7a74\u4e4b\u4e0a\uff0c\u5c06\u4ed6\u7684\u8eab\u8eaf\u8e22\u5f97\u98de\u8d77\u4e00\u4e08\uff0c\u7830\u5730\uff0c\u53c8\u6070\u5de7\u843d\u5728\u201d \u516b\u5366\u638c\u201c\u67f3\u8f89\u7684\u8eab\u65c1\uff01\u51b7\u98ce\u55d6\u55d6\uff0c\u591c\u8272\u60e8\u6de1\u3002\u82d7\u8c79\u5de6\u81c2\u9c9c\u8840\u6dcb\u6f13\uff0c\u67d3\u5f97\u4ed6\u4e00\u8eab\u7d2b\u7ea2\uff0c\u4ed6\u65b9\u624d \u53cd\u8eab\u51fb\u4e2d\u4e00\u638c\uff0c\u81ea\u5df1\u4e5f\u88ab\u8fb9\u5c11\u884d\u957f\u5251\u523a\u4e2d\u3002\u4f46\u662f\u8fd9\u527d\u608d\u72c2\u91ce\u7684\u5c11\u5e74\uff0c\u5374\u4f3c\u4e4e\u6beb\u4e0d\u5728\u610f\uff0c\u751a\u81f3 \u8fde\u671b\u90fd\u672a\u5411\u81ea\u5df1\u7684\u4f24\u5904\u671b\u4e0a\u4e00\u773c\uff0c\u4ec5\u53ea\u5fae\u4e00\u76b1\u7709\uff0c\u4fef\u8eab\u62fe\u8d77\u4e86\u8fb9\u5c11\u884d\u7684\u957f\u5251\uff0c\u8eab\u5f62\u5c55\u52a8\uff0c\u5237 \u5730\uff0c\u524a\u4e0b\u4e00\u5927\u7247\u6811\u76ae\uff0c\u4ee5\u4ed6\u4eec\u4e09\u4eba\u7684\u9c9c\u8840\uff0c\u5728\u65b0\u524a\u4e0b\u7684\u6811\u76ae\u4e0a\u5199\u4e86\u4e03\u4e2a\u89e6\u76ee\u60ca\u5fc3\u7684\u5927\u5b57\uff1a \u201c\u5356\u4e3b\u6c42\u8363\u7684\u4e0b\u573a\uff01\u201d \u3000\u3000\u4ed6\u6ee1\u610f\u5730\u770b\u4e86\u51e0\u773c\uff0c\u8fd9\u5b57\u8ff9\u867d\u7136\u62d9\u52a3\uff0c\u4f46\u662f\u5b57\u53e5\u5374\u5145\u6ee1\u4e86\u6b63\u76f4\u3001\u5fe0\u8bda\uff0c\u4ee5\u53ca\u5bf9\u4e16\u4eba\u7684\u8b66 \u60d5\u3002 \u3000\u3000\u7136\u540e\u4ed6\u968f\u624b\u629b\u5f03\u4e86\u957f\u5251\uff0c\u8f6c\u8eab\u8d70\u4eba\u9ed1\u6697\u91cc\uff0c\u55d6\u55d6\u7684\u51b7\u98ce\uff0c\u5239\u90a3\u95f4\u4fbf\u5438\u5e72\u4e86\u5730\u4e0a\u7684\u9c9c\u8840\uff01 \u3000\u3000\u65f7\u91ce\uff0c\u65f7\u91ce\uff0c\u4ecd\u7136\u662f\u7070\u9eef\u800c\u6e05\u51b7\u7684\u3002 \u3000\u3000\u6c49\u53e3\u57ce\u4e2d\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u5374\u5728\u59ff\u610f\u72c2\u6b22\u7740\u3002 \u3000\u3000\u4ed6\u4eec\u6572\u5f00\u4e86\u6240\u6709\u7684\u9152\u5e97\uff0c\u51e0\u4e4e\u559d\u5e72\u4e86\u6240\u6709\u7684\u9152\u3002 \u3000\u3000\u4ed6\u4eec\u4e09\u4e09\u4e24\u4e24\u75db\u996e\u7740\u7f8e\u9152\uff0c\u7545\u53d9\u7740\u751f\u5e73\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u8fd9\u57ce\u5e02\u4e2d\u9020\u6210\u4e00\u6b21\u7a7a\u524d\u7684\u7eb7\u4e71\u2014\u2014\u56e0\u4e3a\u4ed6\u4eec\u5c31\u8981\u8d70\u4e86\uff0c\u6240\u6709\u7684\u4e89\u6597\uff0c\u770b\u6765\u90fd\u5df2\u6210 \u4e3a\u8fc7\u53bb\uff0c\u201c\u51b7\u8c37\u53cc\u6728\u201d\u4e0d\u77e5\u6240\u8e2a\uff0c\u201c\u98de\u9f99\u9556\u5c40\u201d\u4e00\u8d25\u6d82\u5730\uff0c\u8d4c\u7ea6\u3001\u6597\u4e89\uff0c\u90fd\u6ca1\u6709\u4e86\uff0c\u90fd\u8fc7\u53bb \u4e86\u3002 \u3000\u3000\u867d\u7136\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u8fd8\u672a\u6b7b\uff0c\u4f46\u4ed6\u8d70\u53bb\u4f55\u5904\uff0c\u5374\u662f\u65e0\u4eba\u77e5\u9053\uff0c\u8fd9\u4e00\u7fa4\u6b66\u6797\u8c6a\u58eb\u5728\u6c5f\u6e56\u4e2d \u6240\u9020\u6210\u7684\u7a7a\u524d\u7684\u4f1a\u5408\uff0c\u6b64\u523b\u5df2\u52bf\u5fc5\u89e3\u4f53\uff0c\u6709\u7684\u4eba\u5fc3\u4e2d\u4e0d\u514d\u6709\u4e9b\u5931\u671b\uff0c\u6709\u7684\u4eba\u5fc3\u4e2d\u6709\u4e9b\u843d\u5bde\uff0c \u6709\u4e9b\u4eba\u5374\u5728\u5fc3\u4e2d\u6697\u6697\u5e86\u5e78\uff01 \u3000\u3000\u53ea\u6709\u4e00\u4ef6\u4e8b\uff0c\u662f\u4ed6\u4eec\u5171\u540c\u627f\u8ba4\u7684\uff0c\u90a3\u5c31\u662f\u2014\u2014\u6b66\u6797\u4e2d\u7ec8\u4e8e\u51fa\u73b0\u4e86\u4e00\u9897\u5149\u7167\u4eba\u5bf0\u7684\u660e\u661f\uff01 \u3000\u3000\u4ed6\u4eec\u4e0d\u65f6\u4e3e\u676f\u4e3a\u8fd9\u9897\u660e\u661f\u795d\u8d3a\uff0c\u8fd9\u660e\u661f\u867d\u7136\u5386\u7ecf\u8fc7\u8bb8\u591a\u6298\u78e8\uff0c\u5371\u96be\u4e0e\u5c48\u4fae\uff0c\u4f46\u6b64\u523b\u5728\u6b66 \u6797\u4e2d\u7ec8\u6210\u4e0d\u673d\uff01 \u3000\u3000\u7136\u800c\uff0c\u6b64\u523b\u8fd9\u9897\u660e\u661f\u5374\u4ecd\u662f\u5bc2\u5bde\u7684\uff0c\u5728\u90ca\u5916\u90a3\u5b64\u72ec\u7684\u5e84\u9662\u4e2d\uff0c\u90a3\u51b7\u6e05\u7684\u540e\u9662\u4e2d\uff0c\u88f4\u73cf\u5b64 \u72ec\u800c\u51b7\u6e05\u5730\u5c06\u81ea\u5df1\u9501\u5728\u4e00\u95f4\u5b64\u72ec\u800c\u51b7\u6e05\u7684\u623f\u91cc\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4e0d\u77e5\u6709\u591a\u5c11\u6b66\u6797\u8c6a\u6770\u76fc\u671b\u7740\u4e0e\u4ed6\u540c\u996e\uff0c\u4f46\u662f\u4ed6\u5374\u53ea\u60f3\u5b64\u5bc2\uff0c\u4ed6\u4e95\u975e\u8981\u8fdc\u79bb\u4eba\u7fa4\uff0c \u53ea\u662f\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u6025\u9700\u5b64\u5bc2\u6765\u4e3a\u4ed6\u6574\u7406\u7d0a\u4e71\u7684\u601d\u6f6e\uff0c\u6765\u4e3a\u4ed6\u5206\u6790\u5f53\u524d\u7684\u53bb\u5411\uff0c\u6765\u4e3a\u4ed6\u51b7\u5374\u8fc7 \u6fc0\u7684\u70ed\u60c5\u3002 \u3000\u3000\u4ed6\u4e5f\u66fe\u542c\u5230\u8881\u6cf8\u73cd\u7684\u811a\u6b65\u5230\u4ed6\u7a97\u524d\u6765\u8f7b\u8f7b\u63a2\u671b\uff0c\u4ee5\u53ca\u90bb\u623f\u7684\u5434\u9e23\u4e16\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4ed6\u77e5\u9053 \u8fd9\u4e9b\u90fd\u662f\u5173\u5fc3\u4ed6\u7684\u670b\u53cb\uff0c\u4ed6\u62b1\u6b49\u4e0d\u80fd\u63a5\u53d7\u5434\u9e23\u4e16\u7684\u76db\u60c5\uff0c\u66f4\u62b1\u6b49\u4e0d\u80fd\u4e0e\u4e45\u522b\u91cd\u9022\u7684\u8881\u6cf8\u73cd\u7545 \u8c08\uff0c\u4ed6\u53ea\u8bf4\uff1a\u201c\u7ecf\u8fc7\u8fd9\u4e48\u591a\u5929\u7684\u52b3\u7d2f\uff0c\u6211\u4eec\u90fd\u8be5\u65e9\u4e9b\u7761\u4e86\u3002\u201d \u3000\u3000\u201c\u51b7\u8c37\u53cc\u6728\u201d\u7684\u4e0d\u544a\u800c\u522b\uff0c\u4f7f\u5f97\u4ed6\u5728\u70e6\u607c\u4e0e\u75db\u82e6\u4e4b\u5916\uff0c\u66f4\u52a0\u6dfb\u4e86\u4e00\u4efd\u79bb\u522b\u7684\u60c6\u6005\uff0c\u8fd9\u4e9b \u5929\uff0c\u4ed6\u4e0e\u8fd9\u4e24\u4e2a\u4e0d\u77e5\u662f\u51b7\u9177\u6291\u6216\u662f\u70ed\u60c5\u7684\u8001\u4eba\uff0c\u5df2\u751f\u51fa\u4e00\u4efd\u6d53\u6d53\u7684\u60c5\u611f\uff0c\u800c\u81f3\u4eca\u4ee5\u540e\uff0c\u4ed6\u5374 \u6c38\u8fdc\u518d\u65e0\u6cd5\u77e5\u9053\u4ed6\u4eec\u7684\u53bb\u5904\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u884c\u8e2a\u6c38\u8fdc\u662f\u90a3\u4e48\u98d8\u5ffd\uff0c\u800c\u201c\u51b7\u8c37\u201d\u4e5f\u662f\u4e2a\u865a\u65e0\u98d8\u6e3a \u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u659c\u501a\u5728\u5e8a\u4e0a\uff0c\u6839\u672c\u6ca1\u6709\u4e1d\u6beb\u7761\u610f\uff0c\u6069\u4ec7\u7684\u96be\u89e3\uff0c\u60c5\u6028\u7684\u77db\u76fe\uff0c\u524d\u9014\u7684\u96be\u6d4b\uff0c\u4ee5\u53ca\u4e00\u79cd \u6210\u529f\u540e\u7684\u832b\u7136\uff0c\u4f7f\u5f97\u4ed6\u7684\u5fc3\u548c\u5934\u8111\uff0c\u90fd\u50cf\u662f\u5728\u51b0\u5c71\u4e2d\u51b7\u51bb\u4e86\u6570\u5341\u5e74\u90a3\u6837\u7684\u51b0\u51b7\uff0c\u65b0\u9c9c\u800c\u6e05 \u9192\u3002 \u3000\u3000\u9065\u8fdc\u5904\uff0c\u6709\u66f4\u9f13\u4f20\u6765\uff0c\u4ed6\u6ca1\u6709\u7ec6\u6570\uff0c\u4e5f\u4e0d\u77e5\u5df2\u81f3\u51e0\u66f4\u3002 \u3000\u3000\u591c\uff0c\u6df1\u6df1\u6c89\u6c89\uff0c\u4eba\uff0c\u9759\u9759\u5bc2\u5bc2\uff0c\u6811\uff0c\u51b7\u51b7\u6e05\u6e05\u3002 \u3000\u3000\u5728\u8fd9\u6df1\u6df1\u6c89\u6c89\uff0c\u9759\u9759\u5bc2\u5bc2\uff0c\u51b7\u51b7\u6e05\u6e05\u7684\u591c\u91cc\uff0c\u88f4\u73cf\u5ffd\u7136\u542c\u5230\u4e86\u4e00\u9635\u9635\u547c\u5524\u7684\u58f0\u97f3\u2026\u2026 \u3000\u3000\u8fd9\u58f0\u97f3\u65e2\u4f3c\u9065\u8fdc\uff0c\u53c8\u4f3c\u4e0d\u8fdc\uff0c\u65e2\u4f3c\u98d8\u6e3a\uff0c\u53c8\u4f3c\u771f\u5b9e\uff0c\u4eff\u4f5b\u662f\u5e7d\u51a5\u95f4\u9b3c\u9b42\u7684\u547c\u5524\uff0c\u53c8\u4eff\u4f5b \u662f\u6000\u62b1\u91cc\u60c5\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u4e00\u8df3\uff0c\u60c5\u4e0d\u81ea\u7981\u5730\u957f\u8eab\u800c\u8d77\uff0c\u8f7b\u8f7b\u63a8\u5f00\u7a97\u5b50\uff0c\u5ead\u56ed\u4fbf\u50cf\u662f\u88ab\u6c34\u6d17\u8fc7\u4e86\u7684\u7384\u51b0\u4e00 \u6837\uff0c\u5448\u73b0\u5728\u4ed6\u773c\u524d\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u5f71\uff0c\u4f46\u547c\u5524\u7684\u58f0\u97f3\u8fd8\u5728\u4e0d\u65ad\u4f20\u6765\u3002 \u3000\u3000\u201c\u73cf\u513f\u2026\u2026\u73cf\u513f\u2026\u2026\u201d \u3000\u3000\u4ed6\u84e6\u89c9\u4e00\u9635\u5bd2\u610f\u6d8c\u4e0a\u5fc3\u5934\uff0c\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u6218\u3002 \u3000\u3000\u201c\u73cf\u513f\u2026\u2026\u73cf\u513f\u2026\u2026\u201d \u3000\u3000\u547c\u5524\u7684\u58f0\u97f3\uff0c\u98d8\u8361\u5728\u5c71\u77f3\u3001\u4ead\u9662\u3001\u6797\u6728\u95f4\u3001\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u63a0\u51fa\u7a97\u5916\uff0c\u8f7b\u8f7b\u63a0\u5f00\u4e09\u4e08\uff0c\u773c \u779f\u5904\uff0c\u5434\u9e23\u4e16\u7684\u7a97\u6237\u4ecd\u672a\u5173\u597d\uff0c\u623f\u4e2d\u7adf\u7136\u6ca1\u6709\u5434\u9e23\u4e16\u7684\u5f71\u5b50\uff0c\u5b64\u706f\u672a\u7184\uff0c\u5434\u9e23\u4e16\u7adf\u50cf\u662f\u5df2\u51fa \u53bb\u597d\u4e45\u4e86\u3002 \u3000\u3000\u4ed6\u65e0\u6687\u601d\u7d22\u5434\u9e23\u4e16\u7684\u53bb\u5411\uff0c\u56e0\u4e3a\u90a3\u547c\u5524\u4e0d\u4f46\u54cd\u5728\u4ed6\u8033\u7554\uff0c\u8fd8\u4f3c\u4e4e\u54cd\u5728\u4ed6\u5fc3\u5e95\uff0c\u4ed6\u80a9\u5934\u4e00 \u8038\uff0c\u98de\u63a0\u800c\u51fa\uff0c\u4e09\u4e24\u4e2a\u8d77\u843d\uff0c\u4fbf\u5df2\u63a0\u51fa\u4e86\u8fd9\u6df1\u6c89\u51b7\u6e05\u7684\u5ead\u9662\uff0c\u53ea\u662f\u5ead\u9662\u5916\u7684\u591c\u8272\u66f4\u52a0\u6df1\u6c89\u51b7 \u6e05\u800c\u5df2\u3002 \u3000\u3000\u968f\u7740\u547c\u5524\u7684\u65b9\u5411\uff0c\u4ed6\u63d0\u8d77\u771f\u6c14\uff0c\u6709\u5982\u8f7b\u70df\u4e00\u822c\u5730\u98de\u63a0\u7740\uff0c\u5947\u602a\u7684\u662f\uff0c\u65e0\u8bba\u4ed6\u98de\u63a0\u5f97\u591a\u4e48 \u8fc5\u5feb\uff0c\u65e0\u8bba\u4ed6\u5df2\u63a0\u8fc7\u4e86\u591a\u5c11\u8def\u9014\uff0c\u8fd9\u547c\u5524\u7adf\u4ecd\u7136\u548c\u4ed6\u4fdd\u6301\u7740\u540c\u6837\u7684\u8ddd\u79bb\uff0c\u542c\u6765\u4ecd\u662f\u90a3\u4e48\u9065\u8fdc \u800c\u98d8\u6e3a\uff0c\u5982\u771f\u5982\u5e7b\uff0c\u4f3c\u8fdc\u4f3c\u8fd1\u3002 \u3000\u3000\u6781\u76ee\u671b\u53bb\uff0c\u524d\u9762\u4eff\u4f5b\u662f\u4e00\u7247\u5c0f\u5c0f\u7684\u6e56\u6cca\uff0c\u7cbc\u7cbc\u7684\u6e56\u6c34\uff0c\u5728\u591c\u8272\u4e2d\u53d1\u51fa\u68a6\u4e00\u822c\u7684\u94f6\u767d\u8272\u7684 \u5149\u6cfd\u3002 \u3000\u3000\u4ed6\u5fae\u4e00\u8fdf\u7591\uff0c\u547c\u5524\u5374\u53c8\u54cd\u8d77\uff01 \u3000\u3000\u201c\u73cf\u513f\u2026\u2026\u73cf\u513f\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e24\u58f0\u547c\u5524\u4f3c\u8f83\u771f\u5b9e\uff0c\u4ed6\u63d0\u6c14\u7eb5\u8eab\uff0c\u524d\u63a0\u5341\u4e08\uff0c\u53ea\u89c1\u8361\u6f3e\u7684\u6e56\u6c34\u7554\uff0c\u6709\u4e00\u5e62\u9634\u9634\u7684\u9ed1 \u5f71\uff0c\u4e09\u4e24\u70b9\u660f\u9ec4\u7684\u706f\u5149\uff0c\u6620\u5165\u7cbc\u7cbc\u7684\u6c34\u6ce2\u3002 \u3000\u3000\u7136\u540e\uff0c\u90a3\u5947\u5f02\u7684\u547c\u5524\u58f0\u4e0d\u53ef\u518d\u95fb\uff0c\u4ed6\u7b49\u4e86\u534a\u664c\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u96be\u9053\u5c31\u662f\u8fd9\u91cc\uff0c\u96be\u9053\u8fd9\u5c31 \u662f\u90a3\u5947\u5f02\u7684\u547c\u5524\u58f0\u53eb\u6211\u5bfb\u627e\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u4ed6\u4f0f\u4e0b\u8170\uff0c\u4ee5\u7edd\u9876\u7684\u8f7b\u529f\uff0c\u518d\u5411\u524d\u79fb\u52a8\u5341\u4e08\uff0c\u53ea\u89c1\u90a3\u4e00\u5e62\u5c4b\u5f71\uff0c\u7adf\u662f\u4e09\u8258\u5e9f\u5f03\u4e86\u7684\u697c \u8239\uff0c\u5e76\u6392\u9760\u5728\u4e00\u8d77\uff0c\u6b64\u523b\u60f3\u662f\u5df2\u88ab\u4eba\u7528\u6765\u505a\u6c34\u4e0a\u4eba\u5bb6\uff0c\u4ed6\u8fd8\u770b\u5230\u4e00\u53ea\u72f8\u732b\u6cbf\u7740\u8239\u8237\u8d70\u4eba\u8231 \u91cc\u3002 \u3000\u3000\u201c\u662f\u8c01\u4f4f\u5728\u8fd9\u91cc\uff1f\u8fd9\u91cc\u6709\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u4ed6\u671f\u5f85\u7740\u518d\u4e00\u6b21\u7684\u547c\u5524\uff0c\u4f46\u547c\u5524\u7ec8\u4e0d\u518d\u95fb\uff0c\u4e8e\u662f\u4ed6\u53cc\u81c2\u4e00\u4f38\uff0c\u8f7b\u8f7b\u843d\u5728\u5de6\u9762\u7b2c\u4e00\u8258\u8239\u8237 \u4e0a\uff0c\u6709\u5982\u843d\u53f6\u98d8\u4e0b\uff0c\u4e1d\u6beb\u6ca1\u6709\u5f15\u8d77\u534a\u5206\u58f0\u54cd\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u4ed6\u4eff\u4f5b\u4e58\u98ce\u4e00\u822c\uff0c\u63a0\u5230\u90a3\u6709\u706f\u7684\u8239\u8231\uff0c\u697c\u8239\u5df2\u65e7\uff0c\u81ea\u591a\u88c2\u9699\uff0c\u4ed6\u8c28\u614e\u5730\u51d1 \u76ee\u4e00\u671b\u2014\u2014\u53c8\u662f\u4e00\u5f20\u719f\u6089\u7684\u3001\u7f8e\u4e3d\uff0c\u800c\u82cd\u767d\u7684\u9762\u5bb9\u5448\u73b0\u5728\u4ed6\u773c\u524d\uff01 \u3000\u3000\u4ed6\u51e0\u4e4e\u8131\u53e3\u5524\u51fa\uff01 \u3000\u3000\u201c\u5b59\u9526\u5e73\uff01\u201d \u3000\u3000\u6b64\u523b\uff0c\u5728\u9eef\u6c89\u7684\u706f\u5149\u4e0b\uff0c\u76d8\u819d\u5750\u5728\u4e00\u5f20\u6728\u69bb\u4e0a\uff0c\u624b\u91cc\u8f7b\u8f7b\u629a\u5f04\u7740\u4e00\u53ea\u7070\u767d\u8272\u7684\u72f8\u732b\uff0c\u957f \u53d1\u62ab\u80a9\uff0c\u5bb9\u989c\u6194\u60b4\uff0c\u8fd9\u82cd\u767d\u800c\u7f8e\u4e3d\u7684\u5973\u5b50\uff0c\u4e0d\u5c31\u662f\u90a3\u4e00\u522b\u7ecf\u5e74\uff0c\u4e0d\u77e5\u53bb\u5411\uff0c\u4f46\u4ecd\u7559\u5728\u88f4\u73cf\u5fc3 \u91cc\u7684\u5b59\u9526\u5e73\u4e48\uff1f \u3000\u3000\u5979\u663e\u5df2\u8fdc\u6bd4\u4ee5\u524d\u6194\u60b4\uff0c\u5979\u76ee\u4e2d\u4e5f\u5931\u53bb\u4e86\u90a3\u4e00\u4efd\u52a8\u4eba\u7684\u5149\u5f69\uff0c\u4f46\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5728\u88f4\u73cf\u7684 \u773c\u4e2d\uff0c\u5979\u8fd8\u662f\u5982\u4ee5\u524d\u4e00\u6837\u5730\u4eb2\u5207\u3002 \u3000\u3000\u201c\u5979\u6ca1\u6709\u6b7b\uff01\u201d\u4e00\u9635\u72c2\u559c\uff0c\u4f7f\u5f97\u88f4\u73cf\u5df2\u5c06\u5524\u51fa\u58f0\u6765\uff0c\u4f46\u6620\u4eba\u4ed6\u773c\u5e18\u7684\u7b2c\u4e8c\u5f20\u9762\u5e9e\uff0c\u5374\u4f7f \u5f97\u4ed6\u51e0\u4e4e\u8fde\u547c\u5438\u90fd\u4e00\u8d77\u5c4f\u4f4f\u3002 \u3000\u3000\u4e00\u53ea\u8721\u70db\uff0c\u70db\u706b\u98d8\u6447\uff0c\u98d8\u6447\u7684\u70db\u706b\u65c1\uff0c\u8083\u5bb9\u7aef\u5750\u7684\u8d6b\u7136\u7adf\u662f\u90a3\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\uff0c\u4ed6\u9762 \u8272\u968f\u7740\u70db\u706b\u7684\u53d8\u5e7b\u800c\u53d8\u5e7b\u7740\uff0c\u4ed6\u8fd9\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u795e\u8272\u95f4\u7adf\u4ecd\u662f\u5982\u6b64\u4ece\u5bb9\u800c\u9547 \u5b9a\u3002 \u3000\u3000\u9694\u7740\u4e00\u5f20\u6b8b\u65e7\u7684\u684c\u5b50\uff0c\u4e0e\u6a80\u660e\u5bf9\u9762\u5750\u7740\u7684\uff0c\u7adf\u662f\u201c\u5b59\u8001\u7239\u201d\u4e00--\u201c\u65ad\u9b42\u5200\u201d\u5b59\u658c\uff0c\u8fd9\u4e45 \u5386\u98ce\u5c18\u7684\u8001\u4eba\u795e\u8272\u66f4\u52a0\u82cd\u767d\uff0c\u53f3\u9762\u7684\u8896\u5b50\u865a\u865a\u5782\u4e0b\uff0c\u663e\u89c1\u53f3\u81c2\u5df2\u88ab\u4eba\u9f50\u6839\u65ad\u53bb\uff0c\u672c\u6765\u633a\u76f4\u7684 \u8170\u8eab\uff0c\u6b64\u523b\u4e5f\u53d8\u5f97\u5f2f\u66f2\u800c\u4f5d\u507b\uff0c\u4e0d\u65f6\u53d1\u51fa\u4e00\u4e24\u58f0\u5e72\u54b3\uff0c\u66f4\u52a0\u91cd\u4e86\u4ed6\u82cd\u8001\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u770b\u6765\u5c31\u50cf\u4ed6\u9762\u524d\u7684\u8721\u70db\uff0c\u867d\u4ecd\u5728\u98ce\u4e2d\u6323\u624e\uff0c\u5374\u7ec8\u4e8e\u5c06\u8981\u7184\u706d\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u8001\u4eba\u5bf9\u9762\u800c\u5750\uff0c\u8c01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u201c\u5b59\u8001\u7239\u201d\u4f4e\u5782\u7740\u5934\uff0c\u6b63\u4ed4\u7ec6\u7aef\u8be6\u7740\u624b\u638c\u4e2d\u7684\u4e00 \u4ef6\u4e1c\u897f\u3002 \u3000\u3000\u826f\u4e45\u826f\u4e45\uff0c\u4ed6\u5c06\u638c\u4e2d\u4e4b\u7269\u8f7b\u8f7b\u653e\u5728\u684c\u4e0a\uff0c\u8d6b\u7136\u7adf\u662f\u4e00\u53ea\u201c\u78a7\u7389\u87fe\u870d\u201d\u3002 \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u9635\u72c2\u8df3\uff0c\u53ea\u542c\u201c\u5b59\u8001\u7239\u201d\u8f7b\u54b3\u7740\uff0c\u957f\u53f9\u7740\u9053\uff1a\u201c\u7f8e\u4eba\u591a\u662f\u7978\u6c34\uff0c\u5947\u73cd\u66f4\u591a\u4e0d \u7965\uff0c\u5509\u2026\u2026\u4e3a\u4e86\u8fd9\u4e00\u53ea\u2018\u78a7\u7389\u87fe\u870d\u2019\uff0c\u5f04\u5f97\u6211\u6d6a\u843d\u6c5f\u6e56\u534a\u751f\uff0c\u81f3\u4eca\u4e00\u8eab\u6b8b\u5e9f\uff0c\u8fde\u2026\u2026\u5509\uff0c\u8fde \u9526\u5e73\u90e8\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u8f7b\u54b3\u51e0\u58f0\uff0c\u5b9e\u5728\u4e0d\u5fcd\u518d\u8bf4\u4e0b\u53bb\uff0c\u584c\u4e0a\u7684\u5b59\u9526\u5e73\u5782\u4e0b\u4e86\u5934\uff0c\u79cb\u6ce2\u4e2d\u4e00\u7247\u83b9\u7136\uff0c\u7ec8\u4e8e \u5fcd\u4e0d\u4f4f\u6d41\u4e0b\u4e86\u4e24\u6ef4\u6cea\u73e0\u3002 \u3000\u3000\u5979\u5f97\u77e5\u4e0d\u4f46\u81ea\u5df1\u7684\u9752\u6625\u4e00\u53bb\uff0c\u5df2\u6c38\u65e0\u8ffd\u5bfb\u4e4b\u5904\uff0c\u4fbf\u662f\u5979\u7684\u751f\u547d\uff0c\u6b64\u540e\u4e5f\u6c38\u5c06\u5728\u6101\u82e6\u95f4\u6e21 \u8fc7\uff01 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u4e0a\u795e\u8272\u4ea6\u662f\u4e00\u9635\u9eef\u7136\uff0c\u53f9\u9053\uff1a\u201c\u9020\u5316\u5f04\u4eba\uff0c\u6bcf\u591a\u5982\u6b64\uff0c\u5b59\u5144\uff0c\u4f60\u2026\u2026 \u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u4f3c\u4e4e\u60f3\u8bf4\u51e0\u53e5\u5b89\u6170\u7684\u8bdd\uff0c\u4f46\u7ec8\u662f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u5f3a\u7b54\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f46\u6211\u81ea\u601d\u81ea\u60f3\uff0c\u5982\u4eca\u843d\u5f97\u8fd9\u79cd\u5730\u6b65\uff0c\u4e5f\u662f\u7f6a\u6709\u5e94\u5f97\uff0c\u53ea\u662f \u6a80\u5144\uff0c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5c06\u4e8b\u5b9e\u7684\u771f\u76f8\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u52a8\uff0c\u53ea\u89c1\u6a80\u660e\u773c\u5e18\u4e00\u5408\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u663e\u89c1\u662f\u611f\u89e6\u826f\u591a\uff0c\u201c\u4ed6\u611f\u89e6\u7684\u662f\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u957f\u53f9\u7740\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u6211\u5931\u53bb\u4e86\u8fd9\u2018\u78a7\u7389\u87fe\u870d\u2019\u540e\uff0c\u4fbf\u4e00\u5fc3\u4ee5\u4e3a\u5b83\u662f\u88ab\u2018\u6dee\u9633 \u4e09\u715e\u2019\u76d7\u53bb\uff0c\u7adf\u6ca1\u6709\u53bb\u8ffd\u67e5\u4e8b\u5b9e\u7684\u771f\u76f8\uff0c\u5509\u2026\u2026\u53ea\u53ef\u601c\u2018\u6dee\u9633\u4e09\u715e\u2019\u5144\u5f1f\u4e09\u4eba\u90fd\u88ab\u6211\u2026\u2026 \u5509\uff0c\u4ed6\u4eec\u867d\u7136\u4e3a\u6076\u751a\u591a\uff0c\u4f46\u53c8\u4f55\u5c1d\u5f97\u7f6a\u4e86\u6211\uff0c\u53cd\u662f\u6211\u9519\u602a\u4e86\u4ed6\u4eec\uff0c\u6211\u2026\u2026\u6211\u8fd9\u4e0d\u662f\u7f6a\u6709\u5e94\u5f97 \u4e48\uff1f\u201d \u3000\u3000\u9f99\u5f62\u516b\u638c\u201c\u6a80\u660e\u5f20\u5f00\u773c\u6765\uff0c\u832b\u7136\u7591\u89c6\u7740\u70db\u5149\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5929\u7f51\u6062\u6062\u201d\u758f\u800c\u4e0d\u6f0f\uff0c\u5584\u6076\u4e4b \u62a5\uff0c\u6700\u662f\u4ee4\u4eba\u96be\u6d4b\uff0c\u2018\u6dee\u9633\u4e09\u715e\u2019\u4f5c\u6076\u591a\u7aef\uff0c\u6ca1\u6709\u88ab\u4ec7\u5bb6\u6740\u6b7b\uff0c\u5374\u6b7b\u5728\u4f60\u624b\u91cc\uff0c\u4f60\u5fc3\u91cc\u81ea\u7136 \u96be\u53d7\uff0c\u4f46\u4f60\u82e5\u4ed4\u7ec6\u4e00\u60f3\uff0c\u53c8\u4f55\u5c1d\u4e0d\u4f1a\u662f\u82cd\u5929\u501f\u4f60\u4e4b\u624b\uff0c\u6765\u5c06\u4ed6\u4eec\u9664\u53bb\u5462\uff1f\u201c\u8fd9\u5145\u6ee1\u54f2\u7406\u7684\u8a00 \u8bed\uff0c\u4f7f\u5f97\u5b59\u658c\u53cc\u7709\u4e00\u626c\uff0c\u4f46\u77ac\u4ef0\u53f9\u9053\uff1a\u201c\u6211\u65e0\u5fc3\u94f8\u4e0b\u4e86\u8fd9\u822c\u5927\u9519\uff0c\u4e5f\u53d7\u5230\u4e86\u5e94\u5f97\u7684\u62a5\u5e94\uff0c\u8fd9 \u6837\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u5728\u4e5d\u6cc9\u4e4b\u4e0b\u4e5f\u4f1a\u5b89\u5fc3\u4e9b\uff0c\u53ea\u662f\u6a80\u5144\uff0c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u2026\u2026\u201d \u3000\u3000\u6a80\u660e\u622a\u53e3\u53f9\u9053\uff1a\u201c\u6211\u5982\u4eca\u53d7\u8fd9\u6837\u7684\u51a4\u66f2\u3001\u4fae\u8fb1\uff0c\u5b9e\u5728\u4e5f\u662f\u7f6a\u6709\u5e94\u5f97\uff0c\u6211\u672c\u60f3\u5c06\u8fd9\u2018\u78a7\u7389 \u87fe\u870d\u2019\u7269\u5f52\u539f\u4e3b\u540e\uff0c\u5c31\u8fdc\u8fdc\u4e00\u8d70\uff0c\u8ba9\u6240\u6709\u7684\u7f6a\u5b7d\u90fd\u7b97\u5728\u6211\u8eab\u4e0a\uff0c\u8ba9\u8fd9\u4e00\u6bb5\u6b66\u6797\u4e2d\u7684\u9690\u5bc6\uff0c\u6c38 \u8fdc\u57cb\u85cf\uff0c\u4f46\u2026\u2026\u4f46\u6211\u6ee1\u8154\u79ef\u90c1\u4e0d\u5410\uff0c\u5b9e\u662f\u6b7b\u96be\u7791\u76ee\u3002\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u4e2d\u53c8\u662f\u4e00\u52a8\uff0c\u4ed6\u5df2\u6e10\u6e10\u542c\u51fa\u6b64\u4e8b\u5176\u4e2d\u5fc5\u5b9a\u8fd8\u9690\u85cf\u7740\u4e00\u4ef6\u66f2\u6298\u3001\u79bb\u5947\u3001\u8be1\u5f02\u7684\u7ecf \u8fc7\uff0c\u90a3\u5176\u4e2d\u5fc5\u5b9a\u4e0d\u77e5\u5305\u6db5\u7740\u591a\u5c11\u5fc3\u9178\u4e0e\u8840\u6cea\uff01 \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u8f7b\u54b3\u7740\u62ff\u8d77\u4e00\u4e2a\u9648\u65e7\u7684\u9152\u846b\u82a6\uff0c\u5728\u4e24\u53ea\u571f\u7897\u4e2d\uff0c\u659f\u4e0b\u4e86\u6ee1\u6ee1\u4e24\u7897\u9152\uff0c\u201c\u9f99\u5f62 \u516b\u638c\u201d\u4e00\u996e\u800c\u5c3d\uff0c\u76ee\u5149\u4e2d\u795e\u5149\u4e00\u95ea\uff0c\u77ac\u5373\u53c8\u53d8\u5f97\u6ee1\u9762\u60d8\u7136\uff0c\u832b\u7136\u51dd\u6ce8\u7740\u98d8\u6447\u7740\u7684\u706b\u70db\uff0c\u50cf\u662f \u5df2\u56de\u5230\u9065\u8fdc\u7684\u5f80\u4e8b\u4e2d\u53bb\u3002 \u3000\u3000\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u624d\u7f13\u7f13\u5f00\u53e3\u9053\uff1a\u201c\u5341\u591a\u5e74\u4e86\u2026\u2026\u5341\u591a\u5e74\u524d\uff0c\u90a3\u65f6\u6211\u8fd8\u6ee1\u8154\u96c4\u5fc3\u58ee \u5fd7\uff0c\u5c31\u5728\u6b66\u6797\u4e2d\u521a\u521a\u51fa\u73b0\u4e86\u90a3\u795e\u79d8\u800c\u6b8b\u9177\u7684\u8499\u9762\u4eba\u4e4b\u540e\uff0c\u6211\u4fbf\u7acb\u4e0b\u51b3\u5fc3\uff0c\u8981\u67e5\u51fa\u6b64\u4e2d\u7684\u79d8 \u5bc6\uff0c\u4e8e\u662f\u6211\u653e\u4e0b\u4e00\u5207\u4e8b\u52a1\uff0c\u5b64\u8eab\u51fa\u6765\u63a2\u67e5\u2026\u2026\u201d \u3000\u3000\u88f4\u73cf\u53ea\u89c9\u5fc3\u623f\u4e2d\u5982\u4e2d\u5de8\u77f3\u4e00\u51fb\uff0c\u51db\u7136\u4ed8\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4e0d\u662f\u90a3\u8499\u9762\u4eba\uff1f\u96be\u9053\u771f\u662f\u6211\u4eec\u9519\u602a \u4e86\u4ed6\uff1f\u201d \u3000\u3000\u53ea\u542c\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u90a3\u65f6\u5b59\u5144\u4f60\u4e5f\u6b63\u62a4\u9001\u7740\u8fd9\u53ea\u2018\u78a7\u7389\u87fe\u870d\u2019\u8d77\u7a0b\uff0c\u6211\u76d8\u7b97\u7740\u90a3\u795e\u79d8\u7684\u8499 \u9762\u5bb9\u5fc5\u5b9a\u4f1a\u5411\u4f60\u4e0b\u624b\uff0c\u662f\u4ee5\u4fbf\u5728\u6697\u4e2d\u8ffd\u968f\u7740\u4f60\uff01\u201d \u3000\u3000\u201c\u76f4\u5230\u6cb3\u5317\u5883\u5185\uff0c\u4e00\u4e2a\u98ce\u96e8\u4e4b\u591c\uff0c\u5728\u90a3\u5c71\u57ce\u4e4b\u4e2d\uff0c\u9047\u7740\u2018\u6dee\u9633\u4e09\u715e\u2019\uff0c\u4f3c\u4e4e\u4e5f\u8981\u5411\u4f60\u4e0b \u624b\uff0c\u6211\u751f\u6015\u4ed6\u4eec\u8bef\u4e86\u6211\u7684\u8ba1\u5212\uff0c\u4fbf\u4e00\u76f4\u76d1\u89c6\u7740\u4ed6\u4eec\uff0c\u54ea\u77e5\u5c31\u5728\u90a3\u4e00\u591c\uff0c\u4f60\u7684\u2018\u78a7\u7389\u87fe\u870d\u2019\u5931 \u7a83\uff0c\u8ddf\u968f\u4f60\u7684\u4e24\u4e2a\u9556\u5e08\uff0c\u4e5f\u906d\u4e86\u6bd2\u624b\uff01\u201d \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5f53\u771f\u662f\u9634\u5dee\u9633\u9519\uff0c\u6211\u82e5\u975e\u5728\u5931\u76d7\u7684\u524d\u591c\u89c1\u5230\u2018\u6dee\u9633 \u4e09\u715e\u2019\uff0c\u4e5f\u4e0d\u4f1a\u5c06\u6b64\u4e8b\u9519\u7591\u5230\u4ed6\u4eec\u8eab\u4e0a\uff0c\u65e5\u540e\u4e5f\u4e0d\u81f4\u751f\u51fa\u90a3\u4e48\u591a\u4e8b\u6545\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u9894\u9996\u53f9\u9053\uff1a\u201c\u6211\u82e5\u975e\u662f\u76d1\u89c6\u2018\u6dee\u9633\u4e09\u715e\u2019\uff0c\u4e5f\u4e0d\u81f4\u8ba9\u522b\u4eba\u5f97\u624b\uff0c\u76f4\u5230 \u6211\u542c\u5230\u4f60\u624b\u4e0b\u9556\u5e08\u7684\u60e8\u547c\uff0c\u8fde\u5fd9\u8d76\u56de\u53bb\u65f6\uff0c\u6211\u53ea\u89c1\u4e24\u6761\u9ed1\u5f71\uff0c\u6025\u4e89\u63a0\u8d70\uff0c\u6211\u6697\u4e2d\u8ffd\u4e86\u4e0b\u53bb\uff0c \u7ec8\u4e8e\u53d1\u73b0\u90a3\u4e24\u4eba\u7adf\u662f\u2018\u67aa\u5251\u65e0\u654c\u2019\u88f4\u6c0f\u5144\u5f1f\uff01\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u88f4\u73cf\u7684\u5fc3\u810f\u4e5f\u51e0\u4e4e\u505c\u6b62\u4e86\u8df3\u52a8\uff0c\u4ed6\u51e0\u4e4e\u4e0d\u6562\u518d\u542c\u4e0b\u53bb\uff0c\u4ed6\u51e0\u4e4e\u8981\u7834\u95e8\u800c \u51fa\uff0c\u4ed6\u4e0d\u80fd\u76f8\u4fe1\u4ed6\u81ea\u5df1\u7684\u7239\u7239\u751f\u524d\u4f1a\u505a\u4e0b\u4e0d\u53ef\u5bbd\u6055\u7684\u7f6a\u6076\u3002 \u3000\u3000\u53ea\u542c\u6a80\u660e\u63a5\u9053\uff1a\u201c\u90a3\u65f6\u6211\u771f\u4e0d\u6562\u76f8\u4fe1\u4e00\u5411\u6b63\u76f4\u7684\u88f4\u6c0f\u5144\u5f1f\u7adf\u4f1a\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\uff01\u4f46\u4e8b\u5b9e\u5982 \u6b64\uff0c\u5374\u53c8\u4ee4\u6211\u4e0d\u5f97\u4e0d\u4fe1\uff0c\u6211\u8ba4\u5b9a\u8fd9\u5144\u5f1f\u4e24\u4eba\uff0c\u5fc5\u5b9a\u4fbf\u662f\u90a3\u6b8b\u5fcd\u7684\u8499\u9762\u5bb9\uff0c\u4ed6\u4eec\u4e4b\u6240\u4ee5\u6ca1\u6709\u5c06 \u4f60\u6740\u6b7b\uff0c\u53ea\u4e0d\u8fc7\u662f\u88ab\u6211\u51fb\u9000\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u53f9\u606f\u4e00\u58f0\uff0c\u6a80\u660e\u63a5\u9053\uff1a\u201c\u4e8e\u662f\u4fbf\u8d77\u4e86\u6740\u673a\uff0c\u7ec8\u4e8e\u5728\u4fdd\u5b9a\u57ce\u5916\uff0c\u5c06\u4ed6\u5144\u5f1f\u4e24\u4eba \u51fb\u6bd9\uff0c\u90a3\u65f6\u6211\u5fc3\u5b89\u7406\u5f97\uff0c\u4ee5\u4e3a\u81ea\u5df1\u662f\u5728\u66ff\u5929\u884c\u9053\uff0c\u5230\u540e\u6765\u2026\u2026\u5509\uff0c\u6211\u624d\u77e5\u9053\u6211\u5df2\u505a\u4e0b\u4e00\u4ef6\u4e0d \u53ef\u5f25\u8865\u7684\u9519\u8bef\uff0c\u6211\u8fd9\u9519\u8bef\u7684\u4ee3\u4ef7\uff0c\u8981\u4ee5\u6211\u7ec8\u8eab\u7684\u75db\u82e6\u507f\u4ed8\u5e7f\u88f4\u73cf\u7d27\u63e1\u53cc\u62f3\uff0c\u7d27\u54ac\u7259\u5173\uff0c\u53ea\u542c \u6a80\u660e\u63a5\u9053\uff1a\u201c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u90a3\u2018\u78a7\u7389\u87fe\u870d\u2019\u539f\u662f\u4e00\u4e2a\u585e\u4e0a\u7684\u4f20\u5bb6\u4e4b\u5b9d\uff0c\u800c\u88ab\u90a3\u8c6a\u95e8\u6240\u593a\uff0c \u4ea4\u6258\u4e8e\u4f60\uff0c\u901f\u5230\u4eac\u57ce\u53bb\u4e3a\u4ed6\u7684\u513f\u5b50\u535a\u53d6\u529f\u540d\uff0c\u88f4\u6c0f\u5144\u5f1f\u8def\u89c1\u4e0d\u5e73\uff0c\u624d\u8981\u5c06\u4e4b\u593a\u56de\u7269\u5f52\u539f\u4e3b\uff0c \u5374\u4e0d\u77e5\u9020\u5316\u5f04\u4eba\uff0c\u4e00\u81f3\u4e8e\u6b64\uff0c\u4ee4\u88f4\u6c0f\u5144\u5f1f\u542b\u6068\u800c\u7ec8\uff0c\u4ee4\u6211\u4e5f\u94f8\u4e0b\u8fd9\u65e0\u53ef\u633d\u56de\u7684\u5927\u9519\uff01\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u4ea6\u4e0d\u77e5\u662f\u559c\uff1f\u662f\u60b2\uff1f\u662f\u9a84\u505a\uff1f\u662f\u6028\u6068\uff1f\u662f\u611f\u6168\uff1f\u662f\u75db\u82e6\uff1f\u662f\u8be5 \u5bfb\u6a80\u660e\u590d\u4ec7\uff1f\u6291\u6216\u662f\u8be5\u5411\u82cd\u5929\u63a7\u8bc9\uff1f \u3000\u3000\u6a80\u660e\u5df2\u63a5\u7740\u53f9\u9053\uff1a\u201c\u5230\u540e\u6765\u90a3\u5bd2\u58eb\u542b\u6068\u800c\u6b7b\uff0c\u90a3\u4ed7\u52bf\u51cc\u4eba\u7684\u8c6a\u95e8\u5de8\u5bcc\uff0c\u4e5f\u56e0\u4e8b\u503e\u5bb6\uff0c\u4ed6 \u7684\u72ec\u5b50\u5374\u6d41\u843d\u6c5f\u6e56\u2026\u2026\u201d \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u53cc\u76ee\u4e00\u5f20\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u6b64\u4eba\u540e\u6765\u600e\u4e48\u4e86\uff1f\u8ffd\u6839\u7a76\u5e95\uff0c\u6b64\u4eba\u5b9e\u662f\u7978\u9996\uff0c\u82cd\u5929\u82e5 \u662f\u6709\u773c\uff0c\u4e5f\u5e94\u8ba9\u4ed6\u53d7\u4e9b\u62a5\u5e94\u624d\u662f\uff0c\u6211\u8fd8\u8bb0\u5f97\u90a3\u8c6a\u95e8\u4f3c\u662f\u59d3\u82b1\u3002\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u7f13\u7f13\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u59d3\u82b1\uff0c\u4ed6\u6d41\u843d\u6c5f\u6e56\uff0c\u4ee5\u51fa\u5356\u6d88\u606f\u4e3a\u751f\uff0c\u9996\u9f20\u4e24\u7aef\uff0c\u6709\u5982 \u5899\u5934\u4e4b\u8349\uff0c\u4eba\u79f0\u2018\u5feb\u8baf\u2019\u82b1\u738b\uff0c\u5230\u540e\u6765\u2026\u2026\u5509\uff0c\u5230\u540e\u6765\u4ed6\u7ec8\u4e8e\u6b7b\u5728\u2018\u795e\u624b\u2019\u6218\u98de\u7684\u5e84\u95e8\u4e4b \u5916\uff0c\u81f3\u4eca\u5374\u4ecd\u4e0d\u77e5\u662f\u6b7b\u5728\u8c01\u7684\u624b\u4e0a\uff1f\u201d \u3000\u3000\u88f4\u73cf\u5fc3\u5934\u4e00\u9707\uff0c\u60c5\u4e0d\u81ea\u7981\u5730\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c9\u9edd\u9eef\u7684\u82cd\u7a7a\u4e2d\uff0c\u4eff\u4f5b\u6b63\u6709\u4e24\u53ea\u773c\u775b\uff0c\u5728\u9ed8\u9ed8 \u5730\u67e5\u770b\u4eba\u95f4\u7684\u5584\u826f\u4e0e\u7f6a\u6076\uff0c\u4e00\u4e1d\u4e5f\u4e0d\u4f1a\u9519\u8fc7\u3002 \u3000\u3000\u8d4f\u4e0e\u7f5a\uff0c\u867d\u7136\u4e5f\u8bb8\u6765\u5f97\u5f88\u8fdf\uff0c\u4f46\u4f60\u5374\u6c38\u8fdc\u4e0d\u8981\u5e0c\u671b\u5f53\u4f60\u79cd\u4e00\u7c92\u7f6a\u6076\u7684\u79cd\u5b50\uff0c\u4f1a\u6536\u5230\u751c\u871c \u7684\u679c\u5b9e\u4e0e\u82b1\u6735\u3002 \u3000\u3000\u4e00\u9635\u7531\u656c\u754f\u800c\u751f\u51fa\u7684\u60ca\u6817\uff0c\u4f7f\u5f97\u88f4\u73cf\u5168\u8eab\u90fd\u51e0\u4e4e\u98a4\u6296\u8d77\u6765\uff0c\u4ed6\u8f7b\u8f7b\u5408\u8d77\u624b\u638c\uff0c\u5411\u51a5\u51a5\u4e4b \u4e2d\u7684\u4e3b\u5bb0\u4f5c\u6700\u8654\u8bda\u7684\u656c\u793c\u3002 \u3000\u3000\u6a80\u660e\u53c8\u63a5\u7740\u53f9\u9053\uff1a\u201c\u6211\u5e73\u751f\u9664\u4e86\u9519\u6740\u4e86\u2018\u88f4\u6c0f\u5144\u5f1f\u2019\u5916\uff0c\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u4e5f\u4ee4\u6211\u81f3\u4eca\u72b9\u5728 \u96be\u53d7\uff01\u201d \u3000\u3000\u201c\u6211\u8fd4\u56de\u4eac\u57ce\u4e4b\u540e\uff0c\u5b9e\u5df2\u5fc3\u7070\u610f\u51b7\uff0c\u90a3\u65f6\u2019\u4e2d\u5dde\u4e00\u5251\u2018\u6b27\u9633\u5e73\u4e4b\u5374\u7a81\u7136\u6765\u5230\u4eac \u3000\u3000\u57ce\uff0c\u6211\u4e00\u76f4\u5bf9\u6b64\u4eba\u751a\u4e3a\u5c0a\u656c\uff0c\u662f\u4ee5\u4fbf\u5c06\u4ed6\u7559\u5728\u9556\u5c40\u4e4b\u4e2d\u3002\u201d \u3000\u3000 \u3000\u3000\u201c\u6709\u4e00\u5929\u665a\u4e0a\uff0c\u6211\u4e0e\u4ed6\u5728\u5b81\u4e0b\u5bf9\u914c\uff0c\u6b63\u5f53\u6211\u8f6c\u8eab\u914c\u9152\u7684\u65f6\u5019\uff0c\u7adf\u4ece\u5899\u89d2\u7684\u4e00\u4e2a\u94dc\u955c\u91cc\uff0c \u770b\u5230\u4ed6\u52ff\u5306\u5728\u6211\u676f\u4e2d\u503e\u4e0b\u4e00\u4e9b\u767d\u8272\u7c89\u672a\u3002\u201d \u3000\u3000\u201c\u6211\u60ca\u7591\u4e4b\u4e0b\uff0c\u5374\u4ecd\u4f5c\u82e5\u65e0\u5176\u4e8b\uff0c\u53ea\u662f\u5c06\u90a3\u676f\u9152\u5077\u5077\u5012\u4e86\uff0c\u6211\u540e\u6765\u53c8\u88c5\u4f5c\u4e0d\u80dc \u3000\u3000\u9152\u529b\uff0c\u672a\u5230\u8d77\u66f4\uff0c\u4fbf\u56de\u623f\u4e2d\u3002\u201d \u3000\u3000 \u3000\u3000\u201c\u6211\u7b97\u5b9a\u4e86\u6b27\u9633\u5e73\u4e4b\u5f53\u591c\u5fc5\u6709\u52a8\u4f5c\uff0c\u4f46\u90a3\u65f6\u6211\u8fd8\u771f\u4e0d\u6562\u76f8\u4fe1\u8fd9\u5fb7\u9ad8\u671b\u91cd\u7684\u8001\u9556\u5934\u7adf\u662f\u5982 \u6b64\u8fd9\u6837\u4e00\u4e2a\u6076\u9b54\u3002\u201d \u3000\u3000\u201c\u5230\u4e86\u4e09\u66f4\u5de6\u53f3\uff0c\u6211\u679c\u7136\u542c\u5230\u4ed6\u5728\u7a97\u5916\u8f7b\u8f7b\u5524\u6211\uff0c\u53eb\u6211\u51fa\u53bb\uff0c\u6211\u90a3\u65f6\u53c8\u89c9\u5947\u602a\uff0c\u4ed6\u82e5\u60f3 \u5bb3\u6211\uff0c\u4e3a\u4f55\u53c8\u8981\u8d39\u5982\u6b64\u5468\u6298\uff0c\u6211\u4e3a\u4e86\u4e00\u67e5\u7a76\u7adf\uff0c\u6ca1\u6709\u60ca\u52a8\u4eba\uff0c\u4fbf\u8f7b\u8f7b\u7eb5\u4e86\u51fa\u53bb\uff0c\u4e0e\u4ed6\u4e00\u8d77\u63a0 \u51fa\u5317\u4eac\u57ce\u5916\u3002\u201d \u3000\u3000\u90a3\u4e00\u591c\u5929\u6c14\u751a\u662f\u5bd2\u51b7\uff0c\u57ce\u5916\u4e00\u7247\u767d\u96ea\uff0c\u6211\u5fcd\u4e0d\u4f4f\u95ee\u4ed6\u8981\u505a\u4ec0\u4e48\uff1f\u4ed6\u7adf\u7a81\u5730\u4ef0\u5929\u72c2\u7b11\u8d77 \u6765\uff0c\u95ee\u6211\u53ef\u77e5\u90a3\u8499\u9762\u5ba2\u662f\u8c01\uff1f\u6211\u5fc3\u5934\u4e00\u52a8\uff0c\u4ed6\u5df2\u72c2\u7b11\u7740\u9053\uff1a\u201c\u90a3\u8499\u9762\u5ba2\u5c31\u662f\u6211\u6b27\u9633\u5e73\u4e4b\u3002\u201d \u3000\u3000\u6211\u4e00\u542c\u4e4b\u4e0b\uff0c\u81ea\u662f\u5927\u60ca\uff0c\u4ed6\u5374\u53c8\u7b11\u9053\uff1a\u201c\u81ea\u4eca\u591c\u4ee5\u540e\uff0c\u8fd9\u795e\u79d8\u7684\u8499\u9762\u4eba\u4fbf\u5c06\u6c38\u8fdc\u7edd\u8ff9\u6c5f \u6e56\uff0c\u4f60\u53ef\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u65e2\u60ca\u53c8\u5947\uff0c\u4ed6\u5df2\u72c2\u7b11\u7740\u63a5\u53e3\u9053\uff1a\u2018\u53ea\u56e0\u6b66\u6797\u4e2d\u9556\u5c40\u90fd\u5df2\u89e3\u6563\uff0c\u6211\u5c06\u4f60\u6740\u6b7b\u4e4b\u540e\uff0c\u6211 \u518d\u65e0\u53ef\u6740\u4e4b\u4eba\uff01\u2019\u201d \u3000\u3000\u201c\u6211\u51b7\u7b11\u7740\u9053\uff1a\u2018\u53ea\u6015\u672a\u5fc5\u5427\uff01\u2019\u5176\u5b9e\u5fc3\u4e2d\u5374\u5728\u5e86\u5e78\u6ca1\u6709\u670d\u4e0b\u90a3\u4e00\u676f\u6bd2\u9152\uff0c\u5bd2\u98ce\u55d6\u55d6\uff0c \u6211\u638c\u5fc3\u5b9e\u5df2\u6d41\u6ee1\u51b7\u6c57\u3002\u201c\u201d\u6b27\u9633\u5e73\u4e4b\u679c\u7136\u72c2\u7b11\u9053\uff1a\u2018\u4f60\u5df2\u670d\u4e0b\u6211\u7a7f\u80a0\u8680\u9aa8 \u3000\u3000\u7684\u6bd2\u836f\uff0c\u6b64\u523b\u4f60\u7684\u52a8\u529b\u5df2\u51cf\u4e86\u4e03\u6210\uff0c\u6211\u53ea\u8981\u4e3e\u624b\u4e4b\u52b3\uff0c\u4fbf\u5c06\u4f60\u51fb\u6bd9\uff0c\u90a3\u65f6\u6211\u5c31\u7b49\u5728 \u3000\u3000\u6b64\u5904\uff0c\u7b49\u5230\u7b2c\u4e00\u4e2a\u8d70\u8fc7\u6b64\u95f4\u4e4b\u4eba\uff0c\u6211\u5c31\u5c06\u4ed6\u6740\u6b7b\uff0c\u5c06\u4ed6\u9762\u76ee\u51fb\u6bc1\u3002 \u3000\u3000 \u3000\u3000\u518d\u5c06\u8eab\u8fb9\u6240\u5907\u7684\u9ed1\u8863\uff0c\u7a7f\u5728\u4ed6\u8eab\u4e0a\uff0c\u7b49\u5230\u660e\u65e5\u6b66\u6797\u4e2d\u4eba\u89c1\u4e86\uff0c\u5fc5\u5b9a\u4ee5\u4e3a\u2018\u9f99\u5f62\u516b\u638c\u2019\u5df2 \u4e0e\u8499\u9762\u5bb9\u540c\u5f52\u4e8e\u5c3d\uff0c\u90a3\u65f6\u6211\u4fbf\u53ef\u6c38\u9738\u6b66\u6797\uff0c\u800c\u4f60\u4e5f\u53ef\u843d\u4e2a\u4fa0\u4e49\u540d\u58f0\uff0c\u8fd9\u5f53\u771f\u662f\u4e24\u5168\u5176\u7f8e\u7684\u4e8b \u60c5\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7b11\u5bb9\u4e2d\u5145\u6ee1\u5f97\u610f\u4e4b\u60c5\uff0c\u53ea\u542c\u5f97\u6211\u6012\u706b\u4e0a\u6d8c\uff0c\u4ed6\u8bed\u58f0\u672a\u4e86\uff0c\u6211\u5df2\u4e00\u638c\u51fb\u51fa\uff0c\u4ed6\u4fbf\u4e0d\u7ecf \u5fc3\u5730\u968f\u624b\u4e00\u6321\uff0c\u6211\u62db\u5f0f\u7acb\u53d8\uff0c\u62fc\u5c3d\u5168\u529b\uff0c\u6570\u62db\u4e4b\u5185\uff0c\u4fbf\u5c06\u4ed6\u6bd9\u5728\u638c\u4e0b\uff0c\u4ed6\u4e34\u6b7b\u524d\u9762\u4e0a\u8fd8\u5e26\u6709 \u60ca\u9a87\u7684\u8868\u60c5\uff0c\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48\u4ed6\u7684\u6bd2\u836f\u5bf9\u6211\u6beb\u65e0\u6548\u529b\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201c\u795e\u60c5\u6fc0\u52a8\uff0c\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u8bf4\u5230\u8fd9\u91cc\u7a81\u53c8\u6614\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u6211\u90a3\u65f6\u5fc3\u91cc\u4e0d\u8be5\u5347\u8d77 \u4e2a\u5947\u602a\u7684\u4e3b\u610f\uff0c\u7adf\u60f3\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u7adf\u771f\u7684\u7b49\u5728\u90a3\u91cc\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u6709\u4e00\u4e2a \u9189\u6c49\u81ea\u7530\u964c\u95f4\u8d70\u6765\uff0c\u6211\u4e00\u5ff5\u4e4b\u5dee\uff0c\u5c06\u4e4b\u51fb\u6bd9\uff0c\u4e3a\u4ed6\u7a7f\u4e0a\u6b27\u9633\u5e73\u4e4b\u6240\u5907\u7684\u9ed1\u8863\uff0c\u4e58\u591c\u8fd4\u56de\u57ce \u91cc\uff01\u201d \u3000\u3000\u201c\u5509\uff0c\u60f3\u4e0d\u5230\u6211\u4e00\u5ff5\u4e4b\u5dee\uff0c\u7adf\u4f7f\u5f97\u6211\u7ec8\u8eab\u62b1\u6068\uff0c\u6211\u4eca\u65e5\u5373\u4f7f\u8bf4\u51fa\u5f53\u65f6\u60c5\u51b5\uff0c\u6b66\u6797\u4e2d\u53c8\u6709 \u8c01\u4f1a\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u4eba\u4eba\u4fbf\u90fd\u9677\u5165\u4e00\u79cd\u83ab\u540d\u7684\u60c5\u7eea\u4e2d\uff0c\u4e3a\u4e4b\u76ee\u77aa\u53e3\u5446\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u88f4\u73cf\u66f4\u662f\u624b\u8db3\u51b0\u51b7\uff0c\u53ea\u89c1\u8239\u8231\u5916\u7a81\u5730\u7f13\u7f13\u8d70\u5165\u4e00\u4e2a\u9762\u5bb9\u5446\u6728\uff0c\u5f62\u5982\u767d\u75f4\u7684\u6c49\u5b50\uff0c\u5934\u53d1\u84ec \u4e71\uff0c\u6ee1\u8eab\u69db\u697c\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u846b\u82a6\u9152\uff0c\u653e\u5230\u684c\u4e0a\uff0c\u56de\u8eab\u5c31\u8d70\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u4e00\u53d8\uff0c\u6c89\u58f0 \u9053\uff1a\u201c\u6b64\u4eba\u662f\u8c01\uff1f\u6211\u65b9\u624d\u6240\u8bf4\u7684\u8bdd\uff0c\u4ed6\u53ef\u66fe\u542c\u5230\uff1f\u201d \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u6447\u5934\u9053\uff1a\u201c\u6b64\u4eba\u53c8\u5446\u53c8\u75f4\uff0c\u6709\u65f6\u7ec8\u65e5\u4e0d\u53d1\u4e00\u8bed\uff0c\u5373\u4f7f\u88ab\u4ed6\u542c\u5230\u4ea6\u662f\u65e0\u59a8\u3002\u201d \u3000\u3000\u4ed6\u7a81\u5730\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u7236\u5973\u81ea\u4ece\u88ab\u2018\u5343\u624b\u4e66\u751f\u2019\u4f24\u6b8b\uff0c\u53c8\u88ab\u2018\u91d1\u7ae5\u7389\u5973\u2019\u4e24\u4f4d\u524d\u8f88\u6551 \u6765\u6b64\u95f4\u4e4b\u540e\uff0c\u4fbf\u591a\u4e8f\u6b64\u4eba\u7167\u987e\u996e\u98df\uff0c\u5426\u5219\u2026\u2026\uff0c\u5509\u3002\u53ea\u6015\u6211\u7236\u5973\u65e9\u5df2\u997f\u6b7b\u4e86\uff01\u201d \u3000\u3000\u957f\u53f9\u4e00\u58f0\u4e2d\uff0c\u4ed6\u4e3e\u8d77\u846b\u82a6\uff0c\u4e3a\u6a80\u660e\u659f\u4e86\u4e00\u7897\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u4eca\u591c\u5f53\u771f\u5fc3\u4e8b\u91cd\u91cd\uff0c\u9152\u5230\u676f\u5e72\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u53c8\u81ea\u53f9\u9053\uff1a\u201c\u8fd9\u2018\u91d1\u7ae5\u7389 \u5973\u2019\u4e24\u4f4d\u524d\u8f88\uff0c\u5f53\u771f\u662f\u6b66\u6797\u5947\u4eba\uff0c\u4e16\u4e0a\u4efb\u4f55\u4e8b\uff0c\u90fd\u4eff\u4f5b\u7792\u4e0d\u8fc7\u4ed6\u4eec\u2014\u2014\u201d\u201c\u5b59\u8001\u7239\u201d\u7a81\u5730\u622a \u53e3\u9053\uff1a\u201c\u8fd9\u4ef6\u6b66\u6797\u516c\u6848\uff0c\u867d\u662f\u79bb\u5947\u8be1\u5f02\u5f97\u8ba9\u4eba\u4e0d\u53ef\u601d\u8bae\uff0c\u4f46\u5230\u4e86\u6b64\u523b\uff0c\u5584\u6076\u5404\u6709\u6240\u62a5\uff0c\u5df2\u53ef \u7b97\u662f\u4e86\u7ed3\uff0c\u53ea\u662f\u2014\u2014\u5509\uff0c\u53ea\u6709\u90a3\u2018\u67aa\u5251\u65e0\u654c\u2019\u88f4\u6c0f\u5144\u5f1f\u4e24\u4eba\uff0c\u5374\u662f\u6b7b\u5f97\u5927\u4e0d\u503c\u5f97\u4e86\u4e9b\uff01\u201d \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u731b\u7136\u53f9\u9053\uff1a\u201c\u4f46\u662f\u4ed6\u5144\u5f1f\u4e24\u4eba\uff0c\u4e5f\u7b97\u6709\u4e86\u5584\u62a5\uff0c\u4ed6\u5144\u5f1f\u7684\u540e\u4eba\u88f4\u73cf\uff0c \u5df2\u6210\u4e86\u4eca\u65e5\u6b66\u6797\u7684\u4e00\u9897\u660e\u661f\uff0c\u5509\u2026\u2026\u5f53\u65f6\u6211\u53ea\u89c9\u6b66\u6797\u4e2d\u7ec8\u65e0\u5584\u679c\uff0c\u56e0\u4e4b\u6ca1\u6709\u4f20\u6388\u4ed6\u7684\u6b66\u529f\uff0c \u60f3\u4e0d\u5230\u4eca\u65e5\u8fd8\u662f\u5b66\u6210\u4e86\u4e00\u8eab\u60ca\u4eba\u7edd\u827a\u3002\u201d \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u76ee\u5149\u4e00\u4eae\uff0c\u65b9\u5f85\u8bf4\u8bdd\uff0c\u7acb\u542c\u201c\u9f99\u5f62\u516b\u638c\u201d\u72c2\u543c\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u9707\uff0c\u5c06\u6728\u60bc\u9707\u5f97 \u7247\u7247\u7c89\u788e\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u7a97\u5916\u7a81\u5730\u5c04\u6765\u4e09\u9053\u767d\u5149\uff0c\u4ff1\u90fd\u51fb\u5728\u6a80\u660e\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u9f99\u5f62\u516b\u638c\u201d\u690d\u660e\u518d\u6b21\u5927\u559d\u4e00\u58f0\uff0c\u7ffb\u8eab\u8dcc\u5012\u3002 \u3000\u3000\u201c\u5b59\u8001\u7239\u201d\u60ca\u547c\u9053\uff1a\u201c\u8c01\uff1f\u8fd9\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u8231\u5916\u5df2\u63a0\u5165\u4e00\u6761\u4eba\u5f71\uff0c\u672c\u5df2\u51c9\u8bb6\u4e07\u5206\u7684\u88f4\u73cf\uff0c\u53c8\u662f\u4e00\u60ca\uff0c\u8fd9\u4eba\u5f71\u8d6b\u7136\u7adf\u662f \u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u6ee1\u9762\u6740\u673a\uff0c\u53e3\u5e26\u72de\u7b11\uff0c\u4e00\u628a\u5c06\u6a80\u660e\u81ea\u5730\u4e0a\u62c9\u8d77\u3002\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u6b64\u523b\u5df2\u662f\u6ee1\u8eab \u9c9c\u8840\uff0c\u9762\u5bb9\u626d\u66f2\uff0c\u6b64\u523b\u70db\u5149\u5df2\u706d\uff0c\u53ea\u6709\u9694\u58c1\u7684\u4e00\u76cf\u94dc\u706f\u4ecd\u5728\u53d1\u7740\u660f\u5149\uff0c\u9eef\u6de1\u7684\u5149\u7ebf\uff0c\u5c06\u4ed6\u7684 \u9762\u5bb9\u6620\u5f97\u66f4\u662f\u72f0\u72de\u3002 \u3000\u3000\u5b59\u9526\u5e73\u867d\u5df2\u60ca\u6016\u6b32\u7edd\uff0c\u4f46\u5979\u53cc\u817f\u5df2\u5e9f\uff0c\u5bf8\u6b65\u96be\u884c\uff0c\u201c\u5b59\u8001\u7239\u201d\u8e09\u8dc4\u5730\u51b2\u5230\u5979\u8eab\u524d\uff0c\u5f20\u5f00 \u53cc\u624b\uff0c\u4fdd\u62a4\u7740\u5979\u3002 \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\u5434\u9e23\u4e16\u5c06\u6a80\u660e\u4e00\u9635\u6447\u6643\uff0c\u72de\u7b11\u7740\u9053\uff1a\u201c\u59d3\u6a80\u7684\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u6a80\u660e\u7259\u5173\u7d27\u54ac\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5434\u9e23\u4e16\uff0c\u6211\u4e0e\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u4e03\u5de7\u7ae5\u5b50\u201d\uff0c\u5434\u9e23\u4e16\u7b11\u9053\uff1a\u201c\u65e0\u51a4\u65e0\u4ec7\uff1f\u2026\u2026\u5434\u545c\u4e16\u2026\u2026\u54c8\u54c8\uff01\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u548b\u8d77\uff0c\u9762\u4e0a\u4e00\u7247\u68ee\u5bd2\uff0c\u4e00\u5b57\u4e00\u5b57\u5730\u7f13\u7f13\u9053\uff1a\u201c\u6211\u662f\u5434\u9e23\u4e16\u4e48\uff1f\u6211\u82e5\u662f\u65e0\u540d\u6c0f\uff0c\u4f60 \u6b7b\u4e0d\u4f1a\u7791\u76ee\uff0c\u6b64\u523b\u4f60\u8eab\u4e2d\u6211\u4e09\u4ef6\u7edd\u6bd2\u6697\u5668\uff0c\u6700\u591a\u4e5f\u6d3b\u4e0d\u8fc7\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6211\u4e0d\u59a8\u544a\u8bc9\u4f60\uff0c\u5434\u9e23\u4e16 \u662f\u65e0\u540d\u6c0f\uff0c\u6211\u5374\u662f\u88ab\u4f60\u6740\u6b7b\u7684\u90a3\u6b27\u9633\u5e73\u4e4b\u7684\u540e\u4eba\uff01\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u4f17\u4eba\u5fc3\u5934\u4ff1\u90fd\u4e00\u9707\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u9762\u8272\u66f4\u662f\u5413\u4eba\uff0c\u8fd9\u201c\u5434\u9e21\u4e16\u201d\u5634\u89d2\u53c8\u81ea\u6cdb \u8d77\u4e86\u72de\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u662f\u60f3\u4e0d\u5230\u4e48\uff1f\u6b27\u9633\u5e73\u4e4b\u8fd8\u6709\u540e\u4eba\uff01\u201d \u3000\u3000\u4ed6\u4ef0\u5929\u957f\u5636\u7740\u9053\uff1a\u201c\u5988\u5440\uff0c\u591a\u4e8f\u4f60\u4e00\u542c\u5230\u7239\u7239\u7684\u6b7b\u8baf\uff0c\u5c31\u5e26\u7740\u6211\u8fdc\u8d70\u4ed6\u65b9\uff0c\u591a\u4e8f\u7239\u7239\u59cb \u7ec8\u6ca1\u6709\u5c06\u6211\u6bcd\u5b50\u63a5\u56de\u5bb6\u91cc\uff0c\u6211\u6bcd\u5b50\u867d\u7136\u5403\u5c3d\u5343\u8f9b\u4e07\u82e6\uff0c\u4f46\u5b69\u513f\u4eca\u65e5\u603b\u7b97\u624b\u5203\u4e86\u4ec7\u4eba\uff0c\u82cd\u65e0\u5440 \u82cd\u5929\uff0c\u4f60\u5f85\u6211\u6b27\u9633\u4ec7\u679c\u7136\u4e0d\u8584\uff0c\u7adf\u6559\u8fd9\u59d3\u6a80\u7684\u7a81\u7136\u98a0\u72c2\uff0c\u5426\u5219\u6211\u600e\u80fd\u4e00\u638c\u800c\u5c06\u4e4b\u51fb\u6bd9\uff1f\u201d \u3000\u3000\u8239\u8231\u5916\u7684\u88f4\u73cf\uff0c\u6b64\u523b\u53ea\u89c9\u5fc3\u5934\u98a4\u6296\uff0c\u624b\u8db3\u51b0\u51b7\uff1a\u201c\u96be\u602a\uff0c\u5434\u9e23\u4e16\uff0c\u5982\u6b64\u6614\u5fc3\u5b64\u8be3\u5730\u5e03\u4e0b \u5404\u79cd\u9677\u9631\uff0c\u96be\u602a\u4ed6\u65f6\u65f6\u523b\u523b\u60f3\u5c06\u6a80\u660e\u903c\u4e0a\u6b7b\u8def\uff0c\u96be\u602a\u4ed6\u4e0d\u62e9\u4efb\u4f55\u624b\u6bb5\uff0c\u96be\u602a\u4ed6\u6c38\u8fdc\u4e0d\u80af\u5c06\u81ea \u5df1\u7684\u8eab\u4e16\u544a\u8bc9\u522b\u4eba\uff01\u201d \u3000\u3000\u6240\u6709\u7684\u4e00\u5207\u96be\u8a00\uff0c\u6b64\u523b\u970d\u7136\u6709\u4e86\u7b54\u6848\u3002 \u3000\u3000\u88f4\u73cf\u6697\u53f9\u4e00\u58f0\uff0c\u65b9\u5f85\u957f\u8eab\u800c\u8d77\uff0c\u76f4\u5165\u8239\u8231\uff0c\u54ea\u77e5\u6b64\u523b\u8239\u8231\u5916\u53c8\u7a81\u5730\u6709\u4e00\u58f0\u9634\u607b\u4fa7\u7684\u51b7 \u7b11\uff0c\u4e00\u4e2a\u5636\u54d1\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u9053\u8fd9\u662f\u4e0a\u82cd\u6709\u773c\u4e48\uff1f\u201d \u3000\u3000\u968f\u7740\u8bed\u58f0\uff0c\u8231\u5916\u7f13\u6b65\u8d70\u5165\u4e00\u4eba\uff0c\u7adf\u662f\u90a3\u5f62\u540c\u81ea\u75f4\u4e4b\u4eba\u3002 \u3000\u3000\u4ed6\u5982\u540c\u884c\u5c38\u8d70\u8089\u4e00\u822c\u8d70\u5230\u201c\u6b27\u9633\u4ec7\u201d\u8eab\u65c1\uff0c\u75f4\u5446\u7684\u9762\u5bb9\u4e0a\u6b64\u523b\u4e5f\u9732\u51fa\u4e86\u4e00\u4e1d\u72de\u7b11\uff0c\u7f13\u7f13 \u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u5728\u8fd9\u4e00\u846b\u82a6\u9152\u91cc\uff0c\u65e9\u5df2\u653e\u4e0b\u4e86\u4e13\u6bd2\u8001\u9f20\u7684\u6bd2\u836f\uff0c\u4ed6\u5c31\u662f\u56e0\u4e3a\u53d1\u73b0\u81ea\u5df1\u4e2d\u6bd2\uff0c \u624d\u4f1a\u88ab\u4f60\u6697\u5668\u51fb\u4e2d\u7684\u3002\u201d \u3000\u3000\u201c\u6b27\u9633\u4ec7\u201d\u76ee\u77aa\u53e3\u5446\uff0c\u6a80\u660e\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u201c\u767d\u75f4\u201d\u75f4\u75f4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u5427\uff01\u6211\u5c31\u662f\u90a3\u88ab\u4f60\u5728\u5317\u4eac\u57ce\u5916\u6740\u6b7b\u7684\u9189\u6c49\u7684\u513f \u5b50\uff01\u6211\u7238\u7238\u6b7b\u4e86\uff0c\u6211\u5988\u5988\u4e5f\u6025\u5f97\u75c5\u6b7b\uff0c\u6211\u6ca1\u6709\u996d\u5403\uff0c\u6ca1\u6709\u8863\u7a7f\uff0c\u5fc3\u91cc\u5c31\u8bb0\u5f97\u8981\u66ff\u7238\u7238\u590d\u4ec7\uff0c \u6574\u65e5\u4ec0\u4e48\u4e8b\u4e5f\u4e0d\u60f3\u505a\uff0c\u522b\u4eba\u5374\u4ee5\u4e3a\u6211\u662f\u767d\u75f4\uff0c\u5230\u540e\u6765\u6211\u81ea\u5df1\u4e5f\u4ee5\u4e3a\u6211\u662f\u4e2a\u767d\u75f4\uff01\u201d \u3000\u3000\u4ed6\u54af\u54af\u4e00\u7b11\uff0c\u53ea\u542c\u5f97\u4eba\u4eba\u6bdb\u9aa8\u60ca\u7136\uff0c\u201c\u9f99\u5f62\u516b\u638c\u201d\u6a80\u660e\u76ee\u5149\u4e00\u7247\u60ca\u6016\uff0c\u53e3\u4e2d\u4e0d\u4f4f\u98a4\u58f0 \u9053\uff1a\u201c\u82cd\u5929\u2026\u2026\u82cd\u5929\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u8fd9\u201c\u767d\u75f4\u201d\u54af\u54af\u7b11\u9053\uff1a\u201c\u6211\u5feb\u997f\u6b7b\u7684\u65f6\u5019\uff0c\u624d\u88ab\u4ed6\u4eec\u7236\u5973\u4e24\u4eba\u6536\u5bb9\u5230\u8fd9\u91cc\u6765\uff0c\u90a3\u65f6 \u6211\u53ea\u6c42\u80fd\u6d3b\u4e0b\u53bb\uff0c\u4ec7\u4e5f\u4e0d\u60f3\u62a5\u4e86\uff0c\u54ea\u77e5\u82cd\u5929\u771f\u5f97\u6709\u773c\uff0c\u4eca\u5929\u7adf\u6559\u6211\u542c\u5230\u8fd9\u756a\u8bdd\uff0c\u53ef\u5e78\u6211\u624b\u8fb9 \u6070\u5de7\u6709\u6bd2\u8001\u9f20\u7684\u836f\uff0c\u563b\u563b\uff0c\u54c8\u54c8\u2026\u2026\u6211\u7ec8\u4e8e\u590d\u4e86\u4ec7\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u5750\u5728\u5730\u4e0a\uff0c\u7ade\u6eda\u5230\u5730\u4e0a\u722c\u6765\u722c\u53bb\uff0c\u201c\u6b27\u9633\u4ec7\u201d\u76ee\u77aa\u53e3\u5446\u5730\u671b\u7740\u4ed6\uff0c\u53cc\u624b\u4e00\u677e\uff0c \u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u88f4\u73cf\u4ea6\u662f\u60ca\u9707\uff0c\u6050\u60e7\uff0c\u53ea\u542c\u6a80\u660e\u5927\u559d\u4e00\u58f0\uff0c\u5012\u5367\u5730\u4e0a\uff0c\u518d\u4e0d\u52a8\u5f39\uff0c\u4e34\u6b7b\u524d\u4eff\u4f5b\u8fd8\u5728\u5583\u5583 \u81ea\u8bed\uff1a\u201c\u82cd\u5929\u82cd\u5929\u2026\u2026\u201d \u3000\u3000\u88f4\u73cf\u53cc\u62f3\u4e00\u63e1\uff0c\u98de\u63a0\u5165\u8231\uff0c\u8fd9\u8239\u8231\u4e2d\u7adf\u50cf\u662f\u5df2\u53d8\u6210\u4e00\u4e2a\u75af\u4eba\u7684\u4e16\u754c\uff0c\u4eba\u4eba\u7684\u76ee\u5149\uff0c\u4ff1\u662f \u75f4\u5446\u800c\u9ebb\u6728\u7684\uff01 \u3000\u3000\u56e0\u679c\u5faa\u73af\uff0c\u62a5\u5e94\u4e0d\u723d\uff0c\u7adf\u771f\u5f97\u5982\u6b64\u5c16\u9510\uff0c\u53c8\u6709\u8c01\u80fd\u76f8\u4fe1\uff0c\u8fd9\u4e00\u4e16\u53f1\u54a4\u6b66\u6797\u7684\u3002\u2018\u9f99\u5f62\u516b \u638c\u201c\u7adf\u4f1a\u6b7b\u5728\u4e00\u4e2a\u201d\u767d\u75f4\u201c\u7684\u624b\u91cc\uff1f\u6b7b\u5bc2\u4e4b\u4e2d\uff0c\u53ea\u542c\u201d\u767d\u75f4\u201c\u7a81\u5730\u60e8\u55e5\u4e4b\u58f0\uff0c\u56db\u80a2\u4e00\u633a\uff0c\u7adf \u4e5f\u4e00\u547d\u545c\u547c\u3002\u539f\u6765\u4ed6\u4e50\u6781\u4e4b\u4e0b\uff0c\u7adf\u5c06\u201d\u5b59\u8001\u7239\u201c\u7897\u4e2d\u8fd8\u6ca1\u6709\u559d\u7684\u6bd2\u9152\uff0c\u4e00\u53e3\u559d\u4e0b\u809a\u91cc\uff0c\u8fd9\u53ef \u601c\u7684\u201d\u767d\u75f4\u201c\u7adf\u50cf\u662f\u4e3a\u590d\u4ec7\u800c\u751f\uff0c\u590d\u4ec7\u4e00\u4e86\uff0c\u7acb\u523b\u6b7b\u53bb\uff0c\u4ed6\u4e00\u751f\u6ca1\u6709\u5f97\u5230\u4e1d\u6beb\u6b22\u4e50\uff0c\u4e5f\u6ca1\u6709 \u5927\u591a\u65f6\u5019\u6e05\u9192\uff0c\u90a3\u4e48\u6b64\u523b\u4ed6\u80fd\u5728\u6700\u6b22\u4e50\u4e0e\u6700\u6e05\u9192\u7684\u65f6\u5019\u6b7b\u53bb\uff0c\u5728\u4ed6\u7070\u767d\u7684\u751f\u547d\u4e2d\uff0c\u603b\u7b97\u662f\u6709 \u4e86\u4e00\u7b14\u9c9c\u8840\u7684\u5f69\u8272\u3002\u4e00\u9635\u60ca\u6817\u7684\u60ca\u6016\u4e4b\u540e\uff0c\u7a81\u5730\uff0c\u90a3\u719f\u6089\u7684\u547c\u5524\u53c8\u5728\u88f4\u73cf\u7684\u8eab\u540e\u54cd\u8d77\uff1a\u201c\u73cf \u513f\uff01\u201d \u3000\u3000\u88f4\u73cf\u4e00\u60ca\u56de\u8eab\uff0c\u53ea\u89c1\u201c\u91d1\u7ae5\u7389\u5973\u201d\u53cc\u53cc\u7acb\u5728\u8231\u95e8\u53e3\uff0c\u8fd9\u4e24\u4f4d\u6b66\u6797\u4e2d\u76d6\u4e16\u7684\u5947\u4eba\uff0c\u6b64\u523b\u9762 \u4e0a\u4ea6\u662f\u4e00\u7247\u6006\u7136\u4e4b\u8272\u3002\u201c\u91d1\u7ae5\u201d\u8f7b\u8f7b\u4e00\u63a0\uff0c\u6709\u5982\u5929\u5916\u7684\u8f7b\u4e91\u4e00\u822c\uff0c\u63a0\u5230\u6a80\u660e\u7684\u5c38\u8eab\u65c1\uff0c\u6c89\u58f0 \u53f9\u9053\uff1a\u201c\u8fdf\u4e86\uff0c\u8fdf\u4e86\uff0c\u60f3\u4e0d\u5230\u6211\u8fdf\u6765\u4e00\u6b65\uff0c\u7adf\u843d\u5f97\u5982\u6b64\u5c40\u9762\uff01\u201d \u3000\u3000\u201c\u7389\u5973\u201d\u5e7d\u5e7d\u4e00\u53f9\u9053\uff1a\u201c\u82cd\u592d\u7684\u5b89\u6392\uff0c\u53c8\u5c82\u662f\u4f60\u80fd\u6539\u53d8\u7684\uff1f\u53ea\u4e0d\u8fc7\u662f\u60dc\u4f60\u7684\u624b\uff0c\u6765\u884c\u5b83 \u7684\u65e8\u610f\uff0c\u800c\u4ed6\u8001\u4eba\u5bb6\u7684\u65e8\u610f\u65e9\u6709\u5b89\u6392\uff0c\u4f60\u600e\u4e48\u80fd\u6539\u53d8\u5462\uff1f\u201d \u3000\u3000\u201c\u91d1\u7ae5\u201d\u9ed8\u7136\uff0c\u6123\u4e86\u534a\u664c\uff0c\u81ea\u8bed\u7740\u9053\uff1a\u201c\u6069\u6069\u4ec7\u4ec7\uff0c\u5584\u5584\u6076\u6076\uff0c\u56e0\u679c\u5faa\u73af\uff0c\u62a5\u5e94\u4e0d \u723d\u2026\u2026\u5509\uff01\u82cd\u5929\u65e2\u7136\u6ca1\u6709\u778e\u773c\uff0c\u6211\u7559\u5728\u8fd9\u4e16\u4e0a\u591a\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4ed6\u62ac\u5934\u671b\u4ed6\u7231\u59bb\u4e00\u3002\u773c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u770b\u6211\u4eec\u4e5f\u771f\u5f97\u8be5\u5f52\u9690\u4e86\u3002\u201d \u3000\u3000\u201c\u7389\u5973\u201d\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u627e\u4e00\u4e2a\u5b89\u9759\u7684\u5730\u65b9\uff0c\u4e0d\u8981\u8ba9\u4efb\u4f55\u4eba\u6765\u6253\u6270\u6211\u4eec\u3002\u201d \u3000\u3000\u5979\u76ee\u4e2d\u5145\u6ee1\u4e86\u5149\u5f69\uff0c\u201c\u91d1\u7ae5\u201d\u9762\u4e0a\u4e5f\u662f\u4e00\u7247\u7115\u7136\uff0c\u88f4\u73cf\u53ea\u89c9\u8fd9\u4e24\u4f4d\u5947\u4eba\u5982\u6b64\u53ef\u7231\u800c\u53ef \u4f69\uff0c\u6697\u53f9\u4e00\u58f0\uff0c\u8dea\u4e86\u4e0b\u6765\uff0c\u5c31\u8fde\u201c\u6b27\u9633\u4ec7\u201d\u548c\u201c\u5b59\u8001\u7239\u201d\u4e5f\u60c5\u4e0d\u81ea\u7981\u5730\u968f\u4e4b\u8dea\u5012\uff0c\u5b59\u9526\u5e73\u5374 \u53ea\u80fd\u5782\u9996\u5408\u5341\u800c\u5df2\u3002 \u3000\u3000\u201c\u91d1\u7ae5\u201d\u76ee\u5149\u4e00\u626b\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6069\u4ec7\u4ff1\u4e86\uff0c\u5f80\u4e8b\u5df2\u6210\u6d41\u6c34\uff0c\u4f46\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6b64\u523b\u4e4b \u60c5\uff0c\u4f60\u4eec\u90fd\u5207\u5207\u4e0d\u53ef\u5fd8\u8bb0\uff0c\u4e0d\u8981\u5fd8\u8bb0\u5728\u51a5\u51a5\u4e4b\u4e2d\uff0c\u8fd8\u6709\u4e00\u53cc\u773c\u775b\u5728\u671b\u7740\u4f60\u4eec\u3002\u201d \u3000\u3000\u88f4\u73cf\u3001\u6b27\u9633\u4ec7\u4ff1\u662f\u6ee1\u5fc3\u656c\u610f\uff0c\u4e0d\u6562\u62ac\u5934\u3002 \u3000\u3000\u201c\u91d1\u7ae5\u201d\u53f9\u9053\uff1a\u201c\u65b9\u624d\u6211\u4ee5\u2018\u4f20\u97f3\u5165\u5bc6\u2019\u5c06\u4f60\u4e24\u4eba\u5524\u4e86\u51fa\u6765\uff0c\u5b9e\u5728\u4e5f\u6ca1\u6709\u60f3\u5230\u4e8b\u60c5\u4e00\u53d8 \u5982\u6b64\uff0c\u6a80\u660e\u82e5\u4e0d\u662f\u8fd1\u5e74\u505a\u4e8b\u592a\u8fc7\u9738\u9053\uff0c\u4eca\u65e5\u53c8\u600e\u4f1a\u843d\u5f97\u5982\u6b64\u60c5\u51b5\uff1f\u201d \u3000\u3000\u201c\u7389\u5973\u201d\u8f7b\u8f7b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u65b9\u624d\u8fd8\u8bf4\u6069\u4ec7\u4ff1\u4e86\uff0c\u5f80\u4e8b\u5df2\u6210\u6d41\u6c34\uff0c\u6b64\u523b\u4f60\u8fd8\u8bf4\u5b83\u505a\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u5979\u7f13\u7f13\u8d70\u5230\u5b59\u9526\u5e73\u8eab\u65c1\uff0c\u8f7b\u8f7b\u629a\u7740\u8fd9\u5c11\u5973\u7684\u79c0\u53d1\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6700\u53ef\u601c\u7684\u8fd8\u662f\u4f60\uff0c\u6211\u4eec\u8981 \u53bb\u4e86\uff0c\u4f60\u4e5f\u8ddf\u7740\u6211\u4eec\u4e00\u8d77\u8d70\u597d\u4e48\uff1f\u201d \u3000\u3000\u5b59\u9526\u5e73\u672c\u5728\u4e0d\u4f4f\u555c\u6ce3\uff0c\u6b64\u523b\u66f4\u662f\u6251\u5728\u201c\u7389\u5973\u201d\u8eab\u4e0a\uff0c\u653e\u58f0\u5927\u54ed\u8d77\u6765\uff0c\u201c\u7389\u5973\u201d\u773c\u4e2d\u4ea6\u4e0d \u7981\u4e3a\u4e4b\u4e00\u7247\u83b9\u7136\u3002 \u3000\u3000\u88f4\u73cf\u6ee1\u5fc3\u6006\u75db\uff0c\u5782\u9996\u9053\uff1a\u201c\u5f1f\u5b50\u6069\u4ec7\u5df2\u4e86\uff0c\u6b64\u540e\u4e5f\u60f3\u8ddf\u7740\u2026\u2026\u3001\u201d\u91d1\u7ae5\u201c\u9762\u8272\u4e00\u6c89\uff0c \u9053\uff1a\u201c\u4f60\u4e5f\u60f3\u8ddf\u7740\u6211\u4eec\u8d70\u4e48\uff1f\u201d \u3000\u3000\u88f4\u73cf\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u91d1\u7ae5\u201d\u5927\u6012\u9053\uff1a\u201c\u4f60\u60f3\u8d70\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u6b66\u6797\u4e2d\u8fd8\u6709\u591a\u5c11\u4e8b\u7b49\u7740\u4f60\u53bb \u505a\uff1f\u201d \u3000\u3000\u201c\u7389\u5973\u201d\u76ee\u5149\u67d4\u548c\u5730\u671b\u4e86\u4e00\u773c\uff0c\u8f7b\u8f7b\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u8d70\u7684\uff01\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u5c31\u5728\u4f60\u65b9\u624d \u79bb\u5f00\u7684\u90a3\u95ee\u5c4b\u5b50\u91cc\uff0c\u6b64\u523b\u6b63\u6709\u4e00\u4e2a\u4eba\u5728\u7b49\u7740\u4f60\u3002\u201d \u3000\u3000\u88f4\u73cf\u5168\u8eab\u4e00\u9707\uff0c\u201c\u91d1\u7ae5\u201d\u7f13\u7f13\u9053\uff1a\u201c\u6211\u4eec\u82e5\u4e0d\u662f\u4e3a\u8981\u5c06\u5979\u9001\u53bb\uff0c\u6b64\u523b\u4e0d\u4f1a\u6765\u8fdf\u4e86\uff01\u201d \u3000\u3000\u5239\u90a3\u95f4\u88f4\u73cf\u53ea\u89c9\u4e00\u9635\u70ed\u8840\u6d8c\u4e0a\u4e86\u5fc3\u5934\uff0c\u6240\u6709\u7684\u60b2\u54c0\u3001\u70e6\u607c\u3001\u4ec7\u6068\u3001\u75db\u82e6\u3001\u60ca\u6016\uff0c\u4ff1\u4f3c\u5df2 \u79bb\u4ed6\u8fdc\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u5269\u4e0b\u7684\u53ea\u6709\u4e00\u7247\u6e29\u6696\uff0c\u8fd9\u79cd\u6e29\u6696\u7adf\u662f\u5982\u6b64\u4e0d\u53ef\u6297\u62d2\uff01 \u3000\u3000\u6b64\u523b\u591c\u5df2\u5f88\u6df1\uff0c\u867d\u7136\u4ecd\u6709\u4e00\u6bb5\u9ed1\u6697\uff0c\u4f46\u8ddd\u79bb\u5929\u660e\uff0c\u5df2\u4e0d\u751a\u8fdc\u3002 \u3000\u3000\u5929\u4e0a\u7fa4\u661f\u95ea\u70c1\uff0c\u6709\u5982\u65e0\u6570\u60c5\u4eba\u7684\u773c\u775b\uff0c\u662f\u6c38\u8fdc\u4e0d\u4f1a\u5b64\u5bc2\u7684\uff0c\u53ea\u662f\u6709\u4e9b\u5347\u8d77\u5f97\u65e9\uff0c\u6709\u4e9b\u5347 \u8d77\u5f97\u8fdf\uff0c\u6709\u4e9b\u4f1a\u88ab\u4e91\u973e\u63a9\u6ca1\uff0c\u4f46\u7ec8\u5fc5\u8fd8\u662f\u4f1a\u53d1\u5c04\u5b83\u5e94\u6709\u7684\u5149\u8292\uff0c\u81ea\u8fdc\u53e4\u76f4\u5230\u73b0\u5728\uff0c\u81ea\u73b0\u5728\u76f4 \u5230\u6c38\u8fdc\u2026\u2026"}),showOnHomepage:!0}]}}]);