"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[39616],{9266:(e,n,a)=>{a.r(n),a.d(n,{default:()=>o});var t=a(74848);const o=[{name:"\u7b2c\u4e00\u56de\u3000\u5371\u90a6\u884c\u8700\u9053\u3000\u4e71\u4e16\u574f\u957f\u57ce",content:(0,t.jsx)(t.Fragment,{children:"\u5927\u660e\u6210\u7956\u7687\u5e1d\u6c38\u4e50\u516d\u5e74\u516b\u6708\u4e59\u672a\uff0c\u897f\u5357\u6d77\u5916\u6d61\u6ce5\u56fd\u56fd\u738b\u9ebb\u90a3\u60f9\u52a0\u90a3\u4e43\uff0c\u7387\u540c\u5983\u5b50\u3001\u5f1f\u3001\u59b9\u3001\u4e16\u5b50\u53ca\u966a\u81e3\u6765\u671d\uff0c\u8fdb\u8d21\u9f99\u8111\u3001\u9e64\u9876\u3001\u73b3\u7441\u3001\u7280\u89d2\u3001\u91d1\u94f6\u5b9d\u5668\u7b49\u8bf8\u822c\u7269\u4e8b\u3002\u6210\u7956\u7687\u5e1d\u5927\u60a6\uff0c\u5609\u52b3\u826f\u4e45\uff0c\u8d50\u5bb4\u5949\u5929\u95e8\u3002 \u3000\u3000\u90a3\u6d61\u6ce5\u56fd\u5373\u4eca\u5a46\u7f57\u6d32\u5317\u90e8\u7684\u5a46\u7f57\u4e43\uff0c\u53c8\u79f0\u6587\u83b1\uff08\u6d61\u6ce5\u3001\u5a46\u7f57\u4e43\u3001\u6587\u83b1\u4ee5\u53ca\u82f1\u8bed Brunei \u5747\u7cfb\u540c\u4e00\u5730\u540d\u4e4b\u97f3\u8bd1\uff09\uff0c\u867d\u548c\u4e2d\u571f\u76f8\u9694\u6d77\u7a0b\u4e07\u91cc\uff0c\u4f46\u5411\u6765\u4ef0\u6155 \u4e2d\u534e\u3002\u5b8b\u671d\u592a\u5e73\u5174\u56fd\u4e8c\u5e74\uff0c\u5176\u738b\u5411\u6253\uff08\u5373\u82cf\u4e39\uff0c\u4e2d\u56fd\u53f2\u4e66\u4e0a\u8bd1\u97f3\u4e3a\u201c\u5411\u6253\u201d\uff09 \u3000\u3000\u66fe\u9063\u4f7f\u6765\u671d\uff0c\u8fdb\u8d21\u9f99\u8111\u3001\u8c61\u7259\u3001\u6a80\u9999\u7b49\u7269\uff0c\u5176\u540e\u671d\u8d21\u4e0d\u7edd\u3002 \u3000\u3000\u9ebb\u90a3\u60f9\u52a0\u90a3\u4e43\u56fd\u4e0a\u773c\u89c1\u5929\u671d\u4e0a\u56fd\u6c11\u4e30\u7269\u961c\uff0c\u6587\u6cbb\u6559\u5316\u3001\u8863\u51a0\u5668\u5177\uff0c\u65e0\u4e0d\u4ee4\u4ed6\u6b22\u559c\u8d5e\u53f9\uff0c\u660e\u5e1d\u53c8\u76f8\u5f85\u751a\u539a\uff0c\u7adf\u7136\u7559\u604b\u4e0d\u53bb\u3002\u5230\u8be5\u5e74\u5341\u4e00\u6708\uff0c\u4e00\u6765\u5e74\u8001\uff0c\u4e8c\u6765\u6c34\u571f\u4e0d\u670d\uff0c\u60a3\u75c5\u4e0d\u6cbb\u3002\u6210\u7956\u6df1\u4e3a\u60bc\u60dc\uff0c\u4e3a\u4e4b\u8f8d\u671d\u4e09\u65e5\uff0c\u8d50\u846c\u5357\u4eac\u5b89\u5fb7\u95e8\u5916\uff08\u4eca\u5357\u4eac\u4e2d\u534e\u95e8\u5916\u805a\u5b9d\u5c71\u9e93\uff0c\u6709\u738b\u5893\u9057\u5740\uff0c\u4fd7\u547c\u9a6c\u56de\u56de\u575f\uff09\uff0c\u53c8\u547d\u4e16\u5b50\u9050\u65fa\u88ad\u5c01\u6d61\u6ce5\u56fd\u738b\uff0c\u9063\u4f7f\u8005\u62a4\u9001\u5f52\u56fd\uff0c\u8d4f\u8d50\u91d1\u94f6\u3001\u5668\u76bf\u3001\u9526\u5d0e\u3001\u7eb1\u7f57\u7b49\u7269\u3002 \u3000\u3000\u903b\u65fa\u738b\u594f\u79f0\uff1a\u5c0f\u56fd\u540e\u5c71\uff0c\u9887\u6709\u795e\u5f02\uff0c\u4e5e\u7687\u4e0a\u8d50\u5c01\uff0c\u8868\u4e3a\u4e00\u56fd\u4e4b\u9547\u3002 \u3000\u3000\u6210\u7956\u4fbf\u5c01\u5176\u5c71\u540d\u4e3a\u201c\u957f\u5b81\u9547\u56fd\u5c71\u201d\uff0c\u4eb2\u5236\u7891\u6587\uff0c\u5e76\u9898\u8bd7\u4e00\u9996\uff0c\u8bd7\u66f0\uff1a\u201c\u708e\u6d77\u4e4b\u589f\uff0c\u6d61\u6ce5\u6240\u5904\u3002\u7166\u6c5f\u6e10\u4e49\uff0c\u6709\u987a\u65e0\u8fd5\u3002 \u3000\u3000\u25a0\u25a0\u8d24\u738b\uff0c\u60df\u5316\u4e4b\u6155\u3002 \u3000\u3000\u5bfc\u4ee5\u8c61\u80e5\uff0c\u9002\u6765\u5954\u8d74\u3002\u540c\u5176\u5987\u5b50\uff0c\u65e0\u5f1f\u966a\u81e3\u3002 \u3000\u3000\u7a3d\u98a1\u9619\u4e0b\uff0c\u6709\u8a00\u4ee5\u9648\u3002 \u3000\u3000\u8c13\u541b\u72b9\u5929\uff0c\u9063\u5176\u4f11\u4e50\u3002\u4e00\u89c6\u540c\u4ec1\uff0c\u532a\u504f\u539a\u8584\u3002 \u3000\u3000\u987e\u5179\u9c9c\u5fb7\uff0c\u5f17\u79cd\u6240\u4e91\u3002 \u3000\u3000\u6d6a\u8236\u98ce\u6a2f\uff0c\u5b9e\u52b3\u6073\u52e4\u3002\u7a3d\u53e4\u8fdc\u81e3\uff0c\u987a\u6765\u6012\u8d91\u3002 \u3000\u3000\u4ee5\u8eac\u6216\u96be\uff0c\u77e7\u66f0\u5bb6\u5ba4\uff1f \u3000\u3000\u738b\u5fc3\u76f4\u8bda\uff0c\u91d1\u77f3\u5176\u575a\u3002\u897f\u5357\u8543\u957f\uff0c\u7574\u4e0e\u738b\u8d24\uff1f \u3000\u3000\u77d7\u77d7\u9ad8\u5c71\uff0c\u4ee5\u9547\u738b\u56fd\u3002 \u3000\u3000\u9575\u6587\u4ee5\u77f3\uff0c\u61cb\u662d\u738b\u5fb7\u3002\u738b\u5fb7\u514b\u662d\uff0c\u738b\u56fd\u6538\u5b81\u3002 \u3000\u3000\u4e8e\u65af\u4e07\u5e74\uff0c\u4ef0\u6211\u5927\u660e\u3002\u201d \u3000\u3000\u6210\u7956\u7687\u5e1d\u7684\u5fa1\u5236\u8bd7\u6587\uff0c\u4fbf\u523b\u5728\u6d61\u6ce5\u56fd\u957f\u5b81\u9547\u56fd\u5c71\u7684\u4e00\u5757\u5927\u77f3\u7891\u4e0a\u3002\u6b64\u540e\u6d2a\u7199\u3001\u6b63\u5fb7\u3001\u5609\u9756\u5e74\u95f4\uff0c\u5747\u6709\u671d\u8d21\u3002\u4e2d\u56fd\u4eba\u53bb\u5230\u6d61\u6ce5\u56fd\u7684\uff0c\u6709\u4e9b\u8fd8\u505a\u4e86\u5927\u5b98\uff0c\u88ab\u5c01\u4e3a\u201c\u90a3\u7763\u201d\u3002 \u3000\u3000\u5230\u5f97\u4e07\u5386\u5e74\u95f4\uff0c\u6d61\u6ce5\u56fd\u5185\u5ffd\u8d77\u5185\u4e71\uff0c\u300a\u660e\u53f2\u6d61\u6ce5\u4f20\u300b\u8f7d\u79f0\uff1a\u201c\u5176\u738b\u5352\uff0c\u65e0\u55e3\u3002\u65cf\u4eba\u4e89\u7acb\uff0c\u56fd\u4e2d\u6740\u622e\u51e0\u5c3d\uff0c\u4e43\u7acb\u5176\u5973\u4e3a\u738b\u3002\u6f33\u5dde\u4eba\u5f20\u59d3\u8005\uff0c\u521d\u4e3a\u5176\u56fd\u90a3\u7763\uff0c\u534e\u8a00\u5c0a\u5b98\u4e5f\uff0c\u56e0\u4e71\u51fa\u5954\uff0c\u5973\u738b\u7acb\uff0c\u8fce\u8fd8\u4e4b\u3002\u5176\u5973\u51fa\u5165\u738b\u5bab\uff0c\u5f97\u5fc3\u75be\uff0c\u5984\u8a00\u7236\u6709\u53cd\u8c0b\u3002\u5973\u4e3b\u60e7\uff0c\u9063\u4eba\u6309\u95ee\u5176\u5bb6\uff0c\u90a3\u7763\u81ea\u6740\u3002\u56fd\u4eba\u4e3a\u8bbc\u51a4\u3002\u5973\u4e3b\u6094\uff0c\u7ede\u6740\u5176\u5973\uff0c\u6388\u5176\u5b50\u5b98\u3002\u201d \u3000\u3000\u8fd9\u4f4d\u5f20\u90a3\u7763\u7684\u5973\u513f\u4e3a\u4f55\u795e\u7ecf\u9519\u4e71.\u5411\u5973\u738b\u8bec\u544a\u7236\u4eb2\u9020\u53cd\uff0c\u4ee5\u81f4\u917f\u6210\u8fd9\u4e2a\u60b2\u5267\uff0c\u60f3\u5fc5\u53e6\u6709\u66f2\u6298\u5185\u60c5\uff0c\u53f2\u4e66\u5e76\u672a\u8be6\u8f7d\uff0c\u540e\u4eba\u4e0d\u5f97\u800c\u77e5\u3002\u798f\u5efa\u6f33\u5dde\u5f20\u6c0f\u5728\u6d61\u6ce5\u56fd\u7d2f\u4e16\u53d7\u5c01\u90a3\u7763\uff0c\u9887\u6709\u6743\u52bf\uff0c\u4e3a\u56fd\u4eba\u6240\u656c\u534e\u4eba\u5728\u5f7c\u90a6\u7ecf\u5546\u52a1\u519c\u3002\u6570\u4ea6\u5c0f\u5c11\uff0c\u5f7c\u8346\u65a9\u68d8\uff0c\u751a\u6709\u529f\u7ee9\uff0c\u548c\u5f53\u5730\u571f\u4eba\u76f8\u5904\u878d\u6d3d\u8d39\u4fe1\u300a\u661f\u69ce\u80dc\u89c8\u300b\u4e00\u4e66\u4e2d\u8bb0\u4e91\uff1a\u201c\u6e24\u6ce5\u56fd\u2026\u2026\u5176\u56fd\u4e4b\u6c11\u5d07\u4f5b\u50cf\uff0c\u597d\u658b\u6c90\u3002\u51e1\u89c1\u5510\u4eba\u81f3\u5176\u56fd\uff0c\u751a\u6709\u7231\u656c\u3002 \u3000\u3000\u6709\u9189\u8005\uff0c\u5219\u6276\u5f52\u5bb6\u5bdd\u5bbf\uff0c\u4ee5\u793c\u5f85\u4e4b\u82e5\u6545\u65e7\u3002\u201d\u6709\u8bd7\u4e3a\u8bc1\uff0c\u8bd7\u66f0\uff1a\u201c\u6d61\u6ce5\u6ca7\u6d77\u5916\uff0c\u7acb\u56fd\u81ea\u4f55\u5e74\uff1f\u66f4\u51b7\u51ac\u751f\u70ed\uff0c\u5c71\u76d8\u5730\u81ea\u504f\u3002 \u3000\u3000\u79ef\u4fee\u5d07\u4f5b\u6559\uff0c\u6276\u9189\u4f8d\u5bbe\u8d24\u3002\u53d6\u4fe1\u901a\u5546\u8236\uff0c\u9057\u98ce\u4e8b\u53ef\u4f20\u3002\u201d \u3000\u3000\u6d61\u6ce5\u56fd\u90a3\u7763\u5f20\u6c0f\u6570\u4f20\u540e\u662f\u4e3a\u5f20\u4fe1\uff0c\u819d\u4e0b\u60df\u6709\u4e00\u5b50\u3002\u5f20\u4fe1\u4e0d\u5fd8\u6545\u56fd\uff0c\u4e3a\u513f\u5b50\u53d6\u540d\u671d\u5510\u3002 \u3000\u3000\u5230\u5f20\u671d\u5510\u5341\u4e8c\u5c81\u90a3\u4e00\u5e74\uff0c\u798f\u5efa\u6709\u4e00\u540d\u58eb\u4eba\u5c61\u8bd5\u4e0d\u7b2c\uff0c\u5f03\u5112\u7ecf\u5546\uff0c\u968f\u7740\u4e61\u4eba\u6765\u5230\u6d61\u6ce5\u56fd\u3002\u8fd9\u4eba\u4e0d\u5584\u7ecf\u8425\uff0c\u672c\u94b1\u8680\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u65e0\u989c\u56de\u4e61\uff0c\u5c31\u6b64\u6d41\u843d\u5f02\u90a6\u3002\u6709\u4eba\u8350\u4ed6\u53bb\u89c1\u5f20\u4fe1\uff0c\u60f3\u8981\u8c0b\u4e2a\u751f\u8ba1\u3002\u5f20\u4fe1\u548c\u4ed6\u4e00\u8c08\u4e4b\u4e0b\uff0c\u5fc3\u4e0b\u5927\u559c\uff0c\u4fbf\u5373\u8058\u4e3a\u897f\u5bbe\uff0c\u6559\u513f\u5b50\u8bfb\u4e66\u3002 \u3000\u3000\u5f20\u671d\u5510\u5f00\u8499\u867d\u8fdf\uff0c\u5374\u662f\u5929\u8d44\u806a\u9896\uff0c\u5341\u5e74\u4e4b\u95f4\uff0c\u56db\u4e66\u4e94\u7ecf\u4ff1\u5df2\u719f\u4e60\u3002\u90a3\u8001\u5e08\u529b\u529d\u5f20\u4fe1\u9063\u5b50\u56de\u4e2d\u571f\u5e94\u8bd5\uff0c\u82e5\u80fd\u8003\u5f97\u4e2a\u79c0\u624d\u3001\u4e3e\u4eba\uff0c\u6709\u4e86\u4e2d\u534e\u7684\u529f\u540d\uff0c\u56de\u5230\u6d61\u6ce5\u6765\u90a3\u53ef\u662f\u5927\u6709\u5149\u5f69\u3002\u5f20\u4fe1\u4e5f\u76fc\u513f\u5b50\u56de\u4e61\u53bb\u89c2\u5149\u4e0a\u56fd\u98ce\u7269\uff0c\u4e8e\u662f\u91cd\u91cd\u916c\u8c22\u4e86\u8001\u5e08\uff0c\u6253\u70b9\u91d1\u94f6\u884c\u674e\uff0c\u518d\u6d3e\u50ee\u513f\u5f20\u5eb7\u8ddf\u968f\uff0c\u547d\u5f20\u671d\u5510\u968f\u540c\u8001\u5e08\u56de\u6f33\u5dde\u539f\u7c4d\u5e94\u8bd5\u3002 \u3000\u3000\u5176\u65f6\u6b63\u662f\u5d07\u796f\u516d\u5e74\uff0c\u9006\u5944\u9b4f\u5fe0\u8d24\u867d\u5df2\u4f0f\u8bdb\uff0c\u4f46\u5728\u5929\u542f\u671d\u4e03\u5e74\u4e4b\u95f4\u7978\u56fd\u6b83\u6c11\uff0c\u6740\u5bb3\u5fe0\u826f\uff0c\u5929\u4e0b\u5143\u6c14\u5927\u4f24\uff0c\u517c\u4e4b\u8fde\u5e74\u6c34\u65f1\u6210\u707e\uff0c\u6d41\u5bc7\u56db\u8d77\u3002\u5f20\u671d\u5510\u7b49\u4e09\u4eba\u4ece\u53a6\u95e8\u4e0a\u5cb8\uff0c\u96c7\u8239\u897f\u4e0a\u6f33\u5dde\u3002\u4e0d\u6599\u53ea\u884c\u51fa\u6570\u5341\u91cc\uff0c\u56db\u4e61\u5ffd\u7136\u5927\u4e71\uff0c\u4e00\u7fa4\u76d7\u8d3c\u6d8c\u4e0a\u8239\u6765\uff0c\u4e0d\u7531\u5206\u8bf4\uff0c\u4fbf\u5c06\u90a3\u6559\u4e66\u5148\u751f\u6740\u4e86\u3002\u5f20\u671d\u5510\u4e3b\u4ec6\u5e78\u597d\u8bc6\u5f97\u6c34\u6027\uff0c\u8df3\u6c34\u9003\u547d\uff0c\u624d\u514d\u4e86\u4e00\u5200\u4e4b\u5384\u3002 \u3000\u3000\u4e24\u4eba\u5728\u4e61\u95f4\u8eb2\u4e86\u4e09\u65e5\uff0c\u542c\u5f97\u56db\u4e61\u9965\u6c11\u805a\u4f17\u8981\u653b\u6f33\u5dde\u3001\u53a6\u95e8\u3002\u8fd9\u4e00\u6765\uff0c\u53ea\u5c06\u5f20\u671d\u5510\u5413\u5f97\u6ee1\u8154\u96c4\u5fc3\uff0c\u767b\u5316\u4e4c\u6709\uff0c\u773c\u89c1\u5371\u90a6\u4e0d\u53ef\u5c45\uff0c\u8fd8\u662f\u6025\u901f\u56de\u5bb6\u7684\u4e3a\u662f\u3002 \u3000\u3000\u5176\u65f6\u53a6\u95e8\u5df2\u4e0d\u80fd\u518d\u53bb\uff0c\u4e3b\u4ec6\u4e24\u4eba\u4e00\u5546\u91cf\uff0c\u51b3\u5b9a\u4ece\u9646\u8def\u897f\u8d74\u5e7f\u5dde\uff0c\u518d\u4e58\u6d77\u8239\u51fa\u6d0b\u3002\u4e24\u4eba\u4e70\u4e86\u4e24\u5339\u5750\u9a91\uff0c\u80c6\u6218\u5fc3\u60ca\uff0c\u6cbf\u8def\u6253\u542c\uff0c\u5411\u5e7f\u4e1c\u800c\u53bb\u3002 \u3000\u3000\u5e78\u559c\u4e00\u8def\u65e0\u4e8b\uff0c\u7ecf\u5357\u9756\u3001\u5e73\u548c\uff0c\u6765\u5230\u4e09\u6cb3\u575d\uff0c\u5df2\u662f\u5e7f\u4e1c\u7701\u5883\uff0c\u518d\u8fc7\u6885\u53bf\u3001\u6c34\u53e3\uff0c\u5411\u897f\u8fe4\u9026\u884c\u6765\u3002\u5f20\u671d\u5510\u7d20\u95fb\u5e7f\u4e1c\u662f\u5bcc\u5eb6\u4e4b\u5730\uff0c\u4f46\u6cbf\u9014\u6240\u89c1\uff0c\u5c3d\u662f\u9965\u6c11\uff0c\u5fc3\u60f3\u4e2d\u534e\u5730\u5927\u7269\u535a\uff0c\u767e\u59d3\u4eba\u4eba\u751f\u6b7b\u7cfb\u4e8e\u4e00\u7ebf\uff0c\u6d61\u6ce5\u53ea\u662f\u6d77\u5916\u5c0f\u90a6\uff0c\u7537\u5973\u8001\u5e7c\u5374\u662f\u5b89\u5c45\u4e50\u4e1a\uff0c\u65e0\u5fe7\u65e0\u8651\uff0c\u4e0d\u7531\u5f97\u5927\u662f\u53f9\u606f\uff0c\u5fc3\u60f3\u4e2d\u56fd\u5c71\u5ddd\u96c4\u5947\uff0c\u773c\u89c1\u8005\u767e\u672a\u5f97\u4e00\uff0c\u4f46\u5982\u6b64\u671d\u4e0d\u4fdd\u5915\uff0c\u8fd8\u662f\u53bb\u6d61\u6ce5\u6930\u5b50\u6811\u4e0b\u5531\u6b4c\u7761\u89c9\u5b89\u4e50\u5f97\u591a\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u884c\u7ecf\u9e3f\u56fe\u5d82\uff0c\u5c71\u9053\u5d0e\u5c96\uff0c\u5929\u8272\u6e10\u665a\uff0c\u4ed6\u5fc3\u4e2d\u7126\u6025\u8d77\u6765\uff0c\u50ac\u9a6c\u6025\u5954\u3002 \u3000\u3000\u4e00\u53e3\u6c14\u5954\u51fa\u5341\u591a\u91cc\u5730\uff0c\u5230\u4e86\u4e00\u4e2a\u5c0f\u5e02\u9547\u4e0a\uff0c\u4e3b\u4ec6\u4e24\u4eba\u5927\u559c\uff0c\u60f3\u627e\u4e2a\u5ba2\u5e97\u60dc\u5bbf\uff0c\u54ea\u77e5\u9053\u5e02\u9547\u4e0a\u9759\u6084\u6084\u7684\u4e00\u4e2a\u4eba\u5f71\u4e5f\u65e0\u3002\u5f20\u5eb7\u4e0b\u9a6c\uff0c\u8d70\u5230\u4e00\u5bb6\u6302\u7740\u201c\u7ca4\u4e1c\u5ba2\u6808\u201d \u3000\u3000\u62db\u724c\u7684\u5ba2\u5e97\u4e4b\u5916\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5e97\u5bb6\uff0c\u5e97\u5bb6\uff01\u201d\u5e97\u623f\u9760\u5c71\uff0c\u5c71\u8c37\u54cd\u5e94\uff0c\u53ea\u542c\u89c1\u201c\u5582\uff0c\u5e97\u5bb6\uff0c\u5e97\u5bb6\u201d\u7684\u56de\u58f0\uff0c\u5e97\u91cc\u5374\u6beb\u65e0\u52a8\u9759\u3002\u6b63\u5728\u8fd9\u65f6\uff0c\u4e00\u9635\u5317\u98ce\u5439\u6765\uff0c\u730e\u730e\u4f5c\u54cd\uff0c\u4e24\u4eba\u90fd\u611f\u6bdb\u9aa8\u609a\u7136\u3002 \u3000\u3000\u5f20\u671d\u5510\u62d4\u51fa\u4f69\u5251\uff0c\u95ef\u8fdb\u5e97\u53bb\uff0c\u53ea\u89c1\u9662\u5b50\u5185\u5730\u4e0b\u5012\u7740\u4e24\u5177\u5c38\u9996\uff0c\u6d41\u4e86\u4e00\u5927\u6ee9\u9ed1\u8840\uff0c\u82cd\u8747\u7ed5\u7740\u5c38\u9996\u4e71\u98de\u3002\u8150\u81ed\u6251\u9f3b\uff0c\u770b\u6765\u6b7b\u8005\u5df2\u6b7b\u53bb\u591a\u65e5\u3002\u5f20\u5eb7\u4e00\u58f0\u5927\u53eb\uff0c\u8f6c\u8eab\u9003\u51fa\u5e97\u53bb\u3002 \u3000\u3000\u5f20\u671d\u5510\u56db\u4e0b\u4e00\u77a7\uff0c\u5230\u5904\u7bb1\u7b3c\u6563\u4e71\uff0c\u95e8\u7a97\u6b8b\u7834\uff0c\u4f3c\u7ecf\u76d7\u532a\u6d17\u52ab\u3002\u5f20\u5eb7\u89c1\u4e3b\u4eba\u4e0d\u51fa\u6765\uff0c\u4e00\u6b65\u4e00\u987f\u7684\u53c8\u56de\u8fdb\u5e97\u53bb\u3002\u5f20\u671d\u5510\u9053\uff1a\u201c\u5230\u522b\u5904\u770b\u770b\u3002\u201d\u54ea\u77e5\u53c8\u53bb\u4e86\u4e09\u5bb6\u5e97\u94fa\uff0c\u5bb6\u5bb6\u90fd\u662f\u5982\u6b64\u3002\u6709\u7684\u5973\u5c38\u8eab\u5b50\u8d64\u88f8\uff0c\u663e\u662f\u66fe\u906d\u5f3a\u66b4\u800c\u540e\u88ab\u6740\u3002 \u3000\u3000\u4e00\u5ea7\u5e02\u9547\u4e4b\u4e2d\uff0c\u5230\u5904\u9634\u98ce\u60e8\u60e8\uff0c\u5c38\u81ed\u9635\u9635\u3002\u4e24\u4eba\u518d\u4e5f\u4e0d\u6562\u505c\u7559\uff0c\u6025\u5fd9\u4e0a\u9a6c\u5411\u897f\u3002 \u3000\u3000\u4e3b\u4ec6\u4e24\u4eba\u884c\u4e86\u5341\u51e0\u91cc\uff0c\u5929\u8272\u5168\u9ed1\uff0c\u53c8\u997f\u53c8\u6015\uff0c\u6b63\u72fc\u72c8\u95f4\uff0c\u5f20\u5eb7\u5ffd\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u77a7\uff01\u201d\u5f20\u671d\u5510\u987a\u7740\u4ed6\u624b\u6307\u770b\u53bb\uff0c\u53ea\u89c1\u8fdc\u5904\u6709\u4e00\u70b9\u706b\u5149\uff0c\u559c\u9053\uff1a\u201c\u54b1\u4eec\u501f\u5bbf\u53bb\u3002\u201d \u3000\u3000\u4e24\u4eba\u79bb\u5f00\u5927\u9053\uff0c\u5411\u7740\u706b\u5149\u8d70\u53bb\uff0c\u8d8a\u8d70\u9053\u8def\u8d8a\u662f\u7a84\u5c0f\u3002\u5f20\u671d\u5510\u5ffd\u9053\uff1a\u201c\u5018\u82e5\u90a3\u662f\u8d3c\u7a9f\uff0c\u5c82\u4e0d\u662f\u81ea\u6295\u6b7b\u8def\uff1f\u201d\u5f20\u5eb7\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u522b\u53bb\u5427\u3002\u201d\u5f20\u671d\u5510\u773c\u89c1\u56db\u4e0b\u4e4c\u4e91\u6b32\u5408\uff0c\u9887\u6709\u96e8\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u6084\u6084\u8fc7\u53bb\u77a7\u4e00\u77a7\u3002\u201d\u4e8e\u662f\u4e0b\u4e86\u9a6c\uff0c\u628a\u9a6c\u7f1a\u5728\u8def\u8fb9\u6811\u4e0a\uff0c\u8e51\u8db3\u5411\u706b\u5149\u5904\u8d70\u53bb\u3002 \u3000\u3000\u884c\u5230\u4e34\u8fd1\uff0c\u89c1\u662f\u4e24\u95f4\u8305\u5c4b\uff0c\u5f20\u671d\u5510\u60f3\u5230\u7a97\u53e3\u5f80\u91cc\u7aa5\u63a2\uff0c\u5ffd\u7136\u4e00\u53ea\u72d7\u5927\u58f0\u5420\u53eb\uff0c\u6251\u4e86\u8fc7\u6765\u3002\u5f20\u671d\u5510\u6325\u52a8\u4f69\u5251\uff0c\u90a3\u72d7\u624d\u4e0d\u6562\u8d70\u8fd1\uff0c\u53ea\u662f\u4e71\u53eb\u3002 \u3000\u3000\u67f4\u6249\u5f00\u5904\uff0c\u4e00\u4e2a\u8001\u5a46\u5a46\u8d70\u4e86\u51fa\u6765\uff0c\u624b\u4e2d\u4e3e\u7740\u4e00\u76cf\u6cb9\u706f\uff0c\u98a4\u5dcd\u5dcd\u7684\u8be2\u95ee\u662f\u8c01\uff0c\u5f20\u671d\u5510\u9053\uff1a\u201c\u6211\u4eec\u662f\u8fc7\u8def\u5ba2\u4eba\uff0c\u9519\u8fc7\u4e86\u5bbf\u5934\uff0c\u60f3\u5728\u5e9c\u4e0a\u501f\u5bbf\u4e00\u665a\u3002\u201d\u8001\u5a46\u5a46\u5fae\u4e00\u8fdf\u7591\uff0c\u9053\uff1a\u201c\u8bf7\u8fdb\u6765\u5427\u3002\u201d\u5f20\u671d\u5510\u8d70\u8fdb\u8305\u5c4b\uff0c\u89c1\u5c4b\u91cc\u53ea\u6709\u4e00\u5f20\u4e0a\u5e8a\uff0c\u684c\u6905\u4ff1\u65e0\u3002\u5e8a\u4e0a\u8eba\u7740\u4e00\u4e2a\u8001\u5934\uff0c\u4e0d\u65ad\u54b3\u55fd\u3002\u5f20\u671d\u5510\u547d\u5f20\u5eb7\u53bb\u628a\u9a6c\u7275\u6765\u3002\u5f20\u5eb7\u60f3\u8d77\u521a\u624d\u89c1\u5230\u7684\u6b7b\u4eba\u60e8\u72b6\uff0c\u754f\u754f\u7f29\u7f29\u7684\u4e0d\u6562\u51fa\u53bb\u3002\u90a3\u8001\u5934\u513f\u6328\u4e0b\u5e8a\u6765\uff0c\u966a\u7740\u4ed6\u53bb\u7275\u4e86\u9a6c\u6765\u3002\u8001\u5a46\u5a46\u62ff\u51fa\u51e0\u4e2a\u7389\u7c73\u997c\u6765\u98e8\u5ba2\uff0c\u70e7\u4e86\u4e00\u58f6\u70ed\u6c34\u7ed9\u4ed6\u4eec\u559d\u3002 \u3000\u3000\u5f20\u671d\u5510\u5403\u4e86\u4e00\u4e2a\u7389\u7c73\u997c\uff0c\u95ee\u9053\uff1a\u201c\u524d\u9762\u9547\u4e0a\u6740\u4e86\u4e0d\u5c11\u4eba\uff0c\u662f\u751a\u4e48\u532a\u5e2e\u5e72\u7684\uff1f\u201d\u8001\u5934\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u751a\u4e48\u532a\u5e2e\uff1f\u571f\u532a\u6709\u8fd9\u4e48\u72e0\u5417\uff1f\u90a3\u662f\u5b98\u5175\u5e72\u7684\u597d\u4e8b\u3002\u201d\u5f20\u671d\u5510\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5b98\u5175\uff1f\u5b98\u5175\u600e\u4e48\u4f1a\u8fd9\u6837\u65e0\u6cd5\u65e0\u5929\u3001\u5978\u6deb\u63b3\u63a0\uff1f\u4ed6\u4eec\u957f\u5b98\u4e0d\u7406\u5417\uff1f\u201d \u3000\u3000\u8001\u5934\u513f\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u5c0f\u76f8\u516c\u770b\u6765\u662f\u7b2c\u4e00\u6b21\u51fa\u95e8\uff0c\u751a\u4e48\u4e16\u60c5\u4e5f\u4e0d\u61c2\u7684\u4e86\u3002\u957f\u5b98\uff1f\u957f\u5b98\u5e26\u5934\u5e72\u5440\uff0c\u597d\u7684\u4e1c\u897f\u4ed6\u5148\u62ff\uff0c\u597d\u770b\u7684\u5a18\u4eec\u4ed6\u5148\u8981\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u9053\uff1a\u201c\u8001\u767e\u59d3\u600e\u4e0d\u5411\u5b98\u5e9c\u53bb\u544a\uff1f\u201d\u8001\u5934\u513f\u9053\uff1a\u201c\u544a\u6709\u751a\u4e48\u7528\uff1f\u4f60\u4e00\u544a\uff0c\u5341\u4e4b\u516b\u4e5d\u8fd8\u966a\u4e0a\u4e86\u81ea\u5df1\u6027\u547d\u3002\u201d\u5f20\u671d\u5510\u9053\uff1a\u201c\u90a3\u600e\u6837\u8bf4\uff1f\u201d\u8001\u5934\u513f\u9053\uff1a\u201c\u90a3\u8fd8\u4e0d\u662f\u5b98\u5b98\u76f8\u62a4\uff1f\u522b\u8bf4\u5b98\u8001\u7237\u4e0d\u4f1a\u51c6\u4f60\u72b6\u5b50\uff0c\u8fd8\u628a\u4f60\u4e00\u987f\u677f\u5b50\u6536\u4e86\u76d1\u3002\u4f60\u6ca1\u94b1\u5b5d\u656c\uff0c\u5c31\u522b\u60f3\u51fa\u6765\u5566\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u4e0d\u4f4f\u6447\u5934\uff0c\u53c8\u95ee\uff1a\u201c\u5b98\u5175\u5230\u5c71\u91cc\u6765\u5e72\u4e48\uff1f\u201d\u8001\u5934\u513f\u9053\uff1a\u201c\u8bf4\u662f\u6765\u527f\u532a\u6740\u8d3c\uff0c\u5176\u5b9e\u5c71\u91cc\u7684\u76d7\u8d3c\uff0c\u5341\u4e2a\u4e2d\u5012\u6709\u516b\u4e2a\u662f\u7ed9\u5b98\u5e9c\u903c\u5f97\u6ca1\u751f\u8def\u624d\u5e72\u7684\u3002 \u3000\u3000\u5b98\u5175\u4e0b\u4e61\u6765\u6349\u4e0d\u5230\u5f3a\u76d7\uff0c\u63b3\u63a0\u4e00\u9635\uff0c\u518d\u4e71\u6740\u4e9b\u8001\u767e\u59d3\uff0c\u63d0\u4e86\u9996\u7ea7\u4e0a\u53bb\u62a5\u529f\uff0c\u53d1\u4e86\u8d22\uff0c\u8fd8\u597d\u5347\u5b98\u3002\u201d\u90a3\u8001\u5934\u513f\u8bf4\u5f97\u54ac\u7259\u5207\u9f7f\uff0c\u53c8\u4e0d\u505c\u7684\u54b3\u55fd\u3002\u8001\u5a46\u5a46\u4e0d\u4f4f\u5411\u4ed6\u6253\u624b\u52bf\uff0c\u53eb\u4ed6\u522b\u8bf4\u4e86\uff0c\u53ea\u6015\u5f20\u671d\u5510\u8bc6\u5f97\u5b98\u5bb6\uff0c\u591a\u8a00\u60f9\u7978\u3002 \u3000\u3000\u5f20\u671d\u5510\u542c\u5f97\u95f7\u95f7\u4e0d\u4e50\uff0c\u60f3\u4e0d\u5230\u4e16\u5c40\u8d25\u574f\u5982\u6b64\uff0c\u5fc3\u60f3\uff1a\u201c\u7239\u7239\u5e38\u8bf4\uff0c\u4e2d\u534e\u662f\u6587\u7269\u793c\u4e49\u4e4b\u90a6\uff0c\u738b\u9053\u6559\u5316\uff0c\u8def\u4e0d\u62ac\u9057\uff0c\u591c\u4e0d\u95ed\u6237\uff0c\u4eba\u4eba\u8bb2\u4fe1\u4fee\u7766\uff0c\u4ec1\u4e49\u548c\u7231\u3002\u4eca\u65e5\u773c\u89c1\uff0c\u5374\u662f\u5927\u4e0d\u5c3d\u7136\uff0c\u8fd8\u8fdc\u4e0d\u5982\u6d61\u6ce5\u56fd\u86ee\u5937\u4e4b\u5730\u3002\u201d\u611f\u53f9\u4e86\u4e00\u4f1a\uff0c\u5c31\u5012\u5728\u5e8a\u4e0a\u7761\u4e86\u3002 \u3000\u3000\u521a\u8499\u9647\u5408\u773c\uff0c\u5ffd\u542c\u89c1\u95e8\u5916\u72ac\u5420\u4e4b\u58f0\u5927\u4f5c\uff0c\u8ddf\u7740\u6709\u4eba\u6012\u559d\u53eb\u9a82\uff0c\u84ec\u84ec\u84ec\u7684\u731b\u529b\u6253\u95e8\u3002\u8001\u5a46\u5a46\u4e0b\u5e8a\u6765\u8981\u53bb\u5f00\u95e8\uff0c\u8001\u5934\u513f\u6447\u624b\u6b62\u4f4f\uff0c\u8f7b\u8f7b\u5bf9\u5f20\u671d\u5510\u9053\uff1a\u201c\u76f8\u516c\uff0c\u4f60\u5230\u540e\u9762\u8eb2\u4e00\u8eb2\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u548c\u5f20\u5eb7\u8d70\u5230\u5c4b\u540e\uff0c\u95fb\u5230\u4e00\u9635\u65b0\u9c9c\u7684\u7a3b\u8349\u6c14\u606f\uff0c\u60f3\u662f\u5806\u79ef\u67f4\u8349\u7684\u6240\u5728\uff0c\u53ea\u542c\u89c1\u683c\u5566\u5566\u4e00\u9635\u54cd\uff0c\u5c4b\u95e8\u5df2\u88ab\u63a8\u5012\uff0c\u4e00\u4eba\u7c97\u58f0\u559d\u9053\uff1a\u201c\u5e72\u4e48\u4e0d\u5f00\u95e8\uff1f\u201d \u3000\u3000\u4e5f\u4e0d\u7b49\u56de\u7b54\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u6709\u4eba\u7ed9\u6253\u4e86\u8bb0\u8033\u5149\u3002 \u3000\u3000\u8001\u5a46\u5a46\u9053\uff1a\u201c\u4e0a\u5dee\u8001\u7237\uff0c\u6211\u2026\u2026\u6211\u4eec\u8001\u592b\u59bb\u5e74\u8001\u80e1\u6d82\uff0c\u8033\u6735\u4e0d\u597d\uff0c\u6ca1\u542c\u89c1\u3002\u201d\u54ea\u77e5\u53c8\u662f\u4e00\u8bb0\u8033\u5149\uff0c\u90a3\u4eba\u9a82\u9053\uff1a\u201c\u6ca1\u542c\u89c1\u5c31\u8be5\u6253\u3002\u5feb\u6740\u9e21\uff0c\u505a\u56db\u4e2a\u4eba\u7684\u996d\u3002\u201d\u8001\u5934\u513f\u9053\uff1a\u201c\u6211\u4eec\u4eba\u90fd\u5feb\u997f\u6b7b\u5566\uff0c\u54ea\u91cc\u6709\u751a\u4e48\u9e21\uff1f\u201d\u53ea\u542c\u84ec\u7684\u4e00\u58f0\uff0c\u4f3c\u4e4e\u8001\u5934\u513f\u88ab\u63a8\u5012\u5728\u5730\uff0c\u8001\u5a46\u5a46\u54ed\u53eb\u8d77\u6765\u3002 \u3000\u3000\u53c8\u542c\u53e6\u4e00\u4e2a\u58f0\u97f3\u9053\uff1a\u201c\u8001\u738b\uff0c\u7b97\u4e86\u5427\uff0c\u4eca\u65e5\u8dd1\u4e86\u6574\u6574\u4e00\u5929\uff0c\u53ea\u6536\u5230\u4e09\u4e24\u4e03\u94b1\u7a0e\u94f6\uff0c\u5927\u5bb6\u5fc3\u91cc\u4e0d\u75db\u5feb\uff0c\u4f60\u62ff\u4ed6\u51fa\u6c14\u4e5f\u6ca1\u7528\u3002\u201d\u90a3\u8001\u738b\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\uff0c\u4f60\u4e0d\u7528\u5f3a\u8fd8\u884c\uff1f\u8fd9\u51e0\u4e24\u94f6\u5b50\uff0c\u4e0d\u662f\u6211\u6253\u65ad\u90a3\u4e61\u4e0b\u4f6c\u7684\u72d7\u817f\uff0c\u8fd9\u4e9b\u4e0a\u8001\u513f\u4eec\u80af\u4e56\u4e56\u62ff\u51fa\u6765\u5417\uff1f\u201d\u53e6\u4e00\u4e2a\u5636\u54d1\u7684\u58f0\u97f3\u9053\uff1a\u201c\u8fd9\u4e9b\u4e61\u4e0b\u4f6c\u4e5f\u771f\u662f\u7684\uff0c\u7a77\u7684\u7c73\u7f38\u91cc\u6570\u6765\u6570\u53bb\u4e5f\u5f97\u5341\u51e0\u7c92\u7c73\uff0c\u518d\u903c\u5b9e\u5728\u4e5f\u903c\u4e0d\u51fa\u751a\u4e48\u6765\u5566\uff0c\u53ea\u662f\u5927\u8001\u7237\u53c8\u5f97\u9a82\u54b1\u4eec\u5144\u5f1f\u6ca1\u7528\u2026\u2026\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u7136\u5f20\u671d\u5510\u7684\u9a6c\u5636\u53eb\u8d77\u6765\u3002\u51e0\u540d\u516c\u5dee\u4e00\u60ca\uff0c\u51fa\u95e8\u67e5\u770b\uff0c\u89c1\u5230\u4e24\u5339\u9a6c\uff0c\u8bae\u8bba\u8d77\u6765\uff0c\u8bf4\u4e58\u9a6c\u4e4b\u4eba\u5b9a\u5728\u5c4b\u4e2d\u501f\u5bbf\uff0c\u770b\u6765\u5012\u6709\u4e00\u7b14\u6cb9\u6c34\uff0c\u5f53\u5373\u5174\u5174\u5934\u5934\u7684\u8fdb\u5c4b\u6765\u5bfb\u3002 \u3000\u3000\u5f20\u671d\u5510\u5927\u60ca\uff0c\u4e00\u626f\u5f20\u5eb7\u7684\u624b\uff0c\u8f7b\u8f7b\u4ece\u540e\u95e8\u6e9c\u4e86\u51fa\u53bb\u3002\u4e24\u4eba\u4e00\u811a\u9ad8\u4e00\u811a\u4f4e\uff0c\u5728\u5c71\u91cc\u4e71\u8d70\uff0c\u89c1\u65e0\u4eba\u8ffd\u6765\uff0c\u624d\u653e\u4e86\u5fc3\uff0c\u5e78\u4e8f\u6240\u5e26\u7684\u94f6\u4e24\u5f20\u5eb7\u90e8\u80cc\u5728\u80cc\u4e0a\u3002 \u3000\u3000\u4e24\u4eba\u5728\u6811\u4e1b\u4e2d\u8eb2\u4e86\u4e00\u5bb5\uff0c\u7b49\u5929\u8272\u5927\u4eae\uff0c\u624d\u6162\u6162\u6478\u5230\u5927\u9053\u4e0a\u6765\u3002\u4e3b\u4ec6\u4e24\u4eba\u884c\u51fa\u5341\u591a\u91cc\uff0c\u5546\u91cf\u5230\u524d\u9762\u5e02\u9547\u518d\u4e70\u4ee3\u6b65\u811a\u529b\u3002\u5f20\u5eb7\u4e0d\u4f4f\u75db\u9a82\u516c\u5dee\u5bb3\u4eba\u3002\u6b63\u9a82\u5f97\u75db\u5feb\uff0c\u5ffd\u7136\u659c\u523a\u5c0f\u8def\u91cc\u8d70\u6765\u56db\u540d\u516c\u5dee\uff0c\u624b\u4e2d\u62ff\u7740\u94fe\u6761\u94c1\u5c3a\uff0c\u540e\u9762\u4e24\u4eba\u5404\u7275\u7740\u4e00\u5339\u9a6c\uff0c\u90a3\u6b63\u662f\u4ed6\u4eec\u7684\u5750\u9a91\u3002 \u3000\u3000\u5f20\u671d\u5510\u548c\u5f20\u5eb7\u9762\u9762\u76f8\u89d1\uff0c\u8fd9\u65f6\u8981\u907f\u5f00\u5df2\u7ecf\u6765\u4e0d\u53ca\uff0c\u53ea\u5f97\u88c5\u4f5c\u82e5\u65e0\u5176\u4e8b\uff0c\u7ee7\u7eed\u8d70\u8def\u3002 \u3000\u3000\u90a3\u56db\u540d\u516c\u5dee\u4e0d\u4f4f\u5411\u4ed6\u4eec\u6253\u91cf\uff0c\u4e00\u540d\u6ee1\u8138\u6a2a\u8089\u7684\u516c\u5dee\u659c\u773c\u95ee\u9053\uff1a\u201c\u5582\uff0c\u670b\u53cb\uff0c\u5e72\u751a\u4e48\u7684\uff1f\u201d \u3000\u3000\u5f20\u671d\u5510\u4e00\u542c\u53e3\u97f3\uff0c\u6b63\u662f\u6628\u665a\u6253\u4eba\u7684\u90a3\u4e2a\u8001\u738b\u3002\u5f20\u5eb7\u8d70\u4e0a\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u90a3\u662f\u6211\u4eec\u516c\u5b50\u7237\uff0c\u8981\u4e0a\u5e7f\u5dde\u53bb\u8bfb\u4e66\u3002\u201d \u3000\u3000\u8001\u738b\u4e00\u628a\u63ea\u4f4f\uff0c\u631f\u624b\u593a\u8fc7\u4ed6\u80cc\u4e0a\u5305\u88f9\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u89c1\u7d2f\u7d2f\u5c3d\u662f\u9ec4\u91d1\u767d\u94f6\uff0c\u4e0d\u7531\u5f97\u60ca\u559c\u4ea4\u96c6\uff0c\u559d\u9053\uff1a\u201c\u751a\u4e48\u516c\u5b50\u7237\uff1f\u77a7\u4f60\u4e24\u4e2a\u90e8\u4e0d\u662f\u597d\u4e1c\u897f\uff01\u8fd9\u4e9b\u91d1\u94f6\u54ea\u91cc\u672a\u7684\uff1f\u5b9a\u662f\u5077\u6765\u9a97\u6765\u7684\uff0c\u597d\uff0c\u73b0\u4eca\u62ff\u5230\u8d3c\u8d43\u5566\uff0c\u8ddf\u6211\u89c1\u5927\u8001\u7237\u53bb\u3002\u201d\u4ed6\u89c1\u8fd9\u4e24\u4eba\u5e74\u5e7c\u597d\u6b3a\uff0c\u60f3\u628a\u4ed6\u4eec\u5413\u8dd1\u3002 \u3000\u3000\u54ea\u77e5\u5f20\u5eb7\u9053\uff1a\u201c\u6211\u4eec\u516c\u5b50\u7237\u662f\u5916\u56fd\u5927\u5b98\uff0c\u77e5\u5e9c\u5927\u4eba\u89c1\u4e86\u4ed6\u4e5f\u5ba2\u5ba2\u6c14\u6c14\uff0c\u89c1\u4f60\u4eec\u8001\u7237\u53bb\uff0c\u90a3\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u5566\uff01\u201d \u3000\u3000\u4e00\u540d\u4e2d\u5e74\u516c\u5dee\u542c\u4e86\u8fd9\u8bdd\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u60f3\u8fd9\u4e8b\u53ea\u6015\u8fd8\u6709\u540e\u60a3\uff0c\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u7d22\u6027\u6740\u4e86\u8fd9\u4e24\u4e2a\u96cf\u513f\uff0c\u53d1\u7b14\u6a2a\u8d22\u518d\u8bf4\uff0c\u7a81\u7136\u62bd\u51fa\u5355\u5200\u5411\u5f20\u5eb7\u5288\u53bb\u3002\u5f20\u5eb7\u5927\u9a87\uff0c\u6025\u5fd9\u7f29\u5934\uff0c\u4e00\u5200\u4ece\u5934\u9876\u63a0\u8fc7\uff0c\u780d\u53bb\u4e86\u4ed6\u5e3d\u5b50\u3002\u4ed6\u633a\u8eab\u6321\u4f4f\u516c\u5dee\uff0c\u53eb\u9053\uff0c\u201c\u516c\u5b50\u5feb\u9003\u3002\u201d\u5f20\u671d\u5510\u8f6c\u8eab\u5c31\u5954\u3002 \u3000\u3000\u90a3\u516c\u5dee\u53cd\u624b\u53c8\u662f\u4e00\u5200\uff0c\u8fd9\u6b21\u5f20\u5eb7\u6709\u4e86\u9632\u5907\uff0c\u4fa7\u8eab\u95ea\u8fc7\uff0c\u4ecd\u662f\u6ca1\u7ed9\u780d\u4e2d\u3002 \u3000\u3000\u4e3b\u4ec6\u4e24\u4eba\u6ca1\u547d\u4ef7\u5954\u9003\u3002\u56db\u540d\u516c\u5dee\u624b\u6301\u5175\u5203\uff0c\u5406\u559d\u7740\u8ffd\u6765\u3002 \u3000\u3000\u5f20\u671d\u5510\u5e73\u65f6\u517b\u5c0a\u5904\u4f18\uff0c\u52a0\u4e0a\u5fc3\u4e2d\u4e00\u5413\uff0c\u54ea\u91cc\u8fd8\u8dd1\u7684\u5feb\uff0c\u773c\u89c1\u5c31\u8981\u7ed9\u516c\u5dee\u8ffd\u4e0a\uff0c\u5ffd\u7136\u8fce\u9762\u4e00\u9a91\u9a6c\u5954\u9a70\u800c\u6765\u3002\u90a3\u4e2d\u5e74\u516c\u5dee\u89c1\u6709\u4eba\u6765\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff0c\u5927\u80c6\u76d7\u8d3c\uff0c\u7adf\u6562\u62d2\u6355\uff1f\u201d\u53e6\u5916\u51e0\u540d\u516c\u5dee\u4e5f\u5927\u53eb\uff1a\u201c\u6349\u5f3a\u76d7\uff0c\u6349\u5f3a\u76d7\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8bec\u9677\u5f20\u671d\u5510\u4e3b\u4ec6\u662f\u76d7\u532a\uff0c\u5fc3\u60f3\u6740\u4e86\u4eba\u8c01\u6562\u524d\u6765\u8fc7\u95ee\uff1f \u3000\u3000\u8fce\u9762\u90a3\u4e58\u9a6c\u8d8a\u5954\u8d8a\u8fd1\u3002\u9a6c\u4e0a\u4e58\u5ba2\u773c\u89c1\u524d\u9762\u4e24\u4eba\u5954\u9003\uff0c\u540e\u9762\u56db\u540d\u516c\u5dee\u5927\u547c\u8ffd\u9010\uff0c\u53ea\u9053\u771f\u662f\u6349\u62ff\u5f3a\u4eba\uff0c\u50ac\u9a6c\u75be\u9a70\uff0c\u5954\u5230\u5f20\u671d\u5510\u4e3b\u4ec6\u4e4b\u524d\uff0c\u4fef\u8eab\u4f38\u81c2\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u62c9\u4f4f\u4e24\u4eba\u540e\u9886\uff0c\u63d0\u4e86\u8d77\u6765\u3002\u56db\u540d\u516c\u5dee\u4e5f\u5df2\u6c14\u5598\u5598\u7684\u8d76\u5230\u3002 \u3000\u3000\u9a6c\u4e0a\u4e58\u8005\u628a\u5f20\u671d\u5510\u4e3b\u4ec6\u4e8c\u4eba\u5f80\u5730\u4e0a\u4e00\u63b7\uff0c\u7b11\u9053\uff1a\u201c\u5f3a\u76d7\u6349\u4f4f\u4e86\u3002\u201d\u8df3\u4e0b\u9a6c\u6765\u3002\u8fd9\u4eba\u8eab\u6750\u9b41\u68a7\uff0c\u58f0\u97f3\u6d2a\u4eae\uff0c\u6ee1\u8138\u6d53\u987b\uff0c\u7ea6\u83ab\u56db\u5341\u6765\u5c81\u5e74\u7eaa\u3002 \u3000\u3000\u56db\u540d\u516c\u5dee\u89c1\u4ed6\u8eab\u624b\u77eb\u6377\uff0c\u6c14\u529b\u751a\u5927\uff0c\u5f53\u4e0b\u542b\u7b11\u79f0\u8c22\uff0c\u5c06\u5f20\u671d\u5510\u4e3b\u4ec6\u62c9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u4e58\u9a6c\u5ba2\u89c1\u5f20\u671d\u5510\u4e00\u8eab\u5112\u670d\uff0c\u5f20\u5eb7\u9752\u8863\u5c0f\u5e3d\uff0c\u662f\u4e2a\u4e66\u50ee\uff0c\u54ea\u91cc\u50cf\u662f\u5f3a\u76d7\uff0c\u4e0d\u7981\u4e00\u6014\u3002\u5f20\u5eb7\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u82f1\u96c4\u6551\u547d\uff01\u4ed6\u4eec\u8981\u8c0b\u8d22\u5bb3\u547d\u3002\u201d\u90a3\u4eba\u559d\u95ee\uff1a\u201c\u4f60\u4eec\u5e72\u751a\u4e48\u7684\uff1f\u201d\u5f20\u5eb7\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5bb6\u516c\u5b50\uff0c\u662f\u53bb\u5e7f\u5dde\u8d76\u8003\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5df2\u88ab\u4e00\u540d\u516c\u5dee\u6309\u4f4f\u4e86\u5634\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u516c\u5dee\u5411\u4e58\u9a6c\u5ba2\u9053\uff1a\u201c\u8001\u5144\uff0c\u4f60\u8d70\u4f60\u7684\u9053\u5427\uff0c\u83ab\u7ba1\u6211\u4eec\u8859\u95e8\u7684\u516c\u4e8b\u3002\u201d \u3000\u3000\u4e58\u9a6c\u5ba2\u9053\uff1a\u201c\u4f60\u653e\u5f00\u624b\uff0c\u8ba9\u4ed6\u8bf4\u3002\u201d\u5f20\u671d\u5510\u9053\uff1a\u201c\u5728\u4e0b\u4e00\u4ecb\u4e66\u751f\uff0c\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u5c82\u662f\u5f3a\u4eba\u2026\u2026\u201d\u4e00\u540d\u516c\u5dee\u559d\u9053\uff1a\u201c\u8fd8\u8981\u591a\u5634\uff1f\u201d\u53cd\u8eab\u4e00\u8bb0\u5df4\u638c\uff0c\u5411\u4ed6\u6253\u53bb\u3002 \u3000\u3000\u4e58\u9a6c\u5ba2\u9a6c\u97ad\u6325\u51fa\uff0c\u97ad\u4e0a\u9769\u7ef3\u5377\u4f4f\u516c\u5dee\u624b\u8155\uff0c\u8fd9\u4e00\u638c\u4fbf\u672a\u6253\u7740\u3002\u4e58\u9a6c\u5ba2\u95ee\u9053\uff1a\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5f20\u5eb7\u9053\uff1a\u201c\u6211\u5bb6\u516c\u5b50\u8981\u53bb\u5e7f\u5dde\u8003\u79c0\u624d\uff0c\u9047\u4e0a\u8fd9\u56db\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u89c1\u5230\u6211\u4eec\u7684\u94f6\u5b50\uff0c\u5c31\u60f3\u6740\u4eba\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u8dea\u4e0b\u53eb\u9053\uff1a\u201c\u82f1\u96c4\u6551\u547d\uff01\u201d \u3000\u3000\u4e58\u9a6c\u5ba2\u95ee\u516c\u5dee\u9053\uff1a\u201c\u8fd9\u8bdd\u53ef\u771f\uff1f\u201d\u4f17\u516c\u5dee\u51b7\u7b11\u4e0d\u7b54\u3002\u90a3\u8001\u738b\u7ad9\u5728\u4ed6\u80cc\u540e\uff0c\u4e58\u4ed6\u4e0d\u89c9\uff0c\u7a81\u7136\u4e3e\u5200\u6402\u5934\u780d\u5c06\u4e0b\u6765\u3002 \u3000\u3000\u4e58\u9a6c\u5ba2\u542c\u5f97\u8111\u540e\u98ce\u751f\uff0c\u66f4\u4e0d\u56de\u5934\uff0c\u8eab\u5b50\u5411\u5de6\u5fae\u632b\uff0c\u53f3\u8db3\u201c\u4e4c\u9f99\u626b\u5730\u201d\uff0c\u6a2a\u626b\u800c\u51fa\uff0c\u6b63\u4e2d\u8001\u738b\u8db3\u80eb\uff0c\u5c06\u4ed6\u8e22\u51fa\u6570\u6b65\u3002\u4f59\u4e0b\u4e09\u540d\u516c\u5dee\u5927\u53eb\uff1a\u201c\u771f\u5f3a\u76d7\u6765\u5566\u3002\u201d\u4e24\u4e2a\u4e3e\u8d77\u94c1\u5c3a\uff0c\u4e00\u4e2a\u6325\u52a8\u94c1\u94fe\uff0c\u5411\u4e58\u9a6c\u5ba2\u56f4\u653b\u8fc7\u6765\u3002 \u3000\u3000\u5f20\u671d\u5510\u89c1\u4ed6\u624b\u65e0\u5bf8\u94c1\uff0c\u4e0d\u7981\u6697\u6697\u62c5\u5fe7\u3002\u4e58\u9a6c\u5ba2\u5374\u633a\u7136\u4e0d\u60e7\uff0c\u5de6\u8eb2\u53f3\u95ea\uff0c\u4e09\u540d\u516c\u5dee\u7684\u5175\u5203\u59cb\u7ec8\u4f24\u4ed6\u4e0d\u7740\u3002\u90a3\u8001\u738b\u7ad9\u8d77\u8eab\u6765\uff0c\u62a1\u5200\u4e0a\u524d\u5939\u653b\uff0c\u4e58\u9a6c\u5ba2\u5927\u559d\u4e00\u58f0\uff0c\u8001\u738b\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u5200\u6ca1\u780d\u51c6\uff0c\u4e58\u9a6c\u5ba2\u5288\u9762\u4e00\u62f3\uff0c\u6253\u5f97\u4ed6\u9f3b\u8840\u76f4\u6d41\u3002 \u3000\u3000\u8001\u738b\u53ea\u987e\u62a4\u75db\uff0c\u53cc\u624b\u63a9\u9762\uff0c\u5f53\u54ea\u4e00\u58f0\uff0c\u624b\u4e2d\u5355\u5200\u8dcc\u843d\u5728\u5730\u3002\u4e58\u9a6c\u5ba2\u62a2\u8fc7\u5355\u5200\uff0c\u56de\u624b\u6325\u51fa\uff0c\u780d\u4e2d\u4e86\u4e00\u540d\u624b\u6301\u94c1\u5c3a\u7684\u516c\u5dee\u53f3\u80a9\u3002\u4ed6\u5175\u5203\u5728\u624b\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u5200\u5149\u95ea\u5904\uff0c\u624b\u6301\u94c1\u94fe\u7684\u516c\u5dee\u5de6\u817f\u4e2d\u5200\uff0c\u8dcc\u5012\u5728\u5730\u3002\u5269\u4e0b\u4e00\u540d\u516c\u5dee\u4e0d\u6562\u518d\u6218\uff0c\u4e0d\u987e\u540c\u4f34\u6b7b\u6d3b\uff0c\u548c\u8001\u738b\u4e24\u4eba\u6492\u817f\u5c31\u9003\u3002\u4e58\u9a6c\u5ba2\u54c8\u54c8\u5927\u7b11\uff0c\u5c06\u5355\u5200\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u8dc3\u4e0a\u9a6c\u80cc\u3002 \u3000\u3000\u5f20\u671d\u5510\u5fd9\u4e0a\u524d\u9053\u8c22\uff0c\u8bf7\u95ee\u59d3\u540d\u3002\u4e58\u9a6c\u5ba2\u89c1\u4e24\u540d\u516c\u5dee\u8eba\u5728\u5730\u4e0a\u54fc\u54fc\u5527\u5527\u7684\u53eb\u75db\uff0c\u5411\u4ed6\u6012\u76ee\u800c\u89c6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u662f\u8bf4\u8bdd\u4e4b\u6240\uff0c\u54b1\u4eec\u4e0a\u9a6c\u518d\u8c08\u3002\u201d\u5f20\u5eb7\u62ff\u56de\u5305\u88f9\uff0c\u7275\u8fc7\u9a6c\u6765\uff0c\u4e09\u4eba\u5e76\u8f94\u800c\u884c\u3002 \u3000\u3000\u5f20\u671d\u5510\u8bf4\u4e86\u5bb6\u4e16\u59d3\u540d\u3002\u4e58\u9a6c\u5ba2\u9053\uff1a\u201c\u539f\u6765\u662f\u5f20\u516c\u5b50\u3002\u5728\u4e0b\u59d3\u6768\uff0c\u540d\u9e4f\u4e3e\uff0c\u6c5f\u6e56\u4e0a\u4eba\u79f0\u6469\u4e91\u91d1\u7fc5\uff0c\u662f\u6b66\u4f1a\u9556\u5c40\u7684\u9556\u5934\u3002\u201d\u5f20\u671d\u5510\u9053\uff1a\u201c\u4eca\u65e5\u82e5\u975e\u9601\u4e0b\u76f8\u6551\uff0c\u5c0f\u5f1f\u4e3b\u4ec6\u4e24\u4eba\u51c6\u662f\u6ca1\u547d\u7684\u4e86\u3002\u201d \u3000\u3000\u6768\u9e4f\u4e3e\u9053\uff1a\u201c\u8fd9\u4e00\u5e26\u4e71\u7684\u7740\u5b9e\u5389\u5bb3\uff0c\u5175\u532a\u96be\u5206\uff0c\u516c\u5b50\u8fd8\u662f\u53ca\u65e9\u56de\u53bb\u5916\u56fd\u7684\u4e3a\u662f\u3002\u5728\u4e0b\u4e5f\u6b63\u8981\u53bb\u5e7f\u5dde\uff0c\u516c\u5b50\u82e5\u4e0d\u5acc\u5f03\uff0c\u54b1\u4eec\u4fbf\u53ef\u7ed3\u4f34\u800c\u884c\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u5927\u559c\uff0c\u4e00\u518d\u79f0\u8c22\u3002\u8fd9\u51e0\u65e5\u6765\u4ed6\u5413\u5f97\u5fc3\u795e\u4e0d\u5b9a\uff0c\u73b0\u4eca\u5f97\u548c\u4e00\u4f4d\u9556\u5ba2\u540c\u884c\uff0c\u9002\u624d\u53c8\u89c1\u5230\u4ed6\u6b66\u529f\u4e86\u5f97\uff0c\u767b\u65f6\u5927\u611f\u5fc3\u5b89\u3002 \u3000\u3000\u4e09\u4eba\u884c\u4e86\u4e8c\u5341\u51e0\u91cc\u8def\uff0c\u5bfb\u4e0d\u5230\u6253\u5c16\u7684\u5e97\u5bb6\u3002\u6768\u9e4f\u4e3e\u8eab\u4e0a\u5e26\u7740\u5e72\u7cae\uff0c\u53d6\u51fa\u6765\u5206\u7ed9\u4e24\u4eba\u5403\u4e86\u3002\u5f20\u5eb7\u627e\u5230\u4e2a\u7834\u74e6\u7f50\uff0c\u6361\u4e86\u4e9b\u5e72\u67f4\uff0c\u60f3\u70e7\u4e9b\u6c34\u6765\u559d\uff0c\u5ffd\u542c\u5f97\u8eab\u540e\u6709\u4eba\u5927\u53eb\uff1a\u201c\u5f3a\u76d7\u5728\u8fd9\u91cc\u4e86\uff01\u201d\u5f20\u5eb7\u5413\u4e86\u4e00\u8df3\uff0c\u624b\u4e00\u9707\uff0c\u628a\u74e6\u7f50\u4e2d\u7684\u6c34\u90fd\u6cfc\u5728\u67f4\u4e0a\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u521a\u624d\u9003\u8d70\u7684\u516c\u5dee\u4e00\u9a6c\u5f53\u5148\uff0c\u9886\u4e86\u5341\u591a\u540d\u519b\u58eb\uff0c\u9a91\u4e86\u9a6c\u8d76\u6765\u3002\u6768\u9e4f\u4e3e\u53eb\u9053\uff1a\u201c\u5feb\u4e0a\u9a6c\u3002\u201d\u4e09\u4eba\u6025\u5fd9\u4e0a\u9a6c\u3002\u6768\u9e4f\u4e3e\u8ba9\u4e8c\u4eba\u5148\u8d70\uff0c\u62bd\u51fa\u6302\u5728\u9a6c\u978d\u65c1\u7684\u5355\u5200\uff0c\u5728\u540e\u63a9\u62a4\u3002\u4f17\u519b\u58eb\u9ad8\u53eb\uff1a\u201c\u6349\u5f3a\u76d7\u54ea\uff01\u201d\u7eb5\u9a6c\u6025\u8ffd\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u7b49\u9003\u51fa\u4e00\u7a0b\uff0c\u89c1\u8ffd\u5175\u8d8a\u8d76\u8d8a\u8fd1\uff0c\u519b\u58eb\u7eb7\u7eb7\u653e\u7bad\u3002\u6768\u9e4f\u4e3e\u6325\u5200\u62e8\u6253\uff0c\u5ffd\u89c1\u524d\u9762\u6709\u6761\u5c94\u8def\uff0c\u53eb\u9053\uff1a\u201c\u8d70\u5c0f\u8def\uff01\u201d\u5f20\u671d\u5510\u7eb5\u9a6c\u5411\u5c0f\u8def\u9a70\u53bb\uff0c\u5f20\u5eb7\u548c\u6768\u9e4f\u4e3e\u8ddf\u968f\u5728\u540e\uff0c\u8ffd\u5175\u6beb\u4e0d\u653e\u677e\u3002\u90a3\u516c\u5dee\u5927\u56b7\uff1a\u201c\u8ffd\u554a\uff0c\u6293\u5230\u4e86\u5f3a\u76d7\uff0c\u5927\u4f19\u513f\u5206\u4ed6\u91d1\u94f6\u3002\u201d \u3000\u3000\u6768\u9e4f\u4e3e\u89c1\u8ffd\u5175\u5c06\u8fd1\uff0c\u7d22\u6027\u52d2\u8f6c\u9a6c\u6765\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u6325\u5200\u780d\u53bb\u3002\u90a3\u516c\u5dee\u5413\u5f97\u5012\u9000\uff0c\u5176\u4f59\u519b\u58eb\u5374\u633a\u67aa\u6512\u523a\u3002\u6768\u9e4f\u4e3e\u654c\u4e0d\u8fc7\u4eba\u591a\uff0c\u6df7\u6218\u4e2d\u817f\u4e0a\u4e2d\u4e86\u4e00\u67aa\uff0c\u4f24\u52bf\u867d\u7136\u4e0d\u91cd\uff0c\u5374\u5df2\u4e0d\u6562\u604b\u6218\uff0c\u53cc\u817f\u4e00\u5939\uff0c\u63d0\u7f30\u7eb5\u9a6c\u5411\u524d\u6025\u51b2\uff0c\u6325\u5200\u5c06\u4e00\u540d\u519b\u58eb\u5de6\u81c2\u780d\u65ad\u3002\u5176\u4f59\u519b\u58eb\u5413\u5f97\u7eb7\u7eb7\u540e\u9000\uff0c\u6768\u9e4f\u4e3e\u5df2\u56de\u9a6c\u75be\u9a70\u3002\u4f17\u519b\u58eb\u89c1\u4ed6\u9003\u8dd1\uff0c\u80c6\u6c14\u53c8\u58ee\uff0c\u5450\u558a\u8ffd\u6765\u3002 \u3000\u3000\u4e0d\u4e00\u523b\u6768\u9e4f\u4e3e\u5df2\u8ffd\u4e0a\u5f20\u6c0f\u4e3b\u4ec6\uff0c\u8fd9\u65f6\u9053\u8def\u6108\u6765\u6108\u7a84\uff0c\u4f17\u519b\u58eb\u754f\u60e7\u6768\u9e4f\u4e3e\u52c7\u731b\uff0c\u4e0d\u6562\u5341\u5206\u903c\u8fd1\u3002 \u3000\u3000\u4e09\u4eba\u7eb5\u9a6c\u5954\u8dd1\u4e86\u4e00\u9635\uff0c\u5c71\u9053\u5f2f\u5f2f\u66f2\u66f2\uff0c\u8ffd\u5175\u5450\u558a\u4e4b\u58f0\u867d\u7136\u6e05\u6670\u53ef\u95fb\uff0c\u4eba\u5f71\u5374\u5df2\u4e0d\u89c1\u3002\u6025\u9a70\u4e2d\u524d\u9762\u7a81\u7136\u51fa\u73b0\u4e09\u6761\u5c0f\u5c94\u8def\uff0c\u6768\u9e4f\u4e3e\u4f4e\u559d\uff1a\u201c\u4e0b\u9a6c\uff01\u201d\u4e09\u4eba\u628a\u9a6c\u7275\u5230\u6811\u4e1b\u4e2d\u8eb2\u4e86\u8d77\u6765\uff0c\u7247\u523b\u95f4\u8ffd\u5175\u4e5f\u5df2\u8d76\u5230\u3002\u90a3\u516c\u5dee\u7565\u4e00\u8fdf\u7591\uff0c\u9886\u7740\u519b\u58eb\u5411\u4e00\u6761\u5c94\u8def\u8d76\u4e86\u4e0b\u6768\u9e4f\u4e3e\u9053\uff1a\u201c\u4ed6\u4eec\u8ffd\u4e86\u4e00\u9635\u4e0d\u89c1\uff0c\u5fc5\u5b9a\u56de\u5934\u3002\u54b1\u4eec\u5feb\u8d70\u3002\u201d\u6495\u4e0b\u8863\u895f\u88f9\u597d\u817f\u4f24\uff0c\u4e09\u4eba\u5411\u53e6\u4e00\u6761\u5c94\u8def\u6025\u9a70\u800c\u53bb\u3002 \u3000\u3000\u8fc7\u4e0d\u591a\u4e45\uff0c\u540e\u9762\u8ffd\u5175\u58f0\u53c8\u9690\u9690\u4f20\u6765\uff0c\u6768\u9e4f\u4e3e\u751a\u662f\u60f6\u6025\uff0c\u89c1\u524d\u9762\u6709\u4e09\u95f4\u74e6\u5c4b\uff0c\u5c4b\u524d\u6709\u4e00\u4e2a\u519c\u592b\u6b63\u5728\u9504\u5730\uff0c\u4fbf\u4e0b\u9a6c\u8d70\u5230\u519c\u592b\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u540e\u9762\u6709\u5b98\u5175\u8981\u5bb3\u6211\u4eec\uff0c\u8bf7\u4f60\u627e\u4e2a\u5730\u65b9\u7ed9\u8eb2\u4e00\u8eb2\u3002\u201d\u90a3\u519c\u592b\u53ea\u7ba1\u9504\u5730\uff0c\u4fbf\u4f3c\u6ca1\u542c\u89c1\u4ed6\u8bf4\u8bdd\u3002\u5f20\u671d\u5510\u4e5f\u4e0b\u9a6c\u592e\u544a\u3002 \u3000\u3000\u90a3\u519c\u592b\u7a81\u7136\u62ac\u8d77\u5934\u6765\uff0c\u5411\u4ed6\u4eec\u4ece\u5934\u81f3\u8db3\u6253\u91cf\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u524d\u9762\u6811\u4e1b\u4e2d\u4f20\u6765\u725b\u8e44\u8df5\u4e0a\u4e4b\u58f0\uff0c\u4e00\u4e2a\u7267\u7ae5\u9a91\u5728\u725b\u80cc\u4e0a\u8f6c\u4e86\u51fa\u6765\u3002\u90a3\u7267\u7ae5\u7ea6\u83ab\u5341\u5c81\u4e0a\u4e0b\u5e74\u7eaa\uff0c\u5934\u9876\u7528\u7ea2\u7ef3\u624e\u4e86\u4e2a\u5c0f\u8fab\u5b50\uff0c\u8138\u8272\u9edd\u9ed1\uff0c\u4e00\u53cc\u5927\u773c\u5374\u662f\u70af\u70af\u6709\u795e\u3002\u90a3\u519c\u592b\u5bf9\u7267\u7ae5\u9053\uff1a\u201c\u4f60\u628a\u9a6c\u5e26\u5230\u5c71\u91cc\u53bb\u653e\u8349\uff0c\u5929\u9ed1\u4e86\u518d\u56de\u6765\u5427\u3002\u201d\u5c0f\u7267\u7ae5\u671b\u4e86\u5f20\u671d\u5510\u4e09\u4eba\u4e00\u773c\uff0c\u5e94\u9053\uff1a\u201c\u597d\uff01\u201d\u7275\u4e86\u4e09\u5339\u9a6c\u5c31\u8d70\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u4e0d\u77e5\u90a3\u519c\u592b\u662f\u751a\u4e48\u7528\u610f\uff0c\u53ef\u662f\u4ed6\u8a00\u8bed\u795e\u60c5\u4e4b\u4e2d\uff0c\u4f3c\u6709\u4e00\u80a1\u5a01\u52bf\uff0c\u7adf\u7136\u4e0d\u6562\u51fa\u8a00\u963b\u6b62\u7267\u7ae5\u7275\u9a6c\u3002\u8fd9\u65f6\u8ffd\u5175\u58f0\u66f4\u52a0\u8fd1\u4e86\uff0c\u5f20\u671d\u5510\u6025\u7684\u8fde\u8bf4\uff1a\u201c\u600e\u4e48\u529e\uff0c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u90a3\u519c\u592b\u9053\uff1a\u201c\u8ddf\u6211\u6765\u3002\u201d\u5e26\u9886\u4e09\u4eba\u8d70\u8fdb\u5c4b\u5185\u3002\u5385\u5802\u4e0a\u6728\u684c\u677f\u51f3\uff0c\u5899\u4e0a\u6302\u7740\u84d1\u8863\u7281\u5934\uff0c\u4f46\u6536\u62fe\u5f97\u751a\u662f\u6d01\u51c0\uff0c\u4e0d\u4f3c\u5bfb\u5e38\u519c\u5bb6\u3002\u90a3\u519c\u592b\u76f4\u5165\u540e\u8ff8\uff0c\u4e09\u4eba\u8ddf\u4e86\u8fdb\u53bb\uff0c\u8d70\u8fc7\u5929\u4e95\uff0c\u6765\u5230\u4e00\u95f4\u5367\u623f\u3002\u90a3\u519c\u592b\u64a9\u8d77\u5e10\u5b50\uff0c\u9732\u51fa\u5899\u6765\u3002\u4f38\u624b\u5728\u5899\u4e0a\u4e00\u63a8\uff0c\u4e00\u5757\u5927\u77f3\u7ffb\u4e86\u8fdb\u53bb\uff0c\u5899\u4e0a\u73b0\u51fa\u4e00\u4e2a\u6d1e\u6765\u3002\u90a3\u519c\u592b\u9053\uff1a\u201c\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u4e09\u4eba\u4f9d\u8a00\u4eba\u5185\uff0c\u539f\u6765\u662f\u4e2a\u5bbd\u655e\u7684\u5c71\u4f97\u3002\u8fd9\u5c4b\u501a\u5c71\u800c\u5efa\uff0c\u521a\u9020\u5728\u5c71\u6d1e\u4e4b\u524d\uff0c\u5982\u4e0d\u628a\u623f\u5c4b\u62c6\u53bb\uff0c\u8c01\u4e5f\u731c\u4e0d\u5230\u6709\u6b64\u85cf\u8eab\u4e4b\u6240\u3002 \u3000\u3000\u4e09\u4eba\u8eb2\u597d\uff0c\u90a3\u519c\u592b\u5173\u4e0a\u5bc6\u95e8\uff0c\u81ea\u884c\u51fa\u53bb\u9504\u5730\u3002\u4e0d\u4e00\u523b\uff0c\u516c\u5dee\u5df2\u7387\u9886\u519b\u58eb\u8ffd\u5230\u3002\u90a3\u8001\u738b\u5411\u519c\u592b\u5927\u58f0\u5406\u559d\uff1a\u201c\u5582\uff0c\u6709\u4e09\u4e2a\u4eba\u9a91\u9a6c\u4ece\u8fd9\u8fb9\u8fc7\u53bb\u5417\uff1f\u201d\u90a3\u519c\u592b\u5411\u5c0f\u8def\u7684\u4e00\u8fb9\u6307\u4e86\u4e00\u6307\uff0c\u9053\uff1a\u201c\u65e9\u5c31\u8fc7\u53bb\u5566\uff01\u201d \u3000\u3000\u516c\u5dee\u519b\u58eb\u5954\u51fa\u4e86\u4e03\u516b\u91cc\u5730\uff0c\u4e0d\u89c1\u5f20\u671d\u5510\u7b49\u8e2a\u8ff9\uff0c\u6389\u8f6c\u9a6c\u5934\uff0c\u53c8\u6765\u8be2\u95ee\u3002\u201d \u3000\u3000\u90a3\u519c\u592b\u88c5\u804b\u4f5c\u54d1\uff0c\u8bdd\u4e5f\u8bf4\u4e0d\u5927\u6e05\u695a\u3002\u4e00\u540d\u519b\u58eb\u9a82\u9053\uff1a\u201c\u4ed6\u5988\u7684\uff0c\u591a\u95ee\u8fd9\u50bb\u74dc\u6709\u5c41\u7528\uff1f\u8d70\u5427\uff01\u201d\u4e00\u884c\u4eba\u53c8\u5411\u53e6\u4e00\u6761\u5c94\u8def\u8ffd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u3001\u5f20\u5eb7\u4e09\u4eba\u8eb2\u5728\u5c71\u6d1e\u4e4b\u5185\uff0c\u9690\u9690\u542c\u5f97\u9a6c\u5339\u5954\u9a70\u4e4b\u58f0\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u58f0\u97f3\u542c\u4e0d\u89c1\u4e86\uff0c\u90a3\u519c\u592b\u59cb\u7ec8\u4e0d\u6765\u5f00\u95e8\u3002\u6768\u9e4f\u4e3e\u7126\u8e81\u8d77\u6765\uff0c\u4f7f\u529b\u63a8\u95e8\uff0c\u63a8\u4e86\u534a\u5929\uff0c\u77f3\u95e8\u7eb9\u4e1d\u4e0d\u52a8\u3002\u4e09\u4eba\u53ea\u5f97\u5750\u5728\u5730\u4e0a\u6253\u76f9\u3002\u6768\u9e4f\u4e3e\u521b\u53e3\u4f5c\u75db\uff0c\u4e0d\u4f4f\u5492\u9a82\u516c\u5dee\u519b\u58eb\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u51e0\u4e2a\u65f6\u8fb0\uff0c\u77f3\u95e8\u5ffd\u7136\u8f67\u8f67\u4f5c\u54cd\u7684\u5f00\u4e86\uff0c\u900f\u8fdb\u5149\u6765\u3002\u90a3\u519c\u592b\u624b\u6301\u70db\u53f0\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u51fa\u6765\u5403\u996d\u5427\u3002\u201d \u3000\u3000\u6768\u9e4f\u4e3e\u9996\u5148\u8df3\u8d77\uff0c\u8d70\u4e86\u51fa\u53bb\uff0c\u5f20\u6c0f\u4e3b\u4ec6\u968f\u540e\u8d70\u5230\u5385\u4e0a\u3002\u53ea\u89c1\u677f\u684c\u4e0a\u6446\u4e86\u70ed\u817e\u817e\u7684\u996d\u83dc\uff0c\u5927\u76c6\u9752\u83dc\u8c46\u8150\u4e4b\u5916\uff0c\u5c45\u7136\u8fd8\u6709\u4e24\u53ea\u80a5\u9e21\u3002\u6768\u9e4f\u4e3e\u548c\u5f20\u5eb7\u90fd\u6697\u6697\u6b22\u559c\u3002 \u3000\u3000\u5385\u4e0a\u9664\u4e86\u65e5\u95f4\u6240\u89c1\u7684\u519c\u592b\u548c\u7267\u7ae5\uff0c\u8fd8\u6709\u4e09\u4eba\uff0c\u90fd\u4f5c\u519c\u592b\u6253\u626e\u3002\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u62f1\u624b\u76f8\u8c22\uff0c\u9053\u4e86\u81ea\u5df1\u59d3\u540d\uff0c\u53c8\u8bf7\u95ee\u5bf9\u65b9\u59d3\u540d\u3002 \u3000\u3000\u4e00\u4e2a\u9762\u76ee\u6e05\u766f\u3001\u4e94\u5341\u6765\u5c81\u7684\u519c\u592b\u9053\uff1a\u201c\u5c0f\u4eba\u59d3\u5e94\u3002\u201d\u6307\u7740\u65e5\u95f4\u6307\u5f15\u4ed6\u4eec\u8eb2\u85cf\u7684\u4eba\u9053\uff1a\u201c\u8fd9\u4f4d\u59d3\u6731\u3002\u201d\u4e00\u4e2a\u8eab\u6750\u6781\u9ad8\u7684\u7626\u5b50\u81ea\u79f0\u59d3\u502a\uff0c\u4e00\u4e2a\u80a5\u80a5\u77ee\u77ee\u7684\u5219\u8bf4\u59d3\u7f57\u3002\u5f20\u671d\u5510\u9053\uff1a\u201c\u6211\u8fd8\u9053\u5404\u4f4d\u662f\u4e00\u5bb6\u4eba\uff0c\u539f\u6765\u5747\u975e\u540c\u59d3\u3002\u201d\u90a3\u59d3\u5e94\u7684\u9053\uff1a\u201c\u6211\u4eec\u90fd\u662f\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u89c1\u4ed6\u4eec\u8bf4\u8bdd\u4e0d\u591a\uff0c\u795e\u8272\u51db\u7136\uff0c\u4e3e\u6b62\u7aef\u4e25\uff0c\u7edd\u4e0d\u4f3c\u5bfb\u5e38\u519c\u592b\uff0c\u90a3\u59d3\u6731\u548c\u59d3\u502a\u7684\u5c24\u5177\u5a01\u731b\u4e4b\u6c14\uff0c\u59d3\u5e94\u7684\u5219\u6c14\u5ea6\u9ad8\u96c5\uff0c\u4f3c\u662f\u4f4d\u9971\u8bfb\u8bd7\u4e66\u7684\u58eb\u4eba\u3002\u5f20\u671d\u5510\u8bd5\u63a2\u4e86\u51e0\u53e5\uff0c\u59d3\u5e94\u7684\u552f\u552f\u5426\u5426\uff0c\u5e76\u4e0d\u63a5\u53e3\u3002 \u3000\u3000\u996d\u7f62\uff0c\u59d3\u5e94\u7684\u95ee\u8d77\u5b98\u5175\u8ffd\u9010\u7684\u539f\u56e0\uff0c\u5f20\u671d\u5510\u539f\u539f\u672c\u672c\u8bf4\u4e86\u3002\u4ed6\u53e3\u624d\u4fbf\u7ed9\uff0c\u63cf\u8ff0\u9014\u4e2d\u6240\u89c1\u60e8\u51b5\uff0c\u4ee5\u53ca\u516c\u5dee\u6b3a\u538b\u767e\u59d3\u3001\u8bec\u826f\u4e3a\u76d7\u7684\u79cd\u79cd\u53ef\u6076\u60c5\u72b6\uff0c\u8bf4\u6765\u6709\u58f0\u6709\u8272\uff0c\u90a3\u59d3\u502a\u7684\u6c14\u5f97\u731b\u529b\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u987b\u7709\u4ff1\u5f20\uff0c\u5f00\u53e3\u6b32\u9a82\u3002\u59d3\u5e94\u7684\u4f7f\u4e2a\u773c\u8272\uff0c\u4ed6\u5c31\u4e0d\u8a00\u8bed\u4e86\u3002 \u3000\u3000\u5f20\u671d\u5510\u53c8\u8bf4\u5230\u6768\u9e4f\u4e3e\u5982\u4f55\u51fa\u624b\u76f8\u63f4\uff0c\u628a\u4ed6\u5927\u5927\u7684\u606d\u7ef4\u4e86\u4e00\u9635\u3002\u6768\u9e4f\u4e3e\u5341\u5206\u5f97\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7b97\u5f97\u751a\u4e48\uff0c\u60f3\u5f53\u5e74\u5728\u6c5f\u897f\u6211\u72ec\u529b\u6740\u6b7b\u9131\u9633\u4e09\u51f6\uff0c\u90a3\u624d\u6559\u9732\u8138\u5462\u3002\u201d\u5f53\u4e0b\u4fbf\u7eb5\u8c08\u5f53\u65f6\u60c5\u52bf\u5982\u4f55\u5371\u6025\u3001\u81ea\u5df1\u5982\u4f55\u82f1\u52c7\u3001\u5982\u4f55\u8d25\u4e2d\u53d6\u80dc\u3001\u8bf4\u5f97\u53e3\u6cab\u6a2a\u98de\u3002\u4ed6\u8d8a\u8bf4\u8d8a\u5f97\u610f\uff0c\u5c06\u5341\u591a\u5e74\u6765\u5728\u6c5f\u6e56\u4e0a\u7684\u906d\u9047\u5927\u5439\u7279\u5439\uff0c\u52a0\u6cb9\u6dfb\u9171\uff0c\u8bf4\u5f97\u81ea\u5df1\u82f1\u96c4\u76d6\u4e16\uff0c\u5f53\u4e16\u65e0\u654c\uff0c\u53c8\u8bf4\u9053\u4e0a\u5f3a\u4eba\u600e\u6837\u89c1\u4e86\u4ed6\u4ece\u6765\u4e0d\u6562\u62db\u60f9\u3002\u6b63\u8bf4\u5f97\u9ad8\u5174\uff0c\u90a3\u5c0f\u7267\u7ae5\u5ffd\u7136\u55e4\u7684\u4e00\u58f0\u7b11\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u4e0d\u4f4f\u53e3\u7684\u8c08\u8bba\u6c5f\u6e56\u4e0a\u7684\u4e8b\u8ff9\u3002\u5f20\u671d\u5510\u5bf9\u8fd9\u4e9b\u4e8b\u95fb\u6240\u672a\u95fb\uff0c\u542c\u5f97\u5f88\u6709\u5174\u5473\uff0c\u5f20\u5eb7\u66f4\u662f\u5c0f\u5b69\u813e\u6c14\uff0c\u8fde\u8fde\u60ca\u53f9\u8be2\u95ee\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u540e\u6765\u8bf4\u5230\u4e86\u6b66\u6280\uff0c\u4e3e\u624b\u62ac\u8db3\uff0c\u4e00\u9762\u8bb2\u4e00\u9762\u6bd4\u5212\u3002\u51e0\u4e2a\u519c\u592b\u5374\u4f3c\u4e4e\u542c\u5f97\u610f\u5174\u7d22\u7136\uff0c\u59d3\u7f57\u7684\u80d6\u5b50\u6253\u4e86\u4e2a\u5475\u6b20\u9053\uff1a\u201c\u4e0d\u65e9\u5566\uff0c\u5927\u5bb6\u7761\u5427\uff01\u201d \u3000\u3000\u5c0f\u7267\u7ae5\u8fc7\u53bb\u5173\u4e0a\u4e86\u95e8\uff0c\u59d3\u6731\u7684\u4ece\u6697\u5904\u63d0\u51fa\u4e00\u5757\u5927\u77f3\uff0c\u653e\u5728\u95e8\u540e\u3002\u6768\u9e4f\u4e3e\u4e00\u89c1\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u5012\u62bd\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4eba\u597d\u5927\u529b\u6c14\uff0c\u8fd9\u5757\u77f3\u5934\u5c11\u8bf4\u4e5f\u6709\u56db\u767e\u6765\u65a4\uff0c\u4ed6\u5c45\u7136\u6beb\u4e0d\u8d39\u529b\u7684\u63d0\u6765\u63d0\u53bb\u3002\u201d \u3000\u3000\u59d3\u5e94\u7684\u89c1\u4ed6\u9762\u8272\u6709\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u5c71\u91cc\u8001\u864e\u591a\uff0c\u6709\u65f6\u534a\u591c\u91cc\u649e\u8fdb\u95e8\u6765\uff0c\u56e0\u6b64\u8981\u7528\u77f3\u5934\u5835\u4f4f\u95e8\u6237\u3002\u201d\u8bf4\u58f0\u672a\u6bd5\uff0c\u5ffd\u7136\u4e00\u9635\u72c2\u98ce\u5439\u6765\uff0c\u6811\u679d\u547c\u547c\u4f5c\u54cd\uff0c\u95e8\u7a97\u4ff1\u52a8\uff0c\u968f\u5373\u542c\u5230\u864e\u5578\u8fde\u58f0\uff0c\u751a\u662f\u731b\u6076\uff0c\u63a5\u7740\u95e8\u5916\u725b\u9a6c\u60ca\u5636\u8d77\u6765\u3002\u59d3\u5e94\u7684\u9053\uff1a\u201c\u8bf4\u5230\u66f9\u64cd\uff0c\u66f9\u64cd\u5c31\u5230\u3002\u201d \u3000\u3000\u59d3\u502a\u7684\u7ad9\u8d77\u8eab\u6765\uff0c\u4ece\u95e8\u80cc\u540e\u53d6\u51fa\u4e00\u67c4\u94a2\u53c9\uff0c\u545b\u5577\u5577\u4e00\u6296\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u513f\u4e0d\u80fd\u8ba9\u5b83\u9003\u8d70\u4e86\u3002\u627f\u5fd7\uff0c\u4f60\u4e5f\u53bb\u3002\u201d\u5c0f\u7267\u7ae5\u559c\u5f62\u4e8e\u8272\uff0c\u5927\u58f0\u7b54\u5e94\uff0c\u5954\u8fdb\u53f3\u8fb9\u5c4b\u91cc\uff0c\u968f\u5373\u51fa\u6765\uff0c\u624b\u4e0a\u591a\u4e86\u4e2a\u76ae\u56ca\u548c\u4e00\u652f\u77ed\u94c1\u67aa\u3002\u59d3\u6731\u7684\u63d0\u5f00\u5927\u77f3\uff0c\u4e00\u9635\u72c2\u98ce\u7830\u7684\u4e00\u58f0\u628a\u95e8\u5439\u5f00\uff0c\u98ce\u5939\u843d\u53f6\uff0c\u76f4\u5377\u8fdb\u6765\uff0c\u8721\u70db\u987f\u65f6\u7184\u706d\u3002\u5f20\u5eb7\u60ca\u53eb\u58f0\u4e2d\uff0c\u59d3\u502a\u7684\u548c\u5c0f\u7267\u7ae5\u5148\u540e\u7eb5\u51fa\u95e8\u53bb\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u63d0\u8d77\u5355\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u53bb\uff01\u201d\u521a\u8de8\u51fa\u4e00\u6b65\uff0c\u5ffd\u7136\u5de6\u8155\u88ab\u4eba\u63e1\u4f4f\uff0c\u4ed6\u7528\u529b\u4e00\u6323\uff0c\u54ea\u77e5\u63e1\u4f4f\u4ed6\u7684\u4e94\u6307\u76f4\u5982\u4e00\u628a\u94a2\u722a\uff0c\u5c06\u4ed6\u7262\u7262\u6263\u67f1\uff0c\u4e1d\u6beb\u52a8\u5f39\u4e0d\u5f97\u3002\u9ed1\u6697\u4e2d\u542c\u5f97\u90a3\u59d3\u6731\u7684\u8bf4\u9053\uff1a\u201c\u522b\u51fa\u53bb\uff0c\u5927\u866b\u5f88\u5389\u5bb3\u3002\u201d\u6768\u9e4f\u4e3e\u53c8\u662f\u5f80\u5916\u4e00\u593a\u3002\u90a3\u59d3\u6731\u7684\u6ca1\u7ed9\u4ed6\u62c9\u52a8\uff0c\u4e5f\u6ca1\u66f4\u5411\u91cc\u62c9\uff0c\u53ea\u662f\u6293\u7740\u4e0d\u653e\u3002\u6768\u9e4f\u4e3e\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u5f97\u5750\u4e86\u4e0b\u6765\uff0c\u59d3\u6731\u7684\u4e5f\u5c31\u677e\u5f00\u4e86\u624b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u95e8\u5916\u90a3\u59d3\u502a\u7684\u5406\u559d\u58f0\u3001\u864e\u5578\u58f0\u3001\u94a2\u53c9\u4e0a\u94c1\u73af\u7684\u545b\u5577\u58f0\u3001\u75be\u98ce\u58f0\u3001\u6811\u679d\u5815\u5730\u58f0\uff0c\u54cd\u6210\u4e00\u7247\uff0c\u5076\u7136\u8fd8\u5939\u7740\u5c0f\u7267\u7ae5\u6e05\u8106\u7684\u547c\u53eb\u58f0\uff0c\u4e24\u4eba\u4e00\u864e\uff0c\u663e\u662f\u5728\u95e8\u5916\u6076\u6597\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u58f0\u97f3\u6e10\u8fdc\uff0c\u4f3c\u4e4e\u90a3\u864e\u53d7\u521b\u9003\u8d70\uff0c\u4e24\u4eba\u8ffd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u59d3\u7f57\u7684\u62ff\u51fa\u706b\u77f3\u706b\u7ed2\u70b9\u71c3\u4e86\u8721\u70db\uff0c\u53ea\u89c1\u5c4b\u4e2d\u6ee1\u5730\u90fd\u662f\u6811\u53f6\u3002\u5f20\u5eb7\u65e9\u5413\u5f97\u8138\u65e0\u4eba\u8272\uff0c\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u4e5f\u662f\u60ca\u5f02\u4e0d\u5b9a\u3002 \u3000\u3000\u4f17\u4eba\u5728\u5bc2\u9759\u4e2d\u4e0d\u4f5c\u4e00\u58f0\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8fdc\u5904\u811a\u6b65\u58f0\u54cd\uff0c\u8f6c\u77ac\u95f4\u5c0f\u7267\u7ae5\u51b2\u8fdb\u5c4b\u6765\u540e\uff0c\u7b11\u9010\u989c\u5f00\u7684\u53eb\u9053\uff1a\u201c\u5403\u8001\u864e\u8089\uff0c\u5403\u8001\u864e\u8089\uff01\u201d \u3000\u3000\u5f20\u671d\u5510\u89c1\u4ed6\u77ed\u67aa\u5934\u4e0a\u9c9c\u8840\u6dcb\u6f13\uff0c\u5fc3\u60f3\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u5982\u6b64\u6b66\u52c7\uff0c\u81ea\u5df1\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u5b9e\u5728\u60ed\u6127\u3002 \u3000\u3000\u6b63\u601d\u5ff5\u95f4\uff0c\u53ea\u89c1\u90a3\u59d3\u502a\u7684\u5927\u8e0f\u6b65\u7684\u8d70\u8fdb\u6765\uff0c\u5de6\u624b\u6301\u94a2\u53c9\uff0c\u53f3\u624b\u63d0\u7740\u9ec4\u9ed1\u76f8\u95f4\u7684\u4e00\u53ea\u5927\u8001\u864e\u3002\u4ed6\u5c06\u8001\u864e\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u5f20\u671d\u5510\u5413\u4e86\u4e00\u8df3\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u5f80\u91cc\u4e00\u7f29\uff0c\u77a7\u90a3\u8001\u864e\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u624d\u77e5\u5df2\u88ab\u6253\u6b7b\u3002 \u3000\u3000\u90a3\u59d3\u502a\u7684\u8138\u8272\u90d1\u91cd\uff0c\u5411\u5c0f\u7267\u7ae5\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u521a\u624d\u4f60\u6253\u9519\u4e86\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5c0f\u7267\u7ae5\u4f4e\u4e0b\u4e86\u5934\u9053\uff1a\u201c\u55ef\uff0c\u6211\u4e0d\u8be5\u6b63\u9762\u5bf9\u7740\u5927\u866b\u653e\u9556\u3002\u201d\u59d3\u502a\u7684\u8fd9\u624d\u548c\u989c\u60a6\u8272\u7684\u9053\uff1a\u201c\u6b63\u9762\u653e\u9556\uff0c\u4e5f\u4e0d\u662f\u4e0d\u53ef\u4ee5\uff0c\u4e0d\u8fc7\u4f60\u94a2\u9556\u8131\u624b\u4e4b\u540e\uff0c\u987b\u5f97\u7acb\u65f6\u5f80\u6a2a\u91cc\u8df3\u5f00\u3002\u521a\u624d\u4f60\u4e00\u9556\u6253\u574f\u5b83\u4e00\u53ea\u773c\u775b\uff0c\u5374\u7ad9\u7740\u4e0d\u52a8\u3002\u5927\u866b\u8d1f\u75db\u4e4b\u540e\uff0c\u6251\u8fc7\u6765\u7684\u52bf\u9053\u66f4\u731b\uff0c\u4e0d\u662f\u6211\u4e00\u53c8\u62b5\u4f4f\uff0c\u4f60\u8fd9\u6761\u5c0f\u547d\u8fd8\u5728\u5417\uff1f\u201d\u5c0f\u7267\u7ae5\u4e0d\u6562\u4f5c\u58f0\u3002\u59d3\u502a\u7684\u53c8\u8d5e\u4ed6\u51e0\u53e5\uff1a\u201c\u4f60\u8fd9\u51e0\u652f\u9556\u51c6\u5934\u662f\u5f88\u4e0d\u9519\u7684\u4e86\uff0c\u53ea\u662f\u529b\u9053\u6b20\u7740\u4e00\u70b9\uff0c\u4e0d\u8fc7\u8fd9\u4e5f\u4e0d\u80fd\u602a\u4f60\uff0c\u5c06\u6765\u5e74\u7eaa\u5927\u4e86\uff0c\u8155\u529b\u81ea\u4f1a\u52a0\u6dfb\u3002\u201d\u63d0\u8d77\u90a3\u53ea\u5927\u8001\u864e\uff0c\u6307\u7740\u8001\u864e\u7caa\u95e8\u4e0a\u7684\u4e00\u652f\u9556\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u9556\u8981\u662f\u52b2\u9053\u8db3\uff0c\u6253\u8fdb\u5b83\u809a\u91cc\uff0c\u5df2\u591f\u8981\u4e86\u8fd9\u755c\u7272\u7684\u547d\u5566\u3002\u201d\u5c0f\u7267\u7ae5\u9053\uff1a\u201c\u660e\u513f\u6211\u8981\u7528\u5fc3\u7ec3\u3002\u201d\u59d3\u502a\u7684\u70b9\u70b9\u5934\uff0c\u628a\u8001\u864e\u62d6\u8fdb\u540e\u5802\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u89c1\u8fd9\u4e24\u4eba\u8fd9\u822c\u8f7b\u800c\u6613\u4e3e\u7684\u6740\u4e86\u8fd9\u4e00\u5934\u5927\u8001\u864e\uff0c\u5fc3\u4e0b\u60f4\u60f4\uff0c\u770b\u6765\u8fd9\u6279\u4eba\u8def\u9053\u7740\u5b9e\u4e0d\u5bf9\uff0c\u591a\u534a\u662f\u4e54\u88c5\u7684\u5927\u76d7\uff0c\u81ea\u5df1\u548c\u5f20\u6c0f\u4e3b\u4ec6\u80e1\u91cc\u80e1\u6d82\u7684\u81ea\u6295\u76d7\u7a9f\uff0c\u8fd9\u756a\u53ef\u5f53\u771f\u7cdf\u4e86\u3002\u5f20\u671d\u5510\u5374\u4e0d\u4ee5\u4e3a\u610f\uff0c\u6781\u529b\u79f0\u8d5e\u5c0f\u7267\u7ae5\u7684\u82f1\u52c7\uff0c\u629a\u7740\u4ed6\u7684\u624b\u95ee\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u59d3\u751a\u4e48\uff1f\u4f60\u540d\u53eb\u627f\u5fd7\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u90a3\u7267\u7ae5\u7b11\u800c\u4e0d\u7b54\u3002 \u3000\u3000\u5f53\u665a\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u3001\u5f20\u5eb7\u4e09\u4eba\u540c\u5904\u4e00\u5ba4\u3002\u5f20\u5eb7\u7740\u6795\u4e4b\u540e\u7acb\u5373\u9163\u7761\uff0c\u5f20\u671d\u5510\u60f3\u8d77\u6b64\u884c\u98ce\u6ce2\u4e07\u91cc\uff0c\u5f92\u7136\u62c5\u60ca\u53d7\u6015\uff0c\u4e0d\u77e5\u6b64\u53bb\u5e7f\u5dde\uff0c\u662f\u5426\u5c1a\u6709\u51f6\u9669\uff0c\u53c8\u60f3\u6d61\u6ce5\u56fd\u8001\u864e\u4e5f\u662f\u4e0d\u5c11\uff0c\u5374\u65e0\u5982\u6b64\u5389\u5bb3\u7684\u6740\u864e\u82f1\u96c4\uff0c\u4e2d\u571f\u4eba\u7269\uff0c\u6bd5\u7adf\u4e0d\u51e1\uff0c\u601d\u6f6e\u8d77\u4f0f\uff0c\u4e00\u65f6\u96be\u4ee5\u5165\u7761\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u4e66\u58f0\u6717\u6717\uff0c\u90a3\u5c0f\u7267\u7ae5\u8bfb\u8d77\u4e66\u6765\u3002 \u3000\u3000\u5f20\u671d\u5510\u4fa7\u8033\u7ec6\u542c\uff0c\u4e66\u58f0\u4e2d\u8bf4\u7684\u4f3c\u662f\u5175\u9635\u6218\u6597\u4e4b\u4e8b\uff0c\u4e0d\u7981\u597d\u5947\u5fc3\u8d77\uff0c\u62ab\u8863\u4e0b\u5e8a\uff0c\u8d70\u5230\u5385\u4e0a\u3002\u53ea\u89c1\u684c\u4e0a\u70db\u5149\u660e\u4eae\uff0c\u5c0f\u7267\u7ae5\u6b63\u81ea\u8bfb\u4e66\u3002\u59d3\u5e94\u7684\u5750\u5728\u4e00\u65c1\u6559\u5bfc\uff0c\u89c1\u4ed6\u51fa\u6765\uff0c\u53ea\u5411\u4ed6\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u4f4e\u4e0b\u5934\u6765\uff0c\u6307\u7740\u4e66\u672c\u8bb2\u89e3\u3002 \u3000\u3000\u5f20\u671d\u5510\u8d70\u8fd1\u524d\u53bb\uff0c\u89c1\u684c\u4e0a\u8fd8\u653e\u4e86\u51e0\u672c\u4e66\uff0c\u62ff\u8d77\u6765\u4e00\u770b\uff0c\u4e66\u9762\u4e0a\u5199\u7740\u300a\u7eaa\u6548\u65b0\u4e66\u300b\u56db\u5b57\uff0c\u539f\u6765\u662f\u672c\u671d\u621a\u7ee7\u5149\u5c06\u519b\u6240\u8457\u7684\u5175\u6cd5\u3002\u621a\u7ee7\u5149\u4e4b\u540d\uff0c\u5f20\u671d\u5510\u5728\u6d61\u6ce5\u56fd\u4e5f\u6709\u6240\u95fb\uff0c\u77e5\u9053\u662f\u51fb\u7834\u502d\u5bc7\u7684\u540d\u5c06\uff0c\u540e\u6765\u9547\u5b88\u84df\u5dde\uff0c\u5f3a\u654c\u4e0d\u6562\u72af\u8fb9\uff0c\u7528\u5175\u5982\u795e\uff0c\u5a01\u9707\u56db\u6d77\u3002 \u3000\u3000\u5f20\u671d\u5510\u5411\u59d3\u5e94\u7684\u9053\uff1a\u201c\u5404\u4f4d\u51b3\u8ba1\u4e0d\u662f\u5e73\u5e38\u4eba\uff0c\u5374\u4e0d\u77e5\u4f55\u4ee5\u9690\u5c45\u5728\u6b64\uff1b\u53ef\u80fd\u89c1\u544a\u4e48\uff1f\u201d\u59d3\u5e94\u7684\u9053\uff1a\u201c\u6211\u4eec\u662f\u5bfb\u5e38\u8001\u767e\u59d3\uff0c\u79cd\u7530\u6253\u730e\uff0c\u8bfb\u4e66\u8bc6\u5b57\uff0c\u90a3\u662f\u6700\u5e73\u5e38\u4e0d\u8fc7\u7684\u3002\u516c\u5b50\u4e3a\u4f55\u89c9\u5f97\u5947\u602a\uff1f\u96be\u9053\u53ea\u6709\u5b98\u5bb6\u5b50\u5f1f\u624d\u53ef\u4ee5\u8bfb\u4e66\u5417\uff1f\u201d\u5f20\u671d\u5510\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4e2d\u571f\u5bfb\u5e38\u519c\u592b\uff0c\u4e5f\u662f\u5982\u6b64\u6587\u6b66\u5168\u624d\uff0c\u679c\u975e\u86ee\u90a6\u4e4b\u4eba\u53ef\u6bd4\u3002\u201d \u3000\u3000\u5fc3\u4e0b\u751a\u662f\u4f69\u670d\uff0c\u8bf4\u4e86\u58f0\u201c\u6253\u6270\u201d\uff0c\u53c8\u56de\u623f\u7761\u53bb\u4e86\u3002 \u3000\u3000\u819d\u819d\u80e7\u80e7\u7684\u7761\u4e86\u4e00\u4f1a\u513f\uff0c\u5ffd\u89c9\u6709\u4eba\u76f8\u63a8\uff0c\u60ca\u9192\u5750\u8d77\uff0c\u53ea\u542c\u6768\u9e4f\u4e3e\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u679c\u7136\u662f\u76d7\u7a9f\uff0c\u54b1\u4eec\u5feb\u8d70\u5427\uff01\u201d\u5f20\u671d\u5510\u5927\u5403\u4e00\u60ca\uff0c\u4f4e\u95ee\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6768\u9e4f\u4e3e\u70b9\u71c3\u70db\u706b\uff0c\u8d70\u5230\u4e00\u53ea\u6728\u7bb1\u8fb9\uff0c\u6380\u8d77\u7bb1\u76d6\u9053\uff1a\u201c\u4f60\u770b\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u4e00\u770b\uff0c\u53ea\u89c1\u6ee1\u7bb1\u5c3d\u662f\u91d1\u94f6\u73e0\u5b9d\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u628a\u70db\u53f0\u4ea4\u4ed6\u62ff\u7740\uff0c\u642c\u5f00\u6728\u7bb1\uff0c\u4e0b\u9762\u53c8\u6709\u4e00\u53ea\u6728\u7bb1\uff0c\u4f38\u624b\u4fbf\u53bb\u626d\u7bb1\u4e0a\u94dc\u9501\u3002\u5f20\u671d\u5510\u9053\uff1a\u201c\u522b\u770b\u65c1\u4eba\u9690\u79c1\uff0c\u53ea\u6015\u60f9\u51fa\u7978\u6765\u3002\u201d\u6768\u9e4f\u4e3e\u9053\uff1a\u201c\u8fd9\u91cc\u6c14\u606f\u53e4\u602a\u3002\u201d\u5f20\u671d\u5510\u5fd9\u95ee\uff1a\u201c\u751a\u4e48\u6c14\u606f\uff1f\u201d\u6768\u9e4f\u4e3e\u9053\uff1a\u201c\u8840\u8165\u6c14\u3002\u201d\u5f20\u671d\u5510\u4fbf\u4e0d\u6562\u8a00\u8bed\u4e86\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u626d\u65ad\u4e86\u9501\uff0c\u9759\u542c\u623f\u5916\u6ca1\u6709\u52a8\u9759\uff0c\u8f7b\u8f7b\u63ed\u5f00\u7bb1\u76d6\uff0c\u628a\u70db\u53f0\u5f80\u7bb1\u5185\u4e00\u7167\uff0c\u4e24\u4eba\u767b\u65f6\u5413\u5f97\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u4f46\u89c1\u7bb1\u4e2d\u8d6b\u7136\u662f\u4e24\u9897\u9996\u7ea7\uff0c\u4e00\u9897\u780d\u4e0b\u65f6\u65e5\u5df2\u4e45\uff0c\u8840\u8ff9\u90fd\u5df2\u53d8\u6210\u9ed1\u8272\uff0c\u53e6\u4e00\u9897\u5374\u662f\u65b0\u65a9\u4e0b\u7684\u3002\u4e24\u9897\u9996\u7ea7\u90fd\u7528\u77f3\u7070\u3001\u836f\u6599\u5236\u8fc7\uff0c\u662f\u4ee5\u987b\u7709\u4ff1\u5168\uff0c\u90a3\u9897\u780d\u4e0b\u5df2\u4e45\u7684\u4e5f\u672a\u8150\u70c2\u3002\u6768\u9e4f\u4e3e\u9976\u662f\u4e45\u5386\u6c5f\u6e56\uff0c\u8fd9\u65f6\u4e5f\u5413\u5f97\u624b\u811a\u53d1\u8f6f\uff0c\u5f20\u671d\u5510\u54ea\u91cc\u8fd8\u8bf4\u5f97\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u8f7b\u8f7b\u628a\u7bb1\u5b50\u8fd8\u539f\u653e\u597d\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d\u5230\u7095\u4e0a\u63a8\u9192\u4e86\u5f20\u5eb7\uff0c\u6478\u5230\u5385\u4e0a\u3002\u4e09\u4eba\u8e51\u8db3\u8d70\u5230\u95e8\u8fb9\uff0c\u6768\u9e4f\u4e3e\u6478\u5230\u5927\u77f3\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff0c\u7aed\u5c3d\u5168\u529b\uff0c\u4e5f\u642c\u5b83\u4e0d\u52a8\uff0c\u521a\u53ea\u63a8\u5f00\u5c3a\u8bb8\uff0c\u5ffd\u7136\u706b\u5149\u95ea\u4eae\uff0c\u90a3\u59d3\u6731\u7684\u62ff\u7740\u70db\u53f0\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u624b\u6309\u5200\u67c4\uff0c\u660e\u77e5\u4e0d\u654c\uff0c\u8eab\u5904\u6b64\u5883\uff0c\u4e5f\u53ea\u6709\u786c\u8d77\u5934\u76ae\u4e00\u62da\u3002\u54ea\u77e5\u59d3\u6731\u7684\u5e76\u4e0d\u7406\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u8d70\u4e86\u5417\uff1f\u201d\u4f38\u624b\u628a\u5927\u77f3\u63d0\u5728\u4e00\u8fb9\uff0c\u6253\u5f00\u4e86\u5927\u95e8\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u548c\u5f20\u671d\u5510\u4e0d\u6562\u591a\u8a00\uff0c\u5583\u5583\u8c22\u4e86\u51e0\u53e5\uff0c\u4f4e\u5934\u51fa\u95e8\uff0c\u4e0a\u9a6c\u5411\u4e1c\u75be\u9a70\u3002 \u3000\u3000\u5954\u4e86\u5341\u51e0\u91cc\u5730\uff0c\u6599\u60f3\u5df2\u8131\u9669\u5883\uff0c\u6b63\u611f\u5bbd\u6170\uff0c\u5ffd\u7136\u540e\u9762\u9a6c\u8e44\u58f0\u54cd\uff0c\u6709\u4eba\u5389\u58f0\u53eb\u9053\uff1a\u201c\u5582\uff0c\u7ad9\u4f4f\uff0c\u7ad9\u4f4f\uff01\u201d\u4e09\u4eba\u54ea\u91cc\u6562\u505c\uff0c\u7eb5\u9a6c\u6025\u884c\u3002 \u3000\u3000\u7a81\u7136\u9ed1\u5f71\u4e00\u6643\uff0c\u4e00\u4eba\u4ece\u9a6c\u65c1\u63a0\u8fc7\uff0c\u62a2\u5728\u524d\u9762\uff0c\u624b\u4e00\u4e3e\uff0c\u6768\u9e4f\u4e3e\u5750\u9a91\u53d7\u60ca\uff0c\u957f\u5636\u4e00\u58f0\uff0c\u4eba\u7acb\u8d77\u6765\u3002\u6768\u9e4f\u4e3e\u6325\u5200\u5411\u90a3\u4eba\u5f53\u5934\u780d\u53bb\u3002\u90a3\u4eba\u7a7a\u624b\u62c6\u4e86\u6570\u62db\uff0c\u5ffd\u5730\u9ad8\u8dc3\uff0c\u4f38\u5de6\u62f3\u5411\u6768\u9e4f\u4e3e\u53f3\u592a\u9633\u7a74\u6253\u843d\u3002\u6768\u9e4f\u4e3e\u5355\u5200\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u5411\u4ed6\u624b\u81c2\u75be\u780d\u3002\u5c82\u77e5\u90a3\u4eba\u8fd9\u4e00\u62f3\u4e43\u662f\u865a\u62db\uff0c\u534a\u8def\u4e0a\u53d8\u62f3\u4e3a\u638c\uff0c\u8eab\u672a\u843d\u5730\uff0c\u5df2\u52fe\u4f4f\u6768\u9e4f\u4e3e\u624b\u8155\uff0c\u559d\u58f0\uff1a\u201c\u4e0b\u6765\uff01\u201d\u5c06\u4ed6\u62d6\u4e0b\u9a6c\u6765\uff0c\u987a\u624b\u593a\u8fc7\u4e86\u4ed6\u624b\u4e2d\u5355\u5200\uff0c\u63b7\u5728\u5730\u4e0b\u3002 \u3000\u3000\u661f\u5149\u71b9\u5fae\u4e2d\u770b\u90a3\u4eba\u65f6\uff0c\u6b63\u662f\u90a3\u59d3\u6731\u7684\u519c\u592b\u3002 \u3000\u3000\u90a3\u4eba\u51b7\u51b7\u7684\u9053\uff1a\u201c\u56de\u53bb\uff01\u201d\u56de\u8fc7\u8eab\u672a\uff0c\u9a91\u4e0a\u9a6c\u5f53\u5148\u5c31\u8d70\uff0c\u4e5f\u4e0d\u7406\u4f1a\u4e09\u4eba\u662f\u5426\u968f\u540e\u8ddf\u6765\u3002\u6768\u9e4f\u4e3e\u77e5\u9053\u53cd\u6297\u56fa\u7136\u65e0\u76ca\uff0c\u9003\u4e5f\u9003\u4e0d\u4e86\uff0c\u53ea\u5f97\u4e56\u4e56\u7684\u4e0a\u4e86\u9a6c\uff0c\u4e09\u4eba\u8ddf\u7740\u4ed6\u56de\u53bb\u3002 \u3000\u3000\u4e00\u8fdb\u95e8\uff0c\u53ea\u89c1\u5385\u4e0a\u70db\u706b\u660e\u4eae\uff0c\u90a3\u5c0f\u7267\u7ae5\u548c\u5176\u4f59\u4e09\u4eba\u5750\u7740\u76f8\u5019\uff0c\u795e\u8272\u8083\u7136\uff0c\u4e00\u8bed\u4e0d\u53d1\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u81ea\u5fd6\u4e0d\u514d\u4e00\u6b7b\uff0c\u7d22\u6027\u786c\u6c14\u4e00\u70b9\uff0c\u6602\u7136\u8bf4\u9053\uff1a\u201c\u6768\u5927\u7237\u4eca\u65e5\u843d\u5728\u4f60\u4eec\u624b\u4e2d\uff0c\u8981\u6740\u5c31\u6740\uff0c\u4e0d\u5fc5\u591a\u8bf4\u3002\u201d \u3000\u3000\u59d3\u6731\u7684\u9053\uff1a\u201c\u5e94\u5927\u54e5\uff0c\u4f60\u8bf4\u600e\u4e48\u529e\uff1f\u201d\u59d3\u5e94\u7684\u6c89\u541f\u4e0d\u8bed\u3002\u59d3\u502a\u7684\u9053\uff1a\u201c\u5f20\u516c\u5b50\u4e3b\u4ec6\u653e\u8d70\uff0c\u628a\u59d3\u6768\u7684\u5bb0\u4e86\u3002\u201d\u59d3\u5e94\u7684\u9053\uff1a\u201c\u8fd9\u59d3\u6768\u7684\u5e72\u4fdd\u9556\u751f\u6daf\uff0c\u505a\u6709\u94b1\u4eba\u8d70\u72d7\uff0c\u80fd\u662f\u751a\u4e48\u597d\u4eba\uff1f\u4f46\u4ed6\u4eca\u5929\u89c1\u4e49\u52c7\u4e3a\uff0c\u603b\u7b97\u505a\u4e86\u4ef6\u597d\u4e8b\uff0c\u5c31\u9976\u4ed6\u4e00\u547d\u3002\u7f57\u5144\u5f1f\uff0c\u628a\u4ed6\u4e24\u4e2a\u62db\u5b50\u5e9f\u4e86\u3002\u201d \u3000\u3000\u59d3\u7f57\u7684\u7ad9\u8d77\u8eab\u6765\uff0c\u6768\u9e4f\u4e3e\u60e8\u7136\u53d8\u8272\u3002 \u3000\u3000\u5f20\u671d\u5510\u4e0d\u61c2\u6c5f\u6e56\u4e0a\u7684\u8bf4\u8bdd\uff0c\u4e0d\u77e5\u201c\u628a\u62db\u5b50\u5e9f\u4e86\u201d\u4fbf\u662f\u525c\u53bb\u773c\u775b\u4e4b\u610f\uff0c\u4f46\u89c1\u4e86\u5404\u4eba\u795e\u60c5\uff0c\u60f3\u6765\u5b9a\u662f\u8981\u4f24\u5bb3\u6768\u9e4f\u4e3e\uff0c\u6b63\u60f3\u5f00\u53e3\u6c42\u60c5\uff0c\u90a3\u5c0f\u7267\u7ae5\u9053\uff1a\u201c\u5e94\u53d4\u53d4\uff0c\u6211\u77a7\u4ed6\u602a\u53ef\u601c\u7684\uff0c\u5c31\u9976\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u59d3\u5e94\u7684\u4e0e\u4f17\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u5bf9\u6768\u9e4f\u4e3e\u9053\uff1a\u201c\u65e2\u7136\u6709\u4eba\u7ed9\u4f60\u6c42\u60c5\uff0c\u4e5f\u7f62\uff0c\u4f60\u80fd\u4e0d\u80fd\u7acb\u4e00\u4e2a\u8a93\uff0c\u4eca\u665a\u6240\u89c1\u4e4b\u4e8b\uff0c\u51b3\u4e0d\u6cc4\u6f0f\u4e00\u8a00\u534a\u8bed\uff1f\u201d \u3000\u3000\u6768\u9e4f\u4e3e\u5927\u559c\uff0c\u5fd9\u9053\uff1a\u201d\u4eca\u665a\u4e4b\u4e8b\uff0c\u5728\u4e0b\u5b9e\u975e\u6709\u610f\u7aa5\u63a2\uff0c\u4f46\u65e2\u7136\u88ab\u6211\u89c1\u5230\u4e86\uff0c\u81ea\u602a\u6768\u67d0\u6709\u773c\u65e0\u73e0\uff0c\u4e0d\u8bc6\u5404\u4f4d\u82f1\u96c4\u597d\u6c49\u3002\u5404\u4f4d\u7684\u4e8b\u5728\u4e0b\u7acb\u8a93\u5b88\u53e3\u5982\u74f6\uff0c\u5c06\u6765\u5982\u8fdd\u6b64\u8a93\uff0c\u5929\u8bdb\u5730\u706d\uff0c\u6b7b\u5f97\u60e8\u4e0d\u582a\u8a00\u3002\u201d\u59d3\u5e94\u7684\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u4fe1\u5f97\u8fc7\u4f60\u662f\u4e00\u6761\u6c49\u5b50\uff0c\u4f60\u53bb\u5427\u3002\u201d\u6768\u9e4f\u4e3e\u4e00\u62f1\u624b\uff0c\u8f6c\u8eab\u8981\u8d70\u3002\u59d3\u502a\u7684\u7a81\u7136\u7ad9\u8d77\u6765\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u8d70\u4e48\uff1f\u201d \u3000\u3000\u6768\u9e4f\u4e3e\u4e00\u695e\uff0c\u61c2\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u60e8\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u501f\u628a\u5200\u7ed9\u6211\u3002\u201d \u3000\u3000\u59d3\u6731\u7684\u4ece\u684c\u4e0b\u62bd\u51fa\u4e00\u628a\u5229\u5203\uff0c\u8f7b\u8f7b\u5012\u63b7\u8fc7\u53bb\u3002\u6768\u9e4f\u4e3e\u4f38\u624b\u63a5\u4f4f\uff0c\u8d70\u8fd1\u51e0\u6b65\uff0c\u5de6\u624b\u5e73\u653e\u684c\u4e0a\uff0c\u55d6\u7684\u4e00\u5200\uff0c\u767b\u65f6\u780d\u4e0b\u4e09\u4e2a\u624b\u6307\uff0c\u7b11\u9053\uff1a\u201c\u5149\u68cd\u4e00\u4eba\u4f5c\u4e8b\u4e00\u8eab\u5f53\uff0c\u8fd9\u4e8b\u8ddf\u5f20\u516c\u5b50\u5168\u6ca1\u5e72\u7cfb\u2026\u2026\u201d \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u624b\u4e0a\u8840\u6d41\u5982\u6ce8\uff0c\u5c45\u7136\u8fd8\u786c\u633a\u4f4f\uff0c\u4e5f\u90e8\u4f69\u670d\u4ed6\u7684\u6c14\u6982\uff0c\u59d3\u502a\u7684\u5927\u62c7\u6307\u4e00\u633a\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4eca\u665a\u7684\u4e8b\u5c31\u8fd9\u822c\u4e86\u7ed3\u3002\u201d\u8f6c\u8eab\u5165\u5185\uff0c\u62ff\u51fa\u5200\u4f24\u836f\u548c\u767d\u5e03\u6765\uff0c\u7ed9\u4ed6\u6b62\u8840\uff0c\u7f1a\u4e86\u4f24\u53e3\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u4e0d\u613f\u518d\u884c\u505c\u7559\uff0c\u8f6c\u8eab\u5bf9\u5f20\u671d\u5510\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u89c1\u4ed6\u8138\u8272\u60e8\u767d\uff0c\u81ea\u662f\u75db\u6781\uff0c\u60f3\u53eb\u4ed6\u5728\u6b64\u4f11\u606f\u4e00\u4e0b\uff0c\u53ef\u662f\u53c8\u8bf4\u4e0d\u51fa\u53e3\u3002 \u3000\u3000\u59d3\u5e94\u7684\u9053\uff1a\u201c\u5f20\u516c\u5b50\u6765\u81ea\u4e07\u91cc\u4e4b\u5916\uff0c\u6211\u4eec\u60ca\u5413\u4e86\u8fdc\u5ba2\uff0c\u5f88\u662f\u8fc7\u610f\u4e0d\u53bb\uff0c\u522b\u8ba9\u4f60\u56de\u5230\u5916\u56fd\uff0c\u8bf4\u6211\u4eec\u4e2d\u4e0a\u4eba\u58eb\u90fd\u662f\u7a77\u51f6\u6781\u6076\u4e4b\u8f88\u3002\u8fd9\u4f4d\u6768\u670b\u53cb\u4e5f\u5f88\u591f\u5149\u68cd\u3002\u6211\u9001\u4f60\u8fd9\u4e2a\u4e1c\u897f\u5427\u3002\u201d\u8bf4\u7740\u4ece\u888b\u91cc\u638f\u51fa\u4e00\u5757\u4e1c\u897f\uff0c\u4ea4\u7ed9\u5f20\u671d\u5510\u3002 \u3000\u3000\u5f20\u671d\u5510\u63a5\u8fc7\u4e00\u770b\uff0c\u8f7b\u98d8\u98d8\u7684\u662f\u4e00\u5757\u7af9\u724c\uff0c\u4e0a\u9762\u70d9\u4e86\u201c\u5c71\u5b97\u201d\u4e24\u5b57\uff0c\u724c\u80cc\u70d9\u4e86\u4e00\u4e9b\u82b1\u7eb9\uff0c\u770b\u4e0d\u51fa\u6709\u751a\u4e48\u7528\u5904\u3002 \u3000\u3000\u59d3\u5e94\u7684\u9053\uff1a\u201c\u773c\u524d\u5929\u4e0b\u5927\u4e71\uff0c\u4f60\u4e00\u4e2a\u6587\u5f31\u4e66\u751f\u4e0d\u5b9c\u5728\u5916\u9762\u4e71\u8d70\uff0c\u6211\u529d\u4f60\u8d76\u5feb\u56de\u5bb6\u3002\u8fd9\u51e0\u5929\u5728\u8def\u4e0a\u8981\u662f\u9047\u4e0a\u751a\u4e48\u5371\u96be\uff0c\u62ff\u51fa\u8fd9\u5757\u7af9\u724c\u6765\uff0c\u6216\u8bb8\u6709\u70b9\u51e0\u7528\u5904\u3002\u8fc7\u5f97\u51e0\u5e74\u2026\u2026\u5509\uff0c\u6216\u8005\u662f\u5341\u5e74\uff0c\u4e8c\u5341\u51e0\u5e74\uff0c\u4f60\u542c\u5f97\u4e2d\u571f\u592a\u5e73\u4e86\uff0c\u8fd9\u624d\u518d\u6765\u5427\uff01\u4e71\u4e16\u529f\u540d\uff0c\u5f97\u4e4b\u65e0\u76ca\uff0c\u53cd\u8db3\u60f9\u7978\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u518d\u770b\u7af9\u724c\uff0c\u5b9e\u4e0d\u89c1\u6709\u4f55\u5947\u7279\u4e4b\u5904\uff0c\u4e0d\u4fe1\u5b83\u6709\u4f55\u795e\u79d8\u6cd5\u529b\uff0c\u60f3\u662f\u5409\u7965\u4e4b\u7269\uff0c\u968f\u53e3\u8c22\u4e86\u4e00\u58f0\uff0c\u4ea4\u7ed9\u5f20\u5eb7\u6536\u5728\u8863\u56ca\u4e4b\u4e2d\u3002\u4e09\u4eba\u544a\u8f9e\u51fa\u6765\uff0c\u9a91\u4e0a\u9a6c\u7f13\u7f13\u800c\u884c\u3002\u56de\u5230\u9002\u624d\u548c\u90a3\u59d3\u6731\u7684\u4ea4\u624b\u6240\u5728\uff0c\u89c1\u5355\u5200\u5140\u81ea\u5728\u5730\uff0c\u95e8\u95ea\u53d1\u5149\uff0c\u6768\u9e4f\u4e3e\u62fe\u4e86\u8d77\u6765\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u81ea\u5938\u82f1\u96c4\u4e86\u5f97\uff0c\u78b0\u5728\u4eba\u5bb6\u624b\u91cc\uff0c\u5c41\u4e5f\u4e0d\u503c\uff01\u201d \u3000\u3000\u5929\u660e\u65f6\uff0c\u5230\u4e86\u4e00\u4e2a\u5c0f\u5e02\u9547\u4e0a\uff0c\u5f20\u671d\u5510\u627e\u4e86\u5ba2\u5e97\uff0c\u8ba9\u6768\u9e4f\u4e3e\u5b89\u7761\u4e86\u4e00\u5929\u4e00\u665a\u3002\u6b21\u6668\u624d\u518d\u8d76\u8def\u3002\u884c\u5230\u4e2d\u5348\u65f6\u5206\uff0c\u6253\u8fc7\u5c16\uff0c\u4e0a\u9a6c\u53c8\u884c\u4e86\u4e8c\u5341\u591a\u91cc\u8def\uff0c\u5ffd\u7136\u8e44\u58f0\u54cd\u5904\uff0c\u4e00\u9a91\u9a6c\u8fce\u9762\u5954\u6765\uff0c\u63a0\u8fc7\u8eab\u65c1\uff0c\u5411\u4e09\u4eba\u671b\u4e86\u4e00\u773c\uff0c\u7edd\u5c18\u800c\u53bb\u3002\u884c\u4e86\u4e94\u516d\u91cc\u8def\uff0c\u540e\u9762\u9a6c\u8e44\u58f0\u53c8\u8d77\uff0c\u4ecd\u662f\u90a3\u9a91\u9a6c\u8ffd\u4e86\u4e0a\u6765\u3002\u8fd9\u6b21\u6768\u9e4f\u4e3e\u548c\u5f20\u671d\u5510\u90fd\u770b\u5f97\u6e05\u695a\u4e86\uff0c\u9a6c\u4e0a\u90a3\u4eba\u9752\u5dfe\u5305\u5934\uff0c\u7709\u76ee\u4e4b\u95f4\u82f1\u608d\u4e4b\u6c14\u6bd5\u9732\uff0c\u4ece\u4e09\u4eba\u8eab\u65c1\u63a0\u8fc7\uff0c\u75be\u9a70\u800c\u524d\u3002 \u3000\u3000\u5f20\u671d\u5510\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u4e5f\u53e4\u602a\uff0c\u600e\u4e48\u53bb\u4e86\u53c8\u56de\u6765\u3002\u201d\u6768\u9e4f\u4e3e\u9053\uff1a\u201c\u5f20\u516c\u5b50\uff0c\u5f85\u4f1a\u4f60\u81ea\u884c\u9003\u547d\u7f62\uff0c\u4e0d\u7528\u7b49\u6211\u3002\u201d\u5f20\u671d\u5510\u60ca\u9053\uff1a\u201c\u600e\u4e48\uff1f\u53c8\u6709\u5f3a\u76d7\u4e48\uff1f\u201d\u6768\u9e4f\u4e3e\u9053\uff1a\u201c\u8d70\u4e0d\u4e0a\u4e94\u91cc\uff0c\u5fc5\u6709\u4e8b\u6545\uff0c\u4e0d\u8fc7\u54b1\u4eec\u540e\u65e0\u9000\u8def\uff0c\u4e5f\u53ea\u6709\u5411\u524d\u95ef\u4e86\u3002\u201d \u3000\u3000\u4e09\u4eba\u60f4\u60f4\u4e0d\u5b89\uff0c\u6162\u6162\u5411\u524d\u6328\u53bb\uff0c\u53ea\u8d70\u4e86\u4e24\u91cc\u591a\u8def\uff0c\u53ea\u542c\u89c1\u5618\u54e9\u54e9\u4e00\u58f0\uff0c\u4e00\u652f\u54cd\u7bad\u5c04\u4e0a\u5929\u7a7a\uff0c\u4e09\u4e58\u9a6c\u4ece\u6797\u4e2d\u7a9c\u51fa\uff0c\u62e6\u5728\u5f53\u8def\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u50ac\u9a6c\u4e0a\u524d\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u6b66\u4f1a\u9556\u5c40\u59d3\u6768\uff0c\u8def\u7ecf\u8d35\u5730\uff0c\u5e76\u975e\u4fdd\u9556\uff0c\u6ca1\u5411\u5404\u4f4d\u5f53\u5bb6\u6295\u5e16\u62dc\u8c12\u3002\u8fd9\u4f4d\u5f20\u76f8\u516c\u6765\u81ea\u5916\u56fd\uff0c\u4ed6\u662f\u8bfb\u4e66\u4eba\uff0c\u8bf7\u5404\u4f4d\u9ad8\u62ac\u8d35\u624b\uff0c\u8ba9\u4e00\u6761\u9053\u3002\u201d\u4ed6\u5728\u6c5f\u6e56\u4e0a\u672c\u6765\u7565\u6709\u540d\u5934\uff0c\u624b\u4e0a\u6b66\u827a\u4e5f\u81ea\u4e0d\u5f31\uff0c\u4e0d\u8fc7\u521a\u65ad\u4e86\u624b\u6307\uff0c\u53c8\u60f3\u8fd9\u4e00\u5e26\u9053\u4e0a\u7684\u670b\u53cb\u591a\u534a\u4e0e\u59d3\u5e94\u7684\u662f\u4e00\u4f19\uff0c\u662f\u4ee5\u63aa\u8bcd\u8c26\u606d\uff0c\u597d\u8a00\u76f8\u6c42\u3002 \u3000\u3000\u4e09\u4e58\u4e2d\u5f53\u4e2d\u4e00\u4eba\u53cc\u624b\u7a7a\u7a7a\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5c11\u4e86\u76d8\u7f20\uff0c\u8981\u501f\u4e00\u767e\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u6d59\u5357\u571f\u8bdd\uff0c\u6768\u9e4f\u4e3e\u548c\u5f20\u671d\u5510\u6115\u7136\u76f8\u5bf9\uff0c\u4e0d\u77e5\u4ed6\u8bf4\u4e9b\u751a\u4e48\u3002 \u3000\u3000\u521a\u624d\u9a91\u9a6c\u6765\u56de\u76f8\u63a2\u7684\u90a3\u4eba\u559d\u9053\uff1a\u201c\u501f\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u61c2\u4e86\u6ca1\u6709\uff1f\u201d\u6768\u9e4f\u4e3e\u89c1\u4ed6\u4eec\u5982\u6b64\u65e0\u793c\uff0c\u4e0d\u7981\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u8981\u501f\u94f6\u5b50\uff0c\u987b\u51ed\u672c\u4e8b\uff01\u201d\u5f53\u5148\u90a3\u4eba\u559d\u9053\uff1a\u201c\u597d\uff01\u8fd9\u672c\u4e8b\u503c\u4e0d\u503c\u4e00\u767e\u4e24\u94f6\u5b50\uff1f\u201d\u4ece\u80cc\u4e0a\u53d6\u4e0b\u5f39\u5f13\uff0c\u53ed\u53ed\u53ed\uff0c\u4e09\u7c92\u5f39\u5b50\u6253\u4e0a\u5929\u7a7a\uff0c\u7b49\u5f39\u5b50\u52bf\u5b8c\u843d\u4e0b\uff0c\u53c8\u662f\u8fde\u73e0\u4e09\u5f39\uff0c\u516d\u9897\u5f39\u5b50\u5728\u7a7a\u4e2d\u5206\u6210\u4e09\u5bf9\uff0c\u4e92\u76f8\u649e\u5f97\u7c89\u788e\uff0c\u53d8\u6210\u788e\u6ce5\u7eb7\u7eb7\u4e0b\u5815\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u89c1\u5230\u8fd9\u795e\u5f39\u7edd\u6280\uff0c\u521a\u53ea\u4e00\u5446\uff0c\u7a81\u89c9\u5de6\u8155\u5267\u75db\uff0c\u5355\u5200\u5f53\u7684\u4e00\u58f0\u843d\u5728\u5730\u4e0b\uff0c\u624d\u77e5\u5df2\u88ab\u4ed6\u5f39\u5b50\u6253\u4e2d\u4e86\u624b\u3002 \u3000\u3000\u5bf9\u9762\u7b2c\u4e09\u4eba\u624b\u6301\u8f6f\u97ad\uff0c\u7eb5\u9a6c\u8fc7\u6765\uff0c\u4e00\u62db\u201c\u67af\u85e4\u7f20\u6811\u201d\uff0c\u5411\u4ed6\u8170\u95f4\u76d8\u6253\u800c\u81f3\u3002\u6768\u9e4f\u4e3e\u52d2\u9a6c\u907f\u5f00\u3002\u90a3\u4eba\u8f6f\u97ad\u97ad\u5934\u4e58\u52bf\u5728\u5730\u4e0b\u5377\u8d77\u5355\u5200\uff0c\u6284\u5728\u624b\u4e2d\uff0c\u957f\u7b11\u4e00\u58f0\uff0c\u7eb5\u9a6c\u75be\u9a70\uff0c\u63a0\u8fc7\u5f20\u5eb7\u8eab\u8fb9\u65f6\uff0c\u767d\u5149\u95ea\u52a8\uff0c\u94a2\u5200\u6325\u4e86\u4e24\u6325\uff0c\u5df2\u5272\u65ad\u4ed6\u80cc\u4e0a\u5305\u88f9\u4e24\u7aef\u7684\u5e03\u6761\u3002\u4ed6\u5374\u6beb\u4e0d\u505c\u7559\uff0c\u50ac\u9a6c\u5411\u524d\u5954\u9a70\u3002 \u3000\u3000\u5305\u88f9\u6b63\u4ece\u5f20\u5eb7\u80cc\u4e0a\u6ed1\u843d\uff0c\u6253\u5f39\u5b50\u90a3\u4eba\u6070\u597d\u9a70\u5230\uff0c\u624b\u81c2\u63a2\u51fa\uff0c\u4e0d\u5f85\u5305\u88f9\u843d\u5730\uff0c\u5df2\u4fef\u8eab\u63d0\u8d77\uff0c\u6382\u4e86\u6382\u91cd\u91cf\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4e86\u3002\u201d\u8f6c\u773c\u95f4\u4e09\u4eba\u8dd1\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u53ea\u662f\u53f9\u6c14\uff0c\u65e0\u8bdd\u53ef\u8bf4\u3002\u5f20\u5eb7\u6025\u9053\uff1a\u201c\u6211\u4eec\u7684\u76d8\u8d39\u94f6\u4e24\u90fd\u5728\u5305\u88f9\uff0c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u600e\u4e48\u56de\u5bb6\u5462\uff1f\u201d\u6768\u9e4f\u4e3e\u9053\uff1a\u201c\u7559\u4e0b\u4f60\u8fd9\u6761\u5c0f\u547d\uff0c\u5df2\u7b97\u4e0d\u9519\u7684\u5566\uff0c\u8d70\u7740\u77a7\u5427\u3002\u201d\u4e09\u4eba\u5782\u5934\u4e27\u6c14\u7684\u53c8\u884c\u3002 \u3000\u3000\u8d70\u4e0d\u5230\u4e00\u987f\u996d\u65f6\u5206\uff0c\u5ffd\u7136\u8eab\u540e\u8e44\u58f0\u6742\u6c93\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5c18\u5934\u8d77\u5904\uff0c\u90a3\u4e09\u4eba\u53c8\u8ffd\u4e86\u8f6c\u6765\u3002\u6768\u9e4f\u4e3e\u548c\u5f20\u671d\u5510\u90fd\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u60f3\uff0c\u201c\u62a2\u4e86\u91d1\u94f6\u4e5f\u5c31\u7f62\u4e86\uff0c\u96be\u9053\u5f53\u771f\u8fd8\u975e\u8981\u4e86\u6027\u547d\u4e0d\u6210\uff1f\u201d \u3000\u3000\u90a3\u4e09\u4eba\u9a70\u5230\u8ddf\u524d\uff0c\u4e00\u9f50\u6eda\u978d\u4e0b\u9a6c\uff0c\u5f53\u5148\u4e00\u4eba\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u81ea\u5df1\u4eba\uff0c\u5f97\u7f6a\u5f97\u7f6a\u3002\u6211\u4eec\u4e0d\u77e5\uff0c\u591a\u6709\u5192\u72af\uff0c\u8bf7\u52ff\u89c1\u602a\u3002\u201d\u53e6\u4e00\u4eba\u53cc\u624b\u6258\u4f4f\u5305\u88f9\uff0c\u4ea4\u7ed9\u5f20\u5eb7\u3002\u5f20\u5eb7\u5374\u4e0d\u6562\u63a5\uff0c\u773c\u671b\u4e3b\u4eba\u3002\u5f20\u671d\u5510\u70b9\u70b9\u5934\uff0c\u5f20\u5eb7\u8fd9\u624d\u63a5\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5f53\u5148\u90a3\u4eba\u9053\uff1a\u201c\u521a\u624d\u542c\u5f97\u8fd9\u4f4d\u8a00\u9053\uff0c\u4e00\u4f4d\u662f\u6768\u9556\u5934\uff0c\u4e00\u4f4d\u662f\u5f20\u516c\u5b50\uff0c\u90fd\u662f\u771f\u59d3\u4e48\uff1f\u201d\u5f20\u671d\u5510\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u8bf4\u4e86\u4e24\u4eba\u7684\u59d3\u540d\u6765\u5386\u3002 \u3000\u3000\u4e09\u4eba\u542c\u4e86\uff0c\u5747\u6709\u8be7\u5f02\u4e4b\u8272\uff0c\u4e92\u76f8\u671b\u4e86\u4e00\u773c\u3002\u5f53\u5148\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u9ec4\uff0c\u8fd9\u4e24\u4f4d\u662f\u4eb2\u5144\u5f1f\uff0c\u59d3\u5218\u3002\u5f20\u516c\u5b50\uff0c\u4f60\u65e9\u62ff\u51fa\u7af9\u724c\u6765\u5c31\u597d\u4e86\uff0c\u514d\u5f97\u6211\u4eec\u65e0\u793c\u3002\u201d\u5f20\u671d\u5510\u542c\u4e86\u8fd9\u8bdd\uff0c\u624d\u77e5\u9053\u8fd9\u5757\u7af9\u724c\u679c\u771f\u6548\u529b\u4e0d\u5c0f\uff0c\u5fc3\u795e\u4e0d\u5b9a\u4e4b\u9645\uff0c\u4e5f\u4e0d\u77e5\u8bf4\u751a\u4e48\u8bdd\u597d\u3002 \u3000\u3000\u90a3\u59d3\u9ec4\u7684\u53c8\u9053\uff1a\u201c\u4e24\u4f4d\u4e00\u5b9a\u4e5f\u662f\u5230\u5723\u5cf0\u5d82\u53bb\u4e86\uff0c\u54b1\u4eec\u4e00\u8def\u8d70\u5427\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u90fd\u6599\u60f3\u4ed6\u4eec\u662f\u4e00\u5e2e\u58f0\u52bf\u6d69\u5927\u7684\u76d7\u4f19\uff0c\u8fdc\u907f\u4e4b\u60df\u6050\u4e0d\u53ca\uff0c\u600e\u6562\u518d\u53bb\u62db\u60f9\uff1f\u5f20\u671d\u5510\u9053\uff1a\u201c\u6211\u548c\u8fd9\u4f4d\u670b\u53cb\u8981\u8d76\u8d74\u5e7f\u5dde\uff0c\u5723\u5cf0\u5d82\u662f\u4e0d\u53bb\u4e86\u3002\u201d \u3000\u3000\u59d3\u9ec4\u7684\u8138\u5e26\u6012\u8272\u9053\uff1a\u201c\u518d\u8fc7\u4e09\u5929\u5c31\u662f\u516b\u6708\u5341\u516d\uff0c\u6211\u4eec\u5343\u91cc\u8fe2\u8fe2\u7684\u8d76\u6765\u7ca4\u4e1c\uff0c\u4f60\u4eec\u5230\u4e86\u8fd9\u91cc\uff0c\u600e\u4e0d\u4e0a\u5c71\uff1f\u201d\u4e0a\u5c71\u505a\u751a\u4e48\uff0c\u516b\u6708\u5341\u516d\u6709\u751a\u4e48\u5e72\u7cfb\uff0c\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u4e24\u4eba\u5168\u4e0d\u77e5\u60c5\uff0c\u53ef\u662f\u53c8\u4e0d\u6562\u76f4\u8ba4\u3002\u5f20\u671d\u5510\u786c\u4e86\u5934\u76ae\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u5bb6\u6709\u6025\u4e8b\uff0c\u987b\u5f97\u9a6c\u4e0a\u56de\u53bb\u3002\u201d \u3000\u3000\u59d3\u9ec4\u7684\u6012\u9053\uff1a\u201c\u4e0a\u5c71\u4e5f\u803d\u6401\u4e0d\u4e86\u4f60\u4e24\u5929\u3002\u4f60\u4eec\u8fc7\u5c71\u4e0d\u62dc\uff0c\u7b97\u5f97\u751a\u4e48\u5c71\u5b97\u7684\u670b\u53cb\uff1f\u201d\u5f20\u671d\u5510\u66f4\u52a0\u6478\u4e0d\u7740\u5934\u8111\uff0c\u4e0d\u77e5\u9053\u201c\u5c71\u5b97\u201d\u662f\u751a\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u7ec8\u7a76\u9605\u5386\u591a\uff0c\u89c1\u8fd9\u60c5\u52bf\uff0c\u77e5\u9053\u5723\u5cf0\u5d82\u662f\u975e\u53bb\u4e0d\u53ef\u7684\u4e86\uff0c\u867d\u6709\u51f6\u9669\uff0c\u4e5f\u53ea\u6709\u542c\u5929\u7531\u547d\uff0c\u800c\u4e14\u77a7\u4ed6\u4eec\u795e\u8272\u8bed\u6c14\uff0c\u4e5f\u4f3c\u5e76\u65e0\u6076\u610f\uff0c\u4fbf\u9053\uff0c\u201c\u4e09\u4f4d\u65e2\u7136\u5982\u6b64\u7f8e\u610f\uff0c\u6211\u548c\u5f20\u516c\u5b50\u540c\u4e0a\u5c71\u53bb\u4fbf\u662f\u3002\u201d\u8bf4\u7740\u5411\u5f20\u671d\u5510\u4f7f\u4e2a\u773c\u8272\uff0c\u793a\u610f\u4e0d\u53ef\u8fdd\u62d7\u3002 \u3000\u3000\u59d3\u9ec4\u7684\u9701\u7136\u8272\u559c\uff0c\u7b11\u9053\uff1a\u201c\u672c\u6765\u561b\uff0c\u6211\u60f3\u4f60\u4eec\u4e5f\u4e0d\u4f1a\u8fd9\u822c\u4e0d\u987e\u4e49\u6c14\u3002\u201d \u3000\u3000\u516d\u4eba\u7ed3\u4f34\u540c\u884c\uff0c\u4e00\u8def\u6253\u5c16\u4f4f\u5e97\uff0c\u90fd\u7531\u90a3\u59d3\u9ec4\u7684\u51fa\u5934\uff0c\u4ed6\u53ea\u505a\u51e0\u4e2a\u624b\u52bf\uff0c\u8bf4\u4e86\u51e0\u53e5\u53e4\u91cc\u53e4\u602a\u7684\u8bdd\uff0c\u6cbf\u9014\u996d\u9986\u5ba2\u5e97\u4fbf\u90fd\u4e0d\u6536\u94b1\uff0c\u800c\u4e14\u62db\u5f85\u5f97\u52a0\u610f\u7684\u5468\u5230\u5ba2\u6c14\u3002 \u3000\u3000\u8d70\u4e86\u4e24\u5929\uff0c\u5c06\u8fd1\u5723\u5cf0\u5d82\u5c71\u811a\uff0c\u53ea\u89c1\u6cbf\u9014\u52b2\u88c5\u7ed3\u675f\u4e4b\u4eba\u7edc\u7ece\u4e0d\u7edd\uff0c\u90fd\u662f\u5411\u5723\u5cf0\u5d82\u800c\u53bb\uff0c\u80a5\u7626\u9ad8\u77ee\uff0c\u5404\u8272\u5404\u6837\u7684\u4eba\u90fd\u6709\uff0c\u795e\u8272\u4e3e\u6b62\uff0c\u663e\u5f97\u90fd\u662f\u6b66\u4eba\u3002\u8fd9\u4e9b\u4eba\u4e0e\u59d3\u9ec4\u7684\u4ee5\u53ca\u5218\u6c0f\u5144\u5f1f\u5927\u534a\u719f\u8bc6\uff0c\u89c1\u4e86\u9762\u5c31\u6267\u624b\u9053\u6545\u3002 \u3000\u3000\u5f20\u6768\u4e24\u4eba\u62b1\u5b9a\u5b97\u65e8\u51b3\u4e0d\u518d\u7aa5\u63a2\u522b\u4eba\u9690\u79c1\uff0c\u89c1\u4ed6\u4eec\u8c08\u8bdd\uff0c\u5c31\u7ad9\u5f97\u8fdc\u8fdc\u7684\uff0c\u4f46\u542c\u8fd9\u4e9b\u4eba\u62db\u547c\u7684\u58f0\u97f3\u5357\u8154\u5317\u8c03\uff0c\u8fbd\u4e1c\u6cb3\u6714\u3001\u4e24\u6e56\u5ddd\u9655\u5404\u5730\u90fd\u6709\u3002\u77a7\u4ed6\u4eec\u7684\u884c\u88c5\u6253\u626e\uff0c\u5927\u90e8\u662f\u6765\u81ea\u8fdc\u5730\uff0c\u4eba\u4eba\u90fd\u662f\u98ce\u5c18\u4ec6\u4ec6\u3002\u5f20\u6768\u4e24\u4eba\u6697\u6697\u7eb3\u7f55\uff0c\u53c8\u662f\u6817\u6817\u5371\u60e7\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u5fc3\u60f3\uff1a\u201c\u770b\u6765\u8fd9\u4e9b\u4eba\u662f\u5404\u5730\u5c71\u5be8\u7684\u5927\u76d7\uff0c\u591a\u534a\u662f\u8981\u805a\u4f17\u9020\u53cd\u3002\u6211\u662f\u8eab\u5bb6\u6e05\u767d\u7684\u826f\u6c11\uff0c\u8ddf\u53cd\u8d3c\u4eec\u6df7\u5728\u4e00\u8d77\uff0c\u8d70\u53c8\u8d70\u4e0d\u8131\uff0c\u771f\u662f\u5012\u9709\u4e4b\u6781\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5929\u665a\u4e0a\uff0c\u5f20\u671d\u5510\u7b49\u6b47\u5728\u5723\u5cf0\u5d82\u5c71\u811a\u4e0b\u7684\u4e00\u6240\u5e97\u623f\u91cc\uff0c\u5f85\u6b21\u65e5\u4e00\u65e9\u4e0a\u5c71\u3002 \u3000\u3000\u4f17\u4eba\u6b63\u8981\u5403\u665a\u996d\uff0c\u5ffd\u7136\u4e00\u4eba\u5954\u8fdb\u5e97\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5b59\u76f8\u516c\u5230\u5566\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5e97\u4e2d\u5ba2\u4eba\u5341\u4e4b\u516b\u4e5d\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u6d8c\u51fa\u5e97\u53bb\u3002\u6768\u9e4f\u4e3e\u4e00\u626f\u5f20\u671d\u5510\u7684\u8863\u8896\uff0c\u8bf4\u9053\uff1a\u201c\u77a7\u77a7\u53bb\u3002\u201d \u3000\u3000\u8d70\u51fa\u5e97\u623f\uff0c\u53ea\u89c1\u4f17\u4eba\u5939\u9053\u5782\u624b\u8083\u7acb\uff0c\u4f3c\u5728\u7b49\u751a\u4e48\u4eba\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u897f\u9762\u5c71\u9053\u4e0a\u4f20\u6765\u4e00\u9635\u9a6c\u8e44\u58f0\uff0c\u4f17\u4eba\u90fd\u63d0\u9ad8\u4e86\u811a\u8ddf\u5f20\u671b\uff0c\u53ea\u89c1\u4e00\u4e2a\u56db\u5341\u6765\u5c81\u7684\u4e66\u751f\u9a91\u5728\u9a6c\u4e0a\uff0c\u7f13\u7f13\u800c\u6765\u3002\u4ed6\u89c1\u4f17\u4eba\u7ad9\u5728\u9053\u65c1\u8fce\u63a5\uff0c\u50ac\u9a6c\u5feb\u884c\uff0c\u9a70\u5230\u8ddf\u524d\uff0c\u8df3\u4e0b\u9a6c\u6765\u3002\u4eba\u7fa4\u4e2d\u4e00\u540d\u5927\u6c49\u62a2\u4e0a\u524d\u53bb\uff0c\u633d\u4f4f\u9a6c\u7f30\u3002 \u3000\u3000\u90a3\u4e66\u751f\u4e00\u8def\u8fc7\u6765\uff0c\u548c\u4f17\u4eba\u9010\u4e00\u70b9\u5934\u62db\u547c\u3002\u4ed6\u8d70\u5230\u5f20\u671d\u5510\u8ddf\u524d\uff0c\u89c1\u4ed6\u4e5f\u662f\u4e66\u751f\u6253\u626e\uff0c\u5fae\u5fae\u4e00\u6115\uff0c\u53cc\u624b\u4e00\u62f1\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8c01\uff1f\u201d\u5f20\u671d\u5510\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u5f20\uff0c\u8bf7\u6559\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\u3002\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u5b59\uff0c\u540d\u4ef2\u5bff\uff0c\u201d\u5f20\u671d\u5510\u62f1\u624b\u8bf4\u9053\uff1a\u201d\u4e45\u4ef0\uff0c\u4e45\u4ef0\u3002\u201d\u5b59\u4ef2\u5bff\u5fae\u5fae\u4e00\u7b11\uff0c\u8fdb\u5e97\u623f\u53bb\u4e86\u3002 \u3000\u3000\u665a\u996d\u8fc7\u540e\uff0c\u6768\u9e4f\u4e3e\u4f4e\u58f0\u5bf9\u5f20\u671d\u5510\u9053\uff1a\u201c\u8fd9\u59d3\u5b59\u7684\u4e66\u751f\u76f8\u516c\u663e\u662f\u5f88\u6709\u6743\u52bf\u3002 \u3000\u3000\u5f20\u516c\u5b50\uff0c\u4f60\u53bb\u8ddf\u4ed6\u8bf4\u8bf4\uff0c\u8bf7\u4ed6\u653e\u54b1\u4eec\u8d70\u3002\u5927\u5bb6\u662f\u8bfb\u4e66\u4eba\uff0c\u8bdd\u603b\u5bb9\u6613\u8bf4\u5f97\u901a\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u5fc3\u60f3\u4e0d\u9519\uff0c\u8e31\u5230\u5b59\u4ef2\u5bff\u95e8\u53e3\uff0c\u54b3\u55fd\u4e00\u58f0\uff0c\u4e3e\u624b\u6572\u95e8\u3002\u53ea\u542c\u5230\u623f\u91cc\u6709\u8bf5\u8bfb\u8bd7\u6587\u4e4b\u58f0\uff0c\u4ed6\u6572\u4e86\u51e0\u4e0b\uff0c\u8bfb\u4e66\u58f0\u5c31\u505c\u4e86\u3002 \u3000\u3000\u623f\u95e8\u6253\u5f00\uff0c\u5b59\u4ef2\u5bff\u8fce\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5ba2\u5e97\u5bc2\u5bde\uff0c\u5f20\u5144\u6765\u8c08\u8c08\uff0c\u6700\u597d\u4e0d\u8fc7\u3002\u201d\u5f20\u671d\u5510\u4e00\u63d6\u8fdb\u53bb\uff0c\u89c1\u684c\u4e0a\u653e\u7740\u4e00\u672c\u644a\u5f00\u624b\u6284\u4e66\u672c\uff0c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u89c1\u5199\u7740\u201c\u8fbd\u4e1c\u201d\u3001\u201c\u5b81\u8fdc\u201d\u3001\u201c\u81e3\u201d\u3001\u201c\u7687\u4e0a\u201d\u7b49\u7b49\u5b57\u6837\uff0c\u4f3c\u662f\u4e00\u7bc7\u594f\u7ae0\u3002\u5f20\u671d\u5510\u53ea\u6015\u53c8\u89e6\u4eba\u6240\u5fcc\uff0c\u4e0d\u6562\u591a\u770b\uff0c\u4fbf\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u5148\u8bf7\u95ee\u4ed6\u5bb6\u4e16\u6e0a\u6e90\uff0c\u5f20\u671d\u5510\u636e\u5b9e\u8bf4\u4e86\u3002\u5b59\u4ef2\u5bff\u8bf4\u9053\uff1a\u201c\u5f20\u5144\u8fd9\u756a\u53ef\u6765\u5f97\u4e0d\u5de7\u4e86\u3002\u4e2d\u534e\u671d\u653f\u7cdc\u70c2\uff0c\u4e0d\u77e5\u4f55\u65e5\u65b9\u5f97\u6e05\u660e\u3002\u4ee5\u5144\u5f1f\u4e4b\u89c1\uff0c\u5f20\u5144\u8fd8\u662f\u6682\u56de\u6d61\u6ce5\uff0c\u4fdf\u4e2d\u534e\u5723\u5929\u5b50\u5728\u4f4d\uff0c\u518d\u6765\u5e94\u8bd5\u7684\u4e3a\u662f\u3002\u201d\u5f20\u671d\u5510\u79f0\u662f\uff0c\u8bf4\u9053\u6b63\u8981\u5f52\u53bb\u3002\u63a5\u7740\u628a\u81ea\u5df1\u5982\u4f55\u8eb2\u907f\u5b98\u5dee\u3001\u6768\u9e4f\u4e3e\u5982\u4f55\u76f8\u6551\u3001\u5982\u4f55\u5f97\u5230\u7af9\u724c\u7b49\u4e8b\u8bf4\u4e86\u4e00\u904d\uff0c\u53ea\u662f\u591c\u4e2d\u89c1\u5230\u7bb1\u5185\u4eba\u5934\u4e00\u4e8b\u7565\u53bb\u4e0d\u63d0\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u6211\u4eec\u5728\u6b64\u76f8\u9047\uff0c\u53ef\u7b97\u6709\u7f18\u3002\u660e\u65e5\u5f20\u5144\u968f\u5c0f\u5f1f\u4e0a\u5c71\u3002\u4e5f\u597d\u77e5\u9053\u6211\u4e2d\u571f\u7684\u4e00\u4ef6\u5343\u53e4\u5947\u51a4\u3002\u53ea\u8981\u6b64\u884c\u6240\u89c1\u6240\u95fb\uff0c\u4e0d\u5411\u5916\u4eba\u6cc4\u9732\uff0c\u5c0f\u5f1f\u62c5\u4fdd\u5f20\u5144\u51b3\u65e0\u5371\u5bb3\u3002\u201d\u5f20\u671d\u5510\u8c22\u4e86\uff0c\u5374\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u95ee\u8d77\u6d61\u6ce5\u56fd\u4eba\u7684\u98ce\u571f\u4eba\u60c5\uff0c\u542c\u5f20\u671d\u5510\u6240\u8ff0\uff0c\u7686\u662f\u95fb\u6240\u672a\u95fb\uff0c\u559f\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u51e0\u65f6\u6211\u4e2d\u534e\u767e\u59d3\u624d\u5f97\u5982\u6d61\u6ce5\u56fd\u4e00\u822c\uff0c\u5b89\u5c45\u4e50\u4e1a\uff0c\u4e0d\u5fe7\u6e29\u9971\uff0c\u5171\u4eab\u592a\u5e73\u4e4b\u798f\uff1f\u201d \u3000\u3000\u4e24\u4eba\u76f4\u8c08\u5230\u4e8c\u66f4\u5929\u65f6\uff0c\u5f20\u671d\u5510\u624d\u544a\u522b\u56de\u623f\u3002\u6768\u9e4f\u4e3e\u5df2\u7b49\u5f97\u5341\u5206\u5fc3\u7126\uff0c\u542c\u4ed6\u8f6c\u544a\u4e86\u5b59\u4ef2\u5bff\u4e4b\u8a00\uff0c\u624d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u6b21\u65e5\u6b63\u662f\u4e2d\u79cb\u4f73\u8282\uff0c\u5f20\u671d\u5510\u3001\u6768\u9e4f\u4e3e\u548c\u5f20\u5eb7\u968f\u7740\u5927\u4f17\u4e00\u65e9\u4e0a\u5c71\u3002\u4e2d\u5348\u65f6\u5206\uff0c\u534a\u5c71\u91cc\u6709\u5341\u591a\u4eba\u62c5\u7740\u996d\u83dc\u7b49\u5019\uff0c\u90fd\u662f\u7d20\u83dc\uff0c\u4f17\u4eba\u5403\u4e86\uff0c\u4f11\u606f\u4e00\u9635\uff0c\u7ee7\u7eed\u518d\u884c\u3002 \u3000\u3000\u6b64\u540e\u4e00\u8def\u90fd\u6709\u4eba\u628a\u5b88\uff0c\u76d8\u67e5\u751a\u4e25\u3002\u67e5\u5230\u5f20\u6768\u4e09\u4eba\u65f6\uff0c\u5b59\u4ef2\u5bff\u70b9\u4e00\u70b9\u5934\uff0c\u628a\u5b88\u7684\u4eba\u4fbf\u4e0d\u95ee\u4e86\u3002\u5f20\u671d\u5510\u6697\u53eb\uff1a\u201c\u597d\u9669\uff01\u8981\u662f\u6628\u665a\u6ca1\u8ddf\u4ed6\u8fd9\u4e00\u5915\u8c08\u8bdd\uff0c\u4eca\u65e5\u662f\u6b7b\u662f\u6d3b\uff0c\u5b9e\u6240\u96be\u6599\u3002\u201d \u3000\u3000\u508d\u665a\u65f6\u5206\uff0c\u5df2\u5230\u5c71\u9876\uff0c\u6570\u767e\u540d\u6c49\u5b50\u6392\u961f\u76f8\u8fce\u3002\u4e2d\u95f4\u4e00\u4eba\u8eab\u6750\u9b41\u68a7\uff0c\u4f3c\u662f\u4f17\u4eba\u7684\u9996\u9886\u3002\u89c1\u5230\u5b59\u4ef2\u5bff\u4e0a\u6765\uff0c\u5feb\u6b65\u4e0b\u6765\u8fce\u63a5\uff0c\u643a\u624b\u8d70\u5165\u5c4b\u5185\u3002 \u3000\u3000\u5c71\u4e0a\u758f\u758f\u843d\u843d\u6709\u6570\u5341\u95f4\u623f\u5c4b\uff0c\u6700\u5927\u7684\u4e00\u5ea7\u4f3c\u662f\u4e00\u6240\u5bfa\u5e99\u3002\u8fd9\u4e9b\u5c4b\u5b87\u6a21\u6837\u4e5f\u751a\u5e73\u5e38\uff0c\u5e76\u65e0\u7889\u5821\u671b\u697c\u7b49\u5b88\u5fa1\u8bbe\u5907\uff0c\u5374\u53c8\u4e0d\u50cf\u662f\u76d7\u5e2e\u5c71\u5be8\u3002 \u3000\u3000\u6768\u9e4f\u4e3e\u5728\u5c71\u4e0a\u89c1\u4e86\u4f17\u4eba\u7684\u52bf\u6d3e\uff0c\u6599\u60f3\u5c71\u4e0a\u5efa\u6784\u5fc5\u5b9a\u96c4\u4f1f\u5a01\u6b66\uff0c\u58c1\u5792\u68ee\u4e25\uff0c\u54ea\u77e5\u6d51\u4e0d\u662f\u8fd9\u4e48\u4e00\u56de\u4e8b\uff0c\u5fc3\u4e0b\u6697\u6697\u79f0\u5947\u3002\u4ed6\u5728\u6c5f\u6e56\u4e0a\u6df7\u4e86\u5341\u591a\u5e74\uff0c\u89c1\u95fb\u7b97\u5f97\u5e7f\u535a\uff0c\u8fd9\u4e00\u6b21\u5374\u534a\u70b9\u6478\u4e0d\u7740\u5934\u8111\u3002\u66f4\u6709\u4e00\u4ef6\u5947\u4e8b\uff0c\u8fd9\u4e9b\u4eba\u4e07\u91cc\u6765\u4f1a\uff0c\u77a7\u5404\u4eba\u795e\u60c5\u4eb2\u5bc6\uff0c\u90fd\u662f\u77e5\u4ea4\u597d\u53cb\uff0c\u4f46\u76f8\u89c1\u65f6\u5374\u6b8a\u65e0\u6b22\u6109\u4e4b\u610f\uff0c\u6bcf\u4eba\u795e\u8272\u95f4\u90fd\u663e\u5f97\u5341\u5206\u60b2\u621a\u6124\u6168\u3002 \u3000\u3000\u5f20\u6768\u4e09\u4eba\u88ab\u5f15\u8fdb\u4e00\u95f4\u5c0f\u623f\uff0c\u4e00\u4f1a\u513f\u9001\u8fdb\u996d\u83dc\u3002\u56db\u76d8\u90fd\u662f\u7d20\u83dc\uff0c\u8fd8\u6709\u4e8c\u5341\u591a\u4e2a\u9992\u5934\u3002\u5f53\u665a\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u6084\u6084\u8bae\u8bba\uff0c\u731c\u4e0d\u900f\u8fd9\u4e9b\u4eba\u5230\u5e95\u5728\u5e72\u751a\u4e48\uff0c\u5bf9\u5b59\u4ef2\u5bff\u6240\u8bf4\u201c\u5343\u53e4\u5947\u51a4\u201d\u4e91\u4e91\uff0c\u66f4\u662f\u96be\u660e\u6240\u6307\u3002 \u3000\u3000\u6b21\u65e5\u5f20\u6768\u4e8c\u4eba\u8d77\u8eab\u540e\uff0c\u7528\u8fc7\u65e9\u70b9\uff0c\u5728\u5c71\u8fb9\u6f2b\u6b65\uff0c\u53ea\u89c1\u5230\u5904\u90fd\u662f\u5927\u6c49\u3002\u6709\u7684\u5934\u4e0a\u75a4\u75d5\u7d2f\u7d2f\uff0c\u6709\u7684\u65ad\u624b\u6298\u8db3\uff0c\u4e2a\u4e2a\u662f\u8eab\u7ecf\u767e\u6218\u3001\u9971\u5386\u98ce\u971c\u7684\u6a21\u6837\u3002\u5f20\u6768\u4e24\u4eba\u6015\u751f\u4e8b\u60f9\u7978\uff0c\u8d70\u4e86\u4e00\u4f1a\u5c31\u56de\u8fdb\u623f\u4e2d\uff0c\u4e00\u76f4\u4e0d\u518d\u51fa\u53bb\u3002\u8fd9\u5929\u6574\u65e5\u5403\u7684\u4ecd\u662f\u7d20\u83dc\u3002\u6768\u9e4f\u4e3e\u809a\u91cc\u6697\u9a82\uff1a\u201c\u4ed6\u5988\u7684\u8d3c\u5f3a\u76d7\u6b7b\u4e86\u8001\u8892\u5b97\uff0c\u53eb\u8001\u5b50\u5403\u8fd9\u822c\u5634\u91cc\u6de1\u51fa\u9e1f\u6765\u7684\u7d20\u83dc\u3002\u201d \u3000\u3000\u508d\u665a\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u949f\u58f0\u5621\u5621\u3002\u4e0d\u4e45\u4e00\u540d\u6c49\u5b50\u8d70\u8fdb\u623f\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5b59\u76f8\u516c\u8bf7\u4e24\u4f4d\u5230\u6bbf\u4e0a\u89c2\u793c\u3002\u201d\u5f20\u6768\u4e8c\u4eba\u8ddf\u4ed6\u51fa\u53bb\u3002\u5f20\u5eb7\u4e5f\u60f3\u8ddf\u53bb\uff0c\u90a3\u4eba\u624b\u4e00\u6446\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u65e9\u4e9b\u7761\u5427\u3002\u201d \u3000\u3000\u5f20\u6768\u4e8c\u4eba\u968f\u7740\u4ed6\u7ed5\u8fc7\u51e0\u95f4\u74e6\u5c4b\uff0c\u6765\u5230\u5bfa\u5e99\u8ddf\u524d\u3002\u5f20\u671d\u5510\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u4e00\u5757\u6a2a\u533e\u4e0a\u5199\u7740\u201c\u5fe0\u70c8\u7960\u201d\u4e09\u4e2a\u5927\u5b57\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u662f\u5ea7\u7960\u5802\uff0c\u4e0d\u77e5\u4f9b\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u968f\u7740\u90a3\u6c49\u5b50\u7a7f\u8fc7\u524d\u5802\u548c\u9662\u5b50\uff0c\u89c1\u4e24\u65c1\u9648\u5217\u7740\u5175\u5668\u67b6\u5b50\uff0c\u67b6\u4e0a\u5200\u67aa\u65a7\u94ba\u3001\u53c9\u77db\u621f\u97ad\uff0c\u5341\u516b\u822c\u5175\u5203\u4e00\u5e94\u4ff1\u5168\uff0c\u90fd\u64e6\u5f97\u96ea\u4eae\u8000\u773c\u3002 \u3000\u3000\u6765\u5230\u5927\u6bbf\uff0c\u4f46\u89c1\u6bbf\u4e0a\u9ed1\u538b\u538b\u7684\u5750\u6ee1\u4e86\u4eba\uff0c\u603b\u6709\u4e24\u4e09\u5343\u4e4b\u4f17\u3002\u5f20\u6768\u4e8c\u4eba\u6697\u6697\u5fc3\u60ca\uff0c\u539f\u6765\u8fd9\u8352\u5c71\u4e4b\u4e0a\uff0c\u7ade\u805a\u96c6\u4e86\u8fd9\u8bb8\u591a\u4eba\u3002 \u3000\u3000\u5f20\u671d\u5510\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u6bbf\u4e2d\u5851\u7740\u4e00\u5ea7\u795e\u50cf\uff0c\u672c\u671d\u6587\u5b98\u88c5\u675f\uff0c\u4f46\u5934\u6234\u91d1\u76d4\uff0c\u8eab\u7a7f\u7eef\u888d\uff0c\u5916\u52a0\u9ec4\u7f69\u7532\uff0c\u5de6\u624b\u6367\u7740\u4e00\u67c4\u5b9d\u5251\uff0c\u53f3\u624b\u624b\u6267\u4ee4\u65d7\u3002\u90a3\u795e\u50cf\u8138\u5bb9\u6e05\u766f\uff0c\u4e09\u7efa\u957f\u987b\uff0c\u72b6\u8c8c\u5a01\u4e25\uff0c\u8eab\u5b50\u5fae\u4fa7\uff0c\u76ee\u89c6\u8fdc\u65b9\uff0c\u7709\u68a2\u773c\u89d2\u4e4b\u95f4\uff0c\u4f3c\u4e4e\u5fae\u5e26\u5fe7\u6001\u3002\u795e\u50cf\u4e24\u4fa7\u4f9b\u7740\u4e24\u6392\u7075\u4f4d\u3002\u5f20\u671d\u5510\u9694\u5f97\u8fdc\u4e86\uff0c\u770b\u4e0d\u6e05\u695a\u795e\u4e3b\u4e0a\u6240\u4e66\u7684\u540d\u8bb3\u3002\u5927\u6bbf\u56db\u58c1\u6302\u6ee1\u4e86\u65cc\u65d7\u3001\u76d4\u7532\u3001\u5175\u5203\u3001\u9a6c\u5177\u4e4b\u7c7b\uff0c\u65cc\u65d7\u6216\u7ea2\u6216\u84dd\uff0c\u4e5f\u6709\u9ec4\u8272\u9576\u7ea2\u8fb9\uff0c\u6709\u7684\u662f\u767d\u8272\u9576\u7ea2\u8fb9\u3002 \u3000\u3000\u5f20\u671d\u5510\u6ee1\u8179\u72d0\u7591\uff0c\u4f46\u89c1\u6ee1\u6bbf\u4eba\u4f17\u5bb9\u8272\u60b2\u621a\uff0c\u8083\u9759\u65e0\u58f0\u3002\u5ffd\u7136\u795e\u50cf\u65c1\u4e00\u4e2a\u8eab\u6750\u7626\u957f\u7684\u6c49\u5b50\u7ad9\u4e86\u8d77\u6765\uff0c\u70b9\u70db\u6267\u9999\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u81f4\u796d\u3002\u201d\u6bbf\u4e0a\u767b\u65f6\u9ed1\u538b\u538b\u7684\u8dea\u5f97\u6ee1\u5730\uff0c\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u4e5f\u53ea\u5f97\u8ddf\u7740\u8dea\u4e0b\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u8d8a\u4f17\u800c\u524d\uff0c\u6367\u4f4f\u796d\u6587\u6717\u8bf5\u8d77\u6765\u3002\u6768\u9e4f\u4e3e\u4e0d\u61c2\u796d\u6587\u4e2d\u6587\u7ec9\u7ec9\u7684\u8bf4\u4e9b\u751a\u4e48\uff0c\u5f20\u671d\u5510\u5374\u6108\u542c\u6108\u60ca\u3002 \u3000\u3000\u53ea\u542c\u5f97\u796d\u6587\u6587\u610f\u751a\u662f\u6124\u6168\u6fc0\u6602\uff0c\u65e2\u628a\u6ee1\u6e05\u9791\u5b50\u9a82\u4e86\u4e2a\u72d7\u8840\u6dcb\u4e49\uff0c\u9762\u5bf9\u5f53\u4eca\u5d07\u796f\u7687\u5e1d\u7ade\u4e5f\u4e1d\u6beb\u4e0d\u7528\u60c5\u9762\uff0c\u8bf4\u4ed6\u201c\u660f\u5eb8\u65e0\u9053\uff0c\u4e0d\u8fa8\u5fe0\u5978\u201d\u3001\u201d\u521a\u610e\u81ea\u7528\uff0c\u4f24\u6211\u5143\u620d\u201d\u3001\u201c\u81ea\u574f\u795e\u5dde\u4e07\u91cc\u4e4b\u957f\u57ce\uff0c\u7518\u4e3a\u9ec4\u5e1d\u82d7\u88d4\u4e4b\u7f6a\u4eba\u201d\uff0c\u5bf9\u5f53\u4eca\u7687\u4e0a\u5982\u6b64\u8086\u53e3\u75db\u8bcb\uff0c\u5c82\u4e0d\u662f\u516c\u7136\u8981\u9020\u53cd\u4e86\u5417\uff1f\u5f20\u671d\u5510\u542c\u5f97\u60ca\u7591\u4e0d\u5b9a\u3002\u54ea\u77e5\u796d\u6587\u540e\u9762\u6108\u6765\u6108\u51f6\uff0c\u7adf\u628a\u5d07\u796f\u7687\u5e1d\u7684\u5217\u7956\u5217\u5b97\u4e5f\u9a82\u4e86\u4e2a\u75db\u5feb\uff0c\u751a\u4e48\u201c\u529f\u52cb\u76d6\u4e16\u800c\u9b4f\u516c\u88ab\u6bd2\uff0c\u5e95\u5b9a\u4e2d\u571f\u800c\u9752\u7530\u53d7\u9e29\u201d\uff0c\u90a3\u662f\u8bf4\u660e\u592a\u7956\u6740\u5bb3\u5f90\u8fbe\u3001\u84dd\u7389\u3001\u5218\u57fa\u7b49\u529f\u81e3\u4e4b\u4e8b\uff1b\u540e\u6765\u53c8\u9a82\u795e\u5b97\u4e71\u5f81\u77ff\u7a0e\uff0c\u8336\u6bd2\u767e\u59d3\uff1a\u71b9\u5b97\u4efb\u7528\u5944\u73f0\uff0c\u671d\u4e2d\u8bf7\u6d41\u541b\u5b50\uff0c\u4e0d\u662f\u6740\u5934\uff0c\u4fbf\u662f\u5165\u72f1\uff0c\u5982\u718a\u5ef7\u5f3c\u7b49\u5b88\u571f\u6297\u654c\u5927\u81e3\uff0c\u90fd\u60e8\u906d\u6740\u5bb3\u3002 \u3000\u3000\u8fd9\u7bc7\u796d\u6587\u7406\u76f4\u6c14\u58ee\uff0c\u4e00\u5b57\u4e00\u53e5\u90fd\u6253\u5165\u5f20\u671d\u5510\u5fc3\u574e\u91cc\u53bb\uff0c\u4ed6\u867d\u8fdc\u5728\u5916\u56fd\uff0c\u4f46\u4e2d\u571f\u5927\u4e8b\uff0c\u5374\u4e5f\u77e5\u95fb\u3002\u796d\u6587\u540e\u534a\u6bb5\u5374\u662f\u201c\u6211\u7763\u5e08\u5a01\u9707\u5b81\u8fdc\uff0c\u6b7c\u5f7c\u5de8\u914b\u201d\u7b49\u4e00\u5927\u6bb5\u9882\u626c\u6b66\u529f\u7684\u6587\u5b57\uff0c\u66f4\u540e\u6765\u53c8\u518d\u75db\u9a82\u5d07\u796f\u6740\u5bb3\u5fe0\u826f\u3002 \u3000\u3000\u5f20\u671d\u5510\u542c\u5230\u8fd9\u91cc\uff0c\u624d\u77e5\u9053\u8fd9\u795e\u50cf\u539f\u6765\u662f\u8fde\u7834\u6e05\u5175\u3001\u51fb\u6bd9\u6e05\u592a\u7956\u52aa\u5c14\u54c8\u8d64\u3001\u4f7f\u6e05\u4eba\u95fb\u540d\u4e27\u80c6\u7684\u84df\u8fbd\u7763\u5e08\u8881\u5d07\u7115\u3002\u4ed6\u62ac\u5934\u518d\u770b\uff0c\u89c1\u90a3\u4f38\u50cf\u6829\u6829\u5982\u751f\uff0c\u53cc\u76ee\u8fdc\u77a9\uff0c\u4f3c\u662f\u75db\u60dc\u5f02\u65cf\u5165\u4fb5\uff0c\u5360\u6211\u6cb3\u5c71\uff0c\u4f24\u6211\u9ece\u6c11\uff0c\u6068\u4e0d\u80fd\u590d\u751f\u800c\u7763\u5e08\u8fbd\u4e1c\uff0c\u4ee5\u5fa1\u5916\u4fae\u3002 \u3000\u3000\u8fd9\u65f6\u796d\u6587\u884c\u5c06\u8bfb\u5b8c\uff0c\u5f20\u671d\u5510\u5374\u542c\u5f97\u66f4\u52a0\u5fc3\u60ca\uff0c\u539f\u6765\u796d\u6587\u6700\u540e\u4e00\u6bb5\u662f\u4e0e\u796d\u5404\u4eba\u7684\u8a93\u8a00\uff0c\u7acb\u8a93\uff1a\u201c\u5e76\u8bdb\u660e\u5e1d\u6e05\u914b\u3001\u4ee5\u96ea\u6b64\u5343\u53e4\u5947\u51a4\uff0c\u800c\u6170\u6211\u7763\u5e08\u5728\u5929\u4e4b\u7075\u3002\u201d\u796d\u6587\u8bfb\u6bd5\u3001\u8d5e\u793c\u7684\u4eba\u5531\u9053\uff1a\u201c\u5bf9\u7763\u5e08\u795e\u50cf\u66a8\u5217\u4f4d\u6b89\u96be\u5c06\u519b\u795e\u4e3b\u53e9\u9996\u3002\u201d \u3000\u3000\u4f17\u4eba\u4fef\u8eab\u53e9\u5934\u3002 \u3000\u3000\u4e00\u4e2a\u5e7c\u7ae5\u5168\u8eab\u7f1f\u7d20\uff0c\u7ad9\u5728\u524d\u5217\uff0c\u8f6c\u8eab\u4f0f\u5728\u5730\u4e0b\u5411\u4f17\u4eba\u8fd8\u793c\u3002\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u8fd9\u5e7c\u7ae5\u4fbf\u662f\u90a3\u5929\u6240\u9047\u7684\u6740\u864e\u7267\u7ae5\u3002\u4f17\u4eba\u53e9\u62dc\u5df2\u6bd5\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u90fd\u662f\u6cea\u75d5\u6ee1\u9762\uff0c\u60b2\u6124\u96be\u7981\uff0c\u5b59\u4ef2\u5bff\u5bf9\u5f20\u671d\u5510\u9053\uff1a\u201c\u5f20\u5144\u5927\u624d\uff0c\u5c0f\u5f1f\u8fd9\u7bc7\u796d\u6587\u6709\u4f55\u4e0d\u59a5\u4e4b\u5904\uff0c\u8bf7\u4e88\u5220\u524a\u3002\u201d\u5f20\u671d\u5510\u8fde\u79f0\uff1a\u201c\u4e0d\u6562\u3002\u201d\u5b59\u4ef2\u5bff\u547d\u4eba\u62ff\u8fc7\u6587\u623f\u56db\u5b9d\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u5c0f\u5f1f\u9080\u5f20\u5144\u4e0a\u5c71\uff0c\u4fbf\u662f\u8981\u501f\u91cd\u6d77\u5916\u624d\u5b50\u624b\u7b14\uff0c\u4e8e\u6211\u8881\u7763\u5e08\u7684\u52cb\u4e1a\u66f4\u589e\u5149\u534e\u3002\u4e5f\u597d\u6559\u4e16\u4eba\u77e5\u9053\uff0c\u8881\u7763\u5e08\u8499\u51a4\u906d\u96be\uff0c\u666e\u5929\u5171\u6124\uff0c\u4e2d\u5916\u540c\u60b2\uff0c\u5e76\u975e\u53ea\u662f\u6211\u4eec\u65e7\u90e8\u7684\u4e00\u756a\u79c1\u5fc3\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u5fc3\u60f3\uff0c\u4f60\u53eb\u6211\u4e0a\u5c71\uff0c\u539f\u6765\u4e3a\u6b64\uff0c\u4e0d\u7531\u5f97\u597d\u751f\u4e3a\u96be\u3002\u8881\u5d07\u7115\u7834\u671d\u5ef7\u5904\u6b7b\uff0c\u662f\u56e0\u5d07\u796f\u80e1\u6d82\u660f\u5eb8\uff0c\u4e0d\u660e\u5fe0\u5978\u662f\u975e\uff0c\u542c\u4fe1\u4e86\u5978\u81e3\u548c\u592a\u76d1\u7684\u6311\u62e8\uff0c\u5929\u4e0b\u90fd\u77e5\u51a4\u6789\uff0c\u81ea\u5df1\u5728\u6d61\u6ce5\u4e4b\u65f6\uff0c\u4e5f\u66fe\u542c\u5f97\u51e0\u4e2a\u5e7f\u4e1c\u5546\u4eba\u75db\u54ed\u6d41\u6d95\u7684\u8bf4\u8d77\u8fc7\u3002\u4f46\u65e2\u7531\u7687\u5e1d\u4e0b\u65e8\u800c\u660e\u6b63\u5178\u5211\uff0c\u518d\u8bf4\u51a4\u6789\uff0c\u4fbf\u662f\u8bfd\u8c24\u4eca\u4e0a\u3002\u7687\u5e1d\u82e6\u662f\u77e5\u9053\u4e86\uff0c\u4e00\u7eb8\u8bcf\u4e66\u6765\u5230\u6d61\u6ce5\u56fd\uff0c\u8fde\u7236\u4eb2\u90e8\u4e0d\u514d\u5927\u53d7\u7275\u7d2f\u3002\u53ef\u662f\u5b59\u4ef2\u5bff\u65e2\u8fd9\u4e48\u8bf4\uff0c\u5728\u52bf\u53c8\u4e0d\u80fd\u62d2\u7edd\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u5ffd\u7136\u7075\u673a\u4e00\u52a8\uff0c\u60f3\u8d77\u5728\u6d61\u6ce5\u56fd\u65f6\u6240\u770b\u8fc7\u7684\u4e24\u90e8\u5c0f\u8bf4\uff0c\u4e00\u90e8\u662f\u300a\u4e09\u56fd\u6f14\u4e49\u300b\uff0c\u4e00\u90e8\u662f\u300a\u7cbe\u5fe0\u5cb3\u4f20\u300b\u3002\u4ed6\u8bfb\u4e66\u6709\u9650\uff0c\u4e0d\u80fd\u5982\u5b59\u4ef2\u5bff\u90a3\u4e48\u9a88\u56db\u9a8a\u516d\u7684\u5927\u505a\u6587\u7ae0\uff0c\u5f53\u4e0b\u5fae\u4e00\u6c89\u541f\uff0c\u632f\u7b14\u76f4\u4e66\uff1a\u201c\u9ec4\u9f99\u672a\u6363\u3002\u6b66\u7a46\u8499\u51a4\u3002 \u3000\u3000\u6c49\u795a\u5f85\u590d\uff0c\u8bf8\u845b\u661f\u6b92\u3002\u545c\u547c\u75db\u54c9\uff0c\u4f0f\u7ef4\u5c1a\u98e8\u3002\u201d\u4ed6\u8bf4\u7684\u662f\u53e4\u4eba\uff0c\u4e07\u4e00\u8fd9\u7bc7\u77ed\u77ed\u7684\u796d\u6587\u843d\u5165\u7687\u5e1d\u624b\u4e2d\uff0c\u4e5f\u4e0d\u80fd\u636e\u6b64\u800c\u5b9a\u7f6a\u540d\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u672c\u60f3\u4ed6\u662f\u4e00\u4e2a\u6d77\u5916\u58eb\u4eba\uff0c\u6ca1\u751a\u4e48\u5b66\u95ee\uff0c\u4e5f\u5199\u4e0d\u51fa\u751a\u4e48\u597d\u53e5\u5b50\u6765\uff0c\u53ea\u76fc\u4ed6\u79f0\u8d5e\u51e0\u53e5\u8881\u7763\u5e08\u7684\u529f\u7ee9\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u5f85\u89c1\u4ed6\u5199\u4e0b\u4e86\u8fd9\u516d\u53e5\uff0c\u5341\u5206\u9ad8\u5174\u3002 \u3000\u3000\u5f20\u671d\u5510\u628a\u8881\u5d07\u7115\u6bd4\u4e4b\u4e8e\u8bf8\u845b\u4eae\u548c\u5cb3\u98de\uff0c\u81ea\u662f\u63a8\u5d07\u5907\u81f3\uff0c\u65e0\u4ee5\u590d\u52a0\u3002\u6e05\u4eba\u4e3a\u91d1\u4eba\u540e\u88d4\uff0c\u7686\u4e3a\u5973\u771f\u65cf\uff0c\u6ee1\u6e05\u521d\u7acb\u56fd\u65f6\uff0c\u56fd\u53f7\u4fbf\u4ecd\u79f0\u4e3a\u201c\u91d1\u201d\u3002\u5cb3\u98de\u4e0e\u8881\u5d07\u7115\u7686\u6297\u91d1\u6709\u529f\u800c\u6b7b\u4e8e\u660f\u541b\u5978\u81e3\u4e4b\u624b\uff0c\u4e24\u4eba\u624d\u7565\u906d\u9047\uff0c\u9887\u6709\u76f8\u540c\u4e4b\u5904\uff0c\u5012\u4e0d\u662f\u80e1\u4e71\u778e\u6bd4\u7684\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u628a\u8fd9\u51e0\u53e5\u8bdd\u5411\u4f17\u4eba\u89e3\u91ca\u4e86\uff0c\u5927\u5bb6\u8f70\u7136\u81f4\u8c22\uff0c\u5bf9\u5f20\u6768\u4e24\u4eba\u795e\u6001\u767b\u65f6\u4fbf\u4eb2\u70ed\u5f97\u591a\uff0c\u4e0d\u518d\u4ee5\u5916\u4eba\u76f8\u5f85\u4e86\u3002\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u5f20\u5144\u6587\u7b14\u4e0d\u51e1\uff0c\u6b66\u7a46\u8bf8\u845b\u8fd9\u4e24\u53e5\u8bdd\uff0c\u8363\u5ba0\u4e5d\u6cc9\u3002\u5c0f\u5f1f\u5f85\u4f1a\u53eb\u4ed6\u4eec\u523b\u5728\u7960\u5802\u65c1\u8fb9\u7684\u77f3\u4e0a\uff0c\u8981\u4ee4\u540e\u4eba\u5f97\u77e5\uff0c\u6211\u4eec\u8881\u7763\u5e08\u82f1\u540d\u8fdc\u64ad\uff0c\u8fde\u4e07\u91cc\u4e4b\u5916\u7684\u5f02\u90a6\u58eb\u6c11\u4e5f\u5c3d\u7686\u4ef0\u6155\u3002\u201d\u5f20\u671d\u5510\u4f5c\u63d6\u900a\u8c22\u3002 \u3000\u3000\u5404\u4eba\u53e9\u62dc\u5df2\u6bd5\uff0c\u5404\u5c31\u539f\u4f4d\u5750\u4e0b\u3002\u90a3\u8d5e\u793c\u7684\u4eba\u53c8\u558a\u4e86\u8d77\u6765\uff1a\u201c\u67d0\u67d0\u8425\u67d0\u5c06\u519b\u201d\u3001\u201c\u67d0\u67d0\u9547\u67d0\u603b\u5175\u201d\uff0c\u558a\u4e86\u4e00\u4e2a\u6b66\u5c06\u5b98\u8854\uff0c\u4fbf\u6709\u4e00\u4eba\u7ad9\u8d77\u6765\u5927\u58f0\u8bf4\u8bdd\u3002 \u3000\u3000\u5f20\u671d\u5510\u542c\u4e86\u5b98\u8854\u548c\u8a00\u4e2d\u4e4b\u610f\uff0c\u5f97\u77e5\u8fd9\u4e9b\u4eba\u90fd\u662f\u8881\u5d07\u7115\u7684\u65e7\u90e8\uff0c\u4ed6\u88ab\u5bb3\u4e4b\u540e\uff0c\u5404\u4eba\u6124\u800c\u79bb\u519b\uff0c\u6563\u5904\u56db\u65b9\uff0c\u4eca\u65e5\u662f\u8881\u7763\u5e08\u906d\u96be\u7684\u4e09\u5468\u5e74\u5fcc\u8fb0\u3002\u662f\u4ee5\u5728\u4ed6\u6545\u4e61\u5e7f\u4e1c\u4e1c\u839e\u9644\u8fd1\u7684\u5723\u5cf0\u5d82\u76f8\u805a\uff0c\u796d\u5960\u65e7\u4e3b\u3002\u542c\u4ed6\u4eec\u8bdd\u4e2d\u4e4b\u610f\uff0c\u4f3c\u4e4e\u5c1a\u6709\u751a\u4e48\u91cd\u5927\u56fe\u8c0b\u3002 \u3000\u3000\u5f53\u8d5e\u793c\u4eba\u53eb\u5230\u201c\u84df\u9547\u526f\u603b\u5175\u6731\u5b89\u56fd\u201d\u65f6\uff0c\u4e00\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u90e8\u5fc3\u5934\u4e00\u9707\uff0c\u539f\u6765\u8fd9\u4eba\u4fbf\u662f\u5f15\u5bfc\u4ed6\u4eec\u8eb2\u5165\u5bc6\u5ba4\u7684\u90a3\u4e2a\u519c\u592b\u3002\u6768\u9e4f\u4e3e\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4ed6\u662f\u6297\u6e05\u7684\u84df\u8fbd\u5927\u5c06\uff0c\u90a3\u4e48\u6211\u8d25\u5728\u4ed6\u624b\u91cc\uff0c\u4e5f\u4e0d\u5728\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u4ed6\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8881\u516c\u5b50\u8fd9\u4e09\u5e74\u6765\u8eab\u5b50\u58ee\u5065\uff0c\u6b66\u827a\u5927\u6709\u8fdb\u6b65\uff0c\u4e66\u4e5f\u8bfb\u4e86\u4e0d\u5c11\uff0c\u6211\u548c\u502a\u3001\u7f57\u4e24\u4f4d\u5144\u5f1f\u7684\u6b66\u529f\u90fd\u5df2\u4f20\u7ed9\u4e86\u4ed6\uff0c\u8bf7\u5404\u4f4d\u53e6\u63a8\u660e\u5e08\u3002\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u54b1\u4eec\u5144\u5f1f\u4e2d\uff0c\u8fd8\u6709\u8c01\u6b66\u529f\u66f4\u9ad8\u5f97\u8fc7\u4f60\u4eec\u4e09\u4f4d\u7684\uff0c\u6731\u5c06\u519b\u4e0d\u5fc5\u592a\u8c26\u3002\u201d \u3000\u3000\u6731\u5b89\u56fd\u9053\uff0c\u201c\u8881\u516c\u5b50\u5b66\u6b66\u806a\u660e\u5f97\u5f88\uff0c\u6211\u4eec\u53ea\u7a0d\u52a0\u70b9\u62e8\uff0c\u4ed6\u9a6c\u4e0a\u5c31\u4f1a\u4e86\u3002\u6211\u4eec\u4e09\u4e2a\u5df2\u7ecf\u503e\u56ca\u4ee5\u6388\uff0c\u7684\u786e\u8981\u53e6\u8bf7\u540d\u5e08\uff0c\u4ee5\u514d\u803d\u8bef\u4ed6\u529f\u592b\u3002\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u597d\u5427\uff0c\u8fd9\u4e8b\u5f85\u4f1a\u518d\u8bae\u3002\u8bdb\u5978\u7684\u4e8b\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u90a3\u59d3\u502a\u7684\u6740\u864e\u82f1\u96c4\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u59d3\u8303\u7684\u5978\u8d3c\u662f\u7f57\u53c2\u5c06\u524d\u4e2a\u6708\u8d76\u5230\u6d59\u6c5f\u8bdb\u706d\u7684\u3002\u59d3\u53f2\u7684\u5978\u8d3c\uff0c\u5341\u5929\u524d\u88ab\u6211\u5728\u6f6e\u5dde\u8ffd\u5230\u3002\u4e24\u4eba\u7684\u9996\u7ea7\u5728\u6b64\u3002\u201d \u3000\u3000\u8bf4\u7f62\u4ece\u5730\u4e0a\u63d0\u8d77\u5e03\u56ca\uff1b\u53d6\u51fa\u4e24\u4e2a\u4eba\u5934\u6765\u3002 \u3000\u3000\u4f17\u4eba\u6709\u7684\u8f70\u7136\u53eb\u597d\uff0c\u6709\u7684\u5207\u9f7f\u75db\u9a82\u3002\u5b59\u4ef2\u5bff\u63a5\u8fc7\u4eba\u5934\uff0c\u4f9b\u5728\u795e\u50cf\u684c\u4e0a\u3002 \u3000\u3000\u5f20\u671d\u5510\u8fd9\u624d\u660e\u767d\uff0c\u4ed6\u4eec\u534a\u591c\u91cc\u5728\u7bb1\u4e2d\u53d1\u73b0\u7684\u4eba\u5934\uff0c\u539f\u6765\u662f\u8881\u515a\u7684\u4ec7\u4eba\uff0c\u90a3\u5b9a\u662f\u4e0e\u9677\u5bb3\u8881\u5d07\u7115\u4e00\u6848\u6709\u5173\u7684\u5978\u4eba\u4e86\u3002\u8fd9\u65f6\u4e0d\u65ad\u6709\u4eba\u51fa\u6765\u5448\u732e\u9996\u7ea7\uff0c\u4e00\u65f6\u95f4\u795e\u50cf\u524d\u7684\u4f9b\u684c\u4e0a\u6446\u4e86\u5341\u591a\u4e2a\u4eba\u5934\u3002\u542c\u8fd9\u4e9b\u4eba\u7684\u7980\u62a5\uff0c\u4eba\u5934\u4e2d\u6709\u4e00\u4e2a\u662f\u5f53\u671d\u59d3\u9ad8\u7684\u5fa1\u53f2\uff0c\u4ed6\u662f\u9b4f\u5fe0\u8d24\u7684\u515a\u7fbd\uff0c\u66fe\u8bec\u594f\u8881\u5d07\u7115\u901a\u654c\u5356\u56fd\uff0c\u4f17\u4eba\u5bf9\u4ed6\u6124\u6068\u5c24\u6df1\u3002 \u3000\u3000\u5404\u4eba\u7980\u544a\u5b8c\u6bd5\uff0c\u5b59\u4ef2\u5bff\u8bf4\u9053\uff1a\u201c\u5c0f\u597d\u8bdb\u4e86\u4e0d\u5c11\uff0c\u5927\u4ec7\u5374\u5c1a\u672a\u5f97\u62a5\uff0c\u9791\u5b50\u7687\u592a\u6781\u548c\u660f\u541b\u5d07\u796f\u4ecd\u7136\u5728\u4f4d\u3002\u5982\u4f55\u4e3a\u5927\u5143\u5e05\u62a5\u4ec7\u96ea\u6068\uff0c\u5404\u4f4d\u6709\u751a\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u77ee\u5b50\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5b59\u76f8\u516c\uff01\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u8d75\u53c2\u5c06\u6709\u751a\u4e48\u8bdd\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u90a3\u77ee\u5b50\u8bf4\u9053\uff1a\u201c\u4f9d\u6211\u8bf4\u521a\u8bf4\u4e86\u4e09\u4e2a\u5b57\uff0c\u95e8\u5916\u4e00\u540d\u6c49\u5b50\u5306\u5306\u8fdb\u6765\u7980\u9053\uff1a\u201c\u674e\u95ef\u5c06\u519b\u6d3e\u4e86\u4eba\u6765\u6c42\u89c1\u3002\u201d\u4f17\u4eba\u4e00\u542c\uff0c\u90fd\u8f70\u53eb\u8d77\u6765\u3002\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u8d75\u53c2\u5c06\uff0c\u54b1\u4eec\u5148\u8fce\u63a5\u95ef\u519b\u7684\u4f7f\u8005\u3002\u201d\u8d75\u53c2\u5c06\u9053\uff1a\u201c\u5bf9\u3002\u201d\u9996\u5148\u62a2\u4e86\u51fa\u53bb\uff0c\u4f17\u4eba\u90fd\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u5927\u95e8\u5f00\u5904\uff0c\u4e24\u6761\u5927\u6c49\u624b\u6267\u706b\u628a\uff0c\u5f80\u65c1\u8fb9\u4e00\u7ad9\uff0c\u8d70\u8fdb\u4e09\u4e2a\u4eba\u6765\u3002\u6768\u9e4f\u4e3e\u5df2\u4e45\u95fb\u674e\u95ef\u7684\u540d\u5934\uff0c\u77e5\u4ed6\u540d\u53eb\u674e\u81ea\u6210\uff0c\u8fd9\u51e0\u5e74\u6765\u6740\u5b98\u9020\u53cd\uff0c\u5a01\u52bf\u6781\u5927\uff0c\u5012\u8981\u770b\u770b\u4ed6\u90e8\u4e0b\u662f\u4f55\u7b49\u82f1\u96c4\u4eba\u7269\u3002 \u3000\u3000\u53ea\u89c1\u5f53\u5148\u4e00\u4eba\u56db\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u6ee1\u8138\u9ebb\u76ae\uff0c\u5934\u53d1\u84ec\u677e\uff0c\u8eab\u4e0a\u7a7f\u4e00\u5957\u7c97\u5e03\u886b\u88e4\uff0c\u819d\u76d6\u624b\u8098\u5904\u90fd\u5df2\u64e6\u574f\uff0c\u5230\u5904\u6253\u6ee1\u4e86\u8865\u9489\uff0c\u811a\u4e0b\u8d64\u8db3\uff0c\u7a7f\u4e00\u53cc\u8349\u978b\uff0c\u817f\u4e0a\u6ee1\u662f\u6ce5\u6c61\uff0c\u7eaf\u662f\u4e2a\u5e84\u7a3c\u6c49\u6a21\u6837\u3002\u4ed6\u8eab\u540e\u8ddf\u7740\u4e24\u4eba\uff0c\u4e00\u4e2a\u4e09\u5341\u591a\u5c81\uff0c\u76ae\u80a4\u767d\u51c0\uff0c\u53e6\u4e00\u4e2a\u5eff\u591a\u5c81\uff0c\u8eab\u6750\u9b41\u68a7\uff0c\u9762\u5bb9\u9edd\u9ed1\uff0c\u4e5f\u662f\u519c\u592b\u6a21\u6837\u3002\u8fd9\u4e09\u4eba\u770b\u4e0a\u53bb\u5fe0\u539a\u8001\u5b9e\u3002\u600e\u77e5\u4ed6\u4eec\u7adf\u662f\u6a2a\u884c\u79e6\u664b\u7684\u201c\u6d41\u5bc7\u201d\u3002 \u3000\u3000\u5f53\u5148\u90a3\u4eba\u8d70\u8fdb\u5927\u6bbf\uff0c\u5148\u4e0d\u8bf4\u8bdd\uff0c\u5f80\u795e\u50cf\u524d\u4e00\u7ad9\uff0c\u90a3\u767d\u8138\u6c49\u5b50\u4ece\u80cc\u540e\u5305\u88b1\u4e2d\u53d6\u51fa\u9999\u70db\uff0c\u5728\u795e\u50cf\u524d\u70b9\u4e0a\uff0c\u4e09\u4eba\u62dc\u5012\u5728\u5730\u3002\u78d5\u8d77\u5934\u6765\u3002\u90a3\u5c0f\u7267\u7ae5\u5728\u4f9b\u684c\u524d\u8dea\u4e0b\u78d5\u5934\u8fd8\u793c\u3002 \u3000\u3000\u4e09\u4eba\u62dc\u6bd5\uff0c\u8138\u6709\u9ebb\u5b50\u7684\u6c49\u5b50\u6717\u58f0\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u674e\u5c06\u519b\u77e5\u9053\u8881\u7763\u5e08\u5728\u5173\u5916\u6253\u9791\u5b50\uff0c\u7acb\u4e86\u5927\u529f\uff0c\u5fc3\u91cc\u5f88\u662f\u4f69\u670d\u3002\u540e\u6765\u8881\u7763\u5e08\u88ab\u7687\u5e1d\u51a4\u6789\u5bb3\u6b7b\uff0c\u5929\u4e0b\u8001\u767e\u59d3\u90fd\u6c14\u6124\u5f97\u5f88\u3002\u674e\u5c06\u519b\u6d3e\u6211\u4eec\u6765\u4ee3\u4ed6\u5411\u7763\u5e05\u7684\u795e\u4f4d\u78d5\u5934\u3002\u73b0\u4eca\u5b98\u903c\u6c11\u53cd\uff0c\u6211\u4eec\u4e3a\u4e86\u8981\u5403\u996d\uff0c\u53ea\u597d\u6297\u7cae\u6740\u5b98\u3002\u6c42\u8881\u5927\u5143\u5e05\u82f1\u9b42\u4fdd\u4f51.\u6211\u4eec\u6253\u5230\u5317\u4eac\uff0c\u6349\u4f4f\u7687\u5e1d\u5978\u81e3\uff0c\u4e00\u4e2a\u4e2a\u6740\u4e86\uff0c\u7ed9\u5927\u5143\u5e05\u548c\u5929\u4e0b\u7684\u8001\u767e\u59d3\u62a5\u4ec7\u3002\u201d\u8bf4\u5b8c\u53c8\u62dc\u4e86\u51e0\u62dc\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u674e\u81ea\u6210\u7684\u4f7f\u8005\u5c0a\u91cd\u4ed6\u4eec\u7763\u5e05\uff0c\u90fd\u5fc3\u5b58\u597d\u611f\uff0c\u542c\u4e86\u4ed6\u8fd9\u756a\u8bdd\uff0c\u867d\u7136\u8bed\u6c14\u7c97\u964b\uff0c\u5374\u662f\u81f3\u8bda\u4e4b\u8a00\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u4e0a\u524d\u4f5c\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\uff0c\u591a\u8c22\u3002\u8bf7\u6559\u9ad8\u59d3\u5927\u540d\u3002\u201d\u90a3\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u5218\u82b3\u4eae\u3002\u674e\u5c06\u519b\u5f97\u77e5\u4eca\u65e5\u662f\u8881\u5927\u5143\u5e05\u5fcc\u8fb0\uff0c\u56e0\u6b64\u6d3e\u6211\u524d\u6765\u5728\u7075\u524d\u62dc\u796d\uff0c\u5e76\u548c\u5404\u4f4d\u76f8\u89c1\u3002\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u591a\u627f\u674e\u5c06\u519b\u539a\u610f\u76db\u60c5\uff0c\u5728\u4e0b\u59d3\u5b59\u540d\u4ef2\u5bff\u3002\u201d \u3000\u3000\u90a3\u767d\u51c0\u9762\u76ae\u7684\u4eba\u9053\uff1a\u201c\u554a\uff0c\u4f60\u662f\u5b59\u7956\u5bff\u5c06\u519b\u7684\u5f1f\u5f1f\u3002\u5b59\u5c06\u519b\u548c\u9791\u5b50\u62da\u547d\u800c\u6b7b\uff0c\u6211\u4eec\u4e00\u5411\u662f\u5f88\u656c\u4ef0\u7684\u3002\u201d \u3000\u3000\u5b59\u7956\u5bff\u662f\u6297\u6e05\u5927\u5c06\uff0c\u5728\u8fb9\u5173\u591a\u7acb\u529f\u52cb\uff0c\u4e8e\u6e05\u5175\u5165\u4fb5\u65f6\u968f\u8881\u5d07\u7115\u634d\u536b\u4eac\u5e08\u3002 \u3000\u3000\u8881\u5d07\u7115\u4e0b\u72f1\u540e\uff0c\u5b59\u7956\u5bff\u6124\u800c\u51fa\u6218\uff0c\u5728\u5317\u4eac\u6c38\u5b9a\u95e8\u5916\u548c\u5927\u5c06\u6ee1\u6842\u540c\u65f6\u6218\u6b7b\uff0c\u540d\u626c\u5929\u4e0b\u3002\u5b59\u4ef2\u5bff\u6587\u6b66\u5168\u624d\uff0c\u5411\u4e3a\u5144\u957f\u7684\u5de6\u53f3\u624b\uff0c\u5728\u6b64\u5f79\u4e2d\u529b\u6218\u5f97\u8131\uff0c\u6124\u6068\u5d07\u796f\u51a4\u6740\u5fe0\u81e3\uff0c\u548c\u8881\u5d07\u7115\u7684\u65e7\u90e8\u6563\u5728\u6c5f\u6e56\uff0c\u629a\u80b2\u5e7c\u4e3b\uff0c\u5bc6\u8c0b\u590d\u4ec7\u3002\u4ed6\u7cbe\u660e\u591a\u667a\uff0c\u9690\u4e3a\u8881\u515a\u7684\u9996\u9886\u3002 \u3000\u3000\u5b59\u7956\u5bff\u6177\u6168\u91cd\u4e49\uff0c\u5fe0\u52c7\u5ec9\u6d01\uff0c\u300a\u660e\u53f2\u300b\u4e0a\u8bb0\u8f7d\u4e86\u4e24\u4e2a\u6545\u4e8b\uff1a\u5b59\u7956\u5bff\u9547\u5b88\u56fa\u5173\u6297\u6e05\u65f6\uff0c\u51fa\u6218\u53d7\u4f24\uff0c\u6fd2\u4e8e\u4e0d\u8d77\u3002\u4ed6\u59bb\u5b50\u5f20\u6c0f\u5272\u4e0b\u624b\u81c2\u4e0a\u7684\u8089\uff0c\u716e\u4e86\u6c64\u7ed9\u4ed6\u559d\uff0c\u540c\u65f6\u7edd\u98df\u4e03\u65e5\u4e03\u591c\uff0c\u7948\u7977\u4e0a\u5929\uff0c\u613f\u4ee5\u8eab\u4ee3\u3002\u540e\u6765\u5b59\u7956\u5bff\u75ca\u6108\u800c\u5f20\u6c0f\u5374\u6b7b\u4e86\u3002\u5b59\u7956\u5bff\u611f\u5ff5\u59bb\u6069\uff0c\u7ec8\u8eab\u4e0d\u8fd1\u5987\u4eba\u3002 \u3000\u3000\u4ed6\u8eab\u4e3a\u5927\u5c06\u65f6\uff0c\u6709\u4e00\u540d\u90e8\u5c06\u8def\u8fc7\u4ed6\u660c\u5e73\u6545\u4e61\uff0c\u9001\u4e86\u4e94\u767e\u4e24\u94f6\u5b50\u5230\u4ed6\u5bb6\u91cc\u3002 \u3000\u3000\u5728\u5f53\u65f6\u539f\u662f\u5341\u5206\u5bfb\u5e38\u4e4b\u4e8b\uff0c\u4f46\u4ed6\u513f\u5b50\u575a\u51b3\u4e0d\u53d7\u3002\u540e\u6765\u4ed6\u513f\u5b50\u6765\u5230\u519b\u4e2d\uff0c\u4ed6\u5927\u4e3a\u5609\u5956\uff0c\u8bf7\u513f\u5b50\u559d\u9152\uff0c\u8bf4\uff1a\u201c\u4e0d\u53d7\u8d60\u91d1\uff0c\u6df1\u5f97\u6211\u5fc3\u3002\u5018\u82e5\u4f60\u53d7\u4e86\uff0c\u8fd9\u4e00\u6b21\u975e\u519b\u6cd5\u4ece\u4e8b\u4e0d\u53ef\u3002\u201d\u300a\u660e\u53f2\u300b\u79f0\u8d5e\u4ed6\u201c\u5176\u79c9\u4e49\u6267\u8282\u5982\u6b64\u3002\u201d\u5b59\u4ef2\u5bff\u4e3a\u4eba\u5904\u4e8b\u9887\u6709\u5144\u98ce\uff0c\u662f\u4ee5\u4e3a\u4f17\u6240\u94a6\u4f69\u3002 \u3000\u3000\u6ce8\uff1a\u660e\u6210\u7956\u5e94\u6d61\u6ce5\u56fd\u82cf\u4e39\u4e4b\u8bf7\uff0c\u5c01\u5177\u5c71\u4e3a\u201c\u957f\u5b81\u9547\u56fd\u5c71\u201d\u3002\u4eb2\u5236\u7891\u6587\uff0c\u5e76\u9898\u8bd7\u4e00\u9996\uff0c\u3000\u3000\u8bd1\u610f\u5982\u4e0b\uff1a\u201c\u5728\u70ed\u5e26\u7684\u6d77\u4e0a\uff0c\u662f\u6d61\u6ce5\u56fd\u6240\u5904\u7684\u5730\u65b9\u3002\u4eba\u6c11\u4eb2\u8fd1\u4ec1\u4e49\uff0c\u53ea\u6709\u5f52\u987a\uff0c\u6ca1\u6709\u8fdd\u9006\u3002\u8d24\u3000\u3000\u738b\u52e4\u6073\u8c28\u614e\uff0c\u4ef0\u6155\u4e2d\u534e\u6559\u5316\u3002\u5927\u660e\u7ba1\u7406\u5916\u56fd\u7684\u5b98\u5458\u52a0\u4ee5\u6307\u5bfc\uff0c\u5c31\u5230\u4e2d\u56fd\u6765\u671d\u62dc\u4e86\uff0c\u5e26\u4e86\u4f60\u3000\u3000\u7684\u5983\u5b50\u3001\u4e16\u5b50\u3001\u5144\u5f1f\u3001\u966a\u81e3\uff0c\u6765\u5230\u5927\u660e\u8a00\u6bbf\u9636\u4e0b\u78d5\u5934\uff0c\u9648\u594f\u9053\uff1a\u2018\u7687\u4e0a\u5c31\u8c61\u662f\u5929\u4e00\u6837\uff0c\u5c06\u3000\u3000\u6e29\u6696\u548c\u6109\u4e50\u666e\u8d50\u5929\u4e0b\uff0c\u5bf9\u4efb\u4f55\u4eba\u90e8\u4e00\u6837\u7737\u987e\uff0c\u6ca1\u6709\u504f\u7231\uff0c\u6ca1\u6709\u6b67\u89c6\u3002\u2019\u4f46\u6211\u81ea\u5df1\u53cd\u7701\uff0c\u5fb7\u3000\u3000\u884c\u4e0d\u591f\uff0c\u6ca1\u6709\u4f60\u6240\u8bf4\u7684\u8fd9\u6837\u4f1f\u5927\u3002\u4f60\u5192\u770b\u98ce\u6d6a\uff0c\u8fdc\u6d89\u91cd\u6d0b\uff0c\u4e58\u8239\u6765\u5230\uff0c\u5b9e\u5728\u662f\u9650\u8f9b\u82e6\u3002\u67e5\u3000\u3000\u8003\u5386\u6765\u8fdc\u90a6\u7684\u81e3\u5c5e\uff0c\u5f52\u987a\u7684\u65f6\u5019\u5c31\u6765\u94a5\u62dc\uff0c\u4e0d\u670d\u7684\u65f6\u5019\u5c31\u4e0d\u6765\u4e86\uff0c\u81ea\u5df1\u524d\u6765\u90fd\u4e0d\u5bb9\u6613\uff0c\u4f55\u3000\u3000\u51b5\u8fd8\u5e26\u4e86\u5bb6\u5ba4\uff1f\u4f60\u56fd\u738b\u79c9\u5fd7\u8d1e\u8bda\uff0c\u8c61\u91d1\u77f3\u4e00\u6837\u575a\u56fa\u3002\u897f\u5357\u5404\u56fd\u7684\u8543\u90a6\u541b\u4e3b\uff0c\u54ea\u4e00\u4f4d\u80fd\u53ca\u5f97\u3000\u3000\u4e0a\u4f60\uff1f\u4f60\u56fd\u5185\u6709\u4e00\u5ea7\u5dcd\u5ce8\u7684\u9ad8\u5c71\uff0c\u9547\u5b81\u90a6\u56fd\u3002\u73b0\u5728\u5728\u77f3\u7891\u4e0a\u523b\u4e86\u6587\u5b57\uff0c\u4ee5\u53d1\u626c\u4f60\u56fd\u738b\u7684\u7f8e\u3000\u3000\u5fb7\u3002\u4f46\u613f\u4f60\u56fd\u738b\u7f8e\u5fb7\u5149\u5927\uff0c\u56fd\u6cf0\u6c11\u5b89\uff0c\u4eca\u540e\u5343\u4f19\u4e07\u5c81\uff0c\u90fd\u5f52\u9644\u6211\u5927\u660e\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u56de\u3000\u6069\u4ec7\u540c\u60a3\u96be \u6b7b\u751f\u89c1\u4ea4\u60c5",content:(0,t.jsx)(t.Fragment,{children:"\u4f17\u4eba\u6b63\u8981\u53d9\u8bdd\uff0c\u5218\u82b3\u4eae\u7684\u9ed1\u8138\u4ece\u4eba\u5ffd\u7136\u4ece\u540e\u5ea7\u4e0a\u76f4\u7eb5\u51fa\u53bb\uff0c\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u4f17\u4eba\u51fa\u5176\u4e0d\u610f\uff0c\u4e0d\u77e5\u53d1\u751f\u751a\u4e48\u4e8b\uff0c\u90fd\u7ad9\u4e86\u8d77\u6765\u3002\u53ea\u89c1\u90a3\u9ed1\u8138\u5c11\u5e74\u6307\u7740\u4eba\u7fa4\u4e2d\u4e24\u4e2a\u4e2d\u5e74\u6c49\u5b50\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u66f9\u592a\u76d1\u7684\u624b\u4e0b\u4eba\uff0c\u5230\u8fd9\u91cc\u6765\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u5927\u5403\u4e00\u60ca\uff0c\u5747\u77e5\u5d07\u796f\u7687\u5e1d\u8bdb\u706d\u9b4f\u5fe0\u8d24\u548c\u5ba2\u6c0f\u4e4b\u540e\u3002\u5bab\u4e2d\u671d\u4e2d\u9006\u515a\u867d\u7136\u4e00\u626b\u800c\u7a7a\uff0c\u7136\u800c\u7687\u5e1d\u751f\u6027\u591a\u7591\uff0c\u53c8\u79c9\u627f\u81ea\u592a\u7956\u3001\u6210\u7956\u4ee5\u6765\u7684\u4e60\u6c14\uff0c\u5bf9\u5927\u81e3\u591a\u6240\u731c\u5fcc\uff0c\u6240\u4efb\u7528\u7684\u4ecd\u662f\u4ece\u4ed6\u4fe1\u738b\u5e9c\u5e26\u6765\u7684\u592a\u76d1\uff0c\u5176\u4e2d\u6700\u5f97\u5ba0\u7684\u5219\u662f\u66f9\u5316\u6df3\u3002\u6b64\u4eba\u7edf\u7387\u7687\u5e1d\u7684\u5fa1\u7528\u4fa6\u63a2\u548c\u536b\u58eb\uff0c\u5373\u6240\u8c13\u201c\u5382\u536b\u201d\uff0c\u523a\u63a2\u671d\u4e2d\u5927\u81e3\u548c\u7279\u5730\u5c06\u5e05\u7684\u9690\u79c1\uff0c\u6587\u6b66\u5927\u81e3\u5f80\u5f80\u4e0d\u660e\u4e0d\u767d\u7684\u4e3a\u7687\u5e1d\u4e0b\u65e8\u8bdb\u6740\uff0c\u6216\u662f\u4efb\u610f\u902e\u6355\uff0c\u5173\u5165\u5929\u7262\uff0c\u6240\u8c13\u201c\u4e0b\u8bcf\u72f1\u201d.\u90fd\u662f\u7531\u4e8e\u66f9\u5316\u6df3\u7684\u5bc6\u62a5\u3002\u66f9\u592a\u76d1\u7684\u540d\u4e49\u3002\u5f53\u65f6\u4e00\u63d0\u8d77\u6765\uff0c\u53ef\u8bf4\u662f\u4eba\u4eba\u8c08\u864e\u8272\u53d8\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u4e00\u4e2a\u6ee1\u816e\u9ec4\u987b\uff0c\u56db\u5341\u4e0a\u4e0b\u5e74\u7eaa.\u53e6\u4e00\u4e2a\u5374\u9762\u767d\u65e0\u987b\uff0c\u77ee\u77ee\u80d6\u80d6\u3002 \u3000\u3000\u90a3\u77ee\u80d6\u5b50\u9762\u8272\u500f\u53d8\uff0c\u968f\u5373\u9547\u5b9a\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6211\u5417\uff1f\u5f00\u751a\u4e48\u73a9\u7b11\uff1f\u201d\u9ed1\u8138\u5c11\u5e74\u9053\uff1a\u201c\u54fc\uff0c\u5f00\u73a9\u7b11\uff01\u4f60\u4eec\u4e24\u4e2a\u9b3c\u9b3c\u795f\u795f\u5728\u5ba2\u5e97\u91cc\u5546\u91cf\uff0c\u8981\u6df7\u8fdb\u5c71\u5b97\u6765\uff0c\u53c8\u8bf4\u5df2\u7980\u544a\u4e86\u66f9\u592a\u76d1\uff0c\u8981\u6d3e\u5175\u6765\u4e00\u7f51\u6253\u5c3d\uff0c\u8fd9\u4e9b\u8bdd\u90fd\u7ed9\u6211\u542c\u89c1\u5566\uff01\u201d \u3000\u3000\u9ec4\u987b\u4eba\u62d4\u51fa\u94a2\u5200\uff0c\u4f5c\u52bf\u4fbf\u8981\u6251\u4e0a\u53ae\u62da\u3002\u90a3\u767d\u8138\u80d6\u5b50\u5374\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201d \u3000\u3000\u674e\u95ef\u60f3\u6536\u5e76\u5c71\u5b97\u7684\u670b\u53cb\uff0c\u5c45\u5fc3\u9669\u6076\uff0c\u54ea\u4e00\u4e2a\u4e0d\u77e5\u9053\u4e86\uff1f\u4f60\u60f3\u6765\u9020\u8c23\u751f\u4e8b\uff0c\u6311\u62e8\u79bb\u95f4\uff0c\u90a3\u53ef\u4e0d\u6210\u3002\u201d\u4ed6\u8bf4\u8bdd\u58f0\u53c8\u7ec6\u53c8\u5c16\uff0c\u4fe8\u7136\u592a\u76d1\u58f0\u53e3\uff0c\u53ef\u662f\u8fd9\u51e0\u53e5\u8bdd\u5374\u4e5f\u751f\u4e86\u6548\u3002\u8881\u515a\u4e2d\u4fbf\u6709\u591a\u4eba\u4fa7\u76ee\u659c\u89c6\uff0c\u5bf9\u674e\u81ea\u6210\u7684\u4f7f\u8005\u8d77\u4e86\u7591\u5fc3\u3002 \u3000\u3000\u5218\u82b3\u4eae\u867d\u51fa\u8eab\u519c\u5bb6\uff0c\u4f46\u4e45\u7ecf\u6218\u9635\uff0c\u767e\u70bc\u6210\u94a2\uff0c\u89c1\u4e86\u8881\u515a\u8bf8\u4eba\u7684\u795e\u8272\uff0c\u77e5\u9053\u6b64\u4eba\u7684\u8a00\u8bed\u5df2\u6253\u52a8\u4f17\u5fc3\uff0c\u4fbf\u5373\u559d\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8c01\uff1f\u662f\u5c71\u5b97\u7684\u670b\u53cb\u4e48\uff1f\u201d\u8fd9\u53e5\u8bdd\u95ee\u4e2d\u4e86\u8981\u5bb3\uff0c\u90a3\u4eba\u767b\u65f6\u8bed\u585e\uff0c\u53ea\u662f\u51b7\u7b11\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u559d\u9053\uff1a\u201c\u670b\u53cb\u662f\u8881\u7763\u5e08\u65e7\u90e8\u4e48\uff1f\u6211\u600e\u5730\u6ca1\u89c1\u8fc7\uff1f\u4f60\u662f\u54ea\u4e00\u4f4d\u603b\u5175\u624b\u4e0b\uff1f\u201d \u3000\u3000\u90a3\u767d\u8138\u4eba\u77e5\u9053\u4e8b\u8d25\uff0c\u5411\u9ec4\u987b\u4eba\u4f7f\u4e2a\u773c\u8272\uff0c\u4e24\u4eba\u9661\u5730\u8dc3\u8d77\uff0c\u53cc\u53cc\u843d\u5728\u95e8\u53e3\u3002 \u3000\u3000\u9ec4\u987b\u4eba\u6325\u5200\u5411\u9ed1\u8138\u5c11\u5e74\u780d\u53bb\u3002\u90a3\u767d\u8138\u4eba\u770b\u4f3c\u534a\u7537\u534a\u5973\uff0c\u884c\u52a8\u5374\u751a\u662f\u8fc5\u6377\uff0c\u8155\u5e95\u4e00\u7ffb\uff0c\u5df1\u62bd\u51fa\u5224\u5b98\u53cc\u7b14\uff0c\u5411\u9ed1\u8138\u5c11\u5e74\u80f8\u53e3\u70b9\u5230\u3002 \u3000\u3000\u9ed1\u8138\u5c11\u5e74\u56e0\u662f\u524d\u6765\u62dc\u796d\uff0c\u4e3a\u793a\u5c0a\u5d07\uff0c\u53c8\u514d\u5bf9\u65b9\u8d77\u7591\uff0c\u4e0a\u5c71\u6765\u8eab\u4e0a\u4e0d\u5e26\u5175\u5203\u3002\u4f17\u4eba\u89c1\u4ed6\u53cc\u624b\u7a7a\u7a7a\uff0c\u9aa4\u906d\u5939\u51fb\uff0c\u4fbf\u6709\u4e03\u516b\u4eba\u8981\u62a2\u4e0a\u6551\u63f4\u3002\u4e0d\u6599\u90a3\u5c11\u5e74\u6b66\u529f\u751a\u662f\u4e86\u5f97\uff0c\u5de6\u624b\u5982\u51e4\uff0c\u65bd\u5c55\u64d2\u62ff\u624b\u6cd5\uff0c\u4fbf\u6293\u9ec4\u987b\u5ba2\u7684\u624b\u8155\uff0c\u540c\u65f6\u53f3\u624b\u9a88\u8d77\u98df\u4e2d\u4e24\u6307\uff0c\u62a2\u5148\u70b9\u5411\u767d\u8138\u4eba\u7684\u53cc\u76ee\u3002\u8fd9\u4e24\u62db\u8fdf\u53d1\u5148\u81f3\uff0c\u7acb\u65f6\u903c\u5f97\u4e24\u540d\u654c\u4eba\u90fd\u9000\u5f00\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u8881\u515a\u4f17\u4eba\u89c1\u4ed6\u53ea\u4e00\u62db\u4e4b\u95f4\u4fbf\u53cd\u5b88\u4e3a\u653b\uff0c\u6697\u6697\u559d\u91c7\uff0c\u4ff1\u5404\u6b62\u6b65\u3002\u90a3\u4e24\u4eba\u89c1\u51b2\u4e0d\u51fa\u95e8\u53bb\uff0c\u77e5\u9053\u8eab\u5904\u864e\u7a74\uff0c\u60c5\u52bf\u51f6\u9669\u4e4b\u6781\uff0c\u521a\u9000\u5f97\u4e24\u6b65\uff0c\u4fbf\u53c8\u62a2\u4e0a\u3002\u9ed1\u8138\u5c11\u5e74\u4f7f\u5f00\u53cc\u638c\uff0c\u5728\u5355\u5200\u53cc\u7b14\u4e4b\u95f4\u7a7f\u68ad\u6765\u53bb\uff0c\u653b\u591a\u5b88\u5c11\u3002\u90a3\u4e24\u4eba\u51e0\u6b21\u62a2\u5230\u95e8\u8fb9\uff0c\u90fd\u88ab\u4ed6\u903c\u4e86\u56de\u6765\u3002 \u3000\u3000\u767d\u8138\u4eba\u5fc3\u4e2d\u7126\u8e81\uff0c\u7b14\u6cd5\u4e00\u53d8\uff0c\u53cc\u7b14\u6a2a\u6253\u7ad6\u70b9\uff0c\u62db\u62db\u6307\u5411\u5bf9\u65b9\u8981\u7a74\u3002\u9ec4\u987b\u5ba2\u65bd\u5c55\u5c71\u897f\u6b66\u80dc\u95e8\u5200\u6cd5\uff0c\u77ee\u4e0b\u8eab\u5b50\uff0c\u75be\u780d\u9ed1\u8138\u5c11\u5e74\u4e0b\u76d8\u3002\u4f17\u4eba\u773c\u89c1\u5371\u6025\uff0c\u90fd\u60f3\u4f38\u624b\u76f8\u52a9\uff0c\u4f46\u4e00\u77a5\u773c\u95f4\uff0c\u89c1\u5218\u82b3\u4eae\u795e\u8272\u9547\u5b9a\uff0c\u53cd\u800c\u5750\u4e0b\u6765\u89c2\u6218\uff0c\u5747\u60f3\uff0c\u4ed6\u81ea\u5df1\u4eba\u5c1a\u4e14\u4e0d\u6025\uff0c\u5b9a\u662f\u6709\u6043\u65e0\u6050\uff0c\u4e14\u770b\u4e00\u4e0b\u52a8\u9759\u518d\u8bf4\u3002 \u3000\u3000\u4e09\u4eba\u5728\u5927\u6bbf\u4e2d\u817e\u632a\u6765\u53bb\uff0c\u6597\u5230\u9163\u5904\uff0c\u9ec4\u987b\u4eba\u7a81\u7136\u60ca\u53eb\u4e00\u58f0\uff0c\u5355\u5200\u8131\u624b\u5411\u4eba\u4e1b\u4e2d\u98de\u53bb\u3002\u6731\u5b89\u56fd\u8dc3\u8d77\u4f38\u624b\u4e00\u6284\uff0c\u63a5\u5728\u624b\u4e2d\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u9ed1\u8138\u5c11\u5e74\u8e0f\u8fdb\u4e00\u6b65\uff0c\u5de6\u817f\u8d77\u5904\uff0c\u4e00\u811a\u628a\u9ec4\u987b\u4eba\u8e22\u5012\u3002\u4ed6\u5de6\u817f\u5c1a\u672a\u6536\u56de\uff0c\u53f3\u817f\u4e58\u52bf\u53c8\u8d77\uff0c\u767d\u8138\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u60f3\u903c\u5f00\u654c\u4eba\uff0c\u593a\u95e8\u9003\u8d70\u4e0b\u5c71\uff0c\u5f53\u4e0b\u594b\u8d77\u5e73\u751f\u4e4b\u529b\uff0c\u53cc\u7b14\u4e00\u5148\u4e00\u540e\u53cd\u70b9\u654c\u4eba\u80f8\u53e3\u3002\u9ed1\u8138\u5c11\u5e74\u53f3\u624b\u9661\u51fa\uff0c\u6293\u4f4f\u5de6\u7b14\u7b14\u7aef\uff0c\u4f7f\u529b\u4e00\u626d\uff0c\u5df2\u628a\u4e00\u53ea\u5224\u5b98\u7b14\u62a2\u8fc7\u3002\u8fd9\u65f6\u5bf9\u65b9\u53f3\u7b14\u8ddf\u7740\u70b9\u5230\uff0c\u4ed6\u987a\u624b\u5c06\u7b14\u68a2\u7838\u4e86\u8fc7\u53bb\u3002\u53cc\u7b14\u76f8\u4ea4\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u706b\u661f\u4ea4\u8ff8\uff0c\u767d\u8138\u4eba\u864e\u53e3\u9707\u88c2\uff0c\u53f3\u7b14\u8ddf\u7740\u8131\u624b\u3002 \u3000\u3000\u9ed1\u8138\u5c11\u5e74\u4e00\u58f0\u957f\u7b11\uff0c\u53f3\u624b\u6293\u4f4f\u4ed6\u80f8\u53e3\uff0c\u4e00\u628a\u63d0\u8d77\u3002\u5de6\u624b\u626f\u4f4f\u4ed6\u7684\u88e4\u8170\uff0c\u53cc\u624b\u4e00\u5206\uff0c\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u767d\u8138\u4eba\u4e00\u6761\u88e4\u5b50\u5df2\u88ab\u626f\u4e0b\u6765\uff0c\u88f8\u51fa\u4e0b\u8eab\u3002\u4f17\u4eba\u6115\u7136\u4e4b\u4e0b\uff0c\u9ed1\u8138\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u592a\u76d1\uff0c\u5927\u5bb6\u77a7\u77a7\uff01\u201d\u4f17\u4eba\u76ee\u5149\u5168\u90fd\u96c6\u5230\u90a3\u767d\u8138\u4eba\u7684\u4e0b\u8eab\uff0c\u679c\u89c1\u4ed6\u662f\u51c0\u4e86\u8eab\u7684\u3002\u54c4\u7b11\u58f0\u4e2d\uff0c\u4f17\u4eba\u56f4\u4e86\u62e2\u6765\uff0c\u773c\u89c1\u8fd9\u9ed1\u8138\u5c11\u5e74\u51fa\u624b\u5947\u5feb\uff0c\u6b66\u529f\u9ad8\u660e\u4e4b\u6781\uff0c\u5fc3\u4e0b\u90fd\u751a\u656c\u4f69\u3002 \u3000\u3000\u8fd9\u65f6\u65e9\u6709\u4eba\u62e5\u4e0a\u53bb\u628a\u767d\u8138\u4eba\u548c\u9ec4\u987b\u4eba\u6309\u4f4f\u3002\u5b59\u4ef2\u5bff\u559d\u95ee\uff1a\u201c\u66f9\u592a\u76d1\u6d3e\u4f60\u4eec\u6765\u5e72\u751a\u4e48\uff1f\u8fd8\u6709\u591a\u5c11\u540c\u515a\uff1f\u600e\u4e48\u80fd\u6df7\u8fdb\u6765\u7684\uff1f\u201d\u4e24\u4eba\u9ed8\u4e0d\u4f5c\u58f0\u3002\u5b59\u4ef2\u5bff\u4e00\u4f7f\u773c\u8272\uff0c\u7f57\u53c2\u5c06\u63d0\u8d77\u5355\u5200\uff0c\u547c\u547c\u4e24\u5200\u628a\u4e24\u4eba\u9996\u7ea7\u5272\u4e0b\uff0c\u653e\u5728\u795e\u50cf\u524d\u7684\u4f9b\u684c\u4e0a\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u62f1\u624b\u5411\u5218\u82b3\u4eae\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4e09\u4f4d\u53d1\u73b0\u5978\u8d3c\uff0c\u6211\u4eec\u5927\u7978\u4e34\u5934\u8fd8\u4e0d\u77e5\u9053\u3002\u201d\u5218\u82b3\u4eae\u9053\uff1a\u201c\u90a3\u4e5f\u662f\u78b0\u5de7\u3002\u6211\u4eec\u5728\u9053\u4e0a\u9047\u89c1\u8fd9\u4e24\u4e2a\u5bb6\u4f19\uff0c\u89c1\u4ed6\u4eec\u795e\u8272\u53e4\u602a\uff0c\u8eab\u624b\u53c8\u5f88\u7075\u4fbf\uff0c\u665a\u4e0a\u4fbf\u5230\u5ba2\u5e97\u53bb\u67e5\u63a2\uff0c\u4fa5\u5e78\u53d1\u89c9\u4e86\u4ed6\u4eec\u7684\u5e95\u7ec6\u3002\u201d \u3000\u3000\u5b59\u4ef2\u5bff\u5411\u5218\u82b3\u4eae\u7684\u4e24\u4f4d\u4ece\u4eba\u9053\uff1a\u201c\u8bf7\u6559\u4e24\u4f4d\u5c0a\u59d3\u5927\u540d\u3002\u201d\u4e24\u4eba\u62a5\u4e86\u59d3\u540d\uff0c\u80a4\u8272\u767d\u51c0\u7684\u53eb\u7530\u89c1\u79c0\uff0c\u9ed1\u8138\u5c11\u5e74\u540d\u53eb\u5d14\u79cb\u5c71\u3002\u6731\u5b89\u56fd\u8fc7\u53bb\u62c9\u4f4f\u5d14\u79cb\u5c71\u7684\u624b\uff0c\u8bf4\u4e86\u8bb8\u591a\u8d5e\u4f69\u7684\u8bdd\u3002 \u3000\u3000\u5218\u82b3\u4eae\u548c\u5b59\u4ef2\u5bff\u53ca\u8881\u515a\u4e2d\u51e0\u4e2a\u9996\u8111\u4eba\u7269\u5230\u540e\u5802\u5bc6\u8c08\u3002\u5218\u82b3\u4eae\u8bf4\u9053\uff0c\u674e\u5c06\u519b\u76fc\u671b\u5927\u5bb6\u643a\u624b\u9020\u53cd\uff0c\u5171\u540c\u7ed3\u76df\u3002\u8881\u515a\u7684\u4eba\u5747\u611f\u8e0c\u8e87\u3002\u4f17\u4eba\u867d\u7136\u618e\u6068\u5d07\u796f\u7687\u5e1d\uff0c\u51b3\u610f\u6697\u4e2d\u884c\u523a\uff0c\u6740\u5b98\u8bdb\u5978\u4e4b\u4e8b\u4e5f\u5df2\u4f5c\u4e86\u4e0d\u5c11\uff0c\u4f46\u4eba\u4eba\u672c\u6765\u90fd\u662f\u5927\u660e\u547d\u5b98\uff0c\u8981\u4ed6\u4eec\u9020\u53cd\uff0c\u5374\u662f\u4e0d\u613f\uff0c\u53ea\u6c42\u523a\u6b7b\u5d07\u796f\u540e\uff0c\u53e6\u7acb\u5b97\u5ba4\u660e\u541b\u3002\u4f55\u51b5\u674e\u81ea\u6210\u603b\u662f\u201c\u6d41\u5bc7\u201d\uff0c\u867d\u7136\u540d\u5934\u6781\u5927\uff0c\u4f46\u6253\u5bb6\u52ab\u820d\uff0c\u6d41\u7a9c\u63b3\u63a0\uff0c\u5e72\u7684\u662f\u5f3a\u76d7\u52fe\u5f53\uff0c\u5927\u5bb6\u5fc3\u4e2d\u4e00\u76f4\u4e5f\u4e0d\u5927\u77a7\u5f97\u8d77\u3002\u8881\u515a\u4f17\u4eba\u79bb\u519b\u4e4b\u540e\uff0c\u4e3a\u4e86\u751f\u8ba1\uff0c\u6709\u65f6\u4e5f\u96be\u514d\u505a\u51e0\u6869\u6ca1\u672c\u94b1\u4e70\u5356\uff0c\u5374\u4ece\u6765\u4e0d\u516c\u7136\u81ea\u5c45\u76d7\u8d3c\u3002\u53cc\u65b9\u8eab\u5206\u4e0d\u540c\uff0c\u8bae\u8bba\u826f\u4e45\u96be\u51b3\u3002 \u3000\u3000\u6700\u540e\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u54b1\u4eec\u7684\u4e8b\u5df2\u7ed9\u66f9\u592a\u76d1\u77e5\u9053\uff0c\u5982\u4e0d\u548c\u674e\u5c06\u519b\u5408\u76df\u4ee5\u4e3e\u5927\u4e8b\uff0c\u4e0d\u4f46\u523a\u6740\u5d07\u796f\u7ed9\u8881\u7763\u5e08\u62a5\u4ec7\u4e4b\u4e8b\u96be\u4ee5\u6210\u529f\uff0c\u53ea\u6015\u66f9\u592a\u76d1\u8fd8\u8981\u6d3e\u4eba\u5230\u5904\u622a\u6740\u3002\u54b1\u4eec\u52bf\u5b64\u529b\u5f31\uff0c\u96be\u514d\u4e00\u4e00\u906d\u4e86\u6bd2\u624b\u3002\u5218\u5144\uff0c\u54b1\u4eec\u8fd9\u6837\u8bf4\u5b9a\u6210\u4e0d\u6210\uff1f\u6211\u4eec\u5c71\u5b97\u5e2e\u674e\u5c06\u519b\u6253\u5b98\u5175\uff0c\u674e\u5c06\u519b\u4e8b\u6210\u4e4b\u540e\uff0c\u987b\u5f97\u7aed\u529b\u706d\u4e86\u6ee1\u6d32\u9791\u5b50\u3002\u54b1\u4eec\u8bdd\u53c8\u8bf4\u660e\u5728\u5148\uff0c\u65e5\u540e\u674e\u5c06\u519b\u8981\u505a\u7687\u5e1d\uff0c\u6211\u4eec\u5c71\u5b97\u670b\u53cb\u5374\u4e0d\u8d5e\u6210\uff0c\u987b\u5f97\u7531\u592a\u7956\u7687\u5e1d\u7684\u5b50\u5b59\u59d3\u6731\u7684\u6765\u505a\u3002\u201d \u3000\u3000\u5218\u82b3\u4eae\u9053\uff1a\u201c\u674e\u5c06\u519b\u53ea\u662f\u7ed9\u5b98\u5e9c\u903c\u4e0d\u8fc7\uff0c\u8fd9\u624d\u9020\u53cd\uff0c\u81ea\u5df1\u662f\u51b3\u8ba1\u4e0d\u505a\u7687\u5e1d\u7684\uff0c\u8fd9\u4ef6\u4e8b\u5144\u5f1f\u62cd\u80f8\u62c5\u4fdd\u3002\u4eba\u5bb6\u53eb\u6211\u4eec\u6d41\u5bc7\uff0c\u5176\u5b9e\u6211\u4eec\u53ea\u662f\u79cd\u7530\u7684\u5e84\u7a3c\u6c49\uff0c\u53ea\u6c42\u6709\u53e3\u996d\u5403\uff0c\u5934\u4e0a\u8fd9\u9897\u8111\u888b\u4fdd\u5f97\u7262\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u6211\u4eec\u4e1c\u5954\u897f\u9003\uff0c\u90a3\u662f\u65e0\u53ef\u5948\u4f55\u3002\u51ed\u6211\u4eec\u8fd9\u6837\u7684\u6599\u5b50\uff0c\u4e5f\u505a\u4e0d\u6765\u7687\u5e1d\u5927\u5b98\u3002\u81f3\u4e8e\u6253\u5efa\u5dde\u9791\u5b50\u561b\uff0c\u674e\u5c06\u519b\u7684\u5fc3\u610f\u8ddf\u5404\u4f4d\u4e00\u6a21\u4e00\u6837\uff0c\u5e73\u65f6\u8bf4\u8d77\uff0c\u674e\u5c06\u519b\u5bf9\u9791\u5b50\u5b9e\u662f\u6068\u5230\u9aa8\u5934\u91cc\u53bb\u3002\u201d \u3000\u3000\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u90a3\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d\u8881\u515a\u4f17\u4eba\u66f4\u65e0\u5f02\u8a00\uff0c\u4e8e\u662f\u7ed3\u76df\u4e4b\u4ee5\u4fbf\u6210\u5b9a\u5c40\u3002 \u3000\u3000\u91cc\u9762\u5728\u5546\u8bae\u7ed3\u76df\u5927\u8ba1\uff0c\u6bbf\u4e0a\u6731\u5b89\u56fd\u548c\u502a\u6d69\u62c9\u7740\u5d14\u79cb\u5c71\u7684\u624b\uff0c\u8d70\u5230\u4e00\u4e2a\u50fb\u9759\u7684\u89d2\u843d\u91cc\u3002 \u3000\u3000\u6731\u5b89\u56fd\u9053\uff1a\u201c\u5d14\u5927\u54e5\uff0c\u54b1\u4eec\u867d\u662f\u521d\u4f1a\uff0c\u53ef\u662f\u4e00\u89c1\u5982\u6545\uff0c\u4f60\u522b\u5f53\u6211\u4eec\u662f\u5916\u4eba\u3002\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u54e5\u4ece\u524d\u6253\u9791\u5b50\uff0c\u4fdd\u6c5f\u5c71\uff0c\u5144\u5f1f\u4e00\u5411\u662f\u5f88\u94a6\u4f69\u7684\u3002 \u3000\u3000\u4eca\u65e5\u80fd\u89c1\u5230\u5c71\u4e1c\u8fd9\u8bb8\u591a\u82f1\u96c4\u670b\u53cb\uff0c\u5144\u5f1f\u5b9e\u662f\u9ad8\u5174\u5f97\u5f88\u3002\u201d\u502a\u6d69\u9053\uff1a\u201c\u6211\u5192\u6627\u8bf7\u95ee\uff0c\u5d14\u5927\u54e5\u7684\u5e08\u627f\u662f\u54ea\u4e00\u4f4d\u524d\u8f88\u82f1\u96c4\uff1f\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u5144\u5f1f\u7684\u53d7\u4e1a\u6069\u5e08\uff0c\u662f\u5c71\u897f\u5927\u540c\u5e9c\u4e00\u58f0\u96f7\u767d\u91ce\u767d\u8001\u7237\u5b50\u3002\u4ed6\u8001\u4eba\u5bb6\u5df2\u53bb\u4e16\u591a\u5e74\u4e86\u3002\u201d\u6731\u5b89\u56fd\u548c\u502a\u6d69\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u5747\u611f\u7591\u60d1\u3002\u502a\u6d69\u8bf4\u9053\uff1a\u201c\u4e00\u58f0\u96f7\u767d\u8001\u524d\u8f88\u7684\u5927\u540d\uff0c\u6211\u4eec\u662f\u4e45\u4ef0\u7684\u4e86\u3002\u4e0d\u8fc7\u6709\u4e00\u53e5\u8bdd\u5d14\u5927\u54e5\u8bf7\u52ff\u89c1\u602a\u3002\u767d\u8001\u524d\u8f88\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u4f3c\u4e4e\u8fd8\u4e0d\u53ca\u5d14\u5927\u54e5\u3002\u201d\u5d14\u79cb\u5c71\u9ed8\u7136\u4e0d\u8bed\u3002\u6731\u5b89\u56fd\u9053\uff1a\u201c\u867d\u7136\u9752\u51fa\u4e8e\u84dd\uff0c\u5f92\u5f1f\u9ad8\u8fc7\u5e08\u7236\u7684\u4e8b\u4e5f\u662f\u5e38\u89c1\uff0c\u4f46\u521a\u624d\u6211\u770b\u5d14\u5927\u54e5\u6253\u5012\u4e24\u4e2a\u5978\u7ec6\u7684\u8eab\u6cd5\u624b\u6cd5\uff0c\u5374\u4f3c\u53e6\u6709\u771f\u4f20\u3002\u201d \u3000\u3000\u5d14\u79cb\u5c71\u5fae\u4e00\u8fdf\u7591\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u597d\u670b\u53cb\uff0c\u672c\u6765\u4e0d\u6562\u76f8\u7792\u3002\u6211\u5e08\u7236\u901d\u4e16\u4e4b\u540e\uff0c\u6211\u673a\u7f18\u5de7\u5408\uff0c\u9047\u7740\u4e00\u4f4d\u4e16\u5916\u9ad8\u4eba\u3002\u4ed6\u8001\u4eba\u5bb6\u70b9\u62e8\u4e86\u6211\u4e00\u70b9\u6b66\u827a\uff0c\u8981\u6211\u7acb\u8a93\u4e0d\u8bb8\u8bf4\u4ed6\u540d\u53f7\uff0c\u6240\u4ee5\u8981\u8bf7\u4e24\u4f4d\u5927\u54e5\u539f\u8c05\u3002\u201d \u3000\u3000\u502a\u6731\u4e24\u4eba\u89c1\u4ed6\u8bf4\u5f97\u8bda\u6073\uff0c\u5fd9\u9053\uff1a\u201c\u5d14\u5927\u54e5\u5feb\u522b\u8fd9\u4e48\u8bf4\uff0c\u6211\u4eec\u6709\u4e00\u4e8b\u76f8\u6c42\uff0c\u56e0\u6b64\u624d\u5927\u80c6\u76f8\u95ee\u3002\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u751a\u4e48\u4e8b\uff0c\u4fbf\u8bf7\u76f4\u8a00\uff0c\u5927\u5bb6\u662f\u81ea\u5df1\u4eba\uff0c\u4f55\u5fc5\u5ba2\u6c14\uff1f\u201d\u6731\u5b89\u56fd\u9053\uff1a\u201c\u5d14\u5927\u54e5\u8bf7\u7b49\u4e00\u7b49\uff0c\u6211\u4eec\u53bb\u627e\u4e24\u4f4d\u670b\u53cb\u5546\u91cf\u51e0\u53e5\u3002\u201d \u3000\u3000\u6731\u502a\u4e8c\u4eba\u628a\u90a3\u59d3\u5e94\u548c\u59d3\u7f57\u7684\u62c9\u5728\u4e00\u8fb9\u3002\u6731\u5b89\u56fd\u9053\uff1a\u201c\u8fd9\u4e2a\u5d14\u5144\u5f1f\u6b66\u827a\u9ad8\u5f3a\uff0c\u54b1\u4eec\u8fd9\u91cc\u6ca1\u4e00\u4e2a\u53ca\u5f97\u4e0a\u3002\u542c\u4ed6\u8bf4\u8bdd\uff0c\u6027\u683c\u4e5f\u751a\u662f\u8c6a\u723d\u3002\u201d\u502a\u6d69\u9053\uff1a\u201c\u5c31\u662f\u8bf4\u5230\u5e08\u627f\u65f6\u6709\u70b9\u541e\u541e\u5410\u5410\u3002\u201d\u4e8e\u662f\u628a\u5d14\u79cb\u5c71\u7684\u8bdd\u590d\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u90a3\u59d3\u5e94\u7684\u540d\u53eb\u5e94\u677e\uff0c\u662f\u8881\u5d07\u7115\u5e10\u4e0b\u7684\u8c0b\u58eb\uff0c\u5f53\u5e74\u5b81\u8fdc\u7b51\u57ce\uff0c\u66fe\u51fa\u4e86\u4e0d\u5c11\u529b\u91cf\u3002\u59d3\u7f57\u7684\u540d\u5927\u5343\uff0c\u662f\u8457\u540d\u7684\u70ae\u624b\uff0c\u5b81\u8fdc\u4e00\u6218\uff0c\u4ed6\u70b9\u71c3\u7ea2\u5937\u5927\u70ae\uff0c\u8f70\u6b7b\u6e05\u5175\u65e0\u6570\uff0c\u56e0\u529f\u5347\u5230\u53c2\u5c06\u3002 \u3000\u3000\u5e94\u677e\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u59a8\u76f4\u8a00\u76f8\u6c42\uff0c\u77a7\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d\u6731\u5b89\u56fd\u9053\uff1a\u201c\u8fd9\u4e8b\u5f53\u5148\u95ee\u8fc7\u5b59\u76f8\u516c\u3002\u201d\u5e94\u677e\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u8f6c\u5230\u540e\u6bbf\uff0c\u89c1\u5b59\u4ef2\u5bff\u548c\u5218\u82b3\u4eae\u6b63\u8c08\u5f97\u5341\u5206\u6295\u5951\uff0c\u4e8e\u662f\u628a\u5b59\u4ef2\u5bff\u8bf7\u51fa\u6765\u5546\u91cf\u3002\u8fd9\u4e9b\u6b66\u5c06\u6240\u64c5\u957f\u7684\u662f\u884c\u519b\u6253\u4ed7\uff0c\u51b2\u950b\u9677\u9635\uff0c\u8bf4\u5230\u957f\u67aa\u786c\u5f29\uff0c\u5341\u8361\u5341\u51b3\uff0c\u90a3\u662f\u52c7\u4e0d\u53ef\u5f53\uff0c\u4f46\u6b66\u5b66\u4e2d\u7684\u62f3\u811a\u5668\u68b0\u529f\u592b\uff0c\u5374\u5747\u81ea\u77e5\u4e0d\u53ca\u5d14\u79cb\u5c71\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u5e94\u5e08\u7237\uff0c\u8fd9\u4ef6\u4e8b\u5173\u7cfb\u5e7c\u4e3b\u7684\u7ec8\u8eab\uff0c\u4f60\u5148\u63a2\u63a2\u90a3\u59d3\u5d14\u7684\u53e3\u6c14\u3002\u201d \u3000\u3000\u5e94\u677e\u70b9\u5934\u7b54\u5e94\uff0c\u4e0e\u6731\u5b89\u56fd\u3001\u502a\u6d69\u3001\u7f57\u5927\u5343\u4e09\u4eba\u540c\u53bb\u89c1\u5d14\u79cb\u5c71\u3002 \u3000\u3000\u5e94\u677e\u9053\uff1a\u201c\u6211\u4eec\u6709\u4e00\u4ef6\u4e8b\uff0c\u53ea\u6709\u5d14\u5927\u54e5\u5e2e\u8fd9\u4e2a\u5fd9\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u5d14\u79cb\u5c71\u89c1\u4ed6\u4eec\u6b32\u8a00\u6587\u6b62\uff0c\u4e00\u526f\u597d\u751f\u4e3a\u96be\u7684\u795e\u6c14\uff0c\u4fbf\u9053\uff1a\u201c\u5144\u5f1f\u662f\u7c97\u4eba\uff0c\u5404\u4f4d\u6709\u751a\u4e48\u5429\u5490\uff0c\u53ea\u8981\u5144\u5f1f\u505a\u5f97\u5230\u7684\uff0c\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u5e94\u677e\u9053\uff1a\u201c\u5d14\u5144\u5f88\u723d\u5feb\uff0c\u90a3\u4e48\u6211\u4eec\u76f4\u8bf4\u4e86\u3002\u8881\u7763\u5e08\u88ab\u5bb3\u4e4b\u540e\uff0c\u7559\u4e0b\u4e00\u4f4d\u516c\u5b50\uff0c\u90a3\u65f6\u8fd8\u53ea\u6709\u4e03\u5c81\u3002\u6211\u4eec\u8ddf\u660f\u541b\u6d3e\u6765\u902e\u6355\u7763\u5e08\u5bb6\u5c5e\u7684\u9526\u8863\u536b\u6253\u4e86\u4e09\u573a\uff0c\u6b7b\u4e86\u4e03\u4e2a\u5144\u5f1f\uff0c\u624d\u4fdd\u5168\u8881\u7763\u5e08\u8fd9\u70b9\u9aa8\u8840\u3002\u201d\u5d14\u79cb\u5c71\u55ef\u4e86\u4e00\u58f0\u3002\u5e94\u677e\u9053\uff1a\u201c\u8fd9\u4f4d\u5e7c\u4e3b\u540d\u53eb\u8881\u627f\u5fd7\uff0c\u7531\u6211\u4eec\u56db\u4eba\u6559\u4ed6\u8bc6\u5b57\u7ec3\u6b66\u3002\u4ed6\u806a\u660e\u5f97\u5f88\uff0c\u4e00\u6559\u5c31\u4f1a\uff0c\u8fd9\u51e0\u5e74\u6765\uff0c\u6211\u4eec\u7684\u672c\u9886\u5dee\u4e0d\u591a\u90fd\u5df2\u4f20\u6388\u7ed9\u4ed6\u4e86\u3002\u867d\u7136\u4ed6\u5e74\u7eaa\u5c0f\uff0c\u529f\u592b\u8fd8\u4e0d\u5230\u5bb6\uff0c\u4f46\u518d\u8ddf\u7740\u6211\u4eec\uff0c\u7ec3\u4e0b\u53bb\u8fdb\u5883\u4e00\u5b9a\u4e0d\u5927\u3002\u201d \u3000\u3000\u5d14\u79cb\u5c71\u5df2\u660e\u767d\u4ed6\u4eec\u7684\u610f\u601d\uff0c\u8bf4\uff1a\u201c\u5404\u4f4d\u8981\u4ed6\u8ddf\u6211\u5b66\u6b66\uff1f\u201d\u6731\u5b89\u56fd\u9053\uff1a\u201c\u521a\u624d\u89c1\u5d14\u5927\u54e5\u51fa\u624b\u6740\u8d3c\uff0c\u6b66\u529f\u80dc\u8fc7\u6211\u4eec\u5341\u500d\uff0c\u8981\u662f\u5d14\u5927\u54e5\u80af\u6536\u8fd9\u4e2a\u5f92\u5f1f\uff0c\u683d\u57f9\u4ed6\u6210\u6750\uff0c\u8881\u7763\u5e08\u5728\u5929\u4e4b\u7075\uff0c\u5b9a\u4e5f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u8bf4\u7f62\u56db\u4eba\u90fd\u4f5c\u4e0b\u63d6\u53bb\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u8fde\u5fd9\u8fd8\u793c\uff0c\u6c89\u541f\u9053\uff1a\u201c\u627f\u5404\u4f4d\u77a7\u5f97\u8d77\uff0c\u5144\u5f1f\u672c\u6765\u4e0d\u8be5\u63a8\u8f9e\uff0c\u4e0d\u8fc7\u5144\u5f1f\u73b0\u4e0b\u662f\u5728\u674e\u5c06\u519b\u519b\u4e2d\uff0c\u6765\u53bb\u65e0\u5b9a\uff0c\u6709\u65f6\u8ddf\u5b98\u519b\u63a5\u4ed7\uff0c\u4e5f\u4e0d\u77e5\u80fd\u6d3b\u5230\u54ea\u4e00\u5929\u3002\u8981\u8881\u516c\u5b50\u8ddf\u6211\u5728\u961f\u4f0d\u91cc\uff0c\u4e00\u5219\u6015\u6211\u6ca1\u7a7a\u6559\u4ed6\uff0c\u4e8c\u5219\u5b9e\u5728\u4e5f\u592a\u5371\u9669\u3002\u201d\u5e94\u677e\u7b49\u5747\u60f3\u8fd9\u786e\u662f\u5b9e\u60c5\uff0c\u5fc3\u4e2d\u597d\u751f\u5931\u671b\u3002\u5d14\u79cb\u5c71\u5ffd\u9053\uff1a\u201c\u6709\u4e00\u4eba\u529f\u592b\u80dc\u6211\u4e0d\u77e5\u591a\u5c11\u500d\uff0c\u5982\u679c\u4ed6\u80af\u6536\u8881\u516c\u5b50\uff0c\u90a3\u771f\u662f\u8881\u516c\u5b50\u7684\u9020\u5316\u4e86\u3002\u201d\u5ffd\u53c8\u8fde\u8fde\u6447\u5934\uff0c\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u4e0d\u6210\uff0c\u4e0d\u6210\u3002\u201c\u5e94\u677e\u4e0e\u6731\u5b89\u56fd\u5fd9\u95ee\uff1a\u201c\u90a3\u662f\u8c01\uff1f\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u4fbf\u662f\u6211\u5148\u524d\u8bf4\u7684\u90a3\u4f4d\u5947\u4eba\u3002\u8fd9\u4f4d\u524d\u8f88\u7684\u529f\u592b\u5b9e\u5728\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4ed6\u6559\u4e86\u6211\u4e24\u4e2a\u591a\u6708\uff0c\u5144\u5f1f\u53ea\u5b66\u5230\u4e00\u70b9\u513f\u76ae\u6bdb\u3002\u201d\u6731\u5b89\u56fd\u5927\u559c\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5947\u4eba\u662f\u8c01\uff1f\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u813e\u6c14\u5f88\u662f\u5947\u7279\uff0c\u867d\u7136\u6559\u6211\u6b66\u827a\uff0c\u53ef\u662f\u4e0d\u80af\u8ba9\u6211\u53eb\u4ed6\u5e08\u7236\uff0c\u4e5f\u4e0d\u51c6\u6211\u5411\u4eba\u6cc4\u9732\u4ed6\u59d3\u540d\u3002\u6c42\u4ed6\u8001\u4eba\u5bb6\u6536\u8881\u516c\u5b50\u4e3a\u5f92\uff0c\u53ea\u6015\u65e0\u6cd5\u529e\u5230\u3002\u201d\u502a\u6d69\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5947\u4eba\u4f4f\u5728\u54ea\u91cc\uff1f\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u4ed6\u884c\u8e2a\u65e0\u5b9a\uff0c\u5230\u751a\u4e48\u5730\u65b9\uff0c\u4e5f\u4ece\u6765\u4e0d\u548c\u6211\u8bf4\u3002\u201d\u5e94\u677e\u7b49\u56db\u4eba\u773c\u89c1\u6b64\u4e8b\u65e0\u671b\uff0c\u53ea\u5f97\u4f5c\u7f62\u3002 \u3000\u3000\u5e94\u677e\u628a\u8881\u627f\u5fd7\u53eb\u4e86\u8fc7\u6765\uff0c\u548c\u5d14\u79cb\u5c71\u89c1\u9762\u3002\u5d14\u79cb\u5c71\u89c1\u4ed6\u7075\u52a8\u6d3b\u6cfc\uff0c\u9762\u8c8c\u9edd\u9ed1\uff0c\u5168\u65e0\u534a\u5206\u5bcc\u8d35\u516c\u5b50\u5a07\u751f\u60ef\u517b\u7684\u60c5\u72b6\uff0c\u5f88\u662f\u559c\u6b22\u3002\u95ee\u4ed6\u6240\u5b66\u7684\u6b66\u827a\uff0c\u8881\u627f\u5fd7\u7b54\u4e86\uff0c\u95ee\u9053\uff1a\u201c\u5d14\u53d4\u53d4\uff0c\u4f60\u521a\u624d\u6293\u4f4f\u90a3\u4e24\u4e2a\u5978\u7ec6\uff0c\u4f7f\u5f97\u751a\u4e48\u529f\u592b\uff1f\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u90a3\u53eb\u505a\u4f0f\u864e\u638c\u6cd5\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u6837\u5feb\uff0c\u6211\u770b\u90fd\u770b\u4e0d\u6e05\u695a\u3002\u201d\u5d14\u79cb\u5c71\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u5b66\uff1f\u201d\u8881\u627f\u5fd7\u4e00\u542c\u8fd9\u8bdd\uff0c\u5fd9\u9053\uff1a\u201c\u5d14\u53d4\u53d4\uff0c\u8bf7\u4f60\u6559\u6211\u3002\u201d \u3000\u3000\u5d14\u79cb\u5c71\u5411\u5e94\u677e\u7b11\u9053\uff1a\u201c\u6211\u8ddf\u5218\u5c06\u519b\u8bf4\uff0c\u5728\u8fd9\u91cc\u803d\u51e0\u5929\uff0c\u5c31\u628a\u8fd9\u8def\u638c\u6cd5\u4f20\u7ed9\u4ed6\u5427\uff01\u201d\u8881\u627f\u5fd7\u548c\u5e94\u6731\u502a\u4e09\u4eba\u4ff1\u5404\u5927\u559c\uff0c\u8fde\u58f0\u79f0\u8c22\u3002 \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u5b59\u4ef2\u5bff\u548c\u5f20\u671d\u5510\u3001\u6768\u9e4f\u4e3e\u7b49\u4e09\u4eba\u544a\u522b\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u76f8\u9022\u4e00\u573a\uff0c\u603b\u7b97\u6709\u7f18\u3002\u8fd9\u91cc\u7684\u4e8b\u53ea\u8981\u6cc4\u6f0f\u534a\u53e5\uff0c\u540e\u679c\u5982\u4f55\uff0c\u4e5f\u4e0d\u5fc5\u5144\u5f1f\u591a\u8bf4\u3002\u201d\u5f20\u6768\u4e24\u4eba\u558f\u558f\u8fde\u58f0\u3002\u5b59\u4ef2\u5bff\u6bcf\u4eba\u8d60\u4e86\u4e94\u5341\u4e24\u94f6\u5b50\u7684\u76d8\u8d39\uff0c\u8fd8\u6d3e\u4e86\u4e24\u4f4d\u5144\u5f1f\u9001\u4e0b\u5c71\u53bb\u3002 \u3000\u3000\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u5f84\u8d74\u5e7f\u5dde\uff0c\u9014\u4e2d\u66f4\u65e0\u4ed6\u6545\u3002\u6768\u9e4f\u4e3e\u906d\u6b64\u632b\u6298\uff0c\u5fc3\u7070\u610f\u61d2\uff0c\u77e5\u9053\u6c5f\u6e56\u4e0a\u5c71\u5916\u6709\u5c71\uff0c\u4eba\u4e0a\u6709\u4eba\uff0c\u81ea\u5df1\u51ed\u8fd9\u70b9\u5fae\u672b\u529f\u592b\uff0c\u5c45\u7136\u80fd\u6328\u5230\u4eca\u65e5\uff0c\u7b97\u5f97\u662f\u4fa5\u5e78\u4e4b\u6781\uff0c\u6b64\u756a\u82e5\u975e\u8881\u627f\u5fd7\u8fd9\u5c0f\u5c0f\u5b69\u7ae5\u4e00\u8a00\u76f8\u6551\uff0c\u5df2\u53d8\u6210\u6ca1\u773c\u775b\u7684\u5e9f\u4eba\uff0c\u60f3\u60f3\u6697\u81ea\u5fc3\u60ca\u3002\u5f53\u5373\u5411\u9556\u5c40\u8f9e\u4e86\u5de5\uff0c\u4fbf\u6b32\u56de\u5bb6\u52a1\u519c\u3002\u5f20\u671d\u5510\u611f\u4ed6\u6551\u547d\u4e4b\u6069\uff0c\u89c1\u4ed6\u5fc3\u60c5\u90c1\u90c1\uff0c\u4fbf\u9080\u4ed6\u540c\u53bb\u6d61\u6ce5\u56fd\u6e38\u89c8\u6563\u5fc3\u3002\u6768\u9e4f\u4e3e\u773c\u89c1\u5de6\u53f3\u65e0\u4e8b\uff0c\u81ea\u5df1\u53c8\u65e0\u5bb6\u7d2f\uff0c\u5f53\u5373\u7b54\u5141\u3002 \u3000\u3000\u4e09\u4eba\u5728\u5e7f\u5dde\u96c7\u4e86\u6d77\u8236\uff0c\u524d\u5f80\u6d61\u6ce5\u3002\u6768\u9e4f\u4e3e\u4f4f\u4e86\u6708\u4f59\uff0c\u89c1\u5f53\u5730\u592a\u5e73\u5b89\u4e50\uff0c\u771f\u5982\u4e16\u5916\u6843\u6e90\u4e00\u822c\uff0c\u7adf\u7136\u4e0d\u5174\u5f52\u610f\uff0c\u4fbf\u5728\u5f20\u671d\u5510\u4e4b\u7236\u5f20\u4fe1\u7684\u90a3\u7763\u5e9c\u4e2d\u62c5\u4efb\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u804c\u53f8\u3002\u6bcf\u65e5\u5f53\u5dee\u4e00\u4e24\u4e2a\u65f6\u8fb0\uff0c\u4f59\u4e0b\u6765\u4fbf\u662f\u559d\u9152\u8d4c\u94b1\uff0c\u751a\u662f\u900d\u9065\u5feb\u4e50\u3002 \u3000\u3000\u5218\u82b3\u4eae\u548c\u5b59\u4ef2\u5bff\u7b49\u8bf4\u59a5\u7ed3\u76df\u4e4b\u4e8b\uff0c\u4f17\u4eba\u5728\u8881\u5d07\u7115\u795e\u50cf\u524d\u7acb\u4e0b\u91cd\u8a93\uff0c\u51b3\u4e0d\u76f8\u8d1f\u3002\u5218\u82b3\u4eae\u6b63\u8981\u548c\u8881\u515a\u7740\u610f\u7ed3\u7eb3\uff0c\u542c\u8bf4\u5d14\u79cb\u5c71\u8981\u6559\u8881\u627f\u5fd7\u6b66\u827a\uff0c\u751a\u662f\u6b22\u559c\uff0c\u5f53\u4e0b\u548c\u7530\u89c1\u79c0\u5148\u4e0b\u5c71\u53bb\u3002 \u3000\u3000\u8881\u515a\u5404\u8def\u597d\u6c49\uff0c\u6709\u7684\u53bb\u6295\u674e\u81ea\u6210\uff1a\u6709\u7684\u5404\u5f52\u6545\u4e61\uff0c\u7b79\u5907\u4e3e\u4e8b\uff1b\u4e5f\u6709\u7684\u8a00\u660e\u4e0d\u613f\u9020\u53cd\u4f5c\u4e71\uff0c\u53ea\u662f\u51b3\u4e0d\u6cc4\u9732\u673a\u5bc6\uff0c\u4e5f\u4e0d\u548c\u4f17\u5144\u5f1f\u4f5c\u5bf9\u4e3a\u654c\u3002\u4eba\u5404\u6709\u5fd7\uff0c\u65c1\u4eba\u4e5f\u4e0d\u52c9\u5f3a\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u3001\u6731\u5b89\u56fd\u3001\u502a\u6d69\u3001\u5e94\u677e\u7b49\u7559\u5728\u5c71\u4e0a\uff0c\u8be6\u5546\u8881\u627f\u5fd7\u65e5\u540e\u7684\u51fa\u5904\u3002 \u3000\u3000\u8881\u627f\u5fd7\u81ea\u5d14\u79cb\u5c71\u7b54\u5e94\u6559\u4ed6\u4f0f\u864e\u638c\u540e\uff0c\u6b22\u559c\u5f97\u4e00\u591c\u6ca1\u7761\u597d\u89c9\u3002\u7fcc\u65e5\u5927\u5bb6\u5fd9\u7740\u7ed3\u76df\uff0c\u6ca1\u529f\u592b\u7406\u4f1a\u8fd9\u4e8b\u3002\u4e0b\u5348\u4f17\u4eba\u7eb7\u7eb7\u4e0b\u5c71\uff0c\u4e34\u884c\u65f6\u6bcf\u4eba\u90fd\u548c\u5e7c\u4e3b\u4f5c\u522b\uff0c\u53c8\u5fd9\u788c\u4e86\u534a\u5929\u3002 \u3000\u3000\u5230\u5f97\u665a\u4e0a\uff0c\u5b59\u4ef2\u5bff\u548c\u5e94\u677e\u547d\u4eba\u70b9\u4e86\u7ea2\u70db\uff0c\u8bbe\u4e86\u4ea4\u6905\uff0c\u8bf7\u5d14\u79cb\u5c71\u5750\u5728\u4e0a\u9762\uff0c\u8981\u8881\u627f\u5fd7\u884c\u62dc\u5e08\u4e4b\u793c\u3002\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u8881\u5bb6\u5c0f\u5144\u5f1f\u6211\u4e00\u89c1\u5c31\u5f88\u559c\u6b22\uff0c\u4ed6\u7231\u6211\u8fd9\u5957\u4f0f\u864e\u638c\uff0c\u6211\u5c31\u7834\u8d39\u51e0\u5929\u529f\u592b\uff0c\u4f20\u6388\u4e00\u4e2a\u5927\u6982\u3002\u4f46\u4ed6\u80fd\u4e0d\u80fd\u5728\u8fd9\u51e0\u5929\u4e4b\u5185\u5b66\u4f1a\uff0c\u5b66\u4e86\u4e4b\u540e\u80fd\u4e0d\u80fd\u7528\uff0c\u53ef\u5f97\u77a7\u4ed6\u7684\u609f\u6027\u548c\u4ee5\u540e\u7684\u7ec3\u4e60\u4e86\u3002\u8fd9\u53ea\u662f\u670b\u53cb\u4e4b\u95f4\u7684\u5207\u78cb\uff0c\u5e08\u5f92\u7684\u540d\u4efd\u662f\u65e0\u8bba\u5982\u4f55\u8c08\u4e0d\u4e0a\u7684\u3002\u201d\u5e94\u677e\u9053\uff1a\u201c\u53ea\u8981\u6559\u5f97\u4e00\u62db\u4e24\u5f0f\uff0c\u5c31\u662f\u7ec8\u8eab\u4e3a\u5e08\u3002\u5d14\u5927\u54e5\u4f55\u5fc5\u592a\u8c26\uff1f\u201d\u5d14\u79cb\u5c71\u4e00\u5b9a\u4e0d\u80af\uff0c\u5927\u5bb6\u4e5f\u53ea\u5f97\u7f62\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u77e5\u9053\u6b66\u6797\u4e2d\u7684\u89c4\u77e9\uff0c\u4f20\u827a\u65f6\u522b\u4eba\u4e0d\u4fbf\u65c1\u89c2\uff0c\u9053\u4e86\u52b3\u540e\uff0c\u4fbf\u544a\u8f9e\u51fa\u6765\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u7b49\u4f17\u4eba\u51fa\u53bb\uff0c\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u8fd9\u5957\u4f0f\u864e\u638c\u6cd5\uff0c\u662f\u4e00\u4f4d\u524d\u8f88\u9ad8\u4eba\u4f20\u7ed9\u6211\u7684\u3002\u6211\u4e0d\u80fd\u5c3d\u6570\u9886\u4f1a\u5176\u4e2d\u7684\u7cbe\u5965\uff0c\u529f\u592b\u4e5f\u7740\u5b9e\u8fd8\u5dee\u5f97\u8fdc\uff0c\u4f46\u5728\u6c5f\u6e56\u4e0a\u5bf9\u4ed8\u5bfb\u5e38\u654c\u4eba\uff0c\u4e5f\u5df2\u8db3\u591f\u3002\u4ed6\u8001\u4eba\u5bb6\u4f20\u6388\u8fd9\u5957\u638c\u6cd5\u4e4b\u65f6\uff0c\u66fe\u53eb\u6211\u7acb\u8a93\u3002\u5b66\u4f1a\u4e4b\u540e\uff0c\u51b3\u4e0d\u80fd\u7528\u6765\u6b3a\u538b\u826f\u5584\uff0c\u4f24\u5bb3\u65e0\u8f9c\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u542c\uff0c\u5df2\u660e\u5176\u610f\uff0c\u5f53\u5373\u8dea\u4e0b\uff0c\u8bf4\u9053\uff0c\u201c\u5f1f\u5b50\u8881\u627f\u5fd7\uff0c\u5b66\u4f1a\u4e86\u4f0f\u864e\u638c\u6cd5\u4e4b\u540e\uff0c\u51b3\u4e0d\u6562\u6b3a\u538b\u826f\u5584\uff0c\u4f24\u5bb3\u65e0\u8f9c\uff0c\u5426\u5219\uff0c\u5426\u5219\u2026\u2026\u201d\u4ed6\u4e0d\u77e5\u7acb\u8a93\u7684\u89c4\u77e9\uff0c\u9053\uff1a\u201c\u5426\u5219\u5c31\u7ed9\u5d14\u53d4\u53d4\u6253\u6b7b\u3002\u201d \u3000\u3000\u5d14\u79cb\u5c71\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f88\u597d\u3002\u201d\u5ffd\u7136\u8eab\u5b50\u4e00\u6643\uff0c\u4eba\u5df2\u4e0d\u89c1\u3002\u8881\u627f\u5fd7\u6025\u8f6c\u8eab\u65f6\uff0c\u5d14\u79cb\u5c71\u5df2\u7ed5\u5230\u4ed6\u7684\u8eab\u540e\uff0c\u5728\u4ed6\u80a9\u5934\u4e00\u62cd\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6293\u4f4f\u6211\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7ecf\u8fc7\u6731\u5b89\u56fd\u548c\u502a\u6d69\u3001\u7f57\u5927\u5343\u4e09\u4f4d\u5e08\u7236\u7684\u6307\u70b9\uff0c\u6b66\u529f\u4e5f\u5df2\u7a0d\u6709\u6839\u57fa\uff0c\u7acb\u5373\u77ee\u8eab\uff0c\u5de6\u624b\u865a\u6643\uff0c\u53f3\u624b\u5708\u8f6c\uff0c\u7adf\u4e0d\u56de\u8eab\uff0c\u542c\u98ce\u8fa8\u5f62\uff0c\u4fbf\u5411\u5d14\u79cb\u5c71\u817f\u4e0a\u6293\u53bb\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u559c\u9053\uff1a\u201c\u8fd9\u62db\u4e0d\u9519\uff01\u201d\u8bdd\u58f0\u65b9\u6bd5\uff0c\u624b\u638c\u8f7b\u8f7b\u5728\u4ed6\u80a9\u5934\u4e00\u62cd\uff0c\u4eba\u5f71\u53c8\u5df2\u4e0d\u89c1\u3002\u8881\u627f\u5fd7\u51dd\u795e\u9759\u6c14\uff0c\u4e00\u5bf9\u5c0f\u638c\u4f38\u4e86\u5f00\u6765\uff0c\u5c45\u7136\u4e5f\u62a4\u4f4f\u4e86\u8eab\u4e0a\u5404\u5904\u8981\u5bb3\uff0c\u773c\u89c1\u5d14\u79cb\u5c71\u8eab\u6cd5\u5947\u5feb\uff0c\u518d\u4e5f\u6293\u4ed6\u4e0d\u4f4f\uff0c\u5f53\u4e0b\u4e0d\u518d\u8ddf\u4ed6\u515c\u5708\u5b50\u6349\u8ff7\u85cf\uff0c\u4e00\u6b65\u4e00\u6b65\u9000\u5411\u5899\u58c1\uff0c\u7a81\u7136\u8f6c\u8eab\uff0c\u9760\u7740\u5899\u58c1\uff0c\u7b11\u9053\uff1a\u201c\u5d14\u53d4\u53d4\uff0c\u6211\u89c1\u5230\u4f60\u5566\uff01\u201d \u3000\u3000\u5d14\u79cb\u5c71\u4e0d\u80fd\u518d\u7ed5\u5230\u4ed6\u8eab\u540e\uff0c\u505c\u4f4f\u811a\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u5f88\u806a\u660e\uff0c\u4f0f\u864e\u638c\u4e00\u5b9a\u5b66\u5f97\u6210\u3002\u201d\u4e8e\u662f\u4e00\u62db\u4e00\u5f0f\u7684\u4ece\u5934\u6559\u4ed6\u3002 \u3000\u3000\u8fd9\u8def\u638c\u6cd5\u5171\u4e00\u767e\u5355\u516b\u5f0f\uff0c\u6bcf\u5f0f\u5404\u6709\u4e09\u9879\u53d8\u5316\uff0c\u5947\u6b63\u76f8\u751f\u76f8\u514b\uff0c\u5171\u4e09\u767e\u4e8c\u5341\u56db\u53d8\u3002\u8881\u627f\u5fd7\u9ed8\u9ed8\u8bb0\u5fc6\uff0c\u5b66\u4e86\u51e0\u904d\uff0c\u5df2\u628a\u62db\u5f0f\u8bb0\u5f97\u5927\u81f4\u65e0\u8bef\u3002\u5d14\u79cb\u5c71\u8fde\u6bd4\u5e26\u8bf4\uff0c\u518d\u628a\u6bcf\u4e00\u62db\u6bcf\u4e00\u53d8\u7684\u7528\u6cd5\u7ec6\u52a0\u4f20\u6388\u3002\u8881\u627f\u5fd7\u6b66\u529f\u672c\u6709\u6839\u62b5\uff0c\u609f\u6027\u53c8\u5f3a\uff0c\u5d14\u79cb\u5c71\u4e00\u8bf4\uff0c\u4fbf\u80fd\u9886\u4f1a\u3002\u4e00\u4e2a\u6559\u5f97\u8d77\u52b2\uff0c\u4e00\u4e2a\u5b66\u5f97\u7528\u5fc3\uff0c\u76f4\u81f3\u6df1\u591c\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u5d14\u79cb\u5c71\u5728\u5c71\u8fb9\u6563\u6b65\uff0c\u89c1\u8881\u627f\u5fd7\u6b63\u5728\u7ec3\u62f3\uff0c\u65bd\u5c55\u4f0f\u864e\u638c\u4e00\u767e\u5355\u516b\u62db\u7684\u53d8\u5316\uff0c\u4e8e\u90a3\u52fe\u3001\u6487\u3001\u637a\u3001\u5288\u3001\u6495\u3001\u6253\u3001\u5d29\u3001\u5410\u516b\u5927\u8981\u8bc0\uff0c\u5c45\u7136\u4e5f\u80fd\u660e\u5176\u5927\u65e8\uff0c\u77e5\u5176\u7cbe\u8981\u3002\u5d14\u79cb\u5c71\u5f88\u662f\u559c\u6b22\uff0c\u5f53\u4ed6\u7ec3\u5230\u5165\u795e\u4e4b\u65f6\u7a81\u7136\u4e00\u8dc3\u800c\u524d\uff0c\u62ac\u817f\u5411\u4ed6\u80cc\u5fc3\u8e22\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5ffd\u542c\u80cc\u540e\u98ce\u58f0\u54cd\u52a8\uff0c\u4fa7\u8eab\u907f\u8fc7\uff0c\u56de\u624b\u4fbf\u62c9\u654c\u4eba\u7684\u53f3\u817f\uff0c\u4e00\u773c\u77a5\u89c1\u662f\u5d14\u79cb\u5c71\uff0c\u6025\u5fd9\u7f29\u624b\uff0c\u60ca\u53eb\uff1a\u201c\u5d14\u53d4\u53d4\uff01\u201d\u5d14\u79cb\u5c71\u7b11\u9053\uff1a\u201c\u522b\u505c\u624b\uff0c\u6253\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5288\u9762\u4e00\u638c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77e5\u4ed6\u662f\u548c\u81ea\u5df1\u62c6\u62db\uff0c\u5f53\u4e0b\u8e0f\u4e0a\u4e00\u6b65\uff0c\u5c0f\u62f3\u6512\u51fb\u5d14\u79cb\u5c71\u8170\u80ef\uff0c\u6b63\u662f\u4f0f\u864e\u638c\u7b2c\u516b\u5341\u4e5d\u62db\u201c\u6df1\u5165\u864e\u7a74\u201d\u3002\u5d14\u79cb\u5c71\u8d5e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u8fd9\u6837\u3002\u201d\u53e3\u4e2d\u6307\u70b9\uff0c\u624b\u4e0b\u4e0d\u505c\uff0c\u548c\u4ed6\u5bf9\u62c6\u8d77\u6765\u3001\u89c1\u8881\u627f\u5fd7\u51fa\u62db\u6709\u8bef\uff0c\u4fbf\u7acb\u5373\u7ea0\u6b63\u3002\u4e24\u4eba\u62f3\u6765\u8db3\u5f80\uff0c\u628a\u4f0f\u864e\u638c\u4e00\u767e\u5355\u516b\u5f0f\u3001\u4e09\u767e\u4e8c\u5341\u56db\u53d8\u7ffb\u6765\u8986\u53bb\u7684\u62c6\u89e3\u3002\u8881\u627f\u5fd7\u89c1\u8fd9\u5957\u638c\u6cd5\u53d8\u5316\u591a\u7aef\uff0c\u5d14\u79cb\u5c71\u8fd0\u7528\u65f6\u6108\u51fa\u6108\u5947\uff0c\u6b22\u559c\u65e0\u5df2\uff0c\u7528\u5fc3\u8bb0\u5fc6\u3002\u62c6\u89e3\u826f\u4e45\uff0c\u5d14\u79cb\u5c71\u89c1\u4ed6\u5934\u4e0a\u51fa\u6c57\uff0c\u77e5\u9053\u7d2f\u4e86\uff0c\u4fbf\u505c\u4f4f\u624b\uff0c\u8981\u4ed6\u5750\u4e0b\u4f11\u606f\uff0c\u4e00\u9762\u6bd4\u5212\u8bb2\u89e3\u3002 \u3000\u3000\u8bb2\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u53c8\u53eb\u4ed6\u7ad9\u8d77\u6765\u8fc7\u62db\u3002 \u3000\u3000\u4e24\u4eba\u81ea\u6e05\u6668\u76f4\u81f3\u6df1\u591c\uff0c\u9664\u4e86\u5403\u996d\u4e4b\u5916\uff0c\u4e0d\u505c\u7684\u62c6\u7ec3\u638c\u6cd5\u3002\u5982\u6b64\u7ec3\u4e86\u4e03\u65e5\uff0c\u5230\u4e86\u7b2c\u516b\u5929\u665a\u4e0a\uff0c\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u6211\u6240\u4f1a\u7684\u5df2\u5168\u90e8\u4f20\u4e86\u7ed9\u4f60\uff0c\u65e5\u540e\u662f\u5426\u6709\u6210\uff0c\u5168\u51ed\u4f60\u81ea\u5df1\u7ec3\u4e60\u4e86\u3002\u4e34\u654c\u4e4b\u9645\uff0c\u5c40\u9762\u5343\u53d8\u4e07\u5316\uff0c\u4e03\u5206\u9760\u529f\u592b\uff0c\u4e09\u5206\u9760\u673a\u7075\uff0c\u4e00\u5473\u86ee\u6253\uff0c\u51b3\u96be\u53d6\u80dc\u3002\u201d\u8881\u627f\u5fd7\u70b9\u5934\u53d7\u6559\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u660e\u5929\u6211\u5c31\u8981\u56de\u5230\u674e\u5c06\u519b\u90a3\u91cc\uff0c\u4eca\u540e\u76fc\u4f60\u597d\u597d\u7528\u529f\u3002\u4f20\u6211\u638c\u6cd5\u7684\u90a3\u4f4d\u9ad8\u4eba\u66fe\u8bf4\uff0c\u6b66\u5b66\u9ad8\u4f4e\u7684\u5173\u952e\uff0c\u662f\u5728\u5934\u8111\u4e4b\u4e2d\u800c\u4e0d\u5728\u624b\u811a\u4e4b\u4e0a\uff0c\u662f\u4ee5\u591a\u60f3\u6bd4\u591a\u7ec3\u66f4\u52a0\u8981\u7d27\u3002\u53ef\u60dc\u6211\u7684\u8111\u7b4b\u5b9e\u5728\u4e0d\u5927\u7075\u5149\uff0c\u96be\u6709\u751a\u4e48\u8fdb\u5883\uff0c\u76fc\u4f60\u65e5\u540e\u7ec3\u5f97\u80fd\u80dc\u8fc7\u4e86\u6211\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u5d14\u79cb\u5c71\u76f8\u5904\u867d\u53ea\u6709\u516b\u4e5d\u5929\uff0c\u4f46\u4ed6\u628a\u4f0f\u864e\u638c\u503e\u56ca\u76f8\u6388\uff0c\u6559\u4e4b\u52e4\uff0c\u663e\u89c1\u7231\u4e4b\u6df1\uff0c\u542c\u8bf4\u660e\u5929\u5c31\u8981\u5206\u624b\uff0c\u4e0d\u89c9\u773c\u7736\u7ea2\u4e86\uff0c\u4fbf\u8981\u6389\u4e0b\u6cea\u6765\u3002\u5d14\u79cb\u5c71\u89c1\u4ed6\u5bf9\u81ea\u5df1\u751a\u662f\u4f9d\u604b\uff0c\u4e5f\u4e0d\u7531\u5f97\u611f\u52a8\uff0c\u8f7b\u8f7b\u629a\u6478\u4ed6\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8c61\u4f60\u8fd9\u6837\u806a\u660e\u8d44\u8d28\uff0c\u6b66\u6797\u4e2d\u5b9e\u5728\u5c11\u89c1\uff0c\u53ef\u60dc\u6211\u4eec\u6ca1\u673a\u7f18\u957f\u4e45\u76f8\u805a\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5d14\u53d4\u53d4\uff0c\u6211\u8ddf\u4f60\u5230\u674e\u5c06\u519b\u90a3\u91cc\u3002\u201d\u5d14\u79cb\u5c71\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u5c0f\uff0c\u90a3\u600e\u4e48\u6210\uff1f\u6211\u4eec\u8ddf\u7740\u674e\u5c06\u519b\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u5728\u62da\u547d\uff0c\u9971\u4e00\u987f\u9965\u4e00\u987f\u7684\uff0c\u4eca\u5929\u4e0d\u77e5\u660e\u5929\u7684\u4e8b\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u542c\u5f97\u5c4b\u5916\u6709\u91ce\u517d\u4e00\u58f0\u602a\u53eb\uff0c\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u90a3\u662f\u751a\u4e48\uff1f\u4e0d\u662f\u8001\u864e\uff0c\u4e5f\u4e0d\u662f\u72fc\u3002\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u662f\u8c79\u5b50\u3002\u201d\u7075\u673a\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u53bb\u628a\u8c79\u5b50\u6349\u6765\uff0c\u6211\u6709\u7528\u5904\u3002\u201d\u8881\u627f\u5fd7\u5927\u4e3a\u5174\u594b\uff0c\u5fd9\u95ee\uff1a\u201c\u751a\u4e48\u7528\u5904\uff1f\u201d\u5d14\u79cb\u5c71\u7b11\u800c\u4e0d\u7b54\uff0c\u5306\u5306\u8d70\u4e86\u51fa\u53bb\u3002\u8881\u627f\u5fd7\u5fd9\u8ddf\u51fa\u53bb\uff0c\u89c1\u4ed6\u4e0d\u5e26\u5175\u5203\uff0c\u53c8\u95ee\uff1a\u201c\u5d14\u53d4\u53d4\uff0c\u4f60\u7528\u751a\u4e48\u5175\u5668\u6253\u8c79\u5b50\uff1f\u201d \u3000\u3000\u5d14\u79cb\u5c71\u4e0d\u4ece\u6b63\u95e8\u51fa\u53bb\uff0c\u8d70\u5230\u5185\u8fdb\u5b59\u4ef2\u5bff\u623f\u5916\uff0c\u53eb\u9053\uff1a\u201c\u6731\u5927\u54e5\u3001\u502a\u5927\u54e5\u90fd\u5728\u4e48\uff1f\u201d\u6731\u5b89\u56fd\u7b49\u5728\u623f\u5185\u805a\u8c08\uff0c\u542c\u5f97\u53eb\u58f0\uff0c\u5f00\u95e8\u51fa\u6765\u3002\u5d14\u79cb\u5c71\u7b11\u9053\uff1a\u201c\u8bf7\u5404\u4f4d\u5e2e\u4e00\u4e0b\u624b\uff0c\u628a\u5916\u9762\u90a3\u5934\u8c79\u5b50\u903c\u8fdb\u5c4b\u6765\uff0c\u6211\u6709\u7528\u5904\u3002\u201d\u502a\u6d69\u662f\u6740\u864e\u80fd\u624b\uff0c\u8fde\u8bf4\uff0c\u201c\u597d\uff0c\u597d\u3002\u201d\u62ff\u4e86\u730e\u864e\u53c9\uff0c\u62a2\u5148\u51fa\u95e8\u3002\u5d14\u79cb\u5c71\u53eb\u9053\uff1a\u201c\u502a\u5927\u54e5\uff0c\u522b\u4f24\u90a3\u755c\u751f\u3002\u201d\u502a\u6d69\u9065\u9065\u7b54\u5e94\uff0c\u4e0d\u4e00\u4f1a\uff0c\u547c\u559d\u58f0\u5df2\u8d77\u3002\u5d14\u79cb\u5c71\u548c\u6731\u5b89\u56fd\u3001\u7f57\u5927\u5343\u4e09\u4eba\u4e5f\u7eb5\u51fa\u95e8\u53bb\u3002\u8881\u627f\u5fd7\u62ff\u4e86\u77ed\u94c1\u67aa\u60f3\u8ddf\u51fa\u53bb\u3002\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u522b\u51fa\u53bb\uff0c\u54b1\u4eec\u5728\u8fd9\u91cc\u770b\u3002\u201d\u8881\u627f\u5fd7\u65e0\u5948\uff0c\u53ea\u5f97\u548c\u5b59\u4ef2\u5bff\u3001\u5e94\u677e\u4e09\u4eba\u51ed\u5728\u7a97\u53e3\u89c2\u671b\u3002 \u3000\u3000\u53ea\u89c1\u4e09\u4eba\u62ff\u4e86\u706b\u628a\uff0c\u5206\u7ad9\u4e1c\u897f\u5317\u4e09\u65b9\u3002\u502a\u6d69\u4f7f\u5f00\u730e\u864e\u53c9\uff0c\u5728\u5c71\u8fb9\u548c\u4e00\u5934\u8eaf\u4f53\u5de8\u5927\u7684\u91d1\u94b1\u8c79\u6b63\u81ea\u7ffb\u7ffb\u6eda\u6eda\u7684\u62da\u6597\u3002\u4ed6\u4e00\u67c4\u53c9\u62a4\u4f4f\u5168\u8eab\uff0c\u4e0d\u8ba9\u8c79\u5b50\u6251\u8fd1\uff0c\u5374\u4e5f\u4e0d\u51fa\u53c9\u6233\u523a\u3002\u8c79\u5b50\u89c1\u5230\u706b\u5149\uff0c\u60ca\u6050\u60f3\u9003\uff0c\u5374\u88ab\u6731\u3001\u5d14\u3001\u7f57\u4e09\u4eba\u963b\u4f4f\u4e86\u53bb\u8def\u3002\u8c79\u5b50\u89c1\u5d14\u79cb\u5c71\u624b\u4e2d\u6ca1\u5175\u5668\uff0c\u5927\u543c\u7740\u5411\u4ed6\u6251\u6765\u3002\u5d14\u79cb\u5c71\u95ea\u8eab\u907f\u5f00\u5229\u722a\uff0c\u53f3\u638c\u5728\u8c79\u5b50\u989d\u5934\u4e00\u51fb\uff0c\u8c79\u5b50\u767b\u65f6\u7ffb\u4e86\u4e2a\u7b4b\u6597\uff0c\u8f6c\u8eab\u5411\u5357\u3002\u5357\u9762\u623f\u95e8\u5927\u5f00\uff0c\u8c79\u5b50\u4e0d\u80af\u8fdb\u5c4b\uff0c\u4e1c\u897f\u4e71\u7a9c\uff0c\u4f46\u7ed9\u4f17\u4eba\u903c\u4f4f\u4e86\uff0c\u65e0\u8def\u53ef\u8d70\u3002\u5d14\u79cb\u5c71\u7eb5\u8eab\u800c\u4e0a\uff0c\u5728\u8c79\u5b50\u540e\u81c0\u4e0a\u731b\u529b\u4e00\u811a\u3002\u8c79\u5b50\u8d1f\u75db\uff0c\u543c\u53eb\u4e00\u58f0\uff0c\u76f4\u7a9c\u8fdb\u5c4b\u53bb\u3002 \u3000\u3000\u90a3\u65f6\u5e94\u677e\u5df2\u628a\u5404\u5904\u95e8\u6237\u7d27\u95ed\uff0c\u4ec5\u7559\u51fa\u897f\u8fb9\u504f\u6bbf\u7684\u95e8\u6237\u3002\u8c79\u5b50\u89c1\u4e24\u4eba\u624b\u6301\u706b\u628a\u8ffd\u6765\uff0c\u4e1c\u722c\u897f\u6414\uff0c\u80e1\u80e1\u543c\u53eb\uff0c\u5954\u8fdb\u897f\u6bbf\u3002\u7f57\u5927\u5343\u968f\u540e\u628a\u95e8\u5173\u4e0a\uff0c\u4e00\u5934\u5927\u8c79\u5df2\u5173\u5728\u6bbf\u5185\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u5f88\u9ad8\u5174\uff0c\u671b\u7740\u5d14\u79cb\u5c71\uff0c\u4e0d\u77e5\u4ed6\u8981\u8c79\u4f55\u7528\u3002\u5d14\u79cb\u5c71\u7b11\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u4f60\u8fdb\u53bb\u6253\u8c79\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\u3002\u5b59\u4ef2\u5bff\u9053\uff1a\u201d\u8fd9\u6015\u4e0d\u5927\u59a5\u5f53\u5427\uff1f\u201d \u3000\u3000\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u6211\u5728\u65c1\u8fb9\u77a7\u7740\uff0c\u8fd9\u755c\u751f\u4f24\u4e0d\u4e86\u4ed6\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff01\u201d\u633a\u4e86\u77ed\u67aa\uff0c\u5c31\u53bb\u5f00\u95e8\u3002\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u653e\u4e0b\u67aa\uff0c\u7a7a\u624b\u8fdb\u53bb\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u6014\uff0c\u968f\u5373\u4f1a\u610f\u662f\u8981\u4ed6\u4ee5\u521a\u5b66\u4f1a\u7684\u4f0f\u864e\u638c\u6253\u8c79\uff0c\u4e0d\u7981\u80c6\u602f\u3002\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u4f60\u5bb3\u6015\u4e86\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u66f4\u4e0d\u8fdf\u7591\uff0c\u62d4\u5f00\u6bbf\u95e8\u4e0a\u7684\u63d2\u5934\uff0c\u63a8\u95e8\u8fdb\u53bb\uff0c\u53ea\u542c\u201c\u80e1\u201d\u7684\u4e00\u58f0\u5de8\u543c\uff0c\u4e00\u56e2\u9ed1\u5f71\u8fce\u9762\u6251\u6765\u3002\u4ed6\u53f3\u817f\u4e00\u632b\uff0c\u8ba9\u5f00\u6765\u52bf\uff0c\u53cd\u624b\u51fa\u638c\uff0c\u6253\u5728\u8c79\u5b50\u8033\u4e0a\uff0c\u4f7f\u7684\u6b63\u662f\u4f0f\u864e\u638c\u6cd5\u4e2d\u7684\u201c\u7f57\u6c49\u4f20\u7ecf\u201d\u3002\u8fd9\u638c\u867d\u7136\u6253\u4e2d\uff0c\u53ef\u662f\u624b\u5c0f\u65e0\u529b\uff0c\u8c79\u5b50\u4e0d\u4ee5\u4e3a\u610f\uff0c\u56de\u5934\u4fbf\u54ac\u3002\u8881\u627f\u5fd7\u7a9c\u5230\u8c79\u5b50\u80cc\u540e\u3002\u62c9\u4f4f\u8c79\u5c3e\u4e00\u626f\u3002 \u3000\u3000\u8fd9\u65f6\u5d14\u79cb\u5c71\u5df2\u7ad9\u5728\u4e00\u65c1\u536b\u62a4\uff0c\u60df\u6050\u8c79\u5b50\u731b\u6076\uff0c\u8881\u627f\u5fd7\u5236\u5b83\u4e0d\u4f4f\uff0c\u4f46\u89c1\u4ed6\u4e00\u8def\u4f0f\u864e\u638c\u5df2\u4f7f\u5f97\u9887\u4e3a\u7eaf\u719f\uff0c\u8c79\u5b50\u4e09\u6251\u4e09\u6293\uff0c\u59cb\u7ec8\u6ca1\u78b0\u5230\u4ed6\u4e00\u70b9\u8863\u89d2\uff0c\u53cd\u4e2d\u4e86\u4ed6\u4e00\u638c\u4e00\u811a\uff0c\u5fc3\u4e0b\u6697\u6697\u6b22\u559c\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u7b49\u89c1\u8881\u627f\u5fd7\u7a7a\u624b\u6597\u8c79\uff0c\u867d\u8bf4\u5d14\u79cb\u5c71\u5728\u4e00\u65c1\u7167\u6599\uff0c\u6bd5\u7adf\u5173\u5fc3\uff0c\u5404\u4eba\u62ff\u4e86\u706b\u628a\uff0c\u7ad9\u5728\u6bbf\u89d2\u65c1\u89c2\u3002\u6731\u5b89\u56fd\u548c\u502a\u6d69\u624b\u6263\u6697\u5668\uff0c\u4ee5\u4fbf\u7d27\u6025\u65f6\u5c04\u8c79\u6551\u4eba\u3002 \u3000\u3000\u706b\u5149\u4e2d\u8881\u627f\u5fd7\u817e\u632a\u8d77\u4f0f\uff0c\u8eab\u6cd5\u7075\u6d3b\uff0c\u521d\u65f6\u8fd8\u4e1c\u9003\u897f\u7a9c\uff0c\u4e0d\u6562\u548c\u8c79\u5b50\u63a5\u8fd1\uff0c\u540e\u6765\u89c1\u6240\u5b66\u638c\u6cd5\u65bd\u5c55\u5f00\u6765\u5999\u7528\u751a\u591a\uff0c\u95ea\u907f\u653b\u51fb\uff0c\u5f97\u5fc3\u5e94\u624b\uff0c\u4e0d\u7531\u5f97\u8d8a\u6253\u8d8a\u6709\u7cbe\u795e\u3002 \u3000\u3000\u4ed6\u89c1\u624b\u638c\u6253\u4e0a\u8c79\u8eab\u6beb\u65e0\u7528\u5904\uff0c\u7a81\u7136\u53d8\u62db\uff0c\u6539\u6253\u4e3a\u62c9\uff0c\u6bcf\u638c\u51fb\u5230\uff0c\u56de\u624b\u4fbf\u626f\u4e0b\u4e00\u628a\u6bdb\u6765\u3002\u8c79\u5b50\u53d7\u75db\uff0c\u543c\u53eb\u8fde\u8fde\uff0c\u5bf9\u4ed6\u7684\u5c0f\u638c\u4e5f\u6709\u4e86\u5fcc\u60ee\uff0c\u89c1\u4ed6\u624b\u638c\u4f38\u8fc7\u6765\u65f6\uff0c\u4e0d\u4f4f\u543c\u53eb\u9000\u907f\uff0c\u9732\u9f7f\u62b5\u6297\u3002\u4f46\u8881\u627f\u5fd7\u624b\u6cd5\u6781\u5feb\uff0c\u8c79\u5b50\u603b\u662f\u95ea\u907f\u4e0d\u53ca\uff0c\u4e00\u65f6\u6bbf\u4e2d\u8c79\u6bdb\u56db\u5904\u98de\u626c\uff0c\u4e00\u5934\u597d\u597d\u7684\u91d1\u94b1\u8c79\u5b50\uff0c\u88ab\u4ed6\u4e1c\u4e00\u5757\u897f\u4e00\u5757\u7684\u626f\u53bb\u4e86\u4e0d\u5c11\u9526\u6bdb\u3002\u4f17\u4eba\u90fd\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8c79\u6bdb\u867d\u88ab\u6293\u53bb\uff0c\u4f46\u7a7a\u624b\u7ec8\u7a76\u5236\u5b83\u4e0d\u4f4f\uff0c\u9163\u6597\u4e2d\u4ed6\u7a81\u4f7f\u4e00\u62db\u201c\u83e9\u8428\u4f4e\u7709\u201d\uff0c\u77ee\u8eab\u6b63\u9762\u5411\u8c79\u5b50\u51b2\u53bb\u3002\u8c79\u5b50\u53d7\u60ca\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u968f\u5373\u98de\u8eab\u6251\u6765\uff0c\u4e00\u5239\u90a3\u95f4\uff0c\u8881\u627f\u5fd7\u5df2\u5728\u8c79\u5b50\u8179\u4e0b\u3002 \u3000\u3000\u502a\u6d69\u5927\u60ca\uff0c\u53cc\u9556\u98de\u51fa\u3002\u90a3\u8c79\u4f38\u53f3\u811a\u62e8\u843d\u53cc\u9556\u3002\u8fd9\u65f6\u8881\u627f\u5fd7\u5374\u5df2\u4e0d\u89c1\u3002\u4f17\u4eba\u51dd\u76ee\u770b\u65f6\uff0c\u53ea\u89c1\u4ed6\u8eb2\u5728\u8c79\u5b50\u8179\u5e95\uff0c\u4e00\u53cc\u817f\u52fe\u4f4f\u4e86\u8c79\u80cc\uff0c\u8111\u888b\u9876\u4f4f\u4e86\u8c79\u5b50\u7684\u4e0b\u988f\uff0c\u53eb\u5b83\u54ac\u4e0d\u7740\u6293\u4e0d\u5230\u3002\u8c79\u5b50\u731b\u8df3\u731b\u7a9c\uff0c\u5728\u5730\u4e0b\u6253\u6eda\uff0c\u8881\u627f\u5fd7\u59cb\u7ec8\u4e0d\u653e\u3002 \u3000\u3000\u4ed6\u77e5\u65f6\u95f4\u4e00\u4e45\uff0c\u81ea\u5df1\u529b\u6c14\u4e0d\u8db3\uff0c\u53ea\u8981\u4e00\u677e\u624b\u811a\uff0c\u4e0d\u514d\u4f24\u5728\u8c79\u5b50\u722a\u4e0b\uff0c\u5fd9\u53eb\uff1a\u201c\u5d14\u53d4\u53d4\uff0c\u5feb\u6765\uff01\u201d \u3000\u3000\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u53d6\u5b83\u773c\u775b\uff01\u201d\u4e00\u8a00\u63d0\u9192\uff0c\u8881\u627f\u5fd7\u53f3\u81c2\u7a7f\u51fa\uff0c\u4e24\u6839\u624b\u6307\u63d2\u5411\u8c79\u5b50\u53f3\u773c\uff0c\u8c79\u5b50\u75db\u5f97\u72c2\u53eb\uff0c\u7a9c\u8df3\u66f4\u731b\u3002\u5d14\u79cb\u5c71\u8e0f\u4e0a\u4e00\u6b65\uff0c\u84ec\u84ec\u8fde\u73af\u4e24\u638c\uff0c\u628a\u8c79\u5b50\u6253\u5f97\u5934\u660f\u8111\u80c0\uff0c\u7ffb\u5012\u5728\u5730\uff0c\u968f\u5373\u4e00\u628a\u62b1\u8d77\u8881\u627f\u5fd7\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u574f\uff0c\u4e0d\u574f\uff0c\u771f\u96be\u4e3a\u4f60\u4e86\u3002\u201d \u3000\u3000\u5b59\u4ef2\u5bff\u7b49\u4eba\u4ff1\u5df2\u60ca\u5f97\u6ee1\u5934\u5927\u6c57\uff0c\u5747\u60f3\uff1a\u201c\u5d14\u79cb\u5c71\u4e3a\u4eba\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u5728\u674e\u95ef\u624b\u4e0b\uff0c\u6574\u65e5\u4ef7\u5e72\u7684\u5c3d\u662f\u4ea1\u547d\u751f\u6daf\uff0c\u5927\u80c6\u5984\u4e3a\u3002\u4ed6\u4e0d\u77e5\u8881\u516c\u5b50\u8fd9\u6761\u547d\u53ef\u6709\u591a\u5c0a\u8d35\u3002\u201d\u53c8\u60f3\uff1a\u201c\u8881\u516c\u5b50\u7ecf\u4ed6\u6559\u4e86\u516b\u5929\uff0c\u6b66\u827a\u679c\u7136\u5927\u6709\u957f\u8fdb\u3002\u201d\u5d14\u79cb\u5c71\u6253\u5f00\u6bbf\u95e8\uff0c\u5728\u8c79\u5b50\u540e\u81c0\u4e0a\u8e22\u4e86\u4e00\u811a\uff0c\u7b11\u9053\uff1a\u201c\u653e\u4f60\u8d70\u5427\uff01\u201d\u90a3\u8c79\u5b50\u76f4\u7a9c\u51fa\u53bb\uff0c\u5ffd\u7136\u5916\u9762\u6709\u4eba\u60ca\u53eb\u8d77\u6765\u3002 \u3000\u3000\u4f17\u4eba\u53ea\u9053\u8c79\u5b50\u5954\u5230\u5916\u9762\u4f24\u4e86\u4eba\uff0c\u5fd9\u51fa\u53bb\u770b\u65f6\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\u3002\u53ea\u89c1\u6ee1\u5c71\u90fd\u662f\u70b9\u70b9\u706b\u5149\uff0c\u706b\u5149\u7167\u8000\u4e0b\u5200\u67aa\u95ea\u95ea\u53d1\u4eae\uff0c\u539f\u6765\u5b98\u5175\u5927\u96c6\uff0c\u56f4\u653b\u5723\u5cf0\u5d82\u6765\u4e86\u3002\u770b\u8fd9\u58f0\u52bf\uff0c\u8981\u8131\u9003\u5b9e\u975e\u5bb9\u6613\u3002\u5728\u5c71\u4e0b\u5b88\u671b\u7684\u515a\u4eba\u60f3\u6765\u5747\u5df2\u88ab\u5bb3\uff0c\u662f\u4ee5\u4e8b\u524d\u6beb\u65e0\u8b66\u62a5\uff0c\u800c\u654c\u5175\u7a81\u7136\u6765\u4e34\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u7b49\u90fd\u662f\u8eab\u7ecf\u767e\u6218\uff0c\u867d\u7136\u5fc3\u60ca\uff0c\u5374\u4e0d\u614c\u4e71\uff0c\u5747\u60f3\uff1a\u201c\u53ef\u60dc\u5c71\u4e0a\u7684\u5f1f\u5144\u90fd\u5df2\u6563\u53bb\u4e86\uff0c\u5426\u5219\u5f53\u5e74\u5728\u5b81\u8fdc\u5927\u6218\uff0c\u5341\u51e0\u4e07\u9791\u5b50\u7cbe\u5175\uff0c\u4e5f\u7ed9\u6211\u4eec\u6253\u5f97\u843d\u8352\u800c\u9003\uff0c\u53c8\u600e\u6015\u4f60\u4eec\u8fd9\u4e9b\u5e7f\u4e1c\u5b98\u5175\uff1f\u201d\u5176\u65f6\u8fbd\u4e1c\u5175\u7cbe\uff1b\u7532\u4e8e\u5929\u4e0b\uff0c\u8881\u5d07\u7115\u7684\u65e7\u90e8\u5411\u6765\u4e0d\u628a\u5357\u65b9\u5b98\u5175\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u5f53\u5373\u53d1\u4ee4\uff1a\u201c\u7f57\u5c06\u519b\uff0c\u4f60\u7387\u9886\u716e\u996d\u3001\u6253\u626b\u3001\u5b88\u7960\u7684\u4f17\u5144\u5f1f\u5230\u4e1c\u8fb9\u5c71\u5934\u653e\u706b\u5450\u558a\uff0c\u4f5c\u4e3a\u7591\u5175\u3002\u201d\u7f57\u5927\u5343\u5e94\u4ee4\u53bb\u4e86\u3002\u5b59\u4ef2\u5bff\u53c8\u9053\uff1a\u201c\u6731\u5c06\u519b\u3001\u502a\u5c06\u519b\uff0c\u4f60\u4eec\u4e24\u4f4d\u5230\u524d\u5c71\u53bb\uff0c\u6bcf\u4eba\u5404\u5c04\u5341\u7bad\uff0c\u6559\u5b98\u5175\u4e0d\u6562\u8fc7\u4efd\u903c\u8fd1\uff0c\u5c04\u540e\u7acb\u523b\u56de\u6765\u3002\u201d\u6731\u502a\u4e8c\u4eba\u5e94\u4ee4\u53bb\u4e86\u3002 \u3000\u3000\u201c\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u5d14\u5927\u54e5\uff0c\u6709\u4e00\u4ef6\u91cd\u4efb\u8981\u4ea4\u6258\u7ed9\u4f60\u3002\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u8981\u6211\u4fdd\u62a4\u627f\u5fd7\uff1f\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u8bf4\u7740\u548c\u5e94\u677e\u4e24\u4eba\u62dc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u4f55\u5429\u5490\uff0c\u81ea\u5f53\u9075\u4ece\uff0c\u5feb\u4f11\u5982\u6b64\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u558a\u58f0\u5927\u4f5c\uff0c\u53c8\u9690\u9690\u6709\u91d1\u9f13\u4e4b\u58f0\uff0c\u542c\u58f0\u97f3\u662f\u5c71\u4e0a\u53d1\u51fa\uff0c\u539f\u6765\u7f57\u5927\u5343\u5df2\u628a\u7960\u4e2d\u7684\u5927\u9f13\u5927\u949f\u62ac\u51fa\u6765\u72c2\u6572\u731b\u6253\uff0c\u6270\u4e71\u654c\u5175\u3002\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u8881\u7763\u5e08\u53ea\u6709\u8fd9\u70b9\u9aa8\u8840\uff0c\u8bf7\u5d14\u5927\u54e5\u62a4\u9001\u4ed6\u8131\u9669\u3002\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u6211\u5fc5\u5c3d\u529b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u6731\u5b89\u56fd\u548c\u502a\u6d69\u5df2\u5c04\u5b8c\u7bad\u56de\u6765\u3002\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u6211\u548c\u6731\u5c06\u519b\u4e00\u8def\uff0c\u4f1a\u9f50\u7f57\u5c06\u519b\u540e\uff0c\u4ece\u4e1c\u8fb9\u51b2\u4e0b\uff0c\u5e94\u5148\u751f\u548c\u502a\u5c06\u519b\u4e00\u8def\uff0c\u4ece\u897f\u8fb9\u51b2\u4e0b\u3002\u6211\u4eec\u5148\u51b2\uff0c\u628a\u654c\u5175\u4e3b\u529b\u5f15\u4f4f\u3002\u5d14\u5927\u54e5\u548c\u627f\u5fd7\u518d\u4ece\u540e\u5c71\u51b2\u4e0b\uff0c\u5927\u5bb6\u65e5\u540e\u5728\u674e\u95ef\u5c06\u519b\u90a3\u91cc\u4f1a\u9f50\u3002\u201d\u4f17\u4eba\u9f50\u58f0\u7b54\u5e94\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5f97\u5e94\u677e\u7b49\u6570\u8f7d\u6559\u517b\uff0c\u8fd9\u65f6\u5206\u522b\uff0c\u5fc3\u4e0b\u96be\u8fc7\uff0c\u8dea\u4e0b\u53bb\u62dc\u4e86\u51e0\u62dc\uff0c\u8bf4\u9053\uff1a\u201c\u5b59\u53d4\u53d4\u3001\u5e94\u53d4\u53d4\u3001\u6731\u53d4\u53d4\u3001\u502a\u53d4\u53d4\u3001\u6211\uff0c\u6211\u2026\u2026\u201d\u5589\u4e2d\u54fd\u4f4f\u4e86\u8bf4\u4e0d\u4e0b\u53bb\u3002\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u4f60\u8ddf\u7740\u5d14\u53d4\u53d4\u53bb\uff0c\u8981\u597d\u597d\u542c\u4ed6\u7684\u8bdd\u3002\u201d\u8881\u627f\u5fd7\u70b9\u5934\u7b54\u5e94\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5c71\u8170\u91cc\u5b98\u5175\u53d1\u558a\uff0c\u5411\u5c71\u4e0a\u51b2\u6765\uff0c\u5e94\u677e\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\u3002\u5d14\u5927\u54e5\uff0c\u4f60\u7a0d\u5f85\u7247\u523b\u518d\u8d70\u3002\u201d\u4f17\u4eba\u5404\u4e3e\u5175\u5203\uff0c\u5411\u4e0b\u51b2\u53bb\u3002\u502a\u6d69\u89c1\u5d14\u79cb\u5c71\u6ca1\u5e26\u5175\u5668\uff0c\u628a\u864e\u53c9\u5411\u4ed6\u63b7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5d14\u5927\u54e5\uff0c\u63a5\u4f4f\u3002\u201d \u3000\u3000\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u8fd8\u662f\u502a\u5144\u81ea\u5df1\u7528\u5427\uff01\u201d\u63a5\u4f4f\u864e\u53c9\u60f3\u63b7\u8fd8\u7ed9\u4ed6\u3002\u502a\u6d69\u5df2\u53bb\u5f97\u8fdc\u4e86\uff0c\u4e8e\u662f\u53f3\u624b\u6301\u53c9\uff0c\u5de6\u624b\u62c9\u7740\u8881\u627f\u5fd7\u5411\u5c71\u540e\u8d70\u53bb\u3002\u53ea\u89c1\u540e\u5c71\u5c71\u5761\u4e0a\u4e5f\u6ee1\u662f\u706b\u628a\uff0c\u5bc6\u5bc6\u5c42\u5c42\u7684\u4e0d\u77e5\u6709\u591a\u5c11\u5b98\u5175\u3002\u5c71\u4e0b\u7bad\u5982\u98de\u8757\uff0c\u4e71\u5c04\u4e0a\u6765\uff0c\u5d14\u79cb\u5c71\u4e8e\u662f\u9000\u56de\u7960\u4e2d\uff0c\u8dd1\u5230\u53a8\u4e0b\uff0c\u63ed\u4e86\u4e24\u4e2a\u9505\u76d6\uff0c\u4e00\u5927\u4e00\u5c0f\uff0c\u81ea\u5df1\u62ff\u4e86\u5927\u7684\uff0c\u628a\u5c0f\u9505\u76d6\u9012\u7ed9\u8881\u627f\u5fd7\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u76fe\u724c\uff0c\u8d70\u5427\uff01\u201d\u4e24\u4eba\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u5411\u9ed1\u6697\u4e2d\u7a9c\u53bb\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\uff0c\u5b98\u5175\u5df2\u53d1\u73b0\u4e24\u4eba\u8e2a\u8ff9\uff0c\u5450\u558a\u58f0\u4e2d\u8ffd\u4e86\u8fc7\u6765\uff0c\u6570\u5341\u652f\u7bad\u540c\u65f6\u5c04\u5230\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u6321\u5728\u8881\u627f\u5fd7\u8eab\u540e\uff0c\u6325\u52a8\u9505\u76d6\uff0c\u4e00\u4e00\u6321\u5f00\u6765\u7bad\uff0c\u53ea\u542c\u5f97\u767b\u767b\u767b\u4e4b\u58f0\u4e0d\u7edd\uff0c\u8bb8\u591a\u7bad\u679d\u90fd\u5c04\u5728\u9505\u76d6\u4e4b\u4e0a\u3002\u4e24\u4eba\u76f4\u95ef\u4e0b\u5c71\u53bb\u3002\u4f17\u5b98\u5175\u4e0a\u6765\u62e6\u963b\uff0c\u5d14\u79cb\u5c71\u4f7f\u5f00\u730e\u864e\u53c9\uff0c\u53c9\u523a\u6746\u6253\uff0c\u970e\u65f6\u95f4\u4f24\u4e86\u5341\u591a\u540d\u5b98\u5175\uff0c\u8881\u627f\u5fd7\u7684\u77ed\u94c1\u67aa\u867d\u7136\u96be\u4ee5\u4f24\u4eba\uff0c\u5374\u4e5f\u5c3d\u53ef\u62a4\u8eab\u3002\u5b98\u5175\u89c1\u662f\u4e2a\u5e7c\u7ae5\uff0c\u4e5f\u4e0d\u600e\u4e48\u7406\u4f1a\u4ed6\u3002\u7247\u523b\u95f4\u4e24\u4eba\u5df2\u5954\u5230\u5c71\u8170\u3002 \u3000\u3000\u521a\u5598\u5f97\u4e00\u53e3\u6c14\uff0c\u5ffd\u7136\u558a\u58f0\u5927\u4f5c\uff0c\u4e00\u80a1\u5b98\u5175\u659c\u523a\u91cc\u51b2\u5230\uff0c\u5f53\u5148\u4e00\u540d\u5343\u6237\u624b\u6301\u5927\u5200\uff0c\u6076\u72e0\u72e0\u7684\u780d\u6765\u3002\u5d14\u79cb\u5c71\u4e3e\u53c9\u4e00\u67b6\uff0c\u89c9\u4ed6\u8182\u529b\u9887\u5927\uff0c\u4e00\u53c9\u201c\u6bd2\u9f99\u51fa\u6d1e\u201d\uff0c\u76f4\u523a\u8fc7\u53bb\u3002\u90a3\u5343\u6237\u4e3e\u5200\u683c\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u5f1f\u5144\u4eec\u4e0a\u554a\uff01\u201d\u5d14\u79cb\u5c71\u4e0d\u613f\u604b\u6218\uff0c\u4e3e\u8d77\u9505\u76d6\u5411\u90a3\u5343\u6237\u9762\u524d\u4e00\u6643\u3002\u90a3\u5343\u6237\u5411\u53f3\u95ea\u907f\uff0c\u5d14\u79cb\u5c71\u5927\u559d\u4e00\u58f0\uff0c\u624b\u8d77\u53c9\u843d\uff0c\u4ece\u4ed6\u80c1\u4e0b\u63d2\u4e86\u8fdb\u53bb\uff0c\u5f85\u62d4\u51fa\u53c9\u6765\uff0c\u8f6c\u5934\u5374\u4e0d\u89c1\u4e86\u8881\u627f\u5fd7\uff0c\u5fc3\u4e2d\u5927\u60ca\uff0c\u53ea\u89c1\u5de6\u8fb9\u4e00\u7fa4\u4eba\u56f4\u7740\u5406\u559d\u3002 \u3000\u3000\u4ed6\u5927\u8e0f\u6b65\u8d76\u8fc7\u53bb\uff0c\u633a\u53c9\u4e71\u6233\uff0c\u5b98\u5175\u7eb7\u7eb7\u95ea\u907f\uff0c\u5954\u5230\u8fd1\u5904\uff0c\u679c\u89c1\u8881\u627f\u5fd7\u7ed9\u56f4\u5728\u5793\u5fc3\uff0c\u624b\u4e2d\u77ed\u94c1\u67aa\u5df2\u88ab\u6253\u843d\uff0c\u6b63\u5c55\u5f00\u4f0f\u864e\u638c\u6cd5\u548c\u4e09\u540d\u5b98\u5175\u5bf9\u654c\uff0c\u6bd5\u7adf\u5e74\u5e7c\u529b\u5f31\uff0c\u638c\u6cd5\u53c8\u662f\u521d\u5b66\u672a\u719f\uff0c\u5de6\u652f\u53f3\u7ecc\uff0c\u60c5\u52bf\u5371\u6025\u3002\u5d14\u79cb\u5c71\u66f4\u4e0d\u6253\u8bdd.\u5237\u5237\u4e24\u53c9\uff0c\u523a\u5012\u4e24\u540d\u5b98\u5175\uff0c\u5de6\u624b\u62c9\u4e86\u8881\u627f\u5fd7\u4fbf\u8d70\u3002\u5b98\u5175\u5927\u53eb\u8ffd\u6765\uff0c\u5d14\u79cb\u5c71\u9661\u7136\u56de\u5934\uff0c\u5237\u5237\u4e24\u53c9\uff0c\u523a\u5012\u4e86\u8ffd\u5f97\u6700\u8fd1\u7684\u4e24\u540d\u5b98\u5175\uff0c\u518d\u8e0f\u4e0a\u4e00\u6b65\uff0c\u53c9\u6746\u6284\u8d77\uff0c\u628a\u4e00\u540d\u5b98\u5175\u6311\u4e86\u8d77\u6765\uff0c\u76f4\u63bc\u5728\u5c71\u77f3\u4e4b\u4e0a\u3002\u90a3\u5175\u60e8\u53eb\u4e00\u58f0\uff0c\u7acb\u65f6\u8dcc\u6b7b\u3002 \u3000\u3000\u4f17\u5b98\u5175\u89c1\u4ed6\u5982\u6b64\u52c7\u608d\uff0c\u5413\u5f97\u6b62\u6b65\u4e0d\u8ffd\u3002\u5d14\u79cb\u5c71\u628a\u8881\u627f\u5fd7\u631f\u5728\u80c1\u4e0b\uff0c\u5c55\u5f00\u8f7b\u529f\u63d0\u7eb5\u672f\uff0c\u76f4\u5411\u9ed1\u6697\u65e0\u4eba\u5904\u7a9c\u53bb\uff0c\u4e0d\u4e00\u4f1a\u4fbf\u548c\u4f17\u5b98\u5175\u79bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u653e\u4e0b\u8881\u627f\u5fd7\uff0c\u95ee\u9053\uff1a\u201c\u6ca1\u53d7\u4f24\u5427\uff1f\u201d\u8881\u627f\u5fd7\u4e3e\u624b\u5f80\u8138\u4e0a\u62b9\u6c57.\u53ea\u89c9\u7c98\u817b\u817b\u7684\uff0c\u6708\u5149\u4e0b\u4e00\u770b\uff0c\u6ee1\u624b\u662f\u8840\uff0c\u770b\u5d14\u79cb\u5c71\u65f6\uff0c\u8138\u4e0a\u3001\u624b\u4e0a\u3001\u8863\u4e0a\uff0c\u5c3d\u662f\u8840\u8ff9\u6591\u6591\uff0c\u8bf4\u9053\uff1a\u201c\u5d14\u53d4\u53d4\uff0c\u8840\u2026\u2026\u8840\u2026\u2026\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u662f\u654c\u4eba\u7684\u8840\uff0c\u4f60\u8eab\u4e0a\u6709\u54ea\u91cc\u75db\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff0c\u201c\u6ca1\u6709\u3002\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u518d\u8d70\uff01\u201d \u3000\u3000\u4e24\u4eba\u77ee\u4e86\u8eab\u5b50\uff0c\u5728\u6811\u4e1b\u4e2d\u5411\u4e0b\u94bb\u884c\uff0c\u8d70\u4e86\u5c0f\u534a\u4e2a\u65f6\u8fb0\uff0c\u6811\u4e1b\u5c06\u5b8c\uff0c\u5d14\u79cb\u5c71\u63a2\u5934\u4e00\u671b\uff0c\u89c1\u5c71\u4e0b\u706b\u628a\u660e\u4eae\uff0c\u6570\u767e\u540d\u5b98\u5175\u5b88\u7740\uff0c\u6084\u58f0\u9053\uff1a\u201c\u4e0d\u80fd\u4e0b\u53bb\uff0c\u540e\u9000\u3002\u201d\u4e24\u4eba\u56de\u8eab\u8d70\u4e86\u6570\u767e\u6b65\uff0c\u89c1\u6709\u4e00\u4e2a\u5c71\u6d1e\uff0c\u6d1e\u524d\u751f\u7740\u4e00\u6392\u77ee\u6811\uff0c\u4fbf\u94bb\u8fdb\u6d1e\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6bd5\u7adf\u5e74\u5e7c\uff0c\u867d\u7136\u8eab\u5728\u9669\u5730\uff0c\u4f46\u75b2\u7d2f\u4e4b\u4f59\uff0c\u8eba\u4e0b\u4e0d\u4e45\u4fbf\u7761\u7740\u4e86\u3002\u5d14\u79cb\u5c71\u628a\u4ed6\u8f7b\u8f7b\u62b1\u8d77\uff0c\u501a\u5728\u81ea\u5df1\u6000\u91cc\uff0c\u4fa7\u8033\u9759\u542c\u3002\u53ea\u542c\u547c\u558a\u4e4b\u58f0\u8fde\u7eed\u4e0d\u65ad\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u773c\u89c1\u5c71\u9876\u9ed1\u70df\u5192\u8d77\uff0c\u7ea2\u5149\u51b2\u5929\uff0c\u60f3\u662f\u8881\u5d07\u7115\u7684\u7960\u5802\u5df2\u7ed9\u5b98\u5175\u70e7\u4e86\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u4e2a\u591a\u65f6\u8fb0\uff0c\u542c\u5f97\u5c71\u4e0a\u5439\u8d77\u53f7\u89d2\uff0c\u5d14\u79cb\u5c71\u8ddf\u5b98\u5175\u5927\u5c0f\u6253\u8fc7\u6570\u5341\u4ed7\uff0c\u77e5\u662f\u6536\u961f\u4e0b\u5c71\u7684\u53f7\u4ee4\uff0c\u4e0d\u4e00\u4f1a\uff0c\u5927\u961f\u4eba\u9a6c\u58f0\u7ecf\u8eab\u65c1\u8fc7\u53bb\uff0c\u7edc\u7ece\u4e0d\u7edd\uff0c\u539f\u6765\u8fd9\u5c71\u6d1e\u5c31\u5728\u5b98\u5175\u4e0b\u5c71\u9053\u8def\u4e4b\u65c1\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u4f1a\uff0c\u5ffd\u542c\u5916\u9762\u6811\u4e1b\u4e2d\u6709\u4eba\u5750\u4e86\u4e0b\u6765\uff0c\u5d14\u79cb\u5c71\u53f3\u624b\u63d0\u8d77\u94a2\u53c9\uff0c\u5de6\u624b\u653e\u5728\u8881\u627f\u5fd7\u5634\u8fb9\uff0c\u9632\u4ed6\u5728\u68a6\u4e2d\u53d1\u51fa\u58f0\u54cd\uff0c\u51dd\u795e\u9759\u542c\uff0c\u53ea\u542c\u4e00\u4eba\u559d\u9053\uff1a\u201c\u90a3\u59d3\u8881\u7684\u9006\u8d3c\u7559\u4e0b\u4e00\u4e2a\u513f\u5b50\uff0c\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u8fd9\u53e5\u8bdd\u58f0\u97f3\u5f88\u54cd\uff0c\u767b\u65f6\u628a\u8881\u627f\u5fd7\u5435\u9192\u3002\u5d14\u79cb\u5c71\u5de6\u624b\u8f7b\u8f7b\u6309\u4f4f\u4ed6\u5634\u3002 \u3000\u3000\u542c\u5f97\u90a3\u4eba\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u4e0d\u8bf4\u6211\u5148\u780d\u65ad\u4f60\u4e00\u6761\u817f\u3002\u201d\u4e00\u4e2a\u58f0\u97f3\u9a82\u9053\uff1a\u201c\u4f60\u780d\u5c31\u780d\uff01\u6211\u4eec\u5728\u8fb9\u5ead\u4e0a\u4e00\u5200\u4e00\u67aa\u6253\u9791\u5b50\uff0c\u5c82\u6765\u6015\u4f60\uff1f\u201d\u542c\u53e3\u97f3\u6b63\u662f\u5e94\u677e\u7684\u58f0\u97f3\u3002\u8881\u627f\u5fd7\u6084\u58f0\u9053\uff1a\u201c\u5e94\u53d4\u53d4\uff01\u201d\u90a3\u4eba\u53c8\u9a82\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u8bf4\uff1f\u201d\u5e94\u677e\u5478\u7684\u4e00\u58f0\uff0c\u4f3c\u4e4e\u4e00\u53e3\u553e\u6cab\u5410\u5411\u4ed6\u7684\u8138\u4e0a\uff0c\u63a5\u7740\u4e00\u58f0\u60e8\u53eb\uff0c\u4f3c\u4e4e\u5df2\u88ab\u4ed6\u4e00\u5200\u780d\u4f24\u3002 \u3000\u3000\u8881\u627f\u5fd7\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u7528\u529b\u4e00\u6323\uff0c\u6323\u8131\u4e86\u5d14\u79cb\u5c71\u62c9\u4f4f\u4ed6\u7684\u624b\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u5e94\u53d4\u53d4\uff01\u201d\u76f4\u7a9c\u51fa\u53bb\u3002\u706b\u5149\u4e2d\u89c1\u4e00\u4eba\u6b63\u63d0\u5200\u5411\u6454\u8dcc\u5728\u5730\u7684\u5e94\u677e\u780d\u843d\uff0c\u4ed6\u4eec\u8eab\u7eb5\u4e0a\uff0c\u65bd\u5c55\u4f0f\u864e\u638c\u4e2d\u7684\u201c\u5de6\u51fb\u53f3\u64d2\u201d\u4e4b\u6cd5\uff0c\u4e00\u62f3\u6b63\u4e2d\u90a3\u4eba\u53f3\u773c\u3002\u90a3\u4eba\u53ea\u89c9\u773c\u4e2d\u91d1\u661f\u76f4\u5192\uff0c\u624b\u8155\u4e00\u75db\u3002\u4e00\u67c4\u5200\u5df2\u88ab\u593a\u53bb\u3002\u8881\u627f\u5fd7\u987a\u624b\u4e00\u5200\uff0c\u780d\u5728\u4ed6\u80a9\u5934\uff0c\u867d\u7136\u529b\u5f31\uff0c\u6ca1\u628a\u4e00\u6761\u80a9\u8180\u5378\u4e0b\uff0c\u4e5f\u5df2\u75db\u5f97\u4ed6\u602a\u58f0\u5927\u53eb\u3002 \u3000\u3000\u4f17\u5b98\u5175\u51fa\u5176\u4e0d\u610f\uff0c\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u767b\u65f6\u9003\u6563\uff0c\u5f85\u5f97\u770b\u6e05\u695a\u53ea\u662f\u4e00\u4e2a\u5e7c\u7ae5\uff0c\u5f53\u5373\u56de\u8f6c\u8eab\u7c73\uff0c\u5200\u67aa\u9f50\u4e0b\uff0c\u773c\u89c1\u5c31\u8981\u628a\u4ed6\u780d\u6210\u788e\u5757\u3002 \u3000\u3000\u7a81\u7136\u706b\u5149\u4e2d\u4e00\u67c4\u94a2\u53c9\u98de\u51fa\uff0c\u5404\u5b98\u5175\u53ea\u89c9\u864e\u53e3\u5267\u9707\uff0c\u5175\u5203\u7eb7\u7eb7\u79bb\u624b\u3002\u5d14\u79cb\u5c71\u4e00\u628a\u6293\u4f4f\u8881\u627f\u5fd7\u540e\u5fc3\uff0c\u76f4\u7eb5\u51fa\u53bb\u3002\u4f17\u5b98\u5175\u653e\u7bad\u65f6\uff0c\u4e24\u4eba\u65e9\u5df2\u76f4\u5954\u4e0b\u5c71\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u8fd9\u4e00\u9732\u5f62\uff0c\u5949\u592a\u76d1\u66f9\u5316\u6df3\u4e4b\u547d\u524d\u6765\u641c\u6355\u7684\u4e1c\u5382\u756a\u5b50\u4e4b\u4e2d\uff0c\u4fbf\u6709\u56db\u540d\u597d\u624b\u8ddf\u8e2a\u4e0b\u6765\u3002\u4f46\u89c1\u4ed6\u80c1\u4e0b\u631f\u7740\u4e00\u4e2a\u5e7c\u7ae5\uff0c\u4f46\u4ecd\u662f\u7eb5\u8df3\u5982\u98de\uff0c\u8fc5\u6377\u5f02\u5e38\uff0c\u4e00\u540d\u756a\u5b50\u53d6\u51fa\u4e00\u652f\u7529\u624b\u7bad\uff0c\u4f7f\u8db3\u624b\u52b2\uff0c\u63b7\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u542c\u5f97\u8111\u540e\u751f\u98ce\uff0c\u7acb\u5373\u77ee\u8eab\uff0c\u90a3\u652f\u7bad\u4ece\u5934\u9876\u98de\u4e86\u8fc7\u53bb\uff0c\u5c31\u8fd9\u4e48\u505c\u5f97\u4e00\u505c\uff0c\u53e6\u4e00\u4eba\u5df2\u6263\u4f4f\u4e09\u652f\u94a2\u9556\uff0c\u8fde\u73e0\u53d1\u51fa\u3002\u5d14\u79cb\u5c71\u628a\u8881\u627f\u5fd7\u5f80\u5730\u4e0b\u4e00\u653e\uff0c\u5de6\u624b\u4e00\u6284\uff0c\u63a5\u4f4f\u4e24\u652f\u94a2\u9556\uff0c\u907f\u5f00\u4e86\u7b2c\u4e09\u652f\uff0c\u6b63\u5f85\u53d1\u56de\uff0c\u654c\u4eba\u7684\u8896\u7bad\u3001\u98de\u8757\u77f3\u5df2\u7eb7\u7eb7\u6253\u6765\u3002\u5d14\u79cb\u5c71\u624b\u63a5\u53c9\u62e8\uff0c\u95ea\u907f\u6697\u5668\uff0c\u62c9\u7740\u8881\u627f\u5fd7\u5411\u5c71\u4e0b\u9003\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u4eec\u79bb\u5b98\u5175\u5927\u961f\u5df2\u8fdc\uff0c\u53ef\u662f\u56db\u540d\u756a\u5b50\u59cb\u7ec8\u7d27\u8feb\u4e0d\u820d\u3002\u5176\u4e2d\u4e00\u4eba\u5927\u53eb\u9053\uff1a\u201c\u8bc6\u76f8\u7684\uff0c\u4f60\u6487\u4e0b\u5175\u5668\uff0c\u4e56\u4e56\u7684\u8ddf\u8001\u5b50\u56de\u53bb\uff0c\u5c31\u8ba9\u4f60\u5c11\u5403\u4e9b\u82e6\u5934\u3002\u201d\u5d14\u79cb\u5c71\u6697\u6697\u628a\u94a2\u9556\u4ea4\u5230\u53f3\u624b\uff0c\u5f85\u4ed6\u8ffd\u8fd1\uff0c\u7a81\u7136\u4e24\u9556\u4e00\u4e0a\u4e00\u4e0b\uff0c\u75be\u5982\u95e8\u7535\u822c\u5c04\u4e86\u51fa\u53bb\u3002\u90a3\u4eba\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u817f\u4e0a\u4e00\u9556\u65e9\u7740\uff0c\u767b\u65f6\u683d\u5012\u3002\u5176\u4f59\u4e09\u4eba\u7565\u4e00\u505c\u987f\uff0c\u53c8\u5206\u5934\u63a9\u6765\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u89c1\u654c\u4eba\u8ffd\u8fd1\uff0c\u5bf9\u8881\u627f\u5fd7\u8bf4\uff1a\u201c\u6211\u53bb\u593a\u90a3\u4eba\u7684\u5200\u6765\u7ed9\u4f60\u3002\u201d\u628a\u864e\u53c9\u5f80\u5730\u4e0b\u4e00\u63d2\uff0c\u53cd\u5954\u8fce\u654c\u3002\u90a3\u4f7f\u53cc\u5200\u7684\u4e00\u62db\u201c\u4e91\u9f99\u4e09\u73b0\u201d\uff0c\u5237\u5237\u5237\u8fde\u73af\u4e09\u62db\uff0c\u5d14\u79cb\u5c71\u7adf\u62a2\u4e0d\u5165\u53bb\uff0c\u53e6\u4e00\u4e2a\u4f7f\u94c1\u97ad\u7684\u5374\u5df2\u6b3a\u8fd1\u8881\u627f\u5fd7\u8eab\u65c1\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u89c1\u4e00\u65f6\u593a\u4e0d\u4e0b\u654c\u5203\uff0c\u800c\u90a3\u8fb9\u8881\u627f\u5fd7\u5374\u5df1\u5371\u6025\uff0c\u84e6\u5730\u56de\u8eab\uff0c\u6ef4\u6e9c\u6e9c\u4e00\u4e2a\u65cb\u8eab\uff0c\u5df2\u6b3a\u5230\u90a3\u4f7f\u94c1\u97ad\u7684\u4eba\u80cc\u540e\uff0c\u4e00\u62db\u201c\u91d1\u9f99\u63a2\u722a\u201d\uff0c\u4e94\u6307\u5411\u4ed6\u540e\u5fc3\u6293\u53bb\u3002\u90a3\u4eba\u94c1\u97ad\u6b63\u5411\u8881\u627f\u5fd7\u540e\u5fc3\u626b\u53bb\uff0c\u5ffd\u89c9\u8eab\u540e\u6765\u4e86\u654c\u4eba\uff0c\u5355\u97ad\u4e00\u7acb\uff0c\u8f6c\u8fc7\u8eab\u6765\u3002\u5d14\u79cb\u5c71\u4ee5\u5feb\u6253\u6162\uff0c\u51fa\u624b\u8fc5\u6377\u5f02\u5e38\uff0c\u90a3\u4eba\u62db\u67b6\u4e0d\u4f4f\uff0c\u53ea\u5f97\u8fde\u8fde\u5012\u9000\u3002\u8881\u627f\u5fd7\u5ffd\u5730\u8e0f\u6b65\u4e0a\u524d\uff0c\u98de\u8d77\u4e00\u817f\uff0c\u8e22\u4e2d\u4e86\u4ed6\u540e\u81c0\u3002\u90a3\u4eba\u6012\u543c\u4e00\u58f0\uff0c\u6a2a\u97ad\u53cd\u51fb\uff0c\u7a81\u89c9\u638c\u5fc3\u4e00\u9707\uff0c\u97ad\u68a2\u5df2\u88ab\u5d14\u79cb\u5c71\u6293\u4f4f\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u4f7f\u53cc\u5200\u7684\u4e0e\u4f7f\u9b3c\u5934\u5200\u7684\u4e09\u4ef6\u5175\u5203\u540c\u65f6\u5411\u5d14\u79cb\u5c71\u80cc\u540e\u6253\u6765\uff0c\u8fd9\u65f6\u817f\u4e0a\u4e2d\u9556\u90a3\u4eba\u4e5f\u5df2\u722c\u8d77\uff0c\u633a\u67aa\u5411\u8881\u627f\u5fd7\u5de6\u80c1\u523a\u53bb\u3002 \u3000\u3000\u6b64\u65f6\u5371\u673a\u56db\u4f0f\uff0c\u597d\u4e2a\u5d14\u79cb\u5c71\u3002\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u7d27\u6025\u5173\u5934\uff0c\u7adf\u7136\u4e8e\u8f7b\u91cd\u7f13\u6025\u6599\u5f97\u4e1d\u6beb\u65e0\u8bef\uff0c\u542d\u58f0\u5410\u6c14\uff0c\u563f\u7684\u4e00\u58f0\uff0c\u53f3\u638c\u4e00\u62db\u201c\u964d\u9f99\u4f0f\u864e\u201d\uff0c\u6b63\u6253\u5728\u90a3\u4f7f\u94c1\u97ad\u7684\u4eba\u80f8\u53e3\u3002\u8fd9\u4e00\u62db\u662f\u4f0f\u864e\u638c\u4e2d\u4e09\u5927\u7edd\u62db\u4e4b\u4e00\uff0c\u90a3\u4eba\u5982\u4f55\u62b5\u6321\u5f97\u4f4f\uff0c\u5168\u8eab\u817e\u7a7a\uff0c\u5411\u90a3\u817f\u4e0a\u4e2d\u9556\u7684\u4eba\u67aa\u5c16\u4e0a\u4ef0\u8dcc\u4e0b\u53bb\u3002\u5e78\u5f97\u90a3\u4eba\u6025\u5fd9\u7f29\u67aa\uff0c\u8fd9\u624d\u817e\u7684\u4e00\u58f0\uff0c\u8dcc\u5728\u5730\u4e0b\uff0c\u6ca1\u7ed9\u67aa\u5c16\u7a7f\u4e2a\u900f\u660e\u7a9f\u7abf\u3002\u5d14\u79cb\u5c71\u5355\u97ad\u593a\u5230\uff0c\u53cd\u62a1\u8fc7\u6765\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5c06\u4e09\u628a\u5200\u540c\u65f6\u67b6\u5f00\uff0c\u7eb5\u8fc7\u53bb\u62c9\u4e86\u8881\u627f\u5fd7\u5411\u5c71\u4e0b\u7a9c\u53bb\u3002 \u3000\u3000\u56db\u540d\u756a\u5b50\u89c1\u5d14\u79cb\u5c71\u970e\u65f6\u4e4b\u95f4\u593a\u97ad\u4f7f\u638c\uff0c\u540c\u65f6\u62c6\u5f00\u4e86\u56db\u4eba\u7684\u8fdb\u88ad\uff0c\u6b66\u529f\u7cbe\u5f3a\uff0c\u4e0d\u6562\u518d\u8ffd\uff0c\u7ad9\u5b9a\u8eab\u5b50\uff0c\u7eb7\u7eb7\u53d1\u51fa\u6697\u5668\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u9ed1\u6697\u4e4b\u4e2d\u542c\u5f97\u55d6\u55d6\u4e4b\u58f0\u4e0d\u7edd\uff0c\u5fd9\u628a\u8881\u627f\u5fd7\u62c9\u5728\u80f8\u524d\uff0c\u7a9c\u9ad8\u8dc3\u4f4e\u7684\u95ea\u907f\uff0c\u4f46\u6bd5\u7adf\u624b\u4e2d\u62b1\u4e86\u4eba\uff0c\u7eb5\u8df3\u4e0d\u4fbf\uff0c\u907f\u5f00\u4e86\u53f3\u8fb9\u6253\u6765\u7684\u4e09\u679a\u83e9\u63d0\u5b50\uff0c\u53ea\u89c9\u5de6\u817f\u4e00\u75db\uff0c\u5df2\u4e2d\u4e86\u6697\u5668\u3002\u4f24\u5904\u521a\u521a\u75db\u8fc7\uff0c\u7acb\u5373\u53d1\u75d2\uff0c\u5fc3\u4e2d\u5927\u60ca\uff0c\u77e5\u9053\u7bad\u4e0a\u6709\u6bd2\uff0c\u4e0d\u6562\u505c\u7559\uff0c\u6025\u5411\u5c71\u4e0b\u5954\u9003\uff0c\u4f46\u8fd9\u4e00\u6765\uff0c\u6bd2\u53d1\u66f4\u5feb\uff0c\u518d\u8dd1\u5f97\u51e0\u6b65\uff0c\u5de6\u817f\u4e00\u9635\u9ebb\u75f9\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8dcc\u5012\u5728\u5730\u3002\u8881\u627f\u5fd7\u5927\u60ca\uff0c\u6025\u53eb\uff1a\u201c\u5d14\u53d4\u53d4\u3002\u201d\u56db\u540d\u756a\u5b50\u89c1\u4ed6\u8dcc\u5012\uff0c\u9ad8\u547c\u5927\u53eb\uff0c\u968f\u540e\u8d76\u6765\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u5feb\u8d70\uff0c\u5feb\u8d70\uff0c\u6211\u6321\u4f4f\u4ed6\u4eec\u3002\u201d\u8881\u627f\u5fd7\u53cc\u638c\u4e00\u9519\uff0c\u8dc3\u5230\u5d14\u79cb\u5c71\u8eab\u540e\uff0c\u9884\u5907\u8fce\u654c\u3002\u5d14\u79cb\u5c71\u5fc3\u60f3\uff1a\u201c\u51ed\u4f60\u8fd9\u70b9\u529f\u592b\uff0c\u5c45\u7136\u60f3\u4fdd\u62a4\u6211\u3002\u201d \u3000\u3000\u4f46\u5fc3\u4e2d\u4e5f\u81ea\u611f\u52a8\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u654c\u4eba\u5df2\u7ecf\u8ffd\u5230\uff0c\u4e24\u4e2a\u4f7f\u5200\u7684\u5954\u5728\u6700\u524d\u3002\u4f7f\u9b3c\u5934\u5200\u7684\u4eba\u60f3\u751f\u64d2\u6d3b\u6349\uff0c\u7ffb\u8f6c\u5200\u80cc\uff0c\u5411\u8881\u627f\u5fd7\u8db3\u8e1d\u4e0a\u51fb\u6765\u3002\u8881\u627f\u5fd7\u4e00\u8dc3\u907f\u8fc7\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u6491\u8d77\u53f3\u817f\uff0c\u534a\u8dea\u5728\u5730\uff0c\u624b\u4e2d\u94c1\u97ad\u7b14\u76f4\u7684\u5411\u4f7f\u53cc\u5200\u7684\u63b7\u53bb\u3002\u90a3\u4eba\u5f85\u8981\u907f\u8ba9\uff0c\u5df2\u7136\u4e0d\u53ca\uff0c\u94c1\u97ad\u4ece\u4ed6\u989d\u5934\u4e0a\u63d2\u4e86\u8fdb\u53bb\u3002\u4f7f\u9b3c\u5934\u5200\u7684\u4eba\u4e00\u5446\uff0c\u5d14\u79cb\u5c71\u548c\u8eab\u6251\u4e0a\uff0c\u5341\u6307\u7d27\u7d27\u94b3\u4f4f\u4ed6\u5589\u5499\uff0c\u90a3\u4eba\u6325\u5200\u5411\u5d14\u79cb\u5c71\u81c2\u4e0a\u780d\u6765\uff0c\u5d14\u79cb\u5c71\u624b\u4e0a\u52a0\u52b2\uff0c\u90a3\u4eba\u8fd9\u4e00\u5200\u867d\u7136\u780d\u4e2d\uff0c\u5374\u5df2\u65e0\u529b\uff0c\u7247\u523b\u95f4\u4fbf\u5373\u6c14\u7edd\u800c\u6b7b\u3002\u5176\u4f59\u4e24\u4eba\u672c\u5df2\u53d7\u4f24\uff0c\u53c8\u89c1\u654c\u4eba\u5982\u6b64\u51f6\u608d\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u54ea\u91cc\u8fd8\u6562\u6765\u8ffd\uff0c\u8fde\u5fd9\u9003\u56de\u3002\u5d14\u79cb\u5c71\u81c2\u4e0a\u6d41\u8840\uff0c\u5e78\u597d\u4f24\u52bf\u4e0d\u91cd\uff0c\u4f46\u5de6\u817f\u5df2\u5168\u65e0\u77e5\u89c9\u3002 \u3000\u3000\u4ed6\u54ac\u7d27\u7259\u5173\uff0c\u62fe\u8d77\u5200\u6491\u5728\u5730\u4e0b\uff0c\u5de6\u624b\u63e1\u4f4f\uff0c\u7ad9\u4e86\u8d77\u6765\u3002\u8fd9\u65f6\u654c\u4eba\u867d\u5df2\u9003\u8d70\uff0c\u4f46\u4e0d\u4e45\u5b9a\u7136\u53ec\u63f4\u518d\u6765\uff0c\u5f53\u5730\u51b3\u8ba1\u4e0d\u80fd\u591a\u7559\uff0c\u53ea\u5f97\u5de6\u817f\u865a\u60ac\uff0c\u5411\u5c71\u4e0b\u8d70\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7ad9\u5728\u4ed6\u53f3\u8fb9\uff0c\u8ba9\u4ed6\u53f3\u624b\u642d\u5728\u81ea\u5df1\u80a9\u4e0a\uff0c\u4e00\u8df7\u4e00\u62d0\u7684\u5411\u524d\u8d76\u8def\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u9635\uff0c\u5d14\u79cb\u5c71\u5de6\u817f\u6bd2\u6027\u5411\u4e0a\u5ef6\u4f38\uff0c\u7275\u52a8\u5de6\u624b\u4e5f\u6e10\u6e10\u65e0\u529b\uff0c\u53ea\u5f97\u4ee5\u53f3\u624b\u652f\u6491\u3002\u8881\u627f\u5fd7\u53ea\u89c9\u80a9\u5934\u8d8a\u6765\u8d8a\u91cd\uff0c\u4f46\u4ed6\u4e00\u58f0\u4e0d\u54fc\uff0c\u594b\u529b\u6276\u6301\u7740\u5d14\u79cb\u5c71\u524d\u884c\u3002 \u3000\u3000\u53c8\u8d70\u4e00\u9635\uff0c\u4e24\u4eba\u5b9e\u5df2\u7b4b\u75b2\u529b\u5c3d\u3002\u8881\u627f\u5fd7\u5ffd\u89c1\u5c71\u8fb9\u6709\u95f4\u519c\u820d\uff0c\u8bf4\u9053\uff1a\u201c\u5d14\u53d4\u53d4\uff0c\u524d\u9762\u6709\u4eba\u5bb6\uff0c\u54b1\u4eec\u8fdb\u53bb\u8eb2\u4e00\u8eb2\u3002\u4f60\u518d\u71ac\u4e00\u4e0b\u5427\uff01\u201d\u5d14\u79cb\u5c71\u70b9\u70b9\u5934\uff0c\u52c9\u529b\u62d6\u7740\u534a\u8fb9\u8eab\u5b50\u5411\u524d\u6328\u53bb\uff0c\u5230\u5f97\u95e8\u8fb9\uff0c\u5168\u8eab\u8131\u529b\uff0c\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u60ca\uff0c\u4fef\u8eab\u8fde\u53eb\uff1a\u201c\u5d14\u53d4\u53d4\uff01\u201d\u90a3\u519c\u820d\u7684\u95e8\u5440\u7684\u4e00\u58f0\u5f00\u4e86\uff0c\u51fa\u6765\u4e00\u4e2a\u4e2d\u5e74\u5987\u4eba\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u5a18\uff0c\u6211\u4eec\u9047\u5230\u5b98\u5175\u3002\u6211\u53d4\u53d4\u53d7\u4e86\u4f24\uff0c\u6c42\u6c42\u4f60\u8ba9\u6211\u4eec\u501f\u5bbf\u4e00\u665a\u3002\u201d \u3000\u3000\u90a3\u519c\u5987\u53eb\u51fa\u4e00\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5e74\u6765\uff0c\u547d\u4ed6\u5e2e\u7740\u628a\u5d14\u79cb\u5c71\u6276\u8fdb\u53bb\uff0c\u62fc\u8d77\u4e09\u6761\u957f\u51f3\uff0c\u8ba9\u4ed6\u8eba\u4e0b\u3002\u5d14\u79cb\u5c71\u4e2d\u6bd2\u751a\u6df1\uff0c\u4e8f\u5f97\u6b66\u529f\u7cbe\u6e5b\uff0c\u5fc3\u667a\u5012\u6ca1\u660f\u4e71\uff0c\u53eb\u8881\u627f\u5fd7\u628a\u6cb9\u706f\u79fb\u8fd1\u5de6\u817f\u5904\u5bdf\u770b\u3002\u4e24\u4eba\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u539f\u6765\u90a3\u5de6\u817f\u5df2\u80bf\u5927\u4e86\u51e0\u4e4e\u4e00\u534a\uff0c\u7d2b\u4e2d\u5e26\u9ed1\uff0c\u5341\u5206\u6015\u4eba\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u8bf7\u90a3\u519c\u5bb6\u5c11\u5e74\u88f9\u597d\u4ed6\u81c2\u4e0a\u4f24\u53e3\uff0c\u518d\u7528\u5e03\u6761\u5728\u4ed6\u5de6\u817f\u817f\u6839\u5904\u7528\u529b\u7f20\u7d27\uff0c\u4ee5\u9632\u6bd2\u6c14\u653b\u5fc3\uff0c\u7136\u540e\u6293\u4f4f\u7bad\u7fbd\uff0c\u62d4\u4e86\u51fa\u6765\uff0c\u8ddf\u7740\u6d41\u51fa\u6765\u7684\u90fd\u662f\u9ed1\u8840\u3002\u5d14\u79cb\u5c71\u4fef\u8eab\u8981\u53bb\u542e\u5438\u6bd2\u8840\uff0c\u4f46\u817f\u5b50\u80bf\u5927\uff0c\u5634\u5df4\u591f\u4e0d\u5230\u3002\u8881\u627f\u5fd7\u4fef\u4e0b\u8eab\u53bb\uff0c\u628a\u4f24\u53e3\u4e2d\u7684\u9ed1\u8840\u4e00\u53e3\u53e3\u7684\u5438\u4e86\u51fa\u6765\uff0c\u5410\u5728\u5730\u4e0b\uff0c\u5438\u4e86\u4e09\u56db\u5341\u53e3\u4e4b\u540e\uff0c\u8840\u8272\u624d\u6e10\u6e10\u53d8\u7ea2\u3002\u5d14\u79cb\u5c71\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u6bd2\u836f\u603b\u7b97\u8fd8\u4e0d\u662f\u6700\u5389\u5bb3\u7684\u90a3\u79cd\u3002\u4f60\u5feb\u6f31\u53e3\u3002\u201d \u3000\u3000\u90a3\u519c\u5987\u5728\u65c1\u77a7\u7740\uff0c\u4e0d\u4f4f\u5ff5\u4f5b\u3002 \u3000\u3000\u6b21\u65e5\u5348\u540e\uff0c\u90a3\u5c11\u5e74\u62a5\u8bf4\u5b98\u5175\u5df2\u7ecf\u9000\u5c3d\u3002\u5d14\u79cb\u5c71\u817f\u80bf\u6e10\u6d88\uff0c\u4f46\u5168\u8eab\u53d1\u70e7\uff0c\u80e1\u8a00\u4e71\u8bed\u8d77\u6765\u3002\u8881\u627f\u5fd7\u6ca1\u4e86\u4e3b\u610f\uff0c\u53ea\u662f\u6025\u5f97\u8981\u54ed\u3002 \u3000\u3000\u90a3\u519c\u5987\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5b98\uff0c\u6211\u77a7\u4f60\u53d4\u53d4\u7684\u6bd2\u6c14\u8fd8\u6ca1\u53bb\u5c3d\uff0c\u603b\u5f97\u5230\u9547\u4e0a\u8bf7\u5927\u592b\u77a7\u77a7\u624d\u597d\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u53ef\u662f\u600e\u4e48\u53bb\uff1f\u201d\u90a3\u519c\u5987\u5fc3\u80a0\u751a\u597d\uff0c\u501f\u4e86\u4e00\u8f86\u725b\u8f66\uff0c\u547d\u5c11\u5e74\u9001\u4e86\u4ed6\u4eec\u5230\u9547\u4e0a\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u628a\u4ed6\u4eec\u9001\u5165\u5ba2\u5e97\u4e4b\u540e\uff0c\u5f84\u81ea\u53bb\u4e86\u3002\u5d14\u8881\u4e24\u4eba\u51fa\u6765\u65f6\u8eab\u4e0a\u90fd\u6ca1\u5e26\u94b1\uff0c\u8881\u627f\u5fd7\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u671b\u7740\u5e8a\u4e0a\u660f\u8ff7\u4e0d\u9192\u7684\u5d14\u79cb\u5c71\u53d1\u6101\u3002\u5e97\u4f19\u6765\u95ee\u5403\u751a\u4e48\u4e1c\u897f\uff0c\u8881\u627f\u5fd7\u7b54\u4e0d\u4e0a\u6765\uff0c\u53ea\u597d\u63a8\u8bf4\u4e0d\u997f\uff0c\u4e00\u4e2a\u4eba\u5750\u7740\u60f3\u54ed\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u5d14\u79cb\u5c71\u7ec8\u4e8e\u9192\u6765\uff0c\u8881\u627f\u5fd7\u5fd9\u95ee\u4ed6\u600e\u4e48\u529e\u3002\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u5e26\u7740\u751a\u4e48\u503c\u94b1\u7684\u4e1c\u897f\u6ca1\u6709\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u9879\u5708\u6210\u5417\uff1f\u201d\u8bf4\u7740\u4ece\u8863\u5185\u8d34\u8089\u5904\u9664\u4e86\u4e0b\u6765\u3002\u5d14\u79cb\u5c71\u4e00\u770b\uff0c\u89c1\u9879\u5708\u662f\u91d1\u7684\uff0c\u9576\u7740\u516b\u9897\u5c0f\u73cd\u73e0\uff0c\u9879\u5708\u9501\u7247\u4e0a\u523b\u7740\u201c\u5bcc\u8d35\u6052\u660c\u201d\u56db\u4e2a\u5927\u5b57\uff0c\u8fd8\u6709\u4e24\u884c\u5c0f\u5b57\uff0c\u4e00\u884c\u662f\u201c\u8881\u516c\u5b50\u627f\u5fd7\u5468\u5c81\u4e4b\u5e86\u201d\uff0c\u4e00\u884c\u662f\u201c\u5c0f\u5c06\u8d75\u7387\u6559\u656c\u8d60\u201d\uff0c\u624d\u77e5\u9053\u662f\u8881\u627f\u5fd7\u505a\u5468\u5c81\u65f6\uff0c\u4ed6\u7236\u4eb2\u90e8\u4e0b\u5927\u5c06\u8d75\u7387\u6559\u6240\u8d60\u3002 \u3000\u3000\u8d75\u7387\u6559\u548c\u7956\u5927\u5bff\u3001\u4f55\u53ef\u7eb2\u3001\u6ee1\u6842\u4e09\u4eba\u662f\u8881\u5d07\u7115\u90e8\u4e0b\u7684\u56db\u5927\u540d\u5c06\u3002\u5f53\u5e74\u5b81\u9526\u5927\u6377\uff0c\u8d75\u7387\u6559\u90e8\u6740\u4f24\u6e05\u5175\u751a\u4f17\uff0c\u5b98\u5c01\u5de6\u90fd\u7763\u3001\u5e73\u8fbd\u5c06\u519b\u3002\u5d07\u796f\u4e8c\u5e74\u5341\u6708\uff0c\u6e05\u5175\u7ed5\u8fc7\u5c71\u6d77\u5173\uff0c\u7531\u5927\u5b89\u53e3\u5165\u5bc7\u4eac\u5e08\uff0c\u8881\u5d07\u7115\u7387\u56db\u5c06\u5343\u91cc\u56de\u63f4\uff0c\u53cd\u4e3a\u5d07\u796f\u89c1\u7591\u800c\u4e0b\u72f1\u3002\u8d75\u7387\u6559\u548c\u6ee1\u6842\u51fa\u6218\uff0c\u5148\u540e\u9635\u4ea1\u3002\u7956\u5927\u5bff\u4e0e\u4f55\u53ef\u7eb2\u6124\u800c\u7387\u90e8\u81ea\u884c\u79bb\u53bb\uff0c\u540e\u6765\u8881\u5d07\u7115\u5728\u72f1\u4e2d\u5199\u4fe1\u53bb\u529d\uff0c\u7956\u4f55\u4e8c\u5c06\u624d\u518d\u5f52\u671d\u3002 \u3000\u3000\u8d75\u7387\u6559\u662f\u8881\u5d07\u7115\u90e8\u4e0b\u540d\u5c06\uff0c\u5929\u4e0b\u77e5\u95fb\uff0c\u4f46\u8fd9\u65f6\u5d14\u79cb\u5c71\u8ff7\u8ff7\u7cca\u7cca\uff0c\u672a\u80fd\u7ec6\u60f3\uff0c\u4fbf\u9053\uff1a\u201c\u53eb\u5e97\u4f19\u966a\u4f60\u5230\u5f53\u8217\u53bb\uff0c\u628a\u9879\u5708\u5f53\u4e86\u5427\uff0c\u5c06\u6765\u54b1\u4eec\u518d\u6765\u8d4e\u56de\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8bf4\uff1a\u201c\u597d\uff0c\u6211\u5c31\u53bb\u3002\u201d\u4e8e\u662f\u8bf7\u5e97\u4f19\u540c\u53bb\u9547\u4e0a\u7684\u5f53\u8217\u3002 \u3000\u3000\u5f53\u8217\u671d\u5949\u62ff\u5230\u9879\u5708\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u5c0f\u670b\u53cb\uff0c\u8fd9\u9879\u5708\u4f60\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u662f\u6211\u81ea\u5df1\u7684\u3002\u201d\u90a3\u671d\u5949\u8138\u8272\u767b\u65f6\u53d8\u4e86\uff0c\u5411\u8881\u627f\u5fd7\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u826f\u4e45\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7b49\u4e00\u4e0b\u3002\u201d\u62ff\u4e86\u9879\u5708\u5230\u91cc\u9762\u53bb\uff0c\u534a\u5929\u4e0d\u51fa\u6765\u3002\u8881\u627f\u5fd7\u548c\u90a3\u5e97\u4f19\u7b49\u7684\u7740\u6025\uff0c\u53c8\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u90a3\u671d\u5949\u624d\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u4e8c\u5341\u4e24\u3002\u201d\u8881\u627f\u5fd7\u4e5f\u4e0d\u61c2\u89c4\u77e9\uff0c\u8fd8\u662f\u90a3\u5e97\u4f19\u4ee3\u4ed6\u591a\u4e89\u4e86\u4e8c\u4e24\u94f6\u5b50\u3002\u8881\u627f\u5fd7\u62ff\u4e86\u94f6\u5b50\u548c\u5f53\u7968\uff0c\u987a\u9053\u8981\u5e97\u4f19\u966a\u53bb\u8bf7\u4e86\u5927\u592b\uff0c\u8fd9\u624d\u56de\u5e97\uff0c\u54ea\u77e5\u8eab\u540e\u5df2\u6697\u6697\u8ddf\u4e86\u4e24\u540d\u516c\u5dee\u3002 \u3000\u3000\u8881\u627f\u5fd7\u56de\u5230\u5e97\u623f\uff0c\u89c1\u5d14\u79cb\u5c71\u5df2\u6c89\u6c89\u7761\u719f\uff0c\u989d\u4e0a\u4ecd\u7136\u706b\u70eb\uff0c\u5927\u592b\u8fd8\u6ca1\u5230\u6765\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7126\u6025\uff0c\u8d70\u5230\u5e97\u95e8\u5916\u9762\u5f20\u671b\uff0c\u5ffd\u89c1\u4e03\u516b\u540d\u516c\u5dee\u624b\u6301\u94c1\u94fe\u94c1\u5c3a\uff0c\u62a2\u8fdb\u5e97\u6765\u3002 \u3000\u3000\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u5b69\u5b50\uff01\u201d\u4e3a\u9996\u7684\u516c\u5dee\u559d\u9053\uff1a\u201c\u5582\uff0c\u5b69\u5b50\uff0c\u4f60\u59d3\u8881\u5417\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u3002\u201d\u90a3\u516c\u5dee\u54c8\u54c8\u4e00\u7b11\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u90a3\u4e2a\u91d1\u9879\u5708\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u9879\u5708\u4f60\u4ece\u54ea\u91cc\u5077\u6765\u7684\uff1f\u201d\u8881\u627f\u5fd7\u6025\u9053\uff1a\u201c\u4e0d\u662f\u5077\u6765\u7684\uff0c\u662f\u6211\u81ea\u5df1\u7684\u3002\u201d\u90a3\u516c\u5dee\u7b11\u9053\uff1a\u201c\u8881\u5d07\u7115\u662f\u4f60\u751a\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u6562\u56de\u7b54\uff0c\u5954\u8fdb\u5e97\u623f\uff0c\u731b\u529b\u53bb\u63a8\u5d14\u79cb\u5c71\uff0c\u53ea\u542c\u5f97\u5916\u9762\u516c\u5dee\u558a\u4e86\u8d77\u6765\uff1a\u201c\u5723\u5cf0\u5d82\u7684\u5978\u515a\u8eb2\u5728\u8fd9\u91cc\uff0c\u83ab\u8ba9\u9003\u4e86\u3002\u201d\u5d14\u79cb\u5c71\u970d\u5730\u5750\u8d77\uff0c\u8981\u5f85\u6323\u4e0b\u5730\u6765\uff0c\u5374\u54ea\u91cc\u80fd\u591f\uff1f\u811a\u521a\u7740\u5730\uff0c\u4fbf\u5373\u8dcc\u5012\u3002 \u3000\u3000\u8fd9\u65f6\u4f17\u516c\u5dee\u5df2\u6d8c\u5230\u5e97\u623f\u95e8\u53e3\uff0c\u8881\u627f\u5fd7\u4e0d\u53ca\u53bb\u6276\u5d14\u79cb\u5c71\uff0c\u7eb5\u51fa\u95e8\u6765\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u6321\u5728\u95e8\u53e3\uff0c\u5f53\u65f6\u5fc3\u4e2d\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u51b3\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u6349\u4e86\u5d14\u53d4\u53d4\u53bb\u3002\u201d \u3000\u3000\u95e8\u5916\u662f\u4e2a\u5927\u9662\u5b50\uff0c\u5ba2\u5e97\u4e2d\u4f19\u8ba1\u5ba2\u4eba\u542c\u8bf4\u6349\u62ff\u72af\u4eba\uff0c\u90fd\u62e5\u5230\u9662\u5b50\u91cc\u6765\u77a7\u70ed\u95f9\uff0c\u89c1\u4e03\u516b\u540d\u516c\u5dee\u5bf9\u7740\u4e00\u4e2a\u5341\u5c81\u5de6\u53f3\u7684\u5b69\u5b50\u53d1\u5a01\uff0c\u5747\u89c9\u5947\u602a\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u540d\u516c\u5dee\u6296\u52a8\u94c1\u94fe\uff0c\u5f80\u8881\u627f\u5fd7\u5934\u4e0a\u5957\u53bb\u3002\u8881\u627f\u5fd7\u9000\u540e\u4e00\u6b65\uff0c\u4ecd\u662f\u62e6\u5728\u95e8\u5916\uff0c\u4e0d\u8ba9\u516c\u5dee\u8fdb\u95e8\u3002\u90a3\u516c\u5dee\u6296\u94c1\u94fe\u5957\u4eba\uff0c\u672c\u662f\u5403\u4e86\u5341\u591a\u5e74\u8859\u95e8\u996d\u7684\u62ff\u624b\u672c\u4e8b\uff0c\u5c82\u77e5\u4e00\u4e2a\u5c0f\u5c0f\u5b69\u5b50\u5c45\u7136\u8eab\u624b\u654f\u6377\uff0c\u8fd9\u4e00\u4e0b\u7adf\u6ca1\u5957\u4f4f\uff0c\u8001\u7f9e\u6210\u6012\uff0c\u4f38\u53f3\u624b\u6765\u63ea\u4ed6\u5934\u4e0a\u7684\u5c0f\u8fab\u5b50\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u8fd9\u8bb8\u591a\u516c\u5dee\u6c14\u52bf\u6c79\u6c79\uff0c\u672c\u5df2\u5413\u5f97\u8981\u54ed\uff0c\u4f46\u89c1\u5bf9\u65b9\u4f38\u624b\u6293\u5230\uff0c\u5934\u4e00\u504f\uff0c\u4f7f\u51fa\u4f0f\u864e\u638c\u6cd5\u4e2d\u7684\u201c\u6a2a\u62d6\u5355\u97ad\u201d\uff0c\u5728\u4ed6\u624b\u8155\u4e0a\u4e00\u62c9\u3002\u90a3\u516c\u5dee\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u4e9b\u8dcc\u5012\uff0c\u6012\u706b\u66f4\u70bd\uff0c\u98de\u817f\u731b\u8e22\uff0c\u9a82\u9053\uff0c\u201c\u5c0f\u6742\u79cd\uff0c\u8001\u5b50\u4eca\u65e5\u8981\u4f60\u597d\u770b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8e72\u4e0b\u8eab\u6765\uff0c\u53cc\u624b\u5728\u4ed6\u5927\u817f\u548c\u81c0\u90e8\u4e00\u6258\uff0c\u501f\u529b\u4e58\u52bf\uff0c\u5411\u5916\u63a8\u9001\uff0c\u90a3\u516c\u5dee\u80a5\u80a5\u4e00\u4e2a\u8eab\u8eaf\u767b\u65f6\u51cc\u7a7a\u98de\u4e86\u51fa\u53bb\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u8dcc\u5728\u5730\u4e0b\u3002\u8881\u627f\u5fd7\u672c\u6765\u4e5f\u6ca1\u8fd9\u4e48\u5927\u6c14\u529b\uff0c\u5168\u662f\u4e58\u7740\u90a3\u516c\u5dee\u4e00\u8e22\u4e4b\u52bf\uff0c\u659c\u5f15\u65c1\u8f6c\uff0c\u628a\u4ed6\u72e0\u72e0\u6454\u4e86\u4e00\u4ea4\u3002\u8fd9\u4e00\u62db\u4ecd\u662f\u4f0f\u864e\u638c\u6cd5\u3002 \u3000\u3000\u65c1\u89c2\u4f17\u4eba\u9f50\u9f50\u8f70\u7136\u53eb\u597d\u3002\u4ed6\u4eec\u672c\u6765\u6124\u6068\u5927\u4eba\u6b3a\u4fae\u5c0f\u5b69\uff0c\u4f55\u51b5\u5b98\u5e9c\u516c\u5dee\u6a2a\u884c\u9738\u9053\uff0c\u7d20\u4e3a\u4f17\u767e\u59d3\u6240\u4fa7\u76ee\u5207\u9f7f\uff0c\u8fd9\u65f6\u773c\u89c1\u516c\u5dee\u53cd\u800c\u843d\u8d25\uff0c\u800c\u4e14\u8d25\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u4e0d\u7531\u5f97\u5927\u58f0\u559d\u91c7\u3002 \u3000\u3000\u5176\u4f59\u7684\u516c\u5dee\u4e5f\u90fd\u4e00\u6123\uff0c\u6697\u60f3\u8fd9\u5b69\u5b50\u5012\u6709\u70b9\u90aa\u95e8\uff0c\u4e92\u4f7f\u773c\u8272\uff0c\u624b\u4e3e\u5355\u5200\u94c1\u5c3a\uff0c\u4e00\u6d8c\u800c\u4e0a\u3002\u65c1\u89c2\u4f17\u4eba\u89c1\u4ed6\u4eec\u52a8\u4e86\u5bb6\u4f19\uff0c\u4ff1\u90fd\u5bb3\u6015\uff0c\u7eb7\u7eb7\u9000\u907f\u3002\u8881\u627f\u5fd7\u867d\u5b66\u4e86\u6570\u5e74\u6b66\u827a\uff0c\u7a76\u7adf\u5e74\u5e7c\uff0c\u53c8\u654c\u4e0d\u8fc7\u5bf9\u65b9\u4eba\u591a\uff0c\u65e0\u53ef\u5948\u4f55\u4e4b\u4e2d\uff0c\u53ea\u6709\u594b\u529b\u62b5\u6321\u3002\u4e0d\u4e45\u80a9\u5934\u4fbf\u5403\u94c1\u5c3a\u91cd\u91cd\u6253\u4e2d\u4e86\u4e00\u4e0b\uff0c\u5fcd\u4e0d\u4f4f\u4fbf\u8981\u54ed\u51fa\u58f0\u6765\u3002 \u3000\u3000\u6b63\u5728\u5371\u6025\u4e4b\u9645\uff0c\u5ffd\u7136\u5de6\u8fb9\u53a2\u623f\u4e2d\u5954\u51fa\u4e00\u6761\u5927\u6c49\uff0c\u98de\u8eab\u7eb5\u8d77\uff0c\u843d\u5728\u8881\u627f\u5fd7\u9762\u524d\uff0c\u4f38\u51fa\u53cc\u624b\u4e71\u6293\u4e71\u62ff\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u7528\u4e86\u751a\u4e48\u624b\u6cd5\uff0c\u9877\u523b\u4e4b\u95f4\uff0c\u5df2\u628a\u4f17\u516c\u5dee\u7684\u5175\u5203\u5168\u90e8\u593a\u4e0b\u3002\u51e0\u540d\u516c\u5dee\u9000\u5f97\u7a0d\u8fdf\uff0c\u7834\u4ed6\u51e0\u62f3\u6253\u5f97\u773c\u9752\u53e3\u80bf\u3002\u8fd9\u5927\u6c49\u554a\u554a\u5927\u53eb\uff0c\u58f0\u97f3\u53e4\u602a\u3002 \u3000\u3000\u4e00\u540d\u516c\u5dee\u559d\u9053\uff1a\u201c\u6211\u4eec\u6349\u62ff\u8981\u72af\uff0c\u4f60\u662f\u751a\u4e48\u4eba\uff1f\u5feb\u5feb\u6eda\u5f00\u3002\u201d\u90a3\u5927\u6c49\u5168\u4e0d\u7406\u4f1a\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u5df2\u6b3a\u5230\u4ed6\u8eab\u524d\uff0c\u53f3\u624b\u6293\u4f4f\u4ed6\u80f8\u53e3\uff0c\u5f80\u5916\u63b7\u51fa\u3002\u90a3\u516c\u5dee\u72b9\u5982\u65ad\u7ebf\u9e22\u5b50\u4e00\u822c\uff0c\u60a0\u60a0\u6643\u6643\u7684\u98de\u51fa\u5899\u5916\uff0c\u7830\u84ec\u4e00\u58f0\uff0c\u6454\u5f97\u534a\u6b7b\u3002\u5176\u4f59\u7684\u516c\u5dee\u518d\u4e5f\u4e0d\u6562\u505c\u7559\uff0c\u4e00\u54c4\u51fa\u5916\u3002 \u3000\u3000\u90a3\u5927\u6c49\u8d70\u5230\u8881\u627f\u5fd7\u8ddf\u524d\uff0c\u53cc\u624b\u6bd4\u5212\uff0c\u53e3\u4e2d\u54d1\u54d1\u4f5c\u58f0\uff0c\u539f\u6765\u662f\u4e2a\u54d1\u5df4\uff0c\u4f3c\u5728\u95ee\u4ed6\u6765\u5386\u3002\u8881\u627f\u5fd7\u4e0d\u77e5\u5982\u4f55\u544a\u8bc9\u4ed6\u624d\u597d\uff0c\u751a\u662f\u7126\u6025\u3002 \u3000\u3000\u90a3\u5927\u6c49\u5ffd\u7136\u5de6\u638c\u5411\u4e0a\uff0c\u53f3\u638c\u5411\u5730\uff0c\u4ece\u4f0f\u864e\u638c\u7684\u8d77\u624b\u5f0f\u5f00\u59cb\uff0c\u7ec3\u4e86\u8d77\u6765\uff0c\u6253\u5230\u7b2c\u5341\u62db\u201c\u907f\u6251\u51fb\u865a\u201d\u5c31\u6536\u4e86\u624b\u3002\u8881\u627f\u5fd7\u4f1a\u610f\uff0c\u4ece\u7b2c\u5341\u4e00\u62db\u201c\u6a2a\u8e39\u864e\u8170\u201d\u8d77\u59cb\uff0c\u63a5\u4e0b\u53bb\u7ec3\u4e86\u56db\u62db\u3002\u90a3\u54d1\u5df4\u4e00\u7b11\uff0c\u70b9\u70b9\u5934\uff0c\u4f38\u81c2\u5c06\u4ed6\u62b1\u8d77\uff0c\u795e\u6001\u751a\u662f\u4eb2\u70ed\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6307\u6307\u5e97\u623f\uff0c\u793a\u610f\u91cc\u9762\u6709\u4eba\u3002\u90a3\u54d1\u5df4\u62b1\u7740\u4ed6\u8fdb\u623f\uff0c\u53ea\u89c1\u5d14\u79cb\u5c71\u5750\u5728\u5730\u4e0b\uff0c\u8138\u8272\u72b9\u5982\u6b7b\u7070\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u653e\u4e0b\u8881\u627f\u5fd7\uff0c\u8d70\u4e0a\u524d\u53bb\u3002\u5d14\u79cb\u5c71\u5374\u8ba4\u5f97\u4ed6\uff0c\u505a\u505a\u624b\u52bf\uff0c\u6307\u6307\u81ea\u5df1\u7684\u817f\u3002 \u3000\u3000\u90a3\u54d1\u5df4\u70b9\u70b9\u5934\uff0c\u5de6\u624b\u7275\u7740\u8881\u627f\u5fd7\uff0c\u53f3\u624b\u62b1\u8d77\u4e86\u5d14\u79cb\u5c71\uff0c\u5927\u8e0f\u6b65\u8d70\u51fa\u5ba2\u5e97\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u662f\u4e00\u767e\u51e0\u5341\u65a4\u91cd\u7684\u4e00\u6761\u5927\u6c49\uff0c\u4f46\u54d1\u5df4\u5982\u62b1\u5c0f\u5b69\u3001\u6beb\u4e0d\u8d39\u529b\uff0c\u6b65\u5c65\u5982\u98de\u7684\u51fa\u53bb\u3002 \u3000\u3000\u4e24\u540d\u516c\u5dee\u8eb2\u5728\u4e00\u65c1\uff0c\u89c1\u90a3\u54d1\u5df4\u5411\u897f\u8d70\u53bb\uff0c\u8fdc\u8fdc\u8ddf\u5728\u540e\u9762\uff0c\u60f3\u662f\u8981\u77e5\u9053\u4ed6\u843d\u811a\u4e4b\u6240\uff0c\u518d\u9080\u4eba\u5927\u4e3e\u62ff\u6355\u3002 \u3000\u3000\u8fd9\u65f6\u5d14\u79cb\u5c71\u53c8\u660f\u4e86\u8fc7\u53bb\uff0c\u4eba\u4e8b\u4e0d\u77e5\u3002\u54d1\u5df4\u542c\u4e0d\u5230\u8eab\u540e\u58f0\u606f\uff0c\u8881\u627f\u5fd7\u62c9\u62c9\u54d1\u5df4\u7684\u624b\uff0c\u5634\u5df4\u5411\u540e\u4e00\u52aa\u3002\u54d1\u5df4\u56de\u8fc7\u5934\u6765\uff0c\u77a7\u89c1\u4e86\u516c\u5dee\uff0c\u5374\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u7ee7\u7eed\u524d\u884c\u3002 \u3000\u3000\u8d70\u51fa\u4e24\u4e09\u91cc\u8def\uff0c\u56db\u4e0b\u8352\u50fb\u65e0\u4eba\uff0c\u54d1\u5df4\u5ffd\u5730\u628a\u5d14\u79cb\u5c71\u5f80\u5730\u4e0a\u4e00\u653e\uff0c\u7eb5\u8eab\u8dc3\u5230\u90a3\u4e24\u540d\u516c\u5dee\u9762\u524d\u3002\u4e24\u516c\u5dee\u8f6c\u8eab\u60f3\u9003\uff0c\u54ea\u91cc\u6765\u5f97\u53ca\uff0c\u65e9\u88ab\u4ed6\u4e00\u624b\u4e00\u4e2a\uff0c\u63ea\u4f4f\u540e\u5fc3\uff0c\u76f4\u5411\u5c71\u8c37\u4e2d\u6454\u4e86\u4e0b\u53bb\uff0c\u4e24\u58f0\u60e8\u53eb\uff0c\u90fd\u8dcc\u5f97\u8111\u6d46\u8ff8\u88c2\u800c\u6b7b\u3002 \u3000\u3000\u54d1\u5df4\u6454\u6b7b\u516c\u5dee\uff0c\u62b1\u8d77\u5d14\u79cb\u5c71\uff0c\u5065\u6b65\u5982\u98de\u7684\u5411\u524d\u75be\u8d70\u3002\u8fd9\u4e00\u6765\u8881\u627f\u5fd7\u53ef\u8ddf\u4e0d\u4e0a\u4e86\uff0c\u4ed6\u867d\u52c9\u529b\u5bf9\u4ed8\uff0c\u4e24\u6761\u5c0f\u817f\u62da\u547d\u642c\u52a8\uff0c\u4f46\u53ea\u8dd1\u4e86\u91cc\u8bb8\uff0c\u5df2\u6c14\u5598\u8fde\u8fde\u3002 \u3000\u3000\u54d1\u5df4\u4e00\u7b11\uff0c\u4fef\u8eab\u628a\u4ed6\u62b1\u5728\u624b\u4e2d\uff0c\u4ed6\u53cc\u624b\u5206\u62b1\u4e24\u4eba\uff0c\u53cd\u800c\u8dd1\u5f97\u66f4\u5feb\uff0c\u8dd1\u4e86\u4e00\u4f1a\uff0c\u6298\u800c\u5411\u5de6\uff0c\u671d\u5c71\u4e0a\u5954\u53bb\u3002 \u3000\u3000\u7ffb\u8fc7\u4e24\u4e2a\u5c71\u5934\uff0c\u53ea\u89c1\u5c71\u8170\u4e2d\u6709\u4e09\u95f4\u8305\u5c4b\uff0c\u54d1\u5df4\u5f84\u5411\u8305\u5c4b\u8dd1\u53bb\u3002\u5feb\u8981\u5230\u65f6\uff0c\u5c4b\u524d\u4e00\u4eba\u8fce\u4e86\u8fc7\u6765\uff0c\u8d70\u5230\u4e34\u8fd1\uff0c\u539f\u6765\u662f\u4e2a\u4e8c\u5341\u591a\u5c81\u7684\u5c11\u5987\u3002\u5979\u5411\u54d1\u5df4\u70b9\u4e86\u70b9\u5934\uff0c\u89c1\u5230\u5d14\u8881\u4e24\u4eba\uff0c\u4f3c\u611f\u8bb6\u5f02\uff0c\u548c\u54d1\u5df4\u6253\u4e86\u51e0\u4e2a\u624b\u52bf\uff0c\u9886\u7740\u4ed6\u4eec\u8fdb\u5c4b\u3002 \u3000\u3000\u90a3\u5c11\u5987\u53eb\u9053\uff1a\u201c\u5c0f\u6167\uff0c\u5feb\u62ff\u8336\u58f6\u8336\u7897\u6765\u3002\u201d\u4e00\u4e2a\u5973\u5b69\u7684\u58f0\u97f3\u5728\u9694\u623f\u5e94\u4e86\u4e00\u58f0\uff0c\u63d0\u4e86\u4e00\u628a\u7c97\u8336\u58f6\u548c\u51e0\u53ea\u7897\u8fc7\u6765\uff0c\u6014\u6014\u7684\u671b\u7740\u5d14\u8881\u4e24\u4eba\uff0c\u4e00\u5bf9\u5706\u5706\u7684\u773c\u73e0\u9aa8\u6e9c\u6e9c\u7684\u8f6c\u52a8\uff0c\u751a\u662f\u7075\u6d3b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u90a3\u5c11\u5987\u7c97\u8863\u5e03\u88d9\uff0c\u4f46\u76ae\u8272\u767d\u6da6\uff0c\u9762\u76ee\u59e3\u597d\uff0c\u90a3\u5973\u5b69\u4e5f\u751f\u5f97\u751a\u662f\u7075\u79c0\u3002 \u3000\u3000\u90a3\u5c11\u5987\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\uff0c\u4f60\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u600e\u4e48\u9047\u4e0a\u4ed6\u7684\uff1f\u201d\u8881\u627f\u5fd7\u77e5\u5979\u662f\u54d1\u5df4\u7684\u670b\u53cb\uff0c\u4e8e\u662f\u6beb\u4e0d\u9690\u7792\u7684\u7b80\u7565\u8bf4\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5987\u542c\u5f97\u5d14\u79cb\u5c71\u4e2d\u6bd2\u53d7\u4f24\uff0c\u5fd9\u62ff\u51fa\u836f\u7bb1\uff0c\u4ece\u74f6\u4e2d\u5012\u51fa\u4e9b\u767d\u8272\u548c\u7ea2\u8272\u7684\u836f\u7c89\uff0c\u6df7\u5728\u4e00\u8d77\uff0c\u8c03\u4e86\u6c34\u7ed9\u5d14\u79cb\u5c71\u559d\u4e86\uff0c\u53c8\u53d6\u51fa\u4e00\u628a\u5c0f\u5200\uff0c\u5c06\u4ed6\u817f\u4e0a\u8150\u8089\u522e\u53bb\uff0c\u6577\u4e0a\u4e9b\u9ec4\u8272\u7684\u836f\u672b\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u7528\u6e05\u6c34\u6d17\u53bb\uff0c\u518d\u6577\u836f\u672a\u3002\u9f99\u822c\u6577\u6d17\u4e86\u4e09\u6b21\uff0c\u5d14\u79cb\u5c71\u54fc\u51fa\u58f0\u6765\u3002\u90a3\u5c11\u5987\u5411\u8881\u627f\u5fd7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\u4e86\u3002\u201d\u6253\u624b\u52bf\u53eb\u54d1\u5df4\u628a\u5d14\u79cb\u5c71\u62b1\u5165\u5185\u5802\u4f11\u606f\u3002 \u3000\u3000\u90a3\u5c11\u5987\u6536\u62fe\u836f\u7bb1\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u59d3\u5b89\uff0c\u4f60\u53eb\u6211\u5b89\u5a76\u5a76\u597d\u5566\u3002\u8fd9\u662f\u6211\u5973\u513f\uff0c\u5979\u53eb\u5c0f\u6167\uff0c\u4f60\u5c31\u803d\u5728\u6211\u8fd9\u91cc\u3002\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\u3002\u5b89\u5927\u5a18\u968f\u5373\u4e0b\u53a8\u505a\u9762\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5403\u8fc7\u540e\uff0c\u75b2\u7d2f\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u4fbf\u4f0f\u5728\u684c\u4e0a\u7761\u7740\u4e86\u3002 \u3000\u3000\u6b21\u6668\u9192\u6765\u65f6\u53d1\u89c9\u5df2\u7761\u5728\u5e8a\u4e0a\u3002\u5c0f\u6167\u5e26\u4ed6\u53bb\u6d17\u8138\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u53bb\u77a7\u77a7\u5d14\u53d4\u53d4\uff0c\u4ed6\u4f24\u52bf\u597d\u4e9b\u4e48\uff1f\u201d\u5c0f\u6167\u9053\uff1a\u201c\u54d1\u5df4\u4f2f\u4f2f\u65e9\u80cc\u4e86\u4ed6\u53bb\u5566\uff01\u201d\u8881\u627f\u5fd7\u60ca\u9053\uff1a\u201c\u5f53\u771f\uff1f\u201d\u5c0f\u6167\u70b9\u70b9\u5934\u3002\u8881\u627f\u5fd7\u5954\u5230\u5185\u5ba4\uff0c\u679c\u7136\u4e0d\u89c1\u5d14\u79cb\u5c71\u548c\u54d1\u5df4\u7684\u8e2a\u5f71\u3002\u4ed6\u832b\u7136\u65e0\u4e3b\uff0c\u54c7\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\u3002\u5c0f\u6167\u5fd9\u9053\uff1a\u201c\u522b\u54ed\uff0c\u522b\u54ed\uff01\u201d\u8881\u627f\u5fd7\u54ea\u91cc\u80af\u542c\uff1f\u5c0f\u6167\u53eb\u9053\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\uff0c\u4f60\u5feb\u6765\uff01\u201d\u5b89\u5927\u5a18\u95fb\u58f0\u8d76\u6765\u3002\u5c0f\u6167\u9053\uff1a\u201c\u4ed6\u89c1\u5d14\u53d4\u53d4\u4ed6\u4eec\u8d70\u4e86\uff0c\u54ed\u8d77\u6765\u5566\uff01\u201d \u3000\u3000\u5b89\u5927\u5a18\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u5d14\u53d4\u53d4\u53d7\u4e86\u4f24\uff0c\u5f88\u5389\u5bb3\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\u3002\u5b89\u5927\u5a18\u53c8\u9053\uff1a\u201c\u6211\u53ea\u80fd\u6682\u884c\u6551\u4ed6\uff0c\u8ba9\u4ed6\u4f24\u53e3\u7684\u6bd2\u6c14\u4e0d\u884c\u5f00\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\u65f6\u5019\u9694\u5f97\u592a\u4e45\u5566\uff0c\u53ea\u6015\u4ed6\u817f\u8981\u6b8b\u5e9f\uff0c\u56e0\u6b64\u54d1\u5df4\u4f2f\u4f2f\u80cc\u4ed6\u53bb\u8bf7\u53e6\u5916\u4e00\u4e2a\u4eba\u533b\u6cbb\u3002\u7b49\u4ed6\u533b\u597d\u4e4b\u540e\uff0c\u5c31\u4f1a\u6765\u77a7\u4f60\u7684\u3002\u201d\u8881\u627f\u5fd7\u6162\u6162\u6b62\u4e86\u54ed\u6ce3\u3002\u5b89\u5927\u5a18\u9053\uff1a\u201c\u4ed6\u5c31\u4f1a\u597d\u7684\u3002\u5feb\u6d17\u8138\uff0c\u6d17\u4e86\u8138\u54b1\u4eec\u5403\u996d\u3002\u201d \u3000\u3000\u5403\u8fc7\u65e9\u996d\u540e\uff0c\u5b89\u5927\u5a18\u8981\u4ed6\u628a\u8fc7\u53bb\u7684\u4e8b\u518d\u8be6\u8be6\u7ec6\u7ec6\u8bf4\u4e00\u904d\uff0c\u542c\u5f97\u4e0d\u4f4f\u53f9\u606f\u3002 \u3000\u3000\u5c31\u8fd9\u6837\uff0c\u8881\u627f\u5fd7\u4fbf\u5728\u5b89\u5927\u5a18\u5bb6\u4e2d\u4f4f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5b89\u5927\u5a18\u53eb\u4ed6\u628a\u6240\u5b66\u6b66\u529f\u7ec3\u4e86\u4e00\u904d\uff0c\u770b\u540e\u70b9\u70b9\u5934\u8bf4\uff1a\u201c\u4e5f\u771f\u96be\u4e3a\u4f60\u4e86\u3002\u201d \u3000\u3000\u6b64\u540e\u5b89\u5927\u5a18\u6bcf\u65e5\u53eb\u4ed6\u81ea\u884c\u7ec3\u6b66\uff0c\u7ec3\u5f97\u597d\u4e0d\u597d\uff0c\u5374\u4ece\u4e0d\u52a0\u6307\u70b9\uff0c\u5728\u4ed6\u7ec3\u7684\u65f6\u5019\u4e5f\u6781\u5c11\u5728\u65c1\u89c2\u770b\u3002\u5c0f\u6167\u672c\u6765\u5e38\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u5728\u4ed6\u7ec3\u6b66\u4e4b\u65f6\uff0c\u5374\u603b\u88ab\u5988\u5988\u53eb\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4ece\u5c0f\u6ca1\u4e86\u7236\u6bcd\uff0c\u5e94\u677e\u3001\u6731\u5b89\u56fd\u7b49\u4eba\u867d\u7136\u5bf9\u4ed6\u7167\u987e\u5468\u5230\uff0c\u4f46\u8fd9\u4e9b\u53f1\u54a4\u98ce\u4e91\u7684\u5927\u5c06\uff0c\u7167\u6599\u5b69\u5b50\u603b\u4e0d\u5982\u4f55\u5728\u884c\u3002\u73b0\u4e0b\u5b89\u5927\u5a18\u5bf9\u4ed6\u5982\u6148\u6bcd\u822c\u7167\u987e\uff0c\u4eb2\u5207\u5468\u5230\uff0c\u53c8\u6709\u5c0f\u6167\u4f5c\u4f34\uff0c\u8fd9\u65f6\u5019\u6240\u8fc7\u7684\uff0c\u53ef\u8bf4\u662f\u4ed6\u751f\u5e73\u6700\u6e29\u99a8\u7684\u65e5\u5b50\u4e86\u3002 \u3000\u3000\u5982\u6b64\u8fc7\u4e86\u5341\u591a\u5929\uff0c\u8fd9\u4e00\u65e5\u5b89\u5927\u5a18\u5230\u9547\u4e0a\u53bb\u4e70\u6cb9\u76d0\u7b49\u7269\uff0c\u8fd8\u9884\u5907\u526a\u51e0\u5c3a\u5e03\u6765\uff0c\u7ed9\u8881\u627f\u5fd7\u7f1d\u4e00\u5957\u886b\u88e4\u3002\u90a3\u65e5\u4ed6\u5728\u5723\u5cf0\u5d82\u9047\u96be\uff0c\u8fde\u6eda\u5e26\u722c\uff0c\u8863\u670d\u5df2\u7ed9\u5c71\u77f3\u6811\u679d\u6495\u5f97\u7834\u70c2\u3002\u5b89\u5927\u5a18\u867d\u65e9\u7ed9\u4ed6\u7f1d\u8865\u597d\u4e86\uff0c\u4f46\u6ee1\u8eab\u8865\u9489\uff0c\u603b\u4e0d\u597d\u770b\u3002\u5b89\u5927\u5a18\u53ee\u5631\u4e24\u4e2a\u5b69\u5b50\u5728\u5bb6\u91cc\u73a9\uff0c\u522b\u53bb\u5c71\u91cc\uff0c\u6015\u9047\u4e0a\u72fc\u3002\u4e24\u4e2a\u5b69\u5b50\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u5b89\u5927\u5a18\u8d70\u540e\uff0c\u4e24\u4e2a\u5b69\u5b50\u679c\u7136\u542c\u8bdd\u4e0d\u51fa\uff0c\u5728\u5c4b\u91cc\u8bb2\u4e86\u51e0\u4e2a\u6545\u4e8b\uff0c\u53c8\u6349\u4e86\u534a\u5929\u8ff7\u85cf\uff0c\u540e\u6765\u62ff\u4e9b\u5c0f\u7897\u5c0f\u7b77\uff0c\u5047\u88c5\u716e\u996d\u3002\u5c0f\u6167\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u6740\u9e21\uff0c\u6211\u53bb\u4e70\u8089\u3002\u201d\u6240\u8c13\u6740\u9e21\uff0c\u662f\u628a\u841d\u535c\u5207\u6210\u4e00\u5757\u4e00\u5757\uff0c\u800c\u4e70\u8089\u5219\u662f\u5728\u95e8\u53e3\u6361\u91ce\u6817\u5b50\u3002 \u3000\u3000\u5c0f\u6167\u53bb\u4e86\u4e00\u4f1a\uff0c\u597d\u4e45\u4e0d\u89c1\u56de\u6765\uff0c\u8881\u627f\u5fd7\u5927\u53eb\uff1a\u201c\u5c0f\u6167\uff0c\u5c0f\u6167\u3002\u201d\u4e0d\u89c1\u7b54\u5e94\uff0c\u60f3\u8d77\u5b89\u5927\u5a18\u7684\u8bdd\uff0c\u6015\u771f\u9047\u4e0a\u4e86\u72fc\uff0c\u5fd9\u5728\u7076\u4e0b\u62ff\u4e86\u4e00\u6839\u706b\u53c9\uff0c\u51b2\u51fa\u95e8\u53bb\u3002 \u3000\u3000\u521a\u8d70\u51fa\u5927\u95e8\uff0c\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53ea\u89c1\u5c0f\u6167\u88ab\u4e00\u6761\u8eab\u7a7f\u6b66\u5b98\u670d\u8272\u7684\u5927\u6c49\u631f\u5728\u80c1\u4e0b\uff0c\u6b63\u8981\u4e0b\u5c71\u3002\u8881\u627f\u5fd7\u5927\u558a\u4e00\u58f0\uff0c\u633a\u53c9\u5411\u90a3\u5927\u6c49\u80cc\u540e\u523a\u53bb\u3002\u5927\u6c49\u731d\u4e0d\u53ca\u9632\uff0c\u603b\u7b97\u8881\u627f\u5fd7\u4eba\u77ee\uff0c\u6ca1\u523a\u5230\u80cc\u5fc3\uff0c\u81c0\u90e8\u5374\u5df2\u91cd\u91cd\u7684\u5403\u4e86\u4e00\u53c9\uff0c\u53ea\u662f\u706b\u53c9\u5934\u949d\uff0c\u523a\u4e0d\u5165\u8089\u3002\u5927\u6c49\u5927\u6012\uff0c\u653e\u4e0b\u5c0f\u6167\uff0c\u62d4\u51fa\u5355\u5200\uff0c\u8f6c\u8eab\u5237\u7684\u5c31\u662f\u4e00\u5200\u3002\u8881\u627f\u5fd7\u66fe\u8ddf\u502a\u6d69\u5b66\u8fc7\u67aa\u6cd5\uff0c\u5c06\u4e00\u67c4\u706b\u53c9\u7167\u7740\u201c\u5cb3\u5bb6\u795e\u67aa\u201d\u67aa\u6cd5\u4f7f\u4e86\u5f00\u6765\uff0c\u7adf\u7136\u6709\u653b\u6709\u5b88\uff0c\u548c\u90a3\u5927\u6c49\u5bf9\u6253\u8d77\u6765\u3002 \u3000\u3000\u90a3\u5927\u6c49\u529b\u5927\u5200\u52b2\u3002\u8881\u627f\u5fd7\u4ed7\u7740\u8eab\u6cd5\u7075\u4fbf\uff0c\u5c45\u7136\u4e5f\u5bf9\u4ed8\u7740\u62c6\u4e86\u5341\u6765\u62db\u3002\u90a3\u5927\u6c49\u89c1\u6218\u4e0d\u4e0b\u4e00\u4e2a\u5c0f\u5b69\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u53cc\u817f\u4e00\u8e72\uff0c\u5200\u6cd5\u5ffd\u53d8\u3002\u90a3\u5927\u6c49\u8d77\u521d\u51fa\u62db\uff0c\u5012\u6709\u4e00\u5927\u534a\u90e8\u780d\u7a7a\u4e86\uff0c\u53ea\u56e0\u8881\u627f\u5fd7\u8eab\u77ee\uff0c\u5927\u6c49\u780d\u5411\u654c\u4eba\u4e0a\u90e8\u7684\u5200\u6cd5\uff0c\u5168\u90fd\u780d\u5728\u7a7a\u4e2d\uff0c\u4ed6\u89c9\u5bdf\u4e4b\u540e\uff0c\u4fbf\u6539\u4f7f\u5730\u5802\u5200\u6cd5\uff0c\u53ea\u662f\u89c9\u5f97\u5bf9\u4ed8\u4e00\u4e2a\u5c0f\u5c0f\u5b69\u7ae5\uff0c\u4e0d\u5fc5\u5c0f\u9898\u5927\u505a\uff0c\u662f\u4ee5\u5e76\u4e0d\u8eba\u4e0b\u5730\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u8881\u627f\u5fd7\u767b\u611f\u5403\u529b\uff0c\u6b63\u5371\u6025\u95f4\uff0c\u5ffd\u89c1\u5b89\u5c0f\u6167\u62ff\u4e86\u4e00\u67c4\u957f\u5251\uff0c\u4e00\u5251\u201c\u4ed9\u4eba\u6307\u8def\u201d\uff0c\u5411\u5927\u6c49\u8eab\u4e0a\u523a\u53bb\u3002\u5927\u6c49\u9a82\u9053\uff1a\u201c\u5478\uff01\u4f60\u8fd9\u5c0f\u599e\u4e5f\u6765\u627e\u6b7b\u3002\u201d\u5355\u5200\u6a2a\u780d\u8fc7\u53bb\u3002\u4ed6\u4e0d\u6b32\u4f24\u5979\uff0c\u53ea\u60f3\u9707\u53bb\u5979\u624b\u4e2d\u957f\u5251\u3002 \u3000\u3000\u54ea\u77e5\u5c0f\u6167\u8eab\u624b\u7075\u6d3b\uff0c\u957f\u5251\u5ffd\u5730\u5708\u8f6c\uff0c\u633d\u4e86\u4e2a\u5251\u82b1\uff0c\u4e00\u62db\u201c\u4e09\u5b9d\u83b2\u53f0\u201d\uff0c\u56de\u523a\u5927\u6c49\u540e\u80ef\uff0c\u540c\u65f6\u8881\u627f\u5fd7\u7684\u706b\u53c9\u4e5f\u662f\u4e00\u62db\u201c\u6bd2\u9f99\u51fa\u6d1e\u201d\u523a\u5c06\u8fc7\u53bb\u3002\u90a3\u5927\u6c49\u4e00\u65f6\u4e4b\u95f4\u7adf\u7ed9\u4e24\u4e2a\u5c0f\u5b69\u95f9\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d77\u521d\u89c1\u5c0f\u6167\u8fc7\u6765\u5e2e\u624b\uff0c\u62c5\u5fc3\u5979\u53d7\u4f24\uff0c\u4f46\u4e09\u62db\u4e24\u5f0f\u4e4b\u540e\uff0c\u89c1\u5979\u8eab\u624b\u4fbf\u6377\uff0c\u5c45\u7136\u4e00\u624b\u201c\u8fbe\u6469\u5251\u6cd5\u201d\u4f7f\u5f97\u4e5f\u9887\u7eaf\u719f\uff0c\u4ed6\u5c0f\u5b69\u597d\u80dc\uff0c\u4e0d\u7518\u843d\u540e\uff0c\u4e00\u67c4\u706b\u53c9\u4f7f\u5f97\u66f4\u52a0\u7d27\u4e86\u3002 \u3000\u3000\u90a3\u5927\u6c49\u89c1\u4e24\u4e2a\u5c0f\u5b69\u7684\u67aa\u6cd5\u548c\u5251\u6cd5\u7adf\u7136\u90fd\u662f\u5934\u5934\u662f\u9053\uff0c\u7136\u800c\u529b\u6c14\u592a\u5c0f\uff0c\u603b\u5f52\u65e0\u7528\uff0c\u4e8e\u662f\u5c01\u7d27\u95e8\u6237\uff0c\u53c8\u7b11\u53c8\u9a82\u7684\u4e00\u5473\u6e38\u6597\u3002\u8017\u4e86\u4e00\u9635\uff0c\u4e24\u4e2a\u5c0f\u5b69\u679c\u7136\u652f\u6301\u4e0d\u6765\u4e86\u3002 \u3000\u3000\u90a3\u5927\u6c49\u63d0\u8d77\u5355\u5200\uff0c\u5bf9\u51c6\u5c0f\u6167\u957f\u5251\u731b\u529b\u5288\u53bb\uff0c\u5c0f\u6167\u907f\u8ba9\u4e0d\u53ca\uff0c\u957f\u5251\u548c\u5355\u5200\u4e00\u78b0\uff0c\u62ff\u634f\u4e0d\u4f4f\uff0c\u767b\u65f6\u8131\u624b\u5411\u5929\u7a7a\u98de\u53bb\u3002\u8881\u627f\u5fd7\u5927\u9a87\uff0c\u706b\u53c9\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u5728\u5927\u6c49\u9762\u524d\u4e00\u6643\u3002\u5927\u6c49\u4e3e\u5200\u67b6\u5f00\uff0c\u98de\u811a\u628a\u5c0f\u6167\u8e22\u5012\u3002\u8881\u627f\u5fd7\u4e0d\u987e\u6027\u547d\u7684\u4e3e\u53c9\u529b\u653b\uff0c\u4f46\u5fc3\u4e2d\u614c\u4e71\uff0c\u706b\u53c9\u5df2\u4f7f\u5f97\u4e0d\u6210\u7ae0\u6cd5\u3002 \u3000\u3000\u5927\u6c49\u54c8\u54c8\u5927\u7b11\uff0c\u62a2\u4e0a\u4e00\u6b65\uff0c\u6325\u5200\u5411\u4ed6\u5f53\u5934\u780d\u4e0b\u3002\u8881\u627f\u5fd7\u6a2a\u53c9\u62db\u67b6\uff0c\u5927\u6c49\u5de6\u624b\u5df2\u62c9\u4f4f\u53c9\u5934\uff0c\u7528\u529b\u4e00\u626d\u3002\u8881\u627f\u5fd7\u53ea\u89c9\u864e\u53e3\u5267\u75db\uff0c\u706b\u53c9\u8131\u624b\u3002\u90a3\u5927\u6c49\u4e0d\u53bb\u7406\u4ed6\uff0c\u968f\u624b\u628a\u706b\u53c9\u63b7\u5728\u5730\u4e0b\uff0c\u5954\u5230\u5c0f\u6167\u8eab\u65c1\uff0c\u53f3\u624b\u6284\u51fa\uff0c\u5df2\u62b1\u4f4f\u5979\u8170\uff0c\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u624b\u4e0a\u867d\u75db\uff0c\u4f46\u89c1\u5c0f\u6167\u88ab\u64d2\uff0c\u62fe\u8d77\u706b\u53c9\u968f\u540e\u8d76\u6765\u3002\u5927\u6c49\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u9b3c\uff0c\u4e0d\u8981\u6027\u547d\u4e86\uff1f\u201d\u5de6\u624b\u62b1\u4f4f\u5c0f\u6167\uff0c\u53f3\u624b\u633a\u5200\u56de\u8eab\u4fbf\u780d\uff0c\u62c6\u5f97\u4e94\u516d\u62db\uff0c\u8881\u627f\u5fd7\u5de6\u80a9\u88ab\u5355\u5200\u524a\u53bb\u4e00\u7247\u8863\u670d\uff0c\u76ae\u8089\u4e5f\u5df2\u53d7\u4f24\uff0c\u9c9c\u8840\u76f4\u5192\u3002\u5927\u6c49\u7b11\u9053\uff1a\u201c\u5c0f\u9b3c\uff0c\u4f60\u8fd8\u6562\u6765\u4e48\uff1f\u201d \u3000\u3000\u54ea\u77e5\u8881\u627f\u5fd7\u7adf\u4e0d\u754f\u7f29\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u653e\u4e0b\u5c0f\u6167\uff0c\u6211\u5c31\u4e0d\u8ffd\u4f60\u3002\u201d\u62ff\u4e86\u706b\u53c9\uff0c\u4ecd\u662f\u7d27\u8ffd\u4e0d\u820d\u3002\u90a3\u5927\u6c49\u6012\u4ece\u5fc3\u8d77\uff0c\u6076\u5ff5\u987f\u751f\uff0c\u60f3\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u7ed3\u679c\u8fd9\u5c0f\u9b3c\uff0c\u770b\u6765\u4ed6\u8981\u7ea0\u7f20\u4e0d\u4f11\u3002\u201d\u5927\u559d\u4e00\u58f0\uff0c\u56de\u8eab\u633a\u5200\u72e0\u780d\uff0c\u6570\u62db\u62c6\u8fc7\uff0c\u811a\u4e0b\u4e00\u52fe\uff0c\u5df2\u628a\u8881\u627f\u5fd7\u7eca\u5012\uff0c\u518d\u4e0d\u5bb9\u60c5\uff0c\u4e3e\u5200\u780d\u843d\u3002 \u3000\u3000\u5c0f\u6167\u5927\u60ca\uff0c\u53cc\u624b\u62c9\u4f4f\u5927\u6c49\u624b\u81c2\uff0c\u72e0\u72e0\u5728\u4ed6\u624b\u8155\u4e0a\u54ac\u4e86\u4e00\u53e3\u3002\u5927\u6c49\u5403\u75db\uff0c\u54c7\u54c7\u6012\u543c\uff0c\u8881\u627f\u5fd7\u4e58\u673a\u6eda\u4e86\u5f00\u53bb\u3002\u5927\u6c49\u53cd\u624b\u6253\u4e86\u5c0f\u6167\u4e00\u4e2a\u8033\u62ec\u5b50\uff0c\u53c8\u4e3e\u5200\u5411\u8881\u627f\u5fd7\u780d\u4e0b\u3002\u8881\u627f\u5fd7\u4fa7\u8eab\u6025\u907f\uff0c\u88ab\u4ed6\u5200\u5c16\u5728\u989d\u4e0a\u5e26\u8fc7\uff0c\u5de6\u7709\u4e0a\u767b\u65f6\u5212\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u9c9c\u8840\u76f4\u6d41\u3002 \u3000\u3000\u5927\u6c49\u6599\u60f3\u4ed6\u518d\u4e5f\u4e0d\u6562\u8ffd\u6765\uff0c\u63d0\u4e86\u5c0f\u6167\u5c31\u8d70\u3002\u54ea\u77e5\u8881\u627f\u5fd7\u72b9\u5982\u75af\u4e86\u4e00\u822c\uff0c\u7d27\u7d27\u62b1\u4f4f\u5927\u6c49\u5de6\u811a\uff0c\u767e\u5fd9\u4e2d\u8fd8\u4f7f\u51fa\u4f0f\u864e\u638c\u6cd5\uff0c\u4e00\u4e2a\u201c\u5012\u626d\u91d1\u949f\u201d\uff0c\u5c06\u4ed6\u5de6\u817f\u626d\u8f6c\u3002\u8981\u77e5\u8881\u5d07\u7115\u662f\u5e7f\u4e1c\u4e1c\u839e\u4eba\uff0c\u8881\u627f\u5fd7\u8840\u4e2d\u79c9\u627f\u7740\u5e7f\u4e1c\u4eba\u90a3\u80a1\u5b81\u6b7b\u4e0d\u5c48\u7684\u5014\u5f3a\u6027\u5b50\uff0c\u867d\u7136\u60c5\u52bf\u5371\u6025\uff0c\u4ecd\u662f\u4e0d\u8ba9\u5c0f\u6167\u7ed9\u654c\u4eba\u64d2\u53bb\u3002 \u3000\u3000\u90a3\u5927\u6c49\u53c8\u75db\u53c8\u6c14\uff0c\u53f3\u817f\u8d77\u5904\uff0c\u628a\u4ed6\u8e22\u4e86\u4e2a\u7b4b\u6597\uff0c\u4e3e\u5200\u6b63\u8981\u780d\u4e0b\uff0c\u5ffd\u542c\u80cc\u540e\u6709\u4eba\u559d\u65a5\uff0c\u8ddf\u7740\u540e\u8111\u4e0a\u549a\u7684\u4e00\u58f0\uff0c\u4e00\u9635\u75bc\u75db\uff0c\u540e\u9888\u4e2d\u8ddf\u7740\u6e7f\u6dcb\u6dcb\u3001\u7c98\u817b\u817b\u5730\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u7ed9\u4eba\u6253\u5f97\u540e\u8111\u52fa\u5b50\u6d41\u8840\uff0c\u5fc3\u4e0b\u60ca\u60f6\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u5b89\u5927\u5a18\u53cc\u624b\u626c\u8d77\uff0c\u7ad9\u5728\u6570\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u90a3\u5927\u6c49\u77e5\u5979\u5389\u5bb3\uff0c\u820d\u4e86\u8881\u627f\u5fd7\uff0c\u62b1\u4f4f\u5c0f\u6167\u8981\u8d70\u3002\u5b89\u5927\u5a18\u53f3\u624b\u8fde\u626c\uff0c\u4e09\u679a\u9e21\u86cb\u63a5\u8fde\u5411\u4ed6\u9762\u95e8\u6253\u53bb\u3002\u5927\u6c49\u4e1c\u8eb2\u897f\u95e8\uff0c\u907f\u5f00\u4e86\u4e24\u679a\uff0c\u7b2c\u4e09\u679a\u518d\u4e5f\u95ea\u907f\u4e0d\u5f00\uff0c\u6251\u7684\u4e00\u58f0\uff0c\u6b63\u4e2d\u9f3b\u6881\uff0c\u6ee1\u8138\u5b50\u90fd\u662f\u86cb\u9ec4\u86cb\u767d\u3002\u5b89\u5927\u5a18\u4ece\u7bee\u4e2d\u4e00\u638f\uff0c\u6478\u5230\u6700\u540e\u4e00\u679a\u9e21\u86cb\uff0c\u53c8\u662f\u4e00\u4e0b\u6253\u5728\u4ed6\u5de6\u76ee\u4e4b\u4e0a\u3002\u5979\u624b\u52b2\u4e0d\u5f31\uff0c\u867d\u662f\u4e00\u679a\u9e21\u86cb\uff0c\u53ef\u4e5f\u5df2\u6253\u5f97\u4ed6\u5934\u6655\u773c\u82b1\u3002 \u3000\u3000\u90a3\u5927\u6c49\u9a82\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u4f60\u4e0d\u7092\u9e21\u86cb\u8bf7\u8001\u5b50\u5403\uff0c\u5374\u7528\u9e21\u86cb\u6253\u8001\u5b50\uff01\u201d \u3000\u3000\u629b\u4e0b\u5c0f\u6167\uff0c\u5de6\u624b\u5728\u773c\u4e0a\u62b9\u4e86\u51e0\u4e0b\uff0c\u4e3e\u5200\u5411\u5b89\u5927\u5a18\u6740\u6765\u3002\u5b89\u5927\u5a18\u624b\u4e2d\u6ca1\u5175\u5203\uff0c\u53ea\u5f97\u8fde\u8fde\u95ea\u907f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u5371\u6025\uff0c\u633a\u53c9\u53c8\u5411\u5927\u6c49\u540e\u5fc3\u523a\u53bb\uff0c\u8fd9\u65f6\u4ed6\u89c1\u6765\u4e86\u5e2e\u624b\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u4e00\u67c4\u706b\u53c9\u6311\u523a\u906e\u62e6\uff0c\u201c\u5cb3\u5bb6\u795e\u67aa\u201d\u7684\u67aa\u6cd5\u4f7f\u5f97\u4f3c\u6a21\u4f3c\u6837\u3002 \u3000\u3000\u5b89\u5927\u5a18\u7f13\u51fa\u4e86\u624b\uff0c\u7075\u673a\u4e00\u52a8\uff0c\u628a\u4e70\u6765\u7ed9\u8881\u627f\u5fd7\u505a\u8863\u670d\u7684\u4e00\u5339\u5e03\u4ece\u7bee\u4e2d\u53d6\u4e86\u51fa\u6765\uff0c\u8fce\u98ce\u6296\u5f00\uff0c\u629b\u5165\u8eab\u540e\u7684\u5c0f\u6eaa\uff0c\u8ddf\u7740\u6361\u8d77\u4e09\u5757\u77f3\u5b50\u5411\u5927\u6c49\u6253\u53bb\u3002\u5927\u6c49\u65e2\u8981\u95ea\u907f\u77f3\u5b50\uff0c\u53c8\u8981\u62db\u67b6\u8881\u627f\u5fd7\u7684\u706b\u53c9\uff0c\u8fde\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u5b89\u5927\u5a18\u62ff\u8d77\u6d78\u6e7f\u7684\u5e03\u5339\uff0c\u559d\u9053\uff1a\u201c\u80e1\u8001\u4e09\uff0c\u4f60\u4e58\u6211\u4e0d\u5728\u5bb6\uff0c\u4e0a\u95e8\u6765\u6b3a\u4fae\u5c0f\u5b69\u5b50\uff0c\u7b97\u662f\u54ea\u4e00\u95e8\u5b50\u7684\u597d\u6c49\uff1f\u201d\u547c\u559d\u58f0\u4e2d\uff0c\u4e00\u5339\u5e03\u5df2\u5411\u5927\u6c49\u8fce\u9762\u6253\u53bb\u3002\u5979\u7684\u5185\u529b\u867d\u8fd8\u4e0d\u8db3\u4ee5\u5f53\u771f\u201c\u675f\u6e7f\u6210\u68cd\u201d\uff0c\u628a\u4e00\u5339\u5e03\u5f53\u4f5c\u68cd\u5b50\u4f7f\uff0c\u4f46\u957f\u5e03\u6d78\u6c34\uff0c\u6325\u51fa\u6765\u5374\u4e5f\u9887\u6709\u529b\u9053\u3002\u80e1\u8001\u4e09\u76b1\u8d77\u7709\u5934\uff0c\u62ac\u817f\u628a\u8881\u627f\u5fd7\u8e39\u5012\uff0c\u4e0e\u5b89\u5927\u5a18\u6597\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5b89\u5927\u5a18\u7684\u6b66\u529f\u672c\u5c31\u5728\u80e1\u8001\u4e09\u4e4b\u4e0a\uff0c\u6b64\u65f6\u5fc3\u4e2d\u6124\u6068\uff0c\u4e00\u5339\u6e7f\u5e03\u6325\u51fa\u6765\u66f4\u662f\u6709\u529b\u3002\u80e1\u8001\u4e09\u80cc\u4e0a\u8fde\u88ab\u5e03\u7aef\u6253\u4e2d\u4e24\u4e0b\uff0c\u6c34\u73e0\u56db\u6e85\uff0c\u53ea\u89c9\u80cc\u5fc3\u9690\u9690\u53d1\u75db\uff0c\u51fa\u624b\u7a0d\u6162\uff0c\u5355\u5200\u7a81\u88ab\u6e7f\u5e03\u88f9\u4f4f\u3002\u5b89\u5927\u5a18\u7528\u529b\u56de\u626f\uff0c\u80e1\u8001\u4e09\u5355\u5200\u8131\u624b\u3002 \u3000\u3000\u4ed6\u7eb5\u51fa\u4e24\u6b65\uff0c\u72de\u7b11\u9053\uff1a\u201c\u6211\u662f\u53d7\u4f60\u4e08\u592b\u4e4b\u6258\uff0c\u6765\u63a5\u4ed6\u5973\u513f\u56de\u53bb\u3002\u9634\u9b42\u4e0d\u6563\uff0c\u603b\u6709\u4e00\u5929\u518d\u627e\u4e0a\u4f60\u3002\u5c0f\u6cfc\u5987\uff0c\u6211\u4eec\u9526\u8863\u536b\u7684\u4eba\u4f60\u4e5f\u6562\u5f97\u7f6a\uff0c\u5f53\u771f\u4e0d\u6015\u738b\u6cd5\u4e48\uff1f\u201d\u5b89\u5927\u5a18\u79c0\u7709\u76f4\u7ad6\uff0c\u5c06\u6e7f\u5e03\u6a2a\u626b\u8fc7\u53bb\u3002\u80e1\u8001\u4e09\u65e9\u9632\u5230\u5979\u8fd9\u7740\uff0c\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5df2\u8f6c\u8eab\u8dc3\u51fa\uff0c\u8fdc\u8fdc\u7684\u621f\u6307\u9a82\u9053\uff1a\u201c\u4ed6\u5988\u7684\uff0c\u4eca\u5929\u4f60\u8bf7\u6211\u5403\u751f\u9e21\u86cb\uff0c\u8001\u5b50\u4e0b\u6b21\u6349\u4e86\u4f60\u5173\u5165\u5929\u7262\uff0c\u8bf7\u4f60\u5c41\u80a1\u5403\u7b0b\u7092\u8089\uff0c\u5341\u6839\u7af9\u7b7e\u63d2\u8fdb\u4f60\u7684\u6307\u7532\u7f1d\uff0c\u90a3\u65f6\u4f60\u624d\u77e5\u9053\u6ecb\u5473\uff01\u4eca\u65e5\u77a7\u5728\u4f60\u8001\u516c\u4efd\u4e0a\uff0c\u4e14\u9976\u4f60\u4e00\u906d\u3002\u201d\u9a82\u4e86\u51e0\u53e5\uff0c\u5411\u5c71\u4e0b\u75be\u5954\u800c\u53bb\u3002\u5b89\u5927\u5a18\u4e5f\u4e0d\u8ffd\u8d76\uff0c\u56de\u5934\u6765\u770b\u5c0f\u6167\u4e0e\u8881\u627f\u5fd7\u3002 \u3000\u3000\u5c0f\u6167\u5e76\u6ca1\u53d7\u4f24\uff0c\u53ea\u662f\u5413\u5f97\u6014\u6014\u7684\u50bb\u4e86\u4e00\u822c\uff0c\u9694\u4e86\u4e00\u4f1a\uff0c\u624d\u6251\u5728\u6bcd\u4eb2\u6000\u91cc\u54ed\u4e86\u51fa\u6765\u3002\u8881\u627f\u5fd7\u5374\u6ee1\u8138\u6ee1\u8eab\u90fd\u662f\u9c9c\u8840\u3002\u5b89\u5927\u5a18\u5fd9\u7ed9\u4ed6\u6d17\u62b9\u5e72\u51c0\uff0c\u53d6\u51fa\u5200\u4f24\u836f\u7ed9\u4ed6\u88f9\u597d\uff0c\u5e78\u800c\u4e24\u5904\u5200\u4f24\u53e3\u5b50\u90fd\u4e0d\u6df1\uff0c\u6d41\u8840\u867d\u591a\uff0c\u5e76\u65e0\u5927\u788d\u3002\u5b89\u5927\u5a18\u628a\u4ed6\u62b1\u5230\u5e8a\u4e0a\u7761\u4e86\uff0c\u5c0f\u6167\u624d\u4e00\u4e94\u4e00\u5341\u5730\u628a\u4ed6\u521a\u624d\u820d\u547d\u76f8\u6551\u7684\u60c5\u5f62\u8bf4\u4e86\u3002 \u3000\u3000\u5b89\u5927\u5a18\u671b\u7740\u8881\u627f\u5fd7\uff0c\u5fc3\u60f3\uff1a\u201c\u77a7\u4e0d\u51fa\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u5982\u6b64\u4fa0\u4e49\u5fc3\u80a0\u3002 \u3000\u3000\u54b1\u4eec\u5728\u8fd9\u91cc\u662f\u4e0d\u80fd\u803d\u4e86\uff0c\u5012\u8981\u597d\u597d\u6210\u5168\u4ed6\u4e00\u756a\u3002\u201d\u5bf9\u5c0f\u6167\u9053\uff1a\u201c\u4f60\u4e5f\u53bb\u7761\uff0c\u4eca\u5929\u665a\u4e0a\u54b1\u4eec\u5c31\u5f97\u8d70\u3002\u201d \u3000\u3000\u5c0f\u6167\u968f\u7740\u5979\u6bcd\u4eb2\u4e1c\u8fc1\u897f\u642c\u60ef\u4e86\u7684\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u5947\u3002\u5b89\u5927\u5a18\u6536\u62fe\u4e86\u4e00\u4e0b\u968f\u8eab\u7269\u4ef6\uff0c\u6253\u4e86\u4e24\u4e2a\u5305\u88f9\u3002\u4e09\u4eba\u5403\u8fc7\u665a\u996d\u540e\uff0c\u79c9\u70db\u800c\u5750\u3002\u5979\u5e76\u4e0d\u95e9\u95e8\uff0c\u4f3c\u4e4e\u53e6\u6709\u6240\u5f85\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u79c0\u7709\u7d27\u8e59\uff0c\u652f\u9890\u51fa\u795e\uff0c\u4e00\u4f1a\u513f\u773c\u7736\u7ea2\u4e86\uff0c\u4fbf\u4f3c\u8981\u6389\u4e0b\u6cea\u6765\uff0c\u5fc3\u60f3\uff0c\u201c\u90a3\u80e1\u8001\u4e09\u8bf4\uff0c\u5b89\u5a76\u5a76\u7684\u4e08\u592b\u6d3e\u4ed6\u6765\u63a5\u5c0f\u6167\u56de\u53bb\uff0c\u4e0d\u77e5\u4e3a\u4e86\u751a\u4e48\u3002\u5979\u4e08\u592b\u6b3a\u4fae\u5b89\u5a76\u5a76\uff0c\u7b49\u6211\u957f\u5927\u4e86\uff0c\u7ec3\u597d\u4e86\u6b66\u827a\uff0c\u5b9a\u8981\u6253\u5979\u4e08\u592b\u4e00\u987f\uff0c\u7ed9\u5b89\u5a76\u5a76\u51fa\u6c14\u3002\u53ea\u662f\u5c0f\u6167\u89c1\u6211\u6253\u5979\u7239\u7239\uff0c\u4e0d\u77e5\u4f1a\u4e0d\u4f1a\u4e0d\u9ad8\u5174\u3002\u201d\u53c8\u60f3\uff1a\u201c\u90a3\u80e1\u8001\u4e09\u8bf4\u4ed6\u662f\u9526\u8863\u536b\u7684\uff0c\u54fc\uff0c\u9526\u8863\u536b\u7684\u4eba\u574f\u6b7b\u4e86\uff0c\u6211\u5988\u5988\u4fbf\u662f\u7ed9\u4ed6\u4eec\u6349\u53bb\u5bb3\u6b7b\u7684\u3002\u7ec8\u6709\u4e00\u5929\uff0c\u6211\u8981\u5927\u6740\u9526\u8863\u536b\u7684\u4eba\uff0c\u7ed9\u5988\u5988\u62a5\u4ec7\u3002\u201d \u3000\u3000\u8881\u5d07\u7115\u88ab\u5d07\u796f\u5904\u6b7b\u540e\uff0c\u5144\u5f1f\u59bb\u5b50\u90fd\u88ab\u7687\u5e1d\u4e0b\u65e8\u5145\u519b\u4e09\u5343\u91cc\u3002\u9526\u8863\u536b\u5230\u8881\u5bb6\u62ff\u4eba\uff0c\u8881\u5d07\u7115\u7684\u65e7\u90e8\u5148\u5df2\u5f97\u8baf\uff0c\u8d76\u53bb\u5c06\u8881\u627f\u5fd7\u6551\u4e86\u51fa\u6765\uff0c\u8881\u592b\u4eba\u5374\u672a\u80fd\u6551\u51fa\u3002\u5f53\u5e74\u9526\u8863\u536b\u6284\u5bb6\u62ff\u4eba\u3001\u5982\u864e\u4f3c\u72fc\u7684\u51f6\u72e0\u6a21\u6837\uff0c\u5df2\u6df1\u5370\u5728\u8881\u627f\u5fd7\u5c0f\u5c0f\u7684\u8111\u6d77\u4e4b\u4e2d\u3002 \u3000\u3000\u4e8c\u66f4\u65f6\u5206\uff0c\u95e8\u5916\u8f7b\u8f7b\u4f20\u6765\u4e00\u9635\u811a\u6b65\u58f0\uff0c\u4e00\u4eba\u98d8\u7136\u8fdb\u6765\uff0c\u539f\u6765\u4fbf\u662f\u90a3\u4e2a\u54d1\u5df4\u3002\u4ed6\u8eab\u6750\u9b41\u68a7\u58ee\u5b9e\uff0c\u884c\u8def\u5374\u8f7b\u98d8\u98d8\u7684\uff0c\u843d\u5730\u4ec5\u6709\u5fae\u58f0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5230\u54d1\u5df2\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u6251\u4e0a\u53bb\u62c9\u4f4f\u4e86\u4ed6\uff0c\u8fde\u95ee\uff1a\u201c\u5d14\u53d4\u53d4\u5462\uff1f\u4ed6\u597d\u4e48\uff1f\u201d\u7adf\u5fd8\u4e86\u4ed6\u662f\u54d1\u7684\u3002\u54d1\u5df4\u54a7\u5f00\u4e86\u5634\u53ea\u662f\u50bb\u7b11\uff0c\u663e\u7136\u518d\u89c1\u5230\u8881\u627f\u5fd7\u4e5f\u5f88\u9ad8\u5174\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u5411\u5b89\u5927\u5a18\u6307\u624b\u5212\u811a\u7684\u4f5c\u4e86\u4e00\u9635\u624b\u52bf\u3002 \u3000\u3000\u5b89\u5927\u5a18\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5d14\u53d4\u53d4\u6ca1\u4e8b\uff0c\u4f60\u653e\u5fc3\u3002\u201d\u548c\u54d1\u5df4\u6253\u4e86\u4e00\u9635\u624b\u52bf\uff0c\u54d1\u5df2\u4e0d\u4f4f\u70b9\u5934\uff0c\u53cc\u624b\u8fde\u8fde\u9f13\u638c\uff0c\u62cd\u62cd\u58f0\u54cd\u3002\u8881\u627f\u5fd7\u5374\u4e0d\u77e5\u4ed6\u5bf9\u751a\u4e48\u4e8b\u5982\u6b64\u8877\u5fc3\u8d5e\u6210\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e09\u56de\u3000\u7ecf\u5e74\u4eb2\u5251\u94d7 \u957f\u65e5\u5bf9\u6978\u67b0",content:(0,t.jsx)(t.Fragment,{children:"\u5b89\u5927\u5a18\u62c9\u7740\u8881\u627f\u5fd7\uff0c\u8d70\u5230\u5185\u5ba4\uff0c\u5e76\u6392\u5750\u5728\u5e8a\u6cbf\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u6211\u4e00\u89c1\u4f60\u5c31\u5f88\u559c\u6b22\uff0c\u5c31\u5f53\u4f60\u662f\u6211\u7684\u4eb2\u513f\u5b50\u4e00\u822c\u3002\u4eca\u5929\u4f60\u4e0d\u987e\u6027\u547d\u6551\u4e86\u5c0f\u6167\uff0c\u6211\u66f4\u52a0\u6c38\u8fdc\u5fd8\u4e0d\u4e86\u4f60\u3002\u4eca\u665a\u6211\u8981\u5230\u4e00\u4e2a\u5f88\u8fdc\u7684\u5730\u65b9\u53bb\u3002\u4f60\u8ddf\u7740\u54d1\u4f2f\u4f2f\u53bb\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u548c\u4f60\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u5b89\u5927\u5a18\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u820d\u4e0d\u5f97\u4f60\u554a\u3002\u6211\u8981\u54d1\u4f2f\u4f2f\u5e26\u4f60\u5230\u4e00\u4e2a\u4eba\u90a3\u91cc\u3002\u4ed6\u662f\u4f60\u5d14\u53d4\u53d4\u7684\u8bb0\u540d\u5e08\u7236\u3002\u4f60\u5d14\u53d4\u53d4\u53ea\u8ddf\u4ed6\u5b66\u4e86\u4e24\u4e2a\u6708\u6b66\u827a\uff0c\u5c31\u8fd9\u822c\u4e86\u5f97\u3002\u8fd9\u4f4d\u8001\u524d\u8f88\u7684\u6b66\u529f\u5929\u4e0b\u65e0\u53cc\uff0c\u6211\u8981\u4f60\u53bb\u8ddf\u4ed6\u5b66\u3002\u201d\u8881\u627f\u5fd7\u542c\u5f97\u60a0\u7136\u795e\u5f80\u3002 \u3000\u3000\u5b89\u5927\u5a18\u9053\uff1a\u201c\u4ed6\u5e73\u751f\u53ea\u6536\u8fc7\u4e24\u4e2a\u771f\u6b63\u7684\u5f92\u5f1f\uff0c\u90a3\u90fd\u662f\u8bb8\u591a\u5e74\u524d\u7684\u4e8b\u4e86\uff0c\u53ea\u6015\u4ed6\u672a\u5fc5\u80af\u518d\u6536\u5f92\u5f1f\u3002\u4e0d\u8fc7\u4f60\u8d44\u8d28\u597d\uff0c\u5fc3\u5730\u53c8\u5584\u826f\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u559c\u6b22\u3002\u54d1\u4f2f\u4f2f\u662f\u4ed6\u4ec6\u4eba\uff0c\u6211\u8bf7\u4ed6\u5e26\u4f60\u53bb\u6c42\u4ed6\u3002\u4f60\u597d\u597d\u53bb\u5427\u3002\u8981\u662f\u4ed6\u771f\u7684\u4e0d\u80af\u6536\u4f60\uff0c\u54d1\u4f2f\u4f2f\u4f1a\u628a\u4f60\u9001\u56de\u5230\u6211\u8fd9\u91cc\u3002\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5b89\u5927\u5a18\u53c8\u53ee\u5631\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u524d\u8f88\u813e\u6c14\u5f88\u53e4\u602a\uff0c\u4f60\u4e0d\u542c\u8bdd\uff0c\u4ed6\u56fa\u7136\u4e0d\u559c\u6b22\uff0c\u592a\u542c\u8bdd\u4e86\uff0c\u4ed6\u53c8\u5acc\u4f60\u592a\u7b28\uff0c\u6ca1\u9aa8\u6c14\uff0c\u53ea\u597d\u78b0\u4f60\u7684\u7f18\u6cd5\u5427\u3002\u201d\u4ece\u8155\u4e0a\u8131\u4e0b\u4e00\u53ea\u91d1\u4e1d\u956f\u5b50\u6765\uff0c\u7ed9\u4ed6\u6234\u5728\u81c2\u4e0a\uff0c\u8f7b\u8f7b\u4e00\u634f\uff0c\u91d1\u4e1d\u956f\u5b50\u5df2\u7ecf\u6536\u5c0f\uff0c\u4e0d\u518d\u843d\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u7b49\u4f60\u6b66\u529f\u5b66\u597d\uff0c\u6210\u4e3a\u5927\u5b69\u5b50\u65f6\uff0c\u522b\u5fd8\u8bb0\u5b89\u5a76\u5a76\u548c\u5c0f\u6167\u59b9\u5b50\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u8981\u662f\u90a3\u4f4d\u8001\u524d\u8f88\u80af\u6536\u6211\uff0c\u5b89\u5a76\u5a76\u4f60\u6709\u7a7a\u65f6\uff0c\u5c31\u5e26\u5c0f\u6167\u59b9\u59b9\u6765\u77a7\u77a7\u6211\u3002\u201d\u5b89\u4eba\u5a18\u773c\u5708\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u7684\uff0c\u6211\u4f1a\u65f6\u65f6\u8bb0\u7740\u4f60\u3002\u201d \u3000\u3000\u5b89\u5927\u5a18\u5199\u4e86\u4e00\u5c01\u4fe1\uff0c\u4ea4\u7ed9\u54d1\u5df4\u8f6c\u5448\u4ed6\u4e3b\u4eba\u3002\u56db\u4eba\u51fa\u95e8\uff0c\u5206\u9053\u800c\u522b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e0e\u5b89\u5927\u5a18\u53ca\u5c0f\u6167\u867d\u7136\u76f8\u5904\u5e76\u65e0\u591a\u65e5\uff0c\u4f46\u6bcd\u5973\u4e8c\u4eba\u5f85\u4ed6\u6781\u4e3a\u4eb2\u5207\uff0c\u65e5\u95f4\u4e00\u6218\uff0c\u66f4\u662f\u5171\u7ecf\u751f\u6b7b\u60a3\u96be\uff0c\u5206\u522b\u65f6\u5747\u611f\u604b\u604b\u4e0d\u820d\u3002 \u3000\u3000\u54d1\u5df4\u77e5\u9053\u8881\u627f\u5fd7\u53d7\u4e86\u4f24\uff0c\u6d41\u8840\u751a\u591a\uff0c\u8eab\u5b50\u8870\u5f31\uff0c\u4e8e\u662f\u628a\u4ed6\u62b1\u5728\u624b\u91cc\uff0c\u8fc8\u5f00\u5927\u6b65\uff0c\u884c\u8d70\u82e5\u98de\u3002 \u3000\u3000\u8fd9\u822c\u6653\u884c\u591c\u5bbf\uff0c\u4e0d\u65ad\u7684\u5411\u5317\u884c\u4e86\u4e00\u4e2a\u591a\u6708\u3002\u8881\u627f\u5fd7\u4f24\u5904\u4e5f\u5df2\u597d\u4e86\uff0c\u53ea\u662f\u5de6\u7709\u4e0a\u7559\u4e0b\u4e00\u4e2a\u5c0f\u5c0f\u75a4\u75d5\u3002\u6bcf\u65e5\u508d\u665a\uff0c\u54d1\u5df4\u4e5f\u4e0d\u5728\u5ba2\u5e97\u6295\u5bbf\uff0c\u968f\u4fbf\u627e\u4e2a\u5ca9\u6d1e\u6216\u662f\u7834\u5e99\u6b47\u4e86\u3002\u5728\u5ba2\u5e97\u6253\u5c16\u65f6\uff0c\u90fd\u662f\u8881\u627f\u5fd7\u51fa\u53e3\u8981\u98df\u7269\u3002\u54d1\u5df4\u5bf9\u5403\u751a\u4e48\u5e76\u65e0\u4e3b\u89c1\uff0c\u62ff\u6765\u5c31\u5403\uff0c\u4e00\u987f\u81f3\u5c11\u8981\u5403\u4e24\u65a4\u9762\u3002\u8881\u627f\u5fd7\u6253\u624b\u52bf\u95ee\u4ed6\u5230\u751a\u4e48\u5730\u65b9\uff0c\u4ed6\u603b\u662f\u5411\u5317\u800c\u6307\u3002 \u3000\u3000\u53c8\u884c\u591a\u65e5\uff0c\u6df1\u5165\u7fa4\u5c71\uff0c\u6108\u8d70\u6108\u9ad8\uff0c\u5230\u540e\u6765\u5df2\u65e0\u9053\u8def\u53ef\u5faa\u3002 \u3000\u3000\u54d1\u5df4\u624b\u8db3\u5e76\u7528\uff0c\u6500\u85e4\u9644\u845b\uff0c\u5c3d\u5f80\u9ad8\u5c71\u4e0a\u722c\u53bb\u3002\u8881\u627f\u5fd7\u63fd\u4f4f\u4e86\u4ed6\u5934\u9888\uff0c\u89c1\u5c71\u52bf\u5982\u6b64\u51f6\u9669\uff0c\u53cc\u624b\u62da\u547d\u6402\u7d27\uff0c\u552f\u6050\u4e00\u5931\u4fbf\u7c89\u8eab\u788e\u9aa8\u3002\u5982\u6b64\u6500\u767b\u4e86\u4e00\u5929\uff0c\u4e0a\u4e86\u4e00\u5ea7\u9ad8\u5cf0\u7684\u7edd\u9876\uff0c\u53ea\u89c1\u5cf0\u9876\u662f\u5757\u5927\u5e73\u5730\uff0c\u56db\u5468\u53e4\u677e\u8038\u7acb\uff0c\u7a7f\u8fc7\u677e\u6797\uff0c\u773c\u524d\u51fa\u73b0\u4e94\u516d\u95f4\u65e7\u5c4b\u3002 \u3000\u3000\u54d1\u5df4\u8138\u9732\u7b11\u5bb9\uff0c\u4f3c\u662f\u4e45\u5ba2\u5728\u5916\u3001\u56de\u5f52\u6545\u4e61\u4e00\u822c\u3002\u4ed6\u62c9\u7740\u8881\u627f\u5fd7\u7684\u624b\u8d70\u8fdb\u77f3\u5c4b\uff0c\u5c4b\u5185\u5c18\u5c01\u86db\u7ed3\uff0c\u663e\u662f\u8bb8\u4e45\u6ca1\u4eba\u4f4f\u4e86\u3002\u4ed6\u62ff\u4e86\u4e00\u628a\u5927\u626b\u5e1a\uff0c\u91cc\u91cc\u5916\u5916\u6253\u626b\u5e72\u51c0\uff0c\u7136\u540e\u70e7\u6c34\u716e\u996d\u3002\u5728\u8fd9\u9669\u5cf0\u9876\u4e0a\uff0c\u4e5f\u4e0d\u77e5\u7cae\u98df\u548c\u7528\u5177\u662f\u5982\u4f55\u642c\u8fd0\u4e0a\u6765\u7684\u3002 \u3000\u3000\u8fc7\u4e86\u4e09\u5929\uff0c\u8881\u627f\u5fd7\u5fc3\u6025\u8d77\u6765\uff0c\u505a\u624b\u52bf\u95ee\u5e08\u7236\u5728\u751a\u4e48\u5730\u65b9\u3002 \u3000\u3000\u54d1\u5df4\u6307\u6307\u5c71\u4e0b\uff0c\u8881\u627f\u5fd7\u793a\u610f\u8981\u4e0b\u53bb\uff0c\u54d1\u5df4\u5374\u6447\u5934\u4e0d\u8bb8\u3002\u8881\u627f\u5fd7\u65e0\u5948\uff0c\u53ea\u5f97\u82e6\u6328\u4e0b\u53bb\uff0c\u4e0e\u54d1\u5df4\u8a00\u8bed\u4e0d\u901a\uff0c\u9669\u5cf0\u7d22\u5c45\uff0c\u9887\u82e6\u5bc2\u5bde\uff0c\u5fc6\u53ca\u4e0e\u5b89\u5927\u5a18\u6bcd\u5973\u76f8\u5904\u65f6\u7684\u6e29\u99a8\u65f6\u65e5\uff0c\u6068\u4e0d\u5f97\u80fd\u63d2\u7fc5\u98de\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4e00\u5929\u665a\u4e0a\uff0c\u7761\u68a6\u4e2d\u5ffd\u89c9\u706f\u5149\u523a\u773c\uff0c\u63c9\u63c9\u773c\u775b\uff0c\u5750\u8d77\u8eab\u6765\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u4eba\u624b\u6267\u8721\u70db\uff0c\u7ad9\u5728\u5e8a\u524d\u3002\u90a3\u8001\u4eba\u987b\u7709\u4ff1\u767d\uff0c\u4f46\u7ea2\u5149\u6ee1\u9762\uff0c\u7b11\u563b\u563b\u7684\u6253\u91cf\u7740\u81ea\u5df1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u722c\u4e0b\u7095\u6765\uff0c\u606d\u606d\u656c\u656c\u7684\u5411\u4ed6\u78d5\u4e86\u56db\u4e2a\u5934\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8001\u4eba\u5bb6\u53ef\u6765\u5566\uff01\u201d\u90a3\u8001\u4eba\u5475\u5475\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5a03\u513f\uff0c\u8c01\u6559\u4f60\u53eb\u6211\u5e08\u7236\u7684\uff1f\u4f60\u600e\u77e5\u6211\u51c6\u80af\u6536\u4f60\u4e3a\u5f92\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u4ed6\u8bed\u6c14\uff0c\u77e5\u9053\u4ed6\u662f\u80af\u6536\u4e86\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5b89\u5a76\u5a76\u6559\u6211\u7684\u3002\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u5979\u5c31\u662f\u7ed9\u6211\u6dfb\u9ebb\u70e6\u3002\u597d\u5427\uff0c\u77a7\u4f60\u6545\u4e16\u7684\u7236\u4eb2\u4efd\u4e0a\uff0c\u5c31\u6536\u4e86\u4f60\u5427\uff01\u201d\u8881\u627f\u5fd7\u53c8\u8981\u78d5\u5934\uff0c\u90a3\u8001\u4eba\u9053\uff1a\u201c\u591f\u4e86\uff0c\u591f\u4e86\uff0c\u660e\u5929\u518d\u8bf4\u3002\u201d \u3000\u3000\u6b21\u65e5\u65e9\u6668\u5929\u8fd8\u6ca1\u4eae\uff0c\u8881\u627f\u5fd7\u5c31\u8d77\u6765\u4e86\u3002\u54d1\u5df4\u77e5\u9053\u8001\u4eba\u7b54\u5e94\u6536\u4ed6\uff0c\u559c\u5f97\u628a\u4ed6\u629b\u5411\u7a7a\u4e2d\uff0c\u968f\u624b\u63a5\u4f4f\uff0c\u8fde\u629b\u4e86\u56db\u4e94\u6b21\u3002 \u3000\u3000\u90a3\u8001\u4eba\u542c\u5f97\u8881\u627f\u5fd7\u5b09\u7b11\u4e4b\u58f0\uff0c\u8e31\u51fa\u623f\u6765\uff0c\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u5df2\u77e5\u9053\u884c\u4fa0\u4ed7\u4e49\uff0c\u6551\u4eba\u5987\u5b7a\u3002\u90a3\u53ef\u4e86\u4e0d\u8d77\u54ea\uff01\u4f60\u6709\u751a\u4e48\u672c\u4e8b\uff0c\u5012\u4f7f\u51fa\u6765\u7ed9\u6211\u77a7\u77a7\u3002\u201d\u8881\u627f\u5fd7\u7ed9\u4ed6\u8bf4\u5f97\u9762\u7ea2\u8fc7\u8033\uff0c\u5ff8\u6029\u4e0d\u5b89\u3002 \u3000\u3000\u90a3\u8001\u4eba\u7b11\u9053\uff1a\u201c\u4e0d\u8ba9\u6211\u77a7\u4f60\u7684\u529f\u592b\uff0c\u600e\u4e48\u6559\u4f60\u554a\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u624d\u77e5\u5e08\u7236\u5e76\u975e\u8ddf\u81ea\u5df1\u5f00\u73a9\u7b11\uff0c\u4e8e\u662f\u628a\u5d14\u79cb\u5c71\u6240\u4f20\u7684\u4f0f\u864e\u638c\u6cd5\u4ece\u5934\u81f3\u5c3e\u7ec3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u8001\u4eba\u4e00\u9762\u770b\u4e00\u9762\u5fae\u7b11\uff0c\u5f85\u4ed6\u7ec3\u5b8c\uff0c\u7b11\u9053\uff1a\u201c\u79cb\u5c71\u4e0d\u4f4f\u5938\u4f60\u806a\u660e\uff0c\u6211\u5148\u8fd8\u4e0d\u4fe1\uff0c\u4ed6\u53ea\u6559\u4e86\u4f60\u51e0\u5927\uff0c\u4fbf\u6709\u8fd9\u822c\u6210\u5c31\uff0c\u786e\u662f\u4e0d\u9519\u7684\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u542c\u5230\u5d14\u79cb\u5c71\u7684\u540d\u5b57\uff0c\u4fbf\u60f3\u95ee\u4ed6\u5b89\u5371\uff0c\u53ef\u662f\u8001\u4eba\u5728\u8bf4\u8bdd\uff0c\u4e0d\u6562\u6253\u65ad\u4ed6\u7684\u8bdd\u5934\uff0c\u7b49\u4ed6\u4e00\u505c\u53e3\uff0c\u5fd9\u95ee\uff1a\u201c\u5d14\u53d4\u53d4\u5728\u54ea\u91cc\uff1f\u4ed6\u597d\u5417\uff1f\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u4ed6\u8eab\u5b50\u597d\u4e86\uff0c\u56de\u5230\u674e\u95ef\u5c06\u519b\u90a3\u91cc\u6253\u4ed7\u53bb\u5566\u3002\u201d\u8881\u627f\u5fd7\u542c\u4e86\uff0c\u5f88\u662f\u6b22\u559c\u3002 \u3000\u3000\u54d1\u5df4\u6446\u4e86\u4e00\u5f20\u9999\u6848\u3002\u90a3\u8001\u4eba\u53d6\u51fa\u4e00\u5e45\u753b\uff0c\u753b\u4e0a\u7ed8\u7684\u662f\u4e00\u4e2a\u4e2d\u5e74\u4e66\u751f\uff0c\u795e\u6001\u98d8\u9038\u3002\u90a3\u8001\u4eba\u70b9\u4e86\u9999\u70db\uff0c\u5bf9\u7740\u753b\u50cf\u606d\u606d\u656c\u656c\u7684\u78d5\u4e86\u5934\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u662f\u54b1\u4eec\u534e\u5c71\u6d3e\u7684\u5f00\u5c71\u7956\u5e08\u98ce\u7956\u5e08\u7237\uff0c\u4f60\u8fc7\u6765\u78d5\u5934\u3002\u201d\u8881\u627f\u5fd7\u5411\u753b\u4e2d\u4eba\u77a7\u4e86\u4e24\u773c\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u53ef\u6bd4\u6211\u5e08\u7236\u5e74\u8f7b\u5f97\u591a\u5566\uff0c\u600e\u4e48\u53cd\u800c\u662f\u7956\u5e08\u7237\uff1f\u201d\u5f53\u4e0b\u8fc7\u53bb\u78d5\u5934\uff0c\u4e0d\u77e5\u8be5\u78d5\u51e0\u4e2a\u5934\uff0c\u5fc3\u60f3\u603b\u662f\u8d8a\u591a\u8d8a\u597d\uff0c\u76f4\u78d5\u5230\u90a3\u8001\u4eba\u7b11\u7740\u53eb\u4ed6\u505c\u6b62\u624d\u7f62\u3002\u90a3\u8001\u4eba\u7b11\u541f\u541f\u7684\u6b63\u8981\u5f00\u53e3\u8bf4\u8bdd\uff0c\u8881\u627f\u5fd7\u53c8\u8dea\u4e0b\u78d5\u5934\uff0c\u7b97\u662f\u6b63\u5f0f\u62dc\u5e08\u3002 \u3000\u3000\u90a3\u8001\u4eba\u5fae\u7b11\u7740\u53d7\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u800c\u540e\uff0c\u4f60\u662f\u6211\u534e\u5c71\u6d3e\u7684\u5f1f\u5b50\u4e86\u3002\u6211\u591a\u5e74\u524d\u6536\u8fc7\u4e24\u4e2a\u5f92\u5f1f\uff0c\u6b64\u540e\u4e00\u76f4\u6ca1\u518d\u9047\u5230\u806a\u9896\u80af\u5b66\u7684\u5b69\u5b50\uff0c\u8fd9\u4e9b\u5e74\u6765\u6ca1\u518d\u4f20\u4eba\u3002\u4f60\u662f\u6211\u7684\u7b2c\u4e09\u4e2a\u5f1f\u5b50\uff0c\u4e5f\u662f\u6211\u7684\u5173\u95e8\u5f92\u5f1f\u3002\u4f60\u53ef\u5f97\u597d\u597d\u7684\u5b66\uff0c\u522b\u7ed9\u6211\u4e22\u4eba\u73b0\u773c\u3002\u201d\u8881\u627f\u5fd7\u8fde\u8fde\u70b9\u5934\u3002 \u3000\u3000\u90a3\u8001\u4eba\u9053\uff1a\u201c\u6211\u59d3\u7a46\uff0c\u53eb\u505a\u7a46\u4eba\u6e05\uff0c\u6c5f\u6e56\u4e0a\u670b\u53cb\u53eb\u6211\u505a\u795e\u5251\u4ed9\u733f\u3002\u4f60\u8bb0\u7740\u70b9\uff0c\u4e0b\u6b21\u522b\u8ba9\u4eba\u5bb6\u95ee\u4f4f\uff0c\u4f60\u5e08\u7236\u53eb\u751a\u4e48\u5440\uff1f\u554a\u54df\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u8fd9\u4e2a\u53ef\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u54c8\u5f97\u4e00\u58f0\uff0c\u7b11\u4e86\u51fa\u6765\uff0c\u5fc3\u60f3\u5b89\u5927\u5a18\u8bf4\u4ed6\u813e\u6c14\u53e4\u602a\uff0c\u5fc3\u91cc\u4e00\u76f4\u6709\u70b9\u5bb3\u6015\uff0c\u54ea\u77e5\u5176\u5b9e\u4ed6\u548c\u853c\u53ef\u4eb2\uff0c\u8c08\u5410\u5f88\u662f\u8bd9\u8c10\u3002 \u3000\u3000\u795e\u5251\u4ed9\u733f\u7a46\u4eba\u6e05\u6b66\u529f\u4e4b\u9ad8\uff0c\u5f53\u4e16\u5b9e\u5df2\u53ef\u7b97\u5f97\u7b2c\u4e00\u4eba\uff0c\u5728\u6c5f\u6e56\u4e0a\u884c\u4fa0\u4ed7\u4e49\uff0c\u8fd1\u4e8c\u5341\u5e74\u6765\u4ece\u672a\u9047\u8fc7\u5bf9\u624b\uff0c\u53ea\u56e0\u6240\u4f5c\u6240\u4e3a\u5927\u534a\u5728\u6697\u4e2d\u884c\u4e8b\uff0c\u4e0d\u7559\u59d3\u540d\uff0c\u522b\u4eba\u5f80\u5f80\u4e0d\u77e5\u662f\u53d7\u4e86\u4ed6\u7684\u597d\u5904\uff0c\u662f\u4ee5\u540d\u6c14\u5374\u4e0d\u751a\u54cd\u4eae\u3002\u4ed6\u813e\u6c14\u672c\u5f88\u5b64\u50fb\uff0c\u8fd9\u6b21\u89c1\u8881\u627f\u5fd7\u5b64\u96f6\u96f6\u4e00\u4e2a\u5b69\u5b50\u5f88\u662f\u53ef\u601c\uff0c\u52a0\u4e4b\u656c\u4ed6\u7236\u4eb2\u8881\u5d07\u7115\u4e3a\u56fd\u6740\u654c\uff0c\u51a4\u5c48\u800c\u6b7b\uff0c\u662f\u4e2a\u5927\u5927\u7684\u5fe0\u81e3\uff0c\u662f\u4ee5\u5bf9\u4ed6\u7834\u4f8b\u7684\u9752\u773c\u6709\u52a0\u3002\u7a46\u4eba\u6e05\u65e0\u5b50\u65e0\u5973\uff0c\u4e00\u5251\u72ec\u884c\u6c5f\u6e56\uff0c\u4e34\u5230\u8001\u6765\uff0c\u5ffd\u7136\u89c1\u5230\u4e00\u4e2a\u806a\u660e\u6d3b\u6cfc\u7684\u5b69\u7ae5\uff0c\u5fc3\u4e2d\u7684\u559c\u6b22\uff0c\u5b9e\u5728\u4e0d\u4e0b\u4e8e\u8881\u627f\u5fd7\u7684\u5f97\u9047\u660e\u5e08\uff0c\u4e0d\u7531\u5f97\u7adf\u5927\u53cd\u5e38\u6001\uff0c\u548c\u4ed6\u6709\u8bf4\u6709\u7b11\u8d77\u6765\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u53c8\u9053\uff1a\u201c\u4f60\u90a3\u4e24\u4e2a\u5e08\u5144\u90fd\u6bd4\u4f60\u5927\u4e0a\u4e8c\u4e09\u5341\u5c81\u3002\u4ed6\u4eec\u7684\u5f92\u5f1f\u90fd\u6bd4\u4f60\u5927\u5f97\u591a\u5566\u3002\u4ed6\u4eec\u8bf4\u4e0d\u5b9a\u4f1a\u602a\u6211\uff0c\u5230\u8fd9\u65f6\u8fd8\u7ed9\u4ed6\u4eec\u6dfb\u4e2a\u5a03\u5a03\u5e08\u5f1f\u3002\u563f\u563f\uff0c\u8981\u662f\u4f60\u4e0d\u7528\u529f\uff0c\u5c06\u6765\u7ed9\u4ed6\u4eec\u7684\u5f92\u5b50\u5f92\u5b59\u6bd4\u4e0b\u53bb\uff0c\u4ed6\u4eec\u53ef\u66f4\u6709\u9053\u7406\u6765\u602a\u6211\u8fd9\u8001\u80e1\u6d82\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5f1f\u5b50\u4e00\u5b9a\u7528\u529f\u3002\u201d\u53c8\u95ee\uff1a\u201c\u5d14\u53d4\u53d4\u4e5f\u662f\u4f60\u8001\u4eba\u5bb6\u7684\u5f92\u5f1f\u5417\uff1f\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4ed6\u8981\u8ddf\u7740\u95ef\u738b\u6253\u4ed7\uff0c\u6ca1\u65f6\u5019\u8ddf\u6211\u597d\u597d\u513f\u5b66\uff0c\u6211\u53ea\u4f20\u4e86\u4ed6\u4e00\u5957\u4f0f\u864e\u638c\u6cd5\uff0c\u4e0d\u80fd\u7b97\u662f\u5f92\u5f1f\u3002\u518d\u8bf4\uff0c\u51ed\u4ed6\u8d44\u8d28\uff0c\u4e5f\u4e0d\u80fd\u505a\u6211\u5f92\u5f1f\u3002\u201d\u6307\u6307\u54d1\u5df4\u9053\uff1a\u201c\u8c61\u4ed6\uff0c\u5929\u5929\u77a7\u7740\u77a7\u7740\uff0c\u4e5f\u5b66\u4e86\u4e0d\u5c11\u62db\u513f\u53bb\u5566\uff0c\u4e0d\u8fc7\u548c\u6211\u4e24\u4e2a\u5f92\u5f1f\u76f8\u6bd4\uff0c\u53ef\u5c31\u5929\u5dee\u5730\u8fdc\u4e86\u3002\u201d\u8881\u627f\u5fd7\u89c1\u54d1\u5df4\u4e24\u6b21\u624b\u63b7\u516c\u5dee\uff0c\u51fa\u624b\u4f3c\u7535\uff0c\u4e00\u76f4\u5bf9\u4ed6\u4f69\u670d\u5f97\u4e86\u4e0d\u5f97\uff0c\u542c\u5e08\u7236\u8bf4\u81ea\u5df1\u4e24\u4f4d\u5e08\u5144\u6bd4\u4ed6\u672c\u9886\u8fd8\u9ad8\u5f97\u591a\uff0c\u90a3\u4e48\u53ea\u8981\u81ea\u5df1\u7528\u529f\uff0c\u5373\u4f7f\u53ca\u4e0d\u4e0a\u5e08\u5144\uff0c\u81f3\u5c11\u4e5f\u53ef\u8d76\u5230\u54d1\u5df4\u4e86\uff0c\u5fc3\u4e2d\u5341\u5206\u5feb\u6170\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u54b1\u4eec\u534e\u5c71\u6d3e\u6709\u8bb8\u591a\u89c4\u6761\uff0c\u751a\u4e48\u6212\u6deb\u3001\u6212\u4ed5\u3001\u6212\u4fdd\u9556\uff0c\u73b0\u4e0b\u8ddf\u4f60\u8bf4\uff0c\u4f60\u4e5f\u4e0d\u61c2\u3002\u6211\u53ea\u5631\u5490\u4f60\u4e24\u53e5\u8bdd\uff1a\u8981\u542c\u5e08\u7236\u7684\u8bdd\uff0c\u4e0d\u53ef\u505a\u574f\u4e8b\u3002\u4f60\u53ef\u5f97\u8bb0\u4f4f\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u542c\u5e08\u7236\u7684\u8bdd\uff0c\u4e5f\u4e0d\u6562\u505a\u574f\u4e8b\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u597d\uff0c\u73b0\u4e0b\u54b1\u4eec\u4fbf\u6765\u7ec3\u529f\u592b\u3002\u4f60\u5d14\u53d4\u53d4\u56e0\u65f6\u5019\u5306\u4fc3\uff0c\u628a\u4e00\u5957\u4f0f\u864e\u638c\u4e00\u53e4\u8111\u513f\u7684\u4f20\u7ed9\u4e86\u4f60\u3002\u8fd9\u5957\u638c\u6cd5\u592a\u8fc7\u6df1\u5965\u7e41\u590d\uff0c\u4f60\u5e74\u7eaa\u592a\u5c0f\uff0c\u5b66\u4e86\u4e5f\u4e0d\u80fd\u597d\u597d\u7684\u7528\u3002\u6211\u5148\u6559\u4f60\u4e00\u5957\u957f\u62f3\u5341\u6bb5\u9526\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4f1a\uff0c\u502a\u53d4\u53d4\u4ee5\u524d\u6559\u8fc7\u7684\u3002\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4f60\u4f1a\uff1f\u5b66\u5f97\u51e0\u8def\u52bf\u5b50\uff0c\u5c31\u7b97\u4f1a\u4e86\u5417\uff1f\u5dee\u5f97\u8fdc\u5462\uff01\u4f60\u8981\u662f\u771f\u7684\u61c2\u4e86\u957f\u62f3\u5341\u6bb5\u9526\u7684\u5965\u5999\uff0c\u6c5f\u6e56\u4e0a\u80dc\u5f97\u8fc7\u4f60\u7684\u4eba\u5c31\u4e0d\u591a\u4e86\u3002\u201d\u8881\u627f\u5fd7\u5c0f\u8138\u513f\u80c0\u5f97\u901a\u7ea2\uff0c\u4e0d\u6562\u518d\u8bf4\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u62c9\u5f00\u67b6\u5f0f\uff0c\u5c06\u5341\u6bb5\u9526\u4f7f\u4e86\u51fa\u6765\uff0c\u5f0f\u5b50\u62f3\u8def\uff0c\u4fbf\u548c\u502a\u6d69\u6240\u4f7f\u7684\u4e00\u6a21\u4e00\u6837\u3002\u8881\u627f\u5fd7\u6697\u6697\u7eb3\u7f55\uff0c\u5fc3\u60f3\u8fd9\u6709\u751a\u4e48\u4e0d\u540c\u4e86\uff1f \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4f60\u5f53\u5e08\u7236\u9a97\u4f60\u662f\u4e0d\u662f\uff1f\u6765\u6765\u6765\uff0c\u4f60\u6765\u6293\u6211\u8863\u670d\uff0c\u53ea\u8981\u78b0\u5f97\u5230\u6211\u4e00\u7247\u8863\u89d2\uff0c\u7b97\u4f60\u6709\u672c\u4e8b\u3002\u201d\u8881\u627f\u5fd7\u4e0d\u6562\u548c\u5e08\u7236\u8d4c\u6c14\uff0c\u7b11\u7740\u4e0d\u52a8\u3002\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u5feb\u6765\uff0c\u8fd9\u662f\u6559\u4f60\u529f\u592b\u554a\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u8bf4\u662f\u6559\u529f\u592b\uff0c\u4fbf\u62a2\u4e0a\u524d\u53bb\uff0c\u4f38\u624b\u53bb\u6478\u5e08\u7236\u957f\u886b\u540e\u895f\uff0c\u773c\u89c1\u4fbf\u53ef\u6478\u5230\uff0c\u8863\u895f\u5ffd\u7136\u4e00\u7f29\uff0c\u5c31\u53ea\u8fd9\u4e48\u5dee\u4e86\u4e24\u4e09\u5bf8\u3002 \u3000\u3000\u8881\u627f\u5fd7\u624b\u81c2\u53c8\u524d\u63a2\u6570\u5bf8\uff0c\u6b63\u8981\u5411\u8863\u895f\u6293\u53bb\uff0c\u5e08\u7236\u5ffd\u7136\u4e0d\u89c1\uff0c\u5728\u4ed6\u5934\u9888\u540e\u9762\u8f7b\u8f7b\u634f\u4e86\u4e00\u628a\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u53cc\u624b\u53cd\u62b1\uff0c\u54ea\u77e5\u5e08\u7236\u4eba\u5f71\u53c8\u5df2\u4e0d\u89c1\uff0c\u6025\u5fd9\u8f6c\u8eab\uff0c\u89c1\u5e08\u7236\u5df2\u5728\u4e24\u4e08\u4e4b\u5916\u3002\u4ed6\u751a\u89c9\u6709\u8da3\uff0c\u5fc3\u60f3\uff1a\u201c\u975e\u6293\u4f4f\u4f60\u4e0d\u53ef\u3002\u201d\u7eb5\u4e0a\u524d\u53bb\u626f\u4ed6\u8896\u5b50\u3002\u7a46\u4eba\u6e05\u5927\u8896\u4e00\u62c2\uff0c\u8eab\u5b50\u8361\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u563b\u563b\u54c8\u54c8\u7684\u8ffd\u8d76\uff0c\u4e00\u8f6c\u8eab\uff0c\u5ffd\u89c1\u54d1\u5df4\u5728\u6253\u624b\u52bf\uff0c\u8981\u4ed6\u7559\u795e\uff0c\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u60f3\uff1a\u201c\u5e08\u7236\u4f7f\u7684\u679c\u7136\u90fd\u662f\u5341\u6bb5\u9526\u8eab\u6cd5\uff0c\u4f46\u4ed6\u600e\u80fd\u5982\u6b64\u5feb\u6cd5\uff1f\u201d\u5f53\u4e0b\u4e00\u9762\u8ffd\u6349\uff0c\u4e00\u9762\u6ce8\u89c6\u5e08\u7236\u8eab\u6cd5\uff0c\u5341\u6bb5\u9526\u4ed6\u7ec3\u5f97\u672c\u719f\uff0c\u7136\u89c1\u5e08\u7236\u8fdb\u9000\u8d8b\u907f\uff0c\u7075\u4fbf\u5f02\u5e38\uff0c\u540c\u6837\u7684\u4e00\u62db\u4e00\u5f0f\uff0c\u5728\u4ed6\u4f7f\u51fa\u6765\uff0c\u5374\u53e6\u6709\u5f02\u5e38\u5de7\u601d\u3002\u8881\u627f\u5fd7\u8ffd\u8d76\u4e4b\u9645\uff0c\u6697\u5b66\u8bc0\u7a8d\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u5728\u8ffd\u8d76\u4e4b\u4e2d\u7adf\u4e5f\u7528\u4e0a\u4e86\u4e00\u4e9b\u5e08\u7236\u7684\u7eb5\u8dc3\u8d8b\u9000\u4e4b\u672f\uff0c\u679c\u7136\u767b\u65f6\u8fc5\u6377\u4e86\u8bb8\u591a\u3002\u7a46\u4eba\u6e05\u6697\u6697\u70b9\u5934\uff0c\u6df1\u559c\u5b7a\u5b50\u53ef\u6559\u3002 \u3000\u3000\u8fd9\u65f6\u8881\u627f\u5fd7\u8d76\u5f97\u7d27\uff0c\u7a46\u4eba\u6e05\u4e5f\u907f\u5f97\u5feb\uff0c\u4e24\u4eba\u6025\u5954\u75be\u8d8b\uff0c\u5e7f\u573a\u4e0a\u53ea\u89c1\u4e24\u6761\u4eba\u5f71\uff0c\u98de\u6765\u821e\u53bb\u3002\u8881\u627f\u5fd7\u65e9\u5fd8\u4e86\u5b09\u7b11\uff0c\u5168\u795e\u8d2f\u6ce8\u7684\u8ffd\u6349\u5e08\u7236\u3002 \u3000\u3000\u5ffd\u7136\u7a46\u4eba\u6e05\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u628a\u5c06\u4ed6\u62b1\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5f92\u5f1f\uff0c\u4e56\u5b69\u5b50\uff01\u201d\u8881\u627f\u5fd7\u89c1\u8fd9\u4e00\u5957\u5341\u6bb5\u9526\u4e2d\uff0c\u7adf\u6709\u5982\u8bb8\u5965\u5999\uff0c\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8\u559c\u3002\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u597d\u5566\uff0c\u8fd9\u4e9b\u5df2\u591f\u4f60\u7ec3\u5566\u3002\u201d\u628a\u4ed6\u653e\u4e0b\u5730\u6765\uff0c\u53eb\u4ed6\u590d\u4e60\u51e0\u904d\uff0c\u81ea\u884c\u5165\u5185\u3002 \u3000\u3000\u8881\u627f\u5fd7\u628a\u8fd9\u8def\u62f3\u6cd5\u4ece\u5934\u81f3\u5c3e\u7ec3\u4e86\u5341\u591a\u904d\uff0c\u9664\u4e86\u7262\u8bb0\u5e08\u7236\u8eab\u6cd5\u4e4b\u5916\uff0c\u53c8\u81ea\u884c\u609f\u51fa\u4e86\u4e00\u4e9b\u5de7\u5999\u3002\u53ea\u628a\u4ed6\u559c\u5f97\u6293\u8033\u6320\u816e\uff0c\u4e00\u591c\u6ca1\u597d\u597d\u7761\uff0c\u5c31\u662f\u5728\u68a6\u4e2d\u4e5f\u662f\u5728\u7ec3\u62f3\u3002 \u3000\u3000\u7b49\u5230\u5929\u4e00\u5fae\u4eae\uff0c\u751f\u6015\u5fd8\u4e86\u6628\u5929\u6240\u5b66\uff0c\u53c8\u5230\u5e7f\u573a\u4e0a\u7ec3\u4e86\u8d77\u6765\u3002\u8d8a\u6253\u8d8a\u662f\u8d77\u52b2\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u4e00\u58f0\u54b3\u55fd\uff0c\u5fd9\u8f6c\u8fc7\u8eab\u6765\uff0c\u89c1\u5e08\u7236\u7b11\u541f\u541f\u7684\u7ad9\u5728\u8eab\u540e\uff0c\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5e08\u7236\uff01\u201d\u5782\u624b\u7ad9\u7acb\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u609f\u51fa\u8fd9\u51e0\u62db\u90fd\u8fd8\u4e0d\u9519\u3002\u4f46\u8fd9\u4e00\u62db\u5feb\u662f\u5feb\u4e86\uff0c\u4e0b\u76d8\u9732\u51fa\u4e86\u7a7a\u9699\u3002\u654c\u4eba\u5982\u662f\u597d\u624b\uff0c\u4ed6\u7684\u811a\u8fd9\u6837\u4e00\u52fe\uff0c\u4f60\u5c31\u7cdf\u7cd5\uff0c\u6240\u4ee5\u5e94\u8be5\u8fd9\u6837\u3002\u201d\u8fde\u8bf4\u5e26\u6bd4\u7684\u6559\u4e86\u8d77\u6765\u3002\u8881\u627f\u5fd7\u5927\u662f\u94a6\u670d\uff0c\u8fd9\u4e00\u5929\u53c8\u5b66\u4e86\u4e0d\u5c11\u8bc0\u7a8d\u3002 \u3000\u3000\u4e00\u6643\u4e09\u5e74\uff0c\u8881\u627f\u5fd7\u5df2\u5341\u4e09\u5c81\u4e86\u3002\u8fd9\u4e09\u5e74\u4e4b\u4e2d\uff0c\u7a46\u4eba\u6e05\u53c8\u4f20\u4e86\u4ed6\u201c\u7834\u7389\u62f3\u201d\u548c\u201c\u6df7\u5143\u638c\u201d\u3002\u201c\u6df7\u5143\u638c\u201d\u867d\u662f\u638c\u6cd5\uff0c\u5374\u662f\u4fee\u4e60\u5185\u529f\u4e4b\u7528\u3002\u81ea\u6765\u5404\u5bb6\u5404\u6d3e\u4fee\u7ec3\u5185\u529f\uff0c\u90fd\u8bb2\u7a76\u547c\u5438\u5410\u7eb3\uff0c\u6253\u5750\u7ec3\u6c14\uff0c\u534e\u5c71\u6d3e\u7684\u5185\u529f\u5374\u522b\u5177\u8e4a\u5f84\uff0c\u81ea\u5916\u800c\u5185\uff0c\u4e8e\u638c\u6cd5\u4e2d\u4fee\u4e60\u5185\u52b2\u3002\u8fd9\u95e8\u529f\u592b\u867d\u7136\u8d39\u65f6\u751a\u4e45\uff0c\u89c1\u6548\u6781\u6162\uff0c\u4f46\u4fee\u4e60\u65f6\u65e2\u65e0\u8d70\u706b\u5165\u9b54\u4e4b\u865e\uff0c\u7ec3\u6210\u540e\u53c8\u662f\u5a01\u529b\u5947\u5927\u3002\u76d6\u5185\u5916\u9f50\u4fee\uff0c\u4e34\u654c\u65f6\u4e00\u62db\u4e00\u5f0f\u4e4b\u4e2d\uff0c\u7686\u81ea\u7136\u800c\u6709\u5185\u52b2\u76f8\u9644\uff0c\u80fd\u4e8e\u4e0d\u7740\u610f\u95f4\u5236\u80dc\u514b\u654c\u3002\u5f85\u5f97\u201c\u6df7\u5143\u529f\u201d\u5927\u6210\uff0c\u90a3\u66f4\u662f\u65e0\u5f80\u4e0d\u5229\u3001\u65e0\u575a\u4e0d\u6467\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7ec3\u6b66\u65f6\u65e5\u5c1a\u6d45\uff0c\u201c\u6df7\u5143\u529f\u201d\u81ea\u672a\u6709\u6210\uff0c\u4f46\u8eab\u5b50\u5df2\u51fa\u843d\u5f97\u58ee\u5065\u5f02\u5e38\uff0c\u767e\u75c5\u4e0d\u4fb5\u3002\u7a46\u4eba\u6e05\u6709\u65f6\u4e0b\u5c71\uff0c\u4e00\u53bb\u4fbf\u662f\u4e24\u4e09\u6708\u3001\u4e09\u56db\u6708\u4e0d\u7b49\uff0c\u56de\u5c71\u540e\u67e5\u8003\u6b66\u529f\uff0c\u89c1\u4ed6\u7528\u529f\u52e4\u594b\uff0c\u8fdb\u5883\u8fc5\u901f\uff0c\u6bcf\u6b21\u90fd\u662f\u5956\u52c9\u6709\u52a0\u3002 \u3000\u3000\u8fd9\u4e00\u5e74\u7aef\u5348\u8282\uff0c\u5403\u8fc7\u96c4\u9ec4\u9152\uff0c\u7a46\u4eba\u6e05\u53c8\u8bf7\u51fa\u7956\u5e08\u7237\u7684\u753b\u50cf\uff0c\u81ea\u5df1\u78d5\u4e86\u5934\uff0c\u53c8\u547d\u8881\u627f\u5fd7\u78d5\u5934\u3002\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u6559\u4f60\u62dc\u7956\u5e08\uff0c\u4f60\u77e5\u4e3a\u4e86\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8bf7\u5e08\u7236\u793a\u77e5\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u4ece\u81f3\u5185\u6367\u51fa\u4e00\u53ea\u957f\u957f\u7684\u6728\u5323\uff0c\u653e\u5728\u6848\u4e0a\uff0c\u6728\u5323\u76d6\u4e00\u63ed\u5f00\uff0c\u53ea\u89c1\u7cbe\u5149\u8000\u773c\uff0c\u5323\u4e2d\u6a2a\u653e\u7740\u4e00\u67c4\u660e\u6643\u6643\u7684\u4e09\u5c3a\u957f\u5251\u3002 \u3000\u3000\u8881\u627f\u5fd7\u60ca\u559c\u4ea4\u96c6\uff0c\u5fc3\u4e2d\u7a81\u7a81\u4e71\u8df3\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u662f\u6559\u6211\u5b66\u5251\u3002\u201d\u7a46\u4eba\u6e05\u70b9\u70b9\u5934\uff0c\u4ece\u5323\u4e2d\u63d0\u8d77\u957f\u5251\uff0c\u8138\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8dea\u4e0b\uff0c\u542c\u6211\u8bf4\u8bdd\u3002\u201d\u8881\u627f\u5fd7\u4f9d\u8a00\u4e0b\u8dea\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u5251\u4e3a\u767e\u5175\u4e4b\u7956\uff0c\u6700\u662f\u96be\u5b66\u3002\u672c\u6d3e\u5251\u6cd5\u66f4\u662f\u535a\u5927\u7cbe\u6df1\uff0c\u52a0\u4e4b\u81ea\u5386\u4ee3\u7956\u5e08\u4ee5\u964d\uff0c\u6bcf\u4e00\u4ee3\u90fd\u6709\u589e\u76ca\u3002\u522b\u6d3e\u6b66\u529f\uff0c\u5e08\u7236\u5e38\u5e38\u7559\u4e00\u624b\u770b\u5bb6\u672c\u9886\uff0c\u4ee5\u81f4\u4e00\u4ee3\u4e0d\u5982\u4e00\u4ee3\uff0c\u8d8a\u4f20\u5230\u540e\u6765\u7cbe\u5999\u4e4b\u7740\u8d8a\u5c11\u3002\u672c\u6d3e\u5374\u975e\u5982\u6b64\uff0c\u9009\u5f1f\u5b50\u4e4b\u65f6\u6781\u4e3a\u4e25\u683c\uff0c\u9009\u4e2d\u4e4b\u540e\uff0c\u5374\u662f\u503e\u56ca\u76f8\u6388\u3002\u5355\u4ee5\u5251\u6cd5\u800c\u8bba\uff0c\u6bcf\u4e00\u4ee3\u4fbf\u90fd\u80fd\u9752\u51fa\u4e8e\u84dd\u3002\u4f60\u806a\u660e\u52e4\u594b\uff0c\u8981\u5b66\u597d\u5251\u672f\uff0c\u4e0d\u7b97\u96be\u4e8b\uff0c\u6240\u671f\u671b\u4e8e\u4f60\u7684\uff0c\u662f\u65e5\u540e\u66f4\u8981\u53d1\u626c\u5149\u5927\u3002\u66f4\u987b\u7262\u8bb0\uff1a\u5251\u4e43\u5229\u5668\uff0c\u4ee5\u4e4b\u884c\u5584\uff0c\u5176\u5584\u65e0\u7a77\uff0c\u4ee5\u4e4b\u884c\u6076\uff0c\u5176\u6076\u4ea6\u65e0\u7a77\u3002\u4eca\u65e5\u6211\u8981\u4f60\u53d1\u4e00\u4e2a\u91cd\u8a93\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u51b3\u4e0d\u53ef\u5984\u6740\u4e00\u4e2a\u65e0\u8f9c\u4e4b\u4eba\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e08\u7236\u6559\u4e86\u6211\u5251\u6cd5\uff0c\u8981\u662f\u4ee5\u540e\u6211\u5251\u4e0b\u4f24\u4e86\u4e00\u4e2a\u597d\u4eba\uff0c\u4e00\u5b9a\u4e5f\u88ab\u4eba\u6740\u6b7b\u3002\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u597d\uff0c\u8d77\u6765\u5427\u3002\u201d\u8881\u627f\u5fd7\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u4f60\u5fc3\u5730\u4ec1\u539a\uff0c\u51b3\u4e0d\u4f1a\u6545\u610f\u6740\u5bb3\u597d\u4eba\u3002 \u3000\u3000\u4e0d\u8fc7\u662f\u975e\u4e4b\u95f4\uff0c\u6709\u65f6\u751a\u96be\u5206\u8fa8\uff0c\u4e16\u60c5\u8be1\u9669\uff0c\u4eba\u5fc3\u96be\u6599\uff0c\u597d\u4eba\u6216\u8bb8\u662f\u574f\u4eba\uff0c\u574f\u4eba\u8bf4\u4e0d\u5b9a\u5176\u5b9e\u662f\u597d\u4eba\u3002\u4f46\u53ea\u8981\u4f60\u5e38\u5b58\u5fe0\u6055\u5bbd\u5bb9\u4e4b\u5fc3\uff0c\u5c31\u4e0d\u6613\u8bef\u4f24\u4e86\u3002\u201d\u8881\u627f\u5fd7\u70b9\u5934\u7b54\u5e94\u3002\u7a46\u4eba\u6e05\u53c8\u9053\uff1a\u201c\u5d07\u796f\u7687\u5e1d\u6740\u4e86\u4f60\u7239\u7239\uff0c\u5728\u4ed6\u5fc3\u4e2d\uff0c\u53ea\u9053\u4f60\u7239\u7239\u662f\u574f\u4eba\uff0c\u4ed6\u6740\u5f97\u4e00\u70b9\u513f\u4e5f\u4e0d\u9519\uff0c\u54ea\u77e5\u5374\u5927\u5927\u7684\u9519\u4e86\u3002\u5d07\u796f\u7687\u5e1d\u8fd9\u4e9b\u5e74\u6765\u6740\u4e86\u4e0d\u5c11\u5927\u81e3\u5927\u5c06\uff0c\u6709\u7684\u56fa\u662f\u574f\u4eba\uff0c\u597d\u4eba\u53ef\u4e5f\u7ed9\u4ed6\u6740\u4e86\u4e0d\u5c11\u3002\u4ed6\u4e0d\u660e\u662f\u975e\uff0c\u53c8\u65e0\u4e1d\u6beb\u5bbd\u539a\u4e4b\u5fc3\uff0c\u4ed6\u8fd9\u4e48\u4e71\u6740\u4e00\u901a\uff0c\u8fd9\u5927\u660e\u6c5f\u5c71\uff0c\u96be\u514d\u65ad\u9001\u5728\u4ed6\u624b\u91cc\u3002\u201d\u8881\u627f\u5fd7\u9eef\u7136\u70b9\u5934\uff0c\u77e5\u9053\u5e08\u7236\u63d0\u51fa\u5d07\u796f\u6740\u4ed6\u7236\u4eb2\u7684\u4e8b\u6765\uff0c\u662f\u8981\u4ed6\u5c06\u201c\u662f\u975e\u96be\u8fa8\u3001\u4e0d\u53ef\u5984\u6740\u201d\u7684\u6559\u8bad\u6df1\u6df1\u8bb0\u5728\u5fc3\u5934\uff0c\u518d\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u5de6\u624b\u634f\u4e2a\u5251\u8bc0\uff0c\u53f3\u624b\u957f\u5251\u633a\u51fa\uff0c\u5251\u8d70\u9f99\u86c7\uff0c\u767d\u5149\u5982\u8679\uff0c\u4e00\u5957\u5929\u4e0b\u65e0\u53cc\u7684\u5251\u6cd5\u5c55\u4e86\u5f00\u6765\u3002 \u3000\u3000\u65e5\u5149\u4e0b\u957f\u5251\u95ea\u70c1\u751f\u8f89\uff0c\u821e\u5230\u540e\u6765\uff0c\u4f46\u89c1\u4e00\u56e2\u767d\u5149\u6eda\u6765\u6eda\u53bb\u3002\u8881\u627f\u5fd7\u8ddf\u7740\u5e08\u7236\u7ec3\u4e86\u4e09\u5e74\u62f3\u6cd5\uff0c\u773c\u5149\u4e0e\u4ee5\u524d\u5df2\u5927\u4e0d\u76f8\u540c\uff0c\u9976\u662f\u5982\u6b64\uff0c\u5e08\u7236\u7684\u5251\u6cd5\u3001\u8eab\u6cd5\u8fd8\u662f\u77a7\u4e0d\u6e05\u695a\uff0c\u53ea\u89c9\u51dd\u91cd\u5904\u5982\u5c71\u5dbd\u5dcd\u5cd9\uff0c\u8f7b\u7075\u5904\u82e5\u6e05\u98ce\u65e0\u8ff9\uff0c\u53d8\u5e7b\u83ab\u6d4b\uff0c\u8fc5\u6377\u65e0\u4f26\u3002\u821e\u5230\u6025\u5904\uff0c\u7a46\u4eba\u6e05\u5927\u559d\u4e00\u58f0\uff0c\u957f\u5251\u5ffd\u5730\u98de\u51fa\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u63d2\u5165\u4e86\u5c71\u5cf0\u8fb9\u4e00\u682a\u5927\u677e\u6811\u4e2d\uff0c\u5251\u5203\u76f4\u6ca1\u81f3\u67c4\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77e5\u9053\u677e\u6811\u8d28\u5730\u81f4\u5bc6\uff0c\u9002\u624d\u89c1\u5e08\u7236\u821e\u5251\u4e4b\u65f6\uff0c\u5251\u8eab\u4e0d\u4f4f\u98a4\u52a8\uff0c\u53ef\u89c1\u5251\u5203\u521a\u4e2d\u5e26\u67d4\uff0c\u54ea\u77e5\u8fd9\u4e00\u63b7\u4e4b\u4e0b\uff0c\u4e00\u67c4\u957f\u5251\u7684\u5251\u8eab\u5168\u90e8\u6ca1\u5165\uff0c\u4e0d\u89c9\u60ca\u5947\u5f97\u5f20\u5927\u4e86\u5634\uff0c\u5408\u4e0d\u62e2\u6765\u3002 \u3000\u3000\u5ffd\u542c\u8eab\u540e\u4e00\u4eba\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5728\u5c71\u4e0a\u4e09\u5e74\uff0c\u9664\u4e86\u5e08\u7236\u7684\u58f0\u97f3\u4e4b\u5916\uff0c\u4ece\u6765\u6ca1\u542c\u89c1\u8fc7\u7b2c\u4e8c\u4e2a\u4eba\u7684\u8bf4\u8bdd\uff0c\u867d\u7136\u8fd8\u6709\u4e00\u4e2a\u54d1\u5df4\uff0c\u53ef\u662f\u54d1\u5df4\u4e0d\u4f1a\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u6025\u5fd9\u56de\u5934\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u9053\u7b11\u563b\u563b\u7684\u8d70\u4e0a\u5cf0\u6765\u3002 \u3000\u3000\u90a3\u9053\u4eba\u8eab\u7a7f\u9ec4\u8272\u7c97\u5e03\u9053\u888d\uff0c\u4e00\u5f20\u8138\u9ec4\u7626\u5e72\u67af\uff0c\u5934\u53d1\u7a00\u7a00\u843d\u843d\uff0c\u767d\u591a\u9ed1\u5c11\uff0c\u633d\u7740\u4e2a\u5c0f\u5c0f\u9053\u9afb\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u7334\u513f\uff0c\u8fd9\u4e00\u62db\u2018\u5929\u5916\u98de\u9f99\u2019\uff0c\u4e16\u95f4\u66f4\u65e0\u7b2c\u4e8c\u4eba\u4f7f\u5f97\u51fa\uff0c\u8001\u9053\u4eca\u65e5\u5927\u5f00\u773c\u754c\u3002\u5341\u591a\u5e74\u6ca1\u89c1\u4f60\u7528\u5251\uff0c\u60f3\u4e0d\u5230\u66f4\u7cbe\u8fdb\u5982\u6b64\uff01\u201d \u3000\u3000\u7a46\u4eba\u6e05\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff0c\u751a\u4e48\u98ce\u628a\u4f60\u5439\u6765\u7684\uff1f\u4e00\u4e0a\u534e\u5c71\uff0c\u4fbf\u9001\u6211\u4e00\u9876\u5927\u5927\u7684\u9ad8\u5e3d\u3002\u627f\u5fd7\uff0c\u8fd9\u4f4d\u6728\u6851\u9053\u957f\uff0c\u662f\u5e08\u7236\u7684\u597d\u53cb\uff0c\u5feb\u7ed9\u9053\u957f\u78d5\u5934\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fd9\u8fc7\u6765\u8dea\u4e0b\u78d5\u5934\u3002\u6728\u6851\u9053\u4eba\u7b11\u9053\uff1a\u201c\u7f62\u4e86\uff01\u201d\u4f38\u624b\u4e00\u6276\uff0c\u628a\u4ed6\u626f\u4e86\u8d77\u6765\u3002 \u3000\u3000\u51e1\u5b66\u6b66\u4e4b\u4eba\uff0c\u9047\u5230\u5916\u529b\u65f6\u4e0d\u7531\u81ea\u4e3b\u7684\u4f1a\u8fd0\u529f\u62b5\u5fa1\u3002\u6728\u6851\u9053\u4eba\u8fd9\u4e48\u4e00\u626f\uff0c\u8881\u627f\u5fd7\u8fd9\u65f6\u201c\u6df7\u5143\u529f\u201d\u5df2\u6709\u5c0f\u6210\uff0c\u53cc\u81c2\u987a\u4e4e\u81ea\u7136\u7684\u8f7b\u8f7b\u4e00\u6323\u3002\u6728\u6851\u9053\u4eba\u5df2\u8bd5\u51fa\u4e86\u4ed6\u529f\u592b\uff0c\u5bf9\u7a46\u4eba\u6e05\u7b11\u9053\uff1a\u201c\u8001\u7334\u513f\uff0c\u8fd9\u51e0\u5e74\u89c1\u4e0d\u5230\u4f60\uff0c\u539f\u6765\u5077\u5077\u8eb2\u5728\u8fd9\u91cc\u8c03\u7406\u5c0f\u7334\u513f\u5f92\u5f1f\u3002\u4f60\u8fd0\u6c14\u4e0d\u574f\u5440\uff0c\u4e00\u53ea\u811a\u5df2\u8e0f\u8fdb\u4e86\u68fa\u6750\uff0c\u5c45\u7136\u8fd8\u627e\u5230\u8fd9\u6837\u7684\u4e00\u4e2a\u597d\u5a03\u5a03\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u548c\u4ed6\u6253\u8da3\u60ef\u4e86\u7684\uff0c\u542c\u4ed6\u79f0\u8d5e\u81ea\u5df1\u7684\u5c0f\u5f92\u513f\uff0c\u4e5f\u4e0d\u7981\u62c8\u987b\u5fae\u7b11\uff0c\u6021\u7136\u81ea\u5f97\u3002 \u3000\u3000\u6728\u6851\u9053\u4eba\u9053\uff1a\u201c\u554a\u54df\uff0c\u4eca\u5929\u6ca1\u5e26\u89c1\u9762\u94b1\uff0c\u53ef\u4e5f\u4e0d\u597d\u751f\u53d7\u4f60\u8fd9\u51e0\u4e2a\u5934\uff0c\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u7a46\u4eba\u6e05\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u7075\u673a\u4e00\u52a8\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u9053\u6b66\u529f\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u6c5f\u6e56\u4e0a\u4eba\u79f0\u201c\u5343\u53d8\u4e07\u52ab\u201d\u3002\u5982\u80af\u4f20\u70b9\u751a\u4e48\u7ed9\u627f\u5fd7\uff0c\u5012\u53ef\u4ee4\u4ed6\u5f97\u76ca\u4e0d\u6d45\u3002\u53ea\u662f\u8fd9\u4eba\u7d20\u6765\u4e0d\u80af\u6536\u5f92\uff0c\u5012\u8981\u60f3\u6cd5\u5b50\u6324\u4ed6\u4e00\u6324\u3002\u201d\u8bf4\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u9053\u957f\u7b54\u5e94\u7ed9\u4f60\u597d\u5904\uff0c\u5feb\u78d5\u5934\u9053\u8c22\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u5e08\u7236\u8fd9\u4e48\u8bf4\uff0c\u5f53\u5373\u53c8\u8dea\u4e0b\u78d5\u5934\u3002 \u3000\u3000\u6728\u6851\u9053\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u597d\u597d\uff0c\u6709\u5176\u5e08\u5fc5\u6709\u5176\u5f92\uff0c\u5e08\u7236\u4e0d\u8981\u8138\uff0c\u5f92\u5f1f\u4e5f\u6ca1\u51fa\u606f\u3002\u5582\uff0c\u5a03\u513f\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u4e3a\u4eba\u53ef\u8981\u6b63\u6b63\u6d3e\u6d3e\uff0c\u522b\u5b66\u4f60\u5e08\u7236\u8fd9\u4e48\u539a\u8138\u76ae\uff0c\u542c\u5230\u4eba\u5bb6\u8bf4\u7ed9\u4e1c\u897f\uff0c\u8fde\u5fd9\u6572\u9489\u8f6c\u811a\uff0c\u96be\u9053\u6211\u8001\u4eba\u5bb6\u8fd8\u9a97\u4f60\u5b69\u5b50\u4e0d\u6210\uff1f\u8fd9\u6837\u5427\uff0c\u4eca\u513f\u4e58\u6211\u8001\u4eba\u5bb6\u9ad8\u5174\uff0c\u628a\u8fd9\u4e2a\u7ed9\u4e86\u4f60\u5427\u3002\u201d\u8bf4\u7740\u4ece\u80cc\u56ca\u4e2d\u638f\u51fa\u4e00\u56e2\u4e1c\u897f\u6765\u4ea4\u4e86\u7ed9\u4ed6\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8c22\u4e86\uff0c\u606d\u606d\u656c\u656c\u7684\u53cc\u624b\u63a5\u8fc7\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u6296\u5f00\u4e00\u770b\uff0c\u89c1\u662f\u9ed1\u9edd\u9edd\u7684\u4e00\u4ef6\u80cc\u5fc3\uff0c\u62ff\u5728\u624b\u91cc\u6c89\u7538\u7538\u7684\uff0c\u975e\u4e1d\u975e\u9769\uff0c\u4e0d\u77e5\u662f\u751a\u4e48\u4e1c\u897f\u6240\u5236\uff0c\u6b63\u81ea\u7591\u60d1\uff0c\u542c\u5f97\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u9053\u5144\uff0c\u522b\u5f00\u73a9\u7b11\uff0c\u8fd9\u4ef6\u5b9d\u7269\u600e\u80fd\u7ed9\u4ed6\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u542c\uff0c\u624d\u77e5\u662f\u4ef6\u8d35\u91cd\u5b9d\u7269\uff0c\u53cc\u624b\u6367\u7740\u5fd9\u5373\u4ea4\u8fd8\u3002\u6728\u6851\u9053\u4eba\u4e0d\u63a5\uff0c\u8bf4\u9053\uff1a\u201c\u5478\uff01\u8001\u9053\u54ea\u4f1a\u50cf\u4f60\u5e08\u7236\u8fd9\u4e48\u5bd2\u9178\uff0c\u9001\u51fa\u4e86\u7684\u4e1c\u897f\u600e\u80fd\u6536\u56de\uff1f\u4e56\u4e56\u7684\u7ed9\u6211\u62ff\u53bb\u5427\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u6562\u6536\uff0c\u671b\u7740\u5e08\u7236\u542c\u4ed6\u793a\u4e0b\u3002\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u65e2\u662f\u8fd9\u6837\uff0c\u90a3\u4e48\u591a\u8c22\u9053\u957f\u5427\u3002\u201d\u8881\u627f\u5fd7\u8dea\u4e0b\u53e9\u8c22\u3002\u7a46\u4eba\u6e05\u6b63\u8272\u9053\uff1a\u201c\u8fd9\u662f\u9053\u957f\u5f53\u5e74\u82b1\u4e86\u65e0\u6570\u5fc3\u8840\uff0c\u62da\u4e86\u4e5d\u6b7b\u4e00\u751f\u624d\u5f97\u6765\u7684\u9632\u8eab\u81f3\u5b9d\uff0c\u4f60\u7a7f\u4e0a\u4e86\u3002\u201d\u8881\u627f\u5fd7\u4f9d\u8a00\u628a\u80cc\u5fc3\u7a7f\u4e0a\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u7eb5\u5230\u677e\u6811\u4e4b\u524d\uff0c\u98df\u4e2d\u4e24\u53ea\u624b\u6307\u52fe\u4f4f\u5251\u67c4\uff0c\u8f7b\u8f7b\u4e00\u63d0\uff0c\u5df2\u62d4\u51fa\u957f\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u80cc\u5fc3\u662f\u7528\u4e4c\u91d1\u4e1d\u3001\u5934\u53d1\u3001\u548c\u91d1\u4e1d\u7334\u6bdb\u6df7\u540c\u7ec7\u6210\uff0c\u4efb\u4f55\u5389\u5bb3\u7684\u5175\u5203\u90fd\u4f24\u4ed6\u4e0d\u5f97\u3002\u201d\u8bf4\u7740\u968f\u624b\u4e00\u5251\u5411\u8881\u627f\u5fd7\u80f8\u53e3\u5251\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u8fc5\u6377\u65e0\u6bd4\uff0c\u8881\u627f\u5fd7\u54ea\u6765\u5f97\u53ca\u907f\u8ba9\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u5374\u89c1\u5251\u5c16\u78b0\u5230\u80cc\u5fc3\uff0c\u4fbf\u8f7b\u8f7b\u53cd\u5f39\u51fa\u6765\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u53c8\u8dea\u4e0b\u5411\u6728\u6851\u78d5\u5934\u3002 \u3000\u3000\u6728\u6851\u9053\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u8fd9\u4ef6\u4e1c\u897f\u58a8\u9ed1\u4e00\u56e2\uff0c\u6beb\u4e0d\u8d77\u773c\uff0c\u5148\u524d\u78d5\u4e86\u5934\uff0c\u53ea\u6015\u5fc3\u4e2d\u5f88\u89c9\u5f97\u6709\u70b9\u513f\u51a4\uff0c\u8fd9\u4e00\u6b21\u624d\u771f\u662f\u5fc3\u7518\u60c5\u613f\u7684\u4e86\u3002\u201d\u8881\u627f\u5fd7\u7ed9\u4ed6\u8bf4\u5f97\u8138\u7ea2\u8fc7\u8033\uff0c\u7b11\u563b\u563b\u7684\u4e0d\u7b54\u3002 \u3000\u3000\u8bf4\u4e86\u4e00\u9635\u8bdd\uff0c\u7a46\u4eba\u6e05\u95ee\u9053\uff1a\u201c\u90a3\u4eba\u8fd1\u6765\u6709\u6d88\u606f\u6ca1\u6709\uff1f\u201d\u6728\u6851\u9053\u4eba\u672c\u6765\u6ee1\u8138\u7b11\u5bb9\uff0c\u542c\u4ed6\u63d0\u5230\u201c\u90a3\u4eba\u201d\uff0c\u4e0d\u7531\u5f97\u53f9\u4e86\u53e3\u6c14\uff0c\u795e\u8272\u767b\u65f6\u4e0d\u6109\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7792\u4f60\u8bf4\uff0c\u8fd9\u5bb6\u4f19\u4e0d\u77e5\u5728\u751a\u4e48\u5730\u65b9\u6df7\u4e86\u4e00\u6bb5\u65e5\u5b50\uff0c\u6700\u8fd1\u5374\u53c8\u5728\u5c71\u6d77\u5173\u5185\u5916\u51fa\u6ca1\u3002\u8001\u9053\u4e0d\u60f3\u89c1\u4ed6\uff0c\u8bf4\u4e0d\u5f97\uff0c\u53ea\u597d\u907f\u4ed6\u4e00\u907f\u3002\u6765\u5230\u534e\u5c71\uff0c\u8001\u9053\u662f\u9003\u96be\u6765\u5566\u3002\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u9053\u5144\u4f55\u4ee5\u957f\u4ed6\u4eba\u5fd7\u6c14\uff0c\u706d\u81ea\u5df1\u5a01\u98ce\uff1f\u51ed\u7740\u9053\u5144\u8fd9\u8eab\u51fa\u795e\u5165\u5316\u7684\u529f\u592b\uff0c\u96be\u9053\u4f1a\u5bf9\u4ed8\u4ed6\u4e0d\u4e86\uff1f\u201d \u3000\u3000\u6728\u6851\u6447\u4e86\u6447\u5934\uff0c\u795e\u8272\u751a\u662f\u6cae\u4e27\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u5bf9\u4ed8\u4ed6\u4e0d\u4e86\uff0c\u53ea\u662f\u8001\u9053\u72e0\u4e0d\u4e0b\u8fd9\u4e2a\u5fc3\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u66fe\u548c\u4ed6\u4e24\u6b21\u76f8\u6597\u3002\u7b2c\u4e00\u6b21\u6211\u5df2\u5360\u4e86\u4e0a\u98ce\uff0c\u6700\u540e\u7ec8\u4e8e\u5ff5\u7740\u540c\u95e8\u60c5\u8c0a\uff0c\u5148\u5e08\u4e34\u7ec8\u65f6\u53c8\u53ee\u5631\u6211\u597d\u597d\u7167\u987e\u4ed6\uff0c\u8001\u9053\u6559\u8c15\u65e0\u65b9\uff0c\u81f4\u4ed6\u8bef\u5165\u6b67\u9014\uff0c\u9677\u6eba\u65e5\u6df1\uff0c\u8001\u9053\u5fc3\u4e2d\u6709\u6127\u3002\u6700\u540e\u8fd9\u4e00\u51fb\u4fbf\u4e0b\u4e0d\u4e86\u624b\u3002\u7b2c\u4e8c\u6b21\u76f8\u6597\uff0c\u4ed6\u4e0d\u77e5\u5728\u4f55\u5904\u5b66\u6765\u4e86\u4e00\u4e9b\u90aa\u6d3e\u7684\u5389\u5bb3\u529f\u592b\uff0c\u4e00\u5251\u523a\u5728\u6211\u5fc3\u53e3\uff0c\u5e78\u8d56\u8fd9\u4ef6\u80cc\u5fc3\u62a4\u8eab\uff0c\u5251\u5c16\u523a\u4e0d\u8fdb\u53bb\u3002\u4ed6\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u9053\u6211\u7ec3\u6210\u5947\u5999\u6b66\u529f\uff0c\u8fd9\u4e48\u4e00\u758f\u795e\uff0c\u53c8\u7ed9\u6211\u5236\u4f4f\u3002\u6211\u597d\u597d\u529d\u4e86\u4ed6\u4e00\u573a\uff0c\u4ed6\u5374\u53ea\u662f\u51b7\u7b11\uff0c\u4e34\u522b\u4e4b\u65f6\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u660e\u767d\u4e86\uff0c\u539f\u6765\u4f60\u53ea\u662f\u4ed7\u7740\u5b9d\u8863\u62a4\u8eab\uff0c\u4e0b\u6b21\u52a8\u624b\u3002\u6211\u523a\u4f60\u5934\u8138\uff0c\u4f60\u53c8\u5982\u4f55\u9632\u5907\uff1f\u201d \u3000\u3000\u7a46\u4eba\u6e05\u6012\u9053\uff1a\u201c\u8fd9\u4eba\u5982\u6b64\u72c2\u5984\u3002\u9053\u5144\u5ff5\u7740\u540c\u95e8\u60c5\u4e49\uff0c\u4e00\u518d\u9976\u4ed6\u6027\u547d\uff0c\u59d3\u7a46\u7684\u8ddf\u4ed6\u53ef\u6ca1\u751a\u4e48\u74dc\u845b\uff1f\u9053\u5144\uff0c\u4f60\u5728\u655d\u5904\u76d8\u6853\u5c0f\u4f4f\uff0c\u6211\u8fd9\u5c31\u4e0b\u5c71\u53bb\u627e\u4ed6\u3002\u53ea\u8981\u89c1\u5230\u4ed6\u4ecd\u5728\u4e3a\u975e\u4f5c\u6b79\uff0c\u8001\u7a46\u63d0\u4e86\u4ed6\u9996\u7ea7\u6765\u89c1\u4f60\u3002\u201d \u3000\u3000\u6728\u6851\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u597d\u610f\u3002\u4f46\u662f\u6211\u603b\u76fc\u4ed6\u80fd\u81ea\u884c\u6094\u609f\uff0c\u75db\u6539\u524d\u975e\u3002\u8fd9\u51e0\u5e74\u6765\uff0c\u5bf9\u4ed6\u7684\u90aa\u95e8\u6b66\u529f\u6211\u66fe\u7ec6\u52a0\u63e3\u6469\uff0c\u771f\u8981\u518d\u52a8\u624b\uff0c\u4e5f\u672a\u5fc5\u80dc\u4ed6\u4e0d\u4e86\u3002\u6211\u8eb2\u4e0a\u534e\u5c71\u6765\uff0c\u6c42\u4e2a\u773c\u4e0d\u89c1\u4e3a\u51c0\uff0c\u8033\u4e0d\u95fb\u4e0d\u70e6\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u4ed6\u5982\u5f97\u80fd\u6094\u6539\uff0c\u90a3\u81ea\u662f\u6211\u5e08\u95e8\u4e4b\u798f\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u8ba9\u4ed6\u591a\u884c\u4e0d\u4e49\u5fc5\u81ea\u6bd9\u5427\u3002\u201d\u8bf4\u7740\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u80fd\u6094\u6539\uff1f\u5509\uff0c\u5f88\u96be\uff0c\u5f88\u96be\uff01\u201d \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4eba\u8d2a\u82b1\u597d\u8272\uff0c\u574f\u4e86\u4e0d\u5c11\u826f\u5bb6\u5987\u5973\u7684\u540d\u8282\uff0c\u8fd1\u6765\u66f4\u662f\u53d8\u672c\u52a0\u5389\u3002\u8fd9\u79cd\u6b66\u6797\u8d25\u7c7b\uff0c\u4e0b\u6b21\u843d\u5728\u9053\u5144\u624b\u91cc\uff0c\u5343\u4e07\u4e0d\u53ef\u518d\u91cd\u65e7\u60c5\u3002\u9053\u5144\u6e05\u7406\u95e8\u6237\uff0c\u94f2\u9664\u4e0d\u8096\uff0c\u4fbf\u662f\u7ef4\u62a4\u5c0a\u5e08\u7684\u4ee4\u540d\uff0c\u62a5\u7b54\u5c0a\u5e08\u7684\u6069\u5fb7\u3002\u201d\u6728\u6851\u70b9\u5934\u9053\uff1a\u201c\u7a46\u5144\u8bf4\u7684\u662f\u3002\u5509\uff01\u201d \u3000\u3000\u8bf4\u7740\u53f9\u4e86\u53e3\u957f\u6c14\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u7740\u4e8c\u4eba\u8c08\u8bdd\uff0c\u4f3c\u4e4e\u6728\u6851\u9053\u4eba\u6709\u4e00\u4e2a\u5e08\u5144\u5f1f\u54c1\u6027\u5341\u5206\u4e0d\u7aef\uff0c\u6b66\u529f\u5374\u751a\u662f\u9ad8\u5f3a\uff0c\u6367\u7740\u90a3\u4ef6\u80cc\u5fc3\uff0c\u5bf9\u6728\u6851\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u8981\u9664\u90a3\u6076\u4eba\uff0c\u8fd8\u662f\u7a7f\u4e86\u8fd9\u4ef6\u80cc\u5fc3\u7a33\u5f53\u4e9b\u3002\u7b49\u4f60\u9664\u53bb\u4e86\u4ed6\uff0c\u518d\u8d50\u7ed9\u5f1f\u5b50\u5427\u3002\u5f1f\u5b50\u6b66\u529f\u6ca1\u5b66\u597d\uff0c\u4e0d\u4f1a\u53bb\u8ddf\u574f\u4eba\u52a8\u624b\uff0c\u8fd9\u4ef6\u5b9d\u8d1d\u8fd8\u7528\u4e0d\u7740\u3002\u201d \u3000\u3000\u6728\u6851\u62cd\u62cd\u4ed6\u80a9\u818a\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4e00\u756a\u597d\u5fc3\u3002\u4f46\u5c31\u7b97\u6ca1\u8fd9\u4ef6\u80cc\u5fc3\u62a4\u8eab\uff0c\u8c05\u4ed6\u4e5f\u6740\u4e0d\u4e86\u6211\u3002\u8fd9\u6076\u4eba\u7684\u90aa\u95e8\u529f\u592b\u53ea\u80fd\u653b\u4eba\u65e0\u5907\uff0c\u53ef\u4e00\u800c\u4e0d\u53ef\u518d\u3002\u5c0f\u5a03\u5a03\u5012\u4e0d\u7528\u4e3a\u6211\u62c5\u5fc3\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u89c1\u4ed6\u90c1\u90c1\u4e0d\u4e50\uff0c\u77e5\u9053\u5929\u4e0b\u53ea\u6709\u4e00\u4ef6\u4e8b\u80fd\u4ee4\u4ed6\u4e07\u4e8b\u7f6e\u8bf8\u8111\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u591a\u8bf4\u8d25\u4eba\u6e05\u5174\u3002\u725b\u9f3b\u5b50\uff0c\u4f60\u7684\u68cb\u827a\u2026\u2026\u201d\u6728\u6851\u4e00\u542c\u5230\u201c\u68cb\u827a\u201d\u4e24\u5b57\uff0c\u8138\u4e0a\u808c\u8089\u4e00\u8df3\uff0c\u767b\u65f6\u5bb9\u5149\u7115\u53d1\uff0c\u9661\u7136\u95f4\u5b9b\u5982\u5e74\u8f7b\u4e86\u4e8c\u5341\u5c81\uff0c\u53ea\u542c\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u2026\u2026\u8fd9\u4e9b\u5e74\u6765\uff0c\u53ef\u7a0d\u4e3a\u957f\u8fdb\u4e86\u4e00\u4e9b\u6ca1\u6709\uff1f\u201d\u4ed6\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u751a\u4e48\uff1f\u8001\u9053\u7684\u6b66\u529f\u5411\u6765\u4e0d\u53ca\u4f60\uff0c\u4e0b\u68cb\u7684\u672c\u4e8b\u5374\u5927\u53ef\u505a\u4f60\u5e08\u7236\u3002\u4f60\u82e5\u4e0d\u4fe1\uff0c\u54b1\u4eec\u4fbf\u2026\u2026\u201d\u7a46\u4eba\u6e05\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u6765\u9886\u6559\u9886\u6559\u2018\u5343\u53d8\u4e07\u52ab\u2019\u7684\u529f\u592b\uff0c\u4f60\u7684\u5403\u996d\u5bb6\u4f19\u5e26\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u6728\u6851\u7b11\u541f\u541f\u7684\u4ece\u80cc\u56ca\u4e2d\u62ff\u51fa\u4e00\u53ea\u56f4\u68cb\u76d8\u3001\u4e24\u5305\u68cb\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u8001\u9053\u662f\u7247\u523b\u4e0d\u79bb\u8eab\u7684\u3002\u4f60\u6015\u4e86\u6211\u60f3\u907f\u6218\uff0c\u63a8\u8bf4\u534e\u5c71\u4e0a\u6ca1\u68cb\u76d8\u68cb\u5b50\uff0c\u90a3\u53ef\u8d56\u4e0d\u6389\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d \u3000\u3000\u54d1\u5df4\u642c\u51fa\u53f0\u6905\uff0c\u4e24\u4eba\u5c31\u5728\u6811\u836b\u4e0b\u5bf9\u8d77\u5c40\u6765\u3002\u8881\u627f\u5fd7\u4e0d\u61c2\u56f4\u68cb\uff0c\u6728\u6851\u4e00\u9762\u4e0b\uff0c\u4e00\u9762\u7ed9\u4ed6\u89e3\u91ca\uff0c\u540c\u65f6\u4e0d\u4f4f\u53e3\u7684\u5439\u5618\u81ea\u5df1\u8fd9\u7740\u5982\u4f55\u9ad8\u660e\uff0c\u4ed6\u5e08\u7236\u5982\u4f55\u8fdc\u8fdc\u4e0d\u662f\u654c\u624b\u3002\u7a46\u4eba\u6e05\u53ea\u662f\u5fae\u7b11\u6c89\u601d\uff0c\u4efb\u7531\u4ed6\u81ea\u5439\u81ea\u64c2\u3002 \u3000\u3000\u56f4\u68cb\u662f\u6613\u5b66\u96be\u7cbe\u4e4b\u4e8b\uff0c\u4e0b\u6cd5\u89c4\u77e9\uff0c\u4e00\u70b9\u5c31\u4f1a\u3002\u8881\u627f\u5fd7\u770b\u4e86\u4e00\u5c40\uff0c\u5df2\u660e\u767d\u5176\u4e2d\u5927\u8981\u3002\u4ed6\u89c1\u8fd9\u68cb\u76d8\u662f\u7cbe\u94a2\u6240\u94f8\uff0c\u9ed1\u68cb\u5b50\u662f\u9ed1\u94c1\uff0c\u767d\u68cb\u5b50\u5374\u662f\u767d\u94f6\u3002\u4e24\u4eba\u843d\u5b50\u4e4b\u65f6\uff0c\u53d1\u51fa\u94ee\u94ee\u4e4b\u58f0\uff0c\u751a\u662f\u52a8\u542c\u3002 \u3000\u3000\u8fd9\u4e00\u5c40\u679c\u7136\u662f\u6728\u6851\u80dc\u4e86\u4e24\u5b50\u3002\u8001\u670b\u53cb\u4fe9\u4ece\u65e5\u4e2d\u76f4\u4e0b\u5230\u5929\u9ed1\uff0c\u4e00\u5171\u4e0b\u4e86\u4e09\u5c40\uff0c\u6728\u6851\u4e24\u80dc\u4e00\u8d1f\uff0c\u4f9d\u4ed6\u8bf4\u8fd8\u8981\u518d\u4e0b\uff0c\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u7cbe\u795e\u966a\u4f60\u5566\uff01\u201d\u6728\u6851\u8fd9\u624d\u604b\u604b\u4e0d\u820d\u7684\u53bb\u7761\u3002 \u3000\u3000\u4e00\u8fde\u4e09\u5929\uff0c\u6728\u6851\u603b\u662f\u7f20\u7740\u7a46\u4eba\u6e05\u4e0b\u68cb\u3002\u8881\u627f\u5fd7\u65c1\u89c2\uff0c\u5012\u4e5f\u6d25\u6d25\u6709\u5473\u3002\u5230\u4e86\u7b2c\u56db\u5929\u4e0a\uff0c\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4eca\u5929\u54b1\u4eec\u4f11\u606f\u4e00\u65e5\uff0c\u5f85\u6211\u5148\u4f20\u6388\u5f92\u5f1f\u5251\u6cd5\u518d\u8bf4\u3002\u201d \u3000\u3000\u6728\u6851\u5fc3\u60f3\u8fd9\u662f\u6b63\u4e8b\uff0c\u4e0d\u4fbf\u963b\u6320\uff0c\u53ef\u662f\u53ea\u7b49\u5f97\u5fc3\u75d2\u96be\u6414\uff0c\u597d\u5bb9\u6613\u7a46\u4eba\u6e05\u4f20\u5b8c\u5251\u6cd5\uff0c\u4ed6\u9a6c\u4e0a\u4e00\u628a\u62c9\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u6765\u6765\u6765\uff0c\u518d\u6740\u4e09\u5c40\u3002\u201d\u7a46\u4eba\u6e05\u6559\u4e86\u534a\u5929\u5251\uff0c\u5df2\u5fae\u611f\u75b2\u4e4f\uff0c\u4f46\u77e5\u6728\u6851\u68cb\u763e\u6781\u5927\uff0c\u5982\u4e0d\u966a\u4ed6\uff0c\u53ea\u6015\u4ed6\u6574\u665a\u7761\u4e0d\u5b89\u4e50\uff0c\u4e8e\u662f\u548c\u4ed6\u5230\u6811\u4e0b\u5bf9\u5c40\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7ec3\u4e86\u4e00\u4f1a\u65b0\u5b66\u7684\u5251\u6cd5\uff0c\u5ffd\u542c\u6728\u6851\u559c\u53eb\uff1a\u201c\u627f\u5fd7\uff0c\u5feb\u6765\u770b\uff01 \u3000\u3000\u4f60\u5e08\u7236\u5927\u5927\u7684\u7cdf\u7cd5\uff01\u201d\u4e8e\u662f\u5954\u8fc7\u53bb\u89c2\u770b\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u68cb\u529b\u672c\u6765\u4e0d\u5982\u6728\u6851\uff0c\u8fd9\u65f6\u53c8\u662f\u52c9\u5f3a\u5949\u966a\uff0c\u4e0b\u5f97\u66f4\u52a0\u4e0d\u987a\uff0c\u4e0d\u5230\u4e2d\u5c40\uff0c\u5df2\u662f\u5904\u5904\u53d7\u5236\uff0c\u773c\u89c1\u4e00\u5757\u767d\u5b50\u5f62\u52bf\u5341\u5206\u5371\u6025\uff0c\u5373\u4f7f\u52c9\u5f3a\u505a\u773c\u6c42\u6d3b\uff0c\u56db\u9685\u8981\u70b9\u90fd\u5c06\u88ab\u5bf9\u65b9\u5360\u5c3d\u3002\u4ed6\u62c8\u4e86\u4e00\u7c92\u68cb\u5b50\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u59cb\u7ec8\u653e\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5728\u4e00\u65c1\u89c2\u770b\uff0c\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u4e0b\u5728\u8fd9\u91cc\uff0c\u6728\u6851\u5e08\u4f2f\u5b9a\u8981\u53bb\u6551\u3002\u4f60\u518d\u4e0b\u8fd9\u7740\uff0c\u5c31\u53ef\u51b2\u51fa\u53bb\u4e86\u3002\u4e0d\u77e5\u5f1f\u5b50\u8bf4\u5f97\u5bf9\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u7d20\u6765\u606c\u9000\uff0c\u4e0d\u4f3c\u6728\u6851\u90a3\u6837\u81ea\u8d1f\u597d\u80dc\uff0c\u4e5f\u5c31\u7167\u7740\u5f92\u513f\u6307\u70b9\uff0c\u4e0b\u4e86\u8fd9\u7740\uff0c\u4e00\u5927\u7247\u767d\u68cb\u679c\u7136\u771f\u51b2\u4e86\u51fa\u6765\uff0c\u53cd\u800c\u628a\u9ed1\u5b50\u56f0\u6b7b\u4e86\u4e00\u5c0f\u5757\u3002\u8fd9\u5c40\u68cb\u7a46\u4eba\u6e05\u672c\u6765\u5927\u8f93\u7279\u8f93\uff0c\u8fd9\u4e48\u4e00\u6765\u4e00\u53bb\uff0c\u7ed3\u679c\u53ea\u8f93\u4e86\u4e94\u5b50\u3002 \u3000\u3000\u6728\u6851\u5927\u8d5e\u8881\u627f\u5fd7\u5fc3\u601d\u7075\u5de7\uff0c\u8ba9\u4ed6\u4e5d\u5b50\uff0c\u4e0e\u4ed6\u4e0b\u4e86\u4e00\u5c40\u3002 \u3000\u3000\u8881\u627f\u5fd7\u867d\u7136\u4e0d\u61c2\u524d\u4eba\u4e4b\u6cd5\uff0c\u7136\u800c\u56f4\u68cb\u4e00\u9053\uff0c\u6700\u8bb2\u7a76\u7684\u662f\u609f\u6027\uff0c\u5e38\u8a00\u9053\uff1a\u201c\u4e8c\u5341\u5c81\u4e0d\u6210\u56fd\u624b\uff0c\u7ec8\u8eab\u65e0\u671b\u3002\u201d\u610f\u601d\u662f\u8bf4\u4e0b\u56f4\u68cb\u4e4b\u4eba\u5982\u4e0d\u5728\u7ae5\u5e74\u6210\u540d\uff0c\u5c06\u6765\u518d\u4e0b\u82e6\u529f\uff0c\u4e5f\u7ec8\u662f\u788c\u788c\u5eb8\u624b\u3002 \u3000\u3000\u5982\u82cf\u4e1c\u5761\u5982\u6b64\u806a\u660e\u4e4b\u4eba\uff0c\u7ecf\u53f2\u6587\u7ae0\u3001\u4e66\u753b\u8bd7\u8bcd\uff0c\u65e0\u4e00\u4e0d\u901a\uff0c\u65e0\u4e00\u4e0d\u7cbe\uff0c\u7136\u800c\u56f4\u68cb\u59cb\u7ec8\u4e0b\u4e0d\u8fc7\u5bfb\u5e38\u5eb8\u624b\u3002\u6210\u4e3a\u4ed6\u751f\u5e73\u4e00\u5927\u61be\u4e8b\u3002\u4ed6\u66fe\u6709\u4e00\u53e5\u8bd7\u9053\uff1a\u201c\u80dc\u56fa\u6b23\u7136\u8d25\u4ea6\u559c\u201d\uff0c\u540e\u4eba\u8d5e\u4ed6\u80f8\u895f\u5bbd\u535a\uff0c\u4e0d\u4ee5\u80dc\u8d1f\u8426\u6000\u3002\u5c82\u77e5\u56f4\u68cb\u6700\u91cd\u5f97\u5931\uff0c\u4e00\u5b50\u4e00\u5730\u4e4b\u4e89\uff0c\u5fc5\u987b\u8ba1\u7b97\u6e05\u695a\uff0c\u6beb\u4e0d\u653e\u677e\uff0c\u624d\u53ef\u5f97\u80dc\uff0c\u5982\u8001\u662f\u5b58\u7740\u201c\u80dc\u56fa\u6b23\u7136\u8d25\u4ea6\u559c\u201d\u7684\u5fc3\u610f\u4e0b\u68cb\uff0c\u4f5c\u4e3a\u9676\u60c5\u51b6\u6027\uff0c\u6d88\u9063\u7545\u6000\u3002\u56fa\u65e0\u4e0d\u53ef\uff0c\u4e0d\u8fc7\u5b9a\u662f\u201c\u6b23\u7136\u201d\u7684\u65f6\u5019\u5c11\uff0c\u800c\u201c\u529e\u559c\u201d\u7684\u65f6\u5019\u591a\u4e86\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u6027\u60c5\u6de1\u6cca\uff0c\u6728\u6851\u548c\u4ed6\u4e0b\u68cb\u89c9\u5f97\u640f\u6740\u4e0d\u70c8\uff0c\u4e0d\u5927\u8fc7\u763e\uff0c\u6b64\u523b\u4e0e\u8881\u627f\u5fd7\u5bf9\u5c40\uff0c\u7adf\u7136\u5927\u4e0d\u76f8\u540c\u3002\u8881\u627f\u5fd7\u4e0e\u6b64\u9053\u9887\u6709\u5929\u624d\uff0c\u52a0\u4ee5\u7ae5\u5fc3\u751a\u76db\uff0c\u5343\u65b9\u767e\u8ba1\u7684\u8981\u6218\u80dc\u8fd9\u4f4d\u5e08\u4f2f\u3002\u8fd9\u4e00\u5c40\u7ed3\u679c\u867d\u662f\u6728\u6851\u8d62\u4e86\uff0c\u53ef\u662f\u4e2d\u95f4\u9669\u8c61\u73af\u751f\uff0c\u5e76\u975e\u4e00\u5e06\u98ce\u987a\u7684\u53d6\u80dc\u3002 \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u6728\u6851\u53c8\u628a\u627f\u5fd7\u62c9\u53bb\u4e0b\u68cb\uff0c\u627f\u5fd7\u8fde\u80dc\u4e09\u5c40\uff0c\u4ece\u8ba9\u4e5d\u5b50\u6539\u4e3a\u8ba9\u516b\u5b50\u3002\u4e0d\u5230\u4e00\u6708\uff0c\u4ed6\u68cb\u529b\u5927\u8fdb\uff0c\u6728\u6851\u53ea\u80fd\u8ba9\u4ed6\u4e09\u5b50\uff0c\u8fd9\u624d\u4e92\u6709\u80dc\u8d25\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5728\u56f4\u68cb\u4e0a\u4e00\u7528\u5fc3\uff0c\u81ea\u7136\u7ec3\u6b66\u7684\u65f6\u523b\u51cf\u5c11\u3002\u7a46\u4eba\u6e05\u788d\u4e8e\u8001\u53cb\u7684\u60c5\u9762\uff0c\u8d77\u521d\u8fd8\u4e0d\u8bf4\u751a\u4e48\uff0c\u540e\u6765\u89c1\u8fd9\u4e00\u8001\u4e00\u5c0f\uff0c\u7ec8\u65e5\u5e9f\u5bdd\u5fd8\u98df\u7684\u5728\u6978\u67b0\u4e0a\u6253\u4ea4\u9053\uff0c\u5b9e\u5728\u592a\u4e0d\u6210\u8bdd\uff0c\u4e8e\u662f\u6697\u4e2d\u5631\u5490\u8881\u627f\u5fd7\uff0c\u6bcf\u65e5\u53ea\u53ef\u4e0e\u6728\u6851\u4e0b\u4e00\u5c40\u68cb\uff0c\u5176\u4f59\u7684\u65f6\u5019\u90fd\u8981\u7528\u6765\u7ec3\u6b66\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7ecf\u5e08\u7236\u4e00\u63d0\u9192\uff0c\u5fc3\u60f3\u8fd9\u8bb8\u591a\u5929\u7684\u786e\u8352\u758f\u4e86\u6b66\u529f\uff0c\u6697\u6697\u60ed\u6127\uff0c\u8fde\u5fd9\u8d76\u7ec3\u5251\u6cd5\u3002\u4e00\u8fde\u4e24\u5929\uff0c\u6728\u6851\u53eb\u4ed6\u4e0b\u68cb\uff0c\u4ed6\u603b\u662f\u8bf4\u8981\u7ec3\u5251\u3002\u6728\u6851\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u966a\u6211\u4e0b\u68cb\uff0c\u4e0b\u5b8c\u4e4b\u540e\uff0c\u6211\u6559\u4f60\u4e00\u95e8\u529f\u592b\uff0c\u4f60\u5e08\u7236\u4e00\u5b9a\u559c\u6b22\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u53bb\u95ee\u8fc7\u5e08\u7236\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u95ee\u5427\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5954\u8fdb\u53bb\u628a\u6728\u6851\u7684\u8bdd\u5bf9\u5e08\u7236\u8bf4\u4e86\u3002\u7a46\u4eba\u6e05\u4e00\u542c\u5927\u559c\u3002 \u3000\u3000\u6728\u6851\u9053\u4eba\u5916\u53f7\u201c\u5343\u53d8\u4e07\u52ab\u201d\u3002\u4ed6\u5e74\u8f7b\u4e4b\u65f6\uff0c\u56e0\u8f7b\u529f\u5353\u7edd\uff0c\u8eab\u6cd5\u53d8\u5e7b\u65e0\u7a77\uff0c\u6c5f\u6e56\u4e0a\u9001\u4ed6\u4e2a\u5916\u53f7\uff0c\u53eb\u505a\u201c\u5343\u53d8\u4e07\u5316\u8349\u4e0a\u98de\u201d\u3002\u540e\u6765\u4ed6\u803d\u4e8e\u4e0b\u68cb\u3002\u56f4\u68cb\u4e4b\u9053\uff0c\u8bb2\u7a76\u201c\u6253\u52ab\u201d\uff0c\u65e0\u6570\u53d8\u5316\u4ff1\u4ece\u6253\u52ab\u800c\u751f\u3002\u6728\u6851\u6b66\u529f\u751a\u9ad8\uff0c\u81ea\u5df1\u5012\u4ee5\u4e3a\u5e73\u5e73\u65e0\u5947\uff0c\u68cb\u827a\u4e0d\u8fc7\u4e2d\u4e0a\uff0c\u5374\u662f\u81ea\u8d1f\u5f97\u7d27\uff0c\u7adf\u81ea\u884c\u6539\u4e86\u5916\u53f7\uff0c\u53eb\u505a\u201c\u5343\u53d8\u4e07\u52ab\u68cb\u56fd\u624b\u201d\u3002\u65c1\u4eba\u788d\u4e8e\u4ed6\u7684\u9762\u5b50\uff0c\u4e0d\u4fbf\u5bf9\u4ed6\u81ea\u6539\u7684\u5916\u53f7\u5168\u4e0d\u7406\u4f1a\uff0c\u53ef\u662f\u53c8\u77e5\u4ed6\u68cb\u827a\u548c\u201c\u56fd\u624b\u201d\u4e4b\u5883\u5b9e\u5728\u76f8\u53bb\u592a\u8fdc\uff0c\u4e8e\u662f\u6298\u8877\u800c\u7b80\u5316\u4e4b\uff0c\u79f0\u4e4b\u4e3a\u201c\u5343\u53d8\u4e07\u52ab\u201d\u3002\u8fd9\u56db\u5b57\u5176\u5b9e\u8fd8\u662f\u606d\u7ef4\u4ed6\u6b66\u529f\u5343\u53d8\u4e07\u5316\uff0c\u6740\u5f97\u654c\u4eba\u201c\u4e07\u52ab\u4e0d\u590d\u201d\u3002\u4f46\u5982\u6709\u4eba\u5f53\u9762\u5982\u6b64\u89e3\u91ca\uff0c\u6728\u6851\u52bf\u5fc5\u5927\u4e3a\u751f\u6c14\uff0c\u5b9a\u8981\u5bf9\u65b9\u627f\u8ba4\u8fd9\u5916\u53f7\u662f\u6307\u4ed6\u68cb\u827a\u800c\u8a00\uff0c\u624d\u80af\u7f62\u4f11\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u4e00\u76f4\u4f69\u670d\u4ed6\u6b66\u529f\u4e0a\u5b9e\u6709\u72ec\u5f97\u4e4b\u79d8\uff0c\u4f46\u4ed6\u4ece\u6765\u4e0d\u80af\u6388\u5f92\uff0c\u73b0\u4e0b\u4ed6\u7adf\u7b54\u5e94\u4f20\u6388\u8881\u627f\u5fd7\u6b66\u529f\uff0c\u90a3\u5b9a\u662f\u5b9e\u5728\u71ac\u4e0d\u8fc7\u68cb\u763e\u4e86\uff0c\u5fd9\u62c9\u4e86\u8881\u627f\u5fd7\u7684\u624b\u8d70\u51fa\u6765\uff0c\u5411\u6728\u6851\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u80af\u6210\u5168\u5c0f\u5f92\uff0c\u6211\u8fd9\u91cc\u5148\u8c22\u8c22\u5566\u3002\u201d\u53eb\u8881\u627f\u5fd7\u5411\u6728\u6851\u78d5\u5934\u62dc\u5e08\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8dea\u4e86\u4e0b\u53bb\u3002\u6728\u6851\u7eb5\u8eab\u800c\u8d77\uff0c\u53cc\u624b\u4e71\u6447\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u6536\u5f92\u5f1f\u3002\u4ed6\u8981\u6211\u6559\u529f\u592b\uff0c\u5f97\u51ed\u672c\u4e8b\u6765\u8d62\u3002\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u8fd9\u5c0f\u5a03\u513f\u751a\u4e48\u4e8b\u80fd\u8d62\u5f97\u4e86\u4f60\uff1f\u201d \u3000\u3000\u6728\u6851\u9053\uff1a\u201c\u5251\u6cd5\u62f3\u672f\uff0c\u4f60\u8001\u7a46\u5929\u4e0b\u65e0\u53cc\uff0c\u6211\u8001\u9053\u7518\u62dc\u4e0b\u98ce\uff0c\u8fd9\u5b69\u5b50\u53ea\u6d88\u80fd\u5b66\u5230\u4f60\u529f\u592b\u7684\u4e24\u4e09\u6210\uff0c\u6c5f\u6e56\u4e0a\u5df2\u96be\u89c5\u654c\u624b\u3002\u4f46\u8bf4\u5230\u8f7b\u529f\u3001\u6697\u5668\uff0c\u53ea\u6015\u6211\u8001\u9053\u4e5f\u8fd8\u6709\u4e24\u4e0b\u5b50\uff01\u201d \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u8c01\u4e0d\u77e5\u9053\u4f60\u2018\u5343\u53d8\u4e07\u52ab\u2019\uff0c\u82b1\u6837\u767e\u51fa\uff01\u201d\u6728\u6851\u7b11\u9053\uff1a\u201c\u2018\u5343\u53d8\u4e07\u52ab\u2019\u662f\u6307\u8001\u9053\u68cb\u827a\u5929\u4e0b\u65e0\u53cc\uff0c\u8ddf\u6b66\u529f\u51b3\u8ba1\u6cbe\u4e0d\u4e0a\u8fb9\uff0c\u4e07\u4e07\u4e0d\u53ef\u6df7\u4e3a\u4e00\u8c08\u3002\u53ea\u56e0\u4f60\u81ea\u5c45\u4e00\u6d3e\u5b97\u5e08\uff0c\u4e8b\u4e8b\u8bb2\u7a76\u51a0\u5195\u5802\u7687\u3001\u6c14\u6d3e\u98ce\u5ea6\uff0c\u4e8e\u8f7b\u529f\u6697\u5668\u4e0d\u80af\u591a\u4e0b\u529f\u592b\uff0c\u624d\u8ba9\u8001\u9053\u80fd\u5728\u8fd9\u4e24\u95e8\u4e0a\u51fa\u51fa\u98ce\u5934\u3002\u8fd9\u6837\u7f62\uff0c\u4f60\u8ba9\u627f\u5fd7\u6bcf\u5929\u548c\u6211\u4e0b\u4e24\u76d8\u68cb\uff0c\u6211\u8ba9\u4ed6\u4e09\u5b50\u3002\u6211\u8d62\u4e86\uff0c\u90a3\u5c31\u662f\u966a\u5e08\u4f2f\u6d88\u9063\uff0c\u7b97\u4ed6\u7684\u5b5d\u5fc3\u3002\u8981\u662f\u4ed6\u8d62\u5f97\u4e00\u5c40\uff0c\u6211\u5c31\u6559\u4ed6\u4e00\u62db\u8f7b\u529f\uff0c\u8fde\u8d62\u4e24\u5c40\uff0c\u8f7b\u529f\u4e4b\u5916\u518d\u6559\u4e00\u62db\u6697\u5668\u3002\u54b1\u4eec\u4e0b\u68cb\u8bb2\u7a76\u535a\u5f69\uff0c\u90a3\u4fbf\u662f\u5f69\u5934\u4e86\u3002\u4f60\u8bf4\u8fd9\u4e48\u7740\u516c\u4e0d\u516c\u5e73\uff1f\u201d \u3000\u3000\u7a46\u4eba\u6e05\u5fc3\u60f3\u8fd9\u8001\u9053\u5f53\u771f\u6ed1\u7a3d\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5c31\u662f\u8fd9\u4e48\u529e\u3002 \u3000\u3000\u6211\u672c\u6765\u6015\u627f\u5fd7\u4e0b\u68cb\u803d\u8bef\u4e86\u529f\u592b\uff0c\u73b0\u4e0b\u65e2\u6709\u5982\u6b64\u5927\u597d\u5904\uff0c\u4f60\u4eec\u6bcf\u5929\u4e0b\u5341\u5c40\u516b\u5c40\u6211\u4e5f\u4e0d\u7ba1\u3002\u201d\u6728\u6851\u548c\u8881\u627f\u5fd7\u4e00\u542c\u5927\u559c\uff0c\u4e00\u8001\u4e00\u5c0f\u53c8\u4e0b\u68cb\u53bb\u4e86\u3002 \u3000\u3000\u6728\u6851\u8fd9\u5929\u4e00\u80dc\u4e00\u8d1f\uff0c\u68cb\u5c40\u65e2\u7ec8\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4eca\u5929\u6559\u4f60\u4e00\u62db\u8f7b\u8eab\u529f\u592b\uff0c\u867d\u7136\u53ea\u662f\u4e00\u62db\uff0c\u53ea\u8981\u4f60\u7528\u5fc3\u53bb\u7ec3\uff0c\u53ef\u4e5f\u591f\u4f60\u7ec8\u8eab\u53d7\u7528\u65e0\u7a77\u3002\u4ed4\u7ec6\u77a7\u7740\u3002\u201d\u8bdd\u521a\u8bf4\u6bd5\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u5f2f\u817f\u4f5c\u52bf\uff0c\u5ffd\u7136\u5168\u8eab\u62d4\u8d77\uff0c\u5df2\u7a9c\u5230\u4e86\u5927\u6811\u4e4b\u5dc5\uff0c\u4e00\u4e2a\u5012\u7ffb\u7b4b\u6597\uff0c\u53c8\u7ad9\u5728\u4ed6\u9762\u524d\u3002\u8881\u627f\u5fd7\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u62cd\u638c\u53eb\u597d\u3002 \u3000\u3000\u6728\u6851\u9053\u4eba\u5f53\u4e0b\u628a\u8fd9\u4e00\u62db\u201c\u6500\u4e91\u4e58\u9f99\u201d\u7684\u8f7b\u8eab\u529f\u592b\u6559\u4e86\u4ed6\uff0c\u867d\u8bf4\u53ea\u662f\u4e00\u62db\uff0c\u53ef\u89c1\u8170\u817f\u4e4b\u52b2\uff0c\u6b65\u6cd5\u773c\u795e\uff0c\u90fd\u6709\u65e0\u6570\u5965\u5999\u3002\u8881\u627f\u5fd7\u7528\u5fc3\u5b66\u4e60\uff0c\u4e00\u65f6\u5374\u4e5f\u4e0d\u6613\u9886\u4f1a\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u8881\u627f\u5fd7\u8fde\u8f93\u4e24\u5c40\uff0c\u4e00\u65e0\u6240\u83b7\u3002\u7b2c\u4e09\u5929\u4e0a\uff0c\u4ed6\u7a81\u51fa\u5947\u5175\uff0c\u628a\u8fb9\u89d2\u5168\u90e8\u653e\u5f03\uff0c\u5c3d\u5360\u4e2d\u592e\u8179\u5730\uff0c\u5c45\u7136\u4e24\u5c40\u90fd\u80dc\u3002\u6728\u6851\u4e0d\u670d\u6c14\uff0c\u53c8\u4e0b\u4e24\u5c40\uff0c\u8fd9\u6b21\u662f\u4e00\u80dc\u4e00\u8d1f\uff0c\u7ed3\u7b97\u4e0b\u6765\uff0c\u6728\u6851\u8be5\u6559\u4ed6\u4e09\u62db\u3002 \u3000\u3000\u6728\u6851\u6559\u4e86\u4ed6\u4e24\u62db\u8f7b\u529f\uff0c\u89c1\u4ed6\u8bb0\u4f4f\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u6211\u5bf9\u654c\u65f6\u4f7f\u751a\u4e48\u5175\u5668\uff1f\u201d\u8881\u627f\u5fd7\u6447\u6447\u5934\u3002\u6728\u6851\u9053\u4eba\u6293\u8d77\u68cb\u76d8\uff0c\u7b11\u9053\uff1a\u201c\u672c\u6765\u6211\u4e5f\u4f7f\u5251\uff0c\u4f46\u8fd1\u5e74\u5374\u5df2\u6539\u7528\u8fd9\u5bb6\u4f19\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u65e9\u89c1\u8fd9\u68cb\u76d8\u662f\u7cbe\u94a2\u6240\u94f8\uff0c\u4ee5\u4e3a\u4ed6\u559c\u7231\u5955\u9053\uff0c\u968f\u8eab\u643a\u5e26\u68cb\u5c40\uff0c\u4e3a\u6015\u68cb\u76d8\u635f\u574f\uff0c\u662f\u4ee5\u7279\u7528\u94a2\u94f8\uff0c\u54ea\u77e5\u7adf\u662f\u5bf9\u654c\u7684\u5175\u5668\u3002\u6728\u6851\u53c8\u62c8\u8d77\u4e00\u628a\u68cb\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u6697\u5668\uff01\u201d\u968f\u624b\u63b7\u51fa\uff0c\u5341\u51e0\u9897\u68cb\u5b50\u5411\u5929\u98de\u53bb\u3002 \u3000\u3000\u5f85\u68cb\u5b50\u843d\u4e0b\uff0c\u6728\u6851\u4e3e\u8d77\u68cb\u76d8\u4e00\u63a5\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u5341\u51e0\u9897\u68cb\u5b50\u540c\u65f6\u843d\u5728\u68cb\u76d8\u4e4b\u4e0a\u3002\u8881\u627f\u5fd7\u4f38\u51fa\u4e86\u820c\u5934\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u672c\u6765\u5341\u51e0\u9897\u68cb\u5b50\u629b\u4e0a\u5929\u7a7a\uff0c\u843d\u4e0b\u65f6\u5b9a\u6709\u5148\u540e\uff0c\u94c1\u68cb\u5b50\u548c\u94f6\u68cb\u5b50\u78b0\u5230\u94a2\u68cb\u76d8\uff0c\u5fc5\u662f\u53ee\u53ee\u5f53\u5f53\u7684\u4e71\u54cd\u4e00\u9635\uff0c\u54ea\u77e5\u5341\u51e0\u9897\u68cb\u5b50\u843d\u4e0b\u6765\u7adf\u662f\u540c\u65f6\u78b0\u5230\u68cb\u76d8\uff0c\u7136\u5219\u629b\u63b7\u4e0a\u53bb\u65f6\u624b\u529b\u7684\u5e73\u5300\uff0c\u5b9e\u662f\u60ca\u4eba\u3002\u66f4\u5947\u7684\u662f\uff0c\u5341\u51e0\u9897\u68cb\u5b50\u843d\u5728\u68cb\u76d8\u4e4b\u4e0a\uff0c\u7adf\u65e0\u4e00\u9897\u5f39\u5f00\u843d\u5730\uff0c\u4f46\u89c1\u4ed6\u53f3\u624b\u5fae\u5fae\u4e00\u6c89\uff0c\u5df2\u6d88\u4e86\u68cb\u5b50\u4e0b\u843d\u4e4b\u52bf\uff0c\u4e00\u9897\u9897\u68cb\u5b50\u5c31\u4f3c\u7528\u624b\u6446\u5728\u68cb\u76d8\u4e0a\u4e00\u822c\u3002 \u3000\u3000\u6728\u6851\u7b11\u9053\uff1a\u201c\u6253\u6697\u5668\u8981\u5148\u7ec3\u529b\uff0c\u518d\u7ec3\u51c6\u5934\uff0c\u53d1\u51fa\u53bb\u7684\u8f7b\u91cd\u6709\u4e86\u628a\u63e1\uff0c\u518d\u8c08\u5f97\u4e0a\u51c6\u4e0d\u51c6\u3002\u201d\u4e8e\u662f\u628a\u6295\u63b7\u68cb\u5b50\u7528\u529b\u4f7f\u52b2\u7684\u5fc3\u6cd5\u4f20\u6388\u4e86\u4ed6\u3002 \u3000\u3000\u6728\u6851\u5728\u534e\u5c71\u7edd\u9876\u4e00\u4f4f\u5c31\u662f\u5927\u534a\u5e74\uff0c\u5929\u5929\u4e0e\u8fd9\u4f4d\u5c0f\u53cb\u5bf9\u5f08\uff0c\u6d41\u8fde\u5fd8\u8fd4\uff0c\u4e50\u800c\u5fd8\u5026\uff0c\u800c\u4e00\u8eab\u8f7b\u8eab\u529f\u592b\u548c\u6253\u68cb\u5b50\u7684\u5fc3\u6cd5\uff0c\u5728\u8fd9\u5927\u534a\u5e74\u4e2d\u4e5f\u6beb\u4e0d\u85cf\u79c1\u7684\u4f20\u4e86\u7ed9\u4ed6\u3002 \u3000\u3000\u8fd9\u5929\u6b63\u662f\u76db\u6691\uff0c\u8881\u627f\u5fd7\u4e0a\u5348\u7ec3\u4e86\u62f3\u5251\uff0c\u4e0b\u5348\u548c\u6728\u6851\u5728\u6811\u4e0b\u5bf9\u5f08\u3002\u8fd9\u65f6\u4ed6\u68cb\u529b\u65e9\u5df2\u9ad8\u51fa\u6728\u6851\u4e00\u5148\uff0c\u53ef\u662f\u6728\u6851\u597d\u80dc\uff0c\u6bcf\u6b21\u8fd8\u662f\u8981\u8ba9\u4ed6\u5148\u884c\uff0c\u90a3\u66f4\u662f\u80dc\u5c11\u8d25\u591a\u4e86\u3002\u7eb5\u7136\u201c\u5343\u53d8\u4e07\u52ab\u201d\uff0c\u53d8\u6765\u53d8\u53bb\uff0c\u4e5f\u4ecd\u662f\u4e0d\u514d\u843d\u8d25\u3002\u8d25\u5f97\u8d8a\u591a\uff0c\u4f20\u6388\u6b66\u529f\u7684\u6b21\u6570\u4e5f\u662f\u8d8a\u5bc6\u3002\u597d\u5728\u4ed6\u68cb\u827a\u4e0a\u53d8\u5316\u6709\u9650\uff0c\u6b66\u5b66\u5374\u5b9e\u662f\u5e7f\u535a\uff0c\u8f93\u68cb\u867d\u591a\uff0c\u5c3d\u6709\u5c42\u51fa\u4e0d\u7a77\u7684\u62db\u6570\u6765\u8fd8\u503a\u3002 \u3000\u3000\u8fd9\u5929\u6559\u7684\u4ecd\u662f\u53d1\u6697\u5668\u7684\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u624b\u6cd5\uff0c\u4e00\u624b\u540c\u65f6\u6492\u51fa\u4e03\u9897\u68cb\u5b50\uff0c\u8981\u9897\u9897\u6253\u4e2d\u654c\u4eba\u7a74\u9053\u3002\u8fd9\u9879\u4e0a\u4e58\u6b66\u529f\u81ea\u975e\u671d\u5915\u4e4b\u95f4\u6240\u80fd\u5b66\u4f1a\uff0c\u8881\u627f\u5fd7\u5728\u8fd9\u529f\u592b\u4e0a\u5df2\u4e0b\u4e86\u4e24\u4e2a\u591a\u6708\u82e6\u529f\uff0c\u53ef\u662f\u540c\u65f6\u53d1\u51fa\u4e09\u56db\u9897\u68cb\u5b50\uff0c\u6bcf\u6b21\u603b\u53ea\u80fd\u6709\u4e00\u4e8c\u9897\u6253\u4e2d\u3002 \u3000\u3000\u6728\u6851\u505a\u4e86\u4e2a\u6728\u724c\uff0c\u724c\u4e0a\u753b\u4e86\u4eba\u5f62\uff0c\u53eb\u54d1\u5df4\u4e3e\u4e86\u6728\u724c\u5954\u8dd1\u3002 \u3000\u3000\u6728\u6851\u558a\u9053\uff1a\u201c\u5929\u5b97\u3001\u80a9\u8d1e\u3001\u7389\u6795\uff01\u201d\u8881\u627f\u5fd7\u4e09\u9897\u68cb\u5b50\u53d1\u51fa\uff0c\u6253\u4e2d\u4e86\u5929\u5b97\u3001\u7389\u6795\u4e24\u7a74\uff0c\u80a9\u8d1e\u4e00\u7a74\u5374\u6253\u504f\u4e86\u3002\u6728\u6851\u53c8\u558a\uff1a\u201c\u5173\u5143\u3001\u795e\u5c01\u3001\u4e2d\u5ead\u3002\u201d\u54d1\u5df4\u4e00\u8fb9\u8dd1\uff0c\u4e00\u8fb9\u628a\u6728\u724c\u4e71\u6643\u3002\u8881\u627f\u5fd7\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u8ffd\u8d76\u4e0a\u53bb\uff0c\u624b\u4e00\u6325\uff0c\u6728\u6851\u5df2\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u5173\u5143\u7a74\u6ca1\u4e2d\u3002\u201d \u3000\u3000\u6b63\u8981\u518d\u558a\uff0c\u5ffd\u542c\u5f97\u8881\u627f\u5fd7\u60ca\u53eb\u4e00\u58f0\uff0c\u62a2\u4e0a\u53bb\u5c06\u54d1\u5df4\u4e00\u628a\u62c9\u4f4f\uff0c\u5411\u540e\u529b\u626f\u3002 \u3000\u3000\u54d1\u5df4\u4e00\u5446\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u4e00\u5934\u5927\u7329\u7329\u7ad9\u5728\u8eab\u540e\uff0c\u795e\u6001\u72f0\u72de\uff0c\u5f20\u7259\u821e\u722a\uff0c\u4f5c\u52bf\u6b32\u6251\u3002\u54d1\u5df4\u4e3e\u8d77\u6728\u724c\u5288\u5934\u5411\u7329\u7329\u6253\u4e0b\uff0c\u7a81\u7136\u5de6\u81c2\u4e00\u7d27\uff0c\u5df2\u88ab\u6728\u6851\u62c9\u4e86\u56de\u6765\u3002 \u3000\u3000\u6728\u6851\u53eb\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u4f60\u5bf9\u4ed8\u5b83\uff01\u201d\u8881\u627f\u5fd7\u77e5\u662f\u6728\u6851\u5e08\u4f2f\u8003\u67e5\u4ed6\u529f\u592b\uff0c\u7b54\u5e94\u4e86\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u8f7b\u98d8\u98d8\u7684\u7eb5\u5230\u7329\u7329\u4e4b\u524d\u3002 \u3000\u3000\u7329\u7329\u89c1\u4ed6\u6765\u5f97\u5feb\u901f\uff0c\u8f6c\u8eab\u60f3\u8d70\uff0c\u8881\u627f\u5fd7\u7528\u91cd\u624b\u62cd\u7684\u4e00\u58f0\uff0c\u5728\u5b83\u80cc\u4e0a\u91cd\u91cd\u4e00\u638c\u3002\u7329\u7329\u75db\u5f97\u54c7\u54c7\u602a\u53eb\uff0c\u8f6c\u8eab\u6325\u957f\u81c2\u6765\u6293\u3002\u8881\u627f\u5fd7\u6258\u5730\u8df3\u5f00\uff0c\u6b63\u8981\u4e58\u9699\u8fce\u51fb\uff0c\u5ffd\u89c9\u8eab\u540e\u751f\u98ce\uff0c\u4f3c\u6709\u654c\u4eba\u6765\u88ad\u3002\u4ed6\u4e0d\u53ca\u56de\u5934\uff0c\u5de6\u811a\u4e00\u70b9\uff0c\u8dc3\u5728\u7a7a\u4e2d\uff0c\u4eba\u672a\u843d\u5730\uff0c\u5df2\u89c1\u88ad\u51fb\u4ed6\u7684\u539f\u6765\u662f\u53e6\u4e00\u5934\u5927\u7329\u7329\u3002 \u3000\u3000\u4ed6\u4e0a\u5c71\u540e\u7ec3\u4e86\u8fd9\u4e9b\u5e74\u6b66\u529f\uff0c\u53ea\u4e0e\u5e08\u7236\u62c6\u89e3\uff0c\u5374\u4ece\u672a\u4e0e\u4eba\u5f53\u771f\u52a8\u8fc7\u624b\uff0c\u4e24\u5934\u7329\u7329\u867d\u7136\u72de\u6076\uff0c\u4ed6\u5374\u4e5f\u4e0d\u754f\u60e7\uff0c\u5c55\u5f00\u4f0f\u864e\u638c\u6cd5\u4e0e\u4e24\u517d\u6597\u4e86\u8d77\u6765\u3002\u6b64\u65f6\u7684\u638c\u6cd5\u52b2\u529b\uff0c\u4e0e\u5f53\u5e74\u5728\u5723\u5cf0\u5d82\u5fe0\u70c8\u7960\u4e2d\u6597\u8c79\u4e4b\u65f6\uff0c\u81ea\u5df1\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\u3002 \u3000\u3000\u547c\u559d\u58f0\u4e2d\uff0c\u7a46\u4eba\u6e05\u4e5f\u5954\u4e86\u51fa\u6765\uff0c\u89c1\u8881\u627f\u5fd7\u529b\u6597\u4e24\u517d\uff0c\u624b\u638c\u6240\u5230\u4e4b\u5904\uff0c\u7329\u7329\u65e0\u4e0d\u75db\u5f97\u5475\u5475\u5927\u53eb\uff0c\u5fc3\u4e0b\u4e5f\u81ea\u6b23\u559c\uff1a\u201c\u8fd9\u5b69\u5b50\u4e0d\u6789\u4e86\u6211\u4e00\u756a\u5fc3\u8840\u3002\u201d \u3000\u3000\u4e24\u5934\u7329\u7329\u5403\u4e86\u82e6\u5934\uff0c\u4e0d\u6562\u8feb\u8fd1\uff0c\u53ea\u662f\u7a9c\u6765\u8df3\u53bb\uff0c\u4fdf\u673a\u8fdb\u6251\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u89c1\u8881\u627f\u5fd7\u638c\u6cd5\u5c3d\u53ef\u5236\u5f97\u4f4f\u4e24\u5934\u755c\u751f\uff0c\u8981\u518d\u770b\u770b\u4ed6\u7684\u5251\u6cd5\uff0c\u4e8e\u662f\u5954\u8fdb\u53bb\u53d6\u51fa\u957f\u5251\uff0c\u53eb\u9053\uff1a\u201c\u63a5\u5251\uff01\u201d\u5c06\u5251\u63b7\u5411\u7a7a\u4e2d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7eb5\u8d77\u8eab\u6765\uff0c\u53f3\u624b\u4e00\u6284\uff0c\u63a5\u4f4f\u5251\u67c4\uff0c\u957f\u5251\u5728\u624b\uff0c\u767b\u65f6\u5982\u864e\u6dfb\u7ffc\uff0c\u4eba\u672a\u843d\u4e0b\uff0c\u4e00\u62db\u201c\u7a7f\u9488\u5f15\u7ebf\u201d\uff0c\u5411\u4e00\u5934\u7329\u7329\u80a9\u4e0a\u523a\u4e86\u8fc7\u53bb\uff0c\u90a3\u7329\u7329\u6025\u5fd9\u540e\u9000\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u67c4\u5251\u4f7f\u4e86\u5f00\u6765\uff0c\u767b\u65f6\u628a\u4e24\u5934\u7329\u7329\u88f9\u5728\u5251\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u6728\u6851\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u522b\u4f24\u5b83\u4eec\u6027\u547d\u3002\u201d\u8881\u627f\u5fd7\u7b54\u5e94\u4e00\u58f0\uff0c\u957f\u5251\u4f7f\u5f97\u66f4\u52a0\u7d27\u4e86\uff0c\u8fd9\u65f6\u5019\u4ed6\u8981\u523a\u6740\u7329\u7329\uff0c\u5df2\u662f\u6613\u5982\u53cd\u638c\u3002\u4e24\u5934\u7329\u7329\u8f6c\u773c\u95f4\u81c2\u4e0a\u3001\u80a9\u4e0a\u3001\u817f\u4e0a\u3001\u5934\u4e0a\uff0c\u5251\u521b\u7d2f\u7d2f\uff0c\u4ed6\u59cb\u7ec8\u672a\u4e0b\u7edd\u62db\uff0c\u6bcf\u624b\u90fd\u662f\u6d45\u4f24\u5373\u6b62\u3002 \u3000\u3000\u4e24\u5934\u7329\u7329\u9887\u6709\u7075\u6027\uff0c\u8d77\u521d\u8fd8\u60f3\u594b\u529b\u9003\u547d\uff0c\u540e\u6765\u89c1\u5fae\u4e00\u7eb5\u5f00\uff0c\u5251\u950b\u968f\u5230\uff0c\u53ea\u8981\u505c\u6b65\uff0c\u5bf9\u65b9\u5c31\u6536\u62db\uff0c\u77e5\u4ed6\u6709\u610f\u4e0d\u4e0b\u6740\u624b\uff0c\u5ffd\u7136\u540c\u65f6\u53eb\u4e86\u51e0\u58f0\uff0c\u8e72\u5728\u5730\u4e0b\u3002\u53cc\u624b\u62b1\u5934\uff0c\u4e0d\u518d\u8fdb\u6251\uff0c\u56db\u53ea\u773c\u73e0\u9aa8\u788c\u788c\u7684\u8f6c\u52a8\uff0c\u671b\u7740\u8881\u627f\u5fd7\u3002\u9732\u51fa\u54c0\u6c42\u7684\u795e\u8272\u3002 \u3000\u3000\u54d1\u5df4\u89c1\u8881\u627f\u5fd7\u5236\u670d\u4e86\u4e24\u5934\u755c\u751f\uff0c\u9ad8\u5174\u5f97\u62cd\u624b\u987f\u8db3\uff0c\u5954\u8fdb\u53bb\u53d6\u51fa\u4e00\u6346\u9ebb\u7ef3\u6765\uff0c\u5c06\u4e24\u5934\u7329\u7329\u7f1a\u4f4f\u3002\u53cc\u7329\u8d77\u521d\u8fd8\u9732\u9f7f\u5486\u54ee\uff0c\u4f46\u54d1\u5df4\u7528\u529b\u4e00\u634f\uff0c\u7329\u7329\u7b4b\u9aa8\u5267\u75db\uff0c\u4e0d\u6562\u518d\u884c\u53cd\u6297\uff0c\u53ea\u5f97\u4e56\u4e56\u53d7\u7f1a\uff0c\u53ea\u662f\u53fd\u53fd\u5495\u5495\u7684\u53eb\u4e2a\u4e0d\u4f11\u3002 \u3000\u3000\u6728\u6851\u4e0e\u7a46\u4eba\u6e05\u90fd\u8d5e\u8881\u627f\u5fd7\u8fd1\u6765\u529f\u529b\u5927\u8fdb\uff0c\u7740\u5b9e\u52c9\u52b1\u4e86\u51e0\u53e5\u3002\u8881\u627f\u5fd7\u5f88\u662f\u9ad8\u5174\uff0c\u7528\u91d1\u521b\u836f\u6577\u4e0a\u53cc\u7329\u4f24\u53e3\uff0c\u53c8\u91c7\u4e9b\u679c\u5b50\u7ed9\u5b83\u4eec\u5403\u4e86\u3002 \u3000\u3000\u517b\u4e86\u4e03\u516b\u5929\uff0c\u7329\u7329\u91ce\u6027\u6e10\u9664\uff0c\u89e3\u53bb\u7ef3\u5b50\u540e\uff0c\u5c45\u7136\u4e5f\u4e0d\u9003\u8d70\u3002\u8881\u627f\u5fd7\u5927\u559c\uff0c\u7ed9\u96c4\u7329\u7329\u53d6\u540d\u201c\u5927\u5a01\u201d\uff0c\u96cc\u7329\u7329\u53eb\u505a\u201c\u5c0f\u4e56\u201d\u3002\u7a46\u4eba\u6e05\u4e0e\u6728\u6851\u89c1\u96cc\u7329\u7329\u5982\u6b64\u6bdb\u8338\u8338\u7684\u4e00\u5934\u5e9e\u7136\u5927\u7269\uff0c\u7adf\u53d6\u4e86\u8fd9\u822c\u5c0f\u5de7\u73b2\u73d1\u7684\u540d\u5b57\uff0c\u90fd\u4e0d\u7981\u5931\u7b11\u3002 \u3000\u3000\u5927\u5a01\u548c\u5c0f\u4e56\u8d8a\u517b\u8d8a\u9a6f\uff0c\u8881\u627f\u5fd7\u4e00\u53d1\u547d\u4ee4\uff0c\u53cc\u7329\u7acb\u5373\u9075\u884c\u65e0\u8fdd\u3002 \u3000\u3000\u8fd9\u4e00\u5929\uff0c\u4e24\u5934\u7329\u7329\u6500\u5230\u5cf0\u897f\u7edd\u58c1\u4e0a\u91c7\u6458\u679c\u5b50\uff0c\u8fd9\u7edd\u58c1\u4e00\u9762\u8f83\u659c\uff0c\u5c1a\u53ef\u6500\u63f4\uff0c\u53e6\u4e00\u9762\u5374\u5982\u4e00\u5927\u5835\u5e73\u5899\uff0c\u6beb\u65e0\u53ef\u5bb9\u624b\u8db3\u4e4b\u5904\u3002\u53cc\u7329\u6458\u679c\u5b09\u620f\uff0c\u5c0f\u4e56\u5ffd\u7136\u5931\u8db3\uff0c\u4ece\u6811\u4e0a\u8dcc\u4e86\u4e0b\u6765\uff0c\u76f4\u5411\u7edd\u58c1\u4e00\u9762\u6e9c\u4e0b\u3002\u8fd9\u7edd\u58c1\u79bb\u5730\u56db\u5341\u591a\u4e08\uff0c\u4e00\u6389\u4e0b\u53bb\u81ea\u662f\u7c89\u8eab\u788e\u9aa8\u3002\u5927\u5a01\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u8d76\u5230\u5c71\u58c1\u4e0a\u770b\u65f6\uff0c\u89c1\u5c0f\u4e56\u5e78\u559c\u5e76\u672a\u6389\u4e0b\uff0c\u4e24\u6761\u957f\u81c2\u6500\u5728\u5c71\u58c1\u4e0a\u4e00\u4e2a\u6d1e\u91cc\u3002\u8fd9\u6d1e\u7a74\u5e74\u6df1\u6708\u4e45\uff0c\u672c\u6765\u88ab\u6ce5\u571f\u5c01\u4f4f\uff0c\u5c0f\u4e56\u6389\u4e0b\u6765\u65f6\u5728\u5c71\u58c1\u4e0a\u4e71\u6293\u4e71\u722c\uff0c\u51d1\u5de7\u6293\u7834\u5c01\u6ce5\uff0c\u624b\u6307\u52fe\u4f4f\u4e86\u6d1e\u7a74\u3002\u53ea\u662f\u8eab\u5b50\u6302\u5728\u534a\u7a7a\uff0c\u4e0a\u4e0d\u5f97\uff0c\u4e0b\u4e0d\u53bb\uff0c\u5341\u5206\u72fc\u72c8\u3002 \u3000\u3000\u5927\u5a01\u65e0\u6cd5\u53ef\u65bd\uff0c\u98de\u5954\u4e0b\u5c71\uff0c\u6765\u8ba8\u6551\u5175\u3002\u8881\u627f\u5fd7\u6b63\u5728\u7ec3\u5251\uff0c\u89c1\u5b83\u6ee1\u8eab\u88ab\u8346\u68d8\u523a\u5f97\u6591\u6591\u8840\u8ff9\uff0c\u795e\u6001\u60ca\u60f6\uff0c\u4e0d\u4f4f\u8df3\u8dc3\uff0c\u53e3\u4e2d\u5431\u5431\u4e71\u53eb\uff0c\u77e5\u9053\u5c0f\u4e56\u5fc5\u5b9a\u51fa\u4e86\u4e8b\uff0c\u5fd9\u62db\u547c\u4e86\u54d1\u5df4\uff0c\u4e00\u8d77\u8ddf\u5927\u5a01\u51fa\u53bb\u3002\u5927\u5a01\u6307\u7740\u5ced\u58c1\uff0c\u4e71\u8df3\u4e71\u53eb\u3002\u8881\u627f\u5fd7\u548c\u54d1\u5df4\u5954\u8fd1\u4e00\u770b\uff0c\u89c1\u5230\u5c0f\u4e56\u540a\u5728\u534a\u7a7a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u56de\u5230\u77f3\u5c4b\u53d6\u4e86\u51e0\u6761\u957f\u7ef3\uff0c\u548c\u54d1\u5df4\u3001\u5927\u5a01\u4ece\u659c\u5761\u722c\u4e0a\u7edd\u58c1\uff0c\u5c06\u4e09\u6761\u957f\u7ef3\u63a5\u4e86\u8d77\u6765\uff0c\u60ac\u5782\u4e0b\u53bb\u3002\u5c0f\u4e56\u8fd9\u65f6\u5df2\u7d2f\u5f97\u7b4b\u75b2\u529b\u5c3d\uff0c\u4e00\u89c1\u7ef3\u5b50\uff0c\u53cc\u624b\u53cc\u811a\u6b7b\u547d\u62c9\u4f4f\u3002\u54d1\u5df4\u548c\u5927\u5a01\u4e00\u9f50\u7528\u529b\uff0c\u5c06\u5b83\u62c9\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5c0f\u4e56\u8eab\u4e0a\u88ab\u5c71\u77f3\u64e6\u4f24\u4e86\u6570\u5904\uff0c\u53d7\u4f24\u4e0d\u91cd\uff0c\u4f46\u5b83\u5431\u5431\u800c\u53eb\uff0c\u628a\u624b\u638c\u76f4\u4f38\u5230\u8881\u627f\u5fd7\u9762\u524d\u3002\u8881\u627f\u5fd7\u4e00\u770b\uff0c\u53ea\u89c1\u5b83\u624b\u638c\u4e0a\u9489\u7740\u4e24\u679a\u5947\u5f62\u6697\u5668\uff0c\u94f8\u6210\u5c0f\u86c7\u6a21\u6837\uff0c\u4f38\u624b\u4e00\u62d4\uff0c\u7adf\u62d4\u4e0d\u4e0b\u6765\uff0c\u5c0f\u4e56\u5374\u5df2\u75db\u5f97\u4e71\u8df3\uff0c\u77e5\u9053\u6697\u5668\u4e0b\u9762\u751f\u6709\u5012\u523a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u6765\u4e86\u654c\u4eba\uff1f\u201d\u5fd9\u6253\u624b\u52bf\u95ee\u5c0f\u4e56\uff0c\u6697\u5668\u662f\u8c01\u6253\u6765\u7684\uff1f\u5c0f\u4e56\u6307\u624b\u5212\u811a\uff0c\u793a\u610f\u8bf4\u4f38\u624b\u5230\u6d1e\u4e2d\u65f6\u523a\u4e0a\u7684\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5f88\u662f\u5947\u602a\uff0c\u5fc3\u60f3\u8fd9\u7edd\u58c1\u4e0a\u7684\u6d1e\u7a74\u7d20\u4e0d\u9732\u5f62\uff0c\u800c\u4e14\u4e0a\u8ddd\u5c71\u9876\u3001\u4e0b\u79bb\u5730\u9762\u90fd\u662f\u751a\u8fdc\uff0c\u600e\u4f1a\u6709\u6697\u5668\u85cf\u5728\u5176\u4e2d\uff1f\u60f3\u4e86\u4e00\u4f1a\uff0c\u96be\u4ee5\u7d22\u89e3\uff0c\u4fbf\u53bb\u89c1\u5e08\u7236\u548c\u6728\u6851\u9053\u4eba\u3002 \u3000\u3000\u4e24\u4eba\u542c\u4ed6\u8bf4\u660e\u60c5\u7531\uff0c\u89c1\u4e86\u5c0f\u4e56\u638c\u4e0a\u7684\u6697\u5668\uff0c\u4e5f\u90fd\u79f0\u5947\u3002\u6728\u6851\u9053\uff1a\u201c\u6211\u4ece\u6765\u7231\u6253\u6697\u5668\uff0c\u6c5f\u6e56\u4e0a\u5404\u5bb6\u5404\u95e8\u7684\u6697\u5668\u90fd\u89c1\u8bc6\u8fc7\uff0c\u8fd9\u86c7\u5f62\u5c0f\u9525\u4eca\u65e5\u5374\u662f\u9996\u6b21\u89c1\u5230\u3002\u8001\u7a46\uff0c\u8fd9\u53ef\u628a\u6211\u8003\u5012\u5566\u3002\u201d\u7a46\u4eba\u6e05\u4e5f\u6697\u6697\u7eb3\u7f55\uff0c\u8bf4\u9053\uff1a\u201c\u628a\u5b83\u8d77\u51fa\u6765\u518d\u8bf4\u3002\u201d \u3000\u3000\u6728\u6851\u56de\u5230\u623f\u4e2d\uff0c\u4ece\u836f\u56ca\u91cc\u53d6\u51fa\u4e00\u628a\u950b\u5229\u5c0f\u5200\uff0c\u5272\u5f00\u5c0f\u4e56\u638c\u4e0a\u808c\u8089\uff0c\u5c06\u4e24\u679a\u6697\u5668\u6316\u4e86\u51fa\u6765\u3002\u5c0f\u4e56\u77e5\u662f\u7ed9\u5b83\u6cbb\u4f24\uff0c\u6beb\u6ca1\u53cd\u6297\u3002\u6728\u6851\u7ed9\u5b83\u6577\u4e0a\u836f\uff0c\u7528\u5e03\u624e\u597d\u4f24\u53e3\u3002\u5c0f\u4e56\u7ecf\u8fc7\u8fd9\u6b21\u5927\u96be\uff0c\u751a\u4e3a\u59d4\u987f\u3002\u5927\u5a01\u7ed9\u5b83\u6414\u75d2\u6349\u8671\uff0c\u62da\u547d\u8ba8\u597d\uff0c\u4ee5\u793a\u5b89\u6170\u3002 \u3000\u3000\u90a3\u4e24\u679a\u6697\u5668\u957f\u7ea6\u4e8c\u5bf8\u516b\u5206\uff0c\u6253\u6210\u6602\u9996\u5410\u820c\u7684\u86c7\u5f62\uff0c\u86c7\u820c\u5c16\u7aef\u5206\u6210\u53cc\u53c9\uff0c\u6bcf\u4e00\u53c9\u90fd\u662f\u4e00\u4e2a\u5012\u523a\u3002\u86c7\u8eab\u9edd\u9ed1\uff0c\u79ef\u6ee1\u4e86\u9752\u82d4\u79fd\u571f\u3002\u6728\u6851\u62ff\u8d77\u6765\u7ec6\u7ec6\u5bdf\u770b\uff0c\u7528\u5c0f\u5200\u6311\u53bb\u86c7\u8eab\u5404\u5904\u6c61\u6ce5\uff0c\u90a3\u86c7\u5f62\u9525\u6e10\u6e10\u707f\u70c2\u751f\u5149\uff0c\u7adf\u7136\u662f\u9ec4\u91d1\u6240\u94f8\u3002\u6728\u6851\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4e00\u4ef6\u5c0f\u6697\u5668\u6709\u8fd9\u4e48\u91cd\uff0c\u539f\u6765\u662f\u91d1\u5b50\u6253\u7684\u3002\u4f7f\u8fd9\u6697\u5668\u7684\u4eba\u597d\u9614\u6c14\uff0c\u4e00\u51fa\u624b\u5c31\u662f\u4e00\u4e24\u591a\u91d1\u5b50\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u7a81\u7136\u4e00\u51db\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u91d1\u86c7\u90ce\u541b\u7684\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\uff1f\u4f60\u8bf4\u662f\u590f\u96ea\u5b9c\uff1f\u542c\u8bf4\u6b64\u4eba\u5df2\u6b7b\u4e86\u5341\u591a\u5e74\u5566\uff01\u201d\u521a\u8bf4\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u4ed6\u3002\u201d\u5c0f\u5200\u6311\u522e\u4e0b\uff0c\u86c7\u9525\u7684\u86c7\u8179\u4e0a\u73b0\u51fa\u4e00\u4e2a\u201c\u96ea\u201d\u5b57\u3002\u53e6\u4e00\u679a\u86c7\u9525\u4e0a\u4e5f\u523b\u7740\u8fd9\u5b57\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u91d1\u86c7\u90ce\u541b\u662f\u8c01\uff1f\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u8fd9\u4e8b\u5f85\u4f1a\u518d\u8bf4\u3002\u9053\u5144\uff0c\u4f60\u8bf4\u4ed6\u7684\u6697\u5668\u600e\u4f1a\u85cf\u5728\u8fd9\u6d1e\u91cc\uff1f\u201d\u6728\u6851\u6c89\u601d\u4e0d\u8bed\uff0c\u5446\u5446\u51fa\u795e\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5e08\u7236\u548c\u6728\u6851\u5e08\u4f2f\u795e\u8272\u90d1\u91cd\uff0c\u4fbf\u4e5f\u4e0d\u6562\u591a\u95ee\u3002\u665a\u996d\u8fc7\u540e\uff0c\u7a46\u4eba\u6e05\u4e0e\u6728\u6851\u526a\u70db\u5bf9\u8c08\uff0c\u8bf4\u4e86\u8bb8\u591a\u8bdd\uff0c\u8881\u627f\u5fd7\u90fd\u4e0d\u5927\u61c2\uff0c\u542c\u4ed6\u4eec\u8bf4\u7684\u90fd\u662f\u4ec7\u6740\u3001\u62a5\u590d\u7b49\u4e8b\u3002 \u3000\u3000\u6728\u6851\u5ffd\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8bf4\u91d1\u86c7\u90ce\u541b\u662f\u4e3a\u907f\u4ec7\u800c\u5230\u8fd9\u91cc\uff1f\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4ee5\u4ed6\u7684\u6b66\u529f\u673a\u667a\uff0c\u4f3c\u4e0d\u5fc5\u8fdc\u4ece\u6c5f\u5357\u9003\u5230\u6b64\u5904\uff0c\u8eb2\u5728\u8fd9\u8352\u5c71\u4e4b\u4e2d\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4eba\u8fd8\u6ca1\u6b7b\uff1f\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u6b64\u4eba\u884c\u4e8b\u5411\u6765\u795e\u51fa\u9b3c\u6ca1\uff0c\u54b1\u4eec\u5728\u6c5f\u6e56\u4e2d\u8fd9\u4e9b\u5e74\uff0c\u53ea\u542c\u5230\u4ed6\u7684\u540d\u5934\uff0c\u679c\u7136\u8bf4\u5f97\u4e0a\u662f\u5a01\u540d\u8fdc\u9707\uff0c\u5374\u4ece\u6765\u6ca1\u89c1\u8fc7\u4ed6\u9762\u3002\u542c\u4eba\u8bf4\u4ed6\u5df2\u6b7b\u4e86\uff0c\u53ef\u662f\u8c01\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u6b7b\u7684\u3002\u201d\u6728\u6851\u53f9\u9053\uff1a\u201c\u8fd9\u4eba\u884c\u4e8b\u4e5f\u771f\u53e4\u602a\uff0c\u6709\u65f6\u7a77\u51f6\u6781\u6076\uff0c\u6709\u65f6\u5374\u53c8\u884c\u4fa0\u4ed7\u4e49\uff0c\u662f\u597d\u662f\u574f\uff0c\u6559\u4eba\u6349\u6478\u4e0d\u5b9a\u3002\u6211\u51e0\u6b21\u60f3\u8981\u627e\u4ed6\uff0c\u90fd\u6ca1\u80fd\u627e\u5230\u3002\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u54b1\u4eec\u522b\u778e\u731c\u5566\uff0c\u660e\u513f\u5230\u5c71\u6d1e\u53bb\u7762\u77a7\u3002\u201d \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u7a46\u4eba\u6e05\u3001\u6728\u6851\u3001\u8881\u627f\u5fd7\u3001\u54d1\u5df4\u56db\u4eba\u5e26\u4e86\u7ef3\u7d22\u5175\u5203\uff0c\u722c\u4e0a\u5ced\u58c1\u4e4b\u9876\u3002\u6728\u6851\u9053\uff1a\u201c\u6211\u4e0b\u53bb\u3002\u201d\u7a46\u4eba\u6e05\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5fc3\u4e86\u3002\u201d\u5c06\u7ef3\u7d22\u7f1a\u5728\u4ed6\u8170\u91cc\uff0c\u4e0e\u54d1\u5df4\u4e24\u4eba\u7d27\u7d27\u62c9\u4f4f\uff0c\u6162\u6162\u5c06\u4ed6\u7f12\u4e0b\u53bb\u3002 \u3000\u3000\u6728\u6851\u4e00\u624b\u6301\u7740\u7cbe\u94a2\u68cb\u76d8\uff0c\u4e00\u624b\u6263\u4e86\u4e09\u679a\u68cb\u5b50\uff0c\u6e9c\u5230\u6d1e\u53e3\uff0c\u5411\u4e0b\u4e00\u671b\u53ea\u89c1\u811a\u4e0b\u96fe\u6c14\u4e00\u56e2\u56e2\u7684\u968f\u98ce\u98d8\u8fc7\uff0c\u5374\u4e0d\u770b\u89c1\u5730\uff0c\u867d\u7136\u4ed6\u8f7b\u529f\u5353\u7edd\uff0c\u7edd\u5cf0\u9669\u5cad\uff0c\u4e8e\u4ed6\u4fbf\u5982\u5e73\u5730\uff0c\u8fd9\u65f6\u5374\u4e5f\u4e0d\u7531\u5f97\u5fc3\u60ca\uff0c\u8f6c\u5934\u5411\u6d1e\u91cc\u5f20\u671b\uff0c\u9ed1\u6c89\u6c89\u7684\u770b\u4e0d\u6e05\u695a\uff0c\u53ea\u89c9\u5f97\u6d1e\u7a74\u5f88\u6df1\u3002\u6d1e\u53e3\u751a\u5c0f\uff0c\u90a3\u662f\u94bb\u4e0d\u8fdb\u53bb\u7684\uff0c\u4e8e\u662f\u7528\u5e03\u5305\u4f4f\u4e86\u624b\uff0c\u8f7b\u8f7b\u5230\u6d1e\u91cc\u4e00\u63a2\uff0c\u78b0\u5230\u51e0\u679a\u5c16\u5229\u4e4b\u7269\uff0c\u63d2\u5728\u6d1e\u53e3\uff0c\u4e00\u6478\u4e4b\u4e0b\u5c31\u77e5\u662f\u91d1\u86c7\u9525\uff0c\u8f7b\u8f7b\u62d4\u4e86\u51fa\u6765\uff0c\u4e00\u5171\u62d4\u4e86\u5341\u56db\u679a\uff0c\u5c31\u6ca1\u6709\u4e86\u3002\u518d\u4f38\u624b\u8fdb\u53bb\uff0c\u76f4\u5230\u9762\u988a\u62b5\u4f4f\u6d1e\u53e3\uff0c\u4e5f\u518d\u6478\u4e0d\u5230\u751a\u4e48\uff0c\u7eb5\u58f0\u53eb\u9053\uff1a\u201c\u62c9\u6211\u4e0a\u6765\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u7f13\u7f13\u6536\u7d22\uff0c\u62c9\u4e86\u4e0a\u6765\uff0c\u62c9\u5230\u79bb\u5d16\u9876\u4e8c\u4e08\u591a\u65f6\uff0c\u6728\u6851\u53f3\u811a\u5728\u5ced\u58c1\u4e0a\u4e00\u70b9\uff0c\u7a9c\u4e86\u4e0a\u6765\uff0c\u68cb\u76d8\u4e2d\u6258\u4e86\u4e00\u5927\u628a\u91d1\u86c7\u9525\uff0c\u7b11\u9053\uff1a\u201c\u8001\u7a46\uff0c\u54b1\u54e5\u513f\u4eec\u53d1\u8d22\u5566\uff0c\u8fd9\u4e48\u591a\u91d1\u5b50\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u8138\u8272\u5374\u751a\u662f\u6c89\u91cd\u3002\u53cc\u7709\u5fae\u8e59\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u602a\u4eba\u5c06\u8fd9\u4e9b\u4e1c\u897f\u653e\u5728\u8fd9\u91cc\uff0c\u4e0d\u77e5\u662f\u751a\u4e48\u610f\u601d\u3002\u6d1e\u91cc\u8fd8\u6709\u751a\u4e48\uff1f\u5f85\u6211\u4e0b\u53bb\u77a7\u77a7\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u4f60\u4e0b\u53bb\u4e5f\u662f\u767d\u9976\uff0c\u6d1e\u53e3\u592a\u5c0f\uff0c\u94bb\u4e0d\u8fdb\u53bb\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u6ee1\u8179\u5fc3\u4e8b\uff0c\u4f4e\u5934\u4e0d\u8bed\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5ffd\u9053\uff1a\u201c\u5e08\u4f2f\uff0c\u6211\u6210\u5417\uff1f\u201d\u6728\u6851\u559c\u9053\uff1a\u201c\u4f60\u4e5f\u8bb8\u6210\uff0c\u4f46\u8fd9\u6837\u9ad8\uff0c\u4f60\u6562\u4e0b\u53bb\u5417\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u6562\uff0c\u5e08\u7236\uff0c\u6211\u4e0b\u53bb\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u7a46\u4eba\u6e05\u5bfb\u601d\uff1a\u201c\u8fd9\u4e2a\u6c5f\u6e56\u5f02\u4eba\u628a\u4ed6\u7684\u9632\u8eab\u81f3\u5b9d\u653e\u5728\u6b64\u5730\uff0c\u5fc5\u6709\u7528\u610f\uff0c\u4fbf\u5728\u6211\u5c45\u5904\u4e4b\u4fa7\uff0c\u5c82\u53ef\u4e0d\u63a2\u67e5\u660e\u767d\uff1f\u4f46\u53ea\u6015\u6d1e\u5185\u6709\u9669\uff0c\u8ba9\u8fd9\u5b69\u5b50\u5b64\u8eab\u72af\u96be\uff0c\u5012\u4ee4\u4eba\u62c5\u5fc3\u3002\u201d\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u6d1e\u91cc\u6709\u5371\u9669\u5462\u3002\u201d\u8881\u627f\u5fd7\u5fd9\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u5c0f\u5fc3\u7740\u5c31\u662f\u5566\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u89c1\u4ed6\u795e\u8272\u5174\u594b\uff0c\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u5c31\u70b9\u5934\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u70b9\u4e00\u4e2a\u706b\u628a\uff0c\u4f38\u8fdb\u6d1e\u53bb\uff0c\u5018\u82e5\u706b\u7184\uff0c\u5343\u4e07\u4e0d\u53ef\u8fdb\u53bb\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b54\u5e94\u4e86\uff0c\u53f3\u624b\u6267\u5251\uff0c\u5de6\u624b\u62ff\u7740\u706b\u628a\uff0c\u7f12\u7ef3\u4e0b\u53bb\u3002\u4ed6\u9075\u7167\u5e08\u7236\u7684\u5429\u5490\uff0c\u7528\u706b\u628a\u5148\u63a2\u8fdb\u6d1e\u91cc\u3002\u5c0f\u4e56\u5f04\u7834\u6d1e\u5916\u6ce5\u5c01\uff0c\u5c71\u9876\u98ce\u52b2\uff0c\u5439\u4e86\u4e00\u665a\uff0c\u5df2\u628a\u6d1e\u4e2d\u79fd\u6c14\u5439\u5c3d\uff0c\u706b\u628a\u5e76\u4e0d\u7184\u706d\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6162\u6162\u722c\u4e86\u8fdb\u53bb\uff0c\u89c1\u662f\u4e00\u6761\u72ed\u7a84\u7684\u5929\u751f\u752c\u9053\uff0c\u5176\u5b9e\u662f\u5c71\u8179\u5185\u7684\u4e00\u6761\u88c2\u7f1d\uff0c\u722c\u4e86\u5341\u591a\u4e08\u8fdc\uff0c\u752c\u9053\u6e10\u9ad8\uff0c\u518d\u524d\u8fdb\u4e08\u4f59\uff0c\u5df2\u53ef\u7ad9\u76f4\u3002\u4ed6\u633a\u4e00\u633a\u8170\uff0c\u5411\u524d\u8d70\u53bb\uff0c\u752c\u9053\u5ffd\u7136\u8f6c\u5f2f\uff0c\u4ed6\u4e0d\u6562\u5927\u610f\u3002\u53f3\u624b\u957f\u5251\u5f53\u80f8\uff0c\u8d70\u4e86\u4e24\u4e09\u4e08\u8fdc\uff0c\u524d\u9762\u8c41\u7136\u7a7a\u9614\uff0c\u51fa\u73b0\u4e00\u4e2a\u6d1e\u7a74\uff0c\u4fbf\u5982\u662f\u5ea7\u77f3\u5ba4\u3002 \u3000\u3000\u4e3e\u8d77\u706b\u628a\u4e00\u7167\uff0c\u767b\u65f6\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u5bf9\u9762\u77f3\u58c1\u4e0a\u659c\u501a\u7740\u4e00\u526f\u9ab7\u9ac5\u9aa8\uff0c\u8eab\u4e0a\u8863\u670d\u5df2\u70c2\u4e86\u4e03\u516b\u6210\uff0c\u90a3\u9ab7\u9ac5\u9aa8\u5b9b\u7136\u5c1a\u53ef\u89c1\u5230\u662f\u4e2a\u4eba\u5f62\u3002 \u3000\u3000\u4ed6\u89c1\u5230\u8fd9\u526f\u60c5\u5f62\uff0c\u4e00\u9897\u5fc3\u5623\u5623\u4e71\u8df3\uff0c\u89c1\u77f3\u5ba4\u4e2d\u522b\u65e0\u5176\u4ed6\u53ef\u6016\u4e8b\u7269\uff0c\u4e8e\u662f\u4e3e\u706b\u628a\u4ed4\u7ec6\u7167\u770b\u3002\u9ab7\u9ac5\u524d\u9762\u6a2a\u4e03\u7ad6\u516b\u7684\u653e\u7740\u5341\u51e0\u628a\u91d1\u86c7\u9525\uff0c\u77f3\u58c1\u4e0a\u6709\u51e0\u767e\u5e45\u7528\u5229\u5668\u523b\u6210\u7684\u7b80\u964b\u4eba\u5f62\uff0c\u6bcf\u4e2a\u4eba\u5f62\u5747\u4e0d\u76f8\u540c\uff0c\u4e3e\u624b\u8e22\u8db3\uff0c\u4f3c\u5728\u7ec3\u6b66\u3002\u4ed6\u6328\u6b21\u770b\u53bb\uff0c\u5bc6\u5bc6\u5c42\u5c42\u7684\u90fd\u662f\u56fe\u5f62\uff0c\u5fc3\u4e0b\u4e0d\u89e3\uff0c\u4e0d\u77e5\u523b\u5728\u8fd9\u91cc\u6709\u751a\u4e48\u7528\u610f\u3002 \u3000\u3000\u56fe\u5f62\u5c3d\u5904\uff0c\u77f3\u58c1\u4e0a\u51fa\u73b0\u4e86\u51e0\u884c\u5b57\uff0c\u4e5f\u662f\u4ee5\u5229\u5668\u6240\u523b\uff0c\u51d1\u8fc7\u53bb\u4e00\u770b\uff0c\u89c1\u523b\u7684\u662f\u5341\u516d\u4e2a\u5b57\uff1a\u201c\u91cd\u5b9d\u79d8\u672f\uff0c\u4ed8\u4e0e\u6709\u7f18\uff0c\u5165\u6211\u95e8\u6765\uff0c\u9047\u7978\u83ab\u6028\u3002\u201d\u8fd9\u5341\u516d\u5b57\u4e4b\u65c1\uff0c\u6709\u4e2a\u5251\u67c4\u51f8\u51fa\u5728\u77f3\u58c1\u4e4b\u4e0a\uff0c\u4f3c\u662f\u4e00\u628a\u5251\u63d2\u5165\u4e86\u77f3\u58c1\uff0c\u76f4\u81f3\u5251\u67c4\u3002 \u3000\u3000\u4ed6\u597d\u5947\u5fc3\u8d77\uff0c\u63e1\u4f4f\u5251\u67c4\u5411\u5916\u4e00\u62d4\uff0c\u5374\u662f\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u7adf\u4f3c\u94f8\u5728\u77f3\u91cc\u4e00\u822c\u3002 \u3000\u3000\u6b63\u60f3\u518d\u770b\uff0c\u542c\u5f97\u6d1e\u53e3\u9690\u9690\u4f3c\u6709\u547c\u5524\u4e4b\u58f0\uff0c\u5fd9\u5954\u51fa\u53bb\uff0c\u8f6c\u4e86\u5f2f\u8d70\u5230\u752c\u9053\u53e3\uff0c\u542c\u5f97\u6728\u6851\u5728\u53eb\u81ea\u5df1\u540d\u5b57\uff0c\u5fd9\u9ad8\u58f0\u7b54\u5e94\uff0c\u722c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u539f\u6765\u6728\u6851\u548c\u7a46\u4eba\u6e05\u5728\u5c71\u9876\u89c1\u7ef3\u5b50\u8d8a\u626f\u8d8a\u957f\uff0c\u7b49\u4e86\u5f88\u4e45\u4e0d\u89c1\u51fa\u6765\uff0c\u5fc3\u4e2d\u7126\u6025\uff0c\u6728\u6851\u4e5f\u7f12\u4e0b\u53bb\u5bdf\u770b\u3002\u4ed6\u722c\u4e0d\u8fdb\u53bb\uff0c\u53ea\u5f97\u5728\u6d1e\u53e3\u53eb\u558a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u722c\u4e86\u51fa\u6765\uff0c\u5bf9\u6728\u6851\u9053\uff1a\u201c\u6d1e\u91cc\u6709\u8bb8\u591a\u53e4\u602a\u4e1c\u897f\u3002\u201d\u626f\u52a8\u7ef3\u5b50\uff0c\u4e0a\u9762\u7a46\u4eba\u6e05\u548c\u54d1\u5df4\u5fd9\u628a\u4e24\u4eba\u62c9\u4e0a\u53bb\u3002\u8881\u627f\u5fd7\u5b9a\u4e86\u5b9a\u795e\uff0c\u624d\u5c06\u6d1e\u4e2d\u7684\u60c5\u5f62\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u90a3\u9ab7\u9ac5\u5b9a\u662f\u91d1\u86c7\u90ce\u541b\u590f\u96ea\u5b9c\u4e86\u3002\u60f3\u4e0d\u5230\u4e00\u4ee3\u602a\u6770\uff0c\u6bd5\u547d\u4e8e\u6b64\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u4ed6\u7559\u7684\u8fd9\u5341\u516d\u5b57\u662f\u751a\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u7a46\u4eba\u6e05\u6c89\u541f\u9053\uff1a\u201c\u770b\u6837\u5b50\u4f3c\u4e4e\u4ed6\u5728\u6d1e\u4e2d\u57cb\u85cf\u4e86\u751a\u4e48\u5b9d\u7269\u3002\u77f3\u58c1\u4e0a\u6240\u523b\u56fe\u5f62\uff0c\u5f53\u662f\u4ed6\u7684\u6b66\u529f\u4e86\u3002\u8fd9\u5341\u516d\u5b57\u7559\u8a00\u9887\u4e3a\u8be1\u5947\uff0c\u4f3c\u8bf4\u8c01\u5f97\u5230\u4ed6\u7684\u9057\u8d60\uff0c\u5c31\u5f97\u7b97\u4ed6\u95e8\u4eba\uff0c\u800c\u4e14\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u7978\u60a3\u3002\u201d \u3000\u3000\u6728\u6851\u9053\uff1a\u201c\u6309\u5b57\u4e49\u63a8\u8be6\uff0c\u8be5\u5f53\u5982\u6b64\uff0c\u53ea\u4e0d\u77e5\u8fd9\u602a\u4eba\u8fd8\u6709\u751a\u4e48\u5947\u7279\u82b1\u6837\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u4e0d\u8d2a\u56fe\u4ed6\u7684\u751a\u4e48\u91cd\u5b9d\u79d8\u672f\u3002 \u3000\u3000\u627f\u5fd7\uff0c\u660e\u513f\u4f60\u518d\u8fdb\u53bb\uff0c\u628a\u8fd9\u4f4d\u524d\u8f88\u7684\u9057\u9aa8\u846c\u4e86\uff0c\u70b9\u4e86\u9999\u70db\u5728\u4ed6\u7075\u524d\u53e9\u62dc\u4e00\u756a\uff0c\u4e5f\u5bf9\u5f97\u8d77\u4ed6\u4e86\u3002\u201d\u8881\u627f\u5fd7\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u6b21\u65e5\u6e05\u6668\uff0c\u8881\u627f\u5fd7\u62ff\u4e86\u4e00\u628a\u9504\u5934\uff0c\u548c\u54d1\u5df4\u4e24\u4eba\u722c\u4e0a\u4e86\u5ced\u58c1\u3002\u8fd9\u6b21\u7a46\u4eba\u6e05\u548c\u6728\u6851\u77e5\u9053\u6d1e\u91cc\u6ca1\u6709\u5371\u9669\uff0c\u6ca1\u518d\u548c\u4ed6\u4eec\u540c\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\u57cb\u846c\u9ab8\u9aa8\uff0c\u8d39\u65f6\u4e0d\u5c11\uff0c\u7279\u5730\u5e26\u4e86\u4e09\u4e2a\u706b\u628a\uff0c\u722c\u8fdb\u6d1e\u540e\uff0c\u7528\u9504\u5934\u5728\u5730\u4e0b\u6316\u4e86\u4e2a\u5c0f\u6d1e\uff0c\u63d2\u5165\u706b\u628a\uff0c\u7528\u6ce5\u571f\u62a4\u4f4f\uff0c\u8f6c\u8eab\u77a7\u90a3\u9ab7\u9ac5\u3002 \u3000\u3000\u5fc3\u60f3\uff1a\u542c\u5e08\u7236\u8bf4\uff0c\u8fd9\u4eba\u751f\u524d\u662f\u4e00\u4f4d\u602a\u4fa0\uff0c\u4e0d\u77e5\u4f55\u4ee5\u843d\u5f97\u547d\u4e27\u8352\u5c71\uff0c\u6b7b\u5728\u8fd9\u9690\u79d8\u7684\u6d1e\u7a74\u4e4b\u4e2d\uff0c\u9ab8\u9aa8\u65e0\u4eba\u6b93\u57cb\uff0c\u5fc3\u4e0b\u607b\u7136\uff0c\u5728\u9ab7\u9ac5\u9762\u524d\u8dea\u4e0b\uff0c\u53e9\u4e86\u51e0\u4e2a\u5934\uff0c\u6697\u6697\u795d\u544a\uff1a\u201c\u5f1f\u5b50\u8881\u627f\u5fd7\u65e0\u610f\u4e2d\u5f97\u89c1\u9057\u4f53\uff0c\u4eca\u65e5\u7ed9\u524d\u8f88\u843d\u846c\uff0c\u4f60\u5728\u5730\u4e0b\u957f\u7720\u5b89\u606f\u5427\uff01\u201d \u3000\u3000\u7977\u795d\u65b9\u7f62\uff0c\u4e00\u9635\u51b7\u98ce\u98d5\u98d5\u7684\u522e\u8fdb\u6d1e\u6765\uff0c\u53ea\u89c9\u5bd2\u6c14\u903c\u4eba\uff0c\u4e0d\u7981\u6bdb\u9aa8\u609a\u7136\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u5728\u6d1e\u4e2d\u591a\u803d\uff0c\u4fbf\u7528\u9504\u5934\u5728\u5730\u4e0b\u6316\u6398\uff0c\u5fc3\u60f3\u5730\u4e0b\u90fd\u662f\u575a\u786c\u7684\u5ca9\u77f3\uff0c\u5018\u82e5\u6316\u4e0d\u4e0b\u53bb\uff0c\u53ea\u6709\u628a\u767d\u9aa8\u6361\u5230\u6d1e\u5916\u53bb\u57cb\u846c\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u4e00\u9504\u4e0b\u53bb\uff0c\u5730\u9762\u5e94\u9504\u800c\u5f00\uff0c\u7adf\u7136\u751a\u662f\u677e\u8f6f\uff0c\u5fd9\u52a0\u52b2\u6316\u6398\uff0c\u6316\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u53ee\u7684\u4e00\u58f0\uff0c\u9504\u5934\u78b0\u5230\u4e00\u4ef6\u94c1\u5668\u3002\u79fb\u8fd1\u706b\u628a\u4e00\u770b\uff0c\u89c1\u5e95\u4e0b\u6709\u5757\u94c1\u677f\uff0c\u518d\u7528\u9504\u5934\u6316\u4e86\u51e0\u4e0b\uff0c\u62e8\u5f00\u65c1\u8fb9\u6ce5\u571f\uff0c\u539f\u6765\u7adf\u662f\u4e00\u53ea\u4e24\u5c3a\u89c1\u65b9\u7684\u5927\u94c1\u76d2\u3002 \u3000\u3000\u4ed6\u628a\u94c1\u76d2\u6367\u4e86\u51fa\u6765\uff0c\u89c1\u90a3\u76d2\u5b50\u9ad8\u7ea6\u4e00\u5c3a\uff0c\u7136\u800c\u5165\u624b\u8f7b\u98d8\u98d8\u5730\uff0c\u4f3c\u4e4e\u76d2\u91cc\u5e76\u6ca1\u85cf\u7740\u751a\u4e48\u4e1c\u897f\u3002\u6253\u5f00\u76d2\u76d6\uff0c\u90a3\u76d2\u5b50\u7adf\u6d45\u5f97\u51fa\u5947\uff0c\u79bb\u5e95\u4ec5\u53ea\u4e00\u5bf8\uff0c\u4ed6\u5fc3\u4e0b\u5947\u602a\uff0c\u4e00\u53ea\u5c3a\u6765\u9ad8\u7684\u76d2\u5b50\uff0c\u600e\u5730\u76d2\u91cc\u5374\u8fd9\u822c\u6d45\uff1f\u6599\u5f97\u5fc5\u6709\u5939\u5c42\u3002 \u3000\u3000\u76d2\u4e2d\u6709\u4e2a\u4fe1\u5c01\uff0c\u5c01\u76ae\u4e0a\u5199\u7740\u516b\u5b57\uff1a\u201c\u5f97\u6211\u76d2\u8005\uff0c\u5f00\u542f\u6b64\u67ec\u3002\u201d \u3000\u3000\u62c6\u5f00\u4fe1\u5c01\uff0c\u91cc\u9762\u6709\u5f20\u767d\u7b3a\uff0c\u5e74\u6df1\u65e5\u4e45\uff0c\u7eb8\u7b3a\u65e9\u5df2\u53d8\u9ec4\u3002\u7b3a\u4e0a\u5199\u9053\uff1a\u201c\u76d2\u4e2d\u4e4b\u7269\uff0c\u7559\u8d60\u6709\u7f18\u3002\u60df\u5f97\u76d2\u8005\uff0c\u52a1\u987b\u5148\u846c\u6211\u9ab8\u9aa8\uff0c\u65b9\u53ef\u542f\u76d2\uff0c\u8981\u7d27\u8981\u7d27\u3002\u201d\u4fe1\u5c01\u4e2d\u53c8\u6709\u4e24\u4e2a\u5c0f\u5c01\u5957\uff0c\u4e00\u4e2a\u5c01\u5957\u4e0a\u5199\u7740\u201c\u542f\u76d2\u4e4b\u6cd5\u201d\uff0c\u4e00\u4e2a\u5c01\u5957\u4e0a\u5199\u7740\u201c\u846c\u6211\u9ab8\u9aa8\u4e4b\u6cd5\u201d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e3e\u8d77\u76d2\u5b50\u4e00\u6447\uff0c\u91cc\u9762\u679c\u7136\u6709\u7269\uff0c\u5fc3\u60f3\uff1a\u201c\u5e08\u7236\u601c\u4f60\u66b4\u9aa8\u8352\u5c71\uff0c\u624d\u547d\u6211\u7ed9\u4f60\u6536\u846c\uff0c\u53c8\u4e0d\u662f\u8d2a\u5f97\u4f60\u7684\u7269\u4e8b\u3002\u201d \u3000\u3000\u4e8e\u662f\u62c6\u5f00\u5199\u7740\u201c\u846c\u6211\u9ab8\u9aa8\u4e4b\u6cd5\u201d\u7684\u5c01\u5957\uff0c\u89c1\u91cc\u9762\u53c8\u6709\u767d\u7b3a\uff0c\u5199\u9053\uff1a\u201c\u541b\u5982\u8bda\u5fc3\u846c\u6211\u9ab8\u9aa8\uff0c\u8bf7\u5728\u5751\u4e2d\u518d\u5411\u4e0b\u6316\u6398\u4e09\u5c3a\uff0c\u7136\u540e\u57cb\u846c\uff0c\u4f7f\u6211\u6df1\u5c45\u5730\u4e0b\uff0c\u4e0d\u53d7\u866b\u8681\u4e4b\u5bb3\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u597d\u4eba\u505a\u5230\u5e95\uff0c\u7d22\u6027\u7167\u4f60\u7684\u5429\u5490\u505a\u5427\u3002\u201d\u4e8e\u662f\u53c8\u5411\u5730\u4e0b\u6316\u6398\uff0c\u8fd9\u6b21\u6ce5\u571f\u8f83\u575a\uff0c\u65f6\u65f6\u51fa\u73b0\u5c71\u77f3\uff0c\u6316\u6398\u8fdc\u4e3a\u8d39\u529b\u3002 \u3000\u3000\u4ed6\u6b64\u65f6\u6b66\u529f\u9887\u6709\u6839\u5e95\uff0c\u4f46\u4e5f\u7d2f\u51fa\u4e86\u4e00\u8eab\u5927\u6c57\uff0c\u582a\u582a\u53c8\u5c06\u6316\u4e86\u4e09\u5c3a\uff0c\u5ffd\u7136\u53ee\u7684\u4e00\u58f0\uff0c\u9504\u5934\u53c8\u78b0\u5230\u4e00\u7269\uff0c\u62e8\u5f00\u6ce5\u571f\uff0c\u679c\u7136\u53c8\u662f\u4e00\u53ea\u94c1\u76d2\uff0c\u4e0d\u8fc7\u8fd9\u53ea\u76d2\u5b50\u5c0f\u5f97\u591a\uff0c\u53ea\u4e00\u5c3a\u89c1\u65b9\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u4f4d\u602a\u4fa0\u5f53\u771f\u53e4\u602a\uff0c\u4e0d\u77e5\u8fd9\u76d2\u4e2d\u53c8\u6709\u751a\u4e48\u4e1c\u897f\u3002\u201d\u6253\u5f00\u76d2\u76d6\u770b\u65f6\uff0c\u53ea\u60ca\u5f97\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u539f\u6765\u76d2\u4e2d\u4e00\u5f20\u7b3a\u4e0a\u5199\u9053\uff1a\u201c\u541b\u662f\u5fe0\u539a\u4ec1\u8005\uff0c\u846c\u6211\u9ab8\u9aa8\uff0c\u5f53\u916c\u4ee5\u91cd\u5b9d\u79d8\u672f\u3002\u5927\u94c1\u76d2\u5f00\u542f\u65f6\u6709\u6bd2\u7bad\u5c04\u51fa\uff0c\u6108\u4e2d\u4e66\u8c31\u5730\u56fe\u5747\u5047\uff0c\u4e0a\u6709\u5267\u6bd2\uff0c\u4ee5\u60e9\u8d2a\u6b32\u6076\u5f92\u3002\u771f\u8005\u5728\u6b64\u5c0f\u94c1\u76d2\u5185\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u6562\u591a\u770b\uff0c\u5c06\u4e24\u53ea\u94c1\u76d2\u653e\u5728\u4e00\u65c1\uff0c\u628a\u91d1\u86c7\u90ce\u541b\u7684\u9ab8\u9aa8\u4f9d\u6b21\u642c\u5165\u7a74\u4e2d\uff0c\u76d6\u4e0a\u6ce5\u571f\uff0c\u70b9\u4e0a\u4e86\u9999\u70db\uff0c\u62dc\u4e86\u51e0\u62dc\uff0c\u6367\u4e86\u94c1\u76d2\uff0c\u56de\u8eab\u8d70\u51fa\u3002 \u3000\u3000\u706b\u5149\u7167\u8000\u4e0b\u89c1\u6d1e\u53e3\u662f\u7528\u77f3\u5757\u780c\u6210\uff0c\u60f3\u662f\u91d1\u86c7\u90ce\u541b\u5f53\u65e5\u8fdb\u6d1e\u4e4b\u540e\uff0c\u518d\u7528\u5ca9\u77f3\u5c01\u4f4f\u3002\u5426\u5219\u7684\u8bdd\uff0c\u4ece\u8fd9\u5177\u9ab7\u9ac5\u770b\u6765\uff0c\u4ed6\u8eab\u6750\u9ad8\u5927\uff0c\u53c8\u600e\u8fdb\u5f97\u6d1e\u6765\uff1f\u53ea\u662f\u65f6\u65e5\u5df2\u4e45\uff0c\u6d1e\u5916\u571f\u79ef\u85e4\u6500\uff0c\u53c8\u751f\u6ee1\u4e86\u9752\u82d4\uff0c\u5374\u770b\u4e0d\u51fa\u6765\uff0c\u53ea\u9053\u6d1e\u53e3\u662f\u5929\u751f\u8fd9\u4e48\u7ec6\u5c0f\u7684\u3002\u8881\u627f\u5fd7\u6316\u5f00\u77f3\u5757\uff0c\u5f00\u5927\u6d1e\u53e3\uff0c\u4ee5\u5907\u5e08\u7236\u4e0e\u6728\u6851\u9053\u4eba\u8fdb\u6765\u67e5\u770b\u3002\u51fa\u6d1e\u540e\u54d1\u5df4\u5c06\u4ed6\u62c9\u4e0a\u3002\u4ed6\u62ff\u4e86\u4e24\u53ea\u94c1\u76d2\uff0c\u53bb\u89c1\u5e08\u7236\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u4e0e\u6728\u6851\u6b63\u5728\u5f08\u68cb\uff0c\u89c1\u4ed6\u8fc7\u6765\uff0c\u4fbf\u505c\u5f08\u4e0d\u4e0b\u3002\u8881\u627f\u5fd7\u628a\u7ecf\u8fc7\u4e00\u8bf4\uff0c\u4e24\u4eba\u770b\u4e86\u51e0\u5c01\u4e66\u67ec\uff0c\u90fd\u662f\u6697\u6697\u5fc3\u60ca\uff0c\u53c8\u628a\u5927\u94c1\u76d2\u4e2d\u5199\u7740\u201c\u542f\u76d2\u4e4b\u6cd5\u201d\u7684\u5c01\u5957\u62c6\u5f00\uff0c\u91cc\u9762\u4e00\u5f20\u7eb8\u5199\u9053\uff1a\u201c\u94c1\u76d2\u5de6\u53f3\uff0c\u5404\u6709\u673a\u62ec\uff0c\u53cc\u624b\u6367\u76d2\u540c\u65f6\u529b\u6380\uff0c\u94c1\u76d2\u5373\u5f00\u3002\u201d \u3000\u3000\u6728\u6851\u5411\u7a46\u4eba\u6e05\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u9053\uff1a\u201c\u627f\u5fd7\u8fd9\u6761\u5c0f\u547d\uff0c\u4eca\u65e5\u9669\u4e9b\u9001\u5728\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u8981\u662f\u4ed6\u7a0d\u6709\u8d2a\u5fc3\uff0c\u4e0d\u5148\u57cb\u846c\u9ab8\u9aa8\u800c\u5373\u53bb\u5f00\u542f\u76d2\u5b50\uff0c\u53ea\u6015\u96be\u9003\u6bd2\u7bad\u3002\u201d \u3000\u3000\u53eb\u54d1\u5df4\u642c\u4e86\u4e00\u53ea\u5927\u6728\u6876\u6765\uff0c\u5728\u6728\u6876\u9760\u5e95\u5904\u5f00\u4e86\u4e24\u4e2a\u5b54\uff0c\u5c06\u94c1\u76d2\u626b\u5f00\u4e86\u76d6\u653e\u5728\u6876\u5185\uff0c\u518d\u7528\u6728\u677f\u76d6\u4f4f\u6876\u53e3\uff0c\u7136\u540e\u7528\u4e24\u6839\u5c0f\u68d2\u4ece\u5b54\u4e2d\u4f38\u8fdb\u6876\u5185\uff0c\u4e0e\u8881\u627f\u5fd7\u5404\u6301\u4e00\u6839\u5c0f\u68d2\uff0c\u540c\u65f6\u7528\u529b\u4e00\u62b5\uff0c\u53ea\u542c\u5f97\u5440\u7684\u4e00\u58f0\uff0c\u60f3\u662f\u94c1\u76d2\u7b2c\u4e8c\u5c42\u76d6\u5b50\u5f00\u4e86\uff0c\u63a5\u7740\u55e4\u55e4\u4e1c\u4e1c\u4e4b\u58f0\u4e0d\u7edd\uff0c\u6728\u6876\u5fae\u5fae\u6447\u6643\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u7bad\u58f0\u5df2\u6b62\uff0c\u6b63\u8981\u63ed\u677f\u770b\u65f6\uff0c\u6728\u6851\u4e00\u628a\u62c9\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\uff01\u201d\u8bdd\u58f0\u672a\u7edd\uff0c\u679c\u7136\u53c8\u662f\u55e4\u55e4\u6570\u58f0\u3002 \u3000\u3000\u9694\u4e86\u826f\u4e45\u518d\u65e0\u58f0\u606f\u3002\u6728\u6851\u63ed\u5f00\u6728\u677f\u3002\u679c\u7136\u677f\u4e0a\u6876\u5185\u9489\u4e86\u6570\u5341\u652f\u77ed\u7bad\uff0c\u6216\u659c\u98de\uff0c\u6216\u76f4\u5c04\uff0c\u65b9\u5411\u5404\u4e0d\u76f8\u540c\uff0c\u652f\u652f\u6df1\u5165\u6728\u5185\u3002\u6728\u6851\u62ff\u4e86\u4e00\u628a\u94b3\u5b50\uff0c\u8f7b\u8f7b\u62d4\u4e86\u4e0b\u6765\uff0c\u653e\u5728\u4e00\u8fb9\uff0c\u4e0d\u6562\u7528\u624b\u53bb\u78b0\uff0c\u53f9\u9053\uff1a\u201c\u8fd9\u4eba\u5b9e\u5728\u4e5f\u592a\u5de5\u5fc3\u8ba1\u4e86\uff0c\u60df\u6050\u4e00\u6b21\u5c04\u51fa\u3002\u7ed9\u4eba\u907f\u8fc7\uff0c\u5c06\u6bd2\u7bad\u5206\u4f5c\u4e24\u6b21\u5c04\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u6447\u6447\u5934\u9053\uff1a\u201c\u82e5\u662f\u597d\u5947\u5fc3\u8d77\uff0c\u5148\u53bb\u77a7\u77a7\u94c1\u76d2\u4e2d\u6709\u4f55\u7269\u4e8b\uff0c\u4e5f\u662f\u4eba\u60c5\u4e4b\u5e38\uff0c\u672a\u5fc5\u5c31\u4e0d\u846c\u4ed6\u7684\u9ab8\u9aa8\u3002\u518d\u8bf4\uff0c\u5c31\u7b97\u4e0d\u846c\u4ed6\u7684\u9ab8\u9aa8\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u5c31\u8be5\u6b7b\u4e86\u3002\u6b64\u4eba\u7528\u5fc3\u6df1\u523b\uff0c\u5b9e\u975e\u7aef\u58eb\u3002 \u3000\u3000\u627f\u5fd7\u672c\u6765\u5c0f\u5b69\u5fc3\u6027\uff0c\u8fd9\u6b21\u7adf\u5fcd\u5f97\u4f4f\u624b\uff0c\u4e0d\u5148\u5f00\u76d2\u5b50\u6765\u5f20\u4e0a\u4e00\u5f20\uff0c\u53ef\u8bf4\u5929\u5e78\u3002\u201d \u3000\u3000\u4ece\u6728\u6876\u4e2d\u53d6\u51fa\u94c1\u76d2\uff0c\u89c1\u76d2\u5b50\u7b2c\u4e8c\u5c42\u76d6\u4e0b\u94a2\u4e1d\u7ea0\u7ed3\uff0c\u90fd\u662f\u653e\u5c04\u6bd2\u7bad\u7684\u5f39\u7c27\u673a\u62ec\u3002\u6728\u6851\u94b3\u53bb\u94a2\u4e1d\uff0c\u4e0b\u9762\u662f\u4e00\u672c\u4e66\uff0c\u4e0a\u5199\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u56db\u5b57\uff0c\u7528\u94b3\u5b50\u63ed\u5f00\u6570\u9875\uff0c\u89c1\u5199\u6ee1\u5bc6\u5bc6\u5c0f\u5b57\uff0c\u53c8\u6709\u8bb8\u591a\u56fe\u753b\u3002\u6709\u7684\u662f\u5730\u56fe\uff0c\u6709\u7684\u662f\u6b66\u672f\u59ff\u52bf\uff0c\u66f4\u6709\u4e9b\u5175\u5203\u673a\u5173\u7684\u56fe\u6837\u3002 \u3000\u3000\u518d\u6253\u5f00\u5c0f\u94c1\u76d2\u65f6\uff0c\u91cc\u9762\u4e5f\u6709\u4e00\u672c\u4e66\uff0c\u5f62\u72b6\u5927\u5c0f\uff0c\u5b57\u4f53\u88c5\u8ba2\uff0c\u65e0\u4e0d\u76f8\u540c\uff0c\u7565\u52a0\u5bf9\u7167\uff0c\u4fbf\u89c1\u4e24\u4e66\u5185\u5bb9\u5374\u662f\u5927\u5f02\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u6b64\u4eba\u4e3a\u4e86\u5bf9\u4ed8\u4e0d\u80af\u846c\u4ed6\u9ab8\u9aa8\u4e4b\u4eba\uff0c\u4e0d\u60dc\u82b1\u8d39\u8bfa\u5927\u529f\u592b\uff0c\u9020\u8fd9\u6837\u4e00\u672c\u4f2a\u4e66\uff0c\u5b89\u7f6e\u8fd9\u8bb8\u591a\u6bd2\u7bad\u3002\u5176\u5b9e\u4eba\u90fd\u6b7b\u4e86\uff0c\u522b\u4eba\u5bf9\u4f60\u662f\u597d\u662f\u574f\uff0c\u53c8\u4f55\u82e6\u5982\u6b64\u65a4\u65a4\u8ba1\u8f83\uff1f\u201d\u6728\u6851\u9053\uff1a\u201c\u8fd9\u4eba\u5c31\u662f\u56e0\u4e3a\u60f3\u4e0d\u5f00\uff0c\u624d\u843d\u5f97\u5982\u6b64\u4e0b\u573a\u3002\u4e0d\u8fc7\u8fd9\u4f2a\u4e66\u4e0e\u94c1\u76d2\uff0c\u5374\u591a\u534a\u662f\u65e9\u5c31\u9020\u597d\u4e86\uff0c\u8981\u7528\u6765\u5bf9\u4ed8\u654c\u4eba\u7684\u3002\u4e34\u6b7b\u4e4b\u65f6\uff0c\u6599\u6765\u4e5f\u65e0\u6687\u518d\u5e72\u8fd9\u4e9b\u5bb3\u4eba\u52fe\u5f53\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u70b9\u5934\u53f9\u606f\uff0c\u547d\u8881\u627f\u5fd7\u628a\u4e24\u53ea\u94c1\u76d2\u6536\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u884c\u4e3a\u4e56\u50fb\uff0c\u4ed6\u7684\u4e66\u89c2\u4e4b\u65e0\u76ca\u3002\u90a3\u672c\u4f2a\u4e66\u4e0a\u66f4\u6709\u5267\u6bd2\uff0c\u78b0\u4e5f\u78b0\u4e0d\u5f97\u3002\u201d\u8881\u627f\u5fd7\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u6b64\u540e\u7ec3\u6b66\u5f08\u68cb\uff0c\u5ffd\u5ffd\u6570\u5e74\uff0c\u6728\u6851\u5df2\u628a\u8f7b\u529f\u548c\u6697\u5668\u7684\u8981\u8bc0\u503e\u56ca\u4ee5\u6388\u3002 \u3000\u3000\u8881\u627f\u5fd7\u68cb\u827a\u65e5\u8fdb\uff0c\u6728\u6851\u548c\u4ed6\u4e0b\u68cb\uff0c\u53cd\u8981\u9976\u4e0a\u4e8c\u5b50\uff0c\u800c\u8881\u627f\u5fd7\u6545\u610f\u76f8\u8ba9\u4e4b\u8ff9\uff0c\u8d8a\u6765\u8d8a\u96be\u906e\u63a9\u3002\u6728\u6851\u5174\u5473\u7d22\u7136\uff0c\u81ea\u89c9\u8fd9\u201c\u5343\u53d8\u4e07\u52ab\u68cb\u56fd\u624b\u201d\u7684\u4e03\u5b57\u5916\u53f7\uff0c\u65e9\u5df2\u5c45\u4e4b\u6709\u6127\uff0c\u660e\u660e\u89c9\u5f97\u8881\u627f\u5fd7\u7684\u68cb\u827a\u4e5f\u662f\u5e73\u5e73\uff0c\u53ef\u662f\u81ea\u5df1\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u504f\u504f\u4e0b\u4ed6\u4e0d\u8fc7\uff0c\u53ea\u6015\u81ea\u5df1\u7684\u68cb\u827a\u5e76\u4e0d\u5982\u4f55\u9ad8\u660e\uff0c\u4e5f\u662f\u6709\u7684\uff0c\u4f46\u8bf4\u81ea\u5df1\u68cb\u827a\u4e0d\u9ad8\uff0c\u5374\u53c8\u51b3\u65e0\u662f\u7406\u3002\u8fd9\u4e00\u65e5\u5927\u8d25\u4e4b\u4f59\uff0c\u63a8\u67b0\u800c\u8d77\uff0c\u7adf\u98d8\u7136\u4e0b\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u5d07\u796f\u5341\u516d\u5e74\uff0c\u8881\u627f\u5fd7\u4e5f\u5df2\u4e8c\u5341\u5c81\u4e86\u3002 \u3000\u3000\u8fd9\u5341\u5e74\u4e4b\u95f4\uff0c\u8881\u627f\u5fd7\u6240\u7ec3\u534e\u5c71\u672c\u95e8\u7684\u62f3\u5251\u5185\u529f\uff0c\u4e0e\u65e5\u4ff1\u6df1\uff0c\u5929\u4e0b\u4e8b\u5374\u5df2\u5343\u53d8\u4e07\u5316\uff0c\u773c\u4e0b\u66f4\u662f\u5982\u6cb8\u5982\u7fb9\uff0c\u767e\u59d3\u6b63\u906d\u9022\u65e0\u7a77\u65e0\u5c3d\u7684\u52ab\u96be\u3002 \u3000\u3000\u8fd9\u4e9b\u65f6\u65e5\u4e2d\uff0c\u8fde\u5e74\u6c34\u707e\u3001\u65f1\u707e\u3001\u8757\u707e\u76f8\u7ee7\u4e0d\u65ad\uff0c\u767e\u59d3\u9965\u5bd2\u4ea4\u8feb\uff0c\u6d41\u79bb\u904d\u9053\uff0c\u751a\u81f3\u4ee5\u4eba\u4e3a\u98df\u3002\u671d\u5ef7\u5374\u53cd\u800c\u52a0\u7d27\u641c\u62ec\uff0c\u589e\u6536\u7530\u8d4b\u3001\u52a0\u6d3e\u8fbd\u9977\u3001\u7ec3\u9977\uff0c\u540d\u76ee\u4e0d\u4e00\u800c\u8db3\uff0c\u79e6\u664b\u8c6b\u695a\u5404\u5730\uff0c\u7fa4\u96c4\u8702\u8d77\u3002\u5d07\u796f\u516b\u5e74\u6b63\u6708\uff0c\u9020\u53cd\u6c11\u519b\u5341\u4e09\u5bb6\u4e03\u5341\u4e8c\u8425\u5927\u4f1a\u6cb3\u5357\u8365\u9633\uff0c\u674e\u81ea\u6210\u58f0\u52bf\u5927\u632f\uff0c\u6b21\u5e74\u5373\u79f0\u201c\u95ef\u738b\u201d\uff0c\u653b\u57ce\u63a0\u5730\uff0c\u8fde\u8d25\u5b98\u519b\u3002 \u3000\u3000\u5176\u95f4\u7a46\u4eba\u6e05\u4ecd\u65f6\u65f6\u4e0b\u5c71\uff0c\u56de\u5c71\u540e\u4e5f\u548c\u8881\u627f\u5fd7\u8bf4\u8d77\u6c11\u751f\u75be\u82e6\uff0c\u52c9\u4ed6\u827a\u6210\u4e4b\u540e\uff0c\u52a1\u5f53\u5c3d\u4e00\u5df1\u4e4b\u529b\uff0c\u6276\u96be\u89e3\u56f0\uff0c\u53c8\u8bf4\u6240\u4ee5\u8981\u52e4\u7ec3\u6b66\u529f\uff0c\u4e3b\u65e8\u6b63\u662f\u5728\u6b64\u3002\u8881\u627f\u5fd7\u6bcf\u6b21\u5747\u8083\u7136\u5949\u547d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u517c\u4fee\u4e24\u6d3e\u4e0a\u4e58\u6b66\u529f\uff0c\u5df2\u662f\u6b66\u6797\u4e2d\u7f55\u6709\u7684\u4eba\u7269\u3002\u4e0d\u8fc7\u5341\u5e74\u6765\u4ed6\u4e00\u6b65\u6ca1\u6709\u4e0b\u5c71\uff0c\u6c5f\u6e56\u4e0a\u81ea\u4e0d\u77e5\u534e\u5c71\u6d3e\u5df2\u51fa\u4e86\u8fd9\u6837\u4e00\u4f4d\u5c11\u5e74\u9ad8\u624b\u3002 \u3000\u3000\u8fd9\u5929\u6b63\u662f\u521d\u6625\uff0c\u8881\u627f\u5fd7\u6b63\u5728\u7ec3\u6b66\uff0c\u54d1\u5df4\u4ece\u5c4b\u5185\u51fa\u6765\uff0c\u5411\u4ed6\u505a\u505a\u624b\u52bf\u3002\u8881\u627f\u5fd7\u77e5\u662f\u5e08\u7236\u53ec\u5524\uff0c\u8d70\u8fdb\u5c4b\u5185\uff0c\u89c1\u5e08\u7236\u8eab\u65c1\u7ad9\u7740\u4e24\u540d\u5927\u6c49\u3002\u8fd9\u534e\u5c71\u7edd\u9876\u4e4b\u4e0a\u9664\u6728\u6851\u4e4b\u5916\uff0c\u4ece\u6ca1\u6765\u8fc7\u5916\u5ba2\uff0c\u4ed6\u89c1\u4e86\u4e24\u4eba\uff0c\u5f88\u611f\u8be7\u5f02\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u738b\u5927\u54e5\uff0c\u8fd9\u4f4d\u662f\u9ad8\u5927\u54e5\uff0c\u4f60\u8fc7\u6765\u89c1\u89c1\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u662f\u5e08\u7236\u670b\u53cb\uff0c\u8fc7\u53bb\u62dc\u5012\uff0c\u53e3\u79f0\uff1a\u201c\u738b\u5e08\u53d4\uff0c\u9ad8\u5e08\u53d4\u3002\u201d \u3000\u3000\u90a3\u4e24\u4eba\u5fd9\u5373\u8dea\u4e0b\uff0c\u8fde\u79f0\uff1a\u201c\u4e0d\u6562\uff0c\u8881\u5e08\u53d4\u8bf7\u8d77\u3002\u201d\u8881\u627f\u5fd7\u542c\u4ed6\u4eec\u53cd\u53eb\u81ea\u5df1\u5e08\u53d4\uff0c\u751a\u662f\u5947\u602a\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u5475\u5475\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u8d77\u6765\u3002\u201d\u8881\u627f\u5fd7\u7ad9\u8d77\u8eab\u6765\uff0c\u89c1\u4e24\u4eba\u90fd\u662f\u5e84\u7a3c\u4eba\u6253\u626e\uff0c\u795e\u60c5\u5374\u662f\u82f1\u6b66\u77eb\u633a\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u5bf9\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u4ece\u6765\u6ca1\u8ddf\u6211\u4e0b\u5c71\uff0c\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u8f88\u4efd\u591a\u5927\uff0c\u522b\u5ba2\u6c14\u8fc7\u5934\u5566\uff01\u4f60\u4eec\u8c01\u4e5f\u522b\u53eb\u8c01\u5e08\u53d4\uff0c\u5927\u5bb6\u6309\u5e74\u7eaa\u5144\u5f1f\u76f8\u79f0\u5427\u3002\u201d\u539f\u6765\u8fd9\u59d3\u738b\u4e0e\u59d3\u9ad8\u7684\u662f\u5e08\u5144\u5f1f\uff0c\u4ed6\u4eec\u7684\u5e08\u7236\u53eb\u7a46\u4eba\u6e05\u4e3a\u5e08\u53d4\uff0c\u4f46\u4e5f\u4e0d\u662f\u771f\u7684\u6709\u751a\u4e48\u5e08\u95e8\u4e4b\u8c0a\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e48\u79f0\u547c\u3001\u5c0a\u4ed6\u4e3a\u957f\u8f88\u800c\u5df2\u3002\u5982\u6b64\u7b97\u6765\uff0c\u4e24\u4eba\u8fd8\u6bd4\u8881\u627f\u5fd7\u5c0f\u7740\u4e00\u8f88\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u5927\u54e5\u4ece\u5c71\u897f\u5949\u95ef\u738b\u4e4b\u547d\u524d\u6765\uff0c\u8981\u6211\u53bb\u5546\u91cf\u4e00\u4ef6\u4e8b\u3002\u6211\u660e\u5929\u5c31\u8981\u4e0b\u5c71\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u6b21\u6211\u8ddf\u4f60\u53bb\u77a7\u77a7\u5d14\u53d4\u53d4\u3002\u201d\u4ed6\u5728\u5c71\u4e0a\u5b9e\u5728\u95f7\u5f97\u817b\u4e86\uff0c\u597d\u51e0\u6b21\u60f3\u8ddf\u5e08\u7236\u4e0b\u5c71\uff0c\u90fd\u6ca1\u6709\u5f97\u5230\u51c6\u8bb8\uff0c\u8fd9\u6b21\u53c8\u6c42\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u5fae\u5fae\u4e00\u7b11\u3002\u738b\u9ad8\u4e8c\u4eba\u77e5\u9053\u4ed6\u4eec\u5e08\u5f92\u6709\u8bdd\u8981\u5546\u91cf\uff0c\u544a\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u773c\u524d\u4e49\u519b\u58f0\u52bf\u5927\u5f20\uff0c\u79e6\u664b\u4e24\u7701\u8f6c\u773c\u53ef\u5f97\uff0c\u8fd9\u4e5f\u6b63\u662f\u4f60\u62a5\u7236\u4ec7\u7684\u826f\u673a\u3002\u4f60\u66fe\u51e0\u6b21\u6c42\u6211\u5e26\u4f60\u53bb\u884c\u523a\u5d07\u796f\u7687\u5e1d\uff0c\u6211\u59cb\u7ec8\u6ca1\u51c6\u8bb8\uff0c\u4f60\u53ef\u77e5\u662f\u751a\u4e48\u539f\u56e0\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5b9a\u662f\u5f1f\u5b50\u7684\u529f\u592b\u6ca1\u5b66\u597d\u3002\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u8fd9\u56fa\u7136\u662f\u539f\u56e0\uff0c\u4f46\u53e6\u6709\u66f4\u91cd\u8981\u7684\u5173\u952e\u3002\u4f60\u5750\u4e0b\u542c\u6211\u8bf4\u3002\u201d\u8881\u627f\u5fd7\u4f9d\u8a00\u5750\u4e0b\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u6765\uff0c\u5173\u5916\u519b\u60c5\u7d27\u6025\uff0c\u6ee1\u6d32\u4eba\u91ce\u5fc3\u53f5\u6d4b\uff0c\u5343\u65b9\u767e\u8ba1\u60f3\u5165\u5bc7\u5173\u5185\u3002\u5d07\u796f\u8fd9\u4eba\u867d\u7136\u7591\u5fc3\u91cd\uff0c\u505a\u4e8b\u4e09\u5fc3\u4e24\u610f\uff0c\u4f46\u4ee5\u6297\u5fa1\u6ee1\u6e05\u800c\u8a00\uff0c\u6bd4\u4e4b\u524d\u671d\u4e07\u5386\u3001\u5929\u542f\u90a3\u4e9b\u660f\u541b\uff0c\u603b\u7b97\u8fd8\u662f\u7aed\u529b\u4ee5\u8d74\u7684\u3002\u8981\u662f\u4f60\u4e3a\u4e86\u79c1\u4ec7\uff0c\u8fdb\u5bab\u628a\u4ed6\u523a\u6b7b\uff0c\u7ee7\u4f4d\u7684\u592a\u5b50\u5e74\u5e7c\uff0c\u6743\u67c4\u843d\u5728\u5ba6\u5b98\u5978\u81e3\u624b\u91cc\uff0c\u53ea\u6015\u54b1\u4eec\u6c49\u4eba\u7684\u6c5f\u5c71\u9a6c\u4e0a\u5c31\u5f97\u65ad\u9001\uff0c\u4f60\u5c82\u975e\u6210\u4e86\u5929\u4e0b\u7f6a\u4eba\uff1f\u4f60\u7236\u4eb2\u7ec8\u8eab\u4ee5\u62b5\u5fa1\u6e05\u5175\u3001\u5e73\u5b9a\u8fbd\u4e1c\u4e3a\u5df1\u5fd7\uff0c\u4ed6\u5728\u5929\u4e4b\u7075\u77e5\u9053\u4e86\uff0c\u4e00\u5b9a\u4e5f\u8981\u6012\u4f60\u7684\u4e0d\u5fe0\u4e0d\u5b5d\u5427\uff1f\u201d\u8881\u627f\u5fd7\u542c\u5e08\u7236\u4e00\u8a00\u63d0\u9192\uff0c\u4e0d\u89c9\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u56fd\u5bb6\u4e8b\u5927\uff0c\u79c1\u4ec7\u4e8b\u5c0f\u3002\u6211\u4e0d\u8bb8\u4f60\u53bb\u884c\u523a\u590d\u4ec7\uff0c\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\u3002\u4f46\u73b0\u4e0b\u5c40\u9762\u4e0d\u540c\u4e86\uff0c\u95ef\u738b\u8282\u8282\u80dc\u5229\uff0c\u4e00\u4e24\u5e74\u5185\uff0c\u4fbf\u53ef\u8fdb\u53d6\u5317\u4eac\u3002\u95ef\u738b\u82f1\u660e\u795e\u6b66\uff0c\u90a3\u65f6\u7531\u4ed6\u6765\u4e3b\u6301\u5927\u5c40\uff0c\u54ea\u91cc\u8fd8\u6015\u8fbd\u4e1c\u6ee1\u6d32\u4eba\u5165\u5bc7\uff1f\u201d\u8881\u627f\u5fd7\u542c\u5f97\u8840\u8109\u8d32\u5f20\uff0c\u5174\u594b\u5f02\u5e38\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u773c\u4e0b\u4f60\u6b66\u529f\u5df2\u7ecf\u9887\u6709\u6839\u5e95\uff0c\u867d\u7136\u6b66\u5b66\u6c38\u65e0\u6b62\u5883\uff0c\u4f46\u6211\u6240\u77e5\u6240\u80fd\uff0c\u5df2\u5c3d\u6570\u4f20\u4f60\uff0c\u4ee5\u540e\u5c31\u5168\u51ed\u4f60\u81ea\u5df1\u7528\u529f\u3002\u660e\u5929\u6211\u4e0b\u5c71\u53bb\uff0c\u8981\u8ddf\u9ad8\u738b\u4e8c\u4eba\u53bb\u529e\u51e0\u4ef6\u4e8b\uff0c\u4f60\u7684\u6df7\u5143\u529f\u5c1a\u5dee\u4e86\u6700\u540e\u4e00\u5173\uff0c\u5c11\u5219\u5341\u65e5\uff0c\u591a\u5219\u4e00\u6708\uff0c\u624d\u80fd\u5706\u719f\u5982\u610f\uff0c\u878d\u4f1a\u8d2f\u901a\u3002 \u3000\u3000\u4e0b\u5c71\u5954\u6ce2\uff0c\u8bf8\u4e8b\u5206\u5fc3\uff0c\u7ec3\u529f\u6ca1\u5c71\u4e0a\u5b89\u9759\u3002\u5f85\u5f97\u6df7\u5143\u4e00\u6c14\u6e38\u8d70\u5168\u8eab\uff0c\u66f4\u65e0\u4e1d\u6beb\u7a92\u6ede\uff0c\u4f60\u518d\u4e0b\u5c71\uff0c\u5230\u95ef\u738b\u519b\u4e2d\u6765\u627e\u6211\u5427\u3002\u4e00\u8def\u4e4b\u4e0a\uff0c\u5982\u89c1\u5230\u4e0d\u5e73\u4e4b\u4e8b\uff0c\u4fbf\u987b\u4f38\u624b\u3002\u884c\u4fa0\u4ed7\u4e49\uff0c\u4e43\u6211\u8f88\u4efd\u6240\u5f53\u4e3a\uff0c\u7eb5\u662f\u4e07\u5206\u8270\u96be\u5371\u9669\uff0c\u4e5f\u4e0d\u53ef\u8896\u624b\u4e0d\u7406\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b54\u5e94\u4e86\uff0c\u542c\u5e08\u7236\u51c6\u8bb8\u4ed6\u4e0b\u5c71\uff0c\u751a\u662f\u6b22\u559c\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u5e73\u65f6\u65e9\u5df2\u628a\u672c\u95e8\u7684\u95e8\u89c4\uff0c\u4ee5\u53ca\u6c5f\u6e56\u4e0a\u8bf8\u822c\u7981\u5fcc\u89c4\u77e9\u3001\u5e2e\u4f1a\u90aa\u6b63\u3001\u95e8\u6d3e\u6e0a\u6e90\u3001\u6b66\u529f\u5bb6\u6570\u90fd\u8bf4\u4e86\u7ed9\u4ed6\u542c\uff0c\u8fd9\u65f6\u53c8\u62e9\u8981\u4e00\u63d0\uff0c\u6700\u540e\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4eba\u8c28\u614e\u6b63\u76f4\uff0c\u6211\u662f\u653e\u5fc3\u5f97\u8fc7\u7684\u3002 \u3000\u3000\u53ea\u662f\u4f60\u8840\u6c14\u65b9\u521a\uff0c\u4e8e\u2018\u8272\u2019\u5b57\u4e00\u5173\u53ef\u8981\u52a0\u610f\u5c0f\u5fc3\u3002\u591a\u5c11\u5927\u82f1\u96c4\u5927\u8c6a\u6770\u53ea\u56e0\u5728\u8fd9\u4e8b\u4e0a\u5931\u4e86\u8db3\uff0c\u5f04\u5f97\u8eab\u8d25\u540d\u88c2\u3002\u4f60\u53ef\u8981\u7262\u7262\u8bb0\u4f4f\u5e08\u7236\u8fd9\u53e5\u8bdd\u3002\u201d\u8881\u627f\u5fd7\u51db\u7136\u53d7\u6559\u3002 \u3000\u3000\u6b21\u65e5\u5929\u4eae\uff0c\u8881\u627f\u5fd7\u8d77\u8eab\u540e\uff0c\u5c31\u5982\u5e73\u65f6\u4e00\u822c\uff0c\u5e2e\u54d1\u5df4\u70e7\u6c34\u505a\u996d\uff0c\u7b49\u4e00\u5207\u5f04\u597d\u518d\u5230\u5e08\u7236\u623f\u91cc\u8bf7\u5b89\uff0c\u5374\u89c1\u7a46\u4eba\u6e05\u548c\u4e24\u4f4d\u5ba2\u4eba\u65e9\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u671b\u7740\u5e08\u7236\u7684\u7a7a\u5e8a\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u60f3\u5230\u4e0d\u4e45\u5c31\u53ef\u4e0b\u5c71\uff0c\u6253\u624b\u52bf\u544a\u8bc9\u4e86\u54d1\u5df4\u3002\u54d1\u5df4\u6100\u7136\u4e0d\u4e50\uff0c\u8f6c\u8eab\u8d70\u51fa\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u4ed6\u76f8\u5904\u5341\u4f59\u5e74\uff0c\u65e9\u5df2\u4eb2\u5982\u5144\u5f1f\uff0c\u77e5\u4ed6\u4e0d\u820d\u5f97\u4e0e\u81ea\u5df1\u5206\u79bb\uff0c\u5fc3\u4e0b\u4e5f\u611f\u6005\u60d8\u3002 \u3000\u3000\u5ffd\u5ffd\u8fc7\u4e86\u4e03\u516b\u5929\uff0c\u8881\u627f\u5fd7\u7167\u5e38\u7ec3\u4e60\u6b66\u529f\uff0c\u60f3\u5230\u4e0d\u4e45\u4fbf\u8981\u79bb\u53bb\uff0c\u5bf9\u5c71\u4e0a\u4e00\u8349\u4e00\u6728\u4e0d\u7531\u5f97\u52a0\u610f\u7231\u60dc\u8d77\u6765\u3002\u8fd9\u5929\u7528\u8fc7\u665a\u996d\uff0c\u5750\u5728\u5e8a\u4e0a\u53c8\u7ec3\u4e00\u904d\u6df7\u5143\u529f\uff0c\u4f46\u89c9\u5185\u606f\u6e38\u8d70\u5168\u8eab\u7ecf\u8109\uff0c\u6781\u662f\u987a\u7545\uff0c\u5fc3\u4e0b\u751a\u559c\u3002\u6b63\u8981\u7184\u706f\u7761\u89c9\uff0c\u54d1\u5df4\u8d70\u8fdb\u623f\u6765\uff0c\u505a\u624b\u52bf\u8bf4\u5c71\u4e2d\u4f3c\u4e4e\u6765\u4e86\u751f\u4eba\u3002\u8881\u627f\u5fd7\u8981\u5954\u51fa\u53bb\u5bdf\u770b\uff0c\u54d1\u5df4\u793a\u610f\u5df2\u524d\u540e\u67e5\u8fc7\uff0c\u5374\u672a\u89c1\u8e2a\u8ff9\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u653e\u5fc3\uff0c\u5e26\u4e86\u4e24\u5934\u7329\u7329\u5c71\u524d\u5c71\u540e\u67e5\u770b\uff0c\u679c\u6ca1\u53d1\u73b0\u6709\u4f55\u5f02\u72b6\uff0c\u4e5f\u5c31\u56de\u6765\u7761\u4e86\u3002 \u3000\u3000\u7761\u5230\u534a\u591c\uff0c\u5ffd\u542c\u5230\u5916\u623f\u4e2d\u5927\u5a01\u4e0e\u5c0f\u4e56\u5431\u5431\u4e71\u53eb\uff0c\u8881\u627f\u5fd7\u7ffb\u8eab\u5750\u8d77\uff0c\u4fa7\u8033\u7ec6\u542c\uff0c\u5ffd\u7136\u95f4\u4e00\u9635\u751c\u9999\u6251\u9f3b\uff0c\u6697\u53eb\uff1a\u201c\u4e0d\u597d\uff01\u201d \u3000\u3000\u95ed\u6c14\u7eb5\u51fa\uff0c\u54ea\u77e5\u811a\u4e0b\u9661\u7136\u65e0\u529b\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u4e9b\u8dcc\u5012\u3002\u90a3\u662f\u4ed6\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\uff0c\u6b63\u81ea\u5927\u611f\u60ca\u8bb6\uff0c\u5ba4\u95e8\u7830\u7684\u4e00\u58f0\u88ab\u4eba\u8e22\u5f00\uff0c\u4e00\u6761\u9ed1\u5f71\u7a9c\u5c06\u51fa\u6765\uff0c\u9ed1\u6697\u4e2d\u5200\u98ce\u98d2\u7136\uff0c\u5f53\u5934\u780d\u5230\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53ea\u611f\u5230\u5934\u8111\u53d1\u6655\uff0c\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u5371\u6025\u4e2d\u5f3a\u81ea\u652f\u6301\uff0c\u8eab\u5b50\u5411\u5de6\u4e00\u504f\uff0c\u53f3\u624b\u53cd\u51fb\u4e00\u638c\u3002\u90a3\u4eba\u6325\u5200\u76f4\u5288\u4e0b\u6765\uff0c\u524a\u4ed6\u624b\u81c2\u3002 \u3000\u3000\u8881\u627f\u5fd7\u731d\u9047\u5f3a\u654c\uff0c\u4e0d\u5bb9\u5bf9\u65b9\u6709\u7f13\u624b\u673a\u4f1a\uff0c\u9ed1\u6697\u4e2d\u542c\u58f0\u8fa8\u5f62\uff0c\u6b3a\u8fdb\u4e00\u6b65\uff0c\u5de6\u638c\u5657\u7684\u4e00\u58f0\uff0c\u51fb\u5728\u90a3\u4eba\u80a9\u5934\uff0c\u53ea\u662f\u624b\u81c2\u9178\u8f6f\uff0c\u4f7f\u51fa\u6765\u7684\u8fd8\u4e0d\u5230\u5e73\u65f6\u4e00\u6210\u529f\u529b\uff0c\u9976\u662f\u5982\u6b64\uff0c\u90a3\u4eba\u8fd8\u662f\u5355\u5200\u8131\u624b\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u76f4\u63bc\u51fa\u53bb\u3002\u5916\u9762\u4e00\u4eba\u4f38\u624b\u62c9\u4f4f\uff0c\u95ee\u9053\uff1a\u201c\u70b9\u5b50\u722a\u5b50\u786c\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5f85\u8981\u6251\u51fa\u8ffd\u654c\uff0c\u7a81\u89c9\u4e00\u9635\u8ff7\u7cca\uff0c\u6655\u5012\u5728\u5730\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u9694\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u65b9\u624d\u9192\u6765\uff0c\u53ea\u611f\u6df7\u8eab\u9178\u8f6f\uff0c\u624b\u8db3\u4e00\u52a8\uff0c\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u539f\u6765\u5168\u8eab\u5df2\u88ab\u7ef3\u5b50\u7f1a\u4f4f\u3002\u53ea\u89c1\u5ba4\u4e2d\u706f\u706b\u8f89\u714c\uff0c\u4e24\u4e2a\u4eba\u6b63\u5728\u7ffb\u7bb1\u5012\u7ba7\u7684\u5230\u5904\u641c\u68c0\u3002 \u3000\u3000\u4ed6\u77e5\u906d\u4eba\u6697\u7b97\uff0c\u5fc3\u4e2d\u81ea\u8d23\u65e0\u7528\uff0c\u5e08\u7236\u4e0b\u5c71\u6ca1\u591a\u5929\uff0c\u5c31\u7ed9\u4eba\u63a9\u4e0a\u5c71\u6765\u64d2\u4f4f\u4e86\uff0c\u90a3\u8fd8\u8bf4\u751a\u4e48\u95ef\u6c5f\u6e56\u62a5\u7236\u4ec7\u3002\u8fd9\u65f6\u5140\u81ea\u5934\u6655\u76ee\u7729\uff0c\u4e8e\u662f\u6f5c\u8fd0\u5185\u529f\uff0c\u7247\u523b\u95f4\u4fbf\u5373\u5b81\u5b9a\u3002 \u3000\u3000\u5f53\u4e0b\u5047\u88c5\u660f\u5012\u672a\u9192\uff0c\u773c\u7741\u4e00\u7ebf\u5077\u770b\uff0c\u53ea\u89c1\u4e00\u4eba\u8eab\u6750\u7626\u524a\uff0c\u56db\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u9762\u5bb9\u5e72\u67af\uff0c\u53e6\u4e00\u4e2a\u5934\u9876\u5149\u79c3\uff0c\u8eab\u8eaf\u9ad8\u5927\uff0c\u77a7\u8eab\u5f62\u5c31\u662f\u9002\u624d\u4e0e\u81ea\u5df1\u4ea4\u624b\u4e4b\u4eba\u3002\u4ed6\u60f3\uff1a\u201c\u5c71\u4e0a\u6709\u751a\u4e48\u8d35\u91cd\u4e1c\u897f\uff0c\u503c\u5f97\u4ed6\u4eec\u6765\u62a2\uff1f\u8fd9\u91cc\u5c31\u53ea\u6709\u5e08\u7236\u7559\u4e0b\u7ed9\u6211\u505a\u76d8\u7f20\u7684\u4e94\u5341\u4e24\u94f6\u5b50\u3002\u4f46\u8fd9\u4e8c\u4eba\u7edd\u975e\u5bfb\u5e38\u76d7\u8d3c\uff0c\u8fd9\u79c3\u5b50\u6b66\u529f\u4e0d\u5f31\uff0c\u60f3\u90a3\u7626\u5b50\u4e5f\u81ea\u4e86\u5f97\u3002\u82e5\u8bf4\u662f\u6765\u627e\u5e08\u7236\u62a5\u4ec7\uff0c\u4e3a\u751a\u4e48\u4e0d\u6740\u6211\uff0c\u5374\u5230\u5904\u641c\u5bfb\u4e1c\u897f\uff1f\u201d\u6697\u8fd0\u529f\u529b\uff0c\u60f3\u5d29\u65ad\u624b\u4e0a\u6240\u7f1a\u7ef3\u7d22\u7ef3\u5b50\u3002\u4e0d\u6599\u654c\u4eba\u77e5\u4ed6\u6b66\u529f\u7cbe\u5f3a\uff0c\u5df2\u5728\u4ed6\u53cc\u624b\u4e4b\u95f4\u63d2\u4e86\u4e00\u652f\u7a7a\u7af9\uff0c\u53ea\u8981\u4e00\u7528\u529b\uff0c\u7af9\u5b50\u5148\u7834\uff0c\u7acb\u53d1\u58f0\u54cd\u3002\u8881\u627f\u5fd7\u5fae\u5fae\u4e00\u6323\uff0c\u4fbf\u5373\u53d1\u89c9\uff0c\u4e8e\u662f\u505c\u624b\u4e0d\u52a8\uff0c\u5bfb\u601d\u8131\u8eab\u4e4b\u8ba1\u3002 \u3000\u3000\u90a3\u79c3\u5b50\u5ffd\u7136\u9ad8\u5174\u5f97\u5927\u53eb\u8d77\u6765\uff1a\u201c\u5728\u8fd9\u91cc\u5566\uff01\u201d\u4ece\u5e8a\u5e95\u4e0b\u6367\u51fa\u4e00\u4e2a\u5927\u94c1\u76d2\u6765\uff0c\u6b63\u662f\u91d1\u86c7\u90ce\u541b\u7684\u9057\u7269\u3002\u7626\u5b50\u8138\u9732\u559c\u5bb9\uff0c\u4e0e\u79c3\u5b50\u5750\u5728\u684c\u8fb9\uff0c\u6253\u5f00\u94c1\u76d2\uff0c\u53d6\u51fa\u4e00\u672c\u4e66\u6765\uff0c\u89c1\u5c01\u9762\u4e0a\u5199\u7740\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u56db\u5b57\u3002 \u3000\u3000\u79c3\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u679c\u7136\u5728\u8fd9\u91cc\uff0c\u5e08\u54e5\uff0c\u54b1\u4eec\u8fd9\u5341\u516b\u5e74\u529f\u592b\u53ef\u6ca1\u767d\u8d39\u3002\u201d\u63ed\u5f00\u79d8\u7b08\uff0c\u89c1\u4e66\u9875\u4e0a\u753b\u7740\u8bb8\u591a\u56fe\u5f62\uff0c\u5199\u6ee1\u5c0f\u5b57\uff0c\u559c\u5f97\u6643\u5934\u6414\u8033\uff0c\u4e50\u4e0d\u53ef\u652f\u3002 \u3000\u3000\u7626\u5b50\u5ffd\u53eb\uff1a\u201c\u54a6\uff0c\u90a3\u4eba\u8981\u9003\uff01\u201d\u8bf4\u7740\u5411\u8881\u627f\u5fd7\u4e00\u6307\u3002\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\u3002\u79c3\u5b50\u56de\u8fc7\u5934\u6765\uff0c\u90a3\u7626\u5b50\u624b\u8155\u7ffb\u5904\uff0c\u6ce2\u7684\u4e00\u58f0\uff0c\u4e00\u67c4\u5315\u9996\u63d2\u8fdb\u4e86\u79c3\u5b50\u80cc\u810a\uff0c\u76f4\u6ca1\u81f3\u67c4\uff0c\u968f\u5373\u8dc3\u5f00\u6570\u5c3a\uff0c\u62d4\u51fa\u957f\u5251\uff0c\u62a4\u4f4f\u95e8\u9762\u3002 \u3000\u3000\u79c3\u5b50\u60ca\u6115\u5f02\u5e38\uff0c\u5ffd\u7136\u60e8\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u51e0\u4e2a\u5e08\u5144\u5f1f\u5bfb\u8bbf\u4e86\u5341\u516b\u5e74\uff0c\u4eca\u65e5\u6211\u548c\u4f60\u624d\u5f97\u5230\u8fd9\u5b9d\u8d1d\uff0c\u4f60\u8981\u72ec\u541e\uff0c\u7adf\u5bf9\u6211\u4e0b\u8fd9\u6bd2\u2026\u2026\u624b\u2026\u2026\u54c8\u54c8\u2026\u2026\u54c8\u54c8\u2026\u2026\u4f60\u2026\u2026\u4f60\u5f53\u7136\u8fde\u77f3\u6881\u6d3e\u4e5f\u53db\u4e86\u3002\u53ef\u662f\u8981\u7792\u8fc7\u4e94\u4f4d\u8001\u7237\u5b50\uff0c\u53ea\u6015\u6ca1\u8fd9\u4e48\u5bb9\u6613\uff0c\u6211\u2026\u2026\u77a7\u4f60\u6709\u751a\u4e48\u597d\u4e0b\u573a\u2026\u2026\u54c8\u54c8\u2026\u2026\u201d \u3000\u3000\u9759\u591c\u4e2d\u542c\u5230\u8fd9\u60e8\u5389\u7684\u7b11\u58f0\uff0c\u8881\u627f\u5fd7\u5168\u8eab\u5bd2\u6bdb\u76f4\u7ad6\u3002 \u3000\u3000\u90a3\u79c3\u5b50\u53cd\u624b\u53bb\u62d4\u80cc\u4e0a\u5315\u9996\uff0c\u5374\u603b\u662f\u591f\u4e0d\u5230\uff0c\u84e6\u5730\u91cc\u957f\u58f0\u60e8\u547c\uff0c\u6251\u5728\u5730\u4e0a\uff0c\u62bd\u6410\u4e86\u51e0\u4e0b\uff0c\u5c31\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u7626\u5b50\u6015\u4ed6\u6ca1\u6b7b\uff0c\u53c8\u8fc7\u53bb\u5728\u4ed6\u80cc\u4e0a\u523a\u4e86\u4e24\u5251\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u6740\u4f60\uff0c\u6015\u4f60\u4e0d\u4f1a\u6740\u6211\u4e48\uff1f\u90a3\u53c8\u4f55\u5fc5\u5ba2\u6c14\uff1f\u201d\u968f\u5373\u53c8\u5728\u79c3\u5b50\u7684\u5c38\u8eab\u4e0a\u91cd\u91cd\u8e22\u4e86\u4e00\u811a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u7792\u4e0d\u8fc7\u90a3\u4e94\u4e2a\u7cdf\u8001\u5934\u5b50\uff1f\u4f60\u77a7\u6211\u7684\uff01\u201d \u3000\u3000\u4ed6\u4e0d\u77e5\u8881\u627f\u5fd7\u5df2\u9192\uff0c\u9634\u607b\u607b\u7684\u7b11\u4e86\u4e24\u58f0\uff0c\u5f39\u53bb\u4e86\u8721\u70db\u4e0a\u7684\u706f\u82b1\uff0c\u6253\u5f00\u79d8\u7b08\u770b\u4e86\u8d77\u6765\uff0c\u4ed6\u8eab\u5b50\u5fae\u5fae\u6643\u52a8\uff0c\u6ee1\u8138\u559c\u8272\u3002\u4ed6\u7ffb\u4e86\u51e0\u9875\uff0c\u6709\u51e0\u9875\u7c98\u4f4f\u4e86\u63ed\u4e0d\u5f00\u6765\uff0c\u4f38\u98df\u6307\u5728\u53e3\u4e2d\u4e00\u8210\uff0c\u8638\u4e86\u4e9b\u553e\u6db2\u53c8\u53bb\u7ffb\u9605\uff0c\u8fd9\u822c\u7ffb\u4e86\u51e0\u5f20\uff0c\u8881\u627f\u5fd7\u7a81\u7136\u60f3\u8d77\uff0c\u4e66\u672c\u4e0a\u9644\u6709\u5267\u6bd2\uff0c\u4ed6\u5982\u6b64\u7ffb\u9605\uff0c\u52bf\u5fc5\u4e2d\u6bd2\uff0c\u4e0d\u7531\u5f97\u201c\u5440\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u7626\u5b50\u542c\u5230\u4e86\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u89c1\u8881\u627f\u5fd7\u8138\u4e0a\u5c3d\u662f\u60ca\u60f6\u4e4b\u8272\uff0c\u4fbf\u7f13\u7f13\u7ad9\u8d77\uff0c\u4ece\u79c3\u5b50\u80cc\u4e0a\u62d4\u51fa\u5315\u9996\uff0c\u8d70\u4e0a\u4e24\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u65e0\u6028\u65e0\u4ec7\uff0c\u53ef\u662f\u4eca\u65e5\u5374\u4e0d\u80fd\u9976\u4f60\u6027\u547d\u3002\u201d\u8bf4\u7740\u773c\u9732\u51f6\u5149\uff0c\u4e3e\u8d77\u5315\u9996\uff0c\u72de\u7b11\u4e24\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u65f6\u6740\u4f60\uff0c\u53ea\u6015\u4f60\u5230\u4e86\u9634\u95f4\u4e5f\u4e0d\u77e5\u539f\u56e0\u3002\u8001\u5b9e\u8ddf\u4f60\u8bf4\uff0c\u6211\u662f\u6d59\u6c5f\u8862\u5dde\u77f3\u6881\u6d3e\u7684\u5f20\u6625\u4e5d\u3002\u6211\u4eec\u77f3\u6881\u6d3e\u548c\u91d1\u86c7\u90ce\u541b\u662f\u6b7b\u5bf9\u5934\uff0c\u4ed6\u5978\u6deb\u4e86\u6211\u4eec\u5e08\u59b9\uff0c\u9003\u5f97\u4e0d\u77e5\u53bb\u5411\u3002\u6211\u4eec\u5341\u591a\u5e74\u6765\u5230\u5904\u627e\u4ed6\uff0c\u54ea\u77e5\u4ed6\u7684\u7269\u4e8b\u7adf\u5728\u4f60\u8fd9\u5c0f\u5b50\u624b\u91cc\u3002\u91d1\u86c7\u90ce\u541b\u5728\u54ea\u91cc\uff1f\u201d\u8bf4\u7740\u5411\u7a97\u5916\u4e00\u671b\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u8138\u9732\u754f\u60e7\uff0c\u4f3c\u4e4e\u6015\u91d1\u86c7\u90ce\u541b\u7a81\u7136\u51fa\u73b0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u82e5\u662f\u7a0d\u6709\u6c5f\u6e56\u7ecf\u5386\uff0c\u81ea\u4f1a\u51fa\u8a00\u6050\u5413\uff0c\u7eb5\u4e0d\u80fd\u5c06\u4ed6\u60ca\u8d70\uff0c\u4e5f\u53ef\u4f7f\u4ed6\u5fc3\u6709\u987e\u5fcc\uff0c\u4e0d\u6562\u968f\u4fbf\u52a0\u5bb3\u81ea\u5df1\uff0c\u4f46\u6b64\u65f6\u516d\u795e\u65e0\u4e3b\uff0c\u54ea\u60f3\u5f97\u5230\u9a97\u4eba\uff1f\u53ea\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u65e9\u5df2\u6b7b\u4e86\uff0c\u4ed6\u2026\u2026\u4ed6\u7684\u5c38\u9aa8\u4e5f\u662f\u6211\u846c\u7684\u3002\u201d\u5f20\u6625\u4e5d\u5927\u559c\uff0c\u53c8\u95ee\u4e00\u53e5\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u679c\u7136\u6b7b\u4e86\uff1f\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\u3002\u5f20\u6625\u4e5d\u559d\u95ee\uff1a\u201c\u4ed6\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u771f\u7684\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5f20\u6625\u4e5d\u6ee1\u8138\u72f0\u72de\u4e4b\u8272\uff0c\u6076\u72e0\u72e0\u7684\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4f4f\u5728\u534e\u5c71\u4e4b\u4e0a\uff0c\u51b3\u975e\u597d\u4eba\uff0c\u6599\u6765\u8ddf\u91d1\u86c7\u90ce\u541b\u86c7\u9f20\u4e00\u7a9d\uff0c\u6740\u4e86\u4f60\u4e5f\u4e0d\u51a4\u3002 \u3000\u3000\u4f60\u505a\u4e86\u9b3c\u8981\u62a5\u4ec7\uff0c\u5230\u8862\u5dde\u6765\u627e\u6211\u5f20\u6625\u4e5d\u5427\u3002\u54c8\u54c8\uff0c\u4e0d\u8fc7\u6211\u4eca\u540e\u8862\u5dde\u4e5f\u6c38\u4e0d\u56de\u53bb\u4e86\uff0c\u53ea\u6015\u4f60\u53d8\u4e86\u9b3c\u4e5f\u627e\u6211\u4e0d\u5230\u2026\u2026\u54c8\u54c8\u2026\u2026\u201d\u7b11\u58f0\u672a\u6bd5\uff0c\u7a81\u7136\u6253\u4e86\u4e2a\u8e09\u8dc4\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77e5\u9053\u5371\u673a\u8feb\u5728\u76ee\u524d\uff0c\u5168\u8eab\u529b\u9053\u90fd\u8fd0\u5230\u4e86\u53cc\u81c2\u4e4b\u4e0a\uff0c\u731b\u559d\u4e00\u58f0\uff0c\u7ef3\u7d22\u767b\u65f6\u8ff8\u65ad\uff0c\u6325\u638c\u6b63\u8981\u6253\u51fa\uff0c\u5f20\u6625\u4e5d\u5ffd\u7136\u4ef0\u5929\u4fbf\u5012\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6015\u4ed6\u6709\u8bc8\uff0c\u624b\u6301\u65ad\u7ef3\uff0c\u5728\u9762\u524d\u6325\u4e86\u4e24\u4e0b\uff0c\u547c\u547c\u751f\u98ce\u3002\u5374\u89c1\u4ed6\u53cc\u811a\u4e00\u767b\uff0c\u4fbf\u4e0d\u52a8\u4e86\uff0c\u773c\u4e2d\u3001\u9f3b\u4e2d\u3001\u8033\u4e2d\u3001\u53e3\u4e2d\uff0c\u90fd\u6d41\u51fa\u9ed1\u8840\u6765\uff0c\u624d\u77e5\u4ed6\u5df2\u4e2d\u6bd2\u800c\u6b7b\uff0c\u4fef\u8eab\u89e3\u5f00\u81ea\u5df1\u811a\u4e0b\u7ef3\u7d22\uff0c\u5954\u5230\u5916\u5ba4\uff0c\u89c1\u54d1\u5df4\u4e5f\u5df2\u88ab\u7f1a\uff0c\u53cc\u76ee\u5706\u7741\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u5fd9\u7ed9\u4ed6\u89e3\u4e86\u7f1a\u3002\u53c8\u89c1\u5927\u5a01\u4e0e\u5c0f\u4e56\u660f\u5012\u5728\u5730\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u53bb\u7aef\u4e86\u4e00\u76c6\u51b7\u6c34\u4ece\u5934\u4e0a\u6dcb\u5c06\u4e0b\u53bb\uff0c\u4e24\u5934\u7329\u7329\u6e10\u6e10\u82cf\u9192\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6253\u624b\u52bf\u628a\u7ecf\u8fc7\u60c5\u5f62\u544a\u8bc9\u54d1\u5df4\u3002\u7b49\u5929\u660e\u540e\uff0c\u4e24\u4eba\u628a\u4e24\u5177\u6b7b\u5c38\u62ac\u5230\u540e\u5c71\u3002\u8881\u627f\u5fd7\u60f3\u8fd9\u5927\u94c1\u76d2\u662f\u5bb3\u4eba\u4e4b\u7269\uff0c\u4fbf\u6295\u5728\u5751\u91cc\uff0c\u4e0e\u4e24\u5177\u6b7b\u5c38\u4e00\u8d77\u57cb\u846c\uff0c\u60f3\u8d77\u591c\u6765\u60c5\u4e8b\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u5fc3\u60ca\uff1a\u201c\u8fd9\u4e8c\u4eba\u6240\u4ee5\u7ed1\u4f4f\u6211\u4e0e\u54d1\u5df4\uff0c\u4e0d\u5373\u4e00\u5200\u6740\u6b7b\uff0c\u81ea\u662f\u4e3a\u4e86\u9884\u5907\u62f7\u95ee\u91d1\u86c7\u90ce\u541b\u7684\u4e0b\u843d\u3002\u82e5\u975e\u4ed6\u4eec\u53e6\u6709\u56fe\u8c0b\uff0c\u8fd9\u65f6\u846c\u5728\u8fd9\u5751\u4e2d\u7684\uff0c\u5374\u662f\u6211\u4e0e\u54d1\u5df4\u7684\u5c38\u9996\u4e86\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u56db\u56de\u3000\u77eb\u77eb\u91d1\u86c7\u5251 \u7fe9\u7fe9\u7f8e\u5c11\u5e74",content:(0,t.jsx)(t.Fragment,{children:"\u8881\u627f\u5fd7\u5728\u5341\u4e09\u5c81\u4e0a\u65e0\u610f\u4e2d\u53d1\u73b0\u94c1\u76d2\uff0c\u8fd9\u4e9b\u5e74\u6765\u65e9\u628a\u8fd9\u4ef6\u4e8b\u5fd8\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u773c\u770b\u8fd9\u5f20\u6625\u4e5d\u4e0e\u79c3\u5b50\u7684\u795e\u60c5\uff0c\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u5b9a\u662f\u85cf\u6709\u91cd\u5927\u79d8\u5bc6\uff0c\u5426\u5219\u4ed6\u4eec\u4e0d\u4f1a\u8fde\u7eed\u627e\u4e0a\u5341\u516b\u5e74\u4e4b\u4e45\uff0c\u627e\u5230\u4e4b\u540e\uff0c\u53c8\u5982\u6b64\u4f60\u62a2\u6211\u593a\u7684\u6027\u547d\u76f8\u640f\u3002\u201c\u5230\u5e95\u79d8\u7b08\u4e2d\u5199\u7740\u751a\u4e48\uff1f\u201d\u6b64\u5ff5\u4e00\u52a8\uff0c\u518d\u4e5f\u4e0d\u80fd\u514b\u5236\uff0c\u4e8e\u662f\u5728\u5e8a\u5e95\u89d2\u843d\u4e2d\u628a\u90a3\u53ea\u5c18\u5c01\u86db\u7ed3\u7684\u5c0f\u94c1\u76d2\u627e\u4e86\u51fa\u6765\u3002\u8fd9\u53ea\u76d2\u5b50\u5c0f\u5f97\u591a\uff0c\u5f20\u6625\u4e5d\u548c\u79c3\u5934\u4e00\u65f6\u6ca1\u53d1\u89c1\u3002\u4e24\u4eba\u4e00\u89c1\u5230\u5927\u94c1\u76d2\u4e2d\u7684\u5047\u79d8\u7b08\uff0c\u4fbf\u6b23\u559c\u82e5\u72c2\uff0c\u518d\u4e5f\u4e0d\u53bb\u627e\u5bfb\u522b\u7269\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6253\u5f00\u94c1\u76d2\uff0c\u53d6\u51fa\u771f\u672c\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u653e\u5728\u684c\u4e0a\u3002\u7ffb\u5f00\u9605\u8bfb\uff0c\u524d\u9762\u662f\u4e9b\u7ec3\u529f\u79d8\u8bc0\u4ee5\u53ca\u6253\u6697\u5668\u7684\u5fc3\u6cd5\uff0c\u4e0e\u4ed6\u5e08\u7236\u53ca\u6728\u6851\u9053\u4eba\u6240\u6388\u5927\u540c\u5c0f\u5f02\uff0c\u7ea6\u7565\u770b\u53bb\uff0c\u79d8\u7b08\u4e2d\u6240\u8f7d\uff0c\u9887\u6709\u4e0d\u53ca\u81ea\u5df1\u6240\u5b66\u7684\uff0c\u4f46\u624b\u6cd5\u4e4b\u9634\u6bd2\u72e0\u8fa3\uff0c\u5374\u8fdc\u6709\u8fc7\u4e4b\u3002\u5fc3\u60f3\uff0c\u8fd9\u6b21\u9669\u4e9b\u4e2d\u4e86\u654c\u4eba\u7684\u5351\u9119\u8be1\u8ba1\uff0c\u65e5\u540e\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u96be\u4fdd\u4e0d\u518d\u9047\u5230\u9634\u6076\u7684\u5bf9\u624b\uff0c\u8fd9\u4e9b\u4eba\u7684\u624b\u6cd5\u81ea\u5df1\u867d\u7136\u4e0d\u5c51\u4f7f\u7528\uff0c\u4f46\u77e5\u5df1\u77e5\u5f7c\uff0c\u4e3a\u4e86\u514b\u654c\u62a4\u8eab\uff0c\u5374\u4e0d\u53ef\u4e0d\u77e5\uff0c\u4e8e\u662f\u5bf9\u79d8\u7b08\u4e2d\u6240\u8ff0\u5fc3\u6cd5\u7ec6\u52a0\u53c2\u7814\u3002 \u3000\u3000\u4e00\u8def\u8bfb\u5c06\u4e0b\u53bb\uff0c\u4e0d\u7531\u5f97\u989d\u5934\u51b7\u6c57\u6d94\u6d94\u800c\u4e0b\uff0c\u4e16\u4e0a\u539f\u6765\u7adf\u6709\u8fd9\u79cd\u79cd\u5bb3\u4eba\u7684\u6bd2\u6cd5\uff0c\u5f53\u771f\u662f\u532a\u5937\u6240\u601d\uff0c\u76f8\u8f83\u4e4b\u4e0b\uff0c\u5f20\u6625\u4e5d\u548c\u90a3\u79c3\u5b50\u7528\u95f7\u836f\u8ff7\u4eba\uff0c\u53ef\u8bf4\u662f\u6beb\u4e0d\u8db3\u9053\u4e86\u3002 \u3000\u3000\u8bfb\u5230\u7b2c\u4e09\u65e5\u4e0a\uff0c\u89c1\u79d8\u7b08\u6240\u8f7d\u6b66\u529f\u5df2\u4e0e\u81ea\u5df1\u8fc7\u53bb\u6240\u5b66\u5168\u7136\u4e0d\u540c\uff0c\u4e0d\u4f46\u4e0e\u534e\u5c71\u6d3e\u6b66\u529f\u65e0\u4e1d\u6beb\u5171\u901a\u4e4b\u5904\uff0c\u800c\u4e14\u4ece\u6765\u4e0d\u66fe\u542c\u5e08\u7236\u8bf4\u8d77\u8fc7\uff0c\u90a3\u4e5f\u5e76\u975e\u4ec5\u662f\u522b\u6709\u8e4a\u5f84\u800c\u5df2\uff0c\u76f4\u662f\u5f02\u60f3\u5929\u5f00\uff0c\u5f80\u5f80\u4e0e\u6b66\u5b66\u8981\u65e8\u80cc\u9053\u800c\u9a70\uff0c\u5374\u4e5f\u81ea\u5177\u514b\u654c\u5236\u80dc\u4e4b\u5999\u3002\u4ed6\u4e00\u827a\u901a\u767e\u827a\u901a\uff0c\u6b66\u5b66\u4e0a\u65e2\u5df2\u6709\u9887\u6df1\u9020\u8be3\uff0c\u518d\u5b66\u65c1\u95e8\u81ea\u662f\u4e00\u70b9\u5373\u4f1a\u3002\u79d8\u7b08\u4e2d\u6240\u8f7d\u6b66\u529f\u5947\u60f3\u602a\u7740\uff0c\u7eb7\u81f3\u53e0\u6765\uff0c\u4e00\u5b66\u4e4b\u4e0b\uff0c\u518d\u4e5f\u4e0d\u80fd\u81ea\u4f11\uff0c\u5f53\u4e0b\u4e0d\u7531\u81ea\u4e3b\u7684\u7167\u7740\u79d8\u7b08\u4e00\u8def\u7ec3\u5c06\u4e0b\u53bb\u3002 \u3000\u3000\u7ec3\u5230\u4e8c\u5341\u4f59\u65e5\u540e\u5374\u9047\u4e0a\u4e86\u96be\u5173\uff0c\u79d8\u7b08\u4e2d\u8981\u6cd5\u5173\u7a8d\uff0c\u8bb0\u8f7d\u8be6\u660e\uff0c\u4f46\u6839\u57fa\u6240\u5728\u7684\u59ff\u52bf\u5374\u65e0\u56fe\u5f62\uff0c\u8bc0\u8981\u751a\u662f\u7b80\u7565\uff0c\u4e0d\u77e5\u62db\u5f0f\uff0c\u53ea\u5f97\u7565\u8fc7\u4e0d\u7ec3\u3002 \u3000\u3000\u518d\u7ffb\u4e0b\u53bb\u662f\u4e00\u5957\u201c\u91d1\u86c7\u5251\u6cd5\u201d\uff0c\u5fc3\u60f3\uff1a\u6b64\u5251\u6cd5\u4ee5\u201c\u91d1\u86c7\u201d \u3000\u3000\u4e3a\u540d\uff0c\u91d1\u86c7\u90ce\u541b\u5b9a\u662f\u5341\u5206\u91cd\u89c6\uff0c\u5fc5\u6709\u72ec\u5230\u4e4b\u5904\u3002\u7167\u5f0f\u7ec3\u53bb\uff0c\u521d\u65f6\u8fd8\u4e0d\u89c9\u751a\u4e48\uff0c\u5230\u540e\u6765\u8f6c\u6298\u8d77\u4f0f\uff0c\u523a\u6253\u5288\u524a\u4e4b\u95f4\uff0c\u751a\u662f\u4e0d\u987e\uff0c\u6709\u4e9b\u62db\u5f0f\u66f4\u662f\u7edd\u65e0\u7528\u5904\uff0c\u8fde\u8bd5\u51e0\u6b21\u603b\u611f\u4e0d\u5bf9\uff0c\u7a81\u7136\u60f3\u8d77\uff0c\u91d1\u86c7\u90ce\u541b\u57cb\u9aa8\u7684\u6d1e\u4e2d\u58c1\u4e0a\u6709\u8bb8\u591a\u56fe\u5f62\uff0c\u83ab\u975e\u4e0e\u6b64\u6709\u5173\uff1f \u3000\u3000\u4e00\u60f3\u5230\u8fd9\u4e8b\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u62db\u4e86\u54d1\u5df4\uff0c\u5e26\u4e86\u7ef3\u7d22\u706b\u628a\uff0c\u53c8\u53bb\u6d1e\u4e2d\u3002\u8fd9\u65f6\u4ed6\u8eab\u6750\u5df2\u7ecf\u9ad8\u5927\uff0c\u5e78\u800c\u5f53\u5e74\u66fe\u5c06\u6d1e\u53e3\u62c6\u5927\uff0c\u4e8e\u662f\u94bb\u8fdb\u6d1e\u5185\uff0c\u4e3e\u8d77\u706b\u628a\u5f80\u58c1\u4e0a\u7167\u53bb\uff0c\u5bf9\u56fe\u5f62\u4e00\u52a0\u7422\u78e8\uff0c\u679c\u662f\u79d8\u7b08\u4e2d\u8981\u8bc0\u7684\u56fe\u89e3\u3002\u4ed6\u5fc3\u4e0b\u5927\u559c\uff0c\u7167\u56fe\u8bd5\u7ec3\uff0c\u6697\u6697\u9ed8\u8bb0\uff0c\u82b1\u4e86\u51e0\u4e2a\u65f6\u8fb0\uff0c\u5c06\u56fe\u5f62\u5c3d\u6570\u8bb0\u719f\u4e86\uff0c\u5728\u91d1\u86c7\u90ce\u541b\u5893\u524d\u53c8\u62dc\u4e86\u4e24\u62dc\uff0c\u8c22\u4ed6\u9057\u4e66\u6559\u6388\u6b66\u529f\u3002 \u3000\u3000\u6b63\u8981\u8d70\u51fa\uff0c\u4e00\u77a5\u95f4\u89c1\u5230\u6d1e\u58c1\u4e0a\u7684\u90a3\u4e2a\u5251\u67c4\uff0c\u5f53\u65e5\u5e74\u5e7c\u529b\u5f31\uff0c\u672a\u80fd\u62d4\u51fa\uff0c\u6b64\u65f6\u7d27\u7d27\u63e1\u4f4f\u5251\u67c4\uff0c\u6f5c\u8fd0\u5185\u529b\uff0c\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u62d4\u4e86\u51fa\u6765\uff0c\u5251\u67c4\u4e0b\u679c\u7136\u8fde\u6709\u5251\u8eab\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u5168\u8eab\u51c9\u98d5\u98d5\u5730\u53ea\u611f\u5bd2\u6c14\u903c\u4eba\uff0c\u53ea\u89c1\u90a3\u5251\u5f62\u72b6\u751a\u662f\u5947\u7279\uff0c\u4e0e\u5148\u524d\u6240\u89c1\u7684\u91d1\u86c7\u9525\u4f9d\u7a00\u76f8\u4f3c\uff0c\u6574\u67c4\u5251\u5c31\u5982\u662f\u4e00\u6761\u86c7\u76d8\u66f2\u800c\u6210\uff0c\u86c7\u5c3e\u52fe\u6210\u5251\u67c4\uff0c\u86c7\u5934\u5219\u662f\u5251\u5c16\uff0c\u86c7\u820c\u4f38\u51fa\u5206\u53c9\uff0c\u662f\u4ee5\u5251\u5c16\u7adf\u6709\u4e24\u53c9\u3002\u90a3\u5251\u91d1\u5149\u707f\u70c2\uff0c\u63e1\u5728\u624b\u4e2d\u751a\u662f\u6c89\u91cd\uff0c\u770b\u6765\u7adf\u662f\u9ec4\u91d1\u6df7\u548c\u4e86\u5176\u4ed6\u4e94\u91d1\u6240\u94f8\uff0c\u5251\u8eab\u4e0a\u4e00\u9053\u8840\u75d5\uff0c\u53d1\u51fa\u78a7\u6cb9\u6cb9\u7684\u6697\u5149\uff0c\u6781\u662f\u8be1\u5f02\u3002 \u3000\u3000\u89c2\u770b\u826f\u4e45\uff0c\u5fc3\u4e2d\u9690\u751f\u60e7\u610f\uff0c\u5bfb\u601d\u91d1\u86c7\u90ce\u541b\u6b66\u529f\u5982\u6b64\u9ad8\u5f3a\uff0c\u5f53\u5e74\u624b\u6301\u6b64\u5251\u6a2a\u884c\u6c5f\u6e56\uff0c\u5251\u5203\u4e0d\u77e5\u5df2\u996e\u4e86\u591a\u5c11\u4eba\u8840\u3002\u8fd9\u4e00\u9053\u78a7\u7eff\u7684\u8840\u75d5\uff0c\u4e0d\u77e5\u662f\u4f55\u4eba\u8eab\u4e0a\u7684\u9c9c\u8840\u6240\u5316\uff1f\u662f\u4ec1\u4eba\u4e49\u58eb\uff0c\u8fd8\u662f\u5927\u5978\u5927\u6076\uff1f\u53c8\u8fd8\u662f\u5343\u767e\u4eba\u7684\u9888\u8840\u6240\u51dd\u805a\uff1f \u3000\u3000\u6301\u5251\u5fae\u4e00\u821e\u52a8\uff0c\u767b\u65f6\u660e\u767d\u4e86\u201c\u91d1\u86c7\u5251\u6cd5\u201d\u7684\u602a\u5f02\u4e4b\u5904\uff0c\u539f\u6765\u5251\u5c16\u4e24\u53c9\u65e2\u53ef\u6512\u523a\uff0c\u4ea6\u53ef\u52fe\u9501\u654c\u4eba\u5175\u5203\uff0c\u5012\u62d6\u659c\u6233\uff0c\u7686\u53ef\u4f24\u654c\uff0c\u6bd4\u4e4b\u5bfb\u5e38\u957f\u5251\u589e\u6dfb\u4e86\u4e0d\u5c11\u7528\u6cd5\uff0c\u5148\u524d\u89c9\u5f97\u201c\u91d1\u86c7\u5251\u6cd5\u201d\u4e2d\u9887\u591a\u62db\u5f0f\u751a\u4e0d\u53ef\u89e3\uff0c\u539f\u6765\u7528\u5728\u8fd9\u67c4\u7279\u5f02\u7684\u91d1\u86c7\u5251\u4e0a\uff0c\u5c3d\u6210\u5389\u5bb3\u62db\u672f\u3002 \u3000\u3000\u821e\u5230\u9163\u5904\uff0c\u65e0\u610f\u4e2d\u4e00\u5251\u524a\u5411\u6d1e\u58c1\uff0c\u4e00\u5757\u5ca9\u77f3\u5e94\u624b\u800c\u843d\uff0c\u8fd9\u91d1\u86c7\u5251\u7adf\u662f\u950b\u9510\u7edd\u4f26\u3002\u4ed6\u53c8\u60ca\u53c8\u559c\uff0c\u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u5e76\u672a\u7559\u8a00\u8d60\u6211\u6b64\u5251\uff0c\u6211\u89c1\u6b64\u5b9d\u5251\uff0c\u4fbf\u6b32\u636e\u4e3a\u5df1\u6709\uff0c\u672a\u514d\u8d2a\u5fc3\uff0c\u8fd8\u662f\u8ba9\u5b83\u5728\u6b64\u4f34\u7740\u65e7\u4e3b\u5427\u3002\u201d\u63d0\u8d77\u5251\u6765\uff0c\u594b\u529b\u5411\u77f3\u58c1\u4e0a\u63d2\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u63d2\u4f7f\u5c3d\u4e86\u5168\u529b\uff0c\u5251\u867d\u950b\u9510\uff0c\u4f46\u5251\u8eab\u7ec8\u7a76\u5c1a\u6709\u5c3a\u8bb8\u9732\u5728\u77f3\u5916\uff0c\u672a\u80fd\u53ca\u67c4\u800c\u6b62\u3002\u5251\u5203\u5fae\u5fae\u6447\u6643\uff0c\u5251\u4e0a\u78a7\u7eff\u7684\u8840\u75d5\u6620\u7740\u706b\u5149\uff0c\u4f3c\u4e00\u6761\u6d3b\u86c7\u4e0d\u4f4f\u626d\u52a8\u8eab\u5b50\uff0c\u62da\u547d\u60f3\u94bb\u5165\u77f3\u58c1\u3002 \u3000\u3000\u518d\u770b\u77f3\u58c1\u4e0a\u90a3\u201c\u91cd\u5b9d\u79d8\u672f\uff0c\u4ed8\u4e0e\u6709\u7f18\uff0c\u5165\u6211\u95e8\u6765\uff0c\u9047\u7978\u83ab\u6028\u201d\u90a3\u5341\u516d\u4e2a\u5b57\uff0c\u4e0d\u7531\u5f97\u6014\u6014\u7684\u51fa\u4e86\u795e\uff0c\u5fc3\u60f3\u8fd9\u4f4d\u91d1\u86c7\u524d\u8f88\u4e0d\u77e5\u76f8\u8c8c\u5982\u4f55\uff1f\u4e0d\u77e5\u751f\u5e73\u505a\u8fc7\u591a\u5c11\u60ca\u4e16\u9a87\u4fd7\u7684\u5947\u4e8b\uff1f\u5230\u5934\u6765\u53c8\u4f55\u4ee5\u4f1a\u6b7b\u5728\u8fd9\u5c71\u6d1e\u4e4b\u4e2d\uff1f \u3000\u3000\u4ed6\u91d1\u86c7\u5251\u8fd9\u4e48\u4e00\u63d2\uff0c\u81ea\u77e5\u6b64\u65f6\u4fee\u4e3a\uff0c\u6bd4\u4e4b\u8fd9\u4f4d\u602a\u4fa0\u5c1a\u9887\u6709\u4e0d\u53ca\uff0c\u5bf9\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u6240\u8f7d\u7684\u6b66\u529f\uff0c\u66f4\u589e\u5411\u5f80\uff0c\u800c\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u5fc3\u4e2d\u5bf9\u8fd9\u4f4d\u602a\u4fa0\u53c8\u591a\u4e86\u51e0\u5206\u4eb2\u8fd1\u4e4b\u610f\u3002\u51fa\u5f97\u6d1e\u6765\uff0c\u53c8\u82b1\u4e86\u4e8c\u5341\u591a\u5929\u529f\u592b\uff0c\u5c06\u79d8\u7b08\u4e2d\u6240\u5f55\u7684\u6b66\u529f\u5c3d\u6570\u5b66\u4f1a\u4e86\uff0c\u5176\u4e2d\u53d1\u91d1\u86c7\u9525\u7684\u624b\u6cd5\u5c24\u4e3a\u5947\u5999\uff0c\u4e0e\u6728\u6851\u9053\u4eba\u7684\u6697\u5668\u5fc3\u6cd5\u53ef\u8bf4\u5404\u6709\u5343\u79cb\u3002 \u3000\u3000\u8bfb\u5230\u6700\u540e\u4e09\u9875\uff0c\u53ea\u89c1\u5bc6\u5bc6\u9ebb\u9ebb\u7684\u5199\u6ee1\u4e86\u53e3\u8bc0\uff0c\u53c2\u7167\u524d\u9762\u6240\u8f7d\uff0c\u6709\u4e9b\u5730\u65b9\u53d8\u5316\u7cbe\u5965\uff0c\u9887\u589e\u5999\u609f\uff0c\u4f46\u4e00\u5927\u534a\u5374\u5168\u4e0d\u53ef\u89e3\u3002 \u3000\u3000\u57cb\u5934\u7ec6\u8bfb\u8fd9\u4e09\u9875\u53e3\u8bc0\uff0c\u82e6\u601d\u4e86\u4e24\u5929\uff0c\u603b\u89c9\u5176\u4e2d\u77db\u76fe\u767e\u51fa\uff0c\u5fc5\u5b9a\u53e6\u6709\u5173\u952e\uff0c\u4f46\u628a\u4e00\u672c\u79d8\u7b08\u7ffb\u6765\u8986\u53bb\u7684\u7ec6\u770b\uff0c\u6240\u6709\u529f\u8bc0\u6cd5\u95e8\u5b9e\u5df2\u5168\u90e8\u719f\u8bfb\u9886\u4f1a\uff0c\u66f4\u65e0\u9057\u6f0f\u3002\u4ed6\u91cd\u5165\u5c71\u6d1e\uff0c\u7ec6\u770b\u58c1\u4e0a\u56fe\u5f62\uff0c\u4ecd\u662f\u96be\u4ee5\u7d22\u89e3\u3002 \u3000\u3000\u8fd9\u5929\u665a\u4e0a\uff0c\u4ed6\u56e0\u53c2\u7a76\u4e0d\u51fa\u5176\u4e2d\u9053\u7406\uff0c\u5728\u5e8a\u4e0a\u7ffb\u6765\u8986\u53bb\uff0c\u59cb\u7ec8\u7761\u4e0d\u5b89\u7a33\uff0c\u53ea\u89c1\u7a97\u5916\u4e00\u8f6e\u660e\u6708\u5c04\u8fdb\u5ba4\u6765\uff0c\u7167\u5f97\u6ee1\u5730\u94f6\u5149\uff0c\u5ffd\u60f3\uff1a\u201c\u6211\u6df7\u5143\u529f\u65e9\u5df2\u7ec3\u6210\uff0c\u4e3a\u4e86\u8fd9\u90e8\u91d1\u86c7\u79d8\u7b08\uff0c\u5374\u5728\u5c71\u4e0a\u591a\u803d\u4e86\u4e24\u4e2a\u6708\u529f\u592b\uff0c\u53ea\u6015\u5e08\u7236\u4e45\u7b49\u4e0d\u81f3\uff0c\u4e3a\u6211\u62c5\u5fc3\u3002\u5e08\u7236\u66fe\u8bf4\u91d1\u86c7\u90ce\u541b\u4e3a\u4eba\u602a\u50fb\uff0c\u4ed6\u7684\u4e66\u89c2\u4e4b\u65e0\u76ca\u3002\u6211\u4e00\u65f6\u597d\u5947\u5fc3\u8d77\uff0c\u5b66\u4e86\u4e66\u4e0a\u6b66\u529f\uff0c\u5e08\u7236\u8bf4\u4e0d\u5b9a\u4f1a\u5927\u4e0d\u9ad8\u5174\u3002\u6211\u53c8\u4f55\u5fc5\u82e6\u601d\u7126\u8651\uff0c\u53bb\u63a2\u7d22\u8fd9\u65c1\u95e8\u529f\u592b\u4e2d\u7684\u4e0d\u89e3\u4e4b\u5904\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u6b66\u5b66\u4fee\u4e3a\u65e2\u5230\u5982\u6b64\u5883\u754c\uff0c\u89c1\u5230\u9ad8\u6df1\u7684\u6b66\u529f\u79d8\u5965\u800c\u7adf\u4e0d\u63a2\u7d22\u5230\u5e95\uff0c\u5b9e\u6240\u96be\u80fd\uff0c\u5fc3\u60f3\uff1a\u201c\u773c\u4e0d\u89c1\u4e3a\u51c0\uff0c\u6211\u4e00\u628a\u706b\u5c06\u5b83\u70e7\u4e86\u4fbf\u662f\u3002\u201d\u4e3b\u610f\u5df2\u5b9a\uff0c\u4e0b\u7095\u6765\u70b9\u4eae\u6cb9\u706f\uff0c\u62ff\u8d77\u79d8\u7b08\u653e\u5728\u706f\u4e0a\u711a\u70e7\u3002\u4f46\u70e7\u4e86\u826f\u4e45\uff0c\u90a3\u4e66\u7684\u5c01\u9762\u53ea\u85b0\u5f97\u4e00\u7247\u4e4c\u9ed1\uff0c\u7adf\u662f\u4e0d\u80fd\u7740\u706b\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5927\u5947\uff0c\u7528\u529b\u62c9\u626f\uff0c\u90a3\u4e66\u5c45\u7136\u7eb9\u4e1d\u4e0d\u52a8\u3002\u4ed6\u6b64\u65f6\u6df7\u5143\u529f\u5df2\u6210\uff0c\u53cc\u624b\u5177\u6781\u5f3a\u5185\u5bb6\u52b2\u529b\uff0c\u8fd9\u4e00\u626f\u529b\u9053\u975e\u540c\u5c0f\u53ef\uff0c\u5c31\u662f\u94c1\u7247\u4e5f\u8981\u62c9\u957f\uff0c\u4e0d\u6599\u60f3\u8fd9\u4e66\u5c45\u7136\u4e0d\u635f\uff0c\u60c5\u77e5\u5fc5\u6709\u53e4\u602a\uff0c\u7ec6\u52a0\u5ba1\u89c6\uff0c\u539f\u6765\u5c01\u9762\u662f\u4ee5\u4e4c\u91d1\u4e1d\u548c\u4e0d\u77e5\u751a\u4e48\u7ec6\u7ebf\u7ec7\u6210\uff0c\u5171\u6709\u4e24\u5c42\u3002\u4ed6\u62ff\u5c0f\u5200\u5272\u65ad\u9489\u4e66\u7684\u4e1d\u7ebf\uff0c\u62c6\u4e0b\u5c01\u9762\uff0c\u518d\u628a\u79d8\u7b08\u5728\u706b\u4e0a\u711a\u70e7\uff0c\u8fd9\u4e00\u4e0b\u767b\u65f6\u706b\u5149\u718a\u718a\uff0c\u628a\u91d1\u86c7\u90ce\u541b\u5e73\u751f\u7edd\u5b66\u70e7\u6210\u4e86\u7070\u70ec\u3002 \u3000\u3000\u518d\u770b\u90a3\u4e66\u5c01\u9762\uff0c\u5939\u5c42\u4e4b\u4e2d\u4f3c\u4e4e\u53e6\u6709\u522b\u7269\uff0c\u7ec6\u5fc3\u6311\u5f00\u4e24\u5c42\u4e4b\u95f4\u8fde\u7cfb\u7684\u91d1\u4e1d\uff0c\u679c\u7136\u4e2d\u95f4\u85cf\u6709\u4e24\u5f20\u7eb8\u7b3a\u3002 \u3000\u3000\u4e00\u5f20\u7eb8\u4e0a\u5199\u7740\uff1a\u201c\u91cd\u5b9d\u4e4b\u56fe\u201d\u56db\u5b57\uff0c\u65c1\u8fb9\u753b\u4e86\u4e00\u5e45\u5730\u56fe\uff0c\u53c8\u6709\u8bb8\u591a\u8bb0\u53f7\u3002\u56fe\u540e\u5199\u7740\u4e24\u884c\u5b57\uff1a\u201c\u5f97\u5b9d\u4e4b\u4eba\uff0c\u52a1\u8bf7\u8d74\u6d59\u6c5f\u8862\u5dde\u77f3\u6881\uff0c\u5bfb\u8bbf\u5973\u5b50\u6e29\u4eea\uff0c\u8d60\u4ee5\u9ec4\u91d1\u5341\u4e07\u4e24\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u8bdd\u53e3\u6c14\u597d\u5927\uff01\u201d\u53ea\u89c1\u7b3a\u672b\u53c8\u6709\u4e24\u884c\u5c0f\u5b57\uff1a\u201c\u6b64\u65f6\u7eb5\u805a\u5929\u4e0b\u73cd\u5b9d\uff0c\u4ea6\u7109\u5f97\u4ee5\u6613\u534a\u65e5\u805a\u9996\uff1f\u91cd\u8d22\u5b9d\u800c\u8f7b\u522b\u79bb\uff0c\u611a\u4e4b\u6781\u77e3\uff0c\u6094\u751a\u6068\u751a\uff01\u201d \u3000\u3000\u51dd\u601d\u534a\u664c\uff0c\u4e0d\u660e\u5176\u610f\u3002 \u3000\u3000\u53e6\u4e00\u5f20\u7eb8\u7b3a\u4e0a\u5199\u7684\uff0c\u5374\u5bc6\u5bc6\u7684\u90fd\u662f\u6b66\u529f\u8bc0\u8981\uff0c\u4e0e\u79d8\u7b08\u4e2d\u4e0d\u89e3\u4e4b\u5904\u4e00\u52a0\u53c2\u7167\uff0c\u767b\u65f6\u8c41\u7136\u8d2f\u901a\uff0c\u679c\u7136\u5999\u7528\u65e0\u7a77\u3002 \u3000\u3000\u4ed6\u773c\u671b\u5929\u4e0a\u660e\u6708\uff0c\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u79cd\u79cd\u6b66\u529f\u79d8\u5965\uff0c\u6709\u5982\u4e00\u9053\u6f84\u6f88\u7684\u5c0f\u6eaa\uff0c\u7f13\u7f13\u5728\u5fc3\u4e2d\u6d41\u8fc7\uff0c\u6e05\u53ef\u89c1\u5e95\uff0c\u66f4\u5148\u534a\u5206\u6e23\u6ed3\uff0c\u76f4\u5230\u7ea2\u65e5\u6ee1\u7a97\uff0c\u8fd9\u624d\u9192\u89c9\u3002\u53ea\u662f\u8fd9\u4e9b\u6b66\u529f\u4f3c\u4e4e\u8fc7\u4efd\u7e41\u590d\uff0c\u82b1\u5de7\u592a\u591a\uff0c\u60f3\u6765\u90a3\u662f\u91d1\u86c7\u90ce\u541b\u7684\u5929\u6027\u4f7f\u7136\uff0c\u559c\u5728\u5e73\u6613\u5904\u5f04\u5f97\u5cf0\u56de\u8def\u8f6c\uff0c\u4f7f\u4eba\u773c\u82b1\u64a9\u4e71\u3002 \u3000\u3000\u7ecf\u6b64\u4e00\u665a\u82e6\u601d\uff0c\u4e0d\u4f46\u901a\u89e3\u4e86\u91d1\u86c7\u90ce\u541b\u7684\u9057\u6cd5\uff0c\u800c\u5bf9\u5e08\u7236\u53ca\u6728\u6851\u9053\u4eba\u6240\u6388\u8bf8\u822c\u4e0a\u4e58\u6b66\u529f\uff0c\u4e5f\u6709\u66f4\u6df1\u4e00\u5c42\u4f53\u4f1a\u3002 \u3000\u3000\u4ed6\u671b\u7740\u4e24\u9875\u767d\u7b3a\uff0c\u4e00\u5806\u7070\u70ec\uff0c\u5446\u5446\u51fa\u795e\uff0c\u6697\u53f9\u91d1\u86c7\u90ce\u541b\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u4e00\u81f3\u4e8e\u65af\uff0c\u6545\u610f\u5728\u79d8\u7b08\u4e2d\u7559\u4e0b\u4ee4\u4eba\u4e0d\u89e3\u4e4b\u5904\uff0c\u8bf1\u4f7f\u5f97\u5230\u79d8\u7b08\u4e4b\u4eba\u523b\u610f\u63a2\u7d22\uff0c\u7ec8\u4e8e\u627e\u5230\u85cf\u5b9d\u5730\u56fe\u3002\u5982\u679c\u79d8\u7b08\u843d\u5165\u5eb8\u4eba\u4e4b\u624b\uff0c\u4e0d\u53bb\u94bb\u7814\u6b66\u529f\u7684\u7cbe\u5fae\uff0c\u90a3\u4e48\u591a\u534a\u4e5f\u4e0d\u4f1a\u53d1\u73b0\u5730\u56fe\u3002\u4ed6\u628a\u4e24\u5f20\u7eb8\u7b3a\u4ecd\u7136\u5939\u5728\u4e24\u7247\u5c01\u9762\u4e4b\u95f4\uff0c\u518d\u53bb\u5c71\u6d1e\u53d6\u51fa\u91d1\u86c7\u5251\u6765\uff0c\u7ec3\u719f\u4e86\u5251\u6cd5\uff0c\u624d\u5c06\u91d1\u86c7\u5251\u63d2\u8fd8\u539f\u5904\u3002 \u3000\u3000\u53c8\u8fc7\u4e24\u65e5\uff0c\u8881\u627f\u5fd7\u6536\u62fe\u884c\u88c5\uff0c\u4e0e\u54d1\u5df4\u544a\u522b\u3002\u4ed6\u5728\u5c71\u4e0a\u4f4f\u4e86\u5341\u5e74\uff0c\u5ffd\u7136\u79bb\u53bb\uff0c\u5fc3\u4e0b\u96be\u8fc7\u3002\u5927\u5a01\u4e0e\u5c0f\u4e56\u9887\u901a\u7075\u6027\uff0c\u62c9\u4f4f\u4e86\u5431\u5431\u4e71\u53eb\uff0c\u4e0d\u653e\u4ed6\u8d70\u3002\u8881\u627f\u5fd7\u66f4\u662f\u96be\u5206\u96be\u820d\u3002\u54d1\u5df4\u5e26\u4e86\u4e24\u5934\u7329\u7329\u76f4\u9001\u5230\u5c71\u4e0b\uff0c\u8fd9\u624d\u6d12\u6cea\u800c\u522b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u827a\u6210\u4e0b\u5c71\uff0c\u6240\u95fb\u6240\u89c1\uff0c\u4ff1\u89c9\u65b0\u5947\uff0c\u53ea\u89c1\u4e00\u8def\u884c\u6765\uff0c\u89c1\u767e\u59d3\u4eba\u4eba\u8863\u670d\u8934\u891b\uff0c\u997f\u5f97\u9762\u9ec4\u9965\u7626\u3002\u884c\u51fa\u767e\u4f59\u91cc\u540e\uff0c\u89c1\u6570\u5341\u540d\u767e\u59d3\u5728\u5c71\u95f4\u6316\u6398\u6811\u6839\u800c\u98df\u3002\u4ed6\u8eab\u8fb9\u6709\u4e9b\u5e08\u7236\u7559\u4e0b\u7684\u94f6\u4e24\uff0c\u5374\u4e5f\u65e0\u5904\u53ef\u4e70\u98df\u7269\uff0c\u53ea\u5f97\u65bd\u5c55\u6b66\u529f\uff0c\u6355\u6349\u9e1f\u517d\u4e3a\u98df\u3002\u53c8\u884c\u6570\u5341\u91cc\uff0c\u53ea\u89c1\u5012\u6bd9\u7684\u9965\u6c11\u4e0d\u7edd\u4e8e\u9014\uff0c\u751a\u611f\u51c4\u607b\u3002 \u3000\u3000\u884c\u4e86\u6570\u65e5\uff0c\u5c06\u5230\u5c71\u897f\u5883\u5185\uff0c\u7adf\u89c1\u9965\u6c11\u5728\u716e\u4e86\u997f\u6b7b\u7684\u6b7b\u5c38\u6765\u5403\uff0c\u4ed6\u4e0d\u6562\u591a\u770b\uff0c\u75be\u884c\u800c\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u6765\u5230\u4e00\u5904\u5e02\u9547\uff0c\u53ea\u89c1\u9965\u6c11\u5927\u96c6\uff0c\u9f50\u58f0\u9ad8\u5531\uff0c\u5531\u7684\u662f\uff1a\u201c\u5403\u4ed6\u5a18\uff0c\u7a7f\u4ed6\u5a18\uff0c\u5f00\u4e86\u5927\u95e8\u8fce\u95ef\u738b\u3002\u95ef\u738b\u6765\u65f6\u4e0d\u7eb3\u7cae\u3002\u201d \u3000\u3000\u201c\u671d\u6c42\u5347\uff0c\u5e55\u6c42\u5408\uff0c\u8fd1\u6765\u8d2b\u6c49\u96be\u6c42\u6d3b\u3002\u65e9\u65e9\u5f00\u95e8\u62dc\u95ef\u738b\uff0c\u7ba1\u6559\u5927\u5bb6\u90fd\u6b22\u60a6\u3002\u201d \u3000\u3000\u4e00\u540d\u519b\u5b98\u5e26\u4e86\u5341\u591a\u540d\u5175\u5352\uff0c\u5927\u58f0\u5406\u559d\uff1a\u201c\u4f60\u4eec\u5531\u8fd9\u79cd\u9020\u53cd\u7684\u6b4c\u513f\uff0c\u4e0d\u6015\u6740\u5934\u5417\uff1f\u201d\u6325\u52a8\u97ad\u5b50\uff0c\u5411\u4f17\u767e\u59d3\u4e71\u6253\u3002 \u3000\u3000\u4f17\u9965\u6c11\u53eb\u9053\uff1a\u201c\u95ef\u738b\u4e0d\u6765\uff0c\u5927\u5bb6\u90fd\u662f\u997f\u6b7b\uff0c\u6211\u4eec\u6b63\u662f\u8981\u9020\u53cd\uff01\u201d\u4e00\u62e5\u800c\u4e0a\uff0c\u6293\u4f4f\u4e86\u5b98\u5175\uff0c\u6709\u7684\u6253\uff0c\u6709\u7684\u54ac\uff0c\u767b\u65f6\u5c06\u5341\u591a\u540d\u5b98\u5175\u6d3b\u6d3b\u6253\u6b7b\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4e86\u8fd9\u7b49\u60c5\u666f\uff0c\u5fc3\u60f3\uff1a\u201c\u65e0\u602a\u95ef\u738b\u58f0\u52bf\u65e5\u76db\u3002\u767e\u59d3\u9965\u4e0d\u5f97\u98df\uff0c\u4e5f\u53ea\u597d\u6740\u5b98\u9020\u53cd\u4e86\u3002\u201d\u5411\u4e00\u540d\u9965\u6c11\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u54e5\uff0c\u53ef\u77e5\u95ef\u738b\u662f\u5728\u54ea\u91cc\uff0c\u6211\u60f3\u524d\u53bb\u76f8\u6295\u3002\u201d\u90a3\u9965\u6c11\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u95ef\u738b\u5927\u519b\u773c\u4e0b\u5728\u8944\u9675\u3001\u95fb\u559c\u4e00\u5e26\uff0c\u4e0d\u4e45\u5c31\u8981\u8fc7\u6765\u3002\u6211\u4eec\u5927\u4f19\u4e5f\u6b63\u8981\u53bb\u6295\u519b\u5462\u3002\u201d\u8881\u627f\u5fd7\u53c8\u95ee\uff1a\u201c\u521a\u624d\u542c\u5f97\u5927\u5bb6\u5531\u7684\u6b4c\u513f\u751a\u597d\uff0c\u6b64\u5916\u8fd8\u6709\u6ca1\u6709\uff1f\u201d\u90a3\u9965\u6c11\u9053\uff1a\u201c\u8fd8\u6709\u597d\u591a\u5462\u3002\u90a3\u90fd\u662f\u95ef\u738b\u90e8\u4e0b\u7684\u674e\u516c\u5b50\u6240\u4f5c\u3002\u201d\u4e8e\u662f\u53c8\u5531\u4e86\u51e0\u9996\uff0c\u6b4c\u610f\u90fd\u662f\u529d\u4eba\u6740\u5b98\u9020\u53cd\uff0c\u8fce\u63a5\u95ef\u738b\u3002\u8881\u627f\u5fd7\u6cbf\u9014\u6253\u542c\uff0c\u5728\u9ec4\u6cb3\u8fb9\u4e0a\u9047\u5230\u4e86\u5c0f\u90e8\u95ef\u519b\u3002\u5e26\u5175\u7684\u9996\u9886\u542c\u8bf4\u662f\u6765\u627e\u95ef\u738b\u7684\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u5fd9\u6d3e\u4eba\u966a\u4ed6\u5230\u674e\u81ea\u6210\u519b\u4e2d\u3002 \u3000\u3000\u95ef\u738b\u542c\u5f97\u662f\u795e\u5251\u4ed9\u733f\u7a46\u4eba\u6e05\u7684\u5f1f\u5b50\u5230\u6765\uff0c\u867d\u5728\u519b\u52a1\u5025\u506c\u4e4b\u9645\uff0c\u4ecd\u7136\u4eb2\u81ea\u63a5\u89c1\u3002\u8881\u627f\u5fd7\u89c1\u4ed6\u6c14\u5ea6\u5a01\u731b\uff0c\u795e\u8272\u548c\u853c\uff0c\u751a\u662f\u656c\u4f69\u3002\u95ef\u738b\u8bf4\u4ed6\u5e08\u7236\u53bb\u4e86\u6c5f\u5357\uff0c\u60f3\u662f\u7a46\u4eba\u6e05\u5728\u8a00\u8bed\u4e2d\u5bf9\u81ea\u5df1\u8fd9\u7231\u5f92\u9887\u4e3a\u5956\u8bb8\uff0c\u662f\u4ee5\u95ef\u738b\u5bf9\u4ed6\u751a\u52a0\u5668\u91cd\uff0c\u8a00\u4e0b\u9887\u6709\u62db\u63fd\u4e4b\u610f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u5f97\u5e08\u7236\u4e0d\u5728\uff0c\u767b\u65f6\u5ffd\u5ffd\u4e0d\u4e50\uff0c\u518d\u95ee\u8d77\u5d14\u79cb\u5c71\uff0c\u5219\u662f\u548c\u7a46\u4eba\u6e05\u540c\u5230\u6c5f\u5357\u82cf\u676d\u4e00\u5e26\u7b79\u63aa\u519b\u9977\u53bb\u4e86\u3002\u8881\u627f\u5fd7\u8bf4\u8981\u53bb\u5bfb\u5e08\uff0c\u7980\u660e\u5e08\u7236\u4e4b\u540e\uff0c\u518d\u6765\u6548\u529b\u3002\u95ef\u738b\u4e5f\u4e0d\u52c9\u5f3a\uff0c\u547d\u5236\u5c06\u519b\u674e\u5ca9\u63a5\u5f85\uff0c\u53c8\u9001\u4e86\u4e94\u5341\u4e24\u94f6\u5b50\u4f5c\u8def\u8d39\u3002\u8881\u627f\u5fd7\u8c22\u8fc7\u53d7\u4e86\u3002 \u3000\u3000\u90a3\u674e\u5ca9\u867d\u662f\u95ef\u519b\u4e2d\u5e26\u5175\u7684\u5c06\u5b98\uff0c\u4f46\u8eab\u7a7f\u4e66\u751f\u670d\u8272\uff0c\u8c08\u5410\u5112\u96c5\u3002\u539f\u6765\u4ed6\u662f\u524d\u5175\u90e8\u5c1a\u4e66\u674e\u7cbe\u767d\u4e4b\u5b50\uff0c\u672c\u662f\u4e3e\u4eba\uff0c\u56e0\u8d48\u6d4e\u707e\u6c11\uff0c\u5f97\u7f6a\u4e86\u53bf\u5b98\u548c\u5bcc\u5ba4\uff0c\u88ab\u8bec\u9677\u5165\u72f1\u3002\u6709\u4e00\u4f4d\u5973\u4fa0\u4ef0\u6155\u4ed6\u4e3a\u4eba\uff0c\u7387\u9886\u707e\u6c11\u653b\u7834\u7262\u72f1\uff0c\u6551\u4e86\u4ed6\u51fa\u6765\u3002\u90a3\u5973\u4fa0\u7231\u7a7f\u7ea2\u8863\uff0c\u4f17\u4eba\u53eb\u5979\u4e3a\u7ea2\u5a18\u5b50\u3002\u674e\u5ca9\u5b9e\u903c\u5904\u6b64\uff0c\u5df2\u975e\u9020\u53cd\u4e0d\u53ef\uff0c\u4fbf\u548c\u7ea2\u5a18\u5b50\u7ed3\u6210\u592b\u5987\uff0c\u6295\u5165\u95ef\u738b\u519b\u4e2d\uff0c\u732e\u8bae\u5747\u7530\u514d\u8d4b\uff0c\u5584\u5f85\u767e\u59d3\u3002\u95ef\u738b\u8a00\u542c\u8ba1\u4ece\uff0c\u6781\u4e3a\u91cd\u7528\u3002\u95ef\u519b\u672c\u4e3a\u9965\u6c11\u3001\u53db\u5352\u6240\u805a\uff0c\u9020\u53cd\u53ea\u4e0d\u8fc7\u4e3a\u6c42\u4e00\u9971\uff0c\u539f\u65e0\u5927\u5fd7\uff0c\u6240\u5230\u4e4b\u5904\uff0c\u4e0d\u514d\u52ab\u63a0\uff0c\u56e0\u4e4b\u4eba\u5fc3\u4e0d\u9644\uff0c\u4e1c\u897f\u6d41\u7a9c\uff0c\u65f6\u80dc\u65f6\u8d25\uff0c\u59cb\u7ec8\u96be\u6210\u6c14\u5019\u3002\u81ea\u5f97\u674e\u5ca9\u5f52\u9644\uff0c\u674e\u81ea\u6210\u6574\u987f\u519b\u7eaa\uff0c\u4e25\u7981\u6ee5\u6740\u5978\u6deb\uff0c\u767b\u65f6\u519b\u52bf\u5927\u632f\u3002 \u3000\u3000\u674e\u5ca9\u6cbb\u519b\u4e25\u6574\uff0c\u53c8\u7f16\u4e86\u8bb8\u591a\u6b4c\u513f\uff0c\u4ee4\u4eba\u6559\u5c0f\u513f\u4f20\u5531\uff0c\u56db\u5904\u6d41\u64ad\u3002\u767e\u59d3\u6b63\u81ea\u9965\u4e0d\u5f97\u98df\uff0c\u5b98\u5e9c\u53c8\u6765\u62f7\u6253\u903c\u7cae\uff0c\u4e00\u542c\u8bf4\u201c\u95ef\u738b\u6765\u65f6\u4e0d\u7eb3\u7cae\u201d\uff0c\u81ea\u662f\u4eba\u4eba\u62e5\u6234\u3002\u56e0\u6b64\u95ef\u519b\u672a\u5230\uff0c\u6709\u4e9b\u57ce\u6c60\u5df2\u4e0d\u653b\u81ea\u7834\u3002 \u3000\u3000\u674e\u5ca9\u5bf9\u8881\u5d07\u7115\u5411\u6765\u656c\u4ef0\uff0c\u542c\u8bf4\u8881\u7763\u5e08\u7684\u516c\u5b50\u5230\u6765\uff0c\u76f8\u5f85\u5c3d\u793c\uff0c\u63a5\u5165\u8425\u4e2d\uff0c\u8bf7\u592b\u4eba\u7ea2\u5a18\u5b50\u51fa\u89c1\u3002\u90a3\u7ea2\u5a18\u5b50\u82f1\u98ce\u723d\u6717\uff0c\u8c6a\u8fc8\u4e0d\u8ba9\u987b\u7709\u3002\u4e09\u4eba\u8a00\u8c08\u6295\u673a\uff0c\u5f53\u771f\u662f\u4e00\u89c1\u5982\u6545\u3002\u8881\u627f\u5fd7\u9664\u6b66\u529f\u4e00\u95e8\u4e4b\u5916\uff0c\u89c1\u8bc6\u751a\u6d45\u3002\u674e\u5ca9\u548c\u7ea2\u5a18\u5b50\u8ddf\u4ed6\u7eb5\u8c08\u5929\u4e0b\u5927\u52bf\uff0c\u8881\u627f\u5fd7\u5f53\u771f\u8305\u585e\u987f\u5f00\u3002\u5728\u674e\u5ca9\u8425\u4e2d\u7559\u4e86\u4e09\u65e5\uff0c\u76f4\u81f3\u95ef\u519b\u8981\u62d4\u8425\u5317\u4e0a\uff0c\u8fd9\u624d\u4f9d\u4f9d\u4f5c\u522b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u521d\u51fa\u8305\u5e90\uff0c\u5bf9\u674e\u5ca9\u7684\u98ce\u4eea\u4e3a\u4eba\uff0c\u6697\u751f\u6a21\u4eff\u4e4b\u5fc3\uff0c\u8fc7\u5f97\u6f7c\u5173\uff0c\u4fbf\u53bb\u4e70\u4e86\u4e00\u5957\u4e66\u751f\u8863\u5dfe\uff0c\u5b66\u7740\u4e5f\u4f5c\u4e66\u751f\u6253\u626e\uff0c\u5f84\u6765\u6c5f\u5357\u5bfb\u8bbf\u5e08\u7236\u3002 \u3000\u3000\u6c5f\u5357\u5730\u65b9\u5bcc\u5eb6\uff0c\u867d\u7136\u5b98\u540f\u4e00\u822c\u7684\u8d2a\u6c61\u8650\u6c11\uff0c\u4f46\u4f17\u767e\u59d3\u5c1a\u582a\u6e29\u9971\uff0c\u6bd4\u4e4b\u79e6\u664b\u9965\u6c11\u7684\u82e6\u51b5\uff0c\u5374\u662f\u5982\u5728\u5929\u5802\u4e86\u3002 \u3000\u3000\u8fd9\u65e5\u6765\u5230\u8d63\u4e1c\u7389\u5c71\uff0c\u5403\u8fc7\u996d\u540e\uff0c\u5230\u7801\u5934\u53bb\u642d\u8239\u4e1c\u884c\uff0c\u89c1\u6c5f\u8fb9\u505c\u4e86\u4e00\u8258\u5927\u8239\uff0c\u76f8\u95ee\u4e4b\u4e0b\uff0c\u8bf4\u662f\u4e0a\u9976\u4e00\u4e2a\u5bcc\u5546\u5305\u4e86\u5230\u6d59\u6c5f\u91d1\u534e\u53bb\u529e\u8d27\u7684\uff0c\u8881\u627f\u5fd7\u4fbf\u6c42\u9644\u8f7d\u3002\u8239\u8001\u5927\u8d2a\u7740\u591a\u5f97\u51e0\u4e2a\u8239\u94b1\uff0c\u548c\u5305\u8239\u7684\u5bcc\u5546\u9f99\u5fb7\u90bb\u5546\u91cf\u3002\u9f99\u5fb7\u90bb\u89c1\u4ed6\u662f\u4e2a\u5112\u751f\uff0c\u4e5f\u5c31\u5141\u4e86\u3002 \u3000\u3000\u8239\u8001\u5927\u6b63\u8981\u62d4\u7bd9\u5f00\u822a\uff0c\u5ffd\u7136\u7801\u5934\u4e0a\u5306\u5306\u5954\u6765\u4e00\u4e2a\u5c11\u5e74\uff0c\u53eb\u9053\uff1a\u201c\u8239\u8001\u5927\uff0c\u6211\u6709\u6025\u4e8b\u8981\u53bb\u8862\u5dde\uff0c\u8bf7\u4f60\u884c\u4e2a\u65b9\u4fbf\uff0c\u591a\u642d\u6211\u4e00\u4eba\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u8fd9\u4eba\u58f0\u97f3\u6e05\u8106\u60a6\u8033\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u5fc3\u60f3\uff1a\u201c\u4e16\u4e0a\u7adf\u6709\u5982\u6b64\u7f8e\u8c8c\u5c11\u5e74\uff1f\u201d\u8fd9\u4eba\u5341\u516b\u4e5d\u5c81\u5e74\u7eaa\uff0c\u7a7f\u4e00\u4ef6\u77f3\u9752\u8272\u957f\u886b\uff0c\u5934\u9876\u9752\u5dfe\u4e0a\u9576\u7740\u5757\u767d\u7389\uff0c\u8863\u5c65\u7cbe\u96c5\uff0c\u80cc\u8d1f\u5305\u88f9\uff0c\u76ae\u8272\u767d\u817b\uff0c\u4e00\u5f20\u8138\u767d\u91cc\u900f\u7ea2\uff0c\u4fca\u79c0\u5f02\u5e38\u3002\u9f99\u5fb7\u90bb\u4e5f\u89c1\u8fd9\u5c11\u5e74\u670d\u9970\u534e\u8d35\uff0c\u4eba\u624d\u51fa\u4f17\uff0c\u5fc3\u751f\u597d\u611f\uff0c\u547d\u8239\u8001\u5927\u653e\u4e0b\u8df3\u677f\uff0c\u628a\u4ed6\u63a5\u4e0a\u8239\u6765\u3002 \u3000\u3000\u90a3\u9752\u886b\u5c11\u5e74\u4e00\u8e0f\u4e0a\u8239\uff0c\u90a3\u8239\u4fbf\u5fae\u5fae\u4e00\u6c89\uff0c\u8881\u627f\u5fd7\u5fc3\u4e0b\u6697\u5947\uff0c\u77a7\u4ed6\u8eab\u5f62\u7626\u5f31\uff0c\u4e0d\u8fc7\u767e\u65a4\u4e0a\u4e0b\uff0c\u4f46\u8fd9\u8239\u4e00\u6c89\u4e4b\u52bf\uff0c\u5374\u4f3c\u6709\u4e24\u767e\u591a\u65a4\u91cd\u7269\u538b\u4e0a\u4e00\u822c\uff0c\u4ed6\u80cc\u4e0a\u5305\u88f9\u4e0d\u5927\uff0c\u600e\u4f1a\u5982\u6b64\u6c89\u91cd\uff1f \u3000\u3000\u90a3\u5c11\u5e74\u4e0a\u8239\u4e4b\u540e\uff0c\u8239\u5c31\u5f00\u4e86\u3002 \u3000\u3000\u90a3\u9752\u886b\u5c11\u5e74\u8d70\u8fdb\u4e2d\u8231\uff0c\u4e0e\u9f99\u5fb7\u90bb\u3001\u8881\u627f\u5fd7\u89c1\u793c\uff0c\u81ea\u79f0\u59d3\u6e29\u540d\u9752\uff0c\u56e0\u5f97\u77e5\u6bcd\u4eb2\u75c5\u91cd\uff0c\u662f\u4ee5\u8d76\u7740\u56de\u53bb\u63a2\u671b\uff0c\u4ed6\u89c1\u4e86\u9f99\u5fb7\u90bb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4e00\u53cc\u79c0\u76ee\uff0c\u5374\u4e0d\u4f4f\u5411\u8881\u627f\u5fd7\u6253\u91cf\uff0c\u95ee\u9053\uff1a\u201c\u542c\u8881\u5144\u53e3\u97f3\uff0c\u597d\u4f3c\u4e0d\u662f\u672c\u5730\u4eba\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5c0f\u5f1f\u539f\u7c4d\u5e7f\u4e1c\uff0c\u4ece\u5c0f\u5728\u9655\u897f\u5c45\u4f4f\uff0c\u6c5f\u5357\u8fd8\u662f\u751f\u5e73\u7b2c\u4e00\u6b21\u6765\u3002\u201d\u6e29\u9752\u95ee\u9053\uff1a\u201c\u8881\u5144\u53bb\u6d59\u6c5f\u6709\u4f55\u8d35\u5e72\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u662f\u53bb\u63a2\u8bbf\u4e00\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u4e24\u8258\u5c0f\u8239\u8fd0\u6a79\u5982\u98de\uff0c\u4ece\u5750\u8239\u4e24\u65c1\u62a2\u4e86\u8fc7\u53bb\u3002\u6e29\u9752\u773c\u775b\u76ef\u7740\u5c0f\u8239\uff0c\u76f4\u671b\u7740\u4e24\u8239\u8f6c\u4e86\u4e00\u4e2a\u5f2f\uff0c\u88ab\u524d\u9762\u7684\u5c71\u5d16\u6321\u4f4f\uff0c\u8fd9\u624d\u4e0d\u770b\u3002 \u3000\u3000\u5403\u4e2d\u996d\u65f6\uff0c\u9f99\u5fb7\u90bb\u5f88\u662f\u597d\u5ba2\uff0c\u9080\u8bf7\u4e24\u4eba\u540c\u5403\u3002\u8881\u627f\u5fd7\u4e00\u9910\u8981\u5403\u4e09\u4eba\u7897\uff0c\u9e21\u9c7c\u852c\u83dc\u90fd\u5403\u4e86\u4e0d\u5c11\uff0c\u6e29\u9752\u5374\u53ea\u5403\u4e00\u7897\uff0c\u751a\u662f\u79c0\u6c14\u6587\u96c5\u3002 \u3000\u3000\u521a\u5403\u8fc7\u996d\uff0c\u53ea\u542c\u5f97\u6c34\u58f0\u54cd\u52a8\uff0c\u53c8\u662f\u4e24\u8258\u5c0f\u8239\u62a2\u8fc7\u8239\u65c1\u3002\u4e00\u8258\u5c0f\u8239\u8239\u5934\u7ad9\u7740\u4e00\u540d\u5927\u6c49\uff0c\u671b\u7740\u5927\u8239\u72e0\u72e0\u7684\u77aa\u4e86\u51e0\u773c\u3002\u6e29\u9752\u79c0\u7709\u4e00\u7ad6\uff0c\u6ee1\u8138\u6012\u8272\u3002\u8881\u627f\u5fd7\u5fc3\u611f\u5947\u602a\uff1a\u201c\u4ed6\u4e3a\u751a\u4e48\u89c1\u4e86\u8fd9\u4e24\u8258\u5c0f\u8239\u751f\u6c14\uff1f\u201d\u6e29\u9752\u4f3c\u4e4e\u5bdf\u89c9\u5230\u4e86\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8138\u8272\u767b\u8f6c\u67d4\u548c\uff0c\u63a5\u8fc7\u8239\u4f19\u6ce1\u4e0a\u6765\u7684\u4e00\u676f\u8336\uff0c\u555c\u4e86\u4e00\u53e3\uff0c\u4f3c\u5acc\u8336\u53f6\u7c97\u6da9\uff0c\u76b1\u4e86\u7709\u5934\uff0c\u628a\u8336\u676f\u653e\u5728\u684c\u4e0a\u3002 \u3000\u3000\u5230\u4e86\u508d\u665a\uff0c\u8239\u5728\u4e00\u4e2a\u5e02\u9547\u8fb9\u505c\u6cca\u4e86\u3002\u8881\u627f\u5fd7\u60f3\u4e0a\u5cb8\u6e38\u89c8\uff0c\u9f99\u5fb7\u90bb\u4e0d\u80af\u79bb\u5f00\u8d27\u7269\uff0c\u9080\u6e29\u9752\u65f6\uff0c\u4ed6\u5634\u5507\u4e00\u6241\uff0c\u795e\u6001\u8f7b\u8511\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u8352\u91ce\u5730\u65b9\uff0c\u6709\u751a\u4e48\u53ef\u73a9\u7684\uff1f\u201d\u4f3c\u662f\u8ba5\u4ed6\u6ca1\u89c1\u8fc7\u4e16\u9762\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c9\u8fd9\u5c11\u5e74\u9a84\u6c14\u8feb\u4eba\uff0c\u5374\u4e5f\u4e0d\u4ee5\u4e3a\u5fe4\u3002\u4ed6\u89c1\u6c5f\u5357\u5c71\u6e29\u6c34\u8f6f\uff0c\u666f\u8272\u79c0\u4e3d\uff0c\u4e0e\u534e\u5c71\u7684\u96c4\u5947\u9669\u5cfb\u5168\u7136\u4e0d\u540c\uff0c\u4e00\u8def\u4e0a\u4ece\u4e0d\u80af\u9519\u8fc7\u4e86\u6e38\u89c8\u7684\u673a\u7f18\uff0c\u5f53\u4e0b\u4e0a\u5cb8\u56db\u4e0b\u95f2\u901b\uff0c\u559d\u4e86\u51e0\u676f\u9152\uff0c\u4e70\u4e86\u51e0\u65a4\u6787\u6777\u56de\u8239\uff0c\u60f3\u8bf7\u9f99\u5fb7\u90bb\u548c\u6e29\u9752\u5403\u65f6\uff0c\u89c1\u4e24\u4eba\u90fd\u5df2\u7761\u4e86\uff0c\u4fbf\u4e5f\u89e3\u8863\u5c31\u5bdd\u3002 \u3000\u3000\u7761\u5230\u4e2d\u591c\uff0c\u7761\u68a6\u4e2d\u5ffd\u542c\u8fdc\u5904\u9690\u9690\u6709\u553f\u54e8\u4e4b\u58f0\uff0c\u8881\u627f\u5fd7\u767b\u65f6\u9192\u8f6c\uff0c\u60f3\u8d77\u5e08\u7236\u6240\u8bf4\u6c5f\u6e56\u4e0a\u7684\u79cd\u79cd\u53d8\u6545\u60c5\u72b6\uff0c\u6599\u77e5\u6709\u4e8b\uff0c\u6084\u6084\u5728\u88ab\u4e2d\u7a7f\u4e86\u8863\u670d\u3002 \u3000\u3000\u4e0d\u4e45\u6a79\u58f0\u6025\u54cd\uff0c\u4e0b\u6e38\u6709\u8239\u4e0a\u6765\u3002\u53ea\u89c1\u6e29\u9752\u7a81\u7136\u5750\u8d77\uff0c\u539f\u6765\u4ed6\u5e76\u672a\u8131\u8863\uff0c\u53c8\u89c1\u4ed6\u4ece\u88ab\u7a9d\u4e2d\u53d6\u51fa\u4e00\u67c4\u7cbe\u5149\u8000\u773c\u7684\u957f\u5251\uff0c\u8dc3\u5230\u8239\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u6c34\u76d7\u6d3e\u6765\u5367\u5e95\u7684\uff0c\u8981\u6253\u52ab\u8fd9\u59d3\u9f99\u7684\u5546\u4eba\uff1f\u8fd9\u4e8b\u6559\u6211\u9047\u4e0a\u4e86\uff0c\u53ef\u4e0d\u80fd\u4e0d\u7ba1\u3002\u201d\u7a46\u4eba\u6e05\u79bb\u5c71\u4e4b\u65f6\uff0c\u66fe\u8bf4\u4e16\u95f4\u65b9\u4e71\uff0c\u9053\u8def\u4e0d\u9756\uff0c\u5e26\u7740\u957f\u5251\u60f9\u773c\uff0c\u4e0d\u514d\u591a\u751f\u4e8b\u7aef\uff0c\u56e0\u6b64\u4ed6\u9075\u5e08\u7236\u4e4b\u5631\uff0c\u968f\u8eab\u53ea\u5e26\u4e86\u4e00\u67c4\u5315\u9996\uff0c\u90a3\u67c4\u5e73\u65e5\u4e60\u7ec3\u5251\u6cd5\u7684\u957f\u5251\u7559\u5728\u534e\u5c71\uff0c\u5f53\u4e0b\u4e00\u6478\u8eab\u8fb9\u5315\u9996\uff0c\u5750\u8d77\u8eab\u6765\u3002\u53ea\u542c\u5f97\u5bf9\u9762\u5c0f\u8239\u6447\u8fd1\uff0c\u8239\u5934\u4e0a\u4e00\u4e2a\u7c97\u66b4\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u59d3\u6e29\u7684\uff0c\u4f60\u8bb2\u4e0d\u8bb2\u6c5f\u6e56\u4e49\u6c14\uff1f\u201d\u6e29\u9752\u53f1\u9053\uff1a\u201c\u8bb2\u53c8\u600e\u6837\uff0c\u4e0d\u8bb2\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u6211\u4eec\u8f9b\u8f9b\u82e6\u82e6\u7684\u4ece\u4e5d\u6c5f\u4e00\u8def\u8ddf\u8e2a\u4e0b\u6765\uff0c\u4f60\u5012\u597d\uff0c\u534a\u8def\u91cc\u6740\u51fa\u6765\u5403\u6a2a\u6881\u5b50\uff01\u201d \u3000\u3000\u8fd9\u65f6\u9f99\u5fb7\u90bb\u4e5f\u5df2\u60ca\u9192\uff0c\u63a2\u5934\u5f20\u671b\uff0c\u89c1\u56db\u8258\u5c0f\u8239\u4e0a\u706b\u628a\u70b9\u5f97\u6643\u4eae\uff0c\u8239\u5934\u4e0a\u7ad9\u6ee1\u4e86\u4eba\uff0c\u4e2a\u4e2a\u624b\u6267\u5175\u5203\uff0c\u767b\u65f6\u5413\u5f97\u4e0d\u4f4f\u53d1\u6296\u3002\u8881\u627f\u5fd7\u5df2\u542c\u51fa\u5176\u95f4\u8fc7\u8282\uff0c\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u83ab\u6015\uff0c\u6ca1\u4f60\u7684\u4e8b\uff01\u201d \u3000\u3000\u9f99\u5fb7\u90bb\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u4eec\u4e0d\u662f\u6765\u62a2\u6211\u8d27\u7269\u2026\u2026\u8d27\u7269\u7684\u5f3a\u4eba\u4e48\uff1f\u201d \u3000\u3000\u6e29\u9752\u559d\u9053\uff1a\u201c\u5929\u4e0b\u7684\u8d22\u5929\u4e0b\u4eba\u53d1\u5f97\uff0c\u96be\u9053\u8fd9\u91d1\u5b50\u662f\u4f60\u7684\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u5feb\u628a\u4e24\u5343\u4e24\u91d1\u5b50\u62ff\u51fa\u6765\uff0c\u5927\u5bb6\u5e73\u5206\u4e86\u3002\u54b1\u4eec\u53cc\u65b9\u5404\u5f97\u4e00\u5343\u4e24\uff0c\u5c31\u7b97\u4fbf\u5b9c\u4f60\u3002\u201d\u6e29\u9752\u53eb\u9053\uff1a\u201c\u5478\uff0c\u4f60\u60f3\u4e48\uff1f\u201d\u5c0f\u8239\u4e0a\u4e24\u540d\u5927\u6c49\u6012\u9053\uff1a\u201c\u6c99\u5927\u54e5\uff0c\u4f55\u5fc5\u8ddf\u8fd9\u6a2a\u86ee\u7684\u4e1c\u897f\u591a\u8d39\u53e3\u820c\uff01\u4ed6\u4e0d\u8981\u4e00\u5343\u4e24\u91d1\u5b50\uff0c\u90a3\u4e48\u4e00\u4e2a\u5b50\u513f\u4e5f\u4e0d\u7ed9\u4ed6\u3002\u201d\u624b\u6267\u5175\u5203\uff0c\u5411\u5927\u8239\u4e0a\u7eb5\u6765\u3002 \u3000\u3000\u9f99\u5fb7\u90bb\u542c\u4ed6\u4eec\u559d\u9a82\uff0c\u672c\u5df2\u5168\u8eab\u53d1\u6296\uff0c\u8fd9\u65f6\u89c1\u5c0f\u8239\u4e0a\u4e24\u4eba\u8df3\u5c06\u8fc7\u6765\uff0c\u66f4\u662f\u9b42\u98de\u9b44\u6563\uff0c\u5927\u53eb\u9053\uff1a\u201c\u8881\u2026\u2026\u8881\u76f8\u516c\uff0c\u5f3a\u4eba\u2026\u2026 \u3000\u3000\u5f3a\u4eba\u6765\u6253\u52ab\u2026\u2026\u6253\u52ab\u5566\u3002\u201d\u8881\u627f\u5fd7\u5c06\u4ed6\u62c9\u5230\u81ea\u5df1\u8eab\u540e\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u522b\u6015\u3002\u201d \u3000\u3000\u53ea\u89c1\u6e29\u9752\u8eab\u5b50\u4e00\u504f\uff0c\u5de6\u8db3\u98de\u8d77\uff0c\u6251\u901a\u4e00\u58f0\uff0c\u5de6\u8fb9\u4e00\u4eba\u8e22\u4e0b\u4e86\u6c5f\u53bb\uff0c\u8ddf\u7740\u53f3\u624b\u957f\u5251\u65a9\u843d\u3002\u6765\u4eba\u4e3e\u5200\u4e00\u6321\uff0c\u54ea\u77e5\u4ed6\u957f\u5251\u5ffd\u5730\u659c\u8f6c\uff0c\u907f\u8fc7\u4e86\u5200\u950b\uff0c\u987a\u52bf\u524a\u843d\uff0c\u53ea\u542c\u5f97\u5580\u64e6\u4e00\u58f0\u54cd\uff0c\u90a3\u4eba\u8fde\u80a9\u5e26\u5200\uff0c\u90fd\u88ab\u524a\u4e86\u4e0b\u6765\uff0c\u8dcc\u5728\u8239\u5934\uff0c\u6655\u6b7b\u4e86\u8fc7\u53bb\u3002\u6e29\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u6c99\u8001\u5927\uff0c\u522b\u8ba9\u8fd9\u4e9b\u8113\u5305\u6765\u73b0\u4e16\u5566\u3002\u201d\u5bf9\u9762\u90a3\u5927\u6c49\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53bb\u62ac\u8001\u674e\u56de\u6765\u3002\u201d\u5c0f\u8239\u4e0a\u4e24\u4eba\u7a7a\u624b\u7eb5\u5c06\u8fc7\u6765\uff0c\u6e29\u9752\u53ea\u662f\u51b7\u7b11\uff0c\u5e76\u4e0d\u7406\u4f1a\uff0c\u8ba9\u4e24\u4eba\u5c06\u53f3\u8180\u88ab\u524a\u4e4b\u4eba\u62ac\u4e86\u56de\u53bb\uff0c\u4e0d\u4e45\u8dcc\u5728\u6c5f\u4e2d\u90a3\u4eba\u4e5f\u6e7f\u6dcb\u6dcb\u7684\u722c\u4e0a\u5c0f\u8239\u3002 \u3000\u3000\u6c99\u8001\u5927\u53eb\u9053\uff1a\u201c\u6211\u4eec\u9f99\u6e38\u5e2e\u548c\u4f60\u77f3\u6881\u6d3e\u7d20\u6765\u6cb3\u6c34\u4e0d\u72af\u4e95\u6c34\u3002\u6211\u4eec\u5f53\u5bb6\u7684\u51b2\u7740\u4f60\u4e94\u7956\u9762\u5b50\uff0c\u4e0d\u6765\u8ddf\u4f60\u4e3a\u96be\uff0c\u53ef\u522b\u5f53\u6211\u4eec\u662f\u597d\u60f9\u7684\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u4ed6\u63d0\u5230\u77f3\u6881\u6d3e\uff0c\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u90a3\u5929\u5230\u534e\u5c71\u6765\u7684\u5f20\u6625\u4e5d\uff0c\u4e0d\u662f\u81ea\u79f0\u77f3\u6881\u6d3e\u4e48\uff1f\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u4f60\u522b\u5411\u6211\u5356\u597d\uff0c\u6253\u4e0d\u8fc7\uff0c\u60f3\u8f6f\u6c42\u4e48\uff1f\u201d\u6c99\u8001\u5927\u6012\u9053\uff1a\u201c\u4f60\u5230\u5e95\u6309\u4e0d\u6309\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\u529e\u4e8b\uff1f\u201d\u6e29\u9752\u51b7\u7b11\u9053\uff1a\u201c\u6211\u7231\u600e\u6837\u5c31\u600e\u6837\uff0c\u504f\u6709\u8fd9\u8bb8\u591a\u5e9f\u8bdd\uff1f\u201d\u6c99\u8001\u5927\u9053\uff1a\u201c\u54b1\u4eec\u8bdd\u8bf4\u5728\u5148\uff0c\u6211\u4eec\u9f99\u6e38\u5e2e\u5df2\u5c3d\u5230\u4e86\u793c\u6570\uff0c\u8ddf\u4f60\u597d\u8bf4\u597d\u8bdd\uff0c\u53ea\u76fc\u53cc\u65b9\u4e0d\u4f24\u4e86\u548c\u6c14\u3002\u4f60\u4e94\u7956\u53ef\u4e0d\u80fd\u518d\u8bf4\u6211\u4eec\u4ee5\u591a\u6b3a\u5c11\uff0c\u4ee5\u5927\u6b3a\u5c0f\u3002\u201d\u8881\u627f\u5fd7\u542c\u4ed6\u53e3\u6c14\uff0c\u4f3c\u4e4e\u5bf9\u6e29\u9752\u7684\u4e00\u4e2a\u751a\u4e48\u4e94\u7956\u5f88\u662f\u5fcc\u60ee\u3002\u6e29\u9752\u7b11\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u70b9\u73a9\u827a\u513f\uff0c\u5c31\u80fd\u6b3a\u5f97\u4e86\u6211\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u53cc\u65b9\u8d8a\u8bf4\u8d8a\u50f5\uff0c\u77e5\u9053\u5b9a\u8981\u52a8\u624b\uff0c\u4ece\u4e24\u8fb9\u8a00\u8bed\u4e2d\u542c\u6765\uff0c\u4f3c\u662f\u9f99\u6e38\u5e2e\u60f3\u52ab\u4e00\u6279\u9ec4\u91d1\uff0c\u5374\u7ed9\u6e29\u9752\u4e2d\u95f4\u6740\u51fa\u6765\u631f\u624b\u593a\u4e86\u53bb\uff0c\u9f99\u6e38\u5e2e\u4e0d\u670d\u6c14\uff0c\u8d76\u4e0a\u6765\u8981\u5206\u4e00\u534a\u8d43\u3002\u6e29\u9752\u4e0a\u8239\u65f6\u8eab\u5b50\u5982\u6b64\u6c89\u91cd\uff0c\u60f3\u6765\u5305\u88f9\u4e2d\u5c31\u85cf\u7740\u8fd9\u4e8c\u5343\u4e24\u9ec4\u91d1\u4e86\u3002\u5fc3\u60f3\u4e24\u8fb9\u90fd\u975e\u6b63\u4eba\uff0c\u81ea\u5df1\u88c5\u4f5c\u4e0d\u4f1a\u6b66\u529f\uff0c\u53ea\u8896\u624b\u65c1\u89c2\u4fbf\u662f\u3002 \u3000\u3000\u6c99\u8001\u5927\u5927\u58f0\u547c\u559d\uff0c\u624b\u63e1\u4e00\u67c4\u6cfc\u98ce\u5927\u73af\u5200\uff0c\u8dc3\u4e0a\u8239\u6765\uff0c\u5341\u591a\u540d\u5927\u6c49\u8ddf\u7740\u7eb7\u7eb7\u8dc3\u8fc7\uff0c\u7ad9\u5728\u4ed6\u8eab\u540e\u3002\u6c99\u8001\u5927\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77f3\u6881\u6d3e\u6b66\u529f\u53f7\u79f0\u72ec\u6b65\u6c5f\u5357\uff0c\u4eca\u65e5\u59d3\u6c99\u7684\u9886\u6559\u9601\u4e0b\u9ad8\u62db\uff01\u201d\u6e29\u9752\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u662f\u4f60\u4e00\u4eba\u548c\u6211\u6253\u5462\uff0c\u8fd8\u662f\u4f60\u4eec\u5927\u4f19\u513f\u9f50\u4e0a\uff1f\u201d \u3000\u3000\u6c99\u8001\u5927\u6012\u9053\uff1a\u201c\u4f60\u4e5f\u592a\u77a7\u4e0d\u8d77\u4eba\u5566\uff01\u4f60\u8239\u4e0a\u8fd8\u6709\u751a\u4e48\u670b\u53cb\u8bf7\u4ed6\u51fa\u6765\u4f5c\u4e2a\u89c1\u8bc1\uff0c\u522b\u8ba9\u6c5f\u6e56\u4e0a\u670b\u53cb\u8bf4\u59d3\u6c99\u7684\u4e0d\u8981\u8138\u3002\u201d\u4ed6\u6389\u5934\u5bf9\u7740\u8231\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u53eb\u8231\u91cc\u7684\u670b\u53cb\u51fa\u6765\u5427\uff01\u201d\u4e24\u540d\u5927\u6c49\u8d70\u8fdb\u8231\u53bb\uff0c\u5bf9\u8881\u627f\u5fd7\u548c\u9f99\u5fb7\u90bb\u9053\uff1a\u201c\u6211\u4eec\u5927\u54e5\u8981\u4f60\u4eec\u51fa\u53bb\u3002\u201d \u3000\u3000\u9f99\u5fb7\u90bb\u5168\u8eab\u53d1\u6296\uff0c\u4e0d\u6562\u4f5c\u58f0\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u6253\u67b6\uff0c\u53ea\u4e0d\u8fc7\u53eb\u54b1\u4eec\u4f5c\u4e2a\u89c1\u8bc1\uff0c\u6ca1\u751a\u4e48\u8981\u7d27\u3002\u51fa\u53bb\u5427\u3002\u201d\u62c9\u7740\u4ed6\u624b\uff0c\u8d70\u4e0a\u8239\u5934\u3002 \u3000\u3000\u6e29\u9752\u4f3c\u4e4e\u7b49\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u4e0d\u8ba9\u6c99\u8001\u5927\u518d\u4ea4\u5f85\u751a\u4e48\u573a\u9762\u8bdd\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5b9a\u8981\u51fa\u4e11\uff0c\u53ef\u83ab\u602a\u6211\u624b\u8fa3\uff0c\u8fdb\u62db\u3002\u201d\u5237\u5237\u4e24\u5251\uff0c\u5206\u523a\u5bf9\u65b9\u5de6\u80a9\u53f3\u8180\u3002\u6c99\u8001\u5927\u8eab\u6750\u9b41\u68a7\uff0c\u8eab\u6cd5\u5374\u9887\u4e3a\u7075\u52a8\uff0c\u6cfc\u98ce\u5200\u4e00\u62db\u201c\u94c1\u725b\u9876\u9888\u201d\uff0c\u53cd\u8f6c\u5200\u80cc\uff0c\u5411\u6e29\u9752\u7838\u6765\uff0c\u8fd9\u4e00\u62db\u65e2\u907f\u6765\u5251\uff0c\u53c8\u653b\u654c\u4eba\uff0c\u53ef\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u53ea\u4ee5\u5200\u80cc\u7838\u6253\u3002 \u3000\u3000\u6e29\u9752\u53f1\u9053\uff1a\u201c\u6709\u751a\u4e48\u672c\u4e8b\uff0c\u4e00\u53e4\u8111\u513f\u7684\u90fd\u6296\u51fa\u6765\u5427\uff0c\u6211\u53ef\u4e0d\u9886\u4f60\u60c5\u3002\u201d\u53e3\u4e2d\u8bf4\u7740\uff0c\u624b\u4e0a\u957f\u5251\u8fde\u653b\u6570\u62db\u3002 \u3000\u3000\u6c99\u8001\u5927\u5fae\u4e00\u758f\u795e\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u80a9\u5934\u8863\u670d\u88ab\u523a\u7834\u4e86\u4e00\u7247\uff0c\u80a9\u5934\u4e5f\u5272\u4f24\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u4ed6\u53fd\u54e9\u5495\u565c\u7684\u9a82\u4e86\u51e0\u53e5\uff0c\u4e00\u67c4\u6cfc\u98ce\u5200\u65bd\u5c55\u5f00\u6765\uff0c\u72e0\u780d\u72e0\u6740\uff0c\u62db\u62db\u72e0\u6bd2\u3002\u6e29\u9752\u5251\u8d70\u8f7b\u7075\uff0c\u76d8\u65cb\u6765\u53bb\uff0c\u957f\u5251\u9752\u5149\u95ea\u70c1\uff0c\u5df2\u628a\u5bf9\u65b9\u5168\u8eab\u88f9\u4f4f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u770b\u4e24\u4eba\u62c6\u4e86\u6570\u62db\uff0c\u5df2\u77e5\u6e29\u9752\u6b66\u529f\u8fdc\u5728\u6c99\u8001\u5927\u4e4b\u4e0a\u3002 \u3000\u3000\u6c99\u8001\u5927\u5200\u6c89\u529b\u52b2\uff0c\u770b\u6765\u5012\u662f\u5341\u5206\u5a01\u731b\uff0c\u4f46\u5200\u6cd5\u5931\u4e4b\u5446\u6ede\u3002\u6e29\u9752\u4ee5\u5de7\u964d\u529b\uff0c\u65f6\u5019\u7a0d\u957f\uff0c\u6c99\u8001\u5927\u989d\u5934\u89c1\u6c57\uff0c\u547c\u5438\u6e10\u7c97\uff0c\u8eab\u6cd5\u5df2\u4e0d\u5982\u521d\u6218\u65f6\u7684\u77eb\u6377\u3002 \u3000\u3000\u5200\u5149\u5251\u5f69\u4e2d\u53ea\u542c\u5f97\u6e29\u9752\u4e00\u58f0\u547c\u53f1\uff0c\u6c99\u8001\u5927\u817f\u4e0a\u4e2d\u5251\u3002\u4ed6\u8138\u8272\u5927\u53d8\uff0c\u7eb5\u51fa\u4e09\u6b65\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u4e09\u679a\u900f\u9aa8\u9489\u6253\u4e86\u8fc7\u6765\u3002\u6e29\u9752\u626c\u5251\u6253\u98de\u4e24\u679a\uff0c\u53e6\u4e00\u679a\u4fa7\u8eab\u907f\u8fc7\u3002\u4ed6\u6253\u98de\u7684\u4e24\u679a\u900f\u9aa8\u9489\u4e2d\uff0c\u6709\u4e00\u679a\u7a81\u5411\u8881\u627f\u5fd7\u5f53\u80f8\u98de\u53bb\u3002 \u3000\u3000\u6e29\u9752\u60ca\u547c\u4e00\u58f0\uff0c\u5fc3\u60f3\u8fd9\u4e00\u6b21\u8981\u9519\u4f24\u65c1\u4eba\u3002\u54ea\u77e5\u8881\u627f\u5fd7\u4f38\u51fa\u5de6\u624b\uff0c\u53ea\u7528\u4e24\u6839\u624b\u6307\uff0c\u4fbf\u8f7b\u8f7b\u5de7\u5de7\u7684\u5c06\u90a3\u679a\u900f\u9aa8\u9489\u62c8\u4f4f\u4e86\u3002 \u3000\u3000\u6c99\u8001\u5927\u5e26\u6765\u7684\u5927\u6c49\u4e2d\u591a\u4eba\u624b\u6267\u706b\u628a\uff0c\u5c06\u8239\u5934\u7167\u5f97\u660e\u6643\u6643\u5730\u6709\u5982\u767d\u663c\uff0c\u6e29\u9752\u77a7\u5f97\u6e05\u695a\uff0c\u4e0d\u7981\u4e00\u6014\uff1a\u201c\u8fd9\u624b\u529f\u592b\u53ef\u4fca\u5f97\u5f88\u54ea\uff01 \u3000\u3000\u539f\u6765\u4ed6\u6b66\u529f\u7740\u5b9e\u4e86\u5f97\u3002\u201d \u3000\u3000\u6c99\u8001\u5927\u89c1\u6e29\u9752\u6ce8\u89c6\u7740\u8881\u627f\u5fd7\uff0c\u9762\u9732\u60ca\u6115\u4e4b\u8272\uff0c\u4e58\u4ed6\u4e0d\u5907\uff0c\u53c8\u662f\u4e09\u679a\u900f\u9aa8\u9489\u5c04\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6025\u53eb\uff1a\u201c\u6e29\u5144\uff0c\u7559\u795e\uff01\u201d \u3000\u3000\u6e29\u9752\u6025\u5fd9\u8f6c\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u4e09\u679a\u900f\u9aa8\u9489\u8ddd\u8eab\u5df2\u4e0d\u8fc7\u4e09\u5c3a\uff0c\u82e5\u4e0d\u662f\u5f97\u4ed6\u53ca\u65f6\u547c\u53eb\uff0c\u81f3\u591a\u8eb2\u5f97\u8fc7\u4e00\u679a\uff0c\u4e0b\u9762\u4e24\u679a\u5374\u4e07\u4e07\u8eb2\u907f\u4e0d\u5f00\uff0c\u6025\u5fd9\u4fa7\u5934\u8ba9\u8fc7\u4e86\u4e00\u679a\uff0c\u6325\u5251\u51fb\u98de\u4e86\u53e6\u5916\u4e24\u679a\uff0c\u8f6c\u8eab\u5411\u8881\u627f\u5fd7\u70b9\u5934\u793a\u8c22\uff0c\u633a\u8d77\u957f\u5251\uff0c\u5411\u6c99\u8001\u5927\u76f4\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u6c99\u8001\u5927\u4e00\u51fb\u4e0d\u4e2d\uff0c\u65e9\u5df2\u6709\u5907\uff0c\u63d0\u8d77\u6cfc\u98ce\u5200\u4e00\u8f6e\u731b\u780d\u3002\u6e29\u9752\u6068\u4ed6\u6b79\u6bd2\uff0c\u51fa\u624b\u5c3d\u662f\u6740\u7740\u3002\u62c6\u4e86\u6570\u62db\uff0c\u6c99\u8001\u5927\u53f3\u8180\u4e2d\u5251\uff0c\u545b\u5577\u5577\u4e00\u54cd\uff0c\u6cfc\u98ce\u5200\u8dcc\u843d\u8239\u677f\u3002\u6e29\u9752\u62a2\u4e0a\u4e00\u6b65\uff0c\u6325\u5251\u5c06\u4ed6\u53f3\u817f\u780d\u4e0b\u3002\u6c99\u8001\u5927\u957f\u58f0\u60e8\u53eb\uff0c\u6655\u4e86\u8fc7\u53bb\uff0c\u4ed6\u624b\u4e0b\u4f17\u4eba\u5927\u60ca\uff0c\u62e5\u4e0a\u76f8\u6551\u3002\u6e29\u9752\u638c\u5288\u5251\u523a\uff0c\u767b\u65f6\u6253\u6b7b\u4e86\u4e03\u516b\u4eba\u3002 \u3000\u3000\u8881\u627f\u5fd7\u770b\u7740\u4e0d\u5fcd\uff0c\u8bf4\u9053\uff1a\u201c\u6e29\u5927\u54e5\uff0c\u9976\u4e86\u4ed6\u4eec\u5427\uff01\u201d\u6e29\u9752\u6beb\u4e0d\u7406\u4f1a\uff0c\u7ee7\u7eed\u523a\u6740\uff0c\u53c8\u4f24\u4e86\u4e24\u4eba\u3002\u4f59\u4eba\u89c1\u4ed6\u51f6\u608d\uff0c\u7eb7\u7eb7\u8df3\u6c5f\u9003\u547d\u3002\u6e29\u9752\u987a\u624b\u4e00\u5251\uff0c\u5272\u4e0b\u6c99\u8001\u5927\u7684\u9996\u7ea7\uff0c\u8ddf\u7740\u4e24\u811a\uff0c\u628a\u4ed6\u9996\u7ea7\u548c\u5c38\u8eab\u90fd\u8e22\u5165\u6c5f\u4e2d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e0b\u4e0d\u5feb\uff0c\u6697\u60f3\u4f60\u65e2\u5df2\u5f97\u80dc\uff0c\u4f55\u5fc5\u5982\u6b64\u5fc3\u72e0\u624b\u8fa3\uff0c\u8f6c\u5934\u770b\u9f99\u5fb7\u90bb\u65f6\uff0c\u4ed6\u65e9\u5df2\u5413\u5f97\u5168\u8eab\u762b\u8f6f\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u8df3\u5165\u6c5f\u4e2d\u7684\u9f99\u6e38\u5e2e\u4f17\u7eb7\u7eb7\u722c\u4e0a\u5c0f\u8239\uff0c\u6447\u52a8\u8239\u6a79\uff0c\u5982\u98de\u822c\u5411\u4e0b\u6e38\u9003\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u62a2\u4f60\u8d22\u7269\uff0c\u65e2\u6ca1\u62a2\u53bb\uff0c\u4e5f\u5c31\u7f62\u4e86\uff0c\u4f55\u82e6\u591a\u4f24\u6027\u547d\uff1f\u201d \u3000\u3000\u6e29\u9752\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u6ca1\u89c1\u4ed6\u521a\u624d\u7684\u5351\u9119\u6076\u6bd2\u4e48\uff1f\u8981\u662f\u6211\u843d\u5165\u4ed6\u624b\u91cc\uff0c\u53ea\u6015\u8fd8\u6709\u66f4\u60e8\u7684\u5462\u3002\u4f60\u522b\u4ee5\u4e3a\u6551\u4e86\u6211\u4e00\u6b21\uff0c\u5c31\u53ef\u968f\u4fbf\u6559\u8bad\u4eba\u5bb6\uff0c\u6211\u624d\u4e0d\u7406\u5462\u3002\u201d\u8881\u627f\u5fd7\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u60f3\u8fd9\u4eba\u5b9e\u5728\u4e0d\u901a\u60c5\u7406\u3002 \u3000\u3000\u6e29\u9752\u62ed\u5e72\u5251\u4e0a\u8840\u8ff9\uff0c\u8fd8\u5251\u5165\u9798\uff0c\u5411\u8881\u627f\u5fd7\u4e00\u63d6\uff0c\u5ffd\u7136\u751c\u751c\u7684\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u9002\u624d\u5e78\u5f97\u4f60\u51fa\u58f0\u793a\u8b66\uff0c\u53eb\u6211\u907f\u5f00\u6697\u5668\uff0c\u8c22\u8c22\u4f60\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8138\u4e0a\u4e00\u7ea2\uff0c\u8fd8\u4e86\u4e00\u63d6\uff0c\u5fc3\u4e0b\u53d1\u7a98\uff0c\u65e0\u8a00\u53ef\u7b54\uff0c\u53ea\u89c9\u8fd9\u7f8e\u5c11\u5e74\u6709\u793c\u65f6\u6e29\u82e5\u5904\u5b50\uff0c\u51f6\u6076\u65f6\u72e0\u5982\u72fc\u864e\uff0c\u4e0d\u77e5\u5230\u5e95\u662f\u751a\u4e48\u6027\u5b50\u3002 \u3000\u3000\u6e29\u9752\u53eb\u8239\u592b\u51fa\u6765\uff0c\u5429\u5490\u6d17\u51c0\u8239\u5934\u8840\u8ff9\uff0c\u7acb\u5373\u5f00\u8239\u3002\u8239\u592b\u89c1\u4e86\u521a\u624d\u7684\u72e0\u6597\uff0c\u54ea\u6562\u8fdd\u6297\uff0c\u63d0\u6c34\u6d17\u4e86\u8239\u677f\uff0c\u62d4\u951a\u626c\u5e06\uff0c\u8fde\u591c\u5f00\u8239\u3002 \u3000\u3000\u6e29\u9752\u53c8\u53eb\u8239\u592b\u53d6\u51fa\u9f99\u5fb7\u90bb\u7684\u9152\u83dc\uff0c\u55a7\u5bbe\u593a\u4e3b\uff0c\u81ea\u4e0e\u8881\u627f\u5fd7\u5728\u8239\u5934\u8d4f\u6708\u3002\u4ed6\u7edd\u53e3\u4e0d\u63d0\u521a\u624d\u6076\u6597\uff0c\u4e5f\u4e0d\u8c08\u8bba\u6b66\u529f\uff0c\u559d\u4e86\u51e0\u676f\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u6708\u51e0\u65f6\u6709\uff0c\u628a\u9152\u95ee\u9752\u5929\u3002\u54fc\uff0c\u9752\u5929\u53ea\u6015\u4e5f\u7ba1\u4ed6\u4e0d\u7740\u5462\u3002\u660e\u6708\u51e0\u65f6\u7231\u51fa\u6765\uff0c\u4fbf\u51fa\u6765\uff0c\u4e0d\u7231\u51fa\u6765\u4fbf\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8881\u5927\u54e5\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u4ed6\u5ffd\u7136\u6389\u6587\uff0c\u53ea\u5f97\u968f\u53e3\u55ef\u4e86\u4e00\u58f0\u3002\u4ed6\u5c0f\u65f6\u8ddf\u5e94\u677e\u5ff5\u4e86\u51e0\u5e74\u4e66\uff0c\u81ea\u4ece\u8ddf\u7a46\u4eba\u6e05\u5b66\u6b66\u540e\uff0c\u867d\u7136\u665a\u95f4\u5076\u7136\u7ffb\u9605\u4e00\u4e0b\u4e66\u7c4d\uff0c\u4f46\u4e0d\u5f53\u5b83\u6b63\u7ecf\u529f\u8bfe\uff0c\u662f\u4ee5\u6587\u5b57\u4e0a\u751a\u662f\u6709\u9650\u3002 \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u8881\u5144\uff0c\u6708\u767d\u98ce\u9ad8\uff0c\u5982\u6b64\u826f\u591c\uff0c\u54b1\u4eec\u6765\u8054\u53e5\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8054\u53e5\uff1f\u751a\u4e48\u53eb\u8054\u53e5\uff1f\u6211\u53ef\u4e0d\u4f1a\u3002\u201d\u6e29\u9752\u4e00\u7b11\u4e0d\u7b54\uff0c\u66ff\u8881\u627f\u5fd7\u659f\u4e86\u676f\u9152\u3002\u5ffd\u89c1\u524d\u9762\u6c5f\u4e0a\u4e00\u53f6\u5c0f\u821f\u7834\u6d6a\u800c\u6765\uff0c\u867d\u662f\u9006\u6c34\uff0c\u4f46\u9a76\u5f97\u751a\u5feb\u3002\u6e29\u9752\u8138\u8272\u4e00\u53d8\uff0c\u51b7\u7b11\u6570\u58f0\uff0c\u53ea\u7ba1\u559d\u9152\u3002 \u3000\u3000\u5ea7\u8239\u987a\u98ce\u987a\u6c34\uff0c\u51b2\u5411\u4e0b\u6e38\uff0c\u8f6c\u773c\u95f4\u4e24\u8239\u9a76\u8fd1\u3002\u6e29\u9752\u63b7\u4e0b\u9152\u676f\uff0c\u7a81\u7136\u98de\u8eab\u8dc3\u8d77\uff0c\u53cc\u811a\u5728\u8239\u7bf7\u4e0a\u70b9\u4e86\u51e0\u70b9\uff0c\u843d\u5728\u540e\u68a2\uff0c\u4ece\u8239\u8001\u5927\u624b\u91cc\u62a2\u8fc7\u8235\u6765\uff0c\u53ea\u4e00\u6273\uff0c\u5ea7\u8239\u8239\u5934\u5411\u5de6\u504f\u659c\uff0c\u5bf9\u51c6\u4e86\u5c0f\u8239\u76f4\u649e\u8fc7\u53bb\u3002\u5c0f\u8239\u5fd9\u8981\u907f\u8ba9\uff0c\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff0c\u53ea\u542c\u4e00\u58f0\u5de8\u54cd\uff0c\u4e24\u8239\u5df2\u7136\u76f8\u649e\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u5f97\u4e00\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u5df2\u89c1\u5c0f\u8239\u4e0a\u8dc3\u8d77\u4e09\u4e2a\u4eba\u5f71\uff0c\u5148\u540e\u843d\u5728\u5927\u8239\u8239\u5934\uff0c\u8eab\u624b\u5747\u9887\u8fc5\u6377\u3002\u8fd9\u65f6\u5c0f\u8239\u4e00\u4fa7\uff0c\u7ffb\u4e86\u8fc7\u53bb\uff0c\u8239\u5e95\u5411\u5929\u3002\u8881\u627f\u5fd7\u8001\u8fdc\u770b\u51fa\u5c0f\u8239\u4e0a\u539f\u6709\u4e94\u4eba\uff0c\u9664\u8fd9\u4e09\u4eba\u5916\uff0c\u5c1a\u6709\u4e24\u4eba\uff0c\u4e00\u4e2a\u638c\u8235\uff0c\u4e00\u4e2a\u6253\u6868\u3002\u8fd9\u4e24\u4eba\u4e0d\u53ca\u8dc3\u8d77\uff0c\u90fd\u843d\u5165\u6c34\u4e2d\uff0c\u53ea\u53eb\u5f97\u4e00\u58f0\u201c\u6551\u547d\u201d\u4fbf\u6c89\u843d\u6c5f\u5e95\u3002\u8fd9\u4e00\u5e26\u6c5f\u9762\u6c34\u6025\u7901\u591a\uff0c\u5c31\u7b97\u719f\u8bc6\u6c34\u6027\uff0c\u9ed1\u591c\u4e2d\u8dcc\u5165\u6c5f\u5fc3\u4e5f\u662f\u51f6\u591a\u5409\u5c11\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6697\u9a82\u6e29\u9752\u6b79\u6bd2\uff0c\u65e0\u7aef\u7aef\u7684\u53c8\u53bb\u4f24\u4eba\uff0c\u7b49\u4e24\u4eba\u4ece\u6c34\u4e2d\u5192\u4e0a\uff0c\u5f53\u5373\u4f38\u624b\u626f\u65ad\u5e06\u7d22\uff0c\u54ac\u5728\u53e3\u4e2d\uff0c\u53cc\u8db3\u5728\u8239\u8237\u4e0a\u4e00\u6491\uff0c\u98de\u8eab\u843d\u5411\u6c5f\u4e2d\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u6293\u4f4f\u843d\u6c34\u7684\u4e24\u4eba\u5934\u53d1\uff0c\u501f\u7740\u7259\u9f7f\u54ac\u4f4f\u5e06\u7d22\u4e4b\u529b\uff0c\u5728\u6c5f\u9762\u6253\u4e86\u4e2a\u5708\u5b50\uff0c\u63d0\u7740\u4e24\u4eba\u56de\u5230\u5ea7\u8239\uff0c\u8fd9\u4e00\u4e0b\u65e2\u4f7f\u4e0a\u4e86\u201c\u6df7\u5143\u529f\u201d\u5185\u52b2\uff0c\u53c8\u7528\u4e86\u6728\u6851\u6240\u6388\u7684\u8f7b\u8eab\u529f\u592b\u3002 \u3000\u3000\u53ea\u542c\u56db\u4eba\u9f50\u58f0\u559d\u91c7\u3002\u4e00\u662f\u6e29\u9752\uff0c\u4ed6\u5df2\u4ece\u8239\u68a2\u8dc3\u56de\u8239\u5934\uff0c\u53e6\u5916\u4e09\u4e2a\u5219\u662f\u4ece\u5c0f\u8239\u8df3\u4e0a\u6765\u7684\u3002 \u3000\u3000\u8881\u627f\u5fd7\u653e\u4e0b\u4e24\u4eba\uff0c\u6708\u5149\u4e0b\u770b\u90a3\u4e09\u4eba\u65f6\uff0c\u89c1\u4e00\u4e2a\u662f\u4e94\u5341\u591a\u5c81\u7684\u67af\u7626\u8001\u8005\uff0c\u7559\u4e86\u758f\u758f\u7684\u80e1\u5b50\uff0c\u4e00\u4e2a\u662f\u4e2d\u5e74\u5927\u6c49\uff0c\u8eab\u6750\u7c97\u58ee\uff0c\u53e6\u4e00\u4e2a\u5219\u662f\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u5987\u4eba\u3002 \u3000\u3000\u90a3\u8001\u8005\u9634\u607b\u607b\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u597d\u4fca\u8eab\u624b\uff0c\u8bf7\u6559\u5c0a\u59d3\u5927\u540d\uff0c\u5e08\u627f\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u665a\u751f\u59d3\u8881\uff0c\u56e0\u89c1\u8fd9\u4e24\u4f4d\u843d\u6c34\uff0c\u6015\u6709\u5371\u9669\uff0c\u8fd9\u624d\u62c9\u4e86\u8d77\u6765\uff0c\u5e76\u975e\u80c6\u6562\u5728\u524d\u8f88\u9762\u524d\u5356\u5f04\u7c97\u6d45\u529f\u592b\uff0c\u8bf7\u52ff\u89c1\u602a\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u89c1\u4ed6\u5341\u5206\u8c26\u606d\uff0c\u9887\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u53ea\u9053\u4ed6\u662f\u6015\u4e86\u81ea\u5df1\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u5bf9\u6e29\u9752\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u8fd9\u5a03\u513f\u8d8a\u6765\u8d8a\u5927\u80c6\u5566\uff0c\u539f\u6765\u6709\u4e86\u8fd9\u4e48\u786c\u7684\u4e00\u4e2a\u5e2e\u624b\u3002\u4ed6\u662f\u4f60\u7684\u76f8\u597d\u4e48\uff1f\u201d \u3000\u3000\u6e29\u9752\u767b\u65f6\u6ee1\u8138\u901a\u7ea2\uff0c\u6012\u559d\uff1a\u201c\u6211\u5c0a\u79f0\u4f60\u4e00\u58f0\u957f\u8f88\uff0c\u4f60\u8bf4\u8bdd\u7ed9\u6211\u653e\u5c0a\u91cd\u4e9b\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u770b\u8fd9\u4e9b\u4eba\u795e\u6c14\uff0c\u5168\u90fd\u4e0d\u662f\u6b63\u4eba\uff0c\u6211\u53ef\u83ab\u5377\u5165\u662f\u975e\u6f29\u6da1\u4e4b\u4e2d\u3002\u201d\u4e8e\u662f\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u8fd9\u4f4d\u6e29\u5144\u4e5f\u662f\u840d\u6c34\u76f8\u9022\uff0c\u8c08\u4e0d\u4e0a\u751a\u4e48\u4ea4\u60c5\u3002\u6211\u5949\u529d\u5404\u4f4d\uff0c\u6709\u4e8b\u597d\u597d\u5546\u91cf\uff0c\u4e0d\u5fc5\u52a8\u5200\u52a8\u67aa\u7684\u4f24\u4e86\u548c\u6c14\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u8fd8\u672a\u63a5\u53e3\uff0c\u6e29\u9752\u72e0\u72e0\u77aa\u4e86\u8881\u627f\u5fd7\u4e00\u773c\uff0c\u6012\u9053\uff1a\u201c\u4f60\u8981\u662f\u5bb3\u6015\uff0c\u90a3\u5c31\u4e0a\u5cb8\u8d70\u4f60\u7684\u5427\uff01\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2a\u4eba\u53ef\u5f53\u771f\u86ee\u4e0d\u8bb2\u7406\u3002\u201d\u5f53\u4e0b\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u90a3\u8001\u8005\u542c\u4e86\u8881\u627f\u5fd7\u53e3\u6c14\uff0c\u77e5\u4ed6\u4e0d\u662f\u6e29\u9752\u5e2e\u624b\uff0c\u559c\u9053\uff1a\u201c\u8881\u670b\u53cb\u65e2\u8ddf\u8fd9\u59d3\u6e29\u7684\u6ca1\u6709\u74dc\u845b\uff0c\u90a3\u597d\u6781\u5566\uff0c\u7b49\u6211\u4eec\u4e8b\u4e86\u4e4b\u540e\uff0c\u6211\u518d\u548c\u8881\u670b\u53cb\u8be6\u8c08\uff0c\u54b1\u4eec\u5f88\u53ef\u4ee5\u4ea4\u4ea4\u3002\u201d\u8a00\u4e0b\u9887\u6709\u7ed3\u7eb3\u4e4b\u610f\u3002\u8881\u627f\u5fd7\u4e0d\u4fbf\u56de\u7b54\uff0c\u4f5c\u4e86\u4e00\u63d6\uff0c\u9000\u5728\u6e29\u9752\u8eab\u540e\u3002 \u3000\u3000\u90a3\u8001\u8005\u5bf9\u6e29\u9752\u9053\uff1a\u201c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u505a\u4e8b\u8fd9\u7b49\u5fc3\u72e0\u624b\u8fa3\u3002\u6c99\u8001\u5927\u6253\u4e0d\u8fc7\u4f60\uff0c\u4f60\u8d76\u4e86\u4ed6\u8d70\uff0c\u4e5f\u5c31\u7f62\u4e86\uff0c\u5e72\u4e48\u8981\u4f24\u4ed6\u6027\u547d\uff1f\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u6211\u53ea\u4e00\u4e2a\u4eba\uff0c\u4f60\u4eec\u8fd9\u8bb8\u591a\u5927\u6c49\u5b50\u4e00\u62e5\u800c\u4e0a\uff0c\u6211\u4e0d\u72e0\u4e00\u4e9b\u6210\u4e48\uff1f\u8fd8\u8bf4\u4eba\u5bb6\u5462\uff1f\u4e5f\u4e0d\u6015\u65c1\u4eba\u7b11\u4f60\u4eec\u5927\u6b3a\u5c0f\uff0c\u591a\u6b3a\u5c11\u3002\u6709\u672c\u4e8b\u54ea\uff0c\u5c31\u628a\u4eba\u5bb6\u7684\u91d1\u5b50\u7ed9\u62fe\u4e0b\u6765\u3002\u7b49\u6211\u6361\u4e86\uff0c\u53c8\u662f\u9634\u9b42\u4e0d\u6563\u7684\u8ffd\u7740\u6765\u8981\uff0c\u60f3\u5403\u73b0\u6210\u4e48\uff1f\u4e5f\u4e0d\u77e5\u9053\u8981\u4e0d\u8981\u8138\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u6e05\u8106\uff0c\u54ad\u54ad\u5471\u5471\u7684\u4e00\u987f\u62a2\u767d\uff0c\u90a3\u8001\u8005\u7ed9\u4ed6\u8bf4\u5f97\u54d1\u53e3\u65e0\u8a00\u3002 \u3000\u3000\u90a3\u5987\u4eba\u7a81\u7136\u53cc\u7709\u7ad6\u8d77\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5a03\u513f\uff0c\u4f60\u6e29\u5bb6\u5927\u4eba\u628a\u4f60\u5ba0\u5f97\u8d8a\u6765\u8d8a\u6ca1\u89c4\u77e9\u5566\u3002\u6211\u8981\u95ee\u95ee\u4f60\u7237\u7237\u53bb\uff0c\u662f\u8c01\u6559\u4f60\u8fd9\u822c\u76ee\u65e0\u5c0a\u957f\uff1f\u201d\u6e29\u9752\u9053\uff1a\u201c\u5c0a\u957f\u4e5f\u8981\u6709\u5c0a\u957f\u7684\u6837\u513f\uff0c\u60f3\u6446\u6446\u7a7a\u67b6\u5b50\uff0c\u6765\u6361\u4fbf\u5b9c\uff0c\u90a3\u53ef\u4e0d\u6210\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u5927\u6012\uff0c\u53f3\u624b\u5657\u7684\u4e00\u638c\uff0c\u51fb\u5728\u8239\u5934\u684c\u4e0a\uff0c\u684c\u9762\u767b\u65f6\u788e\u88c2\u3002\u6e29\u9752\u9053\uff1a\u201c\u8363\u8001\u7237\u5b50\u7684\u529f\u592b\u5982\u4f55\uff0c\u6211\u65e9\u5c31\u77e5\u9053\uff0c\u5de6\u53f3\u4e5f\u4e0d\u8fc7\u8fd9\u70b9\u73a9\u827a\u513f\uff0c\u53c8\u4f55\u5fc5\u5728\u5c0f\u8f88\u9762\u524d\u5356\u5f04\uff1f\u4f60\u8981\u663e\u529f\u592b\uff0c\u53bb\u663e\u7ed9\u6211\u7237\u7237\u4eec\u770b\u3002\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u4f60\u522b\u62ac\u51fa\u4f60\u90a3\u51e0\u4e2a\u7237\u7237\u6765\u538b\u4eba\u3002\u4f60\u7237\u7237\u4fbf\u600e\u6837\uff1f\u4ed6\u4eec\u771f\u6709\u672c\u4e8b\uff0c\u4e5f\u4e0d\u4f1a\u8ba9\u5973\u513f\u7ed9\u4eba\u7cdf\u8e4b\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4f60\u8fd9\u5c0f\u6742\u79cd\u6765\u73b0\u4e16\u5566\uff01\u201d\u6e29\u9752\u60e8\u7136\u53d8\u8272\uff0c\u4f38\u624b\u63e1\u4f4f\u4e86\u5251\u67c4\uff0c\u4e00\u53ea\u767d\u7389\u822c\u7684\u624b\u4e0d\u4f4f\u6296\u52a8\uff0c\u663e\u662f\u6c14\u607c\u5df2\u6781\u3002\u90a3\u5927\u6c49\u548c\u5987\u4eba\u5374\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u6e29\u9752\u8138\u988a\u4e0a\u6d41\u4e0b\u4e24\u9053\u6e05\u6cea\uff0c\u5fc3\u4e2d\u8001\u5927\u4e0d\u5fcd\uff0c\u6697\u9053\uff1a\u201c\u4ed6\u884c\u4e8b\u6bd4\u6211\u8001\u7ec3\u5f97\u591a\uff0c\u600e\u4e48\u7ed9\u4eba\u4e00\u6fc0\u5c31\u54ed\u4e86\u8d77\u6765\uff1f\u8fd9\u8001\u5934\u513f\u8ddf\u4eba\u5435\u5634\uff0c\u600e\u5730\u53c8\u53bb\u9a82\u4eba\u5bb6\u7684\u7236\u6bcd\uff1f\u5e74\u7eaa\u4e00\u5927\u628a\uff0c\u5374\u4e0d\u5206\u8bf4\u9053\u7406\uff0c\u4e71\u4e03\u516b\u7cdf\u7684\uff0c\u5c3d\u8bf4\u4e9b\u96be\u542c\u8bdd\u6765\u635f\u4eba\u3002\u201d\u4ed6\u672c\u6765\u51b3\u610f\u4e24\u4e0d\u76f8\u52a9\uff0c\u4f46\u773c\u89c1\u6e29\u9752\u88ab\u4eba\u6b3a\u4fae\uff0c\u5374\u52a8\u4e86\u9504\u5f3a\u6276\u5f31\u4e4b\u5ff5\u3002 \u3000\u3000\u90a3\u8001\u8005\u9634\u68ee\u68ee\u7684\u9053\uff1a\u201c\u54ed\u6709\u751a\u4e48\u7528\uff1f\u5feb\u628a\u91d1\u5b50\u62ff\u51fa\u6765\u3002\u6211\u4eec\u81ea\u5df1\u4e5f\u4e0d\u8d2a\uff0c\u91d1\u5b50\u8981\u62ff\u53bb\u7ed9\u6c99\u8001\u5927\u7684\u5be1\u5987\u3002\u518d\u8bf4\uff0c\u8fd9\u4f4d\u8881\u670b\u53cb\u4e5f\u8be5\u5206\u4e0a\u4e00\u4efd\u3002\u201d\u8881\u627f\u5fd7\u5fd9\u6447\u624b\u9053\uff1a\u201c\u6211\u4e0d\u8981\uff01\u201d \u3000\u3000\u6e29\u9752\u6c14\u5f97\u8eab\u5b50\u53d1\u98a4\uff0c\u54ed\u9053\uff1a\u201c\u6211\u504f\u504f\u4e0d\u7ed9\u3002\u201d \u3000\u3000\u90a3\u5927\u6c49\u54fc\u4e86\u4e00\u58f0\uff0c\u89c1\u5927\u8239\u867d\u5df2\u6536\u5e06\uff0c\u4f46\u4ecd\u987a\u6c34\u4e0b\u6d41\uff0c\u4e3e\u8d77\u8239\u5934\u7684\u5927\u94c1\u951a\uff0c\u5728\u7a7a\u4e2d\u821e\u4e86\u4e00\u4e2a\u5708\uff0c\u5411\u5cb8\u4e0a\u63b7\u53bb\u3002\u90a3\u94c1\u951a\u8fde\u4e0a\u94c1\u94fe\uff0c\u4e0d\u4e0b\u4e24\u767e\u591a\u65a4\uff0c\u4ed6\u63b7\u5f97\u8fd9\u4e48\u8fdc\uff0c\u529b\u6c14\u786e\u7136\u975e\u540c\u5c0f\u53ef\u3002\u94c1\u951a\u4e00\u843d\u5728\u5cb8\u4e0a\uff0c\u5927\u8239\u767b\u65f6\u505c\u4e86\u3002\u90a3\u5927\u6c49\u53eb\u9053\uff1a\u201c\u4f60\u5230\u5e95\u62ff\u4e0d\u62ff\u51fa\u6765\uff1f\u201d \u3000\u3000\u6e29\u9752\u4e3e\u8d77\u5de6\u8896\uff0c\u62ed\u5e72\u4e86\u6cea\u6c34\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u62ff\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u5954\u8fdb\u8239\u8231\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53cc\u624b\u6367\u7740\u4e00\u4e2a\u5305\u88f9\u51fa\u6765\uff0c\u770b\u6a21\u6837\u751a\u662f\u6c89\u91cd\u3002\u90a3\u5927\u6c49\u6b63\u8981\u4f38\u624b\u53bb\u63a5\uff0c\u6e29\u9752\u559d\u9053\uff1a\u201c\u5478\uff0c\u6709\u8fd9\u4e48\u5bb9\u6613\uff01\u201d \u3000\u3000\u624b\u4e0a\u4f7f\u52b2\uff0c\u90a3\u5305\u88f9\u76f4\u98de\u51fa\u53bb\uff0c\u6251\u901a\u4e00\u58f0\u5927\u54cd\u3001\u843d\u5165\u6c5f\u5fc3\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u6709\u79cd\u5c31\u628a\u6211\u6740\u4e86\uff0c\u8981\u60f3\u5f97\u91d1\u5b50\u5417\uff1f\u522b\u5984\u60f3\u5566\uff01\u201d\u90a3\u5927\u6c49\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u62d4\u5200\u5411\u4ed6\u780d\u6765\u3002 \u3000\u3000\u6e29\u9752\u4e00\u63b7\u51fa\u5305\u88f9\uff0c\u65e9\u5df2\u62e8\u5251\u5728\u624b\uff0c\u5237\u5237\u4e24\u5251\uff0c\u8fd8\u523a\u5927\u6c49\u3002 \u3000\u3000\u90a3\u8001\u8005\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u5927\u6c49\u56de\u67b6\u6765\u5251\uff0c\u8dc3\u5f00\u4e24\u6b65\u3002 \u3000\u3000\u90a3\u8001\u8005\u5411\u6e29\u9752\u4fa7\u76ee\u659c\u89c6\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u679c\u7136\u9f99\u751f\u9f99\uff0c\u51e4\u751f\u51e4\uff0c\u4e4c\u9f9f\u539f\u662f\u738b\u516b\u79cd\u3002\u6709\u8fd9\u6837\u7684\u8001\u5b50\uff0c\u5c31\u751f\u8fd9\u6837\u7684\u5c0f\u755c\u751f\u3002\u4eca\u65e5\u518d\u8ba9\u4f60\u8fd9\u5c0f\u8f88\u5728\u8001\u592b\u9762\u524d\u653e\u8086\uff0c\u6211\u5c31\u4e0d\u59d3\u8363\u5566\u3002\u201d\u4e5f\u4e0d\u89c1\u4ed6\u8eab\u5b50\u6643\u52a8\uff0c\u7a81\u7136\u62d4\u4e86\u8d77\u6765\uff0c\u843d\u5728\u6e29\u9752\u9762\u524d\u3002\u6e29\u9752\u633a\u5251\u523a\u53bb\uff0c\u90a3\u8001\u8005\u7a7a\u624b\u8fdb\u62db\uff0c\u8fd0\u638c\u6210\u98ce\uff0c\u653b\u52bf\u51cc\u5389\u4e4b\u6781\u3002\u6e29\u9752\u867d\u6709\u957f\u5251\u5728\u624b\uff0c\u5374\u88ab\u4ed6\u903c\u5f97\u8fde\u8fde\u5012\u9000\u3002\u62c6\u5f97\u5341\u591a\u62db\uff0c\u6e29\u9752\u53f3\u8155\u5ffd\u88ab\u4ed6\u624b\u6307\u70b9\u4e2d\uff0c\u957f\u5251\u5f53\u5577\u843d\u5730\u3002\u90a3\u8001\u8005\u811a\u5c16\u4e00\u6311\uff0c\u628a\u5251\u8e22\u4e86\u8d77\u6765\uff0c\u5de6\u624b\u63e1\u4f4f\u5251\u67c4\uff0c\u53f3\u624b\u642d\u5b9a\u5251\u5c16\uff0c\u53cc\u624b\u91cc\u5f2f\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u5251\u8eab\u767b\u65f6\u6298\u65ad\u3002\u6e29\u9752\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u8001\u8005\u559d\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u5728\u4f60\u8eab\u4e0a\u7559\u4e2a\u8bb0\u53f7\uff0c\u53ea\u6015\u4f60\u65e5\u540e\u5fd8\u4e86\u8001\u592b\u7684\u5389\u5bb3\uff01\u201d\u624b\u6301\u65ad\u5251\uff0c\u5411\u4ed6\u8138\u4e0a\u5212\u53bb\u3002\u6e29\u9752\u60ca\u547c\u95ea\u907f\uff0c\u8001\u8005\u6b65\u6b65\u8fdb\u903c\uff0c\u6beb\u4e0d\u653e\u677e\uff0c\u5de6\u624b\u9012\u51fa\uff0c\u5251\u5c16\u9752\u5149\u95ea\u70c1\uff0c\u773c\u89c1\u4fbf\u8981\u5212\u5230\u6e29\u9752\u8138\u4e0a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u518d\u4e0d\u51fa\u624b\uff0c\u4ed6\u8138\u4e0a\u975e\u53d7\u91cd\u4f24\u4e0d\u53ef\u3002\u201d\u4ece\u56ca\u4e2d\u638f\u51fa\u4e00\u679a\u94dc\u94b1\uff0c\u5411\u8001\u8005\u624b\u4e2d\u65ad\u5251\u4e0a\u6295\u53bb\u3002 \u3000\u3000\u5f53\u7684\u4e00\u58f0\uff0c\u8001\u8005\u53ea\u611f\u624b\u4e0a\u4e00\u9707\uff0c\u4e00\u679a\u6697\u5668\u6253\u5728\u65ad\u5251\u4e4b\u4e0a\uff0c\u649e\u51fb\u4e4b\u4e0b\uff0c\u864e\u53e3\u4e00\u75db\uff0c\u65ad\u5251\u7adf\u81ea\u8131\u624b\u3002\u6e29\u9752\u672c\u5df2\u5413\u5f97\u9762\u8272\u5927\u53d8\uff0c\u8fd9\u65f6\u559c\u6781\u800c\u547c\uff0c\u7eb5\u5230\u8881\u627f\u5fd7\u8eab\u540e\uff0c\u62c9\u7740\u4ed6\u7684\u624b\u81c2\uff0c\u4f3c\u4e4e\u6c42\u4ed6\u4fdd\u62a4\u3002 \u3000\u3000\u90a3\u8001\u8005\u59d3\u8363\u540d\u5f69\uff0c\u662f\u9f99\u6e38\u5e2e\u7684\u5e2e\u4e3b\uff0c\u5728\u6d59\u5357\u4e00\u5e26\uff0c\u9664\u4e86\u77f3\u6881\u6d3e\u4e94\u7956\u3001\u5415\u4e03\u5148\u751f\u7b49\u5be5\u5be5\u6570\u4eba\uff0c\u6b66\u529f\u6570\u4ed6\u4e3a\u9ad8\u3002\u4ed6\u5341\u6307\u7ec3\u5c31\u5927\u529b\u9b54\u722a\u529f\uff0c\u6bd4\u5bfb\u5e38\u5200\u5251\u8fd8\u66f4\u5389\u5bb3\u3002\u54ea\u77e5\u7adf\u88ab\u5bf9\u65b9\u4e00\u679a\u5c0f\u5c0f\u6697\u5668\u5c06\u624b\u4e2d\u5175\u5203\u6253\u843d\uff0c\u771f\u662f\u751f\u5e73\u672a\u9047\u4e4b\u5947\u803b\u5927\u8fb1\uff0c\u767b\u65f6\u9762\u7ea2\u8fc7\u8033\uff0c\u5374\u53c8\u4e0d\u7981\u6697\u6697\u5fc3\u60ca\uff1a\u201c\u8fd9\u5c0f\u4f19\u5b50\u7684\u624b\u52b2\u600e\u5730\u5982\u6b64\u4e86\u5f97\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u548c\u5987\u4eba\u4e5f\u5df2\u770b\u51fa\u8881\u627f\u5fd7\u6b66\u529f\u60ca\u4eba\uff0c\u5fc3\u60f3\u53cd\u6b63\u91d1\u5b50\u5df2\u7ed9\u4e22\u5165\u6c5f\u4e2d\uff0c\u4eca\u65e5\u6709\u8fd9\u786c\u624b\u5728\u8fd9\u91cc\uff0c\u65e0\u8bba\u5982\u4f55\u5360\u4e0d\u5230\u4fbf\u5b9c\u4e86\uff0c\u4e0d\u5982\u4ea4\u5f85\u51e0\u53e5\u573a\u9762\u8bdd\uff0c\u5c31\u6b64\u9000\u8d70\u3002\u90a3\u5987\u4eba\u53eb\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u54b1\u4eec\u8d70\u5427\uff0c\u51b2\u7740\u8fd9\u4f4d\u8881\u670b\u53cb\uff0c\u4eca\u65e5\u5c31\u9976\u4e86\u8fd9\u5a03\u513f\u3002\u201d \u3000\u3000\u6e29\u9752\u53eb\u9053\uff1a\u201c\u89c1\u4eba\u5bb6\u672c\u9886\u597d\uff0c\u5c31\u60f3\u8d70\u5566\uff0c\u4f60\u4eec\u9f99\u6e38\u5e2e\u5c31\u4f1a\u6b3a\u8f6f\u6015\u786c\uff0c\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u201d\u8881\u627f\u5fd7\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u60f3\u8fd9\u4eba\u521a\u8131\u5927\u96be\uff0c\u968f\u5373\u5982\u6b64\u5c16\u9178\u523b\u8584\uff0c\u4e0d\u7ed9\u4eba\u7559\u4e1d\u6beb\u4f59\u5730\u3002\u90a3\u5987\u4eba\u7ed9\u4ed6\u8bf4\u5f97\u795e\u60c5\u72fc\u72c8\uff0c\u52a8\u6b66\u53c8\u4e0d\u662f\uff0c\u4e0d\u7406\u53c8\u4e0d\u662f\uff0c\u6ee1\u8138\u6012\u5bb9\u3002 \u3000\u3000\u8363\u5f69\u4e5f\u611f\u96be\u4ee5\u4e0b\u53f0\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u529f\u592b\u771f\u4fca\uff0c\u4eca\u65e5\u76f8\u9022\uff0c\u4e5f\u662f\u6709\u7f18\uff0c\u54b1\u4fe9\u6765\u73a9\u4e00\u8d9f\u62f3\u811a\u5982\u4f55\uff1f\u201d\u4ed6\u5728\u5927\u529b\u9e70\u722a\u624b\u4e0a\u4e0b\u8fc7\u4e8c\u5341\u4f59\u5e74\u82e6\u529f\uff0c\u9887\u5177\u81ea\u4fe1\uff0c\u5fc3\u60f3\u4f60\u8fd9\u5c0f\u5b50\u6697\u5668\u529f\u592b\u867d\u597d\uff0c\u5728\u62f3\u811a\u4e0a\u5374\u51b3\u4e0d\u80fd\u8f93\u4e86\u7ed9\u4f60\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5bfb\u601d\uff1a\u201c\u5982\u548c\u8fd9\u8001\u8005\u4e00\u52a8\u624b\uff0c\u5c31\u7b97\u662f\u52a9\u5b9a\u4e86\u6e29\u9752\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u5fc3\u80f8\u72ed\u9698\uff0c\u5201\u94bb\u72e1\u733e\uff0c\u4e3a\u4e86\u4e00\u4e9b\u91d1\u5b50\u4fbf\u80e1\u4e71\u6740\u4eba\u3002\u51b3\u4e0d\u80fd\u662f\u76ca\u53cb\u3002\u4f55\u5fc5\u4e3a\u4ed6\u800c\u65e0\u8c13\u4e0e\u4eba\u7ed3\u6028\u3002\u201d\u4e8e\u662f\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u521d\u6d89\u6c5f\u6e56\uff0c\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u3002\u4e00\u70b9\u5fae\u672b\u5c0f\u6280\uff0c\u5982\u4f55\u6562\u5728\u8001\u524d\u8f88\u9762\u524d\u732e\u4e11\uff1f\u201d \u3000\u3000\u8363\u5f69\u5fae\u5fae\u4e00\u7b11\u3002\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c11\u5e74\u5012\u5f88\u4f1a\u505a\u4eba\u3002\u201d\u4ed6\u4e58\u6b64\u6536\u7bf7\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u670b\u53cb\u592a\u5ba2\u6c14\u4e86\uff01\u201d\u72e0\u72e0\u77aa\u4e86\u6e29\u9752\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u7ec8\u6709\u4e00\u5929\uff0c\u6559\u4f60\u8fd9\u5a03\u513f\u77e5\u9053\u8001\u592b\u7684\u5389\u5bb3\u3002\u201d\u8f6c\u5934\u5bf9\u90a3\u5927\u6c49\u4e0e\u5987\u4eba\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u4f60\u6709\u591a\u5927\u5389\u5bb3\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u5566\u3002\u89c1\u5230\u4eba\u5bb6\u529f\u592b\u597d\uff0c\u5c31\u662f\u4e0d\u6562\u52a8\u624b\uff0c\u5df4\u4e0d\u5f97\u60f3\u65e9\u65e9\u626f\u547c\uff0c\u8d76\u56de\u5bb6\u53bb\uff0c\u5148\u670d\u51e0\u5305\u5b9a\u60ca\u6563\uff0c\u518d\u628a\u5934\u94bb\u5728\u88ab\u7a9d\u91cc\u53d1\u6296\u3002\u201d\u4ed6\u5634\u4e0a\u4e1d\u6beb\u4e0d\u80af\u8ba9\u4eba\uff0c\u7acb\u610f\u8981\u6311\u62e8\u4ed6\u4e0e\u8881\u627f\u5fd7\u8fc7\u62db\u3002\u4ed6\u770b\u51fa\u8881\u627f\u5fd7\u6b66\u529f\u9ad8\u5f3a\uff0c\u8363\u5f69\u4e0d\u662f\u654c\u624b\u3002\u8fd9\u4e00\u6765\u4e0d\u4f46\u8363\u5f69\u5c34\u5c2c\u4e07\u5206\uff0c\u8fde\u8881\u627f\u5fd7\u4e5f\u81ea\u53d1\u607c\u3002 \u3000\u3000\u8363\u5f69\u6012\u9053\uff1a\u201c\u8fd9\u4f4d\u8881\u670b\u53cb\u5e74\u7eaa\u867d\u8f7b\uff0c\u53ef\u662f\u5f88\u8bb2\u4ea4\u60c5\uff0c\u6765\u6765\u6765\uff0c\u54b1\u4eec\u6765\u73a9\u4e00\u624b\uff0c\u522b\u8ba9\u65e0\u77e5\u5c0f\u8f88\u8bf4\u6211\u6ca1\u80c6\u5b50\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8001\u524d\u8f88\u4f55\u5fc5\u548c\u4ed6\u4e00\u822c\u89c1\u8bc6\uff0c\u4ed6\u662f\u8bf4\u73a9\u8bdd\u3002\u201d\u8363\u5f69\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u51b3\u4e0d\u548c\u4f60\u5f53\u771f\u3002\u201d \u3000\u3000\u6e29\u9752\u51b7\u51b7\u7684\u9053\uff1a\u201c\u8fd8\u8bf4\u4e0d\u6015\u5462\uff0c\u6ca1\u52a8\u624b\uff0c\u5148\u5957\u4ea4\u60c5\uff0c\u8d76\u5feb\u8fd8\u662f\u522b\u8fc7\u62db\u7684\u597d\u3002\u6211\u6d3b\u4e86\u8fd9\u4e48\u5927\uff0c\u8fd8\u6ca1\u89c1\u8fc7\u8fd9\u6837\uff0c\u54fc\uff0c\u54fc\uff0c\u8fd9\u7b97\u751a\u4e48\uff1f\u6211\u53ef\u8bf4\u4e0d\u4e0a\u6765\u5566\u3002\u8363\u8001\u7237\u5b50\uff0c\u4f60\u65e2\u6015\u5f97\u5f88\u4e86\uff0c\u4f55\u4e0d\u8bf7\u8fd9\u4f4d\u8881\u76f8\u516c\u56de\u53bb\uff0c\u8bf7\u4ed6\u6765\u5f53\u9f99\u6e38\u5e2e\u7684\u5e2e\u4e3b\u5462\uff1f\u201d \u3000\u3000\u8363\u5f69\u6012\u6c14\u51b2\u5929\uff0c\u6325\u62f3\u5288\u9762\u5411\u8881\u627f\u5fd7\u524a\u53bb\uff0c\u638c\u7f18\u5c06\u8fd1\u4ed6\u9762\u95e8\uff0c\u500f\u5730\u6536\u56de\uff0c\u53eb\u9053\uff1a\u201c\u8881\u670b\u53cb\uff0c\u6765\u6765\u6765\uff0c\u6211\u8bf7\u6559\u8bf7\u6559\u4f60\u7684\u9ad8\u660e\u62db\u672f\u3002\u201d \u3000\u3000\u5230\u4e86\u8fd9\u5730\u6b65\uff0c\u8881\u627f\u5fd7\u5df2\u4e0d\u80fd\u4e0d\u51fa\u624b\uff0c\u53ea\u5f97\u7eb5\u5230\u8239\u5934\u4e2d\u95f4\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u638c\u4e0b\u7559\u60c5\u3002\u201d\u8363\u5f69\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u4f60\u8fdb\u62db\u5427\uff0c\u5927\u5bb6\u521d\u6b21\u89c1\u9762\u3002\u65e0\u51a4\u65e0\u4ec7\uff0c\u70b9\u5230\u5373\u6b62\u4fbf\u662f\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u662f\u554a\uff0c\u8881\u5144\uff0c\u4ed6\u5728\u8ba8\u9976\u5462\uff0c\u82e6\u82e6\u54c0\u6c42\u4f60\u522b\u6253\u75db\u4e86\u4ed6\u7684\u8001\u9aa8\u5934\u3002\u201d\u8363\u5f69\u5478\u7684\u4e00\u58f0\uff0c\u4e00\u53e3\u6d53\u75f0\u5411\u6e29\u9752\u5410\u4e86\u8fc7\u53bb\u3002\u6e29\u9752\u563b\u563b\u4e00\u7b11\uff0c\u4fa7\u8eab\u907f\u8fc7\u3002\u8881\u627f\u5fd7\u77e5\u9053\u82e5\u518d\u8c26\u900a\uff0c\u90a3\u5c31\u662f\u77a7\u4eba\u4e0d\u8d77\uff0c\u5c55\u5f00\u4e94\u884c\u62f3\uff0c\u53d1\u62f3\u5f53\u80f8\u6253\u53bb\u3002 \u3000\u3000\u8363\u5f69\u548c\u65c1\u89c2\u4e09\u4eba\u672c\u6765\u90fd\u4ee5\u4e3a\u4ed6\u6b66\u529f\u6709\u72ec\u5230\u4e4b\u79d8\uff0c\u54ea\u77e5\u4f7f\u51fa\u6765\u7684\u7adf\u662f\u6b66\u6797\u4e2d\u6700\u5bfb\u5e38\u4e0d\u8fc7\u7684\u4e94\u884c\u62f3\u3002\u654c\u5bf9\u4e09\u4eba\u767b\u65f6\u610f\u5b58\u8f7b\u89c6\uff0c\u6e29\u9752\u8138\u4e0a\u4e0d\u81ea\u7981\u9732\u51fa\u5931\u671b\u7684\u795e\u8272\u3002 \u3000\u3000\u8363\u5f69\u5fc3\u4e2d\u6697\u559c\uff0c\u53cc\u62f3\u5982\u98ce\uff0c\u8fde\u62a2\u4e09\u4e0b\u653b\u52bf\uff0c\u6ee1\u62df\u81ea\u5df1\u7684\u5927\u529b\u9b54\u722a\u624b\u6c5f\u5357\u72ec\u6b65\uff0c\u4e09\u56db\u62db\u4e4b\u95f4\u5c31\u53ef\u7834\u53bb\u5bf9\u65b9\u4e94\u884c\u62f3\uff0c\u90a3\u77e5\u8881\u627f\u5fd7\u8f7b\u63cf\u6de1\u5199\u7684\u4e00\u4e00\u5316\u89e3\u3002\u518d\u62c6\u6570\u62db\uff0c\u8363\u5f69\u6697\u6697\u5403\u60ca\uff0c\u539f\u6765\u5bf9\u65b9\u6240\u4f7f\u867d\u662f\u6781\u5bfb\u5e38\u7684\u62f3\u672f\uff0c\u4f46\u6bcf\u4e00\u62db\u5747\u662f\u542b\u52b2\u4e0d\u5410\uff0c\u610f\u5728\u62f3\u5148\uff0c\u4e3e\u624b\u62ac\u8db3\u4e4b\u95f4\u9690\u542b\u6781\u6d51\u539a\u7684\u5185\u529b\u3002\u4e94\u884c\u62f3\u672c\u4ee5\u731b\u653b\u4e3a\u4e3b\uff0c\u4f46\u4ed6\u5168\u4e0d\u62a2\u653b\uff0c\u53ea\u662f\u5c55\u5f00\u67b6\u5f0f\uff0c\u4f7f\u8363\u5f69\u53cc\u624b\u6b3a\u4e0d\u8fd1\u8eab\u3002 \u3000\u3000\u8363\u5f69\u5fc3\u4e2d\u7126\u8e81\uff0c\u5fc3\u60f3\u4ed6\u660e\u660e\u662f\u5728\u8ba9\u7740\u81ea\u5df1\uff0c\u5982\u88ab\u6e29\u9752\u4e00\u8bf4\u7a7f\uff0c\u8001\u8138\u53ef\u6302\u4e0d\u4f4f\u4e86\uff0c\u84e6\u5730\u62f3\u62db\u4e00\u53d8\uff0c\u6539\u638c\u4e3a\u6293\uff0c\u53cc\u624b\u624b\u6307\u5c3d\u662f\u6293\u5411\u5bf9\u65b9\u8981\u5bb3\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u8d8a\u6765\u8d8a\u5feb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u9b54\u722a\u529f\u7ec3\u5230\u6b64\u5730\u6b65\uff0c\u4e5f\u975e\u4e00\u671d\u4e00\u5915\u4e4b\u529f\uff0c\u5f97\u7ed9\u4ed6\u7559\u4e0b\u989c\u9762\uff0c\u5982\u4e0d\u8ba9\u4ed6\u4e00\u62db\uff0c\u6e29\u9752\u514d\u4e0d\u5f97\u8fd8\u8981\u8bf4\u5634\u3002\u201d \u3000\u3000\u4ed6\u81ea\u827a\u6210\u4e0b\u5c71\uff0c\u6b64\u523b\u662f\u521d\u6b21\u4e0e\u4eba\u52a8\u624b\u8fc7\u62db\uff0c\u51b3\u610f\u9075\u7167\u5e08\u7236\u53ee\u5631\uff0c\u5bb9\u8ba9\u4e3a\u5148\uff0c\u773c\u89c1\u8363\u5f69\u53f3\u624b\u5411\u81ea\u5df1\u80a9\u5934\u6293\u6765\uff0c\u6545\u610f\u5e76\u4e0d\u9000\u907f\u3002\u8363\u5f69\u5927\u559c\uff0c\u5fc3\u4e2d\u5012\u5e76\u4e0d\u60f3\u4f24\u4ed6\uff0c\u53ea\u62df\u5c06\u4ed6\u8863\u670d\u6495\u7834\u4e00\u5757\uff0c\u5c31\u7b97\u8d62\u4e86\u4e00\u62db\uff0c\u54ea\u77e5\u4e00\u6293\u5230\u4ed6\u7684\u80a9\u5934\uff0c\u7a81\u89c9\u4ed6\u808c\u8089\u6ed1\u6e9c\u5f02\u5e38\uff0c\u7adf\u50cf\u6c34\u4e2d\u6293\u5230\u4e00\u5c3e\u5927\u9c7c\u90a3\u6837\uff0c\u4e00\u4e0b\u5b50\u5c31\u88ab\u4ed6\u6ed1\u4e86\u5f00\u53bb\uff0c\u6b63\u81ea\u4e00\u60ca\uff0c\u8881\u627f\u5fd7\u5df2\u8df3\u5f00\u4e24\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8f93\u4e86\uff01\u201d\u8363\u5f69\u62f1\u624b\u9053\uff1a\u201c\u627f\u8ba9\uff0c\u627f\u8ba9\uff01\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u4ed6\u662f\u771f\u7684\u8ba9\u4f60\uff0c\u4f60\u81ea\u77e5\u4e4b\u660e\u5012\u8fd8\u6709\u7684\uff0c\u77e5\u9053\u4e86\u5c31\u597d\u5566\uff01\u201d \u3000\u3000\u8363\u5f69\u8138\u4e00\u677f\uff0c\u6b63\u5f85\u53d1\u4f5c\uff0c\u5ffd\u89c1\u5cb8\u4e0a\u706b\u5149\u95ea\u52a8\uff0c\u6570\u5341\u4eba\u624b\u6267\u5175\u5203\u706b\u628a\uff0c\u5feb\u6b65\u5954\u6765\u3002\u5f53\u5148\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u8363\u8001\u7237\u5b50\uff0c\u5df2\u628a\u90a3\u5c0f\u5b50\u6293\u5230\u4e86\u5427\uff1f\u54b1\u4eec\u628a\u8fd9\u5c0f\u5b50\u5250\u4e86\uff0c\u7ed9\u6c99\u8001\u5927\u62a5\u4ec7\uff01\u201d \u3000\u3000\u6e29\u9752\u89c1\u5bf9\u65b9\u5927\u961f\u62e5\u5230\uff0c\u867d\u7136\u80c6\u5927\u5984\u4e3a\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u60f4\u60f4\u3002 \u3000\u3000\u8363\u5f69\u53eb\u9053\uff1a\u201c\u5218\u5bb6\u5144\u5f1f\uff0c\u4f60\u4eec\u4e24\u4eba\u8fc7\u6765\uff01\u201d\u5cb8\u4e0a\u4e24\u4eba\u5e94\u58f0\u8d70\u5230\u5cb8\u8fb9\uff0c\u89c1\u5927\u8239\u79bb\u5cb8\u751a\u8fdc\uff0c\u6251\u901a\u4e24\u58f0\u8df3\u5165\u6c5f\u5185\uff0c\u8fc5\u901f\u6e38\u5230\u8239\u8fb9\uff0c\u6c34\u6027\u6781\u662f\u4e86\u5f97\uff0c\u5355\u624b\u5728\u8239\u8237\u4e0a\u4e00\u642d\uff0c\u6251\u5730\u8df3\u4e86\u4e0a\u6765\u3002\u8363\u5f69\u9053\uff1a\u201c\u90a3\u5305\u8d27\u8272\u7ed9\u8fd9\u5c0f\u5b50\u4e22\u5230\u6c5f\u5fc3\u53bb\u5566\uff0c\u4f60\u54e5\u513f\u4fe9\u53bb\u6361\u8d77\u6765\uff01\u201d\u8bf4\u7740\u5411\u6c5f\u5fc3\u4e00\u6307\u3002\u5218\u6c0f\u5144\u5f1f\u8dc3\u843d\u6c5f\u4e2d\uff0c\u6f5c\u5165\u6c34\u5185\u3002 \u3000\u3000\u6e29\u9752\u4e00\u626f\u8881\u627f\u5fd7\u7684\u8896\u5b50\uff0c\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5feb\u6551\u6551\u6211\u5427\uff0c\u4ed6\u4eec\u8981\u6740\u6211\u5462\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u56de\u8fc7\u5934\u6765\uff0c\u6708\u5149\u4e0b\u89c1\u4ed6\u5bb9\u8272\u6101\u82e6\uff0c\u4e00\u526f\u695a\u695a\u53ef\u601c\u7684\u795e\u6c14\uff0c\u4fbf\u70b9\u4e86\u70b9\u5934\u3002\u6e29\u9752\u62c9\u4f4f\u4ed6\u7684\u624b\u9053\uff1a\u201c\u4ed6\u4eec\u4eba\u591a\u52bf\u4f17\u3002 \u3000\u3000\u4f60\u60f3\u6cd5\u5b50\u65a9\u65ad\u94c1\u94fe\uff0c\u54b1\u4eec\u5f00\u8239\u9003\u8d70\u3002\u201d\u8881\u627f\u5fd7\u8fd8\u672a\u7b54\u5e94\uff0c\u53ea\u89c9\u6e29\u9752\u7684\u624b\u53c8\u8f6f\u53c8\u817b\uff0c\u67d4\u82e5\u65e0\u9aa8\uff0c\u751a\u611f\u8be7\u5f02\uff1a\u201c\u8fd9\u4eba\u7684\u624b\u638c\u50cf\u68c9\u82b1\u4e00\u6837\uff0c\u5f53\u771f\u5e0c\u5947\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8363\u5f69\u5df2\u7559\u610f\u5230\u4e24\u4eba\u5728\u7a83\u7a83\u79c1\u8bae\uff0c\u56de\u5934\u671b\u6765\u3002\u6e29\u9752\u628a\u8881\u627f\u5fd7\u7684\u624b\u634f\u4e86\u4e00\u628a\uff0c\u7a81\u7136\u731b\u529b\u4e3e\u8d77\u8239\u5934\u684c\u5b50\uff0c\u5411\u8363\u5f69\u7b49\u4e09\u4eba\u63a8\u53bb\u3002 \u3000\u3000\u90a3\u5927\u6c49\u4e0e\u5987\u4eba\u6b63\u5168\u795e\u671b\u7740\u5218\u6c0f\u5144\u5f1f\u6f5c\u6c34\u53d6\u91d1\uff0c\u51fa\u5176\u4e0d\u610f\uff0c\u80cc\u4e0a\u88ab\u684c\u5b50\u4e00\u649e\uff0c\u60ca\u53eb\u4e00\u58f0\uff0c\u4e00\u9f50\u6389\u4e0b\u6c34\u53bb\u3002\u8363\u5f69\u7eb5\u8eab\u8dc3\u8d77\uff0c\u4f38\u638c\u6293\u51fa\uff0c\u4e94\u6307\u5d4c\u5165\u684c\u9762\uff0c\u7528\u529b\u4e00\u62c9\u4e00\u6380\uff0c\u683c\u683c\u4e24\u58f0\uff0c\u6e29\u9752\u63e1\u7740\u7684\u684c\u811a\u5df2\u7136\u6298\u65ad\u3002\u8363\u5f69\u77e5\u9053\u90a3\u5927\u6c49\u4e0e\u5987\u4eba\u4e0d\u4f1a\u6c34\u6027\uff0c\u8fd9\u65f6\u6c5f\u6d41\u6b63\u6025\uff0c\u5218\u6c0f\u5144\u5f1f\u76f8\u8ddd\u751a\u8fdc\uff0c\u4e0d\u53ca\u8fc7\u6765\u6551\u63f4\uff0c\u5fd9\u628a\u684c\u5b50\u629b\u5165\u6c5f\u4e2d\uff0c\u8ba9\u4e8c\u4eba\u6500\u4f4f\u4e86\u4e0d\u81f4\u6c89\u4e0b\uff0c\u968f\u5373\u53cc\u62f3\u547c\u547c\u4e24\u62db\uff0c\u5411\u6e29\u9752\u5288\u9762\u6253\u6765\u3002 \u3000\u3000\u6e29\u9752\u63d0\u4e86\u4e24\u6761\u684c\u817f\uff0c\u62a4\u4f4f\u9762\u95e8\uff0c\u6025\u53eb\uff1a\u201c\u5feb\uff01\u4f60\u3002\u201d\u8881\u627f\u5fd7\u63d0\u8d77\u94c1\u94fe\uff0c\u201c\u6df7\u5143\u529f\u201d\u5185\u52b2\u5230\u5904\uff0c\u4e00\u63d0\u4e00\u62c9\uff0c\u90a3\u53ea\u5927\u94c1\u951a\u547c\u7684\u4e00\u58f0\uff0c\u79bb\u5cb8\u5411\u8239\u5934\u98de\u6765\u3002\u8363\u5f69\u548c\u6e29\u9752\u5927\u60ca\uff0c\u5fd9\u5411\u4e24\u4fa7\u8dc3\u5f00\uff0c\u56de\u5934\u770b\u8881\u627f\u5fd7\u65f6\uff0c\u4f46\u89c1\u4ed6\u624b\u4e2d\u6258\u4f4f\u94c1\u951a\uff0c\u7f13\u7f13\u653e\u5728\u8239\u5934\u3002\u94c1\u951a\u4e00\u8d77\uff0c\u5927\u8239\u767b\u65f6\u5411\u4e0b\u6e38\u6d41\u53bb\uff0c\u4e0e\u5cb8\u4e0a\u4f17\u4eba\u6162\u6162\u8fdc\u79bb\u3002\u8363\u5f69\u89c1\u4ed6\u5982\u6b64\u529f\u529b\uff0c\u6599\u77e5\u82e5\u518d\u9017\u7559\uff0c\u51b3\u8ba1\u8ba8\u4e0d\u4e86\u597d\u53bb\uff0c\u53cc\u8db3\u4e00\u987f\uff0c\u63d0\u6c14\u5411\u5cb8\u4e0a\u8dc3\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u770b\u4ed6\u7684\u8eab\u6cd5\uff0c\u77e5\u4ed6\u8dc3\u4e0d\u4e0a\u5cb8\uff0c\u63d0\u8d77\u4e00\u5757\u8239\u677f\uff0c\u5411\u6c5f\u8fb9\u63b7\u53bb\u3002\u8363\u5f69\u4e0b\u843d\u65f6\u89c1\u8db3\u5e95\u832b\u832b\u4e00\u7247\u6c34\u5149\uff0c\u6b63\u81ea\u60ca\u60f6\uff0c\u7a81\u89c1\u8239\u677f\u98de\u5230\uff0c\u6070\u597d\u843d\u5728\u811a\u4e0b\u6c34\u9762\u4e4b\u4e0a\uff0c\u5f53\u771f\u5927\u559c\u8fc7\u671b\uff0c\u5de6\u811a\u5728\u8239\u677f\u4e0a\u4e00\u501f\u529b\u3002\u8dc3\u4e0a\u4e86\u5cb8\uff0c\u6697\u6697\u611f\u6fc0\u4ed6\u7684\u597d\u610f\uff0c\u53c8\u4e0d\u7981\u4f69\u670d\u4ed6\u7684\u529f\u529b\uff0c\u81ea\u5df1\u4eba\u5148\u8dc3\u51fa\uff0c\u4ed6\u98de\u63b7\u8239\u677f\uff0c\u5c45\u7136\u80fd\u53ca\u65f6\u8d76\u5230\u3002 \u3000\u3000\u6e29\u9752\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\uff0c\u4fbf\u662f\u7231\u505a\u6ee5\u597d\u4eba\uff01 \u3000\u3000\u5230\u5e95\u4f60\u662f\u5e2e\u6211\u5462\uff0c\u8fd8\u662f\u5e2e\u8fd9\u8001\u5934\u513f\uff1f\u8ba9\u4ed6\u5728\u6c34\u91cc\u6d78\u4e00\u4e0b\uff0c\u559d\u51e0\u53e3\u6c5f\u6c34\u4e0d\u597d\u5417\uff1f\u53c8\u4e0d\u4f1a\u6df9\u6b7b\u4eba\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u77e5\u9053\u8fd9\u4eba\u53e4\u602a\uff0c\u4e0d\u613f\u518d\u7406\uff0c\u5fc3\u60f3\u8fd9\u79cd\u4eba\u4ee5\u5c11\u52a0\u62db\u60f9\u4e3a\u5999\uff0c\u81ea\u5df1\u6551\u4e86\u4ed6\u6027\u547d\uff0c\u4ed6\u975e\u4f46\u6beb\u4e0d\u611f\u6069\uff0c\u53cd\u800c\u5982\u6b64\u65e0\u793c\u6570\u8bf4\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u63a5\u53e3\uff0c\u56de\u5230\u8231\u91cc\u7761\u4e86\u3002 \u3000\u3000\u6b21\u65e5\u4e0b\u5348\u8239\u5230\u8862\u5dde\uff0c\u8881\u627f\u5fd7\u8c22\u4e86\u9f99\u5fb7\u90bb\uff0c\u53d6\u51fa\u4e94\u94b1\u94f6\u5b50\u7ed9\u8239\u8001\u5927\u3002\u9f99\u5fb7\u90bb\u5b9a\u8981\u4ee3\u4ed8\uff0c\u8881\u627f\u5fd7\u63a8\u8f9e\u4e0d\u5f97\uff0c\u53ea\u5f97\u53c8\u4f5c\u63d6\u76f8\u8c22\u3002 \u3000\u3000\u6e29\u9752\u5bf9\u9f99\u5fb7\u90bb\u9053\uff1a\u201c\u6211\u77e5\u4f60\u4e0d\u80af\u66ff\u6211\u7ed9\u8239\u94b1\uff0c\u54fc\uff0c\u4f60\u5c31\u662f\u8981\u7ed9\uff0c\u6211\u4e5f\u4e0d\u8981\u4f60\u7684\u3002\u201d\u4ece\u5305\u88f9\u4e2d\u53d6\u51fa\u4e00\u53ea\u5341\u4e24\u91cd\u7684\u94f6\u5143\u5b9d\u6765\uff0c\u63b7\u7ed9\u8239\u8001\u5927\uff0c\u9053\uff1a\u201c\u7ed9\u4f60\u3002\u201d\u8239\u8001\u5927\u89c1\u8fd9\u4e48\u5927\u4e00\u53ea\u5143\u5b9d\uff0c\u5413\u5f97\u5446\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u627e\u4e0d\u51fa\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u8c01\u8981\u4f60\u627e\uff1f\u90fd\u7ed9\u4f60\u3002\u201d\u8239\u8001\u5927\u4e0d\u6562\u76f8\u4fe1\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u8fd9\u8bb8\u591a\u3002\u201d\u6e29\u9752\u9a82\u9053\uff1a\u201c\u5570\u55e6\u751a\u4e48\uff1f \u3000\u3000\u6211\u7231\u7ed9\u8fd9\u8bb8\u591a\uff0c\u5c31\u7ed9\u8fd9\u8bb8\u591a\uff0c\u4f60\u62db\u5f97\u6211\u607c\u8d77\u4e0a\u6765\uff0c\u628a\u4f60\u8239\u5e95\u4e0a\u6253\u51e0\u4e2a\u7a9f\u7abf\uff0c\u6559\u4f60\u8fd9\u6761\u8239\u6c89\u4e86\uff01\u201d\u8239\u8001\u5927\u6628\u665a\u89c1\u4ed6\u529b\u6740\u6570\u4eba\uff0c\u51f6\u72e0\u5f02\u5e38\uff0c\u4e0d\u6562\u591a\u8bf4\uff0c\u8fde\u8c22\u4e5f\u4e0d\u6562\u8c22\uff0c\u5fd9\u628a\u5143\u5b9d\u6536\u8d77\u3002 \u3000\u3000\u6e29\u9752\u5728\u684c\u4e0a\u6253\u5f00\u5305\u88f9\uff0c\u4e00\u9635\u91d1\u5149\u8000\u773c\uff0c\u5305\u88f9\u4e2d\u7d2f\u7d2f\u7686\u662f\u9ec4\u91d1\uff0c\u5341\u4e24\u4e00\u6761\u7684\u91d1\u6761\u603b\u6709\u4e8c\u767e\u6765\u6761\uff0c\u4ed6\u53f3\u62f3\u5728\u91d1\u6761\u5806\u4e2d\u5207\u4e86\u4e0b\u53bb\uff0c\u5e73\u5206\u6210\u4e24\u4efd\uff0c\u5c06\u4e00\u4efd\u5305\u5728\u5305\u88f9\uff0c\u80cc\u5728\u80cc\u4e0a\uff0c\u53cc\u624b\u628a\u53e6\u4e00\u5806\u91d1\u6761\u63a8\u5230\u8881\u627f\u5fd7\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u7ed9\u4f60\uff01\u201d\u8881\u627f\u5fd7\u4e0d\u89e3\uff0c\u95ee\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d \u3000\u3000\u6e29\u9752\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u6211\u771f\u7684\u628a\u91d1\u5b50\u629b\u5230\u4e86\u6c5f\u91cc\u5417\uff1f\u50bb\u6b7b\u5566\uff01 \u3000\u3000\u8ba9\u4ed6\u4eec\u53bb\u6c5f\u5e95\u778e\u6478\uff0c\u6478\u6765\u6478\u53bb\u53ea\u662f\u8863\u670d\u5305\u7740\u7684\u4e00\u5757\u538b\u8231\u77f3\u3002\u201d \u3000\u3000\u8bf4\u7740\u683c\u683c\u5927\u7b11\uff0c\u53ea\u7b11\u5f97\u524d\u4ef0\u540e\u5408\uff0c\u4f0f\u5728\u684c\u5b50\u4e0a\u8eab\u5b50\u53d1\u98a4\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e5f\u4e0d\u7981\u4f69\u670d\u4ed6\u7684\u673a\u667a\uff0c\u5fc3\u60f3\u8fd9\u4eba\u5e74\u7eaa\u6bd4\u81ea\u5df1\u8fd8\u8f7b\u7740\u4e00\u4e24\u5c81\uff0c\u8fde\u8363\u5f69\u8fd9\u6837\u7684\u8001\u624b\u4e5f\u7ed9\u4ed6\u7792\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8981\uff0c\u4f60\u90fd\u62ff\u53bb\uff0c\u6211\u5e2e\u4f60\u5e76\u975e\u4e3a\u4e86\u91d1\u5b50\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u8fd9\u662f\u6211\u9001\u7ed9\u4f60\u7684\uff0c\u53c8\u4e0d\u662f\u4f60\u81ea\u5df1\u62ff\u7684\uff0c\u4f55\u5fc5\u88c5\u4f2a\u541b\u5b50\uff1f\u201d\u8881\u627f\u5fd7\u4e0d\u4f4f\u6447\u5934\u3002 \u3000\u3000\u9f99\u5fb7\u90bb\u867d\u662f\u5bcc\u5546\uff0c\u4f46\u9ec4\u6f84\u6f84\u4e00\u5927\u5806\u91d1\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u4e00\u4e2a\u4e00\u5b9a\u4e0d\u8981\uff0c\u4e00\u4e2a\u786c\u8981\u5bf9\u65b9\u62ff\u53bb\uff0c\u8fd9\u6837\u7684\u4e8b\u60c5\u56fa\u7136\u95fb\u6240\u672a\u95fb\uff0c\u6b64\u523b\u4eb2\u773c\u76ee\u7779\uff0c\u5140\u81ea\u4e0d\u4fe1\uff0c\u53ea\u9053\u8881\u627f\u5fd7\u5acc\u5c11\u3002 \u3000\u3000\u6e29\u9752\u6012\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u8981\u4e0d\u8981\uff0c\u6211\u603b\u662f\u7ed9\u4e86\u4f60\u3002\u201d\u7a81\u7136\u8dc3\u8d77\uff0c\u7eb5\u4e0a\u5cb8\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u51fa\u5176\u4e0d\u610f\uff0c\u4e00\u5446\u4e4b\u4e0b\uff0c\u5fd9\u98de\u8eab\u8ffd\u51fa\uff0c\u4e24\u4e2a\u8d77\u843d\uff0c\u5df2\u62a2\u5728\u4ed6\u9762\u524d\uff0c\u53cc\u624b\u4e00\u62e6\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u8d70\uff0c\u4f60\u628a\u91d1\u5b50\u5e26\u53bb\uff01\u201d\u6e29\u9752\u51b2\u5411\u53f3\uff0c\u4ed6\u62e6\u5728\u53f3\u9762\uff0c\u6e29\u9752\u51b2\u5411\u5de6\uff0c\u53c8\u88ab\u4ed6\u62a2\u5148\u6321\u4f4f\u3002\u6e29\u9752\u51e0\u6b21\u95ef\u4e0d\u8fc7\uff0c\u53d1\u4e86\u813e\u6c14\uff0c\u4e3e\u638c\u5411\u4ed6\u5288\u9762\u6253\u53bb\u3002\u8881\u627f\u5fd7\u4e3e\u5de6\u638c\u8f7b\u8f7b\u4e00\u67b6\uff0c\u6e29\u9752\u5df2\u81ea\u62b5\u53d7\u4e0d\u4f4f\uff0c\u5411\u540e\u8fde\u9000\u4e09\u6b65\uff0c\u8fd9\u624d\u7ad9\u4f4f\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u65e0\u6cd5\u51b2\u8fc7\uff0c\u5ffd\u7136\u5f80\u5730\u4e0b\u4e00\u5750\uff0c\u53cc\u624b\u63a9\u9762\uff0c\u545c\u545c\u54bd\u54bd\u7684\u54ed\u4e86\u8d77\u6765\uff0c\u8881\u627f\u5fd7\u5927\u5947\uff0c\u8fde\u95ee\uff1a\u201c\u6211\u9707\u75db\u4e86\u4f60\u5417\uff1f\u201d\u6e29\u9752\u5478\u4e86\u4e00\u58f0\uff1a\u201c\u4f60\u624d\u75db\u5462\uff01\u201d\u4e00\u7b11\u8dc3\u8d77\u3002\u8881\u627f\u5fd7\u4e0d\u6562\u518d\u8ffd\uff0c\u76ee\u9001\u4ed6\u80cc\u5f71\u5728\u6c5f\u8fb9\u9690\u53bb\u3002 \u3000\u3000\u773c\u89c1\u4ed6\u4e00\u8eab\u6b66\u529f\uff0c\u6740\u4eba\u4e0d\u7728\u773c\uff0c\u660e\u660e\u662f\u4e2a\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u54ea\u77e5\u53c8\u54ed\u53c8\u7b11\uff0c\u7adf\u5982\u6b64\u5201\u94bb\u53e4\u602a\uff0c\u4e0d\u7531\u5f97\u6447\u6447\u5934\u56de\u5230\u8239\u5185\uff0c\u628a\u91d1\u6761\u5305\u8d77\uff0c\u4e0e\u9f99\u5fb7\u90bb\u62f1\u624b\u4f5c\u522b\u3002 \u3000\u3000\u4ed6\u5728\u8862\u5dde\u57ce\u5185\u5927\u8857\u4e0a\u627e\u4e86\u4e00\u5bb6\u5ba2\u5e97\u4f4f\u4e0b\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e00\u5343\u4e24\u9ec4\u91d1\u5982\u4e0d\u5f52\u8fd8\uff0c\u5fc3\u4e2d\u5982\u4f55\u80fd\u5b89\uff1f\u6211\u4e0d\u8fc7\u89c1\u4ed6\u53ef\u601c\uff0c\u624d\u51fa\u624b\u76f8\u52a9\uff0c\u5c82\u80fd\u6536\u4ed6\u916c\u8c22\uff1f\u597d\u5728\u4ed6\u662f\u672c\u5730\u77f3\u6881\u6d3e\u7684\u4eba\uff0c\u6211\u4f55\u4e0d\u627e\u5230\u4ed6\u5bb6\u91cc\u53bb\uff1f\u4ed6\u5982\u518d\u6492\u8d56\uff0c\u6211\u653e\u4e0b\u91d1\u5b50\u5c31\u8d70\u3002\u201d \u3000\u3000\u7fcc\u65e5\u95ee\u660e\u4e86\u77f3\u6881\u7684\u9014\u5f84\uff0c\u8d1f\u4e86\u91d1\u5b50\uff0c\u8fc8\u5f00\u5927\u6b65\u8d70\u53bb\u3002\u77f3\u6881\u79bb\u8862\u5dde\u4e8c\u5341\u591a\u91cc\uff0c\u4ed6\u811a\u6b65\u8fc5\u901f\uff0c\u4e0d\u6d88\u534a\u4e2a\u65f6\u8fb0\u5c31\u5230\u4e86\u3002 \u3000\u3000\u77f3\u6881\u662f\u4e2a\u5c0f\u9547\uff0c\u9644\u8fd1\u4fbf\u662f\u70c2\u67ef\u5c71\u3002\u76f8\u4f20\u664b\u65f6\u6a35\u592b\u738b\u8d28\u5165\u5c71\u91c7\u6a35\uff0c\u89c2\u770b\u4e24\u4f4d\u4ed9\u4eba\u5bf9\u5f08\uff0c\u7b49\u5230\u4e00\u5c40\u65e2\u7ec8\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u81ea\u5df1\u7684\u65a7\u5934\u67c4\u5df2\u7ecf\u70c2\u4e86\uff0c\u56de\u5230\u5bb6\u6765\uff0c\u4eba\u4e8b\u5168\u975e\uff0c\u539f\u6765\u5165\u5c71\u4e00\u53bb\u5df2\u7ecf\u6570\u5341\u5e74\u3002\u70c2\u67ef\u5c71\u4e0a\u4e24\u5cf0\u4e4b\u95f4\u6709\u4e00\u6761\u5de8\u5927\u7684\u77f3\u6881\u76f8\u8fde\uff0c\u9b3c\u65a7\u795e\u5de5\uff0c\u975e\u4eba\u529b\u6240\u80fd\u642c\u4e0a\uff0c\u5f53\u5730\u6545\u8001\u76f8\u4f20\u662f\u795e\u4ed9\u4ee5\u6cd5\u529b\u79fb\u6765\uff0c\u77f3\u6881\u4e4b\u540d\uff0c\u7531\u6b64\u800c\u8d77\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6765\u5230\u9547\u4e0a\uff0c\u8fce\u9762\u9047\u89c1\u4e00\u4e2a\u519c\u5987\uff0c\u95ee\u9053\uff1a\u201c\u5927\u5ac2\uff0c\u8bf7\u95ee\u8fd9\u91cc\u59d3\u6e29\u7684\u4f4f\u5728\u54ea\u91cc\uff1f\u201d\u90a3\u519c\u5987\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u8138\u4e0a\u4e00\u526f\u5acc\u6076\u7684\u795e\u6c14\uff0c\u6389\u5934\u4fbf\u8d70\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d70\u5230\u4e00\u5bb6\u5e97\u94fa\uff0c\u5411\u638c\u67dc\u7684\u8bf7\u95ee\u3002\u90a3\u638c\u67dc\u6de1\u6de1\u7684\u9053\uff1a\u201c\u8001\u5144\u627e\u6e29\u5bb6\u6709\u751a\u4e48\u4e8b\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u8981\u53bb\u4ea4\u8fd8\u4e00\u4e9b\u4e1c\u897f\u3002\u201d\u90a3\u638c\u67dc\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u6e29\u5bb6\u7684\u670b\u53cb\u4e86\uff0c\u53c8\u6765\u95ee\u6211\u5e72\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u8ba8\u4e86\u4e2a\u6ca1\u8da3\uff0c\u5fc3\u60f3\u8fd9\u91cc\u7684\u4eba\u600e\u5730\u5982\u6b64\u65e0\u793c\uff0c\u89c1\u8857\u8fb9\u4e24\u4e2a\u5c0f\u7ae5\u5728\u73a9\u800d\uff0c\u6478\u51fa\u5341\u4e2a\u94dc\u94b1\uff0c\u585e\u5728\u4e00\u4e2a\u5c0f\u7ae5\u7684\u624b\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5e26\u6211\u5230\u6e29\u5bb6\u53bb\u3002\u201d\u90a3\u5c0f\u7ae5\u672c\u5df2\u63a5\u8fc7\u4e86\u94b1\uff0c\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u628a\u94b1\u8fd8\u4ed6\uff0c\u6c14\u5fff\u5fff\u7684\u9053\uff1a\u201c\u6e29\u5bb6\uff1f\u90a3\u8fb9\u5927\u5c4b\u5b50\u5c31\u662f\uff0c\u8fd9\u9b3c\u5730\u65b9\u6211\u53ef\u4e0d\u53bb\u3002\u201d\u8881\u627f\u5fd7\u8fd9\u624d\u660e\u767d\uff0c\u539f\u6765\u59d3\u6e29\u7684\u5728\u8fd9\u91cc\u641e\u5f97\u5929\u6012\u4eba\u6028\uff0c\u6ca1\u4eba\u80af\u548c\u4ed6\u5bb6\u6253\u4ea4\u9053\uff0c\u5012\u4e0d\u662f\u6b64\u5730\u5c45\u6c11\u65e0\u793c\u3002 \u3000\u3000\u4ed6\u4f9d\u7740\u5c0f\u7ae5\u7684\u6307\u70b9\uff0c\u5411\u90a3\u5ea7\u5927\u5c4b\u5b50\u8d70\u53bb\uff0c\u8fdc\u8fdc\u53ea\u542c\u5f97\u4eba\u58f0\u5608\u6742\u3002\u8d70\u5230\u8fd1\u5904\uff0c\u89c1\u6570\u767e\u540d\u519c\u4eba\u62ff\u4e86\u9504\u5934\u94c1\u9776\uff0c\u56f4\u5728\u5c4b\u524d\uff0c\u5927\u53eb\u5927\u56b7\uff1a\u201c\u4f60\u4eec\u628a\u4eba\u6253\u5f97\u91cd\u4f24\uff0c\u773c\u89c1\u6027\u547d\u96be\u4fdd\uff0c\u5c31\u6b64\u7f62\u4e86\u4e0d\u6210\uff1f\u59d3\u6e29\u7684\uff0c\u5feb\u51fa\u6765\u62b5\u547d\uff01\u201d\u4eba\u7fa4\u4e2d\u6709\u4e03\u516b\u4e2a\u5987\u4eba\uff0c\u62ab\u6563\u4e86\u5934\u53d1\u5750\u5728\u5730\u4e0a\u54ed\u56b7\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d70\u5c06\u8fc7\u53bb\uff0c\u95ee\u4e00\u4e2a\u519c\u592b\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u5e72\u4e48\uff1f\u201d\u90a3\u519c\u592b\u9053\uff1a\u201c\u554a\uff0c\u4f60\u662f\u8fc7\u8def\u7684\u76f8\u516c\u3002\u8fd9\u91cc\u59d3\u6e29\u7684\u5f3a\u51f6\u9738\u9053\uff0c\u6628\u5929\u4e0b\u4e61\u6536\u79df\uff0c\u7a0b\u5bb6\u8001\u6c49\u6c42\u4ed6\u5bbd\u9650\u51e0\u5929\uff0c\u4ed6\u4e00\u4e0b\u5c31\u628a\u4eba\u63a8\u5f97\u649e\u5411\u5899\u4e0a\uff0c\u53d7\u4e86\u91cd\u4f24\u3002\u7a0b\u8001\u6c49\u7684\u513f\u5b50\u4f84\u513f\u548c\u4ed6\u62da\u547d\uff0c\u90fd\u88ab\u4ed6\u6253\u5f97\u5168\u8eab\u662f\u4f24\uff0c\u53ea\u6015\u4e09\u4e2a\u4eba\u90fd\u96be\u6d3b\u547d\u3002\u4f60\u8bf4\u8fd9\u6837\u7684\u8d22\u4e3b\u72e0\u4e0d\u72e0\uff1f\u76f8\u516c\u4f60\u5012\u8bc4\u8bc4\u8fd9\u4e2a\u7406\u770b\u3002\u201d \u3000\u3000\u6b63\u8bf4\u4e4b\u95f4\uff0c\u4f17\u519c\u592b\u5435\u5f97\u66f4\u5389\u5bb3\u4e86\uff0c\u6709\u4eba\u4e3e\u8d77\u94c1\u8019\u5f80\u95e8\u4e0a\u731b\u7838\uff0c\u66f4\u6709\u4eba\u628a\u77f3\u5934\u4e22\u8fdb\u5899\u53bb\u3002 \u3000\u3000\u5ffd\u7136\u5927\u95e8\u5440\u7684\u4e00\u58f0\u5f00\u4e86\uff0c\u4e00\u6761\u4eba\u5f71\u500f\u5730\u51b2\u51fa\uff0c\u4f17\u4eba\u8fd8\u6ca1\u770b\u6e05\u695a\uff0c\u5df2\u6709\u4e03\u516b\u540d\u519c\u4eba\u7ed9\u4ed6\u98de\u63b7\u51fa\u6765\uff0c\u8dcc\u51fa\u4e24\u4e09\u4e08\u5916\uff0c\u649e\u5f97\u5934\u7834\u8840\u6d41\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u597d\u5feb\u8eab\u624b\uff01\u201d\u5b9a\u775b\u770b\u65f6\uff0c\u89c1\u90a3\u4eba\u8eab\u6750\u53c8\u7626\u53c8\u957f\uff0c\u9ec4\u6f84\u6f84\u4e00\u5f20\u9762\u76ae\uff0c\u53cc\u7709\u659c\u98de\uff0c\u795e\u8272\u751a\u662f\u527d\u608d\u3002 \u3000\u3000\u90a3\u4eba\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u6279\u732a\u72d7\u4e0d\u5982\u7684\u4e1c\u897f\uff0c\u80c6\u6562\u5230\u8fd9\u91cc\u6765\u6492\u91ce\uff1f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff1f\u201d\u4f17\u4eba\u672a\u53ca\u56de\u7b54\uff0c\u90a3\u4eba\u62a2\u4e0a\u4e00\u6b65\uff0c\u53c8\u6293\u4f4f\u6570\u4eba\u4e71\u63b7\u51fa\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u63b7\u4eba\u5982\u63b7\u7a3b\u8349\uff0c\u6beb\u4e0d\u7528\u529b\uff0c\u5fc3\u60f3\u4e0d\u77e5\u6b64\u4eba\u4e0e\u6e29\u9752\u662f\u751a\u4e48\u5e72\u7cfb\uff0c\u5018\u82e5\u524d\u665a\u4ed6\u4e0e\u6e29\u9752\u5728\u4e00\u8d77\uff0c\u90a3\u4e48\u4ed6\u62b5\u654c\u8363\u5f69\u7b49\u4eba\u7ef0\u7ef0\u6709\u4f59\uff0c\u7528\u4e0d\u5230\u81ea\u5df1\u51fa\u624b\u4e86\u3002 \u3000\u3000\u4eba\u7fa4\u4e2d\u4e09\u540d\u519c\u592b\u62a2\u4e86\u51fa\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u6253\u4f24\u4e86\u4eba\uff0c\u5c31\u8fd9\u6837\u7b97\u4e86\u5417\uff1f\u6211\u4eec\u867d\u7a77\uff0c\u53ef\u662f\u7a77\u4eba\u4e5f\u662f\u547d\u54ea\uff01\u201d\u90a3\u7626\u5b50\u54c8\u54c8\u51e0\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6253\u6b7b\u51e0\u4e2a\uff0c\u4f60\u4eec\u8fd8\u4e0d\u77e5\u9053\u597d\u6b79\u3002\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u6293\u4f4f\u4e00\u4e2a\u4e2d\u5e74\u519c\u592b\u540e\u5fc3\uff0c\u968f\u624b\u7529\u51fa\uff0c\u628a\u4ed6\u5411\u4e1c\u8fb9\u5899\u89d2\u63bc\u53bb\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u4e24\u4e2a\u9752\u5e74\u519c\u592b\u4e00\u9f50\u4e3e\u8d77\u9504\u5934\u5411\u4ed6\u5f53\u5934\u6252\u4e0b\u3002 \u3000\u3000\u90a3\u7626\u5b50\u5de6\u624b\u4e00\u6a2a\uff0c\u4e24\u67c4\u9504\u5934\u5411\u5929\u98de\u51fa\uff0c\u968f\u5373\u6293\u4f4f\u4e24\u4eba\u80f8\u53e3\u5411\u95e8\u53e3\u65d7\u6746\u77f3\u4e0a\u63b7\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u8fd9\u4eba\u6b3a\u4fae\u4e61\u6c11\uff0c\u672c\u751a\u607c\u6012\uff0c\u4f46\u89c1\u4ed6\u6b66\u529f\u4e86\u5f97\uff0c\u82e5\u662f\u7ea0\u7f20\u4e0a\u4e86\uff0c\u9ebb\u70e6\u751a\u591a\uff0c\u53ea\u60f3\u7b49\u4ed6\u4eec\u4e8b\u60c5\u4e00\u4e86\uff0c\u4fbf\u6c42\u89c1\u6e29\u9752\uff0c\u4ea4\u8fd8\u9ec4\u91d1\u4e4b\u540e\u7acb\u5373\u52a8\u8eab\uff0c\u54ea\u77e5\u8fd9\u7626\u5b50\u7adf\u7136\u9aa4\u4e0b\u6740\u624b\u3002\u773c\u89c1\u8fd9\u4e09\u4eba\u649e\u5411\u5899\u89d2\u575a\u77f3\uff0c\u4e0d\u6b7b\u4e5f\u5fc5\u91cd\u4f24\uff0c\u4e0d\u7531\u5f97\u6fc0\u52a8\u4e86\u4fa0\u4e49\u5fc3\u80a0\uff0c\u987e\u4e0d\u5f97\u751f\u4e8b\u60f9\u7978\uff0c\u98de\u8eab\u800c\u524d\uff0c\u5de6\u624b\u6293\u4f4f\u4e2d\u5e74\u519c\u592b\u53f3\u817f\u5f80\u540e\u4e00\u62c9\uff0c\u4e22\u5728\u5730\u4e0b\uff0c\u8ddf\u7740\u4e00\u62db\u201c\u5cb3\u738b\u795e\u7bad\u201d\uff0c\u8eab\u5b50\u5f53\u771f\u5982\u7bad\u79bb\u5f26\uff0c\u6025\u5c04\u800c\u51fa\uff0c\u62a2\u8fc7\u53bb\u6293\u4f4f\u4e24\u4e2a\u9752\u5e74\u519c\u592b\u80cc\u5fc3\uff0c\u8fd9\u624d\u633a\u8170\u7ad9\u76f4\uff0c\u5c06\u4e24\u4eba\u8f7b\u8f7b\u653e\u843d\u3002\u8fd9\u62db\u201c\u5cb3\u738b\u795e\u7bad\u201d\u662f\u6728\u6851\u9053\u4eba\u6240\u4f20\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u8eab\u6cd5\u4e4b\u5feb\uff0c\u4efb\u4f55\u5404\u6d3e\u6b66\u529f\u5747\u6240\u4e0d\u53ca\uff0c\u4ed6\u672c\u4e0d\u60f3\u8f7b\u6613\u70ab\u9732\uff0c\u4f46\u4e8b\u6025\u6551\u4eba\uff0c\u4e0d\u5f97\u4e0d\u7528\uff0c\u5fc3\u77e5\u8fd9\u4e00\u6765\u5b9a\u62db\u4e86\u90a3\u7626\u5b50\u4e4b\u6068\uff0c\u597d\u5728\u6e29\u5bb6\u5730\u70b9\u5df2\u77e5\uff0c\u4e0d\u5982\u5f85\u665a\u4e0a\u518d\u6765\u5077\u5077\u4ea4\u8fd8\uff0c\u4e8e\u662f\u4e00\u653e\u4e0b\u519c\u592b\uff0c\u7acb\u5373\u8f6c\u8eab\u5c31\u8d70\uff0c\u66f4\u4e0d\u5411\u7626\u5b50\u591a\u77a7\u4e00\u773c\u3002 \u3000\u3000\u4e09\u4e2a\u519c\u592b\u6b7b\u91cc\u9003\u751f\uff0c\u5446\u5728\u5f53\u573a\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u7626\u5b50\u89c1\u4ed6\u5982\u6b64\u6b66\u529f\uff0c\u60ca\u8bb6\u5f02\u5e38\uff0c\u6697\u5fd6\u81ea\u5df1\u6295\u63b7\u8fd9\u4e09\u4eba\u624b\u6cd5\u6781\u4e3a\u8fc5\u901f\uff0c\u4e14\u662f\u5f80\u4e0d\u540c\u65b9\u5411\u63b7\u53bb\uff0c\u6b64\u4eba\u5c45\u7136\u540e\u53d1\u5148\u81f3\uff0c\u5c06\u4e09\u4eba\u4e00\u4e00\u6551\u4e0b\uff0c\u4e0d\u77e5\u662f\u4f55\u6765\u5934\u3002\u89c1\u4ed6\u8f6c\u8eab\u800c\u53bb\uff0c\u5fd9\u98de\u8eab\u8ffd\u4e0a\uff0c\u4f38\u624b\u5411\u4ed6\u80a9\u5934\u62cd\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u670b\u53cb\uff0c\u6162\u8d70\uff01\u201d\u8fd9\u4e00\u62cd\u4f7f\u7684\u662f\u5927\u529b\u5343\u65a4\u91cd\u624b\u6cd5\u3002\u8881\u627f\u5fd7\u5e76\u4e0d\u95ea\u907f\uff0c\u80a9\u5934\u5fae\u5fae\u5411\u4e0b\u4e00\u6c89\uff0c\u4fbf\u628a\u4ed6\u7684\u91cd\u624b\u6cd5\u5316\u89e3\u4e86\uff0c\u5374\u4e5f\u4e0d\u8fd0\u52b2\u53cd\u51fb\uff0c\u4f3c\u4e4e\u6beb\u4e0d\u77e5\u60c5\u3002\u90a3\u7626\u5b50\u66f4\u662f\u5403\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8fd9\u6279\u5bb6\u4f19\u8bf7\u6765\uff0c\u548c\u6211\u4eec\u4e3a\u96be\u7684\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u62f1\u624b\u9053\uff1a\u201c\u5b9e\u5728\u5bf9\u4e0d\u8d77\uff0c\u5144\u5f1f\u53ea\u6015\u95f9\u51fa\u4eba\u547d\uff0c\u5927\u5bb6\u9ebb\u70e6\uff0c\u662f\u4ee5\u5192\u6627\u6276\u4e86\u4ed6\u4eec\u4e00\u628a\u3002\u8fd9\u53ef\u5f97\u7f6a\u4e86\u3002\u8001\u5144\u5982\u6b64\u672c\u9886\uff0c\u4f55\u5fc5\u8ddf\u8fd9\u4e9b\u4e61\u6c11\u4e00\u822c\u89c1\u8bc6\uff1f\u201d \u3000\u3000\u90a3\u7626\u5b50\u542c\u4ed6\u51fa\u8a00\u8c26\u900a\uff0c\u767b\u65f6\u654c\u610f\u6d88\u4e86\u5927\u534a\uff0c\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u5c0a\u59d3\uff1f\u5230\u655d\u5904\u6765\u6709\u4f55\u8d35\u5e72\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u8881\uff0c\u6709\u4e00\u4f4d\u59d3\u6e29\u7684\u5c11\u5e74\u670b\u53cb\uff0c\u4e0d\u77e5\u662f\u4f4f\u5728\u8fd9\u91cc\u7684\u4e48\uff1f\u201d\u90a3\u7626\u5b50\u9053\uff1a\u201c\u6211\u4e5f\u59d3\u6e29\uff0c\u4e0d\u77e5\u9601\u4e0b\u627e\u7684\u662f\u8c01\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5728\u4e0b\u8981\u627e\u6e29\u9752\u6e29\u76f8\u516c\u3002\u201d\u90a3\u7626\u5b50\u70b9\u70b9\u5934\uff0c\u8f6c\u8eab\u5bf9\u6570\u5341\u540d\u5c1a\u672a\u6563\u53bb\u7684\u4e61\u6c11\u559d\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u6b7b\u662f\u4e0d\u662f\uff1f\u8fd8\u4e0d\u5feb\u6eda\uff1f\u201d \u3000\u3000\u4f17\u519c\u6c11\u89c1\u8881\u627f\u5fd7\u548c\u90a3\u7626\u5b50\u6500\u8d77\u4ea4\u60c5\u6765\uff0c\u9002\u624d\u89c1\u4e86\u4e24\u4eba\u529f\u592b\uff0c\u4e0d\u6562\u518d\u884c\u9017\u7559\uff0c\u7eb7\u7eb7\u6563\u53bb\uff0c\u8d70\u8fdc\u4e4b\u540e\uff0c\u4fbf\u53c8\u5927\u9a82\uff0c\u884c\u5f97\u8d8a\u8fdc\uff0c\u9a82\u5f97\u8d8a\u54cd\u3002\u4e61\u97f3\u4f76\u5c48\uff0c\u8881\u627f\u5fd7\u4e0d\u61c2\u4ed6\u4eec\u9a82\u4e9b\u751a\u4e48\u3002 \u3000\u3000\u90a3\u7626\u5b50\u4e5f\u4e0d\u7406\u4f1a\uff0c\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8bf7\u5230\u820d\u4e0b\u5949\u8336\u3002\u201d\u8881\u627f\u5fd7\u968f\u4ed6\u5165\u5185\uff0c\u53ea\u89c1\u91cc\u9762\u662f\u4e00\u5ea7\u4e8c\u5f00\u95f4\u7684\u5927\u5385\uff0c\u5f53\u4e2d\u4e00\u5757\u5927\u533e\uff0c\u5199\u7740\u4e09\u4e2a\u5927\u5b57\uff1a\u201c\u4e16\u5fb7\u5802\u201d\u3002\u5385\u4e0a\u4e2d\u5802\u6761\u5e45\uff0c\u4e91\u677f\u82b1\u74f6\uff0c\u9648\u8bbe\u5f97\u751a\u662f\u8003\u7a76\uff0c\u4e00\u6d3e\u8c6a\u7ec5\u5927\u5b85\u7684\u6c14\u6d3e\u3002 \u3000\u3000\u90a3\u7626\u5b50\u8bf7\u8881\u627f\u5fd7\u5728\u4e0a\u9996\u5750\u4e86\uff0c\u4ec6\u4eba\u732e\u4e0a\u8336\u6765\u3002\u90a3\u7626\u5b50\u4e0d\u4f4f\u8bf7\u95ee\u8881\u627f\u5fd7\u7684\u5e08\u627f\u51fa\u8eab\uff0c\u8a00\u8bed\u867d\u7136\u5ba2\u6c14\uff0c\u4f46\u8881\u627f\u5fd7\u9690\u9690\u89c9\u5f97\u4ed6\u9887\u542b\u654c\u610f\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8bf7\u6e29\u9752\u76f8\u516c\u51fa\u6765\u4e00\u89c1\uff0c\u5144\u5f1f\u8981\u4ea4\u8fd8\u4ed6\u4e00\u4ef6\u4e1c\u897f\u3002\u201d \u3000\u3000\u90a3\u7626\u5b50\u9053\uff1a\u201c\u6e29\u9752\u5c31\u662f\u820d\u5f1f\uff0c\u5144\u5f1f\u540d\u53eb\u6e29\u6b63\u3002\u820d\u5f1f\u73b0\u4e0b\u51fa\u5916\u53bb\u4e86\uff0c\u4e0d\u4e45\u4fbf\u5f52\uff0c\u8bf7\u8001\u5144\u7a0d\u5f85\u3002\u201d\u8881\u627f\u5fd7\u672c\u6765\u4e0d\u613f\u4e0e\u8fd9\u79cd\u884c\u4e3a\u4e0d\u6b63\u3001\u9c7c\u8089\u4e61\u90bb\u7684\u4eba\u5bb6\u591a\u6253\u4ea4\u9053\uff0c\u4f46\u6e29\u9752\u65e2\u7136\u4e0d\u5728\uff0c\u53ea\u5f97\u7b49\u5019\u3002\u53ef\u662f\u8ddf\u6e29\u6b63\u5b9e\u5728\u6ca1\u751a\u4e48\u8bdd\u53ef\u8bf4\uff0c\u4e24\u4eba\u9ed8\u7136\u76f8\u5bf9\uff0c\u5747\u611f\u65e0\u804a\u3002 \u3000\u3000\u7b49\u5230\u4e2d\u5348\uff0c\u6e29\u9752\u4ecd\u7136\u6ca1\u56de\uff0c\u8881\u627f\u5fd7\u53c8\u4e0d\u613f\u628a\u5927\u6279\u9ec4\u91d1\u4ea4\u4e0e\u522b\u4eba\u3002\u6e29\u6b63\u547d\u4ec6\u4eba\u5f00\u51fa\u996d\u6765\uff0c\u706b\u817f\u814a\u8089\uff0c\u80a5\u9e21\u9c9c\u9c7c\uff0c\u83dc\u80b4\u5341\u5206\u4e30\u76db\u3002 \u3000\u3000\u7b49\u5230\u4e0b\u5348\u65e5\u5934\u504f\u897f\uff0c\u8881\u627f\u5fd7\u5b9e\u5728\u4e0d\u8010\u70e6\u4e86\uff0c\u5fc3\u60f3\u53cd\u6b63\u8fd9\u662f\u6e29\u9752\u5bb6\u91cc\uff0c\u628a\u91d1\u5b50\u7559\u4e0b\u7b97\u4e86\uff0c\u4e8e\u662f\u5c06\u9ec4\u91d1\u5305\u88f9\u5f80\u684c\u4e0a\u4e00\u653e\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ee4\u5f1f\u4e4b\u7269\uff0c\u5c31\u70e6\u4ec1\u5144\u8f6c\u4ea4\u3002\u5144\u5f1f\u8981\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u7136\u95e8\u5916\u4f20\u6765\u4e00\u9635\u7b11\u8bed\u4e4b\u58f0\uff0c\u90fd\u662f\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u5176\u4e2d\u5374\u5939\u7740\u6e29\u9752\u7684\u7b11\u58f0\u3002\u6e29\u6b63\u9053\uff1a\u201c\u820d\u5f1f\u56de\u6765\u5566\u3002\u201d\u62a2\u4e86\u51fa\u53bb\u3002\u8881\u627f\u5fd7\u8981\u8ddf\u51fa\u53bb\uff0c\u6e29\u6b63\u9053\uff1a\u201c\u8881\u5144\u8bf7\u5728\u6b64\u7a0d\u5f85\u3002\u201d\u8881\u627f\u5fd7\u89c1\u4ed6\u795e\u8272\u8be1\u79d8\uff0c\u53ea\u5f97\u505c\u6b65\u3002 \u3000\u3000\u53ef\u662f\u6e29\u9752\u7adf\u4e0d\u8fdb\u6765\u3002\u6e29\u6b63\u56de\u5385\u8bf4\u9053\uff1a\u201c\u820d\u5f1f\u8981\u53bb\u6362\u8863\uff0c\u4e00\u4f1a\u5c31\u51fa\u6765\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6e29\u9752\u8fd9\u4eba\u5b9e\u5728\u5973\u4eba\u6c14\u5f97\u7d27\u3002\u89c1\u4e2a\u5ba2\u4eba\u53c8\u8981\u6362\u751a\u4e48\u8863\u670d\uff1f\u201d \u3000\u3000\u53c8\u7b49\u826f\u4e45\uff0c\u6e29\u9752\u624d\u4ece\u5185\u5802\u51fa\u6765\uff0c\u53ea\u89c1\u4ed6\u6539\u7a7f\u4e86\u7d2b\u8272\u957f\u886b\uff0c\u52a0\u7cfb\u4e86\u6761\u9e45\u9ec4\u8272\u8170\u7ee6\uff0c\u5934\u5dfe\u4e0a\u9576\u7740\u4e00\u9897\u660e\u73e0\uff0c\u6ee1\u8138\u5806\u6b22\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5144\u5927\u9a7e\u5149\u4e34\uff0c\u5e78\u4f55\u5982\u4e4b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6e29\u5144\u5fd8\u8bb0\u4e86\u8fd9\u5305\u4e1c\u897f\uff0c\u7279\u6765\u9001\u8fd8\u3002\u201d\u6e29\u9752\u6120\u9053\uff1a\u201c\u4f60\u77a7\u6211\u4e0d\u8d77\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5144\u5f1f\u7edd\u65e0\u6b64\u610f\uff0c\u53ea\u662f\u4e0d\u6562\u62dc\u9886\u539a\u8d50\u3002\u5c31\u6b64\u544a\u8f9e\u3002\u201d\u7ad9\u8d77\u6765\u5411\u6e29\u6b63\u3001\u6e29\u9752\u5404\u81ea\u4e00\u63d6\u3002 \u3000\u3000\u6e29\u9752\u4e00\u628a\u62c9\u4f4f\u4ed6\u8863\u8896\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8bb8\u4f60\u8d70\u3002\u201d\u8881\u627f\u5fd7\u4e0d\u7981\u6115\u7136\u3002\u6e29\u6b63\u4e5f\u8138\u4e0a\u53d8\u8272\u3002 \u3000\u3000\u6e29\u9752\u7b11\u9053\uff1a\u201c\u6211\u6b63\u6709\u4e00\u4ef6\u8981\u7d27\u4e8b\u987b\u5f97\u8bf7\u95ee\u8881\u5927\u54e5\uff0c\u4f60\u4eca\u65e5\u5c31\u5728\u820d\u4e0b\u6b47\u5427\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5144\u5f1f\u5728\u8862\u5dde\u57ce\u91cc\u6709\u4e8b\u8981\u529e\uff0c\u4e0b\u6b21\u82e5\u6709\u673a\u7f18\uff0c\u5f53\u518d\u524d\u6765\u53e8\u6270\u3002\u201d\u6e29\u9752\u53ea\u662f\u4e0d\u5141\u3002\u6e29\u6b63\u9053\uff1a\u201c\u8881\u5927\u54e5\u65e2\u7136\u6709\u4e8b\uff0c\u54b1\u4eec\u5c31\u522b\u803d\u6401\u4ed6\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e00\u5b9a\u8981\u8d70\uff0c\u90a3\u4f60\u628a\u8fd9\u5305\u4e1c\u897f\u5e26\u8d70\u3002\u4f60\u8bf4\u751a\u4e48\u4e5f\u4e0d\u80af\u5728\u6211\u5bb6\u4f4f\uff0c\u54fc\uff0c\u6211\u77e5\u9053\u4f60\u77a7\u6211\u4e0d\u8d77\u3002\u201d\u8881\u627f\u5fd7\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u89c1\u4ed6\u7559\u5ba2\u610f\u8bda\uff0c\u4fbf\u9053\uff1a\u201d \u3000\u3000\u65e2\u662f\u6e29\u5144\u539a\u610f\uff0c\u5144\u5f1f\u5c31\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u6e29\u9752\u5927\u559c\uff0c\u5fd9\u53eb\u53a8\u623f\u51c6\u5907\u70b9\u5fc3\u3002\u6e29\u6b63\u4e00\u8138\u7684\u4e0d\u4e50\u610f\uff0c\u7136\u800c\u5374\u4e0d\u79bb\u5f00\uff0c\u4e00\u76f4\u966a\u7740\uff0c\u6709\u4e00\u53e5\u6ca1\u4e00\u53e5\u7684\u95f2\u804a\u3002 \u3000\u3000\u6e29\u9752\u5c3d\u4e0e\u8881\u627f\u5fd7\u8c08\u8bba\u4e66\u672c\u4e0a\u7684\u4e8b\u3002\u8881\u627f\u5fd7\u5bf9\u8bd7\u8bcd\u5168\u4e0d\u5728\u884c\uff0c\u53f2\u4e8b\u5175\u6cd5\u5374\u662f\u4ece\u5c0f\u7814\u8bfb\u7684\uff0c\u6e29\u9752\u63a2\u660e\u4e86\u4ed6\u7684\u6027\u4e4b\u6240\u8fd1\uff0c\u4fbf\u8c08\u8d77\u751a\u4e48\u6ddd\u6c34\u4e4b\u6218\u3001\u5b98\u6e21\u4ea4\u5175\u4e4b\u7c7b\u53f2\u4e8b\u6765\u3002\u8881\u627f\u5fd7\u6697\u6697\u94a6\u4f69\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u813e\u6c14\u53e4\u602a\uff0c\u4e66\u5012\u662f\u8bfb\u8fc7\u4e0d\u5c11\uff0c\u53ef\u4e0d\u4f3c\u6211\u8fd9\u5047\u4e66\u751f\u90a3\u4e48\u8349\u5305\u3002\u201d\u6e29\u6b63\u4e8e\u6587\u4e8b\u5374\u4e00\u7a8d\u4e0d\u901a\uff0c\u542c\u5f97\u5341\u5206\u817b\u70e6\uff0c\u5374\u53c8\u4e0d\u80af\u8d70\u5f00\u3002\u8881\u627f\u5fd7\u4e0d\u597d\u610f\u601d\u4e86\uff0c\u548c\u4ed6\u8c08\u4e86\u51e0\u53e5\u6b66\u529f\u3002\u6e29\u6b63\u6b63\u8981\u63a5\u53e3\uff0c\u6e29\u9752\u5374\u53c8\u63d2\u5634\u628a\u8bdd\u9898\u5e26\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u8fd9\u4e24\u5144\u5f1f\u4e4b\u95f4\u7684\u60c5\u5f62\u5f88\u6709\u70b9\u5947\u602a\uff0c\u6e29\u6b63\u867d\u662f\u5144\u957f\uff0c\u5bf9\u8fd9\u5f1f\u5f1f\u5374\u663e\u7136\u9887\u4e3a\u656c\u754f\uff0c\u4e0d\u6562\u4e1d\u6beb\u5f97\u7f6a\uff0c\u8a00\u8c08\u4e4b\u95f4\u5e38\u88ab\u4ed6\u65e0\u793c\u62a2\u767d\uff0c\u53cd\u800c\u8d54\u7b11\uff0c\u8a00\u8bed\u4e2d\u603b\u662f\u8ba8\u597d\u4e8e\u4ed6\u3002\u5982\u6e29\u9752\u5bf9\u4ed6\u8f9e\u610f\u7565\u4e3a\u548c\u5584\uff0c\u4ed6\u5c31\u7709\u5f00\u773c\u7b11\uff0c\u9ad8\u5174\u975e\u51e1\u3002 \u3000\u3000\u5230\u5f97\u665a\u95f4\uff0c\u5f00\u4e0a\u9152\u5e2d\uff0c\u66f4\u662f\u4e30\u76db\u3002\u7528\u8fc7\u9152\u996d\uff0c\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5c0f\u5f1f\u65e5\u95f4\u7d2f\u4e86\uff0c\u60f3\u65e9\u4e9b\u7761\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u5c0f\u5f1f\u5c40\u5904\u4e61\u95f4\uff0c\u96be\u5f97\u8881\u5144\u5149\u4e34\uff0c\u6b63\u60f3\u526a\u70db\u591c\u8bdd\uff0c\u591a\u6240\u8bf7\u76ca\u3002\u8881\u5144\u65e2\u7136\u5026\u4e86\uff0c\u90a3\u4e48\u660e\u65e5\u518d\u8c08\u5427\u3002\u201d \u3000\u3000\u6e29\u6b63\u9053\uff1a\u201c\u8881\u5144\u4eca\u665a\u5230\u6211\u623f\u91cc\u7761\u5427\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u4f60\u8fd9\u623f\u600e\u7559\u5f97\u5ba2\u4eba\uff1f\u81ea\u7136\u5230\u6211\u623f\u91cc\u7761\u3002\u201d\u6e29\u6b63\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u6709\u751a\u4e48\u4e0d\u597d\uff1f\u6211\u53bb\u8ddf\u5988\u7761\u3002\u201d\u6e29\u6b63\u5927\u4e3a\u4e0d\u60a6\uff0c\u4e5f\u4e0d\u9053\u522b\uff0c\u5f84\u81ea\u5165\u5185\u3002\u6e29\u9752\u9053\uff1a\u201c\u54fc\uff0c\u6ca1\u89c4\u77e9\uff0c\u4e5f\u4e0d\u6015\u4eba\u7b11\u8bdd\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u5144\u5f1f\u4e3a\u81ea\u5df1\u6597\u6c14\uff0c\u5f88\u662f\u4e0d\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u8352\u5c71\u91ce\u5cad\u4e2d\u4f4f\u60ef\u4e86\u7684\uff0c\u6e29\u5144\u4e0d\u5fc5\u8d39\u5fc3\u3002\u201d\u6e29\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u4e0d\u8d39\u5fc3\u5c31\u662f\u3002\u201d\u62ff\u8d77\u70db\u53f0\uff0c\u5f15\u4ed6\u8fdb\u5185\u3002 \u3000\u3000\u7a7f\u8fc7\u4e24\u4e2a\u5929\u4e95\uff0c\u76f4\u5230\u7b2c\u4e09\u8fdb\uff0c\u4ece\u4e1c\u8fb9\u4e0a\u697c\u3002\u6e29\u9752\u63a8\u5f00\u623f\u95e8\uff0c\u8881\u627f\u5fd7\u773c\u524d\u4e00\u8000\uff0c\u5148\u95fb\u5230\u4e00\u9635\u5e7d\u5e7d\u7684\u9999\u6c14\uff0c\u53ea\u89c1\u623f\u4e2d\u70b9\u4e86\u4e00\u652f\u5927\u7ea2\u70db\uff0c\u7167\u5f97\u6ee1\u5ba4\u751f\u6625\uff0c\u5e8a\u4e0a\u73e0\u7f57\u7eb1\u7684\u5e10\u5b50\uff0c\u767d\u8272\u7f0e\u88ab\u4e0a\u7ee3\u7740\u4e00\u53ea\u9ec4\u8272\u7684\u51e4\u51f0\uff0c\u58c1\u4e0a\u6302\u7740\u4e00\u5e45\u5de5\u7b14\u4ed5\u5973\u56fe\u3002\u5e8a\u524d\u684c\u4e0a\u653e\u7740\u4e00\u5f20\u96d5\u82b1\u7aef\u781a\uff0c\u51e0\u4ef6\u78a7\u7389\u73a9\u7269\uff0c\u7b14\u7b52\u4e2d\u63d2\u4e86\u5927\u5927\u5c0f\u5c0f\u516d\u4e03\u652f\u7b14\uff0c\u897f\u9996\u4e00\u5f20\u51e0\u4e0a\u4f9b\u7740\u4e00\u76c6\u5170\u82b1\uff0c\u67b6\u5b50\u4e0a\u505c\u7740\u4e00\u53ea\u767d\u9e66\u9e49\u3002\u6ee1\u5ba4\u9526\u7ee3\u3002\u8fde\u6905\u62ab\u4e0a\u4e5f\u7ee3\u4e86\u82b1\u3002\u8881\u627f\u5fd7\u6765\u81ea\u6df1\u5c71\uff0c\u51e0\u65f6\u89c1\u8fc7\u8fd9\u822c\u5bcc\u8d35\u6c14\u8c61\uff0c\u4e0d\u89c9\u5446\u4e86\u3002\u6e29\u9752\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u5144\u5f1f\u7684\u5367\u5ba4\uff0c\u8881\u5144\u5c06\u5c31\u6b47\u4e00\u665a\u5427\u3002\u201d\u4e0d\u7b49\u4ed6\u56de\u7b54\uff0c\u4fbf\u5df2\u6380\u5e37\u51fa\u95e8\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5ba4\u5185\u56db\u4e0b\u5bdf\u770b\uff0c\u89c1\u65e0\u5f02\u72b6\uff0c\u6b63\u8981\u89e3\u8863\u5c31\u5bdd\uff0c\u5ffd\u542c\u6709\u4eba\u8f7b\u8f7b\u6572\u95e8\u3002\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\uff1f\u201d\u8fdb\u6765\u4e00\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u4e2b\u9b1f\uff0c\u624b\u6258\u6731\u6f06\u6728\u76d8\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5c11\u7237\uff0c\u8bf7\u7528\u70b9\u5fc3\u3002\u201d\u628a\u76d8\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u76d8\u4e2d\u662f\u4e00\u7897\u6842\u82b1\u7096\u71d5\u7a9d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u867d\u662f\u7763\u5e08\u4e4b\u5b50\uff0c\u4f46\u81ea\u5e7c\u7a77\u4e61\u964b\u5c45\uff0c\u4ece\u6765\u6ca1\u89c1\u8fc7\u71d5\u7a9d\uff0c\u4e0d\u8bc6\u5f97\u662f\u751a\u4e48\u4e1c\u897f\u3002\u4ed6\u6210\u5e74\u4ee5\u6765\uff0c\u521d\u6b21\u548c\u5c11\u5973\u8c08\u8bdd\uff0c\u5f88\u6709\u70b9\u5bb3\u7f9e\uff0c\u7ea2\u7740\u8138\u5e94\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u90a3\u4e2b\u9b1f\u7b11\u9053\uff1a\u201c\u6211\u53eb\u5c0f\u83ca\uff0c\u662f\u5c11\u7237\u2026\u2026\u5c11\u7237\uff0c\u563b\u563b\uff0c\u5429\u5490\u6211\u6765\u670d\u4f8d\u8881\u5c11\u7237\u7684\u3002\u8881\u5c11\u7237\u6709\u751a\u4e48\u4e8b\u3002\u5dee\u6211\u505a\u597d\u5566\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6ca1\u2026\u2026\u6ca1\u751a\u4e48\u4e8b\u4e86\u3002\u201d\u5c0f\u83ca\u6162\u6162\u9000\u51fa\uff0c\u5ffd\u7136\u56de\u5934\u54ad\u54ad\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u6211\u5bb6\u5c11\u7237\u7279\u5730\u7096\u7ed9\u8881\u5c11\u7237\u5403\u7684\u3002\u201d\u8881\u627f\u5fd7\u6115\u7136\u4e0d\u77e5\u6240\u5bf9\u3002\u5c0f\u83ca\u4e00\u7b11\u51fa\u95e8\uff0c\u8f7b\u8f7b\u628a\u95e8\u5e26\u4e0a\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5c06\u71d5\u7a9d\u4e09\u53e3\u559d\u5b8c\uff0c\u53ea\u89c9\u751c\u751c\u6ed1\u6ed1\uff0c\u9999\u9999\u817b\u817b\uff0c\u4e5f\u8bf4\u4e0d\u4e0a\u597d\u5403\u4e0d\u597d\u5403\uff0c\u89e3\u8863\u4e0a\u5e8a\uff0c\u6296\u5f00\u88ab\u5934\uff0c\u6d53\u9999\u66f4\u70c8\uff0c\u4e2d\u4eba\u6b32\u9189\uff0c\u90a3\u5e8a\u53c8\u8f6f\u53c8\u6696\uff0c\u751f\u5e73\u4ece\u672a\u7761\u8fc7\uff0c\u8ff7\u8ff7\u7cca\u7cca\u4fbf\u7761\u7740\u4e86\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e94\u56de\u3000\u5c71\u5e7d\u82b1\u5bc2\u5bc2 \u6c34\u79c0\u8349\u9752\u9752",content:(0,t.jsx)(t.Fragment,{children:"\u7761\u5230\u4e2d\u591c\uff0c\u7a97\u5916\u5ffd\u7136\u6709\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u5657\u54e7\u4e00\u7b11\uff0c\u8881\u627f\u5fd7\u5728\u8fd9\u5730\u65b9\u672c\u6765\u4e0d\u6562\u6c89\u7761\uff0c\u7acb\u5373\u60ca\u9192\uff0c\u53ea\u542c\u6709\u4eba\u5728\u7a97\u683c\u5b50\u4e0a\u8f7b\u5f39\u4e24\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u6708\u767d\u98ce\u6e05\uff0c\u5982\u6b64\u826f\u591c\u3002\u8881\u5144\u96c5\u4eba\uff0c\u4e0d\u6015\u8f9c\u8d1f\u4e86\u5927\u597d\u65f6\u5149\u5417\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u5f97\u662f\u6e29\u9752\u7684\u58f0\u97f3\uff0c\u4ece\u5e10\u4e2d\u671b\u51fa\u53bb\uff0c\u679c\u89c1\u5e8a\u524d\u5982\u6c34\u94f6\u94fa\u5730\uff0c\u4e00\u7247\u6708\u5149\u3002\u7a97\u5916\u4e00\u4eba\u5934\u4e0b\u811a\u4e0a\uff0c\u201c\u5012\u6302\u73e0\u5e18\u201d\uff0c\u4f3c\u5728\u5411\u623f\u5185\u7aa5\u63a2\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u6211\u7a7f\u8863\u5c31\u6765\u3002\u201d\u5fc3\u60f3\u8fd9\u4eba\u884c\u4e8b\u5b9e\u5728\u4ee4\u4eba\u6349\u6478\u4e0d\u900f\uff0c\u5012\u8981\u770b\u770b\u4ed6\u6df1\u66f4\u534a\u591c\u4e4b\u9645\uff0c\u53c8\u6709\u751a\u4e48\u5e0c\u5947\u53e4\u602a\u7684\u82b1\u6837\u3002\u7a7f\u597d\u8863\u670d\uff0c\u6697\u628a\u5315\u9996\u85cf\u5728\u8170\u91cc\uff0c\u63a8\u5f00\u7a97\u6237\uff0c\u82b1\u9999\u6251\u9762\uff0c\u539f\u6765\u7a97\u5916\u662f\u5ea7\u82b1\u56ed\u3002 \u3000\u3000\u6e29\u9752\u811a\u4e0b\u4f7f\u52b2\uff0c\u4eba\u5df2\u7ffb\u8d77\uff0c\u843d\u4e0b\u5730\u6765\uff0c\u6084\u58f0\u9053\uff1a\u201c\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u63d0\u8d77\u4e86\u653e\u5728\u5730\u4e0b\u7684\u4e00\u53ea\u7af9\u7bee\u3002\u8881\u627f\u5fd7\u4e0d\u77e5\u4ed6\u6363\u751a\u4e48\u9b3c\uff0c\u8ddf\u7740\u4ed6\u8d8a\u5899\u51fa\u5916\u3002 \u3000\u3000\u4e24\u4eba\u7f13\u6b65\u5411\u540e\u5c71\u4e0a\u884c\u53bb\u3002\u90a3\u5c71\u4e5f\u4e0d\u751a\u9ad8\uff0c\u8eab\u5468\u6811\u6728\u8471\u7fe0\uff0c\u56db\u4e0b\u91cc\u8f7b\u70df\u8584\u96fe\uff0c\u51fa\u6ca1\u4e8e\u679d\u53f6\u4e4b\u95f4\u3002\u826f\u591c\u5bc2\u5bc2\uff0c\u4e24\u4eba\u8db3\u8e0f\u8f6f\u8349\uff0c\u7adf\u8fde\u811a\u6b65\u4e5f\u662f\u6084\u65e0\u58f0\u606f\u3002\u5c06\u5230\u5c71\u9876\uff0c\u8f6c\u4e86\u4e24\u4e2a\u5f2f\uff0c\u4e00\u9635\u6e05\u98ce\uff0c\u56db\u5468\u5168\u662f\u82b1\u9999\u3002\u6708\u8272\u5982\u971c\uff0c\u653e\u773c\u671b\u53bb\uff0c\u6ee1\u5761\u5c3d\u662f\u7ea2\u8272\u3001\u767d\u8272\u3001\u9ec4\u8272\u7684\u73ab\u7470\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d5e\u9053\uff1a\u201c\u771f\u662f\u795e\u4ed9\u822c\u7684\u597d\u5730\u65b9\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u8fd9\u4e9b\u82b1\u90fd\u662f\u6211\u4eb2\u624b\u79cd\u7684\uff0c\u9664\u4e86\u5988\u5988\u548c\u5c0f\u83ca\u4e4b\u5916\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u6765\u3002\u201d\u6e29\u9752\u63d0\u4e86\u7bee\u5b50\uff0c\u7f13\u7f13\u800c\u884c\u3002\u8881\u627f\u5fd7\u5728\u540e\u8ddf\u968f\uff0c\u53ea\u89c9\u5fc3\u65f7\u795e\u6021\uff0c\u539f\u6765\u63d0\u9632\u6212\u5907\u4e4b\u610f\uff0c\u4e00\u65f6\u5728\u82b1\u9999\u6708\u5149\u4e2d\u5c3d\u7686\u6d88\u9664\u3002 \u3000\u3000\u53c8\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u6765\u5230\u4e00\u4e2a\u5c0f\u5c0f\u4ead\u5b50\uff0c\u6e29\u9752\u8981\u8881\u627f\u5fd7\u5750\u5728\u77f3\u4e0a\uff0c\u6253\u5f00\u7bee\u5b50\uff0c\u53d6\u51fa\u4e00\u628a\u5c0f\u9152\u58f6\uff0c\u4e24\u53ea\u9152\u676f\uff0c\u659f\u6ee1\u4e86\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u8bb8\u5403\u8364\u3002\u201d\u8881\u627f\u5fd7\u5939\u8d77\u9152\u83dc\uff0c\u679c\u7136\u90fd\u662f\u4e9b\u9999\u83c7\u3001\u6728\u8033\u4e4b\u7c7b\u7684\u7d20\u83dc\u3002 \u3000\u3000\u6e29\u9752\u4ece\u7bee\u91cc\u62bd\u51fa\u4e00\u652f\u6d1e\u7bab\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5439\u4e00\u9996\u66f2\u5b50\u7ed9\u4f60\u542c\u3002\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u6e29\u9752\u8f7b\u8f7b\u5439\u4e86\u8d77\u6765\u3002\u8881\u627f\u5fd7\u4e0d\u61c2\u97f3\u5f8b\uff0c\u4f46\u89c9\u7bab\u58f0\u7f20\u7ef5\uff0c\u5982\u6028\u5982\u6155\uff0c\u4e00\u9897\u5fc3\u4f3c\u4e4e\u4e5f\u968f\u7740\u5a49\u8f6c\u7bab\u58f0\u98de\u626c\uff0c\u98d8\u98d8\u8361\u8361\u5730\uff0c\u5982\u5728\u4ed9\u5883\uff0c\u975e\u590d\u4eba\u95f4\u3002 \u3000\u3000\u6e29\u9752\u5439\u5b8c\u4e00\u66f2\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7231\u751a\u4e48\u66f2\u5b50\uff1f\u6211\u5439\u7ed9\u4f60\u542c\u3002\u201d\u8881\u627f\u5fd7\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u6211\u751a\u4e48\u66f2\u5b50\u90fd\u4e0d\u77e5\u9053\u3002\u4f60\u61c2\u5f97\u771f\u591a\uff0c\u600e\u4e48\u8fd9\u6837\u806a\u660e\uff1f\u201d\u6e29\u9752\u4e0b\u989a\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u62ff\u8d77\u6d1e\u7bab\uff0c\u53c8\u594f\u4e00\u66f2\uff0c\u8fd9\u6b21\u66f2\u8c03\u66f4\u662f\u67d4\u5a9a\uff0c\u6708\u8272\u6eb6\u6eb6\uff0c\u82b1\u9999\u5e7d\u5e7d\uff0c\u8881\u627f\u5fd7\u4e00\u751f\u957f\u4e8e\u5175\u6208\u62f3\u5251\u4e4b\u95f4\uff0c\u4ece\u672a\u9886\u7565\u8fc7\u8fd9\u822c\u98ce\u96c5\u97f5\u4e8b\uff0c\u4e0d\u7981\u91ba\u91ba\u7136\u6709\u5982\u4e2d\u9152\u3002\u6e29\u9752\u6401\u4e0b\u6d1e\u7bab\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u597d\u542c\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e16\u754c\u4e0a\u7adf\u6709\u8fd9\u822c\u597d\u542c\u7684\u7bab\u58f0\uff0c\u4ee5\u524d\u6211\u505a\u68a6\u4e5f\u6ca1\u60f3\u5230\u8fc7\u3002\u8fd9\u66f2\u5b50\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u201d\u6e29\u9752\u8138\u4e0a\u7a81\u7136\u4e00\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u8ddf\u4f60\u8bf4\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u9053\uff1a\u201c\u8fd9\u66f2\u5b50\u53eb\u2018\u773c\u513f\u5a9a\u2019\u3002\u201d\u773c\u6ce2\u6d41\u52a8\uff0c\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u8fd9\u65f6\u4e24\u4eba\u5750\u5f97\u751a\u8fd1\uff0c\u8881\u627f\u5fd7\u9f3b\u4e2d\u6240\u95fb\uff0c\u9664\u4e86\u73ab\u7470\u6e05\u9999\uff0c\u66f4\u6709\u6de1\u6de1\u7684\u8102\u7c89\u4e4b\u6c14\uff0c\u5fc3\u60f3\u8fd9\u4eba\u5b9e\u5728\u592a\u6ca1\u4e08\u592b\u6c14\u6982\uff0c\u4ed6\u76f8\u8c8c\u672c\u5c31\u5df2\u592a\u8fc7\u4fca\u4fcf\uff0c\u518d\u8fd9\u822c\u6d82\u8102\u62b9\u7c89\uff0c\u6210\u751a\u4e48\u6837\u5b50\uff1f\u5e78\u4e8f\u81ea\u5df1\u4e0d\u662f\u53e3\u9f7f\u8f7b\u8584\u4e4b\u4eba\uff0c\u5426\u5219\u5c82\u4e0d\u803b\u7b11\u4e8e\u4ed6\uff1f\u53c8\u60f3\uff1a\u6c5f\u5357\u4e60\u6c14\u5962\u534e\uff0c\u83ab\u975e\u4ed6\u5bcc\u5bb6\u5b50\u5f1f\uff0c\u5c3d\u7686\u5982\u6b64\uff0c\u5012\u662f\u6211\u5c71\u91ce\u6751\u592b\uff0c\u5c11\u89c1\u591a\u602a\u4e86\uff1f \u3000\u3000\u6b63\u81ea\u601d\u5fd6\uff0c\u542c\u5f97\u6e29\u9752\u95ee\u9053\uff1a\u201c\u4f60\u7231\u4e0d\u7231\u542c\u6211\u5439\u7bab\uff1f\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\u3002\u6e29\u9752\u53c8\u628a\u7bab\u653e\u5230\u5507\u8fb9\uff0c\u5439\u4e86\u8d77\u6765\uff0c\u6e10\u6e10\u7684\u97f5\u8f6c\u51c4\u82e6\u3002\u8881\u627f\u5fd7\u542c\u5f97\u51fa\u795e\uff0c\u7a81\u7136\u7bab\u58f0\u9aa4\u6b47\uff0c\u6e29\u9752\u53cc\u624b\u4e00\u62d7\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u628a\u4e00\u652f\u7af9\u7bab\u6298\u6210\u4e24\u622a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u2026\u2026\u4f60\u4e0d\u662f\u5439\u5f97\u597d\u597d\u7684\u4e48\uff1f\u201d \u3000\u3000\u6e29\u9752\u4f4e\u4e0b\u4e86\u5934\uff0c\u6084\u58f0\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e0d\u5439\u7ed9\u8c01\u542c\u3002\u4ed6\u4eec\u5c31\u77e5\u9053\u52a8\u5200\u52a8\u5251\uff0c\u4e5f\u4e0d\u7231\u542c\u8fd9\u4e2a\u3002\u201d\u8881\u627f\u5fd7\u6025\u9053\uff1a\u201c\u6211\u6ca1\u9a97\u4f60\uff0c\u6211\u771f\u7684\u7231\u542c\u5440\uff0c\u771f\u7684\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u4f60\u660e\u5929\u8981\u53bb\u5566\uff0c\u53bb\u4e86\u4e4b\u540e\uff0c\u4f60\u6c38\u8fdc\u4e0d\u4f1a\u518d\u6765\uff0c\u6211\u8fd8\u5439\u751a\u4e48\u7bab\uff1f\u201d\u987f\u4e86\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u6211\u813e\u6c14\u4e0d\u597d\uff0c\u6211\u81ea\u5df1\u77e5\u9053\uff0c\u53ef\u662f\u6211\u5c31\u7ba1\u4e0d\u4e86\u81ea\u5df1\u2026\u2026\u6211\u77e5\u9053\u4f60\u8ba8\u538c\u6211\uff0c\u5fc3\u91cc\u5f88\u77a7\u4e0d\u8d77\u6211\u3002\u201d\u8881\u627f\u5fd7\u4e00\u65f6\u4e0d\u77e5\u8bf4\u751a\u4e48\u8bdd\u597d\u3002\u6e29\u9752\u53c8\u9053\uff1a\u201c\u56e0\u6b64\u4e0a\u4f60\u6c38\u8fdc\u4e0d\u4f1a\u518d\u6765\u4e86\u3002\u6211\u2026\u2026\u6211\u518d\u4e5f\u89c1\u4f60\u4e0d\u7740\u4e86\u3002\u201d \u3000\u3000\u542c\u4ed6\u8a00\u4e2d\u4e4b\u610f\uff0c\u5ff5\u53ca\u4eca\u540e\u4e0d\u590d\u76f8\u89c1\uff0c\u7adf\u662f\u8bf4\u4e0d\u51fa\u7684\u60c6\u6005\u96be\u8fc7\uff0c\u8881\u627f\u5fd7\u4e0d\u7981\u611f\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u77a7\u5f97\u51fa\uff0c\u6211\u751a\u4e48\u4e5f\u4e0d\u61c2\u3002\u6211\u521d\u5165\u6c5f\u6e56\uff0c\u53ef\u4e0d\u4f1a\u8bf4\u8c0e\u3002\u4f60\u8bf4\u6211\u5fc3\u91cc\u77a7\u4e0d\u8d77\u4f60\uff0c\u89c9\u5f97\u4f60\u8ba8\u538c\uff0c\u8001\u5b9e\u8bf4\uff0c\u90a3\u672c\u6765\u4e0d\u9519\uff0c\u4e0d\u8fc7\u73b0\u4e0b\u6709\u4e9b\u4e0d\u540c\u4e86\u3002\u201d\u6e29\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u731c\u4f60\u4e00\u5b9a\u6709\u751a\u4e48\u5fc3\u4e8b\uff0c\u662f\u4ee5\u813e\u6c14\u6709\u70b9\u5947\u602a\uff0c\u90a3\u662f\u751a\u4e48\u4e8b\uff1f\u80fd\u8bf4\u7ed9\u6211\u542c\u4e48\uff1f\u201d \u3000\u3000\u6e29\u9752\u6c89\u541f\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u8bf4\u3002\u5c31\u6015\u4f60\u4f1a\u66f4\u52a0\u77a7\u6211\u4e0d\u8d77\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e00\u5b9a\u4e0d\u4f1a\u3002\u201d\u6e29\u9752\u54ac\u4e00\u54ac\u7259\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u8bf4\u3002\u6211\u5988\u5988\u505a\u59d1\u5a18\u7684\u65f6\u5019\uff0c\u53d7\u4e86\u4eba\u6b3a\u4fae\uff0c\u751f\u4e0b\u6211\u6765\u3002\u6211\u4e94\u4f4d\u7237\u7237\u6253\u4e0d\u8fc7\u8fd9\u4eba\uff0c\u540e\u6765\u7ea6\u4e86\u5341\u591a\u4e2a\u597d\u624b\uff0c\u624d\u628a\u90a3\u4eba\u6253\u8dd1\uff0c\u6240\u4ee5\u6211\u662f\u6ca1\u7238\u7238\u7684\u4eba\uff0c\u6211\u662f\u4e2a\u79c1\u751f\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u8bed\u97f3\u545c\u54bd\uff0c\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u53ef\u602a\u4e0d\u5f97\u4f60\uff0c\u4e5f\u602a\u4e0d\u5f97\u4f60\u5988\u5988\uff0c\u662f\u90a3\u574f\u4eba\u4e0d\u597d\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u662f\u6211\u7684\u7238\u7238\u554a\u3002\u4eba\u5bb6\u2026\u2026\u4eba\u5bb6\u80cc\u5730\u91cc\u90fd\u9a82\u6211\uff0c\u9a82\u6211\u5988\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6709\u8c01\u8fd9\u4e48\u5351\u9119\u65e0\u804a\uff0c\u6211\u5e2e\u4f60\u6253\u4ed6\u3002\u73b0\u4e0b\u6211\u660e\u767d\u4e86\u539f\u56e0\uff0c\u4fbf\u4e0d\u8ba8\u538c\u4f60\u4e86\u3002\u4f60\u5982\u771f\u5f53\u6211\u662f\u670b\u53cb\uff0c\u6211\u4e00\u5b9a\u518d\u6765\u770b\u4f60\u3002\u201d\u6e29\u9752\u5927\u559c\uff0c\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u559c\u52a8\u989c\u8272\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6765\u770b\u4f60\uff0c\u4f60\u5f88\u559c\u6b22\u5417\uff1f\u201d \u3000\u3000\u6e29\u9752\u62c9\u4f4f\u4ed6\u53cc\u624b\u8f7b\u8f7b\u6447\u6643\uff0c\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8bf4\u8fc7\u7684\uff0c\u4e00\u5b9a\u8981\u6765\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u51b3\u4e0d\u9a97\u4f60\u3002\u201d \u3000\u3000\u5ffd\u7136\u80cc\u540e\u6709\u58f0\u5fae\u54cd\uff0c\u8881\u627f\u5fd7\u7ad9\u8d77\u8f6c\u8eab\uff0c\u53ea\u542c\u4e00\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u534a\u591c\u4e09\u66f4\u7684\uff0c\u5728\u8fd9\u91cc\u5077\u5077\u6478\u6478\u7684\u5e72\u4e48\uff1f\u201d\u90a3\u4eba\u6b63\u662f\u6e29\u6b63\u3002\u53ea\u89c1\u4ed6\u6ee1\u8138\u6012\u6c14\uff0c\u53cc\u624b\u53c9\u8170\uff0c\u5927\u6709\u95ee\u7f6a\u4e4b\u610f\u3002 \u3000\u3000\u6e29\u9752\u672c\u6765\u5403\u4e86\u4e00\u60ca\uff0c\u89c1\u5230\u662f\u4ed6\uff0c\u6012\u9053\uff1a\u201c\u4f60\u6765\u5e72\u751a\u4e48\uff1f\u201d\u6e29\u6b63\u9053\uff1a\u201c\u95ee\u4f60\u81ea\u5df1\u5440\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u6211\u548c\u8881\u5144\u5728\u8fd9\u91cc\u8d4f\u6708\uff0c\u8c01\u8bf7\u4f60\u6765\u4e86\uff1f\u8fd9\u91cc\u9664\u4e86\u6211\u5988\u5988\u4e4b\u5916\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u6765\u3002\u4e09\u7237\u7237\u8bf4\u8fc7\u7684\uff0c\u4f60\u6562\u4e0d\u542c\u8bdd\uff1f\u201d\u6e29\u6b63\u5411\u8881\u627f\u5fd7\u4e00\u6307\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u53c8\u6765\u4e86\uff1f\u201d\u6e29\u9752\u9053\uff1a\u201c\u6211\u8bf7\u4ed6\u6765\u7684\uff0c\u4f60\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u5144\u5f1f\u4e3a\u81ea\u5df1\u4f24\u4e86\u548c\u6c14\uff0c\u5f88\u662f\u4e0d\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8d4f\u6708\u5df2\u7ecf\u5c3d\u5174\uff0c\u5927\u5bb6\u540c\u53bb\u5b89\u606f\u5427\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u6211\u504f\u4e0d\u53bb\uff0c\u4f60\u5750\u7740\u3002\u201d\u8881\u627f\u5fd7\u53ea\u5f97\u53c8\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u6e29\u6b63\u5446\u5728\u5f53\u5730\uff0c\u95f7\u95f7\u4e0d\u8bed\uff0c\u5411\u8881\u627f\u5fd7\u4fa7\u76ee\u659c\u7768\uff0c\u773c\u5149\u4e2d\u6ee1\u662f\u618e\u6076\u4e4b\u610f\u3002 \u3000\u3000\u6e29\u9752\u6012\u9053\uff1a\u201c\u8fd9\u4e9b\u82b1\u662f\u6211\u4eb2\u624b\u683d\u7684\uff0c\u6211\u4e0d\u8bb8\u4f60\u770b\u3002\u201d\u6e29\u6b63\u9053\uff1a\u201c\u6211\u770b\u90fd\u770b\u8fc7\u4e86\uff0c\u4f60\u6316\u51fa\u6211\u7684\u773c\u73e0\u5b50\u4e48\uff1f\u6211\u8fd8\u8981\u95fb\u4e00\u4e0b\u3002\u201d \u3000\u3000\u8bf4\u7740\u7528\u9f3b\u5b50\u55c5\u4e86\u51e0\u4e0b\u3002\u6e29\u9752\u6012\u706b\u5927\u70bd\uff0c\u5ffd\u5730\u8df3\u8d77\u8eab\u6765\uff0c\u53cc\u624b\u4e00\u9635\u4e71\u62d4\uff0c\u62d4\u8d77\u4e86\u4e8c\u5341\u51e0\u4e1b\u73ab\u7470\uff0c\u968f\u62d4\u968f\u629b\uff0c\u54ed\u9053\uff1a\u201c\u4f60\u6b3a\u4fae\u6211\uff01\u4f60\u6b3a\u4fae\u6211\uff01\u62d4\u6389\u4e86\u73ab\u7470\uff0c\u8c01\u4e5f\u770b\u4e0d\u6210\uff0c\u8fd9\u6837\u4f60\u624d\u9ad8\u5174\u4e86\u5427\uff1f\u201d \u3000\u3000\u6e29\u6b63\u8138\u8272\u94c1\u9752\uff0c\u6068\u6068\u800c\u53bb\uff0c\u8d70\u4e86\u51e0\u6b65\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u4e00\u756a\u5fc3\u610f\uff0c\u4f60\u5374\u5982\u6b64\u5f85\u6211\uff0c\u4f60\u81ea\u5df1\u60f3\u60f3\uff0c\u6709\u6ca1\u6709\u826f\u5fc3\u3002\u8fd9\u59d3\u8881\u7684\u5e7f\u4e1c\u86ee\u5b50\u9ed1\u4e0d\u6e9c\u79cb\u7684\uff0c\u4f60\u2026\u2026\u4f60\u504f\u751f\u2026\u2026\u201d\u6e29\u9752\u54ed\u9053\uff1a\u201c\u8c01\u8981\u4f60\u5bf9\u6211\u597d\u4e86\uff1f\u4f60\u77a7\u7740\u6211\u4e0d\u987a\u773c\uff0c\u4f60\u8981\u7237\u7237\u4eec\u628a\u6211\u5a18\u513f\u4fe9\u8d76\u51fa\u53bb\u597d\u5566\u3002\u6211\u548c\u8881\u5144\u5728\u8fd9\u91cc\uff0c\u4f60\u53bb\u8ddf\u7237\u7237\u4eec\u8bf4\u597d\u4e86\u3002\u4f60\u81ea\u5df1\u53c8\u751f\u5f97\u597d\u4fca\u5417\uff1f\u201d\u6e29\u6b63\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5782\u5934\u4e27\u6c14\u7684\u8d70\u4e86\u3002 \u3000\u3000\u6e29\u9752\u56de\u5230\u4ead\u4e2d\u5750\u4e0b\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5bf9\u4f60\u54e5\u54e5\u8fd9\u6837\u5b50\uff1f\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u4ed6\u53c8\u4e0d\u662f\u6211\u771f\u7684\u54e5\u54e5\u3002\u6211\u5988\u5988\u624d\u59d3\u6e29\uff0c\u8fd9\u513f\u662f\u6211\u5916\u516c\u5bb6\u3002\u4ed6\u662f\u6211\u5988\u5988\u5802\u5144\u7684\u513f\u5b50\uff0c\u662f\u6211\u8868\u54e5\u3002\u8981\u662f\u6211\u6709\u7238\u7238\uff0c\u6709\u81ea\u5df1\u7684\u5bb6\uff0c\u4e5f\u7528\u4e0d\u7740\u4f4f\u5728\u522b\u4eba\u5bb6\u91cc\uff0c\u53d7\u522b\u4eba\u7684\u6c14\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u53c8\u5782\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u77a7\u4ed6\u5bf9\u4f60\u5012\u662f\u633a\u597d\u7684\uff0c\u53cd\u800c\u4f60\u5440\uff0c\u5bf9\u4ed6\u5f88\u51f6\u3002\u201d\u6e29\u9752\u5ffd\u7136\u7b11\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u6211\u5982\u4e0d\u5bf9\u4ed6\u51f6\uff0c\u4ed6\u66f4\u8981\u65e0\u6cd5\u65e0\u5929\u5462\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u53c8\u54ed\u53c8\u7b11\uff0c\u4e00\u526f\u5929\u771f\u70c2\u6f2b\u7684\u6837\u5b50\uff0c\u53c8\u60f3\u5230\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u4e0d\u7981\u987f\u5174\u540c\u75c5\u76f8\u601c\u4e4b\u611f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7238\u7238\u7ed9\u4eba\u5bb3\u6b7b\u4e86\uff0c\u90a3\u65f6\u6211\u8fd8\u53ea\u4e03\u5c81\uff0c\u6211\u5988\u5988\u4e5f\u662f\u90a3\u5e74\u6b7b\u7684\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u4f60\u62a5\u4e86\u4ec7\u6ca1\u6709\uff1f\u201d\u8881\u627f\u5fd7\u53f9\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u6211\u771f\u662f\u4e0d\u5e78\u2026\u2026\u201d\u6e29\u9752\u9053\uff1a\u201c\u4f60\u62a5\u4ec7\u65f6\u6211\u4e00\u5b9a\u5e2e\u4f60\uff0c\u4e0d\u7ba1\u8fd9\u4ec7\u4eba\u591a\u4e48\u5389\u5bb3\uff0c\u6211\u4e00\u5b9a\u5e2e\u4f60\u3002\u201d\u8881\u627f\u5fd7\u597d\u751f\u611f\u6fc0\uff0c\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\u3002 \u3000\u3000\u6e29\u9752\u7684\u624b\u5fae\u5fae\u4e00\u7f29\uff0c\u968f\u5373\u7ed9\u4ed6\u634f\u7740\u4e0d\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u672c\u4e8b\u6bd4\u6211\u5f3a\u5f97\u591a\uff0c\u4f46\u6211\u77a7\u4f60\u5bf9\u6c5f\u6e56\u4e0a\u7684\u4e8b\u5f88\u751f\uff0c\u6211\u5c06\u6765\u53ef\u4ee5\u5e2e\u4f60\u51fa\u4e9b\u4e3b\u610f\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u771f\u597d\u3002\u6211\u6ca1\u4e00\u4e2a\u5e74\u7eaa\u5dee\u4e0d\u591a\u7684\u670b\u53cb\uff0c\u73b0\u4eca\u9047\u5230\u4e86\u4f60\u2026\u2026\u201d\u6e29\u9752\u4f4e\u5934\u9053\uff1a\u201c\u5c31\u662f\u6211\u813e\u6c14\u4e0d\u597d\uff0c\u603b\u6709\u4e00\u5929\u4f1a\u5f97\u7f6a\u4f60\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u65e2\u5f53\u4f60\u662f\u670b\u53cb\uff0c\u77e5\u9053\u4f60\u5fc3\u5730\u597d\uff0c\u5c31\u7b97\u5f97\u7f6a\u4e86\u6211\uff0c\u4e5f\u4e0d\u4f1a\u4ecb\u610f\u3002\u201d\u6e29\u9752\u5927\u559c\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5c31\u662f\u8fd9\u4ef6\u4e8b\u4e0d\u653e\u5fc3\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u795e\u6001\u5927\u53d8\uff0c\u6e29\u67d4\u65af\u6587\uff0c\u4e0e\u5148\u524d\u72e0\u8fa3\u7684\u795e\u60c5\u5927\u4e0d\u76f8\u540c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u6e29\u5144\u80af\u4e0d\u80af\u542c\uff1f\u201d\u6e29\u9752\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u6211\u5c31\u542c\u4e09\u4e2a\u4eba\u7684\u8bdd\uff0c\u7b2c\u4e00\u4e2a\u662f\u5988\u5988\uff0c\u7b2c\u4e8c\u4e2a\u6211\u4eb2\u5916\u516c\u4e09\u7237\u7237\uff0c\u7b2c\u4e09\u4e2a\u5c31\u662f\u4f60\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u9707\uff0c\u8bf4\u9053\uff1a\u201c\u627f\u4f60\u8fd9\u4e48\u77a7\u5f97\u8d77\u6211\uff0c\u5176\u5b9e\uff0c\u522b\u4eba\u7684\u8bdd\u53ea\u8981\u8bf4\u5f97\u5bf9\uff0c\u54b1\u4eec\u90fd\u8be5\u542c\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u54fc\uff0c\u6211\u624d\u4e0d\u542c\u5462\u3002\u8c01\u5f85\u6211\u597d\uff0c\u6211\u2026\u2026\u6211\u5fc3\u91cc\u4e5f\u559c\u6b22\u4ed6\uff0c\u90a3\u4e48\u4e0d\u7ba1\u4ed6\u8bf4\u5f97\u5bf9\u4e0d\u5bf9\uff0c\u6211\u90fd\u542c\u4ed6\u7684\u3002\u8981\u662f\u6211\u8ba8\u538c\u7684\u4eba\u54ea\uff0c\u4ed6\u8bf4\u5f97\u518d\u5bf9\uff0c\u6211\u504f\u504f\u4e0d\u7167\u4ed6\u7684\u8bdd\u505a\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u5b69\u5b50\u813e\u6c14\uff0c\u4f60\u51e0\u5c81\u4e86\uff1f\u201d\u6e29\u9752\u9053\uff1a\u201c\u6211\u5341\u516b\u5c81\uff0c\u4f60\u5462\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5927\u4f60\u4e24\u5c81\u3002\u201d \u3000\u3000\u6e29\u9752\u4f4e\u4e0b\u4e86\u5934\uff0c\u5ffd\u7136\u8138\u4e0a\u4e00\u7ea2\uff0c\u6084\u58f0\u9053\uff1a\u201c\u6211\u6ca1\u4eb2\u54e5\u54e5\uff0c\u54b1\u4eec\u7ed3\u62dc\u4e3a\u5144\u5f1f\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u81ea\u5e7c\u4fbf\u906d\u8eab\u4e16\u5927\u53d8\uff0c\u81ea\u7136\u800c\u7136\u7684\u8bf8\u4e8b\u8c28\u7ec6\uff0c\u5bf9\u6e29\u9752\u7684\u8eab\u4e16\u5b9e\u5728\u6beb\u4e0d\u77e5\u60c5\uff0c\u867d\u89c1\u4ed6\u5bf9\u81ea\u5df1\u63a8\u5fc3\u7f6e\u8179\uff0c\u4f46\u63d0\u5230\u7ed3\u62dc\uff0c\u90a3\u662f\u7ec8\u8eab\u7978\u798f\u4e0e\u5171\u7684\u5927\u4e8b\uff0c\u4e0d\u7531\u5f97\u8fdf\u7591\u3002 \u3000\u3000\u6e29\u9752\u89c1\u4ed6\u6c89\u541f\u4e0d\u7b54\uff0c\u84e6\u5730\u91cc\u7ad9\u8d77\u8eab\u6765\uff0c\u5954\u51fa\u4ead\u5b50\u3002\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u968f\u540e\u8ffd\u53bb\uff0c\u53ea\u89c1\u4ed6\u5411\u5c71\u9876\u76f4\u5954\uff0c\u5fc3\u60f3\u8fd9\u4eba\u6027\u60c5\u6fc0\u70c8\u3002\u522b\u56e0\u81ea\u5df1\u4e0d\u80af\u7b54\u5e94\uff0c\u7f9e\u8fb1\u4e86\u4ed6\uff0c\u505a\u51fa\u751a\u4e48\u4e8b\u6765\uff0c\u5fd9\u5c55\u5f00\u8f7b\u529f\uff0c\u51e0\u4e2a\u8d77\u843d\uff0c\u5df2\u62a2\u5728\u4ed6\u9762\u524d\uff0c\u53eb\u9053\uff1a\u201c\u6e29\u5144\u5f1f\uff0c\u4f60\u751f\u6211\u7684\u6c14\u4e48\uff1f\u201d \u3000\u3000\u6e29\u9752\u542c\u4ed6\u53e3\u79f0\u201c\u5144\u5f1f\u201d\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u767b\u65f6\u4f4f\u8db3\uff0c\u5750\u5012\u5728\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\u6211\u4e0d\u8d77\uff0c\u600e\u4e48\u53c8\u53eb\u4eba\u5bb6\u5144\u5f1f\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u51e0\u65f6\u77a7\u4f60\u4e0d\u8d77\uff1f\u6765\u6765\u6765\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u7ed3\u62dc\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e24\u4eba\u5411\u7740\u6708\u4eae\u8dea\u5012\uff0c\u53d1\u4e86\u6709\u798f\u5171\u4eab\u3001\u6709\u96be\u540c\u5f53\u7684\u91cd\u8a93\u3002\u7ad9\u8d77\u8eab\u6765\uff0c\u6e29\u9752\u5411\u8881\u627f\u5fd7\u4e00\u63d6\uff0c\u4f4e\u4f4e\u53eb\u4e86\u58f0\uff1a\u201c\u5927\u54e5\uff01\u201d\u8881\u627f\u5fd7\u56de\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u4f60\u4e8c\u5f1f\u5427\u3002\u73b0\u4e0b\u4e0d\u65e9\u5566\uff0c\u54b1\u4eec\u56de\u53bb\u7761\u5427\u3002\u201d\u4e24\u4eba\u7275\u624b\u56de\u623f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u522b\u56de\u53bb\u5435\u9192\u4f2f\u6bcd\u4e86\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u513f\u540c\u69bb\u800c\u7761\u5427\u3002\u201d\u6e29\u9752\u9661\u7136\u6ee1\u8138\u7ea2\u6655\uff0c\u628a\u624b\u4e00\u6454\uff0c\u55d4\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u968f\u5373\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u5929\u89c1\u3002\u201d\u98d8\u7136\u51fa\u623f\uff0c\u628a\u8881\u627f\u5fd7\u5f04\u5f97\u6115\u7136\u534a\u664c\uff0c\u4e0d\u77e5\u6240\u4e91\u3002 \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u8881\u627f\u5fd7\u6b63\u5750\u5728\u5e8a\u4e0a\u7ec3\u529f\uff0c\u5c0f\u83ca\u9001\u6765\u65e9\u70b9\u3002\u8881\u627f\u5fd7\u8df3\u4e0b\u5e8a\u6765\uff0c\u5411\u5979\u9053\u52b3\uff0c\u6b63\u5403\u65e9\u70b9\uff0c\u6e29\u9752\u8d70\u8fdb\u623f\u6765\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5916\u9762\u6765\u4e86\u4e2a\u5973\u5b50\uff0c\u8bf4\u662f\u6765\u8ba8\u91d1\u5b50\u7684\uff0c\u54b1\u4eec\u51fa\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\u3002\u201d\u5fc3\u60f3\u593a\u4eba\u8d22\u7269\uff0c\u7ec8\u7a76\u4e0d\u59a5\uff0c\u5982\u4f55\u529d\u5f97\u4e49\u5f1f\u8fd8\u4e86\u4eba\u5bb6\u624d\u597d\u3002 \u3000\u3000\u4e24\u4eba\u6765\u5230\u5385\u53e3\uff0c\u4fbf\u542c\u5f97\u5385\u4e2d\u811a\u6b65\u58f0\u6025\uff0c\u98ce\u58f0\u547c\u547c\uff0c\u6709\u4eba\u5728\u52a8\u624b\u62da\u6597\uff0c\u4e00\u8d70\u8fdb\u5927\u5385\uff0c\u53ea\u89c1\u6e29\u6b63\u5feb\u6b65\u6e38\u8d70\uff0c\u821e\u52a8\u5355\u5200\uff0c\u6b63\u4e0e\u4e00\u4e2a\u4f7f\u5251\u7684\u5e74\u8f7b\u5973\u5b50\u6597\u5f97\u751a\u7d27\u3002\u65c1\u8fb9\u4e24\u4e2a\u8001\u8005\u5750\u5728\u6905\u4e2d\u89c2\u6218\u3002\u4e00\u4e2a\u8001\u4eba\u624b\u62ff\u62d0\u6756\uff0c\u53e6\u4e00\u4e2a\u5219\u662f\u7a7a\u624b\u3002\u6e29\u9752\u8d70\u5230\u62ff\u62d0\u6756\u7684\u8001\u8005\u8eab\u65c1\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002\u90a3\u8001\u8005\u5411\u8881\u627f\u5fd7\u4ed4\u7ec6\u6253\u91cf\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u90a3\u5c11\u5973\u5927\u7ea6\u5341\u516b\u4e5d\u5c81\u5e74\u7eaa\uff0c\u53cc\u988a\u6655\u7ea2\uff0c\u5bb9\u8c8c\u5a1f\u79c0\uff0c\u653b\u5b88\u4e4b\u95f4\uff0c\u6cd5\u5ea6\u4e25\u8c28\u3002\u4e24\u4eba\u62c6\u4e86\u5341\u4f59\u62db\uff0c\u4e00\u65f6\u5206\u4e0d\u51fa\u9ad8\u4e0b\u3002\u8881\u627f\u5fd7\u5bf9\u5979\u5251\u6cd5\u5374\u8d8a\u770b\u8d8a\u662f\u7591\u5fc3\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5c11\u5973\u6b3a\u8fdb\u4e00\u6b65\uff0c\u957f\u5251\u6307\u5411\u6e29\u6b63\u80a9\u5934\uff0c\u6e29\u6b63\u53cd\u5200\u683c\u51fb\uff0c\u8fc5\u901f\u4e4b\u6781\uff0c\u773c\u89c1\u90a3\u5c11\u5973\u7684\u957f\u5251\u5c31\u8981\u88ab\u4ed6\u5355\u5200\u7838\u98de\u3002\u54ea\u77e5\u6e29\u6b63\u5feb\uff0c\u90a3\u5c11\u5973\u66f4\u5feb\uff0c\u957f\u5251\u5708\u8f6c\uff0c\u500f\u5730\u5411\u6e29\u6b63\u9888\u4e2d\u5212\u6765\u3002\u6e29\u6b63\u4e00\u60ca\uff0c\u5411\u540e\u8fde\u7eb5\u4e09\u6b65\u3002\u90a3\u5c11\u5973\u4e58\u52bf\u76f4\u4e0a\uff0c\u5237\u5237\u6570\u5251\uff0c\u653b\u52bf\u5341\u5206\u8fc5\u6377\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5df2\u770b\u660e\u767d\u5979\u6b66\u529f\u5bb6\u6570\uff0c\u867d\u4e0d\u662f\u534e\u5c71\u6d3e\u95e8\u4eba\uff0c\u4f46\u5fc5\u53d7\u8fc7\u672c\u95e8\u4e2d\u4eba\u7684\u6307\u70b9\uff0c\u5426\u5219\u4f9d\u5979\u529f\u529b\uff0c\u65e9\u5df2\u652f\u6301\u4e0d\u4f4f\uff0c\u4ed7\u7740\u5251\u672f\u7cbe\u5947\uff0c\u624d\u548c\u6e29\u6b63\u52c9\u5f3a\u6253\u4e2a\u5e73\u624b\uff0c\u83ab\u770b\u5979\u653b\u52bf\u51cc\u5389\uff0c\u5176\u5b9e\u6e29\u6b63\u53c8\u7a33\u53c8\u72e0\uff0c\u540e\u52b2\u6bd4\u5979\u957f\u5f97\u591a\u3002\u6e29\u9752\u4e5f\u5df2\u77a7\u51fa\u90a3\u5c11\u5973\u975e\u6e29\u6b63\u654c\u624b\uff0c\u5fae\u5fae\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u51ed\u8fd9\u70b9\u5b50\u9053\u884c\uff0c\u4e5f\u60f3\u4e0a\u95e8\u6765\u8ba8\u4e1c\u897f\u3002\u201d \u3000\u3000\u518d\u62c6\u6570\u5341\u62db\uff0c\u679c\u7136\u90a3\u5c11\u5973\u653b\u52bf\u5df2\u7f13\uff0c\u6e29\u6b63\u5374\u662f\u4e00\u5200\u72e0\u4f3c\u4e00\u5200\uff0c\u518d\u6597\u7247\u523b\uff0c\u90a3\u5c11\u5973\u66f4\u662f\u5de6\u652f\u53f3\u7ecc\uff0c\u8fde\u9047\u51f6\u9669\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u60c5\u52bf\u5371\u6025\uff0c\u5ffd\u5730\u7eb5\u8d77\uff0c\u8dc3\u5165\u4e24\u4eba\u4e4b\u95f4\u3002\u4e24\u4eba\u6597\u5f97\u6b63\u7d27\uff0c\u5175\u5203\u54ea\u91cc\u6536\u5f97\u4f4f\u52bf\uff1f\u4e00\u5200\u4e00\u5251\uff0c\u9f50\u5954\u4ed6\u8eab\u4e0a\u780d\u5230\u3002\u6e29\u9752\u60ca\u547c\u4e00\u58f0\u3002\u90a3\u4e24\u4e2a\u8001\u8005\u4e00\u9f50\u7ad9\u8d77\uff0c\u53ea\u56e0\u51fa\u5176\u4e0d\u610f\uff0c\u90fd\u6765\u4e0d\u53ca\u6551\u63f4\u3002\u5374\u89c1\u8881\u627f\u5fd7\u53f3\u624b\u5728\u6e29\u6b63\u624b\u8155\u4e0a\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5de6\u624b\u53cd\u624b\u5728\u90a3\u5c11\u5973\u624b\u8155\u4e0a\u5fae\u5fae\u4e00\u6321\u3002\u4e24\u4eba\u5175\u5203\u90fd\u662f\u4e0d\u7531\u81ea\u4e3b\u7684\u5411\u5916\u8361\u4e86\u5f00\u53bb\uff0c\u5f53\u5373\u9f50\u5411\u540e\u8dc3\u3002\u4e24\u4e2a\u8001\u8005\u90fd\u662f\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u663e\u7136\u5bf9\u8881\u627f\u5fd7\u8fd9\u624b\u529f\u592b\u751a\u662f\u60ca\u8be7\uff0c\u4e24\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u6e29\u6b63\u53ea\u9053\u8881\u627f\u5fd7\u8bb0\u7740\u6628\u591c\u4e4b\u6068\uff0c\u6b64\u65f6\u51fa\u624b\u8ddf\u81ea\u5df1\u4e3a\u96be\u3002\u90a3\u5c11\u5973\u5374\u89c1\u4ed6\u4e0e\u6e29\u9752\u540c\u4ece\u5185\u5802\u51fa\u6765\uff0c\u81ea\u7136\u4ee5\u4e3a\u4ed6\u662f\u5bf9\u65b9\u4e00\u515a\uff0c\u773c\u89c1\u4e0d\u654c\uff0c\u4ed7\u5251\u5c31\u8981\u8dc3\u51fa\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u4e14\u6162\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u90a3\u5c11\u5973\u6012\u9053\uff1a\u201c\u6211\u6253\u4f60\u4eec\u4e0d\u8d62\uff0c\u81ea\u6709\u529f\u592b\u6bd4\u6211\u9ad8\u7684\u4eba\u6765\u8ba8\u91d1\u5b50\uff0c\u4f60\u4eec\u8981\u5f85\u600e\u6837\uff1f\u201d\u8881\u627f\u5fd7\u62f1\u624b\u9053\uff1a\u201c\u59d1\u5a18\u52ff\u602a\uff0c\u8bf7\u6559\u5c0a\u59d3\u5927\u540d\uff0c\u4ee4\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u90a3\u5c11\u5973\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8c01\u6765\u8ddf\u4f60\u5570\u5506\uff1f\u201d\u9661\u7136\u8dc3\u8d77\uff0c\u5411\u95e8\u5916\u7eb5\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5de6\u8db3\u4e00\u70b9\uff0c\u5df2\u6321\u5728\u95e8\u5916\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u83ab\u8d70\uff0c\u6211\u5e2e\u4f60\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e00\u5446\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u59d3\u8881\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e00\u5bf9\u4e4c\u6e9c\u6e9c\u7684\u773c\u73e0\u76ef\u4f4f\u4ed6\u7684\u8138\uff0c\u5ffd\u7136\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u4f60\u8bc6\u5f97\u5b89\u5927\u5a18\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u5168\u8eab\u4e00\u9707\uff0c\u624b\u5fc3\u53d1\u70ed\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u8881\u627f\u5fd7\uff0c\u4f60\u662f\u5c0f\u6167\uff1f\u201d\u90a3\u5c11\u5973\u9ad8\u5174\u5f97\u5fd8\u4e86\u5f62\uff0c\u62c9\u4f4f\u4ed6\u624b\uff0c\u53eb\u9053\uff1a\u201c\u662f\u554a\uff0c\u662f\u554a\uff01\u4f60\u662f\u627f\u5fd7\u5927\u54e5\u3002\u201d\u9aa4\u7136\u95f4\u60f3\u8d77\u7537\u5973\u6709\u522b\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u653e\u4e0b\u4e86\u624b\u3002\u6e29\u9752\u89c1\u4e86\u8fd9\u526f\u60c5\u72b6\uff0c\u8138\u4e0a\u767b\u65f6\u5982\u540c\u7f69\u4e86\u4e00\u5c42\u4e25\u971c\u3002 \u3000\u3000\u6e29\u6b63\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u6211\u9053\u8881\u5144\u662f\u8c01\uff1f\u539f\u6765\u662f\u674e\u81ea\u6210\u6d3e\u4e86\u6765\u5367\u5e95\u7684\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u4e0e\u95ef\u738b\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u5012\u4e5f\u4e0d\u9519\uff0c\u53ef\u8bf4\u4e0d\u4e0a\u5367\u5e95\u3002\u8fd9\u4f4d\u59d1\u5a18\u662f\u6211\u4e16\u4ea4\u3002\u4e0d\u77e5\u4e24\u4f4d\u56e0\u4f55\u4ea4\u624b\uff0c\u5144\u5f1f\u6597\u80c6\uff0c\u66ff\u4e24\u4f4d\u8bf4\u548c\u5982\u4f55\uff1f\u201d\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u627f\u5fd7\u5927\u54e5\uff0c\u4ed6\u4eec\u65e2\u662f\u4f60\u670b\u53cb\uff0c\u53ea\u8981\u628a\u91d1\u5b50\u4ea4\u51fa\uff0c\u90a3\u5c31\u4e00\u5207\u4e0d\u63d0\u3002\u201d\u6e29\u9752\u51b7\u51b7\u7684\u9053\uff1a\u201c\u6709\u8fd9\u4e48\u5bb9\u6613\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6211\u7ed9\u4f60\u5f15\u89c1\uff0c\u8fd9\u4f4d\u662f\u5b89\u5c0f\u6167\u5b89\u59d1\u5a18\uff0c\u6211\u4eec\u5c0f\u65f6\u5728\u4e00\u5757\u513f\u73a9\uff0c\u5df2\u6574\u6574\u5341\u5e74\u4e0d\u89c1\u5566\u3002\u201d\u6e29\u9752\u51b7\u51b7\u7684\u7785\u4e86\u5b89\u5c0f\u6167\u4e00\u773c\uff0c\u5e76\u4e0d\u65bd\u793c\uff0c\u4e5f\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5f88\u611f\u5c34\u5c2c\uff0c\u95ee\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u8ba4\u5f97\u6211\uff1f\u201d\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u4f60\u7709\u6bdb\u4e0a\u7684\u4f24\u75a4\uff0c\u6211\u600e\u4f1a\u5fd8\u8bb0\uff1f\u5c0f\u65f6\u5019\u90a3\u4e2a\u574f\u4eba\u6765\u6349\u6211\uff0c\u4f60\u62da\u547d\u76f8\u6551\uff0c\u7ed9\u4eba\u5bb6\u780d\u7684\uff0c\u4f60\u5fd8\u8bb0\u4e86\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u90a3\u4e00\u5929\u6211\u4eec\u8fd8\u7528\u5c0f\u7897\u5c0f\u9505\u716e\u996d\u5403\u5462\u3002\u201d \u3000\u3000\u6e29\u9752\u66f4\u662f\u4e0d\u60a6\uff0c\u60bb\u60bb\u7684\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u4f60\u4eec\u7684\u2026\u2026\u9752\u6885\u7af9\u9a6c\u5427\uff0c\u6211\u53ef\u8981\u8fdb\u53bb\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fd9\u9053\uff1a\u201c\u7b49\u4e00\u4e0b\uff0c\u5c0f\u6167\uff0c\u4f60\u600e\u4e48\u8ddf\u8fd9\u4f4d\u5927\u54e5\u6253\u4e86\u8d77\u6765\uff1f\u201d\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u6211\u548c\u2026\u2026\u548c\u5d14\u5e08\u5144\u2026\u2026\u201d\u8881\u627f\u5fd7\u62a2\u7740\u95ee\uff1a\u201c\u5d14\u5e08\u5144\uff1f\u662f\u5d14\u79cb\u5c71\u53d4\u53d4\u5427\uff1f\u201d\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u4e0d\uff0c\u4ed6\u662f\u5d14\u79cb\u5c71\u53d4\u53d4\u7684\u4f84\u513f\u3002\u6211\u4eec\u62a4\u9001\u95ef\u738b\u4e00\u7b14\u519b\u9977\u5230\u6d59\u4e1c\u6765\uff0c\u54ea\u77e5\u8fd9\u4eba\u771f\u574f\uff0c\u534a\u8def\u4e0a\u6765\u5374\u62a2\u4e86\u53bb\u3002\u201d\u8bf4\u7740\u5411\u6e29\u9752\u4e00\u6307\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e0b\u604d\u7136\uff0c\u539f\u6765\u6e29\u9752\u6240\u52ab\u9ec4\u91d1\u662f\u95ef\u738b\u7684\u519b\u9977\uff0c\u522b\u8bf4\u95ef\u738b\u5bf9\u81ea\u5df1\u793c\u9047\uff0c\u5e08\u7236\u53c8\u6b63\u5168\u529b\u8f85\u4f50\u4e8e\u4ed6\uff0c\u4fbf\u51b2\u7740\u5d14\u79cb\u5c71\u3001\u5b89\u5927\u5a18\u3001\u5b89\u5c0f\u6167\u8fd9\u4e09\u4eba\u7684\u6545\u4eba\u4e4b\u60c5\uff0c\u4e5f\u65e0\u8bba\u5982\u4f55\u8981\u8bbe\u6cd5\u5e2e\u4ed6\u627e\u56de\u6765\u3002\u4f55\u51b5\u95ef\u738b\u5343\u91cc\u8fe2\u8fe2\u7684\u9001\u9ec4\u91d1\u5230\u6c5f\u5357\u6765\uff0c\u5fc5\u5b9a\u6709\u91cd\u5927\u7528\u9014\u3002\u4ed6\u6240\u5174\u7684\u662f\u4ec1\u4e49\u4e4b\u5e08\uff0c\u6551\u6c11\u4e8e\u6c34\u706b\u4e4b\u4e2d\uff0c\u5982\u4f55\u4e0d\u4f38\u624b\u76f8\u52a9\uff1f\u5f53\u4e0b\u5fc3\u610f\u5df2\u51b3\uff0c\u5411\u6e29\u9752\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u77a7\u5728\u6211\u7684\u8138\u4e0a\uff0c\u4f60\u628a\u91d1\u5b50\u8fd8\u4e86\u8fd9\u4f4d\u59d1\u5a18\u5427\uff01\u201d\u6e29\u9752\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5148\u89c1\u8fc7\u6211\u4e24\u4f4d\u7237\u7237\u518d\u8bf4\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u8bf4\u4e24\u4f4d\u8001\u8005\u662f\u4ed6\u7237\u7237\uff0c\u5fc3\u60f3\u65e2\u5df2\u548c\u4ed6\u7ed3\u62dc\uff0c\u4ed6\u4eec\u5c31\u662f\u957f\u8f88\uff0c\u4e8e\u662f\u606d\u606d\u656c\u656c\u7684\u8d70\u4e0a\u524d\u53bb\uff0c\u5411\u7740\u4e24\u4e2a\u8001\u8005\u78d5\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u62ff\u62d0\u6756\u7684\u8001\u8005\u9053\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u6562\u5f53\uff0c\u8881\u4e16\u5144\u8bf7\u8d77\u3002\u201d\u628a\u62d0\u6756\u5f80\u6905\u5b50\u8fb9\u4e0a\u4e00\u501a\uff0c\u53cc\u624b\u6258\u4f4f\u4ed6\u8098\u5e95\uff0c\u5f80\u4e0a\u4e00\u62ac\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7a81\u89c9\u4e00\u80a1\u6781\u5927\u52b2\u529b\u5411\u4e0a\u6258\u8d77\uff0c\u7acb\u65f6\u4fbf\u8981\u7ed9\u4ed6\u629b\u5411\u7a7a\u4e2d\uff0c\u5f53\u4e0b\u53cc\u81c2\u4e00\u6c89\uff0c\u8fd0\u52b2\u7a33\u4f4f\u8eab\u5b50\uff0c\u4ecd\u5411\u4e24\u4eba\u78d5\u8db3\u4e86\u56db\u4e2a\u5934\u624d\u7ad9\u8d77\u8eab\u6765\u3002\u90a3\u8001\u8005\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c11\u5e74\u597d\u6d51\u539a\u7684\u5185\u529b\u3002\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u9752\u513f\u8bf4\uff0c\u8881\u4e16\u5144\u529f\u592b\u4fca\u5f97\u5f88\uff0c\u679c\u7136\u4e0d\u9519\u3002\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u4e09\u7237\u7237\u3002\u201d\u53c8\u6307\u7740\u7a7a\u624b\u7684\u8001\u8005\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u4e94\u7237\u7237\u3002\u201d\u8bf4\u4e86\u4e24\u4eba\u540d\u53f7\uff0c\u4e00\u4e2a\u53eb\u6e29\u65b9\u5c71\uff0c\u4e00\u4e2a\u53eb\u6e29\u65b9\u609f\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e24\u4eba\u60f3\u6765\u4fbf\u662f\u77f3\u6881\u6d3e\u4e94\u7956\u4e2d\u7684\u4e24\u7956\u3002\u90a3\u4e09\u7237\u7237\u7684\u6b66\u529f\u6bd4\u6e29\u6b63\u548c\u9752\u5f1f\u53ef\u9ad8\u5f97\u591a\u4e86\u3002\u201d\u4e8e\u662f\u4e5f\u5404\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u4e09\u7237\u7237\uff01\u4e94\u7237\u7237\uff01\u201d\u4e24\u4e2a\u8001\u8005\u9f50\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u6b64\u79f0\u547c\u3002\u201d\u8138\u4e0a\u795e\u8272\u4f3c\u4e4e\u9887\u4e3a\u4e0d\u6109\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u6709\u6c14\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u7239\u7239\u662f\u6297\u6e05\u540d\u5c06\u3001\u8fbd\u4e1c\u7763\u5e08\u3002 \u3000\u3000\u6211\u548c\u4f60\u4eec\u5b59\u513f\u7ed3\u62dc\uff0c\u4e5f\u4e0d\u81f4\u8fb1\u6ca1\u4e86\u4ed6\u3002\u201d\u8f6c\u5934\u5411\u6e29\u9752\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u7684\u91d1\u5b50\uff0c\u5144\u5f1f\u4fbf\u8fd8\u4e86\u5979\u5427\uff01\u201d \u3000\u3000\u6e29\u9752\u6120\u9053\uff1a\u201c\u4f60\u5c31\u662f\u8fd9\u4f4d\u59d1\u5a18\u3001\u90a3\u4f4d\u59d1\u5a18\u7684\uff0c\u53ef\u4e00\u70b9\u4e0d\u628a\u4eba\u5bb6\u653e\u5728\u5fc3\u4e0a\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u54b1\u4eec\u5b66\u6b66\u7684\u4ee5\u4e49\u6c14\u4e3a\u91cd\uff0c\u8fd9\u6279\u91d1\u5b50\u65e2\u662f\u95ef\u738b\u7684\uff0c\u4f60\u53d6\u7684\u65f6\u5019\u4e0d\u77e5\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u73b0\u4e0b\u65e2\u77e5\u5c31\u91cc\uff0c\u82e5\u4e0d\u4ea4\u8fd8\uff0c\u5c82\u975e\u5bf9\u4e0d\u8d77\u4eba\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u8001\u8005\u672c\u4e0d\u77e5\u8fd9\u6279\u9ec4\u91d1\u6709\u5982\u6b64\u91cd\u5927\u7684\u7275\u8fde\uff0c\u53ea\u9053\u662f\u54ea\u4e00\u4e2a\u5bcc\u5546\u4e4b\u7269\uff0c\u6b64\u65f6\u542c\u5b89\u5c0f\u6167\u3001\u8881\u627f\u5fd7\u4e00\u8bf4\uff0c\u5fc3\u4e0b\u4e5f\u9887\u4e0d\u5b89\u3002 \u3000\u3000\u4ed6\u4eec\u77e5\u9053\u95ef\u738b\u58f0\u52bf\u6d69\u5927\uff0c\u6c5f\u6e56\u8c6a\u6770\u95fb\u98ce\u666f\u4ece\uff0c\u8fd9\u6279\u9ec4\u91d1\u8981\u662f\u4e0d\u8fd8\uff0c\u6765\u7d22\u8ba8\u7684\u597d\u624b\u52bf\u5fc5\u6e90\u6e90\u800c\u81f3\uff0c\u5b9e\u662f\u540e\u60a3\u65e0\u7a77\u3002\u6e29\u65b9\u5c71\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u51b2\u7740\u8881\u4e16\u5144\u7684\u9762\u5b50\uff0c\u54b1\u4eec\u5c31\u8fd8\u4e86\u5427\u3002\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u4e09\u7237\u7237\uff0c\u90a3\u4e0d\u6210\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u672c\u6765\u5206\u7ed9\u6211\u4e00\u534a\uff0c\u90a3\u4e48\u6211\u8fd9\u4e00\u534a\u5148\u4ea4\u8fd8\u5979\u518d\u8bf4\u3002\u201d\u6e29\u9752\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u8981\uff0c\u8fde\u6211\u7684\u901a\u7edf\u7ed9\u4f60\u3002\u8c01\u53c8\u8fd8\u8fd9\u6837\u5c0f\u5bb6\u6c14\uff0c\u51e0\u5343\u4e24\u91d1\u5b50\u5c31\u5f53\u5b9d\u8d1d\u4e86\uff1f \u3000\u3000\u4e0d\u8fc7\u662f\u8fd9\u4f4d\u59d1\u5a18\u3001\u90a3\u4f4d\u59d1\u5a18\u6765\u8981\uff0c\u6211\u5c31\u504f\u504f\u4e0d\u7ed9\u3002\u201d \u3000\u3000\u5b89\u5c0f\u6167\u8d70\u4e0a\u4e00\u6b65\uff0c\u6012\u9053\uff1a\u201c\u4f60\u8981\u600e\u6837\u624d\u80af\u8fd8\uff1f\u5212\u4e0b\u9053\u513f\u6765\u5427\uff1f\u201d\u6e29\u9752\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u5e2e\u5979\uff0c\u8fd8\u662f\u5e2e\u6211\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u8e0c\u8e87\u534a\u523b\uff0c\u9053\uff1a\u201c\u6211\u8c01\u4e5f\u4e0d\u5e2e\uff0c\u6211\u53ea\u542c\u5e08\u7236\u7684\u8bdd\u3002\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u5e08\u7236\uff1f\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5e08\u7236\u662f\u95ef\u738b\u519b\u4e2d\u7684\u3002\u201d\u6e29\u9752\u6012\u9053\uff1a\u201c\u54fc\uff0c\u8bf4\u6765\u8bf4\u53bb\uff0c\u4f60\u8fd8\u662f\u5e2e\u5979\u3002\u597d\uff0c\u91d1\u5b50\u662f\u5728\u8fd9\u91cc\uff0c\u6211\u8d39\u5fc3\u673a\u76d7\u6765\uff0c\u4f60\u4e5f\u5f97\u8d39\u5fc3\u673a\u76d7\u53bb\u3002\u4e09\u5929\u4e4b\u5185\uff0c\u4f60\u6709\u672c\u4e8b\u5c31\u6765\u53d6\u53bb\uff0c\u8fc7\u5f97\u4e09\u5929\u62ff\u4e0d\u53bb\uff0c\u6211\u53ef\u4e0d\u5ba2\u6c14\u4e86\uff0c\u5e0c\u91cc\u54d7\u62c9\uff0c\u4e00\u5929\u5c31\u82b1\u4e2a\u5e72\u51c0\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u9ec4\u91d1\uff0c\u4f60\u4e00\u5929\u600e\u82b1\u5f97\u5b8c\uff1f\u201d\u6e29\u9752\u6120\u9053\uff1a\u201c\u82b1\u4e0d\u5b8c\uff0c\u4e0d\u4f1a\u629b\u5728\u5927\u8def\u4e0a\uff0c\u8ba9\u65c1\u4eba\u62e3\u53bb\u5e2e\u7740\u82b1\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u62c9\u62c9\u4ed6\u8863\u8896\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u8ddf\u6211\u6765\u3002\u201d\u4e24\u4eba\u8d70\u5230\u5385\u89d2\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6628\u665a\u4f60\u8bf4\u542c\u6211\u8bdd\u7684\uff0c\u600e\u4e48\u9694\u4e0d\u4e86\u534a\u5929\u5c31\u53d8\u4e86\u5366\uff1f\u201d\u6e29\u9752\u9053\uff1a\u201c\u4f60\u5f85\u6211\u597d\uff0c\u6211\u81ea\u7136\u542c\u4f60\u8bdd\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u600e\u4e48\u4e0d\u5f85\u4f60\u597d\uff1f\u8fd9\u6279\u91d1\u5b50\u771f\u7684\u62ff\u4e0d\u5f97\u554a\u3002\u201d\u6e29\u9752\u773c\u5708\u4e00\u7ea2\u9053\uff1a\u201c\u4f60\u89c1\u4e86\u4ece\u524d\u7684\u76f8\u597d\uff0c\u5168\u5fc3\u5168\u610f\u5c31\u56de\u62a4\u7740\u5979\uff0c\u54ea\u91cc\u8fd8\u628a\u4eba\u5bb6\u653e\u5728\u5fc3\u4e0a\uff1f\u95ef\u738b\u7684\u91d1\u5b50\u6211\u82b1\u4e86\u600e\u6837\uff1f\u5927\u4e0d\u4e86\u7ed9\u4ed6\u6740\u4e86\uff0c\u53cd\u6b63\u6211\u4e00\u751f\u4e00\u4e16\u6ca1\u4eba\u75bc\u3002\u201d\u8bf4\u7740\u53c8\u8981\u6389\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u4e0d\u53ef\u7406\u55bb\uff0c\u5f88\u4e0d\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u6211\u7ed3\u4e49\u5144\u5f1f\uff0c\u5979\u662f\u6211\u6545\u4eba\u4e4b\u5973\uff0c\u6211\u662f\u4e00\u89c6\u540c\u4ec1\uff0c\u4e0d\u5206\u539a\u8584\u3002\u4f60\u600e\u4e48\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u6e29\u9752\u55d4\u9053\uff1a\u201c\u6211\u5c31\u662f\u6068\u4f60\u4e00\u89c6\u540c\u4ec1\uff0c\u4e0d\u5206\u539a\u8584\u3002\u54fc\uff0c\u4e0d\u5fc5\u591a\u8bf4\uff0c\u4f60\u4e09\u5929\u5185\u6765\u76d7\u5427\uff01\u201d\u8881\u627f\u5fd7\u62c9\u4f4f\u4ed6\u7684\u624b\u6b32\u5f85\u518d\u529d\uff0c\u6e29\u9752\u624b\u4e00\u7529\uff0c\u8d70\u8fdb\u5185\u5802\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u8bdd\u5df2\u8bf4\u50f5\uff0c\u53ea\u5f97\u4e0e\u5b89\u5c0f\u6167\u4e24\u4eba\u544a\u8f9e\u51fa\u53bb\uff0c\u627e\u5230\u4e00\u5bb6\u519c\u820d\u501f\u5bbf\uff0c\u95ee\u8d77\u5931\u91d1\u7ecf\u8fc7\u3002\u539f\u6765\u5b89\u5c0f\u6167\u7b49\u62a4\u9001\u91d1\u5b50\u7684\u5171\u6709\u4e09\u4eba\uff0c\u4e2d\u9014\u56e0\u4e8b\u5206\u624b\uff0c\u81f4\u4e3a\u6e29\u9752\u6240\u4e58\u3002 \u3000\u3000\u5b89\u5c0f\u6167\u8bf4\u8d77\u522b\u6765\u60c5\u7531\uff0c\u8bf4\u5979\u6bcd\u4eb2\u8eab\u5b50\u5b89\u5065\uff0c\u4e5f\u5e38\u7275\u8bb0\u7740\u4ed6\u3002\u8881\u627f\u5fd7\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u53ea\u5c0f\u91d1\u4e1d\u956f\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u5988\u4ece\u524d\u7ed9\u6211\u7684\u3002\u4f60\u77a7\uff0c\u6211\u90a3\u65f6\u7684\u624b\u8155\u53ea\u6709\u8fd9\u4e48\u7c97\u3002\u201d\u5b89\u5c0f\u6167\u55e4\u7684\u4e00\u7b11\uff0c\u77a7\u7740\u4ed6\u624b\u81c2\uff0c\u95ee\u9053\uff1a\u201c\u627f\u5fd7\u5927\u54e5\uff0c\u4f60\u8fd9\u4e9b\u5e74\u6765\u5728\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5929\u5929\u5728\u7ec3\u6b66\uff0c\u751a\u4e48\u4e8b\u4e5f\u6ca1\u505a\u3002\u201d\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u6b66\u529f\u8fd9\u4e48\u5f3a\uff0c\u521a\u624d\u4f60\u53ea\u628a\u6211\u7684\u5251\u8f7b\u8f7b\u4e00\u63a8\uff0c\u6211\u5c31\u4e00\u70b9\u52b2\u4e5f\u4f7f\u4e0d\u4e0a\u6765\u5566\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e5f\u4f1a\u534e\u5c71\u6d3e\u5251\u6cd5\uff1f\u8c01\u6559\u4f60\u7684\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u6167\u773c\u5708\u4e00\u7ea2\uff0c\u628a\u5934\u8f6c\u4e86\u8fc7\u53bb\uff0c\u8fc7\u4e86\u4e00\u4f1a\u624d\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e2a\u5d14\u5e08\u54e5\u6559\u7684\uff0c\u4ed6\u4e5f\u662f\u534e\u5c71\u6d3e\u7684\u3002\u201d\u8881\u627f\u5fd7\u5fd9\u95ee\uff1a\u201c\u4ed6\u53d7\u4e86\u4f24\u8fd8\u662f\u600e\u7684\uff1f\u4f60\u4e3a\u751a\u4e48\u96be\u8fc7\uff1f\u201d\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u4ed6\u53d7\u751a\u4e48\u4f24\u554a\uff1f\u4ed6\u4e0d\u7406\u4eba\u5bb6\uff0c\u534a\u8def\u4e0a\u5148\u8d70\u4e86\u3002\u201d\u8881\u627f\u5fd7\u89c1\u5176\u4e2d\u4f3c\u4e4e\u7275\u6d89\u513f\u5973\u79c1\u60c5\uff0c\u4e0d\u4fbf\u518d\u95ee\u3002 \u3000\u3000\u7b49\u5230\u4e8c\u66f4\u65f6\u5206\uff0c\u4e24\u4eba\u5f80\u6e29\u5bb6\u5954\u53bb\u3002\u8881\u627f\u5fd7\u8f7b\u8f7b\u8dc3\u4e0a\u5c4b\u9876\uff0c\u53ea\u89c1\u5927\u5385\u4e2d\u70db\u5149\u70b9\u5f97\u660e\u6643\u6643\u5730\uff0c\u6e29\u65b9\u5c71\u3001\u65b9\u609f\u4e24\u5144\u5f1f\u5750\u5728\u684c\u8fb9\u559d\u9152\u3002\u6e29\u6b63\u3001\u6e29\u9752\u7ad9\u5728\u4e00\u65c1\u4f3a\u5019\u3002\u8881\u627f\u5fd7\u4e0d\u77e5\u9ec4\u91d1\u85cf\u5728\u4f55\u5904\uff0c\u60f3\u5077\u542c\u4ed6\u4eec\u8bf4\u8bdd\uff0c\u4ee5\u4fbf\u5f97\u5230\u4e9b\u7ebf\u7d22\u3002\u53ea\u542c\u6e29\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u5411\u7740\u5c4b\u9876\u9053\uff1a\u201c\u91d1\u5b50\u5c31\u5728\u8fd9\u91cc\uff01\u6709\u672c\u9886\u6765\u62ff\u597d\u4e86\u3002\u201d \u3000\u3000\u5b89\u5c0f\u6167\u4e00\u62c9\u8881\u627f\u5fd7\u7684\u8863\u88fe\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u4ed6\u5df2\u77e5\u9053\u54b1\u4eec\u5230\u4e86\u3002\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u53ea\u89c1\u6e29\u9752\u4ece\u684c\u5e95\u4e0b\u53d6\u51fa\u4e24\u4e2a\u5305\u88f9\uff0c\u5728\u684c\u4e0a\u644a\u4e86\u5f00\u6765\uff0c\u70db\u5149\u4e0b\u8000\u773c\u751f\u8f89\uff0c\u9ec4\u6f84\u6f84\u7684\u5168\u662f\u4e00\u6761\u6761\u7684\u91d1\u5b50\u3002 \u3000\u3000\u6e29\u9752\u548c\u6e29\u6b63\u4e5f\u5750\u4e86\u4e0b\u6765\uff0c\u628a\u5200\u5251\u5f80\u684c\u4e0a\u4e00\u653e\uff0c\u559d\u8d77\u9152\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u5c31\u8fd9\u822c\u5b88\u7740\uff0c\u9664\u975e\u662f\u786c\u593a\uff0c\u5426\u5219\u600e\u80fd\u76d7\u53d6\uff1f\u201d\u7b49\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e0b\u9762\u56db\u4eba\u6beb\u65e0\u8d70\u52a8\u4e4b\u610f\uff0c\u77e5\u9053\u4eca\u665a\u5df2\u65e0\u6cd5\u52a8\u624b\uff0c\u548c\u5b89\u5c0f\u6167\u56de\u5230\u4f4f\u5bbf\u4e4b\u5904\u3002 \u3000\u3000\u6b21\u65e5\u508d\u665a\uff0c\u4e24\u4eba\u53c8\u53bb\u6e29\u5b85\uff0c\u89c1\u5927\u5385\u4e2d\u4ecd\u662f\u56db\u4eba\u770b\u5b88\uff0c\u53ea\u662f\u6362\u4e86\u4e24\u4e2a\u8001\u4eba\uff0c\u770b\u6765\u4e5f\u662f\u4e94\u5144\u5f1f\u4e2d\u7684\uff0c\u5176\u4f59\u4e09\u4eba\u591a\u534a\u662f\u5728\u6697\u4e2d\u57cb\u4f0f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5bf9\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u4ed6\u4eec\u6709\u9ad8\u624b\u5b88\u5728\u9690\u853d\u7684\u5730\u65b9\uff0c\u53ef\u8981\u5c0f\u5fc3\u3002\u201d\u5b89\u5c0f\u6167\u70b9\u70b9\u5934\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u5ffd\u7136\u7eb5\u8eab\u4e0b\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6015\u5979\u843d\u5355\uff0c\u8fde\u5fd9\u8ddf\u4e0b\u3002\u53ea\u89c1\u5979\u4e00\u8def\u8d70\u5230\u5c4b\u540e\uff0c\u6478\u5230\u53a8\u623f\u8fb9\uff0c\u706b\u6298\u4e00\u6643\uff0c\u628a\u5c4b\u65c1\u4e00\u5806\u67f4\u8349\u70b9\u71c3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u706b\u5149\u51b2\u5929\u800c\u8d77\u3002\u6e29\u5b85\u4e2d\u767b\u65f6\u4eba\u58f0\u55a7\u54d7\uff0c\u8bb8\u591a\u5e84\u4e01\u63d0\u6c34\u6301\u7aff\uff0c\u5954\u6765\u6251\u6551\u3002 \u3000\u3000\u4e24\u4eba\u62a2\u5230\u524d\u5385\uff0c\u5385\u4e2d\u70db\u5149\u4ecd\u660e\uff0c\u5750\u7740\u7684\u56db\u4eba\u5374\u5df2\u4e0d\u89c1\u3002\u5b89\u5c0f\u6167\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u4ed6\u4eec\u6551\u706b\u53bb\u5566\uff01\u201d\u7eb5\u8eab\u7ffb\u4e0b\u5c4b\u9876\uff0c\u4ece\u7a97\u4e2d\u7a7f\u8fdb\u5385\u5185\u3002\u8881\u627f\u5fd7\u8ddf\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u62a2\u5230\u684c\u65c1\uff0c\u6b63\u8981\u4f38\u624b\u53bb\u62ff\u9ec4\u91d1\uff0c\u5ffd\u7136\u8db3\u4e0b\u4e00\u8f6f\u3002\u8881\u627f\u5fd7\u6697\u53eb\u4e0d\u5999\uff0c\u9661\u7136\u62d4\u8d77\u8eab\u5b50\uff0c\u53f3\u624b\u4e00\u633d\u60f3\u62c9\u5b89\u5c0f\u6167\uff0c\u5374\u6ca1\u62c9\u7740\uff0c\u539f\u6765\u811a\u5e95\u7adf\u662f\u4e2a\u7ffb\u677f\u673a\u5173\u3002\u4ed6\u8eab\u5b50\u817e\u8d77\uff0c\u5de6\u638c\u642d\u4e0a\u5385\u4e2d\u77f3\u67f1\uff0c\u968f\u5373\u6e9c\u4e0b\uff0c\u53f3\u8db3\u8e0f\u5728\u67f1\u7840\u4e4b\u4e0a\u3002\u8fd9\u65f6\u7ffb\u677f\u5df2\u7ecf\u5408\u62e2\uff0c\u628a\u5b89\u5c0f\u6167\u5173\u5728\u5e95\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u60ca\uff0c\u6251\u51fa\u7a97\u5916\u67e5\u770b\u673a\u5173\uff0c\u8981\u8bbe\u6cd5\u642d\u6551\u3002\u521a\u51fa\u7a97\u5b50\uff0c\u4e00\u80a1\u52b2\u98ce\u8fce\u98ce\u6251\u5230\uff0c\u5f53\u5373\u53f3\u638c\u6325\u51fa\uff0c\u548c\u51fb\u6765\u7684\u4e00\u638c\u76f8\u62b5\uff0c\u4e24\u4eba\u4e00\u7528\u529b\uff0c\u8881\u627f\u5fd7\u501f\u52bf\u8dc3\u4e0a\u5c4b\u9876\uff0c\u5077\u88ad\u4e4b\u4eba\u5374\u8dcc\u4e0b\u5730\u53bb\u3002\u4f46\u6b64\u4eba\u8eab\u624b\u5feb\u6377\uff0c\u7740\u5730\u540e\u4fbf\u5373\u8dc3\u4e0a\u5c4b\u9876\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7acb\u5b9a\u8eab\u8eaf\uff0c\u56db\u4e0b\u4e00\u671b\uff0c\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u53ea\u89c1\u9ad8\u9ad8\u77ee\u77ee\u3001\u80a5\u80a5\u7626\u7626\uff0c\u5c4b\u9876\u4e0a\u7adf\u7136\u7ad9\u6ee1\u4e86\u4eba\u3002\u88ab\u4ed6\u638c\u529b\u9707\u4e0b\u53c8\u8dc3\u4e0a\u6765\u7684\u6b63\u662f\u6e29\u6b63\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8eab\u5165\u91cd\u56f4\uff0c\u4e0d\u77e5\u5bf9\u65b9\u5fc3\u610f\u5982\u4f55\uff0c\u5f53\u4e0b\u51dd\u795e\u5c4f\u6c14\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u7fa4\u4e2d\u8d70\u51fa\u4e94\u4e2a\u8001\u4eba\u6765\uff0c\u5176\u4e2d\u6e29\u65b9\u5c71\u548c\u6e29\u65b9\u609f\u662f\u62dc\u89c1\u8fc7\u7684\uff0c\u53e6\u5916\u4e24\u4e2a\u8001\u4eba\u521a\u624d\u66fe\u5750\u5728\u5385\u4e2d\u770b\u5b88\u9ec4\u91d1\uff0c\u4f59\u4e0b\u4e00\u4eba\u8eab\u6750\u9b41\u68a7\uff0c\u6bd4\u4f17\u4eba\u90fd\u9ad8\u51fa\u534a\u4e2a\u5934\u3002\u90a3\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u58f0\u82e5\u6d2a\u949f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5144\u5f1f\u4e94\u4eba\u50fb\u5904\u4e61\u95f4\uff0c\u5c45\u7136\u6709\u95ef\u738b\u624b\u4e0b\u9ad8\u4eba\u60e0\u7136\u5149\u964d\uff0c\u771f\u662f\u4e09\u751f\u6709\u5e78\u3001\u84ec\u835c\u751f\u8f89\u4e86\u3002\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e0a\u524d\u6253\u4e86\u4e00\u8eac\uff0c\u9053\uff1a\u201c\u665a\u8f88\u62dc\u89c1\u3002\u201d\u4ed6\u56e0\u56db\u5468\u90fd\u662f\u654c\u4eba\uff0c\u53ea\u6015\u78d5\u4e0b\u5934\u53bb\u53d7\u4eba\u6697\u7b97\uff0c\u4f46\u793c\u6570\u4ecd\u662f\u4e0d\u7f3a\u3002 \u3000\u3000\u6e29\u9752\u7ad9\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u5927\u7237\u7237\uff0c\u90a3\u4e24\u4f4d\u662f\u6211\u4e8c\u7237\u7237\u3001\u56db\u7237\u7237\u3002\u201d\u8881\u627f\u5fd7\u4e00\u4e00\u884c\u793c\u3002 \u3000\u3000\u77f3\u6881\u6d3e\u4e94\u7956\u4e2d\u7684\u5927\u54e5\u6e29\u65b9\u8fbe\u3001\u4e8c\u54e5\u6e29\u65b9\u4e49\u3001\u8001\u56db\u6e29\u65b9\u65bd\u70b9\u70b9\u5934\uff0c\u5374\u4e0d\u8fd8\u793c\uff0c\u4e0d\u4f4f\u5411\u4ed6\u6253\u91cf\u3002\u6e29\u65b9\u4e49\u6012\u58f0\u559d\u9053\uff1a\u201c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u80c6\u5b50\u5012\u4e5f\u4e0d\u5c0f\uff0c\u5c45\u7136\u6562\u5728\u6211\u5bb6\u653e\u706b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u662f\u665a\u8f88\u4e00\u4e2a\u540c\u4f34\u7684\u9c81\u83bd\uff0c\u665a\u8f88\u5341\u5206\u8fc7\u610f\u4e0d\u53bb\uff0c\u5e78\u559c\u5e76\u672a\u6210\u707e\u3002\u665a\u8f88\u660e\u65e5\u518d\u6765\u5411\u5404\u4f4d\u78d5\u5934\u966a\u7f6a\u3002\u201d\u8fd9\u65f6\u67f4\u5806\u7684\u706b\u5df2\u88ab\u6251\u706d\uff0c\u5e76\u672a\u71c3\u70e7\u5f00\u6765\u3002 \u3000\u3000\u6e29\u6b63\u7684\u7956\u7236\u6e29\u65b9\u65bd\u8eab\u5f62\u9ad8\u7626\uff0c\u5bb9\u8c8c\u4e5f\u548c\u6e29\u6b63\u9887\u4e3a\u76f8\u4f3c\uff0c\u53d1\u8bdd\u9053\uff1a\u201c\u78d5\u5934\uff1f\u78d5\u51e0\u4e2a\u5934\u5c31\u80fd\u7b97\u4e86\uff1f\u5c0f\u5a03\u5a03\u80c6\u5927\u5984\u4e3a\uff0c\u7adf\u5230\u77f3\u6881\u6e29\u5bb6\u6765\u6492\u91ce\u3002\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u201d\u6e29\u6c0f\u4e94\u8001\u867d\u5bf9\u95ef\u738b\u7684\u58f0\u52bf\u9887\u4e3a\u5fcc\u60ee\uff0c\u4f46\u4e94\u5144\u5f1f\u7d20\u6765\u7231\u8d22\uff0c\u5230\u624b\u4e86\u7684\u9ec4\u91d1\u5374\u4e5f\u4e0d\u80af\u5c31\u6b64\u8f7b\u6613\u5410\u4e86\u51fa\u53bb\uff1b\u9002\u624d\u89c1\u8881\u627f\u5fd7\u4e00\u638c\u9707\u843d\u6e29\u6b63\uff0c\u6b66\u529f\u59d4\u5b9e\u4e86\u5f97\uff0c\u8981\u5148\u67e5\u660e\u4ed6\u7684\u5e08\u627f\u95e8\u6d3e\uff0c\u518d\u5b9a\u5bf9\u7b56\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5bb6\u5e08\u773c\u4e0b\u5728\u95ef\u738b\u519b\u4e2d\uff0c\u53ea\u6c42\u5404\u4f4d\u5c06\u95ef\u738b\u7684\u91d1\u5b50\u53d1\u8fd8\uff0c\u665a\u8f88\u6539\u65e5\u6c42\u5bb6\u5e08\u5199\u4fe1\u524d\u6765\u9053\u8c22\u3002\u201d\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u7d20\u6765\u5c11\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u665a\u8f88\u4e0d\u6562\u63d0\u4ed6\u540d\u5b57\u3002\u201d\u6e29\u65b9\u8fbe\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u96be\u9053\u5c31\u7792\u5f97\u8fc7\u6211\u4eec\uff1f\u5357\u626c\uff0c\u8ddf\u8fd9\u5c0f\u5b50\u8fc7\u8fc7\u62db\u3002\u201d\u5fc3\u60f3\u53ea\u6d88\u4e00\u52a8\u4e0a\u624b\uff0c\u975e\u53eb\u4f60\u7acb\u73b0\u539f\u5f62\u4e0d\u53ef\u3002 \u3000\u3000\u4eba\u7fa4\u4e2d\u4e00\u4eba\u5e94\u58f0\u800c\u51fa\u3002\u8fd9\u4eba\u56db\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u816e\u4e0a\u4e00\u4e1b\u866c\u9aef\uff0c\u662f\u6e29\u65b9\u4e49\u7684\u7b2c\u4e8c\u4e2a\u513f\u5b50\uff0c\u5728\u77f3\u6881\u6d3e\u7b2c\u4e8c\u8f88\u4e2d\u53ef\u8bf4\u662f\u4e00\u6d41\u597d\u624b\u3002\u4ed6\u7eb5\u8eab\u4e0a\u6765\uff0c\u5288\u9762\u4fbf\u662f\u4e00\u62f3\u3002\u8881\u627f\u5fd7\u4fa7\u5934\u8ba9\u8fc7\uff0c\u6e29\u5357\u626c\u5de6\u624b\u4e00\u62f3\u8ddf\u7740\u6253\u5230\uff0c\u62f3\u52b2\u9887\u4e3a\u51cc\u5389\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e0b\u76d8\u7b97\uff1a\u201c\u8fd9\u8bb8\u591a\u4eba\u805a\u5728\u8fd9\u91cc\uff0c\u4e00\u4e2a\u4e2a\u6253\u4e0b\u53bb\uff0c\u52bf\u5fc5\u7ed9\u4ed6\u4eec\u7d2f\u6b7b\u3002\u5982\u4e0d\u901f\u6218\uff0c\u53ea\u6015\u96be\u4ee5\u8131\u8eab\u3002\u201d\u7b49\u4ed6\u5de6\u62f3\u6253\u5230\uff0c\u53f3\u638c\u7a81\u7136\u98de\u51fa\uff0c\u5728\u4ed6\u5de6\u62f3\u4e0a\u4e00\u6321\uff0c\u4e94\u6307\u6293\u62e2\uff0c\u5df2\u62ff\u4f4f\u4ed6\u62f3\u5934\uff0c\u987a\u52bf\u540e\u626f\u3002\u6e29\u5357\u626c\u6536\u52bf\u4e0d\u4f4f\uff0c\u8e09\u8e09\u8dc4\u8dc4\u7684\u5411\u524d\u8dcc\u53bb\uff0c\u811a\u4e0b\u8e0f\u788e\u4e86\u4e00\u5927\u7247\u74e6\u7247\uff0c\u5982\u4e0d\u662f\u4ed6\u4e94\u53d4\u6e29\u65b9\u609f\u4f38\u624b\u62c9\u4f4f\uff0c\u5df2\u8dcc\u4e0b\u623f\u53bb\uff0c\u767b\u65f6\u7f9e\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u56de\u8eab\u6251\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7ad9\u7740\u4e0d\u52a8\uff0c\u5f85\u4ed6\u6251\u5230\uff0c\u8f6c\u8eab\u540e\u4ef0\uff0c\u5de6\u811a\u8f7b\u8f7b\u4e00\u52fe\uff0c\u6e29\u5357\u626c\u53c8\u5411\u524d\u4fef\u8dcc\u4e0b\u53bb\u3002\u8881\u627f\u5fd7\u5de6\u8db3\u65b9\u52fe\uff0c\u53f3\u638c\u540c\u65f6\u4f38\u51fa\uff0c\u6599\u5230\u4ed6\u8981\u5411\u524d\u4fef\u8dcc\uff0c\u5df2\u4e00\u628a\u6293\u4f4f\u4ed6\u7684\u540e\u5fc3\u3002\u6e29\u5357\u626c\u8eab\u5b50\u521a\u8981\u649e\u5230\u74e6\u9762\uff0c\u9aa4\u7136\u88ab\u4eba\u63d0\u8d77\uff0c\u54ea\u91cc\u8fd8\u6562\u4ea4\u624b\uff0c\u72e0\u72e0\u671b\u4e86\u8881\u627f\u5fd7\u4e00\u773c\uff0c\u9000\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6e29\u65b9\u4e49\u559d\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u679c\u7136\u8fd8\u6709\u4e24\u4e0b\u5b50\uff0c\u8001\u592b\u6765\u4f1a\u4f1a\u9ad8\u4eba\u7684\u5f1f\u5b50\u3002\u201d\u53cc\u638c\u4e00\u9519\uff0c\u5c31\u8981\u4e0a\u524d\u3002\u6e29\u9752\u7a81\u7136\u7eb5\u5230\u4ed6\u8eab\u65c1\uff0c\u4fef\u8033\u8bf4\u9053\uff1a\u201c\u4e8c\u7237\u7237\uff0c\u4ed6\u548c\u6211\u7ed3\u62dc\u4e86\uff0c\u4f60\u8001\u4eba\u5bb6\u53ef\u522b\u4f24\u4ed6\u3002\u201d\u6e29\u65b9\u4e49\u9a82\u4e86\u4e00\u58f0\uff1a\u201c\u5c0f\u9b3c\u5934\u513f\uff01\u201d\u6e29\u9752\u62c9\u4f4f\u4ed6\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u7237\u7237\u4f60\u7b54\u5e94\u4e86\uff1f\u201d\u6e29\u65b9\u4e49\u9053\uff1a\u201c\u8d70\u7740\u77a7\uff01\u201d\u624b\u4e00\u7529\uff0c\u6e29\u9752\u7acb\u8db3\u4e0d\u7a33\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u9000\u51fa\u6570\u6b65\u3002 \u3000\u3000\u6e29\u65b9\u4e49\u7a33\u7a33\u5b9e\u5b9e\u7684\u8e0f\u4e0a\u4e24\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53d1\u62db\uff01\u201d\u8881\u627f\u5fd7\u62f1\u624b\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u3002\u201d\u6e29\u65b9\u4e49\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u8bf4\u5e08\u7236\u540d\u5b57\uff0c\u4f60\u53d1\u4e09\u62db\uff0c\u77a7\u6211\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u8881\u627f\u5fd7\u89c1\u4ed6\u4e00\u526f\u8001\u6c14\u6a2a\u79cb\u7684\u6a21\u6837\uff0c\u5fc3\u4e2d\u4e5f\u9053\uff1a\u201c\u4f60\u8d70\u7740\u77a7\u3002\u201d\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u665a\u8f88\u653e\u8086\u4e86\uff0c\u665a\u8f88\u529f\u592b\u6709\u9650\uff0c\u5c1a\u8bf7\u624b\u4e0b\u7559\u60c5\u3002\u201d\u6e29\u65b9\u4e49\u559d\u9053\uff1a\u201c\u5feb\u52a8\u624b\uff0c\u8c01\u8ddf\u4f60\u5570\u91cc\u5570\u5506\uff1f\u6e29\u8001\u4e8c\u624b\u4e0b\u662f\u5411\u6765\u4e0d\u7559\u60c5\u7684\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u6df1\u6df1\u4e00\u63d6\uff0c\u8863\u8896\u521a\u62b5\u74e6\u9762\uff0c\u624b\u4e00\u6296\uff0c\u8896\u5b50\u7a81\u7136\u4ece\u6a2a\u91cc\u7529\u8d77\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5411\u6e29\u65b9\u4e49\u5934\u4e0a\u51fb\u53bb\uff0c\u52b2\u9053\u7740\u5b9e\u51cc\u5389\u3002\u6e29\u65b9\u4e49\u4f4e\u5934\u907f\u8fc7\uff0c\u4f38\u624b\u6765\u6293\u8896\u5b50\uff0c\u5374\u89c1\u4ed6\u8f7b\u98d8\u98d8\u7684\u7eb5\u8d77\uff0c\u5de6\u8896\u515c\u4e86\u4e2a\u5708\u5b50\uff0c\u53f3\u8896\u84e6\u5730\u4ece\u5de6\u8896\u5708\u4e2d\u76f4\u51b2\u51fa\u6765\uff0c\u5f84\u6251\u9762\u95e8\uff0c\u6765\u52bf\u5947\u6025\u3002\u6e29\u65b9\u4e49\u907f\u8ba9\u4e0d\u53ca\uff0c\u5f53\u5373\u8eab\u5b50\u4ef0\u540e\uff0c\u8eb2\u5f00\u4e86\u8fd9\u62db\u3002\u8881\u627f\u5fd7\u4e0d\u8ba9\u4ed6\u6709\u4f59\u88d5\u8fd8\u624b\uff0c\u5ffd\u7136\u56de\u8eab\uff0c\u80cc\u5411\u5bf9\u65b9\u3002 \u3000\u3000\u6e29\u65b9\u4e49\u4e00\u5446\uff0c\u53ea\u9053\u4ed6\u8981\u9003\u8dd1\uff0c\u53f3\u638c\u521a\u8981\u53d1\u51fa\uff0c\u5ffd\u89c9\u4e00\u9635\u52b2\u98ce\u88ad\u5230\uff0c\u4f46\u89c1\u4ed6\u53cc\u8896\u53cd\u624b\u4ece\u4e0b\u5411\u4e0a\uff0c\u72b9\u5982\u4e24\u6761\u957f\u86c7\u822c\u5411\u81ea\u5df1\u814b\u4e0b\u94bb\u6765\uff0c\u8fd9\u4e00\u62db\u66f4\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u5fd9\u4f38\u53cc\u624b\u60f3\u6293\uff0c\u54ea\u77e5\u8896\u5b50\u5df2\u62c2\u5230\u4ed6\u8170\u4e0a\uff0c\u5566\u5566\u4e24\u58f0\uff0c\u7adf\u5c14\u6253\u4e2d\uff0c\u53ea\u611f\u5230\u4e00\u9635\u53d1\u9ebb\uff0c\u5bf9\u624b\u5df2\u501f\u52bf\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u56de\u8fc7\u8eab\u6765\uff0c\u7b11\u541f\u541f\u7684\u7ad9\u4f4f\u3002\u6e29\u9752\u89c1\u4ed6\u8eab\u624b\u5982\u6b64\u5de7\u5999\uff0c\u4e00\u4e2a\u201c\u597d\u201d\u5b57\u9669\u4e9b\u8131\u53e3\u800c\u51fa\uff0c\u6025\u5fd9\u4f38\u624b\u6309\u4f4f\u4e86\u5634\uff0c\u8ddf\u7740\u4f38\u4e86\u4f38\u820c\u5934\u3002 \u3000\u3000\u6e29\u65b9\u4e49\u53c8\u7f9e\u53c8\u607c\uff0c\u9976\u662f\u4ed6\u89c1\u591a\u8bc6\u5e7f\uff0c\u5374\u77a7\u4e0d\u51fa\u8fd9\u4e09\u62db\u8896\u5b50\u529f\u592b\u51fa\u4e8e\u4f55\u95e8\u4f55\u6d3e\u3002\u4ed6\u53c8\u600e\u77e5\u8881\u627f\u5fd7\u7b2c\u4e00\u62db\u4f7f\u7684\u662f\u534e\u5c71\u6d3e\u5ae1\u7cfb\u6b66\u529f\u4f0f\u864e\u638c\u6cd5\uff0c\u7b2c\u4e8c\u62db\u662f\u4ece\u6728\u6851\u9053\u4eba\u7684\u8f7b\u529f\u4e2d\u53d8\u5316\u51fa\u6765\uff0c\u7b2c\u4e09\u62db\u201c\u53cc\u86c7\u94bb\u814b\u201d\u5374\u5f97\u81ea\u91d1\u86c7\u90ce\u541b\u7684\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u3002\u8881\u627f\u5fd7\u6015\u5bf9\u65b9\u8bc6\u5f97\uff0c\u6bcf\u4e00\u62db\u5747\u7565\u52a0\u53d8\u5316\uff0c\u517c\u4e4b\u624b\u638c\u85cf\u5728\u8896\u5b50\u4e4b\u4e2d\uff0c\u6e29\u65b9\u4e49\u5982\u4f55\u80fd\u8bc6\uff1f \u3000\u3000\u6e29\u65b9\u8fbe\u7b49\u56db\u5144\u5f1f\u9762\u9762\u76f8\u89d1\uff0c\u90fd\u89c9\u5927\u5947\u3002 \u3000\u3000\u6e29\u65b9\u4e49\u8001\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u987b\u7709\u4ff1\u5f20\uff0c\u7a81\u7136\u53d1\u638c\u51fb\u51fa\u3002\u6708\u5149\u4e0b\u8881\u627f\u5fd7\u89c1\u4ed6\u5934\u4e0a\u5192\u51fa\u817e\u817e\u70ed\u6c14\uff0c\u811a\u6b65\u4f3c\u4e4e\u8fdf\u949d\u8e52\u8dda\uff0c\u5176\u5b9e\u7a33\u5b9e\u5f02\u5e38\uff0c\u5f53\u4e0b\u4e0d\u6562\u518d\u884c\u620f\u5f04\uff0c\u4e00\u77ee\u8eab\uff0c\u907f\u5f00\u4e24\u62db\uff0c\u5377\u8d77\u8863\u8896\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u51dd\u795e\u63a5\u6218\uff0c\u4ed6\u751f\u6015\u7ed9\u5bf9\u65b9\u53eb\u7834\u81ea\u5df1\u95e8\u6d3e\uff0c\u4f7f\u7684\u662f\u6c5f\u6e56\u4e0a\u6700\u5bfb\u5e38\u7684\u4e94\u884c\u62f3\u3002\u8fd9\u8def\u62f3\u6cd5\u51e0\u4e4e\u51e1\u662f\u5b66\u6b66\u4e4b\u4eba\u8c01\u90fd\u7ec3\u8fc7\uff0c\u6e29\u6c0f\u4e94\u7956\u81ea\u7136\u96be\u4ee5\u4ece\u4ed6\u62db\u5f0f\u4e2d\u731c\u6d4b\u4ed6\u7684\u5e08\u627f\u95e8\u6237\u3002 \u3000\u3000\u6e29\u65b9\u4e49\u867d\u7136\u51fa\u624b\u4e0d\u5feb\uff0c\u4f46\u62f3\u638c\u53d1\u51fa\uff0c\u631f\u6709\u6781\u5927\u52b2\u98ce\uff0c\u62c6\u5f97\u516b\u4e5d\u62db\uff0c\u8881\u627f\u5fd7\u5ffd\u89c9\u5bf9\u65b9\u638c\u98ce\u4e2d\u5fae\u6709\u70ed\u6c14\uff0c\u5411\u4ed6\u624b\u638c\u770b\u53bb\uff0c\u5fc3\u5934\u5fae\u9707\uff0c\u4f46\u89c1\u4ed6\u638c\u5fc3\u6bb7\u7ea2\u5982\u8840\uff0c\u60e8\u6de1\u6708\u5149\u6620\u7167\u4e4b\u4e0b\uff0c\u66f4\u89c9\u53ef\u6016\uff0c\u5fc3\u60f3\uff0c\u8fd9\u4eba\u7ec3\u7684\u662f\u6731\u7802\u638c\uff0c\u542c\u5e08\u7236\u8bf4\uff0c\u8fd9\u95e8\u638c\u529b\u7740\u5b9e\u4e86\u5f97\uff0c\u53ef\u522b\u88ab\u4ed6\u6253\u5230\u4e86\uff0c\u4e8e\u662f\u62f3\u98ce\u4e00\u7d27\uff0c\u62db\u6570\u4ecd\u662f\u5e73\u5eb8\uff0c\u52b2\u529b\u5374\u6e10\u6e10\u589e\u5f3a\u3002 \u3000\u3000\u9163\u6597\u4e2d\u6e29\u65b9\u4e49\u7a81\u89c9\u53f3\u8155\u4e00\u75bc\uff0c\u75be\u5fd9\u8df3\u5f00\uff0c\u4f4e\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u8155\u4e0a\u4e00\u9053\u7ea2\u5370\u80bf\u8d77\uff0c\u539f\u6765\u5df2\u88ab\u4ed6\u624b\u6307\u5212\u8fc7\uff0c\u4f46\u663e\u662f\u624b\u4e0b\u7559\u60c5\u3002\u6e29\u65b9\u4e49\u5fc3\u5934\u867d\u6012\uff0c\u53ef\u662f\u4e5f\u4e0d\u4fbf\u518d\u7f20\u6597\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u4e0a\u524d\u4e00\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8881\u5144\u5f1f\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u62f3\u811a\u5c45\u7136\u751a\u662f\u4e86\u5f97\uff0c\u90a3\u53ef\u4e0d\u5bb9\u6613\u5f97\u5f88\u4e86\u3002\u8001\u592b\u9886\u6559\u9886\u6559\u4f60\u5175\u5203\u4e0a\u7684\u529f\u592b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u8eab\u643a\u5175\u5668\u6765\u5230\u5b9d\u5e84\u3002\u201d\u6e29\u65b9\u5c71\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u793c\u6570\u5012\u4e5f\u5468\u5168\uff0c\u8fd9\u4e5f\u7b97\u827a\u9ad8\u4eba\u80c6\u5927\u4e86\u3002\u597d\u5427\uff0c\u54b1\u4eec\u5230\u7ec3\u6b66\u5385\u53bb\uff01\u201d\u624b\u4e00\u62db\uff0c\u8dc3\u4e0b\u5730\u6765\u3002\u4f17\u4eba\u7eb7\u7eb7\u8df3\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53ea\u5f97\u968f\u7740\u4f17\u4eba\u8fdb\u5c4b\u3002 \u3000\u3000\u6e29\u9752\u8d70\u5230\u4ed6\u8eab\u8fb9\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u62d0\u6756\u91cc\u6709\u6697\u5668\u3002\u201d\u8881\u627f\u5fd7\u6b63\u5f85\u63a5\u5634\uff0c\u6e29\u9752\u5df2\u8f6c\u8eab\u5bf9\u6e29\u6b63\u9053\uff1a\u201c\u9ed1\u4e0d\u6e9c\u79cb\u7684\u5e7f\u4e1c\u86ee\u5b50\u600e\u4e48\u6837\uff1f\u73b0\u4e0b\u53ef\u670d\u4e86\u5427\uff1f\u201d\u6e29\u6b63\u9053\uff1a\u201c\u4e8c\u7237\u7237\u662f\u5ba0\u7740\u4f60\uff0c\u624d\u4e0d\u8ddf\u4ed6\u5f53\u771f\uff0c\u6709\u751a\u4e48\u5e0c\u5947\u4e86\uff1f\u201d\u6e29\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u4e0d\u518d\u7406\u4ed6\u3002 \u3000\u3000\u4f17\u4eba\u8d70\u8fdb\u7ec3\u6b66\u5385\uff0c\u8881\u627f\u5fd7\u89c1\u662f\u4e00\u5ea7\u4e09\u5f00\u95f4\u7684\u5927\u5385\uff0c\u6253\u901a\u4e86\u6210\u4e3a\u4e00\u4e2a\u5927\u573a\u5b50\u3002\u5bb6\u4e01\u8fdb\u6765\u70b9\u8d77\u6570\u5341\u652f\u5de8\u70db\uff0c\u7167\u5f97\u660e\u5982\u767d\u663c\u3002\u6e29\u5bb6\u7537\u5973\u5927\u90fd\u5747\u4f1a\u6b66\u827a\uff0c\u542c\u5f97\u4e09\u8001\u592a\u7237\u8981\u548c\u524d\u65e5\u6765\u7684\u5ba2\u4eba\u6bd4\u6b66\uff0c\u90fd\u62e5\u5230\u5385\u4e0a\u6765\u89c2\u770b\uff0c\u8fde\u5c0f\u5b69\u5b50\u4e5f\u51fa\u6765\u4e86\u3002 \u3000\u3000\u6700\u540e\u6709\u4e2a\u4e2d\u5e74\u7f8e\u5987\u548c\u5c0f\u83ca\u4e00\u9f50\u51fa\u6765\u3002\u6e29\u9752\u62a2\u8fc7\u53bb\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5988\uff01\u201d\u90a3\u7f8e\u5987\u6ee1\u8138\u6101\u5bb9\uff0c\u767d\u4e86\u6e29\u9752\u4e00\u773c\uff0c\u663e\u5f97\u751a\u662f\u4e0d\u5feb\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u6307\u7740\u56db\u5468\u7684\u5200\u67aa\u67b6\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4f7f\u751a\u4e48\u5175\u5203\uff0c\u81ea\u5df1\u6311\u5427\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5bfb\u601d\uff1a\u4eca\u65e5\u4e4b\u4e8b\u773c\u89c1\u5df2\u4e0d\u80fd\u5584\u7f62\uff0c\u53ef\u662f\u53c8\u4e0d\u80fd\u4f24\u4e86\u7ed3\u4e49\u5144\u5f1f\u7684\u5c0a\u957f\uff0c\u521a\u4e0b\u5c71\u6765\u5c31\u9047\u4e0a\u8fd9\u4e2a\u96be\u9898\uff0c\u53ef\u4e0d\u77e5\u5982\u4f55\u5e94\u4ed8\u624d\u597d\u3002 \u3000\u3000\u6e29\u9752\u89c1\u4ed6\u76b1\u7709\u4e0d\u8bed\uff0c\u53ea\u9053\u4ed6\u5fc3\u4e2d\u5bb3\u6015\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u4e09\u7237\u7237\u6700\u75bc\u7231\u5c0f\u8f88\u7684\uff0c\u51b3\u4e0d\u80fd\u4f24\u4f60\u3002\u201d\u8fd9\u8bdd\u4e00\u534a\u4e5f\u662f\u8bf4\u7ed9\u6e29\u65b9\u5c71\u542c\u7684\uff0c\u8981\u4ed6\u4e0d\u4fbf\u75db\u4e0b\u6740\u624b\u3002\u5979\u6bcd\u4eb2\u9053\uff1a\u201c\u9752\u9752\uff0c\u522b\u591a\u8bdd\uff01\u201d\u6e29\u65b9\u5c71\u671b\u4e86\u6e29\u9752\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u5f97\u77a7\u5404\u4eba\u7684\u9020\u5316\u7f62\u3002\u8881\u4e16\u5144\uff0c\u4f60\u4f7f\u751a\u4e48\u5175\u5203\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u6e38\u76ee\u56db\u987e\uff0c\u89c1\u4e00\u4e2a\u516d\u4e03\u5c81\u7537\u5b69\u7ad9\u5728\u4e00\u65c1\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u67c4\u73a9\u5177\u6728\u5251\uff0c\u6f06\u5f97\u82b1\u82b1\u7eff\u7eff\u5730\uff0c\u5251\u957f\u53ea\u6709\u5bfb\u5e38\u957f\u5251\u7684\u4e00\u534a\u3002\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8d70\u8fc7\u53bb\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u628a\u5251\u501f\u7ed9\u6211\u7528\u4e00\u4e0b\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u90a3\u5c0f\u5b69\u7b11\u563b\u563b\u7684\u5c06\u5251\u9012\u4e86\u7ed9\u4ed6\u3002\u8881\u627f\u5fd7\u63a5\u4e86\u8fc7\u6765\uff0c\u5bf9\u6e29\u65b9\u5c71\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u4e0e\u8001\u524d\u8f88\u52a8\u771f\u5200\u771f\u67aa\uff0c\u5c31\u4ee5\u8fd9\u628a\u6728\u5251\u8ba8\u6559\u51e0\u62db\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u6765\u4f3c\u4e4e\u8c26\u900a\uff0c\u5b9e\u5219\u662f\u7adf\u6ca1\u628a\u5bf9\u65b9\u653e\u5728\u773c\u91cc\u3002\u4ed6\u60f3\u5bf9\u65b9\u4eba\u591a\uff0c\u4e0d\u65ad\u7f20\u6597\u4e0b\u53bb\uff0c\u4e0d\u77e5\u4f55\u65f6\u65b9\u51b3\uff0c\u5b89\u5c0f\u6167\u53c8\u5df2\u906d\u56f0\uff0c\u987b\u5f97\u663e\u793a\u4e0a\u4e58\u6b66\u529f\uff0c\u5c06\u5bf9\u65b9\u5c3d\u5feb\u5c3d\u6570\u6151\u670d\uff0c\u65b9\u80fd\u53d6\u91d1\u6551\u4eba\uff0c\u65e2\u514d\u7a3d\u8fdf\u751f\u53d8\uff0c\u53c8\u4e0d\u4f24\u4e86\u5bf9\u6e29\u9752\u7684\u91d1\u5170\u4e49\u6c14\u3002 \u3000\u3000\u9002\u624d\u4ed6\u5728\u5c4b\u9876\u8ddf\u6e29\u65b9\u4e49\u52a8\u624b\uff0c\u4e8e\u5bf9\u65b9\u6b66\u529f\u4fee\u4e3a\u5df2\u4e86\u7136\u4e8e\u80f8\uff0c\u5018\u82e5\u6e29\u6c0f\u4e94\u8001\u7684\u6b66\u529f\u5747\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u90a3\u4e48\u4ee5\u6728\u5251\u8fce\u654c\uff0c\u5e76\u4e0d\u80fd\u7b97\u662f\u72af\u9669\u6258\u5927\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u542c\u4e86\u8fd9\u8bdd\uff0c\u6c14\u5f97\u624b\u8db3\u53d1\u6296\uff0c\u4ef0\u5929\u6253\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u592b\u884c\u8d70\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u5982\u6b64\u5c0f\u89d1\u8001\u592b\u8fd9\u67c4\u9f99\u5934\u94a2\u6756\u7684\uff0c\u563f\u563f\uff0c\u4eca\u65e5\u5012\u8fd8\u662f\u521d\u4f1a\u3002\u597d\u5427\uff0c\u4f60\u6709\u672c\u4e8b\uff0c\u7528\u8fd9\u6728\u5251\u6765\u524a\u65ad\u6211\u7684\u94a2\u6756\u5427\u3002\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u62d0\u6756\u6a2a\u8f6c\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u671d\u8881\u627f\u5fd7\u8170\u4e2d\u6a2a\u626b\u800c\u6765\u3002 \u3000\u3000\u98ce\u52bf\u52b2\u6025\uff0c\u8881\u627f\u5fd7\u7684\u8eab\u5b50\u4f3c\u4e4e\u88ab\u94a2\u6756\u5e26\u4e86\u8d77\u6765\uff0c\u6e29\u9752\u201c\u5440\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u89c1\u4ed6\u8eab\u672a\u843d\u5730\uff0c\u6728\u5251\u5251\u5c16\u5df2\u76f4\u6307\u5bf9\u65b9\u9762\u95e8\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u94a2\u6756\u5012\u8f6c\uff0c\u6756\u5934\u5411\u4ed6\u540e\u5fc3\u8981\u7a74\u70b9\u5230\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u8fd9\u62d0\u6756\u8fd8\u53ef\u7528\u6765\u70b9\u7a74\uff0c\u9752\u5f1f\u53c8\u8bf4\u6756\u4e2d\u6709\u6697\u5668\uff0c\u987b\u5f97\u5c0f\u5fc3\u3002\u201d\u8eab\u5b50\u4e00\u504f\uff0c\u62d0\u6756\u70b9\u7a7a\uff0c\u6728\u5251\u4e00\u62db\u201c\u6cbe\u5730\u98de\u7d6e\u201d\uff0c\u8d34\u7740\u62d0\u6756\u76f4\u524a\u4e0b\u53bb\uff0c\u53bb\u52bf\u5feb\u6781\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u77a7\u4ed6\u5251\u52bf\uff0c\u77e5\u9053\u867d\u662f\u6728\u5251\uff0c\u7ed9\u524a\u4e0a\u4e86\u624b\u6307\u4e5f\u8981\u53d7\u4f24\uff0c\u5371\u6025\u4e2d\u53f3\u624b\u4e00\u677e\uff0c\u62d0\u6756\u843d\u4e0b\uff0c\u521a\u8981\u78b0\u5230\u5730\u9762\uff0c\u5de6\u624b\u5feb\u5982\u95ea\u7535\uff0c\u4f38\u4e0b\u53bb\u6293\u7740\u6756\u5c3e\uff0c\u84e6\u5730\u4e00\u6296\uff0c\u4e00\u67c4\u6570\u5341\u65a4\u7684\u94a2\u6756\u6602\u5934\u633a\u8d77\uff0c\u53cd\u51fb\u5bf9\u65b9\u3002\u8881\u627f\u5fd7\u89c1\u4ed6\u773c\u660e\u624b\u5feb\uff0c\u53d8\u62db\u8fc5\u6377\uff0c\u4e5f\u81ea\u4f69\u670d\u3002 \u3000\u3000\u4e24\u4eba\u8d8a\u6597\u8d8a\u7d27\uff0c\u6e29\u65b9\u5c71\u7684\u94a2\u6756\u4f7f\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u6709\u65f6\u4e00\u6756\u51fb\u7a7a\uff0c\u6253\u5728\u5730\u4e0b\uff0c\u7816\u5934\u767b\u65f6\u7c89\u788e\uff0c\u58f0\u52bf\u7740\u5b9e\u60ca\u4eba\u3002\u8881\u627f\u5fd7\u5728\u6756\u7f1d\u4e2d\u5982\u8774\u8776\u822c\u7a7f\u6765\u63d2\u53bb\uff0c\u6728\u5251\u8f7b\u7075\uff0c\u62db\u62db\u4e0d\u79bb\u654c\u4eba\u8981\u5bb3\u3002 \u3000\u3000\u8f6c\u77ac\u62c6\u4e86\u4e03\u516b\u5341\u62db\uff0c\u6e29\u65b9\u5c71\u7126\u8e81\u8d77\u6765\uff0c\u5fc3\u60f3\u81ea\u5df1\u8fd9\u67c4\u9f99\u5934\u94a2\u6743\u5a01\u9707\u6c5f\u5357\uff0c\u7eb5\u6a2a\u65e0\u654c\uff0c\u4eca\u65e5\u5374\u88ab\u8fd9\u540e\u751f\u5c0f\u8f88\u4ee5\u4e00\u4ef6\u73a9\u7269\u6253\u6210\u5e73\u624b\uff0c\u4e00\u751f\u5a01\u540d\uff0c\u5c82\u975e\u65ad\u9001\uff1f\u6756\u6cd5\u7a81\u53d8\uff0c\u6a2a\u626b\u76f4\u7838\uff0c\u5df2\u5c06\u654c\u4eba\u5168\u8eab\u88f9\u4f4f\u3002 \u3000\u3000\u65c1\u89c2\u4f17\u4eba\u53ea\u89c9\u6756\u98ce\u6108\u6765\u6108\u5927\uff0c\u6162\u6162\u9000\u540e\uff0c\u90fd\u628a\u80cc\u810a\u9760\u4f4f\u5385\u58c1\uff0c\u4ee5\u9632\u88ab\u6756\u5934\u5e26\u5230\uff0c\u70db\u5f71\u4e0b\u53ea\u89c1\u94a2\u6756\u821e\u6210\u4e00\u4e2a\u4eae\u6643\u6643\u7684\u5927\u5708\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u7684\u6b66\u529f\uff0c\u6bd4\u4e4b\u90a3\u9f99\u6e38\u5e2e\u5e2e\u4e3b\u8363\u5f69\u53ef\u9ad8\u5f97\u591a\u4e86\u3002\u8881\u627f\u5fd7\u827a\u6210\u4e0b\u5c71\uff0c\u6b64\u65f6\u65b9\u59cb\u771f\u6b63\u9047\u5230\u6b66\u529f\u9ad8\u5f3a\u7684\u5bf9\u624b\uff0c\u53ea\u662f\u4e0d\u613f\u4f7f\u51fa\u534e\u5c71\u6d3e\u6b63\u5b97\u5251\u6cd5\u6765\uff0c\u4ee5\u514d\u7ed9\u6e29\u6c0f\u4e94\u8001\u8ba4\u51fa\u4e86\u81ea\u5df1\u95e8\u6d3e\uff0c\u800c\u5bf9\u65b9\u94a2\u6756\u6781\u5177\u5a01\u52bf\uff0c\u6b3a\u4e0d\u8fd1\u8eab\u53bb\uff0c\u624b\u4e2d\u6728\u5251\u53c8\u4e0d\u80fd\u4e0e\u4ed6\u94a2\u6756\u76f8\u78b0\uff0c\u5fc3\u60f3\u975e\u51fa\u7edd\u62db\uff0c\u4e0d\u6613\u53d6\u80dc\uff0c\u5ffd\u5730\u8eab\u6cd5\u7a0d\u6ede\uff0c\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u5927\u559c\uff0c\u6a2a\u6756\u626b\u6765\u3002\u8881\u627f\u5fd7\u5de6\u624b\u8fd0\u8d77\u201c\u6df7\u5143\u529f\u201d\uff0c\u786c\u751f\u751f\u4e00\u628a\u6293\u4f4f\u6756\u5934\uff0c\u8fd0\u529b\u4e0b\u62d7\uff0c\u53f3\u624b\u6728\u5251\u76f4\u8fdb\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u6e29\u65b9\u5c71\u80a9\u5934\u8863\u670d\u5df2\u88ab\u523a\u7834\uff0c\u8fd9\u8fd8\u662f\u4ed6\u5b58\u5fc3\u76f8\u8ba9\uff0c\u5426\u5219\u4e00\u5251\u523a\u5728\u80f8\u53e3\uff0c\u867d\u662f\u6728\u5251\uff0c\u4f46\u5185\u52b2\u51cc\u5389\uff0c\u5374\u4e5f\u662f\u7a7f\u80f8\u5f00\u819b\u4e4b\u7978\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u5927\u5403\u4e00\u60ca\uff0c\u864e\u53e3\u5267\u75db\uff0c\u94a2\u6756\u5df2\u88ab\u631f\u624b\u593a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\u4ed6\u662f\u6e29\u9752\u7684\u4eb2\u5916\u516c\uff0c\u4e0d\u80fd\u4ee4\u4ed6\u96be\u582a\uff0c\u5f53\u4e0b\u7acb\u5373\u6536\u56de\u6728\u5251\uff0c\u5de6\u624b\u4e00\u9001\uff0c\u5df2\u5c06\u94a2\u6756\u4ea4\u8fd8\u5728\u4ed6\u624b\u4e2d\u3002\u8fd9\u53ea\u662f\u4e00\u77ac\u95f4\u4e4b\u4e8b\uff0c\u6b66\u529f\u7a0d\u5dee\u7684\u4eba\u6d51\u6ca1\u770b\u51fa\u94a2\u6756\u4e00\u593a\u4e00\u8fd8\uff0c\u5df2\u8f6c\u8fc7\u4e86\u4e00\u6b21\u624b\uff0c\u6599\u60f3\u4ee4\u4ed6\u5982\u6b64\u4e0b\u53f0\uff0c\u5341\u5206\u987e\u5168\u4e86\u4ed6\u8001\u4eba\u5bb6\u7684\u989c\u9762\u3002 \u3000\u3000\u54ea\u77e5\u6e29\u65b9\u5c71\u8ddf\u7740\u4fbf\u6a2a\u6756\u6253\u51fa\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u5df2\u7ecf\u8f93\u4e86\u62db\uff0c\u600e\u4e48\u5982\u6b64\u4e0d\u8bb2\u7406\uff0c\u5168\u6ca1\u6b66\u6797\u4e2d\u9ad8\u4eba\u7684\u8eab\u5206\uff1f\u201d\u5f53\u5373\u5411\u5de6\u907f\u5f00\uff0c\u7a81\u7136\u55e4\u55e4\u55e4\u4e09\u58f0\uff0c\u6756\u5934\u9f99\u53e3\u4e2d\u98de\u51fa\u4e09\u679a\u94a2\u9489\uff0c\u5206\u5411\u4e0a\u4e2d\u4e0b\u4e09\u8def\u6253\u5230\u3002\u6756\u5934\u548c\u4ed6\u8eab\u5b50\u76f8\u8ddd\u4e0d\u8fc7\u4e00\u5c3a\uff0c\u6697\u5668\u7a81\u53d1\uff0c\u54ea\u91cc\u907f\u8ba9\u5f97\u6389\uff1f \u3000\u3000\u6e29\u9752\u4e0d\u7531\u5f97\u201c\u5440\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u773c\u89c1\u60c5\u52bf\u5371\u6025\uff0c\u8138\u8272\u5927\u53d8\u3002 \u3000\u3000\u5374\u89c1\u8881\u627f\u5fd7\u6728\u5251\u56de\u8f6c\uff0c\u556a\u556a\u556a\u4e09\u58f0\uff0c\u5df2\u5c06\u4e09\u679a\u94a2\u9489\u90fd\u6253\u5728\u5730\u4e0b\u3002\u8fd9\u62db\u534e\u5c71\u5251\u6cd5\uff0c\u6709\u4e2a\u540d\u76ee\u53eb\u4f5c\u201c\u5b54\u96c0\u5f00\u5c4f\u201d\uff0c\u53d6\u4e49\u4e8e\u5b54\u96c0\u5f00\u5c4f\uff0c\u987e\u5c3e\u81ea\u601c\u3002\u8fd9\u62db\u5251\u67c4\u5728\u5916\uff0c\u5251\u5c16\u5411\u5df1\uff0c\u4e13\u5728\u7d27\u6025\u5173\u5934\u6321\u683c\u654c\u4eba\u5175\u5668\u3002\u8881\u627f\u5fd7\u6253\u843d\u6697\u5668\uff0c\u6728\u5251\u53cd\u64a9\uff0c\u6a2a\u8fc7\u6765\u5728\u94a2\u6756\u7684\u9f99\u5934\u4e0a\u4e00\u6309\u3002\u6728\u5251\u867d\u8f7b\uff0c\u8fd9\u4e00\u6309\u5374\u6309\u5728\u6756\u8170\u7684\u4e0d\u5f53\u529b\u5904\uff0c\u6b63\u6df1\u5f97\u6b66\u5b66\u4e2d\u201c\u56db\u4e24\u62e8\u5343\u65a4\u201d\u7684\u8981\u65e8\u3002 \u3000\u3000\u6e29\u65b9\u51fa\u53ea\u89c9\u4e00\u80a1\u52b2\u529b\u5c06\u94a2\u6756\u5411\u4e0b\u637a\u843d\uff0c\u5fd9\u8fd0\u529b\u53cd\u633a\uff0c\u5374\u5df2\u6162\u4e86\u4e00\u6b65\uff0c\u6756\u5934\u843d\u5730\u3002\u8881\u627f\u5fd7\u5de6\u8db3\u4e00\u8e6c\uff0c\u8e0f\u4e0a\u6756\u5934\u3002\u6e29\u65b9\u5c71\u7528\u529b\u56de\u626f\uff0c\u7adf\u6ca1\u626f\u8d77\uff0c\u8881\u627f\u5fd7\u677e\u8db3\u5411\u540e\u7eb5\u5f00\u4e08\u4f59\u3002\u6e29\u65b9\u5c71\u6536\u56de\u94a2\u6756\uff0c\u53ea\u89c1\u5385\u4e0a\u9752\u7816\u6df1\u6df1\u51f9\u4e0b\u4e86\u534a\u4e2a\u9f99\u5934\uff0c\u987b\u7259\u5b9b\u7136\uff0c\u7adf\u662f\u6756\u4e0a\u9f99\u5934\u88ab\u4ed6\u8e6c\u5165\u7816\u4e2d\u7559\u4e0b\u7684\u5370\u75d5\u3002\u56db\u5468\u4f17\u4eba\u89c1\u4e86\uff0c\u5c3d\u7686\u9a87\u7136\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u8138\u8272\u5927\u53d8\uff0c\u53cc\u624b\u5c06\u94a2\u6756\u731b\u529b\u5f80\u5c4b\u9876\u4e0a\u63b7\u53bb\uff0c\u53ea\u542c\u5f97\u5ffd\u5566\u4e00\u58f0\u5de8\u54cd\uff0c\u94a2\u6756\u7a7f\u7834\u5c4b\u9876\uff0c\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u7eb5\u58f0\u5927\u53eb\uff1a\u201c\u8fd9\u5bb6\u4f19\u8f93\u7ed9\u4f60\u7684\u6728\u5251\uff0c\u8fd8\u8981\u5b83\u5e72\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u8fd9\u8001\u5934\u5b50\u6012\u6c14\u52c3\u52c3\uff0c\u547c\u547c\u5598\u6c14\uff0c\u5c06\u4e00\u4e1b\u80e1\u5b50\u90fd\u5439\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u8fd9\u662f\u4f60\u8f93\u4e86\u7ed9\u6211\uff0c\u53ef\u4e0d\u662f\u94a2\u6756\u8f93\u4e86\u7ed9\u6728\u5251\uff01\u201d \u3000\u3000\u5c4b\u9876\u7816\u74e6\u6ce5\u5c18\u7eb7\u843d\u4e4b\u4e2d\uff0c\u6e29\u65b9\u65bd\u7eb5\u8eab\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\u6253\u6697\u5668\u7684\u529f\u592b\u8fd8\u4e0d\u574f\uff0c\u6765\u63a5\u63a5\u6211\u7684\u98de\u5200\u600e\u6837\uff1f\u201d\u968f\u624b\u89e3\u4e0b\u8170\u4e2d\u76ae\u5957\uff0c\u8d1f\u5728\u80cc\u4e0a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u76ae\u5957\u4e2d\u63d2\u7740\u4e8c\u5341\u56db\u67c4\u660e\u6643\u6643\u7684\u98de\u5200\uff0c\u5203\u957f\u5c3a\u8bb8\uff0c\u5fc3\u60f3\u5927\u51e1\u6697\u5668\uff0c\u5747\u662f\u4e58\u4eba\u4e0d\u5907\uff0c\u5352\u7136\u65bd\u53d1\uff0c\u8896\u7bad\u85cf\u5728\u8896\u4e2d\uff0c\u91d1\u9556\u3001\u94c1\u83b2\u5b50\u4e4b\u5c5e\u85cf\u5728\u8863\u56ca\uff0c\u4ed6\u7684\u98de\u5200\u5374\u660e\u6446\u5728\u8eab\u4e0a\u5f53\u773c\u4e4b\u5904\uff0c\u6599\u60f3\u5fc5\u6709\u8fc7\u4eba\u4e4b\u957f\uff0c\u77e5\u9053\u8fd9\u65f6\u8c26\u900a\u9000\u8ba9\u4e5f\u5df2\u65e0\u7528\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u624b\u4e0b\u5bb9\u60c5\uff01\u201d\u5c06\u6728\u5251\u8fd8\u7ed9\u5c0f\u5b69\uff0c\u8f6c\u8fc7\u8eab\u6765\u3002 \u3000\u3000\u6e29\u5bb6\u4f17\u4eba\u77e5\u9053\u56db\u8001\u7237\u7684\u98de\u5200\u52bf\u5934\u52b2\u6025\uff0c\u6377\u5982\u7535\u95ea\uff0c\u500f\u7136\u4fbf\u81f3\u3002\u8fd9\u5c11\u5e74\u5982\u5168\u6570\u63a5\u4f4f\uff0c\u5012\u4e5f\u7f62\u4e86\uff0c\u8981\u662f\u4ed6\u95ea\u907f\u9000\u8ba9\uff0c\u98de\u5200\u4e0d\u751f\u773c\u775b\uff0c\u90a3\u53ef\u8c01\u4e5f\u53d7\u4e0d\u4f4f\u4ed6\u4e00\u5200\u3002\u5f53\u4e0b\u9664\u4e86\u56db\u8001\u4e4b\u5916\uff0c\u4f59\u4eba\u7eb7\u7eb7\u8d70\u51fa\u5385\u53bb\uff0c\u6328\u5728\u95e8\u8fb9\u89c2\u770b\u3002 \u3000\u3000\u6e29\u65b9\u65bd\u53eb\u9053\uff1a\u201c\u770b\u5200\uff01\u201d\u624b\u4e00\u626c\uff0c\u5bd2\u5149\u95ea\u5904\uff0c\u4e00\u5200\u545c\u545c\u98de\u51fa\u3002\u539f\u6765\u4ed6\u7684\u98de\u5200\u5200\u67c4\u51ff\u7a7a\uff0c\u5728\u7a7a\u4e2d\u6025\u98de\u800c\u8fc7\u4e4b\u65f6\uff0c\u98ce\u7a7f\u7a7a\u6d1e\uff0c\u53d1\u51fa\u545c\u545c\u4e4b\u58f0\uff0c\u5982\u5439\u5522\u5450\uff0c\u58f0\u97f3\u51c4\u5389\u3002\u5200\u53d1\u6709\u58f0\uff0c\u4f3c\u662f\u5148\u7ed9\u654c\u4eba\u8b66\u544a\uff0c\u663e\u5f97\u5149\u660e\u78ca\u843d\uff0c\u5176\u5b9e\u4e5f\u662f\u5a01\u6151\u6050\u5413\uff0c\u6270\u4eba\u5fc3\u795e\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u98de\u5200\u5a01\u731b\uff0c\u4e0e\u4e00\u822c\u6697\u5668\u4ee5\u8f7b\u7075\u6216\u9634\u6bd2\u89c1\u80dc\u8005\u8fe5\u5f02\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u5982\u7528\u624b\u63a5\u5200\uff0c\u4e0d\u663e\u529f\u592b\uff0c\u96be\u632b\u4ed6\u9a84\u6c14\uff0c\u603b\u8981\u4ee4\u4ed6\u4eec\u8f93\u5f97\u5fc3\u60a6\u8bda\u670d\uff0c\u624d\u80fd\u53eb\u4ed6\u4eec\u653e\u51fa\u5c0f\u6167\uff0c\u4ea4\u8fd8\u9ec4\u91d1\u3002\u201d\u4e8e\u662f\u5728\u6000\u4e2d\u6478\u51fa\u4e24\u679a\u94dc\u94b1\uff0c\u5de6\u624b\u4e00\u679a\uff0c\u53f3\u624b\u4e00\u679a\uff0c\u5206\u5411\u98de\u5200\u6253\u53bb\u3002 \u3000\u3000\u5de6\u624b\u4e00\u679a\u5148\u5230\uff0c\u53ea\u542c\u94ee\u7684\u4e00\u58f0\u54cd\uff0c\u98de\u5200\u767b\u65f6\u65e0\u58f0\uff0c\u539f\u6765\u94dc\u94b1\u5df2\u628a\u9542\u7a7a\u7684\u5200\u67c4\u6253\u6298\u3002\u53f3\u624b\u4e00\u679a\u94dc\u94b1\u518d\u98de\u8fc7\u53bb\uff0c\u4e0e\u98de\u5200\u4e00\u649e\uff0c\u540c\u65f6\u8dcc\u5728\u5730\u4e0a\u3002\u90a3\u98de\u5200\u91cd\u903e\u534a\u65a4\uff0c\u94dc\u94b1\u53c8\u8f7b\u53c8\u5c0f\uff0c\u7136\u800c\u4e24\u8005\u76f8\u649e\u4e4b\u540e\uff0c\u5c45\u7136\u4e00\u9f50\u4e0b\u5815\uff0c\u663e\u89c1\u4ed6\u7684\u624b\u52b2\u529b\u9053\uff0c\u6bd4\u6e29\u65b9\u65bd\u9ad8\u51fa\u4f55\u6b62\u6570\u500d\u3002 \u3000\u3000\u6e29\u65b9\u65bd\u767b\u65f6\u53d8\u8272\uff0c\u4e24\u5200\u540c\u65f6\u53d1\u51fa\u3002\u8881\u627f\u5fd7\u4e5f\u7167\u6837\u53d1\u51fa\u56db\u679a\u94dc\u94b1\uff0c\u5148\u5c06\u53cc\u5200\u58f0\u97f3\u6253\u54d1\uff0c\u8ddf\u7740\u51fb\u843d\u5728\u5730\u3002 \u3000\u3000\u6e29\u65b9\u65bd\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u597d\u672c\u4e8b\uff01\u597d\u529f\u592b\uff01\u201d\u53e3\u4e2d\u8bf4\u7740\uff0c\u624b\u4e0b\u4e1d\u6beb\u4e0d\u7f13\uff0c\u516d\u628a\u98de\u5200\u4e00\u8fde\u4e32\u7684\u63b7\u4e86\u51fa\u53bb\u3002\u4ed6\u8fd9\u65f6\u5df2\u77e5\u52bf\u96be\u51fb\u4e2d\u5bf9\u65b9\uff0c\u6545\u610f\u5c06\u516d\u67c4\u98de\u5200\u56db\u6563\u63b7\u51fa\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u4f60\u8fd8\u80fd\u4e00\u4e00\u628a\u6211\u98de\u5200\u6253\u843d\uff1f\u201d\u5374\u542c\u5f97\u545c\u94ee\u3001\u545c\u94ee\u63a5\u8fde\u516d\u54cd\uff0c\u516d\u67c4\u98de\u5200\u7adf\u7136\u53c8\u88ab\u5341\u4e8c\u679a\u94dc\u94b1\u6253\u54d1\u78b0\u8dcc\u3002\u8881\u627f\u5fd7\u5f53\u65e5\u5728\u534e\u5c71\u7edd\u9876\uff0c\u4e0d\u77e5\u548c\u6728\u6851\u9053\u4eba\u4e0b\u4e86\u591a\u5c11\u76d8\u68cb\uff0c\u6253\u4e86\u591a\u5c11\u5343\u53d8\u4e07\u5316\u4e4b\u52ab\uff0c\u518d\u52a0\u4e0a\u65e0\u6570\u6668\u5915\u7684\u82e6\u7ec3\uff0c\u624d\u5b66\u4f1a\u8fd9\u624b\u4e16\u4e0a\u7f55\u89c1\u7684\u6697\u5668\u529f\u592b\u3002\u6728\u6851\u82e5\u662f\u5728\u65c1\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u6307\u6458\u4ed6\u624b\u6cd5\u672a\u7eaf\uff0c\u4f46\u6e29\u5bb6\u8bf8\u4eba\u5374\u5df2\u5c3d\u7686\u5fc3\u60ca\u3002 \u3000\u3000\u6e29\u65b9\u65bd\u5927\u559d\u4e00\u58f0\uff1a\u201c\u597d\uff01\u201d\u53cc\u624b\u9f50\u65bd\uff0c\u516d\u67c4\u98de\u5200\u540c\u65f6\u5411\u5bf9\u65b9\u8981\u5bb3\u5904\u63b7\u51fa\uff0c\u516d\u5200\u521a\u51fa\u624b\uff0c\u53c8\u662f\u516d\u5200\u9f50\u98de\uff0c\u8fd9\u662f\u4ed6\u5e73\u751f\u7edd\u6280\uff0c\u529f\u592b\u518d\u597d\u7684\u4eba\u8eb2\u5f00\u4e86\u524d\u9762\u516d\u5200\uff0c\u51b3\u518d\u8eb2\u4e0d\u5f00\u540e\u9762\u8ddf\u4e0a\u7684\u516d\u5200\u3002\u5341\u4e8c\u67c4\u98de\u5200\u545c\u545c\u58f0\u54cd\uff0c\u56db\u9762\u516b\u65b9\u7684\u9f50\u5411\u8881\u627f\u5fd7\u98de\u53bb\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u773c\u89c1\u8881\u627f\u5fd7\u6b66\u529f\u5353\u7edd\uff0c\u5fc5\u662f\u9ad8\u4eba\u5f1f\u5b50\uff0c\u7a81\u89c1\u56db\u5f1f\u4f7f\u51fa\u6700\u5389\u5bb3\u7684\u5200\u6cd5\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u56db\u5f1f\uff0c\u522b\u4f24\u4ed6\u6027\u547d\u2026\u2026\u201d\u8bdd\u58f0\u672a\u6bd5\uff0c\u53ea\u89c1\u8881\u627f\u5fd7\u53cc\u624b\u5728\u7a7a\u4e2d\u4e00\u9635\u4e71\u6293\uff0c\u53f3\u624b\u516d\u67c4\uff0c\u5de6\u624b\u516d\u67c4\uff0c\u5341\u4e8c\u67c4\u98de\u5200\u5c3d\u6570\u6293\u5728\u624b\u4e2d\uff0c\u63a5\u7740\u53cc\u624b\u5bf9\u7740\u5175\u5668\u67b6\u8fde\u7eed\u626c\u4e86\u51e0\u626c\u3002 \u3000\u3000\u5200\u67aa\u67b6\u4e0a\u672c\u6765\u660e\u6643\u6643\u7684\u63d2\u6ee1\u4e86\u5200\u67aa\u77db\u621f\uff0c\u4f46\u89c1\u767d\u5149\u95ea\u70c1\uff0c\u67aa\u5934\u77db\u68a2\uff0c\u5c3d\u7686\u6298\u65ad\uff0c\u539f\u6765\u90fd\u88ab\u4ed6\u7528\u5341\u4e8c\u628a\u98de\u5200\u65a9\u65ad\u4e86\u3002\u98de\u5200\u4f59\u52bf\u4e0d\u8870\uff0c\u63d2\u5165\u4e86\u5899\u58c1\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4e94\u8001\u4e00\u9f50\u7ad9\u8d77\uff0c\u5708\u5728\u4ed6\u8eab\u5468\uff0c\u76ee\u9732\u51f6\u5149\uff0c\u540c\u65f6\u559d\u9053\uff1a\u201c\u4f60\u662f\u91d1\u86c7\u5978\u8d3c\u6d3e\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u7a7a\u4e2d\u6293\u5200\u7684\u624b\u6cd5\uff0c\u786e\u662f\u5f97\u81ea\u300a\u91d1\u86c7\u79d8\u7b08\u300b\uff0c\u84e6\u89c1\u4e94\u8001\u795e\u6001\u51f6\u6076\uff0c\u4fbf\u4f3c\u8981\u540c\u65f6\u6251\u4e0a\u6765\u54ac\u566c\u4e00\u822c\uff0c\u5fc3\u4e0b\u4e0d\u7981\u60ca\u614c\uff0c\u6b63\u8981\u56de\u7b54\uff0c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u5ffd\u89c1\u5385\u5916\u4e09\u4e2a\u4eba\u8d70\u8fc7\uff0c\u5176\u4e2d\u4e00\u4eba\u6b63\u662f\u5b89\u5c0f\u6167\uff0c\u88ab\u4e24\u540d\u5927\u6c49\u7ed1\u7f1a\u4e86\u62bc\u7740\uff0c\u5f53\u662f\u521a\u4ece\u7ffb\u677f\u4e0b\u9762\u7684\u5730\u7a96\u88ab\u64d2\u4e86\u4e0a\u6765\u3002\u4ed6\u5fc3\u6025\u6551\u4eba\uff0c\u4e00\u4e2a\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u7eb5\u51fa\u5385\u53bb\u3002\u6e29\u65b9\u8fbe\u4e0e\u6e29\u65b9\u4e49\u5404\u62bd\u5175\u5203\uff0c\u968f\u540e\u8ffd\u5230\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u987e\u8ffd\u654c\uff0c\u76f4\u5411\u5b89\u5c0f\u6167\u51b2\u53bb\u3002\u4e24\u540d\u5927\u6c49\u5200\u5251\u9f50\u626c\uff0c\u6402\u5934\u780d\u4e0b\u3002\u53ea\u542c\u5f97\u5f53\u5f53\u4e24\u58f0\uff0c\u4e24\u540d\u5927\u6c49\u624b\u4e2d\u7684\u5200\u5251\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u5446\uff0c\u89c1\u7838\u53bb\u4ed6\u4eec\u5175\u5203\u7684\u7adf\u662f\u5927\u8001\u7237\u548c\u4e8c\u8001\u7237\uff0c\u5413\u4e86\u4e00\u8df3\u3002\u6e29\u65b9\u8fbe\u4e0e\u6e29\u65b9\u4e49\u9a82\u4e86\u58f0\uff1a\u201c\u8113\u5305\uff01\u201d\u62a2\u4e0a\u8ffd\u8d76\u3002 \u3000\u3000\u539f\u6765\u8881\u627f\u5fd7\u8eab\u624b\u5feb\u6781\uff0c\u4e0d\u67b6\u654c\u5203\uff0c\u55d6\u7684\u4e00\u4e0b\uff0c\u7adf\u4ece\u5200\u5251\u4e0b\u94bb\u4e86\u8fc7\u53bb\u3002\u90a3\u4e24\u540d\u5927\u6c49\u5175\u5203\u780d\u4e0b\u6765\u65f6\uff0c\u6e29\u6c0f\u4e8c\u8001\u6070\u597d\u8d76\u5230\uff0c\u4e00\u5200\u4e00\u5251\uff0c\u4fbf\u540c\u65f6\u5411\u5927\u8001\u7237\u3001\u4e8c\u8001\u7237\u7684\u5934\u4e0a\u62db\u547c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53cc\u624b\u4e00\u626f\uff0c\u626f\u65ad\u4e86\u7f1a\u4f4f\u5b89\u5c0f\u6167\u624b\u4e0a\u7684\u7ef3\u7d22\u3002\u5b89\u5c0f\u6167\u5927\u559c\uff0c\u8fde\u53eb\uff1a\u201c\u627f\u5fd7\u5927\u54e5\uff01\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u4e24\u4eba\u7684\u5200\u5251\u6b63\u4ece\u7a7a\u4e2d\u843d\u4e0b\uff0c\u8881\u627f\u5fd7\u7529\u51fa\u65ad\u7ef3\uff0c\u7f20\u4f4f\u957f\u5251\uff0c\u626f\u4e86\u56de\u6765\uff0c\u5bf9\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u63a5\u7740\uff01\u201d\u7ef3\u5b50\u4e00\u677e\uff0c\u90a3\u5251\u5251\u67c4\u5728\u524d\uff0c\u5012\u8f6c\u7740\u5411\u5979\u98de\u53bb\u3002\u5b89\u5c0f\u6167\u4f38\u624b\u63a5\u4f4f\u3002 \u3000\u3000\u8fd9\u5f53\u513f\u5f53\u771f\u662f\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u957f\u5251\u521a\u63b7\u51fa\uff0c\u6e29\u65b9\u8fbe\u4e24\u67c4\u77ed\u621f\u5df2\u5411\u8881\u627f\u5fd7\u80f8\u524d\u6420\u5230\u3002\u5374\u542c\u5f97\u201c\u554a\uff01\u54fc\uff01\u201d\u4e24\u58f0\u53eb\u558a\uff0c\u539f\u6765\u90a3\u4e24\u540d\u5927\u6c49\u6321\u5728\u8def\u53e3\uff0c\u6e29\u65b9\u4e49\u5acc\u4ed6\u4eec\u788d\u624b\u788d\u811a\uff0c\u4e00\u4e2a\u626b\u5802\u817f\u8e22\u5f00\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u811a\u6b65\u4e0d\u52a8\uff0c\u4e0a\u8eab\u5411\u540e\u4e00\u7f29\uff0c\u9661\u7136\u9000\u5f00\u4e24\u5c3a\u3002\u6e29\u65b9\u8fbe\u53cc\u621f\u9012\u7a7a\uff0c\u6b63\u8981\u518d\u6233\uff0c\u52b2\u672a\u4f7f\u51fa\uff0c\u500f\u89c9\u53cc\u621f\u81ea\u52a8\u5411\u524d\uff0c\u70db\u5149\u6620\u5c04\u4e0b\uff0c\u53ea\u89c1\u5bf9\u65b9\u624b\u4e2d\u4e00\u622a\u65ad\u7ef3\u5df2\u7f20\u4f4f\u53cc\u621f\uff0c\u5411\u524d\u62c9\u626f\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u501f\u529b\u6253\u529b\uff0c\u53cc\u621f\u4e00\u62db\u201c\u6cfe\u6e2d\u540c\u6d41\u201d\uff0c\u4e58\u52bf\u6233\u4e86\u8fc7\u53bb\uff0c\u621f\u5934\u950b\u9510\uff0c\u95ea\u95ea\u751f\u5149\u3002\u8881\u627f\u5fd7\u4fa7\u8fc7\u8eab\u5b50\uff0c\u7528\u529b\u4e00\u626f\u65ad\u7ef3\uff0c\u968f\u5373\u7a81\u7136\u677e\u624b\u3002\u6e29\u65b9\u8fbe\u51fa\u5176\u4e0d\u610f\uff0c\u6536\u52bf\u4e0d\u53ca\uff0c\u5411\u524d\u8e09\u8dc4\u4e86\u4e24\u6b65\uff0c\u770b\u8881\u627f\u5fd7\u65f6\uff0c\u5df2\u62c9\u4e86\u5b89\u5c0f\u6167\u62a2\u8fdb\u7ec3\u6b66\u5385\u5185\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u672c\u5df2\u51b2\u51b2\u5927\u6012\uff0c\u8fd9\u65f6\u66f4\u52a0\u6ee1\u8138\u6740\u6c14\uff0c\u53cc\u624b\u4e00\u5d29\uff0c\u5df2\u628a\u621f\u4e0a\u77ed\u7ef3\u5d29\u65ad\uff0c\u7eb5\u8fdb\u5385\u6765\u3002\u6e29\u5bb6\u4f17\u4eba\u4e5f\u90fd\u56de\u5230\u5385\u5185\uff0c\u7ad9\u5728\u4e94\u8001\u8eab\u540e\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u53cc\u621f\u5f52\u4e8e\u5de6\u624b\uff0c\u53f3\u624b\u6307\u7740\u8881\u627f\u5fd7\uff0c\u6076\u72e0\u72e0\u7684\u559d\u9053\uff1a\u201c\u90a3\u91d1\u86c7\u5978\u8d3c\u5728\u54ea\u91cc\uff1f\u5feb\u8bf4\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u6709\u8bdd\u597d\u8bf4\uff0c\u4e0d\u5fc5\u52a8\u6012\u3002\u201d \u3000\u3000\u6e29\u65b9\u4e49\u6012\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u590f\u96ea\u5b9c\u662f\u4f60\u751a\u4e48\u4eba\uff1f\u4ed6\u5728\u751a\u4e48\u5730\u65b9\uff1f\u4f60\u662f\u4ed6\u6d3e\u6765\u7684\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u4ece\u6ca1\u89c1\u8fc7\u91d1\u86c7\u90ce\u541b\u7684\u9762\uff0c\u4ed6\u600e\u4f1a\u6d3e\u6211\u6765\uff1f\u201d \u3000\u3000\u6e29\u65b9\u5c71\u9053\uff1a\u201c\u8fd9\u8bdd\u5f53\u771f\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5e72\u4e48\u9a97\u4f60\uff1f\u665a\u8f88\u5728\u8862\u6c5f\u4e4b\u4e2d\uff0c\u65e0\u610f\u4e0e\u8fd9\u4f4d\u6e29\u5144\u5f1f\u76f8\u9047\uff0c\u627f\u4ed6\u77a7\u5f97\u8d77\uff0c\u7ed3\u4ea4\u4e3a\u53cb\uff0c\u8fd9\u8ddf\u91d1\u86c7\u94f6\u86c7\u6709\u751a\u4e48\u5e72\u7cfb\uff1f\u201d \u3000\u3000\u4e94\u8001\u9762\u8272\u7a0d\u548c\uff0c\u4f46\u4ecd\u5341\u5206\u6000\u7591\u3002\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u4f60\u4e0d\u628a\u91d1\u86c7\u5978\u8d3c\u85cf\u8eab\u4e4b\u6240\u8bf4\u51fa\u6765\uff0c\u4eca\u65e5\u83ab\u60f3\u79bb\u5f00\u77f3\u6881\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u51ed\u4f60\u4eec\u8fd9\u70b9\u529f\u592b\u60f3\u6263\u7559\u6211\uff0c\u53ea\u6015\u4e0d\u80fd\u3002\u201d\u542c\u4ed6\u4eec\u53e3\u53e3\u58f0\u58f0\u7684\u628a\u91d1\u86c7\u90ce\u541b\u53eb\u4f5c\u201c\u91d1\u86c7\u5978\u8d3c\u201d\uff0c\u66f4\u662f\u8bf4\u4e0d\u51fa\u7684\u6c14\u607c\uff0c\u4f46\u9762\u5b50\u4ecd\u5f88\u606d\u8c28\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u4e0e\u91d1\u86c7\u90ce\u541b\u65e0\u4eb2\u65e0\u6545\uff0c\u8fde\u9762\u4e5f\u6ca1\u6709\u4f1a\u8fc7\u3002\u4e0d\u8fc7\u4ed6\u5728\u54ea\u91cc\uff0c\u6211\u5012\u4e5f\u77e5\u9053\uff0c\u5c31\u53ea\u6015\u8fd9\u91cc\u6ca1\u4e00\u4e2a\u6562\u53bb\u89c1\u4ed6\u3002\u201d \u3000\u3000\u6e29\u6c0f\u4e94\u8001\u6012\u706b\u4e0a\u51b2\uff0c\u7eb7\u7eb7\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u6562\uff1f\u201d\u201c\u8fd9\u5341\u591a\u5e74\u6765\uff0c\u6211\u4eec\u54ea\u4e00\u5929\u4e0d\u5728\u627e\u4ed6\uff1f\u201d\u201c\u8fd9\u5978\u8d3c\u65e9\u5df2\u662f\u5e9f\u4eba\u4e00\u4e2a\uff0c\u53c8\u6709\u8c01\u6015\u4ed6\u4e86\uff1f\u201d\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u201d\u201c\u5feb\u8bf4\uff0c\u5feb\u8bf4\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u771f\u7684\u8981\u53bb\u89c1\u4ed6\uff1f\u201d\u6e29\u65b9\u8fbe\u8e0f\u4e0a\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u89c1\u4ed6\u6709\u751a\u4e48\u597d\uff1f\u201d\u6e29\u65b9\u8fbe\u6012\u9053\uff1a\u201c\u5c0f\u670b\u53cb\uff0c\u8c01\u8ddf\u4f60\u5f00\u73a9\u7b11\uff1f\u5feb\u7ed9\u6211\u8bf4\u51fa\u6765\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5404\u4f4d\u8eab\u5b50\u58ee\u5065\uff0c\u603b\u8fd8\u5f97\u518d\u9694\u597d\u51e0\u5e74\uff0c\u624d\u80fd\u8ddf\u4ed6\u4f1a\u9762\u3002\u4ed6\u5df2\u7ecf\u6b7b\u5566\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5404\u4eba\u5c3d\u7686\u6115\u7136\u3002\u53ea\u542c\u5f97\u6e29\u9752\u6025\u53eb\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u90a3\u4e2d\u5e74\u7f8e\u5987\u5df2\u6655\u5012\u5728\u6e29\u9752\u6000\u4e2d\uff0c\u8138\u8272\u60e8\u767d\uff0c\u8fde\u5634\u5507\u90fd\u6beb\u65e0\u8840\u8272\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u8138\u8272\u5927\u53d8\uff0c\u8fde\u9a82\uff1a\u201c\u51a4\u5b7d\u3002\u201d\u6e29\u65b9\u4e49\u5bf9\u6e29\u9752\u9053\uff1a\u201c\u9752\u9752\uff0c\u5feb\u628a\u4f60\u5988\u6276\u8fdb\u53bb\uff0c\u522b\u4e22\u4e11\u5566\uff0c\u8ba9\u4eba\u5bb6\u7b11\u8bdd\u3002\u201d\u6e29\u9752\u54c7\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e22\u751a\u4e48\u4e11\uff1f\u5988\u5988\u542c\u5230\u7238\u7238\u6b7b\u4e86\uff0c\u81ea\u7136\u8981\u4f24\u5fc3\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u5403\u4e00\u60ca\uff1a\u201c\u4ed6\u5988\u5988\u662f\u91d1\u86c7\u90ce\u541b\u7684\u59bb\u5b50\uff1f\u6e29\u9752\u662f\u4ed6\u7684\u513f\u5b50\uff1f\u201d \u3000\u3000\u6e29\u65b9\u4e49\u542c\u5f97\u6e29\u9752\u51fa\u8a00\u51b2\u649e\uff0c\u66f4\u5728\u5916\u4eba\u4e4b\u524d\u5410\u9732\u4e86\u6e29\u95e8\u8fd9\u4ef6\u5947\u803b\u5927\u8fb1\uff0c\u7259\u9f7f\u54ac\u5f97\u683c\u683c\u76f4\u54cd\uff0c\u5bf9\u6e29\u65b9\u5c71\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u4f60\u518d\u5ba0\u8fd9\u5a03\u5a03\uff0c\u6211\u53ef\u8981\u7ba1\u4e86\u3002\u201d\u6e29\u65b9\u5c71\u5411\u6e29\u9752\u65a5\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7238\u7238\uff1f \u3000\u3000\u5c0f\u5b69\u5b50\u80e1\u8a00\u4e71\u8bed\u3002\u8fd8\u4e0d\u5feb\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u6e29\u9752\u6276\u7740\u6bcd\u4eb2\uff0c\u6162\u6162\u5165\u5185\u3002\u90a3\u7f8e\u5987\u60a0\u60a0\u9192\u8f6c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8bf7\u8881\u76f8\u516c\u660e\u665a\u6765\u89c1\u6211\uff0c\u6211\u6709\u8bdd\u95ee\u4ed6\u3002\u201d\u6e29\u9752\u70b9\u5934\uff0c\u56de\u5934\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u5929\uff0c\u660e\u665a\u4f60\u518d\u6765\u76d7\u5427\u3002\u4f60\u5c31\u662f\u5e2e\u7740\u4eba\u5bb6\u3002 \u3000\u3000\u4f60\uff0c\u4f60\u2026\u2026\u53d1\u7684\u8a93\u90fd\u662f\u9a97\u4eba\u7684\uff01\u201d\u6068\u6068\u7684\u5411\u5b89\u5c0f\u6167\u671b\u4e86\u4e00\u773c\uff0c\u6276\u7740\u6bcd\u4eb2\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5bf9\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d\u4e24\u4eba\u5411\u5916\u8d70\u51fa\u3002\u6e29\u65b9\u609f\u7ad9\u5728\u95e8\u53e3\uff0c\u53cc\u624b\u4e00\u62e6\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u6162\u8d70\uff0c\u8fd8\u6709\u8bdd\u95ee\u4f60\u3002\u201d\u8881\u627f\u5fd7\u4e00\u62f1\u624b\u9053\uff1a\u201c\u4eca\u65e5\u5df2\u665a\uff0c\u660e\u65e5\u665a\u8f88\u518d\u6765\u5949\u8bbf\u3002\u201d\u6e29\u65b9\u609f\u9053\uff1a\u201c\u90a3\u91d1\u86c7\u5978\u8d3c\u6b7b\u5728\u751a\u4e48\u5730\u65b9\uff1f\u4ed6\u6b7b\u65f6\u6709\u8c01\u89c1\u5230\u4e86\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u60f3\u8d77\u90a3\u665a\u5f20\u6625\u4e5d\u523a\u6b7b\u4ed6\u79c3\u5934\u5e08\u5f1f\u7684\u60e8\u72b6\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u4eec\u77f3\u6881\u6d3e\u597d\u4e0d\u5978\u8bc8\u51f6\u9669\uff0c\u90a3\u665a\u5728\u534e\u5c71\u4e4b\u4e0a\uff0c\u6211\u4fbf\u9669\u4e9b\u6b7b\u5728\u4f60\u4eec\u624b\u4e2d\uff0c\u53c8\u4f55\u5fc5\u8ddf\u4f60\u4eec\u8bf4\u771f\u8bdd\uff1f\u4f55\u51b5\u4f60\u4eec\u89ca\u89ce\u91d1\u86c7\u90ce\u541b\u7684\u9057\u7269\uff0c\u6211\u66f4\u4e0d\u80fd\u8bf4\u3002\u201d\u4fbf\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8f97\u8f6c\u542c\u670b\u53cb\u8bf4\u8d77\u7684\uff0c\u91d1\u86c7\u90ce\u541b\u662f\u6b7b\u5728\u5e7f\u4e1c\u6d77\u5916\u7684\u4e00\u4e2a\u8352\u5c9b\u4e4b\u4e0a\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7ae5\u5fc3\u5ffd\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u8d35\u6d3e\u6709\u4e00\u4e2a\u7626\u5b50\uff0c\u53eb\u4f5c\u5f20\u6625\u4e5d\uff0c\u8fd8\u6709\u4e00\u4e2a\u79c3\u5934\uff0c\u662f\u4e0d\u662f\uff1f\u91d1\u86c7\u90ce\u541b\u7684\u4e0b\u843d\uff0c\u4ed6\u5e08\u5144\u5f1f\u4fe9\u77e5\u9053\u5f97\u6e05\u6e05\u695a\u695a\u3002\u53ea\u6d88\u53eb\u4ed6\u4e8c\u4eba\u6765\u4e00\u95ee\uff0c\u5c31\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\uff0c\u7528\u4e0d\u7740\u6765\u95ee\u6211\u3002\u201d \u3000\u3000\u6e29\u6c0f\u4e94\u8001\u9762\u9762\u76f8\u89d1\uff0c\u900f\u7740\u5341\u5206\u8be7\u5f02\u3002\u6e29\u65b9\u4e49\u9053\uff1a\u201c\u5f20\u6625\u4e5d\u548c\u6c5f\u79c3\u5934\uff1f\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u4e0d\u77e5\u6b7b\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u4ed6\u5988\u7684\uff0c\u56de\u6765\u4e0d\u5265\u4ed6\u4eec\u7684\u76ae\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u9053\uff1a\u201c\u4f60\u4eec\u5230\u5e7f\u4e1c\u6d77\u5916\u51e0\u5343\u4e2a\u8352\u5c9b\u4e0a\u53bb\u7ec6\u7ec6\u7684\u627e\u5427\uff01\u8981\u4e0d\u7136\uff0c\u4eb2\u81ea\u53bb\u95ee\u5f20\u6625\u4e5d\u548c\u90a3\u79c3\u5934\u4e5f\u597d\u3002\u201d\u5411\u4f17\u4eba\u62b1\u62f3\u9053\uff1a\u201c\u665a\u8f88\u5931\u966a\u3002\u201d \u3000\u3000\u6e29\u65b9\u609f\u9053\uff1a\u201c\u5fd9\u751a\u4e48\uff1f\u201d\u4ed6\u5b9a\u8981\u95ee\u4e2a\u6e05\u695a\uff0c\u4f38\u81c2\u62e6\u4f4f\u3002\u8881\u627f\u5fd7\u4f38\u638c\u8f7b\u8f7b\u5411\u4ed6\u624b\u81c2\u63a8\u53bb\u3002\u6e29\u65b9\u609f\u624b\u8155\u4e00\u52fe\uff0c\u8981\u65bd\u5c55\u64d2\u62ff\u624b\u6cd5\u62ff\u4ed6\u624b\u8155\u3002\u54ea\u77e5\u8881\u627f\u5fd7\u4e0d\u60f3\u518d\u548c\u4eba\u52a8\u624b\uff0c\u8fd9\u4e00\u62db\u5176\u5b9e\u662f\u865a\u62db\uff0c\u5bf9\u65b9\u624b\u4e00\u52a8\uff0c\u5de6\u65b9\u9732\u51fa\u7a7a\u9699\uff0c\u4ed6\u62c9\u4f4f\u5b89\u5c0f\u6167\u7684\u624b\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u6070\u597d\u4ece\u7a7a\u9699\u4e2d\u7a7f\u4e86\u51fa\u53bb\uff0c\u8fde\u6e29\u65b9\u609f\u7684\u8863\u670d\u4e5f\u6ca1\u78b0\u5230\u3002 \u3000\u3000\u6e29\u65b9\u609f\u5927\u6012\uff0c\u53f3\u624b\u5728\u8170\u95f4\u4e00\u6296\uff0c\u5df2\u628a\u4e00\u6761\u725b\u76ae\u8f6f\u97ad\u89e3\u4e86\u4e0b\u6765\uff0c\u4e00\u62db\u201c\u9a8f\u9a6c\u8131\u7f30\u201d\uff0c\u5411\u4ed6\u540e\u5fc3\u6253\u5230\u3002\u6b66\u6797\u4e2d\u7684\u8f6f\u97ad\u6709\u7684\u4ee5\u7cbe\u94a2\u6240\u94f8\uff0c\u8003\u7a76\u7684\u66f4\u4ee5\u91d1\u4e1d\u7ed5\u6210\uff0c\u4f46\u6e29\u65b9\u609f\u5185\u529f\u7cbe\u6e5b\uff0c\u6240\u7528\u5175\u5203\u5c31\u53ea\u5e73\u5e73\u5e38\u5e38\u7684\u4e00\u6761\u76ae\u97ad\u3002\u76ae\u97ad\u53c8\u97e7\u53c8\u8f6f\uff0c\u5728\u4ed6\u624b\u91cc\u4f7f\u5f00\u6765\u5982\u81c2\u4f7f\u6307\uff0c\u5185\u52b2\u5230\u5904\uff0c\u6bd4\u4e4b\u4e94\u91d1\u8f6f\u97ad\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u5f97\u80cc\u540e\u98ce\u58f0\uff0c\u62c9\u7740\u5b89\u5c0f\u6167\u5411\u524d\u76f4\u7a9c\uff0c\u76ae\u97ad\u843d\u7a7a\uff0c\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u52b2\u9053\u51cc\u5389\uff0c\u77e5\u662f\u4e00\u4ef6\u5389\u5bb3\u7684\u8f6f\u5175\u5668\uff0c\u4ed6\u5934\u4e5f\u4e0d\u56de\uff0c\u5411\u5899\u5934\u7eb5\u53bb\u3002 \u3000\u3000\u6e29\u65b9\u609f\u5728\u8fd9\u6761\u8f6f\u97ad\u4e0a\u4e0b\u8fc7\u6570\u5341\u5e74\u7684\u529f\u592b\uff0c\u88ab\u4ed6\u8fd9\u4e48\u8f7b\u6613\u907f\u5f00\uff0c\u5c82\u80af\u5c31\u6b64\u7f62\u624b\uff1f\u53f3\u624b\u6325\u51fa\uff0c\u5708\u51fa\u4e00\u4e2a\u97ad\u82b1\uff0c\u5411\u5b89\u5c0f\u6167\u811a\u4e0a\u5377\u6765\u3002\u8fd9\u4e00\u4e0b\u907f\u5b9e\u5c31\u865a\uff0c\u77e5\u9053\u8fd9\u5c11\u5973\u529f\u529b\u4e0d\u9ad8\uff0c\u8fd9\u4e00\u62db\u5b9a\u7136\u8eb2\u4e0d\u5f00\uff0c\u5982\u628a\u5979\u62c9\u4e86\u4e0b\u6765\uff0c\u7b49\u4e8e\u662f\u622a\u4f4f\u4e86\u8881\u627f\u5fd7\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u5f97\u98ce\u58f0\uff0c\u5de6\u624b\u64a9\u51fa\uff0c\u5e26\u4f4f\u97ad\u68a2\uff0c\u4ed6\u4e0a\u8dc3\u4e4b\u52bf\u4e0d\u505c\uff0c\u5de6\u624b\u4f7f\u52b2\uff0c\u7adf\u5c06\u6e29\u65b9\u609f\u63d0\u4e86\u8d77\u6765\u3002\u6e29\u5bb6\u4f17\u4eba\u4e00\u89c1\uff0c\u65e0\u4e0d\u5927\u9a87\u3002 \u3000\u3000\u6e29\u65b9\u65bd\u8981\u6551\u4e94\u5f1f\uff0c\u53f3\u624b\u6025\u626c\uff0c\u4e24\u67c4\u98de\u5200\u545c\u545c\u53d1\u58f0\uff0c\u5411\u8881\u627f\u5fd7\u540e\u5fc3\u98de\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5de6\u624b\u677e\u5f00\u4e86\u76ae\u97ad\u97ad\u68a2\uff0c\u62c9\u7740\u5b89\u5c0f\u6167\u5411\u5899\u5916\u8dc3\u51fa\uff0c\u542c\u5f97\u98de\u5200\u4e4b\u58f0\uff0c\u7adf\u4e0d\u56de\u5934\uff0c\u811a\u5fc3\u5728\u98de\u5200\u5200\u8eab\u8f7b\u8f7b\u4e00\u6321\uff0c\u98de\u5200\u7acb\u65f6\u5012\u8f6c\u3002 \u3000\u3000\u6e29\u65b9\u609f\u811a\u521a\u843d\u5730\uff0c\u4e24\u67c4\u98de\u5200\u5df2\u5f53\u5934\u5c04\u843d\u3002\u4ed6\u4e0d\u53ca\u8d77\u8eab\uff0c\u6296\u8d77\u76ae\u97ad\uff0c\u60f3\u6253\u5f00\u98de\u5200\uff0c\u54ea\u77e5\u76ae\u97ad\u5ffd\u7136\u5bf8\u5bf8\u65ad\u88c2\uff0c\u539f\u6765\u521a\u624d\u8881\u627f\u5fd7\u5728\u534a\u7a7a\u4e2d\u63d0\u8d77\u6e29\u65b9\u609f\uff0c\u5b9e\u5df2\u4f7f\u4e0a\u4e86\u6df7\u5143\u529f\u7684\u4e0a\u4e58\u5185\u52b2\uff0c\u5426\u5219\u4ed6\u5728\u534a\u7a7a\u4e2d\u65e0\u4ece\u501f\u529b\uff0c\u5982\u4f55\u63d0\u5f97\u8d77\u4e00\u4e2a\u4e00\u767e\u51e0\u5341\u65a4\u7684\u5927\u6c49\uff1f \u3000\u3000\u8fd9\u6df7\u5143\u52b2\u4f20\u5230\u76ae\u97ad\u4e4b\u4e0a\uff0c\u7adf\u7136\u5c06\u97ad\u5b50\u626f\u65ad\u4e86\u3002\u6e29\u65b9\u609f\u5927\u60ca\uff0c\u4e00\u4e2a\u201c\u61d2\u9a74\u6253\u6eda\u201d\uff0c\u6eda\u4e86\u5f00\u53bb\uff0c\u4f46\u4e00\u67c4\u98de\u5200\u5df2\u628a\u4ed6\u8863\u895f\u523a\u7834\u3002\u4ed6\u7ad9\u8d77\u6765\u65f6\u4e00\u8eab\u51b7\u6c57\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u4e0d\u4f4f\u6447\u5934\u3002\u4e94\u8001\u5747\u662f\u6697\u6697\u7eb3\u7f55\u3002\u6e29\u65b9\u4e49\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u8fc7\u5eff\u5c81\u5de6\u53f3\uff0c\u5c31\u7b97\u5728\u5a18\u80ce\u91cc\u8d77\u59cb\u7ec3\u6b66\uff0c\u4e5f\u4e0d\u8fc7\u5eff\u5e74\u529f\u529b\uff0c\u600e\u5730\u624b\u4e0b\u7adf\u5982\u6b64\u4e86\u5f97\uff1f\u201d\u6e29\u65b9\u5c71\u9053\uff1a\u201c\u91d1\u86c7\u5978\u8d3c\u8fd9\u822c\u5389\u5bb3\uff0c\u4e5f\u683d\u5728\u54b1\u4eec\u624b\u91cc\u3002\u8fd9\u5c0f\u5b50\u660e\u665a\u518d\u6765\uff0c\u54b1\u4eec\u597d\u597d\u7684\u5bf9\u4ed8\u4ed6\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u5b89\u5c0f\u6167\u56de\u5230\u501f\u5bbf\u7684\u519c\u5bb6\u3002\u5b89\u5c0f\u6167\u628a\u8fd9\u4f4d\u627f\u5fd7\u5927\u54e5\u6ee1\u53e3\u79f0\u8d5e\uff0c\u4f69\u670d\u5f97\u4e86\u4e0d\u5f97\uff0c\u8bf4\u9053\uff1a\u201c\u5d14\u5e08\u54e5\u8001\u662f\u5938\u4ed6\u5e08\u7236\u600e\u4e48\u4e86\u4e0d\u8d77\uff0c\u6211\u770b\u4ed6\u5e08\u7236\u4e00\u5b9a\u53ca\u4e0d\u4e0a\u4f60\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5d14\u5e08\u54e5\u53eb\u751a\u4e48\u540d\u5b57\uff0c\u4ed6\u5e08\u7236\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u5b89\u5c0f\u6167\u9053\uff1a\u201c\u4ed6\u53eb\u5d14\u5e0c\u654f\uff0c\u5916\u53f7\u53eb\u751a\u4e48\u4f0f\u864e\u91d1\u521a\u3002\u4ed6\u5e08\u7236\u662f\u534e\u5c71\u6d3e\u7a46\u8001\u7956\u5e08\u7684\u5f92\u5f1f\uff0c\u5916\u53f7\u53eb\u2018\u94dc\u7b14\u94c1\u7b97\u76d8\u2019\u3002\u6211\u542c\u4e86\u8fd9\u5916\u53f7\u5c31\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u4e5f\u4ece\u6765\u6ca1\u95ee\u5d14\u5e08\u54e5\u4ed6\u5e08\u7236\u53eb\u751a\u4e48\u540d\u5b57\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u662f\u5927\u5e08\u54e5\u7684\u5f92\u5f1f\uff0c\u4ed6\u8fd8\u5f97\u53eb\u6211\u58f0\u5e08\u53d4\u5462\u3002\u201d\u4e5f\u4e0d\u4e0e\u5979\u8bf4\u7a7f\uff0c\u4e24\u4eba\u5404\u81ea\u5b89\u5bdd\u3002 \u3000\u3000\u6b21\u65e5\u665a\u4e0a\uff0c\u8881\u627f\u5fd7\u53eb\u5b89\u5c0f\u6167\u5728\u519c\u5bb6\u7b49\u4ed6\uff0c\u4e0d\u8981\u540c\u53bb\u3002\u5b89\u5c0f\u6167\u77e5\u9053\u81ea\u5df1\u529f\u592b\u5dee\uff0c\u53ea\u6709\u788d\u624b\u788d\u811a\uff0c\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u53cd\u8981\u4ed6\u5206\u5fc3\u7167\u987e\uff0c\u867d\u7136\u4e0d\u5927\u613f\u610f\uff0c\u8fd8\u662f\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b49\u5230\u4e8c\u66f4\u5929\u65f6\uff0c\u53c8\u5230\u6e29\u5bb6\uff0c\u53ea\u89c1\u5230\u5904\u9ed1\u6c89\u6c89\u7684\u706f\u70db\u65e0\u5149\uff0c\u6b63\u8981\u98de\u8eab\u5165\u5185\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u8f7b\u8f7b\u4f20\u6765\u4e09\u58f0\u7bab\u58f0\uff0c\u90a3\u6d1e\u7bab\u4e00\u5439\u5373\u505c\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u53c8\u662f\u4e09\u58f0\u3002\u8881\u627f\u5fd7\u5fc3\u5ff5\u4e00\u52a8\uff0c\u77e5\u662f\u6e29\u9752\u4ee5\u7bab\u76f8\u547c\uff0c\u5fc3\u60f3\u6e29\u6c0f\u4e94\u8001\u6781\u51f6\u6076\uff0c\u6e29\u9752\u5374\u5bf9\u81ea\u5df1\u5c1a\u6709\u7ed3\u4e49\u4e4b\u60c5\uff0c\u6700\u597d\u80fd\u529d\u5f97\u5979\u4ea4\u8fd8\u9ec4\u91d1\uff0c\u4e0d\u5fc5\u518d\u52a8\u624b\u4e86\uff0c\u4e8e\u662f\u5faa\u7740\u7bab\u58f0\uff0c\u5f80\u73ab\u7470\u5c71\u5761\u4e0a\u5954\u53bb\u3002 \u3000\u3000\u5230\u5f97\u5c71\u5761\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u89c1\u4ead\u4e2d\u5750\u7740\u4e24\u4eba\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u4e91\u9b13\u96fe\u9b1f\uff0c\u4e24\u4e2a\u90fd\u662f\u5973\u5b50\uff0c\u5f53\u5373\u505c\u4e86\u811a\u6b65\uff0c\u5fc3\u60f3\uff1a\u201c\u9752\u5f1f\u4e0d\u5728\u8fd9\u91cc\uff01\u201d\u53ea\u89c1\u4e00\u4e2a\u5973\u5b50\u4e3e\u8d77\u6d1e\u7bab\u5439\u594f\uff0c\u542c\u90a3\u66f2\u8c03\uff0c\u4fbf\u662f\u6e29\u9752\u90a3\u5929\u5439\u8fc7\u7684\u90a3\u9996\u97f3\u8c03\u51c4\u51c9\u7684\u66f2\u5b50\uff0c\u5fcd\u4e0d\u4f4f\u8d70\u8fd1\u51e0\u6b65\uff0c\u60f3\u770b\u6e05\u695a\u662f\u8c01\u3002 \u3000\u3000\u90a3\u624b\u6301\u6d1e\u7bab\u7684\u5973\u5b50\u51fa\u4ead\u76f8\u8fce\uff0c\u4f4e\u4f4e\u53eb\u4e86\u58f0\uff1a\u201c\u5927\u54e5\uff01\u201d\u8881\u627f\u5fd7\u5927\u5403\u4e00\u60ca\uff0c\u6eb6\u6eb6\u6708\u8272\u4e0b\u4e00\u5f20\u4fcf\u4e3d\u9762\u5e9e\uff0c\u7adf\u7136\u4fbf\u662f\u6e29\u9752\u3002\u4ed6\u767b\u65f6\u5446\u4e86\uff0c\u9694\u4e86\u534a\u664c\uff0c\u624d\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u6e29\u9752\u6d45\u6d45\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5176\u5b9e\u662f\u5973\u5b50\uff0c\u4e00\u76f4\u7792\u7740\u5927\u54e5\uff0c\u8fd8\u8bf7\u52ff\u602a\uff01\u201d\u8bf4\u7740\u6df1\u6df1\u4e00\u4e2a\u4e07\u798f\u3002\u8881\u627f\u5fd7\u8fd8\u4e86\u4e00\u63d6\uff0c\u4ee5\u524d\u8bb8\u591a\u7591\u8651\u4e4b\u5904\uff0c\u8c41\u7136\u987f\u89e3\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u4e00\u76f4\u602a\u5979\u8102\u7c89\u6c14\u592a\u91cd\uff0c\u53c8\u8fc7\u4e8e\u5c0f\u6027\u513f\uff0c\u6ca1\u4e08\u592b\u6c14\u6982\uff0c\u539f\u6765\u7adf\u662f\u5973\u5b50\u3002\u5509\uff0c\u6211\u7adf\u662f\u83ab\u540d\u5176\u5999\u7684\u8ddf\u4e00\u4e2a\u59d1\u5a18\u62dc\u4e86\u628a\u5b50\uff0c\u8fd9\u53ef\u4ece\u54ea\u91cc\u8bf4\u8d77\uff1f\u201d \u3000\u3000\u6e29\u9752\u9053\uff1a\u201c\u6211\u53eb\u6e29\u9752\u9752\uff0c\u4e0a\u6b21\u5bf9\u4f60\u8bf4\u65f6\u5c11\u4e86\u4e00\u4e2a\u9752\u5b57\u3002\u201d\u8bf4\u7740\u62bf\u5634\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u5462\uff0c\u6211\u8be5\u53eb\u590f\u9752\u9752\u624d\u662f\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u6539\u7a7f\u5973\u88c5\uff0c\u79c0\u7709\u51e4\u76ee\uff0c\u7389\u988a\u6a31\u5507\uff0c\u7adf\u662f\u4e00\u4e2a\u7f8e\u8c8c\u4f73\u4eba\uff0c\u5fc3\u4e2d\u6697\u9a82\u81ea\u5df1\u80e1\u6d82\uff0c\u8fd9\u4e48\u4e00\u4e2a\u7f8e\u4eba\u8c01\u90fd\u770b\u5f97\u51fa\u6765\uff0c\u81ea\u5df1\u7adf\u4f1a\u5982\u6b64\u8001\u5b9e\uff0c\u88ab\u5979\u7792\u4e86\u8fd9\u8bb8\u591a\u5929\u3002\u8981\u77e5\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u9664\u4e86\u5a74\u513f\u4e4b\u65f6\uff0c\u53ea\u548c\u5b89\u5927\u5a18\u548c\u5b89\u5c0f\u6167\u540c\u5904\u8fc7\u6570\u65e5\uff0c\u6b64\u540e\u5341\u591a\u5e74\u5728\u534e\u5c71\u7edd\u9876\u7ec3\u6b66\uff0c\u4ece\u672a\u89c1\u8fc7\u5973\u5b50\u3002\u540e\u6765\u5728\u95ef\u738b\u519b\u4e2d\u89c1\u5230\u674e\u5ca9\u4e4b\u59bb\u7ea2\u5a18\u5b50\uff0c\u8fd9\u4f4d\u5973\u4fa0\u8c6a\u8fc8\u723d\u6717\uff0c\u4e0e\u7537\u5b50\u65e0\u5f02\u3002\u56e0\u6b64\u4e8e\u7537\u5973\u4e4b\u522b\uff0c\u4ed6\u5b9e\u662f\u6d51\u6d51\u5669\u5669\uff0c\u8ba4\u4e0d\u51fa\u6e29\u9752\u9752\u5973\u626e\u7537\u88c5\u3002 \u3000\u3000\u6e29\u9752\u9752\u9053\uff1a\u201c\u6211\u5988\u5728\u8fd9\u91cc\uff0c\u5979\u6709\u8bdd\u8981\u95ee\u4f60\u3002\u201d\u8881\u627f\u5fd7\u8d70\u8fdb\u4ead\u53bb\uff0c\u4f5c\u63d6\u884c\u793c\uff0c\u53eb\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u5c0f\u4f84\u8881\u627f\u5fd7\u62dc\u89c1\u3002\u201d\u90a3\u4e2d\u5e74\u7f8e\u5987\u7ad9\u8d77\u8eab\u6765\u56de\u793c\uff0c\u8fde\u8bf4\uff1a\u201c\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u53cc\u76ee\u7ea2\u80bf\uff0c\u8138\u8272\u6194\u60b4\uff0c\u77e5\u5979\u4f24\u5fc3\u96be\u53d7\uff0c\u9ed8\u9ed8\u65e0\u8a00\u7684\u5750\u4e86\u4e0b\u6765\uff0c\u5bfb\u601d\uff1a\u201c\u542c\u9752\u9752\u8bf4\uff0c\u5979\u6bcd\u4eb2\u662f\u7ed9\u4eba\u5f3a\u5978\u624d\u751f\u4e0b\u5979\u6765\uff0c\u90a3\u4eba\u81ea\u662f\u91d1\u86c7\u90ce\u541b\u4e86\u3002\u4e94\u8001\u5bf9\u91d1\u86c7\u90ce\u541b\u6df1\u6076\u75db\u7edd\uff0c\u9752\u9752\u63d0\u4e00\u58f0\u7238\u7238\uff0c\u5c31\u88ab\u5979\u4e8c\u7237\u7237\u559d\u65a5\u6012\u9a82\u3002\u53ef\u662f\u5979\u5988\u5988\u542c\u5f97\u91d1\u86c7\u90ce\u541b\u901d\u4e16\uff0c\u7acb\u5373\u6655\u5012\uff0c\u4f24\u5fc3\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u5bf9\u4ed6\u663e\u7136\u60c5\u610f\u5f88\u6df1\uff0c\u5176\u4e2d\u53ea\u6015\u53e6\u6709\u522b\u60c5\u3002\u201d \u3000\u3000\u9752\u9752\u7684\u6bcd\u4eb2\u5446\u4e86\u4e00\u9635\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u662f\u771f\u7684\u6b7b\u4e86\uff1f\u8881\u76f8\u516c\u53ef\u4eb2\u773c\u89c1\u5230\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\u3002\u5979\u53c8\u9053\uff1a\u201c\u8881\u76f8\u516c\u5bf9\u6211\u9752\u9752\u5f88\u597d\uff0c\u6211\u662f\u77e5\u9053\u7684\u3002\u6211\u51b3\u4e0d\u50cf\u6211\u7239\u7239\u4e0e\u53d4\u4f2f\u4eec\u90a3\u6837\uff0c\u5f53\u4f60\u662f\u4ec7\u4eba\uff0c\u8bf7\u2026\u2026\u8bf7\u4f60\u628a\u4ed6\u6b7b\u65f6\u7684\u60c5\u5f62\u89c1\u544a\u3002\u662f\u8c01\u5bb3\u6b7b\u4ed6\u7684\uff1f\u4ed6\u2026\u2026\u4ed6\u6b7b\u5f97\u5f88\u82e6\u5417\uff1f\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u53d1\u98a4\uff0c\u6cea\u73e0\u6251\u7c0c\u7c0c\u7684\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5bf9\u91d1\u86c7\u90ce\u541b\u7684\u5fc3\u60c5\uff0c\u5b9e\u5728\u81ea\u5df1\u4e5f\u4e0d\u5927\u660e\u767d\uff0c\u542c\u5e08\u7236\u4e0e\u6728\u6851\u9053\u4eba\u8bf4\uff0c\u8fd9\u4eba\u813e\u6c14\u53e4\u602a\uff0c\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u4e3a\u4eba\u4ecb\u4e8e\u6b63\u90aa\u4e4b\u95f4\u3002\u4ed6\u5b89\u6392\u94c1\u76d2\u5f29\u7bad\u3001\u79d8\u7b08\u5267\u6bd2\uff0c\u786e\u662f\u7528\u5fc3\u9669\u72e0\uff0c\u5b9e\u975e\u6b63\u4eba\u7aef\u58eb\u3002\u53ef\u662f\u81ea\u4ece\u7814\u4e60\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u7684\u6b66\u529f\u4e4b\u540e\uff0c\u5bf9\u8fd9\u4f4d\u7edd\u4e16\u7684\u5947\u624d\u4e0d\u7981\u6697\u6697\u94a6\u4f69\uff0c\u5728\u5185\u5fc3\u6df1\u5904\uff0c\u4e0d\u81ea\u89c9\u7684\u5df2\u628a\u4ed6\u5f53\u4f5c\u5e08\u7236\u4e4b\u4e00\u3002\u6628\u665a\u542c\u5230\u6e29\u6c0f\u4e94\u8001\u6012\u65a5\u91d1\u86c7\u90ce\u541b\u4e3a\u201c\u5978\u8d3c\u201d\uff0c\u5fc3\u4e2d\u8bf4\u4e0d\u51fa\u7684\u6124\u6012\uff0c\u4e8b\u540e\u60f3\u53ca\uff0c\u4e5f\u89c9\u5947\u602a\u3002\u8fd9\u65f6\u542c\u9752\u9752\u4e4b\u6bcd\u95ee\u8d77\uff0c\u4fbf\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u6211\u6ca1\u89c1\u8fc7\u9762\uff0c\u4e0d\u8fc7\u8bf4\u8d77\u6765\uff0c\u8fd9\u4f4d\u524d\u8f88\u548c\u6211\u5b9e\u6709\u5e08\u5f92\u4e4b\u4efd\uff0c\u6211\u8bb8\u591a\u6b66\u529f\u662f\u4ece\u4ed6\u90a3\u91cc\u5b66\u7684\u3002\u8fd9\u4f4d\u524d\u8f88\u6b7b\u540e\u7684\u60c5\u5f62\uff0c\u6055\u6211\u4e0d\u4fbf\u5bf9\u4f2f\u6bcd\u8bf4\uff0c\u53ea\u6015\u6709\u574f\u4eba\u8981\u53bb\u53d1\u6398\u4ed6\u7684\u9ab8\u9aa8\u3002\u201d \u3000\u3000\u9752\u9752\u4e4b\u6bcd\u8eab\u5b50\u4e00\u6643\uff0c\u5411\u540e\u4fbf\u5012\u3002\u9752\u9752\u8fde\u5fd9\u62b1\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u522b\u4f24\u5fc3\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u9752\u9752\u4e4b\u6bcd\u60a0\u60a0\u9192\u6765\uff0c\u54ed\u9053\uff1a\u201c\u6211\u82e6\u82e6\u7b49\u4e86\u5341\u516b\u5e74\uff0c\u53ea\u76fc\u4ed6\u6765\u63a5\u6211\u4eec\u5a18\u513f\u79bb\u5f00\u8fd9\u5730\u65b9\uff0c\u54ea\u77e5\u4ed6\u7adf\u4e00\u4e2a\u4eba\u5148\u53bb\u4e86\u3002\u9752\u9752\u8fde\u5979\u7238\u7238\u4e00\u9762\u4e5f\u89c1\u4e0d\u7740\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f2f\u6bcd\u4e0d\u5fc5\u96be\u8fc7\u3002\u590f\u8001\u524d\u8f88\u73b0\u4eca\u5b89\u5b89\u7a33\u7a33\u7684\u957f\u7720\u5730\u4e0b\u3002\u4ed6\u7684\u9ab8\u9aa8\u5c0f\u4f84\u5df2\u7ecf\u597d\u597d\u5b89\u846c\u4e86\u3002\u201d\u53c8\u9053\uff1a\u201c\u590f\u524d\u8f88\u6b7b\u65f6\u8eab\u5b50\u7aef\u5750\uff0c\u901d\u4e16\u4e4b\u524d\u53c8\u4f5c\u4e86\u5404\u79cd\u5b89\u6392\uff0c\u663e\u975e\u4ed3\u5352\u4e4b\u95f4\u7ed9\u4eba\u5bb3\u6b7b\u3002\u201d \u3000\u3000\u9752\u9752\u4e4b\u6bcd\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u8881\u76f8\u516c\u846c\u7684\uff0c\u5927\u6069\u5927\u5fb7\uff0c\u771f\u4e0d\u77e5\u600e\u6837\u62a5\u7b54\u624d\u597d\u3002\u201d\u8bf4\u7740\u7ad9\u8d77\u6765\u65bd\u4e86\u4e00\u793c\uff0c\u53c8\u9053\uff1a\u201c\u9752\u9752\uff0c\u5feb\u7ed9\u8881\u5927\u54e5\u78d5\u5934\u3002\u201d\u9752\u9752\u62dc\u5012\u5728\u5730\uff0c\u8881\u627f\u5fd7\u5fd9\u4e5f\u8dea\u4e0b\u8fd8\u793c\u3002\u9752\u9752\u4e4b\u6bcd\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u53ef\u6709\u751a\u4e48\u9057\u4e66\u7ed9\u6211\u4eec\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u60f3\u8d77\u79d8\u7b08\u5c01\u9762\u5939\u5c42\u4e2d\u7684\u5730\u56fe\u548c\u56fe\u4e0a\u5b57\u6837\uff1a\u201c\u5f97\u5b9d\u4e4b\u4eba\uff0c\u52a1\u8bf7\u8d74\u6d59\u6c5f\u8862\u5dde\u77f3\u6881\uff0c\u5bfb\u8bbf\u6e29\u4eea\uff0c\u8d60\u4ee5\u9ec4\u91d1\u5341\u4e07\u4e24\u3002\u201d\u5f53\u65f6\u770b\u4e86\u8fd9\u5f20\u201c\u91cd\u5b9d\u4e4b\u56fe\u201d\uff0c\u56e0\u65e0\u8d2a\u56fe\u4e4b\u5ff5\uff0c\u968f\u624b\u5728\u884c\u56ca\u4e2d\u4e00\u585e\uff0c\u6b64\u540e\u6ca1\u518d\u52a0\u7559\u610f\uff0c\u66fe\u60f3\u91d1\u86c7\u90ce\u541b\u4ee5\u65f7\u4e16\u6b66\u529f\uff0c\u7edd\u9876\u806a\u660e\uff0c\u7adf\u81f3\u4e27\u8eab\u8352\u5c71\uff0c\u9669\u4e9b\u9ab8\u9aa8\u65e0\u4eba\u6536\u6b93\uff0c\u53ea\u6015\u8fd8\u662f\u53d7\u4e86\u8fd9\u91cd\u5b9d\u4e4b\u5bb3\u3002 \u3000\u3000\u5929\u4e0b\u5947\u73cd\u5f02\u5b9d\uff0c\u65e0\u4e0d\u8db3\u62db\u5927\u7978\uff0c\u8fd9\u8bdd\u5e08\u7236\u5e38\u5e38\u63d0\u8d77\uff0c\u56e0\u6b64\u5bf9\u8fd9\u5f20\u9057\u56fe\u9887\u6709\u4e9b\u538c\u618e\u4e4b\u611f\uff0c\u8fd9\u65f6\u7ecf\u9752\u9752\u4e4b\u6bcd\u4e00\u95ee\uff0c\u8fd9\u624d\u8bb0\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4f84\u65e0\u793c\uff0c\u6597\u80c6\u8bf7\u95ee\uff0c\u4f2f\u6bcd\u7684\u95fa\u5b57\uff0c\u53ef\u662f\u4e00\u4e2a\u2018\u4eea\u2019\u5b57\uff1f\u201d \u3000\u3000\u9752\u9752\u4e4b\u6bcd\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u600e\u77e5\u9053\uff1f\u201d\u968f\u5373\u9053\uff1a\u201c\u90a3\u5b9a\u662f\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u9057\u4e66\u4e0a\u5199\u7740\u7684\u4e86\uff0c\u8881\u76f8\u516c\u53ef\u2026\u2026\u53ef\u6709\u5e26\u7740\uff1f\u201d\u795e\u60c5\u4e2d\u5145\u6ee1\u76fc\u671b\u548c\u7126\u8651\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6b63\u8981\u56de\u7b54\uff0c\u7a81\u7136\u53f3\u8db3\u4e00\u70b9\uff0c\u4ece\u4ead\u5b50\u680f\u5e72\u4e0a\u659c\u523a\u8dc3\u51fa\u3002\u6e29\u4eea\u6bcd\u5973\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u542c\u4e00\u4eba\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u8881\u627f\u5fd7\u5df2\u4f38\u624b\u4ece\u73ab\u7470\u4e1b\u4e2d\u6293\u4e86\u4e00\u4e2a\u4eba\u51fa\u6765\uff0c\u8d70\u56de\u4ead\u5b50\u3002\u90a3\u4eba\u5df2\u88ab\u4ed6\u70b9\u4e2d\u7a74\u9053\uff0c\u624b\u8db3\u8f6f\u8f6f\u7684\u5782\u4e0b\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u9752\u9752\u53eb\u9053\uff1a\u201c\u662f\u4e03\u4f2f\u4f2f\u3002\u201d\u6e29\u4eea\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u8bf7\u4f60\u653e\u4e86\u4ed6\u5427\u3002\u6e29\u5bb6\u95e8\u4e2d\uff0c\u6ca1\u4e00\u4e2a\u5f53\u6211\u4eec\u6bcd\u5973\u662f\u4eb2\u4eba\u4e86\u3002\u201d\u8881\u627f\u5fd7\u4f38\u624b\u5728\u90a3\u4eba\u8eab\u4e0a\u62cd\u634f\u51e0\u4e0b\uff0c\u89e3\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\u3002\u539f\u6765\u90a3\u4eba\u662f\u6628\u665a\u4e0e\u4ed6\u4ea4\u8fc7\u624b\u7684\u6e29\u5357\u626c\u3002\u4ed6\u662f\u6e29\u65b9\u4e49\u7684\u513f\u5b50\uff0c\u5728\u5144\u5f1f\u4e2d\u6392\u884c\u7b2c\u4e03\u3002 \u3000\u3000\u6e29\u9752\u9752\u6012\u9053\uff1a\u201c\u4e03\u4f2f\u4f2f\uff0c\u6211\u4eec\u5728\u8fd9\u91cc\u8bf4\u8bdd\uff0c\u4f60\u600e\u4e48\u6765\u5077\u542c\uff1f\u4e5f\u6ca1\u70b9\u957f\u8f88\u6837\u5b50\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u4e00\u542c\u5927\u6012\uff0c\u4fbf\u6b32\u53d1\u4f5c\uff0c\u4f46\u521a\u624d\u88ab\u8881\u627f\u5fd7\u64d2\u4f4f\u65f6\u5168\u65e0\u6297\u5fa1\u4e4b\u80fd\uff0c\u6628\u665a\u53c8\u5728\u4ed6\u624b\u5e95\u5403\u8fc7\u82e6\u5934\uff0c\u6068\u6068\u7684\u671b\u4e86\u4e09\u4eba\u4e00\u773c\uff0c\u8f6c\u5934\u5c31\u8d70\uff0c\u8d70\u51fa\u4ead\u5b50\u6570\u6b65\uff0c\u6076\u72e0\u72e0\u7684\u9053\uff1a\u201c\u4e0d\u8981\u8138\u7684\u5973\u4eba\uff0c\u81ea\u5df1\u5077\u6c49\u5b50\u4e0d\u7b97\uff0c\u8fd8\u6559\u5973\u513f\u4e5f\u5077\u6c49\u5b50\u3002\u201d \u3000\u3000\u6e29\u4eea\u4e00\u9635\u6c14\u82e6\uff0c\u4e24\u884c\u73e0\u6cea\u6302\u4e86\u4e0b\u6765\u3002\u9752\u9752\u54ea\u91cc\u5fcd\u5f97\u4ed6\u5982\u6b64\u8fb1\u9a82\uff0c\u8ffd\u51fa\u53bb\u559d\u9053\uff1a\u201c\u5582\uff0c\u4e03\u4f2f\u4f2f\uff0c\u4f60\u5634\u91cc\u4e0d\u5e72\u4e0d\u51c0\u7684\u8bf4\u751a\u4e48\uff1f\u201d \u3000\u3000\u6e29\u5357\u626c\u8f6c\u8eab\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u8d31\u4e2b\u5934\u8981\u53cd\u4e86\u5417\uff1f\u662f\u7237\u7237\u4eec\u53eb\u6211\u6765\u7684\uff0c\u4f60\u6562\u600e\u6837\uff1f\u201d \u3000\u3000\u6e29\u9752\u9752\u9a82\u9053\uff1a\u201c\u4f60\u8981\u6559\u8bad\u6211\uff0c\u5927\u5927\u65b9\u65b9\u7684\u5f53\u9762\u8bf4\u4fbf\u662f\uff0c\u5e72\u4e48\u6765\u5077\u542c\u6211\u4eec\u8bf4\u8bdd\uff1f\u201d\u6e29\u5357\u626c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4eec\uff1f\u4e5f\u4e0d\u77e5\u662f\u54ea\u91cc\u94bb\u51fa\u6765\u7684\u91ce\u7537\u4eba\uff0c\u5c45\u7136\u4e00\u8d77\u79f0\u8d77\u6211\u4eec\u6765\u5566\u3002\u6e29\u5bb6\u5341\u516b\u4ee3\u7956\u5b97\u7684\u8138\uff0c\u90fd\u7ed9\u4f60\u4eec\u4e22\u5e72\u51c0\u4e86\uff01\u201d\u9752\u9752\u6c14\u5f97\u80c0\u7ea2\u4e86\u8138\uff0c\u8f6c\u5934\u9053\uff1a\u201c\u5988\uff0c\u4f60\u542c\u4ed6\u8bf4\u8fd9\u79cd\u8bdd\u3002\u201d \u3000\u3000\u6e29\u4eea\u4f4e\u58f0\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u8bf7\u4f60\u8fc7\u6765\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u6e29\u5357\u626c\u7565\u4e00\u6c89\u541f\uff0c\u5927\u8e0f\u6b65\u8d70\u8fdb\u4ead\u5b50\u7ad9\u5b9a\uff0c\u548c\u8881\u627f\u5fd7\u76f8\u8ddd\u751a\u8fdc\uff0c\u9632\u4ed6\u7a81\u7136\u51fa\u624b\u3002 \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u6211\u4eec\u5a18\u513f\u8eab\u906d\u4e0d\u5e78\uff0c\u8499\u4e94\u4f4d\u7237\u7237\u548c\u5404\u4f4d\u5144\u5f1f\u7167\u987e\uff0c\u5728\u6e29\u5bb6\u53c8\u803d\u4e86\u5341\u591a\u5e74\u3002\u90a3\u59d3\u590f\u7684\u4e8b\uff0c\u6211\u4ece\u6765\u6ca1\u8ddf\u9752\u9752\u8bf4\u8fc7\uff0c\u73b0\u4e0b\u65e2\u7136\u4ed6\u5df2\u4e0d\u5728\u4eba\u4e16\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u518d\u884c\u9690\u7792\u3002\u8fd9\u4ef6\u4e8b\u4e03\u54e5\u5934\u5c3e\u77e5\u9053\u5f97\u5f88\u6e05\u695a\uff0c\u8bf7\u4f60\u5bf9\u8881\u76f8\u516c\u4e0e\u9752\u9752\u8bf4\u4e00\u8bf4\u5427\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u602b\u7136\u9053\uff1a\u201c\u6211\u5e72\u4e48\u8981\u8bf4\uff1f\u4f60\u7684\u4e8b\u4f60\u81ea\u5df1\u8bf4\u597d\u5566\uff0c\u53ea\u8981\u4f60\u4e0d\u6015\u4e11\u3002\u201d\u6e29\u4eea\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u5e7d\u5e7d\u7684\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u53ea\u9053\u4ed6\u6551\u8fc7\u4f60\u6027\u547d\uff0c\u4f60\u8fd8\u4f1a\u6709\u4e00\u4e9b\u513f\u611f\u6fc0\u4e4b\u5fc3\uff0c\u54ea\u77e5\u6e29\u5bb6\u7684\u4eba\uff0c\u5168\u662f\u90a3\u4e48\u5fd8\u2026\u2026\u5fd8\u2026\u2026\u5509\uff01\u201d\u6e29\u5357\u626c\u6012\u9053\uff1a\u201c\u4ed6\u6551\u8fc7\u6211\u6027\u547d\uff0c\u90a3\u4e0d\u9519\u3002\u53ef\u662f\u4ed6\u4e3a\u751a\u4e48\u8981\u6551\u6211\uff1f\u597d\uff0c\u6211\u75db\u75db\u5feb\u5feb\u8bf4\u51fa\u6765\uff0c\u514d\u5f97\u4f60\u81ea\u5df1\u8bf4\u65f6\uff0c\u4e0d\u77e5\u5982\u4f55\u80e1\u8a00\u4e71\u8bed\uff0c\u5c3d\u8bf4\u4e9b\u8c0e\u8bdd\u3002\u201d\u9752\u9752\u6012\u9053\uff1a\u201c\u6211\u5988\u5988\u600e\u4f1a\u8bf4\u8c0e\uff1f\u201d\u6e29\u4eea\u62c9\u4e86\u5979\u4e00\u628a\uff0c\u9053\uff1a\u201c\u8ba9\u4e03\u4f2f\u4f2f\u8bf4\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u5750\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u59d3\u8881\u7684\uff0c\u9752\u9752\uff0c\u6211\u600e\u6837\u8bc6\u5f97\u90a3\u91d1\u86c7\u5978\u8d3c\uff0c\u73b0\u4eca\u539f\u539f\u672c\u672c\u7684\u8ddf\u4f60\u4eec\u8bf4\uff0c\u4e5f\u597d\u8ba9\u4f60\u4eec\u77e5\u9053\uff0c\u90a3\u5978\u8d3c\u7684\u7528\u5fc3\u662f\u600e\u6837\u9669\u6bd2\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u574f\u8bdd\u6211\u4e0d\u542c\u3002\u201d\u8bf4\u7740\u53cc\u624b\u63a9\u4f4f\u8033\u6735\u3002 \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u9752\u9752\uff0c\u4f60\u542c\u597d\u5566\u3002\u4f60\u8fc7\u4e16\u7684\u7238\u7238\u867d\u7136\u4e0d\u80fd\u8bf4\u662f\u597d\u4eba\uff0c\u53ef\u662f\u6bd4\u6e29\u5bb6\u5168\u5bb6\u7684\u597d\u5904\u8fd8\u591a\u4e0a\u767e\u500d\u3002\u201d\u6e29\u5357\u626c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u81ea\u5df1\u4e5f\u59d3\u6e29\u3002\u201d \u3000\u3000\u6e29\u4eea\u62ac\u5934\u8fdc\u671b\u5929\u8fb9\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u65e9\u5df2\u4e0d\u59d3\u6e29\u4e86\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u516d\u56de\u3000\u903e\u5899\u6402\u5904\u5b50 \u7ed3\u9635\u56f0\u90ce\u541b",content:(0,t.jsx)(t.Fragment,{children:"\u6e29\u5357\u626c\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u4e8c\u5341\u5e74\u524d\u7684\u4e8b\u4e86\uff0c\u90a3\u65f6\u6211\u4e8c\u5341\u516d\u5c81\u3002 \u3000\u3000\u7239\u7239\u53eb\u6211\u5230\u626c\u5dde\u53bb\u7ed9\u516d\u53d4\u505a\u5e2e\u624b\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u77f3\u6881\u6e29\u6c0f\u4e94\u7956\u672c\u6709\u516d\u5144\u5f1f\u3002\u201d\u6e29\u5357\u626c\u7eed\u9053\uff1a\u201c\u6211\u5230\u4e86\u626c\u5dde\uff0c\u6ca1\u9047\u4e0a\u516d\u53d4\u3002\u4e00\u5929\u665a\u4e0a\u51fa\u53bb\u505a\u6848\u5b50\uff0c\u4e0d\u5c0f\u5fc3\u5931\u4e86\u624b\u3002\u201d\u6e29\u4eea\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u505a\u751a\u4e48\u6848\u5b50\uff1f\u201d \u3000\u3000\u6e29\u5357\u626c\u6012\u9053\uff1a\u201c\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u6562\u505a\u96be\u9053\u4e0d\u6562\u8bf4\uff1f\u6211\u662f\u77a7\u89c1\u4e00\u5bb6\u5927\u59d1\u5a18\u957f\u5f97\u597d\uff0c\u591c\u91cc\u8df3\u8fdb\u5899\u53bb\u91c7\u82b1\u3002\u5979\u4e0d\u4ece\uff0c\u6211\u5c31\u4e00\u5200\u6740\u4e86\u3002\u54ea\u77e5\u5979\u4e34\u6b7b\u65f6\u4e00\u58f0\u5927\u53eb\uff0c\u7ed9\u4eba\u542c\u89c1\u4e86\u3002\u62a4\u9662\u7684\u6b66\u5e08\u4e2d\u7adf\u6709\u51e0\u540d\u597d\u624b\uff0c\u4e00\u9f50\u6d8c\u6765\uff0c\u597d\u6c49\u654c\u4e0d\u8fc7\u4eba\u591a\uff0c\u6211\u5c31\u7ed9\u4ed6\u4eec\u64d2\u4f4f\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u4ed6\u8ff0\u8bf4\u81ea\u5df1\u7684\u6076\u884c\uff0c\u7adf\u7136\u6beb\u65e0\u7f9e\u6127\u4e4b\u610f\uff0c\u5fc3\u60f3\u8fd9\u4eba\u5b9e\u662f\u65e0\u803b\u5df2\u6781\u3002 \u3000\u3000\u6e29\u5357\u626c\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u6253\u4e86\u6211\u4e00\u987f\uff0c\u5c06\u6211\u9001\u5230\u8859\u95e8\u91cc\u76d1\u4e86\u8d77\u6765\u3002\u6211\u53ef\u4e5f\u4e0d\u6015\u3002\u6211\u8fd9\u4ef6\u6848\u5b50\u4e0d\u662f\u5c0f\u4e8b\uff0c\u6cb8\u6cb8\u626c\u626c\u7684\u65e9\u4f20\u5f00\u4e86\u3002\u6211\u60f3\u516d\u53d4\u65e2\u5728\u626c\u5dde\uff0c\u4ed6\u6b66\u529f\u4f55\u7b49\u4e86\u5f97\uff0c\u5f97\u77e5\u8baf\u606f\u540e\uff0c\u81ea\u4f1a\u6765\u6551\u6211\u51fa\u72f1\u3002\u54ea\u77e5\u7b49\u4e86\u5341\u591a\u5929\uff0c\u516d\u53d4\u59cb\u7ec8\u6ca1\u6765\u3002\u4e0a\u5b98\u8be6\u6587\u4e0b\u6765\uff0c\u7ed9\u6211\u5224\u4e86\u4e2a\u65a9\u7acb\u51b3\u3002\u72f1\u5352\u8ddf\u6211\u4e00\u8bf4\uff0c\u6211\u624d\u60ca\u614c\u8d77\u6765\u3002\u201d \u3000\u3000\u6e29\u9752\u9752\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u9053\u4f60\u662f\u4e0d\u4f1a\u6015\u7684\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u4e0d\u53bb\u7406\u5979\uff0c\u7eed\u9053\uff1a\u201c\u8fc7\u4e86\u4e09\u5929\uff0c\u7262\u5934\u62ff\u4e86\u4e00\u5927\u7897\u9152\u3001\u4e00\u76d8\u8089\u6765\u7ed9\u6211\u5403\u3002\u6211\u77e5\u9053\u660e\u5929\u5c31\u8981\u5904\u51b3\u4e86\uff0c\u5fc3\u60f3\u662f\u4eba\u90fd\u8981\u6b7b\uff0c\u53ea\u4e0d\u8fc7\u8001\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u8fd8\u6ca1\u597d\u597d\u4eab\u591f\u4e86\u798f\uff0c\u4e0d\u514d\u6709\u70b9\u53ef\u60dc\uff0c\u5fc3\u4e00\u6a2a\uff0c\u628a\u9152\u8089\u5403\u4e86\u4e2a\u5e72\u51c0\uff0c\u5012\u5934\u4fbf\u7761\u3002\u7761\u5230\u534a\u591c\uff0c\u5ffd\u7136\u6709\u4eba\u8f7b\u8f7b\u62cd\u6211\u80a9\u5934\u3002\u6211\u7ffb\u8eab\u5750\u8d77\uff0c\u542c\u5f97\u6709\u4eba\u4f4e\u58f0\u5728\u6211\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u522b\u4f5c\u58f0\uff0c\u6211\u6551\u4f60\u51fa\u53bb\uff01\u201d\u63a5\u7740\u5693\u5693\u51e0\u58f0\u54cd\uff0c\u6211\u624b\u811a\u7684\u94c1\u9563\u624b\u94d0\uff0c\u90fd\u88ab\u4ed6\u4e00\u67c4\u950b\u5229\u4e4b\u6781\u7684\u5175\u5203\u524a\u65ad\u4e86\u3002\u4ed6\u62c9\u7740\u6211\u7684\u624b\uff0c\u8df3\u51fa\u72f1\u53bb\u3002\u90a3\u4eba\u8f7b\u529f\u597d\u6781\uff0c\u624b\u52b2\u53c8\u5927\uff0c\u62c9\u7740\u6211\u624b\uff0c\u6211\u8d76\u8def\u7701\u4e86\u4e00\u5927\u534a\u529b\u6c14\u3002\u4e24\u4eba\u6765\u5230\u57ce\u5916\u4e00\u5ea7\u7834\u5e99\u91cc\uff0c\u4ed6\u70b9\u4eae\u795e\u6848\u4e0a\u7684\u8721\u70db\uff0c\u6211\u624d\u770b\u6e05\u695a\u4ed6\u662f\u4e2a\u957f\u5f97\u5f88\u4fca\u7684\u5e74\u8f7b\u4eba\uff0c\u5e74\u7eaa\u8fd8\u6bd4\u6211\u5c0f\u7740\u51e0\u5c81\u3002\u4ed6\u662f\u4e2a\u5c0f\u767d\u8138\uff0c\u54fc\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5411\u6e29\u4eea\u548c\u9752\u9752\u72e0\u72e0\u7684\u671b\u4e86\u4e00\u773c\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4fbf\u5411\u4ed6\u884c\u793c\u9053\u8c22\u3002\u90a3\u4eba\u9a84\u50b2\u5f97\u5f88\uff0c\u4e5f\u4e0d\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u2018\u6211\u59d3\u590f\uff0c\u4f60\u662f\u77f3\u6881\u6d3e\u59d3\u6e29\u7684\u4e86\uff1f\u2019\u6211\u70b9\u5934\u8bf4\u662f\uff0c\u8fd9\u65f6\u89c1\u4ed6\u8170\u95f4\u6302\u7740\u90a3\u67c4\u524a\u65ad\u6211\u94d0\u9563\u7684\u5175\u5203\uff0c\u5f2f\u5f2f\u66f2\u66f2\u7684\u4f3c\u4e4e\u662f\u4e00\u67c4\u5251\uff0c\u53ea\u662f\u5251\u5934\u5206\u53c9\uff0c\u6a21\u6837\u5f88\u662f\u53e4\u602a\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u90a3\u4fbf\u662f\u90a3\u67c4\u91d1\u86c7\u5251\u4e86\u3002\u201d\u4ed6\u4e0d\u52a8\u58f0\u8272\uff0c\u542c\u6e29\u5357\u626c\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff1a\u201c\u6211\u95ee\u4ed6\u59d3\u540d\uff0c\u4ed6\u51b7\u51b7\u7684\u9053\uff1a\u2018\u4f60\u4e0d\u5fc5\u77e5\u9053\uff0c\u53cd\u6b63\u4ee5\u540e\u4f60\u4e5f\u4e0d\u4f1a\u611f\u6fc0\u6211\u3002\u2019\u5f53\u65f6\u6211\u5f88\u5947\u602a\uff0c\u5fc3\u60f3\u4ed6\u6551\u6211\u6027\u547d\uff0c\u6211\u5f53\u7136\u4e00\u8f88\u5b50\u611f\u6fc0\u3002\u90a3\u4eba\u9053\uff1a\u2018\u6211\u662f\u4e3a\u4e86\u4f60\u516d\u53d4\u6e29\u65b9\u7984\u624d\u6551\u4f60\u7684\u3002\u8ddf\u6211\u6765\uff01\u2019\u6211\u8ddf\u7740\u4ed6\u8d70\u5230\u8fd0\u6cb3\u8fb9\u4e0a\uff0c\u4e0a\u4e86\u4e00\u8258\u8239\uff0c\u4ed6\u5429\u5490\u8239\u8001\u5927\u5411\u5357\u9a76\u53bb\u3002\u90a3\u8239\u79bb\u5f00\u4e86\u626c\u5dde\u5341\u591a\u91cc\u8def\uff0c\u6211\u624d\u6162\u6162\u653e\u5fc3\uff0c\u77e5\u9053\u5b98\u5e9c\u4e0d\u4f1a\u518d\u6765\u8ffd\u8d76\u4e86\u3002\u6211\u95ee\u4e86\u51e0\u53e5\uff0c\u4ed6\u53ea\u662f\u51b7\u7b11\u4e0d\u7b54\uff0c\u5ffd\u7136\u4ece\u8863\u56ca\u91cc\u62ff\u51fa\u4e00\u5bf9\u86fe\u7709\u523a\u6765\u3002\u8fd9\u662f\u516d\u53d4\u7684\u5175\u5668\uff0c\u7d20\u6765\u968f\u8eab\u4e0d\u79bb\uff0c\u600e\u4e48\u4f1a\u843d\u5728\u8fd9\u4eba\u624b\u4e2d\uff0c\u6211\u5fc3\u4e2d\u5f88\u5947\u602a\u3002\u90a3\u4eba\u9053\uff1a\u2018\u4f60\u516d\u53d4\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u54c8\u54c8\uff01\u2019\u602a\u7b11\u4e86\u51e0\u58f0\uff0c\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u4e00\u9635\u6740\u6c14\uff0c\u6211\u4e0d\u7531\u5f97\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\u3002\u4ed6\u9053\uff1a\u2018\u8fd9\u53e3\u7bb1\u5b50\uff0c\u4f60\u5e26\u56de\u5bb6\u53bb\u3002\u2019\u8bf4\u7740\u5411\u8239\u8231\u4e2d\u4e00\u6307\uff0c\u6211\u89c1\u90a3\u7bb1\u5b50\u5f88\u5927\uff0c\u7528\u94c1\u9489\u9489\u5f97\u5341\u5206\u7262\u56fa\uff0c\u5916\u9762\u8fd8\u7528\u7c97\u7ef3\u7f1a\u4f4f\u3002\u4ed6\u9053\uff1a\u2018\u4f60\u8d76\u5feb\u56de\u5bb6\uff0c\u8def\u4e0a\u4e0d\u53ef\u505c\u7559\u3002\u8fd9\u53e3\u7bb1\u5b50\u5fc5\u987b\u4ea4\u4f60\u5927\u4f2f\u4f2f\u4eb2\u624b\u6253\u5f00\u3002\u2019\u6211\u4e00\u4e00\u7b54\u5e94\u4e86\u3002\u4ed6\u53c8\u8bf4\uff1a\u2018\u4e00\u4e2a\u6708\u4e4b\u5185\uff0c\u6211\u5230\u4f60\u5bb6\u6765\u62dc\u8bbf\uff0c\u4f60\u5bb6\u91cc\u7684\u957f\u8f88\u4eec\u597d\u597d\u63a5\u5f85\u5427\u3002\u2019\u6211\u542c\u4ed6\u8bf4\u8bdd\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u4f46\u4e5f\u53ea\u5f97\u7b54\u5e94\u3002\u4ed6\u5631\u5490\u5b8c\u6bd5\uff0c\u5ffd\u7136\u63d0\u8d77\u8239\u4e0a\u7684\u94c1\u951a\uff0c\u5580\u5587\u5580\u5587\uff0c\u628a\u56db\u53ea\u951a\u722a\u90fd\u62d7\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u6e29\u9752\u9752\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u597d\uff01\u201d\u6e29\u5357\u626c\u5478\u7684\u4e00\u58f0\uff0c\u5728\u5730\u4e0a\u5410\u4e86\u4e00\u53e3\u6d53\u75f0\u3002\u9752\u9752\u6027\u7231\u6d01\u51c0\uff0c\u89c1\u4ed6\u5982\u6b64\u7cdf\u8e4b\u81ea\u5df1\u4eb2\u624b\u5e03\u7f6e\u7684\u73ab\u7470\u5c0f\u4ead\uff0c\u5fc3\u4e2d\u4e00\u9635\u96be\u8fc7\u3002\u8881\u627f\u5fd7\u77e5\u5979\u5fc3\u610f\uff0c\u4f38\u811a\u628a\u75f0\u64e6\u53bb\u3002\u9752\u9752\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u773c\u5149\u4e2d\u751a\u6709\u611f\u6fc0\u4e4b\u610f\u3002 \u3000\u3000\u6e29\u5357\u626c\u7eed\u9053\uff1a\u201c\u4ed6\u5411\u6211\u663e\u793a\u6b66\u529f\uff0c\u4e5f\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff0c\u53ea\u89c1\u4ed6\u628a\u65ad\u951a\u5f80\u8239\u8231\u4e2d\u4e00\u63b7\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u5982\u4e0d\u7167\u6211\u7684\u5429\u5490\uff0c\u5f00\u7bb1\u5077\u770b\uff0c\u79c1\u53d6\u5b9d\u7269\uff0c\u4e00\u8def\u4e0a\u82e5\u662f\u518d\u505a\u6848\u5b50\uff0c\u8fd9\u94c1\u951a\u4fbf\u662f\u4f60\u7684\u699c\u6837\uff01\u2019\u4ece\u56ca\u4e2d\u62ff\u51fa\u4e00\u952d\u94f6\u5b50\uff0c\u63b7\u5728\u8239\u677f\u4e0a\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u7684\u8def\u8d39\uff01\u2019\u62d4\u8d77\u8239\u5934\u4e0a\u7684\u4e24\u652f\u7af9\u7bd9\uff0c\u53cc\u624b\u5206\u522b\u63e1\u5b9a\uff0c\u5de6\u624b\u7af9\u7bd9\u63d2\u5165\u6cb3\u4e2d\uff0c\u8eab\u5b50\u5df2\u8dc3\u4e86\u8d77\u6765\uff0c\u53f3\u624b\u7af9\u7bd9\u968f\u5373\u5165\u6cb3\uff0c\u540c\u65f6\u62d4\u8d77\u5de6\u624b\u7af9\u7bd9\uff0c\u53c8\u5411\u524d\u70b9\u53bb\u3002\u8fd9\u6837\u51e0\u4e0b\u5b50\uff0c\u5c31\u5982\u4e00\u53ea\u957f\u817f\u9e6d\u9e5a\u822c\u8d70\u5230\u4e86\u5cb8\u4e0a\u3002\u4ed6\u9ad8\u58f0\u53eb\u9053\uff1a\u2018\u63a5\u7740\uff01\u2019\u8bed\u58f0\u65b9\u6bd5\uff0c\u4e24\u652f\u7af9\u7bd9\u5982\u6807\u67aa\u822c\u5c04\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6211\u89c1\u6765\u52bf\u52b2\u6025\uff0c\u4e0d\u6562\u53bb\u63a5\uff0c\u95ea\u8eab\u8eb2\u5f00\uff0c\u6251\u6251\u4e24\u58f0\uff0c\u7af9\u7bd9\u7a7f\u5165\u8239\u7bf7\u3002\u4f46\u542c\u5f97\u4ed6\u5728\u5cb8\u4e0a\u4e00\u58f0\u957f\u7b11\uff0c\u8eab\u5b50\u5df2\u6d88\u5931\u5728\u9ed1\u5f71\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u4f4d\u91d1\u86c7\u90ce\u541b\u5927\u6709\u8c6a\u6c14\u3002\u201d\u4ed6\u53ea\u5fc3\u91cc\u60f3\u60f3\uff0c\u9752\u9752\u5374\u516c\u7136\u8d5e\u4e86\u8d77\u6765\uff1a\u201c\u8fd9\u4eba\u771f\u662f\u82f1\u96c4\u8c6a\u6770\u3002\u597d\u5a01\u98ce\uff0c\u597d\u6c14\u6982\uff01\u201d \u3000\u3000\u6e29\u5357\u626c\u9053\uff1a\u201c\u82f1\u96c4\uff1f\u5478\uff01\u82f1\u4ed6\u5988\u7684\u96c4\u3002\u5f53\u65f6\u6211\u53ea\u9053\u4ed6\u662f\u6211\u6551\u547d\u6069\u4eba\uff0c\u867d\u89c1\u4ed6\u8bf4\u8bdd\u65f6\u773c\u9732\u51f6\u5149\uff0c\u4f3c\u4e4e\u5bf9\u6211\u5341\u5206\u618e\u538c\uff0c\u8fd8\u9053\u4ed6\u813e\u6c14\u53e4\u602a\uff0c\u4e5f\u4e0d\u600e\u4e48\u5728\u610f\u3002\u8fc7\u6c5f\u540e\uff0c\u6211\u53e6\u884c\u96c7\u8239\uff0c\u56de\u5230\u5bb6\u6765\u3002\u4e00\u8def\u4e0a\u642c\u8fd0\u7684\u4eba\u90fd\u8bf4\u8fd9\u53e3\u7bb1\u5b50\u597d\u91cd\uff0c\u6211\u60f3\u516d\u53d4\u8fd9\u6b21\u5b9a\u662f\u53d1\u4e86\u6a2a\u8d22\uff0c\u7bb1\u5b50\u4e2d\u76db\u6ee1\u4e86\u91d1\u94f6\u8d22\u5b9d\u3002\u6211\u82b1\u4e86\u8fd9\u4e48\u591a\u529b\u6c14\u8fd0\u56de\u5bb6\u6765\uff0c\u53d4\u4f2f\u4eec\u5b9a\u4f1a\u591a\u5206\u6211\u4e00\u4efd\uff0c\u56e0\u6b64\u5fc3\u91cc\u5f88\u662f\u9ad8\u5174\u3002\u56de\u5bb6\u4e4b\u540e\uff0c\u7239\u7239\u548c\u53d4\u4f2f\u4eec\u5f88\u5938\u5956\u6211\u80fd\u5e72\uff0c\u8bf4\u7b2c\u4e00\u6b21\u51fa\u9053\uff0c\u5c45\u7136\u5e72\u5f97\u4e0d\u574f\u3002\u201d \u3000\u3000\u9752\u9752\u63d2\u53e3\u9053\uff1a\u201c\u7684\u786e\u4e0d\u574f\uff0c\u6740\u4e86\u4e00\u4e2a\u5927\u95fa\u5973\uff0c\u5e26\u6765\u4e00\u53e3\u5927\u7bb1\u5b50\u3002\u201d\u6e29\u4eea\u9053\uff1a\u201c\u9752\u9752\uff0c\u522b\u591a\u5634\uff0c\u542c\u4e03\u4f2f\u4f2f\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u9053\uff1a\u201c\u8fd9\u5929\u665a\u4e0a\uff0c\u5385\u4e0a\u70b9\u6ee1\u8721\u70db\uff0c\u4e24\u540d\u5bb6\u4e01\u628a\u7bb1\u5b50\u62ac\u8fdb\u6765\u3002\u7239\u7239\u548c\u56db\u4f4d\u53d4\u4f2f\u5750\u5728\u4e2d\u95f4\u3002\u6211\u4eb2\u81ea\u52a8\u624b\uff0c\u5148\u5272\u65ad\u7ef3\u5b50\uff0c\u518d\u628a\u94c1\u9489\u4e00\u679a\u679a\u7684\u8d77\u51fa\u6765\u3002\u6211\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u5927\u4f2f\u4f2f\u90a3\u65f6\u7b11\u7740\u8bf4\uff1a\u2018\u8001\u516d\u53c8\u4e0d\u77e5\u770b\u4e2d\u4e86\u54ea\u5bb6\u7684\u5a18\u513f\uff0c\u8352\u5510\u7684\u4e0d\u60f3\u56de\u5bb6\uff0c\u628a\u8fd9\u7bb1\u4e1c\u897f\u53eb\u5b69\u5b50\u5148\u5e26\u56de\u6765\u3002\u6765\uff0c\u54b1\u4eec\u77a7\u77a7\u662f\u751a\u4e48\u5b9d\u8d1d\uff01\u2019\u6211\u63ed\u5f00\u7bb1\u76d6\uff0c\u89c1\u91cc\u9762\u88c5\u5f97\u6ee1\u6ee1\u7684\uff0c\u4e0a\u9762\u94fa\u7740\u4e00\u5c42\u7eb8\uff0c\u7eb8\u4e0a\u6709\u4e00\u5c01\u4fe1\uff0c\u4fe1\u5c01\u4e0a\u5199\u7740\u2018\u6e29\u6c0f\u5144\u5f1f\u540c\u62c6\u2019\u51e0\u4e2a\u5b57\u3002\u6211\u89c1\u90a3\u51e0\u4e2a\u5b57\u4f3c\u4e4e\u4e0d\u662f\u516d\u53d4\u7684\u624b\u7b14\uff0c\u5c31\u628a\u4fe1\u4ea4\u7ed9\u5927\u4f2f\u4f2f\u3002\u4ed6\u5e76\u4e0d\u62c6\u4fe1\uff0c\u8bf4\u9053\uff1a\u2018\u4e0b\u9762\u662f\u751a\u4e48\u4e1c\u897f\uff1f\u2019\u6211\u628a\u90a3\u5c42\u7eb8\u63ed\u5f00\uff0c\u4e0b\u9762\u662f\u65b9\u65b9\u7684\u4e00\u4e2a\u5927\u5305\u88f9\uff0c\u5305\u88f9\u7528\u7ebf\u5bc6\u5bc6\u7f1d\u4f4f\u3002\u5927\u4f2f\u4f2f\u9053\uff1a\u2018\u516d\u5ac2\uff0c\u4f60\u62ff\u526a\u5200\u6765\u62c6\u5427\u3002\u516d\u5f1f\u600e\u4e48\u5ffd\u7136\u7ec6\u5fc3\u8d77\u6765\u5566\uff1f\u2019\u516d\u5a76\u62c6\u5f00\u7f1d\u7740\u7684\u7ebf\uff0c\u628a\u5305\u88b1\u4e00\u63ed\u5f00\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u5305\u88f9\u55d6\u55d6\u55d6\u7684\u5c04\u51fa\u4e03\u516b\u652f\u6bd2\u7bad\u3002\u201d \u3000\u3000\u9752\u9752\u60ca\u547c\u4e86\u4e00\u58f0\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u662f\u91d1\u86c7\u90ce\u541b\u7684\u60ef\u6280\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u73b0\u4eca\u60f3\u8d77\u6765\u8fd8\u662f\u6559\u4eba\u5fc3\u60ca\u80c6\u6218\uff0c\u8981\u662f\u6211\u6027\u6025\u53bb\u63ed\u5305\u88b1\uff0c\u8fd9\u6761\u547d\u8fd8\u5728\u5417\uff1f\u8fd9\u51e0\u652f\u6bd2\u7bad\u54ea\uff0c\u6bcf\u4e00\u7bad\u90fd\u5c04\u8fdb\u4e86\u516d\u5a76\u7684\u8089\u91cc\u3002\u90a3\u662f\u89c1\u8840\u5c01\u5589\u3001\u5267\u6bd2\u65e0\u6bd4\u7684\u836f\u7bad\uff0c\u516d\u5a76\u767b\u65f6\u5168\u8eab\u53d1\u9ed1\uff0c\u54fc\u4e5f\u6ca1\u54fc\u4e00\u58f0\u5c31\u5012\u5730\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u8f6c\u8fc7\u5934\u5389\u58f0\u5bf9\u9752\u9752\u9053\uff1a\u201c\u90a3\u5c31\u662f\u4f60\u8001\u5b50\u5e72\u7684\u597d\u4e8b\u3002\u8fd9\u4e00\u6765\uff0c\u5385\u4e0a\u4f17\u4eba\u5168\u90fd\u8f70\u52a8\u3002\u4e94\u53d4\u7591\u5fc3\u662f\u6211\u4f7f\u5978\uff0c\u903c\u6211\u6253\u5f00\u5305\u88b1\u3002\u6211\u7ad9\u5f97\u8fdc\u8fdc\u5730\uff0c\u7528\u4e00\u6761\u957f\u7aff\u628a\u5305\u88b1\u6311\u5f00\uff0c\u603b\u7b97\u518d\u6ca1\u7bad\u5c04\u51fa\u6765\u3002\u4f60\u9053\u5305\u88f9\u91cc\u662f\u751a\u4e48\u73cd\u73e0\u5b9d\u8d1d\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d \u3000\u3000\u6e29\u5357\u626c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4f60\u516d\u7237\u7237\u7684\u5c38\u9996\uff01\u7ed9\u65a9\u6210\u4e86\u516b\u5757\uff01\u201d \u3000\u3000\u9752\u9752\u5403\u4e86\u4e00\u60ca\uff0c\u5413\u5f97\u5634\u5507\u90fd\u767d\u4e86\u3002\u6e29\u4eea\u4f38\u624b\u6402\u4f4f\u4e86\u5979\u3002 \u3000\u3000\u56db\u4eba\u9759\u9ed8\u4e86\u4e00\u9635\u3002\u6e29\u5357\u626c\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u4eba\u6bd2\u4e0d\u6bd2\uff1f\u4ed6\u6740\u4e86\u516d\u53d4\u4e5f\u5c31\u7f62\u4e86\uff0c\u5374\u628a\u4ed6\u5c38\u9996\u8fd9\u822c\u9001\u56de\u5bb6\u6765\u3002\u201d\u6e29\u4eea\u9053\uff1a\u201c\u4ed6\u4e3a\u751a\u4e48\u8fd9\u6837\u505a\uff0c\u4f60\u53ef\u8fd8\u6ca1\u8bf4\u3002\u201d\u6e29\u5357\u626c\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u5f53\u7136\u89c9\u5f97\u633a\u5e94\u8be5\u54ea\u3002\u53ea\u8981\u662f\u4f60\u59d8\u5934\u5e72\u7684\u4e8b\uff0c\u4e0d\u8bba\u751a\u4e48\uff0c\u4f60\u90fd\u8bf4\u4e0d\u9519\u3002\u201d \u3000\u3000\u6e29\u4eea\u671b\u7740\u5929\u7a7a\u7684\u661f\u661f\uff0c\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u7f13\u7f13\u7684\u9053\uff1a\u201c\u4ed6\u662f\u6211\u4e08\u592b\uff0c\u867d\u7136\u6211\u4eec\u6ca1\u62dc\u5929\u5730\uff0c\u53ef\u662f\u5728\u6211\u5fc3\u4e2d\uff0c\u4ed6\u662f\u6211\u7684\u4eb2\u4e08\u592b\u3002 \u3000\u3000\u9752\u9752\uff0c\u90a3\u65f6\u6211\u6bd4\u4f60\u6b64\u523b\u8fd8\u5c0f\u4e24\u5c81\uff0c\u6bd4\u4f60\u66f4\u52a0\u5b69\u5b50\u6c14\uff0c\u53c8\u4e0d\u7231\u5b66\u6b66\uff0c\u751a\u4e48\u4e5f\u4e0d\u61c2\u3002\u8fd9\u4e9b\u53d4\u4f2f\u4eec\u5728\u5bb6\u91cc\u51f6\u6a2a\u91ce\u86ee\uff0c\u65e0\u6076\u4e0d\u4f5c\uff0c\u6211\u5411\u6765\u4e0d\u559c\u6b22\u4ed6\u4eec\uff0c\u89c1\u516d\u53d4\u6b7b\u4e86\uff0c\u8001\u5b9e\u8bf4\u6211\u5fc3\u91cc\u4e5f\u4e0d\u96be\u53d7\u3002\u90a3\u65f6\u6211\u53ea\u89c9\u5f97\u5947\u602a\uff0c\u516d\u53d4\u8fd9\u4e48\u597d\u7684\u6b66\u529f\uff0c\u600e\u4e48\u4f1a\u7ed9\u4eba\u6740\u6b7b\u3002\u53ea\u542c\u5f97\u5927\u4f2f\u4f2f\u62ff\u8d77\u4e86\u90a3\u5c01\u4fe1\uff0c\u5927\u58f0\u8bfb\u4e86\u8d77\u6765\u3002\u8fd9\u4ef6\u4e8b\u8fc7\u53bb\u6709\u4e8c\u5341\u5e74\u4e86\uff0c\u53ef\u662f\u90a3\u5929\u665a\u4e0a\u7684\u60c5\u5f62\uff0c\u6211\u8fd8\u662f\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\u3002\u90a3\u5c01\u4fe1\u91cc\u7684\u8bdd\uff0c\u6211\u4e5f\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u201c\u5927\u4f2f\u4f2f\u6c14\u5f97\u8138\u8272\u53d1\u767d\uff0c\u8bfb\u4fe1\u7684\u58f0\u97f3\u4e5f\u53d1\u98a4\u4e86\uff0c\u4ed6\u8fd9\u4e48\u5ff5\uff1a\u2018\u77f3\u6881\u6d3e\u6e29\u6c0f\u5144\u5f1f\u5171\u9274\uff1a\u9001\u4e0a\u4ee4\u5f1f\u6e29\u65b9\u7984\u5c38\u9996\u4e00\u5177\uff0c\u52a1\u8bf7\u7b11\u7eb3\u3002 \u3000\u3000\u6b64\u4eba\u5f53\u5e74\u6c61\u8fb1\u6211\u4eb2\u59ca\u4e4b\u540e\uff0c\u53c8\u5c06\u5176\u6740\u5bb3\uff0c\u5e76\u5c06\u6211\u7236\u6bcd\u5144\u957f\uff0c\u4e00\u5bb6\u4e94\u53e3\u5c3d\u6570\u6740\u6b7b\u3002\u6211\u5b64\u8eab\u4e00\u4eba\u9003\u8131\u5728\u5916\uff0c\u73b0\u5f52\u6765\u62a5\u4ec7\u3002\u8840\u503a\u5341\u500d\u56de\u62a5\uff0c\u65b9\u89e3\u6211\u6068\u3002\u6211\u5fc5\u6740\u4f60\u5bb6\u4e94\u5341\u4eba\uff0c\u6c61\u4f60\u5bb6\u5987\u5973\u5341\u4eba\u3002 \u3000\u3000\u4e0d\u8db3\u6b64\u6570\uff0c\u8a93\u4e0d\u4e3a\u4eba\u3002\u91d1\u86c7\u90ce\u541b\u590f\u96ea\u5b9c\u767d\u3002\u201d \u3000\u3000\u5979\u80cc\u5b8c\u90a3\u5c01\u4fe1\uff0c\u5401\u4e86\u53e3\u6c14\uff0c\u5bf9\u6e29\u5357\u626c\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u516d\u53d4\u6740\u4ed6\u5168\u5bb6\uff0c\u6b64\u4e8b\u53ef\u662f\u6709\u7684\uff1f\u201d \u3000\u3000\u6e29\u5357\u626c\u50b2\u7136\u9053\uff1a\u201c\u6211\u4eec\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u5165\u4e86\u9ed1\u9053\uff0c\u52ab\u8d22\u52ab\u8272\uff0c\u6740\u4eba\u653e\u706b\uff0c\u90a3\u4e5f\u7a00\u677e\u5e73\u5e38\u3002\u516d\u53d4\u89c1\u4ed6\u59ca\u59ca\u957f\u5f97\u4e0d\u9519\uff0c\u7528\u5f3a\u4e0d\u4ece\uff0c\u62d4\u5200\u6740\u4e86\uff0c\u53c8\u6709\u751a\u4e48\u4e86\u4e0d\u8d77\uff1f\u672c\u6765\u4e5f\u4e0d\u7528\u6740\u4ed6\u6ee1\u95e8\uff0c\u5b9a\u662f\u516d\u53d4\u8ddf\u5979\u5bb6\u4eba\u671d\u4e86\u76f8\uff0c\u8fd9\u624d\u8981\u6740\u4eba\u706d\u53e3\u3002\u53ea\u53ef\u60dc\u5f53\u65f6\u7ed9\u8fd9\u5154\u5d3d\u5b50\u6f0f\u4e86\u7f51\uff0c\u4ee5\u81f4\u540e\u60a3\u65e0\u7a77\u3002\u201d\u6e29\u4eea\u53f9\u9053\uff1a\u201c\u4f60\u4eec\u7537\u4eba\u5728\u5916\u9762\u4f5c\u4e86\u8fd9\u6837\u5927\u7684\u5b7d\uff0c\u6211\u4eec\u5973\u5b50\u5728\u5bb6\u91cc\u54ea\u91cc\u77e5\u9053\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u9053\uff1a\u201c\u5927\u4f2f\u4f2f\u8bfb\u5b8c\u4e86\u4fe1\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u2018\u8fd9\u8d3c\u5b50\u627e\u4e0a\u95e8\u6765\u6700\u597d\uff0c\u5426\u5219\u54b1\u4eec\u53bb\u627e\u4ed6\uff0c\u8fd8\u4e0d\u77e5\u4ed6\u8eb2\u5728\u54ea\u91cc\u5462\uff1f\u2019\u4ed6\u8bdd\u867d\u8fd9\u4e48\u8bf4\uff0c\u53ef\u5341\u5206\u8c28\u614e\uff0c\u4ed4\u7ec6\u76d8\u95ee\u6211\u8fd9\u5978\u8d3c\u7684\u76f8\u8c8c\u548c\u6b66\u529f\uff0c\u5f53\u665a\u5927\u5bb6\u4e25\u884c\u6212\u5907\uff0c\u53c8\u6d3e\u4eba\u8fde\u591c\u53bb\u628a\u4e03\u53d4\u548c\u516b\u53d4\u4ece\u91d1\u534e\u548c\u4e25\u5dde\u53eb\u56de\u6765\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u5947\u602a\uff1a\u201c\u600e\u4e48\u4ed6\u4eec\u5144\u5f1f\u8fd9\u4e48\u591a\uff1f\u201d\u9752\u9752\u4e5f\u95ee\u4e86\u8d77\u6765\uff1a\u201c\u5988\uff0c\u6211\u4eec\u8fd8\u6709\u4e03\u7237\u7237\u3001\u516b\u7237\u7237\uff0c\u600e\u4e48\u6211\u4e0d\u77e5\u9053\uff1f\u201d\u6e29\u4eea\u9053\uff1a\u201c\u90a3\u662f\u4f60\u7237\u7237\u7684\u5802\u5144\u5f1f\uff0c\u672c\u6765\u4e0d\u4f4f\u5728\u8fd9\u513f\u7684\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u9053\uff1a\u201c\u4e03\u53d4\u4e00\u5411\u5728\u91d1\u534e\u4f4f\uff0c\u516b\u53d4\u5728\u4e25\u5dde\u4f4f\uff0c\u867d\u662f\u4e00\u5bb6\uff0c\u5916\u9762\u77e5\u9053\u7684\u4eba\u4e0d\u591a\uff0c\u54ea\u77e5\u8fd9\u91d1\u86c7\u5978\u8d3c\u6d88\u606f\u4e5f\u771f\u7075\uff0c\u4e03\u53d4\u548c\u516b\u53d4\u4e00\u52a8\u8eab\uff0c\u534a\u8def\u4e0a\u5c31\u7ed9\u4ed6\u5bb3\u6b7b\u4e86\u3002\u8fd9\u5978\u8d3c\u795e\u51fa\u9b3c\u6ca1\uff0c\u4e0d\u77e5\u5728\u54ea\u4e00\u5929\u4e0a\uff0c\u628a\u6211\u4eec\u5bb6\u91cc\u6536\u79df\u7c73\u65f6\u8ba1\u6570\u7528\u7684\u7af9\u7b79\u5077\u53bb\u4e86\u4e00\u6279\u3002\u4ed6\u6740\u6b7b\u6211\u4eec\u4e00\u4e2a\u4eba\uff0c\u4fbf\u5728\u6b7b\u4eba\u8eab\u4e0a\u63d2\u4e00\u6839\u7af9\u7b79\uff0c\u770b\u6765\u4e0d\u63d2\u6ee1\u4e94\u5341\u6839\uff0c\u4e0d\u80af\u6536\u624b\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u54b1\u4eec\u5b85\u5b50\u91cc\u4e0a\u4e0a\u4e0b\u4e0b\u4e00\u767e\u591a\u4eba\uff0c\u600e\u4f1a\u62b5\u6321\u4e0d\u4f4f\uff1f\u4ed6\u6709\u591a\u5c11\u4eba\u5462\uff1f\u201d \u3000\u3000\u6e29\u5357\u626c\u9053\uff1a\u201c\u4ed6\u53ea\u6709\u4e00\u4e2a\u3002\u8fd9\u5978\u8d3c\u4ece\u6765\u4e0d\u516c\u7136\u9732\u9762\uff0c\u5e73\u65f6\u4e5f\u4e0d\u77e5\u8eb2\u5728\u751a\u4e48\u5730\u65b9\uff0c\u53ea\u7b49\u6211\u4eec\u7684\u4eba\u4e00\u843d\u5355\uff0c\u5c31\u51fa\u624b\u52a0\u5bb3\u3002\u5927\u4f2f\u4f2f\u9080\u4e86\u51e0\u5341\u4f4d\u6c5f\u6e56\u597d\u624b\u6765\u77f3\u6881\uff0c\u6574\u5929\u5728\u5b85\u5b50\u91cc\u5403\u559d\uff0c\u7b49\u8fd9\u5978\u8d3c\u5230\u6765\uff0c\u5b85\u5b50\u5916\u9762\u8d34\u4e86\u5927\u5e03\u544a\uff0c\u9080\u4ed6\u6b63\u5927\u5149\u660e\u7684\u524d\u6765\u51b3\u6597\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u4e0d\u7406\u4f1a\uff0c\u89c1\u6211\u4eec\u4eba\u591a\uff0c\u5c31\u7edd\u8ff9\u4e0d\u6765\u3002\u8fc7\u4e86\u534a\u5e74\uff0c\u8fd9\u4e9b\u6c5f\u6e56\u597d\u624b\u6162\u6162\u6563\u53bb\u4e86\uff0c\u5927\u623f\u7684\u4e09\u54e5\u548c\u4e94\u623f\u7684\u4e5d\u5f1f\u5ffd\u7136\u6eba\u6b7b\u5728\u5858\u91cc\uff0c\u8eab\u4e0a\u53c8\u63d2\u4e86\u7af9\u7b79\u3002\u539f\u6765\u8fd9\u5978\u8d3c\u4e5f\u771f\u6709\u8010\u5fc3\uff0c\u6084\u6084\u7684\u7b49\u4e86\u534a\u5e74\uff0c\u770b\u51c6\u4e86\u65f6\u673a\u65b9\u4e0b\u624b\u3002\u63a5\u8fde\u5341\u6765\u5929\uff0c\u5b85\u5b50\u91cc\u5929\u5929\u6709\u4eba\u6bd9\u547d\u3002\u77f3\u6881\u9547\u4e0a\u68fa\u6750\u5e97\u505a\u68fa\u6750\u4e5f\u6765\u4e0d\u53ca\uff0c\u53ea\u5f97\u5230\u8862\u5dde\u57ce\u91cc\u53bb\u4e70\u3002\u5bf9\u5916\u9762\u8bf4\uff0c\u53ea\u8bf4\u5b85\u5b50\u91cc\u649e\u4e86\u761f\u795e\uff0c\u95f9\u761f\u75ab\u3002\u4eea\u59b9\u59b9\uff0c\u8fd9\u4e9b\u53ef\u6015\u7684\u65e5\u5b50\u4f60\u603b\u8bb0\u5f97\u5427\uff1f\u201d \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u90a3\u65f6\u5019\u5168\u9547\u90fd\u4eba\u5fc3\u60f6\u60f6\u3002\u54b1\u4eec\u5b85\u5b50\u91cc\u65e5\u591c\u6709\u4eba\u5de1\u903b\uff0c\u7239\u7239\u548c\u53d4\u4f2f\u4eec\u8f6e\u73ed\u5de1\u5b88\u3002\u5973\u4eba\u548c\u5b69\u5b50\u90fd\u805a\u96c6\u5728\u4e2d\u95f4\u5c4b\u91cc\uff0c\u4e0d\u6562\u8d70\u51fa\u5927\u95e8\u4e00\u6b65\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u5207\u9f7f\u9053\uff1a\u201c\u9976\u662f\u8fd9\u6837\uff0c\u56db\u623f\u91cc\u7684\u4e24\u4e2a\u5ac2\u5ac2\u534a\u591c\u91cc\u8fd8\u662f\u7ed9\u4ed6\u63b3\u4e86\u53bb\uff0c\u5f53\u65f6\u54b1\u4eec\u53ea\u9053\u53c8\u88ab\u4ed6\u5bb3\u6b7b\u4e86\uff0c\u54ea\u77e5\u8fc7\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u4e24\u4e2a\u5ac2\u5ac2\u4ece\u626c\u5dde\u634e\u4fe1\u6765\uff0c\u8bf4\u7ed9\u8fd9\u5978\u8d3c\u5356\u5230\u4e86\u5a3c\u5bee\uff0c\u88ab\u8feb\u63a5\u4e86\u4e00\u4e2a\u6708\u5ba2\u4eba\u3002\u56db\u53d4\u6c14\u5f97\u9669\u9669\u6655\u6b7b\u8fc7\u53bb\uff0c\u8fd9\u4e24\u4e2a\u5ab3\u5987\u4e5f\u4e0d\u8981\u4e86\uff0c\u6d3e\u4eba\u53bb\u6740\u5149\u4e86\u5a3c\u5bee\u91cc\u7684\u8001\u9e28\u9f9f\u5974\u3001\u5993\u5973\u5ad6\u5ba2\uff0c\u8fde\u4e24\u4e2a\u5ac2\u5ac2\u4e5f\u4e00\u8d77\u6740\u4e86\uff0c\u4e00\u628a\u706b\u8fde\u70e7\u4e86\u626c\u5dde\u516b\u5bb6\u5a3c\u5bee\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u91d1\u86c7\u90ce\u541b\u867d\u7136\u662f\u62a5\u7236\u6bcd\u5144\u59ca\u4e4b\u4ec7\uff0c\u4f46\u628a\u5143\u51f6\u9996\u6076\u6740\u6b7b\u4e5f\u5df2\u7ecf\u591f\u4e86\uff0c\u8fd9\u6837\u505a\u672a\u514d\u8fc7\u4efd\u3002\u201d \u3000\u3000\u53c8\u60f3\uff1a\u201c\u6e29\u65b9\u65bd\u600e\u4e48\u5730\u8fc1\u6012\u4e8e\u4eba\uff0c\u8fde\u81ea\u5df1\u7684\u4e24\u4e2a\u5ab3\u5987\u4e5f\u6740\u4e86\uff1f\u201d \u3000\u3000\u4e0d\u81ea\u7981\u7684\u6447\u5934\uff0c\u5f88\u89c9\u4e0d\u4ee5\u4e3a\u7136\u3002 \u3000\u3000\u6e29\u5357\u626c\u9053\uff1a\u201c\u6700\u6c14\u4eba\u7684\u662f\uff0c\u6bcf\u5230\u7aef\u5348\u3001\u4e2d\u79cb\u3001\u5e74\u5173\u4e09\u8282\uff0c\u4ed6\u5c31\u9001\u4e00\u5c01\u4fe1\u6765\uff0c\u5f00\u4e00\u5f20\u6e05\u5355\uff0c\u8bf4\u8fd8\u6b20\u4eba\u547d\u51e0\u6761\uff0c\u5987\u5973\u51e0\u4eba\u3002 \u3000\u3000\u77f3\u6881\u6d3e\u5728\u6c5f\u5357\u7eb5\u6a2a\u6570\u5341\u5e74\uff0c\u5374\u88ab\u8fd9\u5978\u8d3c\u4e00\u4eba\u7d2f\u5f97\u5982\u6b64\u4e4b\u60e8\uff0c\u5927\u5bb6\u5904\u5fc3\u79ef\u8651\uff0c\u8981\u62a5\u6b64\u4ec7\u3002\u4f46\u8fd9\u5978\u8d3c\u8eab\u624b\u5b9e\u5728\u592a\u5f3a\uff0c\u7239\u7239\u548c\u53d4\u4f2f\u4eec\u548c\u4ed6\u4ea4\u4e86\u51e0\u6b21\u624b\uff0c\u90fd\u62fe\u593a\u4ed6\u4e0d\u4e0b\u3002\u54b1\u4eec\u9632\u5f97\u7d27\u4e86\uff0c\u4ed6\u63a5\u8fde\u51e0\u4e2a\u6708\u4e0d\u6765\uff0c\u53ea\u8981\u7a0d\u7a0d\u4e00\u677e\uff0c\u7acb\u523b\u51fa\u4e8b\u3002\u5927\u5bb6\u5b9e\u5728\u65e0\u8ba1\u53ef\u65bd\u3002\u4e24\u5e74\u4e4b\u95f4\uff0c\u54b1\u4eec\u6e29\u5bb6\u88ab\u4ed6\u5927\u5927\u5c0f\u5c0f\u4e00\u5171\u6740\u6b7b\u4e86\u4e09\u5341\u516b\u53e3\u3002\u9752\u9752\uff0c\u4f60\u8bf4\uff0c\u54b1\u4eec\u8be5\u4e0d\u8be5\u6068\u8fd9\u6076\u8d3c\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u540e\u6765\u600e\u6837\uff1f\u201d \u3000\u3000\u6e29\u5357\u626c\u9053\uff1a\u201c\u8ba9\u4f60\u5988\u8bf4\u4e0b\u53bb\u5427\u3002\u201d \u3000\u3000\u6e29\u4eea\u5bf9\u8881\u627f\u5fd7\u671b\u4e86\u4e00\u773c\uff0c\u51c4\u7136\u9053\uff1a\u201c\u4ed6\u7684\u9ab8\u9aa8\u662f\u8881\u76f8\u516c\u57cb\u846c\u7684\uff0c\u90a3\u4e48\u6211\u751a\u4e48\u4e8b\u4e5f\u4e0d\u5fc5\u7792\u4f60\uff0c\u53ea\u6c42\u8881\u76f8\u516c\u5f85\u4f1a\u628a\u4ed6\u6b7b\u65f6\u7684\u60c5\u5f62\uff0c\u8bf4\u7ed9\u6211\u4eec\u6bcd\u5973\u4fe9\u77e5\u9053\u2026\u2026\u90a3\u4e48\u2026\u2026\u201d \u3000\u3000\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u53c8\u54bd\u54fd\u4e86\uff0c\u9694\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4e0d\u61c2\u4ed6\u4e3a\u4f55\u8fd9\u6837\u72e0\uff0c\u5176\u5b9e\u4e5f\u4e0d\u60f3\u61c2\u3002\u7239\u7239\u4e0d\u8bb8\u6211\u4eec\u8d70\u51fa\u5927\u95e8\u4e00\u6b65\uff0c\u6211\u597d\u6c14\u95f7\uff0c\u6bcf\u5929\u53ea\u80fd\u5728\u56ed\u5b50\u91cc\u73a9\u73a9\uff0c\u7239\u7239\u8fd8\u8bf4\uff0c\u6ca1\u54e5\u54e5\u4eec\u966a\u7740\uff0c\u5973\u5b69\u5b50\u4eec\u5c31\u662f\u5927\u767d\u5929\u4e5f\u4e0d\u80fd\u5230\u56ed\u5b50\u91cc\u53bb\u3002\u8fd9\u5929\u662f\u9633\u6625\u4e09\u6708\uff0c\u7530\u91cc\u6cb9\u83dc\u82b1\u7684\u9999\u5473\u4e00\u9635\u9635\u4ece\u7a97\u91cc\u5439\u8fdb\u6765\uff0c\u6211\u771f\u60f3\u5230\u5c71\u5761\u4e0a\u53bb\u770b\u770b\u82b1\uff0c\u95fb\u95fb\u7530\u91ce\u91cc\u90a3\u80a1\u98ce\u7684\u6c14\u606f\uff0c\u53ef\u662f\u8fd9\u5bb3\u6b7b\u4e86\u4eba\u7684\u91d1\u86c7\u90ce\u541b\u5440\uff0c\u5728\u8fd9\u6837\u597d\u7684\u5929\u6c14\uff0c\u628a\u6211\u5173\u5728\u5c4b\u5b50\u91cc\u3002\u6211\u771f\u60f3\u72ec\u81ea\u4e2a\u6e9c\u51fa\u53bb\u4e00\u4f1a\u513f\uff0c\u53ef\u662f\u60f3\u8d77\u7239\u7239\u90a3\u80a1\u4e25\u5389\u7684\u795e\u6c14\uff0c\u53c8\u4e0d\u6562\u5566\u3002\u8fd9\u5929\u4e0b\u5348\uff0c\u6211\u548c\u4e8c\u623f\u91cc\u7684\u4e09\u59ca\u59ca\u3001\u4e94\u623f\u91cc\u7684\u5ac2\u5ac2\uff0c\u8fd8\u6709\u5357\u626c\u54e5\u4f60\u548c\u5929\u9738\u54e5\uff0c\u6211\u4eec\u4e94\u4e2a\u4eba\u5728\u56ed\u5b50\u91cc\u73a9\uff0c\u6211\u5728\u8361\u79cb\u5343\uff0c\u8d8a\u8361\u8d8a\u9ad8\u3002\u8eab\u5b50\u98d8\u4e86\u8d77\u6765\uff0c\u4ece\u5899\u5934\u4e0a\u671b\u51fa\u53bb\uff0c\u89c1\u5230\u7eff\u6cb9\u6cb9\u7684\u6768\u67f3\uff0c\u4e00\u682a\u682a\u5f00\u5f97\u5341\u5206\u8302\u76db\u7684\u6843\u82b1\uff0c\u5fc3\u91cc\u771f\u662f\u9ad8\u5174\u3002\u5ffd\u7136\uff0c\u5929\u9738\u54e5\u602a\u53eb\u4e86\u4e00\u58f0\uff0c\u4ef0\u5929\u8dcc\u5012\uff0c\u6211\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u540e\u6765\u624d\u77e5\u4ed6\u80f8\u53e3\u4e2d\u4e86\u90a3\u4eba\u4e00\u679a\u91d1\u86c7\u9525\uff0c\u5f53\u573a\u5c31\u6253\u6b7b\u4e86\u3002\u5357\u626c\u54e5\u4f60\u5462\uff1f\u6211\u8bb0\u5f97\u4f60\u9a6c\u4e0a\u9003\u8fdb\u4e86\u5c4b\uff0c\u628a\u6211\u4eec\u4e09\u4e2a\u5973\u4eba\u4e22\u5728\u5916\u9762\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u80c0\u7ea2\u4e86\u8138\uff0c\u8fa9\u9053\uff1a\u201c\u6211\u6253\u4e0d\u8fc7\u4ed6\uff0c\u4e0d\u8d70\u5c82\u4e0d\u662f\u767d\u9001\u6027\u547d\uff0c\u6211\u662f\u53bb\u53eb\u6551\u5175\u3002\u201d \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u6211\u8fd8\u4e0d\u660e\u767d\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u53ea\u89c1\u5899\u5934\u4e00\u4e2a\u4eba\u8df3\u4e86\u4e0b\u6765\uff0c\u521a\u597d\u7ad9\u5728\u6211\u7684\u79cb\u5343\u4e0a\u3002\u4ed6\u7528\u529b\u4e00\u8361\uff0c\u79cb\u5343\u98de\u4e86\u8d77\u6765\uff0c\u4ed6\u4e00\u628a\u5c06\u6211\u62e6\u8170\u62b1\u4f4f\uff0c\u6211\u53ea\u89c9\u817e\u4e91\u9a7e\u96fe\u822c\u7684\u98de\u4e86\u51fa\u53bb\u3002\u6211\u4ee5\u4e3a\u8fd9\u4e00\u4e0b\u4e24\u4eba\u90fd\u8981\u8dcc\u6b7b\u4e86\uff0c\u54ea\u77e5\u4ed6\u5de6\u624b\u62b1\u7740\u6211\uff0c\u53f3\u624b\u5728\u5899\u5916\u5927\u6811\u679d\u4e0a\u4e00\u6273\uff0c\u4fbf\u53c8\u5f39\u4e86\u8d77\u6765\uff0c\u8f7b\u8f7b\u7684\u843d\u5728\u6570\u4e08\u4e4b\u5916\u3002\u8fd9\u65f6\u6211\u5413\u80e1\u6d82\u4e86\uff0c\u4e3e\u8d77\u62f3\u5934\u5f80\u4ed6\u8138\u4e0a\u4e71\u6253\u3002\u4ed6\u624b\u6307\u5728\u6211\u80a9\u7a9d\u91cc\u4e00\u70b9\uff0c\u6211\u767b\u65f6\u5168\u8eab\u762b\u8f6f\uff0c\u4e00\u52a8\u4e5f\u4e0d\u80fd\u52a8\u5566\u3002\u53ea\u542c\u5f97\u540e\u9762\u5f88\u591a\u4eba\u5927\u58f0\u53eb\u56b7\u8ffd\u8d76\uff0c\u4f46\u540e\u6765\u58f0\u97f3\u8d8a\u6765\u8d8a\u8fdc\u3002\u4ed6\u631f\u7740\u6211\u5954\u4e86\u534a\u5929\uff0c\u5230\u4e86\u4e00\u4e2a\u60ac\u5d16\u5ced\u58c1\u4e0a\u7684\u5c71\u6d1e\u91cc\u3002\u4ed6\u89e3\u4e86\u6211\u7a74\u9053\uff0c\u671b\u7740\u6211\u72de\u7b11\u3002\u6211\u5ffd\u7136\u60f3\u8d77\u4e86\u90a3\u4e24\u4f4d\u5ac2\u5ac2\uff0c\u5fc3\u60f3\u4e0e\u5176\u53d7\u8fb1\uff0c\u4e0d\u5982\u81ea\u5df1\u6b7b\u4e86\u5e72\u51c0\uff0c\u5c31\u4e00\u5934\u5411\u5c71\u77f3\u4e0a\u649e\u53bb\u3002\u4ed6\u5728\u6211\u540e\u5fc3\u4e00\u62c9\uff0c\u6211\u624d\u6ca1\u649e\u6b7b\uff0c\u7559\u4e0b\u4e86\u8fd9\u4e2a\u75a4\u3002\u201d\u8bf4\u7740\u5f80\u81ea\u5df1\u989d\u4e0a\u4e00\u6307\u3002\u8881\u627f\u5fd7\u89c1\u90a3\u4f24\u75a4\u9690\u5728\u5934\u53d1\u4e1b\u91cc\uff0c\u9732\u5728\u5916\u9762\u7684\u6709\u4e00\u5bf8\u6765\u957f\uff0c\u6df1\u5165\u5934\u9876\uff0c\u770b\u6765\u5f53\u65f6\u53d7\u4f24\u7740\u5b9e\u4e0d\u8f7b\u3002 \u3000\u3000\u6e29\u4eea\u53f9\u9053\uff1a\u201c\u5018\u82e5\u5c31\u8fd9\u4e48\u8ba9\u6211\u649e\u6b7b\u4e86\uff0c\u5bf9\u4ed6\u81ea\u5df1\u53ef\u597d\u5f97\u591a\uff0c\u8c01\u77e5\u8fd9\u4e00\u62c9\u7adf\u5bb3\u82e6\u4e86\u4ed6\u3002\u90a3\u65f6\u6211\u660f\u4e86\u8fc7\u53bb\uff0c\u7b49\u9192\u6765\u65f6\uff0c\u89c1\u8eab\u4e0a\u88f9\u7740\u4e00\u6761\u6bef\u5b50\uff0c\u6211\u4e00\u60ca\u53c8\u9669\u9669\u6655\u4e86\u8fc7\u53bb\uff0c\u540e\u6765\u89c1\u81ea\u5df1\u8eab\u4e0a\u8863\u670d\u7a7f\u5f97\u597d\u597d\u5730\uff0c\u624d\u7a0d\u7a0d\u653e\u4e86\u4e00\u70b9\u5fc3\uff0c\u60f3\u662f\u4ed6\u89c1\u6211\u5bfb\u6b7b\uff0c\u5f3a\u76d7\u53d1\u4e86\u5584\u5fc3\uff0c\u4fbf\u4e0d\u518d\u4e0b\u624b\u5bb3\u6211\u3002\u6211\u7d27\u7d27\u95ed\u4f4f\u4e86\u773c\u775b\uff0c\u4e00\u773c\u4e5f\u4e0d\u6562\u77a7\u4ed6\uff0c\u8fde\u5fc3\u91cc\u4e5f\u4e0d\u6562\u53bb\u60f3\u773c\u524d\u7684\u4e8b\u3002 \u3000\u3000\u201c\u4ed6\u6015\u6211\u518d\u5bfb\u6b7b\uff0c\u90a3\u4e24\u5929\u4e4b\u4e2d\uff0c\u65e5\u591c\u90fd\u5b88\u7740\u6211\u3002\u8ddf\u6211\u8bf4\u8bdd\uff0c\u6211\u81ea\u7136\u4e0d\u7b54\u3002\u4ed6\u716e\u4e86\u4e1c\u897f\u7ed9\u6211\u5403\uff0c\u6211\u53ea\u662f\u54ed\uff0c\u751a\u4e48\u4e5f\u4e0d\u5403\u3002\u5230\u7b2c\u56db\u5929\u4e0a\uff0c\u4ed6\u89c1\u6211\u997f\u5f97\u5b9e\u5728\u4e0d\u6210\u6837\u5b50\u4e86\uff0c\u4e8e\u662f\u71ac\u4e86\u4e00\u5927\u7897\u8089\u6c64\uff0c\u8f7b\u58f0\u8f7b\u6c14\u7684\u529d\u6211\u559d\u3002\u6211\u4e0d\u7406\u4e0d\u776c\uff0c\u4ed6\u5ffd\u7136\u6293\u4f4f\u6211\uff0c\u634f\u4f4f\u6211\u7684\u9f3b\u5b50\uff0c\u628a\u8089\u6c64\u5f80\u6211\u53e3\u91cc\u704c\uff0c\u8fd9\u6837\u5f3a\u7740\u6211\u559d\u4e86\u5927\u534a\u7897\u6c64\u3002\u4ed6\u624b\u4e00\u677e\uff0c\u6211\u5c31\u5c06\u4e00\u53e3\u70ed\u6c64\u55b7\u5728\u4ed6\u8138\u4e0a\u3002\u6211\u662f\u8981\u6fc0\u4ed6\u751f\u6c14\uff0c\u5e72\u8106\u4e00\u5200\u6740\u4e86\u6211\uff0c\u514d\u5f97\u53d7\u4ed6\u6b3a\u4fae\uff0c\u518d\u628a\u6211\u50cf\u4e8c\u4f4d\u5ac2\u5ac2\u90a3\u6837\uff0c\u5356\u5230\u5a3c\u5bee\u91cc\u53bb\u6d3b\u53d7\u7f6a\u3002\u54ea\u77e5\u4ed6\u5e76\u4e0d\u53d1\u6012\uff0c\u53ea\u662f\u7b11\u7b11\uff0c\u7528\u8896\u5b50\u64e6\u53bb\u4e86\u8138\u4e0a\u6c64\u6c34\uff0c\u5446\u5446\u671b\u7740\u6211\uff0c\u4e0d\u4f4f\u53f9\u6c14\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u9752\u9752\u7a81\u7136\u95f4\u7ea2\u6655\u6ee1\u8138\u3002 \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u90a3\u5929\u665a\u4e0a\uff0c\u4ed6\u7761\u5728\u6d1e\u53e3\uff0c\u5bf9\u6211\u8bf4\uff1a\u2018\u6211\u5531\u5c0f\u66f2\u513f\u7ed9\u4f60\u542c\u597d\u5417\uff1f\u2019\u6211\u8bf4\uff1a\u2018\u6211\u4e0d\u7231\u542c\u3002\u2019\u4ed6\u9ad8\u5174\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u6211\u5f53\u4f5c\u4f60\u662f\u54d1\u5df4\uff0c\u539f\u6765\u4f1a\u8bf4\u8bdd\u3002\u2019\u6211\u9a82\u9053\uff1a\u2018\u8c01\u662f\u54d1\u5df4\u6765\u7740\uff1f\u89c1\u4e86\u574f\u4eba\u6211\u5c31\u4e0d\u8bf4\u8bdd\u3002\u2019\u4ed6\u4e0d\u518d\u8a00\u8bed\u4e86\uff0c\u9ad8\u9ad8\u5174\u5174\u7684\u5531\u8d77\u5c71\u6b4c\u6765\uff0c\u5531\u4e86\u5927\u534a\u591c\uff0c\u76f4\u5230\u6708\u4eae\u51fa\u6765\uff0c\u4ed6\u8fd8\u5728\u5531\u3002\u6211\u4e00\u76f4\u5728\u5927\u5b85\u5b50\u91cc\u4f4f\u7740\uff0c\u54ea\u91cc\u542c\u89c1\u8fc7\u8fd9\u79cd\u2026\u2026\u8fd9\u79cd\u5c71\u6b4c\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u559d\u9053\uff1a\u201c\u4f60\u53c8\u6015\u542c\u53c8\u60f3\u542c\uff0c\u662f\u4e0d\u662f\uff1f\u8c01\u8010\u70e6\u6765\u542c\u4f60\u8fd9\u4e9b\u4e0d\u8981\u8138\u7684\u4e8b\uff1f\u201d\u5927\u8e0f\u6b65\u4fbf\u5411\u4ead\u5916\u8d70\u53bb\u3002\u9752\u9752\u9053\uff1a\u201c\u4ed6\u5b9a\u662f\u53bb\u544a\u8bc9\u7237\u7237\u4eec\u3002\u201d\u6e29\u4eea\u9053\uff1a\u201c\u7531\u4ed6\u8bf4\u53bb\uff0c\u6211\u65e9\u5c31\u751a\u4e48\u90fd\u4e0d\u5728\u4e4e\u4e86\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u5988\uff0c\u4f60\u518d\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u540e\u6765\u6211\u6726\u6726\u80e7\u80e7\u7684\u5c31\u7761\u7740\u4e86\u3002\u7b2c\u4e8c\u5929\u65e9\u6668\u9192\u6765\u5374\u4e0d\u89c1\u4e86\u4ed6\uff0c\u6211\u60f3\u4e00\u4e2a\u4eba\u9003\u56de\u5bb6\u6765\uff0c\u53ef\u662f\u8fd9\u5c71\u6d1e\u662f\u5728\u4e00\u4e2a\u5c71\u5cf0\u9876\u4e0a\uff0c\u5c71\u5cf0\u5f88\u9661\uff0c\u65e0\u8def\u53ef\u4e0b\uff0c\u53ea\u6709\u4f3c\u4ed6\u8fd9\u6837\u8f7b\u529f\u6781\u9ad8\u7684\u4eba\uff0c\u624d\u80fd\u4e0a\u4e0b\u3002\u5230\u4e2d\u5348\u65f6\u4ed6\u56de\u6765\u4e86\uff0c\u7ed9\u6211\u5e26\u6765\u4e86\u8bb8\u591a\u9996\u9970\u3001\u8102\u7c89\u3002 \u3000\u3000\u6211\u4e0d\u8981\uff0c\u62ff\u8d77\u6765\u90fd\u629b\u5230\u4e86\u5c71\u8c37\u91cc\u3002\u4ed6\u53ef\u4e5f\u4e0d\u751f\u6c14\uff0c\u665a\u4e0a\u53c8\u5531\u6b4c\u7ed9\u6211\u542c\u3002 \u3000\u3000\u201c\u6709\u4e00\u5929\uff0c\u4ed6\u5e26\u4e86\u597d\u591a\u5c0f\u9e21\u3001\u5c0f\u732b\u3001\u5c0f\u4e4c\u9f9f\u4e0a\u5c71\u5cf0\u6765\uff0c\u4ed6\u77e5\u9053\u6211\u4e0d\u5fcd\u5fc3\u628a\u8fd9\u4e9b\u6d3b\u4e1c\u897f\u4e22\u4e0b\u5c71\u53bb\u3002\u4ed6\u6574\u5929\u966a\u6211\u9017\u732b\u513f\u73a9\uff0c\u5582\u5c0f\u4e4c\u9f9f\u5403\u4e1c\u897f\uff0c\u665a\u4e0a\u5531\u6b4c\u7ed9\u6211\u542c\u3002\u6211\u5728\u5c71\u6d1e\u91cc\u7761\uff0c\u4ed6\u4ece\u6765\u4e0d\u8e0f\u8fdb\u5c71\u6d1e\u4e00\u6b65\u3002\u6211\u89c1\u4ed6\u4e0d\u6765\u4fb5\u72af\u6211\uff0c\u653e\u5fc3\u4e86\u4e9b\uff0c\u4e5f\u80af\u5403\u4e1c\u897f\u4e86\u3002\u53ef\u662f\u4e00\u4e2a\u591a\u6708\u4e2d\uff0c\u6211\u4e00\u76f4\u4e0d\u8ddf\u4ed6\u8bf4\u8bdd\u3002\u4ed6\u59cb\u7ec8\u5bf9\u6211\u5f88\u6e29\u67d4\u5f88\u548c\u6c14\uff0c\u7239\u7239\u548c\u5988\u5988\u90fd\u6ca1\u4ed6\u5f85\u6211\u8fd9\u6837\u597d\u3002 \u3000\u3000\u201c\u53c8\u8fc7\u5f97\u51e0\u5929\uff0c\u4ed6\u5ffd\u7136\u677f\u8d77\u4e86\u8138\uff0c\u6076\u72e0\u72e0\u7684\u77a7\u6211\uff0c\u6211\u5f88\u5bb3\u6015\uff0c\u54ed\u4e86\u8d77\u6765\u3002\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u54c4\u6211\u522b\u54ed\u3002\u90a3\u5929\u665a\u4e0a\u6211\u542c\u5f97\u4ed6\u5728\u54ed\u6ce3\uff0c\u54ed\u5f97\u5f88\u662f\u4f24\u5fc3\u3002\u4e0d\u4e45\uff0c\u5929\u4e0b\u8d77\u5927\u96e8\u6765\uff0c\u4ed6\u4ecd\u662f\u4e0d\u8fdb\u6d1e\u6765\uff0c\u6211\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u53eb\u4ed6\u8fdb\u5c71\u6d1e\u6765\u8eb2\u96e8\uff0c\u4ed6\u4e5f\u4e0d\u7406\u3002 \u3000\u3000\u201c\u6211\u95ee\u4ed6\u4e3a\u751a\u4e48\u54ed\uff0c\u4ed6\u7c97\u58f0\u7c97\u6c14\u8bf4\uff1a\u2018\u660e\u5929\u662f\u6211\u7238\u7238\u3001\u5988\u5988\u3001\u54e5\u54e5\u3001\u59ca\u59ca\u7684\u5fcc\u8fb0\u3002\u6211\u4e00\u5bb6\u5168\u88ab\u4f60\u5bb6\u7684\u4eba\u5728\u8fd9\u5929\u5bb3\u6b7b\u4e86\u3002 \u3000\u3000\u660e\u5929\u6211\u8bf4\u751a\u4e48\u4e5f\u5f97\u6740\u4e00\u4e2a\u4eba\u6765\u62a5\u4ec7\u3002\u4f60\u5bb6\u91cc\u73b0\u4e0b\u9632\u5907\u5f88\u4e25\uff0c\u8bf7\u4e86\u5d06\u5cd2\u6d3e\u7684\u674e\u62d9\u9053\u4eba\u548c\u5341\u65b9\u5bfa\u7684\u6e05\u660e\u7985\u5e08\u4f5c\u5e2e\u624b\uff0c\u54fc\uff0c\u8fd9\u4e24\u4eba\u867d\u7136\u5389\u5bb3\uff0c\u6211\u96be\u9053\u5c31\u6b64\u7f62\u624b\u4e0d\u6210\uff1f\u2019\u4ed6\u54ac\u7259\u5207\u9f7f\u7684\uff0c\u5192\u7740\u5927\u96e8\u5c31\u4e0b\u5cf0\u53bb\u4e86\u3002\u7b2c\u4e8c\u5929\u5230\u508d\u665a\u65f6\uff0c\u4ed6\u8fd8\u662f\u6ca1\u56de\u6765\uff0c\u6211\u5012\u6709\u4e9b\u8bb0\u6302\u4e86\uff0c\u6697\u6697\u76fc\u671b\u4ed6\u5e73\u5b89\u56de\u6765\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u9752\u9752\u5077\u5077\u671b\u4e86\u8881\u627f\u5fd7\u4e00\u773c\uff0c\u77a7\u4ed6\u662f\u5426\u6709\u8f7b\u89c6\u4e4b\u8272\uff0c\u4f46\u89c1\u4ed6\u7aef\u8c28\u606d\u5750\uff0c\u7559\u795e\u503e\u542c\uff0c\u8fd9\u624d\u5bbd\u6170\uff0c\u7f13\u7f13\u7684\u5401\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u5929\u5feb\u9ed1\u4e86\uff0c\u6211\u51e0\u6b21\u5230\u5c71\u5cf0\u8fb9\u773a\u671b\u3002\u4e5f\u4e0d\u77e5\u53bb\u671b\u4e86\u51e0\u6b21\uff0c\u7ec8\u4e8e\u89c1\u5230\u5bf9\u9762\u90a3\u5ea7\u5c71\u5cf0\u4e0a\u6709\u56db\u4e2a\u4eba\u5f71\u5728\u4e92\u76f8\u8ffd\u9010\uff0c\u8eab\u6cd5\u90fd\u5feb\u5f97\u4e0d\u5f97\u4e86\u3002\u6211\u7528\u5fc3\u7ec6\u770b\uff0c\u6700\u5148\u4e00\u4eba\u679c\u7136\u662f\u4ed6\uff0c\u540e\u9762\u4e00\u4e2a\u662f\u9053\u58eb\uff0c\u53e6\u4e00\u4e2a\u662f\u548c\u5c1a\uff0c\u7b2c\u56db\u4e2a\u5374\u662f\u6211\u7239\u7239\u3002\u4ed6\u624b\u4e2d\u62ff\u7684\u662f\u90a3\u628a\u91d1\u86c7\u5251\uff0c\u4e00\u4e2a\u6597\u4ed6\u4eec\u4e09\u4e2a\uff0c\u8fb9\u6253\u8fb9\u9003\u3002\u6597\u4e86\u4e00\u4f1a\uff0c\u90a3\u548c\u5c1a\u4e00\u7985\u6756\u6a2a\u626b\u8fc7\u53bb\uff0c\u773c\u89c1\u4ed6\u65e0\u6cd5\u907f\u5f00\uff0c\u6211\u5fc3\u4e2d\u7740\u6025\uff0c\u5927\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u54ea\u77e5\u4ed6\u91d1\u86c7\u5251\u56de\u8fc7\u6765\u4e00\u683c\uff0c\u7adf\u628a\u7985\u6756\u65a9\u53bb\u4e86\u4e00\u622a\u3002 \u3000\u3000\u7239\u7239\u542c\u89c1\u53eb\u58f0\uff0c\u56de\u5934\u671b\u89c1\u4e86\u6211\uff0c\u4e0d\u518d\u4e89\u6597\uff0c\u5f80\u6211\u8fd9\u5c71\u5cf0\u4e0a\u5954\u6765\u3002 \u3000\u3000\u201c\u4ed6\u5f88\u662f\u7126\u6025\uff0c\u4e24\u5251\u628a\u548c\u5c1a\u4e0e\u9053\u4eba\u903c\u5f00\uff0c\u968f\u540e\u8ffd\u8d76\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u53d8\u6210\u6211\u7239\u7239\u5728\u524d\u9762\uff0c\u4ed6\u5728\u4e2d\u95f4\uff0c\u50e7\u9053\u4e8c\u4eba\u5728\u540e\u3002\u56db\u4eba\u4e0d\u4e45\u5c31\u5954\u4e0b\u5c71\u8c37\u3002\u4ed6\u8ffd\u4e0a\u4e86\u6211\u7239\u7239\uff0c\u62e6\u4f4f\u4e86\u4e0d\u8bb8\u4ed6\u5230\u6211\u8fd9\u8fb9\u5c71\u5cf0\u6765\u3002\u6597\u4e86\u51e0\u56de\u5408\uff0c\u4e00\u50e7\u4e00\u9053\u8d76\u5230\uff0c\u6211\u7239\u7239\u62bd\u7a7a\u8df3\u51fa\uff0c\u81ea\u6211\u8fd9\u8fb9\u6500\u4e0a\u6765\u3002\u8fd9\u56db\u4e2a\u4eba\u8fb9\u6597\u8fb9\u5954\uff0c\u8ffd\u5230\u4e86\u6211\u7ad9\u7740\u7684\u5c71\u5cf0\u4e0a\u3002 \u3000\u3000\u6211\u5f88\u662f\u9ad8\u5174\uff0c\u5927\u53eb\uff1a\u2018\u7239\u7239\uff0c\u5feb\u6765\uff01\u2019\u8fd9\u65f6\u4ed6\u5982\u53d1\u75af\u822c\u62a2\u4e86\u8fc7\u6765\uff0c\u63a5\u8fde\u4e09\u5251\uff0c\u628a\u7239\u7239\u903c\u5f97\u4e0d\u4f4f\u5012\u9000\u3002\u7239\u7239\u6253\u4ed6\u4e0d\u8fc7\uff0c\u773c\u89c1\u5371\u6025\uff0c\u50e7\u9053\u4e8c\u4eba\u4e5f\u5230\u4e86\u3002\u7239\u7239\u53eb\u9053\uff1a\u2018\u963f\u4eea\uff0c\u4f60\u600e\u6837\uff01\u2019\u6211\u8bf4\uff1a\u2018\u6211\u5f88\u597d\uff0c\u7239\uff0c\u4f60\u653e\u5fc3\u3002\u2019\u7239\u7239\u9053\uff1a\u2018\u597d\uff0c\u54b1\u4eec\u5148\u6599\u7406\u4e86\u8fd9\u5978\u8d3c\u518d\u8bf4\u3002\u2019\u4e09\u4eba\u53c8\u628a\u4ed6\u56f4\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u201c\u90a3\u9053\u4eba\u9053\uff1a\u2018\u91d1\u86c7\u90ce\u541b\uff0c\u6211\u4eec\u5d06\u5cd2\u6d3e\u8ddf\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u53ea\u4e0d\u8fc7\u89c1\u4f60\u5e72\u5f97\u592a\u4e5f\u8fc7\u4efd\uff0c\u56e0\u6b64\u633a\u8eab\u51fa\u6765\u4f5c\u4e2a\u548c\u4e8b\u4f6c\u3002\u6211\u8c01\u4e5f\u4e0d\u5e2e\uff0c\u5982\u4f60\u7b54\u5e94\u7f62\u624b\uff0c\u4ee5\u540e\u4e0d\u518d\u53bb\u6e29\u5bb6\u60f9\u4e8b\uff0c\u4eca\u65e5\u4e4b\u4e8b\u5c31\u6b64\u5584\u7f62\u3002\u2019\u4ed6\u5927\u58f0\u53eb\u9053\uff1a\u2018\u7236\u6bcd\u5144\u59ca\u4e4b\u4ec7\uff0c\u5c82\u80fd\u4e0d\u62a5\uff1f\u2019\u90a3\u548c\u5c1a\u9053\uff1a\u2018\u4f60\u5df2\u7ecf\u6740\u4e86\u8fd9\u8bb8\u591a\u4eba\uff0c\u4e5f\u8be5\u591f\u4e86\u3002\u529d\u4f60\u77a7\u5728\u6211\u4eec\u4e8c\u4eba\u7684\u8138\u4e0a\uff0c\u5c31\u6b64\u505c\u624b\u5427\uff01\u2019\u4ed6\u5ffd\u7136\u4e00\u5251\u5411\u548c\u5c1a\u523a\u53bb\uff0c\u56db\u4eba\u53c8\u6076\u6597\u8d77\u6765\u3002\u90a3\u9053\u4eba\u7684\u5175\u5203\u6709\u70b9\u513f\u53e4\u602a\uff0c\u60f3\u6765\u6b66\u529f\u751a\u5f3a\uff0c\u548c\u5c1a\u7684\u7985\u6756\u4f7f\u5f00\u6765\uff0c\u98ce\u58f0\u547c\u547c\u731b\u54cd\uff0c\u4e5f\u5f88\u5389\u5bb3\u3002\u4ed6\u8d8a\u6253\u8d8a\u4e0d\u6210\u4e86\uff0c\u6ee1\u5934\u5927\u6c57\uff0c\u5ffd\u7136\u4e00\u4e2a\u8dc4\u8e09\uff0c\u9669\u9669\u8dcc\u5012\u3002 \u3000\u3000\u201c\u90a3\u548c\u5c1a\u4e00\u6756\u6253\u4e0b\u53bb\uff0c\u88ab\u4ed6\u4fa7\u8eab\u8eb2\u8fc7\uff0c\u4ed6\u8eab\u5b50\u8fd9\u6837\u4e00\u4fa7\uff0c\u89c1\u5230\u4e86\u6211\u7684\u8138\u3002\u4ed6\u540e\u6765\u8bf4\uff0c\u4ed6\u90a3\u65f6\u5019\u672c\u5df2\u7b4b\u75b2\u529b\u7aed\uff0c\u4f46\u4e00\u89c1\u5230\u6211\u6d41\u9732\u51fa\u5bf9\u4ed6\u5341\u5206\u5173\u6000\u7684\u795e\u6c14\uff0c\u7a81\u7136\u95f4\u7cbe\u795e\u5927\u632f\u3002\u4ed6\u7684\u5251\u4f7f\u5f97\u8d8a\u6765\u8d8a\u5feb\uff0c\u5c71\u8c37\u4e2d\u96fe\u6c14\u4e0a\u5347\uff0c\u70df\u96fe\u4e2d\u53ea\u89c1\u5230\u91d1\u5149\u95ea\u8000\u3002\u53ea\u542c\u5f97\u4ed6\u53eb\u9053\uff1a\u2018\u6e29\u59d1\u5a18\uff0c\u522b\u6015\uff0c\u77a7\u6211\u7684\uff01\u2019\u90a3\u548c\u5c1a\u5927\u53eb\u4e00\u58f0\uff0c\u9aa8\u6e9c\u6e9c\u7684\u6eda\u4e0b\u5c71\u53bb\uff0c\u8111\u95e8\u6b63\u4e2d\u9489\u4e86\u4e00\u679a\u91d1\u86c7\u9525\u3002\u6211\u7239\u548c\u90a3\u9053\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\u3002\u4ed6\u633a\u5251\u5411\u6211\u7239\u523a\u53bb\uff0c\u90a3\u9053\u4eba\u4e58\u865a\u653b\u4ed6\u540e\u5fc3\u3002\u4ed6\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u5de6\u624b\u53cc\u6307\u5411\u9053\u4eba\u773c\u4e2d\u622a\u53bb\u3002\u9053\u4eba\u5934\u4e00\u4f4e\uff0c\u4ed6\u4e00\u5251\u6325\u8fc7\uff0c\u5c06\u9053\u4eba\u62e6\u8170\u65a9\u4e3a\u4e24\u622a\u3002\u201d \u3000\u3000\u9752\u9752\u5440\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002\u6e29\u4eea\u9053\uff1a\u201c\u4ed6\u56de\u624b\u4e00\u5251\uff0c\u4fbf\u5411\u6211\u7239\u7239\u523a\u53bb\u3002\u7239\u7239\u89c1\u4ed6\u8fde\u6740\u4e24\u4e2a\u6b66\u529f\u9ad8\u624b\uff0c\u65e9\u5df2\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u94a2\u6756\u4f7f\u5f00\u6765\u5df2\u4e0d\u6210\u5bb6\u6570\u3002\u6211\u5fd9\u4ece\u6d1e\u91cc\u5954\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u2018\u4f4f\u624b\uff0c\u4f4f\u624b\uff01\u2019\u4ed6\u542c\u6211\u4e00\u53eb\uff0c\u5c31\u505c\u4e86\u624b\u3002\u6211\u9053\uff1a\u2018\u8fd9\u662f\u6211\u7239\u7239\uff01\u2019\u4ed6\u5411\u6211\u7239\u7239\u72e0\u72e0\u671b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u8d70\u5427\uff0c\u9976\u4f60\u6027\u547d\uff01\u2019\u7239\u7239\u5f88\u611f\u610f\u5916\uff0c\u56de\u8eab\u8981\u8d70\u3002\u8fd9\u65f6\u6211\u56e0\u6574\u5929\u6ca1\u5403\u4e1c\u897f\uff0c\u52a0\u4e4b\u521a\u624d\u62c5\u5fc3\u53d7\u60ca\uff0c\u89c1\u4ed6\u9976\u4e86\u7239\u7239\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u7a81\u7136\u8dcc\u5012\u3002\u4ed6\u5fd9\u62a2\u8fc7\u6765\u6276\u6211\uff0c\u6211\u4ece\u4ed6\u80a9\u4e0a\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u7239\u7239\u76ee\u9732\u51f6\u5149\uff0c\u5ffd\u7136\u4e3e\u8d77\u94a2\u6756\uff0c\u731b\u529b\u5411\u4ed6\u540e\u5fc3\u6253\u53bb\u3002 \u3000\u3000\u201c\u4ed6\u4e00\u5fc3\u53ea\u5173\u6ce8\u7740\u6211\u6709\u6ca1\u53d7\u4f24\uff0c\u5168\u6ca1\u60f3\u5230\u7239\u7239\u7adf\u4f1a\u5077\u88ad\u3002 \u3000\u3000\u6211\u5fcd\u4e0d\u4f4f\u547c\u53eb\uff1a\u2018\u7559\u5fc3\uff01\u2019\u4ed6\u4e00\u6123\uff0c\u8981\u5f85\u907f\u8ba9\uff0c\u5df2\u7ecf\u4e0d\u53ca\uff0c\u5c06\u5934\u4e00\u4fa7\uff0c\u8fd9\u4e00\u6756\u6253\u4e2d\u5728\u4ed6\u7684\u80cc\u4e0a\u3002\u4ed6\u5939\u624b\u593a\u8fc7\u94a2\u6756\uff0c\u63b7\u5165\u5c71\u8c37\uff0c\u53cc\u638c\u5411\u7239\u7239\u6253\u53bb\u3002\u7239\u7239\u65e0\u6cd5\u62db\u67b6\uff0c\u95ed\u76ee\u7b49\u6b7b\u3002\u54ea\u77e5\u4ed6\u56de\u5934\u5411\u6211\u671b\u4e86\u4e00\u773c\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u5bf9\u7239\u7239\u9053\uff1a\u2018\u4f60\u5feb\u8d70\u3002\u522b\u8ba9\u6211\u56de\u5fc3\u8f6c\u610f\uff0c\u53c8\u4e0d\u9976\u4f60\u4e86\uff01\u2019\u7239\u7239\u4e0d\u518d\u8bf4\u8bdd\uff0c\u5954\u4e0b\u5c71\u53bb\u3002\u4ed6\u80cc\u4e0a\u5403\u4e86\u7239\u7239\u8fd9\u4e00\u6756\uff0c\u53d7\u4f24\u7740\u5b9e\u6c89\u91cd\uff0c\u7239\u7239\u521a\u8d70\uff0c\u4ed6\u5c31\u4e00\u53e3\u9c9c\u8840\uff0c\u55b7\u5728\u6211\u80f8\u524d\u8863\u4e0a\u3002 \u3000\u3000\u9752\u9752\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u7237\u7237\u8fd9\u4e48\u4e0d\u8981\u8138\uff0c\u660e\u91cc\u6253\u4e0d\u8fc7\u4eba\u5bb6\uff0c\u5c31\u6765\u6697\u4e0b\u6bd2\u624b\uff01\u201d \u3000\u3000\u6e29\u4eea\u53f9\u9053\uff1a\u201c\u6309\u7406\u8bf4\uff0c\u4ed6\u662f\u6211\u5bb6\u7684\u5927\u4ec7\u4eba\uff0c\u8fde\u6740\u4e86\u6211\u5bb6\u51e0\u5341\u53e3\u4eba\u3002\u53ef\u662f\u89c1\u4ed6\u53d7\u4eba\u56f4\u653b\u6697\u7b97\uff0c\u6211\u7981\u4e0d\u4f4f\u5fc3\u91cc\u5411\u7740\u4ed6\uff0c\u8fd9\u4e5f\u53eb\u4f5c\u524d\u751f\u7684\u51a4\u5b7d\u3002 \u3000\u3000\u201c\u4ed6\u6447\u6447\u6643\u6643\u7684\u8d70\u8fdb\u6d1e\u53bb\uff0c\u4ece\u56ca\u4e2d\u62ff\u51fa\u4f24\u836f\u6765\u5403\u4e86\uff0c\u63a5\u8fde\u53c8\u55b7\u4e86\u8bb8\u591a\u9c9c\u8840\u51fa\u6765\u3002\u6211\u5413\u5f97\u53ea\u662f\u54ed\u3002\u4ed6\u867d\u7136\u53d7\u4f24\uff0c\u795e\u8272\u5374\u5f88\u9ad8\u5174\uff0c\u95ee\u6211\uff1a\u2018\u4f60\u5e72\u4e48\u54ed\uff1f\u2019\u6211\u54ed\u9053\uff1a\u2018\u4f60\u4f24\u5f97\u8fd9\u6837\u3002\u2019\u4ed6\u7b11\u95ee\uff1a\u2018\u4f60\u662f\u4e3a\u4e86\u6211\u624d\u54ed\uff1f\u2019\u6211\u56de\u7b54\u4e0d\u51fa\uff0c\u53ea\u89c9\u5f97\u5f88\u662f\u4f24\u5fc3\u3002 \u3000\u3000\u201c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ed6\u8bf4\uff1a\u2018\u81ea\u4ece\u6211\u5168\u5bb6\u7684\u4eba\u7ed9\u4f60\u516d\u53d4\u5bb3\u6b7b\u4e4b\u540e\uff0c\u4ece\u6765\u6ca1\u4e00\u4eba\u5173\u5fc3\u8fc7\u6211\u3002\u6211\u4eca\u5929\u6740\u4e86\u4f60\u7684\u4e00\u4e2a\u5802\u5144\uff0c\u524d\u540e\u4e00\u5171\u5df2\u6740\u4e86\u56db\u5341\u4eba\uff0c\u672c\u6765\u8fd8\u8981\u518d\u6740\u5341\u4eba\uff0c\u770b\u5728\u4f60\u7684\u773c\u6cea\u4efd\u4e0a\uff0c\u5c31\u6b64\u7f62\u624b\u4e0d\u6740\u4e86\u3002\u2019\u6211\u53ea\u662f\u54ed\uff0c\u4e0d\u8bf4\u8bdd\u3002\u4ed6\u53c8\u9053\uff1a\u2018\u4f60\u5bb6\u7684\u5973\u4eba\u6211\u4e5f\u4e0d\u5bb3\u4e86\uff0c\u7b49\u6211\u4f24\u597d\u4e4b\u540e\uff0c\u9001\u4f60\u56de\u5bb6\u3002\u2019\u6211\u5fc3\u91cc\u662f\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u53ea\u89c9\u5f97\u4ed6\u7b54\u5e94\u4e0d\u6740\u4eba\u4e86\uff0c\u90a3\u5f88\u597d\u3002\u4ee5\u540e\u51e0\u5929\u6211\u70e7\u6c64\u716e\u996d\uff0c\u7528\u5fc3\u670d\u4f8d\u4ed6\u3002\u53ef\u662f\u4ed6\u4e0d\u505c\u7684\u5455\u8840\uff0c\u6709\u65f6\u8ff7\u8ff7\u7cca\u7cca\u7684\u8001\u662f\u53eb\u2018\u5988\u5988\u2019\u3002 \u3000\u3000\u201c\u6709\u4e00\u5929\u4ed6\u6574\u5929\u6655\u4e86\u8fc7\u53bb\uff0c\u5230\u4e86\u508d\u665a\uff0c\u773c\u89c1\u4e0d\u6210\u4e86\u3002\u6211\u54ed\u5f97\u4e24\u773c\u90fd\u80bf\u4e86\u3002\u4ed6\u5ffd\u7136\u7741\u5f00\u773c\u6765\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u2018\u4e0d\u8981\u7d27\uff0c\u4e0d\u4f1a\u6b7b\u3002\u2019\u8fc7\u4e86\u4e24\u5929\uff0c\u679c\u7136\u6162\u6162\u597d\u4e86\u8d77\u6765\uff0c\u4e00\u5929\u665a\u4e0a\u5bf9\u6211\u8bf4\uff0c\u90a3\u5929\u4e2d\u4e86\u8fd9\u4e00\u6756\uff0c\u672c\u6765\u6d3b\u4e0d\u6210\u4e86\uff0c\u4f46\u60f3\u5230\u4ed6\u6b7b\u4e4b\u540e\uff0c\u6211\u5728\u8fd9\u9ad8\u5cf0\u7edd\u9876\u4e4b\u4e0a\u8d70\u4e0d\u4e0b\u53bb\uff0c\u6211\u5bb6\u7684\u4eba\u53c8\u6015\u4e86\u4ed6\uff0c\u4e0d\u6562\u6765\u627e\uff0c\u90a3\u6211\u975e\u997f\u6b7b\u4e0d\u53ef\u3002\u4e3a\u4e86\u6211\uff0c\u4ed6\u65e0\u8bba\u5982\u4f55\u8981\u6d3b\u7740\u3002\u201d \u3000\u3000\u9752\u9752\u63d2\u5634\u9053\uff1a\u201c\u5988\uff0c\u4ed6\u5f85\u4f60\u5f88\u597d\u554a\uff0c\u8fd9\u4eba\u5f88\u6709\u826f\u5fc3\u3002\u201d\u8bf4\u7740\u72e0\u72e0\u671b\u4e86\u8881\u627f\u5fd7\u4e00\u773c\u3002\u8881\u627f\u5fd7\u8138\u4e0a\u4e00\u9635\u53d1\u70ed\uff0c\u628a\u5934\u8f6c\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u6e29\u4eea\u53c8\u9053\uff1a\u201c\u4ee5\u540e\u4ed6\u8eab\u5b50\u6e10\u6e10\u590d\u5143\uff0c\u8ddf\u6211\u8bf4\u8d77\u5c0f\u65f6\u5019\u7684\u4e8b\u60c5\uff0c\u4ed6\u7238\u7238\u5988\u5988\u600e\u6837\u75bc\u4ed6\uff0c\u54e5\u54e5\u59ca\u59ca\u53c8\u600e\u6837\u7231\u62a4\u4ed6\u3002\u6709\u4e00\u6b21\u4ed6\u751f\u75c5\uff0c\u4ed6\u5988\u5988\u4e09\u5929\u4e09\u591c\u6ca1\u7761\u89c9\u7684\u5b88\u5728\u4ed6\u5e8a\u8fb9\u3002\u54ea\u77e5\u4e00\u5929\u665a\u4e0a\uff0c\u516d\u53d4\u7adf\u628a\u4ed6\u5168\u5bb6\u6740\u4e86\u3002\u90a3\u65f6\u6211\u89c9\u5f97\u8fd9\u4eba\u867d\u7136\u624b\u6bb5\u51f6\u72e0\u6bd2\u8fa3\uff0c\u4f46\u8bf4\u5230\u4ed6\u4eb2\u4eba\u7684\u65f6\u5019\uff0c\u5374\u663e\u5f97\u5fc3\u80a0\u5f88\u662f\u826f\u5584\u67d4\u548c\u3002\u4ed6\u62ff\u51fa\u4e00\u4e2a\u7ee3\u82b1\u7684\u7ea2\u809a\u515c\u6765\u7ed9\u6211\u770b\uff0c\u8bf4\u662f\u4ed6\u5468\u5c81\u65f6\u4ed6\u5988\u5988\u7ee3\u7684\u3002\u201d \u3000\u3000\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ece\u6000\u4e2d\u53d6\u4e86\u4e00\u4e2a\u5c0f\u5b69\u7528\u7684\u809a\u515c\u51fa\u6765\uff0c\u644a\u5728\u684c\u4e0a\u3002\u8881\u627f\u5fd7\u89c1\u8fd9\u809a\u515c\u7ea2\u7f0e\u9762\u5b50\uff0c\u767d\u7f0e\u91cc\u5b50\uff0c\u7ee3\u7740\u4e2a\u5149\u8eab\u7684\u80d6\u5a03\u5a03\u7761\u5728\u4e00\u5f20\u5927\u82ad\u8549\u53f6\u5b50\u4e0a\u3002\u80d6\u5a03\u5a03\u795e\u60c5\u61a8\u61a8\u7684\u5f88\u662f\u53ef\u7231\uff0c\u7ee3\u5de5\u7cbe\u81f4\uff0c\u60f3\u5f97\u5230\u4ed6\u5988\u5988\u523a\u7ee3\u65f6\u6ee1\u5fc3\u662f\u7231\u5b50\u4e4b\u60c5\u3002\u8881\u627f\u5fd7\u4ece\u5c0f\u6ca1\u6709\u7239\u5a18\uff0c\u770b\u5230\u8fd9\u809a\u515c\uff0c\u60f3\u5230\u81ea\u5df1\u8eab\u4e16\uff0c\u4e0d\u7981\u4e00\u9635\u5fc3\u9178\u3002 \u3000\u3000\u6e29\u4eea\u7eed\u9053\uff1a\u201c\u4ed6\u5e38\u5e38\u5531\u5c71\u6b4c\u7ed9\u6211\u542c\u3002\u8fd8\u7528\u6728\u5934\u524a\u6210\u5c0f\u72d7\u3001\u5c0f\u9a6c\u3001\u5c0f\u5a03\u5a03\u7ed9\u6211\u73a9\uff0c\u8bf4\u6211\u662f\u4e2a\u4e0d\u61c2\u4e8b\u7684\u5973\u5a03\u5a03\u3002\u540e\u6765\u4ed6\u4f24\u52bf\u5b8c\u5168\u597d\u4e86\uff0c\u6211\u89c1\u4ed6\u8d8a\u6765\u8d8a\u4e0d\u5f00\u5fc3\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u4ed6\u539f\u56e0\u3002\u4ed6\u8bf4\u4ed6\u820d\u4e0d\u5f97\u79bb\u5f00\u6211\u3002\u6211\u8bf4\uff1a\u2018\u90a3\u4e48\u6211\u5c31\u4f4f\u5728\u8fd9\u91cc\u966a\u4f60\u597d\u5566\uff01\u2019\u201c\u4ed6\u975e\u5e38\u5f00\u5fc3\uff0c\u5927\u53eb\u5927\u56b7\uff0c\u5728\u5c71\u5cf0\u4e0a\u4e24\u682a\u5927\u6811\u4e0a\u8df3\u4e0a\u8df3\u4e0b\uff0c\u50cf\u7334\u5b50\u4e00\u6837\u7ffb\u7b4b\u6597\u3002 \u3000\u3000\u201c\u4ed6\u5bf9\u6211\u8bf4\uff1a\u4ed6\u5f97\u5230\u4e86\u4e00\u5f20\u56fe\uff0c\u77e5\u9053\u4e86\u4e00\u4e2a\u5927\u5b9d\u85cf\u7684\u6240\u5728\uff0c\u5176\u4e2d\u91d1\u94f6\u73e0\u5b9d\uff0c\u591a\u5f97\u96be\u4ee5\u4f30\u91cf\u3002\u636e\u8bf4\u4ece\u524d\u71d5\u738b\u7be1\u4f4d\uff0c\u4ece\u5317\u4eac\u6253\u5230\u5357\u4eac\u3002\u5efa\u6587\u7687\u5e1d\u4ed3\u7687\u51fa\u8d70\uff0c\u628a\u5185\u5e93\u91cc\u7684\u73cd\u73e0\u5b9d\u8d1d\u57cb\u5728\u5357\u4eac\u4e00\u4e2a\u79d8\u5bc6\u5730\u65b9\u3002\u71d5\u738b\u63a5\u4f4d\u4e4b\u540e\uff0c\u641c\u904d\u4e86\u5357\u4eac\u5168\u57ce\u4e5f\u627e\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u6d3e\u4e09\u4fdd\u592a\u76d1\u51e0\u6b21\u4e0b\u897f\u6d0b\uff0c\u4e00\u6765\u662f\u4e3a\u4e86\u627e\u5bfb\u5efa\u6587\u7687\u5e1d\u7684\u4e0b\u843d\uff0c\u4e8c\u6765\u5219\u662f\u4e3a\u4e86\u63a2\u67e5\u8fd9\u6279\u73cd\u5b9d\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5728\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u53d1\u73b0\u7684\uff0c\u4fbf\u662f\u8fd9\u5f20\u5b9d\u85cf\u7684\u5730\u56fe\u3002\u201d \u3000\u3000\u6e29\u4eea\u7eed\u9053\uff1a\u201c\u4ed6\u8bf4\u6210\u7956\u7687\u5e1d\u4e00\u751f\u6ca1\u627e\u5230\u8fd9\u5f20\u5730\u56fe\uff0c\u4f46\u51e0\u767e\u5e74\u540e\uff0c\u5374\u8ba9\u4ed6\u65e0\u610f\u4e4b\u4e2d\u5f97\u5230\u4e86\uff0c\u773c\u4e0b\u4ed6\u5927\u4ec7\u5df2\u62a5\uff0c\u5c31\u8981\u53bb\u5bfb\u8fd9\u6279\u73cd\u5b9d\uff0c\u5bfb\u5230\u4e4b\u540e\uff0c\u4fbf\u6765\u63a5\u6211\uff0c\u73b0\u4e0b\u5148\u628a\u6211\u9001\u56de\u5bb6\u53bb\u3002\u201d \u3000\u3000\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u4ed6\u820d\u4e0d\u5f97\u6211\u79bb\u5f00\u4ed6\uff0c\u5176\u5b9e\u6211\u5fc3\u4e2d\u4e5f\u820d\u4e0d\u5f97\u3002\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u6211\u603b\u4e0d\u80fd\u5c31\u8fd9\u6837\u8ddf\u4e86\u4ed6\u53bb\u3002\u6211\u56de\u5bb6\u4e4b\u540e\uff0c\u5927\u5bb6\u5374\u77a7\u6211\u4e0d\u8d77\uff0c\u6211\u5f88\u662f\u607c\u6012\uff0c\u4ed6\u4eec\u6ca1\u672c\u4e8b\u4fdd\u62a4\u81ea\u5df1\u7684\u5973\u513f\uff0c\u6211\u6e05\u6e05\u767d\u767d\u7684\u56de\u5bb6\uff0c\u5927\u5bb6\u53cd\u800c\u6765\u7f9e\u8fb1\u6211\uff0c\u6211\u4e5f\u5c31\u4e0d\u7406\u4ed6\u4eec\u3002\u4e0d\u8ddf\u4ed6\u4eec\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u9752\u9752\u63a5\u53e3\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u5f88\u5bf9\uff0c\u4f60\u53c8\u505a\u9519\u4e86\u751a\u4e48\uff1f\u201d \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u6211\u5728\u5bb6\u91cc\u7b49\u4e86\u4e09\u4e2a\u6708\uff0c\u4e00\u5929\u665a\u4e0a\uff0c\u5ffd\u7136\u542c\u5f97\u7a97\u4e0b\u6709\u4eba\u5531\u6b4c\uff0c\u4e00\u542c\u58f0\u97f3\u6211\u5c31\u77e5\u9053\u662f\u4ed6\u5230\u4e86\uff0c\u5fd9\u6253\u5f00\u7a97\u5b50\u8ba9\u4ed6\u8fdb\u6765\u3002 \u3000\u3000\u6211\u4eec\u89c1\u4e86\u5f88\u662f\u6b22\u559c\u3002\u8fd9\u5929\u6211\u5c31\u548c\u4ed6\u597d\u4e86\uff0c\u6709\u4e86\u4f60\u8fd9\u5b69\u5b50\u3002 \u3000\u3000\u90a3\u662f\u6211\u81ea\u5df1\u613f\u610f\u7684\uff0c\u5230\u5982\u4eca\u6211\u4e5f\u4e00\u70b9\u4e0d\u540e\u6094\u3002\u4eba\u5bb6\u8bf4\u4ed6\u5f3a\u8feb\u6211\uff0c\u4e0d\u662f\u7684\u3002\u9752\u513f\uff0c\u4f60\u7238\u7238\u5f85\u4f60\u5988\u5988\u5f88\u597d\u3002\u6211\u4eec\u4e4b\u95f4\u4e00\u76f4\u5f88\u6069\u7231\u3002\u4ed6\u59cb\u7ec8\u5c0a\u91cd\u6211\uff0c\u4ece\u6765\u6ca1\u5f3a\u8feb\u8fc7\u6211\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u94a6\u4f69\u5979\u7684\u52c7\u6c14\uff0c\u542c\u5979\u8bf4\u5f97\u4e00\u5f80\u60c5\u6df1\uff0c\u4e0d\u7981\u51c4\u7136\u3002\u9752\u9752\u5ffd\u7136\u4f4e\u58f0\u5531\u4e86\u8d77\u6765\uff1a\u201c\u4ece\u5357\u6765\u4e86\u4e00\u7fa4\u96c1\uff0c\u4e5f\u6709\u6210\u53cc\u4e5f\u6709\u5b64\u5355\u3002\u6210\u53cc\u7684\u6b22\u5929\u559c\u5730\u58f0\u5639\u4eae\uff0c\u5b64\u5355\u7684\u843d\u5728\u540e\u5934\u98de\u4e0d\u4e0a\u3002\u4e0d\u770b\u6210\u53cc\uff0c\u53ea\u770b\u5b64\u5355\uff0c\u7ec6\u601d\u91cf\u4f60\u7684\u51c4\u51c9\uff0c\u548c\u6211\u662f\u4e00\u822c\u6837\uff01\u7ec6\u601d\u91cf\u4f60\u7684\u51c4\u51c9\uff0c\u548c\u6211\u662f\u4e00\u822c\u6837\u3002\u201d \u3000\u3000\u6b4c\u58f0\u5a07\u67d4\u5a49\u8f6c\uff0c\u5145\u6ee1\u4e86\u54c0\u6028\u4e4b\u60c5\u3002 \u3000\u3000\u6e29\u4eea\u51c4\u7136\u9053\uff1a\u201c\u90a3\u5c31\u662f\u5979\u7238\u7238\u5531\u7ed9\u6211\u542c\u8fc7\u7684\u4e00\u652f\u5c0f\u66f2\u3002\u8fd9\u5b69\u5b50\u4ece\u5c0f\u5728\u6211\u6000\u91cc\u542c\u8fd9\u4e9b\u6b4c\u513f\uff0c\u542c\u5f97\u591a\u4e86\u3002\u5c45\u7136\u4e5f\u8bb0\u4f4f\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u590f\u524d\u8f88\u90a3\u65f6\u5019\u60f3\u662f\u5df2\u7ecf\u627e\u5230\u4e86\u5b9d\u85cf\uff1f\u201d \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u4ed6\u8bf4\u8fd8\u6ca1\u627e\u5230\uff0c\u4e0d\u8fc7\u5df2\u6709\u4e86\u7ebf\u7d22\u3002\u4ed6\u5fc3\u4e2d\u6302\u5ff5\u7740\u6211\uff0c\u4e0d\u613f\u518d\u4e3a\u4e86\u5b9d\u85cf\u800c\u803d\u6401\u65f6\u65e5\u3002\u4ed6\u8bf4\u5230\u5b9d\u85cf\u7684\u4e8b\uff0c\u6211\u4e5f\u6ca1\u7559\u5fc3\u542c\u3002\u6211\u4eec\u5546\u91cf\u7740\u7b2c\u4e8c\u5929\u4e00\u65e9\u5c31\u5077\u5077\u7684\u6e9c\u8d70\uff0c\u5fc3\u4e2d\u5341\u5206\u6b22\u559c\uff0c\u751a\u4e48\u4e5f\u6ca1\u9632\u5907\uff0c\u4e0d\u6599\u60f3\u8bf4\u8bdd\u5374\u7ed9\u4eba\u5077\u542c\u53bb\u4e86\u3002 \u3000\u3000\u201c\u7b2c\u4e8c\u65e5\u5929\u8fd8\u6ca1\u4eae\uff0c\u6211\u6536\u62fe\u597d\u4e86\u8863\u670d\uff0c\u7559\u4e86\u4e00\u5c01\u4fe1\u7ed9\u7239\u7239\uff0c\u6b63\u60f3\u8981\u8d70\uff0c\u5ffd\u7136\u6709\u4eba\u6572\u95e8\uff0c\u6211\u5f53\u7136\u5f88\u6015\uff0c\u4ed6\u8bf4\u4e0d\u8981\u7d27\uff0c\u5c31\u662f\u5343\u519b\u4e07\u9a6c\u4e5f\u6740\u5f97\u51fa\u53bb\u3002\u4ed6\u63d0\u4e86\u91d1\u86c7\u5251\uff0c\u6253\u5f00\u623f\u95e8\uff0c\u8fdb\u6765\u7684\u7adf\u662f\u6211\u7239\u7239\u53ca\u5927\u4f2f\uff0c\u4e8c\u4f2f\u4e09\u4eba\u3002\u4ed6\u4eec\u90fd\u7a7a\u7740\u53cc\u624b\uff0c\u6ca1\u5e26\u5175\u5203\uff0c\u7a7f\u4e86\u957f\u888d\u9a6c\u8902\uff0c\u8138\u4e0a\u5c45\u7136\u90fd\u662f\u7b11\u563b\u563b\u5730\uff0c\u4e1d\u6beb\u6ca1\u6709\u654c\u610f\u3002\u6211\u4eec\u89c1\u4ed6\u4e09\u4eba\u8fd9\u526f\u6a21\u6837\uff0c\u5f88\u662f\u8be7\u5f02\u3002 \u3000\u3000\u201c\u7239\u7239\u8bf4\uff1a\u2018\u4f60\u4eec\u7684\u4e8b\u6211\u90fd\u77e5\u9053\u4e86\uff0c\u8fd9\u4e5f\u662f\u524d\u751f\u7684\u51a4\u5b7d\u3002\u4e0a\u6b21\u4f60\u4e0d\u6740\u6211\uff0c\u6211\u4e5f\u5f88\u627f\u4f60\u7684\u60c5\u3002\u4ee5\u540e\u54b1\u4eec\u7ed3\u6210\u4eb2\u5bb6\uff0c\u53ef\u4e0d\u8bb8\u518d\u52a8\u5200\u52a8\u67aa\u3002\u2019\u4ed6\u4ee5\u4e3a\u7239\u7239\u6015\u4ed6\u518d\u6740\u4eba\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u653e\u5fc3\uff0c\u6211\u65e9\u7b54\u5e94\u4e86\u4f60\u5c0f\u59d0\uff0c\u4e0d\u518d\u5bb3\u4f60\u5bb6\u7684\u4eba\uff01\u2019\u7239\u7239\u8bf4\uff1a\u2018\u79c1\u4e0b\u8d70\u53ef\u4e0d\u6210\uff0c\u987b\u5f97\u660e\u5a92\u6b63\u5a36\uff0c\u597d\u597d\u62dc\u5802\u3002\u2019\u4ed6\u6447\u5934\u4e0d\u4fe1\u3002\u6211\u7239\u7239\u8bf4\uff1a\u2018\u963f\u4eea\u662f\u6211\u7684\u72ec\u751f\u7231\u5973\uff0c\u603b\u4e0d\u80fd\u8ba9\u5979\u8ddf\u4eba\u79c1\u5954\uff0c\u4e00\u751f\u4e00\u4e16\u62ac\u4e0d\u8d77\u5934\u6765\u3002\u2019\u4ed6\u60f3\u8fd9\u8bdd\u4e0d\u9519\u3002\u54ea\u77e5\u4ed6\u4e3a\u4e86\u987e\u5168\u6211\uff0c\u5374\u4e0a\u4e86\u7239\u7239\u7684\u5f53\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ee4\u5c0a\u662f\u9a97\u4ed6\u7684\uff0c\u4e0d\u662f\u771f\u5fc3\uff1f\u201d \u3000\u3000\u6e29\u4eea\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\u5c31\u7559\u4ed6\u5728\u53a2\u623f\u91cc\u6b47\uff0c\u529e\u8d77\u559c\u4e8b\u6765\u3002\u4ed6\u59cb\u7ec8\u4fe1\u4e0d\u8fc7\uff0c\u6211\u5bb6\u9001\u7ed9\u4ed6\u5403\u7684\u9152\u996d\u8336\u6c34\uff0c\u4ed6\u5148\u62ff\u7ed9\u72d7\u5403\u3002\u72d7\u5403\u4e86\u4e00\u70b9\u6ca1\u4e8b\uff0c\u4f46\u4ed6\u4ecd\u4e0d\u653e\u5fc3\uff0c\u6beb\u4e0d\u6cbe\u5507\uff0c\u665a\u4e0a\u90fd\u62ff\u53bb\u5012\u6389\uff0c\u81ea\u5df1\u5728\u77f3\u6881\u9547\u4e0a\u4e70\u4e1c\u897f\u5403\u3002 \u3000\u3000\u201c\u4e00\u5929\u665a\u4e0a\uff0c\u5988\u5988\u62ff\u4e86\u4e00\u7897\u83b2\u5b50\u7fb9\u6765\uff0c\u5bf9\u6211\u8bf4\uff1a\u2018\u4f60\u62ff\u53bb\u7ed9\u59d1\u7237\u5403\u5427\uff01\u2019\u6211\u4e0d\u61c2\u4e8b\uff0c\u8fd8\u9053\u5988\u5988\u4f53\u60dc\u4ed6\uff0c\u9ad8\u9ad8\u5174\u5174\u7684\u6367\u5230\u623f\u91cc\u3002\u4ed6\u89c1\u6211\u4eb2\u624b\u6367\u53bb\uff0c\u559c\u6b22\u5f97\u751a\u4e48\u4e5f\u6ca1\u9632\u5907\uff0c\u51e0\u53e3\u5403\u4e86\u4e0b\u53bb\uff0c\u6b63\u548c\u6211\u8bf4\u8bdd\uff0c\u5ffd\u7136\u8138\u8272\u5927\u53d8\uff0c\u7ad9\u8d77\u6765\u53eb\u9053\uff1a\u2018\u963f\u4eea\uff0c\u4f60\u5fc3\u80a0\u8fd9\u6837\u72e0\uff01\u2019\u6211\u5413\u614c\u4e86\uff0c\u95ee\u9053\uff1a\u2018\u751a\u4e48\uff1f\u2019\u4ed6\u9053\uff1a\u2018\u4f60\u4e3a\u751a\u4e48\u4e0b\u6211\u7684\u6bd2\uff1f\u2019\u201d \u3000\u3000\u201c\u4f60\u4e3a\u751a\u4e48\u4e0b\u6211\u7684\u6bd2\uff1f\u201d\u8fd9\u53e5\u8bdd\uff0c\u867d\u5728\u6e29\u4eea\u8f7b\u67d4\u7684\u8bed\u97f3\u4e2d\u8bf4\u6765\uff0c\u8fd8\u662f\u5145\u6ee1\u4e86\u68ee\u7136\u53ef\u6016\u4e4b\u610f\uff0c\u60f3\u89c1\u5f53\u65f6\u91d1\u86c7\u90ce\u541b\u662f\u5982\u4f55\u6124\u6012\uff0c\u53c8\u662f\u5982\u4f55\u4f24\u5fc3\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u542c\u4e86\uff0c\u4e0d\u7531\u5f97\u6bdb\u9aa8\u609a\u7136\u3002 \u3000\u3000\u6e29\u4eea\u7684\u773c\u6cea\u4e00\u6ef4\u6ef4\u843d\u5728\u8863\u895f\u4e4b\u4e0a\uff0c\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u5bc2\u9759\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u4ead\u5916\u78d4\u78d4\u602a\u7b11\u3002\u4e09\u4eba\u6025\u5fd9\u56de\u5934\uff0c\u53ea\u89c1\u6e29\u6c0f\u4e94\u5144\u5f1f\u5e76\u80a9\u8d70\u8fd1\uff0c\u540e\u9762\u8ddf\u7740\u4e8c\u4e09\u5341\u4eba\uff0c\u624b\u4e2d\u90fd\u62ff\u7740\u5175\u5203\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u559d\u9053\uff1a\u201c\u963f\u4eea\uff0c\u4f60\u628a\u81ea\u5df1\u7684\u4e11\u4e8b\u8bf4\u7ed9\u5916\u4eba\u542c\uff0c\u8fd8\u8981\u8138\u4e48\uff1f\u201d \u3000\u3000\u6e29\u4eea\u80c0\u7ea2\u4e86\u8138\uff0c\u8981\u5f85\u56de\u7b54\uff0c\u968f\u5373\u5fcd\u4f4f\uff0c\u8f6c\u5934\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5341\u4e5d\u5e74\u6765\uff0c\u6211\u6ca1\u8ddf\u7239\u7239\u8bf4\u8fc7\u4e00\u53e5\u8bdd\uff0c\u4ee5\u540e\u6211\u4e5f\u6c38\u4e0d\u4f1a\u548c\u4ed6\u8bf4\u8bdd\u3002\u6211\u672c\u6765\u65e9\u4e0d\u8be5\u518d\u4f4f\u5728\u6e29\u5bb6\uff0c\u53ef\u662f\u6211\u6709\u4e86\u9752\u9752\uff0c\u53c8\u80fd\u53bb\u54ea\u91cc\uff1f\u518d\u8bf4\uff0c\u6211\u603b\u76fc\u671b\u4ed6\u6ca1\u6709\u6b7b\uff0c\u6709\u4e00\u5929\u4f1a\u518d\u6765\u627e\u6211\u3002\u6211\u82e5\u662f\u79bb\u5f00\u4e86\u8fd9\u91cc\uff0c\u4ed6\u53c8\u600e\u627e\u5f97\u5230\u6211\uff1f\u4ed6\u65e2\u7136\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6211\u4e5f\u6ca1\u751a\u4e48\u987e\u5fcc\u4e86\u3002\u6211\u4e0d\u6015\u4ed6\u4eec\uff0c\u4f60\u6015\u4e0d\u6015\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u8fd8\u6ca1\u7b54\u8bdd\uff0c\u9752\u9752\u5df2\u62a2\u7740\u9053\uff1a\u201c\u627f\u5fd7\u5927\u54e5\u4e0d\u4f1a\u6015\u7684\u3002\u201d \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u8bf4\u4e0b\u53bb\u3002\u201d\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u6025\u5f97\u54ed\u4e86\u51fa\u6765\uff0c\u4e0d\u77e5\u9053\u8981\u600e\u6837\u8bf4\u3001\u600e\u6837\u505a\u624d\u597d\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u623f\u95e8\u88ab\u4eba\u8e22\u98de\uff0c\u8bb8\u591a\u4eba\u624b\u6267\u4e86\u5200\u67aa\u6d8c\u4e86\u8fdb\u6765\u3002\u201d\u5979\u5411\u4ead\u5916\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\u7ad9\u5728\u623f\u95e8\u5916\u7684\uff0c\u5c31\u662f\u8fd9\u4e9b\u4eba\u3002\u4ed6\u4eec\u2026\u2026\u4ed6\u4eec\u624b\u91cc\u90fd\u62ff\u7740\u6697\u5668\u3002\u7239\u7239\u603b\u7b97\u5bf9\u6211\u8fd8\u6709\u51e0\u5206\u7236\u5973\u4e4b\u60c5\uff0c\u53eb\u9053\uff1a\u2018\u963f\u4eea\uff0c\u51fa\u6765\uff01\u2019\u6211\u77e5\u9053\u4ed6\u4eec\u8981\u7b49\u6211\u51fa\u53bb\u4e4b\u540e\uff0c\u7acb\u523b\u5411\u4ed6\u53d1\u5c04\u6697\u5668\uff0c\u623f\u95f4\u53ea\u662f\u8fd9\u4e48\u4e00\u70b9\u5730\u65b9\uff0c\u4ed6\u5f80\u54ea\u91cc\u8eb2\u53bb\uff1f\u6211\u53eb\u9053\uff1a\u2018\u6211\u4e0d\u51fa\u6765\uff0c\u4f60\u4eec\u8fde\u6211\u4e00\u8d77\u6740\u4e86\u5427\uff01\u2019\u6211\u6321\u5728\u4ed6\u8eab\u524d\uff0c\u5fc3\u4e2d\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\uff0c\u8981\u4fdd\u62a4\u4ed6\uff0c\u4e0d\u8ba9\u4ed6\u7ed9\u4eba\u4f24\u5bb3\u3002 \u3000\u3000\u201c\u4ed6\u672c\u6765\u7709\u5934\u6df1\u9501\uff0c\u5750\u5728\u6905\u4e0a\uff0c\u4ee5\u4e3a\u6211\u548c\u5bb6\u91cc\u7684\u4eba\u4e32\u901a\u4e86\u4e0b\u6bd2\u5bb3\u4ed6\uff0c\u5341\u5206\u4f24\u5fc3\u96be\u53d7\uff0c\u4e5f\u4e0d\u60f3\u52a8\u624b\u53cd\u6297\uff0c\u542c\u6211\u8fd9\u4e48\u8bf4\uff0c\u7a81\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u5f88\u5f00\u5fc3\u7684\u9053\uff1a\u2018\u4f60\u4e0d\u77e5\u83b2\u5b50\u7fb9\u91cc\u6709\u6bd2\uff1f\u2019\u6211\u7aef\u8d77\u7897\u6765\uff0c\u89c1\u7897\u91cc\u8fd8\u5269\u4e86\u4e00\u4e9b\u513f\u7fb9\u6c41\uff0c\u4e00\u53e3\u559d\u4e0b\uff0c\u8bf4\u9053\uff1a\u2018\u6211\u8ddf\u4f60\u4e00\u8d77\u6b7b\uff01\u2019\u4ed6\u4e00\u638c\u628a\u7897\u6253\u843d\uff0c\u4f46\u6211\u5df2\u7ecf\u559d\u4e86\u3002\u4ed6\u7b11\u9053\uff1a\u2018\u597d\uff0c\u5927\u5bb6\u4e00\u8d77\u6b7b\uff01\u2019\u8f6c\u5934\u5411\u4ed6\u4eec\u9a82\u9053\uff1a\u2018\u4f7f\u8fd9\u79cd\u5351\u9119\u9634\u6bd2\u7684\u624b\u6bb5\uff0c\u4f60\u4eec\u4e5f\u4e0d\u6015\u4e11\u4e48\uff1f\u2019\u201c\u5927\u4f2f\u4f2f\u6012\u9053\uff1a\u2018\u8c01\u7528\u6bd2\u4e86\uff1f\u4e0b\u6bd2\u7684\u4e0d\u662f\u82f1\u96c4\u597d\u6c49\u3002\u4f60\u81ea\u6043\u672c\u9886\u9ad8\uff0c\u5c31\u51fa\u6765\u6597\u6597\uff01\u201d\u4ed6\u8bf4\uff1a\u2018\u597d\uff01\u2019\u5c31\u51fa\u53bb\u548c\u4ed6\u4eec\u4e94\u5144\u5f1f\u6253\u4e86\u8d77\u6765\u3002\u4ed6\u559d\u7684\u83b2\u5b50\u7fb9\u91cc\u867d\u6ca1\u6bd2\u836f\uff0c\u4f46\u653e\u7740\u4ed6\u4eec\u6e29\u5bb6\u79d8\u5236\u7684\u2018\u9189\u4ed9\u871c\u2019\uff0c\u53ea\u8981\u559d\u4e86\uff0c\u6162\u6162\u4f1a\u5168\u8eab\u65e0\u529b\uff0c\u660f\u7761\u5982\u6b7b\uff0c\u8981\u8fc7\u4e00\u65e5\u4e00\u591c\u624d\u80fd\u9192\u6765\u3002\u8fd9\u4e9b\u4eba\u54ea\uff0c\u8fd8\u820d\u4e0d\u5f97\u7528\u6bd2\u836f\u5bb3\u6b7b\u4ed6\uff0c\u60f3\u628a\u4ed6\u8ff7\u5012\uff0c\u518d\u6162\u6162\u6765\u6298\u78e8\u4ed6\u3002\u4ed6\u4eec\u2026\u2026\u4ed6\u4eec\u5f53\u771f\u662f\u82f1\u96c4\u597d\u6c49\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u8bed\u6c14\u4e2d\u5145\u6ee1\u6028\u6bd2\uff0c\u53ea\u662f\u5979\u751f\u6027\u6e29\u67d4\uff0c\u4e0d\u4f1a\u4ee5\u6076\u8bed\u9a82\u4eba\u3002 \u3000\u3000\u6e29\u65b9\u65bd\u6012\u9053\uff1a\u201c\u8fd9\u65e0\u803b\u8d31\u4eba\uff0c\u65e9\u5c31\u8be5\u6740\u4e86\uff0c\u517b\u5979\u5230\u4eca\u65e5\uff0c\u53cd\u800c\u6069\u5c06\u4ec7\u62a5\uff01\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u5a18\u513f\u5728\u6e29\u5bb6\u5403\u4e86\u5341\u51e0\u5e74\u996d\uff0c\u53ef\u662f\u56db\u7237\u7237\uff0c\u6211\u8fd9\u4e24\u5e74\u6765\uff0c\u7ed9\u4f60\u4eec\u627e\u4e86\u591a\u5c11\u91d1\u94f6\u8d22\u5b9d\uff1f\u5c31\u662f\u4e00\u767e\u4e2a\u4eba\uff0c\u4e00\u8f88\u5b50\u4e5f\u5403\u4e0d\u5b8c\u5427\uff1f\u6211\u5a18\u513f\u4fe9\u6b20\u4f60\u4eec\u6e29\u5bb6\u7684\u503a\uff0c\u65e9\u8fd8\u6e05\u5566\uff01\u201d\u6e29\u65b9\u8fbe\u4e0d\u613f\u5728\u5916\u4eba\u4e4b\u524d\u591a\u63d0\u5bb6\u95e8\u4e11\u4e8b\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u59d3\u8881\u7684\uff0c\u4f60\u6562\u4e0d\u6562\u8ddf\u6211\u4eec\u4e94\u5144\u5f1f\u4e00\u8d77\u6597\u6597\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u524d\u4e24\u65e5\u5ff5\u5728\u4ed6\u4eec\u662f\u9752\u9752\u7684\u957f\u8f88\uff0c\u5bf9\u4e4b\u793c\u6570\u5468\u5230\uff0c\u8fd9\u65f6\u542c\u6e29\u4eea\u8bf4\u4e86\u4ed6\u4eec\u7684\u9634\u9669\u6bd2\u8fa3\uff0c\u4e0d\u89c9\u6ee1\u6000\u6124\u6012\uff0c\u53eb\u9053\uff1a\u201c\u54fc\uff0c\u522b\u8bf4\u4e94\u4eba\uff0c\u4f60\u4eec\u5c31\u662f\u6709\u5341\u5144\u5f1f\u9f50\u4e0a\uff0c\u6211\u53c8\u4f55\u60e7\uff1f\u201d \u3000\u3000\u6e29\u4eea\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u5929\u665a\u4e0a\uff0c\u4ed6\u4eec\u4e5f\u662f\u4e94\u5144\u5f1f\u6253\u4ed6\u4e00\u4eba\u3002\u672c\u6765\u4ed6\u80fd\u62b5\u654c\u5f97\u4f4f\u7684\uff0c\u4f46\u4ed6\u559d\u4e86\u2018\u9189\u4ed9\u871c\u2019\u4e4b\u540e\uff0c\u8d8a\u6253\u8d8a\u662f\u624b\u8db3\u9178\u8f6f\uff0c\u4ed6\u4eec\u4e94\u5144\u5f1f\u6709\u4e2a\u7ec3\u597d\u4e86\u7684\u2018\u4e94\u884c\u9635\u2019\uff0c\u6253\u8d77\u67b6\u6765\uff0c\u4e94\u5144\u5f1f\u5c31\u5982\u662f\u4e00\u4e2a\u4eba\u2026\u2026\u201d\u6e29\u65b9\u5c71\u559d\u9053\uff1a\u201c\u963f\u4eea\uff0c\u4f60\u5403\u91cc\u6252\u5916\uff0c\u6cc4\u6e29\u5bb6\u7684\u5e95\uff1f\u201d \u3000\u3000\u6e29\u4eea\u4e0d\u7406\u7236\u4eb2\u7684\u8bdd\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ed6\u6025\u7740\u60f3\u51fb\u5012\u4e94\u4eba\u4e2d\u7684\u4e00\u4eba\uff0c\u5c31\u53ef\u7834\u4e86\u8fd9\u4e94\u884c\u9635\uff0c\u4f46\u4ed6\u6447\u6447\u6643\u6643\u7684\u8d8a\u6765\u8d8a\u4e0d\u884c\u3002\u6211\u53eb\u9053\uff1a\u2018\u4f60\u5feb\u8d70\u5427\uff0c\u6211\u6c38\u4e0d\u8d1f\u4f60\uff01\u2019\u201d\u5979\u8fd9\u4e00\u58f0\u53eb\u5524\u58f0\u97f3\u51c4\u5389\uff0c\u4f3c\u4e4e\u5c31\u548c\u90a3\u5929\u665a\u662f\u53eb\u7684\u4e00\u6837\u3002\u9752\u9752\u5413\u6015\u4e86\uff0c\u8fde\u53eb\uff1a\u201c\u5988\u5988\uff01\u201d\u8881\u627f\u5fd7\u8bf4\u9053\uff1a\u201c\u4f2f\u6bcd\u56de\u623f\u4f11\u606f\u5427\uff0c\u6211\u548c\u4ee4\u5c0a\u4ed6\u4eec\u8c08\u4e00\u8c08\uff0c\u660e\u513f\u518d\u6765\u77a7\u4f60\u3002\u201d \u3000\u3000\u6e29\u4eea\u62c9\u4f4f\u4ed6\u7684\u8863\u8896\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u5728\u5fc3\u4e2d\u618b\u4e86\u5341\u4e5d\u5e74\u5566\uff0c\u4eca\u513f\u975e\u8bf4\u51fa\u6765\u4e0d\u53ef\u3002\u8881\u76f8\u516c\uff0c\u4f60\u542c\u6211\u8bf4\u5440\uff01\u201d\u8881\u627f\u5fd7\u542c\u5979\u8bdd\u4e2d\u5e26\u7740\u54ed\u58f0\uff0c\u70b9\u5934\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u542c\u7740\u5462\u3002\u201d \u3000\u3000\u6e29\u4eea\u4ecd\u7136\u662f\u7d27\u7d27\u626f\u4f4f\u4ed6\u8863\u8896\u4e0d\u653e\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u4ed6\u7684\u547d\uff0c\u53ef\u662f\u66f4\u52a0\u8981\u7d27\u7684\uff0c\u4ed6\u4eec\u60f3\u53d1\u8d22\u3002\u4ed6\u518d\u6253\u4e00\u9635\uff0c\u8eab\u4e0a\u53d7\u4e86\u4f24\uff0c\u652f\u6301\u4e0d\u4f4f\uff0c\u8dcc\u5728\u5730\u4e0b\uff0c\u7ec8\u4e8e\u2026\u2026\u7ec8\u4e8e\u7ed9\u4ed6\u4eec\u64d2\u4f4f\u4e86\uff0c\u6211\u6251\u5230\u4ed6\u8eab\u4e0a\uff0c\u4e5f\u4e0d\u77e5\u662f\u54ea\u4e00\u4f4d\u53d4\u4f2f\u5c06\u6211\u4e00\u811a\u8e22\u5f00\u3002\u4ed6\u4eec\u903c\u7740\u4ed6\u4ea4\u51fa\u85cf\u5b9d\u7684\u5730\u56fe\u6765\u3002\u4ed6\u8bf4\uff1a\u2018\u90a3\u56fe\u4e0d\u5728\u6211\u8eab\u4e0a\uff0c\u8c01\u6709\u79cd\u5c31\u8ddf\u6211\u53bb\u62ff\u3002 \u3000\u3000\u4ed6\u4eec\u7ec6\u641c\u4ed6\u8eab\u4e0a\uff0c\u679c\u7136\u6ca1\u56fe\u3002\u8fd9\u6837\u5c31\u4e3a\u96be\u5566\uff0c\u653e\u4e86\u4ed6\u5427\uff0c\u7b49\u836f\u6027\u4e00\u8fc7\uff0c\u53ef\u6ca1\u4eba\u518d\u5236\u5f97\u4f4f\u4ed6\u3002\u6740\u4e86\u4ed6\u5427\uff0c\u90a3\u5927\u5b9d\u85cf\u53ef\u6c38\u8fdc\u5f97\u4e0d\u5230\u624b\u3002\u6700\u540e\u8fd8\u662f\u6211\u7684\u7239\u7239\u4e3b\u610f\u513f\u9ad8\u660e\uff0c\u54c8\u54c8\uff0c\u597d\u806a\u660e\uff0c\u4e0d\u662f\u5417\uff1f\u90a3\u65f6\u5019\u4ed6\u5df2\u7ecf\u660f\u4e86\u8fc7\u53bb\uff0c\u6211\u4e5f\u6655\u5012\u4e86\u3002\u7b49\u6211\u9192\u6765\uff0c\u4ed6\u4eec\u5df2\u7ecf\u628a\u4ed6\u7684\u811a\u7b4b\u548c\u624b\u7b4b\u90fd\u6311\u65ad\u4e86\uff0c\u6559\u4ed6\u7a7a\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u6c38\u8fdc\u4e0d\u80fd\u518d\u4f7f\u52b2\uff0c\u7136\u540e\u903c\u7740\u4ed6\u53bb\u53d6\u56fe\u5bfb\u5b9d\u3002\u771f\u806a\u660e\uff0c\u662f\u4e0d\u662f\uff1f\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u8881\u627f\u5fd7\u89c1\u5979\u773c\u5149\u6563\u4e71\uff0c\u547c\u5438\u6025\u4fc3\uff0c\u8bf4\u8bdd\u5df2\u6709\u4e9b\u795e\u667a\u5931\u5e38\u3002\u529d\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u4f60\u8fd8\u662f\u56de\u623f\u53bb\u6b47\u6b47\u3002\u201d \u3000\u3000\u6e29\u4eea\u9053\uff1a\u201c\u4e0d\uff0c\u7b49\u4f60\u4e00\u8d70\uff0c\u4ed6\u4eec\u5c31\u628a\u6211\u6740\u6b7b\u4e86\uff0c\u6211\u8981\u8bf4\u5b8c\u4e86\u624d\u80fd\u6b7b\u2026\u2026\u4ed6\u4eec\u62bc\u7740\u4ed6\u8d70\u4e86\u3002\u8fd8\u6709\u5d06\u5cd2\u6d3e\u7684\u4e24\u540d\u597d\u624b\u540c\u53bb\u3002 \u3000\u3000\u4eba\u5bb6\u90fd\u60f3\u53d1\u8fd9\u7b14\u6a2a\u8d22\u3002\u4f46\u4e0d\u77e5\u600e\u6837\uff0c\u8fd8\u662f\u88ab\u4ed6\u9003\u8131\u4e86\u3002\u591a\u534a\u662f\u4ed6\u7ed9\u4e86\u4ed6\u4eec\u4e00\u5f20\u56fe\uff0c\u4ed6\u4eec\u4e00\u5feb\u6d3b\uff0c\u9632\u5907\u5c31\u758f\u4e86\u3002\u4ed6\u4eec\u5f88\u806a\u660e\uff0c\u6211\u90a3\u90ce\u541b\u53ef\u4e5f\u4e0d\u8822\u54ea\u3002\u4ed6\u4eec\u4e03\u4e2a\u4eba\u62ff\u5230\u8fd9\u5f20\u85cf\u5b9d\u56fe\uff0c\u4f60\u62a2\u6211\u593a\uff0c\u4e94\u5144\u5f1f\u5408\u8c0b\uff0c\u628a\u5d06\u5cd2\u6d3e\u7684\u4e24\u4eba\u5148\u7ed9\u5bb3\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6e29\u65b9\u4e49\u5389\u58f0\u9a82\u9053\uff1a\u201c\u963f\u4eea\uff0c\u4f60\u518d\u80e1\u8bf4\u516b\u9053\uff0c\u53ef\u5c0f\u5fc3\u7740\uff01\u201d \u3000\u3000\u6e29\u4eea\u7b11\u9053\uff1a\u201c\u6211\u5e72\u4e48\u5c0f\u5fc3\uff1f\u4f60\u4ee5\u4e3a\u6211\u8fd8\u6015\u6b7b\u4e48\uff1f\u201d\u8f6c\u5934\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54ea\u77e5\u9053\u8fd9\u5f20\u56fe\u5374\u662f\u5047\u7684\u3002\u4ed6\u4eec\u4e94\u4eba\u5728\u5357\u4eac\u94bb\u6765\u94bb\u53bb\u641e\u4e86\u5927\u534a\u5e74\uff0c\u82b1\u4e86\u51e0\u5343\u4e24\u94f6\u5b50\u672c\u94b1\uff0c\u4e00\u4e2a\u5c0f\u94b1\u4e5f\u6ca1\u627e\u5230\uff0c\u54c8\u54c8\uff0c\u771f\u662f\u518d\u6709\u8da3\u4e5f\u6ca1\u6709\u5566\u3002\u201d \u3000\u3000\u6e29\u6c0f\u5144\u5f1f\u7a7a\u81ea\u5728\u4ead\u5916\u6a2a\u7709\u6012\u76ee\uff0c\u5374\u754f\u60e7\u8881\u627f\u5fd7\uff0c\u4e0d\u6562\u51b2\u8fdb\u4ead\u6765\u3002 \u3000\u3000\u6e29\u4eea\u8bf4\u5230\u8fd9\u91cc\uff0c\u5446\u5446\u7684\u51fa\u795e\uff0c\u4f4e\u58f0\u7f13\u7f13\u7684\u9053\uff1a\u201c\u4ed6\u8fd9\u4e00\u53bb\uff0c\u6211\u5c31\u6ca1\u518d\u5f97\u5230\u4ed6\u7684\u97f3\u8baf\u3002\u4ed6\u624b\u811a\u4e0a\u7684\u7b4b\u90fd\u65ad\u4e86\uff0c\u5df2\u6210\u5e9f\u4eba\u3002\u4ed6\u662f\u8fd9\u6837\u7684\u5fc3\u9ad8\u6c14\u50b2\uff0c\u4e0d\u75db\u6b7b\u4e5f\u4f1a\u6c14\u6b7b\u2026\u2026\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u53c8\u53eb\uff1a\u201c\u59d3\u8881\u7684\uff0c\u8fd9\u5c0f\u8d31\u4eba\u8bf4\u8d77\u6211\u4eec\u6e29\u6c0f\u7684\u4e94\u884c\u9635\uff0c\u4f60\u5df2\u542c\u5230\u4e86\uff0c\u6709\u79cd\u7684\u5c31\u51fa\u6765\u8bd5\u8bd5\u3002\u201d\u6e29\u4eea\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff0c\u522b\u8ddf\u4ed6\u4eec\u6597\u3002\u201d\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u6240\u906d\u51a4\u5c48\uff0c\u7ec8\u4e8e\u662f\u6709\u4eba\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u66fe\u548c\u6e29\u6c0f\u4e94\u5144\u5f1f\u4e00\u4e00\u8f83\u91cf\u8fc7\u3002\u77e5\u9053\u5355\u6253\u72ec\u6597\uff0c\u6ca1\u4e00\u4e2a\u662f\u81ea\u5df1\u5bf9\u624b\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u4e94\u4eba\u9f50\u4e0a\uff0c\u518d\u52a0\u4e0a\u6709\u751a\u4e48\u64cd\u7ec3\u7eaf\u719f\u7684\u4e94\u884c\u9635\uff0c\u53ea\u6015\u786e\u662f\u4e0d\u6613\u51fb\u7834\u3002\u521d\u6b21\u8f83\u91cf\u65f6\u53cc\u65b9\u5e76\u65e0\u51a4\u4ec7\uff0c\u624b\u4e0b\u4e92\u76f8\u5bb9\u60c5\uff0c\u73b0\u4e0b\u81ea\u5df1\u5df2\u77e5\u4ed6\u4eec\u9690\u79c1\uff0c\u800c\u4ed6\u4eec\u53c8\u8ba4\u5b9a\u81ea\u5df1\u4e0e\u91d1\u86c7\u90ce\u541b\u9887\u6709\u6e0a\u6e90\uff0c\u8fd9\u79cd\u4eba\u751a\u4e48\u9634\u72e0\u6bd2\u8fa3\u7684\u624b\u6bb5\u90fd\u4f7f\u5f97\u51fa\uff0c\u4e00\u4e0d\u7559\u795e\uff0c\u60e8\u7978\u7acb\u81f3\uff0c\u81ea\u5df1\u5374\u53c8\u4e0d\u6b32\u5bf9\u4ed6\u4eec\u75db\u4e0b\u6740\u624b\uff0c\u4e00\u65f6\u4e0d\u7981\u9887\u4e3a\u8e0c\u8e87\u3002 \u3000\u3000\u6e29\u65b9\u4e49\u53eb\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4e0d\u6562\u4e48\uff1f\u4e56\u4e56\u7684\u8ddf\u7237\u7237\u4eec\u53e9\u4e09\u4e2a\u54cd\u5934\uff0c\u5c31\u653e\u4f60\u51fa\u53bb\u3002\u201d\u6e29\u65b9\u65bd\u9634\u68ee\u68ee\u7684\u9053\uff1a\u201c\u8fd9\u65f6\u5019\u53e9\u5934\u4e5f\u4e0d\u6210\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5bfb\u601d\uff1a\u201c\u987b\u5f97\u9759\u4e0b\u6765\u597d\u597d\u60f3\u4e00\u60f3\uff0c\u7b79\u601d\u4e2a\u5584\u7b56\u3002\u201d\u4ed6\u521d\u51fa\u8305\u5e90\uff0c\u9605\u5386\u751a\u6d45\uff0c\u4e0d\u4f3c\u6c5f\u6e56\u4e0a\u7684\u8001\u624b\uff0c\u4e00\u9047\u96be\u9898\uff0c\u7acb\u751f\u5e94\u53d8\u4e4b\u8ba1\uff0c\u4e8e\u662f\u6717\u58f0\u9053\uff1a\u201c\u6e29\u6c0f\u4e94\u884c\u9635\u65e2\u662f\u5389\u5bb3\u65e0\u6bd4\uff0c\u665a\u8f88\u5012\u4e5f\u60f3\u89c1\u8bc6\u89c1\u8bc6\u3002\u4e0d\u8fc7\u6211\u73b0\u4e0b\u751a\u662f\u75b2\u7d2f\uff0c\u8ba9\u6211\u4f11\u606f\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6210\u5417\uff1f\u201d \u3000\u3000\u6e29\u65b9\u4e49\u968f\u53e3\u9053\uff1a\u201c\u4e00\u4e2a\u65f6\u8fb0\u5c31\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4f60\u518d\u6328\u4e0a\u5341\u5929\u534a\u6708\u4e5f\u9003\u4e0d\u4e86\u3002\u201d\u6e29\u65b9\u5c71\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u522b\u4f7f\u751a\u4e48\u8be1\u8ba1\uff0c\u54b1\u4eec\u9a6c\u4e0a\u7ed9\u4ed6\u5e72\u3002\u201d\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u4e8c\u5f1f\u5df2\u7ecf\u7b54\u5e94\u4e86\u4ed6\uff0c\u5c31\u8ba9\u4ed6\u591a\u6d3b\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4e5f\u6559\u4ed6\u6b7b\u800c\u65e0\u6028\u3002\u201d \u3000\u3000\u6e29\u4eea\u6025\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4f60\u522b\u4e0a\u5f53\uff0c\u4ed6\u4eec\u884c\u4e8b\u5411\u6765\u72e0\u8fa3\uff0c\u54ea\u6709\u8fd9\u4e48\u597d\u5fc3\uff0c\u80af\u8ba9\u4f60\u591a\u4f11\u606f\u4e00\u4e2a\u65f6\u8fb0\uff1f\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u4eec\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u5c31\u662f\u90a3\u4e2a\u5b9d\u85cf\u3002\u4ed6\u4eec\u8981\u60f3\u6cd5\u5b50\u5bb3\u4f60\uff0c\u8981\u6311\u65ad\u4f60\u7684\u624b\u7b4b\u811a\u7b4b\uff0c\u903c\u4f60\u53bb\u5e2e\u7740\u5bfb\u5b9d\u3002\u4f60\u5feb\u548c\u9752\u9752\u4e00\u8d77\u8d70\u5427\uff0c\u8d70\u5f97\u8d8a\u8fdc\u8d8a\u597d\u3002\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u542c\u5979\u8bf4\u7a7f\u4e86\u81ea\u5df1\u7528\u5fc3\uff0c\u8138\u8272\u66f4\u662f\u94c1\u9752\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a\u8fd8\u60f3\u8d70\u5f97\u8d8a\u8fdc\u8d8a\u597d\uff1f\u54fc\uff0c\u5ff5\u5934\u5012\u8f6c\u7684\u633a\u7f8e\u3002\u59d3\u8881\u7684\uff0c\u4f60\u5230\u7ec3\u6b66\u5385\u4e0a\u4f11\u606f\u53bb\u5427\u3002\u5f85\u4f1a\u52a8\u624b\uff0c\u5927\u5bb6\u65b9\u4fbf\u4e9b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\u5427\uff01\u201d\u7ad9\u8d77\u8eab\u6765\u3002\u6e29\u4eea\u6bcd\u5973\u77e5\u9053\u4e94\u884c\u9635\u7684\u5389\u5bb3\uff0c\u5fc3\u4e2d\u7126\u6025\uff0c\u4f46\u4e5f\u6ca1\u6cd5\u963b\u62e6\uff0c\u53ea\u5f97\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u4e00\u9f50\u51fa\u4ead\u3002 \u3000\u3000\u5230\u4e86\u7ec3\u6b66\u5385\u4e2d\uff0c\u6e29\u65b9\u8fbe\u547d\u4eba\u70b9\u8d77\u6570\u5341\u652f\u5de8\u70db\uff0c\u8bf4\u9053\uff1a\u201c\u8721\u70db\u70b9\u5230\u5c3d\u5904\uff0c\u4f60\u603b\u517b\u8db3\u7cbe\u795e\u4e86\u5427\uff1f\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u5728\u4e2d\u95f4\u4e00\u5f20\u6905\u4e0a\u5750\u4e0b\u3002\u6e29\u6c0f\u4e94\u8001\u5404\u81ea\u62ff\u8d77\u6905\u5b50\uff0c\u6392\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u5c06\u4ed6\u56f4\u5728\u4e2d\u95f4\uff0c\u4e94\u4eba\u95ed\u76ee\u9759\u5750\u3002\u5728\u4e94\u4eba\u4e4b\u5916\uff0c\u6e29\u5357\u626c\u3001\u6e29\u6b63\u7b49\u77f3\u6881\u6d3e\u4e2d\u5341\u516d\u540d\u597d\u624b\uff0c\u53c8\u5206\u5750\u5341\u516d\u5f20\u77ee\u51f3\uff0c\u56f4\u6210\u4e00\u4e2a\u5927\u5708\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u8fd9\u5341\u516d\u4eba\u6309\u7740\u516b\u5366\u65b9\u4f4d\u800c\u5750\uff0c\u4e43\u662f\u4f5c\u4e3a\u4e94\u884c\u9635\u7684\u8f85\u4f50\uff0c\u5fc3\u60f3\uff1a\u201c\u4e94\u884c\u9635\u5916\u53c8\u6709\u516b\u5366\u9635\uff0c\u8981\u7834\u6b64\u9635\uff0c\u66f4\u662f\u96be\u4e0a\u52a0\u96be\u3002\u201d\u4ed6\u7aef\u5750\u6905\u4e0a\uff0c\u7ec6\u601d\u5e08\u95e8\u6240\u6388\u5404\u9879\u6b66\u529f\uff0c\u53cd\u590d\u601d\u8003\uff0c\u603b\u89c9\u5728\u8fd9\u4e8c\u5341\u4e00\u540d\u597d\u624b\u7684\u56f4\u653b\u4e4b\u4e0b\uff0c\u6700\u591a\u53ea\u80fd\u81ea\u4fdd\uff0c\u8981\u60f3\u51b2\u7834\u9635\u52bf\u8131\u8eab\uff0c\u53ea\u6015\u96be\u4ee5\u529e\u5230\uff0c\u65f6\u5019\u4e00\u957f\uff0c\u7cbe\u795e\u529b\u6c14\u52bf\u5fc5\u4e0d\u6d4e\uff0c\u7ec8\u987b\u843d\u8d25\u3002\u5c31\u7b97\u4ee5\u6728\u6851\u9053\u957f\u6240\u4f20\u8f7b\u529f\u9003\u51fa\u9635\u53bb\uff0c\u90a3\u6279\u9ec4\u91d1\u53c8\u600e\u80fd\u593a\u56de\uff1f\u7559\u4e0b\u6e29\u4eea\u6bcd\u5973\uff0c\u5979\u4e8c\u4eba\u96be\u514d\u6740\u8eab\u4e4b\u7978\uff0c\u90a3\u53ef\u5982\u4f55\u662f\u597d\uff1f \u3000\u3000\u6b63\u7126\u6025\u95f4\uff0c\u5ffd\u7136\u7075\u673a\u4e00\u52a8\uff0c\u60f3\u5230\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u6700\u540e\u7684\u6570\u9875\u3002 \u3000\u3000\u90a3\u51e0\u9875\u4e0a\u7684\u6b66\u529f\u5f53\u65f6\u63e3\u6478\u4e0d\u900f\uff0c\u76f4\u5230\u91cd\u5165\u5ca9\u6d1e\uff0c\u770b\u4e86\u77f3\u58c1\u4e0a\u7684\u56fe\u5f62\uff0c\u518d\u53c2\u7167\u79d8\u7b08\u5c01\u9762\u5939\u5c42\u4e2d\u7684\u79d8\u8bc0\uff0c\u65b9\u624d\u9886\u609f\uff0c\u4f46\u59cb\u7ec8\u4e0d\u660e\u767d\u8fd9\u4e9b\u6b66\u529f\u4f55\u4ee5\u7adf\u8981\u641e\u5f97\u5982\u6b64\u7e41\u590d\uff0c\u6709\u8bb8\u591a\u62db\u6570\u663e\u7136\u9887\u6709\u86c7\u8db3\u4e4b\u5acc\u3002\u63a5\u6218\u4e4b\u9645\uff0c\u654c\u4eba\u6b66\u529f\u518d\u9ad8\uff0c\u4eba\u6570\u518d\u591a\uff0c\u4e5f\u51b3\u4e0d\u80fd\u4ece\u56db\u9762\u516b\u65b9\u540c\u65f6\u8fdb\u653b\uff0c\u4e0d\u9732\u4e1d\u6beb\u7a7a\u9699\uff0c\u800c\u8fd9\u5957\u6b66\u529f\u660e\u660e\u662f\u4e3a\u4e86\u5e94\u4ed8\u591a\u65b9\u540c\u65f6\u8fdb\u653b\u800c\u521b\u3002\u6b64\u523b\u8eab\u5904\u56f0\u5883\uff0c\u7ec8\u4e8e\u7701\u609f\uff0c\u539f\u6765\u91d1\u86c7\u90ce\u541b\u5f53\u65e5\u5403\u4e86\u5927\u4e8f\uff0c\u8131\u9003\u4e4b\u540e\uff0c\u6b9a\u7aed\u5fc3\u667a\uff0c\u521b\u51fa\u8fd9\u5957\u6b66\u529f\u6765\uff0c\u5374\u662f\u4e13\u4e3a\u7834\u8fd9\u4e94\u884c\u9635\u800c\u7528\u3002\u4ed6\u5f53\u7136\u662f\u60f3\u6765\u77f3\u6881\u62a5\u4ec7\uff0c\u53ef\u60dc\u624b\u811a\u7b4b\u8109\u5747\u88ab\u6311\u65ad\uff0c\u4f7f\u4e0d\u51fa\u52b2\u3002\u8881\u627f\u5fd7\u5fc3\u4e0b\u76d8\u7b97\uff1a\u81ea\u5df1\u65e0\u610f\u4e2d\u5b66\u5230\u4e86\u8fd9\u5957\u6b66\u529f\uff0c\u65e2\u53ef\u8131\u4eca\u65e5\u4e4b\u96be\uff0c\u53c8\u80fd\u66ff\u8fd9\u4f4d\u6ca1\u89c1\u8fc7\u9762\u7684\u6069\u5e08\u4e00\u6cc4\u5f53\u65e5\u7684\u6028\u6bd2\uff0c\u4ed6\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u82e5\u662f\u6709\u77e5\uff0c\u4e5f\u5fc5\u6b23\u6170\uff0c\u4e0d\u6789\u4e86\u5f53\u5e74\u8fd9\u4e00\u756a\u82e6\u5fc3\u3002\u60f3\u5230\u8fd9\u91cc\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u7741\u773c\u4e00\u671b\uff0c\u53ea\u89c1\u684c\u4e0a\u8721\u70db\u5df2\u70b9\u5269\u4e0d\u5230\u4e00\u5bf8\u3002 \u3000\u3000\u6e29\u6c0f\u4e94\u8001\u89c1\u4ed6\u8138\u4e0a\u5ffd\u5fe7\u5ffd\u559c\uff0c\u4e0d\u77e5\u4ed6\u5728\u6253\u751a\u4e48\u4e3b\u610f\uff0c\u4f46\u81ea\u6043\u4e94\u884c\u516b\u5366\u9635\u5a01\u529b\u65e0\u7a77\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u53ea\u662f\u5706\u7741\u7740\u5341\u53ea\u773c\u775b\uff0c\u4e25\u52a0\u9632\u5907\uff0c\u6015\u4ed6\u4e58\u9699\u8131\u9003\u3002 \u3000\u3000\u8881\u627f\u5fd7\u91cd\u53c8\u95ed\u773c\uff0c\u5c06\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u672b\u7ae0\u6240\u8f7d\u6b66\u529f\u4ece\u5934\u81f3\u5c3e\u7ec6\u60f3\u4e00\u904d\uff0c\u60f3\u5230\u6700\u540e\u6467\u654c\u81f4\u80dc\u7684\u90a3\u4e00\u8def\u201c\u5feb\u5200\u65a9\u4e71\u9ebb\u201d\u65f6\uff0c\u9661\u7136\u4e00\u60ca\uff0c\u5168\u8eab\u767b\u65f6\u51b7\u6c57\u76f4\u5192\uff0c\u6697\u53eb\uff1a\u201c\u4e0d\u597d\u4e86\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u4ee5\u540e\u6570\u5341\u62db\u90fd\u662f\u8981\u9760\u5b9d\u5200\u5b9d\u5251\u6765\u4f7f\u654c\u4eba\u4e0d\u6562\u6b3a\u8fd1\uff0c\u65b9\u80fd\u4e58\u673a\u6253\u4e71\u654c\u9635\u3002\u6211\u624b\u5934\u5374\u65e0\u91d1\u86c7\u5251\uff0c\u8fd9\u4e00\u65f6\u4e09\u523b\u4e4b\u95f4\uff0c\u5374\u5230\u54ea\u91cc\u627e\u5b9d\u5200\u5b9d\u5251\u53bb\uff1f\u201d \u3000\u3000\u9752\u9752\u5728\u65c1\u8fb9\u4e00\u76f4\u6ce8\u89c6\u7740\u4ed6\uff0c\u84e6\u5730\u91cc\u89c1\u4ed6\u8138\u4e0a\u5927\u663e\u60f6\u6025\uff0c\u989d\u5934\u89c1\u6c57\uff0c\u5fc3\u60f3\u8fd8\u672a\u4ea4\u950b\uff0c\u5df2\u81ea\u5fc3\u602f\u6c14\u9981\uff0c\u5982\u4f55\u5f97\u4e86\uff1f\u4e0d\u7531\u5f97\u4ee3\u4ed6\u62c5\u5fe7\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u8721\u70db\u5df2\u5feb\u70e7\u5230\u5c3d\u5934\uff0c\u70db\u7130\u541e\u5410\u98a4\u52a8\uff0c\u5c06\u706d\u672a\u706d\uff0c\u4f46\u7834\u9635\u4e4b\u6cd5\uff0c\u4ecd\u672a\u60f3\u51fa\uff0c\u66f4\u662f\u5fe7\u6025\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u540d\u4e2b\u9b1f\u6367\u4e86\u4e00\u7897\u8336\u8d70\u5230\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u76f8\u516c\u8bf7\u7528\u7897\u7cd6\u8336\uff01\u201d\u4ed6\u6b63\u5728\u51fa\u795e\uff0c\u968f\u624b\u63a5\u8fc7\uff0c\u653e\u5230\u5507\u8fb9\u5f20\u53e3\u8981\u559d\uff0c\u7a81\u7136\u95f4\u624b\u4e0a\u4e00\u9707\uff0c\u8336\u676f\u88ab\u4e00\u652f\u8896\u7bad\u6253\u843d\uff0c\u5f53\u5577\u4e00\u58f0\u54cd\uff0c\u5728\u5730\u4e0b\u8dcc\u5f97\u7c89\u788e\u3002\u8881\u627f\u5fd7\u4e00\u6643\u773c\u95f4\uff0c\u89c1\u9752\u9752\u53f3\u624b\u5411\u540e\u4e00\u7f29\uff0c\u77e5\u9053\u8fd9\u7bad\u662f\u5979\u6240\u653e\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6697\u60f3\uff1a\u201c\u597d\u9669\uff1f\u6211\u600e\u4e48\u5982\u6b64\u80e1\u6d82\uff0c\u7adf\u6ca1\u60f3\u5230\u4ed6\u4eec\u53c8\u4f1a\u7ed9\u6211\u559d\u751a\u4e48\u9189\u4ed9\u871c\u3002\u201d \u3000\u3000\u6e29\u65b9\u609f\u89c1\u8be1\u8ba1\u4e3a\u9752\u9752\u63ed\u7834\uff0c\u6012\u4e0d\u53ef\u904f\uff0c\u7834\u53e3\u5927\u9a82\uff1a\u201c\u8fd9\u6837\u7684\u5a18\uff0c\u5c31\u751f\u8fd9\u6837\u7684\u5973\u513f\uff01\u6e29\u5bb6\u7956\u5b97\u4e0d\u79ef\u5fb7\uff0c\u5c3d\u51fa\u4e9b\u5411\u7740\u5916\u4eba\u7684\u8d31\u8d27\uff01\u201d \u3000\u3000\u9752\u9752\u5634\u5934\u6beb\u4e0d\u8ba9\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u6e29\u5bb6\u7956\u5b97\u79ef\u597d\u5927\u7684\u5fb7\u5440\uff0c\u4fee\u6865\u94fa\u8def\uff0c\u6551\u6d4e\u7a77\u4eba\uff0c\u751a\u4e48\u597d\u4e8b\u90fd\u5e72\u3002\u5c31\u662f\u4e0d\u5077\u4e0d\u62a2\uff0c\u4e0d\u6740\u4eba\u653e\u706b\u3002\u201d \u3000\u3000\u6e29\u65b9\u609f\u5927\u6012\uff0c\u8df3\u8d77\u6765\u5c31\u8981\u6253\u4eba\u3002\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u4e94\u5f1f\uff0c\u6c89\u4f4f\u6c14\uff0c\u7559\u795e\u8fd9\u5c0f\u5b50\u3002\u201d \u3000\u3000\u539f\u6765\u8881\u627f\u5fd7\u8fd9\u65f6\u53c8\u662f\u4e00\u8138\u559c\u8272\uff0c\u9752\u9752\u8fd9\u4e00\u652f\u8896\u7bad\u89e6\u52a8\u4e86\u7075\u673a\uff1a\u201c\u7528\u6697\u5668\uff01\u201d\u53ea\u89c1\u70db\u706b\u6643\u52a8\uff0c\u5df2\u6709\u4e24\u652f\u8721\u70db\u7184\u4e86\uff0c\u5f53\u4e0b\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5566\uff0c\u8bf7\u8d50\u6559\u5427\uff01\u8fd9\u6b21\u5206\u4e86\u80dc\u8d1f\u4e4b\u540e\u600e\u6837\uff1f\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u4f60\u80dc\u4e86\uff0c\u91d1\u5b50\u7531\u4f60\u5e26\u53bb\u3002\u4f60\u80dc\u4e0d\u4e86\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u591a\u8bf4\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u77e5\u9053\u81ea\u5df1\u82e5\u662f\u843d\u8d25\uff0c\u5f53\u7136\u6027\u547d\u4e0d\u4fdd\uff0c\u4f46\u5982\u5f97\u80dc\uff0c\u53ea\u6015\u4ed6\u4eec\u8fd8\u8981\u62b5\u8d56\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u628a\u91d1\u5b50\u62ff\u51fa\u6765\uff0c\u6211\u7834\u9635\u4e4b\u540e\uff0c\u62ff\u4e86\u5c31\u8d70\u3002\u201d \u3000\u3000\u6e29\u6c0f\u4e94\u8001\u89c1\u4ed6\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u8981\u5634\u786c\uff0c\u5fc3\u60f3\u4ee5\u91d1\u86c7\u90ce\u541b\u5982\u6b64\u9ad8\u624b\uff0c\u5c1a\u4e14\u4e3a\u6e29\u6c0f\u4e94\u884c\u9635\u6240\u64d2\uff0c\u73b0\u4e0b\u7ecf\u8fc7\u5341\u591a\u5e74\u6f5c\u5fc3\u94bb\u7814\uff0c\u53c8\u521b\u4e86\u4e00\u4e2a\u516b\u5366\u9635\u6765\u4f5c\u8f85\u4f50\uff0c\u4f60\u5982\u4f55\u80fd\u591f\u8131\u9003\uff1f\u8fd9\u9635\u52bf\u4ed6\u4eec\u5e73\u7d20\u7ec3\u5f97\u7eaf\u719f\u5f02\u5e38\uff0c\u5bf9\u4ed8\u4e09\u56db\u5341\u540d\u597d\u624b\u5c1a\u4e14\u7ef0\u7ef0\u6709\u4f59\uff0c\u5b9e\u662f\u77f3\u6881\u6d3e\u9547\u6d3e\u4e4b\u5b9d\uff0c\u5411\u6765\u4e0d\u80af\u8f7b\u7528\uff0c\u4ee5\u514d\u88ab\u4eba\u7aa5\u89c1\u4e86\u865a\u5b9e\u3002\u8fd9\u6b21\u5b9e\u56e0\u8881\u627f\u5fd7\u6b66\u529f\u592a\u5f3a\uff0c\u4e94\u5144\u5f1f\u4e2a\u4e2a\u8eab\u6000\u7edd\u827a\uff0c\u5374\u5747\u88ab\u4ed6\u4e09\u62db\u4e24\u5f0f\u4e4b\u95f4\u5c31\u6253\u5f97\u4e00\u8d25\u6d82\u5730\u3002\u4e94\u4eba\u4e00\u5546\u8bae\uff0c\u53ea\u5f97\u62ff\u51fa\u8fd9\u95e8\u770b\u5bb6\u672c\u9886\u6765\uff0c\u4e5f\u987e\u4e0d\u5f97\u88ab\u4ed6\u8bf4\u4ee5\u4f17\u6b3a\u5be1\u4e86\u3002\u6e29\u65b9\u8fbe\u5429\u5490\u5bb6\u4e01\u6362\u4e0a\u8721\u70db\uff0c\u5bf9\u9752\u9752\u9053\uff1a\u201c\u628a\u91d1\u5b50\u62ff\u51fa\u6765\u3002\u201d \u3000\u3000\u9752\u9752\u65e9\u5728\u540e\u6094\uff0c\u5fc3\u60f3\u65e9\u77e5\u5982\u6b64\uff0c\u628a\u9ec4\u91d1\u90fd\u8fd8\u7ed9\u4ed6\u4e5f\u5c31\u7b97\u4e86\uff0c\u8fd9\u65f6\u60f3\u518d\u79c1\u4e0b\u7ed9\u4ed6\uff0c\u4e5f\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u53ea\u5f97\u628a\u4e00\u5927\u5305\u91d1\u6761\u90fd\u6367\u5230\u7ec3\u6b66\u5385\u4e2d\uff0c\u653e\u5728\u684c\u4e0a\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u5de6\u624b\u5728\u684c\u4e0a\u6a2a\u626b\u8fc7\u53bb\uff0c\u91d1\u5305\u6253\u5f00\uff0c\u556a\u556a\u556a\u4e00\u58f0\u54cd\uff0c\u6570\u5341\u5757\u91d1\u6761\u6563\u6ee1\u4e86\u4e00\u5730\uff0c\u707f\u7136\u751f\u5149\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6e29\u5bb6\u867d\u7a77\uff0c\u8fd9\u51e0\u5343\u4e24\u91d1\u5b50\u8fd8\u6ca1\u77a7\u5728\u773c\u91cc\u3002\u59d3\u8881\u7684\uff0c\u4f60\u6709\u672c\u4e8b\u7834\u4e86\u6211\u4eec\u8fd9\u4e94\u884c\u9635\uff0c\u5c3d\u7ba1\u53d6\u53bb\uff01\u201d\u4e94\u8001\u4e00\u58f0\u547c\u559d\uff0c\u5404\u6267\u5175\u5203\uff0c\u5df2\u5c06\u8881\u627f\u5fd7\u56f4\u4f4f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u4ed6\u4eec\u8fde\u5c4b\u4e0a\u4e5f\u5e03\u4e86\u4eba\uff0c\u8fd9\u9635\u6cd5\u53ef\u53c8\u5982\u4f55\u7834\u89e3\uff1f\u201d\u5374\u542c\u5f97\u6e29\u65b9\u65bd\u9053\uff1a\u201c\u5c4b\u4e0a\u6709\u4eba\uff01\u201d\u5927\u58f0\u559d\u9053\uff1a\u201c\u751a\u4e48\u4eba\uff1f\u90fd\u7ed9\u6211\u6eda\u4e0b\u6765\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u5c4b\u9876\u4e0a\u6709\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u6e29\u5bb6\u4e94\u4f4d\u8001\u7237\u5b50\uff0c\u59d3\u8363\u7684\u767b\u95e8\u8bf7\u7f6a\u6765\u5566\uff01\u201d\u547c\u559d\u58f0\u4e2d\uff0c\u5c4b\u4e0a\u8dc3\u4e0b\u4e8c\u5341\u591a\u4e2a\u4eba\u6765\u3002\u5f53\u5148\u4e00\u4eba\u6b63\u662f\u9f99\u6e38\u5e2e\u5e2e\u4e3b\u8363\u5f69\u3002 \u3000\u3000\u8881\u627f\u5fd7\u767b\u65f6\u5927\u4e3a\u5bbd\u6000\uff0c\u5411\u9752\u9752\u671b\u4e86\u4e00\u773c\uff0c\u89c1\u5979\u8138\u8272\u5fae\u53d8\uff0c\u54ac\u4f4f\u4e0b\u5507\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u8001\u8363\uff0c\u4f60\u4e09\u66f4\u534a\u591c\u5149\u4e34\u820d\u4e0b\uff0c\u6709\u751a\u4e48\u6307\u6559\uff1f\u554a\uff0c\u65b9\u5ca9\u7684\u5415\u4e03\u5148\u751f\u4e5f\u6765\u4e86\u3002\u201d\u8bf4\u7740\u5411\u8363\u5f69\u8eab\u540e\u4e00\u4e2a\u8001\u5934\u5b50\u62f1\u4e86\u62f1\u624b\u3002\u90a3\u8001\u8005\u62f1\u624b\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5144\u5f1f\u4eec\u90fd\u6e05\u5065\uff0c\u8fd9\u53ef\u6709\u51e0\u5e74\u4e0d\u89c1\u4e86\u54ea\uff01\u201d \u3000\u3000\u8363\u5f69\u7b11\u9053\uff1a\u201c\u4e94\u4f4d\u8001\u7237\u5b50\u597d\u798f\u6c14\uff0c\u751f\u5f97\u4e00\u4f4d\u6b66\u529f\u65e2\u9ad8\u3001\u8ba1\u8c0b\u53c8\u5f3a\u7684\u5b59\u5c0f\u59d0\uff0c\u4e0d\u4f46\u628a\u6211\u4eec\u7684\u6c99\u8001\u5927\u548c\u5341\u591a\u4e2a\u5144\u5f1f\u4f24\u4e86\uff0c\u8fde\u6211\u5c0f\u8001\u513f\u4e5f\u5403\u4e86\u5979\u4e8f\u3002\u201d \u3000\u3000\u6e29\u6c0f\u5144\u5f1f\u4e0d\u77e5\u9752\u9752\u548c\u4ed6\u4eec\u8fd9\u5c42\u8fc7\u8282\uff0c\u5e73\u65f6\u77f3\u6881\u6d3e\u4e0e\u9f99\u6e38\u5e2e\u9887\u6709\u6765\u5f80\uff0c\u8fd9\u65f6\u5f3a\u654c\u5f53\u524d\uff0c\u4e0d\u613f\u518d\u65c1\u751f\u679d\u8282\u3002\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u8001\u8363\uff0c\u6211\u5bb6\u5c0f\u5b69\u513f\u6709\u751a\u4e48\u5bf9\u4e0d\u8d77\u4f60\u7684\uff0c\u6211\u4eec\u51b3\u4e0d\u62a4\u77ed\uff0c\u6740\u4eba\u507f\u547d\uff0c\u6b20\u503a\u8fd8\u94b1\uff0c\u597d\u4e0d\u597d\u5440\uff1f\u201d \u3000\u3000\u8363\u5f69\u4e00\u6123\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2a\u7d20\u6765\u6a2a\u86ee\u72c2\u50b2\u7684\u8001\u5934\u4eca\u65e5\u7adf\u8fd9\u4e48\u597d\u8bf4\u8bdd\uff1f\u96be\u9053\u4ed6\u5f53\u771f\u6015\u4e86\u5415\u4e03\u5148\u751f\uff1f\u201d\u4e00\u77a5\u4e4b\u95f4\u89c1\u5230\u4e86\u8881\u627f\u5fd7\uff0c\u66f4\u662f\u4e0d\u89e3\uff1a\u201c\u4ed6\u4eec\u6709\u8fd9\u6837\u7684\u4e00\u4e2a\u786c\u624b\u5728\u6b64\uff0c\u5415\u4e03\u5148\u751f\u4e5f\u672a\u5fc5\u80fd\u591f\u80dc\u4ed6\u3002\u6211\u8fd8\u662f\u89c1\u597d\u6536\u7bf7\u5427\uff01\u201d\u4fbf\u9053\uff1a\u201c\u9f99\u6e38\u5e2e\u8ddf\u8d35\u6d3e\u7d20\u6765\u6ca1\u8fc7\u8282\uff0c\u51b2\u7740\u5404\u4f4d\u8001\u7237\u5b50\u7684\u91d1\u9762\uff0c\u6c99\u8001\u5927\u5df2\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u603b\u6028\u4ed6\u5b66\u827a\u4e0d\u7cbe\u3002\u4e0d\u8fc7\u8fd9\u6279\u91d1\u5b50\u2026\u2026\u201d\u773c\u5149\u5411\u7740\u5730\u4e0b\u4e00\u5757\u5757\u7684\u91d1\u6761\u4e00\u626b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u9f99\u6e38\u5e2e\u8ddf\u4e86\u51e0\u767e\u91cc\u8def\u7a0b\uff0c\u8d39\u4e86\u4e0d\u5c11\u5fc3\u8840\uff0c\u53c8\u6709\u4eba\u4e3a\u6b64\u9001\u547d\uff0c\u5927\u5bb6\u5728\u6c5f\u6e56\u4e0a\u6df7\u996d\u5403\u2026\u2026\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u542c\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u4fbf\u4f4f\u53e3\u4e0d\u5f80\u4e0b\u8bf4\u4e86\uff0c\u77e5\u4ed6\u610f\u5728\u94b1\u8d22\u800c\u975e\u4e3a\u4e86\u62a5\u4ec7\uff0c\u4fbf\u9053\uff1a\u201c\u9ec4\u91d1\u90fd\u5728\u8fd9\u91cc\uff0c\u4f60\u8981\u561b\uff0c\u90fd\u62ff\u53bb\u90a3\u4e5f\u4e0d\u59a8\u3002\u201d \u3000\u3000\u8363\u5f69\u542c\u4ed6\u8bf4\u5f97\u6177\u6168\u5927\u65b9\uff0c\u53ea\u9053\u662f\u53cd\u8bed\u8ba5\u523a\uff0c\u4f46\u77a7\u4ed6\u8138\u8272\uff0c\u5374\u4f3c\u5e76\u65e0\u6076\u610f\uff0c\u9053\uff1a\u201c\u6e29\u8001\u7237\u5b50\u5982\u80af\u8d50\u7ed9\u534a\u6570\uff0c\u4f5c\u4e3a\u655d\u5e2e\u51e0\u540d\u6b7b\u4f24\u5144\u5f1f\u7684\u629a\u6064\uff0c\u5144\u5f1f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u6e29\u65b9\u5c71\u9053\uff1a\u201c\u4f60\u62ff\u5427\u3002\u201d\u8363\u5f69\u53cc\u624b\u4e00\u62f1\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u591a\u8c22\u4e86\uff01\u201d\u624b\u4e00\u6446\uff0c\u4ed6\u8eab\u540e\u51e0\u540d\u5927\u6c49\u4fef\u8eab\u53bb\u62fe\u91d1\u6761\u3002 \u3000\u3000\u90a3\u51e0\u4eba\u624b\u6307\u521a\u8981\u78b0\u5230\u91d1\u6761\u3002\u7a81\u7136\u80a9\u5934\u88ab\u4eba\u4e00\u63a8\uff0c\u53ea\u89c9\u4e00\u80a1\u6781\u5927\u7684\u529b\u91cf\u6d8c\u6765\uff0c\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u8dc3\u51fa\u6570\u6b65\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u89c1\u8881\u627f\u5fd7\u5df2\u7ad9\u5728\u9762\u524d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8363\u8001\u7237\u5b50\uff0c\u8fd9\u6279\u91d1\u5b50\u662f\u95ef\u738b\u7684\u519b\u9977\uff0c\u4f60\u8981\u62ff\u53bb\uff0c\u53ef\u4e0d\u5927\u7a33\u4fbf\u3002\u201d \u3000\u3000\u95ef\u738b\u7684\u540d\u5934\u5728\u5317\u65b9\u56fa\u7136\u5a01\u58f0\u8fdc\u9707\uff0c\u4f46\u5728\u6c5f\u5357\uff0c\u6c5f\u6e56\u4eba\u7269\u5374\u4e0d\u5927\u7406\u4f1a\u3002\u8363\u5f69\u8f6c\u5934\u5bf9\u5415\u4e03\u5148\u751f\u7b11\u9053\uff1a\u201c\u4ed6\u62ff\u95ef\u738b\u7684\u540d\u5934\u6765\u5413\u54b1\u4eec\u3002\u201d\u5415\u4e03\u5148\u751f\u624b\u4e2d\u62ff\u7740\u4e00\u6839\u7c97\u5927\u5f02\u5e38\u7684\u65f1\u70df\u7b52\uff0c\u5438\u4e86\u4e00\u53e3\uff0c\u55b7\u4e00\u53e3\u70df\uff0c\u6162\u6761\u65af\u7406\uff0c\u4fa7\u76ee\u5411\u8881\u627f\u5fd7\u6253\u91cf\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u795e\u60c5\u65e0\u793c\uff0c\u5fc3\u5934\u6709\u6c14\uff0c\u53ea\u662f\u4ed6\u4e00\u526f\u6c14\u6d3e\u663e\u662f\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u5012\u4e5f\u4e0d\u6562\u8f7b\u6162\uff0c\u4f5c\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u524d\u8f88\u53ef\u662f\u59d3\u5415\uff1f\u665a\u8f88\u521d\u6765\u6c5f\u5357\uff0c\u6055\u6211\u4e0d\u8bc6\u3002\u201d \u3000\u3000\u5415\u4e03\u5148\u751f\u5410\u4e86\u4e00\u53e3\u70df\uff0c\u7b14\u76f4\u5411\u8881\u627f\u5fd7\u8138\u4e0a\u55b7\u53bb\uff0c\u53c8\u5438\u4e86\u4e00\u53e3\uff0c\u8ddf\u7740\u4e24\u9053\u767d\u86c7\u822c\u7684\u6d53\u70df\u4ece\u9f3b\u5b54\u4e2d\u5c04\u51fa\uff0c\u51dd\u805a\u4e86\u7247\u523b\u4e0d\u6563\u3002\u8881\u627f\u5fd7\u8fd8\u4e0d\u600e\u7684\uff0c\u9752\u9752\u77a7\u7740\u5374\u5df2\u6c14\u5f80\u4e0a\u51b2\uff0c\u4fbf\u60f3\u5f00\u53e3\u8bf4\u8bdd\u3002\u6e29\u4eea\u5728\u5979\u81c2\u4e0a\u8f7b\u8f7b\u4e00\u634f\u3002\u9752\u9752\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u6bcd\u4eb2\u7f13\u7f13\u6447\u5934\uff0c\u624d\u628a\u4e00\u53e5\u9a82\u4eba\u7684\u8bdd\u5fcd\u4f4f\u4e86\u3002\u53ea\u89c1\u5415\u4e03\u5148\u751f\u5c06\u65f1\u70df\u888b\u5728\u7816\u5730\u4e0a\u7b03\u7b03\u7b03\u7684\u6572\u4e86\u4e00\u9635\uff0c\u6572\u53bb\u70df\u7070\uff0c\u53c8\u88c5\u4e0a\u70df\u4e1d\u3002 \u3000\u3000\u8fd9\u65f6\u8fde\u6e29\u6c0f\u4e94\u8001\u4e5f\u6709\u70b9\u8010\u4e0d\u4f4f\u4e86\uff0c\u4f46\u77e5\u4ed6\u5728\u6b66\u6797\u4e2d\u6210\u540d\u5df2\u4e45\uff0c\u636e\u8bf4\u5f53\u5e74\u4ee5\u4e00\u5957\u9e64\u5f62\u62f3\u6253\u8d25\u8fc7\u65e0\u6570\u9ad8\u624b\uff0c\u624b\u4e2d\u7684\u70df\u888b\u66f4\u662f\u4e00\u4ef6\u5947\u5f62\u5175\u5668\uff0c\u64c5\u80fd\u6253\u7a74\uff0c\u593a\u4eba\u5175\u5203\uff0c\u53ef\u662f\u5230\u5e95\u672c\u9886\u5982\u4f55\uff0c\u8c01\u4e5f\u6ca1\u6709\u89c1\u8fc7\u3002\u6e29\u6c0f\u4e94\u8001\u90fd\u76fc\u4ed6\u4e0e\u8881\u627f\u5fd7\u8bf4\u50f5\u4e86\u52a8\u624b\uff0c\u4ed6\u80fd\u53d6\u80dc\u56fa\u7136\u6700\u597d\uff0c\u5426\u5219\u81f3\u5c11\u4e5f\u53ef\u6d88\u53bb\u8881\u627f\u5fd7\u7684\u4e00\u70b9\u529b\u6c14\u3002 \u3000\u3000\u53ea\u89c1\u5415\u5148\u751f\u4ece\u6000\u4e2d\u6478\u51fa\u706b\u77f3\u706b\u7eb8\uff0c\u6251\u6251\u6251\u7684\u6572\u51fb\uff0c\u70df\u4e1d\u8fd8\u672a\u70b9\u7740\uff0c\u5ffd\u7136\u5c4b\u9876\u4e0a\u6709\u4eba\u5927\u559d\uff1a\u201c\u5feb\u8fd8\u6211\u4eec\u91d1\u5b50\uff01\u201d\u4e00\u4e2a\u5c11\u5973\u3001\u4e00\u4e2a\u7c97\u58ee\u5c11\u5e74\u53cc\u53cc\u8dc3\u4e0b\uff0c\u968f\u540e\u53c8\u6e9c\u4e0b\u4e00\u4e2a\u4e94\u5341\u4f59\u5c81\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u77a7\u6253\u626e\u4f3c\u662f\u4e2a\u5546\u8d3e\uff0c\u5de6\u624b\u62ff\u7740\u4e00\u4e2a\u7b97\u76d8\uff0c\u53f3\u624b\u62ff\u7740\u4e00\u652f\u7b14\uff0c\u6a21\u6837\u5f88\u662f\u53e4\u602a\u3002\u4ed6\u6162\u541e\u541e\u7684\u4ece\u5899\u4e0a\u6e9c\u4e0b\uff0c\u4e5f\u77a7\u4e0d\u51fa\u4ed6\u6b66\u529f\u9ad8\u4f4e\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u90a3\u5c11\u5973\u6b63\u662f\u5b89\u5c0f\u6167\uff0c\u53c8\u559c\u53c8\u5fe7\uff0c\u559c\u7684\u662f\u6765\u4e86\u5e2e\u624b\uff0c\u4f46\u4e0d\u77e5\u53e6\u5916\u4e24\u4eba\u6b66\u529f\u5982\u4f55\u3002\u773c\u4e0b\u654c\u4eba\u9664\u4e86\u77f3\u6881\u6d3e\u5916\uff0c\u53c8\u591a\u4e86\u9f99\u6e38\u5e2e\u4e0e\u5415\u4e03\u5148\u751f\u8fd9\u6279\u4eba\u3002\u6e29\u4eea\u4e0e\u9752\u9752\u6bcd\u5973\u548c\u6e29\u6c0f\u4e94\u8001\u6495\u7834\u4e86\u8138\uff0c\u5df2\u5904\u4e8e\u7edd\u5927\u5371\u9669\u4e4b\u4e2d\uff0c\u975e\u5c06\u5979\u4eec\u6551\u8d70\u4e0d\u53ef\uff0c\u8981\u662f\u65b0\u6765\u7684\u4e24\u4eba\u672c\u9886\u90fd\u548c\u5b89\u5c0f\u6167\u5dee\u4e0d\u591a\uff0c\u81ea\u5df1\u53cd\u800c\u8981\u5206\u795e\u7167\u987e\uff0c\u5c82\u4e0d\u7cdf\u7cd5\uff1f \u3000\u3000\u8fd9\u65f6\u6e29\u6c0f\u5f1f\u5b50\u4e2d\u5df2\u6709\u4eba\u62a2\u4e0a\u53bb\u62e6\u963b\u559d\u95ee\u3002\u90a3\u5c11\u5e74\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u628a\u6211\u4eec\u7684\u91d1\u5b50\u8fd8\u6765\uff01\u201d\u89c1\u91d1\u6761\u6563\u5728\u5730\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u554a\u54c8\uff0c\u539f\u6765\u90fd\u5728\u8fd9\u91cc\uff01\u201d\u4fef\u8eab\u5c31\u62fe\u3002\u8881\u627f\u5fd7\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u60f3\u8fd9\u4eba\u884c\u4e8b\u751a\u4e3a\u9c81\u83bd\uff0c\u53ea\u6015\u6ca1\u751a\u4e48\u9ad8\u660e\u6b66\u529f\u3002 \u3000\u3000\u6e29\u5357\u626c\u89c1\u4ed6\u4fef\u8eab\uff0c\u98de\u8db3\u5f80\u4ed6\u81c2\u4e0a\u8e22\u53bb\u3002\u5b89\u5c0f\u6167\u6025\u53eb\uff1a\u201c\u5d14\u5e08\u54e5\u5f53\u5fc3\uff01\u201d\u90a3\u5c11\u5e74\u4fa7\u8eab\u907f\u5f00\uff0c\u968f\u5373\u62a2\u653b\u800c\u524d\uff0c\u53cc\u638c\u75be\u5288\u8fc7\u53bb\u3002 \u3000\u3000\u6e29\u5357\u626c\u4e0d\u53ca\u9000\u8ba9\uff0c\u4e5f\u4f38\u51fa\u53cc\u638c\u76f8\u62b5\uff0c\u556a\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u56db\u638c\u76f8\u4ea4\uff0c\u4e24\u4eba\u5404\u81ea\u9000\u5f00\u6570\u6b65\u3002\u90a3\u5c11\u5e74\u53c8\u5f85\u4e0a\u524d\uff0c\u90a3\u5546\u8d3e\u6253\u626e\u7684\u4eba\u53eb\u9053\uff1a\u201c\u5e0c\u654f\uff0c\u6162\u7740\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8bb0\u8d77\u5b89\u5c0f\u6167\u7684\u8bdd\uff0c\u8bf4\u6709\u4e00\u4e2a\u59d3\u5d14\u7684\u5e08\u54e5\u548c\u5979\u4e00\u8d77\u62a4\u9001\u8fd9\u7b14\u91d1\u5b50\uff0c\u56e0\u4e24\u4eba\u95f9\u4e86\u522b\u626d\uff0c\u4e2d\u9014\u5206\u624b\uff0c\u81f3\u88ab\u9752\u9752\u51fa\u5176\u4e0d\u610f\u7684\u52ab\u4e86\u53bb\u3002\u90a3\u4e48\u8fd9\u5c11\u5e74\u4fbf\u662f\u5d14\u79cb\u5c71\u7684\u4f84\u513f\u5d14\u5e0c\u654f\u4e86\uff0c\u96be\u9053\u8fd9\u4e2a\u5f62\u8c8c\u6ed1\u7a3d\u7684\u5546\u4eba\uff0c\u7adf\u662f\u5927\u5e08\u54e5\u94dc\u7b14\u94c1\u7b97\u76d8\u9ec4\u771f\uff1f\u4ed4\u7ec6\u4e00\u770b\uff0c\u89c1\u4ed6\u53f3\u624b\u4e2d\u90a3\u652f\u7b14\u6746\u95ea\u95ea\u53d1\u5149\uff0c\u679c\u662f\u9ec4\u94dc\u94f8\u6210\uff0c\u5de6\u624b\u4e2d\u90a3\u7b97\u76d8\u9ed1\u9edd\u9edd\u5730\uff0c\u591a\u534a\u662f\u94c1\u7684\uff0c\u8fd9\u4e00\u4e0b\u559c\u51fa\u671b\u5916\uff0c\u5fd9\u7eb5\u8eab\u8fc7\u53bb\uff0c\u8dea\u4e0b\u53e9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u8881\u627f\u5fd7\u53e9\u89c1\u5927\u5e08\u54e5\u3002\u201d \u3000\u3000\u90a3\u4eba\u6b63\u662f\u9ec4\u771f\uff0c\u53cc\u624b\u6276\u8d77\uff0c\u7ec6\u7ec6\u6253\u91cf\uff0c\u6b22\u7136\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u5e08\u5f1f\uff0c\u4f60\u8fd9\u4e48\u5e74\u8f7b\uff0c\u771f\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u89c1\u5230\u4f60\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8bf7\u95ee\u5927\u5e08\u54e5\uff0c\u6069\u5e08\u73b0\u4eca\u5728\u54ea\u91cc\uff1f\u4ed6\u8001\u4eba\u5bb6\u8eab\u5b50\u5b89\u5065\uff1f\u201d\u9ec4\u771f\u9053\uff1a\u201c\u6069\u5e08\u6b64\u523b\u5728\u5357\u4eac\uff0c\u4ed6\u8001\u4eba\u5bb6\u5f88\u597d\u3002\u201d \u3000\u3000\u5b89\u5c0f\u6167\u8fc7\u6765\u8bf4\u9053\uff1a\u201c\u627f\u5fd7\u5927\u54e5\uff0c\u8fd9\u5c31\u662f\u6211\u8bf4\u7684\u5d14\u5e08\u54e5\u3002\u201d\u8881\u627f\u5fd7\u5411\u4ed6\u70b9\u70b9\u5934\u3002\u5b89\u5c0f\u6167\u89c1\u8881\u627f\u5fd7\u80cc\u4e0a\u7c98\u4e86\u4e9b\u67af\u8349\uff0c\u4f38\u624b\u62c8\u4e86\u4e0b\u6765\u3002\u8881\u627f\u5fd7\u5fae\u5fae\u4e00\u7b11\uff0c\u795e\u8272\u8868\u793a\u8c22\u610f\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u77a7\u7740\u5f88\u4e0d\u4e50\u610f\u3002\u9ec4\u771f\u559d\u9053\uff1a\u201c\u5e0c\u654f\uff0c\u600e\u4e48\u8fd9\u6837\u6ca1\u89c4\u77e9\uff1f\u5feb\u5411\u5e08\u53d4\u53e9\u5934\uff01\u201d\u5d14\u5e0c\u654f\u89c1\u8881\u627f\u5fd7\u6bd4\u81ea\u5df1\u8fd8\u5c0f\u7740\u51e0\u5c81\uff0c\u5fc3\u5934\u4e0d\u670d\u6c14\uff0c\u6162\u541e\u541e\u7684\u8fc7\u6765\uff0c\u4f5c\u52bf\u8981\u8dea\u3002\u8881\u627f\u5fd7\u8fde\u8bf4\uff1a\u201c\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u53cc\u624b\u62e6\u4f4f\u3002\u5d14\u5e0c\u654f\u4e5f\u5c31\u4e0d\u8dea\u4e0b\u53bb\u4e86\uff0c\u4f5c\u4e86\u4e00\u63d6\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u5c0f\u5e08\u53d4\uff01\u201d\u9ec4\u771f\u53c8\u9a82\uff1a\u201c\u751a\u4e48\u5c0f\u5e08\u53d4\u5927\u5e08\u53d4\uff0c\u5c31\u7b97\u4f60\u5927\u8fc7\u4ed6\uff0c\u5e08\u53d4\u603b\u662f\u957f\u8f88\u3002\u6211\u6bd4\u4f60\u8001\uff0c\u4f60\u53c8\u600e\u4e0d\u53eb\u6211\u8001\u5e08\u7236\uff1f\u201d\u8881\u627f\u5fd7\u5411\u5d14\u5e0c\u654f\u7b11\u9053\uff1a\u201c\u4f60\u53d4\u53d4\u53ef\u597d\uff0c\u6211\u60e6\u8bb0\u4ed6\u5f97\u7d27\u3002\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u6211\u53d4\u53d4\u597d\u3002\u201d \u3000\u3000\u5415\u4e03\u5148\u751f\u89c1\u4ed6\u4eec\u5e08\u5144\u5f1f\u3001\u5e08\u4f84\u53d4\u89c1\u793c\u53d9\u8bdd\uff0c\u95f9\u4e2a\u4e0d\u5b8c\uff0c\u5c06\u65c1\u4eba\u89c6\u82e5\u65e0\u7269\uff0c\u8fd9\u65f6\u5374\u8f6e\u5230\u4ed6\u8010\u4e0d\u4f4f\u4e86\uff0c\u602a\u76ee\u4e00\u7ffb\uff0c\u62ac\u5934\u671b\u7740\u5c4b\u9876\uff0c\u8bf4\u9053\uff1a\u201c\u6765\u7684\u90fd\u662f\u4e9b\u751a\u4e48\u4eba\uff1f\u201d\u8fd9\u4e00\u51fa\u58f0\uff0c\u4f17\u4eba\u90fd\u5413\u4e86\u4e00\u8df3\u3002\u539f\u6765\u4ed6\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u58f0\u82e5\u602a\u67ad\uff0c\u5341\u5206\u523a\u8033\uff0c\u6c99\u560e\u4e2d\u5939\u6742\u7740\u5c16\u9510\u4e4b\u97f3\uff0c\u96be\u542c\u5f02\u5e38\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u8e0f\u4e0a\u4e00\u6b65\u3002\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u91d1\u5b50\u662f\u6211\u4eec\u7684\uff0c\u7ed9\u4f60\u4eec\u5077\u4e86\u6765\uff0c\u73b0\u4eca\u5e08\u7236\u5e26\u6211\u4eec\u6765\u62ff\u56de\u53bb\u3002\u201d\u5415\u4e03\u5148\u751f\u4ecd\u662f\u773c\u671b\u5c4b\u9876\uff0c\u53e3\u55b7\u767d\u70df\u3002\u5ffd\u7136\u563f\u563f\u51b7\u7b11\u4e24\u58f0\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u89c1\u4ed6\u8001\u6c14\u6a2a\u79cb\u3001\u4e00\u526f\u5168\u4e0d\u628a\u4eba\u77a7\u5728\u773c\u91cc\u7684\u6a21\u6837\uff0c\u6c14\u5f80\u4e0a\u51b2\uff0c\u8bf4\u9053\uff1a\u201c\u5230\u5e95\u91d1\u5b50\u8fd8\u662f\u4e0d\u8fd8\uff0c\u4f60\u660e\u767d\u8bf4\u4e00\u53e5\u3002\u8981\u662f\u4f60\u4f5c\u4e0d\u5f97\u4e3b\uff0c\u4fbf\u8ba9\u4f5c\u5f97\u4e3b\u7684\u4eba\u51fa\u6765\u8bf4\u8bdd\u3002\u201d\u5415\u4e03\u5148\u751f\u53c8\u662f\u78d4\u78d4\u4e24\u58f0\u602a\u7b11\uff0c\u8f6c\u5934\u5411\u8363\u5f69\u9053\uff1a\u201c\u4f60\u544a\u8bc9\u8fd9\u5a03\u513f\uff0c\u6211\u662f\u751a\u4e48\u4eba\u3002\u201d\u8363\u5f69\u559d\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u5415\u4e03\u5148\u751f\uff0c\u53ef\u522b\u628a\u4f60\u5413\u574f\u4e86\u3002\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u8fd9\u4e48\u65e0\u793c\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u4e0d\u77e5\u5415\u4e03\u5148\u751f\u662f\u751a\u4e48\u4eba\uff0c\u81ea\u7136\u4e5f\u5413\u4e0d\u574f\uff0c\u53eb\u9053\uff1a\u201c\u6211\u7ba1\u4f60\u662f\u751a\u4e48\u4e03\u5148\u751f\u516b\u5148\u751f\uff0c\u6211\u4eec\u662f\u6765\u62ff\u91d1\u5b50\u7684\u3002\u201d \u3000\u3000\u6e29\u5357\u626c\u521a\u624d\u4e0e\u4ed6\u4ea4\u4e86\u624b\uff0c\u672a\u5206\u80dc\u8d1f\uff0c\u5fc3\u4e2d\u4e0d\u8010\uff0c\u8df3\u51fa\u6765\u559d\u9053\uff1a\u201c\u8981\u62ff\u91d1\u5b50\uff0c\u90a3\u5f88\u5bb9\u6613\uff0c\u5f97\u77a7\u4f60\u6709\u6ca1\u6709\u672c\u4e8b\u3002\u5148\u8d62\u4e86\u6211\u518d\u8bf4\u3002\u201d\u4e0d\u7b49\u5bf9\u65b9\u7b54\u8bdd\uff0c\u8df3\u8fc7\u6765\u5c31\u662f\u4e00\u62f3\u3002\u5d14\u5e0c\u654f\u731d\u4e0d\u53ca\u9632\uff0c\u8fd9\u62f3\u6b63\u4e2d\u80a9\u5934\u3002\u4ed6\u5927\u6012\u4e4b\u4e0b\uff0c\u51fa\u624b\u4e00\u62f3\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u6b63\u6253\u5728\u6e29\u5357\u626c\u809a\u4e0a\u3002\u5404\u4eba\u5404\u81ea\u8d1f\u75db\u8df3\u5f00\uff0c\u4e92\u76f8\u77aa\u4e86\u4e00\u773c\uff0c\u91cd\u53c8\u6253\u5728\u4e00\u8d77\u3002\u9877\u523b\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u7830\u84ec\u3001\u7830\u84ec\u4e4b\u58f0\u5927\u4f5c\uff0c\u5404\u4eba\u5934\u4e0a\u8eab\u4e0a\u90fd\u4e2d\u4e86\u5341\u4f59\u62f3\u3002\u4e24\u4eba\u6253\u6cd5\u4e00\u822c\uff0c\u90fd\u662f\u758f\u4e8e\u9632\u5fa1\uff0c\u52c7\u4e8e\u8fdb\u653b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u53f9\u6c14\uff1a\u201c\u5927\u5e08\u54e5\u6559\u7684\u5f92\u5f1f\u600e\u5730\u5982\u6b64\u4e0d\u6210\u8bdd\uff0c\u8981\u662f\u9047\u5230\u597d\u624b\uff0c\u8eab\u4e0a\u4e2d\u4e86\u4e00\u4e24\u62f3\u90a3\u8fd8\u4e86\u5f97\uff1f\u96be\u9053\u5d14\u53d4\u53d4\u4e5f\u4e0d\u597d\u597d\u70b9\u62e8\u4ed6\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u77e5\u5d14\u5e0c\u654f\u4e3a\u4eba\u8d63\u76f4\uff0c\u6027\u5b50\u9887\u4e3a\u66b4\u8e81\uff0c\u5b66\u6b66\u65f6\u4e0d\u80fd\u7ec6\u5fc3\u3002\u597d\u5728\u4ed6\u8eab\u5b50\u7c97\u58ee\uff0c\u6328\u51e0\u4e0b\u5c3d\u80fd\u633a\u5f97\u4f4f\u3002\u6df7\u6218\u4e2d\u53ea\u89c1\u4ed6\u53f3\u624b\u865a\u6643\u4e00\u62f3\uff0c\u6e29\u5357\u626c\u5411\u53f3\u95ea\u907f\uff0c\u4ed6\u5de6\u624b\u4e00\u8bb0\u94a9\u62f3\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6b63\u4e2d\u5bf9\u624b\u4e0b\u989a\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u6e29\u5357\u626c\u8dcc\u5012\u5728\u5730\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u5f97\u610f\u6d0b\u6d0b\uff0c\u5411\u5e08\u7236\u671b\u4e86\u4e00\u773c\uff0c\u4ee5\u4e3a\u5b9a\u5f97\u8d5e\u8bb8\uff0c\u5374\u89c1\u5e08\u7236\u4e00\u8138\u6012\u8272\uff0c\u5fc3\u4e0b\u5927\u662f\u4e0d\u89e3\uff0c\u6697\u60f3\u6211\u6253\u80dc\u4e86\uff0c\u600e\u4e48\u5e08\u7236\u53cd\u800c\u89c1\u602a\u3002\u5c0f\u6167\u89c1\u4ed6\u5634\u5507\u80bf\u8d77\uff0c\u53f3\u8033\u9c9c\u8840\u6dcb\u6f13\uff0c\u62ff\u624b\u5e15\u7ed9\u4ed6\u62b9\u8840\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4e0d\u95ea\u907f\uff1f\u4e00\u5473\u86ee\u6253\uff01\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u907f\u751a\u4e48\uff1f\u4e00\u907f\u5c31\u6253\u4e0d\u4e2d\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5415\u4e03\u5148\u751f\u602a\u58f0\u8bf4\u9053\uff1a\u201c\u6253\u5012\u4e00\u4e2a\u86ee\u6c49\uff0c\u6709\u751a\u4e48\u597d\u5f97\u610f\u7684\uff1f\u4f60\u8981\u91d1\u5b50\u5417\uff1f\u201d\u7a81\u7136\u62d4\u8d77\u8eab\u5b50\uff0c\u7ad9\u5230\u4e86\u4e24\u5757\u91d1\u6761\u4e4b\u4e0a\uff0c\u53f3\u624b\u4e2d\u7684\u65f1\u70df\u888b\u70b9\u7740\u53e6\u4e00\u5757\u91d1\u6761\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8bba\u4f60\u62f3\u6253\u811a\u8e22\uff0c\u53ea\u8981\u628a\u8fd9\u4e09\u5757\u91d1\u6761\u4ece\u6211\u811a\u5e95\u4e0b\u5f04\u4e86\u5f00\u53bb\uff0c\u6240\u6709\u8fd9\u4e9b\u91d1\u6761\u90fd\u662f\u4f60\u7684\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u89c9\u5f97\u4ed6\u8fc7\u4e8e\u72c2\u5984\u3002\u9002\u624d\u8fd9\u573a\u6253\u6597\uff0c\u5927\u5bb6\u90fd\u770b\u4e86\u51fa\u6765\uff0c\u5d14\u5e0c\u654f\u6b66\u529f\u867d\u7136\u4e0d\u9ad8\uff0c\u8182\u529b\u5374\u5f3a\u3002\u4ee5\u4e00\u6839\u70df\u7ba1\u70b9\u4f4f\u91d1\u6761\uff0c\u6599\u5b9a\u4ed6\u65e0\u6cd5\u62e8\u52a8\uff0c\u4e5f\u4e0d\u514d\u592a\u8fc7\u5c0f\u89d1\u4e86\u4eba\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u53ef\u4e0d\u8bb8\u53cd\u6094\u3002\u201d\u5415\u4e03\u5148\u751f\u4ef0\u5929\u5927\u7b11\uff0c\u5411\u8363\u5f69\u9053\uff1a\u201c\u4f60\u542c\uff0c\u4ed6\u6015\u6211\u53cd\u6094\u3002\u201d\u8363\u5f69\u53ea\u5f97\u8ddf\u7740\u5e72\u7b11\u4e00\u9635\uff0c\u5fc3\u4e2d\u5374\u4e5f\u9887\u4e3a\u7591\u60d1\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u597d\uff0c\u6211\u6765\u4e86\uff01\u201d\u7eb5\u4e0a\u4e09\u6b65\uff0c\u770b\u51c6\u4e86\u4ed6\u70df\u7ba1\u6240\u70b9\u7684\u91d1\u6761\uff0c\u8fd0\u529b\u53f3\u8db3\uff0c\u4e00\u4e2a\u626b\u5802\u817f\u6a2a\u8e22\u8fc7\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u770b\u5f97\u6e05\u695a\uff0c\u4f30\u8ba1\u8fd9\u4e00\u817f\u8e22\u53bb\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e8c\u4e09\u767e\u65a4\u529b\u9053\uff0c\u5415\u4e03\u5148\u751f\u529f\u529b\u518d\u9ad8\uff0c\u4e5f\u51b3\u4e0d\u80fd\u7528\u4e00\u6839\u70df\u7ba1\u5c06\u91d1\u6761\u70b9\u4f4f\u4e0d\u52a8\uff0c\u9664\u975e\u4ed6\u6709\u751a\u4e48\u5996\u6cd5\u9b54\u672f\u3002 \u3000\u3000\u773c\u89c1\u5d14\u5e0c\u654f\u4e00\u817f\u5c06\u5230\uff0c\u5415\u4e03\u5148\u751f\u70df\u7ba1\u7a81\u7136\u4e00\u6643\uff0c\u5728\u4ed6\u819d\u5f2f\u91cc\u4e00\u70b9\u3002\u5d14\u5e0c\u654f\u4e00\u6761\u817f\u767b\u65f6\u9ebb\u6728\uff0c\u8e22\u5230\u4e2d\u9014\uff0c\u4fbf\u5373\u8f6f\u5782\uff0c\u819d\u76d6\u4e00\u5f2f\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u8dea\u4e86\u4e0b\u6765\u3002\u5415\u4e03\u5148\u751f\u8fde\u8fde\u62f1\u624b\uff0c\u4e00\u9635\u602a\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff01\u5c0f\u5144\u5f1f\u4f55\u5fc5\u591a\u793c\uff1f\u201d \u3000\u3000\u5b89\u5c0f\u6167\u5927\u60ca\uff0c\u62a2\u4e0a\u53bb\u628a\u5d14\u5e0c\u654f\u6276\u8d77\uff0c\u6276\u5230\u9ec4\u771f\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u9ec4\u5e08\u4f2f\uff0c\u8fd9\u8001\u5934\u513f\u4f7f\u5978\uff0c\u60a8\u53bb\u6559\u8bad\u6559\u8bad\u4ed6\u3002\u201d\u5d14\u5e0c\u654f\u7834\u53e3\u5927\u9a82\uff1a\u201c\u4f60\u6697\u7b97\u4f24\u4eba\uff0c\u8001\u5bb6\u4f19\uff0c\u4f60\u4e0d\u662f\u82f1\u96c4\u597d\u6c49\uff01\u201d \u3000\u3000\u9ec4\u771f\u4f38\u624b\u7ed9\u4ed6\u5728\u8170\u91cc\u4e00\u634f\uff0c\u5927\u817f\u4e0a\u4e00\u6233\uff0c\u89e3\u5f00\u4e86\u95ed\u4f4f\u7684\u7a74\u9053\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u5c0f\u5bb6\u4f19\u4e2d\u4e86\u4eba\u5bb6\u6697\u7b97\uff0c\u624d\u662f\u82f1\u96c4\u597d\u6c49\uff0c\u4f69\u670d\u554a\u4f69\u670d\uff01\u201d\u4ed6\u89c1\u5415\u4e03\u5148\u751f\u624b\u6cd5\u5982\u6b64\u8fc5\u6377\uff0c\u4e5f\u81ea\u5403\u60ca\uff0c\u5fc3\u60f3\u5728\u6d59\u5357\u504f\u50fb\u4e4b\u5730\uff0c\u5c45\u7136\u6709\u8fd9\u7b49\u6253\u7a74\u597d\u624b\u3002\u9ec4\u771f\u4f7f\u7684\u5175\u5203\u5de6\u624b\u662f\u628a\u94c1\u7b97\u76d8\uff0c\u4e13\u95e8\u9501\u62ff\u654c\u4eba\u7684\u5175\u5668\uff0c\u53f3\u624b\u662f\u4e00\u652f\u94dc\u7b14\uff0c\u90a3\u81ea\u7136\u4e5f\u64c5\u4e8e\u6253\u7a74\u3002\u4ed6\u4f38\u624b\u5728\u7b97\u76d8\u4e0a\u4e00\u62e8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7b14\u5e10\u8bb0\u4e0b\u4e86\uff01 \u3000\u3000\u54b1\u4eec\u73b0\u94f6\u4ea4\u6613\uff0c\u4e0d\u653e\u8d4a\u5e10\uff0c\u5415\u4e03\u5148\u751f\uff0c\u4f60\u8fd9\u5c31\u8fd8\u5e10\u5427\uff01\u201d\u94dc\u7b14\u4e00\u6307\uff0c\u4fbf\u8981\u4e0a\u524d\u7ed9\u5f92\u5f1f\u627e\u56de\u8fd9\u4e2a\u573a\u5b50\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u662f\u5e08\u5f1f\uff0c\u8be5\u5f53\u5148\u4e0a\uff01\u201d\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u5f85\u5c0f\u5f1f\u5148\u6765\u3002\u6211\u4e0d\u6210\u65f6\uff0c\u4f60\u518d\u63a5\u4e0a\u3002\u201d \u3000\u3000\u9ec4\u771f\u89c1\u4ed6\u5e74\u7eaa\u751a\u8f7b\uff0c\u5fc3\u60f3\u4ed6\u5373\u5b66\u5168\u4e86\u672c\u95e8\u6b66\u529f\uff0c\u706b\u5019\u4e5f\u5fc5\u4e0d\u8db3\uff0c\u8c05\u6765\u4e0d\u662f\u8fd9\u5415\u4e03\u5148\u751f\u7684\u5bf9\u624b\u3002\u5e08\u7236\u4e34\u8001\u6536\u5e7c\u5f92\uff0c\u5bf9\u4ed6\u4e00\u5b9a\u751a\u662f\u953a\u7231\uff0c\u5982\u6709\u5931\u95ea\uff0c\u5c82\u4e0d\u662f\u4f24\u4e86\u5e08\u7236\u4e4b\u5fc3\u3002\u8fd9\u53ef\u4e0e\u8ba9\u5d14\u5e0c\u654f\u51fa\u9635\u4e0d\u540c\uff0c\u987b\u77e5\u81ea\u5df1\u8fd9\u4e2a\u5b9d\u8d1d\u5f92\u513f\u6b66\u529f\u5e73\u5e73\uff0c\u9c81\u83bd\u81ea\u5927\uff0c\u8ba9\u4ed6\u591a\u5403\u70b9\u82e6\u5934\uff0c\u53d7\u4e9b\u632b\u6298\uff0c\u4e8e\u4ed6\u65e5\u540e\u827a\u4e1a\u5927\u6709\u597d\u5904\uff0c\u4e8e\u662f\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u8fd8\u662f\u6211\u6765\u5427\u3002\u201d\u8881\u627f\u5fd7\u4e5f\u653e\u4f4e\u4e86\u58f0\u97f3\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u4ed6\u4eec\u597d\u624b\u5f88\u591a\uff0c\u8fd9\u4e94\u4e2a\u8001\u5934\u513f\u6709\u4e00\u5957\u5f88\u5389\u5bb3\u7684\u4e94\u884c\u9635\uff0c\u5f85\u4f1a\u8fd8\u6709\u6076\u6597\u3002\u4f60\u662f\u54b1\u4eec\u4e3b\u5c06\uff0c\u8fd8\u662f\u8ba9\u5c0f\u5f1f\u5148\u6765\u3002\u201d\u9ec4\u771f\u89c1\u4ed6\u6267\u610f\u8981\u4e0a\uff0c\u5fc3\u60f3\u521d\u751f\u728a\u513f\u4e0d\u6015\u864e\uff0c\u4e0d\u4fbf\u62c2\u4e86\u4ed6\u5c11\u5e74\u4eba\u7684\u5174\u5934\uff0c\u4fbf\u9053\uff1a\u201c\u90a3\u4e48\u5e08\u5f1f\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u8d70\u4e0a\u4e00\u6b65\u3002\u5411\u5415\u4e03\u5148\u751f\u9053\uff1a\u201c\u6211\u4e5f\u6765\u8e22\u4e00\u811a\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5415\u4e03\u5148\u751f\u4e0e\u4f17\u4eba\u90fd\u611f\u6115\u7136\uff0c\u5fc3\u60f3\u521a\u624d\u90a3\u7c97\u8c6a\u5c11\u5e74\u660e\u660e\u5403\u4e86\u82e6\u5934\uff0c\u600e\u5730\u4f60\u8fd8\u662f\u4e0d\u77e5\u6b7b\u6d3b\u3002\u5415\u4e03\u5148\u751f\u89c1\u4ed6\u6bd4\u5d14\u5e0c\u654f\u8fd8\u5e74\u8f7b\uff0c\u8d8a\u53d1\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u54b1\u4eec\u8bdd\u8bf4\u660e\u5728\u5148\uff0c\u4f60\u7ed9\u6211\u884c\u5927\u793c\u53ef\u4e0d\u6562\u5f53\u3002\u201d\u4e00\u8fb9\u8bf4\uff0c\u4e00\u8fb9\u53c8\u4f38\u70df\u7ba1\u70b9\u4f4f\u4e86\u91d1\u6761\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e5f\u548c\u5d14\u5e0c\u654f\u4e00\u6a21\u4e00\u6837\uff0c\u8d70\u4e0a\u4e09\u6b65\uff0c\u63d0\u8d77\u53f3\u8db3\uff0c\u6a2a\u626b\u8fc7\u53bb\u3002\u5d14\u5e0c\u654f\u770b\u5f97\u7740\u6025\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u90a3\u4e0d\u6210\uff0c\u8001\u5bb6\u4f19\u8981\u70b9\u7a74\uff01\u201d \u3000\u3000\u6e29\u6c0f\u4e94\u5144\u5f1f\u5374\u77e5\u8881\u627f\u5fd7\u867d\u7136\u5e74\u8f7b\uff0c\u53ef\u662f\u6b66\u529f\u5947\u9ad8\uff0c\u773c\u89c1\u4ed6\u8981\u91cd\u8e48\u5d14\u5e0c\u654f\u7684\u8986\u8f99\uff0c\u90fd\u611f\u5947\u602a\uff0c\u96be\u9053\u4ed6\u7adf\u80fd\u95ed\u4f4f\u817f\u4e0a\u7a74\u9053\uff0c\u4e0d\u6015\u4eba\u70b9\uff1f \u3000\u3000\u4f17\u4eba\u773c\u5149\u90fd\u671b\u7740\u8881\u627f\u5fd7\u90a3\u6761\u817f\u3002\u9ec4\u771f\u94dc\u7b14\u4ea4\u5728\u5de6\u624b\uff0c\u51c6\u62df\u4e00\u89c1\u8881\u627f\u5fd7\u5931\u5229\uff0c\u7acb\u5373\u51fa\u624b\uff0c\u5148\u6551\u5e08\u5f1f\uff0c\u518d\u653b\u654c\u4eba\u3002 \u3000\u3000\u53ea\u89c1\u8881\u627f\u5fd7\u53f3\u817f\u6a2a\u626b\uff0c\u5c06\u8981\u8e22\u5230\u91d1\u6761\uff0c\u5415\u4e03\u5148\u751f\u90a3\u652f\u70df\u888b\u53c8\u662f\u5feb\u5982\u95ea\u7535\u822c\u4f38\u51fa\uff0c\u5411\u4ed6\u817f\u4e0a\u70b9\u53bb\uff0c\u5c82\u77e5\u4ed6\u8fd9\u4e00\u811a\u8e22\u51fa\u5374\u662f\u865a\u62db\uff0c\u5bf9\u65b9\u624b\u81c2\u521a\u52a8\uff0c\u65e9\u5df2\u6536\u56de\u3002\u5415\u4e03\u5148\u751f\u4e00\u70b9\u4e0d\u4e2d\uff0c\u70df\u888b\u4e58\u52bf\u524d\u9001\u3002\u8881\u627f\u5fd7\u53f3\u817f\u6253\u4e86\u534a\u4e2a\u5c0f\u5708\u3002\u521a\u597d\u907f\u5f00\u70df\u888b\uff0c\u8f7b\u8f7b\u4e00\u6311\uff0c\u5df2\u5c06\u91d1\u6761\u6311\u8d77\uff0c\u53f3\u8db3\u4e0d\u505c\uff0c\u7ee7\u7eed\u6a2a\u626b\u3002 \u3000\u3000\u5415\u4e03\u5148\u751f\u4e5f\u5373\u53d8\u62db\uff0c\u70df\u7ba1\u5411\u4ed6\u540e\u5fc3\u731b\u7838\u3002\u8881\u627f\u5fd7\u5f13\u8eab\u5411\u53f3\u659c\u5c04\uff0c\u5de6\u624b\u5728\u6311\u8d77\u6765\u7684\u91d1\u6761\u4e0a\u4e00\u62cd\uff0c\u90a3\u91d1\u6761\u5411\u53f3\u98de\u51fa\uff0c\u540c\u65f6\u5de6\u8db3\u5728\u5415\u4e03\u5148\u751f\u8e0f\u5b9a\u7684\u4e24\u5757\u91d1\u6761\u4e0a\u626b\u53bb\uff0c\u91d1\u6761\u767b\u65f6\u98de\u8d77\u3002\u5415\u4e03\u5148\u751f\u8eab\u5b50\u4e00\u6643\uff0c\u9000\u6b65\u62ff\u6869\u7ad9\u5b9a\u3002\u8881\u627f\u5fd7\u53cc\u624b\u5404\u6293\u4f4f\u4e00\u5757\u91d1\u6761\uff0c\u5411\u5185\u4e00\u5408\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u5c06\u7b2c\u4e09\u5757\u91d1\u6761\u5939\u4f4f\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u91d1\u6761\u6211\u53ef\u90fd\u8981\u62ff\u4e86\uff0c\u5415\u8001\u524d\u8f88\u7684\u8bdd\uff0c\u603b\u7b97\u6570\u5427\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u4e0b\u624b\u6cd5\u8fc5\u6377\u4e4b\u6781\uff0c\u4f17\u4eba\u53ea\u89c9\u4e00\u9635\u773c\u82b1\u7f2d\u4e71\uff0c\u7b49\u5230\u4e24\u4eba\u5206\u5f00\uff0c\u8881\u627f\u5fd7\u4e09\u5757\u91d1\u6761\u5df2\u5728\u624b\u4e2d\uff0c\u8fd9\u4e00\u6765\uff0c\u9752\u9752\u7b11\u9765\u5982\u82b1\uff0c\u9ec4\u771f\u60ca\u559c\u4ea4\u96c6\uff0c\u5b89\u5c0f\u6167\u548c\u5d14\u5e0c\u654f\u62cd\u624b\u559d\u91c7\uff0c\u8fde\u77f3\u6881\u6d3e\u7684\u4eba\u4e5f\u90fd\u4e0d\u81ea\u7981\u7684\u53eb\u8d77\u597d\u6765\u3002 \u3000\u3000\u5415\u4e03\u5148\u751f\u8001\u8138\u7ea2\u5f97\u53d1\u7d2b\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u5de6\u638c\u55d6\u7684\u4e00\u58f0\u5411\u8881\u627f\u5fd7\u5288\u6765\uff0c\u638c\u521a\u53d1\u51fa\uff0c\u53f3\u8db3\u534a\u8f6c\uff0c\u540e\u8ddf\u53cd\u8e22\uff0c\u8e39\u5411\u5bf9\u65b9\u80eb\u9aa8\u3002 \u3000\u3000\u8fd9\u662f\u9e64\u5f62\u62f3\u4e2d\u7684\u602a\u62db\uff0c\u53cc\u638c\u4fbf\u5982\u4ed9\u9e64\u4e24\u7ffc\u6251\u51fb\uff0c\u53cc\u811a\u4f38\u7f29\uff0c\u5ffd\u957f\u5ffd\u77ed\uff0c\u5c31\u5982\u767d\u9e64\u76f8\u6597\u4e00\u822c\u3002\u4ed6\u5c06\u70df\u7ba1\u7f29\u5728\u53f3\u624b\u8896\u4e2d\uff0c\u624b\u638c\u7ffb\u98de\uff0c\u751a\u662f\u7075\u52a8\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4ece\u6ca1\u89c1\u8fc7\u8fd9\u8def\u602a\u62f3\uff0c\u4e00\u65f6\u4e0d\u6562\u6b3a\u8fd1\uff0c\u8fdc\u8fdc\u7ed5\u7740\u4ed6\u76d8\u65cb\u6253\u8f6c\uff0c\u8d8a\u5954\u8d8a\u5feb\u3002\u5415\u4e03\u5148\u751f\u89c1\u4ed6\u4e0d\u6562\u63a5\u8fd1\uff0c\u5fc3\u60f3\u8fd9\u5c0f\u5b50\u8eab\u624b\u867d\u7136\u654f\u6377\uff0c\u529f\u529b\u5374\u6d45\uff0c\u767b\u65f6\u8d77\u4e86\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u4ece\u8896\u4e2d\u638f\u51fa\u70df\u888b\u5927\u5438\u4e00\u53e3\uff0c\u55b7\u4e86\u53e3\u767d\u70df\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8f6c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u5df2\u6478\u5230\u4ed6\u638c\u6cd5\u7684\u7ea6\u7565\u8def\u5b50\uff0c\u89c1\u4ed6\u5438\u70df\u8f7b\u654c\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u5ffd\u7136\u7eb5\u8d77\uff0c\u5288\u9762\u4e00\u62f3\u5411\u4ed6\u9f3b\u6881\u6253\u53bb\u3002 \u3000\u3000\u5415\u4e03\u5148\u751f\u4e00\u60ca\uff0c\u4e3e\u8d77\u70df\u7ba1\u6321\u67b6\u3002\u8881\u627f\u5fd7\u62f3\u5df2\u53d8\u638c\uff0c\u5728\u70df\u7ba1\u4e0a\u4e00\u642d\uff0c\u53cd\u624b\u6293\u4f4f\u3002\u5415\u4e03\u5148\u751f\u7528\u529b\u540e\u626f\u3002\u8881\u627f\u5fd7\u65e9\u6599\u5230\u6b64\u62db\uff0c\u4e58\u4ed6\u4e00\u626f\u4e4b\u9645\u53f3\u80c1\u9732\u7a7a\uff0c\u4f38\u624b\u6233\u53bb\uff0c\u6b63\u4e2d\u4ed6\u201c\u5929\u5e9c\u7a74\u201d\u3002 \u3000\u3000\u5415\u4e03\u5148\u751f\u53f3\u8fb9\u8eab\u5b50\u4e00\u9635\u9178\u9ebb\uff0c\u70df\u7ba1\u8131\u624b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u77a5\u4e4b\u95f4\uff0c\u89c1\u9752\u9752\u7b11\u541f\u541f\u7684\u77a7\u7740\u81ea\u5df1\uff0c\u5fc3\u60f3\u7d22\u6027\u518d\u8ba9\u5979\u5f00\u5f00\u5fc3\uff0c\u5012\u8f6c\u70df\u888b\uff0c\u653e\u5230\u5415\u4e03\u5148\u751f\u80e1\u5b50\u4e0a\u3002\u70df\u888b\u4e2d\u7684\u70df\u4e1d\u7ed9\u4ed6\u9002\u624d\u4e00\u53e3\u731b\u5438\uff0c\u70e7\u5f97\u6b63\u65fa\uff0c\u80e1\u5b50\u767b\u65f6\u70e7\u7126\uff0c\u4e00\u9635\u9752\u70df\u5192\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u9ec4\u771f\u53eb\u9053\uff1a\u201c\u4e56\u4e56\u4e0d\u5f97\u4e86\uff01\u5415\u4e03\u5148\u751f\u62ff\u80e1\u5b50\u5f53\u70df\u4e1d\u62bd\u3002\u201d\u8881\u627f\u5fd7\u5f20\u53e3\u5728\u70df\u7ba1\u4e0a\u4e00\u5439\uff0c\u70df\u4e1d\u3001\u70df\u7070\u3001\u706b\u661f\u4e00\u9f50\u98de\u51fa\uff0c\u7c98\u5f97\u5415\u4e03\u5148\u751f\u6ee1\u8138\u90fd\u662f\u3002\u9ec4\u771f\u54c8\u54c8\u5927\u7b11\uff0c\u7eb5\u8eab\u8fc7\u53bb\uff0c\u63a8\u634f\u51e0\u4e0b\uff0c\u89e3\u5f00\u4e86\u5415\u4e03\u5148\u751f\u7684\u7a74\u9053\uff0c\u631f\u624b\u593a\u8fc7\u70df\u7ba1\uff0c\u585e\u5728\u4ed6\u7684\u624b\u91cc\u3002 \u3000\u3000\u5415\u4e03\u5148\u751f\u6123\u5728\u5f53\u5730\uff0c\u89c1\u4f17\u4eba\u90fd\u4f3c\u7b11\u975e\u7b11\u7684\u671b\u7740\u4ed6\uff0c\u53ea\u6c14\u5f97\u8138\u8272\u53d1\u9752\uff0c\u628a\u70df\u7ba1\u5f80\u5730\u4e0b\u4e00\u6454\uff0c\u8f6c\u8eab\u5954\u4e86\u51fa\u53bb\u3002\u8363\u5f69\u53eb\u9053\uff1a\u201c\u5415\u4e03\u5148\u751f\uff01\u201d\u62fe\u8d77\u70df\u7ba1\uff0c\u8ffd\u4e0a\u53bb\u62c9\u4ed6\u7684\u8896\u5b50\uff0c\u88ab\u4ed6\u731b\u529b\u4e00\u7529\uff0c\u6253\u4e86\u4e2a\u8e09\u8dc4\u3002\u5415\u4e03\u5148\u751f\u811a\u4e0d\u505c\u6b65\uff0c\u65e9\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8001\u5bb6\u4f19\u6253\u4e86\u8d25\u4ed7\uff0c\u600e\u5730\u8fde\u70df\u7ba1\u4e5f\u4e0d\u8981\u4e86\uff1f\u201d\u9ec4\u771f\u4e00\u672c\u6b63\u7ecf\u7684\u7b54\u9053\uff1a\u201c\u8001\u5bb6\u4f19\u6212\u4e86\u70df\u5566\uff01\u201d\u5d14\u5e0c\u654f\u6414\u6414\u5934\u76ae\uff0c\u53ef\u5c31\u4e0d\u660e\u767d\u6253\u4e86\u8d25\u4ed7\u5e72\u4e48\u5f97\u6212\u70df\u3002\u4ed6\u4e0d\u6562\u518d\u95ee\u5e08\u7236\uff0c\u5411\u5b89\u5c0f\u6167\u671b\u53bb\uff0c\u53ea\u89c1\u5979\u5140\u81ea\u4e3a\u5415\u4e03\u5148\u751f\u72fc\u72c8\u8d25\u9003\u800c\u683c\u683c\u5a07\u7b11\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e03\u56de\u3000\u7834\u9635\u7f18\u79d8\u7b08 \u85cf\u73cd\u6709\u9057\u56fe",content:(0,t.jsx)(t.Fragment,{children:"\u77f3\u6881\u6d3e\u8bf8\u4eba\u89c1\u8fc7\u8881\u627f\u5fd7\u7684\u6b66\u529f\uff0c\u8fd8\u4e0d\u600e\u6837\u3002\u9f99\u6e38\u5e2e\u7684\u515a\u5f92\u7d20\u6765\u628a\u5415\u4e03\u5148\u751f\u5949\u82e5\u5929\u795e\uff0c\u8fd9\u65f6\u89c1\u4e00\u4e2a\u5e74\u8f7b\u5c0f\u4f19\u5b50\u968f\u624b\u5c06\u4ed6\u6253\u5f97\u5927\u8d25\u800c\u8d70\uff0c\u90fd\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u4e2d\u6700\u611f\u5947\u602a\u7684\u5374\u662f\u9ec4\u771f\u3002\u4ed6\u89c1\u8881\u627f\u5fd7\u5728\u5415\u4e03\u80c1\u4e0b\u8fd9\u4e00\u6233\uff0c\u786e\u662f\u534e\u5c71\u6d3e\u7edd\u6280\u201c\u94c1\u6307\u8bc0\u201d\uff0c\u7136\u800c\u4ed6\u7ed5\u7740\u5bf9\u65b9\u6e38\u8d70\u3001\u4ee5\u53ca\u8896\u5b50\u515c\u63a5\u91d1\u6761\u7684\u8eab\u6cd5\uff0c\u5374\u4e0e\u81ea\u5df1\u6240\u4e60\u8fe5\u7136\u4e0d\u540c\uff0c\u9664\u4e86\u53cd\u624b\u6293\u593a\u70df\u7ba1\u8fd9\u4e00\u62db\u4e4b\u5916\uff0c\u4f59\u4e0b\u8fd9\u51e0\u4e0b\u5c0f\u5de7\u53d8\u5e7b\uff0c\u53c8\u5e26\u7740\u4e09\u5206\u8be1\u79d8\u4e4b\u6c14\uff0c\u51b3\u975e\u534e\u5c71\u6d3e\u6b66\u529f\u4ee5\u6d51\u539a\u7cbe\u5947\u89c1\u957f\u7684\u5bb6\u6570\uff0c\u81ea\u4e0d\u662f\u5e08\u7236\u665a\u5e74\u522b\u521b\u65b0\u62db\u800c\u4f20\u6388\u4e86\u8fd9\u5c0f\u5e08\u5f1f\uff0c\u4e00\u65f6\u4e5f\u60f3\u4e0d\u660e\u767d\uff0c\u5f53\u4e0b\u5728\u94c1\u7b97\u76d8\u4e0a\u4e00\u62e8\uff0c\u8bf4\u9053\uff1a\u201c\u521a\u624d\u90a3\u4f4d\u8001\u7237\u5b50\u8bf4\u8fc7\uff0c\u53ea\u8981\u52a8\u4e86\u4e09\u6839\u91d1\u6761\uff0c\u5168\u90e8\u9ec4\u91d1\u5949\u8fd8\uff0c\u5144\u5f1f\u5728\u8fd9\u91cc\u8c22\u8fc7\u3002\u201d\u53cc\u624b\u4e00\u62f1\uff0c\u5bf9\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u90fd\u6361\u8d77\u6765\u5427\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u4fef\u8eab\u53c8\u8981\u53bb\u62fe\u91d1\u6761\u3002\u8363\u5f69\u773c\u89c1\u9ec4\u6f84\u6f84\u7684\u8bb8\u591a\u91d1\u6761\u4fbf\u8981\u843d\u5165\u522b\u4eba\u624b\u4e2d\uff0c\u5fc3\u4e0b\u5927\u6025\uff0c\u660e\u77e5\u6709\u8881\u627f\u5fd7\u8fd9\u7b49\u9ad8\u624b\u5728\u4fa7\uff0c\u51ed\u81ea\u5df1\u529f\u592b\u7edd\u4e0d\u80fd\u8ba8\u5f97\u4e86\u597d\u53bb\uff0c\u53ef\u662f\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\u201c\u89c1\u8005\u6709\u4efd\u201d\uff0c\u9f99\u6e38\u5e2e\u4e3a\u8fd9\u6279\u9ec4\u91d1\u635f\u6298\u4eba\u547d\uff0c\u5954\u6ce2\u591a\u65e5\uff0c\u5c31\u7b97\u5206\u4e0d\u5230\u4e00\u534a\uff0c\u4e5f\u5f97\u5206\u4e0a\u4e09\u6210\uff0c\u591a\u591a\u5c11\u5c11\u4e5f\u5f97\u6367\u51e0\u6839\u91d1\u6761\u56de\u5bb6\uff0c\u6b3a\u5d14\u5e0c\u654f\u6b66\u529f\u5e73\u5e73\uff0c\u5f53\u5373\u62a2\u4e0a\u524d\u6765\uff0c\u6a2a\u8fc7\u5de6\u81c2\u5728\u4ed6\u53cc\u81c2\u4e0a\u4e00\u63a8\u3002\u5d14\u5e0c\u654f\u9000\u51fa\u6570\u6b65\uff0c\u6012\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4e5f\u8981\u89c1\u8fc7\u8f93\u8d62\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u9ec4\u771f\u773c\u770b\u8363\u5f69\u8eab\u6cd5\uff0c\u77e5\u9053\u5f92\u513f\u4e0d\u662f\u4ed6\u5bf9\u624b\uff0c\u559d\u9053\uff1a\u201c\u5e0c\u654f\uff0c\u9000\u4e0b\uff01\u201d\u62a2\u4e0a\u6765\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u606d\u559c\u53d1\u8d22\uff01\u638c\u67dc\u7684\u5b9d\u53f7\u662f\u751a\u4e48\u5b57\u53f7\uff1f\u5927\u8001\u677f\u4e00\u5411\u505a\u751a\u4e48\u751f\u610f\uff1f\u60f3\u5fc5\u662f\u751f\u610f\u5174\u9686\u901a\u56db\u6d77\uff0c\u8d22\u6e90\u8302\u76db\u8fbe\u4e09\u6c5f\u3002\u201d\u4ed6\u662f\u5546\u8d3e\u51fa\u8eab\uff0c\u751f\u6027\u6ed1\u7a3d\uff0c\u4e34\u654c\u65f6\u5fc5\u5b9a\u8bf4\u756a\u4e0d\u4f26\u4e0d\u7c7b\u7684\u751f\u610f\u7ecf\u3002 \u3000\u3000\u8363\u5f69\u6012\u9053\uff1a\u201c\u8c01\u8ddf\u4f60\u5f00\u73a9\u7b11\uff1f\u5728\u4e0b\u59d3\u8363\u540d\u5f69\uff0c\u5fdd\u4efb\u9f99\u6e38\u5e2e\u7684\u5e2e\u4e3b\u3002\u8fd8\u6ca1\u8bf7\u6559\u9601\u4e0b\u7684\u4e07\u513f\u3002\u201d\u9ec4\u771f\u9053\uff1a\u201c\u8d31\u59d3\u9ec4\uff0c\u4fbf\u662f\u2018\u9ec4\u91d1\u4e07\u4e24\u2019\u4e4b\u9ec4\uff0c\u5f69\u5934\u751a\u597d\u3002\u8349\u5b57\u5355\u540d\u4e00\u4e2a\u771f\u5b57\uff0c\u53d6\u5176\u771f\u4e0d\u4e8c\u4ef7\u3001\u8d27\u771f\u4ef7\u5b9e\u7684\u610f\u601d\u3002\u4e00\u4e24\u94f6\u5b50\u7684\u4e1c\u897f\uff0c\u5c0f\u53f7\u51b3\u4e0d\u6562\u8981\u4e00\u4e24\u96f6\u4e00\u6587\uff0c\u90a3\u771f\u662f\u8001\u5e7c\u54b8\u5b9c\uff0c\u7ae5\u53df\u65e0\u6b3a\u3002\u5927\u8001\u677f\u6709\u751a\u4e48\u751f\u610f\uff0c\u8bf7\u4f60\u5e2e\u886c\u5e2e\u886c\u3002\u201d \u3000\u3000\u8363\u5f69\u542c\u4ed6\u8bf4\u4e2a\u6ca1\u5b8c\uff0c\u8d8a\u542c\u8d8a\u6012\uff0c\u773c\u89c1\u4ed6\u5f62\u8c8c\u840e\u7410\uff0c\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u62ff\u5bb6\u4f19\u6765\u3002\u201d\u9f99\u6e38\u5e2e\u7684\u5144\u5f1f\uff0c\u5f53\u5373\u9012\u8fc7\u4e00\u6746\u5927\u67aa\u3002\u8363\u5f69\u63a5\u67aa\u4e00\u9001\uff0c\u4e00\u4e2a\u6597\u5927\u67aa\u82b1\uff0c\u52bf\u631f\u52b2\u98ce\uff0c\u8fce\u9762\u523a\u51fa\u3002\u9ec4\u771f\u5012\u8e29\u4e03\u661f\u6b65\uff0c\u500f\u7136\u62d4\u8d77\u8eab\u5b50\uff0c\u5411\u5de6\u8df3\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u54b1\u4eec\u505a\u751f\u610f\u7684\uff0c\u91d1\u5b50\u53ef\u4e0d\u80fd\u4e0d\u8981\u3002\u201d\u5c06\u7b97\u76d8\u548c\u94dc\u7b14\u5f80\u6000\u91cc\u4e00\u63e3\uff0c\u4fef\u8eab\u5c31\u53bb\u6361\u91d1\u6761\u3002 \u3000\u3000\u6e29\u6c0f\u4e94\u5144\u5f1f\u89c1\u4ed6\u8eab\u6cd5\uff0c\u77e5\u662f\u52b2\u654c\uff0c\u8363\u5f69\u7edd\u975e\u5bf9\u624b\u3002\u6e29\u65b9\u4e49\u3001\u6e29\u65b9\u609f\u4e24\u4eba\u540c\u65f6\u6251\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u8981\u62ff\u91d1\u5b50\uff0c\u53ef\u6ca1\u90a3\u4e48\u5bb9\u6613\u3002\u201d \u3000\u3000\u9ec4\u771f\u89c1\u4e8c\u4eba\u6765\u52bf\u731b\u6076\uff0c\u5411\u53f3\u659c\u8eab\u907f\u5f00\uff0c\u5de6\u624b\u201c\u656c\u5fb7\u6302\u97ad\u201d\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u659c\u5288\u4e0b\u6765\u3002\u6e29\u65b9\u4e49\u3001\u65b9\u609f\u4e24\u4eba\u4e00\u51fa\u624b\u8d70\u7684\u5c31\u662f\u4e94\u884c\u9635\u8def\u5b50\uff0c\u4e00\u62db\u6253\u51fa\uff0c\u4e24\u4eba\u65e9\u5df2\u9000\u5f00\u3002\u6e29\u65b9\u8fbe\u3001\u6e29\u65b9\u5c71\u5144\u5f1f\u62a2\u4e86\u4e0a\u6765\u3002\u6e29\u65b9\u5c71\u53f3\u624b\u5f80\u4e0a\u4e00\u6321\uff0c\u67b6\u5f00\u9ec4\u771f\u4e00\u62db\uff0c\u6e29\u65b9\u65bd\u5de6\u62f3\u5df2\u5411\u4ed6\u540e\u5fc3\u51fb\u5230\u3002 \u3000\u3000\u9ec4\u771f\u867d\u7136\u8bf4\u8bdd\u8bd9\u8c10\uff0c\u505a\u4e8b\u5374\u662f\u5c0f\u5fc3\u8c28\u614e\uff0c\u52a0\u4e4b\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e00\u751f\u4e0e\u4eba\u5bf9\u654c\uff0c\u6781\u5c11\u843d\u4e8e\u4e0b\u98ce\uff0c\u8fd9\u65f6\u9661\u7136\u9677\u5165\u4e94\u884c\u9635\u4e4b\u4e2d\uff0c\u6570\u62db\u4e00\u8fc7\uff0c\u6e29\u6c0f\u5144\u5f1f\u6b64\u53bb\u5f7c\u6765\uff0c\u4f60\u6321\u6211\u51fb\uff0c\u4e94\u4e2a\u4eba\u5c31\u5982\u6570\u5341\u4eba\u822c\u6e90\u6e90\u800c\u4e0a\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\u8fd9\u662f\u751a\u4e48\u9635\u6cd5\uff0c\u600e\u5730\u5982\u6b64\u590d\u6742\u8fc5\u6377\uff0c\u5f53\u4e0b\u62b1\u5143\u5b88\u4e00\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u4e0d\u6562\u518d\u884c\u8fdb\u653b\u3002 \u3000\u3000\u8363\u5f69\u89c1\u9ec4\u771f\u9677\u5165\u5305\u56f4\uff0c\u53ea\u89c1\u52c9\u529b\u62db\u67b6\uff0c\u65e0\u6cd5\u8fd8\u624b\uff0c\u5fc3\u5934\u5927\u559c\uff0c\u53ea\u9053\u6709\u4fbf\u5b9c\u53ef\u6361\uff0c\u4f7f\u5f00\u6768\u5bb6\u67aa\u6cd5\uff0c\u4e00\u62db\u201c\u7075\u86c7\u535a\u51fb\u201d\uff0c\u75be\u5f80\u9ec4\u771f\u540e\u5fc3\u523a\u53bb\u3002 \u3000\u3000\u5c0f\u6167\u5403\u4e86\u4e00\u60ca\uff0c\u5927\u53eb\uff1a\u201c\u9ec4\u5e08\u4f2f\u7559\u795e\u3002\u201d\u9ec4\u771f\u662f\u7a46\u4eba\u6e05\u7684\u5f00\u5c71\u5927\u5f1f\u5b50\uff0c\u6b66\u529f\u6df1\u5f97\u534e\u5c71\u6d3e\u771f\u4f20\uff0c\u6e29\u6c0f\u4e94\u5144\u5f1f\u82e5\u975e\u7ec3\u5c31\u8fd9\u72ec\u95e8\u9635\u6cd5\uff0c\u5c31\u662f\u4e94\u4eba\u9f50\u4e0a\uff0c\u4e5f\u4e0d\u662f\u4ed6\u7684\u654c\u624b\u3002\u533a\u533a\u8363\u5f69\uff0c\u5c82\u80fd\u5948\u4f55\u4e86\u4ed6\uff1f\u8033\u542c\u5f97\u80cc\u540e\u94c1\u67aa\u98ce\u58f0\uff0c\u9ec4\u771f\u53cd\u624b\u4e00\u635e\uff0c\u5df2\u6293\u4f4f\u67aa\u5934\uff0c\u8fd9\u7a7a\u624b\u5165\u767d\u5203\u7684\u624b\u6cd5\uff0c\u6b63\u4e0e\u8881\u627f\u5fd7\u521a\u624d\u6293\u4f4f\u5415\u4e03\u70df\u7ba1\u5982\u51fa\u4e00\u8f99\uff0c\u53ea\u662f\u9ec4\u771f\u4ee5\u6570\u5341\u5e74\u7684\u529f\u529b\uff0c\u66f4\u52a0\u8fc5\u6377\u5389\u5bb3\uff0c\u987a\u624b\u5c06\u8363\u5f69\u62c9\u4e86\u8fc7\u6765\uff0c\u540c\u65f6\u5de6\u638c\u201c\u5355\u638c\u5f00\u7891\u201d\uff0c\u62cd\u5f00\u6e29\u65b9\u5c71\u6253\u6765\u7684\u4e00\u62f3\uff0c\u53f3\u817f\u8e0f\u4e0a\u534a\u6b65\uff0c\u8ba9\u53bb\u4e86\u6e29\u65b9\u4e49\u4ece\u540e\u9762\u8e39\u4e0a\u6765\u7684\u4e00\u811a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u5927\u67aa\u98de\u8d77\uff0c\u8363\u5f69\u8ddf\u7740\u4ece\u516d\u4eba\u5934\u9876\u98de\u4e86\u51fa\u6765\uff0c\u6454\u5728\u5730\u4e0b\u3002\u9f99\u6e38\u5e2e\u7684\u5f1f\u5144\u4eec\u5fd9\u62a2\u4e0a\u6276\u8d77\u3002\u9f99\u6e38\u5e2e\u526f\u5e2e\u4e3b\u3001\u8363\u5f69\u7684\u5927\u5f1f\u5b50\u3001\u4e8c\u5f1f\u5b50\u89c1\u5e2e\u4e3b\u5931\u624b\uff0c\u5f53\u5373\u4e00\u8d77\u62a2\u5165\uff0c\u4e0d\u6570\u62db\uff0c\u4e09\u4eba\u63a5\u4e8c\u8fde\u4e09\u7684\u88ab\u9ec4\u771f\u6454\u4e86\u51fa\u6765\u3002\u526f\u5e2e\u4e3b\u66f4\u662f\u6298\u65ad\u4e86\u53f3\u81c2\uff0c\u8eab\u53d7\u91cd\u4f24\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u9f99\u6e38\u5e2e\u65e0\u4eba\u518d\u6562\u52a0\u5165\u6218\u56e2\u3002 \u3000\u3000\u9ec4\u771f\u53eb\u9053\uff1a\u201c\u5927\u8001\u677f\u3001\u4e8c\u8001\u677f\uff0c\u89c1\u8005\u6709\u4efd\uff0c\u4eba\u4eba\u6709\u4efd\u6454\u4e0a\u4e00\u4ea4\uff0c\u51b3\u4e0d\u843d\u7a7a\uff01\u201d \u3000\u3000\u4ed6\u529b\u6597\u6e29\u6c0f\u4e94\u8001\uff0c\u6253\u5230\u9163\u5904\uff0c\u53ea\u89c1\u516d\u6761\u4eba\u5f71\u5f80\u6765\u98de\u821e\uff0c\u6709\u65f6\u9ec4\u771f\u7a81\u51fa\u5305\u56f4\uff0c\u4f46\u4e94\u4eba\u5982\u5f71\u968f\u5f62\uff0c\u7acb\u5373\u88f9\u4e0a\u3002\u9ec4\u771f\u5fc3\u91cc\u6697\u6697\u7740\u6025\uff0c\u5927\u53eb\uff1a\u201c\u672c\u5c0f\u5229\u5927\uff0c\u9ec4\u8001\u677f\u4e00\u4e2a\u4eba\u505a\u4e94\u7b14\u751f\u610f\uff0c\u53ef\u6709\u70b9\u513f\u5fd9\u4e0d\u8fc7\u6765\u5566\uff01\u201d\u6e29\u6c0f\u5144\u5f1f\u4e5f\u4e0d\u80dc\u9a87\u5f02\uff0c\u5fc3\u60f3\u77a7\u4e0d\u51fa\u8fd9\u571f\u8001\u513f\u6a21\u6837\u7684\u5bb6\u4f19\uff0c\u5c45\u7136\u95e8\u6237\u5b88\u5f97\u5982\u6b64\u4e25\u5bc6\u3002 \u3000\u3000\u9ec4\u771f\u89c1\u654c\u624b\u8d8a\u6253\u8d8a\u6025\uff0c\u4e94\u4e2a\u4eba\u5982\u7a7f\u82b1\u8774\u8776\u822c\u4e71\u8f6c\u3002\u6709\u65f6\u4e00\u4eba\u4f5c\u52bf\u6b32\u8e22\uff0c\u5c82\u77e5\u7a81\u7136\u5f80\u65c1\u8ba9\u5f00\uff0c\u4ed6\u8eab\u540e\u4e00\u4eba\u731b\u7136\u53d1\u62f3\u6253\u5230\uff1b\u6709\u65f6\u4e00\u4eba\u53cc\u624b\u5408\u62b1\uff0c\u610f\u6b32\u8089\u640f\uff0c\u4ed6\u5f80\u540e\u9762\u9000\u907f\uff0c\u540e\u5fc3\u6709\u811a\u521a\u597d\u8e22\u5230\uff0c\u51d1\u5f97\u518d\u5408\u62cd\u4e5f\u6ca1\u6709\u3002\u773c\u89c1\u654c\u62db\u53d8\u5316\u65e0\u7a77\u65e0\u5c3d\uff0c\u9ec4\u771f\u7adf\u662f\u500f\u9047\u51f6\u9669\uff0c\u5168\u4ed7\u6b66\u529f\u7cbe\u7eaf\uff0c\u8fd9\u624d\u907f\u8fc7\uff0c\u4e8e\u662f\u957f\u5578\u4e00\u58f0\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u94dc\u7b14\u94c1\u7b97\u76d8\uff0c\u5fc3\u60f3\u4f60\u4eec\u4e94\u4e2a\u6253\u6211\u4e00\u4e2a\uff0c\u5df2\u975e\u516c\u5e73\u4ea4\u6613\uff0c\u9ec4\u8001\u677f\u5148\u4f7f\u5175\u5203\uff0c\u7b97\u4e0d\u5f97\u574f\u4e86\u7ae5\u53df\u65e0\u6b3a\u7684\u89c4\u77e9\u3002\u5f53\u4e0b\u4ee5\u653b\u4e3a\u5b88\uff0c\u7b97\u76d8\u65c1\u6572\u4fa7\u51fb\uff0c\u94dc\u7b14\u6a2a\u626b\u659c\u70b9\uff0c\u5175\u5203\u6240\u6307\u4e4b\u5904\uff0c\u5c3d\u662f\u4e94\u8001\u7684\u8981\u7a74\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u553f\u54e8\u4e00\u58f0\uff0c\u6e29\u6b63\u548c\u6e29\u5357\u626c\u7b49\u5c06\u4e94\u4eba\u5175\u5203\u629b\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e94\u5144\u5f1f\u6216\u633a\u53cc\u621f\uff0c\u6216\u4f7f\u5355\u5200\uff0c\u6216\u821e\u8f6f\u97ad\uff0c\u6216\u6325\u94a2\u6756\uff0c\u957f\u77ed\u9f50\u4e0a\uff0c\u521a\u67d4\u5e76\u6d4e\uff0c\u5076\u800c\u8fd8\u5939\u7740\u51e0\u67c4\u98de\u5200\u3002\u8fd9\u756a\u6076\u6597\uff0c\u6bd4\u4e4b\u521a\u624d\u62f3\u811a\u4ea4\u52a0\uff0c\u53c8\u591a\u4e86\u51e0\u5206\u51f6\u9669\uff0c\u9ec4\u8001\u677f\u8fd9\u6869\u4e70\u5356\uff0c\u773c\u770b\u662f\u8981\u5927\u8680\u800c\u7279\u8680\u4e86\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u89c1\u5e08\u7236\u60c5\u52bf\u5371\u6025\uff0c\u660e\u77e5\u81ea\u5df1\u4e0d\u6d4e\uff0c\u5374\u4e5f\u7ba1\u4e0d\u5f97\u4e86\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u62d4\u51fa\u5355\u5200\uff0c\u76f4\u5411\u4e94\u884c\u9635\u4e2d\u7eb5\u53bb\u3002\u521a\u8de8\u51fa\u4e24\u6b65\uff0c\u5ffd\u89c1\u773c\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u6709\u4eba\u4e3e\u638c\u5411\u81ea\u5df1\u80a9\u5934\u6309\u6765\u3002\u5d14\u5e0c\u654f\u6a2a\u5200\u4fbf\u780d\u3002\u90a3\u4eba\u8fd9\u4e00\u6309\u5feb\u6781\uff0c\u500f\u7136\u95f4\u5df2\u642d\u4e0a\u4ed6\u80a9\u5934\u3002\u5d14\u5e0c\u654f\u8eab\u5b50\u767b\u5982\u4e07\u65a4\u4e4b\u91cd\uff0c\u518d\u4e5f\u8de8\u4e0d\u51fa\u6b65\u53bb\uff0c\u5927\u9a87\u4e4b\u4e0b\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u5d14\u5927\u54e5\uff0c\u4f60\u4e0d\u80fd\u53bb\u3002\u201d\u624d\u770b\u6e05\u90a3\u4eba\u539f\u6765\u662f\u8881\u627f\u5fd7\u3002\u521a\u624d\u8881\u627f\u5fd7\u70b9\u5012\u5415\u4e03\u5148\u751f\uff0c\u4ed6\u8fd8\u4e0d\u600e\u4e48\u4f69\u670d\uff0c\u5fc3\u60f3\u4e0d\u8fc7\u662f\u4e00\u65f6\u4fa5\u5e78\uff0c\u53ef\u662f\u6b64\u523b\u88ab\u4ed6\u4e00\u638c\u8f7b\u8f7b\u642d\u5728\u80a9\u5934\uff0c\u81ea\u5df1\u534a\u8fb9\u8eab\u4f53\u7adf\u4e1d\u6beb\u4f7f\u4e0d\u51fa\u52b2\uff0c\u624d\u77e5\u4eba\u5bb6\u6b66\u529f\u6bd4\u81ea\u5df1\u9ad8\u5f97\u592a\u591a\uff0c\u90a3\u5c31\u5f53\u771f\u5947\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u653e\u5f00\u4e86\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u8fd8\u53ef\u62b5\u6321\u4e00\u9635\uff0c\u522b\u7740\u6025\u3002\u201d \u3000\u3000\u4ed6\u89c1\u516d\u4eba\u53c8\u6597\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u4e2a\u96be\u9898\uff0c\u7709\u5934\u5fae\u8e59\uff0c\u4e00\u65f6\u62ff\u4e0d\u5b9a\u4e3b\u610f\u3002 \u3000\u3000\u5b89\u5c0f\u6167\u8d70\u5230\u4ed6\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u627f\u5fd7\u5927\u54e5\uff0c\u4f60\u5feb\u53bb\u5e2e\u9ec4\u5e08\u4f2f\u554a\u3002\u4ed6\u4eec\u4e94\u4e2a\u4eba\u6253\u4ed6\u4e00\u4e2a\uff0c\u591a\u4e0d\u8981\u8138\u3002\u201d\u8881\u627f\u5fd7\u4e0d\u7b54\uff0c\u6325\u624b\u53eb\u5979\u8d70\u5f00\u3002\u5c0f\u6167\u8ba8\u4e86\u4e2a\u6ca1\u8da3\uff0c\u6485\u8d77\u4e86\u5c0f\u5634\u8d70\u5f00\u3002\u9752\u9752\u770b\u5728\u773c\u91cc\uff0c\u82b3\u5fc3\u6697\u559c\u3002 \u3000\u3000\u53ea\u89c1\u516d\u4eba\u8d8a\u6253\u8d8a\u5feb\uff0c\u9ec4\u771f\u6bcf\u6b21\u7528\u94c1\u7b97\u76d8\u53bb\u9501\u62ff\u5bf9\u65b9\u5175\u5203\uff0c\u4e94\u8001\u603b\u662f\u8fc5\u901f\u95ea\u5f00\uff0c\u516d\u4eba\u6253\u5f97\u867d\u7d27\uff0c\u5374\u4e1d\u6beb\u4e0d\u95fb\u91d1\u94c1\u4ea4\u5e76\u4e4b\u58f0\uff0c\u5927\u5385\u4e2d\u4f46\u542c\u5f97\u5175\u5203\u6325\u52a8\u548c\u8863\u886b\u98de\u821e\u7684\u547c\u547c\u98ce\u58f0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5ffd\u5730\u8dc3\u8d77\uff0c\u8d70\u5230\u5c0f\u6167\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u6167\u59b9\u59b9\uff0c\u4f60\u522b\u602a\u6211\u65e0\u793c\u3002\u521a\u624d\u6211\u5728\u60f3\u4e00\u4ef6\u4e8b\u51fa\u4e86\u795e\uff0c\u73b0\u4e0b\u53ef\u60f3\u901a\u5566\u3002\u201d\u5c0f\u6167\u5ffd\u9053\uff1a\u201c\u8fd9\u5f53\u53e3\u8fd8\u9053\u751a\u4e48\u6b49\u5566\uff0c\u5feb\u53bb\u5e2e\u9ec4\u5e08\u4f2f\u5440\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u6211\u60f3\u901a\u4e86\u5c31\u4e0d\u6015\u4e86\u3002\u201d\u5c0f\u6167\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u662f\u7684\uff0c\u4e5f\u4e0d\u5206\u4e2a\u8f7b\u91cd\u7f13\u6025\u3002\u6709\u751a\u4e48\u4e3a\u96be\u7684\u4e8b\uff0c\u6253\u5b8c\u4e86\u518d\u60f3\u4e0d\u6210\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u6211\u60f3\u7684\u5c31\u662f\u600e\u6837\u7834\u8fd9\u9635\u6cd5\u3002\u4f60\u6709\u6ca1\u770b\u51fa\u6765\uff0c\u8fd9\u4e94\u4e2a\u8001\u5934\u513f\u7684\u5175\u5668\uff0c\u4ece\u6765\u6ca1\u8ddf\u5e08\u54e5\u7684\u94dc\u7b14\u94c1\u7b97\u76d8\u78b0\u8fc7\u4e00\u4e0b\uff1f\u201d\u5c0f\u6167\u9053\uff1a\u201c\u6211\u4e5f\u89c9\u5f97\u5947\u602a\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u8fd9\u65f6\u5bf9\u8881\u627f\u5fd7\u5df2\u9887\u6709\u70b9\u4f69\u670d\uff0c\u95ee\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u90a3\u5374\u662f\u751a\u4e48\u9053\u7406\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u9635\u52bf\u5706\u8f6c\u6d51\u6210\uff0c\u4e0d\u9732\u4e1d\u6beb\u7834\u7efd\uff0c\u53cc\u65b9\u5175\u5668\u4e00\u78b0\uff0c\u7a0d\u6709\u987f\u632b\uff0c\u5c31\u4e0d\u514d\u6709\u7a7a\u9699\u53ef\u5bfb\u3002\u7834\u9635\u4e4b\u9053\uff0c\u5728\u4e8e\u8bbe\u6cd5\u5fe7\u4e71\u4e94\u4eba\u7684\u811a\u6b65\u65b9\u4f4d\uff0c\u53ea\u5f97\u5f15\u5f97\u4e94\u4e2a\u8001\u5934\u513f\u4e2d\u6709\u4e00\u4eba\u8d70\u9519\u811a\u6b65\uff0c\u6216\u662f\u6162\u5f97\u4e00\u6162\uff0c\u8fd9\u9635\u5c31\u7834\u4e86\u3002\u201d\u5d14\u5e0c\u654f\u6447\u5934\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u719f\u7ec3\u4e86\u7684\uff0c\u5305\u7ba1\u95ed\u4e86\u773c\u775b\u4e5f\u4e0d\u4f1a\u8d70\u9519\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u70b9\u5934\u9053\uff1a\u201c\u4ed6\u4eec\u7ec3\u5f97\u5f53\u771f\u719f\u6781\u3002\u201d\u8f6c\u5934\u5bf9\u5c0f\u6167\u9053\uff1a\u201c\u4f60\u7684\u53d1\u9497\u8bf7\u501f\u6211\u4e00\u7528\u3002\u201d\u5c0f\u6167\u628a\u63d2\u5728\u5934\u53d1\u4e0a\u7684\u7389\u7c2a\u62d4\u4e86\u4e0b\u6765\u9012\u7ed9\u4ed6\u3002\u8fd9\u7389\u7c2a\u6e05\u6f84\u6676\u83b9\uff0c\u53d1\u51fa\u6de1\u6de1\u78a7\u5149\uff0c\u8881\u627f\u5fd7\u63a5\u4e86\u8fc7\u6765\uff0c\u7a81\u7136\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u620a\u571f\u751f\u4e59\u6728\uff0c\u8e0f\u4e7e\u5bab\uff0c\u8d70\u574e\u4f4d\u3002\u201d \u3000\u3000\u9ec4\u771f\u4e00\u6014\uff0c\u5c1a\u672a\u660e\u767d\uff0c\u6e29\u6c0f\u4e94\u8001\u5374\u5df2\u6697\u6697\u9a87\u5f02\uff1a\u201c\u600e\u4e48\u6211\u4eec\u8fd9\u4e94\u884c\u9635\u7684\u79d8\u5965\uff0c\u7ed9\u8fd9\u5c0f\u5b50\u77a7\u51fa\u6765\u4e86\uff1f\u201d\u8881\u627f\u5fd7\u53c8\u53eb\uff1a\u201c\u4e19\u706b\u514b\u5e9a\u91d1\uff0c\u8d70\u9738\u5bab\uff0c\u51fa\u79bb\u4f4d\uff01\u201d \u3000\u3000\u9ec4\u771f\u7f20\u6597\u826f\u4e45\uff0c\u4e0d\u8bba\u5f3a\u653b\u5de7\u8bf1\uff0c\u59cb\u7ec8\u8131\u4e0d\u51fa\u4e94\u8001\u7684\u5305\u56f4\uff0c\u4ed6\u65e9\u60f3\u5230\uff0c\u8fd9\u9635\u52bf\u65e2\u53eb\u4e94\u884c\u9635\uff0c\u5fc5\u542b\u4e94\u884c\u751f\u514b\u53d8\u5316\u4e4b\u7406\uff0c\u7136\u4e94\u8001\u7a7f\u68ad\u822c\u6765\u53bb\uff0c\u653b\u52bf\u51cc\u5389\uff0c\u53ea\u5f97\u594b\u529b\u62b5\u5fa1\uff0c\u6beb\u65e0\u4e1d\u6beb\u4f59\u6687\u53bb\u63a8\u6572\u9635\u6cd5\uff0c\u5ffd\u542c\u8881\u627f\u5fd7\u53eb\u558a\uff0c\u5fc3\u60f3\uff1a\u201c\u8bd5\u4e00\u8bd5\u4e5f\u597d\u3002\u201d\u7acb\u65f6\u8d70\u9707\u5bab\uff0c\u51fa\u79bb\u4f4d\uff0c\u679c\u7136\u89c1\u5230\u4e86\u4e00\u4e2a\u7a7a\u6863\u3002 \u3000\u3000\u4ed6\u95ea\u8eab\u6b63\u8981\u7a7f\u51fa\uff0c\u6025\u542c\u8881\u627f\u5fd7\u5927\u53eb\uff1a\u201c\u8d70\u4e7e\u4f4d\uff0c\u8d70\u4e7e\u4f4d\uff01\u201d \u3000\u3000\u4f46\u4e7e\u4f4d\u4e0a\u660e\u660e\u6709\u6e29\u65b9\u5c71\u3001\u6e29\u65b9\u65bd\u4e8c\u4eba\u6321\u7740\uff0c\u9ec4\u771f\u77e5\u9053\u673a\u4e0d\u53ef\u5931\uff0c\u4e0d\u53ca\u7ec6\u60f3\uff0c\u731b\u5411\u4e8c\u4eba\u51b2\u53bb\uff0c\u521a\u62a2\u8fd1\u8eab\uff0c\u4e24\u4eba\u5df2\u5206\u5f00\u4ece\u4e24\u4fa7\u5305\u6284\uff0c\u800c\u586b\u8865\u7a7a\u6863\u7684\u6e29\u65b9\u8fbe\u548c\u6e29\u65b9\u609f\u8fd8\u6ca1\u8865\u4e0a\uff0c\u9ec4\u771f\u8eab\u624b\u5feb\u6781\uff0c\u94dc\u7b14\u53f3\u70b9\uff0c\u94c1\u7b97\u76d8\u5de6\u7838\uff0c\u5df2\u7136\u76f4\u7a9c\u51fa\u6765\uff0c\u7ad9\u5728\u8881\u627f\u5fd7\u8eab\u65c1\u3002 \u3000\u3000\u6e29\u6c0f\u4e94\u8001\u89c1\u4ed6\u8131\u51fa\u4e86\u4e94\u884c\u9635\uff0c\u8fd9\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\uff0c\u4e0d\u7981\u9a87\u7136\uff0c\u4e94\u4eba\u540c\u65f6\u9000\u5f00\uff0c\u6392\u6210\u4e00\u884c\u3002\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u4f60\u80fd\u9003\u51fa\u6211\u4eec\u7684\u4e94\u884c\u9635\uff0c\u8eab\u624b\u4e5f\u81ea\u4e0d\u51e1\u3002\u9601\u4e0b\u662f\u534e\u5c71\u6d3e\u7684\u5417\uff1f\u4e0e\u7a46\u4eba\u6e05\u8001\u524d\u8f88\u600e\u6837\u79f0\u547c\uff1f\u201d \u3000\u3000\u9ec4\u771f\u6b66\u529f\u7cbe\u7eaf\uff0c\u4e0d\u4f3c\u8881\u627f\u5fd7\u7684\u9a73\u6742\uff0c\u4e94\u8001\u53ea\u8ddf\u4ed6\u62c6\u5f97\u5341\u4f59\u62db\uff0c\u4fbf\u65e9\u8ba4\u51fa\u4e86\u4ed6\u7684\u95e8\u6d3e\u3002 \u3000\u3000\u9ec4\u771f\u8eab\u8131\u91cd\u56f4\uff0c\u767b\u65f6\u53c8\u662f\u5b09\u76ae\u7b11\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u7a46\u8001\u524d\u8f88\u662f\u6211\u6069\u5e08\u3002\u600e\u4e48\uff0c\u6211\u8fd9\u5f92\u5f1f\u4e22\u4e86\u4ed6\u8001\u4eba\u5bb6\u7684\u8138\u4e48\uff1f\u201d\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u2018\u795e\u5251\u4ed9\u733f\u2019\u53ca\u95e8\u5f1f\u5b50\uff0c\u81ea\u7136\u9ad8\u660e\u3002\u201d\u9ec4\u771f\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff01\u4e0d\u6015\u4e0d\u8bc6\u8d27\uff0c\u53ea\u6015\u8d27\u6bd4\u8d27\u3002\u54b1\u4eec\u8d27\u6bd4\u8d27\u6bd4\u8fc7\u4e86\u3002\u59d3\u9ec4\u7684\u5c0f\u8001\u677f\u6ca1\u80fd\u6253\u5012\u6e29\u5bb6\u4e94\u4f4d\u5927\u8001\u677f\uff0c\u5404\u4f4d\u4e5f\u6ca1\u80fd\u6293\u4f4f\u533a\u533a\u5728\u4e0b\u3002\u7b97\u662f\u516c\u5e73\u4ea4\u6613\uff0c\u534a\u65a4\u516b\u4e24\u3002\u8fd9\u6279\u91d1\u5b50\u600e\u4e48\u529e\uff1f\u201d\u8f6c\u5934\u5bf9\u8363\u5f69\u9053\uff1a\u201c\u638c\u67dc\u7684\uff0c\u4f60\u7684\u751f\u610f\u662f\u8680\u5b9a\u5566\uff0c\u8fd9\u6279\u91d1\u5b50\uff0c\u6ca1\u4f60\u8001\u4eba\u5bb6\u7684\u4efd\u513f\u3002\u201d \u3000\u3000\u8363\u5f69\u81ea\u77e5\u529f\u592b\u4e0e\u4eba\u5bb6\u5dee\u5f97\u592a\u8fdc\uff0c\u53ef\u662f\u773c\u7741\u7741\u7684\u77a7\u7740\u6ee1\u5730\u9ec4\u91d1\uff0c\u5b9e\u5728\u5fc3\u75bc\uff0c\u53ea\u5f97\u8bf4\u51e0\u53e5\u95e8\u9762\u8bdd\u906e\u7f9e\uff1a\u201c\u59d3\u9ec4\u7684\u4f60\u522b\u5f20\u72c2\uff0c\u603b\u6709\u4e00\u5929\u6570\u4f60\u843d\u5728\u6211\u624b\u91cc\u3002\u201d\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u5b9d\u53f7\u6709\u751a\u4e48\u751f\u610f\uff0c\u5c3d\u7ba1\u4f5c\u6210\u5c0f\u53f7\uff0c\u5403\u4e8f\u4fbf\u5b9c\u65e0\u6240\u8c13\uff0c\u5927\u5bb6\u8001\u5bbe\u4e1c\uff0c\u4ef7\u94b1\u53ef\u4ee5\u7279\u522b\u5546\u91cf\u3002\u201d \u3000\u3000\u8363\u5f69\u660e\u77e5\u6597\u4ed6\u4e0d\u8fc7\uff0c\u90a3\u59d3\u8881\u7684\u53c8\u8ddf\u4ed6\u662f\u5e08\u5144\u5f1f\uff0c\u5415\u5148\u751f\u5c1a\u4e14\u94e9\u7fbd\u800c\u53bb\uff0c\u4f55\u51b5\u81ea\u5df1\uff1f\u5f53\u4e0b\u5e26\u4e86\u5f92\u5f1f\u5e2e\u4f17\uff0c\u6c14\u6124\u6124\u7684\u8d70\u4e86\u3002\u4e34\u51fa\u95e8\u53e3\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u5411\u6ee1\u5730\u9ec4\u91d1\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u7a81\u7136\u5927\u6094\uff1a\u201c\u521a\u624d\u4ed6\u4eec\u516d\u4eba\u6076\u6597\u4e4b\u65f6\uff0c\u6211\u600e\u5730\u6ca1\u5077\u5077\u5728\u5730\u4e0a\u6361\u4e0a\u4e00\u4e24\u6761\uff0c\u8c05\u6765\u4e5f\u4e0d\u4f1a\u7ed9\u4eba\u53d1\u89c1\u3002\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u4e5f\u4e0d\u53bb\u7406\u4f1a\u9f99\u6e38\u5e2e\u4eba\u4f17\u7684\u6765\u53bb\uff0c\u5bf9\u9ec4\u771f\u9053\uff1a\u201c\u9601\u4e0b\u8fd9\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u7b97\u662f\u5f53\u4e16\u8c6a\u6770\u3002\u8fd9\u6837\u5427\uff0c\u8fd9\u6279\u91d1\u5b50\u77a7\u5728\u4f60\u8001\u54e5\u8138\u4e0a\uff0c\u6211\u4eec\u5949\u8fd8\u4e00\u534a\u3002\u201d\u4ed6\u9707\u4e8e\u534e\u5c71\u6d3e\u7684\u5a01\u540d\uff0c\u4e0d\u613f\u591a\u7ed3\u51a4\u5bb6\uff0c\u9887\u60f3\u5584\u7f62\u3002 \u3000\u3000\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u8fd9\u6279\u91d1\u5b50\u5018\u4f7f\u662f\u5144\u5f1f\u81ea\u5df1\u7684\uff0c\u867d\u7136\u73b0\u4eca\u4e16\u754c\u4e0d\u592a\u5e73\uff0c\u8d5a\u94b1\u4e0d\u5927\u5bb9\u6613\uff0c\u4e0d\u8fc7\u670b\u53cb\u4eec\u5f53\u771f\u8981\u4f7f\uff0c\u62ff\u53bb\u4e5f\u6ca1\u6709\u5173\u7cfb\u3002\u987b\u77e5\u80dc\u8d25\u4e43\u5175\u5bb6\u5e38\u4e8b\uff0c\u8d5a\u8680\u4e43\u5546\u5bb6\u5e38\u4e8b\u3002\u548c\u6c14\u751f\u8d22\uff0c\u751f\u610f\u4e0d\u6210\u4ec1\u4e49\u5728\u3002\u53ef\u662f\u8001\u5144\u4f60\u8981\u660e\u767d\uff0c\u8fd9\u662f\u95ef\u738b\u7684\u519b\u9977\u5440\u3002\u6211\u8fd9\u4e2a\u4e0d\u6210\u6750\u7684\u5f92\u513f\u8d1f\u8d23\u8fd0\u9001\uff0c\u7ed9\u8001\u5144\u7684\u624b\u4e0b\u6361\u4e86\u4e00\u534a\u53bb\uff0c\u6211\u600e\u4e48\u4ea4\u4ee3\u5440\uff1f\u201d \u3000\u3000\u6e29\u65b9\u4e49\u9053\uff1a\u201c\u8981\u5168\u90e8\u4ea4\u8fd8\uff0c\u4e5f\u4e0d\u662f\u4e0d\u53ef\u4ee5\uff0c\u4f46\u987b\u5f97\u4f9d\u6211\u4eec\u4e24\u4ef6\u4e8b\u3002\u201d\u9ec4\u771f\u9053\uff1a\u201c\u6709\u4ef7\u94b1\u5f00\u51fa\u76d8\u6765\uff0c\u5c31\u597d\u5546\u91cf\u3002\u4f60\u4e0d\u59a8\u6f2b\u5929\u8ba8\u4ef7\uff0c\u6211\u53ef\u4ee5\u7740\u5730\u8fd8\u94b1\u3002\u8bf7\u4f60\u5f00\u51fa\u4ef7\u94b1\u6765\uff0c\u54b1\u4eec\u6162\u6162\u6765\u8ba8\u4ef7\u8fd8\u4ef7\u3002\u201d\u6e29\u65b9\u4e49\u9053\uff1a\u201c\u8fd9\u6ca1\u6709\u4ef7\u94b1\u597d\u8bb2\u3002\u7b2c\u4e00\uff0c\u4f60\u987b\u5f97\u62ff\u793c\u7269\u6765\u6362\u91d1\u5b50\uff0c\u793c\u7269\u591a\u5c11\u4e0d\u8bba\u3002\u8fd9\u662f\u6211\u4eec\u7684\u89c4\u77e9\uff0c\u5230\u4e86\u624b\u7684\u8d22\u7269\uff0c\u51b3\u4e0d\u80fd\u8f7b\u6613\u9000\u8fd8\u3002\u201d \u3000\u3000\u9ec4\u771f\u77e5\u9053\u8fd9\u53e5\u8bdd\u4e0d\u8fc7\u662f\u4e3a\u4e86\u9762\u5b50\uff0c\u770b\u6765\u5bf9\u65b9\u5df2\u80af\u4ea4\u8fd8\u91d1\u5b50\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u4e5f\u4e0d\u5fc5\u591a\u7ed3\u51a4\u5bb6\uff0c\u5f53\u4e0b\u6536\u8d77\u5b09\u76ae\u7b11\u8138\uff0c\u6b63\u8272\u9053\uff1a\u201c\u6e29\u7237\u5429\u5490\uff0c\u5144\u5f1f\u65e0\u6709\u4e0d\u9075\u3002\u660e\u513f\u4e00\u65e9\uff0c\u5144\u5f1f\u81ea\u53bb\u8862\u5dde\u57ce\u91cc\uff0c\u91c7\u529e\u4e00\u4efd\u91cd\u793c\u9001\u4e0a\uff0c\u518d\u9884\u5907\u7b75\u5e2d\uff0c\u9080\u8bf7\u672c\u5730\u6709\u9762\u5b50\u7684\u670b\u53cb\u4f5c\u966a\uff0c\u5411\u5404\u4f4d\u9053\u8c22\u3002\u201d \u3000\u3000\u6e29\u65b9\u4e49\u542c\u4ed6\u8bf4\u8bdd\u5728\u7406\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u7f62\u4e86\u3002\u7b2c\u4e8c\u4ef6\u4e8b\uff0c\u8fd9\u59d3\u8881\u7684\u5c0f\u5b50\u53ef\u5f97\u7ed9\u6211\u4eec\u7559\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u771f\u4e00\u6123\uff0c\u5fc3\u60f3\u4f60\u4eec\u65e2\u80af\u5f52\u8fd8\u91d1\u5b50\uff0c\u6211\u4e5f\u7ed9\u4e86\u4f60\u4eec\u5f88\u5927\u9762\u5b50\uff0c\u53c8\u4f55\u5fc5\u65c1\u751f\u679d\u8282\uff1f\u6709\u6211\u5728\u6b64\uff0c\u8fd9\u4e2a\u5e08\u5f1f\u5c82\u5bb9\u4f60\u4eec\u6b3a\u4fae\uff1f \u3000\u3000\u4ed6\u53ef\u4e0d\u77e5\u8881\u627f\u5fd7\u548c\u4ed6\u4eec\u4e4b\u95f4\u7684\u7275\u6d89\u751a\u591a\u3002\u4ed6\u65e2\u5f97\u6089\u91d1\u86c7\u90ce\u541b\u4e0e\u6e29\u4eea\u4e4b\u95f4\u7684\u9690\u4e8b\uff0c\u4e94\u8001\u5df2\u662f\u5fc5\u6b32\u6740\u4e4b\u800c\u540e\u7518\u5fc3\uff0c\u800c\u5c24\u5176\u8981\u7d27\u7684\uff0c\u662f\u8981\u7740\u843d\u5728\u4ed6\u8eab\u4e0a\uff0c\u627e\u5230\u91d1\u86c7\u90ce\u541b\u90a3\u5f20\u5b9d\u85cf\u5730\u56fe\u3002\u4e94\u8001\u867d\u77e5\u4ed6\u6b66\u529f\u6781\u5f3a\uff0c\u4f46\u81ea\u4fe1\u4e94\u884c\u9635\u5965\u5999\u65e0\u7a77\uff0c\u5b9a\u53ef\u5236\u4ed6\u5f97\u4f4f\u3002 \u3000\u3000\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u5e08\u5f1f\u996d\u91cf\u5f88\u5927\u3002\u4f60\u4eec\u8981\u7559\u4ed6\uff0c\u672c\u662f\u4e00\u4ef6\u597d\u4e8b\uff0c\u53ea\u662f\u4e00\u5e74\u534a\u8f7d\u5403\u4e0b\u6765\uff0c\u5c31\u6015\u5404\u4f4d\u4e8f\u8680\u4e0d\u8d77\u3002\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u521a\u624d\u6307\u70b9\u4f60\u8d70\u51fa\u9635\u52bf\uff0c\u5b9a\u662f\u660e\u767d\u5176\u4e2d\u5173\u8bc0\u3002\u90a3\u5c31\u8bf7\u4ed6\u6765\u8bd5\u8bd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u539f\u6765\u6e29\u6c0f\u4e94\u884c\u9635\u5171\u6709\u4e94\u5957\u9635\u6cd5\uff0c\u9002\u624d\u5bf9\u4ed8\u9ec4\u771f\uff0c\u53ea\u7528\u4e86\u4e59\u6728\u9635\u6cd5\uff0c\u8fd8\u6709\u751a\u591a\u5947\u5999\u7684\u62db\u672f\u53d8\u5316\u672a\u7528\u3002\u6e29\u65b9\u8fbe\u5fc3\u60f3\u9002\u624d\u4f60\u5df2\u5de6\u652f\u53f3\u7ecc\uff0c\u867d\u7136\u4fa5\u5e78\u8131\u51fa\u5305\u56f4\uff0c\u5374\u672a\u635f\u5f97\u9635\u52bf\u5206\u6beb\uff0c\u4f60\u8fd9\u5e08\u5f1f\u65c1\u89c2\u8005\u6e05\uff0c\u624d\u77a7\u51fa\u4e86\u4e00\u4e9b\u7aef\u502a\uff0c\u5f53\u771f\u81ea\u8eab\u9677\u9635\uff0c\u4e5f\u4e0d\u514d\u5f53\u5c40\u8005\u8ff7\u4e86\uff0c\u662f\u4ee5\u4ed6\u6709\u6043\u65e0\u6050\uff0c\u5411\u8881\u627f\u5fd7\u53eb\u9635\u3002 \u3000\u3000\u9ec4\u771f\u9886\u7565\u8fc7\u8fd9\u9635\u6cd5\u7684\u6ecb\u5473\uff0c\u5fc3\u60f3\u51ed\u6211\u6570\u5341\u5e74\u529f\u529b\uff0c\u5c1a\u4e14\u95ef\u4e0d\u51fa\u6765\uff0c\u5e08\u5f1f\u867d\u7136\u51fa\u8a00\u70b9\u62e8\u4e86\u51e0\u4e0b\uff0c\u4f46\u663e\u662f\u5728\u65c1\u9759\u5fc3\u7ec6\u89c2\uff0c\u5ffd\u6709\u6240\u89c1\uff0c\u771f\u8981\u8fc7\u624b\uff0c\u4e94\u654c\u6b64\u53bb\u5f7c\u6765\uff0c\u8fde\u7ef5\u4e0d\u65ad\uff0c\u4ed6\u5982\u4f55\u5bf9\u4ed8\u5f97\u4e86\uff1f\u4fbf\u9053\uff1a\u201c\u4f60\u4eec\u9635\u6cd5\u5f88\u5389\u5bb3\uff0c\u5728\u4e0b\u5df2\u9886\u6559\u8fc7\u4e86\u3002\u6211\u8fd9\u4e2a\u5c0f\u5e08\u5f1f\u8fd8\u6ca1\u6709\u4f60\u4eec\u5b59\u5b50\u7684\u5e74\u7eaa\u5927\uff0c\u8001\u5934\u5b50\u4f55\u5fc5\u8ddf\u4ed6\u4e3a\u96be\uff1f\u8981\u662f\u771f\u7684\u77a7\u7740\u4ed6\u4e0d\u987a\u773c\uff0c\u4f60\u4eec\u968f\u4fbf\u54ea\u4e00\u4f4d\u51fa\u6765\u6559\u8bad\u6559\u8bad\u4ed6\u5c31\u662f\u5566\u3002\u201d\u8fd9\u8bdd\u4f3c\u4e4e\u793a\u5f31\uff0c\u5176\u5b9e\u5374\u662f\u6324\u5151\u4e94\u8001\uff0c\u8981\u4ed6\u4eec\u5355\u6253\u72ec\u6597\uff0c\u60f3\u6765\u4ee5\u5e08\u5f1f\u70b9\u5012\u5415\u4e03\u5148\u751f\u7684\u8eab\u624b\uff0c\u4e00\u5bf9\u4e00\u7684\u52a8\u624b\uff0c\u8fd8\u4e0d\u81f4\u8f93\u4e86\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u51b7\u7b11\u9053\uff1a\u201c\u534e\u5c71\u6d3e\u540d\u6c14\u4e0d\u5c0f\uff0c\u53ef\u662f\u89c1\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u4e94\u884c\u9635\uff0c\u7acb\u523b\u5413\u5f97\u85cf\u5934\u7f29\u5c3e\uff0c\u4ece\u4eca\u800c\u540e\uff0c\u8fd8\u662f\u522b\u5728\u6c5f\u6e56\u4e0a\u5145\u5b57\u53f7\u4e86\u5427\uff01\u201d \u3000\u3000\u5d14\u5e0c\u654f\u5927\u6012\uff0c\u4ece\u9ec4\u771f\u8eab\u540e\u62a2\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u4eec\u534e\u5c71\u6d3e\u6015\u4e86\u4f60\uff1f\u201d\u6e29\u65b9\u5c71\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u534e\u5c71\u6d3e\u7684\u5417\uff01\u563f\u563f\uff0c\u5389\u5bb3\uff0c\u5389\u5bb3\uff01\u90a3\u4e48\u4f60\u6765\u5427\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u53ea\u9053\u4ed6\u8bf4\u81ea\u5df1\u5389\u5bb3\uff0c\u7eb5\u51fa\u53bb\u5c31\u8981\u52a8\u624b\u3002\u8881\u627f\u5fd7\u4e00\u628a\u62c9\u4f4f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5d14\u5927\u54e5\uff0c\u6211\u5148\u4e0a\uff0c\u6211\u4e0d\u6210\u7684\u65f6\u5019\uff0c\u4f60\u518d\u6765\u5e2e\u624b\u3002\u201d\u5d14\u5e0c\u654f\u70b9\u5934\u9053\uff1a\u201c\u597d\uff01\u4f60\u8981\u6211\u5e2e\u5fd9\u65f6\uff0c\u53eb\u4e00\u58f0\u2018\u5e0c\u654f\u2019\uff0c\u6211\u5c31\u4e0a\u6765\uff0c\u7528\u4e0d\u7740\u751a\u4e48\u5d14\u5927\u54e5\u3001\u5d14\u4e8c\u54e5\u7684\u5ba2\u6c14\u3002\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\u3002\u5c0f\u6167\u5728\u65c1\u7a81\u7136\u5657\u54e7\u4e00\u7b11\u3002\u5d14\u5e0c\u654f\u53cc\u773c\u4e00\u77aa\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7b11\u751a\u4e48\uff1f\u201d\u5c0f\u6167\u7b11\u9053\uff1a\u201c\u6ca1\u751a\u4e48\uff0c\u6211\u81ea\u5df1\u89c9\u5f97\u597d\u7b11\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u8fd8\u5f85\u518d\u95ee\uff0c\u8881\u627f\u5fd7\u5df2\u8fc8\u6b65\u5411\u524d\uff0c\u624b\u62c8\u7389\u7c2a\uff0c\u8bf4\u9053\uff1a\u201c\u77f3\u6881\u6d3e\u4e94\u884c\u9635\u5982\u6b64\u5389\u5bb3\uff0c\u665a\u8f88\u786e\u662f\u751f\u5e73\u4ece\u6240\u672a\u89c1\u3002\u201d \u3000\u3000\u6e29\u65b9\u4e49\u9053\uff1a\u201c\u4f60\u4e73\u81ed\u672a\u5e72\uff0c\u8c05\u6765\u4e5f\u6ca1\u89c1\u8bc6\u8fc7\u751a\u4e48\u4e1c\u897f\uff0c\u522b\u8bf4\u6211\u4eec\u7684\u4e94\u884c\u9635\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\uff0c\u665a\u8f88\u89c1\u8bc6\u6d45\u964b\uff0c\u8001\u7237\u5b50\u4eec\u8981\u628a\u6211\u7559\u4e0b\uff0c\u665a\u8f88\u6c42\u4e4b\u4e0d\u5f97\uff0c\u6b63\u53ef\u4e58\u6b64\u673a\u4f1a\uff0c\u5411\u8001\u7237\u5b50\u4eec\u8ba8\u6559\u4e00\u4e0b\u4e94\u884c\u9635\u7684\u79d8\u5965\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u6025\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u4ed6\u4eec\u54ea\u662f\u597d\u5fc3\u7559\u4f60\uff1f\u4f60\u522b\u4e0a\u5f53\u3002\u201d \u3000\u3000\u5c0f\u6167\u53c8\u662f\u5657\u54e7\u4e00\u7b11\u3002\u8881\u627f\u5fd7\u5411\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u4ed6\u4eec\u8001\u4eba\u5bb6\u4e0d\u4f1a\u6b3a\u4fae\u54b1\u4eec\u5e74\u8f7b\u4eba\uff0c\u5d14\u5927\u54e5\u653e\u5fc3\u597d\u5566\u3002\u201d\u8f6c\u5934\u5bf9\u4e94\u8001\u9053\uff1a\u201c\u665a\u8f88\u5b66\u827a\u672a\u7cbe\uff0c\u534e\u5c71\u6d3e\u7684\u6b66\u529f\u53ea\u662f\u7c97\u77e5\u76ae\u6bdb\uff0c\u8bf7\u8001\u7237\u5b50\u4eec\u624b\u4e0b\u5bb9\u60c5\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u8a00\u8bed\u8f6f\u5f31\uff0c\u5927\u6709\u602f\u610f\uff0c\u4f46\u795e\u8272\u95f4\u5374\u662f\u6ee1\u4e0d\u5728\u4e4e\uff0c\u90fd\u4e0d\u77e5\u4ed6\u6253\u5f97\u662f\u751a\u4e48\u4e3b\u610f\u3002\u9ec4\u771f\u6697\u81ea\u7740\u6025\uff0c\u5374\u53c8\u4e0d\u4fbf\u963b\u62e6\u5e08\u5f1f\uff0c\u5fc3\u4e2d\u53ea\u8bf4\uff1a\u201c\u5509\uff0c\u8fd9\u7b14\u751f\u610f\u505a\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u6e29\u6c0f\u4e94\u8001\u8bd5\u8fc7\u4ed6\u7684\u529f\u529b\uff0c\u4e0d\u6562\u8f7b\u5ffd\uff0c\u4e94\u4eba\u4e00\u6253\u624b\u52bf\uff0c\u6e29\u65b9\u4e49\u3001\u6e29\u65b9\u5c71\u5411\u53f3\u8de8\u6b65\uff0c\u6e29\u65b9\u65bd\u3001\u6e29\u65b9\u609f\u5411\u5de6\u8f6c\u8eab\uff0c\u9635\u52bf\u5e03\u5f00\uff0c\u9877\u523b\u95f4\u5df2\u5c06\u4ed6\u56f4\u5728\u5793\u5fc3\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4f3c\u4e4e\u832b\u7136\u4e0d\u89c9\uff0c\u62b1\u62f3\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5c31\u7ec3\u5417\uff1f\u201d\u6e29\u65b9\u8fbe\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4f60\u4eae\u5175\u5668\u5427\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5e73\u4f38\u53f3\u638c\uff0c\u5c06\u7389\u7c2a\u6258\u5728\u638c\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u662f\u957f\u8f88\uff0c\u665a\u8f88\u54ea\u6562\u65e0\u793c\u52a8\u5200\u52a8\u67aa\uff1f\u4fbf\u7528\u8fd9\u7389\u7c2a\u5411\u8001\u7237\u5b50\u4eec\u9886\u6559\u51e0\u62db\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u53c8\u5404\u4e00\u60ca\uff0c\u90fd\u89c9\u5f97\u8fd9\u4eba\u5b9e\u5728\u72c2\u5984\u5927\u80c6\uff0c\u8fd9\u7389\u7c2a\u53ea\u6015\u4e00\u53ea\u7532\u866b\u4e5f\u672a\u5fc5\u523a\u5f97\u6b7b\uff0c\u4e00\u78b0\u4fbf\u65ad\u3002\u600e\u80fd\u7ecf\u5f97\u8d77\u4e94\u8001\u624b\u4e2d\u94a2\u6756\u3001\u5200\u5251\u7b49\u7269\u7838\u649e\uff1f\u5982\u6b64\u80e1\u95f9\uff0c\u5c82\u4e0d\u662f\u81ea\u901f\u5176\u6b7b\uff1f\u9752\u9752\u5fc3\u4e2d\u5fe7\u6025\uff0c\u53ea\u662f\u6697\u53eb\uff1a\u2018\u90a3\u600e\u2026\u2026\u600e\u751f\u662f\u597d\uff1f\u201d \u3000\u3000\u9ec4\u771f\u77e5\u9053\u8fd9\u65f6\u5df2\u96be\u4e8e\u529d\u963b\uff0c\u5fc3\u60f3\u8fd9\u5c0f\u5e08\u5f1f\u5b9a\u662f\u7ed9\u5e08\u7236\u5ba0\u60ef\u4e86\uff0c\u521d\u6d89\u6c5f\u6e56\uff0c\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u53ea\u5f97\u7d27\u7d27\u6293\u4f4f\u94dc\u7b14\u94c1\u7b97\u76d8\uff0c\u4e00\u5f85\u4ed6\u9047\u9669\uff0c\u7acb\u5373\u7a9c\u5165\u76f8\u6551\uff0c\u4f4e\u58f0\u5631\u5490\u5d14\u5e0c\u654f\u548c\u5c0f\u6167\uff1a\u201c\u654c\u4eba\u592a\u5f3a\uff0c\u54b1\u4eec\u5be1\u4e0d\u654c\u4f17\uff0c\u975e\u8680\u672c\u4e0d\u53ef\u3002\u5f85\u4f1a\u6211\u559d\u4ee4\u4f60\u4eec\u8d70\uff0c\u4f60\u4e8c\u4eba\u7acb\u5373\u4e0a\u5c4b\u5411\u5916\u51b2\u51fa\u3002\u6211\u548c\u8881\u5e08\u5f1f\u65ad\u540e\uff0c\u4e0d\u8bba\u5982\u4f55\u51f6\u9669\uff0c\u4f60\u4eec\u5343\u4e07\u4e0d\u53ef\u56de\u5934\u5e2e\u624b\u3002\u201d\u5d14\u5e0c\u654f\u548c\u5c0f\u6167\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u9ec4\u771f\u601d\u5fd6\u81ea\u5df1\u548c\u8881\u627f\u5fd7\u8981\u8bbe\u6cd5\u8131\u8eab\uff0c\u603b\u8fd8\u4e0d\u662f\u96be\u4e8b\uff0c\u53ea\u8981\u5d14\u5b89\u4e24\u4eba\u4e0d\u6210\u4e3a\u7d2f\u8d58\uff0c\u90a3\u5c31\u597d\u529e\u5f97\u591a\u3002\u4eca\u65e5\u843d\u8352\u800c\u9003\uff0c\u6682\u5fcd\u4e00\u65f6\u4e4b\u8fb1\uff0c\u4ed6\u65e5\u7ea6\u9f50\u534e\u5c71\u6d3e\u4e94\u4f4d\u9ad8\u624b\uff0c\u540c\u65f6\u653b\u6253\u4e94\u884c\u9635\uff0c\u5b9a\u53ef\u7834\u4e86\u3002\u90a3\u65f6\u624d\u6559\u8fd9\u4e94\u4e2a\u8001\u5934\u513f\u77e5\u9053\u534e\u5c71\u6d3e\u662f\u5426\u6d6a\u5f97\u865a\u540d\u3002\u4ed6\u5fc3\u4e2d\u9884\u8ba1\u7684\u4e94\u4eba\uff0c\u9664\u81ea\u5df1\u5916\uff0c\u662f\u4e8c\u5e08\u5f1f\u5f52\u8f9b\u6811\u592b\u5987\u3001\u81ea\u5df1\u7684\u5927\u5f1f\u5b50\u201c\u516b\u9762\u5a01\u98ce\u201d\u51af\u96be\u654c\uff0c\u518d\u52a0\u4e0a\u5e08\u7236\u7a46\u4eba\u6e05\u4eb2\u81ea\u4e3b\u6301\uff0c\u53ea\u987b\u5c06\u6e29\u6c0f\u4e94\u8001\u5206\u522b\u7f20\u4f4f\uff0c\u4ee4\u4e94\u4eba\u5404\u81ea\u4e3a\u654c\uff0c\u4e0d\u80fd\u5206\u8fdb\u5408\u51fb\uff0c\u4e94\u884c\u9635\u7acb\u5373\u7834\u53bb\uff0c\u8bba\u5230\u5355\u6253\u72ec\u6597\uff0c\u6e29\u6c0f\u4e94\u8001\u53ef\u4e0d\u662f\u81ea\u5df1\u5bf9\u624b\u3002\u9ec4\u771f\u9762\u5b50\u4e0a\u5b09\u76ae\u7b11\u8138\uff0c\u5185\u91cc\u5374\u662f\u6df1\u8c0b\u8fdc\u8651\uff0c\u672a\u601d\u80dc\uff0c\u5148\u8651\u8d25\uff0c\u5b9a\u4e0b\u4e86\u773c\u524d\u8131\u8eab\u4e4b\u7b56\uff0c\u53c8\u7b79\u5212\u597d\u4e86\u65e5\u540e\u53d6\u80dc\u4e4b\u9053\u3002\u4ed6\u7834\u4e94\u884c\u9635\u7684\u4eba\u9009\u4e2d\uff0c\u8fd8\u4e0d\u628a\u8881\u627f\u5fd7\u8ba1\u7b97\u5728\u5185\uff0c\u6599\u60f3\u5c0f\u5e08\u5f1f\u529f\u529b\u5c1a\u6d45\uff0c\u8fdc\u4e0d\u53ca\u81ea\u5df1\u7684\u5f97\u610f\u95e8\u5f92\u51af\u96be\u654c\u3002 \u3000\u3000\u53ea\u542c\u5f97\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8001\u7237\u5b50\u4eec\u65e2\u7136\u8bda\u5fc3\u8d50\u6559\uff0c\u600e\u4e48\u53c8\u7559\u4e00\u624b\uff0c\u4f7f\u665a\u8f88\u5b66\u4e0d\u5230\u5168\u5957\uff1f\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u4e00\u6014\u9053\uff1a\u201c\u751a\u4e48\u5168\u5957\u4e0d\u5168\u5957\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5404\u4f4d\u9664\u4e86\u4e94\u884c\u9635\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u8f85\u4f50\u7684\u516b\u5366\u9635\uff0c\u4f55\u4e0d\u4e00\u8d77\u6446\u4e86\u51fa\u6765\uff0c\u8ba9\u665a\u8f88\u5f00\u5f00\u773c\u754c\uff1f\u201d\u6e29\u65b9\u4e49\u559d\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff0c\u53ef\u6559\u4f60\u6b7b\u800c\u65e0\u6028\u3002\u201d\u8f6c\u5934\u5bf9\u6e29\u5357\u626c\u9053\uff1a\u201c\u4f60\u4eec\u6765\u5427\uff01\u201d \u3000\u3000\u6e29\u5357\u626c\u624b\u4e00\u6325\uff0c\u5e26\u540c\u5341\u4e94\u4eba\u4e00\u9f50\u7eb5\u51fa\u3002\u6e29\u5357\u626c\u4e00\u58f0\u5406\u559d\uff0c\u5341\u516d\u4eba\u4fbf\u53d1\u8db3\u7ed5\u7740\u4e94\u8001\u5954\u8dd1\uff0c\u5de6\u65cb\u53f3\u8f6c\uff0c\u7a7f\u68ad\u6765\u53bb\u3002\u8fd9\u5341\u516d\u4eba\u6709\u7684\u662f\u6e29\u5bb6\u5b50\u4f84\uff0c\u6709\u7684\u662f\u4e94\u8001\u7684\u5916\u59d3\u5f92\u5f1f\u3002\u90fd\u662f\u77f3\u6881\u6d3e\u4e8c\u4ee3\u7684\u597d\u624b\uff0c\u7279\u5730\u6311\u9009\u51fa\u6765\u7ec3\u719f\u4e86\u8fd9\u516b\u5366\u9635\u7684\u3002 \u3000\u3000\u9ec4\u771f\u89c1\u4e86\u8fd9\u822c\u60c5\u52bf\uff0c\u9976\u662f\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e5f\u4e0d\u7981\u9a87\u7136\uff0c\u5fc3\u9053\uff1a\u201c\u8881\u5e08\u5f1f\u5b9e\u5728\u5c11\u4e0d\u66f4\u4e8b\uff0c\u7ed9\u81ea\u5df1\u591a\u6dfb\u96be\u9898\u3002\u5355\u548c\u4e94\u8001\u76f8\u6597\uff0c\u5f53\u771f\u9047\u9669\u4e4b\u65f6\uff0c\u6211\u8fd8\u53ef\u51b2\u5165\u76f8\u6551\uff0c\u73b0\u4e0b\u5916\u56f4\u53c8\u6709\u5341\u516d\u4eba\u6321\u4f4f\uff0c\u6240\u6709\u7a7a\u9699\u5168\u88ab\u586b\u5f97\u5bc6\u5bc6\u5b9e\u5b9e\uff0c\u53ea\u6015\u96c0\u9e1f\u4e5f\u98de\u4e0d\u8fdb\u53bb\u4e86\u3002\u81ea\u5df1\u660e\u660e\u672c\u94b1\u77ed\u7f3a\uff0c\u600e\u5730\u751f\u610f\u5374\u8d8a\u505a\u8d8a\u5927\uff1f\u5934\u5bf8\u8f6c\u4e0d\u8fc7\u6765\uff0c\u5c82\u4e0d\u7cdf\u7cd5\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u53f3\u624b\u5927\u62c7\u6307\u4e0e\u4e2d\u6307\u62c8\u4e86\u7389\u7c2a\uff0c\u5de6\u624b\u8f7b\u626c\uff0c\u53f3\u8db3\u7f29\u8d77\uff0c\u4ee5\u5de6\u8db3\u4e3a\u8f74\uff0c\u8eab\u5b50\u7a81\u7136\u8f6c\u4e86\u56db\u4e94\u4e2a\u5708\u5b50\u3002\u4ed6\u8eab\u5f62\u4e00\u52a8\uff0c\u6e29\u6c0f\u4e94\u8001\u7acb\u5373\u63a8\u52a8\u9635\u52bf\uff0c\u51dd\u76ee\u6ce8\u89c6\u4ed6\u7684\u52a8\u9759\u3002\u4f46\u8881\u627f\u5fd7\u53ea\u662f\u5982\u4e00\u4e2a\u9640\u87ba\u822c\u5728\u539f\u5730\u6ef4\u6e9c\u6e9c\u7684\u65cb\u8f6c\uff0c\u5e76\u4e0d\u79fb\u6b65\u51fa\u624b\u3002 \u3000\u3000\u539f\u6765\u91d1\u86c7\u90ce\u541b\u5f53\u65e5\u4e0e\u4e94\u8001\u4ea4\u624b\uff0c\u4e2d\u6bd2\u88ab\u64d2\uff0c\u5f97\u4eba\u76f8\u6551\u8131\u9669\u4e4b\u540e\uff0c\u8eb2\u5728\u534e\u5c71\u7edd\u9876\u53cd\u590d\u601d\u91cf\u6614\u65e5\u6076\u6597\u7684\u60c5\u5883\uff0c\u81ea\u5fd6\u5176\u65f6\u7eb5\u4f7f\u4e0d\u670d\u201c\u9189\u4ed9\u871c\u201d\uff0c\u7b4b\u9aa8\u5b8c\u597d\uff0c\u5185\u529b\u65e0\u6ede\uff0c\u7ec8\u7a76\u4e5f\u653b\u4e0d\u7834\u4e94\u884c\u9635\uff0c\u53ea\u4e0d\u8fc7\u591a\u652f\u6491\u5f97\u4e00\u65f6\u4e09\u523b\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u5c06\u4e94\u8001\u7684\u8eab\u6cd5\u62db\u672f\u9010\u4e00\u63a8\u7a76\uff0c\u7ec8\u4e8e\u53d1\u89c1\u8fd9\u9635\u6cd5\u7684\u5173\u7a8d\uff0c\u5728\u4e8e\u654c\u4eba\u5165\u56f4\u4e4b\u540e\uff0c\u4e0d\u8bba\u5982\u4f55\u786c\u95ef\u5de7\u95ea\uff0c\u4e94\u8001\u5fc5\u80fd\u4ee5\u5389\u5bb3\u62db\u672f\u53cd\u51fb\uff0c\u4e00\u4eba\u51fa\u624b\uff0c\u5176\u4ed6\u56db\u4eba\u7acb\u5373\u7ef5\u7ef5\u800c\u4e0a\uff0c\u4e0d\u5230\u654c\u4eba\u6216\u6b7b\u6216\u64d2\uff0c\u6c38\u65e0\u4f11\u6b62\u3002\u4e94\u8001\u62db\u6570\u4e92\u4e3a\u5b88\u5fa1\uff0c\u6b65\u6cd5\u76f8\u8865\u7a7a\u9699\u3002\u4e34\u654c\u4e4b\u9645\uff0c\u4e94\u4eba\u72b9\u4f3c\u4e00\u4eba\u3002\u91d1\u86c7\u90ce\u541b\u4e8e\u4e94\u8001\u5f53\u65e5\u6240\u4f7f\u7684\u62db\u672f\uff0c\u5fc3\u4e2d\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8d8a\u60f3\u8d8a\u89c9\u8fd9\u9635\u52bf\u5b9e\u662f\u4e0d\u53ef\u6467\u7834\uff0c\u7a77\u5e74\u7d2f\u6708\u7684\u82e6\u601d\u7126\u8651\uff0c\u5404\u79cd\u5404\u6837\u53e4\u602a\u7684\u65b9\u6cd5\u7b56\u7565\u90fd\u60f3\u5230\u4e86\uff0c\u4f46\u63a8\u7a76\u5230\u7ec8\u6781\uff0c\u603b\u89c9\u96be\u4ee5\u6536\u6548\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u4e5f\u66fe\u60f3\u5230\u6697\u6740\u4e0b\u6bd2\uff0c\u53ea\u987b\u5bb3\u6b7b\u4e94\u8001\u4e2d\u7684\u4e00\u4eba\uff0c\u4e94\u884c\u9635\u4fbf\u4e0d\u6210\u5176\u4e3a\u4e94\u884c\u9635\u4e86\u3002\u4f46\u4ed6\u5fc3\u9ad8\u6c14\u50b2\uff0c\u81ea\u4e0d\u5c51\u884c\u6b64\u65e0\u8d56\u4e0b\u7b56\u3002\u4f55\u51b5\u4ed6\u7b4b\u8109\u5df2\u65ad\uff0c\u6b66\u529f\u5168\u5931\uff0c\u7eb5\u4f7f\u60f3\u51fa\u7834\u9635\u4e4b\u6cd5\uff0c\u6b64\u9635\u4e5f\u4e0d\u80fd\u6bc1\u4e8e\u81ea\u5df1\u4eb2\u624b\u3002\u65e2\u8bf4\u662f\u7834\u9635\uff0c\u5c31\u987b\u5802\u5802\u6b63\u6b63\uff0c\u4ee5\u771f\u5b9e\u672c\u9886\u5c06\u5176\u653b\u7834\u3002 \u3000\u3000\u4e00\u65e5\u65e9\u6668\uff0c\u4ed6\u5728\u5c71\u95f4\u95f2\u6b65\uff0c\u5ffd\u89c1\u4e00\u6761\u5c0f\u9752\u86c7\u5728\u8349\u4e1b\u6e38\u8d70\uff0c\u542c\u5f97\u4eba\u58f0\uff0c\u7acb\u5373\u8737\u76d8\u6210\u5708\uff0c\u6602\u8d77\u4e86\u5934\uff0c\u7565\u4e0d\u52a8\u5f39\u3002 \u3000\u3000\u4ed6\u6240\u4ee5\u5f97\u4e86\u91d1\u86c7\u90ce\u541b\u8fd9\u5916\u53f7\uff0c\u56fa\u56e0\u4ed6\u884c\u4e8b\u6ed1\u6e9c\uff0c\u72e0\u6bd2\u51f6\u9669\uff0c\u5374\u4e5f\u56e0\u4ed6\u7231\u517b\u6bd2\u86c7\uff0c\u6324\u53d6\u6bd2\u6db2\u6765\u8c03\u5236\u6697\u5668\u836f\u7bad\u3002\u5f53\u5e74\u6e29\u6c0f\u5144\u5f1f\u4e2d\u6e29\u65b9\u7984\u7684\u59bb\u5b50\u4e2d\u4ed6\u836f\u7bad\u7acb\u65f6\u6bd9\u547d\uff0c\u7bad\u5934\u4e0a\u6240\u5582\u7684\u4fbf\u662f\u86c7\u6bd2\u3002\u4ed6\u719f\u77e5\u86c7\u6027\uff0c\u77e5\u9053\u6253\u5708\u6602\u9996\uff0c\u4fbf\u662f\u7b49\u654c\u4eba\u5148\u884c\u52a8\u624b\u8fdb\u653b\uff0c\u7136\u540e\u8d81\u865a\u800c\u5165\uff0c\u4ece\u654c\u4eba\u7834\u7efd\u4e2d\u53cd\u51fb\uff0c\u654c\u4eba\u82e5\u662f\u4e0d\u52a8\uff0c\u86c7\u7c7b\u6781\u5c11\u5148\u653b\u3002\u86c7\u8eab\u8737\u76d8\u6210\u56e2\uff0c\u7cfb\u9690\u85cf\u5df1\u8eab\u6240\u6709\u5f31\u5904\uff0c\u6602\u9996\u84c4\u52bf\uff0c\u7cfb\u4ee5\u5df1\u8eab\u6700\u5f3a\u7684\u6bd2\u7259\u4f3a\u673a\u51fa\u51fb\u3002\u5982\u679c\u8d38\u7136\u7a9c\u51fa\u566c\u654c\uff0c\u86c7\u8eab\u6781\u957f\uff0c\u5f31\u70b9\u751a\u591a\uff0c\u4e0d\u514d\u4e3a\u654c\u6240\u4e58\u3002\u6b64\u4e43\u86c7\u7c7b\u81ea\u4fdd\u7684\u5929\u6027\u3002\u8fd9\u4e9b\u884c\u52a8\uff0c\u91d1\u86c7\u90ce\u541b\u5f80\u6614\u4e5f\u4e0d\u77e5\u89c1\u8fc7\u51e0\u767e\u6b21\u4e86\uff0c\u4ece\u6765\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4f46\u6b64\u523b\u4ed6\u6b63\u6f5c\u5fc3\u601d\u7d22\u653b\u7834\u4e94\u884c\u9635\u7684\u8bc0\u7a8d\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u8111\u6d77\u4e2d\u7075\u5149\u4e00\u95ea\uff0c\u767b\u65f6\u559c\u5f97\u5927\u53eb\u5927\u8df3\uff0c\u7834\u4e94\u884c\u9635\u7684\u7b56\u7565\u5c31\u6b64\u5236\u5b9a\uff0c\u90a3\u5c31\u662f\uff1a\u201c\u540e\u53d1\u5236\u4eba\u201d\u56db\u5b57\u3002 \u3000\u3000\u6b66\u5b66\u4e2d\u672c\u6765\u8bb2\u7a76\u7684\u662f\u5236\u654c\u673a\u5148\uff0c\u8fd9\u201c\u540e\u53d1\u5236\u4eba\u201d\u5374\u662f\u5168\u7136\u53cd\u5176\u9053\u800c\u884c\u3002\u6839\u672c\u65b9\u7565\u4e00\u5b9a\uff0c\u5176\u4f59\u624b\u6bb5\u8fce\u5203\u800c\u89e3\uff0c\u4e0d\u5230\u4e00\u4e2a\u6708\u529f\u592b\uff0c\u5df2\u5c06\u6467\u7834\u4e94\u884c\u9635\u7684\u65b9\u6cd5\u5168\u90e8\u60f3\u5b9a\uff0c\u8be6\u8be6\u7ec6\u7ec6\u7684\u5199\u5165\u4e86\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u3002 \u3000\u3000\u4ed6\u660e\u77e5\u8fd9\u79d8\u7b08\u672a\u5fc5\u80fd\u6709\u4eba\u53d1\u73b0\uff0c\u5373\u4f7f\u6709\u4eba\u89c1\u5230\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u5728\u5343\u767e\u5e74\u540e\uff0c\u90a3\u65f6\u6e29\u6c0f\u4e94\u8001\u5c38\u9aa8\u65e9\u5df2\u5316\u4e3a\u5c18\u571f\u3002\u53ea\u662f\u4ed6\u5fc3\u4e2d\u4e00\u53e3\u6028\u6c14\u4e0d\u51fa\uff0c\u53c8\u60f3\u90a3\u4e94\u884c\u9635\u603b\u8981\u6d41\u4f20\u4e0b\u6765\uff0c\u8981\u662f\u59cb\u7ec8\u65e0\u4eba\u80fd\u7834\uff0c\u5c82\u975e\u8ba9\u77f3\u6881\u6d3e\u79f0\u9738\u4e8e\u5929\u4e0b\uff1f\u4ed6\u5c06\u6b9a\u5fc3\u7aed\u8651\u6240\u60f3\u51fa\u6765\u7684\u7834\u6cd5\u5199\u5728\u79d8\u7b08\u4e4b\u4e2d\uff0c\u56e0\u5728\u4ed6\u5185\u5fc3\uff0c\u7834\u9635\u4e4b\u6cd5\u65e2\u5df2\u60f3\u51fa\uff0c\u4e94\u884c\u9635\u4fbf\u7b97\u5df2\u7ecf\u7834\u4e86\u3002\u82e5\u771f\u80fd\u4ee5\u6b64\u6cd5\u6467\u7834\u4e94\u884c\u9635\uff0c\u81ea\u7136\u518d\u597d\u4e0d\u8fc7\uff0c\u53ef\u662f\u90a3\u6bd5\u7adf\u6e3a\u832b\u4e4b\u6781\uff0c\u4ed6\u4ece\u6765\u6ca1\u6709\u60f3\u8981\u6536\u4e00\u4e2a\u5f92\u5f1f\u6765\u4e3a\u5df1\u5b8c\u6210\u5fc3\u613f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5f53\u4e0b\u6301\u5b9a\u201c\u540e\u53d1\u5236\u4eba\u201d\u7684\u65b9\u7565\uff0c\u8f6c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u5df2\u5c06\u4e94\u884c\u9635\u4e0e\u516b\u5366\u9635\u5168\u90e8\u5e26\u52a8\u3002 \u3000\u3000\u516b\u5366\u9635\u6cd5\u867d\u4e3a\u4e94\u8001\u540e\u521b\uff0c\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u672a\u66fe\u63d0\u53ca\uff0c\u4f46\u6839\u672c\u8981\u65e8\uff0c\u4e0e\u4e94\u884c\u9635\u5168\u65e0\u4e8c\u81f4\u3002\u8881\u627f\u5fd7\u53ea\u770b\u5341\u516d\u4eba\u8f6c\u5f97\u51e0\u4e2a\u5708\u5b50\uff0c\u5df2\u662f\u4e86\u7136\u4e8e\u80f8\uff0c\u5fc3\u60f3\uff1a\u201c\u654c\u4eba\u82e5\u662f\u7834\u4e0d\u4e86\u4e94\u884c\u9635\uff0c\u4f55\u5fc5\u518d\u52a0\u4e00\u4e2a\u516b\u5366\u9635\uff1f\u82e5\u662f\u7834\u4e86\u4e94\u884c\u9635\uff0c\u516b\u5366\u9635\u5f92\u7136\u81ea\u788d\u624b\u811a\u3002\u6e29\u6c0f\u4e94\u8001\u7684\u5929\u8d44\u89c1\u8bc6\uff0c\u548c\u91d1\u86c7\u90ce\u541b\u679c\u7136\u5dee\u5f97\u751a\u8fdc\u3002\u770b\u6765\u8fd9\u4e94\u884c\u9635\u4e5f\u662f\u4e0a\u4ee3\u4f20\u4e0b\u6765\u7684\uff0c\u8c05\u4e94\u8001\u81ea\u5df1\u4e5f\u521b\u4e0d\u51fa\u6765\u3002\u4ed6\u4eec\u81ea\u884c\u589e\u6dfb\u4e00\u4e2a\u9635\u52bf\uff0c\u53cd\u6210\u7d2f\u8d58\u3002\u91d1\u86c7\u90ce\u541b\u5f53\u5e74\u82e5\u77e5\u6e29\u6c0f\u4e94\u8001\u65e5\u540e\u6709\u6b64\u753b\u86c7\u6dfb\u8db3\u4e4b\u4e3e\uff0c\u8bb8\u591a\u82e6\u5fc3\u7684\u7b79\u8c0b\u53cd\u53ef\u7701\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e94\u8001\u8981\u7b49\u4ed6\u51fa\u624b\uff0c\u7136\u540e\u4e58\u52bf\u6251\u4e0a\uff0c\u5374\u89c1\u4ed6\u8eab\u5b50\u8d8a\u8f6c\u8d8a\u6162\uff0c\u6b8a\u65e0\u8fdb\u653b\u4e4b\u610f\uff0c\u6700\u540e\u7adf\u5750\u4e0b\u5730\u6765\uff0c\u53cc\u624b\u653e\u5728\u819d\u4e0a\uff0c\u8138\u9732\u5fae\u7b11\u3002 \u3000\u3000\u4e94\u8001\u56fa\u662f\u5fc3\u4e0b\u9a87\u7136\uff0c\u65c1\u89c2\u5404\u4eba\u4e5f\u90fd\u5927\u60d1\u4e0d\u89e3\uff0c\u5747\u60f3\u4ed6\u5927\u654c\u5f53\u524d\uff0c\u600e\u4e48\u5982\u6b64\u987d\u76ae\u3002\u5c82\u77e5\u8fd9\u662f\u8881\u627f\u5fd7\u6162\u519b\u4e4b\u8ba1\uff0c\u4e00\u6765\u662f\u8bf1\u654c\u6765\u653b\uff0c\u4e8c\u6765\u8981\u4f7f\u4e94\u8001\u5fc3\u70e6\u610f\u4e71\uff0c\u4e0d\u80fd\u6c89\u7740\u3002 \u3000\u3000\u6e29\u65b9\u4e49\u89c1\u4ed6\u5750\u4e0b\uff0c\u679c\u7136\u5fcd\u8010\u4e0d\u4f4f\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u4fbf\u8981\u51fb\u4ed6\u540e\u5fc3\u3002\u6e29\u65b9\u609f\u5fd9\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u83ab\u4e71\u4e86\u9635\u6cd5\uff01\u201d\u6e29\u65b9\u4e49\u8fd9\u624d\u5fcd\u4f4f\u3002 \u3000\u3000\u4e94\u8001\u811a\u4e0b\u52a0\u901f\uff0c\u7ee7\u7eed\u53d8\u9635\uff0c\u53ea\u5f85\u4ed6\u51fa\u624b\uff0c\u7acb\u5373\u62e5\u4e0a\u3002\u987b\u77e5\u4e0d\u8bba\u5927\u519b\u4ea4\u950b\uff0c\u8fd8\u662f\u4e24\u4eba\u4e92\u5085\uff0c\u8fdb\u653b\u8005\u96c6\u4e2d\u5168\u529b\u653b\u51fb\u5bf9\u65b9\uff0c\u5df1\u65b9\u5fc5\u6709\u5927\u91cf\u5f31\u70b9\u4e0d\u52a0\u9632\u5fa1\uff0c\u53ea\u987b\u653b\u52bf\u51cc\u5389\uff0c\u654c\u4eba\u5fd9\u4e8e\u81ea\u5b88\uff0c\u65e0\u6687\u53cd\u51fb\uff0c\u5df1\u65b9\u7684\u5f31\u70b9\u4fbf\u4e0d\u5b88\u800c\u5b88\u3002\u4e94\u884c\u9635\u4ee5\u4e00\u4eba\u6765\u5f15\u81f4\u5bf9\u65b9\u8fdb\u653b\uff0c\u81ea\u663e\u5f31\u70b9\uff0c\u5176\u4f59\u56db\u4eba\u4fbf\u9488\u5bf9\u654c\u4eba\u8eab\u4e0a\u7684\u5f31\u70b9\u8fdb\u88ad\uff0c\u6240\u8c13\u76f8\u751f\u76f8\u514b\uff0c\u4fbf\u662f\u8fd9\u4e2a\u9053\u7406\u3002\u73b0\u4e0b\u8881\u627f\u5fd7\u5168\u4e0d\u52a8\u5f39\uff0c\u90a3\u4fbf\u662f\u5468\u8eab\u65e0\u4e00\u4e0d\u5907\uff0c\u4e94\u8001\u4e00\u65f6\u5012\u662f\u65e0\u6cd5\u53ef\u65bd\u3002 \u3000\u3000\u53c8\u8fc7\u4e00\u4f1a\uff0c\u8881\u627f\u5fd7\u5ffd\u7136\u6253\u4e2a\u5475\u6b20\uff0c\u8eba\u5367\u5728\u5730\uff0c\u53cc\u624b\u53e0\u8d77\u653e\u5728\u5934\u4e0b\u5f53\u6795\u5934\uff0c\u663e\u5f97\u5341\u5206\u4f18\u95f2\u8212\u9002\u3002\u5916\u9762\u516b\u5366\u9635\u7684\u5341\u516d\u540d\u5f1f\u5b50\u6e38\u8d70\u826f\u4e45\uff0c\u8d8a\u5954\u8d8a\u5feb\uff0c\u529f\u529b\u7a0d\u5dee\u7684\u4eba\u5df2\u989d\u89d2\u89c1\u6c57\uff0c\u5fae\u5fae\u6c14\u5598\u3002\u4e94\u8001\u4e5f\u771f\u8010\u5f97\uff0c\u4ecd\u4e0d\u51fa\u624b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4e8f\u4f60\u4eec\u8fd9\u6279\u8001\u5bb6\u4f19\u53d7\u5f97\u4e86\u8fd9\u53e3\u6c14\u3002\u201d\u5ffd\u5730\u4e00\u4e2a\u7ffb\u8eab\uff0c\u80cc\u810a\u5411\u4e0a\uff0c\u628a\u8138\u57cb\u5728\u624b\u91cc\uff0c\u547c\u547c\u6253\u8d77\u9f3e\u6765\u3002\u81ea\u6765\u6b66\u6797\u4e2d\u6253\u6597\uff0c\u5343\u53e4\u4ee5\u6765\uff0c\u4ece\u672a\u6709\u8fc7\u8fd9\u9879\u59ff\u52bf\uff0c\u540e\u5fc3\u5411\u4e0a\u800c\u5367\uff0c\u5c82\u975e\u4efb\u4eba\u5bb0\u5272\uff1f \u3000\u3000\u5d14\u5e0c\u654f\u3001\u5c0f\u6167\u3001\u9752\u9752\u3001\u6e29\u4eea\u7b49\u4eba\u53c8\u662f\u597d\u7b11\uff0c\u53c8\u662f\u4ee3\u4ed6\u62c5\u5fc3\u3002\u9ec4\u771f\u5148\u89c1\u4ed6\u5750\u4e0b\u5367\u5012\uff0c\u5df2\u609f\u51fa\u4e86\u4ed6\u5bf9\u654c\u7684\u65b9\u7565\uff0c\u4e0d\u7981\u4f69\u670d\u4ed6\u806a\u660e\u5927\u80c6\uff0c\u8fd9\u65f6\u89c1\u4ed6\u8086\u65e0\u5fcc\u60ee\u7684\u7ffb\u8eab\u800c\u5367\uff0c\u6697\u53eb\u4e0d\u5999\uff0c\u89c9\u5f97\u6b64\u4e3e\u672a\u514d\u8fc7\u4efd\uff0c\u4e94\u8001\u82e5\u5411\u4ed6\u80cc\u540e\u7a81\u88ad\uff0c\u5374\u53c8\u5982\u4f55\u95ea\u907f\uff1f\u62db\u5f95\u751f\u610f\uff0c\u53ef\u4e0d\u80fd\u7528\u82e6\u8089\u8ba1\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u773c\u89c1\u826f\u673a\uff0c\u5927\u559c\u4e4b\u4e0b\uff0c\u5de6\u624b\u5411\u53f3\u6025\u6325\uff0c\u5f80\u4e0b\u4e00\u6309\uff0c\u6e29\u65b9\u65bd\u56db\u67c4\u98de\u5200\u5feb\u5982\u95ea\u7535\uff0c\u5df2\u5411\u8881\u627f\u5fd7\u80cc\u5fc3\u63d2\u53bb\u3002\u8fd9\u4e0b\u53d1\u96be\u53c8\u5feb\u53c8\u51c6\uff0c\u65c1\u89c2\u4f17\u4eba\u60ca\u53eb\u58f0\u4e2d\uff0c\u767d\u5149\u95ea\u5904\uff0c\u56db\u628a\u660e\u6643\u6643\u7684\u98de\u5200\u4e00\u9f50\u65a9\u5728\u8881\u627f\u5fd7\u80cc\u4e0a\u3002 \u3000\u3000\u6e29\u4eea\u3001\u9752\u9752\u3001\u548c\u5c0f\u6167\u90fd\u662f\u795e\u6447\u5fc3\u60b8\uff0c\u8f6c\u5934\u63a9\u9762\u3002\u77f3\u6881\u6d3e\u4f17\u4eba\u6b22\u58f0\u96f7\u52a8\u3002\u516b\u5366\u9635\u7684\u5341\u516d\u5f1f\u5b50\u4e5f\u6709\u4e03\u516b\u4eba\u505c\u4e86\u811a\u6b65\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u8881\u627f\u5fd7\u5ffd\u5730\u8dc3\u8d77\uff0c\u80cc\u4e0a\u56db\u628a\u98de\u5200\u7acb\u65f6\u9707\u843d\u3002\u4ed6\u8eab\u52a8\u5982\u7bad\uff0c\u659c\u5c04\u800c\u51fa\uff0c\u556a\u7684\u4e00\u638c\uff0c\u6b63\u6253\u5728\u6e29\u5357\u626c\u540e\u5fc3\u3002\u6e29\u5357\u626c\u4e00\u53e3\u9c9c\u8840\u5c1a\u672a\u55b7\u51fa\u3002\u5df2\u88ab\u8881\u627f\u5fd7\u63d0\u8d77\u63b7\u8fdb\u4e94\u884c\u9635\u4e2d\u3002 \u3000\u3000\u4f17\u4eba\u8fd8\u6ca1\u770b\u6e05\u695a\u4ed6\u5982\u4f55\u7a9c\u51fa\u4e94\u884c\u9635\u6765\uff0c\u53ea\u89c1\u9635\u5916\u5341\u516d\u540d\u5f1f\u5b50\u72b9\u5982\u6e34\u9a6c\u5954\u6cc9\uff0c\u5bd2\u9e26\u8d74\u6c34\uff0c\u7eb7\u7eb7\u5411\u4e94\u884c\u9635\u4e2d\u5fc3\u6295\u53bb\u3002\u8881\u627f\u5fd7\u8fd9\u91cc\u4e00\u62f3\uff0c\u90a3\u8fb9\u4e00\u817f\uff0c\u6bcf\u4e00\u62db\u4e0b\u7684\u90fd\u662f\u91cd\u624b\uff0c\u4f17\u5f1f\u5b50\u4e0d\u662f\u7ed9\u4ed6\u5236\u4f4f\u8981\u5bb3\uff0c\u6293\u8d77\u63b7\u4e86\u8fdb\u53bb\uff0c\u5c31\u662f\u88ab\u4ed6\u7528\u638c\u529b\u6325\u8fdb\u9635\u5185\u3002 \u3000\u3000\u6e29\u6b63\u7b49\u4eba\u529f\u529b\u8f83\u6df1\uff0c\u8fd0\u62f3\u62b5\u6297\uff0c\u4e5f\u662f\u4e09\u62db\u4e24\u5f0f\uff0c\u7acb\u88ab\u6253\u5012\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u4e94\u884c\u516b\u5366\u9635\u767b\u65f6\u5927\u4e71\u3002\u9635\u4e2d\u4e0d\u89c1\u654c\u4eba\uff0c\u6765\u6765\u53bb\u53bb\u7684\u5c3d\u662f\u81ea\u5df1\u4eba\u3002\u4f17\u4eba\u4e07\u6599\u4e0d\u5230\u8881\u627f\u5fd7\u8eab\u7a7f\u6728\u6851\u6240\u8d50\u7684\u91d1\u4e1d\u80cc\u5fc3\uff0c\u98de\u5200\u4e0d\u80fd\u76f8\u4f24\uff0c\u53cd\u800c\u88ab\u4ed6\u4e58\u673a\u8fdb\u88ad\uff0c\u4e3e\u624b\u4e4b\u95f4\u5c31\u628a\u516b\u5366\u9635\u653b\u7834\u3002 \u3000\u3000\u6e29\u6c0f\u4e94\u8001\u8fde\u58f0\u602a\u53eb\uff0c\u624b\u5fd9\u811a\u4e71\u7684\u63a5\u4f4f\u98de\u8fdb\u9635\u6765\u7684\u4f17\u5f1f\u5b50\u3002 \u3000\u3000\u8881\u627f\u5fd7\u54ea\u91cc\u8fd8\u5bb9\u5f97\u4ed6\u4eec\u7f13\u624b\u91cd\u884c\u5e03\u9635\uff0c\u62a2\u4e0a\u4e24\u6b65\uff0c\u5de6\u624b\u4e09\u6307\u76f4\u6233\u6e29\u65b9\u65bd\u7684\u7a74\u9053\u3002 \u3000\u3000\u6e29\u65b9\u65bd\u89c1\u98de\u5200\u4f24\u4ed6\u4e0d\u5f97\uff0c\u672c\u5df2\u5927\u9a87\uff0c\u89c1\u4ed6\u653b\u6765\uff0c\u53c8\u662f\u56db\u67c4\u98de\u5200\u5411\u4ed6\u80f8\u524d\u63b7\u53bb\u3002\u8881\u627f\u5fd7\u4e0d\u907f\u4e0d\u8ba9\uff0c\u624b\u6307\u76f4\u5411\u4ed6\u54bd\u5589\u4e0b\u4e8c\u5bf8\u516d\u5206\u7684\u201c\u7487\u7391\u7a74\u201d\u70b9\u5230\uff0c\u98de\u5200\u4ece\u4ed6\u80f8\u524d\u9707\u843d\uff0c\u4e09\u6307\u5374\u5df2\u4f38\u5230\u6e29\u65b9\u65bd\u7a74\u9053\u4e0a\u3002\u6e29\u65b9\u5c71\u94a2\u6756\u201c\u6cfc\u98ce\u76d8\u6253\u201d\uff0c\u52bf\u631f\u52b2\u98ce\uff0c\u731b\u5411\u8881\u627f\u5fd7\u53f3\u80ef\u6253\u53bb\u3002\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u62d0\u6756\u4e0a\u4e86\u5c4b\u9876\uff0c\u53c8\u6361\u56de\u6765\u4e86\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u4e0a\u4e1d\u6beb\u4e0d\u7f13\uff0c\u987a\u624b\u4e00\u62c9\uff0c\u5c06\u4e00\u540d\u77f3\u6881\u6d3e\u5f1f\u5b50\u62d6\u8fc7\u6765\u5411\u4ed6\u6756\u5934\u6321\u53bb\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u5927\u9a87\uff0c\u8fd9\u4e00\u6756\u867d\u6ca1\u76fc\u80fd\u6253\u4e2d\u654c\u4eba\uff0c\u4f46\u4f30\u8ba1\u5f53\u65f6\u60c5\u52bf\uff0c\u4ed6\u524d\u540e\u65e0\u6cd5\u95ea\u907f\uff0c\u9664\u4e86\u4ee5\u5175\u5668\u6321\u67b6\u4e4b\u5916\uff0c\u66f4\u65e0\u522b\u6cd5\uff0c\u7136\u800c\u4ed6\u4f7f\u7684\u5374\u662f\u4e00\u679a\u8106\u7ec6\u7684\u7389\u7c2a\uff0c\u53ea\u8981\u94a2\u6756\u8f7b\u8f7b\u5728\u7389\u7c2a\u4e0a\u4e00\u64e6\uff0c\u5c31\u628a\u7c2a\u5b50\u9707\u4e3a\u7c89\u788e\u3002\u54ea\u77e5\u4ed6\u7adf\u62d6\u4e86\u4e00\u540d\u672c\u95e8\u5f1f\u5b50\u6765\u6321\uff0c\u8fd9\u4e00\u6756\u4e0a\u53bb\uff0c\u5c82\u4e0d\u5c06\u4ed6\u6253\u5f97\u7b4b\u65ad\u9aa8\u6298\uff1f\u603b\u7b97\u4ed6\u6b66\u529f\u9ad8\u5f3a\uff0c\u5e94\u53d8\u795e\u901f\uff0c\u5371\u6025\u4e2d\u731b\u7136\u8e0f\u4e0a\u4e00\u6b65\uff0c\u5de6\u624b\u5728\u6756\u5934\u529b\u6273\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u7559\u795e\uff01\u201d\u94a2\u6756\u4f59\u52bf\u6781\u5927\uff0c\u51c6\u5934\u504f\u8fc7\uff0c\u731b\u5411\u6e29\u65b9\u8fbe\u7838\u53bb\u3002\u4ed6\u77e5\u5927\u54e5\u5c3d\u53ef\u6321\u5f97\u4f4f\u8fd9\u4e00\u6756\uff0c\u679c\u7136\u6e29\u65b9\u8fbe\u53cc\u621f\u4e00\u7acb\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u706b\u661f\u56db\u6e85\uff0c\u94a2\u6756\u548c\u77ed\u621f\u5404\u81ea\u9707\u4e86\u56de\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5374\u5df2\u4e58\u673a\u5411\u6e29\u65b9\u609f\u75be\u653b\u3002\u4ed6\u5de6\u638c\u731b\u5288\uff0c\u53f3\u624b\u4e2d\u7684\u7389\u7c2a\u4e0d\u4f4f\u5411\u4ed6\u53cc\u76ee\u523a\u53bb\u3002\u6e29\u65b9\u609f\u8fde\u8fde\u5012\u9000\uff0c\u6325\u52a8\u76ae\u97ad\u60f3\u5c01\u4f4f\u95e8\u6237\uff0c\u4f46\u8881\u627f\u5fd7\u5df2\u6b3a\u5230\u8eab\u524d\u4e09\u5c3a\u4e4b\u5730\uff0c\u624b\u4e2d\u76ae\u97ad\u53ea\u5acc\u592a\u957f\uff0c\u6240\u8c13\u201c\u97ad\u957f\u83ab\u53ca\u201d\uff0c\u6b64\u65f6\u5374\u53e6\u6709\u542b\u4e49\u4e86\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u88ab\u7389\u7c2a\u8fde\u653b\u4e86\u516d\u4e03\u62db\u3002\u6e29\u65b9\u609f\u89c1\u7389\u7c2a\u95ea\u95ea\u6643\u52a8\uff0c\u4e0d\u79bb\u81ea\u5df1\u53cc\u76ee\uff0c\u8fde\u7eed\u4e24\u6b21\u90fd\u5df2\u523a\u5230\u773c\u76ae\u4e4b\u4e0a\u3002\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u6b64\u65f6\u65b9\u77e5\u7389\u7c2a\u7684\u5389\u5bb3\uff0c\u6700\u540e\u4e00\u6b21\u5b9e\u5728\u8eb2\u4e0d\u8fc7\u4e86\uff0c\u4e22\u5f00\u76ae\u97ad\uff0c\u53cc\u624b\u8499\u4f4f\u773c\u775b\uff0c\u5012\u5730\u63a5\u8fde\u6253\u4e86\u51e0\u4e2a\u6eda\uff0c\u8fd9\u624d\u907f\u5f00\uff0c\u4f46\u540e\u5fc3\u5df2\u4e2d\u4e86\u91cd\u91cd\u4e00\u811a\uff0c\u75db\u5f7b\u5fc3\u80ba\u3002\u4ed6\u5f53\u5e74\u4ee5\u4e00\u6761\u76ae\u97ad\u5728\u6e29\u5dde\u64c2\u53f0\u4e0a\u8fde\u8d25\u5341\u4e8c\u6761\u597d\u6c49\uff0c\u5a01\u98ce\u8fdc\u9707\uff0c\u6570\u5341\u5e74\u76db\u540d\u4e0d\u8870\uff0c\u54ea\u77e5\u4eca\u65e5\u5728\u8fd9\u5c11\u5e74\u4eba\u624b\u4e2d\u7684\u4e00\u679a\u78a7\u7389\u7c2a\u4e0b\u8d25\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u7ad9\u8d77\u8eab\u6765\u56fa\u662f\u7f9e\u6124\u96be\u5f53\uff0c\u65c1\u89c2\u4f17\u4eba\u4e5f\u7686\u9a87\u7136\u3002 \u3000\u3000\u9ec4\u771f\u89c1\u5c0f\u5e08\u5f1f\u5982\u6b64\u4e86\u5f97\uff0c\u51fa\u624b\u4e4b\u602a\uff0c\u4ece\u6240\u672a\u89c1\uff0c\u60ca\u559c\u4e4b\u4f59\uff0c\u5fc3\u60f3\u5c31\u662f\u5e08\u7236\u4e5f\u4e0d\u4f1a\u8fd9\u4e9b\u529f\u592b\uff0c\u201c\u4ed6\u8fd9\u5bb6\u5b9d\u53f7\u8d27\u8272\u7e41\u591a\uff0c\u4e94\u82b1\u516b\u95e8\uff0c\u770b\u6765\u4e0d\u662f\u6211\u534e\u5c71\u6d3e\u4e00\u5bb6\u8fdb\u7684\u8d27\u3002\u4ed6\u751f\u610f\u7684\u8def\u5b50\u53ef\u5e7f\u5f97\u5f88\u554a\u3002\u201d\u5d14\u5e0c\u654f\u72c2\u53eb\u559d\u91c7\u3002\u5c0f\u6167\u62bf\u7740\u5634\u513f\u5fae\u7b11\u3002\u6e29\u4eea\u4e0e\u9752\u9752\u5fc3\u4e2d\u7a83\u559c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6467\u7834\u575a\u9635\uff0c\u7cbe\u795e\u9661\u957f\uff0c\u6b64\u65f6\u80dc\u5238\u5728\u63e1\uff0c\u7740\u7740\u8fdb\u903c\uff0c\u5de6\u624b\u4f7f\u7684\u662f\u534e\u5c71\u6d3e\u7684\u4f0f\u864e\u638c\u6cd5\uff0c\u53f3\u624b\u7389\u7c2a\u4f7f\u5f97\u5374\u662f\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u7684\u91d1\u86c7\u9525\u6cd5\u3002\u8fd9\u8eab\u6cd5\u4fbf\u662f\u795e\u5251\u4ed9\u733f\u7a46\u4eba\u6e05\u4eb2\u4e34\uff0c\u91d1\u86c7\u90ce\u541b\u590f\u96ea\u5b9c\u590d\u751f\uff0c\u4e5f\u53ea\u8bc6\u5f97\u4e00\u534a\uff0c\u6e29\u6c0f\u4e94\u8001\u5982\u4f55\u61c2\u5f97\uff1f\u4ed6\u6253\u9000\u6e29\u65b9\u609f\u540e\uff0c\u8f6c\u5411\u6e29\u65b9\u4e49\u653b\u51fb\uff0c\u4e5f\u662f\u8fde\u65bd\u9669\u62db\uff0c\u903c\u5f97\u4ed6\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u89c1\u60c5\u52bf\u7d27\u6025\uff0c\u553f\u54e8\u4e00\u58f0\uff0c\u7a81\u7136\u53d1\u638c\u628a\u4e00\u540d\u5f1f\u5b50\u63a8\u4e86\u51fa\u53bb\u3002\u6e29\u65b9\u5c71\u4e5f\u624b\u811a\u9f50\u65bd\uff0c\u628a\u9635\u4e2d\u5f1f\u5b50\u6216\u63b7\u6216\u8e22\uff0c\u4e00\u4e00\u6e05\u9664\u3002\u7ec3\u6b66\u5385\u4e2d\u4eba\u6570\u4e00\u5c11\uff0c\u4e94\u884c\u9635\u53c8\u63a8\u52a8\u8d77\u6765\u3002\u4f46\u8881\u627f\u5fd7\u903c\u4f4f\u4e86\u6e29\u65b9\u4e49\u6beb\u4e0d\u653e\u677e\uff0c\u4f7f\u4e94\u4eba\u65e0\u6cd5\u8fde\u73af\u9080\u51fb\u3002\u9163\u6597\u4e2d\u6e29\u65b9\u4e49\u5de6\u80a9\u4e2d\u638c\uff0c\u6e29\u65b9\u5c71\u94a2\u6756\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u7b14\u76f4\u5411\u8881\u627f\u5fd7\u540e\u5fc3\u6363\u53bb\uff0c\u540c\u65f6\u6e29\u65b9\u8fbe\u53cc\u621f\u5411\u5de6\u653b\u5230\uff0c\u6e29\u65b9\u4e49\u5de6\u80a9\u867d\u75db\uff0c\u4ecd\u6309\u7167\u9635\u6cd5\u65bd\u4e3a\u3002\u8fd9\u65f6\u516b\u5366\u9635\u5df2\u7834\uff0c\u4e94\u884c\u9635\u4e5f\u5df2\u6253\u4e71\uff0c\u4f46\u4e94\u8001\u4ecd\u662f\u6309\u7167\u9635\u6cd5\uff0c\u5e76\u529b\u62b5\u5fa1\u3002 \u3000\u3000\u6e29\u4eea\u77a7\u7740\u8881\u627f\u5fd7\u5728\u4e94\u8001\u5305\u56f4\u4e2d\u8fdb\u9000\u8d8b\u907f\uff0c\u8eab\u5f62\u6f47\u6d12\uff0c\u6b63\u662f\u5f53\u5e74\u91d1\u86c7\u90ce\u541b\u5728\u4e94\u884c\u9635\u4e2d\u7684\u6a21\u6837\uff0c\u53c8\u770b\u4e00\u4f1a\uff0c\u53ea\u89c1\u81ea\u5df1\u671d\u601d\u591c\u60f3\u7684\u60c5\u90ce\uff0c\u767d\u8863\u98d8\u98d8\uff0c\u6b63\u5728\u9635\u4e2d\u9163\u6218\uff0c\u4e0d\u7531\u5f97\u5fc3\u795e\u6fc0\u8361\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u53eb\u9053\uff1a\u201c\u590f\u90ce\uff0c\u590f\u90ce\uff0c\u4f60\u2026\u2026\u4f60\u7ec8\u4e8e\u6765\u4e86\u3002\u201d\u8fc8\u6b65\u4fbf\u5411\u5385\u5fc3\u8d70\u53bb\u3002 \u3000\u3000\u9752\u9752\u5fd9\u62c9\u4f4f\u5979\u624b\u81c2\uff0c\u53eb\u9053\uff1a\u201c\u5988\uff0c\u4f60\u522b\u53bb\u3002\u201d\u6e29\u4eea\u773c\u775b\u4e00\u82b1\uff0c\u51dd\u795e\u770b\u6e05\u695a\u9635\u4e2d\u5c11\u5e74\u8eab\u5f62\u4eff\u4f5b\uff0c\u9762\u76ee\u5374\u975e\uff0c\u767b\u65f6\u8eab\u5b50\u4e00\u6643\uff0c\u5012\u5728\u9752\u9752\u7684\u6000\u4e2d\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u8881\u627f\u5fd7\u5ffd\u5730\u8dc3\u8d77\uff0c\u53f3\u624b\u5c06\u7389\u7c2a\u5f80\u5934\u4e0a\u4e00\u63d2\uff0c\u5de6\u624b\u5df2\u633d\u4f4f\u4e86\u5385\u9876\u7684\u6a2a\u6881\uff0c\u7ffb\u8eab\u800c\u4e0a\u3002 \u3000\u3000\u4e94\u8001\u6597\u5f97\u6b63\u7d27\uff0c\u5ffd\u7136\u4e0d\u89c1\u4e86\u654c\u4eba\uff0c\u4e00\u6014\u4e4b\u9645\uff0c\u4fbf\u89c9\u5934\u9876\u98ce\u751f\uff0c\u6570\u5341\u4ef6\u6697\u5668\u4ece\u7a7a\u4e2d\u6492\u5c06\u4e0b\u6765\uff0c\u77e5\u9053\u4e0d\u5999\uff0c\u5f85\u8981\u95ea\u907f\uff0c\u6e29\u65b9\u5c71\u4e0e\u6e29\u65b9\u65bd\u5df2\u88ab\u94b1\u9556\u5206\u522b\u6253\u4e2d\u7a74\u9053\uff0c\u8dcc\u5012\u5728\u5730\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u4fef\u8eab\u53bb\u6551\uff0c\u8881\u627f\u5fd7\u53c8\u662f\u4e00\u628a\u94dc\u94b1\u6492\u4e86\u4e0b\u6765\u3002\u6e29\u65b9\u8fbe\u53cc\u621f\u201c\u5bc6\u4e91\u6b32\u96e8\u201d\uff0c\u5728\u5934\u9876\u4e00\u9635\u76d8\u65cb\uff0c\u53ea\u542c\u53ee\u53ee\u4e4b\u58f0\u4e0d\u7edd\uff0c\u7838\u98de\u4e86\u5341\u591a\u7c92\u94dc\u94b1\u3002\u5f53\u4e0b\u821e\u52a8\u53cc\u621f\uff0c\u5316\u6210\u4e00\u56e2\u767d\u5149\u62a4\u4f4f\u9876\u95e8\uff0c\u5ffd\u7136\u95f4\u624b\u4e0a\u4e00\u9707\uff0c\u53cc\u621f\u5df2\u88ab\u751a\u4e48\u4e1c\u897f\u7f20\u4f4f\uff0c\u821e\u4e0d\u5f00\u6765\u3002\u4ed6\u5403\u4e86\u4e00\u60ca\u3002\u7528\u529b\u56de\u593a\uff0c\u54ea\u77e5\u5c31\u8fd9\u4e48\u4e0a\u593a\uff0c\u53cc\u621f\u7a81\u7136\u8131\u624b\u98de\u53bb\u3002\u4ed6\u4e0d\u6687\u7ec6\u601d\uff0c\u4e8e\u65c1\u89c2\u4f17\u4eba\u60ca\u547c\u58f0\u4e2d\u5411\u65c1\u8dc3\u5f00\u4e09\u6b65\uff0c\u4f38\u638c\u62a4\u8eab\uff0c\u53ea\u89c1\u8881\u627f\u5fd7\u5df2\u81ea\u7a7a\u8dc3\u4e0b\uff0c\u7ad9\u5728\u5385\u4fa7\uff0c\u624b\u6301\u53cc\u621f\uff0c\u6e29\u65b9\u65bd\u7684\u76ae\u97ad\u5140\u81ea\u7f20\u5728\u621f\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u559d\u9053\uff1a\u201c\u77a7\u7740\uff01\u201d\u4e24\u621f\u8131\u624b\u98de\u51fa\uff0c\u6fc0\u5c04\u800c\u524d\uff0c\u5206\u522b\u9489\u5165\u5385\u4e0a\u7684\u4e24\u6839\u7c97\u67f1\uff0c\u621f\u5203\u76f4\u900f\u67f1\u8eab\u3002\u4e24\u6839\u67f1\u5b50\u4e00\u9635\u6643\u52a8\uff0c\u5934\u9876\u5c4b\u74e6\u4e71\u54cd\u3002\u7ad9\u5728\u95e8\u53e3\u7684\u4eba\u7eb7\u7eb7\u9003\u51fa\u5385\u5916\uff0c\u53ea\u6015\u5927\u5385\u5012\u574d\u3002 \u3000\u3000\u5f53\u5e74\u7a46\u4eba\u6e05\u521d\u6388\u8881\u627f\u5fd7\u5251\u672f\u65f6\uff0c\u66fe\u98de\u5251\u63b7\u51fa\uff0c\u6ca1\u5165\u6811\u5e72\uff0c\u6728\u6851\u9053\u4eba\u8a89\u4e3a\u5929\u4e0b\u65e0\u53cc\u4e4b\u5251\u6cd5\uff0c\u8881\u627f\u5fd7\u4eca\u65e5\u663e\u8fd9\u4e00\u624b\uff0c\u4fbf\u662f\u4ece\u90a3\u4e00\u62db\u53d8\u6765\u3002\u9ec4\u771f\u89c1\u4ed6\u4ee5\u672c\u95e8\u624b\u6cd5\u63b7\u621f\u64bc\u67f1\uff0c\u5a01\u4e0d\u53ef\u5f53\uff0c\u4e0d\u7981\u5927\u53eb\uff1a\u201c\u8881\u5e08\u5f1f\uff0c\u597d\u4e00\u62db\u2018\u98de\u5929\u795e\u9f99\u2019\u5440\uff01\u201d\u8881\u627f\u5fd7\u56de\u5934\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5fd8\u4e86\u5e08\u7236\u7684\u6559\u5bfc\uff0c\u8fd8\u8bf7\u5927\u5e08\u54e5\u6307\u6559\u3002\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u56db\u987e\u832b\u7136\uff0c\u53ea\u89c1\u56db\u4e2a\u5144\u5f1f\u90fd\u5df2\u5012\u5728\u5730\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7f13\u6b65\u8d70\u5230\u9ec4\u771f\u8eab\u8fb9\uff0c\u62d4\u4e0b\u5934\u4e0a\u7389\u7c2a\uff0c\u8fd8\u7ed9\u4e86\u5c0f\u6167\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u89c1\u672c\u6d3e\u8fd9\u5ea7\u5929\u4e0b\u65e0\u654c\u7684\u4e94\u884c\u516b\u5366\u9635\uff0c\u7adf\u88ab\u8fd9\u5c0f\u5b50\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u5982\u6467\u67af\u62c9\u673d\u822c\u4e00\u756a\u626b\u8361\uff0c\u767b\u65f6\u95f9\u4e86\u4e2a\u5168\u519b\u8986\u6ca1\uff0c\u4e00\u9635\u5fc3\u9178\uff0c\u7adf\u60f3\u5728\u67f1\u5b50\u4e0a\u4e00\u5934\u78b0\u6b7b\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u6211\u5df2\u5782\u66ae\u4e4b\u5e74\uff0c\u8fd9\u4ec7\u591a\u534a\u96be\u62a5\u3002\u4f46\u53ea\u8981\u7559\u5f97\u4e00\u53e3\u6c14\u5728\uff0c\u603b\u4e0d\u80fd\u5584\u7f62\u5e72\u4f11\uff01\u201d\u53cc\u624b\u4e00\u6446\uff0c\u5bf9\u9ec4\u771f\u9053\uff1a\u201c\u91d1\u5b50\u90fd\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u62ff\u53bb\u5427\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u4e0d\u5f85\u4ed6\u518d\u8bf4\u7b2c\u4e8c\u53e5\u8bdd\uff0c\u5f53\u5373\u5c06\u5730\u4e0b\u91d1\u6761\u5c3d\u884c\u6361\u5165\u76ae\u888b\u4e4b\u4e2d\uff0c\u77f3\u6881\u6d3e\u7a7a\u6709\u6570\u5341\u4eba\u7ad9\u5728\u4e00\u65c1\uff0c\u5374\u773c\u7741\u7741\u7684\u4e0d\u6562\u963b\u62e6\u3002\u8881\u627f\u5fd7\u9002\u624d\u8fd9\u4e00\u4ed7\uff0c\u5df2\u6253\u5f97\u4ed6\u4eec\u5fc3\u60ca\u80c6\u6218\uff0c\u6597\u5fd7\u5168\u5931\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u8d70\u5230\u4e8c\u5f1f\u65b9\u4e49\u8eab\u8fb9\uff0c\u4f46\u89c1\u4ed6\u773c\u73e0\u4e71\u8f6c\uff0c\u8eab\u5b50\u4e0d\u80fd\u52a8\u5f39\uff0c\u77e5\u662f\u7ed9\u8881\u627f\u5fd7\u4ee5\u94b1\u9556\u6253\u4e2d\u8981\u7a74\uff0c\u5f53\u5373\u7ed9\u4ed6\u5728\u201c\u4e91\u53f0\u7a74\u201d\u63a8\u5bab\u8fc7\u8840\uff0c\u4f46\u63c9\u634f\u826f\u4e45\uff0c\u6e29\u65b9\u4e49\u59cb\u7ec8\u762b\u75ea\u4e0d\u52a8\u3002\u53c8\u53bb\u5bdf\u770b\u53e6\u5916\u4e09\u4e2a\u5144\u5f1f\uff0c\u4e00\u773c\u5c31\u77e5\u5404\u4eba\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u7136\u800c\u4f9d\u7167\u6240\u5b66\u7684\u89e3\u7a74\u6cd5\u6f5c\u8fd0\u5185\u529b\u65bd\u6cbb\uff0c\u5374\u5168\u65e0\u529f\u6548\uff0c\u5fc3\u77e5\u8881\u627f\u5fd7\u7684\u70b9\u7a74\u6cd5\u53e6\u6709\u602a\u5f02\u4e4b\u5904\uff0c\u53ef\u662f\u60e8\u8d25\u4e4b\u4f59\uff0c\u4ee5\u81ea\u5df1\u8eab\u5206\uff0c\u5b9e\u4e0d\u613f\u4f4e\u58f0\u4e0b\u6c14\u7684\u76f8\u6c42\uff0c\u8f6c\u5934\u77a7\u7740\u9752\u9752\uff0c\u5634\u5507\u4e00\u52aa\u3002 \u3000\u3000\u9752\u9752\u77e5\u4ed6\u8981\u81ea\u5df1\u5411\u8881\u627f\u5fd7\u6c42\u6073\uff0c\u6545\u4f5c\u4e0d\u89e3\uff0c\u95ee\u9053\uff1a\u201c\u5927\u7237\u7237\uff0c\u4f60\u53eb\u6211\u5417\uff1f\u201d\u6e29\u65b9\u4e49\u6697\u9a82\uff1a\u201c\u4f60\u8fd9\u5201\u94bb\u4e2b\u5934\uff0c\u8fd9\u65f6\u6765\u8ddf\u6211\u4e3a\u96be\uff0c\u7b49\u6b64\u4e8b\u8fc7\u4e86\uff0c\u518d\u77a7\u6211\u6765\u6574\u6cbb\u4f60\u4eec\u5a18\u513f\u4fe9\u3002\u201d\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8981\u4ed6\u7ed9\u56db\u4f4d\u7237\u7237\u89e3\u5f00\u7a74\u9053\u3002\u201d \u3000\u3000\u9752\u9752\u8d70\u5230\u8881\u627f\u5fd7\u8ddf\u524d\uff0c\u798f\u4e86\u4e00\u798f\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u6211\u5927\u7237\u7237\u8bf4\uff0c\u8bf7\u4f60\u7ed9\u6211\u56db\u4f4d\u7237\u7237\u89e3\u5f00\u7a74\u9053\u3002\u8fd9\u662f\u6211\u5927\u7237\u7237\u6c42\u4f60\u7684\uff0c\u53ef\u4e0d\u662f\u6211\u6c42\u4f60\u554a\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\u3002\u201d\u4e0a\u524d\u6b63\u8981\u4fef\u8eab\u89e3\u6cbb\uff0c\u9ec4\u771f\u5ffd\u7136\u5728\u94c1\u7b97\u76d8\u4e0a\u4e00\u62e8\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5e08\u5f1f\uff0c\u4f60\u5b9e\u5728\u4e00\u70b9\u4e5f\u4e0d\u61c2\u751f\u610f\u7ecf\u3002\u5947\u8d27\u53ef\u5c45\uff0c\u600e\u4e0d\u8d77\u4ef7\uff1f\u4f60\u5f00\u51fa\u76d8\u53bb\u3002\u4e0d\u6015\u4ef7\u94b1\u600e\u4e48\u4fcf\uff0c\u4eba\u5bb6\u603b\u662f\u8981\u5403\u7684\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u77e5\u9053\u5927\u5e08\u5144\u5bf9\u77f3\u6881\u6d3e\u5f88\u6709\u6076\u611f\uff0c\u8fd9\u65f6\u8981\u4e58\u673a\u62a5\u590d\u3002 \u3000\u3000\u4ed6\u60f3\u5e08\u7236\u5e38\u8bf4\uff1a\u201c\u5f97\u9976\u4eba\u5904\u4e14\u9976\u4eba\u201d\uff0c\u9752\u9752\u53c8\u5df2\u51fa\u8a00\u76f8\u6c42\uff0c\u91d1\u5b50\u65e2\u5df2\u53d6\u56de\uff0c\u867d\u4e0d\u613f\u518d\u7559\u96be\u6e29\u6c0f\u4e94\u8001\uff0c\u4f46\u5927\u5e08\u5144\u5728\u6b64\uff0c\u81ea\u7136\u4e00\u5207\u7531\u4ed6\u4e3b\u6301\uff0c\u4fbf\u9053\uff1a\u201c\u8bf7\u5927\u5e08\u54e5\u5429\u5490\u3002\u201d \u3000\u3000\u9ec4\u771f\u9053\uff1a\u201c\u6e29\u5bb6\u5728\u8fd9\u91cc\u6b8b\u5bb3\u4e61\u6c11\uff0c\u4ed7\u52bf\u6a2a\u884c\uff0c\u8862\u5dde\u56db\u4e61\u6028\u58f0\u8f7d\u9053\uff0c\u6211\u8fd9\u4e24\u5929\u5df2\u6253\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u6211\u8bf4\u5e08\u5f1f\u54ea\uff0c\u4f60\u7ed9\u4eba\u6cbb\u75c5\uff0c\u90a3\u662f\u8981\u843d\u672c\u94b1\u7684\uff0c\u603b\u5f97\u6536\u70b9\u513f\u8bca\u8d39\u624d\u4e0d\u8680\u672c\uff0c\u8fd9\u7b14\u94b1\u54b1\u4eec\u81ea\u5df1\u5012\u4e5f\u4e0d\u7528\u8981\u4e86\uff0c\u82e5\u662f\u53bb\u6551\u6d4e\u7ed9\u4ed6\u6e29\u5bb6\u5bb3\u82e6\u4e86\u7684\u5e84\u7a3c\u4eba\uff0c\u8fd9\u6869\u751f\u610f\u505a\u5f97\u8fc7\u5427\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u60f3\u8d77\u521d\u6765\u77f3\u6881\u4e4b\u65f6\uff0c\u89c1\u5230\u8bb8\u591a\u4e61\u6c11\u5728\u6e29\u5bb6\u5927\u5c4b\u524d\u8bc9\u6028\u8bf4\u7406\uff0c\u7ed9\u6e29\u6b63\u6253\u5f97\u843d\u82b1\u6d41\u6c34\uff0c\u53c8\u60f3\u8d77\u77f3\u6881\u9547\u4e0a\u65e0\u4e00\u4eba\u4e0d\u5bf9\u6e29\u5bb6\u5927\u5c4b\u6068\u4e4b\u5165\u9aa8\uff0c\u4fa0\u4e49\u4e4b\u5fc3\u987f\u8d77\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u91cc\u7684\u5e84\u7a3c\u6c49\u771f\u662f\u7ed9\u4ed6\u4eec\u5bb3\u82e6\u5566\u3002\u5927\u5e08\u54e5\u4f60\u8bf4\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u9ec4\u771f\u5728\u7b97\u76d8\u4e0a\u6ef4\u6ef4\u7b03\u7b03\u7684\u62e8\u4e0a\u62e8\u4e0b\uff0c\u6447\u5934\u6643\u8111\u7684\u5ff5\u7740\u73e0\u7b97\u53e3\u8bc0\uff0c\u751a\u4e48\u201c\u516d\u4e0a\u4e00\u53bb\u4e94\u8fdb\u4e00\u201d\u3001\u201c\u4e09\u4e00\u4e09\u5341\u4e00\uff0c\u4e8c\u4e00\u6dfb\u4f5c\u4e94\u201d\u8bf4\u4e2a\u4e0d\u505c\uff0c\u4e5f\u4e0d\u77e5\u7b97\u751a\u4e48\u5e10\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u548c\u5c0f\u6167\u89c1\u60ef\u9ec4\u771f\u5982\u6b64\u88c5\u6a21\u4f5c\u6837\u3002\u8881\u627f\u5fd7\u5bf9\u5927\u5e08\u5144\u5f88\u662f\u606d\u656c\uff0c\u89c1\u4ed6\u7b97\u5e10\u7b97\u5f97\u5e0c\u5947\u53e4\u602a\uff0c\u5374\u4e0d\u6562\u5b09\u7b11\u3002\u77f3\u6881\u6d3e\u4f17\u4eba\u6ee1\u8154\u6c14\u6124\uff0c\u54ea\u91cc\u8fd8\u7b11\u5f97\u51fa\uff1f\u53ea\u6709\u9752\u9752\u5374\u55e4\u7684\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ec4\u771f\u6447\u5934\u6643\u8111\u7684\u9053\uff1a\u201c\u8881\u5e08\u5f1f\uff0c\u4f60\u7684\u8bca\u8d39\u90fd\u7ed9\u4f60\u7b97\u51fa\u6765\u5566\uff01 \u3000\u3000\u6551\u4e00\u6761\u547d\u662f\u56db\u767e\u77f3\u767d\u7c73\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u56db\u767e\u77f3\uff1f\u201d\u9ec4\u771f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u56db\u767e\u77f3\u4e0a\u7b49\u767d\u7c73\uff0c\u4e0d\u8bb8\u6400\u4e00\u7c92\u6c99\u5b50\u79d5\u8c37\uff0c\u65a4\u4e24\u5347\u6597\uff0c\u53ef\u4e0d\u80fd\u6709\u4e00\u70b9\u513f\u6363\u9b3c\u3002\u201d\u4e5f\u4e0d\u95ee\u6e29\u65b9\u8fbe\u662f\u5426\u7b54\u5141\uff0c\u5df2\u8bf4\u8d77\u767d\u7c73\u7684\u7ec6\u8282\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u91cc\u56db\u4f4d\u8001\u7237\u5b50\uff0c\u90a3\u4e48\u4e00\u5171\u662f\u4e00\u5343\u516d\u767e\u77f3\u4e86\uff1f\u201d\u9ec4\u771f\u5927\u62c7\u6307\u4e00\u7ad6\uff0c\u8d5e\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u7684\u5fc3\u7b97\u771f\u884c\uff0c\u4e0d\u7528\u7b97\u76d8\uff0c\u5c31\u7b97\u51fa\u4e00\u4e2a\u4eba\u56db\u767e\u77f3\uff0c\u56db\u4e2a\u4eba\u5c31\u662f\u4e00\u5343\u516d\u767e\u77f3\u3002\u201d\u5d14\u5e0c\u654f\u60f3\uff1a\u201c\u90a3\u6709\u751a\u4e48\u5e0c\u5947\uff1f\u6211\u4e0d\u7528\u7b97\u76d8\u4e5f\u7b97\u5f97\u51fa\u3002\u201d \u3000\u3000\u9ec4\u771f\u5bf9\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u660e\u513f\u4e00\u65e9\uff0c\u4f60\u5907\u9f50\u4e00\u5343\u516d\u767e\u77f3\u767d\u7c73\uff0c\u5206\u7ed9\u56db\u4e61\u8d2b\u6c11\uff0c\u6bcf\u4eba\u4e00\u6597\u3002\u4f60\u53d1\u6ee1\u4e86\u4e00\u5343\u516d\u767e\u77f3\uff0c\u6211\u5e08\u5f1f\u5c31\u7ed9\u4f60\u6551\u6cbb\u8fd9\u56db\u4f4d\u4ee4\u5f1f\u3002\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u5fcd\u6c14\u9053\uff1a\u201c\u4e00\u65f6\u4e09\u523b\u4e4b\u95f4\uff0c\u6211\u54ea\u91cc\u6765\u8fd9\u8bb8\u591a\u767d\u7c73\uff1f \u3000\u3000\u6211\u5bb6\u91cc\u642c\u7a7a\u4e86\u7c73\u4ed3\uff0c\u53ea\u6015\u4e5f\u4e0d\u8fc7\u4e03\u516b\u5341\u77f3\u7f62\u4e86\u3002\u201d\u9ec4\u771f\u9053\uff1a\u201c\u8bca\u91d1\u5b9a\u4ef7\u5212\u4e00\uff0c\u6298\u6263\u662f\u4e0d\u80fd\u6253\u7684\u3002\u4e0d\u8fc7\u770b\u5728\u8001\u670b\u53cb\u4efd\u4e0a\uff0c\u5206\u671f\u53d1\u7c73\uff0c\u5012\u4e5f\u4e0d\u59a8\u901a\u878d\u3002\u4f60\u53d1\u6ee1\u56db\u767e\u77f3\uff0c\u5c31\u7ed9\u4f60\u6551\u4e00\u4e2a\u4eba\u3002\u7b49\u4f60\u53d1\u6ee1\u516b\u767e\u77f3\uff0c\u518d\u7ed9\u4f60\u6551\u7b2c\u4e8c\u4e2a\u3002\u8981\u662f\u4f60\u624b\u5934\u4e0d\u4fbf\uff0c\u90a3\u4e48\u9694\u8fd9\u4e48\u5341\u5929\u534a\u6708\u3001\u4e00\u5e74\u534a\u8f7d\u4e4b\u540e\u518d\u53d1\u7c73\uff0c\u6211\u5e08\u5f1f\u968f\u8bf7\u968f\u5230\uff0c\u5c31\u7b97\u662f\u5728\u8fbd\u4e1c\u3001\u4e91\u5357\uff0c\u4e5f\u4f1a\u8d76\u6765\u6551\u4eba\uff0c\u51b3\u4e0d\u4f1a\u6709\u4e00\u70b9\u513f\u62d6\u5ef6\u63a8\u642a\u3002\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u5fc3\u60f3\uff1a\u201c\u56db\u4e2a\u5144\u5f1f\u7ed9\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u6700\u591a\u8fc7\u5f97\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u7a74\u9053\u81ea\u89e3\uff0c\u53ea\u4e0d\u8fc7\u635f\u8017\u4e9b\u5185\u529b\u800c\u5df2\uff0c\u4e0d\u5fc5\u53d7\u4ed6\u5982\u6b64\u6572\u8bc8\u52d2\u7d22\u3002\u201d\u9ec4\u771f\u5df2\u731c\u4e2d\u4e86\u4ed6\u5fc3\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u5462\uff0c\u4f60\u6211\u90fd\u662f\u884c\u5bb6\uff0c\u77e5\u9053\u8fc7\u5f97\u51e0\u4e2a\u65f6\u8fb0\uff0c\u7a74\u9053\u81ea\u89e3\uff0c\u8fd9\u4e00\u5343\u516d\u767e\u77f3\u767d\u7c73\uff0c\u5927\u53ef\u7701\u4e4b\u3002\u53ea\u4e0d\u8fc7\u6211\u4eec\u534e\u5c71\u6d3e\u7684\u70b9\u7a74\u529f\u592b\u6709\u70b9\u513f\u9738\u9053\uff0c\u82e5\u4e0d\u4ee5\u672c\u95e8\u529f\u592b\u89e3\u6551\uff0c\u7ed9\u70b9\u4e86\u7a74\u9053\u4e4b\u4eba\u65e5\u540e\u672a\u514d\u624b\u811a\u4e0d\u5927\u7075\u4fbf\uff0c\u81f3\u4e8e\u5934\u660f\u773c\u82b1\uff0c\u5927\u4fbf\u4e0d\u901a\uff0c\u5c0f\u4fbf\u95ed\u585e\uff0c\u4e5f\u662f\u5728\u6240\u96be\u514d\uff0c\u5185\u529b\u5927\u635f\uff0c\u66f4\u662f\u4e0d\u5728\u8bdd\u4e0b\u3002\u597d\u5728\u56db\u4f4d\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u518d\u7ec3\u4ed6\u4e94\u516d\u5341\u5e74\uff0c\u4e5f\u5c31\u6062\u590d\u539f\u72b6\u4e86\u3002\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u77e5\u9053\u6b64\u8a00\u975e\u865a\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u660e\u5929\u6211\u53d1\u7c73\u5c31\u662f\u3002\u201d\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u5927\u8001\u677f\u505a\u751f\u610f\u771f\u662f\u723d\u5feb\u4e0d\u8fc7\uff0c\u4e00\u70b9\u4e5f\u4e0d\u8ba8\u4ef7\u8fd8\u4ef7\u3002\u4e0b\u6b21\u518d\u6709\u751f\u610f\uff0c\u8981\u8bf7\u4f60\u65f6\u65f6\u5149\u987e\u3002\u201d\u6e29\u65b9\u8fbe\u53d7\u4ed6\u595a\u843d\u4e86\u534a\u5929\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u62c2\u62bd\u5165\u5185\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5411\u6e29\u4eea\u548c\u9752\u9752\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u5929\u89c1\u3002\u201d\u4ed6\u77e5\u77f3\u6881\u6d3e\u73b0\u4e0b\u6709\u6c42\u4e8e\u5df1\uff0c\u51b3\u4e0d\u6562\u5bf9\u5979\u4eec\u6bcd\u5973\u4e3a\u96be\u3002\u5e08\u5144\u5f1f\u7b49\u56db\u4eba\u63d0\u4e86\u9ec4\u91d1\uff0c\u5174\u9ad8\u91c7\u70c8\u7684\u56de\u5230\u501f\u5bbf\u7684\u519c\u6c11\u5bb6\u91cc\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u624d\u5fae\u660e\u3002\u5c0f\u6167\u4e0b\u53a8\u5f04\u4e86\u4e9b\u9762\u6761\uff0c\u56db\u4eba\u5403\u4e86\uff0c\u8c08\u8d77\u8fd9\u573a\u5927\u80dc\uff0c\u65e0\u4e0d\u7709\u98de\u8272\u821e\u3002 \u3000\u3000\u9ec4\u771f\u4e3e\u8d77\u9762\u7897\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5e08\u5f1f\uff0c\u5f53\u65f6\u6211\u542c\u5e08\u7236\u8bf4\u6536\u4e86\u4e00\u4f4d\u5e74\u7eaa\u5f88\u8f7b\u7684\u5f92\u5f1f\uff0c\u66fe\u5bf9\u4f60\u4e8c\u5e08\u54e5\u5f52\u8f9b\u6811\u592b\u5987\u8bb2\u7b11\uff0c\u8bf4\u54b1\u4eec\u81ea\u5df1\u7684\u5f1f\u5b50\u6709\u4e9b\u5e74\u7eaa\u90fd\u5df2\u4e09\u5341\u5f00\u5916\u4e86\uff0c\u5e08\u7236\u5ffd\u7136\u7ed9\u4ed6\u4eec\u6dfb\u4e0a\u4e86\u4e00\u4f4d\u5c0f\u5e08\u53d4\uff0c\u53ea\u6015\u5927\u4f19\u513f\u6709\u70b9\u5c34\u5c2c\u5427\u3002\u54ea\u77e5\u5e08\u5f1f\u4f60\u529f\u592b\u7adf\u8fd9\u4e48\u4fca\uff0c\u522b\u8bf4\u6211\u5927\u5e08\u54e5\u8ddf\u4f60\u5dee\u5f97\u8fdc\uff0c\u4f60\u4e8c\u5e08\u54e5\u5916\u53f7\u795e\u62f3\u65e0\u654c\uff0c\u5927\u6c5f\u5357\u5317\u5c11\u6709\u654c\u624b\uff0c\u4f46\u6211\u77a7\u6765\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u80dc\u5f97\u8fc7\u4f60\u3002\u54b1\u4eec\u534e\u5c71\u6d3e\u5c06\u6765\u53d1\u626c\u5149\u5927\uff0c\u90fd\u5e94\u5728\u5e08\u5f1f\u4f60\u8eab\u4e0a\u4e86\u3002\u8fd9\u91cc\u65e0\u9152\uff0c\u6211\u656c\u4f60\u4e00\u7897\u9762\u6c64\u3002\u201d\u8bf4\u7f62\u4e3e\u8d77\u7897\u6765\uff0c\u5c06\u9762\u6c64\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fd9\u7ad9\u8d77\u8eab\u6765\uff0c\u7aef\u6c64\u559d\u4e86\u4e00\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4eca\u65e5\u4fa5\u5e78\u53d6\u80dc\uff0c\u5927\u5e08\u54e5\u7684\u79f0\u8d5e\u5b9e\u5728\u6127\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u8fd9\u4efd\u8c26\u900a\u8c28\u614e\uff0c\u6b66\u6797\u4e2d\u5c31\u6781\u4e3a\u96be\u5f97\uff0c\u5feb\u5750\u4e0b\u5403\u9762\u3002\u201d\u4ed6\u5403\u4e86\u51e0\u7b77\uff0c\u8f6c\u5934\u5bf9\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u4f60\u53ea\u8981\u5b66\u5230\u8881\u5e08\u53d4\u529f\u592b\u7684\u4e00\u6210\uff0c\u5c31\u591f\u4f60\u53d7\u7528\u4e00\u4e16\u4e86\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u5728\u6e29\u5bb6\u773c\u89c1\u8881\u627f\u5fd7\u5927\u5c55\u795e\u5a01\uff0c\u4e3e\u624b\u4e4b\u95f4\u7834\u4e86\u90a3\u5389\u5bb3\u5f02\u5e38\u7684\u4e94\u884c\u9635\uff0c\u5fc3\u91cc\u4f69\u670d\u4e4b\u6781\uff0c\u542c\u5e08\u7236\u8fd9\u4e48\u8bf4\uff0c\u7a81\u7136\u8dea\u5012\uff0c\u5411\u8881\u627f\u5fd7\u78d5\u4e86\u51e0\u4e2a\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6c42\u5c0f\u5e08\u53d4\u6559\u6211\u70b9\u672c\u4e8b\u3002\u201d\u8881\u627f\u5fd7\u5fd9\u8dea\u4e0b\u8fd8\u793c\uff0c\u8fde\u8bf4\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u6211\u5927\u5e08\u54e5\u7684\u529f\u592b\uff0c\u6bd4\u6211\u7cbe\u7eaf\u5341\u500d\u3002\u201d \u3000\u3000\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u6211\u529f\u592b\u4e0d\u53ca\u4f60\uff0c\u53ef\u662f\u8981\u6559\u8fd9\u5bb6\u4f19\uff0c\u5374\u4e5f\u7ef0\u7ef0\u6709\u4f59\uff0c\u53ea\u662f\u6211\u5b9e\u5728\u6ca1\u6709\u8010\u5fc3\u3002\u5e08\u5f1f\u82e5\u80af\u6210\u5168\u8fd9\u5c0f\u5b50\uff0c\u505a\u5e08\u54e5\u7684\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u539f\u6765\u9ec4\u771f\u56e0\u5374\u4e0d\u8fc7\u5d14\u79cb\u5c71\u7684\u60c5\u9762\uff0c\u6536\u4e86\u5d14\u5e0c\u654f\u4e3a\u5f92\u3002\u4f46\u8fd9\u5f1f\u5b50\u8d44\u8d28\u9c81\u949d\uff0c\u95fb\u5341\u800c\u4e0d\u80fd\u77e5\u4e00\uff0c\u4e0e\u9ec4\u771f\u673a\u53d8\u7075\u52a8\u7684\u6027\u683c\u6781\u4e0d\u76f8\u6295\u3002\u9ec4\u771f\u7eb5\u662f\u5728\u6388\u827a\u4e4b\u65f6\uff0c\u4e5f\u662f\u4e0d\u65ad\u7684\u63d2\u79d1\u6253\u8be8\uff0c\u80e1\u8bf4\u516b\u9053\u3002\u5f1f\u5b50\u8d8a\u8822\uff0c\u4ed6\u8ba5\u523a\u8d8a\u591a\u3002\u5d14\u5e0c\u654f\u600e\u80fd\u5206\u8fa8\u5e08\u7236\u7684\u8a00\u8bed\u54ea\u4e00\u53e5\u662f\u771f\uff0c\u54ea\u4e00\u53e5\u662f\u5047\uff1f\u9ec4\u771f\u660e\u660e\u8bf4\u7684\u662f\u8bbd\u523a\u53cd\u8bdd\uff0c\u4ed6\u8fd8\u9053\u662f\u79f0\u8d5e\u81ea\u5df1\u3002\u5982\u6b64\u5b66\u827a\uff0c\u81ea\u7136\u96be\u6709\u6210\u5c31\u3002\u540e\u6765\u8881\u627f\u5fd7\u611f\u5ff5\u4ed6\u53d4\u53d4\u5d14\u79cb\u5c71\u820d\u547d\u76f8\u6551\u4e4b\u5fb7\uff0c\u53c8\u89c1\u4ed6\u662f\u5c0f\u6167\u7684\u7231\u4fa3\uff0c\u679c\u7136\u8be6\u52a0\u6307\u70b9\u3002\u5d14\u5e0c\u654f\u867d\u56e0\u5929\u8d44\u6240\u9650\uff0c\u4e0d\u80fd\u9886\u4f1a\u5230\u591a\u5c11\uff0c\u4f46\u6bd4\u4e4b\u8fc7\u53bb\uff0c\u5374\u5df2\u5927\u6709\u8fdb\u76ca\u4e86\u3002 \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u9ec4\u771f\u548c\u8881\u627f\u5fd7\u521a\u8d77\u8eab\uff0c\u5916\u8fb9\u6709\u4eba\u53eb\u95e8\uff0c\u8fdb\u6765\u4e00\u540d\u58ee\u6c49\uff0c\u62ff\u4e86\u6e29\u65b9\u8fbe\u7684\u540d\u5e16\uff0c\u9080\u8bf7\u56db\u4eba\u524d\u53bb\u3002\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6d88\u606f\u4e5f\u771f\u7075\u901a\uff0c\u6211\u4eec\u843d\u811a\u7684\u5730\u65b9\u5c45\u7136\u6253\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u56db\u4eba\u6765\u5230\u6e29\u5bb6\uff0c\u53ea\u89c1\u4e61\u6c11\u4e91\u96c6\uff0c\u4e00\u62c5\u62c5\u767d\u7c73\u4ece\u57ce\u91cc\u6311\u6765\uff0c\u539f\u6765\u6e29\u65b9\u8fbe\u8fde\u591c\u547d\u4eba\u5230\u8862\u5dde\u57ce\u91cc\u91c7\u8d2d\uff0c\u8862\u5dde\u57ce\u662f\u6d59\u4e1c\u5927\u57ce\uff0c\u751a\u662f\u5bcc\u9976\uff0c\u4f46\u9aa4\u7136\u8981\u91c7\u8d2d\u4e00\u5343\u516d\u767e\u77f3\u7c73\uff0c\u5374\u4e5f\u4e0d\u5927\u5bb9\u6613\uff0c\u7c73\u4ef7\u9661\u8d77\uff0c\u4f7f\u6e29\u5bb6\u53c8\u591a\u82b1\u4e86\u51e0\u767e\u4e24\u94f6\u5b50\u3002\u6e29\u65b9\u8fbe\u5f53\u4e0b\u8bf7\u9ec4\u771f\u8fc7\u76ee\u70b9\u6570\uff0c\u7136\u540e\u4e00\u6597\u6597\u7684\u53d1\u7ed9\u8d2b\u6c11\u3002\u56db\u4e61\u8d2b\u6c11\u7eb7\u7eb7\u8bae\u8bba\uff0c\u90fd\u8bf4\u6e29\u5bb6\u600e\u4e48\u5ffd\u7136\u8f6c\u4e86\u6027\u3002 \u3000\u3000\u9ec4\u771f\u89c1\u6e29\u65b9\u8fbe\u8ba4\u771f\u53d1\u7c73\uff0c\u867d\u77e5\u51fa\u4e8e\u65e0\u5948\uff0c\u4f46\u4e5f\u4e0d\u518d\u52a0\u4ee5\u8ba5\u8bee\uff0c\u8bf4\u9053\uff1a\u201c\u6e29\u8001\u7237\u5b50\uff0c\u4f60\u53d1\u7c73\u6d4e\u8d2b\uff0c\u4e43\u662f\u4e3a\u5b50\u5b59\u79ef\u5fb7\u3002\u6709\u4e2a\u65b0\u7f16\u7684\u597d\u6b4c\uff0c\u5728\u4e0b\u5531\u7ed9\u4f60\u542c\u542c\u3002\u201d\u653e\u5f00\u55d3\u5b50\uff0c\u5531\u4e86\u8d77\u6765\uff1a\u201c\u5e74\u6765\u8757\u65f1\u82e6\u9891\u4ecd\uff0c\u56bc\u556e\u79be\u82d7\u5c81\u4e0d\u767b\uff0c\u7c73\u4ef7\u5347\u817e\u589e\u6570\u500d\uff0c\u9ece\u6c11\u5904\u5904\u4e0d\u804a\u751f\u3002 \u3000\u3000\u8349\u6839\u6728\u53f6\u6743\u5145\u8179\uff0c\u513f\u5973\u5471\u5471\u76f8\u5411\u54ed\uff1b \u3000\u3000\u91dc\u7511\u5c18\u98de\u7228\u7edd\u70df\uff0c\u6570\u65e5\u96be\u6c42\u4e00\u9910\u7ca5\u3002 \u3000\u3000\u5b98\u5e9c\u5f81\u7cae\u7eb5\u864e\u5dee\uff0c\u8c6a\u5bb6\u7d22\u503a\u5982\u72fc\u8c7a\u3002 \u3000\u3000\u53ef\u601c\u6b8b\u5598\u5b58\u547c\u5438\uff0c\u9b42\u9b44\u5148\u5f52\u6cc9\u58e4\u57cb\u3002 \u3000\u3000\u9ab7\u9ac5\u904d\u5730\u79ef\u5982\u5c71\uff0c\u4e1a\u91cd\u96be\u8fc7\u9965\u997f\u5173\u3002 \u3000\u3000\u80fd\u4e0d\u6559\u4eba\u6570\u884c\u6cea\uff0c\u6cea\u6d12\u8fd8\u6210\u70b9\u8840\u73ed\uff1f \u3000\u3000\u5949\u529d\u5bcc\u5bb6\u540c\u8d48\u6d4e\uff0c\u592a\u4ed3\u4e00\u7c92\u6069\u65e0\u65e2\u3002 \u3000\u3000\u67af\u9aa8\u91cd\u6559\u5f97\u518d\u751f\uff0c\u597d\u751f\u4e00\u5ff5\u611f\u5929\u5730\u3002 \u3000\u3000\u5929\u5730\u65e0\u79c1\u4f51\u5584\u4eba\uff0c\u5584\u4eba\u5fb7\u539a\u798f\u957f\u81fb\u3002 \u3000\u3000\u52a9\u8d2b\u6551\u751f\u529f\u52cb\u5927\uff0c\u5fb7\u539a\u6d41\u5149\u88d5\u5b50\u5b59\u3002\u201d \u3000\u3000\u4ed6\u55d3\u5b50\u867d\u7136\u4e0d\u4f73\uff0c\u4f46\u6b4c\u8bcd\u611f\u4eba\uff0c\u95fb\u8005\u5c3d\u7686\u52a8\u5bb9\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u8fd9\u9996\u6b4c\u513f\u4f5c\u5f97\u5f88\u597d\u554a\u3002\u201d\u9ec4\u771f\u9053\uff1a\u201c\u6211\u54ea\u6709\u8fd9\u4e48\u5927\u7684\u624d\u5b66\uff1f\u8fd9\u662f\u95ef\u738b\u624b\u4e0b\u5927\u5c06\u674e\u5ca9\u674e\u516c\u5b50\u4f5c\u7684\u6b4c\u513f\u3002\u201d\u8881\u627f\u5fd7\u70b9\u5934\u9053\uff1a\u201c\u539f\u6765\u53c8\u662f\u674e\u516c\u5b50\u7684\u5927\u4f5c\u3002\u4ed6\u5ff5\u5ff5\u4e0d\u5fd8\u9ece\u6c11\u75be\u82e6\uff0c\u90a3\u624d\u662f\u771f\u82f1\u96c4\u3001\u5927\u8c6a\u6770\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e5f\u4e0d\u5f85\u4e00\u5343\u516d\u767e\u77f3\u767d\u7c73\u53d1\u5b8c\uff0c\u4fbf\u7ed9\u6e29\u6c0f\u56db\u8001\u89e3\u5f00\u7a74\u9053\uff0c\u63a8\u5bab\u8fc7\u8840\u3002\u56db\u8001\u59d4\u987f\u4e86\u534a\u591c\uff0c\u5747\u5df2\u6709\u6c14\u65e0\u529b\uff0c\u8138\u8272\u6c14\u5f97\u94c1\u9752\u3002\u8881\u627f\u5fd7\u5411\u4e94\u8001\u4f5c\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u591a\u5f97\u7f6a\uff0c\u665a\u8f88\u4e07\u5206\u62b1\u6b49\u3002\u201d \u3000\u3000\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u9001\u4e86\u4e00\u5343\u516d\u767e\u77f3\u7c73\uff0c\u4e0d\u514d\u6709\u70b9\u8089\u75db\uff0c\u4f46\u77f3\u6881\u6e29\u5bb6\u7684\u540d\u58f0\u5374\u597d\u4e86\u4e0d\u5c11\u3002\u8fd9\u6869\u751f\u610f\u4f60\u4eec\u5176\u5b9e\u662f\u5927\u6709\u8d5a\u5934\uff0c\u4e0d\u53ef\u4e0d\u77e5\u3002\u201d\u4e94\u8001\u4e00\u8a00\u4e0d\u53d1\uff0c\u6389\u5934\u5165\u5185\u3002 \u3000\u3000\u9ec4\u771f\u89c1\u53d1\u7c73\u5df2\u6bd5\uff0c\u8d2b\u6c11\u6563\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\u987b\u5f97\u4e0e\u9752\u9752\u544a\u522b\uff0c\u53c8\u60f3\u5979\u6bcd\u5973\u548c\u6e29\u5bb6\u5df2\u7ecf\u7834\u8138\uff0c\u53ea\u6015\u6b64\u5904\u5df2\u4e0d\u80fd\u5c45\uff0c\u6b63\u5f85\u548c\u5e08\u54e5\u5546\u8bae\uff0c\u5ffd\u89c1\u9752\u9752\u62b1\u7740\u6bcd\u4eb2\uff0c\u54ed\u53eb\uff1a\u201c\u627f\u5fd7\u5927\u54e5\uff01\u201d\u5feb\u6b65\u5954\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u60ca\uff0c\u5fd9\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d\u731b\u542c\u5f97\u98d5\u98d5\u98ce\u58f0\uff0c\u77e5\u9053\u4e0d\u5999\uff0c\u5fd9\u6025\u8dc3\u800c\u524d\uff0c\u4f38\u624b\u4e00\u6284\uff0c\u6293\u4f4f\u4e86\u56db\u67c4\u5c04\u5411\u9752\u9752\u80cc\u5fc3\u7684\u98de\u5200\u3002\u53ea\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u6e29\u65b9\u65bd\u907f\u5165\u4e86\u95e8\u540e\uff0c\u8ddf\u7740\u7830\u7684\u4e00\u58f0\uff0c\u5927\u95e8\u5408\u4e0a\uff0c\u5c06\u516d\u4eba\u5173\u5728\u95e8\u5916\u3002 \u3000\u3000\u9752\u9752\u54ed\u9053\uff1a\u201c\u56db\u7237\u7237\u4e0b\u6bd2\u624b\u6740\u2026\u2026\u6740\u4e86\u6211\u5988\u3002\u201d\u8f6c\u8fc7\u624b\u4e2d\u6bcd\u4eb2\u7684\u8eab\u5b50\uff0c\u53ea\u89c1\u6e29\u4eea\u80cc\u5fc3\u4e0a\u63d2\u4e86\u4e00\u67c4\u98de\u5200\uff0c\u76f4\u6ca1\u81f3\u67c4\u3002 \u3000\u3000\u8881\u627f\u5fd7\u60ca\u6012\u4ea4\u96c6\uff0c\u4f38\u624b\u8981\u53bb\u62d4\u5200\u3002\u9ec4\u771f\u628a\u4ed6\u624b\u4e00\u6321\uff0c\u9053\uff1a\u201c\u62d4\u4e0d\u5f97\uff0c\u4e00\u62d4\u7acb\u65f6\u5c31\u6b7b\uff01\u201d\u773c\u89c1\u6e29\u4eea\u4f24\u91cd\u96be\u6551\uff0c\u4fbf\u70b9\u4e86\u5979\u4e24\u5904\u7a74\u9053\uff0c\u4f7f\u5979\u7a0d\u51cf\u75db\u695a\u3002 \u3000\u3000\u6e29\u4eea\u8138\u9732\u5fae\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9752\u513f\uff0c\u522b\u96be\u53d7\u3002\u6211\u2026\u2026\u6211\u53bb\u2026\u2026\u53bb\u89c1\u4f60\u7238\u7238\u5566\u3002\u5728\u4f60\u7238\u7238\u8eab\u8fb9\uff0c\u6ca1\u4eba\u2026\u2026\u6ca1\u4eba\u518d\u6b3a\u4fae\u6211\u3002\u201d\u9752\u9752\u54ed\u7740\u8fde\u8fde\u70b9\u5934\u3002 \u3000\u3000\u6e29\u4eea\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u53ef\u4e0d\u80fd\u7792\u6211\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f2f\u6bcd\u8981\u77e5\u9053\u751a\u4e48\u4e8b\uff1f\u665a\u8f88\u51b3\u4e0d\u9690\u7792\u3002\u201d\u6e29\u4eea\u9053\uff1a\u201c\u4ed6\u6709\u6ca1\u6709\u9057\u4e66\uff1f\u6709\u6ca1\u63d0\u5230\u6211\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u590f\u524d\u8f88\u7559\u4e0b\u4e86\u4e9b\u6b66\u529f\u56fe\u8c31\u3002\u6628\u5929\u6211\u7834\u4e94\u884c\u9635\uff0c\u5c31\u662f\u7528\u4ed6\u9057\u6cd5\uff0c\u603b\u7b97\u66ff\u4ed6\u62a5\u4e86\u5927\u4ec7\uff0c\u51fa\u4e86\u6028\u6c14\u3002\u201d\u6e29\u4eea\u9053\uff1a\u201c\u4ed6\u6ca1\u7559\u4e0b\u7ed9\u6211\u7684\u4fe1\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u4e0d\u7b54\uff0c\u53ea\u7f13\u7f13\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u6e29\u4eea\u597d\u751f\u5931\u671b\uff0c\u9053\uff1a\u201c\u4ed6\u559d\u4e86\u90a3\u7897\u83b2\u5b50\u7fb9\u624d\u6ca1\u529b\u6c14\uff0c\u8fd9\u7897\u2026\u2026\u8fd9\u7897\u83b2\u5b50\u7fb9\u662f\u6211\u7ed9\u4ed6\u559d\u7684\u3002\u53ef\u662f\u6211\u771f\u7684\u2026\u2026\u771f\u7684\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u5440\u3002\u201d\u8881\u627f\u5fd7\u5b89\u6170\u5979\u9053\uff1a\u201c\u590f\u524d\u8f88\u5728\u5929\u4e4b\u7075\uff0c\u4e00\u5b9a\u660e\u767d\uff0c\u51b3\u4e0d\u4f1a\u602a\u4f2f\u6bcd\u7684\u3002\u201d\u6e29\u4eea\u9053\uff1a\u201c\u4ed6\u5b9a\u662f\u4f24\u5fc3\u6b7b\u7684\uff0c\u602a\u6211\u6697\u4e2d\u5bb3\u4ed6\uff0c\u73b0\u4eca\u5c31\u7b97\u660e\u767d\uff0c\u53ef\u662f\u4e5f\u5df2\u8fdf\u4e86\u3002\u201d\u9752\u9752\u6ce3\u9053\uff1a\u201c\u5988\uff0c\u7239\u7239\u65e9\u77e5\u9053\u7684\u3002\u4f60\u4e5f\u559d\u4e86\u83b2\u5b50\u7fb9\uff0c\u8981\u966a\u7239\u7239\u4e00\u8d77\u6b7b\u3002\u4ed6\u5f53\u65f6\u5c31\u660e\u767d\u4e86\u3002\u201d\u6e29\u4eea\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u5f53\u771f\u660e\u767d\u5417\uff1f\u4e3a\u751a\u4e48\u4e00\u76f4\u4e0d\u6765\u63a5\u6211\uff1f\u8fde\u2026\u2026\u8fde\u9057\u4e66\u4e5f\u4e0d\u7ed9\u6211\u4e00\u5c01\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u4e34\u6b7b\u5c1a\u4e3a\u8fd9\u4e8b\u803f\u803f\u4e8e\u6000\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u60f3\u4e0d\u51fa\u751a\u4e48\u8bdd\u6765\u5b89\u6170\uff0c\u4f46\u89c1\u5979\u76ee\u5149\u6563\u4e71\uff0c\u53cc\u624b\u6162\u6162\u5782\u4e86\u4e0b\u6765\uff0c\u5ffd\u7136\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u8d77\u4e86\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u90a3\u5f20\u201c\u91cd\u5b9d\u4e4b\u56fe\u201d\uff0c\u5176\u4e2d\u63d0\u5230\u8fc7\u6e29\u4eea\u7684\u540d\u5b57\uff0c\u5fd9\u4ece\u6000\u91cc\u53d6\u51fa\u6765\uff0c\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u4f60\u8bf7\u770b\uff01\u201d \u3000\u3000\u6e29\u4eea\u53cc\u76ee\u672c\u5df2\u5408\u62e2\uff0c\u8fd9\u65f6\u53c8\u6162\u6162\u7741\u5f00\uff0c\u4e00\u89c1\u56fe\u4e0a\u5b57\u8ff9\uff0c\u7a81\u7136\u7cbe\u795e\u5927\u632f\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u7684\u5b57\uff0c\u6211\u8ba4\u5f97\u7684\u3002\u201d\u4f4e\u58f0\u5ff5\u7740\u90a3\u51e0\u884c\u5b57\u9053\uff1a\u201c\u5f97\u5b9a\u4e4b\u4eba\u2026\u2026\u52a1\u8bf7\u8d74\u6d59\u6c5f\u8862\u5dde\u77f3\u6881\u2026\u2026\u5bfb\u8bbf\u6e29\u4eea\uff0c\u2026\u2026\u5bfb\u8bbf\u6e29\u4eea\uff0c\u90a3\u5c31\u662f\u6211\u5440\u2026\u2026\u916c\u4ee5\u9ec4\u91d1\u5341\u4e07\u4e24\u3002\u201d\u53c8\u89c1\u5230\u90a3\u4e24\u884c\u5c0f\u5b57\uff1a\u201c\u6b64\u65f6\u7eb5\u805a\u5929\u4e0b\u73cd\u5b9d\uff0c\u4ea6\u7109\u5f97\u4ee5\u6613\u534a\u65e5\u805a\u9996\uff0c\u91cd\u8d22\u5b9d\u800c\u8f7b\u522b\u79bb\uff0c\u611a\u4e4b\u6781\u77e3\uff0c\u6094\u751a\u6068\u751a\u3002\u201d\u5979\u6ee1\u8138\u7b11\u5bb9\uff0c\u4f38\u624b\u62c9\u4f4f\u8881\u627f\u5fd7\u7684\u8863\u8896\uff0c\u9053\uff1a\u201c\u4ed6\u6ca1\u602a\u6211\uff0c\u4ed6\u5fc3\u91cc\u4ecd\u7136\u8bb0\u7740\u6211\uff0c\u60f3\u7740\u6211\u2026\u2026\u800c\u4eca\u6211\u662f\u8981\u53bb\u4e86\uff0c\u8981\u53bb\u89c1\u4ed6\u4e86\u2026\u2026\u201d\u8bf4\u7740\u6162\u6162\u95ed\u4e0a\u4e86\u773c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u6b64\u60c5\u666f\uff0c\u4e0d\u7981\u5782\u6cea\u3002\u6e29\u4eea\u5ffd\u7136\u53c8\u7741\u5f00\u773c\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u6211\u6c42\u4f60\u4e24\u4ef6\u4e8b\uff0c\u4f60\u4e00\u5b9a\u5f97\u7b54\u5e94\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f2f\u6bcd\u8bf7\u8bf4\uff0c\u53ea\u8981\u505a\u5f97\u5230\u7684\uff0c\u65e0\u4e0d\u5e94\u547d\u3002\u201d\u6e29\u4eea\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\uff0c\u4f60\u628a\u6211\u846c\u5728\u4ed6\u8eab\u8fb9\u3002\u7b2c\u4e8c\u4ef6\u2026\u2026\u7b2c\u4e8c\u4ef6\u2026\u2026\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u662f\u751a\u4e48\uff1f\u4f2f\u6bcd\u8bf7\u8bf4\u3002\u201d\u6e29\u4eea\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e16\u4e0a\u4eb2\u4eba\uff0c\u53ea\u6709\u2026\u2026\u53ea\u6709\u8fd9\u4e2a\u5973\u513f\uff0c\u4f60\u2026\u2026\u4f60\u4eec\u2026\u2026\u4f60\u4eec\u2026\u2026\u201d\u624b\u6307\u7740\u9752\u9752\uff0c\u5ffd\u7136\u4e00\u53e3\u6c14\u63a5\u4e0d\u4e0a\uff0c\u53cc\u773c\u4e00\u95ed\uff0c\u5782\u5934\u4e0d\u52a8\uff0c\u5df2\u505c\u4e86\u547c\u5438\u3002 \u3000\u3000\u9752\u9752\u4f0f\u5728\u6bcd\u4eb2\u8eab\u4e0a\u5927\u54ed\uff0c\u8881\u627f\u5fd7\u8f7b\u62cd\u5979\u80a9\u5934\u3002\u9ec4\u771f\u3001\u5b89\u5c0f\u6167\u3001\u548c\u5d14\u5e0c\u654f\u4e09\u4eba\u773c\u89c1\u8881\u627f\u5fd7\u5bf9\u5979\u6781\u662f\u5173\u5207\uff0c\u53c8\u89c1\u5979\u6bcd\u4eb2\u60e8\u906d\u6740\u5bb3\uff0c\u5747\u611f\u607b\u7136\uff0c\u53ea\u662f\u4e8e\u6b64\u4e2d\u5185\u60c5\u4e00\u65e0\u6240\u6089\uff0c\u4e0d\u77e5\u8bf4\u751a\u4e48\u8bdd\u6765\u5b89\u6170\u624d\u597d\u3002 \u3000\u3000\u9752\u9752\u5ffd\u5730\u653e\u4e0b\u6bcd\u4eb2\u5c38\u8eab\uff0c\u62d4\u5251\u800c\u8d77\uff0c\u5954\u5230\u5927\u95e8\u4e4b\u524d\uff0c\u4e3e\u5251\u4e71\u5241\u5927\u95e8\uff0c\u54ed\u53eb\uff1a\u201c\u4f60\u4eec\u5bb3\u6b7b\u6211\u7239\u7239\uff0c\u53c8\u5bb3\u6b7b\u6211\u5988\u5988\uff0c\u6211\u2026\u2026\u6211\u8981\u6740\u5149\u4e86\u4f60\u6e29\u5bb6\u5168\u5bb6\u3002\u201d\u7eb5\u8eab\u8dc3\u8d77\uff0c\u8df3\u4e0a\u4e86\u5899\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e5f\u8dc3\u4e0a\u5899\u5934\uff0c\u8f7b\u8f7b\u63e1\u4f4f\u5979\u5de6\u81c2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u4ed6\u4eec\u679c\u7136\u72e0\u6bd2\u3002\u4e0d\u8fc7\uff0c\u7ec8\u7a76\u662f\u4f60\u7684\u5916\u516c\u3002\u201d \u3000\u3000\u9752\u9752\u4e00\u9635\u6c14\u82e6\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u6454\u4e86\u4e0b\u6765\u3002\u8881\u627f\u5fd7\u5fd9\u4f38\u81c2\u633d\u4f4f\u5979\u8170\uff0c\u5374\u89c1\u5979\u5df2\u660f\u6655\u8fc7\u53bb\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u8fde\u53eb\uff1a\u201c\u9752\u5f1f\uff0c\u9752\u5f1f\uff01\u201d \u3000\u3000\u9ec4\u771f\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u53ea\u662f\u4f24\u5fc3\u8fc7\u5ea6\u3002\u201d\u53d6\u51fa\u4e00\u5757\u827e\u7ed2\uff0c\u7528\u706b\u6298\u70b9\u7740\u4e86\uff0c\u5728\u9752\u9752\u9f3b\u4e0b\u718f\u5f97\u7247\u523b\uff0c\u5979\u6253\u4e86\u4e00\u4e2a\u55b7\u568f\uff0c\u60a0\u60a0\u9192\u6765\uff0c\u5446\u5446\u77a7\u7740\u6bcd\u4eb2\u5c38\u8eab\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u5979\u53ea\u662f\u4e0d\u7b54\u3002\u8881\u627f\u5fd7\u5782\u6cea\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u4eec\u53bb\u5427\uff0c\u8fd9\u91cc\u4e0d\u80fd\u4f4f\u4e86\u3002\u201d\u9752\u9752\u5446\u5446\u7684\u70b9\u4e86\u70b9\u5934\u3002\u8881\u627f\u5fd7\u62b1\u8d77\u6e29\u4eea\u5c38\u8eab\uff0c\u4e94\u4eba\u4e00\u9f50\u79bb\u4e86\u6e29\u5bb6\u5927\u5c4b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d70\u51fa\u6570\u5341\u6b65\uff0c\u56de\u5934\u4e00\u671b\uff0c\u4f46\u89c1\u5c4b\u524d\u5e7f\u573a\u4e0a\u6ee1\u5730\u767d\u7c73\uff0c\u90fd\u662f\u9002\u624d\u53d1\u7c73\u65f6\u6389\u4e0b\u6765\u7684\uff0c\u6570\u5341\u5934\u9ebb\u96c0\u8df3\u8dc3\u5544\u98df\u3002\u6b64\u65f6\u7ea2\u65e5\u5f53\u7a7a\uff0c\u6d53\u836b\u531d\u5730\uff0c\u6e29\u5bb6\u5927\u5c4b\u5374\u7d27\u95ed\u4e86\u5927\u95e8\uff0c\u9759\u6084\u6084\u5730\u6ca1\u534a\u70b9\u58f0\u606f\uff0c\u5c4b\u5185\u4fbf\u5982\u7a7a\u65e0\u4e00\u4eba\u3002 \u3000\u3000\u9ec4\u771f\u5bf9\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u8fd9\u4e94\u5341\u4e24\u94f6\u5b50\uff0c\u62ff\u53bb\u7ed9\u54b1\u4eec\u501f\u5bbf\u7684\u519c\u5bb6\uff0c\u53eb\u4ed6\u4eec\u8fde\u591c\u642c\u5bb6\u3002\u201d\u5d14\u5e0c\u654f\u63a5\u4e86\uff0c\u77aa\u7740\u773c\u95ee\u5e08\u7236\u9053\uff1a\u201c\u5e72\u4e48\u8981\u8fde\u591c\u642c\u5bb6\u5440\uff1f\u201d\u9ec4\u771f\u9053\uff1a\u201c\u77f3\u6881\u6d3e\u7684\u4eba\u5bf9\u54b1\u4eec\u65e0\u53ef\u5948\u4f55\uff0c\u81ea\u7136\u4f1a\u8fc1\u6012\u4e8e\u522b\u4eba\uff0c\u5b9a\u4f1a\u53bb\u5411\u90a3\u5bb6\u519c\u5bb6\u4e3a\u96be\u3002\u4f60\u60f3\u90a3\u51e0\u4e2a\u5e84\u7a3c\u4eba\uff0c\u80fd\u7834\u5f97\u4e86\u4e94\u884c\u9635\u5417\uff1f\u201d\u5d14\u5e0c\u654f\u70b9\u5934\u9053\uff1a\u201c\u90a3\u53ef\u7834\u4e0d\u4e86\uff01\u201d\u98de\u5954\u7740\u53bb\u4e86\u3002 \u3000\u3000\u56db\u4eba\u7b49\u4ed6\u56de\u6765\uff0c\u7ed5\u5c0f\u8def\u79bb\u5f00\u77f3\u6881\u9547\uff0c\u884c\u4e86\u5341\u591a\u91cc\uff0c\u89c1\u8def\u8fb9\u6709\u5ea7\u7834\u5e99\u3002\u9ec4\u771f\u9053\uff1a\u201c\u8fdb\u53bb\u6b47\u6b47\u5427\u3002\u5e99\u7834\u83e9\u8428\u70c2\uff0c\u65c1\u4eba\u4e0d\u4f1a\u7591\u5fc3\u54b1\u4eec\u987a\u624b\u7275\u7f8a\u3001\u5077\u9e21\u6478\u72d7\u3002\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u90a3\u5f53\u7136\uff01\u201d \u3000\u3000\u8d70\u8fdb\u5e99\u4e2d\uff0c\u5728\u6bbf\u4e0a\u5750\u4e86\u3002\u9ec4\u771f\u9053\uff1a\u201c\u8fd9\u4f4d\u592a\u592a\u7684\u9057\u4f53\u600e\u4e48\u529e\uff1f\u662f\u5c31\u5730\u5b89\u846c\u5462\uff0c\u8fd8\u662f\u5230\u57ce\u91cc\u5165\u6b93\uff1f\u201d\u8881\u627f\u5fd7\u76b1\u7709\u4e0d\u8bed\u3002\u9ec4\u771f\u9053\uff1a\u201c\u5982\u5230\u57ce\u91cc\u627e\u7075\u67e9\u5165\u6b93\uff0c\u5979\u662f\u56e0\u5200\u4f24\u81f4\u6b7b\uff0c\u5b98\u5e9c\u67e5\u95ee\u8d77\u6765\uff0c\u54b1\u4eec\u867d\u7136\u4e0d\u6015\uff0c\u603b\u662f\u9ebb\u70e6\u3002\u201d\u8a00\u4e0b\u610f\u601d\u662f\u5c31\u5728\u6b64\u846c\u4e86\u3002 \u3000\u3000\u9752\u9752\u54ed\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u5988\u5988\u8bf4\u8fc7\u7684\uff0c\u5979\u8981\u548c\u7238\u7238\u846c\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u9ec4\u771f\u9053\uff1a\u201c\u4ee4\u5c0a\u9057\u4f53\u846c\u5728\u751a\u4e48\u5730\u65b9\uff1f\u201d\u9752\u9752\u8bf4\u4e0d\u4e0a\u6765\uff0c\u671b\u7740\u8881\u627f\u5fd7\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5728\u54b1\u4eec\u534e\u5c71\uff01\u201d\u56db\u4eba\u542c\u4e86\u90fd\u611f\u8be7\u5f02\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53c8\u9053\uff1a\u201c\u5979\u7236\u4eb2\u4fbf\u662f\u91d1\u86c7\u90ce\u541b\u590f\u524d\u8f88\u3002\u201d \u3000\u3000\u9ec4\u771f\u5e74\u7eaa\u4e0e\u590f\u96ea\u5b9c\u76f8\u4eff\uff0c\u4f46\u590f\u96ea\u5b9c\u5c11\u5e74\u6210\u540d\uff0c\u9ec4\u771f\u521d\u51fa\u9053\u65f6\uff0c\u91d1\u86c7\u90ce\u541b\u7684\u5a01\u540d\u65e9\u5df2\u9707\u52a8\u6b66\u6797\uff0c\u4e00\u542c\u4e4b\u4e0b\uff0c\u767b\u65f6\u8083\u7136\u52a8\u5bb9\uff0c\u5fae\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e2a\u4e3b\u610f\uff0c\u59d1\u5a18\u83ab\u602a\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u8001\u4f2f\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u9ec4\u771f\u6307\u7740\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ed6\u662f\u6211\u5e08\u5f1f\uff0c\u4f60\u53eb\u6211\u8001\u4f2f\u4e0d\u6562\u5f53\uff0c\u8fd8\u662f\u79f0\u5927\u54e5\u5427\u3002\u201d\u5d14\u5e0c\u654f\u5411\u9752\u9752\u76f4\u77aa\u773c\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u6211\u5c82\u4e0d\u662f\u53c8\u5f97\u53eb\u4f60\u8fd9\u5c0f\u599e\u513f\u4f5c\u59d1\u59d1\uff1f\u201d\u9752\u9752\u5411\u8881\u627f\u5fd7\u671b\u4e86\u4e00\u773c\uff0c\u7adf\u7136\u6539\u4e86\u79f0\u547c\uff0c\u9053\uff1a\u201c\u9ec4\u5927\u54e5\u7684\u8bdd\uff0c\u5c0f\u59b9\u81ea\u5f53\u9075\u4f9d\u3002\u201d\u5d14\u5e0c\u654f\u6697\u6697\u53eb\u82e6\uff1a\u201c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff0c\u8fd9\u5c0f\u599e\u5c45\u7136\u8001\u5b9e\u4e0d\u5ba2\u6c14\u7684\u53eb\u8d77\u9ec4\u5927\u54e5\u6765\u3002\u201d \u3000\u3000\u9ec4\u771f\u600e\u60f3\u5f97\u5230\u8fd9\u6d51\u5c0f\u5b50\u809a\u91cc\u5728\u8f6c\u8fd9\u8bb8\u591a\u5ff5\u5934\uff0c\u5bf9\u9752\u9752\u9053\uff1a\u201c\u4ee4\u5802\u9057\u5fd7\u662f\u8981\u4e0e\u4ee4\u5c0a\u5408\u846c\uff0c\u54b1\u4eec\u603b\u8981\u5b8c\u6210\u5979\u8fd9\u756a\u5fc3\u613f\u624d\u597d\u3002 \u3000\u3000\u4f46\u4e0d\u8bf4\u6b64\u5904\u5230\u534e\u5c71\u5343\u91cc\u8fe2\u8fe2\uff0c\u7075\u67e9\u96be\u8fd0\uff0c\u5c31\u7b97\u7075\u67e9\u5230\u4e86\u534e\u5c71\u811a\u4e0b\uff0c\u4e5f\u8fd0\u4e0d\u4e0a\u53bb\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u534e\u5c71\u5c71\u5cf0\u9669\u5cfb\u4e4b\u6781\uff0c\u6b66\u529f\u7a0d\u5dee\u4e00\u4e9b\u7684\u5c31\u4e0a\u4e0d\u4e86\u3002\u8fd0\u7075\u67e9\u4e0a\u53bb\u662f\u51b3\u8ba1\u4e0d\u6210\u7684\u3002\u201d\u9ec4\u771f\u9053\uff1a\u201c\u53e6\u5916\u6709\u4e2a\u6cd5\u5b50\uff0c\u662f\u5c06\u4ee4\u5c0a\u7684\u9057\u9aa8\u63a5\u4e0b\u6765\u5408\u846c\u3002\u4e0d\u8fc7\u4ee4\u5c0a\u9057\u4f53\u5df2\u7ecf\u5b89\u5c45\u5409\u7a74\uff0c\u518d\u53bb\u60ca\u52a8\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u5f88\u59a5\u5f53\u3002\u201d \u3000\u3000\u9752\u9752\u89c1\u4ed6\u8bf4\u5f97\u5728\u7406\uff0c\u5341\u5206\u7740\u6025\uff0c\u54ed\u9053\uff1a\u201c\u90a3\u600e\u4e48\u529e\u5462\uff1f\u201d\u9ec4\u771f\u9053\uff1a\u201c\u6211\u610f\u601d\u662f\u628a\u4ee4\u5802\u9057\u4f53\u5728\u8fd9\u91cc\u706b\u5316\u4e86\uff0c\u7136\u540e\u5c06\u9aa8\u7070\u9001\u4e0a\u5cf0\u53bb\u5b89\u846c\u3002\u201d\u8bf4\u5230\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u53ef\u4e00\u672c\u6b63\u7ecf\uff0c\u518d\u4e0d\u80e1\u8a00\u4e71\u8bed\u4e86\u3002 \u3000\u3000\u9752\u9752\u867d\u7136\u4e0b\u613f\uff0c\u4f46\u9664\u6b64\u4e4b\u5916\u4e5f\u65e0\u522b\u6cd5\uff0c\u53ea\u5f97\u542b\u6cea\u70b9\u5934\u3002 \u3000\u3000\u5f53\u4e0b\u4f17\u4eba\u6536\u96c6\u67f4\u8349\uff0c\u628a\u6e29\u4eea\u7684\u5c38\u4f53\u70e7\u5316\u4e86\u3002\u9752\u9752\u81ea\u5e7c\u5728\u6e29\u5bb6\u9887\u906d\u767d\u773c\uff0c\u867d\u7136\u6e29\u6b63\u7b49\u51e0\u4e2a\u8868\u5144\u89c1\u5979\u7f8e\u8c8c\uff0c\u8ba8\u597d\u4e8e\u5979\uff0c\u5374\u4e5f\u5168\u662f\u5fc3\u5b58\u6b79\u5ff5\uff0c\u53ea\u6709\u6bcd\u4eb2\u4e00\u4eba\u624d\u771f\u5fc3\u7231\u5979\uff0c\u8fd9\u65f6\u89c1\u81f3\u7231\u4e4b\u4eba\u5728\u706b\u5149\u4e2d\u6e10\u6e10\u6d88\u5931\uff0c\u4e0d\u7981\u4f0f\u5730\u5927\u54ed\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5728\u7834\u5e99\u4e2d\u627e\u4e86\u4e00\u4e2a\u74e6\u7f50\uff0c\u7b49\u706b\u7184\u5c38\u9500\uff0c\u5c06\u9aa8\u7070\u6361\u5165\u7f50\u4e2d\uff0c\u62dc\u4e86\u4e24\u62dc\uff0c\u6697\u6697\u7977\u795d\uff1a\u201c\u4f2f\u6bcd\u5728\u5929\u4e4b\u7075\u5c3d\u7ba1\u653e\u5fc3\uff0c\u5c0f\u4f84\u5b9a\u5c06\u4f2f\u6bcd\u9aa8\u7070\u9001\u5230\u534e\u5c71\u7edd\u9876\u5b89\u846c\uff0c\u51b3\u4e0d\u6562\u6709\u8d1f\u91cd\u6258\u3002\u201d \u3000\u3000\u9ec4\u771f\u89c1\u6b64\u4e8b\u5df2\u6bd5\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u4eec\u8981\u5c06\u9ec4\u91d1\u9001\u5230\u6c5f\u897f\u4e5d\u6c5f\u53bb\u3002\u95ef\u738b\u6d3e\u4e86\u8bb8\u591a\u5144\u5f1f\u5728\u6c5f\u5357\u6d59\u8d63\u4e00\u5e26\u8054\u7edc\uff0c\u4ee5\u5f85\u4e2d\u539f\u5927\u4e3e\u4e4b\u65f6\uff0c\u5357\u65b9\u4e5f\u7ad6\u4e49\u65d7\u54cd\u5e94\uff0c\u4eba\u591a\u4e8b\u7e41\uff0c\u5728\u5728\u9700\u94b1\u3002\u8881\u5e08\u5f1f\u593a\u8fd8\u9ec4\u91d1\uff0c\u529f\u52b3\u771f\u662f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u5c0f\u59b9\u4e0d\u77e5\u8fd9\u6279\u91d1\u5b50\u5982\u6b64\u4e8b\u5173\u91cd\u5927\uff0c\u8981\u4e0d\u662f\u4e24\u4f4d\u5927\u54e5\u5230\u6765\uff0c\u53ef\u574f\u4e86\u95ef\u738b\u5927\u4e8b\u3002\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u4e5f\u8981\u4f60\u77e5\u9053\u624d\u597d\u3002\u201d \u3000\u3000\u9752\u9752\u5728\u53e3\u5934\u4e0a\u7d20\u4e0d\u8ba9\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u540e\u5982\u4e0d\u662f\u9ec4\u5927\u54e5\u4eb2\u81ea\u62a4\u9001\uff0c\u591a\u534a\u8def\u4e0a\u8fd8\u8981\u51fa\u4e71\u5b50\u3002\u201d\u5d14\u5e0c\u654f\u6025\u9053\uff1a\u201c\u751a\u2026\u2026\u751a\u4e48\uff1f\u4f60\u53c8\u8981\u6765\u62a2\u5417\uff1f\u201d \u3000\u3000\u9ec4\u771f\u773c\u775b\u4e00\u6a2a\uff0c\u4e0d\u8bb8\u4ed6\u591a\u8a00\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5e08\u5f1f\u4e0e\u6e29\u59d1\u5a18\u5982\u6ca1\u751a\u4e48\u4e8b\uff0c\u5927\u5bb6\u540c\u53bb\u4e5d\u6c5f\u5982\u4f55\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5c0f\u5f1f\u60f3\u5ff5\u5e08\u7236\uff0c\u60f3\u5230\u5357\u4eac\u53bb\u62dc\u89c1\u4ed6\u8001\u4eba\u5bb6\uff0c\u8fd8\u60f3\u89c1\u89c1\u5d14\u53d4\u53d4\u3002\u5927\u5e08\u54e5\u4ee5\u4e3a\u600e\u6837\uff1f\u201d\u9ec4\u771f\u70b9\u5934\u9053\uff1a\u201c\u5e08\u7236\u8eab\u8fb9\u6b63\u611f\u4eba\u624b\u4e0d\u8db3\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e5f\u60f3\u5ff5\u4f60\u5f97\u5f88\u3002\u5e08\u5f1f\uff0c\u4f60\u8fd9\u4e00\u6b21\u5728\u77f3\u6881\u5f00\u5f20\u5927\u53d1\uff0c\u8d5a\u4e86\u4e2a\u6ee1\u5802\u7ea2\u3002 \u3000\u3000\u4eca\u540e\u884c\u4fa0\u4ed7\u4e49\uff0c\u4e3a\u6c11\u9664\u5bb3\uff0c\u76fc\u4f60\u8bf8\u4e8b\u987a\u9042\uff0c\u5927\u5409\u5927\u5229\uff0c\u751f\u610f\u5174\u9686\uff0c\u4e00\u672c\u4e07\u5229\u3002\u201d\u8881\u627f\u5fd7\u8083\u7136\u9053\uff1a\u201c\u8fd8\u8bf7\u5927\u5e08\u54e5\u591a\u591a\u6559\u8bf2\u3002\u201d \u3000\u3000\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8ddf\u4f60\u6765\u8fd9\u5957\uff0c\u54b1\u4eec\u5c31\u6b64\u522b\u8fc7\u3002\u590f\u59d1\u5a18\uff0c\u4f60\u4ee5\u540e\u987a\u624b\u53d1\u8d22\uff0c\u53ef\u5f97\u8ba4\u660e\u4eba\u5bb6\u62db\u724c\u5b57\u53f7\u5440\u3002\u201d\u7ad9\u8d77\u6765\u4e00\u62f1\u624b\uff0c\u8f6c\u5934\u5c31\u8d70\u3002\u5d14\u5e0c\u654f\u4e5f\u5411\u5e08\u53d4\u62dc\u522b\u3002 \u3000\u3000\u5c0f\u6167\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u627f\u5fd7\u5927\u54e5\uff0c\u4f60\u591a\u591a\u4fdd\u91cd\u3002\u201d\u8881\u627f\u5fd7\u70b9\u5934\u9053\uff1a\u201c\u89c1\u5230\u5b89\u5a76\u5a76\u65f6\uff0c\u8bf4\u6211\u5f88\u8bb0\u6302\u5979\u3002\u201d\u5c0f\u6167\u9053\uff1a\u201c\u5988\u77e5\u9053\u4f60\u957f\u5f97\u8fd9\u6837\u9ad8\u4e86\uff0c\u4e00\u5b9a\u5f88\u559c\u6b22\u3002\u6211\u53bb\u5566\uff01\u201d\u884c\u793c\u544a\u522b\uff0c\u8ffd\u4e0a\u9ec4\u771f\u548c\u5d14\u5e0c\u654f\uff0c\u5411\u897f\u800c\u53bb\u3002 \u3000\u3000\u5979\u4e00\u9762\u8d70\uff0c\u4e00\u9762\u8f6c\u5934\u6325\u624b\u3002\u8881\u627f\u5fd7\u4e5f\u4e0d\u505c\u6325\u624b\u62db\u547c\uff0c\u76f4\u81f3\u4e09\u4eba\u5728\u5c71\u8fb9\u8f6c\u5f2f\uff0c\u4e0d\u89c1\u80cc\u5f71\uff0c\u8fd9\u624d\u505c\u624b\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u516b\u56de\u3000\u6613\u5bd2\u5f3a\u654c\u80c6 \u96be\u89e3\u5973\u513f\u5fc3",content:(0,t.jsx)(t.Fragment,{children:"\u9752\u9752\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e72\u4e48\u4e0d\u8ffd\u4e0a\u53bb\u518d\u6325\u624b\uff1f\u201d\u8881\u627f\u5fd7\u4e00\u6014\uff0c\u4e0d\u77e5\u4ed6\u8fd9\u8bdd\u662f\u751a\u4e48\u610f\u601d\u3002\u9752\u9752\u6012\u9053\uff1a\u201c\u8fd9\u822c\u604b\u604b\u4e0d\u820d\uff0c\u53c8\u600e\u4e0d\u8ddf\u5979\u4e00\u8d77\u53bb\uff1f\u201d\u8881\u627f\u5fd7\u624d\u660e\u767d\u5979\u539f\u6765\u751f\u7684\u662f\u8fd9\u4e2a\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u9047\u5230\u5371\u96be\uff0c\u627f\u5979\u5988\u5988\u76f8\u6551\uff0c\u6211\u4eec\u4ece\u5c0f\u5c31\u5728\u4e00\u5757\u513f\u73a9\u7684\u3002\u201d \u3000\u3000\u9752\u9752\u66f4\u52a0\u6c14\u4e86\uff0c\u62ff\u4e86\u4e00\u5757\u77f3\u5934\uff0c\u5728\u77f3\u9636\u4e0a\u4e71\u7838\uff0c\u53ea\u6253\u5f97\u706b\u661f\u76f4\u8fdb\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u90a3\u5c31\u53eb\u505a\u9752\u6885\u7af9\u9a6c\u4e86\u3002\u201d\u53c8\u9053\uff1a\u201c\u4f60\u8981\u7834\u4e94\u884c\u9635\uff0c\u5e72\u4e48\u4e0d\u7528\u65c1\u7684\u5175\u5203\uff0c\u5b9a\u8981\u7528\u5979\u5934\u4e0a\u7684\u7389\u7c2a\uff1f\u96be\u9053\u6211\u5c31\u6ca1\u7c2a\u5b50\u5417\uff1f\u201d\u8bf4\u7740\u62d4\u4e0b\u81ea\u5df1\u5934\u4e0a\u7389\u7c2a\uff0c\u6298\u6210\u4e24\u6bb5\uff0c\u6454\u5728\u5730\u4e0b\uff0c\u8e39\u4e86\u51e0\u811a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c9\u5f97\u5979\u5728\u65e0\u7406\u53d6\u95f9\uff0c\u53ea\u597d\u4e0d\u4f5c\u58f0\u3002\u9752\u9752\u6012\u9053\uff1a\u201c\u4f60\u548c\u5979\u8fd9\u4e48\u6709\u8bf4\u6709\u7b11\u7684\uff0c\u89c1\u4e86\u6211\u5c31\u95f7\u95f7\u4e0d\u4e50\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u51e0\u65f6\u95f7\u95f7\u4e0d\u4e50\u4e86\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u4eba\u5bb6\u7684\u5988\u5988\u597d\uff0c\u5728\u4f60\u5c0f\u65f6\u5019\u6551\u4f60\u75bc\u4f60\uff0c\u6211\u53ef\u662f\u4e2a\u6ca1\u5988\u5988\u7684\u4eba\u3002\u201d\u8bf4\u5230\u6bcd\u4eb2\uff0c\u53c8\u5782\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6025\u9053\uff1a\u201c\u4f60\u522b\u5c3d\u53d1\u813e\u6c14\u5566\u3002\u54b1\u4eec\u597d\u597d\u5546\u91cf\u4e00\u4e0b\uff0c\u4ee5\u540e\u600e\u6837\uff1f\u201d\u9752\u9752\u542c\u5230\u201c\u4ee5\u540e\u600e\u6837\u201d\u56db\u5b57\uff0c\u82cd\u767d\u7684\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u5546\u91cf\u751a\u4e48\uff1f\u4f60\u53bb\u8ffd\u4f60\u90a3\u5c0f\u6167\u59b9\u59b9\u53bb\u3002\u6211\u8fd9\u82e6\u547d\u4eba\uff0c\u5728\u5929\u6daf\u6d77\u89d2\u98d8\u6cca\u7f62\u5566\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u4e2d\u76d8\u7b97\uff0c\u5982\u4f55\u5b89\u7f6e\u8fd9\u4f4d\u5927\u59d1\u5a18\uff0c\u786e\u662f\u4e00\u4ef6\u96be\u4e8b\u3002 \u3000\u3000\u9752\u9752\u89c1\u4ed6\u4e0d\u8bed\uff0c\u7ad9\u8d77\u6765\u6367\u4e86\u76db\u7740\u6bcd\u4eb2\u9aa8\u7070\u7684\u74e6\u8000\uff0c\u6389\u5934\u5c31\u8d70\u3002\u8881\u627f\u5fd7\u5fd9\u95ee\uff1a\u201c\u4f60\u53bb\u54ea\u91cc\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u7406\u6211\u5462\uff1f\u201d\u5f84\u5411\u5317\u884c\u3002\u8881\u627f\u5fd7\u65e0\u5948\uff0c\u53ea\u5f97\u7d27\u8ddf\u5728\u540e\u9762\u3002\u4e00\u8def\u4e0a\u9752\u9752\u59cb\u7ec8\u4e0d\u8ddf\u4ed6\u4ea4\u8c08\uff0c\u8881\u627f\u5fd7\u9017\u5979\u8bf4\u8bdd\uff0c\u603b\u662f\u4e0d\u7b54\u3002 \u3000\u3000\u5230\u4e86\u91d1\u534e\uff0c\u4e24\u4eba\u5165\u5ba2\u5e97\u6295\u5bbf\u3002\u9752\u9752\u4e0a\u8857\u4e70\u4e86\u5957\u7537\u4eba\u8863\u5dfe\uff0c\u53c8\u6539\u7a7f\u7537\u88c5\u3002\u8881\u627f\u5fd7\u77e5\u5979\u4ed3\u5352\u79bb\u5bb6\uff0c\u8eab\u8fb9\u6ca1\u5e26\u751a\u4e48\u94b1\uff0c\u4e58\u5979\u5916\u51fa\u65f6\u5728\u5979\u8863\u56ca\u4e2d\u653e\u4e86\u4e24\u952d\u94f6\u5b50\u3002\u9752\u9752\u56de\u6765\u540e\uff0c\u6485\u8d77\u4e86\u5634\uff0c\u5c06\u94f6\u5b50\u9001\u56de\u4ed6\u623f\u4e2d\u3002 \u3000\u3000\u8fd9\u5929\u665a\u4e0a\u5979\u51fa\u53bb\u505a\u6848\uff0c\u5728\u4e00\u5bb6\u5bcc\u6237\u76d7\u4e86\u4e94\u767e\u591a\u4e24\u94f6\u5b50\u3002\u7b2c\u4e8c\u5929\u91d1\u534e\u57ce\u91cc\u4fbf\u8f70\u4f20\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6599\u77e5\u662f\u5979\u5e72\u7684\u4e8b\uff0c\u4e0d\u7981\u6697\u76b1\u7709\u5934\uff0c\u771f\u4e0d\u61c2\u5f97\u5979\u4e3a\u751a\u4e48\u83ab\u540d\u5176\u5999\u7684\u5ffd\u7136\u5927\u53d1\u813e\u6c14\uff1f\u5982\u4f55\u5bf9\u4ed8\u5b9e\u662f\u4e00\u7a8d\u4e0d\u901a\u3002\u8f6f\u8a00\u76f8\u6c42\u5427\uff1f\u5b9e\u5728\u653e\u4e0d\u4e0b\u8138\u6765\uff1b\u5f03\u4e4b\u4e0d\u7406\u5427\uff1f\u53c8\u89c9\u8ba9\u5979\u4e00\u4e2a\u5c11\u5973\u5b64\u8eab\u72ec\u95ef\u6c5f\u6e56\uff0c\u672a\u514d\u5fc3\u6709\u4e0d\u5fcd\u3002\u60f3\u6765\u60f3\u53bb\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u8fd9\u65e5\u4e24\u4eba\u79bb\u4e86\u91d1\u534e\uff0c\u5411\u4e49\u4e4c\u884c\u53bb\u3002\u9752\u9752\u6c89\u7740\u8138\u5728\u524d\uff0c\u8881\u627f\u5fd7\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u884c\u4e86\u4e09\u5341\u591a\u91cc\uff0c\u5ffd\u7136\u5929\u8fb9\u4e4c\u4e91\u5bc6\u5e03\uff0c\u4e24\u4eba\u5fd9\u52a0\u7d27\u811a\u6b65\uff0c\u884c\u4e0d\u5230\u4e94\u91cc\uff0c\u5927\u96e8\u5df2\u503e\u76c6\u800c\u4e0b\u3002\u8881\u627f\u5fd7\u5e26\u7740\u96e8\u4f1e\uff0c\u9752\u9752\u5374\u5acc\u96e8\u4f1e\u7d2f\u8d58\u6ca1\u5e26\u3002\u5979\u5c55\u5f00\u8f7b\u529f\u5411\u524d\u6025\u5954\uff0c\u9644\u8fd1\u5374\u6ca1\u4eba\u5bb6\uff0c\u4e5f\u65e0\u5e99\u5b87\u51c9\u4ead\u3002\u8881\u627f\u5fd7\u811a\u4e0b\u52a0\u5feb\uff0c\u62a2\u5230\u5979\u524d\u9762\uff0c\u9012\u4f1e\u7ed9\u5979\u3002\u9752\u9752\u4f38\u624b\u628a\u4f1e\u4e00\u63a8\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u54b1\u4eec\u662f\u7ed3\u4e49\u5144\u5f1f\uff0c\u8bf4\u662f\u540c\u751f\u5171\u6b7b\uff0c\u7978\u798f\u4e0e\u5171\u3002\u600e\u4e48\u4f60\u5230\u8fd9\u65f6\u5019\u8fd8\u5728\u751f\u54e5\u54e5\u7684\u6c14\uff1f\u201d \u3000\u3000\u9752\u9752\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u6c14\u8272\u7a0d\u548c\uff0c\u9053\uff1a\u201c\u4f60\u8981\u6211\u4e0d\u751f\u6c14\uff0c\u90a3\u4e5f\u5bb9\u6613\uff0c\u53ea\u6d88\u4f9d\u6211\u4e00\u4ef6\u4e8b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff0c\u522b\u8bf4\u4e00\u4ef6\uff0c\u5341\u4ef6\u4e5f\u4f9d\u4e86\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u597d\uff0c\u4f60\u542c\u7740\u3002\u4ece\u4eca\u800c\u540e\uff0c\u4f60\u4e0d\u80fd\u518d\u89c1\u90a3\u4e2a\u5b89\u59d1\u5a18\u548c\u5979\u6bcd\u4eb2\u3002\u5982\u4f60\u7b54\u5141\u4e86\uff0c\u6211\u9a6c\u4e0a\u5411\u4f60\u8d54\u4e0d\u662f\u3002\u201d\u8bf4\u7740\u5ae3\u7136\u4e00\u7b11\u3002 \u3000\u3000\u8881\u627f\u5fd7\u597d\u751f\u4e3a\u96be\uff0c\u5fc3\u60f3\u5b89\u5bb6\u6bcd\u5973\u5bf9\u5df1\u6709\u6069\uff0c\u5c06\u6765\u7ec8\u987b\u8bbe\u6cd5\u62a5\u7b54\uff0c\u65e0\u7f18\u65e0\u6545\u7684\u907f\u4e0d\u89c1\u9762\uff0c\u90a3\u6210\u751a\u4e48\u8bdd\uff1f\u8fd9\u4ef6\u4e8b\u53ef\u4e0d\u80fd\u8f7b\u6613\u7b54\u5141\uff0c\u4e0d\u7531\u5f97\u9887\u4e3a\u8e0c\u8e87\u3002 \u3000\u3000\u9752\u9752\u4fcf\u8138\u4e00\u677f\uff0c\u6012\u9053\uff1a\u201c\u6211\u539f\u77e5\u4f60\u820d\u4e0d\u5f97\u4f60\u90a3\u5c0f\u6167\u59b9\u59b9\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u6765\uff0c\u5411\u524d\u72c2\u5954\u3002\u8881\u627f\u5fd7\u5927\u53eb\uff1a\u201c\u9752\u5f1f\uff0c\u9752\u5f1f\uff01\u201d\u9752\u9752\u5145\u8033\u4e0d\u95fb\uff0c\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u89c1\u8def\u4e2d\u6709\u5ea7\u51c9\u4ead\uff0c\u4fbf\u76f4\u7a9c\u8fdb\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5954\u8fdb\u51c9\u4ead\uff0c\u89c1\u5979\u5df2\u7136\u5168\u8eab\u6e7f\u900f\u3002\u5176\u65f6\u5929\u6c14\u6b63\u70ed\uff0c\u8863\u886b\u5355\u8584\uff0c\u96e8\u6c34\u6d78\u6e7f\u540e\u751a\u662f\u4e0d\u96c5\uff0c\u9752\u9752\u53c8\u7f9e\u53c8\u6025\uff0c\u4f0f\u5728\u51c9\u4ead\u680f\u6746\u4e0a\u54ed\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u6b3a\u4fae\u6211\uff0c\u4f60\u6b3a\u4fae\u6211\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u5012\u5947\u4e86\uff0c\u6211\u51e0\u65f6\u6b3a\u4fae\u8fc7\u4f60\u4e86\uff1f\u201d\u5f53\u4e0b\u4e5f\u4e0d\u5206\u8fa9\uff0c\u89e3\u4e0b\u957f\u886b\uff0c\u7ed9\u5979\u62ab\u5728\u8eab\u4e0a\u3002\u4ed6\u6709\u4f1e\u906e\u96e8\uff0c\u8863\u886b\u672a\u6e7f\u3002 \u3000\u3000\u5bfb\u601d\uff1a\u201c\u5230\u5e95\u5979\u8981\u751a\u4e48\uff1f\u5fc3\u91cc\u5728\u60f3\u751a\u4e48\uff1f\u6211\u53ef\u4e00\u70b9\u4e5f\u4e0d\u61c2\u3002\u5c0f\u6167\u59b9\u59b9\u53c8\u6ca1\u5f97\u7f6a\u5979\uff0c\u4e3a\u751a\u4e48\u8981\u6211\u4eca\u540e\u4e0d\u53ef\u548c\u5979\u518d\u89c1\uff1f\u96be\u9053\u4e3a\u4e86\u5c0f\u6167\u59b9\u59b9\u5411\u5979\u7d22\u8ba8\u91d1\u5b50\uff0c\u56e0\u800c\u5bb3\u6b7b\u5979\u5988\u5988\uff1f\u8fd9\u53ef\u4e5f\u4e0d\u80fd\u602a\u5c0f\u6167\u554a\u3002\u201d\u4ed6\u5c06\u5415\u4e03\u5148\u751f\u3001\u6e29\u6c0f\u4e94\u8001\u8fd9\u4e9b\u5f3a\u654c\u6740\u5f97\u5927\u8d25\u4e8f\u8f93\uff0c\u5fc3\u60ca\u80c6\u5bd2\uff0c\u4e5f\u4e0d\u7b97\u662f\u4f55\u7b49\u96be\u4e8b\uff0c\u53ef\u662f\u9752\u9752\u8fd9\u4f4d\u5927\u59d1\u5a18\u5ffd\u559c\u5ffd\u55d4\uff0c\u5ffd\u54ed\u5ffd\u7b11\uff0c\u5b9e\u4ee4\u4ed6\u6414\u5934\u6478\u816e\uff0c\u8d8a\u60f3\u8d8a\u662f\u80e1\u6d82\u3002 \u3000\u3000\u9752\u9752\u60f3\u8d77\u6bcd\u4eb2\u60e8\u6b7b\uff0c\u7d22\u6027\u653e\u58f0\u5927\u54ed\u8d77\u6765\uff0c\u76f4\u54ed\u5f97\u8881\u627f\u5fd7\u624b\u8db3\u65e0\u63aa\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u96e8\u6e10\u6e10\u505c\u4e86\uff0c\u9752\u9752\u5374\u4ecd\u662f\u54ed\u4e2a\u4e0d\u4f11\u3002\u5979\u5077\u773c\u5411\u8881\u627f\u5fd7\u4e00\u671b\uff0c\u89c1\u4ed6\u4e5f\u6b63\u671b\u7740\u81ea\u5df1\uff0c\u5fd9\u8f6c\u8fc7\u773c\u5149\uff0c\u7ee7\u7eed\u5927\u54ed\u3002\u8881\u627f\u5fd7\u4e5f\u6a2a\u4e86\u5fc3\uff0c\u5fc3\u60f3\uff1a\u201c\u770b\u4f60\u6709\u591a\u5c11\u773c\u6cea\uff01\u201d \u3000\u3000\u6b63\u81ea\u50f5\u6301\u4e0d\u51b3\uff0c\u5ffd\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u4e00\u4e2a\u9752\u5e74\u519c\u592b\u6276\u7740\u4e00\u4e2a\u8001\u5987\u8d70\u8fdb\u4ead\u6765\u3002\u8001\u5987\u8eab\u4e0a\u6709\u75c5\uff0c\u54fc\u4e2a\u4e0d\u505c\u3002\u90a3\u519c\u592b\u662f\u4ed6\u513f\u5b50\uff0c\u4e0d\u4f4f\u6e29\u8a00\u5b89\u6170\u3002\u9752\u9752\u89c1\u6709\u4eba\u6765\uff0c\u4e5f\u5c31\u6536\u6cea\u4e0d\u54ed\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u6211\u8bd5\u8bd5\u8fd9\u6cd5\u513f\u770b\u3002\u201d\u8fc7\u4e0d\u591a\u65f6\uff0c\u8fd9\u5bf9\u519c\u5bb6\u6bcd\u5b50\u51fa\u4ead\u53bb\u4e86\u3002\u9752\u9752\u89c1\u96e8\u5df2\u505c\uff0c\u6b63\u8981\u4e0a\u9053\uff0c\u8881\u627f\u5fd7\u5ffd\u7136\u201c\u54ce\u5537\uff0c\u54ce\u5537\u201d\u7684\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9752\u9752\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u5934\u770b\u65f6\uff0c\u89c1\u4ed6\u6367\u4f4f\u4e86\u809a\u5b50\uff0c\u8e72\u5728\u5730\u4e0b\uff0c\u5fd9\u8d70\u8fc7\u53bb\u770b\u3002\u8881\u627f\u5fd7\u8fd0\u8d77\u6df7\u5143\u529f\uff0c\u989d\u4e0a\u767b\u65f6\u9ec4\u8c46\u822c\u7684\u6c57\u73e0\u76f4\u6dcc\u4e0b\u6765\u3002\u9752\u9752\u614c\u4e86\uff0c\u8fde\u95ee\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u809a\u5b50\u75db\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u88c5\u5047\u7d22\u6027\u88c5\u5230\u5e95\uff01\u201d\u8fd0\u6c14\u95ed\u4f4f\u4e86\u624b\u4e0a\u7a74\u9053\u3002\u9752\u9752\u4e00\u6478\u4ed6\u624b\uff0c\u53ea\u89c9\u4e00\u9635\u51b0\u51b7\uff0c\u66f4\u662f\u614c\u4e86\u624b\u811a\uff0c\u5fd9\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e86\uff1f\u600e\u4e48\u4e86\uff1f\u201d\u8881\u627f\u5fd7\u5927\u58f0\u547b\u541f\uff0c\u53ea\u662f\u4e0d\u7b54\u3002\u9752\u9752\u6025\u5f97\u53c8\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u547b\u541f\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u6211\u2026\u2026\u6211\u8fd9\u75c5\u662f\u597d\u4e0d\u4e86\u7684\u4e86\uff0c\u4f60\u83ab\u7406\u6211\u3002\u4f60\u4f60\u2026\u2026\u81ea\u5df1\u53bb\u5427\u3002\u201d\u9752\u9752\u6025\u9053\uff1a\u201c\u600e\u4e48\u597d\u7aef\u7aef\u7684\u751f\u8d77\u75c5\u6765\uff1f\u201d\u8881\u627f\u5fd7\u6709\u6c14\u65e0\u529b\u7684\u9053\uff1a\u201c\u6211\u4ece\u5c0f\u6709\u4e00\u4e2a\u75c5\u2026\u2026\u53d7\u4e0d\u5f97\u6c14\u2026\u2026\u8981\u662f\u4eba\u5bb6\u53d1\u6211\u813e\u6c14\uff0c\u6211\u5fc3\u91cc\u4e00\u6025\uff0c\u7acb\u523b\u4f1a\u5fc3\u75db\u809a\u75db\uff0c\u54ce\u5537\uff0c\u54ce\u5537\uff0c\u75db\u6b7b\u5566\uff01\u6628\u5929\u8ddf\u4f60\u7684\u4e94\u4f4d\u7237\u7237\u76f8\u6597\uff0c\u53c8\u4f7f\u529b\u5389\u5bb3\u4e86\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u9752\u9752\u60ca\u60f6\u4e4b\u4e0b\uff0c\u53cc\u624b\u6402\u4f4f\u4e86\u4ed6\uff0c\u7ed9\u4ed6\u80f8\u53e3\u63c9\u6413\u3002\u8881\u627f\u5fd7\u88ab\u5979\u62b1\u4f4f\uff0c\u5f88\u662f\u4e0d\u597d\u610f\u601d\u3002\u9752\u9752\u54ed\u9053\uff1a\u201c\u627f\u5fd7\u5927\u54e5\uff0c\u90fd\u662f\u6211\u4e0d\u597d\uff0c\u4f60\u522b\u751f\u6c14\u5566\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u4e0d\u7ee7\u7eed\u88c5\u5047\uff0c\u4e0d\u514d\u7ed9\u5979\u5f53\u4f5c\u4e86\u8f7b\u8584\u4e4b\u4eba\u3002\u201d\u6b64\u65f6\u9a91\u864e\u96be\u4e0b\uff0c\u53ea\u5f97\u5782\u4e0b\u4e86\u5934\uff0c\u547b\u541f\u9053\uff1a\u201c\u6211\u662f\u6d3b\u4e0d\u6210\u5566\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u7ed9\u6211\u846c\u4e86\uff0c\u53bb\u544a\u8bc9\u6211\u5927\u5e08\u54e5\u4e00\u58f0\u3002\u201d\u4ed6\u8d8a\u88c5\u8d8a\u8c61\uff0c\u809a\u91cc\u5374\u5728\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u9752\u9752\u54ed\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u751f\u6c14\u662f\u5047\u7684\uff0c\u6211\u662f\u6545\u610f\u6c14\u4f60\u7684\uff0c\u6211\u5fc3\u91cc\u2026\u2026\u5fc3\u91cc\u5f88\u662f\u559c\u6b22\u4f60\u5440\u3002\u4f60\u8981\u662f\u6b7b\u4e86\uff0c\u6211\u8ddf\u4f60\u4e00\u8d77\u6b7b\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u5934\u4e00\u60ca\uff1a\u201c\u539f\u6765\u5979\u662f\u7231\u7740\u6211\u3002\u201d\u4ed6\u751f\u5e73\u7b2c\u4e00\u6b21\u9886\u7565\u5c11\u5973\u7684\u6e29\u67d4\uff0c\u5fc3\u5934\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u53c8\u662f\u751c\u871c\uff0c\u53c8\u662f\u7f9e\u6127\uff0c\u6014\u6014\u7684\u4e0d\u8bed\u3002 \u3000\u3000\u9752\u9752\u53ea\u9053\u4ed6\u771f\u7684\u8981\u6b7b\u4e86\uff0c\u7d27\u7d27\u7684\u62b1\u4f4f\u4ed6\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5927\u54e5\uff0c\u4f60\u4e0d\u80fd\u6b7b\u5440\u3002\u201d\u8881\u627f\u5fd7\u53ea\u89c9\u5979\u5439\u6c14\u5982\u5170\uff0c\u8f6f\u7ef5\u7ef5\u7684\u8eab\u4f53\u504e\u4f9d\u7740\u81ea\u5df1\uff0c\u4e0d\u7981\u4e00\u9635\u795e\u9b42\u98a0\u5012\u3002\u9752\u9752\u53c8\u9053\uff1a\u201c\u6211\u751f\u6c14\u662f\u5047\u7684\uff0c\u4f60\u522b\u5f53\u771f\u3002\u201d\u8881\u627f\u5fd7\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u751f\u75c5\u4e5f\u662f\u5047\u7684\u5440\uff0c\u4f60\u522b\u5f53\u771f\uff01\u201d \u3000\u3000\u9752\u9752\u4e00\u5446\uff0c\u5ffd\u5730\u8df3\u8d77\uff0c\u5288\u8138\u91cd\u91cd\u4e00\u4e2a\u8033\u5149\uff0c\u556a\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u53ea\u6253\u5f97\u4ed6\u773c\u524d\u91d1\u661f\u4e71\u5192\u3002\u9752\u9752\u63a9\u8138\u5c31\u8d70\u3002\u8881\u627f\u5fd7\u6115\u7136\u4e0d\u89e3\uff1a\u201c\u521a\u624d\u8fd8\u8bf4\u5f88\u559c\u6b22\u6211\uff0c\u6ca1\u6709\u6211\u5c31\u6d3b\u4e0d\u6210\uff0c\u600e\u4e48\u5ffd\u7136\u4e4b\u95f4\u53c8\u7ffb\u8138\u6253\u4eba\uff1f\u201d\u4ed6\u4e0d\u89e3\u9752\u9752\u7684\u5fc3\u4e8b\uff0c\u53ea\u5f97\u8ddf\u5728\u540e\u9762\u3002\u9752\u9752\u4e00\u756a\u60ca\u60f6\uff0c\u4e00\u756a\u559c\u6170\uff0c\u65e9\u5c06\u5bf9\u5b89\u5c0f\u6167\u7684\u7591\u5fcc\u4e4b\u5fc3\u629b\u5728\u4e00\u65c1\uff0c\u89c1\u8881\u627f\u5fd7\u5de6\u8fb9\u8138\u4e0a\u7ea2\u7ea2\u7684\u5370\u7740\u81ea\u5df1\u4e94\u4e2a\u624b\u6307\u5370\uff0c\u4e0d\u7981\u6709\u4e9b\u6b49\u7136\uff0c\u4e5f\u4e0d\u7981\u6709\u4e9b\u5f97\u610f\uff0c\u60f3\u5230\u7ec8\u4e8e\u6cc4\u9732\u4e86\u81ea\u5df1\u5fc3\u4e8b\uff0c\u53c8\u611f\u7f9e\u6127\u96be\u5f53\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u662f\u5fc3\u4e2d\u6709\u6127\uff0c\u4e00\u8def\u4e0a\u518d\u4e0d\u8bf4\u8bdd\uff0c\u6709\u65f6\u76ee\u5149\u76f8\u89e6\uff0c\u5747\u662f\u8138\u4e0a\u4e00\u7ea2\uff0c\u7acb\u5373\u540c\u65f6\u8f6c\u5934\u56de\u907f\u3002\u5fc3\u4e2d\u5374\u5747\u662f\u751c\u751c\u7684\uff0c\u8fd9\u6570\u5341\u91cc\u8def\uff0c\u4fbf\u5982\u662f\u98d8\u98d8\u8361\u8361\u7684\u5728\u4e91\u7aef\u884c\u8d70\u4e00\u822c\u3002 \u3000\u3000\u8fd9\u5929\u508d\u665a\u5230\u4e86\u4e49\u4e4c\uff0c\u9752\u9752\u627e\u5230\u4e00\u5bb6\u5ba2\u5e97\u6295\u5bbf\u3002\u8881\u627f\u5fd7\u8ddf\u7740\u8fdb\u5e97\u3002 \u3000\u3000\u9752\u9752\u6a2a\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6b7b\u76ae\u8d56\u6d3b\u7684\u8ddf\u7740\u4eba\u5bb6\uff0c\u771f\u8ba8\u538c\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6478\u7740\u8138\u988a\uff0c\u7b11\u9053\uff1a\u201c\u6211\u809a\u75db\u662f\u5047\uff0c\u8fd9\u91cc\u75db\u5374\u662f\u771f\u7684\u3002\u201d\u9752\u9752\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8981\u662f\u6c14\u4e0d\u8fc7\uff0c\u5c31\u6253\u8fd8\u6211\u4e00\u8bb0\u5427\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e8e\u662f\u548c\u597d\u5982\u521d\uff0c\u665a\u996d\u540e\u95f2\u8c08\u4e00\u4f1a\uff0c\u4e24\u4eba\u5206\u623f\u7761\u4e86\u3002\u9752\u9752\u89c1\u4ed6\u4e8e\u81ea\u5df1\u5410\u9732\u771f\u60c5\u4e4b\u540e\uff0c\u4ecd\u662f\u6e29\u6587\u5b88\u793c\uff0c\u4e0d\u518d\u63d0\u8d77\u90a3\u4e8b\uff0c\u5012\u514d\u4e86\u81ea\u5df1\u5c34\u5c2c\u72fc\u72c8\uff0c\u53ef\u662f\u5fcd\u4e0d\u4f4f\u53c8\u60f3\uff1a\u201c\u6211\u8bf4\u4e86\u559c\u6b22\u4ed6\uff0c\u4ed6\u5374\u53c8\u600e\u5730\u4e0d\u8ddf\u6211\u8bf4\uff1f\u201d\u8fd9\u4e00\u665a\u7ffb\u6765\u8986\u53bb\uff0c\u53c8\u600e\u7761\u5f97\u5b89\u7a33\uff1f \u3000\u3000\u6b21\u65e5\u8d77\u8eab\u4e0a\u9053\uff0c\u9752\u9752\u95ee\u8d77\u4ed6\u5982\u4f55\u89c1\u5230\u5979\u7239\u7239\u7684\u9057\u9aa8\u3002\u8881\u627f\u5fd7\u4e8e\u662f\u8be6\u7ec6\u8bf4\u4e86\u7329\u7329\u600e\u6837\u53d1\u73b0\u6d1e\u7a74\uff0c\u4ed6\u600e\u6837\u8fdb\u6d1e\u89c1\u5230\u9ab7\u9ac5\u3001\u600e\u6837\u6398\u5230\u94c1\u76d2\uff0c\u600e\u6837\u53d1\u73b0\u56fe\u8c31\u7b49\u60c5\uff0c\u53c8\u8bb2\u5230\u5f20\u6625\u4e5d\u548c\u90a3\u79c3\u5934\u591c\u4e2d\u524d\u6765\u5077\u88ad\u3001\u53cd\u800c\u906d\u6b83\u7684\u4e8b\u3002 \u3000\u3000\u9752\u9752\u53ea\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u6625\u4e5d\u662f\u6211\u56db\u7237\u7237\u7684\u5f92\u5f1f\uff0c\u6700\u662f\u5978\u6076\u4e0d\u8fc7\u3002\u90a3\u79c3\u5934\u662f\u4e8c\u7237\u7237\u7684\u5f92\u5f1f\u3002\u6211\u4e94\u4e2a\u7237\u7237\u6bcf\u5e74\u6b63\u6708\u5341\u516d\uff0c\u603b\u662f\u6d3e\u4e86\u51e0\u6279\u5b50\u4f84\u5f92\u5f1f\u51fa\u53bb\u5bfb\u627e\u751a\u4e48\u3002\u5230\u5e95\u5bfb\u751a\u4e48\u4eba\uff0c\u8fd8\u662f\u627e\u751a\u4e48\u4e1c\u897f\uff0c\u5927\u5bb6\u9b3c\u9b3c\u795f\u795f\u7684\uff0c\u53ef\u4ece\u6765\u4e0d\u8ddf\u6211\u8bf4\u3002\u4e0d\u8fc7\u6bcf\u4e2a\u4eba\u56de\u6765\uff0c\u5168\u90fd\u5782\u5934\u4e27\u6c14\u7684\uff0c\u5b9a\u662f\u751a\u4e48\u4e5f\u627e\u4e0d\u5230\u3002\u73b0\u4e0b\u60f3\u6765\uff0c\u81ea\u7136\u662f\u5728\u627e\u6211\u7239\u7239\u7684\u4e0b\u843d\u4e86\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u9053\uff1a\u201c\u6211\u7239\u7239\u6b7b\u4e86\u4e4b\u540e\u8fd8\u80fd\u7528\u8ba1\u6740\u654c\uff0c\u771f\u662f\u4e86\u4e0d\u8d77\u3002\u201d\u8a00\u4e0b\u8d5e\u53f9\u4e0d\u5df2\uff0c\u53c8\u9053\uff1a\u201c\u8981\u662f\u7239\u7239\u6d3b\u7740\uff0c\u89c1\u5230\u4f60\u628a\u6e29\u5bb6\u90a3\u4e9b\u574f\u4eba\u6253\u5f97\u8fd9\u822c\u72fc\u72c8\uff0c\u5b9a\u662f\u9ad8\u5174\u5f97\u5f88\u2026\u2026\u5582\uff0c\u5988\u5988\u662f\u4eb2\u773c\u89c1\u5230\u7684\uff0c\u5979\u5b9a\u4f1a\u544a\u8bc9\u7239\u7239\u2026\u2026\u4f60\u518d\u628a\u7239\u7239\u7684\u7b14\u8ff9\u7ed9\u6211\u77a7\u77a7\u3002\u201d\u8881\u627f\u5fd7\u53d6\u51fa\u90a3\u5e45\u56fe\u6765\uff0c\u9012\u7ed9\u5979\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7239\u7239\u7684\u4e1c\u897f\uff0c\u8be5\u5f53\u5f52\u4f60\u3002\u201d\u9752\u9752\u77a7\u7740\u7236\u4eb2\u7684\u5b57\u8ff9\uff0c\u53c8\u662f\u4f24\u5fc3\uff0c\u53c8\u662f\u6b22\u559c\u3002 \u3000\u3000\u8fd9\u5929\u6765\u5230\u677e\u6c5f\uff0c\u9752\u9752\u5ffd\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5230\u4e86\u5357\u4eac\uff0c\u89c1\u8fc7\u4f60\u5e08\u7236\u540e\uff0c\u54b1\u4eec\u5c31\u53bb\u628a\u5b9d\u8d1d\u8d77\u51fa\u6765\u3002\u201d\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u751a\u4e48\u5b9d\u8d1d\uff1f\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u7239\u7239\u8fd9\u5f20\u56fe\u4e0d\u662f\u53eb\u505a\u2018\u91cd\u5b9d\u4e4b\u56fe\u2019\u4e48\uff1f\u4ed6\u8bf4\u5f97\u5b9d\u4e4b\u4eba\u8981\u916c\u6211\u5988\u5988\u9ec4\u91d1\u5341\u4e07\u4e24\uff0c\u5988\u5988\u53c8\u8bf4\u8fd9\u662f\u7687\u5bab\u5185\u5e93\u4e2d\u7684\u7269\u4e8b\uff0c\u5176\u4e2d\u4e0d\u77e5\u6709\u591a\u5c11\u91d1\u94f6\u73e0\u5b9d\u3002\u201d\u8881\u627f\u5fd7\u6c89\u541f\u9053\uff1a\u201c\u8bdd\u662f\u4e0d\u9519\uff0c\u53ef\u662f\u54b1\u4eec\u529e\u6b63\u4e8b\u8981\u7d27\u3002\u201d\u4ed6\u4e00\u5fc3\u8bb0\u6302\u7684\uff0c\u53ea\u662f\u4f1a\u89c1\u5e08\u7236\u4e4b\u540e\u53bb\u62a5\u7236\u4ec7\u3002\u9752\u9752\u9053\uff1a\u201c\u6309\u56fe\u5bfb\u5b9d\uff0c\u4e5f\u4e0d\u89c1\u5f97\u4f1a\u803d\u6401\u591a\u5c11\u65f6\u5019\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u795e\u8272\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4fe9\u62ff\u5230\u8fd9\u8bb8\u591a\u91d1\u94f6\u73e0\u5b9d\uff0c\u53c8\u6709\u751a\u4e48\u7528\uff1f\u9752\u5f1f\uff0c\u6211\u529d\u4f60\u603b\u8981\u89c4\u89c4\u77e9\u77e9\u7684\u505a\u4eba\uff0c\u522b\u8fd9\u4e48\u8d2a\u8d22\u624d\u597d\u3002\u201d\u53ea\u8bf4\u5f97\u9752\u9752\u6485\u8d77\u4e86\u5c0f\u5634\uff0c\u8d4c\u6c14\u4e0d\u5403\u665a\u996d\u3002 \u3000\u3000\u6b21\u65e5\u4e0a\u8def\uff0c\u9752\u9752\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u62ff\u4e86\u95ef\u738b\u4e8c\u5343\u4e24\u9ec4\u91d1\uff0c\u4ed6\u4eec\u5c31\u6025\u5f97\u751a\u4e48\u4f3c\u7684\uff0c\u8981\u4f60\u5927\u5e08\u5144\u4eb2\u81ea\u51fa\u9a6c\u6765\u53d6\u56de\u53bb\u3002\u95ef\u738b\u5e72\u4e48\u8fd9\u6837\u5c0f\u5bb6\u6c14\u554a\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u95ef\u738b\u54ea\u91cc\u5c0f\u5bb6\u6c14\u4e86\uff1f\u6211\u89c1\u8fc7\u4ed6\u7684\u3002\u4ed6\u5f85\u4eba\u6700\u662f\u4ed7\u4e49\u758f\u8d22\uff0c\u4ed6\u4e3a\u5929\u4e0b\u8001\u767e\u59d3\u89e3\u9664\u75be\u82e6\uff0c\u81ea\u5df1\u8282\u4fed\u5f97\u5f88\uff0c\u5f53\u771f\u662f\u4e00\u4f4d\u5927\u82f1\u96c4\u5927\u8c6a\u6770\u3002\u8fd9\u4e8c\u5343\u4e24\u9ec4\u91d1\u4ed6\u6709\u6b63\u7528\uff0c\u81ea\u7136\u4e0d\u80fd\u8f7b\u6613\u5931\u53bb\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u662f\u5440\uff0c\u8981\u662f\u54b1\u4eec\u7ed9\u95ef\u738b\u732e\u4e0a\u9ec4\u91d1\u4e8c\u5341\u4e07\u4e24\uff0c\u751a\u81f3\u4e8c\u767e\u4e07\u4e24\u3001\u4e09\u767e\u4e07\u4e24\uff0c\u4f60\u8bf4\u8fd9\u4ef6\u4e8b\u597d\u4e0d\u597d\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u8a00\u63d0\u9192\uff0c\u53ea\u559c\u5f97\u8881\u627f\u5fd7\u6293\u4f4f\u4e86\u5979\u624b\uff0c\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u6211\u771f\u80e1\u6d82\u5566\uff0c\u591a\u4e8f\u4f60\u8bf4\u3002\u201d\u9752\u9752\u628a\u624b\u4e00\u7529\u3002\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8981\u4f60\u89c1\u60c5\uff0c\u4ee5\u540e\u5c11\u9a82\u4eba\u5bb6\u5c31\u662f\u5566\u3002\u201d\u8881\u627f\u5fd7\u966a\u7b11\u9053\uff1a\u201c\u8981\u662f\u6211\u4eec\u627e\u5230\u8fd9\u6279\u91d1\u73e0\u5b9d\u8d1d\uff0c\u732e\u7ed9\u95ef\u738b\uff0c\u53ef\u4e0d\u77e5\u80fd\u6551\u5f97\u591a\u5c11\u53d7\u82e6\u767e\u59d3\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u4e24\u4eba\u5750\u5728\u8def\u8fb9\uff0c\u53d6\u51fa\u56fe\u6765\u7ec6\u770b\uff0c\u89c1\u56fe\u4e2d\u5fc3\u5904\u6709\u4e2a\u7ea2\u5708\uff0c\u5708\u65c1\u6ce8\u7740\u201c\u9b4f\u56fd\u516c\u5e9c\u201d\u56db\u5b57\u3002 \u3000\u3000\u4e24\u4eba\u53c8\u7ec6\u770b\u4e86\u4e00\u4f1a\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5b9d\u85cf\u662f\u5728\u9b4f\u56fd\u516c\u5e9c\u7684\u4e00\u95f4\u504f\u623f\u5e95\u4e0b\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u54b1\u4eec\u5230\u5357\u4eac\u540e\uff0c\u53ea\u6d88\u5bfb\u5230\u9b4f\u56fd\u516c\u5e9c\uff0c\u5c31\u6709\u6cd5\u5b50\u3002\u9b4f\u56fd\u516c\u662f\u5927\u5c06\u519b\u5f90\u8fbe\u7684\u5c01\u53f7\uff0c\u4ed6\u662f\u672c\u671d\u7b2c\u4e00\u5927\u529f\u81e3\uff0c\u5e9c\u7b2c\u5b9a\u7136\u6781\u5927\uff0c\u6613\u627e\u5f97\u5f88\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6447\u6447\u5934\u9053\uff1a\u201c\u5927\u5c06\u519b\u7684\u5e9c\u7b2c\u975e\u540c\u5c0f\u53ef\uff0c\u9632\u5b88\u5b9a\u4e25\uff0c\u5c31\u7b97\u6df7\u5f97\u8fdb\u53bb\uff0c\u8981\u8fd9\u4e48\u5927\u4e3e\u6316\u6398\uff0c\u5b9e\u5728\u4e5f\u4e3a\u96be\u5f97\u7d27\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u73b0\u4e0b\u51ed\u7a7a\u731c\u6d4b\uff0c\u4e5f\u662f\u65e0\u7528\uff0c\u5230\u4e86\u5357\u4eac\u518d\u76f8\u673a\u884c\u4e8b\u5427\u3002\u201d \u3000\u3000\u8def\u4e0a\u6570\u65e5\uff0c\u5230\u4e86\u5357\u4eac\u3002\u90a3\u91d1\u9675\u77f3\u5934\u57ce\u662f\u5929\u4e0b\u7b2c\u4e00\u5927\u57ce\uff0c\u4e43\u592a\u7956\u5f53\u5e74\u5f00\u56fd\u5efa\u90fd\u4e4b\u5730\uff0c\u5343\u95e8\u4e07\u6237\uff0c\u4e94\u65b9\u8f90\u8f8f\uff0c\u6731\u96c0\u6865\u7554\u7bab\u9f13\uff0c\u4e4c\u8863\u5df7\u53e3\u7eee\u7f57\uff0c\u867d\u9022\u4e71\u4e16\uff0c\u5374\u662f\u4e0d\u51cf\u6614\u5e74\u4f88\u9761\u3002 \u3000\u3000\u4e24\u4eba\u6295\u5e97\u540e\uff0c\u8881\u627f\u5fd7\u4fbf\u4f9d\u7740\u5927\u5e08\u54e5\u6240\u8bf4\u5730\u5740\u53bb\u89c1\u5e08\u7236\u3002\u4e00\u95ee\u4e4b\u4e0b\uff0c\u5374\u77e5\u7a46\u4eba\u6e05\u5f80\u5b89\u5e86\u5e9c\u53bb\u4e86\uff0c\u81f3\u4e8e\u5230\u4e86\u5b89\u5e86\u5e9c\u4f55\u5904\uff0c\u5728\u5357\u4eac\u8054\u7edc\u4f20\u8baf\u4e4b\u4eba\u4e5f\u4e0d\u77e5\u60c5\u3002\u8881\u627f\u5fd7\u90c1\u90c1\u4e0d\u4e50\uff0c\u9752\u9752\u62c9\u4ed6\u51fa\u53bb\u6e38\u73a9\uff0c\u4e5f\u662f\u5168\u65e0\u5fc3\u7eea\uff0c\u53ea\u662f\u5750\u5728\u5ba2\u5e97\u4e2d\u53d1\u95f7\u3002 \u3000\u3000\u9752\u9752\u628a\u5e97\u4f19\u53eb\u6765\uff0c\u8be2\u95ee\u9b4f\u56fd\u516c\u5e9c\u7684\u6240\u5728\u3002\u90a3\u5e97\u4f19\u832b\u7136\u4e0d\u77e5\uff0c\u8bf4\u5357\u4eac\u54ea\u91cc\u6709\u751a\u4e48\u9b4f\u56fd\u516c\u5e9c\u3002\u9752\u9752\u607c\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u9b4f\u56fd\u516c\u662f\u672c\u671d\u7b2c\u4e00\u5927\u529f\u81e3\uff0c\u600e\u4f1a\u6ca1\u56fd\u516c\u5e9c\uff1f\u201d\u5e97\u4f19\u9053\uff1a\u201c\u8981\u662f\u6709\uff0c\u76f8\u516c\u81ea\u5df1\u53bb\u627e\u5427\u3002\u5c0f\u4eba\u751f\u5728\u5357\u4eac\uff0c\u957f\u5728\u5357\u4eac\uff0c\u5728\u5357\u4eac\u4f4f\u4e86\u56db\u5341\u591a\u5e74\uff0c\u53ef\u5c31\u662f\u6ca1\u542c\u89c1\u8fc7\u3002\u201d\u9752\u9752\u602a\u4ed6\u9876\u649e\uff0c\u4f38\u624b\u8981\u6253\uff0c\u7ed9\u8881\u627f\u5fd7\u62e6\u4f4f\u3002\u90a3\u5e97\u4f19\u5520\u5520\u53e8\u53e8\u7684\u53bb\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u5728\u5357\u4eac\u5bfb\u8bbf\u4e86\u4e03\u516b\u5929\uff0c\u6ca1\u627e\u5230\u4e1d\u6beb\u7ebf\u7d22\u3002\u8881\u627f\u5fd7\u4fbf\u8981\u53bb\u5b89\u5e86\u5e9c\u5bfb\u5e08\uff0c\u9752\u9752\u8bf4\u65e2\u7136\u5230\u4e86\u5357\u4eac\uff0c\u603b\u5f97\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u624d\u7f62\u3002\u4e24\u4eba\u53c8\u63a2\u95ee\u4e86\u4e94\u516d\u65e5\u3002\u6709\u4eba\u8bf4\u5f90\u5927\u5c06\u519b\u7684\u540e\u4eba\u5728\u6c38\u4e50\u7687\u5e1d\u65f6\u6539\u5c01\u5b9a\u56fd\u516c\uff0c\u542c\u8bf4\u73b0\u4eca\u662f\u5728\u5317\u4eac\u3002\u6709\u4eba\u8bf4\uff1a\u5927\u5c06\u519b\u901d\u4e16\u540e\u8ffd\u8d60\u4e2d\u5c71\u738b\uff0c\u5357\u4eac\u953a\u5c71\u6709\u4e2d\u5c71\u738b\u5893\uff0c\u4e24\u4f4d\u8981\u4e0d\u8981\u53bb\u77a7\u77a7\uff1f \u3000\u3000\u53c8\u6709\u4eba\u8bf4\uff0c\u5357\u4eac\u5b88\u5907\u56fd\u516c\u7237\u5012\u662f\u59d3\u5f90\uff0c\u4f46\u4ed6\u4f4f\u5728\u5b88\u5907\u5e9c\uff0c\u5374\u4e0d\u77e5\u9b4f\u56fd\u516c\u5e9c\u5728\u54ea\u91cc\u3002\u4e24\u4eba\u53bb\u5b88\u5907\u5e9c\u5bdf\u770b\uff0c\u5374\u89c1\u8ddf\u5730\u56fe\u4e0a\u6240\u7ed8\u5168\u7136\u4e0d\u5bf9\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u4e24\u4eba\u96c7\u4e86\u8258\u6cb3\u8239\uff0c\u5728\u79e6\u6dee\u6cb3\u4e2d\u6e38\u6cb3\u89e3\u95f7\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u7239\u7239\u4f55\u7b49\u82f1\u96c4\uff0c\u4ed6\u5f97\u4e86\u8fd9\u5f20\u5730\u56fe\u5374\u627e\u4e0d\u5230\u5b9d\u85cf\uff0c\u53ef\u89c1\u8fd9\u56de\u4e8b\u672c\u6765\u662f\u5f88\u6e3a\u832b\u7684\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u7239\u7239\u660e\u660e\u8fd9\u6837\u5199\u7740\uff0c\u54ea\u4f1a\u6709\u9519\uff1f\u53c8\u4e0d\u662f\u4e00\u4e24\u91d1\u5b50\u3001\u4e8c\u4e24\u94f6\u5b50\u7684\u4e8b\uff0c\u5f53\u7136\u4e0d\u4f1a\u8f7b\u8f7b\u6613\u6613\u5c31\u80fd\u5f97\u5230\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u518d\u627e\u4e00\u5929\uff0c\u8981\u662f\u4ecd\u65e0\u7aef\u502a\uff0c\u54b1\u4eec\u53ef\u5f97\u8d70\u4e86\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u518d\u627e\u4e09\u5929\uff01\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f9d\u4f60\uff0c\u4e09\u5929\u5c31\u4e09\u5929\u3002\u4f60\u9053\u6211\u4e0d\u60f3\u627e\u5230\u5b9d\u85cf\u4e48\uff1f\u201d \u3000\u3000\u6cb3\u4e2d\u7b1b\u6b4c\u5904\u5904\uff0c\u6868\u58f0\u8f7b\u67d4\uff0c\u706f\u5f71\u6726\u80e7\uff0c\u4f3c\u4e4e\u98ce\u4e2d\u6c34\u91cc\u90fd\u6709\u8102\u7c89\u9999\u6c14\uff0c\u8fd9\u822c\u65d6\u65ce\u98ce\u5149\u8881\u627f\u5fd7\u56fa\u662f\u4ece\u6240\u672a\u5386\uff0c\u9752\u9752\u50fb\u5904\u6d59\u4e1c\uff0c\u5374\u4e5f\u6ca1\u89c1\u8fc7\u8fd9\u7b49\u70df\u6c34\u98ce\u534e\u7684\u6c14\u8c61\u3002\u5979\u559d\u4e86\u51e0\u676f\u9152\uff0c\u8138\u4e0a\u9161\u7ea2\uff0c\u542c\u5f97\u90bb\u8239\u4e0a\u4f20\u6765\u9635\u9635\u6b4c\u58f0\uff0c\u76c8\u76c8\u7b11\u8bed\uff0c\u4e0d\u7981\u6709\u5fae\u91ba\u4e4b\u610f\uff0c\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u53eb\u4e24\u4e2a\u59d0\u513f\u6765\u5531\u66f2\u966a\u9152\u597d\u5417\uff1f\u201d\u8881\u627f\u5fd7\u767b\u65f6\u6ee1\u8138\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u559d\u9189\u4e86\u4e48\uff1f\u8fd9\u4e48\u80e1\u95f9\uff01\u201d \u3000\u3000\u6e38\u8239\u4e0a\u7684\u8239\u592b\u63a5\u53e3\u9053\uff1a\u201c\u5230\u79e6\u6dee\u6cb3\u6765\u73a9\u7684\u76f8\u516c\uff0c\u54ea\u4e00\u4e2a\u4e0d\u53eb\u59d0\u513f\u966a\u9152\uff1f\u4e24\u4f4d\u76f8\u516c\u5982\u6709\u76f8\u719f\u7684\uff0c\u5c0f\u7684\u5c31\u53bb\u53eb\u6765\u3002\u201d\u8881\u627f\u5fd7\u53cc\u624b\u4e71\u6447\uff0c\u8fde\u53eb\uff1a\u201c\u4e0d\u8981\uff0c\u4e0d\u8981\uff01\u201d \u3000\u3000\u9752\u9752\u7b11\u95ee\u8239\u592b\uff1a\u201c\u6cb3\u4e0a\u54ea\u51e0\u4f4d\u59d1\u5a18\u6700\u51fa\u540d\u5440\uff1f\u201d\u8239\u592b\u9053\uff1a\u201c\u8bb2\u5230\u540d\u5934\uff0c\u50cf\u535e\u7389\u4eac\u5566\uff0c\u67f3\u5982\u662f\u5566\uff0c\u8463\u5c0f\u5b9b\u5566\uff0c\u674e\u9999\u541b\u5566\uff0c\u54ea\u4e00\u4f4d\u90fd\u662f\u624d\u8c8c\u53cc\u5168\uff0c\u53c8\u4f1a\u505a\u8bd7\uff0c\u53c8\u4f1a\u5531\u66f2\u7684\u7f8e\u8c8c\u59d1\u5a18\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u628a\u751a\u4e48\u67f3\u5982\u662f\u3001\u8463\u5c0f\u5b9b\u7ed9\u6211\u4eec\u53eb\u4e24\u4e2a\u6765\u5427\u3002\u201d\u8239\u592b\u4f38\u4e86\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u76f8\u516c\u5b9a\u662f\u521d\u6765\u5357\u4eac\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8239\u592b\u9053\uff1a\u201c\u8fd9\u4e9b\u51fa\u540d\u7684\u59d1\u5a18\uff0c\u76f8\u4ea4\u7684\u4e0d\u662f\u738b\u5b59\u516c\u5b50\uff0c\u5c31\u662f\u51fa\u540d\u7684\u8bfb\u4e66\u4eba\u3002\u5bfb\u5e38\u505a\u751f\u610f\u7684\uff0c\u5c31\u662f\u628a\u91d1\u5c71\u94f6\u5c71\u62ac\u53bb\uff0c\u8981\u89c1\u5979\u4eec\u4e00\u9762\uff0c\u4e5f\u672a\u5fc5\u89c1\u5f97\u7740\u5462\uff0c\u53c8\u600e\u968f\u4fbf\u53eb\u5f97\u6765\uff1f\u201d\u9752\u9752\u5550\u9053\uff1a\u201c\u4e00\u4e2a\u5993\u5973\u4e5f\u6709\u8fd9\u4e48\u5927\u7684\u52bf\u6d3e\uff1f\u201d \u3000\u3000\u8239\u592b\u9053\uff1a\u201c\u79e6\u6dee\u6cb3\u91cc\u6709\u7684\u662f\u597d\u59d1\u5a18\uff0c\u5c0f\u7684\u7ed9\u4e24\u4f4d\u76f8\u516c\u53eb\u4e24\u4e2a\u6765\u5427\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u8981\u56de\u53bb\u5566\uff0c\u6539\u5929\u518d\u8bf4\u5427\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u6211\u53ef\u8fd8\u6ca1\u73a9\u591f\uff01\u201d\u5bf9\u8239\u592b\u9053\uff1a\u201c\u4f60\u53eb\u5427\uff01\u201d \u3000\u3000\u90a3\u8239\u592b\u5df4\u4e0d\u5f97\u6709\u8fd9\u4e48\u4e00\u53e5\u8bdd\uff0c\u653e\u5f00\u5589\u5499\u558a\u4e86\u51e0\u58f0\u3002\u4e0d\u591a\u4e00\u523b\uff0c\u4e00\u8258\u82b1\u822b\u4ece\u6cb3\u8fb9\u8f6c\u51fa\uff0c\u4e24\u540d\u6b4c\u5973\u4ece\u8df3\u677f\u4e0a\u8fc7\u6765\uff0c\u5411\u8881\u627f\u5fd7\u4e0e\u9752\u9752\u798f\u4e86\u4e24\u798f\u3002\u8881\u627f\u5fd7\u8d77\u8eab\u56de\u793c\uff0c\u795e\u8272\u5c34\u5c2c\u3002\u9752\u9752\u5374\u5927\u6a21\u5927\u6837\u7684\u7aef\u5750\u4e0d\u52a8\uff0c\u53ea\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u89c1\u8881\u627f\u5fd7\u4e00\u526f\u72fc\u72c8\u6a21\u6837\uff0c\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff0c\u53c8\u60f3\uff1a\u201c\u4ed6\u539f\u662f\u4e2a\u8001\u5b9e\u5934\uff0c\u5c31\u7b97\u5fc3\u91cc\u5bf9\u6211\u597d\uff0c\u6599\u4ed6\u4e5f\u8bf4\u4e0d\u51fa\u53e3\u3002\u201d \u3000\u3000\u90a3\u4e24\u540d\u6b4c\u5973\u59ff\u8272\u5e73\u5eb8\u3002\u4e00\u4e2a\u62ff\u8d77\u7bab\u6765\uff0c\u5439\u4e86\u4e2a\u201c\u6298\u6842\u4ee4\u201d\u7684\u724c\u5b50\uff0c\u5012\u4e5f\u60a0\u626c\u52a8\u542c\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u6b4c\u5973\u5bf9\u9752\u9752\u9053\uff1a\u201c\u76f8\u516c\uff0c\u6211\u4e24\u4eba\u5408\u5531\u4e2a\u2018\u6302\u679d\u513f\u2019\u7ed9\u4f60\u542c\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u597d\u554a\u3002\u201d\u90a3\u6b4c\u5973\u5f39\u8d77\u7435\u7436\uff0c\u5531\u7684\u662f\u7537\u5b50\u8154\u8c03\uff0c\u5531\u9053\uff1a\u201c\u6211\u6559\u4f60\u53eb\u6211\uff0c\u4f60\u53ea\u662f\u4e0d\u5e94\uff0c\u4e0d\u7b49\u6211\u8bf4\u5c31\u53eb\u6211\u624d\u662f\u771f\u60c5\u3002 \u3000\u3000\u8981\u4f60\u53eb\u58f0\u2018\u4eb2\u54e5\u54e5\u2019\uff0c\u63a8\u751a\u4e48\u8138\u7ea2\u7f9e\u4eba\uff1f\u4f60\u53e3\u513f\u91cc\u4e0d\u80af\u53eb\uff0c\u60f3\u662f\u5fc3\u91cc\u513f\u4e0d\u75bc\u3002\u4f60\u82e5\u75bc\u6211\u662f\u771f\u5fc3\u4e5f\uff0c\u4e3a\u4f55\u5f00\u53e3\u96be\u5f97\u7d27\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u5230\u8fd9\u91cc\uff0c\u60f3\u8d77\u81ea\u5df1\u5e73\u65f6\u5e38\u53eb\u201c\u9752\u5f1f\u201d\uff0c\u53ef\u662f\u5979\u4ece\u6765\u5c31\u4e0d\u53eb\u81ea\u5df1\u4e00\u58f0\u201c\u54e5\u54e5\u201d\uff0c\u53ea\u662f\u53eb\u201c\u627f\u5fd7\u5927\u54e5\u201d\uff0c\u8981\u4e0d\u7136\u4fbf\u53eb\u201c\u5927\u54e5\u201d\uff0c\u4e0d\u7531\u5f97\u5411\u9752\u9752\u77a7\u53bb\u3002\u53ea\u89c1\u5979\u8138\u4e0a\u6655\u7ea2\uff0c\u4e5f\u6b63\u5411\u81ea\u5df1\u77a7\u6765\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u89e6\uff0c\u90fd\u611f\u4e0d\u597d\u610f\u601d\uff0c\u540c\u65f6\u8f6c\u5f00\u4e86\u5934\uff0c\u53ea\u542c\u90a3\u6b4c\u5973\u53c8\u5531\u9053\uff1a\u201c\u4fcf\u51a4\u5bb6\uff0c\u975e\u662f\u6211\u597d\u6559\u4f60\u53eb\uff0c\u4f60\u53eb\u58f0\u65e0\u798f\u7684\u4e5f\u81ea\u96be\u6d88\u3002\u4f60\u5fc3\u4e0d\u987a\uff0c\u600e\u80af\u4fbf\u628a\u6211\u6765\u53eb\uff1f\u53eb\u7684\u8fd9\u58f0\u97f3\u513f\u5a07\uff0c\u542c\u7684\u5f80\u5fc3\u7a9d\u91cc\u70e7\u3002\u5c31\u662f\u5047\u610f\u513f\u7684\u6bb7\u52e4\u4e5f\uff0c\u6bd4\u4e0d\u53eb\u5230\u5e95\u597d\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u6b4c\u5973\u4ee5\u5973\u5b50\u8154\u8c03\u63a5\u7740\u5531\u9053\uff1a\u201c\u4fcf\u51a4\u5bb6\uff0c\u4f46\u89c1\u6211\u5c31\u8981\u6211\u53eb\uff0c\u4e00\u4f1a\u513f\u4e0d\u53eb\u4f60\uff0c\u4f60\u5c31\u5fc3\u7126\u3002 \u3000\u3000\u6211\u75bc\u4f60\u54ea\u5728\u4e4e\u53eb\u4e0e\u4e0d\u53eb\u3002\u53eb\u662f\u53e3\u4e2d\u6b22\uff0c\u75bc\u662f\u5fc3\u60f3\u7740\u3002\u6211\u82e5\u75bc\u4f60\u662f\u771f\u5fc3\u4e5f\uff0c\u5c31\u4e0d\u53eb\u4e5f\u662f\u597d\u3002\u201d \u3000\u3000\u6b4c\u58f0\u5a07\u5a9a\uff0c\u8881\u627f\u5fd7\u548c\u9752\u9752\u542c\u4e86\uff0c\u90fd\u4e0d\u7531\u5f97\u5fc3\u795e\u8361\u6f3e\u3002\u53ea\u542c\u90a3\u5531\u7537\u8154\u7684\u6b4c\u5973\u5531\u9053\uff1a\u201c\u6211\u53ea\u76fc\uff0c\u4f46\u89c1\u4f60\u5c31\u542c\u4f60\u53eb\uff0c\u4f60\u5374\u662f\u6015\u542c\u89c1\u7684\u5411\u65c1\u4eba\u5b66\u3002\u624d\u5f85\u53eb\u53c8\u4e0d\u53eb\uff0c\u53ea\u662f\u4f4e\u7740\u5934\u513f\u7b11\uff0c\u4e00\u9762\u4f4e\u4f4e\u53eb\uff0c\u4e00\u9762\u628a\u4eba\u77a7\u3002\u53eb\u5f97\u867d\u7136\u8270\u96be\u4e5f\uff0c\u5fc3\u610f\u513f\u5176\u5b9e\u597d\u3002\u201d \u3000\u3000\u4e24\u4eba\u6700\u540e\u5408\u5531\uff1a\u201c\u6211\u82e5\u75bc\u4f60\u662f\u771f\u5fc3\u4e5f\uff0c\u4fbf\u4e0d\u53eb\u4e5f\u662f\u597d\uff01\u201d\u7435\u7436\u738e\u738e\u7424\u7424\uff0c\u8f7b\u67d4\u6d41\u8361\uff0c\u4e00\u58f0\u58f0\u6311\u4eba\u5fc3\u5f26\uff0c\u886c\u7740\u66f2\u8bcd\uff0c\u5f53\u771f\u5982\u871c\u7cd6\u91cc\u8c03\u6cb9\u3001\u80ed\u8102\u4e2d\u63ba\u7c89\uff0c\u53c8\u751c\u53c8\u817b\uff0c\u53c8\u9999\u53c8\u5a07\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u751f\u4e0e\u5200\u5251\u4e3a\u4f0d\uff0c\u8bc6\u5f97\u9752\u9752\u4e4b\u524d\uff0c\u7ed3\u4ea4\u7684\u90fd\u662f\u8c6a\u723d\u7537\u513f\uff0c\u54ea\u60f3\u5f97\u5230\u5355\u662f\u53eb\u8fd9\u4e48\u4e00\u58f0\uff0c\u5176\u4e2d\u4fbf\u6709\u8fd9\u8bb8\u591a\u8bb2\u7a76\uff0c\u60f3\u5230\u66f2\u4e2d\u7f20\u7ef5\u4e4b\u610f\uff0c\u7ef8\u7f2a\u4e4b\u60c5\uff0c\u4e0d\u7981\u5fc3\u4e2d\u6026\u6026\u4f5c\u8df3\u3002 \u3000\u3000\u9752\u9752\u773c\u76ae\u4f4e\u5782\uff0c\u4ece\u90a3\u6b4c\u5973\u624b\u4e2d\u63a5\u8fc7\u7bab\u6765\uff0c\u62ff\u624b\u5e15\u91ae\u4e86\u9152\uff0c\u5728\u5439\u53e3\u5904\u64e6\u5e72\u51c0\u4e86\uff0c\u63a5\u5634\u5410\u6c14\uff0c\u5439\u4e86\u8d77\u6765\u3002\u8881\u627f\u5fd7\u5f53\u65e5\u5728\u77f3\u6881\u73ab\u7470\u5761\u4e0a\u66fe\u542c\u5979\u5439\u7bab\uff0c\u8fd9\u65f6\u6cb3\u4e0a\u6ce2\u5149\u6708\u5f71\uff0c\u9152\u6d53\u8102\u9999\uff0c\u53c8\u662f\u4e00\u756a\u5149\u666f\uff0c\u7bab\u58f0\u5a49\u8f6c\u6e05\u626c\uff0c\u5439\u7684\u6b63\u662f\u90a3\u201c\u6302\u679d\u513f\u201d\u66f2\u8c03\uff0c\u60f3\u5230\u201c\u6211\u82e5\u75bc\u4f60\u662f\u771f\u5fc3\u4e5f\uff0c\u4fbf\u4e0d\u53eb\u4e5f\u662f\u597d\u201d\u90a3\u4e24\u53e5\uff0c\u706f\u4e0b\u89c1\u5230\u9752\u9752\u7684\u4e3d\u8272\uff0c\u4e0d\u89c9\u5fc3\u795e\u4ff1\u9189\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u5f97\u51fa\u795e\uff0c\u6ca1\u53d1\u89c9\u4e00\u8258\u5927\u82b1\u822b\u5df2\u9760\u5230\u8239\u8fb9\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u597d\u7bab\uff0c\u597d\u7bab\uff01\u201d\u63a5\u7740\u4e09\u4e2a\u4eba\u8de8\u4e0a\u8239\u6765\u3002\u9752\u9752\u89c1\u6709\u4eba\u6253\u6270\uff0c\u5fc3\u5934\u605a\u6012\uff0c\u653e\u4e0b\u7bab\u7ba1\uff0c\u4fa7\u76ee\u659c\u89c6\u3002\u89c1\u4e0a\u6765\u4e09\u4eba\u4e2d\u524d\u9762\u4e00\u4eba\u6447\u7740\u6298\u6247\uff0c\u6ee1\u8eab\u9526\u7ee3\uff0c\u4e09\u5341\u6765\u5c81\u5e74\u7eaa\uff0c\u751f\u5f97\u7ec6\u7709\u7ec6\u773c\uff0c\u76ae\u8089\u6bd4\u4e4b\u90a3\u4e24\u4e2a\u6b4c\u5973\u8fd8\u767d\u4e86\u4e09\u5206\u3002\u540e\u9762\u8ddf\u7740\u4e24\u4e2a\u5bb6\u4e01\uff0c\u63d0\u7740\u7684\u706f\u7b3c\u4e0a\u9762\u5199\u7740\u201c\u603b\u7763\u5e9c\u201d\u4e09\u4e2a\u7ea2\u5b57\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7ad9\u8d77\u6765\u62f1\u624b\u76f8\u8fce\u3002\u4e24\u540d\u6b4c\u5973\u53e9\u4e0b\u5934\u53bb\u3002\u9752\u9752\u5374\u4e0d\u7406\u776c\u3002 \u3000\u3000\u90a3\u4eba\u4e00\u9762\u5927\u7b11\uff0c\u4e00\u9762\u8d70\u8fdb\u8239\u8231\uff0c\u8bf4\u9053\uff1a\u201c\u6253\u6270\u4e86\uff0c\u6253\u6270\u4e86\uff01\u201d \u3000\u3000\u5927\u523a\u523a\u7684\u5750\u4e86\u4e0b\u6765\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8bf7\u95ee\u5c0a\u59d3\u5927\u540d\u3002\u201d\u90a3\u4eba\u8fd8\u6ca1\u56de\u7b54\uff0c\u4e00\u4e2a\u6b4c\u5973\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u51e4\u9633\u603b\u7763\u5e9c\u7684\u9a6c\u516c\u5b50\u3002\u79e6\u6dee\u6cb3\u4e0a\u6709\u540d\u7684\u9614\u5c11\u3002\u201d\u9a6c\u516c\u5b50\u4e5f\u4e0d\u95ee\u8881\u627f\u5fd7\u59d3\u540d\uff0c\u4e00\u53cc\u8272\u8ff7\u8ff7\u7684\u773c\u775b\u5c3d\u5728\u9752\u9752\u7684\u8138\u4e0a\u6e9c\u6765\u6e9c\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u54ea\u4e2a\u73ed\u5b50\u91cc\u7684\uff1f\u5012\u5439\u5f97\u597d\u7bab\uff0c\u600e\u4e0d\u6765\u4f3a\u5019\u6211\u5927\u7237\u554a\uff1f\u54c8\u54c8\uff01\u201d \u3000\u3000\u9752\u9752\u542c\u4ed6\u628a\u81ea\u5df1\u5f53\u4f5c\u4f18\u4f36\u4e50\u5320\uff0c\u67f3\u7709\u4e00\u633a\uff0c\u5f53\u573a\u4fbf\u8981\u53d1\u4f5c\u3002\u8881\u627f\u5fd7\u5411\u5979\u8fde\u4f7f\u773c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u5144\u5f1f\uff0c\u6211\u4eec\u662f\u5230\u5357\u4eac\u6765\u8bbf\u53cb\u7684\u3002\u201d\u9a6c\u516c\u5b50\u7b11\u9053\uff1a\u201c\u8bbf\u751a\u4e48\u53cb\uff1f\u4eca\u65e5\u9047\u89c1\u4e86\u6211\uff0c\u4ea4\u4e86\u4f60\u516c\u5b50\u7237\u8fd9\u4e2a\u670b\u53cb\uff0c\u4f60\u4eec\u5c31\u5403\u7740\u4e0d\u5c3d\u4e86\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u4e2d\u607c\u6012\uff0c\u6de1\u6de1\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u5728\u603b\u7763\u5e9c\u505a\u751a\u4e48\u5b98\uff1f\u201d\u9a6c\u516c\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u603b\u7763\u9a6c\u5927\u4eba\uff0c\u4fbf\u662f\u5bb6\u53d4\u3002\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u8fb9\u82b1\u822b\u4e0a\u53c8\u8fc7\u6765\u4e00\u4eba\uff0c\u90a3\u4eba\u7a7f\u7740\u4e00\u8eab\u85d5\u8272\u719f\u7f57\u957f\u888d\uff0c\u8eab\u6750\u77ee\u5c0f\uff0c\u7559\u4e86\u4e24\u6487\u5c0f\u80e1\u5b50\uff0c\u795e\u60c5\u5374\u662f\u4e00\u56e2\u548c\u6c14\uff0c\u5411\u9a6c\u516c\u5b50\u7b11\u9053\uff1a\u201c\u516c\u5b50\u7237\uff0c\u8fd9\u5144\u5f1f\u7684\u7bab\u5439\u5f97\u4e0d\u9519\u5427\uff1f\u201d\u8881\u627f\u5fd7\u77a7\u4ed6\u6a21\u6837\uff0c\u6599\u60f3\u4ed6\u662f\u9a6c\u516c\u5b50\u8eab\u8fb9\u7684\u6e05\u5ba2\u3002\u9a6c\u516c\u5b50\u9053\uff1a\u201c\u666f\u4ead\uff0c\u4f60\u8ddf\u4ed6\u4eec\u8bf4\u8bf4\u3002\u201d \u3000\u3000\u90a3\u4eba\u81ea\u79f0\u59d3\u6768\u540d\u666f\u4ead\uff0c\u5f53\u4e0b\u558f\u558f\u8fde\u58f0\uff0c\u5bf9\u8881\u590f\u4e8c\u4eba\u9053\uff1a\u201c\u9a6c\u516c\u5b50\u662f\u51e4\u9633\u603b\u7763\u9a6c\u5927\u4eba\u7684\u4eb2\u4f84\u513f\uff0c\u4ea4\u670b\u53cb\u662f\u6700\u70ed\u5fc3\u4e0d\u8fc7\u7684\uff0c\u4e00\u63b7\u5343\u91d1\uff0c\u6beb\u65e0\u541d\u8272\u3002\u8c01\u4ea4\u5230\u4e86\u8fd9\u4f4d\u670b\u53cb\uff0c\u90a3\u771f\u662f\u4e00\u4ea4\u8dcc\u5165\u9752\u4e91\u91cc\u53bb\u5566\u3002\u9a6c\u5927\u4eba\u6700\u5ba0\u7231\u8fd9\u4e2a\u4f84\u513f\uff0c\u5f85\u4ed6\u6bd4\u4eb2\u751f\u513f\u5b50\u8fd8\u597d\uff0c\u8fd9\u4f4d\u5144\u5f1f\u8981\u4ea4\u670b\u53cb\u561b\uff0c\u6700\u597d\u5c31\u642c\u5230\u9a6c\u516c\u5b50\u5e9c\u91cc\u53bb\u4f4f\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u4eec\u51fa\u8a00\u4e0d\u900a\uff0c\u751f\u6015\u9752\u9752\u53d1\u6012\uff0c\u54ea\u77e5\u9752\u9752\u5374\u7b11\u9010\u989c\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\uff0c\u54b1\u4eec\u8fd9\u5c31\u4e0a\u5cb8\u53bb\u5427\u3002\u201d\u9a6c\u516c\u5b50\u5927\u559c\uff0c\u4f38\u624b\u53bb\u62c9\u5979\u624b\u3002\u9752\u9752\u4e00\u7f29\uff0c\u628a\u4e00\u540d\u6b4c\u5973\u5f80\u4ed6\u8eab\u4e0a\u63a8\u53bb\u3002\u8881\u627f\u5fd7\u5927\u5947\uff0c\u5f53\u4e0b\u9ed8\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u9752\u9752\u7ad9\u8d77\u8eab\u6765\uff0c\u5bf9\u9a6c\u516c\u5b50\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u59d1\u5a18\u548c\u8239\u5bb6\uff0c\u5c0f\u5f1f\u60f3\u6bcf\u4eba\u6253\u8d4f\u4e94\u4e24\u94f6\u5b50\u2026\u2026\u201d\u9a6c\u516c\u5b50\u5fd9\u9053\uff1a\u201c\u5f53\u7136\u662f\u5144\u5f1f\u7ed9\uff0c\u4f60\u4eec\u660e\u513f\u5230\u8d26\u623f\u6765\u9886\u8d4f\uff01\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u4eca\u513f\u8d4f\u4e86\u4ed6\u4eec\uff0c\u5c82\u4e0d\u723d\u5feb\uff1f\u201d\u9a6c\u516c\u5b50\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u624b\u4e00\u6446\uff0c\u5bb6\u4e01\u5df2\u53d6\u51fa\u5341\u4e94\u4e24\u94f6\u5b50\u653e\u5728\u684c\u4e0a\u3002\u8239\u592b\u4e0e\u4e24\u540d\u6b4c\u5973\u8c22\u4e86\u3002\u9a6c\u516c\u5b50\u76ee\u4e0d\u8f6c\u775b\u7684\u671b\u7740\u9752\u9752\uff0c\u7709\u5f00\u773c\u7b11\uff0c\u5fc3\u75d2\u96be\u6414\uff0c\u5f53\u771f\u5982\u540c\u6361\u5230\u4e86\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u5947\u73cd\u5f02\u5b9d\u4e00\u822c\u3002\u4e0d\u4e00\u4f1a\uff0c\u8239\u5df2\u62e2\u5cb8\u3002\u6768\u666f\u4ead\u9053\uff1a\u201c\u6211\u53bb\u53eb\u8f7f\u5b50\uff01\u201d \u3000\u3000\u9752\u9752\u5ffd\u9053\uff1a\u201c\u554a\u54df\uff0c\u6211\u6709\u4e00\u4ef6\u8981\u7d27\u7269\u4e8b\u653e\u5728\u4e0b\u5904\uff0c\u8fd9\u5c31\u8981\u53bb\u62ff\u3002\u201d \u3000\u3000\u9a6c\u516c\u5b50\u9053\uff1a\u201c\u6211\u5dee\u5bb6\u4eba\u7ed9\u4f60\u53bb\u53d6\u597d\u5566\uff0c\u597d\u5144\u5f1f\uff0c\u4f60\u4f4f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u6211\u5728\u592a\u5e73\u95e8\u8986\u821f\u5c71\u7684\u548c\u5c1a\u5e99\u91cc\u501f\u4f4f\u3002\u8fd9\u4e1c\u897f\u53ef\u4e0d\u80fd\u8ba9\u522b\u4eba\u53bb\u62ff\u3002\u201d \u3000\u3000\u6768\u666f\u4ead\u5728\u9a6c\u516c\u5b50\u8033\u8fb9\u4f4e\u58f0\u9053\uff1a\u201c\u9489\u7740\u4ed6\uff0c\u522b\u8ba9\u8fd9\u5b69\u5b50\u6e9c\u4e86\uff1f\u201d\u9a6c\u516c\u5b50\u7728\u7728\u773c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff01\u201d\u8f6c\u5934\u5bf9\u9752\u9752\u9053\uff1a\u201c\u90a3\u4e48\u597d\u5144\u5f1f\uff0c\u6211\u548c\u4f60\u4e00\u8d77\u53bb\u5427\uff01\u201d\u8bf4\u7740\u4f38\u624b\u53bb\u6402\u5979\u80a9\u818a\u3002\u9752\u9752\u55e4\u7684\u4e00\u7b11\u3002\u5411\u65c1\u4e00\u907f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u8981\u4f60\u53bb\uff01\u201d \u3000\u3000\u9a6c\u516c\u5b50\u795e\u9b42\u98d8\u8361\uff0c\u5bf9\u6768\u666f\u4ead\u9053\uff1a\u201c\u666f\u4ead\uff0c\u8fd9\u5b69\u5b50\u82e5\u662f\u7a7f\u4e0a\u4e86\u5973\u88c5\uff0c\u91d1\u9675\u57ce\u91cc\u6ca1\u4e00\u4e2a\u5a18\u4eec\u80fd\u6bd4\u5f97\u4e0a\u3002\u5929\u4e0b\u5c45\u7136\u6709\u8fd9\u7b49\u7edd\u8272\u5c11\u5e74\uff0c\u4eca\u65e5\u5374\u53eb\u6211\u9047\u4e0a\u4e86\uff01\u771f\u662f\u7956\u5b97\u79ef\u5fb7\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u53bb\u5427\uff01\u201d\u633d\u4e86\u8881\u627f\u5fd7\u7684\u624b\u4fbf\u8d70\u3002\u9a6c\u516c\u5b50\u4e00\u4f7f\u773c\u8272\uff0c\u56db\u4eba\u90fd\u8ddf\u5728\u540e\u9762\u3002\u4ed6\u62a2\u4e0a\u51e0\u6b65\uff0c\u548c\u9752\u9752\u8bf4\u7b11\u3002 \u3000\u3000\u9752\u9752\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u7684\u8ddf\u4ed6\u95f2\u8c08\u3002 \u3000\u3000\u9752\u9752\u4e0e\u8881\u627f\u5fd7\u4e3a\u4e86\u5bfb\u8bbf\u9b4f\u56fd\u516c\u5e9c\uff0c\u5341\u591a\u5929\u6765\u5357\u4eac\u57ce\u5185\u57ce\u5916\u3001\u5927\u8857\u5c0f\u5df7\u90fd\u8d70\u904d\u4e86\uff0c\u4e8e\u9053\u8def\u5df2\u5f88\u719f\u6089\u3002\u8881\u627f\u5fd7\u89c1\u5979\u5c3d\u5f80\u8352\u50fb\u4e4b\u5730\u8d70\u53bb\uff0c\u77e5\u5979\u5df2\u751f\u6740\u673a\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u9a6c\u516c\u5b50\u867d\u7136\u65e0\u884c\uff0c\u4f46\u770b\u9519\u4e86\u4eba\uff0c\u5374\u662f\u7f6a\u4e0d\u81f4\u6b7b\u3002\u5e08\u7236\u5e38\u8bf4\uff0c\u5b66\u6b66\u4e4b\u4eba\u4e0d\u80fd\u6ee5\u6740\u65e0\u8f9c\uff0c\u6211\u5c82\u53ef\u4e0d\u963b\uff1f\u201d\u4e8e\u662f\u505c\u6b65\u8bf4\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u522b\u8ddf\u9a6c\u516c\u5b50\u5f00\u73a9\u7b11\u4e86\uff0c\u54b1\u4eec\u56de\u5ba2\u5e97\u53bb\u5427\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u4f60\u4e00\u4eba\u5148\u56de\u53bb\uff01\u201d\u9a6c\u516c\u5b50\u5927\u559c\uff0c\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u4f60\u4e00\u4e2a\u4eba\u56de\u53bb\u3002\u4f60\u8981\u4e0d\u8981\u94f6\u5b50\u4f7f\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u6447\u5934\u53f9\u606f\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u8bf4\u56de\u5ba2\u5e97\uff0c\u5df2\u70b9\u540d\u5e76\u975e\u5728\u8986\u821f\u5c71\u548c\u5c1a\u5e99\u501f\u4f4f\u3002\u8fd9\u4eba\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u662f\u4e0d\u609f\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5230\u4e86\u4e00\u7247\u575f\u573a\uff0c\u9a6c\u516c\u5b50\u5df2\u8d70\u5f97\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u5feb\u2026\u2026\u5feb\u5230\u4e86\u5417\uff1f\u201d\u9752\u9752\u4e00\u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5df2\u7ecf\u5230\u5566\uff01\u201d\u9a6c\u516c\u5b50\u4e00\u6123\uff0c\u5fc3\u60f3\u5230\u8fd9\u575f\u5806\u4e2d\u6765\u5e72\u751a\u4e48\u3002\u90a3\u7bfe\u7247\u6768\u666f\u4ead\u770b\u51fa\u60c5\u5f62\u6709\u4e9b\u513f\u4e0d\u5bf9\uff0c\u4f46\u60f3\u6211\u4eec\u5171\u6709\u56db\u4eba\uff0c\u4e24\u540d\u5bb6\u4e01\u53c8\u662f\u5b54\u6b66\u6709\u529b\uff0c\u8c05\u8fd9\u4e24\u4e2a\u6587\u5f31\u5c11\u5e74\u4e5f\u4f7f\u4e0d\u51fa\u751a\u4e48\u5978\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u522b\u95f9\u7740\u73a9\u4e86\uff0c\u5927\u4f19\u513f\u53bb\u516c\u5b50\u5e9c\u91cc\uff0c\u70ed\u70d8\u70d8\u7684\u559d\u4e24\u76c5\u4e50\u4e0a\u4e00\u4e50\uff0c\u4f60\u7ed9\u5927\u4f19\u5531\u4e0a\u51e0\u652f\u66f2\u513f\uff0c\u5c82\u4e0d\u662f\u597d\uff1f\u201d\u9752\u9752\u51b7\u7b11\u4e24\u58f0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u559d\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u8d70\u3002\u505a\u4eba\u89c4\u89c4\u77e9\u77e9\u7684\uff0c\u4fbf\u5c11\u78b0\u4e9b\u9489\u5b50\u3002\u201d\u6768\u666f\u4ead\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u60f9\u538c\u5f97\u5f88\uff0c\u8fd8\u662f\u81ea\u5df1\u89c4\u89c4\u77e9\u77e9\u7684\u5148\u56de\u53bb\u5427\uff01\u522b\u62db\u5f97\u9a6c\u516c\u5b50\u751f\u6c14\u3002\u201d\u9a6c\u516c\u5b50\u8bc8\u766b\u7eb3\u798f\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5144\u5f1f\uff0c\u6211\u7d2f\u5566\uff0c\u4f60\u6276\u6211\u4e00\u628a\uff01\u201d\u6328\u8fd1\u9752\u9752\u8eab\u65c1\uff0c\u4f38\u53f3\u81c2\u5f80\u5979\u80a9\u5934\u642d\u53bb\u3002 \u3000\u3000\u9752\u9752\u8eab\u5b50\u4e00\u4fa7\uff0c\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u54e5\uff0c\u90a3\u8fb9\u662f\u751a\u4e48\uff1f\u201d\u4f38\u624b\u4e1c\u6307\u3002\u8881\u627f\u5fd7\u8f6c\u8fc7\u5934\u53bb\u4e00\u671b\uff0c\u53ea\u542c\u5f97\u80cc\u540e\u55e4\u5f97\u4e00\u58f0\u54cd\uff0c\u6025\u5fd9\u56de\u5934\uff0c\u9a6c\u516c\u5b50\u90a3\u9897\u80e1\u6d82\u8111\u888b\u5df2\u6eda\u4e0b\u5730\u6765\uff0c\u9888\u5b50\u4e2d\u9c9c\u8840\u76f4\u55b7\u3002 \u3000\u3000\u6768\u666f\u4ead\u548c\u4e24\u4e2a\u5bb6\u4e01\u90fd\u60ca\u5446\u4e86\u3002\u9752\u9752\u4e0a\u524d\u4e00\u5251\u4e00\u4e2a\uff0c\u5168\u90fd\u523a\u6b7b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\u65e2\u5df2\u6740\u4e86\u4e00\u4e2a\uff0c\u5f62\u8ff9\u5df2\u9732\uff0c\u7d22\u6027\u65a9\u8349\u9664\u6839\uff0c\u4ee5\u514d\u540e\u60a3\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u963b\u6321\u3002 \u3000\u3000\u9752\u9752\u5728\u9a6c\u516c\u5b50\u8eab\u4e0a\u62ed\u4e86\u5251\u4e0a\u8840\u8ff9\uff0c\u563b\u563b\u5a07\u7b11\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u6253\u4ed6\u4e00\u987f\uff0c\u6559\u8bad\u6559\u8bad\u4e5f\u5c31\u591f\u4e86\uff0c\u4f60\u4e5f\u5fd2\u72e0\u4e86\u4e00\u70b9\u3002\u201d\u9752\u9752\u773c\u4e00\u6a2a\uff0c\u9053\uff1a\u201c\u8fd9\u810f\u6c14\u6211\u53ef\u53d7\u4e0d\u4e0b\u3002\u54b1\u4e24\u4e2a\u5728\u6cb3\u4e0a\u5439\u7bab\u542c\u66f2\uff0c\u73a9\u5f97\u591a\u597d\uff0c\u8fd9\u5bb6\u4f19\u5374\u6765\u626b\u5174\uff0c\u4f60\u8bf4\u4ed6\u8be5\u4e0d\u8be5\u6b7b\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\u5355\u662f\u6253\u6270\u626b\u5174\uff0c\u81ea\u7136\u8bf4\u4e0d\u4e0a\u8be5\u6b7b\uff0c\u4f46\u9a6c\u516c\u5b50\u8fd9\u79cd\u4eba\u4ed7\u52bf\u6a2a\u884c\uff0c\u4f24\u5929\u5bb3\u7406\u4e4b\u4e8b\u5b9a\u662f\u505a\u4e86\u4e0d\u5c11\uff0c\u6740\u4e86\u4ed6\u4e5f\u4e0d\u80fd\u8bf4\u6ee5\u6740\u65e0\u8f9c\uff0c\u4e8e\u662f\u6b63\u8272\u9053\uff1a\u201c\u8fd9\u6837\u7684\u574f\u86cb\uff0c\u6740\u5c31\u6740\u4e86\uff0c\u53ef\u662f\u4f60\u5c06\u6765\u4e71\u6740\u4e00\u4e2a\u597d\u4eba\uff0c\u54b1\u4eec\u7684\u4ea4\u60c5\u5c31\u6b64\u5b8c\u4e86\u3002\u201d\u9752\u9752\u5410\u4e86\u5410\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u6562\uff01\u201d \u3000\u3000\u4e24\u4eba\u628a\u5c38\u9996\u8e22\u5728\u8349\u4e1b\u4e4b\u4e2d\uff0c\u6b63\u8981\u56de\u5f52\u5ba2\u5e97\uff0c\u8881\u627f\u5fd7\u5ffd\u7136\u5728\u9752\u9752\u8863\u8896\u4e0a\u626f\u4e86\u4e00\u628a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6709\u4eba\uff01\u201d\u4e24\u4eba\u5f53\u7f29\u8eab\u8eb2\u5728\u4e00\u5ea7\u575f\u5893\u4e4b\u540e\u3002 \u3000\u3000\u53ea\u542c\u5f97\u8fdc\u5904\u811a\u6b65\u58f0\u54cd\uff0c\u4e1c\u9762\u548c\u897f\u9762\u90fd\u6709\u4eba\u8fc7\u6765\u3002\u4e24\u4eba\u4ece\u575f\u540e\u63a2\u773c\u76f8\u671b\uff0c\u89c1\u4e24\u8fb9\u5404\u6709\u5341\u591a\u4eba\uff0c\u63d0\u7740\u6cb9\u7eb8\u706f\u7b3c\u3002\u53cc\u65b9\u6e10\u884c\u6e10\u8fd1\uff0c\u4e1c\u9762\u7684\u4eba\u51fb\u638c\u4e09\u4e0b\uff0c\u505c\u4e00\u505c\uff0c\u53c8\u51fb\u4e24\u4e0b\u3002\u897f\u8fb9\u7684\u4eba\u4e5f\u51fb\u638c\u4e09\u4e0b\uff0c\u8ddf\u7740\u53c8\u51fb\u4e24\u4e0b\uff0c\u8d70\u8fd1\u805a\u5728\u4e00\u8d77\uff0c\u56f4\u5750\u5728\u4e00\u5ea7\u5927\u575f\u4e4b\u524d\u3002\u6240\u5750\u4e4b\u5904\uff0c\u4e0e\u4e24\u4eba\u76f8\u8ddd\u5341\u591a\u4e08\uff0c\u8bf4\u8bdd\u542c\u4e0d\u6e05\u695a\u3002\u9752\u9752\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u60f3\u6328\u8fd1\u53bb\u542c\u3002\u8881\u627f\u5fd7\u62c9\u4f4f\u5979\u8863\u8896\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7b49\u4e00\u4e0b\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u7b49\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u6447\u624b\u793a\u610f\u3002\u53eb\u5979\u522b\u4f5c\u58f0\u3002\u9752\u9752\u7b49\u5f97\u5f88\u4e0d\u8010\u70e6\u3002 \u3000\u3000\u7ea6\u83ab\u8fc7\u4e86\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u4e00\u9635\u75be\u98ce\u5439\u6765\uff0c\u56db\u4e0b\u957f\u8349\u745f\u745f\u4f5c\u58f0\uff0c\u575f\u8fb9\u7684\u677e\u67cf\u679d\u6761\u98de\u821e\u3002\u8881\u627f\u5fd7\u6258\u7740\u9752\u9752\u53f3\u81c2\uff0c\u65bd\u5c55\u8f7b\u529f\uff0c\u7adf\u4e0d\u957f\u8eab\uff0c\u72b9\u5982\u811a\u4e0d\u70b9\u5730\u822c\u5954\u51fa\u5341\u591a\u4e08\uff0c\u5230\u4e86\u90a3\u6279\u4eba\u8eab\u540e\u4e00\u5ea7\u575f\u540e\u4f0f\u4e0b\u3002\u8fd9\u65f6\u98ce\u58f0\u672a\u606f\uff0c\u90a3\u4e9b\u4eba\u4e1d\u6beb\u4e0d\u89c9\uff0c\u4e24\u4eba\u4e00\u4f0f\u4e0b\uff0c\u8881\u627f\u5fd7\u7acb\u5373\u628a\u624b\u7f29\u56de\uff0c\u5982\u907f\u86c7\u874e\u3002\u9752\u9752\u5fc3\u60f3\uff1a\u201c\u4ed6\u786e\u662f\u4e2a\u5fd7\u8bda\u541b\u5b50\uff0c\u53ea\u662f\u4e5f\u672a\u514d\u592a\u53e4\u677f\u4e86\u4e9b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u548c\u4f17\u4eba\u76f8\u8ddd\u5df2\u4e0d\u8fc7\u4e09\u4e08\uff0c\u53ea\u542c\u4e00\u4e2a\u55d3\u5b50\u5fae\u6c99\u7684\u4eba\u9053\uff1a\u201c\u8d35\u6d3e\u5404\u4f4d\u5927\u54e5\u8fdc\u9053\u800c\u6765\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u5144\u5f1f\u5b9e\u5728\u4e07\u5206\u611f\u6fc0\u3002\u201d\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u6211\u5e08\u7236\u8bf4\u9053\uff0c\u95f5\u8001\u5e08\u89c1\u62db\u3002\u672c\u5f53\u4eb2\u6765\uff0c\u53ea\u662f\u4ed6\u8001\u4eba\u5bb6\u5367\u75c5\u5df2\u4e00\u4e2a\u591a\u6708\uff0c\u8d77\u4e0d\u4e86\u5e8a\uff0c\u56e0\u6b64\u4e0a\u8bf7\u4e07\u5e08\u53d4\u5e26\u9886\u6211\u4eec\u5341\u4e8c\u5f1f\u5b50\uff0c\u6765\u4f9b\u95f5\u8001\u5e08\u5dee\u9063\u3002\u201d\u90a3\u6c99\u55d3\u5b50\u7684\u4eba\u9053\uff1a\u201c\u5c0a\u5e08\u9f99\u8001\u7237\u5b50\u7684\u8d35\u6059\uff0c\u53ea\u76fc\u53ca\u65e9\u75ca\u6108\u3002\u6b64\u95f4\u5927\u4e8b\u4e00\u4e86\uff0c\u5144\u5f1f\u5f53\u4eb2\u53bb\u4e91\u5357\uff0c\u5411\u9f99\u8001\u7237\u5b50\u95ee\u5b89\u9053\u8c22\u3002\u8ffd\u98ce\u5251\u4e07\u5e08\u5144\u5251\u6cd5\u901a\u795e\uff0c\u5a01\u9707\u5929\u5357\uff0c\u5144\u5f1f\u4e00\u89c1\u4e07\u5e08\u5144\u9a7e\u5230\uff0c\u5fc3\u5934\u7acb\u5373\u77f3\u5934\u843d\u5730\u4e86\u3002\u201d\u4e00\u4eba\u7ec6\u58f0\u7ec6\u6c14\u7684\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u53ea\u6015\u6211\u4eec\u70b9\u82cd\u6d3e\u4e0d\u80fd\u7ed9\u95f5\u8001\u5e08\u51fa\u751a\u4e48\u529b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u8d77\u5e08\u7236\u8c08\u8bba\u5929\u4e0b\u5251\u6cd5\uff0c\u66fe\u8bf4\u5f53\u4e16\u95e8\u6d3e\u4e4b\u4e2d\uff0c\u5ce8\u5d4b\u3001\u6606\u4ed1\u3001\u534e\u5c71\u3001\u70b9\u82cd\uff0c\u6b66\u6797\u4e2d\u79f0\u4e3a\u56db\u5927\u5251\u6d3e\u3002\u56db\u6d3e\u4eba\u6750\u9f0e\u76db\uff0c\u5251\u6cd5\u4e2d\u5747\u6709\u72ec\u5f97\u4e4b\u79d8\u3002\u5176\u4ed6\u5c11\u6797\u3001\u6b66\u5f53\u7b49\u6d3e\u6b66\u5b66\u867d\u6df1\uff0c\u5374\u4e0d\u4e13\u4ee5\u5251\u672f\u89c1\u79f0\u3002\u8fd9\u59d3\u4e07\u7684\u53f7\u79f0\u8ffd\u98ce\u5251\uff0c\u53c8\u662f\u70b9\u82cd\u6d3e\u7684\u9ad8\u624b\uff0c\u5251\u672f\u5fc5\u662f\u6781\u7cbe\u7684\u4e86\u3002\u4ed6\u5343\u91cc\u8fe2\u8fe2\u6765\u5230\u91d1\u9675\uff0c\u4e0d\u77e5\u56fe\u8c0b\u751a\u4e48\u5927\u4e8b\u3002 \u3000\u3000\u53ea\u542c\u4e24\u4eba\u5ba2\u6c14\u4e86\u51e0\u53e5\uff0c\u8fdc\u5904\u53c8\u6709\u4eba\u51fb\u638c\u4e4b\u58f0\uff0c\u8fd9\u8fb9\u51fb\u638c\u76f8\u5e94\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u5df2\u5148\u540e\u6765\u4e86\u4e09\u8d77\u4eba\u7269\uff0c\u542c\u4ed6\u4eec\u76f8\u89c1\u53d9\u8bdd\uff0c\u77e5\u9053\u4e00\u8d77\u662f\u5c71\u897f\u4e94\u53f0\u5c71\u6e05\u51c9\u5bfa\u7684\u50e7\u4f17\uff0c\u7531\u76d1\u5bfa\u5341\u529b\u5927\u5e08\u7387\u9886\uff1b\u4e00\u8d77\u662f\u6d59\u95fd\u6cbf\u6d77\u7684\u6d77\u76d7\uff0c\u7531\u4e03\u5341\u4e8c\u5c9b\u603b\u76df\u4e3b\u78a7\u6d77\u957f\u9cb8\u90d1\u8d77\u4e91\u7387\u9886\uff1b\u7b2c\u4e09\u8d77\u662f\u9655\u897f\u79e6\u5cad\u592a\u767d\u5c71\u592a\u767d\u6d3e\u7684\u4e09\u4e2a\u76df\u5144\u5f1f\uff0c\u53f7\u79f0\u592a\u767d\u4e09\u82f1\u7684\u53f2\u79c9\u5149\u3001\u53f2\u79c9\u6587\u3001\u9ece\u521a\u4e09\u4eba\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d8a\u542c\u8d8a\u5947\uff0c\u5fc3\u60f3\u8fd9\u4e9b\u90fd\u662f\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u4eba\uff0c\u600e\u4e48\u5ffd\u7136\u805a\u96c6\u5230\u5357\u4eac\u6765\uff1f\u53ea\u542c\u90a3\u59d3\u95f5\u7684\u4e0d\u4f4f\u79f0\u8c22\uff0c\u663e\u7136\u8fd9\u4e9b\u4eba\u90fd\u662f\u4ed6\u9080\u6765\u7684\u4e86\u3002 \u3000\u3000\u9752\u9752\u65e9\u89c9\u8fd9\u4f19\u4eba\u884c\u8ff9\u8be1\u79d8\uff0c\u53ea\u60f3\u8be2\u95ee\u8881\u627f\u5fd7\uff0c\u53ef\u662f\u8033\u542c\u5f97\u4f17\u4eba\u53e3\u6c14\u7686\u975e\u5bfb\u5e38\u4e4b\u8f88\uff0c\u81ea\u5df1\u53ea\u8981\u7a0d\u53d1\u5fae\u58f0\uff0c\u52bf\u5fc5\u7acb\u88ab\u5bdf\u89c9\uff0c\u56e0\u6b64\u8fde\u5927\u6c14\u4e5f\u4e0d\u6562\u900f\u4e00\u53e3\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u59d3\u95f5\u7684\u63d0\u9ad8\u4e86\u55d3\u5b50\u8bf4\u9053\uff1a\u201c\u627f\u5404\u4f4d\u524d\u8f88\u3001\u5e08\u5144\u3001\u5e08\u5f1f\u5343\u5c71\u4e07\u6c34\u7684\u8d76\u6765\u76f8\u52a9\uff0c\u4e49\u6c14\u6df1\u91cd\uff0c\u5728\u4e0b\u95f5\u5b50\u534e\u5b9e\u662f\u611f\u6fc0\u4e07\u5206\uff0c\u8bf7\u53d7\u6211\u4e00\u62dc\uff01\u201d\u542c\u58f0\u97f3\u662f\u8dea\u4e0b\u6765\u53e9\u5934\u3002\u4f17\u4eba\u5fd9\u8c26\u8c22\u6276\u8d77\uff0c\u90fd\u8bf4\uff1a\u201c\u95f5\u4e8c\u54e5\u5feb\u522b\u8fd9\u6837\uff01\u201d\u201c\u6298\u6740\u5c0f\u5f1f\u4e86\uff0c\u8fd9\u54ea\u91cc\u6562\u5f53\uff1f\u201d\u201c\u6b66\u6797\u4e2d\u8def\u89c1\u4e0d\u5e73\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u90a3\u662f\u4efd\u6240\u5f53\u4e3a\uff0c\u95f5\u5144\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u4e71\u4e86\u4e00\u9635\uff0c\u95f5\u5b50\u534e\u53c8\u9053\uff1a\u201c\u8fd9\u51e0\u65e5\u5185\uff0c\u6606\u4ed1\u6d3e\u7684\u5f20\u5fc3\u4e00\u5e08\u5144\uff0c\u5ce8\u5d4b\u6d3e\u7684\u51e0\u4f4d\u9053\u957f\uff0c\u534e\u5c71\u6d3e\u7684\u51e0\u4f4d\u5e08\u5144\u4e5f\u90fd\u53ef\u5230\u4e86\u3002\u201d\u6709\u4eba\u95ee\u9053\uff1a\u201c\u534e\u5c71\u6d3e\u4e5f\u6709\u4eba\u6765\u5417\uff1f\u90a3\u597d\u6781\u4e86\uff0c\u662f\u8c01\u7684\u95e8\u4e0b\u5440\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4f60\u95ee\u5f97\u5012\u597d\uff0c\u6211\u4e5f\u6b63\u60f3\u95ee\u8fd9\u53e5\u8bdd\u3002\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u662f\u795e\u62f3\u65e0\u654c\u95e8\u4e0b\u7684\u51e0\u4f4d\u5e08\u5144\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u9053\uff1a\u201c\u90a3\u662f\u4e8c\u5e08\u54e5\u7684\u95e8\u4e0b\u4e86\u3002\u201d\u90a3\u4eba\u53c8\u95ee\uff1a\u201c\u95f5\u4e8c\u54e5\u8ddf\u5f52\u4e8c\u7237\u592b\u5987\u6709\u4ea4\u60c5\u4e48\uff1f\u90a3\u597d\u6781\u5566\uff0c\u6709\u4ed6\u4eec\u592b\u5987\u6491\u8170\uff0c\u8fd8\u6015\u90a3\u59d3\u7126\u7684\u5978\u8d3c\u751a\u4e48\uff1f\u201d \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u5f52\u6c0f\u592b\u5987\u524d\u8f88\u9ad8\u4eba\uff0c\u5728\u4e0b\u600e\u591f\u5f97\u4e0a\u7ed3\u4ea4\uff1f\u4ed6\u5927\u5f92\u5f1f\u6885\u5251\u548c\u6885\u5144\uff0c\u5374\u8ddf\u5728\u4e0b\u6709\u8fc7\u547d\u7684\u4ea4\u60c5\u3002\u201d\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u6885\u5251\u548c\uff1f\u90a3\u5c31\u662f\u5728\u5c71\u4e1c\u9053\u4e0a\u4e00\u5251\u4f0f\u4e03\u96c4\u7684\u2018\u6ca1\u5f71\u5b50\u2019\u4e86\u3002\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u4ed6\u3002\u201d\u8881\u627f\u5fd7\u542c\u5230\u8fd9\u91cc\uff0c\u767b\u65f6\u91ca\u7136\uff0c\u5fc3\u60f3\u65e2\u6709\u672c\u95e8\u4e2d\u4eba\u53c2\u9884\uff0c\u90a3\u5b9a\u662f\u6b63\u4e8b\uff0c\u6211\u4e14\u4e0d\u9732\u9762\uff0c\u5982\u6709\u673a\u7f18\uff0c\u4e0d\u59a8\u6697\u4e2d\u76f8\u52a9\u3002 \u3000\u3000\u53c8\u542c\u95f5\u5b50\u534e\u9053\uff1a\u201c\u5148\u5144\u5f53\u5e74\u906d\u5bb3\u8eab\u4ea1\uff0c\u5144\u5f1f\u5341\u591a\u5e74\u6765\u5230\u5904\u8bbf\u67e5\uff0c\u59cb\u7ec8\u4e0d\u77e5\u4ec7\u5bb6\u662f\u8c01\u3002\u73b0\u4e0b\u5e78\u8499\u592a\u767d\u5c71\u53f2\u6c0f\u6606\u4ef2\u89c1\u544a\uff0c\u624d\u77e5\u5bb3\u6b7b\u5148\u5144\u7684\u7adf\u662f\u90a3\u59d3\u7126\u7684\u5978\u8d3c\u3002\u6b64\u4ec7\u4e0d\u62a5\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff01\u201d\u8bed\u6c14\u60b2\u6124\uff0c\u53c8\u542c\u5f53\u7684\u4e00\u58f0\uff0c\u60f3\u662f\u7528\u5175\u5668\u5728\u5893\u7891\u4e0a\u91cd\u91cd\u4e00\u780d\u3002 \u3000\u3000\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u90a3\u94c1\u80cc\u91d1\u9ccc\u7126\u516c\u793c\u662f\u6c5f\u6e56\u4e0a\u6709\u540d\u7684\u6c49\u5b50\uff0c\u91d1\u9f99\u5e2e\u540d\u58f0\u5411\u6765\u4e5f\u5e76\u4e0d\u574f\uff0c\u6599\u4e0d\u5230\u7adf\u505a\u51fa\u8fd9\u7b49\u4e8b\u6765\u3002 \u3000\u3000\u53f2\u6c0f\u6606\u4ef2\u4e0d\u77e5\u54ea\u91cc\u5f97\u6765\u7684\u8baf\u606f\uff1f\u201d\u8a00\u4e0b\u4f3c\u4e4e\u9887\u6709\u6000\u7591\u3002 \u3000\u3000\u95f5\u5b50\u534e\u4e0d\u7b49\u53f2\u6c0f\u5144\u5f1f\u7b54\u8154\uff0c\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u53f2\u6c0f\u6606\u4ef2\u5df2\u5c06\u5148\u5144\u5728\u5c71\u4e1c\u906d\u96be\u7684\u7ecf\u8fc7\uff0c\u8be6\u7ec6\u8ddf\u665a\u8f88\u8bf4\u4e86\uff0c\u90a3\u662f\u6709\u51ed\u6709\u636e\u7684\u4e8b\uff0c\u5341\u529b\u5927\u5e08\u5012\u4e0d\u5fc5\u591a\u7591\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u7126\u516c\u793c\u5728\u5357\u4eac\u6570\u5341\u5e74\uff0c\u6839\u6df1\u8482\u56fa\uff0c\u91d1\u9f99\u5e2e\u4eba\u591a\u52bf\u4f17\uff0c\u867d\u7136\u6ca1\u542c\u8bf4\u6709\u751a\u4e48\u4e86\u4e0d\u8d77\u7684\u9ad8\u624b\uff0c\u6bd5\u7adf\u662f\u5730\u5934\u86c7\uff0c\u54b1\u4eec\u8fd9\u6b21\u52a8\u4ed6\uff0c\u53ef\u8981\u5c0f\u5fc3\u4e86\u3002\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u5c0f\u5f1f\u81ea\u77e5\u72ec\u529b\u96be\u652f\uff0c\u662f\u4ee5\u6597\u80c6\u904d\u9080\u5404\u4f4d\u597d\u670b\u53cb\u7684\u5927\u9a7e\u3002\u660e\u5929\u9149\u65f6\u6b63\uff0c\u5144\u5f1f\u5728\u5927\u529f\u574a\u820d\u4e0b\u6446\u51e0\u5e2d\u6c34\u9152\uff0c\u548c\u5404\u4f4d\u6d17\u5c18\u63a5\u98ce\uff0c\u52a1\u8bf7\u5149\u4e34\u3002\u201d \u3000\u3000\u4f17\u4eba\u7eb7\u7eb7\u9053\u8c22\uff0c\u90fd\u8bf4\uff1a\u201c\u81ea\u5df1\u4eba\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u8fd9\u6b21\u597d\u670b\u53cb\u6765\u7684\u5f88\u591a\uff0c\u96be\u4fdd\u5bf9\u5934\u4e0d\u4f1a\u53d1\u89c9\u3002 \u3000\u3000\u660e\u65e5\u5404\u4f4d\u9a7e\u5230\uff0c\u8bf7\u5bf9\u5728\u95e8\u53e3\u63a5\u5f85\u7684\u5144\u5f1f\u4f38\u51fa\u53f3\u624b\u4e2d\u6307\u3001\u65e0\u540d\u6307\u3001\u5c0f\u6307\u4e09\u4e2a\u6307\u5934\u4f5c\u4e00\u4e0b\u624b\u52bf\uff0c\u8f7b\u8f7b\u8bf4\u4e00\u53e5\uff1a\u2018\u6c5f\u6e56\u4e49\u6c14\uff0c\u62d4\u5200\u76f8\u52a9\u2019\uff0c\u4ee5\u514d\u7ed9\u91d1\u9f99\u5e2e\u6d3e\u4eba\u6df7\u8fdb\u6765\u6478\u4e86\u5e95\u53bb\u3002\u201d \u3000\u3000\u4f17\u4eba\u90fd\u8bf4\u6b63\u8be5\u5982\u6b64\uff0c\u52a9\u62f3\u8005\u6765\u81ea\u56db\u65b9\uff0c\u591a\u6570\u4e92\u4e0d\u76f8\u8bc6\uff0c\u4ee5\u540e\u5bf9\u654c\uff0c\u90fd\u4ee5\u8fd9\u624b\u52bf\u548c\u6697\u53f7\u4e3a\u8bb0\u3002\u4f17\u4eba\u8bf4\u7f62\u6b63\u4e8b\uff0c\u53c8\u8c08\u4e86\u4e00\u4f1a\u674e\u81ea\u6210\u3001\u5f20\u732e\u5fe0\u7b49\u548c\u5b98\u519b\u6253\u4ed7\u7684\u65b0\u95fb\uff0c\u4fbf\u9646\u7eed\u6563\u4e86\u3002 \u3000\u3000\u5f85\u4f17\u4eba\u53bb\u8fdc\uff0c\u8881\u627f\u5fd7\u548c\u9752\u9752\u624d\u8eba\u4e0b\u6765\u4f11\u606f\u3002\u9752\u9752\u8e72\u7740\u826f\u4e45\u4e0d\u52a8\uff0c\u8fd9\u65f6\u811a\u90fd\u9ebb\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u660e\u513f\u77a7\u77a7\u70ed\u95f9\u53bb\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u77a7\u77a7\u5012\u4e5f\u4e0d\u59a8\u3002\u53ef\u662f\u987b\u5f97\u542c\u6211\u7684\u8bdd\uff0c\u4e0d\u8bb8\u95f9\u4e8b\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u8c01\u8bf4\u8981\u95f9\u4e8b\u4e86\u554a\uff1f\u8981\u95f9\u4e8b\u4e5f\u53ea\u8ddf\u4f60\u95f9\uff0c\u4e0d\u8ddf\u4eba\u5bb6\u95f9\u3002\u201d \u3000\u3000\u6b21\u65e5\u4e2d\u5348\uff0c\u9a6c\u516c\u5b50\u88ab\u6740\u7684\u6d88\u606f\u5728\u5357\u4eac\u57ce\u91cc\u4f20\u5f97\u6cb8\u6cb8\u626c\u626c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u6574\u5929\u8eb2\u5728\u5ba2\u5e97\u4e0d\u51fa\u3002\u508d\u665a\u65f6\u5206\uff0c\u4e24\u4eba\u6362\u4e86\u8863\u886b\uff0c\u6539\u4f5c\u5bfb\u5e38\u6c5f\u6e56\u6c49\u5b50\u7684\u6253\u626e\uff0c\u8e31\u5230\u5927\u529f\u574a\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u5ea7\u5927\u5b85\u5b50\u524d\u6302\u8d77\u4e86\u5927\u706f\u7b3c\uff0c\u5ba2\u4eba\u6b63\u7edc\u7ece\u4e0d\u7edd\u7684\u8fdb\u53bb\u3002\u90a3\u5b85\u7b2c\u751a\u5927\uff0c\u4f46\u5899\u5766\u6b8b\u65e7\u3001\u9636\u77f3\u65ad\u7f3a\uff0c\u95e8\u53e3\u7565\u4f5c\u4fee\u6574\u7c89\u5237\uff0c\u5374\u4e5f\u662f\u6025\u5c31\u7ae0\uff0c\u9887\u4e3a\u8349\u8349\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u8d70\u5230\u95e8\u53e3\uff0c\u4f38\u51fa\u4e09\u6307\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u4e49\u6c14\uff0c\u62d4\u5200\u76f8\u52a9\u3002\u201d\u4e00\u4e2a\u8eab\u7a7f\u957f\u888d\u7684\u4eba\u8fde\u8fde\u62f1\u624b\uff0c\u65c1\u8fb9\u4e00\u4e2a\u58ee\u6c49\u966a\u4ed6\u4eec\u8fdb\u53bb\uff0c\u732e\u4e0a\u8336\u6765\uff0c\u8bf7\u6559\u59d3\u540d\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u968f\u53e3\u80e1\u8bcc\u4e24\u4e2a\u540d\u5b57\u3002\u90a3\u58ee\u6c49\u9053\uff1a\u201c\u4e45\u4ef0\u4e45\u4ef0\uff0c\u5144\u5f1f\u5728\u6c5f\u6e56\u4e0a\u4e45\u95fb\u4e24\u4f4d\u5927\u540d\u3002\u201d\u9752\u9752\u809a\u91cc\u6697\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u540d\u8fde\u6211\u4eec\u81ea\u5df1\u4e5f\u8fd8\u662f\u4eca\u65e5\u521d\u6b21\u542c\u5230\uff0c\u4f60\u5012\u4e45\u95fb\u4e86\u3002\u201d\u4e0d\u4e45\u5ba2\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u90a3\u58ee\u6c49\u89c1\u4e24\u4eba\u5e74\u8f7b\uff0c\u6599\u60f3\u5fc5\u662f\u54ea\u4e00\u6d3e\u4e2d\u8ddf\u968f\u5e08\u957f\u800c\u6765\u7684\u5f1f\u5b50\uff0c\u4e5f\u4e0d\u5982\u4f55\u770b\u91cd\uff0c\u8bf4\u4e86\u58f0\u201c\u5931\u966a\u201d\uff0c\u62db\u547c\u522b\u4eba\u53bb\u4e86\u3002\u4e0d\u4e00\u4f1a\u5f00\u51fa\u5e2d\u6765\uff0c\u8881\u627f\u5fd7\u548c\u9752\u9752\u5728\u504f\u5e2d\u4e0a\u5750\u4e86\uff0c\u966a\u5e2d\u7684\u662f\u4ed9\u90fd\u6d3e\u7684\u4e00\u4e2a\u5c0f\u5f92\u5f1f\uff0c\u540c\u5e2d\u7684\u90fd\u662f\u4e9b\u540e\u8f88\u95e8\u4eba\uff0c\u4e5f\u6ca1\u4eba\u6765\u7406\u4f1a\u4ed6\u4eec\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u95f5\u5b50\u534e\u5230\u5404\u5e2d\u656c\u9152\uff0c\u656c\u5230\u8fd9\u8fb9\u5e2d\u4e0a\u65f6\uff0c\u8881\u627f\u5fd7\u89c1\u4ed6\u7ea6\u83ab\u4e09\u5341\u5c81\u5de6\u53f3\u5e74\u7eaa\uff0c\u624b\u4e0a\u9752\u7b4b\u51f8\u8d77\uff0c\u4e00\u8138\u527d\u608d\u4e4b\u8272\uff0c\u4e3e\u6b62\u6b65\u884c\u4e4b\u95f4\uff0c\u663e\u5f97\u6b66\u529f\u4e0d\u4f4e\u3002\u4ed6\u53cc\u76ee\u7ea2\u80bf\uff0c\u6599\u662f\u60f3\u8d77\u5144\u957f\u88ab\u5bb3\u4e4b\u4ec7\uff0c\u8fde\u65e5\u60b2\u4f24\u54c0\u54ed\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u7b03\u4e8e\u624b\u8db3\u4e4b\u60c5\uff0c\u751a\u662f\u53ef\u656c\u3002\u4ed6\u5927\u4e3e\u9080\u670b\u96c6\u53cb\uff0c\u60f3\u6765\u90a3\u59d3\u7126\u7684\u4ec7\u4eba\u548c\u751a\u4e48\u91d1\u9f99\u5e2e\u58f0\u52bf\u5b9a\u662f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u5148\u5411\u4f17\u4eba\u4f5c\u4e86\u4e09\u63d6\uff0c\u8fde\u58f0\u9053\u8c22\uff0c\u7136\u540e\u656c\u9152\u3002\u5e2d\u4e0a\u4f17\u4eba\u90fd\u662f\u665a\u8f88\uff0c\u5168\u90fd\u79bb\u5e2d\u8fd8\u793c\u3002 \u3000\u3000\u95f5\u5b50\u534e\u656c\u5b8c\u9152\u5f52\u5ea7\uff0c\u521a\u5750\u5b9a\u8eab\uff0c\u4e00\u540d\u5f1f\u5b50\u5306\u5306\u8d70\u5230\u4ed6\u8eab\u8fb9\uff0c\u4fef\u8033\u8bf4\u4e86\u51e0\u53e5\u3002\u95f5\u5b50\u534e\u6ee1\u8138\u559c\u8272\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u606d\u606d\u656c\u656c\u7684\u966a\u7740\u4e09\u4eba\u8fdb\u6765\uff0c\u5230\u9996\u5e2d\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4e86\u95f5\u5b50\u534e\u7684\u795e\u6c14\uff0c\u6599\u77e5\u8fd9\u4e09\u4eba\u6765\u5934\u4e0d\u5c0f\uff0c\u4ed4\u7ec6\u770b\u4e86\u51e0\u773c\u3002\u89c1\u5934\u4e00\u4eba\u5112\u751f\u6253\u626e\uff0c\u80cc\u8d1f\u957f\u5251\uff0c\u53cc\u773c\u5fae\u7ffb\uff0c\u6ee1\u8138\u50b2\u8272\uff0c\u5927\u6a21\u5927\u6837\u7684\u6602\u9996\u76f4\u5165\u3002\u7b2c\u4e8c\u4eba\u662f\u4e2a\u58ee\u6c49\uff0c\u5f62\u8c8c\u6734\u5b9e\u3002\u7b2c\u4e09\u4eba\u5374\u662f\u4e8c\u5341\u4e8c\u4e09\u5c81\u7684\u9ad8\u7626\u5973\u5b50\uff0c\u76f8\u8c8c\u751a\u7f8e\uff0c\u79c0\u7709\u5fae\u8e59\uff0c\u674f\u773c\u542b\u5a01\u3002 \u3000\u3000\u95f5\u5b50\u534e\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6885\u5927\u54e5\u53ca\u65f6\u8d76\u5230\uff0c\u5144\u5f1f\u5b9e\u5728\u611f\u6fc0\u4e4b\u81f3\u3002\u201d\u90a3\u5112\u751f\u9053\uff1a\u201c\u95f5\u4e8c\u54e5\u7684\u4e8b\uff0c\u5144\u5f1f\u5c82\u6709\u4e0d\u6765\u4e4b\u7406\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u4fbf\u662f\u4e8c\u5e08\u54e5\u7684\u5f1f\u5b50\u6885\u5251\u548c\uff0c\u600e\u5730\u795e\u6001\u5982\u6b64\u50b2\u6162\uff1f\u201d\u53ea\u542c\u6885\u5251\u548c\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u591a\u4e8b\uff0c\u4ee3\u9080\u4e86\u4e24\u4e2a\u5e2e\u624b\u3002\u8fd9\u662f\u6211\u4e09\u5e08\u5f1f\u5218\u57f9\u751f\uff0c\u8fd9\u662f\u6211\u4e94\u5e08\u59b9\u5b59\u4ef2\u541b\u3002\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u4e45\u4ef0\u4e94\u4e01\u624b\u5218\u5144\u4e0e\u5b59\u5973\u4fa0\u7684\u5a01\u540d\uff0c\u5144\u5f1f\u771f\u662f\u4e07\u5206\u6709\u5e78\u3002\u201d\u4ed6\u6ca1\u8bf4\u5b59\u4ef2\u541b\u7684\u5916\u53f7\u3002\u539f\u6765\u8fd9\u5916\u53f7\u4e0d\u5927\u96c5\u81f4\uff0c\u53eb\u4f5c\u201c\u98de\u5929\u9b54\u5973\u201d\u3002\u5f53\u4e0b\u95f5\u5b50\u534e\u53c8\u7ed9\u5341\u529b\u5927\u5e08\u3001\u592a\u767d\u4e09\u82f1\u3001\u90d1\u8d77\u4e91\u3001\u4e07\u91cc\u98ce\u7b49\u4f17\u4eba\u5f15\u89c1\u3002\u5404\u4eba\u4e92\u9053\u4ef0\u6155\uff0c\u6b22\u547c\u7545\u996e\u3002 \u3000\u3000\u9152\u610f\u6e10\u9163\uff0c\u95f5\u5bb6\u4e00\u540d\u5bb6\u4e01\u62ff\u4e86\u4e00\u5f20\u5927\u7ea2\u5e16\u5b50\u8fdb\u6765\uff0c\u5448\u7ed9\u4e3b\u4eba\u3002\u95f5\u5b50\u534e\u4e00\u770b\uff0c\u8138\u8272\u7acb\u53d8\uff0c\u5e72\u7b11\u6570\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u7126\u8001\u513f\u679c\u7136\u795e\u901a\u5e7f\u5927\uff0c\u54b1\u4eec\u8fd8\u6ca1\u627e\u4ed6\uff0c\u4ed6\u5012\u5148\u5bfb\u4e0a\u95e8\u6765\u5566\u3002\u6885\u5927\u54e5\uff0c\u4f60\u4eec\u521a\u5230\uff0c\u4ed6\u7adf\u4e5f\u5f97\u5230\u4e86\u6d88\u606f\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u63a5\u8fc7\u5e16\u5b50\uff0c\u89c1\u5c01\u9762\u4e0a\u5199\u7740\uff1a\u201c\u540e\u5b66\u6559\u5f1f\u7126\u516c\u793c\u987f\u9996\u767e\u62dc\u201d\u51e0\u4e2a\u5927\u5b57\uff0c\u7ffb\u4e86\u5f00\u6765\uff0c\u91cc\u9762\u5199\u7740\u95f5\u5b50\u534e\u3001\u5341\u529b\u5927\u5e08\u3001\u592a\u767d\u4e09\u82f1\u7b49\u4eba\u59d3\u540d\uff0c\u6240\u6709\u4e0e\u5bb4\u7684\u6210\u540d\u4eba\u7269\u5168\u90fd\u5728\u5185\uff0c\u8fde\u6885\u5251\u548c\u7b49\u4e09\u4eba\u7684\u540d\u5b57\u4e5f\u52a0\u5728\u540e\u9762\uff0c\u58a8\u8ff9\u672a\u5e72\uff0c\u663e\u662f\u4e34\u65f6\u6dfb\u4e0a\u53bb\u7684\u3002\u5e16\u4e2d\u9080\u8bf7\u8bf8\u4eba\u660e\u65e5\u4e2d\u5348\u5230\u7126\u5b85\u8d74\u5bb4\u3002\u6885\u5251\u548c\u5c06\u5e16\u5b50\u5f80\u684c\u4e0a\u4e00\u63b7\uff0c\u8bf4\u9053\uff1a\u201c\u7126\u8001\u513f\u8fd9\u5730\u5934\u86c7\u4e5f\u771f\u6709\u4ed6\u7684\uff0c\u8baf\u606f\u7075\u901a\u4e4b\u6781\u3002\u54b1\u4eec\u591f\u4e0d\u4e0a\u505a\u5f3a\u9f99\uff0c\u53ef\u662f\u8fd9\u5730\u5934\u86c7\u4e5f\u5f97\u6597\u4e0a\u4e00\u6597\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u9001\u5e16\u6765\u7684\u90a3\u4f4d\u670b\u53cb\u5462\uff1f\u8bf7\u4ed6\u8fdb\u6765\u5427\uff01\u201d\u90a3\u5bb6\u4e01\u5e94\u58f0\u51fa\u53bb\u3002\u4f17\u4eba\u505c\u676f\u4e0d\u996e\uff0c\u76ee\u5149\u4e00\u9f50\u671b\u5411\u95e8\u53e3\u3002\u53ea\u89c1\u90a3\u5bb6\u4e01\u8eab\u540e\u8ddf\u7740\u4e00\u4eba\uff0c\u4e09\u5341\u5c81\u5de6\u53f3\u5e74\u7eaa\uff0c\u8eab\u7a7f\u957f\u888d\uff0c\u7f13\u6b65\u8fdb\u6765\uff0c\u5411\u9996\u5e2d\u8bf8\u4eba\u8eac\u8eab\u884c\u793c\uff0c\u8ddf\u7740\u62b1\u62f3\u4f5c\u4e86\u56db\u65b9\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e08\u7236\u542c\u8bf4\u5404\u4f4d\u524d\u8f88\u9a7e\u4e34\u5357\u4eac\uff0c\u660e\u5929\u8bf7\u5404\u4f4d\u8fc7\u53bb\u53d9\u53d9\uff0c\u5429\u5490\u5f1f\u5b50\u9080\u8bf7\u5404\u4f4d\u7684\u5927\u9a7e\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u51b7\u7b11\u9053\uff1a\u201c\u7126\u8001\u513f\u6446\u4e0b\u9e3f\u95e8\u5bb4\u5566\uff01\u201d\u8f6c\u5934\u5bf9\u9001\u8bf7\u5e16\u7684\u4eba\u9053\uff1a\u201c\u5582\uff0c\u4f60\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u201d\u90a3\u4eba\u542c\u4ed6\u8a00\u8bed\u65e0\u793c\uff0c\u4f46\u4ecd\u606d\u8c28\u7b54\u9053\uff1a\u201c\u5f1f\u5b50\u7f57\u7acb\u5982\u3002\u201d\u6885\u5251\u548c\u559d\u9053\uff1a\u201c\u7126\u516c\u793c\u9080\u6211\u4eec\u8fc7\u53bb\uff0c\u6709\u751a\u4e48\u8be1\u8ba1\uff1f\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u7f57\u7acb\u5982\u9053\uff1a\u201c\u5bb6\u5e08\u542c\u5f97\u5404\u4f4d\u524d\u8f88\u5927\u9a7e\u5230\u6765\uff0c\u5341\u5206\u4ef0\u6155\uff0c\u60f3\u548c\u5404\u4f4d\u89c1\u89c1\uff0c\u5f97\u4ee5\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u9053\uff1a\u201c\u54fc\uff0c\u8bdd\u5012\u8bf4\u5f97\u6f02\u4eae\u3002\u6211\u95ee\u4f60\uff0c\u7126\u516c\u793c\u5f53\u5e74\u5bb3\u6b7b\u95f5\u8001\u5e08\u7684\u5144\u957f\u95f5\u5927\u7237\uff0c\u4f60\u5728\u4e0d\u5728\u573a\uff1f\u201d\u7f57\u7acb\u5982\u9053\uff1a\u201c\u5bb6\u5e08\u8bf4\u9053\uff0c\u660e\u65e5\u8bf7\u5404\u4f4d\u8fc7\u53bb\uff0c\u4e00\u5219\u662f\u5411\u5404\u4f4d\u524d\u8f88\u8868\u793a\u666f\u4ef0\u4e4b\u610f\uff0c\u4e8c\u5219\u662f\u8981\u5411\u95f5\u4e8c\u7237\u966a\u8bdd\u8c22\u7f6a\u3002\u76fc\u95f5\u4e8c\u7237\u5927\u4eba\u5927\u91cf\uff0c\u63ed\u8fc7\u4e86\u8fd9\u4e2a\u6881\u5b50\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u559d\u9053\uff1a\u201c\u6740\u4e86\u4eba\uff0c\u966a\u8bdd\u8c22\u7f6a\u5c31\u6210\u4e86\u4e48\uff1f\u201d\u7f57\u7acb\u5982\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u7684\u524d\u56e0\u540e\u679c\uff0c\u5bb6\u5e08\u8bf4\u5b9e\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u7275\u6d89\u5230\u540d\u95e8\u5927\u6d3e\u7684\u58f0\u540d\uff0c\u56e0\u6b64\u2026\u2026\u201d \u3000\u3000\u5b59\u4ef2\u541b\u7a81\u7136\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u80e1\u626f\u4e9b\u751a\u4e48\uff1f\u6211\u5e08\u54e5\u95ee\u4f60\uff0c\u5f53\u65f6\u4f60\u662f\u4e0d\u662f\u5728\u573a\uff1f\u201d\u7f57\u7acb\u5982\u9053\uff1a\u201c\u5f1f\u5b50\u90a3\u65f6\u5019\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u5c1a\u672a\u62dc\u5165\u5e08\u95e8\u3002\u4f46\u6211\u5e08\u7236\u4e3a\u4eba\u6b63\u6d3e\uff0c\u51b3\u4e0d\u81f4\u6ee5\u6740\u65e0\u8f9c\u2026\u2026\u201d \u3000\u3000\u5b59\u4ef2\u541b\u559d\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u8fd8\u5f3a\u5634\uff01\u4f9d\u4f60\u8bf4\u6765\uff0c\u95f5\u5927\u7237\u662f\u6b7b\u6709\u4f59\u8f9c\u4e86\uff1f\u201d\u559d\u53eb\u58f0\u4e2d\uff0c\u5979\u7a81\u7136\u98de\u9e1f\u822c\u7684\u7eb5\u4e86\u51fa\u6765\uff0c\u53f3\u624b\u4e2d\u5df2\u63e1\u4f4f\u4e86\u660e\u6643\u6643\u7684\u4e00\u67c4\u957f\u5251\uff0c\u5de6\u624b\u51fa\u638c\u5411\u7f57\u7acb\u5982\u80f8\u53e3\u6309\u5230\u3002\u7f57\u7acb\u5982\u5927\u5403\u4e00\u60ca\uff0c\u53f3\u81c2\u4e00\u62db\u201c\u94c1\u95e8\u95e9\u201d\uff0c\u6a2a\u683c\u5979\u8fd9\u4e00\u638c\u6025\u6309\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u7cdf\u4e86\uff01\u4ed6\u53f3\u81c2\u4e0d\u4fdd\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u542c\u5f97\u7f57\u7acb\u5982\u60e8\u53eb\u4e00\u58f0\uff0c\u4e00\u6761\u53f3\u81c2\u679c\u771f\u5df2\u88ab\u4e00\u5251\u65a9\u4e0b\u3002\u5385\u4e2d\u5404\u4eba\u9f50\u58f0\u60ca\u547c\uff0c\u90fd\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7f57\u7acb\u5982\u8138\u8272\u60e8\u767d\uff0c\u4f46\u5c45\u7136\u5e76\u4e0d\u6655\u5012\uff0c\u5de6\u624b\u6495\u4e0b\u8863\u895f\uff0c\u5728\u53f3\u80a9\u4e0a\u4e00\u7f20\uff0c\u4fef\u8eab\u62fe\u8d77\u65ad\u81c2\uff0c\u5927\u8e0f\u6b65\u8d70\u4e86\u51fa\u53bb\u3002\u4f17\u4eba\u89c1\u4ed6\u5982\u6b64\u786c\u6717\uff0c\u4e0d\u7981\u9a87\u7136\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u62ed\u53bb\u5251\u4e0a\u8840\u8ff9\uff0c\u8fd8\u5251\u5165\u9798\uff0c\u795e\u8272\u81ea\u82e5\u7684\u5f52\u5ea7\uff0c\u4e3e\u8d77\u9152\u676f\u4e00\u996e\u800c\u5c3d\u3002\u8fd9\u4e00\u5251\u5e72\u51c0\u5229\u843d\uff0c\u51fa\u624b\u5feb\u6781\uff0c\u53ef\u662f\u5385\u4e0a\u6570\u767e\u4eba\u7adf\u65e0\u4e00\u4eba\u559d\u91c7\uff0c\u5747\u89c9\u4e0d\u8bba\u5bf9\u65b9\u5982\u4f55\u4e0d\u662f\uff0c\u5374\u4e5f\u4e0d\u8be5\u8fd9\u822c\u8fa3\u624b\u5bf9\u5f85\u524d\u6765\u9080\u5ba2\u7684\u4f7f\u8005\u3002\u8fde\u95f5\u5b50\u534e\u4e8e\u9707\u60ca\u4e4b\u4e0b\uff0c\u4e5f\u5fd8\u4e86\u53eb\u4e00\u58f0\u597d\u3002\u5b59\u4ef2\u541b\u5fc3\u4e0b\u751a\u4e0d\u4e50\u610f\u3002 \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u8fd9\u4eba\u5982\u6b64\u51f6\u608d\uff0c\u8db3\u89c1\u4ed6\u5e08\u7236\u66f4\u52a0\u5978\u6076\u3002\u54b1\u4eec\u660e\u65e5\u53bb\u4e0d\u53bb\u8d74\u5bb4\uff1f\u201d \u3000\u3000\u4e07\u91cc\u98ce\u9053\uff1a\u201c\u90a3\u5f53\u7136\u53bb\u554a\u3002\u5018\u82e5\u4e0d\u53bb\u3002\u5c82\u975e\u8ba9\u4ed6\u5c0f\u89d1\u4e86\u3002\u201d \u3000\u3000\u90d1\u8d77\u4e91\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u665a\u6d3e\u4eba\u5148\u53bb\u8e29\u8e29\u76d8\u5b50\uff0c\u6478\u4e2a\u5e95\u7ec6\uff0c\u77a7\u90a3\u7126\u516c\u793c\u9080\u4e86\u4e9b\u751a\u4e48\u5e2e\u624b\uff0c\u91d1\u9f99\u5e2e\u660e\u5929\u6709\u751a\u4e48\u9b3c\u8ba1\uff0c\u662f\u5426\u8981\u5728\u9152\u83dc\u4e2d\u4e0b\u6bd2\u3002\u6709\u5907\u65e0\u60a3\uff0c\u514d\u5f97\u4e0a\u5f53\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u90d1\u5c9b\u4e3b\u6240\u89c1\u6781\u662f\u3002\u6211\u60f3\u4ed6\u4eec\u5b9a\u7136\u9632\u5907\u5f88\u7d27\uff0c\u5012\u8981\u8bf7\u51e0\u4f4d\u5144\u957f\u8f9b\u82e6\u4e00\u8d9f\u624d\u597d\u3002\u201d\u4e07\u91cc\u98ce\u9053\uff1a\u201c\u5c0f\u5f1f\u6765\u81ea\u544a\u594b\u52c7\u5427\uff01\u201d\u95f5\u5b50\u534e\u7ad9\u8d77\u6765\u659f\u4e86\u4e00\u676f\u9152\uff0c\u6367\u5230\u4ed6\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u5148\u656c\u4e00\u676f\uff0c\u4e07\u5927\u54e5\u9a6c\u5230\u6210\u529f\u3002\u201d\u4e24\u4eba\u5bf9\u996e\u5e72\u676f\u3002 \u3000\u3000\u7b75\u5e2d\u6563\u540e\uff0c\u5404\u4eba\u7eb7\u7eb7\u8f9e\u51fa\u3002\u8881\u627f\u5fd7\u4e00\u6253\u624b\u52bf\uff0c\u548c\u9752\u9752\u6084\u6084\u8ddf\u5728\u4e07\u91cc\u98ce\u4e4b\u540e\u3002\u8fd9\u65f6\u5df2\u662f\u521d\u66f4\u65f6\u5206\uff0c\u53ea\u89c1\u4ed6\u56de\u5ba2\u5e97\u6362\u4e86\u77ed\u88c5\uff0c\u5411\u4e1c\u800c\u53bb\u3002\u4e24\u4eba\u8fdc\u8fdc\u8ddf\u7740\uff0c\u89c1\u4ed6\u8f6c\u5f2f\u62b9\u89d2\u7684\u7a7f\u8fc7\u4e86\u4e03\u516b\u6761\u8857\u9053\uff0c\u7ed5\u5230\u4e00\u6240\u5927\u5b85\u7b2c\u540e\u9762\uff0c\u5f84\u81ea\u7a9c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u8eab\u6cd5\u6781\u5feb\uff0c\u5fc3\u60f3\uff1a\u201c\u5012\u4e5f\u4e0d\u6789\u4e86\u2018\u8ffd\u98ce\u5251\u2019\u4e09\u5b57\u3002\u201d\u4e24\u4eba\u968f\u540e\u8ddf\u8fdb\uff0c\u89c1\u4e00\u95f4\u623f\u4e2d\u900f\u7740\u706f\u5149\uff0c\u5728\u7a97\u7f1d\u4e2d\u5f20\u53bb\u3002 \u3000\u3000\u89c1\u5ba4\u4e2d\u5750\u7740\u4e09\u4eba\uff0c\u671d\u5916\u4e00\u4eba\u4e94\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u8138\u988a\u7ea2\u6da6\uff0c\u989d\u5934\u5168\u662f\u76b1\u7eb9\uff0c\u7709\u5934\u7d27\u9501\uff0c\u5fe7\u5f62\u4e8e\u8272\u3002 \u3000\u3000\u53ea\u542c\u90a3\u4eba\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u7acb\u5982\u600e\u6837\u4e86\uff1f\u201d\u4e0b\u9996\u4e00\u4eba\u9053\uff1a\u201c\u7f57\u5e08\u54e5\u6655\u8fc7\u53bb\u4e86\u51e0\u6b21\uff0c\u73b0\u4e0b\u8840\u662f\u6b62\u4f4f\u4e86\u3002\u201d\u8881\u627f\u5fd7\u542c\u4e24\u4eba\u53e3\u6c14\uff0c\u6599\u60f3\u8fd9\u8001\u8005\u4fbf\u662f\u7126\u516c\u793c\uff0c\u5e08\u5f92\u4eec\u5728\u8c08\u7f57\u7acb\u5982\u7684\u4f24\u52bf\u3002 \u3000\u3000\u53c8\u542c\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u5e08\u7236\uff0c\u54b1\u4eec\u6700\u597d\u6d3e\u51e0\u540d\u5144\u5f1f\u5728\u5b85\u5b50\u56db\u5468\u5de1\u67e5\uff0c\u53ea\u6015\u5bf9\u5934\u6709\u4eba\u6765\u8e29\u76d8\u5b50\u3002\u201d \u3000\u3000\u7126\u516c\u793c\u53f9\u9053\uff1a\u201c\u67e5\u4e0d\u67e5\u90fd\u662f\u4e00\u6837\uff0c\u6211\u662f\u8ba4\u547d\u5566\uff01\u660e\u5929\u4e0a\u5348\uff0c\u4f60\u4eec\u9001\u5e08\u5a18\u3001\u5e08\u59b9\u548c\u5c0f\u5e08\u5f1f\u5230\u5f90\u5dde\u5434\u5bb6\u53bb\u3002\u201d\u90a3\u5f92\u5f1f\u9053\uff1a\u201c\u5e08\u7236\uff01\u5bf9\u5934\u867d\u7136\u5389\u5bb3\uff0c\u4f60\u8001\u4eba\u5bb6\u4e5f\u4e0d\u5fc5\u7070\u5fc3\u3002\u672c\u5e2e\u5355\u5728\u5357\u4eac\u57ce\u91cc\u5c31\u6709\u4e24\u5343\u591a\u5144\u5f1f\uff0c\u5927\u4f19\u513f\u4e00\u8d77\u8ddf\u4ed6\u4eec\u62da\u4e2a\u6b7b\u6d3b\uff0c\u6015\u4ed6\u4eec\u600e\u7684\uff1f\u201d \u3000\u3000\u7126\u516c\u793c\u53f9\u9053\uff1a\u201c\u5bf9\u5934\u9080\u7684\u90fd\u662f\u6c5f\u6e56\u4e0a\u9876\u513f\u5c16\u513f\u7684\u597d\u624b\uff0c\u5e2e\u91cc\u8fd9\u4e9b\u5144\u5f1f\u8ddf\u4ed6\u4eec\u5bf9\u654c\uff0c\u53ea\u662f\u767d\u9001\u6027\u547d\u2026\u2026\u5509\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u4eec\u597d\u597d\u4f8d\u5949\u5e08\u5a18\u3002\u5e08\u5f1f\u548c\u5e08\u59b9\uff0c\u90fd\u8981\u9760\u4f60\u4eec\u6559\u517b\u6210\u4eba\u4e86\u3002\u201d\u8bf4\u7740\u4e0d\u7981\u6d41\u4e0b\u6cea\u6765\u3002\u4e00\u4e2a\u5f92\u5f1f\u9053\uff1a\u201c\u5e08\u7236\u5feb\u522b\u8fd9\u4e48\u8bf4\uff0c\u4f60\u8001\u4eba\u5bb6\u4e00\u8eab\u6b66\u529f\uff0c\u5a01\u9547\u6c5f\u5357\uff0c\u5c31\u7b97\u4e0d\u80dc\uff0c\u4e5f\u51b3\u4e0d\u81f4\u843d\u8d25\u3002\u54b1\u4eec\u4e8c\u5341\u4e94\u540d\u5e08\u5144\u5f1f\uff0c\u9664\u4e86\u7f57\u5e08\u54e5\u4e4b\u5916\uff0c\u8fd8\u6709\u4e8c\u5341\u56db\u4eba\u3002\u771f\u7684\u6253\u4e0d\u8d62\uff0c\u4f60\u8001\u4ea4\u6e38\u904d\u5929\u4e0b\uff0c\u5e7f\u9080\u670b\u53cb\uff0c\u8ddf\u4ed6\u4eec\u518d\u62da\u8fc7\u3002\u4ed6\u4eec\u6709\u597d\u670b\u53cb\uff0c\u96be\u9053\u54b1\u4eec\u5c31\u6ca1\u6709\uff1f\u201d \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u5f53\u5e74\u6211\u8840\u6c14\u65b9\u521a\uff0c\u6027\u5b50\u4e5f\u662f\u548c\u4f60\u4e00\u822c\u66b4\u8e81\uff0c\u4ee5\u81f4\u60f9\u4e86\u8fd9\u573a\u7978\u4e8b\u3002\u73b0\u4e0b\u6211\u8ba9\u4ed6\u4eec\u6740\u4e86\uff0c\u8fd8\u4e86\u8fd9\u7b14\u8840\u503a\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u201d\u8881\u627f\u5fd7\u548c\u9752\u9752\u5747\u611f\u607b\u7136\uff0c\u5fc3\u60f3\uff1a\u8fd9\u7126\u516c\u793c\u4f3c\u4e4e\u4e5f\u975e\u7a77\u51f6\u6781\u6076\u4e4b\u8f88\uff0c\u5f53\u5e74\u505a\u9519\u4e86\u4e8b\uff0c\u73b0\u4e0b\u5374\u5df2\u8bda\u5fc3\u6094\u8fc7\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u542c\u5f97\u4e00\u540d\u5f92\u5f1f\u53eb\u4e86\u58f0\uff1a\u201c\u5e08\u7236\uff01\u201d\u7126\u516c\u793c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u5e08\u7236\u65e2\u4e0d\u613f\u8ddf\u4ed6\u4eec\u5bf9\u654c\uff0c\u90a3\u4e48\u54b1\u4eec\u8fde\u591c\u52a8\u8eab\uff0c\u6682\u4e14\u907f\u4ed6\u4eec\u4e00\u907f\u3002\u5927\u4e08\u592b\u80fd\u5c48\u2026\u2026\u201d\u53e6\u4e00\u4eba\u6025\u9053\uff1a\u201c\u90a3\u600e\u4e48\u6210\uff1f\u5e08\u7236\u4e00\u4e16\u82f1\u540d\uff0c\u96be\u9053\u6015\u4e86\u4ed6\u4eec\uff1f\u201d\u7126\u516c\u793c\u9053\uff1a\u201c\u751a\u4e48\u82f1\u540d\u4e0d\u82f1\u540d\uff0c\u6211\u4e5f\u4e0d\u5728\u4e4e\u4e86\uff0c\u4e0d\u8fc7\u907f\u662f\u907f\u4e0d\u6389\u7684\u3002\u518d\u8bf4\uff0c\u91d1\u9f99\u5e2e\u7684\u5e2e\u4e3b\u8fd9\u4e48\u7f29\u5934\u4e00\u8d70\uff0c\u5e2e\u4e2d\u6570\u5343\u5144\u5f1f\uff0c\u4eca\u540e\u8fd8\u80fd\u633a\u76f4\u8170\u80cc\u505a\u4eba\u5417\uff1f\u660e\u5929\u4e00\u65e9\uff0c\u4f60\u4eec\u5927\u5bb6\u90fd\u8d70\u3002\u6211\u4e00\u4eba\u7559\u5728\u8fd9\u91cc\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4e24\u4e2a\u5f92\u5f1f\u90fd\u6025\u4e86\u8d77\u6765\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u6211\u7559\u7740\u966a\u5e08\u7236\u3002\u201d\u7126\u516c\u793c\u6012\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6211\u5927\u96be\u4e34\u5934\uff0c\u4f60\u4eec\u8fd8\u4e0d\u542c\u6211\u8bdd\u5417\uff1f\u201d\u4e24\u4e2a\u5f92\u5f1f\u4e0d\u6562\u8a00\u8bed\u4e86\u3002\u7126\u516c\u793c\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u5e2e\u5e08\u5a18\u6536\u62fe\u6536\u62fe\uff0c\u77a7\u8f66\u5b50\u5957\u597d\u4e86\u6ca1\u6709\uff1f\u4e5f\u4e0d\u7528\u5e26\u592a\u591a\u4e1c\u897f\uff0c\u8be5\u5c3d\u5feb\u4e0a\u8def\u8981\u7d27\u3002\u201d\u4e24\u4eba\u5634\u91cc\u7b54\u5e94\uff0c\u5374\u53ea\u662f\u7ad9\u7740\u4e0d\u52a8\u3002\u7126\u516c\u793c\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u53bb\u53eb\u5927\u5bb6\u8fdb\u6765\uff01\u201d \u3000\u3000\u4e24\u4eba\u7b54\u5e94\u4e86\uff0c\u5f00\u95e8\u8d70\u51fa\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u5fd9\u5728\u5899\u89d2\u4e00\u7f29\uff0c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u89c1\u897f\u8fb9\u5899\u89d2\u6709\u4e24\u4eba\u4f0f\u7740\uff0c\u770b\u8eab\u5f62\u4e00\u4e2a\u662f\u8ffd\u98ce\u5251\u4e07\u91cc\u98ce\uff0c\u53e6\u4e00\u4e2a\u8eab\u6750\u82d7\u6761\uff0c\u662f\u4e2a\u5973\u5b50\uff0c\u6b63\u662f\u5b59\u4ef2\u541b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u607c\u5979\u5148\u524d\u51fa\u624b\u6b79\u6bd2\uff0c\u8981\u60e9\u6212\u5979\u4e00\u4e0b\uff0c\u6084\u58f0\u5bf9\u9752\u9752\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\uff0c\u53ef\u522b\u52a8\uff01\u201d\u9752\u9752\u8eab\u5b50\u8f7b\u6643\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u504f\u8981\u52a8\u51e0\u52a8\u3002\u201d\u8881\u627f\u5fd7\u5fae\u7b11\uff0c\u4f0f\u4f4e\u4e86\u8eab\uff0c\u89c1\u4e07\u91cc\u98ce\u4e0e\u5b59\u4ef2\u541b\u90fd\u5728\u51dd\u795e\u5411\u91cc\u5f20\u671b\uff0c\u4e8e\u662f\u6084\u6ca1\u58f0\u7684\u4ece\u5b59\u4ef2\u541b\u8eab\u65c1\u4e00\u63a0\u800c\u8fc7\uff0c\u968f\u624b\u5df2\u628a\u5979\u8170\u95f4\u4f69\u5251\u62bd\u5728\u624b\u4e2d\u3002\u8fd9\u4e00\u4e0b\u624b\u6cd5\u8f7b\u6781\u5feb\u6781\uff0c\u5b59\u4ef2\u541b\u5168\u795e\u8d2f\u6ce8\u7684\u77a7\u7740\u7126\u516c\u793c\uff0c\u7adf\u672a\u5bdf\u89c9\u3002 \u3000\u3000\u8881\u627f\u5fd7\u56de\u5230\u9752\u9752\u8eab\u8fb9\u3002\u9752\u9752\u89c1\u4ed6\u5077\u4e86\u4eba\u5bb6\u5927\u59d1\u5a18\u7684\u4f69\u5251\uff0c\u9887\u4e3a\u4e0d\u60a6\u3002\u8881\u627f\u5fd7\u628a\u5251\u9012\u4e86\u7ed9\u5979\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u6536\u7740\uff01\u201d\u9752\u9752\u8fd9\u624d\u9ad8\u5174\u3002 \u3000\u3000\u4e24\u4eba\u53c8\u4ece\u7a97\u7f1d\u4e2d\u5411\u5ba4\u5185\u5f20\u671b\uff0c\u53ea\u89c1\u9646\u7eed\u8fdb\u6765\u4e86\u4e8c\u5341\u591a\u4eba\uff0c\u5e74\u957f\u7684\u5df2\u6709\u56db\u65ec\u5de6\u53f3\u5e74\u7eaa\uff0c\u6700\u5e74\u8f7b\u7684\u5374\u53ea\u6709\u5341\u516d\u4e03\u5c81\uff0c\u60f3\u6765\u90fd\u662f\u7126\u516c\u793c\u7684\u5f92\u5f1f\u4e86\u3002\u4f17\u5f92\u5f1f\u5411\u5e08\u7236\u884c\u4e86\u793c\uff0c\u5782\u624b\u7ad9\u7acb\uff0c\u4eba\u4eba\u8138\u4e0a\u5747\u6709\u6c14\u6124\u4e4b\u8272\u3002 \u3000\u3000\u7126\u516c\u793c\u8138\u8272\u60e8\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e74\u8f7b\u65f6\u8eab\u5728\u7eff\u6797\uff0c\u73b0\u65f6\u4e5f\u4e0d\u5fc5\u5bf9\u5927\u5bb6\u76f8\u7792\u4e86\u3002\u201d\u8881\u627f\u5fd7\u89c1\u4f17\u5f92\u8138\u73b0\u8be7\u5f02\uff0c\u5fc3\u60f3\u539f\u6765\u4ed6\u4eec\u5747\u4e0d\u77e5\u5e08\u7236\u7684\u8eab\u4e16\u7ecf\u5386\u3002 \u3000\u3000\u7126\u516c\u793c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u773c\u4e0b\u4ec7\u4eba\u627e\u4e0a\u95e8\u6765\uff0c\u6211\u8981\u5bf9\u5927\u5bb6\u8bf4\u4e00\u8bf4\u7ed3\u4ec7\u7684\u7f18\u7531\u3002 \u3000\u3000\u201c\u90a3\u4e00\u5e74\u6211\u5728\u53cc\u9f99\u5c97\u5f00\u5c71\u7acb\u67dc\uff0c\u5f1f\u5144\u4eec\u62a5\u8bf4\uff0c\u5c71\u4e1c\u7701\u4e1c\u5156\u9053\u4e18\u9053\u53f0\u5378\u4efb\uff0c\u5e26\u540c\u4e86\u5bb6\u7737\u56de\u7c4d\uff0c\u8981\u4ece\u53cc\u9f99\u5c97\u4e0b\u7ecf\u8fc7\uff0c\u6cb9\u6c34\u5f88\u591a\u3002\u54b1\u4eec\u5728\u7eff\u6797\u7684\uff0c\u5403\u7684\u662f\u6253\u5bb6\u52ab\u820d\u7684\u996d\uff0c\u9047\u5230\u8d2a\u5b98\u6c61\u540f\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u4e00\u6765\u8d2a\u5b98\u641c\u522e\u5f97\u591a\u4e86\uff0c\u52ab\u4e00\u4e2a\u8d2a\u5b98\uff0c\u80dc\u8fc7\u52ab\u4e00\u767e\u4e2a\u5bfb\u5e38\u5ba2\u5546\u3002\u4e8c\u6765\u52ab\u8d2a\u5b98\u4e0d\u4f24\u9634\u9a98\uff0c\u4ed6\u79ef\u7684\u662f\u4e0d\u4e49\u4e4b\u8d22\uff0c\u62ff\u4ed6\u7684\u94f6\u5b50\u54b1\u4eec\u662f\u5fc3\u5b89\u7406\u5f97\u3002\u4e0d\u8fc7\u6253\u542c\u5f97\u62a4\u9001\u4ed6\u7684\uff0c\u5374\u662f\u4e2a\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\uff0c\u662f\u5c71\u4e1c\u6d4e\u5357\u5e9c\u4f1a\u53cb\u9556\u5c40\u7684\u603b\u9556\u5934\u95f5\u5b50\u53f6\uff0c\u90a3\u5c31\u662f\u56e0\u5b50\u534e\u7684\u5144\u957f\u4e86\u2026\u2026\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u8881\u627f\u5fd7\u548c\u9752\u9752\u5df2\u5373\u604d\u7136\uff0c\u5fc3\u60f3\uff1a\u201c\u53cc\u65b9\u7684\u6881\u5b50\u539f\u6765\u662f\u8fd9\u6837\u7ed3\u7684\uff0c\u7126\u516c\u793c\u8981\u52ab\u8d22\uff0c\u95f5\u5b50\u53f6\u8981\u4fdd\u9556\uff0c\u4e89\u6597\u8d77\u6765\uff0c\u95f5\u5b50\u53f6\u4e0d\u654c\u88ab\u6740\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u9762\u503e\u542c\u5ba4\u5185\u7126\u516c\u793c\u7684\u8bf4\u8bdd\uff0c\u4e00\u9762\u65f6\u65f6\u659c\u773c\u5bdf\u770b\u4e07\u91cc\u98ce\u4e0e\u5b59\u4ef2\u541b\u7684\u52a8\u9759\u3002\u8fd9\u65f6\u53ea\u89c1\u5b59\u4ef2\u541b\u4f38\u624b\u5230\u8170\u95f4\u4e00\u6478\uff0c\u7a81\u7136\u8df3\u8d77\uff0c\u53d1\u73b0\u4f69\u5251\u88ab\u4eba\u62bd\u53bb\uff0c\u5fd9\u4e0e\u4e07\u91cc\u98ce\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u4e24\u4eba\u4e0d\u6562\u518d\u884c\u9017\u7559\uff0c\u8d8a\u5899\u8d70\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u597d\u7b11\uff0c\u518d\u542c\u7126\u516c\u793c\u8bf4\u4e0b\u53bb\uff1a\u201c\u2026\u2026\u95f5\u5b50\u53f6\u5728\u6c5f\u6e56\u4e0a\u9887\u6709\u540d\u671b\uff0c\u662f\u4ed9\u90fd\u6d3e\u7684\u9ad8\u624b\u2026\u2026\u201d\u8881\u627f\u5fd7\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u95f5\u6c0f\u5144\u5f1f\u90fd\u662f\u4ed9\u90fd\u6d3e\u7684\u3002\u542c\u5e08\u7236\u8bf4\uff0c\u4ed9\u90fd\u662f\u5185\u5bb6\u6b63\u5b97\uff0c\u6e0a\u6e90\u4e8e\u6b66\u5f53\uff0c\u53ef\u8bf4\u662f\u6b66\u5f53\u6d3e\u7684\u65c1\u652f\u3002\u638c\u95e8\u4eba\u7d20\u7231\u7ed3\u4ea4\uff0c\u548c\u5404\u95e8\u5404\u6d3e\u5e7f\u901a\u58f0\u6c14\u3002\u602a\u4e0d\u5f97\u95f5\u5b50\u534e\u4e00\u4e3e\u4fbf\u9080\u96c6\u4e86\u8fd9\u8bb8\u591a\u80fd\u4eba\u3002\u201d \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u6211\u4e00\u542c\u4e4b\u540e\uff0c\u5012\u4e0d\u6562\u8d38\u7136\u52a8\u624b\u4e86\uff0c\u4e8e\u662f\u4eb2\u81ea\u53bb\u8e29\u76d8\u3002\u90a3\u5929\u665a\u4e0a\u5728\u5ba2\u5e97\u4e2d\u5bdf\u770b\u4ed6\u4eec\u884c\u8e2a\uff0c\u5374\u542c\u5230\u4e86\u4e00\u4ef6\u6c14\u70b8\u4eba\u809a\u5b50\u7684\u4e8b\u3002 \u3000\u3000\u201c\u539f\u6765\u95f5\u5b50\u53f6\u90a3\u4eba\u8d2a\u82b1\u597d\u8272\uff0c\u89c1\u4e18\u9053\u53f0\u7684\u4e8c\u5c0f\u59d0\u751f\u5f97\u7f8e\u8c8c\uff0c\u4fbf\u5b9a\u4e0b\u4e86\u8ba1\u8c0b\u3002\u4ed6\u6697\u4e2d\u4e0e\u98de\u864e\u5be8\u7684\u5f20\u5be8\u4e3b\u7ea6\u597d\uff0c\u53eb\u4ed6\u5728\u98de\u864e\u5be8\u5de6\u8fd1\u4e0b\u624b\uff0c\u62a2\u52ab\u4e18\u9053\u53f0\uff0c\u95f5\u5b50\u53f6\u5047\u88c5\u594b\u529b\u62b5\u6297\uff0c\u7ec8\u4e8e\u5be1\u4e0d\u654c\u4f17\uff0c\u7531\u5f20\u5be8\u4e3b\u6740\u6b7b\u4e18\u9053\u53f0\u5168\u5bb6\uff0c\u62a2\u8d70\u8d22\u7269\uff0c\u5c06\u4e8c\u5c0f\u59d0\u63b3\u53bb\u3002\u95f5\u5b50\u53f6\u7136\u540e\u5b64\u8eab\u72af\u9669\uff0c\u5c06\u4e8c\u5c0f\u59d0\u6551\u51fa\u6765\u3002\u4e8c\u5c0f\u59d0\u5bb6\u7834\u4eba\u4ea1\uff0c\u65e0\u4f9d\u65e0\u9760\uff0c\u53c8\u662f\u611f\u6069\u56fe\u62a5\uff0c\u81ea\u7136\u4f1a\u59d4\u8eab\u4e0b\u5ac1\u4e8e\u4ed6\u3002\u5f20\u5be8\u4e3b\u8981\u8ba8\u597d\u95f5\u5b50\u53f6\uff0c\u53c8\u8d2a\u56fe\u8d22\u5b9d\uff0c\u7b54\u5e94\u4e00\u5207\u9075\u547d\u3002\u4e24\u4eba\u5728\u5bc6\u5ba4\u4e2d\u7a83\u7a83\u79c1\u8bae\uff0c\u90fd\u53eb\u6211\u542c\u89c1\u5566\u3002\u6211\u607c\u6012\u5f02\u5e38\uff0c\u56de\u53bb\u62db\u96c6\u5f1f\u5144\uff0c\u57cb\u4f0f\u98de\u864e\u5be8\u4e4b\u65c1\uff0c\u5230\u4e86\u7ea6\u5b9a\u7684\u65f6\u5019\uff0c\u4e18\u9053\u53f0\u4e00\u884c\u4eba\u679c\u7136\u5230\u6765\u2026\u2026\u201d \u3000\u3000\u8fd9\u756a\u8a00\u8bed\u5b9e\u5927\u51fa\u8881\u627f\u5fd7\u610f\u6599\u4e4b\u5916\uff0c\u53ea\u542c\u7126\u516c\u793c\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u6211\u60f3\u54b1\u4eec\u6b66\u6797\u4e2d\u4eba\uff0c\u867d\u7136\u7a77\u9014\u843d\u9b44\uff0c\u9677\u8eab\u9ed1\u9053\uff0c\u505a\u8fd9\u6ca1\u672c\u94b1\u4e70\u5356\uff0c\u4f46\u5728\u8272\u5b57\u5173\u5934\u4e0a\u603b\u8981\u5149\u660e\u78ca\u843d\uff0c\u624d\u4e0d\u5931\u597d\u6c49\u5b50\u884c\u5f84\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u95f5\u5b50\u53f6\u5982\u6b64\u65e0\u803b\u3002\u4ed6\u662f\u540d\u95e8\u6b63\u6d3e\u7684\u5f1f\u5b50\uff0c\u6c5f\u6e56\u4e0a\u4e5f\u7b97\u662f\u9887\u6709\u540d\u671b\uff0c\u8eab\u4e3a\u603b\u9556\u5934\uff0c\u5374\u505a\u8fd9\u79cd\u52fe\u5f53\u3002\u6211\u773c\u89c1\u5f20\u5be8\u4e3b\u7387\u9886\u4e86\u55bd\u7f57\u524d\u6765\u62a2\u52ab\uff0c\u95f5\u5b50\u53f6\u5374\u88c5\u8154\u4f5c\u52bf\uff0c\u5927\u58f0\u53f1\u559d\uff0c\u6325\u5251\u4e71\u4e03\u516b\u7cdf\u7684\u5047\u6253\uff0c\u4e0d\u7531\u5f97\u706b\u6c14\u76f4\u5192\uff0c\u5c31\u8df3\u5c06\u51fa\u6765\u8ddf\u4ed6\u52a8\u624b\u3002\u95f5\u5b50\u53f6\u5251\u6cd5\u679c\u7136\u4e86\u5f97\uff0c\u672c\u6765\u6211\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4f46\u6211\u53eb\u7834\u4e86\u4ed6\u7684\u9b3c\u8ba1\uff0c\u628a\u4ed6\u7684\u56fe\u8c0b\u4e00\u4e94\u4e00\u5341\u7684\u90fd\u8bf4\u4e86\u51fa\u6765\u3002\u4ed6\u7f9e\u6124\u4ea4\u52a0\uff0c\u6c89\u4e0d\u4f4f\u6c14\uff0c\u7ec8\u4e8e\u7ed9\u6211\u4e00\u5200\u780d\u6b7b\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u5f92\u5f1f\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u4eba\u672c\u6765\u8be5\u6740\uff0c\u54b1\u4eec\u4f55\u5fc5\u6015\u4ed6\u4eec\uff1f\u7b49\u660e\u65e5\u5bf9\u5934\u6765\u4e86\uff0c\u5927\u5bb6\u6296\u5f00\u6765\u8bf4\u4e2a\u660e\u767d\uff0c\u5c31\u7b97\u4ed6\u5144\u5f1f\u5b9a\u8981\u62a5\u4ec7\uff0c\u522b\u7684\u4eba\u4e5f\u4e0d\u89c1\u5f97\u90fd\u4e0d\u660e\u662f\u975e\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4e0d\u9519\u554a\uff0c\u8981\u662f\u8fd9\u59d3\u7126\u7684\u679c\u771f\u662f\u8def\u89c1\u4e0d\u5e73\uff0c\u6740\u4e86\u95f5\u5b50\u53f6\uff0c\u6b66\u6797\u4e2d\u81ea\u6709\u516c\u8bba\uff0c\u53ea\u6015\u4ed6\u8fd9\u756a\u8bdd\u672a\u5fc5\u53ef\u4fe1\uff0c\u53c8\u6216\u8005\u53e6\u6709\u9690\u60c5\u3002\u201d \u3000\u3000\u7126\u516c\u793c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u6740\u4e86\u90a3\u59d3\u95f5\u7684\u4e4b\u540e\uff0c\u4f55\u5c1d\u4e0d\u77e5\u95ef\u4e86\u5927\u7978\u3002\u4ed6\u662f\u4ed9\u90fd\u6d3e\u4e2d\u54cd\u5f53\u5f53\u7684\u89d2\u8272\uff0c\u4ed6\u5e08\u7236\u9ec4\u6728\u9053\u4eba\u51b3\u4e0d\u80fd\u5e72\u4f11\uff0c\u82e5\u662f\u7387\u9886\u95e8\u4e0b\u4f17\u5f1f\u5b50\u5411\u6211\u5bfb\u4ec7\uff0c\u6211\u4fbf\u6709\u4e09\u5934\u516d\u81c2\u4e5f\u62b5\u6321\u4e0d\u4f4f\u3002\u5e78\u597d\u6211\u624b\u4e0b\u628a\u90a3\u5f20\u5be8\u4e3b\u622a\u4f4f\u4e86\uff0c\u6211\u903c\u7740\u4ed6\u5199\u4e86\u4e00\u5f20\u4f0f\u8fa9\uff0c\u5c06\u95f5\u5b50\u53f6\u7684\u5978\u8c0b\u6e05\u6e05\u695a\u695a\u7684\u5199\u5728\u4e0a\u9762\u3002 \u3000\u3000\u201c\u90a3\u4e18\u9053\u53f0\u81ea\u7136\u5bf9\u6211\u5341\u5206\u611f\u6fc0\uff0c\u9001\u4e86\u6211\u4e8c\u5343\u4e24\u94f6\u5b50\u3002\u6211\u60f3\u672c\u6765\u662f\u8981\u62a2\u5149\u4e86\u4f60\u7684\uff0c\u73b0\u4e0b\u96be\u5f97\u5f3a\u76d7\u53d1\u5584\u5fc3\uff0c\u505a\u4e86\u4e00\u4ef6\u884c\u4fa0\u4ed7\u4e49\u4e4b\u4e8b\uff0c\u7d22\u6027\u8fde\u4e00\u4e24\u94f6\u5b50\u4e5f\u4e0d\u6536\u4f60\u7684\u3002\u4e18\u9053\u53f0\u5343\u6069\u4e07\u8c22\uff0c\u5199\u4e86\u4e00\u5c01\u8c22\u4e66\uff0c\u8a00\u660e\u8be6\u7ec6\u7ecf\u8fc7\uff0c\u8fd8\u53eb\u4f1a\u53cb\u9556\u5c40\u968f\u540c\u4fdd\u9556\u7684\u4e24\u4e2a\u9556\u5934\u753b\u62bc\uff0c\u4f5c\u4e2a\u89c1\u8bc1\u3002\u8fd9\u4e24\u4e2a\u9556\u5934\u672c\u6765\u5e76\u4e0d\u77e5\u60c5\uff0c\u542c\u5f20\u5be8\u4e3b\u548c\u98de\u864e\u5be8\u5176\u4f59\u76d7\u4f19\u8bf4\u5f97\u660e\u767d\uff0c\u5927\u9a82\u95f5\u5b50\u53f6\u65e0\u803b\uff0c\u8bf4\u9669\u4e9b\u7ed9\u4ed6\u5356\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u6027\u547d\u4e5f\u5f97\u9001\u5728\u8fd9\u91cc\uff0c\u53cd\u800c\u5411\u6211\u9053\u52b3\uff0c\u5f88\u5957\u4ea4\u60c5\u3002 \u3000\u3000\u201c\u6211\u505a\u4e86\u8fd9\u4ef6\u4e8b\u540e\uff0c\u77e5\u9053\u4e0d\u80fd\u518d\u5728\u9ed1\u9053\u4e2d\u6df7\u4e86\uff0c\u4e8e\u662f\u548c\u4f17\u5144\u5f1f\u6563\u4e86\u4f19\uff0c\u62ff\u4e86\u90a3\u4e24\u5c01\u4fe1\uff0c\u4e0a\u4ed9\u90fd\u5c71\u9f99\u864e\u89c2\u53bb\u89c1\u9ec4\u6728\u9053\u4eba\u3002 \u3000\u3000\u201c\u90a3\u65f6\u4ed9\u90fd\u6d3e\u95e8\u4eba\u5df2\u5f97\u77e5\u8baf\u606f\uff0c\u4e0d\u7b49\u6211\u4e0a\u5c71\uff0c\u4e2d\u9014\u62e6\u4f4f\u4e86\u6211\u5c31\u548c\u6211\u4e3a\u96be\uff0c\u5927\u5bb6\u6c14\u52bf\u6c79\u6c79\uff0c\u4e5f\u4e0d\u5bb9\u6211\u5206\u8fa9\u3002\u5e78\u4e8f\u4e00\u4f4d\u6c5f\u6e56\u5947\u4fa0\u8def\u8fc7\u89c1\u5230\uff0c\u62d4\u5251\u76f8\u52a9\uff0c\u5c06\u6211\u62a4\u9001\u4e0a\u5c71\uff0c\u548c\u9ec4\u6728\u9053\u957f\u4e09\u5bf9\u516d\u9762\u7684\u8bf4\u4e86\u4e2a\u6e05\u695a\u3002\u90a3\u9ec4\u6728\u9053\u957f\u5f88\u8bc6\u5927\u4f53\uff0c\u7ea6\u675f\u95e8\u4eba\uff0c\u6c38\u8fdc\u4e0d\u5f97\u5411\u6211\u5bfb\u4ec7\u3002\u4f46\u4e3a\u4e86\u4ed9\u90fd\u6d3e\u7684\u58f0\u540d\uff0c\u8981\u6211\u522b\u5728\u5916\u5ba3\u626c\u6b64\u4e8b\u3002\u6211\u81ea\u7136\u7b54\u5e94\uff0c\u4e0b\u5c71\u4e4b\u540e\uff0c\u4ece\u6b64\u7edd\u53e3\u4e0d\u63d0\uff0c\u56e0\u6b64\u8fd9\u4e8b\u7684\u539f\u59d4\uff0c\u6c5f\u6e56\u4e0a\u77e5\u9053\u7684\u4eba\u6781\u5c11\u3002\u90a3\u65f6\u95f5\u5b50\u53f6\u7684\u5144\u5f1f\u95f5\u5b50\u534e\u5e74\u7eaa\u5e7c\u5c0f\uff0c\u591a\u534a\u4e0d\u77e5\u5185\u56e0\uff0c\u4ed9\u90fd\u6d3e\u7684\u95e8\u4eba\u81ea\u7136\u4e5f\u4e0d\u4f1a\u8ddf\u4ed6\u8bf4\u3002\u201d \u3000\u3000\u4e00\u540d\u95e8\u5f92\u9053\uff1a\u201c\u5e08\u7236\uff0c\u90a3\u4e24\u5c01\u4fe1\u4f60\u8fd8\u6536\u7740\u4e48\uff1f\u201d \u3000\u3000\u7126\u516c\u793c\u6447\u5934\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u602a\u6211\u778e\u4e86\u773c\u73e0\u3001\u4e0d\u8bc6\u5f97\u4eba\u4e86\u3002\u53bb\u5e74\u79cb\u5929\uff0c\u6709\u670b\u53cb\u4f20\u8bdd\u7ed9\u6211\uff0c\u8bf4\u95f5\u5b50\u53f6\u7684\u5144\u5f1f\u5728\u4ed9\u90fd\u6d3e\u827a\u6210\u4e0b\u5c71\uff0c\u5f97\u77e5\u6211\u662f\u4ed6\u6740\u5144\u4ec7\u4eba\uff0c\u8981\u6765\u62a5\u4ec7\u3002\u540e\u6765\u6211\u6253\u63a2\u51fa\u6765\uff0c\u592a\u767d\u4e09\u82f1\u8ddf\u95f5\u5b50\u534e\u4ea4\u60c5\u4e0d\u5dee\u3002\u4ed6\u4eec\u662f\u6211\u591a\u5e74\u8001\u53cb\uff0c\u867d\u7136\u5df2\u6709\u5341\u51e0\u5e74\u4e0d\u89c1\u9762\uff0c\u4f46\u5927\u5bb6\u5e74\u8f7b\u65f6\u5728\u7eff\u6797\u9053\u4e0a\u662f\u4e00\u8d77\u51fa\u6b7b\u5165\u751f\u8fc7\u7684\u3002 \u3000\u3000\u4e8e\u662f\u6211\u4fbf\u53bb\u627e\u4e09\u82f1\u4e2d\u7684\u53f2\u5bb6\u5144\u5f1f\u2026\u2026\u201d \u3000\u3000\u4e00\u540d\u95e8\u5f92\u63d2\u5634\u9053\uff1a\u201c\u554a\uff0c\u5e08\u7236\u53bb\u5e74\u814a\u6708\u8d76\u53bb\u9655\u897f\uff0c\u8fde\u5e74\u4e5f\u4e0d\u5728\u5bb6\u91cc\u8fc7\uff0c\u5c31\u4e3a\u8fd9\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u5230\u4e86\u9655\u897f\u79e6\u5cad\u592a\u767d\u5c71\u53f2\u5bb6\u5144\u5f1f\u5bb6\u91cc\uff0c\u6ee1\u60f3\u5bd2\u5929\u814a\u6708\uff0c\u54e5\u513f\u4fe9\u4e00\u5b9a\u5728\u5bb6\uff0c\u54ea\u77e5\u5e76\u4e0d\u89c1\u4eba\uff0c\u5374\u539f\u6765\u4e0a\u8fbd\u4e1c\u53bb\u4e86\uff0c\u8bf4\u662f\u53bb\u505a\u4e00\u7b14\u5927\u4e70\u5356\u3002\u6211\u5728\u4ed6\u4eec\u5bb6\u7b49\u4e86\u5341\u591a\u5929\uff0c\u53f2\u79c9\u5149\u3001\u79c9\u6587\u5144\u5f1f\u624d\u56de\u6765\uff0c\u8001\u670b\u53cb\u4f1a\u9762\uff0c\u5927\u5bb6\u5341\u5206\u6b22\u559c\u3002\u6211\u628a\u8ddf\u95f5\u5bb6\u7ed3\u4ec7\u7684\u4e8b\u4e00\u8bf4\uff0c\u53f2\u8001\u5927\u5f53\u573a\u5373\u62cd\u80f8\u819b\u62c5\u4fdd\u6ca1\u4e8b\u3002\u6211\u628a\u4e18\u9053\u53f0\u7684\u4fe1\u4e0e\u5f20\u5be8\u4e3b\u7684\u4f0f\u8fa9\u90fd\u7ed9\u4e86\u4ed6\u3002\u4e24\u5144\u5f1f\u90fd\u8bf4\uff0c\u53ea\u8981\u62ff\u53bb\u95f5\u5b50\u534e\u4e00\u770b\uff0c\u95f5\u8001\u4e8c\u54ea\u91cc\u8fd8\u6709\u8138\u6765\u627e\u6211\u62a5\u4ec7\uff0c\u53ea\u6015\u8fd8\u8981\u627e\u4eba\u6765\u8d54\u8bdd\u8c22\u7f6a\uff0c\u6c42\u6211\u522b\u628a\u4ed6\u5144\u957f\u7684\u4e11\u4e8b\u5ba3\u626c\u51fa\u53bb\u5462\u3002\u4ed6\u5144\u5f1f\u5bf9\u6211\u6bb7\u52e4\u62db\u5f85\uff0c\u53cd\u6b63\u6211\u6ca1\u751a\u4e48\u8981\u7d27\u4e8b\uff0c\u5929\u5929\u8ddf\u4ed6\u4eec\u4e00\u8d77\u6253\u730e\u3001\u542c\u620f\u3002\u4ed6\u5144\u5f1f\u4ece\u8fbd\u4e1c\u5e26\u6765\u4e86\u4e0d\u5c11\u4eba\u53c2\u3001\u8c82\u76ae\uff0c\u9001\u4e86\u6211\u4e00\u6279\u3002 \u3000\u3000\u201c\u6709\u4e00\u5929\u4e09\u4eba\u559d\u9152\u95f2\u8c08\uff0c\u53f2\u8001\u5927\u5ffd\u8bf4\u5927\u660e\u7684\u6c14\u6570\u5df2\u5b8c\uff0c\u54b1\u54e5\u513f\u4eec\u90fd\u662f\u4e00\u526f\u597d\u8eab\u624b\uff0c\u4e3a\u751a\u4e48\u4e0d\u6295\u6548\u660e\u4e3b\uff0c\u505a\u4e2a\u5f00\u56fd\u529f\u81e3\uff1f \u3000\u3000\u6211\u8bf4\u53bb\u6295\u95ef\u738b\uff0c\u5e72\u4e00\u756a\u4e8b\u4e1a\uff0c\u5012\u4e5f\u4e0d\u9519\u3002\u4ed6\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u674e\u81ea\u6210\u662f\u571f\u532a\u6d41\u5bc7\uff0c\u6210\u5f97\u751a\u4e48\u6c14\u5019\u3002\u773c\u89c1\u6ee1\u6e05\u5175\u52bf\u65e0\u654c\uff0c\u6307\u65e5\u5165\u5173\uff0c\u8981\u662f\u6211\u80af\u6295\u6548\uff0c\u4ed6\u5144\u5f1f\u53ef\u5728\u4e5d\u738b\u7237\u9762\u524d\u529b\u4fdd\u3002\u6211\u4e00\u542c\u4e4b\u4e0b\uff0c\u767b\u65f6\u5927\u6012\uff0c\u9a82\u4ed6\u4eec\u5fd8\u4e86\u81ea\u5df1\u662f\u751a\u4e48\u4eba\uff0c\u600e\u4e48\u597d\u7aef\u7aef\u7684\u5927\u660e\u8c6a\u6770\uff0c\u7adf\u53bb\u6295\u964d\u80e1\u5974\uff1f\u90a3\u5c82\u4e0d\u662f\u53bb\u505a\u4e0d\u8981\u8138\u7684\u6c49\u5978\uff1f\u6b7b\u4e86\u4e4b\u540e\u4e5f\u6ca1\u9762\u76ee\u53bb\u89c1\u7956\u5b97\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u60f3\u7126\u516c\u793c\u8fd9\u4eba\u867d\u662f\u76d7\u8d3c\u51fa\u8eab\uff0c\u662f\u975e\u4e4b\u9645\u5012\u4e5f\u770b\u5f97\u660e\u767d\uff0c\u9047\u4e0a\u4e86\u5927\u4e8b\u5012\u662f\u633a\u4e0d\u542b\u7cca\u7684\u3002 \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u5f53\u65f6\u6211\u62cd\u6848\u5927\u9a82\uff0c\u4e09\u4eba\u5435\u4e86\u4e00\u573a\u3002\u7b2c\u4e8c\u65e5\u53f2\u5bb6\u5144\u5f1f\u5411\u6211\u9053\u6b49\uff0c\u53f2\u8001\u5927\u8bf4\u6628\u5929\u559d\u6211\u4e86\u9152\uff0c\u4e0d\u77e5\u8bf4\u4e86\u4e9b\u751a\u4e48\u80e1\u6d82\u8bdd\uff0c\u8981\u6211\u4e0d\u53ef\u4ecb\u610f\u3002\u6211\u4eec\u662f\u5341\u591a\u5e74\u7684\u8001\u53cb\uff0c\u5435\u8fc7\u4e86\u4e5f\u5c31\u7b97\u4e86\u3002\u4ed6\u4eec\u4e00\u822c\u7684\u6bb7\u52e4\u62db\u5f85\uff0c\u518d\u4e5f\u4e0d\u63d0\u6b64\u4e8b\u3002\u6211\u5728\u9655\u897f\u53c8\u4f4f\u4e86\u5341\u591a\u5929\uff0c\u8fd9\u624d\u56de\u5230\u5357\u4eac\u3002 \u3000\u3000\u201c\u54ea\u77e5\u53f2\u5bb6\u5144\u5f1f\u7adf\u662f\u72fc\u5fc3\u72d7\u80ba\uff0c\u975e\u4f46\u4e0d\u53bb\u5411\u95f5\u5b50\u534e\u89e3\u91ca\uff0c\u53cd\u800c\u4ece\u4e2d\u6311\u62e8\uff0c\u5927\u4e3e\u7ea6\u4eba\uff0c\u6574\u6574\u7b79\u5212\u4e86\u534a\u5e74\u3002\u6211\u53ef\u5168\u7ed9\u8499\u5728\u9f13\u91cc\uff0c\u534a\u70b9\u4e5f\u6ca1\u5f97\u5230\u98ce\u58f0\uff0c\u4e00\u5fc3\u53ea\u9053\u53f2\u5bb6\u5144\u5f1f\u5df2\u8ddf\u95f5\u5b50\u534e\u8bf4\u660e\u771f\u76f8\uff0c\u4ed6\u81ea\u7136\u4e0d\u4f1a\u518d\u8d77\u5bfb\u4ec7\u4e4b\u5fc3\u3002\u7a81\u7136\u95f4\u6674\u5929\u9739\u96f3\uff0c\u8fd9\u8bb8\u591a\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\u5230\u4e86\u5357\u4eac\u3002 \u3000\u3000\u201c\u90a3\u4e24\u5c01\u4fe1\u53f2\u5bb6\u5144\u5f1f\u591a\u534a\u4e0d\u4f1a\u7ed9\u95f5\u5b50\u534e\u77a7\u3002\u4e8b\u60c5\u9694\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u5f53\u65f6\u5728\u573a\u7684\u4eba\u4e0d\u662f\u6b7b\u4e86\uff0c\u5c31\u662f\u6563\u5f97\u4e0d\u77e5\u53bb\u5411\uff0c\u4efb\u51ed\u6211\u600e\u4e48\u5206\u8bf4\uff0c\u95f5\u5b50\u534e\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002\u53ea\u6015\u4ed6\u6012\u6c14\u66f4\u5927\uff0c\u53cd\u800c\u4f1a\u8bf4\u6211\u778e\u9020\u8c23\u8a00\uff0c\u8bfd\u8c24\u4ed6\u5df2\u53bb\u4e16\u7684\u5144\u957f\u2026\u2026\u6211\u5c31\u662f\u4e0d\u61c2\uff0c\u6211\u548c\u53f2\u5bb6\u5144\u5f1f\u7d20\u6765\u4ea4\u597d\uff0c\u5c31\u7b97\u6709\u8fc7\u4e00\u6b21\u8a00\u8bed\u5931\u548c\uff0c\u4e5f\u7b97\u4e0d\u4e86\u751a\u4e48\u3002\u4f55\u5fc5\u8fd9\u822c\u5904\u5fc3\u79ef\u8651\u3001\u5927\u4e3e\u800c\u6765\uff1f\u77a7\u8fd9\u756a\u5e03\u7f6e\uff0c\u4e0d\u662f\u660e\u660e\u8981\u628a\u6211\u8d76\u5c3d\u6740\u7edd\u4e48\uff1f\u5230\u5e95\u6211\u6709\u751a\u4e48\u4e8b\u5f97\u7f6a\u4e86\u4ed6\u4eec\uff0c\u5b9e\u5728\u60f3\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u4f17\u5f1f\u5b50\u542c\u4e86\u8fd9\u756a\u8bdd\uff0c\u90fd\u6c14\u607c\u5f02\u5e38\uff0c\u4e03\u5634\u516b\u820c\uff0c\u51b3\u610f\u4e0e\u53f2\u5bb6\u5144\u5f1f\u4ee5\u6b7b\u76f8\u62da\u3002 \u3000\u3000\u7126\u516c\u793c\u624b\u4e00\u6446\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u51fa\u53bb\u5427\u3002\u4eca\u665a\u6211\u8bf4\u7684\u8bdd\uff0c\u4e0d\u8bb8\u6f0f\u51fa\u53bb\u4e00\u53e5\u3002\u6211\u66fe\u5728\u9ec4\u6728\u9053\u957f\u9762\u524d\u8d77\u8fc7\u8a93\uff0c\u51b3\u4e0d\u5c06\u95f5\u5b50\u53f6\u7684\u4e8b\u5411\u5916\u4eba\u6cc4\u6f0f\u3002\u54b1\u4eec\u662f\u81ea\u5df1\u4eba\uff0c\u8bf4\u4e00\u8bf4\u8fd8\u4e0d\u6253\u7d27\u3002\u5b81\u53ef\u4ed6\u4eec\u65e0\u4e49\uff0c\u6211\u53ef\u4e0d\u80fd\u8a00\u800c\u65e0\u4fe1\u3002\u6211\u6b7b\u4e4b\u540e\uff0c\u8c01\u90fd\u4e0d\u8bb8\u8d77\u5fc3\u62a5\u4ec7\uff0c\u53ea\u987b\u63d0\u5230\u2018\u62a5\u4ec7\u2019\u4e8c\u5b57\uff0c\u4fbf\u662f\u5bf9\u6211\u4e0d\u4f4f\uff0c\u91d1\u9f99\u5e2e\u4e0a\u4e0b\uff0c\u52a1\u987b\u9075\u4f9d\u3002\u201d\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53eb\u5e08\u5f1f\u3001\u5e08\u59b9\u6765\u3002\u201d\u4f17\u95e8\u5f92\u4eba\u4eba\u8138\u73b0\u60b2\u6124\u4e4b\u8272\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8ddf\u7740\u95e8\u5e37\u6380\u5f00\uff0c\u8fdb\u6765\u4e00\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5973\uff0c\u4e00\u4e2a\u4e03\u516b\u5c81\u7684\u7537\u5b69\u3002\u90a3\u5c11\u5973\u8138\u6709\u6cea\u75d5\uff0c\u53eb\u4e86\u4e00\u58f0\u201c\u7239\uff01\u201d\u6251\u5230\u7126\u516c\u793c\u6000\u91cc\u3002 \u3000\u3000\u7126\u516c\u793c\u8f7b\u8f7b\u629a\u6478\u5979\u7684\u5934\u53d1\uff0c\u534a\u664c\u4e0d\u8bed\uff0c\u90a3\u5c11\u5973\u53ea\u662f\u62bd\u62bd\u564e\u564e\u7684\u54ed\uff0c\u90a3\u5b69\u5b50\u7741\u5927\u4e86\u773c\u775b\uff0c\u4e0d\u77e5\u59ca\u59ca\u4e3a\u751a\u4e48\u4f24\u5fc3\u3002\u7126\u516c\u793c\u9053\uff1a\u201c\u5988\u5988\u4e1c\u897f\u90fd\u6536\u62fe\u597d\u4e86\u5417\uff1f\u201d\u90a3\u5c11\u5973\u70b9\u70b9\u5934\u3002\u7126\u516c\u793c\u9053\uff1a\u201c\u5f1f\u5f1f\u957f\u5927\u4e4b\u540e\uff0c\u4f60\u6559\u4ed6\u597d\u597d\u5ff5\u4e66\u8015\u7530\uff0c\u53ef\u662f\u5343\u4e07\u522b\u8003\u8bd5\u505a\u5b98\uff0c\u4e5f\u4e0d\u8981\u518d\u5b66\u6b66\u4e86\u3002\u201d\u90a3\u5c11\u5973\u54ed\u9053\uff1a\u201c\u5f1f\u5f1f\u8981\u5b66\u6b66\u7684\uff0c\u5b66\u597d\u4e86\u5c06\u6765\u7ed9\u7239\u7239\u62a5\u4ec7\u3002\u201d \u3000\u3000\u7126\u516c\u793c\u6012\u559d\uff1a\u201c\u80e1\u8bf4\uff01\u4f60\u8981\u628a\u6211\u5148\u6c14\u6b7b\u5417\uff1f\u2018\u62a5\u4ec7\u2019\u4e24\u5b57\uff0c\u63d0\u4e5f\u4f11\u63d0\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u67d4\u58f0\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u6028\u6028\u76f8\u62a5\uff0c\u4f55\u65f6\u65b9\u4e86\uff1f\u4e0d\u5982\u505a\u4e2a\u5b89\u4efd\u5b88\u5df1\u7684\u8001\u767e\u59d3\uff0c\u5f97\u7ec8\u5929\u5e74\u3002\u4f60\u5f1f\u5f1f\u8d44\u8d28\u4e0d\u597d\uff0c\u5b66\u6b66\u51b3\u8ba1\u5b66\u4e0d\u5230\u6211\u4e00\u534a\u529f\u592b\u3002\u5c31\u7b97\u662f\u6211\u5427\uff0c\u4eca\u65e5\u4e5f\u88ab\u4eba\u5982\u6b64\u903c\u8feb\uff0c\u4e0d\u5f97\u5584\u7ec8\u2026\u2026\u5509\uff0c\u53ea\u662f\u6ca1\u89c1\u5230\u4f60\u8bf4\u597d\u5a46\u5bb6\uff0c\u7ec8\u662f\u4e00\u6869\u5fc3\u4e8b\u672a\u4e86\u2026\u2026\u4f60\u8ddf\u5927\u5bb6\u8bf4\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u91d1\u9f99\u5e2e\u7684\u4e8b\uff0c\u90fd\u542c\u526f\u5e2e\u4e3b\u9ad8\u53d4\u53d4\u7684\u5429\u5490\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u8fd9\u5c31\u6d3e\u4eba\u5230\u51e4\u9633\u53bb\u627e\u9ad8\u53d4\u53d4\u6765\u3002\u201d \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u600e\u4e48\u4f60\u8fd8\u4e0d\u660e\u767d\u6211\u7684\u5fc3\u601d\uff1f\u628a\u9ad8\u53d4\u53d4\u627e\u6765\uff0c\u4ed6\u662f\u706b\u7206\u9739\u96f3\u7684\u6027\u5b50\uff0c\u5c82\u5bb9\u522b\u4eba\u6b3a\u6211\uff1f\u8fd9\u6837\u4e00\u6765\uff0c\u773c\u89c1\u5c31\u8981\u5927\u52a8\u5200\u67aa\uff0c\u4e0d\u77e5\u8981\u6b7b\u4f24\u591a\u5c11\u4eba\u547d\u3002\u5c31\u7b97\u6211\u9003\u5f97\u4e00\u6761\u6027\u547d\uff0c\u8ba9\u51e0\u767e\u5144\u5f1f\u4e3a\u6211\u800c\u6b7b\uff0c\u4e8e\u5fc3\u4f55\u5fcd\uff1f\u4f60\u53bb\u5427\uff01\u201d\u62b1\u8d77\u513f\u5b50\uff0c\u5728\u4ed6\u8138\u4e0a\u4eb2\u4e86\u4eb2\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e56\u513f\u5b50\uff0c\u4eca\u540e\u53ef\u5f97\u542c\u59ca\u59ca\u7684\u8bdd\u3002\u201d \u3000\u3000\u90a3\u5b69\u5b50\u9053\uff1a\u201c\u662f\uff0c\u7239\u7239\uff0c\u4f60\u4e3a\u751a\u4e48\u54ed\u4e86\uff1f\u201d\u7126\u516c\u793c\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u51e0\u65f6\u54ed\u4e86\uff1f\u201d\u5c06\u5b69\u5b50\u653e\u4e0b\u5730\u6765\uff0c\u6478\u6478\u4ed6\u5934\u9876\uff0c\u8138\u4e0a\u663e\u5f97\u7231\u601c\u6a2a\u6ea2\uff0c\u4f3c\u4e4e\u751f\u6b7b\u6c38\u522b\uff0c\u751a\u662f\u4e0d\u820d\u3002 \u3000\u3000\u7126\u59d1\u5a18\u6cea\u6d41\u6ee1\u9762\uff0c\u7275\u4e86\u5144\u5f1f\u7684\u624b\u51fa\u53bb\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u505c\u6b65\u56de\u5934\uff0c\u9053\uff1a\u201c\u7239\uff0c\u96be\u9053\u4f60\u9664\u4e86\u6b7b\u7ed9\u4ed6\u4eec\u770b\u4e4b\u5916\uff0c\u771f\u7684\u6ca1\u7b2c\u4e8c\u6761\u8def\u4e86\uff1f\u201d \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u751a\u4e48\u8def\u5b50\u6211\u90fd\u60f3\u8fc7\u4e86\uff0c\u5982\u80fd\u4e0d\u6b7b\uff0c\u96be\u9053\u4e0d\u60f3\u4e48\uff1f\u5509\uff01\u8fd9\u4e16\u4e0a\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u6551\u5f97\u6211\u6027\u547d\uff0c\u53ef\u662f\u8fd9\u4eba\u591a\u534a\u5df2\u4e0d\u5728\u4e16\u4e86\u3002\u201d \u3000\u3000\u7126\u59d1\u5a18\u8138\u4e0a\u9732\u51fa\u5149\u5f69\uff0c\u5fd9\u8d70\u8fd1\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u7239\uff0c\u90a3\u662f\u8c01\uff1f \u3000\u3000\u6216\u8bb8\u4ed6\u6ca1\u6709\u6b7b\u5462\uff1f\u201d\u7126\u516c\u793c\u9053\uff1a\u201c\u8fd9\u4f4d\u6069\u516c\u59d3\u590f\uff0c\u5916\u53f7\u53eb\u505a\u91d1\u86c7\u90ce\u541b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u542c\u4e86\uff0c\u90fd\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u7126\u516c\u793c\u53c8\u9053\uff1a\u201c\u4ed6\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u4f4d\u5947\u4fa0\uff0c\u6211\u6740\u95f5\u5b50\u53f6\u7684\u539f\u59d4\uff0c\u4ed6\u662f\u77e5\u9053\u5f97\u6e05\u6e05\u695a\u695a\u7684\u3002\u5f53\u5e74\u4ed9\u90fd\u6d3e\u5341\u4e00\u540d\u5927\u5f1f\u5b50\u8ddf\u6211\u4e3a\u96be\uff0c\u5168\u4ed7\u4ed6\u72ec\u529b\u9a71\u9000\uff0c\u62a4\u9001\u6211\u4e0a\u4ed9\u90fd\u5c71\u89c1\u9ec4\u6728\u9053\u4eba\u3002\u73b0\u4e0b\u9ec4\u6728\u9053\u4eba\u4e91\u6e38\u79bb\u5c71\uff0c\u591a\u5e74\u6765\u4e0d\u77e5\u53bb\u5411\uff0c\u6599\u6765\u65e9\u5df2\u901d\u4e16\u3002\u542c\u8bf4\u91d1\u86c7\u90ce\u541b\u5341\u591a\u5e74\u524d\u906d\u4eba\u6697\u7b97\uff0c\u4e5f\u5df2\u4e0d\u5728\u4eba\u4e16\u3002\u6211\u5927\u6069\u4e0d\u62a5\uff0c\u5fc3\u4e2d\u5e38\u89c9\u4e0d\u5b89\u3002\u53ea\u8981\u8fd9\u4eba\u8fd8\u6d3b\u7740\u2026\u2026\u5509\uff0c\u4f60\u4eec\u53bb\u5427\u3002\u201d\u7126\u59d1\u5a18\u795e\u8272\u51c4\u7136\uff0c\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5411\u9752\u9752\u4e00\u4f5c\u624b\u52bf\uff0c\u6084\u6084\u8ddf\u5728\u4e24\u4eba\u8eab\u540e\uff0c\u6765\u5230\u4e00\u5ea7\u82b1\u56ed\uff0c\u773c\u89c1\u56db\u4e0b\u65e0\u4eba\uff0c\u8881\u627f\u5fd7\u7a81\u7136\u98de\u8eab\u62a2\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u4f60\u60f3\u4e0d\u60f3\u6551\u4f60\u7239\u7239\uff1f\u201d \u3000\u3000\u7126\u59d1\u5a18\u4e00\u60ca\u3002\u62d4\u5251\u5728\u624b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8981\u6551\u4f60\u7239\u7239\uff0c\u5c31\u8ddf\u6211\u6765\uff01\u201d\u9661\u7136\u4e00\u4e2a\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u8f7b\u98d8\u98d8\u8dc3\u51fa\u5899\u5916\u3002\u9752\u9752\u8fde\u7eed\u4e09\u8dc3\uff0c\u7ffb\u8fc7\u5899\u5934\u3002\u7126\u59d1\u5a18\u60f3\u4e0d\u5230\u8881\u627f\u5fd7\u7684\u8f7b\u8eab\u529f\u592b\u7adf\u80fd\u5982\u6b64\u4e86\u5f97\u3002\u5b9e\u662f\u4ece\u6240\u672a\u89c1\uff0c\u4e00\u6014\u4e4b\u4e0b\uff0c\u4ed7\u5251\u7ffb\u5899\u8ffd\u51fa\u3002 \u3000\u3000\u5979\u8ffd\u4e86\u4e00\u6bb5\u8def\uff0c\u8d77\u4e86\u7591\u60e7\u4e4b\u5fc3\uff0c\u7a81\u7136\u505c\u6b65\u4e0d\u8ffd\uff0c\u8f6c\u8eab\u60f3\u56de\u3002\u521a\u56de\u8fc7\u8eab\u6765\uff0c\u8eab\u65c1\u4e00\u9635\u98ce\u63a0\u8fc7\uff0c\u8170\u91cc\u7684\u98d8\u5e26\u626c\u4e86\u8d77\u6765\uff0c\u53d1\u89c9\u624b\u8155\u5fae\u9ebb\uff0c\u624b\u6307\u4e00\u677e\uff0c\u957f\u5251\u5df2\u88ab\u8881\u627f\u5fd7\u593a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7126\u59d1\u5a18\u5927\u60ca\uff0c\u5175\u5203\u8131\u624b\uff0c\u9000\u8def\u53c8\u88ab\u6321\u4f4f\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u59d1\u5a18\u522b\u6015\uff0c\u6211\u8981\u4f24\u4f60\uff0c\u6613\u5982\u53cd\u638c\u3002\u6211\u662f\u4f60\u5bb6\u670b\u53cb\u3002\u201d \u3000\u3000\u8bf4\u7740\u5c06\u5251\u8fd8\u7ed9\u4e86\u5979\uff0c\u7126\u59d1\u5a18\u63a5\u4e86\u5251\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u5c06\u4fe1\u5c06\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u7239\u773c\u4e0b\u5927\u96be\u4e34\u5934\uff0c\u4f60\u80af\u4e0d\u80af\u5192\u9669\u6551\u7236\uff1f\u201d\u7126\u59d1\u5a18\u773c\u775b\u4e00\u7ea2\u9053\uff1a\u201c\u53ea\u8981\u80fd\u6551\u5f97\u7239\u7239\uff0c\u7eb5\u7136\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u662f\u7518\u5fc3\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u7239\u7239\u4e3a\u4eba\u5f88\u597d\uff0c\u5b81\u53ef\u820d\u4e86\u81ea\u5df1\u7684\u6027\u547d\uff0c\u4e5f\u4e0d\u613f\u5927\u52a8\u5e72\u6208\u3002\u6211\u8981\u5e2e\u4ed6\u4e2a\u5fd9\u3002\u201d\u7126\u59d1\u5a18\u542c\u4ed6\u8bf4\u5f97\u8bda\u6073\uff0c\u4f55\u51b5\u5371\u96be\u4e4b\u4e2d\uff0c\u53ea\u8981\u6709\u4e00\u4e1d\u6307\u671b\uff0c\u4e5f\u51b3\u4e0d\u80af\u653e\u8fc7\uff0c\u53cc\u819d\u4e00\u5c48\uff0c\u5c31\u8981\u8dea\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u59d1\u5a18\u4e14\u52ff\u591a\u793c\uff0c\u4e8b\u60c5\u80fd\u5426\u6210\u529f\uff0c\u6211\u4e5f\u6ca1\u5341\u5206\u628a\u63e1\u3002\u201d\u7126\u59d1\u5a18\u53ea\u89c9\u53f3\u81c2\u88ab\u4ed6\u8f7b\u8f7b\u4e00\u67b6\uff0c\u4e00\u80a1\u6781\u5927\u7684\u529b\u91cf\u6258\u5c06\u4e0a\u6765\uff0c\u5c31\u6b64\u8dea\u4e0d\u4e0b\u53bb\uff0c\u767b\u65f6\u53c8\u5bf9\u4ed6\u591a\u4fe1\u4e86\u51e0\u5206\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8bf7\u4f60\u9886\u6211\u53bb\u5e9c\u4e0a\uff0c\u6211\u8981\u5199\u4e2a\u5b57\u6761\u7ed9\u4f60\u7239\u7239\u3002\u201d \u3000\u3000\u7126\u59d1\u5a18\u9053\uff1a\u201c\u4e24\u4f4d\u9ad8\u59d3\u5927\u540d\uff1f\u8bf7\u4e24\u4f4d\u53bb\u529d\u529d\u6211\u7239\u7239\u597d\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u59d3\u540d\u6682\u4e14\u4e0d\u8bf4\uff0c\u4f60\u7239\u7239\u89c1\u4e86\u6211\u8fd9\u5b57\u6761\uff0c\u5b9a\u4f1a\u6d88\u4e86\u6b7b\u5fd7\u3002\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u5148\u529e\u4e86\u8fd9\u4e8b\u518d\u8bf4\u3002\u201d\u7126\u59d1\u5a18\u5927\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u4e09\u4eba\u8d8a\u5899\u5165\u5185\u3002\u7126\u59d1\u5a18\u5f15\u4e8c\u4eba\u8d70\u8fdb\u4e00\u95f4\u5c0f\u4e66\u623f\u4e2d\uff0c\u62ff\u51fa\u7eb8\u58a8\u7b14\u781a\uff0c\u78e8\u597d\u4e86\u58a8\uff0c\u8fdc\u8fdc\u5750\u5728\u65c1\u8fb9\uff0c\u53ea\u89c1\u8881\u627f\u5fd7\u4e00\u6325\u800c\u5c31\uff0c\u4e0d\u77e5\u5199\u4e86\u4e9b\u751a\u4e48\u3002\u9752\u9752\u5728\u684c\u65c1\u5750\u7740\uff0c\u8138\u73b0\u8be7\u5f02\u4e4b\u8272\u3002 \u3000\u3000\u8881\u627f\u5fd7\u628a\u7eb8\u7b3a\u6298\u4e86\u5957\u5165\u4fe1\u5c01\uff0c\u7528\u6d46\u7cca\u7c98\u4f4f\u4e86\uff0c\u4ea4\u7ed9\u7126\u59d1\u5a18\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\u7ed9\u4f60\u7239\u7239\uff0c\u4f46\u987b\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d\u7126\u59d1\u5a18\u9053\uff1a\u201c\u5c0a\u9a7e\u5429\u5490\uff0c\u81ea\u5f53\u9075\u547d\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u5343\u4e07\u4e0d\u80fd\u5bf9\u4f60\u7239\u7239\u8bf4\u5230\u6211\u7684\u76f8\u8c8c\u5e74\u7eaa\u3002\u201d\u7126\u59d1\u5a18\u5947\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u4e00\u8bf4\uff0c\u6211\u5c31\u4e0d\u80fd\u5e2e\u4f60\u5fd9\u4e86\u3002\u201d\u7126\u59d1\u5a18\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u660e\u65e5\u536f\u65f6\u6b63\uff0c\u8bf7\u4f60\u5230\u6c34\u897f\u95e8\u5174\u9686\u5ba2\u6808\u9ec4\u5b57\u7b2c\u4e09\u53f7\u623f\u6765\u3002\u6211\u8ddf\u4f60\u5546\u8bae\u5982\u4f55\u89e3\u9664\u4ee4\u5c0a\u7684\u5371\u96be\u3002\u4f46\u6b64\u4e8b\u52a1\u987b\u4e25\u5b88\u79d8\u5bc6\u3002\u201d \u3000\u3000\u7126\u59d1\u5a18\u70b9\u5934\u7b54\u5e94\u3002\u8881\u627f\u5fd7\u4e00\u62c9\u9752\u9752\u7684\u624b\u9053\uff1a\u201c\u597d\u5566\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u7126\u59d1\u5a18\u89c1\u4e24\u4eba\u8d8a\u5899\u800c\u51fa\uff0c\u5fc3\u4e2d\u53c8\u662f\u60ca\u7591\uff0c\u53c8\u662f\u559c\u6b22\u3002\u5fd9\u5954\u56de\u7236\u4eb2\u5367\u623f\uff0c\u89c1\u623f\u95e8\u7d27\u95ed\uff0c\u5979\u62cd\u4e86\u51e0\u4e0b\u95e8\uff0c\u5927\u53eb\uff1a\u201c\u7239\u7239\uff0c\u5f00\u95e8\uff01\u201d\u534a\u5929\u6ca1\u6709\u58f0\u606f\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u5fd9\u7ed5\u5230\u7a97\u8fb9\uff0c\u6325\u638c\u6253\u65ad\u7a97\u683c\uff0c\u8d8a\u7a97\u8fdb\u53bb\uff0c\u53ea\u89c1\u7126\u516c\u793c\u795e\u8272\u60e8\u7136\uff0c\u624b\u4e3e\u9152\u676f\u6b63\u8981\u653e\u5230\u5507\u8fb9\u3002\u7126\u59d1\u5a18\u53eb\u9053\uff1a\u201c\u7239\uff01\u4f60\u770b\u8fd9\u4fe1\uff01\u201d\u7126\u516c\u793c\u5446\u5446\u4e0d\u8bed\u3002\u7126\u59d1\u5a18\u62c6\u5f00\u4fe1\u5c01\uff0c\u62bd\u51fa\u7eb8\u6765\uff0c\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7126\u516c\u793c\u6728\u7136\u4e00\u77a5\uff0c\u89c1\u7eb8\u4e0a\u753b\u7740\u4e00\u67c4\u957f\u5251\uff0c\u4e0d\u7531\u5f97\u5168\u8eab\u5927\u9707\uff0c\u624b\u4e00\u677e\uff0c\u5f53\u5577\u4e00\u58f0\uff0c\u9152\u676f\u5728\u5730\u4e0b\u8dcc\u5f97\u7c89\u7cb9\u3002\u7126\u59d1\u5a18\u5413\u4e86\u4e00\u8df3\u3002\u7126\u516c\u793c\u5374\u662f\u6ee1\u8138\u559c\u8272\uff0c\u53cc\u624b\u5fae\u5fae\u53d1\u6296\uff0c\u8fde\u95ee\uff1a\u201c\u8fd9\u662f\u54ea\u91cc\u6765\u7684\uff1f\u8c01\u7ed9\u4f60\u7684\uff1f\u4ed6\u2026\u2026\u4ed6\u6765\u4e86\u4e48\uff1f\u771f\u7684\u6765\u4e86\u4e48\uff1f\u201d\u7126\u59d1\u5a18\u51d1\u8fd1\u770b\u65f6\uff0c\u89c1\u7eb8\u4e0a\u6ca1\u5199\u4e00\u5b57\uff0c\u53ea\u753b\u4e86\u4e00\u67c4\u957f\u5251\u3002\u5251\u8eab\u66f2\u6298\u5982\u86c7\uff0c\u5251\u5c16\u5374\u662f\u4e2a\u86c7\u5934\uff0c\u86c7\u820c\u4f38\u51fa\uff0c\u5206\u6210\u4e24\u53c9\u3002 \u3000\u3000\u5979\u4e0d\u77e5\u4f55\u4ee5\u7236\u4eb2\u4e00\u89c1\u6b64\u5251\uff0c\u7adf\u7136\u5982\u6b64\u559c\u51fa\u671b\u5916\uff0c\u95ee\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u662f\u751a\u4e48\uff1f\u201d\u7126\u516c\u793c\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u4e00\u5230\uff0c\u7239\u7239\u7684\u8001\u547d\u5c31\u6709\u6551\u4e86\uff0c\u4f60\u89c1\u5230\u4e86\u4ed6\u4e48\uff1f\u201d\u7126\u59d1\u5a18\u9053\uff1a\u201c\u8c01\u5440\uff1f\u201d\u7126\u516c\u793c\u9053\uff1a\u201c\u753b\u8fd9\u67c4\u5251\u7684\u4eba\u3002\u201d\u7126\u59d1\u5a18\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u53eb\u6211\u660e\u5929\u518d\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u6709\u6ca1\u6709\u8981\u6211\u4e5f\u53bb\uff1f\u201d\u7126\u59d1\u5a18\u9053\uff1a\u201c\u4ed6\u6ca1\u8bf4\u8d77\u3002\u201d\u7126\u516c\u793c\u9053\uff1a\u201c\u8fd9\u4f4d\u5947\u4fa0\u813e\u6c14\u53e4\u602a\uff0c\u54b1\u4eec\u4e0d\u53ef\u8fdd\u80cc\u4e86\u4ed6\u7684\u5429\u5490\u3002\u660e\u5929\u4f60\u4e00\u4e2a\u4eba\u53bb\u5427\uff01\u5509\uff0c\u4f60\u8fdf\u6765\u4e00\u523b\uff0c\u7239\u7239\u5c31\u89c1\u4f60\u4e0d\u5230\u4e86\u3002\u201d\u7126\u59d1\u5a18\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8fd9\u624d\u660e\u767d\u539f\u6765\u521a\u624d\u9152\u676f\u4e2d\u76db\u7684\u7adf\u662f\u6bd2\u836f\uff0c\u5fd9\u62ff\u626b\u5e1a\u6765\u626b\u53bb\uff0c\u670d\u4f8d\u7236\u4eb2\u7761\u4e0b\u3002 \u3000\u3000\u7126\u592b\u4eba\u4e0e\u4f17\u5f1f\u5b50\u542c\u8bf4\u5230\u4e86\u6551\u661f\uff0c\u867d\u60f3\u4e0d\u8bba\u4ed6\u6b66\u529f\u5982\u4f55\u4e86\u5f97\uff0c\u4ee5\u4e00\u4eba\u4e4b\u529b\uff0c\u7ec8\u7a76\u96be\u4e0e\u5bf9\u65b9\u8fd9\u8bb8\u591a\u9ad8\u624b\u76f8\u6297\uff0c\u4f46\u7126\u516c\u793c\u65e2\u7136\u5982\u6b64\u653e\u5fc3\uff0c\u5fc5\u6709\u9053\u7406\uff0c\u767b\u65f6\u90fd\u662f\u559c\u6170\u4e0d\u5df2\u3002\u7126\u516c\u793c\u8981\u4ed6\u4eec\u56db\u6563\u907f\u96be\uff0c\u5927\u5bb6\u672c\u6765\u4e0d\u613f\uff0c\u73b0\u4e0b\u81ea\u7136\u90fd\u4e0d\u8d70\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u4ece\u7126\u5bb6\u51fa\u6765\uff0c\u9752\u9752\u95ee\u9053\uff1a\u201c\u4f60\u753b\u8fd9\u67c4\u5251\u662f\u751a\u4e48\u610f\u601d\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u7126\u516c\u793c\u8bf4\u4e16\u4e0a\u53ea\u6709\u4f60\u7239\u7239\u4e00\u5230\uff0c\u624d\u80fd\u6551\u4ed6\u6027\u547d\u3002\u6211\u753b\u7684\u5c31\u662f\u4f60\u7239\u7239\u7528\u7684\u91d1\u86c7\u5251\u3002\u201d \u3000\u3000\u9752\u9752\u70b9\u5934\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u4f1a\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u8981\u6551\u4ed6\uff1f\u201d\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u90a3\u7126\u516c\u793c\u4e0d\u662f\u574f\u4eba\uff0c\u7ed9\u670b\u53cb\u5356\u4e86\uff0c\u903c\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u96be\u9053\u54b1\u4eec\u89c1\u6b7b\u4e0d\u6551\uff1f\u4f55\u51b5\u4ed6\u53c8\u662f\u4f60\u7239\u7239\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u9752\u9752\u7b11\u9053\uff1a\u201c\u55ef\uff0c\u6211\u8fd8\u9053\u4f60\u89c1\u4ed6\u5973\u513f\u751f\u5f97\u7f8e\u8c8c\uff0c\u60f3\u8ba8\u597d\u8fd9\u4e2a\u5927\u59d1\u5a18\u3002\u201d\u8881\u627f\u5fd7\u6012\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u751a\u4e48\u4eba\uff1f\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u554a\u54df\uff0c\u522b\u53d1\u813e\u6c14\uff0c\u5e72\u4e48\u4f60\u53c8\u7ea6\u5979\u5230\u5ba2\u5e97\u6765\u627e\u4f60\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5fc3\u773c\u513f\u771f\u662f\u4e0d\u53ef\u6551\u836f\uff0c\u522b\u5570\u5506\u5566\uff0c\u5feb\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u9752\u9752\u55e4\u7684\u4e00\u7b11\uff0c\u8ddf\u7740\u4ed6\u5411\u897f\u800c\u884c\u3002\u4e0d\u591a\u65f6\u6765\u5230\u5927\u529f\u574a\u95f5\u5b50\u534e\u7684\u5b85\u7b2c\u3002 \u3000\u3000\u4e24\u4eba\u8d8a\u5899\u8fdb\u5185\uff0c\u8eb2\u5728\u5899\u89d2\uff0c\u5bdf\u770b\u52a8\u9759\uff0c\u8881\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u5c4b\u91cc\u4e0d\u77e5\u4f4f\u7740\u591a\u5c11\u9ad8\u624b\uff0c\u4e00\u7ed9\u53d1\u89c9\uff0c\u54b1\u4eec\u7684\u4e8b\u5c31\u5e72\u4e0d\u6210\u5566\u3002\u201d \u3000\u3000\u9752\u9752\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u8981\u5e2e\u90a3\u7f8e\u8c8c\u59d1\u5a18\uff0c\u6211\u53ef\u4e0d\u8bb8\uff0c\u504f\u504f\u8981\u8ddf\u4f60\u6363\u86cb\u3002\u6211\u8981\u5927\u53eb\u5927\u56b7\u5566\uff01\u201d\u8881\u627f\u5fd7\u4e00\u7b11\u3002\u4e0d\u53bb\u7406\u5979\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u89c1\u65e0\u5f02\u72b6\uff0c\u4e24\u4eba\u6084\u6084\u524d\u884c\uff0c\u6293\u4f4f\u4e00\u4e2a\u7537\u4ec6\uff0c\u95ee\u660e\u4e86\u53f2\u6c0f\u5144\u5f1f\u4f4f\u5bbf\u7684\u6240\u5728\u3002\u8881\u627f\u5fd7\u628a\u4ed6\u70b9\u4e86\u54d1\u7a74\uff0c\u629b\u5728\u6811\u4e1b\u4e4b\u4e2d\uff0c\u6765\u5230\u53f2\u6c0f\u5144\u5f1f\u5367\u623f\u7a97\u5916\uff0c\u6084\u6ca1\u58f0\u606f\u7684\u634f\u65ad\u7a97\u683c\uff0c\u8dc3\u4e86\u8fdb\u53bb\u3002\u53f2\u6c0f\u5144\u5f1f\u4e5f\u751a\u4e86\u5f97\uff0c\u7acb\u5373\u60ca\u89c9\u3002\u6b63\u5f85\u559d\u95ee\uff0c\u53cc\u53cc\u5df2\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6643\u4eae\u706b\u6298\uff0c\u70b9\u4e86\u8721\u70db\uff0c\u548c\u9752\u9752\u5728\u6795\u5934\u4e0b\u3001\u62bd\u5c49\u4e2d\u3001\u5305\u88f9\u91cc\u5230\u5904\u641c\u68c0\uff0c\u89c1\u5230\u7684\u5374\u53ea\u662f\u4e9b\u8863\u7269\u94f6\u4e24\u3001\u5175\u5203\u6697\u5668\u3002\u6b63\u8981\u518d\u67e5\uff0c\u5ffd\u542c\u623f\u5916\u811a\u6b65\u8f7b\u54cd\uff0c\u8881\u627f\u5fd7\u5fd9\u5439\u7184\u70db\u706b\uff0c\u4f38\u624b\u5728\u53f2\u6c0f\u5144\u5f1f\u8863\u888b\u4e2d\u4e00\u6478\uff0c\u90fd\u662f\u4e9b\u7eb8\u7247\u4fe1\u672d\u4e4b\u7c7b\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5c3d\u6570\u53d6\u51fa\uff0c\u653e\u5165\u6000\u91cc\uff0c\u6084\u58f0\u9053\uff1a\u201c\u5f97\u624b\u5566\uff01\u201d\u9752\u9752\u9053\uff1a\u201c\u8d70\u5427\uff0c\u5916\u9762\u597d\u50cf\u6709\u4eba\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u7b49\u4e00\u4e0b\u3002\u201d\u62ff\u8d77\u53f2\u6c0f\u5144\u5f1f\u7684\u4e00\u628a\u5315\u9996\uff0c\u5728\u684c\u9762\u4e0a\u5212\u4e86\u201c\u611a\u5f1f\u7126\u516c\u793c\u987f\u9996\u201d\u4e03\u4e2a\u5927\u5b57\u3002 \u3000\u3000\u731b\u542c\u5f97\u95e8\u5916\u6709\u4eba\u559d\u95ee\uff1a\u201c\u751a\u4e48\u4eba\uff1f\u201d\u4e24\u4eba\u5f53\u5373\u4ece\u7a97\u4e2d\u8dc3\u51fa\uff0c\u968f\u5373\u7ffb\u8fc7\u5899\u5934\uff0c\u53ea\u542c\u5f97\u51fb\u638c\u4e4b\u58f0\u56db\u4e0b\u54cd\u52a8\uff0c\u6b64\u51fb\u5f7c\u5e94\uff0c\u77e5\u9053\u5bf9\u65b9\u5e03\u7f6e\u5468\u5bc6\uff0c\u9ad8\u624b\u5185\u5916\u904d\u4f0f\uff0c\u4e0d\u6562\u8d38\u7136\u95ef\u51fa\uff0c\u5f53\u4e0b\u4e24\u4eba\u8e72\u5728\u5899\u811a\u8fb9\u4e0d\u52a8\uff0c\u53ea\u542c\u5f97\u5c4b\u9876\u6709\u4eba\u6765\u53bb\u5de1\u903b\u3002 \u3000\u3000\u9752\u9752\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u751a\u4e48\uff1f\u201d\u62ff\u4f4f\u4ed6\u624b\uff0c\u7275\u5f15\u5230\u5899\u811a\u8fb9\u3002\u8881\u627f\u5fd7\u4e00\u6478\uff0c\u5899\u811a\u7684\u9752\u82d4\u4e0b\u4f3c\u4e4e\u523b\u5f97\u6709\u5b57\uff0c\u624b\u6307\u987a\u7740\u8fd9\u5b57\u7b14\u5212\u4e2d\u7684\u51f9\u5904\u5199\u53bb\uff0c\u5f2f\u5f2f\u66f2\u66f2\u7684\u662f\u4e2a\u7bc6\u6587\u3002\u4ed6\u4e0d\u8bc6\u5f97\u7bc6\u5b57\uff0c\u6084\u58f0\u95ee\u9053\uff1a\u201c\u751a\u4e48\u5b57\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u662f\u2018\u7b2c\u2019\u5b57\uff0c\u7b2c\u4e00\u7b2c\u4e8c\u7684\u2018\u7b2c\u2019\u5b57\u201d\u3002\u518d\u5411\u4e0a\u6478\u53bb\uff0c\u53c8\u662f\u4e00\u5b57\uff0c\u9752\u9752\u8ddf\u4ed6\u8bf4\u662f\u4e2a\u201c\u8d50\u201d\u5b57\u3002\u4e0a\u9762\u662f\u4e2a\u201c\u516c\u201d\u5b57\uff0c\u518d\u4e0a\u662f\u4e2a\u201c\u56fd\u201d\u5b57\uff0c\u6700\u540e\u4e00\u5b57\u7b14\u5212\u6781\u591a\uff0c\u9752\u9752\u8bf4\u662f\u201c\u9b4f\u201d\u5b57\u3002\u8881\u627f\u5fd7\u5fc3\u4e2d\u5c06\u8fd9\u4e94\u5b57\u81ea\u4e0a\u800c\u4e0b\u7684\u8fde\u63a5\u8d77\u6765\uff0c\u7adf\u662f\u201c\u9b4f\u56fd\u516c\u8d50\u7b2c\u201d\u3002 \u3000\u3000\u5bfb\u8bbf\u4e86\u5341\u591a\u5929\u800c\u6beb\u65e0\u5f71\u8e2a\u7684\u9b4f\u56fd\u516c\u5e9c\uff0c\u5c82\u77e5\u5c31\u662f\u5bf9\u65b9\u7684\u5927\u8425\u6240\u5728\uff0c\u6b63\u662f\u201c\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u201d\u4e86\u3002\u8fd9\u51e0\u4e2a\u5b57\u5b57\u8ff9\u6591\u5265\uff0c\u5e74\u4ee3\u5df2\u4e45\uff0c\u5b9a\u662f\u5f90\u5927\u5c06\u519b\u540e\u4eba\u5c06\u5b85\u5b50\u51fa\u5356\u4e86\uff0c\u6570\u4ee3\u4e4b\u540e\uff0c\u8f97\u8f6c\u6613\u624b\uff0c\u518d\u4e5f\u65e0\u4eba\u5f97\u77e5\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u6b63\u559c\uff0c\u5ffd\u89c9\u5934\u9888\u4e2d\u75d2\u75d2\u7684\uff0c\u539f\u6765\u662f\u9752\u9752\u5728\u5475\u6c14\uff0c\u60f3\u662f\u5979\u627e\u5230\u4e86\u9b4f\u56fd\u516c\u5e9c\uff0c\u4e50\u6781\u5fd8\u5f62\u3002\u8881\u627f\u5fd7\u5934\u4e00\u7f29\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u522b\u987d\u76ae\uff01\u201d\u542c\u5f97\u897f\u9996\u638c\u58f0\u6e10\u5411\u5357\u79fb\uff0c\u8bf4\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d \u3000\u3000\u4e24\u4eba\u4ece\u897f\u9996\u75be\u5954\u800c\u51fa\uff0c\u56de\u5230\u5ba2\u5e97\u3002 \u3000\u3000\u5176\u65f6\u5df2\u662f\u56db\u66f4\u65f6\u5206\uff0c\u9752\u9752\u70b9\u4eae\u8721\u70db\u3002\u8881\u627f\u5fd7\u53d6\u51fa\u4fe1\u4ef6\uff0c\u62e3\u4e86\u4e24\u901a\u989c\u8272\u9ec4\u65e7\u7684\u4fe1\u6765\uff0c\u62bd\u51fa\u4e00\u770b\uff0c\u679c\u7136\u662f\u5f20\u5be8\u4e3b\u7684\u4f0f\u8fa9\u4e0e\u4e18\u9053\u53f0\u7684\u8c22\u51fd\u3002 \u3000\u3000\u9752\u9752\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u4e0b\u6551\u4e86\u5979\u7239\u7239\u6027\u547d\uff0c\u4e0d\u77e5\u5979\u62ff\u751a\u4e48\u6765\u8c22\u4f60\uff1f\u201d\u8881\u627f\u5fd7\u6115\u7136\u9053\uff1a\u201c\u751a\u4e48\u5979\uff1f\u201d\u9752\u9752\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7126\u516c\u793c\u7684\u5927\u5c0f\u59d0\u54ea\uff01\u201d\u8881\u627f\u5fd7\u5411\u5979\u6241\u6241\u5634\uff0c\u4e0d\u53bb\u7406\u5979\uff0c\u7ec6\u7ec6\u770b\u4e86\u4e24\u901a\u4e66\u4fe1\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u7126\u516c\u793c\u8bf4\u7684\u786e\u662f\u53e5\u53e5\u771f\u8bdd\uff0c\u8981\u662f\u4ed6\u53e6\u6709\u79c1\u5f0a\uff0c\u90a3\u6211\u5c31\u8896\u624b\u4e0d\u7ba1\u4e86\uff0c\u4f55\u5fc5\u53bb\u5f97\u7f6a\u8fd9\u8bb8\u591a\u6c5f\u6e56\u4e0a\u7684\u524d\u8f88\uff1f\u4f55\u51b5\u5176\u4e2d\u8fd8\u6709\u4e8c\u5e08\u54e5\u7684\u5f1f\u5b50\u3002\u201d \u3000\u3000\u9752\u9752\u4f3c\u7b11\u975e\u7b11\u7684\u9053\uff1a\u201c\u90a3\u4e2a\u98de\u5929\u9b54\u5973\u5012\u5f88\u7f8e\u554a\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u5fc3\u72e0\u624b\u8fa3\uff0c\u4f5c\u4e8b\u4e0d\u5f53\uff0c\u6beb\u6ca1\u6765\u7531\u628a\u4eba\u5bb6\u4e00\u6761\u81c2\u8180\u5378\u4e86\u4e0b\u6765\u3002\u201d\u6c89\u541f\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u6015\u4e8c\u5e08\u54e5\u89c1\u602a\uff0c\u6211\u5012\u771f\u8981\u51fa\u624b\u7ba1\u4e0a\u4e00\u7ba1\u3002\u6211\u8981\u7126\u59d1\u5a18\u5230\u8fd9\u91cc\u6765\u627e\u6211\uff0c\u662f\u6015\u9732\u51fa\u4e86\u5f62\u8ff9\u3002\u8981\u662f\u6211\u4eec\u540c\u95e8\u5e08\u5144\u5f1f\u4e4b\u95f4\u6709\u4e86\u5acc\u9699\uff0c\u90a3\u5c31\u5bf9\u4e0d\u8d77\u5e08\u7236\u517b\u80b2\u4e4b\u6069\u4e86\u3002\u201d\u9752\u9752\u89c1\u4ed6\u795e\u8272\u8083\u7136\uff0c\u4e0d\u6562\u518d\u5f00\u73a9\u7b11\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53c8\u6253\u5f00\u53e6\u5916\u51e0\u5c01\u4fe1\u6765\u4e00\u770b\uff0c\u4e0d\u89c9\u5927\u6012\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u770b\u3002\u201d \u3000\u3000\u9752\u9752\u4ece\u6765\u6ca1\u89c1\u8fc7\u4ed6\u5982\u6b64\u6124\u6012\uff0c\u4ee5\u5f80\u4ed6\u5373\u4f7f\u5728\u4e34\u654c\u4e4b\u9645\uff0c\u4e5f\u662f\u96cd\u5bb9\u81ea\u82e5\uff0c\u8fd9\u65f6\u5ffd\u89c1\u4ed6\u6ee1\u8138\u80c0\u5f97\u901a\u7ea2\uff0c\u989d\u5934\u4e0a\u4e00\u6761\u9752\u7b4b\u731b\u51f8\u8d77\u6765\uff0c\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c\u5fd9\u63a5\u8fc7\u6765\u770b\u3002\u539f\u6765\u662f\u6ee1\u6e05\u4e5d\u738b\u591a\u5c14\u886e\u7684\u8bb0\u5ba4\u5199\u7ed9\u53f2\u6c0f\u5144\u5f1f\u7684\u5bc6\u51fd\uff0c\u5429\u5490\u4ed6\u4eec\u6740\u4e86\u7126\u516c\u793c\u540e\uff0c\u4e58\u673a\u593a\u8fc7\u91d1\u9f99\u5e2e\u6765\uff0c\u5148\u5728\u6c5f\u5357\u6811\u7acb\u52bf\u529b\uff0c\u523a\u63a2\u6d88\u606f\uff0c\u8054\u7edc\u6c5f\u6e56\u597d\u6c49\uff0c\u5f85\u6e05\u5175\u5927\u4e3e\u5165\u5173\u4e4b\u65f6\uff0c\u4fbf\u5728\u5357\u65b9\u8d77\u4e8b\u4f5c\u4e3a\u5185\u5e94\u3002\u4fe1\u672b\u76d6\u4e86\u4e24\u4e2a\u5927\u5927\u7684\u6731\u5370\uff0c\u4e0a\u9762\u4e00\u4e2a\u662f\u201c\u5927\u6e05\u777f\u4eb2\u738b\u201d\u4e94\u5b57\u96b6\u6587\uff0c\u4e0b\u9762\u662f\u201c\u591a\u5c14\u886e\u201d\u4e09\u5b57\u7684\u7bc6\u6587\u3002 \u3000\u3000\u9752\u9752\u4e00\u65f6\u5446\u4f4f\u4e86\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u8d8a\u60f3\u8d8a\u6012\uff0c\u5c31\u8981\u626f\u4fe1\u3002\u8881\u627f\u5fd7\u4e00\u628a\u62a2\u4f4f\uff0c\u9053\uff1a\u201c\u626f\u4e0d\u5f97\uff01\u201d\u9752\u9752\u767b\u65f6\u9192\u609f\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u5929\u5927\u7684\u8bc1\u636e\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u60f3\u53f2\u6c0f\u5144\u5f1f\u62ff\u5230\u7126\u516c\u793c\u8fd9\u4e24\u5c01\u4fe1\u540e\uff0c\u5e72\u4e48\u4e0d\u9a6c\u4e0a\u6bc1\u53bb\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u77e5\u9053\u5566\uff0c\u4ed6\u4eec\u8981\u7528\u6765\u631f\u5236\u95f5\u5b50\u534e\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5b9a\u662f\u8fd9\u6837\u3002\u6211\u672c\u60f3\u6551\u4e86\u7126\u516c\u793c\u540e\uff0c\u5c31\u6b64\u8896\u624b\u4e0d\u7ba1\u3002\u54ea\u77e5\u8fd9\u4e2d\u95f4\u53e6\u6709\u8fd9\u6837\u4e00\u4e2a\u5927\u5978\u8c0b\u3002\u522b\u8bf4\u5f97\u7f6a\u4e8c\u5e08\u54e5\uff0c\u518d\u5927\u7684\u6765\u5934\uff0c\u6211\u4e5f\u4e0d\u6015\uff01\u201d \u3000\u3000\u9752\u9752\u77a7\u7740\u4ed6\uff0c\u76ee\u5149\u4e2d\u6d41\u9732\u4ef0\u6155\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5f53\u7136\u8981\u7ba1\uff0c\u5c31\u7b97\u4e8c\u5e08\u54e5\u544a\u5230\u4f60\u5e08\u7236\u90a3\u91cc\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e5f\u4e00\u5b9a\u8bf4\u662f\u4f60\u5bf9\u2026\u2026\u54b1\u4eec\u53bb\u8bf7\u4f60\u90a3\u5927\u5e08\u54e5\u6765\uff0c\u8981\u4ed6\u7528\u94c1\u7b97\u76d8\u6765\u4e8c\u4e00\u6dfb\u4f5c\u4e94\u7684\u7b97\u4e00\u7b97\uff0c\u5230\u5e95\u4f60\u6709\u7406\uff0c\u8fd8\u662f\u4f60\u4e8c\u5e08\u54e5\u6709\u7406\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u5feb\u53bb\u7761\u5427\u3002\u6211\u5f97\u597d\u597d\u60f3\u4e00\u60f3\uff0c\u600e\u751f\u6765\u5bf9\u4ed8\u8fd9\u6279\u5978\u8d3c\u3002\u201d \u3000\u3000\u6b21\u65e5\u65e9\u6668\uff0c\u8881\u627f\u5fd7\u8d77\u8eab\u540e\u5750\u5728\u5e8a\u4e0a\u6253\u5750\uff0c\u8c03\u5300\u547c\u5438\uff0c\u610f\u5b88\u4e39\u7530\uff0c\u4e00\u80a1\u5185\u606f\u5728\u5168\u8eab\u767e\u7a74\u8fd0\u884c\u4e00\u904d\uff0c\u4ece\u5c0f\u8179\u4e0b\u76f4\u6696\u4e0a\u6765\uff0c\u81ea\u89c9\u8fd1\u6765\u529f\u529b\u7cbe\u8fdb\uff0c\u9887\u4e3a\u6b23\u6170\u3002 \u3000\u3000\u4e0b\u5f97\u5e8a\u6765\uff0c\u89c1\u684c\u4e0a\u653e\u4e86\u4e24\u7897\u8c46\u6d46\uff0c\u8fd8\u6709\u4e00\u789f\u5927\u997c\u6cb9\u6761\u3002\u5ffd\u542c\u9752\u9752\u563b\u563b\u4e00\u7b11\uff0c\u4ece\u95e8\u540e\u94bb\u4e86\u51fa\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8001\u548c\u5c1a\uff0c\u6253\u5b8c\u4e86\u5750\u5417\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u5012\u8d77\u5f97\u65e9\u3002\u201d \u3000\u3000\u4e24\u4eba\u521a\u5403\u5b8c\u65e9\u70b9\uff0c\u5e97\u5c0f\u4e8c\u5f15\u4e86\u4e00\u4e2a\u4eba\u8fdb\u6765\uff0c\u53e3\u4e2d\u5520\u5520\u53e8\u53e8\u7684\u9053\uff1a\u201c\u662f\u627e\u8fd9\u4e24\u4f4d\u5427\uff1f\u95ee\u4f60\u627e\u59d3\u751a\u4e48\u7684\uff0c\u53c8\u8bf4\u4e0d\u77e5\u9053\u3002\u201d\u8881\u627f\u5fd7\u548c\u9752\u9752\u4e00\u770b\uff0c\u8fd9\u4eba\u6b63\u662f\u7126\u59d1\u5a18\u3002\u5979\u7b49\u5e97\u5c0f\u4e8c\u4e00\u51fa\u95e8\uff0c\u7acb\u65f6\u62dc\u5012\u3002\u8881\u627f\u5fd7\u8fde\u5fd9\u8fd8\u793c\u3002\u9752\u9752\u62c9\u7740\u5979\u624b\uff0c\u626f\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7126\u59d1\u5a18\u89c1\u8fd9\u7f8e\u8c8c\u5c11\u5e74\u62c9\u4f4f\u81ea\u5df1\u7684\u624b\uff0c\u4e0d\u7981\u7f9e\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u4f46\u4ed6\u4eec\u6709\u6551\u7236\u4e4b\u6069\uff0c\u4e0d\u4fbf\u6323\u8131\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u8f7b\u8f7b\u7f29\u624b\u3002\u9752\u9752\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u4f60\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u201d\u7126\u59d1\u5a18\u9053\uff1a\u201c\u6211\u53eb\u5b9b\u513f\u3002\u4e24\u4f4d\u8d35\u59d3\uff1f\u201d\u9752\u9752\u5411\u8881\u627f\u5fd7\u4e00\u6307\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u51f6\u5f97\u5f88\uff0c\u4e0d\u8bb8\u6211\u8bf4\uff0c\u4f60\u95ee\u4ed6\u5427\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u77e5\u662f\u8bf4\u7b11\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u968f\u5373\u655b\u5bb9\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u6551\u4e86\u6211\u7239\u7239\u6027\u547d\uff0c\u5927\u6069\u5927\u5fb7\uff0c\u7c89\u8eab\u96be\u62a5\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ee4\u5c0a\u662f\u6c5f\u6e56\u524d\u8f88\uff0c\u4fa0\u4e49\u9ad8\u98ce\uff0c\u4ee4\u4eba\u5341\u5206\u94a6\u4f69\u3002\u665a\u8f88\u7a0d\u6548\u5fae\u52b3\uff0c\u4efd\u6240\u5f53\u4e3a\uff0c\u4f55\u8db3\u6302\u9f7f\uff1f\u59d1\u5a18\u56de\u53bb\u7980\u544a\u4ee4\u5c0a\uff0c\u8bf7\u4ed6\u4eca\u65e5\u4e2d\u5348\u7167\u5e38\u5bb4\u5ba2\u3002 \u3000\u3000\u8fd9\u91cc\u6709\u4e24\u5305\u4e1c\u897f\uff0c\u8bf7\u4f60\u4ea4\u7ed9\u4ee4\u5c0a\u3002\u5728\u7d27\u6025\u5173\u5934\u5f53\u4f17\u5f00\u542f\uff0c\u5fc5\u6709\u5947\u6548\u3002\u8fd9\u4e24\u5305\u4e1c\u897f\u4e8b\u5173\u91cd\u5927\uff0c\u987b\u9632\u6709\u4eba\u534a\u8def\u52ab\u593a\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u89c1\u4e00\u4e2a\u957f\u957f\u7684\u5305\u88f9\uff0c\u4efd\u91cf\u6c89\u91cd\uff0c\u4f3c\u662f\u5305\u7740\u5175\u5203\uff0c\u53e6\u4e00\u5305\u5374\u662f\u8f7b\u8f7b\u7684\u4e00\u4e2a\u5c0f\u5305\uff0c\u53cc\u624b\u63a5\u8fc7\uff0c\u53c8\u518d\u62dc\u8c22\u3002 \u3000\u3000\u7b49\u5979\u8d70\u51fa\u5e97\u623f\uff0c\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u6697\u4e2d\u968f\u540e\u4fdd\u62a4\uff0c\u522b\u8ba9\u574f\u86cb\u593a\u56de\u53bb\u3002\u201d\u5e26\u4e0a\u623f\u95e8\u51fa\u53bb\uff0c\u53ea\u89c1\u7126\u5b9b\u513f\u5750\u5728\u5ba2\u5385\u4e4b\u4e2d\u3002\u4e24\u4eba\u75be\u5fd9\u7f29\u8eab\uff0c\u5fae\u89c9\u5947\u602a\uff0c\u4e0d\u77e5\u5979\u4f55\u4ee5\u8fd8\u5728\u5ba2\u5e97\u9017\u7559\u3002 \u3000\u3000\u53ea\u542c\u7126\u5b9b\u513f\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u53eb\u638c\u67dc\u7684\u6765\u3002\u91d1\u9f99\u63a2\u722a\uff0c\u7126\u96f7\u9707\u7a7a\uff01\u201d\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u5979\u8bf4\u751a\u4e48\uff1f\u201d\u9752\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u591a\u534a\u662f\u4ed6\u4eec\u5e2e\u91cc\u7684\u5207\u53e3\u3002\u201d\u90a3\u5e97\u5c0f\u4e8c\u672c\u6765\u76db\u6c14\u51cc\u4eba\uff0c\u542c\u5f97\u8fd9\u8bdd\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u6025\u5fd9\u8eac\u8eab\u7b54\u5e94\uff1a\u201c\u662f\uff0c\u662f\u3002\u201d\u638c\u67dc\u8fc7\u6765\uff0c\u5475\u4e86\u8170\u606d\u606d\u656c\u656c\u7684\u9053\uff1a\u201c\u59d1\u5a18\u6709\u751a\u4e48\u5429\u5490\uff0c\u5c0f\u7684\u9a6c\u4e0a\u53bb\u529e\u3002\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u6211\u662f\u7126\u5927\u59d1\u5a18\u3002\u4f60\u5230\u6211\u5bb6\u53bb\uff0c\u8bf4\u6211\u6709\u8981\u4e8b\uff0c\u8bf7\u5e08\u54e5\u4eec\u90fd\u6765\u3002\u201d\u90a3\u638c\u67dc\u542c\u5f97\u662f\u7126\u5927\u59d1\u5a18\uff0c\u66f4\u52a0\u5413\u4e86\u4e00\u8df3\uff0c\u9a91\u4e0a\u5feb\u9a6c\uff0c\u4eb2\u81ea\u9a70\u53bb\u3002\u53ea\u4e00\u987f\u996d\u529f\u592b\uff0c\u5e97\u5916\u6d8c\u8fdb\u4e8c\u5341\u591a\u540d\u6b66\u5e08\u6765\uff0c\u624b\u4e2d\u90fd\u62ff\u4e86\u5175\u5203\uff0c\u62e5\u7740\u7126\u5b9b\u513f\u53bb\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u91d1\u9f99\u5e2e\u5728\u8fd9\u91cc\u597d\u5927\u7684\u58f0\u52bf\u3002\u54b1\u4eec\u4e0d\u5fc5\u8ddf\u53bb\u4e86\uff0c\u5f85\u4f1a\u5230\u7126\u5bb6\u5403\u9152\u53bb\u5427\u3002\u201d \u3000\u3000\u4e24\u4eba\u95f2\u8c08\u4e00\u4f1a\uff0c\u5348\u65f6\u5c06\u5230\uff0c\u6162\u6162\u8e31\u5230\u7126\u5e9c\uff0c\u53ea\u89c1\u5ba2\u4eba\u6b63\u5728\u9646\u7eed\u8fdb\u53bb\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u968f\u4f17\u5165\u5185\u3002\u8d70\u5230\u95e8\u53e3\uff0c\u7126\u516c\u793c\u548c\u4e24\u4eba\u76f8\u4e92\u4e00\u63d6\uff0c\u4ed6\u53ea\u9053\u8fd9\u4e24\u4eba\u662f\u5bf9\u65b9\u7684\u95e8\u5f92\u5c0f\u8f88\uff0c\u4e5f\u4e0d\u5728\u610f\u3002 \u3000\u3000\u7b49\u5ba2\u4eba\u5230\u9f50\uff0c\u5f00\u51fa\u5e2d\u6765\uff0c\u4e00\u756a\u52bf\u6d3e\uff0c\u4e0e\u95f5\u5b50\u534e\u8bf7\u5ba2\u65f6\u53c8\u81ea\u4e0d\u540c\u3002\u91d1\u9f99\u5e2e\u8d22\u96c4\u52bf\u5927\uff0c\u8fd9\u6b21\u9686\u91cd\u5bb4\u5ba2\uff0c\u684c\u6905\u90fd\u8499\u4e86\u7ee3\u91d1\u7ea2\u62ab\uff0c\u5e2d\u4e0a\u7ec6\u74f7\u7259\u7b77\uff0c\u83dc\u80b4\u7cbe\u81f4\u5f02\u5e38\uff0c\u4f5c\u83dc\u7684\u662f\u5357\u4eac\u540d\u53a8\uff0c\u9152\u58f6\u4e2d\u659f\u51fa\u6765\u7684\u90fd\u662f\u80ed\u8102\u822c\u7684\u9648\u5e74\u7ecd\u9152\u3002 \u3000\u3000\u95f5\u5b50\u534e\u548c\u5341\u529b\u5927\u5e08\u3001\u90d1\u8d77\u4e91\u3001\u6606\u4ed1\u6d3e\u540d\u5bbf\u5f20\u5fc3\u4e00\u3001\u6885\u5251\u548c\u3001\u4e07\u91cc\u98ce\u3001\u5b59\u4ef2\u541b\u7b49\u5750\u5728\u9996\u5e2d\uff0c\u7126\u516c\u672d\u4eb2\u81ea\u76f8\u966a\uff0c\u6bb7\u52e4\u529d\u9152\u3002\u6885\u5251\u548c\u7b49\u5374\u4e0d\u996e\u9152\uff0c\u53ea\u77a7\u7740\u95f5\u5b50\u534e\u7684\u8138\u8272\u3002 \u3000\u3000\u95f5\u5b50\u534e\u7a81\u7136\u63d0\u8d77\u9152\u676f\uff0c\u63b7\u5728\u5730\u4e0b\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u767b\u65f6\u7c89\u788e\uff0c\u559d\u9053\uff1a\u201c\u59d3\u7126\u7684\uff0c\u4eca\u65e5\u6b66\u6797\u4e2d\u7684\u597d\u670b\u53cb\u4eec\uff0c\u90fd\u8d4f\u8138\u5230\u8fd9\u91cc\u6765\u5566\u3002 \u3000\u3000\u6211\u7684\u6740\u5144\u4e4b\u4ec7\u5982\u4f55\u4e86\u7ed3\uff0c\u4f60\u81ea\u5df1\u8bf4\u5427\u3002\u201d \u3000\u3000\u4ed6\u5f00\u95e8\u89c1\u5c71\u7684\u63d0\u4e86\u51fa\u6765\uff0c\u7126\u516c\u793c\u4e00\u65f6\u5012\u611f\u96be\u4ee5\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u5927\u5f1f\u5b50\u5434\u5e73\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u95f5\u4e8c\u7237\uff0c\u4f60\u90a3\u5144\u957f\u89c1\u8272\u8d77\u610f\uff0c\u8d25\u574f\u6b66\u6797\u4e2d\u7684\u89c4\u77e9\uff0c\u6211\u5e08\u7236\u2026\u2026\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u84e6\u5730\u91cc\u4e00\u80a1\u52b2\u98ce\u5c04\u5411\u9762\u95e8\uff0c\u6025\u5fd9\u4f4e\u5934\uff0c\u767b\u7684\u4e00\u58f0\uff0c\u4e00\u679a\u4e94\u5bf8\u957f\u7684\u4e09\u89d2\u94a2\u9489\u9489\u5728\u684c\u9762\u3002\u5434\u5e73\u89c1\u8fd9\u94a2\u9489\u662f\u5b59\u4ef2\u541b\u6240\u53d1\uff0c\u6012\u6c14\u52c3\u53d1\uff0c\u5f53\u5373\u62d4\u51fa\u5355\u5200\uff0c\u53eb\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u6697\u7b97\u6211\u7f57\u5e08\u5f1f\uff0c\u4f24\u4e86\u4ed6\u7684\u81c2\u8180\uff0c\u4f60\u8fd9\u5a46\u5a18\u8fd8\u60f3\u5bb3\u4eba\uff01\u201d\u6251\u4e0a\u53bb\u5c31\u8981\u548c\u5979\u53ae\u6740\u3002 \u3000\u3000\u7126\u516c\u793c\u6025\u5fd9\u559d\u6b62\uff0c\u65a5\u9053\uff1a\u201c\u8d35\u5bbe\u9762\u524d\uff0c\u4e0d\u5f97\u65e0\u793c\u3002\u201d\u8f6c\u5934\u5411\u5b59\u4ef2\u541b\u7b11\u9053\uff1a\u201c\u5b59\u59d1\u5a18\u662f\u534e\u5c71\u6d3e\u9ad8\u624b\uff0c\u4f55\u5fc5\u8ddf\u5c0f\u5f92\u4e00\u822c\u89c1\u8bc6\u2026\u2026\u201d \u3000\u3000\u95f5\u5b50\u534e\u7ea2\u4e86\u773c\uff0c\u6293\u8d77\u4e00\u53cc\u7b77\u5b50\uff0c\u5bf9\u51c6\u7126\u516c\u793c\u773c\u4e2d\u63b7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4eca\u65e5\u8ddf\u4f60\u8fd9\u8001\u8d3c\u62da\u4e86\u3002\u201d\u7126\u516c\u793c\u4e5f\u4f38\u51fa\u7b77\u5b50\uff0c\u8f7b\u8f7b\u5939\u4f4f\u8fce\u9762\u98de\u6765\u7684\u4e24\u652f\u7b77\u5b50\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u95f5\u4e8c\u7237\u600e\u5730\u504c\u5927\u706b\u6c14\uff0c\u6709\u8bdd\u6162\u6162\u597d\u8bf4\u3002\u6765\u4eba\u54ea\uff0c\u7ed9\u95f5\u4e8c\u7237\u62ff\u53cc\u5e72\u51c0\u7b77\u5b50\u6765\u3002\u201d\u95f5\u4e8c\u7237\u89c1\u4ed6\u6b66\u529f\u4e86\u5f97\uff0c\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u54e5\u54e5\u547d\u4e27\u4ed6\u624b\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u89c1\u95f5\u5b50\u534e\u8f93\u4e86\u4e00\u62db\uff0c\u75be\u4f38\u53f3\u624b\uff0c\u53bb\u62c9\u7126\u516c\u793c\u624b\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u7126\u5e2e\u4e3b\u597d\u672c\u4e8b\uff0c\u54b1\u54e5\u513f\u4fe9\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d\u7126\u516c\u793c\u89c1\u4ed6\u624b\u638c\u6765\u5f97\u597d\u5feb\uff0c\u8eab\u5b50\u7565\u504f\uff0c\u7a9c\u4e86\u5f00\u53bb\u3002\u6885\u5251\u548c\u4e00\u628a\u6293\u4f4f\u6905\u80cc\uff0c\u5580\u5587\u4e00\u58f0\uff0c\u6905\u80cc\u4e0a\u6a2a\u6728\u767b\u65f6\u65ad\u4e86\u3002 \u3000\u3000\u7126\u516c\u793c\u89c1\u5bf9\u65b9\u8d8a\u903c\u8d8a\u7d27\uff0c\u95f5\u65b9\u8bf8\u4eba\u6709\u7684\u78e8\u62f3\u64e6\u638c\uff0c\u6709\u7684\u62bd\u51fa\u4e86\u5175\u5668\uff0c\u81ea\u5df1\u8fd9\u8fb9\u7684\u5e2e\u4f17\u95e8\u5f92\u4e5f\u90fd\u4e25\u884c\u6212\u5907\uff0c\u53cc\u65b9\u7fa4\u6bb4\u4e00\u89e6\u5373\u53d1\uff0c\u800c\u90a3\u91d1\u86c7\u90ce\u541b\u8fd8\u6ca1\u6709\u5230\u6765\u89e3\u56f4\uff0c\u773c\u89c1\u60c5\u52bf\u5371\u6025\uff0c\u53cc\u65b9\u4e00\u52a8\u4e0a\u624b\uff0c\u90a3\u5c31\u4e0d\u77e5\u8981\u4f24\u6298\u591a\u5c11\u4eba\u547d\u4e86\uff0c\u4e8e\u662f\u5411\u5973\u513f\u4f7f\u4e2a\u773c\u8272\u3002 \u3000\u3000\u7126\u5b9b\u513f\u6367\u7740\u90a3\u4e24\u4e2a\u5305\u88f9\uff0c\u65e9\u5df2\u5fc3\u6025\u5f02\u5e38\uff0c\u89c1\u5230\u7236\u4eb2\u773c\u8272\uff0c\u7acb\u5373\u6253\u5f00\u957f\u5f62\u5305\u88f9\uff0c\u53ea\u89c1\u5305\u88f9\u662f\u4e00\u67c4\u957f\u5251\uff0c\u6258\u8fc7\u6765\u653e\u5728\u7236\u4eb2\u9762\u524d\u3002 \u3000\u3000\u7126\u516c\u793c\u89c1\u4e86\u90a3\u5251\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff0c\u6b63\u81ea\u7591\u60d1\uff0c\u5b59\u4ef2\u541b\u5df2\u89c1\u5230\u662f\u81ea\u5df1\u5175\u5203\uff0c\u4e0d\u7981\u7f9e\u6012\u4ea4\u96c6\uff0c\u62a2\u8fc7\u53bb\u4e00\u628a\u6293\u8d77\uff0c\u9a82\u9053\uff1a\u201c\u6709\u672c\u4e8b\u7684\uff0c\u5927\u5bb6\u660e\u5200\u660e\u67aa\u7684\u6bd4\u62da\u4e00\u573a\u3002\u5077\u4eba\u4e1c\u897f\uff0c\u7b97\u751a\u4e48\u82f1\u96c4\u597d\u6c49\uff1f\u201d\u7126\u516c\u793c\u6115\u7136\u4e0d\u89e3\uff0c\u5b59\u4ef2\u541b\u8de8\u4e0a\u4e24\u6b65\uff0c\u5251\u5c16\u9752\u5149\u95ea\u95ea\uff0c\u5411\u4ed6\u80f8\u53e3\u75be\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8ba9\u7126\u516c\u793c\u4ea4\u8fd8\u5b59\u4ef2\u541b\u7684\u957f\u5251\uff0c\u53ea\u9053\u5979\u4f53\u5ff5\u6628\u665a\u81ea\u5df1\u624b\u4e0b\u7559\u60c5\uff0c\u5fc3\u4e2d\u611f\u6fc0\uff0c\u4eca\u65e5\u5fc5\u53ef\u4ece\u4e2d\u51fa\u529b\u8c03\u89e3\u606f\u4e89\uff0c\u54ea\u77e5\u5979\u7adf\u662f\u5982\u6b64\u6a2a\u86ee\uff0c\u5fc3\u4e0b\u751a\u662f\u607c\u6012\u3002 \u3000\u3000\u7126\u516c\u793c\u89c1\u5bf9\u65b9\u5251\u62db\u72e0\u8fa3\uff0c\u75be\u9000\u4e24\u6b65\uff0c\u4e00\u540d\u5f1f\u5b50\u628a\u4ed6\u7684\u6298\u94c1\u5200\u9012\u4e86\u4e0a\u6765\u3002\u7126\u516c\u793c\u63a5\u5728\u624b\u4e2d\uff0c\u5e76\u4e0d\u8fd8\u62db\u3002\u4f46\u5b59\u4ef2\u541b\u51fa\u624b\u751a\u5feb\uff0c\u4e00\u5251\u523a\u7a7a\uff0c\u8ddf\u7740\u4e00\u62db\u201c\u884c\u4e91\u6d41\u6c34\u201d\uff0c\u5251\u5c16\u6296\u52a8\uff0c\u53c8\u523a\u5411\u4ed6\u54bd\u5589\u3002\u7126\u516c\u793c\u518d\u4e0d\u62db\u67b6\uff0c\u4e0d\u514d\u547d\u4e27\u5251\u5e95\uff0c\u53ea\u5f97\u62a1\u6298\u94c1\u5200\u4f7f\u62db\u201c\u957f\u7a7a\u843d\u96c1\u201d\uff0c\u5bf9\u51c6\u5979\u5251\u8eab\u780d\u843d\u3002\u5b59\u4ef2\u541b\u5251\u8eab\u4e00\u6c89\uff0c\u4f3c\u662f\u907f\u5f00\u4ed6\u8fd9\u4e00\u5200\uff0c\u54ea\u77e5\u6c89\u5230\u4e0b\u76d8\uff0c\u7a81\u7136\u8fc5\u5982\u95ea\u7535\u7684\u7ffb\u5c06\u4e0a\u6765\uff0c\u6025\u523a\u5bf9\u65b9\u5c0f\u8179\u3002\u8fd9\u62db\u5feb\u6781\u51c6\u6781\uff0c\u9976\u662f\u7126\u516c\u793c\u5728\u8fd9\u628a\u6298\u94c1\u5200\u4e0a\u6c89\u6d78\u6570\u5341\u5e74\uff0c\u4e5f\u5df2\u4e0d\u53ca\u56de\u529b\u62db\u67b6\uff0c\u6025\u5fd9\u4e2d\u7eb5\u8eab\u8dc3\u8d77\uff0c\u4ece\u65c1\u4eba\u5934\u9876\u7a9c\u4e86\u51fa\u53bb\uff0c\u8fd9\u624d\u907f\u8fc7\u4e86\u957f\u5251\u7834\u8179\u4e4b\u5384\uff0c\u4f46\u55e4\u7684\u4e00\u58f0\uff0c\u5927\u817f\u65c1\u7684\u88e4\u811a\u7ec8\u4e8e\u88ab\u5251\u5c16\u5212\u7834\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u53eb\uff1a\u201c\u597d\u9669\uff01\u201d\u56de\u5934\u77a7\u5979\u662f\u5426\u7ee7\u7eed\u8ffd\u6765\uff0c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u5927\u559c\u8fc7\u671b\uff0c\u4f46\u89c1\u5973\u513f\u624b\u4e2d\u6258\u7740\u7684\uff0c\u6b63\u662f\u7ed9\u592a\u767d\u4e09\u82f1\u9a97\u53bb\u7684\u90a3\u4e24\u5c01\u4fe1\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u4e24\u540d\u5f92\u5f1f\u5df2\u6325\u5200\u628a\u5b59\u4ef2\u541b\u62e6\u4f4f\u3002\u4e24\u4eba\u6df1\u6068\u5979\u574f\u4e86\u7f57\u5e08\u54e5\u7684\u624b\u8180\uff0c\u5200\u98ce\u864e\u864e\uff0c\u820d\u547d\u76f8\u6251\u3002\u5b59\u4ef2\u541b\u5634\u89d2\u8fb9\u5fae\u5fae\u51b7\u7b11\uff0c\u5de6\u624b\u53c9\u5728\u8170\u91cc\uff0c\u53f3\u624b\u957f\u5251\u968f\u624b\u6325\u821e\uff0c\u767b\u65f6\u4fbf\u628a\u8fd9\u4e24\u4e2a\u5927\u6c49\u903c\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u56e2\u56e2\u4e71\u8f6c\u3002\u7126\u516c\u793c\u63a5\u8fc7\u4fe1\u6765\uff0c\u5927\u53eb\uff1a\u201c\u4f4f\u624b\uff0c\u4f4f\u624b\uff01\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u4e24\u540d\u5f92\u5f1f\u542c\u5f97\u5e08\u7236\u559d\u53eb\uff0c\u5fd9\u6536\u5200\u9000\u4e0b\u3002\u4e00\u4e2a\u9000\u5f97\u7a0d\u6162\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u80f8\u53e3\u88ab\u5b59\u4ef2\u541b\u8e22\u4e86\u4e00\u811a\uff0c\u8fde\u9000\u6570\u6b65\uff0c\u5927\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u8138\u8272\u7acb\u8f6c\u60e8\u767d\u3002 \u3000\u3000\u7126\u516c\u793c\u5411\u5b59\u4ef2\u541b\u77a7\u4e86\u4e00\u773c\uff0c\u5f3a\u6291\u6012\u6c14\uff0c\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u670b\u53cb\uff0c\u8bf7\u542c\u6211\u8bf4\u4e00\u53e5\u8bdd\uff01\u201d\u5927\u5385\u4e2d\u672c\u5df2\u5341\u5206\u6df7\u4e71\uff0c\u5f53\u4e0b\u6162\u6162\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u8fd9\u4f4d\u95f5\u670b\u53cb\u602a\u6211\u5bb3\u4e86\u4ed6\u7684\u5144\u957f\uff0c\u4e0d\u9519\uff0c\u4ed6\u5144\u957f\u95f5\u5b50\u53f6\u662f\u6211\u6740\u7684\uff01\u201d\u5927\u5385\u4e2d\u4e00\u65f6\u5bc2\u9759\u65e0\u58f0\u3002 \u3000\u3000\u95f5\u5b50\u534e\u545c\u54bd\u9053\uff1a\u201c\u6b20\u503a\u8fd8\u94b1\uff0c\u6740\u4eba\u62b5\u547d\u3002\u201d\u95f5\u65b9\u6b66\u5e08\u7eb7\u7eb7\u8d77\u54c4\uff0c\u4e03\u5634\u516b\u820c\u7684\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6740\u4eba\u62b5\u547d\uff01\u5341\u6761\u547d\u62b5\u4e00\u6761\u3002\u201d \u3000\u3000\u201c\u7126\u516c\u793c\uff0c\u4f60\u81ea\u5df1\u4e86\u65ad\u5427\uff01\u201d \u3000\u3000\u7126\u516c\u793c\u5f85\u4eba\u58f0\u7a0d\u9759\uff0c\u6717\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e24\u5c01\u4fe1\uff0c\u8981\u8bf7\u51e0\u4f4d\u5fb7\u9ad8\u671b\u91cd\u7684\u524d\u8f88\u8fc7\u76ee\u3002\u8981\u662f\u8fd9\u51e0\u4f4d\u524d\u8f88\u770b\u4fe1\u4e4b\u540e\uff0c\u8bf4\u7126\u67d0\u8be5\u5f53\u62b5\u547d\uff0c\u7126\u67d0\u7acb\u5373\u5f53\u573a\u81ea\u520e\uff0c\u76b1\u4e00\u4e0b\u7709\u5934\u90fd\u4e0d\u7b97\u597d\u6c49\u3002\u201d \u3000\u3000\u4f17\u4eba\u597d\u5947\u5fc3\u8d77\uff0c\u7eb7\u7eb7\u8981\u4e0a\u6765\u770b\u4fe1\u3002\u7126\u516c\u793c\u9053\uff1a\u201c\u6162\u6765\u3002\u8bf7\u95f5\u4e8c\u7237\u63a8\u4e09\u4f4d\u524d\u8f88\u5148\u770b\u3002\u201d\u95f5\u5b50\u534e\u4e0d\u77e5\u4fe1\u4e2d\u5199\u7684\u662f\u751a\u4e48\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u8bf7\u5341\u529b\u5927\u5e08\u3001\u90d1\u5c9b\u4e3b\u3001\u6885\u5927\u54e5\u4e09\u4f4d\u770b\u5427\u3002\u201d \u3000\u3000\u4e09\u4eba\u63a5\u8fc7\u4fe1\u6765\uff0c\u4e00\u8d77\u51d1\u5728\u684c\u8fb9\uff0c\u4f4e\u58f0\u5ff5\u4e86\u8d77\u6765\u3002\u592a\u767d\u4e09\u82f1\u94c1\u9752\u7740\u8138\uff0c\u5728\u4e00\u65c1\u7a83\u7a83\u79c1\u8bae\u3002 \u3000\u3000\u5341\u529b\u5927\u5e08\u7b2c\u4e00\u4e2a\u770b\u5b8c\u4e86\u4fe1\uff0c\u8bf4\u9053\uff1a\u201c\u4f9d\u8001\u8872\u4e4b\u89c1\uff0c\u95f5\u4e8c\u7237\u8fd8\u662f\u6350\u5f03\u524d\u5acc\uff0c\u5316\u654c\u4e3a\u53cb\u5427\uff01\u201d\u4ed6\u5728\u6b66\u6797\u4e2d\u58f0\u671b\u6781\u9ad8\uff0c\u6b66\u529f\u89c1\u8bc6\uff0c\u4f17\u4eba\u7d20\u6765\u94a6\u670d\uff0c\u6b64\u8a00\u4e00\u51fa\uff0c\u5927\u5385\u4e0a\u5c3d\u7686\u6115\u7136\u3002 \u3000\u3000\u95f5\u5b50\u534e\u63a5\u8fc7\u4fe1\u6765\uff0c\u5148\u770b\u5f20\u5be8\u4e3b\u7684\u4f0f\u8fa9\uff0c\u5f20\u5be8\u4e3b\u6587\u7406\u4e0d\u901a\uff0c\u522b\u5b57\u8fde\u7bc7\uff0c\u770b\u6765\u8fd8\u4e0d\u5927\u4e86\u7136\uff0c\u518d\u770b\u4e18\u9053\u53f0\u7684\u8c22\u51fd\uff0c\u90a3\u5374\u662f\u53d9\u4e8b\u660e\u6670\u3001\u6587\u8bcd\u6d41\u7545\u4e4b\u4f5c\uff0c\u53ea\u770b\u5230\u4e00\u534a\uff0c\u4e0d\u7981\u53c8\u662f\u7f9e\u6127\uff0c\u53c8\u662f\u96be\u8fc7\uff0c\u5446\u5728\u5f53\u5730\uff0c\u505a\u58f0\u4e0d\u5f97\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u5fc3\u5934\u8bb8\u591a\u4e00\u76f4\u5927\u60d1\u4e0d\u89e3\u4e4b\u4e8b\u90fd\u5192\u51fa\u4e86\u7b54\u6848\uff1a\u201c\u592a\u767d\u4e09\u82f1\u6765\u8ddf\u6211\u8bf4\u77e5\uff0c\u5bb3\u6b7b\u6211\u54e5\u54e5\u7684\u4e43\u662f\u91d1\u9f99\u5e2e\u7126\u516c\u793c\u3002\u6211\u9080\u4f17\u4f4d\u5e08\u54e5\u52a9\u6211\u62a5\u4ec7\uff0c\u5927\u5bb6\u5374\u90fd\u63a8\u4e09\u963b\u56db\u3002\u6c34\u4e91\u5927\u5e08\u54e5\u53c8\u8bf4\u8981\u7b49\u5bfb\u5230\u5e08\u7236\uff0c\u518d\u7531\u4ed6\u8001\u4eba\u5bb6\u4e3b\u6301\u3002 \u3000\u3000\u4f17\u5e08\u54e5\u5411\u6765\u548c\u6211\u4ea4\u597d\uff0c\u600e\u5730\u5982\u6b64\u6ca1\u540c\u95e8\u4e49\u6c14\uff1f\u53ea\u6709\u6d1e\u7384\u5e08\u5f1f\u4e00\u4eba\uff0c\u624d\u966a\u6211\u524d\u6765\u3002\u6211\u4ed9\u90fd\u6d3e\u4eba\u591a\u52bf\u4f17\uff0c\u9047\u4e0a\u8fd9\u7b49\u5927\u4e8b\uff0c\u672c\u95e8\u7684\u4eba\u5374\u4e0d\u51fa\u5934\uff0c\u8feb\u5f97\u6211\u53ea\u597d\u53bb\u9080\u5916\u4eba\u76f8\u52a9\uff0c\u5b9e\u5728\u592a\u4e0d\u6210\u8bdd\u3002 \u3000\u3000\u539f\u6765\u6211\u54e5\u54e5\u5f53\u5e74\u5e72\u4e0b\u4e86\u8fd9\u7b49\u89c1\u4e0d\u5f97\u4eba\u9762\u4e4b\u4e8b\u3002\u4f17\u4f4d\u5e08\u54e5\u5b9a\u7136\u77e5\u9053\u771f\u76f8\uff0c\u662f\u4ee5\u4e0d\u80af\u76f8\u52a9\uff0c\u5374\u53c8\u6015\u626b\u4e86\u6211\u8138\u9762\uff0c\u5c31\u6b64\u5f80\u5931\u8e2a\u591a\u5e74\u7684\u5e08\u7236\u5934\u4e0a\u4e00\u63a8\uff0c\u53ea\u6d1e\u7384\u5e08\u5f1f\u5e74\u8f7b\u4e0d\u77e5\u2026\u2026\u201d \u3000\u3000\u5ffd\u542c\u6885\u5251\u548c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u5047\u9020\u7684\uff0c\u60f3\u9a97\u8c01\u5440\uff1f\u201d\u4f38\u624b\u62a2\u8fc7\u4e24\u4fe1\uff0c\u626f\u5f97\u7c89\u788e\u3002 \u3000\u3000\u7126\u516c\u793c\u4e07\u6599\u4e0d\u5230\u4ed6\u7adf\u4f1a\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\u626f\u788e\u4e86\u4e24\u901a\u4e66\u4fe1\uff0c\u8fd9\u4e00\u6765\uff0c\u4ed6\u501a\u4e3a\u62a4\u8eab\u7b26\u4e4b\u7269\u91cd\u53c8\u6d88\u5931\uff0c\u4e0d\u7531\u5f97\u53c8\u6025\u53c8\u6012\uff0c\u8138\u76ae\u7d2b\u80c0\uff0c\u5927\u559d\uff1a\u201c\u59d3\u6885\u7684\uff0c\u4f60\u8981\u8138\u4e0d\u8981\uff1f\u201d \u3000\u3000\u6885\u5251\u548c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4e5f\u4e0d\u77e5\u662f\u8c01\u4e0d\u8981\u8138\uff1f\u5bb3\u4e86\u4eba\u5bb6\u5144\u957f\uff0c\u8fd8\u5047\u9020\u51e0\u5c01\u72d7\u5c41\u4e0d\u901a\u7684\u4e66\u4fe1\u6765\u51a4\u6789\u6b7b\u4eba\uff0c\u660e\u77e5\u6b7b\u65e0\u5bf9\u8bc1\uff0c\u4efb\u7531\u4f60\u6492\u4e2a\u6f2b\u5929\u5927\u8c0e\u3002\u8fd9\u6837\u5b50\u7684\u4fe1\u54ea\uff0c\u6211\u5173\u4e0a\u4e86\u95e8\uff0c\u4e00\u5929\u53ef\u4ee5\u5199\u4e00\u767e\u5c01\u3002\u6211\u9a6c\u4e0a\u5c31\u5199\u7ed9\u4f60\u770b\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u4f60\u8981\u51a4\u6789\u5341\u529b\u5927\u5e08\u65e0\u6076\u4e0d\u4f5c\uff0c\u51a4\u6789\u90d1\u5c9b\u4e3b\u6740\u4e86\u95f5\u4e8c\u54e5\u7684\u5144\u957f\uff0c\u90a3\u6837\u7684\u4fe1\u6211\u90fd\u4f1a\u5199\u3002\u201d \u3000\u3000\u5341\u529b\u5927\u5e08\u4e0e\u90d1\u8d77\u4e91\u672c\u89c9\u95f5\u5b50\u534e\u7406\u5c48\uff0c\u542c\u6885\u5251\u548c\u4e00\u8bf4\uff0c\u53c8\u662f\u8e0c\u8e87\u8d77\u6765\uff0c\u4e0d\u77e5\u8fd9\u4e24\u5c01\u4e66\u4fe1\u5230\u5e95\u662f\u771f\u662f\u5047\uff0c\u4e24\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u96be\u4ee5\u59d4\u51b3\u3002 \u3000\u3000\u5434\u5e73\u89c1\u5e08\u7236\u5982\u6b64\u53d7\u4eba\u6b3a\u8fb1\uff0c\u6c14\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u6251\u5730\u8df3\u51fa\uff0c\u6325\u5200\u5411\u6885\u5251\u548c\u780d\u53bb\u3002\u6885\u5251\u548c\u8eab\u5b50\u5fae\u4fa7\uff0c\u5df2\u62d4\u5251\u5728\u624b\u3002\u767d\u5149\u95ea\u52a8\uff0c\u5434\u5e73\u72c2\u53eb\u4e00\u58f0\uff0c\u5355\u5200\u8131\u624b\uff0c\u6885\u5251\u548c\u7684\u5251\u5c16\u5df2\u6307\u5728\u4ed6\u54bd\u5589\u6b63\u4e2d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8dea\u4e0b\uff0c\u6885\u5927\u7237\u5c31\u9976\u4f60\u4e00\u6761\u5c0f\u547d\uff01\u201d\u5434\u5e73\u8fde\u9000\u4e09\u6b65\uff0c\u4f46\u654c\u4eba\u5251\u5c16\u59cb\u7ec8\u4e0d\u79bb\u5589\u53e3\u3002\u6885\u5251\u548c\u7b11\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u8dea\uff0c\u6211\u53ef\u8981\u523a\u4e86\uff01\u201d\u5434\u5e73\u9053\uff1a\u201c\u4f60\u523a\u5427\uff0c\u5a46\u5a46\u5988\u5988\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u7126\u95e8\u5f1f\u5b50\u5404\u6267\u5175\u5203\uff0c\u62a2\u5230\u5385\u4e2d\u3002\u95f5\u65b9\u6b66\u5e08\u4e2d\u4e00\u4e9b\u52c7\u5f80\u76f4\u524d\u4e4b\u8f88\u4e5f\u7eb7\u7eb7\u62bd\u51fa\u5175\u5668\uff0c\u5206\u522b\u9080\u6597\uff0c\u767b\u65f6\u4e52\u4e52\u4e53\u4e53\u7684\u6253\u5f97\u5341\u5206\u70ed\u95f9\u3002 \u3000\u3000\u7126\u516c\u793c\u8dc3\u4e0a\u6905\u5b50\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u4f4f\u624b\uff0c\u77a7\u6211\u7684\uff01\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u6298\u94c1\u5200\u6a2a\u5728\u5589\u5934\uff0c\u53eb\u9053\uff1a\u201c\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\uff01\u6211\u4eca\u65e5\u7ed9\u95f5\u5b50\u53f6\u62b5\u547d\u4fbf\u4e86\u3002\u5f92\u513f\u4eec\u5feb\u7ed9\u6211\u9000\u4e0b\u3002\u201d \u3000\u3000\u4f17\u95e8\u5f92\u4f9d\u8a00\u9000\u5f00\uff0c\u60e8\u7136\u671b\u7740\u5e08\u7236\u3002 \u3000\u3000\u7126\u5b9b\u513f\u6025\u547c\uff1a\u201c\u7239\uff0c\u4e14\u6162\uff01\u90a3\u5c01\u4fe1\u5462\uff1f\u4ed6\u8bf4\u4f1a\u6765\u6551\u4f60\u7684\u5440\uff01\u201d \u3000\u3000\u7126\u516c\u793c\u53d6\u51fa\u4fe1\u5c01\uff0c\u626f\u51fa\u4e00\u5f20\u767d\u7eb8\uff0c\u5411\u4eba\u7fa4\u62db\u4e86\u51e0\u62db\u3002\u4f17\u4eba\u89c1\u7eb8\u4e0a\u753b\u7740\u4e00\u67c4\u602a\u5251\uff0c\u90fd\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff0c\u53ea\u542c\u4ed6\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u91d1\u86c7\u5927\u4fa0\uff0c\u4f60\u6765\u8fdf\u4e00\u6b65\u4e86\uff01\u201d\u4e3e\u5200\u5c31\u5f80\u8116\u5b50\u4e0a\u62b9\u53bb\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e5d\u56de\u3000\u53cc\u59dd\u62da\u5de8\u8d4c \u4e00\u4f7f\u89e3\u6df1\u6028",content:(0,t.jsx)(t.Fragment,{children:"\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u6709\u7269\u649e\u5411\u5200\u4e0a\uff0c\u6298\u94c1\u5200\u545b\u5577\u5577\u8dcc\u5728\u5730\u4e0b\uff0c\u7126\u516c\u793c\u8eab\u65c1\u5df2\u591a\u4e86\u4e00\u4eba\u3002\u4f17\u4eba\u89c1\u8fd9\u4eba\u6d53\u7709\u5927\u773c\u3001\u80a4\u8272\u9edd\u9ed1\uff0c\u662f\u4e2a\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5c11\u5e74\uff0c\u4ed6\u5982\u4f55\u8fc7\u6765\uff0c\u7adf\u6ca1\u4e00\u4eba\u770b\u6e05\u695a\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u81ea\u7136\u4fbf\u662f\u8881\u627f\u5fd7\u4e86\u3002\u4ed6\u5728\u4eba\u7fa4\u4e2d\u89c2\u770b\uff0c\u672c\u4ee5\u4e3a\u6709\u4e86\u90a3\u4e24\u5c01\u4e66\u4fe1\uff0c\u7126\u516c\u793c\u4e4b\u4e8b\u8fce\u5203\u53ef\u89e3\uff0c\u81ea\u5df1\u4e0d\u5fc5\u9732\u9762\uff0c\u4ee5\u514d\u4e0e\u4e8c\u5e08\u54e5\u7684\u95e8\u4eba\u751f\u4e86\u5acc\u9699\uff0c\u54ea\u77e5\u6885\u5251\u548c\u7adf\u4f1a\u800d\u4e86\u8fd9\u4e00\u624b\uff0c\u7126\u516c\u793c\u65e0\u53ef\u5948\u4f55\u903c\u5f97\u8981\u6a2a\u5200\u81ea\u520e\uff0c\u81ea\u5df1\u518d\u4e0d\u633a\u8eab\u800c\u51fa\uff0c\u5df2\u4e0d\u53ef\u5f97\uff0c\u4e8e\u662f\u53d1\u94b1\u9556\u6253\u4e0b\u6298\u94c1\u5200\uff0c\u7eb5\u8eab\u800c\u524d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u662f\u4e0d\u80fd\u6765\u4e86\uff0c\u7531\u4ed6\u516c\u5b50\u548c\u5144\u5f1f\u524d\u6765\uff0c\u7ed9\u5404\u4f4d\u505a\u4e2a\u548c\u4e8b\u4f6c\u3002\u201d \u3000\u3000\u8001\u4e00\u8f88\u4e2d\uff0c\u4e0d\u5c11\u4eba\u90fd\u542c\u5230\u8fc7\u91d1\u86c7\u90ce\u541b\u7684\u540d\u5934\uff0c\u77e5\u4ed6\u6b66\u529f\u60ca\u4eba\uff0c\u884c\u4e8b\u795e\u51fa\u9b3c\u6ca1\uff0c\u4f46\u8fd1\u5341\u5e74\u6765\uff0c\u6c5f\u6e56\u4e0a\u4e45\u5df2\u4e0d\u89c1\u8e2a\u8ff9\u3002\u4f20\u8a00\u90fd\u8bf4\u5df2\u7ecf\u53bb\u4e16\uff0c\u54ea\u77e5\u8fd9\u65f6\u7a81\u7136\u9063\u4eba\u524d\u6765\uff0c\u5404\u4eba\u5fc3\u4e2d\u90fd\u662f\u51db\u7136\u4e00\u60ca\u3002 \u3000\u3000\u7126\u5b9b\u513f\u53c8\u60ca\u53c8\u559c\uff0c\u4f4e\u58f0\u5bf9\u7236\u4eb2\u9053\uff1a\u201c\u7239\uff0c\u5c31\u662f\u4ed6\uff01\u201d\u7126\u516c\u793c\u5fc3\u795e\u7a0d\u5b9a\uff0c\u4fa7\u76ee\u6253\u91cf\uff0c\u89c1\u662f\u4e2a\u540e\u751f\u5c0f\u5b50\uff0c\u4e0d\u7981\u6ee1\u8179\u72d0\u7591\uff0c\u5fae\u5fae\u6447\u5934\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u5c16\u58f0\u559d\u9053\uff1a\u201c\u4f60\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u8c01\u53eb\u4f60\u5230\u8fd9\u91cc\u6765\u591a\u4e8b\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u867d\u7136\u5e74\u7eaa\u5c0f\u8fc7\u4f60\uff0c\u53ef\u6bd4\u4f60\u957f\u7740\u4e00\u8f88\uff0c\u5f85\u4f1a\u8bf4\u51fa\u6765\uff0c\u77a7\u4f60\u8fd8\u6562\u4e0d\u6562\u65e0\u793c\uff1f\u201d\u5f53\u4e0b\u4e0d\u52a8\u58f0\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u8881\u3002\u627f\u91d1\u86c7\u90ce\u541b\u590f\u5927\u4fa0\u4e4b\u547d\u6765\u89c1\u7126\u5e2e\u4e3b\u3002\u4eca\u65e5\u5f97\u6709\u673a\u7f18\u62dc\u89c1\u5404\u4f4d\u524d\u8f88\u82f1\u96c4\uff0c\u751a\u662f\u8363\u5e78\u3002\u201d\u8bf4\u7740\u5411\u4f17\u4eba\u62b1\u62f3\u884c\u793c\u3002 \u3000\u3000\u7126\u65b9\u4f17\u4eba\u89c1\u4ed6\u6551\u4e86\u7126\u516c\u793c\u6027\u547d\uff0c\u4e00\u9f50\u606d\u8c28\u884c\u793c\u3002\u95f5\u65b9\u8bf8\u4eba\u5374\u53ea\u5341\u529b\u5927\u5e08\u7b49\u51e0\u4e2a\u7aef\u4e25\u5b88\u793c\u7684\u62f1\u624b\u7b54\u793c\uff0c\u4f59\u4eba\u89c1\u4ed6\u5e74\u8f7b\uff0c\u5747\u4e0d\u7406\u4f1a\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u4e0d\u8fc7\u4e8c\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u4e0d\u77e5\u91d1\u86c7\u90ce\u541b\u5f53\u5e74\u7684\u5a01\u540d\uff0c\u5979\u6027\u5b50\u53c8\u8e81\uff0c\u9ad8\u58f0\u9a82\u9053\uff1a\u201c\u751a\u4e48\u91d1\u86c7\u94c1\u86c7\uff0c\u5feb\u7ed9\u6211\u4e0b\u53bb\uff0c\u522b\u5728\u8fd9\u91cc\u788d\u624b\u788d\u811a\u3002\u201d \u3000\u3000\u9752\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u5411\u5979\u9f3b\u5b50\u4e00\u8038\uff0c\u4f38\u4f38\u820c\u5934\uff0c\u505a\u4e2a\u9b3c\u8138\u3002\u5b59\u4ef2\u541b\u5927\u6012\uff0c\u53ea\u9053\u8fd9\u6cb9\u5934\u7c89\u8138\u7684\u5c11\u5e74\u89c1\u81ea\u5df1\u751f\u5f97\u7f8e\u8c8c\uff0c\u8f7b\u8584\u8c03\u620f\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u65e0\u793c\uff01\u201d\u7a81\u7136\u6b3a\u8fd1\uff0c\u633a\u5251\u5411\u5979\u5c0f\u8179\u523a\u53bb\uff0c\u5251\u52bf\u52b2\u6025\uff0c\u6b63\u662f\u534e\u5c71\u5251\u672f\u7684\u9669\u7740\u4e4b\u4e00\uff0c\u53eb\u505a\u201c\u5f57\u661f\u98de\u5815\u201d\uff0c\u4e43\u795e\u5251\u4ed9\u733f\u7a46\u4eba\u6e05\u72ec\u521b\u7684\u7edd\u62db\uff0c\u9752\u9752\u54ea\u91cc\u8eb2\u907f\u5f97\u5f00\uff1f \u3000\u3000\u8881\u627f\u5fd7\u8bc6\u5f97\u6b64\u62db\uff0c\u767b\u5373\u5927\u6012\uff0c\u5fc3\u60f3\u5979\u4e0e\u4f60\u521d\u6b21\u89c1\u9762\uff0c\u65e0\u6028\u65e0\u4ec7\uff0c\u4f60\u4e0d\u95ee\u662f\u975e\u597d\u6b79\uff0c\u4e00\u4e0a\u6765\u5c31\u4e0b\u6740\u624b\uff0c\u8981\u5236\u5979\u6b7b\u547d\uff0c\u5b9e\u5728\u72e0\u8fa3\u592a\u8fc7\uff0c\u4fa7\u8eab\u6321\u5728\u9752\u9752\u4e4b\u524d\uff0c\u62ac\u9ad8\u5de6\u811a\uff0c\u4e00\u811a\u8e39\u5c06\u53bb\uff0c\u5df2\u5c06\u5b59\u4ef2\u541b\u7684\u957f\u5251\u8e0f\u5728\u5730\u4e0b\u3002\u8fd9\u662f\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u7684\u602a\u62db\uff0c\u5927\u5385\u4e0a\u65e0\u4eba\u80fd\u8bc6\u3002\u4eba\u4ece\u4e2d\u767b\u65f6\u8d77\u4e86\u4e00\u9635\u54c4\u58f0\uff0c\u5567\u5567\u79f0\u5947\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u7528\u529b\u62bd\u5251\uff0c\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u773c\u89c1\u5bf9\u65b9\u5de6\u638c\u51fb\u5230\uff0c\u76f4\u6251\u9762\u95e8\uff0c\u53ea\u5f97\u6492\u5251\u8df3\u5f00\u3002\u8881\u627f\u5fd7\u6068\u5979\u6b79\u6bd2\uff0c\u811a\u4e0b\u8fd0\u52b2\uff0c\u5580\u5587\u4e00\u58f0\u54cd\uff0c\u5c06\u957f\u5251\u8e0f\u65ad\u4e86\u3002 \u3000\u3000\u5218\u57f9\u751f\u89c1\u5e08\u59b9\u53d7\u632b\uff0c\u4fbf\u8981\u4e0a\u524d\u52a8\u624b\u3002\u6885\u5251\u548c\u89c1\u8881\u627f\u5fd7\u62db\u5f0f\u602a\u5f02\uff0c\u5f53\u5373\u4f38\u624b\u62c9\u4f4f\u5218\u57f9\u751f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7b49\u4e00\u4e0b\uff0c\u4e14\u542c\u4ed6\u80e1\u8bf4\u4e9b\u751a\u4e48\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9ad8\u58f0\u9053\uff1a\u201c\u95f5\u5b50\u534e\u95f5\u7237\u7684\u5144\u957f\u5f53\u5e74\u884c\u4e3a\u4e0d\u7aef\uff0c\u7126\u5e2e\u4e3b\u8def\u89c1\u4e0d\u5e73\uff0c\u62d4\u5200\u6740\u6b7b\u3002\u8fd9\u4ef6\u4e8b\u7684\u524d\u56e0\u540e\u679c\uff0c\u91d1\u86c7\u90ce\u541b\u77e5\u9053\u5f97\u5341\u5206\u6e05\u695a\u3002\u4ed6\u8bf4\u5f53\u5e74\u6709\u4e24\u5c01\u4fe1\u8a00\u660e\u6b64\u4e8b\uff0c\u4ed6\u66fe\u548c\u7126\u5e2e\u4e3b\u540c\u53bb\u62dc\u89c1\u4ed9\u90fd\u6d3e\u638c\u95e8\u5e08\u5c0a\u9ec4\u6728\u9053\u957f\uff0c\u5448\u4e0a\u4e24\u4fe1\u3002\u9ec4\u6728\u9053\u957f\u9605\u4fe1\u4e4b\u540e\uff0c\u4fbf\u4e0d\u518d\u8ffd\u7a76\u6b64\u4e8b\u3002\u60f3\u6765\u8fd9\u4e24\u5c01\u4fe1\u591a\u534a\u5c31\u662f\u4e86\u3002\u201d\u8bf4\u7740\u5411\u5730\u4e0b\u7684\u4e66\u4fe1\u788e\u7247\u4e00\u6307\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u7237\u53f0\u5c06\u4e24\u5c01\u4fe1\u626f\u5f97\u7c89\u788e\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff1f\u201d \u3000\u3000\u7126\u516c\u793c\u542c\u4ed6\u8bf4\u5f97\u4e1d\u6beb\u4e0d\u9519\uff0c\u5fc3\u5934\u5927\u559c\uff0c\u8fd9\u624d\u4fe1\u4ed6\u771f\u662f\u91d1\u86c7\u90ce\u541b\u6240\u4f7f\uff0c\u7d27\u7d27\u63e1\u4f4f\u4e86\u5973\u513f\u7684\u624b\uff0c\u5fc3\u4e2d\u7a81\u7a81\u4e71\u8df3\u3002 \u3000\u3000\u6885\u5251\u548c\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u634f\u9020\u7684\u5047\u4fe1\uff0c\u8fd9\u59d3\u7126\u7684\u5984\u60f3\u501f\u6b64\u9a97\u4eba\uff0c\u4e0d\u626f\u788e\u4e86\u7559\u7740\u5e72\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u4eec\u6765\u65f6\uff0c\u91d1\u86c7\u5927\u4fa0\u66fe\u63d0\u5230\u4e66\u4fe1\u5185\u5bb9\u3002\u8fd9\u4e24\u5c01\u4fe1\u867d\u5df2\u7c89\u788e\uff0c\u8fd9\u4f4d\u5927\u5e08\u4e0e\u8fd9\u4f4d\u7237\u53f0\u662f\u770b\u8fc7\u7684\u3002\u201d\u8f6c\u5934\u5411\u5341\u529b\u5927\u5e08\u4e0e\u78a7\u6d77\u957f\u9cb8\u90d1\u8d77\u4e91\u62f1\u624b\u9053\uff1a\u201c\u53ea\u6d88\u8ba9\u5728\u4e0b\u548c\u91d1\u86c7\u90ce\u541b\u590f\u5927\u4fa0\u7684\u540e\u4eba\u628a\u4e66\u4fe1\u5185\u5bb9\u7ea6\u7565\u4e00\u8bf4\uff0c\u662f\u771f\u662f\u5047\uff0c\u5c31\u53ef\u5206\u8fa8\u4e86\u3002\u201d \u3000\u3000\u5341\u529b\u5927\u5e08\u4e0e\u90d1\u8d77\u4e91\u90fd\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u671b\u7740\u95f5\u5b50\u534e\u9053\uff1a\u201c\u95f5\u7237\uff0c\u4ee4\u5144\u5df2\u7ecf\u8fc7\u4e16\uff0c\u91cd\u63d0\u65e7\u4e8b\uff0c\u4e8e\u4ee4\u5144\u9762\u4e0a\u53ef\u4e0d\u5927\u5149\u5f69\u3002\u5230\u5e95\u8981\u4e0d\u8981\u8bf4\uff1f\u201d\u95f5\u5b50\u534e\u65e9\u5c31\u5728\u5fc3\u865a\uff0c\u4f46\u7ed9\u4ed6\u8fd9\u4e48\u5f53\u4f17\u6324\u903c\u4f4f\u4e86\uff0c\u603b\u4e0d\u80fd\u6c42\u4ed6\u4e0d\u53ef\u5410\u9732\u4fe1\u4e2d\u5185\u5bb9\uff0c\u4e00\u65f6\u5f20\u7687\u5931\u63aa\uff0c\u989d\u4e0a\u9752\u7b4b\u6839\u6839\u7206\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u6211\u54e5\u54e5\u5c82\u662f\u90a3\u6837\u7684\u4eba\uff1f\u8fd9\u4fe1\u5b9a\u662f\u5047\u7684\u3002\u201d\u8881\u627f\u5fd7\u5bf9\u9752\u9752\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u90a3\u4e24\u5c01\u4fe1\u4e2d\u7684\u8a00\u8bed\uff0c\u90fd\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u9752\u9752\u5f53\u5373\u6717\u58f0\u80cc\u4fe1\u3002\u5979\u5728\u5ba2\u5e97\u4e2d\u770b\u4fe1\u4e4b\u540e\uff0c\u867d\u4e0d\u80fd\u8bf4\u8fc7\u76ee\u4e0d\u5fd8\uff0c\u4f46\u4e5f\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\u3002\u4e8e\u662f\u5148\u628a\u4e18\u9053\u53f0\u7684\u8c22\u51fd\u5ff5\u4e86\u8d77\u6765\u3002\u5979\u8bed\u97f3\u6e05\u723d\uff0c\u53e3\u9f7f\u4f36\u4fd0\uff0c\u4e00\u5b57\u4e00\u53e5\uff0c\u4eba\u4eba\u542c\u5f97\u5206\u660e\uff0c\u5ff5\u5230\u8981\u7d27\u5173\u8282\u4e4b\u5904\uff0c\u5979\u5fcd\u4e0d\u4f4f\u53c8\u81ea\u884c\u52a0\u4e0a\u51e0\u53e5\u523b\u8584\u8a00\u8bed\uff0c\u628a\u95f5\u5b50\u53f6\u72e0\u72e0\u7684\u635f\u4e86\u51e0\u4e0b\u3002\u5979\u53ea\u5ff5\u5f97\u6570\u5341\u53e5\uff0c\u4f17\u4eba\u4ea4\u5934\u63a5\u8033\uff0c\u7eb7\u7eb7\u8bae\u8bba\uff0c\u5ff5\u5230\u4e00\u534a\uff0c\u95f5\u5b50\u534e\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u53e3\uff01 \u3000\u3000\u4f60\u8fd9\u5c0f\u5b50\u7537\u4e0d\u7537\u3001\u5973\u4e0d\u5973\u7684\uff0c\u662f\u751a\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u9752\u9752\u8fd8\u672a\u56de\u7b54\uff0c\u6885\u5251\u548c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u591a\u534a\u662f\u59d3\u7126\u7684\u624b\u4e0b\u4eba\uff0c\u8981\u4e48\u662f\u91d1\u9f99\u5e2e\u9080\u6765\u52a9\u62f3\u7684\u3002\u4ed6\u4eec\u81ea\u7136\u662f\u4e8b\u5148\u4e32\u901a\u597d\u4e86\uff0c\u90a3\u6709\u751a\u4e48\u5e0c\u5947\uff1f\u201d \u3000\u3000\u95f5\u5b50\u534e\u731b\u7136\u9192\u609f\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u751a\u4e48\u91d1\u86c7\u90ce\u541b\u6d3e\u6765\u7684\uff0c\u8c01\u77e5\u9053\u662f\u771f\u662f\u5047\uff0c\u5374\u5728\u8fd9\u91cc\u80e1\u8bf4\u516b\u9053\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u8981\u600e\u6837\u624d\u80fd\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u95f5\u5b50\u534e\u957f\u5251\u4e00\u6446\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u591a\u8bf4\u91d1\u86c7\u90ce\u541b\u6b66\u529f\u60ca\u4eba\uff0c\u4f60\u5982\u771f\u662f\u91d1\u86c7\u90ce\u541b\u540e\u8f88\uff0c\u5b9a\u5df2\u5f97\u4ed6\u771f\u4f20\u3002\u4f60\u53ea\u8981\u80dc\u5f97\u6211\u624b\u4e2d\u957f\u5251\uff0c\u6211\u5c31\u4fe1\u4e86\u3002\u201d\u5728\u4ed6\u5185\u5fc3\uff0c\u65e9\u5df2\u6709\u4e03\u516b\u6210\u76f8\u4fe1\u4e66\u4fe1\u662f\u771f\uff0c\u5426\u5219\u5404\u4f4d\u540c\u95e8\u5e08\u5144\u51b3\u4e0d\u4f1a\u8896\u624b\u4e0d\u7406\uff0c\u53cd\u6709\u4eba\u529f\u4ed6\u4e0d\u53ef\u9c81\u83bd\u64cd\u5207\uff0c\u6b64\u65f6\u8d8a\u8fa9\u8d8a\u4e11\uff0c\u4e0d\u5982\u52a8\u6b66\uff0c\u53ef\u64cd\u5fc5\u80dc\u4e4b\u7b97\uff0c\u773c\u89c1\u8881\u627f\u5fd7\u5e74\u5e7c\uff0c\u5fc3\u60f3\u5c31\u7b97\u4f60\u771f\u662f\u91d1\u86c7\u90ce\u541b\u4f20\u4eba\uff0c\u5b66\u4e86\u4e9b\u602a\u62db\uff0c\u8fd9\u51e0\u5c81\u5e74\u7eaa\uff0c\u53c8\u600e\u80fd\u7ec3\u5f97\u751a\u4e48\u6df1\u539a\u7684\u529f\u592b\uff0c\u53ea\u8981\u4e00\u7ecf\u6bd4\u8bd5\uff0c\u81ea\u53ef\u5c06\u4f60\u6253\u5f97\u4e00\u8d25\u6d82\u5730\uff0c\u72fc\u72c8\u4e07\u72b6\uff0c\u90a3\u4e48\u90a3\u767d\u8138\u5c11\u5e74\u6240\u5ff5\u7684\u4fe1\u5c31\u6ca1\u4eba\u4fe1\u4e86\uff1b\u662f\u5426\u8981\u6740\u7126\u516c\u793c\u4e3a\u5144\u957f\u62a5\u4ec7\uff0c\u4e0d\u7ea6\u6682\u4e14\u6401\u5728\u4e00\u8fb9\uff0c\u773c\u524d\u5927\u4e8b\uff0c\u603b\u662f\u8981\u7ef4\u62a4\u5df2\u6b7b\u5144\u957f\u7684\u58f0\u540d\uff0c\u5426\u5219\u8fde\u4ed9\u90fd\u6d3e\u7684\u6e05\u8a89\u4e5f\u8981\u5927\u53d7\u7275\u7d2f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e0b\u76d8\u7b97\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u72c2\u50b2\u602a\u8bde\uff0c\u4f17\u6240\u5468\u77e5\u3002\u6211\u5192\u5145\u662f\u4ed6\u4f7f\u8005\uff0c\u4e5f\u987b\u88c5\u5f97\u9a84\u50b2\u72c2\u653e\uff0c\u602a\u6a21\u602a\u6837\uff0c\u65b9\u80fd\u4f7f\u4eba\u5165\u4fe1\u3002\u201d \u3000\u3000\u4e8e\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u7aef\u8d77\u9152\u676f\u559d\u4e86\u4e00\u53e3\uff0c\u53c8\u4f38\u7b77\u5939\u4e2a\u8089\u4e38\u5403\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u8981\u8d62\u4f60\u624b\u4e2d\u4e4b\u5251\uff0c\u53ea\u987b\u5b66\u5f97\u91d1\u86c7\u90ce\u541b\u7684\u4e00\u70b9\u513f\u76ae\u6bdb\uff0c\u4e5f\u5df2\u7ef0\u7ef0\u6709\u4f59\u3002\u4f60\u53d7\u4eba\u5229\u7528\uff0c\u5c1a\u4e14\u4e0d\u609f\uff0c\u53ef\u53f9\u554a\u53ef\u53f9\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u6012\u9053\uff1a\u201c\u6211\u53d7\u751a\u4e48\u4eba\u5229\u7528\uff1f\u4f60\u8fd9\u5c0f\u5b50\uff0c\u6562\u6bd4\u5c31\u6bd4\uff0c\u82e5\u662f\u4e0d\u6562\uff01\u5feb\u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u201d \u3000\u3000\u53ea\u56e0\u8881\u627f\u5fd7\u9002\u624d\u8db3\u8e39\u5b59\u4ef2\u541b\u957f\u5251\uff0c\u9732\u4e86\u4e00\u624b\u602a\u62db\uff0c\u95f5\u65b9\u6b66\u5e08\u624d\u5bf9\u4ed6\u5fc3\u6709\u6240\u5fcc\uff0c\u5426\u5219\u65e9\u5c31\u6709\u4eba\u4e0a\u6765\u64b5\u4ed6\u4e0b\u53bb\uff0c\u54ea\u5bb9\u4ed6\u5982\u6b64\u8086\u65e0\u5fcc\u60ee\uff0c\u65c1\u82e5\u65e0\u4eba\uff1f \u3000\u3000\u8881\u627f\u5fd7\u53c8\u559d\u4e86\u4e00\u53e3\u9152\uff0c\u9053\uff1a\u201c\u4e45\u95fb\u4ed9\u90fd\u5251\u6cd5\u7cbe\u5fae\u5965\u5999\uff0c\u4eca\u65e5\u6b63\u597d\u89c1\u8bc6\u9886\u6559\u3002\u4e0d\u8fc7\u54b1\u4eec\u8bdd\u8bf4\u5728\u524d\u5934\uff0c\u8981\u662f\u6211\u80dc\u4e86\uff0c\u4f60\u8ddf\u7126\u5e2e\u4e3b\u7684\u8fc7\u8282\u53ea\u597d\u4ece\u6b64\u4e0d\u63d0\u3002\u4f60\u518d\u5bfb\u4ec7\u751f\u4e8b\uff0c\u8fd9\u91cc\u6b66\u6797\u4e2d\u7684\u8bf8\u4f4d\u524d\u8f88\uff0c\u53ef\u90fd\u5f97\u8bf4\u53e5\u516c\u9053\u8bdd\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u6012\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u8fd9\u91cc\u5341\u529b\u5927\u5e08\u3001\u90d1\u5c9b\u4e3b\u7b49\u5404\u4f4d\u90fd\u53ef\u4f5c\u8bc1\u3002\u8981\u662f\u4f60\u8d62\u4e0d\u4e86\u6211\u5462\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5411\u4f60\u53e9\u5934\u8d54\u7f6a\u3002\u8fd9\u91cc\u7684\u4e8b\uff0c\u6211\u4eec\u81ea\u7136\u4e5f\u4e0d\u914d\u591a\u7ba1\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u597d\uff0c\u6765\u5427\uff01\u201d\u957f\u5251\u4e00\u632f\uff0c\u5251\u8eab\u55e1\u55e1\u4f5c\u54cd\uff0c\u95f5\u65b9\u6b66\u5e08\u9f50\u58f0\u559d\u91c7\u3002\u8fd9\u4e00\u8bb0\u6296\u5251\u679c\u7136\u529f\u529b\u4e0d\u6d45\u3002\u4ed6\u751a\u662f\u5f97\u610f\uff0c\u5fc3\u60f3\u975e\u7ed9\u4f60\u8eab\u4e0a\u7559\u4e0b\u51e0\u4e2a\u8bb0\u53f7\uff0c\u663e\u4e0d\u4e86\u6211\u4ed9\u90fd\u6d3e\u7684\u5a01\u98ce\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u91d1\u86c7\u5927\u4fa0\u5429\u5490\u6211\u8bf4\uff0c\u4ed9\u90fd\u6d3e\u7075\u5b9d\u62f3\u3001\u4e0a\u6e05\u62f3\u3001\u4e0a\u6e05\u5251\uff0c\u90fd\u662f\u535a\u5927\u7cbe\u6df1\uff0c\u6b66\u6797\u7edd\u827a\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e9b\u62f3\u672f\u592a\u8fc7\u8270\u6df1\uff0c\u59d3\u95f5\u7684\u591a\u534a\u9886\u4f1a\u4e0d\u5230\uff0c\u53ea\u6709\u4e00\u8def\u4e24\u4eea\u5251\u6cd5\uff0c\u60f3\u6765\u4ed6\u662f\u7ec3\u719f\u4e86\u7684\u3002\u91d1\u86c7\u5927\u4fa0\u8bf4\u9053\uff1a\u2018\u4f60\u8fd9\u6b21\u53bb\uff0c\u8981\u662f\u59d3\u95f5\u7684\u4e0d\u542c\u597d\u8a00\u76f8\u529d\uff0c\u52a8\u8d77\u624b\u6765\uff0c\u987b\u5f97\u7559\u795e\u4ed6\u4eec\u8fd9\u4e00\u8def\u5251\u6cd5\u3002\u2019\u201d\u95f5\u5b50\u534e\u659c\u773c\u7768\u89c6\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8bdd\u5012\u662f\u4e0d\u9519\uff0c\u4ed6\u53c8\u600e\u4e48\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\u95f5\u5b50\u534e\u7684\u5e08\u7236\u9ec4\u6728\u9053\u4eba\u6027\u683c\u521a\u5f3a\uff0c\u4e8e\u4ed9\u90fd\u6d3e\u5386\u4ee3\u76f8\u4f20\u3001\u4ee5\u8f7b\u7075\u89c1\u957f\u7684\u7075\u5b9d\u62f3\u3001\u4e0a\u6e05\u62f3\u5251\u9020\u8be3\u4e0d\u9ad8\uff0c\u6700\u5f97\u610f\u7684\u6b66\u529f\u662f\u81ea\u521b\u7684\u4e00\u8def\u4e24\u4eea\u5251\u6cd5\uff0c\u66fe\u5411\u91d1\u86c7\u90ce\u541b\u63d0\u53ca\u3002\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u201c\u7834\u654c\u7bc7\u201d\u4e2d\u53d9\u8ff0\u5d06\u5cd2\u3001\u4ed9\u90fd\u7b49\u95e8\u6d3e\u7684\u6b66\u529f\u53ca\u7834\u6cd5\uff0c\u4e8e\u4e24\u4eea\u5251\u6cd5\u66fe\u52a0\u8bd1\u8bba\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6599\u60f3\u5176\u5e08\u65e2\u4e13\u7cbe\u4e8e\u6b64\uff0c\u95f5\u5b50\u534e\u4e8e\u8fd9\u8def\u5251\u6cd5\u4e5f\u5fc5\u64c5\u957f\uff0c\u8bf4\u5230\u6b64\u5904\uff0c\u6ce8\u89c6\u4ed6\u7684\u795e\u60c5\uff0c\u5fc3\u77e5\u679c\u5df2\u8bf4\u4e2d\uff0c\u53c8\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u8def\u5251\u6cd5\uff0c\u5728\u6211\u773c\u4e2d\uff0c\u4e5f\u662f\u4e0d\u503c\u4e00\u7b11\uff0c\u73b0\u4eca\u6559\u4f60\u51e0\u62db\u7834\u6cd5\uff01\u2019\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u4eba\u7fa4\u4e2d\u5ffd\u5730\u7eb5\u51fa\u4e00\u540d\u9752\u5e74\u9053\u4eba\uff0c\u6012\u9053\uff1a\u201c\u597d\u54c7\uff01 \u3000\u3000\u4e24\u4eea\u5251\u6cd5\u4e0d\u503c\u4e00\u7b11\uff0c\u6211\u5012\u8981\u77a7\u77a7\u91d1\u86c7\u90ce\u541b\u600e\u751f\u7834\u6cd5\uff1f\u201d\u5237\u7684\u4e00\u5251\uff0c\u75be\u5411\u8881\u627f\u5fd7\u8138\u4e0a\u523a\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5411\u5de6\u907f\u8fc7\uff0c\u8dc3\u5230\u5927\u5385\u4e2d\u5fc3\uff0c\u5de6\u624b\u62ff\u7740\u9152\u676f\u3002\u53f3\u624b\u7b77\u5b50\u5939\u7740\u4e00\u6761\u9e21\u817f\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u6559\u9053\u957f\u6cd5\u53f7\uff1f\u201d\u90a3\u9053\u4eba\u53eb\u9053\uff1a\u201c\u6211\u53eb\u6d1e\u7384\uff0c\u4ed9\u90fd\u6d3e\u7b2c\u5341\u4e09\u4ee3\u5f1f\u5b50\uff0c\u662f\u95f5\u5e08\u54e5\u7684\u5e08\u5f1f\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u518d\u597d\u4e5f\u6ca1\u6709\u3002\u91d1\u86c7\u5927\u4fa0\u4e0e\u5c0a\u5e08\u9ec4\u6728\u9053\u957f\u5f53\u5e74\u5728\u4ed9\u90fd\u5c71\u9f99\u864e\u89c2\u8bba\u5251\uff0c\u9ec4\u6728\u9053\u4eba\u81ea\u79f0\u4ed6\u72ec\u521b\u7684\u4e24\u4eea\u5251\u6cd5\u65e0\u654c\u4e8e\u5929\u4e0b\u3002 \u3000\u3000\u91d1\u86c7\u5927\u4fa0\u4e00\u7b11\u4e86\u4e4b\uff0c\u4e5f\u4e0d\u4e0e\u4ed6\u7f6e\u8fa9\u3002\u4eca\u65e5\u6709\u5e78\uff0c\u54b1\u4eec\u540e\u4e00\u8f88\u7684\u6765\u8003\u8f83\u8003\u8f83\u3002\u201d\u6d1e\u7384\u9053\u4eba\u5927\u58f0\u9053\uff1a\u201c\u4e24\u4eea\u5251\u6cd5\u65e0\u654c\u4e8e\u5929\u4e0b\u7684\u8bdd\uff0c\u6211\u5e08\u7236\u4ece\u6765\u6ca1\u8bf4\u8fc7\u3002\u6211\u4ed9\u90fd\u6d3e\u51b3\u8ba1\u4e0d\u6562\u5982\u6b64\u72c2\u5984\u81ea\u5927\u3002\u4f46\u8981\u6536\u62fe\u4f60\u8fd9\u4e73\u81ed\u672a\u5e72\u7684\u9ed1\u5c0f\u5b50\uff0c\u5374\u4e5f\u662f\u8f7b\u800c\u6613\u4e3e\u3002\u201d\u5411\u95f5\u5b50\u534e\u6253\u4e2a\u62db\u547c\uff0c\u53cc\u5251\u9f50\u51fa\uff0c\u98ce\u58f0\u52b2\u6025\uff0c\u5411\u8881\u627f\u5fd7\u523a\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8eab\u5f62\u4e00\u6643\uff0c\u4ece\u53cc\u5251\u5939\u7f1d\u4e2d\u94bb\u4e86\u8fc7\u53bb\u3002\u6d1e\u7384\u4e0e\u95f5\u5b50\u534e\u6325\u5251\u4e00\u653b\u4e00\u5b88\uff0c\u5feb\u6377\u5f02\u5e38\u3002 \u3000\u3000\u9752\u9752\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4e09\u4f4d\u4f4f\u624b\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u95f5\u5b50\u534e\u4e0e\u6d1e\u7384\u9053\u4eba\u6536\u5251\u5f53\u80f8\uff0c\u95f5\u5b50\u534e\u53f3\u624b\u6267\u5251\uff0c\u6d1e\u7384\u5de6\u624b\u6267\u5251\uff0c\u4e24\u4eba\u5df2\u7ad9\u6210\u201c\u4e24\u4eea\u5251\u6cd5\u201d\u4e2d\u7684\u8d77\u624b\u5f0f\u3002\u9752\u9752\u9053\uff1a\u201c\u8881\u5927\u54e5\u53ea\u7b54\u5e94\u8ddf\u95f5\u7237\u4e00\u4eba\u6bd4\uff0c\u600e\u4e48\u53c8\u591a\u4e86\u4e00\u4f4d\u9053\u7237\u51fa\u6765\uff1f\u201d \u3000\u3000\u6d1e\u7384\u53cc\u773c\u4e00\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u5c0f\u54e5\u4e0d\u6253\u81ea\u62db\uff0c\u6446\u660e\u4e86\u662f\u5192\u724c\u3002\u8c01\u4e0d\u77e5\u4e24\u4eea\u5251\u6cd5\u662f\u4e24\u4eba\u540c\u4f7f\uff1f\u4f60\u4e0d\u77e5\u9053\uff0c\u96be\u9053\u91d1\u86c7\u90ce\u541b\u8fd9\u4e48\u5927\u7684\u5a01\u540d\uff0c\u4ed6\u4e5f\u4f1a\u4e0d\u77e5\u4e48\uff1f\u201d \u3000\u3000\u9752\u9752\u8138\u4e0a\u4e00\u7ea2\uff0c\u96be\u4ee5\u56de\u7b54\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u56de\u53ef\u7cdf\u4e86\u3002\u7ed9\u4ed6\u62c6\u7a7f\u4e86\u897f\u6d0b\u955c\u3002\u201d\u53ea\u5f97\u7ed9\u4ed6\u4e1c\u62c9\u897f\u626f\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4ed9\u90fd\u6d3e\u8ddf\u4eba\u6253\u67b6\uff0c\u5b9a\u987b\u4e24\u4e2a\u4eba\u9f50\u4e0a\u3002\u5018\u82e5\u9053\u7237\u843d\u4e86\u5355\uff0c\u5c82\u4e0d\u662f\u975e\u5f97\u5feb\u9a6c\u52a0\u97ad\u56de\u5230\u4ed9\u90fd\u5c71\u53bb\uff0c\u9080\u4e86\u4e00\u4f4d\u540c\u95e8\u5e08\u5144\u5f1f\uff0c\u518d\u5feb\u9a6c\u52a0\u97ad\u7684\u56de\u6765\uff0c\u8fd9\u624d\u4e24\u4e2a\u4eba\u6253\u4eba\u5bb6\u4e00\u4e2a\uff1f\u4eba\u5bb6\u82e5\u662f\u4e0d\u8ba9\u4f60\u8d70\uff0c\u5b9a\u8981\u5355\u6253\u72ec\u6597\uff0c\u4e24\u4eea\u5251\u6cd5\u53c8\u600e\u4e48\u6837\u4e2a\u65e0\u654c\u4e8e\u5929\u4e0b\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u63d2\u53e3\u9053\uff1a\u201c\u4e24\u4eea\u5251\u6cd5\uff0c\u9634\u9633\u751f\u514b\uff0c\u672c\u9886\u5dee\u7684\u56fa\u987b\u4e24\u4eba\u540c\u4f7f\uff0c\u529f\u592b\u5230\u5bb6\u7684\uff0c\u5f53\u7136\u662f\u4e00\u4e2a\u4eba\u4f7f\u7684\u4e86\u3002\u96be\u9053\u5c0a\u5e08\u8fd9\u4e48\u9ad8\u7684\u6b66\u529f\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u72ec\u4f7f\u4e48\uff1f\u201d \u3000\u3000\u9752\u9752\u4e8e\u4e24\u4eea\u5251\u6cd5\u4e00\u65e0\u6240\u77e5\uff0c\u773c\u89c1\u4e8c\u4eba\u5939\u51fb\u8881\u627f\u5fd7\uff0c\u5173\u6000\u4e4b\u4e0b\u968f\u53e3\u8d28\u95ee\uff0c\u7adf\u9732\u51fa\u4e86\u9a6c\u811a\u3002\u8881\u627f\u5fd7\u53ea\u5f97\u4fe1\u53e3\u5f00\u6cb3\uff0c\u7ed9\u5979\u5706\u8c0e\u3002\u5176\u5b9e\u4ed9\u90fd\u6d3e\u8fd9\u4e24\u4eea\u5251\u6cd5\uff0c\u5411\u6765\u662f\u4e24\u4eba\u5408\u4f7f\u7684\u3002 \u3000\u3000\u95f5\u5b50\u534e\u4e0e\u6d1e\u7384\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5747\u60f3\uff1a\u201c\u5e08\u7236\u53ef\u6ca1\u8bf4\u8fc7\u8fd9\u5251\u6cd5\u4e00\u4e2a\u4eba\u53ef\u4f7f\uff0c\u6562\u60c5\u8fd9\u5c0f\u5b50\u80e1\u8bf4\u516b\u9053\uff1f\u201d\u5374\u4e5f\u4e0d\u80af\u627f\u8ba4\u5e08\u7236\u4e0d\u4f1a\u72ec\u4f7f\u3002 \u3000\u3000\u9752\u9752\u542c\u8881\u627f\u5fd7\u8bf4\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u5927\u662f\u9ad8\u5174\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u7d20\u6765\u8001\u5b9e\uff0c\u4eca\u65e5\u5374\u6ed1\u5934\u8d77\u6765\u3002\u201d\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u4e24\u4f4d\u9f50\u4e0a\uff0c\u8d4c\u8d5b\u7684\u5229\u7269\u53c8\u5f97\u52a0\u4e00\u4e9b\u4e86\u3002\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u8d4c\u751a\u4e48\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u8981\u662f\u4f60\u4eec\u8f93\u4e86\uff0c\u9664\u4e86\u6c38\u8fdc\u4e0d\u5f97\u518d\u627e\u7126\u5e2e\u4e3b\u751f\u4e8b\u4e4b\u5916\uff0c\u4f60\u5728\u5927\u529f\u574a\u7684\u90a3\u6240\u5927\u5b85\u5b50\uff0c\u53ef\u4e5f\u5f97\u8f93\u7ed9\u4e86\u8881\u5927\u54e5\u3002\u201d\u95f5\u5b50\u534e\u5fc3\u60f3\uff1a\u201c\u4e0d\u59a8\u751a\u4e48\u90fd\u7b54\u5e94\u4ed6\u4eec\uff0c\u53cd\u6b63\u9877\u523b\u4e4b\u95f4\uff0c\u4e0d\u662f\u628a\u4ed6\u4e00\u5251\u523a\u6b7b\uff0c\u4e5f\u8981\u6559\u4ed6\u8eab\u53d7\u91cd\u4f24\u3002\u201d\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u6837\uff01\u4f60\u8981\u4e00\u8d77\u6765\u4e24\u5bf9\u4e24\u4e5f\u6210\u3002\u522b\u8bf4\u6211\u4eec\u4ee5\u5927\u538b\u5c0f\uff0c\u4ee5\u591a\u80dc\u5c11\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u53c8\u600e\u77e5\u4e0d\u662f\u4ee5\u5c0f\u538b\u5927\uff0c\u4ee5\u5c11\u80dc\u591a\uff1f\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u3002\u4ed9\u90fd\uff0c\u4ed9\u90fd\uff0c\u725b\u76ae\u5439\u5f97\u561f\u561f\u561f\uff01\u201d\u95f5\u5b50\u534e\u6012\u706b\u66f4\u70bd\uff0c\u53eb\u9053\uff1a\u201c\u59d3\u8881\u7684\uff0c\u8981\u662f\u4f60\u7ed9\u6211\u4f24\u4e86\uff0c\u53c8\u8f93\u4e9b\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u4e00\u65f6\u5012\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u95f5\u4e8c\u54e5\uff0c\u4f60\u8fd9\u6240\u5b85\u5b50\u503c\u591a\u5c11\u94b1\uff1f\u201d\u95f5\u5b50\u534e\u6012\u9053\uff1a\u201c\u8c01\u8ddf\u4f60\u79f0\u5144\u9053\u5f1f\u4e86\uff1f\u8fd9\u5b85\u4e86\u6211\u8fd8\u662f\u4e0a\u4e2a\u6708\u4e70\u6765\u7684\uff0c\u82b1\u4e86\u56db\u5343\u4e09\u767e\u4e24\u94f6\u5b50\u3002\u5b85\u5b50\u867d\u65e7\uff0c\u5730\u65b9\u5374\u5927\u3002\u201d\u7126\u516c\u793c\u70b9\u5934\u9053\uff1a\u201c\u5927\u529f\u574a\u65e7\u5b85\u5bbd\u655e\u5f97\u7d27\u54ea\uff0c\u95f5\u7237\u4e70\u5f97\u4fbf\u5b9c\u4e86\u3002\u4e09\u4f4d\u8bf7\u7b49\u4e00\u4e0b\u3002\u201d \u3000\u3000\u8f6c\u5934\u5411\u5973\u513f\u5631\u5490\u4e86\u51e0\u53e5\u3002\u7126\u5b9b\u513f\u5954\u8fdb\u5185\u5ba4\uff0c\u62ff\u4e86\u4e00\u53e0\u94b1\u5e84\u7684\u5e84\u7968\u51fa\u6765\u3002 \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u8fd9\u4f4d\u8881\u7237\u4e3a\u5728\u4e0b\u5982\u6b64\u51fa\u529b\uff0c\u5144\u5f1f\u611f\u6fc0\u4e0d\u5c3d\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u56db\u5343\u4e09\u767e\u4e24\u94f6\u5b50\uff0c\u8981\u662f\u8881\u7237\u53cc\u62f3\u4e0d\u654c\u56db\u624b\uff0c\u90a3\u4e48\u8bf7\u95f5\u7237\u62ff\u53bb\u4fbf\u4e86\u3002\u53e6\u5916\u7684\u4e8b\uff0c\u95f5\u7237\u518d\u6765\u627e\u6211\uff0c\u54b1\u4eec\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\u3002\u597d\u670b\u53cb\u4ed7\u4e49\u52a9\u62f3\uff0c\u53ea\u987b\u70b9\u5230\u4e3a\u6b62\uff0c\u8fd8\u8bf7\u5927\u5bb6\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u4ed6\u6599\u60f3\u8881\u627f\u5fd7\u5b9a\u7136\u4e0d\u654c\uff0c\u53ef\u4e0d\u613f\u4ed6\u4e3a\u81ea\u5df1\u53d7\u5230\u635f\u4f24\u3002 \u3000\u3000\u90d1\u8d77\u4e91\u6027\u5b50\u8c6a\u723d\uff0c\u6700\u7231\u8d4c\u535a\uff0c\u767b\u65f6\u8d4c\u6027\u5927\u53d1\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u8bdd\u4e0d\u9519\uff0c\u53ea\u6bd4\u8f93\u8d62\uff0c\u4e0d\u51b3\u751f\u6b7b\u3002\u6211\u770b\u597d\u95f5\u4e8c\u54e5\uff01\u201d\u4ece\u8eab\u8fb9\u6478\u51fa\u4e24\u53ea\u91d1\u5143\u5b9d\u6765\uff0c\u5f80\u684c\u4e0a\u4e00\u63b7\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u8d4c\u4e09\u5bf9\u4e00\uff0c\u8fd9\u91cc\u662f\u4e09\u767e\u4e24\u91d1\u5b50\uff0c\u535a\u8c01\u7684\u4e00\u5343\u4e24\u94f6\u5b50\uff1f\u201d\u4ed6\u53eb\u4e86\u51e0\u58f0\uff0c\u6ca1\u4eba\u7b54\u5e94\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u8881\u627f\u5fd7\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u80fd\u662f\u4ed9\u90fd\u6d3e\u4e24\u4f4d\u9ad8\u624b\u4e4b\u654c\uff0c\u867d\u7136\u4ee5\u4e00\u535a\u4e09\uff0c\u751a\u5360\u4fbf\u5b9c\uff0c\u5374\u4e5f\u90fd\u4e0d\u6295\u6ce8\u3002 \u3000\u3000\u7126\u5b9b\u513f\u633a\u8eab\u800c\u51fa\uff0c\u8bf4\uff1a\u201c\u90d1\u4f2f\u4f2f\uff0c\u6211\u8ddf\u4f60\u8d4c\u3002\u201d\u9664\u4e0b\u8155\u4e0a\u7684\u4e00\u53ea\u5b9d\u77f3\u956f\u5b50\uff0c\u5f80\u684c\u4e0a\u4e00\u653e\u3002\u4f17\u4eba\u89c1\u8fd9\u956f\u4e0a\u5b9d\u77f3\u5728\u70db\u5149\u4e0b\u707f\u7136\u8000\u773c\uff0c\u5341\u5206\u73cd\u8d35\u3002\u90d1\u8d77\u4e91\u6bd5\u751f\u4e3a\u76d7\uff0c\u591a\u8bc6\u73cd\u5b9d\uff0c\u62ff\u8d77\u5b9d\u956f\u77a7\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u53ea\u956f\u5b50\u503c\u5f97\u4e09\u5343\u4e24\u94f6\u5b50\uff0c\u6211\u4e0d\u80fd\u6b3a\u5c0f\u5b69\u5b50\u3002\u5582\uff0c\u7ed9\u6211\u52a0\u516d\u5343\u4e24\u3002\u201d\u4ed6\u624b\u4e0b\u4eba\u53c8\u6367\u4e0a\u56db\u53ea\u91d1\u5143\u5b9d\u6765\u3002 \u3000\u3000\u90d1\u8d77\u4e91\u7b11\u9053\uff1a\u201c\u82e5\u662f\u4f60\u8d62\uff0c\u8fd9\u7b14\u94b1\u4f5c\u4f60\u7684\u5ac1\u5986\u5427\uff01\u201d\u9752\u9752\u542c\u5230\u201c\u5ac1\u5986\u201d\u4e24\u5b57\uff0c\u5411\u5b9b\u513f\u77aa\u4e86\u4e00\u773c\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u8001\u5927\u4e0d\u81ea\u5728\u8d77\u6765\u3002 \u3000\u3000\u98de\u5929\u9b54\u5973\u5b59\u4ef2\u541b\u5ffd\u628a\u534a\u622a\u65ad\u5251\u5f80\u684c\u4e0a\u4e00\u4e22\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u6211\u8d4c\u8fd9\u5251\uff01\u201d\u5979\u957f\u5251\u5148\u524d\u7ed9\u8881\u627f\u5fd7\u8e0f\u65ad\u4e86\uff0c\u6b64\u5251\u662f\u5e08\u5a18\u6240\u8d50\uff0c\u56e0\u6b64\u5f53\u4f17\u4eba\u53e3\u820c\u7eb7\u4e89\u4e4b\u65f6\uff0c\u5df2\u8fc7\u53bb\u5c06\u4e24\u622a\u65ad\u5251\u62fe\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9752\u9752\u5947\u9053\uff1a\u201c\u4f60\u8fd9\u534a\u622a\u5251\uff0c\u8c01\u8981\u5440\uff1f\u201d\u65c1\u4eba\u4e5f\u5747\u611f\u5947\u602a\u3002\u5b59\u4ef2\u541b\u5389\u58f0\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4e09\u535a\u4e00\u3002\u8981\u662f\u8fd9\u5c0f\u5b50\u4fa5\u5e78\u80dc\u4e86\uff0c\u4f60\u7528\u8fd9\u534a\u622a\u5251\u5728\u6211\u8eab\u6233\u622a\u4e09\u4e2a\u7a9f\u7abf\u3002\u4ed6\u8f93\u4e86\uff0c\u6211\u5728\u4f60\u8eab\u4e0a\u6233\u4e00\u4e2a\u7a9f\u7abf\u3002\u81ed\u5c0f\u5b50\uff0c\u8fd9\u53ef\u61c2\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5385\u4e0a\u4e00\u4f17\u6c5f\u6e56\u8c6a\u6770\u751f\u5e73\u4e5f\u4e0d\u77e5\u89c1\u8bc6\u8fc7\u591a\u5c11\u51f6\u6740\uff0c\u7ecf\u5386\u8fc7\u591a\u5c11\u5927\u8d4c\uff0c\u4f46\u8fd9\u822c\u4ee5\u6027\u547d\u76f8\u535a\u7684\u8d4c\u8d5b\uff0c\u5374\u662f\u4ece\u6240\u672a\u89c1\uff0c\u542c\u4e86\u5b59\u4ef2\u541b\u7684\u8bdd\uff0c\u90fd\u4e0d\u7981\u6697\u6697\u548b\u820c\u3002\u9752\u9752\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u4e00\u4e2a\u7f8e\u4eba\u513f\uff0c\u6211\u600e\u820d\u5f97\u4e0b\u624b\uff1f\u201d\u6885\u5251\u548c\u559d\u9053\uff1a\u201c\u6df7\u5e10\u5c0f\u5b50\uff0c\u5634\u91cc\u5e72\u51c0\u4e9b\uff01\u201d \u3000\u3000\u9752\u9752\u7b11\u7b11\u4e0d\u8bed\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u77aa\u773c\u77a7\u7740\u7126\u65b9\u4f17\u4eba\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53ea\u9053\u91d1\u9f99\u5e2e\u5728\u6c5f\u5357\u5f00\u5c71\u7acb\u67dc\uff0c\u603b\u6709\u51e0\u4e2a\u54cd\u5f53\u5f53\u7684\u811a\u8272\uff0c\u54ea\u77e5\u5c3d\u662f\u4e9b\u5a18\u513f\u4eec\u4e5f\u4e0d\u5982\u7684\u8113\u5305\u201d\u7126\u5b9b\u513f\u53eb\u9053\uff1a\u201c\u5a18\u513f\u4fbf\u600e\u6837\uff1f\u6211\u8ddf\u4f60\u8d4c\u4e86\u3002\u201d\u7126\u95e8\u5f1f\u5b50\u4e2d\u6709\u56db\u4e94\u4eba\u540c\u65f6\u7ad9\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u6211\u8ddf\u5979\u8d4c\u3002\u201d\u5b9b\u513f\u9053\uff1a\u201c\u4e0d\u7528\uff0c\u6211\u6765\u8d4c\u3002\u201d\u5b59\u4ef2\u541b\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90d1\u5c9b\u4e3b\uff0c\u4f60\u4f5c\u516c\u8bc1\u3002\u201d \u3000\u3000\u90d1\u8d77\u4e91\u867d\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5927\u6d77\u76d7\uff0c\u751f\u6027\u53c8\u6700\u597d\u8d4c\uff0c\u4f46\u5bf9\u8fd9\u9879\u8d4c\u8d5b\u5374\u4e5f\u6709\u4e9b\u4e0d\u5fcd\u5352\u7779\uff0c\u529d\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u59d1\u5a18\uff0c\u8981\u8d4c\u561b\uff0c\u5c31\u8d4c\u4e9b\u80ed\u8102\u82b1\u7c89\u513f\u751a\u4e48\u7684\uff0c\u4f55\u5fc5\u8fd9\u4e48\u8ba4\u771f\uff1f\u201d\u5b9b\u513f\u9053\uff1a\u201c\u5979\u5e9f\u4e86\u6211\u4eec\u7f57\u5e08\u54e5\u4e00\u6761\u624b\u81c2\uff0c\u56de\u5934\u6211\u8981\u628a\u5979\u4e24\u4e2a\u62db\u5b50\u5e9f\u4e86\u3002\u201d\u90d1\u8d77\u4e91\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u4fbf\u518d\u529d\u3002 \u3000\u3000\u6885\u5251\u548c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u7126\u5927\u59d1\u5a18\u5bf9\u8fd9\u4f4d\u91d1\u86c7\u95e8\u4eba\uff0c\u5012\u4e5f\u771f\u662f\u4e00\u5f80\u60c5\u6df1\uff0c\u5b81\u613f\u966a\u4ed6\u9976\u4e0a\u4e00\u6761\u6027\u547d\u3002\u201d\u7126\u5b9b\u513f\u8138\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u8d4c\uff1f\u201d \u3000\u3000\u9752\u9752\u542c\u4e86\u6885\u5251\u548c\u7684\u8bdd\uff0c\u4e0d\u7981\u4e00\u6123\uff0c\u5341\u5206\u607c\u6012\uff0c\u53eb\u9053\uff1a\u201c\u6211\u8ddf\u8fd9\u4e2a\u6ca1\u5f71\u5b50\u8d4c\u3002\u201d\u6885\u5251\u548c\u9053\uff1a\u201c\u8d4c\u751a\u4e48\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4e09\u535a\u4e00\u8ddf\u4f60\u8d4c\u3002\u4ed6\u8f93\u4e86\uff0c\u6211\u5f53\u573a\u53eb\u4f60\u4e09\u58f0\u7237\u7237\u3002\u4ed6\u8d62\u4e86\u5462\uff0c\u4f60\u53eb\u6211\u4e00\u58f0\u5c31\u591f\u4e86\uff0c\u7b97\u4f60\u4fbf\u5b9c\u3002\u201d\u4f17\u4eba\u4e0d\u7981\u597d\u7b11\uff0c\u89c9\u8fd9\u5c11\u5e74\u5b9e\u5728\u987d\u76ae\u5f97\u7d27\u3002\u6885\u5251\u548c\u6120\u9053\uff1a\u201c\u8c01\u8ddf\u4f60\u80e1\u95f9\uff1f\u6211\u8fd9\u91cc\u7b49\u7740\uff0c\u8981\u662f\u4ed6\u80dc\u4e86\uff0c\u6211\u518d\u6765\u9886\u6559\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u5355\u4eba\u72ec\u5251\uff0c\u6bd4\u4ed9\u90fd\u6d3e\u4e24\u4eba\u540c\u4f7f\u7684\u4e24\u4eea\u5251\u6cd5\u8fd8\u8981\u5389\u5bb3\uff1f\u201d\u6885\u5251\u548c\u9053\uff1a\u201c\u6211\u662f\u534e\u5c71\u6d3e\uff0c\u4ed6\u4eec\u662f\u4ed9\u90fd\u6d3e\uff0c\u5404\u6709\u5404\u7684\u7edd\u62db\u3002\u4f60\u522b\u6311\u62e8\u79bb\u95f4\u3002\u201d \u3000\u3000\u6d1e\u7384\u9053\u4eba\u542c\u4ed6\u4eec\u8bf4\u4e2a\u4e0d\u4e86\uff0c\u5fc3\u5934\u7126\u8e81\uff0c\u53eb\u9053\uff1a\u201c\u522b\u8bf4\u5566\uff0c\u5582\uff0c\u5c0f\u5b50\uff0c\u770b\u62db\u3002\u201d\u633a\u5251\u5411\u8881\u627f\u5fd7\u523a\u53bb\u3002\u95f5\u5b50\u534e\u8ddf\u7740\u8e0f\u6d2a\u95e8\uff0c\u8fdb\u504f\u950b\u3002\u53ea\u89c1\u4ed9\u90fd\u6d3e\u4e00\u4fd7\u4e00\u9053\u4e24\u540d\u5f1f\u5b50\uff0c\u4e00\u4eba\u5de6\u624b\u5251\uff0c\u4e00\u4eba\u53f3\u624b\u5251\uff0c\u6309\u7740\u6613\u7ecf\u516b\u516b\u516d\u5341\u56db\u5366\u7684\u5366\u8c61\uff0c\u53cc\u5251\u7eb5\u6a2a\u3002\u767d\u5149\u95ea\u52a8\uff0c\u5251\u62db\u751f\u751f\u706d\u706d\uff0c\u6d88\u6d88\u957f\u957f\uff0c\u9690\u9690\u6709\u98ce\u96f7\u4e4b\u52bf\u3002 \u3000\u3000\u91d1\u86c7\u90ce\u541b\u5148\u65f6\u5728\u4ed9\u90fd\u5c71\u548c\u9ec4\u6728\u9053\u4eba\u8bba\u5251\uff0c\u5373\u77e5\u4e24\u4eea\u5251\u6cd5\u867d\u7136\u53d8\u5316\u7e41\u590d\uff0c\u51cc\u5389\u72e0\u8fa3\uff0c\u5176\u5b9e\u8fd8\u4e0d\u53ca\u4ed9\u90fd\u6d3e\u539f\u6709\u7684\u4e0a\u6e05\u5251\u6cd5\uff0c\u5176\u4e2d\u9887\u6709\u4e0d\u5c11\u7834\u7efd\uff0c\u968f\u53e3\u6307\u51fa\u4e86\u4e24\u5904\u3002\u4f46\u9ec4\u6728\u9053\u4eba\u751a\u4e3a\u81ea\u8d1f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u5251\u6cd5\u4e2d\u5c31\u7b97\u5c1a\u6709\u6f0f\u6d1e\uff0c\u53ea\u6015\u5929\u4e0b\u4e5f\u5df2\u65e0\u4eba\u7834\u5f97\u3002\u201d\u91d1\u86c7\u90ce\u541b\u4e5f\u4e0d\u518d\u8bf4\u3002\u540e\u6765\u6e29\u6c0f\u4e94\u8001\u5927\u4e3e\u9080\u4eba\u5bf9\u6297\u91d1\u86c7\u90ce\u541b\uff0c\u6240\u9080\u6765\u7684\u9ad8\u624b\u4e4b\u4e2d\uff0c\u6709\u4ed9\u90fd\u6d3e\u5251\u5ba2\u5728\u5185\u3002\u5bf9\u654c\u65f6\u91d1\u86c7\u90ce\u541b\u6210\u7af9\u5728\u80f8\uff0c\u4e58\u865a\u800c\u5165\uff0c\u6570\u62db\u95f4\u5373\u628a\u4e24\u4eea\u5251\u6cd5\u7834\u53bb\u3002\u4ed6\u540e\u6765\u5728\u79d8\u7b08\u4e4b\u4e2d\u66fe\u8be6\u7ec6\u53d9\u660e\u3002\u662f\u4ee5\u8881\u627f\u5fd7\u6709\u6043\u65e0\u6050\uff0c\u5728\u4e24\u4eba\u5251\u5149\u4e2d\u7a7f\u8dc3\u6765\u53bb\uff0c\u6f47\u6d12\u81ea\u5982\u3002 \u3000\u3000\u95f5\u5b50\u534e\u4e0e\u6d1e\u7384\u9053\u4eba\u53cc\u5251\u5982\u75be\u98ce\uff0c\u5982\u95ea\u7535\uff0c\u59cb\u7ec8\u523a\u4e0d\u5230\u4ed6\u8eab\u4e0a\uff0c\u65c1\u89c2\u4f17\u4eba\u6108\u770b\u6108\u5947\u3002 \u3000\u3000\u90d1\u8d77\u4e91\u5bf9\u5341\u529b\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u8f7b\u8eab\u529f\u592b\u7684\u786e\u4e86\u5f97\uff0c\u91d1\u86c7\u90ce\u541b\u5f53\u771f\u540d\u4e0d\u865a\u4f20\u3002\u201d\u5341\u529b\u5927\u5e08\u70b9\u5934\u9053\uff1a\u201c\u540e\u8f88\u4e4b\u4e2d\uff0c\u5982\u6b64\u4eba\u624d\u4e5f\u7b97\u5341\u5206\u96be\u5f97\u4e86\u3002\u201d\u6885\u5251\u548c\u4e0e\u5b59\u4ef2\u541b\u5374\u90fd\u4e0d\u7981\u6697\u6697\u6709\u4e9b\u62c5\u5fc3\u3002\u5b59\u4ef2\u541b\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5c31\u662f\u9003\u6765\u8eb2\u53bb\u4e0d\u6562\u771f\u6253\uff0c\u90a3\u7b97\u751a\u4e48\u6bd4\u6b66\u4e86\uff1f\u201d \u3000\u3000\u95f5\u5b50\u534e\u6740\u5f97\u6027\u8d77\uff0c\u5251\u8d70\u4e2d\u5bab\uff0c\u7b14\u76f4\u5411\u8881\u627f\u5fd7\u80f8\u524d\u523a\u53bb\u3002\u6d1e\u7384\u540c\u65f6\u4e00\u62db\u201c\u5de6\u53f3\u5f00\u5f13\u201d\uff0c\u5de6\u523a\u4e00\u5251\uff0c\u53f3\u523a\u4e00\u5251\u3002\u4e24\u4eba\u5939\u653b\uff0c\u8981\u6559\u4ed6\u65e0\u5904\u53ef\u907f\u3002\u8881\u627f\u5fd7\u7a81\u7136\u6b3a\u8eab\u76f4\u8fdb\uff0c\u5728\u5251\u5e95\u94bb\u8fc7\uff0c\u5de6\u80a9\u4e00\u633a\uff0c\u649e\u5728\u95f5\u5b50\u534e\u5de6\u8180\u3002\u4ed6\u53ea\u4f7f\u4e86\u4e09\u6210\u529b\uff0c\u95f5\u5b50\u534e\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u4e9b\u8dcc\u5012\u3002\u6d1e\u7384\u5927\u60ca\uff0c\u5237\u5237\u5237\u8fde\u73af\u4e09\u5251\uff0c\u594b\u529b\u6321\u4f4f\u3002\u95f5\u5b50\u534e\u8fd9\u624d\u7ad9\u5b9a\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u6742\u79cd\uff0c\u649e\u4f60\u7237\u7237\u5417\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u8fd9\u6b21\u51fa\u624b\uff0c\u672c\u6765\u4f46\u6c42\u6392\u89e3\u7ea0\u7eb7\uff0c\u4e0d\u60f3\u5f97\u7f6a\u6c5f\u6e56\u4e0a\u4eba\u7269\uff0c\u66f4\u4e0d\u613f\u7ed3\u6028\u79cd\u4ec7\uff0c\u8fd9\u65f6\u542c\u95f5\u5b50\u534e\u53e3\u5410\u6c61\u8a00\uff0c\u8fb1\u53ca\u81ea\u5df1\u5148\u4eba\uff0c\u4e0d\u7981\u5927\u6012\uff0c\u5fc3\u4e0b\u76d8\u7b97\uff1a\u4eca\u65e5\u5982\u4e0d\u9732\u4e00\u4e24\u624b\u4e0a\u4e58\u6b66\u529f\uff0c\u5c06\u8fd9\u4e8c\u4eba\u5f53\u573a\u538b\u5012\uff0c\u8fd9\u4ef6\u4e8b\u96be\u4ee5\u8f7b\u6613\u4e86\u7ed3\uff0c\u540c\u65f6\u5a01\u98ce\u4e0d\u663e\uff0c\u5f85\u4f1a\u5904\u7f6e\u901a\u654c\u5356\u56fd\u7684\u592a\u767d\u4e09\u82f1\u4e4b\u65f6\uff0c\u53ea\u6015\u65c1\u4eba\u4e0d\u670d\uff0c\u52bf\u987b\u591a\u8d39\u5507\u820c\u3002\u6700\u597d\u662f\u5192\u5145\u91d1\u86c7\u95e8\u4eba\u5230\u5e95\uff0c\u4ee5\u514d\u4e8c\u5e08\u54e5\u8138\u4e0a\u4e0d\u597d\u770b\uff0c\u53ea\u662f\u987b\u5f97\u72c2\u50b2\u53e4\u602a\uff0c\u4e0e\u81ea\u5df1\u5e73\u65e5\u4e3a\u4eba\u5927\u4e0d\u76f8\u540c\u624d\u6210\u3002\u4e8e\u662f\u8dc3\u5230\u684c\u8fb9\uff0c\u4f38\u624b\u62ff\u8d77\u9152\u676f\uff0c\u4ef0\u5934\u559d\u5e72\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u6253\uff0c\u5feb\u6253\uff0c\u6211\u9152\u6ca1\u559d\u591f\uff0c\u996d\u6ca1\u5403\u9971\u5462\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u89c1\u4ed6\u5bf9\u81ea\u5df1\u5982\u6b64\u8f7b\u8511\uff0c\u66f4\u662f\u607c\u6012\uff0c\u957f\u5251\u8d8a\u523a\u8d8a\u5feb\u3002 \u3000\u3000\u6d1e\u7384\u4f4e\u58f0\u9053\uff1a\u201c\u95f5\u5e08\u54e5\uff0c\u6c89\u4f4f\u6c14\uff0c\u522b\u4e2d\u4e86\u6fc0\u5c06\u4e4b\u8ba1\u3002\u201d\u95f5\u5b50\u534e\u7acb\u65f6\u9192\u609f\u3002\u4e24\u4eba\u5de6\u53f3\u76d8\u65cb\uff0c\u53cc\u5251\u6c89\u7a33\u72e0\u8fa3\uff0c\u53c8\u628a\u8881\u627f\u5fd7\u88f9\u5728\u5793\u5fc3\u3002\u8881\u627f\u5fd7\u5de6\u624b\u6301\u676f\uff0c\u53f3\u624b\u6301\u7b77\uff0c\u968f\u5251\u8fdb\u9000\u3002\u4e24\u4eba\u5251\u6cd5\u867d\u72e0\uff0c\u5374\u600e\u5948\u4f55\u5f97\u4e86\u4ed6\uff1f \u3000\u3000\u5251\u5149\u6eda\u52a8\u4e2d\uff0c\u8881\u627f\u5fd7\u5ffd\u5730\u8dc3\u51fa\u5708\u5b50\uff0c\u628a\u9152\u676f\u5f80\u684c\u4e0a\u4e00\u653e\uff0c\u53eb\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u7ed9\u6211\u659f\u9152\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u597d\uff01\u201d\u8881\u627f\u5fd7\u5de6\u624b\u63d0\u4e86\u4e00\u5f20\u6905\u5b50\uff0c\u7ad9\u5728\u684c\u8fb9\uff0c\u5c06\u4e24\u4eba\u653b\u6765\u5251\u62db\u968f\u624b\u6321\u5f00\uff0c\u5f85\u9152\u659f\u6ee1\uff0c\u4f38\u7b77\u5939\u4e86\u4e00\u6761\u9e21\u817f\uff0c\u653e\u4e0b\u6905\u5b50\uff0c\u62ff\u4e86\u9152\u676f\u53c8\u8dc3\u5165\u5385\u5fc3\uff0c\u54ac\u4e86\u4e00\u53e3\u9e21\u817f\uff0c\u53eb\u9053\uff1a\u201c\u4e24\u4eea\u5251\u6cd5\u672c\u6765\u5c31\u6709\u6bdb\u75c5\uff0c\u4f60\u4eec\u53c8\u4f7f\u5f97\u4e0d\u5bf9\uff0c\u600e\u80fd\u4f24\u6211\uff1f\u4f60\u4eec\u8fd9\u6869\u4e70\u5356\uff0c\u4eca\u65e5\u5b9a\u8981\u8680\u672c\u4e86\u3002\u201d \u3000\u3000\u9752\u9752\u89c1\u8fd9\u4e2a\u7d20\u6765\u8c28\u539a\u7684\u5927\u54e5\u5ffd\u7136\u5927\u4f5c\u72c2\u6001\uff0c\u5374\u59cb\u7ec8\u653e\u4e0d\u5f00\uff0c\u4e0d\u5927\u50cf\u6837\uff0c\u8981\u8bf4\u51e0\u53e5\u7b11\u8bdd\uff0c\u4e5f\u53ea\u80fd\u62fe\u4ed6\u5927\u5e08\u54e5\u7684\u7259\u6167\uff0c\u4e0d\u7981\u6697\u6697\u597d\u7b11\u3002\u8981\u77e5\u8881\u627f\u5fd7\u751f\u5e73\u5e76\u672a\u89c1\u8fc7\u771f\u6b63\u758f\u72c2\u6f47\u6d12\u4e4b\u4eba\uff0c\u8fd9\u65f6\u60f3\u5b66\u91d1\u86c7\u90ce\u541b\uff0c\u5176\u5b9e\u4e09\u5206\u50cf\u4e86\u5927\u5e08\u54e5\u9ec4\u771f\u7684\u6ed1\u7a3d\u7a81\u68af\uff0c\u53e6\u6709\u4e09\u5206\uff0c\u5374\u5b66\u4e86\u5f53\u65e5\u5728\u6e29\u5bb6\u5e84\u4e0a\u6240\u89c1\u5415\u4e03\u5148\u751f\u7684\u50b2\u6162\u81ea\u5927\u3002\u9752\u9752\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6709\u4eba\u966a\u4f60\u6349\u8ff7\u85cf\uff0c\u4f60\u5012\u5feb\u6d3b\uff0c\u53ef\u6ca1\u4eba\u966a\u6211\u73a9\u800d\u3002\u6211\u4e0d\u5982\u4f5c\u4e00\u7bc7\u6587\u7ae0\uff0c\u4e5f\u514d\u5f97\u95f2\u7740\u65e0\u804a\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u4f5c\u751a\u4e48\u6587\u7ae0\u5462\uff1f\u201d\u6d1e\u7384\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u770b\u5251\uff01\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u6709\u4e86\uff0c\u9898\u76ee\u53eb\u4f5c\u2018\u91d1\u86c7\u4f7f\u8005\u5251\u620f\u4e24\u50bb\u8bb0\u2019\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u9898\u76ee\u4e0d\u9519\uff0c\u6587\u7ae0\u5fc5\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u9752\u9752\u6447\u5934\u6643\u8111\uff0c\u62d6\u957f\u4e86\u58f0\u97f3\u5ff5\u9053\uff1a\u201c\u592b\u5b9d\u5251\u8005\uff0c\u8bda\u6740\u4eba\u4e4b\u5229\u5668\uff1b\u800c\u50bb\u74dc\u8005\uff0c\u4e43\u8822\u6750\u4e4b\u522b\u53f7\u3002\u4e00\u50bb\u4ee4\u4eba\u8f97\u7136\u89e3\u9890\uff0c\u4e8c\u50bb\u62db\u4eba\u6367\u8179\u72c2\u7b11\uff0c\u800c\u4e8c\u50bb\u624b\u6325\u957f\u5251\u6b32\u56fe\u6740\u4eba\uff0c\u4e43\u4f7f\u6211\u55b7\u9152\u5782\u6d95\uff0c\u5927\u547c\u7cdf\u7cd5\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u55b7\u9152\u5782\u6d95\uff0c\u53ef\u5708\u53ef\u70b9\u3002\u201d\u8bf4\u7740\u8fde\u907f\u4e09\u8bb0\u9669\u62db\u3002 \u3000\u3000\u9752\u9752\u53c8\u5ff5\u9053\uff1a\u201c\u6211\u4e43\u91d1\u86c7\u4f7f\u8005\uff0c\u6b23\u4f5c\u4ef2\u8fde\uff1b\u541b\u60df\u6267\u8ff7\u4e0d\u609f\uff0c\u987d\u6297\u6ecb\u6270\u3002\u56db\u65b9\u541b\u5b50\u505c\u676f\u89c2\u6597\uff0c\u4e09\u540d\u5978\u8d3c\u5fe7\u5fc3\u5982\u6f6e\u3002\u5251\u6cd5\u6709\u4e24\u4eea\u4e4b\u540d\uff0c\u5343\u62db\u4e07\u62db\uff0c\u5c3d\u662f\u4f4e\u62db\uff1b\u8d4c\u535a\u4ee5\u5de8\u5b85\u4e3a\u6ce8\uff0c\u4e00\u8f93\u518d\u8f93\uff0c\u4fdd\u4e0d\u4f4f\u4e86\u3002\u4ed9\u90fd\u4e24\u50bb\u624b\u5fd9\u811a\u4e71\uff0c\u4e0d\u89c9\u7834\u7efd\u767e\u51fa\uff1b\u91d1\u86c7\u4f7f\u8005\u65e0\u53ef\u5948\u4f55\uff0c\u60df\u6709\u5c06\u4e4b\u51fb\u5012\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u9752\u9752\u5ff5\u5230\u8fd9\u4e2a\u201c\u5012\u201d\u5b57\uff0c\u7a81\u7136\u8f6c\u8eab\uff0c\u7b77\u4e0a\u9e21\u817f\u8fce\u9762\u5f80\u95f5\u5b50\u534e\u63b7\u53bb\uff0c\u4f38\u7b77\u5939\u4f4f\u6d1e\u7384\u523a\u6765\u4e4b\u5251\uff0c\u529b\u900f\u7bb8\u5c16\uff0c\u731b\u559d\uff1a\u201c\u6492\u5251\uff01\u201d\u53ea\u542c\u545b\u5577\u5577\u4e00\u58f0\uff0c\u6d1e\u7384\u62ff\u6301\u4e0d\u7a33\uff0c\u957f\u5251\u843d\u5730\u3002\u4ed6\u53f3\u638c\u4e00\u7acb\uff0c\u5de6\u817f\u500f\u5730\u626b\u51fa\uff0c\u6b32\u56fe\u8d25\u4e2d\u6c42\u80dc\u3002\u8881\u627f\u5fd7\u53cc\u8db3\u4e00\u70b9\uff0c\u8eab\u5b50\u8dc3\u8d77\uff0c\u907f\u5f00\u4e86\u8fd9\u817f\uff0c\u624b\u4e2d\u9152\u676f\u540c\u65f6\u98de\u51fa\uff0c\u6b63\u6253\u4e2d\u95f5\u5b50\u534e\u5de6\u624b\u201c\u66f2\u5c3a\u7a74\u201d\u4e0a\u3002\u95f5\u5b50\u534e\u624b\u81c2\u4e00\u9ebb\uff0c\u5251\u5df2\u8131\u624b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u62db\u201c\u5bd2\u9e26\u8d74\u6c34\u201d\uff0c\u6251\u4e86\u4e0b\u53bb\uff0c\u62a2\u8d77\u53cc\u5251\uff0c\u624b\u8155\u4e00\u632f\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u6ca1\u89c1\u8fc7\u4e00\u4eba\u4f7f\u7684\u4e24\u4eea\u5251\u6cd5\uff0c\u8fd9\u5c31\u7559\u795e\u77a7\u7740\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u53cc\u5251\u821e\u4e86\u5f00\u6765\uff0c\u5de6\u653b\u53f3\u5b88\uff0c\u53f3\u51fb\u5de6\u62d2\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u679c\u7136\u4e0e\u4e24\u4eea\u5251\u6cd5\u6beb\u65e0\u4e8c\u81f4\u3002\u5251\u62db\u7e41\u590d\uff0c\u53d8\u5316\u591a\u7aef\uff0c\u6d1e\u7384\u548c\u95f5\u5b50\u534e\u9002\u624d\u5206\u522b\u4f7f\u51fa\uff0c\u4eba\u4eba\u90fd\u5df2\u4eb2\u89c1\uff0c\u6b64\u65f6\u89c1\u4ed6\u4e00\u4eba\u53cc\u5251\u7adf\u56ca\u62ec\u4ed9\u90fd\u6d3e\u4e8c\u5927\u5f1f\u5b50\u7684\u5251\u62db\uff0c\u5c3d\u7686\u76f8\u987e\u9a87\u7136\u3002 \u3000\u3000\u8881\u627f\u5fd7\u821e\u5230\u9163\u5904\uff0c\u5251\u6c14\u5982\u8679\uff0c\u52bf\u82e5\u96f7\u9706\uff0c\u771f\u6709\u6c14\u541e\u6cb3\u5cb3\u4e4b\u6982\uff0c\u4e24\u4eea\u5251\u6cd5\u516d\u5341\u56db\u62db\u4f7f\u5b8c\uff0c\u53ea\u542c\u4ed6\u4e00\u58f0\u65ad\u559d\uff0c\u53cc\u5251\u8131\u624b\u98de\u51fa\uff0c\u63d2\u5165\u5c4b\u9876\u5de8\u6881\uff0c\u76f4\u6ca1\u5251\u67c4\u3002\u8fd9\u4e00\u8bb0\u201c\u5929\u5916\u98de\u9f99\u201d\uff0c\u5374\u662f\u534e\u5c71\u6d3e\u7a46\u4eba\u6e05\u7684\u7edd\u62db\u3002\u8881\u627f\u5fd7\u7edd\u6280\u4e00\u663e\uff0c\u5782\u624b\u9000\u5f00\uff0c\u53ea\u542c\u5385\u4e2d\u91c7\u58f0\u56db\u8d77\uff0c\u9f13\u638c\u5982\u96f7\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u5374\u6697\u6697\u540e\u6094\uff1a\u201c\u554a\u54df\u4e0d\u597d\uff0c\u6211\u4f7f\u5f97\u5174\u8d77\uff0c\u7adf\u7528\u4e0a\u4e86\u672c\u95e8\u7684\u7edd\u62db\uff0c\u4e8c\u5e08\u54e5\u7684\u95e8\u4eba\u600e\u4f1a\u770b\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u9752\u9752\u53eb\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u6709\u4eba\u8981\u53eb\u6211\u4eb2\u7237\u7237\u5566\uff01\u201d\u6885\u5251\u548c\u94c1\u9752\u7740\u8138\uff0c\u624b\u6309\u5251\u67c4\u3002 \u3000\u3000\u90d1\u8d77\u4e91\u7b11\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u4f60\u8d62\u5566\uff0c\u8bf7\u6536\u4e86\u5427\uff01\u201d\u968f\u624b\u628a\u91d1\u5143\u5b9d\u4e00\u63a8\u3002\u5b9b\u513f\u8eac\u8eab\u9053\u8c22\uff0c\u8bf4\u9053\uff1a\u201c\u90d1\u4f2f\u4f2f\uff0c\u6211\u4ee3\u4f60\u8d4f\u4e86\u4eba\u5427\uff01\u201d \u3000\u3000\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u91cc\u4e5d\u5343\u4e24\u94f6\u5b50\uff0c\u662f\u90d1\u5c9b\u4e3b\u8ddf\u6211\u95f9\u7740\u73a9\u6253\u8d4c\u7684\u5f69\u91d1\u3002\u5404\u4f4d\u8fdc\u9053\u800c\u6765\uff0c\u91d1\u9f99\u5e2e\u62db\u5f85\u4e0d\u5468\uff0c\u5f88\u662f\u60ed\u6127\uff0c\u73b0\u4eca\u501f\u82b1\u732e\u4f5b\uff0c\u4f17\u4f4d\u524d\u8f88\u53d4\u4f2f\u3001\u5144\u957f\u59ca\u59ca\u5e26\u6765\u7684\u4ec6\u4ece\u7ba1\u4e8b\uff0c\u6bcf\u4f4d\u5949\u9001\u94f6\u5b50\u4e00\u767e\u4e24\u3002\u660e\u5929\u6211\u5dee\u4eba\u9001\u5230\u5404\u4f4d\u5bd3\u6240\u6765\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u4e0d\u4f24\u4eba\u547d\uff0c\u89e3\u4e86\u8fd9\u573a\u6028\u4ec7\uff0c\u91d1\u9f99\u5e2e\u5904\u7f6e\u5f97\u4e5f\u5f88\u5f97\u5f53\uff0c\u90fd\u5f88\u5feb\u6170\uff0c\u53ea\u662f\u95f5\u5b50\u534e\u4e0e\u6d1e\u7384\u906d\u6b64\u5927\u8d25\uff0c\u672a\u514d\u8138\u4e0a\u65e0\u5149\u3002 \u3000\u3000\u7126\u516c\u793c\u53c8\u9053\uff1a\u201c\u5728\u4e0b\u5f53\u5e74\u6027\u5b50\u6025\u8e81\uff0c\u505a\u4e8b\u83bd\u649e\uff0c\u4ee5\u81f4\u5931\u624b\u4f24\u4e86\u95f5\u4e8c\u7237\u7684\u5144\u957f\uff0c\u5b9e\u5728\u4e07\u5206\u62b1\u6127\u3002\u73b0\u4e0b\u5f53\u7740\u5404\u4f4d\u82f1\u96c4\uff0c\u5411\u95f5\u4e8c\u7237\u8c22\u7f6a\u3002\u5b9b\u513f\uff0c\u4f60\u5411\u95f5\u53d4\u53d4\u884c\u793c\u3002\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u5411\u95f5\u5b50\u534e\u4f5c\u63d6\u3002\u7126\u5b9b\u513f\u662f\u665a\u8f88\uff0c\u4fbf\u78d5\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u95f5\u5b50\u534e\u6709\u8a00\u5728\u5148\uff0c\u6c5f\u6e56\u4e0a\u597d\u6c49\u8bf4\u4e00\u662f\u4e00\uff0c\u81ea\u5df1\u82e5\u8981\u53cd\u6094\uff0c\u9080\u6765\u7684\u670b\u53cb\u672a\u5fc5\u80af\u518d\u76f8\u52a9\uff0c\u8fd9\u91d1\u86c7\u90ce\u541b\u7684\u5f1f\u5b50\u6b66\u529f\u5982\u6b64\u9ad8\u5f3a\uff0c\u81ea\u5df1\u53ef\u4e07\u4e07\u4e0d\u662f\u654c\u624b\uff0c\u800c\u4e14\u770b\u4e86\u90a3\u4e24\u901a\u4e66\u4fe1\u540e\uff0c\u5fc3\u4e2d\u4e5f\u77e5\u66f2\u5728\u5df1\u65b9\uff0c\u4e0d\u5982\u4e58\u6b64\u6536\u7bf7\uff0c\u4e8e\u662f\u4f5c\u63d6\u8fd8\u793c\uff0c\u4f46\u60f3\u8d77\u8fc7\u4e16\u7684\u5144\u957f\uff0c\u4e0d\u7981\u5782\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u95f5\u4e8c\u7237\u5bbd\u6d2a\u5927\u91cf\uff0c\u4e0d\u548e\u65e2\u5f80\uff0c\u5144\u5f1f\u611f\u6fc0\u4e0d\u5c3d\u3002\u81f3\u4e8e\u8d4c\u5b85\u5b50\u7684\u8bdd\uff0c\u60f3\u6765\u8fd9\u4f4d\u7237\u53f0\u4e5f\u662f\u4e00\u53e5\u7b11\u8bdd\uff0c\u4e0d\u5fc5\u518d\u63d0\u3002\u5144\u5f1f\u660e\u5929\u9a6c\u4e0a\u7ed9\u4e24\u4f4d\u7237\u53f0\u53e6\u7f6e\u4e00\u6240\u5b85\u7b2c\u5c31\u662f\u3002\u201d \u3000\u3000\u9752\u9752\u4e0b\u988f\u4e00\u6602\uff0c\u9053\uff1a\u201c\u90a3\u4e0d\u6210\uff0c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff0c\u8bf4\u51fa\u4e86\u7684\u8bdd\u600e\u80fd\u53cd\u6094\u4e0d\u7b97\uff1f\u201d \u3000\u3000\u4f17\u4eba\u90fd\u662f\u4e00\u6123\uff0c\u5fc3\u60f3\u7126\u516c\u793c\u65e2\u7136\u7b54\u5e94\u53e6\u7f6e\u5b85\u7b2c\uff0c\u6240\u4e70\u7684\u623f\u5b50\u6bd4\u95f5\u5b50\u534e\u7684\u4f4f\u5b85\u597d\u4e0a\u5341\u500d\uff0c\u4e5f\u4e0d\u5e0c\u5947\uff0c\u4f55\u5fc5\u5b9a\u8981\u626b\u4eba\u989c\u9762\uff1f\u8fd9\u767d\u8138\u5c0f\u5b50\u59d4\u5b9e\u592a\u4e0d\u4f1a\u505a\u4eba\u4e86\u3002 \u3000\u3000\u7126\u516c\u793c\u5411\u9752\u9752\u4f5c\u4e86\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u53f0\uff0c\u4f60\u4eec\u4e24\u4f4d\u7684\u6069\u60c5\uff0c\u6211\u662f\u6c38\u8fdc\u8865\u62a5\u4e0d\u8fc7\u6765\u7684\u4e86\u3002\u8bf7\u8001\u5f1f\u53f0\u518d\u5e2e\u6211\u4e00\u4e2a\u5fd9\u3002\u5144\u5f1f\u5728\u5357\u95e8\u6709\u5ea7\u56ed\u5b50\uff0c\u5728\u5357\u4eac\u4e5f\u7b97\u662f\u6709\u540d\u6c14\u7684\uff0c\u8bf7\u4e24\u4f4d\u8d4f\u5149\u6536\u7528\uff0c\u5305\u4e24\u4f4d\u79f0\u5fc3\u6ee1\u610f\u5c31\u662f\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u8fd9\u4f4d\u95f5\u7237\u521a\u624d\u8981\u6740\u4f60\u62a5\u4ec7\uff0c\u4f60\u8bf4\u522b\u6740\u6211\u5566\uff0c\u6211\u53e6\u5916\u62ff\u4e00\u4e2a\u4eba\u7ed9\u4f60\u6740\uff0c\u8fd9\u4e2a\u4eba\u5728\u5357\u4eac\u4e5f\u7b97\u662f\u6709\u540d\u6c14\u7684\uff0c\u8bf7\u95f5\u7237\u8d4f\u5149\u6740\u4e86\uff0c\u5305\u4f60\u6740\u5f97\u79f0\u5fc3\u6ee1\u610f\u5c31\u662f\u3002\u4ed6\u80af\u4e0d\u80af\u5440\uff1f\u201d \u3000\u3000\u7126\u516c\u793c\u7ed9\u5979\u51e0\u53e5\u62a2\u767d\uff0c\u8baa\u8baa\u7684\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u53ea\u6709\u82e6\u7b11\uff0c\u8f6c\u5934\u5bf9\u5973\u513f\u9053\uff1a\u201c\u8fd9\u4f4d\u7237\u53f0\u65e2\u7136\u559c\u6b22\u95f5\u4e8c\u53d4\u7684\u5b85\u5b50\uff0c\u4f60\u5dee\u4eba\u628a\u56db\u5343\u4e09\u767e\u4e24\u94f6\u5b50\u7684\u5c4b\u4ef7\uff0c\u56de\u5934\u7ed9\u95f5\u4e8c\u53d4\u9001\u8fc7\u53bb\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u6211\u8fd8\u8981\u751a\u4e48\u94f6\u5b50\uff1f\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\uff0c\u6211\u8ddf\u7126\u5e2e\u4e3b\u7684\u6028\u4ec7\u5c31\u6b64\u4e00\u7b14\u5e26\u8fc7\u3002\u5144\u5f1f\u660e\u65e5\u56de\u5230\u4e61\u4e0b\uff0c\u6311\u7caa\u79cd\u7530\uff0c\u518d\u4e5f\u6ca1\u8138\u5728\u6c5f\u6e56\u4e0a\u6df7\u4e86\u3002\u8fd9\u6240\u5b85\u5b50\u4e24\u4f4d\u53d6\u53bb\u4fbf\u662f\u3002\u201d\u56e2\u56e2\u5411\u4f17\u4eba\u4f5c\u63d6\uff0c\u9053\uff1a\u201c\u5404\u4f4d\u597d\u670b\u53cb\u8fdc\u6765\u76f8\u52a9\uff0c\u54ea\u77e5\u5144\u5f1f\u4e0d\u4e89\u6c14\uff0c\u5b66\u827a\u4e0d\u7cbe\uff0c\u6ca1\u80fd\u7ed9\u8fc7\u4e16\u7684\u5144\u957f\u62a5\u4ec7\uff0c\u7d2f\u5f97\u5404\u4f4d\u767d\u8d70\u4e00\u8d9f\uff0c\u5144\u5f1f\u53ea\u6709\u5c06\u6765\u518d\u56fe\u8865\u62a5\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u8bf4\u5f97\u723d\u5feb\uff0c\u81ea\u89c9\u9002\u624d\u8fb1\u4eba\u592a\u751a\uff0c\u4e0d\u7559\u4f59\u5730\uff0c\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u95f5\u4e8c\u7237\uff0c\u4f60\u867d\u8d25\u5728\u6211\u624b\u4e0b\uff0c\u5176\u5b9e\u6211\u529f\u592b\u8ddf\u4f60\u548c\u6d1e\u7384\u9053\u957f\u5dee\u5f97\u5f88\u8fdc\uff0c\u8bf7\u4e24\u4f4d\u4e0d\u8981\u4ecb\u610f\u3002\u665a\u8f88\u9002\u624d\u65e0\u793c\uff0c\u5927\u662f\u4e0d\u8be5\uff0c\u8c28\u5411\u4e24\u4f4d\u8c22\u8fc7\u3002\u201d\u8bf4\u7740\u5411\u4e8c\u4eba\u4e00\u8eac\u5230\u5730\uff0c\u8ddf\u7740\u8dc3\u8d77\u8eab\u6765\uff0c\u62d4\u4e0b\u6881\u4e0a\u53cc\u5251\uff0c\u6a2a\u6258\u5728\u624b\uff0c\u8fd8\u7ed9\u4e86\u4e8c\u4eba\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u8dc3\u8d77\u53d6\u5251\u7684\u8f7b\u529f\uff0c\u53c8\u90fd\u559d\u91c7\uff0c\u5747\u60f3\uff1a\u8fd9\u9ed1\u8138\u5c11\u5e74\u6b66\u529f\u5947\u9ad8\uff0c\u53c8\u8c26\u900a\u77e5\u793c\uff0c\u7ed9\u4eba\u8138\u9762\uff0c\u53ea\u662f\u81ea\u8c26\u529f\u592b\u4e0d\u5982\u4eba\u5bb6\uff0c\u5374\u662f\u8c01\u4e5f\u4e0d\u4fe1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53c8\u9053\uff1a\u201c\u4e24\u4f4d\u5e76\u4e0d\u662f\u8d25\u5728\u6211\u624b\u91cc\u3002\u800c\u662f\u8d25\u5728\u91d1\u86c7\u5927\u4fa0\u624b\u91cc\u3002\u4ed6\u6599\u5230\u4e86\u4e24\u4f4d\u7684\u62db\u672f\uff0c\u5429\u5490\u665a\u8f88\u6545\u610f\u8f7b\u72c2\uff0c\u88c5\u6a21\u4f5c\u6837\uff0c\u6fc0\u52a8\u4e24\u4f4d\u6012\u6c14\uff0c\u4ee5\u4fbf\u4e58\u673a\u53d6\u80dc\u3002\u665a\u8f88\u5bf9\u4e24\u4f4d\u4e0d\u656c\uff0c\u5b9e\u975e\u80c6\u6562\u6709\u610f\u4fae\u8fb1\uff0c\u4e43\u662f\u6fc0\u5c06\u4e4b\u8ba1\uff0c\u597d\u4f7f\u4e24\u4f4d\u5341\u6210\u4e2d\u7684\u529f\u592b\uff0c\u53ea\u4f7f\u5f97\u51fa\u4e00\u6210\u3002\u91d1\u86c7\u5927\u4fa0\u662f\u5f53\u4e16\u9ad8\u4eba\uff0c\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\u3002\u665a\u8f88\u4e5f\u4e0d\u80fd\u8bf4\u771f\u662f\u4ed6\u7684\u4f20\u4eba\uff0c\u53ea\u4e0d\u8fc7\u5076\u7136\u76f8\u9022\uff0c\u5949\u547d\u524d\u6765\u89e3\u56f4\u8bf4\u548c\u800c\u5df2\u3002\u4e24\u4f4d\u8d25\u5728\u4ed6\u624b\u91cc\uff0c\u53c8\u4f55\u8db3\u4e3a\u803b\uff1f\u665a\u8f88\u8981\u8bf4\u53e5\u4e0d\u4e2d\u542c\u7684\u8bdd\uff0c\u522b\u8bf4\u662f\u4e24\u4f4d\uff0c\u5c31\u662f\u5c0a\u5e08\u9ec4\u6728\u9053\u957f\uff0c\u5f53\u5e74\u5bf9\u91d1\u86c7\u5927\u4fa0\u4e5f\u662f\u5f88\u4f69\u670d\u7684\u3002\u201d \u3000\u3000\u6d1e\u7384\u4e0e\u95f5\u5b50\u534e\u5bf9\u8fd9\u756a\u8bdd\u867d\u7136\u5c06\u4fe1\u5c06\u7591\uff0c\u4f46\u4e5f\u5df2\u5927\u4e3a\u5fc3\u5e73\u6c14\u548c\u3002\u6d1e\u7384\u8bf4\u9053\uff1a\u201c\u65bd\u4e3b\u4e3a\u6211\u4eec\u5144\u5f1f\u5706\u8138\uff0c\u8d2b\u9053\u591a\u8c22\u4e86\uff0c\u8bf7\u6559\u65bd\u4e3b\u9ad8\u59d3\u5927\u540d\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u518d\u4e0d\u8bf4\u81ea\u5df1\u771f\u59d3\uff0c\u5bf9\u65b9\u5fc5\u9053\u6211\u77a7\u4ed6\u4eec\u4e0d\u8d77\u3002\u201d\u4e8e\u662f\u5411\u9752\u9752\u4e00\u6307\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u91d1\u86c7\u5927\u4fa0\u7684\u5ae1\u55e3\uff0c\u59d3\u590f\u3002\u665a\u8f88\u59d3\u8881\u3002\u201d\u8bb8\u591a\u4eba\u90fd\u4e0d\u77e5\u91d1\u86c7\u90ce\u541b\u7684\u59d3\u540d\uff0c\u8fd9\u65f6\u624d\u77e5\u4ed6\u59d3\u590f\u3002 \u3000\u3000\u95f5\u5b50\u534e\u5411\u7126\u516c\u793c\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u591a\u591a\u5435\u6270\uff0c\u544a\u8f9e\u4e86\u3002\u201d\u7126\u516c\u793c\u9053\uff1a\u201c\u660e\u65e5\u5144\u5f1f\u518d\u5230\u5e9c\u4e0a\u8d1f\u8346\u8bf7\u7f6a\u3002\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u6b63\u8981\u8d70\u51fa\uff0c\u9752\u9752\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u534a\u622a\u5251\u7684\u8d4c\u8d5b\u53c8\u600e\u4e48\u4e86\uff1f\u201d\u7126\u5b9b\u513f\u89c1\u7236\u4eb2\u8131\u5374\u5927\u96be\uff0c\u5fc3\u4e0b\u5df2\u7136\u559c\u4e0d\u81ea\u80dc\uff0c\u54ea\u613f\u518d\u591a\u751f\u4e8b\u7aef\uff0c\u5fd9\u9053\uff1a\u201c\u590f\u7237\uff0c\u8bf7\u5230\u5185\u5802\u5949\u8336\uff0c\u8fd9\u4e9b\u4e8b\u4e0d\u5fc5\u63d0\u4e86\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u5c0f\u5b50\u8fd8\u6ca1\u53eb\u6211\u4eb2\u7237\u7237\u54ea\uff0c\u8fd9\u53ef\u4e0d\u6210\u3002\u201d\u5979\u8d62\u5f97\u9b4f\u56fd\u516c\u8d50\u7b2c\uff0c\u672c\u5df2\u5fc3\u6ee1\u610f\u8db3\uff0c\u4f46\u521a\u624d\u6885\u5251\u548c\u8bf4\u7126\u5b9b\u513f\u5bf9\u8881\u627f\u5fd7\u4e00\u5f80\u60c5\u6df1\uff0c\u8fd9\u53e5\u8bdd\u5979\u5374\u803f\u803f\u4e8e\u6000\uff0c\u4e0d\u80af\u7f62\u4f11\u3002 \u3000\u3000\u6885\u5251\u548c\u672c\u6765\u89c1\u8881\u627f\u5fd7\u6b66\u529f\u9ad8\u5f3a\uff0c\u8eab\u6cd5\u602a\u5f02\uff0c\u867d\u4e0d\u6b32\u5411\u4ed6\u751f\u4e8b\uff0c\u4f46\u9752\u9752\u4e00\u518d\u53eb\u9635\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u6307\u7740\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u662f\u751a\u4e48\u4eba\uff1f\u4f60\u53cc\u5251\u63d2\u6881\uff0c\u8fd9\u4e00\u62db\u2018\u5929\u5916\u98de\u9f99\u2019\uff0c\u662f\u4ece\u54ea\u91cc\u5077\u5b66\u6765\u7684\uff1f\u5feb\u8bf4\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5077\u5b66\uff1f\u6211\u5e72\u4e48\u8981\u5077\u5b66\uff1f\u201d\u5b59\u4ef2\u541b\u9a82\u9053\uff1a\u201c\u5478\uff0c\u5c0f\u8d3c\uff0c\u5077\u5b66\u4e86\u8fd8\u60f3\u8d56\u3002\u201d\u6885\u5251\u548c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u4ece\u54ea\u91cc\u5b66\u6765\u7684\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u662f\u534e\u5c71\u6d3e\u95e8\u4e0b\u3002\u201d \u3000\u3000\u5b59\u4ef2\u541b\u8de8\u4e0a\u4e00\u6b65\uff0c\u621f\u6307\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u63ae\u7740\u751a\u4e48\u91d1\u86c7\u94f6\u86c7\u7684\u62db\u724c\u62db\u6447\uff0c\u65c1\u4eba\u4e0d\u77e5\u4f60\u6765\u5386\uff0c\u53ea\u597d\u7531\u5f97\u4f60\u80e1\u8bf4\u516b\u9053\u3002\u597d\u5440\uff0c\u73b0\u4e0b\u53c8\u5439\u8d77\u534e\u5c71\u6d3e\u6765\u5566\uff01\u4f60\u53ef\u77e5\u4f60\u59d1\u5976\u5976\u662f\u751a\u4e48\u95e8\u6237\uff0c\u563f\u563f\uff0c\u5047\u674e\u9b3c\u9047\u4e0a\u771f\u674e\u9035\u5566\u3002\u8001\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u6211\u4eec\u4e09\u4eba\u6b63\u662f\u534e\u5c71\u6d3e\u7684\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u65e9\u8bf4\u8fc7\uff0c\u6211\u8ddf\u91d1\u86c7\u90ce\u541b\u6ca1\u751a\u4e48\u5e72\u7cfb\uff0c\u53ea\u4e0d\u8fc7\u662f\u4ed6\u8fd9\u4f4d\u8d24\u90ce\u7684\u670b\u53cb\u3002\u81f3\u4e8e\u4f60\u4eec\u4e09\u4f4d\uff0c\u6211\u65e9\u77e5\u662f\u534e\u5c71\u6d3e\u7684\uff0c\u54b1\u4eec\u6b63\u662f\u4e00\u5bb6\u4eba\u3002\u201d \u3000\u3000\u4e09\u4eba\u4e2d\u5218\u57f9\u751f\u8f83\u4e3a\u6301\u91cd\uff0c\u8bf4\u9053\uff1a\u201c\u9ec4\u5e08\u4f2f\u7684\u95e8\u4eba\u6211\u5168\u8ba4\u5f97\uff0c\u53ef\u6ca1\u4f60\u8001\u54e5\u5728\u5185\u3002\u5b59\u5e08\u59b9\uff0c\u4f60\u53ef\u542c\u8bf4\u9ec4\u5e08\u4f2f\u65b0\u8fd1\u6536\u4e86\u751a\u4e48\u5f92\u5f1f\u5417\uff1f\u201d\u5b59\u4ef2\u541b\u9053\uff1a\u201c\u9ec4\u5e08\u4f2f\u773c\u754c\u4f55\u7b49\u9ad8\uff0c\u600e\u4f1a\u6536\u8fd9\u7b49\u62db\u6447\u649e\u9a97\u4e4b\u5f92\uff1f\u201d\u5979\u56e0\u8881\u627f\u5fd7\u6298\u65ad\u4e86\u5979\u957f\u5251\uff0c\u607c\u6012\u5f02\u5e38\uff0c\u51fa\u8a00\u8d8a\u6765\u8d8a\u662f\u96be\u542c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u52a8\u58f0\u8272\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u94dc\u7b14\u94c1\u7b97\u76d8\u9ec4\u5e08\u54e5\u7684\u773c\u754c\u7684\u786e\u5f88\u9ad8\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u4ed6\u79f0\u9ec4\u771f\u4e3a\u201c\u9ec4\u5e08\u54e5\u201d\uff0c\u90fd\u5403\u4e86\u4e00\u60ca\u3002\u5218\u57f9\u751f\u9053\uff1a\u201c\u4f60\u53eb\u8c01\u9ec4\u5e08\u54e5\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5e08\u7236\u59d3\u7a46\uff0c\u540d\u8bb3\u4e0a\u2018\u4eba\u2019\u4e0b\u2018\u6e05\u2019\uff0c\u6c5f\u6e56\u4e0a\u5c0a\u79f0\u4ed6\u8001\u4eba\u5bb6\u4e3a\u2018\u795e\u5251\u4ed9\u733f\u2019\u3002\u94dc\u7b14\u94c1\u7b97\u76d8\u662f\u6211\u5927\u5e08\u5144\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u542c\u8881\u627f\u5fd7\u81ea\u79f0\u662f\u534e\u5c71\u6d3e\u95e8\u4eba\uff0c\u672c\u6709\u70b9\u5c06\u4fe1\u5c06\u7591\uff0c\u4ee5\u4e3a\u4ed6\u6216\u8bb8\u662f\u5e26\u827a\u6295\u5e08\uff0c\u65b0\u8fd1\u62dc\u5728\u9ec4\u771f\u95e8\u4e0b\uff0c\u8fd9\u65f6\u542c\u4ed6\u8bf4\u7adf\u662f\u5e08\u7956\u7684\u5f92\u5f1f\uff0c\u90a3\u663e\u7136\u662f\u4fe1\u53e3\u80e1\u5439\uff0c\u5fc3\u60f3\u5e08\u7956\u7d20\u6765\u884c\u8e2a\u98d8\u5ffd\uff0c\u81ea\u5df1\u4e5f\u53ea\u89c1\u8fc7\u4ed6\u4e09\u9762\uff0c\u5e08\u7236\u795e\u62f3\u65e0\u654c\u5f52\u8f9b\u6811\u5df2\u8fd1\u4e94\u5341\u5c81\u4e86\uff0c\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c45\u7136\u6765\u5192\u5145\u81ea\u5df1\u5e08\u53d4\uff0c\u771f\u662f\u5927\u80c6\u72c2\u5984\u4e4b\u81f3\uff0c\u5f53\u4e0b\u51b7\u51b7\u7684\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u9601\u4e0b\u662f\u6211\u5e08\u53d4\u4e86\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u53ef\u4e5f\u771f\u4e0d\u6562\u8ba4\u4e09\u4f4d\u505a\u5e08\u4f84\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u542c\u4ed6\u8a00\u4e2d\u610f\u5b58\u5632\u8bbd\uff0c\u8bf4\u9053\uff1a\u201c\u83ab\u975e\u6211\u8fb1\u6ca1\u4e86\u534e\u5c71\u6d3e\u7684\u95e8\u6963\u5417\uff1f\u5e08\u53d4\u5927\u4eba\uff0c\u54c8\u54c8\uff0c\u4f60\u6559\u8bad\u6559\u8bad\u6211\u4eec\u4e09\u4e2a\u53ef\u601c\u7684\u5c0f\u5e08\u4f84\u5427\uff01\u201d\u6885\u5251\u548c\u5e74\u7eaa\u5df2\u6709\u4e09\u5341\u516d\u4e03\uff0c\u8fd9\u4e48\u4e00\u8bf4\uff0c\u95f5\u65b9\u6b66\u5e08\u90fd\u8f70\u7136\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6b63\u8272\u9053\uff1a\u201c\u5f52\u5e08\u54e5\u8981\u662f\u5728\u8fd9\u91cc\uff0c\u81ea\u4f1a\u6559\u8bad\u4f60\u4eec\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u52c3\u7136\u800c\u8d77\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u957f\u5251\u51fa\u9798\uff0c\u9a82\u9053\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u4f60\u8fd8\u5728\u80e1\u8bf4\u516b\u9053\uff1f\u201d \u3000\u3000\u7126\u516c\u793c\u89c1\u4e8b\u60c5\u672c\u5df2\u5e73\u606f\uff0c\u8fd9\u65f6\u4e3a\u4e86\u4e9b\u679d\u8282\u5c0f\u4e8b\uff0c\u53c8\u8d77\u4e89\u7aef\uff0c\u5f88\u662f\u7126\u6025\uff0c\u5fd9\u9053\uff1a\u201c\u8fd9\u4f4d\u8881\u7237\u5f00\u5f00\u73a9\u7b11\uff0c\u6885\u7237\u4e0d\u5fc5\u52a8\u6012\u3002 \u3000\u3000\u6765\u6765\u6765\uff0c\u54b1\u4eec\u5927\u5bb6\u6765\u559d\u4e00\u676f\u548c\u6c14\u9152\u3002\u201d\u8a00\u4e0b\u663e\u7136\u4e0d\u4fe1\u8881\u627f\u5fd7\u662f\u6885\u5251\u548c\u7684\u5e08\u53d4\u3002 \u3000\u3000\u6885\u5251\u548c\u6717\u58f0\u9053\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u4f60\u4fbf\u662f\u78d5\u5934\u53eb\u6211\u4e09\u58f0\u5e08\u53d4\uff0c\u6211\u6ca1\u5f71\u5b50\u8fd8\u4e0d\u5c51\u7b54\u5e94\u5462\u3002\u201d\u8fd9\u8fb9\u9752\u9752\u5374\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u5582\uff0c\u6ca1\u5f71\u5b50\uff0c\u4f60\u5148\u53eb\u6211\u4e00\u58f0\u4eb2\u7237\u7237\u5427\u3002\u8d4c\u8f93\u4e86\u60f3\u8d56\u8d26\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u8f6c\u5934\u5411\u9752\u9752\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u522b\u80e1\u95f9\u3002\u201d\u53c8\u5bf9\u6885\u5251\u548c\u9053\uff1a\u201c\u5f52\u5e08\u54e5\u6211\u8fd8\u6ca1\u62dc\u89c1\u8fc7\uff0c\u4f60\u4eec\u4e09\u4f4d\u53c8\u6bd4\u6211\u5e74\u957f\uff0c\u6309\u7406\u6211\u7684\u786e\u4e0d\u914d\u505a\u5e08\u53d4\u3002\u4e0d\u8fc7\u4f60\u4eec\u4e09\u4f4d\u8fd9\u6b21\u884c\u4e8b\uff0c\u5374\u5b9e\u5728\u662f\u592a\u4e0d\u8be5\u4e86\u3002\u5f52\u5e08\u54e5\u77e5\u9053\u4e86\uff0c\u53ea\u6015\u8981\u5927\u5927\u751f\u6c14\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u53cc\u7709\u76f4\u7ad6\uff0c\u4ef0\u5929\u5927\u7b11\uff0c\u5fc3\u4e2d\u6124\u6012\u5df2\u6781\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u771f\u6559\u8bad\u8d77\u4eba\u6765\u5566\u3002\u5012\u8981\u8bf7\u6559\uff0c\u6211\u4eec\u4e09\u4eba\u751a\u4e48\u5730\u65b9\u9519\u4e86\uff1f\u670b\u53cb\u6709\u4e8b\uff0c\u96be\u9053\u4e0d\u8be5\u62d4\u5200\u76f8\u52a9\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u68ee\u7136\u9053\uff1a\u201c\u54b1\u4eec\u534e\u5c71\u6d3e\u98ce\u7956\u5e08\u7237\u4f20\u4e0b\u5341\u4e8c\u5927\u6212\uff0c\u95e8\u4eba\u5f1f\u5b50\uff0c\u52a1\u5f53\u51db\u9075\u3002\u7b2c\u4e09\u6761\u3001\u7b2c\u4e94\u6761\u3001\u7b2c\u516d\u6761\u3001\u7b2c\u5341\u4e00\u6761\u662f\u751a\u4e48\uff1f\u201d \u3000\u3000\u6885\u5251\u548c\u4e00\u6014\uff0c\u8fd8\u672a\u56de\u7b54\u3002\u5b59\u4ef2\u541b\u63d0\u8d77\u534a\u622a\u65ad\u5251\uff0c\u731b\u5411\u8881\u627f\u5fd7\u9762\u95e8\u63b7\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f7f\u4f7f\u4f60\u7684\u534e\u5c71\u6d3e\u529f\u592b\u5427\uff01\u201d\u9752\u5149\u95ea\u70c1\uff0c\u6025\u98de\u800c\u524d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5f85\u65ad\u5251\u98de\u5230\u4e34\u8fd1\uff0c\u5de6\u638c\u5e73\u4f38\u5411\u4e0a\uff0c\u53f3\u638c\u5411\u4e0b\u4e00\u62cd\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u628a\u65ad\u5251\u5408\u5728\u53cc\u638c\u4e4b\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53eb\u505a\u2018\u6a2a\u62dc\u89c2\u97f3\u2019\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6885\u5251\u548c\u4e0e\u5218\u57f9\u751f\u53c8\u90fd\u4e00\u6014\uff0c\u5fc3\u4e0b\u5600\u5495\uff1a\u201c\u8fd9\u786e\u662f\u672c\u95e8\u638c\u6cd5\uff0c\u4e0d\u8fc7\u8fd9\u4e00\u62db\u662f\u7528\u6765\u62cd\u51fb\u654c\u4eba\u624b\u638c\u7684\u3002\u4ed6\u53d8\u5316\u63a5\u5251\uff0c\u624b\u6cd5\u5de7\u5999\u4e4b\u6781\uff0c\u5e08\u7236\u53ef\u6ca1\u6559\u8fc7\u6211\u4eec\u3002\u201d \u3000\u3000\u5218\u57f9\u751f\u62a2\u4e0a\u4e00\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u521a\u624d\u6240\u4f7f\uff0c\u6b63\u662f\u672c\u95e8\u638c\u6cd5\uff0c\u5728\u4e0b\u8981\u60f3\u8bf7\u6559\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5218\u5927\u54e5\uff0c\u4f60\u5916\u53f7\u4e94\u4e01\u624b\uff0c\u4e94\u4e01\u5f00\u5c71\uff0c\u60f3\u5fc5\u62f3\u529b\u638c\u529b\u751a\u662f\u4e86\u5f97\u3002\u672c\u95e8\u7684\u4f0f\u864e\u638c\u6cd5\u4e0e\u5288\u77f3\u3001\u7834\u7389\u4e24\u8def\u62f3\u6cd5\uff0c\u5b9a\u662f\u5f88\u6709\u5fc3\u5f97\u7684\u4e86\u3002\u201d\u5218\u57f9\u751f\u89c1\u4e86\u8881\u627f\u5fd7\u521a\u624d\u8fd9\u4e00\u62db\uff0c\u5df2\u7136\u5341\u5206\u4f69\u670d\uff0c\u4fbf\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u8fc7\u5b66\u4e86\u5e08\u95e8\u6240\u6388\u7684\u4e00\u70b9\u76ae\u6bdb\uff0c\u4e5f\u8c08\u4e0d\u4e0a\u751a\u4e48\u5fc3\u5f97\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5218\u5927\u54e5\u4e0d\u5fc5\u8fc7\u8c26\u3002\u4f60\u8ddf\u5c0a\u5e08\u5582\u62db\uff0c\u4ed6\u8981\u662f\u4f7f\u51fa\u771f\u529f\u592b\u6765\uff0c\u6bd4\u5982\u8bf4\u4f7f\u4e86\u62b1\u5143\u52b2\u6216\u8005\u6df7\u5929\u529f\uff0c\u5218\u5927\u54e5\u53ef\u4ee5\u63a5\u5f97\u51e0\u62db\uff1f\u201d\u5218\u57f9\u751f\u9053\uff1a\u201c\u6211\u5e08\u7236\u5185\u529b\u6df1\u539a\uff0c\u8ddf\u95e8\u4eba\u8fc7\u62db\uff0c\u4ece\u6765\u4e0d\u771f\u4f7f\u5185\u52b2\uff0c\u5426\u5219\u6211\u4eec\u4e00\u62db\u4e5f\u6321\u4e0d\u4f4f\u3002\u82e5\u662f\u53ea\u62c6\u62f3\u6cd5\uff0c\u90a3\u4e48\u5934\u4e0a\u5341\u62db\uff0c\u52c9\u5f3a\u8fd8\u53ef\u5bf9\u4ed8\u3002\u5341\u62db\u4ee5\u540e\uff0c\u5c31\u5403\u529b\u5f97\u5f88\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5c0a\u5e08\u5916\u53f7\u2018\u795e\u62f3\u65e0\u654c\u2019\uff0c\u62f3\u6cd5\u5b9a\u7136\u7cbe\u5999\u4e4b\u6781\u3002\u5218\u5927\u54e5\u80fd\u63a5\u5230\u5341\u62db\u4ee5\u5916\uff0c\u5728\u6c5f\u6e56\u4e0a\u81ea\u5df2\u5c11\u89c1\uff0c\u2018\u4e94\u4e01\u624b\u2019\u4e09\u5b57\uff0c\u81ea\u53ef\u5f53\u4e4b\u65e0\u6127\u3002\u201d\u5218\u57f9\u751f\u9053\uff1a\u201c\u8fd9\u662f\u522b\u4eba\u5f00\u73a9\u7b11\u8bf4\u7684\uff0c\u6211\u529f\u592b\u8fd8\u5dee\u5f97\u5f88\u8fdc\uff0c\u5b9e\u5728\u6127\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u5b59\u4ef2\u541b\u542c\u4ed6\u8bed\u6c14\uff0c\u5bf9\u8fd9\u5c11\u5e74\u7adf\u7136\u8d8a\u6765\u8d8a\u606d\u656c\uff0c\u9887\u6709\u8ba4\u4ed6\u4e3a\u5e08\u53d4\u4e4b\u610f\uff0c\u6012\u9053\uff1a\u201c\u5218\u5e08\u54e5\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u51ed\u4eba\u5bb6\u80e1\u5439\u51e0\u53e5\uff0c\u5c31\u628a\u4f60\u5413\u5012\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u53bb\u7406\u5979\uff0c\u95ee\u5218\u57f9\u751f\u9053\uff1a\u201c\u8981\u600e\u6837\uff0c\u4f60\u624d\u4fe1\u6211\u662f\u5e08\u53d4\uff1f\u201d\u5218\u57f9\u751f\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u4f60\u8ddf\u6211\u8fc7\u8fc7\u62db\uff0c\u9601\u4e0b\u7684\u672c\u95e8\u62f3\u6cd5\u5982\u786e\u6bd4\u6211\u597d\u2026\u2026\u201d\u8881\u627f\u5fd7\u89c1\u8fc7\u6885\u5251\u548c\u4e0e\u5b59\u4ef2\u541b\u4e8c\u4eba\u51fa\u624b\uff0c\u6599\u60f3\u5218\u57f9\u751f\u7684\u6b66\u529f\u4e0e\u4ed6\u4eec\u76f8\u5dee\u4e0d\u8fdc\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u5e08\u7236\u82e5\u662f\u5f53\u771f\u4f7f\u51fa\u5185\u52b2\uff0c\u4f60\u53ea\u6015\u4e00\u62db\u4e5f\u63a5\u4e0d\u4f4f\u3002\u6211\u7684\u529f\u592b\u6bd4\u4e4b\u5c0a\u5e08\u81ea\u7136\u5927\u5927\u4e0d\u5982\u3002\u4ed6\u4f7f\u4e00\u62db\uff0c\u6211\u5f97\u4f7f\u4e94\u62db\u3002\u4f60\u53ea\u8981\u63a5\u5f97\u4f4f\u6211\u4e94\u62db\uff0c\u90a3\u6211\u5c31\u662f\u5047\u5192\u7684\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u6885\u5251\u548c\u672c\u6765\u62c5\u5fc3\u5e08\u5f1f\u672a\u5fc5\u80fd\u591f\u80dc\u4ed6\uff0c\u4f46\u542c\u4ed6\u7adf\u8bf4\u53ea\u7528\u4e94\u62db\uff0c\u5c31\u80fd\u628a\u540c\u95e8\u4e2d\u62f3\u6cd5\u7b2c\u4e00\u7684\u5218\u5e08\u5f1f\u6253\u5012\uff0c\u5fc3\u5934\u4e00\u5bbd\uff0c\u6599\u60f3\u5fc5\u662f\u4fe1\u53e3\u80e1\u5439\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u5c31\u8fd9\u6837\uff0c\u6211\u6570\u7740\u3002\u201d \u3000\u3000\u5218\u57f9\u751f\u4f5c\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u529f\u592b\u4e0d\u5230\u4e4b\u5904\uff0c\u8bf7\u4f60\u624b\u4e0b\u7559\u60c5\u3002\u201d\u8881\u627f\u5fd7\u7f13\u7f13\u8d70\u8fd1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7b2c\u4e00\u62db\u662f\u2018\u77f3\u7834\u5929\u60ca\u2019\uff0c\u4f60\u63a5\u7740\u5427\uff01\u201d\u5218\u57f9\u751f\u9053\uff1a\u201c\u597d\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u52a8\u624b\u8fc7\u62db\uff0c\u54ea\u6709\u5148\u628a\u62db\u6570\u8bf4\u7ed9\u4eba\u542c\u7684\uff1f\u5176\u4e2d\u5b9a\u5f53\u6709\u8bc8\uff0c\u53eb\u6211\u7559\u5fc3\u4e0a\u76d8\uff0c\u5374\u51fa\u5176\u4e0d\u610f\u7684\u6765\u653b\u6211\u4e0b\u76d8\u3002\u201d\u4e8e\u662f\u53f3\u638c\u865a\u6321\u95e8\u9762\uff0c\u5de6\u638c\u6a2a\u5b88\u4e39\u7530\uff0c\u53ea\u5f85\u8881\u627f\u5fd7\u5411\u4e0b\u76d8\u653b\u5230\uff0c\u7acb\u5373\u6c89\u62f3\u4e0b\u51fb\uff0c\u53ea\u542c\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u7b2c\u4e00\u62db\u6765\u4e86\uff01\u201d\u5de6\u638c\u865a\u629a\uff0c\u53f3\u62f3\u55d6\u7684\u4e00\u58f0\uff0c\u4ece\u638c\u98ce\u4e2d\u731b\u7a7f\u51fa\u6765\uff0c\u679c\u7136\u4fbf\u662f\u534e\u5c71\u6d3e\u7684\u7edd\u62db\u4e4b\u4e00\u201c\u77f3\u7834\u5929\u60ca\u201d\u3002 \u3000\u3000\u5218\u57f9\u751f\u75be\u4f38\u53f3\u638c\u6321\u683c\uff0c\u8881\u627f\u5fd7\u4e00\u62f3\u5c06\u5230\u4ed6\u9762\u95e8\uff0c\u5ffd\u5730\u505c\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u600e\u4e0d\u4fe1\u6211\u7684\u8bdd\uff1f\u5355\u638c\u62e6\u4e0d\u4f4f\uff0c\u53cc\u624b\u540c\u65f6\u6765\u3002\u201d \u3000\u3000\u5218\u57f9\u751f\u89c1\u4ed6\u62f3\u52bf\uff0c\u5df2\u77e5\u53f3\u638c\u65e0\u6cd5\u963b\u6321\uff0c\u773c\u89c1\u8fd9\u4e00\u62f3\u4fbf\u8981\u6253\u7834\u81ea\u5df1\u9f3b\u5b50\uff0c\u6b63\u81ea\u7126\u6025\uff0c\u5e78\u5f97\u4ed6\u62f3\u52bf\u5ffd\u505c\uff0c\u5fd9\u63d0\u8d77\u5de6\u62f3\uff0c\u5c55\u6307\u53d8\u638c\uff0c\u53cc\u62f3\u201c\u94c1\u95e9\u6a2a\u95e8\u201d\uff0c\u53e3\u4e2d\u201c\u563f\u201d\u7684\u4e00\u58f0\uff0c\u8fd0\u52b2\u63a8\u4e86\u51fa\u53bb\u3002\u8881\u627f\u5fd7\u8fd9\u624d\u4e00\u62f3\u6253\u843d\uff0c\u548c\u4ed6\u53cc\u638c\u4e00\u62b5\u3002\u5218\u57f9\u751f\u53ea\u611f\u638c\u4e0a\u538b\u529b\u6c89\u91cd\u4e4b\u6781\uff0c\u53cc\u81c2\u683c\u683c\u6709\u58f0\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u8fd9\u62f3\u5728\u4e2d\u9014\u505c\u6b62\uff0c\u53c8\u518d\u8ddf\u7740\u51fb\u51fa\uff0c\u5e76\u975e\u6536\u62f3\u518d\u53d1\uff0c\u5982\u4f55\u80fd\u6709\u5982\u6b64\u52b2\u529b\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u6536\u62f3\u8bf4\u9053\uff1a\u201c\u4ee5\u540e\u4e09\u62db\u6211\u63a5\u8fde\u53d1\u51fa\uff0c\u90a3\u662f\u2018\u529b\u5288\u4e09\u5173\u2019\u3001\u2018\u629b\u7816\u5f15\u7389\u2019\u3001\u2018\u91d1\u521a\u63a3\u5c3e\u2019\u3002\u4f60\u5982\u4f55\u62b5\u6321\uff1f\u201d \u3000\u3000\u5218\u57f9\u751f\u6beb\u4e0d\u601d\u7d22\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7528\u2018\u5c01\u95ed\u624b\u2019\u3001\u2018\u767d\u4e91\u51fa\u5cab\u2019\u3001\u2018\u508d\u82b1\u62c2\u67f3\u2019\u63a5\u7740\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u524d\u4e24\u62db\u5bf9\u4e86\uff0c\u540e\u4e00\u62db\u4e0d\u5bf9\u3002\u8981\u77e5\u2018\u508d\u82b1\u62c2\u67f3\u2019\u5b88\u4e2d\u5e26\u653b\uff0c\u5982\u8ddf\u529f\u529b\u6089\u654c\u7684\u5bf9\u624b\u8fc7\u62db\uff0c\u90a3\u5f53\u7136\u6781\u597d\uff0c\u4f46\u8fd9\u4e00\u62db\u8981\u56de\u624b\u53cd\u51fb\uff0c\u5b88\u5fa1\u7684\u529b\u9053\u51cf\u4e86\u4e00\u534a\uff0c\u6211\u8fd9\u62db\u2018\u91d1\u521a\u63a3\u5c3e\u2019\u4f60\u5c31\u63a5\u4e0d\u4f4f\u4e86\u3002\u201d\u5218\u57f9\u751f\u9053\uff1a\u201c\u90a3\u4e48\u6211\u7528\u2018\u5343\u65a4\u5815\u5730\u2019\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u63a5\u7740\uff01\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u53f3\u638c\u4e00\u8d77\uff0c\u5218\u57f9\u751f\u5fd9\u6446\u597d\u52bf\u5b50\u76f8\u6321\uff0c\u54ea\u77e5\u4ed6\u53f3\u638c\u60ac\u5728\u534a\u7a7a\uff0c\u5de6\u638c\u5374\u500f\u5730\u5288\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6b66\u5b66\u4e4b\u9053\uff0c\u4e0d\u53ef\u62d8\u6ce5\u6210\u6cd5\uff0c\u5e08\u7236\u6559\u4f60\u2018\u529b\u5288\u4e09\u5173\u2019\u662f\u7528\u53f3\u638c\uff0c\u4f46\u968f\u673a\u5e94\u53d8\uff0c\u7528\u5de6\u638c\u4e5f\u65e0\u4e0d\u53ef\u3002\u201d\u53e3\u4e2d\u8bf4\u7740\uff0c\u62f3\u52bf\u4e0d\u505c\uff0c\u4e0d\u7b49\u5218\u57f9\u751f\u5c01\u95ed\uff0c\u5df2\u62a2\u4f4f\u4ed6\u624b\u8155\u5f80\u524d\u4e00\u62c9\u3002\u5218\u57f9\u751f\u7528\u201c\u767d\u4e91\u51fa\u5cab\u201d\u968f\u52bf\u4e00\u9001\uff0c\u62db\u6570\u4e2d\u6697\u85cf\u9634\u7740\uff0c\u5982\u5bf9\u65b9\u4e0d\u5bdf\uff0c\u80f8\u53e3\u7a74\u9053\u7acb\u88ab\u70b9\u4e2d\u3002\u4f46\u4ed6\u8fd9\u65f6\u4e0d\u6562\u53cd\u51fb\uff0c\u62db\u89e3\u5f00\uff0c\u7acb\u5373\u6536\u52bf\uff0c\u6c89\u6c14\u4e0b\u76d8\uff0c\u53cc\u817f\u72b9\u5982\u9489\u5728\u5730\u4e0a\u4e00\u822c\uff0c\u8fd9\u62db\u201c\u5343\u65a4\u5815\u5730\u201d\u679c\u5982\u6709\u5343\u65a4\u4e4b\u91cd\u3002\u8881\u627f\u5fd7\u201c\u91d1\u521a\u63a3\u5c3e\u201d\u4f7f\u51fa\uff0c\u5de6\u638c\u4f38\u5230\u4ed6\u7684\u540e\u5fc3\u8fd0\u529b\u4e00\u63a8\uff0c\u5218\u57f9\u751f\u8fd8\u662f\u7acb\u8db3\u4e0d\u5b9a\uff0c\u5411\u524d\u51b2\u51fa\u4e24\u6b65\uff0c\u6ef4\u6e9c\u6e9c\u6253\u4e2a\u65cb\u5b50\uff0c\u8f6c\u4e86\u8fc7\u6765\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u4e0d\u786c\u6297\u6211\u8fd9\u4e00\u62db\uff0c\u90a3\u597d\u5f97\u5f88\u3002\u5c0a\u5e08\u8c03\u6559\u7684\u5f1f\u5b50\uff0c\u5927\u662f\u4e0d\u51e1\u3002\u6211\u8fd9\u7b2c\u4e94\u62db\u662f\u7834\u7389\u62f3\u7684\u2018\u8d77\u624b\u5f0f\u2019\u3002\u201d\u5218\u57f9\u751f\u5f88\u662f\u5947\u602a\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u8d77\u624b\u5f0f\u53ea\u662f\u5ba2\u5957\u793c\u6570\uff0c\u4e34\u654c\u65f6\u65e0\u7528\u7684\u4e48\uff1f\u8981\u77e5\u54b1\u4eec\u7956\u5e08\u7237\u521b\u4e0b\u8fd9\u5957\u62f3\u6765\uff0c\u6ca1\u4e00\u62db\u4e0d\u80fd\u514b\u654c\u5236\u80dc\u3002\u4f60\u77a7\u7740\u3002\u201d\u8eab\u5b50\u5fae\u5fae\u4e00\u5f13\uff0c\u53f3\u62f3\u5de6\u638c\uff0c\u5408\u7740\u4e00\u63d6\uff0c\u8eab\u5b50\u968f\u7740\u8fd9\u4e00\u63d6\u4e4b\u52bf\uff0c\u5411\u524d\u75be\u63a2\uff0c\u8fde\u62f3\u8fde\u638c\uff0c\u6b63\u6253\u5728\u5218\u57f9\u751f\u5de6\u80ef\u4e4b\u4e0a\u3002\u4ed6\u518d\u4e5f\u7ad9\u7acb\u4e0d\u7a33\uff0c\u8eab\u5b50\u98de\u8d77\uff0c\u6454\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u8dc3\u800c\u81f3\uff0c\u53cc\u624b\u7a33\u7a33\u63a5\u4f4f\uff0c\u5c06\u4ed6\u653e\u5728\u5730\u4e0b\u3002 \u3000\u3000\u5218\u57f9\u751f\u6251\u7ffb\u5728\u5730\uff0c\u62dc\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u8bc6\u5e08\u53d4\uff0c\u521a\u624d\u65e0\u793c\u5192\u72af\u3002\u8bf7\u5e08\u53d4\u770b\u5728\u5bb6\u5e08\u9762\u4e0a\uff0c\u591a\u591a\u62c5\u5f85\u3002\u201d\u8881\u627f\u5fd7\u8fde\u5fd9\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5927\u54e5\u5e74\u7eaa\u6bd4\u6211\u957f\uff0c\u54b1\u4eec\u5144\u5f1f\u76f8\u79f0\u5427\u3002\u201d\u5218\u57f9\u751f\u9053\uff1a\u201c\u8fd9\u4e2a\u665a\u8f88\u5982\u4f55\u6562\u5f53\uff1f\u5e08\u53d4\u62f3\u6cd5\u795e\u5999\u83ab\u6d4b\uff0c\u9002\u624d\u8fd9\u4e94\u62db\u660e\u8bf4\u8fc7\u62db\uff0c\u5176\u5b9e\u662f\u4ee5\u672c\u95e8\u62f3\u6cd5\u4e2d\u7684\u7cbe\u4e49\u76f8\u6388\u3002\u665a\u8f88\u611f\u6fc0\u4e0d\u5c3d\uff0c\u56de\u53bb\u4e00\u5b9a\u7ec6\u5fc3\u4f53\u4f1a\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fae\u5fae\u4e00\u7b11\u3002\u5218\u57f9\u751f\u4ece\u8fd9\u4e94\u62db\u4e4b\u4e2d\u5b66\u5f97\u4e86\u968f\u673a\u5e94\u53d8\u7684\u8981\u65e8\uff0c\u65e5\u540e\u89e6\u7c7b\u65c1\u901a\uff0c\u62f3\u6cd5\u679c\u7136\u5927\u8fdb\uff0c\u7ec8\u8eab\u5bf9\u8881\u627f\u5fd7\u606d\u656c\u4e07\u5206\u3002\u8981\u77e5\u4ed6\u5e08\u7236\u5f52\u8f9b\u6811\u7684\u62f3\u6cd5\u51b3\u4e0d\u5728\u8881\u627f\u5fd7\u4e4b\u4e0b\uff0c\u4f46\u751f\u6027\u4e25\u5cfb\uff0c\u6388\u5f92\u65f6\u4e0d\u4f1a\u5faa\u5faa\u5584\u8bf1\uff0c\u5f92\u513f\u4e00\u89c1\u4ed6\u9762\u5fc3\u4e2d\u5c31\u5148\u5bb3\u6015\uff0c\u62c6\u62db\u65f6\u58a8\u5b88\u5e08\u4f20\u624b\u6cd5\uff0c\u4e0d\u6562\u6709\u4e1d\u6beb\u8d70\u6837\uff0c\u662f\u4ee5\u4e8e\u534e\u5c71\u6d3e\u6b66\u529f\u7684\u7cbe\u8981\u4e4b\u5904\u5f80\u5f80\u9886\u4f1a\u4e0d\u5230\u3002 \u3000\u3000\u6885\u5251\u548c\u4e0e\u5b59\u4ef2\u541b\u8fd9\u65f6\u54ea\u91cc\u518d\u6709\u6000\u7591\u3002\u53ea\u662f\u6885\u5251\u548c\u81ea\u6043\u5251\u6cd5\u6df1\u5f97\u672c\u95e8\u7cbe\u9ad3\uff0c\u5fc3\u60f3\u4f60\u62f3\u811a\u4e0a\u529f\u592b\u867d\u9ad8\uff0c\u5251\u672f\u672a\u5fc5\u80fd\u591f\u80dc\u6211\uff0c\u6b63\u81ea\u6c89\u541f\uff0c\u5b59\u4ef2\u541b\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u6885\u5e08\u54e5\uff0c\u4f60\u8bd5\u8bd5\u4ed6\u7684\u5251\u6cd5\uff01\u201d \u3000\u3000\u6885\u5251\u548c\u9053\uff1a\u201c\u597d\uff01\u201d\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u60f3\u5728\u5251\u4e0a\u5411\u9601\u4e0b\u9886\u6559\u51e0\u62db\u3002\u201d \u3000\u3000\u8bed\u6c14\u867d\u5df2\u8f83\u524d\u5927\u4e3a\u8c26\u900a\uff0c\u8138\u4e0a\u5374\u4ecd\u662f\u4e00\u80a1\u50b2\u6c14\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u5927\u6982\u6b64\u4eba\u5251\u6cd5\u786e\u5df2\u5f97\u5230\u672c\u95e8\u771f\u4f20\uff0c\u5728\u6c5f\u6e56\u4e4b\u4e0a\u672a\u9047\u5f3a\u654c\uff0c\u7ed9\u4eba\u5bb6\u4f60\u6367\u6211\u62cd\uff0c\u5949\u627f\u5f97\u9a84\u50b2\u5f02\u5e38\uff0c\u4ee5\u81f4\u884c\u4e3a\u72c2\u6096\u3002\u8fd9\u4eba\u4e0d\u6bd4\u5218\u57f9\u751f\uff0c\u987b\u5f97\u597d\u597d\u632b\u6298\u4ed6\u4e00\u4e0b\uff0c\u4ee5\u540e\u624d\u4e0d\u81f4\u4f7f\u5f97\u534e\u5c71\u6d3e\u95e8\u5362\u8d3b\u7f9e\u3002\u201d\u4fbf\u9053\uff1a\u201c\u6bd4\u5251\u662f\u53ef\u4ee5\u7684\uff0c\u4e0d\u8fc7\u51b3\u4e86\u80dc\u8d25\u4e4b\u540e\uff0c\u987b\u5f97\u542c\u6211\u51e0\u53e5\u9006\u8033\u4e4b\u8a00\u3002\u201d\u6885\u5251\u548c\u50b2\u7136\u9053\uff1a\u201c\u6b64\u523b\u80dc\u8d1f\u672a\u51b3\uff0c\u4f60\u8bf4\u8fd9\u8bdd\u672a\u514d\u592a\u65e9\u4e86\u4e9b\u3002\u201d\u5f53\u4e0b\u957f\u5251\u6a2a\u80f8\uff0c\u7ad9\u5728\u5de6\u9996\u3002\u5218\u57f9\u751f\u53eb\u9053\uff1a\u201c\u6885\u5e08\u54e5\uff0c\u4f60\u7ad9\u4e0b\u9996\u5427\u3002\u201d\u6885\u5251\u548c\u4e0d\u52a0\u7406\u776c\uff0c\u53ea\u5f53\u6ca1\u542c\u89c1\u3002\u539f\u6765\u5404\u95e8\u6d3e\u4e2d\u7684\u89c4\u77e9\uff0c\u665a\u8f88\u8ddf\u957f\u8f88\u8bd5\u5251\u5b66\u6b66\uff0c\u5fc5\u987b\u7ad9\u5728\u4e0b\u9996\uff0c\u8868\u793a\u5e76\u975e\u6562\u4e0e\u5bf9\u654c\uff0c\u4e0d\u8fc7\u662f\u5b66\u4e60\u827a\u4e1a\u3001\u5411\u5c0a\u957f\u8ba8\u6559\u4e4b\u610f\u3002\u6885\u5251\u548c\u7ad9\u5728\u5de6\u9996\uff0c\u90a3\u662f\u5e73\u8f88\u76f8\u5f85\uff0c\u4e0d\u8ba4\u4ed6\u662f\u5e08\u53d4\u3002 \u3000\u3000\u4ed6\u5de6\u638c\u62b1\u4f4f\u5251\u67c4\uff0c\u62f1\u624b\u9053\uff1a\u201c\u9601\u4e0b\u7528\u5251\u5427\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5ff5\u5934\u4e00\u8f6c\uff0c\u5bf9\u7126\u516c\u793c\u9053\uff1a\u201c\u7126\u8001\u4f2f\uff0c\u8bf7\u4f60\u53eb\u4eba\u53d6\u5341\u67c4\u5251\u6765\u3002\u201d\u7126\u516c\u793c\u5fd9\u9053\uff1a\u201c\u8881\u76f8\u516c\u5feb\u522b\u8fd9\u6837\u79f0\u547c\uff0c\u6211\u4e07\u4e07\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u624b\u4e00\u6325\uff0c\u65e9\u6709\u7126\u516c\u793c\u7684\u51e0\u4e2a\u95e8\u5f92\u6367\u4e86\u5341\u67c4\u957f\u5251\u51fa\u6765\u3002\u4ed6\u4eec\u89c1\u8881\u627f\u5fd7\u4e3a\u5e08\u95e8\u51fa\u529b\uff0c\u81ea\u7136\u9009\u4e86\u6700\u597d\u7684\u5229\u5668\uff0c\u5341\u67c4\u5251\u4e00\u5217\u6392\u5728\u684c\u4e0a\u3002\u70db\u5149\u7167\u8000\u4e0b\u3002\u5341\u5251\u5149\u8292\u4e92\u6fc0\uff0c\u95ea\u70c1\u4e0d\u5b9a\u3002\u4f17\u4eba\u76ee\u5149\u5728\u5341\u67c4\u5229\u5251\u4e0e\u8881\u627f\u5fd7\u4e4b\u95f4\u6765\u56de\uff0c\u77a7\u4ed6\u9009\u7528\u54ea\u4e00\u67c4\u3002 \u3000\u3000\u54ea\u77e5\u8881\u627f\u5fd7\u6361\u8d77\u5b59\u4ef2\u541b\u521a\u624d\u63b7\u6765\u7684\u534a\u622a\u65ad\u5251\uff0c\u7b11\u9053\uff1a\u201c\u6211\u7528\u8fd9\u65ad\u5251\u5427\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u60ca\u8bb6\uff0c\u5fc3\u60f3\u8fd9\u5251\u6ca1\u6709\u5251\u67c4\uff0c\u5982\u4f55\u4f7f\u6cd5\uff1f\u53ea\u89c1\u4ed6\u5c06\u534a\u622a\u5251\u5939\u5728\u53f3\u624b\u62c7\u6307\u4e0e\u98df\u6307\u4e4b\u95f4\uff0c\u8bf4\u9053\uff1a\u201c\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u6885\u5251\u548c\u5927\u6012\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u5bf9\u6211\u5982\u6b64\u8f7b\u89c6\uff0c\u6b7b\u4e86\u53ef\u6028\u4e0d\u5f97\u6211\u3002 \u3000\u3000\u7ba1\u4f60\u662f\u771f\u5e08\u53d4\uff0c\u5047\u5e08\u53d4\uff0c\u5982\u6b64\u72c2\u5984\u81ea\u5927\uff0c\u4fbf\u662f\u8be5\u6b7b\uff01\u201d\u81c2\u8fd0\u5185\u52b2\uff0c\u5251\u8eab\u632f\u8361\uff0c\u53ea\u89c1\u5bd2\u5149\u95ea\u95ea\uff0c\u63a5\u7740\u662f\u4e00\u9635\u55e1\u55e1\u4e4b\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u770b\u62db\uff01\u201d\u5251\u8d70\u504f\u950b\uff0c\u5411\u8881\u627f\u5fd7\u53f3\u8155\u523a\u6765\uff0c\u5fc3\u60f3\u4f60\u5982\u6b64\u6301\u5251\uff0c\u53f3\u624b\u4e00\u5b9a\u8f6c\u52a8\u4e0d\u7075\uff0c\u6211\u5bf9\u51c6\u4f60\u8fd9\u5f31\u70b9\u653b\u51fb\uff0c\u77a7\u4f60\u600e\u751f\u5e94\u4ed8\u3002\u5385\u4e0a\u6570\u767e\u9053\u76ee\u5149\u4e00\u9f50\u968f\u7740\u4ed6\u5251\u5c16\u5149\u8292\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5251\u5c16\u5c06\u8981\u523a\u5230\uff0c\u8881\u627f\u5fd7\u624b\u8155\u5fae\u4fa7\uff0c\u534a\u622a\u65ad\u5251\u5df2\u7136\u4f38\u51fa\u3002\u53cc\u5251\u76f8\u4ea4\uff0c\u53ea\u542c\u5580\u5587\u4e00\u58f0\uff0c\u63a5\u7740\u5f53\u5577\u4e00\u54cd\uff0c\u6885\u5251\u548c\u624b\u4e2d\u957f\u5251\u9f50\u67c4\u6298\u65ad\uff0c\u5251\u5203\u843d\u5730\uff0c\u624b\u4e2d\u53ea\u5269\u4e86\u4e2a\u5251\u67c4\u3002 \u3000\u3000\u4f17\u4eba\u5f02\u53e3\u540c\u58f0\uff0c\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5411\u684c\u4e0a\u4e00\u6307\u9053\uff1a\u201c\u7ed9\u4f60\u9884\u5907\u7740\u5341\u67c4\u5251\u3002\u6362\u5251\u5427\uff01\u201d\u4f17\u4eba\u624d\u77e5\u4ed6\u8981\u5341\u67c4\u5251\uff0c\u539f\u6765\u662f\u9884\u5148\u7ed9\u5bf9\u65b9\u5907\u4e0b\u7684\u3002 \u3000\u3000\u6885\u5251\u548c\u53c8\u60ca\u53c8\u6012\uff0c\u62a2\u4e86\u684c\u4e0a\u4e00\u5251\uff0c\u5411\u4ed6\u4e0b\u76d8\u523a\u53bb\u3002\u8881\u627f\u5fd7\u77e5\u662f\u865a\u62db\uff0c\u5e76\u4e0d\u62db\u67b6\uff0c\u679c\u7136\u4ed6\u4e00\u5251\u523a\u51fa\uff0c\u7acb\u5373\u56de\u62db\uff0c\u6539\u523a\u5c0f\u8179\u3002\u8881\u627f\u5fd7\u4f38\u65ad\u5251\u4e00\u6321\uff0c\u5580\u5587\u4e00\u58f0\uff0c\u6885\u5251\u548c\u624b\u4e2d\u957f\u5251\u53c8\u88ab\u9707\u4e3a\u4e24\u622a\u3002\u6885\u5251\u548c\u8ddf\u7740\u8fde\u6362\u4e09\u5251\uff0c\u4e09\u5251\u5747\u88ab\u534a\u622a\u65ad\u5251\u9707\u6298\uff0c\u4e0d\u7531\u5f97\u5446\u5728\u5f53\u5730\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u53eb\u9053\uff1a\u201c\u8bf4\u662f\u6bd4\u5251\uff0c\u600e\u4e48\u5374\u4f7f\u5996\u6cd5\uff0c\u8fd9\u8fd8\u6bd4\u751a\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u629b\u53bb\u65ad\u5251\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u684c\u4e0a\u62ff\u8d77\u4e24\u67c4\u957f\u5251\uff0c\u4e00\u67c4\u629b\u7ed9\u4e86\u6885\u5251\u548c\uff0c\u8f6c\u5934\u5bf9\u5b59\u4ef2\u541b\u9053\uff1a\u201c\u4e8f\u4f60\u8fd8\u662f\u672c\u95e8\u4e2d\u4eba\uff0c\u8fd9\u624b\u6df7\u5143\u529f\u4e5f\u4e0d\u77e5\uff0c\u8bf4\u751a\u4e48\u5996\u6cd5\uff1f\u201d \u3000\u3000\u6885\u5251\u548c\u4e58\u4ed6\u8f6c\u5934\uff0c\u7a81\u7136\u51fa\u5251\uff0c\u5feb\u5982\u95ea\u7535\u822c\u523a\u5411\u4ed6\u540e\u5fc3\uff0c\u5251\u5c16\u5373\u5c06\u53ca\u8eab\uff0c\u53e3\u4e2d\u624d\u559d\uff1a\u201c\u770b\u5251\uff01\u201d\u8fd9\u4e00\u5251\u5b9e\u662f\u5077\u88ad\uff0c\u4eba\u4eba\u90fd\u770b\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8eab\u5b50\u4fa7\u8fc7\uff0c\u4e5f\u559d\uff1a\u201c\u770b\u5251\uff01\u201d\u6885\u5251\u548c\u4f7f\u7684\u662f\u4e00\u62db\u201c\u82cd\u9e70\u640f\u5154\u201d\uff0c\u8881\u627f\u5fd7\u4f9d\u5f0f\u800c\u4e3a\uff0c\u4f7f\u7684\u4e5f\u662f\u4e00\u62db\u201c\u82cd\u9e70\u640f\u5154\u201d\u3002 \u3000\u3000\u6885\u5251\u548c\u8ddf\u7740\u8eab\u5b50\u4e00\u4fa7\uff0c\u60f3\u7167\u6837\u8ba9\u5f00\u6765\u5251\uff0c\u54ea\u77e5\u8881\u627f\u5fd7\u4e00\u5251\u523a\u51fa\uff0c\u7acb\u5373\u8f6c\u5708\uff0c\u7b49\u4ed6\u8eab\u5b50\u4fa7\u8fc7\uff0c\u5251\u5c16\u4e5f\u8ddf\u7740\u70b9\u5230\u3002\u6885\u5251\u548c\u53ea\u89c9\u5251\u5c16\u5df2\u523a\u53ca\u540e\u5fc3\uff0c\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u4f7f\u52b2\u524d\u6251\uff0c\u63a5\u7740\u5411\u4e0a\u7eb5\u8dc3\u3002\u5c82\u6599\u8881\u627f\u5fd7\u7684\u5251\u59cb\u7ec8\u70b9\u5728\u4ed6\u540e\u5fc3\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u4efb\u4ed6\u95ea\u907f\u817e\u632a\uff0c\u5251\u5c16\u603b\u4e0d\u79bb\u5f00\uff0c\u5e78\u597d\u8881\u627f\u5fd7\u624b\u4e0b\u5bb9\u60c5\uff0c\u53ea\u662f\u70b9\u7740\u4ed6\u7684\u8863\u670d\uff0c\u53ea\u8981\u8f7b\u8f7b\u5411\u524d\u4e00\u9001\uff0c\u4ed6\u518d\u591a\u5341\u6761\u6027\u547d\u4e5f\u90fd\u4e86\u5e10\u4e86\u3002 \u3000\u3000\u6885\u5251\u548c\u5916\u53f7\u53eb\u505a\u201c\u6ca1\u5f71\u5b50\u201d\uff0c\u8f7b\u529f\u81ea\u7136\u751a\u9ad8\uff0c\u5fc3\u91cc\u53c8\u60ca\u53c8\u6015\uff0c\u8fde\u4f7f\u4e03\u516b\u822c\u8eab\u6cd5\uff0c\u817e\u632a\u95ea\u8dc3\uff0c\u6781\u5c3d\u53d8\u5316\uff0c\u8981\u60f3\u6446\u8131\u80cc\u4e0a\u5251\u5c16\uff0c\u59cb\u7ec8\u6446\u8131\u4e0d\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u5df2\u5413\u5f97\u53cc\u624b\u53d1\u6296\uff0c\u5fc3\u60f3\u4ed6\u7ec8\u7a76\u662f\u81ea\u5df1\u5e08\u4f84\uff0c\u4e5f\u522b\u8feb\u5f97\u592a\u7d27\uff0c\u6536\u5251\u64a4\u62db\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u672c\u95e8\u4e2d\u7684\u5251\u6cd5\u5440\uff0c\u4f60\u6ca1\u5b66\u8fc7\u4e48\uff1f\u201d\u6885\u5251\u548c\u7565\u4e00\u5b9a\u795e\uff0c\u4f4e\u5934\u5598\u606f\u9053\uff1a\u201c\u8fd9\u53eb\u2018\u9644\u9aa8\u4e4b\u86c6\u2019\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u540d\u5b57\u867d\u7136\u4e0d\u5927\u597d\u542c\uff0c\u5251\u6cd5\u5374\u662f\u6781\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u90a3\u8fb9\u9752\u9752\u53c8\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u53eb\u6ca1\u5f71\u5b50\uff0c\u600e\u4e48\u80cc\u540e\u8001\u662f\u8ddf\u7740\u4eba\u5bb6\u4e00\u628a\u5251\u5462\uff1f\u2018\u6ca1\u5f71\u5b50\u2019\u7684\u5916\u53f7\uff0c\u8fd8\u662f\u6539\u4e3a\u2018\u5251\u5f71\u5b50\u2019\u5427\uff01\u201d \u3000\u3000\u6885\u5251\u548c\u6c89\u4f4f\u4e86\u6c14\u4e0d\u776c\uff0c\u4ed6\u7cbe\u7814\u4e8c\u5341\u591a\u5e74\u7684\u5251\u6cd5\u59cb\u7ec8\u6ca1\u673a\u4f1a\u65bd\u5c55\uff0c\u603b\u662f\u5fc3\u4e2d\u4e0d\u670d\uff0c\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u597d\u597d\u7684\u6765\u6bd4\u6bd4\u5251\u3002\u4f60\u7684\u6742\u5b66\u592a\u591a\uff0c\u6211\u53ef\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u672c\u95e8\u6b63\u5b97\u6b66\u529f\uff0c\u600e\u8bf4\u662f\u6742\u5b66\uff1f\u597d\uff0c\u770b\u5251\uff01\u201d\u633a\u5251\u5f53\u80f8\u5e73\u523a\u3002\u6885\u5251\u548c\u4e3e\u5251\u6321\u5f00\uff0c\u8fd8\u4e86\u4e00\u5251\uff0c\u8881\u627f\u5fd7\u56de\u5251\u683c\u8fc7\u3002\u6885\u5251\u548c\u5f85\u8981\u6536\u5251\u518d\u523a\uff0c\u4e0d\u77e5\u600e\u6837\uff0c\u5df1\u5251\u5df2\u88ab\u7c98\u5728\u5bf9\u65b9\u5251\u4e0a\uff0c\u53ea\u89c1\u8881\u627f\u5fd7\u53cd\u624b\u8f6c\u4e86\u4e24\u4e2a\u5708\u5b50\uff0c\u81ea\u5df1\u624b\u81c2\u4e0d\u80fd\u8ddf\u7740\u65cb\u8f6c\uff0c\u53ea\u5f97\u64a4\u624b\uff0c\u4e00\u67c4\u5251\u8131\u624b\u98de\u53bb\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8981\u4e0d\u8981\u518d\u8bd5\uff1f\u201d \u3000\u3000\u6885\u5251\u548c\u6a2a\u4e86\u5fc3\uff0c\u62a2\u4e86\u684c\u4e0a\u4e00\u67c4\u5251\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u659c\u523a\u5bf9\u65b9\u5de6\u80a9\uff0c\u8fd9\u6b21\u4ed6\u5b66\u4e86\u4e56\uff0c\u518d\u4e0d\u548c\u654c\u5251\u63a5\u89e6\uff0c\u4e00\u89c1\u8881\u627f\u5fd7\u4f38\u5251\u6765\u683c\uff0c\u7acb\u5373\u6536\u62db\u3002\u54ea\u77e5\u5bf9\u65b9\u957f\u5251\u4e58\u9699\u76f4\u5165\uff0c\u7adf\u6307\u81ea\u5df1\u524d\u80f8\uff0c\u5982\u4e0d\u62b5\u6321\uff0c\u5c82\u4e0d\u7ed9\u523a\u4e2a\u900f\u660e\u7a9f\u7abf\uff1f\u53ea\u5f97\u6a2a\u5251\u76f8\u683c\u3002\u53cc\u5251\u5251\u5203\u4e00\u4ea4\uff0c\u8881\u627f\u5fd7\u624b\u81c2\u4e00\u65cb\uff0c\u6885\u5251\u548c\u957f\u5251\u53c8\u5411\u7a7a\u9645\u98de\u51fa\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u7adf\u5728\u534a\u7a7a\u65ad\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u4ed6\u62a2\u7740\u8981\u518d\u53bb\u53d6\u5251\uff0c\u8881\u627f\u5fd7\u559d\u9053\uff1a\u201c\u5230\u8fd9\u5730\u6b65\u4f60\u8fd8\u4e0d\u670d\uff1f\u201d \u3000\u3000\u5237\u5237\u4e24\u5251\uff0c\u6885\u5251\u548c\u8eab\u5b50\u540e\u4ef0\u907f\u5f00\uff0c\u4e0b\u76d8\u7a7a\u865a\uff0c\u88ab\u627f\u5fd7\u5de6\u811a\u8f7b\u8f7b\u4e00\u52fe\uff0c\u4ef0\u5929\u8dea\u5012\u3002\u8881\u627f\u5fd7\u5251\u5c16\u6307\u4f4f\u4ed6\u5589\u5934\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u670d\u4e86\u4e48\uff1f\u201d\u6885\u5251\u548c\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u4ece\u672a\u53d7\u8fc7\u8fd9\u822c\u6298\u8fb1\uff0c\u4e00\u53e3\u6c14\u8f6c\u4e0d\u8fc7\u6765\uff0c\u7adf\u81ea\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u89c1\u4ed6\u53cc\u76ee\u4e0a\u7ffb\uff0c\u8eba\u5728\u5730\u4e0b\u4e0d\u52a8\uff0c\u53ea\u9053\u88ab\u8881\u627f\u5fd7\u6253\u6b7b\u4e86\uff0c\u7eb5\u8eab\u6251\u5c06\u4e0a\u6765\uff0c\u5927\u53eb\uff1a\u201c\u8fde\u6211\u4e00\u8d77\u6740\u4e86\u5427\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u6885\u5251\u548c\u95ed\u4f4f\u4e86\u6c14\uff0c\u4e0d\u89c9\u5927\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u5982\u5931\u624b\u6253\u6b7b\u4e86\u4ed6\uff0c\u5c06\u6765\u5982\u4f55\u89c1\u5f97\u5e08\u7236\u548c\u4e8c\u5e08\u54e5\u4e4b\u9762\uff1f\u201d\u5fd9\u4fef\u8eab\u5bdf\u770b\uff0c\u4e00\u6478\u4ed6\u7684\u80f8\u819b\uff0c\u89c9\u5230\u5fc3\u810f\u8fd8\u5728\u7f13\u7f13\u8df3\u52a8\uff0c\u8fd9\u624d\u653e\u5fc3\uff0c\u5fd9\u5728\u4ed6\u80c1\u4e0b\u548c\u9888\u4e0a\u7a74\u9053\u4e2d\u62cd\u4e86\u51e0\u4e0b\u3002\u5b59\u4ef2\u541b\u53cc\u62f3\u6b64\u843d\u5f7c\u8d77\uff0c\u5728\u4ed6\u80cc\u4e0a\u5982\u64c2\u9f13\u822c\u6572\u6253\uff0c\u8881\u627f\u5fd7\u53ea\u662f\u4e0d\u7406\uff0c\u5fd9\u7740\u65bd\u6551\u3002 \u3000\u3000\u9752\u9752\u548c\u5218\u57f9\u751f\u4e00\u9f50\u8dc3\u5230\u559d\u6b62\u3002\u5b59\u4ef2\u541b\u5750\u5012\u5728\u5730\uff0c\u5927\u54ed\u8d77\u6765\u3002\u4e0d\u4e45\u6885\u5251\u548c\u60a0\u60a0\u9192\u6765\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u5427\uff01\u201d\u5218\u57f9\u751f\u529d\u9053\uff1a\u201c\u6885\u5e08\u54e5\uff0c\u54b1\u4eec\u542c\u5e08\u53d4\u6559\u8bad\uff0c\u522b\u4efb\u6027\u5566\u3002\u201d \u3000\u3000\u9752\u9752\u5411\u5b59\u4ef2\u541b\u7b11\u9053\uff1a\u201c\u4ed6\u53c8\u6ca1\u6b7b\uff0c\u4f60\u54ed\u751a\u4e48\uff1f\u4f60\u5bf9\u4ed6\u5012\u771f\u4e00\u5f80\u60c5\u6df1\uff01\u201d \u3000\u3000\u5b59\u4ef2\u541b\u7f9e\u6012\u4ea4\u52a0\uff0c\u5ffd\u5730\u7eb5\u8d77\uff0c\u4e00\u62f3\u5411\u9752\u9752\u6253\u53bb\uff0c\u5979\u7a76\u662f\u534e\u5c71\u6d3e\u597d\u624b\uff0c\u8fd9\u4e00\u62f3\u53c8\u5feb\u53c8\u72e0\uff0c\u9752\u9752\u7adf\u6ca1\u80fd\u907f\u5f00\uff0c\u53ea\u6253\u5f97\u5979\u5de6\u80a9\u4e00\u9635\u5267\u75db\u3002\u9752\u9752\u5f85\u8981\u8fd8\u624b\uff0c\u5b59\u4ef2\u541b\u5ffd\u7136\u201c\u54ce\u5537\uff0c\u54ce\u5537\u201d\u5927\u53eb\u8d77\u6765\uff0c\u5f2f\u4e0b\u8170\u53bb\u3002\u9752\u9752\u4e00\u5446\uff0c\u6012\u9053\uff1a\u201c\u6253\u4e86\u4eba\u5bb6\uff0c\u81ea\u5df1\u53cd\u6765\u53eb\u75db\uff1f\u201d\u8881\u627f\u5fd7\u5411\u5979\u4f7f\u4e2a\u773c\u8272\uff0c\u9752\u9752\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff0c\u4e5f\u5c31\u4e0d\u518d\u8a00\u8bed\u4e86\u3002\u4f46\u89c1\u5b59\u4ef2\u541b\u53cc\u62f3\u7ea2\u80bf\uff0c\u63d0\u5728\u9762\u524d\uff0c\u75db\u5f97\u773c\u6cea\u76f4\u6d41\u3002 \u3000\u3000\u539f\u6765\u5979\u521a\u624d\u731b\u529b\u5728\u8881\u627f\u5fd7\u80cc\u4e0a\u6572\u51fb\uff0c\u8881\u627f\u5fd7\u8fd0\u6c14\u4e8e\u80cc\uff0c\u6bcf\u4e00\u4e0b\u6253\u51fb\u4e4b\u529b\uff0c\u90fd\u88ab\u53cd\u5f39\u51fa\u6765\u56de\u5230\u5979\u81ea\u5df1\u62f3\u4e0a\u3002\u521d\u65f6\u8fd8\u4e0d\u89c9\u5f97\uff0c\u5f85\u5f97\u5728\u9752\u9752\u80a9\u5934\u6253\u4e86\u4e00\u62f3\uff0c\u7a81\u7136\u95f4\u5947\u75db\u5165\u9aa8\uff0c\u5982\u5343\u679a\u7ec6\u9488\u5728\u8089\u91cc\u4e71\u94bb\u4e71\u523a\u3002\u8981\u77e5\u8881\u627f\u5fd7\u6068\u5979\u51fa\u624b\u6bd2\u8fa3\uff0c\u4e0d\u7531\u5206\u8bf4\u5c31\u780d\u53bb\u4e86\u90a3\u59d3\u7f57\u7684\u4e00\u6761\u81c2\u8180\uff0c\u76f8\u8f83\u4e4b\u4e0b\uff0c\u6885\u5251\u548c\u867d\u7136\u72c2\u5984\uff0c\u771f\u6b63\u8fc7\u6076\u5012\u6ca1\u6709\u751a\u4e48\uff0c\u662f\u4ee5\u5b58\u5fc3\u8981\u7ed9\u5979\u591a\u5403\u70b9\u82e6\u5934\u3002 \u3000\u3000\u65c1\u4eba\u4e0d\u77e5\uff0c\u8fd8\u9053\u9752\u9752\u65e2\u662f\u91d1\u86c7\u90ce\u541b\u7684\u513f\u5b50\uff0c\u6b66\u529f\u53ea\u6015\u6bd4\u8881\u627f\u5fd7\u8fd8\u9ad8\uff0c\u5b59\u4ef2\u541b\u4e0d\u81ea\u91cf\u529b\uff0c\u5f53\u7136\u662f\u81ea\u8ba8\u82e6\u5403\u4e86\u3002\u5341\u529b\u5927\u5e08\u3001\u90d1\u8d77\u4e91\u3001\u4e07\u91cc\u98ce\u7b49\u5374\u77e5\u5b59\u4ef2\u541b\u662f\u53d7\u4e86\u53cd\u5f39\u4e4b\u529b\uff0c\u53ea\u8981\u62ff\u7b4b\u6309\u6469\uff0c\u70b9\u89e3\u76f8\u5e94\u7a74\u9053\uff0c\u4fbf\u53ef\u6b62\u75db\u6d88\u80bf\uff0c\u53ea\u662f\u81ea\u77e5\u975e\u8881\u627f\u5fd7\u4e4b\u654c\uff0c\u4e0d\u6562\u8d38\u7136\u51fa\u624b\u89e3\u6551\u3002 \u3000\u3000\u6885\u5251\u548c\u81ea\u5e7c\u4fbf\u5728\u5f52\u8f9b\u6811\u95e8\u4e0b\uff0c\u89c1\u5230\u4e25\u5e08\uff0c\u5411\u6765\u72b9\u4f3c\u8017\u5b50\u89c1\u732b\u4e00\u822c\uff0c\u538b\u6291\u65e2\u4e45\uff0c\u72ec\u81ea\u95ef\u8361\u6c5f\u6e56\uff0c\u7adf\u52a0\u500d\u7684\u72c2\u50b2\u81ea\u5927\u8d77\u6765\u3002\u5f52\u8f9b\u6811\u53c8\u751f\u6027\u6c89\u9ed8\u5be1\u8a00\uff0c\u96be\u5f97\u8ddf\u5f1f\u5b50\u4eec\u8bf4\u4e9b\u505a\u4eba\u5904\u4e16\u7684\u9053\u7406\uff0c\u4e0d\u514d\u5c11\u4e86\u6559\u8bf2\u3002\u6885\u5251\u548c\u81ea\u5df1\u53d7\u632b\uff0c\u90a3\u662f\u5b81\u6b7b\u4e0d\u5c48\uff0c\u4f46\u89c1\u5e08\u59b9\u75db\u695a\u96be\u5f53\uff0c\u767b\u65f6\u518d\u4e5f\u4e0d\u6562\u5014\u5f3a\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u5411\u8881\u627f\u5fd7\u8fde\u4f5c\u4e86\u4e09\u4e2a\u63d6\uff0c\u9053\uff1a\u201c\u8881\u5e08\u53d4\uff0c\u665a\u8f88\u4e0d\u77e5\u4f60\u8001\u9a7e\u5230\uff0c\u591a\u591a\u5192\u72af\uff0c\u8bf7\u4f60\u8001\u7ed9\u5b59\u5e08\u59b9\u89e3\u6551\u5427\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6b63\u8272\u9053\uff1a\u201c\u4f60\u77e5\u9519\u4e86\u5417\uff1f\u201d\u6885\u5251\u548c\u4f4e\u5934\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u8be5\u64c5\u81ea\u6495\u6bc1\u7126\u5e2e\u4e3b\u7684\u4fe1\uff0c\u53c8\u4e0d\u8be5\u5f3a\u884c\u66ff\u95f5\u4e8c\u54e5\u51fa\u5934\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ee5\u540e\u6885\u5927\u54e5\u505a\u4e8b\uff0c\u603b\u8981\u518d\u52a0\u8c28\u614e\u624d\u597d\u3002\u201d\u6885\u5251\u548c\u9053\uff1a\u201c\u665a\u8f88\u542c\u5e08\u53d4\u6559\u8bad\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u95f5\u4e8c\u7237\u4e0d\u77e5\u5f53\u5e74\u7f18\u7531\uff0c\u8981\u4e3a\u5144\u957f\u62a5\u4ec7\uff0c\u672c\u6765\u5e76\u65e0\u4e0d\u5f53\u3002\u4f60\u548c\u8fd9\u91cc\u4f17\u4f4d\u82f1\u96c4\u53d7\u9080\u52a9\u62f3\uff0c\u4e5f\u90fd\u662f\u51fa\u4e8e\u670b\u53cb\u4e49\u6c14\u3002\u73b0\u4eca\u65e2\u5df2\u660e\u767d\u6b64\u4e8b\u7f18\u7531\uff0c\u5927\u5bb6\u7f62\u624b\uff0c\u5316\u654c\u4e3a\u53cb\uff0c\u8db3\u89c1\u9ad8\u4e49\u3002\u8fd9\u4e00\u70b9\u6211\u51b3\u4e0d\u602a\u4f60\u3002\u53ef\u662f\u4f60\u505a\u4e86\u4e00\u4ef6\u4e07\u5206\u4e0d\u5bf9\u7684\u4e8b\uff0c\u53ea\u6015\u6885\u5927\u54e5\u8fd8\u4e0d\u660e\u767d\u5462\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u4e00\u6123\uff0c\u95ee\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u534e\u5c71\u6d3e\u5341\u4e8c\u5927\u6212\uff0c\u7b2c\u4e94\u6761\u662f\u751a\u4e48\uff1f\u201d\u6885\u5251\u548c\u9053\uff1a\u201c\u9002\u624d\u5e08\u53d4\u95ee\u5f1f\u5b50\u56db\u6761\u6212\u5f8b\uff0c\u7b2c\u4e09\u6761\uff0c\u2018\u6ee5\u6740\u65e0\u8f9c\u2019\uff0c\u5b59\u5e08\u59b9\u786e\u662f\u72af\u4e86\u8fc7\u9519\uff0c\u53ea\u597d\u5f85\u4f1a\u5411\u7f57\u5927\u54e5\u90d1\u91cd\u8c22\u7f6a\uff0c\u6211\u4eec\u518d\u8d54\u4ed6\u4e00\u70b9\u635f\u5931\u2026\u2026\u201d \u3000\u3000\u7126\u516c\u793c\u7684\u4e00\u540d\u5f1f\u5b50\u5728\u4eba\u4e1b\u4e2d\u53eb\u9053\uff1a\u201c\u8c01\u8981\u4f60\u7684\u81ed\u94b1\uff1f\u65ad\u4e86\u8180\u5b50\uff0c\u94f6\u5b50\u8865\u5f97\u4e0a\u4e48\uff1f\u201d\u6885\u5251\u548c\u81ea\u77e5\u7406\u66f2\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8f6c\u5934\u5411\u53d1\u8bdd\u90a3\u4eba\u9053\uff1a\u201c\u6211\u8fd9\u5e08\u4f84\u786e\u662f\u884c\u4e3a\u9c81\u83bd\uff0c\u5144\u5f1f\u5341\u5206\u62b1\u6127\u3002\u5f85\u7f57\u5927\u54e5\u4f24\u6108\u4e4b\u540e\uff0c\u5144\u5f1f\u60f3\u8ddf\u4ed6\u5207\u78cb\u4e00\u8def\u72ec\u81c2\u5200\u6cd5\u3002\u8fd9\u529f\u592b\u4e0d\u662f\u534e\u5c71\u6d3e\u7684\uff0c\u5144\u5f1f\u4e0d\u5fc5\u5148\u884c\u7980\u660e\u5e08\u5c0a\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u8fc7\u4ed6\u7684\u60ca\u4eba\u6b66\u529f\uff0c\u77e5\u4ed6\u867d\u7136\u8c26\u79f0\u201c\u5207\u78cb\u5200\u6cd5\u201d\uff0c\u5b9e\u5219\u7b54\u5141\u4f20\u6388\u4e00\u9879\u7edd\u827a\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u7f57\u7acb\u5982\u867d\u7136\u5c11\u4e86\u4e00\u81c2\uff0c\u4f46\u56e0\u7978\u5f97\u798f\uff0c\u5c06\u6765\u6b66\u529f\u4e00\u5b9a\u53cd\u800c\u9ad8\u51fa\u540c\u95e8\u4faa\u8f88\u4e86\u3002\u7126\u95e8\u5f1f\u5b50\u89c1\u4ed6\u53c8\u628a\u5b59\u4ef2\u541b\u7684\u8fc7\u5931\u63fd\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u5012\u4e0d\u4fbf\u518d\u8bf4\u751a\u4e48\u3002 \u3000\u3000\u6885\u5251\u548c\u53c8\u9053\uff1a\u201c\u7b2c\u516d\u6761\u662f\u2018\u4e0d\u656c\u5c0a\u957f\u2019\uff0c\u8fd9\u6761\u5f1f\u5b50\u77e5\u7f6a\u3002\u7b2c\u5341\u4e00\u6761\u662f\u2018\u4e0d\u8fa8\u662f\u975e\u2019\uff0c\u5f1f\u5b50\u4e5f\u77e5\u7f6a\u4e86\u3002\u53ea\u662f\u7b2c\u4e94\u6761\u2018\u7ed3\u4ea4\u5978\u5f92\u2019\uff0c\u95f5\u4e8c\u54e5\u4e3a\u4eba\u6b63\u76f4\uff0c\u662f\u4f4d\u591f\u670b\u53cb\u7684\u597d\u6c49\u5b50\u3002\u201d \u3000\u3000\u4f17\u4eba\u5927\u534a\u4e0d\u77e5\u534e\u5c71\u6d3e\u7684\u5341\u4e8c\u5927\u6212\u662f\u751a\u4e48\uff0c\u4e00\u542c\u6885\u5251\u548c\u8fd9\u8bdd\uff0c\u95f5\u5b50\u534e\u7b2c\u4e00\u4e2a\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u751a\u4e48\uff1f\u6211\u662f\u5978\u5f92\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u95f5\u4e8c\u7237\u8bf7\u52ff\u8bef\u4f1a\uff0c\u6211\u51b3\u4e0d\u662f\u8bf4\u4f60\u3002\u201d\u95f5\u5b50\u534e\u6012\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8bf4\u8c01\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u6b63\u8981\u56de\u7b54\uff0c\u53ea\u89c1\u4e24\u540d\u7126\u95e8\u5f1f\u5b50\u628a\u7f57\u7acb\u5982\u4ece\u540e\u5802\u6276\u51fa\uff0c\u5411\u8881\u627f\u5fd7\u62dc\u4e86\u4e0b\u53bb\u3002\u8881\u627f\u5fd7\u8fde\u5fd9\u8fd8\u793c\u3002\u7f57\u7acb\u5982\u53f3\u8896\u7a7a\u5782\uff0c\u8138\u65e0\u8840\u8272\uff0c\u4f46\u795e\u6c14\u4ecd\u5f88\u786c\u6717\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5927\u4fa0\u6551\u4e86\u6211\u5e08\u7236\uff0c\u53c8\u7b54\u5e94\u6388\u6211\u6b66\u827a\uff0c\u5f1f\u5b50\u771f\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u8881\u627f\u5fd7\u8fde\u58f0\u8c26\u8ba9\uff0c\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u95f4\u5207\u78cb\u6b66\u827a\uff0c\u4e8b\u5c5e\u5bfb\u5e38\uff0c\u7f57\u5927\u54e5\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u7b49\u5230\u7f57\u7acb\u5982\u8fdb\u53bb\uff0c\u4f46\u89c1\u5b59\u4ef2\u541b\u989d\u5934\u6c57\u73e0\u4e00\u6ef4\u4e00\u6ef4\u7684\u843d\u4e0b\uff0c\u75db\u5f97\u5168\u8eab\u98a4\u6296\uff0c\u5634\u5507\u53d1\u7d2b\uff0c\u8881\u627f\u5fd7\u89c1\u5979\u5df2\u53d7\u82e6\u4e0d\u5c0f\uff0c\u8d70\u8fd1\u8eab\u53bb\uff0c\u4fbf\u8981\u4f38\u624b\u63a8\u7a74\u65bd\u6551\u3002\u5b59\u4ef2\u541b\u6012\u9053\uff1a\u201c\u522b\u78b0\u6211\uff0c\u75db\u6b7b\u4e86\u4e5f\u4e0d\u8981\u4f60\u6551\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8138\u4e0a\u4e00\u7ea2\uff0c\u60f3\u628a\u89e3\u6cd5\u8bf4\u7ed9\u6885\u5251\u548c\u77e5\u6653\uff0c\u7a81\u7136\u95f4\u7830\u7830\u4e24\u54cd\uff0c\u4e24\u6247\u677f\u95e8\u88ab\u4eba\u638c\u529b\u9707\u843d\uff0c\u98de\u8fdb\u5385\u6765\u3002 \u3000\u3000\u4f17\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u5385\u5916\u7f13\u6b65\u8d70\u8fdb\u4e24\u4eba\u3002\u4e00\u4e2a\u4e94\u5341\u5de6\u53f3\u5e74\u7eaa\uff0c\u7a7f\u4e00\u8eab\u5e84\u7a3c\u4eba\u88c5\u675f\uff0c\u53e6\u4e00\u4e2a\u662f\u56db\u5341\u591a\u5c81\u7684\u519c\u5987\uff0c\u624b\u91cc\u62b1\u7740\u4e2a\u5b69\u5b50\uff0c\u5b59\u4ef2\u541b\u5927\u53eb\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u5a18\uff01\u201d\u5954\u4e0a\u524d\u53bb\u3002\u4f17\u4eba\u4e00\u542c\u5979\u79f0\u547c\uff0c\u77e5\u9053\u662f\u795e\u62f3\u65e0\u654c\u5f52\u8f9b\u6811\u592b\u5987\u5230\u4e86\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u628a\u5b69\u5b50\u9012\u7ed9\u4e08\u592b\u62b1\u4e86\uff0c\u94c1\u9752\u4e86\u8138\uff0c\u7ed9\u5b59\u4ef2\u541b\u63a8\u5bab\u8fc7\u8840\u3002\u6885\u5251\u548c\u4e0e\u5218\u57f9\u751f\u4e5f\u5fd9\u4e0a\u524d\u53c2\u89c1\u3002\u5218\u57f9\u751f\u4f4e\u58f0\u8bf4\u4e86\u8881\u627f\u5fd7\u7684\u6765\u5386\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5f52\u8f9b\u6811\u5f62\u8c8c\u8d28\u6734\uff0c\u4e8c\u5e08\u5ac2\u5374\u662f\u82f1\u6c14\u903c\u4eba\uff0c\u4e8e\u662f\u8ddf\u5728\u6885\u5218\u4e24\u4eba\u8eab\u540e\uff0c\u4e5f\u4e0a\u524d\u62dc\u5012\u3002\u5f52\u8f9b\u6811\u4f38\u624b\u6276\u8d77\uff0c\u8bf4\u53e5\uff1a\u201c\u4e0d\u6562\u5f53\uff01\u201d\u5c31\u4e0d\u8a00\u8bed\u4e86\u3002\u5f52\u4e8c\u5a18\u7ed9\u5b59\u4ef2\u541b\u4e00\u9762\u6309\u6469\u624b\u81c2\uff0c\u4e00\u9762\u4fa7\u4e86\u5934\u51b7\u51b7\u6253\u91cf\u8881\u627f\u5fd7\uff0c\u8fde\u5934\u4e5f\u4e0d\u70b9\u4e00\u4e0b\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u80bf\u75db\u6e10\u6d88\uff0c\u54ed\u8bc9\u9053\uff1a\u201c\u5e08\u5a18\uff0c\u8fd9\u4eba\u8bf4\u662f\u6211\u7684\u751a\u4e48\u5e08\u53d4\uff0c\u628a\u6211\u7684\u624b\u5f04\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u8fd8\u628a\u4f60\u7ed9\u6211\u7684\u5251\u4e5f\u8e29\u65ad\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u542c\uff0c\u5fc3\u91cc\u6697\u53eb\u7cdf\u7cd5\uff0c\u6697\u60f3\uff1a\u201c\u65e9\u77e5\u8fd9\u5251\u662f\u4e8c\u5e08\u5ac2\u6240\u8d50\uff0c\u53ef\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u8e29\u65ad\u4e86\u3002\u201d\u5fd9\u9053\uff1a\u201c\u5c0f\u5f1f\u72c2\u5984\u65e0\u77e5\uff0c\u8bf7\u5e08\u54e5\u5e08\u5ac2\u6055\u7f6a\u3002\u201d \u3000\u3000\u5f52\u4e8c\u5a18\u5bf9\u4e08\u592b\u9053\uff1a\u201c\u5582\uff0c\u4e8c\u54e5\uff0c\u542c\u8bf4\u5e08\u7236\u8fd1\u6765\u6536\u4e86\u4e2a\u5c0f\u5f92\u5f1f\uff0c\u5c31\u662f\u4ed6\u4e48\uff1f\u600e\u4e48\u8fd9\u6837\u6ca1\u89c4\u77e9\uff1f\u201d\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u6211\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u5f52\u4e8c\u5a18\u9053\uff1a\u201c\u8981\u77e5\u5b66\u65e0\u6b62\u5883\uff0c\u5929\u5916\u6709\u5929\uff0c\u4eba\u4e0a\u6709\u4eba\u3002\u5b66\u4e86\u4e00\u70b9\u529f\u592b\uff0c\u5c31\u968f\u4fbf\u6b3a\u4fae\u4eba\u3002\u54fc\uff01\u6211\u7684\u5f92\u513f\u4e0d\u597d\uff0c\u81ea\u6709\u6211\u6765\u8d23\u7f5a\uff0c\u4e0d\u7528\u5e08\u53d4\u6765\u4ee3\u52b3\u554a\uff01\u201d\u8881\u627f\u5fd7\u5fd9\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u662f\u5c0f\u5f1f\u83bd\u649e\u3002\u201d\u5f52\u4e8c\u5a18\u677f\u8d77\u4e86\u8138\u9053\uff1a\u201c\u4f60\u5f04\u65ad\u6211\u7684\u5251\uff0c\u76ee\u4e2d\u8fd8\u6709\u5c0a\u957f\u4e48\uff1f\u5c31\u7b97\u5e08\u7236\u5ba0\u7231\u4f60\uff0c\u96be\u9053\u5c31\u53ef\u5bf9\u5e08\u54e5\u8fd9\u822c\u65e0\u793c\uff1f\u201d \u3000\u3000\u65c1\u4eba\u542c\u5979\u53e3\u6c14\u8d8a\u6765\u8d8a\u51f6\uff0c\u663e\u7136\u662f\u5f3a\u8bcd\u593a\u7406\uff0c\u8881\u627f\u5fd7\u5374\u53ea\u662f\u4e00\u5473\u7684\u4f4e\u58f0\u4e0b\u6c14\u3002\u7126\u516c\u793c\u4e00\u8fb9\u7684\u4eba\u5747\u662f\u6124\u6124\u4e0d\u5e73\u3002\u95f5\u5b50\u534e\u548c\u6d1e\u7384\u3001\u4e07\u91cc\u98ce\u7b49\u4eba\u90fd\u6697\u6697\u5f97\u610f\uff0c\u5fc3\u60f3\uff1a\u201c\u521a\u624d\u7ed9\u4f60\u5360\u8db3\u4e86\u4e0a\u98ce\uff0c\u4f60\u5e08\u54e5\u5e08\u5ac2\u4e00\u5230\uff0c\u8fd8\u6709\u4f60\u72e0\u7684\u5417\uff1f\u201d \u3000\u3000\u5b59\u4ef2\u541b\u9053\uff1a\u201c\u5e08\u7236\u5e08\u5a18\uff0c\u4ed6\u8bf4\u6709\u4e00\u4e2a\u751a\u4e48\u91d1\u86c7\u90ce\u541b\u7ed9\u4ed6\u6491\u8170\uff0c\u628a\u6885\u5e08\u54e5\u3001\u5218\u5e08\u54e5\u4e5f\u90fd\u7ed9\u6253\u4e86\uff0c\u8fd8\u80e1\u8bf4\u516b\u9053\u7684\u6559\u8bad\u4e86\u6211\u4eec\u534a\u5929\uff0c\u5168\u4e0d\u628a\u4f60\u4e8c\u4f4d\u77a7\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u539f\u6765\u5f52\u8f9b\u6811\u592b\u5987\u56e0\u72ec\u5b50\u5f52\u949f\u8eab\u67d3\u91cd\u75c5\uff0c\u56db\u51fa\u8bbf\u5bfb\u540d\u533b\u3002\u51e0\u4f4d\u533b\u9053\u9ad8\u660e\u4e4b\u58eb\u770b\u4e86\uff0c\u90fd\u8bf4\u5f52\u4e8c\u5a18\u5728\u6000\u5b55\u4e4b\u65f6\u548c\u4eba\u52a8\u624b\uff0c\u4f24\u4e86\u80ce\u6c14\uff0c\u5b69\u5b50\u5728\u80ce\u91cc\u5c31\u53d7\u4e86\u5185\u4f24\uff0c\u73b0\u4e0b\u53d1\u4f5c\u51fa\u6765\uff0c\u8fd9\u79cd\u80ce\u4f24\u5343\u4e0d\u4e00\u6d3b\uff0c\u53e4\u65b9\u4e0a\u8bf4\u5982\u6709\u5927\u8865\u7075\u836f\u5343\u5e74\u832f\u82d3\uff0c\u518d\u52a0\u4e0a\u6210\u4e86\u5f62\u7684\u4f55\u9996\u4e4c\u6216\u53ef\u6551\u6cbb\u3002\u8981\u4e0d\u7136\u4fbf\u662f\u5343\u5e74\u4eba\u53c2\u3001\u7075\u829d\u4ed9\u8349\uff0c\u90a3\u53ef\u66f4\u96be\u5f97\u4e86\u3002\u5982\u65e0\u7075\u836f\uff0c\u81f3\u591a\u518d\u62d6\u5f97\u4e00\u4e24\u5e74\uff0c\u5b9a\u4f1a\u67af\u7626\u800c\u6b7b\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u592b\u5987\u4e2d\u5e74\u5f97\u5b50\uff0c\u5bf9\u5b69\u5b50\u7231\u903e\u6027\u547d\uff0c\u904d\u6258\u6b66\u6797\u540c\u9053\u8bbf\u836f\u3002\u4f46\u5343\u5e74\u832f\u82d3\u5df2\u662f\u4e07\u5206\u96be\u5f97\u4e4b\u7269\uff0c\u518d\u52a0\u6210\u5f62\u4f55\u9996\u4e4c\uff0c\u5374\u5230\u54ea\u91cc\u53bb\u5bfb\uff1f\u8bbf\u4e86\u5e74\u4f59\uff0c\u6beb\u65e0\u7ed3\u679c\u3002\u773c\u89c1\u5b69\u5b50\u4e00\u5929\u5929\u7684\u7626\u4e0b\u53bb\uff0c\u5f52\u4e8c\u5a18\u53ea\u662f\u5077\u5077\u5782\u6cea\u3002\u592b\u59bb\u4fe9\u4e00\u5546\u91cf\uff0c\u91d1\u9675\u662f\u6c5f\u5357\u7b2c\u4e00\u91cd\u9547\uff0c\u5947\u73cd\u5f02\u7269\u5fc5\u591a\uff0c\u4e8e\u662f\u540c\u6765\u5357\u4eac\u8bbf\u836f\u3002\u5411\u6b66\u6797\u540c\u9053\u6253\u542c\uff0c\u5f97\u77e5\u6885\u5251\u548c\u7b49\u4e09\u540d\u5f1f\u5b50\u90fd\u5728\u6b64\u5730\u3002\u592b\u5987\u4e8c\u4eba\u5fc3\u60f3\u8fd9\u4e09\u4eba\u90fd\u5f88\u80fd\u5e72\uff0c\u53ef\u4ee5\u5e2e\u540c\u5bfb\u836f\uff0c\u7acb\u5373\u627e\u6765\u7126\u5bb6\uff0c\u54ea\u77e5\u7adf\u89c1\u5230\u5b59\u4ef2\u541b\u624b\u638c\u53d7\u4f24\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u672c\u6765\u6027\u5b50\u66b4\u8e81\uff0c\u52a0\u4e4b\u513f\u5b50\u75c5\u91cd\uff0c\u5fc3\u4e2d\u7126\u6025\uff0c\u542c\u4e86\u7231\u5f92\u7684\u4e00\u9762\u4e4b\u8f9e\uff0c\u5f53\u4e0b\u6ca1\u5934\u6ca1\u8111\u7684\u628a\u8881\u627f\u5fd7\u8d23\u5907\u4e86\u4e00\u987f\uff0c\u8fd9\u65f6\u542c\u8bf4\u4ed6\u5c1a\u6709\u5916\u4eba\u6491\u8170\uff0c\u66f4\u662f\u6124\u6012\uff0c\u4fa7\u5934\u95ee\u4e08\u592b\u9053\uff1a\u201c\u8fd9\u91d1\u86c7\u602a\u7269\u8fd8\u6d3b\u7740\uff1f\u201d\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u542c\u8bf4\u662f\u8fc7\u4e16\u4e86\uff0c\u4e0d\u8fc7\u8c01\u4e5f\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u9752\u9752\u542c\u5979\u65e0\u7406\u8d23\u9a82\u8881\u627f\u5fd7\uff0c\u65e9\u5df2\u5341\u5206\u6709\u6c14\uff0c\u5f85\u5f97\u542c\u5979\u53c8\u53eb\u81ea\u5df1\u7236\u4eb2\u4e3a\u602a\u7269\uff0c\u66f4\u662f\u607c\u6012\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u6cfc\u5987\uff01\u5e72\u4e48\u4e71\u9a82\u4eba\uff1f\u201d\u5f52\u4e8c\u5a18\u6012\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u5b59\u4ef2\u541b\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u91d1\u86c7\u602a\u7269\u7684\u513f\u5b50\u3002\u201d\u5f52\u4e8c\u5a18\u624b\u8155\u4e00\u6296\uff0c\u4e00\u7f15\u5bd2\u661f\uff0c\u75be\u5411\u9752\u9752\u80a9\u5934\u5c04\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6697\u53eb\u4e0d\u597d\uff0c\u5f85\u6b32\u8dc3\u8d77\u62cd\u6253\uff0c\u4f46\u5f52\u4e8c\u5a18\u51fa\u624b\u4f3c\u7535\uff0c\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff1f\u53ea\u89c1\u9752\u9752\u8eab\u5b50\u4e00\u98a4\uff0c\u6697\u5668\u5df2\u4e2d\u5de6\u80a9\u3002\u8881\u627f\u5fd7\u5927\u60ca\uff0c\u62a2\u4e0a\u53bb\u63e1\u4f4f\u5979\u624b\u81c2\u4e00\u770b\uff0c\u89c1\u4e4c\u6c89\u6c89\u7684\u662f\u679a\u4e27\u95e8\u9489\u3002\u8fd9\u65f6\u9752\u9752\u53c8\u60ca\u53c8\u6012\uff0c\u5df2\u75db\u5f97\u9762\u5bb9\u5931\u8272\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u522b\u52a8\uff01\u201d\u5de6\u624b\u98df\u4e2d\u53cc\u6307\u6309\u5728\u4e27\u95e8\u9489\u4e24\u65c1\uff0c\u5fae\u4e00\u7528\u52b2\uff0c\u89c1\u94a2\u9489\u8131\u51fa\u4e86\u4e09\u56db\u5206\uff0c\u77e5\u9053\u9489\u5c16\u6ca1\u5b89\u5012\u94a9\uff0c\u8fd9\u624d\u529b\u900f\u4e24\u6307\uff0c\u4e00\u8fd0\u5185\u52b2\uff0c\u90a3\u9489\u4ece\u8089\u91cc\u8df3\u4e86\u51fa\u6765\uff0c\u53ee\u7684\u4e00\u58f0\uff0c\u8dcc\u843d\u5730\u4e0b\u3002\u7126\u5b9b\u513f\u65e9\u7ad9\u5728\u4e00\u65c1\u76f8\u52a9\uff0c\u5fd9\u9012\u8fc7\u4e24\u5757\u5e72\u51c0\u624b\u5e15\u3002 \u3000\u3000\u8881\u627f\u5fd7\u66ff\u9752\u9752\u5305\u624e\u597d\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u4f60\u542c\u6211\u8bdd\uff0c\u522b\u8ddf\u5979\u5435\u3002\u201d\u9752\u9752\u6012\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u51b2\u7740\u6211\u5e08\u54e5\uff0c\u54b1\u4eec\u53ea\u5f97\u5fcd\u8ba9\u3002\u201d\u9752\u9752\u59d4\u59d4\u5c48\u5c48\u7684\u70b9\u4e86\u70b9\u5934\u3002\u8881\u627f\u5fd7\u77e5\u5979\u7d20\u6027\u5014\u5f3a\uff0c\u8fd9\u6b21\u5403\u4e86\u4e8f\u5c45\u7136\u80af\u542c\u81ea\u5df1\u7684\u8bdd\uff0c\u4e0d\u4e88\u8ba1\u8f83\uff0c\u6bd4\u5f80\u6614\u6e29\u67d4\u548c\u987a\u5f97\u591a\uff0c\u5f88\u662f\u6b22\u559c\uff0c\u5411\u5979\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u7b49\u4ed6\u4eec\u5305\u624e\u597d\u4f24\u53e3\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u968f\u624b\u53d1\u679a\u5c0f\u9489\uff0c\u8bd5\u8bd5\u4ed6\u7684\u865a\u5b9e\uff0c\u8981\u662f\u4ed6\u7236\u4eb2\u91d1\u86c7\u90ce\u541b\u771f\u6709\u672c\u9886\uff0c\u600e\u4e48\u4ed6\u8fde\u4e00\u679a\u5c0f\u9489\u4e5f\u8eb2\u4e0d\u5f00\uff1f\u53ef\u89c1\u751a\u4e48\u91d1\u86c7\u94f6\u86c7\uff0c\u53ea\u4e0d\u8fc7\u662f\u6b3a\u4e16\u76d7\u540d\u3001\u62db\u6447\u649e\u9a97\u4e4b\u5f92\u7f62\u5566\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4e8c\u5e08\u5ac2\u8fd9\u65f6\u8bef\u4f1a\u5f88\u6df1\uff0c\u5982\u52a0\u5206\u8fa9\uff0c\u53ea\u6709\u66f4\u589e\u5979\u6012\u6c14\u3002\u201d\u5f53\u4e0b\u4e00\u58f0\u4e0d\u4f5c\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u9053\uff1a\u201c\u8fd9\u91cc\u5916\u4eba\u4f17\u591a\uff0c\u54b1\u4eec\u95e8\u6237\u4e4b\u4e8b\u4e0d\u4fbf\u591a\u8bf4\u3002\u660e\u665a\u4e09\u66f4\uff0c\u6211\u4eec\u592b\u5987\u5728\u7d2b\u91d1\u5c71\u96e8\u82b1\u53f0\u8fb9\u76f8\u5019\uff0c\u8bf7\u8881\u7237\u8fc7\u6765\uff0c\u53ef\u8981\u67e5\u4e2a\u660e\u767d\uff0c\u5230\u5e95\u4f60\u771f\u662f\u6211\u4eec\u5f53\u5bb6\u7684\u5e08\u5f1f\u5462\uff0c\u8fd8\u662f\u563f\u563f\u2026\u2026\u201d\u8bf4\u7740\u51b7\u7b11\u51e0\u58f0\u3002 \u3000\u3000\u4f17\u4eba\u4e00\u542c\uff0c\u8fd9\u660e\u660e\u662f\u53eb\u9635\u52a8\u624b\u4e86\u3002\u7126\u516c\u793c\u5f88\u662f\u4e3a\u96be\uff0c\u8bf4\u9053\uff1a\u201c\u8d24\u4f09\u4fea\u5a01\u9547\u6c5f\u5357\uff0c\u5927\u4f19\u513f\u542c\u5230\u795e\u62f3\u65e0\u654c\u7684\u5927\u540d\uff0c\u5411\u6765\u4ef0\u6155\u5f97\u7d27\uff0c\u4eca\u65e5\u6709\u5e78\u5149\u4e34\uff0c\u90a3\u771f\u662f\u8bf7\u4e5f\u8bf7\u4e0d\u5230\u7684\u3002\u201d\u5f52\u4e8c\u5a18\u54fc\u4e86\u4e00\u58f0\uff0c\u5f52\u8f9b\u6811\u62b1\u7740\u513f\u5b50\uff0c\u5fc3\u795e\u4e0d\u5c5e\uff0c\u4fbf\u4f3c\u6ca1\u6709\u542c\u89c1\u3002\u7126\u516c\u793c\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u8881\u7237\u89c1\u5144\u5f1f\u9047\u4e0a\u4e86\u4e3a\u96be\u4e4b\u4e8b\uff0c\u4ed7\u4e49\u6392\u89e3\u3002\u6885\u5927\u54e5\u3001\u5218\u5927\u54e5\u3001\u5b59\u59d1\u5a18\u4e09\u4f4d\u4e5f\u90fd\u8bf4\u6e05\u695a\u4e86\u3002\u660e\u665a\u5144\u5f1f\u4f5c\u4e1c\uff0c\u7ed9\u8d24\u4f09\u4fea\u63a5\u98ce\uff0c\u540c\u65f6\u5e86\u8d3a\u4e09\u4f4d\u5e08\u5144\u5f1f\u76f8\u9022\u2026\u2026\u201d \u3000\u3000\u5f52\u4e8c\u5a18\u4e0d\u8010\u70e6\u542c\u4ed6\u8bf4\u4e0b\u53bb\uff0c\u8f6c\u5934\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u600e\u6837\uff1f\u4f60\u4e0d\u6562\u53bb\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e08\u54e5\u5e08\u5ac2\u4f4f\u5728\u54ea\u91cc\uff1f\u5c0f\u5f1f\u660e\u65e5\u4e00\u65e9\u8fc7\u6765\u8bf7\u4e24\u4f4d\u6559\u8bad\u3002\u5e08\u54e5\u5e08\u5ac2\u8981\u600e\u4e48\u8d23\u7f5a\uff0c\u5c0f\u5f1f\u4e00\u5b9a\u4e0d\u6562\u89c4\u907f\u3002\u201d \u3000\u3000\u5f52\u4e8c\u5a18\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8c01\u77e5\u4f60\u662f\u771f\u662f\u5047\uff0c\u5148\u522b\u8fd9\u6837\u79f0\u547c\u3002\u660e\u665a\u8bd5\u4e86\u4f60\u7684\u529f\u592b\u518d\u8bf4\u3002\u8d70\u5427\uff01\u201d\u62c9\u4e86\u5b59\u4ef2\u541b\u624b\u81c2\uff0c\u8f6c\u8eab\u8d70\u51fa\u3002 \u3000\u3000\u592a\u767d\u4e09\u82f1\u5148\u89c1\u8881\u627f\u5fd7\u51fa\u5934\u5e72\u9884\uff0c\u5df2\u77e5\u6240\u8c0b\u96be\u6210\uff0c\u6599\u60f3\u6628\u665a\u5236\u4f4f\u81ea\u5df1\u800c\u76d7\u53bb\u4e66\u51fd\u7684\uff0c\u5b9a\u4e5f\u662f\u6b64\u4eba\u65e0\u7591\uff0c\u53ea\u6015\u4ed6\u968f\u65f6\u4f1a\u53d6\u51fa\u591a\u5c14\u886e\u7684\u51fd\u4ef6\uff0c\u63ed\u9732\u901a\u654c\u5356\u56fd\u4e4b\u4e8b\uff0c\u4e00\u76f4\u5728\u60f3\u4e58\u673a\u6e9c\u8d70\uff0c\u6070\u597d\u5f52\u8f9b\u6811\u592b\u5987\u5230\u6765\uff0c\u4e89\u95f9\u53c8\u8d77\u3002\u4e09\u4eba\u6697\u6697\u6b23\u559c\uff0c\u53ea\u76fc\u4e8b\u60c5\u95f9\u5927\uff0c\u5c31\u53ef\u6df7\u6c34\u6478\u9c7c\uff0c\u5f85\u89c1\u4ed6\u4eec\u7ea6\u5b9a\u660e\u665a\u5728\u96e8\u82b1\u53f0\u6bd4\u6b66\uff0c\u4eca\u665a\u5df2\u7ecf\u65e0\u4e8b\uff0c\u4e09\u4eba\u4e00\u6253\u773c\u8272\uff0c\u62a2\u5728\u5f52\u6c0f\u592b\u5987\u5934\u91cc\u6e9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u5582\uff0c\u6162\u8d70\uff01\u201d\u98de\u8eab\u51fa\u53bb\u62e6\u963b\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u65e0\u793c\uff0c\u4f60\u8981\u62e6\u6211\uff01\u201d\u4e00\u638c\u5f80\u4ed6\u5934\u9876\u76f4\u5288\u4e0b\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7f29\u8eab\u4e00\u504f\uff0c\u5f52\u4e8c\u5a18\u7684\u624b\u638c\u4ece\u4ed6\u80a9\u65c1\u63a0\u8fc7\uff0c\u638c\u98ce\u6240\u53ca\uff0c\u5fae\u89c9\u9178\u9ebb\u3002\u5f52\u4e8c\u5a18\u4e0e\u4e08\u592b\u5728\u5bb6\u4e4b\u65f6\uff0c\u65e0\u65e5\u4e0d\u5bf9\u638c\u8fc7\u62db\uff0c\u52e4\u7ec3\u6b66\u529f\uff0c\u638c\u6cd5\u4e4b\u51cc\u5389\u72e0\u8fa3\uff0c\u81ea\u8d1f\u9664\u4e86\u4e08\u592b\u4e4b\u5916\uff0c\u6b66\u6797\u4e2d\u5df2\u5c11\u6709\u654c\u624b\uff0c\u4f46\u8fd9\u4e00\u638c\u5c45\u7136\u6ca1\u6253\u5230\u5bf9\u65b9\uff0c\u90a3\u662f\u8fd1\u5341\u5e74\u6765\u6240\u672a\u6709\u4e4b\u4e8b\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u624b\u638c\u53d8\u5288\u4e3a\u524a\uff0c\u968f\u52bf\u6a2a\u626b\u3002\u8881\u627f\u5fd7\u53cc\u8db3\u4e00\u70b9\uff0c\u8eab\u5b50\u9661\u7136\u62d4\u8d77\uff0c\u8dc3\u8fc7\u4e86\u4e00\u5f20\u684c\u5b50\u3002\u8fd9\u4e00\u6765\uff0c\u5f52\u4e8c\u5a18\u4e0d\u4fbf\u518d\u884c\u8ffd\u51fb\uff0c\u72e0\u72e0\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u4e0e\u5f52\u8f9b\u6811\u3001\u5b59\u4ef2\u541b\u3001\u6885\u5251\u548c\u3001\u5218\u57f9\u751f\u76f4\u51fa\u5927\u95e8\u3002 \u3000\u3000\u592a\u767d\u4e09\u82f1\u89c1\u6b64\u826f\u673a\uff0c\u7acb\u5373\u968f\u7740\u5954\u51fa\u3002\u8881\u627f\u5fd7\u751f\u6015\u5f52\u4e8c\u5a18\u53c8\u8d77\u8bef\u4f1a\uff0c\u4e0d\u6562\u518d\u884c\u547c\u559d\uff0c\u7eb5\u8eab\u6251\u51fa\uff0c\u4e00\u628a\u6293\u4f4f\u8d70\u5728\u6700\u540e\u7684\u9ece\u521a\uff0c\u968f\u624b\u70b9\u4e86\u7a74\u9053\uff0c\u63b7\u5728\u5730\u4e0b\u3002\u53f2\u6c0f\u5144\u5f1f\u5374\u7ec8\u4e8e\u9003\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8ffd\u51fa\u95e8\u5916\uff0c\u6df1\u591c\u4e4b\u4e2d\uff0c\u56db\u4e0b\u9ed1\u6c89\u6c89\u5730\u5df2\u4e0d\u89c1\u5f71\u8e2a\uff0c\u5fc3\u60f3\u6293\u4f4f\u4e00\u4eba\uff0c\u4e5f\u53ef\u4ee5\u8ffd\u95ee\u53e3\u4f9b\u4e86\uff0c\u5f53\u5373\u8f6c\u8eab\u56de\u5165\u5385\u4e2d\u3002\u5ffd\u542c\u5f97\u8eab\u540e\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u5c0f\u670b\u53cb\uff0c\u591a\u5e74\u4e0d\u89c1\uff0c\u529f\u592b\u53ef\u4fca\u5f97\u5f88\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8033\u542c\u58f0\u97f3\u719f\u8bc6\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u75be\u5fd9\u56de\u5934\uff0c\u53ea\u89c1\u5385\u5916\u5927\u8e0f\u6b65\u8d70\u8fdb\u4e24\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\u987b\u7709\u7686\u767d\uff0c\u80cc\u4e0a\u8d1f\u7740\u4e00\u5757\u9ed1\u9edd\u9edd\u7684\u65b9\u76d8\uff0c\u7adf\u662f\u4f20\u8fc7\u4ed6\u8f7b\u529f\u6697\u5668\u79d8\u672f\u7684\u6728\u6851\u9053\u4eba\u3002\u53ea\u89c1\u4ed6\u4e00\u624b\u63d0\u7740\u53f2\u79c9\u6587\uff0c\u4e00\u624b\u63d0\u7740\u53f2\u79c9\u5149\u3002\u8881\u627f\u5fd7\u8fd9\u4e00\u4e0b\u559c\u51fa\u671b\u5916\uff0c\u5fd9\u62a2\u4e0a\u62dc\u5012\u5728\u5730\uff0c\u53eb\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u8001\u4eba\u5bb6\u597d\uff01\u201d \u3000\u3000\u6728\u6851\u9053\u4eba\u7b11\u9053\uff1a\u201c\u8d77\u6765\uff0c\u8d77\u6765\uff01\u4f60\u77a7\u8fd9\u4eba\u662f\u8c01\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8d77\u8eab\u770b\u65f6\uff0c\u89c1\u4ed6\u8eab\u65c1\u7ad9\u7740\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u4e24\u9b13\u5fae\u971c\uff0c\u4e00\u8138\u98ce\u5c18\u4e4b\u8272\uff0c\u518d\u4e00\u7ec6\u770b\uff0c\u8fd9\u624d\u8ba4\u51fa\u662f\u5f53\u5e74\u820d\u547d\u6551\u8fc7\u81ea\u5df1\u7684\u5d14\u79cb\u5c71\u3002\u6728\u6851\u9053\u4eba\u5e74\u7eaa\u5df2\u8001\uff0c\u5341\u4f59\u5e74\u6765\u9762\u8c8c\u6ca1\u751a\u4e48\u6539\u53d8\uff0c\u5d14\u79cb\u5c71\u5728\u95ef\u738b\u519b\u4e2d\u51fa\u6b7b\u5165\u751f\uff0c\u4ece\u5c11\u5e74\u800c\u81f3\u4e2d\u5e74\uff0c\u4e45\u5386\u98ce\u971c\uff0c\u795e\u60c5\u5374\u5df2\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8fd9\u4e00\u4e0b\u53c8\u60ca\u53c8\u559c\uff0c\u62a2\u4e0a\u53bb\u62b1\u4f4f\u4e86\u4ed6\uff0c\u53eb\u9053\uff1a\u201c\u5d14\u53d4\u53d4\uff0c\u539f\u6765\u662f\u4f60\u3002\u201d\u4e0d\u7981\u6cea\u6c34\u593a\u7736\u800c\u51fa\u3002\u5d14\u79cb\u5c71\u89c1\u4ed6\u6545\u4eba\u60c5\u91cd\uff0c\u771f\u60c5\u6d41\u9732\uff0c\u773c\u4e2d\u4e5f\u4e0d\u7981\u6e7f\u6da6\u3002 \u3000\u3000\u5ffd\u542c\u95f5\u5b50\u534e\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u5e72\u4e48\u8ddf\u592a\u767d\u4e09\u82f1\u4e3a\u96be\uff1f\u600e\u5730\u62ff\u4f4f\u4e86\u4ed6\u4eec\u4e0d\u653e\uff1f\u201d\u4f17\u4eba\u7d20\u77e5\u53f2\u6c0f\u5144\u5f1f\u6b66\u529f\u4e86\u5f97\uff0c\u53ef\u662f\u7ed9\u8fd9\u8001\u9053\u6293\u5728\u624b\u4e2d\uff0c\u5982\u63d0\u5a74\u513f\uff0c\u4e1d\u6beb\u6ca1\u6709\u6323\u624e\uff0c\u663e\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u5747\u611f\u60ca\u5947\u3002 \u3000\u3000\u6728\u6851\u54c8\u54c8\u4e00\u7b11\uff0c\u5c06\u53f2\u6c0f\u5144\u5f1f\u63b7\u5728\u5730\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u62ff\u4f4f\u4e86\u73a9\u800d\u73a9\u800d\u4e0d\u53ef\u4ee5\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u4f38\u624b\u5411\u6728\u6851\u9053\u4eba\u8eab\u65c1\u4e00\u6446\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u6728\u6851\u9053\u957f\uff0c\u662f\u94c1\u5251\u95e8\u7684\u524d\u8f88\u9ad8\u4eba\u3002\u201d\u53c8\u5411\u5d14\u79cb\u5c71\u4e00\u6446\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5d14\u5927\u53d4\u4ee5\u4f0f\u864e\u638c\u6cd5\u540d\u91cd\u6b66\u6797\uff0c\u662f\u5144\u5f1f\u5b66\u6b66\u65f6\u7684\u5f00\u8499\u5e08\u5085\u3002\u201d \u3000\u3000\u5385\u4e0a\u8001\u4e00\u8f88\u7684\u7d20\u95fb\u201c\u5343\u53d8\u4e07\u52ab\u201d\u6728\u6851\u9053\u4eba\u7684\u5927\u540d\uff0c\u53ea\u662f\u4ed6\u884c\u8e2a\u795e\u51fa\u9b3c\u6ca1\uff0c\u5341\u4e4b\u516b\u4e5d\u90fd\u6ca1\u89c1\u4ed6\u9762\uff0c\u53ea\u6709\u5341\u529b\u5927\u5e08\u548c\u6606\u4ed1\u6d3e\u5f20\u5fc3\u4e00\u662f\u4ed6\u65e7\u8bc6\uff0c\u4f46\u7b97\u6765\u4e5f\u662f\u665a\u8f88\u4e86\uff0c\u4e24\u4eba\u5fd9\u8fc7\u6765\u53ae\u89c1\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u5341\u529b\u5927\u5e08\u548c\u5f20\u5fc3\u4e00\u4ee5\u5982\u6b64\u8eab\u5206\u5730\u4f4d\uff0c\u5c1a\u4e14\u5bf9\u4ed6\u8fd9\u822c\u606d\u8c28\uff0c\u65e0\u4e0d\u8083\u7136\u3002 \u3000\u3000\u6728\u6851\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u9664\u4e86\u5403\u996d\uff0c\u5c31\u7231\u4e0b\u68cb\uff0c\u7f57\u91cc\u7f57\u5506\u7684\u4e8b\u5411\u6765\u4e0d\u7406\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u8001\u9053\u7684\u68cb\u672f\u600e\u80fd\u5982\u6b64\u51fa\u795e\u5165\u5316\uff1f\u53ef\u662f\u4e0a\u4e2a\u6708\u5ffd\u7136\u5f97\u5230\u6d88\u606f\uff0c\u8bf4\u6709\u4eba\u79c1\u901a\u5916\u56fd\uff0c\u8981\u5230\u5357\u4eac\u6765\u8c0b\u5e72\u4e00\u4ef6\u5927\u5927\u7684\u5356\u56fd\u52fe\u5f53\uff0c\u8d2b\u9053\u53ef\u5c31\u4e0d\u80fd\u8896\u624b\u4e86\uff0c\u56e0\u6b64\u4e00\u8def\u8ddf\u4e86\u8fc7\u6765\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u5947\u9053\uff1a\u201c\u8c01\u662f\u5356\u56fd\u5978\u8d3c\uff1f\u96be\u9053\u4f1a\u662f\u592a\u767d\u4e09\u82f1\uff1f\u201d\u6728\u6851\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8fd9\u4e09\u4e2a\u5927\u540d\u9f0e\u9f0e\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u72d7\u718a\u8017\u5b50\uff01\u201d \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u4e09\u4f4d\u662f\u597d\u670b\u53cb\uff0c\u600e\u4f1a\u505a\u8fd9\u79cd\u65e0\u803b\u52fe\u5f53\uff0c\u4f60\u522b\u51a4\u6789\u597d\u4eba\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u8001\u9053\u8ddf\u8fd9\u4e09\u4e2a\u5bb6\u4f19\u4ece\u6765\u6ca1\u89c1\u8fc7\u9762\uff0c\u65e0\u6028\u65e0\u4ec7\uff0c\u5e72\u4e48\u8981\u51a4\u6789\u4ed6\u4eec\uff1f\u4ed6\u4eec\u548c\u6ee1\u6d32\u9791\u5b50\u5077\u5077\u6478\u6478\u6363\u9b3c\uff0c\u6211\u5728\u5173\u5916\u4eb2\u773c\u89c1\u5230\uff0c\u4eb2\u8033\u542c\u5230\uff0c\u54ea\u8fd8\u80fd\u6709\u9519\uff1f\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u6709\u751a\u4e48\u8bc1\u636e\uff1f\u201d\u6728\u6851\u5947\u9053\uff1a\u201c\u8bc1\u636e\uff1f\u8981\u751a\u4e48\u8bc1\u636e\uff1f\u96be\u9053\u51ed\u8001\u9053\u7684\u4e00\u53e5\u8bdd\uff0c\u8fd8\u4f5c\u4e0d\u5f97\u6570\uff1f\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u8fd9\u4e2a\u8c01\u76f8\u4fe1\u5440\uff1f\u201d \u3000\u3000\u6728\u6851\u6012\u559d\uff1a\u201c\u4f60\u662f\u96be\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4ed9\u90fd\u6d3e\u95f5\u5b50\u534e\u95f5\u4e8c\u7237\u3002\u201d\u6728\u6851\u6012\u9053\uff1a\u201c\u4f60\u5e08\u7236\u9ec4\u6728\u9053\u4eba\uff0c\u5f53\u5e74\u5bf9\u6211\u7684\u8bf4\u8bdd\u4e5f\u4e0d\u6562\u9053\u534a\u4e2a\u4e0d\u5b57\u3002\u4f60\u8fd9\u5c0f\u5b50\u80c6\u6562\u4e0d\u4fe1\u9053\u7237\u7684\u8bdd\uff1f\u201d \u3000\u3000\u4f17\u4eba\u867d\u90fd\u656c\u4ed6\u662f\u6b66\u6797\u524d\u8f88\uff0c\u4f46\u89c9\u5982\u6b64\u6b66\u65ad\uff0c\u672a\u514d\u592a\u8fc7\u6a2a\u86ee\u65e0\u7406\uff0c\u5fc3\u4e2d\u5747\u611f\u4e0d\u670d\uff0c\u5374\u4e5f\u65e0\u4eba\u51fa\u8a00\u8ddf\u4ed6\u4e89\u8fa9\u3002\u6728\u6851\u634b\u7740\u80e1\u5b50\u76f4\u751f\u6c14\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5c01\u4fe1\u6765\uff0c\u4ea4\u7ed9\u95f5\u5b50\u534e\u9053\uff1a\u201c\u95f5\u4e8c\u7237\uff0c\u8bf7\u4f60\u7ed9\u5927\u4f19\u513f\u5ff5\u4e00\u5ff5\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u63a5\u8fc7\u4fe1\u6765\uff0c\u53ea\u770b\u4e86\u51e0\u53e5\uff0c\u5c31\u5413\u4e86\u4e00\u8df3\u3002\u8881\u627f\u5fd7\u5b88\u5728\u4e00\u65c1\uff0c\u82e5\u89c1\u4ed6\u4e5f\u5b66\u6885\u5251\u548c\u7684\u6837\uff0c\u8981\u60f3\u626f\u788e\u4fe1\u7b3a\uff0c\u7acb\u5373\u4fbf\u70b9\u4ed6\u7a74\u9053\uff0c\u593a\u8fc7\u4fe1\u6765\u3002\u5374\u89c1\u4ed6\u53cc\u624b\u6367\u4fe1\uff0c\u9ad8\u58f0\u6717\u8bf5\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4fe1\u4fbf\u662f\u6ee1\u6d32\u777f\u4eb2\u738b\u591a\u5c14\u886e\u5199\u7ed9\u592a\u767d\u4e09\u82f1\u7684\uff0c\u5429\u5490\u4ed6\u4eec\u4fdf\u673a\u593a\u53d6\u6c5f\u5357\u5e2e\u4f1a\u7684\u5730\u76d8\uff0c\u5728\u6b66\u6797\u4eba\u58eb\u4e2d\u6311\u62e8\u79bb\u95f4\uff0c\u5f15\u81f4\u4f17\u4eba\u81ea\u76f8\u6b8b\u6740\uff0c\u540c\u65f6\u8bbe\u6cd5\u6269\u5145\u52bf\u529b\uff0c\u7b49\u6e05\u5175\u5165\u5173\uff0c\u5c31\u8d77\u4e8b\u5185\u5e94\u3002 \u3000\u3000\u4fe1\u672b\u76d6\u7740\u777f\u4eb2\u738b\u7684\u4e24\u679a\u6731\u5370\u3002\u95f5\u5b50\u534e\u8fd8\u6ca1\u5ff5\u5b8c\uff0c\u7fa4\u8c6a\u65e9\u5df2\u5927\u6012\uff0c\u7eb7\u7eb7\u559d\u9a82\u3002\u90d1\u8d77\u4e91\u62c9\u8d77\u9ece\u521a\uff0c\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u6709\u751a\u4e48\u5978\u8ba1\uff1f\u5feb\u62db\u51fa\u6765\u3002\u201d\u9ece\u521a\u778b\u76ee\u4e0d\u8bed\u3002\u90d1\u8d77\u4e91\u556a\u556a\u4e24\u8bb0\u8033\u5149\uff0c\u4ed6\u4e24\u8fb9\u8138\u988a\u767b\u65f6\u80bf\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5f53\u4e0b\u628a\u5982\u4f55\u5f97\u5230\u5bc6\u4ef6\u7684\u7ecf\u8fc7\uff0c\u539f\u539f\u672c\u672c\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ece\u521a\u77e5\u9053\u65e0\u6cd5\u62b5\u8d56\uff0c\u53eb\u9053\uff1a\u201c\u6e05\u5175\u4e0d\u65e5\u5c31\u8981\u5165\u5173\uff0c\u8fd9\u91cc\u4fbf\u662f\u5927\u6e05\u56fd\u7684\u5929\u4e0b\u3002\u4f60\u4eec\u73b0\u4e0b\u6295\u987a\uff0c\u8fd8\u4e0d\u5931\u4e3a\u5f00\u56fd\u529f\u81e3\uff0c\u8981\u662f\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u90d1\u8d77\u4e91\u5f53\u80f8\u4e00\u62f3\uff0c\u628a\u4ed6\u6253\u5f97\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f2\u6c0f\u5144\u5f1f\u6bd4\u9ece\u521a\u9634\u9e37\u5f97\u591a\uff0c\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u77e5\u8981\u7cdf\uff0c\u8981\u60f3\u9970\u8f9e\u5206\u8fa9\uff0c\u5374\u82e6\u4e8e\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u90d1\u8d77\u4e91\u9053\uff1a\u201c\u9053\u957f\uff0c\u8fd9\u79cd\u5978\u8d3c\u7559\u7740\u5e72\u4e48\uff1f\u6bd9\u4e86\u7b97\u5566\uff01\u201d\u7126\u516c\u793c\u9053\uff1a\u201c\u6599\u60f3\u8fd9\u4e9b\u5978\u8d3c\u4e00\u5b9a\u8fd8\u6709\u540c\u515a\uff0c\u54b1\u4eec\u5f97\u67e5\u95ee\u660e\u767d\u3002\u4eca\u65e5\u4e0d\u65e9\u4e86\uff0c\u6539\u65e5\u518d\u8bf7\u5404\u4f4d\u4e00\u9f50\u5546\u91cf\u3002\u201d\u4f17\u4eba\u90fd\u8bf4\u4e0d\u9519\uff0c\u5f53\u4e0b\u7eb7\u7eb7\u544a\u8f9e\uff0c\u6709\u7684\u8fd8\u5411\u592a\u767d\u4e09\u82f1\u53e3\u5410\u553e\u6d8e\uff0c\u8e22\u4e0a\u51e0\u811a\u3002 \u3000\u3000\u95f5\u5b50\u534e\u77e5\u9053\u53d7\u4e86\u5978\u4eba\u5229\u7528\uff0c\u5f88\u662f\u61ca\u6094\uff0c\u6781\u529b\u5411\u7126\u516c\u793c\u544a\u7f6a\uff0c\u53c8\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8981\u4e0d\u662f\u8881\u76f8\u516c\u51fa\u6765\u6392\u89e3\uff0c\u6d88\u5f2d\u4e86\u4e00\u573a\u5927\u7978\uff0c\u53c8\u63ed\u7834\u4e86\u5978\u4eba\u7684\u9634\u8c0b\u6bd2\u8ba1\uff0c\u5144\u5f1f\u771f\u662f\u7f6a\u4e0d\u53ef\u8d66\u3002\u201d\u5341\u529b\u5927\u5e08\u3001\u90d1\u8d77\u4e91\u3001\u5f20\u5fc3\u4e00\u7b49\u4e5f\u5747\u5411\u8881\u627f\u5fd7\u81f4\u8c22\uff0c\u7136\u540e\u8f9e\u51fa\u3002 \u3000\u3000\u6728\u6851\u89e3\u4e0b\u80cc\u4e0a\u68cb\u76d8\uff0c\u6478\u51fa\u56ca\u4e2d\u68cb\u5b50\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\u6211\u8001\u662f\u7275\u6302\u7740\u4f60\uff0c\u522b\u7684\u5012\u6ca1\u751a\u4e48\uff0c\u5c31\u662f\u60f3\u4f60\u966a\u6211\u4e0b\u68cb\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u5174\u81f4\u52c3\u52c3\uff0c\u5fae\u7b11\u7740\u5750\u4e86\u4e0b\u6765\uff0c\u62c8\u8d77\u4e86\u68cb\u5b50\uff0c\u5fc3\u60f3\uff1a\u201c\u9053\u957f\u5f85\u6211\u6069\u91cd\uff0c\u96be\u4ee5\u62a5\u7b54\u3002\u4ed6\u4e00\u751f\u60df\u597d\u4e0b\u68cb\uff0c\u53ea\u6709\u966a\u4ed6\u4e0b\u68cb\u6765\u7a0d\u5c3d\u6211\u7684\u5b5d\u5fc3\u4e86\u3002\u201d\u6728\u6851\u7709\u82b1\u773c\u7b11\uff0c\u5411\u4f59\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u53bb\u7761\u5427\u3002\u8001\u9053\u68cb\u827a\u9ad8\u6df1\uff0c\u5343\u53d8\u4e07\u5316\uff0c\u8c05\u4f60\u4eec\u4e5f\u770b\u4e0d\u61c2\u3002\u201d \u3000\u3000\u7126\u516c\u793c\u5f15\u5d14\u79cb\u5c71\u5165\u5185\u5b89\u7761\u3002\u9752\u9752\u5374\u5b9a\u8981\u65c1\u89c2\uff0c\u4e0d\u80af\u53bb\u7761\u3002 \u3000\u3000\u7126\u5b9b\u513f\u5728\u4e00\u8fb9\u9012\u9001\u9152\u83dc\u6c34\u679c\u3002 \u3000\u3000\u9752\u9752\u4e0d\u61c2\u56f4\u68cb\uff0c\u770b\u5f97\u6c14\u95f7\uff0c\u52a0\u4e4b\u80a9\u5934\u53d7\u4f24\uff0c\u4e0d\u514d\u7cbe\u795e\u5026\u6020\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u7adf\u4f0f\u5728\u51e0\u4e0a\u7761\u7740\u4e86\u3002\u6728\u6851\u5bf9\u5b9b\u513f\u9053\uff1a\u201c\u7126\u5927\u59d1\u5a18\uff0c\u6276\u5979\u5230\u4f60\u623f\u91cc\u7761\u53bb\u5427\u3002\u201d\u5b9b\u513f\u8138\u4e00\u7ea2\uff0c\u53ea\u88c5\u4e0d\u542c\u89c1\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4f4d\u9053\u957f\u600e\u5730\u98ce\u8a00\u98ce\u8bed\u7684\uff1f\u201d\u6728\u6851\u5475\u5475\u7b11\u9053\uff1a\u201c\u5979\u662f\u5973\u5b69\u5b50\u554a\uff0c\u4f60\u6015\u751a\u4e48\u7f9e\uff1f\u201d\u5b9b\u513f\u95ee\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u662f\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u5979\u5973\u626e\u7537\u88c5\uff0c\u5728\u5916\u9762\u8d70\u52a8\u65b9\u4fbf\u4e9b\u3002\u201d \u3000\u3000\u5b9b\u513f\u5e74\u7eaa\u6bd4\u9752\u9752\u5c0f\u4e86\u4e00\u5c81\uff0c\u4f46\u8ddf\u7740\u7236\u4eb2\u5386\u7ec3\u60ef\u4e86\uff0c\u5f88\u662f\u7cbe\u660e\uff0c\u9752\u9752\u5973\u626e\u7537\u88c5\uff0c\u672c\u6765\u4e0d\u4f1a\u770b\u4e0d\u51fa\u6765\uff0c\u53ea\u662f\u8fd9\u4e24\u65e5\u5979\u7275\u6302\u7236\u4eb2\u751f\u6b7b\u5b89\u5371\u3002\u5fc3\u65e0\u65c1\u9a9b\uff0c\u53c8\u89c1\u9752\u9752\u662f\u4e2a\u7f8e\u8c8c\u5c11\u5e74\uff0c\u4e00\u89c1\u9762\u5c31\u62c9\u5979\u7684\u624b\uff0c\u9690\u9690\u89c9\u5f97\u6b64\u4eba\u751a\u4e0d\u5e84\u91cd\uff0c\u6b64\u540e\u5c31\u4e0d\u6562\u5bf9\u5979\u76f4\u89c6\uff0c\u8fd9\u65f6\u542c\u8881\u627f\u5fd7\u8bf4\u4e86\uff0c\u5140\u81ea\u4e0d\u653e\u5fc3\uff0c\u8f7b\u8f7b\u9664\u4e0b\u9752\u9752\u7684\u5934\u5dfe\uff0c\u9732\u51fa\u4e00\u5934\u9752\u4e1d\u79c0\u53d1\uff0c\u5934\u53d1\u4e0a\u8fd8\u63d2\u4e86\u4e24\u679a\u7389\u7c2a\uff0c\u4e8e\u662f\u6276\u5979\u8d77\u8eab\uff0c\u4ed4\u7ec6\u770b\u65f6\uff0c\u4f46\u89c1\u9752\u9752\u7ec6\u7709\u6a31\u53e3\uff0c\u808c\u80a4\u767d\u5ae9\uff0c\u679c\u7136\u662f\u4e2a\u7f8e\u8c8c\u5973\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u6211\u6276\u4f60\u53bb\u7761\u3002\u201d\u9752\u9752\u8ff7\u8ff7\u7cca\u7cca\u7684\u9053\uff1a\u201c\u6211\u4e0d\u56f0\uff0c\u6211\u8fd8\u8981\u770b\u3002\u9053\u957f\u2026\u2026\u9053\u957f\u8f93\u4e86\u51e0\u5c40\u5566\uff1f\u201d \u3000\u3000\u6728\u6851\u7b11\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u201d\u5b9b\u513f\u5fae\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f11\u606f\u4e00\u4e0b\uff0c\u54b1\u4eec\u518d\u6765\u770b\u3002\u201d\u6276\u5979\u5230\u81ea\u5df1\u623f\u91cc\u5b89\u7761\u3002 \u3000\u3000\u8881\u627f\u5fd7\u597d\u51e0\u5e74\u6ca1\u4e0b\u68cb\u4e86\uff0c\u4e0d\u514d\u751f\u758f\uff0c\u5fc3\u4e2d\u53c8\u5c3d\u60f3\u5230\u660e\u665a\u5f52\u6c0f\u592b\u5987\u4e4b\u7ea6\uff0c\u5fc3\u795e\u4e0d\u5c5e\uff0c\u8fde\u8d70\u4e86\u4e24\u4e0b\u9519\u7740\uff0c\u767d\u767d\u7684\u8f93\u4e86\u4e00\u4e2a\u52ab\uff0c\u4e00\u5b9a\u795e\uff0c\u5ffd\u7136\u60f3\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u600e\u77e5\u5979\u662f\u5973\u5b50\uff1f\u201d \u3000\u3000\u6728\u6851\u5475\u5475\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60\u5d14\u53d4\u53d4\u4e94\u5929\u524d\u5c31\u89c1\u5230\u4f60\u5566\u3002\u6211\u8981\u6697\u4e2d\u5bdf\u770b\u4f60\u7684\u529f\u592b\u4eba\u54c1\uff0c\u4e00\u76f4\u6ca1\u8ddf\u4f60\u76f8\u89c1\u3002\u5c0f\u5fc3\uff0c\u8981\u5403\u4f60\u8fd9\u4e00\u5757\u4e86\uff0c\u70b9\u773c\uff01\u201d\u8bf4\u7740\u4e0b\u4e86\u4e00\u5b50\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u6b66\u529f\u5927\u8fdb\uff0c\u679c\u7136\u4e86\u5f97\u3002\u6216\u8bb8\u8fd8\u53ca\u4e0d\u4e0a\u4f60\u5e08\u7236\uff0c\u8001\u9053\u53ef\u4e0d\u662f\u4f60\u5bf9\u624b\u5566\u3002\u201d\u8881\u627f\u5fd7\u8d77\u7acb\u900a\u8c22\uff0c\u9053\uff1a\u201c\u90a3\u5168\u8499\u6069\u5e08\u4e0e\u9053\u957f\u7684\u6559\u8bf2\u3002\u8fd9\u51e0\u5929\u9053\u957f\u82e5\u662f\u6709\u7a7a\uff0c\u8bf7\u4f60\u518d\u6307\u70b9\u5f1f\u5b50\u51e0\u624b\u3002\u201d \u3000\u3000\u6728\u6851\u7b11\u9053\uff1a\u201c\u4f60\u966a\u6211\u4e0b\u68cb\uff0c\u5411\u6765\u662f\u4e0d\u80af\u767d\u8d39\u529f\u592b\u7684\u3002\u4e0d\u8fc7\u6211\u6559\u4f60\u4e9b\u751a\u4e48\u5462\uff1f\u4f60\u6b66\u529f\u65e9\u80dc\u8fc7\u6211\u5566\uff0c\u8fd8\u662f\u4f60\u6559\u6211\u51e0\u62db\u5427\u3002\u4f60\u82e5\u8981\u6211\u6559\u51e0\u8def\u68cb\u9053\u4e0a\u7684\u53d8\u5316\uff0c\u90a3\u5012\u53ef\u4ee5\u3002\u201d\u4ed6\u8d8a\u4e0b\u8d8a\u662f\u5f97\u610f\uff0c\u53c8\u9053\uff1a\u201c\u6b66\u529f\u597d\uff0c\u5f53\u7136\u4e0d\u5bb9\u6613\uff0c\u4f46\u4f60\u4eba\u54c1\u7aef\u65b9\uff0c\u66f4\u662f\u96be\u5f97\u3002\u5c11\u5e74\u4eba\u80fd\u591f\u4e0d\u6b3a\u6697\u5ba4\uff0c\u5bf9\u540c\u884c\u5c11\u5973\u89c4\u89c4\u77e9\u77e9\u7684\uff0c\u6211\u548c\u4f60\u5d14\u53d4\u53d4\u90fd\u8d5e\u4e0d\u7edd\u53e3\u5462\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6697\u53eb\u60ed\u6127\uff0c\u8138\u4e0a\u4e00\u9635\u53d1\u70e7\uff0c\u5fc3\u60f3\u8981\u662f\u81ea\u5df1\u8ddf\u9752\u9752\u6709\u751a\u4e48\u4eb2\u70ed\u4e3e\u52a8\uff0c\u5c82\u4e0d\u662f\u5168\u8ba9\u4ed6\u77a7\u4e86\u53bb\uff1f\u600e\u4e48\u4ed6\u4ece\u65c1\u7aa5\u63a2\uff0c\u81ea\u5df1\u7adf\u6ca1\u53d1\u89c9\uff1f\u8fd9\u4f4d\u9053\u957f\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u5b9e\u5728\u662f\u9ad8\u660e\u4e4b\u6781\u4e86\u3002 \u3000\u3000\u53c8\u4e0b\u6570\u5b50\uff0c\u6728\u6851\u5728\u897f\u8fb9\u89d2\u4e0a\u5ffd\u843d\u4e00\u5b50\uff0c\u90a3\u672c\u662f\u8881\u627f\u5fd7\u7684\u767d\u68cb\u4e4b\u5730\uff0c\u9ed1\u68cb\u5b64\u5b50\u4fb5\u5165\uff0c\u53ef\u8bf4\u662f\u5e72\u5192\u5947\u9669\u3002\u4ed6\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u6211\u8fd9\u4e00\u624b\u662f\u6709\u540d\u5802\u7684\u3002\u8001\u9053\u8fc7\u5f97\u51e0\u5929\uff0c\u5c31\u8981\u5230\u897f\u85cf\u53bb\u3002\u8fd9\u4e00\u5b50\u6df1\u5165\u91cd\u5730\uff0c\u6210\u8d25\u7978\u798f\uff0c\u5927\u662f\u96be\u6599\u3002\u201d\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u9053\u957f\u4e07\u91cc\u8fe2\u8fe2\u7684\u8fdc\u53bb\u897f\u85cf\u5e72\u751a\u4e48\uff1f\u201d\u6728\u6851\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u53bb\u627e\u4e00\u4ef6\u4e1c\u897f\u3002\u90a3\u662f\u5148\u5e08\u7684\u9057\u7269\u3002\u8fd9\u4ef6\u7269\u4e8b\u627e\u4e0d\u5230\uff0c\u672c\u6765\u4e5f\u4e0d\u6253\u7d27\uff0c\u4f46\u82e5\u7ed9\u53e6\u4e00\u4eba\u5f97\u53bb\u4e86\uff0c\u90a3\u53ef\u5927\u5927\u7684\u4e0d\u59a5\u3002\u597d\u6bd4\u4e0b\u68cb\uff0c\u8fd9\u662f\u62a2\u5148\u624b\u3002\u8001\u9053\u82e5\u662f\u5931\u5148\uff0c\u4e00\u76d8\u68cb\u5c31\u8f93\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u539f\u6765\u5bf9\u65b9\u65e9\u5df2\u53bb\u4e86\u51e0\u5e74\uff0c\u6211\u8fd9\u51e0\u5929\u624d\u77e5\uff0c\u73b0\u4e0b\u9a6c\u4e0a\u8d76\u53bb\uff0c\u4e5f\u5df2\u843d\u540e\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u8138\u6709\u5fe7\u8272\uff0c\u6d51\u4e0d\u662f\u5e73\u65f6\u6f47\u6d12\u81ea\u82e5\u7684\u6a21\u6837\uff0c\u77e5\u4ed6\u6b64\u884c\u5173\u7cfb\u91cd\u5927\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u968f\u9053\u957f\u540c\u53bb\u3002\u54b1\u4eec\u51e0\u65f6\u52a8\u8eab\uff1f\u201d \u3000\u3000\u6728\u6851\u6447\u6447\u5934\uff1a\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u8fd9\u4e8b\u4f60\u53ef\u5e2e\u4e0d\u4e0a\u5fd9\u3002\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5385\u5916\u5fae\u6709\u58f0\u54cd\uff0c\u77e5\u9053\u5c4b\u9876\u8dc3\u4e0b\u4e86\u4e09\u4e2a\u4eba\u6765\uff0c\u8881\u627f\u5fd7\u89c1\u6728\u6851\u4e0d\u52a8\u58f0\u8272\uff0c\u4e5f\u5c31\u4e0d\u7406\uff0c\u7ee7\u7eed\u4e0b\u68cb\u3002 \u3000\u3000\u6728\u6851\u9053\uff1a\u201c\u4f60\u5e08\u5ac2\u521a\u624d\u7684\u4e3e\u52a8\u6211\u90fd\u89c1\u5230\u4e86\u3002\u4f60\u653e\u5fc3\uff0c\u660e\u5929\u6211\u5e2e\u4f60\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u80fd\u8ddf\u5e08\u54e5\u5e08\u5ac2\u52a8\u624b\uff0c\u53ea\u6c42\u9053\u957f\u8bbe\u6cd5\u6392\u89e3\u3002\u5f1f\u5b50\u81ea\u53ef\u8ba4\u9519\u8d54\u7f6a\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u6015\u751a\u4e48\uff1f\u52a8\u624b\u6253\u597d\u5566\uff0c\u8f93\u4e0d\u4e86\uff01\u4f60\u5e08\u7236\u602a\u8d77\u4e0a\u6765\uff0c\u5c31\u8bf4\u662f\u6211\u53eb\u6253\u7684\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5c4b\u9876\u4e0a\u53c8\u7a9c\u4e0b\u56db\u4e2a\u4eba\u6765\uff0c\u968f\u89c9\u4e00\u9635\u52b2\u98ce\uff0c\u56db\u679a\u94a2\u9556\u6fc0\u5c04\u800c\u81f3\u3002\u6728\u6851\u968f\u624b\u63a5\u4f4f\uff0c\u77a7\u4e5f\u4e0d\u77a7\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u53ea\u5f53\u6ca1\u8fd9\u4e00\u4f1a\u4e8b\u3002\u5385\u5916\u4e03\u4eba\u4e00\u9f50\u8dc3\u4e86\u8fdb\u6765\uff0c\u624b\u4e2d\u90fd\u62ff\u7740\u5175\u5203\u3002 \u3000\u3000\u6728\u6851\u7b11\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u4e00\u53e3\u6c14\u5403\u6389\u4e03\u5b50\uff1f\u201d\u8881\u627f\u5fd7\u4f1a\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u8bd5\u8bd5\u3002\u201d\u8fd9\u65f6\u4e03\u4eba\u4e2d\u6709\u4e24\u4eba\u53bb\u6276\u8d77\u5730\u4e0a\u7684\u592a\u767d\u4e09\u82f1\uff0c\u5176\u4f59\u4e94\u4eba\u5404\u633a\u5200\u5251\uff0c\u51b2\u5c06\u8fc7\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6293\u8d77\u4e00\u628a\u68cb\u5b50\uff0c\u6492\u4e86\u51fa\u53bb\uff0c\u53ea\u542c\u5f97\u7bf7\u7bf7\u58f0\u54cd\uff0c\u4e03\u540d\u654c\u4eba\u9f50\u88ab\u6253\u4e2d\u7a74\u9053\uff0c\u545b\u5577\u5577\u7684\u4e00\u9635\u54cd\uff0c\u5175\u5203\u6492\u4e86\u4e00\u5730\u3002\u6728\u6851\u70b9\u5934\u9053\uff1a\u201c\u5927\u6709\u957f\u8fdb\uff0c\u5927\u6709\u957f\u8fdb\uff01\u201d \u3000\u3000\u5b9b\u513f\u521a\u670d\u4f8d\u9752\u9752\u7761\u4e0b\uff0c\u542c\u5f97\u54cd\u58f0\uff0c\u5fd9\u5954\u51fa\u6765\uff0c\u53ea\u89c1\u4e8c\u4eba\u4ecd\u5728\u51dd\u795e\u4e0b\u68cb\uff0c\u5730\u4e0b\u5374\u5012\u4e86\u4e03\u540d\u5927\u6c49\u3002\u5979\u4e5f\u4e0d\u591a\u95ee\uff0c\u53ec\u6765\u5bb6\u4e01\uff0c\u547d\u5c06\u4e03\u4eba\u548c\u592a\u767d\u4e09\u82f1\u90fd\u7ed1\u7f1a\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u6728\u6851\u4fb5\u5165\u897f\u9685\u7684\u9ed1\u68cb\u5df2\u53d7\u91cd\u91cd\u56f4\u56f0\uff0c\u773c\u89c1\u5df2\u9677\u7edd\u5883\uff0c\u8881\u627f\u5fd7\u5ffd\u7136\u60f3\u8d77\uff1a\u201c\u9053\u957f\u628a\u8fd9\u5757\u68cb\u6bd4\u4f5c\u4ed6\u897f\u85cf\u4e4b\u884c\uff0c\u82e5\u662f\u6211\u5c06\u4ed6\u8fd9\u7247\u68cb\u5b50\u6740\u4e86\uff0c\u53ea\u6015\u4e8e\u4ed6\u6b64\u884c\u4e0d\u5409\u3002\u201d\u6c89\u541f\u7247\u523b\uff0c\u8f6c\u53bb\u4e1c\u5317\u89d2\u4e0b\u4e86\u4e00\u5b50\u3002\u6728\u6851\u5475\u5475\u5927\u7b11\uff0c\u7eed\u5728\u897f\u9685\u4e0b\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u51f6\u9669\u4e4b\u6781\uff01\u8fd9\u7740\u68cb\u4e00\u4e0b\uff0c\u90a3\u53ef\u6d3b\u4e86\u3002\u4f60\u6740\u6211\u4e0d\u4e86\u5566\uff01\u201d \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u53cc\u65b9\u5b98\u7740\u4e0b\u5b8c\uff0c\u8881\u627f\u5fd7\u8f93\u4e86\u4e94\u5b50\u3002\u6728\u6851\u5f97\u610f\u975e\u51e1\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u6b66\u529f\u662f\u7cbe\u8fdb\u4e86\uff0c\u68cb\u827a\u5374\u6ca1\u751a\u4e48\u8fdb\u5c55\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u90a3\u662f\u9053\u957f\u5999\u7740\u53e0\u751f\uff0c\u53d8\u5316\u7cbe\u5965\uff0c\u5f1f\u5b50\u62b5\u6321\u4e0d\u4f4f\u3002\u201d\u6728\u6851\u5475\u5475\u5927\u7b11\uff0c\u6253\u4ece\u5fc3\u91cc\u559c\u6b22\u51fa\u6765\uff0c\u81ea\u5439\u81ea\u64c2\u4e86\u4e00\u4f1a\uff0c\u624d\u8f6c\u5934\u5bf9\u5b9b\u513f\u9053\uff1a\u201c\u4f60\u53eb\u4eba\u641c\u641c\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5b9b\u513f\u547d\u4f17\u5bb6\u4e01\u5728\u5341\u4eba\u8eab\u4e0a\u641c\u67e5\uff0c\u9664\u4e86\u6697\u5668\u94f6\u4e24\u4e4b\u5916\uff0c\u641c\u51fa\u51e0\u5c01\u4e66\u4fe1\u3001\u51e0\u518c\u6697\u8bed\u5207\u53e3\u7684\u6284\u672c\u3002\u4e66\u4fe1\u4e2d\u6709\u4e00\u5c01\u662f\u6ee1\u6e05\u4e5d\u738b\u591a\u5c14\u886e\u5199\u4fe1\u7ed9\u5317\u4eac\u7687\u5b98\u53f8\u793c\u592a\u76d1\u66f9\u5316\u6df3\u7684\uff0c\u8bf4\u9053\u5173\u53e3\u76d8\u67e5\u4e25\u5bc6\uff0c\u662f\u4ee5\u7279\u5730\u7ed5\u9053\uff0c\u4ece\u6d77\u4e0a\u6d3e\u9063\u4f7f\u8005\u524d\u6765\uff0c\u673a\u5bc6\u5927\u4e8b\uff0c\u53ef\u4e0e\u6301\u4fe1\u7684\u4f7f\u8005\u6d2a\u80dc\u6d77\u6d3d\u5546\u4e91\u4e91\u3002 \u3000\u3000\u6728\u6851\u5927\u6012\uff0c\u53eb\u9053\uff1a\u201c\u5978\u8d3c\u8d8a\u6765\u8d8a\u5927\u80c6\u5566\uff0c\u54fc\uff0c\u8fde\u7687\u5bab\u91cc\u7684\u592a\u76d1\u4e5f\u4e32\u901a\u4e86\u3002\u201d\u53f3\u811a\u4e00\u8d77\uff0c\u5c06\u4e00\u540d\u5978\u7ec6\u8e22\u5f97\u8111\u6d46\u8ff8\u88c2\u3002 \u3000\u3000\u4ed6\u4f38\u811a\u53c8\u5f85\u518d\u8e22\uff0c\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6162\u6765\uff0c\u9053\u957f\uff01\u4e14\u5f85\u5f1f\u5b50\u4ed4\u7ec6\u76d8\u95ee\u3002\u201d\u6728\u6851\u6012\u6c14\u4e0d\u606f\uff0c\u53c8\u8981\u6495\u4fe1\uff0c\u4e5f\u7ed9\u8881\u627f\u5fd7\u529d\u4f4f\u3002\u6728\u6851\u9053\uff1a\u201c\u8bdd\u5c31\u4f9d\u4f60\uff0c\u660e\u5929\u53ef\u5f97\u966a\u6211\u4e0b\u4e09\u76d8\u68cb\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u53ea\u8981\u9053\u957f\u6709\u5174\uff0c\u8fde\u4e0b\u5341\u76d8\uff0c\u90a3\u4e5f\u65e0\u59a8\u3002\u201d\u6728\u6851\u5927\u559c\uff0c\u968f\u7740\u5bb6\u4e01\u8fdb\u5185\u7761\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u770b\u4e86\u4e66\u4fe1\u548c\u5207\u53e3\u6284\u672c\u7b49\u7269\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u60f3\uff1a\u201c\u7239\u7239\u7684\u5927\u4ec7\u5c1a\u672a\u5f97\u62a5\uff0c\u4ed7\u7740\u8fd9\u4e9b\u5bc6\u4ef6\uff0c\u6b63\u597d\u6df7\u8fdb\u5bab\u53bb\u884c\u523a\u660f\u541b\uff0c\u4e3a\u7239\u7239\u62a5\u4ec7\u3002\u201d\u4e8e\u662f\u628a\u4e00\u4eba\u7a74\u9053\u89e3\u4e86\uff0c\u95ee\u4ed6\u8c01\u662f\u6d2a\u80dc\u6d77\u3002\u90a3\u4eba\u5411\u4e00\u4e2a\u4e09\u5341\u591a\u5c81\u3001\u767d\u51c0\u9762\u76ae\u7684\u4eba\u4e00\u6307\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5c06\u6d2a\u80dc\u6d77\u7a74\u9053\u89e3\u5f00\u76d8\u95ee\u3002\u90a3\u6d2a\u80dc\u6d77\u53ea\u662f\u5014\u5f3a\u4e0d\u8bf4\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff0c\u770b\u6765\u4ed6\u5728\u540c\u515a\u9762\u524d\uff0c\u51b3\u4e0d\u80af\u5410\u9732\u4e00\u5b57\u534a\u53e5\uff0c\u4e8e\u662f\u547d\u5bb6\u4e01\u5c06\u4ed6\u5e26\u5165\u4e66\u623f\u4e4b\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u95ee\u4f60\u8bdd\uff0c\u4f60\u82e5\u662f\u8001\u8001\u5b9e\u5b9e\u56de\u7b54\uff0c\u6216\u8005\u8fd8\u53ef\u7ed9\u4f60\u4e00\u6761\u751f\u8def\uff0c\u53ea\u8981\u7a0d\u6709\u9690\u7792\uff0c\u6211\u53eb\u4f60\u5206\u4f5c\u51e0\u5929\uff0c\u6162\u6162\u53d7\u7f6a\u800c\u6b7b\u3002\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u6012\u9053\uff1a\u201c\u4f60\u90a3\u5996\u9053\u4f7f\u90aa\u6cd5\u8ff7\u4eba\uff0c\u6211\u867d\u6b7b\u4ea6\u4e0d\u5fc3\u670d\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u81ea\u4ee5\u4e3a\u6b66\u529f\u7cbe\u5f3a\uff0c\u662f\u4e0d\u662f\uff1f\u4f60\u662f\u6c49\u4eba\uff0c\u5374\u53bb\u505a\u756a\u90a6\u5974\u624d\uff0c\u8fd9\u662f\u7f6a\u6709\u5e94\u5f97\uff0c\u6b7b\u6709\u4f59\u8f9c\u3002\u4f60\u65e2\u4e0d\u670d\uff0c\u6211\u5c31\u8ddf\u4f60\u6bd4\u6bd4\u3002\u4f60\u82e5\u8d62\u4e86\uff0c\u653e\u4f60\u8d70\u8def\u3002\u4f60\u82e5\u8f93\u4e86\uff0c\u4e00\u5207\u53ef\u5f97\u4ece\u5b9e\u8bf4\u6765\u3002\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u5927\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u521a\u624d\u4e5f\u4e0d\u77e5\u600e\u6837\uff0c\u7a81\u7136\u7a74\u9053\u4e0a\u4e00\u9ebb\uff0c\u5c31\u6b64\u8dcc\u5012\uff0c\u5fc5\u662f\u5996\u9053\u884c\u4f7f\u5996\u6cd5\u3002\u90a3\u5996\u9053\u65e2\u5df2\u4e0d\u5728\uff0c\u8fd9\u540e\u751f\u5c11\u5e74\u5982\u4f55\u662f\u6211\u5bf9\u624b\uff1f\u4e50\u5f97\u4e00\u5207\u7b54\u5e94\u3002\u201d\u7b54\u9053\uff1a\u201c\u597d\uff0c\u53ea\u8981\u4f60\u6253\u8d25\u6211\uff0c\u4e0d\u8bba\u4f60\u95ee\u751a\u4e48\uff0c\u6211\u90fd\u5b9e\u8bf4\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8d70\u8fd1\u8eab\u53bb\uff0c\u53cc\u624b\u6267\u4f4f\u7ed1\u5728\u4ed6\u8eab\u4e0a\u7684\u7ef3\u7d22\uff0c\u4e00\u62c9\u4e00\u626f\uff0c\u7ef3\u7d22\u767b\u65f6\u65ad\u6210\u6570\u622a\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u4e00\u6014\uff0c\u4ed6\u8eab\u4e0a\u6240\u7f1a\uff0c\u90fd\u662f\u4e1d\u9ebb\u7ede\u6210\u7684\u7c97\u7d22\uff0c\u4ed6\u7a74\u9053\u89e3\u5f00\u540e\uff0c\u66fe\u6697\u4e2d\u7528\u529b\u6323\u624e\uff0c\u53ea\u6323\u5f97\u7ef3\u7d22\u8d8a\u7f1a\u8d8a\u7d27\uff0c\u54ea\u77e5\u8fd9\u5c11\u5e74\u53ea\u968f\u624b\u4e00\u626f\uff0c\u7ef3\u7d22\u7acb\u65ad\uff0c\u672c\u6765\u5c0f\u89d1\u4e4b\u5fc3\uff0c\u90fd\u53d8\u6210\u4e86\u754f\u60e7\u4e4b\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u6837\u6bd4\u6cd5\uff1f\u54b1\u4eec\u5230\u5916\u9762\u53bb\u5427\uff0c\u662f\u6bd4\u5175\u5203\u8fd8\u662f\u6bd4\u62f3\u811a\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u6211\u7528\u68cb\u5b50\u6253\u4e2d\u4f60\u7a74\u9053\uff0c\u4f60\u7adf\u4ee5\u4e3a\u662f\u90a3\u9053\u957f\u4f7f\u5996\u6cd5\uff0c\u771f\u662f\u597d\u7b11\u3002\u770b\u4f60\u8dc3\u8fdb\u5385\u6765\u7684\u8eab\u6cd5\uff0c\u662f\u5c11\u6797\u6d3e\u4e1c\u652f\u7684\u5185\u5bb6\u529f\u592b\u4e86\u3002\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u53c8\u662f\u4e00\u60ca\uff0c\u5165\u5385\u65f6\u89c1\u4e24\u4eba\u51dd\u795e\u4e0b\u68cb\uff0c\u773c\u76ae\u4e5f\u4e0d\u62ac\u4e00\u4e0b\uff0c\u5b9b\u82e5\u4e0d\u89c9\uff0c\u54ea\u77e5\u81ea\u5df1\u7684\u884c\u52a8\u5168\u5df2\u6e05\u6e05\u695a\u695a\u843d\u5728\u4ed6\u773c\u91cc\uff0c\u8fde\u95e8\u6d3e\u5bb6\u6570\u4e5f\u8bf4\u5f97\u4e0d\u9519\uff0c\u4fbf\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e5f\u4e0d\u7528\u51fa\u53bb\uff0c\u5c31\u5728\u8fd9\u91cc\u63a8\u63a8\u624b\u5427\u3002\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8bf7\u6559\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u7b49\u4f60\u80dc\u4e86\u6211\uff0c\u81ea\u7136\u4f1a\u5bf9\u4f60\u8bf4\u3002\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u53cc\u624b\u62a4\u80f8\uff0c\u8eab\u5b50\u5fae\u5f13\uff0c\u6446\u597d\u4e86\u67b6\u5b50\uff0c\u7b49\u4ed6\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5e76\u4e0d\u7406\u4f1a\uff0c\u78e8\u58a8\u62c8\u6beb\uff0c\u644a\u5f00\u4e00\u5f20\u767d\u7eb8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u5199\u5b57\uff0c\u5199\u751a\u4e48\u5462\uff1f\u201d\u6d2a\u80dc\u6d77\u89c1\u4ed6\u8bf4\u8981\u6bd4\u6b66\uff0c\u5374\u5199\u8d77\u5b57\u6765\uff0c\u5f88\u611f\u8be7\u5f02\uff0c\u53c8\u5750\u4e86\u4e0b\u6765\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u522b\u5750\uff01\u201d\u4f38\u51fa\u5de6\u638c\uff0c\u9053\uff1a\u201c\u4f60\u53ea\u8981\u628a\u6211\u63a8\u5f97\u6643\u4e86\u4e00\u6643\uff0c\u6211\u5199\u7684\u5b57\u6709\u4e00\u7b14\u626d\u66f2\u6296\u52a8\uff0c\u5c31\u7b97\u4f60\u8d62\u4e86\uff0c\u7acb\u523b\u653e\u4f60\u8d70\u8def\u3002\u8981\u662f\u6211\u5199\u6ee1\u4e86\u4e00\u5f20\u7eb8\uff0c\u4f60\u8fd8\u662f\u63a8\u4e0d\u52a8\u6211\uff0c\u90a3\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u6211\u518d\u4e0d\u8ba4\u8f93\uff0c\u8fd8\u8981\u8138\u4e48\uff1f\u201d \u3000\u3000\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50\u521d\u51fa\u9053\u513f\uff0c\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u81ea\u6043\u624b\u4e0a\u529b\u9053\u4e86\u5f97\uff0c\u7adf\u7136\u5bf9\u6211\u5982\u6b64\u5c0f\u770b\uff0c\u554a\uff0c\u662f\u4e86\uff0c\u4ed6\u89c1\u6211\u751f\u5f97\u6587\u79c0\uff0c\u53ea\u9053\u6211\u6ca1\u6709\u672c\u4e8b\uff0c\u4e14\u53eb\u4ed6\u8bd5\u8bd5\u3002\u201d\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u6bd4\u4e0d\u5927\u516c\u5e73\u5427\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4e0d\u76f8\u5e72\u3002\u6211\u5199\u4e86\uff0c\u4f60\u6765\u5427\u3002\u201d\u53f3\u624b\u63e1\u7ba1\uff0c\u5199\u4e86\u201c\u6062\u590d\u4e4b\u8ba1\u201d\u56db\u5b57\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u6f5c\u8fd0\u5185\u529b\uff0c\u53cc\u638c\u4e00\u62db\u201c\u6392\u5c71\u5012\u6d77\u201d\uff0c\u731b\u5411\u8881\u627f\u5fd7\u5de6\u638c\u63a8\u53bb\uff0c\u53ea\u89c9\u4ed6\u5de6\u638c\u5fae\u4fa7\uff0c\u5df2\u628a\u81ea\u5df1\u7684\u52b2\u529b\u6ed1\u4e86\u5f00\u53bb\u3002\u6d2a\u80dc\u6d77\u4e00\u51fb\u4e0d\u4e2d\uff0c\u53f3\u638c\u4e0b\u538b\uff0c\u5de6\u638c\u4e0a\u62ac\uff0c\u60f3\u628a\u8881\u627f\u5fd7\u4e00\u6761\u80f3\u81c2\u5939\u5728\u4e2d\u95f4\uff0c\u53ea\u8981\u4e0a\u4e0b\u4e00\u7528\u529b\uff0c\u4ed6\u81c2\u8180\u975e\u65ad\u4e0d\u53ef\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53f3\u624b\u5199\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u62db\u2018\u5347\u5929\u5165\u5730\u2019\uff0c\u4f3c\u4e4e\u662f\u5c71\u4e1c\u6e24\u6d77\u6d3e\u7684\u62db\u6570\u3002\u55ef\uff0c\u90a3\u662f\u2018\u65a9\u86df\u62f3\u2019\u3002\u6e24\u6d77\u6d3e\u51fa\u81ea\u5c11\u6797\u4e1c\u652f\uff0c\u539f\u6765\u9601\u4e0b\u662f\u6e24\u6d77\u6d3e\u3002\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u542c\u4ed6\u5c06\u81ea\u5df1\u7684\u6b66\u529f\u6765\u5386\u8bf4\u5f97\u534a\u70b9\u4e0d\u9519\uff0c\u5fc3\u4e0b\u9a87\u7136\uff0c\u8fd9\u65f6\u4ed6\u53cc\u638c\u5df2\u631f\u4f4f\u5bf9\u65b9\u81c2\u8180\uff0c\u8fde\u8fd0\u51e0\u6b21\u52b2\u529b\uff0c\u5bf9\u65b9\u4e00\u6761\u81c2\u8180\u4fbf\u5982\u751f\u94c1\u94f8\u6210\uff0c\u7eb9\u4e1d\u4e0d\u52a8\u3002\u8881\u627f\u5fd7\u51e0\u53e5\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u81c2\u8180\u4e00\u7f29\uff0c\u5982\u4e00\u5c3e\u6e38\u9c7c\u822c\u4ece\u4ed6\u4e24\u638c\u95f4\u7f29\u4e86\u51fa\u6765\uff0c\u53ea\u542c\u556a\u7684\u4e00\u58f0\uff0c\u4ed6\u5de6\u53f3\u53cc\u638c\u6536\u52bf\u4e0d\u53ca\uff0c\u81ea\u884c\u6253\u4e86\u4e00\u8bb0\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u53c8\u60ca\u53c8\u6012\uff0c\u5c55\u5f00\u672c\u95e8\u7edd\u5b66\uff0c\u53cc\u638c\u98de\u821e\uff0c\u60ca\u6d9b\u9a87\u6d6a\u822c\u653b\u51fa\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5750\u5728\u6905\u4e0a\u53f3\u624b\u4e66\u5199\u4e0d\u505c\uff0c\u5de6\u638c\u6f47\u6d12\u81ea\u5982\uff0c\u628a\u5bf9\u65b9\u6765\u62db\u4e00\u4e00\u5316\u89e3\u3002\u4ed6\u5de6\u81c2\u5ffd\u524d\u5ffd\u540e\uff0c\u5bf9\u6d2a\u80dc\u6d77\u59cb\u7ec8\u6ca1\u77a7\u4e0a\u4e00\u773c\uff0c\u5076\u5c14\u8fd8\u53d1\u51fa\u4e00\u4e24\u4e0b\u53cd\u51fb\uff0c\u4f46\u5de6\u81c2\u4f38\u7f29\u53ea\u5230\u80a9\u7a9d\u4e3a\u6b62\uff0c\u4e0a\u8eab\u7a33\u7a33\u4e0d\u52a8\uff0c\u5bf9\u65b9\u653b\u6765\u65f6\u65e2\u4e0d\u540e\u4ef0\uff0c\u8ffd\u51fb\u5bf9\u65b9\u65f6\u4e5f\u4e0d\u524d\u4fef\u3002 \u3000\u3000\u62c6\u5f97\u826f\u4e45\uff0c\u6d2a\u80dc\u6d77\u4e00\u5957\u201c\u65a9\u86df\u62f3\u201d\u5df2\u4f7f\u5230\u5c3d\u5934\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u7684\u2018\u65a9\u86df\u62f3\u2019\u8fd8\u6709\u4e5d\u62db\uff0c\u6211\u8fd9\u7bc7\u6587\u7ae0\u5374\u8981\u5199\u5b8c\u4e86\u3002\u597d\uff0c\u6211\u7b49\u4f60\u4e00\u4e0b\uff0c\u4f60\u53d1\u4e00\u62db\uff0c\u6211\u5199\u4e00\u4e2a\u5b57\uff01\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u5fc3\u4e0b\u66f4\u60ca\uff0c\u6697\u60f3\u6b64\u4eba\u600e\u4e48\u5bf9\u6211\u62f3\u6cd5\u5982\u6b64\u719f\u6089\uff0c\u96be\u9053\u7adf\u662f\u672c\u95e8\u4e2d\u4eba\u4e0d\u6210\uff1f\u4e0d\u8fc7\u4ed6\u7684\u638c\u6cd5\u6211\u4ece\u672a\u89c1\u8fc7\uff0c\u8981\u8bf4\u662f\u672c\u95e8\u4e4b\u4eba\uff0c\u90a3\u53c8\u51b3\u8ba1\u4e0d\u662f\u3002\u5f53\u4e0b\u628a\u201c\u65a9\u86df\u62f3\u201d\u6700\u540e\u4e5d\u62db\u4f7f\u4e86\u51fa\u6765\uff0c\u51dd\u805a\u529f\u529b\uff0c\u6bcf\u4e00\u62db\u90fd\u5982\u5200\u5288\u65a7\u524a\u4e00\u822c\uff0c\u51cc\u5389\u5f02\u5e38\uff0c\u8fd9\u65f6\u5df2\u4e0d\u6c42\u6253\u5012\u5bf9\u65b9\uff0c\u53ea\u76fc\u5c06\u4ed6\u8eab\u5b50\u9707\u5f97\u4e00\u9707\uff0c\u53f3\u624b\u5199\u7684\u5b57\u6709\u4e00\u7b14\u6d82\u6c61\u626d\u66f2\uff0c\u4e5f\u5c31\u53ef\u4ee5\u501f\u53e3\u8131\u8eab\u4e86\u3002\u53ea\u542c\u8881\u627f\u5fd7\u8bf5\u9053\uff1a\u201c\u2018\u4f46\u4e2d\u6709\u6240\u5371\uff0c\u4e0d\u6562\u4e0d\u544a\u2019\u3002\u6700\u540e\u8fd8\u6709\u4e00\u4e2a\u2018\u544a\u2019\u5b57\uff01\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u4f7f\u5230\u6700\u540e\u4e24\u62db\uff0c\u4ecd\u7136\u63a8\u4ed6\u4e0d\u52a8\uff0c\u7a81\u7136\u4f4e\u5934\uff0c\u53cc\u8098\u5f2f\u8fc7\uff0c\u81c2\u8180\u653e\u5728\u5934\u524d\uff0c\u731b\u529b\u5411\u4ed6\u51b2\u53bb\uff0c\u5fc3\u60f3\u4f60\u6b66\u529f\u518d\u597d\uff0c\u8fd9\u6905\u5b50\u603b\u4f1a\u88ab\u6211\u63a8\u52a8\u3002\u54ea\u77e5\u4ed6\u8fd9\u4e00\u4f7f\u86ee\u52b2\uff0c\u53ea\u53d1\u4e0d\u6536\uff0c\u72af\u4e86\u6b66\u5bb6\u7684\u5927\u5fcc\uff0c\u53ea\u89c9\u8098\u4e0b\u4e0d\u77e5\u4ece\u54ea\u91cc\u6765\u7684\u4e00\u80a1\u5927\u529b\uff0c\u84e6\u5730\u5411\u4e0a\u6258\u8d77\uff0c\u767b\u65f6\u7acb\u8db3\u4e0d\u7a33\uff0c\u5411\u540e\u4fbf\u4ef0\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u5728\u7a7a\u4e2d\u8fde\u7ffb\u4e86\u4e09\u4e2a\u7b4b\u6597\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u5750\u5012\u5728\u5730\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u6478\u6e05\u81ea\u5df1\u539f\u6765\u5df2\u88ab\u5bf9\u65b9\u6253\u5012\u4e86\uff0c\u5fd9\u53cc\u8db3\u4e00\u987f\uff0c\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7126\u5b9b\u513f\u62ff\u4e86\u4e00\u628a\u7d2b\u7802\u8336\u58f6\uff0c\u8d70\u8fdb\u4e66\u623f\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u8fd9\u662f\u65b0\u7119\u7684\u72ee\u5cf0\u9f99\u4e95\uff0c\u4f60\u559d\u4e00\u676f\u5427\u3002\u201d\u8bf4\u7740\u628a\u8336\u7b5b\u5728\u676f\u91cc\u3002 \u3000\u3000\u8881\u627f\u5fd7\u63a5\u8fc7\u8336\u676f\uff0c\u89c1\u8336\u6c34\u78a7\u7eff\u5982\u7fe1\u7fe0\uff0c\u4e00\u80a1\u6e05\u9999\u5e7d\u5e7d\u5165\u9f3b\uff0c\u559d\u4e86\u4e00\u53e3\uff0c\u8d5e\u9053\uff1a\u201c\u597d\u8336\uff01\u201d\u62ff\u8d77\u684c\u4e0a\u7684\u90a3\u5f20\u7eb8\uff0c\u8bf4\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u8bf7\u4f60\u77a7\u77a7\uff0c\u7eb8\u4e0a\u53ef\u6709\u751a\u4e48\u7834\u7b14\u6d82\u6c61\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u63a5\u4e86\u8fc7\u6765\uff0c\u8f7b\u8f7b\u5ff5\u8bf5\u4e86\u8d77\u6765\uff1a\u201c\u6062\u590d\u4e4b\u8ba1\uff0c\u4e0d\u5916\u81e3\u6614\u5e74\u2018\u4ee5\u8fbd\u4eba\u5b88\u8fbd\u571f\uff0c\u4ee5\u8fbd\u571f\u517b\u8fbd\u4eba\u2019\uff0c\u2018\u5b88\u4e3a\u6b63\u7740\uff0c\u6218\u4e3a\u5947\u7740\uff0c\u548c\u4e3a\u65c1\u7740\u2019\u4e4b\u8bf4\u3002\u6cd5\u5728\u6e10\u4e0d\u5728\u9aa4\uff0c\u5728\u5b9e\u4e0d\u5728\u865a\u3002\u6b64\u81e3\u4e0e\u8bf8\u8fb9\u81e3\u6240\u80fd\u4e3a\u3002\u81f3\u7528\u4eba\u4e4b\u4eba\uff0c\u4e0e\u4e3a\u4eba\u7528\u4e4b\u4eba\uff0c\u7686\u81f3\u5c0a\u53f8\u5176\u94a5\u3002\u4f55\u4ee5\u4efb\u800c\u52ff\u8d30\uff0c\u4fe1\u800c\u52ff\u7591\uff1f\u76d6\u9a6d\u8fb9\u81e3\u4e0e\u5ef7\u81e3\u5f02\u3002\u519b\u4e2d\u53ef\u60ca\u53ef\u7591\u8005\u6b8a\u591a\uff0c\u4f46\u5f53\u8bba\u6210\u8d25\u4e4b\u5927\u5c40\uff0c\u4e0d\u5fc5\u6458\u4e00\u8a00\u4e00\u884c\u4e4b\u5fae\u6687\u3002\u4e8b\u4efb\u65e2\u91cd\uff0c\u4e3a\u6028\u5b9e\u591a\u3002\u8bf8\u6709\u5229\u4e8e\u5c01\u7586\u8005\uff0c\u7686\u4e0d\u5229\u4e8e\u6b64\u8eab\u8005\u4e5f\u3002\u51b5\u56fe\u654c\u4e4b\u6025\uff0c\u654c\u4ea6\u4ece\u800c\u95f4\u4e4b\u3002\u662f\u4ee5\u4e3a\u8fb9\u81e3\u751a\u96be\u3002\u965b\u4e0b\u7231\u81e3\u77e5\u81e3\uff0c\u81e3\u4f55\u5fc5\u8fc7\u7591\u60e7\uff1f\u4f46\u4e2d\u6709\u6240\u5371\uff0c\u4e0d\u6562\u4e0d\u544a\u3002\u201d \u3000\u3000\u5979\u4e8e\u6587\u4e2d\u6240\u6307\uff0c\u4e0d\u751a\u4e86\u4e86\uff0c\u89c1\u8fd9\u4e00\u767e\u591a\u5b57\u4e66\u6cd5\u751a\u662f\u5e73\u5e73\uff0c\u7ed3\u6784\u7ae0\u6cd5\uff0c\u53ef\u8bf4\u9887\u4e3a\u62d9\u52a3\uff0c\u4f46\u4e00\u7b14\u4e00\u5212\uff0c\u529b\u900f\u7eb8\u80cc\uff0c\u5e76\u65e0\u4e1d\u6beb\u626d\u66f2\u6d82\u6c61\uff0c\u8bf4\u9053\uff1a\u201c\u6e05\u6e05\u695a\u695a\uff0c\u4e00\u7b14\u4e0d\u82df\uff0c\u8fd9\u662f\u4e00\u7bc7\u751a\u4e48\u6587\u7ae0\uff1f\u201d\u8881\u627f\u5fd7\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u8881\u7763\u5e08\u5f53\u5e74\u5b88\u8fbd\u4e4b\u65f6\uff0c\u4e0a\u7ed9\u7687\u5e1d\u7684\u594f\u7ae0\u3002\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u8881\u76f8\u516c\u6587\u6b66\u5168\u624d\uff0c\u7559\u5fc3\u8fb9\u4e8b\uff0c\u4e8e\u8fd9\u4e9b\u594f\u7ae0\u4e5f\u70c2\u719f\u4e8e\u80f8\u3002\u201d\u8881\u627f\u5fd7\u6447\u5934\u9053\uff1a\u201c\u6211\u4e5f\u53ea\u8bfb\u8fc7\u8fd9\u51e0\u7bc7\uff0c\u90a3\u662f\u6211\u4ece\u5c0f\u4fbf\u80cc\u719f\u4e86\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u8881\u5d07\u7115\u5f53\u5e74\u5b88\u536b\u8fbd\u8fb9\uff0c\u6297\u5fa1\u6ee1\u6d32\u5165\u4fb5\uff0c\u6df1\u77e5\u5d07\u796f\u6027\u683c\u591a\u7591\uff0c\u6613\u542c\u5c0f\u4eba\u4e4b\u8a00\uff0c\u56e0\u6b64\u4e0a\u4e86\u8fd9\u7bc7\u594f\u7ae0\u3002\u540e\u6765\u5d07\u796f\u679c\u7136\u4e2d\u4e86\u6ee1\u6d32\u7687\u592a\u6781\u7684\u53cd\u95f4\u4e4b\u8ba1\uff0c\u53c8\u4fe1\u4e86\u5978\u81e3\u7684\u8a00\u8bed\uff0c\u5c06\u8881\u5d07\u7115\u6740\u4e86\u3002\u8881\u5d07\u7115\u6240\u7591\u60e7\u7684\u4e8b\u60c5\uff0c\u7686\u4e0d\u5e78\u800c\u4e00\u4e00\u6599\u4e2d\u3002\u8881\u627f\u5fd7\u5e74\u5e7c\u65f6\uff0c\u5e94\u677e\u6559\u4ed6\u8bfb\u4e66\u4e60\u5b57\uff0c\u66fe\u5c06\u4ed6\u7236\u4eb2\u8881\u5d07\u7115\u7684\u8bf8\u7bc7\u594f\u7ae0\u8be6\u4e3a\u8bb2\u6388\u3002\u4ed6\u9664\u6b64\u4e4b\u5916\uff0c\u8bfb\u4e66\u65e0\u591a\uff0c\u6b64\u523b\u8981\u5199\u5b57\uff0c\u53c8\u60f3\u8d77\u6ee1\u6d32\u56fe\u8c0b\u65e5\u4e9f\uff0c\u8fb9\u5c06\u65e0\u4eba\uff0c\u968f\u624b\u4fbf\u5199\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7126\u5b9b\u513f\u9053\uff1a\u201c\u8881\u76f8\u516c\u8fd9\u5e45\u5b57\uff0c\u5c31\u7ed9\u4e86\u6211\u5427\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u7684\u5b57\u5b9e\u5728\u96be\u770b\u3002\u521a\u624d\u8ddf\u8fd9\u670b\u53cb\u6253\u8d4c\uff0c\u624d\u597d\u73a9\u5199\u7684\u3002\u7126\u59d1\u5a18\u8981\uff0c\u62ff\u53bb\u4e0d\u59a8\uff0c\u53ef\u4e0d\u80fd\u7ed9\u6709\u5b66\u95ee\u7684\u4eba\u89c1\u5230\uff0c\u8ba9\u4eba\u5bb6\u7b11\u8bdd\u3002\u201d\u7126\u5b9b\u513f\u8c22\u4e86\u6536\u8d77\uff0c\u8d70\u51fa\u4e66\u623f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u6ee1\u6d32\u4e5d\u738b\u6d3e\u4f60\u53bb\u89c1\u66f9\u5316\u6df3\uff0c\u5546\u91cf\u4e9b\u751a\u4e48\u4e8b\uff1f\u201d\u6d2a\u80dc\u6d77\u541e\u541e\u5410\u5410\u7684\u4e0d\u8bf4\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u521a\u624d\u4e0d\u662f\u6253\u4e86\u8d4c\u4e48\uff1f\u4f60\u6709\u6ca1\u63a8\u52a8\u6211\uff1f\u201d\u6d2a\u80dc\u6d77\u4f4e\u5934\u9053\uff1a\u201c\u76f8\u516c\u6b66\u529f\u60ca\u4eba\uff0c\u5c0f\u4eba\u786e\u662f\u95fb\u6240\u672a\u95fb\uff0c\u89c1\u6240\u672a\u89c1\uff0c\u62dc\u670d\u4e4b\u81f3\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u5de6\u4e73\u4e0b\u7b2c\u4e8c\u6839\u808b\u9aa8\u4e00\u5e26\uff0c\u6709\u751a\u4e48\u77e5\u89c9\uff1f\u201d\u6d2a\u80dc\u6d77\u4f38\u624b\u4e00\u6478\uff0c\u60ca\u9053\uff1a\u201c\u90a3\u91cc\u5b8c\u5168\u9ebb\u6728\u4e86\uff0c\u6ca1\u4e00\u70b9\u77e5\u89c9\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u53f3\u8fb9\u8170\u773c\u91cc\u5462\uff1f\u201d\u6d2a\u80dc\u6d77\u4e00\u6309\uff0c\u5ffd\u7136\u201c\u54ce\u5537\u201d\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6478\u5012\u4e0d\u89c9\u751a\u4e48\uff0c\u4e00\u78b0\u53ef\u75db\u5f97\u4e0d\u5f97\u4e86\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\u3002\u201d\u659f\u4e86\u676f\u8336\uff0c\u4e00\u9762\u559d\u8336\uff0c\u4e00\u9762\u7ffb\u5f00\u6848\u5934\u4e00\u672c\u4e66\u6765\u770b\uff0c\u4e0d\u518d\u7406\u4ed6\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u60f3\u8d70\uff0c\u5374\u53c8\u4e0d\u6562\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u8881\u627f\u5fd7\u62ac\u8d77\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u8d70\u4e48\uff1f\u201d\u6d2a\u80dc\u6d77\u8a00\u9053\uff1a\u201c\u76f8\u516c\u653e\u6211\u8d70\u4e86\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u662f\u4f60\u81ea\u5df1\u6765\u7684\u3002\u6211\u53c8\u6ca1\u8bf7\u4f60\u3002\u4f60\u8981\u8d70\uff0c\u6211\u4e5f\u4e0d\u4f1a\u7559\u5ba2\u3002\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u559c\u51fa\u671b\u5916\uff0c\u8dea\u4e0b\u78d5\u5934\uff0c\u7ad9\u8d77\u6765\u4f5c\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u4e0d\u6562\u5fd8\u4e86\u76f8\u516c\u7684\u6069\u5fb7\u3002\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u53c8\u81ea\u770b\u4e66\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u8d70\u5230\u4e66\u623f\u95e8\u53e3\uff0c\u5ffd\u60f3\u51fa\u53bb\u6015\u6709\u4eba\u62e6\u963b\uff0c\u63a8\u5f00\u7a97\u683c\uff0c\u98de\u8eab\u800c\u51fa\uff0c\u56de\u5934\u4e00\u671b\uff0c\u89c1\u8881\u627f\u5fd7\u4ecd\u5728\u770b\u4e66\uff0c\u5e76\u65e0\u8ffd\u51fb\u4e4b\u72b6\uff0c\u8fd9\u624d\u653e\u5fc3\uff0c\u8dc3\u4e0a\u5c4b\u9876\uff0c\u75be\u5954\u800c\u53bb\u3002 \u3000\u3000\u7126\u5b9b\u513f\u81ea\u8881\u627f\u5fd7\u6551\u5979\u7236\u4eb2\u8131\u5374\u5927\u96be\uff0c\u8877\u5fc3\u611f\u6fc0\uff0c\u5fc3\u60f3\u4ed6\u6b66\u529f\u60ca\u4eba\uff0c\u4eca\u540e\u4e5f\u65e0\u53ef\u62a5\u7b54\u4ed6\u4e4b\u5904\uff0c\u53ea\u6709\u4e58\u7740\u4ed6\u7559\u5728\u81ea\u5df1\u5bb6\u91cc\u8fd9\u51e0\u5929\u5c3d\u5fc3\u670d\u4f8d\u3002\u8fd9\u65f6\u6f0f\u5c3d\u66f4\u6b8b\uff0c\u5929\u5c06\u9ece\u660e\uff0c\u5979\u5728\u4e66\u623f\u5916\u6765\u56de\u6570\u6b21\uff0c\u89c1\u95e8\u7f1d\u4e2d\u4ecd\u662f\u900f\u51fa\u5149\u4eae\uff0c\u77e5\u4ed6\u8fd8\u6ca1\u7761\uff0c\u4e8e\u662f\u547d\u5a62\u5973\u5f04\u4e86\u51e0\u8272\u70b9\u5fc3\uff0c\u4eb2\u81ea\u6367\u5411\u4e66\u623f\u3002\u5728\u95e8\u4e0a\u8f7b\u6572\u6570\u4e0b\uff0c\u7136\u540e\u63a8\u95e8\u8fdb\u53bb\uff0c\u53ea\u89c1\u8881\u627f\u5fd7\u62ff\u7740\u4e00\u90e8\u300a\u5fe0\u4e49\u6c34\u6d52\u4f20\u300b\u6b63\u770b\u5f97\u8d77\u52b2\u3002 \u3000\u3000\u7126\u5b9b\u513f\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u8fd8\u4e0d\u5b89\u606f\u4e48\uff1f\u8bf7\u7528\u4e00\u4e9b\u70b9\u5fc3\uff0c\u4fbf\u5b89\u606f\u4e86\uff0c\u597d\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u8d77\u8eab\u9053\u8c22\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u5feb\u8bf7\u5b89\u7761\uff0c\u4e0d\u5fc5\u62db\u547c\u6211\u5566\u3002\u6211\u5728\u8fd9\u91cc\u7b49\u4e00\u4e2a\u4eba\u2026\u2026\u201d\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a97\u683c\u4e00\u52a8\uff0c\u4e00\u4eba\u8df3\u4e86\u8fdb\u6765\u3002\u7126\u5b9b\u513f\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u6e05\u695a\u65f6\uff0c\u539f\u6765\u4fbf\u662f\u6d2a\u80dc\u6d77\u3002 \u3000\u3000\u4ed6\u5728\u8881\u627f\u5fd7\u9762\u524d\u8dea\u5012\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5927\u82f1\u96c4\uff0c\u5c0f\u4eba\u77e5\u9519\u4e86\uff0c\u6c42\u4f60\u6551\u6211\u4e00\u547d\u3002\u201d\u8881\u627f\u5fd7\u4f38\u624b\u76f8\u6276\uff0c\u6d2a\u80dc\u6d77\u8dea\u7740\u4e0d\u80af\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u5c0f\u4eba\u4e00\u5b9a\u6539\u8fc7\u81ea\u65b0\uff0c\u6c42\u8881\u5927\u82f1\u96c4\u9976\u547d\u3002\u201d\u7126\u5b9b\u513f\u5728\u4e00\u65c1\u7741\u5927\u773c\u775b\uff0c\u6115\u7136\u4e0d\u89e3\u3002 \u3000\u3000\u53ea\u89c1\u8881\u627f\u5fd7\u4f38\u624b\u4e00\u6258\uff0c\u6d2a\u80dc\u6d77\u53c8\u662f\u8eab\u4e0d\u7531\u4e3b\u7684\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u5750\u5728\u5730\u4e0b\u3002\u4ed6\u968f\u624b\u4e00\u6478\u814b\u4e0b\uff0c\u8138\u4e0a\u767b\u73b0\u559c\u8272\uff0c\u518d\u6309\u80f8\u95f4\uff0c\u5374\u53c8\u6101\u7709\u91cd\u9501\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u61c2\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u4e00\u8f6c\u5ff5\u95f4\uff0c\u5df2\u660e\u8881\u627f\u5fd7\u4e4b\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5927\u82f1\u96c4\u4f60\u8981\u95ee\u751a\u4e48\uff0c\u5c0f\u4eba\u4e00\u5b9a\u5b9e\u8bf4\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u77e5\u9053\u4ed6\u4eec\u8bf4\u7684\u662f\u673a\u5bc6\u5927\u4e8b\uff0c\u5f53\u5373\u9000\u51fa\u3002 \u3000\u3000\u539f\u6765\u6d2a\u80dc\u6d77\u79bb\u7126\u5bb6\u540e\uff0c\u75be\u5954\u56de\u5bd3\uff0c\u89e3\u5f00\u8863\u670d\u4e00\u770b\uff0c\u53ea\u89c1\u80f8\u524d\u6709\u94dc\u94b1\u5927\u5c0f\u4e00\u4e2a\u7ea2\u5757\uff0c\u6478\u4e0a\u53bb\u6beb\u65e0\u77e5\u89c9\uff0c\u814b\u4e0b\u5374\u6709\u4e09\u4e2a\u8695\u8c46\u5927\u5c0f\u7684\u9ed1\u70b9\uff0c\u89e6\u624b\u5267\u75db\uff0c\u77e5\u9053\u5728\u63a8\u624b\u65f6\u4e0d\u77e5\u4e0d\u89c9\u95f4\u88ab\u5bf9\u624b\u6253\u4f24\u3002\u5f53\u4e0b\u76d8\u819d\u5750\u5728\u5e8a\u4e0a\uff0c\u8fd0\u8d77\u5185\u529f\u7597\u4f24\uff0c\u5c82\u77e5\u4e0d\u8fd0\u6c14\u5012\u4e5f\u7f62\u4e86\uff0c\u4e00\u52a8\u5185\u606f\uff0c\u814b\u4e0b\u5947\u75db\u5f7b\u5fc3\uff0c\u8fde\u5fd9\u8eba\u4e0b\uff0c\u5374\u53c8\u65e0\u4e8b\u3002\u8fd9\u4e48\u4e00\u8fde\u4e09\u6b21\uff0c\u5ffd\u7136\u60f3\u5230\u6b66\u672f\u4e2d\u7684\u9ad8\u6df1\u6b66\u529f\uff0c\u80fd\u5c06\u5bf9\u65b9\u4e4b\u529b\u53cd\u51fb\u8fc7\u6765\uff0c\u53d7\u8005\u91cd\u4f24\u96be\u6cbb\uff0c\u4e0d\u7531\u5f97\u8d8a\u60f3\u8d8a\u6015\uff0c\u53ea\u5f97\u53c8\u8d76\u56de\u6765\u6c42\u6551\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u53d7\u4e86\u4e24\u5904\u4f24\uff0c\u4e00\u5904\u6709\u75db\u695a\u7684\uff0c\u6211\u5df2\u7ed9\u4f60\u6cbb\u597d\uff1b\u53e6\u4e00\u5904\u76ee\u524d\u6ca1\u6709\u77e5\u89c9\uff0c\u4e09\u4e2a\u6708\u4e4b\u540e\uff0c\u9ebb\u6728\u4e4b\u5904\u6162\u6162\u6269\u5927\uff0c\u7b49\u5230\u80f8\u53e3\u5fc3\u95f4\u53d1\u9ebb\uff0c\u90a3\u5c31\u662f\u4f60\u7684\u5bff\u9650\u5230\u4e86\u3002\u201d\u6d2a\u80dc\u6d77\u53c8\u5657\u7684\u8dea\u4e0b\uff0c\u78d5\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6b63\u8272\u9053\uff1a\u201c\u4f60\u6295\u964d\u756a\u90a6\uff0c\u53bb\u505a\u6c49\u5978\uff0c\u5b9e\u662f\u7f6a\u4e0d\u5bb9\u8bdb\u3002 \u3000\u3000\u6211\u95ee\u4f60\uff0c\u4f60\u613f\u4e0d\u613f\u5c06\u529f\u6298\u7f6a\uff1f\u201d\u6d2a\u80dc\u6d77\u5782\u6cea\u9053\uff1a\u201c\u5c0f\u4eba\u505a\u8fd9\u4ef6\u4e8b\uff0c\u6709\u65f6\u4e2d\u591c\u626a\u5fc3\u81ea\u95ee\uff0c\u4e5f\u89c9\u5bf9\u4e0d\u8d77\u5148\u4eba\uff0c\u8fb1\u6ca1\u4e0a\u4ee3\u7956\u5b97\u3002\u76f8\u516c\u7ed9\u5c0f\u4eba\u4e00\u6761\u81ea\u65b0\u4e4b\u8def\uff0c\u5b9e\u662f\u518d\u751f\u7236\u6bcd\u3002\u5c0f\u4eba\u4e5f\u4e0d\u662f\u81ea\u7518\u5815\u843d\uff0c\u53ea\u662f\u5f53\u5e74\u4e3a\u4e86\u4e00\u4ef6\u4e8b\uff0c\u8feb\u5f97\u65e0\u8def\u53ef\u8d70\uff0c\u8fd9\u624d\u51fa\u6b64\u4e0b\u7b56\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u8bf4\u5f97\u8bda\u6073\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u8d77\u6765\uff0c\u5750\u4e0b\u6162\u6162\u8bf4\u3002\u662f\u8c01\u8feb\u5f97\u4f60\u65e0\u8def\u53ef\u8d70\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u6068\u6068\u7684\u9053\uff1a\u201c\u662f\u534e\u5c71\u6d3e\u7684\u5f52\u4e8c\u5a18\u548c\u5b59\u4ef2\u541b\u5e08\u5f92\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5927\u51fa\u8881\u627f\u5fd7\u610f\u6599\u4e4b\u5916\uff0c\u5fd9\u95ee\uff1a\u201c\u751a\u4e48\uff1f\u662f\u5979\u4eec\uff1f\u201d\u6d2a\u80dc\u6d77\u8138\u8272\u500f\u53d8\uff0c\u8feb\uff1a\u201c\u76f8\u516c\u8bc6\u5f97\u5979\u4eec\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u521a\u624d\u8fd8\u548c\u5979\u4eec\u4ea4\u4e86\u624b\u3002\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u542c\u4e86\u4e00\u559c\u4e00\u5fe7\uff0c\u559c\u7684\u662f\u773c\u524d\u8fd9\u6837\u4e00\u4e2a\u5927\u672c\u9886\u7684\u4eba\u662f\u5979\u4eec\u7684\u5bf9\u5934\uff0c\u5fe7\u7684\u662f\u8fd9\u4e24\u4eba\u7adf\u5728\u5357\u4eac\uff0c\u53ea\u6015\u51a4\u5bb6\u8def\u7a84\uff0c\u72ed\u8def\u76f8\u9022\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5a18\u513f\u672c\u9886\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u51b3\u4e0d\u662f\u76f8\u516c\u7684\u5bf9\u624b\u3002\u53ea\u662f\u5979\u5e08\u5f92\u4fe9\u5fc3\u72e0\u624b\u8fa3\uff0c\u751a\u4e48\u4e8b\u90fd\u505a\u5f97\u51fa\u6765\uff0c\u76f8\u516c\u53ef\u8981\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u54fc\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u5979\u4eec\u8feb\u4f60\uff0c\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u5fae\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u4e0d\u6562\u76f8\u7792\uff0c\u5c0f\u4eba\u672c\u5728\u5c71\u4e1c\u6d77\u9762\u4e0a\u505a\u4e9b\u6ca1\u672c\u94b1\u7684\u4e70\u5356\u3002\u4f19\u4f34\u4e2d\u6709\u4e2a\u4e49\u5144\uff0c\u770b\u4e2d\u4e86\u90a3\u5b59\u4ef2\u541b\uff0c\u5411\u5979\u6c42\u5a5a\u3002\u5979\u4e0d\u7b54\u5e94\u4e5f\u5c31\u7f62\u4e86\uff0c\u54ea\u77e5\u4e00\u8a00\u4e0d\u53d1\uff0c\u7a81\u7136\u7528\u5251\u524a\u53bb\u4e86\u4ed6\u4e24\u53ea\u8033\u6735\u3002\u5c0f\u4eba\u5fc3\u5934\u4e0d\u5fff\uff0c\u7ea6\u4e86\u51e0\u5341\u4e2a\u4eba\uff0c\u53bb\u5c06\u5979\u63b3\u4e86\u6765\uff0c\u672c\u60f3\u8feb\u5979\u548c\u6211\u90a3\u4e49\u5144\u6210\u4eb2\uff0c\u4e0d\u6599\u5979\u5e08\u5a18\u5f52\u4e8c\u5a18\u5f53\u665a\u4fbf\u5373\u8d76\u5230\uff0c\u5c06\u6211\u4e49\u5144\u4e00\u5251\u6740\u6b7b\uff0c\u5176\u4f59\u670b\u53cb\u4e5f\u90fd\u7ed9\u6740\u4e86\u3002\u5c0f\u4eba\u9003\u5f97\u5feb\uff0c\u603b\u7b97\u8d70\u8131\u4e86\u4e00\u6761\u6027\u547d\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u63b3\u4eba\u8feb\u5a5a\uff0c\u672c\u6765\u662f\u4f60\u4e0d\u597d\u554a\u3002\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u5c0f\u4eba\u4e5f\u77e5\u4e8b\u60c5\u505a\u5f97\u5364\u83bd\uff0c\u95ef\u4e86\u5927\u7978\uff0c\u9003\u8131\u540e\u4e5f\u4e0d\u6562\u9732\u9762\u3002\u54ea\u77e5\u5979\u4eec\u6253\u542c\u5f97\u5c0f\u4eba\u5bb6\u4e61\u6240\u5728\uff0c\u8d76\u53bb\u5c06\u6211\u4e03\u5341\u5c81\u7684\u8001\u6bcd\u3001\u5c06\u6211\u59bb\u5b50\u548c\u4e09\u4e2a\u513f\u5973\uff0c\u6740\u5f97\u4e00\u4e2a\u4e0d\u7559\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u8bf4\u5230\u8fd9\u91cc\u65f6\u6d41\u4e0b\u6cea\u6765\uff0c\u6599\u60f3\u6240\u8a00\u4e0d\u865a\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u53c8\u9053\uff1a\u201c\u6211\u6597\u4e0d\u8fc7\u5979\u4eec\uff0c\u53ef\u662f\u6b64\u4ec7\u4e0d\u62a5\uff0c\u96be\u4e0b\u5f97\u8fd9\u4e00\u53e3\u6c14\u2026\u2026\u5c0f\u4eba\u5728\u4e2d\u539f\u65e0\u6cd5\u5b58\u8eab\uff0c\u77e5\u9053\u8fdf\u65e9\u4f1a\u7ed9\u8fd9\u4e24\u4e2a\u6cfc\u8fa3\u5a46\u5a18\u6740\u4e86\uff0c\u4e00\u65f6\u610f\u5de6\uff0c\u4fbf\u5230\u8fbd\u4e1c\u53bb\u6295\u4e86\u4e5d\u738b\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u53c8\u662f\u6c14\u6124\uff0c\u53c8\u662f\u60ed\u6127\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5979\u4eec\u6740\u4f60\u6bcd\u4eb2\u59bb\u513f\uff0c\u867d\u7136\u672a\u514d\u592a\u8fc7\uff0c\u4f46\u8d77\u56e0\u603b\u662f\u4f60\u4e0d\u597d\u3002\u800c\u4e14\u8fd9\u662f\u79c1\u4ec7\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5\u6295\u964d\u756a\u90a6\uff0c\u7518\u505a\u6c49\u5978\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u53ea\u6c42\u8881\u5927\u82f1\u96c4\u7ed9\u6211\u62a5\u4e86\u6b64\u4ec7\uff0c\u4f60\u53eb\u6211\u4f5c\u751a\u4e48\u5168\u6210\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u62a5\u4ec7\uff1f\u4f60\u8fd9\u751f\u522b\u4f5c\u8fd9\u6253\u7b97\u4e86\uff0c\u5f52\u4e8c\u5a18\u6b66\u529f\u6781\u9ad8\uff0c\u5979\u4e08\u592b\u795e\u62f3\u65e0\u654c\u66f4\u662f\u4e86\u5f97\u3002\u6211\u95ee\u4f60\uff0c\u4e5d\u738b\u53eb\u4f60\u53bb\u89c1\u66f9\u592a\u76d1\u5e72\u4e48\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u4e5d\u738b\u7237\u5429\u5490\u5c0f\u4eba\uff0c\u8981\u66f9\u592a\u76d1\u5c06\u5bab\u91cc\u671d\u4e2d\u7684\u5927\u4e8b\u90fd\u8bf4\u7ed9\u5c0f\u4eba\u542c\uff0c\u7136\u540e\u53bb\u8f6c\u544a\u4e5d\u738b\u7237\u3002\u201d\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u66f9\u5316\u6df3\u505a\u5230\u53f8\u793c\u592a\u76d1\uff0c\u5df2\u662f\u592a\u76d1\u4e2d\u7684\u9876\u513f\u5c16\u513f\uff0c\u4ed6\u6295\u964d\u6ee1\u6e05\uff0c\u53c8\u56fe\u7684\u662f\u751a\u4e48\uff1f\u591a\u5c14\u886e\u8bb8\u7ed9\u4ed6\u7684\u597d\u5904\uff0c\u96be\u9053\u80fd\u6bd4\u6211\u5927\u660e\u7687\u5e1d\u7ed9\u4ed6\u7684\u66f4\u591a\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u6ee1\u6e05\u4e5d\u738b\u7237\u53ea\u7b54\u5e94\u4ed6\u4e00\u4ef6\u4e8b\uff1a\u5c06\u6765\u653b\u7834\u5317\u4eac\uff0c\u4e0d\u6740\u4ed6\u7684\u5934\uff0c\u8ba9\u4ed6\u4fdd\u6709\u5bb6\u4ea7\uff1b\u4ed6\u82e5\u4e0d\u4f5c\u5185\u5e94\uff0c\u5317\u4eac\u7ec8\u7a76\u8fd8\u662f\u80fd\u7834\uff0c\u90a3\u65f6\u4fbf\u5c06\u4ed6\u5343\u5200\u4e07\u5250\u3002\u201d\u8881\u627f\u5fd7\u8fd9\u624d\u604d\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u66f9\u592a\u76d1\u80af\u505a\u6c49\u5978\uff0c\u53ea\u662f\u6015\u6b7b\uff0c\u4e3a\u4e86\u94fa\u4e00\u6761\u540e\u8def\u3002\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u8881\u627f\u5fd7\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u60f3\uff1a\u201c\u6709\u4e9b\u4eba\u751a\u4e48\u90fd\u6709\u4e86\uff0c\u4fbf\u53ea\u6015\u6b7b\u3002\u4e3a\u4e86\u6015\u6b7b\uff0c\u4fbf\u751a\u4e48\u90fd\u80af\u5e72\u3002\u201d \u3000\u3000\u4ed6\u5411\u6d2a\u80dc\u6d77\u77a7\u53bb\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u4e5f\u6015\u6b7b\uff0c\u53ea\u6c42\u4fdd\u4f4f\u6027\u547d\uff0c\u751a\u4e48\u90fd\u80af\u5e72\u3002\u574f\u4e8b\u56fa\u7136\u80af\u505a\uff0c\u597d\u4e8b\u4f55\u5c1d\u4e0d\u80fd\uff1f\u201d\u95ee\u9053\uff1a\u201c\u4f60\u613f\u610f\u6539\u90aa\u5f52\u6b63\uff0c\u505a\u4e2a\u597d\u4eba\u5462\uff1f\u8fd8\u662f\u5b81\u53ef\u5728\u4e09\u4e2a\u6708\u540e\u6b7b\u4e8e\u975e\u547d\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8881\u82f1\u96c4\u6307\u70b9\u6211\u4e00\u6761\u660e\u8def\uff0c\u4f46\u6709\u6240\u547d\uff0c\u5c0f\u4eba\u4e0d\u6562\u6709\u8fdd\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8ddf\u7740\u6211\u4f5c\u4e2a\u4eb2\u968f\u5427\u3002\u201d\u6d2a\u80dc\u6d77\u5927\u559c\uff0c\u6251\u5730\u8dea\u5012\uff0c\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ee5\u540e\u4f60\u522b\u53eb\u6211\u751a\u4e48\u82f1\u96c4\u4e0d\u82f1\u96c4\u4e86\u3002\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u662f\uff0c\u6211\u53eb\u4f60\u76f8\u516c\u3002\u201d\u5fc3\u4e2d\u6697\u559c\uff1a\u201c\u53ea\u8981\u8ddf\u5b9a\u4e86\u4f60\uff0c\u518d\u4e5f\u4e0d\u6015\u5f52\u4e8c\u5a18\u548c\u5b59\u4ef2\u541b\u8fd9\u4e24\u4e2a\u5973\u8d3c\u6765\u6740\u6211\u4e86\u3002\u4e09\u4e2a\u6708\u540e\u4f24\u52bf\u53d1\u4f5c\uff0c\u4f60\u81ea\u7136\u4e5f\u4e0d\u4f1a\u8896\u624b\u65c1\u89c2\u3002\u201d\u5f53\u4e0b\u5fc3\u5b89\u7406\u5f97\uff0c\u80f8\u6000\u5927\u7545\uff0c\u4ee5\u524d\u505a\u6ee1\u6e05\u5978\u7ec6\uff0c\u65f6\u89c9\u795e\u660e\u5185\u759a\uff0c\u604d\u60da\u4e0d\u5b89\uff0c\u6b64\u523b\u5fc3\u5934\u5b9b\u5982\u79fb\u53bb\u4e00\u5757\u5927\u77f3\uff0c\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fd9\u4e86\u4e00\u591c\uff0c\u8fd9\u624d\u5165\u5185\u5b89\u7761\uff0c\u547d\u6d2a\u80dc\u6d77\u548c\u4ed6\u540c\u7761\u4e00\u5ba4\u3002\u4ed6\u89c1\u8881\u627f\u5fd7\u5bf9\u5df1\u5341\u5206\u4fe1\u4efb\uff0c\u6b8a\u65e0\u63d0\u9632\u4e4b\u610f\uff0c\u5fc3\u4e2d\u5f88\u662f\u611f\u6fc0\u3002\u5176\u5b9e\u8881\u627f\u5fd7\u7528\u6df7\u5143\u529f\u4f24\u4ed6\u4e4b\u540e\uff0c\u77e5\u9053\u4ed6\u8981\u9760\u81ea\u5df1\u89e3\u6551\uff0c\u5982\u6562\u6697\u4e2d\u52a0\u5bb3\uff0c\u90a3\u5c31\u662f\u5bb3\u4e86\u81ea\u8eab\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u56de\u3000\u4e0d\u4f20\u4f20\u767e\u53d8 \u65e0\u654c\u654c\u5343\u62db",content:(0,t.jsx)(t.Fragment,{children:"\u8881\u627f\u5fd7\u7761\u5230\u65e5\u4e0a\u4e09\u7aff\uff0c\u8fd9\u624d\u8d77\u8eab\u3002\u7126\u5b9b\u513f\u4eb2\u81ea\u6367\u4e86\u76e5\u6d17\u7528\u5177\u548c\u65e9\u70b9\u8fdb\u623f\uff0c\u8881\u627f\u5fd7\u8fde\u5fd9\u900a\u8c22\u3002\u6d2a\u80dc\u6d77\u4fbf\u5728\u65c1\u670d\u4f8d\u3002 \u3000\u3000\u521a\u6d17\u597d\u8138\uff0c\u6728\u6851\u9053\u4eba\u62ff\u4e86\u68cb\u76d8\uff0c\u9752\u9752\u62ff\u7740\u68cb\u5b50\uff0c\u4e24\u4eba\u4e00\u9f50\u8fdb\u6765\u3002\u9752\u9752\u7b11\u9053\uff1a\u201c\u8d2a\u7761\u732b\uff0c\u5230\u8fd9\u65f6\u5019\u624d\u8d77\u8eab\uff0c\u9053\u957f\u53ef\u7b49\u5f97\u6025\u574f\u4e86\uff0c\u5feb\u4e0b\u68cb\uff0c\u5feb\u4e0b\u68cb\u3002\u201d\u8881\u627f\u5fd7\u5411\u7740\u5979\u77a7\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u4e00\u7b11\u3002\u9752\u9752\u7b11\u9053\uff1a\u201c\u7b11\u751a\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u9053\u957f\u7ed9\u4f60\u751a\u4e48\u597d\u5904\uff1f\u4f60\u8fd9\u822c\u51fa\u529b\u7ed9\u4ed6\u627e\u5bf9\u624b\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u9053\u957f\u6559\u4e86\u6211\u4e00\u5957\u529f\u592b\u3002\u8fd9\u529f\u592b\u554a\uff0c\u53ef\u771f\u5999\u5566\u3002\u522b\u4eba\u5411\u4f60\u62f3\u6253\u811a\u8e22\uff0c\u4f60\u5374\u53ea\u7ba1\u8ddf\u4ed6\u6349\u8ff7\u85cf\uff0c\u4e1c\u4e00\u6e9c\uff0c\u897f\u4e00\u6643\uff0c\u4ed6\u518d\u4e5f\u522b\u60f3\u6253\u5230\u4f60\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u91cc\u4e00\u52a8\uff0c\u5077\u773c\u770b\u6728\u6851\u9053\u4eba\u65f6\uff0c\u89c1\u4ed6\u62ff\u4e86\u4e24\u9897\u767d\u5b50\u3001\u4e24\u9897\u9ed1\u5b50\uff0c\u653e\u5728\u68cb\u76d8\u56db\u89d2\uff0c\u624b\u4e2d\u62c8\u7740\u4e00\u9897\u9ed1\u5b50\uff0c\u8f7b\u8f7b\u6572\u51fb\u68cb\u76d8\uff0c\u53d1\u51fa\u4e01\u4e01\u4e4b\u58f0\uff0c\u5634\u89d2\u8fb9\u9732\u51fa\u5fae\u7b11\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4eca\u665a\u4e8c\u5e08\u54e5\u3001\u4e8c\u5e08\u5ac2\u96e8\u82b1\u53f0\u4e4b\u7ea6\uff0c\u90a3\u662f\u975e\u53bb\u4e0d\u53ef\u7684\u3002\u77a7\u4e8c\u5e08\u5ac2\u7684\u795e\u6c14\uff0c\u53ea\u6015\u4e0d\u80fd\u4e0d\u52a8\u624b\uff0c\u6211\u53c8\u4e0d\u80fd\u8ddf\u4ed6\u4eec\u771f\u6253\u3002\u4e8c\u5e08\u54e5\u53f7\u79f0\u795e\u62f3\u65e0\u654c\uff0c\u6211\u5168\u529b\u65bd\u4e3a\uff0c\u5c1a\u4e14\u672a\u5fc5\u80fd\u80dc\uff0c\u5982\u518d\u76f8\u8ba9\uff0c\u975e\u53d7\u91cd\u4f24\u4e0d\u53ef\uff0c\u771f\u6709\u5dee\u6c60\uff0c\u53ea\u6015\u8fde\u547d\u4e5f\u9001\u4e86\u3002\u9053\u957f\u4f20\u6388\u5979\u6b66\u529f\uff0c\u4f3c\u4e4e\u522b\u6709\u6df1\u610f\u3002\u201d\u4fbf\u9053\uff1a\u201c\u8981\u6211\u4e0b\u68cb\uff0c\u5012\u4e5f\u53ef\u4ee5\uff0c\u53ef\u662f\u4f60\u5f97\u628a\u8fd9\u5957\u529f\u592b\u8f6c\u6559\u7ed9\u6211\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u597d\u54c7\uff0c\u8fd9\u53eb\u505a\u89c1\u8005\u6709\u4efd\uff0c\u4f60\u8ddf\u6211\u8bb2\u8d77\u9ed1\u9053\u4e0a\u7684\u89c4\u77e9\u6765\u5566\u3002\u201d\u4e24\u4eba\u8bf4\u7b11\u4e86\u51e0\u53e5\uff0c\u8881\u627f\u5fd7\u5c31\u966a\u6728\u6851\u4e0b\u68cb\u3002 \u3000\u3000\u5348\u996d\u540e\uff0c\u8881\u627f\u5fd7\u548c\u5d14\u79cb\u5c71\u8c08\u8d77\u522b\u6765\u60c5\u7531\u3002\u4e00\u4e2a\u77e5\u9053\u95ef\u738b\u52bf\u529b\u5927\u5f20\uff0c\u4e0d\u4e45\u5c31\u8981\u5927\u4e3e\u5165\u4eac\uff1b\u53e6\u4e00\u4e2a\u89c1\u65e7\u65f6\u5c0f\u53cb\u5df2\u82f1\u6b66\u5982\u65af\uff0c\u827a\u6210\u54c1\u7acb\uff0c\u5747\u89c9\u559c\u6170\u3002\u8c08\u4e86\u4e00\u9635\uff0c\u53c8\u8bf4\u5230\u5d14\u5e0c\u654f\u548c\u5b89\u5c0f\u6167\u5931\u91d1\u593a\u91d1\u4e4b\u4e8b\u3002\u9752\u9752\u4e0d\u4f4f\u5411\u8881\u627f\u5fd7\u6253\u624b\u52bf\uff0c\u53eb\u4ed6\u51fa\u53bb\u3002\u5d14\u79cb\u5c71\u7b11\u9053\uff1a\u201c\u4f60\u5c0f\u670b\u53cb\u53eb\u4f60\u5462\uff0c\u5feb\u53bb\u5427\uff01\u201d\u8881\u627f\u5fd7\u8138\u4e00\u7ea2\uff0c\u4e0d\u597d\u610f\u601d\u4fbf\u8d70\u3002 \u3000\u3000\u5d14\u79cb\u5c71\u7b11\u7740\u8d77\u8eab\u8d70\u51fa\u3002\u9752\u9752\u5954\u4e86\u8fdb\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5feb\u6765\uff0c\u6211\u628a\u9053\u957f\u6559\u7684\u529f\u592b\u8ddf\u4f60\u8bf4\u3002\u4ed6\u6559\u7684\u65f6\u5019\u6211\u538b\u6839\u513f\u5c31\u4e0d\u61c2\u3002\u4ed6\u8bf4\uff1a\u2018\u4f60\u786c\u8bb0\u7740\uff0c\u5c06\u6765\u6162\u6162\u513f\u5c31\u61c2\u4e86\u3002\u2019\u6211\u6015\u518d\u8fc7\u4e00\u9635\u5c31\u5168\u7ed9\u5fd8\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u8fde\u6bd4\u5e26\u5212\uff0c\u628a\u6728\u6851\u6240\u6388\u7684\u4e00\u5957\u7edd\u9876\u8f7b\u529f\u201c\u795e\u884c\u767e\u53d8\u201d\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6728\u6851\u9053\u4eba\u8f7b\u529f\u4e0e\u6697\u5668\u4e4b\u672f\u5929\u4e0b\u72ec\u6b65\uff0c\u8fd9\u5957\u201c\u795e\u884c\u767e\u53d8\u201d\u66f4\u662f\u7cbe\u5fae\u5965\u5999\uff0c\u5f53\u5e74\u5728\u534e\u5c71\u4e4b\u65f6\uff0c\u8881\u627f\u5fd7\u6240\u5b66\u5c1a\u6d45\uff0c\u65e0\u6cd5\u9886\u4f1a\u4fee\u4e60\uff0c\u662f\u4ee5\u6ca1\u6709\u4f20\u4ed6\u3002\u9752\u9752\u6b66\u529f\u867d\u4e0d\u751a\u7cbe\uff0c\u4f46\u8bb0\u6027\u6781\u597d\uff0c\u4eba\u53c8\u7075\u609f\uff0c\u77e5\u9053\u6728\u6851\u4f20\u5979\u662f\u5bbe\uff0c\u4f20\u8881\u627f\u5fd7\u662f\u4e3b\uff0c\u53ea\u662f\u4e0d\u660e\u767d\u4e3a\u751a\u4e48\u8981\u81ea\u5df1\u8f6c\u8a00\uff0c\u5f53\u65f6\u751f\u541e\u6d3b\u5265\u7684\u786c\u8bb0\u4e86\u4e0b\u6765\uff0c\u8fd9\u65f6\u628a\u53e3\u8bc0\u3001\u8fd0\u6c14\u3001\u811a\u6b65\u3001\u8eab\u6cd5\u7b49\u9879\u4e00\u4e00\u7167\u8bf4\u3002\u53ea\u542c\u5f97\u8881\u627f\u5fd7\u5fc3\u82b1\u6012\u653e\uff0c\u559c\u4e0d\u81ea\u80dc\u3002\u4ed6\u4e60\u7ec3\u6728\u6851\u6240\u4f20\u7684\u8f7b\u529f\u5df2\u5386\u591a\u5e74\uff0c\u8fd9\u5957\u201c\u795e\u884c\u767e\u53d8\u201d\u53ea\u4e0d\u8fc7\u66f4\u52a0\u53d8\u5316\u5965\u5999\uff0c\u987b\u4ee5\u66f4\u6df1\u5185\u529f\u4f5c\u4e3a\u6839\u5e95\uff0c\u57fa\u672c\u9053\u7406\u5374\u4e5f\u4e0e\u4ee5\u524d\u6240\u5b66\u7684\u8f7b\u529f\u65e0\u522b\u3002\u6b64\u65f6\u4ed6\u6b66\u5b66\u4fee\u4e3a\u5927\u8fdb\uff0c\u4e00\u95fb\u8981\u8bc0\uff0c\u4fbf\u5373\u9886\u609f\u3002\u9752\u9752\u6709\u51e0\u5904\u5730\u65b9\u6ca1\u8bb0\u6e05\u695a\uff0c\u8881\u627f\u5fd7\u4e00\u95ee\uff0c\u5979\u7b54\u4e0d\u4e0a\u6765\uff0c\u4fbf\u53c8\u5954\u8fdb\u53bb\u95ee\u6728\u6851\u9053\u4eba\u3002\u7b49\u5230\u4e8c\u6b21\u6307\u70b9\uff0c\u8881\u627f\u5fd7\u5df2\u5c3d\u884c\u660e\u767d\uff0c\u5f53\u4e0b\u5728\u5385\u4e2d\u6309\u5f0f\u7ec3\u4e86\u4e00\u904d\u3002 \u3000\u3000\u4f46\u89c9\u8fd9\u5957\u8f7b\u529f\u8f6c\u6298\u6ed1\u6e9c\uff0c\u76f4\u4f3c\u6e38\u9c7c\u4e00\u822c\uff0c\u4e0e\u4eba\u52a8\u624b\u4e4b\u9645\uff0c\u82e5\u662f\u4f46\u6c42\u8d8b\u907f\u81ea\u4fdd\uff0c\u654c\u4eba\u5175\u5203\u62f3\u811a\u4e07\u96be\u53ca\u8eab\uff0c\u8fd9\u624d\u660e\u767d\u6728\u6851\u7684\u7528\u610f\u3002\u7136\u4ed6\u77e5\u4e8c\u5e08\u54e5\u6b66\u529f\u7cbe\u7edd\uff0c\u5f53\u5e74\u5e08\u7236\u66fe\u8bf4\uff1a\u201c\u4f60\u5927\u5e08\u54e5\u4e3a\u4eba\u6ed1\u7a3d\uff0c\u4e0d\u514d\u6709\u70b9\u6d6e\u8e81\u3002\u4e8c\u5e08\u54e5\u5374\u6728\u8bb7\u6df1\u6c89\uff0c\u7528\u529f\u5c24\u4e3a\u624e\u5b9e\u3002\u201d\u7531\u6b64\u53ef\u77e5\uff0c\u4e8c\u5e08\u54e5\u7684\u529f\u529b\u591a\u534a\u5728\u5927\u5e08\u54e5\u4e4b\u4e0a\uff0c\u8fd9\u5957\u529f\u592b\u65b0\u7ec3\u672a\u719f\uff0c\u4ee5\u4e4b\u95ea\u907f\u62b5\u6321\uff0c\u53ea\u6015\u672a\u5fc5\u80fd\u6210\u3002 \u3000\u3000\u4ed6\u51dd\u601d\u826f\u4e45\uff0c\u5ffd\u7136\u60f3\u8d77\u5e08\u7236\u521d\u6388\u6b66\u529f\u4e4b\u65f6\u66fe\u6559\u8fc7\u4e00\u5957\u5341\u6bb5\u9526\uff0c\u5f53\u65f6\u81ea\u5df1\u51fa\u5c3d\u672c\u4e8b\uff0c\u4e5f\u6478\u4e0d\u5230\u5e08\u7236\u4e00\u7247\u8863\u89d2\uff0c\u5176\u4e2d\u786e\u662f\u5999\u7528\u65e0\u7a77\u3002\u6728\u6851\u9053\u4eba\u7684\u201c\u795e\u884c\u767e\u53d8\u201d\u529f\u592b\u867d\u7136\u8f7b\u7075\u5df2\u6781\uff0c\u4f46\u4f3c\u5acc\u4e0d\u591f\u6c89\u539a\uff0c\u59cb\u7ec8\u8eb2\u95ea\u800c\u4e0d\u542b\u53cd\u51fb\u4f0f\u7740\uff0c\u5bf9\u65b9\u4e0d\u514d\u65e0\u6240\u987e\u5fcc\uff0c\u5982\u548c\u672c\u95e8\u8f7b\u529f\u6df7\u5408\u4f7f\u7528\uff0c\u5c82\u975e\u5e76\u517c\u4e24\u5bb6\u6240\u957f\uff1f\u4ed6\u72ec\u81ea\u5728\u4e66\u623f\u4e2d\u95ed\u76ee\u5bfb\u601d\uff0c\u4e00\u62db\u4e00\u5f0f\u7684\u9ed8\u5ff5\u3002\u65c1\u4eba\u4e5f\u4e0d\u53bb\u6253\u6270\u3002 \u3000\u3000\u5230\u5f97\u7533\u724c\u65f6\u5206\uff0c\u8881\u627f\u5fd7\u5df2\u5168\u76d8\u60f3\u901a\uff0c\u4f46\u6015\u6ca1\u6709\u628a\u63e1\uff0c\u987b\u5f97\u8bd5\u7ec3\u4e00\u756a\u3002\u4e8e\u662f\u8bf7\u7126\u5b9b\u513f\u7ea6\u4e86\u5341\u591a\u4f4d\u5e08\u5144\u5f1f\uff0c\u5404\u4eba\u63d0\u4e86\u4e00\u5927\u6876\u6c34\uff0c\u5728\u7ec3\u6b66\u573a\u56db\u5468\u56f4\u4f4f\uff0c\u81ea\u5df1\u7ad9\u5728\u4e2d\u5fc3\uff0c\u4e00\u6446\u624b\uff0c\u5404\u4eba\u4fbf\u8200\u6c34\u5411\u4ed6\u4e71\u6cfc\uff0c\u4ed6\u7a9c\u9ad8\u4f0f\u4f4e\uff0c\u4e1c\u8eb2\u897f\u907f\uff0c\u7b49\u5230\u5341\u6876\u6c34\u6cfc\u5b8c\uff0c\u53ea\u6709\u53f3\u624b\u8896\u5b50\u4e0e\u5de6\u811a\u4e0a\u6e7f\u4e86\u4e00\u6ee9\u3002\u5404\u4eba\u7eb7\u7eb7\u4e0a\u524d\u9053\u559c\uff0c\u8d3a\u4ed6\u53c8\u7ec3\u6210\u4e00\u9879\u7edd\u6280\u3002 \u3000\u3000\u6728\u6851\u9053\u4eba\u5374\u4e00\u76f4\u5728\u623f\u4e2d\u547c\u547c\u5927\u7761\uff0c\u5168\u4e0d\u7406\u4f1a\u3002 \u3000\u3000\u665a\u81b3\u8fc7\u540e\uff0c\u8881\u627f\u5fd7\u4fbf\u8981\u53bb\u96e8\u82b1\u53f0\u8d74\u7ea6\u3002\u7126\u516c\u793c\u3001\u7126\u5b9b\u513f\u7236\u5973\u60f3\u540c\u53bb\u89e3\u91ca\uff0c\u9752\u9752\u8981\u968f\u4f34\u52a9\u9635\uff0c\u8881\u627f\u5fd7\u90fd\u5a49\u8a00\u76f8\u5374\u3002\u9752\u9752\u6485\u8d77\u4e86\u5634\u5f88\u4e0d\u9ad8\u5174\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6211\u5e08\u54e5\u5e08\u5ac2\uff0c\u4eca\u665a\u6211\u53ea\u662f\u6328\u6253\u4e0d\u8fd8\u624b\uff0c\u4f60\u77a7\u7740\u4e00\u5b9a\u751f\u6c14\uff0c\u5c82\u4e0d\u662f\u574f\u4e86\u6211\u7684\u4e8b\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u8ba9\u4ed6\u4eec\u4e09\u62db\u4e5f\u5c31\u662f\u4e86\uff0c\u5e72\u4e48\u8001\u4e0d\u8fd8\u624b\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u8981\u7528\u4f60\u6559\u6211\u7684\u529f\u592b\uff0c\u77a7\u4ed6\u4eec\u6253\u4e0d\u6253\u5f97\u7740\u6211\u3002\u201d\u9752\u9752\u62cd\u624b\u7b11\u9053\uff1a\u201c\u90a3\u6211\u66f4\u8981\u53bb\u77a7\u77a7\uff0c\u4eb2\u773c\u770b\u6211\u4e56\u5f92\u513f\u5927\u663e\u8eab\u624b\u3002\u4f60\u6015\u6211\u5f97\u7f6a\u4f60\u5e08\u54e5\u5e08\u5ac2\uff0c\u6211\u4e00\u53e5\u8bdd\u4e0d\u8bf4\u5c31\u662f\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u80af\u88c5\u54d1\u5df4\uff1f\u201d\u9752\u9752\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u5c31\u88c5\u54d1\u5df4\u3002\u201d\u8881\u627f\u5fd7\u62d7\u4e0d\u8fc7\u5979\uff0c\u53ea\u5f97\u8ba9\u5979\u540c\u53bb\u3002\u8fdb\u53bb\u5411\u6728\u6851\u544a\u8f9e\uff0c\u53ea\u89c1\u4ed6\u5411\u7740\u91cc\u5e8a\u800c\u7761\uff0c\u53eb\u4e86\u51e0\u58f0\u4e0d\u9192\uff0c\u5d14\u79cb\u5c71\u5374\u5df2\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u4e24\u4eba\u5411\u7126\u5bb6\u501f\u4e86\u4e24\u5339\u5065\u9a6c\uff0c\u4e8c\u66f4\u65f6\u5206\uff0c\u5df2\u5230\u4e86\u96e8\u82b1\u53f0\u7554\u3002 \u3000\u3000\u89c1\u56db\u4e0b\u65e0\u4eba\uff0c\u4fbf\u4e0b\u9a6c\u76f8\u5019\uff0c\u7b49\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u53ea\u89c1\u4e1c\u8fb9\u4e24\u4eba\u5954\u8fd1\uff0c\u8ddf\u7740\u8f7b\u8f7b\u4e24\u58f0\u51fb\u638c\u3002\u8881\u627f\u5fd7\u62cd\u638c\u76f8\u5e94\u3002 \u3000\u3000\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u8881\u5e08\u53d4\u5230\u4e86\u4e48\uff1f\u201d\u542c\u58f0\u97f3\u662f\u5218\u57f9\u751f\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u7b49\u5019\u5e08\u54e5\u5e08\u5ac2\u3002\u201d\u773c\u89c1\u5218\u57f9\u751f\u548c\u6885\u5251\u548c\u8d70\u8fd1\uff0c\u8fdc\u5904\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u597d\u554a\uff0c\u679c\u7136\u6765\u4e86\uff01\u201d \u3000\u3000\u8bed\u58f0\u521a\u6bd5\uff0c\u4e24\u4e2a\u4eba\u5f71\u4fbf\u5954\u5230\u8ddf\u524d\u3002\u9752\u9752\u4e00\u60ca\uff0c\u5fc3\u60f3\u8fd9\u4e24\u4eba\u6765\u5f97\u597d\u5feb\u3002\u6885\u5218\u4e8c\u4eba\u5f80\u5916\u4e00\u5206\uff0c\u90a3\u4e24\u4e2a\u4eba\u5f71\u500f\u5730\u7a9c\u51fa\uff0c\u6b63\u662f\u5f52\u8f9b\u6811\u548c\u5f52\u4e8c\u5a18\u592b\u5987\u3002\u8fdc\u5904\u53c8\u6709\u4e00\u4e2a\u4eba\u5954\u6765\uff0c\u8881\u627f\u5fd7\u89c1\u5979\u8eab\u5f62\uff0c\u77e5\u662f\u98de\u5929\u9b54\u5973\u5b59\u4ef2\u541b\u3002\u5979\u529f\u592b\u53ef\u5c31\u548c\u5e08\u7236\u5e08\u5a18\u5dee\u5f97\u8fdc\u4e86\uff0c\u5954\u4e86\u597d\u4e00\u9635\u624d\u5230\u8ddf\u524d\u3002\u5979\u624b\u4e2d\u62b1\u7740\u4e00\u4e2a\u5c0f\u5b69\uff0c\u662f\u5f52\u6c0f\u592b\u5987\u7684\u5b69\u5b50\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u51b7\u51b7\u7684\u9053\uff1a\u201c\u8881\u7237\u5012\u662f\u4fe1\u4eba\uff0c\u6211\u592b\u5987\u8fd8\u6709\u8981\u4e8b\uff0c\u522b\u803d\u6401\u8fb0\u5149\uff0c\u8fd9\u5c31\u8fdb\u62db\u5427\u3002\u201d\u8881\u627f\u5fd7\u8eac\u8eab\u884c\u793c\uff0c\u606d\u606d\u656c\u656c\u7684\u9053\uff1a\u201c\u5c0f\u5f1f\u4eca\u65e5\u662f\u5411\u5e08\u54e5\u5e08\u5ac2\u966a\u7f6a\u6765\u7684\u3002\u5c0f\u5f1f\u6298\u65ad\u5e08\u5ac2\u7684\u5b9d\u5251\uff0c\u5b9e\u662f\u4e8b\u524d\u672a\u77e5\u3002\u5192\u72af\u4e4b\u5904\uff0c\u8fd8\u8bf7\u5e08\u54e5\u5e08\u5ac2\u77a7\u5728\u5e08\u7236\u9762\u4e0a\uff0c\u5927\u91cf\u5305\u5bb9\u3002\u201d\u5f52\u4e8c\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6211\u4eec\u5e08\u5f1f\uff0c\u8c01\u4e5f\u4e0d\u77e5\uff0c\u5148\u8fc7\u4e86\u62db\u518d\u8bf4\u3002\u201d\u8881\u627f\u5fd7\u53ea\u662f\u63a8\u8ba9\uff0c\u4e0d\u80af\u52a8\u624b\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u89c1\u4ed6\u4e00\u5473\u9000\u7f29\uff0c\u5fc3\u60f3\u82e5\u975e\u5047\u5192\uff0c\u4f55\u5fc5\u5982\u6b64\u80c6\u602f\u6c14\u9981\uff1f\u5ffd\u5730\u5de6\u638c\u63d0\u8d77\uff0c\u659c\u5288\u4e0b\u6765\u3002\u8881\u627f\u5fd7\u75be\u5411\u540e\u4ef0\uff0c\u638c\u950b\u4ece\u9f3b\u5c16\u4e0a\u6025\u63a0\u800c\u8fc7\uff0c\u5fc3\u4e2d\u6697\u60ca\uff1a\u201c\u77a7\u4e0d\u51fa\u5979\u5973\u6d41\u4e4b\u8f88\uff0c\u638c\u6cd5\u5982\u6b64\u51cc\u5389\u4e86\u5f97\u3002\u201d\u5f52\u4e8c\u5a18\u4e00\u51fb\u4e0d\u4e2d\uff0c\u53f3\u62f3\u968f\u4e0a\uff0c\u4f7f\u7684\u6b63\u662f\u534e\u5c71\u6d3e\u7684\u7834\u7389\u62f3\u3002\u8881\u627f\u5fd7\u5bf9\u8fd9\u8def\u62f3\u6cd5\u7814\u4e60\u6709\u7d20\uff0c\u6210\u7af9\u5728\u80f8\uff0c\u5f53\u4e0b\u53cc\u624b\u4e0b\u5782\uff0c\u7d27\u8d34\u5927\u817f\u4e24\u4fa7\uff0c\u4ee5\u793a\u51b3\u4e0d\u8fd8\u624b\uff0c\u8eab\u5b50\u6643\u52a8\uff0c\u4f7f\u5f00\u878d\u4f1a\u4e86\u201c\u795e\u884c\u767e\u53d8\u201d\u548c\u5341\u6bb5\u9526\u7684\u8f7b\u529f\uff0c\u5728\u5f52\u4e8c\u5a18\u62f3\u811a\u7684\u7a7a\u9699\u4e2d\u7a7f\u6765\u63d2\u53bb\u3002\u5f52\u4e8c\u5a18\u8fde\u53d1\u5341\u4f59\u6025\u62db\uff0c\u52bf\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c\u90fd\u88ab\u4ed6\u4fa7\u8eab\u907f\u5f00\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u5728\u65c1\u77a7\u5f97\u51db\u7136\u5fc3\u60ca\uff0c\u6697\u60f3\u8fd9\u5c11\u5e74\u600e\u5730\u5982\u6b64\u4e86\u5f97\uff0c\u4ed6\u7684\u8f7b\u529f\u6709\u4e9b\u786e\u662f\u672c\u95e8\u8eab\u6cd5\uff0c\u4f46\u5927\u534a\u5374\u53c8\u622a\u7136\u4e0d\u540c\uff0c\u83ab\u975e\u8fd9\u5c11\u5e74\u662f\u522b\u6d3e\u5978\u5f92\uff0c\u4e0d\u77e5\u5982\u4f55\uff0c\u7adf\u5077\u5b66\u4e86\u672c\u95e8\u7684\u4e0a\u4e58\u529f\u592b\u53bb\uff1f\u5f53\u4e0b\u5168\u795e\u6ce8\u89c6\uff0c\u53ea\u6015\u59bb\u5b50\u5403\u4e8f\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u89c1\u8881\u627f\u5fd7\u5e76\u4e0d\u8fd8\u624b\uff0c\u5fc3\u60f3\u4f60\u5982\u6b64\u8f7b\u89c6\u4e8e\u6211\uff0c\u53eb\u4f60\u77e5\u9053\u5f52\u4e8c\u5a18\u7684\u5389\u5bb3\uff01\u53cc\u62f3\u5982\u98ce\uff0c\u8d8a\u6253\u8d8a\u5feb\uff0c\u5979\u65e2\u77e5\u5bf9\u65b9\u5e76\u4e0d\u53cd\u51fb\uff0c\u4fbf\u628a\u5b88\u5fa1\u7684\u62db\u6570\u5c3d\u6570\u6401\u4e0b\uff0c\u62db\u62db\u8fdb\u88ad\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u53eb\u82e6\uff0c\u60f3\u4e0d\u5230\u4e8c\u5e08\u5ac2\u5c06\u8fd9\u8def\u7834\u7389\u62f3\u4f7f\u5f97\u5982\u6b64\u52bf\u9053\u51cc\u5389\uff0c\u52a0\u4e4b\u53ea\u653b\u4e0d\u5b88\uff0c\u5a01\u529b\u66f4\u662f\u500d\u589e\uff0c\u5fc3\u60f3\u5f53\u771f\u62b5\u6321\u4e0d\u4f4f\u4e4b\u65f6\uff0c\u8bf4\u4e0d\u5f97\uff0c\u4e5f\u53ea\u597d\u4f38\u624b\u62db\u67b6\u4e86\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u89c1\u8881\u627f\u5fd7\u53cc\u624b\u4e0b\u5782\uff0c\u4efb\u51ed\u5e08\u5a18\u51fa\u624b\u5982\u4f55\u8fc5\u6377\uff0c\u59cb\u7ec8\u6253\u4e0d\u4e2d\u4ed6\u4e00\u62db\uff0c\u8d8a\u770b\u8d8a\u607c\uff0c\u659c\u773c\u95f4\u89c1\u9752\u9752\u7ad9\u5728\u4e00\u65c1\uff0c\u770b\u5f97\u5174\u9ad8\u91c7\u70c8\uff0c\u6ee1\u8138\u7b11\u5bb9\uff0c\u5f53\u5373\u5c06\u5c0f\u5e08\u5f1f\u5f80\u6885\u5251\u548c\u624b\u4e2d\u4e00\u9001\uff0c\u62d4\u51fa\u957f\u5251\u7eb5\u8eab\u800c\u524d\uff0c\u5411\u9752\u9752\u80f8\u53e3\u523a\u53bb\u3002 \u3000\u3000\u9752\u9752\u5403\u4e86\u4e00\u60ca\uff0c\u75be\u5fd9\u4fa7\u8eab\u907f\u5f00\u3002\u5979\u53d7\u8881\u627f\u5fd7\u4e4b\u5631\uff0c\u6b64\u884c\u4e0d\u5e26\u5175\u5203\uff0c\u88ab\u5b59\u4ef2\u541b\u5237\u5237\u6570\u5251\uff0c\u903c\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u5979\u6b66\u529f\u672c\u5c31\u4e0d\u53ca\uff0c\u66f4\u4f55\u51b5\u8d64\u624b\u7a7a\u62f3\uff0c\u6570\u62db\u4e4b\u540e\uff0c\u7acb\u5373\u5371\u9669\u4e07\u72b6\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u5979\u60ca\u547c\uff0c\u4fbf\u60f3\u8fc7\u53bb\u6551\u63f4\uff0c\u4f46\u88ab\u5f52\u4e8c\u5a18\u7d27\u7d27\u7f20\u4f4f\u4e86\u65e0\u6cd5\u8131\u8eab\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u5411\u5b59\u4ef2\u541b\u559d\u9053\uff1a\u201c\u522b\u4f24\u4eba\u6027\u547d\u3002\u201d\u5b59\u4ef2\u541b\u9053\uff1a\u201c\u6b64\u4eba\u662f\u91d1\u86c7\u90ce\u541b\u7684\u513f\u5b50\u3002\u8fd9\u8f7b\u8584\u5c11\u5e74\uff0c\u6b63\u662f\u7f6a\u9b41\u7978\u9996\u3002\u201d\u5f52\u8f9b\u6811\u66fe\u542c\u6c5f\u5357\u6b66\u6797\u4e2d\u4eba\u8a00\u9053\u91d1\u86c7\u90ce\u541b\u5fc3\u72e0\u624b\u8fa3\uff0c\u5e76\u975e\u5584\u826f\u4e4b\u8f88\uff0c\u4e5f\u5c31\u4e0d\u8a00\u8bed\u4e86\u3002\u5b59\u4ef2\u541b\u89c1\u5e08\u7236\u5df2\u7136\u9ed8\u8bb8\uff0c\u5251\u62db\u52a0\u7d27\uff0c\u767d\u5149\u95ea\u95ea\uff0c\u773c\u89c1\u9752\u9752\u4fbf\u8981\u547d\u4e27\u5f53\u5730\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5c40\u52bf\u7d27\u8feb\uff0c\u5ffd\u5730\u53cc\u817f\u9f50\u98de\uff0c\u4e24\u624b\u4ecd\u662f\u8d34\u5728\u80ef\u4fa7\uff0c\u4f46\u4e24\u817f\u5de6\u4e00\u811a\u53f3\u4e00\u811a\uff0c\u8fde\u73af\u516d\u811a\uff0c\u90fd\u662f\u5feb\u8981\u8e22\u5230\u5f52\u4e8c\u5a18\u8eab\u4e0a\u65f6\u500f\u5730\u6536\u56de\uff0c\u7136\u800c\u5df2\u5c06\u5979\u903c\u5f97\u8fde\u9000\u516d\u6b65\u3002\u8881\u627f\u5fd7\u5c31\u6b64\u6446\u8131\uff0c\u7eb5\u8eab\u8dc3\u8d77\uff0c\u7a7a\u4e2d\u8f6c\u8eab\u524d\u6251\uff0c\u5de6\u624b\u53cc\u6307\u70b9\u5411\u5b59\u4ef2\u541b\u540e\u5fc3\uff0c\u8981\u593a\u843d\u5979\u624b\u4e2d\u957f\u5251\uff0c\u5ffd\u542c\u8eab\u65c1\u4e00\u58f0\u957f\u5578\uff0c\u4e00\u80a1\u52b2\u98ce\u731b\u5411\u8170\u95f4\u88ad\u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u6687\u653b\u654c\uff0c\u5148\u62c6\u6765\u62db\uff0c\u53f3\u638c\u52fe\u4f4f\u6765\u4eba\u624b\u8155\u4e00\u5e26\uff0c\u54ea\u77e5\u6765\u4eba\u4e1d\u6beb\u4e0d\u52a8\uff0c\u81ea\u5df1\u5374\u88ab\u4ed6\u53cd\u529b\u63a8\u4e86\u51fa\u53bb\u3002\u8881\u627f\u5fd7\u81ea\u4e0b\u5c71\u4ee5\u6765\uff0c\u4ece\u672a\u9047\u5230\u52b2\u529b\u5982\u6b64\u6df1\u539a\u4e4b\u4eba\uff0c\u77e5\u9053\u5fc5\u662f\u4e8c\u5e08\u5144\u51fa\u624b\uff0c\u4e0d\u7531\u5f97\u4e00\u60ca\uff1a\u201c\u6211\u539f\u77e5\u4e8c\u5e08\u54e5\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u6ca1\u6599\u5230\u4ed6\u8eab\u6750\u7626\u7626\u5c0f\u5c0f\uff0c\u7adf\u5177\u5982\u6b64\u795e\u529b\u3002\u201d \u3000\u3000\u4ed6\u843d\u4e0b\u5730\u540e\uff0c\u8eab\u5b50\u4fbf\u5982\u6728\u6869\u822c\u731b\u7136\u9489\u4f4f\uff0c\u6beb\u4e0d\u6447\u6643\u3002\u53eb\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u5c0f\u5f1f\u5f97\u7f6a\uff01\u201d\u53eb\u58f0\u672a\u6b47\uff0c\u5f52\u8f9b\u6811\u5de6\u638c\u5df2\u5230\u8eab\u524d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8fd9\u6b21\u6709\u4e86\u63d0\u9632\uff0c\u5de6\u80a9\u5fae\u4fa7\uff0c\u6765\u638c\u6253\u7a7a\uff0c\u6b63\u662f\u4eca\u65e5\u5b66\u4f1a\u7684\u201c\u795e\u884c\u767e\u53d8\u201d\u8eab\u6cd5\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u9002\u624d\u8ddf\u4ed6\u4e00\u5e26\u4e00\u63a8\uff0c\u5df2\u5bdf\u89c9\u4ed6\u5185\u52b2\u5168\u662f\u672c\u95e8\u6df7\u5143\u529f\uff0c\u62db\u5f0f\u53ef\u4ee5\u5077\u5b66\uff0c\u5185\u529b\u5374\u987b\u4eb2\u4f20\uff0c\u53ea\u8fd9\u4e00\u63a8\u4e4b\u95f4\uff0c\u4fbf\u77e5\u4ed6\u786e\u662f\u5e08\u7236\u65b0\u6536\u7684\u5c0f\u5f92\u5f1f\u3002\u7b2c\u4e8c\u62db\u51fa\u624b\u5982\u7535\uff0c\u773c\u89c1\u4e00\u638c\u4fbf\u53ef\u6253\u5230\u4ed6\u80a9\u5934\uff0c\u751f\u6015\u6253\u4f24\u4e86\u4ed6\uff0c\u5e08\u7236\u8138\u4e0a\u987b\u4e0d\u597d\u770b\uff0c\u624b\u638c\u5c06\u5230\u65f6\u6f5c\u529b\u659c\u56de\uff0c\u53ea\u4f7f\u4e86\u4e09\u6210\u529b\uff0c\u54ea\u77e5\u9053\u5bf9\u65b9\u6ed1\u6e9c\u5f02\u5e38\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u7adf\u5c14\u8eb2\u5f00\uff0c\u4e0d\u89c9\u4e5f\u662f\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u597d\u5feb\u7684\u8eab\u6cd5\uff01\u201d\u62f3\u968f\u58f0\u843d\uff0c\u547c\u547c\u6570\u62db\u3002\u4ed6\u62f3\u6cd5\u4e0e\u5f52\u4e8c\u5a18\u4e00\u6a21\u4e00\u6837\uff0c\u4f46\u529f\u529b\u4e4b\u7eaf\uff0c\u6536\u53d1\u4e4b\u901f\uff0c\u5b9e\u5df2\u81fb\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u8881\u627f\u5fd7\u65e2\u60ca\u4e14\u4f69\uff0c\u5fc3\u60f3\u602a\u4e0d\u5f97\u4e8c\u5e08\u54e5\u4eab\u540d\u5982\u6b64\u4e4b\u76db\uff0c\u4ed6\u51e0\u4e2a\u5f92\u513f\u51fa\u6765\uff0c\u6b66\u6797\u4e2d\u4e00\u822c\u597d\u624b\u90fd\u5bf9\u4e4b\u606d\u656c\u5f02\u5e38\uff0c\u539f\u6765\u4ed6\u624b\u4e0b\u4e5f\u5f53\u771f\u4e86\u5f97\u3002\u8fd9\u65f6\u54ea\u91cc\u8fd8\u6562\u6709\u4e1d\u6beb\u6020\u5ffd\uff1f\u201c\u795e\u884c\u767e\u53d8\u201d\u7684\u8eab\u6cd5\u521d\u5b66\u4e4d\u7ec3\uff0c\u5c1a\u9887\u751f\u758f\uff0c\u5bf9\u4ed8\u5f52\u4e8c\u5a18\u7ef0\u7ef0\u6709\u4f59\uff0c\u7528\u6765\u4e0e\u4e8c\u5e08\u54e5\u8fc7\u62db\u53ea\u6015\u8eb2\u4e0d\u8fc7\u4ed6\u7684\u5341\u62f3\uff0c\u4e8e\u662f\u4e5f\u5c55\u5f00\u5e08\u95e8\u6240\u6388\u7edd\u827a\uff0c\u4ee5\u7834\u7389\u62f3\u6cd5\u62db\u67b6\u3002 \u3000\u3000\u4e8c\u4eba\u62f3\u6cd5\u76f8\u540c\uff0c\u8bf8\u822c\u53d8\u5316\u5747\u662f\u4e86\u7136\u4e8e\u80f8\uff0c\u8d8a\u6253\u8d8a\u5feb\uff0c\u610f\u5230\u5373\u6536\uff0c\u672a\u6cbe\u5148\u6b62\uff0c\u53ef\u8bf4\u662f\u719f\u6781\u800c\u6d41\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u5728\u534e\u5c71\u8ddf\u5e08\u7236\u62c6\u62db\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002\u201d\u4f46\u4e0e\u5e08\u7236\u62c6\u62db\uff0c\u660e\u77e5\u5e76\u65e0\u51f6\u9669\uff0c\u4e8c\u5e08\u54e5\u5374\u662f\u62f3\u638c\u6c89\u91cd\uff0c\u4e07\u4e07\u53d7\u4e0d\u5f97\u4ed6\u4e00\u62db\uff0c\u867d\u77e5\u9752\u9752\u547d\u5728\u9877\u523b\uff0c\u7adf\u65e0\u4f59\u6687\u53bb\u77a7\u5979\u4e00\u773c\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u80cc\u4e0a\u51b7\u6c57\u76f4\u6dcb\u3002\u4ed6\u6025\u6b32\u53bb\u6551\u9752\u9752\uff0c\u51fa\u62db\u7aed\u5c3d\u5168\u529b\uff0c\u66f4\u4e0d\u7559\u60c5\uff0c\u5fc3\u60f3\uff1a\u201c\u9752\u5f1f\u82e5\u662f\u4e27\u547d\uff0c\u5c31\u7b97\u4f60\u662f\u5e08\u54e5\uff0c\u6211\u4e5f\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u8fd9\u8fb9\u5b59\u4ef2\u541b\u89c1\u8881\u627f\u5fd7\u88ab\u5e08\u7236\u7eca\u4f4f\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5251\u6cd5\u66f4\u89c1\u51cc\u5389\u3002\u5218\u57f9\u751f\u4e0e\u6885\u5251\u548c\u540c\u65f6\u53eb\u9053\uff1a\u201c\u5e08\u59b9\u4e0d\u53ef\u4f24\u4eba\u2026\u2026\u201d\u53eb\u58f0\u672a\u6b47\uff0c\u5b59\u4ef2\u541b\u633a\u5251\u731b\u5411\u9752\u9752\u80f8\u53e3\u523a\u5230\u3002\u9752\u9752\u96be\u4ee5\u95ea\u907f\uff0c\u6025\u5411\u540e\u4ef0\uff0c\u6253\u4e2a\u6eda\u9003\u5f00\u3002\u5b59\u4ef2\u541b\u53cd\u5251\u6a2a\u524a\uff0c\u9752\u9752\u4e00\u4f4e\u5934\uff0c\u5934\u5dfe\u767b\u88ab\u524a\u843d\uff0c\u957f\u53d1\u56db\u6563\uff0c\u4e0b\u5782\u62ab\u8138\u3002\u5b59\u4ef2\u541b\u89c1\u5979\u539f\u6765\u662f\u4e2a\u5973\u5b50\uff0c\u4e00\u5446\u4e4b\u4e0b\uff0c\u633a\u5251\u53c8\u523a\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5934\u9876\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u597d\u72e0\u7684\u5973\u5a03\u5b50\uff01\u201d\u6811\u9876\u4e00\u56e2\u9ed1\u5f71\u76f4\u6251\u4e0b\u6765\uff0c\u8d77\u811a\u5c06\u5979\u957f\u5251\u8e22\u98de\u3002\u5b59\u4ef2\u541b\u5927\u5403\u4e00\u60ca\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u6708\u5149\u4e0b\u89c1\u90a3\u4eba\u9053\u88c5\u6253\u626e\uff0c\u987b\u7709\u4ff1\u767d\uff0c\u6321\u5728\u9752\u9752\u8eab\u524d\u3002\u5979\u4e0e\u6885\u3001\u5218\u4e8c\u4eba\u4e0d\u77e5\u8fd9\u8001\u9053\u662f\u8c01\uff0c\u5f52\u4e8c\u5a18\u5374\u8ba4\u5f97\u4ed6\u662f\u5e08\u7236\u7684\u597d\u53cb\u6728\u6851\u9053\u4eba\uff0c\u4fbf\u5373\u8fc7\u6765\u89c1\u793c\u3002\u6728\u6851\u7b11\u9053\uff1a\u201c\u522b\u5fd9\u884c\u793c\uff0c\u4e14\u77a7\u4ed6\u54e5\u513f\u4fe9\u7ec3\u6b66\u3002\u201d \u3000\u3000\u5f52\u4e8c\u5a18\u56de\u5934\u770b\u4e08\u592b\u65f6\uff0c\u53ea\u89c1\u4e24\u6761\u4eba\u5f71\u5939\u7740\u547c\u547c\u98ce\u58f0\uff0c\u6253\u5f97\u6fc0\u70c8\u5f02\u5e38\u3002\u5f52\u8f9b\u6811\u529b\u5927\u62db\u6c89\uff0c\u8881\u627f\u5fd7\u8eab\u624b\u5feb\u6377\u3002\u4e00\u4e2a\u719f\u5a34\u672c\u95e8\u6b66\u529f\uff0c\u4e00\u4e2a\u517c\u6536\u4e09\u5bb6\u4e4b\u957f\uff0c\u5f53\u771f\u5404\u64c5\u80dc\u573a\uff0c\u96be\u5206\u9ad8\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u521d\u65f6\u6302\u5ff5\u9752\u9752\u7684\u5b89\u5371\uff0c\u751a\u662f\u7126\u6025\uff0c\u5f85\u89c1\u6728\u6851\u9053\u4eba\u5230\u6765\u76f8\u6551\uff0c\u8fd9\u624d\u5168\u795e\u4e0e\u5e08\u5144\u62c6\u89e3\uff0c\u62db\u6570\u4e2d\u5f62\u540c\u62da\u547d\u7684\u72e0\u8fa3\u4e4b\u52b2\uff0c\u5374\u4e5f\u6536\u4e86\u3002\u4e24\u4eba\u8d8a\u6597\u8d8a\u7d27\uff0c\u672c\u95e8\u7684\u4f0f\u864e\u638c\u3001\u5288\u77f3\u62f3\u3001\u7834\u7389\u62f3\u3001\u6df7\u5143\u638c\u7b49\u7b49\u4e0a\u4e58\u529f\u592b\u5168\u90fd\u4f7f\u4e0a\u4e86\u3002\u8881\u627f\u5fd7\u6bd5\u7adf\u529f\u529b\u8f83\u6d45\uff0c\u4fee\u4e60\u6ca1\u5f52\u8f9b\u6811\u4e4b\u4e45\uff0c\u6597\u5230\u8fd1\u5343\u62db\u65f6\uff0c\u4fbf\u6e10\u843d\u4e0b\u98ce\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u89c1\u4e08\u592b\u8d8a\u6765\u8d8a\u662f\u653b\u591a\u5b88\u5c11\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u4f46\u89c1\u8881\u627f\u5fd7\u672c\u95e8\u529f\u592b\u5982\u6b64\u7eaf\u719f\uff0c\u4e5f\u5df2\u6beb\u4e0d\u6000\u7591\u4ed6\u786e\u662f\u5e08\u5f1f\uff0c\u4e8e\u4ed6\u62f3\u672f\u9020\u8be3\u4e4b\u7cbe\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u53c8\u62c6\u5f97\u6570\u5341\u62db\uff0c\u8881\u627f\u5fd7\u7a81\u7136\u62f3\u6cd5\u4e00\u53d8\uff0c\u8eab\u5f62\u4fbf\u5982\u6c34\u86c7\u822c\u6e38\u8d70\u4e0d\u5b9a\u3002\u8fd9\u662f\u91d1\u86c7\u90ce\u541b\u624b\u521b\u7684\u201c\u91d1\u86c7\u6e38\u8eab\u62f3\u201d\uff0c\u7cfb\u4ece\u6c34\u86c7\u5728\u6c34\u4e2d\u6e38\u52a8\u7684\u8eab\u6cd5\u4e2d\u6240\u609f\u51fa\u3002\u4e0d\u8fc7\u8fd9\u5957\u638c\u6cd5\u4e2d\u6240\u6709\u9634\u6bd2\u51fb\u654c\u7684\u62db\u6570\uff0c\u8881\u627f\u5fd7\u6b64\u65f6\u90fd\u820d\u5f03\u4e0d\u7528\uff0c\u5374\u52a0\u4e0a\u201c\u795e\u884c\u767e\u53d8\u201d\u8f7b\u529f\u3002\u4f46\u89c1\u4ed6\u500f\u8fdb\u500f\u9000\uff0c\u5ffd\u4e1c\u5ffd\u897f\uff0c\u65c1\u89c2\u5404\u4eba\u773c\u90fd\u82b1\u4e86\u3002\u5f52\u8f9b\u6811\u62f3\u6cd5\u867d\u9ad8\uff0c\u5374\u4e5f\u770b\u4e0d\u660e\u767d\u4ed6\u7684\u8eab\u6cd5\uff0c\u7adf\u65e0\u4e0b\u624b\u4e4b\u5904\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e0b\u7126\u8e81\uff0c\u5bfb\u601d\uff1a\u201c\u6211\u53f7\u79f0\u795e\u62f3\u65e0\u654c\uff0c\u53ef\u662f\u548c\u8fd9\u4e2a\u5c0f\u5e08\u5f1f\u5df2\u62c6\u4e86\u4e00\u5343\u62db\u4ee5\u4e0a\uff0c\u5140\u81ea\u5948\u4f55\u4ed6\u4e0d\u5f97\u3002\u6211\u8fd9\u4e2a\u5916\u53f7\uff0c\u53ef\u6709\u70b9\u540d\u4e0d\u526f\u5b9e\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6a2a\u8d8b\u659c\u884c\uff0c\u6b63\u81ea\u6025\u7ed5\u5708\u5b50\uff0c\u5f52\u8f9b\u6811\u5ffd\u5730\u8df3\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u4f4f\uff01\u201d\u8881\u627f\u5fd7\u75be\u5fd9\u7ad9\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u662f\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u4ed6\u6253\u6211\u4e0d\u5230\uff0c\u53cc\u65b9\u5c31\u7b97\u5e73\u624b\u3002\u5404\u4eba\u987e\u4f4f\u9762\u5b50\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u201d \u3000\u3000\u5374\u89c1\u5f52\u8f9b\u6811\u5411\u7a7a\u4e2d\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8001\u4eba\u5bb6\u4e5f\u6765\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u4e00\u682a\u5927\u6811\u4e0a\u8fde\u7eed\u7eb5\u4e0b\u56db\u4eba\uff0c\u5f53\u5148\u4e00\u4eba\u6b63\u662f\u6069\u5e08\u7a46\u4eba\u6e05\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u559c\uff0c\u62a2\u4e0a\u62dc\u5012\uff0c\u7ad9\u8d77\u8eab\u6765\u65f6\uff0c\u89c1\u5e08\u7236\u8eab\u540e\u662f\u5d14\u79cb\u5c71\u548c\u5927\u5e08\u5144\u94dc\u7b14\u94c1\u7b97\u76d8\u9ec4\u771f\uff0c\u6700\u540e\u4e00\u4eba\u7adf\u662f\u54d1\u5df4\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5ffd\u9047\u6069\u5e08\u6545\u4eba\uff0c\u6b23\u559c\u5f02\u5e38\uff0c\u548c\u54d1\u5df4\u6253\u4e86\u51e0\u4e2a\u624b\u52bf\uff0c\u5fc3\u60f3\u81ea\u5df1\u7ec8\u7a76\u9605\u5386\u592a\u6d45\uff0c\u53ea\u987e\u4e0e\u4e8c\u5e08\u54e5\u8fc7\u62db\uff0c\u6ca1\u7559\u795e\u56db\u4e0b\u60c5\u52bf\uff0c\u8981\u662f\u6811\u4e0a\u8eb2\u7740\u7684\u4e0d\u662f\u5e08\u7236\u800c\u662f\u654c\u4eba\uff0c\u5c82\u4e0d\u662f\u4e2d\u4e86\u4ed6\u4eba\u7684\u6697\u7b97\uff1f\u4e8c\u5e08\u54e5\u5374\u773c\u89c2\u516d\u8def\uff0c\u8033\u542c\u516b\u65b9\uff0c\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\u6bd5\u7adf\u4e0d\u540c\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u94a6\u4f69\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u6478\u6478\u8881\u627f\u5fd7\u7684\u5934\u9876\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u5927\u5e08\u54e5\u8bf4\u4e86\u4f60\u5728\u6d59\u6c5f\u8862\u5dde\u7684\u4e8b\uff0c\u505a\u5f97\u4e0d\u9519\u3002\u201d\u968f\u5373\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u5c11\u5e74\u4eba\u4e3a\u751a\u4e48\u4e0d\u656c\u5c0a\u957f\uff0c\u8ddf\u5e08\u54e5\u3001\u5e08\u5ac2\u52a8\u8d77\u624b\u6765\uff1f\u201d\u8881\u627f\u5fd7\u4f4e\u5934\u9053\uff1a\u201c\u662f\u5f1f\u5b50\u4e0d\u662f\uff0c\u4e0b\u6b21\u51b3\u8ba1\u4e0d\u6562\u5566\u3002\u201d\u8d70\u8fc7\u53bb\u5411\u5f52\u8f9b\u6811\u592b\u5987\u8fde\u4f5c\u4e86\u4e24\u4e2a\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u5411\u5e08\u54e5\u5e08\u5ac2\u8d54\u7f6a\u3002\u201d \u3000\u3000\u5f52\u4e8c\u5a18\u6027\u5b50\u76f4\u723d\uff0c\u5bf9\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u5012\u4e0d\u5fc5\u602a\u5e08\u5f1f\u52a8\u624b\uff0c\u90a3\u662f\u6211\u4eec\u592b\u5987\u903c\u4ed6\u7684\u3002\u6211\u4eec\u602a\u4ed6\u7528\u522b\u6d3e\u6b66\u529f\uff0c\u6765\u6298\u8fb1\u6211\u4eec\u8fd9\u51e0\u4e2a\u4e0d\u6210\u5668\u7684\u5f92\u5f1f\u3002\u201d\u8bf4\u7740\u5411\u6885\u5251\u548c\u7b49\u4e09\u4eba\u4e00\u6307\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u8bf4\u5230\u95e8\u6237\u4e4b\u89c1\uff0c\u6211\u5012\u770b\u5f97\u5f88\u6de1\u3002\u5582\uff0c\u5251\u548c\uff0c\u8fc7\u6765\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u8881\u5e08\u53d4\u8ddf\u5e08\u5144\u52a8\u624b\uff0c\u662f\u4ed6\u4e0d\u597d\u3002\u4f60\u4eec\u4e09\u4eba\u5374\u600e\u4e48\u53c8\u8ddf\u5e08\u53d4\u8fc7\u62db\u4e86\uff1f\u54b1\u4eec\u95e8\u4e2d\u7684\u5c0a\u5351\u4e4b\u5206\uff0c\u5927\u5bb6\u90fd\u4e0d\u7ba1\u4e86\u4e48\uff1f\u201d\u6885\u5251\u548c\u5728\u5e08\u7956\u9762\u524d\u4e0d\u6562\u9690\u7792\uff0c\u4fbf\u628a\u95f5\u5b50\u534e\u5bfb\u4ec7\u7684\u7ecf\u8fc7\uff0c\u539f\u539f\u672c\u672c\u8bf4\u4e86\uff0c\u63d0\u5230\u5b59\u4ef2\u541b\u65ad\u4eba\u81c2\u8180\u4e4b\u4e8b\uff0c\u53ea\u8bf4\u201c\u8ddf\u7126\u516c\u793c\u7684\u4e00\u540d\u5f92\u5f1f\u52a8\u4e86\u624b\u201d\uff0c\u5c31\u6b64\u8f7b\u63cf\u6de1\u5199\u7684\u4e00\u8a00\u5e26\u8fc7\u3002\u4ed6\u8a00\u8bed\u4e2d\u6240\u7740\u91cd\u7684\uff0c\u5374\u662f\u8881\u627f\u5fd7\u8e29\u65ad\u4e86\u5f52\u4e8c\u5a18\u8d50\u7ed9\u5b59\u4ef2\u541b\u7684\u957f\u5251\u3002 \u3000\u3000\u9752\u9752\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u4f4d\u98de\u5929\u9b54\u5973\u5b59\u4ef2\u541b\uff0c\u597d\u6ca1\u6765\u7531\u7684\uff0c\u4e00\u5251\u5c31\u628a\u4eba\u5bb6\u4e00\u6761\u81c2\u8180\u524a\u4e86\u4e0b\u6765\u3002\u90a3\u4e2a\u4eba\u53ea\u4e0d\u8fc7\u5949\u4e86\u5e08\u7236\u4e4b\u547d\u9001\u5c01\u4fe1\u6765\uff0c\u662f\u4e2a\u8001\u8001\u5b9e\u5b9e\u7684\u597d\u4eba\u3002\u8881\u5927\u54e5\u8bf4\uff0c\u4ed6\u534e\u5c71\u6d3e\u95e8\u4eba\u4e0d\u80fd\u6ee5\u4f24\u65e0\u8f9c\uff0c\u4ed6\u65e2\u89c1\u5230\u4e86\uff0c\u82e5\u662f\u4e0d\u7ba1\uff0c\u8981\u7ed9\u5e08\u7236\u8d23\u7f5a\u7684\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u5f97\u51fa\u5934\u7ba1\u4e0a\u8fd9\u6869\u4e8b\u3002\u4ed6\u8bf4\u65e0\u610f\u4e2d\u5f97\u7f6a\u4e86\u5e08\u54e5\u3001\u5e08\u5ac2\uff0c\u5fc3\u91cc\u96be\u8fc7\u5f97\u5f88\uff0c\u53ef\u53c8\u6ca1\u6709\u6cd5\u5b50\u3002\u201d\u5979\u77e5\u9053\u8881\u627f\u5fd7\u4e0d\u64c5\u8a00\u8f9e\uff0c\u4e00\u5207\u90fd\u4ee3\u4ed6\u8bf4\u4e86\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u8138\u5982\u4e25\u971c\uff0c\u95ee\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u201d\u5f52\u6c0f\u592b\u5987\u4e0d\u77e5\u6b64\u4e8b\uff0c\u671b\u7740\u5b59\u4ef2\u541b\u3002\u6885\u5251\u548c\u4f4e\u58f0\u9053\uff1a\u201c\u5b59\u5e08\u59b9\u5f53\u65f6\u8ba4\u5b9a\u4ed6\u662f\u574f\u4eba\uff0c\u662f\u4ee5\u624b\u4e0b\u6ca1\u6709\u5bb9\u60c5\uff0c\u800c\u4eca\u5df2\u5f88\u662f\u540e\u6094\uff0c\u8bf7\u5e08\u7956\u9976\u6055\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u54b1\u4eec\u534e\u5c71\u6d3e\u6700\u5927\u7684\u6212\u5f8b\u662f\u4e0d\u53ef\u6ee5\u4f24\u65e0\u8f9c\u3002\u8f9b\u6811\uff0c\u4f60\u6536\u8fd9\u5f92\u513f\u4e4b\u65f6\uff0c\u6709\u6ca1\u6559\u8bad\u8fc7\u5979\uff1f\u201d \u3000\u3000\u5f52\u8f9b\u6811\u4ece\u6765\u6ca1\u89c1\u8fc7\u5e08\u7236\u6c14\u5f97\u5982\u6b64\u5389\u5bb3\uff0c\u6025\u5fd9\u8dea\u5012\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u5931\u4e8e\u6559\u8bf2\uff0c\u662f\u5f1f\u5b50\u4e0d\u662f\u3002\u8bf7\u5e08\u7236\u606f\u6012\uff0c\u5f1f\u5b50\u4e00\u5b9a\u597d\u597d\u8d23\u7f5a\u5979\u3002\u201d\u5f52\u4e8c\u5a18\u3001\u6885\u3001\u5218\u3001\u5b59\u56db\u4eba\u5fd9\u90fd\u8ddf\u7740\u8dea\u5728\u5f52\u8f9b\u6811\u4e4b\u540e\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u6012\u6c14\u4e0d\u606f\uff0c\u9a82\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u89c1\u4e86\u8fd9\u4e8b\uff0c\u600e\u4e48\u6298\u65ad\u4e86\u5979\u7684\u5251\u5c31\u7b97\u4e86\u4e8b\uff1f\u600e\u4e48\u4e0d\u628a\u5979\u7684\u81c2\u8180\u4e5f\u780d\u4e0b\u6765\uff1f\u54b1\u4eec\u4e0d\u6b63\u81ea\u5df1\u95e8\u98ce\uff0c\u5c82\u4e0d\u88ab\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u4eec\u803b\u7b11\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u8dea\u4e0b\u78d5\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u5f1f\u5b50\u5904\u7f6e\u5f97\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u513f\uff0c\u201d\u8bf4\u7740\u5411\u9752\u9752\u4e00\u6307\uff0c\u5bf9\u5b59\u4ef2\u541b\u9053\uff1a\u201c\u53c8\u72af\u4e86\u751a\u4e48\u5341\u6076\u4e0d\u8d66\u7684\u6076\u884c\uff0c\u4f60\u5374\u8fde\u4f7f\u4e5d\u4e0b\u72e0\u62db\u6740\u7740\uff0c\u975e\u53d6\u5979\u6027\u547d\u4e0d\u53ef\uff1f\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u5b59\u4ef2\u541b\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u54ea\u6562\u8fc7\u53bb\uff1f\u4f0f\u5728\u5730\u4e0b\u8fde\u8fde\u78d5\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5f92\u5b59\u53ea\u9053\u5979\u662f\u7537\u4eba\uff0c\u662f\u4e2a\u8f7b\u8584\u4e4b\u5f92\u2026\u2026\u201d \u3000\u3000\u7a46\u4eba\u6e05\u6012\u9053\uff1a\u201c\u4f60\u524a\u4e0b\u5979\u5e3d\u5b50\uff0c\u5df2\u89c1\u5230\u5979\u662f\u5973\u5b50\uff0c\u5374\u4ecd\u4e0b\u6bd2\u624b\u3002\u518d\u8bf4\uff0c\u662f\u7537\u4eba\u5c31\u53ef\u6ee5\u6740\u5417\uff1f\u5355\u51ed\u4f60\u2018\u98de\u5929\u9b54\u5973\u2019\u8fd9\u56db\u5b57\u5916\u53f7\uff0c\u5c31\u53ef\u60f3\u89c1\u4f60\u5e73\u7d20\u4e3a\u4eba\u3002\u4f60\u4e0d\u8fc7\u6765\u5417\uff1f\u201d\u5f52\u4e8c\u5a18\u77e5\u9053\u5e08\u7236\u8981\u5c06\u5979\u70b9\u6210\u5e9f\u4eba\uff0c\u5378\u53bb\u5168\u8eab\u6b66\u529f\uff0c\u53ea\u5f97\u78d5\u5934\u6c42\u9053\uff1a\u201c\u5e08\u7236\u4f60\u8001\u4eba\u5bb6\u8bf7\u606f\u6012\uff0c\u5f1f\u5b50\u56de\u53bb\uff0c\u4e00\u5b9a\u5c06\u5979\u91cd\u91cd\u8d23\u6253\u3002\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4f60\u780d\u4e0b\u5979\u7684\u80a9\u8180\uff0c\u660e\u513f\u62ac\u5230\u7126\u5bb6\u53bb\u6c42\u60c5\u8d54\u7f6a\u3002\u201d\u5f52\u4e8c\u5a18\u4e0d\u6562\u4f5c\u58f0\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5f92\u513f\u5df2\u5411\u7126\u5bb6\u8d54\u8fc7\u7f6a\uff0c\u53c8\u7b54\u5e94\u4f20\u6388\u4e00\u95e8\u6b66\u529f\u7ed9\u90a3\u4eba\uff0c\u56e0\u6b64\u7126\u5bb6\u8fd9\u8fb9\u662f\u6ca1\u4e8b\u4e86\u3002\u201d\u7a46\u4eba\u6e05\u54fc\u4e86\u58f0\uff0c\u9053\uff1a\u201c\u6728\u6851\u9053\u5144\u5e78\u4e8f\u4e0d\u662f\u5916\u4eba\uff0c\u5426\u5219\u771f\u53eb\u4ed6\u7b11\u6b7b\u5566\u3002\u7a76\u7adf\u662f\u4ed6\u806a\u660e\uff0c\u5403\u4e86\u672c\u95e8\u4e2d\u4e0d\u8096\u5b50\u5f1f\u7684\u4e8f\uff0c\u4e00\u751f\u4e0d\u6536\u5f92\u5f1f\uff0c\u4e5f\u514d\u5f97\u4e22\u8138\u5455\u6c14\u3002\u90fd\u8d77\u6765\u5427\uff01\u201d\u4f17\u4eba\u90fd\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u5411\u5b59\u4ef2\u541b\u4e00\u77aa\u773c\uff0c\u5b59\u4ef2\u541b\u5413\u5f97\u53c8\u8dea\u4e86\u4e0b\u6765\u3002\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u62ff\u5251\u8fc7\u6765\u3002\u201d\u5b59\u4ef2\u541b\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u53ea\u5f97\u53cc\u624b\u6367\u5251\u8fc7\u9876\uff0c\u732e\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u6293\u4f4f\u5251\u67c4\uff0c\u5fae\u5fae\u4e00\u6296\uff0c\u5b59\u4ef2\u541b\u53ea\u89c9\u5de6\u624b\u4e00\u75db\uff0c\u9c9c\u8840\u76f4\u6d41\uff0c\u539f\u6765\u4e00\u6839\u5c0f\u6307\u5df2\u88ab\u524a\u843d\u3002\u7a46\u4eba\u6e05\u518d\u5c06\u5251\u4e00\u6296\uff0c\u957f\u5251\u65ad\u4e3a\u4e24\u622a\uff0c\u559d\u9053\uff1a\u201c\u4ece\u4eca\u800c\u540e\uff0c\u4e0d\u8bb8\u4f60\u518d\u7528\u5251\u3002\u201d\u5b59\u4ef2\u541b\u5fcd\u75db\u7b54\u9053\uff1a\u201c\u662f\u3002\u5f92\u5b59\u77e5\u9519\u4e86\u3002\u201d\u5979\u53c8\u7f9e\u53c8\u60ca\uff0c\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u6495\u4e0b\u8863\u89d2\uff0c\u7ed9\u5979\u5305\u88f9\u4f24\u5904\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u597d\u5566\uff0c\u5e08\u7956\u4e0d\u4f1a\u518d\u7f5a\u4f60\u5566\u3002\u201d \u3000\u3000\u6885\u5251\u548c\u89c1\u5e08\u7956\u968f\u624b\u4e00\u6296\uff0c\u957f\u5251\u7acb\u65ad\uff0c\u8fd9\u624d\u77e5\u8881\u627f\u5fd7\u63a5\u8fde\u9707\u65ad\u4ed6\u624b\u4e2d\u957f\u5251\uff0c\u786e\u662f\u672c\u95e8\u529f\u592b\uff0c\u5fc3\u60f3\u539f\u6765\u672c\u95e8\u6b66\u672f\u5982\u6b64\u7cbe\u5999\uff0c\u6211\u53ea\u5b66\u5f97\u4e00\u70b9\u513f\u76ae\u6bdb\uff0c\u4fbf\u5728\u5916\u9762\u8000\u6b66\u626c\u5a01\uff0c\u60f3\u8d77\u8fc7\u53bb\u7684\u72c2\u5984\u50b2\u6162\uff0c\u751a\u662f\u60f6\u6050\u60ed\u6127\uff0c\u53c8\u6015\u5e08\u7956\u89c1\u8d23\uff0c\u4e0d\u7981\u6c57\u6d41\u6d43\u80cc\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u72e0\u72e0\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u5374\u4e0d\u8a00\u8bed\uff0c\u8f6c\u5934\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u7b54\u5141\u4f20\u6388\u4eba\u5bb6\u529f\u592b\uff0c\u53ef\u5f97\u597d\u597d\u7684\u6559\u3002\u4f60\u6559\u751a\u4e48\u5440\uff1f\u201d\u8881\u627f\u5fd7\u8138\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u672a\u5f97\u5e08\u7236\u5141\u51c6\uff0c\u4e0d\u6562\u5c06\u672c\u95e8\u6b66\u529f\u5984\u6388\u522b\u4eba\uff0c\u53ea\u60f3\u4f20\u4ed6\u4e00\u5957\u72ec\u81c2\u5200\u6cd5\u3002\u90a3\u662f\u5f1f\u5b50\u65e0\u610f\u4e2d\u5b66\u6765\u7684\u6742\u5b66\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4f60\u7684\u6742\u5b66\u4e5f\u592a\u591a\u4e86\u4e00\u70b9\u5440\uff0c\u521a\u624d\u89c1\u4f60\u548c\u4f60\u4e8c\u5e08\u54e5\u8fc7\u62db\uff0c\u597d\u4f3c\u7528\u4e0a\u4e86\u6728\u6851\u9053\u957f\u7684\u2018\u795e\u884c\u767e\u53d8\u2019\u529f\u592b\u3002\u6709\u8fd9\u4f4d\u68cb\u53cb\u4e00\u529b\u5e2e\u4f60\uff0c\u4e8c\u5e08\u54e5\u81ea\u7136\u662f\u5948\u4f55\u4f60\u4e0d\u5f97\u4e86\u3002\u201d\u8bf4\u7f62\u5475\u5475\u5927\u7b11\u3002\u6728\u6851\u9053\u4eba\u7b11\u9053\uff1a\u201c\u627f\u5fd7\uff0c\u4f60\u6562\u4e0d\u6562\u8ddf\u4f60\u5e08\u7236\u6492\u8c0e\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u597d\uff0c\u6211\u95ee\u4f60\uff0c\u81ea\u4ece\u79bb\u5f00\u534e\u5c71\u4e4b\u540e\uff0c\u6211\u6709\u6ca1\u6709\u4eb2\u624b\u4f20\u6388\u8fc7\u4f60\u6b66\u529f\uff1f\u542c\u7740\uff0c\u6211\u6709\u6ca1\u4eb2\u624b\u4f20\u6388\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u8fd9\u624d\u4f1a\u610f\uff0c\u6728\u6851\u6240\u4ee5\u8981\u9752\u9752\u8f6c\u6388\uff0c\u539f\u6765\u662f\u6015\u5e08\u7236\u53ca\u4e8c\u5e08\u54e5\u89c1\u602a\uff0c\u8fd9\u4f4d\u9053\u957f\u673a\u7075\u591a\u667a\uff0c\u4e00\u5207\u65e9\u5728\u4ed6\u610f\u6599\u4e4b\u4e2d\uff0c\u4e8e\u662f\u7b54\u9053\uff1a\u201c\u81ea\u4e0b\u534e\u5c71\u4e4b\u540e\uff0c\u9053\u957f\u6ca1\u4eb2\u624b\u6559\u8fc7\u6211\u6b66\u529f\uff0c\u8fd9\u6b21\u89c1\u9762\uff0c\u5c31\u53ea\u4e0b\u8fc7\u4e24\u76d8\u68cb\u3002\u201d\u53c8\u60f3\uff1a\u201c\u8fd9\u8bdd\u867d\u975e\u8c0e\u8a00\uff0c\u6bd5\u7adf\u7528\u610f\u5728\u6b3a\u7792\u5e08\u7236\uff0c\u81f3\u5c11\u662f\u5b58\u5fc3\u53d6\u5de7\u3002\u4f46\u8fd9\u65f6\u660e\u8a00\uff0c\u4e8c\u5e08\u54e5\u5fc5\u5b9a\u4f1a\u5bf9\u9053\u957f\u89c1\u602a\uff0c\u5f85\u4f1a\u80cc\u7740\u4e8c\u5e08\u54e5\uff0c\u987b\u5f97\u5411\u5e08\u7236\u7980\u660e\u5b9e\u60c5\u3002\u201d \u3000\u3000\u6728\u6851\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u4f60\u518d\u8ddf\u5e08\u5144\u7ec3\u8fc7\u3002\u6211\u4ee5\u524d\u6559\u8fc7\u4f60\u7684\u6b66\u529f\uff0c\u4e00\u62db\u90fd\u4e0d\u8bb8\u7528\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\u53f7\u79f0\u65e0\u654c\u795e\u62f3\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002\u5f1f\u5b50\u672c\u5df2\u62b5\u6321\u4e0d\u4f4f\uff0c\u53ea\u6709\u8eb2\u95ea\u907f\u8ba9\uff0c\u6b63\u8981\u8ba4\u8f93\uff0c\u8bf7\u4e8c\u5e08\u54e5\u505c\u624b\uff0c\u54ea\u77e5\u4ed6\u5df2\u89c1\u5230\u4e86\u5e08\u7236\u3002\u4e00\u8fc7\u62db\uff0c\u5f1f\u5b50\u5c31\u518d\u6ca1\u80fd\u987e\u5230\u65c1\u7684\u5730\u65b9\u3002\u201d\u7a46\u4eba\u6e05\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u597d\u5566\u3002\u9053\u957f\u65e2\u7136\u8981\u4f60\u4eec\u7ec3\uff0c\u732e\u4e00\u4e0b\u4e11\u53c8\u6015\u600e\u7684\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u65e0\u5948\uff0c\u53ea\u5f97\u6574\u4e00\u4e0b\u8863\u895f\uff0c\u8d70\u8fd1\u53bb\u5411\u5f52\u8f9b\u6811\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u8bf7\u4e8c\u5e08\u54e5\u6307\u6559\u3002\u201d\u5f52\u8f9b\u6811\u62f1\u624b\u9053\uff1a\u201c\u597d\u8bf4\u3002\u201d\u8f6c\u5934\u5bf9\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u6211\u4eec\u9519\u4e86\u8bf7\u5e08\u7236\u6307\u70b9\u3002\u201d\u4e24\u4eba\u91cd\u53c8\u653e\u5bf9\u3002 \u3000\u3000\u8fd9\u4e00\u756a\u6bd4\u8bd5\uff0c\u548c\u521a\u624d\u53c8\u4e0d\u76f8\u540c\u3002\u5f52\u8f9b\u6811\u5728\u6728\u6851\u9053\u4eba\u3001\u5e08\u7236\u3001\u5927\u5e08\u5144\u53ca\u4f17\u5f92\u5f1f\u4e4b\u524d\u54ea\u80fd\u4e22\u8138\uff1f\u53ea\u89c1\u4ed6\u653b\u65f6\u8fc5\u5982\u96f7\u9706\uff0c\u5b88\u65f6\u51dd\u82e5\u5c71\u5cb3\uff0c\u540d\u5bb6\u8eab\u624b\uff0c\u679c\u771f\u4e0d\u51e1\u3002\u8881\u627f\u5fd7\u4e5f\u662f\u6709\u653b\u6709\u5b88\uff0c\u6240\u4f7f\u7684\u5168\u662f\u5e08\u95e8\u7edd\u6280\uff0c\u62c6\u4e86\u4e00\u767e\u4f59\u62db\uff0c\u4e24\u4eba\u62f3\u6cd5\u4e2d\u4e1d\u6beb\u4e0d\u89c1\u7834\u7efd\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u4e0e\u6728\u6851\u5728\u4e00\u65c1\u637b\u987b\u5fae\u7b11\u3002\u6728\u6851\u7b11\u9053\uff1a\u201c\u771f\u662f\u660e\u5e08\u95e8\u4e2d\u51fa\u9ad8\u5f92\uff0c\u5f3a\u5c06\u624b\u4e0b\u65e0\u5f31\u5175\u3002\u770b\u4e86\u4f60\u8fd9\u4e24\u4f4d\u8d24\u5f92\uff0c\u6211\u8001\u9053\u53c8\u6709\u70b9\u773c\u7ea2\uff0c\u540e\u6094\u5f53\u5e74\u4e0d\u597d\u597d\u6559\u51e0\u4e2a\u5f92\u513f\u4e86\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u53c8\u62c6\u4e86\u6570\u5341\u62db\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u4e45\u6597\u4e0d\u4e0b\uff0c\u6e10\u6e10\u52a0\u91cd\u52b2\u529b\uff0c\u653b\u52bf\u987f\u9aa4\u3002\u8881\u627f\u5fd7\u5bfb\u601d\uff0c\u6253\u5230\u8fd9\u65f6\uff0c\u6211\u8be5\u5f53\u8ba9\u4ed6\u4e00\u62db\u4e86\u3002\u4f46\u5f52\u8f9b\u6751\u62db\u62db\u5389\u5bb3\u5f02\u5e38\uff0c\u53ea\u8981\u62db\u67b6\u4e0d\u7528\u5168\u529b\uff0c\u7acb\u5373\u8eab\u53d7\u91cd\u4f24\uff0c\u8981\u8ba9\u4ed6\u4e00\u62db\uff0c\u5b9e\u662f\u5927\u5927\u7684\u96be\u4e8b\uff0c\u6597\u5230\u5206\u9645\uff0c\u5ffd\u60f3\uff1a\u201c\u542c\u5e08\u7236\u521a\u624d\u8bed\u6c14\uff0c\u5bf9\u6211\u8d2a\u591a\u52a1\u5f97\uff0c\u7814\u4e60\u522b\u6d3e\u6742\u5b66\uff0c\u4f3c\u4e4e\u4e0d\u5927\u8d5e\u53ef\u3002\u5148\u524d\u6211\u5355\u4f7f\u672c\u95e8\u62f3\u6cd5\uff0c\u6570\u767e\u62db\u540e\u4fbf\u5c45\u52a3\u52bf\uff0c\u76f4\u81f3\u7528\u4e0a\u4e86\u6728\u6851\u9053\u957f\u4e0e\u91d1\u86c7\u90ce\u541b\u7684\u529f\u592b\uff0c\u624d\u7a0d\u5fae\u5360\u4e86\u4e00\u70b9\u4e0a\u98ce\uff0c\u73b0\u4e0b\u53c8\u5355\u4f7f\u672c\u95e8\u6b66\u529f\uff0c\u4ecd\u53ea\u80fd\u4ee5\u4e0b\u98ce\u4e4b\u52bf\u6253\u6210\u5e73\u624b\uff0c\u8fd9\u5c82\u4e0d\u662f\u8bf4\u522b\u6d3e\u6b66\u529f\u80dc\u8fc7\u672c\u95e8\u529f\u592b\u4e86\uff1f\u6211\u5f97\u4ee5\u522b\u6d3e\u6b66\u529f\u8f93\u4e86\u7ed9\u4ed6\u3002\u9053\u957f\u4e0d\u8bb8\u6211\u7528\u4ed6\u6240\u4f20\u7684\u529f\u592b\uff0c\u6211\u4fbf\u4f7f\u91d1\u86c7\u90ce\u541b\u7684\u6b66\u529f\u3002\u201d\u5f53\u4e0b\u62f3\u62db\u4e00\u53d8\uff0c\u4f7f\u7684\u662f\u4e00\u5957\u201c\u91d1\u86c7\u64d2\u9e64\u62f3\u201d\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u89c1\u62db\u62c6\u62db\uff0c\u653b\u52bf\u4e1d\u6beb\u4e0d\u7f13\u3002\u8881\u627f\u5fd7\u7a81\u7136\u8fde\u7eed\u56db\u8bb0\u602a\u62db\uff0c\u5f52\u8f9b\u6811\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u62f3\u81ea\u4fdd\u3002\u8881\u627f\u5fd7\u7f13\u4e86\u4e00\u53e3\u6c14\uff0c\u8fd0\u6c14\u4e8e\u80cc\u3002\u5f52\u8f9b\u6751\u89c1\u4ed6\u540e\u5fc3\u7a81\u7136\u9732\u51fa\u7a7a\u9699\uff0c\u89c1\u865a\u5373\u5165\uff0c\u6b66\u5bb6\u672c\u6027\uff0c\u5f53\u4e0b\u6beb\u4e0d\u601d\u7d22\uff0c\u4e00\u638c\u6251\u51fb\u5bf9\u65b9\u80cc\u5fc3\u3002\u8881\u627f\u5fd7\u65e9\u5df2\u6709\u5907\uff0c\u8eab\u5b50\u5411\u524d\u4e00\u6251\uff0c\u8dcc\u51fa\u56db\u4e94\u6b65\uff0c\u56de\u8eab\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u8f93\u4e86\u3002\u201d\u5f52\u8f9b\u6811\u4e00\u638c\u6253\u51fa\uff0c\u4fbf\u5373\u61ca\u6094\uff0c\u53ea\u6015\u5e08\u5f1f\u8981\u53d7\u91cd\u4f24\uff0c\u5fd9\u62a2\u4e0a\u53bb\u6276\uff0c\u54ea\u77e5\u4ed6\u832b\u7136\u672a\u89c9\uff0c\u751a\u662f\u60ca\u7591\u3002\u539f\u6765\u8881\u627f\u5fd7\u65e2\u5df2\u5148\u8fd0\u6c14\u4e8e\u80cc\uff0c\u4e58\u52bf\u524d\u6251\u65f6\u518d\u6d88\u53bb\u4e86\u5bf9\u65b9\u5927\u534a\u638c\u529b\uff0c\u53c8\u6709\u6728\u6851\u6240\u8d50\u7684\u91d1\u4e1d\u80cc\u5fc3\u4fdd\u62a4\uff0c\u867d\u7136\u80cc\u4e0a\u4e00\u9635\u5267\u75db\uff0c\u5374\u672a\u53d7\u4f24\u3002 \u3000\u3000\u8881\u627f\u5fd7\u56de\u8fc7\u8eab\u6765\uff0c\u4f17\u4eba\u89c1\u4ed6\u957f\u8863\u540e\u5fc3\u88c2\u6210\u788e\u7247\uff0c\u4e00\u9635\u98ce\u8fc7\u53bb\uff0c\u8863\u7247\u968f\u98ce\u98de\u821e\u3002\u9752\u9752\u6781\u4e3a\u5173\u5fc3\uff0c\u5fd9\u5954\u8fc7\u6765\u95ee\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\u4e86\u5417\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u5411\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u4f60\u529f\u592b\u786e\u6709\u7cbe\u8fdb\uff0c\u4f46\u8fd9\u4e00\u62db\u4f7f\u5f97\u592a\u72e0\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u662f\uff0c\u8881\u5e08\u5f1f\u6b66\u529f\u4e86\u5f97\uff0c\u5f1f\u5b50\u5f88\u662f\u4f69\u670d\u3002\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4ed6\u672c\u95e8\u529f\u529b\u662f\u4e0d\u53ca\u4f60\u7cbe\u7eaf\uff0c\u8fd8\u5dee\u7740\u8fd9\u4e48\u4e00\u5927\u622a\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u524d\u4e9b\u65f6\u5019\u66fe\u542c\u4eba\u8bf4\uff0c\u4f60\u4eec\u592b\u5987\u7eb5\u5bb9\u5f92\u5f1f\uff0c\u5728\u5916\u9762\u62db\u6447\u5f97\u5f88\u662f\u5389\u5bb3\u3002\u6211\u672c\u6765\u60f3\u4f60\u59bb\u5b50\u867d\u7136\u4e0d\u5927\u660e\u767d\u4e8b\u7406\uff0c\u4f60\u8fd8\u4e0d\u662f\u90a3\u6837\u7684\u4eba\uff0c\u4f46\u77a7\u4f60\u521a\u624d\u8fd9\u6837\u5bf9\u4ed8\u81ea\u5df1\u5e08\u5f1f\uff0c\u54fc\uff01\u201d\u5f52\u8f9b\u6811\u4f4e\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u77e5\u9519\u4e86\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u6bd4\u6b66\u8fc7\u62db\uff0c\u4e0b\u624b\u8c01\u4e5f\u4e0d\u80fd\u5bb9\u60c5\uff0c\u53cd\u6b63\u627f\u5fd7\u53c8\u6ca1\u53d7\u4f24\uff0c\u4f60\u8fd9\u8001\u513f\u8fd8\u8bf4\u751a\u4e48\u7684\uff1f\u201d\u7a46\u4eba\u6e05\u8fd9\u624d\u4e0d\u8a00\u8bed\u4e86\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u592b\u5987\u6210\u540d\u5df2\u4e45\uff0c\u9690\u7136\u662f\u6c5f\u5357\u6b66\u6797\u9886\u8896\uff0c\u8fd9\u6b21\u88ab\u5e08\u7236\u5f53\u4f17\u8d23\u9a82\uff0c\u867d\u56e0\u5e08\u6069\u6df1\u91cd\uff0c\u4e8e\u5e08\u7236\u5e76\u65e0\u6028\u603c\u4e4b\u610f\uff0c\u4f46\u5bf9\u8881\u627f\u5fd7\u5374\u66f4\u662f\u6000\u6124\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u95ef\u738b\u4eca\u79cb\u8981\u5927\u4e3e\u8d77\u4e8b\uff0c\u4f60\u4eec\u62db\u96c6\u95e8\u4eba\uff0c\u7acb\u5373\u7740\u624b\u8054\u7edc\u6c5f\u5357\u6b66\u6797\u8c6a\u6770\uff0c\u4e00\u5f85\u95ef\u738b\u4e49\u65d7\u5357\u4e0b\uff0c\u4fbf\u5373\u63ed\u7aff\u54cd\u5e94\u3002\u201d \u3000\u3000\u5f52\u8f9b\u6811\u592b\u5987\u9f50\u58f0\u5e94\u9053\uff1a\u201c\u662f\u3002\u201d\u7a46\u4eba\u6e05\u773c\u671b\u5f52\u8f9b\u6811\uff0c\u8138\u8272\u6e10\u8f6c\u6148\u548c\uff0c\u6e29\u8a00\u9053\uff1a\u201c\u8f9b\u6811\uff0c\u4f60\u83ab\u8bf4\u6211\u504f\u7231\u5c0f\u5f92\u5f1f\u3002\u4f60\u5e74\u7eaa\u867d\u5df2\u4e0d\u5c0f\uff0c\u5728\u6211\u5fc3\u4e2d\uff0c\u4f60\u4ecd\u4e0e\u5f53\u5e74\u521d\u4e0a\u534e\u5c71\u65f6\u7684\u5c0f\u5f92\u5f1f\u4e00\u822c\u65e0\u5f02\u3002\u201d \u3000\u3000\u5f52\u8f9b\u6811\u4f4e\u4e0b\u5934\u6765\uff0c\u5fc3\u4e2d\u4e00\u9635\u6e29\u6696\uff0c\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u5f1f\u5b50\u5fc3\u4e2d\u4e5f\u51b3\u6ca1\u8bf4\u5e08\u7236\u504f\u5fc3\u3002\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u4f60\u6027\u5b50\u5411\u6765\u6897\u76f4\uff0c\u4e09\u5341\u5e74\u6765\u4e13\u5fc3\u7ec3\u6b66\uff0c\u65c1\u7684\u4e8b\u60c5\u66f4\u662f\u751a\u4e48\u4e5f\u4e0d\u613f\u591a\u60f3\u3002\u53ef\u662f\u5929\u4e0b\u7684\u4e8b\u60c5\uff0c\u5e76\u975e\u5355\u51ed\u6b66\u529f\u9ad8\u5f3a\u4fbf\u53ef\u529e\u5f97\u4e86\u7684\u3002\u9047\u4e0a\u4e86\u5927\u4e8b\uff0c\u66f4\u987b\u7ec6\u601d\u524d\u56e0\u540e\u679c\uff0c\u4e0d\u53ef\u8f7b\u4fe1\u4eba\u8a00\u3002\u201d\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u662f\uff0c\u5f1f\u5b50\u7262\u7262\u8bb0\u4f4f\u5e08\u7236\u7684\u6559\u8bad\u3002\u201d \u3000\u3000\u7a46\u4eba\u6e05\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u548c\u4f60\u8fd9\u4f4d\u5c0f\u670b\u53cb\u52a8\u8eab\u53bb\u5317\u4eac\uff0c\u6253\u63a2\u671d\u5ef7\u52a8\u9759\uff0c\u4f46\u4e0d\u5f97\u6253\u8349\u60ca\u86c7\uff0c\u4e5f\u4e0d\u80fd\u4f24\u5bb3\u7687\u5e1d\u548c\u671d\u4e2d\u6743\u8981\uff0c\u82e5\u662f\u8bbf\u5230\u91cd\u5927\u6d88\u606f\uff0c\u5c31\u53bb\u9655\u897f\u62a5\u4fe1\u3002\u201d\u8881\u627f\u5fd7\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u6211\u4eca\u665a\u8981\u53bb\u89c1\u4e03\u5341\u4e8c\u5c9b\u76df\u4e3b\u90d1\u8d77\u4e91\u548c\u6e05\u51c9\u5bfa\u7684\u5341\u529b\u5927\u5e08\u3002\u542c\u8bf4\u5341\u529b\u5927\u5e08\u521a\u63a5\u5230\u4e94\u53f0\u5c71\u6e05\u51c9\u5bfa\u4f4f\u6301\u6cd5\u65e8\uff0c\u6d3e\u4ed6\u63a5\u4efb\u6cb3\u5357\u5357\u9633\u6e05\u51c9\u4e0b\u9662\u7684\u4f4f\u6301\uff0c\u4e00\u6765\u5411\u4ed6\u9053\u559c\uff0c\u4e8c\u6765\u8981\u8ddf\u4ed6\u5546\u91cf\u5546\u91cf\u6cb3\u5357\u6b66\u6797\u4e2d\u7684\u4e8b\u60c5\u3002\u9053\u5144\uff0c\u4f60\u8981\u53bb\u54ea\u91cc\uff1f\u201d\u6728\u6851\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec1\u4eba\u4e49\u58eb\uff0c\u5fe7\u56fd\u4e3a\u6c11\uff0c\u6574\u5929\u5fd9\u5f97\u9a6c\u4e0d\u505c\u8e44\u3002\u8d2b\u9053\u5374\u662f\u95f2\u4e91\u91ce\u9e64\uff0c\u6211\u60f3\u803d\u6401\u4f60\u5c0f\u5f92\u5f1f\u51e0\u5929\u529f\u592b\uff0c\u6210\u4e48\uff1f\u201d\u7a46\u4eba\u6e05\u7b11\u9053\uff1a\u201c\u53cd\u6b63\u4ed6\u7b54\u5e94\u6559\u4eba\u5bb6\u6b66\u529f\uff0c\u5728\u5357\u4eac\u603b\u5f97\u8fd8\u6709\u51e0\u5929\u9017\u7559\u3002\u4f60\u4eec\u591a\u4e0b\u51e0\u76d8\u68cb\u5427\u3002\u4f60\u8fd8\u6709\u591a\u5c11\u672c\u4e8b\uff0c\u7d22\u6027\u4e00\u80a1\u8111\u513f\u4f20\u4e86\u4ed6\u5427\u3002\u201d \u3000\u3000\u6728\u6851\u5374\u4f3c\u610f\u5174\u9611\u73ca\uff0c\u9eef\u7136\u9053\uff1a\u201c\u8fd9\u6b21\u4e0b\u4e86\u8fd9\u51e0\u5c40\u68cb\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ee5\u540e\u662f\u4e0d\u662f\u8fd8\u6709\u5f97\u4e0b\u3002\u201d\u7a46\u4eba\u6e05\u4e00\u6115\uff0c\u9053\uff1a\u201c\u9053\u5144\u4f55\u51fa\u6b64\u8a00\uff1f\u773c\u4e0b\u6c11\u6028\u5982\u6cb8\uff0c\u95ef\u738b\u5927\u4e8b\u6307\u65e5\u53ef\u6210\u3002\u5c06\u6765\u56db\u6d77\u5bb4\u5b89\uff0c\u5929\u4e0b\u592a\u5e73\uff0c\u4f17\u767e\u59d3\u5b89\u5c45\u4e50\u4e1a\uff0c\u54b1\u4eec\u65e0\u4e8b\u53ef\u4e3a\u3002\u522b\u8bf4\u627f\u5fd7\uff0c\u8fde\u6211\u4e5f\u53ef\u5929\u5929\u966a\u4f60\u4e0b\u68cb\u3002\u201d \u3000\u3000\u6728\u6851\u6447\u5934\u9053\uff1a\u201c\u672a\u5fc5\uff0c\u672a\u5fc5\uff01\u65e7\u52ab\u6253\u5b8c\uff0c\u65b0\u52ab\u53c8\u751f\uff0c\u5c40\u4e2d\u65e2\u6709\u767d\u5b50\u9ed1\u5b50\uff0c\u8fd9\u52ab\u5c31\u5faa\u73af\u4e0d\u5c3d\u3002\u201d\u7a46\u4eba\u6e05\u7b11\u9053\uff1a\u201c\u591a\u65e5\u4e0d\u89c1\uff0c\u9053\u5144\u609f\u9053\u66f4\u6df1\u3002\u6211\u4eec\u4fd7\u4eba\uff0c\u8fd9\u4e9b\u7384\u673a\u53ef\u5c31\u4e0d\u61c2\u4e86\u3002\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u62f1\u624b\u9053\u522b\u3002\u9ec4\u771f\u548c\u5d14\u79cb\u5c71\u90fd\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u54d1\u5df4\u5374\u7ad9\u4f4f\u4e0d\u52a8\uff0c\u5927\u6253\u624b\u52bf\uff0c\u8981\u548c\u8881\u627f\u5fd7\u5728\u4e00\u8d77\u3002\u7a46\u4eba\u6e05\u70b9\u5934\u5141\u53ef\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8bb0\u6302\u4f60\u7684\u5c0f\u670b\u53cb\uff0c\u5c31\u8ddf\u7740\u4ed6\u5427\u3002\u201d\u54d1\u5df4\u5927\u559c\uff0c\u5954\u8fc7\u6765\u5c06\u8881\u627f\u5fd7\u62b1\u8d77\uff0c\u5c06\u4ed6\u63b7\u5411\u7a7a\u4e2d\uff0c\u5f85\u4ed6\u843d\u4e0b\uff0c\u4f38\u624b\u63a5\u4f4f\uff0c\u90a3\u662f\u8881\u627f\u5fd7\u5e7c\u65f6\u4ed6\u4e8c\u4eba\u5728\u534e\u5c71\u5e38\u5e72\u7684\u73a9\u610f\u3002 \u3000\u3000\u9752\u9752\u5413\u4e86\u4e00\u8df3\uff0c\u6708\u5149\u4e0b\u89c1\u4ed6\u8138\u6709\u559c\u8272\uff0c\u624d\u77e5\u4ed6\u5e76\u65e0\u6076\u610f\u3002 \u3000\u3000\u54d1\u5df4\u8ddf\u7740\u4ece\u80cc\u4e0a\u5305\u88b1\u4e2d\u62bd\u51fa\u4e00\u67c4\u5251\u6765\uff0c\u4ea4\u7ed9\u8881\u627f\u5fd7\uff0c\u6b63\u662f\u90a3\u67c4\u91d1\u86c7\u5251\u3002\u539f\u6765\u4ed6\u4e0a\u6b21\u968f\u8881\u627f\u5fd7\u8fdb\u5165\u5c71\u6d1e\u63d2\u56de\u91d1\u86c7\u5251\uff0c\u6b64\u6b21\u79bb\u5c71\uff0c\u89c1\u7a46\u4eba\u6e05\u793a\u610f\u8981\u53bb\u548c\u8881\u627f\u5fd7\u76f8\u4f1a\uff0c\u5fc3\u60f3\u5c71\u4e0a\u65e0\u4eba\uff0c\u8fd9\u628a\u5b9d\u5251\u53ef\u522b\u8ba9\u4eba\u5077\u4e86\u53bb\uff0c\u4e8e\u662f\u8fdb\u6d1e\u53bb\u62d4\u4e86\u51fa\u6765\uff0c\u85cf\u5728\u5305\u88b1\u4e4b\u4e2d\uff0c\u5374\u8fde\u7a46\u4eba\u6e05\u4e5f\u4e0d\u77e5\u9053\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6b64\u5251\u662f\u9752\u5f1f\u7236\u4eb2\u7684\u9057\u7269\uff0c\u6211\u6682\u4e14\u6536\u7740\u4f7f\u7528\uff0c\u65e5\u540e\u6211\u4f20\u5979\u91d1\u86c7\u5251\u6cd5\uff0c\u518d\u5c06\u8fd9\u5251\u8fd8\u5f52\u7ed9\u5979\u3002\u201d\u9752\u9752\u62ff\u8fc7\u5251\u6765\u89c2\u770b\uff0c\u60f3\u5230\u7236\u4eb2\u6bcd\u4eb2\uff0c\u5fc3\u4e2d\u4e00\u9635\u96be\u8fc7\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e0e\u5e08\u7236\u89c1\u9762\u53c8\u8981\u5206\u624b\uff0c\u5f88\u662f\u604b\u604b\u4e0d\u820d\u3002\u7a46\u4eba\u6e05\u7b11\u9053\uff1a\u201c\u4f60\u5f88\u597d\uff0c\u4e0d\u6789\u5927\u5bb6\u6559\u4e86\u4f60\u4e00\u573a\u3002\u201d\u888d\u8896\u4e00\u62c2\uff0c\u5df2\u9690\u6ca1\u5728\u9ed1\u6697\u4e4b\u4e2d\u3002\u5f52\u8f9b\u6811\u592b\u5987\u62f1\u624b\u76f8\u9001\uff0c\u5f85\u5e08\u7236\u53ca\u5927\u5e08\u5144\u8d70\u5f97\u4e0d\u89c1\uff0c\u5411\u6728\u6851\u8eac\u8eab\u4e00\u63d6\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u62b1\u4e86\u5b69\u5b50\uff0c\u5e26\u9886\u4e09\u4e2a\u5f92\u5f1f\u5c31\u8d70\u3002 \u3000\u3000\u6728\u6851\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4ed6\u4eec\u5bf9\u4f60\u5fc3\u4e2d\u6000\u6068\uff0c\u8fd9\u4e24\u4eba\u529f\u592b\u975e\u540c\u5c0f\u53ef\uff0c\u65e5\u540e\u9047\u4e0a\u53ef\u8981\u5c0f\u5fc3\u3002\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u65e0\u7aef\u7aef\u5f97\u7f6a\u4e86\u4e8c\u5e08\u5144\uff0c\u5fc3\u5934\u90c1\u90c1\uff0c\u56de\u5230\u7126\u5bb6\uff0c\u5012\u5934\u4fbf\u7761\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u521a\u8d77\u8eab\uff0c\u9752\u9752\u5927\u53eb\u5927\u56b7\u7684\u8fdb\u6765\uff0c\u6367\u7740\u4e2a\u6728\u5236\u7684\u62dc\u76d2\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u731c\u662f\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u5140\u81ea\u63d0\u4e0d\u8d77\u5174\u81f4\uff0c\u9053\uff1a\u201c\u6709\u5ba2\u4eba\u6765\u4e48\uff1f\u201d\u9752\u9752\u63ed\u5f00\u76d2\u76d6\uff0c\u6ee1\u8138\u7b11\u5bb9\uff0c\u5982\u82b1\u76db\u5f00\u3002 \u3000\u3000\u53ea\u89c1\u76d2\u4e2d\u4e00\u5f20\u5927\u7ea2\u5e16\u5b50\uff0c\u5199\u7740\u201c\u611a\u6559\u5f1f\u95f5\u5b50\u534e\u62dc\u201d\u51e0\u4e2a\u5927\u5b57\u3002\u9752\u9752\u8c61\u8d77\u5e16\u5b50\uff0c\u4e0b\u9762\u662f\u4e00\u5f20\u623f\u5951\uff0c\u4e00\u5f20\u5c4b\u91cc\u5bb6\u5177\u5668\u7269\u7684\u6e05\u5355\u3002\u8881\u627f\u5fd7\u89c1\u95f5\u5b50\u534e\u9075\u5b88\u8bfa\u8a00\uff0c\u5c06\u5b85\u5b50\u9001\u4e86\u8fc7\u6765\uff0c\u5f88\u662f\u8fc7\u610f\u4e0d\u53bb\uff0c\u5fd9\u6362\u4e86\u888d\u8902\u8fc7\u53bb\u9053\u8c22\u3002\u54ea\u77e5\u95f5\u5b85\u4e2d\u4eba\u5df2\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u53ea\u7559\u4e0b\u4e24\u4e2a\u4e0b\u4eba\u5728\u56db\u5904\u6253\u626b\u3002\u8881\u627f\u5fd7\u4e00\u95ee\uff0c\u8bf4\u662f\u95f5\u4e8c\u7237\u4e00\u65e9\u5c31\u5e26\u540c\u5bb6\u4eba\u670b\u53cb\u8d70\u4e86\uff0c\u53bb\u751a\u4e48\u5730\u65b9\u5374\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u53d6\u51fa\u91d1\u86c7\u90ce\u541b\u9057\u56fe\u4e0e\u623f\u5b50\u5bf9\u770b\uff0c\u89c1\u5c4b\u4e2d\u901a\u9053\u623f\u820d\u867d\u6709\u4e0d\u5c11\u66f4\u52a8\uff0c\u4f46\u5927\u5c40\u95f4\u67b6\uff0c\u82e5\u5408\u7b26\u8282\u3002\u4e24\u4eba\u5927\u559c\uff0c\u77e5\u9053\u8fd9\u5ea7\u201c\u9b4f\u56fd\u516c\u8d50\u7b2c\u201d\u679c\u7136\u4fbf\u662f\u56fe\u4e2d\u6240\u6307\uff0c\u6309\u7740\u56fe\u4e0a\u85cf\u5b9d\u8bb0\u53f7\u5bfb\u7d22\uff0c\u539f\u6765\u662f\u5728\u540e\u82b1\u56ed\u7684\u4e00\u95f4\u67f4\u623f\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u5929\u4e0b\u5348\uff0c\u7126\u5b9b\u513f\u6d3e\u4e86\u4eba\u6765\u5e2e\u540c\u6253\u626b\u5e03\u7f6e\uff0c\u8fd8\u62e8\u4e86\u4e24\u540d\u5a62\u5973\u670d\u4f8d\u9752\u9752\uff0c\u5176\u4ed6\u53a8\u5b50\u3001\u95e8\u516c\u3001\u82b1\u5320\u3001\u4f8d\u4ec6\u3001\u66f4\u592b\u3001\u9a6c\u592b\u4e00\u5e94\u4ff1\u5168\uff0c\u6d2a\u80dc\u6d77\u4fbf\u505a\u4e86\u603b\u7ba1\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4f4d\u7126\u59d1\u5a18\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u60f3\u5f97\u5012\u771f\u5468\u5230\u3002\u201d\u9752\u9752\u62bf\u5634\u7b11\u9053\uff1a\u201c\u82e5\u80fd\u8bf7\u5f97\u5230\u5979\u6765\u8fd9\u5927\u5b85\u5b50\u4eb2\u4e3b\u5bb6\u52a1\uff0c\u90a3\u5c31\u4e00\u5b9a\u5468\u5230\u4e4b\u6781\u5566\uff01\u6211\u53ef\u2026\u2026\u6211\u53ef\u2026\u2026\u201d\u8138\u4e0a\u4e00\u7ea2\uff0c\u4e0b\u9762\u7684\u8bdd\u53ef\u4e0d\u4fbf\u8bf4\u4e86\u3002\u8881\u627f\u5fd7\u4e00\u6014\uff0c\u968f\u5373\u660e\u767d\uff0c\u5fc3\u60f3\u5979\u751a\u4e48\u90fd\u597d\uff0c\u5c31\u662f\u5c0f\u5fc3\u773c\u513f\uff0c\u4e00\u7b11\u4e4b\u4e0b\uff0c\u4e0d\u518d\u63a5\u53e3\u3002 \u3000\u3000\u5f53\u665a\u4e8c\u66f4\u8fc7\u540e\uff0c\u8881\u627f\u5fd7\u53eb\u4e86\u54d1\u5df4\uff0c\u4e8c\u4eba\u642c\u51fa\u67f4\u623f\u4e2d\u67f4\u8349\uff0c\u62ff\u4e86\u94c1\u9539\uff0c\u6316\u6398\u4e0b\u53bb\u3002\u9752\u9752\u4ed7\u5251\u5728\u67f4\u623f\u5916\u628a\u98ce\u3002\u6316\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u53ea\u542c\u5f97\u94ee\u7684\u4e00\u58f0\uff0c\u94c1\u9539\u78b0\u5230\u4e86\u4e00\u5757\u5927\u77f3\uff0c\u94f2\u53bb\u77f3\u4e0a\u6ce5\u571f\uff0c\u9732\u51fa\u4e00\u5757\u5927\u77f3\u677f\u6765\u3002\u4e24\u4eba\u5408\u529b\u5c06\u77f3\u677f\u62ac\u8d77\uff0c\u4e0b\u9762\u662f\u4e2a\u5927\u6d1e\u3002 \u3000\u3000\u9752\u9752\u542c\u5f97\u8881\u627f\u5fd7\u559c\u53eb\uff0c\u5954\u8fdb\u6765\u770b\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u5566\u3002\u201d \u3000\u3000\u53d6\u4e86\u4e24\u6346\u67f4\u8349\uff0c\u70b9\u71c3\u4e86\u4e22\u5728\u6d1e\u91cc\uff0c\u5f85\u79fd\u6c14\u9a71\u5c3d\uff0c\u6253\u624b\u52bf\u53eb\u54d1\u5df4\u5b88\u5916\u9762\uff0c\u4e0e\u9752\u9752\u5faa\u77f3\u7ea7\u8d70\u4e0b\u53bb\uff0c\u706b\u628a\u5149\u4e0b\u53ea\u89c1\u5341\u53ea\u5927\u94c1\u7bb1\u6392\u6210\u4e00\u5217\u3002\u94c1\u7bb1\u90fd\u7528\u5de8\u9501\u9501\u4f4f\uff0c\u94a5\u5319\u5374\u904d\u5bfb\u4e0d\u89c1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u518d\u53d6\u56fe\u7ec6\u770b\uff0c\u89c1\u85cf\u5b9d\u4e4b\u5904\u5de6\u89d2\u8fb9\u753b\u7740\u4e00\u6761\u5c0f\u5c0f\u91d1\u9f99\uff0c\u7075\u673a\u4e00\u52a8\uff0c\u62ff\u8d77\u94c1\u9539\u4f9d\u7740\u65b9\u4f4d\u6316\u4e0b\u53bb\uff0c\u6316\u4e0d\u4e86\u51e0\u4e0b\uff0c\u4fbf\u627e\u5230\u4e00\u53ea\u94c1\u76d2\uff0c\u76d2\u5b50\u5374\u6ca1\u4e0a\u9501\u3002\u4ed6\u8bb0\u8d77\u91d1\u86c7\u90ce\u541b\u7684\u76d2\u4e2d\u6bd2\u7bad\uff0c\u7528\u7ef3\u7f1a\u4f4f\u76d2\u76d6\u4e0a\u7684\u94c1\u73af\uff0c\u5c06\u94c1\u76d2\u653e\u5f97\u8fdc\u8fdc\u7684\uff0c\u7528\u7ef3\u62c9\u8d77\u76d2\u76d6\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u89c1\u65e0\u5f02\u72b6\uff0c\u79fb\u8fdb\u706b\u628a\u770b\u76d2\u4e2d\u65f6\uff0c\u89c1\u76d2\u91cc\u653e\u7740\u4e00\u4e32\u94a5\u5319\uff0c\u8fd8\u6709\u4e24\u5f20\u7eb8\u3002 \u3000\u3000\u53d6\u8d77\u4e0a\u9762\u4e00\u7eb8\uff0c\u89c1\u7eb8\u4e0a\u5199\u9053\uff1a\u201c\u543e\u53d4\u4e4b\u53db\uff0c\u6b66\u81e3\u65e0\u4e0d\u964d\u8005\u3002\u9b4f\u56fd\u516c\u5f90\u8f89\u7956\u4ee5\u529f\u81e3\u4e16\u52cb\uff0c\u5fe0\u4e8e\u793e\u7a37\uff0c\u6b8a\u53ef\u5609\u4e5f\u3002\u5185\u5e9c\u91cd\u5b9d\uff0c\u4ed3\u7687\u4e0d\u53ca\u643a\uff0c\u9b4f\u516c\u4e3a\u6715\u5b88\u4e4b\u3002\u4ed6\u65e5\u91cd\u5149\u5b97\u5e99\u793e\u7a37\uff0c\u4ee5\u6b64\u4e3a\u8d44\u3002 \u3000\u3000\u5efa\u6587\u56db\u5e74\u516d\u6708\u5e9a\u7533\u5fa1\u7b14\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u770b\u4e86\u4e0d\u7981\u51db\u7136\uff0c\u5fc3\u60f3\u8fd9\u679c\u7136\u662f\u71d5\u738b\u7be1\u4f4d\u4e4b\u65f6\u5efa\u6587\u5e1d\u6240\u9057\u4e0b\u7684\u91cd\u5b9d\u3002 \u3000\u3000\u539f\u6765\u660e\u671d\u5f00\u56fd\uff0c\u5927\u5c06\u519b\u5f90\u8fbe\u529f\u5c45\u7b2c\u4e00\u3002\u4ed6\u548c\u660e\u592a\u7956\u6731\u5143\u748b\u662f\u5e03\u8863\u4e4b\u4ea4\u3002\u6731\u5143\u748b\u505a\u4e86\u7687\u5e1d\u540e\uff0c\u8fd8\u662f\u79f0\u4ed6\u4e3a\u201c\u5f90\u5144\u201d\u3002\u5f90\u8fbe\u81ea\u7136\u4e0d\u6562\u518d\u548c\u7687\u5e1d\u79f0\u5144\u9053\u5f1f\uff0c\u59cb\u7ec8\u606d\u656c\u8c28\u614e\u3002 \u3000\u3000\u6709\u4e00\u5929\uff0c\u660e\u592a\u7956\u548c\u4ed6\u4e00\u8d77\u559d\u9152\uff0c\u996e\u9152\u4e2d\u95f4\uff0c\u8bf4\u9053\uff1a\u201c\u5f90\u5144\u529f\u52b3\u5f88\u5927\uff0c\u8fd8\u6ca1\u5b89\u5c45\u7684\u5730\u65b9\uff0c\u6211\u7684\u65e7\u90b8\u8d50\u4e86\u7ed9\u4f60\u5427\u3002\u201d\uff08\u300a\u660e\u53f2\xb7\u5f90\u8fbe\u4f20\u300b\u539f\u6587\u662f\uff1a\u201c\u5f90\u5144\u529f\u5927\uff0c\u672a\u6709\u5b81\u5c45\uff0c\u53ef\u8d50\u4ee5\u65e7\u90b8\u3002\u201d\uff09 \u3000\u3000\u6240\u8c13\u65e7\u90b8\uff0c\u662f\u592a\u7956\u505a\u5434\u738b\u65f6\u6240\u5c45\u7684\u5e9c\u7b2c\uff0c\u4ed6\u767b\u6781\u4e3a\u5e1d\u4e4b\u540e\uff0c\u81ea\u7136\u53e6\u5efa\u5bab\u6bbf\u4e86\u3002\u5f90\u8fbe\u5fc3\u60f3\uff1a\u592a\u7956\u81ea\u5434\u738b\u800c\u767b\u6781\uff0c\u81ea\u5df1\u82e5\u662f\u4f4f\u5230\u5434\u738b\u65e7\u90b8\u4e4b\u4e2d\uff0c\u8fd9\u4e2a\u5acc\u7591\u53ef\u72af\u5f97\u5927\u4e86\u3002\u4ed6\u6df1\u77e5\u592a\u7956\u731c\u5fcc\u5fc3\u6781\u91cd\uff0c\u5f53\u4e0b\u53ea\u662f\u9053\u8c22\uff0c\u5374\u8bf4\u751a\u4e48\u4e5f\u4e0d\u80af\u63a5\u53d7\u3002 \u3000\u3000\u592a\u7956\u51b3\u5b9a\u518d\u8bd5\u4ed6\u4e00\u8bd5\uff0c\u8fc7\u4e86\u51e0\u5929\uff0c\u9080\u4e86\u5f90\u8fbe\u540c\u53bb\u65e7\u90b8\u559d\u9152\uff0c\u4e0d\u4f4f\u529d\u9152\uff0c\u628a\u4ed6\u704c\u9189\u4e86\uff0c\u547d\u4f8d\u4ece\u5c06\u4ed6\u62ac\u5230\u5367\u5ba4\u4e4b\u4e2d\uff0c\u653e\u5728\u592a\u7956\u4ece\u524d\u6240\u7761\u7684\u5e8a\u4e0a\uff0c\u76d6\u4e0a\u4e86\u88ab\u3002\u5f90\u8fbe\u9152\u9192\u4e4b\u540e\uff0c\u4e00\u89c1\u60c5\u5f62\uff0c\u5927\u4e3a\u5403\u60ca\uff0c\u6025\u5fd9\u4e0b\u9636\uff0c\u4fef\u4f0f\u4e0b\u62dc\uff0c\u8fde\u79f0\uff1a\u201c\u6b7b\u7f6a\uff01\u201d\u4f8d\u4ece\u5c06\u60c5\u5f62\u56de\u594f\uff0c\u592a\u7956\u4e00\u542c\u5927\u559c\uff0c\u5fc3\u60f3\u6b64\u4eba\u5fe0\u5b57\u5f53\u5934\uff0c\u5168\u65e0\u53cd\u610f\uff0c\u5f53\u5373\u4e0b\u65e8\uff0c\u5728\u65e7\u90b8\u4e4b\u524d\u53e6\u8d77\u4e00\u5ea7\u5927\u5b85\u8d50\u4ed6\uff0c\u4eb2\u9898\u201c\u5927\u529f\u201d\u4e24\u5b57\uff0c\u4f5c\u4e3a\u8fd9\u5b85\u7b2c\u6240\u5728\u7684\u574a\u540d\u3002\u90a3\u4fbf\u662f\u5357\u4eac\u201c\u5927\u529f\u574a\u201d\u548c\u201c\u9b4f\u56fd\u516c\u8d50\u7b2c\u201d\u7684\u7531\u6765\u3002 \u3000\u3000\u636e\u7b14\u8bb0\u4e2d\u8f7d\u79f0\uff0c\u5f90\u8fbe\u867d\u7136\u5bf9\u7687\u5e1d\u606d\u987a\uff0c\u592a\u7956\u8fd8\u662f\u6015\u4ed6\u9020\u53cd\u3002\u6d2a\u6b66\u5341\u516b\u5e74\uff0c\u5f90\u8fbe\u80cc\u4e0a\u751f\u75bd\u3002\u636e\u8bf4\u751f\u80cc\u75bd\u4e4b\u4eba\uff0c\u5403\u84b8\u9e45\u7acb\u6b7b\u3002\u592a\u7956\u6d3e\u4eba\u6170\u95ee\uff0c\u9644\u8d50\u84b8\u9e45\u4e00\u53ea\u3002\u5f90\u8fbe\u6cea\u6d41\u6ee1\u9762\uff0c\u5f53\u7740\u4f7f\u8005\u628a\u4e00\u53ea\u84b8\u9e45\u5403\u4e2a\u5e72\u51c0\uff0c\u5f53\u591c\u5c31\u6bd2\u53d1\u800c\u6b7b\u3002\u751f\u80cc\u75bd\u800c\u5403\u4e86\u84b8\u9e45\uff0c\u672a\u5fc5\u4fbf\u6b7b\uff0c\u4f46\u6731\u5143\u748b\u8d50\u8fd9\u84b8\u9e45\uff0c\u4fbf\u662f\u8d50\u6b7b\uff0c\u5f90\u8fbe\u7eb5\u7136\u5403\u4e86\u84b8\u9e45\u65e0\u4e8b\uff0c\u4e5f\u53ea\u597d\u670d\u6bd2\u81ea\u5c3d\u3002\u6b64\u4e8b\u6b63\u53f2\u4e0d\u8f7d\uff0c\u4e0d\u77e5\u662f\u5426\u5c5e\u5b9e\u3002 \u3000\u3000\u5f90\u8fbe\u6709\u56db\u5b50\u4e09\u5973\uff0c\u4e09\u4e2a\u5973\u513f\u90fd\u4f5c\u592a\u7956\u513f\u5b50\u7684\u738b\u5983\uff0c\u957f\u5973\u662f\u71d5\u738b\u738b\u5983\uff0c\u540e\u6765\u4fbf\u662f\u6210\u7956\u7684\u7687\u540e\uff0c\u6b21\u5973\u662f\u4ee3\u738b\u738b\u5983\uff0c\u4e09\u5973\u662f\u5b89\u738b\u738b\u5983\u3002\u71d5\u738b\u8d77\u5175\u9020\u53cd\uff0c\u5f90\u8fbe\u7684\u957f\u5b50\u5f90\u8f89\u7956\u5fe0\u4e8e\u5efa\u6587\u5e1d\uff0c\u5e26\u5175\u529b\u6297\u71d5\u519b\u3002\u5f90\u8fbe\u7684\u5e7c\u5b50\u5f90\u589e\u5bff\u5374\u548c\u59ca\u592b\u71d5\u738b\u6697\u4e2d\u52fe\u7ed3\u3002\u71d5\u738b\u5175\u4e34\u5357\u4eac\u57ce\u4e0b\uff0c\u5efa\u6587\u5e1d\u53ec\u5f90\u589e\u5bff\u6765\u8d28\u95ee\u3002\u5f90\u589e\u5bff\u4e0d\u7b54\uff0c\u5efa\u6587\u5e1d\u4eb2\u624b\u6325\u5251\u65a9\u4e86\u4ed6\u3002 \u3000\u3000\u6210\u7956\u7be1\u4f4d\u540e\uff0c\u5f90\u8f89\u7956\u642c\u5165\u4e86\u7236\u4eb2\u7684\u7960\u5802\u5c45\u4f4f\uff0c\u4e0d\u80af\u671d\u89c1\u3002 \u3000\u3000\u6210\u7956\u6d3e\u5b98\u540f\u5ba1\u95ee\uff0c\u5f90\u8f89\u7956\u5199\u4e86\u201c\u6211\u7236\u5f00\u56fd\u529f\u81e3\uff0c\u5b50\u5b59\u514d\u6b7b\u201d\u5341\u4e2a\u5927\u5b57\u56de\u62a5\u3002\u6210\u7956\u89c1\u4e86\u5927\u6012\uff0c\u4f46\u4ed6\u521d\u5373\u5e1d\u4f4d\uff0c\u8981\u6536\u62fe\u4eba\u5fc3\uff0c\u9976\u4e86\u4ed6\u4e0d\u6740\u3002\u5f90\u8f89\u7956\u5bf9\u5efa\u6587\u5e1d\u5fe0\u5fc3\u803f\u803f\uff0c\u59cb\u7ec8\u5728\u56fe\u8c0b\u590d\u8f9f\u3002\u4ed6\u540e\u4eba\u4e16\u88ad\u9b4f\u56fd\u516c\uff0c\u4e00\u76f4\u7edf\u5e26\u5b88\u536b\u5357\u4eac\u7684\u90e8\u961f\uff0c\u76f4\u81f3\u660e\u4ea1\u3002\u660e\u671d\u5357\u4eac\u5b88\u5907\u5e9c\u4f4d\u5c0a\u6743\u91cd\uff0c\u5357\u4eac\u767e\u59d3\u53ea\u77e5\u201c\u5b88\u5907\u5e9c\u5f90\u516c\u7237\u201d\uff0c\u5374\u4e0d\u77e5\u9b4f\u56fd\u516c\uff0c\u662f\u4ee5\u8881\u627f\u5fd7\u548c\u9752\u9752\u6253\u542c\u4e0d\u7740\u3002 \u3000\u3000\u6210\u7956\u611f\u5ff5\u5f90\u589e\u5bff\u4e3a\u5df1\u800c\u6b7b\uff0c\u8ffd\u5c01\u4ed6\u4e3a\u5b9a\u56fd\u516c\u3002\u56e0\u6b64\u5f90\u8fbe\u7684\u5b50\u5b59\u5171\u6709\u9b4f\u56fd\u516c\u548c\u5b9a\u56fd\u516c\u4e24\u4e2a\u516c\u7235\u3002\u4e24\u4f4d\u516c\u7235\u7684\u540e\u88d4\u4e00\u5c45\u5357\u4eac\uff0c\u4e00\u5c45\u5317\u4eac\u3002\u5f90\u8f89\u7956\u5f97\u7f6a\u4e86\u6210\u7956\uff0c\u4ed6\u5b50\u5b59\u4e0d\u6562\u518d\u5728\u5927\u529f\u574a\u7684\u8d50\u7b2c\u5c45\u4f4f\uff0c\u53e6\u884c\u8fc1\u5c45\u3002\u5927\u529f\u574a\u8d50\u7b2c\u6570\u5ea6\u6613\u624b\uff0c\u7ecf\u8fc7\u4e8c\u767e\u56db\u5341\u591a\u5e74\uff0c\u540e\u4eba\u518d\u4e5f\u4e0d\u660e\u8fd9\u5ea7\u65e7\u5b85\u7684\u6765\u5386\u3002\u8fd9\u4e2d\u95f4\u7684\u7ecf\u8fc7\uff0c\u8881\u627f\u5fd7\u548c\u9752\u9752\u81ea\u7136\u4e0d\u77e5\u3002 \u3000\u3000\u8881\u627f\u5fd7\u770b\u7b2c\u4e8c\u5f20\u7eb8\u65f6\uff0c\u89c1\u5199\u7684\u662f\u4e00\u9996\u5f8b\u8bd7\uff0c\u8bd7\u4e91\uff1a\u201c\u7262\u843d\u897f\u5357\u56db\u5341\u79cb\uff0c\u8427\u8427\u767d\u53d1\u5df2\u76c8\u5934\u3002 \u3000\u3000\u4e7e\u5764\u6709\u6068\u5bb6\u4f55\u5728\uff1f\u6c5f\u6c49\u65e0\u60c5\u6c34\u81ea\u6d41\u3002 \u3000\u3000\u957f\u4e50\u5bab\u4e2d\u4e91\u6c14\u6563\uff0c\u671d\u5143\u9601\u4e0a\u96e8\u58f0\u6536\u3002 \u3000\u3000\u65b0\u84b2\u7ec6\u67f3\u5e74\u5e74\u7eff\uff0c\u91ce\u8001\u541e\u58f0\u54ed\u672a\u4f11\u3002\u201d \u3000\u3000\u7b14\u8ff9\u4e0e\u53e6\u4e00\u4fe1\u4e00\u6a21\u4e00\u6837\uff0c\u53ea\u662f\u66f4\u89c1\u82cd\u52b2\u633a\u62d4\u3002\u539f\u6765\u6b64\u8bd7\u662f\u5efa\u6587\u5e1d\u5728\u95fd\u7ca4\u5ddd\u6ec7\u5404\u5730\u6f2b\u6e38\u56db\u5341\u5e74\u540e\uff0c\u91cd\u8fd8\u91d1\u9675\u6240\u4f5c\u3002\u4ed6\u7ecf\u5386\u6c38\u4e50\uff08\u6210\u7956\uff09\u3001\u6d2a\u7199\uff08\u4ec1\u5b97\uff09\u3001\u5ba3\u5fb7\uff08\u5ba3\u5b97\uff09\u3001\u6b63\u7edf\uff08\u82f1\u5b97\uff09\u5404\u671d\u4e4b\u540e\uff0c\u5df2\u662f\u516d\u5341\u4f59\u5c81\uff0c\u590d\u4f4d\u4e4b\u60f3\u65e9\u5df2\u6d88\u5c3d\uff0c\u56de\u6765\u629a\u89c6\u6545\u7269\uff0c\u4e0d\u7981\u611f\u6168\u65e0\u5df2\uff0c\u4ece\u6b64\u98d8\u7136\u51fa\u4e16\uff0c\u4e0d\u77e5\u6240\u7ec8\u3002\u6b64\u4e2d\u8fc7\u8282\uff0c\u8881\u627f\u5fd7\u548c\u9752\u9752\u81ea\u7136\u731c\u60f3\u4e0d\u5230\u3002\u8881\u627f\u5fd7\u4e0d\u61c2\u8bd7\u4e2d\u8bf4\u4e9b\u751a\u4e48\uff0c\u9752\u9752\u66f4\u6025\u6b32\u5bdf\u770b\u7bb1\u4e2d\u7269\u4e8b\uff0c\u5bf9\u8bd7\u7b3a\u968f\u610f\u4e00\u77a5\uff0c\u4fbf\u653e\u5728\u4e00\u65c1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53d6\u51fa\u94a5\u5319\uff0c\u5c06\u94c1\u7bb1\u6253\u5f00\uff0c\u4e00\u63ed\u7bb1\u76d6\uff0c\u53ea\u89c9\u8000\u773c\u751f\u82b1\uff0c\u4e00\u5927\u7bb1\u6ee1\u6ee1\u7684\u90fd\u662f\u5b9d\u7389\u3001\u73cd\u73e0\uff0c\u53c8\u5f00\u4e00\u7bb1\uff0c\u5374\u662f\u739b\u7459\u3001\u7fe1\u7fe0\u4e4b\u5c5e\uff0c\u6ca1\u4e00\u4ef6\u4e0d\u662f\u4ef7\u503c\u5de8\u4e07\u7684\u73cd\u7269\u3002\u9752\u9752\u4f4e\u58f0\u60ca\u547c\uff0c\u4e0d\u7531\u5f97\u8138\u4e0a\u53d8\u8272\uff0c\u53c8\u60ca\u53c8\u559c\u3002\u6284\u5230\u5e95\u4e0b\uff0c\u5374\u89c1\u4e0b\u534a\u7bb1\u53e0\u6ee1\u4e86\u91d1\u7816\uff0c\u5341\u7bb1\u7686\u662f\u5982\u6b64\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e9b\u5b9d\u7269\u662f\u660e\u592a\u7956\u5f53\u5e74\u5728\u5929\u4e0b\u767e\u59d3\u8eab\u4e0a\u641c\u522e\u800c\u6765\uff0c\u54b1\u4eec\u7528\u6765\u5e72\u751a\u4e48\uff1f\u201d\u9752\u9752\u548c\u4ed6\u76f8\u5904\u65e5\u4e45\uff0c\u660e\u767d\u4ed6\u5fc3\u610f\uff0c\u77e5\u9053\u53ea\u8981\u7a0d\u751f\u8d2a\u5ff5\uff0c\u4e0d\u514d\u906d\u4ed6\u8f7b\u89c6\uff0c\u4fbf\u9053\uff1a\u201c\u54b1\u4eec\u8bf4\u8fc7\uff0c\u5bfb\u5230\u8d22\u7269\uff0c\u8981\u52a9\u95ef\u738b\u8c0b\u5e72\u5927\u4e8b\uff0c\u81ea\u7136\u662f\u53d6\u4e4b\u4e8e\u6c11\uff0c\u7528\u4e4b\u4e8e\u6c11\u3002\u201d\u8881\u627f\u5fd7\u5927\u559c\uff0c\u63e1\u4f4f\u5979\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u4f60\u771f\u662f\u6211\u7684\u77e5\u5df1\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u81ea\u5e7c\u5373\u77e5\u7236\u4eb2\u5c3d\u7601\u56fd\u4e8b\uff0c\u5e9f\u5bdd\u5fd8\u98df\uff0c\u975e\u4f46\u4e0d\u8d2a\u94b1\u8d22\uff0c\u8fde\u5bb6\u5ead\u4e2d\u7684\u5929\u4f26\u4e4b\u4e50\u3001\u670b\u53cb\u95f4\u7684\u4ea4\u6e38\u4e4b\u5a31\uff0c\u4e5f\u96be\u4ee5\u5f97\u4eab\u3002 \u3000\u3000\u5f53\u5e74\u5e94\u677e\u6559\u4ed6\u8bfb\u4e66\uff0c\u66fe\u6559\u8fc7\u8881\u5d07\u7115\u81ea\u53d9\u5fc3\u5883\u7684\u4e00\u7bc7\u6587\u7ae0\uff0c\u5176\u4e2d\u8bf4\u9053\uff1a\u201c\u4e88\u4f55\u4eba\u54c9\uff1f\u5341\u5e74\u4ee5\u6765\uff0c\u7236\u6bcd\u4e0d\u5f97\u4ee5\u4e3a\u5b50\uff0c\u59bb\u5b65\u4e0d\u5f97\u4ee5\u4e3a\u592b\uff0c\u624b\u8db3\u4e0d\u5f97\u4ee5\u4e3a\u5144\u5f1f\uff0c\u4ea4\u6e38\u4e0d\u5f97\u4ee5\u4e3a\u670b\u53cb\u3002\u4e88\u4f55\u4eba\u54c9\uff1f \u3000\u3000\u76f4\u8c13\u4e4b\u66f0\u2018\u5927\u660e\u56fd\u91cc\u4e00\u4ea1\u547d\u4e4b\u5f92\u2019\u53ef\u4e5f\u3002\u201d\u5f53\u65f6\u5e74\u5e7c\uff0c\u8fd8\u4e0d\u80fd\u5b8c\u5168\u4f53\u4f1a\u7236\u4eb2\u5c3d\u5fc3\u7aed\u529b\u3001\u5b88\u571f\u5fa1\u654c\u7684\u7cbe\u5fe0\u679c\u6bc5\uff0c\u6210\u957f\u540e\u6bcf\u60f3\u5230\u201c\u5927\u660e\u56fd\u91cc\u4e00\u4ea1\u547d\u4e4b\u5f92\u201d\u90a3\u53e5\u8bdd\uff0c\u4e0d\u7531\u5f97\u70ed\u8840\u6cb8\u817e\uff0c\u65e9\u5c31\u7acb\u5fd7\u4ee5\u7236\u4e3a\u699c\u6837\u3002\u8881\u5d07\u7115\u4e3a\u4eba\u9898\u5b57\uff0c\u7231\u5199\u201c\u5fc3\u672f\u4e0d\u53ef\u5f97\u7f6a\u4e8e\u5929\u5730\uff0c\u8a00\u884c\u8981\u7559\u597d\u6837\u4e0e\u513f\u5b59\u201d\u4e24\u53e5\uff0c\u8881\u627f\u5fd7\u6240\u5b58\u7236\u4eb2\u9057\u7269\uff0c\u4e5f\u53ea\u6709\u8fd9\u4e00\u5e45\u5b57\u800c\u5df2\u3002\u8fd9\u65f6\u4ed6\u89c1\u5230\u65e0\u6570\u91d1\u94f6\u8d22\u5b9d\uff0c\u6240\u60f3\u5230\u7684\u81ea\u7136\u662f\u5982\u4f55\u5b66\u7236\u4eb2\u7684\u8a00\u884c\u597d\u6837\uff0c\u5982\u4f55\u5c06\u73cd\u5b9d\u7528\u4e8e\u4fdd\u56fd\u536b\u6c11\u3002 \u3000\u3000\u9752\u9752\u5374\u51fa\u8eab\u4e8e\u5927\u76d7\u4e4b\u5bb6\uff0c\u5411\u6765\u89c1\u4eba\u9022\u8d22\u4fbf\u53d6\uff0c\u7ba1\u4ed6\u6709\u4e3b\u65e0\u4e3b\uff0c\u4e49\u4e0e\u4e0d\u4e49\u3002\u4f55\u51b5\u8fd9\u8bb8\u591a\u4ef7\u503c\u8fde\u57ce\u7684\u73e0\u5b9d\uff0c\u90fd\u662f\u51ed\u5979\u7236\u4eb2\u9057\u56fe\u800c\u5f97\uff0c\u82e5\u4e0d\u662f\u5979\u5bf9\u8881\u627f\u5fd7\u949f\u60c5\u5df2\u6df1\uff0c\u5c82\u80af\u4e0d\u636e\u4e3a\u5df1\u6709\uff1f \u3000\u3000\u542c\u8881\u627f\u5fd7\u79f0\u81ea\u5df1\u4e3a\u201c\u77e5\u5df1\u201d\uff0c\u4e0d\u7531\u5f97\u611f\u5230\u4e00\u9635\u751c\u610f\uff0c\u970e\u65f6\u95f4\u5fc3\u5934\u6d6e\u8d77\u4e86\u4e24\u53e5\u53e4\u8bd7\uff1a\u201c\u6613\u6c42\u65e0\u4ef7\u5b9d\uff0c\u96be\u5f97\u6709\u60c5\u90ce\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6709\u4e86\u8fd9\u8bb8\u591a\u8d44\u8d22\uff0c\u54b1\u4eec\u5c31\u53ef\u5230\u5317\u4eac\u53bb\u5927\u5e72\u4e00\u756a\u4e8b\u4e1a\u3002\u660e\u671d\u7687\u5e1d\u641c\u522e\u800c\u6765\uff0c\u54b1\u4eec\u5c31\u7528\u6765\u76f8\u52a9\u95ef\u738b\uff0c\u63a8\u5012\u660e\u671d\u7687\u5e1d\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u8fd9\u53eb\u505a\u5373\u4ee5\u5176\u4eba\u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u6389\u4e66\u5305\u7684\u672c\u4e8b\u53ef\u4e86\u4e0d\u8d77\u3002\u201d \u3000\u3000\u6b21\u65e5\u4e0b\u5348\uff0c\u8881\u627f\u5fd7\u547d\u6d2a\u80dc\u6d77\u5230\u7126\u5bb6\u53bb\u628a\u7f57\u7acb\u5982\u53eb\u6765\u3002\u4ed6\u65ad\u81c2\u4f24\u52bf\u8fd8\u5f88\u6c89\u91cd\uff0c\u542c\u5f97\u8881\u627f\u5fd7\u89c1\u62db\uff0c\u7acb\u5373\u547d\u4eba\u76f8\u6276\uff0c\u559c\u6c14\u6d0b\u6d0b\u7684\u5230\u6765\uff0c\u89c1\u9762\u540e\u4fbf\u8981\u884c\u62dc\u5e08\u4e4b\u793c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u575a\u8f9e\u4e0d\u53d7\uff0c\u53eb\u4ed6\u5750\u7740\uff0c\u5c06\u4e00\u5957\u72ec\u81c2\u5200\u6cd5\u7ec6\u7ec6\u8bf4\u4e86\u7ed9\u4ed6\u542c\u3002\u7f57\u7acb\u5982\u6b66\u529f\u672c\u6709\u6839\u5e95\uff0c\u8881\u627f\u5fd7\u53c8\u4e00\u62db\u4e00\u5f0f\u7684\u6559\u5f97\u751a\u662f\u4ed4\u7ec6\uff0c\u8fde\u7eed\u6559\u4e86\u4e94\u5929\uff0c\u7f57\u7acb\u5982\u5df2\u7262\u7262\u8bb0\u4f4f\uff0c\u53ea\u5f85\u81c2\u4f24\u75ca\u4e86\uff0c\u5c31\u53ef\u4e60\u7ec3\u3002\u8881\u627f\u5fd7\u8fd9\u5957\u5200\u6cd5\u5f97\u81ea\u300a\u91d1\u86c7\u79d8\u7b08\u300b\uff0c\u4e0e\u6c5f\u6e56\u4e0a\u6d41\u4f20\u7684\u5de6\u81c2\u5200\u6cd5\u5927\u4e0d\u76f8\u540c\uff0c\u62db\u62db\u9669\uff0c\u5200\u5200\u5feb\uff0c\u5b9e\u662f\u5389\u5bb3\u4e0d\u8fc7\u3002\u7f57\u7acb\u5982\u867d\u65ad\u4e00\u81c2\uff0c\u5374\u6362\u6765\u4e86\u4e00\u5957\u8db3\u4ee5\u626c\u540d\u6c5f\u6e56\u7684\u7edd\u6280\uff0c\u53ef\u8bf4\u662f\u56e0\u7978\u5f97\u798f\uff0c\u5fc3\u91cc\u6b22\u559c\u4e0d\u5c3d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e86\u7ed3\u8fd9\u4ef6\u5fc3\u4e8b\u540e\uff0c\u96c7\u4e86\u5341\u591a\u8f86\u5927\u8f66\uff0c\u9884\u5907\u4e0a\u9053\u8d74\u4eac\u3002\u7126\u516c\u793c\u7236\u5973\u53ca\u4f17\u95e8\u5f92\u5927\u6446\u7b75\u5e2d\uff0c\u6bb7\u52e4\u76f8\u9001\u3002\u8881\u627f\u5fd7\u8bf7\u7126\u516c\u793c\u9001\u4fe1\u7ed9\u95f5\u5b50\u534e\uff0c\u5c06\u5927\u529f\u574a\u5b85\u7b2c\u4ecd\u7136\u4ea4\u8fd8\u3002\u7126\u516c\u793c\u5e94\u627f\u529e\u7406\u3002\u592a\u767d\u4e09\u82f1\u7b49\u6c49\u5978\u5219\u9001\u4ea4\u5b98\u529e\u3002 \u3000\u3000\u8fd9\u65e5\u79cb\u9ad8\u6c14\u723d\uff0c\u91d1\u98ce\u9001\u6691\uff0c\u8881\u627f\u5fd7\u3001\u9752\u9752\u3001\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u4e00\u884c\u4eba\u522b\u8fc7\u6728\u6851\u9053\u4eba\uff0c\u5c06\u5341\u53ea\u94c1\u7bb1\u88c5\u4e0a\u5927\u8f66\uff0c\u5411\u5317\u8fdb\u53d1\u3002\u7126\u516c\u793c\u7236\u5973\u53ca\u4f17\u5f1f\u5b50\u540c\u8fc7\u957f\u6c5f\uff0c\u9001\u51fa\u4e09\u5341\u91cc\u5916\uff0c\u65b9\u624d\u4f5c\u522b\u3002\u6c5f\u5317\u4e00\u5e26\u4ecd\u662f\u91d1\u9f99\u5e2e\u7684\u5730\u76d8\uff0c\u7126\u516c\u793c\u4e8b\u5148\u65e9\u5df2\u6d3e\u4eba\u9001\u8baf\uff0c\u6bcf\u4e2a\u7801\u5934\u90fd\u6709\u4eba\u6bb7\u52e4\u63a5\u9001\u3002 \u3000\u3000\u884c\u4e86\u5341\u591a\u65e5\uff0c\u6765\u5230\u5c71\u4e1c\u754c\u5185\u3002\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u76f8\u516c\uff0c\u8fd9\u91cc\u5df2\u4e0d\u662f\u91d1\u9f99\u5e2e\u7684\u5730\u754c\u3002\u4ece\u4eca\u65e5\u8d77\uff0c\u54b1\u4eec\u5f97\u591a\u7559\u4e00\u70b9\u513f\u795e\u5566\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6709\u4eba\u6562\u6765\u592a\u5c81\u5934\u4e0a\u52a8\u571f\u5417\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u65b9\u4eca\u5929\u4e0b\u76d7\u8d3c\u5982\u6bdb\uff0c\u5c71\u4e1c\u5f3a\u4eba\u5c24\u591a\u3002\u6700\u5389\u5bb3\u7684\u662f\u4e24\u5e2e\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4e00\u5e2e\u662f\u4f60\u4eec\u6e24\u6d77\u6d3e\u4e86\u3002\u201d\u6d2a\u80dc\u6d77\u7b11\u9053\uff1a\u201c\u6e24\u6d77\u6d3e\u4e13\u505a\u6d77\u4e0a\u4e70\u5356\uff0c\u9646\u4e0a\u7684\u4e1c\u897f\uff0c\u5c31\u7b97\u9ec4\u91d1\u5b9d\u8d1d\u4e22\u5728\u5730\u4e0b\uff0c\u6211\u4eec\u4e5f\u662f\u4e0d\u6361\u7684\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u539f\u6765\u8d35\u6d3e\u4e0d\u7b97\uff0c\u90a3\u4e48\u662f\u54ea\u4e24\u5e2e\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u4e00\u5e2e\u662f\u6ca7\u5dde\u5343\u67f3\u5e84\u891a\u7ea2\u67f3\u891a\u5927\u7237\u7684\u624b\u4e0b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u4e5f\u66fe\u542c\u5e08\u7236\u8bf4\u8d77\u8fc7\u891a\u7ea2\u67f3\u4ee5\u6731\u7802\u638c\u9a70\u540d\u6c5f\u6e56\u3002\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u6b63\u662f\u3002\u53e6\u4e00\u5e2e\u5728\u6076\u864e\u6c9f\u5f00\u5c71\u7acb\u67dc\uff0c\u5927\u5f53\u5bb6\u9634\u9633\u6247\u6c99\u901a\u5929\u6b66\u529f\u4e86\u5f97\uff0c\u624b\u4e0b\u4eba\u591a\u52bf\u4f17\u3002\u201d\u8881\u627f\u5fd7\u70b9\u5934\u9053\uff1a\u201c\u54b1\u4eec\u4ee5\u540e\u5c0f\u5fc3\u5728\u610f\uff0c\u6bcf\u665a\u4e00\u4eba\u8f6e\u6d41\u5b88\u591c\u3002\u201d \u3000\u3000\u8d70\u4e86\u4e24\u65e5\uff0c\u6b63\u5f53\u4e2d\u5348\uff0c\u8fce\u9762\u9e3e\u94c3\u54cd\u5904\uff0c\u4e24\u5339\u5feb\u9a6c\u75be\u5954\u800c\u6765\uff0c\u4ece\u4f17\u4eba\u8eab\u65c1\u64e6\u8fc7\u3002\u6d2a\u80dc\u6d77\u8bf4\u9053\uff1a\u201c\u90a3\u8bdd\u513f\u6765\u5566\u3002\u201d\u4ed6\u60f3\u8881\u627f\u5fd7\u6b66\u529f\u6781\u9ad8\uff0c\u81ea\u5df1\u4e5f\u975e\u5eb8\u624b\uff0c\u51e0\u4e2a\u6bdb\u8d3c\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u8fc7\u4e0d\u4e00\u4e2a\u65f6\u8fb0\uff0c\u90a3\u4e24\u4e58\u9a6c\u679c\u7136\u4ece\u540e\u8d76\u4e86\u4e0a\u6765\uff0c\u5728\u9aa1\u8f66\u961f\u4e24\u65c1\u63a0\u4e86\u8fc7\u53bb\u3002\u9752\u9752\u53ea\u662f\u51b7\u7b11\u3002\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u4e0d\u51fa\u5341\u91cc\uff0c\u524d\u9762\u5fc5\u6709\u5f3a\u4eba\u62e6\u8def\u3002\u201d\u54ea\u77e5\u8d70\u4e86\u5341\u591a\u91cc\u5730\uff0c\u7adf\u7136\u592a\u5e73\u65e0\u4e8b\u3002\u5f53\u665a\u5728\u53cc\u77f3\u94fa\u5bbf\u6b47\u3002\u6d2a\u80dc\u6d77\u5567\u5567\u79f0\u5947\uff0c\u9053\uff1a\u201c\u96be\u9053\u6211\u8fd9\u8001\u6c5f\u6e56\u8d70\u4e86\u773c\u4e86\u3002\u201d \u3000\u3000\u6b21\u65e5\u53c8\u884c\uff0c\u8d70\u4e0d\u51fa\u4e94\u91cc\uff0c\u53ea\u89c1\u540e\u9762\u56db\u9a91\u9a6c\u8fdc\u8fdc\u8ddf\u7740\u3002\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u4eec\u6628\u513f\u4eba\u624b\u8fd8\u6ca1\u8c03\u9f50\uff0c\u4eca\u65e5\u5fc5\u6709\u4e8b\u6545\u3002\u201d\u4e2d\u5348\u6253\u8fc7\u5c16\u540e\uff0c\u53c8\u6709\u4e24\u9a91\u9a6c\u8d9f\u4e0b\u6765\u770b\u76f8\u6478\u5e95\u3002\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8fd9\u5012\u5947\u4e86\uff0c\u9053\u4e0a\u770b\u98ce\u8e29\u76d8\u5b50\uff0c\u4ece\u6765\u6ca1\u8fd9\u4e48\u591a\u4eba\u7684\u3002\u201d\u884c\u534a\u65e5\uff0c\u53c8\u89c1\u4e24\u4e58\u9a6c\u63a0\u8fc7\u9aa1\u961f\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u76b1\u7709\u601d\u7d22\uff0c\u5ffd\u9053\uff1a\u201c\u662f\u4e86\u3002\u201d\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u76f8\u516c\uff0c\u54b1\u4eec\u4eca\u665a\u5f97\u8d76\u4e0a\u4e00\u4e2a\u5927\u5e02\u9547\u6295\u5bbf\u624d\u597d\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8ddf\u7740\u54b1\u4eec\u7684\uff0c\u4e0d\u6b62\u4e00\u4e2a\u5c71\u5be8\u7684\u4eba\u9a6c\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u662f\u4e48\uff1f\u6709\u51e0\u5bb6\u5be8\u4e3b\u770b\u4e2d\u4e86\u8fd9\u6279\u8d27\u8272\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8981\u662f\u6bcf\u4e00\u5bb6\u6d3e\u4e86\u4e24\u4e2a\u4eba\uff0c\u90a3\u4e48\u524d\u524d\u540e\u540e\u5df2\u6709\u4e94\u5bb6\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u90a3\u5012\u70ed\u95f9\u3002\u201d\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u53c8\u600e\u77e5\u54b1\u4eec\u643a\u4e86\u91d1\u94f6\u8d22\u5b9d\uff1f\u5018\u82e5\u54b1\u4eec\u8fd9\u5341\u53ea\u94c1\u7bb1\u4e2d\u88c5\u6ee1\u4e86\u6c99\u5b50\u77f3\u5934\uff0c\u8fd9\u4e94\u5bb6\u5927\u5be8\u4e3b\u5c82\u4e0d\u662f\u767d\u8f9b\u82e6\u4e00\u573a\uff1f\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u5c31\u4e0d\u5728\u884c\u4e86\u3002\u5927\u8f66\u4e2d\u88c5\u4e86\u91d1\u94f6\uff0c\u8f66\u8f6e\u5370\u75d5\u3001\u884c\u8f66\u58f0\u54cd\u3001\u626c\u8d77\u7684\u5c18\u571f\u7b49\u7b49\u90fd\u4e0d\u76f8\u540c\u3002\u522b\u8bf4\u5341\u53ea\u5927\u94c1\u7bb1\u6613\u770b\u5f97\u5f88\uff0c\u4fbf\u662f\u4f60\u5c0f\u6167\u59b9\u59b9\u7684\u4e8c\u5343\u4e24\u9ec4\u91d1\uff0c\u5f53\u65e5\u4e5f\u7ed9\u6211\u8fd9\u5c0f\u5f3a\u4eba\u770b\u4e86\u51fa\u6765\u3002\u5e38\u8a00\u9053\u5f97\u597d\uff1a\u2018\u9694\u884c\u5982\u9694\u5c71\u3002\u2019\u4f60\u81ea\u7136\u4e0d\u61c2\u7684\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d\u6d2a\u80dc\u6d77\u5fc3\u60f3\uff1a\u201c\u5c0f\u59d0\u8fd9\u6837\u5a07\u6ef4\u6ef4\u7684\u4e00\u4e2a\u5c0f\u59d1\u5a18\uff0c\u96be\u9053\u4ece\u524d\u4e5f\u662f\u5e72\u6211\u4eec\u8fd9\u4e00\u884c\u7684\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53c8\u662f\u4e24\u4e58\u9a6c\u4ece\u8f66\u961f\u65c1\u63a0\u8fc7\uff0c\u9752\u9752\u51b7\u7b11\u9053\uff1a\u201c\u60f3\u52a8\u624b\u5374\u53c8\u4e0d\u6562\uff0c\u9a91\u4e86\u9a6c\u8dd1\u6765\u8dd1\u53bb\uff0c\u5c31\u662f\u778e\u8d77\u5fd9\u5934\u3002\u8fd9\u822c\u8113\u5305\uff0c\u4eba\u518d\u591a\u4e5f\u6ca1\u7528\uff01\u201d\u6d2a\u80dc\u6d77\u6b63\u8272\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u597d\u6c49\u654c\u4e0d\u8fc7\u4eba\u591a\u3002\u54b1\u4eec\u867d\u7136\u4e0d\u6015\uff0c\u4f46\u7bb1\u7b3c\u7269\u4ef6\u8fd9\u4e48\u8bb8\u591a\uff0c\u8981\u4e00\u65e0\u9519\u5931\uff0c\u5012\u4e5f\u5f97\u8d39\u4e00\u756a\u5fc3\u529b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u54b1\u4eec\u4eca\u665a\u5c31\u5728\u524d\u9762\u7684\u77f3\u80f6\u9547\u4f4f\u5e97\uff0c\u5c11\u8d70\u51e0\u5341\u91cc\u5427\u3002\u201d \u3000\u3000\u5230\u4e86\u77f3\u80f6\u9547\u4e0a\uff0c\u62e3\u4e86\u4e00\u5bb6\u5927\u5e97\u4f4f\u4e0b\u3002\u8881\u627f\u5fd7\u5429\u5490\u628a\u5341\u53ea\u94c1\u7bb1\u90fd\u642c\u5728\u81ea\u5df1\u623f\u4e2d\uff0c\u4e0e\u54d1\u5df4\u4e24\u4eba\u5408\u7761\u4e00\u623f\u3002\u521a\u653e\u597d\u94c1\u7bb1\uff0c\u53ea\u89c1\u4e24\u6761\u5927\u6c49\u8d70\u8fdb\u5e97\u6765\uff0c\u5411\u8881\u627f\u5fd7\u671b\u4e86\u4e00\u773c\uff0c\u5bf9\u5e97\u4f19\u8bf4\u8981\u4f4f\u5e97\u3002 \u3000\u3000\u5e97\u4f19\u62db\u547c\u4e24\u4eba\u5165\u5185\uff0c\u524d\u811a\u63a5\u540e\u811a\uff0c\u53c8\u6709\u4e24\u540d\u7c97\u8c6a\u6c49\u5b50\u8fdb\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u4e0b\u76d8\u7b97\u5df2\u5b9a\uff0c\u665a\u996d\u8fc7\u540e\uff0c\u5404\u4eba\u56de\u623f\u7761\u89c9\u3002 \u3000\u3000\u7761\u5230\u534a\u591c\uff0c\u53ea\u542c\u5f97\u5c4b\u9876\u5fae\u5fae\u54cd\u52a8\uff0c\u77e5\u9053\u76d7\u4f19\u5230\u4e86\u3002\u4ed6\u8d77\u8eab\u70b9\u4eae\u4e86\u8721\u70db\uff0c\u6253\u5f00\u94c1\u7bb1\uff0c\u53d6\u51fa\u4e00\u628a\u628a\u660e\u73e0\u3001\u5b9d\u77f3\u3001\u7fe1\u7fe0\u3001\u739b\u7459\uff0c\u5728\u706f\u4e0b\u628a\u73a9\u3002\u5947\u73cd\u5f02\u5b9d\u5728\u706f\u4e0b\u707f\u7136\u751f\u5149\uff0c\u53ea\u89c1\u7a97\u68c2\u4e4b\u8fb9\u3001\u95e8\u7f1d\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u53ea\u8d2a\u5a6a\u7684\u773c\u775b\u5728\u5411\u91cc\u7aa5\u63a2\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u542c\u5f97\u58f0\u97f3\uff0c\u653e\u5fc3\u4e0d\u4e0b\uff0c\u8fc7\u6765\u5bdf\u770b\uff0c\u4ed6\u4e00\u8d70\u8fd1\uff0c\u5341\u4f59\u540d\u63a2\u5b50\u4ff1\u5404\u9690\u8eab\u3002\u6d2a\u80dc\u6d77\u5fae\u5fae\u51b7\u7b11\uff0c\u5728\u8881\u627f\u5fd7\u623f\u95e8\u4e0a\u8f7b\u6572\u6570\u4e0b\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u4e00\u63a8\u95e8\uff0c\u623f\u95e8\u5440\u7684\u4e00\u58f0\u5f00\u4e86\uff0c\u539f\u6765\u7adf\u6ca1\u5173\u4e0a\u3002\u4ed6\u4e00\u8fdb\u623f\uff0c\u53ea\u89c1\u684c\u4e0a\u73e0\u5149\u5b9d\u6c14\uff0c\u8000\u773c\u751f\u8f89\uff0c\u4e0d\u89c9\u5446\u4e86\uff0c\u8d70\u8fd1\u770b\u65f6\uff0c\u4f46\u89c1\u6709\u6307\u5934\u5927\u5c0f\u7684\u6d51\u5706\u73cd\u73e0\uff0c\u6709\u4e24\u5c3a\u6765\u957f\u7684\u6731\u7ea2\u73ca\u745a\uff0c\u6709\u6676\u83b9\u78a7\u7eff\u7684\u5927\u5757\u7956\u6bcd\u7eff\uff0c\u6b64\u5916\u732b\u513f\u773c\u3001\u7ea2\u5b9d\u77f3\u3001\u84dd\u5b9d\u77f3\u3001\u7d2b\u7389\uff0c\u6ca1\u4e00\u4ef6\u4e0d\u662f\u65e0\u4ef7\u4e4b\u5b9d\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u672c\u4e0d\u77e5\u5341\u53ea\u94c1\u7bb1\u4e2d\u6240\u85cf\u4f55\u7269\uff0c\u53ea\u9053\u90fd\u662f\u91d1\u94f6\uff0c\u8fd9\u624d\u5f15\u8d77\u7fa4\u76d7\u7684\u8d2a\u5fc3\uff0c\u54ea\u77e5\u7adf\u6709\u5982\u8bb8\u73cd\u54c1\u3002\u4ed6\u5728\u6c5f\u6e56\u591a\u5e74\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u4f46\u8fd9\u4e48\u591a\u3001\u8fd9\u4e48\u8d35\u91cd\u7684\u5b9d\u7269\u5374\u4ece\u672a\u89c1\u8fc7\uff0c\u8881\u76f8\u516c\u5374\u4ece\u4f55\u5904\u5f97\u6765\uff0c\u5012\u771f\u4ee4\u4eba\u4e0d\u89e3\u4e86\u3002\u4ed6\u8d70\u5230\u8881\u627f\u5fd7\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u76f8\u516c\uff0c\u6211\u6765\u6536\u8d77\u4e86\u597d\u4e48\uff1f\u5916\u9762\u6709\u4eba\u5077\u770b\u3002\u201d\u8881\u627f\u5fd7\u4e5f\u4f4e\u58f0\u9053\uff1a\u201c\u6b63\u8981\u8ba9\u4ed6\u4eec\u77a7\u77a7\u3002\u53cd\u6b63\u662f\u8fd9\u4e48\u4e00\u56de\u4e8b\u3002\u201d\u62ff\u8d77\u4e00\u4e32\u73cd\u73e0\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4e32\u73e0\u5b50\u62ff\u5230\u4eac\u91cc\uff0c\u4f60\u77a7\u5356\u5f97\u591a\u5c11\u94f6\u5b50\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u4e09\u767e\u4e24\u94f6\u5b50\u4e00\u9897\uff0c\u90a3\u662f\u518d\u4e5f\u4e0d\u80fd\u5c11\u4e86\u3002\u8fd9\u91cc\u5171\u662f\u4e8c\u5341\u56db\u9897\uff0c\u5c11\u8bf4\u4e5f\u503c\u5f97\u4e00\u4e07\u4e94\u5343\u4e24\u94f6\u5b50\u3002\u201d\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u600e\u4e48\u662f\u4e00\u4e07\u4e94\u5343\u4e24\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u5355\u662f\u8fd9\u4e48\u5927\u3001\u8fd9\u4e48\u5706\u3001\u8fd9\u4e48\u5149\u6d01\u7684\u4e00\u9897\u73e0\u5b50\uff0c\u5df2\u7ecf\u5341\u5206\u5c11\u89c1\uff0c\u96be\u5f97\u7684\u662f\u4e8c\u5341\u56db\u9897\u7adf\u4e00\u822c\u5927\u5c0f\uff0c\u5168\u65e0\u7455\u75b5\u3002\u4e00\u9897\u503c\u4e09\u767e\u4e24\u94f6\u5b50\uff0c\u90a3\u4e48\u4e8c\u5341\u56db\u9897\u81f3\u5c11\u503c\u5f97\u4e00\u4e07\u4e94\u5343\u4e24\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u53ea\u628a\u623f\u5916\u7fa4\u76d7\u542c\u5f97\u5fc3\u75d2\u96be\u6414\uff0c\u6068\u4e0d\u5f97\u7acb\u65f6\u8df3\u8fdb\u53bb\u62a2\u4e86\u8fc7\u6765\u3002\u53ea\u662f\u4e0a\u9762\u5934\u9886\u6709\u4ee4\uff0c\u770b\u4e2d\u8fd9\u6279\u8d27\u7684\u5c71\u5be8\u592a\u591a\uff0c\u5927\u4f19\u8981\u5546\u91cf\u597d\u4e86\u518d\u52a8\uff0c\u514d\u4f24\u540c\u9053\u548c\u6c14\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u5148\u884c\u4e0b\u624b\u3002\u773c\u89c1\u8881\u627f\u5fd7\u5411\u6d2a\u80dc\u6d77\u6446\u6446\u624b\uff0c\u7b11\u7740\u7761\u4e86\uff0c\u70db\u706b\u4e0d\u7184\uff0c\u73e0\u5b9d\u4e5f\u4e0d\u6536\u62fe\uff0c\u644a\u6ee1\u4e86\u4e00\u684c\uff0c\u53ea\u628a\u7fa4\u76d7\u5f15\u5f97\u9762\u7ea2\u8033\u8d64\uff0c\u4e0d\u4f4f\u5e72\u54bd\u553e\u6d8e\u3002 \u3000\u3000\u8881\u627f\u5fd7\u81ea\u53d1\u89c9\u7fa4\u76d7\u5927\u96c6\uff0c\u610f\u6b32\u52ab\u593a\uff0c\u4e00\u8def\u4e0a\u4fbf\u5728\u76d8\u7b97\u5e94\u4ed8\u4e4b\u7b56\uff0c\u6b63\u5982\u6d2a\u80dc\u6d77\u6240\u8bf4\uff1a\u201c\u597d\u6c49\u654c\u4e0d\u8fc7\u4eba\u591a\u3002\u7bb1\u7b3c\u7269\u4ef6\u8fd9\u4e48\u8bb8\u591a\uff0c\u8981\u4e00\u65e0\u9519\u592b\uff0c\u5012\u4e5f\u5f97\u8d39\u4e00\u756a\u5fc3\u529b\u3002\u201d\u81ea\u7136\u800c\u7136\u7684\u4fbf\u60f3\uff1a\u201c\u8981\u662f\u91d1\u86c7\u90ce\u541b\u9047\u4e0a\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u4fbf\u5982\u4f55\u5bf9\u4ed8\uff1f\u201d\u8ddf\u7740\u60f3\u5230\uff1a\u91d1\u86c7\u90ce\u541b\u4e3a\u6e29\u6c0f\u4e94\u8001\u53ca\u5d06\u5cd2\u6d3e\u8bf8\u4eba\u6240\u64d2\uff0c\u4ee5\u5b9d\u85cf\u5de8\u5229\u5f15\u5f97\u53cc\u65b9\u4e92\u76f8\u4e89\u593a\uff0c\u6e29\u6c0f\u4e94\u8001\u51fa\u624b\u6740\u4e86\u6240\u9080\u6765\u7684\u5d06\u5cd2\u6d3e\u670b\u53cb\uff0c\u4ed6\u7531\u6b64\u800c\u4e58\u673a\u9003\u8131\uff1b\u53c8\u60f3\u5230\uff1a\u90a3\u665a\u77f3\u6881\u6d3e\u7684\u5f20\u6625\u4e5d\u548c\u6c5f\u79c3\u5934\u5077\u88ad\u534e\u5c71\uff0c\u89c1\u5230\u6709\u6bd2\u7684\u5047\u79d8\u7b08\uff0c\u8fde\u5e08\u5144\u5f1f\u4e5f\u90fd\u6740\u4e86\uff1b\u9f99\u6e38\u5e2e\u548c\u9752\u9752\u4e3a\u4e86\u4e89\u593a\u95ef\u738b\u9ec4\u91d1\u800c\u76f8\u4e89\u6597\u3002\u8db3\u89c1\u5927\u5229\u6240\u5728\uff0c\u89c1\u5229\u5fd8\u4e49\u4e4b\u4eba\u975e\u4e92\u76f8\u6b8b\u6740\u4e0d\u53ef\u3002\u201c\u7fa4\u76d7\u4eba\u591a\uff0c\u82e5\u662f\u4f60\u6740\u6211\uff0c\u6211\u6740\u4f60\uff0c\u4eba\u4fbf\u5c11\u4e86\u3002\u201d\u60f3\u660e\u767d\u4e86\u6b64\u8282\u4e4b\u540e\uff0c\u4fbf\u5728\u5ba2\u5e97\u4e2d\u6545\u610f\u5c55\u793a\u5b9d\u7269\uff0c\u6599\u60f3\u8d22\u5b9d\u8d8a\u662f\u4f17\u591a\uff0c\u7fa4\u76d7\u81ea\u76f8\u65ab\u6740\u8d77\u6765\u4fbf\u8d8a\u52a0\u7684\u6fc0\u70c8\u3002 \u3000\u3000\u53c8\u884c\u4e86\u4e24\u65e5\uff0c\u5df2\u8fc7\u6d4e\u5357\u5e9c\u5730\u754c\uff0c\u6387\u7740\u8f66\u961f\u7684\u76d7\u5bc7\u6108\u6765\u6108\u591a\u3002\u6d2a\u80dc\u6d77\u672c\u6765\u6709\u6043\u65e0\u6050\uff0c\u4f46\u89c1\u7fa4\u76d7\u8fdf\u8fdf\u4e0d\u52a8\u624b\uff0c\u4e0d\u77e5\u5b89\u6392\u4e0b\u751a\u4e48\u5978\u8c0b\uff0c\u4e0d\u7531\u5f97\u60f4\u60f4\u4e0d\u5b89\u8d77\u6765\uff0c\u529b\u529d\u8881\u627f\u5fd7\u6539\u6b65\u6d77\u9053\uff0c\u8bf4\u81ea\u5df1\u6d77\u4e0a\u670b\u53cb\u5f88\u591a\uff0c\u5750\u8239\u5230\u5929\u6d25\u8d77\u5cb8\uff0c\u518d\u53bb\u5317\u4eac\uff0c\u867d\u7136\u8981\u7ed5\u4e2a\u5927\u5f2f\uff0c\u591a\u8d39\u65f6\u65e5\uff0c\u4f46\u4fdd\u9669\u4e0d\u51fa\u4e71\u5b50\u3002\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u6211\u672c\u8981\u7528\u8fd9\u6279\u73e0\u5b9d\u6765\u7ed3\u4ea4\u5929\u4e0b\u82f1\u96c4\u597d\u6c49\uff0c\u5c31\u662f\u6563\u5c3d\u4e86\u4e5f\u4e0d\u6253\u7d27\u3002\u94b1\u8d22\u662f\u8eab\u5916\u4e4b\u7269\uff0c\u54b1\u4eec\u8bb2\u7a76\u7684\u662f\u4ec1\u4e49\u4e3a\u5148\u3002\u201d\u6d2a\u80dc\u6d77\u542c\u4ed6\u5982\u6b64\u8bf4\uff0c\u4e5f\u5c31\u4e0d\u4fbf\u518d\u529d\u3002 \u3000\u3000\u8fd9\u5929\u5230\u4e86\u79b9\u57ce\uff0c\u6295\u4e86\u5ba2\u5e97\u3002\u9752\u9752\u4fbf\u9080\u8881\u627f\u5fd7\u51fa\u53bb\u73a9\u800d\u3002\u4f46\u8881\u627f\u5fd7\u5fc3\u60f3\u6b64\u523b\u4e0d\u77e5\u6709\u591a\u5c11\u53cc\u773c\u775b\u6ce8\u89c6\u7740\u8fd9\u6279\u73cd\u5b9d\uff0c\u53ea\u8981\u7a0d\u4e00\u6258\u5927\uff0c\u7acb\u5373\u51fa\u4e8b\uff0c\u4fbf\u8ddf\u5979\u8bf4\u660e\u539f\u7531\uff0c\u8981\u5979\u72ec\u81ea\u53bb\u73a9\uff0c\u81ea\u5df1\u4e0e\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u7559\u5728\u5e97\u4e2d\u770b\u5b88\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u9752\u9752\u559c\u5b5c\u5b5c\u7684\u56de\u6765\uff0c\u624b\u91cc\u63d0\u7740\u4e24\u53ea\u5c0f\u7af9\u7b3c\uff0c\u7b3c\u91cc\u5404\u653e\u7740\u4e00\u53ea\u4fc3\u7ec7\uff0c\u55e4\u55e4\u55e4\u7684\u53eb\u4e2a\u4e0d\u505c\u3002\u5979\u628a\u4e00\u53ea\u9001\u7ed9\u8881\u627f\u5fd7\uff0c\u8bf4\u9053\uff1a\u201c\u56db\u6587\u94b1\u4e00\u53ea\uff0c\u4f60\u591c\u91cc\u6302\u5728\u5e10\u5b50\u91cc\uff0c\u624d\u6559\u597d\u542c\u5462\uff01\u201d\u8881\u627f\u5fd7\u7b11\u7740\u63a5\u8fc7\uff0c\u7b11\u95ee\uff1a\u201c\u4f60\u5728\u8857\u4e0a\u9047\u5230\u8c01\u4e86\uff1f\u201d\u9752\u9752\u4e00\u6123\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u80cc\u4e0a\u600e\u4e48\u7ed9\u4eba\u505a\u4e86\u8bb0\u53f7\u5566\uff1f\u201d \u3000\u3000\u9752\u9752\u5fd9\u5954\u56de\u81ea\u5df1\u623f\u91cc\uff0c\u8131\u4e0b\u5916\u8863\u4e00\u770b\uff0c\u679c\u89c1\u540e\u5fc3\u753b\u7740\u4e2a\u767d\u7c89\u5708\uff0c\u60f3\u662f\u4e70\u4fc3\u7ec7\u65f6\u9ad8\u5174\u5f97\u5fd8\u4e86\u522b\u7684\uff0c\u753b\u5708\u4e4b\u4eba\u53c8\u5f88\u673a\u7075\uff0c\u7adf\u6ca1\u53d1\u89c9\u3002 \u3000\u3000\u5979\u53c8\u7f9e\u53c8\u607c\uff0c\u56de\u6765\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5feb\u53bb\u7ed9\u6211\u628a\u90a3\u4eba\u6293\u6765\uff0c\u6253\u4ed6\u4e00\u987f\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u5374\u5230\u54ea\u91cc\u627e\u53bb\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u4e5f\u53bb\u8857\u4e0a\u901b\u901b\uff0c\u5047\u88c5\u50bb\u91cc\u50bb\u6c14\u7684\u4e0d\u7559\u795e\u2026\u2026\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u5c31\u50cf\u4f60\u521a\u624d\u90a3\u526f\u6a21\u6837\uff0c\u81ea\u7136\u6709\u4eba\u6765\u80cc\u4e0a\u753b\u5708\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u5feb\u53bb\u3002\u201d\u8881\u627f\u5fd7\u62d7\u5979\u4e0d\u8fc7\uff0c\u53ea\u5f97\u5631\u5490\u5979\u4e0e\u6d2a\u80dc\u6d77\u5c0f\u5fc3\u5728\u610f\uff0c\u72ec\u81ea\u51fa\u5e97\u3002 \u3000\u3000\u90a3\u79b9\u57ce\u662f\u4e2a\u70ed\u95f9\u6240\u5728\uff0c\u867d\u5c06\u5165\u591c\uff0c\u505a\u4e70\u5356\u7684\u3001\u8d76\u8f66\u7684\u3001\u6311\u62c5\u5b50\u7684\u8fd8\u662f\u6765\u53bb\u4e0d\u7edd\u3002\u8881\u627f\u5fd7\u4e00\u51fa\u5e97\u623f\uff0c\u884c\u4e0d\u6570\u6b65\uff0c\u4fbf\u5bdf\u89c9\u8eab\u540e\u6709\u4eba\u6697\u4e2d\u8ddf\u968f\uff0c\u5fc3\u60f3\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u4eec\u8d8a\u6765\u8d8a\u7316\u72c2\u5566\uff0c\u4e0d\u4f46\u9489\u4f4f\u4e86\u8d27\u8272\uff0c\u8fd8\u77a7\u7740\u6211\u4eec\u6bcf\u4e00\u4e2a\u4eba\u3002\u53ef\u662f\u5728\u9752\u5f1f\u540e\u5fc3\u753b\u4e2a\u767d\u7c89\u5708\uff0c\u53c8\u662f\u751a\u4e48\u7528\u610f\uff1f\u5c82\u975e\u6253\u8349\u60ca\u86c7\uff0c\u8ba9\u6211\u4eec\u6709\u4e86\u63d0\u9632\uff1f\u201d\u5f53\u4e0b\u4e0d\u52a8\u58f0\u8272\uff0c\u5f84\u5f80\u4eba\u591a\u5904\u884c\u53bb\uff0c\u540e\u9762\u90a3\u4eba\u679c\u7136\u8ddf\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d70\u5230\u4e00\u5bb6\u94c1\u94fa\u9762\u524d\uff0c\u89c2\u770b\u94c1\u5320\u94f8\u5200\uff0c\u7b49\u90a3\u4eba\u8d70\u5230\u4e34\u8fd1\uff0c\u7a81\u7136\u53cd\u624b\u4f38\u51fa\uff0c\u6263\u4f4f\u4e86\u4ed6\u624b\u8155\u8109\u95e8\u3002\u90a3\u4eba\u9ebb\u4e86\u534a\u8fb9\u8eab\u5b50\uff0c\u88ab\u8881\u627f\u5fd7\u8f7b\u8f7b\u4e00\u62c9\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u8ddf\u4ed6\u8d70\u5165\u4e86\u4e00\u6761\u5c0f\u5df7\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\u7684\u624b\u4e0b\uff1f\u201d\u90a3\u4eba\u65e9\u5df2\u75db\u5f97\u6ee1\u5934\u5927\u6c57\uff0c\u7ed9\u8881\u627f\u5fd7\u624b\u4e0a\u5fae\u4e00\u7528\u52b2\uff0c\u66f4\u662f\u96be\u5f53\uff0c\u5fd9\u9053\uff1a\u201c\u76f8\u516c\u5feb\u653e\u624b\uff0c\u522b\u634f\u65ad\u4e86\u6211\u9aa8\u5934\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u6211\u8fde\u4f60\u5934\u9888\u9aa8\u4e5f\u626d\u65ad\u4e86\u3002\u201d\u5de6\u624b\u4f38\u51fa\uff0c\u5728\u4ed6\u9888\u91cc\u4e00\u6478\u3002\u90a3\u4eba\u5fd9\u9053\uff1a\u201c\u6211\u8bf4\uff0c\u6211\u8bf4\u3002 \u3000\u3000\u5c0f\u4eba\u53eb\u505a\u9ec4\u4e8c\u6bdb\u5b50\uff0c\u662f\u6076\u864e\u6c9f\u6c99\u5be8\u4e3b\u7684\u624b\u4e0b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u60f3\u5728\u6211\u80cc\u4e0a\u753b\u4e2a\u5708\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u9ec4\u4e8c\u6bdb\u5b50\u9053\uff1a\u201c\u662f\u6c99\u5be8\u4e3b\u5429\u5490\u5c0f\u4eba\u753b\u7684\uff0c\u4e0b\u2026\u2026\u4e0b\u6b21\u518d\u4e5f\u4e0d\u6562\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e72\u4e48\u8981\u753b\u4e2a\u5708\uff1f\u201d\u9ec4\u4e8c\u6bdb\u5b50\u9053\uff1a\u201c\u6c99\u5be8\u4e3b\u8bf4\uff0c\u8fd9\u662f\u6211\u4eec\u6076\u864e\u6c9f\u7684\u8d27\u8272\uff0c\u5148\u505a\u4e0a\u8bb0\u53f7\uff0c\u53eb\u522b\u5bb6\u4e0d\u53ef\u52a8\u624b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u53c8\u597d\u7b11\uff0c\u53c8\u597d\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u6c99\u5be8\u4e3b\u5462\uff1f\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9ec4\u4e8c\u6bdb\u5b50\u4e1c\u5f20\u897f\u671b\u7684\u4e0d\u6562\u8bf4\u3002\u8881\u627f\u5fd7\u6307\u529b\u7a0d\u91cd\uff0c\u9ec4\u4e8c\u6bdb\u5b50\u8155\u9aa8\u767b\u65f6\u683c\u683c\u4f5c\u54cd\uff0c\u751f\u6015\u7ed9\u634f\u65ad\u4e86\uff0c\u5fd9\u9053\uff1a\u201c\u6c99\u5be8\u4e3b\u53eb\u5c0f\u4eba\u2026\u2026\u53eb\u5c0f\u4eba\u4eca\u665a\u5230\u57ce\u5916\u4e09\u5149\u5bfa\u53bb\u4f1a\u9f50\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5e26\u8def\u3002\u201d \u3000\u3000\u9ec4\u4e8c\u6bdb\u5b50\u4e0d\u6562\u4e0d\u4f9d\uff0c\u9886\u7740\u4ed6\u6765\u5230\u4e09\u5149\u5bfa\u3002\u8fd9\u65f6\u5929\u8272\u5c1a\u65e9\uff0c\u5e99\u4e2d\u65e0\u4eba\u3002\u8881\u627f\u5fd7\u89c1\u90a3\u5e99\u751a\u4e3a\u7834\u8d25\uff0c\u4e5f\u4e0d\u89c1\u5e99\u795d\u548c\u5c1a\uff0c\u524d\u524d\u540e\u540e\u67e5\u4e86\u4e00\u904d\uff0c\u5c06\u9ec4\u4e8c\u6bdb\u5b50\u70b9\u4e86\u54d1\u7a74\uff0c\u63b7\u5728\u795e\u9f9b\u4e4b\u4e2d\u3002\u7b49\u4e86\u4e00\u4f1a\uff0c\u542c\u5f97\u5e99\u5916\u4f20\u6765\u8bf4\u8bdd\u4e4b\u58f0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ea\u8eab\u8eb2\u5728\u4f5b\u50cf\u4e4b\u540e\uff0c\u53ea\u542c\u5f97\u6570\u5341\u4eba\u8d70\u8fdb\u5e99\u6765\uff0c\u5728\u5927\u6bbf\u4e2d\u95f4\u56e2\u56e2\u5750\u4e0b\u3002\u4e00\u4e2a\u5c16\u7ec6\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4e25\u8001\u56db\u3001\u4e25\u8001\u4e94\uff0c\u4f60\u54e5\u513f\u4fe9\u5e26\u9886\u56db\u540d\u5f1f\u5144\u56db\u4e0b\u671b\u98ce\uff0c\u5c4b\u4e0a\u4e5f\u6d3e\u4e24\u4eba\u3002\u201d\u90a3\u4e24\u4eba\u5e94\u58f0\u51fa\u53bb\uff0c\u4e0d\u4e45\u4fbf\u542c\u5f97\u5c4b\u4e0a\u6709\u811a\u6b65\u4e4b\u58f0\u3002\u8881\u627f\u5fd7\u6697\u7b11\uff1a\u201c\u9976\u4f60\u4ed4\u7ec6\uff0c\u6211\u5374\u5df2\u5148\u5728\u8fd9\u91cc\u606d\u5019\u4e86\u3002\u201d\u8fc7\u5f97\u4e00\u9635\uff0c\u5e99\u5916\u53c8\u9646\u7eed\u8fdb\u6765\u591a\u4eba\uff0c\u5927\u5bb6\u95f9\u54c4\u54c4\u7684\u79f0\u5144\u9053\u5f1f\uff0c\u5ba2\u6c14\u4e86\u4e00\u9635\u3002\u8881\u627f\u5fd7\u542c\u4f17\u4eba\u79f0\u547c\uff0c\u539f\u6765\u662f\u5c71\u4e1c\u516b\u5927\u5c71\u5be8\u7684\u5be8\u4e3b\u5728\u6b64\u805a\u4f1a\uff0c\u5012\u4e5f\u4e0d\u6562\u5927\u610f\uff0c\u5f53\u4e0b\u5c4f\u606f\u9759\u542c\u3002 \u3000\u3000\u53ea\u542c\u90a3\u58f0\u97f3\u5c16\u7ec6\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u7b14\u8d27\u8272\u5df2\u63a2\u5f97\u660e\u767d\uff0c\u786e\u662f\u975e\u540c\u5c0f\u53ef\u3002\u62bc\u8fd0\u7684\u662f\u4e24\u4e2a\u96cf\u513f\u3002\u4fdd\u9556\u7684\u540d\u53eb\u6d2a\u80dc\u6d77\uff0c\u662f\u6e24\u6d77\u6d3e\u7684\uff0c\u542c\u8bf4\u624b\u4e0b\u8fd8\u786c\u3002\u53ef\u662f\u4ed6\u5355\u67aa\u5339\u9a6c\uff0c\u8d70\u8fd9\u8d9f\u5927\u9556\u3002\u5f53\u771f\u72c2\u5984\u81ea\u5927\u4e4b\u81f3\u3002\u201d\u7fa4\u76d7\u90fd\u8f70\u7b11\u8d77\u6765\u3002\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u600e\u4e48\u53d6\u9556\uff0c\u4e0d\u52b3\u5927\u4f19\u513f\u8d39\u5fc3\uff0c\u8fd8\u4e0d\u662f\u624b\u5230\u8d27\u6765\uff0c\u5f00\u5f20\u53d1\u8d22\uff1f\u4f46\u600e\u4e48\u5206\u7ea2\uff0c\u5927\u4f19\u513f\u53ef\u5148\u5f97\u5546\u91cf\u597d\uff0c\u522b\u8981\u574f\u4e86\u9053\u4e0a\u7684\u4e49\u6c14\u3002\u201d\u90a3\u6c99\u5be8\u4e3b\u9053\uff1a\u201c\u5c0f\u5f1f\u9080\u8bf7\u5404\u4f4d\u5144\u957f\u5230\u8fd9\u91cc\u805a\u4f1a\uff0c\u5c31\u662f\u4e3a\u6b64\u3002\u201d \u3000\u3000\u4e00\u4e2a\u58f0\u97f3\u7c97\u8c6a\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u7b14\u8d27\u662f\u6211\u4eec\u7b2c\u4e00\u4e2a\u770b\u4e0a\u7684\u3002 \u3000\u3000\u6211\u8bf4\u561b\uff0c\u8d27\u8272\u5341\u80a1\u5747\u5206\u3002\u6076\u864e\u6c9f\u5360\u4e24\u4efd\uff0c\u6211\u4eec\u6740\u8c79\u5c97\u5360\u4e24\u4efd\uff0c\u5176\u4f59\u7684\u4e00\u5bb6\u4e00\u4efd\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u4eec\u5df2\u628a\u522b\u4eba\u7684\u8d22\u5b9d\uff0c\u5f53\u4f5c\u4e86\u81ea\u5df1\u56ca\u4e2d\u4e4b\u7269\u3002\u805a\u5728\u8fd9\u91cc\uff0c\u539f\u6765\u662f\u4e3a\u5206\u8d43\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u4f60\u6740\u8c79\u5c97\u51ed\u751a\u4e48\u5206\u4e24\u4efd\uff1f\u6211\u8bf4\u662f\u516b\u5bb6\u5e73\u5206\u3002\u201d \u3000\u3000\u7fa4\u76d7\u767b\u65f6\u55a7\u58f0\u5927\u4f5c\uff0c\u7eb7\u4e89\u4e0d\u5df2\u3002\u8881\u627f\u5fd7\u6697\u6697\u559c\u6b22\uff1a\u201c\u5411\u6765\u53ea\u6709\u5206\u8d43\u4e0d\u5300\uff0c\u8fd9\u624d\u6253\u67b6\u3002\u4f60\u4eec\u8d43\u7269\u8fd8\u6ca1\u5230\u624b\uff0c\u5374\u5df2\u5148\u5206\u4e0d\u5300\u4e86\uff0c\u4e0d\u59a8\u5c31\u5728\u8fd9\u91cc\u62da\u4e2a\u4f60\u6b7b\u6211\u6d3b\u3002\u201d \u3000\u3000\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u8fd9\u6b21\u54b1\u4eec\u5408\u4f19\u505a\u4e70\u5356\uff0c\u53ef\u4e0d\u80fd\u4f24\u4e86\u7eff\u6797\u4e2d\u7684\u4e49\u6c14\u3002\u5927\u4f19\u513f\u603b\u8981\u516c\u516c\u9053\u9053\u3002\u6076\u864e\u6c9f\u6709\u51e0\u5343\u5144\u5f1f\uff0c\u6740\u8c79\u5c97\u548c\u4e71\u77f3\u5be8\u90fd\u53ea\u6709\u4e09\u767e\u6765\u4eba\uff0c\u96be\u9053\u62ff\u540c\u6837\u7684\u4efd\u513f\uff1f\u6211\u8bf4\u561b\uff0c\u8fd9\u6869\u4e70\u5356\uff0c\u5f53\u7136\u8bf7\u6c99\u5be8\u4e3b\u9886\u5934\uff0c\u4ed6\u8001\u4eba\u5bb6\u591a\u5f97\u5341\u4e07\u4e24\u94f6\u5b50\u7684\u73e0\u5b9d\u3002\u6740\u8c79\u5c97\u6700\u5148\u770b\u4e0a\u8fd9\u7968\u8d27\u8272\uff0c\u4ed6\u6740\u8c79\u5c97\u591a\u5f97\u4e00\u4e07\u4e24\u3002\u4f59\u4e0b\u7684\u5e73\u5206\u4e5d\u4efd\uff0c\u6076\u864e\u6c9f\u62ff\u4e24\u4efd\uff0c\u4f59\u4e0b\u4e03\u5be8\u5404\u62ff\u4e00\u4efd\u3002\u201d\u7fa4\u76d7\u4e00\u6765\u4e0d\u6562\u8ddf\u6076\u864e\u6c9f\u76f8\u4e89\uff0c\u4e8c\u6765\u4e5f\u89c9\u6b64\u8a00\u6709\u7406\uff0c\u4fbf\u90fd\u8d5e\u540c\u4e86\u3002\u6c99\u5be8\u4e3b\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u660e\u513f\u5c31\u52a8\u624b\u3002\u54b1\u4eec\u5728\u5f20\u5e84\u5f00\u6252\uff0c\u5927\u4f19\u513f\u7387\u9886\u5144\u5f1f\u53bb\u5f20\u5e84\u5427\uff01\u201d\u4f17\u4eba\u8f70\u7136\u7b54\u5e94\uff0c\u7eb7\u7eb7\u51fa\u5e99\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u4eec\u5012\u5206\u5f97\u516c\u9053\uff0c\u81ea\u5df1\u5b9a\u4e0b\u7684\u8ba1\u7b56\u4f3c\u4e4e\u4e0d\u7ba1\u4e8b\uff0c\u4e0d\u514d\u591a\u4e86\u5c42\u5fe7\u5fc3\u3002\u5bfb\u601d\uff1a\u201c\u6211\u60f3\u5f97\u5230\u7684\u4e8b\uff0c\u8fd9\u4e9b\u8001\u5978\u5de8\u6ed1\u7684\u5f3a\u76d7\u5f53\u7136\u65e9\u60f3\u5230\u4e86\u3002\u9752\u5f1f\u4ece\u524d\u662f\u4ed6\u4eec\u7684\u884c\u5bb6\uff0c\u5979\u7684\u4e3b\u610f\u5b9a\u7136\u6bd4\u6211\u7684\u5728\u884c\u3002\u201d\u5f53\u4e0b\u4e5f\u4e0d\u7406\u4f1a\u90a3\u9ec4\u4e8c\u6bdb\u5b50\uff0c\u5f84\u81ea\u56de\u5e97\uff0c\u628a\u63a2\u542c\u5230\u7684\u6d88\u606f\u5bf9\u9752\u9752\u8bf4\u4e86\uff0c\u95ee\u5979\u9053\uff1a\u201c\u76d7\u8d3c\u52bf\u5927\uff0c\u6253\u4e0d\u5b8c\uff0c\u6740\u4e0d\u5c3d\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u4e8b\u5230\u4e34\u5934\u4e4b\u65f6\uff0c\u54b1\u4eec\u5148\u6c89\u4f4f\u6c14\uff0c\u5f85\u5f97\u8ba4\u51fa\u4e86\u76d7\u9b41\uff0c\u4f60\u4e00\u4e0b\u5b50\u628a\u4ed6\u6293\u4f4f\uff0c\u5c0f\u55bd\u7f57\u4eec\u5c31\u4e0d\u6562\u52a8\u4e86\u3002\u201d\u8881\u627f\u5fd7\u5927\u559c\uff0c\u7b11\u9053\uff1a\u201c\u64d2\u8d3c\u5148\u64d2\u738b\uff0c\u8fd9\u4e3b\u610f\u6700\u597d\u3002\u201d \u3000\u3000\u6b21\u65e5\u4e0a\u8def\uff0c\u4e00\u8def\u4e0a\u7fa4\u76d7\u54e8\u63a2\u6765\u53bb\u4e0d\u7edd\uff0c\u660e\u76ee\u5f20\u80c6\uff0c\u5168\u4e0d\u628a\u8881\u627f\u5fd7\u7b49\u653e\u5728\u773c\u91cc\u3002\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u76f8\u516c\uff0c\u77a7\u8fd9\u795e\u6c14\uff0c\u8fc7\u4e0d\u4e86\u4eca\u5929\u5566\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u7167\u6599\u8f66\u961f\uff0c\u522b\u8ba9\u9aa1\u5b50\u53d7\u60ca\u4e71\u8dd1\u3002 \u3000\u3000\u5f3a\u4eba\u7531\u6211\u4eec\u4e09\u4eba\u5bf9\u4ed8\u3002\u201d\u6d2a\u80dc\u6d77\u5e94\u4e86\u3002\u8881\u627f\u5fd7\u6253\u624b\u52bf\u544a\u8bc9\u54d1\u5df4\uff0c\u53eb\u4ed6\u770b\u81ea\u5df1\u624b\u52bf\u624d\u52a8\u624b\uff0c\u4e13\u7ba1\u6349\u4eba\u3002\u54d1\u5df4\u70b9\u5934\u7b54\u5e94\u3002 \u3000\u3000\u884c\u5230\u7533\u724c\u65f6\u5206\uff0c\u5c06\u5230\u5f20\u5e84\uff0c\u773c\u524d\u9ed1\u538b\u538b\u4e00\u5927\u7247\u6811\u6797\uff0c\u5ffd\u542c\u5f97\u5934\u9876\u545c\u545c\u58f0\u54cd\uff0c\u51e0\u53ea\u54cd\u7bad\u5c04\u8fc7\uff0c\u9523\u58f0\u54cd\u5904\uff0c\u6797\u4e2d\u94bb\u51fa\u6570\u767e\u540d\u5927\u6c49\uff0c\u4e00\u4e2a\u4e2a\u90fd\u662f\u9752\u5e03\u5305\u5934\uff0c\u9ed1\u8863\u9ed1\u88e4\uff0c\u624b\u6267\u5175\u5203\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u7684\u62e6\u5728\u5f53\u8def\u3002\u4f17\u8f66\u592b\u65e9\u77e5\u60c5\u5f62\u4e0d\u5bf9\uff0c\u62c9\u4f4f\u7272\u53e3\uff0c\u62b1\u5934\u5f80\u5730\u4e0b\u4e00\u8e72\u3002\u8fd9\u662f\u884c\u811a\u7684\u89c4\u77e9\uff0c\u53ea\u8981\u4e0d\u4e71\u9003\u4e71\u95ef\uff0c\u52ab\u9053\u7684\u5f3a\u4eba\u4e0d\u4f24\u8f66\u592b\u3002\u53c8\u542c\u5f97\u553f\u54e8\u8fde\u8fde\uff0c\u8e44\u58f0\u6742\u6c93\uff0c\u6797\u4e2d\u659c\u523a\u91cc\u51b2\u51fa\u6570\u5341\u9a91\u9a6c\u6765\uff0c\u6321\u5728\u8f66\u961f\u4e4b\u540e\uff0c\u62e6\u4f4f\u4e86\u9000\u8def\uff0c\u4e5f\u90fd\u662f\u8083\u9759\u65e0\u54d7\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6628\u5929\u5728\u4e09\u5149\u5e99\u4e2d\u6ca1\u89c1\u5230\u7fa4\u76d7\u9762\u76ee\uff0c\u8fd9\u65f6\u4ed4\u7ec6\u6253\u91cf\uff0c\u53ea\u89c1\u524d\u9762\u516b\u4eba\u4e00\u5b57\u6392\u5f00\u3002\u4e00\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u767d\u8138\u6c49\u5b50\u8d8a\u4f17\u800c\u51fa\uff0c\u624b\u4e2d\u4e0d\u62ff\u5175\u5203\uff0c\u53ea\u6447\u7740\u4e00\u67c4\u6298\u6247\uff0c\u7ec6\u58f0\u7ec6\u6c14\u7684\u9053\uff1a\u201c\u8881\u76f8\u516c\u8bf7\u4e86\uff01\u201d\u8881\u627f\u5fd7\u4e00\u542c\u58f0\u97f3\uff0c\u5c31\u77e5\u4ed6\u662f\u6076\u864e\u6c9f\u7684\u6c99\u5be8\u4e3b\uff0c\u89c1\u4ed6\u811a\u6b65\u51dd\u91cd\uff0c\u5fc3\u60f3\u8fd9\u4eba\u679c\u7136\u6b66\u529f\u4e0d\u5f31\uff0c\u624b\u6301\u94c1\u9aa8\u6298\u6247\uff0c\u591a\u534a\u64c5\u4e8e\u6253\u7a74\uff0c\u5f53\u4e0b\u4e00\u62f1\u624b\u9053\uff1a\u201c\u6c99\u5be8\u4e3b\u8bf7\u4e86\u3002\u201d \u3000\u3000\u6c99\u5be8\u4e3b\u4e00\u60ca\uff0c\u5bfb\u601d\uff1a\u201c\u4ed6\u600e\u77e5\u6211\u59d3\u6c99\uff1f\u201d\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\u8fdc\u6765\u8f9b\u82e6\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u8138\u4e0a\u795e\u8272\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u4e00\u8def\u6d3e\u4eba\u8ddf\u8e2a\uff0c\u81ea\u7136\u65e9\u6253\u542c\u5230\u4e86\u6211\u59d3\u8881\u3002\u4f46\u6211\u53eb\u4ed6\u6c99\u5be8\u4e3b\uff0c\u53ea\u6015\u4ed6\u5927\u60d1\u4e0d\u89e3\u4e86\u3002\u7d22\u6027\u7ed9\u4ed6\u88c5\u849c\u3002\u201d\u8bf4\u9053\uff1a\u201c\u6c99\u5be8\u4e3b\u4f60\u4e5f\u8f9b\u82e6\u3002\u5144\u5f1f\u8d76\u9053\u5012\u6ca1\u751a\u4e48\uff0c\u5c31\u662f\u884c\u674e\u592a\u7b28\u91cd\uff0c\u5e26\u7740\u8ba8\u538c\u3002\u201d \u3000\u3000\u6c99\u5be8\u4e3b\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\u4e0a\u4eac\u662f\u53bb\u8d76\u8003\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u975e\u4e5f\uff01\u5c0f\u5f1f\u8bfb\u4e66\u4e0d\u6210\uff0c\u8003\u6765\u8003\u53bb\uff0c\u59cb\u7ec8\u843d\u7b2c\uff0c\u53ea\u597d\u53bb\u7eb3\u6350\u884c\u8d3f\uff0c\u6d3b\u52a8\u4e2a\u529f\u540d\uff0c\u56e0\u6b64\u809a\u91cc\u58a8\u6c34\u4e0d\u591a\uff0c\u624b\u8fb9\u8d22\u7269\u4e0d\u5c11\uff0c\u54c8\u54c8\uff0c\u60ed\u6127\u554a\u60ed\u6127\u3002\u201d\u6c99\u5be8\u4e3b\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u5012\u5f88\u723d\u76f4\uff0c\u6ca1\u6709\u8bfb\u4e66\u4eba\u7684\u9178\u6c14\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u6628\u5929\u6709\u4f4d\u670b\u53cb\u8ddf\u6211\u8bf4\uff0c\u4eca\u513f\u6709\u4e00\u4f4d\u59d3\u6c99\u7684\u6c99\u5be8\u4e3b\u5728\u9053\u4e0a\u7b49\u5019\uff0c\u53ef\u987b\u5c0f\u5fc3\u5728\u610f\u3002\u8fd8\u6709\u6740\u8c79\u5c97\u3001\u4e71\u77f3\u5be8\u7b49\u7b49\uff0c\u4e00\u5171\u6709\u516b\u5bb6\u5be8\u4e3b\u3002\u5144\u5f1f\u6b22\u559c\u5f97\u7d27\uff0c\u5fc3\u60f3\u8fd9\u4e00\u6765\u53ef\u633a\u70ed\u95f9\u4e86\u3002\u6211\u4e00\u8def\u4e4b\u4e0a\u6ca1\u6562\u758f\u5ffd\uff0c\u8001\u662f\u4e1c\u5f20\u897f\u671b\u7684\u7b49\u5019\u6c99\u5be8\u4e3b\uff0c\u5c31\u53ea\u6015\u9519\u8fc7\u4e86\uff0c\u54ea\u77e5\u679c\u7136\u5728\u6b64\u76f8\u9047\u3002\u4eca\u65e5\u4e00\u89c1\uff0c\u4e09\u751f\u6709\u5e78\u3002\u77a7\u9601\u4e0b\u8fd9\u526f\u6253\u626e\uff0c\u83ab\u4e0d\u662f\u4e5f\u4e0a\u4eac\u4e48\uff1f\u54b1\u4eec\u7ed3\u4f34\u800c\u884c\u5982\u4f55\uff1f\u4e00\u8def\u4e0a\u8c08\u8c08\u8bb2\u8bb2\uff0c\u996e\u9152\u73a9\u4e50\uff0c\u5012\u662f\u9887\u4e0d\u5bc2\u5bde\u3002\u201d\u6c99\u5be8\u4e3b\u5fc3\u4e2d\u4e00\u4e50\uff0c\u6697\u60f3\u539f\u6765\u8fd9\u4eba\u662f\u4e2a\u4e66\u5446\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\u5728\u5bb6\u7eb3\u798f\uff0c\u5c82\u4e0d\u662f\u597d\uff0c\u4f55\u5fc5\u51fa\u95e8\u5954\u6ce2\uff1f\u8981\u77e5\u6c5f\u6e56\u4e0a\u9669\u6076\u5f97\u5f88\u5462\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5728\u5bb6\u65f6\u66fe\u542c\u4eba\u8bf4\u9053\uff0c\u6c5f\u6e56\u4e0a\u6709\u751a\u4e48\u9a97\u5b50\u75de\u68cd\uff0c\u5f3a\u76d7\u6076\u8d3c\uff0c\u54ea\u77e5\u8d70\u4e86\u4e0a\u5343\u91cc\u8def\uff0c\u4e00\u4e2a\u4e5f\u6ca1\u9047\u7740\u3002\u60f3\u6765\u591a\u534a\u662f\u6b3a\u4eba\u4e4b\u8c08\uff0c\u5f53\u4e0d\u662f\u771f\u7684\u3002\u8fd9\u8bb8\u591a\u670b\u53cb\u4eec\u6392\u5728\u8fd9\u91cc\u5e72\u751a\u4e48\uff1f\u5927\u4f19\u513f\u73a9\u64cd\u5175\u4e48\uff1f\u5012\u4e5f\u6709\u8da3\u3002\u201d \u3000\u3000\u90a3\u4e03\u5bb6\u5be8\u4e3b\u542c\u8881\u627f\u5fd7\u534a\u75f4\u534a\u5446\u7684\u5520\u53e8\u4e0d\u4f11\uff0c\u65e9\u5df2\u5fcd\u8010\u4e0d\u4f4f\uff0c\u4e0d\u505c\u5411\u6c99\u5be8\u4e3b\u6253\u773c\u8272\uff0c\u8981\u4ed6\u5feb\u4e0b\u4ee4\u52a8\u624b\u3002\u6c99\u5be8\u4e3b\u7b11\u5bb9\u5ffd\u655b\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u6247\u5b50\u500f\u5730\u5f20\u5f00\u3002\u53ea\u89c1\u767d\u6247\u4e0a\u753b\u7740\u4e00\u4e2a\u9ed1\u8272\u9ab7\u9ac5\u5934\uff0c\u9ab7\u9ac5\u53e3\u4e2d\u6a2a\u54ac\u4e00\u67c4\u5200\u5b50\uff0c\u6a21\u6837\u5341\u5206\u53ef\u6016\u3002 \u3000\u3000\u9752\u9752\u89c1\u4e86\u4e0d\u89c9\u5fc3\u60ca\uff0c\u8f7b\u58f0\u4f4e\u547c\u3002\u8881\u627f\u5fd7\u867d\u7136\u827a\u9ad8\u80c6\u5927\uff0c\u5374\u4e5f\u611f\u5230\u4e00\u9635\u9634\u68ee\u68ee\u7684\u5bd2\u6c14\u3002\u6c99\u5be8\u4e3b\u78d4\u78d4\u602a\u7b11\uff0c\u6247\u5b50\u4e00\u62db\uff0c\u6570\u767e\u540d\u76d7\u5bc7\u9f50\u5411\u9aa1\u961f\u6251\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6b63\u8981\u7eb5\u8eab\u51fa\u53bb\u64d2\u62ff\u6c99\u5be8\u4e3b\uff0c\u5ffd\u542c\u5f97\u6797\u4e2d\u4f20\u51fa\u4e00\u9635\u53e3\u5439\u7af9\u53f6\u7684\u5c16\u5389\u54e8\u58f0\u3002\u6c99\u5be8\u4e3b\u4e00\u542c\uff0c\u8138\u8272\u9661\u53d8\uff0c\u6247\u5b50\u53c8\u662f\u4e00\u6325\uff0c\u7fa4\u76d7\u767b\u65f6\u505c\u6b65\u3002 \u3000\u3000\u53ea\u89c1\u6797\u4e2d\u9a70\u51fa\u4e24\u4e58\u9a6c\u6765\uff0c\u5f53\u5148\u4e00\u4eba\u662f\u4e2a\u987b\u7709\u7686\u767d\u7684\u8001\u8005\uff0c\u540e\u9762\u8ddf\u7740\u4e00\u4e2a\u5782\u9afb\u9752\u8863\u5c11\u5973\uff0c\u4e00\u77a5\u4e4b\u95f4\uff0c\u4f46\u89c1\u5bb9\u8272\u7edd\u4e3d\u3002\u4e24\u4e2a\u6765\u5230\u6c99\u5be8\u4e3b\u4e0e\u8881\u627f\u5fd7\u4e4b\u95f4\uff0c\u52d2\u4f4f\u4e86\u9a6c\u3002 \u3000\u3000\u6c99\u5be8\u4e3b\u77aa\u773c\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u5c71\u4e1c\u5730\u754c\u3002\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\u554a\uff01\u201d\u6c99\u5be8\u4e3b\u9053\uff1a\u201c\u54b1\u4eec\u5f53\u5e74\u5728\u6cf0\u5c71\u5927\u4f1a\uff0c\u600e\u4e48\u8bf4\u6765\u7740\uff1f\u201d\u8001\u8005\u9053\uff1a\u201c\u6211\u4eec\u9752\u7af9\u5e2e\u4e0d\u6765\u5c71\u4e1c\u505a\u6848\uff0c\u4f60\u4eec\u4e5f\u522b\u53bb\u5317\u76f4\u96b6\u52a8\u624b\u3002\u201d \u3000\u3000\u6c99\u5be8\u4e3b\u9053\uff1a\u201c\u7167\u5440\uff01\u4eca\u65e5\u751a\u4e48\u597d\u98ce\u628a\u7a0b\u8001\u7237\u5b50\u5439\u6765\u5566\uff1f\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u542c\u8bf4\u6709\u4e00\u6279\u8d27\u8272\u8981\u4e0a\u5317\u76f4\u96b6\u6765\uff0c\u4e1c\u897f\u597d\u50cf\u4e0d\u5c11\uff0c\u56e0\u6b64\u6211\u4eec\u5148\u6765\u77a7\u77a7\u8d27\u6837\u6210\u8272\u3002\u201d\u6c99\u5be8\u4e3b\u53d8\u8272\u9053\uff1a\u201c\u7b49\u8d27\u8272\u5230\u4e86\u7a0b\u8001\u7237\u5b50\u5883\u5185\uff0c\u4f60\u8001\u518d\u77a7\u4e0d\u8fdf\u5427\uff1f\u201d\u90a3\u8001\u8005\u5475\u5475\u7b11\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u8fdf\uff1f \u3000\u3000\u90a3\u65f6\u8d27\u8272\u65e9\u5230\u4e86\u6076\u864e\u6c9f\u4f60\u8001\u5f1f\u5be8\u91cc\uff0c\u8001\u5934\u513f\u600e\u4e48\u8fd8\u597d\u610f\u601d\u524d\u6765\u63a2\u5934\u63a2\u8111\uff1f\u90a3\u53ef\u4e0d\u662f\u592a\u4e0d\u8bb2\u4e49\u6c14\u4e86\u5417\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u3001\u6d2a\u80dc\u6d77\u4e09\u4eba\u5bf9\u671b\u4e86\u4e00\u8ddf\uff0c\u5fc3\u60f3\u539f\u6765\u6cb3\u5317\u5927\u76d7\u4e5f\u5f97\u5230\u4e86\u6d88\u606f\uff0c\u8981\u6765\u5206\u4e00\u676f\u7fb9\uff0c\u4e14\u77a7\u4ed6\u4eec\u600e\u4e48\u6253\u4ea4\u9053\u3002 \u3000\u3000\u53ea\u542c\u5c71\u4e1c\u7fa4\u76d7\u7eb7\u7eb7\u8d77\u54c4\uff0c\u4e03\u5634\u516b\u820c\u7684\u5927\u53eb\uff1a\u201c\u7a0b\u9752\u7af9\uff0c\u4f60\u86ee\u4e0d\u8bb2\u7406\uff01\u201d\u201c\u4ed6\u5988\u7684\uff0c\u4f60\u82e5\u8bb2\u4e49\u6c14\uff0c\u5c31\u4e0d\u8be5\u5230\u5c71\u4e1c\u5730\u754c\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u5b88\u9053\u4e0a\u89c4\u77e9\uff0c\u4e0d\u8981\u8138\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u5927\u4f19\u513f\u4e71\u4e03\u516b\u7cdf\u7684\u8bf4\u4e9b\u751a\u4e48\uff1f\u8001\u5934\u513f\u5e74\u7eaa\u5927\u4e86\uff0c\u8033\u6735\u4e0d\u7075\uff0c\u542c\u4e0d\u6e05\u695a\u3002\u5c71\u4e1c\u9053\u4e0a\u7684\u5217\u4f4d\u670b\u53cb\u4eec\uff0c\u90fd\u5728\u8d5e\u6211\u8001\u5934\u513f\u4e49\u8584\u4e91\u5929\u5417\uff1f\u201d \u3000\u3000\u6c99\u5be8\u4e3b\u6298\u6247\u4e00\u6325\uff0c\u7fa4\u76d7\u4f4f\u53e3\u3002\u6c99\u5be8\u4e3b\u9053\uff1a\u201c\u54b1\u4eec\u6709\u7ea6\u5728\u5148\uff0c\u7a0b\u8001\u7237\u5b50\u600e\u4e48\u53c8\u6765\u53cd\u6094\uff1f\u65e0\u4fe1\u65e0\u4e49\uff0c\u5c82\u4e0d\u662f\u89c1\u7b11\u4e8e\u6c5f\u6e56\u4e0a\u7684\u82f1\u96c4\u597d\u6c49\uff1f\u201d \u3000\u3000\u7a0b\u9752\u7af9\u4e0d\u7b54\u8bdd\uff0c\u95ee\u8eab\u65c1\u5c11\u5973\u9053\uff1a\u201c\u963f\u4e5d\u554a\uff0c\u6211\u5728\u5bb6\u91cc\u8ddf\u4f60\u8bf4\u751a\u4e48\u4e86\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u8bf4\uff0c\u54b1\u4eec\u95f2\u7740\u4e5f\u662f\u95f2\u7740\uff0c\u4e0d\u5982\u5230\u5c71\u4e1c\u901b\u901b\uff0c\u4e58\u4fbf\u5c31\u77a7\u77a7\u8d27\u6837\u3002\u201d \u3000\u3000\u9752\u9752\u542c\u5979\u5410\u8bed\u5982\u73e0\uff0c\u58f0\u97f3\u53c8\u662f\u67d4\u548c\u53c8\u662f\u6e05\u8106\uff0c\u52a8\u542c\u4e4b\u6781\uff0c\u5411\u5979\u7ec6\u671b\u4e86\u51e0\u773c\uff0c\u89c1\u5979\u795e\u6001\u5929\u771f\uff0c\u53cc\u988a\u6655\u7ea2\uff0c\u5e74\u7eaa\u867d\u5e7c\uff0c\u5374\u662f\u5bb9\u8272\u6e05\u4e3d\uff0c\u6c14\u5ea6\u9ad8\u96c5\uff0c\u5f53\u771f\u6bd4\u753b\u513f\u91cc\u6458\u4e0b\u6765\u7684\u4eba\u8fd8\u8981\u597d\u770b\uff0c\u60f3\u4e0d\u5230\u76d7\u4f19\u4e4b\u4e2d\uff0c\u7adf\u4f1a\u6709\u5982\u6b64\u660e\u73e0\u7f8e\u7389\u4e00\u822c\u4fca\u6781\u65e0\u4fe6\u7684\u4eba\u54c1\u3002 \u3000\u3000\u9752\u9752\u5411\u6765\u81ea\u8d1f\u7f8e\u8c8c\uff0c\u76f8\u5f62\u4e4b\u4e0b\uff0c\u81ea\u89c9\u9887\u6709\u4e0d\u5982\uff0c\u5fcd\u4e0d\u4f4f\u5411\u8881\u627f\u5fd7\u659c\u77a5\u4e00\u773c\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u8bf4\u8fc7\u8981\u4f38\u624b\u505a\u6848\u6ca1\u6709\uff1f\u201d\u963f\u4e5d\u9053\uff1a\u201c\u6ca1\u6709\u554a\u3002\u4f60\u8001\u4eba\u5bb6\u8bf4\uff0c\u54b1\u4eec\u8ddf\u5c71\u4e1c\u7684\u670b\u53cb\u4eec\u8bf4\u597d\u4e86\u7684\uff0c\u5c71\u4e1c\u5883\u5185\uff0c\u5c31\u662f\u6709\u91d1\u5c71\u94f6\u5c71\u5806\u5728\u9762\u524d\uff0c\u9752\u7af9\u5e2e\u4e5f\u4e0d\u80fd\u62ff\u4e00\u4e2a\u5927\u94b1\uff0c\u8fd9\u53eb\u505a\u8a00\u800c\u6709\u4fe1\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u8f6c\u5934\u5bf9\u6c99\u5be8\u4e3b\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u542c\u89c1\u6ca1\u6709\uff1f\u6211\u51e0\u65f6\u8bf4\u8fc7\u8981\u5728\u5c71\u4e1c\u5730\u754c\u505a\u6848\u54ea\uff1f\u201d \u3000\u3000\u6c99\u5be8\u4e3b\u7ef7\u7d27\u7684\u8138\u767b\u65f6\u677e\u4e86\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u554a\uff0c\u8fd9\u624d\u591f\u4e49\u6c14\u3002\u7a0b\u8001\u7237\u5b50\u8fdc\u9053\u800c\u6765\uff0c\u5f85\u4f1a\u4e5f\u5206\u4e00\u4efd\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u4e0d\u7406\u4ed6\uff0c\u53c8\u5411\u963f\u4e5d\u9053\uff1a\u201c\u963f\u4e5d\u554a\uff0c\u54b1\u4eec\u5728\u5bb6\u53c8\u8bf4\u751a\u4e48\u6765\u7740\uff1f\u201d\u963f\u4e5d\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u8bf4\u8d27\u8272\u4e0d\u5c11\uff0c\u8def\u4e0a\u82e5\u662f\u5931\u843d\u4e86\u751a\u4e48\uff0c\u54b1\u4eec\u53ef\u5403\u4e8f\u4e0d\u8d77\uff0c\u8981\u662f\u8ba9\u4eba\u5bb6\u987a\u624b\u7275\u4e86\u7f8a\u53bb\uff0c\u54b1\u4eec\u7684\u8138\u5c31\u4e22\u5927\u4e86\u3002\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u55ef\uff0c\u8981\u662f\u4eba\u5bb6\u4e0d\u7ed9\u9762\u5b50\uff0c\u5b9a\u8981\u62ff\u5462\uff1f\u201d\u963f\u4e5d\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u8bf4\uff0c\u54b1\u4eec\u5728\u5317\u76f4\u96b6\u9ed1\u9053\u4e0a\u53d1\u8d22\uff0c\u5230\u4e86\u5c71\u4e1c\uff0c\u8f6c\u884c\u505a\u505a\u4fdd\u9556\u7684\uff0c\u5012\u4e5f\u65b0\u9c9c\u3002\u5018\u82e5\u6709\u4eba\u8981\u52a8\u624b\uff0c\u54b1\u4eec\u65e0\u53ef\u5948\u4f55\uff0c\u7ed9\u4eba\u5bb6\u903c\u4e0a\u6881\u5c71\uff0c\u4e5f\u53ea\u597d\u51fa\u624b\u4fdd\u62a4\u4e86\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u7b11\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\u8bb0\u6027\u771f\u4e0d\u574f\uff0c\u6211\u8bb0\u5f97\u786e\u662f\u8fd9\u4e48\u8bf4\u8fc7\u7684\u3002\u201d\u8f6c\u5934\u5bf9\u6c99\u5be8\u4e3b\u9053\uff1a\u201c\u8001\u5f1f\u53ef\u660e\u767d\u4e86\u5427\uff1f\u6211\u4eec\u4e0d\u80fd\u5728\u5c71\u4e1c\u505a\u6848\uff0c\u54ea\u4e00\u70b9\u513f\u4e5f\u6ca1\u9519\uff0c\u53ef\u662f\u9752\u7af9\u5e2e\u8981\u8f6c\u884c\u5e72\u4fdd\u9556\u7684\u3002\u6cf0\u5c71\u5927\u4f1a\u4e2d\uff0c\u6211\u53ef\u6ca1\u7b54\u5e94\u4e0d\u8d70\u9556\u554a\u3002\u201d \u3000\u3000\u6c99\u5be8\u4e3b\u94c1\u9752\u4e86\u8138\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8bb8\u6211\u4eec\u52a8\u624b\uff0c\u7b49\u8d27\u8272\u8fdb\u4e86\u5317\u76f4\u96b6\u5730\u754c\uff0c\u81ea\u5df1\u4fbf\u6765\u4f38\u624b\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u662f\u554a\uff01\u6cf0\u5c71\u5927\u4f1a\u4e0a\u7684\u7ea6\u5b9a\uff0c\u603b\u662f\u8981\u5b88\u7684\uff0c\u4e00\u56de\u5230\u5317\u76f4\u96b6\uff0c\u6211\u4eec\u672c\u4e61\u672c\u571f\uff0c\u505a\u60ef\u4e86\u5f3a\u4eba\uff0c\u4e0d\u597d\u610f\u601d\u518d\u5e72\u9556\u884c\uff0c\u963b\u4e86\u8001\u4e61\u4eec\u7684\u8d22\u8def\u3002\u201d \u3000\u3000\u7fa4\u76d7\u542c\u4ed6\u4e00\u756a\u5f3a\u8f9e\u593a\u7406\u3001\u8f6c\u5f2f\u62b9\u89d2\u7684\u8bf4\u8bdd\uff0c\u8bf4\u7a7f\u4e86\u8fd8\u4e0d\u662f\u60f3\u62a2\u593a\u73cd\u5b9d\uff0c\u65e0\u4e0d\u5927\u6012\uff0c\u6b3a\u4ed6\u4e24\u4eba\u4e00\u4e2a\u8001\u7fc1\uff0c\u4e00\u4e2a\u5e7c\u5973\uff0c\u5f53\u573a\u5c31\u8981\u4e00\u62e5\u800c\u524d\uff0c\u4e71\u5200\u5206\u5c38\u3002 \u3000\u3000\u963f\u4e5d\u5c06\u624b\u4e2d\u4e24\u7247\u7af9\u53f6\u653e\u5230\u5507\u8fb9\uff0c\u5618\u6e9c\u6e9c\u7684\u4e00\u5439\uff0c\u6797\u4e2d\u7a81\u7136\u62e5\u51fa\u6570\u767e\u540d\u5927\u6c49\uff0c\u8863\u670d\u5404\u8272\uff0c\u5934\u4e0a\u5374\u90fd\u63d2\u7740\u4e00\u622a\u4e94\u5bf8\u6765\u957f\u3001\u5e26\u7740\u7af9\u53f6\u7684\u9752\u7af9\u3002 \u3000\u3000\u6c99\u5be8\u4e3b\u4e00\u60ca\uff1a\u201c\u539f\u6765\u8fd9\u8001\u513f\u65e9\u6709\u5e03\u7f6e\u3002\u4ed6\u8fd9\u8bb8\u591a\u4eba\u9a6c\u6765\u5230\u5c71\u4e1c\uff0c\u6211\u4eec\u7684\u54e8\u63a2\u5168\u662f\u8113\u5305\uff0c\u7adf\u6ca1\u63a2\u5230\u4e00\u70b9\u6d88\u606f\u3002\u201d\u6298\u6247\u4e00\u6325\uff0c\u4e03\u5bb6\u5be8\u4e3b\u8fde\u540c\u6076\u864e\u6c9f\u8c2d\u4e8c\u5be8\u4e3b\u7387\u9886\u516b\u5be8\u4eba\u9a6c\uff0c\u5217\u6210\u9635\u52bf\uff0c\u773c\u89c1\u5c31\u662f\u4e00\u573a\u7fa4\u6bb4\u6076\u6597\u3002\u4eba\u6570\u662f\u5c71\u4e1c\u7fa4\u76d7\u5c45\u591a\uff0c\u4f46\u9752\u7af9\u5e2e\u6709\u5907\u800c\u6765\uff0c\u6311\u9009\u7684\u90fd\u662f\u7cbe\u58ee\u6c49\u5b50\uff0c\u4e89\u6597\u8d77\u6765\u4e5f\u672a\u5fc5\u5904\u4e8e\u4e0b\u98ce\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u76f8\u89c6\u800c\u563b\u3002\u9752\u9752\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4e1c\u897f\u8fd8\u6ca1\u5230\u624b\uff0c\u81ea\u4f19\u91cc\u5148\u4e89\u4e86\u8d77\u6765\uff0c\u771f\u662f\u597d\u7b11\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u6765\u4e2a\u6e14\u7fc1\u5f97\u5229\uff0c\u5012\u4e5f\u4e0d\u574f\u3002\u201d\u53ea\u89c1\u5c71\u4e1c\u7fa4\u76d7\u9884\u5907\u7fa4\u6bb4\uff0c\u5374\u7559\u4e0b\u6570\u5341\u4eba\u76d1\u89c6\u8f66\u961f\uff0c\u4ee5\u9632\u4e58\u4e71\u9003\u8d70\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5411\u6d2a\u80dc\u6d77\u62db\u62db\u624b\uff0c\u5f85\u4ed6\u8d70\u8fd1\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u9752\u7af9\u5e2e\u662f\u751a\u4e48\u8def\u9053\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u5317\u76f4\u96b6\u5730\u754c\u5168\u662f\u9752\u7af9\u5e2e\u7684\u52bf\u529b\uff0c\u90a3\u8001\u5934\u7a0b\u9752\u7af9\u5c31\u662f\u5e2e\u4e3b\u3002\u522b\u77a7\u4ed6\u53c8\u7626\u53c8\u8001\uff0c\u529f\u592b\u53ef\u7740\u5b9e\u5389\u5bb3\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u90a3\u5973\u5b69\u5b50\u5462\uff1f\u662f\u4ed6\u5b59\u5973\u513f\u4e48\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u542c\u8bf4\u7a0b\u9752\u7af9\u813e\u6c14\u602a\u5f97\u5389\u5bb3\uff0c\u4e00\u751f\u6ca1\u5a36\u59bb\uff0c\u8be5\u6ca1\u5b59\u5973\u513f\u3002\u96be\u9053\u662f\u5e72\u5b59\u5973\u513f\uff1f\u201d\u9752\u9752\u70b9\u70b9\u5934\u4e0d\u8a00\u8bed\u4e86\uff0c\u89c1\u963f\u4e5d\u795e\u8272\u81ea\u82e5\uff0c\u5e76\u65e0\u60e7\u6015\u4e4b\u8272\uff0c\u5fc3\u60f3\u5979\u5927\u6982\u4e5f\u4f1a\u6b66\u529f\uff0c\u4e14\u770b\u53cc\u65b9\u8c01\u80dc\u8c01\u8d25\u3002 \u3000\u3000\u8fd9\u65f6\u53ea\u542c\u5f97\u9752\u7af9\u5e2e\u91cc\u7af9\u54e8\u8fde\u5439\uff0c\u6570\u767e\u4eba\u5217\u6210\u56db\u961f\u3002\u7a0b\u9752\u7af9\u548c\u963f\u4e5d\u52d2\u9a6c\u56de\u9635\uff0c\u7ad9\u5728\u56db\u961f\u4e4b\u524d\uff0c\u624b\u4e2d\u4ecd\u662f\u4e0d\u62ff\u5175\u5203\u3002 \u3000\u3000\u773c\u89c1\u53cc\u65b9\u5251\u62d4\u5f29\u5f20\uff0c\u5df2\u6210\u4e00\u89e6\u5373\u53d1\u4e4b\u52bf\u3002\u5ffd\u542c\u5357\u65b9\u6765\u8def\u4e0a\u9e3e\u94c3\u54cd\u52a8\uff0c\u4e09\u9a91\u9a6c\u6025\u9a70\u800c\u6765\u3002\u5f53\u5148\u4e00\u4eba\u9ad8\u58f0\u5927\u53eb\uff1a\u201c\u5927\u5bb6\u662f\u597d\u670b\u53cb\uff0c\u77a7\u7740\u5144\u5f1f\u7684\u9762\u5b50\uff0c\u53ef\u522b\u52a8\u624b\uff01\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u548c\u4e8b\u4f6c\u6765\u4e86\uff0c\u53ef\u6709\u4e9b\u4e0d\u5999\u3002\u201d\u53ea\u89c1\u4e09\u9a91\u9a6c\u8d8a\u5954\u8d8a\u8fd1\uff0c\u5f53\u5148\u4e00\u4eba\u662f\u4e2a\u4e94\u5341\u6765\u5c81\u7684\u80d6\u5b50\uff0c\u8eab\u7a7f\u56e2\u82b1\u9526\u7f0e\u957f\u888d\uff0c\u62ff\u7740\u4e00\u652f\u7c97\u5927\u70df\u7ba1\uff0c\u9762\u56e2\u56e2\u7684\u4f3c\u4e4e\u662f\u4e2a\u571f\u8d22\u4e3b\u3002\u540e\u9762\u8ddf\u7740\u4e24\u540d\u7c97\u58ee\u5927\u6c49\u3002 \u3000\u3000\u90a3\u80d6\u5b50\u9a70\u5230\u4e24\u961f\u4eba\u9a6c\u4e2d\u95f4\uff0c\u70df\u7ba1\u4e00\u6446\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5bb6\u5144\u5f1f\uff0c\u6709\u751a\u4e48\u8bdd\u4e0d\u597d\u8bf4\u7684\uff0c\u5374\u5728\u8fd9\u91cc\u52a8\u5200\u52a8\u67aa\uff0c\u4e0d\u6015\u6c5f\u6e56\u4e0a\u670b\u53cb\u4eec\u7b11\u8bdd\u4e48\uff1f\u201d\u6c99\u5be8\u4e3b\u9053\uff1a\u201c\u891a\u5e84\u4e3b\uff0c\u4f60\u5012\u6765\u8bc4\u8bc4\u8fd9\u4e2a\u7406\u770b\u3002\u201d\u5f53\u4e0b\u628a\u9752\u7af9\u5e2e\u8981\u8d8a\u754c\u505a\u6848\u7684\u4e8b\u7b80\u7565\u8bf4\u4e86\u3002\u7a0b\u9752\u7af9\u53ea\u662f\u51b7\u7b11\uff0c\u5e76\u4e0d\u63d2\u5634\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u76f8\u516c\uff0c\u90a3\u6c99\u5be8\u4e3b\u6c99\u5929\u5e7f\u7ef0\u53f7\u9634\u9633\u6247\uff0c\u548c\u8fd9\u891a\u5e84\u4e3b\u891a\u7ea2\u67f3\uff0c\u662f\u5c71\u4e1c\u7701\u5185\u7684\u4e24\u9738\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u5582\uff0c\u65e9\u5148\u4f60\u8bf4\u7684\u5c31\u662f\u8fd9\u4e24\u4e2a\u4eba\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u53c8\u662f\u751a\u4e48\u5e84\u4e3b\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u6c99\u5929\u5e7f\u5f00\u5c71\u7acb\u67dc\uff0c\u5728\u7ebf\u4e0a\u5f00\u6252\u3002\u90a3\u891a\u7ea2\u67f3\u5374\u5b89\u5b89\u7a33\u7a33\u7684\u505a\u5458\u5916\uff0c\u9020\u4e86\u4e00\u5ea7\u5e84\u5b50\uff0c\u524d\u524d\u540e\u540e\u5171\u6709\u5343\u6765\u682a\u67f3\u6811\uff0c\u79f0\u4e3a\u5343\u67f3\u5e84\u3002\u5176\u5b9e\u4ed6\u662f\u4e2a\u72ec\u811a\u5927\u76d7\uff0c\u51fa\u6765\u505a\u4e70\u5356\u5e38\u5e38\u72ec\u6765\u72ec\u5f80\uff0c\u6700\u591a\u53ea\u5e26\u4e24\u4e09\u4e2a\u5e2e\u624b\u3002\u201d\u9752\u9752\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u8ddf\u6211\u77f3\u6881\u4e94\u4e2a\u516c\u516c\u662f\u540c\u884c\uff0c\u505a\u7684\u662f\u4e00\u8def\u751f\u610f\u3002\u5c0f\u59b9\u4ece\u524d\u4e5f\u662f\u4f60\u7684\u884c\u5bb6\uff0c\u8c05\u6765\u4f60\u8fd9\u5927\u80d6\u5b50\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u891a\u7ea2\u67f3\u9053\uff1a\u201c\u7a0b\u5927\u54e5\uff0c\u8fd9\u4ef6\u4e8b\u8bf4\u6765\u662f\u8001\u54e5\u7684\u4e0d\u5bf9\u4e86\u3002\u5f53\u5e74\u6cf0\u5c71\u5927\u4f1a\uff0c\u627f\u5404\u4f4d\u77a7\u5f97\u8d77\uff0c\u4e5f\u9080\u5144\u5f1f\u4e0e\u4f1a\u3002\u5927\u5bb6\u8bf4\u5b9a\u4e0d\u80fd\u8d8a\u754c\u505a\u6848\u5440\uff01\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6211\u4eec\u53c8\u4e0d\u662f\u6765\u505a\u6848\uff0c\u9752\u7af9\u5e2e\u4e0d\u8fc7\u73a9\u73a9\u7968\uff0c\u6539\u884c\u8d70\u4e00\u8d9f\u9556\u3002\u5927\u660e\u671d\u7684\u738b\u6cd5\uff0c\u53ef\u6ca1\u4e0d\u8bb8\u4eba\u8d70\u9556\u8fd9\u4e00\u6761\u554a\u3002\u891a\u8001\u54e5\uff0c\u4f60\u8baf\u606f\u4e5f\u771f\u7075\u901a\uff0c\u54ea\u91cc\u6709\u6cb9\u6c34\uff0c\u4f60\u7684\u70df\u888b\u513f\u5c31\u4f38\u5230\u4e86\u90a3\u91cc\u6765\u3002\u201d \u3000\u3000\u891a\u7ea2\u67f3\u5475\u5475\u5927\u7b11\uff0c\u5411\u8eab\u540e\u4e24\u540d\u6c49\u5b50\u4e00\u6307\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u662f\u6dee\u9634\u53cc\u6770\uff0c\u524d\u51e0\u5929\u5df4\u5df4\u7684\u8d76\u5230\u6211\u5e84\u4e0a\u6765\uff0c\u8bf4\u6709\u4e00\u4efd\u8d22\u559c\u8981\u5949\u9001\u7ed9\u6211\u3002\u5144\u5f1f\u8eab\u5b50\u80d6\u4e86\uff0c\u53c8\u6015\u70ed\uff0c\u672c\u6765\u61d2\u5f97\u52a8\uff0c\u53ef\u662f\u4ed6\u54e5\u513f\u4fe9\u5341\u5206\u70ed\u5fc3\uff0c\u5144\u5f1f\u5374\u4e0d\u8fc7\u597d\u610f\uff0c\u53ea\u5f97\u51fa\u6765\u77a7\u77a7\u3002\u54ea\u77e5\u9047\u4e0a\u4e86\u5404\u4f4d\u90fd\u5728\u8fd9\u91cc\uff0c\u771f\u662f\u70ed\u95f9\u5f97\u7d27\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u90fd\u9053\uff1a\u201c\u597d\u54c7\uff0c\u53c8\u591a\u4e86\u4e09\u53ea\u591c\u732b\u5b50\u3002\u201d \u3000\u3000\u6c99\u5929\u5e7f\u5fc3\u60f3\uff1a\u201c\u8fd9\u59d3\u891a\u7684\u6b66\u529f\u9ad8\u5f3a\uff0c\u54b1\u4eec\u7834\u7740\u5206\u4e00\u4efd\u7ed9\u4ed6\uff0c\u4e0d\u5982\u8ddf\u4ed6\u8054\u624b\uff0c\u4e00\u8d77\u5bf9\u4ed8\u9752\u7af9\u5e2e\u3002\u201d\u8bf4\u9053\uff1a\u201c\u891a\u5e84\u4e3b\u662f\u5c71\u4e1c\u5730\u754c\u4e0a\u7684\u4eba\uff0c\u8981\u5206\u4e00\u4efd\uff0c\u6211\u4eec\u6ca1\u5f97\u8bf4\u7684\u3002\u53ef\u662f\u522b\u7701\u7684\u4eba\u6a2a\u6765\u63d2\u624b\uff0c\u8fd9\u6b21\u8ba9\u4e86\uff0c\u4e0b\u6b21\u5c71\u4e1c\u7684\u5144\u5f1f\u8fd8\u6709\u996d\u5403\u4e48\uff1f\u201d\u891a\u7ea2\u67f3\u9053\uff1a\u201c\u7a0b\u5927\u54e5\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6211\u4eec\u96be\u5f97\u8d70\u4e00\u8d9f\u9556\uff0c\u6c99\u5be8\u4e3b\u4e00\u5b9a\u4e0d\u7ed9\u9762\u5b50\uff0c\u90a3\u6709\u751a\u4e48\u6cd5\u5b50\uff1f\u5927\u5bb6\u723d\u723d\u5feb\u5feb\uff0c\u5200\u67aa\u4e0a\u89c1\u8f93\u8d62\u5427\u3002\u201d\u891a\u7ea2\u67f3\u8f6c\u5934\u9053\uff1a\u201c\u6c99\u8001\u5f1f\u4f60\u8bf4\u5462\uff1f\u201d\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u54b1\u4eec\u5c71\u4e1c\u597d\u6c49\uff0c\u4e0d\u80fd\u8ba9\u4eba\u5bb6\u4e0a\u95e8\u6b3a\u4fae\u3002\u201d\u8fd9\u8bdd\u660e\u660e\u662f\u628a\u891a\u7ea2\u67f3\u7ed9\u62c9\u626f\u5728\u4e00\u8d77\u4e86\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u54b1\u4eec\u5927\u4f19\u9f50\u4e0a\u5462\uff0c\u8fd8\u662f\u4e00\u5bf9\u4e00\u7684\u8f83\u91cf\uff1f\u6c99\u5be8\u4e3b\u5212\u4e0b\u9053\u513f\u6765\uff0c\u5728\u4e0b\u65e0\u4e0d\u4ece\u547d\u3002\u201d\u6c99\u5929\u5e7f\u9634\u9633\u6247\u500f\u5730\u5f20\u5f00\uff0c\u563f\u563f\u8fde\u58f0\uff0c\u95ee\u891a\u7ea2\u67f3\u9053\uff1a\u201c\u891a\u5e84\u4e3b\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u891a\u7ea2\u67f3\u81ea\u5f97\u6dee\u9634\u53cc\u6770\u62a5\u4fe1\uff0c\u672c\u60f3\u72ec\u541e\u73cd\u5b9d\uff0c\u4f46\u5f97\u8baf\u8f83\u8fdf\uff0c\u5df2\u7136\u6162\u4e86\u4e00\u6b65\uff0c\u4ed6\u4eba\u624b\u5355\u8584\uff0c\u8fd9\u65f6\u53ea\u60f3\u539a\u539a\u7684\u5206\u5f97\u4e00\u4efd\u3002\u4ed6\u77e5\u9752\u7af9\u5e2e\u4e2d\u597d\u624b\u4e0d\u5c11\uff0c\u5e2e\u4e3b\u7a0b\u9752\u7af9\u4eab\u540d\u591a\u5e74\uff0c\u51b3\u975e\u5eb8\u624b\uff0c\u4e5f\u4e0d\u613f\u5f00\u7f6a\u4e8e\u4ed6\uff0c\u4fbf\u9053\uff1a\u201c\u65e2\u7136\u8fd9\u6837\uff0c\u6bd4\u5212\u4e00\u4e0b\u662f\u514d\u4e0d\u4e86\u7684\u5566\u3002 \u3000\u3000\u7fa4\u6bb4\u591a\u4f24\u4eba\u547d\uff0c\u5927\u5bb6\u672c\u6765\u65e0\u51a4\u65e0\u4ec7\uff0c\u53c8\u4f55\u5fc5\u4f24\u4e86\u548c\u6c14\uff1f\u8ba9\u5144\u5f1f\u51fa\u4e2a\u4e3b\u610f\u600e\u6837\uff1f\u201d\u7a0b\u9752\u7af9\u548c\u6c99\u5929\u5e7f\u9f50\u58f0\u9053\uff1a\u201c\u891a\u5e84\u4e3b\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u891a\u7ea2\u67f3\u63d0\u8d77\u70df\u888b\uff0c\u5411\u5341\u8f86\u5927\u8f66\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u5341\u53e3\u7bb1\u5b50\u3002\u54b1\u4eec\u5c71\u4e1c\u5317\u76f4\u96b6\u5404\u6d3e\u5341\u4e2a\u4eba\uff0c\u4e00\u5171\u6bd4\u8bd5\u5341\u573a\uff0c\u70b9\u5230\u4e3a\u6b62\uff0c\u4e0d\u53ef\u4f24\u5bb3\u4eba\u547d\u3002\u80dc\u4e00\u573a\uff0c\u53d6\u4e00\u53e3\u7bb1\u5b50\uff0c\u6700\u662f\u516c\u5e73\u4e0d\u8fc7\u3002\u54b1\u4eec\u5c31\u7b97\u95f2\u7740\u65e0\u4e8b\uff0c\u7ec3\u7ec3\u6b66\u529f\uff0c\u5370\u8bc1\u89c2\u6469\u3002\u5f97\u5230\u7bb1\u5b50\uff0c\u90a3\u662f\u5f69\u5934\u3002\u5f97\u4e0d\u7740\uff0c\u53cd\u6b63\u4e0d\u662f\u81ea\u5df1\u4e1c\u897f\uff0c\u4e5f\u4e0d\u4f24\u813e\u80c3\u3002\u4e24\u4f4d\u77a7\u7740\u600e\u6837\uff1f\u201d \u3000\u3000\u7a0b\u9752\u7af9\u89c9\u5f97\u6b64\u6cd5\u751a\u4f73\uff0c\u9996\u5148\u53eb\u597d\u3002\u6c99\u5be8\u4e3b\u5fc3\u4e2d\u5bf9\u7a0b\u9752\u7af9\u9887\u4e3a\u5fcc\u60ee\uff0c\u77a7\u4e86\u4ed6\u9752\u7af9\u5e2e\u6709\u5907\u800c\u6765\u7684\u58f0\u52bf\uff0c\u90e8\u52d2\u4e25\u6574\uff0c\u8fdc\u80dc\u4e8e\u5c71\u4e1c\u7fa4\u76d7\u7684\u4e4c\u5408\u4e4b\u4f17\uff0c\u82e5\u662f\u51b3\u6218\uff0c\u5b9e\u65e0\u5fc5\u80dc\u628a\u63e1\uff0c\u53c8\u60f3\uff1a\u201c\u6211\u53eb\u6bcf\u5be8\u6d3e\u4eba\u4e0a\u9635\uff0c\u80dc\u4e86\u662f\u4ed6\u4eec\u672c\u4e8b\uff0c\u90a3\u672c\u662f\u8981\u5206\u7ed9\u4ed6\u4eec\u7684\uff0c\u8d25\u4e86\u4e5f\u8ddf\u672c\u5be8\u65e0\u5173\u3002\u6211\u548c\u8c2d\u8001\u4e8c\u51fa\u9635\uff0c\u90a3\u662f\u51b3\u4e0d\u4f1a\u8d25\u7684\uff0c\u603b\u53ef\u593a\u5230\u4e24\u7bb1\u3002\u53e6\u4e00\u7bb1\u8ba9\u891a\u5e84\u4e3b\u81ea\u5df1\u53bb\u53d6\u3002\u201d\u5f53\u4e0b\u4e5f\u7b54\u5141\u4e86\u3002 \u3000\u3000\u53cc\u65b9\u6536\u961f\u5546\u91cf\u4eba\u9009\u3002\u891a\u7ea2\u67f3\u547d\u4eba\u5728\u94c1\u7bb1\u4e0a\u7528\u9ec4\u571f\u5199\u4e0a\u4e86\u7532\u4e59\u4e19\u4e01\u620a\u5df1\u5e9a\u8f9b\u58ec\u7678\u5341\u4e2a\u5927\u5b57\u53f7\u7801\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u7531\u5f97\u7fa4\u76d7\u80e1\u641e\uff0c\u6beb\u4e0d\u7406\u4f1a\u3002\u7a0b\u9752\u7af9\u89c1\u4e24\u4eba\u5e76\u65e0\u754f\u60e7\u4e4b\u8272\uff0c\u5012\u6709\u4e9b\u5947\u602a\uff0c\u4e0d\u7531\u5f97\u5411\u4ed6\u4eec\u671b\u4e86\u51e0\u773c\u3002\u7fa4\u76d7\u56f4\u6210\u4e86\u4e00\u4e2a\u5927\u5708\u5b50\uff0c\u891a\u7ea2\u67f3\u5728\u4e2d\u95f4\u4f5c\u516c\u8bc1\u3002 \u3000\u3000\u7b2c\u4e00\u9635\u5c71\u4e1c\u7fa4\u76d7\u5148\u6d3e\u4eba\u51fa\u9635\uff0c\u53cc\u65b9\u6bd4\u62f3\u3002\u4e24\u4eba\u90fd\u8eab\u6750\u7c97\u58ee\uff0c\u8182\u529b\u751a\u5927\uff0c\u7830\u7830\u84ec\u84ec\u7684\u6253\u4e86\u597d\u4e00\u9635\u3002\u5317\u76f4\u96b6\u90a3\u4eba\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u811a\u4e0b\u88ab\u5bf9\u65b9\u4e00\u52fe\uff0c\u6251\u5730\u5012\u4e86\uff0c\u7ad9\u8d77\u8eab\u6765\u5f85\u8981\u518d\u6253\uff0c\u891a\u7ea2\u67f3\u6447\u624b\u6b62\u4f4f\uff0c\u5728\u201c\u7532\u201d\u5b57\u53f7\u7684\u94c1\u7bb1\u4e0a\u5199\u4e86\u4e2a\u201c\u9c81\u201d\u5b57\u3002\u5c71\u4e1c\u80dc\u4e86\u7b2c\u4e00\u9635\uff0c\u7fa4\u76d7\u6b22\u58f0\u96f7\u52a8\u3002 \u3000\u3000\u7b2c\u4e8c\u9635\u5317\u76f4\u96b6\u6d3e\u4eba\u51fa\u6765\u3002\u6c99\u5929\u5e7f\u8bc6\u5f97\u4ed6\u662f\u94c1\u6c99\u638c\u597d\u624b\uff0c\u4f46\u5df1\u65b9\u8c2d\u4e8c\u5be8\u4e3b\u8fd8\u80dc\u4ed6\u4e00\u7b79\uff0c\u5fc3\u60f3\u673a\u4e0d\u53ef\u5931\uff0c\u5fd9\u53eb\u8c2d\u4e8c\u5be8\u4e3b\u4e0a\u9635\u3002\u4e24\u4eba\u638c\u6cd5\u5bb6\u6570\u76f8\u5dee\u4e0d\u8fdc\uff0c\u8c2d\u4e8c\u5be8\u4e3b\u529f\u529b\u8f83\u6df1\uff0c\u62c6\u4e86\u6570\u5341\u62db\uff0c\u4e00\u638c\u6253\u5728\u5bf9\u65b9\u81c2\u4e0a\uff0c\u90a3\u4eba\u81c2\u8180\u518d\u4e5f\u4e3e\u4e0d\u8d77\u6765\uff0c\u5c71\u4e1c\u53c8\u80dc\u4e86\u4e00\u9635\u3002 \u3000\u3000\u5c71\u4e1c\u7fa4\u76d7\u6b63\u81ea\u5f97\u610f\uff0c\u54ea\u77e5\u7b2c\u4e09\u3001\u7b2c\u56db\u3001\u7b2c\u4e94\u3001\u7b2c\u516d\u56db\u9635\u5168\u8f93\u4e86\uff0c\u56db\u53ea\u94c1\u7bb1\u4e0a\u90e8\u5199\u4e86\u4e00\u4e2a\u201c\u76f4\u201d\u5b57\u3002\u7b2c\u4e03\u9635\u6bd4\u5175\u5203\uff0c\u6740\u8c79\u5c97\u5be8\u4e3b\u63d0\u4e86\u4e00\u67c4\u6cfc\u98ce\u4e5d\u73af\u5200\u4e0a\u9635\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u679c\u7136\u4e00\u6218\u6210\u529f\uff0c\u628a\u5bf9\u65b9\u7684\u624b\u81c2\u780d\u4f24\u4e86\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u5fc3\u60f3\u773c\u524d\u53ea\u5269\u4e0b\u4e09\u53ea\u94c1\u7bb1\uff0c\u518d\u4e0d\u51fa\u9a6c\uff0c\u7ed9\u53cc\u65b9\u5206\u5b8c\u4e86\uff0c\u81ea\u5df1\u5c82\u975e\u843d\u7a7a\uff1f\u7b2c\u516b\u9635\u7531\u9752\u7af9\u5e2e\u6d3e\u4eba\u5148\u51fa\uff0c\u81ea\u5df1\u4fbf\u4f5c\u4e3a\u9c81\u65b9\u4eba\u9a6c\u51fa\u6218\uff0c\u62ff\u5230\u4e00\u53ea\u94c1\u7bb1\u518d\u8bf4\uff0c\u4e8e\u662f\u5bf9\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u6c99\u8001\u5f1f\uff0c\u5bf9\u65b9\u8d8a\u6765\u8d8a\u5389\u5bb3\u4e86\uff0c\u4e0b\u4e00\u9635\u6211\u7ed9\u4f60\u63a5\u4e86\u5427\u3002\u201d\u6c99\u5929\u5e7f\u77e5\u4ed6\u7edd\u4e0d\u80fd\u7a7a\u624b\u800c\u5f52\uff0c\u5c31\u9053\uff1a\u201c\u5168\u4ed7\u891a\u5e84\u4e3b\u7ed9\u54b1\u4eec\u5c71\u4e1c\u4e89\u9762\u5b50\u3002\u201d \u3000\u3000\u53ea\u89c1\u5bf9\u65b9\u961f\u4e2d\u51fa\u6765\u4e00\u4eba\uff0c\u891a\u7ea2\u67f3\u4e0d\u89c9\u4e00\u5446\u3002 \u3000\u3000\u539f\u6765\u51fa\u6765\u7684\u7adf\u662f\u90a3\u5c11\u5973\u963f\u4e5d\uff0c\u5979\u4e0d\u8fc7\u5341\u4e94\u516d\u5c81\u5e74\u7eaa\uff0c\u624b\u91cc\u4e5f\u6ca1\u5175\u5203\uff0c\u53ea\u63e1\u7740\u4e24\u6839\u7ec6\u7ec6\u7684\u7af9\u6746\u3002\u891a\u7ea2\u67f3\u5fc3\u60f3\u6211\u662f\u6b66\u6797\u5927\u8c6a\uff0c\u5c82\u80fd\u81ea\u5931\u8eab\u5206\uff0c\u53bb\u8ddf\u8fd9\u5c0f\u59d1\u5a18\u53ae\u62da\uff0c\u672c\u5df2\u8de8\u51fa\u6570\u6b65\uff0c\u5f53\u4e0b\u53c8\u9000\u4e86\u56de\u6765\uff0c\u5bf9\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u53e6\u5916\u6d3e\u4eba\u5427\u3002\u4e0b\u4e00\u9635\u6211\u63a5\u3002\u201d\u6c99\u5929\u5e7f\u77e5\u4ed6\u4e0d\u613f\u4e0e\u8fd9\u5973\u5b69\u513f\u4ea4\u624b\uff0c\u90a3\u662f\u80dc\u4e4b\u4e0d\u6b66\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u5144\u5f1f\u5174\u81f4\u597d\uff0c\u966a\u8fd9\u5c0f\u599e\u800d\u800d\u3002\u201d \u3000\u3000\u7fa4\u76d7\u4e2d\u7a9c\u51fa\u4e00\u4eba\uff0c\u8eab\u9ad8\u8180\u9614\uff0c\u9762\u76ae\u767d\u51c0\uff0c\u624b\u63d0\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u6b63\u662f\u5c71\u4e1c\u516b\u5be8\u4e2d\u9ec4\u77f3\u5761\u5be8\u4e3b\u79e6\u680b\u3002\u8fd9\u4eba\u98ce\u6d41\u81ea\u8d4f\uff0c\u89c1\u90a3\u5c11\u5973\u7f8e\u8c8c\u7edd\u4f26\uff0c\u867d\u7136\u5e74\u5e7c\uff0c\u4f46\u8273\u4e3d\u5f02\u5e38\uff0c\u4e0d\u7981\u5fc3\u75d2\u8270\u6414\uff0c\u542c\u5f97\u6c99\u5929\u5e7f\u53eb\u5524\uff0c\u5fd9\u5e94\u58f0\u800c\u51fa\u3002\u6c99\u5929\u5e7f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4e9b\u4eba\u4e2d\uff0c\u4e5f\u53ea\u6709\u4f60\u8001\u5f1f\u914d\u5f97\u4e0a\u3002\u201d \u3000\u3000\u79e6\u680b\u6545\u610f\u5356\u5f04\uff0c\u9661\u7136\u8dc3\u8d77\uff0c\u8f7b\u98d8\u98d8\u7684\u843d\u5728\u963f\u4e5d\u9762\u524d\uff0c\u4ed6\u672c\u60f3\u70ab\u8000\u4e00\u4e0b\u8f7b\u529f\uff0c\u518d\u4ea4\u4ee3\u51e0\u53e5\u573a\u9762\u8bdd\uff0c\u54ea\u77e5\u8db3\u521a\u7740\u5730\uff0c\u7a81\u89c1\u9752\u5f71\u4e00\u6643\uff0c\u4e00\u6839\u9752\u7af9\u6746\u5df2\u523a\u5411\u80f8\u53e3\u8981\u7a74\uff0c\u6746\u6765\u5982\u98ce\uff0c\u8fc5\u6377\u4e4b\u6781\u3002\u79e6\u680b\u4f7f\u5224\u5b98\u7b14\uff0c\u81ea\u7136\u719f\u6089\u7a74\u9053\uff0c\u8fd9\u4e00\u4e0b\u5927\u5403\u4e00\u60ca\uff0c\u5de6\u7b14\u4e00\u67b6\uff0c\u773c\u89c1\u5bf9\u65b9\u5de6\u624b\u7af9\u6746\u53c8\u5230\uff0c\u767e\u5fd9\u4e2d\u4e00\u4e2a\u6253\u6eda\uff0c\u8fd9\u624d\u907f\u5f00\uff0c\u4f46\u5df2\u6ee1\u5934\u7070\u571f\uff0c\u4e00\u8eab\u51b7\u6c57\u3002\u5c71\u4e1c\u7fa4\u76d7\u89c1\u963f\u4e5d\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u6b66\u529f\u7adf\u5982\u6b64\u4e86\u5f97\uff0c\u90fd\u611f\u60ca\u8be7\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u4e5f\u5927\u51fa\u610f\u5916\uff0c\u4e92\u76f8\u5bf9\u671b\u4e86\u51e0\u773c\u3002 \u3000\u3000\u53ea\u89c1\u963f\u4e5d\u624b\u4e2d\u7af9\u6746\u4f7f\u7684\u662f\u53cc\u67aa\u67aa\u6cd5\uff0c\u7af9\u6746\u6027\u67d4\uff0c\u76d8\u6253\u6311\u70b9\u4e4b\u4e2d\uff0c\u53c8\u542b\u7740\u8f6f\u97ad\u4e0e\u5927\u6746\u5b50\u7684\u62db\u6570\uff0c\u767e\u5fd9\u4e2d\u8fd8\u627e\u654c\u4eba\u7a74\u9053\u3002 \u3000\u3000\u79e6\u680b\u5fc3\u60f3\u8fde\u4e00\u4e2a\u5c0f\u5c0f\u5973\u5a03\u5b50\u4e5f\u62fe\u593a\u4e0d\u4e0b\uff0c\u54ea\u91cc\u8fd8\u80fd\u5728\u5c71\u4e1c\u9053\u4e0a\u7acb\u8db3\uff1f\u5fc3\u4e2d\u7126\u8e81\uff0c\u5224\u5b98\u53cc\u7b14\u6108\u4f7f\u6108\u7d27\u3002\u963f\u4e5d\u7a81\u7136\u5de6\u624b\u6746\u5728\u5730\u4e0b\u4e00\u6491\uff0c\u8eab\u5b50\u98de\u8d77\uff0c\u53f3\u624b\u7af9\u6746\u5728\u5730\u4e0b\u4e00\u6491\uff0c\u53c8\u518d\u8dc3\u8d77\uff0c\u5de6\u624b\u6746\u5c45\u9ad8\u4e34\u4e0b\uff0c\u4fef\u51fb\u654c\u4eba\u3002\u79e6\u680b\u4e0d\u77e5\u5982\u4f55\u62b5\u5fa1\uff0c\u4e0d\u4f4f\u5012\u9000\uff0c\u4e00\u4e2a\u758f\u795e\uff0c\u88ab\u963f\u4e5d\u4e00\u6746\u70b9\u5728\u201c\u80a9\u8d1e\u7a74\u201d\u4e0a\uff0c\u5de6\u81c2\u4e00\u9ebb\uff0c\u5224\u5b98\u7b14\u843d\u5730\uff0c\u6ee1\u8138\u901a\u7ea2\uff0c\u8d25\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u963f\u4e5d\u6b63\u8981\u9000\u4e0b\uff0c\u891a\u7ea2\u67f3\u5927\u8e0f\u6b65\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u59d1\u5a18\u795e\u6280\uff0c\u679c\u771f\u662f\u5f3a\u5c06\u624b\u4e0b\u65e0\u5f31\u5175\u3002\u5f85\u6211\u9886\u6559\u51e0\u62db\u5982\u4f55\uff1f\u201d\u963f\u4e5d\u7b11\u9053\uff1a\u201c\u6211\u6b63\u73a9\u5f97\u8fd8\u6ca1\u591f\uff0c\u891a\u4f2f\u4f2f\u80af\u8d50\u6559\uff0c\u90a3\u662f\u518d\u597d\u6ca1\u6709\u3002\u891a\u4f2f\u4f2f\u4f7f\u751a\u4e48\u5175\u5203\uff1f\u201d\u891a\u7ea2\u67f3\u7b11\u9053\uff1a\u201c\u5927\u4eba\u8ddf\u5c0f\u5b69\u513f\u73a9\u800d\uff0c\u8fd8\u80fd\u7528\u5175\u5203\u5417\uff1f\u5c31\u662f\u7a7a\u624b\u63a5\u7740\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u5728\u4e00\u65c1\u89c2\u6218\uff0c\u5fc3\u60f3\u8fd9\u5c0f\u5973\u5b69\u513f\u5df2\u5982\u6b64\u5389\u5bb3\uff0c\u4e0b\u9762\u4e24\u9635\uff0c\u5bf9\u65b9\u4e00\u5b9a\u66f4\u6709\u9ad8\u624b\uff0c\u591c\u957f\u68a6\u591a\uff0c\u4e0d\u5982\u62e6\u4f4f\u5979\u6253\u4e00\u9635\uff0c\u5148\u8d62\u4e00\u53ea\u94c1\u7bb1\u518d\u8bf4\u3002\u9752\u7af9\u5e2e\u4f17\u4eba\u89c9\u5f97\u963f\u4e5d\u8fde\u6597\u4e24\u9635\uff0c\u672a\u514d\u8f9b\u82e6\uff0c\u65e9\u6709\u4e09\u4eba\u8dc3\u51fa\uff0c\u5747\u8981\u63a5\u66ff\u3002\u963f\u4e5d\u5e74\u5c11\u597d\u80dc\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7b54\u5e94\u891a\u4f2f\u4f2f\u5566\u3002\u201d\u90a3\u4e09\u4eba\u53ea\u5f97\u9000\u4e0b\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u5411\u963f\u4e5d\u62db\u62db\u624b\uff0c\u963f\u4e5d\u7eb5\u8eab\u8fc7\u53bb\u3002\u7a0b\u9752\u7af9\u5728\u5979\u8033\u8fb9\u5631\u5490\u4e86\u51e0\u53e5\u3002\u963f\u4e5d\u70b9\u5934\u7b54\u5e94\uff0c\u56de\u8fdb\u573a\u5b50\uff0c\u5f2f\u4e86\u5f2f\u8170\u884c\u4e2a\u793c\uff0c\u53cc\u6746\u98de\u52a8\uff0c\u62a4\u4f4f\u5168\u8eab\uff0c\u5374\u4e0d\u8fdb\u51fb\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u811a\u6b65\u8fdf\u7f13\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u8d70\u8fd1\uff0c\u7a81\u7136\u5de6\u638c\u6253\u51fa\uff0c\u653b\u5979\u53f3\u80a9\u3002\u963f\u4e5d\u53cc\u6746\u4e00\u6491\uff0c\u98de\u8eab\u907f\u5f00\uff0c\u624b\u56de\u6746\u51fa\uff0c\u53f3\u6746\u65b9\u53d1\uff0c\u5de6\u6746\u968f\u81f3\uff0c\u653b\u52bf\u72b9\u5982\u72c2\u98ce\u9aa4\u96e8\uff0c\u4e00\u7247\u9752\u5f71\u4e2d\u4e00\u6746\u5df2\u6233\u8fdb\u891a\u7ea2\u67f3\u80a9\u80db\u9aa8\u4e0b\u3002\u9752\u7af9\u5e2e\u5e2e\u4f17\u9f50\u58f0\u559d\u91c7\u3002\u891a\u7ea2\u67f3\u5374\u6d51\u82e5\u4e0d\u89c9\uff0c\u8138\u4e0a\u7684\u6731\u7802\u4e4b\u8272\u76f4\u7ea2\u5230\u8116\u5b50\u91cc\uff0c\u4ecd\u662f\u4e00\u6b65\u4e00\u6b65\u7684\u653b\u5c06\u8fc7\u53bb\u3002\u963f\u4e5d\u8eab\u624b\u8f7b\u7075\uff0c\u98d8\u8361\u6765\u53bb\uff0c\u53ea\u8981\u7a0d\u6709\u7a7a\u9699\uff0c\u4fbf\u662f\u4e00\u9635\u6025\u653b\u3002\u891a\u7ea2\u67f3\u8eab\u5b50\u7c97\u58ee\uff0c\u53ea\u662f\u62a4\u4f4f\u8981\u7a74\uff0c\u56db\u80a2\u4e0e\u80a9\u80cc\u53d7\u4e86\u51e0\u6746\uff0c\u7adf\u6f2b\u4e0d\u5728\u610f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5bf9\u9752\u9752\u9053\uff1a\u201c\u8fd9\u4eba\u5e74\u7eaa\u4e00\u5927\u628a\uff0c\u5374\u53bb\u6b3a\u4fae\u5c0f\u59d1\u5a18\u3002\u77a7\u7740\uff0c\u8fd9\u5c31\u8981\u4e0b\u6bd2\u624b\u5566\u3002\u201d\u9752\u9752\u6025\u9053\uff1a\u201c\u6211\u53bb\u6551\u5979\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4e24\u4e2a\u90fd\u662f\u8981\u593a\u54b1\u4eec\u8d22\u7269\u7684\uff0c\u6551\u751a\u4e48\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u8fd9\u5c0f\u59d1\u5a18\u602a\u8ba8\u4eba\u559c\u6b22\u7684\uff0c\u6551\u4e86\u518d\u8bf4\u3002\u5927\u54e5\uff0c\u4f60\u51fa\u624b\u5427\u3002\u201d\u8881\u627f\u5fd7\u4e00\u7b11\uff0c\u70b9\u70b9\u5934\u3002 \u3000\u3000\u573a\u4e2d\u4e24\u4eba\u8d8a\u6253\u8d8a\u662f\u6fc0\u70c8\u3002\u891a\u7ea2\u67f3\u901a\u7ea2\u7684\u8138\u4e0a\u4f3c\u4e4e\u8981\u6ef4\u51fa\u8840\u6765\uff0c\u518d\u8fc7\u4e00\u9635\uff0c\u624b\u81c2\u4e0a\u4e5f\u6162\u6162\u7ea2\u4e86\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u7b49\u4ed6\u624b\u638c\u4e00\u7ea2\uff0c\u90a3\u5c0f\u59d1\u5a18\u5c31\u8981\u7cdf\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u891a\u7ea2\u67f3\u8eab\u4e0a\u53c8\u8fde\u4e2d\u6570\u6746\uff0c\u4ed6\u4e00\u8a00\u4e0d\u53d1\uff0c\u4e00\u638c\u4e00\u638c\u7684\u7f13\u7f13\u53d1\u51fa\uff0c\u53c8\u7a33\u53c8\u72e0\u3002\u963f\u4e5d\u6e10\u89c9\u4e0d\u5999\uff0c\u88ab\u5bf9\u65b9\u638c\u98ce\u903c\u5f97\u5a07\u5598\u8fde\u8fde\uff0c\u8eab\u6cd5\u5df2\u4e0d\u5982\u5148\u524d\u8fc5\u6377\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u53eb\u9053\uff1a\u201c\u963f\u4e5d\uff0c\u56de\u6765\u3002\u891a\u4f2f\u4f2f\u8d62\u4e86\u3002\u201d\u963f\u4e5d\u8f6c\u8eab\u8981\u9000\uff0c\u891a\u7ea2\u67f3\u5374\u4e0d\u8ba9\u5979\u8d70\u4e86\uff0c\u559d\u9053\uff1a\u201c\u6233\u4e86\u6211\u8fd9\u8bb8\u591a\u6746\uff0c\u8fd8\u60f3\u8d70\u5417\uff1f\u201d\u51fa\u624b\u867d\u6162\uff0c\u963f\u4e5d\u5374\u603b\u662f\u8131\u4e0d\u51fa\u4ed6\u638c\u98ce\u7684\u7b3c\u7f69\u4e4b\u4e0b\u3002 \u3000\u3000\u773c\u89c1\u4ed6\u624b\u638c\u8d8a\u6765\u8d8a\u7ea2\uff0c\u7a0b\u9752\u7af9\u4ece\u90e8\u5c5e\u624b\u4e2d\u63a5\u8fc7\u4e24\u6761\u7af9\u6746\uff0c\u7eb5\u8eab\u800c\u524d\uff0c\u5728\u891a\u7ea2\u67f3\u548c\u963f\u4e5d\u4e4b\u95f4\u865a\u523a\u8fc7\u53bb\uff0c\u4ece\u4e2d\u4e00\u9694\uff0c\u53eb\u9053\uff1a\u201c\u80dc\u8d1f\u5df2\u5206\u3002\u891a\u5144\u8bf4\u8fc7\u70b9\u5230\u4e3a\u6b62\uff0c\u8fd8\u8bf7\u638c\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u6c99\u5929\u5e7f\u53eb\u9053\uff1a\u201c\u4e24\u4e2a\u6253\u4e00\u4e2a\u5417\uff1f\u201d\u63d0\u8d77\u94c1\u6247\uff0c\u6b3a\u8eab\u800c\u8fdb\uff0c\u5f84\u70b9\u7a0b\u9752\u7af9\u7684\u7a74\u9053\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u6325\u6746\u683c\u5f00\u3002\u891a\u7ea2\u67f3\u51b7\u7b11\u9053\uff1a\u201c\u70b9\u5230\u4e3a\u6b62\uff0c\u56fa\u7136\u4e0d\u9519\uff0c\u563f\u563f\uff0c\u53ef\u662f\u8fd8\u6ca1\u70b9\u5230\u5462\u3002\u201d\u52a0\u7d27\u50ac\u52a8\u638c\u529b\u3002\u7a0b\u9752\u7af9\u60f3\u6551\u963f\u4e5d\uff0c\u4f46\u88ab\u6c99\u5929\u5e7f\u7f20\u4f4f\u4e86\u65e0\u6cd5\u5206\u8eab\uff0c\u53ea\u5f97\u51dd\u795e\u63a5\u6218\u3002\u963f\u4e5d\u6ee1\u5934\u5927\u6c57\uff0c\u5de6\u53f3\u652f\u6491\uff0c\u773c\u89c1\u4fbf\u8981\u4f24\u4e8e\u891a\u7ea2\u67f3\u638c\u5e95\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5ffd\u7136\u5927\u53eb\uff1a\u201c\u554a\u54df\uff0c\u554a\u54df\uff0c\u4e0d\u5f97\u4e86\u3002\u6551\u547d\u5440\uff0c\u6551\u547d\u5440\uff01\u201d\u9a91\u7740\u9a6c\u76f4\u51b2\u8fdb\u573a\u4e2d\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u4e0e\u6c99\u5929\u5e7f\u500f\u5730\u5f80\u4e24\u65c1\u8df3\u5f00\u3002\u53ea\u89c1\u8881\u627f\u5fd7\u5728\u9a6c\u4e0a\u6447\u6765\u6643\u53bb\uff0c\u53cc\u624b\u62b1\u4f4f\u9a6c\u9888\uff0c\u5ffd\u7136\u7ffb\u5230\u4e86\u9a6c\u809a\u4e4b\u4e0b\uff0c\u8ddf\u7740\u53c8\u7ffb\u4e86\u4e0a\u6765\uff0c\u53cc\u811a\u4e71\u6491\uff0c\u72fc\u72c8\u4e4b\u6781\u3002\u90a3\u9a6c\u76f4\u51b2\u5411\u963f\u4e5d\u8eab\u65c1\uff0c\u5728\u5979\u548c\u891a\u7ea2\u67f3\u4e4b\u95f4\u7ad9\u5b9a\u4e86\u3002\u8881\u627f\u5fd7\u6c14\u5598\u5598\u7684\u722c\u4e0b\u9a6c\u6765\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u53c8\u9669\u9669\u8dcc\u5012\uff0c\u5927\u53eb\uff1a\u201c\u5371\u4e4e\u9669\u54c9\uff0c\u771f\u662f\u6b7b\u91cc\u9003\u751f\u3002\u755c\u751f\uff0c\u755c\u751f\uff0c\u4f60\u8fd9\u4e0d\u662f\u8981\u4e86\u5927\u7237\u7684\u547d\u4e48\uff1f\u201d\u8fd9\u4e48\u4e00\u963b\uff0c\u963f\u4e5d\u6697\u53eb\u60ed\u6127\uff0c\u62b9\u4e86\u62b9\u989d\u5934\u6c57\u6c34\uff0c\u6536\u6746\u9000\u56de\u3002\u891a\u7ea2\u67f3\u5fc3\u4e2d\u867d\u7136\u4e0d\u7518\uff0c\u53ef\u4e5f\u4e0d\u6562\u8ffd\u5165\u5bf9\u65b9\u961f\u4f0d\u4e4b\u4e2d\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6c99\u5be8\u4e3b\uff0c\u8001\u592b\u8fd8\u8981\u9886\u6559\u4f60\u7684\u9634\u9633\u5b9d\u6247\u3002\u201d\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6700\u540e\u8fd9\u4e00\u7bb1\uff0c\u4fbf\u7531\u54b1\u4fe9\u6765\u51b3\u80dc\u8d1f\u5427\u3002\u201d\u4e24\u4eba\u521a\u624d\u4ea4\u624b\u5341\u4f59\u62db\uff0c\u672a\u5206\u9ad8\u4e0b\uff0c\u4e8c\u6b21\u4ea4\u950b\uff0c\u5404\u4e0d\u5bb9\u60c5\uff0c\u9f50\u4e0b\u6740\u624b\u3002\u7a0b\u9752\u7af9\u53cc\u6746\u751a\u957f\uff0c\u62db\u672f\u7cbe\u5947\uff0c\u6c99\u5929\u5e7f\u4e00\u67c4\u94c1\u6247\u59cb\u7ec8\u6b3a\u4e0d\u8fd1\u8eab\u3002 \u3000\u3000\u8fd9\u65f6\u7ea2\u65e5\u897f\u659c\uff0c\u5f52\u9e26\u58f0\u55a7\uff0c\u4e00\u9635\u9635\u5728\u7a7a\u4e2d\u98de\u8fc7\u3002\u518d\u6218\u6570\u5341\u62db\uff0c\u6c99\u5929\u5e7f\u6e10\u843d\u4e0b\u98ce\uff0c\u811a\u6b65\u5df2\u89c1\u865a\u6d6e\u3002\u891a\u7ea2\u67f3\u53eb\u9053\uff1a\u201c\u53cc\u65b9\u52bf\u5747\u529b\u654c\uff0c\u96be\u5206\u80dc\u8d25\u3002\u8fd9\u4e00\u7bb1\u5e73\u5206\u4e86\u5427\u3002\u201d\u7a0b\u9752\u7af9\u4e00\u58f0\u957f\u7b11\uff0c\u7af9\u6746\u7740\u5730\u6a2a\u626b\u3002\u6c99\u5929\u5e7f\u5fd9\u8dc3\u8d77\u95ea\u907f\u3002\u7a0b\u9752\u7af9\u53cc\u624b\u6025\u6536\u6025\u53d1\uff0c\u8fde\u6233\u6570\u6746\u3002\u6c99\u5929\u5e7f\u8eab\u5b50\u51cc\u7a7a\uff0c\u96be\u4ee5\u95ea\u907f\uff0c\u5de6\u817f\u7a9d\u91cc\u516d\u6746\u65e9\u7740\uff0c\u843d\u4e0b\u6765\u7ad9\u7acb\u4e0d\u7a33\uff0c\u6251\u5730\u5012\u4e86\u3002\u7a0b\u9752\u7af9\u62f1\u624b\u9053\uff1a\u201c\u627f\u8ba9\uff01\u201d\u6536\u6746\u56de\u5934\u3002 \u3000\u3000\u6c99\u5929\u5e7f\u4e00\u54ac\u7259\uff0c\u4e00\u6309\u6247\u4e0a\u673a\u62ec\uff0c\u5411\u7a0b\u9752\u7af9\u80cc\u540e\u6247\u53bb\uff0c\u4e94\u679a\u94a2\u9489\u75be\u5c04\u800c\u51fa\u3002\u7a0b\u9752\u7af9\u5f85\u5f97\u542c\u5230\u98ce\u58f0\uff0c\u5df2\u7136\u4e0d\u53ca\u907f\u8ba9\uff0c\u4e94\u679a\u94a2\u9489\u4e00\u9f50\u6253\u5728\u80cc\u5fc3\uff0c\u53ea\u89c9\u4e00\u9635\u9178\u9ebb\uff0c\u77e5\u9053\u4e0d\u5999\uff0c\u8ff8\u4f4f\u6c14\u4e00\u8a00\u4e0d\u53d1\uff0c\u7eb5\u8eab\u8dc3\u8fd1\uff0c\u4e24\u6746\u75be\u51fa\uff0c\u70b9\u4e2d\u4e86\u6c99\u5929\u5e7f\u5c0f\u8179\u3002\u8fd9\u4e24\u4e0b\u542b\u6124\u800c\u53d1\uff0c\u4f7f\u8db3\u4e86\u52b2\u529b\uff0c\u6c99\u5929\u5e7f\u767b\u65f6\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5c71\u4e1c\u7fa4\u76d7\u5404\u633a\u5175\u5203\u6251\u4e0a\u76f8\u6551\uff0c\u5c1a\u672a\u5954\u8fd1\uff0c\u7a0b\u9752\u7af9\u4e5f\u5df2\u652f\u6301\u4e0d\u4f4f\uff0c\u4ef0\u5929\u4e00\u4ea4\u6454\u5012\uff0c\u4e94\u679a\u94a2\u9489\u5728\u5730\u4e0b\u4e00\u78b0\uff0c\u53c8\u523a\u8fdb\u4e86\u4e00\u622a\u3002\u963f\u4e5d\u6025\u5954\u4e0a\u524d\u6276\u56de\u3002 \u3000\u3000\u9752\u7af9\u5e2e\u5e2e\u4f17\u89c1\u5e2e\u4e3b\u751f\u6b7b\u4e0d\u660e\uff0c\u65e0\u4e0d\u5927\u6124\uff0c\u56db\u961f\u4eba\u9a6c\u4e00\u9f50\u6251\u4e0a\uff0c\u4e0e\u5c71\u4e1c\u7fa4\u76d7\u6df7\u6218\u8d77\u6765\u3002\u8fd9\u65f6\u5df2\u975e\u6bd4\u6b66\uff0c\u7247\u523b\u95f4\u5404\u6709\u6b7b\u4f24\uff0c\u9c9c\u8840\u56db\u6e85\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u6293\u4f4f\u6076\u864e\u6c9f\u8c2d\u4e8c\u5be8\u4e3b\u7684\u624b\u81c2\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u547d\u5f1f\u5144\u4eec\u505c\u624b\u3002\u201d\u8c2d\u4e8c\u5be8\u4e3b\u62ff\u51fa\u53f7\u89d2\uff0c\u561f\u561f\u561f\u7684\u4e00\u5439\uff0c\u5c71\u4e1c\u7fa4\u76d7\u9000\u4e86\u4e0b\u6765\u3002\u90a3\u8fb9\u7af9\u54e8\u58f0\u54cd\uff0c\u9752\u7af9\u5e2e\u4eba\u4f17\u4e5f\u5404\u540e\u9000\u3002\u539f\u6765\u963f\u4e5d\u89c1\u7a0b\u9752\u7af9\u9192\u8f6c\uff0c\u77e5\u9053\u6df7\u6218\u4e0d\u662f\u4e86\u5c40\uff0c\u89c1\u5bf9\u65b9\u6536\u961f\uff0c\u4e5f\u5c31\u4e58\u673a\u7ea6\u675f\u5e2e\u4f17\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u7ad9\u5728\u53cc\u65b9\u4e4b\u95f4\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u522b\u4f24\u4e86\u548c\u6c14\uff0c\u54b1\u4eec\u628a\u94c1\u7bb1\u5206\u4e86\uff0c\u8fd9\u5c42\u8fc7\u8282\u6162\u6162\u518d\u7b97\u3002\u201d\u8c2d\u4e8c\u5be8\u4e3b\u9053\uff1a\u201c\u6700\u540e\u4e00\u7bb1\u662f\u6211\u4eec\u7684\u3002\u201d\u9752\u7af9\u5e2e\u7684\u4eba\u53eb\u9053\uff1a\u201c\u8981\u4e0d\u8981\u8138\u54ea\uff1f\u8f93\u4e86\u65bd\u6697\u7b97\uff0c\u8fd8\u901e\u751a\u4e48\u597d\u6c49\uff1f\u201d\u53cc\u65b9\u6c79\u6c79\u53eb\u9a82\uff0c\u53c8\u8981\u52a8\u624b\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u9053\uff1a\u201c\u8fd9\u7bb1\u6253\u5f00\u6765\u5e73\u5206\u5427\u3002\u201d\u53cc\u65b9\u5747\u89c1\u9996\u9886\u8eab\u53d7\u91cd\u4f24\uff0c\u4e0d\u6562\u62c2\u9006\u891a\u7ea2\u67f3\u4e4b\u610f\uff0c\u53cd\u6b63\u5df2\u5f97\u5230\u4e0d\u5c11\u73cd\u5b9d\uff0c\u4e5f\u5df2\u5fc3\u6ee1\u610f\u8db3\uff0c\u5f53\u4e0b\u4fbf\u6d3e\u4eba\u6765\u642c\u3002 \u3000\u3000\u963f\u4e5d\u53eb\u9053\uff1a\u201c\u7b2c\u516b\u7bb1\u662f\u6211\u8d62\u7684\uff0c\u6211\u4e0d\u8981\uff0c\u7559\u7ed9\u90a3\u4f4d\u5ba2\u4eba\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u8bb8\u52a8\u4ed6\u7684\u3002\u201d\u891a\u7ea2\u67f3\u9053\uff1a\u201c\u5e72\u4e48\u5440\uff1f\u201d\u963f\u4e5d\u9053\uff1a\u201c\u8981\u4e0d\u662f\u4ed6\u7684\u9a6c\u53d1\u766b\uff0c\u6211\u65e9\u4f24\u5728\u4f60\u8001\u4f2f\u638c\u4e0b\u4e86\uff0c\u7559\u4e00\u7bb1\u916c\u8c22\u4ed6\u3002\u201d\u891a\u7ea2\u67f3\u7b11\u9053\uff1a\u201c\u5c0f\u599e\u5012\u4e5f\u6069\u6028\u5206\u660e\u3002\u597d\u5427\uff0c\u5927\u4f19\u513f\u642c\u5427\u3002\u7bb1\u4e0a\u5199\u7740\u5b57\uff0c\u53ef\u522b\u5f04\u9519\u4e86\u3002\u201d \u3000\u3000\u7fa4\u76d7\u6b63\u8981\u52a8\u624b\u53bb\u642c\u94c1\u7bb1\uff0c\u8881\u627f\u5fd7\u5ffd\u9053\uff1a\u201c\u5404\u4f4d\u521a\u624d\u662f\u7ec3\u6b66\u529f\u5417\uff1f\u5012\u4e5f\u70ed\u95f9\u597d\u770b\uff0c\u80dc\u8fc7\u4e86\u6c5f\u6e56\u4e0a\u5356\u827a\u7684\u3002\u73b0\u4e0b\u53c8\u8981\u5e72\u751a\u4e48\u4e86\uff1f\u201d \u3000\u3000\u963f\u4e5d\u5657\u54e7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u6211\u4eec\u8981\u642c\u7bb1\u5b50\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e2a\u53ef\u4e0d\u6562\u5f53\uff0c\u6211\u5df2\u96c7\u4e86\u5927\u8f66\u3002\u5404\u4f4d\u5982\u6b64\u5ba2\u6c14\uff0c\u840d\u6c34\u76f8\u9022\uff0c\u600e\u597d\u52b3\u9a7e\uff1f\u201d\u963f\u4e5d\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u4ee3\u4f60\u642c\uff0c\u662f\u81ea\u5df1\u642c\u554a\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5012\u5947\u4e86\uff0c\u8fd9\u4e9b\u7bb1\u5b50\u597d\u50cf\u662f\u6211\u7684\u554a\u3002 \u3000\u3000\u96be\u9053\u5404\u4f4d\u8ba4\u9519\u4e86\u7bb1\u5b50\uff1f\u201d \u3000\u3000\u5c71\u4e1c\u76d7\u5e2e\u4e2d\u4e00\u4eba\u9a82\u9053\uff1a\u201c\u8fd9\u79cd\u516c\u5b50\u54e5\u513f\u5c31\u4f1a\u5403\u996d\u62c9\u5c4e\uff0c\u8ddf\u4ed6\u591a\u8bf4\u5e72\u4e48\uff1f\u8fd9\u6b21\u7559\u4e0b\u4e86\u4ed6\u7684\u5c0f\u547d\uff0c\u7b97\u4ed6\u7956\u4e0a\u79ef\u5fb7\u3002\u201d\u4fef\u8eab\u5c31\u53bb\u62ac\u7bb1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u52a8\u4e0d\u5f97\u7684\u3002\u201d\u722c\u5230\u7bb1\u4e0a\uff0c\u4e00\u62ac\u817f\u95f4\uff0c\u90a3\u5927\u6c49\u76f4\u8dcc\u4e86\u51fa\u53bb\u3002\u8881\u627f\u5fd7\u722c\u5728\u7bb1\u4e0a\uff0c\u624b\u8db3\u4e71\u821e\uff0c\u8fde\u53eb\uff1a\u201c\u554a\u54df\uff0c\u6551\u4eba\u54ea\uff01\u201d \u3000\u3000\u963f\u4e5d\u8fd8\u9053\u4ed6\u771f\u7684\u6454\u8dcc\uff0c\u7eb5\u4e0a\u53bb\u62c9\u4f4f\u4ed6\u624b\u81c2\u63d0\u4e86\u8d77\u6765\uff0c\u534a\u55d4\u534a\u7b11\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u662f\u7684\uff01\u201d\u7fa4\u76d7\u89c1\u4ed6\u5982\u6b64\u72fc\u72c8\uff0c\u4ee5\u4e3a\u4ed6\u8fd9\u4e00\u811a\u4e0d\u8fc7\u8e22\u5f97\u51d1\u5de7\uff0c\u53c8\u8981\u53bb\u642c\u7bb1\u5b50\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53cc\u624b\u8fde\u6447\uff0c\u53eb\u9053\uff1a\u201c\u6162\u6765\uff0c\u6162\u6765\uff0c\u5404\u4f4d\u8981\u628a\u6211\u7bb1\u5b50\u642c\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u963f\u4e5d\u9053\uff1a\u201c\u54b1\u4eec\u5404\u56de\u5404\u7684\u5bb6\u5440\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5462\uff1f\u201d\u963f\u4e5d\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u5446\u5934\u5446\u8111\u7684\uff0c\u8fd8\u662f\u4e56\u4e56\u7684\u4e5f\u8d76\u5feb\u56de\u5bb6\u5427\uff0c\u522b\u628a\u5c0f\u6027\u547d\u4e5f\u5728\u9053\u4e0a\u9001\u4e86\u3002\u201d\u8881\u627f\u5fd7\u70b9\u5934\u9053\uff1a\u201c\u59d1\u5a18\u6b64\u8a00\u6709\u7406\uff0c\u6211\u8fd9\u5c31\u5e26\u4e86\u7bb1\u5b50\u56de\u5bb6\u3002\u201d \u3000\u3000\u521a\u624d\u88ab\u8e22\u4e86\u4e00\u4ea4\u7684\u90a3\u5927\u6c49\u5fc3\u4e0b\u607c\u6012\uff0c\u4f38\u624b\u5411\u4ed6\u80a9\u5934\u731b\u529b\u63a8\u53bb\uff0c\u559d\u9053\uff1a\u201c\u6eda\u4f60\u5988\u7684\uff01\u201d\u4e00\u58f0\u672a\u6bd5\uff0c\u540e\u5fc3\u5df2\u88ab\u8881\u627f\u5fd7\u6293\u4f4f\uff0c\u4e00\u626c\u624b\u5904\uff0c\u90a3\u5927\u6c49\u5f53\u771f\u662f\u9ad8\u98de\u8fdc\u8d70\uff0c\u5728\u7a7a\u4e2d\u5212\u4e86\u4e2a\u5f27\u5f62\uff0c\u843d\u5728\u4e03\u516b\u4e08\u5916\u4e00\u682a\u5927\u6811\u9876\u4e0a\uff0c\u62da\u6b7b\u547d\u62b1\u4f4f\u6811\u5e72\uff0c\u5927\u53eb\u5927\u56b7\u3002\u4e00\u7fa4\u4e4c\u9e26\u4ece\u6811\u4e0a\u60ca\u98de\u8d77\u6765\uff0c\u8052\u566a\u4e0d\u5df2\uff0c\u5728\u4ed6\u5934\u9876\u4e71\u515c\u5708\u5b50\u3002\u8fd9\u4e00\u6765\uff0c\u7fa4\u76d7\u65b9\u77e5\u773c\u524d\u8fd9\u5c11\u5e74\u8eab\u6000\u7edd\u827a\uff0c\u8fd9\u4e00\u526f\u516c\u5b50\u54e5\u513f\u822c\u7684\u9178\u76f8\uff0c\u5168\u662f\u88c5\u51fa\u6765\u5f00\u73a9\u7b11\u7684\uff0c\u7136\u800c\u81ea\u6043\u4eba\u591a\u52bf\u4f17\uff0c\u4e5f\u6ca1\u5c06\u4ed6\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u7a0b\u9752\u7af9\u80cc\u4e0a\u6240\u4e2d\u4e94\u679a\u94a2\u9489\u5df2\u7531\u90e8\u5c5e\u62d4\u51fa\uff0c\u81ea\u77e5\u53d7\u4f24\u4e0d\u8f7b\uff0c\u8fd0\u6c14\u62a4\u4f4f\u4f24\u53e3\uff0c\u53ea\u5f85\u5206\u5230\u8d43\u7269\u540e\u7acb\u5373\u9000\u8d70\uff0c\u5ffd\u89c1\u8881\u627f\u5fd7\u9732\u4e86\u8fd9\u4e00\u624b\uff0c\u5b9e\u662f\u9ad8\u6df1\u5df2\u6781\u7684\u6b66\u529f\uff0c\u773c\u524d\u65e0\u4e00\u4eba\u662f\u4ed6\u654c\u624b\uff0c\u4e0d\u7531\u5f97\u5927\u60ca\uff0c\u5fd9\u62db\u624b\u53eb\u963f\u4e5d\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u53ef\u8f7b\u654c\uff0c\u52a1\u987b\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u963f\u4e5d\u70b9\u5934\u7b54\u5e94\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u6599\u4e0d\u5230\u8fd9\u6837\u4e00\u4e2a\u79c0\u624d\u76f8\u516c\u7adf\u4f1a\u662f\u6b66\u5b66\u9ad8\u624b\uff0c\u53c8\u60f3\u5230\u4ed6\u9002\u624d\u7eb5\u9a6c\u89e3\u56f4\uff0c\u5e76\u975e\u65e0\u5fc3\u78b0\u5de7\uff0c\u5b9e\u662f\u6709\u5fc3\u76f8\u6551\uff0c\u4e0d\u7981\u6697\u6697\u611f\u6fc0\u3002 \u3000\u3000\u53ea\u542c\u8881\u627f\u5fd7\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6253\u4e86\u534a\u5929\uff0c\u53c8\u5728\u6211\u7bb1\u4e0a\u5199\u751a\u4e48\u7532\u4e59\u4e19\u4e01\uff0c\u5c71\u4e1c\u76f4\u96b6\uff0c\u73b0\u4e0b\u73a9\u591f\u4e86\u5427\uff1f\u54c8\u54c8\uff0c\u6211\u53ef\u8981\u64e6\u53bb\u5566\uff01\u201d\u968f\u624b\u6293\u8d77\u8eab\u65c1\u4e00\u6761\u5927\u6c49\uff0c\u6253\u6a2a\u63d0\u5728\u624b\u4e2d\uff0c\u7ed5\u7740\u94c1\u7bb1\u5954\u8dd1\u4e00\u5468\uff0c\u4fbf\u628a\u4ed6\u5f53\u62b9\u5e03\u4f7f\uff0c\u628a\u7bb1\u4e0a\u201c\u7532\u4e59\u4e19\u4e01\u201d\u53ca\u201c\u76f4\u9c81\u201d\u7b49\u5b57\u64e6\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u53cc\u624b\u4e00\u9001\uff0c\u90a3\u5927\u6c49\u53c8\u98de\u5230\u4e86\u6811\u9876\u4e4b\u4e0a\u3002 \u3000\u3000\u5c71\u4e1c\u76d7\u5e2e\u4e2d\u5341\u4f59\u4eba\u5927\u58f0\u5450\u558a\uff0c\u624b\u6267\u5175\u5203\u6251\u4e0a\u3002\u8881\u627f\u5fd7\u62f3\u6253\u8db3\u8e22\uff0c\u4f46\u89c1\u7a7a\u4e2d\u5175\u5203\u548c\u5927\u6c49\u9f50\u98de\uff0c\u60ca\u547c\u5171\u9e26\u9e23\u4ea4\u4f5c\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5341\u4f59\u540d\u5927\u6c49\u90fd\u88ab\u4ed6\u5148\u540e\u6293\u8d77\uff0c\u6454\u4e0a\u56db\u5468\u6811\u5dc5\u3002 \u3000\u3000\u5c71\u4e1c\u7fa4\u76d7\u548c\u9752\u7af9\u5e2e\u90fd\u662f\u4e00\u9635\u5927\u4e71\uff0c\u5230\u8fd9\u65f6\u65b9\u59cb\u5fc3\u60ca\u3002\u7a0b\u9752\u7af9\u548c\u6c99\u5929\u5e7f\u5404\u53d7\u91cd\u4f24\uff0c\u7fa4\u76d7\u9f50\u671b\u7740\u891a\u7ea2\u67f3\u5f85\u4ed6\u4f5c\u4e3b\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u54fc\u4e86\u4e00\u58f0\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u539f\u6765\u4e5f\u662f\u6b66\u6797\u4e00\u8109\uff0c\u8981\u8bf7\u6559\u9601\u4e0b\u7684\u4e07\u513f\uff0c\u662f\u4f55\u4eba\u7684\u95e8\u4e0b\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u665a\u751f\u59d3\u8881\uff0c\u6211\u5e08\u7236\u662f\u53fd\u54e9\u5495\u53fd\u8001\u592b\u5b50\u3002\u4ed6\u8001\u4eba\u5bb6\u662f\u7ecf\u5b66\u5927\u5e08\uff0c\u5bf9\u300a\u793c\u8bb0\u300b\u548c\u300a\u6625\u79cb\u300b\u662f\u6700\u6709\u5fc3\u5f97\u7684\u4e86\u3002\u8fd8\u6709\u4e00\u4f4d\u674e\u8001\u592b\u5b50\uff0c\u4ed6\u662f\u6559\u6211\u516b\u80a1\u65f6\u6587\u7684\uff0c\u8bb2\u7a76\u8d77\u627f\u8f6c\u5408\u2026\u2026\u201d \u3000\u3000\u891a\u7ea2\u67f3\u9053\uff1a\u201c\u8fd9\u65f6\u5019\u8fd8\u88c5\u751a\u4e48\u849c\uff1f\u4f60\u628a\u6b66\u5b66\u5e08\u627f\u8bf4\u51fa\u6765\uff0c\u8981\u662f\u6211\u4eec\u6709\u751a\u4e48\u6e0a\u6e90\uff0c\u5927\u5bb6\u4e5f\u4e0d\u662f\u4e0d\u8bb2\u4ea4\u60c5\u4e49\u6c14\u7684\u4eba\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u8bf4\u5230\u6e0a\u6e90\uff0c\u8fc7\u53bb\u662f\u6ca1\u6709\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u90a3\u4e0d\u662f\u6709\u4e86\u89c1\u9762\u4e4b\u60c5\u4e48\uff1f\u5404\u4f4d\u751f\u610f\u4e0d\u6210\u4ec1\u4e49\u5728\uff0c\u867d\u7136\u6ca1\u8d5a\u5230\uff0c\u5374\u4e5f\u6ca1\u8680\u4e86\u672c\u3002\u5929\u8272\u4e0d\u65e9\u5566\uff0c\u8bf7\u8bf7\uff0c\u5728\u4e0b\u8981\u8d70\u5566\u3002\u201d \u3000\u3000\u6740\u8c79\u5c97\u4faf\u5be8\u4e3b\u5927\u9a82\u201c\u4f60\u5976\u5976\u7684\u201d\u58f0\u4e2d\uff0c\u63d0\u8d77\u6cfc\u98ce\u4e5d\u73af\u5200\uff0c\u4e00\u62db\u201c\u98ce\u626b\u8d25\u53f6\u201d\uff0c\u5411\u8881\u627f\u5fd7\u80a9\u5934\u6a2a\u780d\u8fc7\u53bb\u3002\u8881\u627f\u5fd7\u8eab\u5b50\u7a0d\u4fa7\uff0c\u4e5d\u73af\u5200\u4ece\u4ed6\u8eab\u65c1\u524a\u8fc7\u3002\u4faf\u5be8\u4e3b\u8fd9\u4e00\u62db\u7528\u529b\u6781\u731b\uff0c\u5927\u5200\u4f59\u52bf\u4e0d\u8870\uff0c\u76f4\u780d\u891a\u7ea2\u67f3\u524d\u80f8\u3002 \u3000\u3000\u4f17\u4eba\u60ca\u547c\u58f0\u4e2d\uff0c\u891a\u7ea2\u67f3\u4f38\u51fa\u5de6\u624b\uff0c\u98df\u4e2d\u4e24\u6307\u94b3\u4f4f\u5200\u80cc\uff0c\u5411\u540e\u4e00\u62c9\uff0c\u90a3\u5200\u624d\u505c\u4f4f\u4e86\u3002\u4faf\u5be8\u4e3b\u53ea\u81ca\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u891a\u5e84\u4e3b\uff0c\u5bf9\u2026\u2026\u5bf9\u4e0d\u4f4f\uff01\u201d\u891a\u7ea2\u67f3\u5fae\u5fae\u4e00\u7b11\uff0c\u653e\u5f00\u624b\u6307\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u51ed\u8fd9\u624b\u529f\u592b\uff0c\u5f97\u4f60\u4e00\u7bb1\u8d22\u7269\uff0c\u8fd8\u4e0d\u7b97\u4e0d\u914d\u5427\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u624b\u751a\u4e48\u529f\u592b\uff1f\u201d\u891a\u7ea2\u67f3\u5f97\u610f\u6d0b\u6d0b\u7684\u9053\uff1a\u201c\u6211\u8fd9\u95e8\u2018\u87f9\u94b3\u529f\u2019\uff0c\u4f60\u8981\u662f\u4e5f\u4f1a\uff0c\u6211\u5c31\u670d\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u751a\u4e48\u87f9\u94b3\u3001\u867e\u94b3\uff1f\u6211\u6ca1\u77a7\u89c1\u3002\u201d\u891a\u7ea2\u67f3\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u6211\u7528\u4e24\u6839\u624b\u6307\u94b3\u4f4f\u4e86\u4ed6\u5927\u5200\uff0c\u96be\u9053\u4f60\u778e\u4e86\u773c\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u8fd9\u4e2a\uff0c\u90a3\u662f\u4f60\u4eec\u4e24\u4e2a\u4e32\u901a\u7684\uff0c\u6709\u751a\u4e48\u7a00\u5947\uff1f\u9752\u5f1f\uff0c\u6765\uff0c\u54b1\u4eec\u4e5f\u6765\u7ec3\u4e00\u62db\u3002\u201d\u9752\u9752\u7b11\u563b\u563b\u7684\u4ece\u5730\u4e0b\u6361\u8d77\u4e00\u67c4\u5355\u5200\uff0c\u4f5c\u52bf\u5411\u8881\u627f\u5fd7\u780d\u6765\uff0c\u780d\u5230\u4e34\u8fd1\uff0c\u653e\u6162\u4e86\u52bf\u5934\uff0c\u8f7b\u8f7b\u63a8\u5c06\u8fc7\u53bb\u3002\u8881\u627f\u5fd7\u53cc\u624b\u6bdb\u624b\u6bdb\u811a\u6293\u4f4f\u5200\u80cc\u3002\u9752\u9752\u5047\u610f\u7528\u529b\u6323\u624e\uff0c\u4e71\u8df3\u4e00\u9635\uff0c\u59cb\u7ec8\u6ca1\u80fd\u6323\u5f00\uff0c\u5927\u53eb\uff1a\u201c\u554a\u54df\uff0c\u597d\u5389\u5bb3\u7684\u87f9\u94b3\u529f\uff01\u201d \u3000\u3000\u963f\u4e5d\u89c1\u4e24\u4eba\u4f5c\u5f04\u891a\u7ea2\u67f3\uff0c\u4e0d\u7981\u683c\u683c\u5a07\u7b11\u3002\u76f4\u9c81\u7fa4\u76d7\u4e5f\u5fcd\u4e0d\u4f4f\u653e\u58f0\u8f70\u7b11\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u7eb5\u6a2a\u5c71\u4e1c\uff0c\u4e00\u5411\u9890\u6307\u6c14\u4f7f\u60ef\u4e86\u7684\uff0c\u54ea\u5bb9\u5f97\u4e24\u4e2a\u540e\u751f\u5c0f\u8f88\u620f\u4fae\u4e8e\u4ed6\uff1f\u631f\u624b\u593a\u8fc7\u4faf\u5be8\u4e3b\u7684\u4e5d\u73af\u5200\uff0c\u6a2a\u6258\u5728\u624b\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u6765\u5288\u6211\u4e00\u5200\u8bd5\u8bd5\u3002\u90a3\u603b\u4e0d\u662f\u4e32\u901a\u4e86\u5427\uff01\u201d\u4ed6\u89c1\u8881\u627f\u5fd7\u624b\u6267\u7fa4\u76d7\uff0c\u6b66\u529f\u751a\u9ad8\uff0c\u82e5\u548c\u4ed6\u52a8\u62f3\u811a\u6bd4\u5175\u5203\uff0c\u672a\u5fc5\u80fd\u80dc\uff0c\u81ea\u5df1\u8fd9\u95e8\u201c\u87f9\u94b3\u529f\u201d\u7ec3\u4e86\u6570\u5341\u5e74\uff0c\u6781\u6709\u628a\u63e1\uff0c\u8fd9\u5c11\u5e74\u4e0d\u8bc6\u8d27\uff0c\u6b63\u53ef\u51ed\u6b64\u731b\u4e0b\u6bd2\u624b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5288\u6b7b\u4e86\u4eba\u53ef\u4e0d\u507f\u547d\uff01\u4f60\u4e5f\u4e0d\u80fd\u62a5\u5230\u5b98\u91cc\u53bb\u3002 \u3000\u3000\u8981\u6253\u5b98\u53f8\uff0c\u54b1\u4eec\u5c31\u4e0d\u5e72\u3002\u201d\u891a\u7ea2\u67f3\u6108\u6012\uff0c\u5df2\u8d77\u6740\u5fc3\uff0c\u9ed1\u8d77\u4e86\u8138\u9053\uff1a\u201c\u4e0d\u8bba\u8c01\u6b7b\uff0c\u90fd\u4e0d\u507f\u547d\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff0c\u5200\u6765\u5566\uff01\u201d\u5ffd\u5730\u53cd\u624b\u6a2a\u5288\u4e00\u5200\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u4e07\u6599\u4e0d\u5230\u8fd9\u4e00\u5200\u7adf\u4f1a\u4ece\u8fd9\u65b9\u4f4d\u5288\u6765\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u4f4e\u5934\uff0c\u5e3d\u5b50\u5df2\u88ab\u524a\u4e86\u4e0b\u6765\uff0c\u7fa4\u76d7\u53c8\u662f\u4e00\u9635\u8f70\u7b11\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u87f9\u94b3\u5462\uff1f\u600e\u4e48\u6211\u597d\u50cf\u6ca1\u77a7\u89c1\u554a\uff01\u201d\u8bdd\u58f0\u65b9\u6b47\uff0c\u6325\u5200\u7740\u5730\u780d\u53bb\u3002\u891a\u7ea2\u67f3\u817e\u8eab\u6025\u8df3\uff0c\u94a2\u5200\u5df2\u628a\u4ed6\u4e00\u53cc\u9774\u5b50\u7684\u9774\u5e95\u5207\u4e0b\u3002\u8fd9\u4e00\u5200\u82e5\u662f\u4e0a\u5f97\u4e09\u5bf8\uff0c\u891a\u5e84\u4e3b\u4fbf\u6210\u4e3a\u65e0\u811a\u5e84\u5e84\u4e3b\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u662f\u4e86\uff1a\u592a\u9ad8\u592a\u4f4e\u90fd\u4e0d\u6210\uff0c\u592a\u5feb\u4e86\u4f60\u53c8\u4e0d\u6210\uff0c\u6211\u6162\u6162\u7684\u4ece\u4e2d\u95f4\u780d\u6765\u5427\uff01\u201d\u8fd9\u4e00\u5200\u679c\u7136\u4fbf\u4e0e\u9752\u9752\u521a\u624d\u90a3\u6837\uff0c\u6162\u6162\u63a8\u5c06\u8fc7\u53bb\u3002\u891a\u7ea2\u67f3\u4f38\u51fa\u5de6\u624b\u6765\u94b3\uff0c\u51c6\u62df\u4e00\u94b3\u94b3\u4f4f\u5bf9\u65b9\u5175\u5203\uff0c\u53f3\u638c\u6bd2\u62db\u7acb\u53d1\uff0c\u975e\u5c06\u4ed6\u4e94\u5b98\u51fb\u5f97\u7a00\u70c2\u4e0d\u53ef\u3002\u4e0d\u6599\u8881\u627f\u5fd7\u8fd9\u4e00\u5200\u5feb\u8981\u63a8\u8fd1\uff0c\u7a81\u7136\u4e00\u7ffb\u4e00\u5212\uff0c\u5203\u950b\u5df2\u5728\u4ed6\u4e24\u6839\u624b\u6307\u4e0a\u5404\u5212\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u767b\u65f6\u9c9c\u8840\u6dcb\u6f13\u3002\u8fd9\u4e09\u5200\u9ad8\u4e0b\u5feb\u6162\uff0c\u53d8\u5316\u83ab\u6d4b\uff0c\u4f3c\u662f\u6e38\u620f\u4e4b\u4f5c\uff0c\u5b9e\u5219\u5305\u542b\u4e86\u6781\u9ad8\u6df1\u7684\u6b66\u529f\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u5927\u6012\uff0c\u559d\uff1a\u201c\u9f20\u8f88\uff0c\u4f60\u6211\u638c\u5e95\u89c1\u751f\u6b7b\uff01\u201d\u8881\u627f\u5fd7\u53cd\u624b\u63b7\u51fa\u5927\u5200\uff0c\u6500\u5728\u6811\u9876\u7684\u90a3\u5927\u6c49\u6b63\u5f80\u4e0b\u722c\uff0c\u8fd9\u5200\u98de\u5c06\u8fc7\u53bb\uff0c\u6070\u597d\u5272\u65ad\u4e86\u4ed6\u843d\u811a\u7684\u6811\u679d\uff0c\u4e00\u4e2a\u5012\u683d\u8471\uff0c\u8dcc\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f17\u4eba\u4e71\u53eb\u58f0\u4e2d\uff0c\u8881\u627f\u5fd7\u5438\u4e00\u53e3\u6c14\uff0c\u5df2\u8fd0\u8d77\u4e86\u6df7\u5143\u529f\uff0c\u63d0\u8d77\u5341\u53ea\u94c1\u7bb1\uff0c\u968f\u624b\u4e71\u4e22\uff0c\u4e00\u53ea\u63a5\u4e00\u53ea\u7684\u53e0\u4e86\u8d77\u6765\uff0c\u51e0\u8fbe\u4e09\u4e08\uff0c\u8bf4\u9053\uff1a\u201c\u6bd4\u5c31\u6bd4\uff01\u53ef\u662f\u6211\u4e0d\u5927\u653e\u5fc3\u3002\u4f60\u4eec\u8fd9\u4e9b\u4eba\u8d3c\u5934\u8d3c\u8111\u7684\uff0c\u522b\u4e58\u6211\u6253\u5f97\u8d77\u52b2\u4e4b\u65f6\uff0c\u5077\u4e86\u7bb1\u5b50\u53bb\u3002\u201d\u8e0a\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u7bb1\u9876\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4e0a\u6765\u6bd4\u5427\u3002\u201d \u3000\u3000\u891a\u7ea2\u67f3\u89c1\u4ed6\u628a\u4e00\u53e3\u53e3\u6c89\u91cd\u7684\u7bb1\u5b50\u8d8a\u63b7\u8d8a\u9ad8\uff0c\u5df2\u81ea\u60ca\u9a87\u4e8e\u4ed6\u7684\u795e\u529b\uff0c\u5f85\u89c1\u4ed6\u8f7b\u98d8\u98d8\u7684\u4e00\u8dc3\u800c\u4e0a\uff0c\u8f7b\u529f\u9020\u8be3\u5c24\u5176\u4e0d\u51e1\uff0c\u66f4\u662f\u5403\u60ca\u3002\u4ed6\u81ea\u77e5\u8f7b\u529f\u4e0d\u6210\uff0c\u54ea\u6562\u4e0a\u9ad8\u732e\u4e11\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6709\u79cd\u5c31\u4e0b\u6765\uff01\u201d\u8881\u627f\u5fd7\u5728\u4e0a\u9762\u9ad8\u53eb\uff1a\u201c\u4f60\u6709\u79cd\u5c31\u4e0a\u6765\uff01\u201d \u3000\u3000\u891a\u7ea2\u67f3\u8e0f\u6b65\u4e0a\u524d\uff0c\u62b1\u4f4f\u4e0b\u9762\u51e0\u53ea\u94c1\u7bb1\u4e00\u9635\u6447\u52a8\uff0c\u53ea\u89c1\u8881\u627f\u5fd7\u5934\u4e0b\u811a\u4e0a\uff0c\u5012\u683d\u4e0b\u6765\u3002 \u3000\u3000\u7fa4\u76d7\u4e00\u9635\u6b22\u547c\uff0c\u5374\u89c1\u8881\u627f\u5fd7\u8dcc\u5230\u891a\u7ea2\u67f3\u5934\u9876\u65f6\uff0c\u500f\u5730\u4e00\u62db\u201c\u82cd\u9e70\u640f\u5154\u201d\uff0c\u5de6\u638c\u51cc\u7a7a\u4e0b\u51fb\u3002\u891a\u7ea2\u67f3\u4e00\u60ca\uff0c\u6325\u8d77\u53f3\u638c\u53cd\u51fb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u4f38\u624b\uff0c\u5df2\u6263\u4f4f\u4ed6\u8109\u95e8\uff0c\u5f85\u5f97\u53cc\u8db3\u7740\u5730\uff0c\u559d\u4e00\u58f0\uff1a\u201c\u8d77\uff01\u201d \u3000\u3000\u628a\u891a\u7ea2\u67f3\u4e00\u4e2a\u80a5\u80a5\u7684\u8eab\u8eaf\u6325\u4e86\u8d77\u6765\uff0c\u521a\u843d\u5728\u4e00\u53e0\u94c1\u7bb1\u4e4b\u9876\u3002\u5341\u53e3\u7bb1\u5b50\u672c\u5c31\u53e0\u5f97\u4e1c\u6b6a\u897f\u659c\uff0c\u8fd9\u6837\u4e00\u4e2a\u5927\u80d6\u5b50\u52a0\u4e86\u4e0a\u53bb\uff0c\u767b\u65f6\u4e00\u9635\u6447\u6643\u3002\u891a\u7ea2\u67f3\u5728\u4e0a\u9762\u53cc\u624b\u4e71\u821e\uff0c\u5341\u5206\u72fc\u72c8\uff0c\u5230\u540e\u6765\u60c5\u4e0d\u81ea\u7981\uff0c\u4fef\u4e0b\u8eab\u6765\uff0c\u62b1\u4f4f\u4e86\u7bb1\u76d6\u3002\u7fa4\u76d7\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u597d\u7b11\u3002 \u3000\u3000\u9752\u9752\u53eb\u9053\uff1a\u201c\u4f60\u6709\u79cd\u5c31\u4e0b\u6765\uff01\u201d\u963f\u4e5d\u60f3\u8d77\u891a\u7ea2\u67f3\u521a\u624d\u7684\u8bf4\u8bdd\uff0c\u4e0d\u7981\u62bf\u5634\u5fae\u7b11\u3002 \u3000\u3000\u891a\u7ea2\u67f3\u7684\u6b66\u529f\u6df1\u5f97\u201c\u7a33\u3001\u72e0\u3001\u51c6\u3001\u97e7\u201d\u56db\u5b57\u8bc0\u4e2d\u7cbe\u8981\uff0c\u9002\u624d\u4e0e\u963f\u4e5d\u6bd4\u6b66\uff0c\u5c31\u5341\u8db3\u663e\u793a\u4e86\u8fd9\u56db\u5b57\u8bc0\u7684\u957f\u5904\u3002\u4ed6\u8eab\u6750\u80a5\u80d6\uff0c\u7d20\u4e0d\u4e60\u7ec3\u8f7b\u529f\uff0c\u81ea\u6765\u4ee5\u7a33\u8865\u5feb\uff0c\u4ee5\u72e0\u4ee3\u5de7\uff0c\u638c\u6cd5\u7531\u62d9\u89c1\u529f\uff0c\u73b0\u4e0b\u7a81\u7136\u767b\u9ad8\uff0c\u6b63\u662f\u72af\u4e86\u4ed6\u7684\u5927\u5fcc\uff0c\u867d\u7136\u4e00\u8eab\u6b66\u529f\uff0c\u5374\u5f04\u5f97\u624b\u8db3\u65e0\u63aa\u3002\u9002\u624d\u8881\u627f\u5fd7\u89c1\u4ed6\u51fa\u624b\uff0c\u770b\u51fa\u4e86\u4ed6\u7684\u77ed\u5904\uff0c\u6545\u610f\u5e03\u7f6e\u8fd9\u4e2a\u9677\u9631\u6765\u8ddf\u4ed6\u4e3a\u96be\u3002 \u3000\u3000\u7fa4\u76d7\u8c01\u4e5f\u4e0d\u6562\u53bb\u79fb\u52a8\u94c1\u7bb1\uff0c\u53ea\u6015\u4e00\u52a8\uff0c\u4e0a\u9762\u7bb1\u5b50\u5012\u5c06\u4e0b\u6765\uff0c\u4e0d\u4f46\u6454\u574f\u4e86\u891a\u7ea2\u67f3\uff0c\u8fd8\u4f1a\u538b\u6b7b\u591a\u4eba\u3002\u5f53\u4e0b\u90fd\u7ad9\u5f97\u8fdc\u8fdc\u5730\u3002 \u3000\u3000\u50f5\u6301\u4e86\u4e00\u9635\uff0c\u6c99\u5929\u5e7f\u4f4e\u58f0\u9053\uff1a\u201c\u8c2d\u8d24\u5f1f\uff0c\u56f4\u653b\u90a3\u5c0f\u5b50\uff0c\u5148\u5e72\u6389\u4ed6\u3002\u201d\u4e00\u8a00\u63d0\u9192\u4e86\u8c2d\u4e8c\u5be8\u4e3b\uff0c\u5f53\u5373\u5439\u52a8\u53f7\u89d2\uff0c\u5c71\u4e1c\u7fa4\u76d7\u62d4\u51fa\u5175\u5203\uff0c\u9f50\u5411\u8881\u627f\u5fd7\u51b2\u6765\u3002 \u3000\u3000\u54d1\u5df4\u3001\u9752\u9752\u3001\u6d2a\u80dc\u6d77\u4e00\u9f50\u7ad9\u5230\u8881\u627f\u5fd7\u8eab\u8fb9\u3002\u9752\u9752\u6301\u5251\uff0c\u6d2a\u80dc\u6d77\u7528\u5200\uff0c\u821e\u52a8\u6740\u780d\u3002\u8881\u627f\u5fd7\u548c\u54d1\u5df4\u5374\u662f\u7a7a\u624b\uff0c\u6293\u4f4f\u4e86\u4eba\u4e71\u4e22\u4e71\u63b7\u3002\u7fa4\u76d7\u51fa\u9053\u4ee5\u6765\uff0c\u4ece\u672a\u89c1\u8fc7\u8fd9\u6837\u7684\u6253\u6cd5\u3002\u4e8c\u4eba\u6240\u5230\u4e4b\u5904\uff0c\u7fa4\u76d7\u7eb7\u7eb7\u8d70\u907f\u3002\u8881\u627f\u5fd7\u6570\u8dc3\u4e4b\u95f4\uff0c\u5df2\u5954\u5230\u6c99\u5929\u5e7f\u8eab\u65c1\u3002\u4ed6\u5367\u5728\u5730\u4e0b\uff0c\u4e24\u540d\u76d7\u9996\u5728\u65c1\u7167\u6599\uff0c\u5ffd\u89c1\u8881\u627f\u5fd7\u51b2\u6765\uff0c\u4e00\u4e2a\u4e3e\u5200\u780d\u6321\uff0c\u53e6\u4e00\u4e2a\u80cc\u8d77\u6c99\u5929\u5e7f\u907f\u8ba9\u3002\u8881\u627f\u5fd7\u5934\u4e00\u4f4e\uff0c\u4ece\u5200\u4e0b\u94bb\u8fc7\uff0c\u6293\u4f4f\u524d\u9762\u76d7\u9996\u7684\u5934\u4e00\u626d\uff0c\u90a3\u4eba\u75db\u5f97\u5927\u53eb\uff0c\u6492\u624b\u628a\u6c99\u5929\u5e7f\u4e22\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4f38\u624b\u63a5\u4f4f\uff0c\u7eb5\u8eab\u8df3\u4e0a\u4e00\u8f86\u5927\u8f66\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8981\u4e0d\u8981\u4ed6\u6027\u547d\uff1f\u201d\u7fa4\u76d7\u89c1\u9996\u9886\u88ab\u64d2\uff0c\u4e00\u65f6\u5012\u5446\u4f4f\u4e86\uff0c\u4e0d\u6562\u52a8\u624b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5411\u54d1\u5df4\u4e00\u6253\u624b\u52bf\uff0c\u54d1\u5df4\u5f84\u5f80\u9752\u7af9\u5e2e\u51b2\u53bb\u3002\u9752\u7af9\u5e2e\u5e2e\u4f17\u672c\u6765\u8896\u624b\u89c2\u6218\uff0c\u5ffd\u89c1\u54d1\u5df4\u5982\u731b\u864e\u822c\u51b2\u6765\uff0c\u5404\u4e3e\u5175\u5203\u62e6\u963b\u3002 \u3000\u3000\u4f46\u54d1\u5df4\u8ffd\u968f\u795e\u5251\u4ed9\u733f\u7a46\u4eba\u6e05\u591a\u5e74\uff0c\u6b66\u529f\u5df2\u975e\u5bfb\u5e38\u6b66\u5e08\u6240\u80fd\u654c\uff0c\u53ea\u89c1\u4ed6\u5934\u9876\u5200\u67aa\u4e71\u98de\uff0c\u8d64\u624b\u7a7a\u62f3\u7684\u51b2\u5230\u7a0b\u9752\u7af9\u8eab\u65c1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5728\u9ad8\u5904\u76f8\u671b\uff0c\u89c1\u54d1\u5df4\u5373\u5c06\u5f97\u624b\uff0c\u6b63\u81ea\u6b23\u559c\uff0c\u5ffd\u89c1\u963f\u4e5d\u629a\u7740\u7a0b\u9752\u7af9\u7684\u8eab\u5b50\uff0c\u4f0f\u5730\u5927\u54ed\uff0c\u8fd9\u4e00\u4e0b\u5012\u5927\u51fa\u4ed6\u7684\u610f\u6599\u4e4b\u5916\uff0c\u5018\u82e5\u7a0b\u9752\u7af9\u6b7b\u4e86\uff0c\u8981\u5bf9\u4ed8\u7fa4\u9f99\u65e0\u9996\u7684\u9752\u7af9\u5e2e\u5c31\u9887\u4e3a\u4e0d\u6613\uff0c\u5fd9\u7eb5\u58f0\u5927\u53eb\uff1a\u201c\u80dc\u6d77\uff0c\u5feb\u53eb\u54d1\u5df4\u8001\u5144\u56de\u6765\u3002\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u6487\u4e0b\u5bf9\u624b\uff0c\u51b2\u5230\u54d1\u5df4\u8ddf\u524d\uff0c\u6253\u624b\u52bf\u53eb\u4ed6\u56de\u6765\u3002\u54d1\u5df4\u56de\u5934\u5411\u7ad9\u5728\u5927\u8f66\u9876\u4e0a\u7684\u8881\u627f\u5fd7\u4e00\u671b\u3002\u8881\u627f\u5fd7\u62db\u62db\u624b\uff0c\u54d1\u5df4\u968f\u5373\u9000\u56de\u3002 \u3000\u3000\u8881\u627f\u5fd7\u628a\u624b\u4e2d\u534a\u6b7b\u4e0d\u6d3b\u7684\u6c99\u5929\u5e7f\u4ea4\u7ed9\u54d1\u5df4\uff0c\u7eb5\u8eab\u5165\u56f4\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u963f\u4e5d\u54ed\u7740\u53eb\u9053\uff1a\u201c\u6211\u5e08\u7236\u6b7b\u5566\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4fef\u8eab\u4e00\u63a2\u7a0b\u9752\u7af9\u7684\u9f3b\u606f\uff0c\u679c\u7136\u5df2\u65e0\u547c\u5438\uff0c\u518d\u6478\u4ed6\u80f8\u819b\uff0c\u4e00\u9897\u5fc3\u5374\u8fd8\u5728\u5fae\u5fae\u8df3\u52a8\uff0c\u7ffb\u8fc7\u4ed6\u7684\u8eab\u5b50\uff0c\u53ea\u89c1\u80cc\u4e0a\u4e94\u4e2a\u5c0f\u5b54\uff0c\u867d\u7136\u8840\u5df2\u6b62\u4f4f\uff0c\u4f46\u4e94\u5b54\u90fd\u5728\u8981\u7a74\uff0c\u9976\u662f\u7a0b\u9752\u7af9\u6b66\u529f\u7cbe\u6e5b\uff0c\u4e5f\u5df2\u62b5\u53d7\u4e0d\u4f4f\u3002\u8881\u627f\u5fd7\u8fd0\u8d77\u6df7\u5143\u529f\uff0c\u5728\u4ed6\u7684\u201c\u5929\u5e9c\u7a74\u201d\u548c\u8db3\u5e95\u201c\u6d8c\u6cc9\u7a74\u201d\u5404\u70b9\u4e00\u6307\u3002\u5185\u529b\u5230\u5904\uff0c\u7a0b\u9752\u7af9\u8840\u8109\u6d41\u8f6c\uff0c\u60a0\u60a0\u9192\u6765\uff0c\u7741\u5f00\u4e86\u773c\u775b\u3002\u963f\u4e5d\u5927\u559c\uff0c\u9ad8\u53eb\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff01\u201d \u3000\u3000\u7a0b\u9752\u7af9\u70b9\u4e86\u70b9\u5934\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u653e\u5fc3\uff01\u4f60\u5e08\u7236\u7684\u4f24\u6cbb\u5f97\u597d\u3002\u201d\u963f\u4e5d\u660e\u8273\u7684\u8138\u86cb\u4e0a\u5140\u81ea\u6302\u7740\u51e0\u6ef4\u6cea\u73e0\uff0c\u6e05\u6f88\u7684\u5927\u773c\u5374\u5df2\u5145\u6ee1\u4e86\u559c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u591a\u8c22\u4f60\u5566\u3002\u201d \u3000\u3000\u8fd9\u65f6\u9752\u9752\u3001\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u4e09\u4eba\u631f\u7740\u6c99\u5929\u5e7f\uff0c\u5df2\u9000\u5165\u9752\u7af9\u5e2e\u7684\u5708\u5b50\u3002\u5c71\u4e1c\u7fa4\u76d7\u89c1\u9996\u9886\u88ab\u64d2\uff0c\u8981\u95ef\u8fdb\u6765\u6551\u4eba\uff0c\u9752\u7af9\u5e2e\u5e2e\u4f17\u51fa\u624b\u62e6\u963b\u3002\u53cc\u65b9\u4e71\u559d\uff0c\u6df7\u4e71\u4e2d\u4ea4\u8d77\u624b\u6765\uff0c\u767b\u65f6\u4e52\u4e52\u4e53\u4e53\u6253\u5f97\u5341\u5206\u6fc0\u70c8\uff0c\u9877\u523b\u95f4\u53cc\u65b9\u5404\u6709\u6570\u5341\u4eba\u6b7b\u4f24\u3002 \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u518d\u6253\u534a\u4e2a\u65f6\u8fb0\uff0c\u53cc\u65b9\u90fd\u6b7b\u5f97\u5dee\u4e0d\u591a\u5566\uff01\u201d\u8881\u627f\u5fd7\u5fae\u7b11\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u7ad9\u5728\u94c1\u7bb1\u9876\u4e0a\u7684\u891a\u7ea2\u67f3\u626c\u81c2\u5927\u547c\uff1a\u201c\u4e0d\u597d\u5566\uff0c\u5b98\u5175\u6765\u5566\uff0c\u603b\u6709\u51e0\u5343\u4eba\uff0c\u5927\u5bb6\u5feb\u9000\u2026\u2026\u4e0d\uff0c\u6709\u4e0a\u4e07\u4eba\uff0c\u626f\u547c\uff0c\u626f\u547c\uff01\u201d\u4ed6\u7ad9\u5f97\u9ad8\uff0c\u662f\u4ee5\u9996\u5148\u77a7\u89c1\u3002\u4f17\u4eba\u90fd\u662f\u4e00\u60ca\uff0c\u5200\u67aa\u9f50\u505c\u3002 \u3000\u3000\u53ea\u89c1\u4e09\u9a91\u9a6c\u6025\u5954\u800c\u6765\u3002\u4e24\u9a91\u662f\u5c71\u4e1c\u76d7\u5e2e\u653e\u51fa\u7684\u5361\u5b50\uff0c\u4e00\u9a91\u662f\u9752\u7af9\u5e2e\u7684\u54e8\u63a2\uff0c\u4e09\u4eba\u8fde\u8fde\u547c\u5578\u3002\u9ad8\u58f0\u5927\u53eb\uff1a\u201c\u5927\u961f\u5b98\u5175\u5230\u5566\uff01\u201d \u3000\u3000\u891a\u7ea2\u67f3\u518d\u4e5f\u987e\u4e0d\u5f97\u5371\u9669\uff0c\u8e0a\u8eab\u4ece\u7bb1\u9876\u8df3\u4e0b\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u5728\u5730\u4e0b\u6253\u4e86\u4e09\u4e2a\u6eda\uff0c\u722c\u8d77\u8eab\u6765\uff0c\u53cc\u8db3\u80bf\u75db\u5f02\u5e38\uff0c\u62a2\u4e86\u4e00\u5339\u9a6c\uff0c\u7387\u9886\u5c71\u4e1c\u7fa4\u76d7\u9000\u5374\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5c06\u6c99\u5929\u5e7f\u63b7\u4e86\u8fc7\u53bb\uff0c\u7fa4\u76d7\u62a2\u4f4f\u653e\u5728\u9a6c\u80cc\uff0c\u7eb7\u7eb7\u6d8c\u5165\u6811\u6797\u3002\u9752\u7af9\u5e2e\u4e2d\u4e5f\u662f\u7af9\u54e8\u8fde\u58f0\uff0c\u62a2\u8d77\u5730\u4e0b\u6b7b\u4f24\u4eba\u4f17\uff0c\u4ecd\u662f\u5206\u6210\u56db\u961f\u9000\u4e86\u4e0b\u53bb\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u4e00\u7247\u7a7a\u5730\u4e0a\u53ea\u5269\u4e0b\u8881\u627f\u5fd7\u7b49\u4e00\u5e72\u4eba\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e00\u56de\u3000\u6177\u6168\u540c\u4ec7\u65e5 \u95f4\u5173\u767e\u6218\u65f6",content:(0,t.jsx)(t.Fragment,{children:"\u8881\u627f\u5fd7\u8df3\u4e0a\u7bb1\u9876\uff0c\u628a\u7bb1\u5b50\u9010\u53ea\u63b7\u4e0b\uff0c\u54d1\u5df4\u4e00\u4e00\u63a5\u4f4f\uff0c\u653e\u4e0a\u5927\u8f66\u3002\u9752\u9752\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4f24\u4e86\u8fd9\u8bb8\u591a\u4eba\uff0c\u53ea\u5728\u94c1\u7bb1\u5916\u9762\u6478\u5f97\u51e0\u4e0b\uff0c\u4f60\u8bf4\u662f\u8d5a\u4e86\u8fd8\u662f\u8680\u4e86\uff0c\u5f97\u8bf7\u4f60\u5927\u5e08\u54e5\u7528\u94c1\u7b97\u76d8\u6765\u7b97\u4e00\u4e0b\u4e86\u3002\u201d\u53ea\u542c\u5f97\u8fdc\u5904\u53f7\u89d2\u8fde\u58f0\uff0c\u4eba\u55a7\u9a6c\u5636\uff0c\u679c\u7136\u6709\u5927\u961f\u4eba\u9a6c\u5230\u6765\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6709\u8fd9\u8bb8\u591a\u5b98\u5175\uff0c\u76d7\u8d3c\u662f\u4e0d\u6562\u518d\u6765\u7684\u4e86\u3002\u54b1\u4eec\u8d70\u5427\u3002\u201d\u68c0\u89c6\u8f66\u8f86\u4f15\u5f79\uff0c\u5e78\u65e0\u635f\u4f24\u3002 \u3000\u3000\u6b63\u8981\u542f\u884c\uff0c\u53ea\u89c1\u6570\u767e\u540d\u5b98\u5175\u5206\u6210\u4e24\u961f\uff0c\u5f53\u5148\u51b2\u5230\u3002\u4e00\u540d\u628a\u603b\u624b\u821e\u957f\u5200\uff0c\u559d\u9053\uff1a\u201c\u5e72\u751a\u4e48\u7684\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8d76\u8def\u7684\u8001\u767e\u59d3\u3002\u201d\u90a3\u628a\u603b\u9053\uff1a\u201c\u5e72\u4e48\u8fd9\u91cc\u6709\u8840\u8ff9\uff0c\u6709\u5175\u5668\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u6b63\u6709\u5f3a\u4eba\u62e6\u8def\u6253\u52ab\uff0c\u5e78\u5f97\u5b98\u5175\u5230\u6765\uff0c\u5413\u9000\u4e86\u5f3a\u4eba\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u6709\u6570\u961f\u5b98\u5175\u524d\u53bb\u8ffd\u51fb\u9000\u8d70\u7684\u7fa4\u76d7\u3002\u90a3\u628a\u603b\u659c\u7740\u773c\u6253\u91cf\u5927\u8f66\u4e0a\u7684\u94c1\u7bb1\uff0c\u51b7\u51b7\u7684\u95ee\u9053\uff1a\u201c\u90a3\u4e9b\u662f\u751a\u4e48\u4e1c\u897f\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u662f\u884c\u674e\u3002\u201d\u90a3\u628a\u603b\u9053\uff1a\u201c\u6253\u5f00\u6765\u77a7\u77a7\u3002\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u662f\u4e9b\u968f\u8eab\u8863\u7269\uff0c\u6ca1\u751a\u4e48\u7279\u522b\u7269\u4e8b\u3002\u201d\u90a3\u628a\u603b\u9053\uff1a\u201c\u6211\u8bf4\u6253\u5f00\uff0c\u5c31\u6253\u5f00\uff0c\u5570\u5506\u751a\u4e48\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u53c8\u6ca1\u5e26\u8fdd\u7981\u72af\u6cd5\u7684\u4e1c\u897f\uff0c\u77a7\u751a\u4e48\uff1f\u201d\u90a3\u628a\u603b\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u597d\u6a2a\uff01\u201d\u5012\u63d0\u957f\u5200\uff0c\u5c06\u5200\u6746\u5939\u5934\u5939\u8111\u7838\u8fc7\u53bb\u3002\u9752\u9752\u95ea\u8eab\u907f\u5f00\u3002 \u3000\u3000\u90a3\u628a\u603b\u89c1\u5341\u53ea\u94c1\u7bb1\u7ed3\u7ed3\u5b9e\u5b9e\uff0c\u6599\u60f3\u5b9a\u662f\u88c5\u7740\u8d35\u91cd\u8d22\u7269\uff0c\u4e00\u89c1\u65e9\u5c31\u8d77\u4e86\u8d2a\u5fc3\uff0c\u8fd9\u65f6\u4e58\u673a\u53eb\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u80c6\u6562\u62d2\u6355\uff1f\u5582\uff0c\u5f1f\u5144\u4eec\uff0c\u628a\u8d43\u7269\u5145\u516c\uff01\u201d\u5b98\u5175\u62a2\u593a\u767e\u59d3\u8d22\u7269\uff0c\u90a3\u8fd8\u7528\u591a\u8bf4\uff1f\u4e00\u542c\u201c\u5145\u516c\u201d\u4e8c\u5b57\uff0c\u65e9\u6709\u5341\u4f59\u5b98\u5175\u4e00\u6d8c\u800c\u4e0a\uff0c\u4e03\u624b\u516b\u811a\u6765\u62ac\u94c1\u7bb1\u3002 \u3000\u3000\u90a3\u628a\u603b\u5b58\u5fc3\u72e0\u6bd2\uff0c\u53ea\u6015\u4e8b\u4e3b\u544a\u5230\u4e0a\u5b98\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u571f\u532a\u6d41\u5bc7\uff0c\u7adf\u6562\u6297\u62d2\u5b98\u5175\uff0c\u4e00\u6982\u683c\u6740\u52ff\u8bba\uff01\u201d\u5f53\u5373\u63d0\u5200\u6740\u6765\u3002\u8881\u627f\u5fd7\u5927\u6012\uff0c\u5fc3\u60f3\uff1a\u201c\u8981\u662f\u6211\u4eec\u4e0d\u4f1a\u6b66\u827a\uff0c\u5c82\u4e0d\u7ed9\u4ed6\u6740\u4e86\u706d\u53e3\u3002\u8fd9\u4eba\u4e0d\u77e5\u5df2\u5bb3\u4e86\u591a\u5c11\u826f\u6c11\uff1f\u201d\u5f85\u4ed6\u94a2\u5200\u780d\u5230\uff0c\u8eab\u5b50\u4fa7\u8fc7\u907f\u5f00\uff0c\u4e00\u638c\u6253\u5728\u4ed6\u80cc\u5fc3\u3002\u8fd9\u4eba\u5982\u4f55\u7981\u53d7\u5f97\u8d77\u8fd9\u4e00\u638c\uff1f\u5012\u649e\u4e0b\u9a6c\uff0c\u767b\u65f6\u6bd9\u547d\u3002 \u3000\u3000\u4f17\u5b98\u5175\u60ca\u53eb\u8d77\u6765\uff1a\u201c\u5f3a\u4eba\u62e6\u8def\uff0c\u62a2\u52ab\u6f15\u8fd0\u5566\uff0c\u62a2\u52ab\u6f15\u8fd0\u5566\uff01\u201d \u3000\u3000\u5f53\u5148\u7684\u5b98\u5175\u88ab\u9752\u9752\u3001\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u4e09\u4eba\u4e00\u51b2\uff0c\u56db\u6563\u5954\u9003\uff0c\u4f46\u540e\u9762\u5927\u961f\u4eba\u9a6c\u8ddf\u7740\u6d8c\u5230\u3002\u8881\u627f\u5fd7\u62fe\u8d77\u90a3\u628a\u603b\u7684\u5927\u5200\uff0c\u6325\u821e\u65ad\u540e\u3002\u54d1\u5df4\u7b49\u4e09\u4eba\u7387\u9886\u8f66\u961f\uff0c\u9000\u5165\u6797\u4e2d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u91d1\u94c1\u4ea4\u9e23\uff0c\u4f46\u89c1\u6811\u6797\u4e2d\u5b98\u5175\u6b63\u4e0e\u5c71\u4e1c\u7fa4\u76d7\u53ca\u9752\u7af9\u5e2e\u6253\u5f97\u706b\u70bd\u3002\u76d7\u5e2e\u867d\u7136\u90fd\u6709\u6b66\u827a\uff0c\u4f46\u6321\u4e0d\u4f4f\u5b98\u5175\u4eba\u591a\u52bf\u4f17\uff0c\u4e0d\u591a\u65f6\u5df2\u7eb7\u7eb7\u8d25\u9000\u3002\u6c99\u5929\u5e7f\u548c\u7a0b\u9752\u7af9\u90fd\u53d7\u4f24\u751a\u91cd\uff0c\u65e0\u4eba\u9886\u5934\uff0c\u7fa4\u76d7\u5404\u81ea\u4e3a\u6218\uff0c\u88ab\u5b98\u5175\u4e00\u5806\u5806\u7684\u56f4\u4f4f\u653b\u51fb\uff0c\u60e8\u547c\u58f0\u6b64\u8d77\u5f7c\u4f0f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u7b49\u5c06\u8f66\u961f\u96c6\u5728\u6811\u6797\u4e00\u89d2\u3002\u9752\u9752\u9053\uff1a\u201c\u600e\u4e48\u529e\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e2e\u5f3a\u76d7\uff0c\u6740\u5b98\u5175\uff01\u201d\u9752\u9752\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u5b88\u4f4f\uff01\u201d\u9752\u9752\u70b9\u5934\u7b54\u5e94\uff0c\u4e0e\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u4e09\u4eba\u5b88\u4f4f\u4e00\u4e2a\u5c0f\u89d2\uff0c\u5b98\u5175\u8fc7\u6765\u7acb\u5373\u683c\u6740\u3002\u4f17\u5b98\u5175\u89c1\u4e09\u4eba\u51f6\u72e0\uff0c\u4e00\u65f6\u5012\u4e5f\u4e0d\u6562\u5341\u5206\u903c\u8fd1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u98de\u8eab\u4e0a\u6811\uff0c\u5bdf\u770b\u56db\u4e0b\u5f62\u52bf\uff0c\u53ea\u89c1\u963f\u4e5d\u4e0e\u51e0\u540d\u9752\u7af9\u5e2e\u7684\u5934\u76ee\u6b63\u53d7\u6570\u5341\u540d\u5b98\u5175\u56f4\u653b\uff0c\u5f62\u52bf\u751a\u662f\u9669\u6076\uff0c\u5f53\u5373\u7eb5\u8eab\u4e0b\u6251\uff0c\u5de6\u81c2\u957f\u51fa\uff0c\u9707\u98de\u4e24\u652f\u523a\u5411\u963f\u4e5d\u7684\u94c1\u67aa\uff0c\u53eb\u9053\uff1a\u201c\u9000\u56de\u897f\u9996\u5c71\u5c97\uff01\u201d\u963f\u4e5d\u4e00\u6014\uff0c\u4e00\u540d\u519b\u5b98\u6325\u5200\u5411\u5979\u780d\u6765\u3002\u8881\u627f\u5fd7\u98de\u811a\u8e22\u53bb\u94a2\u5200\uff0c\u5f53\u80f8\u4e00\u62f3\uff0c\u5c06\u90a3\u519b\u5b98\u6253\u5f97\u53e3\u55b7\u9c9c\u8840\uff0c\u4ef0\u9762\u8dcc\u5012\u3002 \u3000\u3000\u963f\u4e5d\u5439\u8d77\u7af9\u54e8\uff0c\u9752\u7af9\u5e2e\u7684\u5e2e\u4f17\u9f50\u5411\u897f\u9000\uff0c\u6e10\u6e10\u96c6\u62e2\u3002\u8881\u627f\u5fd7\u7eb5\u6a2a\u6765\u53bb\uff0c\u547d\u5c71\u4e1c\u7fa4\u76d7\u4e5f\u5411\u897f\u9000\uff0c\u89c1\u6709\u76d7\u4f17\u7ed9\u5b98\u5175\u56f4\u4f4f\u65e0\u6cd5\u8131\u8eab\u7684\uff0c\u7acb\u5373\u51b2\u5165\u89e3\u6551\u3002\u4f17\u4eba\u4e00\u4f1a\u9f50\uff0c\u58f0\u52bf\u987f\u58ee\uff0c\u5728\u8881\u627f\u5fd7\u7387\u9886\u4e0b\u4e14\u6218\u4e14\u9000\uff0c\u4e0a\u4e86\u5c71\u5c97\u3002\u8881\u627f\u5fd7\u53c8\u7387\u9886\u4e86\u6570\u5341\u540d\u6b66\u529f\u8f83\u9ad8\u7684\u5e2e\u4f17\u76d7\u4f19\uff0c\u51b2\u4e0b\u53bb\u628a\u9752\u9752\u7b49\u8f66\u961f\u63a5\u5f15\u4e0a\u5c97\u3002\u4f17\u5b98\u5175\u5728\u5c97\u4e0b\u5450\u558a\u53eb\u56b7\uff0c\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u547d\u7fa4\u76d7\u53d1\u5c04\u6697\u5668\uff0c\u5b88\u4f4f\u5c71\u5c97\u3002\u7fa4\u76d7\u672c\u5df2\u4e00\u8d25\u6d82\u5730\uff0c\u4eba\u4eba\u6027\u547d\u96be\u4fdd\uff0c\u7a81\u7136\u6709\u4eba\u51fa\u6765\u9886\u4ed6\u4eec\u6682\u8131\u9669\u5883\uff0c\u5bf9\u4ed6\u5429\u5490\u54ea\u6709\u4e0d\u5949\u547d\u552f\u8c28\u4e4b\u7406\uff1f\u4e8c\u767e\u4f59\u540d\u5b98\u5175\u5411\u5c97\u4e0a\u51b2\u6765\uff0c\u88ab\u4e00\u9635\u6697\u5668\u5c04\u56de\uff0c\u6b7b\u4f24\u4e86\u6570\u5341\u4eba\u3002\u5b98\u5175\u5728\u5f97\u80dc\u65f6\u52c7\u5f80\u76f4\u524d\uff0c\u4e00\u53d7\u632b\u6298\uff0c\u5927\u5bb6\u6015\u6b7b\uff0c\u8c01\u80af\u820d\u547d\u653b\u5c71\uff1f\u4e2a\u4e2a\u5927\u58f0\u5450\u558a\uff0c\u6577\u884d\u957f\u5b98\uff0c\u6740\u58f0\u5012\u662f\u9707\u5929\uff0c\u5374\u662f\u524d\u4ec6\u6709\u4eba\uff0c\u540e\u7ee7\u65e0\u5175\uff0c\u518d\u4e5f\u4e0d\u89c1\u6709\u5b98\u5175\u51b2\u8fd1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5b89\u6392\u9632\u5fa1\uff0c\u547d\u8c2d\u4e8c\u5be8\u4e3b\u3001\u891a\u7ea2\u67f3\u3001\u6d2a\u80dc\u6d77\u3001\u963f\u4e5d\u56db\u4eba\u5404\u7387\u4e00\u961f\u5b88\u4f4f\u4e00\u65b9\uff0c\u4f59\u4e0b\u7684\u6551\u6b7b\u6276\u4f24\uff0c\u5c31\u5730\u4f11\u606f\u3002\u4ed6\u518d\u66ff\u7a0b\u9752\u7af9\u6309\u6469\u4e86\u4e00\u756a\uff0c\u53c8\u7ed9\u6c99\u5929\u5e7f\u63a8\u5bab\u8fc7\u8840\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e24\u4eba\u7adf\u5148\u540e\u5728\u5c71\u5c97\u4e0a\u7761\u7740\u4e86\u3002\u5c71\u4e1c\u7fa4\u76d7\u548c\u9752\u7af9\u5e2e\u5e2e\u4f17\u89c1\u9996\u9886\u65e0\u6059\uff0c\u5bf9\u8881\u627f\u5fd7\u66f4\u662f\u656c\u670d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5bf9\u9752\u9752\u9053\uff1a\u201c\u5b98\u5175\u4eba\u591a\uff0c\u4e0d\u80fd\u529b\u654c\uff0c\u53ea\u53ef\u667a\u53d6\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7528\u751a\u4e48\u8ba1\u7b56\u624d\u597d\uff1f\u201d\u8881\u627f\u5fd7\u5411\u719f\u6089\u5f53\u5730\u5730\u5f62\u7684\u76d7\u4f19\u95ee\u4e86\u4e00\u4f1a\uff0c\u518d\u8df3\u4e0a\u8f66\u9876\uff0c\u5bdf\u770b\u5b98\u5175\u961f\u5f62\uff0c\u53ea\u89c1\u5b98\u5175\u540e\u961f\u6709\u5927\u6279\u8f8e\u91cd\u8f66\u8f86\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8df3\u4e86\u4e0b\u6765\uff0c\u5bf9\u9752\u9752\u9053\uff1a\u201c\u521a\u624d\u5b98\u5175\u53eb\u751a\u4e48\u62a2\u52ab\u6f15\u8fd0\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u891a\u7ea2\u67f3\u6b63\u7531\u6dee\u9634\u53cc\u6770\u63a5\u66ff\u4e86\u4e0b\u6765\u4f11\u606f\uff0c\u542c\u8881\u627f\u5fd7\u95ee\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u5b98\u5175\uff0c\u5b9a\u662f\u8fd0\u9001\u6f15\u94f6\u53bb\u5317\u4eac\u7684\u3002\u54b1\u4eec\u521a\u597d\u9047\u4e0a\uff0c\u771f\u662f\u4e0d\u5de7\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd0\u9001\u6f15\u94f6\uff0c\u600e\u5730\u8981\u5927\u961f\u5b98\u5175\uff1f\u201d \u3000\u3000\u891a\u7ea2\u67f3\u9053\uff1a\u201c\u73b0\u4eca\u5929\u4e0b\u5927\u4e71\uff0c\u7fa4\u96c4\u5e76\u8d77\uff0c\u54ea\u4e00\u5904\u6ca1\u5f00\u5c71\u7acb\u67dc\u7684\u8c6a\u6770\uff1f\u671d\u5ef7\u5168\u9760\u6c5f\u5357\u8fd0\u53bb\u7684\u6f15\u7c73\u94f6\u4e24\u53d1\u9977\u53d1\u7cae\u3002\u5d07\u796f\u65e2\u8981\u9632\u5fa1\u8fbd\u4e1c\u7684\u6ee1\u6d32\u5175\uff0c\u53c8\u8981\u5e94\u4ed8\u95ef\u738b\u548c\u5404\u8def\u82f1\u96c4\uff0c\u8fd9\u6f15\u94f6\u662f\u4ed6\u547d\u6839\u5b50\uff0c\u82e5\u662f\u51fa\u4e86\u5c94\u5b50\uff0c\u4ed6\u7684\u9f99\u5ef7\u4e5f\u5750\u4e0d\u7a33\u4e86\uff0c\u81ea\u7136\u8981\u591a\u6d3e\u4eba\u9a6c\u62a4\u9001\u3002\u6f15\u7c73\u94f6\u4e24\u672c\u6765\u90fd\u7531\u8fd0\u6cb3\u6c34\u8fd0\uff0c\u60f3\u662f\u7687\u5e1d\u8981\u94b1\u8981\u5f97\u6025\u4e86\uff0c\u624d\u7531\u9646\u8def\u8d76\u8fd0\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e9b\u5b98\u5175\u8eab\u4e0a\u6311\u7740\u8fd9\u6837\u91cd\u7684\u62c5\u5b50\uff0c\u5c45\u7136\u8fd8\u6765\u591a\u7ba1\u95f2\u4e8b\uff0c\u8ddf\u4eba\u4e3a\u96be\u3002\u201d\u891a\u7ea2\u67f3\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4ee5\u4e3a\u54b1\u4eec\u8f6c\u773c\u4e2a\u4e2a\u5c31\u64d2\uff0c\u53ea\u987b\u7ed9\u54b1\u4eec\u5b89\u4e0a\u51e0\u4e2a\u751a\u4e48\u738b\u3001\u751a\u4e48\u661f\u7684\u5389\u5bb3\u532a\u53f7\uff0c\u594f\u62a5\u4e0a\u53bb\uff0c\u5c82\u4e0d\u662f\u5927\u529f\u4e00\u4ef6\uff1f\u201d\u53c8\u9053\uff1a\u201c\u6211\u4eec\u672c\u662f\u571f\u532a\u5f3a\u4eba\uff0c\u5012\u4e5f\u4e0d\u662f\u51a4\u6789\uff0c\u53ea\u53ef\u60dc\u7d2f\u4e86\u76f8\u516c\u3002\u201d\u8881\u627f\u5fd7\u53f9\u9053\uff1a\u201c\u5b98\u903c\u6c11\u53cd\uff0c\u4eca\u65e5\u53ef\u6559\u6211\u4eb2\u8eab\u9047\u4e0a\u4e86\u3002\u201d\u6c89\u541f\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u5904\u5411\u897f\u5317\u6709\u4e2a\u5ce1\u53e3\uff0c\u54b1\u4eec\u4ece\u90a3\u8fb9\u51b2\u51fa\u53bb\u5427\u3002\u201d \u3000\u3000\u891a\u7ea2\u67f3\u8fd9\u65f6\u5bf9\u4ed6\u5df2\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u54ea\u4f1a\u6709\u4f55\u5f02\u8bae\uff0c\u4fbf\u9053\uff1a\u201c\u8bf7\u8881\u76f8\u516c\u5429\u5490\uff0c\u5927\u4f19\u513f\u9f50\u542c\u53f7\u4ee4\u3002\u201d\u8881\u627f\u5fd7\u5728\u5730\u4e0a\u753b\u4e86\u56fe\uff0c\u8ba1\u8bae\u7a81\u56f4\u4e4b\u7b56\u5df2\u5b9a\uff0c\u4fbf\u5373\u5206\u62e8\u4eba\u624b\u3002\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7fa4\u76d7\u9f50\u58f0\u53d1\u558a\u3002\u8881\u627f\u5fd7\u548c\u54d1\u5df4\u5f53\u5148\u5f00\u8def\uff0c\u7387\u9886\u4f17\u4eba\u51b2\u4e0b\u5c97\u53bb\u3002 \u3000\u3000\u5b98\u5175\u672c\u5df2\u6020\u61c8\u75b2\u5026\uff0c\u9664\u4e86\u5c11\u6570\u5949\u547d\u5b88\u5fa1\uff0c\u4f59\u4eba\u5747\u5df2\u5c31\u5730\u5750\u5012\u4f11\u606f\uff0c\u5ffd\u89c1\u7fa4\u76d7\u9aa4\u7136\u6d8c\u5230\uff0c\u6765\u52bf\u51f6\u731b\uff0c\u7a0d\u52a0\u62b5\u6321\uff0c\u5c31\u88ab\u51b2\u7834\u4e00\u9053\u53e3\u5b50\u3002\u7fa4\u76d7\u5411\u5ce1\u53e3\u76f4\u5954\uff0c\u5b98\u5175\u53eb\u558a\u7740\u968f\u540e\u8ffd\u6765\u3002\u8ffd\u4e86\u4e00\u9635\uff0c\u6bbf\u540e\u7684\u6570\u5341\u540d\u76d7\u5e2e\u5ffd\u7136\u56de\u8eab\u9080\u6597\uff0c\u628a\u5b98\u5175\u8ffd\u52bf\u6321\u4e86\u4e00\u6321\u3002\u5f85\u5f97\u5b98\u5175\u5927\u961f\u653b\u5230\uff0c\u6bbf\u540e\u7684\u76d7\u5e2e\u4e5f\u5df2\u9000\u5165\u5ce1\u53e3\u3002 \u3000\u3000\u90a3\u5ce1\u53e3\u4e24\u65c1\u90fd\u662f\u9ad8\u5cf0\u5ced\u58c1\uff0c\u5f62\u52bf\u9669\u6076\uff0c\u5b98\u5175\u4e00\u8ffd\u5165\u5ce1\u53e3\uff0c\u7387\u961f\u957f\u5b98\u4e0b\u4ee4\u6682\u505c\uff0c\u4ee5\u9632\u4e2d\u4f0f\u3002\u5ffd\u7136\u95f4\u524d\u9762\u5927\u8f66\u4e2d\u4e00\u53ea\u94c1\u7bb1\u6eda\u4e86\u4e0b\u6765\uff0c\u7bb1\u76d6\u7ffb\u5f00\uff0c\u9053\u4e0a\u4e22\u6ee1\u4e86\u73e0\u5b9d\u73cd\u7269\uff0c\u95ea\u95ea\u53d1\u5149\u3002\u90a3\u7edf\u5175\u7684\u603b\u5175\u4e00\u89c1\u5927\u559c\uff0c\u4e0b\u4ee4\u6025\u8ffd\uff0c\u8981\u628a\u5341\u53ea\u5b9d\u7bb1\u5168\u90fd\u62a2\u4e86\u4e0b\u6765\u3002\u8ffd\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u7fa4\u76d7\u629b\u4e0b\u8863\u7532\u5175\u5668\uff0c\u4e71\u7a9c\u4e71\u5954\uff0c\u9053\u65c1\u4e22\u6ee1\u4e86\u91d1\u7816\u94f6\u952d\u3002\u4f17\u5b98\u5175\u4f60\u62a2\u6211\u593a\uff0c\u4e71\u6210\u4e00\u56e2\u3002\u90a3\u603b\u5175\u89c1\u7fa4\u76d7\u6e83\u6563\uff0c\u8fde\u5175\u5668\u4e5f\u968f\u5730\u4e71\u4e22\uff0c\u4e0d\u518d\u5b58\u9632\u5907\u4e4b\u5ff5\uff0c\u4e00\u610f\u8981\u62a2\u5b9d\u7bb1\uff0c\u4e0b\u4ee4\u524d\u3001\u4e2d\u3001\u540e\u4e09\u961f\u9f50\u8d76\u3002 \u3000\u3000\u6709\u5206\u6559\uff1a\u6297\u5916\u654c\u4e0d\u59a8\u843d\u540e\uff0c\u62a2\u91d1\u94f6\u52a1\u5fc5\u4e89\u5148\u3002 \u3000\u3000\u8fd9\u65f6\u8881\u627f\u5fd7\u5df2\u98de\u8eab\u8dc3\u4e0a\u5ced\u58c1\uff0c\u624b\u8db3\u5e76\u7528\uff0c\u62c9\u7740\u77f3\u58c1\u4e0a\u7684\u85e4\u679d\u6811\u6761\uff0c\u6284\u5411\u5b98\u5175\u540e\u8def\u3002\u8d70\u4e86\u4e00\u4f1a\uff0c\u679c\u89c1\u5b98\u5175\u961f\u4e2d\u8f66\u8f86\u4e00\u8f86\u63a5\u7740\u4e00\u8f86\uff0c\u873f\u8712\u800c\u6765\uff0c\u4e0d\u8ba1\u5176\u6570\uff0c\u8f66\u8f86\u90fd\u7528\u9ec4\u5e03\u8499\u4f4f\uff0c\u8f66\u4e0a\u63d2\u4e86\u65d7\u5e1c\uff0c\u65d7\u4e0a\u5199\u7684\u662f\u201c\u5927\u660e\u6c5f\u5357\u6f15\u8fd0\u201d\u51e0\u4e2a\u7ea2\u5b57\uff0c\u4ece\u4e0a\u9762\u653e\u773c\u671b\u4e0b\u53bb\uff0c\u8f66\u961f\u76f4\u5982\u4e00\u6761\u5176\u957f\u65e0\u6bd4\u7684\u9ec4\u9f99\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u6b64\u60c5\u52bf\uff0c\u4e0d\u89c9\u53c8\u60ca\u53c8\u559c\uff0c\u60ca\u7684\u662f\u5b98\u5175\u52bf\u5927\uff0c\u4e0d\u6613\u5bf9\u654c\uff0c\u559c\u7684\u662f\u5982\u80fd\u52ab\u4e0b\u6f15\u8fd0\uff0c\u90a3\u771f\u662f\u5bf9\u5927\u4ec7\u4eba\u5d07\u796f\u7687\u5e1d\u4e00\u4e2a\u5f53\u5934\u731b\u51fb\uff0c\u95ef\u738b\u4e49\u5175\u5c31\u66f4\u6613\u6210\u4e8b\uff0c\u5b9e\u662f\u5947\u529f\u4e00\u4ef6\u3002\u773c\u89c1\u5761\u4e0b\u6811\u6728\u8302\u5bc6\uff0c\u5f53\u5373\u7a7f\u6797\u800c\u4e0b\uff0c\u8981\u5c31\u8fd1\u770b\u6e05\u695a\u8f66\u961f\u3002\u4e0d\u4e00\u523b\uff0c\u9760\u8fd1\u5b98\u5175\u961f\u4f0d\uff0c\u501f\u7740\u6811\u6728\u906e\u63a9\uff0c\u8fde\u5b98\u5175\u7684\u8bf4\u8bdd\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u8f66\u8f86\u8fde\u7ef5\u4e0d\u65ad\uff0c\u9686\u9686\u800c\u8fc7\uff0c\u8fc7\u4e86\u597d\u4e00\u9635\uff0c\u5ffd\u542c\u5f97\u8f66\u884c\u8f9a\u8f9a\u4e4b\u58f0\u6e10\u8f7b\uff0c\u8f66\u4e2d\u6240\u88c5\u4f3c\u4e4e\u5df2\u975e\u94f6\u5b50\uff0c\u4ece\u6811\u6728\u7a7a\u9699\u4e2d\u5411\u5916\u671b\u53bb\uff0c\u89c1\u662f\u767e\u4f59\u8f86\u56da\u8f66\u3002\u8f66\u4e2d\u56da\u5f92\u53cc\u624b\u53cd\u7f1a\uff0c\u76d8\u819d\u800c\u5750\uff0c\u6bcf\u8f86\u8f66\u4e0a\u90fd\u63d2\u7740\u4e00\u9762\u767d\u65d7\uff0c\u5199\u7740\u201c\u5019\u65a9\u5de8\u5bc7\u67d0\u67d0\u67d0\u201d\u7b49\u5b57\u6837\uff0c\u53c8\u662f\u751a\u4e48\u201c\u6c5f\u6d0b\u5927\u76d7\u201d\u3001\u201c\u6d41\u5bc7\u5934\u76ee\u201d\u3001\u201c\u53cd\u53db\u9006\u9996\u201d\u3001\u201c\u6dee\u5357\u5de8\u8d3c\u201d\u7b49\u7b49\uff0c\u663e\u89c1\u90fd\u662f\u53cd\u6297\u671d\u5ef7\u7684\u9965\u6c11\u6216\u5c71\u5be8\u76d7\u9b41\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u987b\u52a0\u4ee5\u642d\u6551\uff0c\u4f46\u5982\u4f55\u4e0b\u624b\u624d\u662f\uff1f\u201d \u3000\u3000\u6b63\u81ea\u5bfb\u601d\uff0c\u5ffd\u89c1\u4e00\u8f86\u8f66\u5b50\u8fc7\u6765\uff0c\u65d7\u4e0a\u5199\u7740\u201c\u5019\u65a9\u53cd\u9006\u5b59\u4ef2\u5bff\u4e00\u540d\u201d\u4e5d\u5b57\uff0c\u8881\u627f\u5fd7\u5927\u5403\u4e00\u60ca\uff0c\u8ffd\u4e86\u51e0\u6b65\u7ec6\u770b\uff0c\u89c1\u8f66\u4e2d\u6240\u5750\u7684\u679c\u7136\u4fbf\u662f\u5b59\u4ef2\u5bff\u3002\u4f46\u89c1\u4ed6\u4e24\u9b13\u6591\u767d\uff0c\u6ee1\u8138\u98ce\u971c\u4e4b\u8272\uff0c\u8f83\u4e4b\u6614\u65e5\u5728\u5723\u5cf0\u5d82\u4e0a\u7387\u9886\u540c\u888d\u796d\u5960\u6545\u5e05\u4e4b\u65f6\uff0c\u5df2\u82cd\u8001\u5f97\u591a\uff0c\u4f46\u4e00\u526f\u6177\u6168\u98ce\u81f4\uff0c\u867d\u5728\u96be\u4e2d\uff0c\u4ecd\u662f\u4e0d\u51cf\u5f53\u5e74\u3002\u8881\u627f\u5fd7\u60ca\u8bb6\u672a\u5b9a\uff0c\u53ea\u89c1\u540e\u9762\u56da\u8f66\u4e2d\u63a8\u6765\u7684\u53c8\u90fd\u662f\u7236\u4eb2\u65e7\u90e8\uff0c\u5f53\u65f6\u6559\u5bfc\u629a\u517b\u81ea\u5df1\u7684\u502a\u6d69\u3001\u6731\u5b89\u56fd\u3001\u7f57\u5927\u5343\u4e09\u4eba\u90fd\u5728\u5176\u5185\uff0c\u53ea\u662f\u4e0d\u89c1\u5e94\u677e\u3002\u8881\u627f\u5fd7\u4e00\u9635\u5fc3\u9178\uff0c\u968f\u53c8\u6697\u6697\u6b22\u559c\uff1a\u201c\u8001\u5929\u7237\u6709\u773c\uff0c\u6559\u6211\u4eca\u65e5\u649e\u89c1\u4f17\u4f4d\u53d4\u53d4\u3002\u201d \u3000\u3000\u4e0d\u4e45\u56da\u8f66\u8fc7\u5b8c\uff0c\u8881\u627f\u5fd7\u5411\u4e0a\u5954\u4e86\u6570\u4e08\uff0c\u75be\u5411\u540e\u8ffd\u3002\u5b98\u5175\u671b\u89c1\uff0c\u9f13\u566a\u8d77\u6765\uff0c\u6709\u7684\u4fbf\u53d1\u7bad\u5c04\u6765\u3002\u4f46\u8881\u627f\u5fd7\u8eab\u6cd5\u5feb\u6377\uff0c\u7bad\u679d\u5230\u65f6\uff0c\u4eba\u65e9\u4e0d\u89c1\u3002\u4ed6\u5954\u51fa\u6570\u5341\u4e08\uff0c\u5b98\u5175\u961f\u4f0d\u5df2\u5c3d\uff0c\u6700\u540e\u4e00\u540d\u519b\u5b98\u9a91\u5728\u9a6c\u4e0a\uff0c\u624b\u63d0\u5927\u5200\u62bc\u961f\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u62ff\u4f4f\u8fd9\u519b\u5b98\uff0c\u5148\u6363\u4e71\u4e00\u9635\uff0c\u7136\u540e\u4e58\u673a\u76f8\u6551\u5b59\u53d4\u53d4\u3001\u6731\u53d4\u53d4\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6b63\u8981\u98de\u8eab\u8dc3\u4e0b\uff0c\u5ffd\u7136\u671b\u89c1\u8fdc\u5904\u5c18\u571f\u98de\u626c\uff0c\u51e0\u9a91\u9a6c\u5954\u9a70\u800c\u6765\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u540e\u9762\u8fd8\u6709\u63a5\u5e94\uff0c\u7b49\u4ed6\u4eec\u8fc7\u6765\u770b\u4e2a\u660e\u767d\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e0d\u4e00\u523b\u4e94\u9a91\u9a6c\u5954\u5230\uff0c\u5f53\u5148\u4e00\u4eba\u662f\u4e2a\u5973\u5b50\uff0c\u5374\u662f\u98de\u5929\u9b54\u5973\u5b59\u4ef2\u541b\uff0c\u540e\u9762\u56db\u4eba\u6b63\u662f\u4e8c\u5e08\u5144\u5f52\u8f9b\u6811\u592b\u5987\u4ee5\u53ca\u6885\u5251\u548c\u3001\u5218\u57f9\u751f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u89c1\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff01\u201d\u98de\u8eab\u843d\u4e0b\uff0c\u843d\u5728\u5f52\u8f9b\u6811\u592b\u5987\u9a6c\u524d\u3002 \u3000\u3000\u5f52\u6c0f\u592b\u5987\u4e00\u8d77\u52d2\u9a6c\uff0c\u89c1\u5230\u662f\u4ed6\uff0c\u5f52\u4e8c\u5a18\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u55ef\uff01\u662f\u4f60\uff0c\u6709\u751a\u4e48\u8d35\u5e72\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5c0f\u5f1f\u6709\u4ef6\u6025\u4e8b\uff0c\u6c42\u5e08\u54e5\u5e08\u5ac2\u51e0\u4f4d\u4f38\u624b\u76f8\u52a9\u3002\u201d\u5f52\u4e8c\u5a18\u9053\uff1a\u201c\u6211\u4eec\u81ea\u5df1\u4e5f\u6709\u8981\u4e8b\uff0c\u6ca1\u7a7a\uff01\u201d\u548c\u5f52\u8f9b\u6811\u4e8c\u4eba\u4e00\u63d0\u7f30\uff0c\u53cc\u9a91\u4ece\u4ed6\u4e24\u4fa7\u64e6\u8fc7\uff0c\u5411\u524d\u51b2\u4e86\u8fc7\u53bb\u3002\u6885\u5251\u548c\u62f1\u624b\u53eb\u58f0\uff1a\u201c\u5e08\u53d4\uff01\u201d\u8ddf\u7740\u5e08\u7236\u5e08\u5a18\u53bb\u4e86\u3002 \u3000\u3000\u5218\u57f9\u751f\u8df3\u4e0b\u9a6c\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u5e08\u5a18\u6b63\u6709\u4e00\u4ef6\u8981\u7d27\u4e8b\u3002\u5f1f\u5b50\u529e\u4e86\u4e4b\u540e\uff0c\u7acb\u523b\u8fc7\u6765\u542c\u5e08\u53d4\u5dee\u9063\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u4e0d\u5fc5\u4e86\uff0c\u6211\u501f\u5750\u4e00\u4e0b\u5218\u5927\u54e5\u7684\u7272\u53e3\u3002\u201d\u5218\u57f9\u751f\u9053\uff1a\u201c\u5e08\u53d4\u8bf7\u7528\u3002\u201d\u5c06\u7f30\u7ef3\u9012\u5c06\u8fc7\u53bb\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4fe9\u5408\u9a91\uff0c\u8ffd\u4e0a\u524d\u9762\u5b98\u5175\u5c31\u884c\u4e86\u3002\u201d\u8bf4\u7740\u98de\u8eab\u4e0a\u9a6c\u3002\u5218\u57f9\u751f\u4e5f\u8df3\u4e0a\u9a6c\u6765\u3002\u8881\u627f\u5fd7\u53cc\u817f\u4e00\u5939\uff0c\u90a3\u9a6c\u53d1\u8db3\u5954\u9a70\u3002 \u3000\u3000\u5218\u57f9\u751f\u95ee\u9053\uff1a\u201c\u5e08\u53d4\u8ffd\u5b98\u5175\u5e72\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6551\u4eba\uff01\u201d \u3000\u3000\u5218\u57f9\u751f\u559c\u9053\uff1a\u201c\u90a3\u597d\u6781\u5566\uff0c\u6211\u4eec\u4e5f\u6b63\u8981\u5bfb\u5b98\u5175\u7684\u6666\u6c14\u3002\u201d\u8881\u627f\u5fd7\u4e00\u542c\u5927\u559c\uff0c\u50ac\u9a6c\u6025\u884c\uff0c\u4e0d\u4e00\u4f1a\u5df2\u671b\u89c1\u62bc\u961f\u519b\u5b98\u7684\u80cc\u5f71\u3002\u4f46\u4e0d\u89c1\u5f52\u8f9b\u6811\u7b49\u4eba\uff0c\u60f3\u5df2\u62a2\u8fc7\u4e86\u5934\u3002\u8881\u627f\u5fd7\u7eb5\u9a6c\u5411\u524d\u6025\u51b2\u3002 \u3000\u3000\u62bc\u961f\u7684\u6e38\u51fb\u542c\u5f97\u8eab\u540e\u9a6c\u8e44\u58f0\u75be\uff0c\u56de\u5934\u671b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u5f71\u4ece\u9a6c\u80cc\u8dc3\u8d77\uff0c\u6251\u5c06\u8fc7\u6765\u3002\u4ed6\u5927\u5403\u4e00\u60ca\uff0c\u6325\u8d77\u5927\u5200\u5f80\u7a7a\u4e2d\u6a2a\u626b\uff0c\u6ee1\u62df\u5c06\u8fd9\u4eba\u4e00\u5200\u65a9\u4e3a\u4e24\u622a\uff0c\u5c82\u77e5\u8881\u627f\u5fd7\u53f3\u624b\u524d\u4f38\uff0c\u62a2\u4f4f\u5200\u67c4\uff0c\u8eab\u5b50\u5df2\u843d\u5728\u4ed6\u9a6c\u4e0a\uff0c\u5de6\u624b\u65e9\u70b9\u4e2d\u4ed6\u540e\u5fc3\u7a74\u9053\u3002\u90a3\u6e38\u51fb\u53ea\u89c9\u80cc\u5fc3\u9178\u9ebb\uff0c\u8981\u5f85\u6323\u624e\uff0c\u5374\u5df2\u52a8\u5f39\u4e0d\u5f97\u3002\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u8981\u6b7b\u8fd8\u662f\u8981\u6d3b\uff1f\u201d\u90a3\u6e38\u51fb\u98a4\u58f0\u9053\uff1a\u201c\u5927\u2026\u2026\u5927\u738b\u7237\u9976\u547d\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5feb\u4e0b\u4ee4\uff0c\u53eb\u540e\u961f\u56da\u8f66\u90fd\u505c\u4e0b\u6765\u3002\u201d\u90a3\u6e38\u51fb\u53ea\u5f97\u4f9d\u8a00\u4e0b\u4ee4\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u5f52\u8f9b\u6811\u592b\u5987\u4ece\u6811\u6797\u4e2d\u51b2\u51fa\uff0c\u5e08\u5f92\u4e94\u4eba\u62bd\u51fa\u5175\u5203\uff0c\u5f80\u5b98\u5175\u961f\u91cc\u6740\u53bb\u3002\u961f\u4f0d\u767b\u65f6\u5927\u4e71\u3002\u8881\u627f\u5fd7\u672c\u62df\u8feb\u90a3\u6e38\u51fb\u6307\u6325\u961f\u4f0d\uff0c\u8ba9\u4f17\u5b98\u5175\u6df7\u4e71\u4e2d\u81ea\u76f8\u6b8b\u6740\uff0c\u54ea\u77e5\u5f52\u8f9b\u6811\u7b49\u5ffd\u6765\u52a8\u624b\uff0c\u5b98\u5175\u540e\u961f\u4e00\u4e71\uff0c\u8fd9\u8ba1\u7b56\u5374\u884c\u4e0d\u5f97\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u62a2\u4e86\u4e24\u67c4\u77ed\u65a7\uff0c\u5954\u5230\u5b59\u4ef2\u5bff\u56da\u8f66\u8fb9\uff0c\u5288\u5f00\u8f66\u5b50\uff0c\u5927\u53eb\uff1a\u201c\u5b59\u53d4\u53d4\uff0c\u6211\u662f\u8881\u627f\u5fd7\u3002\u201d\u5b59\u4ef2\u5bff\u5982\u5728\u68a6\u4e2d\uff0c\u4e00\u9635\u8ff7\u60d8\u3002\u8881\u627f\u5fd7\u53c8\u5df2\u628a\u6731\u5b89\u56fd\u3001\u502a\u6d69\u3001\u7f57\u5927\u5343\u7b49\u4eba\u6551\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u90fd\u662f\u8eab\u7ecf\u767e\u6218\u7684\u6b66\u5c06\uff0c\u73b0\u4eca\u867d\u5df2\u5e74\u8001\uff0c\u4f46\u82f1\u98ce\u72b9\u5b58\uff0c\u62a2\u4e86\u5175\u5668\uff0c\u6709\u7684\u4e71\u6740\u5b98\u5175\uff0c\u6709\u7684\u5288\u5f00\u56da\u8f66\u6551\u4eba\uff0c\u4e0d\u4e00\u523b\uff0c\u767e\u4f59\u8f86\u56da\u8f66\u9f50\u90fd\u5288\u70c2\uff0c\u653e\u51fa\u767e\u4f59\u6761\u597d\u6c49\u6765\u3002\u5176\u4e2d\u4e09\u6570\u5341\u4eba\u662f\u8881\u5d07\u7115\u90e8\u5c5e\u7684\u201c\u5c71\u5b97\u201d\u65e7\u4fa3\uff0c\u542c\u8bf4\u8d76\u6765\u76f8\u6551\u7684\u662f\u7763\u5e08\u516c\u5b50\uff0c\u65e0\u4e0d\u5927\u4e3a\u632f\u594b\uff0c\u5f53\u4e0b\u4e00\u9635\u780d\u6740\uff0c\u5c06\u5b98\u5175\u540e\u961f\u6740\u5f97\u4e03\u96f6\u516b\u843d\uff0c\u5411\u524d\u9003\u7a9c\u3002 \u3000\u3000\u8fd9\u65f6\u5b98\u5175\u524d\u961f\u4e5f\u5df2\u53d1\u73b0\u524d\u9762\u5de8\u77f3\u62e6\u8def\uff0c\u4e0d\u80fd\u901a\u884c\uff0c\u767b\u65f6\u4e24\u5934\u5927\u4e71\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5b98\u5175\u867d\u7136\u52bf\u4e71\uff0c\u4f46\u4eba\u6570\u4f17\u591a\uff0c\u903c\u5f97\u7d27\u4e86\uff0c\u5f53\u771f\u62da\u8d77\u547d\u6765\uff0c\u5374\u662f\u65e0\u6cd5\u62b5\u6321\uff0c\u5f53\u4e0b\u6487\u4e86\u53cc\u65a7\uff0c\u5c55\u5f00\u8f7b\u529f\uff0c\u8fde\u5954\u5e26\u8dc3\uff0c\u5728\u4e00\u957f\u5217\u6f15\u8fd0\u8f66\u8f86\u9876\u4e0a\u8dd1\u5c06\u8fc7\u53bb\u3002\u884c\u51fa\u91cc\u8bb8\uff0c\u89c1\u9886\u961f\u7684\u603b\u5175\u5b98\u5934\u6234\u94c1\u76d4\uff0c\u6b63\u624b\u821e\u957f\u5200\uff0c\u6307\u6325\u4f5c\u6218\u3002\u8881\u627f\u5fd7\u75be\u5954\u800c\u524d\uff0c\u5c06\u4e24\u540d\u4e0a\u524d\u62e6\u963b\u7684\u4eb2\u5175\u63a8\u5165\u4e86\u5c71\u5751\uff0c\u8dc3\u4e0a\u90a3\u603b\u5175\u5750\u9a91\u7684\u9a6c\u81c0\u3002\u90a3\u603b\u5175\u56de\u5200\u6765\u780d\uff0c\u8881\u627f\u5fd7\u631f\u624b\u4fbf\u593a\uff0c\u54ea\u77e5\u8fd9\u603b\u5175\u4e00\u4e2a\u7b4b\u6597\u4ece\u9a6c\u80cc\u4e0a\u7ffb\u4e86\u4e0b\u53bb\uff0c\u7adf\u6ca1\u80fd\u6293\u4f4f\u4ed6\u7684\u624b\u8155\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u9053\uff1a\u201c\u6ca1\u6599\u60f3\u5b98\u519b\u4e4b\u4e2d\u8fd8\u6709\u5982\u6b64\u597d\u624b\u3002\u201d\u5de6\u624b\u4e00\u626c\uff0c\u4e09\u679a\u94dc\u94b1\u53d1\u4e86\u51fa\u53bb\u3002\u4f7f\u7684\u662f\u6728\u6851\u6240\u6388\u53d1\u56f4\u68cb\u5b50\u7684\u624b\u6cd5\u3002\u90a3\u603b\u5175\u4e00\u4e00\u7528\u957f\u5200\u683c\u5f00\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\u672c\u4e8b\uff01\u4f60\u518d\u683c\u683c\u770b\u3002\u201d\u53cc\u624b\u8fde\u6325\uff0c\u4e09\u4e5d\u4e8c\u5341\u4e03\u679a\u94dc\u94b1\u5206\u4e0a\u4e2d\u4e0b\u4e09\u8def\u540c\u65f6\u6253\u5230\u3002\u5c31\u7b97\u6b66\u6797\u9ad8\u624b\uff0c\u8fd9\u4e00\u6765\u4e5f\u4e0d\u6613\u62b5\u6321\uff0c\u90a3\u603b\u5175\u6b66\u827a\u867d\u7136\u9ad8\u5f3a\uff0c\u5374\u54ea\u91cc\u8eb2\u5f97\u5f00\u8fd9\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u7684\u624b\u6cd5\uff1f\u5f53\u5577\u4e00\u58f0\uff0c\u5148\u662f\u957f\u5200\u8131\u624b\uff0c\u63a5\u7740\u819d\u5f2f\u3001\u8170\u80c1\u3001\u80cc\u5fc3\u3001\u8db3\u80eb\u5404\u5904\u90fd\u4e2d\u4e86\u94dc\u94b1\uff0c\u7adf\u671d\u7740\u8881\u627f\u5fd7\u8fce\u9762\u8dea\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u793c\uff01\u201d\u4f38\u624b\u633d\u4f4f\u4ed6\u5de6\u81c2\u3002\u90a3\u603b\u5175\u5f53\u80f8\u4e00\u62f3\uff0c\u52bf\u6025\u529b\u52b2\u3002\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u5c31\u8ba9\u4f60\u6253\u4e00\u62f3\u51fa\u6c14\u3002\u201d\u8fd9\u4e00\u62f3\u660e\u660e\u6253\u5728\u4ed6\u80f8\u524d\uff0c\u5374\u5982\u6253\u4e2d\u4e00\u56e2\u68c9\u82b1\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u5168\u65e0\u7740\u529b\u5904\u3002\u8881\u627f\u5fd7\u8fd0\u8d77\u5185\u529b\uff0c\u63d0\u8d77\u90a3\u603b\u5175\u5f80\u4e0a\u629b\u51fa\u3002\u53ea\u89c1\u4ed6\u5c31\u5982\u65ad\u7ebf\u98ce\u7b5d\u822c\u5f80\u4e0a\u76f4\u98de\uff0c\u5168\u5b98\u5175\u9ad8\u58f0\u5927\u53eb\u8d77\u6765\u3002\u90a3\u603b\u5175\u81ea\u5206\u8fd9\u4e00\u4e0b\u5fc5\u6b7b\uff0c\u95ed\u4f4f\u4e86\u53cc\u773c\uff0c\u54ea\u77e5\u843d\u4e0b\u65f6\u88ab\u4eba\u53cc\u624b\u6258\u4f4f\uff0c\u7741\u5f00\u773c\u6765\uff0c\u4ecd\u662f\u90a3\u4e2a\u4e66\u751f\u6253\u626e\u7684\u5c11\u5e74\u3002\u4ed6\u77e5\u6b64\u4eba\u6b66\u529f\u6bd4\u5df1\u9ad8\u51fa\u5341\u500d\uff0c\u65e2\u7136\u843d\u5165\u4ed6\u624b\uff0c\u65e0\u53ef\u6297\u62d2\uff0c\u751f\u6b7b\u53ea\u597d\u7f6e\u4e4b\u5ea6\u5916\u3002\u4f55\u51b5\u5c31\u7b97\u786c\u8981\u7f6e\u4e4b\u5ea6\u5185\uff0c\u5374\u4e5f\u65e0\u4ece\u7f6e\u8d77\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u4e0b\u4ee4\u5168\u4f53\u5b98\u5175\u629b\u4e0b\u5175\u5203\uff0c\u9976\u4f60\u4eec\u4e0d\u6b7b\u3002\u201d\u90a3\u603b\u5175\u5fc3\u60f3\uff1a\u201c\u8fd9\u6f15\u8fd0\u4f55\u7b49\u8981\u7d27\uff0c\u7ed9\u76d7\u8d3c\u52ab\u4e86\u53bb\uff0c\u53cd\u6b63\u4e5f\u662f\u6b7b\u7f6a\u3002\u201d \u3000\u3000\u4e8e\u662f\u9888\u9879\u4e00\u633a\uff0c\u6717\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981\u6740\u4fbf\u6740\uff0c\u4f55\u5fc5\u591a\u8a00\u3002\u201d\u8881\u627f\u5fd7\u4e00\u7b11\uff0c\u624b\u4e00\u4f7f\u52b2\uff0c\u53c8\u5c06\u4ed6\u8eab\u8eaf\u629b\u5411\u7a7a\u4e2d\uff0c\u843d\u4e0b\u6765\u65f6\u63a5\u7740\u518d\u629b\uff0c\u8fde\u629b\u4e86\u4e09\u6b21\uff0c\u90a3\u603b\u5175\u5df2\u5934\u6655\u8111\u80c0\uff0c\u4e0d\u77e5\u8eab\u5728\u4f55\u5904\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u4e0b\u4ee4\uff0c\u4f60\u6b7b\u4e86\uff0c\u90e8\u4e0b\u4e5f\u90fd\u6d3b\u4e0d\u6210\u3002\u4e0d\u5982\u964d\u4e86\u5427\u3002\u201d\u90a3\u603b\u5175\u4e00\u60f3\uff0c\u773c\u4e0b\u53ea\u6709\u8fd9\u6761\u662f\u6d3b\u8def\uff0c\u53ea\u5f97\u70b9\u4e86\u70b9\u5934\u3002\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u4f60\u8d35\u59d3\uff1f\u201d\u90a3\u603b\u5175\u9053\uff1a\u201c\u5c0f\u5c06\u59d3\u6c34\u3002\u201d\u4ed6\u5b9a\u4e00\u5b9a\u795e\uff0c\u547d\u4eb2\u5175\u628a\u624b\u4e0b\u7684\u526f\u5c06\u3001\u53c2\u5c06\u3001\u6e38\u51fb\u3001\u90fd\u53f8\u7b49\u90fd\u53eb\u4e86\u6765\uff0c\u4f17\u5c06\u542c\u5f97\u8981\u6295\u964d\u76d7\u8d3c\uff0c\u5413\u5f97\u9762\u9762\u76f8\u89d1\u3002\u4e00\u5458\u90fd\u53f8\u9a82\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u98df\u541b\u4e4b\u7984\uff0c\u4e0d\u5fe0\u4e0d\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u8881\u627f\u5fd7\u5df2\u6293\u4f4f\u4ed6\u5f80\u5730\u4e0b\u4e00\u6454\uff0c\u767b\u65f6\u6655\u53bb\u3002\u4f59\u4e0b\u4f17\u5c06\u98a4\u58f0\u9f50\u9053\uff1a\u201c\u6807\u4e0b\u5949\u2026\u2026\u5949\u603b\u5ea7\u5c06\u4ee4\u3002\u201d \u3000\u3000\u6c34\u603b\u5175\u9053\uff1a\u201c\u4e0b\u4ee4\u505c\u6218\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e5f\u4f20\u4e0b\u53f7\u4ee4\uff0c\u547d\u5c71\u4e1c\u7fa4\u76d7\u4e0d\u518d\u53ae\u6740\uff0c\u53c8\u5429\u5490\u6c34\u603b\u5175\u547d\u5b98\u5175\u629b\u4e0b\u5175\u5203\u3002\u6c34\u603b\u5175\u65e0\u5948\uff0c\u53ea\u5f97\u4f9d\u8a00\u3002\u706b\u628a\u7167\u8000\u4e0b\u53ea\u89c1\u53cc\u65b9\u5175\u6208\u9f50\u606f\u3002 \u3000\u3000\u5ffd\u89c1\u4e94\u4e2a\u4eba\u5728\u8f66\u961f\u4e2d\u5954\u9a70\u6765\u53bb\uff0c\u4e71\u7ffb\u4e71\u627e\uff0c\u6253\u5f00\u4e86\u8bb8\u591a\u7bb1\u7b3c\uff0c\u89c1\u662f\u94f6\u5b50\u7cae\u98df\uff0c\u4fbf\u8e22\u5728\u4e00\u65c1\u3002\u4f17\u5b98\u5175\u89c1\u4e94\u4eba\u52bf\u6076\uff0c\u8d25\u964d\u4e4b\u4f59\uff0c\u4e0d\u6562\u963b\u62e6\u3002\u5954\u5230\u4e34\u8fd1\uff0c\u539f\u6765\u662f\u5f52\u8f9b\u6811\u592b\u5987\u5e08\u5f92\u4e94\u4eba\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u4f60\u4eec\u627e\u751a\u4e48\uff1f\u6211\u53eb\u4ed6\u4eec\u62ff\u51fa\u6765\u3002\u201d \u3000\u3000\u5f52\u8f9b\u6811\u89c1\u7edf\u5175\u5c06\u5b98\u90fd\u96c6\u5728\u8881\u627f\u5fd7\u8eab\u65c1\uff0c\u4e09\u4e2a\u8d77\u843d\uff0c\u5df2\u5954\u5230\u6c34\u603b\u5175\u8eab\u8fb9\uff0c\u4e00\u628a\u63ea\u4f4f\u4ed6\u80f8\u812f\uff0c\u63d0\u4e86\u8d77\u6765\u3002\u6c34\u603b\u5175\u60ca\u9b42\u672a\u5b9a\uff0c\u54ea\u60f3\u7a81\u7136\u53c8\u9047\u5230\u4e00\u4e2a\u6b66\u529f\u6781\u9ad8\u4e4b\u4eba\uff0c\u7ed9\u4ed6\u6293\u4f4f\u4e86\uff0c\u4efb\u51ed\u5982\u4f55\u731b\u529b\u6323\u624e\uff0c\u603b\u5f52\u65e0\u7528\u3002\u5f52\u8f9b\u6811\u559d\u9053\uff1a\u201c\u9a6c\u4e0a\u82f1\u8fdb\u8d21\u7684\u832f\u82d3\u9996\u4e4c\u4e38\uff0c\u85cf\u5728\u54ea\u91cc\uff1f\u201d\u6c34\u603b\u5175\u9053\uff1a\u201c\u9a6c\u7763\u629a\u5acc\u6211\u4eec\u8f66\u591a\u8d70\u5f97\u6162\uff0c\u53e6\u884c\u6d3e\u4eba\u9001\u5230\u4eac\u91cc\u53bb\u4e86\u3002\u201d\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d\u6c34\u603b\u5175\u9053\uff1a\u201c\u6211\u8eab\u5bb6\u6027\u547d\u90fd\u5728\u4f60\u4eec\u624b\u91cc\uff0c\u4f55\u5fc5\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u5f52\u8f9b\u6811\u5fc3\u60f3\u770b\u6765\u6b64\u8a00\u4e0d\u5047\uff0c\u628a\u4ed6\u5f80\u5730\u4e0b\u4e00\u629b\uff0c\u559d\u9053\uff1a\u201c\u8981\u662f\u67e5\u5230\u4f60\u80e1\u8a00\u9a97\u4eba\uff0c\u56de\u6765\u53d6\u4f60\u72d7\u547d\u3002\u201d\u8f6c\u5934\u5bf9\u5f52\u4e8c\u5a18\u9053\uff1a\u201c\u5f80\u524d\u8ffd\u3002\u201d\u5f52\u4e8c\u5a18\u62b1\u7740\u5b69\u5b50\uff0c\u5fc3\u5934\u70e6\u8e81\uff0c\u5355\u638c\u8d77\u5904\uff0c\u628a\u6321\u5728\u9762\u524d\u7684\u5b98\u5175\u6253\u5f97\u4e1c\u5012\u897f\u6b6a\uff0c\u9f3b\u9752\u76ee\u80bf\uff0c\u5e26\u7740\u4e09\u4e2a\u5f92\u5f1f\uff0c\u8ddf\u4e08\u592b\u8d70\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77e5\u9053\u4e8c\u5e08\u5144\u592b\u5987\u5bf9\u81ea\u5df1\u5fc3\u5b58\u82a5\u8482\uff0c\u53ea\u6709\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5f85\u4e94\u4eba\u53bb\u540e\uff0c\u95ee\u6c34\u603b\u5175\u9053\uff1a\u201c\u4ed6\u4eec\u627e\u751a\u4e48\u836f\u4e38\uff1f\u201d\u6c34\u603b\u5175\u88ab\u64d2\u964d\u654c\uff0c\u5fc3\u4e71\u610f\u70e6\uff0c\u795e\u4e0d\u5b88\u820d\uff0c\u4e00\u65f6\u60f3\u5230\u5bb6\u4e2d\u662f\u5426\u4f1a\u7ed9\u7687\u5e1d\u4e0b\u65e8\u6ee1\u95e8\u6284\u65a9\uff0c\u4e00\u65f6\u53c8\u60f3\u81ea\u5df1\u529f\u540d\u524d\u7a0b\uff0c\u4ece\u6b64\u4ed8\u4e0e\u6d41\u6c34\u3002\u8881\u627f\u5fd7\u63a5\u8fde\u8be2\u95ee\uff0c\u4ed6\u7b54\u975e\u6240\u95ee\uff0c\u4e0d\u77e5\u6240\u4e91\uff0c\u8bf4\u4e86\u534a\u5929\uff0c\u8881\u627f\u5fd7\u624d\u660e\u767d\u4e86\u4e2a\u5927\u6982\u3002 \u3000\u3000\u539f\u6765\u6700\u8fd1\u9ec4\u5c71\u6df1\u8c37\u91cc\u627e\u5230\u4e86\u4e00\u5757\u5927\u832f\u82d3\uff0c\u4f30\u8ba1\u5df2\u5728\u5343\u5e74\u4ee5\u4e0a\uff0c\u51d1\u5de7\u6d59\u4e1c\u53c8\u6709\u4eba\u6398\u5230\u4e00\u4e2a\u4eba\u5f62\u4f55\u9996\u4e4c\u3002\u8fd9\u4e24\u6837\u90fd\u662f\u5343\u8f7d\u96be\u9022\u7684\u5b9d\u7269\u3002\u51e4\u9633\u603b\u7763\u9a6c\u58eb\u82f1\u5f97\u5230\u8baf\u606f\uff0c\u5dee\u5e55\u5ba2\u4e00\u534a\u5f3a\u53d6\u3001\u4e00\u534a\u4ef7\u8d2d\u7684\u4e70\u4e86\u6765\uff0c\u547d\u9ad8\u624b\u836f\u5e08\u5236\u6210\u4e86\u516b\u5341\u9897\u832f\u82d3\u9996\u4e4c\u4e38\uff0c\u8fd8\u914d\u4e0a\u4e86\u8001\u5c71\u4eba\u53c2\u3001\u73cd\u73e0\u7c89\u672b\u7b49\u73cd\u8d35\u836f\u6750\uff0c\u5355\u662f\u836f\u6750\u672c\u94b1\u5c31\u82b1\u4e86\u4e24\u4e09\u4e07\u94f6\u5b50\u3002\u8fd9\u4ef6\u4e8b\u8f70\u52a8\u4e86\u6c5f\u5357\u5b98\u573a\u548c\u533b\u884c\u836f\u4e1a\u3002\u636e\u53e4\u65b9\u6240\u8f7d\uff0c\u8fd9\u836f\u4e38\u5b9e\u6709\u8d77\u6b7b\u56de\u751f\u7684\u795e\u6548\uff0c\u4f53\u8d28\u865a\u5f31\u7684\u4eba\uff0c\u53ea\u670d\u4e00\u4e38\u4fbf\u7acb\u523b\u89c1\u529f\u3002\u9a6c\u58eb\u82f1\u81ea\u5df1\u7559\u4e0b\u56db\u5341\u9897\uff0c\u4ee5\u5907\u6b64\u540e\u56db\u5341\u5e74\u4e2d\u6bcf\u5e74\u670d\u98df\u4e00\u9897\uff0c\u4f59\u4e0b\u56db\u5341\u9897\u4fbf\u53bb\u8fdb\u8d21\uff0c\u76fc\u5d07\u796f\u518d\u505a\u56db\u5341\u5e74\u7687\u5e1d\uff0c\u5e74\u5e74\u5347\u81ea\u5df1\u7684\u5b98\u3002 \u3000\u3000\u8881\u627f\u5fd7\u597d\u5bb9\u6613\u542c\u5f97\u660e\u767d\uff0c\u5fc3\u9053\uff1a\u201c\u90a3\u5c31\u662f\u4e86\uff0c\u4e8c\u5e08\u54e5\u7231\u5b50\u6709\u75c5\uff0c\u4e45\u6cbb\u4e0d\u6108\uff0c\u6025\u7740\u8981\u8fd9\u4e9b\u836f\u4e38\u3002\u201d \u3000\u3000\u6c34\u603b\u5175\u53c8\u9053\uff1a\u201c\u9a6c\u603b\u7763\u672c\u60f3\u5dee\u6211\u4e00\u5e76\u5c06\u5b9d\u836f\u9001\u53bb\u5317\u4eac\uff0c\u4f46\u540e\u6765\u5acc\u6211\u4eec\u8f66\u591a\u884c\u5f97\u6162\uff0c\u53c8\u62bc\u7740\u6b7b\u56da\u4e0d\u5409\u5229\uff0c\u56e0\u6b64\u53e6\u5dee\u91d1\u9675\u6c38\u80dc\u9556\u5c40\u7684\u8463\u9556\u5934\u62a4\u9001\u8d74\u4eac\uff0c\u732e\u7ed9\u7687\u4e0a\u3002\u201d\u81f3\u4e8e\u9a6c\u603b\u7763\u81ea\u5df1\u7559\u4e0b\u56db\u5341\u9897\u4e4b\u4e8b\uff0c\u90a3\u662f\u5929\u5927\u673a\u5bc6\uff0c\u8fde\u5bf9\u4ed6\u6700\u5f97\u5ba0\u7684\u59ec\u59be\u4e5f\u90fd\u4e0d\u8bf4\uff0c\u6c34\u603b\u5175\u81ea\u7136\u66f4\u4e0d\u4f1a\u77e5\u9053\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u5fc3\u76fc\u671b\u4e8c\u5e08\u54e5\u80fd\u593a\u5230\u836f\u4e38\uff0c\u6551\u5f97\u5b69\u5b50\u4e4b\u547d\uff0c\u5fd9\u95ee\uff1a\u201c\u90a3\u9556\u5e08\u8d70\u4e86\u51e0\u5929\u5566\uff1f\u201d\u6c34\u603b\u5175\u9053\uff1a\u201c\u542f\u7a0b\u662f\u5728\u540c\u4e00\u5929\uff0c\u4e0d\u8fc7\u9556\u5c40\u5b50\u53ea\u6709\u5341\u6765\u4e2a\u4eba\uff0c\u884c\u9053\u5feb\u5f97\u591a\uff0c\u7b97\u6765\u62a2\u5728\u6211\u4eec\u4e4b\u524d\uff0c\u603b\u6709\u4e94\u516d\u5929\u8def\u7a0b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5b59\u4ef2\u5bff\u3001\u6731\u5b89\u56fd\u3001\u502a\u6d69\u3001\u7f57\u5927\u5343\u7b49\u8881\u90e8\u65e7\u5c06\u7eb7\u7eb7\u8fc7\u6765\u76f8\u89c1\u3002\u5404\u4eba\u5f97\u8131\u5927\u96be\uff0c\u53c8\u89c1\u8881\u627f\u5fd7\u957f\u5927\u6210\u4eba\uff0c\u4e00\u8eab\u6b66\u827a\uff0c\u4eca\u65e5\u8fd9\u4e00\u6218\u867d\u662f\u5c0f\u8bd5\u725b\u5200\uff0c\u4ea6\u5df2\u7565\u6709\u4e43\u7236\u5f53\u65e5\u96c4\u98ce\uff0c\u65e0\u4e0d\u60ca\u559c\u4ea4\u96c6\u3002\u8881\u627f\u5fd7\u95ee\u8d77\u88ab\u6355\u7f18\u7531\uff0c\u5b59\u4ef2\u5bff\u7ea6\u7565\u8bf4\u4e86\u3002\u539f\u6765\u5f53\u65e5\u201c\u5c71\u5b97\u201d\u65e7\u53cb\u5728\u5723\u5cf0\u5d82\u805a\u4f1a\uff0c\u660e\u5175\u7a81\u65bd\u88ad\u51fb\uff0c\u5e78\u800c\u5927\u90e8\u4eba\u4f17\u65e9\u5df2\u6563\u8d70\uff0c\u53ea\u5e94\u677e\u7ec8\u4e8e\u88ab\u5bb3\uff0c\u5b59\u4ef2\u5bff\u7b49\u90fd\u544a\u8131\u9669\uff0c\u540e\u6765\u91cd\u53c8\u805a\u96c6\u3002\u4f17\u4eba\u5728\u6dee\u5317\u9c81\u5357\u4e00\u5e26\u4f1a\u805a\u8c6a\u6770\uff0c\u9884\u5907\u5927\u4e3e\uff0c\u54ea\u77e5\u4e8b\u673a\u4e0d\u5bc6\uff0c\u4e0a\u4e2a\u6708\u88ab\u51e4\u9633\u603b\u7763\u9a6c\u58eb\u82f1\u6240\u7834\uff0c\u9996\u8981\u4eba\u7269\u4e00\u9f13\u6210\u64d2\uff0c\u68b0\u7cfb\u8d74\u4eac\u95ee\u65a9\u3002\u5dee\u5e78\u5929\u7f18\u5de7\u5408\uff0c\u7adf\u4f1a\u5728\u6b64\u5904\u4e0e\u8881\u627f\u5fd7\u76f8\u9047\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u542c\u8bf4\u8881\u627f\u5fd7\u548c\u95ef\u738b\u9887\u6709\u8054\u7edc\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u91cc\u53c8\u6709\u76d7\u5e2e\uff0c\u53c8\u6709\u6295\u964d\u7684\u5927\u6279\u5b98\u5175\uff0c\u4ed6\u4eec\u5bf9\u4f60\u90fd\u5f88\u656c\u670d\uff0c\u6b63\u662f\u96be\u9047\u7684\u826f\u673a\u3002\u4f55\u4e0d\u6682\u7f13\u8d74\u4eac\uff0c\u628a\u8fd9\u6279\u4eba\u624b\u597d\u597d\u6574\u987f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u559c\u9053\uff1a\u201c\u5b59\u53d4\u53d4\u8bf4\u5f97\u662f\uff0c\u8fd9\u4e00\u5e26\u82f1\u96c4\u8c6a\u6770\u5f88\u591a\uff0c\u54b1\u4eec\u7d22\u6027\u5927\u5e72\u4e00\u573a\uff0c\u627e\u4e2a\u5730\u65b9\u4f1a\u96c6\u7fa4\u96c4\u3002\u201d\u5b59\u4ef2\u5bff\u4e00\u62cd\u5927\u817f\uff0c\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u4f55\u4e0d\u5c31\u5728\u6cf0\u5c71\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6cf0\u5c71\u76f8\u53bb\u4e0d\u8fdc\uff0c\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u6536\u62fe\u597d\u94c1\u7bb1\u4e2d\u6563\u5f00\u7684\u5b9d\u7269\uff0c\u628a\u6f15\u8fd0\u94f6\u5b50\u53d6\u51fa\u4e8c\u5341\u4e07\u4e24\uff0c\u4ff5\u5206\u7ed9\u9752\u7af9\u5e2e\u4e0e\u5c71\u4e1c\u5404\u5be8\u7fa4\u76d7\u3002\u891a\u7ea2\u67f3\u4e5f\u5f97\u4e86\u4e94\u5343\u4e24\u3002\u518d\u53d6\u51fa\u4e8c\u5341\u4e07\u4e24\u8d4f\u7ed9\u6295\u964d\u7684\u5b98\u5175\uff0c\u4e00\u65f6\u5ce1\u8c37\u524d\u540e\uff0c\u6b22\u58f0\u5982\u96f7\u3002 \u3000\u3000\u6295\u964d\u7684\u519b\u5b98\u672c\u6765\u90fd\u662f\u5fc3\u60c5\u90c1\u90c1\uff0c\u5206\u5f97\u5927\u6279\u94f6\u4e24\uff0c\u624d\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\u3002 \u3000\u3000\u53ea\u89c1\u9752\u7af9\u5e2e\u7684\u4e24\u540d\u5e2e\u4f17\u62ac\u7740\u4e00\u4e2a\u62c5\u67b6\uff0c\u5c06\u5e2e\u4e3b\u7a0b\u9752\u7af9\u62ac\u5c06\u8fc7\u6765\u3002\u8881\u627f\u5fd7\u89c1\u4ed6\u8138\u4e0a\u5df2\u73b0\u8840\u8272\uff0c\u559c\u9053\uff1a\u201c\u7a0b\u5e2e\u4e3b\u7684\u4f24\u52bf\u597d\u5f97\u5f88\u5feb\u554a\uff0c\u8db3\u89c1\u5185\u529f\u6df1\u539a\u3002\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u591a\u8c22\u516c\u5b50\uff0c\u5728\u4e0b\u5f97\u77e5\u516c\u5b50\u662f\u8881\u7763\u5e08\u7684\u9aa8\u8089\uff0c\u5b9e\u662f\u6b22\u559c\u4e4b\u6781\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u4e2d\u7adf\u5fae\u5e26\u545c\u54bd\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u7a0b\u5e2e\u4e3b\u5f53\u5e74\u8bc6\u5f97\u5148\u7236\u5417\uff1f\u201d\u7a0b\u9752\u7af9\u6447\u4e86\u6447\u5934\uff0c\u5429\u5490\u968f\u4ece\u5728\u4e00\u53ea\u5e03\u56ca\u4e2d\u53d6\u51fa\u4e00\u5377\u624b\u7a3f\uff0c\u4ea4\u7ed9\u8881\u627f\u5fd7\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u770b\u4e86\u8fd9\u4e2a\uff0c\u4fbf\u77e5\u7aef\u7684\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u63a5\u8fc7\uff0c\u53ea\u89c1\u5c01\u9762\u4e0a\u5199\u7740\u201c\u6f29\u58f0\u8bb0\u201d\u4e09\u4e2a\u5927\u5b57\uff0c\u53c8\u6709\u201c\u7a0b\u672c\u76f4\u64b0\u201d\u56db\u5b57\uff0c\u53f3\u4e0a\u89d2\u9898\u7740\u4e00\u526f\u5bf9\u8054\uff1a\u201c\u4e00\u5bf9\u75f4\u5fc3\u4eba\uff0c\u4e24\u6761\u6cfc\u80c6\u6c49\u3002\u201d\u5fc3\u4e2d\u4e0d\u89e3\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u7a0b\u672c\u76f4\u7a0b\u5148\u751f\uff0c\u8ddf\u7a0b\u5e2e\u4e3b\u662f\u2026\u2026\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u90a3\u662f\u5148\u5144\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u7ffb\u5f00\u624b\u7a3f\uff0c\u53ea\u89c1\u6587\u4e2d\u5199\u9053\uff1a\u201c\u5d07\u7115\u5341\u8f7d\u8fb9\u81e3\uff0c\u5c61\u7ecf\u6218\u5b88\uff0c\u72ec\u63d0\u4e00\u65c5\uff0c\u633a\u51fa\u4e25\u5173\u2026\u2026\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u51db\uff0c\u95ee\u9053\uff1a\u201c\u4e66\u4e2d\u8bf4\u7684\u662f\u5148\u7236\u4e4b\u4e8b\uff1f\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6b63\u662f\u3002\u4ee4\u5c0a\u7763\u5e08\u5927\u4eba\uff0c\u662f\u5148\u5144\u751f\u5e73\u6700\u4f69\u670d\u4e4b\u4eba\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5f53\u4e0b\u53cc\u624b\u6367\u4f4f\u624b\u7a3f\uff0c\u606d\u606d\u656c\u656c\u7684\u8bfb\u4e0b\u53bb\uff1a\u201c\u2026\u2026\u8fc4\u4eca\u5c71\u6d77\u800c\u5916\uff0c\u4e00\u91cc\u4e4b\u8349\u83b1\uff0c\u5d07\u7115\u624b\u8f9f\u4e4b\u4e5f\uff1b\u4e00\u5821\u4e4b\u5792\uff0c\u4e00\u57ce\u4e4b\u581e\uff0c\u5d07\u7115\u624b\u7b51\u4e4b\u4e5f\u3002\u8bd5\u95ee\u81ea\u6709\u8fbd\u4e8b\u4ee5\u6765\uff0c\u8c01\u4e0d\u671b\u654c\u4e8e\u6570\u767e\u91cc\u800c\u9003\uff1f\u5f03\u57ce\u4e8e\u6570\u5341\u91cc\u800c\u9041\uff1f\u6562\u4e8e\u654c\u4eba\u753b\u5730\u800c\u5b88\uff0c\u5bf9\u5792\u800c\u6218\uff0c\u53cd\u4f7f\u6b64\u654c\u671b\u800c\u9003\u3001\u5f03\u800c\u9041\u8005\uff0c\u820d\u5d07\u7115\u5176\u8c01\u4e0e\u5f52\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9605\u4e86\u8fd9\u4e00\u6bb5\u6587\u5b57\uff0c\u773c\u7736\u4e0d\u7531\u5f97\u6e7f\u4e86\uff0c\u7ffb\u8fc7\u4e00\u9875\uff0c\u53c8\u8bfb\u4e86\u4e0b\u53bb\uff1a\u201c\u5ba2\u4ea6\u95fb\u654c\u4eba\u81ea\u53d1\u96be\u4ee5\u6765\uff0c\u4ea6\u6709\u653b\u800c\u4e0d\u4e0b\uff0c\u6218\u800c\u4e0d\u514b\u8005\u5426\uff1f \u3000\u3000\u66f0\uff1a\u672a\u4e5f\u3002\u5ba2\u4ea6\u77e5\u4e4e\u6709\u5b81\u8fdc\u4e19\u5bc5\u4e4b\u56f4\uff0c\u800c\u540e\u4e2d\u56fd\u77e5\u6240\u4ee5\u5b88\uff1f\u6709\u9526\u5dde\u4e01\u536f\u4e4b\u529f\uff0c\u800c\u540e\u4e2d\u56fd\u77e5\u6240\u4ee5\u6218\u5426\u4e5f\uff1f\u66f0\uff1a\u7136\u4e5f\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u518d\u770b\u4e0b\u53bb\uff0c\u4e0b\u9762\u5199\u9053\uff1a\u201c\u4eca\u65e5\u6ee6\u4e4b\u590d\u3001\u9075\u4e4b\u590d\u4e5f\uff0c\u8c01\u5175\u4e5f\uff1f\u8fbd\u5175\u4e5f\u3002\u8c01\u9a6c\u4e5f\uff1f\u8fbd\u9a6c\u4e5f\u3002\u81ea\u5d07\u7115\u672a\u8385\u8fbd\u4ee5\u524d\uff0c\u8fbd\u4ea6\u6709\u662f\u5175\u3001\u6709\u662f\u9a6c\u5426\u4e5f\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u968f\u624b\u53c8\u7ffb\u4e86\u4e00\u9875\uff0c\u8bfb\u9053\uff1a\u201c\u4e3e\u4e16\u7686\u5de7\u4eba\uff0c\u800c\u8881\u516c\u4e00\u5927\u75f4\u6c49\u4e5f\u3002\u552f\u5176\u75f4\uff0c\u6545\u4e3e\u4e16\u6700\u7231\u8005\u94b1\uff0c\u8881\u516c\u4e0d\u77e5\u7231\u4e5f\u3002\u552f\u5176\u75f4\uff0c\u6545\u4e3e\u4e16\u6700\u60dc\u8005\u6b7b\uff0c\u8881\u516c\u4e0d\u77e5\u6015\u4e5f\u3002\u4e8e\u662f\u4e4e\u4e3e\u4e16\u6240\u4e0d\u6562\u4efb\u4e4b\u52b3\u6028\uff0c\u8881\u516c\u76f4\u4efb\u4e4b\u800c\u5f17\u8f9e\u4e5f\uff1b\u4e8e\u662f\u4e4e\u4e3e\u4e16\u6240\u4e0d\u5f97\u4e0d\u907f\u4e4b\u5acc\u7591\uff0c\u8881\u516c\u76f4\u4e0d\u907f\u4e4b\u800c\u72ec\u884c\u4e5f\uff1b\u800c\u4e14\u4e3e\u4e16\u6240\u4e0d\u80fd\u8010\u4e4b\u9965\u5bd2\uff0c\u8881\u516c\u76f4\u8010\u4e4b\u4ee5\u4e3a\u58eb\u5352\u5148\u4e5f\uff1b\u800c\u4e14\u4e3e\u4e16\u6240\u4e0d\u80af\u7834\u4e4b\u793c\u8c8c\uff0c\u8881\u516c\u529b\u7834\u4e4b\u4ee5\u4e0e\u8bf8\u5c06\u540f\u63a8\u5fc3\u800c\u7f6e\u8179\u4e5f\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8bfb\u5230\u6b64\u5904\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u6cea\u6c34\u6d94\u6d94\u800c\u4e0b\uff0c\u6ef4\u4e0a\u7eb8\u9875\uff0c\u6cea\u773c\u6a21\u7cca\u4e4b\u4e2d\uff0c\u770b\u5230\u4e0b\u9762\u4e00\u884c\u5b57\u9053\uff1a\u201c\u4e88\u5219\u8c13\u6380\u7ffb\u4e24\u76f4\u96b6\u3001\u8e0f\u904d\u4e00\u5341\u4e09\u7701\uff0c\u6c42\u5176\u6d51\u8eab\u62c5\u8377\u3001\u5f7b\u91cc\u627f\u5f53\u5982\u8881\u516c\u8005\uff0c\u6b63\u6050\u4e0d\u53ef\u518d\u5f97\u4e5f\u3002\u6b64\u6240\u4ee5\u60df\u8881\u516c\u503c\u5f97\u7a0b\u672c\u76f4\u4e00\u6b7b\u4e5f\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u63a9\u4e86\u624b\u7a3f\uff0c\u6d41\u6cea\u9053\uff1a\u201c\u4ee4\u5144\u771f\u662f\u5148\u7236\u7684\u77e5\u5df1\uff0c\u5982\u6b64\u79f0\u8a89\uff0c\u5728\u4e0b\u5b9e\u5728\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u53f9\u9053\uff1a\u201c\u5148\u5144\u4e0e\u4ee4\u5c0a\u672c\u6765\u7d20\u4e0d\u76f8\u8bc6\u3002\u4ed6\u662f\u4e2a\u5e03\u8863\u767e\u59d3\uff0c\u66fe\u4e09\u6b21\u6c42\u89c1\uff0c\u90fd\u56e0\u4ee4\u5c0a\u4e8b\u5fd9\uff0c\u672a\u66fe\u89c1\u7740\u3002\u5148\u5144\u5fc3\u7ec8\u4e0d\u6b7b\uff0c\u4fbf\u6295\u5165\u7763\u5e08\u90e8\u4e0b\uff0c\u51fa\u529b\u529e\u4e8b\uff0c\u7ec8\u4e8e\u5f97\u8499\u7763\u5e08\u89c1\u91cd\uff0c\u6536\u4e3a\u95e8\u751f\u3002 \u3000\u3000\u4ee4\u5c0a\u8499\u51a4\u4e0b\u72f1\uff0c\u53c8\u906d\u51cc\u8fdf\u6bd2\u5211\u3002\u5148\u5144\u5411\u671d\u5ef7\u4e0a\u4e66\uff0c\u4e3a\u4ee4\u5c0a\u9e23\u51a4\uff0c\u53ea\u56e0\u8a00\u8f9e\u5207\u76f4\uff0c\u660f\u541b\u5927\u4e3a\u607c\u6012\uff0c\u7adf\u628a\u5148\u5144\u4e5f\u5904\u6b7b\u4e86\u3002\u201d\u8881\u627f\u5fd7\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u6012\u9053\uff1a\u201c\u8fd9\u660f\u541b\uff01\u201d \u3000\u3000\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u5148\u5144\u9057\u8a00\u9053\uff0c\u4e3a\u8881\u516c\u800c\u6b7b\uff0c\u6b7b\u4e5f\u4e0d\u6789\uff0c\u53ea\u613f\u65e5\u540e\u80fd\u846c\u4e8e\u8881\u516c\u5893\u65c1\uff0c\u7891\u4e0a\u9898\u5b57\u2018\u4e00\u5bf9\u75f4\u5fc3\u4eba\uff0c\u4e24\u6761\u6cfc\u80c6\u6c49\u2019\uff0c\u90a3\u4e48\u4ed6\u6b7b\u4e5f\u7791\u76ee\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u8fd9\u4e8b\u53ef\u529e\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u9752\u7af9\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4ee4\u5c0a\u8eab\u906d\u5947\u51a4\uff0c\u660f\u541b\u5978\u81e3\u90fd\u8bf4\u4ed6\u901a\u654c\uff0c\u52fe\u7ed3\u6ee1\u6e05\uff0c\u4e00\u822c\u65e0\u77e5\u767e\u59d3\u5374\u4e5f\u4e0d\u8fa8\u5fe0\u5978\u662f\u975e\uff0c\u4fe1\u4e86\u8fd9\u8bdd\u3002\u4ee4\u5c0a\u88ab\u7ed1\u4e0a\u6cd5\u573a\u540e\uff0c\u611a\u6c11\u4e00\u62e5\u800c\u4e0a\uff0c\u5c06\u4ed6\u8eab\u5b50\u54ac\u5f97\u7c89\u788e\uff0c\u8bf4\u9053\u2026\u2026\u8bf4\u9053\u8981\u5403\u5c3d\u5356\u56fd\u5978\u8d3c\u7684\u8840\u8089\u2026\u2026\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u5f97\u653e\u58f0\u5927\u54ed\uff0c\u95ee\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u5b59\u53d4\u53d4\uff0c\u8fd9\u2026\u2026\u8fd9\u662f\u771f\u7684\u4e48\uff1f\u201d\u5b59\u4ef2\u5bff\u5782\u6cea\u70b9\u5934\uff0c\u9053\uff1a\u201c\u771f\u662f\u5982\u6b64\u3002 \u3000\u3000\u5f53\u5e74\u4f60\u5e74\u7eaa\u5e7c\u5c0f\uff0c\u6211\u4eec\u4e0d\u8ddf\u4f60\u8bf4\uff0c\u514d\u4f60\u4f24\u5fc3\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6012\u9053\uff1a\u201c\u660f\u541b\u5978\u81e3\u4e3a\u975e\u4f5c\u6b79\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u5317\u4eac\u57ce\u7684\u8001\u767e\u59d3\uff0c\u5374\u4e5f\u5982\u6b64\u53ef\u6076\uff01\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u8001\u767e\u59d3\u4e0d\u660e\u771f\u76f8\uff0c\u53ea\u9053\u7687\u5e1d\u7684\u5723\u65e8\uff0c\u662f\u518d\u4e5f\u4e0d\u4f1a\u9519\u7684\u3002\u6e05\u5175\u5728\u5317\u4eac\u57ce\u5916\u70e7\u6740\u63b3\u63a0\uff0c\u5bb3\u6b7b\u7684\u4eba\u6210\u5343\u6210\u4e07\uff0c\u56e0\u6b64\u767e\u59d3\u5bf9\u52fe\u7ed3\u654c\u5175\u7684\u6c49\u5978\u75db\u6068\u5165\u9aa8\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u5fff\u5144\u957f\u88ab\u5bb3\uff0c\u8bbe\u6cd5\u6295\u8eab\u7687\u5bab\uff0c\u5f53\u4e86\u4e2a\u4f8d\u536b\uff0c\u60f3\u4fdf\u673a\u884c\u523a\u660f\u541b\uff0c\u4e3a\u5148\u5144\u548c\u8881\u7763\u5e08\u62a5\u4ec7\u3002\u53ea\u6068\u6b66\u827a\u4f4e\u5fae\uff0c\u884c\u523a\u4e0d\u6210\uff0c\u53cd\u4e3a\u5fa1\u524d\u4f8d\u536b\u6240\u64d2\uff0c\u5e78\u5f97\u6709\u4eba\u76f8\u6551\uff0c\u9003\u51fa\u7687\u5bab\u3002\u8fd9\u4e9b\u5e74\u6765\u5728\u9ed1\u9053\u4e0a\u5e72\u4e9b\u6ca1\u672c\u94b1\u4e70\u5356\uff0c\u6ca1\u6599\u5230\u6709\u773c\u65e0\u73e0\uff0c\u7adf\u770b\u4e0a\u4e86\u516c\u5b50\u7684\u8d22\u7269\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u5bb6\u8bf4\u6765\u6df1\u6709\u6e0a\u6e90\uff0c\u82e5\u975e\u5982\u6b64\uff0c\u4e5f\u4e0d\u5f97\u8ddf\u5e2e\u4e3b\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u9752\u9752\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u90a3\u4e2a\u5c0f\u59d1\u5a18\u5462\uff1f\u5979\u6ca1\u4e8b\u5427\uff1f\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u591a\u8c22\u5173\u6000\u3002\u5c0f\u5f92\u5df2\u81ea\u884c\u53bb\u4e86\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u6b63\u60f3\u627e\u5979\u8bf4\u8bdd\uff0c\u600e\u4e48\u5979\u8d70\u4e86\uff1f\u201d\u8a00\u4e0b\u4e0d\u7981\u60d8\u7136\u3002 \u3000\u3000\u4f17\u4eba\u4f11\u606f\u4e86\u4e00\u65e5\u3002\u8881\u627f\u5fd7\u6d3e\u9063\u9752\u7af9\u5e2e\u3001\u5c71\u4e1c\u7fa4\u76d7\u53ca\u201c\u5c71\u5b97\u201d\u6240\u90e8\u5f97\u529b\u4eba\u5458\uff0c\u5206\u8d74\u5404\u5730\u9001\u4fe1\uff0c\u7ea6\u5b9a\u7aef\u5348\u8282\u5728\u6cf0\u5c71\u9876\u4e0a\u53d6\u9f50\uff1b\u53c8\u8bf7\u5b59\u4ef2\u5bff\u3001\u6731\u5b89\u56fd\u7b49\u4eba\uff0c\u4f1a\u540c\u6c34\u603b\u5175\u5e26\u9886\u6295\u964d\u7684\u5b98\u5175\uff0c\u5728\u8352\u50fb\u9669\u5cfb\u4e4b\u5730\u8d77\u9020\u5c71\u5be8\u3002 \u3000\u3000\u8fd9\u4e00\u5f79\u9a6c\u58eb\u82f1\u90e8\u4e0b\u516d\u5343\u540d\u5b98\u5175\u5168\u519b\u8986\u6ca1\uff0c\u4e8c\u767e\u4f59\u4e07\u4e24\u6f15\u94f6\u6ca1\u7559\u4e0b\u534a\u661f\u4e00\u70b9\uff0c\u4eac\u5e08\u5c71\u4e1c\uff0c\u65e0\u4e0d\u9707\u52a8\u3002\u7b49\u5230\u9a6c\u58eb\u82f1\u518d\u8c03\u5927\u519b\u524d\u6765\u8ffd\u527f\uff0c\u76d7\u5e2e\u65e9\u5df2\u5f71\u8e2a\u5168\u65e0\uff0c\u54ea\u91cc\u8fd8\u8ffd\u5bfb\u5f97\u7740\u3002 \u3000\u3000\u773c\u89c1\u69b4\u82b1\u5410\u8273\uff0c\u7aef\u5348\u5c06\u5c4a\u3002\u6cf0\u5c71\u5404\u5904\u5bfa\u5e99\u9053\u89c2\u4e4b\u4e2d\uff0c\u9646\u9646\u7eed\u7eed\u5230\u4e86\u5343\u4f59\u4f4d\u5404\u5e2e\u5404\u6d3e\u7684\u82f1\u96c4\u8c6a\u6770\u3002 \u3000\u3000\u4e94\u6708\u521d\u4e94\u6e05\u6668\u7edd\u65e9\uff0c\u7fa4\u96c4\u5728\u77f3\u7ecf\u8c37\u4f1a\u805a\u3002\u8c37\u4e2d\u4e00\u7247\u5e73\u5e7f\uff0c\u6570\u4ea9\u77f3\u573a\uff0c\u5149\u6d01\u5f02\u5e38\uff0c\u76f8\u4f20\u662f\u53e4\u4ee3\u9ad8\u50e7\u8bb2\u7ecf\u4e4b\u6240\u3002\u5c71\u77f3\u4e0a\u523b\u6709\u516b\u5206\u4e66\u91d1\u521a\u7ecf\uff0c\u5b57\u5927\u5982\u6597\uff0c\u7b14\u529b\u96c4\u52b2\u3002 \u3000\u3000\u8fd9\u5929\u5230\u4f1a\u7684\u9664\u8881\u627f\u5fd7\u3001\u9752\u9752\u3001\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u7b49\u4eba\u5916\uff0c\u6709\u8881\u90e8\u65e7\u5c06\u5b59\u4ef2\u5bff\u3001\u6731\u5b89\u56fd\u3001\u502a\u6d69\u3001\u7f57\u5927\u5343\u7b49\u4eba\uff1b\u6709\u6c5f\u82cf\u91d1\u9f99\u5e2e\u7126\u516c\u793c\u3001\u7126\u5b9b\u513f\u3001\u5434\u5e73\u3001\u7f57\u7acb\u5982\u7b49\u4eba\uff1b\u6709\u6cb3\u5317\u9752\u7af9\u5e2e\u7a0b\u9752\u7af9\u7b49\u4eba\uff1b\u6709\u5c71\u4e1c\u7fa4\u76d7\u6c99\u5929\u5e7f\u3001\u891a\u7ea2\u67f3\u3001\u8c2d\u6587\u7406\u7b49\u4eba\uff1b\u6709\u6d59\u6c5f\u9f99\u6e38\u5e2e\u7684\u8363\u5f69\u7b49\u4eba\uff1b\u6709\u6cb3\u5357\u5357\u9633\u6e05\u51c9\u5bfa\u4e0b\u9662\u65b9\u4e08\u5341\u529b\u5927\u5e08\u3001\u6d77\u5916\u4e03\u5341\u4e8c\u5c9b\u76df\u4e3b\u90d1\u8d77\u4e91\u7b49\u4eba\uff1b\u6709\u4ece\u56da\u8f66\u83b7\u6551\u7684\u6dee\u5357\u98de\u864e\u5cea\u5be8\u4e3b\u8042\u5929\u98ce\u3001\u8d63\u5317\u9131\u9633\u5e2e\u5e2e\u4e3b\u6881\u94f6\u9f99\u7b49\u4eba\uff1b\u6709\u6295\u964d\u8fc7\u6765\u7684\u660e\u603b\u5175\u6c34\u9274\u7b49\u4eba\u3002\u6b64\u5916\u5c1a\u6709\u65e0\u6570\u6c5f\u6e56\u597d\u6c49\uff0c\u6b66\u6797\u540d\u5bb6\u3002\u4e00\u65f6\u6cf0\u5c71\u9876\u4e0a\u7fa4\u8c6a\u805a\u4f1a\uff0c\u82f1\u8d24\u6bd5\u81f3\u3002 \u3000\u3000\u8fd9\u65f6\u5c71\u8c37\u95f4\u5ffd\u5410\u767d\u4e91\u4e00\u7f15\uff0c\u6276\u6447\u76f4\u5347\uff0c\u826f\u4e45\uff0c\u4e1c\u8fb9\u4e00\u7247\u9ed1\u6697\u4e2d\u9690\u9690\u6731\u971e\u70ab\u6643\uff0c\u989c\u8272\u53d8\u5e7b\u4e0d\u5b9a\uff0c\u6216\u767d\u6216\u6a59\uff0c\u7f13\u7f13\u7684\u8840\u7ebf\u56db\u6620\uff0c\u4e00\u55b7\u4e00\u8000\uff0c\u8f6c\u77ac\u95f4\u592a\u9633\u5982\u4e00\u4e2a\u5927\u8d64\u76d8\u8e0a\u8dc3\u800c\u51fa\u3002\u4e0b\u9762\u4e91\u5f69\u88ab\u65e5\u5149\u4e00\u7167\uff0c\u5947\u4e3d\u53d8\u5e7b\uff0c\u767d\u8679\u873f\u8712\u3002\u7fa4\u8c6a\u5c3d\u7686\u559d\u5f69\u3002 \u3000\u3000\u89c2\u65e5\u5347\u5df2\u6bd5\uff0c\u7fa4\u8c6a\u5e2d\u5730\u5750\u4e0b\u3002\u9634\u9633\u6247\u6c99\u5929\u5e7f\u662f\u5c71\u4e1c\u5f53\u5730\u7684\u5730\u4e3b\uff0c\u8fd9\u65f6\u4ed6\u4f24\u52bf\u5df2\u6108\uff0c\u7ad9\u8d77\u8eab\u6765\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u524d\u8f88\u5927\u54e5\u8d4f\u8138\uff0c\u6765\u5230\u655d\u7701\uff0c\u5144\u5f1f\u62db\u5f85\u4e0d\u5468\uff0c\u8bf7\u591a\u591a\u5305\u6db5\u3002\u201d\u8bf4\u7740\u56e2\u56e2\u4f5c\u4e86\u4e00\u4e2a\u56db\u65b9\u63d6\u3002\u7fa4\u8c6a\u9f50\u58f0\u8c26\u8c22\u3002\u6c99\u5929\u5e7f\u53c8\u9053\uff1a\u201c\u5144\u5f1f\u662f\u7c97\u4eba\uff0c\u4e0d\u660e\u4e8b\u7406\uff0c\u73b0\u4e0b\u8bf7\u7a0b\u9752\u7af9\u524d\u8f88\u8bf4\u8bdd\u3002\u201d\u8fd9\u4e24\u4eba\u4ee5\u524d\u4e92\u4e0d\u76f8\u4e0b\uff0c\u90a3\u5929\u51fa\u751f\u5165\u6b7b\u7684\u53ae\u62da\u4e86\u4e00\u6b21\u4e4b\u540e\uff0c\u5404\u81ea\u94a6\u4f69\u5bf9\u65b9\u6b66\u529f\uff0c\u53cd\u800c\u7ed3\u6210\u4e86\u597d\u53cb\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\uff0c\u4ee5\u524d\u5728\u6cf0\u5c71\u4e5f\u805a\u8fc7\u4f1a\uff0c\u4e0d\u8fc7\u4eba\u6570\u4ece\u6765\u6ca1\u8fd9\u4e48\u591a\u3002\u4e0d\u6015\u5404\u4f4d\u7b11\u8bdd\uff0c\u4ee5\u524d\u6211\u4eec\u5230\u8fd9\u91cc\u5e72\u751a\u4e48\uff1f\u4e0d\u8fc7\u662f\u5212\u5730\u76d8\u3001\u5206\u8d43\u94f6\u7f62\u5566\u3002\u201d\u7fa4\u8c6a\u4e00\u9635\u8f70\u7b11\u3002\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u8fd9\u6b21\u8fd9\u8bb8\u591a\u82f1\u96c4\u670b\u53cb\u5927\u9a7e\u5149\u4e34\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u518d\u6ca1\u51fa\u606f\u5566\u3002\u773c\u524d\u5929\u4e0b\u5927\u4e71\uff0c\u8001\u767e\u59d3\u6d3b\u4e0d\u4e0b\u53bb\uff0c\u660f\u541b\u65e0\u9053\uff0c\u671d\u4e2d\u5168\u662f\u8d2a\u5b98\u6c61\u540f\uff0c\u5173\u5916\u6ee1\u5974\u53c8\u65f6\u65f6\u72af\u754c\u63b3\u63a0\uff0c\u5f53\u771f\u4eba\u547d\u8d31\u4f3c\u8682\u8681\uff0c\u8fc7\u5f97\u4e86\u4eca\u5929\uff0c\u4e5f\u4e0d\u77e5\u8fd8\u6709\u6ca1\u6709\u660e\u5929\uff1f\u54b1\u4eec\u603b\u8981\u597d\u597d\u5546\u8bae\uff0c\u505a\u4e00\u756a\u4e8b\u4e1a\u51fa\u6765\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u5f97\u8840\u8109\u594b\u5f20\uff0c\u9f50\u58f0\u559d\u5f69\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u53c8\u9053\uff1a\u201c\u4eca\u65e5\u5230\u4f1a\u7684\u90fd\u662f\u597d\u670b\u53cb\uff0c\u54b1\u4eec\u6b43\u8840\u4e3a\u76df\uff0c\u4ee5\u540e\u60a3\u96be\u76f8\u52a9\uff0c\u5171\u56fe\u5927\u4e8b\u3002\u5982\u6709\u8d2a\u56fe\u5bcc\u8d35\uff0c\u51fa\u5356\u670b\u53cb\uff0c\u6216\u662f\u8d2a\u751f\u6015\u6b7b\uff0c\u81ea\u79c1\u81ea\u5229\u7684\uff0c\u5927\u5bb6\u4e00\u9f50\u5e72\u4ed6\u5976\u5976\u7684\u3002\u201d\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u559d\u5f69\u3002 \u3000\u3000\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u4f1a\u76df\u4e0d\u53ef\u65e0\u76df\u4e3b\u3002\u54b1\u4eec\u63a8\u4e3e\u4e00\u4f4d\u5927\u5bb6\u4f69\u670d\u7684\u82f1\u96c4\u5927\u54e5\u51fa\u6765\uff0c\u4ee5\u540e\u9f50\u90fd\u542c\u4ed6\u7684\u53f7\u4ee4\u3002\u4e0d\u7ba1\u662f\u8c01\u5f53\u76df\u4e3b\uff0c\u5144\u5f1f\u5fc5\u5b9a\u8ffd\u968f\u5230\u5e95\uff0c\u51b3\u65e0\u5f02\u8a00\u3002\u201d\u5341\u529b\u5927\u5e08\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7fa4\u9f99\u65e0\u9996\uff0c\u51b3\u4e0d\u80fd\u6210\u5927\u4e8b\u3002\u63a8\u4e3e\u76df\u4e3b\uff0c\u8001\u8872\u662f\u4e00\u529b\u8d5e\u6210\u7684\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u4f4d\u76df\u4e3b\u5fc5\u987b\u667a\u52c7\u53cc\u5168\uff0c\u6709\u4ec1\u6709\u4e49\uff0c\u65b9\u80fd\u670d\u4f17\u3002\u201d\u90d1\u8d77\u4e91\u9053\uff1a\u201c\u90a3\u662f\u5f53\u7136\u7684\u4e86\uff0c\u6211\u77a7\u4f60\u5927\u5e08\u5c31\u5f88\u4e0d\u9519\u3002\u201d\u5341\u529b\u5927\u5e08\u7b11\u9053\uff1a\u201c\u8001\u8872\u98ce\u70db\u6b8b\u5e74\uff0c\u54ea\u80fd\u62c5\u5f53\u91cd\u4efb\uff1f\u90d1\u5c9b\u4e3b\u522b\u53d6\u7b11\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u4ea4\u5934\u63a5\u8033\uff0c\u7eb7\u7eb7\u8bae\u8bba\uff0c\u90fd\u89c9\u76df\u4e3b\u5e94\u8be5\u63a8\u4e3e\uff0c\u4ee5\u4fbf\u53f7\u4ee4\u4e00\u81f4\uff0c\u597d\u4f7f\u6563\u5904\u5404\u5730\u3001\u4e92\u4e0d\u7edf\u5c5e\u7684\u82f1\u96c4\u8c6a\u6770\u8054\u6210\u4e00\u8d77\u3002\u90a3\u65f6\u76f8\u4e92\u4e4b\u95f4\u56fa\u7136\u4e0d\u4f1a\u6b8b\u6740\u4e89\u6597\uff0c\u8fde\u5b98\u5e9c\u4e5f\u4e0d\u6562\u8f7b\u6613\u641c\u527f\u3002\u53ea\u662f\u7fa4\u96c4\u5411\u6765\u5404\u9738\u4e00\u65b9\uff0c\u8c01\u4e5f\u4e0d\u80af\u670d\u8c01\uff0c\u522b\u8981\u4e3a\u4e86\u4e89\u505a\u76df\u4e3b\uff0c\u53cd\u800c\u6bb4\u6740\u4e00\u573a\uff0c\u90a3\u5c31\u5f04\u5de7\u6210\u62d9\u4e86\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u5f85\u4f17\u4eba\u8bae\u8bba\u4e86\u4e00\u4f1a\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u5982\u65e0\u5f02\u8bae\uff0c\u73b0\u4e0b\u5c31\u6765\u63a8\u4e3e\u5982\u4f55\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4eba\u7fa4\u4e2d\u7ad9\u8d77\u4e00\u6761\u8eab\u9ad8\u4e03\u5c3a\u7684\u9b41\u68a7\u5927\u6c49\uff0c\u58f0\u82e5\u6d2a\u949f\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u76d6\u5b5f\u5c1d\u5b5f\u8001\u7237\u5b50\u5728\u6b66\u6797\u65e0\u4eba\u4e0d\u656c\uff0c\u65e0\u4eba\u4e0d\u670d\u3002\u4eca\u65e5\u4ed6\u8001\u4eba\u5bb6\u867d\u7136\u4e0d\u5728\u6b64\u5730\uff0c\u4f46\u76df\u4e3b\u4e00\u5e2d\u81ea\u7136\u975e\u4ed6\u83ab\u5c5e\uff0c\u5144\u5f1f\u4ee5\u4e3a\u4e0d\u5fc5\u53e6\u63a8\u4e86\u3002\u201d\u4ed6\u8bdd\u4e00\u8bf4\u6bd5\uff0c\u7fa4\u96c4\u4e2d\u767b\u65f6\u4fbf\u6709\u8bb8\u591a\u4eba\u968f\u58f0\u9644\u548c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u76d6\u5b5f\u5c1d\u662f\u751a\u4e48\u4eba\uff1f\u201d\u6d2a\u80dc\u6d77\u7565\u611f\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u76f8\u516c\u4e0d\u77e5\u6b64\u4eba\u5417\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u8bc6\u5f97\u5f88\u5c11\u3002\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u5b5f\u4f2f\u98de\u5b5f\u8001\u7237\u5b50\u4eba\u79f0\u76d6\u5b5f\u5c1d\uff0c\u7aef\u7684\u662f\u4ed7\u4e49\u758f\u8d22\uff0c\u6700\u7231\u670b\u53cb\uff0c\u6b66\u6797\u4e2d\u4eba\u7f18\u6781\u597d\u3002\u4ed6\u72ec\u521b\u7684\u5b5f\u5bb6\u795e\u62f3\u3001\u5feb\u6d3b\u4e09\u5341\u638c\uff0c\u53d8\u5e7b\u83ab\u6d4b\uff0c\u6295\u62dc\u5728\u4ed6\u95e8\u4e0b\u7684\u5f1f\u5b50\u6570\u4e5f\u6570\u4e0d\u6e05\uff0c\u8bf4\u5f97\u4e0a\u6843\u674e\u6ee1\u5929\u4e0b\u3002\u5317\u65b9\u5b66\u6b66\u7684\u4eba\u63d0\u5230\u76d6\u5b5f\u5c1d\uff0c\u90a3\u662f\u6ca1\u6709\u4eba\u4e0d\u4f69\u670d\u7684\u3002\u8fd9\u5927\u6c49\u662f\u4ed6\u5927\u5f1f\u5b50\uff0c\u53eb\u505a\u4e01\u7532\u795e\u4e01\u6e38\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u55ef\uff0c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u4e48\u63a8\u5b5f\u8001\u7237\u5b50\u505a\u76df\u4e3b\u5012\u4e5f\u5f88\u597d\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u4f4d\u5b5f\u8001\u7237\u5b50\u591a\u534a\u4eba\u7f18\u6781\u597d\uff0c\u6b66\u529f\u5374\u4e0d\u5982\u4f55\u4e86\u5f97\uff0c\u5426\u5219\u5e08\u7236\u4e0d\u4f1a\u4e0d\u8ddf\u6211\u8bf4\u8d77\u3002\u4f5c\u6b66\u6797\u76df\u4e3b\u7684\u4eba\uff0c\u539f\u672c\u4eba\u7f18\u6bd4\u6b66\u529f\u8981\u7d27\u5f97\u591a\u3002\u201d \u3000\u3000\u4e03\u5341\u4e8c\u5c9b\u76df\u4e3b\u90d1\u8d77\u4e91\u9053\uff1a\u201c\u5b5f\u8001\u7237\u5b50\u5a01\u540d\u8fdc\u9707\uff0c\u5144\u5f1f\u867d\u7136\u4ea1\u547d\u6d77\u5916\uff0c\u5374\u4e5f\u662f\u4e45\u4ef0\u7684\u4e86\uff0c\u63a8\u4ed6\u505a\u76df\u4e3b\uff0c\u8bba\u5fb7\u671b\uff0c\u8bba\u89c1\u8bc6\uff0c\u90a3\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u3002\u4e0d\u8fc7\u5144\u5f1f\u6709\u4e00\u70b9\u987e\u8651\uff0c\u4e0d\u77e5\u8be5\u4e0d\u8be5\u8bf4\u3002\u201d\u4e01\u6e38\u9053\uff1a\u201c\u90d1\u5c9b\u4e3b\u4f46\u8bf4\u4e0d\u59a8\u3002\u201d\u90d1\u8d77\u4e91\u9053\uff1a\u201c\u5b5f\u8001\u7237\u5b50\u5728\u4fdd\u5b9a\u5e9c\u8fd9\u4e9b\u5e74\uff0c\u8eab\u5bb6\u8d22\u4ea7\uff0c\u975e\u540c\u5c0f\u53ef\u3002\u54b1\u4eec\u5927\u5bb6\u6240\u5e72\u7684\uff0c\u5374\u662f\u5578\u805a\u5c71\u6797\u3001\u6740\u5b98\u9020\u53cd\u7684\u52fe\u5f53\uff0c\u8981\u662f\u5b5f\u8001\u7237\u5b50\u7ed9\u54b1\u4eec\u5e26\u5934\uff0c\u5fc5\u5b9a\u6709\u4e8b\u8fde\u7d2f\u4e8e\u4ed6\uff0c\u5927\u5bb6\u5fc3\u91cc\u4e0d\u5b89\u3002\u201d\u7fa4\u96c4\u4e00\u542c\u8fd9\u8bdd\u5012\u4e5f\u6709\u7406\uff0c\u5404\u4eba\u9759\u9ed8\u4e86\u4e00\u4f1a\u3002 \u3000\u3000\u91d1\u9f99\u5e2e\u5e2e\u4e3b\u7126\u516c\u793c\u7ad9\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u63a8\u4e3e\u4e00\u4f4d\u6b66\u529f\u76d6\u4e16\u3001\u4ec1\u4e49\u5305\u5929\u7684\u82f1\u96c4\u3002\u8fd9\u4f4d\u82f1\u96c4\u867d\u7136\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u6b66\u6797\u4e2d\u8bb8\u591a\u670b\u53cb\u5927\u90fd\u4e0d\u8bc6\uff0c\u4f46\u5144\u5f1f\u65a9\u9489\u622a\u94c1\u7684\u8bf4\u4e00\u53e5\uff0c\u53ea\u8981\u8fd9\u4f4d\u82f1\u96c4\u80af\u51fa\u6765\u5e26\u5934\uff0c\u505a\u4e8b\u4e00\u5b9a\u516c\u6b63\uff0c\u7ba1\u6559\u5a01\u98ce\u5927\u9707\uff0c\u5b98\u5e9c\u4e0d\u6562\u5c0f\u89d1\u4e86\u54b1\u4eec\u3002\u201d \u3000\u3000\u6c99\u5929\u5e7f\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u5fc3\u91cc\uff0c\u4e5f\u6709\u4e00\u4f4d\u5e74\u8f7b\u7684\u82f1\u96c4\uff0c\u53ea\u6015\u4e0d\u89c1\u5f97\u6bd4\u7126\u5e2e\u4e3b\u6240\u8bf4\u7684\u90a3\u4f4d\u5dee\u3002\u201d\u4ed6\u58f0\u97f3\u5c16\u7ec6\uff0c\u63d0\u9ad8\u4e86\u55d3\u5b50\uff0c\u66f4\u662f\u523a\u8033\u3002 \u3000\u3000\u7126\u516c\u793c\u9053\uff1a\u201c\u5144\u5f1f\u5e74\u7eaa\u4e0d\u6562\u8bf4\u957f\uff0c\u4e5f\u5df2\u865a\u6d3b\u4e86\u4e94\u5341\u591a\u5c81\uff1b\u89c1\u8bc6\u4e0d\u6562\u8bf4\u5e7f\uff0c\u4e5f\u4f1a\u8fc7\u4e86\u5929\u4e0b\u65e0\u6570\u6210\u540d\u7684\u8c6a\u6770\u3002\u53ef\u662f\u50cf\u6211\u6240\u8bf4\u7684\u90a3\u4f4d\u82f1\u96c4\uff0c\u8ba9\u5144\u5f1f\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\u7684\uff0c\u5f53\u4e16\u5374\u4e5f\u53ea\u6709\u4e00\u4eba\u800c\u5df2\u3002\u201d\u7a0b\u9752\u7af9\u51b7\u51b7\u7684\u9053\uff1a\u201c\u6c99\u5929\u5e7f\u6c99\u5be8\u4e3b\u7684\u813e\u6c14\u6211\u662f\u77e5\u9053\u7684\uff0c\u4ed6\u9634\u9633\u5b9d\u6247\u6253\u7a74\u7684\u529f\u592b\uff0c\u5f53\u4eca\u6b66\u6797\u4e2d\u867d\u7136\u8bf4\u4e0d\u4e0a\u72ec\u4e00\u65e0\u4e8c\uff0c\u4e5f\u603b\u662f\u9876\u513f\u5c16\u513f\u7684\u4e86\u3002\u4ed6\u53e3\u670d\u5fc3\u670d\u7684\u4eba\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u9519\uff0c\u6211\u4eec\u9752\u7af9\u5e2e\u4e00\u9f50\u8d5e\u6210\u6c99\u5be8\u4e3b\u7684\u8bdd\u3002\u201d\u7126\u516c\u793c\u80c0\u7ea2\u4e86\u8138\u9053\uff1a\u201c\u76df\u4e3b\u5230\u5e95\u662f\u600e\u6837\u9009\u6cd5\uff1f\u6211\u4eec\u91d1\u9f99\u5e2e\u867d\u7136\u65e0\u7528\uff0c\u4eba\u6570\u5374\u6bd4\u9752\u7af9\u5e2e\u591a\u4e9b\u3002\u201d\u773c\u89c1\u4e24\u4eba\u5c31\u8981\u4e89\u5435\u8d77\u6765\u3002 \u3000\u3000\u5341\u529b\u5927\u5e08\u9053\uff1a\u201c\u7126\u5e2e\u4e3b\u4e14\u83ab\u5fc3\u6025\uff0c\u4f60\u8bf4\u7684\u90a3\u4f4d\u82f1\u96c4\u662f\u8c01\uff0c\u8001\u8872\u731c\u4e2a\u4e5d\u6210\u513f\u4e0d\u4f1a\u9519\u3002\u8bf7\u95ee\u6c99\u5be8\u4e3b\uff0c\u4f60\u8bf4\u7684\u670b\u53cb\u662f\u8c01\uff1f\u4e24\u5bb6\u90fd\u8bf4\u51fa\u6765\uff0c\u8bf7\u5728\u573a\u7684\u670b\u53cb\u4eec\u79c9\u516c\u8bc4\u5b9a\u5c31\u662f\u3002\u4e5f\u8bf4\u4e0d\u5b9a\u5927\u5bb6\u5bf9\u8fd9\u4f4d\u82f1\u96c4\u90fd\u4e0d\u5fc3\u670d\u5462\uff1f\u201d \u3000\u3000\u6c99\u5929\u5e7f\u5411\u8881\u627f\u5fd7\u4e00\u6307\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u7684\u662f\u8fd9\u4f4d\u8881\u76f8\u516c\u3002\u5404\u4f4d\u83ab\u77a7\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6b66\u529f\u884c\u4e8b\u5374\u662f\u9ad8\u4eba\u4e00\u7b49\u3002\u6211\u58f0\u660e\u5728\u5148\uff0c\u5144\u5f1f\u4e0e\u8881\u76f8\u516c\u8fd8\u662f\u6700\u8fd1\u76f8\u8bc6\uff0c\u8ddf\u4ed6\u65e2\u975e\u540c\u95e8\uff0c\u53c8\u975e\u65e7\u4ea4\uff0c\u7eaf\u56e0\u4f69\u670d\u82f1\u96c4\uff0c\u8fd9\u624d\u4e00\u529b\u63a8\u8350\u3002\u201d\u8fd9\u756a\u8bdd\u4e00\u8bf4\uff0c\u5c71\u4e1c\u5404\u5be8\u7fa4\u76d7\u4e0e\u9752\u7af9\u5e2e\u4f17\u4eba\u9f50\u58f0\u6b22\u547c\uff0c\u58f0\u52bf\u6781\u58ee\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u4ed6\u8bf4\u5230\u81ea\u5df1\uff0c\u4e8b\u5148\u5168\u6ca1\u60f3\u5230\uff0c\u7ad9\u8d77\u8eab\u6765\u53cc\u624b\u4e71\u6447\uff0c\u8fde\u8bf4\uff1a\u201c\u4e0d\u884c\uff01\u201d \u3000\u3000\u7126\u516c\u793c\u5f85\u4eba\u58f0\u7a0d\u9759\uff0c\u4ef0\u5929\u5927\u7b11\uff1a\u201c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u597d\u4e00\u9635\u4e0d\u7edd\u3002\u6c99\u5929\u5e7f\u6012\u9053\uff1a\u201c\u7126\u5e2e\u4e3b\uff0c\u6211\u5012\u8981\u8bf7\u6559\uff0c\u4f60\u5e72\u4e48\u8ba5\u7b11\u5144\u5f1f\uff1f\u201d \u3000\u3000\u7a0b\u9752\u7af9\u4e5f\u6012\u9053\uff1a\u201c\u7126\u5e2e\u4e3b\uff0c\u5728\u4e0b\u7d20\u6765\u4f69\u670d\u4f60\u662f\u4e00\u6761\u597d\u6c49\u5b50\uff0c\u53ef\u662f\u5bf9\u6c99\u5be8\u4e3b\u8fd9\u7b49\u65e0\u793c\uff0c\u5728\u4e0b\u5374\u6613\u77a7\u4e0d\u8fc7\u773c\u3002\u201d\u7126\u516c\u793c\u62f1\u624b\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u54ea\u6562\u8ba5\u7b11\uff1f\u6c99\u5be8\u4e3b\u3001\u7a0b\u5e2e\u4e3b\uff0c\u4f60\u4e24\u4f4d\u53ef\u77e5\u5144\u5f1f\u8981\u63a8\u4e3e\u7684\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u6c99\u5929\u5e7f\u6120\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u77e5\u3002\u201d\u7126\u516c\u793c\u9053\uff1a\u201c\u9664\u4e86\u8fd9\u4f4d\u8881\u76f8\u516c\u8fd8\u6709\u4f55\u4eba\uff1f\u201d\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u8f6c\u6012\u4e3a\u559c\uff0c\u4e5f\u662f\u4ef0\u5929\u5927\u7b11\u3002 \u3000\u3000\u4f17\u4eba\u542c\u4e09\u4eba\u4e89\u4e86\u534a\u5929\uff0c\u8bf4\u7684\u539f\u6765\u540c\u662f\u4e00\u4eba\uff0c\u90fd\u4e0d\u7981\u8f70\u7b11\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5f88\u662f\u7740\u6025\uff0c\u5fd9\u9053\uff1a\u201c\u5144\u5f1f\u5e74\u8f7b\u8bc6\u6d45\uff0c\u4eca\u65e5\u5f97\u80fd\u53c2\u4e0e\u6cf0\u5c71\u5927\u4f1a\uff0c\u5df2\u611f\u8363\u5e78\uff0c\u53ea\u76fc\u8ffd\u968f\u5404\u4f4d\u524d\u8f88\u4e4b\u540e\uff0c\u7a0d\u6548\u5fae\u52b3\uff0c\u5c82\u6562\u62c5\u5f53\u5927\u4efb\uff1f\u8fd8\u8bf7\u53e6\u9009\u8d24\u80fd\u3002\u201d \u3000\u3000\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u8881\u516c\u5b50\u662f\u6211\u4eec\u8881\u7763\u5e08\u7684\u72ec\u751f\u4eb2\u5b50\uff0c\u6211\u4eec\u2018\u5c71\u5b97\u2019\u65e7\u53cb\u5185\u4e3e\u4e0d\u907f\u4eb2\uff0c\u4ee5\u4e3a\u8bf7\u4ed6\u62c5\u5f53\u76df\u4e3b\uff0c\u6700\u662f\u5408\u9002\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u90d1\u8d77\u4e91\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u8881\u7763\u5e08\uff1f\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u5c31\u662f\u5728\u8fbd\u4e1c\u529b\u6297\u6e05\u5175\u3001\u65e0\u8f9c\u88ab\u660f\u541b\u5bb3\u6b7b\u7684\u8881\u5d07\u7115\u8881\u7763\u5e08\u3002\u201d \u3000\u3000\u8881\u5d07\u7115\u6297\u654c\u5fa1\u4fae\uff0c\u6709\u5927\u529f\u4e8e\u56fd\uff0c\u5f53\u65f6\u53ea\u6709\u5317\u4eac\u57ce\u4e2d\u4e4b\u4eba\u624d\u4ee5\u4e3a\u4ed6\u5f53\u771f\u901a\u654c\uff0c\u5b9e\u56e0\u5f3a\u654c\u5175\u4e34\u57ce\u4e0b\uff0c\u541b\u81e3\u767e\u59d3\u5c3d\u7686\u5f20\u7687\u5931\u63aa\uff0c\u4ee5\u81f4\u4e0d\u660e\u662f\u975e\u3002\u4f46\u8881\u5d07\u7115\u60e8\u906d\u6740\u5bb3\uff0c\u5404\u5730\u95fb\u77e5\uff0c\u5374\u6781\u662f\u6124\u6168\u3002\u7fa4\u96c4\u542c\u4e86\u8fd9\u8bdd\uff0c\u53f9\u58f0\u56db\u8d77\uff0c\u672c\u6765\u65e0\u53ef\u65e0\u4e0d\u53ef\u7684\u4eba\u4e5f\u4e00\u81f4\u8d5e\u6210\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6781\u529b\u63a8\u8f9e\uff0c\u5374\u54ea\u91cc\u63a8\u8f9e\u5f97\u6389\uff1f\u52a0\u4e4b\u6295\u964d\u8fc7\u6765\u7684\u6c34\u603b\u5175\u3001\u7531\u8881\u627f\u5fd7\u4ece\u56da\u8f66\u4e0a\u6551\u51fa\u6765\u7684\u8042\u5929\u98ce\u3001\u6881\u94f6\u9f99\u7b49\u4eba\u4e5f\u6781\u529b\u9644\u548c\uff0c\u76df\u4e3b\u4e00\u5e2d\u52bf\u6210\u5b9a\u5c40\u3002 \u3000\u3000\u9f99\u6e38\u5e2e\u5e2e\u4e3b\u8363\u5f69\u672c\u8ddf\u8881\u627f\u5fd7\u6709\u70b9\u8fc7\u8282\uff0c\u4f46\u4e00\u5219\u89c1\u4f17\u671b\u6240\u5f52\uff0c\u5c0f\u5c0f\u4e00\u4e2a\u9f99\u6e38\u5e2e\u4e0d\u80fd\u529b\u6392\u4f17\u8bae\uff0c\u518d\u5219\u60f3\u5230\u4ed6\u5f53\u65e5\u5728\u8862\u6c5f\u4e2d\u4e0d\u4e3a\u5df2\u751a\uff0c\u63b7\u677f\u76f8\u6551\uff0c\u4f7f\u81ea\u5df1\u4e0d\u81f4\u843d\u6c34\u51fa\u4e11\uff0c\u4e5f\u7b97\u53d7\u8fc7\u4ed6\u7684\u6069\u60e0\uff0c\u5fc3\u60f3\u7d22\u6027\u9526\u4e0a\u6dfb\u82b1\uff0c\u8bf4\u51e0\u53e5\u597d\u8bdd\uff0c\u4fbf\u7ad9\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8881\u76f8\u516c\u6b66\u529f\u7cbe\u6e5b\uff0c\u5728\u573a\u8bb8\u591a\u670b\u53cb\u90fd\u77e5\u9053\u7684\u4e86\u3002\u5144\u5f1f\u5c31\u66fe\u683d\u8fc7\u5728\u4ed6\u624b\u91cc\u3002\u201d\u4f17\u4eba\u4e0d\u89c9\u4e00\u6123\uff0c\u8363\u5f69\u53c8\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u5f88\u7ed9\u5144\u5f1f\u7559\u4f59\u5730\uff0c\u5144\u5f1f\u867d\u7136\u683d\u4e86\uff0c\u5374\u4e5f\u5fc3\u4e0b\u611f\u6fc0\u3002\u73b0\u4e0b\u9009\u4ed6\u505a\u76df\u4e3b\uff0c\u5144\u5f1f\u4e00\u529b\u8d5e\u6210\u3002\u201d\u4f17\u4eba\u89c1\u66fe\u7ecf\u4e0e\u4ed6\u654c\u5bf9\u8fc7\u7684\u4eba\u4e5f\u8fd9\u6837\u8bf4\uff0c\u90fd\u6b22\u547c\u8d77\u6765\u3002\u53ea\u6709\u9752\u9752\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u8001\u6ed1\u5934\uff01\u201d \u3000\u3000\u4e01\u7532\u795e\u4e01\u6e38\u8d70\u522b\u8881\u627f\u5fd7\u8eab\u8fb9\uff0c\u5411\u4ed6\u7ec6\u7ec6\u6253\u91cf\uff0c\u89c1\u4ed6\u8eab\u6750\u4e0d\u9ad8\uff0c\u9762\u76ee\u9edd\u9ed1\uff0c\u8c8c\u4e0d\u60ca\u4eba\uff0c\u5e74\u7eaa\u53c8\u8f7b\uff0c\u4f55\u4ee5\u7fa4\u96c4\u5bf9\u4ed6\u5982\u6b64\u62e5\u6234\uff1f\u5fc3\u60f3\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u58f0\u5a01\u4e00\u4e0b\u5b50\u4fbf\u76d6\u8fc7\u4e86\u81ea\u5df1\u5e08\u7236\uff0c\u5f88\u4e0d\u670d\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u606d\u559c\u4f60\u5566\uff0c\u8881\u76f8\u516c\u3002\u201d\u4f38\u624b\u51fa\u53bb\uff0c\u62c9\u7740\u4ed6\u624b\uff0c\u663e\u5f97\u751a\u662f\u4eb2\u70ed\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5144\u5f1f\u5b9e\u5728\u96be\u4ee5\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a81\u89c9\u624b\u4e0a\u4e00\u7d27\u3002\u539f\u6765\u4e01\u6e38\u4f7f\u51fa\u4e86\u201c\u9738\u738b\u625b\u9f0e\u201d\u7684\u5e08\u4f20\u7edd\u827a\uff0c\u7528\u529b\u4e00\u626f\uff0c\u60f3\u6454\u8881\u627f\u5fd7\u4e00\u4ea4\uff0c\u8ba9\u8fd9\u4f4d\u201c\u76df\u4e3b\u201d\u5728\u4f17\u4eba\u9762\u524d\u51fa\u4e2a\u5927\u4e11\uff0c\u867d\u7136\u8fd9\u6837\u4e00\u6765\uff0c\u4e0d\u514d\u5f97\u7f6a\u4e86\u65e0\u6570\u82f1\u96c4\u597d\u6c49\uff0c\u8bf4\u4e0d\u5b9a\u5f53\u573a\u7ed9\u4f17\u4eba\u6253\u6210\u4e00\u56e2\u8089\u9171\uff0c\u4f46\u4ed6\u662f\u4e2a\u83bd\u649e\u4e4b\u5f92\uff0c\u6c14\u6124\u4e4b\u4e0b\uff0c\u4e5f\u987e\u4e0d\u5230\u8fd9\u4e48\u8bb8\u591a\u4e86\u3002\u8881\u627f\u5fd7\u4e0d\u52a8\u58f0\u8272\uff0c\u6697\u4e2d\u4f7f\u51fa\u201c\u5343\u65a4\u5760\u201d\u529f\u3002\u4e01\u6e38\u8fde\u626f\u4e09\u626f\uff0c\u80f3\u81c2\u4e0a\u808c\u8089\u9ad8\u9ad8\u8d32\u8d77\uff0c\u51fa\u5c3d\u4e86\u5e73\u751f\u4e4b\u529b\uff0c\u4f46\u5bf9\u65b9\u5c31\u5982\u751f\u6839\u5728\u77f3\u5c71\u4e0a\u4e00\u822c\uff0c\u53ea\u542c\u4ed6\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u2026\u2026\u62c5\u5f53\u5927\u4efb\u3002\u4e01\u5144\u4ee4\u5e08\u5b5f\u8001\u7237\u5b50\u540d\u6ee1\u5929\u4e0b\uff0c\u5b9a\u6bd4\u5144\u5f1f\u9002\u5f53\u5f97\u591a\u3002\u201d \u3000\u3000\u4e01\u6e38\u518d\u662f\u7528\u529b\u4e00\u626f\uff0c\u81ea\u5df1\u53f3\u81c2\u4e0a\u683c\u7684\u4e00\u58f0\uff0c\u9669\u4e9b\u626f\u8131\u4e86\u9ab1\uff0c\u75be\u5fd9\u653e\u624b\uff0c\u89c1\u8881\u627f\u5fd7\u5374\u4f3c\u6beb\u65e0\u6240\u89c9\uff0c\u77e5\u9053\u5bf9\u65b9\u6b66\u529f\u6bd4\u81ea\u5df1\u4e0d\u77e5\u8981\u9ad8\u51fa\u591a\u5c11\uff0c\u9002\u624d\u82e5\u662f\u4e58\u52bf\u53cd\u51fb\uff0c\u81ea\u5df1\u65e9\u7ed9\u4e22\u4e0b\u5c71\u8c37\u4e4b\u4e2d\uff0c\u4f46\u4ed6\u987e\u5168\u81ea\u5df1\u9762\u5b50\uff0c\u4ee4\u65c1\u4eba\u4e1d\u6beb\u77a7\u4e0d\u51fa\u6765\uff0c\u4e0d\u7981\u987f\u751f\u611f\u6fc0\u4e4b\u610f\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u505a\u76df\u4e3b\u5f88\u597d\uff01\u201d\u8bf4\u7740\u62dc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8fde\u5fd9\u8fd8\u793c\uff0c\u5fc3\u5934\u4e5f\u559c\u6b22\u8fd9\u5927\u6c49\u83bd\u5f97\u53ef\u7231\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u54b1\u4eec\u65e2\u7136\u4f1a\u76df\uff0c\u5c31\u8981\u6709\u4e2a\u76df\u89c4\uff0c\u73b0\u4e0b\u8bf7\u76df\u4e3b\u5ba3\u5e03\uff0c\u5927\u4f19\u513f\u5171\u540c\u5546\u914c\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8fd8\u5f85\u63a8\u8f9e\uff0c\u5b59\u4ef2\u5bff\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u8c26\u8f9e\u4e0d\u5c31\uff0c\u5018\u82e5\u76df\u4e3b\u4e00\u5e2d\u4e0d\u5e78\u843d\u5165\u5978\u4eba\u4e4b\u624b\uff0c\u7978\u5bb3\u4e0d\u5c0f\u3002\u8981\u662f\u4f60\u80fd\u9886\u8896\u7fa4\u96c4\uff0c\u7763\u5e08\u7684\u8840\u6d77\u6df1\u4ec7\u5c31\u53ef\u5f97\u62a5\u4e86\u3002\u7763\u5e08\u4e00\u751f\u505a\u4e8b\uff0c\u5411\u6765\u5c31\u662f\u5f53\u4ec1\u4e0d\u8ba9\uff0c\u4e0d\u907f\u8270\u5371\u3002\u201d\u8881\u627f\u5fd7\u542c\u4ed6\u8d23\u4ee5\u5927\u4e49\uff0c\u66f4\u63d0\u5230\u5148\u516c\u7684\u201c\u597d\u6837\u201d\uff0c\u4e0d\u89c9\u51db\u7136\u5fc3\u60ca\uff0c\u7ad9\u8d77\u8eab\u56e2\u56e2\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5404\u4f4d\u7f8e\u610f\uff0c\u5144\u5f1f\u606d\u656c\u4e0d\u5982\u4ece\u547d\u3002\u53ea\u662f\u5144\u5f1f\u8bc6\u89c1\u6d45\u8584\uff0c\u8fd8\u671b\u5404\u4f4d\u524d\u8f88\u4ee5\u5927\u4e8b\u4e3a\u91cd\uff0c\u968f\u65f6\u6307\u6559\uff0c\u5144\u5f1f\u5fc5\u5b9a\u9075\u4ece\uff0c\u4e0d\u6562\u72c2\u5984\u81ea\u5927\u3002\u201d \u3000\u3000\u7fa4\u96c4\u542c\u4ed6\u5141\u4efb\u76df\u4e3b\uff0c\u6cf0\u5c71\u9876\u4e0a\u767b\u65f6\u6b22\u58f0\u96f7\u52a8\uff0c\u5c71\u8c37\u9e23\u54cd\uff0c\u56db\u4e0b\u91cc\u90fd\u662f\u9f13\u638c\u548c\u6b22\u547c\u7684\u56de\u97f3\uff0c\u4f3c\u4e4e\u811a\u5e95\u7684\u5343\u5cf0\u4e07\u58d1\u4e5f\u4e00\u9f50\u5728\u9f13\u638c\u559d\u5f69\u4e00\u822c\u3002 \u3000\u3000\u7fa4\u96c4\u5f53\u4e0b\u70b9\u8d77\u9999\u70db\uff0c\u4e00\u9f50\u62dc\u5929\u7977\u795d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5411\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u76df\u7ea6\u5c31\u8bf7\u5b59\u53d4\u53d4\u8d77\u8349\u4e86\u3002\u201d\u5b59\u4ef2\u5bff\u4e5f\u4e0d\u63a8\u8f9e\uff0c\u56de\u8fdb\u5e99\u91cc\u8349\u62df\u3002\u4ed6\u77e5\u7fa4\u96c4\u4ee5\u4fe1\u4e49\u4e3a\u5148\uff0c\u4e0d\u91cd\u6587\u91c7\uff0c\u5f53\u4e0b\u8a00\u7b80\u610f\u6df1\u7684\u5199\u4e86\u767e\u4f59\u5b57\u3002\u8881\u627f\u5fd7\u5f53\u4f17\u5ba3\u8bfb\u4e86\u3002\u7fa4\u96c4\u6b43\u8840\u5ba3\u8a93\uff0c\u51b3\u4e0d\u80cc\u76df\u3002\u4e00\u4e2a\u8f70\u52a8\u6cbf\u6d77\u5404\u7701\u6b66\u6797\u7684\u6cf0\u5c71\u5927\u4f1a\uff0c\u81f3\u6b64\u544a\u6210\u3002 \u3000\u3000\u8881\u627f\u5fd7\u51fa\u9053\u4e0d\u5230\u534a\u5e74\uff0c\u4ed7\u7740\u6b66\u529f\u7edd\u9876\uff0c\u81f3\u8bda\u5f85\u4eba\uff0c\u518d\u52a0\u4e4b\u673a\u7f18\u5de7\u5408\uff0c\u4ee5\u53ca\u7236\u4eb2\u7684\u5a01\u540d\uff0c\u7adf\u5c14\u6210\u4e3a\u5357\u5317\u76f4\u96b6\u3001\u9c81\u3001\u8c6b\u3001\u6d59\u3001\u95fd\u3001\u8d63\u4e03\u7701\u8349\u83bd\u7fa4\u8c6a\u7684\u5927\u9996\u9886\u3002 \u3000\u3000\u5f53\u665a\u7fa4\u96c4\u5e2d\u5730\u6b22\u5bb4\uff0c\u6597\u9152\u8f70\u996e\uff0c\u55a7\u95f9\u6b22\u7b11\u4e4b\u58f0\uff0c\u5e03\u6ee1\u5cf0\u8c37\u3002 \u3000\u3000\u6b63\u70ed\u95f9\u95f4\uff0c\u7a81\u89c1\u4e00\u4e2a\u6d41\u661f\u76f4\u51b2\u4e0a\u5929\uff0c\u8fd9\u662f\u5c71\u4e0b\u6709\u8b66\u8baf\u53f7\uff0c\u7fa4\u96c4\u767b\u65f6\u505c\u676f\u4e0d\u996e\u3002\u8881\u627f\u5fd7\u548c\u5b59\u4ef2\u5bff\u7b49\u4eba\uff0c\u7acb\u65f6\u4fbf\u60f3\u8d77\u5f53\u5e74\u805a\u4f1a\u5723\u5d82\u5cf0\u800c\u5b98\u5175\u6765\u88ad\u7684\u60c5\u666f\uff0c\u83ab\u975e\u5b98\u5e9c\u5f97\u77e5\u6f15\u94f6\u88ab\u52ab\u3001\u56e0\u800c\u8c03\u5175\u6765\u653b\u4e48\uff1f \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u4e24\u540d\u5728\u5c71\u5761\u4e0a\u54e8\u5b88\u7684\u6c49\u5b50\u5954\u4e0a\u5c71\u6765\uff0c\u5411\u8881\u627f\u5fd7\u7980\u62a5\uff1a\u201c\u542f\u7980\u76df\u4e3b\uff0c\u5c71\u4e0b\u54e8\u63a2\u6025\u62a5\uff0c\u6e05\u5175\u5927\u519b\u5df2\u653b\u4e0b\u9752\u5dde\uff0c\u6b63\u5411\u6cf0\u5b89\u8fdb\u519b\uff0c\u79bb\u6b64\u5904\u5df2\u4e0d\u8fc7\u4e8c\u767e\u4f59\u91cc\uff0c\u8bf7\u76df\u4e3b\u5b9a\u593a\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u60ca\u9053\uff1a\u201c\u6e05\u5175\u6765\u5f97\u8fd9\u4e48\u5feb\uff01\u201d\u4ed6\u867d\u66fe\u542c\u8bf4\u6e05\u5175\u4e8e\u53bb\u5e74\u5165\u5173\uff0c\u653b\u5230\u5c71\u4e1c\uff0c\u4f46\u4e00\u76f4\u53ea\u5728\u767b\u5dde\u3001\u83b1\u5dde\u4e00\u5e26\u9a9a\u6270\uff0c\u62a2\u52ab\u711a\u6740\uff0c\u60f3\u4e0d\u5230\u7adf\u4f1a\u4e00\u4e3e\u7834\u4e86\u9752\u5dde\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u6e05\u5175\u53bb\u5e74\u5341\u6708\u7ffb\u8fc7\u5899\u5b50\u5cad\uff0c\u76f4\u6253\u5230\u5156\u5dde\uff0c\u5728\u5c71\u4e1c\u5404\u5730\u70e7\u6740\u52ab\u63a0\u3002\u542c\u8bf4\u5e26\u5175\u7684\u5934\u5b50\u662f\u5949\u547d\u5927\u5c06\u519b\u963f\u5df4\u6cf0\u3002\u8fd9\u4eba\u662f\u52aa\u5c14\u54c8\u8d64\u7684\u7b2c\u4e03\u5b50\uff0c\u8fd8\u662f\u9791\u5b50\u7687\u5e1d\u7684\u54e5\u54e5\uff0c\u4ed6\u5584\u80fd\u7528\u5175\uff0c\u66fe\u548c\u6ee1\u6e05\u777f\u4eb2\u738b\u591a\u5c14\u886e\u6253\u6765\u8fc7\u5c71\u4e1c\uff0c\u5bf9\u5c71\u4e1c\u7684\u60c5\u5f62\u662f\u5f88\u719f\u6089\u7684\u3002\u201d\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u591a\u5c14\u886e\u6253\u6765\u8fc7\u5c71\u4e1c\uff1f\u201d\u4ed6\u6f5c\u5fc3\u6b66\u5b66\uff0c\u4e8e\u4e16\u4e8b\u6240\u77e5\u5b9e\u751a\u6709\u9650\u3002\u5b59\u4ef2\u5bff\u53f9\u9053\uff1a\u201c\u90a3\u662f\u56db\u5e74\u524d\u7684\u4e8b\u4e86\u3002\u90a3\u65f6\u76df\u4e3b\u5728\u534e\u5c71\u5b66\u6b66\uff0c\u56e0\u6b64\u4e0d\u77e5\u9053\u3002\u201d\u89c1\u7fa4\u96c4\u6b63\u7eb7\u7eb7\u4e92\u76f8\u8be2\u95ee\uff0c\u4eba\u5fc3\u6d6e\u52a8\uff0c\u4fbf\u7ad9\u8d77\u8eab\u6765\uff0c\u767b\u4e0a\u9ad8\u5904\u4e00\u5757\u5927\u77f3\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5c71\u4e0b\u5144\u5f1f\u6025\u62a5\uff0c\u6e05\u5175\u653b\u7834\u9752\u5dde\uff0c\u6b63\u5411\u6cf0\u5b89\u800c\u6765\u3002\u5404\u4f4d\u8bf7\u7ee7\u7eed\u559d\u9152\uff0c\u76df\u4e3b\u81ea\u6709\u4e3b\u5f20\u3002\u201d \u3000\u3000\u7fa4\u96c4\u4e2d\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5927\u4f19\u513f\u51b2\u4e0b\u5c71\u53bb\uff0c\u6740\u4ed6\u5988\u7684\u9791\u5b50\u5175\u3002\u201d \u3000\u3000\u53c8\u6709\u4eba\u53eb\u9053\uff1a\u201c\u9791\u5b50\u5175\u53ef\u6b3a\u4fae\u5f97\u54b1\u4eec\u72e0\u4e86\uff0c\u8fd9\u56de\u975e\u8ddf\u4ed6\u62da\u4e2a\u4f60\u6b7b\u6211\u6d3b\u4e0d\u53ef\u3002\u201d\u6ee1\u5c71\u8f70\u53eb\uff0c\u7fa4\u60c5\u6124\u6fc0\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u56de\u5230\u8881\u627f\u5fd7\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f17\u5144\u5f1f\u90fd\u8981\u53bb\u6253\u9791\u5b50\u5175\uff0c\u4f60\u77a7\u600e\u6837\uff1f\u201c\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u7239\u7239\u4e00\u751f\u5c3d\u5fe0\u62a5\u56fd\uff0c\u4e3a\u7684\u5c31\u662f\u6740\u9791\u5b50\u3002\u773c\u4e0b\u9791\u5b50\u6b3a\u4e0a\u95e8\u6765\uff0c\u6b63\u597d\u4f17\u5144\u5f1f\u5728\u6b64\u805a\u4f1a\uff0c\u54b1\u4eec\u5c31\u6b64\u4e0b\u5c71\u53bb\u6253\u3002\u53ea\u662f\u6211\u4e8e\u884c\u519b\u6253\u4ed7\u4e00\u9053\uff0c\u5168\u7136\u4e0d\u61c2\uff0c\u8fd8\u662f\u8bf7\u5b59\u53d4\u53d4\u53d1\u4ee4\u3002\u201d \u3000\u3000\u5b59\u4ef2\u5bff\u6c89\u541f\u7247\u523b\uff0c\u6d3e\u4e86\u5341\u51e0\u4e2a\u4eba\u51fa\u53bb\u67e5\u63a2\u6e05\u5175\u865a\u5b9e\uff0c\u7136\u540e\u8bf4\u9053\uff1a\u201c\u81ea\u4ece\u7763\u5e08\u8881\u516c\u88ab\u5bb3\uff0c\u671d\u4e2d\u65e0\u4eba\uff0c\u518d\u4e5f\u65e0\u529b\u6297\u5fa1\u6e05\u5175\u4e86\u3002\u5d07\u796f\u4e5d\u5e74\u516d\u6708\uff0c\u6ee1\u6e05\u5934\u5b50\u7687\u592a\u6781\u6d3e\u4e86\u963f\u9f50\u683c\u3001\u963f\u5df4\u6cf0\u7b49\u5927\u5c06\u653b\u8fdb\u957f\u57ce\uff0c\u76f4\u6253\u5230\u5317\u76f4\u96b6\u8179\u5730\u3002\u5341\u4e00\u5e74\uff0c\u4e5d\u738b\u591a\u5c14\u886e\u7387\u9886\u963f\u5df4\u6cf0\u7b49\u4eba\u53c8\u6253\u5230\u5317\u76f4\u96b6\uff0c\u5fe0\u81e3\u5362\u8c61\u6607\u548c\u5b59\u627f\u5b97\u5148\u540e\u6b89\u56fd\u3002 \u3000\u3000\u591a\u5c14\u886e\u90a3\u5e74\u8fd8\u653b\u7834\u4e86\u6d4e\u5357\uff0c\u4fd8\u864f\u4e86\u6211\u56db\u5341\u591a\u4e07\u767e\u59d3\u53bb\u3002\u8fd9\u4e00\u6b21\u53c8\u662f\u963f\u5df4\u6cf0\u8fd9\u9791\u5b50\u5c06\u519b\u6765\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6e05\u5175\u600e\u5730\u53c8\u4e0d\u653b\u5317\u4eac\uff0c\u53ea\u662f\u653b\u6253\u6cb3\u5317\u3001\u5c71\u4e1c\u5404\u5904\uff1f\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u7687\u592a\u6781\u662f\u5f88\u4f1a\u7528\u5175\u7684\u3002\u4ed6\u6d3e\u5175\u6765\u6cb3\u5317\u3001\u5c71\u4e1c\uff0c\u5176\u5fd7\u4e0d\u5728\u5360\u5730\uff0c\u800c\u662f\u62a2\u593a\u8d22\u7269\uff0c\u6740\u4eba\u653e\u706b\uff0c\u6467\u7834\u6211\u4e2d\u56fd\u7684\u7cbe\u534e\uff0c\u8981\u4ee4\u5f97\u5927\u660e\u7cbe\u75b2\u529b\u5c3d\uff0c\u7136\u540e\u518d\u4e00\u4e3e\u800c\u653b\u5317\u4eac\u3002\u5f53\u5e74\u4ed6\u8fdb\u653b\u5317\u4eac\uff0c\u5728\u8881\u7763\u5e08\u624b\u4e0b\u5403\u4e86\u4e2a\u8d25\u4ed7\uff0c\u6b64\u540e\u5c31\u4e0d\u6562\u518d\u653b\u4eac\u5e08\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5ffd\u60f3\uff1a\u201c\u95ef\u738b\u548c\u5404\u8def\u4e49\u519b\u56db\u4e0b\u6d41\u7a9c\uff0c\u5c82\u4e0d\u662f\u5e2e\u4e86\u9791\u5b50\u5175\u7684\u5927\u5fd9\uff1f\u201d\u8fd9\u53e5\u8bdd\u5374\u4e0d\u4fbf\u51fa\u53e3\uff0c\u53ea\u5fc3\u4e2d\u9690\u9690\u89c9\u5f97\u4e0d\u5b89\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u9791\u5b50\u5175\u51e0\u6b21\u4e09\u756a\u7684\u6253\u6765\u6cb3\u5317\u3001\u5c71\u4e1c\uff0c\u4e00\u8def\u4e0a\u52bf\u5982\u7834\u7af9\uff0c\u660e\u5175\u4ece\u6765\u6ca1\u6253\u8fc7\u4e00\u573a\u80dc\u4ed7\uff0c\u9791\u5b50\u5175\u5c06\u4e00\u5b9a\u4e0d\u628a\u660e\u5175\u653e\u5728\u773c\u91cc\u3002\u5e38\u8a00\u9053\u9a84\u5175\u5fc5\u8d25\uff0c\u54b1\u4eec\u6b63\u597d\u4e58\u673a\u6740\u6740\u4ed6\u4eec\u7684\u5a01\u98ce\u3002\u4ece\u9752\u5dde\u6765\u6cf0\u5b89\uff0c\u9526\u9633\u5173\u662f\u5fc5\u7ecf\u4e4b\u5730\u3002\u90a3\u91cc\u5730\u52bf\u9669\u8981\uff0c\u54b1\u4eec\u53ef\u5728\u9526\u9633\u5173\u8bbe\u4f0f\uff0c\u72e0\u72e0\u7684\u6253\u4e00\u4ed7\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5927\u559c\uff0c\u7ad9\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u4f17\u4f4d\u5144\u5f1f\uff0c\u54b1\u4eec\u8fd9\u5c31\u6740\u9791\u5b50\u5175\u53bb\uff0c\u4eca\u665a\u597d\u597d\u5b89\u7761\uff0c\u660e\u65e5\u6e05\u6668\u4e0b\u5c71\u3002\u201d\u7fa4\u96c4\u5927\u58f0\u5450\u558a\uff1a\u201c\u6740\u9791\u5b50\u5175\uff0c\u6740\u9791\u5b50\u5175\uff01\u201d \u3000\u3000\u6b21\u65e5\u6e05\u6668\uff0c\u8881\u627f\u5fd7\u548c\u5b59\u4ef2\u5bff\u5546\u8bae\u540e\uff0c\u5206\u9063\u7fa4\u96c4\u5148\u540e\u51fa\u53d1\u3002 \u3000\u3000\u7ea6\u5b9a\u56db\u65b9\u57cb\u4f0f\uff0c\u89c1\u5230\u76df\u4e3b\u4e2d\u519b\u7684\u9ec4\u8272\u5927\u65d7\u9ad8\u9ad8\u7ad6\u8d77\uff0c\u4fbf\u4e00\u9f50\u5411\u6e05\u5175\u51b2\u6740\u3002\u547d\u6c34\u603b\u5175\u5e26\u540c\u4e24\u5343\u540d\u672c\u90e8\u5175\u9a6c\uff0c\u6253\u5934\u9635\u8fce\u654c\uff0c\u751f\u6015\u6c34\u603b\u5175\u4e0b\u5c71\u540e\u53d8\u5366\uff0c\u6d3e\u4e86\u7126\u516c\u793c\u7387\u540c\u91d1\u9f99\u5e2e\u7684\u624b\u4e0b\u76d1\u89c6\u3002\u8981\u6c34\u603b\u5175\u53ea\u8bb8\u8d25\uff0c\u4e0d\u8bb8\u80dc\uff0c\u5f15\u8bf1\u6e05\u5175\u8fc7\u6765\u3002\u6c34\u603b\u5175\u6240\u90e8\u5175\u7532\u5668\u4ed7\u4e00\u5e94\u4ff1\u5168\uff0c\u5c3d\u662f\u660e\u519b\u670d\u8272\uff0c\u5b9e\u65e0\u534a\u5206\u7834\u7efd\uff0c\u81f3\u4e8e\u6253\u8d25\u4ed7\u4e43\u660e\u5175\u5bb6\u5e38\u4fbf\u996d\uff0c\u66f4\u80fd\u5c3d\u5c55\u6240\u957f\u3002 \u3000\u3000\u90a3\u9526\u9633\u5173\u4e24\u4fa7\u53cc\u5cf0\u5939\u9053\uff0c\u53ea\u4e2d\u95f4\u4e00\u6761\u5c0f\u5f84\u3002\u5230\u7b2c\u56db\u65e5\u508d\u665a\uff0c\u8033\u542c\u5f97\u558a\u58f0\u5927\u4f5c\uff0c\u4f17\u660e\u5175\u7529\u7532\u66f3\u5175\uff0c\u4ece\u5c0f\u5f84\u5954\u6765\u3002\u6c34\u603b\u5175\u8de8\u4e0b\u6218\u9a6c\uff0c\u624b\u6267\u5927\u5200\uff0c\u4eb2\u81ea\u65ad\u540e\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u4fbf\u89c1\u4e00\u7fa4\u8fab\u5b50\u5175\u8702\u6d8c\u800c\u6765\u3002\u8881\u627f\u5fd7\u4f0f\u5728\u5de6\u5cf0\u7684\u5ca9\u77f3\u4e4b\u540e\uff0c\u521d\u6b21\u89c1\u5230\u6e05\u5175\uff0c\u60f3\u8d77\u7236\u4eb2\u8fde\u5e74\u4e0e\u9791\u5b50\u5175\u8840\u6218\uff0c\u4e0d\u7531\u5f97\u5168\u8eab\u70ed\u8840\u5982\u6cb8\uff0c\u9ad8\u4e3e\u91d1\u86c7\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u5b59\u53d4\u53d4\uff0c\u54b1\u4eec\u51b2\u4e0b\u53bb\uff01\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\uff0c\u5f85\u9791\u5b50\u5175\u5927\u961f\u8fc7\u6765\u3002\u90a3\u65f6\u54b1\u4eec\u518d\u7ad6\u8d77\u9ec4\u65d7\uff0c\u56db\u9762\u4f0f\u5175\u9f50\u8d77\uff0c\u6e05\u5175\u4fbf\u8d70\u4e0d\u8131\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u53f7\u89d2\u58f0\u54cd\u8d77\uff0c\u5927\u961f\u6e05\u519b\u9a91\u5175\u51b2\u5230\uff0c\u6570\u5341\u591a\u843d\u540e\u7684\u660e\u5175\u767b\u65f6\u88ab\u5200\u780d\u67aa\u523a\uff0c\u5c38\u6a2a\u5c31\u5730\u3002\u8881\u627f\u5fd7\u5fc3\u4e0b\u4e0d\u5fcd\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u51b2\u4e0b\u53bb\u63a5\u5e94\uff01\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u8fd8\u5f97\u7b49\u4e00\u4f1a\u3002\u201d\u9752\u9752\u6025\u9053\uff1a\u201c\u518d\u4e0d\u4e0b\u53bb\uff0c\u6211\u4eec\u7684\u4eba\u8981\u7ed9\u4ed6\u4eec\u6740\u5149\u4e86\u3002\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u518d\u7b49\u4e00\u4f1a\uff01\u201d \u3000\u3000\u9752\u9752\u6025\u5f97\u53ea\u662f\u987f\u8db3\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u53f3\u5cf0\u4e0a\u558a\u58f0\u5927\u4f5c\uff0c\u6c99\u5929\u5e7f\u7387\u9886\u5c71\u4e1c\u5404\u5be8\u7fa4\u76d7\uff0c\u4ece\u5c71\u5761\u4e0a\u6740\u5c06\u4e0b\u6765\u3002\u5b59\u4ef2\u5bff\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u597d\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5b59\u4ef2\u5bff\u9053\uff1a\u201c\u6e05\u5175\u6765\u7684\u53ea\u662f\u5148\u950b\uff0c\u8fd9\u4e00\u6765\uff0c\u5c31\u6293\u4e0d\u5230\u4ed6\u4eec\u7684\u5143\u5e05\u4e86\u3002\u600e\u4e48\u4e0d\u89c1\u65d7\u53f7\uff0c\u4fbf\u81ea\u884c\u52a8\u624b\u4e86\uff1f\u201d\u53ea\u89c1\u5c71\u4e1c\u7fa4\u76d7\u4e00\u9f13\u4f5c\u6c14\u7684\u6740\u5165\u6e05\u5175\u9635\u4e2d\uff0c\u8ddf\u7740\u9752\u7af9\u5e2e\u3001\u91d1\u9f99\u5e2e\uff0c\u4ee5\u53ca\u5404\u5904\u57cb\u4f0f\u7684\u7fa4\u8c6a\u4e00\u65f6\u5c3d\u8d77\uff0c\u6c34\u603b\u5175\u4e5f\u5e26\u540c\u660e\u5175\u56de\u5934\u622a\u6740\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u8fde\u58f0\u53f9\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u5e74\u8881\u516c\u5e26\u5175\uff0c\u90e8\u4e0b\u82e5\u662f\u8fd9\u822c\u4e0d\u542c\u53f7\u4ee4\uff0c\u81ea\u884c\u6740\u654c\uff0c\u6240\u6709\u7684\u5927\u5c06\u4e00\u4e2a\u4e2a\u90fd\u975e\u7ed9\u8881\u516c\u8bf7\u51fa\u5c1a\u65b9\u5b9d\u5251\u65a9\u4e86\u4e0d\u53ef\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u4e0b\u6b49\u7136\uff0c\u9053\uff1a\u201c\u90fd\u662f\u6211\u4e8b\u5148\u6ca1\u4e25\u7533\u53f7\u4ee4\u7684\u4e0d\u662f\u3002\u201d\u5b59\u4ef2\u5bff\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4e9b\u82f1\u96c4\u597d\u6c49\uff0c\u6bcf\u4e2a\u4eba\u6b66\u529f\u90fd\u5f3a\uff0c\u4f46\u76f4\u662f\u4e00\u7fa4\u4e4c\u5408\u4e4b\u4f17\uff0c\u600e\u6bd4\u5f97\u8881\u516c\u5f53\u5e74\u5728\u5b81\u8fdc\u6240\u7ec3\u7684\u7cbe\u5175\uff1f\u76df\u4e3b\u4f60\u4e5f\u662f\u65e0\u6cd5\u53ef\u65bd\u7684\u3002\u5509\uff0c\u9ec4\u65d7\u8fd8\u6ca1\u7ad6\u8d77\uff0c\u5927\u4f19\u513f\u5c31\u4e71\u7cdf\u7cdf\u7684\u51b2\u6740\u51fa\u53bb\u4e86\uff0c\u8fd9\u54ea\u91cc\u662f\u6253\u4ed7\uff0c\u7b80\u76f4\u662f\u80e1\u95f9\uff01\u201d \u3000\u3000\u4e0d\u4f4f\u7684\u5509\u58f0\u53f9\u6c14\uff0c\u60f3\u8d77\u5f53\u5e74\u8881\u5d07\u7115\u5728\u5b81\u9526\u5e26\u5175\u65f6\u7684\u53f7\u4ee4\u4e25\u5cfb\uff0c\u5341\u4f59\u4e07\u5175\u5c06\u65e0\u4e0d\u8083\u7136\u5949\u547d\uff0c\u61ca\u607c\u4e4b\u4e2d\uff0c\u53c8\u611f\u5fc3\u9178\u3002 \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u53f9\u6c14\u4e5f\u65e0\u7528\u4e86\u3002\u627f\u5fd7\u54e5\u54e5\uff0c\u54b1\u4eec\u52a8\u624b\u5427\uff01\u201d\u8881\u627f\u5fd7\u65e9\u5df2\u5fc3\u75d2\u96be\u6414\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u5927\u4f19\u513f\u6740\u554a\uff01\u201d\u624b\u6267\u91d1\u86c7\u5251\uff0c\u51b2\u4e0b\u5cf0\u53bb\u3002\u5b59\u4ef2\u5bff\u60ca\u53eb\uff1a\u201c\u76df\u4e3b\uff0c\u76df\u4e3b\uff01\u4f60\u662f\u4e3b\u5e05\uff0c\u987b\u5f53\u5750\u9547\u4e2d\u519b\uff0c\u4e0d\u53ef\u4eb2\u4e34\u524d\u654c\u2026\u2026\u201d\u53eb\u58f0\u672a\u6bd5\uff0c\u8881\u627f\u5fd7\u5c55\u5f00\u8f7b\u529f\uff0c\u65e9\u53bb\u5f97\u8fdc\u4e86\uff0c\u4f46\u89c1\u4ed6\u75be\u51b2\u5165\u9635\uff0c\u91d1\u86c7\u5251\u6325\u52a8\uff0c\u524a\u53bb\u4e86\u4e24\u540d\u6e05\u5175\u7684\u8111\u888b\u3002 \u3000\u3000\u5343\u4f59\u540d\u6e05\u5175\u6324\u5728\u5c71\u9053\u4e4b\u4e2d\uff0c\u96be\u4ee5\u7ed3\u9635\u4e3a\u6218\u3002\u654c\u4eba\u51b2\u5230\u8eab\u524d\uff0c\u5f13\u7bad\u4e5f\u7528\u4e0d\u4e0a\u4e86\uff0c\u88ab\u7fa4\u96c4\u56db\u9762\u516b\u65b9\u7684\u56f4\u4e0a\u653b\u6253\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5df2\u5c3d\u6570\u5c31\u6b7c\u3002\u6e05\u519b\u7edf\u5e05\u963f\u5df4\u6cf0\u5f97\u62a5\u524d\u950b\u5728\u9526\u9633\u5173\u4e2d\u4f0f\u8986\u6ca1\uff0c\u5f53\u5373\u7387\u5175\u9000\u56de\u9752\u5dde\u3002 \u3000\u3000\u8fd9\u4e00\u5f79\u867d\u7136\u6ca1\u6740\u4e86\u963f\u5df4\u6cf0\uff0c\u4f46\u805a\u6b7c\u6e05\u519b\u4e00\u5343\u4f59\u4eba\uff0c\u5b9e\u662f\u5341\u4f59\u5e74\u6765\u4ece\u6240\u672a\u6709\u7684\u5927\u80dc\u3002\u7fa4\u96c4\u5728\u9526\u9633\u5173\u524d\u5927\u53eb\u5927\u8df3\uff0c\u6b22\u547c\u82e5\u72c2\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77a7\u7740\u91d1\u86c7\u5251\u4e0a\u7684\u70b9\u70b9\u8840\u8ff9\uff0c\u5fc3\u60f3\uff1a\u201c\u6b64\u5251\u4eca\u65e5\u6740\u4e86\u4e0d\u5c11\u9791\u5b50\u5175\uff0c\u624d\u4e0d\u6789\u4e86\u8fd9\u5251\u8eab\u4e0a\u7684\u9690\u9690\u78a7\u8840\uff01\u201d \u3000\u3000\u5f53\u665a\u8881\u627f\u5fd7\u3001\u5b59\u4ef2\u5bff\u4e0e\u6731\u5b89\u56fd\u3001\u502a\u6d69\u3001\u7f57\u5927\u5343\u7b49\u8c08\u5230\u4eca\u65e5\u4e00\u573a\u5927\u6377\uff0c\u5b9e\u53ef\u6170\u8881\u7763\u5e08\u7684\u5728\u5929\u4e4b\u7075\uff0c\u90fd\u662f\u4e0d\u7981\u70ed\u6cea\u76c8\u7736\u3002 \u3000\u3000\u5b59\u4ef2\u5bff\u4ee5\u6740\u4e0d\u4e86\u6e05\u519b\u5143\u5e05\u963f\u5df4\u6cf0\uff0c\u5140\u81ea\u6068\u6068\u4e0d\u5df2\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5b59\u53d4\u53d4\uff0c\u54b1\u4eec\u8fd9\u6279\u4eba\uff0c\u5f53\u771f\u8981\u6253\u5927\u4ed7\u662f\u4e0d\u6210\u7684\u3002\u660e\u65e5\u6211\u5317\u4e0a\uff0c\u8fd9\u4e9b\u660e\u519b\u5b98\u5175\u548c\u522b\u7684\u5f1f\u5144\u4eec\u8bf7\u4f60\u4e0e\u6731\u53d4\u53d4\u3001\u502a\u53d4\u53d4\u3001\u7f57\u53d4\u53d4\u5404\u4f4d\u597d\u597d\u64cd\u7ec3\uff0c\u65e5\u540e\u518d\u78b0\u4e0a\u9791\u5b50\u5175\uff0c\u53ef\u4e0d\u4f1a\u518d\u50cf\u4eca\u65e5\u8fd9\u822c\u4e71\u6740\u4e00\u9635\u4e86\u3002\u201d\u5b59\u4ef2\u5bff\u7b49\u4ff1\u5404\u5949\u547d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e0e\u9752\u9752\u5e76\u80a9\u6f2b\u6b65\uff0c\u773c\u89c1\u7fa4\u96c4\u4e1c\u4e00\u5806\u3001\u897f\u4e00\u5806\u7684\u56f4\u7740\u8c08\u8bba\uff0c\u4eba\u4eba\u795e\u60c5\u6fc0\u6602\uff0c\u8bf4\u7684\u81ea\u90fd\u662f\u65e5\u95f4\u7684\u5927\u80dc\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u65e5\u8fd8\u53ea\u4e00\u6218\uff0c\u8981\u706d\u4e86\u6ee1\u6e05\u9791\u5b50\uff0c\u5c1a\u987b\u8840\u6218\u767e\u573a\uff0c\u5f53\u771f\u662f\uff1a\u2018\u6177\u6168\u540c\u4ec7\u65e5\uff0c\u95f4\u5173\u767e\u6218\u65f6\u3002\u2019\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u53e5\u8bd7\u505a\u5f97\u771f\u597d\u3002\u201d\u8881\u627f\u5fd7\u5fae\u7b11\u9053\uff1a\u201c\u6211\u600e\u4f1a\u505a\u8bd7\uff1f\u8fd9\u662f\u7239\u7239\u7684\u9057\u4f5c\u3002\u201d \u3000\u3000\u9752\u9752\u55ef\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53f9\u9053\uff1a\u201c\u6211\u751a\u4e48\u90fd\u53ca\u4e0d\u4e0a\u7239\u7239\uff0c\u4ed6\u4f1a\u505a\u8bd7\uff0c\u4f1a\u7528\u5175\u6253\u4ed7\uff0c\u6211\u53ef\u5168\u4e0d\u4f1a\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u5374\u4e00\u5b9a\u6bd4\u4f60\u7239\u7239\u5f3a\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u7239\u7239\u8fdb\u58eb\u51fa\u8eab\uff0c\u6ca1\u7ec3\u8fc7\u6b66\u3002\u4f46\u6b66\u529f\u5f3a\u53ea\u80fd\u529e\u4e9b\u5c0f\u4e8b\uff0c\u53ef\u529e\u4e0d\u4e86\u5927\u4e8b\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4e5f\u4e0d\u89c1\u5f97\uff0c\u6b66\u529f\u5f3a\uff0c\u5f53\u7136\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7a81\u7136\u62d4\u51fa\u91d1\u86c7\u5251\u6765\uff0c\u865a\u5288\u4e24\u4e0b\uff0c\u864e\u864e\u751f\u98ce\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u9752\u5f1f\uff0c\u6211\u53bb\u523a\u6740\u9791\u5b50\u7687\u5e1d\u7687\u592a\u6781\uff0c\u518d\u53bb\u523a\u6740\u5d07\u796f\u7687\u5e1d\uff0c\u4e3a\u6211\u7239\u7239\u62a5\u4ec7\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e8c\u56de\u3000\u738b\u6bcd\u6843\u4e2d\u836f \u5934\u9640\u5e2d\u4e0a\u73cd",content:(0,t.jsx)(t.Fragment,{children:"\u8881\u627f\u5fd7\u548c\u9752\u9752\u3001\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u4e09\u4eba\u62bc\u7740\u94c1\u7bb1\u9996\u9014\u8d74\u4eac\u3002\u7a0b\u9752\u7af9\u4e0e\u6c99\u5929\u5e7f\u8c6a\u5174\u52c3\u53d1\uff0c\u8981\u968f\u76df\u4e3b\u5230\u4eac\u5e08\u53bb\u901b\u901b\u3002\u8881\u627f\u5fd7\u89c1\u591a\u6709\u4e24\u4e2a\u5f97\u529b\u5e2e\u624b\u968f\u884c\uff0c\u81ea\u662f\u6b23\u7136\u540c\u610f\u3002\u53c8\u89c1\u6d2a\u80dc\u6d77\u4e00\u8def\u5fe0\u5fc3\u803f\u803f\uff0c\u518d\u65e0\u53cd\u53db\u4e4b\u610f\uff0c\u4fbf\u7ed9\u4ed6\u6cbb\u597d\u4e86\u8eab\u4e0a\u4f24\u52bf\uff0c\u6d2a\u80dc\u6d77\u66f4\u662f\u611f\u6fc0\u3002 \u3000\u3000\u4e00\u884c\u516d\u4eba\u626c\u97ad\u9a70\u9a6c\uff0c\u5728\u4e00\u671b\u65e0\u9645\u7684\u5c71\u4e1c\u5e73\u539f\u4e0a\u5317\u884c\u3002\u8fd9\u4e00\u5e26\u90fd\u662f\u6c99\u5929\u5e7f\u7684\u5c5e\u4e0b\uff0c\u8fdb\u5165\u5317\u76f4\u96b6\u540e\u662f\u9752\u7af9\u5e2e\u7684\u5730\u754c\uff0c\u81ea\u6709\u6cbf\u9014\u5404\u5730\u5934\u76ee\u9686\u91cd\u8fce\u9001\u3002\u9752\u9752\u89c1\u610f\u4e2d\u4eba\u5982\u6b64\u5f97\u4eba\u63a8\u5d07\uff0c\u5fc3\u4e2d\u5f97\u610f\u975e\u51e1\uff0c\u672c\u6765\u7231\u95f9\u95f9\u5c0f\u813e\u6c14\u7684\uff0c\u8fd9\u65f6\u4e5f\u5927\u4e3a\u6536\u655b\u4e86\u3002 \u3000\u3000\u8fd9\u5929\u6765\u5230\u6cb3\u95f4\u5e9c\uff0c\u5f53\u5730\u9752\u7af9\u5e2e\u7684\u5934\u76ee\u5927\u5f20\u7b75\u5e2d\uff0c\u4e3a\u76df\u4e3b\u5e86\u8d3a\uff0c\u4f5c\u966a\u7684\u90fd\u662f\u6cb3\u95f4\u5e9c\u6b66\u6797\u6709\u540d\u4e4b\u58eb\u3002\u9152\u8fc7\u4e09\u5de1\uff0c\u4f17\u4eba\u7eb5\u8c08\u6c5f\u6e56\u8f76\u95fb\uff0c\u6b66\u6797\u638c\u6545\u3002 \u3000\u3000\u5ffd\u6709\u4e00\u4eba\u5411\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u7a0b\u5e2e\u4e3b\uff0c\u518d\u8fc7\u56db\u5929\uff0c\u5c31\u662f\u5b5f\u4f2f\u98de\u5b5f\u8001\u7237\u5b50\u7684\u516d\u5341\u5927\u5bff\uff0c\u4f60\u4e0d\u53bb\u4e86\u5427\uff1f\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6211\u8981\u968f\u76df\u4e3b\u4e0a\u4eac\uff0c\u795d\u5bff\u662f\u4e0d\u80fd\u53bb\u4e86\u3002\u6211\u662f\u793c\u5230\u4eba\u4e0d\u5230\uff0c\u5df2\u5907\u4e86\u4e00\u4efd\u793c\uff0c\u53eb\u4eba\u9001\u53bb\u4fdd\u5b9a\u5e9c\u3002\u201d\u6c99\u5929\u5e7f\u4e5f\u9053\uff1a\u201c\u5144\u5f1f\u7684\u793c\u4e5f\u65e9\u5df2\u9001\u53bb\u3002\u5b5f\u8001\u7237\u5b50\u77e5\u9053\u6211\u4eec\u4e0d\u5230\uff0c\u5fc5\u662f\u8eab\u6709\u8981\u4e8b\uff0c\u51b3\u4e0d\u80fd\u89c1\u602a\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u8fd9\u76d6\u5b5f\u5c1d\u5728\u5317\u4e94\u7701\u5927\u5927\u6709\u540d\uff0c\u65e2\u662f\u4ed6\u5bff\u8fb0\u5728\u5373\uff0c\u4f55\u4e0d\u4e58\u673a\u7ed3\u4ea4\u4e00\u756a\uff1f\u201d\u8bf4\u9053\uff1a\u201c\u5b5f\u8001\u7237\u5b50\u5144\u5f1f\u662f\u4e45\u4ef0\u4e86\uff0c\u539f\u6765\u65e5\u5185\u5c31\u662f\u4ed6\u8001\u4eba\u5bb6\u516d\u5341\u5927\u5e86\uff0c\u5144\u5f1f\u60f3\u524d\u53bb\u795d\u8d3a\uff0c\u5404\u4f4d\u4ee5\u4e3a\u600e\u6837\uff1f\u201d\u4f17\u4eba\u9f13\u638c\u53eb\u597d\uff0c\u90fd\u8bf4\uff1a\u201c\u76df\u4e3b\u7ed9\u4ed6\u8fd9\u4e48\u5927\u7684\u9762\u5b50\uff0c\u5b5f\u8001\u7237\u5b50\u4e00\u5b9a\u4e50\u6781\u3002\u201d \u3000\u3000\u6b21\u65e5\u4f17\u4eba\u6539\u9053\u897f\u884c\uff0c\u8fd9\u5929\u6765\u5230\u9ad8\u9633\uff0c\u79bb\u4fdd\u5b9a\u5e9c\u5df2\u4e0d\u8fc7\u4e00\u65e5\u8def\u7a0b\u3002\u4f17\u4eba\u5230\u5927\u8857\u4e0a\u60a6\u6765\u5ba2\u5e97\u6295\u5bbf\uff0c\u5b89\u987f\u597d\u94c1\u7bb1\u884c\u674e\uff0c\u5230\u5927\u5802\u91cc\u996e\u9152\u7528\u996d\u3002 \u3000\u3000\u53ea\u89c1\u4e1c\u9762\u684c\u8fb9\u5750\u7740\u4e2a\u80d6\u5927\u5934\u9640\uff0c\u5934\u4e0a\u4e00\u4e2a\u94dc\u7b8d\uff0c\u7b8d\u4f4f\u4e86\u957f\u53d1\uff0c\u76f8\u8c8c\u751a\u662f\u5a01\u731b\uff0c\u684c\u4e0a\u5df2\u653e\u4e86\u4e03\u516b\u628a\u7a7a\u9152\u58f6\u3002\u5e97\u5c0f\u4e8c\u9001\u9152\u5230\u6765\uff0c\u4ed6\u63ed\u5f00\u9152\u58f6\u76d6\uff0c\u5c06\u9152\u5012\u5728\u4e00\u53ea\u5927\u7897\u91cc\uff0c\u9aa8\u90fd\u9aa8\u90fd\u4e00\u53e3\u6c14\u559d\u5e72\uff0c\u53cc\u624b\u5de6\u4e0a\u53f3\u843d\uff0c\u6293\u8d77\u76d8\u4e2d\u725b\u8089\uff0c\u7247\u523b\u95f4\u5403\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u4e00\u53e0\u8fde\u58f0\u5927\u56b7\uff1a\u201c\u6dfb\u9152\u6dfb\u8089\uff0c\u5feb\u5feb\uff01\u201d\u8fd9\u65f6\u51e0\u4e2a\u5e97\u5c0f\u4e8c\u6b63\u5fd9\u7740\u62db\u547c\u8881\u627f\u5fd7\u7b49\u4eba\uff0c\u4e0d\u53ca\u7406\u4f1a\u3002\u90a3\u5934\u9640\u5927\u6012\uff0c\u4f38\u638c\u5728\u684c\u4e0a\u731b\u529b\u4e00\u62cd\uff0c\u9152\u58f6\u3001\u676f\u76d8\u90fd\u8df3\u4e86\u8d77\u6765\uff0c\u8fde\u4ed6\u90bb\u684c\u5ba2\u4eba\u7684\u9152\u676f\u90fd\u9707\u7ffb\u4e86\uff0c\u9152\u6c34\u6d41\u4e86\u4e00\u684c\u3002 \u3000\u3000\u90a3\u5ba2\u4eba\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u5374\u662f\u4e2a\u8eab\u6750\u7626\u5c0f\u7684\u6c49\u5b50\uff0c\u4e0a\u5507\u7559\u4e86\u4e24\u6487\u9f20\u987b\uff0c\u7738\u5b50\u4e00\u7ffb\uff0c\u7cbe\u5149\u903c\u4eba\uff0c\u53eb\u9053\uff1a\u201c\u5927\u5e08\u7236\uff0c\u4f60\u8981\u559d\u9152\uff0c\u522b\u4eba\u4e5f\u8981\u559d\u554a\u3002\u201d\u90a3\u5934\u9640\u6b63\u6ca1\u597d\u6c14\uff0c\u53c8\u662f\u91cd\u91cd\u4e00\u638c\u62cd\u5728\u684c\u4e0a\uff0c\u731b\u559d\uff1a\u201c\u6211\u81ea\u53eb\u5e97\u5c0f\u4e8c\uff0c\u5e72\u4f60\u5c41\u4e8b\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4ece\u6765\u6ca1\u89c1\u8fc7\u8fd9\u822c\u51f6\u72e0\u7684\u51fa\u5bb6\u4eba\u3002\u201d\u90a3\u5934\u9640\u559d\u9053\uff1a\u201c\u4eca\u65e5\u53eb\u4f60\u89c1\u89c1\u3002\u201d \u3000\u3000\u9752\u9752\u77a7\u5f97\u4e0d\u670d\u6c14\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u53bb\u7ba1\u7ba1\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u7b49\u7740\u77a7\uff0c\u522b\u770b\u90a3\u6c49\u5b50\u77ee\u5c0f\uff0c\u53ea\u6015\u4e5f\u4e0d\u662f\u4e2a\u597d\u60f9\u7684\u3002\u201d\u9752\u9752\u6b63\u60f3\u77a7\u4e24\u4eba\u6253\u67b6\uff0c\u4e0d\u6599\u90a3\u6c49\u5b50\u597d\u4f3c\u6015\u4e86\u5934\u9640\u7684\u5a01\u52bf\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u7b97\u6211\u9519\uff0c\u6210\u4e0d\u6210\uff1f\u201d\u5934\u9640\u89c1\u4ed6\u8ba4\u9519\uff0c\u6b63\u597d\u5e97\u5c0f\u4e8c\u53c8\u9001\u4e0a\u9152\u6765\uff0c\u4e5f\u5c31\u4e0d\u518d\u7406\u4f1a\uff0c\u81ea\u884c\u559d\u9152\u3002\u90a3\u6c49\u5b50\u8d70\u4e86\u5f00\u53bb\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u53c8\u56de\u6765\u3002\u8881\u627f\u5fd7\u7b49\u89c1\u6ca1\u70ed\u95f9\u597d\u77a7\uff0c\u81ea\u987e\u996e\u9152\u5403\u996d\u3002\u7a81\u7136\u4e00\u9635\u98ce\u8fc7\u53bb\uff0c\u4e00\u80a1\u81ed\u6c14\u6251\u9f3b\u800c\u6765\uff0c\u9752\u9752\u6478\u51fa\u624b\u5e15\u63a9\u4f4f\u9f3b\u5b50\u3002\u8881\u627f\u5fd7\u4e00\u8f6c\u5934\uff0c\u53ea\u89c1\u5934\u9640\u684c\u4e0a\u7aef\u7aef\u6b63\u6b63\u7684\u653e\u7740\u4e00\u628a\u4fbf\u58f6\uff0c\u90a3\u5934\u9640\u7adf\u672a\u5bdf\u89c9\uff0c\u8fd9\u4e00\u4e0b\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u58f0\u6765\uff0c\u5411\u9752\u9752\u4f7f\u4e2a\u773c\u8272\uff0c\u5634\u89d2\u5411\u5934\u9640\u4e00\u52aa\u3002\u9752\u9752\u4e00\u89c1\u4e4b\u4e0b\uff0c\u7b11\u5f97\u5f2f\u4e0b\u8170\u6765\u3002 \u3000\u3000\u5927\u5802\u4e2d\u8bb8\u591a\u5403\u996d\u7684\u4eba\u8fd8\u672a\u53d1\u89c9\uff0c\u90fd\u8bf4\uff1a\u201c\u597d\u81ed\uff0c\u597d\u81ed\uff01\u201d\u90a3\u7626\u5c0f\u6c49\u5b50\u5374\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u9999\u554a\uff0c\u9999\u554a\uff01\u201d\u9752\u9752\u6084\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u5b9a\u662f\u90a3\u6c49\u5b50\u62ff\u6765\u7684\u4e86\u3002\u4ed6\u624b\u811a\u597d\u5feb\uff0c\u4e0d\u77e5\u4ed6\u600e\u4e48\u653e\u7684\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5934\u9640\u4e5f\u89c9\u81ed\u6c14\u89e6\u9f3b\uff0c\u4f38\u624b\u53bb\u62ff\u9152\u58f6\uff0c\u63d0\u5728\u624b\u91cc\u4e00\u770b\u4e0d\u5bf9\uff0c\u8d6b\u7136\u662f\u628a\u4fbf\u58f6\uff0c\u800c\u4e14\u91cd\u7538\u7538\u7684\uff0c\u663e\u7136\u88c5\u6ee1\u4e86\u5c3f\uff0c\u4e0d\u7531\u5f97\u6012\u4e0d\u53ef\u904f\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u628a\u8eab\u65c1\u7684\u5e97\u5c0f\u4e8c\u6253\u5f97\u8dcc\u51fa\u4e08\u4f59\uff0c\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002\u53ea\u542c\u90a3\u7626\u5c0f\u6c49\u5b50\u8fd8\u5728\u5927\u8d5e\uff1a\u201c\u597d\u9152\uff0c\u597d\u9152\uff01\u9999\u554a\uff0c\u9999\u554a\u3002\u201d\u624d\u77e5\u662f\u4ed6\u4f5c\u602a\uff0c\u5288\u8138\u5c06\u4fbf\u58f6\u5411\u4ed6\u63b7\u53bb\u3002\u90a3\u6c49\u5b50\u65e9\u6709\u63d0\u9632\uff0c\u4ed6\u8eab\u6cd5\u6ed1\u6e9c\u5f02\u5e38\uff0c\u77ee\u8eab\u4fbf\u4ece\u684c\u5e95\u94bb\u4e86\u8fc7\u53bb\uff0c\u5df2\u8eb2\u5728\u5934\u9640\u8eab\u540e\u3002\u90a3\u4fbf\u58f6\u5728\u684c\u4e0a\u78b0\u5f97\u7c89\u788e\uff0c\u5c3f\u6c34\u56db\u6e85\u3002\u4f17\u4eba\u5927\u547c\u5c0f\u53eb\uff0c\u7eb7\u7eb7\u8d77\u7acb\u95ea\u907f\u3002 \u3000\u3000\u90a3\u5934\u9640\u6012\u6c14\u66f4\u76db\uff0c\u4f38\u51fa\u4e24\u53ea\u5927\u638c\u56de\u8eab\u5c31\u6293\u3002\u90a3\u6c49\u5b50\u53c8\u4ece\u684c\u5e95\u4e0b\u94bb\u8fc7\u3002\u90a3\u5934\u9640\u4e00\u817f\u8e22\u7ffb\u684c\u5b50\u3002\u5927\u5802\u4e2d\u4e71\u6210\u4e00\u7247\u3002\u4f17\u4eba\u65e9\u90fd\u9000\u5728\u4e24\u65c1\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u6c49\u5b50\u4e1c\u9003\u897f\u7a9c\uff0c\u5934\u9640\u62f3\u6253\u8db3\u8e22\uff0c\u59cb\u7ec8\u78b0\u4e0d\u5230\u4ed6\u8eab\u5b50\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u5927\u5802\u4e2d\u684c\u51f3\u90fd\u5df2\u88ab\u4e24\u4eba\u63a8\u5012\u3002\u7897\u7b77\u9152\u58f6\u6389\u4e86\u4e00\u5730\u3002\u90a3\u6c49\u5b50\u62fe\u8d77\u9152\u58f6\u7b49\u7269\uff0c\u4e0d\u4f4f\u5411\u5934\u9640\u63b7\u53bb\u3002\u5934\u9640\u543c\u53eb\u8fde\u5929\uff0c\u63a5\u8fc7\u56de\u63b7\u3002\u4e24\u4eba\u8eab\u6cd5\u5feb\u6377\uff0c\u5c45\u7136\u90fd\u662f\u4e00\u8eab\u597d\u6b66\u529f\u3002 \u3000\u3000\u6253\u5230\u540e\u6765\uff0c\u5927\u5802\u4e2d\u5df2\u6e05\u51fa\u4e00\u5757\u7a7a\u5730\u3002\u90a3\u6c49\u5b50\u4e0d\u518d\u9000\u907f\uff0c\u62f3\u6765\u8fd8\u62f3\uff0c\u8db3\u6765\u8fd8\u8db3\uff0c\u65bd\u5c55\u5c0f\u5de7\u529f\u592b\u548c\u5934\u9640\u5bf9\u6253\u8d77\u6765\u3002\u5934\u9640\u8eab\u96c4\u529b\u58ee\uff0c\u4f7f\u7684\u662f\u6ca7\u5dde\u5927\u6d2a\u62f3\uff0c\u62f3\u52bf\u864e\u864e\u751f\u98ce\u3002\u90a3\u6c49\u5b50\u7684\u62f3\u6cd5\u5374\u81ea\u6210\u4e00\u5bb6\uff0c\u65f6\u65f6\u53cc\u624b\u4e24\u8fb9\u5212\u52a8\uff0c\u77ee\u8eab\u8e52\u8dda\u800c\u8d70\uff0c\u6a21\u6837\u5341\u5206\u53e4\u602a\uff0c\u504f\u53c8\u8eab\u6cd5\u7075\u52a8\u3002 \u3000\u3000\u9752\u9752\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u5b50\u771f\u96be\u770b\uff0c\u90a3\u53c8\u662f\u751a\u4e48\u6b66\u529f\u4e86\uff1f\u201d\u8881\u627f\u5fd7\u4e5f\u6ca1\u89c1\u8fc7\uff0c\u53ea\u89c9\u4ed6\u624b\u811a\u77eb\u6377\uff0c\u6a21\u6837\u867d\u4e11\uff0c\u5374\u81ea\u6210\u7ae0\u6cd5\uff0c\u5c3d\u80fd\u62b5\u654c\u5f97\u4f4f\u3002\u7a0b\u9752\u7af9\u89c1\u591a\u8bc6\u5e7f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53eb\u505a\u9e2d\u5f62\u62f3\uff0c\u6c5f\u6e56\u4e0a\u4f1a\u7684\u4eba\u4e0d\u591a\u3002\u201d\u9752\u9752\u542c\u4e86\u8fd9\u540d\u79f0\u66f4\u89c9\u597d\u7b11\uff0c\u89c1\u90a3\u6c49\u5b50\u8eab\u5f62\u6b65\u6cd5\u679c\u7136\u6d3b\u8131\u50cf\u662f\u53ea\u9e2d\u5b50\u3002 \u3000\u3000\u90a3\u5934\u9640\u4e45\u6597\u4e0d\u4e0b\uff0c\u7126\u8e81\u8d77\u6765\uff0c\u7a81\u7136\u8dcc\u8dcc\u649e\u649e\uff0c\u4f7f\u51fa\u4e00\u5957\u9c81\u667a\u6df1\u9189\u6253\u5c71\u95e8\u62f3\uff0c\u4e1c\u6b6a\u897f\u5012\uff0c\u5b9b\u7136\u662f\u4e2a\u9189\u6c49\uff0c\u6709\u65f6\u53cc\u8db3\u4e00\u632b\uff0c\u5728\u5730\u4e0a\u6253\u4e00\u4e2a\u6eda\uff0c\u7b49\u654c\u4eba\u653b\u5230\uff0c\u500f\u5730\u8dc3\u8d77\u731b\u51fb\u3002\u4ed6\u53c8\u6eda\u53c8\u7ffb\uff0c\u8eab\u4e0a\u6cbe\u4e86\u4e0d\u5c11\u9152\u996d\u6b8b\u7fb9\uff0c\u8fde\u4fbf\u58f6\u4e2d\u5012\u51fa\u7684\u5c3f\u6c34\uff0c\u4e5f\u6709\u4e0d\u5c11\u6cbe\u5728\u8863\u4e0a\u3002 \u3000\u3000\u6597\u5230\u5206\u9645\uff0c\u5934\u9640\u5ffd\u5730\u62a2\u4e0a\u4e00\u6b65\uff0c\u5de6\u62f3\u4e00\u8bb0\u865a\u62db\uff0c\u53f3\u638c\u201c\u6392\u5c71\u5012\u6d77\u201d\uff0c\u76f4\u5288\u654c\u4eba\u80f8\u53e3\u3002\u90a3\u7626\u5c0f\u6c49\u5b50\u77e5\u9053\u5389\u5bb3\uff0c\u8fd0\u8d77\u5185\u529b\uff0c\u53cc\u638c\u6a2a\u80f8\uff0c\u559d\u4e00\u58f0\uff1a\u201c\u597d\uff01\u201d\u4e09\u5f20\u624b\u638c\u5df2\u62b5\u5728\u4e00\u8d77\u3002\u5934\u9640\u7684\u624b\u638c\u80a5\u5927\uff0c\u6c49\u5b50\u7684\u624b\u638c\u53c8\u7279\u522b\u7626\u5c0f\uff0c\u53cc\u638c\u62b5\u5728\u5934\u9640\u4e00\u638c\u4e4b\u4e2d\uff0c\u6070\u6070\u6b63\u597d\u3002 \u3000\u3000\u4e24\u4eba\u5404\u8fd0\u5168\u529b\uff0c\u5411\u524d\u731b\u63a8\u3002\u5934\u9640\u5de6\u624b\u867d\u7136\u7a7a\u7740\uff0c\u4f46\u5168\u8eab\u4e4b\u529b\u5df2\u8fd0\u5728\u53f3\u638c\uff0c\u5de6\u81c2\u5c31\u5982\u5e9f\u4e86\u4e00\u822c\uff0c\u5168\u7136\u65e0\u529b\u51fa\u62db\u3002\u53cc\u65b9\u52bf\u5747\u529b\u654c\uff0c\u767b\u65f6\u50f5\u6301\u4e0d\u52a8\uff0c\u8fdb\u65e2\u4e0d\u80fd\uff0c\u9000\u4ea6\u4e0d\u5f97\uff0c\u5747\u77e5\u8c01\u5148\u6536\u529b\u9000\u7f29\uff0c\u4e0d\u514d\u7acb\u6bd9\u4e8e\u5bf9\u65b9\u638c\u4e0b\uff0c\u4f46\u5982\u6b64\u62da\u6597\u4e0b\u53bb\uff0c\u52bf\u4e0d\u514d\u5185\u529b\u8017\u7aed\uff0c\u4e24\u8d25\u4ff1\u4f24\u3002\u4e24\u4eba\u5747\u611f\u61ca\u6094\uff0c\u5fc3\u60f3\u4e0e\u5bf9\u65b9\u672c\u65e0\u6028\u4ec7\uff0c\u53ea\u4e0d\u8fc7\u4e00\u65f6\u5fff\u4e89\uff0c\u5982\u6b64\u62da\u4e86\u6027\u547d\uff0c\u5b9e\u5728\u65e0\u8c13\u3002\u518d\u8fc7\u4e00\u9635\uff0c\u4e24\u4eba\u989d\u5934\u90fd\u5192\u51fa\u9ec4\u8c46\u822c\u7684\u6c57\u73e0\u6765\u3002 \u3000\u3000\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u7a0b\u8001\u5144\uff0c\u4f60\u62ff\u53eb\u5316\u68d2\u513f\u53bb\u62c6\u89e3\u4e00\u4e0b\u5427\uff0c\u518d\u8fdf\u4e00\u4f1a\uff0c\u4e24\u4e2a\u90fd\u8981\u7cdf\u7cd5\u3002\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6211\u4e00\u4eba\u6ca1\u8fd9\u672c\u4e8b\uff0c\u8fd8\u662f\u54b1\u54e5\u4fe9\u513f\u9f50\u4e0a\u3002\u201d\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u8fc7\u8fd9\u4e24\u4e2a\u80e1\u95f9\u5bb6\u4f19\u6027\u547d\u867d\u7136\u53ef\u4fdd\uff0c\u91cd\u4f24\u7ec8\u7a76\u96be\u514d\u3002\u201d\u6b63\u8981\u4e0a\u524d\u62c6\u89e3\uff0c\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u6211\u6765\u5427\u3002\u201d\u7f13\u6b65\u8d70\u8fd1\uff0c\u53cc\u624b\u5206\u5728\u4e24\u4eba\u81c2\u5f2f\u91cc\u4e00\u683c\u3002\u5934\u9640\u4e0e\u6c49\u5b50\u7684\u624b\u638c\u500f\u5730\u6ed1\u5f00\uff0c\u6536\u52bf\u4e0d\u4f4f\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u4e09\u638c\u540c\u65f6\u6253\u5728\u8881\u627f\u5fd7\u80f8\u4e0a\u3002\u7a0b\u6c99\u4e24\u4eba\u5927\u53eb\uff1a\u201c\u4e0d\u597d\uff01\u201d\u540c\u65f6\u62a2\u4e0a\u76f8\u6551\uff0c\u5374\u89c1\u4ed6\u795e\u8272\u81ea\u82e5\uff0c\u5e76\u672a\u53d7\u4f24\u3002\u539f\u6765\u8881\u627f\u5fd7\u77e5\u9053\u5018\u82e5\u7528\u529b\u62c6\u89e3\u6216\u662f\u53cd\u63a8\uff0c\u8fd9\u4e24\u4eba\u6b63\u5728\u5168\u529b\u65bd\u4e3a\uff0c\u4e00\u80a1\u5185\u529b\u903c\u56de\u53bb\u53cd\u6253\u81ea\u8eab\uff0c\u5fc5\u53d7\u91cd\u4f24\uff0c\u56e0\u6b64\u8fd0\u6c14\u4e8e\u80f8\uff0c\u63a5\u4e86\u8fd9\u4e09\u638c\uff0c\u4ed7\u7740\u5185\u529f\u795e\u5999\uff0c\u8f7b\u8f7b\u6613\u6613\u7684\u628a\u638c\u529b\u627f\u53d7\u4e86\u3002 \u3000\u3000\u5934\u9640\u548c\u90a3\u6c49\u5b50\u8fd9\u65f6\u529b\u5df2\u4f7f\u5c3d\uff0c\u8f6f\u7ef5\u7ef5\u7684\u762b\u75ea\u5728\u5730\u3002\u7a0b\u9752\u7af9\u548c\u6c99\u5929\u5e7f\u6276\u8d77\u4e24\u4eba\uff0c\u547d\u5e97\u5c0f\u4e8c\u8fdb\u6765\u6536\u62fe\u3002\u8881\u627f\u5fd7\u6478\u51fa\u5341\u4e24\u94f6\u5b50\uff0c\u9012\u7ed9\u638c\u67dc\u7684\u9053\uff1a\u201c\u6253\u574f\u4e86\u7684\u4e1c\u897f\u90fd\u5f52\u6211\u8d54\u3002\u8bb8\u591a\u5ba2\u4eba\u8fd8\u6ca1\u5403\u5b8c\u996d\uff0c\u4f60\u7167\u539f\u6837\u91cd\u65b0\u5f00\u8fc7\uff0c\u90fd\u7b97\u5728\u6211\u5e10\u4e0a\u3002\u201d\u90a3\u638c\u67dc\u7684\u63a5\u4e86\u94f6\u5b50\uff0c\u4e0d\u4f4f\u79f0\u8c22\uff0c\u53eb\u9f50\u4f19\u8ba1\uff0c\u6536\u62fe\u4e86\u6253\u70c2\u7684\u4e1c\u897f\uff0c\u518d\u5f00\u9152\u5e2d\u3002 \u3000\u3000\u8fc7\u5f97\u4e00\u4f1a\uff0c\u5934\u9640\u548c\u90a3\u6c49\u5b50\u529b\u6c14\u6e10\u590d\uff0c\u4e00\u9f50\u8fc7\u6765\u5411\u8881\u627f\u5fd7\u62dc\u8c22\u6551\u547d\u4e4b\u6069\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\u3002\u8bf7\u6559\u4e24\u4f4d\u9ad8\u59d3\u5927\u540d\u3002\u4e24\u4f4d\u5982\u6b64\u6b66\u529f\uff0c\u5fc5\u662f\u6c5f\u6e56\u4e0a\u6210\u540d\u7684\u82f1\u96c4\u597d\u6c49\u4e86\u3002\u201d\u90a3\u5934\u9640\u9053\uff1a\u201c\u6211\u6cd5\u540d\u4e49\u751f\uff0c\u4f46\u65c1\u4eba\u90fd\u53eb\u6211\u94c1\u7f57\u6c49\u3002\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u80e1\u540d\u6842\u5357\u3002 \u3000\u3000\u8bf7\u6559\u9ad8\u59d3\u5927\u540d\uff0c\u8fd9\u4e24\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5c1a\u672a\u56de\u7b54\uff0c\u6c99\u5929\u5e7f\u5df2\u63a5\u53e3\u9053\uff1a\u201c\u539f\u6765\u662f\u5723\u624b\u795e\u5077\u80e1\u5927\u54e5\u3002\u201d\u80e1\u6842\u5357\u89c1\u4ed6\u77e5\u9053\u81ea\u5df1\u59d3\u540d\u548c\u5916\u53f7\uff0c\u5f88\u662f\u559c\u6b22\uff0c\u5fd9\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8bf7\u6559\u5144\u957f\u5c0a\u59d3\u5927\u540d\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u628a\u6c99\u5929\u5e7f\u624b\u4e2d\u7684\u6247\u5b50\u63a5\u8fc7\u4e00\u6296\u3002\u80e1\u6842\u5357\u89c1\u6247\u4e0a\u753b\u7740\u4e2a\u9ab7\u9ac5\u5934\uff0c\u6a21\u6837\u53ef\u6016\uff0c\u4fbf\u9053\uff1a\u201c\u539f\u6765\u662f\u9634\u9633\u6247\u6c99\u5be8\u4e3b\uff0c\u4e45\u6155\u5be8\u4e3b\u4e4b\u540d\uff0c\u5f53\u771f\u5e78\u4f1a\u3002\u201d\u8ddf\u7740\u53c8\u89c1\u5230\u501a\u5728\u684c\u8fb9\u7684\u4e00\u6839\u9752\u7af9\uff0c\u4ed6\u77e5\u9053\u9752\u7af9\u5e2e\u4e2d\u7684\u4eba\u6240\u6301\u9752\u7af9\u4ee5\u7af9\u8282\u591a\u5c11\u5206\u5730\u4f4d\u9ad8\u4e0b\uff0c\u8fd9\u679d\u9752\u7af9\u7adf\u6709\u5341\u4e09\u8282\uff0c\u90a3\u662f\u5e2e\u4e2d\u6700\u9ad8\u7684\u9996\u9886\u4e86\uff0c\u5c31\u5411\u7a0b\u9752\u7af9\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u7a0b\u8001\u5e2e\u4e3b\u5427\uff1f\u201d\u7a0b\u9752\u7af9\u5475\u5475\u7b11\u9053\uff1a\u201c\u5723\u624b\u795e\u5077\u773c\u5149\u5389\u5bb3\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002\u4e24\u4f4d\u4e0d\u6253\u4e0d\u76f8\u8bc6\u3002\u6765\u6765\u6765\uff0c\u5927\u5bb6\u540c\u5e72\u4e00\u676f\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u9f50\u5c31\u5750\uff0c\u80e1\u6842\u5357\u4e0e\u94c1\u7f57\u6c49\u5404\u656c\u4e86\u4e00\u676f\u9152\uff0c\u9053\u58f0\uff1a\u201c\u83bd\u649e\uff01\u201d\u94c1\u7f57\u6c49\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u77e5\u4ece\u54ea\u91cc\u5077\u4e86\u8fd9\u628a\u81ed\u4fbf\u58f6\u6765\uff0c\u771f\u662f\u53e4\u602a\uff01\u201d\u4f17\u4eba\u4e00\u9f50\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6842\u5357\u77e5\u9053\u7a0b\u3001\u6c99\u4e8c\u4eba\u5206\u522b\u662f\u5317\u76f4\u8083\u548c\u5c71\u4e1c\u6c5f\u6e56\u8c6a\u6770\u9996\u9886\uff0c\u4f46\u89c1\u4e8c\u4eba\u5bf9\u8881\u627f\u5fd7\u795e\u6001\u606d\u656c\uff0c\u6b64\u4eba\u521a\u624d\u51fa\u624b\u76f8\u6551\uff0c\u5185\u529f\u6df1\u6e5b\uff0c\u5fc5\u662f\u975e\u540c\u5c0f\u53ef\u4e4b\u4eba\uff0c\u53ea\u662f\u672a\u901a\u59d3\u540d\uff0c\u4e5f\u4e0d\u6562\u8d38\u7136\u518d\u95ee\u3002 \u3000\u3000\u4ed6\u672c\u6765\u751f\u6027\u6ed1\u7a3d\uff0c\u7231\u5f00\u73a9\u7b11\uff0c\u8fd9\u65f6\u5374\u89c4\u89c4\u77e9\u77e9\u7684\u4e0d\u6562\u653e\u8086\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u4e24\u4f4d\u5230\u6b64\u6709\u4f55\u8d35\u5e72\uff1f\u80e1\u8001\u5f1f\u53ef\u662f\u770b\u4e2d\u4e86\u751a\u4e48\u5927\u6237\uff0c\u8981\u4e00\u663e\u8eab\u624b\u4e48\uff1f\u201d\u80e1\u6842\u5357\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u5728\u7a0b\u8001\u524d\u8f88\u7684\u5730\u65b9\u4e0d\u6562\u80e1\u6765\u3002\u6211\u662f\u53bb\u7ed9\u5b5f\u4f2f\u98de\u5b5f\u8001\u7237\u5b50\u62dc\u5bff\u53bb\u7684\u3002\u201d\u94c1\u7f57\u6c49\u4e00\u62cd\u684c\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u4f55\u4e0d\u65e9\u8bf4\uff1f\u6211\u4e5f\u662f\u62dc\u5bff\u53bb\u7684\u3002\u65e9\u77e5\u9053\uff0c\u5c31\u6253\u4e0d\u8d77\u6765\u4e86\uff0c\u53ea\u4e0d\u8fc7\u4f60\u5728\u5b5f\u5927\u7237\u7684\u9152\u7b75\u4e4b\u4e0a\uff0c\u53ef\u522b\u53c8\u7aef\u4e00\u628a\u81ed\u4fbf\u58f6\u51fa\u6765\u3002\u201d\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u5927\u7b11\u3002\u7a0b\u9752\u7af9\u7b11\u9053\uff1a\u201c\u90a3\u597d\u6781\u5566\uff0c\u6211\u4eec\u4e5f\u662f\u8981\u53bb\u7ed9\u5b5f\u8001\u7237\u5b50\u795d\u5bff\uff0c\u660e\u65e5\u6b63\u597d\u7ed3\u4f34\u540c\u884c\u3002\u4e24\u4f4d\u8ddf\u5b5f\u8001\u7237\u5b50\u662f\u597d\u670b\u53cb\u5427\uff1f\u201d \u3000\u3000\u94c1\u7f57\u6c49\u9053\uff1a\u201c\u597d\u670b\u53cb\u662f\u9ad8\u6500\u4e0d\u4e0a\uff0c\u4f46\u8bf4\u6765\u4e5f\u6709\u4e8c\u5341\u591a\u5e74\u4ea4\u60c5\u4e86\u3002\u53ea\u662f\u8fd1\u5e74\u6765\u6211\u591a\u5728\u6e56\u5e7f\u4e00\u5e26\uff0c\u5c11\u5230\u5317\u65b9\u3002\u5012\u6709\u516b\u4e5d\u5e74\u4e0d\u89c1\u5566\u3002\u201d\u80e1\u6842\u5357\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u7f57\u6c49\u5927\u54e5\u8fd8\u5f97\u7ed9\u6211\u5f15\u89c1\u5f15\u89c1\u3002\u201d \u3000\u3000\u94c1\u7f57\u6c49\u5947\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4e0d\u8bc6\u5b5f\u5927\u7237\u4e48\uff1f\u90a3\u53c8\u7ed9\u4ed6\u53bb\u62dc\u751a\u4e48\u5bff\uff1f\u201d \u3000\u3000\u80e1\u6842\u5357\u9053\uff1a\u201c\u5144\u5f1f\u5bf9\u76d6\u5b5f\u5c1d\u5b5f\u5927\u7237\u4e00\u5411\u4ef0\u6155\u5f97\u7d27\uff0c\u53ea\u662f\u6ca1\u7f18\u62dc\u89c1\u3002\u8fd9\u6b21\u65e0\u610f\u4e2d\u5f97\u5230\u4e86\u4e00\u4ef6\u5b9d\u7269\uff0c\u4fbf\u60f3\u501f\u82b1\u732e\u4f5b\uff0c\u4f5c\u4e3a\u5bff\u793c\uff0c\u597d\u5f97\u4f1a\u4e00\u4f1a\u8fd9\u4f4d\u6c5f\u6e56\u95fb\u540d\u7684\u8c6a\u6770\u3002\u201d\u94c1\u7f57\u6c49\u9053\uff1a\u201c\u90a3\u5c31\u662f\u4e86\u3002\u522b\u8bf4\u4f60\u6709\u5bff\u793c\uff0c\u5c31\u662f\u6ca1\u6709\uff0c\u5b5f\u5927\u7237\u8fd8\u4e0d\u662f\u4e00\u6837\u63a5\u5f85\u3002\u8c01\u53eb\u4ed6\u5916\u53f7\u76d6\u5b5f\u5c1d\u5462\uff1f\u54c8\u54c8\uff01\u201d \u3000\u3000\u7a0b\u9752\u7af9\u5374\u7559\u4e86\u5fc3\uff0c\u95ee\u9053\uff1a\u201c\u80e1\u8001\u5f1f\uff0c\u4f60\u5f97\u4e86\u751a\u4e48\u5b9d\u7269\u554a\uff1f \u3000\u3000\u7ed9\u6211\u4eec\u5f00\u5f00\u773c\u754c\u6210\u4e0d\u6210\uff1f\u201d\u6c99\u5929\u5e7f\u4e5f\u9053\uff1a\u201c\u5bfb\u5e38\u7269\u4e8b\u54ea\u4f1a\u5728\u5723\u624b\u795e\u5077\u7684\u773c\u91cc\uff1f\u8fd9\u4e48\u5938\u8d5e\uff0c\u90a3\u5b9a\u662f\u4ef7\u503c\u8fde\u57ce\u4e86\u3002\u201d \u3000\u3000\u80e1\u6842\u5357\u5f88\u662f\u5f97\u610f\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u53ea\u9576\u73e0\u5d4c\u7389\u3001\u624b\u5de5\u7cbe\u81f4\u7684\u9ec4\u91d1\u76d2\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u8033\u76ee\u4f17\u591a\uff0c\u8bf7\u5404\u4f4d\u5230\u5144\u5f1f\u623f\u91cc\u89c2\u770b\u5427\u3002\u201d\u4f17\u4eba\u89c1\u76d2\u5b50\u5df2\u662f\u4ef7\u503c\u4e0d\u8d40\uff0c\u6599\u60f3\u5185\u85cf\u4e4b\u7269\u5fc5\u66f4\u73cd\u8d35\u3002 \u3000\u3000\u80e1\u6842\u5357\u5f85\u4f17\u4eba\u8fdb\u623f\u540e\uff0c\u63a9\u4e0a\u623f\u95e8\uff0c\u6253\u5f00\u76d2\u5b50\uff0c\u9732\u51fa\u4e24\u53ea\u6b7b\u767d\u87fe\u870d\u6765\u3002\u8fd9\u5bf9\u87fe\u870d\u901a\u4f53\u96ea\u767d\uff0c\u773c\u73e0\u5374\u8840\u4e5f\u822c\u7ea2\uff0c\u6a21\u6837\u751a\u662f\u53ef\u7231\uff0c\u5374\u4e5f\u4e0d\u89c1\u6709\u4f55\u73cd\u5f02\u4e4b\u5904\u3002\u80e1\u6842\u5357\u5411\u94c1\u7f57\u6c49\u7b11\u9053\uff1a\u201c\u521a\u624d\u6211\u548c\u8001\u5144\u5bf9\u638c\uff0c\u8981\u662f\u4e00\u9f50\u545c\u547c\u54c0\u54c9\uff0c\u90a3\u4e5f\u662f\u5927\u96be\u4e34\u5934\uff0c\u65e0\u6cd5\u53ef\u65bd\u4e86\u3002\u4f46\u5982\u53ea\u662f\u8eab\u53d7\u91cd\u4f24\uff0c\u6211\u5374\u6709\u89e3\u6551\u4e4b\u65b9\u3002\u201d\u6307\u7740\u767d\u87fe\u870d\u9053\uff1a\u201c\u8fd9\u662f\u4ea7\u5728\u897f\u57df\u96ea\u5c71\u4e0a\u7684\u6731\u775b\u51b0\u87fe\uff0c\u4efb\u4ed6\u591a\u5389\u5bb3\u7684\u5185\u4f24\u3001\u5200\u4f24\uff0c\u53ea\u8981\u5f53\u573a\u4e0d\u6b7b\uff0c\u4e00\u670d\u51b0\u87fe\uff0c\u836f\u5230\u4f24\u6108\uff0c\u771f\u662f\u7075\u4e39\u5999\u836f\uff0c\u65e0\u6bd4\u795e\u5947\u3002\u8981\u662f\u4e2d\u4e86\u5267\u6bd2\uff0c\u8fd9\u51b0\u87fe\u66f4\u6709\u53bb\u6bd2\u4e4b\u529f\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u95ee\u9053\uff1a\u201c\u5982\u6b64\u5b9d\u7269\uff0c\u80e1\u5927\u54e5\u5374\u54ea\u91cc\u5f97\u6765\uff1f\u201d\u80e1\u6842\u5357\u9053\uff1a\u201c\u4e0a\u4e2a\u6708\u6211\u5728\u6cb3\u5357\u5ba2\u5e97\u91cc\u9047\u5230\u4e00\u4e2a\u91c7\u836f\u8001\u9053\uff0c\u75c5\u5f97\u5feb\u6b7b\u4e86\uff0c\u89c1\u4ed6\u53ef\u601c\uff0c\u5e2e\u4e86\u4ed6\u51e0\u5341\u4e24\u94f6\u5b50\uff0c\u8fd8\u7ed9\u4ed6\u5ef6\u533b\u670d\u836f\u3002\u4f46\u4ed6\u5e74\u5bff\u5df2\u5230\uff0c\u836f\u77f3\u65e0\u7075\uff0c\u7ec8\u4e8e\u6d3b\u4e0d\u4e86\u3002\u4ed6\u4e34\u6b7b\u65f6\u628a\u8fd9\u5bf9\u51b0\u87fe\u7ed9\u4e86\u6211\uff0c\u8bf4\u662f\u62a5\u7b54\u6211\u770b\u987e\u4ed6\u7684\u60c5\u610f\u3002\u201d\u94c1\u7f57\u6c49\u9053\uff1a\u201c\u8fd9\u76d2\u5b50\u5012\u4e5f\u597d\u770b\u3002\u201d \u3000\u3000\u80e1\u6842\u5357\u9053\uff1a\u201c\u90a3\u8001\u9053\u672c\u6765\u653e\u5728\u4e00\u53ea\u94c1\u76d2\u91cc\uff0c\u53ef\u662f\u62ff\u53bb\u9001\u793c\uff0c\u5c82\u80fd\u4e0d\u88c5\u5f97\u597d\u770b\u4e00\u70b9\u2026\u2026\u201d\u6c99\u5929\u5e7f\u7b11\u9053\uff1a\u201c\u4e8e\u662f\u4f60\u5999\u624b\u7a7a\u7a7a\uff0c\u5230\u4e00\u5bb6\u5bcc\u6237\u53bb\u53d6\u4e86\u8fd9\u53ea\u91d1\u76d2\u3002\u201d\u80e1\u6842\u5357\u7b11\u9053\uff1a\u201c\u6c99\u5be8\u4e3b\u6599\u4e8b\u5982\u795e\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u90a3\u672c\u662f\u5f00\u5c01\u5e9c\u5218\u5927\u8d22\u4e3b\u7684\u5c0f\u59d0\u88c5\u9996\u9970\u7528\u7684\u3002\u201d\u4f17\u4eba\u4e00\u9f50\u5927\u7b11\u3002 \u3000\u3000\u80e1\u6842\u5357\u9053\uff1a\u201c\u521a\u624d\u6211\u4e24\u4eba\u9669\u4e9b\u513f\u643a\u624b\u9f50\u8d74\u9b3c\u95e8\u5173\uff0c\u62da\u6597\u4e4b\u65f6\u6211\u5fc3\u4e2d\u5728\u60f3\uff0c\u6211\u548c\u94c1\u7f57\u6c49\u5927\u54e5\u82e5\u5f97\u4fa5\u5e78\u4e0d\u6b7b\uff0c\u6211\u5c31\u81ea\u670d\u4e00\u53ea\u51b0\u87fe\uff0c\u518d\u62ff\u4e00\u53ea\u6551\u4ed6\u6027\u547d\u3002\u6211\u4e24\u4eba\u53c8\u65e0\u6028\u4ec7\uff0c\u4f55\u5fc5\u4e3a\u4e86\u4e00\u628a\u81ed\u4fbf\u58f6\uff0c\u641e\u51fa\u4eba\u547d\u5927\u4e8b\uff1f\u201d\u94c1\u7f57\u6c49\u7b11\u9053\uff1a\u201c\u90a3\u5012\u751f\u53d7\u4f60\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u53c8\u90fd\u5927\u7b11\u3002 \u3000\u3000\u80e1\u6842\u5357\u9053\uff1a\u201c\u603b\u800c\u8a00\u4e4b\uff0c\u8fd9\u4e24\u53ea\u51b0\u87fe\uff0c\u5df2\u4e0d\u662f\u6211\u7684\u4e86\u3002\u201d\u53cc\u624b\u4e3e\u8d77\u91d1\u76d2\uff0c\u9001\u5230\u8881\u627f\u5fd7\u9762\u524d\u9053\uff1a\u201c\u4e0d\u6562\u8bf4\u662f\u62a5\u7b54\uff0c\u53ea\u662f\u7a0d\u8868\u656c\u610f\u3002\u8bf7\u76f8\u516c\u8d4f\u8138\u6536\u4e0b\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6115\u7136\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53ef\u4ee5\uff1f\u8fd9\u662f\u80e1\u5144\u8981\u9001\u7ed9\u5b5f\u8001\u7237\u5b50\u7684\u3002\u201d\u80e1\u6842\u5357\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u76f8\u516c\u4ed7\u4e49\u76f8\u6551\uff0c\u5144\u5f1f\u975e\u6b7b\u5373\u4f24\uff0c\u8fd9\u5bf9\u51b0\u87fe\u603b\u4e4b\u662f\u5230\u4e0d\u4e86\u5b5f\u8001\u7237\u5b50\u624b\u4e2d\u5566\u3002\u81f3\u4e8e\u5bff\u793c\u561b\uff0c\u4e0d\u662f\u5144\u5f1f\u5938\u53e3\uff0c\u624b\u5230\u62ff\u6765\uff0c\u968f\u5904\u5373\u662f\uff0c\u7528\u4e0d\u7740\u64cd\u5fc3\u3002\u201d\u8881\u627f\u5fd7\u53ea\u662f\u63a8\u8c22\u3002\u80e1\u6842\u5357\u6709\u4e9b\u4e0d\u9ad8\u5174\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u76f8\u516c\u65e2\u4e0d\u80af\u89c1\u544a\u59d3\u540d\uff0c\u53c8\u4e0d\u80af\u53d7\u8fd9\u51b0\u87fe\uff0c\u96be\u9053\u7591\u5fc3\u662f\u5144\u5f1f\u5077\u6765\u7684\uff0c\u5acc\u810f\u4e0d\u8981\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u80e1\u5144\u8bf4\u54ea\u91cc\u8bdd\u6765\uff1f\u9002\u624d\u5306\u5fd9\uff0c\u672a\u53ca\u901a\u540d\u3002\u5c0f\u5f1f\u59d3\u8881\u540d\u627f\u5fd7\u3002\u201d \u3000\u3000\u94c1\u7f57\u6c49\u548c\u80e1\u6842\u5357\u540c\u65f6\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\u3002\u80e1\u6842\u5357\u9053\uff1a\u201c\u539f\u6765\u662f\u4e03\u7701\u76df\u4e3b\u8881\u5927\u7237\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u597d\u8eab\u624b\u3002\u8881\u5927\u7237\u7387\u9886\u7fa4\u96c4\uff0c\u5728\u9526\u9633\u5173\u5927\u7834\u9791\u5b50\u5175\uff0c\u5929\u4e0b\u65e0\u4e0d\u666f\u4ef0\u3002\u201d\u94c1\u7f57\u6c49\u9053\uff1a\u201c\u6211\u5148\u51e0\u65e5\u542c\u5230\u8fd9\u6d88\u606f\uff0c\u4e0d\u7531\u5f97\u4f38\u624b\u5927\u6253\u6211\u81ea\u5df1\u8033\u5149\u3002\u201d\u4f17\u4eba\u6115\u7136\u4e0d\u89e3\u3002 \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u4e3a\u751a\u4e48\u6253\u81ea\u5df1\u8033\u5149\uff1f\u201d\u94c1\u7f57\u6c49\u9053\uff1a\u201c\u6211\u607c\u6068\u81ea\u5df1\u8fd0\u6c14\u4e0d\u597d\uff0c\u6ca1\u80fd\u8d76\u4e0a\u6253\u8fd9\u4e00\u573a\u5927\u4ed7\uff0c\u8fde\u4e00\u540d\u9791\u5b50\u5175\u4e5f\u6ca1\u6740\u5230\u3002\u201d\u4f17\u4eba\u53c8\u90fd\u88ab\u4ed6\u9017\u5f97\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u80e1\u5927\u54e5\u65e2\u7136\u5b9a\u8981\u89c1\u8d50\uff0c\u5144\u5f1f\u5374\u4e4b\u4e0d\u606d\uff0c\u53ea\u597d\u53d7\u4e86\uff0c\u591a\u8c22\u591a\u8c22\u3002\u201d\u53cc\u624b\u63a5\u4e86\u8fc7\u53bb\uff0c\u653e\u5728\u6000\u91cc\u3002\u80e1\u6842\u5357\u559c\u5f62\u4e8e\u8272\u3002 \u3000\u3000\u8881\u627f\u5fd7\u56de\u5230\u81ea\u5df1\u623f\u91cc\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6367\u7740\u4e00\u682a\u6731\u7ea2\u7684\u73ca\u745a\u6811\u8fc7\u6765\u3002\u90a3\u73ca\u745a\u6811\u6709\u4e24\u5c3a\u6765\u9ad8\uff0c\u904d\u4f53\u6676\u83b9\uff0c\u96be\u5f97\u7684\u662f\u65e0\u4e00\u5904\u7834\u635f\uff0c\u65e0\u4e00\u7c92\u6c99\u77f3\u6df7\u6742\u5728\u5185\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u767b\u89c9\u6ee1\u5ba4\u751f\u8f89\uff0c\u5947\u4e3d\u65e0\u6bd4\u3002\u80e1\u6842\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u8c6a\u5bcc\u4e4b\u5bb6\u5230\u8fc7\u4e0d\u5c11\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u957f\u5927\u5b8c\u7f8e\u7684\u73ca\u745a\u6811\u3002\u53ea\u6015\u53ea\u6709\u7687\u5bab\u5185\u9662\uff0c\u624d\u6709\u8fd9\u822c\u73cd\u7269\u3002\u8fd9\u662f\u8881\u76f8\u516c\u5bb6\u4f20\u81f3\u5b9d\u5427\uff1f\u771f\u4ee4\u4eba\u5927\u5f00\u773c\u754c\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u65e0\u610f\u4e2d\u5f97\u6765\u7684\u3002\u8fd9\u4ef6\u4e1c\u897f\u8bf7\u80e1\u5144\u6536\u7740\uff0c\u660e\u513f\u5230\u4e86\u4fdd\u5b9a\u5e9c\uff0c\u4f5c\u4e3a\u8d3a\u793c\u5982\u4f55\uff1f\u201d\u80e1\u6842\u5357\u60ca\u9053\uff1a\u201c\u90a3\u592a\u8d35\u91cd\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e9b\u8d4f\u73a9\u4e4b\u7269\uff0c\u867d\u7136\u8d35\u91cd\uff0c\u5374\u65e0\u7528\u5904\uff0c\u4e0d\u6bd4\u51b0\u87fe\u53ef\u4ee5\u6551\u4eba\u6d3b\u547d\u3002\u80e1\u5144\u5feb\u6536\u4e86\u5427\u3002\u201d\u80e1\u6842\u5357\u53ea\u5f97\u8c22\u4e86\u6536\u8d77\u3002\u4ed6\u548c\u94c1\u7f57\u8bae\u89c1\u8881\u627f\u5fd7\u51fa\u624b\u8c6a\u9614\uff0c\u5fc3\u4e0b\u90fd\u6697\u6697\u79f0\u5947\u3002 \u3000\u3000\u6b21\u65e5\u508d\u665a\u5230\u4e86\u4fdd\u5b9a\u5e9c\uff0c\u4f17\u4eba\u5148\u5728\u5ba2\u5e97\u6b47\u4e86\uff0c\u7b2c\u4e8c\u5929\u4e00\u65e9\u5230\u5b5f\u5e9c\u9001\u793c\u8d3a\u5bff\u3002 \u3000\u3000\u5b5f\u4f2f\u98de\u89c1\u4e86\u8881\u627f\u5fd7\u3001\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u4e09\u4eba\u7684\u540d\u5e16\uff0c\u5fd9\u4eb2\u81ea\u8fce\u63a5\u51fa\u6765\u3002\u4ed6\u65e9\u77e5\u8881\u627f\u5fd7\u5e74\u8f7b\uff0c\u8fd8\u9053\u5fc5\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u6b64\u523b\u76f8\u4f1a\uff0c\u89c1\u4ed6\u53ea\u662f\u4e2a\u9edd\u9ed1\u5c11\u5e74\uff0c\u5f62\u8c8c\u5e73\u5eb8\uff0c\u4e0d\u89c9\u4e00\u6123\uff0c\u8001\u5927\u4e0d\u60a6\uff0c\u5fc3\u60f3\uff1a\u201c\u4e03\u7701\u7684\u82f1\u96c4\u597d\u6c49\u600e\u5730\u98a0\u4e09\u5012\u56db\uff0c\u63a8\u4e3e\u8fd9\u4e48\u4e2a\u6bdb\u5934\u5c0f\u4f19\u5b50\u505a\u76df\u4e3b\uff1f\u201d\u4f46\u4f17\u4eba\u8fdc\u9053\u524d\u6765\u62dc\u5bff\uff0c\u81ea\u7136\u662f\u7ed9\u81ea\u5df1\u6781\u5927\u9762\u5b50\uff0c\u4e8e\u662f\u548c\u5927\u513f\u5b50\u5b5f\u94ee\uff0c\u4e8c\u513f\u5b50\u5b5f\u94f8\u8fde\u58f0\u9053\u8c22\uff0c\u8fce\u4e86\u8fdb\u53bb\uff0c\u4e92\u9053\u4ef0\u6155\u3002\u8881\u627f\u5fd7\u89c1\u5b5f\u4f2f\u98de\u8eab\u6750\u9b41\u68a7\uff0c\u987b\u53d1\u5982\u94f6\uff0c\u867d\u4ee5\u516d\u65ec\u4e4b\u5e74\uff0c\u4ecd\u662f\u58f0\u82e5\u6d2a\u949f\uff0c\u6b65\u5c65\u4e4b\u95f4\u66f4\u662f\u7a33\u5065\u5f02\u5e38\uff0c\u60f3\u662f\u6b66\u529f\u6df1\u539a\u3002 \u3000\u3000\u4e24\u4e2a\u513f\u5b50\u5747\u5728\u58ee\u5e74\uff0c\u4e5f\u90fd\u82f1\u6c14\u52c3\u52c3\u3002 \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5b5f\u4f2f\u98de\u5bf9\u6cf0\u5c71\u5927\u4f1a\u4f3c\u4e4e\u9887\u4e0d\u4ee5\u4e3a\u7136\uff0c\u7a0b\u9752\u7af9\u8c08\u5230\u6cf0\u5c71\u4e4b\u4f1a\uff0c\u4ed6\u90fd\u6545\u4f5c\u4e0d\u95fb\uff0c\u5e76\u4e0d\u63a5\u53e3\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u6709\u8d3a\u5ba2\u5230\u6765\uff0c\u5b5f\u4f2f\u98de\u8bf4\u58f0\uff1a\u201c\u5931\u966a\uff01\u201d\u51fa\u5385\u8fce\u5bbe\u53bb\u4e86\u3002\u9752\u9752\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u53f7\u79f0\u76d6\u5b5f\u5c1d\uff0c\u600e\u4e48\u5bf9\u597d\u670b\u53cb\u5982\u6b64\u51b7\u6de1\uff1f\u539f\u6765\u662f\u6d6a\u5f97\u865a\u540d\u3002 \u3000\u3000\u65e9\u77e5\u4ed6\u8fd9\u4e48\u8001\u6c14\u6a2a\u79cb\u7684\uff0c\u5c31\u4e0d\u6765\u7ed9\u4ed6\u62dc\u751a\u4e48\u5bff\u4e86\u3002\u8001\u5bb6\u4f19\u6211\u8fd8\u89c1\u5f97\u4e0d\u591f\u591a\u4e48\uff1f\u201d \u3000\u3000\u5bb6\u4e01\u732e\u8fc7\u70b9\u5fc3\u540e\uff0c\u5b5f\u94f8\u966a\u7740\u8881\u627f\u5fd7\u7b49\u4eba\u5230\u540e\u5802\u53bb\u770b\u5bff\u793c\u3002 \u3000\u3000\u8fd9\u65f6\u5b5f\u4f2f\u98de\u6b63\u548c\u8bb8\u591a\u5ba2\u4eba\u56f4\u7740\u4e00\u5f20\u684c\u5b50\uff0c\u8d5e\u53f9\u4e0d\u7edd\u3002\u89c1\u8881\u627f\u5fd7\u7b49\u8fdb\u6765\uff0c\u5b5f\u4f2f\u98de\u5fd9\u62a2\u4e0a\u6765\u8c22\u9053\uff1a\u201c\u8881\u5144\u3001\u590f\u5144\u9001\u8fd9\u6837\u539a\u793c\uff0c\u5144\u5f1f\u5982\u4f55\u514b\u5f53\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8001\u524d\u8f88\u534e\u8bde\uff0c\u4e00\u70b9\u513f\u656c\u610f\uff0c\u592a\u8fc7\u5fae\u8584\u3002\u201d \u3000\u3000\u4f17\u4eba\u8d70\u8fd1\u684c\u8fb9\uff0c\u53ea\u89c1\u684c\u4e0a\u5149\u5f69\u593a\u76ee\uff0c\u6446\u6ee1\u4e86\u793c\u54c1\uff0c\u5176\u4e2d\u8881\u627f\u5fd7\u9001\u7684\u767d\u7389\u516b\u9a8f\u9a6c\uff0c\u9752\u9752\u9001\u7684\u7fe1\u7fe0\u7389\u897f\u74dc\uff0c\u5c24\u5176\u540d\u8d35\u3002\u80e1\u6842\u5357\u9001\u7684\u73ca\u745a\u5b9d\u6811\u4e5f\u5f88\u62a2\u773c\u3002 \u3000\u3000\u5b5f\u4f2f\u98de\u5bf9\u8881\u627f\u5fd7\u88ab\u63a8\u4e3a\u4e03\u7701\u76df\u4e3b\u4e00\u4e8b\uff0c\u672c\u6765\u9887\u4e3a\u4e0d\u5feb\uff0c\u4f46\u89c1\u4ed6\u8bf4\u8bdd\u8c26\u548c\uff0c\u53e3\u53e3\u58f0\u58f0\u8001\u524d\u8f88\uff0c\u9001\u7684\u53c8\u662f\u8fd9\u822c\u73cd\u8d35\u975e\u51e1\u7684\u5f02\u5b9d\uff0c\u8db3\u89c1\u5bf9\u81ea\u5df1\u5341\u5206\u5c0a\u91cd\uff0c\u89c9\u5f97\u8fd9\u4eba\u5e74\u7eaa\u867d\u8f7b\uff0c\u884c\u4e8b\u679c\u7136\u4e0d\u540c\uff0c\u4e0d\u89c9\u751f\u4e86\u4e00\u4efd\u597d\u611f\uff0c\u8bf4\u8bdd\u4e4b\u95f4\u4e5f\u5ba2\u6c14\u5f97\u591a\u4e86\u3002 \u3000\u3000\u5404\u8def\u8d3a\u5ba2\u62dc\u8fc7\u5bff\u540e\uff0c\u665a\u4e0a\u5bff\u7fc1\u5927\u5bb4\u5bbe\u670b\u3002\u76d6\u5b5f\u5c1d\u5bcc\u7532\u4fdd\u5b9a\uff0c\u7d20\u6765\u7231\u597d\u4ea4\u53cb\uff0c\u8fd9\u5929\u516d\u5341\u5927\u5bff\uff0c\u5404\u5904\u6765\u7684\u8d3a\u5ba2\u7adf\u6709\u4e09\u5343\u591a\u4eba\u3002\u5b5f\u4f2f\u98de\u6380\u987b\u5927\u4e50\uff0c\u5411\u5404\u8def\u82f1\u8c6a\u4e0d\u505c\u53e3\u7684\u62db\u547c\u9053\u8c22\u3002\u5927\u5385\u4e2d\u5f00\u4e86\u4e03\u516b\u5341\u5e2d\u3002\u4f4d\u671b\u4e0d\u9ad8\u3001\u8f88\u4efd\u8f83\u4f4e\u7684\u5bbe\u5ba2\u5219\u5728\u540e\u5385\u5165\u5e2d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u3001\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u4e09\u4eba\u90fd\u7ed9\u8ba9\u5728\u5c45\u4e2d\u7b2c\u4e00\u5e2d\u4e0a\uff0c\u5b5f\u4f2f\u98de\u5728\u4e3b\u4f4d\u76f8\u966a\u3002\u5728\u7b2c\u4e00\u5e2d\u5165\u5ea7\u7684\u8fd8\u6709\u8001\u82f1\u96c4\u9e33\u9e2f\u80c6\u5f20\u82e5\u8c37\u3001\u7edf\u5175\u9a7b\u9632\u4fdd\u5b9a\u5e9c\u7684\u51af\u540c\u77e5\u3001\u6c38\u80dc\u9556\u5c40\u7684\u603b\u9556\u5934\u8463\u5f00\u5c71\uff0c\u6b64\u5916\u4e5f\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u9886\u8896\u4eba\u7269\u3002\u7fa4\u8c6a\u5411\u5bff\u7fc1\u656c\u8fc7\u9152\u540e\uff0c\u731c\u62f3\u6597\u9152\uff0c\u751a\u662f\u70ed\u95f9\u3002 \u3000\u3000\u996d\u9152\u6b63\u9163\uff0c\u4e00\u540d\u5bb6\u4e01\u5306\u5306\u8fdb\u6765\uff0c\u6367\u7740\u4e00\u4e2a\u62dc\u76d2\uff0c\u8d70\u5230\u5b5f\u94ee\u8eab\u8fb9\uff0c\u8f7b\u8f7b\u8bf4\u4e86\u51e0\u53e5\u3002\u5b5f\u94ee\u6b63\u966a\u5ba2\u4eba\u996e\u9152\uff0c\u4e00\u542c\u5bb6\u4e01\u8bf4\u8bdd\uff0c\u5fd9\u7ad9\u8d77\u6765\uff0c\u8d70\u5230\u5b5f\u4f2f\u98de\u8eab\u65c1\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8001\u4eba\u5bb6\u771f\u597d\u5927\u9762\u5b50\uff0c\u795e\u62f3\u65e0\u654c\u5f52\u4e8c\u7237\u592b\u5987\uff0c\u5e26\u4e86\u5f92\u5f1f\u7ed9\u60a8\u62dc\u5bff\u6765\u5566\u3002\u201d\u5b5f\u4f2f\u98de\u4e00\u6123\uff0c\u9053\uff1a\u201c\u6211\u8ddf\u5f52\u8001\u4e8c\u7d20\u6765\u6ca1\u4ea4\u60c5\u554a\uff01\u201d\u63ed\u5f00\u62dc\u76d2\uff0c\u89c1\u5927\u7ea2\u5e16\u5b50\u4e0a\u5199\u7740\uff1a\u201c\u7737\u5f1f\u5f52\u8f9b\u6811\u7387\u95e8\u4eba\u656c\u8d3a\u201d\u51e0\u4e2a\u5927\u5b57\uff0c\u53e6\u6709\u5c0f\u5b57\u6ce8\u7740\u201c\u83f2\u4eea\u9ec4\u91d1\u5341\u4e24\u201d\uff0c\u5e16\u5b50\u65c1\u8fb9\u653e\u7740\u4e00\u53ea\u5341\u4e24\u91cd\u7684\u91d1\u5143\u5b9d\u3002 \u3000\u3000\u5b5f\u4f2f\u98de\u5fc3\u4e0b\u751a\u559c\uff0c\u5411\u5e2d\u4e0a\u4f17\u5bbe\u8bf4\u58f0\uff1a\u201c\u5931\u966a\u3002\u201d\u5e26\u4e86\u4e24\u4e2a\u513f\u5b50\u51fa\u53bb\u8fce\u5ba2\u3002 \u3000\u3000\u4e0d\u591a\u65f6\uff0c\u53ea\u89c1\u4ed6\u6ee1\u9762\u6625\u98ce\uff0c\u966a\u7740\u5f52\u8f9b\u6811\u592b\u5987\u3001\u6885\u5251\u548c\u3001\u5218\u57f9\u751f\u3001\u5b59\u4ef2\u541b\u4e94\u4eba\u8fdb\u6765\u3002\u5f52\u4e8c\u5a18\u624b\u4e2d\u62b1\u7740\u90a3\u4e2a\u76ae\u5305\u9aa8\u5934\u3001\u5944\u5944\u4e00\u606f\u7684\u5b69\u5b50\u5f52\u949f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u65e9\u7ad9\u5728\u4e00\u65c1\uff0c\u4f5c\u4e86\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\u3001\u4e8c\u5e08\u5ac2\uff0c\u60a8\u4e24\u4f4d\u597d\u3002\u201d\u5f52\u8f9b\u6811\u70b9\u70b9\u5934\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u4e5f\u5728\u8fd9\u91cc\u3002\u201d\u5f52\u4e8c\u5a18\u54fc\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d\u7406\u776c\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e08\u54e5\u5e08\u5ac2\u8bf7\u4e0a\u5ea7\uff0c\u6211\u4e0e\u5251\u548c\u4ed6\u4eec\u4e00\u8d77\u5750\u597d\u5566\u3002\u201d\u5b5f\u4f2f\u98de\u542c\u8881\u627f\u5fd7\u8fd9\u822c\u79f0\u547c\uff0c\u7b11\u9053\uff1a\u201c\u597d\u54c7\uff0c\u6709\u8fd9\u6837\u4e00\u4f4d\u4e86\u4e0d\u8d77\u7684\u5e08\u54e5\u6491\u8170\uff0c\u522b\u8bf4\u4e03\u7701\u76df\u4e3b\uff0c\u5c31\u662f\u5341\u56db\u7701\u76df\u4e3b\uff0c\u4e5f\u597d\u5f53\u5440\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u662f\u8bf4\u8881\u627f\u5fd7\u5c11\u5e74\u5f97\u610f\uff0c\u5f53\u4e0a\u4e03\u7701\u76df\u4e3b\uff0c\u5168\u662f\u4ef0\u4ed7\u5e08\u5144\u7684\u5927\u529b\u3002\u8881\u627f\u5fd7\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u4e0d\u8a00\u8bed\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u8fd9\u4e9b\u65e5\u5b50\u5fd9\u4e8e\u4e3a\u7231\u5b50\u89c5\u836f\uff0c\u5c1a\u4e0d\u77e5\u6cf0\u5c71\u5927\u4f1a\u4e4b\u4e8b\uff0c\u6115\u7136\u9053\uff1a\u201c\u751a\u4e48\u76df\u4e3b\uff1f\u201d\u5b5f\u4f2f\u98de\u7b11\u9053\uff1a\u201c\u6211\u662f\u968f\u4fbf\u8bf4\u7b11\uff0c\u5f52\u4e8c\u54e5\u4e0d\u5fc5\u4ecb\u610f\u3002\u201d\u5f53\u4e0b\u8bf7\u5f52\u6c0f\u592b\u5987\u5728\u9e33\u9e2f\u80c6\u5f20\u8001\u82f1\u96c4\u4e0b\u9996\u5750\u4e86\u3002\u4f17\u8d3a\u5ba2\u5747\u662f\u8c6a\u6770\u4e4b\u4e0a\uff0c\u7537\u5973\u6742\u5750\uff0c\u5e76\u4e0d\u5206\u5e2d\u3002\u8881\u627f\u5fd7\u81ea\u4e0e\u6885\u5251\u548c\u7b49\u5750\u5728\u4e00\u684c\u3002\u7a0b\u9752\u7af9\u548c\u6c99\u5929\u5e7f\u5374\u53bb\u548c\u54d1\u5df4\u3001\u9752\u9752\u540c\u5e2d\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u4e0e\u5b5f\u4f2f\u98de\u7b49\u4e92\u76f8\u656c\u9152\u3002\u5404\u4eba\u559d\u4e86\u4e09\u676f\u540e\uff0c\u6c38\u80dc\u9556\u5c40\u603b\u9556\u5934\u8463\u5f00\u5c71\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u9152\u91cf\u4e0d\u884c\uff0c\u5404\u4f4d\u5bbd\u5750\u3002 \u3000\u3000\u5144\u5f1f\u5230\u540e\u9762\u6b47\u4e00\u4e0b\u3002\u201d\u5f52\u8f9b\u6811\u51b7\u7136\u9053\uff1a\u201c\u6211\u4eec\u5230\u5904\u627e\u8463\u9556\u5934\u4e0d\u5230\uff0c\u5fc3\u60f3\u5b9a\u5728\u8fd9\u91cc\uff0c\u679c\u7136\u4e0d\u9519\u3002\u201d\u8463\u5f00\u5c71\u795e\u8272\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u8ddf\u5f52\u4e8c\u7237\u5f80\u65e5\u65e0\u6028\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u5f52\u4e8c\u7237\u4f55\u5fc5\u82e6\u82e6\u627e\u6211\uff1f\u201d \u3000\u3000\u4f17\u4eba\u4e00\u542c\u6b64\u8a00\uff0c\u90fd\u505c\u676f\u4e0d\u996e\uff0c\u671b\u7740\u4e8c\u4eba\u3002 \u3000\u3000\u5b5f\u4f2f\u98de\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u751a\u4e48\u8fc7\u8282\uff0c\u77a7\u5144\u5f1f\u8fd9\u4e2a\u5c0f\u9762\u5b50\uff0c\u8ba9\u5144\u5f1f\u6765\u6392\u89e3\u6392\u89e3\u3002\u201d\u8bf4\u5230\u6392\u96be\u89e3\u7eb7\uff0c\u4e8e\u4ed6\u5b9e\u662f\u751f\u5e73\u81f3\u4e50\u3002\u8463\u5f00\u5c71\u9053\uff1a\u201c\u5728\u4e0b\u4e45\u4ef0\u5f52\u4e8c\u7237\u5927\u540d\uff0c\u4e00\u5411\u662f\u5f88\u656c\u91cd\u7684\uff0c\u53ea\u662f\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u77e5\u4f55\u6545\u4e00\u8def\u8ffd\u8e2a\u5144\u5f1f\u3002\u201d \u3000\u3000\u5b5f\u4f2f\u98de\u4e00\u542c\uff0c\u5fc3\u4e2d\u96ea\u4eae\uff1a\u201c\u597d\u554a\uff0c\u4f60\u4eec\u4e24\u4eba\u90fd\u4e0d\u662f\u8bda\u5fc3\u7ed9\u8001\u592b\u62dc\u5bff\u6765\u7740\u3002\u539f\u6765\u4e00\u4e2a\u662f\u907f\u96be\uff0c\u4e00\u4e2a\u662f\u8ffd\u4eba\u3002\u8fd9\u59d3\u8463\u7684\u65e2\u7136\u77a7\u5f97\u6211\u8d77\uff0c\u5230\u4e86\u6211\u5c4b\u91cc\uff0c\u603b\u4e0d\u80fd\u8ba9\u4ed6\u5403\u4e8f\u4e22\u4eba\u3002\u201d\u4e8e\u662f\u5bf9\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u5f52\u4e8c\u7237\u6709\u751a\u4e48\u4e8b\uff0c\u54b1\u4eec\u8fc7\u4e86\u4eca\u5929\u6162\u6162\u518d\u8c08\u3002\u5927\u5bb6\u662f\u597d\u670b\u53cb\uff0c\u603b\u8bf4\u5f97\u5f00\u3002\u201d \u3000\u3000\u5f52\u8f9b\u6811\u4e0d\u5584\u8a00\u8f9e\uff0c\u5f52\u4e8c\u5a18\u4e00\u6307\u624b\u4e2d\u5b69\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u4e8c\u7237\u4e09\u623f\u72ec\u7967\u5355\u4f20\u7684\u513f\u5b50\uff0c\u773c\u89c1\u75c5\u5f97\u5feb\u6b7b\u5566\u3002\u60f3\u6c42\u8463\u9556\u5934\u5f00\u6069\uff0c\u8d50\u51e0\u7c92\u836f\u4e38\uff0c\u6551\u4e86\u8fd9\u5b69\u5b50\u4e00\u6761\u5c0f\u547d\u3002\u6211\u4eec\u592b\u5987\u6c38\u611f\u5927\u5fb7\u3002\u201d\u5b5f\u4f2f\u98de\u9053\uff1a\u201c\u90a3\u662f\u5e94\u8be5\u7684\u3002\u201d\u8f6c\u5934\u5bf9\u8463\u5f00\u5c71\u9053\uff1a\u201c\u8463\u7237\uff0c\u6551\u4eba\u4e00\u547d\uff0c\u80dc\u9020\u4e03\u7ea7\u6d6e\u5c60\u3002\u4f55\u51b5\u662f\u5f52\u4e8c\u7237\u8fd9\u6837\u7684\u5927\u82f1\u96c4\u6c42\u4f60\u3002\u751a\u4e48\u836f\u4e38\uff0c\u5feb\u62ff\u51fa\u6765\u5427\uff01\u4f60\u77a7\u8fd9\u5b69\u5b50\u786e\u662f\u75c5\u91cd\u3002\u201d\u8463\u5f00\u5c71\u9053\uff1a\u201c\u8fd9\u832f\u82d3\u9996\u4e4c\u4e38\u5018\u82e5\u662f\u5144\u5f1f\u81ea\u5df1\u7684\uff0c\u53ea\u987b\u5f52\u4e8c\u7237\u4e00\u53e5\u8bdd\uff0c\u5144\u5f1f\u65e9\u5c31\u53cc\u624b\u5949\u4e0a\u4e86\u3002\u4e0d\u8fc7\u8fd9\u662f\u51e4\u9633\u603b\u7763\u9a6c\u5927\u4eba\u8fdb\u8d21\u7684\u8d21\u54c1\uff0c\u7740\u843d\u6c38\u80dc\u9556\u5c40\u9001\u5230\u4eac\u5e08\u3002\u82e5\u6709\u5931\u95ea\uff0c\u5144\u5f1f\u4e0d\u80fd\u518d\u5728\u6c5f\u6e56\u4e0a\u6df7\u996d\u5403\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u53ef\u662f\u4e0d\u514d\u8fde\u8eab\u5bb6\u6027\u547d\u4e5f\u90fd\u96be\u4fdd\uff0c\u53ea\u597d\u8bf7\u5f52\u4e8c\u7237\u9ad8\u62ac\u8d35\u624b\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\u8fd9\u8bdd\uff0c\u90fd\u89c9\u4e8b\u5728\u4e24\u96be\u3002\u51af\u540c\u77e5\u4e00\u542c\u662f\u8d21\u7269\uff0c\u5fd9\u9053\uff1a\u201c\u8d21\u7269\u5c31\u662f\u5723\u4e0a\u7684\u4e1c\u897f\uff0c\u54ea\u4e00\u4e2a\u5927\u80c6\u6562\u52a8\uff1f\u201d\u5f52\u4e8c\u5a18\u9053\uff1a\u201c\u54fc\uff0c\u5c31\u7b97\u662f\u7389\u7687\u5927\u5e1d\u7684\uff0c\u8fd9\u4e00\u6b21\u4e5f\u53ea\u5f97\u52a8\u4e0a\u4e00\u52a8\u4e86\u3002\u201d\u51af\u540c\u77e5\u559d\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u8fd9\u5973\u4eba\u60f3\u9020\u53cd\u4e48\uff1f\u201d\u5f52\u4e8c\u5a18\u5927\u6012\uff0c\u4f38\u7b77\u5728\u7897\u4e2d\u5939\u8d77\u4e00\u4e2a\u9c7c\u5706\uff0c\u4e58\u51af\u540c\u77e5\u5634\u8fd8\u6ca1\u95ed\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u63b7\u5165\u4e86\u4ed6\u7684\u53e3\u4e2d\u3002\u51af\u540c\u77e5\u4e00\u60ca\uff0c\u54ea\u77e5\u53c8\u662f\u4e24\u4e2a\u9c7c\u5706\u63a5\u8fde\u800c\u6765\uff0c\u628a\u4ed6\u7684\u5634\u585e\u5f97\u6ee1\u6ee1\u7684\uff0c\u541e\u4e5f\u4e0d\u662f\uff0c\u5410\u4e5f\u4e0d\u662f\uff0c\u767b\u65f6\u72fc\u72c8\u4e0d\u582a\u3002 \u3000\u3000\u8001\u82f1\u96c4\u5f20\u82e5\u8c37\u4e00\u89c1\u5927\u6012\uff0c\u5fc3\u60f3\u4eca\u5929\u662f\u5b5f\u5144\u5f1f\u7684\u5bff\u8fb0\uff0c\u8fd9\u822c\u641e\u6cd5\u5c82\u4e0d\u662f\u5b58\u5fc3\u6363\u86cb\uff0c\u968f\u624b\u62ff\u8d77\u684c\u4e0a\u4e00\u53ea\u5143\u5b9d\u5f62\u7684\u7b77\u67b6\uff0c\u7528\u529b\u4e00\u62cd\uff0c\u7b77\u67b6\u6574\u6574\u9f50\u9f50\u7684\u5d4c\u5165\u4e86\u684c\u9762\u4e4b\u4e2d\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u624b\u8098\u9760\u684c\uff0c\u6f5c\u8fd0\u6df7\u5143\u529f\u5185\u529b\u5411\u4e0b\u4e00\u62b5\uff0c\u5168\u8eab\u5e76\u672a\u52a8\u5f39\u5206\u6beb\uff0c\u5d4c\u5728\u684c\u9762\u91cc\u7684\u7b77\u67b6\u7a81\u7136\u8df3\u51fa\uff0c\u649e\u5411\u5f20\u82e5\u8c37\u8138\u4e0a\u3002\u5f20\u82e5\u8c37\u6025\u5fd9\u95ea\u907f\uff0c\u867d\u672a\u649e\u4e2d\uff0c\u5374\u5df2\u663e\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u4ed6\u6ee1\u8138\u901a\u7ea2\uff0c\u970d\u5730\u7ad9\u8d77\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5c06\u684c\u9762\u6253\u4e0b\u4e00\u5757\uff0c\u8f6c\u8eab\u5bf9\u5b5f\u4f2f\u98de\u9053\uff1a\u201c\u5b5f\u8001\u5f1f\uff0c\u8001\u54e5\u54e5\u5728\u4f60\u5e9c\u4e0a\u4e22\u4e86\u8138\u4e86\u3002\u201d\u8bf4\u7740\u5927\u8e0f\u6b65\u5411\u5916\u5c31\u8d70\u3002 \u3000\u3000\u804c\u53f8\u62db\u5f85\u7684\u4e24\u540d\u5b5f\u95e8\u5f1f\u5b50\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u5f20\u8001\u7237\u5b50\u4e0d\u5fd9\uff0c\u8bf7\u5230\u540e\u5802\u7528\u676f\u8336\u5427\u3002\u201d\u5f20\u82e5\u8c37\u94c1\u9752\u7740\u8138\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u4e24\u540d\u5f1f\u5b50\u8e09\u8dc4\u8dcc\u5f00\u3002 \u3000\u3000\u5b5f\u4f2f\u98de\u602b\u7136\u4e0d\u60a6\uff0c\u5fc3\u60f3\u597d\u597d\u4e00\u5802\u5bff\u7b75\uff0c\u5374\u7ed9\u5f52\u8f9b\u6811\u8fd9\u6076\u5ba2\u8d76\u5230\u95f9\u5c40\uff0c\u4ee5\u81f4\u8001\u670b\u53cb\u4e0d\u6b22\u800c\u53bb\uff0c\u6b63\u8981\u53d1\u8bdd\uff0c\u51af\u540c\u77e5\u5341\u6307\u9f50\u65bd\uff0c\u5df2\u5c06\u4e24\u4e2a\u9c7c\u5706\u4ece\u53e3\u4e2d\u6316\u4e86\u51fa\u6765\uff0c\u53e6\u5916\u4e00\u4e2a\u5374\u7ec8\u4e8e\u54bd\u4e86\u4e0b\u53bb\uff0c\u54c7\u54c7\u5927\u53eb\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff0c\u8fd9\u8fd8\u6709\u738b\u6cd5\u5417\uff1f\u6765\u4eba\u54ea\uff01\u201d\u4e24\u540d\u4eb2\u968f\u8fd8\u4e0d\u77e5\u8001\u7237\u4e3a\u4f55\u53d1\u6012\uff0c\u5fd9\u5954\u8fc7\u6765\u3002\u51af\u540c\u77e5\u53eb\u9053\uff1a\u201c\u62ac\u6211\u5927\u5173\u5200\u6765\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u51af\u540c\u77e5\u9760\u7740\u7956\u836b\u5f97\u5b98\uff0c\u6b66\u827a\u4f4e\u5fae\uff0c\u5374\u504f\u504f\u7231\u51fa\u98ce\u5934\uff0c\u8981\u94c1\u5320\u6253\u4e86\u4e00\u67c4\u5203\u957f\u80cc\u539a\u3001\u9540\u91d1\u5782\u7f28\u3001\u8584\u94c1\u76ae\u7684\u7a7a\u5fc3\u5927\u5173\u5200\uff0c\u81ea\u5df1\u9a91\u5728\u9a6c\u4e0a\uff0c\u53eb\u4e24\u540d\u4eb2\u5175\u62ac\u4e86\u8ddf\u7740\u8d70\uff0c\u52a1\u987b\u53e3\u4e2d\u676d\u80b2\u3001\u676d\u80b2\uff0c\u53eb\u58f0\u4e0d\u7edd\uff0c\u88c5\u4f5c\u5341\u5206\u6c89\u9ed8\u3001\u4e0d\u80dc\u8d1f\u8377\u7684\u6a21\u6837\uff0c\u4ed6\u53ea\u8981\u968f\u624b\u4e00\u63d0\uff0c\u5374\u662f\u8f7b\u677e\u968f\u4fbf\u3002\u65c1\u4eba\u770b\u4e86\uff0c\u81ea\u7136\u4f69\u670d\u540c\u77e5\u8001\u7237\u795e\u529b\u60ca\u4eba\u3002\u4ed6\u628a\u201c\u62ac\u6211\u5927\u5173\u5200\u6765\u201d\u8fd9\u53e5\u8bdd\u8bf4\u987a\u4e86\u53e3\uff0c\u8fd9\u65f6\u813e\u6c14\u53d1\u4f5c\uff0c\u53c8\u558a\u4e86\u51fa\u6765\u3002\u4e24\u540d\u4eb2\u968f\u4e00\u6123\uff0c\u8fd9\u6b21\u524d\u6765\u62dc\u5bff\uff0c\u5e76\u672a\u62ac\u8fd9\u7d2f\u8d58\u4e4b\u7269\uff0c\u4e00\u540d\u4eb2\u968f\u5f53\u5373\u89e3\u4e0b\u8170\u95f4\u4f69\u5200\uff0c\u9012\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5b5f\u4f2f\u98de\u77e5\u4ed6\u5e95\u7ec6\uff0c\u89c1\u4ed6\u88c5\u6a21\u4f5c\u6837\uff0c\u53c8\u662f\u597d\u6c14\uff0c\u53c8\u662f\u597d\u7b11\uff0c\u8fde\u53eb\uff1a\u201c\u4f7f\u4e0d\u5f97\u3002\u201d \u3000\u3000\u51af\u540c\u77e5\u8349\u83c5\u4eba\u547d\u60ef\u4e86\u7684\uff0c\u4e5f\u4e0d\u77e5\u5f52\u8f9b\u6811\u662f\u591a\u5927\u7684\u6765\u5934\uff0c\u773c\u89c1\u4ed6\u662f\u4e2a\u4e61\u519c\u6a21\u6837\uff0c\u54ea\u653e\u5728\u5fc3\u4e0a\uff1f\u63a5\u8fc7\u4f69\u5200\uff0c\u6325\u5200\u6402\u5934\u5411\u5f52\u4e8c\u5a18\u780d\u53bb\u3002\u5f52\u4e8c\u5a18\u53f3\u624b\u62b1\u7740\u5b69\u5b50\uff0c\u5de6\u624b\u4e00\u4f38\uff0c\u5f2f\u7740\u98df\u4e2d\u4e24\u6307\u94b3\u4f4f\u4e86\u5200\u80cc\uff0c\u95ee\u9053\uff1a\u201c\u5927\u8001\u7237\uff0c\u4f60\u8981\u600e\u6837\uff1f\u201d \u3000\u3000\u51af\u540c\u77e5\u7528\u529b\u4e00\u62c9\uff0c\u54ea\u77e5\u8fd9\u628a\u5200\u5c31\u5982\u7ed9\u4eba\u7528\u94c1\u94b3\u94b3\u4f4f\u4e86\uff0c\u4e00\u62c9\u4e4b\u4e0b\uff0c\u7adf\u662f\u7eb9\u4e1d\u4e0d\u52a8\u3002\u4ed6\u53cc\u624b\u63e1\u4f4f\u5200\u67c4\uff0c\u7528\u529b\u5f80\u540e\u62c9\u593a\uff0c\u970e\u65f6\u95f4\u4e00\u5f20\u8138\u80c0\u5f97\u901a\u7ea2\uff0c\u624b\u4e2d\u867d\u65e0\u5927\u5173\u5200\uff0c\u4f46\u8138\u5982\u91cd\u67a3\uff0c\u5012\u4e5f\u5b9b\u82e5\u5173\u516c\uff0c\u6240\u5dee\u8005\u4e5f\u4e0d\u8fc7\u5173\u516c\u7684\u4e39\u51e4\u773c\u53d8\u6210\u4e86\u51af\u516c\u7684\u6597\u9e21\u773c\u800c\u5df2\u3002\u5f52\u4e8c\u5a18\u7a81\u7136\u653e\u624b\u3002\u51af\u540c\u77e5\u4ef0\u5929\u4e00\u4ea4\uff0c\u8dcc\u5f97\u7ed3\u7ed3\u5b9e\u5b9e\uff0c\u5200\u80cc\u7838\u5728\u989d\u5934\u4e4b\u4e0a\uff0c\u767b\u65f6\u80bf\u8d77\u4e86\u5706\u5706\u4e00\u5757\uff0c\u6709\u4f3c\u9002\u624d\u4ed6\u541e\u4e0b\u809a\u53bb\u7684\u9c7c\u5706\u94bb\u4e0a\u4e86\u989d\u5934\u3002\u4e24\u540d\u4eb2\u968f\u5fd9\u62a2\u4e0a\u6276\u8d77\u3002\u51af\u540c\u77e5\u4e0d\u6562\u518d\u591a\u8bf4\u4e00\u53e5\uff0c\u624b\u6309\u989d\u5934\uff0c\u4e09\u811a\u4e24\u6b65\u7684\u8d70\u4e86\u3002\u53ea\u542c\u4ed6\u51fa\u4e86\u5385\u95e8\uff0c\u4e00\u8def\u5927\u58f0\u559d\u9a82\u4eb2\u968f\uff1a\u201c\u6df7\u5e10\u738b\u516b\u86cb\uff01\u5c31\u662f\u6015\u91cd\u5077\u61d2\uff0c\u4e0d\u62ac\u8001\u7237\u7528\u60ef\u4e86\u7684\u5927\u5173\u5200\u6765\u3002\u5426\u5219\u7684\u8bdd\uff0c\u8fd8\u4e0d\u662f\u4e00\u5200\u4fbf\u5c06\u8fd9\u6cfc\u5987\u5288\u6210\u4e24\u534a\u3002\u201d \u3000\u3000\u8463\u5f00\u5c71\u8d81\u4e71\u60f3\u6e9c\u3002\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u8463\u9556\u5934\uff0c\u4f60\u7559\u4e0b\u4e38\u836f\uff0c\u6211\u51b3\u4e0d\u96be\u4e3a\u4f60\u3002\u201d\u8463\u5f00\u5c71\u53d7\u903c\u4e0d\u8fc7\uff0c\u7ad9\u5230\u5385\u5fc3\uff0c\u53eb\u9053\uff1a\u201c\u59d3\u8463\u7684\u660e\u77e5\u4e0d\u662f\u4f60\u795e\u62f3\u65e0\u654c\u7684\u5bf9\u624b\u3002\u6027\u547d\u662f\u5728\u8fd9\u91cc\uff0c\u4f60\u8981\uff0c\u5c31\u6765\u62ff\u53bb\u5427\u3002\u201d\u5f52\u4e8c\u5a18\u9053\uff1a\u201c\u8c01\u8981\u4f60\u6027\u547d\uff1f\u628a\u4e38\u836f\u62ff\u51fa\u6765\uff01\u201d \u3000\u3000\u5b5f\u4f2f\u98de\u7684\u5927\u513f\u5b50\u5b5f\u94ee\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u5f52\u4e8c\u7237\uff0c\u6211\u4eec\u5b5f\u5bb6\u53ef\u6ca1\u5f97\u7f6a\u4e86\u4f60\uff0c\u4f60\u4eec\u6709\u8fc7\u8282\uff0c\u8bf7\u5230\u5916\u9762\u53bb\u95f9\u3002\u201d\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u597d\uff0c\u8463\u9556\u5934\uff0c\u54b1\u4eec\u51fa\u53bb\u5427\u3002\u201d\u8463\u5f00\u5c71\u5374\u4e0d\u80af\u8d70\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u4e0d\u8010\u70e6\u4e86\uff0c\u4f38\u624b\u5f80\u4ed6\u81c2\u4e0a\u6293\u53bb\u3002\u8463\u5f00\u5c71\u5411\u540e\u4e00\u9000\uff0c\u5f52\u8f9b\u6811\u624b\u638c\u8ddf\u7740\u4f38\u524d\u3002\u8463\u5f00\u5c71\u65e2\u505a\u5230\u9556\u5c40\u5b50\u7684\u603b\u9556\u5934\uff0c\u6b66\u529f\u81ea\u7136\u4e5f\u975e\u6cdb\u6cdb\uff0c\u773c\u89c1\u5f52\u8f9b\u6811\u638c\u5230\uff0c\u75be\u5fd9\u7f29\u80a9\uff0c\u51fa\u624b\u76f8\u683c\uff0c\u5374\u54ea\u78b0\u5f97\u5230\u5bf9\u65b9\u624b\u638c\uff1f\u4f46\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u80a9\u5934\u8863\u670d\u5df2\u88ab\u6495\u4e0b\u4e86\u4e00\u5757\u3002 \u3000\u3000\u5b5f\u94ee\u62a2\u4e0a\u524d\u53bb\uff0c\u6321\u5728\u8463\u5f00\u5c71\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u8463\u9556\u5934\u662f\u6765\u8d3a\u5bff\u7684\u5ba2\u4eba\uff0c\u4e0d\u5bb9\u4ed6\u5728\u820d\u4e0b\u53d7\u4eba\u6b3a\u4fae\u3002\u201d\u5f52\u4e8c\u5a18\u9053\uff1a\u201c\u90a3\u600e\u6837\uff1f\u6211\u4eec\u5f53\u5bb6\u7684\u4e0d\u662f\u53eb\u4ed6\u51fa\u53bb\u5417\uff1f\u201d\u5b5f\u94ee\u9053\uff1a\u201c\u4f60\u4eec\u6709\u4e8b\u627e\u8463\u9556\u5934\uff0c\u4e0d\u4f1a\u5230\u6c38\u80dc\u9556\u5c40\u53bb\u627e\uff1f\u5e72\u4e48\u5230\u8fd9\u91cc\u6405\u5c40\uff1f\u201d\u8a00\u4e0b\u8d8a\u6765\u8d8a\u4e0d\u5ba2\u6c14\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u5389\u58f0\u9053\uff1a\u201c\u5c31\u7b97\u6405\u4e86\u5c40\uff0c\u53c8\u600e\u4e48\u6837\uff1f\u201d\u8fd9\u4e9b\u65e5\u5b50\u6765\u5979\u5fc3\u70e6\u610f\u4e71\uff0c\u4e3a\u4e86\u513f\u5b50\u75c5\u91cd\u96be\u6108\uff0c\u81ea\u5df1\u7684\u547d\u4e5f\u4e0d\u60f3\u8981\u4e86\uff0c\u5426\u5219\u4ee5\u5b5f\u4f2f\u98de\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u671b\u5730\u4f4d\uff0c\u5979\u51b3\u4e0d\u80fd\u5982\u6b64\u4e0a\u95e8\u80e1\u6765\u3002 \u3000\u3000\u5b5f\u4f2f\u98de\u6c14\u5f97\u8138\u4e0a\u53d8\u8272\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\u54c7\uff0c\u5f52\u4e8c\u7237\u77a7\u5f97\u8d77\uff0c\u8001\u592b\u5c31\u6765\u9886\u6559\u9886\u6559\u3002\u201d\u5b5f\u94ee\u9053\uff1a\u201c\u7239\u7239\uff0c\u4eca\u513f\u662f\u60a8\u8001\u4eba\u5bb6\u597d\u65e5\u5b50\u3002\u513f\u5b50\u6765\u3002\u201d\u5f53\u4e0b\u547d\u5bb6\u4e01\u5728\u5385\u4e2d\u642c\u5f00\u684c\u6905\uff0c\u9732\u51fa\u4e86\u4e00\u7247\u7a7a\u5730\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8981\u6405\u5c40\uff0c\u7d22\u6027\u5927\u6405\u4e00\u573a\u3002\u5f52\u4e8c\u7237\uff0c\u8fd9\u5c31\u8bf7\u663e\u663e\u4f60\u7684\u795e\u62f3\u65e0\u654c\u3002\u201d \u3000\u3000\u5f52\u4e8c\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981\u8ddf\u6211\u4eec\u5f53\u5bb6\u52a8\u624b\uff0c\u518d\u7ec3\u4e8c\u5341\u5e74\uff0c\u8fd8\u4e0d\u77e5\u6210\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5b5f\u94ee\u6b66\u529f\u5df2\u5c3d\u5f97\u5b5f\u4f2f\u98de\u5feb\u6d3b\u4e09\u5341\u638c\u7684\u771f\u4f20\uff0c\u65b9\u5f53\u58ee\u5e74\uff0c\u751f\u5e73\u5c11\u9022\u654c\u624b\uff0c\u867d\u7136\u4e45\u95fb\u795e\u62f3\u65e0\u654c\u7684\u5927\u540d\uff0c\u4f46\u5f53\u7740\u6570\u5343\u5bbe\u670b\uff0c\u8fd9\u53e3\u6c14\u54ea\u91cc\u54bd\u5f97\u4e0b\u53bb\uff1f\u559d\u9053\uff1a\u201c\u5f52\u8001\u4e8c\uff0c\u4f60\u5f3a\u51f6\u9738\u9053\uff0c\u5230\u8fd9\u91cc\u6765\u6492\u91ce\uff01\u5b5f\u5c11\u7237\u62f3\u5934\u4e0a\u53ea\u8981\u8f93\u7ed9\u4e86\u4f60\uff0c\u4efb\u51ed\u4f60\u627e\u8463\u9556\u5934\u7b97\u5e10\uff0c\u6211\u4eec\u5b5f\u5bb6\u81ea\u8ba4\u6ca1\u80fd\u8010\u7ba1\u8fd9\u4ef6\u4e8b\u3002\u8981\u662f\u80dc\u4e86\u4f60\uff0c\u5374\u53c8\u600e\u6837\uff1f\u201d\u5f52\u8f9b\u6811\u4e0d\u7231\u591a\u8a00\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u63a5\u5f97\u4e86\u6211\u4e09\u62db\uff0c\u5f52\u8001\u4e8c\u8ddf\u4f60\u78d5\u5934\u3002\u201d \u3000\u3000\u65c1\u4eba\u6ca1\u542c\u89c1\uff0c\u7eb7\u7eb7\u4e92\u76f8\u8be2\u95ee\u3002\u5b5f\u94ee\u6012\u6781\u800c\u7b11\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u77a7\u8fd9\u4eba\u72c2\u4e0d\u72c2\uff1f\u4ed6\u8bf4\u53ea\u8981\u6211\u63a5\u5f97\u4ed6\u4e09\u62db\uff0c\u4ed6\u5c31\u5411\u6211\u78d5\u5934\u3002\u54c8\u54c8\uff0c\u662f\u4e0d\u662f\u554a\uff0c\u5f52\u4e8c\u7237\uff1f\u201d \u3000\u3000\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u63a5\u62db\u5427\uff01\u201d\u547c\u7684\u4e00\u58f0\uff0c\u53f3\u62f3\u201c\u6cf0\u5c71\u538b\u9876\u201d\uff0c\u731b\u51fb\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u9752\u9752\u5df2\u7ad9\u5230\u8881\u627f\u5fd7\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5e08\u54e5\u5b66\u4e86\u4f60\u7684\u6cd5\u5b50\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u8ddf\u4ed6\u5f92\u5f1f\u6bd4\u62f3\uff0c\u4e0d\u4e5f\u662f\u9650\u4e86\u62db\u6570\u6765\u8ba9\u4ed6\u63a5\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u59d3\u5b5f\u7684\u4e0d\u8bc6\u597d\u6b79\uff0c\u4ed6\u54ea\u77e5\u6211\u5e08\u54e5\u795e\u62f3\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u5b5f\u94ee\u89c1\u5bf9\u65b9\u62f3\u5230\uff0c\u786c\u63a5\u786c\u67b6\uff0c\u53f3\u81c2\u7528\u529b\u4e00\u6321\uff0c\u5de6\u624b\u968f\u5373\u6253\u51fa\u4e00\u62f3\u3002\u4e24\u4eba\u53cc\u81c2\u4e00\u4ea4\uff0c\u5f52\u8f9b\u6811\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u72c2\u5984\uff0c\u679c\u7136\u6709\u70b9\u529f\u592b\u3002\u201d\u4e58\u4ed6\u5de6\u62f3\u6253\u6765\uff0c\u5de6\u638c\u556a\u7684\u4e00\u58f0\uff0c\u6253\u5728\u4ed6\u5de6\u8098\u4e4b\u4e0a\uff0c\u53d1\u529b\u5f80\u5916\u4e00\u9001\u3002\u54ea\u77e5\u5b5f\u94ee\u7684\u529f\u592b\u6700\u8bb2\u7a76\u9a6c\u6b65\u575a\u5b9e\uff0c\u8fd9\u4e00\u9001\u7adf\u53ea\u5c06\u4ed6\u63a8\u5f97\u8eab\u5b50\u6643\u4e86\u51e0\u6643\u3002\u8881\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u8fd9\u4e00\u62db\u6ca1\u6253\u5012\u4e86\u4ed6\uff0c\u59d3\u5b5f\u7684\u8981\u53d7\u91cd\u4f24\u3002\u201d\u4f46\u89c1\u5f52\u8f9b\u6811\u53c8\u662f\u4e00\u638c\u6253\u51fa\uff0c\u5b5f\u94ee\u53cc\u81c2\u594b\u529b\u62b5\u51fa\uff0c\u731b\u89c9\u4e00\u80a1\u52b2\u98ce\u903c\u6765\uff0c\u767b\u65f6\u795e\u667a\u80e1\u6d82\uff0c\u4ef0\u5929\u8dcc\u5012\uff0c\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4f17\u4eba\u5927\u58f0\u60ca\u547c\u3002\u5b5f\u4f2f\u98de\u548c\u5b5f\u94f8\u62a2\u4e0a\u76f8\u6276\uff0c\u53ea\u89c1\u5b5f\u94ee\u6162\u6162\u9192\u8f6c\uff0c\u53e3\u4e2d\u8fde\u55b7\u9c9c\u8840\uff0c\u4e00\u53e3\u6c14\u6e10\u6e10\u63a5\u4e0d\u4e0a\u6765\u3002\u5f52\u8f9b\u6811\u521a\u624d\u4e00\u9001\u6ca1\u63a8\u52a8\u4ed6\uff0c\u53ea\u9053\u4ed6\u6b66\u529f\u679c\u9ad8\uff0c\u7b2c\u4e09\u638c\u4fbf\u51fa\u4e86\u5168\u529b\u3002\u5b5f\u94ee\u62da\u547d\u67b6\u5f97\u4e24\u62db\uff0c\u529b\u6c14\u5df2\u5c3d\uff0c\u8fd9\u7b2c\u4e09\u62db\u5c31\u7b97\u662f\u8f7b\u8f7b\u4e00\u6307\uff0c\u4e5f\u5c31\u5012\u4e86\uff0c\u8fd9\u80a1\u638c\u529b\u6392\u5c71\u5012\u6d77\u800c\u6765\uff0c\u54ea\u91cc\u7981\u53d7\u5f97\u4f4f\uff1f\u5f52\u8f9b\u6811\u4e07\u60f3\u4e0d\u5230\u4ed6\u5df2\u7ecf\u5168\u7136\u65e0\u529b\u62b5\u5fa1\uff0c\u773c\u89c1\u4ed6\u53d7\u4f24\u5fc5\u6b7b\uff0c\u5012\u4e5f\u9887\u4e3a\u540e\u6094\u3002 \u3000\u3000\u4e01\u7532\u795e\u4e01\u6e38\u548c\u5b5f\u94f8\u4e24\u4eba\u6c14\u5f97\u773c\u4e2d\u5192\u706b\uff0c\u9f50\u5411\u5f52\u8f9b\u6811\u6251\u51fb\u3002 \u3000\u3000\u5b5f\u4f2f\u98de\u7ed9\u513f\u5b50\u63a8\u5bab\u8fc7\u8840\uff0c\u773c\u89c1\u4ed6\u6c14\u82e5\u6e38\u4e1d\uff0c\u4e0d\u7981\u8001\u6cea\u6cc9\u6d8c\uff0c\u7a81\u7136\u8f6c\u8eab\uff0c\u5411\u5f52\u8f9b\u6811\u6253\u6765\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u89c1\u6b63\u70b9\u5b50\u8463\u5f00\u5c71\u4e58\u673a\u60f3\u6e9c\uff0c\u8eab\u5b50\u4e00\u632b\uff0c\u4ece\u4e01\u6e38\u4e0e\u5b5f\u94f8\u62f3\u4e0b\u94bb\u4e86\u8fc7\u53bb\uff0c\u4f38\u6307\u5728\u8463\u5f00\u5c71\u80c1\u4e0b\u4e00\u70b9\u3002\u8463\u5f00\u5c71\u767b\u65f6\u5446\u4f4f\uff0c\u4e00\u8db3\u5728\u524d\uff0c\u4e00\u8db3\u5728\u540e\uff0c\u4e00\u526f\u5411\u5916\u6025\u5954\u7684\u795e\u6c14\uff0c\u5374\u662f\u79fb\u52a8\u4e0d\u5f97\u534a\u6b65\uff0c\u5634\u91cc\u5140\u81ea\u5728\u53eb\uff1a\u201c\u5f52\u8001\u4e8c\uff0c\u8001\u5b50\u2026\u2026\u8001\u5b50\u8ddf\u4f60\u62da\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5b5f\u4f2f\u98de\u5df2\u4e0e\u5f52\u4e8c\u5a18\u4ea4\u4e0a\u4e86\u624b\uff0c\u4e24\u4eba\u529f\u529b\u76f8\u5f53\uff0c\u5f52\u4e8c\u5a18\u5403\u4e8f\u5728\u62b1\u4e86\u5b69\u5b50\uff0c\u88ab\u4ed6\u52bf\u5982\u75af\u864e\u822c\u7684\u4e00\u8f6e\u6025\u653b\uff0c\u8fed\u9047\u9669\u62db\u3002 \u3000\u3000\u6885\u5251\u548c\u3001\u5218\u57f9\u751f\u3001\u5b59\u4ef2\u541b\u4e09\u4eba\u4e5f\u5df2\u548c\u5b5f\u95e8\u5f1f\u5b50\u6253\u5f97\u5341\u5206\u6fc0\u70c8\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u54b1\u4eec\u5feb\u529d\uff0c\u522b\u5f04\u51fa\u5927\u4e8b\u6765\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5e08\u54e5\u5e08\u5ac2\u8ddf\u6211\u5f88\u6709\u5acc\u9699\uff0c\u6211\u82e5\u51fa\u5934\u76f8\u529d\uff0c\u4e8b\u60c5\u53ea\u6709\u66f4\u7cdf\uff0c\u4e14\u770b\u4e00\u9635\u518d\u8bf4\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5f52\u8f9b\u6811\u4e0a\u524d\u52a9\u6218\uff0c\u4e0d\u6570\u62db\u5df2\u70b9\u4e2d\u4e86\u5b5f\u4f2f\u98de\u7684\u7a74\u9053\u3002\u53ea\u89c1\u4ed6\u5728\u5927\u5385\u4e2d\u4e1c\u4e00\u6643\uff0c\u897f\u4e00\u95ea\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u5c06\u5b5f\u5bb6\u6570\u5341\u540d\u5f1f\u5b50\u4eb2\u5c5e\u5168\u90fd\u70b9\u4e2d\u4e86\u7a74\u9053\u3002\u8fd9\u4e9b\u4eba\u6709\u7684\u4f38\u62f3\uff0c\u6709\u7684\u8e22\u8db3\uff0c\u6709\u7684\u5f2f\u8170\uff0c\u6709\u7684\u626d\u5934\uff0c\u59ff\u52bf\u5404\u4e0d\u76f8\u540c\uff0c\u7136\u800c\u4e2a\u4e2a\u52a8\u5f39\u4e0d\u5f97\uff0c\u53ea\u662f\u773c\u73e0\u9aa8\u788c\u788c\u7684\u8f6c\u52a8\u3002\u8d3a\u5ba2\u4e2d\u867d\u6709\u4e0d\u5c11\u6b66\u6797\u9ad8\u624b\uff0c\u4f46\u89c1\u795e\u62f3\u65e0\u654c\u5982\u6b64\u5389\u5bb3\uff0c\u54ea\u4e2a\u8fd8\u6562\u51fa\u5934\uff1f \u3000\u3000\u5f52\u4e8c\u5a18\u5bf9\u6885\u5251\u548c\u9053\uff1a\u201c\u641c\u90a3\u59d3\u8463\u7684\u3002\u201d\u6885\u5251\u548c\u89e3\u4e0b\u8463\u5f00\u5c71\u80cc\u4e0a\u5305\u88f9\uff0c\u5728\u4ed6\u8eab\u4e0a\u91cc\u91cc\u5916\u5916\u641c\u4e86\u4e00\u904d\uff0c\u5374\u54ea\u91cc\u6709\u832f\u82d3\u9996\u4e4c\u4e38\u7684\u8e2a\u5f71\uff1f\u5f52\u8f9b\u6811\u89e3\u5f00\u4ed6\u7a74\u9053\uff0c\u95ee\u9053\uff1a\u201c\u4e38\u836f\u653e\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u54fc\uff0c\u60f3\u5f97\u4e38\u836f\uff0c\u8ddf\u6211\u5230\u8fd9\u91cc\u6765\u5e72\u751a\u4e48\uff1f\u4e8f\u4f60\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u8fde\u8fd9\u91d1\u8749\u8131\u58f3\u4e4b\u8ba1\u4e5f\u4e0d\u61c2\u3002\u201d\u5f52\u4e8c\u5a18\u6012\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u8463\u5f00\u5c71\u9053\uff1a\u201c\u4e38\u836f\u65e9\u5230\u4e86\u5317\u4eac\u5566\u3002\u201d\u5f52\u4e8c\u5a18\u53c8\u60ca\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u5f53\u771f\uff1f\u201d\u8463\u5f00\u5c71\u9053\uff1a\u201c\u6211\u4ef0\u6155\u5b5f\u8001\u7237\u5b50\u662f\u597d\u670b\u53cb\uff0c\u4e13\u8bda\u524d\u6765\u62dc\u5bff\u3002\u96be\u9053\u660e\u77e5\u4f60\u4eec\u60f3\u62a2\u4e38\u836f\uff0c\u8fd8\u4f1a\u628a\u8fd9\u4e1c\u897f\u5e26\u4e0a\u95e8\u6765\u8fde\u7d2f\u4ed6\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u5723\u624b\u795e\u5077\u80e1\u6842\u5357\u8d70\u5230\u8881\u627f\u5fd7\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u8fd9\u9556\u5934\u626f\u8c0e\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u80e1\u6842\u5357\u9053\uff1a\u201c\u4ed6\u7684\u4e38\u836f\u85cf\u5728\u8fd9\u91cc\u3002\u201d\u8bf4\u7740\u5411\u201c\u5bff\u201d\u5b57\u5927\u9526\u8f74\u4e0b\u7684\u4e00\u76d8\u5bff\u6843\u4e00\u6307\u3002\u8881\u627f\u5fd7\u5f88\u662f\u5947\u602a\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\uff1f\u201d\u80e1\u6842\u5357\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u6c5f\u6e56\u4e0a\u5077\u5077\u6478\u6478\u7684\u52fe\u5f53\uff0c\u522b\u60f3\u9003\u8fc7\u6211\u7684\u773c\u775b\u3002\u201d\u9752\u9752\u5728\u4e00\u65c1\u542c\u7740\uff0c\u7b11\u9053\uff1a\u201c\u65c1\u4eba\u60f3\u5728\u795e\u5077\u8001\u7956\u5b97\u9762\u524d\u641e\u9b3c\uff0c\u5f53\u771f\u662f\u9c81\u73ed\u95e8\u524d\u5f04\u5927\u65a7\u4e86\u3002\u201d\u80e1\u6842\u5357\u7b11\u9053\uff1a\u201c\u59d3\u80e1\u7684\u522b\u7684\u80fd\u4e3a\u662f\u6ca1\u6709\uff0c\u8bf4\u5230\u5077\u5077\u6478\u6478\u751a\u4e48\u7684\u52fe\u5f53\uff0c\u5374\u8f93\u4e0d\u4e86\u7ed9\u4eba\u3002\u8fd9\u59d3\u8463\u7684\u597d\u5201\u6ed1\uff0c\u4ed6\u6599\u5230\u5f52\u4e8c\u7237\u5b9a\u4f1a\u8ffd\u6765\uff0c\u56e0\u6b64\u628a\u4e38\u836f\u653e\u5728\u5bff\u6843\u4e4b\u4e2d\uff0c\u7b49\u5bf9\u5934\u8d70\u4e86\uff0c\u518d\u6084\u6084\u53bb\u53d6\u51fa\u6765\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u4ece\u4eba\u4e1b\u4e2d\u51fa\u6765\uff0c\u8d70\u5230\u5b5f\u4f2f\u98de\u8eab\u8fb9\uff0c\u4f38\u638c\u5728\u4ed6\u201c\u7487\u7391\u201d\u3001\u201c\u795e\u5ead\u201d\u4e24\u7a74\u4e0a\u6309\u634f\u63a8\u62ff\u51e0\u4e0b\uff0c\u5185\u529b\u5230\u5904\uff0c\u5b5f\u4f2f\u98de\u8eab\u5b50\u767b\u65f6\u6d3b\u52a8\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u5389\u58f0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u53c8\u8981\u6765\u591a\u7ba1\u95f2\u4e8b\uff1f\u201d\u628a\u5b69\u5b50\u5f80\u5b59\u4ef2\u541b\u624b\u91cc\u4e00\u9001\uff0c\u4f38\u624b\u5f80\u8881\u627f\u5fd7\u80a9\u5934\u6293\u6765\u3002\u8881\u627f\u5fd7\u5f80\u5de6\u4e00\u504f\uff0c\u907f\u5f00\u4e86\u5979\u4e00\u6293\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5ac2\uff0c\u4e14\u542c\u6211\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5b5f\u4f2f\u98de\u7b4b\u9aa8\u6d3b\u52a8\u4e4b\u540e\uff0c\u5de6\u638c\u201c\u74dc\u68da\u62c2\u6247\u201d\uff0c\u53f3\u638c\u201c\u53e4\u9053\u626c\u97ad\u201d\uff0c\u8fde\u7eed\u4e24\u638c\uff0c\u5411\u5f52\u4e8c\u5a18\u62cd\u6765\u3002\u4ed6\u8fd9\u5feb\u6d3b\u4e09\u5341\u638c\u9a70\u8a89\u6b66\u6797\uff0c\u81ea\u6709\u72ec\u5f97\u4e4b\u79d8\uff0c\u9047\u5230\u5f52\u8f9b\u6811\u65f6\u68cb\u5dee\u4e00\u7740\uff0c\u7f1a\u624b\u7f1a\u811a\uff0c\u4f46\u4e0e\u5f52\u4e8c\u5a18\u5374\u4e0d\u76f8\u4e0a\u4e0b\u3002\u4e24\u4eba\u62f3\u6765\u638c\u5f80\uff0c\u8fc5\u5373\u4ea4\u4e86\u5341\u591a\u62db\u3002\u5f52\u8f9b\u6811\u9053\uff1a\u201c\u4f60\u8ba9\u5f00\u3002\u201d\u5f52\u4e8c\u5a18\u5f80\u5de6\u95ea\u5f00\u3002\u5b5f\u4f2f\u98de\u53f3\u638c\u98de\u4e0a\u3002\u5f52\u8f9b\u6811\u4fa7\u62f3\u800c\u51fa\uff0c\u4e0d\u6570\u62db\u53c8\u5df2\u70b9\u4e2d\u4e86\u4ed6\u7684\u7a74\u9053\u3002\u8881\u627f\u5fd7\u82e5\u518d\u8fc7\u53bb\u89e3\u4ed6\u7a74\u9053\uff0c\u52bf\u5fc5\u8ddf\u5e08\u54e5\u52a8\u624b\uff0c\u5f53\u4e0b\u53ea\u6709\u76b1\u7709\u4e0d\u52a8\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u813e\u6c14\u672c\u6765\u66b4\u8e81\uff0c\u8fd9\u65f6\u7231\u5b50\u5fc3\u5207\uff0c\u884c\u4e8b\u66f4\u589e\u4e86\u51e0\u5206\u4e56\u5f20\uff0c\u53eb\u9053\uff1a\u201c\u59d3\u8463\u7684\uff0c\u4f60\u4e0d\u62ff\u836f\u51fa\u6765\uff0c\u6211\u628a\u4f60\u4e24\u6761\u81c2\u8180\u6298\u4e86\u3002\u201d \u3000\u3000\u5de6\u624b\u62ff\u4f4f\u8463\u5f00\u5c71\u624b\u8155\uff0c\u5c06\u4ed6\u624b\u81c2\u626d\u8f6c\uff0c\u53f3\u62f3\u8d77\u5728\u7a7a\u4e2d\uff0c\u53ea\u8981\u5f80\u4e0b\u4e00\u843d\uff0c\u4e00\u62f3\u6253\u5728\u8098\u5173\u8282\u4e0a\uff0c\u624b\u81c2\u7acb\u65f6\u6298\u65ad\u3002\u8463\u5f00\u5c71\u54ac\u7d27\u7259\u5173\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u836f\u4e0d\u5728\u6211\u8fd9\u91cc\uff0c\u6298\u78e8\u6211\u4e5f\u6ca1\u7528\u3002\u201d\u8d3a\u5ba2\u4e2d\u6709\u4e9b\u4eba\u77a7\u4e0d\u8fc7\u773c\uff0c\u633a\u8eab\u51fa\u6765\u53eb\u9635\u3002 \u3000\u3000\u8881\u627f\u5fd7\u773c\u89c1\u5c40\u9762\u5927\u4e71\uff0c\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u4f4f\u624b\uff01\u201d\u53eb\u4e86\u51e0\u58f0\uff0c\u65e0\u4eba\u7406\u776c\uff0c\u5fc3\u60f3\uff1a\u518d\u8fc7\u5f97\u7247\u523b\uff0c\u82e5\u662f\u6740\u4f24\u4e86\u4eba\u547d\uff0c\u90a3\u5c31\u96be\u4ee5\u633d\u6551\uff0c\u975e\u5feb\u5200\u65a9\u4e71\u9ebb\u4e0d\u53ef\uff0c\u7a81\u7136\u7eb5\u8d77\uff0c\u843d\u5728\u5b59\u4ef2\u541b\u8eab\u65c1\uff0c\u5de6\u624b\u4e00\u62db\u201c\u53cc\u9f99\u62a2\u73e0\u201d\uff0c\u98df\u4e2d\u4e8c\u6307\u5f80\u5979\u773c\u4e2d\u6316\u53bb\u3002\u5b59\u4ef2\u541b\u5927\u60ca\uff0c\u75be\u5fd9\u4f38\u53f3\u81c2\u6321\u67b6\u3002\u5c82\u77e5\u4ed6\u8fd9\u4e00\u62db\u53ea\u662f\u58f0\u4e1c\u51fb\u897f\uff0c\u4e58\u5979\u5fd9\u4e71\u4e2d\u56de\u62a4\u773c\u73e0\uff0c\u53f3\u638c\u5728\u5979\u80a9\u5934\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5b59\u4ef2\u541b\u9000\u5f00\u4e09\u6b65\uff0c\u5b69\u5b50\u5df2\u88ab\u4ed6\u62a2\u4e86\u8fc7\u53bb\u3002\u5b59\u4ef2\u541b\u5927\u60ca\uff0c\u9ad8\u53eb\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u5a18\uff01\u5feb\uff0c\u5feb\uff0c\u4ed6\u2026\u2026\u201d \u3000\u3000\u5f52\u8f9b\u6811\u592b\u5987\u56de\u8fc7\u5934\u6765\uff0c\u8881\u627f\u5fd7\u5df2\u62b1\u7740\u5b69\u5b50\uff0c\u8df3\u4e0a\u4e00\u5f20\u684c\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u5251\uff01\u201d\u9752\u9752\u63b7\u8fc7\u5251\u53bb\uff0c\u8881\u627f\u5fd7\u4f38\u5de6\u624b\u63a5\u4f4f\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u522b\u52a8\u624b\uff0c\u542c\u6211\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5f52\u4e8c\u5a18\u7ea2\u4e86\u773c\u775b\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u6742\u79cd\uff0c\u4f60\u6562\u4f24\u6211\u5b69\u5b50\uff0c\u6211\u2026\u2026\u6211\u8ddf\u4f60\u62da\u4e86\uff01\u201d\u8bf4\u7740\u8981\u6251\u4e0a\u53bb\u62da\u547d\u3002\u5f52\u8f9b\u6811\u4e00\u628a\u62c9\u4f4f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b69\u5b50\u5728\u4ed6\u624b\u91cc\uff0c\u522b\u5fd9\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u8bf7\u4f60\u628a\u5b5f\u8001\u7237\u5b50\u7684\u7a74\u9053\u89e3\u5f00\u4e86\u3002\u201d\u5f52\u8f9b\u6811\u54fc\u4e86\u4e00\u58f0\uff0c\u4f9d\u8a00\u5c06\u5b5f\u4f2f\u98de\u7a74\u9053\u62cd\u5f00\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u524d\u8f88\uff0c\u4f17\u5bb6\u670b\u53cb\u3002\u6211\u5e08\u54e5\u5b69\u5b50\u6709\u75c5\uff0c\u8981\u501f\u8d2a\u5b98\u9a6c\u58eb\u82f1\u7684\u4e38\u836f\u6551\u547d\uff0c\u53ef\u662f\u8fd9\u4f4d\u8463\u9556\u5934\u7518\u5fc3\u7ed9\u8d43\u5b98\u5356\u547d\uff0c\u6211\u5e08\u54e5\u624d\u8ddf\u4ed6\u8fc7\u4e0d\u53bb\u3002\u5b5f\u8001\u7237\u5b50\u662f\u597d\u670b\u53cb\uff0c\u4eca\u65e5\u662f\u4ed6\u8001\u4eba\u5bb6\u5343\u79cb\u5927\u559c\u4e4b\u65e5\uff0c\u6211\u4eec\u51b3\u4e0d\u4f1a\u6709\u610f\u524d\u6765\u6253\u6270\u3002\u201d\u4f17\u4eba\u4e00\u542c\uff0c\u90fd\u89c9\u5947\u602a\uff0c\u660e\u660e\u89c1\u4ed6\u4eec\u5e08\u5144\u5f1f\u4e92\u6597\uff0c\u600e\u4e48\u4ed6\u5374\u5e2e\u5e08\u5144\u8bf4\u8d77\u8bdd\u6765\u4e86\u3002\u5f52\u6c0f\u592b\u5987\u66f4\u52a0\u60ca\u5f02\u3002\u5f52\u4e8c\u5a18\u53c8\u53eb\uff1a\u201c\u5feb\u8fd8\u6211\u5b69\u5b50\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u9ad8\u58f0\u9053\uff1a\u201c\u5b5f\u8001\u7237\u5b50\uff0c\u8bf7\u4f60\u628a\u8fd9\u76d8\u5bff\u6843\u63b0\u5f00\u6765\u77a7\u77a7\uff0c\u4e2d\u95f4\u53ef\u6709\u70b9\u513f\u53e4\u602a\u3002\u201d\u8463\u5f00\u5c71\u4e00\u542c\uff0c\u767b\u65f6\u53d8\u8272\u3002\u5b5f\u4f2f\u98de\u4e0d\u77e5\u4ed6\u846b\u82a6\u91cc\u5356\u751a\u4e48\u836f\uff0c\u4f9d\u8a00\u63b0\u5f00\u4e00\u4e2a\u5bff\u6843\uff0c\u53ea\u89c1\u67a3\u6ce5\u9985\u5b50\u4e4b\u5185\u9732\u51fa\u4e00\u9897\u767d\u8272\u8721\u4e38\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u4e00\u65f6\u4e0d\u660e\u767d\u8fd9\u662f\u751a\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u8463\u9556\u5934\u8981\u662f\u771f\u6709\u80fd\u8010\u7ed9\u8d43\u5b98\u5356\u547d\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u53ef\u662f\u4ed6\u5fc3\u80a0\u72e0\u6bd2\uff0c\u524d\u6765\u6311\u62e8\u79bb\u95f4\uff0c\u8981\u54b1\u4eec\u574f\u4e86\u6b66\u6797\u540c\u9053\u7684\u4e49\u6c14\u3002\u5b5f\u8001\u7237\u5b50\uff0c\u8fd9\u51e0\u76d8\u5bff\u6843\u662f\u8463\u9556\u5934\u9001\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u5b5f\u4f2f\u98de\u70b9\u70b9\u5934\u3002\u8881\u627f\u5fd7\u53c8\u9053\uff1a\u201c\u4ed6\u628a\u4e38\u836f\u85cf\u5728\u5bff\u6843\u4e4b\u5185\uff0c\u660e\u77e5\u5bff\u6843\u4e00\u65f6\u4e0d\u4f1a\u5403\uff0c\u7b49\u5bff\u7b75\u8fc7\u4e86\uff0c\u6211\u5e08\u54e5\u8ddf\u5b5f\u8001\u7237\u5b50\u4f24\u4e86\u548c\u6c14\uff0c\u4ed6\u518d\u5077\u5077\u53d6\u51fa\uff0c\u9001\u5230\u4eac\u91cc\uff0c\u5c82\u4e0d\u662f\u5947\u529f\u4e00\u4ef6\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u8d70\u8fd1\u684c\u8fb9\u3002\u9752\u9752\u4e5f\u8fc7\u6765\u76f8\u52a9\u3002\u4e24\u4eba\u628a\u5bff\u6843\u90fd\u63b0\u4e86\u5f00\u6765\uff0c\u5c06\u9985\u91cc\u6240\u85cf\u7684\u56db\u5341\u9897\u4e38\u836f\u5c3d\u6570\u53d6\u51fa\u3002\u8881\u627f\u5fd7\u634f\u7834\u4e00\u9897\u8721\u4e38\uff0c\u4e00\u9635\u82b3\u9999\u6251\u9f3b\uff0c\u9732\u51fa\u9f99\u773c\u5927\u4e00\u679a\u6731\u7ea2\u4e38\u836f\u6765\u3002 \u3000\u3000\u4ed6\u53eb\u9752\u9752\u53d6\u6765\u4e00\u676f\u6e05\u6c34\uff0c\u5c06\u4e38\u836f\u8c03\u4e86\uff0c\u5582\u5165\u5b69\u5b50\u53e3\u4e2d\u3002\u90a3\u5b69\u5b50\u65e9\u5df2\u6c14\u82e5\u6e38\u4e1d\uff0c\u4e5f\u4e0d\u54ed\u95f9\uff0c\u4e00\u53e3\u53e3\u7684\u90fd\u54bd\u5165\u4e86\u809a\u91cc\u3002\u5f52\u4e8c\u5a18\u53cc\u76ee\u542b\u6cea\uff0c\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u5fc3\u60f3\u4eca\u5929\u82e5\u4e0d\u662f\u5c0f\u5e08\u5f1f\u8bc6\u7834\u673a\u5173\uff0c\u4e0d\u4f46\u6551\u4e0d\u4e86\u513f\u5b50\u7684\u547d\uff0c\u8fd8\u5f97\u7f6a\u4e86\u4e0d\u5c11\u82f1\u96c4\u8c6a\u6770\uff0c\u7d2f\u4e86\u4e08\u592b\u4e00\u4e16\u82f1\u540d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b49\u5b69\u5b50\u670d\u8fc7\u836f\u540e\uff0c\u53cc\u624b\u62b1\u7740\u4ea4\u8fc7\u3002\u5f52\u4e8c\u5a18\u63a5\u4e86\u8fc7\u53bb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u6211\u4eec\u592b\u5987\u771f\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u5f52\u8f9b\u6811\u53ea\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u5f88\u597d\uff0c\u5f88\u597d\u3002\u201d\u9752\u9752\u628a\u4e38\u836f\u90fd\u9012\u7ed9\u4e86\u5f52\u4e8c\u5a18\uff0c\u7b11\u9053\uff1a\u201c\u5b69\u5b50\u518d\u751f\u51e0\u573a\u91cd\u75c5\uff0c\u4e5f\u591f\u5403\u7684\u4e86\u3002\u201d\u5f52\u4e8c\u5a18\u5fc3\u4e2d\u6b63\u81ea\u6b22\u559c\u4e0d\u5c3d\uff0c\u4e5f\u4e0d\u7406\u4f1a\u5979\u8bdd\u4e2d\u542b\u523a\uff0c\u8c22\u7740\u63a5\u8fc7\u3002 \u3000\u3000\u5f52\u8f9b\u6811\u5fd9\u7740\u7ed9\u70b9\u4e2d\u7a74\u9053\u7684\u4eba\u89e3\u7a74\uff0c\u89e3\u4e00\u4e2a\uff0c\u8bf4\u4e00\u53e5\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff01\u201d\u5b5f\u4f2f\u98de\u9ed8\u7136\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u513f\u5b50\u662f\u6551\u6d3b\u4e86\uff0c\u6211\u513f\u5b50\u5374\u7ed9\u4f60\u6253\u6b7b\u4e86\u3002\u5b9a\u5f53\u9080\u7ea6\u80fd\u4eba\uff0c\u62a5\u6b64\u5927\u4ec7\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5b5f\u95e8\u5f1f\u5b50\u62ac\u4e86\u5782\u6b7b\u7684\u5b5f\u94ee\u6b63\u8981\u8d70\u5165\u5185\u5802\uff0c\u53eb\u9053\uff1a\u201c\u8bf7\u7b49\u4e00\u4e0b\u3002\u201d\u5b5f\u94f8\u6012\u9053\uff1a\u201c\u6211\u54e5\u54e5\u5df2\u6b7b\u5b9a\u5566\uff0c\u8fd8\u8981\u600e\u6837\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5e08\u54e5\u7d20\u6765\u4ef0\u6155\u5b5f\u8001\u7237\u5b50\u7684\u5a01\u540d\uff0c\u4eb2\u8fd1\u8fd8\u6765\u4e0d\u53ca\uff0c\u54ea\u4f1a\u771f\u7684\u4f24\u5bb3\u5b5f\u5927\u54e5\u6027\u547d\uff1f\u8fd9\u4e00\u638c\u867d\u7136\u4f7f\u529b\u5927\u4e86\u4e00\u70b9\uff0c\u4f46\u5b5f\u5927\u54e5\u6027\u547d\u65e0\u788d\uff0c\u5c3d\u53ef\u4e0d\u5fc5\u62c5\u5fc3\u3002\u201d\u4f17\u4eba\u4e00\u542c\uff0c\u90fd\u60f3\uff1a\u201c\u773c\u89c1\u4ed6\u53d7\u4f24\u8fd9\u822c\u6c89\u91cd\uff0c\u4f60\u8fd9\u8bdd\u9a97\u8c01\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5e08\u54e5\u5e76\u672a\u5b58\u5fc3\u4f24\u4ed6\uff0c\u53ea\u8981\u7ed9\u5b5f\u5927\u54e5\u670d\u4e00\u5242\u836f\uff0c\u8c03\u517b\u4e00\u6bb5\u65f6\u5019\uff0c\u5c31\u6ca1\u4e8b\u4e86\u3002\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u53d6\u51fa\u91d1\u76d2\uff0c\u63ed\u5f00\u76d2\u76d6\uff0c\u62ff\u4e86\u4e00\u53ea\u6731\u775b\u51b0\u87fe\u51fa\u6765\uff0c\u7528\u624b\u634f\u788e\uff0c\u5728\u7897\u4e2d\u51b2\u9152\u8c03\u5408\uff0c\u7ed9\u5b5f\u94ee\u559d\u4e86\u4e0b\u53bb\u3002\u4e0d\u4e00\u523b\uff0c\u5b5f\u94ee\u679c\u7136\u8138\u4e0a\u89c1\u7ea2\uff0c\u547b\u541f\u547c\u75db\u3002\u5b5f\u4f2f\u98de\u559c\u51fa\u671b\u5916\uff0c\u5fcd\u4e0d\u4f4f\u6cea\u6c34\u4ece\u8138\u988a\u4e0a\u76f4\u6d41\u4e0b\u6765\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u8881\u76df\u4e3b\uff0c\u4f60\u771f\u662f\u6211\u513f\u5b50\u7684\u6551\u547d\u6069\u4eba\u3002\u201d\u8881\u627f\u5fd7\u8fde\u58f0\u900a\u8c22\u3002 \u3000\u3000\u5f53\u4e0b\u5b5f\u94f8\u6307\u6325\u5bb6\u4eba\uff0c\u5c06\u5144\u957f\u62ac\u5230\u5185\u623f\u4f11\u606f\u3002\u5385\u4e0a\u91cd\u6574\u676f\u76d8\uff0c\u5f00\u6000\u7545\u996e\u3002 \u3000\u3000\u5f52\u4e8c\u5a18\u5411\u5b5f\u4f2f\u98de\u9053\uff1a\u201c\u5b5f\u8001\u7237\u5b50\uff0c\u6211\u4eec\u5b9e\u5728\u5364\u83bd\uff0c\u5343\u4e07\u8bf7\u4f60\u539f\u8c05\u3002\u201d\u4e00\u62c9\u4e08\u592b\uff0c\u4e0e\u4e09\u4e2a\u5f92\u5f1f\u4e00\u9f50\u62dc\u4e86\u4e0b\u53bb\u3002\u5b5f\u4f2f\u98de\u5475\u5475\u7b11\u9053\uff1a\u201c\u513f\u5b50\u8981\u6b7b\uff0c\u8c01\u90fd\u5fc3\u614c\uff0c\u8001\u592b\u4e5f\u662f\u4e00\u822c\uff0c\u8fd9\u4e5f\u602a\u4e0d\u5f97\u8d24\u5b5f\u6881\u3002\u201d\u5f52\u6c0f\u592b\u5987\u53c8\u53bb\u5411\u9002\u624d\u52a8\u8fc7\u624b\u7684\u4eba\u5206\u522b\u9053\u6b49\u3002\u7fa4\u96c4\u7545\u996e\u4e86\u4e00\u4f1a\u3002\u5b5f\u4f2f\u98de\u7ec8\u662f\u4e0d\u653e\u5fc3\uff0c\u8fdb\u53bb\u770b\u513f\u5b50\u4f24\u52bf\u5982\u4f55\uff0c\u53ea\u89c1\u4ed6\u6c89\u6c89\u7761\u719f\uff0c\u547c\u5438\u5300\u51c0\uff0c\u6599\u5df2\u65e0\u4e8b\u3002 \u3000\u3000\u5b5f\u4f2f\u98de\u5fc3\u65e0\u6302\u788d\uff0c\u51fa\u6765\u4e0e\u656c\u9152\u7684\u8d3a\u5ba2\u4eec\u9152\u5230\u676f\u5e72\uff0c\u76f4\u996e\u5230\u516b\u4e5d\u5206\u3002\u4ed6\u66f4\u53eb\u62ff\u5927\u7897\u6765\uff0c\u6ee1\u6ee1\u659f\u4e86\u4e24\u7897\uff0c\u7aef\u5230\u8881\u627f\u5fd7\u9762\u524d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8881\u76df\u4e3b\uff0c\u6cf0\u5c71\u5927\u4f1a\u4e0a\u4f17\u82f1\u96c4\u63a8\u4f60\u4e3a\u5c0a\uff0c\u8001\u5b9e\u4e0d\u5ba2\u6c14\u8bf4\uff0c\u5728\u4e0b\u672c\u6765\u662f\u5fc3\u91cc\u4e0d\u670d\u7684\u3002\u4f46\u4eca\u65e5\u4f60\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u5728\u4e0b\u4e0d\u4f46\u611f\u6fc0\uff0c\u4e14\u662f\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\u3002\u6765\uff0c\u656c\u4f60\u4e00\u7897\u3002\u201d\u7aef\u8d77\u5927\u7897\uff0c\u9aa8\u90fd\u90fd\u4e00\u53e3\u6c14\u5c06\u9152\u559d\u4e86\u3002\u8881\u627f\u5fd7\u9152\u91cf\u672c\u4e0d\u751a\u9ad8\uff0c\u4f46\u89c1\u4ed6\u4e00\u756a\u7f8e\u610f\uff0c\u4e5f\u53ea\u5f97\u628a\u7897\u4e2d\u9152\u5e72\u4e86\u3002\u7fa4\u96c4\u8f70\u7136\u53eb\u597d\u3002\u5b5f\u4f2f\u98de\u5927\u62c7\u6307\u4e00\u7fd8\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76df\u4e3b\u6b64\u540e\u4f46\u6709\u751a\u4e48\u5dee\u9063\uff0c\u5728\u4e0b\u529b\u91cf\u867d\u5c0f\uff0c\u8981\u94b1\uff0c\u5341\u4e07\u516b\u4e07\u94f6\u5b50\u8fd8\u5bf9\u4ed8\u5f97\u4e86\u3002\u8981\u4eba\uff0c\u5728\u4e0b\u7236\u5b50\u5e08\u5f92\uff0c\u81ea\u7136\u8d74\u6c64\u8e48\u706b\uff0c\u5728\u6240\u4e0d\u8f9e\u3002\u8981\u518d\u9080\u4e09\u56db\u767e\u4f4d\u82f1\u96c4\u597d\u6c49\uff0c\u5728\u4e0b\u4e5f\u8fd8\u6709\u8fd9\u70b9\u5c0f\u9762\u5b50\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u8bf4\u5f97\u8c6a\u723d\uff0c\u53c8\u60f3\u4e00\u573a\u5927\u98ce\u6ce2\u7ec8\u4e8e\u987a\u5229\u5316\u89e3\uff0c\u5e08\u5144\u5f1f\u95f4\u539f\u6765\u7684\u5acc\u9699\u4e5f\u70df\u6d88\u4e91\u6563\uff0c\u5fc3\u91cc\u5f88\u662f\u7545\u5feb\u3002\u8fd9\u4e00\u665a\u4f17\u4eba\u5c3d\u9189\u800c\u6563\uff0c\u90a3\u8463\u9556\u5934\u65e9\u5df2\u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u5d07\u796f\u7687\u5e1d\u65e2\u5f97\u4e0d\u5230\u7075\u836f\uff0c\u96be\u4ee5\u5ef6\u5e74\u76ca\u5bff\uff0c\u4ed6\u8463\u603b\u9556\u5934\u81ea\u5df1\u5982\u4f55\u5ef6\u5e74\u76ca\u5bff\uff0c\u8fd9\u5927\u4e8b\u81ea\u987b\u5c3d\u65e9\u5b89\u6392\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b49\u4eba\u5728\u5b5f\u5bb6\u5e84\u76d8\u6853\u6570\u65e5\uff0c\u51e0\u6b21\u8981\u884c\uff0c\u5b5f\u4f2f\u98de\u603b\u662f\u82e6\u7559\u4e0d\u653e\u3002\u5b5f\u94ee\u53d7\u7684\u662f\u5916\u4f24\uff0c\u8fd9\u51e0\u65e5\u4e2d\u597d\u5f97\u751a\u5feb\u3002\u5f52\u8f9b\u6811\u7684\u513f\u5b50\u5f52\u949f\u670d\u4e86\u832f\u82d3\u9996\u4e4c\u4e38\u540e\uff0c\u679c\u7136\u4e5f\u662f\u4e00\u65e5\u597d\u4e8e\u4e00\u65e5\u3002\u5f52\u8f9b\u6811\u592b\u5987\u5fc3\u4e2d\u7684\u6b22\u559c\uff0c\u90a3\u4e5f\u4e0d\u7528\u8bf4\u4e86\u3002 \u3000\u3000\u5230\u7b2c\u4e03\u65e5\u4e0a\uff0c\u76d6\u5b5f\u5c1d\u867d\u7136\u597d\u5ba2\uff0c\u4e5f\u77e5\u4e0d\u80fd\u518d\u7559\uff0c\u53ea\u5f97\u5927\u5f20\u7b75\u5e2d\uff0c\u66ff\u5f52\u8f9b\u6811\u4e0e\u8881\u627f\u5fd7\u7b49\u9001\u884c\u3002\u5e2d\u95f4\u7a0b\u9752\u7af9\u8bf4\u9053\uff1a\u201c\u5b5f\u8001\u54e5\uff0c\u6c38\u80dc\u9556\u5c40\u90a3\u59d3\u8463\u7684\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u4ed6\u5931\u5374\u8d21\u54c1\u4ea4\u4ee3\u4e0d\u4e86\uff0c\u53c8\u627e\u4e0d\u4e0a\u5f52\u4e8c\u7237\uff0c\u53ea\u6015\u8981\u63a8\u5728\u8001\u54e5\u8eab\u4e0a\uff0c\u987b\u5f97\u63d0\u9632\u4e00\u4e8c\u3002\u201d\u5b5f\u4f2f\u98de\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u8981\u662f\u771f\u6765\u60f9\u6211\uff0c\u53ef\u4e0d\u518d\u7ed9\u4ed6\u5ba2\u6c14\u3002\u201d\u5f52\u4e8c\u5a18\u9053\uff1a\u201c\u5b5f\u8001\u54e5\uff0c\u8fd9\u5168\u662f\u6211\u4eec\u60f9\u7684\u4e8b\uff0c\u8981\u662f\u6709\u751a\u4e48\u9ebb\u70e6\uff0c\u53ef\u5343\u4e07\u5f97\u7ed9\u6211\u4eec\u9001\u4fe1\u3002\u201d\u5b5f\u4f2f\u98de\u9053\uff1a\u201c\u597d\uff01\u8fd9\u5c0f\u5b50\u6211\u4e0d\u6015\u4ed6\u3002\u201d\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u5c31\u662f\u9632\u4ed6\u52fe\u7ed3\u5b98\u5e9c\u3002\u201d\u5b5f\u4f2f\u98de\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8981\u662f\u6df7\u4e0d\u4e86\uff0c\u6211\u5c31\u5b66\u4f60\u8001\u5f1f\uff0c\u5360\u5c71\u4e3a\u4e3b\u3002\u201d \u3000\u3000\u7fa4\u96c4\u5728\u7b11\u58f0\u4e2d\u5404\u81ea\u4e0a\u9a6c\u800c\u522b\u3002\u5f52\u8f9b\u6811\u592b\u5987\u62b1\u4e86\u5b69\u5b50\uff0c\u5e26\u7740\u4e09\u4e2a\u5f92\u5f1f\u6b23\u7136\u5357\u5f52\u3002\u8881\u627f\u5fd7\u3001\u9752\u9752\u3001\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u3001\u54d1\u5df4\u3001\u94c1\u7f57\u6c49\u3001\u80e1\u6842\u5357\u3001\u6d2a\u80dc\u6d77\u7b49\u516b\u4eba\u62bc\u7740\u94c1\u7bb1\uff0c\u8fde\u9a91\u5317\u4e0a\u3002 \u3000\u3000\u8fd9\u65e5\u6765\u5230\u9ad8\u7891\u5e97\uff0c\u5929\u8272\u5c06\u66ae\uff0c\u56e0\u884c\u674e\u7b28\u91cd\uff0c\u4e5f\u5c31\u4e0d\u8d2a\u8d76\u8def\u7a0b\uff0c\u5f53\u4e0b\u5728\u9547\u897f\u7684\u201c\u71d5\u8d75\u5c45\u201d\u5ba2\u6808\u6b47\u5bbf\u3002\u4f17\u4eba\u884c\u4e86\u4e00\u5929\u8def\uff0c\u90fd\u5df2\u5026\u4e86\uff0c\u6b63\u8981\u5b89\u7761\uff0c\u5ffd\u7136\u95e8\u5916\u8f66\u58f0\u9686\u9686\uff0c\u4eba\u8bed\u55a7\u54d7\uff0c\u5435\u5f97\u9e21\u98de\u72d7\u8d70\u3002\u9664\u4e86\u54d1\u5df4\u5145\u8033\u4e0d\u95fb\u4e4b\u5916\uff0c\u5404\u4eba\u90fd\u89c9\u5f97\u5341\u5206\u5947\u602a\u3002\u53ea\u542c\u5f97\u58f0\u97f3\u5608\u6742\uff0c\u5ba2\u5e97\u4e2d\u6d8c\u8fdb\u4e00\u6279\u4eba\u6765\uff0c\u542c\u4ed6\u4eec\u53fd\u54e9\u5495\u565c\uff0c\u8bf4\u7684\u8bdd\u534a\u53e5\u4e5f\u4e0d\u61c2\u3002 \u3000\u3000\u4f17\u4eba\u51fa\u623f\u4e00\u770b\uff0c\u53ea\u89c1\u5385\u4e0a\u6216\u5750\u6216\u7ad9\uff0c\u7adf\u662f\u6570\u5341\u540d\u5916\u56fd\u5175\uff0c\u624b\u4e2d\u62ff\u7740\u5947\u5f62\u602a\u72b6\u7684\u5175\u5668\uff0c\u4e71\u54c4\u54c4\u5728\u8bf4\u8bdd\u3002\u8881\u627f\u5fd7\u7b49\u4ece\u6ca1\u89c1\u8fc7\u8fd9\u7b49\u7eff\u773c\u73e0\u3001\u9ad8\u9f3b\u5b50\u7684\u5916\u56fd\u4eba\uff0c\u90fd\u611f\u60ca\u5947\uff0c\u6ce8\u76ee\u6253\u91cf\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4e2a\u4e2d\u56fd\u4eba\u5411\u638c\u67dc\u5927\u58f0\u547c\u559d\uff0c\u8981\u4ed6\u7acb\u5373\u817e\u51fa\u5341\u51e0\u95f4\u4e0a\u623f\u6765\u3002\u638c\u67dc\u9053\uff1a\u201c\u5927\u4eba\uff0c\u5b9e\u5728\u5bf9\u4e0d\u4f4f\u5566\uff0c\u5c0f\u5e97\u51e0\u95f4\u4e0a\u623f\u90fd\u5df2\u4f4f\u4e86\u5ba2\u4eba\u3002\u201d\u90a3\u4eba\u4e0d\u95ee\u60c5\u7531\uff0c\u987a\u624b\u5c31\u662f\u4e00\u8bb0\u8033\u5149\u3002\u90a3\u638c\u67dc\u5de6\u624b\u6309\u4f4f\u9762\u988a\uff0c\u53c8\u6c14\u53c8\u6025\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u90a3\u4eba\u559d\u9053\uff1a\u201c\u4e0d\u8ba9\u51fa\u4e0a\u623f\u6765\uff0c\u653e\u706b\u628a\u4f60\u7684\u5e97\u5b50\u70e7\u4e86\u3002\u201d\u638c\u67dc\u65e0\u6cd5\uff0c\u53ea\u5f97\u6765\u5411\u6d2a\u80dc\u6d77\u54c0\u6c42\uff0c\u6253\u8eac\u4f5c\u63d6\uff0c\u8bf7\u4ed6\u4eec\u632a\u4e24\u95f4\u623f\u51fa\u6765\u3002 \u3000\u3000\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4e5f\u6709\u4e2a\u5148\u6765\u540e\u5230\u3002\u8fd9\u4eba\u662f\u751a\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u638c\u67dc\u5fd9\u9053\uff1a\u201c\u8fbe\u5b98\u7237\uff0c\u522b\u8ddf\u8fd9\u5403\u6d0b\u996d\u7684\u4e00\u822c\u89c1\u8bc6\u3002\u201d\u6c99\u5929\u5e7f\u5947\u9053\uff1a\u201c\u4ed6\u5403\u751a\u4e48\u6d0b\u996d\uff1f\u5403\u4e86\u6d0b\u996d\u5c31\u5a01\u98ce\u4e9b\u4e48\uff1f\u201d\u638c\u67dc\u7684\u6084\u58f0\u9053\uff1a\u201c\u8fd9\u4e9b\u5916\u56fd\u5175\uff0c\u662f\u8fd0\u9001\u7ea2\u5937\u5927\u70ae\u5230\u4eac\u91cc\u53bb\u7684\u3002\u8fd9\u4eba\u4f1a\u8bf4\u6d0b\u8bdd\uff0c\u662f\u5916\u56fd\u5927\u4eba\u7684\u901a\u8bd1\u3002\u201d\u8881\u627f\u5fd7\u7b49\u8fd9\u624d\u660e\u767d\uff0c\u539f\u6765\u8fd9\u4eba\u72d0\u5047\u864e\u5a01\uff0c\u4ed7\u7740\u5916\u56fd\u5175\u7684\u52bf\u4f5c\u5a01\u4f5c\u798f\u3002 \u3000\u3000\u6c99\u5929\u5e7f\u94c1\u6247\u4e00\u5c55\uff0c\u9053\uff1a\u201c\u6211\u53bb\u6559\u8bad\u6559\u8bad\u8fd9\u5c0f\u5b50\u3002\u201d\u8881\u627f\u5fd7\u4e00\u628a\u62c9\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u6162\u6765\uff01\u201d\u628a\u4f17\u4eba\u9080\u5165\u623f\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u7236\u5f53\u5e74\u9547\u5b88\u5173\u8fbd\uff0c\u5b81\u8fdc\u4e24\u4ed7\u5927\u6377\uff0c\u5f97\u529b\u4e8e\u897f\u6d0b\u56fd\u7684\u7ea2\u5937\u5927\u70ae\u751a\u591a\u3002 \u3000\u3000\u6ee1\u6e05\u864f\u9996\u52aa\u5c14\u54c8\u8d64\u5c31\u662f\u7ed9\u7ea2\u5937\u5927\u70ae\u8f70\u6b7b\u7684\u3002\u73b0\u4e0b\u6ee1\u6e05\u5175\u52bf\u7316\u7357\uff0c\u8fd9\u4e9b\u5916\u56fd\u5175\u65e2\u662f\u8fd0\u70ae\u53bb\u52a9\u6218\u7684\uff0c\u54b1\u4eec\u5c31\u8ba9\u4e00\u8ba9\u5427\u3002\u201d\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u96be\u9053\u5c31\u7531\u5f97\u8fd9\u5c0f\u5b50\u53d1\u5a01\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u79cd\u8d31\u7537\u5b50\uff0c\u4f55\u5fc5\u8ddf\u4ed6\u4e00\u822c\u89c1\u8bc6\u3002\u201d\u4f17\u4eba\u542c\u4ed6\u5982\u6b64\u8bf4\uff0c\u5c31\u817e\u4e86\u4e24\u95f4\u4e0a\u623f\u51fa\u6765\u3002 \u3000\u3000\u90a3\u901a\u8bd1\u59d3\u94b1\u540d\u901a\u56db\uff0c\u89c1\u6709\u4e86\u4e24\u95f4\u4e0a\u623f\uff0c\u867d\u7136\u4ecd\u662f\u5476\u5476\u8d23\u9a82\uff0c\u4e5f\u4e0d\u518d\u53eb\u638c\u67dc\u591a\u8ba9\u623f\u95f4\u4e86\u3002\u4ed6\u51fa\u53bb\u4e86\u4e00\u4f1a\uff0c\u9886\u4e86\u4e24\u540d\u5916\u56fd\u519b\u5b98\u8fdb\u5e97\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u5916\u56fd\u519b\u5b98\u4e00\u4e2a\u56db\u5341\u4f59\u5c81\uff0c\u53e6\u4e00\u4e2a\u4e09\u5341\u6765\u5c81\u3002\u4e24\u4eba\u53fd\u54e9\u5495\u565c\u8bf4\u4e86\u4e00\u4f1a\u8bdd\uff0c\u90a3\u5e74\u957f\u519b\u5b98\u51fa\u53bb\u966a\u7740\u4e00\u4e2a\u897f\u6d0b\u5973\u5b50\u8fdb\u6765\u3002\u8fd9\u5973\u5b50\u5e74\u7eaa\u751a\u8f7b\uff0c\u9752\u9752\u7b49\u4e5f\u4f30\u4e0d\u5b9a\u5979\u6709\u591a\u5927\u5e74\u7eaa\uff0c\u6599\u60f3\u662f\u4e8c\u5341\u5c81\u5de6\u53f3\uff0c\u4e00\u5934\u9ed1\u53d1\uff0c\u886c\u7740\u96ea\u767d\u7684\u808c\u80a4\uff0c\u773c\u73e0\u5374\u662f\u78a7\u7eff\uff0c\u5168\u8eab\u73e0\u5149\u5b9d\u6c14\uff0c\u5728\u706f\u4e0b\u707f\u7136\u95ea\u8000\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4ece\u6765\u6ca1\u89c1\u8fc7\u5916\u56fd\u5973\u4eba\uff0c\u4e0d\u514d\u591a\u770b\u4e86\u51e0\u773c\u3002\u9752\u9752\u5374\u4e0d\u9ad8\u5174\u4e86\uff0c\u4f4e\u58f0\u95ee\uff1a\u201c\u4f60\u8bf4\u8fd9\u5973\u5b50\u597d\u770b\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5916\u56fd\u5973\u4eba\u539f\u6765\u8fd9\u4e48\u7231\u6253\u626e\uff01\u201d\u9752\u9752\u54fc\u4e86\u4e00\u58f0\uff0c\u5c31\u4e0d\u8a00\u8bed\u4e86\u3002 \u3000\u3000\u6b21\u65e5\u6e05\u6668\u8d77\u6765\uff0c\u5927\u4f19\u5728\u5927\u5385\u4e0a\u5403\u9762\u70b9\u3002\u4e24\u4e2a\u5916\u56fd\u519b\u5b98\u548c\u90a3\u5973\u4eba\u5750\u5728\u4e00\u684c\u3002\u901a\u8bd1\u94b1\u901a\u56db\u4e0d\u4f4f\u8fc7\u53bb\u8c04\u5a9a\uff0c\u5351\u8eac\u5c48\u819d\uff0c\u6ee1\u8138\u8d54\u7b11\uff0c\u7b49\u56de\u8fc7\u5934\u6765\uff0c\u5374\u5411\u5e97\u4f19\u5927\u58f0\u547c\u559d\uff0c\u8981\u8fd9\u8981\u90a3\uff0c\u7a0d\u4e0d\u5982\u610f\uff0c\u5c31\u662f\u4e00\u8bb0\u5df4\u638c\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u5b9e\u5728\u770b\u4e0d\u8fc7\u773c\u4e86\uff0c\u5bf9\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u6c99\u5144\uff0c\u77a7\u6211\u53d8\u4e2a\u5c0f\u5c0f\u620f\u6cd5\uff01\u201d\u5f53\u4e0b\u4e5f\u4e0d\u56de\u8eab\uff0c\u987a\u624b\u5411\u540e\u4e00\u626c\uff0c\u624b\u4e2d\u7684\u4e00\u53cc\u7af9\u7b77\u98de\u4e86\u51fa\u53bb\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u6b63\u63d2\u5165\u4e86\u94b1\u901a\u56db\u53e3\u91cc\uff0c\u628a\u4ed6\u4e0a\u4e0b\u95e8\u7259\u649e\u5f97\u9669\u4e9b\u513f\u6389\u5c06\u4e0b\u6765\u3002\u8981\u77e5\u7a0b\u9752\u7af9\u6240\u7528\u6697\u5668\u5c31\u662f\u4e00\u679d\u679d\u7ec6\u7af9\uff0c\u8fd9\u95e8\u9752\u7af9\u9556\u7edd\u6280\uff0c\u4e8c\u5341\u6b65\u5185\u6253\u4eba\u7a74\u9053\uff0c\u767e\u53d1\u767e\u4e2d\uff0c\u52b2\u529b\u4e0d\u8f93\u94a2\u9556\u3002\u4e5f\u662f\u4ed6\u542c\u4e86\u8881\u627f\u5fd7\u7684\u8bdd\u8fd9\u624d\u624b\u4e0b\u7559\u60c5\uff0c\u5426\u5219\u8fd9\u53cc\u7b77\u5b50\u7a0d\u9ad8\u6570\u5bf8\uff0c\u94b1\u901a\u56db\u7684\u4e00\u53cc\u773c\u73e0\u5c31\u522b\u60f3\u4fdd\u4f4f\u4e86\u3002 \u3000\u3000\u94b1\u901a\u56db\u75db\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u53ef\u8fd8\u4e0d\u77e5\u7af9\u7b77\u662f\u54ea\u91cc\u98de\u6765\u7684\u3002\u4e24\u4e2a\u5916\u56fd\u519b\u5b98\u53eb\u4ed6\u8fc7\u53bb\u67e5\u95ee\u3002\u94b1\u901a\u56db\u8bf4\u4e86\uff0c\u90a3\u5973\u5b50\u7b11\u5f97\u82b1\u679d\u62db\u5c55\uff0c\u8033\u73af\u6447\u6643\u3002 \u3000\u3000\u5e74\u957f\u7684\u519b\u5b98\u5411\u8881\u627f\u5fd7\u8fd9\u4e00\u684c\u4eba\u671b\u4e86\u51e0\u773c\uff0c\u5fc3\u60f3\u591a\u534a\u662f\u8fd9\u6279\u4eba\u4f5c\u602a\uff0c\u62ff\u8d77\u684c\u4e0a\u4e24\u53ea\u9152\u676f\uff0c\u5ffd\u5f80\u7a7a\u4e2d\u63b7\u53bb\uff0c\u53cc\u624b\u5df2\u5404\u63e1\u4e86\u4e00\u652f\u77ed\u67aa\uff0c\u4e00\u67aa\u4e00\u54cd\uff0c\u628a\u4e24\u53ea\u9152\u676f\u6253\u5f97\u7c89\u788e\u3002\u8881\u627f\u5fd7\u7b49\u542c\u5f97\u5de8\u54cd\uff0c\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u60f3\u8fd9\u706b\u5668\u679c\u7136\u5389\u5bb3\uff0c\u800c\u4ed6\u653e\u67aa\u7684\u51c6\u5934\u4e5f\u81ea\u4e0d\u51e1\u3002 \u3000\u3000\u5e74\u957f\u519b\u5b98\u9762\u6709\u5f97\u8272\uff0c\u4ece\u706b\u836f\u7b52\u4e2d\u53d6\u51fa\u706b\u836f\u94c5\u4e38\uff0c\u88c5\u5165\u77ed\u67aa\uff0c\u5bf9\u5e74\u8f7b\u519b\u5b98\u9053\uff1a\u201c\u5f7c\u5f97\uff0c\u4f60\u4e5f\u8bd5\u8bd5\u4e48\uff1f\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u6211\u7684\u67aa\u6cd5\u600e\u53ca\u5f97\u4e0a\u54b1\u4eec\u8461\u8404\u7259\u56fd\u7b2c\u4e00\u795e\u67aa\u624b\uff1f\u201d\u90a3\u897f\u6d0b\u5973\u4eba\u5fae\u7b11\u9053\uff1a\u201c\u96f7\u8499\u662f\u7b2c\u4e00\u795e\u67aa\u624b\u4e48\uff1f\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4e16\u754c\u7b2c\u4e00\uff0c\u81f3\u5c11\u4e5f\u662f\u6b27\u6d32\u7b2c\u4e00\u3002\u201d\u96f7\u8499\u7b11\u9053\uff1a\u201c\u6b27\u6d32\u7b2c\u4e00\uff0c\u96be\u9053\u4e0d\u662f\u4e16\u754c\u7b2c\u4e00\u4e48\uff1f\u201d \u3000\u3000\u5f7c\u5f97\u9053\uff1a\u201c\u4e1c\u65b9\u4eba\u5f88\u53e4\u602a\uff0c\u4ed6\u4eec\u6709\u8bb8\u591a\u672c\u9886\uff0c\u6bd4\u6b27\u6d32\u4eba\u5389\u5bb3\u5f97\u591a\uff0c\u6240\u4ee5\u6211\u4e0d\u6562\u8bf4\u3002\u82e5\u514b\u7433\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d\u82e5\u514b\u7433\u7b11\u9053\uff1a\u201c\u6211\u60f3\u4f60\u8bf4\u5f97\u5bf9\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b49\u542c\u4e09\u4eba\u53fd\u54e9\u5495\u565c\u7684\u8bf4\u8bdd\uff0c\u81ea\u662f\u534a\u53e5\u4e0d\u61c2\u3002 \u3000\u3000\u96f7\u8499\u89c1\u82e5\u514b\u7433\u5bf9\u5f7c\u5f97\u795e\u6001\u4eb2\u70ed\uff0c\u9887\u6709\u5992\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u4e1c\u65b9\u4eba\u53e4\u602a\u4e48\uff1f\u201d\u53c8\u662f\u4e24\u67aa\u8fde\u53d1\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u7784\u51c6\u4e86\u9752\u9752\u7684\u5934\u5dfe\u3002 \u3000\u3000\u706b\u5149\u4e00\u95ea\uff0c\u9752\u9752\u7684\u5934\u5dfe\u6253\u843d\u5728\u684c\uff0c\u9732\u51fa\u4e86\u4e00\u5934\u5973\u5b50\u7684\u957f\u53d1\u3002\u8881\u627f\u5fd7\u7b49\u9f50\u5403\u4e00\u60ca\u3002\u96f7\u8499\u4e0e\u53e6\u684c\u4e0a\u7684\u8bb8\u591a\u5916\u56fd\u5175\u90fd\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u9752\u9752\u5927\u6012\u7ad9\u8d77\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u957f\u5251\u51fa\u9798\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u5982\u4e00\u52a8\u624b\uff0c\u5bf9\u65b9\u706b\u5668\u5389\u5bb3\uff0c\u53cc\u65b9\u5fc5\u6709\u6b7b\u4f24\u3002\u8fd9\u4e9b\u5916\u56fd\u5175\u662f\u53bb\u6559\u5b98\u5175\u653e\u70ae\u6253\u6ee1\u6e05\u9791\u5b50\u7684\uff0c\u6740\u4e86\u4ed6\u4eec\u4e8e\u56fd\u5bb6\u6709\u635f\uff0c\u8fd8\u662f\u5fcd\u4e00\u4e0b\u5427\u3002\u201d\u5bf9\u9752\u9752\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u7b97\u4e86\u5427\u3002\u201d\u9752\u9752\u5411\u4e09\u4e2a\u5916\u56fd\u4eba\u6012\u76ee\u6a2a\u89c6\uff0c\u53c8\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u82e5\u514b\u7433\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4e2a\u59d1\u5a18\uff0c\u602a\u4e0d\u5f97\u8fd9\u6837\u7f8e\u8c8c\u3002\u201d\u96f7\u8499\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u65e9\u5728\u7559\u5fc3\u4eba\u5bb6\u5c0f\u4f19\u5b50\u7f8e\u4e0d\u7f8e\u5566\u3002\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u5979\u8fd8\u4f1a\u4f7f\u5251\u5462\uff0c\u597d\u50cf\u60f3\u6765\u8ddf\u6211\u4eec\u6253\u4e00\u67b6\u3002\u201d\u96f7\u8499\u9053\uff1a\u201c\u5979\u6765\u65f6\u8c01\u53bb\u62b5\u654c\uff1f\u5f7c\u5f97\uff0c\u54b1\u4fe9\u7684\u5251\u6cd5\u8c01\u597d\u4e9b\uff1f\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u6211\u5e0c\u671b\u6c38\u8fdc\u6ca1\u4eba\u77e5\u9053\u3002\u201d\u96f7\u8499\u8138\u6709\u6012\u8272\uff0c\u95ee\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d\u82e5\u514b\u7433\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u522b\u4e3a\u8fd9\u4e2a\u5435\u5634\u3002\u201d\u62bf\u5634\u7b11\u9053\uff1a\u201c\u4e1c\u65b9\u4eba\u5f88\u795e\u79d8\uff0c\u53ea\u6015\u4f60\u4eec\u8c01\u4e5f\u6253\u4e0d\u8d62\u8fd9\u4e2a\u6f02\u4eae\u5927\u59d1\u5a18\u5462\u3002\u201d \u3000\u3000\u96f7\u8499\u53eb\u9053\uff1a\u201c\u901a\u56db\u94b1\uff0c\u4f60\u8fc7\u6765\uff01\u201d\u94b1\u901a\u56db\u8fde\u5fd9\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u4e0a\u6821\u6709\u751a\u4e48\u5429\u5490\uff1f\u201d\u96f7\u8499\u9053\uff1a\u201c\u4f60\u53bb\u95ee\u90a3\u4e2a\u5927\u59d1\u5a18\uff0c\u662f\u4e0d\u662f\u8981\u8ddf\u6211\u6bd4\u5251\uff1f\u5feb\u53bb\u95ee\u3002\u201d\u94b1\u901a\u56db\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u96f7\u8499\u4ece\u888b\u91cc\u6293\u51fa\u5341\u591a\u5757\u91d1\u6d0b\uff0c\u629b\u5728\u684c\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u5979\u8981\u6bd4\uff0c\u5c31\u8fc7\u6765\u3002\u53ea\u8981\u8d62\u4e86\u6211\uff0c\u8fd9\u4e9b\u91d1\u6d0b\u90fd\u662f\u5979\u7684\u3002\u5979\u8f93\u4e86\uff0c\u6211\u53ef\u8981\u4eb2\u4e00\u4e2a\u5634\uff01\u4f60\u5feb\u53bb\u8bf4\uff0c\u5feb\u53bb\u8bf4\u3002\u201d \u3000\u3000\u94b1\u901a\u56db\u5927\u6a21\u5927\u6837\u7684\u8d70\u4e86\u8fc7\u53bb\uff0c\u7167\u5b9e\u5bf9\u9752\u9752\u8bf4\u4e86\uff0c\u8bf4\u5230\u6700\u540e\u4e00\u53e5\u201c\u4eb2\u4e00\u4e2a\u5634\u201d\u65f6\uff0c\u9752\u9752\u53cd\u624b\u4e00\u638c\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u6b63\u4e2d\u4ed6\u53f3\u988a\u3002\u8fd9\u4e00\u638c\u52b2\u529b\u597d\u5927\uff0c\u94b1\u901a\u56db\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u6ee1\u53e3\u9c9c\u8840\uff0c\u56db\u679a\u5927\u7259\uff0c\u534a\u8fb9\u8138\u988a\u767b\u65f6\u80bf\u4e86\u8d77\u6765\uff0c\u4ece\u6b64\u5634\u91cc\u56db\u901a\u516b\u8fbe\uff0c\u5f53\u771f\u4e0d\u6789\u4e86\u901a\u56db\u4e4b\u540d\u3002 \u3000\u3000\u96f7\u8499\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u679c\u7136\u6709\u70b9\u529b\u6c14\uff01\u201d\u62d4\u51fa\u5251\u6765\uff0c\u5728\u7a7a\u4e2d\u547c\u547c\u547c\u7684\u865a\u5288\u4e86\u51e0\u4e0b\uff0c\u8d70\u5230\u5927\u5385\u4e2d\u95f4\uff0c\u53eb\u9053\uff1a\u201c\u6765\uff0c\u6765\uff0c\u6765\uff01\u201d \u3000\u3000\u9752\u9752\u4e0d\u77e5\u4ed6\u8bf4\u4e9b\u751a\u4e48\uff0c\u4f46\u77a7\u4ed6\u795e\u6c14\uff0c\u663e\u7136\u4fbf\u662f\u8981\u548c\u81ea\u5df1\u6bd4\u5251\uff0c\u5f53\u5373\u62d4\u5251\u51fa\u5ea7\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u4f60\u8fc7\u6765\u3002\u201d\u9752\u9752\u4ee5\u4e3a\u4ed6\u8981\u62e6\u963b\uff0c\u8eab\u5b50\u4e00\u626d\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u6765\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u6559\u4f60\u600e\u6837\u80dc\u4ed6\u3002\u201d\u9752\u9752\u9002\u624d\u773c\u89c1\u90a3\u5916\u56fd\u4eba\u706b\u5668\u5389\u5bb3\u65e0\u6bd4\uff0c\u53ea\u6015\u5251\u6cd5\u4e5f\u662f\u5982\u6b64\u5a01\u529b\u60ca\u4eba\uff0c\u53c8\u6216\u662f\u5251\u4e0a\u4f1a\u653e\u51fa\u4e9b\u751a\u4e48\u9739\u96f3\u58f0\u54cd\u7684\u7269\u4e8b\u6765\uff0c\u672c\u6709\u4e9b\u5bb3\u6015\uff0c\u4e00\u542c\u5927\u559c\uff0c\u5fd9\u8d70\u8fc7\u6765\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u77a7\u4ed6\u521a\u624d\u780d\u5288\u8fd9\u51e0\u4e0b\uff0c\u51fa\u624b\u654f\u6377\uff0c\u52b2\u9053\u4e5f\u8db3\u3002\u4ed6\u8fd9\u5251\u67d4\u4e2d\u5e26\u97e7\uff0c\u8981\u9632\u4ed6\u76f4\u523a\uff0c\u4e0d\u6015\u4ed6\u780d\u524a\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u90a3\u4e48\u6211\u53ef\u8bbe\u6cd5\u9707\u53bb\u4ed6\u5251\uff01\u201d\u8881\u627f\u5fd7\u559c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8fd9\u6837\uff0c\u53ef\u662f\u522b\u4f24\u4e86\u4ed6\u3002\u201d \u3000\u3000\u96f7\u8499\u89c1\u4e24\u4eba\u8c08\u8bba\u4e0d\u4f11\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u6765\uff0c\u5feb\u6765\uff01\u201d \u3000\u3000\u9752\u9752\u53cd\u8eab\u8dc3\u51fa\uff0c\u56de\u624b\u7a81\u7136\u4e00\u5251\uff0c\u5411\u4ed6\u80a9\u5934\u524a\u53bb\u3002\u96f7\u8499\u4e07\u60f3\u4e0d\u5230\u5979\u51fa\u624b\u5982\u6b64\u5feb\u6377\uff0c\u603b\u7b97\u4ed6\u662f\u8461\u8404\u7259\u7684\u5251\u672f\u9ad8\u624b\uff0c\u53c8\u53d7\u8fc7\u6cd5\u56fd\u4e0e\u610f\u5927\u5229\u540d\u5e08\u7684\u6307\u70b9\uff0c\u5371\u6025\u4e2d\u6eda\u5012\u5728\u5730\uff0c\u4e3e\u5251\u4e00\u6321\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u5df2\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002\u82e5\u514b\u7433\u5728\u4e00\u65c1\u62cd\u624b\u53eb\u597d\u3002 \u3000\u3000\u4e24\u4eba\u5c55\u5f00\u5251\u672f\uff0c\u653b\u5b88\u523a\u62d2\uff0c\u6597\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7ec6\u770b\u96f7\u8499\u7684\u5251\u6cd5\uff0c\u89c1\u4ed6\u56de\u6321\u8fdb\u523a\uff0c\u751a\u662f\u5feb\u901f\u3002\u6597\u5230\u9163\u5904\uff0c\u9752\u9752\u5251\u6cd5\u5ffd\u53d8\uff0c\u5168\u662f\u865a\u62db\uff0c\u5251\u5c16\u5373\u5c06\u70b9\u5230\uff0c\u7acb\u5373\u6536\u56de\uff0c\u8fd9\u662f\u77f3\u6881\u6d3e\u7684\u201c\u96f7\u9707\u5251\u6cd5\u201d\uff0c\u516d\u516d\u4e09\u5341\u516d\u62db\uff0c\u7adf\u65e0\u4e00\u62db\u5b9e\u62db\uff0c\u90a3\u662f\u96f7\u9707\u4e4b\u524d\u7684\u95ea\u7535\uff0c\u628a\u654c\u4eba\u5f04\u5f97\u5934\u6655\u773c\u82b1\u4e4b\u540e\uff0c\u8ddf\u7740\u800c\u4e0a\u7684\u4fbf\u662f\u96f7\u8f70\u9739\u96f3\u7684\u731b\u653b\u3002 \u3000\u3000\u96f7\u8499\u5251\u6cd5\u867d\u7136\u9ad8\u660e\uff0c\u4f46\u8fd9\u6837\u7684\u5251\u672f\u5374\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\uff0c\u53ea\u89c1\u5bf9\u65b9\u5251\u5c16\u4e71\u95ea\uff0c\u4f3c\u4e4e\u5251\u5251\u8981\u523a\u5411\u81ea\u5df1\u8981\u5bb3\uff0c\u5f85\u5f97\u4e3e\u5251\u62b5\u6321\uff0c\u5bf9\u65b9\u5374\u53c8\u4e0d\u653b\u8fc7\u6765\u3002\u897f\u65b9\u5251\u672f\u4e4b\u4e2d\u539f\u4e5f\u6709\u4f6f\u653b\u4f2a\u51fb\u7684\u82b1\u62db\uff0c\u4f46\u6700\u591a\u4e00\u4e8c\u62db\u800c\u5df2\uff0c\u51b3\u65e0\u6570\u5341\u62db\u90fd\u662f\u4f6f\u653b\u7684\uff0c\u5fc3\u60f3\u8fd9\u79cd\u82b1\u5de7\u53ea\u56fe\u597d\u770b\uff0c\u6709\u4f55\u7528\u5904\uff1f\u6b63\u8981\u7b11\u9a82\uff0c\u9752\u9752\u7a81\u7136\u6325\u5251\u731b\u5288\u3002\u96f7\u8499\u4e3e\u5251\u6321\u67b6\uff0c\u864e\u53e3\u5927\u9707\uff0c\u7adf\u81ea\u628a\u63e1\u4e0d\u4f4f\uff0c\u957f\u5251\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u9752\u9752\u4e58\u52bf\u76f4\u4e0a\uff0c\u5251\u5c16\u6307\u4f4f\u4ed6\u7684\u80f8\u819b\u3002\u96f7\u8499\u53ea\u5f97\u4e3e\u8d77\u53cc\u624b\uff0c\u4f5c\u6295\u964d\u670d\u8f93\u4e4b\u72b6\u3002\u9752\u9752\u563b\u563b\u4e00\u7b11\uff0c\u6536\u5251\u56de\u5ea7\u3002\u96f7\u8499\u6ee1\u8138\u7f9e\u60ed\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u5728\u6b27\u9646\u7eb5\u6a2a\u65e0\u654c\uff0c\u7adf\u4f1a\u5230\u4e2d\u56fd\u6765\u8d25\u5728\u4e00\u4e2a\u5c11\u5973\u624b\u91cc\u3002 \u3000\u3000\u82e5\u514b\u7433\u7b11\u541f\u541f\u7684\u62ff\u8d77\u684c\u4e0a\u90a3\u53e0\u91d1\u5e01\uff0c\u8d70\u8fc7\u6765\u4ea4\u7ed9\u9752\u9752\u3002\u9752\u9752\u6447\u624b\u4e0d\u8981\u3002\u82e5\u514b\u7433\u4e00\u9762\u7b11\uff0c\u4e00\u9762\u54ad\u54ad\u54af\u54af\u7684\u5927\u8bf4\u8461\u8bed\uff0c\u5b9a\u8981\u7ed9\u5979\u3002\u7a0b\u9752\u7af9\u4f38\u624b\u63a5\u8fc7\uff0c\u5c06\u5341\u591a\u5757\u91d1\u6d0b\u53e0\u6210\u4e00\u53e0\uff0c\u53cc\u638c\u7528\u529b\u5728\u4e24\u7aef\u62b5\u4f4f\uff0c\u8fd0\u8d77\u5185\u529b\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5c06\u91d1\u5e01\u8fd8\u7ed9\u82e5\u514b\u7433\u3002\u82e5\u514b\u7433\u63a5\u4e86\u8fc7\u6765\uff0c\u60f3\u518d\u4ea4\u7ed9\u9752\u9752\uff0c\u4e00\u62ff\u4e0a\u624b\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\uff0c\u539f\u6765\u5341\u591a\u679a\u91d1\u5e01\u5df2\u4e92\u76f8\u7c98\u4f4f\uff0c\u7ed3\u6210\u4e00\u6761\u5706\u67f1\uff0c\u7adf\u7136\u62c9\u4e0d\u5f00\u6765\uff0c\u4e0d\u7981\u7741\u5927\u4e86\u5706\u5706\u7684\u773c\u775b\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4e1c\u65b9\u4eba\u771f\u662f\u795e\u79d8\uff0c\u771f\u662f\u795e\u79d8\uff01\u201d\u56de\u53bb\u628a\u91d1\u67f1\u7ed9\u4e24\u4e2a\u519b\u5b98\u770b\u3002\u96f7\u8499\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u6709\u9b54\u672f\uff01\u201d \u3000\u3000\u5f7c\u5f97\u9053\uff1a\u201c\u522b\u60f9\u4ed6\u4eec\u5566\uff01\u8d70\u5427\uff01\u201d\u4e24\u4eba\u4f20\u4e0b\u53f7\u4ee4\uff0c\u4e0d\u4e00\u4f1a\u53ea\u542c\u5f97\u95e8\u5916\u8f66\u58f0\u9686\u9686\uff0c\u62d6\u52a8\u5927\u70ae\u800c\u53bb\u3002\u96f7\u8499\u548c\u5f7c\u5f97\u4e5f\u7ad9\u8d77\u8eab\u6765\uff0c\u8d70\u51fa\u5e97\u53bb\u3002\u82e5\u514b\u7433\u8d70\u8fc7\u9752\u9752\u8eab\u8fb9\u65f6\uff0c\u5411\u5979\u5ae3\u7136\u4e00\u7b11\uff0c\u5e26\u7740\u4e00\u9635\u6d53\u90c1\u7684\u9999\u98ce\uff0c\u73af\u73ee\u53ee\u5679\uff0c\u51fa\u5e97\u53bb\u4e86\u3002 \u3000\u3000\u94c1\u7f57\u6c49\u9053\uff1a\u201c\u7ea2\u5937\u5927\u70ae\u5230\u5e95\u662f\u600e\u4e48\u6837\u5b50\uff1f\u6211\u4ece\u6765\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u80e1\u6842\u5357\u9053\uff1a\u201c\u54b1\u4eec\u53bb\u77a7\u77a7\u3002\u201d\u6c99\u5929\u5e7f\u7b11\u9053\uff1a\u201c\u80e1\u5144\uff0c\u8981\u662f\u4f60\u80fd\u5999\u624b\u7a7a\u7a7a\uff0c\u5077\u4e00\u5c0a\u5927\u70ae\u6765\uff0c\u90a3\u6211\u5c31\u4f69\u670d\u4f60\u4e86\u3002\u201d\u80e1\u6842\u5357\u7b11\u9053\uff1a\u201c\u5927\u70ae\u8fd9\u7b28\u5bb6\u4f19\u5012\u771f\u6ca1\u5077\u8fc7\u3002\u54b1\u4eec\u8981\u4e0d\u8981\u6253\u4e2a\u8d4c\uff1f\u201d\u6c99\u5929\u5e7f\u7b11\u9053\uff1a\u201c\u5927\u70ae\u662f\u62ff\u53bb\u6253\u6ee1\u6e05\u9791\u5b50\u7684\uff0c\u53ef\u5077\u4e0d\u5f97\uff0c\u5426\u5219\u6211\u771f\u8981\u8ddf\u4f60\u8d4c\u4e0a\u4e00\u8d4c\u3002\u201d\u4f17\u4eba\u5728\u7b11\u8bed\u58f0\u4e2d\u51fa\u5e97\u3002\u4e0d\u4e00\u523b\uff0c\u5df2\u8ffd\u8fc7\u62bc\u8fd0\u5927\u70ae\u7684\u519b\u961f\u3002\u89c1\u5927\u70ae\u5171\u6709\u5341\u5c0a\uff0c\u679c\u662f\u5e9e\u7136\u5927\u7269\uff0c\u5355\u89c2\u5176\u5f62\uff0c\u5df2\u662f\u5a01\u98ce\u51db\u51db\uff0c\u6bcf\u5c0a\u70ae\u7528\u516b\u5339\u9a6c\u62d6\u62c9\uff0c\u540e\u9762\u53c8\u6709\u592b\u5f79\u63a8\u9001\uff0c\u70ae\u8f66\u8fc7\u53bb\uff0c\u8def\u4e0a\u538b\u51fa\u4e86\u4e24\u6761\u6df1\u6c9f\u3002 \u3000\u3000\u7fa4\u96c4\u9a70\u51fa\u4e8c\u5341\u4f59\u91cc\uff0c\u5ffd\u542c\u524d\u9762\u9e3e\u94c3\u54cd\u5904\uff0c\u5341\u591a\u9a91\u8fce\u9762\u5954\u6765\u3002\u5f85\u5230\u4e34\u8fd1\uff0c\u89c1\u9a6c\u4e0a\u4e58\u8005\u8d1f\u5f13\u6301\u7bad\uff0c\u9a6c\u4e0a\u6302\u6ee1\u7350\u5154\u4e4b\u7c7b\u7684\u91ce\u5473\uff0c\u5374\u662f\u51fa\u6765\u6253\u730e\u7684\u3002\u8fd9\u4e9b\u4eba\u8863\u9970\u534e\u8d35\uff0c\u90fd\u662f\u7f0e\u888d\u76ae\u9774\uff0c\u6c14\u6d3e\u751a\u5927\uff0c\u73af\u62e5\u7740\u4e00\u4e2a\u97f6\u9f84\u5c11\u5973\u3002 \u3000\u3000\u90a3\u5c11\u5973\u89c1\u4e86\u8881\u627f\u5fd7\u7b49\u4eba\uff0c\u62cd\u9a6c\u8fce\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff01\u201d \u3000\u3000\u7a0b\u9752\u7af9\u7b11\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u4e5f\u6765\u5566\uff01\u201d\u539f\u6765\u90a3\u5c11\u5973\u4fbf\u662f\u4ed6\u7684\u5973\u5f92\u963f\u4e5d\u3002\u4f17\u4eba\u5728\u52ab\u94c1\u7bb1\u65f6\u66fe\u548c\u5979\u4f1a\u8fc7\u3002\u5979\u4e0a\u6b21\u7a7f\u4e00\u4ef6\u9752\u5e03\u8863\u886b\uff0c\u4f3c\u4e2a\u4e61\u4e0b\u59d1\u5a18\uff0c\u8fd9\u65f6\u5374\u6253\u626e\u5f97\u660e\u8273\u65e0\u4f26\uff0c\u5de6\u8033\u4e0a\u6234\u7740\u4e00\u7c92\u62c7\u6307\u5927\u7684\u73cd\u73e0\uff0c\u8863\u895f\u4e0a\u4e00\u9897\u5927\u7ea2\u5b9d\u77f3\uff0c\u95ea\u95ea\u751f\u5149\u3002\u963f\u4e5d\u89c1\u4e86\u8881\u627f\u5fd7\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u5e08\u7236\u5728\u4e00\u8d77\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u7740\u70b9\u70b9\u5934\u3002\u963f\u4e5d\u5411\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u6c99\u5be8\u4e3b\uff0c\u54b1\u4eec\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff01\u201d \u3000\u3000\u7a0b\u9752\u7af9\u53eb\u5979\u89c1\u8fc7\u4e86\u80e1\u6842\u5357\u3001\u94c1\u7f57\u6c49\u7b49\u4eba\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u963f\u4e5d\u9053\uff1a\u201c\u51fa\u6765\u6253\u730e\uff0c\u77a7\u6211\u8d70\u5f97\u8fdc\u4e0d\u8fdc\uff1f\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6211\u4eec\u6b63\u8981\u4e0a\u4eac\uff0c\u4f60\u8ddf\u6211\u4eec\u4e00\u8d77\u53bb\u5427\uff01\u201d\u963f\u4e5d\u5f88\u662f\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff01\u201d\u508d\u5728\u5e08\u7236\u8eab\u8fb9\uff0c\u5e76\u9a6c\u800c\u884c\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u89c1\u5979\u867d\u7136\u5e7c\u5c0f\uff0c\u4f46\u81ea\u6709\u4e00\u80a1\u9890\u6307\u6c14\u4f7f\u7684\u52bf\u6d3e\uff0c\u4e3e\u6b62\u4e4b\u95f4\uff0c\u6c14\u5ea6\u9ad8\u534e\uff0c\u5fc3\u4e2d\u4e0d\u7981\u7eb3\u95f7\uff0c\u5f53\u65e5\u5c71\u4e1c\u9053\u4e0a\u521d\u9047\uff0c\u672c\u4ee5\u4e3a\u5979\u662f\u7a0b\u9752\u7af9\u7684\u5b59\u5973\uff0c\u540e\u6765\u624d\u77e5\u662f\u5f92\u5f1f\u3002\u8fd9\u65f6\u770b\u6765\uff0c\u7adf\u662f\u4e00\u4f4d\u8c6a\u95e8\u5de8\u5ba4\u7684\u5a07\u5973\uff0c\u51fa\u6765\u6253\u730e\uff0c\u7adf\u5e26\u4e86\u8fd9\u8bb8\u591a\u4ece\u4eba\uff0c\u4e5f\u4e0d\u77e5\u5982\u4f55\u4f1a\u62dc\u7a0b\u9752\u7af9\u4e3a\u5e08\uff0c\u53c8\u6df7\u5728\u9752\u7af9\u5e2e\u4e2d\uff0c\u5012\u771f\u5947\u4e86\u3002 \u3000\u3000\u5f53\u665a\u5728\u996e\u9a6c\u96c6\u6295\u5e97\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u89c1\u963f\u4e5d\u7684\u4ece\u4eba\u8bf4\u8bdd\u90fd\u5e26\u5b98\u8154\uff0c\u9664\u4e86\u5bf9\u963f\u4e5d\u5341\u5206\u606d\u8c28\u4e4b\u5916\uff0c\u5bf9\u65c1\u4eba\u8c01\u4e5f\u4e0d\u7406\uff0c\u795e\u6001\u5028\u50b2\uff0c\u5355\u72ec\u770b\u6765\uff0c\u4e00\u4e2a\u4e2a\u7adf\u662f\u5b98\u5ba6\uff0c\u54ea\u91cc\u50cf\u662f\u4ece\u4ec6\uff0c\u5fc3\u4e0b\u66f4\u5947\u3002\u9752\u9752\u95ee\u963f\u4e5d\u9053\uff1a\u201c\u4e5d\u59b9\u59b9\uff0c\u90a3\u65e5\u54b1\u4eec\u5927\u6740\u5b98\u5175\uff0c\u6253\u5f97\u597d\u75db\u5feb\uff0c\u540e\u6765\u5ffd\u7136\u4e0d\u89c1\u4e86\u4f60\u3002\u6211\u8001\u662f\u7275\u8bb0\uff0c\u4f60\u5230\u54ea\u91cc\u53bb\u4e86\u554a\uff1f\u201d\u963f\u4e5d\u8138\u4e00\u7ea2\uff0c\u5514\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9752\u59ca\uff0c\u4f60\u8981\u662f\u6253\u626e\u8d77\u6765\uff0c\u90a3\u624d\u7f8e\u5462\uff01\u201d\u7adf\u662f\u987e\u5de6\u53f3\u800c\u8a00\u4ed6\u3002\u9752\u9752\u5f85\u8981\u8ffd\u95ee\uff0c\u7a0b\u9752\u7af9\u5ffd\u5728\u5bf9\u9762\u8fde\u4f7f\u773c\u8272\u3002\u9752\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u9053\u4e0a\u8d70\uff0c\u6ee1\u5934\u6ee1\u8138\u7684\u7070\u571f\uff0c\u6253\u626e\u7ed9\u8c01\u770b\u554a\uff1f\u201d\u5404\u4eba\u95f2\u8c08\u4e86\u4e00\u4f1a\uff0c\u5206\u522b\u5b89\u5bdd\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6b63\u8981\u4e0a\u5e8a\uff0c\u7a0b\u9752\u7af9\u8d70\u8fdb\u623f\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u6709\u4e00\u4ef6\u4e8b\u60f3\u8ddf\u4f60\u8bf4\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u5750\uff01\u201d\u7a0b\u9752\u7af9\u4f4e\u58f0\u9053\uff1a\u201c\u8fd8\u662f\u5230\u5916\u9762\u7a7a\u65f7\u4e4b\u5730\u8bf4\u7684\u597d\u3002\u201d\u8881\u627f\u5fd7\u77e5\u662f\u673a\u5bc6\u4e4b\u4e8b\uff0c\u4e8e\u662f\u91cd\u884c\u7a7f\u4e0a\u957f\u8863\uff0c\u51fa\u4e86\u5ba2\u5e97\uff0c\u6765\u5230\u9547\u5916\u4e00\u4e2a\u5c0f\u5c71\u5c97\u4e0a\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u89c1\u56db\u4e0b\u65e0\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u6211\u8fd9\u5973\u5f92\u5f1f\u963f\u4e5d\u6765\u5386\u5f88\u662f\u5947\u7279\u3002\u5979\u4e8e\u6211\u66fe\u6709\u5927\u6069\uff0c\u62dc\u5e08\u4e4b\u65f6\uff0c\u6211\u66fe\u7b54\u5e94\u8fc7\uff0c\u51b3\u4e0d\u6cc4\u9732\u5979\u7684\u8eab\u4efd\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u4e5f\u77a7\u5979\u5e76\u4e0d\u5bfb\u5e38\u3002\u4f60\u65e2\u7b54\u5e94\u8fc7\u5979\uff0c\u5c31\u4e0d\u7528\u5bf9\u6211\u8bf4\u4e86\u3002\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u5979\u624b\u4e0b\u6240\u5e26\u7684\u90fd\u662f\u5b98\u5e9c\u4e2d\u4eba\uff0c\u56e0\u6b64\u54b1\u4eec\u7684\u56fe\u8c0b\uff0c\u51b3\u4e0d\u53ef\u5728\u4ed6\u4eec\u9762\u524d\u6f0f\u4e86\u53e3\u98ce\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u70b9\u5934\u9053\uff1a\u201c\u539f\u6765\u679c\u7136\u662f\u5b98\u5e9c\u4e2d\u4eba\u3002\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6599\u60f3\u8fd9\u5973\u5f92\u662f\u51b3\u4e0d\u81f4\u5356\u6211\u7684\uff0c\u4f46\u5979\u5e74\u7eaa\u5c0f\uff0c\u4e16\u4e8b\u7ec8\u7a76\u96be\u6599\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u5728\u5979\u8ddf\u524d\u7279\u522b\u7559\u795e\u5c31\u662f\u4e86\u3002\u201d\u4e24\u4eba\u4e09\u8a00\u4e24\u8bed\u5c31\u8bf4\u5b8c\u4e86\uff0c\u4e0b\u5c97\u56de\u5e97\u3002 \u3000\u3000\u6765\u5230\u5ba2\u5e97\u95e8\u53e3\uff0c\u53ea\u89c1\u4e00\u4e2a\u6c49\u5b50\u4ece\u4e1c\u8fb9\u5927\u8857\u4e0a\u8fc7\u6765\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u76cf\u706f\u7b3c\uff0c\u95ea\u8eab\u8fdb\u5e97\u3002\u5fae\u5149\u4e4b\u4e0b\uff0c\u8881\u627f\u5fd7\u89c1\u90a3\u6c49\u5b50\u6709\u4e9b\u773c\u719f\uff0c\u53ef\u662f\u4e00\u65f6\u60f3\u4e0d\u8d77\u5728\u54ea\u91cc\u89c1\u8fc7\u3002\u7761\u5728\u5e8a\u4e0a\uff0c\u4e00\u8def\u5f80\u56de\u63a8\u6eaf\uff0c\u7ec6\u60f3\u5728\u5b5f\u5bb6\u5e84\u5bff\u7b75\u3001\u5728\u6cf0\u5c71\u5927\u4f1a\u3001\u5728\u5357\u4eac\u3001\u5728\u8862\u5dde\u77f3\u6881\u3001\u5728\u95ef\u738b\u519b\u4e2d\uff0c\u90fd\u6ca1\u89c1\u8fc7\u8fd9\u4eba\uff0c\u7136\u800c\u4ee5\u524d\u4e00\u5b9a\u4f1a\u8fc7\uff0c\u6b64\u4eba\u5230\u5e95\u662f\u8c01\uff1f \u3000\u3000\u6b63\u81ea\u601d\u7d22\uff0c\u5ffd\u7136\u95e8\u4e0a\u6709\u8f7b\u8f7b\u5265\u5544\u4e4b\u58f0\uff0c\u4fbf\u62ab\u8863\u4e0b\u5e8a\uff0c\u95ee\u9053\uff1a\u201c\u8c01\u5440\uff1f\u201d\u95e8\u5916\u9752\u9752\u7b11\u9053\uff1a\u201c\u8981\u4e0d\u8981\u5403\u4e1c\u897f\uff1f\u201d\u8881\u627f\u5fd7\u70b9\u706f\u5f00\u95e8\uff0c\u89c1\u5979\u6258\u7740\u4e00\u53ea\u76d8\u5b50\uff0c\u88c5\u7740\u4e24\u53ea\u7897\uff0c\u6bcf\u7897\u5404\u6709\u4e09\u4e2a\u9e21\u86cb\uff0c\u60f3\u662f\u521a\u624d\u4e0b\u53a8\u505a\u7684\u3002\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4e86\uff0c\u8fd9\u4e48\u665a\u4e86\uff0c\u600e\u4e48\u8fd8\u4e0d\u7761\uff1f\u201d \u3000\u3000\u9752\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u60f3\u7740\u90a3\u963f\u4e5d\u5f88\u53e4\u602a\uff0c\u7761\u4e0d\u7740\u3002\u77e5\u9053\u4f60\u4e5f\u5728\u60f3\u5979\uff0c\u4e5f\u4e00\u5b9a\u7761\u4e0d\u7740\u3002\u201d\u8bf4\u7740\u6d45\u6d45\u4e00\u7b11\u3002\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u6211\u60f3\u5979\u5e72\u4e48\uff1f\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u60f3\u5979\u5f88\u7f8e\u554a\uff0c\u4f60\u8bf4\u5979\u7f8e\u4e0d\u7f8e\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u77e5\u5979\u5f88\u5c0f\u6027\u513f\uff0c\u5982\u8bf4\u963f\u4e5d\u7f8e\uff0c\u5b9a\u8981\u4e0d\u9ad8\u5174\uff0c\u8bf4\u963f\u4e5d\u4e0d\u7f8e\u5427\uff0c\u53c8\u662f\u660e\u660e\u6492\u8c0e\uff0c\u5979\u4e5f\u4e0d\u4fe1\uff0c\u62ff\u5319\u7fb9\u6284\u4e86\u4e2a\u9e21\u86cb\uff0c\u54ac\u4e86\u4e00\u53e3\uff0c\u7a81\u7136\u628a\u5319\u7fb9\u4e00\u63b7\uff0c\u53eb\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u539f\u6765\u662f\u4ed6\u3002\u201d \u3000\u3000\u9752\u9752\u5413\u4e86\u4e00\u8df3\uff0c\u95ee\u9053\uff1a\u201c\u751a\u4e48\u662f\u4ed6\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u56de\u5934\u518d\u8bf4\uff0c\u5feb\u8ddf\u6211\u51fa\u53bb\u3002\u201d\u9752\u9752\u89c1\u4ed6\u4e0d\u5403\u9e21\u86cb\uff0c\u4fbf\u6709\u4e9b\u7740\u607c\uff0c\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u8881\u627f\u5fd7\u4ece\u6d2a\u80dc\u6d77\u8eab\u65c1\u62ff\u4e86\u4e00\u67c4\u5251\uff0c\u4ea4\u7ed9\u5979\u9053\uff1a\u201c\u62ff\u7740\u3002\u201d\u9752\u9752\u63a5\u4f4f\uff0c\u624d\u77e5\u662f\u8981\u53bb\u4f1a\u654c\u3002 \u3000\u3000\u539f\u6765\u8881\u627f\u5fd7\u4e00\u5403\u5230\u9e21\u86cb\uff0c\u5ffd\u7136\u60f3\u8d77\u5f53\u5e74\u5728\u5b89\u5927\u5a18\u5bb6\u91cc\uff0c\u9526\u8863\u536b\u80e1\u8001\u4e09\u6765\u62a2\u5c0f\u6167\uff0c\u4ed6\u62da\u547d\u62b5\u6297\uff0c\u5e78\u5f97\u5b89\u5927\u5a18\u53ca\u65f6\u8d76\u56de\uff0c\u7528\u9e21\u86cb\u51fb\u6253\u80e1\u8001\u4e09\uff0c\u624d\u5c06\u4ed6\u8d76\u8dd1\u3002\u521a\u624d\u89c1\u5230\u7684\u5c31\u662f\u90a3\u4e2a\u80e1\u8001\u4e09\u4e86\uff0c\u4e0d\u77e5\u4ed6\u9b3c\u9b3c\u795f\u795f\u7684\u6765\u5e72\u751a\u4e48\uff0c\u53ef\u987b\u5f97\u63a2\u4e2a\u660e\u767d\u3002 \u3000\u3000\u4e24\u4eba\u77ee\u7740\u8eab\u5b50\uff0c\u5230\u6bcf\u95f4\u5e97\u623f\u4e0b\u4fa7\u8033\u503e\u542c\uff0c\u6765\u5230\u4e00\u95f4\u5927\u623f\u540e\u9762\uff0c\u679c\u7136\u542c\u5230\u6709\u4eba\u5728\u8c08\u8bba\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4eba\u9053\uff1a\u201c\u8fd9\u91cc\u600e\u4e48\u8d70\u5f97\u5f00\uff1f\u8981\u662f\u51fa\u4e86\u70b9\u513f\u4e71\u5b50\uff0c\u54e5\u513f\u4eec\u8fd8\u6709\u547d\u4e48\uff1f\u201d\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u5b89\u5927\u4eba\u8fd9\u4ef6\u4e8b\u4e5f\u5f88\u8981\u7d27\u554a\u3002\u773c\u524d\u6446\u7740\u4e00\u4ef6\u5947\u529f\uff0c\u767d\u767d\u653e\u8fc7\u4e86\uff0c\u5c82\u4e0d\u53ef\u60dc\uff1f\u201d\u4f17\u4eba\u6c89\u541f\u4e86\u4e00\u4f1a\u3002 \u3000\u3000\u4e00\u4e2a\u58f0\u97f3\u7c97\u6c89\u7684\u4eba\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u54b1\u4eec\u4e00\u534a\u4eba\u7559\u5728\u8fd9\u91cc\uff0c\u5206\u4e00\u534a\u4eba\u53bb\u542c\u5b89\u5927\u4eba\u8c03\u6d3e\u3002\u8981\u662f\u7acb\u4e86\u529f\u52b3\uff0c\u5374\u662f\u5927\u5bb6\u6709\u4efd\u3002\u201d\u7b2c\u4e00\u4e2a\u4eba\u624b\u638c\u5728\u5927\u817f\u4e0a\u4e00\u62cd\uff0c\u5927\u58f0\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u6709\u798f\u5171\u4eab\uff0c\u6709\u7978\u540c\u5f53\u3002\u8981\u662f\u51fa\u4e86\u4e8b\uff0c\u4e5f\u662f\u5927\u4f19\u513f\u4e00\u9f50\u9876\u3002\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u5927\u5bb6\u6765\u62c8\u9604\uff0c\u8c01\u53bb\u8c01\u7559\uff0c\u81ea\u5df1\u62c8\u7684\u6ca1\u8bdd\u8bf4\u3002\u201d\u4f17\u4eba\u9f50\u58f0\u9644\u548c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u5728\u8fd9\u91cc\u6709\u751a\u4e48\u5927\u4e8b\u8d70\u4e0d\u5f00\uff1f\u53c8\u6709\u751a\u4e48\u5b89\u5927\u4eba\u548c\u5947\u529f\uff0c\u8fd9\u5012\u602a\u4e86\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u542c\u5230\u5200\u5251\u8f7b\u8f7b\u78b0\u649e\u4e4b\u58f0\uff0c\u60f3\u662f\u62c8\u9604\u5df2\u6bd5\uff0c\u4fbf\u8981\u51fa\u6765\u3002\u8881\u627f\u5fd7\u5728\u9752\u9752\u8033\u8fb9\u4f4e\u8bed\uff1a\u201c\u4f60\u53eb\u6c99\u5929\u5e7f\u4ed6\u4eec\u9632\u5907\uff0c\u6211\u8ddf\u7740\u53bb\u77a7\u77a7\u3002\u201d\u9752\u9752\u70b9\u70b9\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u623f\u95e8\u5440\u7684\u4e00\u58f0\u6253\u5f00\uff0c\u623f\u4e2d\u70db\u5149\u4ece\u95e8\u53e3\u7167\u5c04\u51fa\u6765\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u8eb2\u5728\u6697\u5904\uff0c\u89c1\u7b2c\u4e00\u4e2a\u51fa\u6765\u7684\u6b63\u662f\u80e1\u8001\u4e09\uff0c\u540e\u9762\u8ddf\u7740\u516b\u540d\u624b\u6301\u5175\u5203\u4e4b\u4eba\uff0c\u70db\u5149\u4e0b\u770b\u5f97\u660e\u767d\uff0c\u5374\u90fd\u662f\u963f\u4e5d\u7684\u4ece\u4eba\u3002\u4e5d\u4eba\u4e00\u4e00\u8d8a\u5899\u800c\u51fa\u3002\u9752\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u554a\uff0c\u662f\u4ed6\u4eec\uff01\u6211\u65e9\u77e5\u8fd9\u5973\u5a03\u5b50\u4e0d\u662f\u597d\u4eba\u3002\u201d\u8881\u627f\u5fd7\u4e5f\u611f\u5947\u602a\uff0c\u5fc3\u60f3\u4e14\u6162\u5b9a\u8bba\uff0c\u8ddf\u7740\u53bb\u770b\u4e2a\u660e\u767d\u518d\u8bf4\uff0c\u5f53\u4e0b\u8d8a\u5899\u51fa\u5e97\uff0c\u6084\u6084\u8ddf\u5728\u4e5d\u4eba\u4e4b\u540e\u3002 \u3000\u3000\u90a3\u4e5d\u4eba\u5168\u4e0d\u77e5\u6709\u4eba\u8ddf\u8e2a\uff0c\u51fa\u5e02\u9547\u884c\u5f97\u91cc\u8bb8\uff0c\u4fbf\u8d70\u5411\u4e00\u5ea7\u5927\u5c4b\u3002\u80e1\u8001\u4e09\u4e00\u53eb\u95e8\uff0c\u5927\u95e8\u968f\u5373\u6253\u5f00\uff0c\u628a\u4e5d\u4eba\u653e\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7ed5\u5230\u540e\u95e8\uff0c\u8d8a\u5899\u5165\u5185\uff0c\u8d70\u5411\u7a97\u4e2d\u900f\u51fa\u706f\u5149\u7684\u4e00\u95f4\u53a2\u623f\uff0c\u8dc3\u4e0a\u5c4b\u9876\uff0c\u8f7b\u8f7b\u63ed\u5f00\u74e6\u7247\uff0c\u671b\u5c06\u4e0b\u53bb\uff0c\u53ea\u89c1\u623f\u4e2d\u5750\u7740\u4e00\u4e2a\u5e74\u8fd1\u4e94\u5341\u7684\u6c49\u5b50\uff0c\u8eab\u6750\u9ad8\u5927\u3002\u80e1\u8001\u4e09\u4e0e\u963f\u4e5d\u7684\u516b\u540d\u4ece\u4eba\u9c7c\u8d2f\u5165\u623f\uff0c\u5411\u90a3\u4eba\u884c\u793c\u53c2\u89c1\u3002\u53ea\u542c\u80e1\u8001\u4e09\u9053\uff1a\u201c\u5c0f\u7684\u5728\u9547\u4e0a\u649e\u89c1\u738b\u526f\u6307\u6325\uff0c\u77e5\u9053\u4ed6\u4eec\u51d1\u5de7\u5728\u8fd9\u91cc\uff0c\u56e0\u6b64\u4e0a\u9080\u4e86\u8fd9\u51e0\u4f4d\u6765\u505a\u5e2e\u624b\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u597d\u6781\u4e86\uff01\u738b\u526f\u6307\u6325\u600e\u4e48\u8bf4\uff1f\u201d\u4e00\u4eba\u9053\uff1a\u201c\u738b\u526f\u6307\u6325\u8bf4\uff0c\u65e2\u7136\u5b89\u5927\u4eba\u6709\u4e8b\uff0c\u5f53\u5f97\u6548\u52b3\uff01\u201d\u90a3\u5b89\u5927\u4eba\u9053\uff1a\u201c\u8fd9\u6b21\u8981\u662f\u5f97\u624b\uff0c\u5927\u4f19\u513f\u8fd9\u4ef6\u529f\u52b3\u53ef\u4e0d\u5c0f\u554a\uff0c\u54c8\u54c8\uff01\u201d\u4e00\u4eba\u9053\uff1a\u201c\u5168\u51ed\u5927\u4eba\u683d\u57f9\u3002\u201d\u5b89\u5927\u4eba\u9053\uff1a\u201c\u54b1\u4eec\u54e5\u513f\u53ef\u522b\u5206\u8c01\u662f\u5185\u5ef7\u4f8d\u536b\uff0c\u8c01\u662f\u9526\u8863\u536b\u7684\uff0c\u5927\u4f19\u513f\u90fd\u662f\u4e3a\u7687\u4e0a\u51fa\u529b\uff01\u201d\u4f17\u4eba\u9053\uff1a\u201c\u5b89\u5927\u4eba\u8bf4\u5f97\u662f\uff0c\u5168\u51ed\u60a8\u8001\u5429\u5490\u3002\u201d\u5b89\u5927\u4eba\u9053\uff1a\u201c\u597d\u554a\uff01\u8d70\u5427\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u66f4\u662f\u60ca\u5947\uff0c\u5fc3\u60f3\uff1a\u201c\u80e1\u8001\u4e09\u548c\u5b89\u5927\u4eba\u4e00\u4f19\u662f\u9526\u8863\u536b\uff0c\u963f\u4e5d\u90a3\u4e9b\u968f\u4ece\u7adf\u662f\u5185\u5ef7\u4f8d\u536b\u3002\u963f\u4e5d\u8fd9\u5c0f\u59d1\u5a18\u5230\u5e95\u5e72\u751a\u4e48\u7684\uff0c\u600e\u5730\u5e26\u4e86\u4e00\u6279\u5185\u5ef7\u4f8d\u536b\u5230\u5904\u4e71\u8d70\uff1f\u201d \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u5b89\u5927\u4eba\u7387\u9886\u4f17\u4eba\u8d70\u51fa\u3002\u8881\u627f\u5fd7\u4f0f\u5728\u5c4b\u9876\u70b9\u6570\uff0c\u89c1\u5171\u6709\u4e00\u5341\u516d\u4eba\uff0c\u77e5\u9053\u5b89\u5927\u4eba\u81ea\u5df1\u5e26\u7740\u516d\u4eba\uff0c\u7b49\u4f17\u4eba\u8d70\u8fdc\uff0c\u53c8\u6084\u6084\u8ddf\u5728\u540e\u9762\u3002\u8fd9\u6279\u4eba\u8d8a\u8d70\u8d8a\u8352\u50fb\uff0c\u8d70\u4e86\u4e03\u516b\u91cc\u8def\uff0c\u6709\u4eba\u8f7b\u8f7b\u4f4e\u8bed\u4e86\u51e0\u58f0\uff0c\u5927\u4f19\u513f\u5ffd\u7136\u6563\u5f00\uff0c\u56f4\u4f4f\u4e86\u4e00\u6240\u5b64\u96f6\u96f6\u7684\u623f\u5b50\uff0c\u5404\u4eba\u77ee\u4e86\u8eab\u5b50\uff0c\u6084\u6ca1\u58f0\u7684\u903c\u8fd1\u3002\u8881\u627f\u5fd7\u5b66\u4ed6\u4eec\u7684\u6837\uff0c\u4e5f\u8fd9\u822c\u4fef\u8eab\u8d70\u5c06\u8fc7\u53bb\u3002\u6709\u4eba\u9ed1\u6697\u4e2d\u89c1\u5230\u4ed6\u4eba\u5f71\uff0c\u53ea\u9053\u662f\u540c\u4f19\uff0c\u4e5f\u4e0d\u5728\u610f\u3002\u5b89\u5927\u4eba\u89c1\u5305\u56f4\u4e4b\u52bf\u5df2\u6210\uff0c\u6325\u624b\u547d\u4f17\u4eba\u4f0f\u4f4e\uff0c\u4f38\u624b\u6572\u95e8\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5c4b\u4e2d\u4e00\u4e2a\u5973\u4eba\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u8c01\u554a\uff1f\u201d\u5b89\u5927\u4eba\u4e00\u5446\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u5973\u4eba\u58f0\u97f3\u60ca\u9053\uff1a\u201c\u554a\uff0c\u662f\u2026\u2026\u662f\u2026\u2026\u662f\u4f60\uff0c\u6df1\u66f4\u534a\u591c\u6765\u5e72\u4e48\uff1f\u201d\u5b89\u5927\u4eba\u53eb\u9053\uff1a\u201c\u771f\u53eb\u505a\u4e0d\u662f\u51a4\u5bb6\u4e0d\u805a\u5934\u4e86\u3002\u539f\u6765\u4f60\u5728\u8fd9\u91cc\uff0c\u5feb\u5f00\u95e8\u5427\uff01\u201d\u58f0\u97f3\u4e2d\u663e\u5f97\u53c8\u60ca\u53c8\u559c\u3002\u90a3\u5973\u4eba\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4e0d\u518d\u89c1\u4f60\uff0c\u53c8\u6765\u5e72\u751a\u4e48\u4e86\uff1f\u201d\u5b89\u5927\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u89c1\u6211\uff0c\u6211\u5374\u60f3\u5ff5\u6211\u7684\u5a18\u5b50\u5462\uff01\u201d\u90a3\u5973\u4eba\u6012\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5a18\u5b50\uff1f\u54b1\u4eec\u65e9\u5df2\u4e00\u5200\u4e24\u65ad\uff01\u4f60\u8981\u662f\u653e\u4e0d\u8fc7\u6211\uff0c\u653e\u706b\u628a\u8fd9\u5c4b\u70e7\u4e86\u5427\uff0c\u6211\u5b81\u6b7b\u4e5f\u4e0d\u613f\u89c1\u4f60\u8fd9\u4e27\u5fc3\u75c5\u72c2\u3001\u6ca1\u826f\u5fc3\u7684\u4eba\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8d8a\u542c\u8d8a\u89c9\u58f0\u97f3\u597d\u719f\uff0c\u7ec8\u4e8e\u60ca\u89c9\uff1a\u201c\u662f\u5b89\u5927\u5a18\uff01\u539f\u6765\u8fd9\u5b89\u5927\u4eba\u662f\u5979\u4e08\u4eba\u3001\u662f\u5c0f\u6167\u7684\u7236\u4eb2\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e09\u56de\u3000\u6325\u690e\u5e08\u535a\u6d6a \u6bc1\u70ae\u632b\u54e5\u8212",content:(0,t.jsx)(t.Fragment,{children:"\u53ea\u542c\u5f97\u5b89\u5927\u4eba\u8d3c\u5fd2\u563b\u563b\u7684\u7b11\u9053\uff1a\u201c\u6211\u627e\u5f97\u4f60\u597d\u82e6\uff0c\u820d\u5f97\u70e7\u4f60\u5417\uff1f\u54b1\u4eec\u6765\u53d9\u53d9\u65e7\u60c5\u5427\uff01\u201d\u8bf4\u7740\u53d1\u8db3\u8e22\u95e8\uff0c\u53ea\u4e24\u811a\uff0c\u95e8\u95e9\u5580\u5587\u4e00\u58f0\u65ad\u4e86\u3002\u8881\u627f\u5fd7\u542c\u8e22\u95e8\u4e4b\u58f0\uff0c\u77e5\u4ed6\u6b66\u529f\u9887\u4e3a\u4e86\u5f97\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u5200\u5149\u95ea\u52a8\uff0c\u5b89\u5927\u5a18\u4e00\u5200\u76f4\u5288\u51fa\u6765\u3002\u5b89\u5927\u4eba\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u8c0b\u6740\u4eb2\u592b\uff01\u201d\u6015\u5c4b\u5185\u53e6\u6709\u522b\u4eba\uff0c\u4e0d\u6562\u7a9c\u8fdb\uff0c\u7ad9\u5728\u95e8\u5916\u7a7a\u624b\u548c\u5b89\u5927\u5a18\u53ae\u6597\u3002\u8881\u627f\u5fd7\u6162\u6162\u722c\u8fd1\uff0c\u7741\u5927\u773c\u775b\u89c2\u6218\u3002 \u3000\u3000\u90a3\u5b89\u5927\u4eba\u6b66\u529f\u679c\u7136\u4e0d\u51e1\uff0c\u5728\u9ed1\u6697\u4e2d\u542c\u7740\u5200\u98ce\u95ea\u8eb2\u8fdb\u62db\uff0c\u53e3\u4e2d\u5374\u662f\u4e0d\u65ad\u98ce\u8a00\u98ce\u8bed\u7684\u8c03\u7b11\u3002\u5b89\u5927\u5a18\u5374\u5341\u5206\u6124\u6012\uff0c\u8fb9\u6253\u8fb9\u9a82\u3002 \u3000\u3000\u6597\u4e86\u4e00\u9635\uff0c\u5b89\u5927\u4eba\u7a81\u7136\u4f38\u624b\u5728\u5979\u8eab\u4e0a\u6478\u4e86\u4e00\u628a\u3002\u5b89\u5927\u5a18\u66f4\u6012\uff0c\u6325\u5200\u5f53\u5934\u75be\u780d\uff0c\u5b89\u5927\u4eba\u6b63\u662f\u8981\u8bf1\u5979\u8fd9\u4e00\u62db\uff0c\u504f\u8eab\u62a2\u8fdb\u4e00\u6b65\uff0c\u626d\u4f4f\u4e86\u5979\u624b\u8155\uff0c\u7528\u529b\u4e00\u62e7\uff0c\u5b89\u5927\u5a18\u5355\u5200\u843d\u5730\u3002\u5b89\u5927\u4eba\u5c06\u5979\u53cc\u624b\u634f\u4f4f\uff0c\u53f3\u817f\u67b6\u5728\u5979\u53cc\u817f\u819d\u4e0a\uff0c\u5b89\u5927\u5a18\u767b\u65f6\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u542c\u8fd9\u59d3\u5b89\u7684\u53e3\u6c14\uff0c\u4e00\u65f6\u4e0d\u81f4\u4f24\u5bb3\u4e8e\u5979\uff0c\u6211\u4e14\u591a\u63a2\u542c\u4e00\u4f1a\uff0c\u518d\u51fa\u624b\u76f8\u6551\u3002\u201d\u4e58\u90a3\u5b89\u5927\u4eba\u54c8\u54c8\u72c2\u7b11\u3001\u5b89\u5927\u5a18\u7834\u53e3\u5927\u9a82\u4e4b\u9645\uff0c\u8eab\u5b50\u4e00\u7f29\uff0c\u4ece\u95e8\u89d2\u8fb9\u94bb\u4e86\u8fdb\u53bb\uff0c\u8f7b\u8f7b\u6478\u5230\u5899\u58c1\uff0c\u65bd\u5c55\u201c\u58c1\u864e\u6e38\u5899\u529f\u201d\u76f4\u4e0a\uff0c\u6500\u5728\u6881\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u5b89\u5927\u4eba\u53eb\u9053\uff1a\u201c\u80e1\u8001\u4e09\uff0c\u8fdb\u53bb\u70b9\u706b\uff01\u201d\u80e1\u8001\u4e09\u5728\u95e8\u5916\u4eae\u4e86\u706b\u6298\u5b50\uff0c\u62d4\u5200\u62a4\u8eab\uff0c\u5148\u628a\u706b\u6298\u5f80\u95e8\u91cc\u4e00\u63a2\uff0c\u53c8\u4fef\u8eab\u6361\u4e86\u5757\u77f3\u5b50\u6295\u8fdb\u5c4b\u91cc\uff0c\u8fc7\u4e86\u4e00\u4f1a\u89c1\u65e0\u52a8\u9759\uff0c\u624d\u5165\u5185\u5728\u684c\u4e0a\u627e\u5230\u70db\u53f0\uff0c\u70b9\u4eae\u8721\u70db\u3002\u5b89\u5927\u4eba\u5c06\u5b89\u5927\u5a18\u62b1\u8fdb\u5c4b\u53bb\uff0c\u4f7f\u4e2a\u773c\u8272\uff0c\u80e1\u8001\u4e09\u4ece\u8eab\u8fb9\u62ff\u51fa\u7ef3\u7d22\uff0c\u5c06\u5b89\u5927\u5a18\u624b\u811a\u90fd\u7f1a\u4f4f\u4e86\u3002\u5b89\u5927\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u518d\u4e5f\u4e0d\u8981\u89c1\u6211\uff0c\u8fd9\u53ef\u4e0d\u89c1\u4e86\u4e48\uff1f\u77a7\u77a7\u6211\uff0c\u767d\u5934\u53d1\u591a\u4e86\u51e0\u6839\u5427\uff1f\u201d\u5b89\u5927\u5a18\u95ed\u76ee\u4e0d\u7b54\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4ece\u6881\u4e0a\u671b\u4e0b\u6765\uff0c\u5b89\u5927\u4eba\u7684\u9762\u8c8c\u770b\u5f97\u66f4\u6e05\u695a\u4e86\uff0c\u89c1\u4ed6\u867d\u7136\u5df2\u8fc7\u4e2d\u5e74\uff0c\u4f46\u9762\u76ee\u4ecd\u5f88\u82f1\u4fca\uff0c\u60f3\u6765\u5e74\u8f7b\u65f6\u5fc5\u662f\u4e2a\u7f8e\u8c8c\u5c11\u5e74\uff0c\u4e0e\u5b89\u5927\u5a18\u5012\u662f\u4e00\u5bf9\u74a7\u4eba\u3002 \u3000\u3000\u5b89\u5927\u4eba\u4f38\u624b\u6478\u6478\u5b89\u5927\u5a18\u7684\u8138\uff0c\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u5341\u591a\u5e74\u4e0d\u89c1\uff0c\u8138\u86cb\u513f\u5012\u8fd8\u662f\u96ea\u767d\u7c89\u5ae9\u3002\u201d\u4fa7\u5934\u5bf9\u80e1\u4e09\u9053\uff1a\u201c\u51fa\u53bb\uff01\u201d\u80e1\u8001\u4e09\u7b11\u7740\u7b54\u5e94\uff0c\u51fa\u53bb\u65f6\u5e26\u4e0a\u4e86\u95e8\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u5bf9\u9ed8\u7136\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5b89\u5927\u4eba\u53f9\u6c14\u9053\uff1a\u201c\u5c0f\u6167\u5462\uff1f\u6211\u8fd9\u4e9b\u5e74\u6765\u5929\u5929\u60f3\u5ff5\u5979\u3002\u201d\u5b89\u5927\u5a18\u4ecd\u662f\u4e0d\u7406\u3002\u5b89\u5927\u4eba\u9053\uff1a\u201c\u4f60\u6211\u5c11\u5e74\u592b\u59bb\uff0c\u5927\u5bb6\u706b\u6c14\u5927\uff0c\u4e00\u65f6\u53cd\u76ee\uff0c\u5206\u522b\u4e86\u8fd9\u8bb8\u591a\u5e74\uff0c\u73b0\u4eca\u603b\u8be5\u548c\u597d\u5982\u521d\u4e86\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u77a7\u6211\u5341\u591a\u5e74\u6765\uff0c\u5e76\u6ca1\u53e6\u5a36\uff0c\u4f55\u66fe\u6709\u4e00\u65f6\u4e00\u523b\u5fd8\u8bb0\u4f60\uff1f\u96be\u9053\u4f60\u8fde\u4e00\u70b9\u592b\u59bb\u4e4b\u60c5\u4e5f\u6ca1\u6709\u4e48\uff1f\u201d\u5b89\u5927\u5a18\u5389\u58f0\u9053\uff1a\u201c\u6211\u7239\u7239\u548c\u54e5\u54e5\u662f\u600e\u4e48\u6b7b\u7684\uff0c\u4f60\u5fd8\u8bb0\u4e86\u5417\uff1f\u201d\u5b89\u5927\u4eba\u53f9\u9053\uff1a\u201c\u6211\u5cb3\u7236\u548c\u5927\u8205\u5b50\u662f\u9526\u8863\u536b\u5bb3\u6b7b\u7684\uff0c\u90a3\u4e0d\u9519\u3002\u53ef\u662f\u4e5f\u4e0d\u80fd\u4e00\u7af9\u7bd9\u6253\u5c3d\u4e00\u8239\u4eba\uff0c\u9526\u8863\u536b\u4e2d\u6709\u597d\u4eba\u4e5f\u6709\u574f\u4eba\u3002\u6211\u4e3a\u7687\u4e0a\u51fa\u529b\uff0c\u8fd9\u4e5f\u662f\u5149\u5b97\u8000\u7956\u7684\u4f53\u9762\u4e8b\u2026\u2026\u201d\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u5b89\u5927\u5a18\u5df2\u201c\u5478\uff0c\u5478\uff0c\u5478\u201d\u7684\u4e0d\u4f4f\u5f80\u5730\u4e0b\u553e\u5410\u3002 \u3000\u3000\u9694\u4e86\u4e00\u4f1a\uff0c\u5b89\u5927\u4eba\u6362\u4e86\u8bdd\u9898\uff1a\u201c\u6211\u601d\u5ff5\u5c0f\u6167\uff0c\u53eb\u4eba\u6765\u63a5\u5979\u3002\u5e72\u4e48\u4f60\u4e1c\u8eb2\u897f\u9003\uff0c\u59cb\u7ec8\u4e0d\u8ba9\u5979\u8ddf\u6211\u89c1\u9762\uff1f\u201d\u5b89\u5927\u5a18\u9053\uff1a\u201c\u6211\u8ddf\u5979\u8bf4\uff0c\u5979\u7684\u597d\u7238\u7238\u65e9\u5c31\u6b7b\u5566\uff01\u5979\u7238\u7238\u591a\u6709\u672c\u4e8b\uff0c\u591a\u6709\u5fd7\u6c14\uff0c\u5c31\u53ef\u60dc\u5bff\u547d\u77ed\u4e9b\uff01\u201d\u8bed\u6c14\u4e2d\u5145\u6ee1\u4e86\u6028\u6124\u3002\u5b89\u5927\u4eba\u9053\uff1a\u201c\u4f60\u4f55\u82e6\u9a97\u5979\uff1f\u53c8\u4f55\u82e6\u5492\u6211\uff1f\u201d\u5b89\u5927\u5a18\u9053\uff1a\u201c\u5979\u7238\u7238\u4ece\u524d\u5012\u771f\u662f\u4e2a\u6709\u5fd7\u6c14\u7684\u597d\u4eba\uff0c\u6211\u5bb6\u91cc\u7684\u4eba\u4e0d\u8bb8\u6211\u5ac1\u4ed6\uff0c\u6211\u5077\u5077\u8ddf\u7740\u4ed6\u8d70\u4e86\uff0c\u54ea\u77e5\u9053\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u54fd\u54bd\u8d77\u6765\uff0c\u8ddf\u7740\u53c8\u6068\u6068\u7684\u9053\uff1a\u201c\u4f60\u5bb3\u6b7b\u4e86\u6211\u7684\u597d\u4e08\u592b\uff0c\u6211\u6068\u4e0d\u5f97\u6740\u4e86\u4f60\u3002\u201d\u5b89\u5927\u4eba\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5012\u5947\u4e86\uff0c\u6211\u5c31\u662f\u4f60\u7684\u4e08\u592b\uff0c\u600e\u8bf4\u6211\u5bb3\u6b7b\u4e86\u4f60\u4e08\u592b\uff1f\u201d\u5b89\u5927\u5a18\u9053\uff1a\u201c\u6211\u4e08\u592b\u672c\u6765\u662f\u4e2a\u6709\u8840\u6027\u7684\u597d\u7537\u5b50\uff0c\u4e0d\u77e5\u600e\u7684\u5229\u7984\u718f\u5fc3\uff0c\u59bb\u5b50\u4e0d\u8981\u4e86\uff0c\u5973\u513f\u4e5f\u4e0d\u8981\u4e86\u3002\u4ed6\u53ea\u60f3\u505a\u5927\u5b98\uff0c\u53d1\u5927\u8d22\u2026\u2026\u6211\u4ece\u524d\u7684\u597d\u4e08\u592b\u65e9\u6b7b\u4e86\uff0c\u6211\u518d\u4e5f\u89c1\u4e0d\u5230\u4ed6\u5566\uff01\u201d\u8881\u627f\u5fd7\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u5fc3\u4e0b\u607b\u7136\u3002 \u3000\u3000\u5b89\u5927\u5a18\u9053\uff1a\u201c\u6211\u4e08\u592b\u540d\u53eb\u5b89\u5251\u6e05\uff0c\u672c\u662f\u4e2a\u6c5f\u6e56\u597d\u6c49\uff0c\u4e0d\u662f\u7ed9\u4f60\u8fd9\u9526\u8863\u536b\u957f\u5b98\u5b89\u5927\u4eba\u5bb3\u6b7b\u4e86\u4e48\uff1f\u6211\u4e08\u592b\u6709\u4f4d\u6069\u5e08\u695a\u5927\u5200\u695a\u8001\u62f3\u5e08\uff0c\u662f\u5b89\u5927\u4eba\u8d2a\u56fe\u5229\u7984\u800c\u5bb3\u6b7b\u4ed6\u7684\u3002\u695a\u8001\u62f3\u5e08\u7684\u592b\u4eba\u3001\u5973\u513f\uff0c\u90fd\u7ed9\u8fd9\u5b89\u5927\u5927\u903c\u6b7b\u4e86\u2026\u2026\u201d\u5b89\u5251\u6e05\u6012\u559d\uff1a\u201c\u4e0d\u8bb8\u518d\u8bf4\uff01\u201d \u3000\u3000\u5b89\u5927\u5a18\u9053\uff1a\u201c\u4f60\u8fd9\u72fc\u5fc3\u72d7\u80ba\u7684\u4eba\uff0c\u81ea\u5df1\u60f3\u60f3\u5427\u3002\u201d\u5b89\u5251\u6e05\u9053\uff1a\u201c\u5b98\u5e9c\u8981\u695a\u5927\u5200\u53bb\u95ee\u8bdd\uff0c\u53c8\u4e0d\u4e00\u5b9a\u96be\u4e3a\u4ed6\u3002\u4ed6\u5e72\u4e48\u52a8\u5200\u6740\u6211\uff1f \u3000\u3000\u4ed6\u59bb\u5b50\u5973\u513f\u662f\u81ea\u6740\u7684\uff0c\u53c8\u602a\u5f97\u4e86\u8c01\uff1f\u201d\u5b89\u5927\u5a18\u9053\uff1a\u201c\u662f\u554a\uff0c\u695a\u5927\u5200\u778e\u4e86\u773c\u54ea\uff0c\u8c01\u6559\u4ed6\u6536\u4e86\u8fd9\u6837\u4e00\u4f4d\u597d\u5f92\u5f1f\uff1f\u8fd9\u5f92\u5f1f\u53c8\u51bb\u53c8\u997f\u5feb\u6b7b\u5566\uff0c\u695a\u5927\u5200\u6559\u4ed6\u6b66\u827a\uff0c\u517b\u5927\u4ed6\uff0c\u53c8\u7ed9\u4ed6\u5a36\u5ab3\u5987\u2026\u2026\u201d\u5979\u8d8a\u8bf4\u8d8a\u662f\u6028\u6bd2\u3002\u5b89\u5251\u6e05\u731b\u529b\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u559d\u9053\uff1a\u201c\u4eca\u5929\u4f60\u6211\u592b\u59bb\u76f8\u89c1\uff0c\u662f\u4f55\u7b49\u7684\u6b22\u559c\u4e4b\u4e8b\uff0c\u5c3d\u63d0\u90a3\u6b7b\u4eba\u5e72\u4e48\uff1f\u201d\u5b89\u5927\u5a18\u53eb\u9053\uff1a\u201c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u6211\u504f\u504f\u8981\u63d0\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4ece\u4e24\u4eba\u8bdd\u4e2d\u7422\u78e8\u51fa\u6765\u5f53\u65f6\u60c5\u5f62\uff0c\u5b89\u5251\u6e05\u662f\u695a\u5927\u5200\u4e00\u624b\u6276\u517b\u957f\u5927\u7684\uff0c\u540e\u6765\u4ed6\u8d2a\u56fe\u5bcc\u8d35\uff0c\u5bb3\u6b7b\u5e08\u7236\u4e00\u5bb6\u3002\u5b89\u5251\u6e05\u5728\u9526\u8863\u536b\u5f53\u5dee\uff0c\u800c\u5b89\u5927\u5a18\u7684\u7236\u4eb2\u5144\u957f\u5374\u5747\u4e3a\u9526\u8863\u536b\u5bb3\u6b7b\u3002\u5b89\u5927\u5a18\u6c14\u5fff\u4e0d\u8fc7\uff0c\u7ec8\u4e8e\u8ddf\u4e08\u592b\u51b3\u88c2\u5206\u624b\u3002\u4ece\u524d\u80e1\u8001\u4e09\u6765\u62a2\u5c0f\u6167\uff0c\u5b89\u5927\u5a18\u4e1c\u5954\u897f\u907f\uff0c\u90fd\u662f\u4e3a\u4e86\u8fd9\u5fc3\u80a0\u72e0\u6bd2\u7684\u4e08\u592b\u5b89\u5251\u6e05\u5b89\u5927\u4eba\u4e86\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u60f3\u6765\u5f53\u65e5\u5bb3\u6b7b\u4ed6\u6069\u5e08\u4e00\u5bb6\u4e4b\u65f6\uff0c\u60c5\u5f62\u4e00\u5b9a\u5f88\u60e8\u3002\u8fd9\u4eba\u6b7b\u6709\u4f59\u8f9c\u3002\u4f46\u4e0d\u77e5\u5b89\u5927\u5a18\u5bf9\u4ed6\u662f\u5426\u5c1a\u6709\u592b\u59bb\u4e4b\u60c5\uff0c\u5012\u4e0d\u53ef\u9c81\u83bd\u4e86\u3002\u201d\u60f3\u518d\u591a\u542c\u4e00\u4e9b\u8bf4\u8bdd\uff0c\u4ee5\u4fbf\u51b3\u5b9a\u662f\u5426\u8be5\u51fa\u624b\u6740\u4ed6\uff0c\u54ea\u77e5\u4e24\u4eba\u90fd\u4f4f\u4e86\u53e3\uff0c\u9ed8\u4e0d\u51fa\u58f0\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8fdc\u5904\u5ffd\u7136\u9690\u9690\u6709\u9a6c\u8e44\u4e4b\u58f0\u3002\u5b89\u5251\u6e05\u62d4\u51fa\u4f69\u5200\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u7b49\u4eba\u6765\u65f6\uff0c\u4f60\u5982\u53eb\u558a\u793a\u8b66\uff0c\u6211\u53ef\u987e\u4e0d\u5f97\u592b\u59bb\u4e4b\u60c5\uff01\u201d \u3000\u3000\u5b89\u5927\u5a18\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53c8\u60f3\u5bb3\u4eba\u4e86\u3002\u201d \u3000\u3000\u5b89\u5251\u6e05\u77e5\u9053\u59bb\u5b50\u813e\u6c14\uff0c\u6325\u5200\u5272\u4e0b\u4e00\u5757\u5e03\u5e10\uff0c\u585e\u5728\u5979\u53e3\u91cc\u3002 \u3000\u3000\u8fd9\u65f6\u9a6c\u8e44\u58f0\u6108\u8fd1\uff0c\u5b89\u5251\u6e05\u5c06\u5b89\u5927\u5a18\u653e\u5728\u5e8a\u4e0a\uff0c\u5782\u4e0b\u5e10\u5b50\uff0c\u4ed7\u5200\u8eb2\u5728\u95e8\u540e\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77e5\u4ed6\u662f\u60f3\u5077\u65bd\u6bd2\u624b\uff0c\u867d\u4e0d\u77e5\u6765\u8005\u662f\u8c01\uff0c\u4f46\u603b\u662f\u5b89\u5927\u5a18\u4e00\u9762\u7684\u597d\u4eba\uff0c\u5728\u6881\u4e0a\u62b9\u4e86\u4e9b\u7070\u5c18\uff0c\u52a0\u70b9\u553e\u6cab\uff0c\u634f\u6210\u4e00\u4e2a\u5c0f\u5c0f\u6ce5\u56e2\u5b50\uff0c\u5bf9\u51c6\u70db\u706b\u63b7\u53bb\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u70db\u706b\u767b\u65f6\u7184\u4e86\u3002\u5b89\u5251\u6e05\u5583\u5583\u5492\u9a82\u3002\u8881\u627f\u5fd7\u4e58\u4ed6\u53bb\u6478\u706b\u6298\uff0c\u8f7b\u8f7b\u6e9c\u4e0b\u5730\u6765\uff0c\u7ed5\u5230\u5c4b\u5916\uff0c\u89c1\u5c4b\u89d2\u8fb9\u4e00\u540d\u9526\u8863\u536b\u6267\u5200\u4f0f\u5730\uff0c\u5168\u795e\u8d2f\u6ce8\u7684\u671b\u7740\u5c4b\u4e2d\u52a8\u9759\uff0c\u4fbf\u4fdf\u8fd1\u4ed6\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4eba\u6765\u5566\uff01\u201d\u90a3\u9526\u8863\u536b\u4e5f\u4f4e\u58f0\u9053\uff1a\u201c\u55ef\uff0c\u5feb\u4f0f\u4e0b\u3002\u201d\u8881\u627f\u5fd7\u4f38\u624b\u70b9\u4e86\u4ed6\u7a74\u9053\uff0c\u8131\u4e0b\u4ed6\u5916\u8863\uff0c\u7f69\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u518d\u5728\u4ed6\u91cc\u8863\u4e0a\u626f\u4e0b\u4e00\u5757\u5e03\uff0c\u8499\u5728\u9762\u4e0a\uff0c\u6495\u5f00\u4e86\u4e24\u4e2a\u773c\u5b54\uff0c\u7136\u540e\u62b1\u4e86\u90a3\u4eba\uff0c\u722c\u5411\u95e8\u8fb9\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u8e44\u58f0\u66f4\u54cd\uff0c\u4e94\u9a91\u9a6c\u5954\u5230\u5c4b\u524d\u3002\u4e58\u8005\u8df3\u4e0b\u9a6c\u6765\uff0c\u8f7b\u62cd\u4e09\u638c\u3002\u5b89\u5251\u6e05\u5728\u5c4b\u91cc\u4e5f\u56de\u62cd\u4e86\u4e09\u638c\uff0c\u70b9\u4eae\u706f\u706b\uff0c\u7f29\u5728\u95e8\u540e\uff0c\u53ea\u542c\u95e8\u58f0\u4e00\u54cd\uff0c\u4e00\u4e2a\u4eba\u63a2\u8fdb\u5934\u6765\u3002 \u3000\u3000\u4ed6\u4e3e\u5200\u731b\u529b\u780d\u4e0b\uff0c\u4e00\u4e2a\u4eba\u5934\u9aa8\u788c\u788c\u7684\u6eda\u5728\u4e00\u8fb9\uff0c\u9888\u53e3\u9c9c\u8840\u76f4\u55b7\u3002\u5728\u70db\u5149\u4e0b\u5411\u4eba\u5934\u77a5\u4e86\u4e00\u773c\uff0c\u4e0d\u89c9\u5927\u60ca\uff0c\u780d\u6b7b\u7684\u7adf\u662f\u81ea\u5df1\u4e00\u540d\u4f19\u4f34\u3002\u6b63\u8981\u5f20\u53e3\u72c2\u53eb\uff0c\u95e8\u5916\u7a9c\u8fdb\u4e00\u4e2a\u8499\u8138\u602a\u5ba2\uff0c\u4f38\u6307\u70b9\u4e86\u4ed6\u7a74\u9053\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u6253\u5728\u4ed6\u9888\u540e\u201c\u5927\u690e\u7a74\u201d\u4e0a\uff0c\u90a3\u662f\u4eba\u8eab\u624b\u8db3\u4e09\u9633\u3001\u7763\u8109\u4e4b\u4f1a\uff0c\u54ea\u91cc\u8fd8\u80fd\u52a8\u5f39\uff1f\u8881\u627f\u5fd7\u987a\u624b\u63a5\u8fc7\u4ed6\u624b\u4e2d\u4f69\u5200\uff0c\u8f7b\u8f7b\u653e\u5728\u5730\u4e0b\uff0c\u4ee5\u9632\u95e8\u5916\u4f59\u4eba\u542c\u89c1\uff0c\u7eb5\u5230\u5e8a\u524d\u6276\u8d77\u5b89\u5927\u5a18\uff0c\u626f\u65ad\u7ed1\u5728\u5979\u624b\u811a\u4e0a\u7684\u7ef3\u7d22\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u5b89\u5a76\u5a76\uff0c\u6211\u6551\u4f60\u6765\u5566\uff01\u201d \u3000\u3000\u5b89\u5927\u5a18\u89c1\u4ed6\u7a7f\u7740\u9526\u8863\u536b\u670d\u8272\uff0c\u8138\u4e0a\u53c8\u8499\u4e86\u5e03\uff0c\u4e0d\u89c9\u7591\u8651\u4e0d\u5b9a\uff0c\u521a\u95ee\u5f97\u4e00\u58f0\uff1a\u201c\u5c0a\u9a7e\u662f\u8c01\uff1f\u201d\u5916\u9762\u5954\u8fdb\u4e94\u4e2a\u4eba\u6765\uff0c\u5f53\u5148\u4e00\u4eba\u4e0e\u5b89\u5927\u5a18\u62db\u547c\u4e86\u4e00\u58f0\uff0c\u89c1\u5230\u5c4b\u4e2d\u60c5\u72b6\uff0c\u6115\u7136\u6014\u4f4f\u3002 \u3000\u3000\u95e8\u5916\u9526\u8863\u536b\u89c1\u8fdb\u6765\u4eba\u591a\uff0c\u6015\u5b89\u5251\u6e05\u4e00\u4eba\u6709\u5931\uff0c\u65e9\u6709\u4e24\u4eba\u62a2\u8fdb\u95e8\u6765\uff0c\u4e3e\u5200\u6b32\u780d\uff0c\u8881\u627f\u5fd7\u51fa\u638c\u780d\u5288\uff0c\u4e24\u540d\u9526\u8863\u536b\u9888\u9aa8\u9f50\u65ad\u3002\u95e8\u5916\u654c\u4eba\u9646\u7eed\u8fdb\u6765\uff0c\u8881\u627f\u5fd7\u5288\u6253\u6293\u62ff\uff0c\u63d0\u8d77\u6765\u4e00\u4e2a\u4e2a\u90fd\u63b7\u4e86\u51fa\u53bb\uff0c\u6709\u7684\u521a\u5954\u8fdb\u6765\u5c31\u88ab\u4e00\u817f\u8e22\u51fa\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6253\u5f97\u5341\u4e8c\u540d\u9526\u8863\u536b\u548c\u5185\u5ef7\u4f8d\u536b\u660f\u5929\u9ed1\u5730\uff0c\u98de\u4e5f\u4f3c\u7684\u9003\u8d70\u4e86\u3002\u8881\u627f\u5fd7\u6495\u4e0b\u5e03\u6761\uff0c\u585e\u5165\u5b89\u5251\u6e05\u8033\u4e2d\uff0c\u53c8\u4ece\u6b7b\u4eba\u8eab\u4e0a\u626f\u4e0b\u4e24\u4ef6\u8863\u670d\uff0c\u5728\u4ed6\u5934\u4e0a\u5305\u4e86\u51e0\u5c42\uff0c\u6559\u4ed6\u542c\u4e0d\u89c1\u534a\u70b9\u58f0\u606f\uff0c\u77a7\u4e0d\u89c1\u4e00\u70b9\u5149\u4eae\uff0c\u7136\u540e\u626f\u53bb\u8499\u5728\u81ea\u5df1\u8138\u4e0a\u8499\u7740\u7684\u7834\u5e03\uff0c\u5411\u4e94\u4eba\u5f53\u4e2d\u4e00\u4eba\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u597d\u3002\u95ef\u738b\u597d\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4eba\u4e00\u5446\uff0c\u968f\u5373\u54c8\u54c8\u5927\u7b11\uff0c\u62c9\u7740\u4ed6\u624b\u8fde\u8fde\u6447\u6643\u3002\u539f\u6765\u8fd9\u4eba\u6b63\u662f\u674e\u95ef\u738b\u624b\u4e0b\u5927\u5c06\u3001\u8881\u627f\u5fd7\u8ddf\u4ed6\u7ed3\u4e3a\u5144\u5f1f\u7684\u674e\u5ca9\u3002 \u3000\u3000\u8881\u627f\u5fd7\u65e0\u610f\u4e2d\u8fde\u6551\u4e24\u4f4d\u6545\u4eba\uff0c\u5341\u5206\u559c\u6b22\uff0c\u8f6c\u5934\u5bf9\u5b89\u5927\u5a18\u9053\uff1a\u201c\u5b89\u5a76\u5a76\uff0c\u4f60\u8fd8\u8bb0\u5f97\u6211\u4e48\uff1f\u201d\u8fd9\u65f6\u662f\u5d07\u987a\u5341\u516d\u5e74\u516d\u6708\uff0c\u79bb\u8881\u627f\u5fd7\u5728\u5b89\u5927\u5a18\u5bb6\u907f\u96be\u65f6\u5df2\u6709\u5341\u5e74\uff0c\u4ed6\u4ece\u4e00\u4e2a\u5c0f\u5c0f\u5b69\u7ae5\u957f\u5927\u6210\u4eba\uff0c\u5b89\u5927\u5a18\u54ea\u91cc\u8fd8\u8ba4\u5f97\u51fa\uff1f \u3000\u3000\u8881\u627f\u5fd7\u4ece\u5185\u8863\u888b\u91cc\u6478\u51fa\u5f53\u65e5\u5b89\u5927\u5a18\u6240\u8d60\u7684\u91d1\u4e1d\u5c0f\u956f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5929\u5929\u5e26\u5728\u8eab\u8fb9\u3002\u201d\u5b89\u5927\u5a18\u731b\u7136\u60f3\u8d77\uff0c\u62c9\u4ed6\u51d1\u8fd1\u70db\u5149\u4e00\u770b\uff0c\u679c\u89c1\u4ed6\u5de6\u7709\u4e0a\u6de1\u6de1\u7684\u6709\u4e2a\u5200\u75a4\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u554a\uff0c\u5b69\u5b50\uff0c\u4f60\u957f\u5f97\u8fd9\u4e48\u9ad8\u5566\uff0c\u53c8\u5b66\u4e86\u8fd9\u4e00\u8eab\u4fca\u529f\u592b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5728\u6d59\u6c5f\u89c1\u5230\u5c0f\u6167\u59b9\u59b9\uff0c\u5979\u4e5f\u957f\u9ad8\u5566\uff01\u201d\u5b89\u5927\u5a18\u9053\uff1a\u201c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u5b69\u5b50\u4eec\u90fd\u5927\u4e86\uff0c\u8fc7\u5f97\u771f\u5feb\u3002\u201d\u5411\u8eba\u5728\u5730\u4e0b\u7684\u4e08\u592b\u77a7\u4e86\u4e00\u773c\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u559f\u7136\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd8\u662f\u4f60\u8fd9\u5b69\u5b50\u6765\u6551\u6211\u3002\u201d \u3000\u3000\u674e\u5ca9\u4e0d\u77e5\u4ed6\u4eec\u66fe\u6709\u4e00\u6bb5\u6545\u65e7\u4e4b\u60c5\uff0c\u542c\u5b89\u5927\u5a18\u6ee1\u53e3\u53eb\u4ed6\u201c\u5b69\u5b50\uff0c\u5b69\u5b50\u201d\u7684\uff0c\u53ea\u9053\u4e24\u4eba\u662f\u4eb2\u621a\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\u597d\u9669\u3002 \u3000\u3000\u6211\u5949\u95ef\u738b\u4e4b\u547d\uff0c\u5230\u6cb3\u5317\u6765\u7ea6\u51e0\u4e2a\u4eba\u76f8\u89c1\u3002\u9526\u8863\u536b\u7684\u6d88\u606f\u4e5f\u771f\u7075\uff0c\u4e0d\u77e5\u600e\u6837\u7adf\u4f1a\u5f97\u5230\u98ce\u58f0\uff0c\u5728\u8fd9\u91cc\u57cb\u4f0f\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u7684\u670b\u53cb\u5feb\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u674e\u5ca9\u5c1a\u672a\u56de\u7b54\uff0c\u8fdc\u5904\u5df2\u95fb\u8e44\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4e48\uff1f\u201d\u4ece\u4eba\u5f00\u95e8\u51fa\u53bb\uff0c\u4e0d\u4e45\u8fce\u4e86\u4e09\u4e2a\u4eba\u8fdb\u6765\u3002\u8fd9\u4e09\u4eba\u4e00\u4e2a\u662f\u5218\u82b3\u4eae\uff0c\u4e00\u4e2a\u662f\u7530\u89c1\u79c0\uff0c\u90fd\u662f\u5f53\u5e74\u5728\u5723\u5cf0\u5d82\u4f1a\u4e0a\u89c1\u8fc7\u7684\u3002\u4ed6\u4e8c\u4eba\u5df2\u4e0d\u8bc6\u8881\u627f\u5fd7\uff0c\u8881\u627f\u5fd7\u5374\u8fd8\u8bb0\u5f97\u4ed6\u4eec\u76f8\u8c8c\u3002\u53e6\u4e00\u4e2a\u59d3\u4faf\uff0c\u5374\u66fe\u5728\u6cf0\u5c71\u5927\u4f1a\u4e2d\u89c1\u8fc7\u3002\u4e09\u4eba\u4e0e\u674e\u5ca9\u62db\u547c\u540e\uff0c\u90a3\u59d3\u4faf\u7684\u5411\u8881\u627f\u5fd7\u606d\u656c\u884c\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u597d\uff01\u201d \u3000\u3000\u674e\u5ca9\u4e0e\u5b89\u5927\u5a18\u90fd\u9053\uff1a\u201c\u4f60\u4eec\u672c\u6765\u76f8\u8bc6\uff1f\u201d\u59d3\u4faf\u7684\u9053\uff1a\u201c\u8881\u76df\u4e3b\u662f\u4e03\u7701\u603b\u76df\u4e3b\uff0c\u4f17\u5144\u5f1f\u9f50\u5949\u53f7\u4ee4\u3002\u201d\u674e\u5ca9\u559c\u9053\uff1a\u201c\u554a\uff0c\u6211\u5fd9\u7740\u5728\u6cb3\u5357\u529e\u4e8b\uff0c\u4e1c\u8def\u7684\u8baf\u606f\u7adf\u90fd\u9694\u7edd\u4e86\u3002\u539f\u6765\u51fa\u4e86\u8fd9\u6837\u4e00\u4ef6\u5927\u4e8b\uff0c\u53ef\u559c\u53ef\u8d3a\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u8fd8\u662f\u4e0a\u4e2a\u6708\u7684\u4e8b\uff0c\u627f\u597d\u670b\u53cb\u4eec\u77a7\u5f97\u8d77\uff0c\u7ed9\u4e86\u8fd9\u6837\u4e00\u4e2a\u79f0\u547c\uff0c\u5176\u5b9e\u5144\u5f1f\u54ea\u91cc\u62c5\u5f53\u5f97\u8d77\uff1f\u201d \u3000\u3000\u59d3\u4faf\u7684\u9053\uff1a\u201c\u76df\u4e3b\u6b66\u529f\u597d\uff0c\u89c1\u8bc6\u9ad8\uff0c\u90a3\u662f\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u5355\u662f\u8fd9\u4efd\u4ec1\u4e49\uff0c\u6b66\u6797\u4e2d\u54ea\u4e00\u4e2a\u4e0d\u4f69\u670d\uff1f\u201d \u3000\u3000\u674e\u5ca9\u559c\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\u3002\u201d\u5f53\u4e0b\u4f20\u8fbe\u4e86\u95ef\u738b\u7684\u53f7\u4ee4\u3002\u539f\u6765\u674e\u81ea\u6210\u5728\u6cb3\u5357\u6c5d\u5dde\u5927\u7834\u5175\u90e8\u5c1a\u4e66\u5b59\u4f20\u5ead\u6240\u7edf\u5b98\u5175\u5341\u4f59\u4e07\uff0c\u8fdb\u8feb\u6f7c\u5173\uff0c\u547d\u674e\u5ca9\u79d8\u5bc6\u524d\u6765\u6cb3\u5317\uff0c\u8054\u7edc\u7fa4\u8c6a\u54cd\u5e94\u3002 \u3000\u3000\u59d3\u4faf\u7684\u9053\uff1a\u201c\u76df\u4e3b\u4f60\u8bf4\u600e\u4e48\u529e\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u95ef\u738b\u4e49\u4e3e\uff0c\u5929\u4e0b\u8c6a\u6770\u81ea\u7136\u95fb\u98ce\u9f50\u8d77\u3002\u5c0f\u5f1f\u7acb\u5373\u53d1\u51fa\u8baf\u53bb\u3002\u54b1\u4eec\u4e03\u7701\u597d\u6c49\uff0c\u8f70\u8f70\u70c8\u70c8\u7684\u5927\u5e72\u4e00\u573a\uff01\u201d\u516d\u4eba\u8c08\u5f97\u6177\u6168\u6fc0\u6602\uff0c\u7709\u98de\u8272\u821e\u3002 \u3000\u3000\u674e\u5ca9\u9053\uff1a\u201c\u5b98\u519b\u8150\u8d25\u5df2\u6781\uff0c\u4e49\u5175\u4e00\u5230\uff0c\u90a3\u662f\u6467\u67af\u62c9\u673d\uff0c\u52bf\u5982\u7834\u7af9\uff0c\u53ea\u662f\u773c\u524d\u5374\u6709\u4e00\u4e2a\u96be\u9898\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u674e\u5ca9\u9053\uff1a\u201c\u521a\u624d\u63a5\u5230\u6025\u62a5\uff0c\u8bf4\u6709\u5341\u5c0a\u897f\u6d0b\u7684\u7ea2\u5937\u5927\u70ae\uff0c\u8981\u8fd0\u5230\u6f7c\u5173\u53bb\u7ed9\u5b59\u4f20\u5ead\u3002\u5b59\u8001\u513f\u5927\u8d25\u4e4b\u4f59\uff0c\u58eb\u65e0\u6597\u5fd7\uff0c\u5df2\u7136\u4e0d\u8db3\u4e3a\u60a3\u3002\u53ea\u4e0d\u8fc7\u7ea2\u5937\u5927\u70ae\u5a01\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u4e00\u70ae\u8f70\u5c06\u51fa\u6765\uff0c\u7acb\u65f6\u6740\u4f24\u6570\u767e\u4eba\uff0c\u5012\u662f\u4e00\u4ef6\u9690\u5fe7\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u5341\u5c0a\u5927\u70ae\u5c0f\u5f1f\u5728\u9053\u4e0a\u89c1\u8fc7\uff0c\u786e\u662f\u795e\u6001\u53ef\u754f\uff0c\u60f3\u6765\u5a01\u529b\u975e\u5e38\uff0c\u96be\u9053\u4e0d\u662f\u8fd0\u53bb\u5c71\u6d77\u5173\u6253\u6ee1\u6e05\u7684\u4e48\uff1f\u201d\u674e\u5ca9\u9053\uff1a\u201c\u8fd9\u4e9b\u5927\u70ae\u4e07\u91cc\u8fe2\u8fe2\u7684\u8fd0\u6765\uff0c\u542c\u8bf4\u672c\u662f\u8981\u53bb\u5c71\u6d77\u5173\u9632\u5907\u6e05\u5175\u7684\u3002\u4f46\u95ef\u738b\u8282\u8282\u5f97\u80dc\uff0c\u671d\u5ef7\u4fbf\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u5341\u5c0a\u5927\u70ae\u5df2\u6298\u800c\u5357\u4e0b\uff0c\u9996\u9014\u8d74\u6f7c\u5173\u53bb\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u76b1\u7709\u9053\uff1a\u201c\u7687\u5e1d\u9632\u8303\u767e\u59d3\uff0c\u91cd\u4e8e\u62b5\u5fa1\u5916\u654c\u3002\u5927\u54e5\uff0c\u4f60\u8bf4\u600e\u4e48\u529e\uff1f\u201d\u674e\u5ca9\u9053\uff1a\u201c\u5927\u70ae\u4e00\u5230\u6f7c\u5173\uff0c\u54b1\u4eec\u653b\u5173\u4e4b\u65f6\uff0c\u52bf\u5fc5\u4ee5\u8840\u8089\u4e4b\u8eaf\u62b5\u6321\u706b\u70ae\u5229\u5668\uff0c\u867d\u7136\u4e0d\u4e00\u5b9a\u843d\u8d25\uff0c\u4f46\u635f\u6298\u5fc5\u591a\u2026\u2026\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u56e0\u6b64\u54b1\u4eec\u8981\u5148\u5728\u534a\u8def\u4e0a\u622a\u4ed6\u4e0b\u6765\u3002\u201d \u3000\u3000\u674e\u5ca9\u62ca\u638c\u5927\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53ef\u8981\u504f\u52b3\u5144\u5f1f\uff0c\u7acb\u6b64\u5927\u529f\u3002\u201d\u8881\u627f\u5fd7\u6c89\u541f\u9053\uff1a\u201c\u6d0b\u5175\u706b\u5668\u5f88\u662f\u5389\u5bb3\uff0c\u5144\u5f1f\u5df2\u89c1\u8bc6\u4e86\u4e00\u4e9b\uff0c\u8981\u593a\u5927\u70ae\uff0c\u987b\u5f97\u53e6\u51fa\u8ba1\u8c0b\uff0c\u80fd\u5426\u6210\u4e8b\uff0c\u5b9e\u5728\u96be\u8bf4\u3002\u4e0d\u8fc7\u8fd9\u4ef6\u4e8b\u6709\u5173\u5929\u4e0b\u6c14\u8fd0\uff0c\u5c0f\u5f1f\u5fc5\u5f53\u5c3d\u529b\u800c\u4e3a\uff0c\u82e5\u80fd\u4ef0\u4ed7\u95ef\u738b\u795e\u5a01\uff0c\u4e00\u4e3e\u6210\u529f\uff0c\u90a3\u662f\u4e07\u6c11\u4e4b\u798f\u3002\u201d \u3000\u3000\u4f17\u4eba\u53c8\u8c08\u4e86\u4e00\u4f1a\u519b\u65c5\u4e4b\u4e8b\uff0c\u8881\u627f\u5fd7\u95ee\u8d77\u674e\u5ca9\u7684\u592b\u4eba\u3002\u674e\u5ca9\u9053\uff1a\u201c\u5979\u5728\u6cb3\u5357\uff0c\u5e73\u65f6\u4e5f\u5e38\u5e38\u8bf4\u8d77\u4f60\u3002\u201d\u5b89\u5927\u5a18\u63d2\u53e3\u9053\uff1a\u201c\u674e\u5c06\u519b\u7684\u592b\u4eba\u771f\u662f\u5973\u4e2d\u82f1\u8c6a\u3002\u5582\uff0c\u5b69\u5b50\uff0c\u4f60\u6709\u4e86\u610f\u4e2d\u4eba\u5417\uff1f\u201d\u8881\u627f\u5fd7\u60f3\u8d77\u9752\u9752\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u5fae\u7b11\u4e0d\u7b54\u3002\u5b89\u5927\u5a18\u53f9\u9053\uff1a\u201c\u4f3c\u4f60\u8fd9\u822c\u7684\u4eba\u624d\uff0c\u4e0d\u77e5\u8c01\u5bb6\u59d1\u5a18\u6709\u798f\u6c14\uff0c\u5509\uff01\u201d\u5ffd\u7136\u60f3\u8d77\u4e86\u5c0f\u6167\uff1a\u201c\u5c0f\u6167\u8ddf\u4ed6\u5c0f\u65f6\u662f\u60a3\u96be\u65e7\u4fa3\u3002\u4ed6\u5982\u80fd\u505a\u6211\u5973\u5a7f\uff0c\u5c0f\u6167\u771f\u662f\u7ec8\u8eab\u6709\u6258\u3002\u4f46\u5979\u504f\u504f\u548c\u90a3\u50bb\u91cc\u50bb\u6c14\u7684\u5d14\u5e0c\u654f\u597d\uff0c\u90a3\u4e5f\u53eb\u505a\u5404\u6709\u5404\u7684\u7f18\u6cd5\u4e86\u3002\u201d \u3000\u3000\u5218\u3001\u7530\u3001\u4faf\u4e09\u4eba\u542c\u4ed6\u4eec\u8c08\u5230\u79c1\u4e8b\uff0c\u63d2\u4e0d\u8fdb\u53e3\u53bb\uff0c\u5c31\u7ad9\u8d77\u6765\u544a\u8f9e\u3002\u59d3\u4faf\u7684\u4faf\u98de\u6587\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u660e\u513f\u4e00\u65e9\uff0c\u6211\u5e26\u9886\u624b\u4e0b\u5144\u5f1f\u524d\u6765\u542c\u4ee4\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff01\u201d\u4e09\u4eba\u8f9e\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u674e\u5ca9\u4e0e\u8881\u627f\u5fd7\u526a\u70db\u957f\u8c08\u5929\u4e0b\u5927\u52bf\uff0c\u8d8a\u8bf4\u8d8a\u662f\u60c5\u6295\u610f\u5408\u3002\u8881\u627f\u5fd7\u4e8e\u56fd\u4e8b\u5174\u8870\uff0c\u4e16\u5c40\u53d8\u5e7b\uff0c\u6240\u77e5\u751a\u662f\u80a4\u6d45\uff0c\u542c\u7740\u674e\u5ca9\u7684\u8c08\u8bba\uff0c\u6bcf\u4e00\u53e5\u8bdd\u90fd\u4ee4\u4ed6\u6709\u8305\u585e\u987f\u5f00\u4e4b\u611f\u3002\u76f4\u5230\u4e1c\u65b9\u5927\u767d\uff0c\u91d1\u9e21\u4e09\u5531\uff0c\u4e24\u4eba\u5174\u72b9\u672a\u5df2\u3002\u56de\u987e\u5b89\u5927\u5a18\uff0c\u53ea\u89c1\u5979\u4ee5\u624b\u652f\u5934\uff0c\u5140\u81ea\u77a7\u7740\u8eba\u5728\u5730\u4e0b\u7684\u4e08\u592b\u9ed8\u9ed8\u51fa\u795e\u3002 \u3000\u3000\u674e\u5ca9\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u5b89\u5927\u5a18\uff01\u201d\u5b89\u5927\u5a18\u62ac\u8d77\u4e86\u5934\u3002\u674e\u5ca9\u9053\uff1a\u201c\u8fd9\u4eba\u600e\u4e48\u5904\u7f6e\uff1f\u201d\u5b89\u5927\u5a18\u5fc3\u4e71\u5982\u9ebb\uff0c\u6447\u5934\u4e0d\u7b54\u3002\u674e\u5ca9\u77e5\u5979\u96be\u4ee5\u51b3\u65ad\uff0c\u4e5f\u5c31\u4e0d\u518d\u7406\u4f1a\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u6211\u5c31\u6b64\u522b\u8fc7\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u9001\u5927\u54e5\u4e00\u7a0b\u3002\u201d \u3000\u3000\u4e24\u4eba\u548c\u5b89\u5927\u5a18\u522b\u8fc7\uff0c\u643a\u624b\u51fa\u5c4b\uff0c\u5e76\u80a9\u800c\u884c\u3002\u674e\u5ca9\u7684\u4ece\u4eba\u8fdc\u8fdc\u8ddf\u968f\u5728\u540e\u3002\u4e24\u4eba\u4e00\u8def\u8bf4\u8bdd\uff0c\u8d70\u51fa\u4e86\u4e03\u516b\u91cc\u8def\u3002\u674e\u5ca9\u9053\uff1a\u201c\u9001\u541b\u5343\u91cc\uff0c\u7ec8\u987b\u4e00\u522b\uff0c\u5144\u5f1f\uff0c\u4f60\u56de\u53bb\u5427\u3002\u201d\u8881\u627f\u5fd7\u548c\u4ed6\u610f\u6c14\u76f8\u6295\uff0c\u604b\u604b\u4e0d\u820d\u3002\u674e\u5ca9\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u95ef\u738b\u5927\u4e8b\u544a\u6210\u4e4b\u540e\uff0c\u6211\u548c\u4f60\u9690\u5c45\u5c71\u6797\uff0c\u996e\u9152\u4e3a\u4e50\uff0c\u4eca\u540e\u7684\u65e5\u5b50\u957f\u7740\u5462\u3002\u201d\u8881\u627f\u5fd7\u559c\u9053\uff1a\u201c\u82e5\u80fd\u5982\u6b64\uff0c\u5b9e\u6170\u751f\u5e73\u4e4b\u613f\u3002\u201d\u5f53\u4e0b\u4e8c\u4eba\u6d12\u6cea\u800c\u522b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u773c\u671b\u4e49\u5144\u4e0a\u9a6c\u7edd\u5c18\u800c\u53bb\uff0c\u8fd9\u624d\u56de\u5f52\u5ba2\u5e97\u3002\u53ea\u89c1\u4faf\u98de\u6587\u5df2\u5e26\u4e86\u6570\u5341\u540d\u7cbe\u58ee\u6c49\u5b50\u5728\u5e97\u4e2d\u7b49\u5019\uff0c\u628a\u5927\u5385\u548c\u51e0\u4e2a\u9662\u5b50\u90fd\u6324\u5f97\u6ee1\u6ee1\u7684\u3002\u9752\u9752\u3001\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u7b49\u4eba\u5374\u5df2\u4e0d\u89c1\u3002\u963f\u4e5d\u548c\u4e00\u4f17\u4ece\u4eba\u89c1\u4e86\u8fd9\u8bb8\u591a\u7c97\u8c6a\u5927\u6c49\uff0c\u7adf\u7136\u4e0d\u52a8\u58f0\u8272\uff0c\u803d\u5728\u623f\u4e2d\uff0c\u5e76\u4e0d\u51fa\u6765\u3002\u8881\u627f\u5fd7\u5bf9\u4faf\u98de\u6587\u9053\uff1a\u201c\u4faf\u5927\u54e5\uff0c\u4f60\u5e26\u9886\u51e0\u4f4d\u5f1f\u5144\u5411\u5357\u67e5\u63a2\uff0c\u770b\u90a3\u961f\u897f\u6d0b\u5175\u5e26\u7684\u5927\u70ae\u662f\u5411\u5317\u6765\u5462\uff0c\u8fd8\u662f\u6298\u5411\u5357\u65b9\u3002\u67e5\u660e\u4e4b\u540e\uff0c\u8bf7\u8d76\u901f\u56de\u62a5\u3002\u201d\u4faf\u98de\u6587\u542c\u4e86\uff0c\u6311\u4e86\u4e09\u540d\u540c\u4f34\uff0c\u4e0a\u9a6c\u51fa\u5e97\u800c\u53bb\u3002 \u3000\u3000\u4faf\u98de\u6587\u521a\u8d70\uff0c\u6c99\u5929\u5e7f\u548c\u7a0b\u9752\u7af9\u4e24\u4eba\u5954\u8fdb\u5e97\u6765\uff0c\u89c1\u4e86\u8881\u627f\u5fd7\uff0c\u559c\u9053\uff1a\u201c\u554a\uff0c\u8881\u76f8\u516c\u56de\u6765\u4e86\u3002\u201d\u8881\u627f\u5fd7\u672a\u53ca\u7b54\u8bdd\uff0c\u53c8\u89c1\u9752\u9752\u3001\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u95ef\u8fdb\u5385\u6765\u3002\u9752\u9752\u4e00\u5934\u79c0\u53d1\u88ab\u98ce\u5439\u5f97\u6563\u4e71\uff0c\u8138\u988a\u6655\u7ea2\uff0c\u89c1\u4e86\u8881\u627f\u5fd7\uff0c\u4e0d\u7531\u5f97\u559c\u4e0a\u7709\u68a2\uff0c\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u65f6\u5019\u624d\u56de\u6765\uff1f\u201d\u8881\u627f\u5fd7\u624d\u77e5\u5927\u5bb6\u4e0d\u653e\u5fc3\uff0c\u5206\u5934\u51fa\u53bb\u63a5\u5e94\u81ea\u5df1\uff0c\u5f53\u4e0b\u8bf4\u4e86\u6628\u665a\u4e4b\u4e8b\u3002 \u3000\u3000\u9752\u9752\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e00\u8bed\u4e0d\u53d1\u3002\u8881\u627f\u5fd7\u89c1\u5979\u795e\u8272\u4e0d\u5bf9\uff0c\u628a\u5979\u62c9\u5728\u4e00\u65c1\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u662f\u6211\u6559\u4f60\u62c5\u5fc3\u4e86\u3002\u201d\u9752\u9752\u4e00\u626d\u8eab\u5b50\uff0c\u522b\u5f00\u4e86\u5934\u3002\u8881\u627f\u5fd7\u77e5\u5979\u751f\u6c14\uff0c\u642d\u8baa\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u6ca1\u6709\u89c1\u5230\u6211\u90a3\u4f4d\u674e\u5927\u54e5\u3002\u9752\u5f1f\uff0c\u4ed6\u4e5f\u7b97\u662f\u4f60\u54e5\u54e5\u554a\u3002\u201d\u9752\u9752\u867d\u662f\u5973\u5b50\uff0c\u4f46\u8881\u627f\u5fd7\u53eb\u987a\u4e86\u53e3\uff0c\u4e00\u76f4\u4ecd\u53eb\u5979\u9752\u5f1f\u3002\u9752\u9752\u9053\uff1a\u201c\u54e5\u54e5\u6ca1\u826f\u5fc3\uff0c\u8981\u54e5\u54e5\u6765\u505a\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u771f\u662f\u5bf9\u4e0d\u8d77\uff0c\u4e0b\u6b21\u4e00\u5b9a\u4e0d\u518d\u8ba9\u4f60\u62c5\u5fc3\u5566\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4e0b\u6b21\u81ea\u6709\u522b\u4eba\u6765\u7ed9\u4f60\u62c5\u5fc3\uff0c\u8981\u6211\u62c5\u5fc3\u5e72\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u54a6\uff0c\u8c01\u554a\uff1f\u201d\u9752\u9752\u4e00\u987f\u8db3\uff0c\u56de\u5230\u81ea\u5df1\u623f\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u7b49\u5230\u4e2d\u5348\uff0c\u4e0d\u89c1\u5979\u51fa\u6765\u5403\u996d\uff0c\u8881\u627f\u5fd7\u53eb\u5e97\u4f19\u628a\u996d\u83dc\u9001\u5230\u5979\u623f\u91cc\u53bb\uff0c\u5fc3\u60f3\u4e0d\u77e5\u4e3a\u751a\u4e48\u751f\u8fd9\u4e48\u5927\u7684\u6c14\uff0c\u7b49\u5403\u8fc7\u996d\u540e\uff0c\u518d\u53bb\u8d54\u7f6a\u5c31\u662f\uff0c\u9002\u624d\u89c1\u5979\u614c\u4e71\u5fe7\u6025\u4e4b\u72b6\uff0c\u6b64\u65f6\u56de\u60f3\uff0c\u5fc3\u4e0b\u7740\u5b9e\u611f\u52a8\u3002\u54ea\u77e5\u5e97\u4f19\u628a\u996d\u83dc\u6367\u4e86\u56de\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u5728\u5c4b\u91cc\uff01\u201d\u8881\u627f\u5fd7\u4e00\u60ca\uff0c\u5fd9\u6487\u4e0b\u7b77\u5b50\uff0c\u5954\u5230\u9752\u9752\u623f\u91cc\uff0c\u53ea\u89c1\u4eba\u56fa\u4e0d\u5728\uff0c\u8fde\u5175\u5203\u8863\u56ca\u4e5f\u90fd\u5e26\u8d70\u4e86\u3002\u4ed6\u5fc3\u4e2d\u7740\u6025\uff0c\u5bfb\u601d\uff1a\u201c\u8fd9\u4e00\u8d1f\u6c14\u800c\u53bb\uff0c\u5374\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u5979\u5e38\u5e38\u60f9\u4e8b\u95ef\u7978\uff0c\u597d\u6559\u4eba\u653e\u5fc3\u4e0d\u4e0b\u3002\u53ea\u662f\u73b0\u4e0b\u5927\u4e8b\u5728\u8eab\uff0c\u4e0d\u80fd\u4eb2\u81ea\u53bb\u5bfb\u3002\u201d\u4e8e\u662f\u6d3e\u6d2a\u80dc\u6d77\u51fa\u53bb\u63a2\u8bbf\uff0c\u5429\u5490\u82e5\u662f\u89c1\u5230\u4e86\uff0c\u597d\u6b79\u8981\u529d\u59d1\u5a18\u56de\u6765\u3002 \u3000\u3000\u7b49\u5230\u508d\u665a\uff0c\u4faf\u98de\u6587\u9a91\u7740\u5feb\u9a6c\u56de\u6765\u4e86\uff0c\u4e00\u8fdb\u95e8\u5c31\u9053\uff1a\u201c\u6d0b\u5175\u961f\u4f0d\u679c\u7136\u6298\u800c\u5411\u5357\uff0c\u54b1\u4eec\u5feb\u8ffd\u3002\u201d\u8881\u627f\u5fd7\u5f53\u5373\u7ad9\u8d77\uff0c\u547d\u54d1\u5df4\u5728\u5e97\u4e2d\u7559\u5b88\u94c1\u7bb1\uff0c\u81ea\u5df1\u7387\u9886\u7a0b\u3001\u6c99\u3001\u80e1\u3001\u94c1\u56db\u4eba\u4ee5\u53ca\u4faf\u98de\u6587\u7b49\u6cb3\u5317\u7fa4\u8c6a\uff0c\u8fde\u591c\u4ece\u6765\u8def\u8d76\u53bb\uff0c\u4f30\u91cf\u5de8\u70ae\u79fb\u52a8\u7f13\u6162\uff0c\u5fc5\u53ef\u8ffd\u4e0a\u3002 \u3000\u3000\u5230\u7b2c\u4e09\u65e5\u6e05\u6668\uff0c\u8881\u627f\u5fd7\u7b49\u7a7f\u8fc7\u4e00\u4e2a\u5c0f\u9547\uff0c\u53ea\u89c1\u5341\u5c0a\u5927\u70ae\u6392\u5728\u4e00\u5bb6\u9152\u697c\u4e4b\u5916\uff0c\u6bcf\u5c0a\u70ae\u65c1\u6709\u516d\u540d\u6d0b\u5175\u6267\u67aa\u5b88\u536b\u3002\u4f17\u4eba\u5927\u559c\uff0c\u76f8\u89c6\u800c\u7b11\u3002\u94c1\u7f57\u6c49\u53eb\u9053\uff1a\u201c\u809a\u5b50\u997f\u5566\uff0c\u809a\u5b50\u997f\u5566\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u518d\u53bb\u4f1a\u4f1a\u90a3\u4e24\u4e2a\u6d0b\u5b98\u3002\u201d \u3000\u3000\u4f17\u4eba\u76f4\u4e0a\u9152\u697c\uff0c\u94c1\u7f57\u6c49\u8d70\u5728\u5934\u91cc\uff0c\u4e00\u4e0a\u697c\u5c31\u60ca\u53eb\u4e00\u58f0\u3002\u53ea\u89c1\u51e0\u540d\u6d0b\u5175\u624b\u6301\u6d0b\u67aa\uff0c\u5bf9\u51c6\u4e86\u9752\u9752\uff0c\u624b\u6307\u6273\u4f4f\u67aa\u673a\u3002\u4e00\u65c1\u5750\u7740\u90a3\u4e24\u4e2a\u897f\u6d0b\u519b\u5b98\u5f7c\u5f97\u3001\u96f7\u8499\u548c\u90a3\u897f\u6d0b\u5973\u5b50\u82e5\u514b\u7433\u3002 \u3000\u3000\u96f7\u8499\u89c1\u4f17\u4eba\u4e0a\u6765\uff0c\u53fd\u54a6\u5495\u565c\u7684\u53eb\u4e86\u51e0\u58f0\uff0c\u53c8\u6709\u51e0\u540d\u6d0b\u5175\u4e3e\u8d77\u4e86\u67aa\u5bf9\u7740\u4ed6\u4eec\uff0c\u5927\u58f0\u547c\u559d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6025\u4e2d\u751f\u667a\uff0c\u63d0\u8d77\u4e00\u5f20\u684c\u5b50\uff0c\u731b\u5411\u4f17\u6d0b\u5175\u63b7\u53bb\uff0c\u8ddf\u7740\u98de\u8eab\u800c\u524d\uff0c\u5728\u9752\u9752\u80a9\u5934\u4e00\u6309\uff0c\u4e24\u4eba\u8e72\u4f4e\u8eab\u5b50\uff0c\u4e00\u9635\u70df\u96fe\u8fc7\u53bb\uff0c\u4f17\u67aa\u9f50\u53d1\uff0c\u94c5\u5b50\u90fd\u6253\u5728\u684c\u9762\u4e0a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6015\u706b\u5668\u5389\u5bb3\uff0c\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u4e0b\u697c\u3002\u201d\u62c9\u7740\u9752\u9752\uff0c\u4e0e\u4f17\u4eba\u90fd\u4ece\u7a97\u53e3\u8df3\u4e0b\u697c\u53bb\u3002 \u3000\u3000\u96f7\u8499\u5927\u6012\uff0c\u638f\u51fa\u77ed\u67aa\u5411\u4e0b\u8f70\u51fb\u3002\u94c1\u7f57\u6c49\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u5c41\u80a1\u4e0a\u7ed9\u67aa\u5f39\u6253\u4e2d\uff0c\u6454\u5012\u5728\u5730\u3002\u6c99\u5929\u5e7f\u8fde\u5fd9\u6276\u8d77\u3002\u5404\u4eba\u4e0a\u9a6c\u5411\u5357\u5954\u9a70\u3002\u90a3\u65f6\u897f\u6d0b\u706b\u5668\u4f7f\u7528\u4e0d\u4fbf\uff0c\u653e\u4e86\u4e00\u67aa\uff0c\u987b\u5f97\u518d\u4e0a\u706b\u836f\u94c5\u5b50\uff0c\u4f17\u6d0b\u5175\u4e00\u67aa\u4e0d\u4e2d\uff0c\u518d\u4e0a\u706b\u836f\u8ffd\u51fb\u65f6\uff0c\u4f17\u4eba\u65e9\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u540c\u4e58\u4e00\u9a91\uff0c\u4e00\u9762\u5954\u9a70\uff0c\u4e00\u9762\u95ee\u9053\uff1a\u201c\u5e72\u4e48\u8ddf\u6d0b\u5175\u5435\u4e86\u8d77\u6765\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u8c01\u77e5\u9053\u554a\uff1f\u201d\u8881\u627f\u5fd7\u89c1\u5979\u795e\u8272\u5ff8\u6029\uff0c\u6599\u77e5\u522b\u6709\u9690\u60c5\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u5c31\u4e0d\u95ee\u4e86\u3002\u8fd9\u4e09\u65e5\u6765\u65e5\u591c\u8bb0\u6302\uff0c\u6b64\u523b\u91cd\u9022\uff0c\u5fc3\u4e2d\u6b22\u559c\u65e0\u9650\u3002 \u3000\u3000\u9a70\u51fa\u4e8c\u5341\u4f59\u91cc\uff0c\u5230\u4e86\u4e00\u5904\u5e02\u9547\uff0c\u4f17\u4eba\u4e0b\u9a6c\u6253\u5c16\u3002\u80e1\u6842\u5357\u7528\u5c0f\u5200\u628a\u94c1\u7f57\u6c49\u8089\u91cc\u7684\u94c5\u5b50\u525c\u4e86\u51fa\u6765\u3002\u94c1\u7f57\u6c49\u75db\u5f97\u4e71\u53eb\u4e71\u9a82\u3002 \u3000\u3000\u9752\u9752\u628a\u8881\u627f\u5fd7\u62c9\u5230\u897f\u9996\u4e00\u5f20\u684c\u65c1\u5750\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8c01\u53eb\u5979\u6253\u626e\u5f97\u5996\u91cc\u5996\u6c14\u7684\uff0c\u624b\u81c2\u4e5f\u9732\u4e86\u51fa\u6765\uff0c\u771f\u4e0d\u6015\u4e11\uff01\u201d\u8881\u627f\u5fd7\u6478\u4e0d\u7740\u5934\u8111\uff0c\u95ee\u9053\uff1a\u201c\u8c01\u554a\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u90a3\u4e2a\u897f\u6d0b\u56fd\u5973\u4eba\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u53c8\u788d\u4f60\u4e8b\u4e86\uff1f\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u6211\u770b\u4e0d\u60ef\uff0c\u7528\u4e24\u679a\u94dc\u94b1\u628a\u5979\u7684\u8033\u73af\u6253\u70c2\u4e86\u3002\u201d\u8881\u627f\u5fd7\u4e0d\u89c9\u597d\u7b11\uff0c\u9053\uff1a\u201c\u5509\uff0c\u4f60\u771f\u662f\u80e1\u95f9\uff0c\u540e\u6765\u600e\u6837\uff1f\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u6bd4\u5251\u8f93\u4e86\u7ed9\u6211\u7684\u6d0b\u5b98\u5c31\u53eb\u6d0b\u5175\u7528\u67aa\u5bf9\u7740\u6211\u3002\u6211\u4e0d\u61c2\u4ed6\u8bdd\uff0c\u6599\u60f3\u53c8\u8981\u548c\u6211\u6bd4\u5251\u5462\uff0c\u5fc3\u60f3\u6bd4\u5c31\u6bd4\u5427\uff0c\u96be\u9053\u8fd8\u6015\u4e86\u4f60\uff1f\u6b63\u5728\u8fd9\u65f6\u5019\uff0c\u4f60\u4eec\u5c31\u6765\u5566\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u53c8\u4e3a\u751a\u4e48\u72ec\u81ea\u8d70\u4e86\uff1f\u201d \u3000\u3000\u9752\u9752\u672c\u6765\u8a00\u7b11\u664f\u664f\uff0c\u4e00\u542c\u8fd9\u8bdd\uff0c\u4fcf\u8138\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd8\u8981\u95ee\u6211\u5462\uff0c\u81ea\u5df1\u505a\u7684\u4e8b\u4e0d\u77e5\u9053\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u771f\u7684\u4e0d\u77e5\u9053\u554a\uff0c\u5230\u5e95\u751a\u4e48\u4e8b\u5f97\u7f6a\u4f60\u4e86\uff1f\u201d\u9752\u9752\u522b\u5f00\u5934\u4e0d\u7406\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77e5\u5979\u813e\u6c14\uff0c\u5018\u82e5\u7ee7\u7eed\u8ffd\u95ee\uff0c\u5979\u603b\u4e0d\u80af\u7b54\uff0c\u4e0d\u5982\u88c5\u4f5c\u6beb\u4e0d\u5728\u4e4e\uff0c\u5979\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u53cd\u4f1a\u81ea\u5df1\u8bf4\u51fa\u6765\uff0c\u4e8e\u662f\u6362\u4e86\u8bdd\u9898\uff0c\u8bf4\u9053\uff1a\u201c\u6d0b\u5175\u706b\u5668\u5389\u5bb3\uff0c\u4f60\u770b\u7528\u751a\u4e48\u6cd5\u5b50\uff0c\u624d\u80fd\u62a2\u52ab\u4ed6\u4eec\u7684\u5927\u70ae\u5230\u624b\uff1f\u201d\u9752\u9752\u55d4\u9053\uff1a\u201c\u8c01\u8ddf\u4f60\u8bf4\u8fd9\u4e2a\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u6211\u8ddf\u6c99\u5929\u5e7f\u4ed6\u4eec\u5546\u91cf\u53bb\u3002\u201d\u7ad9\u8d77\u8eab\u8981\u8d70\uff0c\u9752\u9752\u4e00\u628a\u6293\u4f4f\u4ed6\u7684\u8863\u89d2\uff0c\u9053\uff1a\u201c\u4e0d\u8bb8\u4f60\u8d70\uff0c\u8bdd\u6ca1\u8bf4\u5b8c\u5462\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b11\u7b11\uff0c\u53c8\u5750\u4e86\u4e0b\u6765\u3002\u9694\u4e86\u826f\u4e45\uff0c\u9752\u9752\u9053\uff1a\u201c\u4f60\u90a3\u5c0f\u6167\u59b9\u59b9\u5462\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u5929\u5206\u624b\u540e\u8fd8\u6ca1\u89c1\u8fc7\uff0c\u4e0d\u77e5\u9053\u5979\u5728\u54ea\u91cc\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u8ddf\u5979\u5988\u8bf4\u4e86\u4e00\u591c\u8bdd\uff0c\u820d\u4e0d\u5f97\u5206\u5f00\uff0c\u5b9a\u662f\u4e0d\u4f4f\u53e3\u7684\u8bb2\u5979\u4e86\u3002\u201d\u8881\u627f\u5fd7\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u5979\u751f\u6c14\u4e3a\u7684\u662f\u8fd9\u4e2a\uff0c\u4e8e\u662f\u8bda\u8bda\u6073\u6073\u7684\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u6211\u5bf9\u4f60\u7684\u5fc3\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u9752\u9752\u53cc\u988a\u6655\u7ea2\uff0c\u8f6c\u8fc7\u4e86\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53c8\u9053\uff1a\u201c\u6211\u4ee5\u540e\u6c38\u8fdc\u4e0d\u4f1a\u79bb\u5f00\u4f60\u7684\uff0c\u4f60\u653e\u5fc3\u597d\u5566\uff01\u201d \u3000\u3000\u9752\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u600e\u4e48\u4f60\u2026\u2026\u8ddf\u4f60\u90a3\u5c0f\u6167\u59b9\u59b9\u2026\u2026\u53c8\u8fd9\u6837\u597d\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5e7c\u5c0f\u4e4b\u65f6\uff0c\u5979\u5988\u5988\u5f85\u6211\u5f88\u597d\uff0c\u5c31\u5f53\u6211\u662f\u5979\u513f\u5b50\u4e00\u822c\uff0c\u6211\u81ea\u7136\u611f\u6fc0\u3002\u518d\u8bf4\uff0c\u4f60\u4e0d\u89c1\u5979\u8ddf\u6211\u90a3\u4e2a\u5e08\u4f84\u5f88\u8981\u597d\u4e48\uff1f\u201d \u3000\u3000\u9752\u9752\u5634\u4e00\u6241\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u90a3\u4e2a\u59d3\u5d14\u7684\u5c0f\u5b50\uff1f\u4ed6\u53c8\u50bb\u53c8\u6ca1\u672c\u4e8b\uff0c\u751f\u5f97\u53c8\u96be\u770b\uff0c\u5979\u4e3a\u751a\u4e48\u559c\u6b22\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u9752\u83dc\u841d\u535c\uff0c\u5404\u4eba\u6240\u7231\u3002\u6211\u8fd9\u59d3\u8881\u7684\u5c0f\u5b50\u53c8\u50bb\u53c8\u6ca1\u672c\u4e8b\uff0c\u751f\u5f97\u53c8\u96be\u770b\uff0c\u4f60\u600e\u4e48\u5374\u559c\u6b22\u6211\u5462\uff1f\u201d\u9752\u9752\u55e4\u7684\u4e00\u58f0\u7b11\uff0c\u5550\u9053\uff1a\u201c\u5478\uff0c\u4e0d\u5bb3\u81ca\uff0c\u8c01\u559c\u6b22\u4f60\u5440\uff1f\u201d \u3000\u3000\u7ecf\u8fc7\u8fd9\u4e00\u573a\u5c0f\u5c0f\u98ce\u6ce2\uff0c\u4e24\u4eba\u8a00\u5f52\u4e8e\u597d\uff0c\u60c5\u610f\u5374\u53c8\u6df1\u4e86\u4e00\u5c42\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5403\u996d\u53bb\u5427\uff01\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u8fd8\u95ee\u4f60\u4e00\u53e5\u8bdd\uff0c\u4f60\u8bf4\u963f\u4e5d\u90a3\u5c0f\u59d1\u5a18\u7f8e\u4e0d\u7f8e\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5979\u7f8e\u4e0d\u7f8e\uff0c\u8ddf\u6211\u6709\u751a\u4e48\u76f8\u5e72\uff1f\u8fd9\u4eba\u884c\u8e2a\u8be1\u79d8\uff0c\u54b1\u4eec\u5012\u8981\u5c0f\u5fc3\u7740\u3002\u201d\u9752\u9752\u70b9\u70b9\u5934\u3002\u4e24\u4eba\u91cd\u53c8\u5230\u4f17\u4eba\u7684\u684c\u8fb9\u5165\u5ea7\uff0c\u548c\u6c99\u5929\u5e7f\u3001\u7a0b\u9752\u7af9\u7b49\u5546\u8bae\u5982\u4f55\u52ab\u593a\u5927\u70ae\u3002 \u3000\u3000\u80e1\u6842\u5357\u9053\uff1a\u201c\u4eca\u665a\u8ba9\u5c0f\u5f1f\u53bb\u63a2\u63a2\uff0c\u4e58\u673a\u5077\u51e0\u652f\u67aa\u6765\u3002\u4eca\u5929\u62ff\u51e0\u652f\uff0c\u660e\u5929\u62ff\u51e0\u652f\uff0c\u6162\u6162\u7684\u628a\u6d0b\u67aa\u5077\u5b8c\uff0c\u5c31\u4e0d\u6015\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6b64\u8ba1\u5927\u5999\uff0c\u6211\u8ddf\u4f60\u540c\u53bb\u77a7\u77a7\u3002\u201d\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u76df\u4e3b\u4f55\u5fc5\u4eb2\u81ea\u51fa\u9a6c\uff1f\u5f85\u5c0f\u5f1f\u53bb\u597d\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u60f3\u77a7\u660e\u767d\u706b\u5668\u7684\u7528\u6cd5\uff0c\u706b\u67aa\u5077\u5230\u624b\uff0c\u5c31\u53ef\u7528\u6d0b\u67aa\u6765\u6253\u6d0b\u5175\u3002\u201d\u4f17\u4eba\u70b9\u5934\u79f0\u662f\u3002\u9752\u9752\u7b11\u9053\uff1a\u201c\u4ed6\u8fd8\u60f3\u5077\u77a7\u4e00\u4e0b\u90a3\u4e2a\u897f\u6d0b\u7f8e\u4eba\u513f\u3002\u201d\u4f17\u4eba\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5f53\u65e5\u4e0b\u5348\uff0c\u8881\u627f\u5fd7\u4e0e\u80e1\u6842\u5357\u4e58\u9a6c\u6298\u56de\uff0c\u8fdc\u8fdc\u8ddf\u7740\u6d0b\u5175\u5927\u961f\uff0c\u773c\u89c1\u4ed6\u4eec\u5728\u5ba2\u5e97\u4e2d\u6295\u5bbf\uff0c\u5019\u5230\u4e09\u66f4\u65f6\u5206\uff0c\u8d8a\u5899\u8fdb\u4e86\u5ba2\u5e97\u3002 \u3000\u3000\u4e00\u4e0b\u5c4b\uff0c\u5c31\u542c\u5f97\u5175\u5203\u649e\u51fb\u4e4b\u58f0\uff0c\u9535\u9535\u4e0d\u7edd\uff0c\u4ece\u4e00\u95f4\u623f\u4e2d\u4f20\u51fa\u6765\u3002\u4e24\u4eba\u4f0f\u5728\u7a97\u5916\uff0c\u4ece\u7a97\u7f1d\u4e2d\u5411\u5185\u5f20\u671b\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a\u897f\u6d0b\u519b\u5b98\u5404\u633a\u957f\u5251\uff0c\u6b63\u5728\u6fc0\u6597\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e07\u60f3\u4e0d\u5230\u8fd9\u4e24\u4eba\u7adf\u4f1a\u540c\u5ba4\u64cd\u6208\uff0c\u751a\u89c9\u5947\u602a\uff0c\u5f53\u4e0b\u9759\u4f0f\u89c2\u6218\u3002\u770b\u4e86\u6570\u5341\u62db\uff0c\u89c1\u96f7\u8499\u653b\u52bf\u51cc\u5389\uff0c\u5251\u6cd5\u950b\u9510\uff0c\u5f7c\u5f97\u5374\u51b7\u9759\u5f02\u5e38\uff0c\u867d\u7136\u4e00\u5473\u62db\u67b6\u9000\u5b88\uff0c\u4f46\u53ea\u8981\u4e00\u51fa\u624b\u8fd8\u51fb\uff0c\u90a3\u4fbf\u62db\u62db\u72e0\u8fa3\u3002\u8881\u627f\u5fd7\u77e5\u9053\u65f6\u95f4\u4e00\u4e45\uff0c\u90a3\u5e74\u957f\u519b\u5b98\u5b9a\u5c06\u843d\u8d25\u3002 \u3000\u3000\u679c\u7136\u6597\u5230\u5206\u9645\uff0c\u5f7c\u5f97\u56de\u5251\u5411\u5de6\u51fb\u523a\uff0c\u4e58\u5bf9\u65b9\u5251\u8eab\u6643\u52a8\uff0c\u7a81\u7136\u53cd\u5251\u76f4\u523a\u3002\u96f7\u8499\u5fd9\u6536\u5251\u56de\u6321\uff0c\u5251\u8eab\u6b6a\u4e86\u3002\u5f7c\u5f97\u81ea\u4e0b\u5411\u4e0a\u731b\u529b\u4e00\u64a9\uff0c\u96f7\u8499\u957f\u5251\u767b\u65f6\u8131\u624b\u3002\u5f7c\u5f97\u62a2\u4e0a\u8e0f\u4f4f\u654c\u5251\uff0c\u624b\u4e2d\u5251\u5c16\u6307\u7740\u5bf9\u65b9\u80f8\u819b\uff0c\u53fd\u53fd\u5495\u5495\u7684\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002\u96f7\u8499\u6c14\u5f97\u8eab\u5b50\u53d1\u98a4\uff0c\u5583\u5583\u5492\u9a82\u3002\u5f7c\u5f97\u628a\u5730\u4e0b\u957f\u5251\u62fe\u8d77\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u8f6c\u8eab\u5f00\u95e8\u51fa\u53bb\u3002 \u3000\u3000\u96f7\u8499\u63d0\u5251\u5728\u5ba4\u4e2d\u6a2a\u780d\u76f4\u5288\uff0c\u4e0d\u4f4f\u7684\u9a82\u4eba\uff0c\u5ffd\u7136\u505c\u624b\uff0c\u8138\u6709\u559c\u8272\uff0c\u5f00\u95e8\u51fa\u53bb\u62ff\u4e86\u4e00\u67c4\u94c1\u94f2\uff0c\u5728\u5730\u4e0b\u6316\u6398\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u80e1\u6842\u5357\u672c\u60f3\u79bb\u5f00\uff0c\u8fd9\u65f6\u5012\u60f3\u770b\u4e2a\u7a76\u7adf\uff0c\u770b\u4ed6\u8981\u57cb\u85cf\u751a\u4e48\u4e1c\u897f\uff0c\u53ea\u89c1\u4ed6\u6398\u4e86\u597d\u4e00\u9635\uff0c\u6316\u4e86\u4e2a\u5f84\u957f\u4e24\u5c3a\u7684\u6d1e\u7a74\uff0c\u6316\u51fa\u6765\u7684\u6ce5\u571f\u90fd\u63b7\u5230\u4e86\u5e8a\u4e0b\uff0c\u6316\u4e86\u4e24\u5c3a\u6765\u6df1\u65f6\uff0c\u5c31\u4f4f\u624b\u4e0d\u6316\u4e86\uff0c\u6495\u4e0b\u4e00\u5757\u88ab\u5355\uff0c\u7f69\u5728\u6d1e\u4e0a\uff0c\u5148\u5728\u56db\u5468\u7528\u6ce5\u571f\u6309\u5b9e\uff0c\u7136\u540e\u5728\u88ab\u5355\u4e0a\u94fa\u4e86\u8584\u8584\u4e00\u5c42\u6ce5\u571f\u3002\u4ed6\u51b7\u7b11\u51e0\u58f0\uff0c\u5f00\u95e8\u51fa\u5ba4\u3002\u8881\u627f\u5fd7\u548c\u80e1\u6842\u5357\u5fc3\u4e2d\u8001\u5927\u7eb3\u95f7\uff0c\u4e0d\u77e5\u4ed6\u5728\u4f7f\u751a\u4e48\u897f\u6d0b\u5996\u6cd5\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u96f7\u8499\u53c8\u8fdb\u5ba4\u6765\uff0c\u5f7c\u5f97\u8ddf\u5728\u540e\u9762\u3002\u53ea\u89c1\u96f7\u8499\u58f0\u8272\u4ff1\u5389\u7684\u8bf4\u8bdd\uff0c\u5f7c\u5f97\u5374\u53ea\u662f\u6447\u5934\u3002\u7a81\u7136\u95f4\u556a\u7684\u4e00\u58f0\uff0c\u96f7\u8499\u4f38\u624b\u6253\u4e86\u4ed6\u4e00\u8bb0\u8033\u5149\u3002\u5f7c\u5f97\u5927\u6012\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u4e24\u4eba\u53c8\u6597\u4e86\u8d77\u6765\u3002 \u3000\u3000\u96f7\u8499\u4e0d\u4f4f\u79fb\u52a8\u811a\u6b65\uff0c\u6162\u6162\u628a\u5f7c\u5f97\u5f15\u5411\u5751\u8fb9\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8fd9\u624d\u604d\u7136\uff0c\u539f\u6765\u6b64\u4eba\u660e\u6253\u4e0d\u8d62\uff0c\u4fbf\u6697\u8bbe\u9677\u9631\uff0c\u4ed6\u65e2\u5982\u6b64\u5904\u5fc3\u79ef\u8651\uff0c\u90a3\u662f\u975e\u6740\u5bf9\u65b9\u4e0d\u53ef\u4e86\u3002\u8881\u627f\u5fd7\u5bf9\u8fd9\u4e24\u4eba\u672c\u65e0\u597d\u6076\uff0c\u4f46\u89c1\u96f7\u8499\u4f7f\u5978\uff0c\u4e0d\u89c9\u6fc0\u52a8\u4e86\u4fa0\u4e49\u4e4b\u5fc3\u3002\u53ea\u89c1\u96f7\u8499\u6570\u5251\u76f4\u523a\uff0c\u90fd\u88ab\u5f7c\u5f97\u67b6\u4f4f\u3002\u5f7c\u5f97\u53cd\u653b\u4e00\u5251\uff0c\u96f7\u8499\u9000\u4e86\u4e24\u6b65\u3002\u5f7c\u5f97\u53f3\u811a\u62a2\u8fdb\uff0c\u5df2\u8e0f\u5728\u9677\u9631\u4e4b\u4e0a\uff0c\u201c\u554a\u201d\u7684\u4e00\u58f0\u5927\u53eb\uff0c\u5411\u524d\u6454\u8dcc\u3002 \u3000\u3000\u96f7\u8499\u56de\u5251\u76f4\u523a\u4ed6\u80cc\u5fc3\uff0c\u773c\u89c1\u8fd9\u4e00\u5251\u8981\u4ece\u540e\u80cc\u76f4\u901a\u5230\u524d\u5fc3\uff0c\u8881\u627f\u5fd7\u65e9\u5df2\u6709\u5907\uff0c\u6025\u63a8\u7a97\u683c\uff0c\u98de\u8eab\u8dc3\u8fdb\uff0c\u91d1\u86c7\u5251\u9012\u51fa\uff0c\u5251\u5934\u86c7\u820c\u94a9\u4f4f\u96f7\u8499\u7684\u5251\u8eab\u5411\u540e\u4e00\u62c9\u3002\u5f7c\u5f97\u5f97\u8131\u5927\u96be\uff0c\u7acb\u5373\u8dc3\u8d77\uff0c\u53f3\u811a\u5374\u5df2\u626d\u8131\u4e86\u81fc\u3002\u96f7\u8499\u529f\u8d25\u5782\u6210\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u633a\u5251\u5411\u8881\u627f\u5fd7\u523a\u6765\u3002\u8881\u627f\u5fd7\u4e00\u58f0\u51b7\u7b11\uff0c\u91d1\u86c7\u5b9d\u5251\u5de6\u53f3\u6643\u52a8\uff0c\u53ea\u542c\u94ee\u94ee\u94ee\u4e4b\u58f0\u4e0d\u7edd\uff0c\u96f7\u8499\u7684\u5251\u8eab\u88ab\u91d1\u86c7\u5251\u534a\u5bf8\u534a\u5bf8\u7684\u524a\u4e0b\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u524a\u5269\u77ed\u77ed\u4e00\u622a\u3002\u96f7\u8499\u6b63\u81ea\u53d1\u5446\uff0c\u8881\u627f\u5fd7\u62a2\u4e0a\u53bb\u62ff\u4f4f\u4ed6\u624b\u8155\uff0c\u4e00\u628a\u63d0\u8d77\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u63b7\u5165\u4e86\u4ed6\u81ea\u5df1\u6240\u6398\u7684\u9677\u5751\u4e4b\u4e2d\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8dc3\u51fa\u7a97\u53bb\u3002 \u3000\u3000\u80e1\u6842\u5357\u4ece\u540e\u8ddf\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4f60\u77a7\u3002\u201d\u53cc\u624b\u63d0\u8d77\uff0c\u62ff\u7740\u4e09\u628a\u77ed\u67aa\u3002\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\uff1f\u201d\u80e1\u6842\u5357\u5411\u7a97\u91cc\u6307\u6307\u3002 \u3000\u3000\u539f\u6765\u8881\u627f\u5fd7\u51fa\u624b\u6551\u4eba\u4e4b\u65f6\uff0c\u80e1\u6842\u5357\u8ddf\u7740\u8fdb\u6765\uff0c\u5fd9\u4e71\u4e4b\u4e2d\uff0c\u4e58\u65f6\u5c06\u4e24\u4e2a\u897f\u6d0b\u519b\u5b98\u7684\u4e09\u628a\u77ed\u67aa\u90fd\u5077\u4e86\u6765\u3002\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u771f\u4e0d\u6127\u5723\u624b\u795e\u5077\u4e4b\u540d\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d76\u56de\u548c\u4f17\u4eba\u76f8\u4f1a\u3002\u9752\u9752\u62ff\u7740\u4e00\u628a\u77ed\u67aa\u73a9\u5f04\uff0c\u65e0\u610f\u4e2d\u5728\u67aa\u6263\u4e0a\u4e00\u6273\uff0c\u53ea\u542c\u5f97\u8f70\u7684\u4e00\u58f0\uff0c\u70df\u96fe\u5f25\u6f2b\u3002\u6c99\u5929\u5e7f\u5750\u5728\u5979\u7684\u5bf9\u9762\uff0c\u5e78\u800c\u8eab\u624b\u654f\u6377\uff0c\u6025\u5fd9\u7f29\u5934\uff0c\u4e00\u9876\u5934\u5dfe\u6253\u4e86\u4e0b\u6765\uff0c\u7099\u5f97\u6ee1\u8138\u90fd\u662f\u706b\u836f\u7070\u3002\u9752\u9752\u5927\u60ca\u5931\u8272\uff0c\u8fde\u8fde\u9053\u6b49\u3002\u6c99\u5929\u5e7f\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff01\u201d \u3000\u3000\u4f17\u4eba\u628a\u53e6\u5916\u4e24\u628a\u77ed\u67aa\u62ff\u6765\u7ec6\u770b\uff0c\u89c1\u67aa\u819b\u4e2d\u88c5\u7740\u706b\u836f\u94c5\u4e38\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u706b\u836f\u672c\u662f\u4e2d\u56fd\u7269\u4e8b\u3002\u54b1\u4eec\u7528\u6765\u6253\u730e\u505a\u97ad\u70ae\uff0c\u897f\u6d0b\u4eba\u5b66\u5230\u4e4b\u540e\u5374\u62ff\u6765\u6740\u4eba\u3002\u8fd9\u961f\u6d0b\u5175\u6709\u4e00\u767e\u591a\u4eba\uff0c\u4e00\u767e\u591a\u652f\u67aa\u653e\u5c06\u8d77\u6765\uff0c\u53ef\u4e0d\u662f\u73a9\u7684\u3002\u201d\u5404\u4eba\u5747\u89c9\u706b\u5668\u5389\u5bb3\uff0c\u4e0d\u80fd\u4ee5\u6b66\u529f\u4e0e\u4e4b\u5bf9\u654c\uff0c\u4e00\u65f6\u9ed8\u7136\u65e0\u8bed\uff0c\u6c89\u601d\u5bf9\u7b56\u3002 \u3000\u3000\u80e1\u6842\u5357\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u6211\u6709\u4e2a\u4e0a\u4e0d\u5f97\u53f0\u76d8\u7684\u8be1\u8ba1\uff0c\u4e0d\u77e5\u884c\u4e0d\u884c\uff1f\u201d\u94c1\u7f57\u6c49\u7b11\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u4e0d\u4f1a\u6709\u751a\u4e48\u6b63\u7ecf\u4e3b\u610f\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u80e1\u5927\u54e5\u4e14\u8bf4\u6765\u542c\u542c\u3002\u201d\u80e1\u6842\u5357\u7b11\u7740\u8bf4\u4e86\u3002\u9752\u9752\u9996\u5148\u62cd\u624b\u8d5e\u597d\u3002\u6c99\u5929\u5e7f\u7b49\u4e5f\u90fd\u8bf4\u5999\u8ba1\u3002\u8881\u627f\u5fd7\u4ed4\u7ec6\u4e00\u60f3\uff0c\u9887\u89c9\u6b64\u8ba1\u53ef\u884c\uff0c\u4e8e\u662f\u4e0b\u4ee4\u5206\u5934\u5e03\u7f6e\u3002 \u3000\u3000\u90a3\u897f\u6d0b\u5973\u5b50\u82e5\u514b\u7433\u7684\u7236\u4eb2\u672c\u662f\u6fb3\u95e8\u8461\u8404\u7259\u56fd\u519b\u5b98\uff0c\u5df2\u4e8e\u5e74\u524d\u901d\u4e16\u3002\u5979\u8fd9\u6b21\u8981\u642d\u4e58\u8fd0\u9001\u5927\u70ae\u7684\u6d77\u8239\u56de\u5f52\u672c\u56fd\uff0c\u56e0\u6b64\u968f\u540c\u9001\u70ae\u519b\u961f\u5317\u4e0a\uff0c\u518d\u8d74\u5929\u6d25\u4e0a\u8239\u3002\u5f7c\u5f97\u662f\u5979\u7236\u4eb2\u7684\u90e8\u5c5e\uff0c\u4e0e\u82e5\u514b\u7433\u76f8\u7231\u5df2\u4e45\u3002\u96f7\u8499\u6765\u81ea\u8461\u56fd\u672c\u571f\uff0c\u4e00\u89c1\u4e4b\u4e0b\uff0c\u4fbf\u60f3\u6a2a\u5200\u593a\u7231\u3002\u4ed6\u867d\u5b98\u9636\u8f83\u9ad8\uff0c\u81ea\u8d1f\u98ce\u6d41\uff0c\u5374\u65e0\u4ece\u63d2\u624b\uff0c\u607c\u7f9e\u6210\u6012\u4e4b\u4f59\uff0c\u4fbf\u5411\u60c5\u654c\u6311\u6218\uff0c\u6bd4\u5251\u65f6\u64cd\u4e4b\u8fc7\u6025\uff0c\u53cd\u81f4\u5931\u624b\uff0c\u800c\u884c\u4f7f\u8be1\u8ba1\uff0c\u53c8\u88ab\u8881\u627f\u5fd7\u7a81\u6765\u95ef\u7834\u3002\u5f7c\u5f97\u89c1\u4ed6\u662f\u4e0a\u53f8\uff0c\u4e0d\u6562\u600e\u6837\uff0c\u53ea\u6709\u52a0\u7d27\u63d0\u9632\u3002 \u3000\u3000\u8fd9\u65e5\u6765\u5230\u4e00\u5904\u5927\u6751\u5e84\u4e07\u516c\u6751\uff0c\u5728\u6751\u4e2d\u201c\u4e07\u6c0f\u5b97\u7960\u201d\u6b47\u5bbf\u3002 \u3000\u3000\u7761\u5230\u534a\u591c\uff0c\u5ffd\u542c\u5f97\u4eba\u58f0\u55a7\u54d7\uff0c\u653e\u54e8\u7684\u6d0b\u5175\u5954\u8fdb\u6765\u8bf4\u6751\u4e2d\u5931\u706b\u3002 \u3000\u3000\u96f7\u8499\u4e0e\u5f7c\u5f97\u6025\u5fd9\u8d77\u6765\uff0c\u89c1\u706b\u5934\u5df2\u70e7\u5f97\u751a\u8fd1\uff0c\u5fd9\u547d\u4f17\u5175\u5c06\u706b\u836f\u6876\u642c\u51fa\u7960\u5802\uff0c\u653e\u4e8e\u7a7a\u5730\u3002\u5fd9\u4e71\u4e2d\u89c1\u4f17\u4e61\u6c11\u63d0\u4e86\u6c34\u6876\u6551\u706b\uff0c\u6570\u5341\u540d\u5927\u6c49\u95ef\u8fdb\u7960\u5802\uff0c\u5230\u5904\u6cfc\u6c34\u3002\u96f7\u8499\u559d\u95ee\u539f\u56e0\u3002\u4f17\u4e61\u6c11\u5bf9\u4f20\u8bd1\u94b1\u901a\u56db\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u7956\u5148\u7684\u7960\u5802\uff0c\u5148\u6cfc\u4e0a\u6c34\uff0c\u514d\u5f97\u706b\u5934\u5ef6\u70e7\u8fc7\u6765\u3002\u201d\u96f7\u8499\u89c9\u5f97\u6709\u7406\uff0c\u4e5f\u5c31\u4e0d\u52a0\u5e72\u6d89\u3002\u54ea\u77e5\u4f17\u4e61\u6c11\u4fe1\u624b\u4e71\u6cfc\uff0c\u4e00\u6876\u6876\u6c34\u5c3d\u5f80\u706b\u836f\u4e0a\u5012\u53bb\u3002\u6d0b\u5175\u62ff\u8d77\u67aa\u6746\u8d76\u6253\uff0c\u8d76\u5f00\u4e00\u4e2a\u53c8\u6765\u4e00\u4e2a\uff0c\u4e0d\u5230\u4e00\u987f\u996d\u529f\u592b\uff0c\u7960\u5802\u5185\u5916\u4e00\u7247\u6c6a\u6d0b\uff0c\u706b\u836f\u6876\u548c\u5927\u70ae\u3001\u67aa\u652f\uff0c\u65e0\u4e00\u4e0d\u662f\u6dcb\u5f97\u6e7f\u900f\uff0c\u706b\u52bf\u5374\u6e10\u6e10\u7184\u4e86\u3002 \u3000\u3000\u4e71\u5230\u9ece\u660e\uff0c\u96f7\u8499\u548c\u5f7c\u5f97\u89c1\u4e61\u6c11\u4e3e\u52a8\u6709\u5f02\uff0c\u706b\u836f\u53c8\u90fd\u6dcb\u6e7f\uff0c\u5fc3\u60f3\u8fd9\u5730\u65b9\u6709\u70b9\u90aa\u95e8\uff0c\u8fd8\u662f\u53ca\u65e9\u79bb\u53bb\u4e3a\u5999\uff0c\u6b63\u8981\u4e0b\u4ee4\u5f00\u62d4\uff0c\u4e00\u540d\u5c0f\u519b\u5b98\u6765\u62a5\uff0c\u62d6\u70ae\u62c9\u8f66\u7684\u7272\u53e3\u6628\u665a\u5728\u6df7\u4e71\u4e2d\u7adf\u7136\u5c3d\u6570\u9003\u5149\u4e86\u3002\u96f7\u8499\u4e3e\u8d77\u9a6c\u97ad\u4e71\u6253\uff0c\u9a82\u4ed6\u4e0d\u5c0f\u5fc3\uff0c\u547d\u94b1\u901a\u56db\u5e26\u9886\u6d0b\u5175\u5230\u6751\u4e2d\u5f81\u96c6\u3002\u4e0d\u6599\u6751\u5b50\u867d\u5927\uff0c\u5374\u662f\u4e00\u5934\u7272\u53e3\u4e5f\u6ca1\u6709\uff0c\u60f3\u662f\u65e9\u5df2\u5f97\u5230\u98ce\u58f0\uff0c\u628a\u7272\u53e3\u90fd\u85cf\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5c31\u65e0\u6cd5\u8d77\u884c\uff0c\u96f7\u8499\u547d\u5f7c\u5f97\u5e26\u4e86\u94b1\u901a\u56db\uff0c\u5230\u524d\u9762\u5e02\u9547\u53bb\u8c03\u96c6\u7272\u53e3\u3002 \u3000\u3000\u96f7\u8499\u7763\u7387\u58eb\u5175\uff0c\u6253\u5f00\u706b\u836f\u6876\uff0c\u628a\u706b\u836f\u5012\u51fa\u6765\u6652\u3002\u6652\u5230\u508d\u665a\uff0c\u706b\u836f\u5df2\u5e72\uff0c\u4f17\u5175\u6b63\u8981\u6536\u5165\u6876\u4e2d\uff0c\u7a81\u7136\u6c11\u623f\u4e2d\u629b\u51fa\u6570\u5341\u6839\u706b\u628a\uff0c\u6295\u5165\u706b\u836f\u5806\u4e2d\uff0c\u767b\u65f6\u70c8\u7130\u51b2\u5929\u3002\u4f17\u6d0b\u5175\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u7eb7\u7eb7\u5954\u9003\uff0c\u4e71\u6210\u4e00\u56e2\u3002\u96f7\u8499\u8fde\u58f0\u4e0b\u4ee4\uff0c\u7ea6\u675f\u58eb\u5175\uff0c\u5f80\u6c11\u623f\u653e\u5c04\u6392\u67aa\u3002\u70df\u96fe\u5f25\u6f2b\u4e2d\u53ea\u89c1\u6570\u5341\u540d\u5927\u6c49\u7a9c\u5165\u6797\u4e2d\u4e0d\u89c1\u4e86\u3002\u96f7\u8499\u68c0\u70b9\u706b\u836f\uff0c\u5df2\u70e7\u53bb\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u5341\u5206\u61ca\u4e27\u3002\u7b49\u5230\u7b2c\u4e09\u65e5\u4e0b\u5348\uff0c\u5f7c\u5f97\u624d\u5f81\u4e86\u6570\u5341\u5339\u9aa1\u9a6c\u6765\u62d6\u62c9\u5927\u70ae\u3002 \u3000\u3000\u5728\u8def\u4e0a\u884c\u4e86\u56db\u4e94\u65e5\uff0c\u8fd9\u5929\u6765\u5230\u4e00\u6761\u5c71\u5ce1\u9669\u9053\uff0c\u773c\u89c1\u662f\u6781\u9661\u7684\u4e0b\u5c71\u8def\uff0c\u96f7\u8499\u4e0e\u5f7c\u5f97\u6307\u6325\u58eb\u5175\uff0c\u6bcf\u4e00\u5c0a\u5927\u70ae\u7531\u5341\u540d\u58eb\u5175\u7528\u5de8\u7d22\u5728\u540e\u62d6\u4f4f\uff0c\u4ee5\u9632\u5c71\u8def\u8fc7\u9661\uff0c\u5927\u70ae\u5815\u8dcc\u3002\u5c71\u8def\u8d8a\u8d70\u8d8a\u9669\uff0c\u4f17\u4eba\u6b63\u81ea\u63d0\u5fc3\u540a\u80c6\uff0c\u5168\u529b\u62d6\u4f4f\u5927\u70ae\uff0c\u7a81\u7136\u5c71\u51f9\u91cc\u55d6\u55d6\u4e4b\u58f0\u5927\u4f5c\uff0c\u6570\u5341\u652f\u7bad\u5c04\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5341\u591a\u540d\u6d0b\u5175\u7acb\u65f6\u4e2d\u7bad\uff0c\u53e6\u6709\u5341\u591a\u652f\u7bad\u5c04\u5728\u9aa1\u9a6c\u8eab\u4e0a\u3002\u7272\u53e3\u53d7\u75db\uff0c\u5411\u4e0b\u6025\u5954\uff0c\u4f17\u6d0b\u5175\u54ea\u91cc\u62c9\u626f\u5f97\u4f4f\uff1f\u5341\u5c0a\u5927\u70ae\u6bcf\u4e00\u5c0a\u90fd\u662f\u6570\u5343\u65a4\u4e4b\u91cd\uff0c\u8fd9\u4e00\u80a1\u4e0b\u5815\u4e4b\u52bf\u771f\u662f\u975e\u540c\u5c0f\u53ef\u3002\u52a0\u4e4b\u8def\u4e0a\u53c8\u7a81\u7136\u51fa\u73b0\u9677\u5751\uff0c\u8bb8\u591a\u9aa1\u9a6c\u90fd\u8dcc\u5165\u4e86\u5751\u91cc\u3002\u53ea\u542c\u89c1\u8f70\u9686\u4e4b\u58f0\u5927\u4f5c\uff0c\u6700\u540e\u4e24\u5c0a\u5927\u70ae\u5ffd\u7136\u5012\u8f6c\uff0c\u4e00\u8def\u7b4b\u6597\u7ffb\u4e86\u4e0b\u53bb\u3002\u6570\u540d\u6d0b\u5175\u88ab\u538b\u6210\u4e86\u8089\u9171\u3002\u524d\u9762\u7684\u516b\u5c0a\u5927\u70ae\u7acb\u65f6\u5747\u88ab\u5e26\u52a8\u3002 \u3000\u3000\u4f17\u5175\u987e\u4e0d\u5f97\u62b5\u6321\u6765\u88ad\u654c\u4eba\uff0c\u5fd9\u5411\u4e24\u65c1\u4e71\u7a9c\u3002\u6709\u7684\u65e0\u8def\u53ef\u8d70\uff0c\u89c1\u5927\u70ae\u6eda\u4e0b\u6765\u7684\u58f0\u52bf\u9669\u6076\uff0c\u8e0a\u8eab\u4e00\u8df3\uff0c\u8dcc\u5165\u4e86\u6df1\u8c37\u3002\u5341\u5c0a\u5927\u70ae\u7ffb\u7ffb\u6eda\u6eda\uff0c\u5411\u4e0b\u76f4\u51b2\uff0c\u8d8a\u6765\u8d8a\u5feb\u3002\u9aa1\u9a6c\u5728\u524d\u75be\u9a70\uff0c\u4e0d\u4e45\u5c31\u88ab\u5927\u70ae\u8d76\u4e0a\uff0c\u538b\u5f97\u8840\u8089\u6a2a\u98de\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u5de8\u54cd\u9707\u8033\u6b32\u804b\uff0c\u5341\u5c0a\u5927\u70ae\u90fd\u8dcc\u5165\u6df1\u8c37\u53bb\u4e86\u3002 \u3000\u3000\u96f7\u8499\u548c\u5f7c\u5f97\u60ca\u9b42\u752b\u5b9a\uff0c\u56de\u987e\u82e5\u514b\u7433\u65f6\uff0c\u89c1\u5979\u5df2\u5413\u5f97\u6655\u4e86\u8fc7\u53bb\u3002\u4e24\u4eba\u6551\u8d77\u4e86\u5979\uff0c\u6307\u6325\u58eb\u5175\u4f0f\u4e0b\u62b5\u654c\u3002\u654c\u4eba\u65e9\u5728\u5761\u4e0a\u6316\u4e86\u6df1\u5751\uff0c\u7528\u5c71\u6ce5\u7b51\u6210\u6321\u58c1\uff0c\u706b\u67aa\u5c04\u53bb\uff0c\u4f24\u4e0d\u5230\u4e00\u6839\u6beb\u6bdb\uff0c\u7fbd\u7bad\u5374\u4e0d\u4f4f\u55d6\u55d6\u5c04\u6765\u3002\u6218\u4e86\u4e24\u4e2a\u591a\u65f6\u8fb0\uff0c\u6d0b\u5175\u59cb\u7ec8\u4e0d\u80fd\u7a81\u56f4\u3002 \u3000\u3000\u96f7\u8499\u9053\uff1a\u201c\u54b1\u4eec\u706b\u836f\u4e0d\u591f\u7528\u4e86\uff0c\u53ea\u5f97\u786c\u51b2\u3002\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u53eb\u94b1\u901a\u56db\u53bb\u95ee\u95ee\uff0c\u8fd9\u4e9b\u571f\u532a\u5230\u5e95\u8981\u751a\u4e48\u3002\u201d\u96f7\u8499\u6012\u9053\uff1a\u201c\u8ddf\u571f\u532a\u6709\u751a\u4e48\u8bf4\u7684\uff1f\u4f60\u4e0d\u6562\u53bb\uff0c\u6211\u6765\u51b2\u3002\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u571f\u532a\u5f13\u7bad\u5389\u5bb3\uff0c\u4f55\u5fc5\u901e\u65e0\u8c13\u7684\u52c7\u6562\uff1f\u201d\u96f7\u8499\u671b\u4e86\u82e5\u514b\u7433\u4e00\u773c\uff0c\u6076\u72e0\u72e0\u7684\u5410\u4e86\u53e3\u553e\u6cab\uff0c\u9a82\u9053\uff1a\u201c\u61e6\u592b\uff0c\u61e6\u592b\uff01\u201d\u5f7c\u5f97\u6c14\u5f97\u9762\u8272\u82cd\u767d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7b49\u6253\u9000\u4e86\u571f\u532a\uff0c\u53eb\u4f60\u77e5\u9053\u65e0\u793c\u7684\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u96f7\u8499\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u662f\u597d\u6c49\u8ddf\u6211\u6765\uff01\u201d\u5f7c\u5f97\u53eb\u9053\uff1a\u201c\u96f7\u8499\u4e0a\u6821\uff0c\u4f60\u60f3\u5bfb\u6b7b\u4e48\uff1f\u201d\u4f17\u6d0b\u5175\u77e5\u9053\u51fa\u53bb\u5c31\u662f\u9001\u6b7b\uff0c\u8c01\u80af\u8ddf\u4ed6\u4e71\u51b2\uff1f\u96f7\u8499\u4ed7\u5251\u5927\u547c\uff0c\u5954\u4e0d\u6570\u6b65\uff0c\u4e00\u7bad\u5c04\u6765\uff0c\u7a7f\u80f8\u800c\u6b7b\u3002 \u3000\u3000\u5f7c\u5f97\u4e0e\u4f17\u6d0b\u5175\u7f29\u5728\u5c71\u6c9f\u91cc\uff0c\u4ed7\u7740\u706b\u5668\u9510\u5229\uff0c\u654c\u4eba\u4e0d\u6562\u903c\u8fd1\uff0c\u50f5\u6301\u4e86\u4e00\u65e5\u4e00\u591c\uff0c\u53ea\u76fc\u5b98\u5175\u6765\u6551\uff0c\u4f46\u5176\u65f6\u5b98\u573a\u8150\u8d25\u5f02\u5e38\uff0c\u82e5\u662f\u8c03\u5175\u9063\u5c06\uff0c\u516c\u6587\u6765\u5f80\uff0c\u53c8\u8981\u8bf7\u793a\uff0c\u53c8\u8981\u5546\u8bae\uff0c\u4e0d\u8fc7\u5341\u5929\u534a\u6708\uff0c\u5b98\u5175\u54ea\u91cc\u80fd\u6765\uff1f \u3000\u3000\u5b88\u5230\u7b2c\u4e8c\u65e5\u508d\u665a\uff0c\u4f17\u5175\u997f\u5f97\u5934\u660f\u773c\u82b1\uff0c\u53ea\u5f97\u7ad6\u8d77\u4e86\u767d\u65d7\u3002 \u3000\u3000\u94b1\u901a\u56db\u9ad8\u58f0\u5927\u53eb\uff1a\u201c\u6211\u4eec\u6295\u964d\u4e86\uff0c\u6d0b\u5927\u4eba\u8bf4\u6295\u964d\u4e86\uff01\u201d\u5c71\u5761\u4e0a\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u628a\u706b\u67aa\u90fd\u629b\u51fa\u6765\u3002\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u4e0d\u80fd\u7f34\u67aa\u3002\u201d \u3000\u3000\u654c\u4eba\u5e76\u4e0d\u7406\u4f1a\uff0c\u4e5f\u4e0d\u518d\u653b\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u4e00\u9635\u8089\u9999\u9152\u9999\uff0c\u968f\u98ce\u98d8\u4e86\u8fc7\u6765\u3002\u4f17\u6d0b\u5175\u5df2\u4e00\u65e5\u4e24\u591c\u6ca1\u5403\u4e1c\u897f\uff0c\u8fd9\u65f6\u54ea\u91cc\u8fd8\u62b5\u53d7\u5f97\u4f4f\uff1f\u7eb7\u7eb7\u628a\u706b\u67aa\u5411\u4e0a\u629b\u53bb\uff0c\u5954\u51fa\u6c9f\u6765\u3002\u5f7c\u5f97\u89c1\u5927\u52bf\u5df2\u53bb\uff0c\u53ea\u5f97\u4e0b\u4ee4\u5f03\u68b0\u6295\u964d\u3002\u4f17\u5175\u628a\u706b\u67aa\u5806\u5728\u4e00\u8d77\uff0c\u5927\u53eb\u5927\u56b7\u8981\u5403\u4e1c\u897f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e24\u8fb9\u5c71\u5761\u4e0a\u53f7\u89d2\u58f0\u54cd\uff0c\u571f\u5751\u4e2d\u7ad9\u8d77\u6570\u767e\u540d\u5927\u6c49\uff0c\u5f2f\u5f13\u642d\u7bad\uff0c\u5bf9\u4f4f\u4e86\u4f17\u6d0b\u5175\u3002\u51e0\u4e2a\u4eba\u7f13\u6b65\u8fc7\u6765\uff0c\u8d70\u5230\u4e34\u8fd1\uff0c\u5f7c\u5f97\u770b\u5f97\u6e05\u695a\uff0c\u5f53\u5148\u4e00\u4eba\u4fbf\u662f\u90a3\u665a\u6551\u4e86\u81ea\u5df1\u6027\u547d\u7684\u5c11\u5e74\u3002\u4ed6\u8eab\u65c1\u90a3\u4eba\u6b63\u662f\u66fe\u88ab\u96f7\u8499\u51fb\u843d\u5934\u5dfe\u7684\u5c11\u5973\u3002\u82e5\u514b\u7433\u53eb\u9053\uff1a\u201c\u554a\uff0c\u5c31\u662f\u8fd9\u6279\u6709\u9b54\u6cd5\u7684\u4eba\uff01\u201d\u5f7c\u5f97\u62d4\u51fa\u4f69\u5251\uff0c\u8d70\u4e0a\u51e0\u6b65\uff0c\u53cc\u624b\u6a2a\u6367\uff0c\u4ea4\u7ed9\u8881\u627f\u5fd7\uff0c\u610f\u793a\u6295\u964d\uff0c\u5fc3\u60f3\u8f93\u5728\u8fd9\u4eba\u624b\u4e0b\u4e5f\u8fd8\u503c\u5f97\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5148\u662f\u4e00\u6123\uff0c\u968f\u5373\u9886\u609f\u8fd9\u662f\u670d\u8f93\u6295\u964d\u4e4b\u610f\uff0c\u4e8e\u662f\u6447\u4e86\u6447\u624b\uff0c\u5bf9\u94b1\u901a\u56db\u9053\uff1a\u201c\u4f60\u5bf9\u4ed6\u8bf4\uff0c\u4ed6\u4eec\u6d0b\u5175\u5e26\u5927\u70ae\u6765\uff0c\u5982\u662f\u5e2e\u52a9\u4e2d\u56fd\u5b88\u536b\u56fd\u571f\uff0c\u62b5\u6297\u5916\u654c\uff0c\u90a3\u4e48\u6211\u4eec\u5f88\u662f\u611f\u8c22\uff0c\u5f53\u4ed6\u4eec\u662f\u597d\u670b\u53cb\u3002\u201d\u94b1\u901a\u56db\u7167\u4ed6\u7684\u8bdd\u8bd1\u4e86\u3002\u5f7c\u5f97\u8fde\u8fde\u70b9\u5934\uff0c\u4f38\u51fa\u624b\u6765\u548c\u8881\u627f\u5fd7\u62c9\u4e86\u62c9\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53c8\u9053\uff1a\u201c\u4f46\u4f60\u4eec\u5230\u6f7c\u5173\u53bb\uff0c\u662f\u5e2e\u7687\u5e1d\u6740\u6211\u4eec\u767e\u59d3\uff0c\u8fd9\u4e2a\u6211\u4eec\u5c31\u4e0d\u8bb8\u4e86\u3002\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u662f\u53bb\u6253\u4e2d\u56fd\u767e\u59d3\u4e48\uff1f\u6211\u5b8c\u5168\u4e0d\u77e5\u9053\u3002\u201d\u8881\u627f\u5fd7\u89c1\u4ed6\u8138\u8272\u8bda\u6073\uff0c\u76f8\u4fe1\u4e0d\u662f\u5047\u8bdd\uff0c\u53c8\u9053\uff1a\u201c\u5168\u4e2d\u56fd\u7684\u767e\u59d3\u5f88\u82e6\uff0c\u6ca1\u6709\u996d\u5403\uff0c\u53ea\u76fc\u671b\u6709\u4eba\u9886\u4ed6\u4eec\u6253\u6389\u7687\u5e1d\uff0c\u8131\u79bb\u82e6\u6d77\u3002\u7687\u5e1d\u6015\u4e86\uff0c\u53eb\u4f60\u4eec\u7528\u5927\u70ae\u53bb\u8f70\u6b7b\u767e\u59d3\u3002\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u6211\u4e5f\u662f\u7a77\u4eba\u51fa\u8eab\uff0c\u77e5\u9053\u7a77\u4eba\u7684\u82e6\u5904\u3002\u6211\u8fd9\u5c31\u56de\u672c\u56fd\u53bb\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u4f60\u628a\u5175\u90fd\u5e26\u8d70\u5427\u3002\u201d \u3000\u3000\u5f7c\u5f97\u4e0b\u4ee4\u96c6\u961f\u3002\u8881\u627f\u5fd7\u547d\u90e8\u4e0b\u62ff\u51fa\u9152\u8089\uff0c\u8ba9\u6d0b\u5175\u9971\u9910\u4e86\u4e00\u987f\u3002\u5f7c\u5f97\u5411\u8881\u627f\u5fd7\u4e3e\u624b\u81f4\u656c\uff0c\u9886\u961f\u4e0a\u5761\u3002\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u5e72\u4e48\u4e0d\u628a\u706b\u67aa\u5e26\u8d70\uff1f\u201d\u94b1\u901a\u56db\u8bd1\u4e86\u3002\u5f7c\u5f97\u5947\u9053\uff1a\u201c\u90a3\u662f\u4f60\u7684\u6218\u5229\u54c1\u3002\u4f60\u653e\u6211\u4eec\u8d70\uff0c\u4e0d\u8981\u6211\u4eec\u7528\u94b1\u6765\u8d4e\u8eab\uff0c\u6211\u4eec\u5df2\u5f88\u611f\u8c22\u4f60\u7684\u5bbd\u6d2a\u5927\u91cf\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u5931\u4e86\u5927\u70ae\uff0c\u518d\u4e0d\u628a\u67aa\u5e26\u8d70\uff0c\u53ea\u6015\u56de\u53bb\u957f\u5b98\u8d23\u7f5a\u66f4\u91cd\u3002\u62ff\u53bb\u5427\u3002\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u6211\u4eec\u5f00\u67aa\u6253\u4f60\u4eec\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\u3002\u6211\u4eec\u4e2d\u56fd\u4eba\u8bb2\u7a76\u809d\u80c6\u76f8\u7167\uff0c\u65e2\u5f53\u4f60\u662f\u597d\u6c49\u5b50\uff0c\u54ea\u6709\u7591\u5fc3\uff1f\u201d\u5f7c\u5f97\u8fde\u58f0\u9053\u8c22\uff0c\u547d\u58eb\u5175\u53d6\u4e86\u706b\u67aa\uff0c\u5217\u961f\u800c\u53bb\u3002\u4ed6\u4e00\u8def\u4e0a\u5761\uff0c\u8d8a\u60f3\u8d8a\u662f\u611f\u4f69\uff0c\u547d\u4f17\u5175\u5750\u4e0b\u4f11\u606f\uff0c\u548c\u94b1\u901a\u56db\u4e24\u4eba\u53c8\u9a70\u56de\u6765\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u4e2a\u5e03\u5305\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u9601\u4e0b\u5982\u6b64\u8c6a\u6770\uff0c\u6211\u6709\u4e00\u4ef6\u4e1c\u897f\u76f8\u8d60\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6253\u5f00\u5e03\u5305\u4e00\u770b\uff0c\u89c1\u662f\u4e00\u5f20\u6298\u53e0\u7740\u7684\u539a\u7eb8\uff0c\u644a\u4e86\u5f00\u6765\uff0c\u539f\u6765\u662f\u4e00\u5e45\u5730\u56fe\uff0c\u56fe\u4e2d\u6240\u7ed8\u7684\u4f3c\u662f\u5927\u6d77\u4e2d\u7684\u4e00\u5ea7\u5c9b\u5c7f\uff0c\u56fe\u4e0a\u6ce8\u4e86\u8bb8\u591a\u5f2f\u5f2f\u66f2\u66f2\u7684\u6587\u5b57\u3002 \u3000\u3000\u5f7c\u5f97\u9053\uff1a\u201c\u8fd9\u662f\u5357\u65b9\u6d77\u4e0a\u7684\u4e00\u5ea7\u5927\u5c9b\uff0c\u79bb\u5f00\u6d77\u5cb8\u6709\u4e00\u5343\u591a\u91cc\u3002\u5c9b\u4e0a\u6c14\u5019\u6e29\u6696\uff0c\u7269\u4ea7\u4e30\u5bcc\uff0c\u771f\u5982\u5929\u5802\u4e00\u6837\u3002\u6211\u822a\u6d77\u65f6\u5230\u8fc7\u90a3\u91cc\u3002\u201d\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u8fd9\u56fe\u662f\u751a\u4e48\u610f\u601d\uff1f\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u5f88\u662f\u8f9b\u82e6\uff0c\u4e0d\u5982\u5e26\u4e86\u4e2d\u56fd\u6ca1\u996d\u5403\u7684\u53d7\u82e6\u767e\u59d3\uff0c\u90fd\u5230\u90a3\u5c9b\u4e0a\u53bb\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u8fd9\u5916\u56fd\u4eba\u5fc3\u5730\u5012\u597d\uff0c\u53ea\u4e0d\u8fc7\u6211\u4eec\u4e2d\u56fd\u6709\u591a\u5927\uff0c\u4ebf\u4e07\u4e4b\u4f17\uff0c\u51ed\u4f60\u518d\u5927\u7684\u5c9b\u4e5f\u5c45\u4f4f\u4e0d\u4e0b\u3002\u201d\u95ee\u9053\uff1a\u201c\u8fd9\u5c9b\u4e0a\u6ca1\u4eba\u4f4f\u4e48\uff1f\u201d\u5f7c\u5f97\u9053\uff1a\u201c\u6709\u65f6\u6709\u897f\u73ed\u7259\u7684\u6d77\u76d7\uff0c\u6709\u65f6\u6ca1\u6709\u3002\u4f60\u4eec\u8fd9\u6837\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u4e5f\u4e0d\u4f1a\u6015\u90a3\u4e9b\u8be5\u6b7b\u7684\u897f\u73ed\u7259\u6d77\u76d7\u3002\u201d\u8881\u627f\u5fd7\u89c1\u4ed6\u4e00\u7247\u8bda\u610f\uff0c\u5c31\u9053\u4e86\u8c22\uff0c\u6536\u8d77\u5730\u56fe\u3002\u5f7c\u5f97\u4f5c\u522b\u800c\u53bb\u3002 \u3000\u3000\u94b1\u901a\u56db\u8f6c\u8fc7\u8eab\u5b50\uff0c\u6b63\u8981\u968f\u540c\u4e0a\u5c71\uff0c\u9752\u9752\u5ffd\u5730\u4f38\u624b\uff0c\u626f\u4f4f\u4ed6\u7684\u8033\u6735\uff0c\u559d\u9053\uff1a\u201c\u4e0b\u6b21\u518d\u89c1\u4f60\u4f5c\u5a01\u4f5c\u798f\uff0c\u6b3a\u4fae\u540c\u80de\uff0c\u5c0f\u5fc3\u4f60\u7684\u72d7\u547d\uff01\u201d\u94b1\u901a\u56db\u8033\u4e0a\u5267\u75db\uff0c\u8fde\u8bf4\uff1a\u201c\u5c0f\u4eba\u4e0d\u6562\uff01\u201d\u4ed6\u53e3\u4e2d\u5c11\u4e86\u8bb8\u591a\u7259\u9f7f\uff0c\u8bf4\u8bdd\u6f0f\u98ce\uff0c\u5012\u4f3c\u8bf4\uff1a\u201c\u5c0f\u4eba\u9887\u6562\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u6307\u6325\u4f17\u4eba\uff0c\u722c\u5230\u6df1\u8c37\u5e95\u4e0b\u53bb\u5bdf\u770b\u5927\u70ae\uff0c\u89c1\u5341\u5c0a\u5de8\u70ae\u4e92\u76f8\u78b0\u649e\uff0c\u90fd\u5df2\u6bc1\u5f97\u4e0d\u6210\u6a21\u6837\uff0c\u65e0\u6cd5\u518d\u7528\uff0c\u4e8e\u662f\u6398\u571f\u76d6\u4e0a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5927\u529f\u544a\u6210\uff0c\u4e0e\u4faf\u98de\u6587\u7b49\u7fa4\u8c6a\u6b22\u805a\u534a\u65e5\uff0c\u75db\u996e\u4e00\u573a\uff0c\u8fd9\u624d\u5206\u624b\u3002\u6b21\u65e5\u4f1a\u9f50\u4e86\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u7b49\u4eba\uff0c\u5411\u5317\u4eac\u8fdb\u53d1\u3002 \u3000\u3000\u8fd9\u4e00\u5f79\u80e1\u6842\u5357\u53a5\u529f\u6700\u4f1f\uff0c\u5f04\u6e7f\u706b\u836f\u3001\u6398\u5751\u9677\u70ae\u7b49\u5de7\u8ba1\u90fd\u662f\u4ed6\u60f3\u51fa\u6765\u7684\u3002\u4f17\u4eba\u4e00\u8def\u4e0a\u5bf9\u4ed6\u79f0\u626c\u5907\u81f3\u3002\u518d\u4e5f\u4e0d\u6562\u8f7b\u89c6\u4ed6\u662f\u5c0f\u5077\u51fa\u8eab\u3002 \u3000\u3000\u6b64\u53bb\u4e00\u8def\u4e4b\u4e0a\uff0c\u4f46\u89c1\u7126\u571f\u6b8b\u57a3\uff0c\u91ce\u72ac\u98df\u5c38\uff0c\u5c3d\u662f\u6e05\u5175\u70e7\u6740\u52ab\u63a0\u7684\u9057\u8ff9\uff0c\u7fa4\u96c4\u65e0\u4e0d\u770b\u5f97\u5fc3\u5934\u706b\u8d77\u3002\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u53ef\u60dc\u90a3\u65e5\u6ca1\u6740\u4e86\u9791\u5b50\u5175\u7684\u5143\u5e05\u963f\u5df4\u6cf0\u3002\u76df\u4e3b\uff0c\u54b1\u4eec\u8d76\u4e0a\u53bb\u523a\u6740\u4ed6\u5982\u4f55\uff1f\u201d\u9752\u9752\u9996\u5148\u4fbf\u9f13\u638c\u53eb\u597d\u3002\u8881\u627f\u5fd7\u6c89\u541f\u4e0d\u7b54\u3002\u9752\u9752\u9053\uff1a\u201c\u53bb\u6740\u4e86\u9791\u5b50\u5175\u5143\u5e05\u6709\u751a\u4e48\u4e0d\u597d\uff1f\u4e5f\u514d\u5f97\u5b59\u4ef2\u5bff\u53d4\u53d4\u8001\u662f\u57cb\u6028\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8981\u523a\u6740\u9791\u5b50\u7684\u5934\u5b50\uff0c\u6740\u5f97\u8d8a\u5927\u8d8a\u597d\uff0c\u54b1\u4eec\u7d22\u6027\u4fbf\u53bb\u523a\u6740\u6ee1\u6e05\u7684\u7687\u5e1d\u7687\u592a\u6781\u3002\u201d\u4f17\u4eba\u4e00\u6014\uff0c\u968f\u5373\u9f50\u58f0\u6b22\u547c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8be6\u7ec6\u8be2\u95ee\u6d2a\u80dc\u6d77\uff0c\u6ee1\u6e05\u7684\u4eac\u57ce\u5982\u4f55\u9632\u536b\uff0c\u5982\u4f55\u65b9\u80fd\u6df7\u5165\u7687\u5bab\u3002\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u6ee1\u6e05\u7684\u4eac\u57ce\u5728\u6c88\u9633\uff0c\u73b0\u4eca\u53eb\u4f5c\u76db\u4eac\uff0c\u90a3\u76db\u4eac\u89c4\u6a21\u7b80\u964b\uff0c\u53ef\u4e07\u4e07\u53ca\u4e0d\u4e0a\u5317\u4eac\u4e86\u3002\u5c0f\u4eba\u5148\u524d\u5728\u777f\u4eb2\u738b\u591a\u5c14\u886e\u624b\u4e0b\u5f53\u5dee\uff0c\u6709\u5757\u8170\u724c\uff0c\u53ef\u4ee5\u76f4\u8fdb\u777f\u4eb2\u738b\u5e9c\uff0c\u7687\u5bab\u5374\u6ca1\u8fdb\u53bb\u8fc7\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5c31\u53bb\u76db\u4eac\uff0c\u5230\u4e86\u4e4b\u540e\u76f8\u673a\u884c\u4e8b\u3002\u201d \u3000\u3000\u4e00\u884c\u4eba\u5148\u5230\u5317\u4eac\uff0c\u5c06\u94c1\u7bb1\u5b89\u987f\u597d\u4e86\uff0c\u6d3e\u9752\u7af9\u5e2e\u7684\u51e0\u540d\u5f97\u529b\u5934\u76ee\u7559\u5b88\uff0c\u5f53\u5373\u51fa\u4eac\uff0c\u5411\u5317\u8fdb\u53d1\uff0c\u4e0d\u4e00\u65e5\u5230\u4e86\u76db\u4eac\u3002 \u3000\u3000\u4f17\u4eba\u5728\u4e00\u5bb6\u5c0f\u5ba2\u5e97\u4e2d\u6b47\u4e86\uff0c\u5546\u8bae\u6df7\u8fdb\u5bab\u4e2d\u4e4b\u7b56\u3002\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u76f8\u516c\uff0c\u4f9d\u5c0f\u4eba\u4e4b\u89c1\uff0c\u8bf7\u4f60\u59d4\u5c48\u4e00\u4e0b\uff0c\u626e\u4f5c\u5c0f\u4eba\u7684\u4f19\u4f34\uff0c\u5148\u53bb\u89c1\u591a\u5c14\u886e\u3002\u4ed6\u662f\u9791\u5b50\u7687\u5e1d\u7684\u4eb2\u5f1f\u5f1f\uff0c\u5728\u5404\u4f4d\u738b\u7237\u4e2d\u6700\u5f97\u5ba0\u4fe1\uff0c\u6743\u529b\u6700\u5927\u3002\u54b1\u4eec\u6216\u80fd\u51ed\u7740\u4ed6\u5e26\u8fdb\u5bab\u53bb\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u591a\u5c14\u886e\u6d3e\u4f60\u9001\u4fe1\u7ed9\u53f8\u793c\u592a\u76d1\u66f9\u5316\u6df3\uff0c\u4f60\u53c8\u600e\u5730\u56de\u62a5\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u5c0f\u4eba\u53ea\u8bf4\u66f9\u5316\u6df3\u8fd8\u6ca1\u80fd\u89c1\u5230\uff0c\u4f46\u5728\u5317\u4eac\u6253\u63a2\u5230\u4e86\u673a\u5bc6\u519b\u60c5\uff0c\u56e0\u6b64\u5148\u884c\u56de\u62a5\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u751a\u4e48\u673a\u5bc6\u519b\u60c5\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u5c0f\u4eba\u80e1\u8bf4\u516b\u9053\u4e00\u756a\uff0c\u8bf4\u662f\u660e\u671d\u7687\u5e1d\u5df2\u5411\u897f\u6d0b\u56fd\u501f\u5175\uff0c\u501f\u6765\u51e0\u767e\u95e8\u5927\u70ae\uff0c\u6570\u5343\u6d0b\u67aa\u961f\uff0c\u65e5\u5185\u5c31\u6765\u653b\u6253\u6ee1\u6e05\u3002\u201d\u8881\u627f\u5fd7\u559c\u9053\uff1a\u201c\u6b64\u8ba1\u5927\u5999\uff0c\u591a\u5c14\u886e\u4e00\u542c\uff0c\u5b9a\u8981\u53bb\u7980\u62a5\u9791\u5b50\u7687\u5e1d\u3002\u201d\u4e8e\u662f\u5411\u9752\u9752\u8981\u4e86\u90a3\u652f\u6d0b\u67aa\uff0c\u5bf9\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u662f\u897f\u6d0b\u5175\u7684\u901a\u8bd1\u94b1\u901a\u56db\uff0c\u56e0\u6b64\u5f97\u6089\u5185\u60c5\u3002\u201d \u3000\u3000\u9752\u9752\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u627f\u5fd7\u54e5\u54e5\uff0c\u4f60\u751a\u4e48\u4eba\u4e0d\u626e\uff0c\u5374\u53bb\u626e\u90a3\u4e2a\u72d7\u901a\u8bd1\u94b1\u901a\u56db\uff0c\u6211\u6253\u6389\u4f60\u6ee1\u5634\u7259\u9f7f\u518d\u8bf4\uff01\u201d\u8bf4\u7740\u4e3e\u8d77\u53f3\u624b\uff0c\u5047\u610f\u5411\u8881\u627f\u5fd7\u5634\u4e0a\u6253\u53bb\u3002\u8881\u627f\u5fd7\u5f20\u53e3\u4fbf\u54ac\uff0c\u9752\u9752\u5fd9\u7f29\u624b\u4e0d\u8fed\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53fd\u54e9\u5495\u565c\u7684\u8bf4\u4e86\u51e0\u53e5\u5192\u5145\u897f\u6d0b\u8bdd\uff0c\u4f17\u4eba\u5c3d\u7686\u5927\u7b11\u3002 \u3000\u3000\u5f53\u65e5\u5348\u540e\uff0c\u8881\u627f\u5fd7\u968f\u540c\u6d2a\u80dc\u6d77\uff0c\u53bb\u777f\u4eb2\u738b\u5e9c\u6c42\u89c1\u738b\u7237\u3002\u591a\u5c14\u886e\u968f\u5373\u4f20\u89c1\u3002\u8881\u627f\u5fd7\u89c1\u90a3\u591a\u5c14\u886e\u4e09\u5341\u4e00\u4e8c\u5c81\u5e74\u7eaa\uff0c\u8eab\u5f62\u9ad8\u7626\uff0c\u4e00\u8138\u7cbe\u608d\u4e4b\u6c14\u3002\u6d2a\u80dc\u6d77\u8ddf\u4ed6\u8bf4\u4e86\u4e00\u9635\u6ee1\u6d32\u8bdd\uff0c\u591a\u5c14\u886e\u679c\u7136\u795e\u8272\u5927\u53d8\uff0c\u968f\u5373\u4ee5\u6c49\u8bed\u8be2\u95ee\u8881\u627f\u5fd7\u3002\u8881\u627f\u5fd7\u53d6\u51fa\u6d0b\u67aa\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u5c06\u5148\u524d\u4e0e\u6d2a\u80dc\u6d77\u5546\u91cf\u597d\u7684\u8a00\u8bed\u8bf4\u4e86\u3002\u591a\u5c14\u886e\u6c89\u541f\u826f\u4e45\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u62a5\u8baf\u6709\u529f\uff0c\u6211\u6709\u91cd\u8d4f\u3002\u8fd9\u5c31\u4e0b\u53bb\u5427\u3002\u660e\u65e5\u518d\u6765\u4f3a\u5019\uff0c\u542c\u53d6\u5429\u5490\u3002\u201d\u4e24\u4eba\u65e0\u5948\uff0c\u53ea\u5f97\u78d5\u5934\u9000\u51fa\u3002 \u3000\u3000\u8881\u627f\u5fd7\u65e0\u7f18\u65e0\u6545\u7684\u5411\u9791\u5b50\u738b\u7237\u78d5\u4e86\u51e0\u4e2a\u5934\uff0c\u5374\u89c1\u4e0d\u5230\u7687\u592a\u6781\uff0c\u56de\u5230\u5ba2\u5e97\uff0c\u5fc3\u4e0b\u8001\u5927\u53d1\u95f7\u3002\u5bfb\u601d\u4e00\u4f1a\uff0c\u8981\u6d2a\u80dc\u6d77\u5e26\u5230\u7687\u5bab\u5916\u53bb\u5bdf\u770b\u4e86\u4e00\u756a\uff0c\u51b3\u610f\u665a\u95f4\u5f84\u884c\u5165\u5bab\u884c\u523a\u3002 \u3000\u3000\u4ed6\u60f3\u6b64\u4e3e\u4e0d\u8bba\u6210\u8d25\uff0c\u6b21\u65e5\u57ce\u4e2d\u5fc5\u5b9a\u5927\u7d22\uff0c\u6349\u62ff\u523a\u5ba2\uff0c\u4e8e\u662f\u8981\u5404\u4eba\u5148\u884c\u51fa\u57ce\uff0c\u7ea6\u5b9a\u660e\u65e5\u5348\u95f4\u5728\u57ce\u5357\u4e8c\u5341\u91cc\u5904\u4e00\u5ea7\u7834\u5e99\u4e2d\u76f8\u4f1a\u3002\u5404\u4eba\u81ea\u77e5\u6b66\u529f\u4e0e\u4ed6\u76f8\u5dee\u592a\u8fdc\uff0c\u591a\u4e00\u4eba\u975e\u4f46\u5e2e\u4e0d\u4e86\u5fd9\uff0c\u53cd\u800c\u6210\u4e3a\u7d2f\u8d58\uff0c\u5355\u662f\u4ed6\u4e00\u4eba\uff0c\u8131\u8eab\u4fbf\u5bb9\u6613\u5f97\u591a\uff0c\u4ff1\u5404\u9075\u547d\uff0c\u53ee\u549b\u4ed6\u52a1\u987b\u5c0f\u5fc3\u3002 \u3000\u3000\u9752\u9752\u51fa\u95e8\u65f6\u5411\u8881\u627f\u5fd7\u51dd\u671b\u7247\u523b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u627f\u5fd7\u54e5\u54e5\uff0c\u9791\u5b50\u7687\u5e1d\u523a\u5f97\u5230\u679c\u7136\u597d\uff0c\u523a\u4e0d\u5230\u4e5f\u5c31\u7f62\u4e86\uff0c\u4f60\u81ea\u5df1\u53ef\u5343\u4e07\u8981\u4fdd\u91cd\u3002\u4f60\u77e5\u9053\uff0c\u5728\u6211\u5fc3\u4e2d\uff0c\u4e00\u767e\u4e2a\u9791\u5b50\u7687\u5e1d\u4e5f\u53ca\u4e0d\u4e0a\u4f60\u4e00\u6839\u5934\u53d1\uff0c\u6211\u82e5\u662f\u4ece\u6b64\u518d\u4e5f\u89c1\u4e0d\u5230\u4f60\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u773c\u5708\u513f\u767b\u65f6\u7ea2\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8981\u8ba9\u5979\u5bbd\u6000\uff0c\u4f38\u624b\u62d4\u4e0b\u5934\u4e0a\u4e00\u6839\u5934\u53d1\uff0c\u7b11\u9053\uff1a\u201c\u6211\u9001\u4e00\u767e\u4e2a\u9791\u5b50\u7687\u5e1d\u7ed9\u4f60\u3002\u201d\u8bf4\u65f6\u5c06\u5934\u53d1\u9012\u5c06\u8fc7\u53bb\u3002\u9752\u9752\u5657\u54e7\u4e00\u7b11\uff0c\u773c\u6cea\u5374\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b49\u5230\u521d\u66f4\u65f6\u5206\uff0c\u643a\u4e86\u91d1\u86c7\u5251\u4e0e\u91d1\u86c7\u9525\uff0c\u6765\u5230\u5bab\u5899\u4e4b\u5916\u3002\u773c\u89c1\u5bab\u5916\u5b88\u536b\u4e25\u5bc6\uff0c\u6084\u6b65\u7ed5\u5230\u4e00\u682a\u5927\u6811\u540e\u8eb2\u8d77\uff0c\u5f85\u536b\u58eb\u5de1\u8fc7\uff0c\u8f7b\u8f7b\u8dc3\u5165\u5bab\u5899\u3002\u773c\u89c1\u6bbf\u9601\u5904\u5904\uff0c\u5374\u4e0d\u77e5\u7687\u592a\u6781\u5c45\u4e8e\u4f55\u5904\uff0c\u4e00\u65f6\u5927\u8d39\u8e0c\u8e87\uff0c\u5fc3\u60f3\u53ea\u6709\u6293\u5230\u4e00\u540d\u536b\u58eb\u6216\u662f\u592a\u76d1\u6765\u903c\u95ee\u3002 \u3000\u3000\u4ed6\u653e\u8f7b\u811a\u6b65\uff0c\u8d70\u4e86\u5c0f\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e0d\u89c1\u4e1d\u6beb\u7aef\u502a\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8270\u96be\u4e07\u5206\uff0c\u600e\u6bd4\u5f97\u5f53\u65e5\u5927\u529f\u574a\u4e2d\u591c\u63a2\uff1f\u52a1\u987b\u6c89\u4f4f\u4e86\u6c14\uff0c\u4eca\u665a\u4e0d\u6210\uff0c\u660e\u665a\u518d\u6765\uff0c\u7eb5\u7136\u987b\u82b1\u4e00\u4e24\u4e2a\u6708\u65f6\u5149\uff0c\u90a3\u4e5f\u4e0d\u59a8\u3002\u201d \u3000\u3000\u8fd9\u4e48\u4e00\u60f3\uff0c\u8d70\u5f97\u66f4\u52a0\u6162\u4e86\uff0c\u7ed5\u8fc7\u4e00\u6761\u56de\u5eca\uff0c\u5ffd\u89c1\u82b1\u4e1b\u4e2d\u706f\u5149\u95ea\u52a8\uff0c\u5fd9\u7f29\u8eab\u5728\u5047\u5c71\u4e4b\u540e\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u53ea\u89c1\u56db\u540d\u592a\u76d1\u63d0\u4e86\u5bab\u706f\uff0c\u5f15\u7740\u4e09\u540d\u5b98\u5458\u8fc7\u6765\u3002\u4ed6\u773c\u89c1\u4eba\u591a\uff0c\u82e5\u662f\u62a2\u51fa\u64d2\u4eba\uff0c\u52bf\u5fc5\u60ca\u52a8\uff0c\u53ea\u8981\u4e00\u58f0\u5f20\uff0c\u7687\u5e1d\u6709\u5907\uff0c\u4fbf\u884c\u523a\u4e0d\u6210\u4e86\uff0c\u5f53\u4e0b\u8e51\u8db3\u5728\u540e\u8ddf\u968f\uff0c\u53ea\u89c1\u90a3\u4e03\u4eba\u8d70\u5411\u4e00\u5ea7\u5927\u6bbf\uff0c\u8fdb\u6bbf\u53bb\u4e86\u3002\u89c1\u6bbf\u5916\u533e\u989d\u5199\u7740\u201c\u5d07\u653f\u6bbf\u201d\u4e09\u5b57\uff0c\u65c1\u8fb9\u6709\u884c\u5f2f\u5f2f\u66f2\u66f2\u7684\u6ee1\u6587\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7ed5\u5230\u6bbf\u540e\uff0c\u4f0f\u8eab\u5728\u5730\uff0c\u53ea\u89c1\u6bbf\u5468\u56db\u4e94\u5341\u540d\u536b\u58eb\u6267\u5200\u5b88\u5fa1\uff0c\u5fc3\u4e2d\u4e00\u559c\uff1a\u201c\u6b64\u5904\u5b88\u536b\u68ee\u4e25\uff0c\u83ab\u975e\u9791\u5b50\u7687\u5e1d\u4fbf\u5728\u6bbf\u4e2d\uff1f\u201d \u3000\u3000\u5728\u5730\u4e0b\u6162\u6162\u722c\u8fd1\uff0c\u62fe\u8d77\u4e00\u5757\u77f3\u5b50\uff0c\u6295\u5165\u82b1\u4e1b\u3002\u56db\u540d\u536b\u58eb\u95fb\u58f0\u8fc7\u53bb\u67e5\u770b\u3002\u8881\u627f\u5fd7\u5c55\u5f00\u8f7b\u529f\uff0c\u5df2\u62a2\u5230\u5899\u8fb9\uff0c\u4f7f\u51fa\u201c\u58c1\u864e\u6e38\u5899\u529f\u201d\u6cbf\u5899\u800c\u4e0a\uff0c\u9877\u523b\u95f4\u5230\u4e86\u6bbf\u9876\uff0c\u4f0f\u5728\u5c4b\u810a\u4e4b\u4e0a\uff0c\u503e\u542c\u56db\u4e0b\u65e0\u58f0\uff0c\u81ea\u5df1\u8e2a\u8ff9\u672a\u88ab\u53d1\u89c1\uff0c\u4e8e\u662f\u8f7b\u8f7b\u63a8\u5f00\u6bbf\u9876\u7684\u51e0\u5757\u7409\u7483\u74e6\uff0c\u4ece\u7f1d\u9699\u4e2d\u51dd\u76ee\u5f80\u4e0b\u77a7\u53bb\u3002\u53ea\u89c1\u6ee1\u6bbf\u706f\u70db\u8f89\u714c\uff0c\u90a3\u4e09\u540d\u5b98\u5458\u6b63\u8dea\u5728\u5730\u4e0b\uff0c\u884c\u7684\u662f\u4e09\u8dea\u4e5d\u53e9\u5927\u793c\uff0c\u8881\u627f\u5fd7\u5927\u559c\uff1a\u201c\u679c\u7136\u662f\u5728\u53c2\u89c1\u7687\u5e1d\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u6700\u524d\u7684\u4e00\u540d\u82b1\u767d\u80e1\u5b50\u7684\u8001\u5b98\u8bf4\u9053\uff1a\u201c\u81e3\u8303\u6587\u7a0b\u89c1\u9a7e\u3002\u201d\u5176\u6b21\u4e00\u540d\u8eab\u6750\u9b41\u68a7\u7684\u5b98\u5458\u9053\uff1a\u201c\u81e3\u5b81\u5b8c\u6211\u89c1\u9a7e\u3002\u201d\u6700\u540e\u4e00\u540d\u5b98\u5458\u8138\u5bb9\u5c16\u524a\uff0c\u8bf4\u9053\uff1a\u201c\u81e3\u9c8d\u627f\u5148\u89c1\u9a7e\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u5b98\u513f\u90fd\u662f\u6c49\u4eba\uff0c\u5374\u6295\u964d\u4e86\u9791\u5b50\uff0c\u90fd\u662f\u6c49\u5978\uff0c\u5f85\u4f1a\u987a\u624b\u4e00\u4e2a\u4e00\u5251\u3002\u201d\u53c8\u60f3\uff1a\u201c\u4ed6\u4eec\u8ddf\u9791\u5b50\u7687\u5e1d\u600e\u5730\u53c8\u90fd\u8bf4\u6c49\u8bdd\uff1f\u201d \u3000\u3000\u7f13\u7f13\u79fb\u8eab\u5411\u5357\uff0c\u4ece\u7f1d\u9699\u4e2d\u5411\u5317\u77a7\u53bb\uff0c\u53ea\u89c1\u9f99\u5ea7\u4e0a\u4e00\u4eba\u65b9\u9762\u5927\u8033\uff0c\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u7ea6\u83ab\u4e94\u5341\u6765\u5c81\u5e74\u7eaa\uff0c\u90a3\u4fbf\u662f\u7236\u4eb2\u5f53\u5e74\u7684\u5927\u654c\u7687\u592a\u6781\u4e86\u3002\u5bfb\u601d\uff1a\u201c\u4ece\u6b64\u53d1\u5c04\u91d1\u86c7\u9525\uff0c\u5f53\u53ef\u53d6\u4ed6\u6027\u547d\uff0c\u53ea\u662f\u9694\u5f97\u8fdc\u4e86\uff0c\u5e76\u65e0\u5341\u8db3\u628a\u63e1\uff0c\u5018\u82e5\u4f8d\u536b\u4e4b\u4e2d\u6709\u9ad8\u624b\u5728\u5185\uff0c\u522b\u8981\u7ed9\u6321\u683c\u5f00\u53bb\uff0c\u8fd8\u662f\u8df3\u4e0b\u53bb\u4e00\u5251\u5272\u4e86\u4ed6\u9996\u7ea7\u7684\u4e3a\u662f\u3002\u201d \u3000\u3000\u53ea\u542c\u7687\u592a\u6781\u9053\uff1a\u201c\u5357\u671d\u519b\u60c5\u8fd9\u51e0\u5929\u600e\u6837\uff1f\u4eca\u65e5\u63a5\u5230\u963f\u5df4\u6cf0\u7684\u6025\u62a5\uff0c\u8bf4\u5728\u5c71\u4e1c\u9752\u5dde\u3001\u6cf0\u5b89\u4e4b\u95f4\u4e2d\u4f0f\uff0c\u6253\u4e86\u4e2a\u5927\u8d25\u4ed7\uff0c\u96be\u9053\u660e\u519b\u5c45\u7136\u8fd8\u8fd9\u4e48\u80fd\u6253\uff1f\u4f60\u4eec\u53ef\u77e5\u9752\u5dde\u3001\u6cf0\u5b89\u8fd9\u4e00\u5e26\u7684\u7edf\u5175\u5b98\u662f\u8c01\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u6b63\u5728\u8bf4\u6211\u4eec\u6253\u7684\u8fd9\u573a\u80dc\u4ed7\uff0c\u5012\u8981\u542c\u542c\u4ed6\u4eec\u8bf4\u4e9b\u751a\u4e48\uff1f\u201d \u3000\u3000\u5b81\u5b8c\u6211\u9053\uff1a\u201c\u542f\u7980\u7687\u4e0a\uff0c\u81e3\u5df2\u8be6\u7ec6\u67e5\u8fc7\u3002\u660e\u519b\u5e26\u5175\u7684\u603b\u5175\u59d3\u6c34\uff0c\u540d\u53eb\u6c34\u9274\uff0c\u6b66\u827a\u751a\u662f\u4e86\u5f97\u3002\u201d\u7687\u592a\u6781\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u4ed4\u7ec6\u67e5\u660e\uff0c\u80fd\u4e0d\u80fd\u8bbe\u6cd5\u8981\u4ed6\u964d\u6211\u5927\u6e05\uff0c\u77a7\u4ed6\u662f\u8d2a\u8d22\u5462\uff0c\u8fd8\u662f\u7231\u7f8e\u8272\u3002\u5018\u82e5\u4ed6\u5014\u5f3a\u4e0d\u670d\uff0c\u4fbf\u53eb\u66f9\u5316\u6df3\u5728\u660e\u671d\u7687\u5e1d\u8ddf\u524d\u8bf4\u4ed6\u7684\u574f\u8bdd\uff0c\u7f62\u4ed6\u7684\u5b98\uff0c\u6740\u4ed6\u7684\u5934\u3002\u4f46\u9996\u5148\u8981\u8bbe\u6cd5\u4ee4\u8fd9\u4eba\u4e3a\u6211\u5927\u6e05\u6240\u7528\u3002\u6b64\u4eba\u80fd\u6253\u8d25\u963f\u5df4\u6cf0\uff0c\u90a3\u662f\u4eba\u624d\uff0c\u54b1\u4eec\u51b3\u4e0d\u80fd\u8f7b\u6613\u653e\u8fc7\u4e86\u3002\u201d\u4e09\u540d\u5b98\u5458\u9f50\u58f0\u9053\uff1a\u201c\u7687\u4e0a\u5723\u660e\u82f1\u65ad\uff0c\u90a3\u6c34\u9274\u82e5\u80af\u964d\u987a\uff0c\u662f\u4ed6\u7684\u798f\u6c14\u3002\u201d \u3000\u3000\u7687\u592a\u6781\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5f53\u5e74\u4f7f\u53cd\u95f4\u8ba1\u6740\u4e86\u8881\u5d07\u7115\uff0c\u6715\u4e8b\u540e\u60f3\u6765\uff0c\u5e38\u89c9\u53ef\u60dc\u2026\u2026\u201d\u8881\u627f\u5fd7\u542c\u4ed6\u63d0\u5230\u81ea\u5df1\u7236\u4eb2\u7684\u540d\u5b57\uff0c\u8033\u4e2d\u767b\u65f6\u55e1\u7684\u4e00\u58f0\uff0c\u5168\u8eab\u53d1\u70ed\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u4eec\u4f7f\u53cd\u95f4\u8ba1\uff0c\u4f7f\u53cd\u95f4\u8ba1\uff01\u6211\u7239\u7239\u679c\u7136\u662f\u4ed6\u5bb3\u7684\u3002\u201d\u53ea\u542c\u7687\u592a\u6781\u7eed\u9053\uff1a\u201c\u5018\u82e5\u8881\u5d07\u7115\u80fd\u4e3a\u6715\u7528\uff0c\u5357\u671d\u7684\u6c5f\u5c71\u8fd9\u65f6\u5019\u591a\u534a\u65e9\u5df2\u662f\u5927\u6e05\u7684\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u5478\u7684\u4e00\u58f0\uff0c\u5fc3\u4e2d\u9a82\u9053\uff1a\u201c\u72d7\u9791\u5b50\u6253\u7684\u597d\u5982\u610f\u7b97\u76d8\uff01 \u3000\u3000\u6211\u7239\u7239\u5fe0\u809d\u4e49\u80c6\uff0c\u5c82\u80fd\u964d\u4f60\uff1f\u201d \u3000\u3000\u7687\u592a\u6781\u53c8\u9053\uff1a\u201c\u53ea\u662f\u8881\u5d07\u7115\u4e3a\u4eba\u611a\u5fe0\uff0c\u4e0d\u8bc6\u5927\u52bf\uff0c\u8c05\u6765\u4e5f\u662f\u4e0d\u80af\u964d\u7684\u3002\u201d\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u6d2a\u627f\u7574\u8fd1\u6765\u600e\u6837\uff1f\u201d\u8881\u627f\u5fd7\u77e5\u9053\u6d2a\u627f\u7574\u672c\u662f\u660e\u671d\u7684\u84df\u8fbd\u603b\u7763\uff0c\u5d07\u796f\u7687\u5e1d\u59d4\u4ee5\u5175\u9a6c\u5927\u6743\uff0c\u5175\u8d25\u88ab\u64d2\uff0c\u964d\u4e86\u6ee1\u6e05\u3002\u6d2a\u627f\u7574\u5931\u9677\u4e4b\u521d\uff0c\u5d07\u796f\u8fd8\u9053\u4ed6\u5df2\u6b89\u56fd\uff0c\u66fe\u4eb2\u81ea\u9686\u91cd\u796d\u7940\u3002\u540e\u6765\u5f97\u77e5\u964d\u6e05\uff0c\u5929\u4e0b\u90fd\u7b11\u5d07\u796f\u65e0\u77e5\u4eba\u4e4b\u660e\u3002 \u3000\u3000\u8303\u6587\u7a0b\u9053\uff1a\u201c\u542f\u594f\u7687\u4e0a\uff0c\u6d2a\u627f\u7574\u5df2\u5c06\u5357\u671d\u7684\u5b9e\u60c5\u751a\u4e48\u90fd\u8bf4\u4e86\u3002\u4ed6\u8bf4\u5d07\u796f\u521a\u610e\u81ea\u7528\uff0c\u4e3e\u63aa\u5931\u5f53\uff0c\u4fe1\u7528\u5978\u4f5e\uff0c\u6740\u5bb3\u5fe0\u826f\uff0c\u56db\u65b9\u6d41\u5bc7\u5927\u8d77\u3002\u6211\u5927\u6e05\u5927\u519b\u6b63\u53ef\u4e58\u673a\u8fdb\u5173\uff0c\u89e3\u6c11\u5012\u60ac\u3002\u201d\u7687\u592a\u6781\u6447\u5934\u9053\uff1a\u201c\u5d07\u796f\u7684\u6027\u5b50\uff0c\u4ed6\u8bf4\u5f97\u4e00\u70b9\u513f\u4e5f\u4e0d\u9519\u3002\u4f46\u6211\u5175\u8fdb\u5173\u5374\u8fd8\u4e0d\u662f\u65f6\u5019\u3002\u603b\u987b\u8ba9\u660e\u5175\u518d\u8ddf\u6d41\u5bc7\u6253\u4e0b\u53bb\uff0c\u53cc\u65b9\u7cbe\u75b2\u529b\u5c3d\uff0c\u4e24\u8d25\u4ff1\u4f24\uff0c\u5927\u6e05\u4fbf\u53ef\u6536\u90a3\u6e14\u7fc1\u4e4b\u5229\uff0c\u4e00\u4e3e\u800c\u5f97\u5929\u4e0b\u3002\u4f60\u4eec\u6c49\u4eba\u53eb\u505a\u535e\u5e84\u523a\u864e\u4e4b\u8ba1\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u4e09\u81e3\u9f50\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u7687\u4e0a\u5723\u660e\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6697\u6697\u5fc3\u60ca\uff1a\u201c\u8fd9\u9791\u5b50\u7687\u5e1d\u5f53\u771f\u5389\u5bb3\uff0c\u5d07\u796f\u548c\u4ed6\u76f8\u6bd4\u53ef\u662f\u5929\u5dee\u5730\u8fdc\u4e86\u3002\u6211\u975e\u6740\u4ed6\u4e0d\u53ef\uff0c\u6b64\u4eba\u4e0d\u9664\uff0c\u6211\u5927\u6c49\u6c5f\u5c71\u4e0d\u7a33\u3002\u5c31\u7b97\u95ef\u738b\u5f97\u4e86\u5929\u4e0b\uff0c\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u201d\u9690\u9690\u89c9\u5f97\u95ef\u738b\u7684\u624d\u5177\u4e0e\u6b64\u4eba\u76f8\u8f83\uff0c\u4f3c\u4e4e\u4e5f\u9887\u6709\u4e0d\u53ca\uff0c\u53ea\u4e0d\u77e5\u5fc3\u4e2d\u4f55\u4ee5\u4f1a\u751f\u51fa\u8fd9\u6837\u7684\u5ff5\u5934\u6765\u3002\u53c8\u60f3\uff1a\u201c\u8fd9\u7687\u5e1d\u7684\u6c49\u8bed\u53ef\u4e5f\u8bf4\u5f97\u6d41\u5229\u5f97\u5f88\u3002\u4ed6\u8fd8\u8bfb\u8fc7\u4e2d\u56fd\u4e66\uff0c\u5c45\u7136\u77e5\u9053\u535e\u5e84\u523a\u864e\u7684\u5178\u6545\u3002\u201d \u3000\u3000\u53ea\u542c\u7687\u592a\u6781\u9053\uff1a\u201c\u90a3\u6d2a\u627f\u7574\u8fd8\u8bf4\u4e9b\u751a\u4e48\uff1f\u201d\u8303\u6587\u7a0b\u9053\uff1a\u201c\u6d2a\u627f\u7574\u5411\u81e3\u9732\u4e86\u51e0\u6b21\u53e3\u98ce\uff0c\u76fc\u671b\u7687\u4e0a\u6069\u5178\uff0c\u8d4f\u4ed6\u4e2a\u5dee\u4f7f\uff0c\u4ed6\u5f97\u4ee5\u4e3a\u7687\u4e0a\u6548\u72ac\u9a6c\u4e4b\u52b3\uff0c\u4ef0\u62a5\u5929\u6069\u3002\u201d\u7687\u592a\u6781\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5dee\u4f7f\u5417\uff1f\u6162\u6162\u518d\u8bf4\u3002\u201d\u9c8d\u627f\u5148\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u81e3\u611a\u9c81\u4e4b\u6781\uff0c\u5fc3\u4e2d\u6709\u4e00\u4e8b\u4e0d\u660e\u767d\uff0c\u76fc\u671b\u7687\u4e0a\u6307\u660e\u3002\u201d\u7687\u592a\u6781\u70b9\u70b9\u5934\u3002\u9c8d\u627f\u5148\u9053\uff1a\u201c\u6d2a\u627f\u7574\u5148\u524d\u4e0d\u80af\u5f52\u987a\uff0c\u7687\u4e0a\u5927\u8d50\u6069\u5ba0\uff0c\u4eb2\u81ea\u89e3\u4e0b\u8eab\u4e0a\u7684\u8c82\u88d8\uff0c\u62ab\u5728\u4ed6\u8eab\u4e0a\uff0c\u53c8\u8fde\u65e5\u5927\u5f20\u7b75\u5e2d\u8bf7\u4ed6\uff0c\u8fde\u6211\u5927\u6e05\u7684\u5f00\u56fd\u529f\u81e3\u4e5f\u4ece\u6765\u6ca1\u8fd9\u822c\u6b8a\u8363\u3002\u4f17\u81e3\u5de5\u90fd\u4e0d\u660e\u767d\u3002\u7687\u4e0a\u5f00\u5bfc\u8bf4\uff1a\u54b1\u4eec\u8fd9\u4e9b\u5e74\u6765\u8f9b\u8f9b\u82e6\u82e6\u3001\u8fde\u5e74\u5f81\u6218\uff0c\u4e3a\u7684\u662f\u751a\u4e48\uff1f\u4f17\u81e3\u5de5\u542f\u594f\u9053\uff1a\u4e3a\u7684\u662f\u6253\u5357\u671d\u6c5f\u5c71\u3002\u7687\u4e0a\u8c15\u9053\uff1a\u662f\u554a\uff0c\u53ef\u662f\u54b1\u4eec\u4e0d\u660e\u5357\u671d\u5185\u60c5\uff0c\u597d\u6bd4\u90fd\u662f\u778e\u5b50\uff0c\u6d2a\u627f\u7574\u4e00\u5f52\u987a\uff0c\u54b1\u4eec\u90fd\u7741\u5f00\u4e86\u773c\u5566\uff0c\u90a3\u8fd8\u4e0d\u559c\u6b22\u4e48\uff1f\u4f17\u81e3\u5de5\u90fd\u62dc\u670d\u7687\u4e0a\u5723\u660e\u3002\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u90a3\u6d2a\u627f\u7574\u4e8e\u5357\u671d\u5404\u5730\u7684\u57ce\u5b88\u804c\u5b98\u3001\u6c11\u60c5\u98ce\u4fd7\uff0c\u679c\u7136\u8bf4\u5f97\u8be6\u8be6\u7ec6\u7ec6\uff0c\u5c3d\u5728\u7687\u4e0a\u7b97\u4e2d\u3002\u4f46\u7687\u4e0a\u5374\u4e0d\u8d4f\u4ed6\u5b98\u804c\u5c01\u7235\uff0c\u4f17\u81e3\u5de5\u53ef\u90fd\u53c8\u4e0d\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u7687\u592a\u6781\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u9c8d\u6027\u5b50\u76f4\u723d\uff0c\u60f3\u95ee\u751a\u4e48\uff0c\u5012\u4e5f\u76f4\u8a00\u65e0\u5fcc\u3002\u4f60\u4eec\u4e09\u4e2a\uff0c\u867d\u7136\u90fd\u662f\u6c49\u4eba\uff0c\u4f46\u65e9\u5c31\u8ddf\u5148\u7687\u548c\u6715\u529e\u4e8b\uff0c\u5fe0\u5fc3\u803f\u803f\uff0c\u6d2a\u627f\u7574\u600e\u80fd\u8ddf\u4f60\u4eec\u76f8\u6bd4\uff1f\u201d\u8303\u6587\u7a0b\u7b49\u4e09\u4eba\u5fd9\u722c\u4e0b\u78d5\u5934\uff0c\u549a\u549a\u6709\u58f0\uff0c\u663e\u662f\u5fc3\u4e2d\u611f\u6fc0\u4e4b\u6781\u3002\u8881\u627f\u5fd7\u6697\u9a82\uff1a\u201c\u65e0\u803b\uff0c\u65e0\u803b\u3002\u201d \u3000\u3000\u53ea\u542c\u7687\u592a\u6781\u9053\uff1a\u201c\u6d2a\u627f\u7574\u8fd9\u4eba\uff0c\u672c\u4e8b\u662f\u6709\u7684\uff0c\u53ef\u662f\u9aa8\u6c14\u5c31\u8bf4\u4e0d\u4e0a\u4e86\u3002\u5148\u524d\u6211\u5df2\u5f85\u4ed6\u592a\u597d\uff0c\u82e5\u518d\u8d50\u4ed6\u9ad8\u5b98\u539a\u7984\uff0c\u8fd9\u4eba\u8fd8\u80af\u51fa\u529b\u529e\u4e8b\u5417\uff1f\u54fc\uff0c\u5d07\u796f\u5c01\u4ed6\u7684\u5b98\u96be\u9053\u8fd8\u4e0d\u591f\u5927\uff0c\u90a3\u65f6\u4ed6\u505a\u7684\u662f\u751a\u4e48\u5b98\uff1f\u201d\u9c8d\u627f\u5148\u9053\uff1a\u201c\u542f\u594f\u7687\u4e0a\uff1a\u90a3\u65f6\u4ed6\u5728\u5357\u671d\u5b98\u5c01\u592a\u5b50\u592a\u4fdd\u3001\u5175\u90e8\u5c1a\u4e66\u3001\u603b\u7763\u84df\u8fbd\u519b\u52a1\uff0c\u9ebe\u4e0b\u7edf\u7387\u516b\u540d\u603b\u5175\u5b98\uff0c\u5b9e\u662f\u5b98\u5927\u6743\u5927\u3002\u201d\u7687\u592a\u6781\u9053\uff1a\u201c\u7167\u554a\u3002\u6211\u5c01\u4ed6\u7684\u5b98\u518d\u5927\uff0c\u4e5f\u5927\u4e0d\u8fc7\u5d07\u796f\u5c01\u4ed6\u7684\u3002\u8981\u4ed6\u5c3d\u5fc3\u7aed\u529b\u529e\u4e8b\uff0c\u4fbf\u4e0d\u80fd\u7ed9\u4ed6\u5b98\u505a\u3002\u201d\u4e09\u81e3\u9f50\u58f0\u9053\uff1a\u201c\u7687\u4e0a\u5723\u660e\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8d8a\u60f3\u8d8a\u6709\u9053\u7406\uff0c\u89c9\u5f97\u4ed6\u8fd9\u9a7e\u9a6d\u4eba\u624d\u7684\u6cd5\u95e8\u5b9e\u662f\u9ad8\u660e\u4e4b\u6781\uff0c\u6b64\u523b\u542c\u5230\u8fd9\u756a\u8bdd\uff0c\u5b9b\u4f3c\u5f53\u5e74\u5728\u534e\u5c71\u7edd\u9876\u521d\u89c1\u300a\u91d1\u86c7\u79d8\u7b08\u300b\uff0c\u5176\u4e2d\u6240\u8ff0\u6cd5\u95e8\u65e0\u4e0d\u532a\u5937\u6240\u601d\uff0c\u867d\u7136\u7edd\u975e\u6b63\u9053\uff0c\u5374\u4ee4\u4eba\u4e0d\u7531\u5f97\u4e0d\u670d\u3002 \u3000\u3000\u4ed6\u5446\u4e86\u4e00\u9635\uff0c\u5374\u542c\u5f97\u7687\u592a\u6781\u5728\u548c\u8303\u6587\u7a0b\u7b49\u5546\u8bae\uff0c\u65e5\u540e\u53d6\u5f97\u660e\u671d\u5929\u4e0b\u4e4b\u540e\u5982\u4f55\u6cbb\u7406\uff0c\u6b64\u65f6\u5982\u4f55\u5148\u4e3a\u4e4b\u5907\uff0c\u5012\u4f3c\u5927\u660e\u7684\u6c5f\u5c71\u5df2\u662f\u4ed6\u638c\u4e2d\u4e4b\u7269\u4e00\u822c\u3002\u8881\u627f\u5fd7\u5fc3\u4e0b\u6124\u6012\uff0c\u8f7b\u8f7b\u53c8\u63ed\u5f00\u4e86\u4e24\u5f20\u7409\u7483\u74e6\uff0c\u770b\u51c6\u4e86\u6bbf\u4e2d\u843d\u811a\u4e4b\u5904\uff0c\u5374\u542c\u5f97\u7687\u592a\u6781\u9053\uff1a\u201c\u5357\u671d\u6240\u4ee5\u6d41\u5bc7\u56db\u8d77\uff0c\u8bf4\u6765\u8bf4\u53bb\uff0c\u4e5f\u53ea\u4e00\u4e2a\u9053\u7406\uff0c\u5c31\u662f\u8001\u767e\u59d3\u6ca1\u996d\u5403\u3002\u54b1\u4eec\u5f97\u4e86\u5357\u671d\u6c5f\u5c71\uff0c\u7b2c\u4e00\u4ef6\u5927\u4e8b\uff0c\u5c31\u662f\u8981\u8ba9\u5929\u4e0b\u767e\u59d3\u4eba\u4eba\u6709\u996d\u5403\u2026\u2026\u201d\u8881\u627f\u5fd7\u5fc3\u4e0b\u4e00\u51db\uff1a\u201c\u8fd9\u8bdd\u5bf9\u6781\uff01\u201d \u3000\u3000\u8303\u6587\u7a0b\u7b49\u9882\u626c\u4e86\u51e0\u53e5\u3002\u7687\u592a\u6781\u9053\uff1a\u201c\u8981\u8001\u767e\u59d3\u6709\u996d\u5403\uff0c\u4f60\u4eec\u8bf4\u6709\u751a\u4e48\u6cd5\u5b50\uff1f\u8303\u5148\u751f\uff0c\u4f60\u5148\u8bf4\u8bf4\u770b\u3002\u201d\u4ed6\u4f3c\u5bf9\u8303\u6587\u7a0b\u9887\u4e3a\u5ba2\u6c14\uff0c\u79f0\u4ed6\u201c\u5148\u751f\u201d\uff0c\u4e0d\u50cf\u5bf9\u9c8d\u627f\u5148\u90a3\u6837\u547c\u4e4b\u4e3a\u201c\u8001\u9c8d\u201d\u3002 \u3000\u3000\u8303\u6587\u7a0b\u9053\uff1a\u201c\u7687\u4e0a\u672a\u5f97\u6c5f\u5c71\uff0c\u5148\u5c31\u5ff5\u5ff5\u4e0d\u5fd8\u4e8e\u767e\u59d3\uff0c\u8fd9\u756a\u5fc3\u610f\uff0c\u5fc5\u5f97\u4e0a\u5929\u7737\u987e\u3002\u4ee5\u81e3\u611a\u89c1\uff0c\u8981\u5929\u4e0b\u767e\u59d3\u90fd\u6709\u996d\u5403\uff0c\u7b2c\u4e00\u987b\u5f97\u8f7b\u5fad\u8584\u8d4b\uff0c\u51b3\u4e0d\u53ef\u5982\u5d07\u796f\u90a3\u6837\uff0c\u4e0d\u65ad\u7684\u52a0\u9977\u641c\u522e\u3002\u201d\u7687\u592a\u6781\u8fde\u8fde\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fdb\u5173\u4e4b\u540e\uff0c\u987b\u5f97\u5b9a\u4e0b\u89c4\u77e9\uff0c\u4e16\u4e16\u4ee3\u4ee3\uff0c\u4e0d\u5f97\u52a0\u8d4b\uff0c\u53ea\u8981\u5e93\u4e2d\u6709\u4f59\uff0c\u5c31\u5f97\u4e0b\u65e8\u514d\u767e\u59d3\u94b1\u7cae\u3002\u201d\u8303\u6587\u7a0b\u9053\uff1a\u201c\u7687\u4e0a\u5982\u6b64\u5b58\u5fc3\uff0c\u5b9e\u662f\u4e07\u6c11\u4e4b\u798f\uff0c\u81e3\u5f97\u4ee5\u6295\u6548\u660e\u4e3b\uff0c\u4e3a\u7687\u4e0a\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u6240\u2026\u2026\u4e5f\u6240\u7518\u613f\u3002\u201d\u8bf4\u5230\u540e\u6765\uff0c\u8bed\u97f3\u7adf\u7136\u545c\u54bd\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2a\u5927\u6c49\u5978\uff0c\u5012\u4f3c\u786e\u6709\u7231\u6c11\u4e4b\u5fc3\uff0c\u4e0d\u77e5\u662f\u505a\u620f\u5462\uff0c\u8fd8\u662f\u771f\u5fc3\u3002\u201d\u7687\u592a\u6781\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\u3002\u4f60\u4eec\u6c49\u4eba\u9a82\u4f60\u4eec\u662f\u6c49\u5978\uff0c\u65e5\u540e\u4f60\u4eec\u597d\u597d\u4e3a\u6715\u529e\u4e8b\uff0c\u4e5f\u5c31\u662f\u4e3a\u5929\u4e0b\u767e\u59d3\u529e\u4e8b\uff0c\u603b\u5f97\u72e0\u72e0\u7684\u6323\u4e00\u53e3\u6c14\uff0c\u8ba9\u5343\u5343\u4e07\u4e07\u767e\u59d3\u77a7\u77a7\uff0c\u5230\u5e95\u662f\u4f60\u4eec\u8fd9\u4e9b\u4eba\u4e3a\u6c49\u4eba\u505a\u4e86\u597d\u4e8b\u5462\uff0c\u8fd8\u662f\u5d07\u796f\u624b\u4e0b\u90a3\u4e9b\u53ea\u77e5\u5347\u5b98\u53d1\u8d22\u3001\u641c\u522e\u767e\u59d3\u7684\u771f\u6c49\u5978\u505a\u4e86\u597d\u4e8b\u3002\u8001\u5b81\uff0c\u4f60\u6709\u751a\u4e48\u6761\u9648\uff1f\u201d \u3000\u3000\u5b81\u5b8c\u6211\u9053\uff1a\u201c\u542f\u594f\u7687\u4e0a\uff1a\u6211\u5927\u6e05\u7684\u6ee1\u6d32\u4eba\u5c11\uff0c\u6c49\u4eba\u4f17\u591a\u3002 \u3000\u3000\u7687\u4e0a\u5f97\u4e86\u5929\u4e0b\u4e4b\u540e\uff0c\u4ee5\u81e3\u611a\u89c1\uff0c\u987b\u5f97\u89c6\u5929\u4e0b\u6ee1\u4eba\u6c49\u4eba\u4ff1\u662f\u7687\u4e0a\u5b50\u6c11\uff0c\u4e0d\u53ef\u50cf\u5143\u671d\u8499\u53e4\u4eba\u90a3\u6837\uff0c\u5f3a\u5206\u5929\u4e0b\u767e\u59d3\u4e3a\u56db\u7b49\u3002\u53ea\u6d88\u6211\u5927\u6e05\u5bf9\u4f17\u767e\u59d3\u4e00\u89c6\u540c\u4ec1\uff0c\u6c49\u4eba\u4e4b\u4e2d\u7eb5\u6709\u5014\u5f3a\u4e4b\u5f92\uff0c\u4e5f\u6210\u4e0d\u4e86\u5927\u4e8b\u3002\u201d\u7687\u592a\u6781\u70b9\u5934\u9053\uff1a\u201c\u6b64\u8a00\u6709\u7406\u3002\u5143\u4eba\u5f13\u9a6c\uff0c\u5929\u4e0b\u65e0\u654c\uff0c\u53ef\u662f\u4ed6\u4eec\u5728\u4e2d\u56fd\u7684\u6c5f\u5c71\u5374\u5750\u4e0d\u7a33\uff0c\u5c31\u662f\u4e3a\u4e86\u8650\u5f85\u6c49\u4eba\u3002\u8fd9\u662f\u524d\u8f66\u751a\u4e48\u7684\uff1f\u201d\u9c8d\u627f\u5148\u9053\uff1a\u201c\u524d\u8f66\u8986\u8f99\u3002\u201d\u7687\u592a\u6781\u5fae\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u8001\u9c8d\uff0c\u6211\u8bfb\u6c49\u4eba\u7684\u4e66\uff0c\u59cb\u7ec8\u4e0d\u6613\u6709\u751a\u4e48\u957f\u8fdb\u3002\u201d\u9c8d\u627f\u5148\u9053\uff1a\u201c\u7687\u4e0a\u65e5\u7406\u4e07\u673a\uff0c\u8fd9\u4e9b\u6c49\u4eba\u4e66\u4e2d\u7684\u5178\u6545\uff0c\u4e5f\u4e0d\u5fc5\u592a\u653e\u5728\u5fc3\u4e0a\u3002\u201d\u7687\u592a\u6781\u53f9\u9053\uff1a\u201c\u6c49\u4eba\u7684\u5b66\u95ee\uff0c\u4e0d\u5c11\u662f\u5f88\u597d\u7684\u3002\u53ea\u4e0d\u8fc7\u4f5c\u4e3b\u5b50\u7684\uff0c\u8bfb\u4e66\u5f53\u5b66\u4e66\u91cc\u5934\u7684\u672c\u4e8b\u7b56\u7565\uff0c\u4e0d\u5fc5\u5b66\u6c49\u4eba\u7684\u79c0\u624d\u8fdb\u58eb\u90a3\u6837\uff0c\u5b66\u751a\u4e48\u541f\u8bd7\u4f5c\u5bf9\u2026\u2026\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u4e86\u8fd9\u4e9b\u8bdd\uff0c\u53ea\u89c9\u53e5\u53e5\u5165\u8033\u52a8\u5fc3\uff0c\u6d51\u5fd8\u4e86\u6b64\u6765\u662f\u8981\u523a\u6b7b\u6b64\u4eba\uff0c\u5185\u5fc3\u9690\u9690\u4f3c\u76fc\u591a\u542c\u4e00\u4f1a\uff0c\u4f46\u542c\u4ed6\u56db\u4eba\u5546\u8bae\u5982\u4f55\u6574\u996c\u519b\u7eaa\u3001\u6e05\u5175\u5165\u5173\u4e4b\u540e\uff0c\u51b3\u8ba1\u4e0d\u53ef\u6b8b\u6740\u767e\u59d3\uff0c\u52a1\u987b\u4e25\u7981\u52ab\u63a0\u3002\u53ea\u89c1\u4e24\u540d\u4f8d\u536b\u8d70\u4e0a\u524d\u6765\uff0c\u6362\u53bb\u5fa1\u5ea7\u524d\u684c\u4e0a\u7684\u5de8\u70db\uff0c\u70db\u5149\u4e00\u660e\u4e00\u6697\u4e4b\u9645\uff0c\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u518d\u4e0d\u52a8\u624b\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d\u5de6\u638c\u63d0\u8d77\uff0c\u731b\u529b\u51fb\u843d\uff0c\u5580\u5587\u5587\u4e00\u58f0\u54cd\uff0c\u6bbf\u9876\u5df2\u65ad\u4e86\u4e24\u6839\u693d\u5b50\uff0c\u4ed6\u968f\u7740\u74e6\u7247\u6ce5\u5c18\uff0c\u8dc3\u4e0b\u6bbf\u6765\uff0c\u53f3\u8db3\u8e0f\u4e0a\u9f99\u6848\uff0c\u91d1\u86c7\u5251\u75be\u5411\u7687\u592a\u6781\u80f8\u53e3\u523a\u53bb\u3002 \u3000\u3000\u7687\u592a\u6781\u4e24\u4fa7\u62a2\u4e0a\u56db\u540d\u536b\u58eb\uff0c\u4e0d\u53ca\u62d4\u5200\uff0c\u5df2\u540c\u65f6\u6321\u5728\u7687\u592a\u6781\u8eab\u524d\u3002\u55e4\u55e4\u4e24\u54cd\uff0c\u4e24\u540d\u536b\u58eb\u5df2\u8eab\u4e2d\u91d1\u86c7\u5251\u800c\u6b7b\u3002\u7687\u592a\u6781\u8eab\u624b\u751a\u662f\u654f\u6377\uff0c\u4ece\u9f99\u6905\u4e2d\u6025\u8dc3\u800c\u8d77\uff0c\u9000\u5f00\u4e24\u6b65\u3002\u8fd9\u65f6\u53c8\u6709\u4e94\u516d\u540d\u536b\u58eb\u62a2\u4e0a\u62e6\u622a\uff0c\u5b81\u5b8c\u6211\u4e0e\u9c8d\u627f\u5148\u6251\u5411\u8881\u627f\u5fd7\u8eab\u540e\uff0c\u5404\u4f38\u53cc\u624b\u53bb\u62b1\u3002\u8881\u627f\u5fd7\u5de6\u811a\u53cd\u8e22\uff0c\u7830\u7830\u4e24\u58f0\uff0c\u5c06\u5b81\u9c8d\u4e24\u4eba\u8e22\u5f97\u76f4\u63bc\u51fa\u53bb\u3002\u4fbf\u8fd9\u4e48\u7f13\u5f97\u4e00\u7f13\uff0c\u7687\u592a\u6781\u53c8\u9000\u5f00\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u6025\uff0c\u5fc3\u60f3\u4eca\u65e5\u83ab\u8981\u7ed9\u8fd9\u9791\u5b50\u7687\u5e1d\u9003\u4e86\u51fa\u53bb\uff0c\u518d\u8981\u884c\u523a\uff0c\u53ef\u5c31\u66f4\u52a0\u4e0d\u6613\u4e86\uff0c\u8fde\u53d1\u4e24\u679a\u91d1\u86c7\u9525\uff0c\u5374\u90fd\u7ed9\u536b\u58eb\u51b2\u4e0a\u6321\u53bb\uff0c\u4f5c\u4e86\u66ff\u6b7b\u9b3c\u3002\u8881\u627f\u5fd7\u91d1\u86c7\u5251\u8fde\u523a\uff0c\u66f4\u4e0d\u7406\u4f1a\u4f17\u536b\u58eb\u6765\u653b\uff0c\u75be\u5411\u7687\u592a\u6781\u51b2\u53bb\u3002\u773c\u89c1\u8ddd\u4ed6\u5df2\u4e0d\u8fc7\u4e08\u8bb8\uff0c\u84e6\u5730\u91cc\u5e37\u5e55\u540e\u62a2\u51fa\u516b\u540d\u6b66\u58eb\uff0c\u90fd\u662f\u7a7a\u624b\uff0c\u540c\u65f6\u6251\u5230\u3002\u8881\u627f\u5fd7\u53f3\u8db3\u4e00\u5f39\uff0c\u63bc\u7684\u4e00\u54cd\uff0c\u8e22\u98de\u4e86\u4e00\u540d\uff0c\u5de6\u8db3\u9e33\u9e2f\u8fde\u73af\uff0c\u8ddf\u7740\u98de\u51fa\uff0c\u4e00\u540d\u6b66\u58eb\u6b63\u5728\u6b64\u65f6\u81ea\u5de6\u4fa7\u6251\u5230\u3002\u8881\u627f\u5fd7\u5de6\u811a\u8e22\u4e2d\u4e86\u4ed6\u80f8\u53e3\uff0c\u4ed6\u53cc\u624b\u5374\u5df2\u7262\u7262\u6293\u4f4f\u4e86\u8881\u627f\u5fd7\u5c0f\u817f\u3002\u8fd9\u6b66\u58eb\u53e3\u4e2d\u9c9c\u8840\u72c2\u55b7\uff0c\u53cc\u624b\u5374\u6b7b\u547d\u6293\u4f4f\u4e0d\u653e\u3002\u8fd9\u516b\u540d\u6b66\u58eb\u5728\u6ee1\u6d32\u8bed\u4e2d\u79f0\u4e3a\u201c\u5e03\u5e93\u201d\uff0c\u64c5\u4e8e\u6454\u4ea4\u64d2\u62ff\uff0c\u5e73\u65f6\u5bab\u4e2d\u6216\u8d1d\u52d2\u738b\u516c\u76db\u5bb4\uff0c\u4f8b\u6709\u89d2\u6597\u5a31\u5bbe\u3002\u7687\u592a\u6781\u63a5\u89c1\u81e3\u4e0b\u4e4b\u540e\uff0c\u4e34\u7761\u4e4b\u524d\u5e38\u8981\u5148\u770b\u4e00\u573a\u89d2\u6597\u3002\u8fd9\u516b\u540d\u5e03\u5e93\u6b66\u58eb\u6b64\u523b\u6b63\u5728\u6bbf\u65c1\u4f3a\u5019\uff0c\u542c\u5f97\u6709\u523a\u5ba2\uff0c\u7eb7\u7eb7\u62a2\u4e0a\u6765\u62a4\u9a7e\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5de6\u8db3\u529b\u7529\uff0c\u5374\u7529\u4e0d\u8131\u8fd9\u6b66\u58eb\uff0c\u91d1\u86c7\u5251\u6325\u51fa\uff0c\u524a\u53bb\u4e86\u4ed6\u534a\u8fb9\u8111\u888b\uff0c\u4f46\u90a3\u6b66\u58eb\u53cc\u624b\u5140\u81ea\u7d27\u7d27\u6293\u4f4f\u8881\u627f\u5fd7\u5c0f\u817f\u3002\u5ffd\u542c\u5f97\u8eab\u540e\u6709\u4eba\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\uff0c\u7adf\u6562\u884c\u523a\u7687\u4e0a\uff1f\u201d\u8bf4\u7684\u662f\u6c49\u8bed\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5168\u4e0d\u7406\u4f1a\uff0c\u5de6\u811a\u5e26\u7740\u90a3\u540d\u6b7b\u6b66\u58eb\uff0c\u8de8\u6b65\u4e0a\u524d\u53bb\u8ffd\u7687\u592a\u6781\uff0c\u53ea\u8de8\u4e00\u6b65\uff0c\u5934\u9876\u98ce\u58f0\u98d2\u7136\uff0c\u4e00\u4ef6\u5175\u5203\u88ad\u5230\uff0c\u52b2\u98ce\u63a0\u9888\uff0c\u6709\u5982\u5229\u5203\u3002\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u77e5\u9053\u654c\u4eba\u6b66\u529f\u9ad8\u5f3a\u4e4b\u6781\uff0c\u5371\u6025\u4e2d\u6eda\u5012\u5728\u5730\uff0c\u4e00\u4e2a\u7b4b\u6597\u7ffb\u51fa\uff0c\u821e\u5251\u62a4\u9876\uff0c\u5de6\u624b\u626f\u8131\u811a\u4e0a\u7684\u6b7b\u6b66\u58eb\uff0c\u8fd9\u624d\u7ad9\u8d77\u3002 \u3000\u3000\u70db\u5149\u7167\u6620\u4e0b\uff0c\u53ea\u89c1\u773c\u524d\u7ad9\u7740\u4e00\u4e2a\u4e2d\u5e74\u9053\u4eba\uff0c\u7709\u6e05\u76ee\u79c0\uff0c\u8138\u5982\u51a0\u7389\uff0c\u53f3\u624b\u6267\u7740\u4e00\u67c4\u62c2\u5c18\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5927\u80c6\u523a\u5ba2\uff0c\u8fd8\u4e0d\u629b\u4e0b\u5175\u5668\u53d7\u7f1a\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u773c\u5149\u53ea\u5411\u4ed6\u4e00\u77a5\uff0c\u53c8\u8f6c\u53bb\u77a7\u7687\u592a\u6781\uff0c\u53ea\u89c1\u5df2\u6709\u5341\u4f59\u540d\u536b\u58eb\u6321\u5728\u4ed6\u8eab\u524d\u3002\u8881\u627f\u5fd7\u6597\u7136\u8dc3\u8d77\uff0c\u6025\u5411\u7687\u592a\u6781\u6251\u53bb\uff0c\u8eab\u5728\u534a\u7a7a\uff0c\u84e6\u89c1\u90a3\u9053\u58eb\u4e5f\u8dc3\u8d77\u8eab\u5b50\uff0c\u62c2\u5c18\u8fce\u9762\u62c2\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u91d1\u86c7\u5251\u8fde\u523a\u4e24\u4e0b\uff0c\u5feb\u901f\u65e0\u4f26\u3002\u90a3\u9053\u58eb\u4fa7\u5934\u907f\u4e86\u4e00\u5251\uff0c\u62c2\u5c18\u6321\u5f00\u4e00\u5251\uff0c\u8ddf\u7740\u5343\u767e\u6839\u62c2\u5c18\u4e1d\u6025\u901f\u6325\u6765\u3002\u8881\u627f\u5fd7\u4f38\u5de6\u624b\u53bb\u6293\u62c2\u5c18\uff0c\u53f3\u624b\u5251\u523a\u4ed6\u54bd\u5589\u3002\u5237\u7684\u4e00\u58f0\u54cd\uff0c\u5c18\u5c3e\u6253\u4e2d\u4e86\u4ed6\u5de6\u624b\uff0c\u624b\u80cc\u4e0a\u767b\u65f6\u9c9c\u8840\u6dcb\u6f13\uff0c\u539f\u6765\u4ed6\u62c2\u5c18\u4e4b\u4e1d\u7cfb\u4ee5\u91d1\u4e1d\u94f6\u4e1d\u6240\u5236\uff0c\u867d\u7136\u67d4\u8f6f\uff0c\u8fd0\u4e0a\u4e86\u5185\u52b2\uff0c\u5374\u662f\u4e00\u4ef6\u81f4\u547d\u7684\u5389\u5bb3\u5175\u5203\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u91d1\u86c7\u5251\u5251\u5c16\u4e0a\u7684\u86c7\u820c\u4e5f\u5df2\u94a9\u4e2d\u90a3\u9053\u4eba\u80a9\u5934\u3002 \u3000\u3000\u4e24\u4eba\u5728\u7a7a\u4e2d\u4ea4\u624b\u4e09\u62db\uff0c\u5404\u53d7\u8f7b\u4f24\uff0c\u843d\u4e0b\u5730\u6765\u65f6\u5df2\u4ea4\u53c9\u6613\u4f4d\uff0c\u5fc3\u4e0b\u5747\u662f\u60ca\u7591\u4e0d\u5b9a\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u6b66\u529f\u6041\u5730\u4e86\u5f97\uff0c\u5b9e\u662f\u6211\u751f\u5e73\u6240\u4ec5\u89c1\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u56db\u56de\u3000\u5251\u5149\u5d07\u653f\u6bbf \u70db\u5f71\u662d\u9633\u5bab",content:(0,t.jsx)(t.Fragment,{children:"\u8881\u627f\u5fd7\u56de\u8eab\u53c8\u5f85\u53bb\u523a\u7687\u592a\u6781\u65f6\uff0c\u90a3\u9053\u4eba\u7684\u62c2\u5c18\u5df2\u5411\u4ed6\u8111\u540e\u62c2\u6765\uff0c\u62c2\u4e1d\u4e3a\u5185\u52b2\u6240\u6fc0\uff0c\u7b14\u76f4\u6233\u81f3\uff0c\u72b9\u4f3c\u6746\u68d2\u3002\u8881\u627f\u5fd7\u65e0\u5948\uff0c\u53ea\u5f97\u56de\u5251\u6321\u5f00\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u642d\u4e0a\u624b\uff0c\u767b\u65f6\u4ee5\u5feb\u6253\u5feb\uff0c\u77ac\u606f\u95f4\u62c6\u4e86\u4e8c\u5341\u4f59\u62db\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7aed\u5c3d\u5e73\u751f\u4e4b\u529b\uff0c\u7adf\u662f\u4e1d\u6beb\u5360\u4e0d\u5230\u4e0a\u98ce\uff0c\u8d8a\u6597\u8d8a\u662f\u5fc3\u60ca\uff0c\u7a81\u7136\u95f4\u98ce\u58f0\u8fc7\u53bb\uff0c\u53f3\u988a\u53c8\u88ab\u62c2\u5c18\u626b\u4e86\u4e00\u4e0b\uff0c\u6599\u60f3\u8138\u988a\u4e0a\u5df2\u662f\u591a\u4e86\u6570\u5341\u6761\u8840\u75d5\uff0c\u84e6\u5730\u91cc\u9752\u9752\u7684\u8bdd\u5728\u8111\u6d77\u4e2d\u4e00\u95ea\uff1a\u201c\u627f\u5fd7\u54e5\u54e5\uff0c\u9791\u5b50\u7687\u5e1d\u523a\u5f97\u5230\u679c\u7136\u597d\uff0c\u523a\u4e0d\u5230\u4e5f\u5c31\u7f62\u4e86\uff0c\u4f60\u81ea\u5df1\u53ef\u5343\u4e07\u8981\u4fdd\u91cd\u3002\u201d\u773c\u89c1\u654c\u4eba\u5982\u6b64\u5389\u5bb3\uff0c\u53ea\u5f97\u5148\u8c0b\u8131\u8eab\uff0c\u4ed6\u4e00\u8fb9\u6597\uff0c\u4e00\u8fb9\u79fb\u52a8\u811a\u6b65\uff0c\u6e10\u6e10\u79fb\u5411\u6bbf\u53e3\u3002\u90a3\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u5728\u6211\u7389\u771f\u5b50\u624b\u4e0b\u4e5f\u60f3\u9003\u547d\uff1f\u75f4\u5fc3\u5984\u60f3\uff01\u201d\u8bf4\u7740\u62c2\u5c18\u8fde\u8fdb\u4e09\u62db\uff0c\u5c3d\u662f\u4ece\u610f\u6599\u4e0d\u5230\u7684\u65b9\u4f4d\u88ad\u6765\u3002\u8881\u627f\u5fd7\u4e00\u65f6\u4e0d\u77e5\u5982\u4f55\u62db\u67b6\u624d\u662f\uff0c\u811a\u4e0b\u81ea\u7136\u800c\u7136\u7684\u4f7f\u51fa\u6728\u6851\u6240\u6388\u201c\u795e\u884c\u767e\u53d8\u201d\u6b65\u6cd5\uff0c\u4e1c\u7a9c\u897f\u659c\uff0c\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4e0d\u6599\u8fd9\u7389\u771f\u5b50\u5982\u5f71\u968f\u5f62\uff0c\u7adf\u4e8e\u4ed6\u7684\u201c\u795e\u884c\u767e\u53d8\u201d\u6b65\u6cd5\u4e86\u7136\u4e8e\u80f8\uff0c\u8881\u627f\u5fd7\u95ea\u5230\u4e1c\uff0c\u4ed6\u8ddf\u5230\u4e1c\uff0c\u7a9c\u5230\u897f\uff0c\u4ed6\u8ffd\u5230\u897f\u3002\u8881\u627f\u5fd7\u867d\u8ba9\u5f00\u4e86\u90a3\u4e09\u62db\uff0c\u5374\u6446\u8131\u4e0d\u4e86\u4ed6\u6e90\u6e90\u800c\u6765\u7684\u653b\u51fb\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e24\u4eba\u90fd\u662f\u5927\u5947\u3002\u7389\u771f\u5b50\u53eb\u9053\uff1a\u201c\u4f60\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u662f\u6728\u6851\u9053\u4eba\u7684\u5f1f\u5b50\u5417\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d\u7389\u771f\u5b50\u95ee\u9053\uff1a\u201c\u4f60\u600e\u5730\u4f1a\u94c1\u5251\u95e8\u7684\u6b65\u6cd5\uff1f\u201d\u8881\u627f\u5fd7\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u662f\u6c49\u4eba\uff0c\u600e\u5730\u53cd\u5e2e\u9791\u5b50\uff1f\u201d\u7389\u771f\u5b50\u6012\u9053\uff1a\u201c\u5014\u5f3a\u5c0f\u5b50\uff0c\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u5728\u80e1\u8bf4\u3002\u201d \u3000\u3000\u5237\u5237\u4e24\u62db\u3002 \u3000\u3000\u8881\u627f\u5fd7\u773c\u89c1\u5bf9\u65b9\u4e86\u5f97\uff0c\u7a0d\u6709\u758f\u795e\uff0c\u4e0d\u514d\u6027\u547d\u96be\u4fdd\uff0c\u5f53\u5373\u51dd\u795e\u81f4\u5fd7\uff0c\u4f7f\u5f00\u672c\u95e8\u534e\u5c71\u6d3e\u5251\u6cd5\u63a5\u62db\u3002\u7389\u771f\u5b50\u770b\u4e86\u6570\u62db\uff0c\u53eb\u9053\uff1a\u201c\u554a\uff0c\u4f60\u662f\u534e\u5c71\u6d3e\u7a46\u8001\u7334\u513f\u95e8\u4e0b\u7684\u5c0f\u7334\u513f\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u8881\u627f\u5fd7\u4e0d\u80af\u9690\u7792\u5e08\u95e8\uff0c\u559d\u9053\uff1a\u201c\u662f\u4fbf\u600e\u6837\uff1f\u201d\u4e00\u62db\u201c\u82cd\u677e\u8fce\u5ba2\u201d\uff0c\u957f\u5251\u659c\u51fa\uff0c\u5185\u529b\u4ece\u5251\u8eab\u4e0a\u55e4\u55e4\u53d1\u51fa\uff0c\u59ff\u5f0f\u7aef\u51dd\uff0c\u62db\u8fc5\u52b2\u8db3\u3002\u7389\u771f\u5b50\u8d5e\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u5c0f\u7334\u513f\u4e0d\u574f\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u9a82\u9053\uff1a\u201c\u4f60\u501a\u8001\u5356\u8001\u751a\u4e48\uff1f\u201d\u7389\u771f\u5b50\u7b11\u9053\uff1a\u201c\u8001\u7334\u513f\u4e5f\u4e0d\u662f\u6211\u5bf9\u624b\uff0c\u4f60\u5c0f\u7334\u513f\u66f4\u52a0\u4e0d\u7528\u60f3\u3002\u201d\u8881\u627f\u5fd7\u4e0d\u518d\u8bf4\u8bdd\uff0c\u5168\u795e\u8d2f\u6ce8\u7684\u51fa\u5251\u62c6\u62db\u3002\u7389\u771f\u5b50\u5fae\u4e00\u758f\u795e\uff0c\u5de6\u81c2\u7adf\u88ab\u91d1\u86c7\u5251\u5212\u4e86\u6d45\u6d45\u4e00\u9053\u53e3\u5b50\u3002\u8fd9\u4e00\u6765\uff0c\u4ed6\u518d\u4e5f\u4e0d\u6562\u6258\u5927\uff0c\u821e\u52a8\u62c2\u5c18\u75be\u653b\u3002 \u3000\u3000\u4e24\u4eba\u7ffb\u7ffb\u6eda\u6eda\u7684\u6597\u4e86\u4e8c\u767e\u4f59\u62db\uff0c\u5140\u81ea\u96be\u5206\u9ad8\u4e0b\uff0c\u90fd\u662f\u6697\u6697\u9a87\u5f02\u3002\u8881\u627f\u5fd7\u4e0d\u6562\u4e71\u4f7f\u91d1\u86c7\u5251\u6cd5\u548c\u6728\u6851\u6240\u6388\u7684\u529f\u592b\uff0c\u524d\u8005\u7a76\u672a\u5341\u5206\u7eaf\u719f\uff0c\u540e\u8005\u5bf9\u65b9\u4f3c\u6240\u6df1\u77e5\uff0c\u62db\u62db\u4f7f\u7684\u5c3d\u662f\u534e\u5c71\u6d3e\u672c\u95e8\u5251\u6cd5\u3002\u91d1\u86c7\u5251\u672c\u6765\u950b\u9510\u7edd\u4f26\uff0c\u65e0\u575a\u4e0d\u6467\uff0c\u4f46\u7389\u771f\u5b50\u7684\u62c2\u5c18\u5c18\u4e1d\u67d4\u8f6f\uff0c\u6beb\u4e0d\u53d7\u529b\uff0c\u7adf\u662f\u524a\u5b83\u4e0d\u65ad\u3002\u91d1\u86c7\u5251\u4e0e\u62c2\u5c18\u62db\u672f\u53d8\u5e7b\uff0c\u52b2\u98ce\u9f13\u8361\uff0c\u5d07\u653f\u6bbf\u56db\u5468\u5de8\u70db\u5ffd\u660e\u5ffd\u6697\u3002 \u3000\u3000\u53c8\u62c6\u6570\u5341\u62db\uff0c\u84e6\u542c\u5f97\u7687\u592a\u6781\u4ee5\u6ee1\u6d32\u8bed\u547c\u559d\u51e0\u53e5\uff0c\u516d\u540d\u5e03\u5e93\u6b66\u58eb\u5206\u4ece\u4e09\u9762\u6251\u4e0a\u3002\u8881\u627f\u5fd7\u6599\u60f3\u4eca\u65e5\u5df2\u523a\u4e0d\u5230\u9791\u5b50\u7687\u5e1d\uff0c\u6025\u6325\u957f\u5251\u75be\u653b\u4e24\u62db\uff0c\u8f6c\u8eab\u5411\u6bbf\u95e8\u5954\u51fa\u3002\u7389\u771f\u5b50\u62c2\u5c18\u6325\u51fa\uff0c\u5c18\u4e1d\u5df2\u5377\u4f4f\u4e86\u91d1\u86c7\u5251\u7684\u5c16\u94a9\u3002\u4e24\u4eba\u540c\u65f6\u62c9\u626f\uff0c\u7247\u523b\u95f4\u76f8\u6301\u4e0d\u4e0b\u3002\u4fbf\u5728\u8fd9\u65f6\uff0c\u4e24\u540d\u6b66\u58eb\u5df2\u540c\u65f6\u6293\u4f4f\u4e86\u8881\u627f\u5fd7\u53cc\u81c2\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u559d\u4e00\u58f0\uff0c\u677e\u624b\u64a4\u5251\uff0c\u53cc\u638c\u5728\u4e24\u540d\u6b66\u58eb\u80cc\u4e0a\u4e00\u62cd\uff0c\u8fd0\u8d77\u6df7\u5143\u529f\u5185\u52b2\uff0c\u4e24\u540d\u6b66\u58eb\u8eab\u4e0d\u7531\u4e3b\u7684\u5411\u7389\u771f\u5b50\u649e\u53bb\uff0c\u7389\u771f\u5b50\u65e0\u5948\uff0c\u53ea\u5f97\u4e5f\u64a4\u624b\u677e\u5f00\u62c2\u5c18\u4e4b\u67c4\uff0c\u51fa\u638c\u63a8\u5f00\u4e24\u540d\u6b66\u58eb\uff0c\u545b\u5577\u5577\u4e00\u54cd\uff0c\u62c2\u5c18\u4e0e\u91d1\u86c7\u5251\u540c\u65f6\u6389\u843d\u5728\u5730\u3002\u4fbf\u5728\u8fd9\u65f6\uff0c\u4e24\u540d\u6b66\u58eb\u5df2\u62b1\u4f4f\u4e86\u8881\u627f\u5fd7\u53cc\u817f\u3002 \u3000\u3000\u7389\u771f\u5b50\u53f3\u638c\u5411\u8881\u627f\u5fd7\u80f8\u53e3\u62cd\u5230\u3002\u8881\u627f\u5fd7\u53cc\u8db3\u51dd\u7acb\uff0c\u8fd8\u638c\u62cd\u51fa\u3002\u4e24\u540d\u6b66\u58eb\u62da\u547d\u62c9\u626f\uff0c\u8981\u5c06\u4ed6\u6273\u5012\uff0c\u5374\u54ea\u91cc\u6273\u5f97\u52a8\uff1f\u7389\u771f\u5b50\u638c\u6765\u5982\u98ce\uff0c\u77ac\u606f\u4e4b\u95f4\u8fde\u51fa\u4e00\u5341\u4e8c\u638c\u3002\u8881\u627f\u5fd7\u4e00\u4e00\u89e3\u5f00\uff0c\u7a81\u7136\u9888\u4e2d\u4e00\u7d27\uff0c\u4e00\u540d\u6b66\u58eb\u6251\u5728\u4ed6\u80cc\u4e0a\uff0c\u4f38\u81c2\u627c\u4f4f\u4e86\u4ed6\u54bd\u5589\u3002\u8881\u627f\u5fd7\u5de6\u8098\u5411\u540e\u649e\u51fa\uff0c\u6b63\u4e2d\u4ed6\u80f8\u8179\u4e4b\u95f4\u3002\u90a3\u6b66\u58eb\u72c2\u55b7\u9c9c\u8840\uff0c\u90fd\u55b7\u5728\u8881\u627f\u5fd7\u540e\u9888\uff0c\u70ed\u8840\u6c69\u6c69\u4ece\u4ed6\u8863\u9886\u4e2d\u6d41\u5411\u80cc\u5fc3\uff0c\u627c\u4f4f\u4ed6\u54bd\u5589\u7684\u624b\u81c2\u6e10\u677e\u3002\u8881\u627f\u5fd7\u6b63\u5f85\u8fd0\u52b2\u6446\u8131\uff0c\u4e00\u540d\u6b66\u58eb\u6251\u4e0a\u6765\u626d\u4f4f\u4e86\u4ed6\u53f3\u81c2\u3002\u7389\u771f\u5b50\u4e58\u673a\u51fa\u6307\u75be\u70b9\uff0c\u8881\u627f\u5fd7\u4f38\u5de6\u624b\u6321\u683c\u3002\u4ed6\u867d\u53ea\u5269\u4e0b\u4e00\u53ea\u5de6\u81c2\u53ef\u7528\uff0c\u4ecd\u662f\u6321\u4f4f\u4e86\u7389\u771f\u5b50\u70b9\u6765\u7684\u4e03\u6307\u8fde\u70b9\u3002 \u3000\u3000\u7389\u771f\u5b50\u53f3\u6307\u518d\u70b9\uff0c\u5de6\u638c\u62cd\u5411\u8881\u627f\u5fd7\u9762\u95e8\u3002\u8881\u627f\u5fd7\u6025\u5fd9\u4fa7\u5934\u76f8\u907f\uff0c\u5de6\u81c2\u5374\u53c8\u88ab\u4e00\u540d\u6b66\u58eb\u62b1\u4f4f\u4e86\u3002\u7389\u771f\u5b50\u5657\u5657\u5657\u8fde\u70b9\u4e09\u4e0b\uff0c\u70b9\u4e86\u4ed6\u80f8\u53e3\u4e09\u5904\u5927\u7a74\uff0c\u7b11\u9053\uff1a\u201c\u653e\u5f00\u5427\uff0c\u4ed6\u52a8\u4e0d\u4e86\u5566\u3002\u201d\u56db\u540d\u62b1\u4f4f\u8881\u627f\u5fd7\u53cc\u624b\u53cc\u817f\u7684\u6b66\u58eb\u5374\u8bf4\u751a\u4e48\u4e5f\u4e0d\u653e\u624b\u3002 \u3000\u3000\u7687\u592a\u6781\u7684\u4f8d\u536b\u961f\u957f\u62ff\u8fc7\u94c1\u94fe\uff0c\u5728\u8881\u627f\u5fd7\u8eab\u4e0a\u548c\u624b\u8db3\u4e0a\u7ed5\u4e86\u6570\u8f6c\uff0c\u4f17\u6b66\u58eb\u8fd9\u624d\u653e\u624b\uff0c\u5c06\u4f38\u81c2\u627c\u5728\u8881\u627f\u5fd7\u9888\u4e2d\u7684\u6b66\u58eb\u6276\u4e0b\u6765\u65f6\uff0c\u53ea\u89c1\u4ed6\u51f8\u775b\u4f38\u820c\uff0c\u65e9\u5df2\u6c14\u7edd\u800c\u6b7b\u3002 \u3000\u3000\u7687\u592a\u6781\u9053\uff1a\u201c\u7389\u771f\u603b\u6559\u5934\u548c\u4f17\u6b66\u58eb\u3001\u4f17\u4f8d\u536b\u62a4\u9a7e\u6709\u529f\uff0c\u91cd\u91cd\u6709\u8d4f\u3002\u8001\u9c8d\u3001\u8001\u5b81\uff0c\u4f60\u4eec\u53d7\u4f24\u4e86\u5417\uff1f\u201d\u9c8d\u627f\u5148\u548c\u5b81\u5b8c\u6211\u5df2\u7531\u4f17\u4f8d\u536b\u6276\u8d77\uff0c\u54fc\u54fc\u5527\u5527\u7684\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u7687\u592a\u6781\u56de\u5165\u9f99\u6905\u5750\u4e0b\uff0c\u7b11\u541f\u541f\u7684\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9\u5e74\u8f7b\u4eba\u6b66\u529f\u5f3a\u5f97\u5f88\u54ea\uff0c\u4f60\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u201d\u8881\u627f\u5fd7\u6602\u7136\u9053\uff1a\u201c\u6211\u884c\u523a\u4e0d\u6210\uff0c\u5feb\u628a\u6211\u6740\u4e86\uff0c\u591a\u95ee\u4e9b\u751a\u4e48\uff1f\u201d\u7687\u592a\u6781\u9053\uff1a\u201c\u662f\u8c01\u6307\u4f7f\u4f60\u6765\u523a\u6211\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u4fbf\u7167\u5b9e\u800c\u8a00\uff0c\u4e5f\u597d\u8ba9\u9791\u5b50\u77e5\u9053\u8881\u7763\u5e08\u6709\u5b50\u3002\u201d\u5927\u58f0\u9053\uff1a\u201c\u6211\u662f\u524d\u84df\u8fbd\u7763\u5e08\u8881\u516c\u7684\u513f\u5b50\uff0c\u540d\u53eb\u8881\u627f\u5fd7\u3002\u4f60\u9791\u5b50\u4fb5\u72af\u6211\u5927\u660e\u6c5f\u5c71\uff0c\u6211\u5343\u4e07\u6c49\u4eba\uff0c\u6068\u4e0d\u5f97\u98df\u4f60\u4e4b\u8089\u3002\u6211\u4eca\u65e5\u6765\u884c\u523a\uff0c\u662f\u4e3a\u6211\u7239\u7239\u62a5\u4ec7\uff0c\u4e3a\u6211\u6210\u5343\u6210\u4e07\u6b7b\u5728\u4f60\u624b\u4e0b\u7684\u6c49\u4eba\u62a5\u4ec7\u3002\u201d \u3000\u3000\u7687\u592a\u6781\u4e00\u51db\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8881\u5d07\u7115\u7684\u513f\u5b50\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6b63\u662f\u3002\u6211\u540d\u53eb\u8881\u627f\u5fd7\uff0c\u4fbf\u662f\u8981\u7ee7\u627f\u6211\u7239\u7239\u9057\u5fd7\uff0c\u6297\u5fa1\u4f60\u9791\u5b50\u5165\u4fb5\u3002\u201d \u3000\u3000\u4f17\u4f8d\u536b\u8fde\u58f0\u547c\u559d\uff1a\u201c\u8dea\u4e0b\uff01\u201d\u8881\u627f\u5fd7\u5168\u4e0d\u7406\u776c\u3002\u7687\u592a\u6781\u6325\u624b\u547d\u4f17\u4f8d\u536b\u4e0d\u5fc5\u518d\u559d\uff0c\u6e29\u8a00\u9053\uff1a\u201c\u8881\u5d07\u7115\u539f\u6765\u6709\u540e\uff0c\u90a3\u597d\u5f97\u5f88\u554a\u3002\u4f60\u8fd8\u6709\u5144\u5f1f\u6ca1\u6709\uff1f\u201d\u8881\u627f\u5fd7\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u95ee\u8fd9\u4e2a\u5e72\u4e48\uff1f\u201d \u3000\u3000\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d\u7687\u592a\u6781\u95ee\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u4f24\u6ca1\u6709\uff1f\u201d\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u5feb\u5c06\u6211\u6740\u4e86\uff0c\u4e0d\u7528\u4f60\u5047\u60fa\u60fa\u3002\u201d \u3000\u3000\u7687\u592a\u6781\u53f9\u9053\uff1a\u201c\u4f60\u7239\u7239\u8881\u516c\uff0c\u6211\u662f\u5f88\u4f69\u670d\u7684\u3002\u53ef\u60dc\u5d07\u796f\u7687\u5e1d\u4e0d\u660e\u662f\u975e\uff0c\u6740\u5bb3\u4e86\u5fe0\u826f\u3002\u5f53\u5e74\u4f60\u7239\u7239\u8ddf\u6211\u66fe\u6709\u548c\u8bae\uff0c\u660e\u6e05\u4e24\u56fd\u7f62\u5175\u4f11\u6c11\uff0c\u6c38\u4e3a\u4e16\u597d\u3002\u53ea\u53ef\u60dc\u548c\u8bae\u4e0d\u6210\uff0c\u5d07\u796f\u53cd\u800c\u8bf4\u8fd9\u662f\u4f60\u7239\u7239\u7684\u5927\u7f6a\uff0c\u6211\u542c\u5230\u540e\u5f88\u662f\u75db\u5fc3\u3002\u5d07\u796f\u6740\u4f60\u7239\u7239\uff0c\u4f60\u53ef\u77e5\u662f\u54ea\u4e24\u6761\u7f6a\u540d\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9ed8\u7136\u3002\u4ed6\u65e9\u77e5\u5d07\u796f\u6740\u4ed6\u7239\u7239\uff0c\u6709\u4e24\u6761\u7f6a\u540d\uff0c\u4e00\u662f\u4e0e\u6e05\u914b\u8bae\u548c\uff0c\u52fe\u7ed3\u5916\u654c\uff0c\u4e8c\u662f\u64c5\u6740\u76ae\u5c9b\u603b\u5175\u6bdb\u6587\u9f99\u3002\u5b59\u4ef2\u5bff\u3001\u5e94\u677e\u7b49\u8bf4\u5f97\u660e\u767d\uff0c\u5f53\u65e5\u8881\u7763\u5e08\u548c\u7687\u592a\u6781\u8bae\u548c\uff0c\u53ea\u662f\u4e00\u65f6\u6743\u5b9c\u4e4b\u8ba1\uff0c\u6e05\u5175\u52bf\u5927\uff0c\u660e\u5175\u529b\u6240\u4e0d\u654c\uff0c\u53ea\u6709\u7ec3\u6210\u4e86\u7cbe\u5175\u4e4b\u540e\uff0c\u65b9\u6709\u7834\u654c\u7684\u628a\u63e1\uff0c\u8bae\u548c\u662f\u4e3a\u4e86\u7ec3\u5175\u4e0e\u5b8c\u7f2e\u57ce\u5b88\u3002\u81f3\u4e8e\u6bdb\u6587\u9f99\u8d2a\u8d43\u8dcb\u6248\uff0c\u52ab\u63a0\u767e\u59d3\uff0c\u4e0d\u6740\u4ed6\u65e0\u4ee5\u6574\u8083\u519b\u7eaa\u3002 \u3000\u3000\u7687\u592a\u6781\u9053\uff1a\u201c\u4f60\u7239\u7239\u662f\u5d07\u796f\u5bb3\u6b7b\u7684\uff0c\u6211\u5374\u662f\u4f60\u7239\u7239\u7684\u670b\u53cb\u3002 \u3000\u3000\u4f60\u600e\u5730\u4e0d\u5206\u597d\u6b79\uff0c\u4e0d\u53bb\u6740\u5d07\u796f\uff0c\u5374\u6765\u5411\u6211\u884c\u523a\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u7239\u7239\u662f\u4f60\u654c\u4eba\uff0c\u600e\u4f1a\u662f\u4f60\u670b\u53cb\uff1f\u4f60\u4f7f\u4e0b\u53cd\u95f4\u8ba1\uff0c\u9a97\u4fe1\u5d07\u796f\uff0c\u5bb3\u6b7b\u6211\u7239\u7239\u3002\u5d07\u796f\u8981\u6740\uff0c\u4f60\u4e5f\u8981\u6740\u3002\u201d\u7687\u592a\u6781\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u5e74\u8f7b\u4e0d\u61c2\u4e8b\uff0c\u751a\u4e48\u4e5f\u4e0d\u660e\u767d\u3002\u201d\u8f6c\u5934\u5411\u8303\u6587\u7a0b\u9053\uff1a\u201c\u8303\u5148\u751f\uff0c\u4f60\u5f00\u5bfc\u5f00\u5bfc\u4ed6\u3002\u201d\u8881\u627f\u5fd7\u5927\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u8981\u6211\u5b66\u6d2a\u627f\u7574\u4e48\uff1f\u54fc\uff0c\u8881\u7763\u5e08\u7684\u513f\u5b50\uff0c\u4f1a\u6295\u964d\u6ee1\u6e05\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5d07\u653f\u6bbf\u5916\u5df2\u805a\u96c6\u4e86\u4e0d\u5c11\u6587\u6b66\u5b98\u5458\uff0c\u90fd\u662f\u542c\u8bf4\u6709\u523a\u5ba2\u72af\u9a7e\u3001\u5924\u591c\u8d76\u6765\u62a4\u9a7e\u7684\u3002\u7687\u592a\u6781\u9053\uff1a\u201c\u7956\u5927\u5bff\u5728\u8fd9\u91cc\u5417\uff1f\u201d\u9636\u4e0b\u4e00\u540d\u6b66\u5c06\u9053\uff1a\u201c\u81e3\u5728\uff01\u201d\u8d70\u5230\u6bbf\u4e0a\uff0c\u8dea\u4e0b\u78d5\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u51db\uff0c\u7956\u5927\u5bff\u662f\u7236\u4eb2\u5f53\u5e74\u9ebe\u4e0b\u7684\u7b2c\u4e00\u5927\u5c06\uff0c\u7236\u4eb2\u88ab\u5d07\u796f\u4e0b\u65e8\u64d2\u62ff\u65f6\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u670d\uff0c\u5e26\u5175\u53cd\u51fa\u5317\u4eac\uff0c\u540e\u6765\u7236\u4eb2\u5728\u72f1\u4e2d\u4fee\u4e66\u76f8\u529d\uff0c\u4ed6\u624d\u91cd\u53d7\u5d07\u796f\u4ee4\u65e8\u3002\u4ed6\u4e0e\u6e05\u5175\u8840\u6218\u524d\u540e\u6570\u5341\u573a\uff0c\u4f46\u5d07\u796f\u5bf9\u4ed6\u7591\u5fcc\uff0c\u6bcf\u6b21\u90fd\u4e0d\u4e88\u589e\u63f4\uff0c\u81f4\u5728\u5927\u51cc\u6cb3\u4e3a\u7687\u592a\u6781\u91cd\u91cd\u56f4\u56f0\uff0c\u4e0d\u5f97\u5df2\u800c\u6295\u964d\uff1b\u6b64\u540e\u964d\u4e86\u53c8\u53cd\uff0c\u5728\u9526\u5dde\u6570\u573a\u8840\u6218\uff0c\u540e\u63f4\u4e0d\u7ee7\uff0c\u88ab\u64d2\u53c8\u964d\u3002\u5fc3\u60f3\uff1a\u201c\u4ed6\u5bf9\u6211\u7239\u7239\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u6295\u964d\u9791\u5b50\u603b\u662f\u5927\u5927\u4e0d\u8be5\u3002\u201d\u5fcd\u4e0d\u4f4f\u9ad8\u58f0\u65a5\u9053\uff1a\u201c\u7956\u5927\u5bff\uff0c\u4f60\u8fd9\u65e0\u803b\u6c49\u5978\uff01\u201d \u3000\u3000\u7956\u5927\u5bff\u7ad9\u8d77\u8eab\u6765\uff0c\u8f6c\u5934\u77a7\u7740\u4ed6\u3002\u8881\u627f\u5fd7\u89c1\u4ed6\u5243\u4e86\u989d\u524d\u5934\u53d1\uff0c\u62d6\u6839\u8fab\u5b50\uff0c\u5934\u53d1\u5df2\u7136\u82b1\u767d\uff0c\u5bb9\u8272\u6194\u60b4\uff0c\u5168\u65e0\u7edf\u5175\u5927\u5c06\u7684\u534a\u5206\u82f1\u6c14\uff0c\u559d\u9053\uff1a\u201c\u7956\u5927\u5bff\uff0c\u4f60\u8fd8\u6709\u8138\u89c1\u6211\u5417\uff1f\u4f60\u6b7b\u4e86\u4e4b\u540e\uff0c\u6709\u8138\u53bb\u89c1\u6211\u7239\u7239\u5417\uff1f\u201d \u3000\u3000\u7956\u5927\u5bff\u5728\u9636\u4e0b\u65f6\u5df2\u542c\u5230\u7687\u592a\u6781\u548c\u8881\u627f\u5fd7\u5bf9\u7b54\u7684\u540e\u534a\u622a\u8bdd\uff0c\u7a81\u7136\u773c\u6cea\u4ece\u53cc\u988a\u4e0a\u6d41\u4e86\u4e0b\u6765\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u8881\u516c\u5b50\uff0c\u4f60\u2026\u2026\u4f60\u957f\u5f97\u8fd9\u4e48\u5927\u4e86\uff0c\u4f60\u2026\u2026\u4f60\u4e09\u5c81\u7684\u65f6\u5019\uff0c\u6211\u2026\u2026\u6211\u62b1\u8fc7\u4f60\u7684\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6012\u9053\uff1a\u201c\u5478\uff0c\u7ed9\u4f60\u8fd9\u6c49\u5978\u62b1\u8fc7\uff0c\u7b97\u6211\u5012\u9709\u3002\u201d\u7956\u5927\u5bff\u5168\u8eab\u4e00\u98a4\uff0c\u5f20\u5f00\u53cc\u81c2\uff0c\u8e0f\u4e0a\u4e24\u6b65\uff0c\u4f3c\u4e4e\u53c8\u60f3\u53bb\u62b1\u4ed6\uff0c\u4f46\u7ec8\u4e8e\u505c\u6b65\uff0c\u5f20\u5634\u8981\u5f85\u8bf4\u8bdd\uff0c\u58f0\u97f3\u5374\u54d1\u4e86\uff0c\u53ea\u201c\u554a\uff0c\u554a\uff0c\u554a\u201d\u51e0\u58f0\u3002 \u3000\u3000\u7687\u592a\u6781\u9053\uff1a\u201c\u7956\u5927\u5bff\uff0c\u8fd9\u59d3\u8881\u7684\u4ea4\u7531\u4f60\u5e26\u53bb\uff0c\u597d\u597d\u529d\u4ed6\u5f52\u987a\u3002\u5f53\u771f\u4e0d\u964d\uff0c\u54b1\u4eec\u628a\u4ed6\u5343\u5200\u4e07\u5250\u3002\u54fc\uff0c\u8fd9\u5c0f\u5b50\u80c6\u5b50\u5012\u5927\uff0c\u5c45\u7136\u6765\u5411\u6715\u884c\u523a\uff0c\u563f\u563f\uff0c\u563f\u563f\u3002\u201d\u7956\u5927\u5bff\u8dea\u4e0b\u8fde\u8fde\u78d5\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u5929\u6069\u6d69\u8361\uff0c\u81e3\u81ea\u5f53\u5c3d\u529b\u76f8\u529d\u3002\u201d\u7687\u592a\u6781\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5e26\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u7956\u5927\u5bff\u8d70\u5230\u8881\u627f\u5fd7\u8eab\u8fb9\uff0c\u4f38\u624b\u6b32\u6276\u3002\u8881\u627f\u5fd7\u9000\u540e\u4e24\u6b65\uff0c\u624b\u811a\u4e0a\u94c1\u94fe\u5f53\u5577\u5577\u76f4\u54cd\uff0c\u559d\u9053\uff1a\u201c\u522b\u6765\u78b0\u6211\uff01\u201d\u7956\u5927\u5bff\u7f29\u5f00\u4e86\u624b\uff0c\u8eac\u8eab\u9000\u51fa\u6bbf\u53bb\u3002\u4e24\u540d\u4f8d\u536b\u643a\u7740\u8881\u627f\u5fd7\uff0c\u8ddf\u5728\u4ed6\u8eab\u540e\u3002\u8881\u627f\u5fd7\u56de\u8fc7\u5934\u6765\uff0c\u5411\u7687\u592a\u6781\u77a7\u53bb\uff0c\u53ea\u89c1\u4ed6\u773c\u5149\u4e5f\u6b63\u5411\u4ed6\u77a7\u6765\uff0c\u795e\u8272\u95f4\u5374\u663e\u5f97\u751a\u662f\u548c\u853c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u832b\u7136\u4e0d\u89e3\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u77e5\u8fd9\u9791\u5b50\u7687\u5e1d\u809a\u5b50\u91cc\u5728\u6253\u751a\u4e48\u9b3c\u4e3b\u610f\u3002\u201d \u3000\u3000\u5230\u5f97\u5bab\u5916\uff0c\u7956\u5927\u5bff\u547d\u4eb2\u968f\u5c06\u8881\u627f\u5fd7\u6276\u4e0a\u81ea\u5df1\u7684\u5750\u9a91\uff0c\u81ea\u5df1\u53e6\u884c\u9a91\u4e86\u5339\u9a6c\uff0c\u540c\u5230\u81ea\u5df1\u5e9c\u4e2d\u3002\u7956\u5927\u5bff\u547d\u4eb2\u968f\u5c06\u8881\u627f\u5fd7\u6276\u5165\u4e66\u623f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u51fa\u53bb\uff01\u201d\u56db\u540d\u4eb2\u968f\u8eac\u8eab\u51fa\u623f\u3002 \u3000\u3000\u7956\u5927\u5bff\u63a9\u4e0a\u4e86\u623f\u95e8\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4fbf\u53bb\u89e3\u8881\u627f\u5fd7\u8eab\u4e0a\u7684\u94c1\u94fe\u3002\u8881\u627f\u5fd7\u81ea\u5728\u5bab\u5185\u4e4b\u65f6\uff0c\u4fbf\u5df2\u7f13\u7f13\u8fd0\u6c14\uff0c\u80f8\u53e3\u6240\u5c01\u7a74\u9053\u5df2\u89e3\u4e86\u5927\u534a\uff0c\u89c1\u4ed6\u7adf\u6765\u89e3\u81ea\u5df1\u8eab\u4e0a\u94c1\u94fe\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u53ea\u9053\u6211\u7a74\u9053\u88ab\u70b9\uff0c\u5140\u81ea\u52a8\u5f39\u4e0d\u5f97\uff0c\u54fc\u54fc\uff0c\u8fd9\u53ef\u592a\u4e5f\u6258\u5927\u4e86\uff01\u201d \u3000\u3000\u7956\u5927\u5bff\u7f13\u7f13\u5c06\u94c1\u94fe\u4e00\u5708\u5708\u7684\u4ece\u8881\u627f\u5fd7\u8eab\u4e0a\u7ed5\u8131\uff0c\u59cb\u7ec8\u4e00\u8a00\u4e0d\u53d1\u3002\u8881\u627f\u5fd7\u6697\u6697\u8fd0\u6c14\uff0c\u89c9\u81bb\u4e2d\u7a74\u5904\u6c14\u606f\u4ecd\u9887\u7a92\u6ede\uff0c\u5fc3\u60f3\uff1a\u201c\u90a3\u9053\u4eba\u7684\u624b\u52b2\u5f53\u771f\u4e86\u5f97\u3002\u6211\u7a7f\u7740\u6728\u6851\u9053\u957f\u6240\u8d50\u7684\u91d1\u4e1d\u80cc\u5fc3\uff0c\u53d7\u4e86\u4ed6\u8fd9\u4e09\u6307\uff0c\u5140\u81ea\u5982\u6b64\u3002\u82e5\u65e0\u8fd9\u80cc\u5fc3\u62a4\u4f53\uff0c\u54ea\u8fd8\u4e86\u5f97\uff1f\u201d\u53c8\u60f3\uff1a\u201c\u7956\u5927\u5bff\u8981\u529d\u6211\u6295\u964d\u9791\u5b50\uff0c\u6211\u4e14\u5047\u88c5\u542c\u4ed6\u7684\uff0c\u62d6\u5ef6\u65f6\u523b\u3002 \u3000\u3000\u4e00\u5f85\u80f8\u95f4\u6c14\u606f\u987a\u7545\uff0c\u4fbf\u53d1\u638c\u51fb\u6b7b\u4e86\u8fd9\u6c49\u5978\uff0c\u7a7f\u7a97\u9003\u8d70\u3002\u201d\u5374\u542c\u7956\u5927\u5bff\u4f4e\u6c89\u7740\u55d3\u5b50\u9053\uff1a\u201c\u8881\u516c\u5b50\uff0c\u4f60\u8fd9\u5c31\u53bb\u5427\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5927\u5403\u4e00\u60ca\uff0c\u51e0\u4e4e\u4e0d\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u751a\u4e48\uff1f\u201d\u7956\u5927\u5bff\u9053\uff1a\u201c\u8981\u523a\u6740\u5927\u6e05\u7687\u5e1d\uff0c\u5b9e\u5728\u96be\u5f97\u5f88\u3002\u4f60\u8fd8\u662f\u53bb\u5427\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u653e\u6211\u8d70\uff1f\u201d\u7956\u5927\u5bff\u9053\uff1a\u201c\u662f\uff0c\u4f60\u6709\u6ca1\u6709\u53d7\u4f24\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u7956\u5927\u5bff\u9053\uff1a\u201c\u4f60\u9a91\u6211\u7684\u9a6c\uff0c\u5929\u4e00\u4eae\u7acb\u5373\u51fa\u57ce\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u653e\u6211\u8d70\uff1f\u201d\u7956\u5927\u5bff\u9eef\u7136\u9053\uff1a\u201c\u4f60\u662f\u8881\u7763\u5e08\u7684\u4eb2\u9aa8\u8840\uff0c\u7956\u5927\u5bff\u8eab\u53d7\u7763\u5e08\u539a\u6069\uff0c\u65e0\u4ee5\u4e3a\u62a5\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u653e\u4e86\u6211\uff0c\u660e\u5929\u9791\u5b50\u7687\u5e1d\u67e5\u95ee\u8d77\u6765\uff0c\u4f60\u5b9a\u6709\u6b7b\u7f6a\u3002\u201d\u7956\u5927\u5bff\u9053\uff1a\u201c\u90a3\u8d70\u7740\u77a7\u5427\u3002\u5927\u6e05\u7687\u5e1d\u8bf4\u8fc7\uff0c\u4e0d\u4f1a\u6740\u6211\u7684\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u79c1\u653e\u523a\u5ba2\uff0c\u7f6a\u540d\u592a\u5927\uff0c\u7687\u5e1d\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u7591\u5fc3\u4f60\u662f\u884c\u523a\u7684\u4e3b\u4f7f\u3002\u6211\u4e0d\u80fd\u81ea\u5df1\u8d2a\u751f\uff0c\u5374\u5bb3\u4e86\u4f60\u4e00\u547d\u3002\u201d \u3000\u3000\u7956\u5927\u5bff\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7684\u6027\u547d\uff0c\u8fd8\u503c\u5f97\u751a\u4e48\uff1f\u5728\u5927\u51cc\u6cb3\u57ce\u7834\u4e4b\u65e5\uff0c\u6211\u65e9\u8be5\u6b7b\u4e86\u3002\u9526\u5dde\u57ce\u7834\u4e4b\u65e5\uff0c\u66f4\u8be5\u5f53\u6b7b\u4e86\u3002\u8881\u516c\u5b50\uff0c\u4f60\u4e0d\u7528\u7ba1\u6211\uff0c\u81ea\u5df1\u53bb\u5427\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8ddf\u6211\u4e00\u8d77\u9003\u8d70\u3002\u201d \u3000\u3000\u7956\u5927\u5bff\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u8001\u6bcd\u59bb\u513f\u3001\u5144\u5f1f\u5b50\u4f84\uff0c\u4e00\u5bb6\u516b\u5341\u4f59\u53e3\u5168\u5728\u76db\u4eac\uff0c\u6211\u662f\u4e0d\u80fd\u9003\u7684\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u795e\u6fc0\u8361\uff0c\u7a81\u7136\u80f8\u53e3\u5185\u606f\u9006\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u8fde\u8fde\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u5fc3\u4e0b\u5bfb\u601d\uff1a\u201c\u4ed6\u6295\u964d\u9791\u5b50\uff0c\u5c31\u662f\u6c49\u5978\uff0c\u6211\u539f\u8be5\u4e00\u638c\u6253\u6b7b\u4e86\u4ed6\uff0c\u60f3\u4e0d\u5230\u4ed6\u7adf\u4f1a\u653e\u6211\u8d70\u3002\u6211\u4e00\u8d70\uff0c\u9791\u5b50\u7687\u5e1d\u975e\u6740\u4e86\u4ed6\u4e0d\u53ef\u3002 \u3000\u3000\u662f\u6211\u6740\u4ed6\uff0c\u8fd8\u662f\u9791\u5b50\u6740\u4ed6\uff0c\u672c\u6765\u6beb\u65e0\u5206\u522b\u3002\u4f46\u662f\u6211\u96be\u9053\u773c\u7741\u7741\u7684\u8ba9\u4ed6\u4ee3\u6211\u800c\u6b7b\uff1f\u6211\u82e5\u4e0d\u8d70\uff0c\u81ea\u7136\u662f\u7ed9\u9791\u5b50\u6740\u4e86\uff0c\u6211\u4ee5\u6709\u4e3a\u4e4b\u8eab\uff0c\u5c1a\u6709\u591a\u5c11\u5927\u4e8b\u672a\u4e86\uff0c\u600e\u80fd\u8f7b\u6613\u9001\u547d\uff1f\u6211\u5f53\u7136\u4e0d\u60f3\u6b7b\uff0c\u4e3a\u4e86\u4e00\u4e2a\u6c49\u5978\u800c\u6b7b\uff0c\u66f4\u52a0\u4e0d\u503c\u4e4b\u81f3\u3002\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u201d\u8d8a\u662f\u59d4\u51b3\u4e0d\u4e0b\uff0c\u8d8a\u662f\u54b3\u5f97\u5389\u5bb3\uff0c\u9762\u7ea2\u8033\u8d64\uff0c\u9669\u4e9b\u6c14\u4e5f\u5598\u4e0d\u8fc7\u6765\u3002 \u3000\u3000\u7956\u5927\u5bff\u8f7b\u8f7b\u62cd\u4ed6\u80cc\u810a\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u516c\u5b50\uff0c\u4f60\u521a\u624d\u6fc0\u6597\u8131\u529b\uff0c\u8eba\u4e0b\u6765\u6b47\u4e00\u4f1a\u513f\u3002\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u76d8\u819d\u800c\u5750\uff0c\u5fc3\u4e2d\u518d\u4e0d\u601d\u91cf\uff0c\u53ea\u662f\u51dd\u795e\u8fd0\u6c14\u3002\u90a3\u7389\u771f\u5b50\u7684\u70b9\u7a74\u529f\u592b\u5f53\u771f\u5389\u5bb3\uff0c\u521d\u65f6\u8fd8\u4ee5\u4e3a\u7ed9\u5c01\u95ed\u4e86\u7684\u7a74\u9053\u5df2\u7136\u89e3\u5f00\uff0c\u4f46\u4e00\u8fd0\u6c14\u95f4\uff0c\u4fbf\u89c9\u80f8\u53e3\u7ec8\u7a76\u4e0d\u751a\u987a\u7545\uff0c\u5fc3\u77e5\u5750\u7740\u4e0d\u52a8\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u82e5\u662f\u4e0e\u4eba\u52a8\u624b\uff0c\u6216\u662f\u65bd\u5c55\u8f7b\u529f\u8df3\u8dc3\u5954\u8dd1\uff0c\u52bf\u5fc5\u4f1a\u95ed\u6c14\u6655\u53a5\u3002\u4e8e\u662f\u6309\u7167\u5e08\u7236\u6240\u6388\u7684\u8c03\u7406\u5185\u606f\u6cd5\u95e8\uff0c\u7f13\u7f13\u5c06\u4e00\u80a1\u771f\u6c14\u5728\u5404\u5904\u7ecf\u8109\u4e2d\u8fd0\u884c\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u624d\u89c9\u771f\u6c14\u7545\u884c\u65e0\u963b\uff0c\u66f4\u65e0\u7a92\u6ede\uff0c\u6162\u6162\u7741\u5f00\u773c\u6765\uff0c\u5374\u89c1\u9633\u5149\u4ece\u7a97\u4e2d\u5c04\u8fdb\uff0c\u7adf\u5df2\u5929\u660e\u3002\u4ed6\u5fae\u5403\u4e00\u60ca\uff0c\u53ea\u89c1\u7956\u5927\u5bff\u5750\u5728\u4e00\u65c1\uff0c\u53cc\u624b\u6401\u819d\uff0c\u4f3c\u5728\u5446\u5446\u51fa\u795e\u3002\u8881\u627f\u5fd7\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u966a\u4e86\u6211\u534a\u591c\uff1f\u201d\u7956\u5927\u5bff\u8138\u4e0a\u5fae\u73b0\u559c\u8272\uff0c\u9053\uff1a\u201c\u516c\u5b50\u597d\u4e9b\u4e86\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5168\u597d\u4e86\uff01\u90a3\u7389\u771f\u5b50\u9053\u4eba\u662f\u751a\u4e48\u6765\u5386\uff1f\u6b66\u529f\u8fd9\u4e48\u5389\u5bb3\u3002\u201d\u7956\u5927\u5bff\u9053\uff1a\u201c\u4ed6\u662f\u65b0\u8fd1\u4ece\u897f\u85cf\u6765\u7684\uff0c\u4e0a\u4e2a\u6708\u5bab\u4e2d\u5e03\u5e93\u5927\u6821\u6280\uff0c\u8fd9\u9053\u4eba\u6253\u8d25\u4e8c\u5341\u4e09\u540d\u4e00\u7b49\u5e03\u5e93\u6b66\u58eb\uff0c\u540e\u6765\u56db\u4e94\u540d\u6b66\u58eb\u8054\u624b\u8ddf\u4ed6\u8f83\u91cf\uff0c\u4e5f\u90fd\u88ab\u4ed6\u6253\u8d25\u4e86\u3002\u7687\u5e1d\u5341\u5206\u559c\u6b22\uff0c\u5c01\u4e86\u4ed6\u4e00\u4e2a\u751a\u4e48\u2018\u62a4\u56fd\u771f\u4eba\u2019\u7684\u5934\u8854\uff0c\u8981\u4ed6\u4f5c\u5e03\u5e93\u603b\u6559\u5934\u3002\u516c\u5b50\uff0c\u4f60\u559d\u4e86\u8fd9\u7897\u9e21\u6c64\uff0c\u5403\u51e0\u5f20\u997c\uff0c\u54b1\u4eec\u8fd9\u5c31\u8d70\u5427\u3002\u201d\u8bf4\u7740\u8d70\u5230\u684c\u8fb9\uff0c\u53cc\u624b\u6367\u8fc7\u4e00\u7897\u6c64\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u4e13\u5fc3\u884c\u529f\uff0c\u6709\u4eba\u9001\u5403\u7684\u4e1c\u897f\u8fdb\u6765\u4e5f\u4e0d\u77e5\u9053\u3002\u4ed6\u672c\u6765\u5c31\u53ef\u6740\u6211\uff0c\u4e5f\u4e0d\u7528\u4e0b\u6bd2\u3002\u201d\u63a5\u8fc7\u6c64\u7897\uff0c\u559d\u4e86\u51e0\u53e3\uff0c\u5fae\u6709\u82e6\u6da9\u4e4b\u5473\u3002\u7956\u5927\u5bff\u9053\uff1a\u201c\u8fd9\u662f\u8fbd\u4e1c\u8001\u5c71\u4eba\u53c2\u7096\u7684\uff0c\u6700\u80fd\u8865\u6c14\u63d0\u795e\u3002\u201d\u8881\u627f\u5fd7\u5403\u4e86\u4e24\u5f20\u997c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e26\u6211\u53bb\u89c1\u9791\u5b50\u7687\u5e1d\uff0c\u6211\u6295\u964d\u4e86\u3002\u201d \u3000\u3000\u7956\u5927\u5bff\u5927\u5403\u4e00\u60ca\uff0c\u53cc\u76ee\u77aa\u89c6\u7740\u4ed6\uff0c\u968f\u5373\u660e\u767d\uff0c\u4ed6\u662f\u4e0d\u613f\u81ea\u5df1\u4e3a\u4ed6\u9001\u547d\uff0c\u5148\u884c\u5047\u610f\u6295\u964d\uff0c\u7136\u540e\u518d\u8c0b\u8131\u8eab\uff0c\u6c89\u541f\u7247\u523b\uff0c\u9053\uff1a\u201c\u597d\uff01\u201d\u5e26\u7740\u4ed6\u51fa\u4e86\u5e9c\u95e8\uff0c\u4e24\u4eba\u4e0a\u4e86\u9a6c\u3002\u7956\u5927\u5bff\u4e5f\u4e0d\u5e26\u968f\u4ece\uff0c\u5f53\u5148\u7eb5\u9a6c\u800c\u884c\uff0c\u8881\u627f\u5fd7\u8ddf\u968f\u5176\u540e\u3002 \u3000\u3000\u884c\u4e86\u51e0\u6761\u8857\uff0c\u8881\u627f\u5fd7\u89c1\u4ed6\u50ac\u9a6c\u8d70\u5411\u57ce\u95e8\uff0c\u89c1\u57ce\u95e8\u4e0a\u5199\u7740\u4e09\u4e2a\u5927\u5b57\u201c\u5fb7\u76db\u95e8\u201d\uff0c\u65c1\u8fb9\u6709\u4e00\u884c\u5f2f\u5f2f\u66f2\u66f2\u7684\u6ee1\u6d32\u6587\uff0c\u77e5\u9053\u8fd9\u662f\u76db\u4eac\u5357\u95e8\uff0c\u6628\u5929\u4fbf\u662f\u4ece\u8fd9\u57ce\u95e8\u4e2d\u8fdb\u6765\u7684\uff0c\u5fc3\u89c9\u8be7\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u600e\u5730\u51fa\u57ce\uff1f\u201d\u7956\u5927\u5bff\u9053\uff1a\u201c\u7687\u5e1d\u5728\u57ce\u5357\u54c8\u5c14\u6492\u5c71\u56f4\u730e\u3002\u201d\u8881\u627f\u5fd7\u4e0d\u518d\u8a00\u8bed\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u51fa\u57ce\u884c\u4e86\u7ea6\u83ab\u5341\u91cc\u3002\u7956\u5927\u5bff\u52d2\u9a6c\u505c\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b50\uff0c\u54b1\u4eec\u8fd9\u5c31\u522b\u8fc7\u4e86\u3002\u201d\u8881\u627f\u5fd7\u60ca\u9053\uff1a\u201c\u600e\u4e48\uff1f\u54b1\u4eec\u4e0d\u662f\u53bb\u89c1\u9791\u5b50\u7687\u5e1d\u4e48\uff1f\u201d\u7956\u5927\u5bff\u6447\u5934\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u8881\u7763\u5e08\u5fe0\u4e49\u5305\u5929\uff0c\u4ed6\u7684\u516c\u5b50\u600e\u80fd\u5982\u6211\u8fd9\u822c\u65e0\u803b\uff0c\u6295\u964d\u9791\u5b50\uff1f\u201d\u89e3\u4e0b\u8170\u95f4\u4f69\u5251\uff0c\u8fde\u9798\u5411\u4ed6\u63b7\u53bb\uff0c\u8881\u627f\u5fd7\u53ea\u5f97\u63a5\u4f4f\u3002\u7956\u5927\u5bff\u7a81\u7136\u5708\u8f6c\u9a6c\u5934\uff0c\u731b\u62bd\u4e24\u97ad\uff0c\u5750\u9a91\u5faa\u7740\u56de\u57ce\u7684\u6765\u8def\u75be\u9a70\u800c\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u7956\u53d4\u53d4\uff0c\u7956\u53d4\u53d4\u3002\u201d\u4e00\u65f6\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u8be5\u8ffd\u4ed6\u56de\u6765\uff0c\u8fd8\u662f\u548c\u4ed6\u4e00\u8d77\u56de\u57ce\uff0c\u5c31\u8fd9\u4e48\u5fae\u4e00\u8fdf\u7591\uff0c\u7956\u5927\u5bff\u50ac\u9a6c\u53bb\u5f97\u8fdc\u4e86\uff0c\u53ea\u542c\u4ed6\u8fdc\u8fdc\u53eb\u9053\uff1a\u201c\u591a\u8c22\u4f60\u53eb\u6211\u4e24\u58f0\u53d4\u53d4\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5750\u5728\u9a6c\u4e0a\uff0c\u832b\u7136\u82e5\u5931\uff0c\u8fc7\u4e86\u826f\u4e45\uff0c\u624d\u7eb5\u9a6c\u5357\u884c\u3002 \u3000\u3000\u53c8\u884c\u4e86\u7ea6\u83ab\u5341\u91cc\uff0c\u8fdc\u8fdc\u671b\u89c1\u9752\u9752\u3001\u6d2a\u80dc\u6d77\u3001\u6c99\u5929\u5e7f\u7b49\u4eba\u5df2\u7b49\u5728\u7ea6\u5b9a\u7684\u7834\u5e99\u4e4b\u5916\u3002\u9752\u9752\u5927\u58f0\u6b22\u547c\uff0c\u5feb\u6b65\u5954\u6765\uff0c\u6251\u5165\u4ed6\u7684\u6000\u91cc\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u56de\u6765\u5566\uff01\u4f60\u56de\u6765\u5566\uff01\u201d\u8881\u627f\u5fd7\u89c1\u5979\u8138\u4e0a\u5927\u6709\u5026\u5bb9\uff0c\u6599\u60f3\u5979\u7126\u8651\u6302\u6000\uff0c\u591a\u534a\u4e00\u591c\u672a\u7761\u3002 \u3000\u3000\u9752\u9752\u89c1\u4ed6\u6b8a\u65e0\u5174\u594b\u4e4b\u8272\uff0c\u731c\u5230\u884c\u523a\u6ca1\u6709\u6210\u529f\uff0c\u8bf4\u9053\uff1a\u201c\u627e\u4e0d\u5230\u9791\u5b50\u7687\u5e1d\uff1f\u201d\u8881\u627f\u5fd7\u6447\u6447\u5934\uff1a\u201c\u4eba\u662f\u627e\u5230\u4e86\uff0c\u523a\u4e0d\u5230\u3002\u201d\u4e8e\u662f\u7b80\u7565\u8bf4\u4e86\u7ecf\u8fc7\u3002\u4f17\u4eba\u542c\u5f97\u90fd\u5f20\u5927\u4e86\u53e3\uff0c\u5408\u4e0d\u62e2\u6765\u3002 \u3000\u3000\u9752\u9752\u62cd\u62cd\u80f8\u53e3\uff0c\u5401\u4e86\u53e3\u957f\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u60f3\u5230\u7956\u5927\u5bff\u8981\u4e3a\u81ea\u5df1\u9001\u547d\uff0c\u5fc3\u4e0b\u603b\u662f\u4e0d\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u6211\u8fd8\u8981\u5165\u57ce\uff0c\u5018\u82e5\u7956\u53d4\u53d4\u7ed9\u9791\u5b50\u7687\u5e1d\u6293\u4e86\u8d77\u6765\uff0c\u6211\u8981\u6551\u4ed6\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u5927\u4f19\u513f\u4e00\u8d77\u53bb\uff01\u6211\u53ef\u518d\u4e5f\u4e0d\u8ba9\u4f60\u72ec\u4e2a\u513f\u53bb\u5192\u9669\u4e86\u3002\u201d \u3000\u3000\u7533\u724c\u65f6\u5206\uff0c\u4e00\u884c\u4eba\u53c8\u5230\u4e86\u76db\u4eac\u57ce\u5185\uff0c\u751f\u6015\u6628\u5929\u5df2\u9732\u4e86\u884c\u8ff9\uff0c\u53e6\u6295\u4e00\u5bb6\u5ba2\u5e97\u501f\u5bbf\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u53bb\u7956\u5927\u5bff\u5e9c\u524d\u5bdf\u770b\uff0c\u56de\u62a5\u8bf4\uff0c\u6ca1\u542c\u5230\u7956\u5927\u5bff\u7ed9\u9791\u5b50\u7687\u5e1d\u9501\u62ff\u7684\u8baf\u606f\uff0c\u5e9c\u95e8\u5916\u5168\u6ca1\u52a8\u9759\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u9791\u5b50\u7687\u5e1d\u591a\u534a\u8fd8\u4e0d\u77e5\u4ed6\u5df2\u653e\u8d70\u4e86\u6211\uff0c\u53ea\u9053\u4ed6\u6b63\u5728\u529d\u6211\u6295\u964d\u3002\u201d\u5429\u5490\u6d2a\u80dc\u6d77\u518d\u53bb\u6253\u63a2\u3002\u94c1\u7f57\u6c49\u9053\uff1a\u201c\u6211\u4e5f\u53bb\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u53bb\uff0c\u522b\u53c8\u8ddf\u4eba\u6253\u67b6\uff0c\u8bef\u4e86\u5927\u4e8b\u3002\u201d\u94c1\u7f57\u6c49\u6485\u8d77\u4e86\u5634\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u4e00\u5b9a\u975e\u6253\u67b6\u4e0d\u53ef\u3002\u201d\u80e1\u6842\u5357\u9053\uff1a\u201c\u6211\u8ddf\u7f57\u6c49\u5927\u54e5\u540c\u53bb\uff0c\u4ed6\u8981\u95f9\u4e8b\uff0c\u6211\u62c9\u4f4f\u4ed6\u4fbf\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u4e00\u5207\u5c0f\u5fc3\u5728\u610f\u3002\u201d \u3000\u3000\u508d\u665a\u65f6\u5206\uff0c\u4e09\u4eba\u56de\u5230\u5ba2\u5e97\u3002\u94c1\u7f57\u6c49\u6781\u662f\u6c14\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u590f\u59d1\u5a18\u5148\u8bf4\u4e86\u6211\uff0c\u5426\u5219\u6211\u771f\u5f97\u626d\u4e0b\u90a3\u51e0\u4e2a\u5c0f\u5b50\u7684\u8111\u888b\u3002\u201d \u3000\u3000\u4f17\u4eba\u95ee\u8d77\u539f\u56e0\uff0c\u6d2a\u80dc\u6d77\u8bf4\u4e86\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u4ecd\u6ca1\u542c\u5230\u6709\u62ff\u6355\u7956\u5927\u5bff\u7684\u8baf\u606f\uff0c\u6628\u665a\u5bab\u91cc\u95f9\u523a\u5ba2\uff0c\u5374\u4e5f\u6ca1\u542c\u5230\u8857\u5934\u5df7\u5c3e\u6709\u4eba\u8c08\u8bba\u3002\u4e09\u4eba\u4e8e\u662f\u53bb\u9152\u697c\u559d\u9152\uff0c\u89c1\u5230\u6709\u516b\u540d\u5e03\u5e93\u6b66\u58eb\u5728\u5927\u5403\u5927\u559d\uff0c\u8bf4\u5f97\u90fd\u662f\u6ee1\u6d32\u8bdd\u3002\u6d2a\u80dc\u6d77\u6084\u6084\u8ddf\u4e24\u4eba\u8bf4\u4e86\u3002\u94c1\u7f57\u6c49\u548c\u80e1\u6842\u5357\u624d\u77e5\u4ed6\u4eec\u5728\u5439\u5618\u603b\u6559\u5934\u5982\u4f55\u82f1\u52c7\u65e0\u654c\uff0c\u6628\u665a\u53c8\u5f97\u4e86\u4e00\u67c4\u602a\u5251\uff0c\u5251\u5934\u6709\u94a9\uff0c\u5251\u8eab\u5f2f\u66f2\uff0c\u950b\u9510\u65e0\u6bd4\uff0c\u5f53\u771f\u5439\u6bdb\u65ad\u53d1\uff0c\u524a\u94c1\u5982\u6ce5\u3002\u8fd9\u4e0d\u662f\u8881\u627f\u5fd7\u7684\u91d1\u86c7\u5251\u662f\u751a\u4e48\uff1f\u94c1\u7f57\u6c49\u7ad9\u8d77\u8eab\u6765\uff0c\u4fbf\u8981\u8fc7\u53bb\u6559\u8bad\u6559\u8bad\u4ed6\u4eec\uff0c\u80e1\u6842\u5357\u6025\u5fd9\u62c9\u4f4f\u3002\u5f85\u516b\u540d\u6b66\u58eb\u98df\u6bd5\u4e0b\u697c\uff0c\u4e09\u4eba\u6084\u6084\u8ddf\u53bb\uff0c\u67e5\u660e\u4e86\u4ed6\u4eec\u4f4f\u5bbf\u7684\u6240\u5728\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5931\u624b\u88ab\u64d2\uff0c\u5175\u5203\u7ed9\u4eba\u593a\u53bb\uff0c\u5b9e\u662f\u751f\u5e73\u4ece\u6240\u672a\u6709\u7684\u5947\u803b\uff0c\u4f46\u90a3\u7389\u771f\u5b50\u7684\u6b66\u529f\u7edd\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff1b\u8fd9\u628a\u5251\u975e\u593a\u56de\u4e0d\u53ef\uff0c\u5374\u53c8\u5982\u4f55\u4ece\u8fd9\u7edd\u9876\u9ad8\u624b\u4e4b\u4e2d\u593a\u56de\u6765\uff1f\u4e00\u65f6\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u80e1\u6842\u5357\u7b11\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u6211\u4eca\u665a\u53bb\u2018\u5999\u624b\u2019\u5b83\u56de\u6765\u3002\u90a3\u7389\u771f\u5b50\u603b\u8981\u7761\u89c9\uff0c\u51ed\u4ed6\u6b66\u529f\u518d\u9ad8\uff0c\u7761\u7740\u4e86\u603b\u6253\u6211\u4e0d\u8fc7\u5427\uff1f\u201d\u4f17\u4eba\u90fd\u7b11\u8d77\u6765\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u5c31\u504f\u52b3\u80e1\u5927\u54e5\u4e86\uff0c\u53ef\u5343\u4e07\u8f7b\u5ffd\u4e0d\u5f97\u3002\u80e1\u5927\u54e5\u53ea\u987b\u76d7\u5251\uff0c\u4e0d\u5fc5\u6740\u4ed6\u3002\u5c06\u4ed6\u5728\u7761\u68a6\u4e2d\u4e0d\u660e\u4e0d\u767d\u7684\u6740\u4e86\uff0c\u975e\u82f1\u96c4\u597d\u6c49\u6240\u4e3a\u3002\u201d\u80e1\u6842\u5357\u9053\uff1a\u201c\u662f\uff0c\u65e5\u540e\u76df\u4e3b\u8ddf\u4ed6\u4e00\u5bf9\u4e00\u7684\u8f83\u91cf\uff0c\u90a3\u65f6\u624d\u6559\u4ed6\u6b7b\u5f97\u5fc3\u670d\u3002\u201d\u8881\u627f\u5fd7\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u5355\u6253\u72ec\u6597\uff0c\u6211\u4e5f\u672a\u5fc5\u80fd\u80dc\u3002\u201d\u4ed6\u8981\u80e1\u6842\u5357\u4e0d\u53ef\u884c\u523a\uff0c\u5374\u662f\u4e3a\u4e86\u6b64\u4e8b\u592a\u8fc7\u51f6\u9669\uff0c\u7389\u771f\u5b50\u7eb5\u5728\u7761\u68a6\u4e4b\u4e2d\uff0c\u82e5\u662f\u767d\u5203\u52a0\u8eab\uff0c\u4e5f\u5fc5\u80fd\u7acb\u65f6\u60ca\u89c9\u53cd\u51fb\uff0c\u5c31\u7b97\u53d7\u4e86\u81f4\u547d\u91cd\u4f24\uff0c\u4ed6\u5728\u4e34\u6b7b\u4e4b\u524d\u7684\u4e00\u51fb\uff0c\u4e5f\u975e\u8981\u4e86\u80e1\u6842\u5357\u7684\u6027\u547d\u4e0d\u53ef\u3002 \u3000\u3000\u7528\u8fc7\u665a\u996d\u540e\uff0c\u80e1\u6842\u5357\u6362\u4e0a\u9ed1\u8863\uff0c\u5174\u51b2\u51b2\u7684\u51fa\u53bb\u3002\u8881\u627f\u5fd7\u7ec8\u662f\u653e\u5fc3\u4e0d\u4e0b\uff0c\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u6211\u53bb\u7ed9\u4f60\u628a\u98ce\u3002\u201d\u4e24\u4eba\u76f8\u5055\u51fa\u5e97\u3002\u9752\u9752\u77e5\u9053\u6b64\u884c\u5e76\u4e0d\u5982\u884c\u523a\u9791\u5b50\u7687\u5e1d\u90a3\u4e48\u8981\u5e72\u5192\u5947\u9669\uff0c\u53c8\u7d20\u77e5\u80e1\u6842\u5357\u5999\u624b\u7a7a\u7a7a\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u5012\u4e0d\u62c5\u5fc3\u3002 \u3000\u3000\u80e1\u6842\u5357\u5728\u524d\u9886\u8def\uff0c\u884c\u4e86\u4e09\u91cc\u591a\u8def\uff0c\u6765\u5230\u5e03\u5e93\u6b66\u58eb\u7684\u5bbf\u5730\u3002 \u3000\u3000\u53ea\u89c1\u5c45\u4e2d\u662f\u4e00\u5ea7\u6781\u5927\u7684\u725b\u76ae\u5927\u5e10\uff0c\u56db\u5468\u90fd\u662f\u4e00\u5ea7\u5ea7\u5c0f\u5c4b\u3002\u80e1\u6842\u5357\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u516b\u540d\u6b66\u58eb\u90fd\u4f4f\u5728\u5317\u9996\u7684\u5c0f\u5c4b\u4e2d\uff0c\u53ea\u4e0d\u77e5\u90a3\u725b\u9f3b\u5b50\u662f\u4e0d\u662f\u4e5f\u4f4f\u5728\u8fd9\u91cc\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u6293\u4e00\u540d\u6b66\u58eb\u6765\u95ee\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u54b1\u4eec\u90fd\u4e0d\u4f1a\u8bf4\u6ee1\u6d32\u8bdd\u3002\u201d\u80e1\u6842\u5357\u9053\uff1a\u201c\u5f85\u6211\u6253\u624b\u52bf\u8981\u4ed6\u5e26\u8def\u4fbf\u662f\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u89c1\u4e24\u540d\u6b66\u58eb\u54fc\u7740\u5c0f\u66f2\uff0c\u65bd\u65bd\u7136\u800c\u6765\u3002\u8881\u627f\u5fd7\u5f85\u4e24\u4eba\u8d70\u5230\u4e34\u8fd1\uff0c\u7a81\u7136\u8dc3\u51fa\uff0c\u4f38\u6307\u5728\u4e24\u4eba\u80cc\u5fc3\u7a74\u9053\u4e0a\u5404\u70b9\u4e00\u6307\uff0c\u52b2\u900f\u8981\u7a74\uff0c\u4e24\u4eba\u767b\u65f6\u52a8\u5f39\u4e0d\u5f97\u3002\u4ed6\u51fa\u624b\u65f6\u5206\u4e86\u8f7b\u91cd\uff0c\u4e00\u540d\u6b66\u58eb\u7acb\u5373\u660f\u6655\uff0c\u53e6\u4e00\u540d\u5374\u795e\u667a\u4e0d\u5931\u3002\u4ed6\u5c06\u6655\u5012\u7684\u6b66\u58eb\u62d6\u5165\u77ee\u6811\u4e1b\u4e2d\uff0c\u80e1\u6842\u5357\u5de6\u624b\u5c06\u5c16\u5200\u62b5\u5728\u53e6\u4e00\u540d\u6b66\u58eb\u5589\u5934\uff0c\u53f3\u624b\u5927\u6253\u624b\u52bf\uff0c\u5728\u81ea\u5df1\u5934\u9876\u4f5c\u4e2a\u9053\u9afb\u6a21\u6837\uff0c\u95ee\u4ed6\u8fd9\u9053\u4eba\u4f4f\u5728\u4f55\u5904\u3002 \u3000\u3000\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u4f60\u4f5c\u751a\u4e48\uff1f\u6211\u4e0d\u660e\u767d\u3002\u201d\u4e0d\u6599\u4ed6\u7adf\u4f1a\u8bf4\u6c49\u8bed\u3002 \u3000\u3000\u539f\u6765\u76db\u4eac\u672c\u540d\u6c88\u9633\uff0c\u5411\u662f\u5927\u660e\u6240\u5c5e\uff0c\u4e3a\u6ee1\u6e05\u6240\u5360\u540e\uff0c\u4e8e\u5929\u542f\u4e94\u5e74\u5efa\u4e3a\u4eac\u90fd\uff0c\u81f3\u6b64\u65f6\u8fd8\u4e0d\u8db3\u4e8c\u5341\u5e74\u3002\u57ce\u4e2d\u5c45\u6c11\u5341\u4e5d\u90fd\u662f\u6c49\u4eba\u3002\u8fd9\u4e9b\u5e03\u5e93\u6b66\u58eb\u9664\u4e86\u7ec3\u6b66\u6454\u4ea4\uff0c\u6bcf\u65e5\u91cc\u4fbf\u5728\u9152\u697c\u8d4c\u9986\u53ae\u6df7\uff0c\u6cf0\u534a\u4f1a\u8bf4\u6c49\u8bed\u3002 \u3000\u3000\u80e1\u6842\u5357\u5927\u559c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u7684\u603b\u6559\u5934\uff0c\u90a3\u4e2a\u9053\u58eb\uff0c\u4f4f\u5728\u54ea\u91cc\uff1f\u201d\u90a3\u6b66\u58eb\u7ed9\u5c16\u5200\u62b5\u4f4f\u54bd\u5589\uff0c\u6b63\u81ea\u60ca\u60e7\uff0c\u4e00\u542c\u4e4b\u4e0b\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u8981\u53bb\u627e\u6211\u4eec\u603b\u6559\u5934\u9001\u6b7b\uff0c\u90a3\u771f\u662f\u5999\u6781\u4e86\u3002\u201d\u5634\u5df4\u5411\u7740\u4e1c\u8fb9\u8fdc\u5904\u4e00\u5ea7\u623f\u5b50\u4e00\u52aa\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u603b\u6559\u5934\u62a4\u56fd\u771f\u4eba\uff0c\u4fbf\u4f4f\u5728\u90a3\u5ea7\u5c4b\u5b50\u91cc\u3002\u201d\u90a3\u5c4b\u5b50\u79bb\u5176\u4f59\u5c0f\u5c4b\u6709\u56db\u4e94\u5341\u4e08\uff0c\u6784\u7b51\u4e5f\u9ad8\u5927\u5f97\u591a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6599\u77e5\u4e0d\u5047\uff0c\u5728\u4ed6\u80c1\u4e0b\u518d\u8865\u4e0a\u4e00\u6307\uff0c\u6559\u4ed6\u6655\u53a5\u540e\u975e\u8fc7\u4e09\u56db\u4e2a\u65f6\u8fb0\u4e0d\u9192\u3002\u80e1\u6842\u5357\u5c06\u4ed6\u62d6\u5165\u4e86\u6811\u4e1b\u3002 \u3000\u3000\u4e24\u4eba\u6084\u6084\u8d70\u8fd1\u90a3\u5ea7\u5927\u5c4b\uff0c\u53ea\u89c1\u5230\u5904\u9ed1\u6c89\u6c89\u5730\uff0c\u7a97\u6237\u4e2d\u5e76\u65e0\u706f\u70db\u4e4b\u5149\u3002\u80e1\u6842\u5357\u4f4e\u58f0\u9053\uff1a\u201c\u725b\u9f3b\u5b50\u7761\u4e86\uff0c\u5012\u4e0d\u7528\u54b1\u4eec\u7b49\u3002\u201d \u3000\u3000\u4e24\u4eba\u7ed5\u5230\u540e\u95e8\uff0c\u80e1\u6842\u5357\u8d34\u8eab\u5899\u4e0a\uff0c\u6084\u6ca1\u58f0\u606f\u7684\u722c\u4e0a\u3002\u8ddf\u7740\u53c8\u6cbf\u5899\u722c\u4e0b\u3002\u8881\u627f\u5fd7\u89c1\u4ed6\u722c\u5899\u7684\u59ff\u5f0f\u751a\u662f\u4e0d\u96c5\uff0c\u56db\u80a2\u4f38\u5f00\uff0c\u7f29\u5934\u8038\u80a9\uff0c\u884c\u52a8\u53c8\u6162\uff0c\u5012\u4f3c\u662f\u4e00\u53ea\u4e4c\u9f9f\u4e00\u822c\uff0c\u4f46\u534a\u70b9\u58f0\u606f\u4e5f\u65e0\u3002 \u3000\u3000\u5374\u975e\u81ea\u5df1\u6240\u53ca\uff0c\u5fc3\u60f3\uff1a\u201c\u5723\u624b\u795e\u5077\uff0c\u679c\u7136\u4e86\u5f97\u3002\u201d\u4ed6\u6015\u8fdb\u5c4b\u65f6\u82e5\u662f\u7a0d\u6709\u58f0\u606f\uff0c\u5b9a\u8ba9\u7389\u771f\u5b50\u53d1\u89c9\uff0c\u5f53\u4e0b\u5b88\u5728\u5899\u8fb9\uff0c\u51dd\u795e\u503e\u542c\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u542c\u5f97\u5899\u5185\u6811\u4e0a\u6709\u53ea\u591c\u67ad\u53eb\u4e86\u51e0\u58f0\uff0c\u8ddf\u7740\u4fbf\u53c8\u4e00\u7247\u9759\u5bc2\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u9690\u9690\u542c\u5f97\u6709\u5973\u5b50\u7684\u5b09\u7b11\u4e4b\u58f0\u3002\u63a5\u7740\u6709\u4e2a\u7537\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u76f8\u9694\u8fdc\u4e86\uff0c\u5374\u542c\u4e0d\u6e05\u695a\uff0c\u4f9d\u7a00\u4fbf\u662f\u7389\u771f\u5b50\u3002\u8881\u627f\u5fd7\u5fc3\u9053\uff1a\u201c\u4ed6\u8fd8\u6ca1\u7761\uff0c\u80e1\u5927\u54e5\u53ef\u4e0b\u4e0d\u4e86\u624b\u3002\u201d \u3000\u3000\u751f\u6015\u80e1\u6842\u5357\u9047\u9669\uff0c\u4e8e\u662f\u8dc3\u5899\u800c\u5165\uff0c\u53ea\u542c\u5f97\u7537\u5973\u5b09\u7b11\u4e4b\u58f0\u4e0d\u7edd\uff0c\u5faa\u58f0\u8d70\u53bb\uff0c\u5ffd\u542c\u5f97\u7389\u771f\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u54ea\u4e00\u5904\u5730\u65b9\u6700\u6ed1\uff1f\u201d\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u7389\u771f\u5b50\u7b11\u9053\uff1a\u201c\u6211\u6765\u6478\u6478\u770b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u767b\u65f6\u9762\u7ea2\u8033\u8d64\uff0c\u7ad9\u5b9a\u4e86\u811a\u6b65\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8d3c\u9053\u5728\u5e72\u90a3\u52fe\u5f53\uff0c\u5e78\u4e8f\u9752\u5f1f\u6ca1\u540c\u6765\u3002\u201d\u542c\u7740\u90a3\u5973\u5b50\u653e\u8086\u7684\u7b11\u58f0\uff0c\u5fc3\u4e2d\u4e5f\u662f\u7981\u4e0d\u4f4f\u4e00\u8361\uff0c\u5f53\u5373\u53c8\u6084\u6084\u51fa\u5899\uff0c\u5750\u5728\u8349\u4e1b\u4e4b\u4e2d\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e00\u9635\u98ce\u5439\u6765\uff0c\u5fae\u611f\u5bd2\u610f\u3002\u8fd9\u65e5\u662f\u516b\u6708\u521d\u65ec\uff0c\u5317\u56fd\u5929\u65f6\u5df2\u548c\u6c5f\u5357\u9686\u51ac\u4e00\u822c\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u7389\u771f\u5b50\u5389\u58f0\u5927\u559d\uff1a\u201c\u751a\u4e48\u4eba\uff1f\u201d\u8881\u627f\u5fd7\u4e00\u60ca\u7ad9\u8d77\uff0c\u6697\u53eb\uff1a\u201c\u7cdf\u7cd5\uff0c\u7ed9\u4ed6\u53d1\u89c9\u4e86\uff01\u201d\u8dc3\u4e0a\u5899\u5934\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ed1\u5f71\u98de\u6b65\u5954\u6765\uff0c\u6b63\u662f\u80e1\u6842\u5357\uff0c\u5954\u5230\u4e34\u8fd1\uff0c\u5374\u89c1\u4ed6\u624b\u4e2d\u7d2f\u7d2f\u8d58\u8d58\u7684\u62b1\u7740\u4e0d\u5c11\u7269\u4e8b\uff0c\u5fc3\u5ff5\u4e00\u95ea\uff1a\u201c\u80e1\u5927\u54e5\u5077\u513f\u7684\u813e\u6c14\u96be\u9664\uff0c\u4e0d\u77e5\u53c8\u5077\u4e86\u4ed6\u751a\u4e48\u4e1c\u897f\uff0c\u8fd9\u4e48\u4e00\u5927\u5806\u7684\u3002\u201d\u5f53\u4e0b\u4e0d\u53ca\u7ec6\u60f3\uff0c\u8dc3\u4e0b\u53bb\u5c06\u4ed6\u4e00\u628a\u6293\u8d77\uff0c\u98de\u8eab\u4e0a\u5899\uff0c\u8dc3\u4e0b\u5730\u6765\uff0c\u4fbf\u542c\u5f97\u7389\u771f\u5b50\u559d\u9053\uff1a\u201c\u9f20\u8f88\uff0c\u4f60\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u3002\u201d\u8eab\u5b50\u5df2\u5728\u5899\u5934\u3002 \u3000\u3000\u80e1\u6842\u5357\u53eb\u9053\uff1a\u201c\u5f97\u624b\u4e86\uff01\u5feb\u8d70\uff01\u201d\u8881\u627f\u5fd7\u5927\u559c\uff0c\u56de\u5934\u4e00\u671b\uff0c\u4e0d\u7531\u5f97\u5927\u5947\uff0c\u661f\u5149\u71b9\u5fae\u4e0b\u53ea\u89c1\u7389\u771f\u5b50\u5168\u8eab\u8d64\u88f8\uff0c\u4e0b\u4f53\u5374\u81c3\u81c3\u80bf\u80bf\u7684\u56f4\u7740\u4e00\u5f20\u539a\u68c9\u88ab\uff0c\u53cc\u624b\u6293\u7740\u88ab\u5b50\u3002\u8881\u627f\u5fd7\u5fcd\u4e0d\u4f4f\u5931\u7b11\u3002 \u3000\u3000\u80e1\u6842\u5357\u7b11\u9053\uff1a\u201c\u725b\u9f3b\u5b50\u6b63\u5728\u5e72\u90a3\u8c03\u8c03\u513f\uff0c\u6211\u5c06\u4ed6\u7684\u8863\u670d\u90fd\u5077\u6765\u4e86\u3002\u201d\u8bf4\u7740\u53cc\u624b\u4e00\u4e3e\uff0c\u539f\u6765\u62b1\u7684\u662f\u4e00\u5806\u8863\u670d\uff0c\u8f6c\u8eab\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u7684\u5b9d\u5251\uff01\u201d\u90a3\u628a\u91d1\u86c7\u5251\u6b63\u63d2\u5728\u4ed6\u7684\u540e\u8170\u3002 \u3000\u3000\u8881\u627f\u5fd7\u62d4\u8fc7\u5251\u6765\uff0c\u987a\u624b\u63d2\u5165\u8170\u5e26\uff0c\u53c8\u5954\u51fa\u51e0\u6b65\u3002\u7389\u771f\u5b50\u5df2\u8fde\u4eba\u5e26\u88ab\uff0c\u6251\u5c06\u4e0b\u6765\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\uff01\u201d\u4f38\u53f3\u638c\u5411\u80e1\u6842\u5357\u5288\u53bb\u3002\u8881\u627f\u5fd7\u51fa\u638c\u659c\u51fb\u4ed6\u80a9\u5934\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6211\u518d\u6597\u4e00\u573a\u3002\u201d \u3000\u3000\u7389\u771f\u5b50\u53ea\u611f\u8fd9\u638c\u6765\u52bf\u51cc\u5389\u4e4b\u6781\uff0c\u6025\u5fd9\u56de\u638c\u6321\u683c\u3002\u53cc\u638c\u76f8\u4ea4\uff0c\u4e24\u4eba\u90fd\u5012\u9000\u4e86\u4e09\u6b65\u3002\u7389\u771f\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u770b\u6e05\u695a\u4e86\u5bf9\u624b\uff0c\u5fc3\u4e0b\u66f4\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u554a\uff01\u4f60\u8fd9\u5c0f\u5b50\u9003\u51fa\u6765\u4e86\u3002\u201d\u4ed6\u521d\u65f6\u53ea\u9053\u5c0f\u5077\u76d7\u5251\uff0c\u4fbf\u8d64\u8eab\u9732\u4f53\u7684\u8ffd\u4e86\u51fa\u6765\uff0c\u54ea\u6599\u5f97\u7adf\u6709\u8881\u627f\u5fd7\u8fd9\u5927\u9ad8\u624b\u8eb2\u5728\u5899\u5916\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u9000\u4e4b\u540e\uff0c\u53c8\u5373\u4e0a\u524d\u3002\u7389\u771f\u5b50\u5de6\u624b\u62c9\u4f4f\u68c9\u88ab\uff0c\u60df\u6050\u6ed1\u8131\uff0c\u53ea\u5f97\u4ee5\u53f3\u638c\u8fce\u654c\u3002\u4f46\u8fd9\u6761\u5927\u68c9\u88ab\u4f55\u7b49\u7d2f\u8d58\uff0c\u53ea\u62c6\u5f97\u4e24\u62db\uff0c\u811a\u4e0b\u4e00\u7eca\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8881\u627f\u5fd7\u987a\u52bf\u4e00\u62f3\uff0c\u91cd\u91cd\u51fb\u5728\u4ed6\u80a9\u5934\u3002\u7389\u771f\u5b50\u53c8\u6025\u53c8\u6012\uff0c\u4ed6\u6b63\u5728\u6d53\u60c5\u7545\u6000\u4e4b\u9645\uff0c\u7ed9\u80e1\u6842\u5357\u4e58\u673a\u5077\u53bb\u4e86\u5b9d\u5251\u8863\u670d\uff0c\u672c\u5df2\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u65f6\u518d\u9047\u52b2\u654c\uff0c\u80a9\u5934\u4e2d\u4e86\u8881\u627f\u5fd7\u7834\u7389\u62f3\u4e2d\u7684\u4e00\u62db\uff0c\u6574\u6761\u53f3\u81c2\u90fd\u9178\u9ebb\u4e86\u3002\u4ed6\u81ea\u516b\u5c81\u4e4b\u540e\uff0c\u4ece\u672a\u5728\u4eba\u524d\u8d64\u88f8\u8fc7\u8eab\u5b50\uff0c\u8fd9\u65f6\u72fc\u72c8\u4e07\u72b6\uff0c\u5168\u60f3\u4e0d\u5230\u82e5\u662f\u629b\u53bb\u68c9\u88ab\uff0c\u8d64\u8eab\u9732\u4f53\u7684\u8ddf\u8881\u627f\u5fd7\u52a8\u624b\u53c8\u6709\u4f55\u59a8\uff1f\u65f6\u5f53\u591c\u665a\uff0c\u53c8\u65e0\u591a\u4eba\u5728\u65c1\uff0c\u5c31\u7b97\u7ed9\u4eba\u77a7\u89c1\u4e86\uff0c\u4ed6\u672c\u662f\u4e2a\u98ce\u6d41\u597d\u8272\u7684\u7537\u5b50\uff0c\u4e5f\u6ca1\u751a\u4e48\u5927\u4e0d\u4e86\u3002\u4f46\u7a7f\u8863\u7684\u4e60\u4fd7\u5728\u5fc3\u4e2d\u5df2\u7136\u6839\u6df1\u8482\u56fa\uff0c\u624b\u5fd9\u811a\u4e71\u7684\u53ea\u987e\u62b5\u6321\u6765\u62db\uff0c\u5de6\u624b\u5374\u59cb\u7ec8\u7d27\u7d27\u6293\u7740\u68c9\u88ab\u4e0d\u653e\u3002\u518d\u62c6\u4e24\u62db\uff0c\u80cc\u5fc3\u4e0a\u53c8\u88ab\u8881\u627f\u5fd7\u4e00\u638c\u51fb\u4e2d\u3002\u8fd9\u4e00\u638c\u84c4\u7740\u6df7\u5143\u529f\u5185\u52b2\uff0c\u7389\u771f\u5b50\u518d\u4e5f\u62b5\u53d7\u4e0d\u4f4f\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4f4f\u624b\u4e0d\u518d\u8ffd\u51fb\uff0c\u7b11\u9053\uff1a\u201c\u6b64\u65f6\u6740\u4f60\uff0c\u8c05\u4f60\u6b7b\u4e86\u4e5f\u4e0d\u5fc3\u670d\uff0c\u4e0b\u6b21\u5f85\u4f60\u7a7f\u4e0a\u4e86\u8863\u670d\u518d\u6253\u8fc7\u3002\u201d\u80e1\u6842\u5357\u6025\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u9976\u4ed6\u4e0d\u5f97\uff0c\u53ea\u6015\u4e8e\u7956\u5927\u5bff\u6027\u547d\u6709\u788d\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u53bb\u7980\u544a\u9791\u5b50\u7687\u5e1d\uff0c\u53c8\u52a0\u91cd\u4e86\u7956\u53d4\u53d4\u7684\u7f6a\u540d\uff0c\u975e\u6740\u4ed6\u706d\u53e3\u4e0d\u53ef\u3002\u201d\u7eb5\u8eab\u4e0a\u524d\uff0c\u53cc\u62f3\u5f80\u4ed6\u592a\u9633\u7a74\u51fb\u53bb\u3002\u7389\u771f\u5b50\u89c1\u6765\u62db\u72e0\u8fa3\uff0c\u81ea\u7136\u800c\u7136\u7684\u4e3e\u8d77\u53cc\u624b\u6321\u683c\uff0c\u867d\u5c06\u5bf9\u65b9\u6765\u62f3\u6321\u5f00\uff0c\u4f46\u68c9\u88ab\u5df2\u6e9c\u5230\u811a\u4e0b\uff0c\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\uff0c\u80f8\u53e3\u5df2\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u88ab\u8881\u627f\u5fd7\u98de\u811a\u8e22\u4e2d\u3002\u7389\u771f\u5b50\u5927\u9a87\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u8eab\u4e0a\u4e00\u4e1d\u4e0d\u6302\uff0c\u62d4\u8db3\u4fbf\u5954\u3002\u8881\u627f\u5fd7\u548c\u80e1\u6842\u5357\u968f\u540e\u8ffd\u53bb\u3002 \u3000\u3000\u8fd9\u9053\u4eba\u6b66\u529f\u4e5f\u5f53\u771f\u4e86\u5f97\uff0c\u8eab\u4e0a\u8fde\u4e2d\u4e09\u62db\uff0c\u53d7\u4f24\u6781\u91cd\uff0c\u5c45\u7136\u8fd8\u662f\u5954\u884c\u5982\u98de\uff0c\u8f7b\u529f\u4e4b\u4f73\uff0c\u5b9e\u662f\u5f53\u4e16\u7f55\u6709\u3002\u8881\u627f\u5fd7\u6025\u6b65\u8ffd\u8d76\uff0c\u773c\u89c1\u4ed6\u7a9c\u5165\u4e86\u90a3\u5ea7\u725b\u76ae\u5927\u5e10\uff0c\u5f53\u5373\u8ffd\u8fdb\u3002 \u3000\u3000\u521a\u5954\u5230\u5e10\u53e3\uff0c\u53ea\u89c1\u5e10\u5185\u70db\u706b\u7167\u8000\u5982\u540c\u767d\u663c\uff0c\u5e10\u5185\u7ad9\u6ee1\u4e86\u4eba\uff0c\u5f53\u5373\u6b62\u6b65\uff0c\u95ea\u5411\u4e00\u65c1\uff0c\u53ea\u542c\u5f97\u5e10\u5185\u4f17\u4eba\u9f50\u58f0\u60ca\u547c\u3002 \u3000\u3000\u8fd9\u65f6\u80e1\u6842\u5357\u4e5f\u5df2\u8d76\u5230\uff0c\u4e00\u626f\u8881\u627f\u5fd7\u624b\u81c2\uff0c\u7ed5\u5230\u5e10\u540e\u3002\u4e24\u4eba\u4f0f\u4f4e\u8eab\u5b50\uff0c\u6380\u5f00\u5e10\u811a\uff0c\u5411\u5185\u77a7\u53bb\u3002\u53ea\u89c1\u7389\u771f\u5b50\u4ef0\u9762\u671d\u5929\uff0c\u6454\u5728\u5730\u4e0b\uff0c\u5168\u8eab\u4e00\u4e1d\u4e0d\u6302\uff0c\u77a7\u4e0d\u51fa\u4ed6\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u5168\u8eab\u808c\u80a4\u5c45\u7136\u96ea\u767d\u7c89\u5ae9\uff0c\u80f8\u53e3\u5374\u6ee1\u662f\u9c9c\u8840\uff0c\u8fd9\u6a21\u6837\u65e2\u53ef\u602a\u4e4b\u6781\uff0c\u53c8\u53ef\u7b11\u65e0\u6bd4\u3002 \u3000\u3000\u5e10\u4e2d\u4e00\u58f0\u60ca\u547c\u4e4b\u540e\uff0c\u4fbf\u5373\u5bc2\u7136\u65e0\u58f0\u3002\u53ea\u542c\u5f97\u4e00\u4e2a\u5a01\u4e25\u7684\u58f0\u97f3\u5927\u58f0\u8bf4\u8d77\u6ee1\u6d32\u8bdd\u6765\u3002\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u8bdd\u4e4b\u4eba\u7adf\u7136\u4fbf\u662f\u6ee1\u6e05\u7687\u5e1d\u7687\u592a\u6781\u3002 \u3000\u3000\u89c1\u5e10\u5185\u7ad9\u6ee1\u7684\u90fd\u662f\u5e03\u5e93\u6b66\u58eb\uff0c\u4e0d\u4e0b\u4e00\u4e8c\u767e\u4eba\uff0c\u5fc3\u9053\uff1a\u201c\u554a\uff0c\u662f\u4e86\uff0c\u8fd9\u9791\u5b50\u7687\u5e1d\u7231\u770b\u4eba\u6bd4\u6b66\uff0c\u4eca\u665a\u53c8\u6765\u77a7\u6765\u5566\u3002\u7b97\u4ed6\u773c\u798f\u4e0d\u6d45\uff0c\u89c1\u5230\u4e86\u6b66\u58eb\u603b\u6559\u5934\u8fd9\u7b49\u602a\u6a21\u6837\u3002\u201d\u4ed6\u6628\u665a\u9886\u7565\u8fc7\u8fd9\u4e9b\u5e03\u5e93\u6b66\u58eb\u7684\u529f\u592b\uff0c\u6b66\u529f\u867d\u7136\u5e73\u5e73\uff0c\u4f46\u7f20\u4e0a\u4e86\u6b7b\u547d\u4e0d\u653e\uff0c\u7740\u5b9e\u96be\u6597\uff0c\u5e10\u4e2d\u6b66\u58eb\u4eba\u6570\u5982\u6b64\u4f17\u591a\uff0c\u8981\u884c\u523a\u7687\u5e1d\u662f\u4e07\u4e07\u4e0d\u80fd\uff0c\u5f53\u4e0b\u9759\u89c2\u5176\u53d8\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u540d\u6b66\u58eb\u9996\u9886\u6a21\u6837\u4e4b\u4eba\u4e0a\u524d\u8eac\u8eab\u7980\u62a5\uff0c\u7687\u592a\u6781\u53c8\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u4fbf\u7ad9\u8d77\u8eab\u6765\uff0c\u4f3c\u662f\u626b\u5174\u5df2\u6781\uff0c\u4e0d\u518d\u77a7\u6bd4\u6b66\u4e86\u3002\u4ed6\u8d70\u5411\u5e10\u53e3\uff0c\u6570\u5341\u540d\u4f8d\u536b\u524d\u540e\u62e5\u536b\uff0c\u51fa\u5e10\u4e0a\u9a6c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u5f53\u771f\u662f\u5929\u8d50\u826f\u673a\uff0c\u6211\u5728\u8def\u4e0a\u51fa\u5176\u4e0d\u610f\u7684\u4e0b\u624b\uff0c\u6bd4\u53bb\u5bab\u4e2d\u884c\u523a\u53ef\u65b9\u4fbf\u5f97\u591a\u4e86\u3002\u201d\u4f4e\u58f0\u5bf9\u80e1\u6842\u5357\u9053\uff1a\u201c\u8fd9\u662f\u9791\u5b50\u7687\u5e1d\uff0c\u4f60\u5148\u56de\u53bb\uff0c\u6211\u4e58\u673a\u5728\u534a\u8def\u4e0a\u52a8\u624b\u3002\u201d\u80e1\u6842\u5357\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u76df\u4e3b\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u8ddf\u5728\u7687\u592a\u6781\u4e00\u884c\u4eba\u4e4b\u540e\uff0c\u53ea\u89c1\u4f17\u4f8d\u536b\u9ad8\u4e3e\u706b\u628a\uff0c\u5411\u897f\u800c\u884c\uff0c\u5fc3\u60f3\uff1a\u201c\u5f85\u4ed6\u8d70\u5f97\u8fdc\u4e9b\u518d\u5e72\uff0c\u514d\u5f97\u52a8\u8d77\u624b\u6765\uff0c\u8fd9\u4e9b\u5e03\u5e93\u6b66\u58eb\u53c8\u8d76\u6765\u7ea0\u7f20\u3002\u201d \u3000\u3000\u8ddf\u4e0d\u5230\u4e00\u91cc\uff0c\u4fbf\u89c1\u4f17\u4f8d\u536b\u62e5\u7740\u7687\u592a\u6781\u8d70\u5411\u4e00\u6240\u5927\u5c4b\uff0c\u7adf\u8fdb\u4e86\u5c4b\u5b50\u3002\u8881\u627f\u5fd7\u597d\u751f\u5947\u602a\uff1a\u201c\u4ed6\u4e0d\u56de\u5bab\uff0c\u5230\u8fd9\u5c4b\u91cc\u53c8\u5e72\u751a\u4e48\u4e86\uff1f\u201d\u5f53\u4e0b\u7ed5\u5230\u5c4b\u540e\uff0c\u8dc3\u8fdb\u5899\u53bb\uff0c\u89c1\u662f\u597d\u5927\u4e00\u5ea7\u82b1\u56ed\uff0c\u5357\u9996\u4e00\u95f4\u5c4b\u5b50\u7a97\u4e2d\u900f\u51fa\u706f\u5149\uff0c\u4ed6\u4f0f\u8eab\u8d70\u8fd1\uff0c\u4ece\u7a97\u7f1d\u4e2d\u5411\u5185\u5f20\u53bb\uff0c\u4f46\u89c1\u623f\u4e2d\u9526\u7ee3\u707f\u70c2\uff0c\u5927\u7ea2\u7f0e\u5e10\u4e0a\u91d1\u7ebf\u7ee3\u7740\u4e00\u5bf9\u5927\u51e4\u51f0\u3002\u8fce\u9762\u4e00\u5f20\u6bb7\u7ea2\u7684\u5e37\u5b50\u6380\u5f00\uff0c\u7687\u592a\u6781\u6b63\u8d70\u8fdb\u623f\u6765\u3002\u8881\u627f\u5fd7\u5927\u559c\uff0c\u6697\u53eb\uff1a\u201c\u5929\u52a9\u6211\u4e5f\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e00\u540d\u6ee1\u6d32\u5973\u5b50\u8d77\u8eab\u76f8\u8fce\u3002\u8fd9\u5973\u5b50\u8863\u9970\u534e\u8d35\uff0c\u5e3d\u5b50\u540e\u9762\u4e5f\u9576\u4e86\u73cd\u73e0\u5b9d\u77f3\u3002\u7687\u592a\u6781\u8fdb\u623f\u540e\uff0c\u90a3\u5973\u5b50\u56de\u8fc7\u8eab\u6765\uff0c\u8881\u627f\u5fd7\u89c1\u5979\u7ea6\u83ab\u4e8c\u5341\u516b\u4e5d\u5c81\u5e74\u7eaa\uff0c\u5bb9\u8c8c\u751a\u662f\u7aef\u4e3d\uff0c\u5168\u8eab\u73e0\u5149\u5b9d\u6c14\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5973\u5b50\u4e0d\u662f\u7687\u540e\uff0c\u4fbf\u662f\u8d35\u5983\u4e86\u3002\u554a\uff0c\u662f\u4e86\uff0c\u7687\u592a\u6781\u53bb\u77a7\u6b66\u58eb\u6bd4\u6b66\uff0c\u8fd9\u5a18\u5a18\u4e0d\u7231\u770b\u6bd4\u6b66\uff0c\u4fbf\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u8fd9\u662f\u7687\u5e1d\u7684\u884c\u5bab\u3002\u201d \u3000\u3000\u7687\u592a\u6781\u4f38\u624b\u6478\u6478\u5979\u7684\u8138\u86cb\uff0c\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002\u90a3\u5973\u5b50\u4e00\u7b11\uff0c\u7b54\u4e86\u51e0\u53e5\u3002\u7687\u592a\u6781\u5750\u5230\u5e8a\u4e0a\uff0c\u6b63\u8981\u8eba\u4e0b\u4f11\u606f\uff0c\u7a81\u7136\u5750\u8d77\uff0c\u8138\u4e0a\u6ee1\u662f\u6000\u7591\u4e4b\u8272\uff0c\u5728\u623f\u4e2d\u4e1c\u5f20\u897f\u671b\uff0c\u84e6\u5730\u89c1\u5230\u5e8a\u8fb9\u4e00\u5bf9\u653e\u5f97\u6b6a\u6b6a\u659c\u659c\u7684\u7537\u4eba\u978b\u5b50\uff0c\u5389\u58f0\u559d\u95ee\u3002\u90a3\u5973\u5b50\u82b1\u5bb9\u60e8\u767d\uff0c\u63a9\u9762\u54ed\u4e86\u8d77\u6765\u3002\u7687\u592a\u6781\u4e00\u628a\u6293\u4f4f\u5979\u80f8\u53e3\uff0c\u4e3e\u624b\u6b32\u6253\uff0c\u90a3\u5973\u5b50\u53cc\u819d\u4e00\u66f2\uff0c\u8dea\u5012\u5728\u5730\u3002\u7687\u592a\u6781\u653e\u5f00\u4e86\u5979\uff0c\u4fef\u8eab\u5230\u5e8a\u5e95\u4e0b\u53bb\u770b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u5947\uff0c\u5fc3\u60f3\uff1a\u201c\u77a7\u8fd9\u6a21\u6837\uff0c\u5b9a\u662f\u7687\u540e\u5a18\u5a18\u4e58\u7687\u5e1d\u53bb\u77a7\u6bd4\u6b66\u4e4b\u65f6\uff0c\u548c\u60c5\u4eba\u5728\u6b64\u5e7d\u4f1a\uff0c\u60f3\u4e0d\u5230\u62a4\u56fd\u771f\u4eba\u7a81\u7136\u6f14\u51fa\u8fd9\u4e48\u4e00\u51fa\u597d\u620f\uff0c\u7687\u5e1d\u63d0\u524d\u56de\u6765\uff0c\u4ee5\u81f4\u77a7\u51fa\u4e86\u7834\u7efd\u3002\u96be\u9053\u7687\u540e\u5a18\u5a18\u4e5f\u5077\u4eba\uff0c\u672a\u514d\u592a\u4e0d\u6210\u8bdd\u4e86\u5427\uff1f\u5979\u60c5\u4eba\u82e5\u662f\u5c1a\u5728\u623f\u4e2d\uff0c\u8fd9\u56de\u53ef\u9003\u4e0d\u8d70\u4e86\u3002\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u7687\u592a\u6781\u8eab\u540e\u7684\u6a71\u95e8\u7a81\u7136\u6253\u5f00\uff0c\u6a71\u4e2d\u8dc3\u51fa\u4e00\u4eba\uff0c\u5200\u5149\u95ea\u8000\uff0c\u4e00\u67c4\u77ed\u5200\u5411\u7687\u592a\u6781\u540e\u5fc3\u63d2\u53bb\u3002\u90a3\u5973\u5b50\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\uff0c\u70db\u5149\u6643\u52a8\u4e86\u51e0\u4e0b\uff0c\u4fbf\u5373\u7184\u706d\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u70db\u706b\u91cd\u53c8\u70b9\u71c3\uff0c\u53ea\u89c1\u7687\u592a\u6781\u4fef\u8eab\u5012\u5728\u5730\u4e0b\uff0c\u66f4\u4e0d\u52a8\u5f39\uff0c\u80cc\u5fc3\u4e0a\u9c9c\u8840\u67d3\u7ea2\u4e86\u9ec4\u888d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8fd9\u4e00\u60ca\u5f53\u771f\u975e\u540c\u5c0f\u53ef\uff0c\u770b\u90a3\u4eba\u65f6\uff0c\u6b63\u662f\u6628\u5929\u89c1\u8fc7\u7684\u777f\u4eb2\u738b\u591a\u5c14\u886e\u3002\u90a3\u5973\u5b50\u6251\u5165\u4ed6\u6000\u91cc\u3002\u591a\u5c14\u886e\u6402\u4f4f\u4e86\uff0c\u4f4e\u58f0\u5b89\u6170\u3002 \u3000\u3000\u8881\u627f\u5fd7\u773c\u89c1\u5230\u8fd9\u60ca\u5fc3\u52a8\u9b44\u7684\u60c5\u666f\uff0c\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u5bfb\u601d\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u591a\u5c14\u886e\u80c6\u5927\u5305\u5929\uff0c\u7adf\u6562\u5f11\u4e86\u54e5\u54e5\u3002\u4e8b\u60c5\u9a6c\u4e0a\u4fbf\u8981\u95f9\u5927\uff0c\u5feb\u4e9b\u8131\u8eab\u4e3a\u5999\u3002\u201d\u5f53\u5373\u8dc3\u51fa\u5899\u5916\uff0c\u56de\u5230\u5ba2\u5e97\u3002 \u3000\u3000\u9752\u9752\u89c1\u4ed6\u795e\u8272\u60ca\u7591\u4e0d\u5b9a\uff0c\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u60f3\u662f\u9791\u5b50\u7687\u5e1d\u798f\u547d\u5927\uff0c\u523a\u4ed6\u4e0d\u5230\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6447\u5934\u9053\uff1a\u201c\u9791\u5b50\u7687\u5e1d\u6b7b\u4e86\uff0c\u4e0d\u662f\u6211\u6740\u7684\u3002\u201d \u3000\u3000\u4f17\u4eba\u6599\u60f3\u9791\u5b50\u7687\u5e1d\u88ab\u523a\uff0c\u4eac\u57ce\u5fc5\u5b9a\u5927\u4e71\uff0c\u6b21\u65e5\u4e00\u65e9\uff0c\u4fbf\u5373\u79bb\u76db\u4eac\u5357\u4e0b\u3002 \u3000\u3000\u4e0d\u4e00\u65e5\uff0c\u8fdb\u5c71\u6d77\u5173\u5230\u4e86\u5317\u4eac\uff0c\u624d\u542c\u8bf4\u6ee1\u6e05\u7687\u5e1d\u7687\u592a\u6781\u5728\u516b\u6708\u5e9a\u5348\u591c\u91cc\u201c\u65e0\u75be\u800c\u7ec8\u201d\uff0c\u6ee1\u6e05\u7acb\u4e86\u7687\u592a\u6781\u7684\u5c0f\u513f\u5b50\u798f\u4e34\u505a\u7687\u5e1d\u3002\u5c0f\u7687\u5e1d\u5e74\u65b9\u516d\u5c81\uff0c\u7531\u777f\u4eb2\u738b\u591a\u5c14\u886e\u8f85\u653f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u591a\u5c14\u886e\u4e5f\u5f53\u771f\u5389\u5bb3\uff0c\u4ed6\u4eb2\u624b\u6740\u4e86\u7687\u5e1d\uff0c\u5c45\u7136\u4e00\u70b9\u6ca1\u4e8b\uff0c\u4e0d\u77e5\u662f\u600e\u751f\u9690\u7792\u7684\u3002\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u777f\u4eb2\u738b\u5411\u6765\u6781\u5f97\u7687\u592a\u6781\u7684\u5ba0\u4fe1\uff0c\u624b\u638c\u5175\u6743\uff0c\u6ee1\u6e05\u7684\u738b\u516c\u4eb2\u8d35\u4e2a\u4e2a\u90fd\u6015\u4ed6\u3002 \u3000\u3000\u4ed6\u8bf4\u7687\u592a\u6781\u65e0\u75be\u800c\u7ec8\uff0c\u8c01\u4e5f\u4e0d\u6562\u591a\u53e3\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u81ea\u5df1\u53c8\u4e0d\u505a\u7687\u5e1d\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8fd9\u4e2a\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u6216\u8bb8\u4ed6\u6015\u4eba\u4e0d\u670d\uff0c\u6740\u5bb3\u7687\u592a\u6781\u7684\u4e8b\u53cd\u800c\u66b4\u9732\u4e86\u51fa\u6765\u3002\u798f\u4e34\u90a3\u5c0f\u5b69\u5b50\u662f\u5e84\u5983\u751f\u7684\uff0c\u76f8\u516c\u90a3\u665a\u6240\u89c1\u7684\u8d35\u5983\uff0c\u5b9a\u7136\u5c31\u662f\u5e84\u5983\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6b64\u756a\u8fdc\u8d74\u8fbd\u4e1c\uff0c\u4e3a\u7684\u662f\u884c\u523a\u6ee1\u6e05\u5de8\u914b\u7687\u592a\u6781\uff0c\u4ee5\u62a5\u7236\u4ec7\uff0c\u7ed3\u679c\u4eb2\u773c\u89c1\u5230\u7687\u592a\u6781\u6bd9\u547d\uff0c\u867d\u975e\u81ea\u5df1\u6240\u6740\uff0c\u6b64\u4eba\u7ec8\u7a76\u662f\u6b7b\u4e86\uff0c\u53ef\u662f\u5185\u5fc3\u5374\u6b8a\u65e0\u6b22\u6109\u4e4b\u610f\uff0c\u4e0d\u518d\u601d\u5fd6\uff1a\u201c\u4ed6\u4e3a\u751a\u4e48\u5c06\u6211\u4ea4\u7ed9\u7956\u53d4\u53d4\uff1f\u4ee5\u4ed6\u77e5\u4eba\u4e4b\u660e\uff0c\u81ea\u7136\u6599\u5f97\u5230\u7956\u53d4\u53d4\u5b9a\u4f1a\u79c1\u81ea\u5c06\u6211\u91ca\u653e\u3002\u4ed6\u662f\u4e0d\u662f\u8981\u6536\u670d\u7956\u53d4\u53d4\u4e4b\u5fc3\uff0c\u597d\u4e3a\u4ed6\u6b7b\u5fc3\u584c\u5730\u7684\u6253\u4ed7\u529e\u4e8b\uff1f\u201d\u53c8\u60f3\uff1a\u201c\u7956\u53d4\u53d4\u6295\u964d\u9791\u5b50\uff0c\u81ea\u7136\u662f\u6c49\u5978\u4e86\u3002\u53ea\u56e0\u4ed6\u6551\u4e86\u6211\u6027\u547d\uff0c\u6211\u4fbf\u51b2\u53e3\u800c\u51fa\u7684\u53eb\u4ed6\u53d4\u53d4\uff0c\u90a3\u5c82\u4e0d\u662f\u53ea\u5ff5\u5c0f\u60e0\uff0c\u4e0d\u987e\u5927\u4e49\uff1f\u5230\u5e95\u8be5\u662f\u4e0d\u8be5\uff1f\u201d\u60f3\u5230\u7687\u592a\u6781\u4e34\u6b7b\u7684\u60c5\u72b6\uff0c\u5f53\u65f6\u4f3c\u4e4e\u5fcd\u4e0d\u4f4f\u4fbf\u60f3\u51b2\u8fdb\u623f\u53bb\u6551\u4ed6\u6027\u547d\uff0c\u8981\u662f\u591a\u5c14\u886e\u4e0b\u624b\u7a0d\u7f13\uff0c\u81ea\u5df1\u662f\u5426\u4f1a\u51fa\u624b\u76f8\u6551\uff0c\u6b64\u65f6\u56de\u601d\uff0c\u5140\u81ea\u96be\u8bf4\u3002 \u3000\u3000\u518d\u60f3\u5230\u7389\u771f\u5b50\u6b66\u529f\u4e4b\u5f3a\uff0c\u6ee1\u6d32\u6b66\u58eb\u4e4b\u52c7\uff0c\u591a\u5c14\u886e\u624b\u6bb5\u7684\u72e0\u8fa3\uff0c\u8303\u6587\u7a0b\u7b49\u4eba\u7684\u6df1\u8c0b\u8fdc\u8651\uff0c\u53ea\u89c9\u4e16\u4e8b\u591a\u8270\uff0c\u6765\u65e5\u5927\u96be\uff0c\u5fc3\u4e2d\u4e00\u7247\u7247\u7a7a\u8361\u8361\u5730\uff0c\u7adf\u65e0\u7740\u843d\u5904\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53d6\u51fa\u94f6\u4e24\uff0c\u547d\u6d2a\u80dc\u6d77\u5728\u7981\u57ce\u9644\u8fd1\u7684\u6b63\u6761\u5b50\u80e1\u540c\u4e70\u4e86\u4e00\u6240\u5927\u5b85\u7b2c\uff0c\u6b64\u6b21\u6765\u4eac\u8981\u7ed3\u4ea4\u738b\u516c\u5de8\u537f\u3001\u6587\u6b66\u5b98\u5458\uff0c\u4ee5\u4f5c\u95ef\u519b\u5185\u5e94\uff0c\u987b\u5f97\u6392\u573a\u8c6a\u9614\u3002 \u3000\u3000\u8fd9\u65e5\u9752\u9752\u5728\u5b85\u4e2d\u6307\u6325\u7ae5\u4ec6\uff0c\u7c89\u5237\u5e03\u7f6e\u3002\u8881\u627f\u5fd7\u72ec\u81ea\u5728\u57ce\u5185\u5927\u8857\u95f2\u901b\u3002\u8d70\u5230\u4e00\u5904\uff0c\u89c1\u6709\u6570\u5341\u540d\u6237\u90e8\u5e93\u4e01\u624b\u6267\u5175\u5203\uff0c\u6212\u5907\u68ee\u4e25\u3002\u542c\u9014\u4eba\u8bf4\uff0c\u662f\u5357\u65b9\u89e3\u6765\u6f15\u94f6\u5165\u5e93\u3002\u4ed6\u60f3\u8fd9\u662f\u5d07\u796f\u7687\u5e1d\u7684\u6839\u672c\uff0c\u5f97\u4ed4\u7ec6\u770b\u770b\uff0c\u5f53\u4e0b\u7ad9\u5f97\u8fdc\u8fdc\u7684\uff0c\u5bdf\u770b\u9644\u8fd1\u7684\u5f62\u52bf\uff0c\u7a81\u89c1\u4e24\u6761\u9ed1\u5f71\u4ece\u5e93\u623f\u5c4b\u9876\u4e0a\u8dc3\u8d77\uff0c\u8eab\u6cd5\u751a\u662f\u8fc5\u901f\uff0c\u4e00\u8f6c\u773c\u95f4\uff0c\u5df2\u5728\u4e1c\u65b9\u9690\u6ca1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u5947\uff0c\u5fc3\u60f3\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u96be\u9053\u7adf\u6709\u5927\u76d7\u52ab\u5e93\uff0c\u5012\u8981\u89c1\u8bc6\u4e00\u4e0b\u662f\u4f55\u7b49\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u811a\u4e0b\u52a0\u52b2\uff0c\u5954\u5230\u4e1c\u5317\u89d2\u4e0a\uff0c\u4eba\u5f71\u5df2\u7136\u4e0d\u89c1\uff0c\u4f46\u8fd9\u8fb9\u53ea\u6709\u4e00\u6761\u9053\u8def\uff0c\u4e8e\u662f\u63d0\u6c14\u5411\u524d\u75be\u8ffd\uff0c\u8fd9\u4e00\u63d0\u6c14\uff0c\u771f\u662f\u75be\u903e\u5954\u9a6c\uff0c\u8ffd\u4e0d\u591a\u65f6\uff0c\u679c\u89c1\u4e24\u4eba\u5728\u5411\u524d\u6025\u5954\u3002 \u3000\u3000\u4ed6\u653e\u8f7b\u811a\u6b65\uff0c\u9632\u90a3\u4e24\u4eba\u53d1\u89c9\uff0c\u4f46\u52bf\u5934\u4e1d\u6beb\u4e0d\u7f13\uff0c\u7247\u523b\u95f4\u76f8\u8ddd\u5df2\u8fd1\u3002\u4f46\u89c1\u90a3\u4e24\u4eba\u8eab\u7a7f\u7ea2\u8863\uff0c\u5934\u4e0a\u4f38\u51fa\u4e24\u6761\u5c0f\u8fab\u5b50\uff0c\u770b\u80cc\u540e\u6a21\u6837\uff0c\u7adf\u662f\u5341\u4e94\u516d\u5c81\u7684\u7ae5\u5b50\u3002\u4e24\u4eba\u80a9\u5934\u5404\u8d1f\u4e00\u4e2a\u5305\u88f9\uff0c\u4ece\u8eab\u5f62\u811a\u6b65\u77a7\u6765\uff0c\u5305\u88f9\u4efd\u91cf\u7740\u5b9e\u4e0d\u8f7b\uff0c\u60f3\u6765\u4fbf\u662f\u5e93\u94f6\u4e86\uff0c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u8d1f\u4e86\u91cd\u7269\u5c45\u7136\u8fd8\u80fd\u5982\u6b64\u5954\u8dc3\u8fc5\u6377\uff0c\u5b9e\u662f\u96be\u5f97\u3002\u5954\u4e0d\u591a\u65f6\uff0c\u4e24\u4e2a\u7ea2\u8863\u7ae5\u5b50\u5df2\u5230\u57ce\u8fb9\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u4ed6\u4eec\u5982\u4f55\u51fa\u57ce\uff1f\u201d\u54ea\u77e5\u4e8c\u7ae5\u7adf\u4e0d\u505c\u6b65\uff0c\u76f4\u51b2\u800c\u51fa\u3002 \u3000\u3000\u5b88\u5728\u57ce\u95e8\u53e3\u7684\u519b\u58eb\u773c\u524d\u4e00\u82b1\uff0c\u4e24\u56e2\u706b\u6837\u7684\u4e1c\u897f\u5df2\u4ece\u8eab\u65c1\u64e6\u8fc7\uff0c\u6b63\u81ea\u60ca\u8be7\uff0c\u7a81\u7136\u4e00\u4e2a\u7070\u5f71\u53c8\u662f\u4e00\u6643\u51fa\u57ce\uff0c\u6bd4\u90a3\u4e24\u56e2\u7ea2\u4e91\u66f4\u52a0\u8fc5\u901f\uff0c\u7b49\u5230\u671b\u89c1\u662f\u4e24\u4e2a\u7a7f\u7ea2\u3001\u4e00\u4e2a\u7a7f\u7070\u4e4b\u4eba\u7684\u80cc\u5f71\u65f6\uff0c\u4e09\u4eba\u65e9\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5c3e\u968f\u53cc\u7ae5\uff0c\u4e24\u540d\u7ae5\u5b50\u59cb\u7ec8\u6ca1\u6709\u53d1\u89c9\u3002\u51fa\u57ce\u540e\u5954\u884c\u4e03\u516b\u91cc\u8def\uff0c\u773c\u524d\u5c3d\u662f\u7530\u91ce\u3002\u4e24\u7ae5\u6765\u5230\u4e00\u5ea7\u5927\u5b85\u4e4b\u524d\uff0c\u4ece\u8eab\u8fb9\u53d6\u51fa\u5e26\u94a9\u7ef3\u7d22\uff0c\u629b\u5c06\u4e0a\u53bb\uff0c\u6293\u4f4f\u5899\u5934\uff0c\u6500\u63f4\u800c\u4e0a\uff0c\u8df3\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d70\u8fd1\uff0c\u89c1\u90a3\u5b85\u7b2c\u5468\u56f4\u4e00\u531d\u9ed1\u8272\u56f4\u5899\uff0c\u5899\u9ad8\u4e24\u4e08\uff0c\u5c45\u7136\u6ca1\u4e00\u9053\u95e8\u6237\u3002\u56f4\u5899\u6d82\u5f97\u9ed1\u6f06\u6f06\u7684\uff0c\u751a\u662f\u9634\u68ee\u53ef\u6016\uff0c\u8fd9\u5df2\u5341\u5206\u5947\u602a\uff0c\u800c\u5c4b\u5b50\u7adf\u6ca1\u95e8\u6237\uff0c\u66f4\u662f\u5929\u4e0b\u5c11\u6709\u7684\u602a\u4e8b\u3002\u4ed6\u597d\u5947\u5fc3\u8d77\uff0c\u7eb5\u8eab\u8dc3\u5165\uff0c\u91cc\u9762\u5730\u57fa\u79bb\u5899\u5374\u6709\u4e24\u4e08\u4e09\u5c3a\u9ad8\uff0c\u5982\u4e0d\u662f\u8eab\u8d1f\u7edd\u9876\u6b66\u529f\uff0c\u591a\u534a\u4f1a\u51fa\u4e8e\u4e0d\u610f\uff0c\u6454\u8dcc\u4e00\u4ea4\u3002\u91cc\u9762\u53c8\u6709\u4e00\u9053\u56f4\u5899\uff0c\u5168\u662f\u767d\u8272\uff0c\u4ecd\u662f\u65e0\u95e8\u3002 \u3000\u3000\u4ed6\u8fd9\u65f6\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u8dc3\u4e0a\u5899\u5934\u3002\u8fd9\u5835\u5899\u6bd4\u5916\u9762\u56f4\u5899\u5df2\u9ad8\u4e86\u4e09\u5c3a\uff0c\u4f46\u56e0\u5730\u57fa\u4f4e\u9677\u4e09\u5c3a\uff0c\u5728\u5916\u9762\u5374\u770b\u4e0d\u51fa\u6765\u3002\u4ed6\u8dc3\u8fdb\u767d\u5899\uff0c\u53d1\u89c9\u5730\u57fa\u53c8\u4f4e\u4e09\u5c3a\uff0c\u524d\u9762\u4e00\u91cd\u56f4\u5899\u5168\u4f5c\u84dd\u8272\uff0c\u5899\u57a3\u66f4\u6bd4\u767d\u5899\u9ad8\u4e86\u4e09\u5c3a\u3002\u8dc3\u8fdb\u4e00\u91cd\u53c8\u662f\u4e00\u91cd\uff0c\u7b2c\u56db\u91cd\u662f\u9ec4\u5899\uff0c\u7b2c\u4e94\u91cd\u662f\u7ea2\u5899\uff0c\u90a3\u65f6\u5899\u9ad8\u5df2\u8fbe\u4e09\u4e08\u4e09\u5c3a\uff0c\u4ed6\u8f7b\u529f\u518d\u9ad8\uff0c\u4e5f\u5df2\u4e0d\u80fd\u8dc3\u4e0a\u5899\u5934\uff0c\u5f53\u4e0b\u65bd\u5c55\u201c\u58c1\u864e\u6e38\u5899\u529f\u201d\uff0c\u624b\u8db3\u5e76\u7528\uff0c\u63d0\u6c14\u76f4\u4e0a\u3002 \u3000\u3000\u5bfb\u601d\uff1a\u201c\u96be\u9053\u51fa\u5165\u6b64\u5c4b\uff0c\u90fd\u662f\u8981\u7528\u7ef3\u7d22\u6500\u63f4\uff1f\u5fc5\u5b9a\u53e6\u6709\u5bc6\u95e8\u3002\u201d \u3000\u3000\u5de6\u624b\u6500\u4e0a\u5899\u5934\uff0c\u4e00\u63d0\u52b2\uff0c\u7ffb\u8eab\u800c\u8d77\uff0c\u5750\u4e0a\u5899\u5934\uff0c\u53ea\u89c1\u91cc\u9762\u662f\u4e94\u5f00\u95f4\u4e09\u8fdb\u74e6\u5c4b\uff0c\u9759\u6084\u6084\u7684\u4f3c\u4e4e\u9612\u65e0\u4e00\u4eba\u3002 \u3000\u3000\u4ed6\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u665a\u8f88\u5192\u6627\uff0c\u64c5\u8fdb\u5b9d\u5e84\u3002\u8d24\u4e3b\u4eba\u53ef\u80fd\u8d50\u89c1\u4e48\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e00\u505c\uff0c\u53ea\u542c\u4e94\u9053\u9ad8\u5899\u4e0a\u649e\u56de\u6765\u7684\u56de\u58f0\u5148\u540e\u4ea4\u7ec7\uff0c\u7ec4\u6210\u4e00\u7247\u70e6\u6742\u4e4b\u58f0\uff0c\u5c4b\u4e2d\u59cb\u7ec8\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u7b49\u4e86\u7247\u523b\uff0c\u53c8\u53eb\u4e00\u904d\uff0c\u7a81\u7136\u7b2c\u4e09\u8fdb\u4e2d\u6251\u51fa\u5341\u4f59\u6761\u5de8\u72ac\uff0c\u5f20\u7259\u821e\u722a\uff0c\u9ad8\u58f0\u72c2\u5420\uff0c\u6a21\u6837\u751a\u662f\u51f6\u6076\u3002\u4ed6\u672c\u89c1\u4e24\u4e2a\u7ae5\u5b50\u6b66\u827a\u9ad8\u5f3a\uff0c\u5fc3\u60f3\u5c4b\u4e3b\u4eba\u5fc5\u662f\u82f1\u4fa0\u4e00\u6d41\uff0c\u9887\u60f3\u7ed3\u8bc6\uff0c\u8fd9\u65f6\u89c1\u5c4b\u91cc\u653e\u51fa\u731b\u72ac\uff0c\u77e5\u9053\u4e3b\u4eba\u538c\u6076\u5916\u5ba2\uff0c\u4e0d\u4fbf\u81ea\u8ba8\u6ca1\u8da3\uff0c\u4e8e\u662f\u8dc3\u51fa\u5899\u5916\uff0c\u56de\u5230\u5c45\u6240\u3002 \u3000\u3000\u8fdb\u5c4b\u65f6\uff0c\u53ea\u89c1\u9752\u9752\u6b63\u5728\u96c7\u5320\u8d2d\u7269\uff0c\u6574\u82b1\u6728\uff0c\u4fee\u95e8\u7a97\uff0c\u6362\u5730\u677f\uff0c\u5237\u5899\u58c1\uff0c\u5fd9\u5f97\u4e0d\u53ef\u5f00\u4ea4\u3002\u8881\u627f\u5fd7\u6697\u559c\uff0c\u5fc3\u60f3\u9752\u5f1f\u52a9\u6211\u751a\u591a\uff0c\u5f53\u65e5\u8862\u6c5f\u6c5f\u4e0a\u90a3\u80a1\u6740\u4eba\u4e0d\u7728\u773c\u7684\u51f6\u72e0\u6c14\u8d28\uff0c\u4e0d\u5230\u4e00\u5e74\uff0c\u7adf\u7136\u9010\u6e10\u6539\u53d8\u3002 \u3000\u3000\u665a\u996d\u540e\uff0c\u4ed6\u628a\u521a\u624d\u6240\u9047\u8bf4\u4e86\u3002\u5927\u5bb6\u5567\u5567\u79f0\u5947\uff0c\u90fd\u731c\u4e0d\u900f\u602a\u5c4b\u4e2d\u6240\u5c45\u662f\u4f55\u7b49\u6837\u4eba\u3002 \u3000\u3000\u6b21\u65e5\u6e05\u6668\uff0c\u4f17\u4eba\u805a\u5728\u82b1\u5385\u91cc\u5403\u65e9\u996d\u3002\u5ead\u4e2d\u79ef\u96ea\u76c8\u5bf8\uff0c\u539f\u6765\u6628\u665a\u7adf\u4e0b\u4e86\u534a\u591c\u5927\u96ea\u3002\u9662\u5b50\u91cc\u4e24\u6811\u6885\u82b1\u542b\u82de\u5410\u8273\uff0c\u6e05\u9999\u6d6e\u52a8\uff0c\u5728\u96ea\u4e2d\u5f00\u5f97\u8d8a\u52a0\u7cbe\u795e\u3002 \u3000\u3000\u4e00\u540d\u5bb6\u4e01\u5306\u5306\u8fdb\u6765\uff0c\u5bf9\u9752\u9752\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u5916\u9762\u6709\u4eba\u9001\u793c\u6765\u3002\u201d \u3000\u3000\u53e6\u4e00\u540d\u5bb6\u4e01\u6367\u8fdb\u793c\u7269\uff0c\u539f\u6765\u662f\u4e00\u4e2a\u5b8b\u74f7\u82b1\u74f6\uff0c\u4e00\u5ea7\u6c88\u77f3\u7530\u7ed8\u7684\u5c0f\u5c4f\u98ce\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e24\u4ef6\u793c\u7269\u5012\u4e5f\u96c5\u81f4\uff0c\u8c01\u9001\u7684\u5440\uff1f\u201d\u793c\u7269\u4e2d\u5374\u65e0\u540d\u5e16\u3002\u9752\u9752\u5c01\u4e86\u4e00\u4e24\u94f6\u5b50\uff0c\u547d\u5bb6\u4e01\u62ff\u51fa\u53bb\u6253\u8d4f\uff0c\u95ee\u6e05\u695a\u662f\u8c01\u5bb6\u9001\u7684\u793c\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5bb6\u4e01\u56de\u6765\u7980\u9053\uff1a\u201c\u9001\u793c\u7684\u4eba\u5df2\u8d70\u4e86\uff0c\u8ffd\u4ed6\u4e0d\u7740\u3002\u201d \u3000\u3000\u4f17\u4eba\u90fd\u7b11\u90a3\u9001\u793c\u4eba\u5192\u5931\uff0c\u767d\u53d7\u4e86\u4ed6\u7684\u793c\uff0c\u5374\u4e0d\u89c1\u4ed6\u60c5\u3002\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8881\u76f8\u516c\u540d\u6ee1\u5929\u4e0b\uff0c\u8fd9\u6b21\u6765\u4eac\uff0c\u6c5f\u6e56\u4e0a\u591a\u6709\u4f20\u95fb\uff0c\u603b\u662f\u6155\u540d\u7684\u670b\u53cb\u5411\u4f60\u8868\u793a\u656c\u610f\u7684\u3002\u201d\u4f17\u4eba\u90fd\u9053\u5fc5\u662f\u5982\u6b64\u3002 \u3000\u3000\u4e2d\u5348\u65f6\u5206\uff0c\u6709\u4eba\u6311\u4e86\u6574\u5e2d\u7cbe\u96c5\u7684\u9152\u80b4\u6765\uff0c\u4e43\u662f\u5317\u4eac\u8457\u540d\u7684\u5168\u805a\u5174\u83dc\u9986\u505a\u7684\u540d\u83dc\u3002\u4e00\u95ee\u53a8\u5e08\uff0c\u8bf4\u662f\u6709\u4eba\u4ed8\u4e86\u94f6\u5b50\u8ba9\u9001\u6765\u7684\u3002\u4f17\u4eba\u8d77\u4e86\u7591\u5fc3\uff0c\u628a\u9152\u80b4\u8ba9\u732b\u72d7\u8bd5\u5403\uff0c\u5e76\u65e0\u5f02\u72b6\u3002 \u3000\u3000\u4e0b\u5348\u53c8\u9646\u7eed\u6709\u4eba\u9001\u4e1c\u897f\u6765\uff0c\u6216\u662f\u684c\u6905\uff0c\u6216\u662f\u82b1\u6728\uff0c\u90fd\u662f\u5b85\u7b2c\u4e2d\u5408\u7528\u4e4b\u7269\u3002\u9752\u9752\u53ea\u8bf4\u5f97\u4e00\u53e5\uff1a\u201c\u8fd9\u91cc\u987b\u5f97\u6302\u4e00\u76cf\u5927\u706f\u624d\u662f\u3002\u201d\u8fc7\u4e0d\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c31\u6709\u4eba\u9001\u6765\u4e00\u76cf\u7cbe\u81f4\u534e\u8d35\u7684\u5927\u5bab\u706f\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u53c8\u6709\u4eba\u9001\u6765\u7ef8\u7f0e\u4e1d\u7ed2\u3001\u978b\u5e3d\u8863\u5dfe\uff0c\u8fde\u9752\u9752\u7528\u7684\u80ed\u8102\u82b1\u7c89\uff0c\u4e5f\u90fd\u662f\u7279\u9009\u4e0a\u7b49\u7684\u9001\u6765\u3002\u94c1\u7f57\u6c49\u4e00\u628a\u6293\u4f4f\u90a3\u9001\u8863\u670d\u7684\u4eba\uff0c\u559d\u9053\uff1a\u201c\u4f60\u600e\u77e5\u8fd9\u91cc\u6709\u4e2a\u5934\u9640\uff1f\u8fde\u6211\u7a7f\u7684\u8888\u88df\u4e5f\u9001\u6765\u4e86\uff1f\u201d \u3000\u3000\u90a3\u8863\u5e97\u4f19\u8ba1\u7ed9\u4ed6\u4e00\u6293\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u554a\uff01\u4eca\u513f\u4e00\u65e9\uff0c\u6709\u4eba\u5230\u5c0f\u5e97\u91cc\u6765\uff0c\u591a\u51fa\u94f6\u5b50\u5429\u5490\u8d76\u505a\u7684\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4eba\u4eba\u5947\u602a\u4e0d\u5df2\uff0c\u7eb7\u7eb7\u731c\u6d4b\u3002\u9752\u9752\u6545\u610f\u9053\uff1a\u201c\u8fd9\u9001\u793c\u7684\u4eba\u8981\u662f\u771f\u77e5\u6211\u5fc3\u601d\uff0c\u7ed9\u6211\u5f04\u4e00\u4e32\u73cd\u73e0\u6765\u5c31\u597d\u5566\u3002\u201d\u9694\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4ec6\u4eba\u8d70\u51fa\u5385\u53bb\u3002\u9752\u9752\u5411\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u5feb\u77a7\u4ed6\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u4e0d\u591a\u65f6\u90a3\u4ec6\u4eba\u53c8\u56de\u6765\u4f8d\u5019\u3002\u6d2a\u80dc\u6d77\u5374\u9694\u4e86\u4e00\u4e2a\u65f6\u8fb0\u624d\u56de\u3002 \u3000\u3000\u4ed6\u521a\u8de8\u8fdb\u95e8\uff0c\u73e0\u5b9d\u5e97\u91cc\u5df2\u9001\u4e86\u4e24\u4e32\u73e0\u5b50\u6765\u3002 \u3000\u3000\u9752\u9752\u63a5\u4e86\u73e0\u5b50\uff0c\u76f4\u5411\u5185\u5ba4\uff0c\u8881\u627f\u5fd7\u548c\u6d2a\u80dc\u6d77\u90fd\u8ddf\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u90a3\u4ec6\u4eba\u8d70\u5230\u95e8\u5916\uff0c\u5bf9\u4e00\u4e2a\u4e5e\u4e10\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u5c31\u56de\u8fdb\u6765\u3002\u6211\u5c31\u8ddf\u7740\u90a3\u4e5e\u4e10\u3002\u89c1\u4ed6\u8d70\u8fc7\u4e86\u4e00\u6761\u8857\uff0c\u5c31\u6709\u8859\u95e8\u7684\u4e00\u4e2a\u516c\u5dee\u8fce\u4e0a\u6765\u3002\u4e24\u4eba\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u90a3\u4e5e\u4e10\u53c8\u56de\u5230\u6211\u4eec\u95e8\u524d\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u76ef\u7740\u90a3\u9e70\u722a\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u6b63\u662f\u3002\u90a3\u9e70\u722a\u5374\u4e0d\u4e0a\u8859\u95e8\uff0c\u8d70\u5230\u4e00\u6761\u80e1\u540c\u7684\u4e00\u5ea7\u5927\u9662\u5b50\u91cc\u3002\u6211\u89c1\u56db\u4e0b\u65e0\u4eba\uff0c\u4e0a\u5c4b\u53bb\u5077\u5077\u4e00\u5f20\u3002\u539f\u6765\u91cc\u9762\u805a\u4e86\u5341\u591a\u540d\u516c\u5dee\uff0c\u4e2d\u95f4\u4e00\u4e2a\u8001\u5934\u513f\uff0c\u778e\u4e86\u4e00\u53ea\u773c\u775b\uff0c\u5927\u5bb6\u53eb\u4ed6\u5355\u8001\u5e08\uff0c\u4f3c\u662f\u4ed6\u4eec\u7684\u5934\u5b50\u3002\u6211\u6015\u4ed6\u4eec\u53d1\u89c9\uff0c\u5c31\u6e9c\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u597d\u554a\uff01\u5b98\u5e9c\u8033\u76ee\u5012\u4e5f\u771f\u7075\uff0c\u54b1\u4eec\u4e00\u5230\u5317\u4eac\uff0c\u9e70\u722a\u5c31\u5f97\u4e86\u6d88\u606f\u3002\u54fc\uff0c\u8981\u52a8\u54b1\u4eec\u7684\u624b\uff0c\u53ea\u6015\u4e5f\u6ca1\u8fd9\u4e48\u5bb9\u6613\u5462\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u53ef\u662f\u5947\u5728\u5e72\u4e48\u8981\u9001\u4e1c\u897f\u6765\uff0c\u4e0d\u662f\u660e\u7740\u8ba9\u54b1\u4eec\u77e5\u9053\u4e48\uff1f\u4eac\u91cc\u5403\u516c\u4e8b\u996d\uff0c\u5fc5\u5b9a\u7cbe\u660e\u5f3a\u5e72\uff0c\u51b3\u4e0d\u4f1a\u505a\u50bb\u4e8b\u3002\u4e0d\u77e5\u662f\u751a\u4e48\u610f\u601d\uff1f\u201d\u547d\u6d2a\u80dc\u6d77\u628a\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u3001\u80e1\u6842\u5357\u7b49\u4eba\u8bf7\u6765\uff0c\u8c08\u4e86\u4e00\u4f1a\uff0c\u90fd\u662f\u731c\u60f3\u4e0d\u900f\u3002 \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u516c\u5dee\u7684\u810f\u4e1c\u897f\uff0c\u54b1\u4eec\u4e0d\u8981\uff01\u201d\u5f53\u665a\u5979\u4e0e\u54d1\u5df4\u3001\u94c1\u7f57\u6c49\u3001\u80e1\u6842\u5357\u3001\u6d2a\u80dc\u6d77\u7b49\u642c\u4e86\u9001\u6765\u5404\u7269\uff0c\u90fd\u653e\u5728\u516c\u5dee\u805a\u4f1a\u7684\u90a3\u4e2a\u5927\u9662\u5b50\u91cc\u3002 \u3000\u3000\u6b21\u65e5\u9752\u9752\u628a\u4f20\u9012\u6d88\u606f\u7684\u4ec6\u4eba\u6253\u53d1\u8d70\u4e86\uff0c\u5374\u4e5f\u6ca1\u96be\u4e3a\u4ed6\u3002\u90a3\u4ec6\u4eba\u606d\u606d\u656c\u656c\u7684\u63a5\u4e86\u5de5\u94b1\uff0c\u4e00\u518d\u79f0\u8c22\uff0c\u78d5\u4e86\u51e0\u4e2a\u5934\u53bb\u4e86\uff0c\u4e1d\u6beb\u6ca1\u9732\u51fa\u4e0d\u6109\u7684\u795e\u8272\u3002\u8881\u627f\u5fd7\u7b49\u4e25\u5bc6\u6212\u5907\uff0c\u9759\u4ee5\u5f85\u53d8\uff0c\u90a3\u5929\u679c\u7136\u6ca1\u518d\u6709\u4eba\u9001\u4e1c\u897f\u6765\u3002 \u3000\u3000\u8fd9\u5929\u665a\u4e0a\u53c8\u662f\u4e0b\u4e86\u4e00\u665a\u5927\u96ea\u3002\u6b21\u65e5\u4e00\u65e9\uff0c\u6d2a\u80dc\u6d77\u6ee1\u8138\u60ca\u8be7\u4e4b\u8272\uff0c\u8fdb\u6765\u7980\u62a5\uff1a\u201c\u5c4b\u5b50\u524d\u9762\u7684\u79ef\u96ea\uff0c\u4e0d\u77e5\u662f\u8c01\u7ed9\u6253\u626b\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u8fd9\u771f\u5947\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u6279\u9e70\u722a\u4f3c\u4e4e\u6697\u4e2d\u5728\u62da\u547d\u8ba8\u597d\u54b1\u4eec\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u554a\uff0c\u6211\u77e5\u9053\u4e86\u3002\u201d\u4f17\u4eba\u5fd9\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u6015\u54b1\u4eec\u5728\u4eac\u91cc\u505a\u51fa\u5927\u6848\u6765\uff0c\u5bf9\u4ed8\u4e0d\u4e86\uff0c\u56e0\u6b64\u5148\u6765\u6253\u4e2a\u62db\u547c\uff0c\u4ea4\u4e2a\u670b\u53cb\u3002\u201d\u6c99\u5929\u5e7f\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u5012\u6709\u70b9\u50cf\u3002\u53ef\u662f\u6211\u505a\u4e86\u8fd9\u4e48\u591a\u5e74\u5f3a\u76d7\uff0c\u4ece\u6765\u6ca1\u542c\u89c1\u8fc7\u8fd9\u79cd\u4e8b\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u5ffd\u9053\uff1a\u201c\u6211\u60f3\u8d77\u5566\uff0c\u90a3\u72ec\u773c\u6355\u5feb\u540d\u53eb\u72ec\u773c\u795e\u9f99\u5355\u94c1\u751f\u3002\u4e0d\u8fc7\u4ed6\u9000\u9690\u5df2\u4e45\uff0c\u8fd9\u624d\u4e00\u65f6\u60f3\u4ed6\u4e0d\u8d77\u3002\u201d \u3000\u3000\u53c8\u8fc7\u6570\u65e5\uff0c\u4f17\u4eba\u89c1\u518d\u65e0\u5f02\u4e8b\uff0c\u4e5f\u6e10\u6e10\u4e0d\u628a\u8fd9\u4e8b\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u8fd9\u5929\u6b63\u662f\u51ac\u81f3\uff0c\u4f17\u4eba\u5728\u5927\u5385\u4e0a\u996e\u9152\u95f2\u8c08\uff0c\u5bb6\u4e01\u9001\u4e0a\u4e2a\u5927\u7ea2\u540d\u5e16\uff0c\u5199\u7740\u201c\u665a\u751f\u5355\u94c1\u751f\u8bf7\u5b89\u201d\u7684\u5b57\u6837\uff0c\u5e76\u6709\u516b\u8272\u793c\u76d8\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5feb\u8bf7\u3002\u201d\u5bb6\u4e01\u9053\uff1a\u201c\u8fd9\u4f4d\u5355\u7237\u4e5f\u771f\u602a\uff0c\u4ed6\u8bf4\u7ed9\u8881\u76f8\u516c\u8bf7\u5b89\uff0c\u8f6c\u5934\u8d70\u4e86\uff0c\u8ba9\u4ed6\u5750\uff0c\u5374\u4e0d\u80af\u8fdb\u6765\u3002\u201d\u6d2a\u80dc\u6d77\u5949\u4e86\u8881\u627f\u5fd7\u4e4b\u547d\uff0c\u62ff\u4e86\u8881\u627f\u5fd7\u3001\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u4e09\u4eba\u7684\u540d\u5e16\u56de\u62dc\uff0c\u5e76\u628a\u793c\u7269\u90fd\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u63a5\u8fde\u4e09\u5929\uff0c\u5355\u94c1\u751f\u603b\u662f\u4e00\u65e9\u5c31\u6765\u6295\u9001\u540d\u5e16\u8bf7\u5b89\u3002\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u72ec\u773c\u795e\u9f99\u5728\u5317\u65b9\u6b66\u6797\u4e2d\u4e5f\u4e0d\u662f\u65e0\u540d\u4e4b\u8f88\uff0c\u600e\u4e48\u9b3c\u9b3c\u795f\u795f\u7684\u5c3d\u641e\u8fd9\u4e00\u5957\uff0c\u660e\u513f\u5f85\u6211\u627e\u4e0a\u95e8\u53bb\u95ee\u95ee\u3002\u201d\u80e1\u6842\u5357\u9053\uff1a\u201c\u8fd9\u4e9b\u62db\u6570\u53ef\u900f\u7740\u5168\u65e0\u6076\u610f\uff0c\u771f\u662f\u90aa\u95e8\u3002\u201d \u3000\u3000\u94c1\u7f57\u6c49\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u5e72\u751a\u4e48\u3002\u201d\u4f17\u4eba\u89c1\u4ed6\u5e73\u65f6\u50bb\u6123\u6123\u7684\uff0c\u8fd9\u65f6\u5c45\u7136\u6709\u72ec\u5f97\u4e4b\u89c1\uff0c\u90fd\u611f\u8be7\u5f02\uff0c\u9f50\u95ee\uff1a\u201c\u5e72\u751a\u4e48\u554a\uff1f\u201d\u94c1\u7f57\u6c49\u9053\uff1a\u201c\u4ed6\u89c1\u8881\u76f8\u516c\u6b66\u529f\u65e2\u9ad8\uff0c\u540d\u6c14\u53c8\u5927\uff0c\u56e0\u6b64\u60f3\u62db\u4ed6\u505a\u5973\u5a7f\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u65e0\u4e0d\u5927\u7b11\u3002\u6c99\u5929\u5e7f\u6b63\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u4e00\u4e0b\u5b50\u5fcd\u4e0d\u4f4f\uff0c\u5168\u55b7\u5728\u80e1\u6842\u5357\u8eab\u4e0a\u3002\u80e1\u6842\u5357\u4e00\u9762\u63e9\u8eab\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u72ec\u773c\u9f99\u7684\u5973\u513f\u4e5f\u662f\u72ec\u773c\u9f99\uff0c\u8881\u76f8\u516c\u600e\u4e48\u4f1a\u8981\uff1f\u201d\u94c1\u7f57\u6c49\u77aa\u773c\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\uff1f\u201d\u80e1\u6842\u5357\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u600e\u77e5\u9053\u4ed6\u6709\u5973\u513f\uff1f\u201d \u3000\u3000\u4f17\u4eba\u5f00\u4e86\u4e00\u9635\u73a9\u7b11\u3002\u9752\u9752\u53e3\u91cc\u4e0d\u8bf4\u751a\u4e48\uff0c\u5fc3\u4e2d\u5374\u8001\u5927\u7684\u4e0d\u4e50\u610f\uff0c\u6697\u60f3\u90a3\u72ec\u773c\u9f99\u53ef\u6076\uff0c\u522b\u771f\u7684\u8981\u62db\u5927\u54e5\u505a\u5973\u5a7f\u3002\u8fd9\u5929\u665a\u4e0a\uff0c\u53d6\u6765\u4e03\u5f20\u767d\u7eb8\uff0c\u90fd\u753b\u4e86\u4e2a\u72ec\u773c\u9f99\u8001\u516c\u5dee\u7684\u56fe\u5f62\uff0c\u5199\u4e0a\u201c\u72ec\u773c\u795e\u9f99\u5355\u94c1\u751f\u76d7\u201d\u7684\u5b57\u6837\uff0c\u591c\u91cc\u98de\u8eab\u8dc3\u5165\u4e03\u5bb6\u8c6a\u95e8\u5927\u6237\uff0c\u6bcf\u5bb6\u76d7\u4e86\u4e9b\u9996\u9970\u94f6\u4e24\uff0c\u518d\u7ed9\u653e\u4e0a\u4e00\u5f20\u72ec\u773c\u9f99\u8096\u50cf\u3002 \u3000\u3000\u6b21\u65e5\u6e05\u6668\uff0c\u6d2a\u80dc\u6d77\u5728\u5979\u623f\u95e8\u4e0a\u6572\u4e86\u51e0\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u72ec\u773c\u9f99\u6765\u5566\u3002\u8881\u76f8\u516c\u966a\u4ed6\u5728\u5385\u4e0a\u8bf4\u8bdd\u3002\u201d\u9752\u9752\u6362\u4e0a\u7537\u88c5\uff0c\u8d70\u5230\u5385\u4e0a\uff0c\u679c\u89c1\u8881\u627f\u5fd7\u3001\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u966a\u7740\u4e00\u4e2a\u7626\u524a\u77ee\u5c0f\u7684\u8001\u5934\u5728\u559d\u8336\u3002\u8881\u627f\u5fd7\u7ed9\u5979\u5f15\u89c1\u4e86\u3002\u9752\u9752\u89c1\u8fd9\u5355\u94c1\u751f\u5df2\u6709\u516d\u5341\u4e0a\u4e0b\u5e74\u7eaa\uff0c\u987b\u7709\u7686\u767d\uff0c\u4e00\u53ea\u5de6\u773c\u70af\u70af\u53d1\u5149\uff0c\u663e\u5f97\u5341\u5206\u7cbe\u660e\u5e72\u7ec3\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u9053\uff1a\u201c\u5c0f\u8001\u513f\u505a\u8fd9\u7b49\u4e8b\uff0c\u5f53\u771f\u5341\u5206\u5192\u6627\u3002\u4e0d\u8fc7\u5b9e\u662f\u6709\u4ef6\u5927\u4e8b\uff0c\u60f3\u6073\u8bf7\u8881\u76f8\u516c\u8ddf\u5404\u4f4d\u9f0e\u529b\u76f8\u52a9\uff0c\u5c0f\u8001\u513f\u548c\u5404\u4f4d\u53c8\u4e0d\u76f8\u8bc6\uff0c\u53ea\u5f97\u51fa\u6b64\u4e0b\u7b56\u3002\u4e0d\u60f3\u62db\u607c\u4e86\u5404\u4f4d\uff0c\u5c0f\u8001\u513f\u8c28\u6b64\u8c22\u8fc7\u3002\u201d\u8bf4\u7740\u8dea\u4e0b\u6765\u78d5\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8fde\u5fd9\u6276\u8d77\uff0c\u6b63\u8981\u95ee\u4ed6\u4f55\u4e8b\u76f8\u6c42\uff0c\u9752\u9752\u5ffd\u9053\uff1a\u201c\u4ee4\u7231\u597d\u5427\uff1f\u600e\u4e0d\u8ddf\u4f60\u540c\u6765\uff1f\u201d\u5355\u94c1\u751f\u4e00\u6123\uff0c\u9053\uff1a\u201c\u5c0f\u8001\u513f\u5149\u8eab\u4e00\u4eba\uff0c\u8fde\u8001\u4f34\u4e5f\u6ca1\u6709\uff0c\u522b\u8bf4\u5b50\u5973\u5566\uff01\u201d\u9752\u9752\u53c8\u95ee\uff1a\u201c\u90a3\u4f60\u6709\u5b59\u5973\u513f\u6ca1\u6709\uff1f\u6709\u5e72\u5973\u513f\u6ca1\u6709\uff1f\u201d\u5355\u94c1\u751f\u9053\uff1a\u201c\u90fd\u6ca1\u6709\u3002\u201d\u9752\u9752\u5ae3\u7136\u4e00\u7b11\uff0c\u8fd4\u8eab\u5165\u623f\uff0c\u6367\u4e86\u76d7\u6765\u7684\u9996\u9970\u94f6\u4e24\uff0c\u90fd\u8fd8\u4e86\u7ed9\u4ed6\uff0c\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u8ddf\u4f60\u5f00\u4e2a\u73a9\u7b11\uff0c\u8bf7\u522b\u89c1\u602a\u3002\u4e0d\u8fc7\u82e5\u975e\u5982\u6b64\uff0c\u4e5f\u8bf7\u4e0d\u5230\u4f60\u5927\u9a7e\u5149\u4e34\u3002\u201d\u5355\u94c1\u751f\u8c22\u4e86\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u73a9\u7b11\u9669\u4e9b\u5bb3\u4e86\u6211\u7684\u8001\u547d\u3002\u201d\u53c8\u60f3\uff1a\u201c\u8fd9\u4e2a\u5973\u626e\u7537\u88c5\u7684\u59d1\u5a18\u600e\u5730\u8001\u662f\u95ee\u6211\u6709\u6ca1\u5973\u513f\uff1f\u603b\u4e0d\u662f\u60f3\u62dc\u6211\u4e3a\u5e72\u7239\u5427\uff1f\u201d \u3000\u3000\u4f17\u4eba\u90fd\u89c9\u5947\u602a\uff0c\u6b63\u8981\u76f8\u8be2\uff0c\u5ffd\u7136\u5916\u9762\u5306\u5306\u8fdb\u6765\u4e00\u540d\u6355\u5feb\uff0c\u5411\u4f17\u4eba\u884c\u4e86\u793c\uff0c\u5bf9\u5355\u94c1\u751f\u9053\uff1a\u201c\u5355\u8001\u5e08\uff0c\u53c8\u5931\u4e86\u4e8c\u5343\u4e24\u5e93\u94f6\u3002\u201d \u3000\u3000\u5355\u94c1\u751f\u500f\u7136\u53d8\u8272\uff0c\u7ad9\u8d77\u8eab\u6765\u4f5c\u4e86\u4e2a\u63d6\uff0c\u9053\uff1a\u201c\u5c0f\u8001\u513f\u6709\u4ef6\u6025\u4e8b\u8981\u67e5\u52d8\uff0c\u5f85\u4f1a\u518d\u6765\u8ddf\u5404\u4f4d\u8bf7\u5b89\u3002\u201d\u6536\u4e86\u9752\u9752\u4ea4\u8fd8\u7684\u7269\u4e8b\uff0c\u968f\u7740\u90a3\u6355\u5feb\u6025\u6025\u53bb\u4e86\u3002 \u3000\u3000\u5230\u5f97\u4e0b\u5348\uff0c\u9e45\u6bdb\u822c\u7684\u5927\u96ea\u6f2b\u5929\u800c\u4e0b\u3002\u9752\u9752\u7ea6\u4e86\u8881\u627f\u5fd7\uff0c\u5230\u57ce\u5916\u897f\u90ca\u996e\u9152\u8d4f\u96ea\u3002\u4e24\u4eba\u6ca1\u5355\u72ec\u5171\u6e38\u5df2\u4e45\uff0c\u8fd9\u65f6\u5077\u5f97\u534a\u65e5\u6e05\u95f2\uff0c\u751a\u662f\u7545\u5feb\u3002\u8fd9\u4e00\u5e26\u56db\u4e0b\u91cc\u90fd\u662f\u82a6\u82c7\u3002\u9752\u9752\u5e26\u7740\u98df\u76d2\uff0c\u76db\u4e86\u9152\u83dc\u3002\u4e24\u4eba\u559d\u9152\u95f2\u8c08\uff0c\u8d4f\u73a9\u98ce\u666f\u3002\u5f53\u5730\u5e73\u65f6\u5c31\u5df2\u8352\u51c9\uff0c\u8fd9\u65f6\u5929\u5bd2\u5927\u96ea\uff0c\u66f4\u662f\u4e0d\u89c1\u6709\u4eba\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u8d77\u4ea4\u8fd8\u4e86\u751a\u4e48\u4e1c\u897f\u7ed9\u5355\u94c1\u751f\uff0c\u9752\u9752\u7b11\u7740\u628a\u6628\u665a\u7684\u4e8b\u8bf4\u4e86\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5509\uff0c\u6211\u521a\u8d5e\u4f60\u53d8\u5f97\u4e56\u4e86\uff0c\u54ea\u77e5\u4ecd\u662f\u8fd9\u822c\u987d\u76ae\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u8d5e\u8fc7\u6211\u5440\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u8d5e\u4f60\uff0c\u4f60\u81ea\u7136\u4e0d\u77e5\u9053\u3002\u201d\u9752\u9752\u5f88\u662f\u9ad8\u5174\uff0c\u7b11\u9053\uff1a\u201c\u8c01\u6559\u4ed6\u4e0d\u80af\u9732\u9762\uff0c\u6697\u4e2d\u6363\u9b3c\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u60f3\u6c42\u54b1\u4eec\u751a\u4e48\u4e8b\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u54ea\uff0c\u54fc\uff0c\u4e0d\u7ba1\u4ed6\u6c42\u751a\u4e48\uff0c\u90fd\u522b\u7b54\u5e94\u3002\u201d\u4e24\u4eba\u559d\u4e86\u4e00\u4f1a\u9152\uff0c\u8bf4\u5230\u5728\u8862\u5dde\u77f3\u6881\u4e2d\u591c\u559d\u9152\u8d4f\u82b1\u4e4b\u4e8b\u3002\u9752\u9752\u60f3\u8d77\u6545\u4e61\u548c\u4ea1\u6bcd\uff0c\u4e0d\u89c9\u51c4\u7136\u6b32\u6ce3\u3002\u8881\u627f\u5fd7\u5fd9\u8bf4\u7b11\u8bdd\u5c94\u5f00\u3002 \u3000\u3000\u5750\u4e86\u534a\u65e5\uff0c\u773c\u89c1\u5929\u8272\u5c06\u665a\uff0c\u4e24\u4eba\u6536\u62fe\u4e86\u98df\u76d2\u56de\u5bb6\u3002\u7ecf\u8fc7\u4e00\u5ea7\u51c9\u4ead\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e5e\u4e10\u5367\u5728\u4e00\u5f20\u8349\u5e2d\u4e0a\uff0c\u53ea\u7a7f\u4e86\u4e00\u6761\u728a\u9f3b\u88e4\uff0c\u4e0a\u8eab\u8d64\u88f8\u3002\u9752\u9752\u9053\uff1a\u201c\u53ef\u601c\uff0c\u53ef\u601c\uff01\u201d\u62ff\u51fa\u4e00\u952d\u94f6\u5b50\uff0c\u653e\u5728\u5e2d\u4e0a\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5feb\u53bb\u4e70\u8863\u670d\uff0c\u522b\u51bb\u574f\u4e86\u3002\u201d\u521a\u8d70\u51fa\u4ead\u5b50\uff0c\u53ea\u542c\u90a3\u4e5e\u4e10\u5495\u54dd\u9053\uff1a\u201c\u7ed9\u6211\u94f6\u5b50\u5e72\u751a\u4e48\uff1f\u518d\u51b7\u4e9b\u4e5f\u51bb\u4e0d\u6b7b\u8001\u5b50\u3002 \u3000\u3000\u6709\u9152\u5374\u4e0d\u8bf7\u4eba\u559d\uff0c\u771f\u4e0d\u591f\u670b\u53cb\u3002\u201d \u3000\u3000\u9752\u9752\u5927\u6012\uff0c\u56de\u5934\u8981\u9a82\u3002\u8881\u627f\u5fd7\u89c1\u8fd9\u4e5e\u4e10\u8d64\u88f8\u4e86\u8eab\u5b50\u3002\u5728\u4e25\u5bd2\u4e2d\u6beb\u65e0\u6218\u745f\u754f\u51bb\u4e4b\u6001\uff0c\u672c\u5df2\u5947\u602a\uff0c\u542c\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4e00\u62c9\u9752\u9752\u7684\u624b\uff0c\u8f6c\u5934\u8bf4\u9053\uff1a\u201c\u9152\u5012\u8fd8\u6709\uff0c\u53ea\u662f\u6b8b\u83dc\u51b7\u9152\uff0c\u9887\u4e3a\u4e0d\u606d\uff0c\u4e0d\u6562\u76f8\u9080\u3002\u201d\u90a3\u4e5e\u4e10\u5750\u8d77\u8eab\u5b50\uff0c\u4f38\u624b\u9053\uff1a\u201c\u505a\u53eb\u5316\u7684\uff0c\u5403\u6b8b\u83dc\u3001\u559d\u51b7\u9152\uff0c\u90a3\u6b63\u5408\u9002\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4ece\u98df\u76d2\u4e2d\u62ff\u51fa\u4e00\u58f6\u5403\u5269\u7684\u9152\u83dc\uff0c\u9012\u4e86\u8fc7\u53bb\u3002\u90a3\u4e5e\u4e10\u63a5\u4e86\uff0c\u4ef0\u8116\u5b50\u9aa8\u561f\u561f\u7684\u731b\u559d\u3002 \u3000\u3000\u8fd9\u4e5e\u4e10\u56db\u5341\u5c81\u5de6\u53f3\u5e74\u7eaa\uff0c\u6ee1\u8138\u80e1\u987b\uff0c\u4e24\u6761\u81c2\u8180\u4e0a\u70b9\u70b9\u6591\u6591\uff0c\u5168\u662f\u4f24\u75a4\u3002\u4ed6\u628a\u4e00\u58f6\u9152\u559d\u5e72\uff0c\u8d5e\u9053\uff1a\u201c\u597d\u9152\uff01\u8fd9\u662f\u4e8c\u5341\u5e74\u7684\u5973\u513f\u7ea2\u9648\u7ecd\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u4f60\u5012\u8bc6\u8d27\uff0c\u4e0a\u53e3\u4fbf\u77e5\u3002\u201d\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u53ef\u60dc\u9152\u5c11\u4e86\uff0c\u559d\u5f97\u4e0d\u8fc7\u763e\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u660e\u65e5\u6211\u4eec\u518d\u643a\u9152\u6765\uff0c\u8bf7\u9601\u4e0b\u4e00\u9189\u5982\u4f55\uff1f\u201d\u4e5e\u4e10\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u4f4d\u76f8\u516c\u5012\u5f88\u6177\u6168\uff0c\u8bfb\u4e66\u4eba\u6709\u8fd9\u6837\u7684\u80f8\u895f\uff0c\u4e5f\u7b97\u96be\u5f97\u3002\u201d\u8881\u627f\u5fd7\u542c\u4ed6\u8c08\u5410\u4e0d\u4fd7\uff0c\u66f4\u77e5\u4ed6\u4e0d\u662f\u5bfb\u5e38\u4e5e\u4e10\uff0c\u4e24\u4eba\u4e00\u7b11\u8f6c\u8eab\u3002\u8d70\u51fa\u4ead\u53bb\u3002 \u3000\u3000\u8d70\u4e86\u6570\u6b65\uff0c\u9752\u9752\u597d\u5947\u56de\u5934\u518d\u671b\uff0c\u53ea\u89c1\u90a3\u4e5e\u4e10\u5f2f\u4e86\u8eab\u5b50\uff0c\u5168\u795e\u8d2f\u6ce8\u7684\u51dd\u89c6\u7740\u5de6\u65b9\u751a\u4e48\u4e1c\u897f\u3002\u9752\u9752\u62c9\u62c9\u8881\u627f\u5fd7\u7684\u624b\u9053\uff1a\u201c\u4ed6\u5728\u77a7\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u770b\u4e86\u4e00\u773c\u9053\uff1a\u201c\u4f3c\u4e4e\u662f\u751a\u4e48\u866b\u8c78\u3002\u201d\u4f46\u89c1\u90a3\u4e5e\u4e10\u795e\u60c5\u7d27\u8feb\uff0c\u53cc\u624b\u7b95\u5f20\uff0c\u4f3c\u4e4e\u4f5c\u52bf\u4fbf\u6b32\u6251\u4e0a\u3002\u4e24\u4eba\u8d70\u8fd1\u53bb\u770b\uff0c\u90a3\u4e5e\u4e10\u8fde\u8fde\u6325\u624b\uff0c\u8138\u8272\u6781\u662f\u4e25\u91cd\u3002 \u3000\u3000\u4e24\u4eba\u4e0d\u518d\u4e0a\u524d\uff0c\u968f\u7740\u4ed6\u773c\u5149\u5411\u96ea\u5730\u91cc\u4e00\u770b\uff0c\u539f\u6765\u662f\u6761\u5c0f\u86c7\uff0c\u957f\u4ec5\u534a\u5c3a\uff0c\u4f46\u901a\u4f53\u91d1\u8272\uff0c\u5728\u767d\u96ea\u4e2d\u707f\u7136\u751f\u5149\u3002 \u3000\u3000\u6ce8\uff1a\u6e05\u592a\u5b97\u7687\u592a\u6781\u6b7b\u56e0\u4e0d\u660e\u3002\u300a\u6e05\u53f2\u7a3f\xb7\u592a\u5b97\u672c\u7eaa\u300b\uff1a\u201c\u5d07\u5fb7\u516b\u5e74\u516b\u6708\u5e9a\u5348\uff0c\u4e0a\u5fa1\u5d07\u653f\u6bbf\uff0c\u662f\u5915\u4ea5\u65f6\u65e0\u75be\u5d29\uff0c\u5e74\u4e94\u5341\u6709\u4e8c\u3002\u201d\u5f53\u5929\u4ed6\u8fd8\u5728\u5904\u7406\u653f\u4e8b\uff0c\u4e00\u65e0\u5f02\u72b6\uff0c\u7a81\u7136\u5728\u534a\u591c\u91cc\u201c\u65e0\u75be\u5d29\u201d\uff0c\u540e\u4eba\u9887\u6709\u7591\u4e3a\u591a\u5c14\u886e\u6240\u8c0b\u6740\uff0c\u4f46\u7edd\u65e0\u4f50\u8bc1\u3002\u987a\u6cbb\u516d\u5e74\uff0c\u201c\u7687\u7236\u6444\u653f\u738b\u201d\u591a\u5c14\u886e\u636e\u8bf4\u548c\u7687\u592a\u6781\u7684\u5983\u5b50\u5e84\u5983\u3001\u5373\u987a\u6cbb\u7687\u5e1d\u7684\u6bcd\u4eb2\u5b5d\u5e84\u592a\u540e\u6b63\u5f0f\u7ed3\u5a5a\u3002\u5f20\u714c\u8a00\u8bd7\u6709\u4e91\uff1a\u201c\u6625\u5b98\u6628\u8fdb\u65b0\u4eea\u6ce8\uff0c\u5927\u793c\u606d\u9022\u592a\u540e\u5a5a\u3002\u201d\u6b64\u4e8b\u666e\u904d\u6d41\u4f20\uff0c\u4f46\u65e0\u660e\u6587\u8bb0\u8f7d\u3002\u8fd1\u4eba\u5b5f\u68ee\u8ba4\u4e3a\u4e0d\u786e\uff0c\u80e1\u9002\u5219\u5bf9\u5b5f\u68ee\u4e4b\u8003\u8bc1\u4ee5\u4e3a\u4e0d\u591f\u4ee4\u4eba\u4fe1\u670d\u3002\u5317\u65b9\u6e38\u7267\u6e14\u730e\u6c11\u65cf\u4e4b\u4e60\u4fd7\u548c\u4e2d\u539f\u6c49\u4eba\u5927\u5f02\uff0c\u5144\u7ec8\u5f1f\u53ca\uff0c\u539f\u5c5e\u5e38\u4e8b\u3002\u6e05\u592a\u540e\u4e0b\u5ac1\u591a\u5c14\u886e\u4e8b\uff0c\u8fd1\u4e16\u6cbb\u6e05\u53f2\u8005\u5927\u90fd\u4e0d\u5426\u5b9a\u6709\u6b64\u53ef\u80fd\u3002 \u3000\u3000\u56de\u76ee\u4e2d\u201c\u70db\u5f71\u201d\u7528\u5b8b\u592a\u5b97\u5f11\u5144\u5b8b\u592a\u7956\u201c\u70db\u5f71\u6447\u7ea2\u201d\u6545\u4e8b\u3002\u201c\u662d\u9633\u201d\u7528\u8d75\u5408\u5fb7\u5c45\u662d\u9633\u6bbf\u6545\u4e8b\u3002\u8d75\u5408\u5fb7\u4e3a\u7687\u540e\u8d75\u98de\u71d5\u4e4b\u59b9\uff0c\u5c01\u662d\u4eea\uff0c\u4e0e\u4eba\u79c1\u901a\uff0c\u540e\u81f4\u6c49\u6210\u5e1d\u4e8e\u6b7b\u3002\u6e05\u5e84\u5983\u4e3a\u592a\u5b97\u5b5d\u7aef\u7687\u540e\u4e4b\u4f84\u5973\uff0c\u6c11\u95f4\u4f20\u8bf4\u79f0\u4e4b\u4e3a\u201c\u5927\u7389\u513f\u201d\u3001\u201c\u5c0f\u7389\u513f\u201d\u8005\u4e5f\u3002\u6c49\u3001\u5b8b\u3001\u6e05\u4e09\u671d\u5bab\u95f1\u79d8\u4e8b\uff0c\u672a\u5c3d\u53ef\u4fe1\uff0c\u7275\u626f\u4e3a\u4e00\uff0c\u6216\u8fd1\u4e8e\u8bec\u3002\u5c0f\u8bf4\u5bb6\u8a00\uff0c\u53f2\u5bb6\u4e0d\u5fc5\u6df1\u7a76\u4e5f\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e94\u56de\u3000\u7ea4\u7ea4\u51fa\u94c1\u624b \u77eb\u77eb\u821e\u91d1\u86c7",content:(0,t.jsx)(t.Fragment,{children:"\u53ea\u89c1\u90a3\u91d1\u8272\u5c0f\u86c7\u6162\u6162\u5728\u96ea\u5730\u4e2d\u6e38\u8d70\uff0c\u90a3\u4e5e\u4e10\u5c4f\u606f\u51dd\u6c14\uff0c\u7d27\u7d27\u8ddf\u968f\u3002\u5c0f\u86c7\u6e38\u51fa\u5341\u4f59\u4e08\uff0c\u6765\u5230\u4e00\u4e2a\u5f84\u957f\u4e08\u8bb8\u7684\u5706\u5708\u3002\u56db\u56f4\u90fd\u662f\u767d\u96ea\uff0c\u5708\u4e2d\u5374\u7247\u96ea\u5168\u65e0\u3002\u773c\u89c1\u96ea\u82b1\u98d8\u5165\u5708\u5b50\u4fbf\u5373\u6d88\u878d\uff0c\u53d8\u6210\u6c34\u6c14\uff0c\u4f3c\u4e4e\u6ce5\u571f\u5e95\u4e0b\u85cf\u7740\u4e2a\u706b\u7089\u4e00\u822c\u3002\u5c0f\u86c7\u6e38\u5230\u5708\u8fb9\uff0c\u5e76\u4e0d\u8fdb\u53bb\uff0c\u56f4\u7740\u5708\u5b50\u7ed5\u4e86\u51e0\u5468\u3002\u90a3\u4e5e\u4e10\u5411\u8881\u627f\u5fd7\u548c\u9752\u9752\u6447\u624b\u793a\u610f\uff0c\u53eb\u4ed6\u4eec\u4e0d\u53ef\u8d70\u8fd1\u3002\u4e24\u4eba\u5fc3\u60f3\u5316\u5b50\u6349\u86c7\uff0c\u6709\u751a\u4e48\u5927\u4e0d\u4e86\uff0c\u89c1\u4ed6\u715e\u6709\u4ecb\u4e8b\uff0c\u5c31\u9759\u9759\u7ad9\u5728\u4e00\u65c1\u89c2\u770b\u3002\u53ea\u89c1\u90a3\u5c0f\u86c7\u5411\u7740\u5708\u5b50\u4e2d\u95f4\u4e00\u4e2a\u5927\u5b54\u4e0d\u4f4f\u5618\u6c14\uff0c\u8fc7\u4e86\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u53ea\u542c\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u5c0f\u86c7\u731d\u7136\u9000\u5012\uff0c\u6d1e\u91cc\u7a9c\u51fa\u4e00\u6761\u5927\u86c7\u6765\u3002\u9752\u9752\u5413\u4e86\u4e00\u8df3\uff0c\u5931\u58f0\u60ca\u547c\u3002\u90a3\u4e5e\u4e10\u6012\u76ee\u6a2a\u89c6\uff0c\u5982\u4e0d\u662f\u4ed6\u5fc3\u60c5\u7d27\u5f20\uff0c\u53ea\u6015\u65e9\u5df2\u5927\u58f0\u65a5\u9a82\u4e86\u3002 \u3000\u3000\u5927\u86c7\u8eab\u957f\u4e08\u4f59\uff0c\u7c97\u5982\u4eba\u81c2\uff0c\u5168\u8eab\u6591\u6593\u4e94\u8272\uff0c\u4e00\u9897\u5934\u4f5c\u4e09\u89d2\u5f62\uff0c\u6bd4\u4eba\u7684\u62f3\u5934\u8fd8\u5927\u3002\u8881\u627f\u5fd7\u66fe\u542c\u6728\u6851\u9053\u4eba\u8bf4\u8d77\uff0c\u51e1\u86c7\u5934\u4f5c\u4e09\u89d2\u5f62\u7684\u5fc5\u5177\u5947\u6bd2\uff0c\u5bfb\u5e38\u5927\u86c7\u65e0\u6bd2\uff0c\u6b64\u86c7\u5982\u6b64\u5de8\u5927\uff0c\u5374\u662f\u6bd2\u86c7\uff0c\u5b9e\u5728\u7f55\u89c1\u3002\u86c7\u866b\u4e4b\u7269\u51ac\u5929\u5fc5\u5b9a\u86f0\u4f0f\u571f\u4e2d\uff0c\u6781\u5c11\u51fa\u5916\uff0c\u8fd9\u5927\u86c7\u4f3c\u662f\u88ab\u5c0f\u86c7\u6fc0\u5f15\u51fa\u6765\uff0c\u8840\u7ea2\u7684\u820c\u5934\u603b\u6709\u534a\u5c3a\u6765\u957f\uff0c\u4e00\u4f38\u4e00\u7f29\uff0c\u5f62\u72b6\u53ef\u6016\u3002\u8fd9\u65f6\u5c0f\u86c7\u7ed5\u5708\u6e38\u8d70\uff0c\u8fc5\u901f\u5df2\u6781\u3002\u5927\u86c7\u8eab\u8eaf\u6bd4\u5c0f\u86c7\u7c97\u5927\u4f55\u903e\u4e94\u516d\u5341\u500d\uff0c\u4f46\u4e0d\u77e5\u600e\u6837\uff0c\u89c1\u4e86\u5c0f\u86c7\u7adf\u4f3c\u9887\u4e3a\u5fcc\u60ee\uff0c\u8eab\u5b50\u7d27\u7d27\u76d8\u6210\u4e00\u56e2\uff0c\u6602\u8d77\u86c7\u5934\uff0c\u53cc\u76ee\u7d27\u7d27\u76ef\u4f4f\u5c0f\u86c7\uff0c\u4e0d\u6562\u4e1d\u6beb\u6020\u5ffd\u3002\u5c0f\u86c7\u8d8a\u6e38\u8d8a\u6025\uff0c\u5927\u86c7\u8f6c\u5934\u4e5f\u968f\u7740\u52a0\u5feb\u3002 \u3000\u3000\u9752\u9752\u8fd9\u65f6\u4e0d\u518d\u5bb3\u6015\uff0c\u53ea\u89c9\u5f88\u662f\u6709\u8da3\uff0c\u4e00\u56de\u5934\uff0c\u5374\u89c1\u90a3\u4e5e\u4e10\u624b\u821e\u8db3\u8e48\uff0c\u6b63\u5728\u5927\u5fd9\u7279\u5fd9\uff0c\u4e0d\u4f4f\u4ece\u4e00\u53ea\u7834\u5e03\u888b\u91cc\u6478\u51fa\u4e00\u5757\u5757\u9ec4\u8272\u4e4b\u7269\uff0c\u585e\u5165\u53e3\u4e2d\u4e71\u56bc\uff0c\u56bc\u4e86\u4e00\u9635\uff0c\u62ff\u51fa\u6765\u634f\u6210\u7ec6\u6761\uff0c\u56f4\u5728\u5708\u5916\uff0c\u6162\u6162\u7684\u5e03\u6210\u4e86\u4e00\u4e2a\u9ec4\u5708\u3002\u836f\u7269\u6c14\u606f\u8f9b\u8fa3\uff0c\u867d\u7136\u76f8\u9694\u4e0d\u8fd1\uff0c\u5374\u4ecd\u662f\u523a\u9f3b\u96be\u95fb\u3002 \u3000\u3000\u90a3\u5c0f\u86c7\u7a81\u7136\u8dc3\u8d77\uff0c\u5411\u5927\u86c7\u5934\u9876\u6251\u53bb\uff0c\u5927\u86c7\u53e3\u4e2d\u55b7\u51fa\u4e00\u9635\u7ea2\u96fe\u3002\u5c0f\u86c7\u5728\u7a7a\u4e2d\u7ffb\u4e86\u51e0\u4e2a\u7b4b\u6597\uff0c\u53c8\u843d\u5728\u5730\u4e0b\u6e38\u8d70\uff0c\u770b\u6765\u7ea2\u96fe\u6781\u6bd2\uff0c\u5c0f\u86c7\u4e0d\u6562\u63a5\u8fd1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7a81\u7136\u60f3\u8d77\uff0c\u300a\u91d1\u86c7\u79d8\u7b08\u300b\u4e2d\u8bb0\u8f7d\u6709\u4e00\u5957\u62f3\u6cd5\uff0c\u8def\u5b50\u6709\u4e9b\u50cf\u201c\u516b\u5366\u6e38\u8eab\u638c\u201d\uff0c\u4f46\u53d8\u5316\u8fdc\u4e3a\u7e41\u590d\u3002\u6b64\u65f6\u89c1\u5230\u5927\u5c0f\u4e24\u86c7\u76f8\u62d2\u4e92\u653b\uff0c\u5ffd\u60f3\u8fd9\u62f3\u6cd5\u548c\u86c7\u6597\u9887\u4e3a\u76f8\u4f3c\uff0c\u91d1\u86c7\u90ce\u541b\u5f53\u5e74\u521b\u4e0b\u8fd9\u8def\u62f3\u6cd5\uff0c\u83ab\u975e\u662f\u5c71\u89c2\u86c7\u6597\u800c\u89e6\u673a\u4e48\uff1f\u53c8\u60f3\uff1a\u8fd9\u6761\u5c0f\u86c7\u4e5f\u662f\u91d1\u8272\uff0c\u5012\u4e5f\u5de7\u5408\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u4ecd\u662f\u4e0d\u4f4f\u56bc\u70c2\u836f\u7269\uff0c\u5728\u7b2c\u4e00\u9053\u9ec4\u7ebf\u5708\u5916\u53c8\u6577\u4e86\u4e24\u9053\u5708\u5b50\uff0c\u6bcf\u9053\u5708\u5b50\u76f8\u8ddd\u5c3a\u8bb8\u3002\u4ed6\u5e03\u7f6e\u5df2\u6bd5\uff0c\u8fd9\u624d\u8138\u9732\u7b11\u5bb9\uff0c\u4fef\u8eab\u9759\u89c2\u4e24\u86c7\u4e89\u6597\uff0c\u90a3\u5c0f\u86c7\u8fde\u6251\u6570\u6b21\uff0c\u90fd\u88ab\u5927\u86c7\u55b7\u7ea2\u96fe\u51fb\u9000\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u5c0f\u86c7\u6570\u6b21\u8fdb\u653b\uff0c\u8eab\u6cd5\u5404\u4e0d\u76f8\u540c\uff0c\u5927\u86c7\u7684\u7ea2\u96fe\u5374\u8d8a\u55b7\u8d8a\u7a00\u3002\u518d\u6597\u4e0b\u53bb\uff0c\u5927\u86c7\u5fc5\u8d25\u3002\u201d\u5374\u89c1\u5927\u86c7\u7a81\u7136\u53cd\u51fb\uff0c\u5f20\u5f00\u5927\u53e3\uff0c\u9732\u51fa\u7360\u7259\u75be\u5411\u5c0f\u86c7\u54ac\u53bb\u3002\u5c0f\u86c7\u4e1c\u95ea\u897f\u907f\uff0c\u5e38\u5e38\u95f4\u4e0d\u5bb9\u53d1\uff0c\u6709\u65f6\u751a\u81f3\u5728\u5927\u86c7\u53e3\u4e2d\u6a2a\u7a7f\u800c\u8fc7\uff0c\u5927\u86c7\u5374\u59cb\u7ec8\u4f24\u5b83\u4e0d\u5230\u3002\u8fd9\u822c\u7a7f\u4e86\u6570\u6b21\uff0c\u5927\u86c7\u4f3c\u4e4e\u660e\u767d\u4e86\u654c\u4eba\u7684\u62db\u6570\uff0c\u4f38\u53e3\u5411\u5de6\u865a\u54ac\u4e00\u53e3\uff0c\u5f85\u5c0f\u86c7\u8dc3\u8d77\uff0c\u5ffd\u7136\u95f4\u8eab\u5b50\u66b4\u957f\uff0c\u5982\u7bad\u79bb\u5f26\uff0c\u4e00\u53e3\u5411\u5c0f\u86c7\u5c3e\u4e0a\u54ac\u53bb\u3002\u90a3\u5c0f\u86c7\u5728\u7a7a\u4e2d\u7adf\u4f1a\u6253\u8f6c\uff0c\u5f2f\u8170\u4e00\u649e\uff0c\u767b\u65f6\u4e00\u5934\u628a\u5927\u86c7\u7684\u5de6\u773c\u649e\u778e\u3002 \u3000\u3000\u8881\u627f\u5fd7\u770b\u5f97\u5fc3\u6447\u795e\u9a70\uff0c\u771f\u89c9\u662f\u751f\u5e73\u672a\u89c1\u4e4b\u5947\uff0c\u60c5\u4e0d\u81ea\u7981\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u597d\u5440\uff01\u201d \u3000\u3000\u5927\u86c7\u53d7\u521b\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u94bb\u5165\u4e86\u6d1e\u4e2d\u3002\u5b83\u51fa\u6765\u5f97\u5feb\uff0c\u56de\u5f97\u66f4\u5feb\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u4e08\u4f59\u7684\u8eab\u5b50\u6ca1\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u5c0f\u86c7\u5bf9\u7740\u6d1e\u53e3\u53c8\u4e0d\u4f4f\u5618\u6c14\u3002 \u3000\u3000\u9752\u9752\u7a81\u7136\u611f\u5230\u4e00\u9635\u5934\u6655\uff0c\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u62c9\u4f4f\u8881\u627f\u5fd7\u624b\u81c2\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u77e5\u5979\u8d2a\u770b\u86c7\u6597\uff0c\u7ad9\u5f97\u592a\u8fd1\uff0c\u5927\u86c7\u55b7\u51fa\u6765\u7684\u7ea2\u96fe\u662f\u5267\u6bd2\u4e4b\u7269\uff0c\u5f25\u6563\u5f00\u6765\uff0c\u4ee5\u81f4\u4e2d\u4e86\u86c7\u6bd2\u3002\u60f3\u8d77\u80e1\u6842\u5357\u6240\u8d60\u7684\u6731\u775b\u51b0\u87fe\u662f\u89e3\u6bd2\u7075\u7269\uff0c\u5e78\u597d\u5e26\u5728\u8eab\u8fb9\uff0c\u5fd9\u6478\u51fa\u6765\u653e\u5728\u5979\u53e3\u8fb9\u3002\u9752\u9752\u5bf9\u7740\u51b0\u87fe\u5438\u4e86\u51e0\u53e3\u6c14\uff0c\u89c9\u5f97\u4e00\u9635\u6e05\u51c9\uff0c\u6c81\u5165\u5fc3\u813e\uff0c\u5934\u6655\u987f\u6b62\u3002\u90a3\u4e5e\u4e10\u671b\u89c1\u4e86\u6731\u775b\u51b0\u87fe\uff0c\u4e0d\u7728\u773c\u7684\u51dd\u89c6\uff0c\u6ee1\u8138\u8273\u7fa1\u4e4b\u8272\u3002\u8881\u627f\u5fd7\u63a5\u8fc7\u51b0\u87fe\uff0c\u653e\u5165\u56ca\u4e2d\uff0c\u62c9\u9752\u9752\u9000\u5f00\u4e86\u6570\u6b65\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u8fd9\u6349\u86c7\u5316\u5b50\u5012\u6709\u773c\u529b\uff0c\u77e5\u9053\u8fd9\u662f\u73cd\u7269\uff0c\u4f60\u5929\u5927\u4e0e\u6bd2\u7269\u4e3a\u4f0d\uff0c\u8fd9\u6731\u775b\u51b0\u87fe\u5012\u662f\u4ef6\u9632\u8eab\u81f3\u5b9d\u5462\u3002\u201d \u3000\u3000\u53ea\u89c1\u86c7\u6d1e\u4e2d\u6e10\u6e10\u5192\u51fa\u7ea2\u96fe\uff0c\u60f3\u662f\u90a3\u5927\u86c7\u62b5\u53d7\u4e0d\u4f4f\u5c0f\u86c7\u5618\u6c14\uff0c\u53c8\u8981\u51fa\u6597\uff0c\u679c\u7136\u7ea2\u96fe\u6e10\u6d53\uff0c\u5927\u86c7\u53c8\u55e4\u7684\u4e00\u58f0\u94bb\u4e86\u51fa\u6765\u3002\u8fd9\u65f6\u5927\u86c7\u5c11\u4e86\u4e00\u53ea\u773c\u775b\uff0c\u7075\u6d3b\u5927\u51cf\uff0c\u4e0d\u591a\u65f6\u53f3\u773c\u53c8\u88ab\u649e\u778e\u3002\u5927\u86c7\u5bf9\u51c6\u6d1e\u53e3\u731b\u7a9c\uff0c\u54ea\u77e5\u5c0f\u86c7\u6b63\u5b88\u5728\u6d1e\u53e3\u3002\u4e24\u86c7\u76f8\u5bf9\uff0c\u5927\u86c7\u4e00\u53e3\u628a\u5c0f\u86c7\u541e\u8fdb\u4e86\u809a\u91cc\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u8881\u627f\u5fd7\u548c\u9752\u9752\u90fd\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u773c\u89c1\u5c0f\u86c7\u5df2\u7ecf\u5927\u80dc\uff0c\u600e\u4e48\u5ffd\u7136\u53cd\u88ab\u654c\u4eba\u541e\u53bb\uff1f\u53ea\u89c1\u5927\u86c7\u7ffb\u7ffb\u6eda\u6eda\uff0c\u663e\u5f97\u5341\u5206\u75db\u695a\uff0c\u7a81\u7136\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5c0f\u86c7\u54ac\u7834\u5927\u86c7\u809a\u5b50\uff0c\u94bb\u4e86\u51fa\u6765\u3002\u9752\u9752\u53f9\u9053\uff1a\u201c\u5509\uff0c\u8fd9\u5c0f\u5bb6\u4f19\u771f\u662f\u53c8\u51f6\u53c8\u72e1\u733e\u3002\u201d\u5927\u86c7\u4ecd\u662f\u7ffb\u817e\u4e0d\u5df2\uff0c\u826f\u4e45\u65b9\u6b7b\u3002\u90a3\u5c0f\u86c7\u6602\u8d77\u8eab\u5b50\uff0c\u7b14\u76f4\u7ad6\u8d77\uff0c\u53ea\u6709\u5c3e\u5df4\u77ed\u77ed\u7684\u4e00\u622a\u7740\u5730\uff0c\u4f3c\u4e4e\u8000\u6b66\u626c\u5a01\uff0c\u81ea\u9e23\u5f97\u610f\uff0c\u7ed5\u7740\u5927\u86c7\u5c38\u8eab\u6e38\u884c\u4e00\u5468\u540e\uff0c\u873f\u8712\u5411\u5916\uff0c\u90a3\u4e5e\u4e10\u795e\u8272\u767b\u65f6\u4e25\u91cd\u3002\u5c0f\u86c7\u6e38\u5230\u9ec4\u5708\u4e4b\u65c1\uff0c\u7a81\u7136\u7ffb\u4e86\u4e2a\u7b4b\u6597\uff0c\u9000\u8fdb\u5708\u5fc3\u3002\u9752\u9752\u95ee\u9053\uff1a\u201c\u8fd9\u4e9b\u9ec4\u8272\u7684\u4e1c\u897f\u662f\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u60f3\u662f\u96c4\u9ec4\u3001\u786b\u78fa\u4e4b\u7c7b\u514b\u5236\u86c7\u866b\u7684\u836f\u7269\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u8fd9\u6761\u5c0f\u86c7\u5f88\u6709\u8da3\uff0c\u6211\u5e2e\u86c7\u513f\uff0c\u76fc\u671b\u8fd9\u5316\u5b50\u6349\u5b83\u4e0d\u5230\u3002\u201d \u3000\u3000\u5979\u4e5f\u65e9\u60f3\u5230\u4e86\u7236\u4eb2\u7684\u5916\u53f7\uff0c\u5148\u524d\u90a3\u4e5e\u4e10\u795e\u6001\u65e0\u793c\uff0c\u5979\u5012\u76fc\u671b\u4ed6\u7ed9\u5c0f\u86c7\u649e\u778e\u4e00\u53ea\u773c\u775b\u3002 \u3000\u3000\u53ea\u89c1\u5c0f\u86c7\u75be\u515c\u5708\u5b50\uff0c\u5ffd\u7136\u8eab\u5b50\u4e00\u6602\uff0c\u5c3e\u90e8\u4f7f\u529b\uff0c\u8dc3\u4e86\u8d77\u6765\uff0c\u4ece\u7a7a\u4e2d\u7a7f\u8fc7\u4e86\u9ec4\u7ebf\uff0c\u843d\u5728\u7b2c\u4e8c\u9053\u5708\u5185\u3002\u4e5e\u4e10\u795e\u8272\u66f4\u89c1\u7d27\u5f20\uff0c\u5c0f\u86c7\u53c8\u662f\u6025\u901f\u6e38\u8d70\uff0c\u4e00\u5f39\u4e4b\u4e0b\uff0c\u53c8\u8dc3\u8fc7\u4e86\u4e00\u5c42\u5708\u5b50\u3002 \u3000\u3000\u4e5e\u4e10\u53e3\u4e2d\u5583\u5583\u81ea\u8bed\uff0c\u53d6\u51fa\u4e00\u628a\u836f\u7269\uff0c\u56bc\u70c2\u4e86\u6d82\u5728\u624b\u4e0a\u81c2\u4e0a\u3002\u5c0f\u86c7\u5728\u5708\u4e2d\u6e38\u8d70\uff0c\u4e5e\u4e10\u8ddf\u7740\u7ed5\u5708\u75be\u884c\u3002\u9752\u9752\u5657\u55e4\u4e00\u58f0\uff0c\u7b11\u4e86\u51fa\u6765\uff0c\u4f46\u4e0d\u4e45\u89c1\u4e5e\u4e10\u5168\u8eab\u6dcc\u6c57\uff0c\u6c57\u6c34\u4e00\u6ef4\u4e00\u6ef4\u843d\u5728\u96ea\u5730\u4e4b\u4e2d\uff0c\u4e0d\u89c9\u6536\u4e86\u7b11\u5bb9\uff0c\u5446\u5446\u6014\u4f4f\uff0c\u5fc3\u60f3\u8fd9\u5c0f\u5c0f\u4e00\u6761\u86c7\u513f\uff0c\u4f55\u82e6\u8ddf\u5b83\u8d39\u90a3\u4e48\u5927\u7684\u52b2\uff1f \u3000\u3000\u8881\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e5e\u4e10\u6b66\u529f\u5f88\u597d\uff0c\u770b\u6765\u8ddf\u6c99\u5929\u5e7f\u3001\u7a0b\u9752\u7af9\u4ed6\u4eec\u4e0d\u76f8\u4e0a\u4e0b\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u770b\u4ed6\u8eab\u6cd5\u624b\u52b2\uff0c\u4e5f\u4e0d\u89c1\u6709\u751a\u4e48\u7279\u522b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u77a7\u4ed6\u80f8\u8179\u4e0d\u52a8\uff0c\u5c4f\u4f4f\u547c\u5438\uff0c\u7adf\u652f\u6301\u4e86\u8fd9\u4e48\u4e45\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4e3a\u751a\u4e48\u4e0d\u547c\u5438\uff1f\u554a\uff0c\u6211\u77e5\u9053\u5566\u3002\u4ed6\u6015\u86c7\u7684\u6bd2\u6c14\uff0c\u4e0d\u6562\u5598\u6c14\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4e00\u4eba\u4e00\u86c7\u90fd\u8d8a\u8d70\u8d8a\u5feb\uff0c\u5c0f\u86c7\u7a81\u7136\u8dc3\u8d77\u5411\u5708\u5916\u7a9c\u51fa\uff0c\u4e5e\u4e10\u521a\u5de7\u8d76\u4e0a\uff0c\u8fce\u5934\u4e00\u53e3\u6c14\u5439\u4e86\u8fc7\u53bb\u3002\u5c0f\u86c7\u556a\u7684\u4e00\u58f0\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u7ee7\u7eed\u6e38\u8d70\u3002\u5982\u6b64\u7a9c\u4e86\u4e09\u6b21\uff0c\u90fd\u88ab\u4e5e\u4e10\u5439\u56de\u3002\u90a3\u5c0f\u86c7\u5ffd\u7136\u4e0d\u4f4f\u6539\u53d8\u65b9\u5411\uff0c\u6709\u65f6\u5411\u5de6\uff0c\u6709\u65f6\u5411\u53f3\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u4e5e\u4e10\u4fbf\u8ddf\u5b83\u4e0d\u4e0a\u4e86\u3002\u90a3\u5c0f\u86c7\u4e1c\u8fb9\u4e00\u7a9c\uff0c\u897f\u8fb9\u4e00\u95ef\uff0c\u7ec8\u4e8e\u627e\u51fa\u7a7a\u9699\uff0c\u8dc3\u51fa\u5708\u5b50\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u4e0d\u7981\u5931\u58f0\u60ca\u547c\u3002\u9752\u9752\u8ddf\u7740\u62cd\u624b\u53eb\u597d\u3002 \u3000\u3000\u4e5e\u4e10\u89c1\u5c0f\u86c7\u8dc3\u51fa\u9ec4\u5708\uff0c\u7acb\u5373\u51dd\u7acb\u4e0d\u52a8\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u5c0f\u86c7\u5e76\u4e0d\u9003\u8d70\uff0c\u53cd\u800c\u6602\u9996\u5bf9\u7740\u4e5e\u4e10\uff0c\u84c4\u52bf\u8fdb\u653b\u3002\u8fd9\u4e00\u6765\u653b\u5b88\u6613\u52bf\uff0c\u4e5e\u4e10\u795e\u6001\u614c\u5f20\uff0c\u60f3\u9003\u4e0d\u80fd\uff0c\u60f3\u653b\u4e0d\u5f97\u3002\u8881\u627f\u5fd7\u624b\u4e2d\u6263\u4f4f\u4e09\u7c92\u94dc\u94b1\uff0c\u53ea\u5f85\u4e5e\u4e10\u9047\u9669\uff0c\u7acb\u5373\u6740\u86c7\u6551\u4eba\u3002\u5c0f\u86c7\u7a9c\u4e86\u6570\u6b21\uff0c\u90a3\u4e5e\u4e10\u90fd\u907f\u5f00\u4e86\uff0c\u4f46\u5df2\u663e\u5f97\u5341\u5206\u72fc\u72c8\u3002\u8881\u627f\u5fd7\u89c1\u4ed6\u5371\u6025\uff0c\u6b63\u60f3\u65bd\u653e\u6697\u5668\uff0c\u4e5e\u4e10\u5ffd\u7136\u6025\u4e2d\u751f\u667a\uff0c\u7b49\u5c0f\u86c7\u518d\u7a9c\u4e0a\u6765\u65f6\uff0c\u4f38\u51fa\u5de6\u624b\u5927\u62c7\u6307\u4e00\u6643\uff0c\u5c0f\u86c7\u5feb\u4f3c\u95ea\u7535\uff0c\u4e00\u53e3\u5df2\u54ac\u4f4f\u62c7\u6307\u3002\u4e5e\u4e10\u53f3\u624b\u98df\u4e2d\u4e24\u6307\u7a81\u7136\u4f38\u51fa\uff0c\u4e5f\u5df2\u94b3\u4f4f\u5c0f\u86c7\u7684\u5934\u9888\uff0c\u4e24\u6307\u7528\u529b\uff0c\u5c0f\u86c7\u53ea\u5f97\u677e\u53e3\u3002\u4ed6\u5fd9\u4ece\u7834\u5e03\u56ca\u91cc\u53d6\u51fa\u4e00\u4e2a\u94c1\u7ba1\uff0c\u628a\u5c0f\u86c7\u653e\u5165\uff0c\u7528\u6728\u585e\u585e\u7262\uff0c\u968f\u624b\u628a\u94c1\u7ba1\u5728\u5730\u4e0a\u4e00\u4e22\uff0c\u8f6c\u5934\u5bf9\u8881\u627f\u5fd7\u5389\u58f0\u9053\uff1a\u201c\u5feb\u62ff\u51b0\u87fe\u6765\u6551\u547d\u3002\u201d \u3000\u3000\u9752\u9752\u89c1\u5c0f\u86c7\u7ec8\u4e8e\u88ab\u64d2\uff0c\u5df2\u662f\u8001\u5927\u4e0d\u5feb\uff0c\u542c\u4ed6\u8bf4\u8bdd\u5982\u6b64\u65e0\u793c\uff0c\u66f4\u662f\u6709\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u504f\u4e0d\u7ed9\uff01\u201d\u8881\u627f\u5fd7\u89c1\u4ed6\u4e00\u8eab\u6b66\u529f\uff0c\u5fc3\u4e2d\u7231\u60dc\uff0c\u53c8\u89c1\u4ed6\u5de6\u638c\u5df2\u6210\u9ed1\u8272\uff0c\u80bf\u5f97\u5927\u4e86\u51e0\u4e4e\u4e00\u500d\uff0c\u800c\u9ed1\u8272\u8fd8\u662f\u5411\u4e0a\u8513\u5ef6\uff0c\u8fd9\u5c0f\u86c7\u7adf\u5177\u5982\u6b64\u5267\u6bd2\uff0c\u4e0d\u7981\u5fc3\u60ca\uff0c\u4e8e\u662f\u53d6\u51fa\u6731\u775b\u51b0\u87fe\uff0c\u9012\u7ed9\u4e86\u4ed6\u3002 \u3000\u3000\u4e5e\u4e10\u5927\u559c\uff0c\u5fd9\u628a\u51b0\u87fe\u4e4b\u53e3\u5bf9\u51c6\u5de6\u624b\u62c7\u6307\uff0c\u4e0d\u5230\u7247\u523b\uff0c\u4f24\u53e3\u4e2d\u7684\u9ed1\u8840\u6c69\u6c69\u6d41\u4e0b\uff0c\u90fd\u6ef4\u5728\u96ea\u4e0a\uff0c\u6709\u5982\u6cfc\u58a8\u4e00\u822c\u3002\u638c\u4e0a\u9ed1\u6c14\u6e10\u9000\uff0c\u80bf\u80c0\u5df2\u6d88\uff0c\u518d\u8fc7\u4e00\u9635\uff0c\u9ed1\u8840\u53d8\u6210\u7ea2\u8840\u3002\u4e5e\u4e10\u54c8\u54c8\u5927\u7b11\uff0c\u5728\u88e4\u4e0a\u6495\u5757\u7834\u5e03\u624e\u4f4f\u4f24\u53e3\uff0c\u628a\u51b0\u87fe\u653e\u5165\u4e86\u81ea\u5df1\u5e03\u56ca\u3002 \u3000\u3000\u9752\u9752\u4f38\u51fa\u624b\u9053\uff1a\u201c\u51b0\u87fe\u8fd8\u6765\u3002\u201d\u4e5e\u4e10\u53cc\u7709\u7ad6\u8d77\uff0c\u6ee1\u8138\u51f6\u76f8\uff0c\u559d\u9053\uff1a\u201c\u751a\u4e48\u51b0\u87fe\uff1f\u201d\u9752\u9752\u5411\u4ed6\u8eab\u540e\u4e00\u6307\uff0c\u60ca\u53eb\u8d77\u6765\uff1a\u201c\u554a\uff0c\u90a3\u8fb9\u53c8\u6709\u4e00\u6761\u5c0f\u91d1\u86c7\uff01\u201d\u4e5e\u4e10\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u5934\u53bb\u770b\u3002\u9752\u9752\u4fef\u8eab\u62fe\u8d77\u5730\u4e0b\u94c1\u7ba1\uff0c\u5bf9\u51c6\u4e5e\u4e10\u7684\u80cc\u5fc3\uff0c\u559d\u9053\uff1a\u201c\u6211\u62d4\u585e\u5b50\u5566\u3002\u201d \u3000\u3000\u4e5e\u4e10\u77e5\u9053\u4e2d\u8ba1\uff0c\u8fd9\u585e\u5b50\u4e00\u62d4\u5f00\uff0c\u5c0f\u86c7\u5fc5\u5b9a\u731b\u7a9c\u800c\u51fa\uff0c\u54ac\u4ed6\u80cc\u5fc3\uff0c\u81ea\u5df1\u4e0a\u8eab\u8d64\u88f8\uff0c\u5982\u88ab\u54ac\u4e2d\u8981\u5bb3\uff0c\u7eb5\u4f7f\u8eab\u6709\u51b0\u87fe\uff0c\u4e5f\u672a\u5fc5\u6551\u6cbb\u5f97\u4e86\uff0c\u53ea\u5f97\u54c8\u54c8\u5927\u7b11\uff0c\u6478\u51fa\u51b0\u87fe\u6765\u8fd8\u7ed9\u8881\u627f\u5fd7\uff0c\u7b11\u9053\uff1a\u201c\u6211\u662f\u8ddf\u4f60\u4eec\u5f00\u73a9\u7b11\u7684\uff0c\u8fd9\u5c0f\u59d1\u5a18\u771f\u806a\u660e\u3002\u201d \u3000\u3000\u9752\u9752\u5f85\u8881\u627f\u5fd7\u63a5\u8fc7\u51b0\u87fe\uff0c\u628a\u5c0f\u94c1\u7ba1\u8fd8\u63b7\u5730\u4e0b\u3002\u8881\u627f\u5fd7\u672c\u6765\u9887\u60f3\u548c\u90a3\u4e5e\u4e10\u7ed3\u4ea4\uff0c\u7136\u89c1\u4ed6\u975e\u4f46\u4e0d\u8c22\u6551\u547d\u4e4b\u6069\uff0c\u53cd\u800c\u89ca\u89ce\u81ea\u5df1\u81f3\u5b9d\uff0c\u4eba\u54c1\u5341\u5206\u5351\u4e0b\uff0c\u62f1\u624b\u8bf4\u4e86\u58f0\uff1a\u201c\u540e\u4f1a\u6709\u671f\u3002\u201d\u5c31\u548c\u9752\u9752\u643a\u624b\u8d70\u4e86\u3002\u90a3\u4e5e\u4e10\u76ee\u9732\u51f6\u5149\uff0c\u559d\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u4e24\u4e2a\u6162\u8d70\uff01\u201d \u3000\u3000\u9752\u9752\u6012\u9053\uff1a\u201c\u5e72\u751a\u4e48\uff1f\u201d\u4e5e\u4e10\u9053\uff1a\u201c\u628a\u51b0\u87fe\u7559\u4e0b\uff0c\u5c31\u653e\u4f60\u4eec\u8d70\u8def\u3002 \u3000\u3000\u4f60\u4eec\u4e24\u4e2a\u5c0f\u5bb6\u4f19\u60f3\u4e0d\u60f3\u6d3b\u547d\uff1f\u201d\u9752\u9752\u89c1\u4ed6\u5982\u6b64\u86ee\u4e0d\u8bb2\u7406\uff0c\u6b63\u8981\u53cd\u5507\u76f8\u8ba5\uff0c\u8881\u627f\u5fd7\u62a2\u7740\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8c01\uff1f\u201d\u90a3\u4e5e\u4e10\u76ee\u5149\u70af\u70af\uff0c\u53cc\u624b\u4e00\u4f38\u4e00\u7f29\uff0c\u4f5c\u52bf\u4fbf\u8981\u6251\u6765\u4f24\u4eba\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u6076\u4e10\u81ea\u8ba8\u82e6\u5403\u3002\u201d \u3000\u3000\u90a3\u4e5e\u4e10\u6b63\u8981\u51fa\u51fb\uff0c\u7a81\u542c\u8fdc\u5904\u5175\u5203\u53ee\u5f53\u76f8\u4ea4\uff0c\u51e0\u4e2a\u4eba\u547c\u65a5\u5954\u9010\uff0c\u8e0f\u96ea\u800c\u6765\u3002\u524d\u9762\u5954\u9003\u7684\u662f\u4e24\u4e2a\u7ea2\u8863\u7ae5\u5b50\uff0c\u80a9\u5934\u90fd\u8d1f\u7740\u4e00\u4e2a\u5927\u5305\u88b1\uff0c\u8fb9\u9003\u8fb9\u6253\uff0c\u540e\u9762\u8ffd\u8d76\u7684\u662f\u56db\u4e94\u540d\u516c\u5dee\uff0c\u4e3a\u9996\u4e00\u4eba\uff0c\u8881\u627f\u5fd7\u548c\u9752\u9752\u8ba4\u5f97\u6b63\u662f\u72ec\u773c\u795e\u9f99\u5355\u94c1\u751f\u3002\u4ed6\u624b\u6301\u4e00\u6746\u94c1\u5c3a\uff0c\u6572\u6253\u622a\u6233\uff0c\u5c45\u7136\u90fd\u662f\u4e0a\u4e58\u7684\u70b9\u7a74\u529f\u592b\u3002\u8fd9\u4ef6\u516c\u95e8\u4e2d\u5dee\u5f79\u6240\u7528\u7684\u5bfb\u5e38\u6b66\u5668\uff0c\u5728\u9ad8\u624b\u624b\u91cc\u7adf\u4e5f\u6781\u5177\u5a01\u529b\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u7ae5\u5b50\u62db\u67b6\u4e0d\u4f4f\uff0c\u76f4\u5411\u4e5e\u4e10\u5954\u6765\uff0c\u53eb\u9053\uff1a\u201c\u9f50\u5e08\u53d4\uff0c\u9f50\u5e08\u53d4\uff01\u201d\u4e00\u9762\u628a\u80a9\u5934\u7684\u5305\u88b1\u629b\u4e86\u8fc7\u6765\u3002\u90a3\u4e5e\u4e10\u53cc\u624b\u5404\u63a5\u4e00\u5305\uff0c\u653e\u5728\u5730\u4e0b\u3002\u4ed6\u89c1\u4e8c\u7ae5\u629b\u53bb\u91cd\u7269\u540e\u8eab\u624b\u767b\u65f6\u4fbf\u6377\uff0c\u8fd4\u8eab\u53cc\u6218\u5355\u94c1\u751f\uff0c\u6253\u5f97\u96be\u89e3\u96be\u5206\uff0c\u5176\u4f59\u51e0\u540d\u516c\u5dee\u6b66\u529f\u90fd\u662f\u5e73\u5e73\uff0c\u5fc3\u4e2d\u8bb0\u7740\u51b0\u87fe\u81f3\u5b9d\uff0c\u8f6c\u8eab\u6251\u5411\u8881\u627f\u5fd7\uff0c\u4f38\u624b\u4fbf\u53bb\u6293\u4ed6\u80a9\u5934\u3002\u8881\u627f\u5fd7\u4e0d\u613f\u663e\u793a\u6b66\u529f\uff0c\u56de\u5934\u5c31\u8dd1\uff0c\u8eb2\u5230\u4e86\u5355\u94c1\u751f\u8eab\u540e\u3002 \u3000\u3000\u5355\u94c1\u751f\u521d\u89c1\u8881\u627f\u5fd7\u3001\u9752\u9752\u548c\u90a3\u4e5e\u4e10\u7ad9\u5728\u4e00\u8d77\uff0c\u65e9\u5c31\u6697\u81ea\u5fc3\u60ca\uff0c\u5ffd\u89c1\u4e5e\u4e10\u4e0e\u8881\u627f\u5fd7\u4e3a\u654c\uff0c\u767b\u65f6\u7cbe\u795e\u5927\u632f\uff0c\u5de6\u638c\u5939\u7740\u94c1\u5c3a\uff0c\u8fde\u8fde\u8fdb\u88ad\uff0c\u53ea\u542c\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u540d\u7ae5\u5b50\u201c\u80a9\u8d1e\u7a74\u201d\u88ab\u94c1\u5c3a\u70b9\u4e2d\u3002\u53e6\u4e00\u540d\u7ae5\u5b50\u4e00\u60ca\uff0c\u5355\u94c1\u751f\u4e58\u52bf\u4e00\u811a\uff0c\u5c06\u4ed6\u8e22\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u6597\u7136\u7ad9\u4f4f\uff0c\u7c97\u58f0\u7c97\u6c14\u7684\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u5355\u8001\u5e08\uff01\u201d\u5355\u94c1\u751f\u9053\uff1a\u201c\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\uff1f\u5728\u4e0b\u6c42\u4f60\u8d4f\u6211\u4eec\u4e00\u53e3\u996d\u5403\u3002\u201d\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u81ed\u53eb\u5316\u5b50\uff0c\u6709\u751a\u4e48\u540d\u5b57\uff1f\u201d\u4fef\u8eab\u89e3\u5f00\u7ea2\u8863\u7ae5\u5b50\u88ab\u70b9\u7684\u7a74\u9053\u3002\u8fd9\u65f6\u4e24\u540d\u516c\u5dee\u5df2\u628a\u5730\u4e0b\u7684\u5305\u88f9\u6361\u8d77\uff0c\u90a3\u4e5e\u4e10\u5ffd\u7136\u547c\u54e8\u4e00\u58f0\uff0c\u4e24\u540d\u7ae5\u5b50\u62a2\u5c06\u4e0a\u53bb\uff0c\u4e00\u638c\u4e00\u4e2a\uff0c\u6253\u5012\u4e24\u540d\u516c\u5dee\uff0c\u62a2\u4e86\u5305\u88b1\u4fbf\u8d70\u3002 \u3000\u3000\u5355\u94c1\u751f\u63d0\u8d77\u94c1\u5c3a\uff0c\u53d1\u8db3\u8ffd\u53bb\uff0c\u559d\u9053\uff1a\u201c\u5927\u80c6\u5c0f\u8d3c\uff0c\u8fd8\u4e0d\u7ed9\u6211\u653e\u4e0b\u3002\u201d\u4e24\u540d\u7ae5\u5b50\u6beb\u4e0d\u7406\u4f1a\uff0c\u53ea\u662f\u72c2\u5954\u3002\u5355\u94c1\u751f\u51e0\u4e2a\u8d77\u843d\uff0c\u4e3e\u94c1\u5c3a\u5411\u540e\u9762\u90a3\u7ae5\u5b50\u80cc\u5fc3\u70b9\u53bb\uff0c\u7a81\u7136\u98ce\u58f0\u54cd\u5904\uff0c\u90a3\u4e5e\u4e10\u659c\u523a\u91cc\u8dc3\u5230\uff0c\u5939\u624b\u5c31\u6765\u593a\u4ed6\u94c1\u5c3a\u3002\u5355\u94c1\u751f\u867d\u53ea\u72ec\u773c\uff0c\u6b66\u529f\u5374\u7740\u5b9e\u4e86\u5f97\uff0c\u94c1\u5c3a\u5012\u7ad6\uff0c\u5c3e\u7aef\u5411\u654c\u4eba\u8155\u4e0a\u7838\u53bb\uff0c\u90a3\u4e5e\u4e10\u624b\u8155\u4e00\u6c89\uff0c\u5de6\u638c\u53cd\u51fb\u5bf9\u65b9\u80cc\u5fc3\u3002\u5355\u94c1\u751f\u5de6\u81c2\u6a2a\u683c\uff0c\u60f3\u8bd5\u8bd5\u654c\u4eba\u7684\u529f\u529b\u3002\u90a3\u4e5e\u4e10\u731d\u7136\u6536\u62db\uff0c\u53cd\u8eab\u4e00\u4e2a\u7b4b\u6597\uff0c\u8dc3\u51fa\u4e08\u4f59\uff0c\u968f\u7740\u4e24\u540d\u7ea2\u8863\u7ae5\u5b50\u53bb\u4e86\u3002 \u3000\u3000\u5355\u94c1\u751f\u89c1\u4ed6\u8eab\u624b\u5982\u6b64\u654f\u6377\uff0c\u4e0d\u89c9\u5403\u60ca\uff0c\u5fc3\u60f3\u5df1\u65b9\u867d\u7136\u4eba\u4f17\uff0c\u4f46\u9664\u81ea\u5df1\u5916\u90fd\u662f\u5eb8\u624b\uff0c\u5b64\u8eab\u8ffd\u53bb\uff0c\u52bf\u6240\u4e0d\u654c\uff0c\u53ea\u5f97\u4f4f\u8db3\u4e0d\u8ffd\uff0c\u5411\u8881\u627f\u5fd7\u957f\u63d6\u5230\u5730\uff0c\u8fde\u79f0\uff1a\u201c\u5c0f\u4eba\u8be5\u6b7b\uff0c\u5c0f\u4eba\u8be5\u6b7b\uff01\u201d\u8881\u627f\u5fd7\u6115\u7136\u4e0d\u89e3\uff0c\u8bf4\u9053\uff1a\u201c\u5355\u5934\u513f\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u90a3\u4e5e\u4e10\u662f\u751a\u4e48\u95e8\u9053\uff1f\u201d \u3000\u3000\u5355\u94c1\u751f\u9053\uff1a\u201c\u8bf7\u4e24\u4f4d\u5230\u4ead\u4e2d\u5bbd\u5750\uff0c\u5c0f\u4eba\u6162\u6162\u7980\u544a\u3002\u201d\u4e09\u4eba\u5728\u4ead\u4e2d\u5750\u5b9a\uff0c\u5355\u94c1\u751f\u628a\u8fd9\u4e8b\u7684\u524d\u56e0\u540e\u679c\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u539f\u6765\u4e0a\u4e2a\u6708\u6237\u90e8\u5927\u5e93\u63a5\u8fde\u4e09\u6b21\u5931\u76d7\uff0c\u88ab\u52ab\u53bb\u6570\u5343\u4e24\u5e93\u94f6\u3002 \u3000\u3000\u5929\u5b50\u811a\u5e95\u4e0b\u5e72\u51fa\u8fd9\u7b49\u5927\u4e8b\u6765\uff0c\u7acb\u65f6\u4e5d\u57ce\u9707\u52a8\u3002\u7687\u5e1d\u8fc7\u4e0d\u4e24\u5929\u5c31\u77e5\u9053\u4e86\uff0c\u628a\u6237\u90e8\u5085\u5c1a\u4e66\u548c\u4e94\u57ce\u5175\u9a6c\u5468\u6307\u6325\u4f7f\u72e0\u72e0\u8bad\u65a5\u4e86\u4e00\u987f\uff0c\u8c15\u793a\uff1a\u4e00\u4e2a\u6708\u5185\u82e5\u4e0d\u7834\u6848\uff0c\u6237\u90e8\u548c\u5175\u9a6c\u6307\u6325\u53f8\u8859\u95e8\u5927\u5c0f\u5b98\u5458\u4e00\u5f8b\u9769\u804c\u4e25\u529e\u3002 \u3000\u3000\u5317\u4eac\u7684\u4f17\u516c\u5dee\u7ed9\u4e0a\u53f8\u8ffd\u903c\u5f97\u53eb\u82e6\u8fde\u5929\uff0c\u8fde\u516c\u5dee\u7684\u5bb6\u5c5e\u4e5f\u90fd\u6536\u4e86\u76d1\u3002\u4e0d\u6599\u8859\u95e8\u4e2d\u8d8a\u662f\u8ffd\u67e5\u5f97\u7d27\uff0c\u5e93\u94f6\u5374\u63a5\u8fde\u4e00\u6b21\u53c8\u4e00\u6b21\u7684\u5931\u76d7\u3002\u4f17\u516c\u5dee\u65e0\u6cd5\u53ef\u65bd\uff0c\u53ea\u5f97\u4e0a\u95e8\u78d5\u5934\uff0c\u82e6\u82e6\u54c0\u6c42\uff0c\u628a\u4e45\u5df2\u9000\u4f11\u7684\u8001\u516c\u5dee\u72ec\u773c\u795e\u9f99\u5355\u94c1\u751f\u8bf7\u4e86\u51fa\u6765\u3002\u5355\u94c1\u751f\u5728\u5927\u5e93\u524d\u540e\u5185\u5916\u4ed4\u7ec6\u67e5\u52d8\uff0c\u77e5\u9053\u76d7\u94f6\u5b50\u7684\u5fc5\u975e\u5bfb\u5e38\u76d7\u8d3c\uff0c\u800c\u662f\u6b66\u6797\u597d\u624b\uff0c\u4e00\u6253\u542c\uff0c\u77e5\u9053\u65b0\u8fd1\u6765\u4eac\u7684\u597d\u624b\u53ea\u6709\u8881\u627f\u5fd7\u7b49\u4e00\u6279\u4eba\u3002 \u3000\u3000\u9752\u9752\u542c\u5230\u8fd9\u91cc\uff0c\u5478\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u7591\u5fc3\u6211\u4eec\u4f5c\u8d3c\uff01\u201d \u3000\u3000\u5355\u94c1\u751f\u9053\uff1a\u201c\u5c0f\u4eba\u8be5\u6b7b\uff0c\u5c0f\u4eba\u5f53\u65f6\u786e\u662f\u8fd9\u4e48\u60f3\uff0c\u540e\u6765\u518d\u8be6\u52a0\u6253\u542c\uff0c\u624d\u77e5\u8881\u76f8\u516c\u5728\u5357\u4eac\u4e49\u6551\u94c1\u80cc\u91d1\u9ccc\u7126\u516c\u793c\uff0c\u5728\u5c71\u4e1c\u7ed3\u4ea4\u6c99\u5be8\u4e3b\u3001\u7a0b\u5e2e\u4e3b\uff0c\u6c5f\u6e56\u7fa4\u96c4\u63a8\u4e3a\u4e03\u7701\u76df\u4e3b\uff0c\u771f\u662f\u5927\u5927\u7684\u82f1\u96c4\u8c6a\u6770\u3002\u201d\u9752\u9752\u542c\u4ed6\u8fd9\u6837\u7684\u8d5e\u6367\u8881\u627f\u5fd7\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e0b\u751a\u559c\uff0c\u8138\u8272\u987f\u548c\u3002 \u3000\u3000\u5355\u94c1\u751f\u53c8\u9053\uff1a\u201c\u5c0f\u4eba\u5f53\u65f6\u5fc3\u60f3\uff0c\u4ee5\u8881\u76f8\u516c\u5982\u6b64\u82f1\u96c4\uff0c\u5982\u6b64\u8eab\u4efd\uff0c\u600e\u80fd\u6765\u76d7\u53d6\u5e93\u94f6\uff1f\u5c31\u7b97\u662f\u4ed6\u624b\u4e0b\u4eba\u5e72\u7684\uff0c\u4ed6\u8001\u4eba\u5bb6\u5f97\u77e5\u540e\u4e5f\u5fc5\u4e25\u4ee4\u7981\u6b62\u3002\u540e\u6765\u518d\u52a0\u4ee5\u7422\u78e8\uff0c\u662f\u4e86\uff0c\u662f\u8881\u76f8\u516c\u8981\u6211\u4eec\u597d\u770b\u6765\u7740\u3002\u8fd9\u4e48\u4e00\u4f4d\u5927\u82f1\u96c4\u6765\u5230\u4eac\u57ce\uff0c\u6211\u4eec\u7adf\u6ca1\u6765\u8fce\u63a5\uff0c\u5b9e\u5728\u662f\u96be\u602a\u8881\u76f8\u516c\u751f\u6c14\u3002\u54b3\uff0c\u8c01\u6559\u5c0f\u4eba\u778e\u4e86\u773c\u73e0\u5462\u3002\u201d\u9752\u9752\u5411\u4ed6\u90a3\u53ea\u767d\u591a\u9ed1\u5c11\u7684\u72ec\u773c\u671b\u4e86\u4e00\u671b\uff0c\u4e0d\u7531\u5f97\u5657\u54e7\u4e00\u7b11\u3002\u5355\u94c1\u751f\u7eed\u9053\uff1a\u201c\u56e0\u6b64\u6211\u4eec\u8fde\u5fd9\u8865\u8fc7\uff0c\u5929\u5929\u5230\u5e9c\u4e0a\u6765\u8bf7\u5b89\u8c22\u7f6a\u3002\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u8c01\u77e5\u9053\u4f60\u7684\u5fc3\u773c\u513f\u554a\uff01\u201d\u5355\u94c1\u751f\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4ef6\u4e8b\u53c8\u600e\u4e48\u80fd\u8bf4\uff1f\u6211\u4eec\u53ea\u76fc\u8881\u76f8\u516c\u606f\u6012\uff0c\u8d4f\u8fd8\u5e93\u94f6\uff0c\u6551\u6551\u4eac\u57ce\u91cc\u6570\u767e\u540d\u516c\u5dee\u7684\u5168\u5bb6\u8001\u5c0f\uff0c\u54ea\u77e5\u8881\u76f8\u516c\u9000\u56de\u6211\u4eec\u9001\u53bb\u7684\u4e1c\u897f\uff0c\u8fd8\u67e5\u77e5\u4e86\u5c0f\u4eba\u7684\u540d\u5b57\u548c\u532a\u53f7\uff0c\u5927\u6492\u540d\u5e16\uff0c\u628a\u5c0f\u4eba\u60e9\u6212\u4e86\u4e00\u756a\u3002\u201d \u3000\u3000\u9752\u9752\u53ea\u5f53\u6ca1\u542c\u89c1\uff0c\u4e1d\u6beb\u4e0d\u52a8\u58f0\u8272\u3002 \u3000\u3000\u5355\u94c1\u751f\u53c8\u9053\uff1a\u201c\u8fd9\u4e00\u6765\uff0c\u5927\u5bb6\u5c31\u72af\u4e86\u6101\u3002\u5c0f\u4eba\u4eca\u65e5\u57cb\u4f0f\u5728\u5e93\u91cc\uff0c\u53ea\u7b49\u8881\u76f8\u516c\u518d\u6d3e\u4eba\u6765\uff0c\u5c31\u8ddf\u4ed6\u62da\u547d\uff0c\u54ea\u77e5\u6765\u7684\u5374\u662f\u8fd9\u4e24\u4e2a\u7ea2\u8863\u7ae5\u5b50\u3002\u6211\u4eec\u8ffd\u8fd9\u4e24\u4e2a\u5c0f\u9b3c\u6765\u5230\u8fd9\u91cc\uff0c\u53c8\u9047\u89c1\u8fd9\u602a\u53eb\u5316\u3002\u8881\u76f8\u516c\uff0c\u603b\u5f97\u8bf7\u4f60\u6307\u70b9\u4e00\u6761\u660e\u8def\u3002\u201d\u8bf4\u7740\u8dea\u4e86\u4e0b\u53bb\uff0c\u8fde\u8fde\u78d5\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fd9\u5373\u6276\u8d77\uff0c\u5bfb\u601d\uff1a\u201c\u90a3\u4e5e\u4e10\u548c\u7ea2\u8863\u7ae5\u5b50\u867d\u7136\u4f3c\u4e4e\u4e0d\u6708\u8d5e\u662f\u5584\u7c7b\uff0c\u4f46\u4ed6\u4eec\u65e2\u4e0e\u5b98\u5e9c\u4e3a\u96be\uff0c\u6211\u53c8\u4f55\u5fc5\u76f8\u52a9\u8fd9\u7b49\u814c\u516c\u5dee\uff1f\u4f55\u51b5\u62a2\u4e86\u671d\u5ef7\u5e93\u94f6\uff0c\u90a3\u4e5f\u662f\u5e2e\u95ef\u738b\u7684\u5fd9\u3002\u201d\u5f53\u4e0b\u628a\u5982\u4f55\u89c1\u5230\u602a\u53eb\u5316\u3001\u5982\u4f55\u770b\u4ed6\u6349\u86c7\u3001\u90a3\u4e5e\u4e10\u5982\u4f55\u60f3\u62a2\u4ed6\u51b0\u87fe\u7684\u4e8b\u8bf4\u4e86\u3002 \u3000\u3000\u5355\u94c1\u751f\u6c42\u4ed6\u5e2e\u540c\u62ff\u8bbf\u3002\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u62ff\u8d43\u662f\u516c\u5dee\u8001\u54e5\u4eec\u5e72\u7684\u4e8b\u3002\u5144\u5f1f\u867d\u7136\u4e0d\u6210\u5668\uff0c\u8fd8\u4e0d\u81f4\u505a\u8fd9\u79cd\u4e8b\u3002\u201d\u5355\u94c1\u751f\u542c\u4ed6\u8bed\u6c14\uff0c\u4e0d\u6562\u518d\u8bf4\uff0c\u53ea\u5f97\u76f8\u63d6\u800c\u522b\uff0c\u548c\u4f17\u516c\u5dee\u600f\u600f\u7684\u8d70\u4e86\u3002 \u3000\u3000\u5f52\u9014\u4e4b\u4e2d\uff0c\u9752\u9752\u5927\u9a82\u90a3\u6076\u4e10\u65e0\u793c\uff0c\u8bf4\u4e0b\u6b21\u82e5\u518d\u649e\u89c1\uff0c\u5b9a\u8981\u53eb\u4ed6\u5403\u70b9\u82e6\u5934\u3002\u6b63\u8d70\u4e4b\u95f4\uff0c\u53ea\u89c1\u8fce\u9762\u8d70\u6765\u4e00\u6279\u9526\u8863\u536b\u8859\u95e8\u7684\u5175\u4e01\uff0c\u62bc\u7740\u4e00\u5927\u7fa4\u72af\u4eba\u3002\u7fa4\u72af\u6709\u7684\u662f\u6ee1\u5934\u767d\u53d1\u7684\u8001\u4eba\uff0c\u6709\u7684\u5374\u662f\u8fd8\u5728\u6000\u62b1\u7684\u5a74\u513f\uff0c\u90fd\u662f\u8001\u5f31\u5987\u5b7a\u3002\u4f17\u5175\u4e01\u5982\u72fc\u4f3c\u864e\uff0c\u5406\u559d\u65a5\u9a82\u3002\u4e00\u540d\u5c11\u5987\u6c42\u9053\uff1a\u201c\u603b\u7237\u4f60\u884c\u884c\u597d\uff0c\u5927\u5bb6\u90fd\u662f\u5403\u516c\u95e8\u996d\u7684\u3002\u6211\u4eec\u53c8\u6ca1\u72af\u751a\u4e48\u4e8b\uff0c\u53ea\u4e0d\u8fc7\u4eac\u57ce\u51fa\u4e86\u98de\u8d3c\uff0c\u7d2f\u5f97\u5927\u5bb6\u8fd9\u6837\u60e8\u3002\u201d\u4e00\u4e2a\u5175\u58eb\u5728\u5979\u8138\u86cb\u4e0a\u6478\u4e86\u4e00\u628a\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u98de\u8d3c\uff0c\u54b1\u4eec\u4f1a\u6709\u7f18\u4efd\u89c1\u9762\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u548c\u9752\u9752\u77a7\u5f97\u751a\u662f\u607c\u6012\uff0c\u77e5\u9053\u72af\u4eba\u90fd\u662f\u4eac\u57ce\u6355\u5feb\u7684\u5bb6\u5c5e\u3002\u516c\u5dee\u6355\u5feb\u6b8b\u5bb3\u826f\u6c11\uff0c\u4f5c\u5b7d\u591a\u7aef\uff0c\u53d7\u4e9b\u8ffd\u903c\uff0c\u4e5f\u51a4\u4e0d\u4e86\u4ed6\u4eec\uff0c\u4f46\u65e0\u8f9c\u5987\u5b7a\u6a2a\u906d\u7d2f\u5bb3\uff0c\u5fc3\u4e2d\u5374\u611f\u4e0d\u5fcd\u3002 \u3000\u3000\u53c8\u8d70\u4e00\u9635\uff0c\u5ffd\u89c1\u4e00\u7fa4\u6355\u5feb\u7528\u94c1\u94fe\u62d6\u4e86\u5341\u591a\u4eba\u5728\u8857\u4e0a\u7ecf\u8fc7\uff0c\u53e3\u91cc\u5927\u53eb\uff1a\u201c\u6349\u5230\u98de\u8d3c\u5566\uff0c\u6349\u5230\u98de\u8d3c\u5566\uff01\u201d\u8bb8\u591a\u767e\u59d3\u5728\u8857\u65c1\u77a7\u7740\uff0c\u4e2a\u4e2a\u6447\u5934\u53f9\u606f\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u6324\u8fd1\u53bb\u4e00\u770b\uff0c\u6240\u8c13\u98de\u8d3c\uff0c\u539f\u6765\u90fd\u662f\u4e9b\u84ec\u5934\u57a2\u9762\u7684\u7a77\u4eba\uff0c\u60f3\u662f\u6355\u5feb\u4e3a\u4e86\u585e\u8d23\uff0c\u80e1\u4e71\u6349\u6765\u9876\u66ff\uff0c\u4e0d\u7531\u5f97\u5927\u6012\u3002 \u3000\u3000\u56de\u5230\u5bd3\u6240\uff0c\u6d2a\u80dc\u6d77\u6b63\u5728\u5c4b\u5916\u63a2\u5934\u63a2\u8111\uff0c\u89c1\u4e86\u4e24\u4eba\uff0c\u5927\u559c\u9053\uff1a\u201c\u597d\u5566\uff0c\u56de\u6765\u5566\uff01\u201d\u8881\u627f\u5fd7\u5fd9\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u7a0b\u8001\u592b\u5b50\u7ed9\u4eba\u6253\u4f24\u4e86\uff0c\u4e13\u7b49\u76f8\u516c\u56de\u6765\u65bd\u6551\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u7a0b\u9752\u7af9\u6b66\u529f\u4e86\u5f97\uff0c\u600e\u4f1a\u7ed9\u4eba\u6253\u4f24\uff1f \u3000\u3000\u5fd9\u968f\u6d2a\u80dc\u6d77\u8d70\u5230\u7a0b\u9752\u7af9\u623f\u4e2d\uff0c\u53ea\u89c1\u4ed6\u8eba\u5728\u5e8a\u4e0a\uff0c\u8138\u4e0a\u7070\u6251\u6251\u7684\u4e00\u5c42\u9ed1\u6c14\u3002\u6c99\u5929\u5e7f\u3001\u80e1\u6842\u5357\u3001\u94c1\u7f57\u6c49\u7b49\u90fd\u5750\u5728\u5e8a\u8fb9\uff0c\u4e2a\u4e2a\u5fe7\u5f62\u4e8e\u8272\u3002\u4f17\u4eba\u89c1\u5230\u8881\u627f\u5fd7\uff0c\u6ee1\u8138\u6101\u5bb9\u4e4b\u4e2d\uff0c\u767b\u65f6\u900f\u51fa\u4e86\u559c\u8272\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u7a0b\u9752\u7af9\u53cc\u76ee\u7d27\u95ed\uff0c\u547c\u5438\u7ec6\u5fae\uff0c\u5fc3\u4e0b\u4e5f\u81ea\u60f6\u6025\uff0c\u5fd9\u95ee\uff1a\u201c\u7a0b\u8001\u592b\u5b50\u4f24\u5728\u54ea\u91cc\uff1f\u201d\u6c99\u5929\u5e7f\u628a\u7a0b\u9752\u7af9\u8f7b\u8f7b\u6276\u8d77\uff0c\u89e3\u5f00\u4e0a\u8863\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u89c1\u4ed6\u53f3\u8fb9\u6574\u4e2a\u80a9\u8180\u5df2\u5168\u6210\u9ed1\u8272\uff0c\u4fbf\u4f3c\u7528\u6d53\u58a8\u6d82\u8fc7\u4e00\u822c\uff0c\u9ed1\u6c14\u5411\u4e0a\u8513\u5ef6\uff0c\u76d6\u6ee1\u4e86\u6574\u5f20\u8138\u5b54\uff0c\u76f4\u5230\u53d1\u5fc3\uff0c\u5411\u4e0b\u5ef6\u5230\u8170\u95f4\u3002\u80a9\u5934\u9ed1\u8272\u6700\u6d53\u5904\u6709\u4e94\u4e2a\u722a\u75d5\u6df1\u5165\u8089\u91cc\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u751a\u4e48\u6bd2\u7269\u4f24\u7684\uff1f\u201d\u6c99\u5e7f\u5929\u9053\uff1a\u201c\u7a0b\u8001\u592b\u5b50\u52c9\u5f3a\u652f\u6301\u7740\u56de\u6765\uff0c\u5df2\u8bf4\u4e0d\u51fa\u8bdd\u4e86\u3002\u4e5f\u4e0d\u77e5\u662f\u4e2d\u4e86\u751a\u4e48\u6bd2\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e78\u597d\u6709\u6731\u775b\u51b0\u87fe\u5728\u6b64\u3002\u201d\u53d6\u51fa\u51b0\u87fe\uff0c\u5c06\u87fe\u5634\u5bf9\u51c6\u4f24\u53e3\u3002 \u3000\u3000\u4f38\u624b\u6309\u4e8e\u87fe\u80cc\uff0c\u6f5c\u8fd0\u5185\u529b\uff0c\u5438\u6536\u6bd2\u6c14\uff0c\u53ea\u89c1\u901a\u4f53\u96ea\u767d\u7684\u51b0\u87fe\u6e10\u6e10\u7531\u767d\u800c\u7070\u3001\u7531\u7070\u800c\u9ed1\u3002\u80e1\u6842\u5357\u9053\uff1a\u201c\u628a\u51b0\u87fe\u6d78\u5728\u70e7\u9152\u91cc\uff0c\u6bd2\u6c41\u5c31\u53ef\u6d78\u51fa\u3002\u201d\u9752\u9752\u5fd9\u53bb\u5012\u4e86\u4e00\u5927\u7897\u70e7\u9152\uff0c\u5c06\u51b0\u87fe\u653e\u5165\u9152\u4e2d\uff0c\u679c\u7136\u7f15\u7f15\u9ed1\u6c34\u4ece\u87fe\u53e3\u4e2d\u5410\u51fa\uff0c\u5f85\u5f97\u4e00\u7897\u70e7\u9152\u53d8\u5f97\u58a8\u6c41\u76f8\u4f3c\uff0c\u51b0\u87fe\u5374\u53c8\u7eaf\u51c0\u96ea\u767d\u3002\u8fd9\u822c\u5438\u6bd2\u6d78\u6bd2\uff0c\u76f4\u6d78\u4e86\u56db\u7897\u70e7\u9152\uff0c\u7a0b\u9752\u7af9\u8eab\u4e0a\u9ed1\u6c14\u65b9\u59cb\u892a\u5c3d\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u7761\u4e86\u4e00\u665a\uff0c\u8881\u627f\u5fd7\u6b21\u65e5\u53bb\u770b\u671b\u65f6\uff0c\u4ed6\u5df2\u80fd\u5750\u8d77\u8eab\u6765\u9053\u8c22\u3002\u8881\u627f\u5fd7\u6447\u624b\u547d\u4ed6\u4e0d\u8981\u8bf4\u8bdd\uff0c\u8bf7\u4e86\u4e00\u4f4d\u5317\u4eac\u57ce\u91cc\u7684\u540d\u533b\u6765\uff0c\u5f00\u51e0\u5e16\u89e3\u6bd2\u6e05\u8840\u7684\u836f\u5403\u4e86\u3002\u8c03\u517b\u5230\u7b2c\u4e09\u65e5\u4e0a\uff0c\u7a0b\u9752\u7af9\u5df2\u6709\u529b\u6c14\u8bf4\u8bdd\uff0c\u624d\u8be6\u8ff0\u4e2d\u6bd2\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u4ed6\u9053\uff1a\u201c\u90a3\u5929\u508d\u665a\uff0c\u6211\u4ece\u7981\u5bab\u95e8\u524d\u7ecf\u8fc7\uff0c\u5ffd\u542c\u4eba\u58f0\u55a7\u54d7\uff0c\u4f3c\u4e4e\u6709\u4eba\u5435\u9a82\u6253\u67b6\u3002\u8d70\u8fd1\u53bb\u770b\uff0c\u89c1\u5730\u4e0b\u6cfc\u4e86\u4e00\u5927\u6ee9\u8c46\u82b1\uff0c\u4e00\u4e2a\u5927\u6c49\u6293\u4f4f\u4e86\u4e2a\u5c0f\u4e2a\u5b50\uff0c\u4e0d\u4f4f\u53d1\u62f3\u6bb4\u6253\u3002\u4e00\u95ee\u65c1\u4eba\uff0c\u624d\u77e5\u90a3\u4e2a\u5c0f\u4e2a\u5b50\u662f\u5356\u8c46\u82b1\u7684\uff0c\u4e0d\u5c0f\u5fc3\u649e\u4e86\u90a3\u5927\u6c49\uff0c\u5f04\u810f\u4e86\u4ed6\u8863\u670d\u3002\u6211\u89c1\u90a3\u5c0f\u4e2a\u5b50\u53ef\u601c\uff0c\u4e0a\u524d\u76f8\u529d\u3002\u90a3\u5927\u6c49\u4e0d\u53ef\u7406\u55bb\uff0c\u5b9a\u8981\u5c0f\u4e2a\u5b50\u8d54\u94b1\u3002\u4e00\u95ee\u4e5f\u4e0d\u8fc7\u4e00\u4e24\u94f6\u5b50\uff0c\u6211\u5c31\u4f38\u624b\u5230\u888b\u91cc\u62ff\u94b1\uff0c\u5fc3\u60f3\u4ee3\u4ed6\u51fa\u4e86\u8fd9\u4e24\u94f6\u5b50\u7b97\u5566\u3002\u5509\uff0c\u54ea\u77e5\u4e00\u65f6\u597d\u4e8b\uff0c\u610f\u4e2d\u4e86\u5978\u4eba\u7684\u5708\u5957\u3002\u6211\u53f3\u624b\u521a\u4f38\u5165\u888b\uff0c\u90a3\u4e24\u4eba\u7a81\u7136\u4e00\u4eba\u4e00\u8fb9\uff0c\u62c9\u4f4f\u4e86\u6211\u7684\u624b\u81c2\u2026\u2026\u2026\u201d \u3000\u3000\u9752\u9752\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u201c\u554a\u201d\u7684\u4e00\u58f0\u3002\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6211\u7acb\u77e5\u4e0d\u5999\uff0c\u53cc\u8180\u4e00\u6c89\uff0c\u60f3\u7529\u8131\u4e8c\u4eba\u518d\u95ee\u60c5\u7531\uff0c\u54ea\u77e5\u53f3\u80a9\u6597\u7136\u95f4\u5947\u75db\u5165\u9aa8\u3002\u8fd9\u4e00\u4e0b\u6765\u5f97\u597d\u4e0d\u7a81\u5140\uff0c\u6211\u4e8b\u5148\u6beb\u6ca1\u9632\u5230\uff0c\u5f53\u4e0b\u594b\u529b\u53cd\u624b\u6263\u4f4f\u90a3\u5927\u6c49\u8109\u95e8\uff0c\u4e3e\u8d77\u4ed6\u8eab\u5b50\uff0c\u5f80\u5c0f\u4e2a\u5b50\u7684\u5934\u9876\u78b0\u53bb\uff0c\u540c\u65f6\u731b\u529b\u5f80\u524d\u76f4\u7a9c\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u624d\u770b\u6e05\u5728\u80cc\u540e\u5077\u88ad\u6211\u7684\u662f\u4e2a\u9ed1\u8863\u8001\u4e5e\u5a46\u3002\u8fd9\u4e5e\u5a46\u7684\u5f62\u76f8\u4e11\u6076\u53ef\u6015\u4e4b\u6781\uff0c\u6ee1\u8138\u90fd\u662f\u51f9\u51f9\u51f8\u51f8\u7684\u4f24\u75a4\uff0c\u53cc\u773c\u4e0a\u7ffb\uff0c\u8d6b\u8d6b\u51b7\u7b11\uff0c\u4e3e\u8d77\u5341\u53ea\u5c16\u5229\u7684\u722a\u5b50\uff0c\u53c8\u5411\u6211\u731b\u6251\u8fc7\u6765\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u8bf4\u5230\u8fd9\u91cc\uff0c\u5fc3\u6709\u4f59\u60b8\uff0c\u8138\u4e0a\u4e0d\u7981\u9732\u51fa\u60ca\u6050\u7684\u795e\u8272\u3002 \u3000\u3000\u9752\u9752\u5440\u7684\u4e00\u58f0\u60ca\u53eb\uff0c\u8fde\u6c99\u5929\u5e7f\u3001\u80e1\u6842\u5357\u7b49\u4e5f\u90fd\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u90a3\u65f6\u6211\u53c8\u60ca\u53c8\u6012\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u5f85\u8981\u53d1\u638c\u53cd\u51fb\uff0c\u4e0d\u6599\u53f3\u81c2\u7adf\u5df2\u52a8\u5f39\u4e0d\u5f97\uff0c\u5168\u7136\u4e0d\u542c\u4f7f\u5524\u3002\u8fd9\u8001\u4e5e\u5a46\u78d4\u78d4\u602a\u7b11\uff0c\u76f4\u903c\u8fc7\u6765\u3002\u6211\u6025\u4e2d\u751f\u667a\uff0c\u5de6\u624b\u63d0\u8d77\u4e00\u6876\u8c46\u82b1\uff0c\u5411\u5979\u8138\u4e0a\u6cfc\u4e86\u8fc7\u53bb\u3002\u5979\u53cc\u624b\u5728\u8138\u4e0a\u4e71\u62b9\uff0c\u6211\u4e58\u673a\u53d1\u4e86\u4e24\u652f\u9752\u7af9\u9556\uff0c\u6253\u4e2d\u4e86\u5979\u80f8\u53e3\uff0c\u603b\u4e5f\u6559\u5979\u53d7\u4e2a\u597d\u7684\u3002\u8fd9\u65f6\u6211\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u56de\u5934\u5f80\u5bb6\u91cc\u72c2\u5954\uff0c\u540e\u6765\u7684\u4e8b\u4fbf\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u8fd9\u8001\u4e5e\u5a46\u8ddf\u4f60\u6709\u6881\u5b50\u4e48\uff1f\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u89c1\u8fc7\u5979\u3002\u6211\u4eec\u9752\u7af9\u5e2e\u8ddf\u6c5f\u5357\u6c5f\u5317\u7684\u4e10\u5e2e\uff0c\u7d20\u6765\u6cb3\u6c34\u4e0d\u72af\u4e95\u6c34\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u96be\u9053\u5979\u770b\u9519\u4e86\u4eba\uff1f\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u7167\u8bf4\u4e0d\u4f1a\u3002 \u3000\u3000\u5979\u7b2c\u4e00\u6b21\u4f24\u6211\u4e4b\u540e\uff0c\u6211\u56de\u8fc7\u5934\u6765\uff0c\u5979\u5df2\u770b\u6e05\u695a\u4e86\u6211\u9762\u8c8c\uff0c\u4ecd\u8981\u518d\u4e0b\u6bd2\u624b\u3002\u201d\u80e1\u6842\u5357\u9053\uff1a\u201c\u5979\u624b\u722a\u4e0a\u4e0d\u77e5\u9053\u5582\u4e86\u751a\u4e48\u6bd2\u836f\uff0c\u6bd2\u6027\u8fd9\u822c\u5389\u5bb3\uff1f\u201d\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u5979\u624b\u722a\u4e0a\u5b9a\u662f\u6234\u4e86\u94a2\u5957\u5b50\uff0c\u5426\u5219\u8fd9\u822c\u5389\u5bb3\u7684\u6bd2\u836f\uff0c\u81ea\u5df1\u53c8\u600e\u53d7\u5f97\u4e86\uff1f\u201d \u3000\u3000\u4f17\u4eba\u8bae\u8bba\u7eb7\u7eb7\uff0c\u731c\u4e0d\u900f\u90a3\u4e5e\u5a46\u7684\u6765\u8def\u3002\u7a0b\u9752\u7af9\u66f4\u662f\u6c14\u6124\uff0c\u4e0d\u4f4f\u53e3\u7684\u5492\u9a82\u3002 \u3000\u3000\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u7a0b\u5144\u4f60\u5b89\u5fc3\u4f11\u517b\uff0c\u6211\u4eec\u53bb\u7ed9\u4f60\u63a2\u8bbf\uff0c\u6709\u4e86\u6d88\u606f\u4e4b\u540e\uff0c\u5305\u4f60\u51fa\u8fd9\u53e3\u6076\u6c14\u3002\u201d\u5f53\u4e0b\u6c99\u5929\u5e7f\u3001\u80e1\u6842\u5357\u3001\u94c1\u7f57\u6c49\u3001\u6d2a\u80dc\u6d77\u7b49\u4eba\u5728\u5317\u4eac\u57ce\u91cc\u56db\u4e0b\u8bbf\u67e5\u3002\u4e00\u8fde\u4e09\u5929\uff0c\u72b9\u5982\u77f3\u6c89\u5927\u6d77\uff0c\u54ea\u91cc\u67e5\u5f97\u5230\u534a\u70b9\u7aef\u502a\uff1f \u3000\u3000\u8fd9\u5929\u65e9\u6668\uff0c\u72ec\u773c\u795e\u9f99\u5355\u94c1\u751f\u53c8\u6765\u62dc\u8bbf\uff0c\u7531\u6c99\u5929\u5e7f\u63a5\u89c1\u3002\u5355\u94c1\u751f\u5fe7\u5bb9\u6ee1\u8138\uff0c\u8bf4\u8d77\u6237\u90e8\u5e93\u94f6\u53c8\u5931\u4e86\u4e09\u5343\u4e24\u3002\u6c99\u5929\u5e7f\u552f\u552f\u5426\u5426\uff0c\u540e\u6765\u968f\u53e3\u8bf4\u8d77\u90a3\u8001\u4e5e\u5a46\u7684\u4e8b\uff0c\u5355\u94c1\u751f\u5374\u7559\u4e0a\u4e86\u5fc3\u3002 \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u5355\u94c1\u751f\u5174\u51b2\u51b2\u7684\u8dd1\u6765\uff0c\u5bf9\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u6c99\u7237\uff0c\u90a3\u8001\u4e5e\u5a46\u7684\u884c\u8e2a\uff0c\u5144\u5f1f\u5df2\u8bbf\u5230\u4e86\u4e00\u70b9\u6d88\u606f\uff0c\u6700\u597d\u8bf7\u8881\u76f8\u516c\u4e00\u8d77\u51fa\u6765\uff0c\u5927\u5bb6\u5546\u914c\u3002\u201d\u6c99\u5929\u5e7f\u8fdb\u53bb\u8bf4\u4e86\u3002\u9752\u9752\u9053\uff1a\u201c\u54fc\uff0c\u4ed6\u662f\u5356\u597d\uff0c\u8fd8\u662f\u8981\u80c1\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e24\u8005\u90fd\u662f\uff0c\u8fd9\u5c31\u53bb\u89c1\u89c1\u4ed6\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u9f50\u51fa\u6765\u3002\u5355\u94c1\u751f\u9053\uff1a\u201c\u5144\u5f1f\u542c\u8bf4\u90a3\u4e5e\u5a46\u4e2d\u4e86\u7a0b\u7237\u7684\u9752\u7af9\u9556\uff0c\u5fc3\u60f3\u5979\u5b9a\u8981\u7528\u5927\u6279\u5730\u9aa8\u76ae\u3001\u5ddd\u4e4c\u989c\u3001\u86c7\u85cf\u5b50\u3001\u9cae\u9c7c\u7532\u8fd9\u51e0\u5473\u836f\u89e3\u4f24\uff0c\u4e8e\u662f\u6d3e\u4eba\u5728\u5404\u5bb6\u5927\u836f\u6750\u5e97\u5b88\u7740\uff0c\u6709\u4eba\u6765\u4e70\u8fd9\u4e9b\u836f\uff0c\u5c31\u6084\u6084\u8ddf\u53bb\u3002\u53ea\u89c1\u8fd9\u8001\u4e5e\u5a46\u53d7\u4f24\u591a\u65e5\uff0c\u5018\u82e5\u836f\u6750\u5df2\u7ecf\u4e70\u8db3\uff0c\u8fd9\u6761\u8ba1\u7b56\u5c31\u4e0d\u7075\u4e86\u3002\u603b\u7b97\u8fd0\u6c14\u4e0d\u9519\uff0c\u505a\u516c\u7684\u76d8\u95ee\u5404\u5904\u836f\u6750\u5e97\uff0c\u5f97\u5230\u4e86\u7ebf\u7d22\u3002\u8fd9\u4ef6\u4e8b\u5b9e\u5728\u53e4\u602a\uff01\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u751a\u4e48\u53e4\u602a\uff1f\u201d\u5355\u94c1\u751f\u9053\uff1a\u201c\u5979\u85cf\u8eab\u7684\u6240\u5728\uff0c\u4f60\u9053\u662f\u5728\u54ea\u91cc\uff1f\u539f\u6765\u662f\u8bda\u738b\u7237\u7684\u522b\u5e9c\uff01\u8bda\u738b\u7237\u662f\u5f53\u4eca\u7687\u4e0a\u7684\u53d4\u7236\uff0c\u5b97\u5ba4\u8d35\u80c4\uff0c\u600e\u4f1a\u8ddf\u8fd9\u4e9b\u6c5f\u6e56\u4eba\u7269\u6253\u4ea4\u9053\uff1f\u56e0\u6b64\u5144\u5f1f\u4e5f\u4e0d\u6562\u786e\u5b9a\u3002\u201d\u4f17\u4eba\u4e00\u542c\uff0c\u90fd\u5927\u4e3a\u60ca\u8be7\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u5e26\u6211\u4eec\u5230\u8fd9\u522b\u5e9c\u53bb\u77a7\u77a7\u518d\u8bf4\u3002\u201d\u5355\u94c1\u751f\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u7a0b\u9752\u7af9\u672a\u66fe\u75ca\u6108\uff0c\u53f3\u81c2\u63d0\u4e0d\u8d77\u6765\uff0c\u542c\u4ece\u8881\u627f\u5fd7\u529d\u544a\uff0c\u5728\u5c4b\u91cc\u5019\u8baf\u3002\u8881\u627f\u5fd7\u6015\u654c\u4eba\u4e58\u673a\u524d\u6765\u5bfb\u4ec7\uff0c\u547d\u6d2a\u80dc\u6d77\u7559\u5b88\u4fdd\u62a4\u3002 \u3000\u3000\u51fa\u57ce\u4e03\u516b\u91cc\uff0c\u8fdc\u8fdc\u671b\u89c1\u4e00\u5217\u9ed1\u8272\u56f4\u5899\u3002\u5355\u94c1\u751f\u9053\uff1a\u201c\u90a3\u5c31\u662f\u4e86\u3002\u201d\u8881\u627f\u5fd7\u7591\u5fc3\u5927\u8d77\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u660e\u660e\u662f\u7ea2\u8863\u7ae5\u5b50\u8fdb\u53bb\u7684\u6240\u5728\u3002\u83ab\u975e\u5355\u94c1\u751f\u67e5\u5230\u4e86\u5927\u76d7\u843d\u811a\u7684\u5730\u65b9\uff0c\u6545\u610f\u5f15\u6211\u4eec\u6765\uff0c\u597d\u505a\u4ed6\u5e2e\u624b\uff1f\u8981\u771f\u662f\u738b\u516c\u7684\u522b\u5e9c\uff0c\u54ea\u6709\u8d77\u9020\u5f97\u5982\u6b64\u53e4\u602a\u7684\uff1f\u201d\u5bfb\u601d\u8fd9\u51e0\u65e5\u6765\u5c3d\u9047\u5230\u8be1\u79d8\u602a\u5f02\u4e4b\u4e8b\uff0c\u5012\u8981\u5c0f\u5fc3\u5728\u610f\u3002 \u3000\u3000\u8fd9\u65f6\u6c99\u5929\u5e7f\u4e5f\u60f3\u8d77\u4e86\u8881\u627f\u5fd7\u65e5\u524d\u6240\u8bf4\u7684\u65e0\u95e8\u5927\u5b85\uff0c\u95ee\u5355\u94c1\u751f\u9053\uff1a\u201c\u8fd9\u5ea7\u5b85\u5b50\u6ca1\u95e8\uff0c\u4e0d\u77e5\u4eba\u600e\u6837\u8fdb\u53bb\uff1f\u201d\u5355\u94c1\u751f\u9053\uff1a\u201c\u603b\u662f\u53e6\u6709\u79d8\u95e8\u5427\u3002\u738b\u7237\u7684\u522b\u5e9c\uff0c\u65c1\u4eba\u4e5f\u4e0d\u6562\u591a\u95ee\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u51b3\u5fc3\u9759\u4ee5\u5f85\u53d8\uff0c\u4e0d\u51fa\u4e3b\u610f\uff0c\u4e14\u770b\u5355\u94c1\u751f\u600e\u6837\uff0c\u4ef0\u5934\u89c2\u8d4f\u5929\u4e0a\u53d8\u5e7b\u4e0d\u5b9a\u7684\u767d\u4e91\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9e21\u58f0\u54af\u54af\uff0c\u4e24\u53ea\u5927\u516c\u9e21\u632f\u7fc5\u4ece\u5899\u5185\u98de\u4e86\u51fa\u6765\u3002\u8ddf\u7740\u8dc3\u51fa\u4e24\u540d\u84dd\u886b\u7ae5\u5b50\uff0c\u8eab\u624b\u751a\u662f\u4fbf\u6377\uff0c\u6570\u6251\u4e4b\u4e0b\uff0c\u4fbf\u6349\u4f4f\u4e86\u516c\u9e21\uff0c\u5411\u8881\u627f\u5fd7\u7b49\u671b\u4e86\u51e0\u773c\uff0c\u53c8\u8dc3\u5165\u56f4\u5899\u3002 \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u8fd9\u6837\u5927\u7684\u516c\u9e21\u5012\u4e5f\u5c11\u89c1\uff0c\u6bcf\u53ea\u6015\u6709\u516b\u4e5d\u65a4\u5427\uff1f\u201d \u3000\u3000\u80e1\u6842\u5357\u9053\uff1a\u201c\u516c\u9e21\u518d\u5927\uff0c\u4e5f\u98de\u4e0d\u5230\u90a3\u4e48\u9ad8\uff0c\u6709\u4eba\u4ece\u5899\u91cc\u63b7\u51fa\u6765\u7684\u3002\u90a3\u4e24\u4e2a\u7ae5\u513f\u5047\u88c5\u6349\u9e21\uff0c\u5176\u5b9e\u662f\u5728\u5bdf\u770b\u54b1\u4eec\u7684\u52a8\u9759\u3002\u201d\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4e24\u4e2a\u7ae5\u513f\u6b66\u529f\u4e5f\u5df2\u5f88\u6709\u6839\u5e95\uff0c\u8fd9\u5730\u65b9\u771f\u6709\u70b9\u513f\u90aa\u95e8\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a81\u7136\u8f67\u8f67\u58f0\u54cd\uff0c\u56f4\u5899\u4e0a\u9732\u51fa\u6d1e\u95e8\uff0c\u4e00\u4e2a\u4eba\u8d70\u4e86\u51fa\u6765\u3002\u8fd9\u4eba\u7a7f\u4e00\u4ef6\u5929\u84dd\u8272\u9526\u7f0e\u76ae\u888d\uff0c\u5341\u5206\u5149\u9c9c\uff0c\u888d\u4e0a\u5374\u7528\u6742\u8272\u7ef8\u7f0e\u6253\u4e86\u8bb8\u591a\u8865\u9489\uff0c\u5c31\u5982\u620f\u53f0\u4e0a\u5316\u5b50\u6240\u7a7f\u7684\u5168\u65b0\u767e\u8872\u8863\u4e00\u822c\u3002\u5f85\u5f97\u8d70\u8fd1\uff0c\u8881\u627f\u5fd7\u3001\u9752\u9752\u548c\u5355\u94c1\u751f\u90fd\u662f\u4e00\u60ca\uff0c\u539f\u6765\u5c31\u662f\u90a3\u65e5\u5728\u96ea\u5730\u6349\u86c7\u7684\u4e5e\u4e10\u3002 \u3000\u3000\u8fd9\u4eba\u602a\u773c\u4e00\u7ffb\uff0c\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u65e5\u524d\u76f8\u516c\u8d50\u6211\u7f8e\u9152\uff0c\u5c1a\u672a\u56de\u62a5\u3002\u4eca\u65e5\u96be\u5f97\u5927\u9a7e\u5149\u4e34\uff0c\u8bf7\u5230\u91cc\u9762\uff0c\u8ba9\u6211\u4f5c\u4e2a\u4e1c\u9053\u5982\u4f55\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u53ea\u662f\u9a9a\u6270\u4e0d\u5f53\uff01\u201d\u90a3\u4eba\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u5de6\u624b\u4e00\u4f38\uff0c\u8083\u5ba2\u5165\u5185\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5f53\u5148\u8fdb\u53bb\uff0c\u89c1\u90a3\u56f4\u5899\u7528\u539a\u539a\u7684\u9752\u77f3\u780c\u6210\uff0c\u94c1\u95e8\u539a\u8fbe\u6570\u5bf8\uff0c\u5916\u9762\u6f06\u5f97\u4e0e\u56f4\u5899\u540c\u8272\uff0c\u94c1\u95e8\u4e0e\u56f4\u5899\u4ea4\u754c\u5904\u9020\u5f97\u7ec6\u81f4\u4e25\u5bc6\uff0c\u662f\u4ee5\u4fbf\u5982\u6ca1\u95e8\u4e00\u822c\u3002\u4f17\u4eba\u6bcf\u8d70\u8fdb\u4e00\u5c42\u56f4\u5899\uff0c\u94c1\u95e8\u5c31\u5728\u8eab\u540e\u6084\u65e0\u58f0\u606f\u7684\u5173\u4e0a\u3002\u8d70\u5165\u7ea2\u5899\u540e\uff0c\u90a3\u4eba\u8bf7\u4f17\u4eba\u5230\u82b1\u5385\u5750\u4e0b\uff0c\u5bb6\u4e01\u7aef\u51fa\u83dc\u80b4\uff0c\u7b5b\u4e0a\u9152\u6765\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u83dc\u80b4\u4e30\u76db\uff0c\u7136\u800c\u6bcf\u4e00\u76d8\u4e2d\u7686\u662f\u5927\u7ea2\u5927\u7eff\u4e4b\u7269\uff0c\u8272\u5f69\u9c9c\u660e\uff0c\u5f62\u72b6\u7279\u5f02\uff0c\u4f3c\u4e4e\u90fd\u662f\u4e9b\u86c7\u866b\u4e4b\u7c7b\uff0c\u54ea\u91cc\u6562\u4e0b\u7bb8\u53bb\uff1f\u90a3\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\uff0c\u8bf7\uff01\u201d\u4f38\u7b77\u4ece\u7897\u4e2d\u5939\u8d77\u4e00\u6761\u4e1c\u897f\uff0c\u53ea\u89c1\u7ea2\u5934\u9ed1\u8eab\uff0c\u8d6b\u7136\u662f\u6761\u8708\u86a3\u3002\u4f17\u4eba\u5c3d\u7686\u5927\u60ca\u3002\u90a3\u4eba\u4ef0\u5934\u5f20\u53e3\uff0c\u628a\u4e00\u6761\u5927\u8708\u86a3\u6d25\u6d25\u6709\u5473\u7684\u5403\u4e86\u4e0b\u53bb\u3002\u9752\u9752\u4e00\u9635\u6076\u5fc3\uff0c\u9669\u4e9b\u5455\u4e86\u51fa\u6765\uff0c\u5fd9\u6389\u5934\u4e0d\u770b\u3002 \u3000\u3000\u90a3\u4eba\u89c1\u628a\u5bf9\u65b9\u5413\u5012\uff0c\u5f97\u610f\u4e4b\u6781\uff0c\u5bf9\u5355\u94c1\u751f\u9053\uff1a\u201c\u4f60\u662f\u8859\u95e8\u7684\u9e70\u722a\u5b59\uff0c\u60f3\u662f\u8981\u5e93\u94f6\u6765\u7740\u3002\u54fc\uff0c\u4f60\u53ef\u77e5\u6211\u662f\u8c01\uff1f\u201d\u5355\u94c1\u751f\u9053\uff1a\u201c\u6055\u5c0f\u4eba\u773c\u62d9\uff0c\u8bf7\u6559\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\u3002\u201d \u3000\u3000\u90a3\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u559d\u4e00\u53e3\u9152\uff0c\u53c8\u5403\u4e86\u4e00\u6761\u4e0d\u77e5\u751a\u4e48\u866b\uff0c\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u9f50\u540d\u4e91\u7488\uff0c\u65e0\u540d\u5c0f\u5352\uff0c\u8001\u5144\u4e5f\u4e0d\u4f1a\u77e5\u9053\u3002\u201d\u5355\u94c1\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u9601\u4e0b\u662f\u9526\u8863\u6bd2\u4e10\u3002\u5728\u4e0b\u4e45\u95fb\u5927\u540d\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4ece\u6ca1\u542c\u8fc7\u9526\u8863\u6bd2\u4e10\u7684\u540d\u5b57\uff0c\u89c1\u5355\u94c1\u751f\u5982\u6b64\u9707\u52a8\uff0c\u60f3\u5fc5\u662f\u4e2a\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\uff0c\u7136\u800c\u65e5\u524d\u89c1\u4ed6\u6597\u86c7\uff0c\u4e5f\u4e0d\u89c1\u5f97\u6709\u751a\u4e48\u4e86\u4e0d\u8d77\u3002\u53c8\u542c\u5355\u94c1\u751f\u606d\u606d\u656c\u656c\u7684\u8bf4\u9053\uff1a\u201c\u8d35\u6559\u5411\u5728\u4e24\u5e7f\u4e91\u8d35\u884c\u9053\uff0c\u4e00\u76f4\u65e0\u7f18\u62dc\u89c1\u3002\u201d\u9f50\u4e91\u7488\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u4eec\u5230\u4eac\u5e08\u6765\uff0c\u4e5f\u4e0d\u8fc7\u51e0\u4e2a\u6708\u3002\u201d\u5355\u94c1\u751f\u9053\uff1a\u201c\u5728\u4e0b\u4e45\u5df2\u4e0d\u5403\u516c\u95e8\u996d\uff0c\u8fd9\u6b21\u9f50\u82f1\u96c4\u4eec\u6765\u5230\u4eac\u57ce\uff0c\u5f1f\u5144\u4eec\u6d88\u606f\u4e0d\u7075\uff0c\u793c\u8c8c\u4e0d\u5468\uff0c\u5728\u4e0b\u8fd9\u91cc\u8c22\u8fc7\u3002\u201d \u3000\u3000\u8bf4\u7740\u8fde\u8fde\u4f5c\u63d6\u3002\u9f50\u4e91\u7488\u81ea\u987e\u996e\u9152\u5403\u83dc\uff0c\u5e76\u4e0d\u56de\u793c\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u516c\u95e8\u6355\u5feb\u6b3a\u538b\u767e\u59d3\u4e4b\u65f6\uff0c\u5982\u72fc\u4f3c\u864e\uff0c\u89c1\u4e86\u786c\u624b\uff0c\u5374\u5982\u6b64\u4f4e\u58f0\u4e0b\u6c14\u3002\u4e14\u770b\u8fd9\u4e8b\u5982\u4f55\u4e86\u7ed3\u3002\u201d \u3000\u3000\u5355\u94c1\u751f\u9053\uff1a\u201c\u5f1f\u5144\u4eec\u80e1\u6d82\u5f97\u7d27\uff0c\u5f97\u7f6a\u4e86\u9f50\u82f1\u96c4\u8fd8\u4e00\u76f4\u4e0d\u77e5\u9053\u3002\u53ea\u8981\u9f50\u82f1\u96c4\u5429\u5490\u4e0b\u6765\uff0c\u6211\u4eec\u505a\u5f97\u5230\u7684\uff0c\u65e0\u6709\u4e0d\u9075\u3002\u201d\u9f50\u4e91\u7488\u9053\uff1a\u201c\u5230\u4eca\u5929\u4e3a\u6b62\uff0c\u6211\u4eec\u4e00\u5171\u53d6\u4e86\u5e93\u94f6\u56db\u4e07\u4e94\u5343\u4e24\uff0c\u8fd9\u6570\u76ee\u5b9e\u5728\u592a\u5c0f\uff0c\u5b9e\u5728\u592a\u5c0f\uff01\u9884\u8ba1\u53d6\u8db3\u5341\u4e07\u4e24\uff0c\u4e5f\u53ef\u4ee5\u7f62\u624b\u5566\uff01\u201d\u5355\u94c1\u751f\u9053\uff1a\u201c\u6237\u90e8\u5085\u5c1a\u4e66\u8ddf\u4e94\u57ce\u5175\u9a6c\u5468\u6307\u6325\u4f7f\u77e5\u9053\u4e4b\u540e\uff0c\u5b9a\u4f1a\u6765\u5411\u8bda\u738b\u7237\u8d54\u7f6a\u3002\u6211\u4eec\u505a\u4e0b\u4eba\u7684\u53ea\u597d\u8bf7\u8001\u54e5\u8d4f\u53e3\u996d\u5403\uff01\u201d \u3000\u3000\u9f50\u4e91\u7488\u602a\u773c\u4e00\u7ffb\uff0c\u68ee\u7136\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u94f6\u5b50\u662f\u5728\u8bda\u738b\u7237\u522b\u5e9c\uff0c\u96be\u9053\u8fd8\u60f3\u6d3b\u7740\u8d70\u51fa\u53bb\u5417\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4eba\u4eba\u4e3a\u4e4b\u8272\u53d8\u3002\u5ffd\u7136\u95f4\u5385\u5916\u4f20\u6765\u4e00\u9635\u5c16\u9510\u7684\u54e8\u5b50\u58f0\uff0c\u58f0\u97f3\u60e8\u5389\u96be\u542c\u4e4b\u6781\uff0c\u5404\u4eba\u90fd\u4e0d\u89c9\u6253\u4e2a\u5bd2\u5664\uff0c\u5bd2\u6bdb\u76f4\u7ad6\u3002\u9752\u9752\u63e1\u4f4f\u8881\u627f\u5fd7\u7684\u624b\uff0c\u60ca\u9053\uff1a\u201c\u90a3\u662f\u751a\u4e48\uff1f\u201d \u3000\u3000\u9f50\u4e91\u7488\u7acb\u5373\u7ad9\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u6559\u4e3b\u5347\u5ea7\u3002\u5927\u5bb6\u53bb\u542c\u51ed\u53d1\u843d\uff0c\u77a7\u5404\u4eba\u7684\u9020\u5316\u5427\uff01\u201d\u5355\u94c1\u751f\u60ca\u9053\uff1a\u201c\u8d35\u6559\u6559\u4e3b\u4e5f\u5230\u4e86\u5317\u4eac\uff1f\u201d\u9f50\u4e91\u7488\u51b7\u7b11\u4e00\u58f0\uff0c\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u5f84\u81ea\u5165\u5185\u3002 \u3000\u3000\u5355\u94c1\u751f\u9053\uff1a\u201c\u60c5\u52bf\u7d27\u903c\uff0c\u54b1\u4eec\u5feb\u8d70\uff01\u8981\u662f\u4e94\u6bd2\u6559\u6559\u4e3b\u771f\u7684\u5230\u4e86\uff0c\u5927\u5bb6\u6b7b\u4e86\u8fde\u9aa8\u5934\u4e5f\u5269\u4e0d\u4e0b\u4e00\u6839\u3002\u201d\u8881\u627f\u5fd7\u8fd8\u60f3\u770b\u4e2a\u7a76\u7adf\uff0c\u4f46\u89c9\u9752\u9752\u7684\u624b\u5fae\u5fae\u53d1\u6296\uff0c\u5468\u56f4\u60c5\u52bf\u53c8\u786e\u662f\u9634\u68ee\u68ee\u7684\u5341\u5206\u53ef\u6016\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5927\u4f19\u513f\u5148\u9000\u51fa\u53bb\u518d\u8bf4\u3002\u201d\u4f17\u4eba\u521a\u8981\u8f6c\u8eab\uff0c\u7a81\u7136\u7830\u7684\u4e00\u58f0\uff0c\u80cc\u540e\u4e00\u5757\u4e0d\u77e5\u662f\u94c1\u677f\u8fd8\u662f\u5927\u77f3\u843d\u4e86\u4e0b\u6765\uff0c\u82b1\u5385\u4e2d\u767b\u65f6\u6f06\u9ed1\u4e00\u56e2\uff0c\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002 \u3000\u3000\u4f17\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u53c8\u542c\u5f97\u4e00\u9635\u60e8\u5389\u7684\u602a\u54cd\uff0c\u4f3c\u662f\u6076\u9e1f\u9f50\u9e23\uff0c\u53c8\u5982\u6bd2\u866b\u5408\u557c\uff0c\u4f17\u4eba\u542c\u4e86\uff0c\u5f53\u771f\u662f\u4e0d\u5bd2\u800c\u6817\u3002\u7a81\u7136\u95f4\u773c\u524d\u4e00\u4eae\uff0c\u5bf9\u9762\u5c04\u6765\u4e00\u9053\u8000\u773c\u5149\u8292\u3002\u767d\u5149\u4e2d\u4e24\u540d\u9ed1\u8863\u7ae5\u5b50\u8d70\u8fdb\u5385\u6765\uff0c\u5fae\u5fae\u8eac\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\u5ba3\u53ec\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff0c\u4e0d\u77e5\u6709\u751a\u4e48\u53e4\u602a\uff0c\u524d\u53bb\u770b\u4e2a\u660e\u767d\u518d\u8bf4\uff0c\u5f53\u4e0b\u633d\u4e86\u9752\u9752\u7684\u624b\uff0c\u8ddf\u7740\u9ed1\u8863\u7ae5\u5b50\u9996\u5148\u8d70\u4e86\u51fa\u53bb\uff0c\u4f17\u4eba\u8ddf\u968f\u5728\u540e\u3002\u8f6c\u5f2f\u62b9\u89d2\u7684\u8d70\u4e86\u597d\u4e00\u9635\uff0c\u7ecf\u8fc7\u4e00\u6761\u6781\u957f\u7684\u752c\u9053\uff0c\u6765\u5230\u4e00\u5ea7\u6bbf\u5802\u3002\u6bbf\u4e0a\u5c45\u4e2d\u8bbe\u4e86\u4e00\u5f20\u5927\u6905\uff0c\u6905\u4e0a\u7f69\u4e86\u6731\u7ea2\u8272\u7684\u9526\u62ab\uff0c\u4e24\u65c1\u5404\u7ad9\u7740\u56db\u4e2a\u7ae5\u5b50\u3002\u9ed1\u8863\u7ae5\u5b50\u4e0a\u6bbf\u5206\u7ad9\u4e24\u65c1\uff0c\u6bcf\u4e00\u8fb9\u90fd\u662f\u5206\u7a7f\u7ea2\u3001\u9ec4\u3001\u84dd\u3001\u767d\u3001\u9ed1\u4e94\u8272\u9526\u8863\u7684\u4e94\u540d\u7ae5\u5b50\uff0c\u90a3\u4e24\u540d\u8eab\u7a7f\u7ea2\u8863\u7684\u5c31\u662f\u76ee\u524d\u76d7\u5e93\u94f6\u7684\u7ae5\u5b50\uff0c\u8fd9\u65f6\u90a3\u4e24\u7ae5\u5782\u9996\u4f4e\u7709\uff0c\u89c1\u5230\u4f17\u4eba\u6beb\u4e0d\u7406\u4f1a\u3002 \u3000\u3000\u53ea\u542c\u6bbf\u540e\u949f\u58f0\u5f53\u5f53\uff0c\u8d70\u51fa\u4e00\u7fa4\u4eba\u6765\uff0c\u9ad8\u9ad8\u77ee\u77ee\uff0c\u6709\u7537\u6709\u5973\uff0c\u5206\u7ad9\u6905\u5b50\u4e24\u65c1\uff0c\u6bcf\u8fb9\u516b\u4eba\uff0c\u5171\u662f\u4e00\u5341\u516d\u53d6\u3002\u9526\u8863\u6bd2\u4e10\u7ad9\u5728\u5de6\u9996\u7b2c\u4e8c\u3002\u53f3\u624b\u7b2c\u4e8c\u4eba\u94a9\u9f3b\u6df1\u76ee\uff0c\u6ee1\u8138\u4f24\u75a4\uff0c\u8d6b\u7136\u662f\u4e2a\u76f8\u8c8c\u51f6\u6076\u7684\u8001\u4e5e\u5a46\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u5fc5\u662f\u4f24\u5bb3\u7a0b\u8001\u592b\u5b50\u7684\u4e5e\u5a46\u5b50\u3002\u201d\u4f4e\u58f0\u95ee\u5355\u94c1\u751f\uff1a\u201c\u4ed6\u4eec\u5728\u6363\u751a\u4e48\u9b3c\uff1f\u201d\u5355\u94c1\u751f\u8138\u8272\u82cd\u767d\uff0c\u58f0\u97f3\u53d1\u98a4\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u662f\u4e91\u5357\u4e94\u6bd2\u6559\u554a\uff0c\u8fd9\u4e00\u56de\u54b1\u4eec\u6b7b\u5b9a\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e94\u6bd2\u6559\u662f\u751a\u4e48\u4e1c\u897f\uff1f\u201d\u5355\u94c1\u751f\u6025\u9053\uff1a\u201c\u554a\u54df\uff0c\u8881\u76f8\u516c\uff0c\u4e94\u6bd2\u6559\u662f\u6740\u4eba\u4e0d\u7728\u773c\u7684\u90aa\u6559\uff0c\u6559\u4e3b\u4f55\u94c1\u624b\uff0c\u4f60\u6ca1\u542c\u89c1\u8fc7\u5417\uff1f\u201d\u8881\u627f\u5fd7\u6447\u6447\u5934\u3002 \u3000\u3000\u5355\u94c1\u751f\u9053\uff1a\u201c\u4e58\u4ed6\u4eec\u6559\u4e3b\u8fd8\u6ca1\u51fa\u6765\uff0c\u54b1\u4eec\u5feb\u9003\u5427\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u77a7\u4e00\u4e0b\u518d\u8bf4\uff01\u201d \u3000\u3000\u5355\u94c1\u751f\u5fc3\u4e2d\u6015\u6781\uff0c\u51b3\u5b9a\u5355\u72ec\u9003\u8d70\uff0c\u7a81\u7136\u53eb\u9053\uff1a\u201c\u5728\u4e0b\u5931\u966a\u4e86\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5df2\u62d4\u8d77\u8eab\u5b50\uff0c\u5411\u5899\u5934\u7a9c\u53bb\u3002\u7ad9\u5728\u5de6\u624b\u7b2c\u4e09\u7684\u9ad8\u4e2a\u5b50\u8eab\u5f62\u4e00\u6643\uff0c\u8ffd\u4e86\u8fc7\u53bb\uff0c\u8dc3\u8d77\u8eab\u6765\uff0c\u4f38\u624b\u6293\u4f4f\u5355\u94c1\u751f\u5de6\u8e1d\u3002\u5355\u94c1\u751f\u8eab\u5b50\u4e00\u5f13\uff0c\u53f3\u638c\u5f80\u4ed6\u5934\u4e0a\u76f4\u5288\u4e0b\u53bb\u3002\u90a3\u9ad8\u4e2a\u5b50\u4e3e\u624b\u4e00\u6321\uff0c\u5566\u7684\u4e00\u58f0\uff0c\u4e24\u4eba\u90fd\u9707\u4e0b\u5730\u6765\u3002\u9ad8\u4e2a\u5b50\u51b7\u7b11\u4e00\u58f0\uff0c\u56de\u73ed\u7ad9\u7acb\u3002 \u3000\u3000\u5355\u94c1\u751f\u53ea\u89c9\u5de6\u811a\u548c\u53f3\u638c\u5747\u4e3a\u5175\u5203\u6240\u4f24\uff0c\u5267\u75db\u523a\u5fc3\uff0c\u4e3e\u624b\u4e00\u770b\uff0c\u638c\u4e0a\u4e94\u4e2a\u5c0f\u5b54\u4e2d\u4e0d\u4f4f\u6d41\u51fa\u9ed1\u8840\uff0c\u4e0d\u7531\u5f97\u5927\u60ca\u5931\u8272\uff0c\u518d\u63d0\u5de6\u811a\u770b\u65f6\uff0c\u4e5f\u6709\u4e94\u4e2a\u5c0f\u5b54\uff0c\u5fc3\u91cc\u4e00\u5413\uff0c\u5012\u5728\u5730\u4e0b\u3002\u539f\u6765\u90a3\u9ad8\u4e2a\u5b50\u5341\u6839\u624b\u6307\u90fd\u6234\u4e86\u88c5\u6709\u5c16\u523a\u7684\u6307\u73af\uff0c\u523a\u4e0a\u5582\u7740\u6781\u5389\u5bb3\u7684\u6bd2\u836f\u3002\u6c99\u5929\u5e7f\u4e0a\u524d\u628a\u5355\u94c1\u751f\u62c9\u8d77\u3002 \u3000\u3000\u53ea\u89c1\u5341\u540d\u7ae5\u5b50\u5404\u4ece\u888b\u91cc\u53d6\u51fa\u54e8\u5b50\u5439\u4e86\u51e0\u4e0b\uff0c\u4e8c\u5341\u591a\u4eba\u4e00\u9f50\u8eac\u8eab\u3002\u6bbf\u540e\u7f13\u6b65\u8d70\u51fa\u4e24\u4e2a\u5c11\u5973\uff0c\u5f80\u6905\u65c1\u4e00\u7ad9\uff0c\u5a07\u58f0\u53eb\u9053\uff1a\u201c\u6559\u4e3b\u5347\u5ea7\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e00\u9635\u91d1\u94c1\u76f8\u649e\u7684\u94ee\u94ee\u4e4b\u58f0\uff0c\u5176\u97f3\u6e05\u8d8a\uff0c\u5982\u594f\u4e50\u5668\uff0c\u8ddf\u7740\u98ce\u9001\u5f02\u9999\uff0c\u6bbf\u540e\u8d70\u51fa\u4e00\u4e2a\u8eab\u7a7f\u7c89\u7ea2\u8272\u7eb1\u8863\u7684\u5973\u90ce\u3002\u53ea\u89c1\u5979\u51e4\u773c\u542b\u6625\uff0c\u957f\u7709\u5165\u9b13\uff0c\u5634\u89d2\u542b\u7740\u7b11\u610f\uff0c\u7ea6\u83ab\u4e8c\u5341\u4e8c\u4e09\u5c81\u5e74\u7eaa\uff0c\u751a\u662f\u7f8e\u8c8c\u3002\u5979\u8d64\u7740\u53cc\u8db3\uff0c\u6bcf\u4e2a\u8db3\u8e1d\u4e0e\u624b\u81c2\u4e0a\u5404\u5957\u7740\u4e24\u679a\u9ec4\u91d1\u5706\u73af\uff0c\u884c\u52a8\u65f6\u91d1\u73af\u4e92\u51fb\uff0c\u94ee\u94ee\u6709\u58f0\u3002\u80a4\u8272\u767d\u817b\u5f02\u5e38\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u8102\u5149\u5982\u7389\uff0c\u5934\u4e0a\u957f\u53d1\u5782\u80a9\uff0c\u4e5f\u4ee5\u91d1\u73af\u675f\u4f4f\u3002\u5979\u8d70\u5230\u6905\u4e2d\u5750\u4e0b\uff0c\u540e\u9762\u53c8\u6709\u4e24\u4e2a\u5c11\u5973\u8ddf\u7740\u51fa\u6765\uff0c\u5206\u6301\u7fbd\u6247\u62c2\u5c18\u3002 \u3000\u3000\u90a3\u5973\u5b50\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u554a\u54df\uff0c\u8fd9\u4e48\u591a\u5ba2\u4eba\uff0c\u5feb\u62ff\u6905\u5b50\u6765\uff0c\u8bf7\u5750\uff01\u201d\u4f17\u7ae5\u5b50\u5fd9\u5165\u5185\u5802\uff0c\u642c\u51fa\u51e0\u5f20\u6905\u5b50\uff0c\u7ed9\u8881\u627f\u5fd7\u7b49\u5750\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b49\u5fc3\u4e2d\u7591\u4e91\u91cd\u91cd\uff1a\u201c\u4e94\u6bd2\u6559\u6559\u4f17\u90fd\u5982\u6b64\u5947\u5f62\u602a\u72b6\uff0c\u6a2a\u86ee\u72e0\u6bd2\uff0c\u6559\u4e3b\u672c\u4eba\u5f53\u66f4\u662f\u51f6\u6076\u65e0\u4f26\uff0c\u96be\u9053\u628a\u5355\u94c1\u751f\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\u7684\u4e94\u6bd2\u6559\u6559\u4e3b\u4f55\u94c1\u624b\uff0c\u4fbf\u662f\u8fd9\u4e2a\u5e74\u8f7b\u59d1\u5a18\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u5a07\u6ef4\u6ef4\u7684\u8bf4\u9053\uff1a\u201c\u8bf7\u6559\u5c0a\u5ba2\u8d35\u59d3\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u8881\u3002\u8fd9\u51e0\u4f4d\u90fd\u662f\u5728\u4e0b\u7684\u670b\u53cb\uff0c\u8bf7\u95ee\u59d1\u5a18\u9ad8\u59d3\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6211\u59d3\u4f55\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u60f3\uff1a\u201c\u90a3\u4e48\u5979\u771f\u7684\u662f\u4e94\u6bd2\u6559\u6559\u4e3b\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u662f\u6765\u8981\u5e93\u94f6\u7684\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u662f\u3002 \u3000\u3000\u8fd9\u4f4d\u5355\u670b\u53cb\u662f\u5403\u516c\u95e8\u996d\u7684\u3002\u6211\u4eec\u5374\u662f\u5e73\u6c11\u8001\u767e\u59d3\uff0c\u8ddf\u8fd9\u4f4d\u5355\u670b\u53cb\u4e5f\u662f\u521d\u4ea4\u3002\u5b98\u5bb6\u7684\u4e8b\u561b\uff0c\u6211\u4eec\u4e0d\u6562\u8fc7\u95ee\u3002\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u597d\u554a\uff0c\u90a3\u4e48\u4f60\u4eec\u5230\u8fd9\u91cc\u5e72\u751a\u4e48\u6765\u7740\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e2a\u59d3\u7a0b\u7684\u670b\u53cb\uff0c\u4e0d\u77e5\u751a\u4e48\u5730\u65b9\u5f00\u7f6a\u4e86\u8d35\u6559\u7684\u670b\u53cb\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u56e0\u6b64\u8fc7\u6765\u8bf7\u95ee\u4e00\u4e0b\u3002\u6211\u90a3\u59d3\u7a0b\u7684\u670b\u53cb\u8bf4\uff0c\u4ed6\u8ddf\u8d35\u6559\u7684\u670b\u53cb\u7d20\u4e0d\u76f8\u8bc6\uff0c\u53ea\u6015\u662f\u8bef\u4f1a\u3002\u201d\u90a3\u5973\u5b50\u7b11\u7b11\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u7a0b\u5e2e\u4e3b\u7684\u670b\u53cb\uff0c\u90a3\u53c8\u4e0d\u540c\u5566\uff0c\u6211\u8fd8\u9053\u8881\u76f8\u516c\u662f\u9e70\u722a\u4e00\u4f19\u5462\uff0c\u6765\u554a\uff0c\u732e\u8336\uff01\u201d\u4f17\u7ae5\u5b50\u642c\u51fa\u8336\u513f\uff0c\u732e\u4e0a\u8336\u6765\u3002\u4f17\u4eba\u89c1\u8336\u6c34\u7eff\u5e7d\u5e7d\u5730\uff0c\u4e5f\u4e0d\u89c1\u8336\u53f6\uff0c\u867d\u7136\u6e05\u9999\u6251\u9f3b\uff0c\u5374\u4e0d\u6562\u559d\u3002 \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u542c\u9f50\u5e08\u5144\u8bf4\uff0c\u8881\u76f8\u516c\u6177\u6168\u597d\u5ba2\uff0c\u8eab\u6000\u51b0\u87fe\u81f3\u5b9d\uff0c\u539f\u60f3\u4e0d\u4f1a\u662f\u9e70\u722a\u4e00\u6d41\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\u5979\u82e5\u662f\u6559\u4e3b\uff0c\u600e\u4f1a\u53c8\u79f0\u5ea7\u4e0b\u5f1f\u5b50\u4e3a\u5e08\u5144\uff0c\u771f\u662f\u5f04\u4ed6\u4eec\u4e0d\u61c2\uff0c\u5f53\u4e0b\u542b\u7cca\u7b54\u5e94\u3002\u90a3\u5973\u5b50\u9053\uff1a\u201c\u8881\u76f8\u516c\u51b0\u87fe\u7684\u5999\u7528\uff0c\u53ef\u80fd\u8ba9\u6211\u4e00\u5f00\u773c\u754c\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\u5982\u5c06\u51b0\u87fe\u4ea4\u5230\u5979\u624b\u91cc\uff0c\u53ea\u6015\u5979\u6492\u8d56\u4e0d\u8fd8\uff0c\u5f53\u4e0b\u53d6\u51fa\u51b0\u87fe\uff0c\u5728\u5355\u94c1\u751f\u7684\u4f24\u53e3\u4e0a\u5438\u6bd2\u3002\u4e94\u6bd2\u6559\u4eba\u4f17\u89c1\u4f24\u53e3\u4e2d\u9ed1\u8840\u7247\u523b\u95f4\u4fbf\u5373\u53bb\u5c3d\uff0c\u90fd\u662f\u8138\u73b0\u6b23\u7fa1\u4e4b\u8272\u3002 \u3000\u3000\u90a3\u5973\u5b50\u597d\u80dc\u5fc3\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u771f\u662f\u5267\u6bd2\u4e4b\u7269\uff0c\u53ea\u6015\u8fd9\u51b0\u87fe\u4e5f\u6cbb\u4e0d\u4e86\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u662f\u4e94\u6bd2\u6559\uff0c\u6211\u8fd9\u51b0\u87fe\u514b\u5236\u6bd2\u7269\uff0c\u6b63\u662f\u4ed6\u4eec\u5927\u5fcc\uff0c\u8fd8\u662f\u8c26\u6291\u4e9b\u4e3a\u662f\u3002\u201d\u8bf4\u9053\uff1a\u201c\u90a3\u5f53\u7136\u5566\uff0c\u5929\u4e0b\u5389\u5bb3\u6bd2\u7269\u751a\u591a\uff0c\u8fd9\u5c0f\u5c0f\u51b0\u87fe\uff0c\u6709\u751a\u4e48\u7528\uff1f\u4f55\u51b5\u53c8\u662f\u6b7b\u7269\u3002\u201d \u3000\u3000\u9752\u9752\u5374\u4e0d\u670d\u6c14\u4e86\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u89c1\u5f97\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u542c\u4e86\u8881\u627f\u5fd7\u7684\u8bdd\u672c\u5f88\u9ad8\u5174\uff0c\u542c\u9752\u9752\u63d2\u53e3\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53d6\u4e94\u5723\u6765\uff01\u201d\u4e94\u540d\u7ae5\u5b50\u5165\u5185\uff0c\u6367\u4e86\u4e94\u53ea\u94c1\u76d2\u51fa\u6765\u3002\u53e6\u5916\u4e94\u540d\u7ae5\u5b50\u6367\u4e86\u4e00\u53ea\u5706\u684c\u9762\u5927\u5c0f\u7684\u6c99\u76d8\uff0c\u653e\u5728\u6bbf\u4e2d\u3002 \u3000\u3000\u5341\u540d\u7ae5\u5b50\u56f4\u7740\u6c99\u76d8\u7ad9\u5b9a\uff0c\u7ea2\u8863\u7ae5\u5b50\u6367\u7ea2\u76d2\uff0c\u9ec4\u8863\u7ae5\u5b50\u6367\u9ec4\u76d2\uff0c\u4e94\u540d\u9526\u8863\u7ae5\u5b50\u5404\u6367\u4e0e\u8863\u540c\u8272\u7684\u94c1\u76d2\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e9b\u4eba\u884c\u52a8\u9887\u6709\u5996\u6c14\u3002\u4f46\u77a7\u4ed6\u4eec\u5982\u6b64\u6392\u5217\uff0c\u6309\u7740\u91d1\u6728\u6c34\u706b\u571f\u4e94\u884c\uff0c\u5012\u4e5f\u4e0d\u662f\u80e1\u4e71\u552c\u4eba\u7684\u3002\u201d\u53c8\u89c1\u5de6\u9996\u7b2c\u4e09\u4e2a\u5937\u65cf\u6253\u626e\u7684\u58ee\u6c49\u8d70\u5230\u6c99\u76d8\u4e4b\u65c1\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u9762\u5c0f\u9752\u65d7\uff0c\u8f7b\u8f7b\u4e00\u6325\u3002\u4e94\u540d\u7ae5\u5b50\u6253\u5f00\u76d2\u5b50\u3002\u9752\u9752\u4e0d\u7981\u5931\u58f0\u60ca\u547c\uff0c\u53ea\u89c1\u6bcf\u53ea\u76d2\u4e2d\uff0c\u5404\u8df3\u51fa\u4e00\u6837\u6bd2\u7269\u3002\u54ea\u4e94\u6837\uff1f\u9752\u86c7\u3001\u8708\u86a3\u3001\u874e\u5b50\u3001\u8718\u86db\u3001\u87fe\u870d\u3002 \u3000\u3000\u90a3\u5937\u4eba\u53c8\u662f\u4e00\u6325\u9752\u65d7\uff0c\u5341\u540d\u7ae5\u5b50\u4e00\u9f50\u9000\u5f00\u3002\u4f17\u5f1f\u5b50\u4e2d\u8d70\u51fa\u56db\u4eba\uff0c\u5206\u636e\u6c99\u76d8\u56db\u5468\uff0c\u5583\u5583\u4f1e\u5492\uff0c\u4ece\u8863\u888b\u4e2d\u53d6\u51fa\u836f\u7269\uff0c\u54ac\u56bc\u4e00\u9635\uff0c\u55b7\u5165\u6c99\u76d8\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5bfb\u601d\uff1a\u201c\u8fd9\u4e9b\u9a71\u4f7f\u6bd2\u7269\u7684\u602a\u6cd5\uff0c\u6211\u53ef\u4e00\u7a8d\u4e0d\u901a\uff0c\u83ab\u8981\u7740\u4e86\u4ed6\u4eec\u9053\u513f\u3002\u201d\u518d\u770b\u76d8\u4e2d\uff0c\u9752\u86c7\u957f\u8fd1\u5c3a\u8bb8\uff0c\u672a\u89c1\u6709\u4f55\u7279\u5f02\uff0c\u5176\u4f59\u56db\u79cd\u6bd2\u7269\uff0c\u5374\u5747\u6bd4\u5e73\u5e38\u6240\u89c1\u7684\u8981\u957f\u5927\u5f97\u591a\u3002\u4e94\u79cd\u6bd2\u7269\u5728\u76d8\u4e2d\u6e38\u8d70\u4e00\u9635\u4e4b\u540e\uff0c\u5404\u81ea\u5c48\u8eab\u84c4\u52bf\uff0c\u5f20\u7259\u821e\u722a\uff0c\u4fbf\u6b32\u4e92\u6597\u3002 \u3000\u3000\u6bd2\u8718\u86db\u4e0d\u4f4f\u5410\u4e1d\uff0c\u5728\u6c99\u76d8\u4e00\u89d2\u7ed3\u8d77\u7f51\u6765\u3002\u874e\u5b50\u6c89\u4e0d\u4f4f\u6c14\uff0c\u5411\u7f51\u4e0a\u4e00\u51b2\uff0c\u5f04\u65ad\u4e86\u8bb8\u591a\u86db\u4e1d\uff0c\u968f\u5373\u9000\u5f00\u3002\u8718\u86db\u77aa\u773c\u5411\u874e\u5b50\u671b\u4e86\u51e0\u773c\uff0c\u53c8\u5410\u4e1d\u7ed3\u7f51\uff0c\u7f51\u672a\u5e03\u59a5\uff0c\u874e\u5b50\u53c8\u662f\u4e00\u51b2\u3002\u8fd9\u822c\u7ed3\u7f51\u51b2\u7f51\uff0c\u51e0\u6b21\u4e4b\u540e\uff0c\u874e\u5b50\u8eab\u4e0a\u5df2\u7c98\u6ee1\u86db\u4e1d\uff0c\u884c\u52a8\u5927\u4e3a\u8fdf\u7f13\uff0c\u4e24\u53ea\u811a\u88ab\u86db\u4e1d\u7c98\u7f20\u5728\u4e00\u8d77\uff0c\u65e0\u6cd5\u6323\u8131\u3002\u8718\u86db\u4e58\u673a\u53cd\u653b\uff0c\u5927\u5410\u67d4\u4e1d\uff0c\u5728\u874e\u5b50\u8eab\u65c1\u539a\u539a\u7684\u7ed3\u4e86\u51e0\u5c42\u7f51\uff0c\u6084\u6084\u8d70\u5230\u874e\u5b50\u8eab\u524d\uff0c\u4f38\u8db3\u64a9\u62e8\u3002\u874e\u5b50\u7a81\u7136\u7ffb\u8fc7\u6bd2\u5c3e\uff0c\u556a\u7684\u4e00\u58f0\u51fb\u6253\u3002\u8718\u86db\u5feb\u5982\u95ea\u7535\uff0c\u65e9\u5df2\u9000\u5f00\u3002\u8fd9\u822c\u6311\u9017\u6570\u6b21\uff0c\u874e\u5b50\u6012\u706b\u5927\u70bd\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5411\u524d\u731b\u8ffd\u8fc7\u53bb\uff0c\u4e0d\u63d0\u9632\u6b63\u5815\u5165\u8718\u86db\u5e03\u7f6e\u7684\u9677\u9631\u4e4b\u4e2d\u3002\u874e\u5b50\u5728\u7f51\u4e0a\u62da\u547d\u6323\u624e\uff0c\u773c\u89c1\u5728\u8718\u86db\u7f51\u4e2d\u5f04\u7834\u4e00\u4e2a\u5927\u6d1e\u3002\u8718\u86db\u5fd9\u53c8\u5410\u4e1d\u7ea0\u7f20\uff0c\u874e\u5b50\u6e10\u6e10\u65e0\u529b\u6323\u624e\u3002\u8718\u86db\u6251\u4e0a\uff0c\u5f20\u53e3\u4e00\u54ac\uff0c\u874e\u5b50\u75db\u5f97\u5431\u5431\u4e71\u53eb\u3002 \u3000\u3000\u8718\u86db\u6b63\u5728\u4eab\u53d7\u7f8e\u5473\uff0c\u7a81\u7136\u4e00\u9635\u87fe\u6c99\u55b7\u5230\uff0c\u6bd2\u87fe\u870d\u7834\u9635\u76f4\u5165\uff0c\u957f\u820c\u4e00\u7ffb\uff0c\u628a\u874e\u5b50\u4ece\u8718\u86db\u7f51\u4e2d\u5377\u4e86\u51fa\u6765\uff0c\u4e00\u53e3\u541e\u5165\u4e86\u809a\u91cc\u3002\u8718\u86db\u5927\u6012\uff0c\u5411\u87fe\u870d\u51b2\u53bb\u3002\u87fe\u870d\u957f\u820c\u7ffb\u51fa\uff0c\u8981\u5377\u8718\u86db\uff0c\u8718\u86db\u5f20\u53e3\u5411\u87fe\u870d\u820c\u5934\u4e0a\u54ac\u53bb\u3002\u87fe\u870d\u957f\u820c\u500f\u7684\u7f29\u56de\u3002\u8718\u86db\u6162\u6162\u722c\u5230\u87fe\u870d\u5de6\u8fb9\uff0c\u5410\u51fa\u4e00\u6761\u7c97\u4e1d\uff0c\u7c98\u5728\u76d8\u4e0a\uff0c\u5ffd\u5730\u8dc3\u8d77\uff0c\u7275\u7740\u90a3\u6839\u4e1d\uff0c\u4ece\u7a7a\u4e2d\u98de\u4e86\u8fc7\u53bb\uff0c\u63a0\u8fc7\u87fe\u870d\u65f6\u5728\u5b83\u80cc\u4e0a\u72e0\u72e0\u54ac\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u9752\u9752\u53f9\u9053\uff1a\u201c\u8fd9\u5c0f\u4e1c\u897f\u7adf\u7136\u4e5f\u4f1a\u7528\u667a\u3002\u201d\u87fe\u870d\u6025\u5fd9\u8f6c\u8eab\uff0c\u8718\u86db\u65e9\u5df2\u98de\u8fc7\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u87fe\u870d\u8eab\u4e0a\u86db\u6bd2\u53d1\u4f5c\uff0c\u4ef0\u9762\u671d\u5929\uff0c\u9732\u51fa\u4e86\u4e00\u4e2a\u5927\u767d\u809a\u5b50\uff0c\u6b7b\u5728\u76d8\u4e2d\u3002 \u3000\u3000\u6bd2\u8718\u86db\u6251\u4e0a\u8eab\u53bb\uff0c\u5f20\u53e3\u54ac\u56bc\u3002\u8fd9\u8fb9\u90a3\u9752\u86c7\u6b63\u88ab\u8708\u86a3\u8d76\u5f97\u7ed5\u76d8\u6025\u9003\uff0c\u6e38\u8fc7\u87fe\u870d\u8eab\u8fb9\u65f6\uff0c\u5ffd\u5730\u6602\u9996\uff0c\u5f20\u53e3\u628a\u6bd2\u8718\u86db\u541e\u5165\u809a\u5185\uff0c\u8ddf\u7740\u54ac\u4f4f\u4e86\u87fe\u870d\u3002\u8708\u86a3\u4ece\u4fa7\u62a2\u4e0a\uff0c\u53e3\u4e2d\u4e00\u5bf9\u6bd2\u94b3\u7262\u7262\u94b3\u4f4f\u87fe\u870d\uff0c\u53cc\u65b9\u518d\u529b\u62c9\u626f\u3002\u62c9\u4e86\u4e00\u9635\uff0c\u9752\u86c7\u529b\u6e10\u4e0d\u654c\uff0c\u88ab\u8708\u86a3\u4e00\u8def\u626f\u4e86\u8fc7\u53bb\u3002\u9752\u86c7\u60f3\u8981\u6487\u4e0b\u87fe\u870d\u9003\u751f\uff0c\u54ea\u77e5\u5b83\u53e3\u5185\u751f\u7684\u90fd\u662f\u5012\u7259\uff0c\u94a9\u5b50\u5411\u5185\uff0c\u65e2\u54ac\u4f4f\u4e86\u98df\u7269\uff0c\u53ea\u80fd\u5411\u5185\u541e\u8fdb\uff0c\u8bf4\u751a\u4e48\u4e5f\u5410\u4e0d\u51fa\u6765\uff0c\u60f3\u9003\u4e0d\u5f97\uff0c\u767b\u65f6\u72fc\u72c8\u4e07\u5206\u3002 \u3000\u3000\u6c99\u76d8\u5468\u56f4\u7684\u4e94\u5f1f\u5b50\u89c1\u80dc\u8d1f\u5df2\u5206\uff0c\u5404\u5f52\u539f\u4f4d\u3002\u4e0d\u4e00\u523b\uff0c\u8708\u86a3\u5c06\u9752\u86c7\u54ac\u6b7b\uff0c\u5728\u9752\u86c7\u548c\u87fe\u870d\u8eab\u4e0a\u5438\u6bd2\uff0c\u7136\u540e\u6e38\u884c\u4e00\u5468\uff0c\u6602\u7136\u81ea\u5f97\u3002 \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u8fd9\u8708\u86a3\u5438\u4e86\u56db\u6bd2\u7684\u6bd2\u8d28\uff0c\u5df2\u6210\u5927\u5723\uff0c\u5bfb\u5e38\u6bd2\u7269\u518d\u591a\uff0c\u4e5f\u4e0d\u662f\u5b83\u654c\u624b\u4e86\u3002\u201d\u89c1\u8881\u627f\u5fd7\u6709\u4e0d\u4fe1\u4e4b\u8272\uff0c\u5bf9\u84dd\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u53d6\u4e9b\u9752\u513f\u6765\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u5165\u5185\uff0c\u6349\u4e86\u4e03\u6761\u9752\u86c7\u51fa\u6765\uff0c\u653e\u5728\u76d8\u5185\u3002\u90a3\u8708\u86a3\u5431\u5431\u5431\u7684\u8f7b\u53eb\u6570\u58f0\uff0c\u6251\u4e0a\u53bb\u8981\u54ac\u3002\u4e03\u6761\u9752\u86c7\u8054\u6210\u4e00\u5708\uff0c\u4e03\u4e2a\u5934\u5411\u5916\u62b5\u5fa1\u5916\u654c\uff0c\u8eab\u5b50\u5374\u53e0\u5728\u4e00\u8d77\uff0c\u8708\u86a3\u4e00\u65f6\u5012\u4e5f\u653b\u4e0d\u8fdb\u53bb\u3002 \u3000\u3000\u8fd9\u822c\u6765\u56de\u653b\u5b88\u51e0\u4e2a\u56de\u5408\uff0c\u4e00\u6761\u9752\u86c7\u88ab\u8708\u86a3\u94b3\u4f4f\u5934\u9888\uff0c\u626f\u4e86\u51fa\u6765\uff0c\u7fa4\u86c7\u4e00\u9f50\u60b2\u9e23\u3002\u8708\u86a3\u54ac\u6b7b\u9752\u86c7\uff0c\u53c8\u5411\u7fa4\u86c7\u653b\u51fb\u3002 \u3000\u3000\u9526\u8863\u6bd2\u4e10\u9f50\u4e91\u7488\u5ffd\u4ece\u73ed\u4e2d\u51fa\u6765\uff0c\u5728\u4f55\u94c1\u624b\u9762\u524d\u5c48\u4e0b\u4e00\u819d\u8dea\u5012\uff0c\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u91d1\u513f\u52a8\u4e2a\u4e0d\u4f11\uff0c\u4e0d\u653e\u51fa\u6765\u53ea\u6015\u4e0d\u59a5\u3002\u201d\u4f55\u94c1\u624b\u79c0\u7709\u4e00\u76b1\u9053\uff1a\u201c\u5b83\u5c31\u7231\u591a\u4e8b\uff0c\u597d\u5427\uff01\u201d\u9f50\u4e91\u7488\u4ece\u6000\u91cc\u53d6\u51fa\u94c1\u7ba1\uff0c\u62d4\u5f00\u585e\u5b50\uff0c\u628a\u76ee\u524d\u5728\u96ea\u5730\u91cc\u6349\u6765\u7684\u91d1\u86c7\u653e\u5165\u6c99\u76d8\u3002 \u3000\u3000\u91d1\u86c7\u4e00\u51fa\u94c1\u7ba1\uff0c\u5ffd\u5730\u8dc3\u8d77\uff0c\u6321\u5728\u7fa4\u86c7\u9762\u524d\u3002\u8708\u86a3\u7acb\u5373\u540e\u9000\u3002\u7fa4\u86c7\u89c1\u6765\u4e86\u6551\u661f\uff0c\u7f29\u6210\u4e00\u56e2\u3002\u91d1\u86c7\u8eab\u8eaf\u867d\u5c0f\uff0c\u5374\u662f\u7075\u6d3b\u5f02\u5e38\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u89c1\u8fc7\u91d1\u86c7\u7684\u672c\u9886\uff0c\u77e5\u9053\u8708\u86a3\u8fdc\u975e\u5176\u654c\uff0c\u679c\u7136\u6597\u4e0d\u591a\u65f6\uff0c\u8708\u86a3\u4fbf\u88ab\u4e00\u53e3\u54ac\u6b7b\u3002\u7fa4\u86c7\u56f4\u4f4f\u4e86\u91d1\u86c7\uff0c\u8eab\u5b50\u4e0d\u4f4f\u6328\u64e6\uff0c\u4f3c\u4e4e\u611f\u8c22\u6551\u547d\u4e4b\u6069\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u866b\u8c78\u4e4b\u4e2d\u4e5f\u6709\u4fa0\u58eb\uff01\u201d\u9752\u9752\u5728\u8881\u627f\u5fd7\u8033\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u8981\u8fd9\u6761\u91d1\u86c7\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5b69\u5b50\u8bdd\uff0c\u4eba\u5bb6\u600e\u80af\u7ed9\u4f60\uff1f\u201d\u9752\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u7239\u7239\u5916\u53f7\u53eb\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\uff01\u96be\u9053\u4ed6\u5f53\u771f\u4e0e\u8fd9\u91d1\u86c7\u6709\u751a\u4e48\u7275\u8fde\uff1f\u201d \u3000\u3000\u201c\u91d1\u86c7\u90ce\u541b\u201d\u56db\u5b57\u8bf4\u5f97\u5927\u58f0\u4e86\u4e9b\uff0c\u90a3\u8001\u4e5e\u5a46\u672c\u6765\u4e00\u76f4\u76ee\u4e0d\u8f6c\u775b\u7684\u671b\u7740\u9752\u9752\uff0c\u4e00\u542c\u5230\u8fd9\u56db\u5b57\uff0c\u7a81\u4ece\u73ed\u4e2d\u8df3\u4e86\u51fa\u6765\uff0c\u4f38\u51fa\u53cc\u624b\uff0c\u6293\u5411\u5979\u80a9\u5934\uff0c\u559d\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u662f\u4f60\u751a\u4e48\u4eba\uff1f\u201d\u5979\u76f8\u8c8c\u5947\u4e11\uff0c\u58f0\u97f3\u5374\u662f\u6e05\u8106\u52a8\u542c\u3002\u9752\u9752\u5403\u4e86\u4e00\u60ca\uff0c\u8df3\u5f00\u4e00\u6b65\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u9661\u7136\u95f4\u8863\u895f\u5e26\u98ce\uff0c\u6559\u4e3b\u4f55\u94c1\u624b\u8eab\u65c1\u4e24\u4eba\u4e00\u8dc3\u800c\u524d\uff0c\u7ad9\u5728\u8001\u4e5e\u5a46\u4e24\u4fa7\uff0c\u540c\u58f0\u53eb\u9053\uff1a\u201c\u90a3\u59d3\u590f\u7684\u5c0f\u5b50\u5728\u54ea\u91cc\uff1f\u201d\u8881\u627f\u5fd7\u89c1\u8fd9\u4e24\u4eba\u7684\u8eab\u5f62\u5fae\u6643\uff0c\u4fbf\u500f\u7136\u4e0a\u524d\u534a\u4e08\uff0c\u6b66\u529f\u751a\u9ad8\u3002\u8fd9\u4e24\u4eba\u4e00\u4e2a\u53c8\u9ad8\u53c8\u7626\uff0c\u53e6\u4e00\u4e2a\u4e2d\u7b49\u8eab\u6750\uff0c\u9762\u5bb9\u9edd\u9ed1\uff0c\u4f3c\u662f\u4e2a\u5bfb\u5e38\u4e61\u4e0b\u4eba\u3002\u4e24\u4eba\u90fd\u662f\u4e94\u5341\u5c81\u5de6\u53f3\u5e74\u7eaa\u3002 \u3000\u3000\u9752\u9752\u4ee5\u524d\u56e0\u8eab\u4e16\u4e0d\u660e\uff0c\u5e38\u5f15\u4ee5\u4e3a\u803b\uff0c\u4f46\u81ea\u542c\u6bcd\u4eb2\u8bf4\u4e86\u5f53\u5e74\u7684\u7ecf\u8fc7\u4e4b\u540e\uff0c\u5bf9\u7236\u4eb2\u4f69\u670d\u5f97\u4e86\u4e0d\u5f97\uff0c\u5f53\u4e0b\u6602\u7136\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u662f\u6211\u7239\u7239\uff0c\u4f60\u4eec\u95ee\u4ed6\u5e72\u4e48\uff1f\u201d \u3000\u3000\u8001\u4e5e\u5a46\u4ef0\u5934\u957f\u7b11\uff0c\u58f0\u97f3\u51c4\u5389\uff0c\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\uff0c\u53eb\u9053\uff1a\u201c\u4ed6\u5c45\u7136\u6ca1\u6b7b\uff0c\u8fd8\u7559\u4e0b\u4e86\u4f60\u8fd9\u5b7d\u79cd\uff01\u201d\u90a3\u7626\u957f\u5b50\u559d\u9053\uff1a\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9752\u9752\u4e0b\u5df4\u4e00\u626c\u9053\uff1a\u201c\u4e3a\u751a\u4e48\u8981\u5bf9\u4f60\u4eec\u8bf4\uff1f\u201d \u3000\u3000\u8001\u4e5e\u5a46\u53cc\u7709\u7ad6\u8d77\uff0c\u4e24\u624b\u731b\u5411\u9752\u9752\u8138\u4e0a\u6293\u6765\u3002\u8fd9\u4e00\u4e0b\u53d1\u96be\u4e8b\u8d77\u4ed3\u5352\uff0c\u9752\u9752\u4e0d\u53ca\u8eb2\u907f\uff0c\u773c\u89c1\u8001\u4e5e\u5a46\u5957\u7740\u660e\u6643\u6643\u94a2\u5957\u7684\u5c16\u5c16\u5341\u6307\uff0c\u4fbf\u8981\u89e6\u5230\u9752\u9752\u96ea\u767d\u7c89\u5ae9\u7684\u8138\u988a\uff0c\u8881\u627f\u5fd7\u53f3\u624b\u8863\u8896\u5411\u4e0b\u4e00\u6325\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u51fb\u4e2d\u8001\u4e5e\u5a46\u53cc\u81c2\u4e2d\u95f4\uff0c\u4e58\u52bf\u4e00\u5377\u4e00\u9001\u3002\u8001\u4e5e\u5a46\u8eab\u4e0d\u7531\u4e3b\uff0c\u5411\u540e\u7ffb\u4e86\u4e2a\u7b4b\u6597\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u5750\u5728\u5730\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u4e94\u6bd2\u6559\u4f17\u4eba\u76f8\u987e\u9a87\u7136\uff0c\u8001\u4e5e\u5a46\u4f55\u7ea2\u836f\u662f\u6559\u4e2d\u7684\u9ad8\u624b\uff0c\u6bd4\u6559\u4e3b\u4f55\u94c1\u624b\u8fd8\u9ad8\u7740\u4e00\u8f88\uff0c\u600e\u4e48\u8fd9\u4e2a\u8c8c\u4e0d\u60ca\u4eba\u7684\u5c11\u5e74\u4e00\u51fa\u624b\uff0c\u5c31\u5982\u6b64\u8f7b\u6613\u7684\u5c06\u5979\u6454\u4e86\u4e2a\u7b4b\u6597\uff1f \u3000\u3000\u7626\u957f\u5b50\u6f58\u79c0\u8fbe\u548c\u90a3\u4e2a\u4e61\u4e0b\u4eba\u822c\u7684\u5c91\u5176\u65af\u662f\u4e94\u6bd2\u6559\u7684\u5de6\u53f3\u62a4\u6cd5\uff0c\u4e24\u4eba\u76f8\u987e\uff0c\u70b9\u4e00\u70b9\u5934\u3002\u6f58\u79c0\u8fbe\u9053\uff1a\u201c\u6211\u6765\u9886\u6559\u3002\u201d\u53cc\u638c\u4e00\u6446\uff0c\u7f13\u6b65\u4e0a\u524d\u3002 \u3000\u3000\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u6211\u63a5\u4ed6\u7684\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6c99\u5144\uff0c\u7528\u6247\u5b50\u3002\u4ed6\u624b\u6307\u4e0a\u6709\u5c16\u73af\uff0c\u8fd9\u4e5f\u7b97\u662f\u5175\u5668\uff01\u201d\u6c99\u5929\u5e7f\u5c55\u5f00\u9634\u9633\u6247\uff0c\u4fbf\u4e0e\u6f58\u79c0\u8fbe\u6597\u5728\u4e00\u8d77\u3002\u8fd9\u8fb9\u54d1\u5df4\u4e0e\u5c91\u5176\u65af\u9ed8\u4e0d\u4f5c\u58f0\u7684\u62f3\u6253\u8db3\u8e22\uff0c\u65e9\u5df2\u6597\u5f97\u706b\u70bd\u3002\u4e94\u6bd2\u6559\u4f17\u4eba\u4e00\u62e5\u800c\u4e0a\u3002\u80e1\u6842\u5357\u3001\u94c1\u7f57\u6c49\u3001\u9752\u9752\u5404\u51fa\u5175\u5203\u63a5\u6218\u3002 \u3000\u3000\u8001\u4e5e\u5a46\u4f55\u7ea2\u836f\u52bf\u5982\u75af\u864e\uff0c\u76f4\u5f80\u9752\u9752\u8eab\u8fb9\u5954\u6765\u3002\u8881\u627f\u5fd7\u77e5\u9053\u6b64\u4eba\u4e0b\u624b\u6bd2\u8fa3\uff0c\u4e0d\u53ef\u8ba9\u5979\u63a5\u8fd1\u9752\u9752\uff0c\u7b49\u5979\u5954\u8fd1\uff0c\u5ffd\u7136\u8dc3\u51fa\uff0c\u4f38\u624b\u6293\u4f4f\u5979\u540e\u5fc3\uff0c\u63d0\u8d77\u6765\u63bc\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f55\u94c1\u624b\u7c89\u8138\u4e00\u6c89\uff0c\u4f38\u51fa\u53f3\u624b\u98df\u6307\uff0c\u653e\u5728\u624b\u4e2d\u5618\u6e9c\u6e9c\u7684\u4e00\u5439\u3002\u4e94\u6bd2\u6559\u6559\u4f17\u7acb\u5373\u540c\u65f6\u9000\u5f00\u3002\u4f17\u4eba\u6251\u4e0a\u65f6\u52bf\u9053\u6781\u731b\uff0c\u9000\u4e0b\u53bb\u4e5f\u771f\u8fc5\u6377\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u4eba\u4eba\u53c8\u90fd\u5728\u6559\u4e3b\u8eab\u65c1\u6574\u6574\u9f50\u9f50\u7684\u6392\u6210\u4e24\u5217\u3002\u4f55\u94c1\u624b\u8138\u9732\u5fae\u7b11\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8881\u76f8\u516c\u6a21\u6837\u65af\u6587\uff0c\u5374\u539f\u6765\u8eab\u8d1f\u7edd\u6280\uff0c\u8ba9\u6211\u9886\u6559\u51e0\u62db\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8d35\u6559\u5404\u4f4d\u670b\u53cb\u6211\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u77e5\u751a\u4e48\u5730\u65b9\u5f00\u7f6a\u5404\u4f4d\uff0c\u8fd8\u8bf7\u660e\u8a00\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u8138\u4e0a\u4e00\u7ea2\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u4eec\u7684\u4e8b\u672c\u6765\u53ea\u8ddf\u5b98\u5e9c\u6709\u5173\uff0c\u8881\u76f8\u516c\u4e0d\u660e\u4e2d\u95f4\u7684\u9053\u7406\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u8fd9\u65f6\u5ffd\u7136\u6709\u91d1\u86c7\u90ce\u541b\u7275\u6d89\u5728\u5185\uff0c\u8bf7\u95ee\u91d1\u86c7\u90ce\u541b\u773c\u4e0b\u662f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9752\u9752\u4e00\u62c9\u8881\u627f\u5fd7\u7684\u624b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u522b\u5bf9\u5979\u8bf4\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6559\u4e3b\u8ddf\u91d1\u86c7\u90ce\u541b\u76f8\u8bc6\u4e48\uff1f\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4ed6\u8ddf\u655d\u6559\u5f88\u6709\u6e0a\u6e90\uff0c\u5bb6\u7236\u5c31\u662f\u56e0\u4ed6\u800c\u5f52\u5929\u7684\u3002\u655d\u6559\u6559\u4f17\u4e07\u4f59\u4eba\uff0c\u6ca1\u4e00\u4e2a\u4e0d\u60f3\u627e\u4ed6\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u9752\u9752\u4e00\u60ca\uff0c\u5747\u60f3\u91d1\u86c7\u90ce\u541b\u884c\u4e8b\u4e0d\u53ef\u4ee5\u5e38\u7406\u6d4b\u5ea6\uff0c\u5230\u5904\u6811\u654c\uff0c\u4e94\u6bd2\u6559\u6068\u4ed6\u5165\u9aa8\uff0c\u4e5f\u975e\u5947\u4e8b\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u79bb\u6b64\u4e07\u91cc\uff0c\u53ea\u6015\u5404\u4f4d\u6c38\u8fdc\u627e\u4ed6\u4e0d\u7740\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u90a3\u4e48\u628a\u4ed6\u516c\u5b50\u7559\u4e0b\u6765\uff0c\u5148\u796d\u4e86\u5148\u7236\u518d\u8bf4\u3002\u201d\u5979\u8bf4\u8bdd\u65f6\u8f7b\u98a6\u6d45\u7b11\uff0c\u795e\u6001\u817c\u8146\uff0c\u4fbf\u662f\u4e2a\u7f9e\u4eba\u7b54\u7b54\u7684\u5c11\u5973\u4e00\u822c\uff0c\u53ef\u662f\u8bf4\u51fa\u8bdd\u6765\u5374\u662f\u72e0\u6bd2\u4e4b\u6781\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e38\u8a00\u9053\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\u3002\u5404\u4f4d\u65e2\u8ddf\u91d1\u86c7\u90ce\u541b\u6709\u6881\u5b50\uff0c\u8fd8\u662f\u53bb\u627e\u4ed6\u672c\u4eba\u4e3a\u662f\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u5148\u7236\u8fc7\u4e16\u4e4b\u65f6\uff0c\u5c0f\u59b9\u8fd8\u53ea\u4e09\u5c81\u3002\u4e8c\u5341\u5e74\u6765\uff0c\u54ea\u91cc\u627e\u5f97\u7740\u8fd9\u4f4d\u524d\u8f88\uff1f\u82e5\u662f\u628a\u4ed6\u516c\u5b50\u6263\u5728\u8fd9\u91cc\uff0c\u4ed6\u8001\u4eba\u5bb6\u81ea\u7136\u4f1a\u5bfb\u627e\u524d\u6765\u3002\u54b1\u4eec\u8fc7\u53bb\u7684\u4e8b\uff0c\u5c31\u53ef\u4ece\u5934\u7b97\u4e00\u7b97\u4e86\u3002\u201d \u3000\u3000\u9752\u9752\u53eb\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u4e5f\u60f3\uff1f\u6211\u7239\u7239\u82e5\u662f\u5230\u6765\uff0c\u7ba1\u6559\u628a\u4f60\u4eec\u4e00\u4e2a\u4e2a\u90fd\u6740\u4e86\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u8f6c\u5934\u95ee\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u50cf\u4ed6\u7239\u7239\u5417\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u76f8\u8c8c\u5f88\u50cf\uff0c\u9a84\u50b2\u7684\u795e\u6c14\u4e5f\u5dee\u4e0d\u591a\u3002\u201d\u4f55\u94c1\u624b\u7ec6\u58f0\u7ec6\u6c14\u7684\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u5404\u4f4d\u8bf7\u4fbf\u5427\u3002\u6211\u4eec\u53ea\u7559\u4e0b\u8fd9\u4f4d\u590f\u516c\u5b50\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u5bfb\u601d\uff1a\u201c\u4ed6\u4eec\u53ea\u8ddf\u9752\u5f1f\u4e00\u4eba\u8fc7\u4e0d\u53bb\u3002\u6b64\u5904\u60c5\u52bf\u9669\u6076\uff0c\u6211\u5148\u628a\u5979\u9001\u51fa\u53bb\u518d\u8bf4\uff0c\u522b\u4eba\u7eb5\u4f7f\u6682\u65f6\u4e0d\u80fd\u8131\u9669\uff0c\u4e5f\u65e0\u5927\u788d\u3002\u201d\u4e8e\u662f\u4f5c\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u518d\u89c1\u4e86\u3002\u201d\u8bed\u58f0\u65b9\u6bd5\uff0c\u5de6\u624b\u5df2\u62e6\u8170\u62b1\u4f4f\u9752\u9752\uff0c\u5954\u5230\u5899\u8fb9\u3002\u5899\u57a3\u751a\u9ad8\uff0c\u4ed6\u62b1\u4e86\u9752\u9752\u540e\uff0c\u66f4\u52a0\u4e0d\u80fd\u4e00\u8dc3\u800c\u4e0a\uff0c\u6258\u4f4f\u5979\u8eab\u5b50\u5411\u4e0a\u629b\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u7559\u795e\uff01\u201d \u3000\u3000\u4e94\u6bd2\u6559\u4f17\u4eba\u9f50\u58f0\u6012\u558a\uff0c\u6697\u5668\u7eb7\u5c04\u3002\u8881\u627f\u5fd7\u8863\u8896\u98de\u821e\uff0c\u53ee\u53ee\u5f53\u5f53\u4e00\u9635\u4e71\u54cd\uff0c\u6697\u5668\u90fd\u88ab\u6253\u843d\u3002\u9752\u9752\u53cc\u624b\u5df2\u6293\u4f4f\u5899\u5934\uff0c\u6b63\u8981\u8e0a\u8eab\u5916\u8dc3\uff0c\u4f55\u94c1\u624b\u500f\u5730\u79bb\u5ea7\uff0c\u5de6\u638c\u731b\u5730\u5411\u8881\u627f\u5fd7\u9762\u95e8\u51fb\u5230\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u8eab\u5f62\u752b\u52a8\uff0c\u4e00\u80a1\u75be\u98ce\u4fbf\u5df2\u6251\u81f3\u9f3b\u7aef\uff0c\u5feb\u901f\u4e4b\u6781\uff0c\u4ee5\u5982\u6b64\u5a07\u5f31\u5973\u513f\u800c\u6709\u8fd9\u822c\u8eab\u624b\uff0c\u4e0d\u7981\u60ca\u4f69\uff0c\u559d\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u4e0a\u8eab\u5411\u540e\u6597\u7f29\u534a\u5c3a\uff0c\u5374\u89c1\u51fb\u5230\u9762\u524d\u7684\u7adf\u662f\u9ed1\u6c89\u6c89\u7684\u4e00\u53ea\u94c1\u94a9\uff0c\u66f4\u662f\u5403\u60ca\u3002\u4f55\u94c1\u624b\u53f3\u624b\u5fae\u6325\uff0c\u4e00\u53ea\u91d1\u73af\u79bb\u8155\u98de\u4e0a\u5899\u5934\uff0c\u559d\u9053\uff1a\u201c\u4e0b\u6765\uff01\u201d\u9752\u9752\u987f\u89c9\u5de6\u817f\u5267\u75db\uff0c\u624b\u4e00\u677e\uff0c\u8dcc\u4e0b\u5899\u6765\u3002\u4f55\u7ea2\u836f\u602a\u58f0\u957f\u7b11\uff0c\u4e94\u679a\u94a2\u5957\u5ffd\u79bb\u6307\u5c16\uff0c\u5411\u5979\u8eab\u4e0a\u5c04\u53bb\u3002 \u3000\u3000\u8fd9\u9877\u523b\u4e4b\u95f4\uff0c\u8881\u627f\u5fd7\u5df2\u548c\u4f55\u94c1\u624b\u62c6\u4e86\u4e94\u62db\u3002\u4e24\u4eba\u653b\u5b88\u90fd\u662f\u8fc5\u75be\u4e4b\u81f3\u3002\u4ed6\u767e\u5fd9\u4e2d\u89c1\u9752\u9752\u52bf\u5371\uff0c\u4e00\u628a\u94dc\u94b1\u63b7\u51fa\uff0c\u94ee\u94ee\u94ee\u54cd\u58f0\u8fc7\u53bb\uff0c\u4f55\u7ea2\u836f\u7684\u4e94\u679a\u94a2\u5957\u90fd\u88ab\u6253\u843d\u5728\u5730\u3002 \u3000\u3000\u4f55\u94c1\u624b\u5a07\u559d\u4e00\u58f0\uff1a\u201c\u597d\u4fca\u529f\u592b\uff01\u201d\u5de6\u624b\u8fde\u8fdb\u4e24\u94a9\u3002\u8881\u627f\u5fd7\u770b\u6e05\u695a\u5979\u53f3\u624b\u767d\u817b\u5982\u8102\uff0c\u4e94\u679a\u5c16\u5c16\u7684\u6307\u7532\u4e0a\u8fd8\u643d\u7740\u7c89\u7ea2\u7684\u51e4\u4ed9\u82b1\u6c41\uff0c\u4e00\u638c\u5288\u6765\uff0c\u638c\u98ce\u4e2d\u5e26\u7740\u4e00\u9635\u6d53\u9999\uff0c\u4f46\u5de6\u624b\u624b\u638c\u5374\u5df2\u5272\u53bb\uff0c\u8155\u4e0a\u88c5\u4e86\u4e00\u53ea\u94c1\u94a9\u3002\u8fd9\u94c1\u94a9\u94f8\u4f5c\u7ea4\u7ea4\u5973\u624b\u4e4b\u5f62\uff0c\u4e94\u722a\u5c16\u5229\uff0c\u4f7f\u52a8\u65f6\u9501\u3001\u6253\u3001\u62c9\u3001\u6233\uff0c\u864e\u864e\u751f\u98ce\uff0c\u7075\u6d3b\u7edd\u4e0d\u5728\u8089\u638c\u4e4b\u4e0b\u3002\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u6c99\u5144\uff0c\u4f60\u4eec\u5feb\u593a\u8def\u51fa\u53bb\u3002\u201d\u6b64\u65f6\u4e94\u6bd2\u6559\u6559\u4f17\u65e9\u5df2\u7f20\u4f4f\u6c99\u5929\u5e7f\u7b49\u4eba\u62da\u6597\uff0c\u91cd\u56f4\u4e4b\u4e0b\uff0c\u5374\u54ea\u91cc\u62a2\u5f97\u51fa\u53bb\uff1f \u3000\u3000\u8881\u627f\u5fd7\u4e4d\u9047\u52b2\u654c\uff0c\u7cbe\u795e\u9661\u957f\uff0c\u4f0f\u864e\u638c\u6cd5\u65bd\u5c55\u5f00\u6765\uff0c\u5a01\u4e0d\u53ef\u5f53\u3002 \u3000\u3000\u4f55\u94c1\u624b\u6b66\u529f\u522b\u5177\u4e00\u683c\uff0c\u867d\u7136\u4e5f\u662f\u62f3\u6253\u8db3\u8e22\uff0c\u638c\u5288\u94a9\u523a\uff0c\u4f46\u62f3\u6253\u591a\u865a\u800c\u638c\u6309\u4ff1\u5b9e\uff0c\u6709\u65f6\u5374\u53c8\u4e00\u638c\u8f7b\u8f7b\u7684\u637a\u6765\uff0c\u5168\u65e0\u52b2\u9053\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53ea\u9053\u5979\u638c\u4e0b\u7559\u60c5\uff0c\u4e0d\u4f7f\u6740\u7740\uff0c\u4e8e\u662f\u53d1\u638c\u4e4b\u65f6\u4e5f\u7a0d\u7559\u4f59\u5730\uff0c\u9163\u6597\u4e2d\u65f6\u65f6\u56de\u987e\u9752\u9752\uff0c\u89c1\u5979\u5750\u5728\u5730\u4e0b\uff0c\u59cb\u7ec8\u7ad9\u4e0d\u8d77\u6765\uff0c\u5f53\u4e0b\u62a2\u653b\u6570\u62db\uff0c\u628a\u4f55\u94c1\u624b\u903c\u9000\u6570\u6b65\uff0c\u7eb5\u8fc7\u53bb\u6276\u5979\u7ad9\u8d77\u3002 \u3000\u3000\u731b\u542c\u5f97\u556a\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u94c1\u7f57\u6c49\u548c\u5c91\u5176\u65af\u56db\u638c\u76f8\u5bf9\uff0c\u5404\u81ea\u9707\u5f00\u3002\u94c1\u7f57\u6c49\u5927\u53eb\u4e00\u58f0\uff0c\u4e0a\u524d\u518d\u653b\uff0c\u62c6\u4e0d\u6570\u62db\uff0c\u624b\u638c\u6e10\u80bf\u3002\u4ed6\u53c8\u6c14\u53c8\u6025\uff0c\u5927\u58f0\u56b7\u9053\uff1a\u201c\u8fd9\u4e9b\u5bb6\u4f19\u638c\u4e0a\u6709\u6bd2\uff0c\u522b\u7740\u4e86\u9053\u513f\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8fd9\u624d\u7701\u609f\uff0c\u539f\u6765\u4e94\u6bd2\u6559\u4f17\u7ec3\u5c31\u4e86\u6bd2\u638c\uff0c\u53ea\u8981\u624b\u638c\u6cbe\u4f53\uff0c\u4fbf\u5373\u4e2d\u6bd2\uff0c\u4f55\u94c1\u624b\u638c\u6cd5\u8f7b\u67d4\uff0c\u5176\u5b9e\u662f\u5728\u8bf1\u81ea\u5df1\u4e0a\u5f53\uff0c\u7528\u5fc3\u9634\u6bd2\uff0c\u51b3\u975e\u6709\u610f\u5bb9\u8ba9\uff0c\u773c\u89c1\u60c5\u52bf\u8d8a\u6765\u8d8a\u7d27\uff0c\u5fc3\u60f3\u5982\u4e0d\u7acb\u65f6\u51b2\u51fa\uff0c\u81ea\u5df1\u867d\u53ef\u8131\u8eab\uff0c\u4f59\u4eba\u53ea\u6015\u90fd\u8981\u846c\u8eab\u5728\u8fd9\u6bd2\u7a9f\u4e4b\u4e2d\u3002 \u3000\u3000\u4f55\u94c1\u624b\u89c1\u4ed6\u6276\u8d77\u9752\u9752\uff0c\u4e0d\u5bb9\u4ed6\u518d\u53bb\u6551\u94c1\u7f57\u6c49\uff0c\u8eab\u6cd5\u5feb\u6377\uff0c\u5982\u4e00\u9635\u98ce\u822c\u6b3a\u8fd1\u8eab\u6765\u3002\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u4f55\u6559\u4e3b\uff0c\u5728\u4e0b\u8ddf\u4f60\u5f80\u65e5\u65e0\u6028\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u4f55\u4ee5\u5982\u6b64\u82e6\u82e6\u76f8\u903c\uff1f\u4f60\u4e0d\u653e\u6211\u4eec\u8d70\uff0c\u83ab\u602a\u65e0\u793c\u3002\u201d\u4f55\u94c1\u624b\u4e00\u7b11\uff0c\u8138\u4e0a\u9732\u51fa\u4e24\u4e2a\u9152\u6da1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53ea\u7559\u590f\u516c\u5b50\uff0c\u5c0a\u9a7e\u5c31\u8bf7\u4fbf\u5427\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5de6\u8db3\u6a2a\u626b\uff0c\u53f3\u638c\u547c\u7684\u4e00\u58f0\u8fce\u9762\u5288\u53bb\uff0c\u4f55\u94c1\u624b\u4f38\u53f3\u624b\u6321\u67b6\uff0c\u731b\u89c1\u8881\u627f\u5fd7\u8fd9\u4e00\u638c\u6765\u52bf\u5947\u52b2\uff0c\u82e5\u662f\u53cc\u638c\u76f8\u4ea4\uff0c\u5373\u4f7f\u5bf9\u65b9\u4e2d\u6bd2\uff0c\u81ea\u5df1\u7684\u624b\u638c\u4e5f\u975e\u6298\u65ad\u4e0d\u53ef\u3002\u77ac\u606f\u95f4\u624b\u638c\u53d8\u6307\uff0c\u5fae\u5fae\u5411\u4e0a\u4e00\u62ac\uff0c\u5f84\u70b9\u8881\u627f\u5fd7\u53f3\u81c2\u201c\u66f2\u6c60\u7a74\u201d\u3002\u8fd9\u4e00\u6307\u53d8\u5f97\u5feb\uff0c\u70b9\u5f97\u51c6\uff0c\u7684\u662f\u9ad8\u62db\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u597d\u6307\u6cd5\uff01\u201d\u5de6\u638c\u659c\u524a\u654c\u9888\u3002\u4ed6\u77e5\u4f55\u94c1\u624b\u867d\u7136\u638c\u4e0a\u6709\u6bd2\uff0c\u5374\u5bb3\u6015\u81ea\u5df1\u638c\u529b\uff0c\u5f53\u4e0b\u62f3\u6cd5\u4e00\u53d8\uff0c\u4f7f\u51fa\u5e08\u95e8\u7edd\u827a\u201c\u7834\u7389\u62f3\u201d\u6765\u3002\u8fd9\u8def\u62f3\u6cd5\u62db\u62db\u529b\u5927\u52bf\u52b2\uff0c\u5218\u57f9\u751f\u53f7\u79f0\u201c\u4e94\u4e01\u624b\u201d\uff0c\u5c1a\u4e14\u6321\u4e0d\u4f4f\u4ed6\u4e94\u62db\u3002\u4f55\u94c1\u624b\u6b66\u529f\u867d\u9ad8\uff0c\u7a76\u662f\u5973\u6d41\uff0c\u89c1\u4ed6\u4e00\u62f3\u62f3\u6253\u6765\uff0c\u72b9\u5982\u94c1\u9524\u51fb\u5ca9\u3001\u5de8\u65a7\u5f00\u5c71\u4e00\u822c\uff0c\u54ea\u6562\u786c\u63a5\uff1f\u5979\u672c\u6765\u8138\u9732\u7b11\u5bb9\uff0c\u5f85\u89c1\u5bf9\u65b9\u62f3\u52bf\u5982\u6b64\u5a01\u731b\uff0c\u4e0d\u7981\u51db\u7136\u751f\u60e7\uff0c\u5c55\u5f00\u817e\u632a\u5c0f\u5de7\u4e4b\u6280\uff0c\u4e00\u5473\u6e38\u6597\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e58\u5979\u9000\u5f00\u534a\u6b65\u4e4b\u9645\uff0c\u5de6\u638c\u5411\u4e0a\u4e00\u62ac\uff0c\u53f3\u62f3\u731b\u7684\u201c\u77f3\u7834\u5929\u60ca\u201d\uff0c\u5411\u8eab\u65c1\u9526\u8863\u6bd2\u4e10\u9f50\u4e91\u7488\u8eab\u4e0a\u6253\u53bb\u3002\u9f50\u4e91\u7488\u53eb\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u5f20\u624b\u5411\u4ed6\u62f3\u4e0a\u62ff\u53bb\uff0c\u53ea\u8981\u624b\u6307\u7a0d\u6cbe\u4ed6\u62f3\u5934\uff0c\u5267\u6bd2\u4fbf\u4f20\u4e86\u8fc7\u53bb\u3002\u8881\u627f\u5fd7\u54ea\u5bb9\u4ed6\u624b\u6307\u78b0\u5230\uff0c\u8eab\u5b50\u4e00\u8e72\uff0c\u5de6\u624b\u53cd\u62ff\u4f4f\u4ed6\u7684\u8863\u8896\uff0c\u53f3\u8db3\u5f80\u4ed6\u811a\u4e0a\u4e00\u94a9\uff0c\u5de6\u8db3\u4e00\u817f\u5df2\u8e39\u5728\u4ed6\u53f3\u8db3\u819d\u76d6\u4e0b\u4e09\u5bf8\u5904\uff0c\u5580\u5587\u4e00\u58f0\uff0c\u9f50\u4e91\u7488\u819d\u76d6\u767b\u65f6\u8131\u81fc\uff0c\u59d4\u987f\u5728\u5730\u3002 \u3000\u3000\u80e1\u6842\u5357\u672c\u5728\u4e0e\u9f50\u4e91\u7488\u6fc0\u6597\uff0c\u767b\u65f6\u63f4\u51fa\u624b\u6765\uff0c\u5954\u53bb\u6551\u63f4\u88ab\u4e09\u654c\u56f4\u5728\u5793\u5fc3\u7684\u6c99\u5929\u5e7f\u3002\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u9000\u5230\u5899\u8fb9\uff0c\u6211\u6765\u6551\u4eba\uff01\u201d \u3000\u3000\u80e1\u6842\u5357\u4f9d\u8a00\u53cd\u8eab\uff0c\u5c06\u9752\u9752\u3001\u94c1\u7f57\u6c49\u3001\u5355\u94c1\u751f\u4e09\u4e2a\u4f24\u8005\u6276\u5230\u5899\u8fb9\u3002\u8881\u627f\u5fd7\u6e38\u76ee\u56db\u987e\uff0c\u89c1\u6c99\u5929\u5e7f\u4e0e\u54d1\u5df4\u5747\u662f\u4ee5\u4e00\u654c\u4e09\uff0c\u6c99\u5929\u5e7f\u5c24\u5176\u5371\u6025\uff0c\u5f53\u4e0b\u53cc\u817f\u5de6\u4e00\u811a\u53f3\u4e00\u811a\uff0c\u8e22\u98de\u4e86\u4e24\u540d\u4e94\u6bd2\u6559\u5f1f\u5b50\uff0c\u7eb5\u5165\u4eba\u4e1b\uff0c\u5580\u5580\u5580\u4e09\u58f0\uff0c\u56f4\u7740\u6c99\u5929\u5e7f\u7684\u4e09\u4eba\u5747\u5df2\u5173\u8282\u53d7\u635f\uff0c\u6216\u80a9\u5934\u8131\u69ab\uff0c\u6216\u5934\u9888\u626d\u66f2\uff0c\u6216\u624b\u8155\u62d7\u6298\u3002\u4ed6\u4e0d\u6b32\u591a\u4f24\u4eba\u4f17\uff0c\u53c8\u4e0d\u6562\u4e0e\u5bf9\u65b9\u6bd2\u638c\u63a5\u89e6\uff0c\u662f\u4ee5\u6bcf\u6b21\u5747\u662f\u8fc5\u5982\u95ea\u7535\u822c\u62a2\u8fd1\u8eab\u53bb\uff0c\u9694\u8863\u62ff\u4f4f\u5bf9\u65b9\u5173\u8282\uff0c\u4e00\u626d\u4e4b\u4e0b\uff0c\u654c\u4eba\u4e0d\u662f\u75db\u6655\u5012\u5730\uff0c\u4fbf\u662f\u52a8\u5f39\u4e0d\u5f97\u3002\u4ed6\u6551\u4e86\u6c99\u5929\u5e7f\u540e\uff0c\u518d\u62a2\u5230\u54d1\u5df4\u8eab\u65c1\u3002 \u3000\u3000\u54d1\u5df4\u62f3\u6cd5\u9887\u5f97\u534e\u5c71\u6d3e\u7684\u7cbe\u8981\uff0c\u529b\u654c\u4e09\u540d\u9ad8\u624b\uff0c\u867d\u7136\u8131\u8eab\u4e0d\u5f97\uff0c\u4e00\u65f6\u4e5f\u8fd8\u4e0d\u81f4\u843d\u8d25\u3002\u4f55\u94c1\u624b\u4e00\u58f0\u547c\u54e8\uff0c\u4e94\u6bd2\u6559\u4eba\u4f17\u9f50\u5411\u4e24\u4eba\u56f4\u6765\u3002\u8881\u627f\u5fd7\u4e1c\u4e00\u7a9c\uff0c\u897f\u4e00\u6643\uff0c\u7f20\u4f4f\u54d1\u5df4\u7684\u4e24\u4eba\u4e00\u4e2a\u4e0b\u989a\u8131\u843d\uff0c\u4e00\u4e2a\u81c2\u4e0a\u8131\u81fc\uff0c\u53e6\u4e00\u4e2a\u4e00\u5446\uff0c\u88ab\u54d1\u5df4\u5288\u9762\u4e00\u62f3\u6253\u5728\u9f3b\u6881\u4e4b\u4e0a\uff0c\u9c9c\u8840\u76f4\u6d41\u3002\u54d1\u5df4\u6253\u53d1\u4e86\u6027\uff0c\u8fd8\u8981\u8ffd\u6253\uff0c\u8881\u627f\u5fd7\u62c9\u4f4f\u4ed6\u624b\u81c2\uff0c\u62d6\u5230\u5899\u8fb9\uff0c\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u5feb\u8d70\uff0c\u6211\u6765\u5e94\u4ed8\u3002\u201d\u80e1\u6842\u5357\u5f53\u5373\u6e38\u4e0a\u9ad8\u5899\uff0c\u5c06\u4e00\u884c\u4eba\u4f17\u63a5\u5e94\u4e0a\u53bb\u3002\u8881\u627f\u5fd7\u5728\u5899\u4e0b\u6765\u56de\u6e38\u8d70\uff0c\u53c8\u6253\u5012\u4e86\u5341\u591a\u4e2a\u654c\u4eba\uff0c\u5411\u4f55\u94c1\u624b\u62f1\u624b\u9053\uff1a\u201c\u6559\u4e3b\u59d1\u5a18\uff0c\u518d\u89c1\u4e86\uff01\u201d\u54c8\u54c8\u957f\u7b11\uff0c\u80cc\u810a\u8d34\u5728\u5899\u4e0a\uff0c\u500f\u5ffd\u95f4\u6e38\u5230\u5899\u9876\u3002 \u3000\u3000\u8001\u4e5e\u5a46\u4f55\u7ea2\u836f\u5927\u53eb\u4e00\u58f0\uff0c\u4e94\u679a\u94a2\u5957\u5411\u4ed6\u4e0a\u4e2d\u4e0b\u4e09\u8def\u6253\u53bb\uff0c\u5fc3\u60f3\u4ed6\u8eab\u5728\u5899\u4e0a\uff0c\u5fc5\u7136\u96be\u4e8e\u95ea\u907f\u3002\u8881\u627f\u5fd7\u5de6\u8896\u4e00\u6325\uff0c\u4e94\u679a\u94a2\u5957\u5012\u8f6c\uff0c\u53cd\u5411\u4e94\u6bd2\u6559\u6559\u4f17\u6253\u6765\u3002\u4f55\u7ea2\u836f\u89c1\u4e86\u8fd9\u4e00\u624b\u53cd\u6325\u6697\u5668\u7684\u529f\u592b\uff0c\u5927\u53eb\uff1a\u201c\u4f60\u662f\u91d1\u86c7\u90ce\u541b\u7684\u5f1f\u5b50\u4e48\uff1f\u201d\u8bed\u97f3\u4e2d\u7adf\u4f3c\u8981\u54ed\u51fa\u6765\u4e00\u822c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u8ddf\u91d1\u86c7\u90ce\u541b\u5fc5\u6709\u6781\u6df1\u6e0a\u6e90\u3002\u201d\u5ff5\u5934\u8f6c\u5f97\u5feb\uff0c\u8eab\u6cd5\u66f4\u5feb\uff0c\u672a\u53ca\u5f20\u53e3\u56de\u7b54\uff0c\u65e9\u5df2\u7ffb\u51fa\u5899\u5916\u3002\u8fd9\u65f6\u54d1\u5df4\u7b49\u4eba\u5df2\u5954\u5230\u7b2c\u56db\u5c42\u9ec4\u5899\u4e4b\u4e0b\uff0c\u53ea\u542c\u5f97\u7ea2\u5899\u4e0a\u8f67\u8f67\u58f0\u54cd\uff0c\u9732\u51fa\u6570\u5c3a\u7a7a\u9699\uff0c\u8881\u627f\u5fd7\u8eab\u5b50\u5982\u7bad\u79bb\u5f26\uff0c\u76f4\u6251\u5230\u95e8\u53e3\uff0c\u53cc\u62f3\u6325\u51fa\uff0c\u5c06\u9996\u5148\u51b2\u51fa\u7684\u4e24\u540d\u6559\u5f92\u9524\u8fdb\u95e8\u5185\u3002\u4e24\u4eba\u51e0\u4e2a\u7b4b\u6597\uff0c\u76f4\u8dcc\u8fdb\u53bb\u3002 \u3000\u3000\u4f59\u4eba\u4e00\u65f6\u4e0d\u6562\u518d\u884c\u653b\u51fa\u3002 \u3000\u3000\u6f58\u79c0\u8fbe\u4e00\u58f0\u53f7\u4ee4\uff0c\u56db\u540d\u6559\u4f17\u4e3e\u8d77\u55b7\u7b52\uff0c\u56db\u80a1\u6bd2\u6c41\u731b\u5411\u8881\u627f\u5fd7\u8138\u4e0a\u55b7\u6765\u3002\u8881\u627f\u5fd7\u53ea\u611f\u8165\u81ed\u6251\u9f3b\uff0c\u6697\u53eb\u4e0d\u5999\uff0c\u4e00\u63d0\u6c14\uff0c\u5012\u9000\u4e08\u4f59\uff0c\u6bd2\u6c41\u53d1\u5c04\u4e0d\u8fdc\uff0c\u6e85\u5728\u5730\u4e0b\uff0c\u72b9\u5982\u58a8\u6cfc\u70df\u718f\u4e00\u822c\u3002 \u3000\u3000\u90a3\u9ec4\u5899\u6bd4\u7ea2\u5899\u5df2\u4f4e\u4e86\u4e09\u5c3a\uff0c\u8881\u627f\u5fd7\u7eb5\u8eab\u9ad8\u8dc3\uff0c\u624b\u6500\u5899\u5934\uff0c\u5728\u7a7a\u4e2d\u6253\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u7ffb\u8fc7\u5899\u5934\u53bb\u4e86\uff0c\u59ff\u52bf\u7f8e\u5999\u4e4b\u6781\u3002\u4f55\u94c1\u624b\u671b\u89c1\uff0c\u4e0d\u7981\u559d\u4e86\u4e00\u58f0\u5f69\u3002\u5916\u9762\u4e09\u9053\u5899\u4e00\u91cd\u4f4e\u8fc7\u4e00\u91cd\uff0c\u5df2\u53ef\u4e00\u7eb5\u800c\u8fc7\u3002\u7247\u523b\u95f4\u4f17\u4eba\u5230\u4e86\u6700\u540e\u4e00\u91cd\u9ed1\u5899\u4e4b\u5916\u3002\u8881\u627f\u5fd7\u89c1\u9759\u6084\u6084\u7684\u65e0\u4eba\u8ffd\u51fa\uff0c\u5374\u4e5f\u4e0d\u6562\u505c\u7559\uff0c\u628a\u9752\u9752\u8d1f\u5728\u80cc\u4e0a\uff0c\u548c\u4f17\u4eba\u75be\u5954\u8fdb\u57ce\u3002 \u3000\u3000\u5c06\u5230\u4f4f\u5b85\u65f6\uff0c\u8881\u627f\u5fd7\u5ffd\u89c9\u5934\u9888\u4e2d\u75d2\u75d2\u7684\u4e00\u9635\u5439\u7740\u70ed\u6c14\uff0c\u56de\u5934\u4e00\u671b\uff0c\u9752\u9752\u5657\u54e7\u4e00\u7b11\u3002\u8881\u627f\u5fd7\u77e5\u5979\u5e76\u65e0\u5927\u788d\uff0c\u5fc3\u4e0b\u5bbd\u6170\uff0c\u8fdb\u5b85\u540e\u5fd9\u53d6\u51fa\u51b0\u87fe\uff0c\u7ed9\u94c1\u7f57\u6c49\u6cbb\u4f24\u3002\u4f59\u4eba\u867d\u672a\u4e2d\u6bd2\uff0c\u4f46\u6fc0\u6597\u4e4b\u4e0b\uff0c\u90fd\u5438\u5165\u4e86\u6bd2\u6c14\uff0c\u5747\u611f\u5934\u6655\u80f8\u585e\uff0c\u4e5f\u5206\u522b\u4ee5\u51b0\u87fe\u9a71\u6bd2\u3002\u9752\u9752\u8db3\u4e0a\u88ab\u4f55\u94c1\u624b\u6253\u4e86\u4e00\u73af\uff0c\u96ea\u767d\u7684\u76ae\u80a4\u5168\u6210\u6de4\u9ed1\uff0c\u9ad8\u9ad8\u80bf\u8d77\u3002 \u3000\u3000\u6298\u817e\u4e86\u534a\u65e5\uff0c\u8881\u627f\u5fd7\u624d\u5411\u5355\u94c1\u751f\u95ee\u8d77\u4e94\u6bd2\u6559\u7684\u6765\u5386\u3002\u5355\u94c1\u751f\u9053\uff1a\u201c\u4e94\u6bd2\u6559\u6559\u5f92\u8db3\u8ff9\u4e0d\u51fa\u4e91\u8d35\u4e24\u5e7f\uff0c\u4ece\u6765\u4e0d\u5230\u5317\u65b9\uff0c\u4e0d\u8fc7\u6076\u540d\u8fdc\u64ad\uff0c\u6b66\u6797\u4e2d\u4eba\u63d0\u5230\u4e94\u6bd2\u6559\u65f6\uff0c\u65e0\u4e0d\u8c08\u864e\u8272\u53d8\uff0c\u4ece\u6765\u4e0d\u6562\u62db\u60f9\u3002\u4ed6\u4eec\u600e\u4e48\u4f1a\u4f4f\u5728\u8bda\u738b\u7237\u7684\u522b\u5e9c\u91cc\uff0c\u5f53\u771f\u4ee4\u4eba\u731c\u60f3\u4e0d\u900f\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u4e00\u65c1\u5728\u9759\u542c\u4ed6\u4eec\u521a\u624d\u6076\u6597\u7684\u7ecf\u8fc7\uff0c\u76b1\u7709\u4e0d\u8bed\uff0c\u8fd9\u65f6\u5ffd\u7136\u63d2\u53e3\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4ed9\u90fd\u6d3e\u7684\u9ec4\u6728\u9053\u4eba\uff0c\u542c\u8bf4\u5c31\u662f\u6b7b\u5728\u4e94\u6bd2\u6559\u7684\u624b\u91cc\u7684\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6709\u4eba\u89c1\u5230\u4e48\uff1f\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u8981\u662f\u6709\u4eba\u89c1\u5230\uff0c\u53ea\u6015\u8fd9\u4eba\u4e5f\u5df2\u96be\u9003\u4e94\u6bd2\u6559\u7684\u6bd2\u624b\u3002\u6c5f\u6e56\u4e0a\u8bb8\u591a\u4eba\u90fd\u8bf4\uff0c\u9ec4\u6728\u9053\u4eba\u6b7b\u5f97\u5f88\u60e8\u3002\u4ed9\u90fd\u6d3e\u540e\u6765\u5927\u4e3e\u5230\u4e91\u5357\u53bb\u5bfb\u4ec7\uff0c\u5374\u53c8\u4e00\u65e0\u7ed3\u679c\uff0c\u4e5f\u771f\u662f\u53e4\u602a\u5f97\u7d27\u3002\u201d \u3000\u3000\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u90a3\u8001\u4e5e\u5a46\u679c\u7136\u72e0\u6bd2\uff0c\u53ea\u53ef\u60dc\u6211\u4eec\u867d\u7136\u89c1\u5230\u4e86\uff0c\u5374\u4e0d\u80fd\u4e3a\u4f60\u62a5\u4ec7\u96ea\u6068\u3002\u201d\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u6211\u8ddf\u4e94\u6bd2\u6559\u4ece\u65e0\u74dc\u845b\uff0c\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u627e\u4e0a\u4e86\u6211\uff0c\u771f\u662f\u83ab\u540d\u5176\u5999\u3002\u201d\u5404\u4eba\u7eb7\u7eb7\u731c\u6d4b\u3002\u5ffd\u7136\u4e00\u540d\u5bb6\u4e01\u8fdb\u6765\u7980\u62a5\uff1a\u201c\u6709\u4e00\u4f4d\u59d3\u7126\u7684\u59d1\u5a18\u8981\u89c1\u8881\u76f8\u516c\u3002\u201d \u3000\u3000\u9752\u9752\u79c0\u7709\u4e00\u8e59\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u6765\u5e72\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8bf7\u5979\u8fdb\u6765\u5427\uff01\u201d\u5bb6\u4e01\u7b54\u5e94\u7740\u51fa\u53bb\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u9886\u7740\u7126\u5b9b\u513f\u8fdb\u6765\u3002 \u3000\u3000\u5979\u4e00\u8d70\u8fdb\u5385\uff0c\u8dea\u5728\u8881\u627f\u5fd7\u9762\u524d\u62dc\u5012\uff0c\u4f0f\u5730\u5927\u54ed\u3002\u8881\u627f\u5fd7\u89c1\u5979\u4e00\u8eab\u7f1f\u7d20\uff0c\u5fc3\u77e5\u4e0d\u5999\uff0c\u5fd9\u8dea\u4e0b\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u7126\u59d1\u5a18\u5feb\u8bf7\u8d77\uff0c\u4ee4\u5c0a\u4ed6\u8001\u4eba\u5bb6\u597d\u4e48\uff1f\u201d\u7126\u5b9b\u513f\u54ed\u9053\uff1a\u201c\u7239\u7239\u2026\u2026\u7ed9\u2026\u2026\u7ed9\u95f5\u5b50\u534e\u90a3\u5978\u8d3c\u5bb3\u6b7b\u5566\u3002\u201d\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u8001\u4eba\u5bb6\u600e\u4f1a\u906d\u96be\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u4ece\u8eab\u4e0a\u62ff\u51fa\u4e00\u4e2a\u5e03\u5305\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u6253\u4e86\u5f00\u6765\uff0c\u9732\u51fa\u4e00\u67c4\u7cbe\u5149\u8000\u773c\u7684\u5315\u9996\uff0c\u5203\u8eab\u4e0a\u8fd8\u6b8b\u7559\u7740\u4e4c\u9ed1\u7684\u8840\u8ff9\u3002\u8881\u627f\u5fd7\u8fde\u7740\u5e03\u5305\u6367\u8d77\u5315\u9996\uff0c\u89c1\u5200\u67c4\u4e0a\u7528\u91d1\u4e1d\u9576\u7740\u201c\u4ed9\u90fd\u95e8\u4e0b\u5b50\u5b57\u8f88\u5f1f\u5b50\u95f5\u5b50\u534e\u6536\u6267\u201d\u51e0\u4e2a\u5b57\uff0c\u663e\u662f\u4ed9\u90fd\u6d3e\u5e08\u5c0a\u8d50\u7ed9\u5f1f\u5b50\u7684\u5229\u5668\u3002 \u3000\u3000\u7126\u5b9b\u513f\u54ed\u9053\uff1a\u201c\u90a3\u5929\u5728\u6cf0\u5c71\u805a\u4f1a\u4e4b\u540e\uff0c\u6211\u8ddf\u7740\u7239\u7239\u4e00\u8d77\u56de\u5bb6\uff0c\u5728\u5f90\u5dde\u5e9c\u5ba2\u5e97\u91cc\u4f4f\u5bbf\u3002\u7b2c\u4e8c\u65e5\u7239\u7239\u7761\u5230\u8fb0\u65f6\u8fc7\u4e86\uff0c\u8fd8\u4e0d\u8d77\u6765\uff0c\u6211\u53bb\u53eb\u4ed6\uff0c\u54ea\u77e5\u2026\u2026\u54ea\u77e5\u2026\u2026\u4ed6\u80f8\u53e3\u63d2\u4e86\u8fd9\u628a\u5200\u2026\u2026\u8881\u76f8\u516c\uff0c\u8bf7\u4f60\u4f5c\u4e3b\uff01\u201d\u8bf4\u7f62\u568e\u5555\u5927\u54ed\u3002 \u3000\u3000\u9752\u9752\u672c\u6765\u5bf9\u5979\u9887\u6709\u7591\u5fcc\u4e4b\u610f\uff0c\u8fd9\u65f6\u89c1\u5979\u54ed\u5f97\u72b9\u5982\u68a8\u82b1\u5e26\u96e8\uff0c\u5a07\u695a\u53ef\u601c\uff0c\u5fc3\u4e2d\u96be\u8fc7\uff0c\u628a\u5979\u62c9\u5728\u8eab\u8fb9\uff0c\u6478\u51fa\u624b\u5e15\u7ed9\u5979\u62ed\u6cea\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u54e5\uff0c\u90a3\u59d3\u95f5\u7684\u5df2\u7b54\u5e94\u63ed\u8fc7\u8fd9\u4e2a\u6881\u5b50\uff0c\u600e\u4e48\u53c8\u5351\u9119\u884c\u523a\uff1f\u54b1\u4eec\u53ef\u4e0d\u80fd\u5584\u7f62\u5e72\u4f11\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u80f8\u4e2d\u9178\u695a\u96be\u8a00\uff0c\u60f3\u8d77\u7126\u516c\u793c\u7684\u6177\u6168\u91cd\u4e49\uff0c\u4e0d\u7981\u6d41\u4e0b\u6cea\u6765\uff0c\u9694\u4e86\u4e00\u9635\uff0c\u95ee\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u540e\u6765\u4f60\u89c1\u8fc7\u90a3\u59d3\u95f5\u7684\u4e48\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u54fd\u54bd\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u89c1\u8fc7\u4ed6\u4e24\u6b21\uff0c\u6211\u4eec\u4e00\u8def\u8ffd\u8d76\uff0c\u6628\u5929\u665a\u4e0a\u8ffd\u5230\u4e86\u5317\u4eac\u3002\u201d\u9752\u9752\u53eb\u9053\uff1a\u201c\u597d\u554a\uff0c\u4ed6\u5728\u5317\u4eac\uff0c\u54b1\u4eec\u8fd9\u5c31\u53bb\u627e\u4ed6\u3002\u59b9\u59b9\u4f60\u653e\u5fc3\uff0c\u5927\u4f19\u513f\u4e00\u5b9a\u7ed9\u4f60\u62a5\u4ec7\u3002\u201d\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u7b49\u65e9\u5df2\u5f97\u77e5\u8881\u627f\u5fd7\u5728\u5357\u4eac\u4e3a\u7126\u95f5\u4e24\u5bb6\u89e3\u4ec7\u7684\u7ecf\u8fc7\uff0c\u8fd9\u65f6\u542c\u5f97\u95f5\u5b50\u534e\u5982\u6b64\u4e0d\u5b88\u6c5f\u6e56\u9053\u4e49\uff0c\u90fd\u662f\u6124\u6168\u5f02\u5e38\u3002\u6c99\u5929\u5e7f\u9053\uff1a\u201c\u95f5\u5b50\u534e\u662f\u751a\u4e48\u4e1c\u897f\uff0c\u6c99\u67d0\u5012\u8981\u6597\u4ed6\u4e00\u6597\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u5411\u4f17\u4eba\u76c8\u76c8\u62dc\u4e86\u4e0b\u53bb\uff0c\u51c4\u7136\u9053\uff1a\u201c\u8981\u8bf7\u4f17\u4f4d\u4f2f\u4f2f\u53d4\u53d4\u4e3b\u6301\u516c\u9053\u3002\u201d \u3000\u3000\u7a0b\u9752\u7af9\u4e00\u62cd\u684c\u5b50\uff0c\u559d\u9053\uff1a\u201c\u95f5\u5b50\u534e\u5728\u54ea\u91cc\uff1f\u4ed9\u90fd\u6d3e\u867d\u7136\u4eba\u591a\u52bf\u4f17\uff0c\u8001\u7a0b\u53ef\u4e0d\u6015\u4ed6\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u9053\uff1a\u201c\u7239\u7239\u901d\u4e16\u540e\uff0c\u6211\u8ddf\u51e0\u4f4d\u5e08\u54e5\u7ed9\u4ed6\u8001\u4eba\u5bb6\u6536\u6b93\uff0c\u7075\u67e9\u5bc4\u5b58\u5728\u5f90\u5dde\u5e7f\u6b66\u9556\u5c40\u3002\u4e00\u9762\u641c\u5bfb\u95f5\u5b50\u534e\u7684\u4e0b\u843d\u3002\u603b\u662f\u7239\u7239\u82f1\u7075\u4f51\u62a4\uff0c\u6ca1\u51e0\u5929\u6cb3\u5357\u7684\u670b\u53cb\u5c31\u4f20\u6765\u8baf\u606f\uff0c\u8bf4\u6709\u4eba\u89c1\u5230\u90a3\u59d3\u95f5\u7684\u5978\u8d3c\u4ece\u6cb3\u5357\u5317\u4e0a\u3002\u91d1\u9f99\u5e2e\u5185\u5916\u9999\u5802\u4f17\u9999\u4e3b\u3001\u5404\u8def\u6c34\u9646\u7801\u5934\u7684\u8235\u4e3b\uff0c\u4e00\u8def\u8def\u5206\u6279\u515c\u622a\uff0c\u66fe\u4ea4\u8fc7\u4e24\u6b21\u624b\uff0c\u90fd\u7ed9\u4ed6\u6ed1\u6e9c\u9003\u8131\u4e86\u3002\u4f84\u5973\u513f\u4e0d\u4e2d\u7528\uff0c\u8fd8\u7ed9\u90a3\u5978\u8d3c\u523a\u4e86\u4e00\u5251\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u5de6\u80a9\u5fae\u9ad8\uff0c\u77e5\u9053\u8863\u91cc\u5305\u7740\u7ef7\u5e26\uff0c\u60f3\u6765\u5979\u4e3a\u7236\u62a5\u4ec7\uff0c\u5fc5\u5b9a\u594b\u4e0d\u987e\u8eab\uff0c\u53ef\u662f\u8bf4\u5230\u6b66\u529f\uff0c\u81ea\u662f\u4e0d\u53ca\u4ed9\u90fd\u597d\u624b\u95f5\u5b50\u534e\u4e86\u3002 \u3000\u3000\u7126\u5b9b\u513f\u53c8\u9053\uff1a\u201c\u6628\u5929\u6211\u4eec\u8ffd\u5230\u5317\u4eac\uff0c\u5df2\u67e5\u660e\u4e86\u90a3\u5978\u8d3c\u7684\u843d\u811a\u6240\u5728\u3002\u201d\u9752\u9752\u6025\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u54b1\u4eec\u5feb\u53bb\uff0c\u83ab\u7ed9\u4ed6\u6e9c\u4e86\u3002\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4ed6\u4f4f\u5728\u897f\u57ce\u5085\u5bb6\u80e1\u540c\uff0c\u6211\u4eec\u5e2e\u91cc\u5df2\u6709\u4e00\u767e\u591a\u4eba\u5b88\u5728\u9644\u8fd1\u3002\u201d\u8881\u627f\u5fd7\u5fae\u5fae\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u5e74\u7eaa\u867d\u5c0f\uff0c\u5374\u662f\u7cbe\u660e\u5e72\u7ec3\u3002\u8fd9\u6b21\u91d1\u9f99\u5e2e\u503e\u5de2\u800c\u51fa\uff0c\u90a3\u662f\u975e\u6740\u95f5\u5b50\u534e\u4e0d\u53ef\u7684\u4e86\u3002\u201d\u7126\u5b9b\u513f\u53c8\u9053\uff1a\u201c\u521a\u624d\u6211\u5728\u5927\u8857\u4e0a\uff0c\u9047\u7740\u4e00\u4f4d\u6cf0\u5c71\u5927\u4f1a\u4e2d\u89c1\u8fc7\u9762\u7684\u670b\u53cb\uff0c\u624d\u77e5\u8881\u76f8\u516c\u8ddf\u5404\u4f4d\u4f4f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u6c99\u5929\u5e7f\u5927\u62c7\u6307\u4e00\u7fd8\uff0c\u8bf4\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u4f60\u505a\u4e8b\u5468\u5230\uff0c\u95f5\u5b50\u534e\u5df2\u5728\u4f60\u4eec\u638c\u63e1\u4e4b\u4e2d\uff0c\u4f60\u8fd8\u662f\u6765\u8bf7\u76df\u4e3b\u4e3b\u6301\u516c\u9053\uff0c\u597d\u8ba9\u6c5f\u6e56\u4e0a\u670b\u53cb\u4eec\u90fd\u8bf4\u4e00\u53e5\u2018\u95f5\u5b50\u534e\u8be5\u6740\u2019\uff0c\u597d\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u9884\u5907\u51e0\u65f6\u52a8\u624b\uff1f\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4eca\u665a\u4e8c\u66f4\u3002\u201d \u3000\u3000\u5979\u628a\u5315\u9996\u5305\u56de\u5e03\u5305\u3002\u9752\u9752\u9053\uff1a\u201c\u59b9\u5b50\uff0c\u5f85\u4f1a\u4f60\u8fd8\u662f\u7528\u8fd9\u5315\u9996\u523a\u6b7b\u4ed6\uff1f\u201d\u7126\u5b9b\u513f\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u60f3\u8d77\u7126\u516c\u793c\u4e00\u751f\u4ed7\u4e49\uff0c\u5230\u5934\u6765\u5374\u6b7b\u4e8e\u975e\u547d\uff0c\u81ea\u5df1\u867d\u5df2\u5c3d\u529b\uff0c\u7ec8\u7a76\u8fd8\u662f\u4e0d\u80fd\u6551\u5f97\u4ed6\u6027\u547d\uff0c\u4e3a\u5fb7\u4e0d\u5352\uff0c\u5fc3\u4e0b\u9887\u4e3a\u6b49\u548e\uff0c\u53c8\u60f3\u4ed9\u90fd\u6d3e\u4e0e\u91d1\u9f99\u5e2e\u6b64\u540e\u52bf\u5fc5\u6028\u6028\u76f8\u62a5\uff0c\u7ea0\u7f20\u4e0d\u6e05\uff0c\u4e0d\u77e5\u5982\u4f55\u4e86\u7ed3\uff1f\u95f5\u5b50\u534e\u6697\u4e2d\u4f24\u4eba\uff0c\u7406\u5e94\u906d\u62a5\uff0c\u4f46\u8fd9\u4e8b\u8981\u505a\u5f97\u8ba9\u4ed9\u90fd\u6d3e\u53e3\u670d\u5fc3\u670d\uff0c\u65b9\u65e0\u540e\u60a3\u3002 \u3000\u3000\u5404\u4eba\u7528\u8fc7\u665a\u996d\uff0c\u4f11\u606f\u4e00\u9635\uff0c\u8881\u627f\u5fd7\u5e26\u540c\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u3001\u54d1\u5df4\u3001\u80e1\u6842\u5357\u3001\u6d2a\u80dc\u6d77\u4e94\u4eba\uff0c\u968f\u7740\u7126\u5b9b\u513f\u5f80\u5085\u5bb6\u80e1\u540c\u800c\u53bb\u3002\u9752\u9752\u3001\u94c1\u7f57\u6c49\u4e24\u4eba\u53d7\u4f24\uff0c\u4e0d\u80fd\u540c\u884c\uff0c\u5355\u94c1\u751f\u81ea\u884c\u56de\u5bb6\u517b\u4f24\u3002\u9752\u9752\u8fde\u8fde\u53f9\u6c14\uff0c\u5492\u9a82\u4f55\u94c1\u624b\u8fd9\u5996\u5973\u5bb3\u5f97\u5979\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u6ce8\uff1a\u8881\u5d07\u7115\u6709\u4e00\u4e2a\u670b\u53cb\u909d\u6e5b\u82e5\uff0c\u5e7f\u4e1c\u540d\u58eb\uff0c\u66fe\u6e38\u7476\u5c71\uff0c\u4e3a\u7476\u5973\u638c\u5175\u6743\u8005\u4e91\u6c0f\u4f5c\u8bb0\u5ba4\uff0c\u4f5c\u6709\u300a\u8d64\u96c5\u300b\u4e00\u4e66\uff0c\u5176\u4e2d\u201c\u50ee\u5987\u755c\u86ca\u201d\u4e00\u8282\u4e91\uff1a\u201c\u4e94\u6708\u4e94\u65e5\uff0c\u805a\u866b\u8c78\u4e4b\u6bd2\u8005\uff0c\u5e76\u7f6e\u5668\u5185\uff0c\u81ea\u76f8\u541e\u98df\uff0c\u6700\u540e\u72ec\u5b58\u8005\u66f0\u86ca\u3002\u6709\u86c7\u86ca\u3001\u8725\u8734\u86ca\u3001\u8723\u8782\u86ca\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u516d\u56de\u3000\u77f3\u5188\u51dd\u51b7\u6708 \u94c1\u624b\u62c2\u6653\u98ce",content:(0,t.jsx)(t.Fragment,{children:"\u4f17\u4eba\u6765\u5230\u80e1\u540c\u5916\u5341\u4f59\u4e08\u5904\uff0c\u7126\u516c\u793c\u7684\u51e0\u540d\u5f1f\u5b50\u5df2\u8fce\u4e86\u4e0a\u6765\uff0c\u8bf4\u95f5\u5b50\u534e\u548c\u4ed6\u5e08\u5f1f\u6d1e\u7384\u9053\u4eba\u5728\u5c4b\u91cc\u8bf4\u8bdd\u3002\u4f17\u4eba\u89c1\u8881\u627f\u5fd7\u51fa\u624b\u76f8\u52a9\uff0c\u6b23\u6170\u5df2\u6781\uff0c\u7cbe\u795e\u5927\u632f\u3002 \u3000\u3000\u7126\u5b9b\u513f\u95ee\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u53ef\u4ee5\u52a8\u624b\u4e86\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u53eb\u5927\u4f19\u5b88\u5728\u5916\u9762\uff0c\u54b1\u4eec\u51e0\u4e2a\u4eba\u5148\u53bb\u4e00\u63a2\u3002\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u597d\uff01\u201d\u4f4e\u58f0\u5bf9\u4f17\u5e2e\u53cb\u5429\u5490\u51e0\u53e5\uff0c\u548c\u8881\u627f\u5fd7\u7b49\u8dc3\u8fdb\u5899\u53bb\u3002\u7126\u5b9b\u513f\u8f7b\u529f\u8f83\u5dee\uff0c\u843d\u5730\u65f6\u811a\u4e0b\u5fae\u5fae\u4e00\u54cd\uff0c\u5c4b\u4e2d\u706f\u706b\u5ffd\u5730\u7184\u706d\u3002\u7126\u5b9b\u513f\u77e5\u9053\u4ec7\u4eba\u5df2\u7ecf\u53d1\u89c9\uff0c\u4e0d\u80fd\u518d\u63a2\u5230\u751a\u4e48\uff0c\u8f7b\u8f7b\u4e00\u58f0\u547c\u54e8\uff0c\u7a81\u7136\u56db\u5468\u5c4b\u9876\u5230\u5904\u90fd\u63a2\u51fa\u5934\u6765\u3002\u7126\u5b9b\u513f\u53eb\u9053\uff1a\u201c\u59d3\u95f5\u7684\uff0c\u51fa\u6765\u77a7\u77a7\uff0c\u662f\u8c01\u6765\u5566\uff01\u201d\u5c4b\u4e2d\u4eba\u9ed8\u4e0d\u4f5c\u58f0\u3002\u7126\u5b9b\u513f\u9053\uff1a\u201c\u70b9\u4e86\u706b\u628a\u8fdb\u53bb\uff01\u201d \u3000\u3000\u91d1\u9f99\u5e2e\u56db\u540d\u5e2e\u53cb\u53d6\u51fa\u706b\u6298\uff0c\u70b9\u7740\u5e26\u6765\u7684\u706b\u628a\uff0c\u6602\u9996\u800c\u5165\uff0c\u65c1\u8fb9\u56db\u540d\u5e2e\u53cb\u6267\u5200\u536b\u62a4\u3002\u7a81\u7136\u556a\u556a\u556a\u6570\u58f0\uff0c\u56db\u6839\u706b\u628a\u6253\u706d\u4e86\u4e09\u6839\uff0c\u4e24\u6761\u9ed1\u5f71\u4ece\u4f17\u4eba\u5934\u9876\u98de\u4e86\u51fa\u6765\u3002\u91d1\u9f99\u5e2e\u5e2e\u4f17\u4e00\u6d8c\u800c\u4e0a\uff0c\u4e52\u4e52\u4e53\u4e53\u7684\u6253\u4e86\u8d77\u6765\u3002\u5404\u4eba\u56db\u4e0b\u56f4\u4f4f\uff0c\u706b\u628a\u8d8a\u70b9\u8d8a\u591a\uff0c\u5c06\u4e00\u4e2a\u5927\u9662\u5b50\u7167\u8000\u5f97\u5982\u540c\u767d\u663c\u3002 \u3000\u3000\u95f5\u5b50\u534e\u548c\u6d1e\u7384\u9053\u4eba\u77e5\u9053\u5df2\u843d\u91cd\u56f4\uff0c\u4e24\u4eba\u80cc\u9760\u80cc\u7684\u62da\u529b\u6b7b\u6218\uff0c\u8f6c\u77ac\u95f4\u628a\u91d1\u9f99\u5e2e\u5e2e\u4f17\u523a\u4f24\u4e86\u516d\u4e03\u4eba\u3002\u4f24\u8005\u4e00\u9000\u4e0b\uff0c\u7acb\u5373\u6709\u4eba\u8865\u4e0a\u3002 \u3000\u3000\u518d\u6597\u4e00\u9635\uff0c\u95f5\u5b50\u534e\u548c\u6d1e\u7384\u53c8\u4f24\u4e86\u4e09\u56db\u4eba\uff0c\u4f46\u6d1e\u7384\u5de6\u81c2\u4e5f\u5df2\u53d7\u4f24\u3002\u4ed6\u5251\u4ea4\u53f3\u624b\uff0c\u731b\u6251\u529b\u6218\u3002\u4e24\u4eea\u5251\u6cd5\u672c\u662f\u4ed6\u4f7f\u5de6\u624b\u5251\uff0c\u95f5\u5b50\u534e\u4f7f\u53f3\u624b\u5251\uff0c\u4e24\u4eba\u5de6\u53f3\u547c\u5e94\uff0c\u56de\u73af\u653b\u5b88\u3002\u73b0\u4e0b\u4e24\u67c4\u90fd\u662f\u53f3\u624b\u5251\uff0c\u5a01\u529b\u7acb\u51cf\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u6d1e\u7384\u4e0e\u95f5\u5b50\u534e\u8eab\u4e0a\u53c8\u5404\u53d7\u4e86\u51e0\u5904\u4f24\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5728\u65c1\u89c2\u6218\uff0c\u5fc3\u60f3\uff1a\u201c\u4e00\u547d\u8fd8\u4e00\u547d\uff0c\u6740\u95f5\u5b50\u534e\u4e00\u4eba\u5df2\u7ecf\u591f\u4e86\uff0c\u4e0d\u5fc5\u8ba9\u6d1e\u7384\u4e5f\u966a\u5728\u8fd9\u91cc\u3002\u201d\u773c\u89c1\u4e24\u4eba\u4fbf\u8981\u4e27\u547d\u5f53\u5730\uff0c\u8e0a\u8eab\u8df3\u5165\u5708\u5b50\uff0c\u767b\u65f6\u91d1\u5149\u95ea\u52a8\uff0c\u545b\u5577\u5577\u4e00\u9635\u4e71\u54cd\uff0c\u4e0d\u4f46\u6d1e\u7384\u4e0e\u95f5\u5b50\u534e\u624b\u4e2d\u957f\u5251\u88ab\u91d1\u86c7\u5251\u524a\u65ad\uff0c\u91d1\u9f99\u5e2e\u8bf8\u4eba\u7684\u5175\u5203\u4e5f\u6709\u4e03\u516b\u67c4\u65ad\u5934\u6298\u8eab\u3002 \u3000\u3000\u4f17\u4eba\u51fa\u5176\u4e0d\u610f\uff0c\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u5411\u540e\u8dc3\u5f00\u3002 \u3000\u3000\u8881\u627f\u5fd7\u81ea\u5f97\u91d1\u86c7\u5251\u4ee5\u6765\uff0c\u9664\u4e86\u4ee5\u4e4b\u524a\u65ad\u897f\u6d0b\u519b\u5b98\u96f7\u8499\u7684\u957f\u5251\u4e4b\u5916\uff0c\u4ece\u672a\u4ed7\u5251\u4e0e\u4eba\u6b63\u5f0f\u4ea4\u624b\uff0c\u4e0d\u610f\u6b64\u5251\u7adf\u6709\u5982\u65af\u5a01\u529b\uff0c\u8fde\u81ea\u5df1\u4e5f\u662f\u4e00\u5446\uff0c\u5fc3\u60f3\u8fd9\u90fd\u662f\u5404\u4eba\u8d81\u624b\u7684\u5175\u5668\uff0c\u81ea\u5df1\u4e0d\u8fc7\u8981\u53cc\u65b9\u7f62\u624b\u505c\u6597\uff0c\u4e0d\u6599\u7adf\u524a\u574f\u4e86\u591a\u4ef6\u5175\u5203\uff0c\u5fc3\u4e0b\u597d\u751f\u4e0d\u5b89\u3002 \u3000\u3000\u8fd9\u65f6\u95f5\u5b50\u534e\u548c\u6d1e\u7384\u5168\u8eab\u8840\u8ff9\u6591\u6591\uff0c\u89c1\u8881\u627f\u5fd7\u5230\u6765\uff0c\u66f4\u77e5\u65e0\u5e78\u3002\u6d1e\u7384\u628a\u65ad\u5251\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u60e8\u7b11\u9053\uff1a\u201c\u6211\u5e08\u5144\u5f1f\u4e0d\u77e5\u4f55\u4e8b\u5f97\u7f6a\u4e86\u9601\u4e0b\uff0c\u5982\u6b64\u82e6\u82e6\u76f8\u903c\uff1f\u201d\u7ffb\u624b\u4ece\u8170\u95f4\u6478\u51fa\u4e00\u67c4\u5315\u9996\uff0c\u731b\u5f80\u81ea\u5df1\u80f8\u819b\u4e0a\u63d2\u53bb\u3002\u8881\u627f\u5fd7\u5de6\u638c\u5982\u98ce\uff0c\u5728\u4ed6\u80f8\u524d\u8f7b\u8f7b\u4e00\u63a8\uff0c\u53f3\u624b\u5df2\u62ff\u4f4f\u4ed6\u624b\u8155\uff0c\u5939\u624b\u593a\u8fc7\u5315\u9996\uff0c\u706b\u5149\u4e0b\u4e00\u770b\uff0c\u89c1\u5315\u9996\u548c\u95f5\u5b50\u534e\u523a\u6b7b\u7126\u516c\u793c\u90a3\u4e00\u67c4\u5168\u7136\u76f8\u540c\uff0c\u67c4\u4e0a\u523b\u7740\u201c\u4ed9\u90fd\u95e8\u4e0b\u5b50\u5b57\u8f88\u5f1f\u5b50\u6d1e\u7384\u6536\u6267\u201d\u4e00\u884c\u5b57\u3002 \u3000\u3000\u6d1e\u7384\u94c1\u9752\u4e86\u8138\uff0c\u559d\u9053\uff1a\u201c\u597d\u6c49\u5b50\u53ef\u6740\u4e0d\u53ef\u8fb1\u3002\u6211\u5b66\u827a\u4e0d\u7cbe\uff0c\u4e0d\u662f\u4f60\u5bf9\u624b\uff0c\u6b7b\u7ed9\u4f60\u770b\u4fbf\u4e86\u3002\u5feb\u628a\u5315\u9996\u8fd8\u6211\uff01\u201d\u8881\u627f\u5fd7\u6015\u4ed6\u53c8\u8981\u81ea\u6740\uff0c\u5c06\u5315\u9996\u5f80\u8170\u91cc\u4e00\u63d2\uff0c\u6b63\u8272\u9053\uff1a\u201c\u5f85\u5f97\u4e00\u5207\u6599\u7406\u6e05\u695a\uff0c\u81ea\u7136\u8fd8\u4f60\u3002\u201d\u6d1e\u7384\u5927\u6012\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8981\u6740\u5c31\u6740\uff0c\u4e0d\u80fd\u5982\u6b64\u6b3a\u4eba\uff01\u201d \u3000\u3000\u8bf4\u7740\u5288\u9762\u4e00\u62f3\u3002\u8881\u627f\u5fd7\u9000\u540e\u4e00\u6b65\u907f\u5f00\uff0c\u6115\u7136\u9053\uff1a\u201c\u5728\u4e0b\u4f55\u6562\u76f8\u6b3a\uff1f\u201d\u6d1e\u7384\u51db\u7136\u9053\uff1a\u201c\u8fd9\u628a\u5315\u9996\u662f\u672c\u6d3e\u5e08\u5c0a\u6240\u8d50\uff0c\u5b81\u6559\u6027\u547d\u4e0d\u5728\uff0c\u4e5f\u4e0d\u80fd\u843d\u5165\u65c1\u4eba\u624b\u4e2d\u3002\u201d\u8881\u627f\u5fd7\u4e00\u695e\uff0c\u7591\u4e91\u5927\u8d77\uff0c\u5fc3\u60f3\u8fd9\u5315\u9996\u65e2\u7136\u5982\u6b64\u8981\u7d27\uff0c\u95f5\u5b50\u534e\u600e\u80fd\u4e8e\u523a\u6740\u7126\u516c\u793c\u540e\u4ecd\u7559\u5728\u4ed6\u8eab\u4e0a\uff0c\u5374\u4e0d\u53d6\u56de\uff1f\u5f53\u4e0b\u5c06\u5315\u9996\u53cc\u624b\u5949\u8fd8\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u8981\u8bf7\u6559\u9053\u957f\u3002\u201d\u6d1e\u7384\u63a5\u8fc7\u5315\u9996\uff0c\u542c\u4ed6\u8bf4\u5f97\u5ba2\u6c14\uff0c\u4fbf\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8f6c\u8fc7\u8eab\u6765\uff0c\u5bf9\u7126\u5b9b\u513f\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u90a3\u5e03\u5305\u7ed9\u6211\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u9012\u8fc7\u5e03\u5305\uff0c\u624b\u63e1\u53cc\u5200\uff0c\u7d27\u7d27\u76d1\u89c6\u95f5\u5b50\u534e\u3002\u8881\u627f\u5fd7\u6253\u5f00\u5e03\u5305\uff0c\u9732\u51fa\u5315\u9996\u3002\u95f5\u5b50\u534e\u548c\u6d1e\u7384\u9f50\u58f0\u60ca\u547c\u3002\u91d1\u9f99\u5e2e\u5e2e\u4f17\u773c\u89c1\u51f6\u5668\uff0c\u60f3\u8d77\u8001\u5e2e\u4e3b\u60e8\u6b7b\uff0c\u76ee\u7726\u6b32\u88c2\uff0c\u5404\u4eba\u903c\u8fd1\u6570\u6b65\u3002 \u3000\u3000\u95f5\u5b50\u534e\u98a4\u58f0\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u8fd9\u662f\u6211\u7684\u5315\u9996\u5440\uff1f\u4f60\u4ece\u54ea\u91cc\u5f97\u6765\uff1f\u201d\u4f38\u624b\u6765\u53d6\u3002\u8881\u627f\u5fd7\u624b\u4e00\u7f29\u3002\u7126\u5b9b\u513f\u5355\u5200\u6325\u51fa\uff0c\u5f80\u95f5\u5b50\u534e\u624b\u81c2\u780d\u843d\u3002\u95f5\u5b50\u534e\u75be\u5fd9\u7f29\u624b\uff0c\u8fd9\u5200\u4fbf\u6ca1\u780d\u4e2d\u3002\u7126\u5b9b\u513f\u5f85\u8981\u8ffd\u51fb\uff0c\u8881\u627f\u5fd7\u4f38\u624b\u62e6\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u95ee\u6e05\u695a\u4e86\u3002\u201d\u7126\u5b9b\u513f\u505c\u5200\u4e0d\u780d\uff0c\u6d41\u4e0b\u4e24\u884c\u6cea\u6765\u3002 \u3000\u3000\u95f5\u5b50\u534e\u6012\u9053\uff1a\u201c\u5f53\u65e5\u6211\u4eec\u5728\u5357\u4eac\u8a00\u660e\uff0c\u53cc\u65b9\u89e3\u4ec7\u91ca\u6028\u3002\u91d1\u9f99\u5e2e\u4e3a\u751a\u4e48\u4e0d\u987e\u4fe1\u4e49\uff0c\u63a5\u8fde\u51e0\u6b21\u524d\u6765\u4f24\u6211\uff1f\u4f60\u53eb\u7126\u516c\u793c\u51fa\u6765\u3002 \u3000\u3000\u54b1\u4eec\u4e09\u5bf9\u516d\u9762\uff0c\u8bf4\u4e2a\u660e\u767d\u3002\u59d3\u95f5\u7684\u5230\u5e95\u54ea\u4e00\u70b9\u4e0a\u9053\u7406\u4e8f\u4e86\u2026\u2026\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u91d1\u9f99\u5e2e\u5e2e\u4f17\u65e9\u5df2\u7eb7\u7eb7\u6012\u559d\uff1a\u201c\u6211\u4eec\u5e2e\u4e3b\u7ed9\u4f60\u5bb3\u6b7b\u4e86\uff0c\u4f60\u8fd9\u5978\u8d3c\u8fd8\u6765\u5047\u6487\u6e05\uff01\u201d\u95f5\u5b50\u534e\u548c\u6d1e\u7384\u90fd\u5927\u5403\u4e00\u60ca\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u751a\u4e48\uff1f\u7126\u516c\u793c\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4e8c\u4eba\u60ca\u8bb6\u795e\u8272\uff0c\u4e0d\u4f3c\u4f5c\u4f2a\uff0c\u5fc3\u60f3\uff1a\u201c\u6216\u8bb8\u5185\u4e2d\u53e6\u6709\u522b\u60c5\u3002\u201d\u95ee\u9053\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u77e5\uff1f\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u6211\u628a\u623f\u5b50\u8f93\u4e86\u7ed9\u4f60\uff0c\u6ca1\u9762\u76ee\u518d\u5728\u6c5f\u6e56\u4e0a\u6df7\uff0c\u4fbf\u4e0a\u5f00\u5c01\u5e9c\u53bb\uff0c\u8981\u8ddf\u638c\u95e8\u5927\u5e08\u5144\u6c34\u4e91\u9053\u957f\u5546\u91cf\uff0c\u54ea\u77e5\u5e08\u5144\u6ca1\u4f1a\u5230\uff0c\u9014\u4e2d\u5374\u4e0d\u660e\u4e0d\u767d\u7684\u8ddf\u91d1\u9f99\u5e2e\u6253\u4e86\u4e24\u573a\u3002\u7126\u516c\u793c\u597d\u7aef\u7aef\u7684\uff0c\u53c8\u600e\u4e48\u4f1a\u6b7b\uff1f\u201d\u7126\u5b9b\u513f\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u77a7\u51fa\u60c5\u5f62\u6709\u70b9\u4e0d\u5bf9\uff0c\u786c\u54bd\u9053\uff1a\u201c\u6211\u7239\u7239\u2026\u2026\u662f\u7ed9\u2026\u2026\u7ed9\u4eba\u7528\u8fd9\u628a\u5315\u9996\u5bb3\u6b7b\u7684\u2026\u2026\u5c31\u7b97\u4e0d\u662f\u4f60\uff0c\u4e5f\u603b\u662f\u4f60\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u604d\u7136\u5927\u609f\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u55ef\uff0c\u8fd9\u5c31\u662f\u4e86\u3002\u201d\u7126\u5b9b\u513f\u559d\u9053\uff1a\u201c\u751a\u4e48\u8fd9\u5c31\u662f\u4e86\uff1f\u201d\u95f5\u5b50\u534e\u8981\u5f85\u5206\u8fa9\uff0c\u4e00\u65f6\u62d9\u4e8e\u8a00\u8f9e\uff0c\u5374\u53c8\u8bf4\u4e0d\u660e\u767d\u3002\u91d1\u9f99\u5e2e\u4f17\u4eba\u53ea\u9053\u4ed6\u5fc3\u865a\uff0c\u58f0\u52bf\u6c79\u6c79\u7684\u53c8\u8981\u64cd\u5200\u4e0a\u524d\u3002 \u3000\u3000\u6d1e\u7384\u9053\u4eba\u63a5\u8fc7\u95f5\u5b50\u534e\u624b\u4e2d\u534a\u622a\u65ad\u5251\uff0c\u63b7\u5728\u5730\u4e0b\uff0c\u51db\u7136\u9053\uff1a\u201c\u5404\u4f4d\u65e2\u7136\u8981\u8ba9\u7126\u5e2e\u4e3b\u7684\u5927\u4ec7\u6c38\u8fdc\u4e0d\u80fd\u5f97\u62a5\uff0c\u8ba9\u771f\u51f6\u5978\u4eba\u5728\u4e00\u65c1\u6697\u4e2d\u51b7\u7b11\uff0c\u6211\u5e08\u5144\u5f1f\u9976\u4e0a\u8fd9\u4e24\u6761\u6027\u547d\uff0c\u53c8\u7b97\u751a\u4e48\uff1f\u201d\u633a\u8d77\u80f8\u819b\uff0c\u675f\u624b\u5c31\u622e\u3002\u4f17\u4eba\u89c1\u4ed6\u5982\u6b64\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u4e00\u65f6\u5012\u62ff\u4e0d\u5b9a\u4e3b\u610f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u7126\u5e2e\u4e3b\u4e0d\u662f\u95f5\u5144\u6740\u7684\uff1f\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u59d3\u95f5\u7684\u51fa\u4e8e\u4ed9\u90fd\u95e8\u4e0b\uff0c\u4e5f\u8fd8\u77e5\u9053\u6c5f\u6e56\u4e0a\u4fe1\u4e49\u4e3a\u5148\u3002\u6211\u65e2\u5df2\u8f93\u7ed9\u4f60\uff0c\u53c8\u77e5\u6709\u5978\u4eba\u4ece\u4e2d\u6311\u62e8\uff0c\u600e\u4f1a\u518d\u5230\u5357\u4eac\u5bfb\u4ec7\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u7126\u5e2e\u4e3b\u4e0d\u662f\u5728\u5357\u4eac\u88ab\u5bb3\u7684\u3002\u201d\u95f5\u5b50\u534e\u5947\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5f90\u5dde\u3002\u201d\u6d1e\u7384\u9053\uff1a\u201c\u6211\u5e08\u5144\u5f1f\u6709\u5341\u591a\u5e74\u6ca1\u5230\u5f90\u5dde\u5566\u3002\u9664\u975e\u6211\u4eec\u4f1a\u653e\u98de\u5251\uff0c\u5343\u91cc\u5916\u53d6\u4eba\u9996\u7ea7\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d\u6d1e\u7384\u4f38\u624b\u4e00\u62cd\u81ea\u5df1\u9879\u9888\uff0c\u8bf4\u9053\uff1a\u201c\u6740\u5934\u4e5f\u4e0d\u6015\uff0c\u4f55\u5fc5\u8bf4\u5047\u8bdd\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u67c4\u5315\u9996\u4ece\u4f55\u800c\u6765\uff1f\u201d\u6d1e\u7384\u9053\uff1a\u201c\u6211\u8fd9\u65f6\u8bf4\u51fa\u771f\u76f8\uff0c\u53ea\u6015\u5404\u4f4d\u8fd8\u4e0d\u76f8\u4fe1\u3002\u73b0\u4e0b\u6211\u5e26\u4f60\u53bb\u4e00\u4e2a\u5730\u65b9\uff0c\u4e00\u770b\u5c31\u77e5\u3002\u201d\u95f5\u5b50\u534e\u6025\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u90a3\u4e0d\u80fd\u53bb\u3002\u201d\u6d1e\u7384\u9053\uff1a\u201c\u53e3\u8bf4\u65e0\u51ed\uff0c\u987b\u6709\u5b9e\u636e\u3002\u7126\u5e2e\u4e3b\u4e3a\u5978\u4eba\u6740\u5bb3\uff0c\u6b64\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u52a1\u987b\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u8881\u76f8\u516c\u548c\u7126\u59d1\u5a18\u4e24\u4f4d\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u51b3\u4e0d\u80fd\u574f\u54b1\u4eec\u7684\u4e8b\u3002\u201d\u95f5\u5b50\u534e\u624d\u4e0d\u8a00\u8bed\u4e86\u3002 \u3000\u3000\u7126\u5b9b\u513f\u9053\uff1a\u201c\u53bb\u54ea\u91cc\uff1f\u201d\u6d1e\u7384\u9053\uff1a\u201c\u6211\u53ea\u80fd\u5e26\u9886\u8881\u76f8\u516c\u548c\u4f60\u4e24\u4f4d\u540c\u53bb\u3002\u4eba\u591a\u4e86\u4e0d\u884c\u3002\u201d \u3000\u3000\u91d1\u9f99\u5e2e\u4e2d\u6709\u4eba\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4ed6\u8981\u4f7f\u5978\uff0c\u83ab\u7ed9\u4ed6\u4eec\u8d70\u4e86\u3002\u201d\u7126\u5b9b\u513f\u95ee\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4f60\u8bf4\u600e\u6837\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u770b\u6765\u8fd9\u4e24\u4eba\u786e\u662f\u522b\u6709\u9690\u60c5\uff0c\u8fd8\u662f\u4e00\u540c\u524d\u5f80\u67e5\u660e\u771f\u76f8\u4e3a\u59a5\u3002\u8981\u662f\u4ed6\u4eec\u60f3\u4f7f\u8be1\u8ba1\uff0c\u8c05\u6765\u4e5f\u9003\u4e0d\u8131\u6211\u624b\u638c\u3002\u201d\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u5c31\u540c\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u5bf9\u91d1\u9f99\u5e2e\u4f17\u4eba\u9053\uff1a\u201c\u6709\u8881\u76f8\u516c\u5728\uff0c\u6599\u60f3\u4ed6\u4eec\u4e5f\u4e0d\u6562\u600e\u6837\u3002\u201d\u81ea\u7126\u516c\u793c\u901d\u4e16\uff0c\u7126\u5b9b\u513f\u5df2\u9690\u7136\u4e3a\u4e00\u5e2e\u4e4b\u4e3b\u3002\u5979\u7387\u9886\u5e2e\u4f17\u5927\u4e3e\u5bfb\u4ec7\uff0c\u4f17\u4eba\u5bf9\u5979\u65e0\u4e0d\u8a00\u542c\u8ba1\u4ece\uff0c\u5927\u5bb6\u53c8\u77e5\u8881\u627f\u5fd7\u4e3a\u4eba\u4ec1\u4e49\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u6709\u8fd9\u4e48\u4e00\u4f4d\u9ad8\u624b\u4ece\u4e2d\u62a4\u6301\uff0c\u771f\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u5f53\u4e0b\u4e5f\u5c31\u6ca1\u6709\u5f02\u8a00\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u968f\u7740\u95f5\u5b50\u534e\u5e08\u5144\u5f1f\u4e00\u8def\u5411\u5317\u3002\u6765\u5230\u57ce\u5899\u8fb9\uff0c\u6d1e\u7384\u53d6\u51fa\u94a9\u7d22\uff0c\u7529\u4e0a\u53bb\u94a9\u4f4f\u57ce\u5899\uff0c\u8ba9\u7126\u5b9b\u513f\u5148\u722c\u4e86\u4e0a\u53bb\uff0c\u7b2c\u4e8c\u8881\u627f\u5fd7\u4e0a\uff0c\u7136\u540e\u4ed6\u5e08\u5144\u5f1f\u5148\u540e\u722c\u4e0a\u57ce\u5934\u3002\u56db\u4eba\u7eb5\u51fa\u57ce\u5899\uff0c\u7eed\u5411\u5317\u884c\u3002\u8fd9\u65f6\u65b9\u5f53\u5b50\u591c\uff0c\u6708\u8272\u5982\u6c34\uff0c\u9053\u8def\u8d8a\u8d70\u8d8a\u662f\u5d0e\u5c96\u3002\u518d\u884c\u56db\u4e94\u91cc\uff0c\u4e0a\u4e86\u4e2a\u4e71\u77f3\u5c71\u5c97\uff0c\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u90fd\u611f\u8bb6\u5f02\uff0c\u4e0d\u77e5\u8fd9\u4e24\u4eba\u6765\u6b64\u8352\u50fb\u4e4b\u5904\uff0c\u6709\u4f55\u7528\u610f\u3002\u7126\u5b9b\u513f\u5bfb\u601d\uff1a\u201c\u83ab\u975e\u8fd9\u4e24\u4eba\u5728\u6b64\u4f0f\u4e0b\u5927\u6279\u5e2e\u624b\uff1f\u4f46\u6709\u8881\u76f8\u516c\u5728\u6b64\uff0c\u5bf9\u65b9\u7eb5\u6709\u5343\u519b\u4e07\u9a6c\uff0c\u4ed6\u4e5f\u5fc5\u80fd\u5e26\u6211\u8131\u9669\u3002\u201d \u3000\u3000\u4e0a\u5c97\u53c8\u8d70\u4e86\u4e8c\u4e09\u91cc\uff0c\u624d\u5230\u5c97\u9876\uff0c\u53ea\u89c1\u602a\u77f3\u5d6f\u5ce8\uff0c\u5cfb\u9669\u5939\u5140\uff0c\u6708\u5149\u4e0b\u4f3c\u9b54\u4f3c\u602a\uff0c\u9634\u68ee\u68ee\u7684\u5bd2\u610f\u903c\u4eba\u3002\u6d1e\u7384\u548c\u95f5\u5b50\u534e\u8d70\u5411\u4e00\u5757\u5927\u5ca9\u77f3\u4e4b\u540e\uff0c\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u8ddf\u7740\u8fc7\u53bb\uff0c\u53ea\u89c1\u5ca9\u8fb9\u8d6b\u7136\u505c\u7740\u4e00\u5177\u68fa\u6728\u3002\u7126\u5b9b\u513f\u4e8e\u9ed1\u591c\u8352\u5c71\u4e4d\u89c1\u6b64\u7269\uff0c\u5fc3\u4e2d\u4e00\u80a1\u51c9\u6c14\u76f4\u5192\u4e0a\u6765\u3002 \u3000\u3000\u6d1e\u7384\u6361\u8d77\u4e00\u5757\u77f3\u5b50\uff0c\u5728\u68fa\u6750\u5934\u4e0a\u8f7b\u51fb\u4e09\u4e0b\uff0c\u7a0d\u505c\u4e00\u4f1a\uff0c\u53c8\u51fb\u4e24\u4e0b\uff0c\u7136\u540e\u518d\u51fb\u4e09\u4e0b\uff0c\u53cc\u624b\u6258\u4f4f\u68fa\u76d6\u5f80\u4e0a\u4e00\u6380\uff0c\u514b\u52d2\u4e00\u58f0\u54cd\uff0c\u68fa\u6750\u4e2d\u5750\u8d77\u4e00\u5177\u50f5\u5c38\u3002\u7126\u5b9b\u513f\u201c\u554a\u201d\u7684\u4e00\u58f0\u5927\u53eb\uff0c\u53cc\u624b\u6293\u4f4f\u4e86\u8881\u627f\u5fd7\u5de6\u624b\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u9760\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u90a3\u50f5\u5c38\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5e26\u4e86\u5916\u4eba\u6765\uff1f\u201d\u6d1e\u7384\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u670b\u53cb\u3002\u8fd9\u4f4d\u8881\u76f8\u516c\uff0c\u662f\u91d1\u86c7\u90ce\u541b\u590f\u5927\u4fa0\u7684\u5f1f\u5b50\u3002\u8fd9\u4f4d\u7126\u59d1\u5a18\uff0c\u662f\u91d1\u9f99\u5e2e\u7126\u5e2e\u4e3b\u7684\u5343\u91d1\u3002\u201d\u90a3\u50f5\u5c38\u5411\u8881\u7126\u4e8c\u4eba\u9053\uff1a\u201c\u4e24\u4f4d\u83ab\u602a\u3002 \u3000\u3000\u8d2b\u9053\u8eab\u4e0a\u6709\u4f24\uff0c\u4e0d\u80fd\u8d77\u8eab\u3002\u201d\u6d1e\u7384\u9053\uff1a\u201c\u8fd9\u662f\u655d\u6d3e\u638c\u95e8\u5e08\u5144\u6c34\u4e91\u9053\u4eba\u3002\u5728\u8fd9\u91cc\u907f\u4ec7\u517b\u4f24\u3002\u201d\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u624d\u77e5\u539f\u6765\u4e0d\u662f\u50f5\u5c38\uff0c\u5f53\u5373\u65bd\u793c\u3002\u6c34\u4e91\u9053\u4eba\u62f1\u624b\u7b54\u793c\u3002 \u3000\u3000\u770b\u90a3\u6c34\u4e91\u9053\u4eba\u65f6\uff0c\u53ea\u89c1\u4ed6\u8138\u5982\u767d\u7eb8\uff0c\u6ca1\u534a\u4e1d\u8840\u8272\uff0c\u989d\u89d2\u6b63\u4e2d\u4ece\u8111\u95e8\u76f4\u5230\u9f3b\u6881\u5374\u662f\u4e00\u6761\u6bb7\u706f\u8272\u7684\u7c97\u5927\u4f24\u75a4\uff0c\u75a4\u75d5\u72b9\u65b0\uff0c\u60f3\u662f\u53d7\u521b\u4e0d\u4e45\uff0c\u88ab\u90a3\u60e8\u767d\u7684\u8138\u8272\u4e00\u52a0\u6620\u6258\uff0c\u66f4\u662f\u53ef\u6016\u3002 \u3000\u3000\u6c34\u4e91\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u5e08\u7236\u8ddf\u5c0a\u5e08\u590f\u8001\u5e08\u4ea4\u597d\u3002\u590f\u8001\u5e08\u6765\u4ed9\u90fd\u5c71\u65f6\uff0c\u8d2b\u9053\u66fe\u4f8d\u5949\u8fc7\u4ed6\u3002\u4ed6\u8001\u4eba\u5bb6\u53ef\u597d\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\u8fd9\u65f6\u4e0d\u5fc5\u518d\u7792\uff0c\u7b54\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u5df2\u53bb\u4e16\u591a\u5e74\u4e86\u3002\u201d \u3000\u3000\u6c34\u4e91\u9053\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u60e8\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u826f\u4e45\uff0c\u624d\u4f4e\u58f0\u9053\uff1a\u201c\u521a\u624d\u542c\u6d1e\u7384\u5e08\u5f1f\u8bf4\u9053\uff0c\u9601\u4e0b\u662f\u91d1\u86c7\u5f1f\u5b50\uff0c\u6211\u5fc3\u4e2d\u5341\u5206\u559c\u6b22\uff0c\u5fc3\u60f3\u53ea\u8981\u91d1\u86c7\u524d\u8f88\u51fa\u624b\uff0c\u6211\u5e08\u7236\u7684\u5927\u4ec7\u6216\u80fd\u5f97\u62a5\u3002\u5509\uff01\u54ea\u77e5\u4ed6\u8001\u4eba\u5bb6\u7adf\u4e5f\u5df2\u5f52\u9053\u5c71\uff0c\u8001\u6210\u51cb\u8c22\uff0c\u53ea\u6015\u8981\u8ba9\u5978\u4eba\u6a2a\u884c\u4e00\u4e16\u4e86\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u5fc3\u9053\uff1a\u201c\u6211\u662f\u4e3a\u62a5\u7236\u4ec7\u800c\u6765\u6b64\u5730\uff0c\u54ea\u77e5\u53c8\u5f15\u51fa\u4e00\u6869\u5e08\u4ec7\u6765\u3002\u201d\u8881\u627f\u5fd7\u5374\u60f3\uff1a\u201c\u4e0d\u77e5\u4ed6\u7684\u5bf9\u5934\u662f\u751a\u4e48\u5389\u5bb3\u811a\u8272\uff0c\u5929\u4e0b\u9664\u4e86\u91d1\u86c7\u90ce\u541b\uff0c\u4fbf\u65e0\u4eba\u5bf9\u4ed8\u5f97\u4e86\uff1f\u201d \u3000\u3000\u6d1e\u7384\u4f4e\u58f0\u628a\u91d1\u9f99\u5e2e\u5bfb\u4ec7\u7684\u4e8b\u8bf4\u4e86\u4e00\u904d\uff0c\u6c42\u5927\u5e08\u5144\u5411\u7126\u5b9b\u513f\u89e3\u91ca\u3002\u6c34\u4e91\u9053\u4eba\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8d8a\u542c\u8d8a\u6012\uff0c\u7a81\u7136\u624b\u638c\u4e00\u7ffb\uff0c\u5728\u8eab\u65c1\u68fa\u4e0a\u731b\u51fb\u4e00\u638c\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u68fa\u6728\u767b\u65f6\u584c\u4e86\u4e00\u5757\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u9053\u4eba\u7684\u6b66\u529f\u6bd4\u4ed6\u4e24\u4e2a\u5e08\u5f1f\u53ef\u9ad8\u660e\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u8eab\u6000\u7edd\u6280\uff0c\u600e\u4e48\u4f1a\u6015\u5f97\u8fd9\u6837\u5389\u5bb3\uff0c\u7adf\u8981\u5077\u5077\u8eb2\u5728\u8fd9\u91cc\u88c5\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u6c34\u4e91\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u6211\u4eec\u4ed9\u90fd\u5f1f\u5b50\uff0c\u6bcf\u4eba\u6ee1\u5e08\u827a\u6210\u3001\u4e0b\u5c71\u884c\u9053\u4e4b\u65f6\uff0c\u5e08\u7236\u5fc5\u5b9a\u8d50\u4ed6\u4e00\u67c4\u5315\u9996\u3002\u8d2b\u9053\u5fdd\u5728\u672c\u6d3e\u638c\u95e8\uff0c\u867d\u7136\u672c\u9886\u4e0d\u6d4e\uff0c\u5fcd\u8fb1\u5728\u8fd9\u91cc\u517b\u4f24\uff0c\u4f46\u8fd8\u4e0d\u6562\u5bf9\u670b\u53cb\u6253\u4e00\u53e5\u8bf3\u8bed\u3002\u7126\u59d1\u5a18\uff0c\u4f60\u9053\u8fd9\u67c4\u5315\u9996\u662f\u505a\u751a\u4e48\u7528\u7684\uff1f\u201d\u7126\u5b9b\u513f\u6068\u6068\u7684\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u6c34\u4e91\u9053\u4eba\u62ac\u5934\u671b\u7740\u6708\u4eae\uff0c\u559f\u7136\u9053\uff1a\u201c\u655d\u6d3e\u7b2c\u5341\u56db\u4ee3\u638c\u95e8\u7956\u5e08\u83ca\u6f6d\u9053\u957f\u5f53\u5e74\u5251\u672f\u5929\u4e0b\u65e0\u53cc\uff0c\u53ea\u53ef\u60dc\u6027\u5b50\u521a\u50b2\uff0c\u6740\u4e86\u4e0d\u5c11\u4eba\uff0c\u7ed3\u4ec7\u592a\u591a\uff0c\u7ec8\u4e8e\u5404\u6d3e\u5251\u5ba2\u5927\u4f1a\u6052\u5c71\uff0c\u4ee5\u8f66\u8f6e\u6218\u6cd5\u6597\u4ed6\u4e00\u4eba\u3002\u83ca\u6f6d\u9053\u957f\u867d\u7136\u5251\u4e0b\u4f24\u4e86\u5bf9\u5934\u5341\u516b\u4eba\uff0c\u4f46\u6700\u540e\u7b4b\u75b2\u529b\u5c3d\uff0c\u8eab\u53d7\u91cd\u4f24\uff0c\u4e8e\u662f\u62d4\u51fa\u5315\u9996\u81ea\u6740\u800c\u6b7b\u3002\u672c\u6d3e\u56e0\u6b64\u5143\u6c14\u5927\u4f24\uff0c\u53c8\u5f97\u7f6a\u4e86\u5929\u4e0b\u82f1\u96c4\uff0c\u6b64\u540e\u5b9a\u4e0b\u4e00\u6761\u89c4\u77e9\uff0c\u6bcf\u540d\u5b66\u827a\u5b8c\u6bd5\u7684\u5f1f\u5b50\u90fd\u6388\u4e00\u67c4\u5315\u9996\u3002\u6d1e\u7384\u5e08\u5f1f\uff0c\u4f60\u5230\u90a3\u8fb9\u53bb\u3002\u201d\u6d1e\u7384\u4e0d\u660e\u4ed6\u7528\u610f\uff0c\u4f46\u8fd8\u662f\u671d\u4ed6\u624b\u6307\u6240\u6307\uff0c\u5411\u897f\u884c\u53bb\u3002\u6c34\u4e91\u7b49\u4ed6\u8d70\u51fa\u6570\u767e\u6b65\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u884c\u4e86\u3002\u201d\u6d1e\u7384\u505c\u6b65\u3002 \u3000\u3000\u6c34\u4e91\u4f4e\u58f0\u95ee\u95f5\u5b50\u534e\u9053\uff1a\u201c\u95f5\u5e08\u5f1f\uff0c\u8fd9\u628a\u5315\u9996\uff0c\u53eb\u4f5c\u751a\u4e48\uff1f\u201d \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u8fd9\u662f\u4ed9\u90fd\u6212\u6740\u5200\u3002\u201d\u6c34\u4e91\u53c8\u95ee\uff1a\u201c\u5e08\u7236\u6388\u4f60\u6212\u6740\u5200\u65f6\uff0c\u6709\u56db\u53e5\u751a\u4e48\u8bad\u793a\uff1f\u4f60\u4f4e\u58f0\u8bf4\u6765\u3002\u201d\u95f5\u5b50\u534e\u8083\u7136\u9053\uff1a\u201c\u4e25\u6212\u64c5\u6740\uff0c\u5584\u89c6\u73cd\u85cf\uff0c\u4e49\u6240\u4e0d\u654c\uff0c\u4e3e\u4ee5\u81ea\u6215\u3002\u201d \u3000\u3000\u6c34\u4e91\u70b9\u70b9\u5934\uff0c\u5411\u4e1c\u8fb9\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60\u5230\u90a3\u8fb9\u53bb\u3002\u201d\u5f85\u95f5\u5b50\u534e\u8d70\u8fdc\uff0c\u628a\u6d1e\u7384\u53eb\u56de\u6765\uff0c\u95ee\u9053\uff1a\u201c\u6d1e\u7384\u5e08\u5f1f\uff0c\u8fd9\u628a\u5315\u9996\uff0c\u53eb\u4f5c\u751a\u4e48\uff1f\u201d\u6d1e\u7384\u9053\uff1a\u201c\u4ed9\u90fd\u6212\u6740\u5200\u3002\u201d\u6c34\u4e91\u53c8\u95ee\uff1a\u201c\u5e08\u7236\u6388\u4f60\u6b64\u5200\u4e4b\u65f6\uff0c\u6709\u4f55\u8bad\u793a\uff1f\u201d\u6d1e\u7384\u8083\u7136\u9053\uff1a\u201c\u4e25\u6212\u64c5\u6740\uff0c\u5584\u89c6\u73cd\u85cf\uff0c\u4e49\u6240\u4e0d\u654c\uff0c\u4e3e\u4ee5\u81ea\u6215\u3002\u201d \u3000\u3000\u6c34\u4e91\u628a\u95f5\u5b50\u534e\u53eb\u56de\uff0c\u5bf9\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u9053\uff1a\u201c\u73b0\u4eca\u4e24\u4f4d\u53ef\u4ee5\u76f8\u4fe1\uff0c\u655d\u6d3e\u786e\u662f\u6709\u6b64\u8bad\u793a\u3002\u655d\u6d3e\u5b50\u5f1f\u72af\u6212\u6740\u4eba\uff0c\u4e5f\u662f\u6709\u7684\uff0c\u53ef\u662f\u51ed\u4ed6\u5982\u4f55\u4e0d\u8096\uff0c\u65e0\u8bba\u5982\u4f55\u4e0d\u6562\u7528\u8fd9\u6212\u6740\u5200\u6740\u4eba\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u8fd9\u5315\u9996\u4e3a\u751a\u4e48\u53eb\u2018\u6212\u6740\u5200\u2019\uff1f\u201d\u6c34\u4e91\u9053\uff1a\u201c\u655d\u6d3e\u9274\u4e8e\u83ca\u6f6d\u7956\u5e08\u7684\u8986\u8f99\uff0c\u4ece\u7b2c\u5341\u4e94\u4ee3\u7956\u5e08\u8d77\u4fbf\u5b9a\u4e0b\u4e00\u6761\u95e8\u89c4\uff0c\u4e25\u7981\u5984\u6740\u65e0\u8f9c\uff0c\u5426\u5219\u5230\u6bcf\u4e24\u5e74\u4e00\u6b21\u5728\u4ed9\u90fd\u5c71\u5927\u4f1a\uff0c\u4fbf\u5f97\u5728\u5e08\u957f\u5144\u5f1f\u4e4b\u524d\uff0c\u4ee5\u8fd9\u6212\u6740\u5200\u81ea\u884c\u4e86\u65ad\u3002\u95f5\u5e08\u5f1f\u8981\u6740\u7126\u5e2e\u4e3b\uff0c\u867d\u7136\u5f53\u5e74\u95f5\u5b50\u53f6\u5e08\u5144\u884c\u4e3a\u4e0d\u7aef\uff0c\u6709\u53d6\u6b7b\u4e4b\u9053\uff0c\u4f46\u4e3a\u5144\u62a5\u4ec7\uff0c\u672c\u6765\u4e5f\u4e0d\u7b97\u662f\u5984\u6740\uff0c\u53ef\u662f\u540e\u6765\u65e2\u77e5\u53d7\u5978\u4eba\u6311\u62e8\uff0c\u518d\u53bb\u52a0\u5bb3\uff0c\u90a3\u662f\u72af\u4e86\u91cd\u5927\u95e8\u89c4\uff0c\u8c05\u4ed6\u4e5f\u662f\u4e0d\u6562\u3002\u201d\u4ed6\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6212\u6740\u5200\u662f\u81ea\u6740\u7528\u7684\uff0c\u8981\u662f\u4ed9\u90fd\u5f1f\u5b50\u9047\u654c\u4e4b\u65f6\uff0c\u6b66\u529f\u4e0d\u5982\uff0c\u800c\u5bf9\u65b9\u53c8\u82e6\u82e6\u76f8\u903c\uff0c\u8131\u8eab\u4e0d\u5f97\uff0c\u90a3\u4e48\u4fbf\u987b\u4ee5\u6b64\u5315\u9996\u81ea\u6740\uff0c\u514d\u635f\u4ed9\u90fd\u5a01\u540d\u3002\u95f5\u5e08\u5f1f\u5c31\u7b97\u6562\u72af\u5e08\u95e8\u4e25\u89c4\uff0c\u5929\u4e0b\u6b66\u5668\u6b63\u591a\uff0c\u600e\u4f1a\u7528\u6212\u6740\u5200\u53bb\u6740\u4eba\uff1f\u800c\u4e14\u523a\u6740\u4e4b\u540e\uff0c\u600e\u4e48\u53c8\u4e0d\u628a\u5200\u5e26\u8d70\uff1f\u201d\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u542c\u5230\u8fd9\u91cc\uff0c\u90fd\u4e0d\u4f4f\u70b9\u5934\u3002 \u3000\u3000\u6c34\u4e91\u53c8\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u6211\u7ed9\u4f60\u77a7\u4e00\u5c01\u4fe1\u3002\u201d\u8bf4\u7740\u4ece\u68fa\u6750\u89d2\u91cc\u53d6\u51fa\u4e00\u4e2a\u5e03\u5305\uff0c\u6253\u4e86\u5f00\u6765\uff0c\u91cc\u9762\u662f\u4e00\u5806\u6587\u4ef6\u6742\u7269\u3002\u4ed6\u4ece\u4e2d\u6361\u51fa\u4e00\u4fe1\uff0c\u9012\u7ed9\u7126\u5b9b\u513f\u3002 \u3000\u3000\u7126\u5b9b\u513f\u773c\u671b\u8881\u627f\u5fd7\u3002\u8881\u627f\u5fd7\u70b9\u70b9\u5934\u3002\u7126\u5b9b\u513f\u63a5\u8fc7\u4fe1\u6765\uff0c\u6708\u5149\u4e0b\u89c1\u5c01\u76ae\u4e0a\u5199\u7740\u201c\u6025\u9001\u6c34\u4e91\u5927\u5e08\u5144\u4eb2\u542f\uff0c\u95f5\u7f04\u201d\u51e0\u4e2a\u5b57\uff0c\u77e5\u662f\u95f5\u5b50\u534e\u5199\u7ed9\u6c34\u4e91\u7684\u4fe1\uff0c\u62bd\u51fa\u4fe1\u7b3a\uff0c\u89c1\u7eb8\u7b3a\u4e0a\u7aef\u5370\u7740\u2018\u868c\u57e0\u901a\u5546\u5927\u5ba2\u6808\u7528\u7b3a\u201d\u7684\u7ea2\u5b57\uff0c\u4fe1\u4e0a\u7684\u5b57\u6b6a\u6b6a\u626d\u626d\uff0c\u6587\u7406\u4e5f\u4e0d\u751a\u901a\uff0c\u5199\u9053\uff1a\u201c\u6c34\u4e91\u5927\u5e08\u5144\uff1a\u4f60\u597d\u3002\u7126\u516c\u793c\u4e4b\u4e8b\uff0c\u5c0f\u5f1f\u5df2\u660e\u767d\u53d7\u4eba\u6b3a\u9a97\uff0c\u62a5\u4ec7\u751a\u4e48\u7684\u5c31\u6b64\u62c9\u5012\u4e0d\u5e72\u4e86\u3002\u4f46\u6628\u665a\u591c\u91cc\uff0c\u5c0f\u5f1f\u7684\u6212\u6740\u5200\u5ffd\u7136\u7ed9\u4e07\u6076\u72d7\u8d3c\u5077\u53bb\uff0c\u771f\u662f\u60ed\u6127\u4e4b\u81f3\u3002\u5982\u679c\u5bfb\u4e0d\u56de\u6765\uff0c\u6211\u518d\u6ca1\u9762\u76ee\u89c1\u5927\u5e08\u5144\u4e86\uff0c\u5343\u4e07\u5343\u4e07\u3002\u5c0f\u5f1f\u95f5\u5b50\u534e\u62dc\u4e0a\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u8bfb\u5b8c\u6b64\u4fe1\uff0c\u66f4\u65e0\u6000\u7591\uff0c\u8eab\u5b50\u98a4\u6296\uff0c\u76c8\u76c8\u5411\u95f5\u5b50\u534e\u62dc\u4e86\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u95f5\u53d4\u53d4\uff0c\u4f84\u5973\u513f\u9519\u602a\u597d\u4eba\uff0c\u5192\u72af\u4f60\u8001\u4eba\u5bb6\u5566\u3002\u201d\u62dc\u7f62\u53c8\u5411\u6d1e\u7384\u8d54\u793c\u3002\u4e24\u4eba\u8fde\u5fd9\u8fd8\u793c\u3002 \u3000\u3000\u95f5\u5b50\u534e\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u54ea\u4e2a\u72d7\u8d3c\u5077\u4e86\u8fd9\u628a\u5200\u53bb\uff0c\u5bb3\u6b7b\u4e86\u7126\u5e2e\u4e3b\u3002\u4ed6\u7559\u5200\u5c38\u4e0a\uff0c\u5c31\u662f\u8981\u4f60\u7591\u5fc3\u6211\u5440\u3002\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4f84\u5973\u771f\u662f\u5364\u83bd\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e00\u7740\uff0c\u53ea\u9053\u95f5\u53d4\u53d4\u5bb3\u4e86\u7239\u7239\u540e\uff0c\u8fd8\u8981\u901e\u82f1\u96c4\u597d\u6c49\uff0c\u7559\u5200\u793a\u5a01\u3002\u201d\u95f5\u5b50\u534e\u9053\uff1a\u201c\u6211\u5931\u4e86\u6212\u6740\u5200\uff0c\u548c\u6d1e\u7384\u5e08\u5144\u5230\u5904\u627e\u5bfb\uff0c\u6ca1\u4e00\u70b9\u7709\u76ee\uff0c\u540e\u6765\u63a5\u5230\u5927\u5e08\u5144\u98de\u5e16\uff0c\u53ec\u6211\u4eec\u5230\u4eac\u5e08\u6765\uff0c\u8fd9\u624d\u52a8\u8eab\u3002\u8def\u4e0a\u4f60\u4eec\u6ca1\u5934\u6ca1\u8111\u7684\u6740\u6765\uff0c\u6211\u4e5f\u53ea\u597d\u6ca1\u5934\u6ca1\u8111\u7684\u8ddf\u4f60\u4eec\u4e71\u6253\u4e00\u9635\u3002\u5e78\u4e8f\u8881\u76f8\u516c\u8d76\u5230\uff0c\u624d\u5f04\u660e\u767d\u8fd9\u56de\u4e8b\u3002\u201d \u3000\u3000\u6c34\u4e91\u9053\uff1a\u201c\u7b49\u6211\u4eec\u7684\u4e8b\u4e86\u7ed3\u4e4b\u540e\uff0c\u8981\u662f\u8d2b\u9053\u4fa5\u5e78\u7559\u5f97\u6027\u547d\uff0c\u5b9a\u8981\u5e2e\u7126\u59d1\u5a18\u627e\u5230\u8fd9\u5077\u5200\u6740\u4eba\u7684\u5978\u8d3c\u3002\u8fd9\u4ef6\u4e8b\u4ed9\u90fd\u6d3e\u7ec8\u7a76\u4e5f\u8131\u4e0d\u4e86\u7275\u8fde\u3002\u201d\u7126\u5b9b\u513f\u53c8\u88e3\u887d\u62dc\u8c22\uff0c\u5c06\u5315\u9996\u8fd8\u7ed9\u95f5\u5b50\u534e\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff0c\u4ed6\u4eec\u5e08\u5144\u5f1f\u53ea\u6015\u53e6\u6709\u79d8\u4e8b\u5546\u914c\uff0c\u5916\u4eba\u4e0d\u4fbf\u53c2\u4e0e\uff0c\u4fbf\u62f1\u624b\u9053\uff1a\u201c\u5144\u5f1f\u5c31\u6b64\u522b\u8fc7\u3002\u201d\u4e24\u4eba\u548c\u6c34\u4e91\u7b49\u4f5c\u522b\uff0c\u8d70\u51fa\u6570\u5341\u6b65\uff0c\u6b63\u8981\u4e0b\u5c97\uff0c\u6d1e\u7384\u5ffd\u7136\u5927\u53eb\uff1a\u201c\u4e24\u4f4d\u8bf7\u7559\u6b65\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u4e00\u9f50\u505c\u6b65\u3002\u6d1e\u7384\u9053\u4eba\u5954\u5c06\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u7126\u59d1\u5a18\uff0c\u8d2b\u9053\u6709\u4e00\u4ef6\u4e8b\u60f3\u8bf4\uff0c\u8bf7\u4e24\u4f4d\u522b\u602a\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u9053\u957f\u4f46\u8bf4\u4e0d\u59a8\u3002\u201d\u6d1e\u7384\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4e8b\uff0c\u8981\u8bf7\u4e24\u4f4d\u5343\u4e07\u4e0d\u53ef\u6cc4\u6f0f\u3002\u672c\u6765\u4e0d\u987b\u8d2b\u9053\u591a\u5634\uff0c\u5b9e\u56e0\u4e0e\u655d\u5e08\u5144\u6027\u547d\u6538\u5173\uff0c\u4e0d\u5f97\u4e0d\u5192\u6627\u76f8\u6c42\u3002\u201d\u6309\u7167\u6c5f\u6e56\u9053\u4e0a\u89c4\u77e9\uff0c\u522b\u5e2e\u522b\u6d3e\u4efb\u4f55\u8be1\u79d8\u602a\u5f02\u4e4b\u4e8b\uff0c\u65c1\u4eba\u77a7\u5728\u773c\u91cc\uff0c\u51b3\u4e0d\u80fd\u4f20\u8a00\u8c08\u8bba\uff0c\u5426\u5219\u51f6\u6740\u707e\u7978\u7acb\u81f3\uff0c\u6b64\u4e8b\u4eba\u6240\u5171\u77e5\uff0c\u4f46\u6d1e\u7384\u7adf\u5982\u6b64\u4e0d\u653e\u5fc3\uff0c\u4e0d\u60dc\u5192\u72af\u53ee\u5631\uff0c\u81ea\u662f\u5927\u975e\u5bfb\u5e38\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u52a8\uff0c\u867d\u7136\u4e8b\u4e0d\u5e72\u5df1\uff0c\u4f46\u521a\u624d\u89c1\u6c34\u4e91\u9053\u4eba\u65e0\u610f\u4e2d\u663e\u9732\u4e86\u4e00\u624b\u6b66\u529f\uff0c\u4e0d\u7981\u751f\u4e86\u60fa\u60fa\u76f8\u60dc\u4e4b\u610f\uff0c\u5bf9\u6d1e\u7384\u9053\uff1a\u201c\u4e0d\u77e5\u4ee4\u5e08\u5144\u9047\u5230\u4e86\u751a\u4e48\u5371\u96be\u4e4b\u4e8b\uff0c\u5144\u5f1f\u6216\u53ef\u76f8\u52a9\u4e00\u81c2\u3002\u201d \u3000\u3000\u6d1e\u7384\u548c\u8881\u627f\u5fd7\u4ea4\u8fc7\u624b\uff0c\u77e5\u4ed6\u6b66\u529f\u5353\u7edd\uff0c\u4e0d\u4f46\u9ad8\u51fa\u81ea\u5df1\u5341\u500d\uff0c\u4e5f\u8fdc\u5728\u4ed9\u90fd\u7b2c\u4e00\u9ad8\u624b\u6c34\u4e91\u5e08\u5144\u4e4b\u4e0a\uff0c\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u5934\u4e00\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u8881\u76f8\u516c\u4ed7\u4e49\u76f8\u52a9\uff0c\u771f\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u5f85\u8d2b\u9053\u7980\u8fc7\u5927\u5e08\u5144\u3002\u201d\u5306\u5306\u56de\u53bb\uff0c\u4f4e\u58f0\u548c\u6c34\u4e91\u3001\u95f5\u5b50\u534e\u5546\u91cf\u3002\u4e09\u4eba\u8c08\u4e86\u826f\u4e45\uff0c\u4f3c\u4e4e\u96be\u4ee5\u51b3\u5b9a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u60f3\u9053\uff1a\u201c\u65e2\u7136\u4ed6\u4eec\u5927\u6709\u4e3a\u96be\uff0c\u4e0d\u613f\u5916\u4eba\u63d2\u624b\uff0c\u90a3\u4e48\u4e5f\u4e0d\u5fc5\u591a\u4e8b\u4e86\u3002\u201d\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4e24\u4f4d\u9053\u957f\u3001\u95f5\u5144\uff0c\u5144\u5f1f\u5148\u8d70\u4e00\u6b65\uff0c\u540e\u4f1a\u6709\u671f\uff01\u201d\u4e00\u62f1\u624b\u5c31\u8981\u4e0b\u5c97\u3002 \u3000\u3000\u6c34\u4e91\u9053\u4eba\u53eb\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u8bf7\u8fc7\u6765\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d\u8881\u627f\u5fd7\u8f6c\u8eab\u8d70\u8fd1\u3002\u6c34\u4e91\u9053\uff1a\u201c\u8881\u76f8\u516c\u80af\u62d4\u5200\u76f8\u52a9\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u5b9e\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u4e0d\u8fc7\u8fd9\u662f\u672c\u95e8\u7684\u79c1\u4e8b\uff0c\u60c5\u52bf\u51f6\u9669\u4e07\u5206\uff0c\u5b9e\u5728\u4e0d\u6562\u8981\u8881\u76f8\u516c\u65e0\u6545\u72af\u9669\u3002\u8fd8\u8bf7\u522b\u602a\u8d2b\u9053\u4e0d\u8bc6\u597d\u6b79\u3002\u201d\u8bf4\u7740\u62f1\u624b\u884c\u793c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77e5\u4ed6\u662f\u4e00\u7247\u597d\u610f\uff0c\u5fc3\u60f3\u8fd9\u4eba\u5012\u4e5f\u9887\u5177\u82f1\u96c4\u6c14\u6982\uff0c\u8bf4\u9053\uff1a\u201c\u9053\u957f\u8bf4\u54ea\u91cc\u8bdd\u6765\uff1f\u65e2\u662f\u5982\u6b64\uff0c\u5c31\u6b64\u544a\u8f9e\u3002\u9053\u957f\u5982\u6709\u9700\u7528\u4e4b\u5904\uff0c\u5144\u5f1f\u81ea\u5f53\u5c3d\u529b\uff0c\u968f\u65f6\u9001\u4e2a\u4fe1\u5230\u6b63\u6761\u5b50\u80e1\u540c\u5c31\u662f\u3002\u201d \u3000\u3000\u6c34\u4e91\u4f4e\u5934\u4e0d\u8bed\uff0c\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\u5982\u6b64\u4e49\u6c14\uff0c\u6211\u4eec\u7684\u4e8b\u867d\u7136\u8bf4\u6765\u7f9e\u4eba\uff0c\u5982\u518d\u76f8\u7792\uff0c\u53ef\u5c31\u4e0d\u591f\u670b\u53cb\u4e86\u3002\u4e24\u4f4d\u8bf7\u5750\u3002\u6d1e\u7384\u5e08\u5f1f\uff0c\u4f60\u5bf9\u4e24\u4f4d\u8bf4\u7f62\u3002\u201d \u3000\u3000\u6d1e\u7384\u7b49\u4e24\u4eba\u5728\u77f3\u4e0a\u5750\u597d\uff0c\u81ea\u5df1\u4e5f\u5750\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6069\u5e08\u9ec4\u6728\u9053\u4eba\u751f\u6027\u597d\u52a8\uff0c\u7d20\u559c\u5230\u5904\u4e91\u6e38\uff0c\u9664\u4e86\u4e24\u5e74\u4e00\u6b21\u7684\u4ed9\u90fd\u5927\u4f1a\u4e4b\u5916\uff0c\u5e73\u65e5\u5c11\u5728\u5c71\u4e0a\u3002\u4e94\u5e74\u524d\u7684\u4e2d\u79cb\uff0c\u53c8\u662f\u5927\u4f1a\u4e4b\u671f\uff0c\u6069\u5e08\u7adf\u7136\u5e76\u4e0d\u56de\u5c71\u4e3b\u6301\uff0c\u4e5f\u4e0d\u5e26\u4fe1\u56de\u6765\uff0c\u8fd9\u662f\u4ece\u6765\u6ca1\u6709\u7684\u4e8b\uff0c\u4f17\u5f1f\u5b50\u53c8\u662f\u5947\u602a\uff0c\u53c8\u662f\u62c5\u5fe7\u3002\u6069\u5e08\u8fd9\u6b21\u662f\u5230\u5357\u65b9\u4e91\u6e38\u91c7\u836f\uff0c\u5927\u4f19\u513f\u5fd9\u5206\u6279\u5230\u4e91\u8d35\u4e24\u5e7f\u67e5\u8bbf\uff0c\u5404\u8def\u90fd\u6ca1\u6d88\u606f\u3002\u6211\u548c\u95f5\u5e08\u54e5\u5374\u5728\u5ba2\u5e97\u4e4b\u4e2d\uff0c\u5f97\u5230\u70b9\u82cd\u6d3e\u8ffd\u98ce\u5251\u4e07\u91cc\u98ce\u7684\u4f20\u8baf\uff0c\u8bf4\u6709\u6025\u4e8b\u9080\u6211\u4eec\u524d\u5f80\u3002\u6211\u4eec\u4e24\u4eba\u8d76\u5230\u4e91\u5357\u5927\u7406\u4e07\u5927\u54e5\u5bb6\u4e2d\uff0c\u89c1\u4ed6\u8eab\u53d7\u91cd\u4f24\uff0c\u8eba\u5728\u5e8a\u4e0a\u3002\u4e00\u95ee\u4e4b\u4e0b\uff0c\u539f\u6765\u662f\u4e3a\u4e86\u6211\u4eec\u6069\u5e08\u624d\u53d7\u7684\u4f24\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u60f3\u8d77\u7a0b\u9752\u7af9\u66fe\u8bf4\u9ec4\u6728\u9053\u4eba\u662f\u6b7b\u4e8e\u4e94\u6bd2\u6559\u4e4b\u624b\uff0c\u6697\u6697\u70b9\u5934\uff0c\u542c\u6d1e\u7384\u53c8\u9053\uff1a\u201c\u8ffd\u98ce\u5251\u4e07\u5927\u54e5\u8bf4\u9053\uff0c\u90a3\u5929\u4ed6\u5230\u5927\u7406\u57ce\u5916\u8bbf\u53cb\uff0c\u89c1\u5230\u6211\u4eec\u6069\u5e08\u53d7\u4eba\u56f4\u653b\u3002\u70b9\u82cd\u6d3e\u8ddf\u4ed9\u90fd\u6d3e\u7d20\u6709\u6e0a\u6e90\uff0c\u4ed6\u5f53\u5373\u4ed7\u5251\u76f8\u52a9\u3002\u5c82\u77e5\u5bf9\u65b9\u4e2a\u4e2a\u90fd\u662f\u9ad8\u624b\uff0c\u4e24\u4eba\u5be1\u4e0d\u654c\u4f17\uff0c\u4e07\u5927\u54e5\u5148\u906d\u6bd2\u624b\uff0c\u660f\u5012\u5728\u5730\uff0c\u540e\u6765\u7531\u4eba\u6551\u56de\uff0c\u6069\u5e08\u5374\u662f\u751f\u6b7b\u4e0d\u660e\u3002\u4e07\u5927\u54e5\u80a9\u5934\u548c\u80c1\u4e0b\u90fd\u4e3a\u94a2\u722a\u6240\u4f24\uff0c\u722a\u4e0a\u5582\u4e86\u5267\u6bd2\u3002\u770b\u8fd9\u60c5\u5f62\uff0c\u5fc5\u662f\u4e94\u6bd2\u6559\u6240\u4e3a\u3002\u4ed6\u540e\u6765\u5343\u8f9b\u4e07\u82e6\u7684\u6c42\u5230\u540d\u533b\uff0c\u8fd9\u624d\u6b7b\u91cc\u9003\u751f\u3002\u4e8e\u662f\u6211\u4eec\u4ed9\u90fd\u4e09\u5341\u4e8c\u5f1f\u5b50\u540c\u4e0b\u4e91\u5357\u5bfb\u5e08\uff0c\u8981\u627e\u4e94\u6bd2\u6559\u62a5\u4ec7\u3002\u53ef\u662f\u56db\u5e74\u6765\u97f3\u8baf\u5168\u65e0\uff0c\u6069\u5e08\u81ea\u662f\u51f6\u591a\u5409\u5c11\u3002\u4e94\u6bd2\u6559\u53c8\u9690\u79d8\u5f02\u5e38\uff0c\u8e0f\u904d\u4e86\u4e91\u5357\u5168\u7701\uff0c\u59cb\u7ec8\u6ca1\u534a\u70b9\u7ebf\u7d22\uff0c\u5927\u5bb6\u675f\u624b\u65e0\u7b56\uff0c\u624d\u79bb\u4e91\u5357\u3002\u540e\u6765\u5317\u65b9\u4f20\u6765\u6d88\u606f\uff0c\u8bf4\u4e94\u6bd2\u6559\u6559\u4e3b\u4f55\u94c1\u624b\u5230\u4e86\u5317\u4eac\u2026\u2026\u201d \u3000\u3000\u8881\u627f\u5fd7\u201c\u554a\u201d\u4e86\u4e00\u58f0\u3002\u6d1e\u7384\u9053\uff1a\u201c\u8881\u76f8\u516c\u8bc6\u5f97\u5979\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u6709\u51e0\u4f4d\u670b\u53cb\u6628\u5929\u521a\u7ed9\u5979\u6bd2\u624b\u6240\u4f24\u3002\u201d\u6d1e\u7384\u9053\uff1a\u201c\u4ee4\u53cb\u4e0d\u788d\u4e8b\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u773c\u4e0b\u5df2\u7136\u65e0\u59a8\u3002\u201d \u3000\u3000\u6d1e\u7384\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u771f\u662f\u5929\u5e78\u3002\u6211\u4eec\u4e00\u5f97\u8baf\uff0c\u5927\u5e08\u5144\u4fbf\u4f20\u4e0b\u6025\u4ee4\uff0c\u4ed9\u90fd\u5f1f\u5b50\u9f50\u96c6\u4eac\u5e08\u3002\u6211\u4eec\u5728\u6765\u4eac\u9014\u4e2d\u9047\u5230\u7126\u59d1\u5a18\uff0c\u90a3\u4e0d\u5fc5\u8bf4\u4e86\u3002\u5927\u5e08\u5144\u6bd4\u6211\u4eec\u5148\u5230\uff0c\u4ed6\u4e0e\u4f55\u94c1\u624b\u72ed\u8def\u76f8\u9022\u3002\u90a3\u8d31\u5a62\u7adf\u7136\u51fa\u8a00\u8ba5\u523a\uff0c\u5341\u5206\u65e0\u793c\u3002\u5927\u5e08\u5144\u8ddf\u5979\u52a8\u8d77\u624b\u6765\uff0c\u8fd9\u8d31\u5a62\u624b\u811a\u6ed1\u6e9c\uff0c\u5927\u5e08\u5144\u4e00\u4e0d\u7559\u795e\uff0c\u989d\u4e0a\u4e3a\u5979\u5de6\u624b\u94c1\u94a9\u6240\u4e2d\uff0c\u4e0b\u76d8\u53c8\u4e2d\u4e86\u5979\u4e94\u679a\u6697\u5668\u3002\u5979\u53ea\u9053\u8fd9\u6697\u5668\u5582\u6709\u5267\u6bd2\uff0c\u5927\u5e08\u5144\u4e00\u5b9a\u6d3b\u4e0d\u4e86\uff0c\u51b7\u7b11\u51e0\u58f0\u4fbf\u8d70\u4e86\u3002\u597d\u5728\u5927\u5e08\u5144\u5185\u529f\u7cbe\u6e5b\uff0c\u53c8\u77e5\u5bf9\u5934\u5468\u8eab\u5e26\u6bd2\uff0c\u5728\u6bd4\u6b66\u4e4b\u524d\u5df2\u5148\u670d\u4e86\u4e0d\u5c11\u89e3\u836f\uff0c\u8eab\u8fb9\u53c8\u5e26\u4e86\u8bf8\u822c\u5916\u7528\u89e3\u6bd2\u818f\u4e39\uff0c\u8fd9\u624d\u6ca1\u6709\u906d\u96be\u3002\u201d \u3000\u3000\u6c34\u4e91\u53f9\u9053\uff1a\u201c\u8d2b\u9053\u6015\u5979\u77e5\u6211\u4e0d\u6b7b\uff0c\u518d\u6765\u8d76\u5c3d\u6740\u7edd\uff0c\u662f\u4ee5\u4e0d\u6562\u5728\u5bd3\u6240\u517b\u4f24\uff0c\u53ea\u5f97\u627e\u4e86\u8fd9\u6837\u53e4\u602a\u7684\u4e00\u4e2a\u5730\u65b9\u9759\u517b\uff0c\u518d\u8fc7\u4e09\u4e2a\u6708\uff0c\u6bd2\u6c14\u53ef\u4ee5\u6162\u6162\u62d4\u5c3d\u3002\u5e08\u7236\u591a\u534a\u5df2\u4e27\u5728\u8d31\u5a62\u624b\u4e0b\uff0c\u8fd9\u4ec7\u975e\u62a5\u4e0d\u53ef\u3002\u53ea\u662f\u5bf9\u5934\u624b\u6bb5\u592a\u8fa3\uff0c\u6bd2\u7269\u5389\u5bb3\uff0c\u662f\u4ee5\u8d2b\u9053\u4e0d\u6562\u62d6\u7d2f\u670b\u53cb\u3002\u201d \u3000\u3000\u95f5\u5b50\u534e\u95ee\u9053\uff1a\u201c\u8881\u76f8\u516c\u600e\u4e48\u4e5f\u8ddf\u4e94\u6bd2\u6559\u7ed3\u4e86\u4ec7\uff1f\u201d\u8881\u627f\u5fd7\u4e8e\u662f\u5c06\u5982\u4f55\u9047\u5230\u9526\u8863\u6bd2\u4e10\u9f50\u4e91\u7488\u3001\u7a0b\u9752\u7af9\u5982\u4f55\u88ab\u8001\u4e10\u5a46\u6293\u4f24\u7684\u4e8b\u7b80\u7565\u8bf4\u4e86\u3002\u6c34\u4e91\u9053\uff1a\u201c\u8881\u76f8\u516c\u65e2\u8ddf\u4ed6\u4eec\u5e76\u65e0\u6df1\u4ec7\uff0c\u5403\u4e86\u4e00\u70b9\u5c0f\u4e8f\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u4f60\u5343\u91d1\u4e4b\u4f53\uff0c\u72af\u4e0d\u7740\u8ddf\u8fd9\u79cd\u6bd2\u5982\u86c7\u874e\u4e4b\u4eba\u76f8\u62da\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\u81ea\u5df1\u6709\u7236\u4ec7\u5728\u8eab\uff0c\u53c8\u8981\u8f85\u4f50\u95ef\u738b\u548c\u4e49\u5144\u674e\u5ca9\u56fe\u8c0b\u5927\u4e8b\uff0c\u8fd9\u79cd\u6c5f\u6e56\u4e0a\u7684\u5c0f\u6028\uff0c\u539f\u4e0d\u80fd\u8fc7\u4e8e\u5f53\u771f\uff0c\u5426\u5219\u7ea0\u7f20\u8d77\u6765\uff0c\u6c38\u65e0\u4e86\u5c40\uff0c\u4e8e\u662f\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u9053\u957f\u8bf4\u5f97\u662f\u3002\u6211\u6709\u4e00\u53ea\u6731\u775b\u51b0\u87fe\uff0c\u53ef\u7ed9\u9053\u957f\u5438\u6bd2\u3002\u201d\u5f53\u4e0b\u7528\u51b0\u87fe\u66ff\u4ed6\u5438\u4e86\u4e00\u6b21\u6bd2\uff0c\u4e71\u77f3\u5c97\u4e0a\u65e0\u9152\u6d78\u51fa\u87fe\u4e2d\u6bd2\u6db2\uff0c\u4e8e\u662f\u628a\u51b0\u87fe\u501f\u7ed9\u6d1e\u7384\uff0c\u6559\u4e86\u7528\u6cd5\uff0c\u8981\u4ed6\u66ff\u6c34\u4e91\u5438\u5c3d\u6bd2\u6c14\u9001\u56de\u3002\u6c34\u4e91\u3001\u95f5\u5b50\u534e\u3001\u6d1e\u7384\u4e0d\u4f4f\u9053\u8c22\u3002 \u3000\u3000\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u7f13\u7f13\u4e0b\u5c97\uff0c\u8d70\u5230\u4e00\u534a\uff0c\u7126\u5b9b\u513f\u5ffd\u5f80\u77f3\u4e0a\u4e00\u5750\uff0c\u8f7b\u8f7b\u555c\u6ce3\u3002\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u7126\u59d1\u5a18\uff0c\u4f60\u4e0d\u8212\u670d\u4e48\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u6447\u6447\u5934\uff0c\u62ed\u5e72\u6cea\u75d5\uff0c\u82e5\u65e0\u5176\u4e8b\u7684\u7ad9\u4e86\u8d77\u6765\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e00\u6765\uff0c\u5979\u91d1\u9f99\u5e2e\u548c\u4ed9\u90fd\u6d3e\u867d\u7136\u5316\u654c\u4e3a\u53cb\uff0c\u4f46\u5979\u62a5\u6740\u7236\u5927\u4ec7\u4e4b\u4e8b\uff0c\u5374\u66f4\u662f\u6e3a\u832b\u4e86\u3002\u4e5f\u96be\u4e3a\u8fd9\u6837\u4e00\u4e2a\u5e74\u8f7b\u59d1\u5a18\uff0c\u5c45\u7136\u8fd9\u822c\u786c\u6717\u3002\u201d \u3000\u3000\u4e24\u4eba\u56de\u8fdb\u57ce\u91cc\uff0c\u5929\u5c06\u5fae\u660e\uff0c\u8881\u627f\u5fd7\u628a\u7126\u5b9b\u513f\u9001\u56de\u91d1\u9f99\u5e2e\u5bd3\u6240\uff0c\u81ea\u56de\u6b63\u6761\u5b50\u80e1\u540c\u3002\u4ed6\u5728\u957f\u8857\u4e00\u6392\u6c11\u623f\u5c4b\u9876\u4e0a\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u500f\u7136\u4e4b\u95f4\uff0c\u5df2\u8fc7\u4e86\u51e0\u6761\u8857\uff0c\u4e00\u65f6\u5954\u5f97\u5174\u53d1\uff0c\u4f7f\u51fa\u201c\u795e\u884c\u767e\u53d8\u201d\u7edd\u6280\uff0c\u771f\u5982\u98de\u71d5\u63a0\u6ce2\u3001\u6d41\u661f\u6a2a\u7a7a\u4e00\u822c\uff0c\u8033\u65c1\u98ce\u52a8\uff0c\u8db3\u5e95\u65e0\u58f0\uff0c\u6b63\u5954\u5f97\u9ad8\u5174\uff0c\u5ffd\u542c\u8eab\u65c1\u4f4e\u559d\u4e00\u58f0\uff1a\u201c\u597d\u529f\u592b\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u6597\u7136\u4f4f\u8db3\uff0c\u767d\u5f71\u5fae\u6643\uff0c\u4e00\u4eba\u4ece\u8eab\u65c1\u63a0\u8fc7\uff0c\u7b11\u9053\uff1a\u201c\u8ffd\u5f97\u4e0a\u6211\u5417\uff1f\u201d\u8bed\u58f0\u65b9\u6bd5\uff0c\u5df2\u7a9c\u5728\u4e03\u516b\u4e08\u5916\u3002\u8881\u627f\u5fd7\u89c1\u8fd9\u4eba\u8eab\u6cd5\u5947\u5feb\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff1a\u201c\u6b64\u4eba\u662f\u8c01\uff1f\u8f7b\u8eab\u529f\u592b\u662f\u5982\u6b64\u4e86\u5f97\uff1f\u201d\u4ed6\u5c11\u5e74\u4eba\u65e2\u597d\u5947\uff0c\u53c8\u597d\u80dc\uff0c\u63d0\u6c14\u75be\u8ffd\u3002\u90a3\u4eba\u6beb\u4e0d\u56de\u987e\uff0c\u5982\u98de\u5954\u8dd1\u3002\u65f6\u5019\u4e00\u957f\uff0c\u8881\u627f\u5fd7\u7684\u8f7b\u8eab\u529f\u592b\u7ec8\u4e8e\u9ad8\u51fa\u4e00\u7b79\uff0c\u811a\u4e0b\u52a0\u52b2\uff0c\u7247\u523b\u95f4\u8ffd\u8fc7\u4e86\u5934\uff0c\u8d76\u5728\u90a3\u4eba\u9762\u524d\u6570\u4e08\uff0c\u56de\u8f6c\u8eab\u6765\u3002 \u3000\u3000\u90a3\u4eba\u683c\u683c\u5a07\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4eca\u65e5\u6211\u624d\u5f53\u771f\u670d\u4f60\u5566\uff01\u201d \u3000\u3000\u53ea\u89c1\u5979\u957f\u8896\u63a9\u53e3\uff0c\u8eab\u5982\u82b1\u679d\u98a4\u8885\uff0c\u6b63\u662f\u4e94\u6bd2\u6559\u6559\u4e3b\u4f55\u94c1\u624b\u3002\u5979\u5168\u8eab\u767d\u8863\u5982\u96ea\uff0c\u7ed9\u8db3\u5e95\u9ed1\u74e6\u4e00\u886c\uff0c\u66f4\u662f\u9ed1\u7684\u6108\u9ed1\uff0c\u767d\u7684\u6108\u767d\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u4eba\u6240\u7a7f\u591c\u884c\u8863\u975e\u9ed1\u5373\u7070\uff0c\u597d\u5f97\u591c\u4e2d\u4e0d\u6613\u4e3a\u4eba\u53d1\u89c9\uff0c\u654c\u4eba\u53d1\u5c04\u6697\u5668\u4e0d\u80fd\u53d6\u5f97\u51c6\u5934\uff0c\u5979\u7adf\u7136\u7a7f\u4e00\u8eab\u767d\u8863\uff0c\u82e5\u975e\u81ea\u6043\u6b66\u827a\u9ad8\u5f3a\uff0c\u51b3\u4e0d\u80fd\u5982\u6b64\u8086\u65e0\u5fcc\u60ee\u3002\u8881\u627f\u5fd7\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u4f55\u6559\u4e3b\u6709\u4f55\u89c1\u6559\uff1f\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\u524d\u65e5\u6789\u9a7e\uff0c\u6709\u8bb8\u591a\u788d\u624b\u788d\u811a\u4e4b\u4eba\u5728\u573a\uff0c\u5927\u5bb6\u5206\u4e86\u5fc3\uff0c\u4e0d\u80fd\u597d\u597d\u89c1\u4e2a\u9ad8\u4e0b\u3002\u5c0f\u59b9\u4eca\u65e5\u4e13\u8bda\u524d\u6765\uff0c\u8ba8\u6559\u51e0\u62db\u3002\u201d\u8fb9\u8bf4\u8fb9\u7b11\uff0c\u58f0\u97f3\u5a07\u5a9a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6559\u4e3b\u8fd9\u822c\u8eab\u624b\uff0c\u5c31\u5728\u7537\u5b50\u4e2d\u4e5f\u662f\u96be\u5f97\u4e00\u89c1\u3002\u5144\u5f1f\u662f\u5341\u5206\u4f69\u670d\u7684\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\u524d\u65e5\u8bd5\u62f3\uff0c\u638c\u98ce\u51cc\u5389\u4e4b\u6781\u3002\u5c0f\u59b9\u529b\u6c14\u4e0d\u591f\uff0c\u4e0d\u6562\u63a5\u62db\u3002\u4eca\u65e5\u6bd4\u6bd4\u5175\u5203\u5982\u4f55\uff1f\u201d\u4e5f\u4e0d\u7b49\u8881\u627f\u5fd7\u56de\u7b54\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5df2\u5c06\u8170\u95f4\u4e00\u6761\u8f6f\u97ad\u6296\u4e86\u51fa\u6765\uff0c\u5fae\u5149\u4e2d\u4f46\u89c1\u97ad\u4e0a\u5168\u662f\u7ec6\u523a\u5012\u94a9\uff0c\u53ea\u8981\u7ed9\u5b83\u626b\u4e2d\u4e00\u4e0b\uff0c\u76ae\u8089\u5b9a\u4f1a\u7ed9\u626f\u4e0b\u4e00\u5927\u5757\u6765\u3002 \u3000\u3000\u4f55\u94c1\u624b\u5a07\u6ef4\u6ef4\u7684\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u8fd9\u53eb\u505a\u874e\u5c3e\u97ad\uff0c\u523a\u4e0a\u662f\u6709\u6bd2\u7684\uff0c\u4f60\u8981\u52a0\u610f\u5c0f\u5fc3\uff0c\u597d\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u542c\u5979\u8bf4\u8bdd\uff0c\u4e0d\u89c9\u6253\u4e86\u4e2a\u5bd2\u6218\u3002 \u3000\u3000\u5979\u8bed\u6c14\u6e29\u67d4\uff0c\u5173\u5207\u4f53\u8d34\uff0c\u542b\u610f\u5374\u5341\u5206\u72e0\u6bd2\uff0c\u4e24\u8005\u6d51\u4e0d\u76f8\u79f0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u6b32\u8ddf\u5979\u6beb\u6ca1\u6765\u7531\u7684\u6bd4\u6b66\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5931\u966a\u4e86\uff01\u201d \u3000\u3000\u4f55\u94c1\u624b\u4e0d\u7b49\u4ed6\u9000\u5f00\uff0c\u624b\u8155\u4e00\u6296\uff0c\u874e\u5c3e\u97ad\u52bf\u631f\u52b2\u98ce\uff0c\u5f84\u6251\u524d\u80f8\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0a\u8eab\u5411\u540e\u4e00\u4ef0\uff0c\u907f\u5f00\u4e86\u8fd9\u62db\uff0c\u4e0d\u7b49\u874e\u5c3e\u97ad\u7b2c\u4e8c\u62db\u518d\u5230\uff0c\u5df2\u7a9c\u51fa\u6570\u4e08\u3002\u4f55\u94c1\u624b\u77e5\u9053\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u6717\u58f0\u53eb\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u7684\u5f1f\u5b50\u5982\u6b64\u8113\u5305\uff0c\u8d25\u574f\u4e86\u5e08\u5c0a\u4e00\u4e16\u5a01\u540d\uff0c\u563b\u563b\uff01\u201d\u8881\u627f\u5fd7\u4e00\u6123\u505c\u6b65\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u51e0\u6b21\u76f8\u8ba9\uff0c\u4ed6\u4eec\u4e94\u6bd2\u6559\u9a84\u7eb5\u60ef\u4e86\uff0c\u8fd8\u9053\u6211\u5f53\u771f\u6015\u5979\u3002\u201d\u5fc3\u5ff5\u5fae\u52a8\u4e4b\u9645\uff0c\u767d\u5f71\u95ea\u5904\uff0c\u874e\u5c3e\u97ad\u53c8\u5e26\u7740\u4e00\u80a1\u8165\u98ce\u6251\u5230\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7709\u5934\u4e00\u76b1\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u7b49\u5582\u6bd2\u5175\u5668\u7eb5\u7136\u5389\u5bb3\uff0c\u7ec8\u7a76\u4e3a\u6b63\u4eba\u541b\u5b50\u6240\u4e0d\u53d6\u3002\u5979\u597d\u597d\u4e00\u4e2a\u5973\u5b50\uff0c\u5374\u8eab\u5728\u90aa\u6559\uff0c\u4ee5\u81f4\u884c\u4e8b\u4e0d\u7aef\u3002\u201d\u6599\u60f3\u874e\u5c3e\u97ad\u5168\u97ad\u6709\u6bd2\uff0c\u4e0d\u80fd\u767d\u624b\u62a2\u593a\uff0c\u7d22\u6027\u53cc\u624b\u62e2\u5165\u8896\u4e2d\uff0c\u8eab\u968f\u610f\u8f6c\uff0c\u7684\u6e9c\u6e9c\u7684\u4e1c\u95ea\u897f\u907f\u3002\u4f55\u94c1\u624b\u97ad\u6cd5\u867d\u5feb\uff0c\u54ea\u91cc\u5e26\u5f97\u5230\u4ed6\u7684\u4e00\u7247\u8863\u89d2\uff1f \u3000\u3000\u8f6c\u77ac\u95f4\u62c6\u4e86\u4e8c\u5341\u4f59\u62db\uff0c\u4f55\u94c1\u624b\u5a07\u559d\uff1a\u201c\u4f60\u4e00\u5473\u95ea\u907f\uff0c\u7b97\u751a\u4e48\u597d\u6c49\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u6fc0\u6211\u593a\u4f60\u97ad\u5b50\uff1f\u53c8\u6709\u4f55\u96be\u3002\u201d\u8eab\u5b50\u4e00\u5f2f\uff0c\u53cc\u624b\u5df2\u5728\u5c4b\u9876\u5206\u522b\u6361\u8d77\u4e00\u7247\u74e6\u723f\uff0c\u51dd\u89c6\u97ad\u5f71\uff0c\u770b\u5f97\u4eb2\u5207\uff0c\u53eb\u9053\uff1a\u201c\u64a4\u97ad\uff01\u201d\u4e24\u5757\u74e6\u7247\u4e00\u4e0a\u4e00\u4e0b\uff0c\u5df2\u5c06\u874e\u5c3e\u97ad\u5939\u5728\u4e2d\u95f4\uff0c\u987a\u624b\u5f80\u91cc\u4e00\u593a\uff0c\u53f3\u8db3\u6643\u52a8\uff0c\u77ac\u606f\u95f4\u8fde\u8e22\u4e09\u811a\u3002\u4f55\u94c1\u624b\u521a\u60f3\u8fd0\u52b2\u593a\u97ad\uff0c\u5bf9\u65b9\u8db3\u5c16\u5df2\u5c06\u53ca\u8eab\uff0c\u53ea\u5f97\u64a4\u97ad\u5012\u9000\uff0c\u4e0d\u60f3\u8e0f\u4e86\u4e00\u4e2a\u7a7a\uff0c\u8dcc\u4e0b\u5c4b\u53bb\u3002\u8881\u627f\u5fd7\u62a2\u4f4f\u97ad\u67c4\uff0c\u7b11\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u7684\u5f1f\u5b50\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5ffd\u542c\u4f55\u94c1\u624b\u67d4\u5a9a\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u5f88\u597d\uff01\u201d\u5979\u8eab\u6cd5\u597d\u5feb\uff0c\u521a\u4e00\u7740\u5730\uff0c\u7acb\u5373\u53c8\u7a9c\u4e86\u4e0a\u6765\uff0c\u9976\u662f\u8881\u627f\u5fd7\u8eab\u6709\u7edd\u9876\u8f7b\u529f\uff0c\u4e5f\u4e0d\u7981\u4f69\u670d\u3002 \u3000\u3000\u4f55\u94c1\u624b\u53f3\u624b\u53c9\u5728\u8170\u95f4\uff0c\u8eab\u5b50\u5fae\u6643\uff0c\u8170\u80a2\u6b3e\u6446\uff0c\u4f3c\u4e4e\u8f6f\u7ef5\u7ef5\u5730\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u7b11\u9053\uff1a\u201c\u8fd8\u8981\u9886\u6559\u8881\u76f8\u516c\u7684\u6697\u5668\u529f\u592b\uff0c\u6211\u4eec\u4e94\u6bd2\u6559\u6709\u4e00\u79cd\u6bd2\u87fe\u7802\u2026\u2026\u201d\u8881\u627f\u5fd7\u542c\u5979\u5a07\u58f0\u8f6f\u8bed\u7684\u8bf4\u7740\u8bdd\uff0c\u4e5f\u4e0d\u89c1\u5979\u8eab\u8f6c\u624b\u626c\uff0c\u7a81\u7136\u95f4\u773c\u524d\u91d1\u5149\u95ea\u52a8\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u77e5\u9053\u4e0d\u5999\uff0c\u767e\u5fd9\u4e2d\u4e00\u98de\u51b2\u5929\uff0c\u8dc3\u8d77\u5bfb\u4e08\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u7ec6\u5fae\u7684\u94ee\u94ee\u4e4b\u58f0\uff0c\u6570\u5341\u679a\u6697\u5668\u90fd\u6253\u5728\u5c4b\u74e6\u4e4b\u4e0a\u3002 \u3000\u3000\u539f\u6765\u8fd9\u6bd2\u87fe\u7802\u662f\u65e0\u6570\u6781\u7ec6\u7684\u94a2\u9488\uff0c\u673a\u62ec\u88c5\u5728\u80f8\u524d\uff0c\u53d1\u5c04\u65f6\u4e0d\u5fc5\u5148\u53d6\u51c6\u5934\uff0c\u53ea\u987b\u8eab\u5b50\u5bf9\u6b63\u654c\u4eba\uff0c\u4f38\u624b\u5728\u8170\u65c1\u4e00\u6309\uff0c\u4e00\u9635\u94a2\u9488\u5c31\u7531\u5f3a\u529b\u5f39\u7c27\u6fc0\u5c04\u800c\u51fa\u3002\u771f\u662f\u795e\u4e0d\u77e5\uff0c\u9b3c\u4e0d\u89c9\uff0c\u4f55\u51b5\u94a2\u9488\u65e2\u7ec6\uff0c\u4e3a\u6570\u53c8\u591a\uff0c\u4e00\u679a\u6cbe\u8eab\uff0c\u4fbf\u4e2d\u5267\u6bd2\u3002\u6b66\u6797\u4e2d\u4efb\u4f55\u6697\u5668\uff0c\u4e0d\u8bba\u662f\u91d1\u9556\u3001\u8896\u7bad\u3001\u5f39\u4e38\u3001\u94c1\u83b2\u5b50\uff0c\u53d1\u5c04\u65f6\u603b\u5f97\u52a8\u81c2\u626c\u624b\uff0c\u5bf9\u65b9\u5982\u662f\u9ad8\u624b\uff0c\u4e00\u89c1\u65e9\u6709\u9632\u5907\u3002\u4f46\u8fd9\u6bd2\u87fe\u7802\u4e4b\u6765\uff0c\u4e8b\u5148\u7edd\u65e0\u5f81\u5146\uff0c\u5b9e\u662f\u5929\u4e0b\u7b2c\u4e00\u9634\u6bd2\u6697\u5668\uff0c\u6559\u5916\u4eba\u77e5\u8005\u6781\u5c11\uff0c\u7b49\u5230\u89c1\u7740\uff0c\u5341\u4e4b\u516b\u4e5d\u975e\u6b7b\u5373\u4f24\uff0c\u800c\u4f24\u8005\u4e0d\u4e45\u4e5f\u5fc5\u9001\u547d\u3002\u4ed6\u4eec\u672c\u6559\u4e4b\u4eba\u79f0\u4e4b\u4e3a\u201c\u542b\u6c99\u5c04\u5f71\u201d\u529f\u592b\uff0c\u7aef\u7684\u6b66\u6797\u72ec\u6b65\uff0c\u4e16\u4e0a\u65e0\u53cc\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8eab\u5b50\u672a\u843d\uff0c\u4e09\u679a\u94dc\u94b1\u5df2\u5411\u5979\u8981\u7a74\u6253\u53bb\uff0c\u6012\u559d\uff1a\u201c\u6211\u8ddf\u4f60\u65e0\u6028\u65e0\u4ec7\uff0c\u4e3a\u751a\u4e48\u4e0b\u6b64\u6bd2\u624b\uff1f\u201d\u4f55\u94c1\u624b\u4fa7\u8eab\u907f\u5f00\u4e24\u679a\u94dc\u94b1\uff0c\u53f3\u624b\u7ffb\u8f6c\uff0c\u63a5\u4f4f\u4e86\u7b2c\u4e09\u679a\uff0c\u8f7b\u53eb\u4e00\u58f0\uff1a\u201c\u554a\u54df\uff0c\u597d\u5927\u7684\u52b2\u513f\uff0c\u4eba\u5bb6\u624b\u4e5f\u7ed9\u4f60\u78b0\u75db\u5566\u3002\u201d\u770b\u51c6\u8881\u627f\u5fd7\u843d\u4e0b\u7684\u65b9\u4f4d\uff0c\u8fd8\u63b7\u8fc7\u6765\u3002 \u3000\u3000\u542c\u58f0\u8fa8\u5f62\uff0c\u8fd9\u679a\u94dc\u94b1\u63b7\u6765\u7684\u529b\u9053\u4e5f\u4e0d\u5f31\uff0c\u8881\u627f\u5fd7\u521a\u60f3\u4f38\u624b\u53bb\u63a5\uff0c\u7a81\u7136\u5fc3\u91cc\u4e00\u52a8\uff1a\u201c\u8fd9\u4eba\u624b\u4e0a\u6709\u6bd2\uff0c\u522b\u4e0a\u5979\u5f53\u3002\u201d\u957f\u8896\u4e00\u62c2\uff0c\u53c8\u628a\u94dc\u94b1\u62c2\u4e86\u56de\u53bb\u3002\u8fd9\u4e00\u4e0b\u52b2\u529b\u5c31\u6ca1\u624b\u63b7\u7684\u5927\uff0c\u4f55\u94c1\u624b\u4f38\u51fa\u4e24\u6307\uff0c\u8f7b\u8f7b\u62c8\u4f4f\uff0c\u653e\u5165\u8863\u56ca\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\uff01\u53ef\u662f\u53ea\u7ed9\u6211\u4e00\u6587\u94b1\uff0c\u4e0d\u592a\u5c0f\u6c14\u4e86\u4e9b\u5417\uff1f\u201d\u624b\u638c\u4f38\u51fa\u6765\u65f6\u8fce\u98ce\u4e00\u6296\uff0c\u5341\u591a\u6761\u975e\u91d1\u975e\u4e1d\u7684\u7ef3\u7d22\u5411\u4ed6\u5934\u4e0a\u7f69\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u607c\u5979\u9002\u624d\u5077\u653e\u6bd2\u87fe\u7802\u624b\u6bb5\u9634\u6bd2\u4e4b\u6781\uff0c\u5f53\u4e0b\u518d\u4e0d\u5ba2\u6c14\uff0c\u626c\u8d77\u874e\u5c3e\u97ad\uff0c\u5f80\u5979\u7ef3\u4e0a\u7f20\u53bb\u3002\u4f55\u94c1\u624b\u6597\u7136\u6536\u7d22\uff0c\u7b11\u9053\uff1a\u201c\u874e\u5c3e\u97ad\u662f\u6211\u7684\u5440\u3002\u4f60\u4f7f\u6211\u5175\u5668\uff0c\u5bb3\u4e0d\u5bb3\u81ca\u5440\uff1f\u201d\u8bf4\u7684\u662f\u4e00\u53e3\u4e91\u5357\u571f\u97f3\uff0c\u53c8\u7cef\u53c8\u8106\uff0c\u624b\u4e0b\u5374\u6beb\u4e0d\u505c\u7559\u3002 \u3000\u3000\u8881\u627f\u5fd7\u628a\u874e\u5c3e\u97ad\u8fdc\u8fdc\u5411\u540e\u63b7\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u6211\u518d\u593a\u4e0b\u4f60\u8fd9\u51e0\u6839\u7ef3\u7d22\u513f\uff0c\u4f60\u4eec\u4e94\u6bd2\u6559\u4ece\u6b64\u4e0d\u80fd\u518d\u6765\u7ea0\u7f20\uff0c\u884c\u4e0d\u884c\uff1f\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u8fd9\u4e0d\u53eb\u7ef3\u7d22\u513f\uff0c\u8fd9\u662f\u8f6f\u7ea2\u86db\u7d22\u3002\u4f60\u7231\u593a\uff0c\u5012\u8bd5\u8bd5\u770b\u3002\u201d\u8bf4\u7740\u86db\u7d22\u6a2a\u626b\uff0c\u62e6\u8170\u5377\u6765\u3002\u8fd9\u86db\u7d22\u7ec6\u957f\u591a\u4e1d\uff0c\u4e00\u62db\u65e2\u51fa\uff0c\u56db\u9762\u516b\u65b9\u540c\u65f6\u6253\u5230\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4fa7\u8eab\u95ea\u907f\uff0c\u60f3\u62a2\u653b\u5bf9\u624b\u7a7a\u9699\uff0c\u54ea\u77e5\u5979\u5341\u591a\u6839\u86db\u7d22\u6709\u7684\u653b\u654c\uff0c\u6709\u7684\u9632\u8eab\uff0c\u653b\u51fa\u53bb\u7684\u521a\u6536\u56de\u5b88\u5fa1\uff0c\u539f\u6765\u7f29\u56de\u7684\u53c8\u53cd\u51fb\u800c\u51fa\uff0c\u653b\u5b88\u8fde\u73af\uff0c\u6beb\u65e0\u7834\u7efd\u3002 \u3000\u3000\u62c6\u4e86\u5341\u4f59\u62db\u540e\uff0c\u8881\u627f\u5fd7\u5df2\u770b\u51fa\u86db\u7d22\u7684\u5965\u5999\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u86db\u7d22\u529f\u592b\u662f\u4ece\u8718\u86db\u7f51\u4e2d\u53d8\u5316\u51fa\u6765\u7684\u3002\u201d\u4e58\u5979\u4e00\u62db\u4f7f\u8001\uff0c\u8fdb\u653b\u7684\u7d22\u5b50\u5c1a\u672a\u6536\u56de\u3001\u800c\u5b88\u5fa1\u7684\u7d22\u5b50\u5df2\u84c4\u52bf\u53d1\u51fa\u4e4b\u9645\uff0c\u8eab\u5f62\u4e00\u659c\uff0c\u9661\u7136\u6b3a\u8fd1\u5979\u80cc\u5fc3\uff0c\u4f38\u624b\u5411\u5979\u80c1\u4e0b\u70b9\u53bb\u3002\u8fd9\u62db\u5feb\u6781\u9669\u6781\uff0c\u4f55\u94c1\u624b\u4e07\u96be\u907f\u5f00\uff0c\u5ffd\u7136\u95f4\u8eab\u5b50\u4e00\u4fa7\u3002\u8881\u627f\u5fd7\u89c1\u8fd9\u4e00\u4e0b\u5982\u70b9\u5b9e\u4e86\uff0c\u624b\u6307\u975e\u78b0\u5230\u5979\u80f8\u90e8\u4e0d\u53ef\uff0c\u8138\u4e0a\u53d1\u70ed\uff0c\u51dd\u6307\u4e0d\u53d1\u3002 \u3000\u3000\u4f55\u94c1\u624b\u4e58\u52bf\u5de6\u624b\u4e00\u94a9\u3002\u8881\u627f\u5fd7\u75be\u5fd9\u7f29\u624b\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u8896\u53e3\u5df2\u88ab\u94a9\u5b50\u5212\u4e86\u4e00\u6761\u7f1d\u3002\u4f55\u94c1\u624b\u9053\uff1a\u201c\u554a\u54df\uff0c\u7cdf\u7cd5\uff0c\u628a\u8881\u76f8\u516c\u8896\u5b50\u5272\u7834\u5566\u3002\u60a8\u628a\u957f\u886b\u9664\u4e0b\u6765\u5427\uff0c\u6211\u62ff\u56de\u53bb\u7ed9\u4f60\u8865\u597d\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u72e1\u8ba1\u767e\u51fa\uff0c\u5fc3\u4e2d\u6108\u6012\uff0c\u4e58\u52bf\u4e00\u62c9\uff0c\u626f\u4e0b\u4e86\u53f3\u81c2\u7834\u8896\uff0c\u4f7f\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u4e0d\u6570\u62db\uff0c\u8896\u5b50\u5df2\u4e0e\u86db\u7d22\u7f20\u4f4f\uff0c\u7528\u529b\u4e00\u6325\uff0c\u7834\u8896\u4e0e\u86db\u7d22\u53cc\u53cc\u8131\u624b\uff0c\u90fd\u6389\u5230\u5730\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u4f55\u94c1\u624b\u683c\u683c\u7b11\u9053\uff1a\u201c\u4e0d\u600e\u4e48\u6837\u3002\u4f60\u7684\u5175\u5203\u4e0d\u4e5f\u8131\u624b\u4e86\u4e48\uff1f\u8fd8\u4e0d\u662f\u6253\u4e86\u4e2a\u5e73\u624b\uff1f\u201d\u53cd\u624b\u5728\u80cc\u4e0a\u4e00\u62bd\uff0c\u53f3\u624b\u4e2d\u591a\u4e86\u4e00\u67c4\u91d1\u5149\u95ea\u95ea\u7684\u94a9\u5b50\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u5468\u8eab\u6cd5\u5b9d\uff0c\u6b66\u5668\u5c42\u51fa\u4e0d\u7a77\uff0c\u4e5f\u4e0d\u7981\u5927\u4e3a\u5934\u75db\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u593a\u4e0b\u4f60\u86db\u7d22\u4e4b\u540e\uff0c\u4f60\u4eec\u53ef\u4e0d\u80fd\u518d\u6765\u7ea0\u7f20\u3002\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u7684\uff0c\u6211\u51e0\u65f6\u7b54\u5141\u8fc7\u554a\uff1f\u201d\u8881\u627f\u5fd7\u4e00\u60f3\uff0c\u679c\u7136\u4e0d\u9519\uff0c\u5979\u786e\u662f\u6ca1\u7b54\u5141\u8fc7\uff0c\u4f46\u8fd9\u822c\u4e00\u4ef6\u4e00\u4ef6\u7684\u6bd4\u4e0b\u53bb\uff0c\u5230\u4f55\u65f6\u65b9\u4e86\uff1f\u5f53\u4e0b\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u77a7\u4f60\u8fd8\u6709\u591a\u5c11\u5175\u5668\uff1f\u201d\u5fc3\u60f3\u628a\u5979\u6bcf\u4e00\u4ef6\u5175\u5668\u90fd\u593a\u4e0b\u6765\uff0c\u5979\u603b\u8981\u77e5\u96be\u800c\u9000\u4e86\u3002 \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u8fd9\u53eb\u505a\u91d1\u8708\u94a9\u3002\u201d\u5de6\u624b\u4e00\u4f38\uff0c\u9732\u51fa\u624b\u4e0a\u94c1\u94a9\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u94c1\u8708\u94a9\uff0c\u4e3a\u4e86\u7ec3\u8fd9\u52b3\u751a\u5b50\uff0c\u7239\u7239\u5272\u65ad\u4e86\u6211\u4e00\u53ea\u624b\u3002 \u3000\u3000\u4ed6\u8bf4\u5175\u5668\u62ff\u5728\u624b\u91cc\uff0c\u603b\u4e0d\u5982\u5e72\u8106\u88c5\u5728\u624b\u4e0a\u7075\u4fbf\u3002\u6211\u7ec3\u4e86\u5341\u4e09\u5e74\u5566\uff0c\u8fd8\u4e0d\u5927\u6210\u3002\u8881\u76f8\u516c\uff0c\u8fd9\u94a9\u4e0a\u53ef\u6709\u6bd2\u836f\uff0c\u4f60\u522b\u7528\u624b\u6765\u593a\u5440\uff01\u201d \u3000\u3000\u53ea\u89c1\u5979\u8fde\u7b11\u5e26\u8bf4\uff0c\u6162\u6162\u8d70\u8fd1\uff0c\u8881\u627f\u5fd7\u5916\u8868\u867d\u7136\u6de1\u7136\u81ea\u82e5\uff0c\u5185\u5fc3\u5b9e\u6df1\u6212\u60e7\uff0c\u53ea\u6015\u5979\u53c8\u4f7f\u751a\u4e48\u5978\u8c0b\uff0c\u6b63\u81ea\u4e25\u52a0\u63d0\u9632\uff0c\u5ffd\u542c\u8fdc\u5904\u9690\u9690\u6709\u547c\u54e8\u4e4b\u58f0\uff0c\u731b\u7136\u95f4\u60f3\u8d77\u4e00\u4e8b\uff0c\u6697\u53eb\uff1a\u201c\u4e0d\u597d\uff01\u83ab\u975e\u6b64\u4eba\u7eca\u4f4f\u4e86\u6211\uff0c\u5374\u547d\u5979\u515a\u7fbd\u53bb\u52a0\u5bb3\u9752\u9752\u4ed6\u4eec\uff1f\u201d\u4e5f\u4e0d\u7b49\u5979\u8bdd\u8bf4\u5b8c\uff0c\u56de\u8eab\u5c31\u8d70\u3002 \u3000\u3000\u4f55\u94c1\u624b\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u65f6\u518d\u53bb\uff0c\u5df2\u7ecf\u8fdf\u4e86\uff01\u201d\u91d1\u94a9\u4e00\u70b9\uff0c\u94c1\u94a9\u75be\u4f38\uff0c\u731b\u5411\u4ed6\u540e\u5fc3\u9012\u5230\u3002\u8881\u627f\u5fd7\u4fa7\u8fc7\u8eab\u5b50\uff0c\u6a2a\u626b\u4e00\u817f\u3002\u4f55\u94c1\u624b\u7eb5\u8eab\u907f\u8fc7\uff0c\u53cc\u94a9\u53cd\u51fb\u3002\u8fd9\u65f6\u66d9\u5149\u521d\u73b0\uff0c\u53ea\u89c1\u4e00\u9053\u9ed1\u6c14\uff0c\u4e00\u7247\u9ec4\u5149\uff0c\u5728\u4ed6\u8eab\u8fb9\u7eb5\u6a2a\u76d8\u65cb\u3002\u8fd9\u5973\u5b50\u5175\u5203\u4e0a\u529f\u592b\u4e4b\u51cc\u5389\uff0c\u4ec5\u6bd4\u5728\u76db\u4eac\u6240\u9047\u7684\u7389\u771f\u5b50\u7a0d\u900a\u800c\u5df2\u3002\u4ed6\u6302\u5ff5\u9752\u9752\u7b49\u4eba\uff0c\u4e0d\u6b32\u604b\u6218\uff0c\u6570\u6b21\u6b3a\u8fd1\u8981\u593a\u5979\u91d1\u94a9\uff0c\u603b\u662f\u88ab\u5979\u56de\u94a9\u53cd\u51fb\uff0c\u6216\u4ee5\u94c1\u94a9\u62a4\u4f4f\u3002\u8fd9\u94c1\u94a9\u88c5\u5728\u624b\u4e0a\uff0c\u8fd0\u7528\u4e4b\u9645\u7684\u662f\u7075\u52a8\u975e\u51e1\uff0c\u5b9b\u5982\u6d3b\u624b\u4e00\u822c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u62c6\u5230\u4e09\u5341\u4f59\u62db\uff0c\u5140\u662f\u6253\u5979\u4e0d\u9000\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u63a2\u624b\u8170\u95f4\uff0c\u91d1\u5149\u4e00\u95ea\uff0c\u62d4\u51fa\u4e86\u91d1\u86c7\u5b9d\u5251\u3002\u4f55\u94c1\u624b\u4e00\u89c1\uff0c\u7b11\u5bb9\u7acb\u655b\uff0c\u559d\u9053\uff1a\u201c\u597d\uff01\u8fd9\u91d1\u86c7\u5251\u7adf\u843d\u5728\u4f60\u624b\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u662f\u4fbf\u600e\u6837\uff1f\u201d \u3000\u3000\u5237\u5237\u6570\u5251\u3002\u4f55\u94c1\u624b\u6b66\u529f\u867d\u9ad8\uff0c\u54ea\u91cc\u62b5\u6321\u5f97\u4f4f\uff1f\u5f53\u7684\u4e00\u58f0\uff0c\u91d1\u94a9\u5df2\u88ab\u91d1\u86c7\u5251\u524a\u53bb\u534a\u622a\u3002\u8881\u627f\u5fd7\u559d\u9053\uff1a\u201c\u518d\u6765\u7ea0\u7f20\uff0c\u628a\u4f60\u7684\u94c1\u624b\u4e5f\u524a\u65ad\u4e86\u3002\u201d\u5979\u4e00\u542c\u4e4b\u4e0b\uff0c\u8138\u4e0a\u5fae\u73b0\u60e7\u8272\uff0c\u679c\u7136\u4e0d\u6562\u903c\u8fd1\u8eab\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6536\u5251\u5165\u9798\uff0c\u75be\u5954\u56de\u5bb6\uff0c\u521a\u5230\u80e1\u540c\u53e3\uff0c\u4fbf\u89c1\u6d2a\u80dc\u6d77\u8eba\u5728\u5730\u4e0b\uff0c\u9888\u4e2d\u6d41\u8840\uff0c\u5fd9\u4e0a\u524d\u6276\u8d77\uff0c\u5e78\u559c\u5c1a\u6709\u6c14\u606f\u3002\u6d2a\u80dc\u6d77\u54bd\u5589\u53d7\u4f24\uff0c\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u4f38\u624b\u5411\u7740\u5b85\u5b50\u8fde\u6307\u3002\u8881\u627f\u5fd7\u62b1\u4ed6\u5165\u5185\uff0c\u53ea\u89c1\u5b85\u5b50\u4e2d\u5230\u5904\u684c\u7ffb\u6905\u6298\uff0c\u95e8\u7834\u7a97\u70c2\uff0c\u663e\u662f\u7ecf\u8fc7\u4e00\u756a\u5267\u6218\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d8a\u770b\u8d8a\u662f\u5fc3\u60ca\uff0c\u6495\u4e0b\u8863\u8896\u66ff\u6d2a\u80dc\u6d77\u624e\u4f4f\u4e86\u54bd\u5589\u4f24\u53e3\uff0c\u76f4\u5954\u5185\u5802\uff0c\u91cc\u9762\u4e5f\u662f\u5904\u5916\u7834\u635f\uff0c\u80e1\u6842\u5357\u4e0e\u7a0b\u9752\u7af9\u8eba\u5728\u5730\u4e0b\u547b\u541f\u3002\u8881\u627f\u5fd7\u5fd9\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d\u80e1\u6842\u5357\u9053\uff1a\u201c\u9752\u59d1\u5a18\uff0c\u9752\u59d1\u5a18\u2026\u2026\u7ed9\u2026\u2026\u4e94\u6bd2\u6559\u63b3\u53bb\u5566\u3002\u201d\u8881\u627f\u5fd7\u5927\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u6c99\u5929\u5e7f\u4ed6\u4eec\u5462\uff1f\u201d\u80e1\u6842\u5357\u4f38\u624b\u6307\u5411\u5c4b\u9876\u3002\u8881\u627f\u5fd7\u4e0d\u53ca\u591a\u95ee\uff0c\u6025\u8dc3\u4e0a\u5c4b\uff0c\u53ea\u89c1\u6c99\u5929\u5e7f\u548c\u54d1\u5df4\u8eba\u5728\u74e6\u9762\uff0c\u6c99\u5929\u5e7f\u6ee1\u8138\u4e4c\u4e91\uff0c\u4e2d\u6bd2\u751a\u6df1\uff0c\u54d1\u5df4\u4e5f\u53d7\u521b\u4f24\u3002\u867d\u7136\u5e78\u559c\u65e0\u4eba\u6b7b\u4ea1\uff0c\u4f46\u6ee1\u5c4b\u4f19\u4f34\uff0c\u4e2a\u4e2a\u91cd\u4f24\uff0c\u771f\u662f\u4e00\u8d25\u6d82\u5730\uff0c\u9752\u9752\u66f4\u4e0d\u77e5\u53bb\u5411\u3002\u8881\u627f\u5fd7\u54ac\u7259\u5207\u9f7f\uff0c\u6124\u6012\u81ea\u8d23\uff1a\u201c\u6211\u600e\u5730\u5982\u6b64\u80e1\u6d82\uff0c\u7adf\u8ba9\u8fd9\u5973\u5b50\u7f20\u4f4f\u4e86\u4e5f\u6ca1\u53d1\u89c9\u3002\u201d \u3000\u3000\u5b85\u4e2d\u7ae5\u4ec6\u5728\u6076\u6597\u65f6\u5c3d\u7686\u9003\u6563\uff0c\u8fd9\u65f6\u5929\u8272\u5927\u660e\uff0c\u654c\u4eba\u5df2\u53bb\uff0c\u624d\u6162\u6162\u56de\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u628a\u54d1\u5df4\u548c\u6c99\u5929\u5e7f\u62b1\u4e0b\u5730\u6765\uff0c\u5199\u4e86\u4e00\u5f20\u5b57\u6761\uff0c\u547d\u4ec6\u4eba\u6025\u901f\u9001\u53bb\u91d1\u9f99\u5e2e\u5bd3\u6240\uff0c\u8bf7\u7126\u5b9b\u513f\u53d6\u56de\u6731\u775b\u51b0\u87fe\uff0c\u524d\u6765\u6551\u4eba\u3002 \u3000\u3000\u4ed6\u66ff\u6c99\u5929\u5e7f\u3001\u80e1\u6842\u5357\u7b49\u5305\u624e\u4f24\u53e3\uff0c\u4e00\u9762\u8be2\u95ee\u654c\u4eba\u6765\u88ad\u60c5\u5f62\u3002 \u3000\u3000\u94c1\u7f57\u6c49\u4e0a\u6b21\u53d7\u4f24\u5367\u5e8a\u672a\u8d77\uff0c\u5e78\u5f97\u672a\u906d\u6bd2\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u66f4\u65f6\u5206\uff0c\u80e1\u6842\u5357\u9996\u5148\u53d1\u89c9\u4e86\u654c\u8e2a\uff0c\u628a\u54d1\u5df4\u8001\u5144\u626f\u4e0a\u5c4b\u53bb\u3002\u4e24\u4eba\u4e00\u4e0a\u5c4b\uff0c\u7acb\u88ab\u5341\u591a\u540d\u654c\u4eba\u56f4\u4f4f\u4e86\u3002\u6211\u5728\u7a97\u53e3\u4e2d\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5c31\u662f\u5168\u8eab\u65e0\u529b\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u53ea\u6709\u5e72\u7740\u6025\u7684\u4efd\u513f\u3002\u773c\u89c1\u54d1\u5df4\u8001\u5144\u3001\u6c99\u8001\u5144\u548c\u7a0b\u8001\u592b\u5b50\u90fd\u4f24\u4e86\u597d\u51e0\u540d\u654c\u4eba\uff0c\u4f46\u5bf9\u65b9\u5b9e\u5728\u4eba\u591a\u3002 \u3000\u3000\u5927\u5bb6\u8fb9\u6253\u8fb9\u9000\uff0c\u5728\u6bcf\u4e00\u95f4\u5c4b\u91cc\u90fd\u62da\u4e86\u597d\u4e00\u9635\uff0c\u6700\u540e\u4e2a\u4e2a\u53d7\u4f24\uff0c\u9752\u59d1\u5a18\u4e5f\u7ed9\u4ed6\u4eec\u63b3\u4e86\u53bb\u3002\u8881\u76f8\u516c\u2026\u2026\u6211\u4eec\u5b9e\u5728\u5bf9\u4f60\u4e0d\u8d77\u2026\u2026\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u654c\u4eba\u597d\u4e0d\u72e0\u6bd2\uff0c\u600e\u602a\u5f97\u4f60\u4eec\uff1f\u773c\u4e0b\u6551\u4eba\u8981\u7d27\u3002\u201d \u3000\u3000\u4ed6\u5230\u9a6c\u53a9\u7275\u4e86\u5339\u9a6c\uff0c\u5411\u57ce\u5916\u9a70\u53bb\uff0c\u5c06\u5230\u602a\u5c4b\u65f6\u4e0b\u4e86\u9a6c\uff0c\u5c06\u9a6c\u7f1a\u5728\u6811\u4e0a\uff0c\u8d70\u5230\u5c4b\u524d\uff0c\u98de\u8eab\u8d8a\u5899\u76f4\u5165\uff0c\u5927\u53eb\uff1a\u201c\u4f55\u6559\u4e3b\uff0c\u8bf7\u51fa\u6765\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u4e00\u9635\u56de\u97f3\u8fc7\u53bb\uff0c\u9ec4\u5899\u4e0a\u94c1\u95e8\u5f00\u5904\uff0c\u4e00\u9635\u72fa\u72fa\u72c2\u5420\uff0c\u6251\u51fa\u5341\u591a\u5934\u51f6\u731b\u5de8\u5927\uff0c\u540e\u9762\u8ddf\u7740\u6570\u5341\u4eba\u3002\u4ed6\u60f3\uff1a\u201c\u8fd9\u6b21\u53ef\u4e0d\u80fd\u518d\u5bf9\u4ed6\u4eec\u5ba2\u6c14\u4e86\uff01\u201d\u5de6\u624b\u8fde\u6325\uff0c\u5341\u591a\u679a\u91d1\u86c7\u9525\u6fc0\u5c04\u800c\u51fa\uff0c\u91d1\u5149\u95ea\u95ea\uff0c\u6bcf\u53ea\u5de8\u7352\u8111\u95e8\u4e2d\u4e86\u4e00\u679a\uff0c\u53ea\u53ea\u5012\u6bd9\u5728\u5730\u3002\u4ed6\u7ed5\u7740\u4f17\u72ac\u8f6c\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u53cc\u624b\u5c06\u91d1\u86c7\u9525\u4e00\u4e00\u6536\u5165\u56ca\u4e2d\u3002 \u3000\u3000\u4e94\u6bd2\u6559\u4eba\u4f17\u672c\u5f85\u4e58\u4ed6\u4e0e\u5de8\u7352\u7f20\u6597\uff0c\u4e58\u9699\u55b7\u5c04\u6bd2\u6c41\uff0c\u54ea\u77e5\u4ed6\u6740\u6bd9\u4f17\u72ac\u7adf\u5982\u6b64\u795e\u901f\uff0c\u4e0d\u7531\u5f97\u90fd\u60ca\u5446\u4e86\uff0c\u5f85\u4ed6\u6536\u56de\u6697\u5668\uff0c\u5148\u5934\u4e00\u4eba\u53d1\u4e00\u58f0\u558a\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u4f59\u4eba\u4e00\u62e5\u8fdb\u5185\uff0c\u5f85\u8981\u5173\u95e8\uff0c\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff1f\u8881\u627f\u5fd7\u5df2\u4ece\u5404\u4eba\u5934\u9876\u4e00\u8dc3\u800c\u8fc7\uff0c\u62a2\u5728\u5934\u91cc\u3002 \u3000\u3000\u4ed6\u6df1\u5165\u654c\u4eba\u8179\u5730\u4e4b\u540e\uff0c\u53cd\u800c\u795e\u5b9a\u6c14\u95f2\uff0c\u53eb\u9053\uff1a\u201c\u4f55\u6559\u4e3b\u518d\u4e0d\u51fa\u6765\uff0c\u83ab\u602a\u6211\u65e0\u793c\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5618\u6e9c\u6e9c\u7684\u4e00\u9635\u53e3\u54e8\uff0c\u4e94\u6bd2\u6559\u4f17\u4eba\u6392\u6210\u4e24\u5217\uff0c\u4e2d\u95f4\u5c4b\u91cc\u51fa\u6765\u5341\u591a\u4eba\u3002\u5f53\u5148\u4e00\u4eba\u662f\u4f55\u7ea2\u836f\uff0c\u540e\u9762\u8ddf\u7740\u5de6\u53f3\u62a4\u6cd5\u6f58\u79c0\u6708\u8d5e\u8fbe\u3001\u5c91\u5176\u65af\uff0c\u4ee5\u53ca\u9526\u8863\u6bd2\u4e10\u9f50\u4e91\u7b49\u4e00\u6279\u6559\u4e2d\u9ad8\u624b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5728\u4e0b\u8ddf\u5404\u4f4d\u7d20\u4e0d\u76f8\u8bc6\uff0c\u65e2\u65e0\u5bbf\u6028\uff0c\u4e5f\u65e0\u65b0\u4ec7\uff0c\u5404\u4f4d\u5374\u6765\u5230\u820d\u4e0b\uff0c\u5c06\u6211\u670b\u53cb\u4e2a\u4e2a\u6253\u5f97\u91cd\u4f24\uff0c\u8fd8\u5c06\u6211\u5144\u5f1f\u63b3\u6765\uff0c\u90a3\u662f\u751a\u4e48\u7f18\u7531\uff0c\u8981\u5411\u4f55\u6559\u4e3b\u8bf7\u6559\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u4f60\u5bb6\u91cc\u65c1\u4eba\u8ddf\u6211\u4eec\u6ca1\u6709\u51a4\u4ec7\uff0c\u90a3\u4e5f\u4e0d\u9519\uff0c\u56e0\u6b64\u624b\u4e0b\u7559\u60c5\uff0c\u6ca1\u5f53\u573a\u8981\u4e86\u4ed6\u4eec\u6027\u547d\u3002\u4f60\u65e2\u6709\u6731\u775b\u51b0\u87fe\uff0c\u5c0f\u5c0f\u4f24\u52bf\u4e5f\u5f88\u6613\u6cbb\u597d\u3002\u81f3\u4e8e\u90a3\u59d3\u590f\u7684\u5c0f\u5b50\u5462\uff0c\u54fc\uff0c\u6211\u4eec\u8981\u6162\u6162\u7684\u75db\u52a0\u6298\u78e8\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5979\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u751a\u4e48\u4e8b\u60c5\u5bf9\u4f60\u4eec\u4e0d\u4f4f\u4e86\uff1f\u201d\u4f55\u7ea2\u836f\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u6559\u4ed6\u662f\u91d1\u86c7\u90ce\u541b\u7684\u513f\u5b50\uff1f\u54fc\uff0c\u8fd9\u4e5f\u7f62\u4e86\uff0c\u8c01\u6559\u4ed6\u662f\u90a3\u4e2a\u8d31\u8d27\u751f\u7684\uff1f\u201d\u8881\u627f\u5fd7\u4e00\u6014\uff0c\u5fc3\u60f3\u5979\u8ddf\u9752\u9752\u7684\u6bcd\u4eb2\u53c8\u6709\u751a\u4e48\u4ec7\u5acc\u4e86\uff1f\u4f55\u7ea2\u836f\u89c1\u4ed6\u6c89\u541f\u4e0d\u8bed\uff0c\u9634\u68ee\u68ee\u7684\u9053\uff1a\u201c\u4f60\u6765\u80e1\u95f9\u4e9b\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u4eec\u5982\u8ddf\u91d1\u86c7\u90ce\u541b\u6709\u6881\u5b50\uff0c\u5e72\u751a\u4e48\u4e0d\u81ea\u53bb\u627e\u4ed6\u62a5\u4ec7\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u8001\u5b50\u8981\u6740\uff0c\u513f\u5b50\u4e5f\u8981\u6740\uff01\u4f60\u65e2\u8ddf\u4ed6\u6709\u74dc\u845b\uff0c\u8fde\u4f60\u4e5f\u8981\u6740\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u613f\u518d\u4e0e\u5979\u5570\u5506\u4e0d\u6e05\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4f55\u6559\u4e3b\uff0c\u4f60\u5230\u5e95\u51fa\u4e0d\u51fa\u6765\uff1f\u653e\u4e0d\u653e\u4eba\uff1f\u201d\u5c4b\u4e2d\u5bc2\u7136\u65e0\u58f0\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u9635\u9635\u56de\u58f0\u4ece\u4e94\u5835\u9ad8\u5899\u4e0a\u649e\u4e86\u56de\u6765\u3002\u8881\u627f\u5fd7\u6302\u5ff5\u9752\u9752\uff0c\u8eab\u5f62\u4e00\u659c\uff0c\u731b\u4ece\u4f55\u7ea2\u836f\u8eab\u65c1\u7a7f\u8fc7\uff0c\u76f4\u5411\u5385\u95e8\u51b2\u53bb\u3002\u4e24\u540d\u6559\u5f92\u6765\u6321\uff0c\u8881\u627f\u5fd7\u53cc\u638c\u8d77\u5904\uff0c\u5c06\u4e24\u4eba\u76f4\u63bc\u51fa\u53bb\u3002\u4ed6\u51b2\u5165\u5385\u5185\uff0c\u89c1\u7a7a\u7a7a\u8361\u8361\u7684\u6ca1\u6709\u4eba\u5f71\uff0c\u8f6c\u8eab\u76f4\u5954\u4e1c\u53a2\u623f\uff0c\u8e22\u5f00\u623f\u95e8\uff0c\u53ea\u89c1\u4e24\u540d\u6559\u4f17\u5367\u5728\u5e8a\u4e0a\uff0c\u5374\u662f\u65e5\u524d\u88ab\u4ed6\u626d\u4f24\u4e86\u5173\u8282\u4e4b\u4eba\uff0c\u89c1\u4ed6\u5165\u6765\uff0c\u5413\u5f97\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e1c\u5954\u897f\u7a9c\uff0c\u56db\u4e0b\u627e\u5bfb\uff0c\u4e94\u6bd2\u6559\u4f17\u4e71\u6210\u4e00\u56e2\uff0c\u5904\u5904\u515c\u622a\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u8881\u627f\u5fd7\u5df2\u628a\u6bcf\u4e00\u95f4\u623f\u5b50\u90fd\u627e\u904d\u4e86\uff0c\u4e0d\u4f46\u6ca1\u6709\u89c1\u5230\u9752\u9752\uff0c\u8fde\u4f55\u94c1\u624b\u4e5f\u4e0d\u5728\u5c4b\u91cc\u3002\u4ed6\u7126\u8e81\u5f02\u5e38\uff0c\u628a\u7f38\u74ee\u7bb1\u7b3c\u4e71\u7ffb\u4e71\u8e22\uff0c\u91cc\u9762\u9972\u517b\u7740\u7684\u86c7\u866b\u6bd2\u7269\u90fd\u722c\u4e86\u51fa\u6765\u3002\u4e94\u6bd2\u6559\u4f17\u5927\u60ca\uff0c\u5fd9\u5206\u4eba\u6355\u6349\u6bd2\u7269\u3002 \u3000\u3000\u6f58\u79c0\u8fbe\u53eb\u9053\uff1a\u201c\u662f\u597d\u6c49\u5230\u5916\u9762\u6765\u51b3\u4e2a\u80dc\u8d1f\u3002\u201d\u8881\u627f\u5fd7\u77e5\u4ed6\u5728\u6559\u4e2d\u9887\u6709\u5730\u4f4d\uff0c\u51b3\u610f\u64d2\u4f4f\u4ed6\u903c\u95ee\u9752\u9752\u7684\u4e0b\u843d\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u6211\u9886\u6559\u9601\u4e0b\u7684\u6bd2\u638c\u529f\u592b\uff01\u201d\u65bd\u5c55\u795e\u884c\u767e\u53d8\u8f7b\u8eab\u529f\u592b\uff0c\u53cc\u8db3\u4e00\u8e8f\uff0c\u5df2\u8dc3\u5230\u4ed6\u9762\u524d\u3002\u6f58\u79c0\u8fbe\u89c1\u4ed6\u8bf4\u5230\u4fbf\u5230\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u547c\u547c\u4e24\u638c\u5288\u5230\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u522b\u4eba\u6015\u4f60\u6bd2\u638c\uff0c\u6211\u504f\u4e0d\u6015\uff01\u201d\u6f58\u79c0\u8fbe\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u8bd5\u8bd5\u3002\u201d\u8881\u627f\u5fd7\u53f3\u638c\u4e00\u8d77\uff0c\u5f80\u4ed6\u638c\u4e0a\u62b5\u53bb\u3002 \u3000\u3000\u6f58\u79c0\u8fbe\u5927\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u7adf\u6765\u548c\u6211\u6bd2\u638c\u76f8\u78b0\uff0c\u8fd9\u53ef\u662f\u81ea\u5bfb\u6b7b\u8def\uff0c\u6028\u6211\u4e0d\u5f97\u3002\u201d\u5f53\u4e0b\u53cc\u638c\u8fd0\u529b\uff0c\u731b\u5411\u524d\u63a8\uff0c\u773c\u89c1\u8981\u548c\u8881\u627f\u5fd7\u624b\u638c\u76f8\u78b0\uff0c\u76f8\u8ddd\u4e0d\u5230\u4e00\u5bf8\uff0c\u7a81\u89c1\u5bf9\u65b9\u624b\u638c\u6025\u7f29\uff0c\u8111\u540e\u98ce\u58f0\u5fae\u52a8\uff0c\u77e5\u9053\u4e0d\u5999\uff0c\u5f85\u8981\u7f29\u8eab\u56de\u638c\uff0c\u53ea\u89c9\u9888\u4e2d\u4e00\u7d27\uff0c\u8eab\u5b50\u5df2\u88ab\u63d0\u8d77\u3002\u4e94\u6bd2\u6559\u4f17\u9f50\u58f0\u5450\u558a\uff0c\u5954\u6765\u76f8\u6551\u3002\u8881\u627f\u5fd7\u6293\u8d77\u6f58\u79c0\u8fbe\u6325\u4e86\u4e2a\u5708\u5b50\u3002\u4f17\u4eba\u6015\u4f24\u4e86\u62a4\u6cd5\uff0c\u4e0d\u6562\u903c\u8fd1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u559d\u9053\uff1a\u201c\u4f60\u4eec\u63b3\u6765\u7684\u4eba\u5728\u54ea\u91cc\uff1f\u5feb\u8bf4\u3002\u201d\u6f58\u79c0\u8fbe\u95ed\u76ee\u4e0d\u7406\u3002\u8881\u627f\u5fd7\u6f5c\u8fd0\u6df7\u5143\u529f\uff0c\u4f38\u624b\u5728\u4ed6\u810a\u9aa8\u65c1\u7a74\u9053\u4e00\u6307\u6233\u53bb\u3002 \u3000\u3000\u6f58\u79c0\u8fbe\u767b\u65f6\u80cc\u5fc3\u5267\u75db\uff0c\u6709\u5982\u4e00\u6839\u94a2\u6761\u5728\u8eab\u4f53\u5185\u7ede\u6765\u6405\u53bb\u3002\u8881\u627f\u5fd7\u677e\u624b\u628a\u4ed6\u6454\u5728\u5730\u4e0b\u3002\u6f58\u79c0\u8fbe\u75db\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u5728\u5730\u4e0b\u6eda\u6765\u6eda\u53bb\uff0c\u5374\u4e0d\u8bf4\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u8bf4\uff0c\u65c1\u4eba\u5462\uff1f\u201d\u7075\u673a\u4e00\u52a8\uff1a\u201c\u6211\u7684\u70b9\u7a74\u9664\u4e86\u672c\u95e8\u4e2d\u4eba\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u6551\u3002\u4e14\u90fd\u7ed9\u4ed6\u4eec\u70b9\u4e0a\u4e86\uff0c\u8c05\u6765\u4f55\u94c1\u624b\u4fbf\u4e0d\u6562\u52a0\u5bb3\u9752\u5f1f\u3002\u201d\u5f53\u4e0b\u8eab\u5f62\u6643\u52a8\uff0c\u5728\u4f17\u4eba\u8eab\u65c1\u7a7f\u6765\u63d2\u53bb\u3002\u6559\u5f92\u4e2d\u6b66\u529f\u9ad8\u5f3a\u4e4b\u4eba\u8fd8\u62b5\u6321\u5f97\u4e86\u4e09\u62db\u4e24\u5f0f\uff0c\u5176\u4f59\u90fd\u662f\u8fd8\u6ca1\u770b\u6e05\u654c\u4eba\u8eab\u6cd5\uff0c\u7a74\u9053\u5df2\u88ab\u95ed\u4f4f\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u9662\u5b50\u4e2d\u8eba\u4e0b\u4e86\u4e8c\u4e09\u5341\u4eba\u3002\u672c\u6765\u7a74\u9053\u88ab\u95ed\uff0c\u5c3d\u7ba1\u70b9\u7a74\u624b\u6cd5\u522b\u5177\u4e00\u529f\uff0c\u65c1\u4eba\u96be\u4ee5\u89e3\u5f00\uff0c\u4f46\u8fc7\u5f97\u51e0\u4e2a\u65f6\u8fb0\uff0c\u6c14\u8840\u6d41\u8f6c\uff0c\u7a74\u9053\u7ec8\u4e8e\u4f1a\u6162\u6162\u81ea\u884c\u901a\u89e3\u3002\u4f46\u8881\u627f\u5fd7\u8fd9\u6b21\u70b9\u7a74\u65f6\u4f7f\u4e0a\u4e86\u6df7\u5143\u529f\uff0c\u771f\u529b\u76f4\u900f\u7ecf\u8109\uff0c\u7a74\u9053\u6570\u65e5\u4e0d\u89e3\uff0c\u6b64\u540e\u7eb5\u7136\u89e3\u5f00\uff0c\u4e5f\u8981\u9178\u75db\u96be\u5f53\uff0c\u5341\u5929\u534a\u6708\u4e0d\u6108\u3002 \u3000\u3000\u90a3\u65e5\u4ed6\u5728\u8862\u5dde\u77f3\u6881\u70b9\u5012\u6e29\u6c0f\u56db\u8001\uff0c\u4f7f\u7684\u4fbf\u662f\u8fd9\u95e8\u624b\u6cd5\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u89c1\u52bf\u5934\u4e0d\u5bf9\uff0c\u547c\u5578\u4e00\u58f0\uff0c\u593a\u95e8\u800c\u51fa\u3002\u4f59\u4f17\u8ddf\u7740\u62e5\u51fa\uff0c\u4e0d\u4e00\u523b\uff0c\u4e00\u5ea7\u5927\u5c4b\u4e2d\u7a7a\u8361\u8361\u7684\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u53ea\u5269\u4e0b\u5730\u4e0a\u52a8\u5f39\u4e0d\u5f97\u7684\u51e0\u5341\u4eba\uff0c\u6709\u7684\u547b\u541f\u4f4e\u547c\uff0c\u6709\u7684\u6012\u76ee\u800c\u89c6\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u53eb\uff1a\u201c\u9752\u5f1f\uff0c\u9752\u5f1f\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d\u9664\u4e86\u9635\u9635\u56de\u58f0\u4e4b\u5916\uff0c\u6beb\u65e0\u58f0\u606f\u3002\u4ed6\u4ecd\u4e0d\u6b7b\u5fc3\uff0c\u53c8\u5230\u6bcf\u4e2a\u623f\u95f4\u67e5\u770b\u4e00\u904d\uff0c\u7ec8\u4e8e\u5e9f\u7136\u9000\u51fa\uff1b\u63d0\u8d77\u51e0\u540d\u6559\u4f17\u903c\u95ee\uff0c\u5404\u4eba\u5747\u662f\u95ed\u76ee\u4e0d\u7b54\u3002 \u3000\u3000\u8881\u627f\u5fd7\u65e0\u6cd5\u53ef\u65bd\uff0c\u53ea\u5f97\u56de\u5230\u6b63\u6761\u5b50\u80e1\u540c\u3002\u89c1\u7126\u5b9b\u513f\u5df2\u53d6\u5f97\u51b0\u87fe\uff0c\u7387\u9886\u4e86\u91d1\u9f99\u5e2e\u7684\u51e0\u540d\u5927\u5f1f\u5b50\u6765\u5230\uff0c\u5c06\u6c99\u5929\u5e7f\u7b49\u8eab\u4e0a\u6bd2\u6c14\u5438\u51c0\u3001\u4f24\u53e3\u5305\u597d\u3002\u8881\u627f\u5fd7\u89c1\u5404\u4eba\u6027\u547d\u65e0\u788d\uff0c\u4f46\u9752\u9752\u843d\u5165\u654c\u624b\uff0c\u4e0d\u7981\u6101\u80a0\u767e\u7ed3\u3002\u7126\u5b9b\u513f\u8f6f\u8bed\u5bbd\u6170\uff0c\u6d3e\u51fa\u5e2e\u53cb\u56db\u5904\u6253\u542c\u6d88\u606f\u3002 \u3000\u3000\u8fc7\u4e86\u5927\u534a\u4e2a\u65f6\u8fb0\uff0c\u5ffd\u7136\u84ec\u7684\u4e00\u58f0\uff0c\u5c4b\u9876\u4e0a\u63b7\u4e0b\u4e00\u4e2a\u5927\u5305\u88f9\u6765\u3002\u4f17\u4eba\u5403\u4e86\u4e00\u60ca\u3002\u8881\u627f\u5fd7\u7126\u6025\u5f02\u5e38\uff0c\u53cc\u624b\u4e00\u626f\uff0c\u62c9\u65ad\u5305\u4e0a\u7ef3\u7d22\uff0c\u8fd8\u672a\u6253\u5f00\uff0c\u5df2\u95fb\u5230\u4e00\u9635\u8840\u8165\u6c14\uff0c\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u53cc\u624b\u51fa\u6c57\uff0c\u4e00\u63ed\u5f00\u5305\u88b1\uff0c\u8d6b\u7136\u662f\u4e00\u5806\u88ab\u5207\u6210\u516b\u5757\u7684\u5c38\u9996\uff0c\u9996\u7ea7\u9762\u8272\u5df2\u6210\u4e4c\u9ed1\uff0c\u4f46\u767d\u987b\u767d\u53d1\u5b9b\u7136\u53ef\u8fa8\u3002\u8881\u627f\u5fd7\u4e00\u5b9a\u795e\uff0c\u624d\u770b\u6e05\u695a\u8fd9\u5c38\u9996\u539f\u6765\u662f\u72ec\u773c\u795e\u9f99\u5355\u94c1\u751f\u3002 \u3000\u3000\u4ed6\u8dc3\u4e0a\u5c4b\u9876\uff0c\u56db\u4e0b\u5f20\u671b\uff0c\u53ea\u89c1\u897f\u5357\u89d2\u4e0a\u8fdc\u5904\u6709\u4e00\u6761\u9ed1\u5f71\u5411\u524d\u75be\u5954\uff0c\u77e5\u9053\u5fc5\u662f\u9001\u5c38\u9996\u6765\u4e4b\u4eba\uff0c\u5f53\u4e0b\u63d0\u6c14\u6025\u8ffd\uff0c\u8d76\u51fa\u91cc\u8bb8\uff0c\u53ea\u89c1\u4ed6\u5954\u5165\u4e00\u5ea7\u6797\u5b50\u4e2d\u53bb\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u76f4\u8ddf\u4e86\u8fdb\u53bb\u3002\u53ea\u89c1\u90a3\u4eba\u8d70\u5230\u6811\u6797\u6df1\u5904\uff0c\u6570\u5341\u540d\u4e94\u6bd2\u6559\u6559\u4f17\u56f4\u7740\u4e00\u5806\u706b\uff0c\u6b63\u5728\u9ad8\u8c08\u9614\u8bba\u3002\u4e00\u4eba\u5076\u7136\u56de\u5934\uff0c\u7a81\u89c1\u8881\u627f\u5fd7\u63a9\u6765\uff0c\u60ca\u53eb\u9053\uff1a\u201c\u514b\u661f\u6765\u5566\uff01\u201d\u56db\u6563\u5954\u9003\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5148\u8ffd\u9003\u5f97\u6700\u8fdc\u6700\u5feb\u7684\uff0c\u4e3e\u624b\u8e22\u8db3\uff0c\u628a\u5404\u4eba\u7a74\u9053\u4e00\u4e00\u70b9\u4e86\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u8fd1\u8005\u624b\u70b9\u8098\u649e\uff0c\u8fdc\u8005\u94dc\u94b1\u63b7\u6253\uff0c\u53ea\u542c\u5f97\u6797\u4e2d\u547c\u5578\u5954\u9010\uff0c\u60ca\u53eb\u65a5\u9a82\u4e4b\u58f0\u5927\u4f5c\u3002\u8fc7\u4e86\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u6797\u4e2d\u58f0\u606f\u4ff1\u5bc2\uff0c\u8881\u627f\u5fd7\u5782\u624b\u8d70\u51fa\uff0c\u62cd\u4e86\u62cd\u8eab\u4e0a\u7684\u7070\u5c18\u3002 \u3000\u3000\u8fd9\u4e00\u5f79\u628a\u5c91\u5176\u65af\u3001\u9f50\u4e91\u7488\u7b49\u4e94\u6bd2\u6559\u4e2d\u9ad8\u624b\u4e00\u9f13\u4f5c\u6c14\u7684\u5c3d\u6570\u70b9\u5012\uff0c\u53ea\u662f\u4f55\u94c1\u624b\u548c\u4f55\u7ea2\u836f\u4e24\u4eba\u4e0d\u5728\u5176\u5185\u3002\u8881\u627f\u5fd7\u5fc3\u4e2d\u7a0d\u5b9a\uff0c\u5bfb\u601d\uff1a\u201c\u53ea\u8981\u9752\u5f1f\u6b64\u65f6\u8fd8\u4e0d\u906d\u6bd2\u624b\uff0c\u4ed6\u4eec\u4fbf\u6709\u518d\u5927\u4ec7\u6068\uff0c\u4e5f\u4e0d\u6562\u52a0\u5bb3\u4e8e\u5979\u3002\u201d \u3000\u3000\u56de\u5230\u4f4f\u5b85\uff0c\u7126\u5fc3\u7b49\u5019\uff0c\u508d\u665a\u65f6\u5206\uff0c\u51fa\u53bb\u6253\u63a2\u7684\u4eba\u90fd\u56de\u62a5\u8bf4\u6ca1\u6709\u7ebf\u7d22\u3002\u5929\u4ea4\u4e8c\u66f4\uff0c\u8881\u627f\u5fd7\u5429\u5490\u5434\u5e73\u4e0e\u7f57\u7acb\u5982\uff0c\u5c06\u5355\u94c1\u751f\u7684\u5c38\u9996\u9001\u5f80\u987a\u5929\u5e9c\u5c39\u8859\u95e8\u53bb\uff0c\u516c\u95e8\u4e2d\u4eba\u89c1\u5230\u4ed6\u7684\u6a21\u6837\uff0c\u81ea\u77e5\u662f\u4e94\u6bd2\u6559\u4e0b\u7684\u6bd2\u624b\u3002\u7126\u5b9b\u513f\u9886\u7740\u51e0\u540d\u5e2e\u53cb\uff0c\u7559\u5728\u5b85\u91cc\u770b\u62a4\u4f24\u8005\uff0c\u9632\u5907\u654c\u4eba\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7126\u8651\u6302\u6000\uff0c\u54ea\u91cc\u7761\u5f97\u7740\uff1f\u76d8\u819d\u5750\u5728\u5e8a\u4e0a\uff0c\u7b79\u601d\u660e\u65e5\u7ee7\u7eed\u627e\u5bfb\u9752\u9752\u4e4b\u7b56\u3002\u7ea6\u83ab\u5750\u4e86\u4e00\u4e2a\u66f4\u6b21\uff0c\u56db\u4e0b\u65e0\u58f0\uff0c\u53ea\u542c\u5f97\u8fdc\u5904\u6df1\u5df7\u4e2d\u6709\u4e00\u4e24\u58f0\u72ac\u5420\uff0c\u6253\u66f4\u7684\u7af9\u67dd\u7531\u8fdc\u800c\u8fd1\uff0c\u53c8\u7531\u8fd1\u800c\u8fdc\u3002\u4ed6\u601d\u6f6e\u8d77\u4f0f\uff0c\u81ea\u6068\u8fd9\u4e00\u6b21\u5931\u7b97\u4e2d\u8ba1\uff0c\u906d\u5230\u4e0b\u5c71\u4ee5\u6765\u7684\u9996\u6b21\u5927\u8d25\uff0c\u9759\u5bc2\u4e2d\u5ffd\u542c\u5f97\u56f4\u5899\u9876\u4e0a\u8f7b\u8f7b\u4e00\u54cd\uff0c\u5fc3\u60f3\uff1a\u201c\u5982\u662f\u5434\u7f57\u4e8c\u4eba\u56de\u6765\uff0c\u8f7b\u8eab\u529f\u592b\u65e0\u6b64\u9ad8\u660e\uff0c\u5fc5\u662f\u6765\u4e86\u654c\u4eba\u3002\u201d\u5f53\u4e0b\u5b89\u5750\u5e8a\u4e0a\uff0c\u9759\u4ee5\u5f85\u53d8\u3002\u53ea\u542c\u7a97\u5916\u5982\u4e00\u53f6\u843d\u5730\uff0c\u63a5\u7740\u4e00\u4eba\u683c\u683c\u5a07\u7b11\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u5ba2\u4eba\u6765\u5566\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6709\u52b3\u4f55\u6559\u4e3b\u6789\u9a7e\uff0c\u8bf7\u8fdb\u6765\u5427\uff01\u201d\u53d6\u51fa\u706b\u6298\u70b9\u4eae\u8721\u70db\uff0c\u5f00\u95e8\u8fce\u5ba2\u3002 \u3000\u3000\u4f55\u94c1\u624b\u98d8\u7136\u800c\u5165\uff0c\u89c1\u8881\u627f\u5fd7\u5ba4\u4e2d\u9648\u8bbe\u7b80\u964b\uff0c\u9664\u4e86\u4e00\u5e8a\u4e00\u684c\u4e4b\u5916\uff0c\u56db\u58c1\u8427\u7136\uff0c\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\u597d\u6e05\u9ad8\u5440\u3002\u201d\u8881\u627f\u5fd7\u54fc\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u6211\u8fd9\u756a\u6765\u610f\uff0c\u8881\u76f8\u516c\u5b9a\u662f\u77e5\u9053\u7684\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8981\u8bf7\u4f55\u6559\u4e3b\u793a\u4e0b\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u6709\u6c42\u4e8e\u6211\uff0c\u6211\u4e5f\u6709\u6c42\u4e8e\u4f60\uff0c\u54b1\u4eec\u8fd9\u4e2a\u56de\u5408\u4ecd\u662f\u6ca1\u6709\u8f93\u8d62\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u5fc5\u518d\u8f83\u91cf\u4e86\u3002\u4f55\u6559\u4e3b\u6709\u667a\u6709\u52c7\uff0c\u5144\u5f1f\u5341\u5206\u4f69\u670d\u3002\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e00\u4e2a\u56de\u5408\uff0c\u9664\u975e\u4f60\u628a\u6211\u4eec\u4e94\u6bd2\u6559\u4e00\u4e0b\u5b50\u706d\u4e86\uff0c\u5426\u5219\u8fd8\u6709\u5f97\u8ba9\u4f60\u5934\u75bc\u7684\u5462\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u51db\uff0c\u5fc3\u60f3\u4ed6\u4eec\u7ea0\u7f20\u4e0d\u4f11\uff0c\u786e\u662f\u4e0d\u6613\u62b5\u6321\uff0c\u8bf4\u9053\uff1a\u201c\u4f55\u6559\u4e3b\u65e2\u4e0e\u6211\u90a3\u5144\u5f1f\u7684\u7236\u4eb2\u6709\u4ec7\uff0c\u8fd8\u662f\u5f84\u53bb\u627e\u4ed6\u672c\u4eba\u4e3a\u662f\uff0c\u4f55\u5fc5\u8ddf\u5e74\u8f7b\u4eba\u4e3a\u96be\uff1f\u5e38\u8a00\u9053\uff1a\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\u2026\u2026\u201d \u3000\u3000\u4f55\u94c1\u624b\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5c06\u6765\u518d\u8bf4\u3002\u5ba2\u4eba\u5230\u6765\uff0c\u4f60\u9152\u4e5f\u4e0d\u8bf7\u4eba\u559d\u4e00\u676f\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\u6b64\u4eba\u771f\u602a\uff0c\u4e8e\u662f\u547d\u7ae5\u4ec6\u7aef\u6574\u9152\u83dc\u3002\u7126\u5b9b\u513f\u4e0d\u653e\u5fc3\uff0c\u6362\u4e0a\u4e86\u4e66\u7ae5\u7684\u88c5\u675f\uff0c\u4eb2\u7aef\u9152\u83dc\uff0c\u9001\u8fdb\u623f\u6765\u3002\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u771f\u662f\u5f3a\u5c06\u624b\u4e0b\u65e0\u5f31\u5175\uff0c\u8881\u76f8\u516c\u7684\u4e66\u7ae5\uff0c\u751f\u5f97\u4e5f\u8fd9\u822c\u4fca\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u659f\u4e86\u4e24\u676f\u9152\u3002\u4f55\u94c1\u624b\u4e3e\u676f\u996e\u5e72\uff0c\u63a5\u7740\u53c8\u8fde\u996e\u4e24\u676f\uff0c\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\u4e0d\u80af\u8d4f\u8138\u559d\u6211\u4eec\u7684\u9152\uff0c\u5c0f\u59b9\u5374\u751f\u6765\u5364\u83bd\u5927\u80c6\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u63a5\u53e3\u9053\uff1a\u201c\u6211\u4eec\u7684\u9152\u6ca1\u6bd2\u3002\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u771f\u662f\u4e00\u4f4d\u4f36\u7259\u5229\u9f7f\u7684\u5c0f\u7ba1\u5bb6\u3002\u5e72\u676f\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u5979\u5bf9\u996e\u4e86\u4e00\u676f\uff0c\u70db\u5149\u4e0b\u89c1\u5979\u661f\u773c\u6d41\u6ce2\uff0c\u6843\u816e\u6b32\u6655\uff0c\u6697\u5fd6\uff1a\u201c\u6240\u8bc6\u5973\u5b50\u4e4b\u4e2d\uff0c\u8bba\u76f8\u8c8c\u4e4b\u7f8e\uff0c\u81ea\u4ee5\u963f\u4e5d\u4e3a\u7b2c\u4e00\u3002 \u3000\u3000\u5c0f\u6167\u8bda\u6073\u771f\u631a\u3002\u5b9b\u513f\u8c6a\u8fc8\u7cbe\u7ec6\u3002\u9752\u5f1f\u867d\u7231\u4f7f\u5c0f\u6027\u513f\uff0c\u4f46\u5bf9\u6211\u4e00\u7247\u771f\u60c5\u3002\u54ea\u77e5\u8fd8\u6709\u4f55\u94c1\u624b\u8fd9\u822c\u8273\u82e5\u6843\u674e\u3001\u6bd2\u5982\u86c7\u874e\u7684\u4eba\u7269\uff0c\u771f\u662f\u5929\u4e0b\u4e4b\u5927\uff0c\u5947\u4eba\u5f02\u58eb\uff0c\u6240\u5728\u90fd\u6709\u3002\u201d\u4f55\u94c1\u624b\u89c1\u4ed6\u51fa\u795e\uff0c\u4e5f\u4e0d\u8a00\u8bed\uff0c\u53ea\u6de1\u6de1\u800c\u7b11\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8881\u76f8\u516c\u7684\u6b66\u529f\uff0c\u5c0f\u59b9\u5fc3\u6298\u4e4b\u6781\u3002\u4f3c\u4e4e\u5c0a\u5e08\u91d1\u86c7\u90ce\u541b\u4e5f\u4e0d\u4f1a\u8fd9\u70b9\u7a74\u624b\u6bb5\uff0c\u8fd9\u95e8\u529f\u592b\uff0c\u8881\u76f8\u516c\u662f\u53e6\u6709\u5e08\u627f\u7684\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u534e\u5c71\u6d3e\u95e8\u4e0b\u5f1f\u5b50\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u8881\u76f8\u516c\u6b66\u529f\u96c6\u8bf8\u5bb6\u6240\u957f\uff0c\u96be\u602a\u795e\u4e4e\u5176\u6280\u3002\u5c0f\u59b9\u4eca\u665a\u662f\u6c42\u5e08\u6765\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u8fd9\u8bdd\u6211\u53ef\u4e0d\u660e\u767d\u4e86\u3002\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\u82e5\u662f\u4e0d\u5acc\u5c0f\u59b9\u8d44\u8d28\u611a\u9c81\uff0c\u5c31\u8bf7\u6536\u5f52\u95e8\u4e0b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f55\u6559\u4e3b\u4e00\u6559\u4e4b\u957f\uff0c\u6b66\u529f\u51fa\u795e\u5165\u5316\uff0c\u5374\u6765\u5f00\u8fd9\u73a9\u7b11\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u5982\u4e0d\u4f20\u6211\u89e3\u7a74\u4e4b\u6cd5\uff0c\u96be\u9053\u6211\u4eec\u6559\u4e2d\u51e0\u5341\u4e2a\u4eba\uff0c\u5c31\u773c\u7741\u7741\u8ba9\u4ed6\u4eec\u9001\u547d\u4e0d\u6210\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u53ea\u8981\u4f60\u628a\u6211\u670b\u53cb\u9001\u56de\uff0c\u518d\u7b54\u5e94\u4ee5\u540e\u6c38\u8fdc\u4e0d\u6765\u7ea0\u7f20\uff0c\u6211\u5f53\u7136\u4f1a\u7ed9\u4ed6\u4eec\u89e3\u6551\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8881\u76f8\u516c\u662f\u4e0d\u80af\u6536\u6211\u8fd9\u4e2a\u5f92\u5f1f\u4e86\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5144\u5f1f\u5b66\u827a\u672a\u7cbe\uff0c\u6c42\u5e08\u8fd8\u6765\u4e0d\u53ca\uff0c\u54ea\u6562\u6559\u4eba\uff1f \u3000\u3000\u54b1\u4eec\u597d\u8a00\u5584\u7f62\uff0c\u65e2\u5f80\u4e0d\u548e\uff0c\u4f60\u9053\u600e\u6837\uff1f\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u6211\u628a\u4f60\u670b\u53cb\u9001\u8fd8\uff0c\u4f60\u628a\u6211\u7684\u90e8\u5c5e\u6cbb\u597d\u3002\u4ee5\u540e\u7684\u4e8b\uff0c\u8d70\u7740\u77a7\u5427\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u59cb\u7ec8\u4e0d\u80af\u7b54\u5e94\u7f62\u624b\u8a00\u548c\uff0c\u6012\u6c14\u6e10\u751f\uff0c\u6697\u60f3\uff1a\u201c\u4e94\u6bd2\u6559\u867d\u7136\u6a2a\u884c\u5929\u5357\uff0c\u4f46\u6211\u4eec\u4e03\u7701\u82f1\u96c4\u8c6a\u6770\uff0c\u4e5f\u4e0d\u89c1\u5f97\u5c31\u6015\u4e86\u4f60\u4eec\u3002\u201d\u5f53\u4e0b\u9ed8\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u4f55\u94c1\u624b\u76c8\u76c8\u7ad9\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u554a\u54df\uff0c\u54b1\u4eec\u7684\u8881\u5927\u76df\u4e3b\u751f\u6c14\u5566\u3002\u201d \u3000\u3000\u88e3\u887d\u4e07\u798f\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u597d\u5566\uff0c\u6211\u7ed9\u4f60\u8d54\u4e0d\u662f\u3002\u201d\u8881\u627f\u5fd7\u8fd8\u4e86\u4e00\u63d6\uff0c\u5fc3\u4e0b\u602b\u7136\u4e0d\u60a6\u3002\u4f55\u94c1\u624b\u9053\uff1a\u201c\u660e\u513f\u6211\u628a\u4f60\u670b\u53cb\u9001\u56de\u6765\u3002 \u3000\u3000\u4fbf\u8bf7\u4f60\u5927\u9a7e\u5149\u4e34\uff0c\u6551\u6cbb\u6211\u7684\u670b\u53cb\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u5fae\u5fae\u8eac\u8eab\uff0c\u8f6c\u8eab\u8d70\u51fa\u3002\u5979\u5e76\u4e0d\u4e0a\u5c4b\uff0c\u5f84\u5f80\u5927\u95e8\u8d70\u53bb\u3002\u8881\u627f\u5fd7\u53ea\u5f97\u8ddf\u7740\u9001\u51fa\uff0c\u7ae5\u4ec6\u70b9\u70db\u5f00\u95e8\u3002 \u3000\u3000\u7126\u5b9b\u513f\u8ddf\u5728\u8881\u627f\u5fd7\u8eab\u540e\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u5973\u5b50\u884c\u52a8\u8be1\u79d8\uff0c\u522b\u5728\u5927\u95e8\u5916\u4f0f\u6709\u5f92\u515a\uff0c\u8bf1\u8881\u76f8\u516c\u51fa\u53bb\u88ad\u51fb\uff0c\u6211\u5148\u53bb\u77a7\u77a7\u3002\u201d\u4e8e\u662f\u6162\u6162\u843d\u540e\uff0c\u8eab\u4e0a\u85cf\u597d\u86fe\u7709\u94a2\u523a\uff0c\u8d8a\u5899\u800c\u51fa\uff0c\u8eb2\u5728\u5899\u89d2\u8fb9\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u5927\u95e8\u53e3\u505c\u4e86\u4e00\u4e58\u6696\u8f7f\uff0c\u56db\u540d\u8f7f\u592b\u7ad9\u5728\u8f7f\u524d\uff0c\u6b64\u5916\u5374\u65e0\u522b\u4eba\u3002\u7126\u5b9b\u513f\u77ee\u4e86\u8eab\u5b50\uff0c\u6084\u6084\u8d70\u5230\u8f7f\u540e\uff0c\u53cc\u624b\u628a\u8f7f\u5b50\u8f7b\u8f7b\u4e00\u6258\uff0c\u77e5\u9053\u8f7f\u5185\u65e0\u4eba\uff0c\u8fd9\u624d\u653e\u5fc3\uff0c\u6b63\u8981\u8d70\u56de\uff0c\u5927\u95e8\u5f00\u5904\uff0c\u7ae5\u4ec6\u624b\u6267\u706f\u7b3c\uff0c\u8881\u627f\u5fd7\u628a\u4f55\u94c1\u624b\u9001\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7126\u5b9b\u513f\u7075\u673a\u4e00\u52a8\uff1a\u201c\u5979\u65e2\u4e0d\u80af\u7f62\u624b\uff0c\u6b64\u540e\u9ebb\u70e6\u6b63\u591a\u3002\u6211\u8981\u627e\u5230\u5979\u7684\u843d\u811a\u6240\u5728\uff0c\u4ed6\u4eec\u518d\u6765\u7ea0\u7f20\uff0c\u597d\u8ba9\u8881\u76f8\u516c\u4e0a\u95e8\u653b\u5979\u4e2a\u51fa\u5176\u4e0d\u610f\u3002\u201d\u5979\u5b58\u4e86\u62a5\u6069\u4e4b\u5fc3\uff0c\u4e5f\u4e0d\u6015\u524d\u9014\u8270\u9669\uff0c\u7f29\u8eab\u94bb\u5165\u8f7f\u5e95\uff0c\u624b\u811a\u6500\u4f4f\u4e86\u8f7f\u5e95\u6728\u67b6\u3002\u90a3\u6696\u8f7f\u56db\u5468\u7528\u539a\u5462\u56f4\u4f4f\uff0c\u53c8\u5728\u9ed1\u591c\uff0c\u7adf\u65e0\u4e00\u4eba\u53d1\u89c9\u3002\u53ea\u542c\u5f97\u4f55\u94c1\u624b\u4e00\u9635\u8f7b\u7b11\uff0c\u8e0f\u5165\u8f7f\u4e2d\u3002\u56db\u540d\u8f7f\u592b\u62ac\u8d77\u8f7f\u5b50\uff0c\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u53ea\u89c9\u56db\u540d\u8f7f\u592b\u5065\u6b65\u5982\u98de\uff0c\u539f\u6765\u62ac\u8f7f\u7684\u4eba\u4e5f\u90fd\u8eab\u6709\u6b66\u529f\uff0c\u5979\u4e0d\u7981\u5bb3\u6015\u8d77\u6765\u3002\u8fd9\u65f6\u6b63\u5f53\u9686\u51ac\uff0c\u5bd2\u98ce\u5f7b\u9aa8\uff0c\u6696\u8f7f\u5e95\u4e0b\u90fd\u7ed3\u4e86\u51b0\uff0c\u88ab\u5979\u53e3\u4e2d\u70ed\u6c14\u4e00\u5475\uff0c\u5316\u6210\u4e86\u51b7\u6c34\u4e00\u6ef4\u6ef4\u7684\u843d\u4e0b\u3002\u7126\u5b9b\u513f\u53ea\u5f97\u4efb\u7531\u51b7\u6c34\u843d\u5728\u8138\u4e0a\uff0c\u4e0d\u6562\u62c2\u62ed\uff0c\u53ea\u6015\u8eab\u5b50\u4e00\u52a8\uff0c\u7acb\u7ed9\u4f55\u94c1\u624b\u53d1\u89c9\u3002 \u3000\u3000\u8d70\u4e86\u7ea6\u83ab\u534a\u4e2a\u65f6\u8fb0\uff0c\u5ffd\u542c\u4e00\u58f0\u547c\u53f1\uff0c\u8f7f\u5b50\u505c\u4f4f\u3002\u4e00\u4e2a\u7537\u4eba\u58f0\u97f3\u559d\u9053\uff1a\u201c\u59d3\u4f55\u7684\u8d31\u5a62\uff0c\u5feb\u51fa\u6765\u9886\u6b7b\u3002\u201d\u7126\u5b9b\u513f\u5fc3\u4e2d\u5947\u602a\uff1a\u201c\u8fd9\u58f0\u97f3\u597d\u719f\uff0c\u90a3\u662f\u8c01\u554a\uff1f\u201d\u53c8\u542c\u53e6\u4e00\u4e2a\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u4e94\u6bd2\u6559\u6a2a\u884c\u4e00\u4e16\uff0c\u60f3\u4e0d\u5230\u4e5f\u6709\u4eca\u5929\u3002\u201d\u7126\u5b9b\u513f\u4e00\u60ca\uff1a\u201c\u90a3\u662f\u95f5\u5b50\u534e\uff01\u55ef\uff0c\u7b2c\u4e00\u4e2a\u8bf4\u8bdd\u7684\u662f\u4ed6\u5e08\u5f1f\u6d1e\u7384\u9053\u4eba\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u56db\u5468\u811a\u6b65\u58f0\u54cd\uff0c\u8bb8\u591a\u4eba\u56f4\u4e86\u4e0a\u6765\u3002\u8f7f\u592b\u653e\u4e0b\u8f7f\u5b50\uff0c\u62bd\u51fa\u5175\u5203\u3002\u7126\u5b9b\u513f\u62c9\u5f00\u8f7f\u969c\u4e00\u89d2\u5411\u5916\u5f20\u671b\uff0c\u89c1\u4e1c\u8fb9\u7ad9\u7740\u56db\u4e94\u4eba\uff0c\u90fd\u662f\u8eab\u7a7f\u9053\u888d\u3001\u624b\u6267\u957f\u5251\u7684\u9053\u58eb\uff0c\u5fc3\u60f3\uff1a\u201c\u897f\u3001\u5317\u3001\u5357\u4e09\u8fb9\u5fc5\u90fd\u6709\u4eba\uff0c\u4ed9\u90fd\u6d3e\u5927\u4e3e\u62a5\u4ec7\u6765\u4e86\u3002\u201d\u53ea\u89c9\u8f7f\u8eab\u5fae\u5fae\u4e00\u6643\uff0c\u4f55\u94c1\u624b\u5df2\u8dc3\u51fa\u8f7f\u5916\uff0c\u5a07\u58f0\u559d\u9053\uff1a\u201c\u6c34\u4e91\u8d3c\u9053\u6b7b\u4e86\u6ca1\u6709\uff1f\u4f60\u4eec\u80c6\u5b50\u4e5f\u771f\u5927\uff0c\u60f3\u5e72\u751a\u4e48\uff1f\u201d\u4e00\u540d\u957f\u987b\u9053\u4eba\u559d\u9053\uff1a\u201c\u6211\u4eec\u5e08\u7236\u9ec4\u6728\u9053\u957f\u5230\u5e95\u5728\u54ea\u91cc\uff0c\u5feb\u8bf4\u51fa\u6765\uff0c\u514d\u4f60\u591a\u53d7\u6298\u78e8\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u683c\u683c\u5a07\u7b11\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5e08\u7236\u53c8\u4e0d\u662f\u4e09\u5c81\u5a03\u5a03\uff0c\u8ff7\u4e86\u8def\u8d70\u5931\u4e86\uff0c\u5374\u6765\u95ee\u6211\u8981\u4eba\u3002\u4f60\u4eec\u628a\u5e08\u7236\u4ea4\u7ed9\u6211\u7167\u7ba1\u4e86\uff0c\u662f\u4e0d\u662f\u5462\uff1f\u597d\u5427\uff0c\u5927\u5bb6\u6b66\u6797\u4e00\u8109\uff0c\u6211\u5e2e\u4f60\u4eec\u627e\u627e\u5427\uff0c\u514d\u5f97\u4ed6\u53ef\u601c\u89c1\u513f\u7684\uff0c\u6d41\u843d\u5728\u5916\uff0c\u6ca1\u4eba\u7167\u987e\u3002\u4e5f\u4e0d\u77e5\u662f\u7ed9\u4eba\u62d0\u53bb\u4e86\u5462\uff0c\u8fd8\u662f\u7ed9\u4eba\u5356\u5230\u4e86\u756a\u90a6\u3002\u201d\u7126\u5b9b\u513f\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5973\u4eba\u8bf4\u8bdd\uff0c\u603b\u662f\u8fd9\u4e48\u5a07\u58f0\u5a9a\u6c14\u7684\uff0c\u6211\u5148\u524d\u8fd8\u9053\u5979\u6545\u610f\u5411\u8881\u76f8\u516c\u53d1\u55f2\u3002\u201d \u3000\u3000\u90a3\u957f\u987b\u9053\u4eba\u6012\u9053\uff1a\u201c\u4e94\u6bd2\u6559\u901e\u51f6\u6a2a\u884c\uff0c\u4eca\u65e5\u6559\u4f60\u77e5\u9053\u6076\u6709\u6076\u62a5\uff01\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u4ed9\u90fd\u6d3e\u5728\u6c5f\u6e56\u4e0a\u672c\u6765\u4e5f\u7b97\u662f\u6709\u70b9\u513f\u5c0f\u540d\u6c14\u7684\uff0c\u53ef\u662f\u5e73\u65f6\u4e0d\u6562\u6b63\u5927\u5149\u660e\u7684\u6765\u627e\u6211\uff0c\u73b0\u4eca\u77e5\u9053\u6211\u4eec\u6559\u91cc\u591a\u4eba\u53d7\u4f24\uff0c\u5c31\u9b3c\u9b3c\u795f\u795f\u7684\u8eb2\u5728\u8fd9\u91cc\u3002\u54c8\u54c8\uff0c\u5475\u5475\uff0c\u563b\u563b\uff0c\u563f\u563f\uff01\u201d\u7247\u523b\u4e4b\u95f4\uff0c\u6362\u4e86\u51e0\u79cd\u7b11\u58f0\uff0c\u5979\u7b11\u58f0\u672a\u6bd5\uff0c\u53ea\u542c\u897f\u5317\u89d2\u4e0a\u4e00\u4eba\u201c\u554a\u201d\u7684\u4e00\u58f0\u60e8\u53eb\uff0c\u60f3\u662f\u4e2d\u4e86\u5979\u6bd2\u624b\uff0c\u4e00\u65f6\u53ea\u542c\u5f97\u547c\u53f1\u6012\u9a82\u3001\u5175\u5203\u78b0\u649e\u4e4b\u58f0\u5927\u4f5c\u3002 \u3000\u3000\u8fd9\u6b21\u4ed9\u90fd\u6d3e\u503e\u5de2\u800c\u51fa\uff0c\u6765\u7684\u90fd\u662f\u9ad8\u624b\uff0c\u9976\u662f\u4f55\u94c1\u624b\u6b66\u529f\u9ad8\u5f3a\uff0c\u5374\u59cb\u7ec8\u95ef\u4e0d\u51fa\u53bb\u3002\u6597\u4e0d\u5230\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u56db\u540d\u8f7f\u592b\u5148\u540e\u4e2d\u5251\uff0c\u6216\u6b7b\u6216\u4f24\u3002 \u3000\u3000\u7126\u5b9b\u513f\u5728\u8f7f\u4e0b\u4e0d\u6562\u52a8\u5f39\uff0c\u773c\u89c1\u4ed9\u90fd\u95e8\u4eba\u5251\u6cd5\u8fc5\u6377\u72e0\u8fa3\uff0c\u679c\u6709\u72ec\u5f97\u4e4b\u79d8\uff0c\u5fc3\u60f3\u5f53\u65e5\u8881\u76f8\u516c\u4e00\u4e3e\u800c\u7834\u4e24\u4eea\u5251\u6cd5\uff0c\u90a3\u662f\u4ed6\u4eec\u9047\u4e0a\u4e86\u7279\u5f3a\u9ad8\u624b\uff0c\u624d\u53d7\u514b\u5236\uff0c\u5bfb\u5e38\u5251\u5ba2\u5374\u51b3\u975e\u4ed9\u90fd\u95e8\u4eba\u5bf9\u624b\u3002 \u3000\u3000\u5979\u6015\u9ed1\u591c\u4e4b\u4e2d\u8d38\u7136\u9732\u9762\uff0c\u7ed9\u4ed9\u90fd\u95e8\u5f92\u8bef\u4f1a\u662f\u4e94\u6bd2\u6559\u4f17\uff0c\u4e0d\u514d\u6789\u6b7b\u4e8e\u5251\u4e0b\uff0c\u53ea\u5f97\u5c4f\u606f\u4e0d\u52a8\u3002\u8fd9\u65f6\u4e8c\u5341\u591a\u67c4\u957f\u5251\u628a\u4f55\u94c1\u624b\u56f4\u5728\u5793\u5fc3\uff0c\u9752\u5149\u970d\u970d\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u53ea\u770b\u5f97\u5979\u60ca\u5fc3\u52a8\u9b44\u3002 \u3000\u3000\u4f55\u94c1\u624b\u5728\u6570\u5341\u540d\u597d\u624b\u56f4\u653b\u4e0b\u6c89\u7740\u5e94\u6218\u3002\u4e00\u4e2a\u5c11\u5e74\u9053\u4eba\u8e81\u8fdb\u731b\u653b\uff0c\u88ab\u5979\u94c1\u94a9\u6a2a\u5212\uff0c\u5e26\u7740\u80a9\u5934\uff0c\u767b\u65f6\u75db\u6655\u5728\u5730\uff0c\u5f53\u4e0b\u7531\u540c\u4f34\u6551\u4e86\u4e0b\u53bb\u3002\u518d\u62c6\u6570\u5341\u62db\uff0c\u4f55\u94c1\u624b\u529b\u6e10\u4e0d\u652f\u3002\u95f5\u5b50\u534e\u957f\u5251\u524a\u6765\uff0c\u75be\u653b\u9879\u9888\uff0c\u5979\u4fa7\u5934\u907f\u8fc7\uff0c\u65c1\u8fb9\u53c8\u6709\u53cc\u5251\u653b\u5230\u3002 \u3000\u3000\u53ea\u542c\u94ee\u7684\u4e00\u58f0\uff0c\u4e00\u4ef6\u7ec6\u7269\u6eda\u5230\u8f7f\u4e0b\u3002\u7126\u5b9b\u513f\u62fe\u8d77\u4e00\u770b\uff0c\u539f\u6765\u662f\u534a\u679a\u5973\u4eba\u6234\u7684\u8033\u73af\u3002\u5979\u5fc3\u4e2d\u53c8\u559c\u53c8\u6025\uff0c\u559c\u7684\u662f\u4f55\u94c1\u624b\u8fd9\u4e00\u5f79\u96be\u9003\u6027\u547d\uff0c\u53ef\u7ed9\u8881\u76f8\u516c\u9664\u4e86\u4e2a\u5927\u5bf9\u5934\uff1b\u6025\u7684\u662f\u5979\u82e5\u4e27\u547d\uff0c\u9752\u9752\u4e0d\u77e5\u843d\u5728\u4f55\u5904\uff0c\u5979\u624b\u4e0b\u6559\u4f17\u80af\u4e0d\u80af\u4ea4\u8fd8\uff0c\u5b9e\u5728\u96be\u8bf4\u3002 \u3000\u3000\u53c8\u6597\u6570\u5341\u62db\uff0c\u4f55\u94c1\u624b\u5934\u53d1\u6563\u4e71\uff0c\u5df2\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u957f\u987b\u9053\u4eba\u4e00\u58f0\u53f7\u4ee4\uff0c\u6570\u5341\u67c4\u957f\u5251\u5ffd\u5730\u56de\u6536\uff0c\u7ec4\u6210\u4e00\u5f20\u70c2\u94f6\u4e5f\u4f3c\u7684\u5251\u7f51\uff0c\u56f4\u5728\u5979\u56db\u5468\u3002\u957f\u987b\u9053\u4eba\u559d\u9053\uff1a\u201c\u6211\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u5728\u54ea\u91cc\uff1f \u3000\u3000\u4ed6\u662f\u751f\u662f\u6b7b\uff0c\u5feb\u8bf4\u3002\u201d\u4f55\u94c1\u624b\u628a\u91d1\u94a9\u5939\u5728\u80c1\u4e0b\uff0c\u6162\u6162\u4f38\u624b\u7406\u597d\u6563\u53d1\uff0c\u5ffd\u7136\u4e00\u9635\u8f7b\u7b11\uff0c\u94c1\u94a9\u8fc5\u5982\u95ea\u7535\uff0c\u4f24\u4e86\u4e00\u540d\u9053\u4eba\u3002\u4f17\u4eba\u5927\u6012\uff0c\u957f\u5251\u9f50\u65bd\uff0c\u8fd9\u4e00\u6b21\u4e0b\u624b\u518d\u4e0d\u5bb9\u60c5\uff0c\u773c\u89c1\u4f55\u94c1\u624b\u5f62\u52bf\u5371\u6025\u4e07\u5206\uff0c\u7a81\u7136\u8fdc\u5904\u4f20\u6765\u5618\u6e9c\u6e9c\u4e00\u58f0\u547c\u54e8\u3002\u4f55\u94c1\u624b\u767e\u5fd9\u4e2d\u7b11\u9053\uff1a\u201c\u6211\u5e2e\u624b\u6765\u5566\uff0c\u4f60\u4eec\u8fd8\u662f\u5feb\u8d70\u7684\u597d\uff0c\u5426\u5219\u8981\u5403\u4e8f\u7684\u5440\u3002\u201d\u7126\u5b9b\u513f\u5fc3\u60f3\uff1a\u201c\u5982\u4e0d\u77e5\u4ed6\u4eec\u662f\u5728\u62da\u6b7b\u6076\u6597\uff0c\u542c\u4e86\u5979\u8fd9\u51e0\u53e5\u53c8\u6e29\u67d4\u53c8\u5173\u5207\u7684\u53ee\u5631\uff0c\u8fd8\u4ee5\u4e3a\u5979\u662f\u5728\u8ddf\u60c5\u90ce\u8c08\u60c5\u8bf4\u7231\u54e9\uff01\u201d \u3000\u3000\u90a3\u957f\u987b\u9053\u4eba\u53eb\u9053\uff1a\u201c\u6599\u7406\u4e86\u8fd9\u8d31\u5a62\u518d\u8bf4\uff01\u201d\u5404\u4eba\u653b\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u8f6c\u773c\u95f4\u4f55\u94c1\u624b\u817f\u4e0a\u8fde\u53d7\u4e24\u5904\u5251\u4f24\uff0c\u4f46\u5979\u8fd8\u662f\u6ee1\u8138\u7b11\u5bb9\u3002\u4e00\u540d\u5e74\u8f7b\u9053\u4eba\u5fc3\u4e2d\u70e6\u8e81\uff0c\u4e0d\u5fcd\u89c1\u8fd9\u4e48\u4e00\u4e2a\u5343\u5a07\u767e\u5a9a\u3001\u7b11\u9765\u8fce\u4eba\u7684\u59d1\u5a18\u7ed9\u4e71\u5251\u5206\u5c38\uff0c\u559d\u9053\uff1a\u201c\u4f60\u522b\u7b11\u5566\uff0c\u6210\u4e0d\u6210\uff1f\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u9053\u957f\u8bf4\u751a\u4e48\uff1f\u201d\u90a3\u9053\u4eba\u4e00\u5446\uff0c\u6b63\u5f85\u56de\u7b54\uff0c\u773c\u524d\u5ffd\u7136\u91d1\u5149\u4e00\u95ea\u3002\u95f5\u5b50\u534e\u6025\u547c\uff1a\u201c\u7559\u795e\uff01\u201d\u4f46\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff0c\u6ce2\u7684\u4e00\u58f0\uff0c\u91d1\u94a9\u5df2\u523a\u4e2d\u4ed6\u80cc\u5fc3\u3002 \u3000\u3000\u9163\u6597\u4e2d\u8fdc\u5904\u54e8\u58f0\u66f4\u6025\uff0c\u4ed9\u90fd\u6d3e\u5206\u51fa\u516b\u4eba\u8fce\u4e0a\u53bb\u963b\u62e6\u3002\u53ea\u542c\u91d1\u94c1\u4ea4\u9e23\uff0c\u4e0d\u4e45\u516b\u4eba\u8d25\u4e86\u4e0b\u6765\uff0c\u4ed9\u90fd\u95e8\u4eba\u53c8\u5206\u4eba\u4e0a\u53bb\u589e\u63f4\u3002 \u3000\u3000\u8fd9\u8fb9\u4f55\u94c1\u624b\u7acb\u65f6\u4e00\u677e\uff0c\u4f46\u4ed9\u90fd\u6d3e\u4f59\u4eba\u4ecd\u662f\u529b\u653b\uff0c\u5979\u60f3\u51b2\u8fc7\u53bb\u4e0e\u6765\u63f4\u4e4b\u4eba\u4f1a\u5408\uff0c\u5374\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u53cc\u65b9\u52bf\u5747\u529b\u654c\uff0c\u9ad8\u547c\u93d6\u6218\u3002\u6253\u4e86\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u95f5\u5b50\u534e\u9ad8\u53eb\uff1a\u201c\u597d\uff0c\u597d\uff01\u592a\u767d\u4e09\u82f1\uff0c\u4f60\u4eec\u4e09\u4e2a\u5356\u56fd\u8d3c\u4e5f\u6765\u5566\u3002\u201d\u4e00\u4eba\u7c97\u58f0\u7c97\u6c14\u7684\u9053\uff1a\u201c\u600e\u4e48\u6837\uff01\u4f60\u77e5\u9053\u7237\u7237\u5389\u5bb3\uff0c\u5feb\u7ed9\u6211\u6eda\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u5fc3\u4e0b\u60ca\u7591\uff1a\u201c\u592a\u767d\u4e09\u82f1\u6311\u62e8\u79bb\u95f4\uff0c\u60f3\u5bb3\u6211\u7239\u7239\uff0c\u660e\u660e\u5df2\u7ed9\u8881\u76f8\u516c\u4ed6\u4eec\u64d2\u4f4f\u3002\u7239\u7239\u540e\u6765\u5c06\u4e09\u4eba\u9001\u4e0a\u5357\u4eac\u8859\u95e8\uff0c\u600e\u4e48\u53c8\u51fa\u6765\u4e86\uff1f\u662f\u8d8a\u72f1\uff1f\u8fd8\u662f\u8d2a\u5b98\u5356\u653e\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u4f55\u94c1\u624b\u7684\u5e2e\u624b\u6765\u8005\u6108\u591a\uff0c\u4ed9\u90fd\u6d3e\u773c\u89c1\u62b5\u6321\u4e0d\u4f4f\uff0c\u957f\u987b\u9053\u4eba\u53d1\u51fa\u53f7\u4ee4\uff0c\u4f17\u4eba\u767b\u65f6\u6536\u5251\u540e\u9000\u3002\u4ed9\u90fd\u95e8\u4eba\u5bf9\u7fa4\u6218\u4e60\u7ec3\u6709\u7d20\uff0c\u8c01\u5f53\u5148\uff0c\u8c01\u65ad\u540e\uff0c\u9635\u52bf\u4e95\u7136\u3002\u4f55\u94c1\u624b\u8eab\u4e0a\u53d7\u4f24\uff0c\u53c8\u89c1\u654c\u4eba\u867d\u8d25\u4e0d\u4e71\uff0c\u5012\u4e5f\u4e0d\u6562\u8ffd\u8d76\uff0c\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u6687\u7740\u518d\u6765\u73a9\u513f\uff0c\u5c0f\u59b9\u4e0d\u9001\u5566\u3002\u201d \u3000\u3000\u4ed9\u90fd\u6d3e\u4f17\u4eba\u6765\u5f97\u7a81\u7136\uff0c\u53bb\u5f97\u4e5f\u5feb\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u5200\u5251\u65e0\u58f0\uff0c\u53ea\u5269\u4e0b\u6714\u98ce\u864e\u864e\uff0c\u5439\u5377\u6b8b\u96ea\u3002 \u3000\u3000\u7126\u5b9b\u513f\u4ece\u8f7f\u969c\u5b54\u4e2d\u6084\u6084\u5f20\u671b\uff0c\u89c1\u573a\u4e0a\u4e1c\u4e00\u5806\u897f\u4e00\u5806\u7684\u7ad9\u4e86\u51e0\u5341\u4e2a\u4eba\u3002\u4e00\u4e2a\u8001\u4e5e\u5a46\u6253\u626e\u7684\u5973\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u6d88\u606f\u4e5f\u771f\u7075\u901a\uff0c\u77e5\u9053\u54b1\u4eec\u4eca\u513f\u53d7\u4f24\u7684\u4eba\u591a\uff0c\u5c31\u6765\u63a9\u88ad\u3002\u6559\u4e3b\uff0c\u4f60\u7684\u4f24\u4e0d\u788d\u4e8b\u5427\uff1f\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u8fd8\u597d\u3002\u5e78\u4e8f\u59d1\u59d1\u63f4\u5175\u6765\u5f97\u5feb\uff0c\u5426\u5219\u8981\u6253\u8dd1\u8fd9\u7fa4\u6742\u6bdb\uff0c\u5012\u8fd8\u4e0d\u5927\u5bb9\u6613\u5462\u3002\u201d\u4e00\u4e2a\u767d\u987b\u8001\u4eba\u9053\uff1a\u201c\u4ed9\u90fd\u6d3e\u8ddf\u534e\u5c71\u6d3e\u6709\u52fe\u7ed3\u5417\uff1f\u201d\u4e00\u4e2a\u55d3\u97f3\u5636\u54d1\u7684\u4eba\u9053\uff1a\u201c\u91d1\u9f99\u5e2e\u8ddf\u90a3\u4e2a\u59d3\u8881\u7684\u5c0f\u5b50\u6405\u5728\u4e00\u8d77\u3002\u54b1\u5144\u5f1f\u5df2\u4f7f\u4e86\u501f\u5200\u6740\u4eba\u7684\u79bb\u95f4\u4e4b\u8ba1\uff0c\u6599\u60f3\u59d3\u8881\u7684\u5fc5\u4f1a\u53bb\u8ddf\u4ed9\u90fd\u6d3e\u4e3a\u96be\u3002\u201d\u90a3\u767d\u987b\u8001\u4eba\u9053\uff1a\u201c\u597d\u5427\uff0c\u8ba9\u4ed6\u4eec\u81ea\u76f8\u6b8b\u6740\u6700\u597d\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u5728\u8f7f\u4e0b\u542c\u5230\u201c\u501f\u5200\u6740\u4eba\u7684\u79bb\u95f4\u4e4b\u8ba1\u201d\u8fd9\u51e0\u4e2a\u5b57\uff0c\u8033\u4e2d\u55e1\u7684\u4e00\u54cd\uff0c\u4e00\u8eab\u51b7\u6c57\uff0c\u5fc3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u662f\u4e86\uff0c\u5bb3\u6b7b\u6211\u7239\u7239\u7684\uff0c\u539f\u6765\u662f\u8fd9\u4e09\u4e2a\u5978\u8d3c\u3002\u201d\u5979\u60f3\u518d\u542c\u4e0b\u53bb\uff0c\u5374\u542c\u4f55\u94c1\u624b\u9053\uff1a\u201c\u5927\u4f19\u513f\u8fdb\u5bab\u53bb\u5427\uff0c\u8f7f\u5b50\u53ef\u4e0d\u80fd\u5750\u5566\u3002\u201d\u4f17\u4eba\u4e00\u62e5\u800c\u53bb\u3002 \u3000\u3000\u7126\u5b9b\u513f\u7b49\u4ed6\u4eec\u8d70\u51fa\u6570\u5341\u6b65\u8fdc\uff0c\u6084\u6084\u4ece\u8f7f\u5e95\u94bb\u4e86\u51fa\u6765\u3002\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u5f53\u5730\u7adf\u662f\u5728\u7981\u57ce\u4e4b\u524d\uff0c\u773c\u89c1\u4e00\u4f19\u4eba\u8fdb\u5bab\u53bb\u4e86\u3002\u4ed9\u90fd\u6d3e\u56f4\u653b\u4f55\u94c1\u624b\uff0c\u62da\u6597\u65f6\u523b\u4e0d\u77ed\uff0c\u5c45\u7136\u5e76\u65e0\u5bab\u95e8\u4f8d\u536b\u524d\u6765\u67e5\u95ee\u5e72\u9884\u3002\u5979\u4e0d\u6562\u591a\u803d\uff0c\u5fd9\u56de\u5230\u6b63\u6761\u5b50\u80e1\u540c\uff0c\u5c06\u9002\u624d\u6240\u89c1\u7ec6\u7ec6\u5bf9\u8881\u627f\u5fd7\u8bf4\u4e86\u3002\u8881\u627f\u5fd7\u5927\u62c7\u6307\u4e00\u7ad6\uff0c\u8bf4\u9053\uff1a\u201c\u7126\u59d1\u5a18\uff0c\u597d\u80c6\u7565\uff0c\u597d\u89c1\u8bc6\uff01\u201d \u3000\u3000\u7126\u5b9b\u513f\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u968f\u5373\u62dc\u4e86\u4e0b\u53bb\u3002\u8881\u627f\u5fd7\u4fa7\u8eab\u907f\u8fc7\uff0c\u6168\u7136\u9053\uff1a\u201c\u4ee4\u5c0a\u7684\u8840\u6d77\u6df1\u4ec7\uff0c\u81ea\u5f53\u7740\u843d\u5728\u6211\u8eab\u4e0a\u3002\u7126\u59d1\u5a18\u518d\u884c\u5927\u793c\uff0c\u90a3\u53ef\u662f\u77a7\u4e0d\u8d77\u6211\u4e86\u3002\u201d\u6c89\u541f\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u6211\u8fd9\u5c31\u8fdb\u5bab\u53bb\u627e\u4ed6\u4eec\u3002\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u8fd9\u4e9b\u5978\u8d3c\u4e0d\u77e5\u600e\u6837\uff0c\u7adf\u6df7\u5165\u4e86\u7687\u5bab\u3002\u770b\u6765\u5fc5\u6709\u5185\u5e94\u3002\u5bab\u91cc\u7981\u536b\u68ee\u4e25\uff0c\u8881\u76f8\u516c\u8d38\u7136\u8fdb\u53bb\uff0c\u53ea\u6015\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u59a8\uff0c\u6211\u6709\u4e00\u4ef6\u597d\u4e1c\u897f\u3002\u672c\u6765\u65e9\u5c31\u8981\u7528\uff0c\u54ea\u77e5\u4e00\u5230\u4eac\u5e08\u4e4b\u540e\uff0c\u602a\u4e8b\u5c42\u51fa\u4e0d\u7a77\uff0c\u7adf\u6ca1\u7a7a\u53bb\u3002\u201d\u8bf4\u7740\u53d6\u51fa\u4e00\u5c01\u4e66\u4fe1\uff0c\u4fbf\u662f\u6ee1\u6e05\u777f\u4eb2\u738b\u591a\u5c14\u886e\u5199\u7ed9\u5bab\u91cc\u53f8\u793c\u592a\u76d1\u66f9\u5316\u6df3\u7684\u5bc6\u51fd\uff0c\u672c\u662f\u8981\u6d2a\u80dc\u6d77\u9001\u53bb\u7684\u3002\u8881\u627f\u5fd7\u77e5\u9053\u8fd9\u4fe1\u5fc5\u6709\u540e\u7528\uff0c\u4e00\u76f4\u7559\u5728\u8eab\u8fb9\u3002 \u3000\u3000\u7126\u5b9b\u513f\u559c\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\uff0c\u6211\u968f\u8881\u76f8\u516c\u53bb\uff0c\u626e\u4f5c\u4f60\u7684\u4e66\u7ae5\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u77e5\u5979\u8981\u624b\u5203\u4ec7\u4eba\uff0c\u90a3\u662f\u4e00\u7247\u5b5d\u5fc3\uff0c\u529d\u963b\u4e0d\u5f97\uff0c\u70b9\u5934\u5141\u4e86\u3002 \u3000\u3000\u7126\u5b9b\u513f\u5728\u8f7f\u4e0b\u8eb2\u4e86\u534a\u591c\uff0c\u5f04\u5f97\u6ee1\u8eab\u6ce5\u6c61\uff0c\u5fd9\u5165\u5185\u6d17\u8138\u6362\u8863\uff0c\u88c5\u626e\u5df2\u6bd5\uff0c\u53c8\u662f\u4e2a\u4fca\u4fcf\u7684\u5c0f\u4e66\u7ae5\u3002\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u80fd\u518d\u53eb\u4f60\u7126\u59d1\u5a18\u5566\uff01\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4f60\u5c31\u53eb\u6211\u5b9b\u513f\u5427\uff0c\u522b\u4eba\u8fd8\u5f53\u662f\u751a\u4e48\u676f\u513f\u7897\u513f\u5462\u3002\u201d \u3000\u3000\u6b63\u8981\u51fa\u95e8\uff0c\u5434\u5e73\u4e0e\u7f57\u7acb\u5982\u5306\u5306\u8fdb\u6765\uff0c\u8bf4\u987a\u5929\u5e9c\u5c39\u8859\u95e8\u6212\u5907\u5f88\u4e25\uff0c\u7b49\u4e86\u4e24\u4e2a\u591a\u65f6\u8fb0\uff0c\u76f4\u5230\u6355\u5feb\u6362\u73ed\uff0c\u624d\u628a\u5355\u94c1\u751f\u7684\u5c38\u9996\u4e22\u4e86\u4e0b\u53bb\u3002\u8881\u627f\u5fd7\u70b9\u5934\u9053\uff1a\u201c\u597d\uff01\u201d\u7126\u5b9b\u513f\u8bf4\u8d77\u8981\u968f\u8881\u627f\u5fd7\u5165\u5bab\u5bfb\u5978\uff0c\u4e3a\u7236\u62a5\u4ec7\u3002\u7f57\u7acb\u5982\u5ffd\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u5e08\u59b9\uff0c\u6211\u8ddf\u4f60\u4eec\u4e00\u8d77\u53bb\uff0c\u597d\u4e48\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u773c\u671b\u8881\u627f\u5fd7\uff0c\u542c\u4ed6\u793a\u4e0b\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u6b21\u6df1\u5165\u7981\u5bab\uff0c\u672c\u5df2\u5371\u673a\u56db\u4f0f\uff0c\u52a0\u4e4b\u5c1a\u6709\u4e0d\u5c11\u9ad8\u624b\u5728\u5185\u3002\u8981\u4fdd\u62a4\u7126\u59d1\u5a18\u5468\u5168\u5df2\u751a\u4e0d\u6613\uff0c\u591a\u4e00\u4eba\u66f4\u788d\u624b\u811a\u3002\u201d\u6b63\u8981\u51fa\u53e3\u63a8\u8f9e\uff0c\u5ffd\u89c1\u5434\u5e73\u4f38\u624b\u6697\u626f\u7f57\u7acb\u5982\u8863\u89d2\uff0c\u8fde\u4f7f\u773c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u7f57\u5e08\u5f1f\uff0c\u4f60\u4f24\u81c2\u4e4b\u540e\u8eab\u5b50\u8fd8\u6ca1\u5b8c\u5168\u590d\u539f\uff0c\u8fd8\u662f\u8ba9\u8881\u76f8\u516c\u5e26\u5e08\u59b9\u53bb\u5427\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u4ed6\u4f3c\u4e4e\u6709\u610f\u8981\u6211\u8ddf\u7126\u59d1\u5a18\u5355\u72ec\u76f8\u5904\u3002\u6628\u665a\u6211\u548c\u5979\u53bb\u89c1\u6c34\u4e91\u9053\u4eba\uff0c\u9752\u5e74\u7537\u5973\u6df1\u591c\u51fa\u5916\uff0c\u53ea\u6015\u5df2\u5f15\u8d77\u65c1\u4eba\u7591\u5fc3\u3002\u867d\u7136\u5927\u4e08\u592b\u5149\u660e\u78ca\u843d\uff0c\u4f46\u8fd8\u662f\u907f\u4e00\u4e0b\u5acc\u7591\u7684\u597d\u3002\u201d\u4e8e\u662f\u5bf9\u7f57\u7acb\u5982\u9053\uff1a\u201c\u7f57\u5927\u54e5\u540c\u53bb\uff0c\u6211\u591a\u4e00\u4e2a\u5e2e\u624b\uff0c\u90a3\u518d\u597d\u6ca1\u6709\u3002\u59d4\u5c48\u4f60\u4e00\u4e0b\uff0c\u8bf7\u4e5f\u6362\u4e0a\u7ae5\u4ec6\u6253\u626e\u3002\u201d \u3000\u3000\u7f57\u7acb\u5982\u5927\u559c\uff0c\u5165\u5185\u66f4\u8863\u3002\u5434\u5e73\u8ddf\u7740\u8fdb\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u7f57\u5e08\u5f1f\uff0c\u4f60\u8fd9\u6b21\u505a\u4e86\u50bb\u4e8b\u5566\uff01\u201d\u7f57\u7acb\u5982\u6115\u7136\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u5434\u5e73\u9053\uff1a\u201c\u8881\u76f8\u516c\u5bf9\u54b1\u4eec\u91d1\u9f99\u5e2e\u6069\u5fb7\u5982\u5c71\uff0c\u5e08\u59b9\u5bf9\u4ed6\u663e\u7136\u53c8\u503e\u5fc3\u4e4b\u81f3\u2026\u2026\u201d\u7f57\u7acb\u5982\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u8ba9\u5e08\u59b9\u914d\u2026\u2026\u914d\u7ed9\u8881\u76f8\u516c\uff1f\u201d\u5434\u5e73\u9053\uff1a\u201c\u6069\u5e08\u5728\u5929\u6709\u7075\uff0c\u5b9a\u4e5f\u5fc5\u5341\u5206\u559c\u6b22\u3002\u4f60\u8ddf\u4e86\u53bb\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u7f57\u7acb\u5982\u9053\uff1a\u201c\u5927\u5e08\u54e5\u8bf4\u5f97\u5bf9\uff0c\u90a3\u6211\u4e0d\u53bb\u5566\uff01\u201d\u5434\u5e73\u9053\uff1a\u201c\u73b0\u4eca\u4e0d\u53bb\uff0c\u53c8\u592a\u7740\u75d5\u8ff9\u3002\u4f60\u76f8\u673a\u884c\u4e8b\uff0c\u5982\u80fd\u64ae\u6210\u8fd9\u6bb5\u59fb\u7f18\uff0c\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u7f57\u7acb\u5982\u70b9\u5934\u7b54\u5e94\uff0c\u5fc3\u4e2d\u5374\u662f\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\u3002\u539f\u6765\u4ed6\u5bf9\u8fd9\u5c0f\u5e08\u59b9\u6697\u5bc4\u76f8\u601d\u5df2\u6709\u6570\u5e74\uff0c\u53ea\u662f\u89c1\u5979\u54c1\u8c8c\u65e2\u7f8e\uff0c\u53c8\u4e0d\u82df\u8a00\u7b11\uff0c\u534f\u52a9\u7126\u516c\u793c\u5904\u7406\u5e2e\u4e2d\u4e8b\u52a1\u9887\u5177\u5a01\u4e25\uff0c\u662f\u4ee5\u4e00\u756a\u6df1\u60c5\u4ece\u4e0d\u6562\u5410\u9732\u534a\u70b9\uff1b\u65ad\u81c2\u540e\u66f4\u662f\u81ea\u60ed\u5f62\u79fd\uff0c\u8fde\u8bdd\u4e5f\u4e0d\u6562\u548c\u5979\u591a\u8bf4\u4e00\u53e5\uff0c\u8fd9\u65f6\u542c\u5434\u5e73\u4e00\u8bf4\uff0c\u4e0d\u7981\u6005\u60d8\uff0c\u4f46\u968f\u5373\u8f6c\u5ff5\uff1a\u201c\u8881\u76f8\u516c\u5982\u6b64\u82f1\u96c4\uff0c\u548c\u5e08\u59b9\u6b63\u662f\u4e00\u5bf9\u3002\u5979\u7ec8\u8eab\u6709\u6258\uff0c\u6211\u81ea\u5f53\u4ee3\u5979\u6b22\u559c\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e03\u56de\u3000\u9752\u887f\u5fc3\u4e0a\u610f \u5f69\u7b14\u753b\u4e2d\u4eba",content:(0,t.jsx)(t.Fragment,{children:"\u8881\u627f\u5fd7\u4ece\u94c1\u7bb1\u4e2d\u53d6\u51fa\u8bb8\u591a\u73cd\u5b9d\uff0c\u5305\u4e86\u4e00\u5927\u5305\uff0c\u8981\u7f57\u7acb\u5982\u6367\u5728\u624b\u91cc\u3002 \u3000\u3000\u4e09\u4eba\u6765\u5230\u5bab\u95e8\u3002\u8881\u627f\u5fd7\u5c06\u6697\u8bed\u4e00\u8bf4\uff0c\u5b88\u95e8\u7684\u7981\u519b\u65e9\u5f97\u5230\u66f9\u592a\u76d1\u5631\u5490\uff0c\u5f53\u5373\u5206\u4eba\u5f15\u4e86\u8fdb\u53bb\u3002\u6765\u5230\u4e00\u5ea7\u6bbf\u524d\uff0c\u7981\u519b\u9000\u51fa\uff0c\u53e6\u6709\u5c0f\u592a\u76d1\u63a5\u5f15\u5165\u5185\uff0c\u4e00\u8def\u8fde\u6362\u4e86\u4e09\u540d\u592a\u76d1\u3002\u8881\u627f\u5fd7\u9ed8\u8bb0\u9053\u8def\uff0c\u5fc3\u60f3\u8fd9\u66f9\u592a\u76d1\u4e5f\u771f\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u751f\u6015\u5bc6\u8c0b\u8d25\u9732\uff0c\u8fde\u5e26\u8def\u4eba\u4e5f\u4e0d\u65ad\u6389\u6362\u3002\u6700\u540e\u6cbf\u7740\u5fa1\u82b1\u56ed\u53f3\u4fa7\u5c0f\u8def\uff0c\u5f2f\u5f2f\u66f2\u66f2\u8d70\u4e86\u4e00\u9635\uff0c\u6765\u5230\u4e00\u5ea7\u5c0f\u5c4b\u5b50\u524d\u3002\u5c0f\u592a\u76d1\u8bf7\u4e09\u4eba\u5165\u5185\uff0c\u7aef\u4e0a\u6e05\u8336\u70b9\u5fc3\u3002\u7b49\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u66f9\u592a\u76d1\u59cb\u7ec8\u4e0d\u6765\uff0c\u4e09\u4eba\u4e5f\u4e0d\u8c08\u8bdd\uff0c\u5750\u7740\u67af\u5019\u3002 \u3000\u3000\u76f4\u5230\u5348\u95f4\uff0c\u624d\u8fdb\u6765\u4e00\u540d\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u592a\u76d1\uff0c\u5411\u8881\u627f\u5fd7\u95ee\u4e86\u51e0\u53e5\u6697\u8bed\u3002\u8881\u627f\u5fd7\u7167\u7740\u6d2a\u80dc\u6d77\u6240\u8a00\u7b54\u4e86\uff0c\u90a3\u592a\u76d1\u70b9\u5934\u800c\u51fa\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u90a3\u592a\u76d1\u5f15\u4e86\u4e00\u540d\u80a5\u80a5\u767d\u767d\u7684\u4e2d\u5e74\u592a\u76d1\u5165\u6765\u3002\u8881\u627f\u5fd7\u89c1\u4ed6\u8eab\u7a7f\u9526\u7ee3\uff0c\u6c14\u6d3e\u6781\u5927\uff0c\u5fc3\u60f3\u8fd9\u591a\u534a\u662f\u5bab\u4e2d\u9664\u4e86\u7687\u5e1d\u4e4b\u5916\u3001\u7b2c\u4e00\u6709\u6743\u6709\u52bf\u7684\u53f8\u793c\u592a\u76d1\u66f9\u5316\u6df3\u4e86\uff0c\u679c\u7136\u90a3\u5148\u524d\u8fdb\u6765\u7684\u592a\u76d1\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u66f9\u516c\u516c\u3002\u201d\u8881\u627f\u5fd7\u548c\u7f57\u7acb\u5982\u3001\u7126\u5b9b\u513f\u4e09\u4eba\u8dea\u4e0b\u78d5\u5934\u3002\u66f9\u5316\u6df3\u7b11\u9053\uff1a\u201c\u522b\u591a\u793c\u5566\uff0c\u8bf7\u5750\uff0c\u777f\u738b\u7237\u5b89\u597d\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u738b\u7237\u798f\u4f53\u5b89\u597d\u3002\u738b\u7237\u547d\u5c0f\u4eba\u95ee\u516c\u516c\u597d\u3002\u201d \u3000\u3000\u66f9\u5316\u6df3\u5475\u5475\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\uff0c\u5374\u4e5f\u591a\u627f\u738b\u7237\u60e6\u8bb0\u3002\u6d2a\u8001\u54e5\u8fdc\u9053\u800c\u6765\uff0c\u4e0d\u77e5\u738b\u7237\u6709\u751a\u4e48\u5631\u5490\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u738b\u7237\u8981\u8bf7\u95ee\u516c\u516c\uff0c\u5927\u4e8b\u7b79\u5212\u5f97\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u66f9\u5316\u6df3\u53f9\u9053\uff1a\u201c\u6211\u4eec\u7687\u4e0a\u7684\u6027\u5b50\uff0c\u771f\u662f\u56fa\u6267\u5f97\u8981\u547d\u3002\u6211\u8fdb\u8a00\u4e86\u597d\u51e0\u6b21\uff0c\u7687\u4e0a\u603b\u8bf4\u501f\u5175\u706d\u5bc7\uff0c\u540e\u60a3\u592a\u591a\uff0c\u53ea\u6c42\u4e24\u56fd\u7f62\u5175\uff0c\u7b49\u5927\u660e\u706d\u4e86\u6d41\u5bc7\uff0c\u91cd\u91cd\u916c\u8c22\u777f\u738b\u7237\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e0d\u77e5\u591a\u5c14\u886e\u4e0e\u66f9\u5316\u6df3\u6709\u4f55\u5bc6\u8c0b\u3002\u6d2a\u80dc\u6d77\u5728\u591a\u5c14\u886e\u5c5e\u4e0b\u5730\u4f4d\u751a\u4f4e\uff0c\u4e0d\u80fd\u9884\u95fb\u673a\u5bc6\uff0c\u53ea\u4e0d\u8fc7\u662f\u4f20\u9012\u6d88\u606f\u7684\u4fe1\u4f7f\u800c\u5df2\u3002\u6d2a\u80dc\u6d77\u4e0d\u77e5\uff0c\u8881\u627f\u5fd7\u81ea\u7136\u4e5f\u4e0d\u77e5\u4e86\u3002\u8fd9\u65f6\u542c\u4e86\u66f9\u5316\u6df3\u4e4b\u8a00\uff0c\u4e0d\u7531\u5f97\u5fc3\u91cc\u6026\u6026\u4e71\u8df3\uff0c\u8033\u4e2d\u53ea\u662f\u54cd\u7740\u201c\u501f\u5175\u706d\u5bc7\u201d\u56db\u5b57\uff0c\u5fc3\u60f3\uff1a\u201c\u7687\u5e1d\u4e0d\u80af\u501f\u5175\uff0c\u6ee1\u6d32\u4eba\u5374\u5fc3\u6025\u8981\u501f\uff0c\u663e\u662f\u4e0d\u6000\u597d\u610f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u867d\u9547\u9759\uff0c\u4f46\u8fd9\u4e2a\u5927\u6d88\u606f\u7a81\u5982\u5176\u6765\uff0c\u4e0d\u514d\u8138\u6709\u5f02\u72b6\u3002 \u3000\u3000\u66f9\u5316\u6df3\u4f1a\u9519\u4e86\u610f\uff0c\u8fd8\u9053\u4ed6\u56e0\u6b64\u4e8b\u4e0d\u6210\uff0c\u5fc3\u4e0b\u4e0d\u6ee1\uff0c\u5fd9\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u522b\u6025\uff0c\u4e00\u8ba1\u4e0d\u6210\uff0c\u53e6\u6709\u4e00\u8ba1\u5440\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u66f9\u516c\u516c\u8db3\u667a\u591a\u8c0b\uff0c\u6211\u4eec\u738b\u7237\u8d5e\u4e0d\u7edd\u53e3\uff0c\u5e38\u8bf4\u6709\u66f9\u516c\u516c\u5728\u5bab\u4e2d\u4e3b\u6301\uff0c\u4f55\u6101\u5927\u4e8b\u4e0d\u6210\u3002\u201d\u66f9\u5316\u6df3\u7b11\u800c\u4e0d\u8a00\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u738b\u7237\u6709\u51e0\u4ef6\u8584\u793c\uff0c\u547d\u5c0f\u4eba\u5e26\u6765\uff0c\u8bf7\u516c\u516c\u7b11\u7eb3\u3002\u201d \u3000\u3000\u8bf4\u7740\u5411\u7f57\u7acb\u5982\u4e00\u6307\u3002\u7126\u5b9b\u513f\u63a5\u4e0b\u4ed6\u80cc\u7740\u7684\u5305\u88f9\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u89e3\u4e86\u5f00\u6765\u3002 \u3000\u3000\u5305\u88f9\u4e00\u89e3\u5f00\uff0c\u767b\u65f6\u73e0\u5149\u5b9d\u6c14\uff0c\u6ee1\u5ba4\u751f\u8f89\u3002\u66f9\u5316\u6df3\u4e45\u5728\u5927\u5185\uff0c\u73cd\u5f02\u5b9d\u7269\u4e0d\u77e5\u89c1\u8fc7\u591a\u5c11\uff0c\u5bfb\u5e38\u73e0\u5b9d\u8fd8\u771f\u4e0d\u5728\u4ed6\u773c\u91cc\uff0c\u4f46\u8fd9\u9635\u5b9d\u6c14\u8fe5\u7136\u6709\u5f02\uff0c\u8d70\u8fd1\u4e00\u770b\uff0c\u4e0d\u89c9\u60ca\u5f97\u5446\u4e86\u3002\u539f\u6765\u5305\u88b1\u4e2d\u73cd\u5b9d\u65e0\u6570\uff0c\u5355\u662f\u4e00\u4e32\u4e00\u767e\u9897\u5927\u73e0\u4e32\u6210\u7684\u671d\u73e0\uff0c\u9897\u9897\u7cbe\u5706\uff0c\u4fbf\u5df2\u4e16\u6240\u7f55\u89c1\u3002\u53e6\u6709\u4e00\u5bf9\u7fe1\u7fe0\u72ee\u5b50\uff0c\u524d\u811a\u76d8\u5f04\u7740\u4e00\u4e2a\u706b\u7ea2\u7684\u7ea2\u5b9d\u77f3\u5706\u7403\uff0c\u8fd9\u822c\u6676\u83b9\u78a7\u7eff\u7684\u6210\u5757\u5927\u7684\u7fe1\u7fe0\u56fa\u7136\u4ece\u672a\u89c1\u8fc7\uff0c\u800c\u7ea2\u5b9d\u77f3\u4e4b\u7470\u4e3d\u707f\u70c2\uff0c\u66f4\u662f\u96be\u5f97\u3002\u66f9\u5316\u6df3\u770b\u4e00\u4ef6\uff0c\u8d5e\u4e00\u4ef6\uff0c\u8f6c\u8eab\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u738b\u7237\u600e\u4e48\u8d4f\u4e86\u6211\u8fd9\u8bb8\u591a\u597d\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u8981\u63a2\u542c\u4ed6\u7684\u56fe\u8c0b\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u738b\u7237\u4e5f\u77e5\u7687\u4e0a\u7cbe\u660e\uff0c\u501f\u5175\u706d\u5bc7\u4e4b\u4e8b\u5f88\u4e0d\u597d\u529e\uff0c\u603b\u662f\u8981\u4ef0\u4ed7\u516c\u516c\u7684\u5927\u529b\u3002\u201d\u66f9\u5316\u6df3\u7ed9\u4ed6\u8fd9\u6837\u4e00\u6367\uff0c\u5341\u5206\u5f97\u610f\uff0c\u7b11\u541f\u541f\u7684\u4e00\u6325\u624b\uff0c\u5bf9\u7f57\u7acb\u5982\u548c\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4f60\u4eec\u5230\u5916\u9762\u53bb\u4f11\u606f\u5427\u3002\u201d\u8881\u627f\u5fd7\u5411\u4e8c\u4eba\u70b9\u70b9\u5934\uff0c\u4fbf\u6709\u5c0f\u592a\u76d1\u6765\u966a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u66f9\u5316\u6df3\u4eb2\u81ea\u5173\u4e0a\u4e86\u95e8\uff0c\u63e1\u4f4f\u8881\u627f\u5fd7\u7684\u624b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u738b\u7237\u51fa\u5175\uff0c\u6709\u751a\u4e48\u6761\u6b3e\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u90a3\u665a\u674e\u5ca9\u5927\u54e5\u8bf4\u5230\u5904\u4e8b\u5e94\u53d8\u4e4b\u9053\uff0c\u66fe\u8bf4\u8981\u9a97\u51fa\u65c1\u4eba\u7684\u673a\u5bc6\uff0c\u987b\u5f97\u5148\u8bf4\u4e9b\u673a\u5bc6\u7ed9\u4ed6\u542c\u3002\u6211\u4fe1\u53e3\u80e1\u8bcc\u4e9b\u4fbf\u4e86\u3002\u201d\u8bf4\u9053\uff1a\u201c\u516c\u516c\u662f\u81ea\u5df1\u4eba\uff0c\u8ddf\u4f60\u8bf4\u5f53\u7136\u4e0d\u59a8\uff0c\u4e0d\u8fc7\u8fd9\u4e8b\u53ef\u673a\u5bc6\u4e4b\u81f3\uff0c\u9664\u4e86\u738b\u7237\uff0c\u8fde\u5c0f\u4eba\u5728\u5185\uff0c\u4e5f\u4e0d\u8fc7\u4e24\u4e09\u4e2a\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u66f9\u5316\u6df3\u773c\u775b\u4e00\u4eae\u3002\u8881\u627f\u5fd7\u6328\u8fd1\u8eab\u53bb\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u5fc3\u60f3\uff0c\u738b\u7237\u867d\u7136\u77a7\u5f97\u8d77\u5c0f\u4eba\uff0c\u4f46\u603b\u662f\u756a\u90a6\u5916\u56fd\uff0c\u8981\u662f\u66f9\u516c\u516c\u6069\u52a0\u683d\u57f9\uff0c\u4f7f\u4e2a\u4eba\u5f97\u4ee5\u5149\u7956\u8000\u5b97\u2026\u2026\u201d\u66f9\u5316\u6df3\u5fc3\u4e2d\u4e86\u7136\uff0c\u77e5\u4ed6\u8981\u8ba8\u5b98\u804c\uff0c\u5475\u5475\u7b11\u9053\uff1a\u201c\u6d2a\u8001\u5f1f\u8981\u529f\u540d\u5bcc\u8d35\uff0c\u90a3\u5305\u5728\u8001\u592b\u8eab\u4e0a\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8981\u88c5\u5047\u5c31\u5047\u5230\u5e95\u3002\u201d\u5fd9\u8dea\u4e0b\u53bb\u78d5\u5934\u9053\u8c22\u3002\u66f9\u5316\u6df3\u7b11\u9053\uff1a\u201c\u4e8b\u6210\u4e4b\u540e\uff0c\u59d4\u4f60\u4e00\u4e2a\u526f\u5c06\u5982\u4f55\uff1f\u5305\u4f60\u6d3e\u5728\u6cb9\u6c34\u4e30\u8db3\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u6ee1\u8138\u559c\u8272\uff0c\u5fd9\u53c8\u9053\u8c22\uff0c\u9053\uff1a\u201c\u516c\u516c\u5927\u6069\u5927\u5fb7\uff0c\u5c0f\u4eba\u751a\u4e48\u4e8b\u4e5f\u4e0d\u80fd\u518d\u7792\u516c\u516c\u3002\u738b\u7237\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u5de6\u53f3\u4e00\u5f20\uff0c\u6084\u58f0\u9053\uff1a\u201c\u516c\u516c\u53ef\u5343\u4e07\u4e0d\u80fd\u6cc4\u9732\uff0c\u5426\u5219\u5c0f\u4eba\u6027\u547d\u96be\u4fdd\u3002\u201d\u66f9\u5316\u6df3\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u600e\u4f1a\u8bf4\u51fa\u53bb\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u6ee1\u6d32\u5175\u8fdb\u5173\u4e4b\u540e\uff0c\u95ef\u8d3c\u662f\u4e00\u5b9a\u53ef\u4ee5\u8361\u5e73\u7684\u3002\u738b\u7237\u7684\u5fc3\u610f\uff0c\u662f\u8981\u671d\u5ef7\u5272\u8ba9\u5317\u76f4\u96b6\u548c\u5c71\u4e1c\u4e00\u5e26\u7684\u5730\u65b9\u76f8\u8c22\u3002\u4e24\u56fd\u4ee5\u9ec4\u6cb3\u4e3a\u754c\uff0c\u6c38\u4e3a\u5144\u5f1f\u4e4b\u90a6\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4fe1\u53e3\u80e1\u8bcc\u3002\u66f9\u5316\u6df3\u5374\u6beb\u4e0d\u6000\u7591\uff0c\u4e00\u5219\u6709\u591a\u5c14\u886e\u4eb2\u51fd\u53ca\u6240\u7ea6\u5b9a\u7684\u6697\u53f7\uff0c\u4e8c\u5219\u6709\u5982\u6b64\u91cd\u793c\uff0c\u4e09\u6765\u6ee1\u6d32\u4eba\u5c45\u5fc3\u53f5\u6d4b\uff0c\u4ed6\u53c8\u5c82\u6709\u4e0d\u77e5\uff1f\u4ed6\u5fae\u5fae\u6c89\u541f\uff0c\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u773c\u524d\u5929\u4e0b\u5927\u4e71\uff0c\u4eca\u65e9\u4f20\u6765\u519b\u8baf\uff0c\u6f7c\u5173\u5df2\u7ed9\u95ef\u8d3c\u653b\u7834\uff0c\u5175\u90e8\u5c1a\u4e66\u5b59\u4f20\u5ead\u6b89\u96be\u3002\u5927\u660e\u8fd8\u6709\u751a\u4e48\u5c06\u519b\u80fd\u7528\uff1f\u5927\u6e05\u518d\u4e0d\u51fa\u5175\uff0c\u773c\u89c1\u95ef\u8d3c\u65e6\u5915\u4e4b\u95f4\u5c31\u5175\u4e34\u57ce\u4e0b\u3002\u5317\u4eac\u4e00\u7834\uff0c\u751a\u4e48\u90fd\u5b8c\u86cb\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u8bf4\u95ef\u738b\u5df2\u7834\u6f7c\u5173\uff0c\u6740\u4e86\u773c\u4e0b\u60df\u4e00\u624b\u63e1\u91cd\u5175\u7684\u7763\u5e08\u5b59\u4f20\u5ead\uff0c\u4e0d\u7981\u5927\u559c\uff0c\u4ed6\u6015\u6d41\u9732\u5fc3\u4e2d\u6b22\u60a6\u4e4b\u60c5\uff0c\u5fd9\u4f4e\u4e0b\u4e86\u5934\uff0c\u773c\u671b\u5730\u4e0b\u3002\u66f9\u5316\u6df3\u9053\uff1a\u201c\u6211\u4eca\u665a\u518d\u5411\u7687\u4e0a\u8fdb\u8a00\uff0c\u5982\u4ed6\u4ecd\u662f\u56fa\u6267\u4e0d\u5316\uff0c\u54b1\u4eec\u4ee5\u56fd\u5bb6\u793e\u7a37\u4e3a\u91cd\uff0c\u53ea\u597d\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u4f3c\u4e4e\u5fc3\u4e2d\u6709\u6781\u5927\u7591\u96be\u3002\u8881\u627f\u5fd7\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u53cd\u6fc0\u4e00\u53e5\uff1a\u201c\u4eca\u4e0a\u82f1\u660e\u521a\u6bc5\uff0c\u516c\u516c\u53ef\u5f97\u4e00\u5207\u5c0f\u5fc3\u3002\u201d\u66f9\u5316\u6df3\u9053\uff1a\u201c\u54fc\uff0c\u521a\u662f\u521a\u4e86\uff0c\u6bc5\u5c31\u4e0d\u89c1\u5f97\u3002\u82f1\u660e\u4e24\u5b57\uff0c\u53ef\u5dee\u5f97\u592a\u8fdc\u3002 \u3000\u3000\u5927\u660e\u6c5f\u5c71\u4ea1\u5728\u4ed6\u624b\u91cc\u4e0d\u6253\u7d27\uff0c\u96be\u9053\u54b1\u4eec\u4e5f\u966a\u7740\u4ed6\u4e00\u8d77\u9001\u6b7b\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u53ef\u8bf4\u5f97\u4e0a\u201c\u5927\u9006\u4e0d\u9053\u201d\uff0c\u82e5\u662f\u6cc4\u6f0f\u51fa\u53bb\uff0c\u5df2\u662f\u706d\u65cf\u7684\u7f6a\u540d\uff0c\u4ed6\u7adf\u6beb\u4e0d\u987e\u5fcc\u7684\u8bf4\u4e86\u51fa\u6765\uff0c\u53ef\u89c1\u5bf9\u8881\u627f\u5fd7\u5168\u65e0\u5fcc\u60ee\u4e4b\u610f\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u77e5\u516c\u516c\u6709\u4f55\u826f\u7b56\uff0c\u597d\u6559\u5c0f\u4eba\u653e\u5fc3\u3002\u201d \u3000\u3000\u66f9\u5316\u6df3\u9053\uff1a\u201c\u55ef\uff0c\u5c31\u7b97\u4ee5\u9ec4\u6cb3\u4e3a\u754c\uff0c\u4e5f\u80dc\u8fc7\u6574\u5ea7\u6c5f\u5c71\u90fd\u65ad\u9001\u5728\u6d41\u5bc7\u624b\u91cc\u3002\u7687\u4e0a\u4e0d\u80af\uff0c\u96be\u9053\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u4f4f\u53e3\uff0c\u5475\u5475\u7b11\u9053\uff1a\u201c\u6d2a\u8001\u5f1f\uff0c\u4e09\u65e5\u4e4b\u5185\uff0c\u5fc5\u6709\u597d\u97f3\u62a5\u7ed9\u738b\u7237\u3002\u4f60\u5728\u8fd9\u91cc\u7b49\u7740\u5427\u3002\u201d\u53cc\u638c\u4e00\u51fb\uff0c\u8fdb\u6765\u51e0\u540d\u5c0f\u592a\u76d1\uff0c\u6367\u8d77\u8881\u627f\u5fd7\u6240\u8d60\u7684\u73e0\u5b9d\uff0c\u62e5\u7740\u66f9\u5316\u6df3\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u56db\u540d\u5c0f\u592a\u76d1\u9886\u7740\u8881\u627f\u5fd7\u3001\u7126\u5b9b\u513f\u3001\u7f57\u7acb\u5982\u4e09\u4eba\u5230\u5de6\u8fd1\u5c4b\u4e2d\u5bbf\u6b47\u3002\u665a\u95f4\u5f00\u4e0a\u81b3\u98df\uff0c\u751a\u662f\u4e30\u76db\uff0c\u7528\u8fc7\u996d\u540e\uff0c\u5929\u8272\u5df2\u9ed1\uff0c\u5c0f\u592a\u76d1\u9053\u4e86\u5b89\uff0c\u9000\u51fa\u623f\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u66f9\u592a\u76d1\u6b63\u5728\u7b79\u5212\u4e00\u4e2a\u5927\u5978\u8c0b\uff0c\u4e8b\u60c5\u975e\u540c\u5c0f\u53ef\uff0c\u6211\u8981\u51fa\u53bb\u6253\u63a2\u4e00\u4e0b\u3002\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u540c\u53bb\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u8ddf\u7f57\u5927\u54e5\u7559\u5728\u8fd9\u91cc\uff0c\u8bf4\u4e0d\u5b9a\u90a3\u66f9\u592a\u76d1\u4e0d\u653e\u5fc3\uff0c\u53c8\u4f1a\u5dee\u4eba\u6765\u77a7\u3002\u201d\u7f57\u7acb\u5982\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u4eba\u7559\u7740\u597d\u4e86\uff0c\u8881\u76f8\u516c\u591a\u4e00\u4e2a\u5e2e\u624b\u597d\u4e9b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u7126\u5b9b\u513f\u4e00\u526f\u8dc3\u8dc3\u6b32\u8bd5\u7684\u795e\u60c5\uff0c\u4e0d\u4fbf\u963b\u5979\u610f\u5174\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8d70\u5230\u90bb\u5ba4\uff0c\u53cc\u624b\u4e00\u4f38\uff0c\u5df2\u70b9\u4e86\u4e24\u540d\u5c0f\u592a\u76d1\u7684\u54d1\u7a74\u3002\u53e6\u5916\u4e24\u540d\u592a\u76d1\u4ece\u5e8a\u4e0a\u8df3\u8d77\uff0c\u7741\u5927\u4e86\u773c\u775b\uff0c\u4e0d\u660e\u6240\u4ee5\u3002\u7126\u5b9b\u513f\u62d4\u51fa\u86fe\u7709\u94a2\u523a\uff0c\u6307\u5728\u4e24\u4eba\u80f8\u524d\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u51fa\u4e00\u53e5\u58f0\uff0c\u6559\u4f60\u4eec\u89c1\u9b4f\u5fe0\u8d24\u53bb\uff01\u201d\u8bf4\u7740\u94a2\u523a\u5fae\u5fae\u524d\u4f38\uff0c\u523a\u7834\u4e24\u4eba\u8863\u670d\uff0c\u523a\u5c16\u62b5\u5165\u4e86\u80f8\u524d\u8089\u91cc\u3002\u8881\u627f\u5fd7\u6697\u7b11\uff0c\u5fc3\u60f3\u8fd9\u5f53\u53e3\u5979\u8fd8\u8bf4\u7b11\u8bdd\u3002\u8981\u77e5\u9b4f\u5fe0\u8d24\u662f\u71b9\u5b97\u65f6\u7684\u5978\u6076\u592a\u76d1\uff0c\u8d25\u574f\u5929\u4e0b\uff0c\u8fd9\u65f6\u65e9\u5df2\u4f0f\u8bdb\u3002 \u3000\u3000\u4ed6\u628a\u4e24\u540d\u592a\u76d1\u7684\u8863\u670d\u5265\u4e86\u4e0b\u6765\uff0c\u81ea\u5df1\u6362\u4e0a\u4e86\u3002\u7126\u5b9b\u513f\u5439\u706d\u8721\u70db\uff0c\u6478\u7d22\u7740\u4e5f\u6362\u4e0a\u4e86\u592a\u76d1\u670d\u8272\u3002\u8881\u627f\u5fd7\u628a\u4e00\u540d\u592a\u76d1\u4e5f\u70b9\u4e0a\u4e86\u54d1\u7a74\uff0c\u5de6\u624b\u634f\u4f4f\u53e6\u4e00\u4eba\u7684\u8109\u95e8\uff0c\u62c9\u51fa\u95e8\u6765\uff0c\u559d\u9053\uff1a\u201c\u9886\u6211\u4eec\u53bb\u66f9\u516c\u516c\u90a3\u91cc\u3002\u201d\u90a3\u592a\u76d1\u534a\u8eab\u9165\u9ebb\uff0c\u4e0d\u6562\u591a\u8bf4\uff0c\u4fbf\u5373\u9886\u8def\uff0c\u8f6c\u5f2f\u62b9\u89d2\u7684\u884c\u4e86\u91cc\u8bb8\uff0c\u6765\u5230\u4e00\u5ea7\u5927\u697c\u4e4b\u524d\u3002\u90a3\u5c0f\u592a\u76d1\u9053\uff1a\u201c\u66f9\u516c\u516c\u2026\u2026\u4f4f\u2026\u2026\u4f4f\u5728\u8fd9\u91cc\u3002\u201d\u8881\u627f\u5fd7\u4e0d\u7b49\u4ed6\u8bf4\u7b2c\u4e8c\u53e5\u8bdd\uff0c\u624b\u8098\u8f7b\u8f7b\u649e\u51fa\uff0c\u5df2\u95ed\u4f4f\u4ed6\u80f8\u53e3\u7a74\u9053\uff0c\u5c06\u4ed6\u4e22\u5728\u82b1\u6728\u6df1\u5904\u3002 \u3000\u3000\u4e24\u4eba\u4f0f\u4e0b\u8eab\u5b50\uff0c\u5954\u5230\u697c\u8fb9\u3002\u8881\u627f\u5fd7\u6b63\u8981\u62c9\u7740\u7126\u5b9b\u513f\u8dc3\u4e0a\uff0c\u5ffd\u542c\u8eab\u540e\u811a\u6b65\u58f0\u54cd\uff0c\u4e00\u4eba\u8fdc\u8fdc\u95ee\u9053\uff1a\u201c\u66f9\u516c\u516c\u5728\u697c\u4e0a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u7b54\u9053\uff1a\u201c\u6211\u4e5f\u521a\u6765\uff0c\u662f\u5728\u697c\u4e0a\u5427\u3002\u201d\u56de\u5934\u770b\u65f6\uff0c\u89c1\u6765\u8005\u5171\u6709\u4e94\u4eba\uff0c\u524d\u9762\u4e00\u4eba\u63d0\u7740\u4e00\u76cf\u7ea2\u7eb1\u706f\uff0c\u706f\u5149\u63a9\u6620\u4e0b\u89c1\u90fd\u662f\u592a\u76d1\u3002 \u3000\u3000\u90a3\u63d0\u706f\u7684\u592a\u76d1\u7b11\u9a82\uff1a\u201c\u5c0f\u7334\u513f\u5d3d\u5b50\uff0c\u8bf4\u8bdd\u5c31\u662f\u6015\u62c5\u5e72\u7cfb\u3002\u201d\u8bf4\u7740\u6162\u6162\u8d70\u8fd1\u3002\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e0d\u8ba9\u4ed6\u4eec\u770b\u6e05\u695a\u9762\u8c8c\u3002 \u3000\u3000\u4e94\u540d\u592a\u76d1\u8fdb\u95e8\u65f6\uff0c\u706f\u5149\u5c04\u4e0a\u95e8\u4e0a\u660e\u6643\u6643\u7684\u6731\u6f06\uff0c\u6709\u5982\u955c\u5b50\uff0c\u7167\u51fa\u4e86\u4e94\u4eba\u7684\u76f8\u8c8c\u3002\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u8f7b\u626f\u7126\u5b9b\u513f\u8863\u8896\uff0c\u7b49\u4e94\u4eba\u4e0a\u4e86\u697c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u592a\u767d\u4e09\u82f1\uff01\u201d\u7126\u5b9b\u513f\u5927\u60ca\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6740\u6211\u7238\u7238\u7684\u5978\u8d3c\uff1f\u4ed6\u4eec\u505a\u4e86\u592a\u76d1\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8ddf\u54b1\u4eec\u4e00\u6837\uff0c\u4e54\u88c5\u6539\u626e\u7684\uff0c\u4e0a\u53bb\uff01\u201d\u4e24\u4eba\u7d27\u8ddf\u5728\u592a\u767d\u4e09\u82f1\u4e4b\u540e\uff0c\u4e00\u8def\u4e0a\u697c\uff0c\u5b88\u536b\u7684\u592a\u76d1\u53ea\u9053\u4ed6\u4eec\u662f\u4e00\u8def\uff0c\u4e5f\u4e0d\u67e5\u95ee\u3002\u5230\u5f97\u697c\u4e0a\uff0c\u524d\u9762\u4e24\u540d\u592a\u76d1\u9886\u7740\u592a\u767d\u4e09\u82f1\u8d70\u8fdb\u4e00\u95f4\u623f\u91cc\u53bb\u4e86\u3002\u8881\u627f\u5fd7\u4e0e\u7126\u5b9b\u513f\u4e0d\u4fbf\u518d\u8ddf\uff0c\u5019\u5728\u95e8\u5916\uff0c\u9690\u9690\u7ea6\u7ea6\u53ea\u542c\u5f97\u90a3\u63d0\u706f\u7684\u592a\u76d1\u8bf4\u9053\uff1a\u201c\u8bf7\u5728\u8fd9\u91cc\u2026\u2026\u66f9\u516c\u516c\u9a6c\u4e0a\u2026\u2026\u201d \u3000\u3000\u5176\u4f59\u7684\u8bdd\u542c\u4e0d\u6e05\u695a\u3002\u4e24\u540d\u592a\u76d1\u968f\u5373\u9000\u4e86\u51fa\u6765\uff0c\u4e0b\u697c\u53bb\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u62c9\u7126\u5b9b\u513f\u7684\u624b\uff0c\u8d70\u8fdb\u623f\u53bb\uff0c\u53ea\u89c1\u56db\u58c1\u56fe\u4e66\uff0c\u539f\u6765\u662f\u95f4\u4e66\u623f\u3002\u592a\u767d\u4e09\u82f1\u5750\u5728\u4e00\u65c1\u6905\u5b50\uff0c\u89c1\u8fdb\u6765\u4e24\u540d\u592a\u76d1\uff0c\u4e5f\u4e0d\u5728\u610f\u3002\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u5f84\u81ea\u5411\u524d\u3002\u7126\u5b9b\u513f\u51b7\u7b11\u9053\uff1a\u201c\u53f2\u53d4\u53d4\uff0c\u9ece\u53d4\u53d4\uff0c\u6211\u7239\u7239\u8bf7\u4e09\u4f4d\u53bb\u5403\u996d\u3002\u201d\u592a\u767d\u4e09\u82f1\u9661\u7136\u89c1\u5230\u7126\u5b9b\u513f\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u9ece\u521a\u7acb\u5373\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u7239\u7239\u4e0d\u662f\u6b7b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u8bf7\u4e09\u4f4d\u53d4\u53d4\u53bb\u5403\u996d\uff01\u201d\u53f2\u79c9\u6587\u7709\u5934\u4e00\u76b1\uff0c\u64e6\u7684\u4e00\u58f0\uff0c\u957f\u5200\u51fa\u9798\u3002\u8881\u627f\u5fd7\u4e00\u8dc3\u800c\u51fa\uff0c\u53cc\u624b\u75be\u4f38\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u6293\u4f4f\u53f2\u6c0f\u5144\u5f1f\u7684\u540e\u9886\u63d0\u4e86\u8d77\u6765\uff0c\u540c\u65f6\u5de6\u811a\u98de\u51fa\u3002\u8e22\u5728\u9ece\u521a\u540e\u5fc3\u80db\u9aa8\u4e0b\u4e09\u5bf8\u201c\u51e4\u5c3e\u7a74\u201d\u4e0a\u3002\u53f2\u79c9\u5149\u53cd\u624b\u4e00\u62f3\uff0c\u8881\u627f\u5fd7\u6beb\u4e0d\u7406\u4f1a\uff0c\u4efb\u4ed6\u6253\u5728\u81ea\u5df1\u80f8\u53e3\uff0c\u53cc\u624b\u8f7b\u8f7b\u4e00\u5408\uff0c\u53f2\u6c0f\u5144\u5f1f\u4e24\u5934\u76f8\u78b0\uff0c\u90fd\u649e\u6655\u4e86\u8fc7\u53bb\u3002\u7126\u5b9b\u513f\u8fd8\u6ca1\u770b\u6e05\u695a\u600e\u7684\uff0c\u592a\u767d\u4e09\u82f1\u90fd\u5df2\u4eba\u4e8b\u4e0d\u77e5\u3002\u5979\u62d4\u51fa\u86fe\u7709\u94a2\u523a\uff0c\u731b\u5411\u53f2\u79c9\u5149\u80f8\u53e3\u6233\u53bb\u3002\u8881\u627f\u5fd7\u4f38\u624b\u62ff\u4f4f\u5979\u7684\u624b\u8155\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6709\u4eba\u3002\u201d \u3000\u3000\u53ea\u542c\u697c\u68af\u4e0a\u811a\u6b65\u58f0\u54cd\uff0c\u8881\u627f\u5fd7\u63d0\u8d77\u53f2\u6c0f\u5144\u5f1f\uff0c\u653e\u5728\u4e66\u67b6\u4e4b\u540e\uff0c\u518d\u8f6c\u8eab\u63d0\u4e86\u9ece\u521a\uff0c\u548c\u7126\u5b9b\u513f\u90fd\u8eb2\u5728\u4e66\u67b6\u80cc\u540e\uff0c\u521a\u521a\u85cf\u597d\uff0c\u51e0\u4e2a\u4eba\u8d70\u8fdb\u5ba4\u6765\u3002 \u3000\u3000\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u8bf7\u5404\u4f4d\u5728\u8fd9\u91cc\u7b49\u4e00\u4e0b\uff0c\u66f9\u516c\u516c\u9a6c\u4e0a\u5c31\u6765\u3002\u201d\u4e00\u4e2a\u5a07\u5a9a\u7684\u5973\u5b50\u58f0\u97f3\u9053\uff1a\u201c\u8f9b\u82e6\u4f60\u5566\uff01\u201d\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u542c\u51fa\u662f\u4e94\u6bd2\u6559\u4e3b\u4f55\u94c1\u624b\u7684\u58f0\u97f3\uff0c\u53cc\u624b\u4e92\u76f8\u4e00\u634f\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u53c8\u8fdb\u6765\u51e0\u4eba\uff0c\u4e0e\u4f55\u94c1\u624b\u7b49\u4e92\u9053\u5bd2\u6684\u3002\u8881\u627f\u5fd7\u5bfb\u601d\uff1a\u201c\u8862\u5dde\u77f3\u6881\u6d3e\u7684\u6e29\u6c0f\u56db\u8001\u4e5f\u6765\u4e86\u3002\u539f\u6765\u5b9b\u513f\u6628\u665a\u77a7\u89c1\u7684\u56db\u4e2a\u8001\u5934\u5b50\uff0c\u7adf\u4fbf\u662f\u4ed6\u4eec\uff0c\u602a\u4e0d\u5f97\u4ed9\u90fd\u6d3e\u62b5\u6321\u4e0d\u4f4f\u3002\u4ed6\u4eec\u6765\u5e72\u751a\u4e48\uff1f\u201d\u4f17\u4eba\u5ba2\u5957\u672a\u6bd5\uff0c\u66f9\u5316\u6df3\u548c\u51e0\u540d\u6b66\u6797\u597d\u624b\u5df2\u8d70\u8fdb\u5ba4\u6765\u3002\u53ea\u542c\u66f9\u5316\u6df3\u7ed9\u5404\u4eba\u5f15\u89c1\uff0c\u7adf\u6709\u65b9\u5ca9\u7684\u5415\u4e03\u5148\u751f\u5728\u5185\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6e29\u65b9\u65bd\u5bb3\u6b7b\u9752\u5f1f\u7684\u6bcd\u4eb2\uff0c\u7ed9\u6211\u6253\u4e2d\u7a74\u9053\uff0c\u65e0\u4eba\u76f8\u6551\uff0c\u591a\u534a\u5df2\u6210\u5e9f\u4eba\uff0c\u6e29\u6c0f\u7684\u4e94\u884c\u9635\u662f\u65bd\u5c55\u4e0d\u51fa\u4e86\u3002\u4f46\u52a0\u4e0a\u4e94\u6bd2\u6559\u7684\u9ad8\u624b\u548c\u5176\u4ed6\u4eba\u4f17\uff0c\u6211\u4e00\u4eba\u4e07\u4e07\u62b5\u654c\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u53ea\u542c\u66f9\u5316\u6df3\u9053\uff1a\u201c\u592a\u767d\u4e09\u82f1\u5462\uff1f\u201d\u4e00\u540d\u592a\u76d1\u7b54\u9053\uff1a\u201c\u53f2\u7237\u4ed6\u4eec\u5df2\u6765\u8fc7\u5566\uff0c\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u201d\u66f9\u5316\u6df3\u6d3e\u4eba\u51fa\u53bb\u627e\u5bfb\uff0c\u51e0\u6279\u592a\u76d1\u627e\u4e86\u597d\u4e45\u56de\u6765\uff0c\u90fd\u8bf4\u4e0d\u89c1\u4e09\u4eba\u5f71\u8e2a\u3002\u4f59\u4eba\u6084\u6084\u8bae\u8bba\uff0c\u663e\u7136\u90fd\u4e0d\u8010\u70e6\u4e86\u3002\u66f9\u5316\u6df3\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u7b49\u4e86\uff0c\u4ed6\u4eec\u81ea\u5df1\u5f03\u4e86\u7acb\u529f\u826f\u673a\uff0c\u4e5f\u6028\u4e0d\u5f97\u65c1\u4eba\u3002\u201d\u53ea\u542c\u4f17\u4eba\u632a\u52a8\u6905\u5b50\u4e4b\u58f0\uff0c\u60f3\u662f\u5927\u5bb6\u5750\u8fd1\u4e86\u542c\u4ed6\u8bf4\u8bdd\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u9053\uff1a\u201c\u95ef\u8d3c\u653b\u7834\u6f7c\u5173\uff0c\u5175\u90e8\u5c1a\u4e66\u5b59\u4f20\u5ead\u6b89\u96be\u3002\u201d\u4f17\u4eba\u566b\u54e6\u8fde\u58f0\uff0c\u751a\u662f\u9707\u52a8\u3002\u66f9\u5316\u6df3\u9053\uff1a\u201c\u54b1\u4eec\u5982\u4e0d\u5feb\u60f3\u6cd5\u5b50\uff0c\u8d3c\u5175\u6307\u65e5\u8feb\u8fd1\u4eac\u5e08\u3002\u8981\u662f\u7687\u4e0a\u518d\u4e0d\u501f\u5175\u706d\u5bc7\uff0c\u5927\u660e\u6570\u767e\u5e74\u7684\u57fa\u4e1a\uff0c\u90fd\u8981\u65ad\u9001\u5728\u4ed6\u624b\u91cc\u3002\u54b1\u4eec\u4ee5\u56fd\u5bb6\u4e3a\u91cd\uff0c\u53ea\u5f97\u53e6\u7acb\u660e\u541b\uff0c\u7ef4\u6301\u793e\u7a37\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u90a3\u5c31\u7acb\u8bda\u738b\u7237\u4e86\u3002\u201d\u66f9\u5316\u6df3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4eca\u65e5\u8981\u501f\u91cd\u5404\u4f4d\uff0c\u4e3a\u65b0\u541b\u6548\u52b3\u3002\u4e00\u5207\u5927\u4e8b\uff0c\u6709\u5144\u5f1f\u627f\u5f53\u3002\u7acb\u4e86\u5927\u529f\uff0c\u5374\u662f\u5927\u5bb6\u7684\u3002\u201d\u89c1\u4f17\u4eba\u5e76\u65e0\u5f02\u8bae\uff0c\u5f53\u4e0b\u5206\u6d3e\u804c\u53f8\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u8bf4\u9053\uff1a\u201c\u518d\u8fc7\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6e29\u5bb6\u56db\u4f4d\u8001\u5148\u751f\u5e26\u9886\u5f97\u529b\u5f1f\u5144\uff0c\u5728\u7687\u4e0a\u5bdd\u5bab\u5916\u56db\u5468\u57cb\u4f0f\uff0c\u963b\u62e6\u65c1\u4eba\u5165\u5185\u3002\u4f55\u6559\u4e3b\u7684\u624b\u4e0b\u4f0f\u5728\u4e66\u623f\u5916\u9762\uff0c\u7531\u8bda\u738b\u7237\u5165\u5185\u8fdb\u8c0f\u3002\u201d \u3000\u3000\u5415\u4e03\u5148\u751f\u9053\uff1a\u201c\u5468\u5927\u5c06\u519b\u7edf\u7387\u4eac\u8425\u5175\u9a6c\uff0c\u4ed6\u662f\u5fe0\u4e8e\u4eca\u4e0a\u7684\u5427\uff1f\u8981\u4e0d\u8981\u5148\u9664\u4e86\u53bb\uff0c\u4ee5\u514d\u4e0d\u6d4b\uff1f\u201d\u66f9\u5316\u6df3\u7b11\u9053\uff1a\u201c\u5468\u5927\u5c06\u519b\u8ddf\u5085\u5c1a\u4e66\u90a3\u4e24\u4e2a\u5bb6\u4f19\uff0c\u65e9\u7ed9\u6211\u7565\u65bd\u5c0f\u8ba1\u9664\u53bb\u4e86\u3002\u4f55\u6559\u4e3b\uff0c\u4f60\u8bf4\u7ed9\u4ed6\u542c\u5427\u3002\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u66f9\u516c\u516c\u8981\u62e5\u8bda\u738b\u767b\u57fa\uff0c\u65e9\u77e5\u5468\u5927\u5c06\u519b\u8ddf\u5085\u5c1a\u4e66\u662f\u4e24\u4e2a\u5927\u60a3\uff0c\u56e0\u6b64\u547d\u5c0f\u59b9\u8fde\u65e5\u6d3e\u4eba\u53bb\u6237\u90e8\u5077\u76d7\u5e93\u94f6\u3002\u7687\u5e1d\u7231\u65a4\u65a4\u8ba1\u8f83\uff0c\u6700\u53d7\u4e0d\u4e86\u8fd9\u4e9b\u5c0f\u4e8b\u3002\u4eca\u65e5\u4e0b\u5348\u5df2\u4e0b\u65e8\u628a\u5468\u5085\u4e8c\u4eba\u9769\u804c\u62ff\u95ee\u4e86\u3002\u201d\u4f17\u4eba\u538b\u4f4e\u4e86\u55d3\u5b50\uff0c\u4e00\u9635\u563b\u7b11\uff0c\u90fd\u79f0\u8d5e\u66f9\u5316\u6df3\u795e\u673a\u5999\u7b97\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8fd9\u65f6\u65b9\u624d\u660e\u767d\uff0c\u539f\u6765\u90a3\u4e9b\u7ea2\u8863\u7ae5\u5b50\u5077\u76d7\u5e93\u94f6\uff0c\u4e0d\u662f\u4e3a\u4e86\u94b1\u8d22\uff0c\u5b9e\u662f\u4e00\u4e2a\u901a\u654c\u7978\u56fd\u7684\u5927\u9634\u8c0b\uff0c\u53ef\u53f9\u5d07\u796f\u81ea\u4ee5\u4e3a\u7cbe\u660e\uff0c\u843d\u5165\u5708\u5957\u4e4b\u4e2d\u5c1a\u81ea\u4e0d\u89c9\u3002 \u3000\u3000\u66f9\u5316\u6df3\u9053\uff1a\u201c\u5404\u4f4d\u4e14\u53bb\u4f11\u606f\u4e00\u4f1a\u513f\uff0c\u5f85\u4f1a\u5144\u5f1f\u518d\u6765\u5949\u8bf7\u3002\u201d \u3000\u3000\u5415\u4e03\u5148\u751f\u4e0e\u6e29\u6c0f\u56db\u8001\u7b49\u544a\u8f9e\u4e86\u51fa\u53bb\u3002\u4f55\u94c1\u624b\u7559\u5728\u6700\u540e\uff0c\u5c06\u5230\u95e8\u53e3\u65f6\uff0c\u5ffd\u9053\uff1a\u201c\u592a\u767d\u4e09\u82f1\u4e3a\u751a\u4e48\u4e0d\u6765\uff1f\u83ab\u975e\u662f\u53bb\u5411\u7687\u5e1d\u544a\u5bc6\uff1f\u201d \u3000\u3000\u66f9\u5316\u6df3\u9053\uff1a\u201c\u7a76\u7adf\u4f55\u6559\u4e3b\u5fc3\u601d\u5468\u5bc6\u3002\u8fd9\u4ef6\u4e8b\u54b1\u4eec\u7d22\u6027\u7792\u8fc7\u4e86\u4ed6\u4eec\u3002\u4e0d\u8fc7\u592a\u767d\u4e09\u82f1\u662f\u6ee1\u6e05\u4e5d\u738b\u7684\u5fc3\u8179\uff0c\u6700\u8fd1\u8fd8\u7acb\u4e86\u4e00\u4ef6\u5927\u529f\uff0c\u8981\u8bf4\u80cc\u53db\u4e5d\u738b\uff0c\u90a3\u5012\u51b3\u4e0d\u81f3\u4e8e\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u751a\u4e48\u5927\u529f\uff1f\u201d\u66f9\u5316\u6df3\u9053\uff1a\u201c\u4ed6\u4eec\u76d7\u4e86\u4ed9\u90fd\u6d3e\u4e00\u4e2a\u59d3\u95f5\u7684\u4e00\u67c4\u5315\u9996\uff0c\u53bb\u523a\u6740\u4e86\u91d1\u9f99\u5e2e\u7684\u5e2e\u4e3b\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u6b66\u6797\u4eba\u7269\u52bf\u5fc5\u5927\u76f8\u6b8b\u6740\u3002\u54b1\u4eec\u5c06\u6765\u907f\u53bb\u91d1\u9675\uff0c\u90a3\u5c31\u8212\u670d\u5f97\u591a\u5566\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u65e9\u6709\u4e5d\u6210\u6599\u5b9a\u662f\u592a\u767d\u4e09\u82f1\u5bb3\u5979\u7236\u4eb2\uff0c\u8fd9\u65f6\u66f4\u65e0\u6000\u7591\u3002\u8881\u627f\u5fd7\u6015\u5979\u4f24\u75db\u6c14\u607c\u4e4b\u9645\u53d1\u51fa\u58f0\u54cd\uff0c\u4f55\u94c1\u624b\u8033\u76ee\u7075\u654f\uff0c\u4e00\u70b9\u513f\u7ec6\u5fae\u52a8\u9759\u90fd\u7792\u5979\u4e0d\u8fc7\uff0c\u5fd9\u4f38\u624b\u8f7b\u8f7b\u6309\u4f4f\u7126\u5b9b\u513f\u7684\u5634\u3002 \u3000\u3000\u53ea\u542c\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u516c\u516c\u5728\u5bab\u5ef7\u4e4b\u5185\uff0c\u5bf9\u6c5f\u6e56\u4e0a\u7684\u4e8b\u60c5\u5374\u8fd9\u822c\u6e05\u695a\uff0c\u771f\u662f\u96be\u5f97\u3002\u201d\u66f9\u5316\u6df3\u5e72\u7b11\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u671d\u5ef7\u91cc\u7684\u4e8b\u6211\u89c1\u5f97\u591a\u4e86\uff0c\u54ea\u4e00\u4e2a\u4e0d\u662f\u8d2a\u56fe\u529f\u540d\u5229\u7984\uff0c\u53cd\u590d\u65e0\u5e38\uff1f\u54ea\u4e00\u4e2a\u8bb2\u751a\u4e48\u4ec1\u4e49\u9053\u5fb7\uff1f\u8fd8\u662f\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u8bf4\u4e00\u662f\u4e00\uff0c\u8bf4\u4e8c\u662f\u4e8c\u3002 \u3000\u3000\u5144\u5f1f\u8fd9\u6b21\u56fe\u8c0b\u5927\u4e8b\uff0c\u4e0d\u6562\u8ddf\u671d\u5ef7\u5927\u81e3\u5546\u8bae\uff0c\u5374\u6765\u793c\u8058\u5404\u4f4d\u62d4\u5200\u76f8\u52a9\uff0c\u4fbf\u662f\u8fd9\u4e2a\u9053\u7406\u2026\u2026\u201d\u4e24\u4eba\u8bf4\u7740\u8bdd\u8d70\u51fa\u4e86\u4e66\u623f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77e5\u9053\u4e8b\u5728\u7d27\u6025\uff0c\u53ef\u662f\u8be5\u5f53\u600e\u4e48\u529e\u5374\u6253\u4e0d\u5b9a\u4e3b\u610f\uff0c\u4e00\u65f6\u56fd\u96be\u5bb6\u4ec7\uff0c\u767e\u611f\u4ea4\u96c6\u3002 \u3000\u3000\u7126\u5b9b\u513f\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u5978\u8d3c\u600e\u6837\u5904\u7f6e\uff1f\u5c0f\u59b9\u53ef\u8981\u6740\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u4f46\u4e0d\u8981\u89c1\u8840\uff0c\u4ee5\u514d\u7ed9\u4eba\u53d1\u89c9\u3002\u201d\u6367\u8d77\u53f2\u79c9\u5149\u7684\u8111\u888b\uff0c\u6307\u7740\u4ed6\u4e24\u8fb9\u201c\u592a\u9633\u7a74\u201d\u9053\uff1a\u201c\u4f60\u4f1a\u4f7f\u2018\u949f\u9f13\u9f50\u9e23\u2019\u8fd9\u4e00\u62db\u4e48\uff1f\u201d\u7126\u5b9b\u513f\u70b9\u70b9\u5934\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u62c7\u6307\u8282\u9aa8\u5411\u5916\uff0c\u8fd9\u6837\u63e1\u62f3\uff0c\u5bf9\u5566\uff0c\u53d1\u62db\uff01\u201d\u7126\u5b9b\u513f\u5e94\u58f0\u51fa\u62f3\u3001\u5657\u7684\u4e00\u58f0\uff0c\u53cc\u62f3\u540c\u65f6\u51fb\u5728\u53f2\u79c9\u5149\u4e24\u8fb9\u201c\u592a\u9633\u7a74\u201d\u4e0a\u3002\u53f2\u79c9\u5149\u4e00\u58f0\u6ca1\u54fc\uff0c\u767b\u65f6\u6c14\u7edd\u3002\u5979\u5982\u6cd5\u65bd\u4e3a\uff0c\u53c8\u5c06\u53f2\u79c9\u6587\u548c\u9ece\u521a\u4e24\u4eba\u6253\u6b7b\uff0c\u8fd9\u65f6\u5927\u4ec7\u5f97\u62a5\uff0c\u60f3\u8d77\u7236\u4eb2\uff0c\u4e0d\u7981\u4f0f\u5728\u8881\u627f\u5fd7\u80a9\u5934\u541e\u58f0\u54ed\u6ce3\u3002\u8881\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u51fa\u53bb\uff0c\u77a7\u90a3\u4f55\u94c1\u624b\u5230\u54ea\u91cc\u53bb\u3002\u201d\u7126\u5b9b\u513f\u62ff\u5f97\u8d77\u653e\u5f97\u4e0b\uff0c\u7acb\u65f6\u6536\u6cea\uff0c\u968f\u7740\u8881\u627f\u5fd7\u8d70\u51fa\u4e66\u623f\u3002 \u3000\u3000\u53ea\u89c1\u66f9\u5316\u6df3\u548c\u4f55\u94c1\u624b\u5728\u524d\u9762\u5c94\u9053\u4e0a\u5df2\u7ecf\u5206\u8def\uff0c\u4e24\u540d\u592a\u76d1\u624b\u63d0\u7eb1\u706f\uff0c\u5f15\u7740\u4f55\u94c1\u624b\u4e00\u884c\u4eba\u5411\u897f\u8d70\u53bb\u3002\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u8eab\u7a7f\u592a\u76d1\u670d\u8272\uff0c\u5c31\u662f\u9047\u5230\u4eba\u4e5f\u81ea\u65e0\u59a8\uff0c\u4e8e\u662f\u8fdc\u8fdc\u8ddf\u7740\u4f55\u94c1\u624b\uff0c\u7a7f\u8fc7\u51e0\u5904\u5ead\u9662\uff0c\u671b\u7740\u5979\u8d70\u8fdb\u4e00\u5ea7\u5c4b\u5b50\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u8ddf\u7740\u8fdb\u53bb\uff0c\u4e00\u8fdb\u95e8\uff0c\u4fbf\u542c\u5f97\u4e1c\u53a2\u623f\u4e2d\u6709\u4eba\u5927\u53eb\uff1a\u201c\u4f55\u94c1\u624b\u4f60\u8fd9\u6bd2\u4e2b\u5934\uff0c\u4f60\u8fd8\u4e0d\u653e\u6211\u51fa\u53bb\uff1f\u201d\u58f0\u97f3\u6e05\u8106\uff0c\u5374\u4e0d\u662f\u9752\u9752\u662f\u8c01\uff1f \u3000\u3000\u8881\u627f\u5fd7\u4e00\u542c\u4e4b\u4e0b\uff0c\u60ca\u559c\u4ea4\u96c6\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u522b\u7684\uff0c\u76f4\u95ef\u8fdb\u53bb\uff0c\u53ea\u89c1\u9752\u9752\u5367\u5728\u5e8a\u4e0a\uff0c\u4e24\u540d\u5c0f\u592a\u76d1\u5728\u65c1\u714e\u836f\u6dfb\u9999\u3002\u8881\u627f\u5fd7\u4f38\u624b\u70b9\u4e86\u4e24\u540d\u592a\u76d1\u7684\u7a74\u9053\u3002\u9752\u9752\u65b9\u624d\u8ba4\u51fa\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff01\u201d\u8881\u627f\u5fd7\u8d70\u5230\u5e8a\u8fb9\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7684\u4f24\u600e\u6837\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u8fd8\u597d\uff01\u201d\u89c1\u7126\u5b9b\u513f\u7ad9\u5728\u8881\u627f\u5fd7\u540e\u9762\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u9053\uff1a\u201c\u55ef\uff0c\u590f\u59d1\u5a18\u539f\u6765\u4e5f\u5728\u8fd9\u91cc\uff0c\u90a3\u771f\u597d\u6781\u4e86\u3002\u8881\u76f8\u516c\u6025\u5f97\u751a\u4e48\u4f3c\u7684\u3002\u201d \u3000\u3000\u9752\u9752\u54fc\u4e86\u4e00\u58f0\u6ca1\u56de\u7b54\uff0c\u5ffd\u9053\uff1a\u201c\u90a3\u4f55\u94c1\u624b\u5c31\u4f1a\u8fc7\u6765\u5566\uff0c\u5927\u54e5\uff0c\u4f60\u7ed9\u6211\u597d\u597d\u6253\u5979\u4e00\u987f\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u53e6\u6709\u5978\u8c0b\uff0c\u6211\u8fd8\u662f\u6682\u4e0d\u9732\u9762\u4e3a\u5999\u3002\u201d\u6025\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u773c\u4e0b\u6682\u65f6\u4e0d\u80fd\u8ddf\u5979\u52a8\u624b\u3002\u4f60\u5f15\u5979\u8bf4\u8bdd\uff0c\u95ee\u660e\u767d\u5979\u52ab\u4f60\u5230\u5bab\u91cc\u6765\u5e72\u751a\u4e48\uff1f\u201d\u9752\u9752\u5947\u9053\uff1a\u201c\u751a\u4e48\u5bab\u91cc\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u4e0d\u77e5\u9053\u8fd9\u662f\u7687\u5bab\u3002\u201d\u53ea\u542c\u623f\u5916\u811a\u6b65\u58f0\u8fd1\uff0c\u4e0d\u53ca\u7ec6\u8bf4\uff0c\u63d0\u8d77\u4e24\u540d\u592a\u76d1\u585e\u5165\u6a71\u4e2d\uff0c\u89c1\u56db\u4e0b\u518d\u65e0\u85cf\u8eab\u4e4b\u6240\uff0c\u95e8\u5916\u7684\u4eba\u4fbf\u8981\u8fdb\u6765\uff0c\u53ea\u5f97\u62c9\u4e86\u7126\u5b9b\u513f\u94bb\u5165\u4e86\u5e8a\u5e95\u3002 \u3000\u3000\u9752\u9752\u4e00\u6014\u4e4b\u95f4\uff0c\u4f55\u94c1\u624b\u4e0e\u4f55\u7ea2\u836f\u5df2\u8de8\u8fdb\u95e8\u6765\u3002\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u590f\u516c\u5b50\uff0c\u4f60\u597d\u4e9b\u4e86\u5417\uff1f\u54a6\uff0c\u670d\u4f8d\u4f60\u7684\u4eba\u54ea\u91cc\u53bb\u5566\uff0c\u8fd9\u4e9b\u5bb6\u4f19\u5c31\u77e5\u9053\u5077\u61d2\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u662f\u6211\u53eb\u4ed6\u4eec\u6eda\u51fa\u53bb\u7684\uff0c\u8c01\u8981\u4ed6\u4eec\u670d\u4f8d\uff1f\u201d\u4f55\u94c1\u624b\u4e0d\u4ee5\u4e3a\u5fe4\uff0c\u7b11\u9053\uff1a\u201c\u771f\u662f\u5b69\u5b50\u813e\u6c14\u3002\u201d\u8d70\u8fd1\u836f\u7f50\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u836f\u714e\u597d\u5566\uff01\u201d\u62ff\u8d77\u4e00\u5757\u4e1d\u68c9\u8499\u5728\u4e00\u53ea\u94f6\u7897\u4e0a\uff0c\u7136\u540e\u628a\u836f\u5012\u5728\u7897\u91cc\uff0c\u836f\u6e23\u90fd\u88ab\u4e1d\u68c9\u6ee4\u53bb\u3002\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u8fd9\u836f\u6cbb\u4f24\uff0c\u6700\u662f\u7075\u9a8c\u4e0d\u8fc7\u3002\u4f60\u653e\u5fc3\uff0c\u836f\u91cc\u8981\u662f\u6709\u6bd2\uff0c\u94f6\u7897\u5c31\u4f1a\u53d8\u9ed1\u3002\u201d \u3000\u3000\u9752\u9752\u8d77\u521d\u89c1\u5230\u8881\u627f\u5fd7\uff0c\u672c\u662f\u6ee1\u6000\u6b22\u60a6\uff0c\u4f46\u968f\u5373\u89c1\u5230\u7126\u5b9b\u513f\uff0c\u5df2\u5f88\u6709\u4e9b\u4e0d\u5feb\uff0c\u540e\u6765\u89c1\u4e24\u4eba\u624b\u62c9\u624b\u7684\u8eb2\u5165\u5e8a\u5e95\uff0c\u795e\u6001\u4f3c\u4e4e\u9887\u4e3a\u4eb2\u5bc6\uff0c\u4e00\u65f6\u6ee1\u5fc3\u6124\u6012\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u9b3c\u9b3c\u795f\u795f\u7684\uff0c\u5f53\u6211\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u9b3c\u9b3c\u795f\u795f\u751a\u4e48\u554a\uff1f\u201d \u3000\u3000\u9752\u9752\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u6b3a\u4fae\u6211\uff0c\u6b3a\u4fae\u6211\u8fd9\u6ca1\u7239\u6ca1\u5a18\u7684\u82e6\u547d\u4eba\uff01\u6ca1\u826f\u5fc3\u7684\u77ed\u547d\u9b3c\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u6014\uff1a\u201c\u5979\u5728\u9a82\u8c01\u5440\uff1f\u201d\u7126\u5b9b\u513f\u5973\u5b69\u513f\u5fc3\u601d\u7ec6\u5bc6\uff0c\u65e9\u5df2\u77a7\u51fa\u9752\u9752\u6709\u7591\u5df1\u4e4b\u610f\uff0c\u8fd9\u65f6\u542c\u5979\u6307\u6851\u9a82\u69d0\uff0c\u4e0d\u7531\u5f97\u5341\u5206\u6c14\u82e6\uff0c\u4e0d\u89c9\u8eab\u5b50\u53d1\u98a4\u3002\u8881\u627f\u5fd7\u968f\u5373\u61c2\u5f97\u4e86\u5979\u7684\u5fc3\u610f\uff0c\u82e6\u4e8e\u65e0\u4ece\u89e3\u91ca\uff0c\u53ea\u5f97\u8f7b\u62cd\u5979\u80a9\u8180\uff0c\u793a\u610f\u5b89\u6170\u3002 \u3000\u3000\u4f55\u94c1\u624b\u54ea\u77e5\u5176\u4e2d\u66f2\u6298\uff0c\u7b11\u9053\uff1a\u201c\u522b\u53d1\u813e\u6c14\u5566\uff0c\u5f85\u4f1a\u6211\u5c31\u9001\u4f60\u56de\u5bb6\u3002\u201d\u9752\u9752\u6012\u9053\uff1a\u201c\u8c01\u8981\u4f60\u9001\uff0c\u96be\u9053\u6211\u81ea\u5df1\u5c31\u8ba4\u4e0d\u5f97\u8def\uff1f\u201d \u3000\u3000\u4f55\u94c1\u624b\u53ea\u662f\u5a07\u7b11\u3002 \u3000\u3000\u8001\u4e5e\u5a46\u4f55\u7ea2\u836f\u5ffd\u7136\u9634\u68ee\u68ee\u5730\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u65e2\u843d\u5165\u6211\u4eec\u624b\u91cc\uff0c\u54ea\u80fd\u518d\u8ba9\u4f60\u597d\u597d\u56de\u53bb\uff1f\u4f60\u7239\u7239\u5728\u54ea\u91cc\uff0c\u751f\u4f60\u51fa\u6765\u7684\u90a3\u4e2a\u8d31\u8d27\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9752\u9752\u672c\u5c31\u5728\u5927\u53d1\u813e\u6c14\uff0c\u542c\u5979\u4fae\u8fb1\u81ea\u5df1\u7684\u6bcd\u4eb2\uff0c\u54ea\u91cc\u8fd8\u5fcd\u8010\u5f97\u4f4f\uff0c\u4f38\u624b\u62ff\u8d77\u5e8a\u5934\u5c0f\u51e0\u4e0a\u7684\u90a3\u7897\u836f\uff0c\u5288\u8138\u5411\u5979\u63b7\u53bb\u3002\u4f55\u7ea2\u836f\u4fa7\u8eab\u4e00\u8eb2\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u836f\u7897\u649e\u5728\u5899\u4e0a\uff0c\u4f46\u8138\u4e0a\u8fd8\u662f\u70ed\u8fa3\u8fa3\u7684\u6e85\u4e0a\u4e86\u8bb8\u591a\u836f\u6c41\u3002\u5979\u6012\u58f0\u559d\u9053\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u4f60\u4e0d\u8981\u547d\u4e86\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5728\u5e8a\u5e95\u4e0b\u51dd\u795e\u5bdf\u770b\uff0c\u89c1\u4f55\u7ea2\u836f\u53cc\u8db3\u4e00\u767b\uff0c\u4f5c\u52bf\u8981\u8dc3\u8d77\u6251\u5411\u9752\u9752\uff0c\u4e5f\u5728\u5e8a\u5e95\u84c4\u52bf\u5f85\u53d1\uff0c\u53ea\u5f85\u4f55\u7ea2\u836f\u8dc3\u8fd1\u65bd\u5c55\u6bd2\u624b\uff0c\u7acb\u5373\u5148\u653b\u5979\u4e0b\u76d8\u3002\u5ffd\u5730\u767d\u5f71\u4e00\u6643\uff0c\u4f55\u94c1\u624b\u7684\u53cc\u8db3\u5df2\u62e6\u5728\u4f55\u7ea2\u836f\u4e0e\u5367\u5e8a\u4e4b\u95f4\u3002 \u3000\u3000\u53ea\u542c\u4f55\u94c1\u624b\u8bf4\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u6211\u7b54\u5e94\u4e86\u90a3\u59d3\u8881\u7684\uff0c\u8981\u9001\u8fd9\u5c0f\u5b50\u56de\u53bb\uff0c\u4e0d\u80fd\u5931\u4fe1\u4e8e\u4eba\u3002\u201d\u4f55\u7ea2\u836f\u51b7\u7b11\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u8bb8\u591a\u4eba\u7ed9\u70b9\u4e86\u7a74\uff0c\u975e\u90a3\u59d3\u8881\u7684\u65bd\u6551\u4e0d\u53ef\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u5f04\u6b7b\u8fd9\u5c0f\u5b50\u4fbf\u662f\uff0c\u4f46\u603b\u5f97\u8ba9\u4ed6\u5148\u5403\u70b9\u82e6\u5934\u3002\u5582\uff0c\u59d3\u590f\u7684\u5c0f\u5b50\uff0c\u4f60\u77a7\u6211\u7f8e\u4e0d\u7f8e\uff1f\u201d\u9752\u9752\u5ffd\u5730\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\uff0c\u58f0\u4e2d\u6ee1\u542b\u60ca\u6016\uff0c\u60f3\u662f\u4f55\u7ea2\u836f\u4e11\u6076\u7684\u8138\u4e0a\u66f4\u505a\u51fa\u53ef\u6015\u7684\u795e\u60c5\uff0c\u76f4\u4f38\u5230\u5979\u9762\u524d\u3002 \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u53c8\u4f55\u5fc5\u5413\u4ed6\uff1f\u201d\u8bed\u97f3\u4e2d\u9887\u6709\u4e0d\u60a6\u4e4b\u610f\u3002\u4f55\u7ea2\u836f\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u662f\u4e86\uff0c\u8fd9\u5c0f\u5b50\u751f\u5f97\u4fca\uff0c\u4f60\u62a4\u7740\u4ed6\u4e86\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u751a\u4e48\u8bdd\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u5e74\u8f7b\u59d1\u5a18\u7684\u5fc3\u4e8b\uff0c\u5f53\u6211\u4e0d\u77e5\u9053\u4e48\uff1f\u6211\u81ea\u5df1\u4e5f\u5e74\u8f7b\u8fc7\u7684\u3002\u4f60\u77a7\uff0c\u4f60\u77a7\uff0c\u8fd9\u662f\u4ece\u524d\u7684\u6211\uff01\u201d \u3000\u3000\u53ea\u542c\u4e00\u9635\u7ab8\u7aa3\u4e4b\u58f0\uff0c\u4f3c\u662f\u4ece\u8863\u888b\u91cc\u53d6\u51fa\u4e86\u751a\u4e48\u4e1c\u897f\u3002\u4f55\u94c1\u624b\u4e0e\u9752\u9752\u90fd\u8f7b\u8f7b\u60ca\u547c\u4e00\u58f0\uff1a\u201c\u554a\uff01\u201d\u53c8\u662f\u8be7\u5f02\uff0c\u53c8\u662f\u8d5e\u53f9\u3002\u4f55\u7ea2\u836f\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5f88\u5947\u602a\uff0c\u662f\u4e0d\u662f\uff1f\u54c8\u54c8\uff0c\u54c8\u54c8\uff0c\u4ece\u524d\u6211\u4e5f\u7f8e\u8fc7\u6765\u7684\u5440\uff01\u201d\u7528\u529b\u4e00\u63b7\uff0c\u4e00\u4ef6\u4e1c\u897f\u4e22\u5728\u5730\u4e0b\uff0c\u539f\u6765\u662f\u4e00\u5e45\u753b\u5728\u7c97\u8695\u4e1d\u7ee2\u4e0a\u7684\u8096\u50cf\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4ece\u5e8a\u5e95\u4e0b\u671b\u51fa\u6765\uff0c\u89c1\u90a3\u8096\u50cf\u662f\u4e2a\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5c11\u5973\uff0c\u53cc\u988a\u6655\u7ea2\uff0c\u7a7f\u7740\u6446\u5937\u4eba\u82b1\u82b1\u7eff\u7eff\u7684\u88c5\u675f\uff0c\u5934\u7f20\u767d\u5e03\uff0c\u76f8\u8c8c\u4fca\u7f8e\uff0c\u4f46\u8bf4\u8fd9\u4fbf\u662f\u4f55\u7ea2\u836f\u90a3\u4e11\u8001\u5a46\u5b50\u5f53\u5e74\u7684\u4f20\u795e\u5199\u7167\uff0c\u53ef\u5c31\u96be\u4ee5\u4ee4\u4eba\u76f8\u4fe1\u4e86\u3002 \u3000\u3000\u53ea\u542c\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u6211\u4e3a\u751a\u4e48\u5f04\u5f97\u8fd9\u6837\u4e11\u516b\u602a\u4f3c\u7684\uff1f\u4e3a\u751a\u4e48\uff1f\u4e3a\u751a\u4e48\uff1f\u2026\u2026\u90fd\u662f\u4e3a\u4e86\u4f60\u90a3\u4e27\u5c3d\u4e86\u826f\u5fc3\u7684\u7239\u7239\u54ea\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u54a6\uff0c\u6211\u7239\u7239\u8ddf\u4f60\u6709\u751a\u4e48\u5e72\u7cfb\uff1f\u4ed6\u662f\u597d\u4eba\uff0c\u51b3\u4e0d\u4f1a\u505a\u5bf9\u4e0d\u8d77\u522b\u4eba\u7684\u4e8b\uff01\u201d\u4f55\u7ea2\u836f\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u90a3\u65f6\u8fd8\u6ca1\u51fa\u4e16\uff0c\u600e\u4f1a\u77e5\u9053\uff1f\u8981\u662f\u4ed6\u6709\u826f\u5fc3\uff0c\u6ca1\u5bf9\u6211\u4e0d\u8d77\uff0c\u6211\u600e\u4f1a\u5f04\u6210\u8fd9\u4e2a\u6837\u5b50\uff1f\u600e\u4f1a\u6709\u4f60\u8fd9\u5c0f\u9b3c\u751f\u5230\u4e16\u754c\u4e0a\u6765\uff1f\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u4f60\u8d8a\u8bf4\u8d8a\u5e0c\u5947\u53e4\u602a\u5566\uff01\u4f60\u4eec\u4e94\u6bd2\u6559\u5728\u4e91\u5357\uff0c\u6211\u7239\u7239\u5988\u5988\u662f\u5728\u6d59\u6c5f\u7ed3\u7684\u4eb2\uff0c\u9053\u8def\u76f8\u5dee\u4e86\u5341\u4e07\u516b\u5343\u91cc\uff0c\u8ddf\u4f60\u53c8\u600e\u4e48\u62c9\u626f\u5f97\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u5927\u6012\uff0c\u6325\u62f3\u5411\u5979\u8138\u4e0a\u6253\u53bb\u3002\u4f55\u94c1\u624b\u4f38\u624b\u683c\u5f00\uff0c\u529d\u9053\uff1a\u201c\u59d1\u59d1\u522b\u53d1\u813e\u6c14\uff0c\u6709\u8bdd\u6162\u6162\u8bf4\u3002\u201d\u4f55\u7ea2\u836f\u559d\u9053\uff1a\u201c\u4f60\u7239\u7239\u5c31\u662f\u7ed9\u91d1\u86c7\u90ce\u541b\u6d3b\u6d3b\u6c14\u6b7b\u7684\uff0c\u73b0\u5728\u53cd\u800c\u51fa\u529b\u56de\u62a4\u8fd9\u5c0f\u5b50\uff0c\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u201d\u4f55\u94c1\u624b\u6012\u9053\uff1a\u201c\u8c01\u56de\u62a4\u4ed6\u4e86\uff1f\u4f60\u82e5\u4f24\u4e86\u4ed6\uff0c\u4fbf\u662f\u5bb3\u4e86\u54b1\u4eec\u6559\u91cc\u56db\u5341\u591a\u4eba\u7684\u6027\u547d\u3002\u6211\u89c1\u4f60\u662f\u957f\u8f88\uff0c\u8ba9\u4f60\u4e09\u5206\u3002\u4f46\u5982\u4f60\u72af\u4e86\u6559\u89c4\uff0c\u6211\u53ef\u4e5f\u4e0d\u80fd\u5bb9\u60c5\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u89c1\u5979\u6446\u51fa\u6559\u4e3b\u7684\u8eab\u4efd\uff0c\u6c14\u7130\u987f\u715e\uff0c\u9893\u7136\u5750\u5728\u6905\u4e0a\uff0c\u4e24\u624b\u6367\u5934\uff0c\u8fc7\u4e86\u826f\u4e45\uff0c\u4f4e\u58f0\u95ee\u9752\u9752\u9053\uff1a\u201c\u4f60\u5988\u5988\u5462\uff1f\u4f60\u5988\u5988\u5b9a\u662f\u4e2a\u5343\u5a07\u767e\u5a9a\u7684\u7f8e\u4eba\u513f\u3001\u72d0\u72f8\u7cbe\uff0c\u8fd9\u624d\u5c06\u4f60\u7239\u8ff7\u4f4f\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u5979\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u505a\u8fc7\u8bb8\u591a\u8bb8\u591a\u68a6\uff0c\u68a6\u5230\u4f60\u7684\u5988\u5988\uff0c\u53ef\u662f\u5979\u76f8\u8c8c\u603b\u662f\u6a21\u6a21\u7cca\u7cca\u7684\uff0c\u77a7\u4e0d\u6e05\u695a\u2026\u2026\u6211\u771f\u60f3\u89c1\u89c1\u5979\u2026\u2026\u201d \u3000\u3000\u9752\u9752\u53f9\u9053\uff1a\u201c\u6211\u5988\u6b7b\u4e86\u3002\u201d\u4f55\u7ea2\u836f\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6b7b\u4e86\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u6b7b\u4e86\uff01\u600e\u4e48\u6837\uff1f\u4f60\u5f88\u5f00\u5fc3\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u4f55\u7ea2\u836f\u58f0\u97f3\u51c4\u5389\uff0c\u5c16\u58f0\u9053\uff1a\u201c\u6211\u903c\u95ee\u4ed6\u4f60\u5988\u5988\u4f4f\u5728\u751a\u4e48\u5730\u65b9\uff0c\u4e0d\u7ba1\u600e\u6837\uff0c\u4ed6\u603b\u662f\u4e0d\u80af\u8bf4\uff0c\u539f\u6765\u5df2\u7ecf\u6b7b\u4e86\u3002\u5f53\u771f\u662f\u8001\u5929\u7237\u6ca1\u773c\uff0c\u6211\u8fd9\u4ec7\u662f\u4e0d\u80fd\u62a5\u7684\u4e86\u3002\u8fd9\u6b21\u653e\u4f60\u56de\u53bb\uff0c\u4f60\u8fd9\u5c0f\u5b50\u603b\u6709\u518d\u843d\u5230\u6211\u624b\u91cc\u7684\u65f6\u5019\u2026\u2026\u4f60\u5988\u5988\u662f\u4e0d\u662f\u5f88\u50cf\u4f60\u5440\uff1f\u201d\u9752\u9752\u607c\u5979\u51fa\u8a00\u65e0\u793c\uff0c\u7ffb\u4e86\u4e2a\u8eab\uff0c\u8138\u5411\u91cc\u5e8a\uff0c\u4e0d\u518d\u7406\u4f1a\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u8981\u8ba9\u90a3\u59d3\u8881\u7684\u5148\u6cbb\u597d\u54b1\u4eec\u7684\u4eba\uff0c\u518d\u653e\u8fd9\u5c0f\u5b50\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\uff1f\u201d\u4f55\u7ea2\u836f\u5ffd\u7136\u4fef\u4e0b\u8eab\u6765\uff0c\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u7136\u89c1\u5979\u5e76\u4e0d\u5f80\u5e8a\u5e95\u4e0b\u77a7\uff0c\u53ea\u4f38\u6307\u5728\u5e8a\u524d\u5730\u677f\u4e0a\u753b\u4e86\u51e0\u4e2a\u5b57\u3002\u8881\u627f\u5fd7\u4e00\u770b\uff0c\u89c1\u662f\uff1a\u201c\u4e0b\u4e00\u5e74\u6bd2\u86db\u86ca\u201d\u516d\u5b57\u3002\u4f55\u94c1\u624b\u968f\u5373\u4f38\u811a\u5728\u5730\u677f\u4e0a\u4e00\u62d6\uff0c\u64e6\u53bb\u4e86\u7070\u5c18\u4e2d\u7684\u5b57\u8ff9\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u5c31\u662f\u8fd9\u6837\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5bfb\u601d\uff1a\u201c\u90a3\u662f\u751a\u4e48\u610f\u601d\uff1f\u2026\u55ef\uff0c\u662f\u4e86\uff0c\u5979\u4eec\u5728\u91ca\u653e\u9752\u5f1f\u4e4b\u524d\uff0c\u5148\u7ed9\u5979\u670d\u4e0b\u6bd2\u86db\u86ca\uff0c\u6bd2\u6027\u5728\u4e00\u5e74\u4e4b\u540e\u65b9\u624d\u53d1\u4f5c\uff0c\u90a3\u65f6\u65e0\u836f\u53ef\u89e3\uff0c\u5979\u4eec\u5c31\u7b97\u62a5\u4e86\u4ec7\u3002\u54fc\uff0c\u597d\u72e0\u6bd2\u7684\u4eba\uff0c\u5929\u5e78\u6559\u6211\u6697\u4e2d\u77a7\u89c1\u3002\u8981\u662f\u6211\u4e0d\u5728\u5e8a\u5e95\u2026\u2026\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u51b7\u6c57\u76f4\u5192\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u7ad9\u8d77\u8eab\u6765\u5411\u95e8\u5916\u8d70\u53bb\u3002\u8881\u627f\u5fd7\u89c1\u5979\u53cc\u8db3\u6b63\u8981\u8de8\u51fa\u95e8\u9650\uff0c\u5ffd\u7136\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u56de\u8eab\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u771f\u7684\u542c\u6211\u8bdd\uff1f\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u5f53\u7136\uff0c\u4e0d\u8fc7\u2026\u2026\u4e0d\u8fc7\u54b1\u4eec\u4e0d\u80fd\u5931\u4fe1\u4e8e\u4eba\u554a\u3002\u201d\u4f55\u7ea2\u836f\u6012\u9053\uff1a\u201c\u6211\u65e9\u77e5\u4f60\u770b\u4e2d\u4e86\u4ed6\uff0c\u538b\u6839\u513f\u5c31\u6ca1\u5b58\u5fc3\u7ed9\u4f60\u7239\u7239\u62a5\u4ec7\u3002\u201d\u6c14\u51b2\u51b2\u7684\u56de\u8f6c\uff0c\u5750\u5728\u6905\u4e0a\uff0c\u5ba4\u4e2d\u767b\u65f6\u5bc2\u9759\u65e0\u58f0\u3002\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u66f4\u662f\u4e0d\u6562\u5598\u4e00\u53e3\u5927\u6c14\u3002 \u3000\u3000\u9752\u9752\u5ffd\u5728\u5e8a\u4e0a\u731b\u6376\u4e00\u8bb0\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u51fa\u6765\u4e48\uff0c\u5e72\u751a\u4e48\u5440\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u5927\u60ca\uff0c\u4fbf\u8981\u7a9c\u51fa\uff0c\u8881\u627f\u5fd7\u5fd9\u62c9\u4f4f\u5979\u624b\u81c2\uff0c\u53ea\u542c\u4f55\u94c1\u624b\u67d4\u58f0\u5b89\u6170\u9053\uff1a\u201c\u4f60\u5b89\u5fc3\u7761\u4e00\u4f1a\u513f\uff0c\u5929\u4eae\u4e86\u5c31\u9001\u4f60\u56de\u53bb\u3002\u201d\u9752\u9752\u54fc\u4e86\u4e00\u58f0\uff0c\u63e1\u62f3\u5728\u5e8a\u677f\u4e0a\u84ec\u84ec\u4e71\u6572\uff0c\u7070\u5c18\u7eb7\u7eb7\u843d\u4e0b\u3002\u8881\u627f\u5fd7\u9669\u4e9b\u6253\u51fa\u55b7\u568f\uff0c\u52aa\u529b\u8c03\u5300\u547c\u5438\uff0c\u8fd9\u624d\u5fcd\u4f4f\u3002 \u3000\u3000\u9752\u9752\u5fc3\u60f3\uff1a\u201c\u90a3\u4f55\u94c1\u624b\u548c\u8001\u4e5e\u5a46\u53c8\u6253\u4f60\u4e0d\u8fc7\uff0c\u4f55\u5fc5\u8eb2\u7740\uff1f\u4f60\u4e8c\u4eba\u5728\u5e8a\u5e95\u4e0b\u5230\u5e95\u5728\u5e72\u751a\u4e48\uff1f\u201d\u5979\u54ea\u77e5\u8881\u627f\u5fd7\u5f97\u6089\u5f11\u5e1d\u53e6\u7acb\u7684\u5978\u8c0b\uff0c\u8fd9\u4e8b\u5173\u7cfb\u5230\u56fd\u5bb6\u7684\u5b58\u4ea1\uff0c\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u56e0\u6b64\u575a\u5fcd\u4e0d\u51fa\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u5bf9\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u662f\u6559\u4e3b\uff0c\u6559\u91cc\u5927\u4e8b\u81ea\u662f\u7531\u4f60\u6267\u638c\u3002 \u3000\u3000\u6559\u7956\u7684\u91d1\u94a9\u65e2\u7136\u4f20\u4e86\u7ed9\u4f60\uff0c\u4f60\u4fbf\u6709\u751f\u6740\u5927\u6743\u3002\u53ef\u662f\u6211\u9047\u5230\u7684\u60e8\u4e8b\uff0c\u8fd8\u4e0d\u80fd\u6559\u4f60\u60ca\u5fc3\u4e48\uff1f\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u59d1\u59d1\u9047\u5230\u4e86\u4e00\u4e2a\u8d1f\u5fc3\u6c49\u5b50\uff0c\u5c31\u5f53\u5929\u4e0b\u7537\u4eba\u4e2a\u4e2a\u662f\u8584\u5e78\u90ce\u3002\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u54fc\uff0c\u7537\u4eba\u4e4b\u4e2d\uff0c\u6709\u751a\u4e48\u597d\u4eba\u4e86\uff1f\u4f55\u51b5\u8fd9\u4eba\u662f\u91d1\u86c7\u90ce\u541b\u7684\u513f\u5b50\u554a\uff01\u4f60\u77a7\u4ed6\u8fd9\u6a21\u6837\u513f\uff0c\u8ddf\u90a3\u4e2a\u5bb6\u4f19\u771f\u6ca1\u751a\u4e48\u5206\u522b\uff0c\u8c01\u8bf4\u4ed6\u7684\u5fc3\u53c8\u4f1a\u8ddf\u8001\u5b50\u4e0d\u540c\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4ed6\u7239\u7239\u8ddf\u4ed6\u4e00\u6837\u4fca\u79c0\u4e48\uff1f\u602a\u4e0d\u5f97\u59d1\u59d1\u8fd9\u822c\u503e\u5fc3\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u4f55\u94c1\u624b\u7684\u8bed\u6c14\uff0c\u663e\u7136\u5bf9\u9752\u9752\u9887\u4e3a\u949f\u60c5\uff0c\u8fd9\u4eba\u7edd\u9876\u6b66\u529f\uff0c\u53c8\u662f\u4e00\u6559\u4e4b\u4e3b\uff0c\u7adf\u7136\u4e0d\u8fa8\u7537\u5973\uff0c\u5012\u4e5f\u597d\u7b11\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u662f\u6267\u8ff7\u4e0d\u609f\u7684\u4e86\u3002\u6211\u628a\u6211\u7684\u4e8b\u6e90\u6e90\u672c\u672c\u8bf4\u7ed9\u4f60\u542c\u3002\u662f\u798f\u662f\u7978\uff0c\u7531\u4f60\u81ea\u51b3\u5427\uff01\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u597d\uff0c\u6211\u6700\u7231\u542c\u59d1\u59d1\u8bf4\u6545\u4e8b\u3002\u7ed9\u4ed6\u542c\u53bb\u4e86\u4e0d\u59a8\u4e48\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u8ba9\u4ed6\u77e5\u9053\u4e86\u4ed6\u8001\u5b50\u7684\u574f\u4e8b\uff0c\u6b7b\u4e86\u4e5f\u597d\u7791\u76ee\u3002\u201d\u9752\u9752\u53eb\u9053\uff1a\u201c\u4f60\u778e\u9020\u8c23\u8a00\uff01\u6211\u7239\u7239\u662f\u5927\u82f1\u96c4\u5927\u8c6a\u6770\uff0c\u600e\u4f1a\u505a\u751a\u4e48\u574f\u4e8b\uff1f\u6211\u4e0d\u542c\uff01\u6211\u4e0d\u542c\uff01\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4ed6\u4e0d\u7231\u542c\uff0c\u600e\u4e48\u529e\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u6211\u662f\u8bf4\u7ed9\u4f60\u542c\u3002\u4ed6\u7231\u4e0d\u7231\u542c\uff0c\u7406\u4ed6\u5462\u3002\u201d \u3000\u3000\u9752\u9752\u7528\u88ab\u8499\u4f4f\u4e86\u5934\uff0c\u53ef\u662f\u7ec8\u4e8e\u7981\u4e0d\u4f4f\u597d\u5947\u5fc3\u8d77\uff0c\u62c9\u5f00\u88ab\u5b50\u4e00\u89d2\uff0c\u542c\u4f55\u7ea2\u836f\u53d9\u8ff0\u91d1\u86c7\u90ce\u541b\u5f53\u5e74\u7684\u6545\u4e8b\u3002 \u3000\u3000\u53ea\u542c\u5979\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u4e8c\u5341\u591a\u5e74\u524d\u7684\u4e8b\u4e86\uff0c\u90a3\u65f6\u5019\u6211\u8fd8\u6ca1\u4f60\u73b0\u4eca\u5e74\u7eaa\u5927\u3002\u4f60\u7239\u7239\u521a\u63a5\u4efb\u505a\u6559\u4e3b\uff0c\u4ed6\u6d3e\u6211\u505a\u4e07\u5999\u5c71\u5e84\u7684\u5e84\u4e3b\uff0c\u7ecf\u7ba1\u90a3\u8fb9\u7684\u86c7\u7a9f\u3002\u8fd9\u5929\u95f2\u7740\u65e0\u4e8b\uff0c\u6211\u4e00\u4e2a\u4eba\u5230\u540e\u5c71\u53bb\u6349\u9e1f\u513f\u73a9\u3002\u201d\u4f55\u94c1\u624b\u63d2\u53e3\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u505a\u4e86\u5e84\u4e3b\uff0c\u8fd8\u6349\u9e1f\u513f\u73a9\u5417\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4e86\uff0c\u90a3\u65f6\u5019\u6211\u8fd8\u5e74\u8f7b\u5f97\u5f88\uff0c\u5dee\u4e0d\u591a\u662f\u4e2a\u5c0f\u5b69\u5b50\u3002\u6211\u6349\u5230\u4e24\u53ea\u7fe0\u9e1f\uff0c\u5fc3\u91cc\u5f88\u662f\u9ad8\u5174\u3002\u56de\u6765\u7684\u65f6\u5019\uff0c\u7ecf\u8fc7\u86c7\u7a9f\u65c1\u8fb9\uff0c\u5ffd\u542c\u5f97\u6811\u4e1b\u91cc\u55d6\u55d6\u58f0\u54cd\uff0c\u77e5\u9053\u6709\u86c7\u9003\u8d70\u4e86\uff0c\u5fd9\u9041\u58f0\u8ffd\u8fc7\u53bb\u3002\u679c\u89c1\u4e00\u6761\u4e94\u82b1\u5728\u5411\u5916\u6e38\u8d70\u3002\u6211\u5f88\u5947\u602a\uff0c\u54b1\u4eec\u86c7\u7a9f\u91cc\u7684\u86c7\u517b\u5f97\u5f88\u9a6f\uff0c\u4ece\u6765\u4e0d\u9003\uff0c\u8fd9\u6761\u4e94\u82b1\u5230\u5916\u9762\u53bb\u5e72\u751a\u4e48\uff1f\u6211\u4e5f\u4e0d\u53bb\u6349\u62ff\uff0c\u4e00\u8def\u8ddf\u7740\u3002\u53ea\u89c1\u90a3\u4e94\u82b1\u5230\u4e86\u6811\u4e1b\u540e\u9762\uff0c\u5f84\u5411\u4e00\u4e2a\u4eba\u6e38\u8fc7\u53bb\uff0c\u6211\u62ac\u5934\u4e00\u770b\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u5e72\u751a\u4e48\uff1f\u201d\u4f55\u7ea2\u836f\u54ac\u7259\u5207\u9f7f\u7684\u9053\uff1a\u201c\u90a3\u4fbf\u662f\u524d\u751f\u7684\u51a4\u5b7d\u4e86\u3002\u4ed6\u662f\u6211\u547d\u91cc\u7684\u9b54\u5934\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u662f\u90a3\u91d1\u86c7\u90ce\u541b\u4e48\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4e5f\u4e0d\u5982\u4ed6\u662f\u8c01\uff0c\u53ea\u89c1\u4ed6\u7709\u6e05\u76ee\u79c0\uff0c\u662f\u4e2a\u957f\u5f97\u5f88\u4fca\u7684\u5c11\u5e74\u3002\u624b\u91cc\u62ff\u7740\u4e00\u675f\u70b9\u7740\u706b\u7684\u5f15\u86c7\u9999\u827e\u3002\u539f\u6765\u4e94\u82b1\u662f\u95fb\u5230\u9999\u6c14\uff0c\u7ed9\u4ed6\u5f15\u51fa\u6765\u7684\u3002\u4ed6\u89c1\u4e86\u6211\uff0c\u5411\u6211\u7b11\u4e86\u7b11\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u59d1\u59d1\u90a3\u65f6\u5019\u957f\u5f97\u5f88\u7f8e\uff0c\u4ed6\u4e00\u5b9a\u7740\u4e86\u8ff7\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u5478\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u548c\u4f60\u8bf4\u6b63\u7ecf\u7684\uff0c\u8c01\u8ddf\u4f60\u95f9\u7740\u73a9\uff1f \u3000\u3000\u6211\u5f53\u65f6\u89c1\u4ed6\u662f\u751f\u4eba\uff0c\u6015\u4ed6\u7ed9\u86c7\u54ac\u4e86\uff0c\u5fd9\u9053\uff1a\u2018\u5582\uff0c\u8fd9\u86c7\u6709\u6bd2\u3002 \u3000\u3000\u4f60\u522b\u52a8\uff0c\u6211\u6765\u6349\uff01\u2019\u4ed6\u53c8\u7b11\u4e86\u7b11\uff0c\u4ece\u80cc\u4e0a\u62ff\u4e0b\u4e00\u53ea\u6728\u7bb1\uff0c\u653e\u5728\u5730\u4e0b\uff0c\u7bb1\u5b50\u89d2\u513f\u4e0a\u6709\u6839\u7ec6\u7ef3\u7f1a\u7740\u4e00\u53ea\u6d3b\u86e4\u87c6\uff0c\u4e00\u8df3\u4e00\u8df3\u7684\u3002\u90a3\u4e94\u82b1\u5f53\u7136\u60f3\u53bb\u5403\u86e4\u87c6\u5566\uff0c\u6162\u6162\u7684\u6e38\u4e0a\u4e86\u6728\u7bb1\uff0c\u6b63\u60f3\u4f38\u5934\u53bb\u54ac\uff0c\u90a3\u5c11\u5e74\u4e00\u62c9\u7ef3\u5b50\uff0c\u7bb1\u5b50\u76d6\u7ffb\u4e86\u4e0b\u53bb\u3002\u4e94\u82b1\u4e00\u6ed1\uff0c\u60f3\u7a33\u4f4f\u8eab\u5b50\uff0c\u90a3\u5c11\u5e74\u5de6\u624b\u4e00\u63a2\uff0c\u4e24\u6839\u624b\u6307\u5df2\u94b3\u4f4f\u4e86\u4e94\u82b1\u7684\u5934\u9888\u3002\u6211\u89c1\u4ed6\u624b\u6cd5\u867d\u8ddf\u54b1\u4eec\u4e0d\u540c\uff0c\u4f46\u624b\u6307\u6240\u94b3\u7684\u90e8\u4f4d\u4e0d\u5dee\u5206\u6beb\uff0c\u4e94\u82b1\u670d\u670d\u5e16\u5e16\u7684\u52a8\u5f39\u4e0d\u5f97\uff0c\u8fd9\u4e00\u6765\uff0c\u77e5\u9053\u4ed6\u662f\u884c\u5bb6\uff0c\u5c31\u653e\u4e86\u5fc3\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u5567\u5567\u5567\uff0c\u59d1\u59d1\u521a\u89c1\u4e86\u4eba\u5bb6\u7684\u9762\uff0c\u5c31\u8fd9\u6837\u5173\u5fc3\u3002\u201d \u3000\u3000\u9752\u9752\u63d2\u53e3\u9053\uff1a\u201c\u5582\uff0c\u4f60\u522b\u6253\u5c94\u6210\u4e0d\u6210\uff1f\u542c\u5979\u8bf4\u5440\u3002\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u7231\u542c\u5440\uff01\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u5ffd\u7136\u7231\u542c\u4e86\uff0c\u53ef\u4e0d\u53ef\u4ee5\uff1f\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u4e0d\u6253\u5c94\u5566\uff01\u201d \u3000\u3000\u4f55\u7ea2\u836f\u6a2a\u4e86\u5979\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u6211\u53c8\u8d77\u4e86\u7591\u5fc3\uff0c\u8fd9\u4eba\u662f\u8c01\u5462\uff1f\u600e\u6562\u8fd9\u751f\u5927\u80c6\uff1f\u5230\u8fd9\u91cc\u6765\u6349\u6211\u4eec\u7684\u86c7\uff1f\u96be\u9053\u4e0d\u77e5\u4e94\u6bd2\u6559\u7684\u5a01\u540d\u5417\uff1f\u53c8\u89c1\u4ed6\u53f3\u624b\u62ff\u51fa\u4e00\u6839\u77ed\u77ed\u7684\u94c1\u68d2\uff0c\u4f38\u5230\u4e94\u82b1\u53e3\u8fb9\u3002\u4e94\u82b1\u4fbf\u4e00\u53e3\u54ac\u4f4f\u3002\u6211\u8d70\u8fd1\u7ec6\u770b\uff0c\u539f\u6765\u94c1\u68d2\u4e2d\u95f4\u662f\u7a7a\u7684\uff0c\u4e94\u82b1\u53e3\u91cc\u7684\u6bd2\u6db2\u4e0d\u4f4f\u6d41\u51fa\u6765\uff0c\u90fd\u7ed9\u94c1\u7ba1\u5b50\u76db\u4f4f\u4e86\u3002\u6211\u8fd9\u624d\u77e5\u9053\uff0c\u54fc\uff0c\u539f\u6765\u4ed6\u662f\u5077\u86c7\u6bd2\u6765\u7740\u3002\u602a\u4e0d\u5f97\u8fd9\u51e0\u5929\u6765\uff0c\u86c7\u7a9f\u91cc\u8bb8\u591a\u86c7\u513f\u4e0d\u5403\u4e1c\u897f\uff0c\u53c8\u7626\u53c8\u61d2\u3002\u6211\u53eb\u4e86\u8d77\u6765\uff1a\u2018\u5582\uff0c\u5feb\u653e\u4e0b\uff01\u2019\u540c\u65f6\u53d6\u51fa\u4f0f\u86c7\u7ba1\u6765\uff0c\u5618\u6e9c\u6e9c\u7684\u4e00\u5439\u3002\u4ed6\u542c\u5f97\u58f0\u97f3\u53e4\u602a\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u90a3\u4e94\u82b1\u5934\u9888\u4e00\u626d\uff0c\u5c31\u5728\u4ed6\u624b\u6307\u4e0a\u54ac\u4e86\u4e00\u53e3\u3002\u4ed6\u5fd9\u628a\u4e94\u82b1\u4e22\u5f00\uff0c\u60f3\u6253\u5f00\u6728\u7bb1\u62ff\u89e3\u836f\u3002\u6211\u8bf4\uff1a\u2018\u4f60\u597d\u5927\u80c6\u5b50\uff01\u2019\uff0c\u62a2\u4e0a\u524d\u53bb\u3002\u54ea\u77e5\u4ed6\u6b66\u529f\u597d\u5f97\u51fa\u5947\uff0c\u53ea\u8f7b\u8f7b\u4e00\u5e26\uff0c\u6211\u5c31\u6454\u4e86\u4e00\u4ea4\u2026\u2026\u201d\u9752\u9752\u63d2\u5634\u9053\uff1a\u201c\u5f53\u7136\u5566\uff0c\u4f60\u600e\u80fd\u662f\u4ed6\u5bf9\u624b\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u767d\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u7684\u4e94\u82b1\u6bd2\u6027\u4f55\u7b49\u5389\u5bb3\uff0c\u4ed6\u6765\u4e0d\u53ca\u53d6\u89e3\u836f\uff0c\u4fbf\u5df2\u4f24\u53e3\u6bd2\u53d1\uff0c\u660f\u4e86\u8fc7\u53bb\u3002\u6211\u8d70\u8fd1\u53bb\u770b\uff0c\u5ffd\u7136\u5fc3\u91cc\u4e0d\u5fcd\u8d77\u6765\uff0c\u5fc3\u60f3\u8fd9\u822c\u5e74\u7eaa\u8f7b\u8f7b\u7684\u4fbf\u9001\u4e86\u6027\u547d\uff0c\u592a\u53ef\u60dc\u4e86\uff0c\u800c\u4e14\u53c8\u662f\u8fd9\u4e48\u4e00\u8eab\u6b66\u529f\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4e8e\u662f\u4f60\u5c31\u5c06\u4ed6\u6551\u4e86\u56de\u53bb\uff0c\u628a\u4ed6\u5077\u5077\u7684\u85cf\u7740\uff0c\u62ff\u836f\u7ed9\u4ed6\u89e3\u4e86\u6bd2\uff0c\u7b49\u4ed6\u4f24\u597d\uff0c\u4f60\u5c31\u7231\u4e0a\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u53f9\u9053\uff1a\u201c\u4e0d\u7b49\u4ed6\u4f24\u597d\uff0c\u6211\u5df2\u7ecf\u628a\u5fc3\u8bb8\u7ed9\u4ed6\u4e86\u3002\u90a3\u65f6\u6559\u91cc\u7684\u5e08\u5144\u5f1f\u4eec\u4e2a\u4e2a\u5bf9\u6211\u597d\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u6211\u90fd\u6ca1\u628a\u4ed6\u4eec\u77a7\u5728\u773c\u91cc\uff0c\u5bf9\u8fd9\u4eba\u5374\u662f\u795e\u9b42\u98a0\u5012\uff0c\u4e0d\u7531\u81ea\u4e3b\u3002\u8fc7\u4e86\u4e09\u5929\uff0c\u90a3\u4eba\u8eab\u4e0a\u7684\u6bd2\u9000\u4e86\uff0c\u6211\u95ee\u4ed6\u5230\u8fd9\u91cc\u6765\u5e72\u751a\u4e48\u3002\u4ed6\u8bf4\u6211\u6551\u4e86\u4ed6\u6027\u547d\uff0c\u751a\u4e48\u4e8b\u4e5f\u4e0d\u80fd\u7792\u6211\u3002\u4ed6\u8bf4\u4ed6\u59d3\u590f\uff0c\u8eab\u4e0a\u8d1f\u4e86\u8840\u6d77\u6df1\u4ec7\uff0c\u5bf9\u5934\u529f\u592b\u65e2\u5f3a\uff0c\u53c8\u662f\u4eba\u591a\u52bf\u4f17\uff0c\u62a5\u4ec7\u6ca1\u628a\u63e1\uff0c\u542c\u8bf4\u4e94\u6bd2\u6559\u7cbe\u7814\u6bd2\u836f\uff0c\u5929\u4e0b\u9996\u5c48\u4e00\u6307\uff0c\u56e0\u6b64\u8d76\u5230\u4e91\u5357\u6765\uff0c\u60f3\u6c42\u6559\u4e94\u6bd2\u6559\u7684\u529f\u592b\u2026\u2026\u201d \u3000\u3000\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u8881\u627f\u5fd7\u548c\u9752\u9752\u65b9\u624d\u660e\u767d\uff0c\u539f\u6765\u91d1\u86c7\u90ce\u541b\u548c\u4e94\u6bd2\u6559\u662f\u5982\u6b64\u8fd9\u822c\u624d\u6253\u8d77\u4ea4\u9053\u6765\u7684\uff0c\u800c\u4ed6\u6240\u4ee5\u8981\u53d6\u6bd2\u836f\uff0c\u81ea\u7136\u65e8\u5728\u5bf9\u4ed8\u77f3\u6881\u6e29\u5bb6\u3002 \u3000\u3000\u53ea\u542c\u4f55\u7ea2\u836f\u53c8\u9053\uff1a\u201c\u4ed6\u8bf4\uff0c\u4ed6\u6697\u91cc\u7aa5\u63a2\u4e86\u8bb8\u4e45\uff0c\u5b66\u5230\u4e86\u4e9b\u70bc\u5236\u6bd2\u836f\u7684\u95e8\u9053\uff0c\u4fbf\u6765\u5077\u6211\u4eec\u86c7\u7a9f\u91cc\u6bd2\u86c7\u7684\u6bd2\u6db2\uff0c\u8981\u70bc\u5728\u6697\u5668\u4e0a\u53bb\u5bf9\u4ed8\u4ec7\u4eba\u3002\u53c8\u8fc7\u4e86\u4e24\u5929\uff0c\u4ed6\u4f24\u52bf\u6162\u6162\u597d\u4e86\uff0c\u8c22\u4e86\u6211\u8981\u8d70\u3002\u6211\u5fc3\u91cc\u5f88\u820d\u4e0d\u5f97\uff0c\u62ff\u4e86\u4e24\u5927\u74f6\u6bd2\u86c7\u7684\u6bd2\u6db2\u7ed9\u4ed6\u3002\u4ed6\u5c31\u7ed9\u6211\u753b\u4e86\u8fd9\u5e45\u8096\u50cf\u3002\u6211\u95ee\u4ed6\u62a5\u4ec7\u7684\u4e8b\u8fd8\u6709\u751a\u4e48\u4e3a\u96be\uff0c\u8981\u4e0d\u8981\u6211\u5e2e\u4ed6\u3002\u4ed6\u7b11\u7b11\uff0c\u8bf4\u6211\u529f\u592b\u8fd8\u5dee\u5f97\u8fdc\uff0c\u5e2e\u4e0d\u4e86\u5fd9\u3002\u6211\u53eb\u4ed6\u62a5\u4e86\u4ec7\u4e4b\u540e\u518d\u6765\u770b\u6211\uff0c\u4ed6\u70b9\u5934\u7b54\u5e94\u4e86\u3002\u6211\u95ee\u4ed6\u751a\u4e48\u65f6\u5019\u6765\u3002\u4ed6\u8bf4\u90a3\u5c31\u96be\u8bf4\u4e86\uff0c\u4ed6\u8981\u62a5\u5927\u4ec7\uff0c\u8fd8\u5c11\u4e86\u4e00\u4ef6\u5229\u5203\uff0c\u542c\u8bf4\u5ce8\u5d4b\u6d3e\u6709\u4e00\u67c4\u9547\u5c71\u4e4b\u5b9d\u7684\u5b9d\u5251\uff0c\u987b\u5f97\u5148\u5230\u56db\u5ddd\u5ce8\u5d4b\u5c71\u53bb\u76d7\u5251\u3002\u4f46\u4e0d\u77e5\u662f\u5426\u771f\u6709\u6b64\u5251\uff0c\u5c31\u7b97\u6709\uff0c\u751a\u4e48\u65f6\u5019\u80fd\u76d7\u5230\uff0c\u4e5f\u8bf4\u4e0d\u4e0a\u6765\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u60f3\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u505a\u4e8b\u5f53\u771f\u4e0d\u987e\u4e00\u5207\uff0c\u4e3a\u4e86\u62a5\u4ec7\uff0c\u751a\u4e48\u4e8b\u90fd\u5e72\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u53f9\u9053\uff1a\u201c\u90a3\u65f6\u5019\u6211\u8ff7\u8ff7\u7cca\u7cca\u7684\uff0c\u53ea\u60f3\u8981\u4ed6\u591a\u966a\u6211\u4e9b\u65e5\u5b50\u3002\u6211\u597d\u4f3c\u53d1\u4e86\u75af\uff0c\u751a\u4e48\u4e8b\u90fd\u4e0d\u6015\uff0c\u660e\u77e5\u662f\u6700\u4e0d\u8be5\u7684\u4e8b\uff0c\u5374\u5fcd\u4e0d\u4f4f\u8981\u53bb\u505a\u3002\u6211\u89c9\u5f97\u4e3a\u4e86\u4ed6\u800c\u53bb\u5192\u9669\uff0c\u8d8a\u662f\u5371\u9669\uff0c\u5fc3\u91cc\u8d8a\u5feb\u6d3b\uff0c\u5c31\u662f\u4e3a\u4ed6\u6b7b\u4e86\uff0c\u4e5f\u662f\u60c5\u613f\u7684\u3002\u5509\uff0c\u90a3\u65f6\u5019\u6211\u771f\u50cf\u7ed9\u9b3c\u8ff7\u4f4f\u4e86\u4e00\u6837\u3002\u6211\u5bf9\u4ed6\u8bf4\uff0c\u6211\u77e5\u9053\u6709\u4e00\u67c4\u5b9d\u5251\uff0c\u950b\u5229\u65e0\u6bd4\uff0c\u751a\u4e48\u5175\u5668\u78b0\u5230\u4e86\u7acb\u523b\u5c31\u65ad\u3002\u4ed6\u6b22\u559c\u5f97\u8df3\u8d77\u6765\uff0c\u5fd9\u95ee\u5728\u751a\u4e48\u5730\u65b9\u3002 \u3000\u3000\u6211\u8bf4\uff0c\u90a3\u5c31\u662f\u6211\u4eec\u4e94\u6bd2\u6559\u4ee3\u4ee3\u76f8\u4f20\u7684\u91d1\u86c7\u5251\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u4e0d\u7531\u5f97\u4f38\u624b\u4e00\u6478\u8d34\u8eab\u85cf\u7740\u7684\u91d1\u86c7\u5251\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u8fd9\u5251\u7adf\u662f\u4e94\u6bd2\u6559\u7684\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u7eed\u9053\uff1a\u201c\u6211\u5bf9\u4ed6\u8bf4\uff0c\u8fd9\u5251\u662f\u6211\u4eec\u6559\u91cc\u7684\u4e09\u5b9d\u4e4b\u4e00\uff0c\u85cf\u5728\u5927\u7406\u53bf\u7075\u86c7\u5c71\u7684\u6bd2\u9f99\u6d1e\u91cc\uff0c\u90a3\u662f\u6211\u6559\u4e94\u5927\u5206\u8235\u4e4b\u4e00\u7684\u6240\u5728\uff0c\u6d1e\u5916\u628a\u5b88\u5f97\u751a\u662f\u4e25\u5bc6\u3002\u4ed6\u6c42\u6211\u9886\u4ed6\u53bb\u5077\u51fa\u6765\u3002\u4ed6\u8bf4\u53ea\u501f\u7528\u4e00\u4e0b\uff0c\u62a5\u4e86\u5927\u4ec7\u4e4b\u540e\u4e00\u5b9a\u5f52\u8fd8\u3002\u4ed6\u4e0d\u65ad\u7684\u76f8\u6c42\uff0c\u6211\u5fc3\u80a0\u8f6f\u4e86\uff0c\u4e8e\u662f\u53bb\u5077\u4e86\u54e5\u54e5\u7684\u4ee4\u724c\uff0c\u5e26\u4ed6\u5230\u6bd2\u9f99\u6d1e\u53bb\u3002\u770b\u5b88\u7684\u4eba\u89c1\u5230\u4ee4\u724c\uff0c\u53c8\u89c1\u6211\u5e26\u7740\u4ed6\uff0c\u4fbf\u653e\u6211\u4eec\u8fdb\u53bb\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u96be\u9053\u6562\u7a7f\u4e86\u8863\u670d\u8fdb\u6bd2\u9f99\u6d1e\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u6211\u81ea\u7136\u4e0d\u6562\u2026\u2026\u201d\u9752\u9752\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u4e3a\u751a\u4e48\u4e0d\u6562\u7a7f\u4e86\u8863\u670d\u8fdb\u90a3\u4e2a\u2026\u2026\u90a3\u4e2a\u6bd2\u9f99\u6d1e\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u54fc\u4e86\u4e00\u58f0\u4e0d\u7b54\u3002\u4f55\u94c1\u624b\u9053\uff1a\u201c\u590f\u516c\u5b50\uff0c\u90a3\u6bd2\u9f99\u6d1e\u91cc\u517b\u7740\u6210\u5343\u6210\u4e07\u6761\u9e64\u9876\u6bd2\u86c7\uff0c\u8fdb\u6d1e\u4e4b\u4eba\u53ea\u8981\u8eab\u4e0a\u6709\u4e00\u5904\u86c7\u836f\u4e0d\u62b9\u5230\uff0c\u7ed9\u9e64\u9876\u86c7\u54ac\u4e0a\u4e00\u53e3\uff0c\u5982\u4f55\u5f97\u4e86\uff1f\u8fd9\u4e9b\u6bd2\u86c7\u5f02\u79cd\u5f02\u8d28\uff0c\u54ac\u4e0a\u4e86\u4e09\u6b65\u6bd9\u547d\uff0c\u6700\u662f\u5389\u5bb3\u4e0d\u8fc7\u3002\u56e0\u6b64\u8fdb\u6d1e\u4e4b\u4eba\u5fc5\u987b\u8131\u53bb\u8863\u886b\uff0c\u5168\u8eab\u62b9\u4e0a\u86c7\u836f\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4eec\u4e94\u6bd2\u6559\u7684\u4e8b\u5f53\u771f\u2026\u2026\u5f53\u771f\u2026\u2026\u201d \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u5f53\u771f\u751a\u4e48\uff1f\u82e5\u4e0d\u662f\u8fd9\u6837\uff0c\u53c8\u600e\u8fdb\u5f97\u6bd2\u9f99\u6d1e\uff1f \u3000\u3000\u4e8e\u662f\u6211\u8131\u53bb\u8863\u670d\uff0c\u5168\u8eab\u62b9\u4e0a\u86c7\u836f\uff0c\u53eb\u4ed6\u4e5f\u643d\u86c7\u836f\u3002\u4ed6\u80cc\u4e0a\u64e6\u4e0d\u5230\u5904\uff0c\u6211\u5e2e\u4ed6\u643d\u62b9\u3002\u5509\uff0c\u4e24\u4e2a\u5c11\u5e74\u7537\u5973\uff0c\u8eab\u4e0a\u6ca1\u4e86\u8863\u670d\uff0c\u5728\u5c71\u6d1e\u4e2d\u4f60\u5e2e\u6211\u643d\u836f\uff0c\u6211\u5e2e\u4f60\u643d\u836f\uff0c\u6700\u540e\u8fd8\u6709\u751a\u4e48\u597d\u4e8b\u505a\u51fa\u6765\uff1f \u3000\u3000\u4f55\u51b5\u6211\u65e9\u5df2\u5bf9\u4ed6\u503e\u5fc3\uff0c\u5c31\u8fd9\u4e48\u80e1\u91cc\u80e1\u6d82\u7684\u628a\u8eab\u5b50\u4ea4\u4e86\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u9752\u9752\u542c\u5f97\u53cc\u988a\u5982\u706b\uff0c\u5ffd\u5730\u60f3\u8d77\u5e8a\u5e95\u4e0b\u7684\u4e8c\u4eba\uff0c\u5f53\u5373\u624b\u811a\u5728\u5e8a\u677f\u4e0a\u4e71\u6376\u4e71\u6253\u3002\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u590f\u516c\u5b50\uff0c\u4f60\u5e72\u751a\u4e48\uff1f\u201d\u9752\u9752\u6012\u9053\uff1a\u201c\u6211\u6068\u4ed6\u4eec\u597d\u4e0d\u6015\u4e11\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u4e0d\u6015\u4e11\uff0c\u90a3\u4e5f\u4e0d\u9519\uff0c\u6211\u4eec\u5937\u5bb6\u5973\u5b50\uff0c\u672c\u6765\u6ca1\u4f60\u4eec\u6c49\u4eba\u8fd9\u8bb8\u591a\u81ed\u89c4\u77e9\u3002\u5509\uff0c\u540e\u6765\u6211\u5c31\u63a8\u5f00\u5185\u6d1e\u77f3\u95e8\uff0c\u5e26\u4e86\u4ed6\u8fdb\u53bb\u3002\u8fd9\u91d1\u86c7\u5251\u548c\u5176\u4f59\u4e24\u5b9d\u653e\u5728\u77f3\u9f99\u7684\u53e3\u91cc\uff0c\u4ed6\u98de\u8eab\u8dc3\u4e0a\u77f3\u9f99\uff0c\u5c31\u62ff\u5230\u4e86\u90a3\u628a\u5251\u3002\u54ea\u77e5\u4ed6\u5b58\u5fc3\u4e0d\u826f\uff0c\u628a\u5176\u4f59\u4e24\u5b9d\u90fd\u62ff\u4e86\u4e0b\u6765\u3002\u90a3\u4fbf\u662f\u4e8c\u5341\u56db\u679a\u91d1\u86c7\u9525\u548c\u90a3\u5f20\u85cf\u5b9d\u5730\u56fe\u4e86\u3002\u201d\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u95ed\u76ee\u6c89\u601d\u5f80\u4e8b\uff0c\u505c\u4e86\u7247\u523b\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u89c1\u4ed6\u628a\u4e09\u5b9d\u90fd\u62ff\u4e86\u4e0b\u6765\uff0c\u5c31\u77e5\u4e8b\u60c5\u4e0d\u5999\uff0c\u5b9a\u8981\u4ed6\u628a\u91d1\u86c7\u9525\u548c\u5730\u56fe\u653e\u56de\u9f99\u53e3\u3002\u201d \u3000\u3000\u9752\u9752\u65e9\u77e5\u90a3\u4fbf\u662f\u5efa\u6587\u7687\u5e1d\u7684\u85cf\u5b9d\u4e4b\u56fe\uff0c\u6545\u610f\u95ee\u9053\uff1a\u201c\u751a\u4e48\u5730\u56fe\uff1f\u6211\u7239\u7239\u4e00\u5fc3\u53ea\u60f3\u62a5\u4ec7\uff0c\u8981\u4f60\u4eec\u4e94\u6bd2\u6559\u7684\u65e7\u5730\u56fe\u6765\u6709\u751a\u4e48\u7528\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u662f\u751a\u4e48\u5730\u56fe\u3002\u8fd9\u662f\u672c\u6559\u51e0\u5341\u5e74\u6765\u4f20\u4e0b\u6765\u7684\u5b9d\u7269\u3002\u54fc\uff0c\u8fd9\u4eba\u5c31\u662f\u4e0d\u5b58\u597d\u5fc3\u3002\u4ed6\u4e5f\u4e0d\u7b54\u6211\u7684\u8bdd\uff0c\u53ea\u662f\u671b\u7740\u6211\u7b11\uff0c\u5ffd\u7136\u8fc7\u6765\u62b1\u4f4f\u4e86\u6211\u3002\u540e\u6765\uff0c\u6211\u4e5f\u5c31\u4e0d\u95ee\u4ed6\u751a\u4e48\u4e86\u3002\u4ed6\u8bf4\u62a5\u4ec7\u4e4b\u540e\uff0c\u4e00\u5b9a\u5f52\u8fd8\u4e09\u5b9d\u3002\u4ed6\u53bb\u4e86\u4e4b\u540e\uff0c\u6211\u5929\u5929\u60f3\u5ff5\u7740\u4ed6\uff0c\u4e24\u5e74\u6765\u7adf\u6ca1\u534a\u70b9\u8baf\u606f\u3002\u540e\u6765\u5ffd\u7136\u6c5f\u6e56\u4e0a\u4f20\u8a00\uff0c\u8bf4\u6c5f\u5357\u51fa\u4e86\u4e00\u4e2a\u602a\u4fa0\uff0c\u4f7f\u4e00\u628a\u602a\u5251\uff0c\u5584\u7528\u91d1\u9525\u4f24\u4eba\uff0c\u5f97\u4e86\u4e2a\u7ef0\u53f7\u53eb\u4f5c\u2018\u91d1\u86c7\u90ce\u541b\u2019\u3002\u6211\u77e5\u9053\u5b9a\u7136\u662f\u4ed6\uff0c\u5fc3\u91cc\u6302\u7740\u4ed6\u4e0d\u77e5\u62a5\u4e86\u5927\u4ec7\u6ca1\u6709\u3002\u8fc7\u4e0d\u591a\u4e45\uff0c\u6559\u4e3b\u8d77\u4e86\u7591\u5fc3\uff0c\u7ec8\u4e8e\u67e5\u5230\u4e09\u5b9d\u5931\u843d\uff0c\u8981\u6211\u81ea\u5df1\u4e86\u65ad\uff0c\u7ec8\u4e8e\u843d\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u4e3a\u751a\u4e48\u662f\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u4f55\u7ea2\u836f\u542b\u6012\u4e0d\u7b54\u3002 \u3000\u3000\u4f55\u94c1\u624b\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u65f6\u6211\u7239\u7239\u5f53\u6559\u4e3b\uff0c\u867d\u662f\u81ea\u5df1\u4eb2\u59b9\u5b50\u72af\u4e86\u8fd9\u4e8b\uff0c\u53ef\u4e5f\u65e0\u6cd5\u56de\u62a4\u3002\u59d1\u59d1\u4f9d\u7740\u6559\u91cc\u7684\u89c4\u77e9\uff0c\u8eab\u5165\u86c7\u7a9f\uff0c\u53d7\u4e07\u86c7\u54ac\u556e\u4e4b\u707e\u3002\u5979\u8138\u4e0a\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u90a3\u662f\u7ed9\u86c7\u54ac\u7684\u3002\u201d\u9752\u9752\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u6218\uff0c\u5fc3\u4e2d\u5bf9\u8fd9\u4e2a\u8001\u4e5e\u5a46\u987f\u611f\u6b49\u4ec4\u3002\u8bf4\u9053\uff1a\u201c\u8fd9\u2026\u2026 \u3000\u3000\u8fd9\u53ef\u771f\u5bf9\u4f60\u4e0d\u4f4f\u4e86\u3002\u6211\u5148\u524d\u5b9e\u5728\u4e0d\u77e5\u9053\u2026\u2026\u201d\u4f55\u7ea2\u836f\u6a2a\u4e86\u5979\u4e00\u773c\uff0c\u54fc\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u4f55\u94c1\u624b\u53c8\u9053\uff1a\u201c\u5979\u517b\u597d\u4f24\u540e\uff0c\u4fbf\u51fa\u5916\u6c42\u4e5e\uff0c\u4f9d\u6211\u4eec\u6559\u89c4\uff0c\u72af\u4e86\u91cd\u7f6a\u4e4b\u4eba\uff0c\u4e09\u5341\u5e74\u4e4b\u5185\u5fc5\u987b\u4e5e\u8ba8\u6d3b\u547d\uff0c\u4e0d\u8bb8\u5077\u76d7\u4e00\u6587\u4e00\u996d\uff0c\u4e5f\u4e0d\u8bb8\u6536\u53d7\u6b66\u6797\u540c\u9053\u7684\u5468\u6d4e\u3002\u201d \u3000\u3000\u9752\u9752\u4f4e\u58f0\u5bf9\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u8981\u662f\u6211\u7239\u7239\u771f\u7684\u8fd9\u822c\u5bb3\u4e86\u4f60\uff0c\u90a3\u786e\u662f\u4ed6\u4e0d\u597d\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u9f3b\u4e2d\u4e00\u54fc\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u6210\u5343\u6210\u4e07\u6761\u86c7\u54ac\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u88ab\u7f5a\u8ba8\u996d\u4e09\u5341\u5e74\uff0c\u90a3\u90fd\u662f\u6211\u81ea\u5df1\u5fc3\u7518\u60c5\u613f\u7684\u3002\u90a3\u65e5\u6211\u5e26\u4ed6\u53bb\u6bd2\u9f99\u6d1e\uff0c\u8fd9\u7ed3\u679c\u65e9\u5c31\u60f3\u5230\u4e86\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u662f\u4ed6\u5bb3\u6211\u7684\u3002\u4ed6\u5bf9\u6211\u4e0d\u8d77\uff0c\u5374\u662f\u4ed6\u5bf9\u6211\u8d1f\u5fc3\u8584\u5e78\u3002\u90a3\u65f6\u6211\u8fd8\u771f\u4e00\u5f80\u60c5\u6df1\uff0c\u4e00\u8def\u4e5e\u8ba8\uff0c\u5230\u6c5f\u5357\u53bb\u627e\u4ed6\uff0c\u5230\u4e86\u6d59\u6c5f\u5883\u5185\uff0c\u5c31\u542c\u5230\u4ed6\u5728\u8862\u5dde\u6740\u4eba\u62a5\u4ec7\u7684\u4e8b\u3002\u6211\u60f3\u8ddf\u4ed6\u4f1a\u9762\uff0c\u4f46\u4ed6\u795e\u51fa\u9b3c\u6ca1\uff0c\u59cb\u7ec8\u6ca1\u80fd\u4f1a\u7740\u3002 \u3000\u3000\u7b49\u5230\u5728\u91d1\u534e\u89c1\u5230\u4ed6\u65f6\uff0c\u4ed6\u5df2\u7ed9\u4eba\u6293\u4f4f\u4e86\u3002\u4f60\u77e5\u9053\u6293\u4ed6\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u662f\u8862\u5dde\u7684\u4ec7\u5bb6\u4e48\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u6b63\u662f\u3002\u5c31\u662f\u521a\u624d\u4f60\u89c1\u5230\u7684\u6e29\u5bb6\u90a3\u51e0\u4e2a\u8001\u5934\u5b50\u3002\u201d\u4f55\u94c1\u624b\u548c\u9752\u9752\u540c\u65f6\u201c\u554a\u201d \u3000\u3000\u7684\u4e00\u58f0\u3002\u4f55\u94c1\u624b\u662f\u60f3\u4e0d\u5230\u6e29\u6c0f\u56db\u8001\u7adf\u4e0e\u6b64\u4e8b\u4f1a\u6709\u7275\u8fde\uff0c\u9752\u9752\u662f\u542c\u5230\u5916\u516c\u4eec\u6765\u5230\u5317\u4eac\u800c\u611f\u60ca\u8be7\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u6211\u51e0\u6b21\u60f3\u4e0b\u6bd2\u5bb3\u6b7b\u654c\u4eba\u3002\u4f46\u8fd9\u4e9b\u4eba\u65e9\u5c31\u5728\u9632\u4ed6\u4e0b\u6bd2\uff0c\u8336\u6c34\u996e\u98df\uff0c\u751a\u4e48\u90fd\u8981\u4ed6\u5148\u8bd5\u8fc7\uff0c\u8fd9\u4e00\u6765\u6211\u5c31\u6ca1\u6cd5\u4e0b\u624b\u3002\u4ed6\u4eec\u62bc\u7740\u4ed6\u4e00\u8def\u5f80\u5317\uff0c\u540e\u6765\u624d\u77e5\u662f\u8981\u903c\u4ed6\u4ea4\u51fa\u90a3\u5f20\u5730\u56fe\u6765\u3002\u6709\u4e00\u6b21\uff0c\u6211\u7ec8\u4e8e\u627e\u5230\u673a\u4f1a\uff0c\u8ddf\u4ed6\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002\u4ed6\u8bf4\u8eab\u4e0a\u7684\u7b4b\u8109\u90fd\u7ed9\u654c\u4eba\u6311\u65ad\u4e86\uff0c\u5df2\u6210\u5e9f\u4eba\uff0c\u5bf9\u5934\u6b66\u529f\u9ad8\u5f3a\uff0c\u51ed\u6211\u4e00\u4eba\u51b3\u8ba1\u62b5\u654c\u4e0d\u4e86\uff0c\u773c\u4e0b\u53ea\u6709\u4e00\u7ebf\u751f\u673a\uff0c\u4ed6\u6b63\u9a97\u4ed6\u4eec\u4e0a\u534e\u5c71\u53bb\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4ed6\u5230\u534e\u5c71\u53bb\u5e72\u751a\u4e48\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u4ed6\u8bf4\u5929\u4e0b\u53ea\u6709\u4e00\u4eba\u80fd\u591f\u6551\u4ed6\uff0c\u90a3\u4fbf\u662f\u534e\u5c71\u6d3e\u638c\u95e8\u4eba\u795e\u5251\u4ed9\u733f\u7a46\u4eba\u6e05\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5728\u5e8a\u5e95\u542c\u7740\u8fd9\u4e2a\u60ca\u5fc3\u52a8\u9b44\u7684\u6545\u4e8b\uff0c\u5fc3\u91cc\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u5bf9\u91d1\u86c7\u90ce\u541b\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u4e0d\u77e5\u662f\u75db\u6068\u3001\u662f\u60cb\u60dc\u3001\u8fd8\u662f\u601c\u60af\uff1f\u8fd9\u65f6\u542c\u5230\u5e08\u7236\u7684\u540d\u5b57\uff0c\u66f4\u662f\u51dd\u795e\u503e\u542c\u3002 \u3000\u3000\u9752\u9752\u542c\u4f55\u7ea2\u836f\u63d0\u5230\u4e86\u8881\u627f\u5fd7\u7684\u5e08\u7236\uff0c\u4e5f\u66f4\u7559\u4e0a\u4e86\u795e\uff0c\u53ea\u542c\u5979\u63a5\u7740\u9053\uff1a\u201c\u6211\u95ee\u4ed6\u7a46\u4eba\u6e05\u662f\u751a\u4e48\u4eba\uff0c\u4ed6\u8bf4\u90a3\u662f\u5929\u4e0b\u62f3\u5251\u65e0\u53cc\u7684\u4e00\u4f4d\u9ad8\u4eba\u4fa0\u58eb\u3002\u4ed6\u867d\u4ece\u672a\u89c1\u8fc7\uff0c\u4f46\u7d20\u77e5\u8fd9\u4eba\u6b63\u76f4\u4ed7\u4e49\uff0c\u82e5\u662f\u89c1\u5230\u4ed6\u5982\u6b64\u53d7\u4eba\u6298\u78e8\uff0c\u5b9a\u4f1a\u51fa\u624b\u76f8\u6551\u3002\u4ed6\u8bf4\u6e29\u6c0f\u4e94\u8001\u7684\u4e94\u884c\u9635\u6cd5\u5389\u5bb3\uff0c\u53c8\u6709\u5d06\u5cd2\u6d3e\u9053\u4eba\u76f8\u52a9\uff0c\u9664\u4e86\u8fd9\u59d3\u7a46\u7684\uff0c\u522b\u4eba\u4e5f\u6253\u4ed6\u4eec\u4e0d\u9000\u3002\u4ed6\u53eb\u6211\u5feb\u53bb\u534e\u5c71\uff0c\u5411\u7a46\u5927\u4fa0\u54ed\u8bc9\u76f8\u6c42\u3002\u6211\u7b54\u5141\u4e86\uff0c\u5fc3\u4e2d\u6253\u5b9a\u4e3b\u610f\uff0c\u8981\u662f\u7a46\u5927\u4fa0\u8896\u624b\u4e0d\u7406\uff0c\u6211\u5c31\u5728\u4ed6\u9762\u524d\u6a2a\u5251\u81ea\u520e\uff0c\u5b81\u53ef\u81ea\u5df1\u6b7b\u4e86\uff0c\u4e5f\u603b\u8981\u6551\u4ed6\u51fa\u6765\u3002\u654c\u4eba\u8f6c\u773c\u4fbf\u56de\uff0c\u4e0d\u80fd\u8ddf\u4ed6\u591a\u8bf4\u8bdd\uff0c\u6211\u62b1\u4f4f\u4e86\u4ed6\uff0c\u60f3\u4eb2\u4eb2\u4ed6\u7684\u8138\u4fbf\u8d70\u4e86\u3002\u54ea\u77e5\u4e00\u6328\u8fd1\u8eab\uff0c\u5ffd\u7136\u95fb\u5230\u4ed6\u80f8\u53e3\u5fae\u6709\u5973\u4eba\u9999\u6c14\uff0c\u4f38\u624b\u5230\u4ed6\u8863\u5185\u4e00\u6478\uff0c\u638f\u51fa\u6765\u4e00\u53ea\u7ee3\u5f97\u5f88\u7cbe\u81f4\u7684\u9999\u8377\u5305\uff0c\u91cc\u9762\u653e\u7740\u4e00\u675f\u5973\u4eba\u7684\u5934\u53d1\uff0c\u4e00\u679a\u5c0f\u5c0f\u7684\u91d1\u9497\uff0c\u6211\u6c14\u5f97\u5168\u8eab\u98a4\u6296\uff0c\u95ee\u4ed6\u662f\u8c01\u7ed9\u7684\u3002\u4ed6\u4e0d\u80af\u8bf4\u3002\u6211\u8bf4\u8981\u662f\u4e0d\u8bf4\uff0c\u6211\u5c31\u4e0d\u53bb\u6c42\u7a46\u5927\u4fa0\u3002\u4ed6\u95ed\u5634\u4e0d\u7406\uff0c\u795e\u6c14\u5f88\u662f\u9ad8\u50b2\u3002\u4f60\u77a7\uff0c\u4f60\u77a7\uff0c\u8fd9\u5c0f\u5b50\u7684\u795e\u6c14\uff0c\u5c31\u8ddf\u4ed6\u8001\u5b50\u5f53\u5e74\u4e00\u6a21\u4e00\u6837\u3002\u201d \u3000\u3000\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u5ffd\u8f6c\u60e8\u5389\uff0c\u4e00\u624b\u6307\u7740\u9752\u9752\uff0c\u505c\u4e86\u4e00\u9635\uff0c\u53c8\u9053\uff1a\u201c\u6211\u8fd8\u60f3\u903c\u4ed6\uff0c\u770b\u5b88\u4ed6\u7684\u4eba\u5374\u56de\u6765\u4e86\u3002\u6211\u5b9e\u5728\u6c14\u82e6\u4e4b\u6781\u3002 \u3000\u3000\u6211\u4e3a\u4ed6\u53d7\u4e86\u8fd9\u822c\u82e6\u695a\uff0c\u4ed6\u5374\u6487\u4e0b\u4e86\u6211\uff0c\u53e6\u5916\u6709\u4e86\u60c5\u4eba\u3002 \u3000\u3000\u201c\u7b49\u90a3\u4e00\u4f19\u4eba\u4e0a\u4e86\u534e\u5c71\uff0c\u6211\u4e5f\u4e0d\u53bb\u627e\u751a\u4e48\u7a46\u5927\u4fa0\uff0c\u6697\u4e2d\u7ed9\u770b\u5b88\u4ed6\u7684\u4eba\u4e0b\u6bd2\uff0c\u5fc3\u60f3\u5c31\u7b97\u8fde\u90a3\u8d1f\u5fc3\u6c49\u4e00\u8d77\u6bd2\u6b7b\uff0c\u4e5f\u4e0d\u7406\u4f1a\u4e86\uff0c\u7ec8\u4e8e\u5f04\u6b7b\u4e86\u4e24\u4e2a\u9053\u58eb\u3002\u90a3\u51e0\u4e2a\u59d3\u6e29\u7684\u5168\u6ca1\u60f3\u5230\u6697\u91cc\u6709\u4eba\u7b97\u8ba1\uff0c\u4e00\u758f\u795e\uff0c\u6211\u5c31\u5c06\u4ed6\u6551\u4e86\u51fa\u6765\uff0c\u8fde\u91d1\u86c7\u5251\u3001\u91d1\u86c7\u9525\u90fd\u4e00\u8d77\u76d7\u5230\u4e86\u624b\u3002\u6211\u5c06\u4ed6\u85cf\u5728\u4e00\u4e2a\u5c71\u6d1e\u91cc\u3002\u6e29\u5bb6\u51e0\u5144\u5f1f\u904d\u627e\u4e0d\u89c1\uff0c\u4e92\u76f8\u7591\u5fc3\uff0c\u81ea\u4f19\u513f\u5435\u4e86\u4e00\u9635\uff0c\u518d\u5927\u4e3e\u641c\u5c71\u3002\u8fd9\u53ef\u5c31\u5f97\u7f6a\u4e86\u7a46\u5927\u4fa0\u3002\u4ed6\u6697\u4e2d\u65bd\u5c55\u7edd\u6280\uff0c\u5c06\u4ed6\u4eec\u90fd\u5413\u4e0b\u4e86\u534e\u5c71\uff0c\u81ea\u5df1\u8ddf\u7740\u4e5f\u4e0b\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u5929\u665a\u4e0a\uff0c\u6211\u8981\u90a3\u8d1f\u5fc3\u6c49\u8bf4\u51fa\u4ed6\u60c5\u4eba\u7684\u59d3\u540d\u6765\u3002\u4ed6\u77e5\u9053\u4e00\u7ecf\u5410\u9732\uff0c\u6211\u5b9a\u4f1a\u53bb\u5bb3\u6b7b\u4ed6\u7684\u5fc3\u4e0a\u4eba\u3002\u4ed6\u6b66\u529f\u5df2\u5931\uff0c\u53c8\u4e0d\u80fd\u8d76\u53bb\u4fdd\u62a4\uff0c\u56e0\u6b64\u59cb\u7ec8\u95ed\u53e3\u4e0d\u7b54\u3002\u6211\u6068\u6781\u4e86\uff0c\u4e00\u8fde\u4e09\u5929\uff0c\u6bcf\u5929\u65e9\u6668\uff0c\u4e2d\u5348\u3001\u665a\u4e0a\uff0c\u90fd\u7528\u523a\u8346\u72e0\u72e0\u97ad\u4ed6\u4e00\u987f\u2026\u2026\u201d \u3000\u3000\u9752\u9752\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u8fd9\u6076\u5a46\u5a18\uff0c\u8fd9\u822c\u6298\u78e8\u6211\u7239\u7239\uff01\u201d \u3000\u3000\u4f55\u7ea2\u836f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u81ea\u4f5c\u81ea\u53d7\u3002\u6211\u8d8a\u6253\u5f97\u5389\u5bb3\uff0c\u4ed6\u7b11\u5f97\u8d8a\u54cd\u3002\u4ed6\u8bf4\u5012\u4e5f\u4e0d\u56e0\u4e3a\u6211\u7684\u8138\u7ed9\u86c7\u54ac\u574f\u4e86\uff0c\u8fd9\u624d\u4e0d\u7231\u6211\u3002\u4ed6\u4ece\u6765\u5c31\u6ca1\u771f\u5fc3\u559c\u6b22\u6211\u8fc7\uff0c\u6bd2\u9f99\u6d1e\u4e2d\u7684\u4e8b\uff0c\u5728\u4ed6\u4e0d\u8fc7\u9022\u573a\u4f5c\u620f\uff0c\u4ed6\u751f\u5e73\u4e0d\u77e5\u73a9\u8fc7\u591a\u5c11\u5973\u4eba\uff0c\u53ef\u662f\u771f\u6b63\u653e\u5728\u5fc3\u574e\u513f\u91cc\u7684\uff0c\u53ea\u662f\u4ed6\u672a\u5a5a\u59bb\u4e00\u4e2a\u3002\u4ed6\u8bf4\u4ed6\u672a\u5a5a\u59bb\u53c8\u7f8e\u8c8c\u53c8\u6e29\u67d4\uff0c\u53c8\u5929\u771f\uff0c\u6bd4\u6211\u53ef\u597d\u4e0a\u4e00\u767e\u500d\u4e86\uff0c\u4ed6\u8bf4\u4e00\u53e5\uff0c\u6211\u62bd\u4ed6\u4e00\u97ad\uff1b\u6211\u62bd\u4e00\u97ad\uff0c\u4ed6\u5c31\u5938\u90a3\u4e2a\u8d31\u5973\u4eba\u4e00\u53e5\u3002\u6253\u5230\u540e\u6765\uff0c\u4ed6\u5168\u8eab\u6ca1\u4e00\u5757\u5b8c\u6574\u76ae\u8089\u4e86\uff0c\u8fd8\u662f\u7b11\u7740\u5938\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u201c\u5230\u7b2c\u4e09\u5929\u4e0a\uff0c\u6211\u4eec\u4e24\u4eba\u90fd\u997f\u5f97\u6ca1\u529b\u6c14\u4e86\u3002\u6211\u51fa\u53bb\u91c7\u679c\u5b50\u5403\uff0c\u56de\u6765\u65f6\u4ed6\u5374\u5b88\u5728\u6d1e\u53e3\uff0c\u8bf4\u9053\u53ea\u8981\u6211\u8e0f\u8fdb\u6d1e\u95e8\u4e00\u6b65\uff0c\u5c31\u662f\u4e00\u5251\u3002\u4ed6\u867d\u5931\u4e86\u6b66\u529f\uff0c\u4f46\u6709\u91d1\u86c7\u5b9d\u5251\u5728\u624b\uff0c\u6211\u4e5f\u4e0d\u6562\u8fdb\u53bb\u3002\u6211\u5bf9\u4ed6\u8bf4\uff0c\u53ea\u8981\u4ed6\u8bf4\u51fa\u90a3\u5973\u5b50\u7684\u59d3\u540d\u4f4f\u6240\uff0c\u6211\u5c31\u9976\u4e86\u4ed6\u5bf9\u6211\u7684\u8d1f\u5fc3\u8584\u5e78\uff0c\u4ed6\u867d\u662f\u4e2a\u5e9f\u4eba\uff0c\u6211\u8fd8\u662f\u4f1a\u597d\u597d\u7684\u670d\u4f8d\u4ed6\u4e00\u751f\u3002\u4ed6\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u4ed6\u7231\u90a3\u5973\u5b50\u80dc\u8fc7\u7231\u81ea\u5df1\u7684\u6027\u547d\u3002\u597d\u5427\uff0c\u6211\u4eec\u4e24\u4eba\u5c31\u8fd9\u4e48\u8017\u7740\u3002\u6211\u6709\u4e1c\u897f\u5403\uff0c\u4ed6\u5374\u6328\u997f\u786c\u633a\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9eef\u7136\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u5c31\u8fd9\u6837\u5f04\u6b7b\u4e86\u4ed6\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u54fc\uff0c\u624d\u6ca1\u8fd9\u4e48\u5bb9\u6613\u8ba9\u4ed6\u6b7b\u5462\u3002\u8fc7\u4e86\u51e0\u5929\uff0c\u4ed6\u997f\u5f97\u5168\u8eab\u8131\u529b\uff0c\u6211\u8d70\u8fdb\u6d1e\u53bb\uff0c\u5c06\u4ed6\u53cc\u8db3\u6253\u6298\u4e86\u3002\u201d \u3000\u3000\u9752\u9752\u60ca\u53eb\u4e00\u58f0\uff0c\u8df3\u8d77\u6765\u8981\u6253\uff0c\u5374\u88ab\u4f55\u94c1\u624b\u4f38\u624b\u8f7b\u8f7b\u6309\u4f4f\u4e86\u80a9\u5934\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u4f55\u94c1\u624b\u529d\u9053\uff1a\u201c\u522b\u751f\u6c14\uff0c\u542c\u59d1\u59d1\u8bf4\u5b8c\u5427\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u8fd9\u534e\u5c71\u7edd\u9876\u9669\u5cfb\u5f02\u5e38\uff0c\u4ed6\u53cc\u8db3\u574f\u4e86\u4e4b\u540e\uff0c\u5fc5\u5b9a\u4e0d\u80fd\u4e0b\u53bb\uff0c\u6211\u5c31\u4e0b\u5c71\u53bb\u6253\u542c\u4ed6\u60c5\u4eba\u7684\u8baf\u606f\u3002\u6211\u8981\u6293\u4f4f\u8fd9\u8d31\u4eba\uff0c\u628a\u5979\u7684\u8138\u5f04\u5f97\u6bd4\u6211\u8fd8\u8981\u4e11\uff0c\u7136\u540e\u5e26\u53bb\u7ed9\u4ed6\u77a7\u77a7\uff0c\u770b\u4ed6\u8fd8\u80fd\u4e0d\u80fd\u518d\u5938\u5979\u8d5e\u5979\u3002 \u3000\u3000\u201c\u6211\u5bfb\u8bbf\u4e86\u534a\u5e74\u591a\uff0c\u6ca1\u5f97\u5230\u4e00\u70b9\u8baf\u606f\uff0c\u62c5\u5fc3\u90a3\u59d3\u7a46\u7684\u56de\u5c71\u649e\u89c1\u4e86\u4ed6\uff0c\u90a3\u53ef\u8981\u7cdf\u3002\u90a3\u5929\u6211\u89c1\u90a3\u59d3\u7a46\u7684\u6697\u4e2d\u663e\u529f\uff0c\u9a71\u9010\u77f3\u6881\u6d3e\u7684\u4eba\uff0c\u672c\u9886\u771f\u662f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u8981\u662f\u90a3\u8d1f\u5fc3\u8d3c\u6c42\u4ed6\u76f8\u52a9\uff0c\u6211\u518d\u4e0a\u534e\u5c71\uff0c\u53ef\u5c31\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\u3002\u5f85\u5f97\u6211\u56de\u5230\u534e\u5c71\uff0c\u54ea\u77e5\u4ed6\u5df2\u4e0d\u77e5\u53bb\u5411\u3002\u6211\u5728\u5c71\u9876\u5230\u5904\u627e\u904d\u4e86\uff0c\u6ca1\u4e00\u70b9\u8e2a\u8ff9\uff0c\u4e0d\u77e5\u662f\u90a3\u59d3\u7a46\u7684\u6551\u4e86\u4ed6\u5462\uff0c\u8fd8\u662f\u53bb\u4e86\u522b\u7684\u5730\u65b9\u3002\u5341\u591a\u5e74\u6765\uff0c\u6c5f\u6e56\u4e0a\u4e0d\u518d\u542c\u5230\u4ed6\u7684\u4fe1\u606f\u3002\u6211\u8d70\u904d\u5929\u5357\u5730\u5317\uff0c\u4e5f\u4e0d\u77e5\u8fd9\u6ca1\u826f\u5fc3\u7684\u574f\u86cb\u662f\u6b7b\u662f\u6d3b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u5979\u6ee1\u8154\u6028\u6bd2\u7684\u8bf4\u5230\u8fd9\u91cc\uff0c\u65b9\u624d\u604d\u7136\u5927\u609f\uff1a\u91d1\u86c7\u90ce\u541b\u6240\u4ee5\u81ea\u884c\u5c01\u95ed\u5728\u8fd9\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u5b9a\u662f\u77e5\u9053\u51a4\u5bb6\u9b54\u5934\u5fc5\u4f1a\u91cd\u6765\uff0c\u4ed6\u6b66\u529f\u5168\u5931\uff0c\u65e0\u6cd5\u62b5\u654c\uff0c\u60f3\u5230\u8d1f\u4eba\u4e0d\u4e49\uff0c\u53c8\u803b\u4e8e\u5411\u4eba\u6c42\u6551\uff0c\u4e8e\u662f\u5165\u6d1e\u81ea\u6740\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4f55\u7ea2\u836f\u5389\u58f0\u5bf9\u9752\u9752\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u4ed6\u8fd8\u7559\u4e0b\u4e86\u4f60\u8fd9\u5b7d\u79cd\u3002\u4f60\u5988\u5988\u5462\uff1f\u5979\u59d3\u751a\u4e48\uff1f\u53eb\u751a\u4e48\uff1f\u4f4f\u5728\u54ea\u91cc\uff1f\u4f60\u4e0d\u8bf4\u51fa\u6765\uff0c\u6211\u5148\u525c\u53bb\u4f60\u7684\u773c\u775b\u3002\u201d \u3000\u3000\u9752\u9752\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4f60\u51f6\uff0c\u4f60\u51f6\uff01\u6211\u7239\u7239\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u5988\u5988\u6bd4\u4f60\u597d\u4e00\u767e\u500d\u4e5f\u4e0d\u6b62\uff0c\u597d\u4e00\u5343\u500d\uff0c\u4e00\u4e07\u500d\u2026\u2026\u201d\u4f55\u7ea2\u836f\u6012\u4e0d\u53ef\u904f\uff0c\u53cc\u624b\u4e00\u63a2\uff0c\u5341\u722a\u5411\u9752\u9752\u8138\u4e0a\u6293\u6765\u3002 \u3000\u3000\u9752\u9752\u6025\u5f80\u88ab\u91cc\u4e00\u7f29\uff0c\u5c06\u88ab\u5b50\u8499\u4f4f\u4e86\u5934\u3002\u4f55\u94c1\u624b\u5fd9\u4f38\u624b\u6321\u4f4f\u4f55\u7ea2\u836f\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u6012\u9053\uff1a\u201c\u4f60\u8981\u4ed6\u8bf4\u51fa\u4ed6\u7236\u6bcd\u7684\u6240\u5728\uff0c\u6211\u5c31\u9976\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u54b1\u4eec\u6709\u5927\u4e8b\u5728\u8eab\uff0c\u4f60\u5374\u603b\u662f\u4e3a\u4e86\u79c1\u6028\uff0c\u5230\u5904\u62db\u60f9\u3002\u4ed9\u90fd\u6d3e\u7684\u4e8b\uff0c\u4e0d\u4e5f\u662f\u4f60\u641e\u7684\u4e48\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u54fc\uff0c\u90a3\u9ec4\u6728\u8d3c\u9053\u8ddf\u4eba\u778e\u5439\uff0c\u8bf4\u4ed6\u8ba4\u5f97\u91d1\u86c7\u90ce\u541b\uff0c\u504f\u5de7\u8ba9\u6211\u542c\u89c1\u4e86\uff0c\u5f53\u7136\u8981\u903c\u95ee\u4ed6\u90a3\u8d1f\u5fc3\u8d3c\u7684\u4e0b\u843d\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u5173\u4e86\u9ec4\u6728\u8fd9\u4e9b\u5e74\uff0c\u7ed9\u4ed6\u4e0a\u4e86\u8fd9\u8bb8\u591a\u6bd2\u5211\uff0c\u4ed6\u59cb\u7ec8\u4e0d\u8bf4\uff0c\u591a\u534a\u662f\u771f\u7684\u4e0d\u77e5\u3002\u591a\u7ed3\u4ec7\u5bb6\u4e5f\u662f\u65e0\u7528\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u7126\u5b9b\u513f\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u60f3\u4ed9\u90fd\u6d3e\u8ddf\u4e94\u6bd2\u6559\u7684\u6881\u5b50\u539f\u6765\u7531\u6b64\u800c\u7ed3\uff0c\u90a3\u4e48\u9ec4\u6728\u9053\u4eba\u5e76\u6ca1\u6709\u6b7b\uff0c\u53ea\u4e0d\u8fc7\u7ed9\u4ed6\u4eec\u6263\u4f4f\u4e86\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u53eb\u9053\uff1a\u201c\u90a3\u59d3\u8881\u7684\u5c0f\u5b50\u62ff\u7740\u54b1\u4eec\u7684\u91d1\u86c7\u5251\uff0c\u53c8\u7528\u91d1\u86c7\u9525\u6253\u54b1\u4eec\u7684\u72d7\u5b50\uff0c\u90a3\u5730\u56fe\u60f3\u5fc5\u4e5f\u843d\u5165\u4e86\u4ed6\u624b\u91cc\u3002\u4f60\u8eab\u4e3a\u6559\u4e3b\uff0c\u600e\u5730\u4e0d\u60f3\u6cd5\u5b50\uff1f\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u597d\u5566\uff0c\u6211\u77e5\u9053\u4e86\u3002\u59d1\u59d1\uff0c\u4f60\u51fa\u53bb\u4f11\u606f\u4e00\u4f1a\u513f\u5427\u3002\u201d\u4f55\u7ea2\u836f\u7ad9\u8d77\u8eab\u6765\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u5207\u5168\u8ddf\u4f60\u8bf4\u4e86\u3002\u7528\u4e0d\u7528\u6211\u7684\u8ba1\u7b56\uff0c\u7ed9\u4e0d\u7ed9\u6211\u51fa\u6c14\u3002\u5168\u51ed\u4f60\u5427\uff01\u201d \u3000\u3000\u4f55\u94c1\u624b\u7b11\u4e86\u7b11\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u4f60\u51fa\u6765\uff0c\u6211\u8fd8\u6709\u8bdd\u8ddf\u4f60\u8bf4\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u8bf4\u4e5f\u4e00\u6837\u3002\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u4e0d\uff0c\u54b1\u4eec\u51fa\u53bb\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4e24\u4eba\u8d70\u51fa\u623f\u53bb\uff0c\u6b65\u58f0\u6e10\u8fdc\uff0c\u5fd9\u94bb\u4e86\u51fa\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u54b1\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u9752\u9752\u6012\u76ee\u671b\u7740\u7126\u5b9b\u513f\uff0c\u89c1\u5979\u5934\u53d1\u84ec\u677e\uff0c\u8138\u4e0a\u53c8\u6cbe\u4e86\u4e0d\u5c11\u7070\u5c18\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u8eb2\u7740\u5e72\u751a\u4e48\uff1f\u201d\u7126\u5b9b\u513f\u4e00\u5446\uff0c\u53cc\u988a\u98de\u7ea2\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5feb\u8d77\u8eab\u3002\u5979\u4eec\u4e0d\u5b89\u597d\u5fc3\uff0c\u8981\u60f3\u6cd5\u513f\u5bb3\u4f60\u5440\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u5bb3\u6b7b\u4e86\u6700\u597d\uff0c\u6211\u4e0d\u8d70\u3002\u201d\u8881\u627f\u5fd7\u6025\u9053\uff1a\u201c\u6709\u751a\u4e48\u4e8b\uff0c\u56de\u53bb\u6162\u6162\u513f\u518d\u8bf4\u4e0d\u597d\u4e48\uff1f\u600e\u4e48\u8fd9\u4e2a\u65f6\u5019\u778e\u6363\u4e71\u3002\u201d\u9752\u9752\u6012\u9053\uff1a\u201c\u6211\u504f\u504f\u8981\u6363\u4e71\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\u8fd9\u4eba\u4e0d\u53ef\u7406\u55bb\uff0c\u60c5\u52bf\u5df2\u6025\uff0c\u7a0d\u518d\u803d\u6401\uff0c\u4e0d\u662f\u65e0\u6cd5\u8131\u8eab\uff0c\u4fbf\u662f\u7687\u5e1d\u8eab\u8fb9\u53d1\u751f\u5927\u4e8b\uff0c\u5fd9\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u4f38\u624b\u53bb\u62c9\u5979\u3002 \u3000\u3000\u9752\u9752\u4e00\u77a5\u773c\u95f4\uff0c\u89c1\u5230\u7126\u5b9b\u513f\u5ff8\u6029\u817c\u8146\u7684\u795e\u8272\uff0c\u60f3\u50cf\u9002\u624d\u5979\u548c\u8881\u627f\u5fd7\u5728\u5e8a\u5e95\u4e0b\u8eb2\u4e86\u8fd9\u4e48\u4e45\uff0c\u4e0d\u77e5\u662f\u5982\u4f55\u4eb2\u70ed\uff0c\u53c8\u60f3\u81ea\u5df1\u4e0d\u5728\u8881\u627f\u5fd7\u8eab\u8fb9\u4e4b\u65f6\uff0c\u4e24\u4eba\u53c8\u4e0d\u77e5\u5982\u4f55\u537f\u537f\u6211\u6211\uff0c\u8d8a\u60f3\u8d8a\u607c\uff0c\u5de6\u624b\u63e1\u4f4f\u4ed6\u624b\uff0c\u53f3\u624b\u72e0\u72e0\u6293\u4e86\u4e00\u628a\u3002\u8881\u627f\u5fd7\u5168\u6ca1\u63d0\u9632\uff0c\u624b\u80cc\u4e0a\u767b\u65f6\u7ed9\u6293\u51fa\u56db\u6761\u8840\u75d5\uff0c\u5fd9\u6323\u8131\u4e86\u624b\uff0c\u6115\u7136\u9053\uff1a\u201c\u4f60\u80e1\u95f9\u751a\u4e48\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u5c31\u662f\u8981\u80e1\u95f9\uff01\u201d\u8bf4\u7740\u628a\u68c9\u88ab\u5728\u5934\u4e0a\u4e00\u515c\u3002\u8881\u627f\u5fd7\u53c8\u6c14\u53c8\u6025\uff0c\u53ea\u662f\u8dfa\u811a\u3002 \u3000\u3000\u7126\u5b9b\u513f\u6025\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4f60\u5b88\u7740\u590f\u59d1\u5a18\uff0c\u6211\u51fa\u53bb\u4e00\u4e0b\u5c31\u56de\u6765\u3002\u201d\u8881\u627f\u5fd7\u5947\u9053\uff1a\u201c\u8fd9\u65f6\u5019\u4f60\u53c8\u53bb\u54ea\u91cc\uff1f\u201d\u7126\u5b9b\u513f\u4e0d\u7b54\uff0c\u63a8\u5f00\u7a97\u6237\uff0c\u8dc3\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5750\u5728\u5e8a\u8fb9\uff0c\u9694\u88ab\u8f7b\u63a8\u9752\u9752\u7684\u8eab\u5b50\u3002\u9752\u9752\u7ffb\u4e86\u4e2a\u8eab\uff0c\u8138\u5b54\u671d\u91cc\u3002\u8fd9\u4e00\u6765\uff0c\u53ef\u771f\u628a\u4ed6\u95f9\u5f97\u65e0\u6cd5\u53ef\u65bd\uff0c\u53c8\u4e0d\u6562\u8d70\u5f00\uff0c\u53ea\u6015\u4f55\u94c1\u624b\u5979\u4eec\u56de\u6765\u4e0b\u86ca\u653e\u6bd2\u3002\u6b63\u5f85\u597d\u8a00\u76f8\u529d\uff0c\u7a81\u7136\u95e8\u53e3\u811a\u6b65\u58f0\u54cd\uff0c\u4ed6\u7eb5\u8eab\u4e0a\u6881\uff0c\u6a2a\u5367\u5728\u5c4b\u9876\u6881\u4e0a\u3002\u53ea\u89c1\u4f55\u94c1\u624b\u91cd\u53c8\u8fdb\u6765\uff0c\u5173\u4e0a\u95e8\u95e9\uff0c\u6162\u6162\u8d70\u5230\u5e8a\u8fb9\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6263\u4f4f\u4e24\u679a\u91d1\u86c7\u9525\u3002\u53ea\u8981\u5979\u6709\u52a0\u5bb3\u4e4b\u610f\uff0c\u7acb\u5373\u53d1\u9525\u6551\u4eba\u3002\u4f55\u94c1\u624b\u51dd\u671b\u7740\u9752\u9752\u7684\u80cc\u5f71\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u590f\u76f8\u516c\uff0c\u6211\u6709\u53e5\u8bdd\u8981\u8ddf\u4f60\u8bf4\u3002\u201d\u9752\u9752\u56de\u8fc7\u5934\u6765\u3002 \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u6211\u59d1\u59d1\u5bf9\u4f60\u7239\u7239\u5982\u6b64\u4e00\u5f80\u60c5\u6df1\uff0c\u4f60\u8bf4\u5979\u662f\u4e0b\u8d31\u4e4b\u4eba\u4e48\uff1f\u201d\u9752\u9752\u4e07\u4e07\u60f3\u4e0d\u5230\u5979\u95ee\u7684\u662f\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e00\u5f80\u60c5\u6df1\uff0c\u600e\u4e48\u4f1a\u662f\u4e0b\u8d31\uff1f\u201d\u63d0\u9ad8\u4e86\u58f0\u97f3\u9053\uff1a\u201c\u8d1f\u5fc3\u8584\u5e78\uff0c\u90a3\u624d\u4e0b\u8d31\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u4e0d\u77e5\u5979\u8fd9\u8bdd\u662f\u6545\u610f\u8bf4\u7ed9\u8881\u627f\u5fd7\u542c\u7684\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u767b\u65f6\u5bb9\u5149\u7115\u53d1\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u7239\u8ddf\u6211\u59d1\u59d1\u65e0\u7f18\uff0c\u90a3\u4e5f\u602a\u4ed6\u4e0d\u5f97\u3002\u4ed6\u5b81\u6b7b\u4e5f\u4e0d\u80af\u8bf4\u51fa\u4f60\u5988\u5988\u7684\u6240\u5728\uff0c\u62da\u7740\u6027\u547d\u6765\u4fdd\u62a4\u5979\uff0c\u5b9e\u662f\u60c5\u6df1\u4e49\u91cd\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u53ef\u60dc\u4e16\u4e0a\u50cf\u6211\u7239\u7239\u90a3\u6837\u7684\u4eba\u5f88\u5c11\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u8981\u662f\u6709\u8fd9\u6837\u7684\u4eba\uff0c\u5b81\u53ef\u4e0d\u8981\u81ea\u5df1\u7684\u6027\u547d\uff0c\u4e5f\u8981\u7ef4\u62a4\u4f60\uff0c\u4f60\u53c8\u600e\u6837\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u8fd9\u822c\u798f\u6c14\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u6211\u4ece\u524d\u4e0d\u61c2\uff0c\u59d1\u59d1\u4e3a\u751a\u4e48\u4f1a\u5982\u6b64\u60c5\u75f4\uff0c\u89c1\u4e86\u4e00\u4e2a\u7537\u5b50\u5c31\u8fd9\u822c\u98a0\u5012\u2026\u2026\u6211\u2026\u2026\u6211\u2026\u2026\u597d\u5427\uff0c\u6211\u4e0d\u8981\u4f60\u751a\u4e48\uff0c\u4f60\u8bb0\u5f97\u6211\u4e5f\u597d\uff0c\u5fd8\u4e86\u6211\u4e5f\u597d\u3002\u201d\u6389\u5934\u4fbf\u8d70\u51fa\u95e8\u53bb\u3002 \u3000\u3000\u9752\u9752\u5750\u5728\u5e8a\u4e0a\u6014\u6014\u53d1\u5446\uff0c\u4e0d\u660e\u767d\u5979\u662f\u751a\u4e48\u610f\u601d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u98d8\u7136\u4e0b\u5730\uff0c\u7b11\u9053\uff1a\u201c\u50bb\u59d1\u5a18\uff0c\u5979\u7231\u4e0a\u4f60\u5566\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u5979\u5f53\u4f60\u662f\u7537\u4eba\u5462\u3002\u201d \u3000\u3000\u9752\u9752\u56de\u60f3\u4f55\u94c1\u624b\u8fd9\u51e0\u65e5\u5bf9\u81ea\u5df1\u7684\u795e\u60c5\u8bf4\u8bdd\uff0c\u679c\u7136\u662f\u542b\u60c5\u8109\u8109\u7684\u6a21\u6837\u3002\u539f\u6765\u5979\u4e00\u89c1\u503e\u5fc3\uff0c\u795e\u667a\u80e1\u6d82\u4e86\u3002\u90a3\u4f55\u7ea2\u836f\u5219\u662f\u6ee1\u8154\u6028\u6bd2\uff0c\u6012\u6c14\u51b2\u5929\u3002\u8fd9\u4e24\u4e2a\u5973\u5b50\u672c\u6765\u90fd\u89c1\u591a\u8bc6\u5e7f\uff0c\u4f46\u4e00\u4e2a\u949f\u60c5\uff0c\u4e00\u4e2a\u6000\u6068\uff0c\u7adf\u90fd\u4f3c\u778e\u4e86\u773c\u4e00\u822c\uff0c\u518d\u4e5f\u6ca1\u7559\u795e\u81ea\u5df1\u662f\u5973\u626e\u7537\u88c5\uff0c\u4e0d\u89c9\u597d\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u529e\u5462\uff1f\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u5a36\u4e86\u8fd9\u4f4d\u4e94\u6bd2\u592b\u4eba\u7b97\u5566\uff01\u201d \u3000\u3000\u9752\u9752\u6b63\u5f85\u56de\u7b54\uff0c\u7a97\u683c\u4e00\u54cd\uff0c\u7126\u5b9b\u513f\u8dc3\u4e86\u8fdb\u6765\uff0c\u540e\u9762\u8ddf\u7740\u7f57\u7acb\u5982\uff0c\u9752\u9752\u8138\u8272\u4e00\u6c89\uff0c\u7b11\u5bb9\u987f\u655b\u3002\u7126\u5b9b\u513f\u5411\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u627f\u8499\u4f60\u9f0e\u529b\u76f8\u52a9\uff0c\u6211\u5927\u4ec7\u5df2\u62a5\uff0c\u660e\u513f\u4e00\u65e9\uff0c\u6211\u5c31\u56de\u91d1\u9675\u53bb\u5566\u3002\u6211\u7239\u7239\u5728\u65e5\uff0c\u5bf9\u4f60\u5341\u5206\u94a6\u4f69\u3002\u4f60\u53c8\u4f20\u4e86\u7f57\u5e08\u54e5\u72ec\u81c2\u5200\u6cd5\uff0c\u5c31\u5982\u662f\u4ed6\u5e08\u7236\u4e00\u822c\u3002\u6211\u4eec\u4fe9\u6709\u4e00\u4ef6\u4e8b\u6c42\u4f60\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u4e0d\u5fd9\uff0c\u54b1\u4eec\u5148\u51fa\u5bab\u53bb\u518d\u8bf4\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4e0d\u3002\u6211\u8981\u8bf7\u4f60\u4f5c\u4e3b\uff0c\u5c06\u6211\u8bb8\u914d\u7ed9\u7f57\u5e08\u54e5\u3002\u201d\u5979\u6b64\u8a00\u4e00\u51fa\uff0c\u8881\u627f\u5fd7\u548c\u9752\u9752\u56fa\u7136\u5403\u4e86\u4e00\u60ca\uff0c\u7f57\u7acb\u5982\u66f4\u662f\u60ca\u6115\u5f02\u5e38\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7684\u9053\uff1a\u201c\u5e08\u2026\u2026\u5e08\u59b9\uff0c\u4f60\u2026\u2026\u4f60\u8bf4\u751a\u4e48\uff1f\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4f60\u4e0d\u559c\u6b22\u6211\u4e48\uff1f\u201d\u7f57\u7acb\u5982\u6ee1\u8138\u80c0\u5f97\u901a\u7ea2\uff0c\u53ea\u662f\u8bf4\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u9752\u9752\u5fc3\u82b1\u6012\u653e\uff0c\u7591\u5fcc\u5c3d\u6d88\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u606d\u559c\u4e24\u4f4d\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u77e5\u9053\u7126\u5b9b\u513f\u662f\u4e3a\u4e86\u8868\u660e\u4e0e\u81ea\u5df1\u6e05\u767d\u65e0\u4ed6\uff0c\u624d\u4e0d\u60dc\u63d0\u51fa\u8981\u4e0b\u5ac1\u8fd9\u4e2a\u72ec\u81c2\u5e08\u54e5\uff0c\u90a3\u5168\u662f\u8981\u53bb\u9752\u9752\u7591\u5fc3\u3001\u4ee5\u62a5\u81ea\u5df1\u6069\u5fb7\u4e4b\u610f\uff0c\u4e0d\u7981\u597d\u751f\u611f\u6fc0\u3002\u9752\u9752\u8fd9\u65f6\u4e5f\u5df2\u660e\u767d\u4e86\u5979\u7684\u7528\u610f\uff0c\u9887\u4e3a\u5185\u6127\uff0c\u62c9\u7740\u7126\u5b9b\u513f\u7684\u624b\u9053\uff1a\u201c\u59b9\u5b50\uff0c\u6211\u5bf9\u4f60\u65e0\u793c\uff0c\u4f60\u522b\u89c1\u602a\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u9053\uff1a\u201c\u6211\u54ea\u91cc\u4f1a\u602a\u59ca\u59ca\uff1f\u201d\u60f3\u8d77\u521a\u624d\u6240\u53d7\u7684\u59d4\u5c48\uff0c\u4e0d\u89c9\u51c4\u7136\u4e0b\u6cea\u3002\u9752\u9752\u4e5f\u966a\u7740\u5979\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5ffd\u7136\u95e8\u5916\u811a\u6b65\u58f0\u53c8\u8d77\uff0c\u8fd9\u6b21\u6709\u4e03\u516b\u4e2a\u4eba\u3002\u8881\u627f\u5fd7\u4e00\u6253\u624b\u52bf\uff0c\u7f57\u7acb\u5982\u7eb5\u8fc7\u53bb\u63a8\u5f00\u4e86\u7a97\u683c\u3002 \u3000\u3000\u53ea\u542c\u4f55\u94c1\u624b\u5728\u95e8\u5916\u559d\u9053\uff1a\u201c\u5230\u5e95\u8c01\u662f\u6559\u4e3b\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u4f60\u4e0d\u4f9d\u6559\u89c4\u884c\u4e8b\uff0c\u54b1\u4eec\u62dc\u8fc7\u6559\u7956\uff0c\u53ea\u6709\u53e6\u7acb\u6559\u4e3b\u3002\u201d\u4e00\u4e2a\u7537\u4eba\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u662f\u672c\u6559\u5927\u4ec7\u4eba\uff0c\u6559\u4e3b\u4f60\u4f55\u5fc5\u5c3d\u62a4\u7740\u4ed6\uff1f \u3000\u3000\u8ba9\u90a3\u59d3\u8881\u7684\u5148\u6551\u6cbb\u4e86\u54b1\u4eec\u5144\u5f1f\uff0c\u54b1\u4eec\u518d\u8fd8\u4ed6\u4e00\u4e2a\u59d3\u590f\u7684\u6b7b\u5c0f\u5b50\u3002\u4f60\u53ea\u7b54\u5e94\u8fd8\u4eba\uff0c\u53ef\u6ca1\u8bf4\u6b7b\u7684\u6d3b\u7684\u3002\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e0d\u8bb8\u4f60\u4eec\u8fdb\u53bb\uff0c\u8c01\u6562\u8fc7\u6765\uff1f\u201d\u53e6\u4e00\u4e2a\u7537\u5b50\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5148\u6599\u7406\u4e86\u90a3\u5c0f\u5b50\uff0c\u518d\u6765\u7b97\u81ea\u5df1\u7684\u5e10\u3002\u201d\u811a\u6b65\u58f0\u54cd\uff0c\u5954\u5411\u95e8\u8fb9\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u60e8\u53eb\u4e00\u58f0\uff0c\u4e00\u4eba\u5012\u5728\u5730\u4e0b\uff0c\u60f3\u662f\u88ab\u4f55\u94c1\u624b\u4f24\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6325\u624b\u8981\u4e09\u4eba\u8d76\u5feb\u51fa\u5bab\u3002\u7f57\u7acb\u5982\u5f53\u5148\u8dc3\u51fa\u7a97\u53bb\u3002\u7126\u5b9b\u513f\u548c\u9752\u9752\u4e5f\u8ddf\u7740\u8dc3\u51fa\u3002 \u3000\u3000\u8fd9\u65f6\u95e8\u5916\u5175\u5203\u76f8\u4ea4\uff0c\u4e94\u6bd2\u6559\u7684\u6559\u4f17\u7adf\u81ea\u5185\u53db\uff0c\u548c\u6559\u4e3b\u6597\u4e86\u8d77\u6765\u3002\u6597\u4e0d\u591a\u65f6\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u6709\u4eba\u8e22\u5f00\u623f\u95e8\uff0c\u62a2\u4e86\u8fdb\u6765\u3002\u8881\u627f\u5fd7\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u7a9c\u51fa\u7a97\u5916\u3002\u90a3\u4eba\u53ea\u89c1\u5230\u8881\u627f\u5fd7\u7684\u80cc\u5f71\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u6765\uff0c\u5feb\u6765\uff01\u90a3\u5c0f\u5b50\u8dd1\u5566\uff01\u201d\u4f55\u94c1\u624b\u4e5f\u662f\u4e00\u60ca\uff0c\u5f53\u5373\u7f62\u624b\u4e0d\u6597\uff0c\u5954\u8fdb\u623f\u6765\uff0c\u53ea\u89c1\u7a97\u6237\u5927\u5f00\uff0c\u5e8a\u4e0a\u5df2\u7a7a\uff0c\u5f53\u5373\u8ddf\u7740\u51fa\u7a97\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u5f71\u7a9c\u5165\u4e86\u524d\u9762\u6811\u4e1b\uff0c\u5fd9\u8ddf\u8e2a\u8fc7\u53bb\u3002\u5979\u60f3\u8ffd\u4e0a\u53bb\u62a4\u9001\u9752\u9752\u51fa\u5bab\uff0c\u4ee5\u514d\u906d\u5230\u81ea\u5df1\u624b\u4e0b\u7684\u6bd2\u624b\uff0c\u53c8\u6216\u662f\u4e3a\u5bab\u4e2d\u4f8d\u536b\u6240\u4f24\u3002\u4e94\u6bd2\u6559\u4f17\u8ddf\u7740\u8ffd\u6765\u3002\u4f17\u4eba\u8ffd\u5f97\u867d\u7d27\uff0c\u4f46\u5747\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u751f\u6015\u7981\u5bab\u4e4b\u5185\uff0c\u60ca\u52a8\u4e86\u65c1\u4eba\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4f55\u94c1\u624b\u7b49\u7d27\u8ffd\u4e0d\u820d\uff0c\u5fc3\u60f3\u9752\u9752\u7b49\u8fd9\u65f6\u5c1a\u672a\u8fdc\u53bb\uff0c\u4e8e\u662f\u4e0d\u5373\u4e0d\u79bb\u7684\u5f15\u7740\u4f17\u4eba\u8ffd\u9010\u81ea\u5df1\uff0c\u5728\u5fa1\u82b1\u56ed\u4e2d\u515c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u7b97\u6765\u4f30\u8ba1\u9752\u9752\u7b49\u4e09\u4eba\u5df2\u7ecf\u51fa\u5bab\uff0c\u773c\u89c1\u524d\u9762\u6709\u5ea7\u5bab\u6bbf\uff0c\u5f53\u4e0b\u76f4\u7a9c\u5165\u5185\u3002\u4e00\u8e0f\u8fdb\u95e8\uff0c\u4fbf\u89c9\u9635\u9635\u82b1\u9999\uff0c\u987a\u624b\u63a8\u5f00\u4e86\u4e00\u6247\u95e8\uff0c\u8eb2\u5728\u95e8\u540e\u3002 \u3000\u3000\u4ed6\u5b9a\u795e\u77a7\u8fd9\u5c4b\u5b50\u65f6\uff0c\u4e0d\u7531\u5f97\u8033\u6839\u4e00\u70ed\u3002\u539f\u6765\u623f\u91cc\u9526\u5e0f\u7ee3\u88ab\uff0c\u73e0\u5e18\u8f6f\u5e10\uff0c\u9e45\u9ec4\u8272\u7684\u5730\u6be1\u4e0a\u7ec7\u7740\u5927\u6735\u7ea2\u8272\u73ab\u7470\uff0c\u7a97\u8fb9\u684c\u4e0a\u653e\u7740\u5973\u5b50\u7528\u7684\u68b3\u5986\u7269\u54c1\uff0c\u5230\u5904\u662f\u7cbe\u5de7\u7684\u6446\u8bbe\uff0c\u770b\u6765\u662f\u7687\u5e1d\u4e00\u540d\u5ad4\u5983\u7684\u5bdd\u5bab\uff0c\u5fc3\u60f3\u5728\u8fd9\u91cc\u53ef\u4e0d\u5927\u59a5\u5f53\uff0c\u6b63\u8981\u9000\u51fa\uff0c\u5ffd\u542c\u95e8\u5916\u811a\u6b65\u7ec6\u788e\uff0c\u4f20\u6765\u51e0\u4e2a\u5c11\u5973\u7684\u7b11\u8bed\u4e4b\u58f0\u3002\u5bfb\u601d\uff1a\u5982\u8fd9\u65f6\u95ef\u51fa\uff0c\u6b63\u597d\u9047\u4e0a\uff0c\u58f0\u5f20\u8d77\u6765\uff0c\u5bab\u4e2d\u5927\u4e71\uff0c\u66f9\u5316\u6df3\u7684\u5978\u8c0b\u52bf\u5fc5\u5ef6\u6401\uff0c\u4e0d\u514d\u53e6\u6709\u82b1\u6837\uff0c\u5f53\u4e0b\u95ea\u8eab\u9690\u5728\u4e00\u5ea7\u753b\u7740\u7f8e\u4eba\u7261\u4e39\u56fe\u7684\u5c4f\u98ce\u4e4b\u540e\u3002 \u3000\u3000\u623f\u95e8\u5f00\u5904\uff0c\u542c\u58f0\u97f3\u662f\u56db\u540d\u5bab\u5973\u5f15\u7740\u4e00\u540d\u5973\u5b50\u8fdb\u6765\u3002\u4e00\u540d\u5bab\u5973\u9053\uff1a\u201c\u6bbf\u4e0b\u662f\u5b89\u606f\u5462\uff0c\u8fd8\u662f\u518d\u77a7\u4e00\u4f1a\u4e66\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u516c\u4e3b\u7684\u5bdd\u5bab\u3002\u8fd9\u5c31\u5feb\u70b9\u513f\u7761\u5427\uff0c\u522b\u77a7\u751a\u4e48\u52b3\u751a\u5b50\u7684\u4e66\u5566\uff01\u201d \u3000\u3000\u90a3\u516c\u4e3b\u55ef\u4e86\u4e00\u58f0\uff0c\u5750\u5728\u69bb\u4e0a\uff0c\u58f0\u97f3\u4e2d\u900f\u7740\u5341\u5206\u5a07\u6175\u3002\u4e00\u540d\u5bab\u5973\u9053\uff1a\u201c\u70e7\u4e0a\u4e9b\u513f\u9999\u5427\uff1f\u201d\u516c\u4e3b\u53c8\u55ef\u4e86\u4e00\u58f0\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u9752\u70df\u7ec6\u7ec6\uff0c\u751c\u9999\u5e7d\u5e7d\uff0c\u8881\u627f\u5fd7\u53ea\u89c9\u773c\u9967\u9aa8\u5026\uff0c\u9887\u6709\u56f0\u610f\u3002 \u3000\u3000\u90a3\u516c\u4e3b\u9053\uff1a\u201c\u628a\u6211\u7684\u753b\u7b14\u62ff\u51fa\u6765\uff0c\u4f60\u4eec\u90fd\u51fa\u53bb\u5427\u3002\u201d\u8881\u627f\u5fd7\u5fae\u89c9\u8bb6\u5f02\uff1a\u201c\u600e\u4e48\u8fd9\u58f0\u97f3\u597d\u719f\uff1f\u201d\u6697\u6697\u7740\u6025\uff0c\u5fc3\u60f3\u5979\u753b\u8d77\u753b\u6765\uff0c\u8c01\u77e5\u8981\u753b\u4e0a\u591a\u5c11\u65f6\u5019\u3002 \u3000\u3000\u4f17\u5bab\u5973\u6446\u597d\u4e39\u9752\u753b\u5177\uff0c\u5411\u516c\u4e3b\u9053\u4e86\u665a\u5b89\uff0c\u884c\u793c\u9000\u51fa\u623f\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u623f\u4e2d\u5bc2\u9759\u65e0\u58f0\uff0c\u53ea\u662f\u5076\u6709\u9999\u7089\u4e2d\u6a80\u9999\u8f7b\u8f7b\u7684\u62c6\u88c2\u4e4b\u97f3\uff0c\u8881\u627f\u5fd7\u66f4\u52a0\u4e0d\u6562\u52a8\u5f39\u3002\u53ea\u542c\u90a3\u516c\u4e3b\u957f\u53f9\u4e00\u58f0\uff0c\u4f4e\u58f0\u541f\u9053\uff1a\u201c\u9752\u9752\u5b50\u887f\uff0c\u60a0\u60a0\u6211\u5fc3\u3002\u7eb5\u6211\u4e0d\u5f80\uff0c\u5b50\u5b81\u4e0d\u55e3\u97f3\uff1f \u3000\u3000\u201c\u9752\u9752\u5b50\u4f69\uff0c\u60a0\u60a0\u6211\u601d\u3002\u7eb5\u6211\u4e0d\u5f80\uff0c\u5b50\u5b81\u4e0d\u6765\uff1f \u3000\u3000\u201c\u6311\u516e\u8fbe\u516e\uff0c\u5728\u57ce\u9619\u516e\u3002\u4e00\u65e5\u4e0d\u89c1\uff0c\u5982\u4e09\u6708\u516e\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u5979\u58f0\u97f3\u5a07\u67d4\u5b9b\u8f6c\uff0c\u81ea\u662f\u4e00\u4e2a\u5e74\u7eaa\u6781\u8f7b\u7684\u5c11\u5973\uff0c\u4ed6\u867d\u4e0d\u61c2\u8fd9\u9996\u53e4\u8bd7\u7684\u539f\u610f\uff0c\u4f46\u542c\u5230\u201c\u4e00\u65e5\u4e0d\u89c1\uff0c\u5982\u4e09\u6708\u516e\u201d\u90a3\u4e00\u53e5\uff0c\u4e5f\u77e5\u662f\u76f8\u601d\u4e4b\u8bcd\uff0c\u540c\u65f6\u8d8a\u52a0\u89c9\u5f97\u5979\u8bed\u97f3\u719f\u6089\uff0c\u5bfb\u601d\u534a\u664c\uff0c\u4e0d\u89c9\u597d\u7b11\uff1a\u201c\u6211\u662f\u6c5f\u6e56\u8349\u83bd\uff0c\u751f\u5e73\u6ca1\u8fdb\u8fc7\u4eac\u5e08\uff0c\u53c8\u600e\u4f1a\u89c1\u8fc7\u91d1\u679d\u7389\u53f6\u7684\u516c\u4e3b\uff1f\u603b\u662f\u5979\u53e3\u97f3\u8ddf\u6211\u76f8\u8bc6\u4e4b\u4eba\u6709\u4e9b\u8fd1\u4f3c\u7f62\u5566\uff01\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u516c\u4e3b\u5df2\u8d70\u8fd1\u6848\u8fb9\uff0c\u53ea\u542c\u7eb8\u58f0\u7ab8\u7aa3\uff0c\u8c03\u6731\u7814\u9752\uff0c\u4f5c\u8d77\u753b\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8001\u5927\u7eb3\u95f7\uff0c\u7ec6\u770b\u623f\u4e2d\uff0c\u623f\u95e8\u659c\u5bf9\u516c\u4e3b\uff0c\u5df2\u7ecf\u63a9\u4e0a\uff0c\u7a97\u524d\u73e0\u5e18\u4f4e\u5782\uff0c\u9664\u4e86\u786c\u95ef\uff0c\u51b3\u8ba1\u8d70\u4e0d\u51fa\u53bb\u3002\u8fc7\u4e86\u826f\u4e45\uff0c\u53ea\u542c\u516c\u4e3b\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u4f4e\u58f0\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u518d\u753b\u4e24\u4e09\u5929\uff0c\u8fd9\u753b\u5c31\u53ef\u5b8c\u5de5\u5566\u3002\u6211\u5929\u5929\u8fd9\u822c\u795e\u9b42\u98a0\u5012\u7684\u60f3\u7740\u4f60\uff0c\u4f60\u4e5f\u6709\u4e00\u65f6\u7247\u523b\u7684\u6302\u5ff5\u7740\u6211\u4e48\uff1f\u201d\u8bf4\u7740\u7ad9\u4e86\u8d77\u6765\uff0c\u628a\u753b\u653e\u5728\u6905\u4e0a\uff0c\u628a\u6905\u5b50\u642c\u5230\u5e8a\u524d\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u966a\u7740\u6211\uff01\u201d\u5bbd\u8863\u89e3\u5e26\uff0c\u4e0a\u5e8a\u5b89\u7761\u3002 \u3000\u3000\u8881\u627f\u5fd7\u597d\u5947\u5fc3\u8d77\uff0c\u60f3\u77a7\u77a7\u516c\u4e3b\u7684\u610f\u4e2d\u4eba\u662f\u600e\u751f\u6a21\u6837\uff0c\u63a2\u5934\u4e00\u671b\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u753b\u4e2d\u8096\u50cf\u7adf\u7136\u4f3c\u8db3\u4e86\u4ed6\u81ea\u5df1\uff0c\u518d\u5b9a\u795e\u7ec6\u770b\uff0c\u53ea\u89c1\u753b\u4e2d\u4eba\u8eab\u7a7f\u6c94\u9633\u9752\u957f\u886b\uff0c\u7cfb\u4e00\u6761\u5c0f\u7f38\u9752\u8170\u5e26\uff0c\u51dd\u76ee\u5fae\u7b11\uff0c\u6d53\u7709\u5927\u773c\uff0c\u4e0b\u5df4\u5c16\u524a\uff0c\u53ef\u4e0d\u662f\u81ea\u5df1\u662f\u8c01\uff1f\u53ea\u4e0d\u8fc7\u753b\u4e2d\u4eba\u5374\u6bd4\u81ea\u5df1\u4fca\u7f8e\u4e86\u51e0\u5206\uff0c\u81ea\u5df1\u539f\u6765\u7684\u6c5f\u6e56\u8349\u83bd\u4e4b\u6c14\uff0c\u7adf\u7ed9\u6539\u6210\u4e86\u7389\u9762\u6731\u5507\u7684\u4fca\u6717\u98ce\u91c7\uff0c\u4f46\u5bb9\u8c8c\u6bd5\u7adf\u65e0\u5f02\uff0c\u8170\u95f4\u6240\u60ac\u7684\u5f2f\u8eab\u86c7\u5251\uff0c\u91d1\u5149\u707f\u7136\uff0c\u66f4\u662f\u5929\u4e0b\u53ea\u6b64\u4e00\u5251\uff0c\u66f4\u65e0\u7b2c\u4e8c\u53e3\u3002\u4ed6\u4e07\u6599\u4e0d\u5230\u516c\u4e3b\u6240\u753b\u4e4b\u50cf\u4fbf\u662f\u81ea\u5df1\uff0c\u4e0d\u7531\u5f97\u60ca\u8be7\u767e\u7aef\uff0c\u4e0d\u7981\u8f7b\u8f7b\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u90a3\u516c\u4e3b\u542c\u5f97\u8eab\u540e\u6709\u4eba\uff0c\u4f38\u624b\u62d4\u4e0b\u5934\u4e0a\u7389\u7c2a\uff0c\u4e5f\u4e0d\u56de\u8eab\uff0c\u987a\u624b\u5f80\u58f0\u97f3\u6765\u5904\u63b7\u51fa\u3002\u8881\u627f\u5fd7\u53ea\u542c\u4e00\u58f0\u52b2\u98ce\uff0c\u7389\u7c2a\u5df2\u5230\u9762\u95e8\uff0c\u5f53\u5373\u4f38\u624b\u634f\u4f4f\u3002\u90a3\u516c\u4e3b\u8f6c\u8fc7\u8eab\u6765\u3002\u4e24\u4eba\u4e00\u671d\u76f8\uff0c\u90fd\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u539f\u6765\u516c\u4e3b\u975e\u522b\uff0c\u7adf\u7136\u4fbf\u662f\u7a0b\u9752\u7af9\u7684\u5c0f\u5f92\u963f\u4e5d\u3002\u90a3\u65e5\u8881\u627f\u5fd7\u867d\u53d1\u89c9\u5979\u6709\u7687\u5bab\u4f8d\u536b\u968f\u4ece\u4fdd\u62a4\uff0c\u6599\u77e5\u5fc5\u975e\u5e38\u4eba\uff0c\u5374\u54ea\u60f3\u5230\u7adf\u662f\u516c\u4e3b\uff1f \u3000\u3000\u963f\u4e5d\u4e4d\u89c1\u8881\u627f\u5fd7\uff0c\u970e\u65f6\u95f4\u8138\u4e0a\u5168\u65e0\u8840\u8272\uff0c\u8eab\u5b50\u98a4\u52a8\uff0c\u4f38\u624b\u6276\u4f4f\u6905\u80cc\uff0c\u4f3c\u6b32\u6655\u5012\uff0c\u968f\u5373\u4e00\u9635\u7ea2\u4e91\uff0c\u7f69\u4e0a\u53cc\u988a\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u884c\u4e86\u4e00\u793c\u9053\uff1a\u201c\u5c0f\u4eba\u7f6a\u8be5\u4e07\u6b7b\uff0c\u95ef\u5165\u516c\u4e3b\u6bbf\u4e0b\u5bdd\u5bab\u3002\u201d\u963f\u4e5d\u8138\u4e0a\u53c8\u662f\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u8bf7\u5750\u4e0b\u8bf4\u8bdd\u3002\u201d\u5ffd\u5730\u60ca\u89c9\u957f\u8863\u5df2\u7ecf\u8131\u4e0b\uff0c\u5fd9\u62c9\u8fc7\u62ab\u4e0a\u3002 \u3000\u3000\u95e8\u5916\u5bab\u5973\u8f7b\u8f7b\u5f39\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\u53eb\u4eba\u5417\uff1f\u201d\u963f\u4e5d\u5fd9\u9053\uff1a\u201c\u6ca1\u2026\u2026\u6ca1\u6709\uff0c\u6211\u770b\u4e66\u5462\u3002\u4f60\u4eec\u90fd\u53bb\u7761\u5427\uff0c\u4e0d\u7528\u5728\u8fd9\u91cc\u4f8d\u5019\uff01\u201d \u3000\u3000\u5bab\u5973\u9053\uff1a\u201c\u662f\u3002\u516c\u4e3b\u8bf7\u65e9\u5b89\u606f\u5427\u3002\u201d \u3000\u3000\u963f\u4e5d\u5411\u8881\u627f\u5fd7\u6253\u4e2a\u624b\u52bf\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u89c1\u4ed6\u76ee\u4e0d\u8f6c\u77ac\u7684\u671b\u7740\u753b\u50cf\uff0c\u4e0d\u7981\u5927\u7f9e\uff0c\u5fd9\u62a2\u8fc7\u53bb\u628a\u6905\u5b50\u63a8\u5728\u4e00\u65c1\u3002\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e24\u4eba\u8c01\u4e5f\u8bf4\u4e0d\u51fa\u751a\u4e48\u8bdd\u6765\uff0c\u56db\u76ee\u4ea4\u6295\uff0c\u963f\u4e5d\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8881\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8bc6\u5f97\u4e94\u6bd2\u6559\u7684\u4eba\u4e48\uff1f\u201d\u963f\u4e5d\u70b9\u5934\u9053\uff1a\u201c\u66f9\u516c\u516c\u8bf4\uff0c\u674e\u95ef\u6d3e\u4e86\u8bb8\u591a\u523a\u5ba2\u6765\u4eac\u5e08\u6270\u4e71\uff0c\u56e0\u6b64\u4ed6\u8bf7\u4e86\u4e00\u6279\u6b66\u6797\u597d\u624b\uff0c\u8fdb\u5bab\u62a4\u9a7e\uff0c\u4e94\u6bd2\u6559\u4e5f\u5728\u5176\u5185\u3002\u542c\u8bf4\u4ed6\u4eec\u7684\u6559\u4e3b\u4f55\u94c1\u624b\u6b66\u529f\u751a\u662f\u4e86\u5f97\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u60a8\u5e08\u7236\u7a0b\u8001\u592b\u5b50\u7ed9\u4ed6\u4eec\u6253\u4f24\u4e86\uff0c\u6bbf\u4e0b\u53ef\u77e5\u9053\u4e48\uff1f\u201d\u963f\u4e5d\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u751a\u4e48\uff1f\u4ed6\u4eec\u4e3a\u751a\u4e48\u4f24\u6211\u5e08\u7236\uff1f\u4ed6\u53d7\u7684\u4f24\u5389\u5bb3\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u81f4\u4e0d\u788d\u4e8b\u4e86\u3002\u201d\u7ad9\u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u591c\u6df1\u4e0d\u4fbf\u591a\u8c08\uff0c\u6211\u4eec\u4f4f\u5728\u6b63\u6761\u5b50\u80e1\u540c\uff0c\u660e\u513f\u6bbf\u4e0b\u80fd\u4e0d\u80fd\u9a7e\u4e34\uff0c\u6765\u77a7\u77a7\u60a8\u5e08\u7236\uff1f\u201d \u3000\u3000\u963f\u4e5d\u9053\uff1a\u201c\u597d\u7684\u3002\u201d\u5fae\u4e00\u6c89\u541f\uff0c\u8138\u4e0a\u53c8\u662f\u7ea2\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5192\u9669\u8fdb\u5bab\u6765\u77a7\u6211\uff0c\u6211\u2026\u2026\u6211\u662f\u5f88\u611f\u6fc0\u7684\u2026\u2026\u201d\u795e\u60c5\u817c\u8146\uff0c\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u4f4e\uff1a\u201c\u4f60\u65e2\u7136\u89c1\u5230\u6211\u753b\u4f60\u7684\u8096\u50cf\uff0c\u6211\u7684\u2026\u2026\u5fc3\u4e8b\u2026\u2026\u4f60\u2026\u2026\u4f60\u81ea\u7136\u4e5f\u660e\u767d\u4e86\u2026\u2026\u201d\u8bf4\u5230\u6700\u540e\u8fd9\u53e5\u65f6\uff0c\u58f0\u7ec6\u5982\u868a\uff0c\u5df2\u51e0\u4e0d\u53ef\u95fb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u7cdf\u7cd5\uff0c\u5979\u753b\u6211\u8096\u50cf\uff0c\u770b\u6765\u5bf9\u6211\u751f\u4e86\u7231\u6155\u4e4b\u610f\uff0c\u8fd9\u65f6\u66f4\u8bef\u4f1a\u6211\u5165\u5bab\u6765\u662f\u77a7\u5979\uff0c\u8fd9\u53ef\u5f97\u5206\u8bf4\u660e\u767d\u3002\u201d\u53ea\u542c\u5979\u53c8\u9053\uff1a\u201c\u81ea\u4ece\u90a3\u65e5\u5728\u5c71\u4e1c\u9053\u4e0a\u89c1\u9762\uff0c\u4f60\u963b\u6321\u891a\u7ea2\u67f3\uff0c\u4ee4\u4ed6\u4e0d\u80fd\u4f24\u6211\uff0c\u6211\u5c31\u5e38\u5e38\u5ff5\u7740\u4f60\u7684\u6069\u5fb7\u2026\u2026\u4f60\u77a7\u8fd9\u8096\u50cf\u753b\u5f97\u8fd8\u50cf\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u70b9\u5934\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u6211\u8fdb\u5bab\u6765\u662f\u2026\u2026\u201d\u963f\u4e5d\u62e6\u4f4f\u4ed6\u7684\u8bdd\u5934\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u522b\u53eb\u6211\u6bbf\u4e0b\uff0c\u6211\u4e5f\u4e0d\u53eb\u4f60\u8881\u76f8\u516c\u3002\u4f60\u521d\u6b21\u8bc6\u5f97\u6211\u65f6\uff0c\u6211\u662f\u963f\u4e5d\uff0c\u90a3\u4e48\u6211\u6c38\u8fdc\u5c31\u662f\u963f\u4e5d\u3002\u6211\u542c\u9752\u59ca\u59ca\u53eb\u4f60\u5927\u54e5\uff0c\u5fc3\u91cc\u5e38\u60f3\uff0c\u54ea\u4e00\u5929\u6211\u4e5f\u80fd\u53eb\u4f60\u5927\u54e5\uff0c\u90a3\u624d\u597d\u5462\u3002\u6211\u4e00\u751f\u4e0b\u6765\uff0c\u94a6\u5929\u76d1\u6b63\u7ed9\u6211\u7b97\u547d\uff0c\u8bf4\u6211\u8981\u662f\u5728\u7687\u5bab\u91cc\u5a07\u751f\u60ef\u517b\uff0c\u5fc5\u5b9a\u592d\u6298\uff0c\u56e0\u6b64\u7236\u7687\u624d\u8bb8\u6211\u5230\u5916\u9762\u4e71\u95ef\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u8ddf\u7740\u7a0b\u8001\u592b\u5b50\u5b66\u529f\u592b\uff0c\u53c8\u968f\u7740\u4ed6\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u3002\u201d\u963f\u4e5d\u9053\uff1a\u201c\u6211\u5728\u5916\u9762\u89c1\u8bc6\u591a\u4e86\uff0c\u77e5\u9053\u8001\u767e\u59d3\u5b9e\u5728\u82e6\u5f97\u5f88\u3002\u6211\u867d\u5e38\u628a\u5bab\u91cc\u7684\u91d1\u94f6\u62ff\u51fa\u53bb\u65bd\u820d\uff0c\u53c8\u54ea\u91cc\u6551\u5f97\u4e86\u8fd9\u8bb8\u591a\u3002\u201d\u8881\u627f\u5fd7\u542c\u5979\u4f53\u5ff5\u6c11\u95f4\u75be\u82e6\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4f60\u8be5\u529d\u529d\u7687\u4e0a\uff0c\u8bf7\u4ed6\u591a\u884c\u4ec1\u653f\u3002\u8001\u767e\u59d3\u8863\u6696\u98df\u8db3\uff0c\u5929\u4e0b\u81ea\u7136\u592a\u5e73\u4e86\u3002\u201d\u963f\u4e5d\u53f9\u9053\uff1a\u201c\u7236\u7687\u80af\u542c\u4eba\u5bb6\u8bdd\uff0c\u65e9\u5c31\u597d\u5566\u3002\u4ed6\u5c31\u662f\u7ed9\u5978\u81e3\u8499\u853d\uff0c\u8fd8\u81ea\u4ee5\u4e3a\u662f\u3002\u4ed6\u8001\u662f\u8bf4\u6587\u6b66\u767e\u5b98\u4e0d\u80af\u51fa\u529b\uff0c\u6d41\u5bc7\u6740\u5f97\u592a\u5c11\u3002\u6211\u8ddf\u4ed6\u8bf4\uff1a\u6d41\u5bc7\u5c31\u662f\u767e\u59d3\uff0c\u53ea\u8981\u6709\u996d\u5403\uff0c\u65e5\u5b50\u8fc7\u5f97\u4e0b\u53bb\uff0c\u6d41\u5bc7\u5c31\u53d8\u6210\u4e86\u597d\u767e\u59d3\uff0c\u5426\u5219\u597d\u767e\u59d3\u4e5f\u7ed9\u903c\u6210\u4e86\u6d41\u5bc7\u3002\u6211\u8bf4\uff1a\u2018\u7236\u7687\uff0c\u4f60\u603b\u4e0d\u80fd\u628a\u5929\u4e0b\u767e\u59d3\u5c3d\u6570\u6740\u4e86\uff01\u2019\u4ed6\u542c\u6211\u8fd9\u4e48\u8bf4\uff0c\u767b\u65f6\u5927\u53d1\u813e\u6c14\uff0c\u8bf4\uff1a\u2018\u4eba\u4eba\u90fd\u53cd\u6211\uff0c\u8fde\u6211\u7684\u4eb2\u751f\u5973\u513f\u4e5f\u53cd\u6211\uff01\u2019\u6211\u4fbf\u4e0d\u6562\u518d\u8bf4\u4e86\uff0c\u5509\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u89c1\u5f97\u4e8b\u591a\uff0c\u89c1\u8bc6\u53cd\u6bd4\u7687\u4e0a\u660e\u767d\u2026\u2026\u201d\u5bfb\u601d\uff1a\u201c\u8981\u4e0d\u8981\u628a\u66f9\u5316\u6df3\u7684\u5978\u8c0b\u5bf9\u5979\u8bf4\uff1f\u201d \u3000\u3000\u963f\u4e5d\u5ffd\u95ee\uff1a\u201c\u7a0b\u8001\u592b\u5b50\u8bf4\u8fc7\u6211\u7684\u4e8b\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ed6\u8bf4\u66fe\u7acb\u8fc7\u91cd\u8a93\uff0c\u4e0d\u80fd\u6cc4\u6f0f\u4f60\u7684\u8eab\u4e16\u3002\u6211\u5f53\u65f6\u53ea\u9053\u7275\u8fde\u5230\u6c5f\u6e56\u4e0a\u7684\u6069\u6028\u9690\u79d8\uff0c\u8bf4\u751a\u4e48\u4e5f\u60f3\u4e0d\u5230\u4f60\u7adf\u662f\u516c\u4e3b\u3002\u201d\u963f\u4e5d\u9053\uff1a\u201c\u7a0b\u5e08\u7236\u672c\u662f\u7236\u7687\u7684\u4f8d\u536b\u3002\u6211\u5c0f\u65f6\u5019\u8d2a\u73a9\uff0c\u66fe\u8ddf\u4ed6\u5b66\u6b66\u3002\u4ed6\u4e0d\u77e5\u600e\u7684\u72af\u4e86\u7f6a\uff0c\u7236\u7687\u53eb\u4eba\u7ed1\u4e86\u8981\u6740\uff0c\u6211\u534a\u591c\u91cc\u6084\u6084\u53bb\u653e\u4e86\u4ed6\u3002 \u3000\u3000\u540e\u6765\u6211\u51fa\u5bab\u6253\u730e\uff0c\u53c8\u8ddf\u4ed6\u76f8\u9047\uff0c\u90a3\u65f6\u4ed6\u5df2\u505a\u4e86\u9752\u7af9\u5e2e\u7684\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u90a3\u65e5\u7a0b\u8001\u592b\u5b50\u8bf4\u4ed6\u884c\u523a\u7687\u5e1d\u88ab\u64d2\uff0c\u5f97\u4eba\u76f8\u6551\u3002\u539f\u6765\u662f\u5979\u6551\u7684\u3002\u201d\u963f\u4e5d\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u600e\u4e48\u53c8\u8ddf\u4e94\u6bd2\u6559\u7684\u4eba\u7ed3\u4ec7\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u6b63\u60f3\u8bf4\uff1a\u201c\u4e94\u6bd2\u6559\u60f3\u5bb3\u4f60\u7239\u7239\uff0c\u5fc5\u662f\u63a2\u77e5\u4e86\u7a0b\u8001\u592b\u5b50\u8ddf\u4f60\u7684\u6e0a\u6e90\uff0c\u6015\u4ed6\u574f\u4e86\u5927\u4e8b\uff0c\u56e0\u6b64\u8981\u5148\u9664\u4e86\u4ed6\u3002\u201d\u731b\u62ac\u5934\u89c1\u7ea2\u70db\u77ed\u4e86\u4e00\u5927\u622a\uff0c\u5fc3\u60f3\u65f6\u673a\u6025\u8feb\uff0c\u600e\u5730\u8ddf\u5979\u8bf4\u4e86\u8fd9\u8bb8\u591a\u8bdd\uff0c\u5fd9\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u7684\u8bdd\uff0c\u660e\u5929\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u963f\u4e5d\u8138\u4e00\u7ea2\uff0c\u4f4e\u4e0b\u5934\u6765\u7f13\u7f13\u70b9\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u5ffd\u7136\u6709\u4eba\u6025\u901f\u62cd\u95e8\uff0c\u51e0\u4e2a\u4eba\u540c\u58f0\u53eb\u9053\uff1a\u201c\u6bbf\u4e0b\u8bf7\u5f00\u95e8\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u516b\u56de\u3000\u6731\u989c\u7f79\u5b9d\u5251 \u9ed1\u7532\u5165\u540d\u90fd",content:(0,t.jsx)(t.Fragment,{children:"\u963f\u4e5d\u5403\u4e86\u4e00\u60ca\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u751a\u4e48\u4e8b\uff1f\u201d\u4e00\u540d\u5bab\u5973\u53eb\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60\u6ca1\u4e8b\u4e48\uff1f\u201d\u963f\u4e5d\u9053\uff1a\u201c\u6211\u7761\u5566\uff0c\u6709\u751a\u4e48\u4e8b\uff1f\u201d\u90a3\u5bab\u5973\u9053\uff1a\u201c\u6709\u4eba\u89c1\u5230\u523a\u5ba2\u6df7\u8fdb\u4e86\u54b1\u4eec\u5bdd\u5bab\u6765\u3002\u201d\u963f\u4e5d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u751a\u4e48\u523a\u5ba2\uff1f\u201d\u53e6\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u8ba9\u5974\u5a62\u4eec\u8fdb\u6765\u77a7\u77a7\u5427\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u5728\u963f\u4e5d\u8033\u8fb9\u4f4e\u58f0\u9053\uff1a\u201c\u4f55\u94c1\u624b\uff01\u201d\u963f\u4e5d\u9ad8\u58f0\u9053\uff1a\u201c\u82e5\u6709\u523a\u5ba2\uff0c\u6211\u8fd8\u80fd\u8fd9\u4e48\u5b89\u5b89\u7a33\u7a33\u7684\u4e48\uff1f\u5feb\u8d70\uff0c\u522b\u5728\u8fd9\u91cc\u80e1\u95f9\uff01\u201d \u3000\u3000\u95e8\u5916\u4f17\u4eba\u542c\u516c\u4e3b\u53d1\u4e86\u813e\u6c14\uff0c\u4e0d\u6562\u518d\u8bf4\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8f7b\u8f7b\u8d70\u5230\u7a97\u8fb9\uff0c\u63ed\u5f00\u7a97\u5e18\u4e00\u89d2\uff0c\u4fbf\u60f3\u7a9c\u51fa\u623f\u53bb\uff0c\u624b\u4e00\u52a8\uff0c\u4e00\u9635\u706b\u5149\u8000\u773c\uff0c\u7a97\u5916\u7adf\u5b88\u7740\u5341\u591a\u540d\u624b\u6267\u706b\u628a\u7684\u592a\u76d1\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u8981\u95ef\u51fa\uff0c\u6709\u8c01\u80fd\u6321\uff1f\u4f46\u8fd9\u4e00\u6765\u53ef\u6c61\u4e86\u516c\u4e3b\u7684\u540d\u58f0\uff0c\u4e07\u4e07\u4f7f\u4e0d\u5f97\u3002\u201d\u5f53\u5373\u9000\u56de\u6765\u8f7b\u58f0\u5bf9\u963f\u4e5d\u8bf4\u4e86\u3002 \u3000\u3000\u963f\u4e5d\u79c0\u7709\u4e00\u8e59\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u6015\uff0c\u5728\u8fd9\u91cc\u591a\u5f85\u4e00\u4f1a\u513f\u597d\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u53ea\u5f97\u53c8\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u53c8\u6709\u4eba\u62cd\u95e8\u3002\u963f\u4e5d\u5389\u58f0\u9053\uff1a\u201c\u5e72\u751a\u4e48\uff1f\u201d\u8fd9\u6b21\u56de\u7b54\u7684\u7adf\u662f\u66f9\u5316\u6df3\u7684\u58f0\u97f3\uff0c\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u542c\u8bf4\u6709\u523a\u5ba2\u8fdb\u5bab\uff0c\u5f88\u4e0d\u653e\u5fc3\uff0c\u547d\u5974\u5a62\u6765\u5411\u6bbf\u4e0b\u95ee\u5b89\u3002\u201d\u963f\u4e5d\u9053\uff1a\u201c\u4e0d\u6562\u52b3\u52a8\u66f9\u516c\u516c\u3002 \u3000\u3000\u4f60\u8bf7\u56de\u5427\uff0c\u6211\u8fd9\u91cc\u6ca1\u4e8b\u3002\u201d\u66f9\u5316\u6df3\u9053\uff1a\u201c\u6bbf\u4e0b\u662f\u4e07\u91d1\u4e4b\u4f53\uff0c\u8fd8\u662f\u8ba9\u5974\u5a62\u8fdb\u6765\u67e5\u5bdf\u4e00\u4e0b\u4e3a\u662f\u3002\u201d\u963f\u4e5d\u77e5\u9053\u8881\u627f\u5fd7\u8fdb\u6765\u65f6\u5b9a\u7136\u7ed9\u4eba\u77a7\u89c1\u4e86\uff0c\u662f\u4ee5\u4ed6\u4eec\u575a\u8981\u67e5\u770b\uff0c\u6068\u6781\u4e86\u66f9\u5316\u6df3\u591a\u7ba1\u95f2\u4e8b\uff0c\u5374\u54ea\u60f3\u5f97\u5230\u4ed6\u4eca\u665a\u7adf\u8981\u4e3e\u4e8b\u52a0\u5bb3\u7687\u5e1d\u3002\u66f9\u5316\u6df3\u77e5\u9053\u516c\u4e3b\u8eab\u6709\u6b66\u529f\uff0c\u53c8\u7ed3\u8bc6\u6c5f\u6e56\u4eba\u7269\uff0c\u542c\u4f55\u94c1\u624b\u62a5\u77e5\u6709\u4eba\u9003\u5165\u516c\u4e3b\u5bdd\u5bab\uff0c\u751f\u6015\u662f\u516c\u4e3b\u7ea6\u6765\u7684\u5e2e\u624b\uff0c\u56e0\u6b64\u975e\u67e5\u7a76\u4e2a\u660e\u767d\u4e0d\u53ef\u3002 \u3000\u3000\u66f9\u5316\u6df3\u5728\u5bab\u4e2d\u6781\u6709\u6743\u52bf\uff0c\u516c\u4e3b\u4e5f\u8fdd\u6297\u4ed6\u4e0d\u5f97\uff0c\u5f53\u4e0b\u5fae\u4e00\u6c89\u541f\uff0c\u5411\u8881\u627f\u5fd7\u6253\u4e86\u4e2a\u624b\u52bf\uff0c\u547d\u4ed6\u4e0a\u5e8a\u94bb\u5165\u88ab\u4e2d\u3002\u8881\u627f\u5fd7\u65e0\u5948\uff0c\u53ea\u5f97\u9664\u4e0b\u978b\u5b50\uff0c\u63e3\u5165\u6000\u4e2d\uff0c\u4e0a\u5e8a\u5367\u5012\uff0c\u62c9\u4e86\u7ee3\u88ab\u76d6\u5728\u8eab\u4e0a\uff0c\u53ea\u89c9\u4e00\u9635\u751c\u9999\uff0c\u76f4\u94bb\u5165\u9f3b\u7aef\u3002 \u3000\u3000\u623f\u5916\u66f9\u5316\u6df3\u53c8\u5728\u4e0d\u65ad\u50ac\u4fc3\u3002\u963f\u4e5d\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u4eec\u6765\u77a7\u5427\uff01\u201d \u3000\u3000\u9664\u4e0b\u5916\u8863\uff0c\u8d70\u8fc7\u53bb\u62d4\u5f00\u95e8\u95e9\uff0c\u968f\u5373\u4e00\u4e2a\u7bad\u6b65\u8df3\u4e0a\u5e8a\u53bb\uff0c\u62a2\u8d77\u88ab\u5b50\u76d6\u5728\u8eab\u4e0a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7a81\u89c9\u963f\u4e5d\u7761\u5728\u8eab\u65c1\uff0c\u8863\u670d\u8d34\u7740\u8863\u670d\uff0c\u811a\u4e0b\u808c\u80a4\u4e00\u78b0\uff0c\u53ea\u89c9\u4e00\u9635\u6e29\u8f6f\u67d4\u817b\uff0c\u5fc3\u4e2d\u4e00\u9635\u8361\u6f3e\uff0c\u4f46\u77e5\u66f9\u5316\u6df3\u4e0e\u4f55\u94c1\u624b\u7b49\u5df2\u7136\u8fdb\u623f\uff0c\u4e0d\u6562\u52a8\u5f39\uff0c\u53ea\u611f\u5230\u963f\u4e5d\u7684\u8eab\u5b50\u5fae\u5fae\u53d1\u98a4\u3002 \u3000\u3000\u963f\u4e5d\u88c5\u7740\u7761\u773c\u60fa\u5fea\uff0c\u6253\u4e2a\u54c8\u6b20\uff0c\u8bf4\u9053\uff1a\u201c\u66f9\u516c\u516c\uff0c\u591a\u8c22\u4f60\u8d39\u5fc3\u3002\u201d \u3000\u3000\u66f9\u5316\u6df3\u5728\u623f\u4e2d\u56db\u4e0b\u6253\u91cf\uff0c\u4e0d\u89c1\u6709\u4f55\u5f02\u72b6\u3002 \u3000\u3000\u4f55\u94c1\u624b\u5047\u4f5c\u4e0d\u5c0f\u5fc3\uff0c\u628a\u624b\u5e15\u6389\u5728\u5730\u4e0b\uff0c\u4fef\u8eab\u53bb\u62fe\uff0c\u5f80\u5e8a\u5e95\u4e00\u5f20\u3002\u963f\u4e5d\u7b11\u9053\uff1a\u201c\u5e8a\u5e95\u4e0b\u4e5f\u67e5\u8fc7\u4e86\uff0c\u6211\u6ca1\u85cf\u7740\u523a\u5ba2\u5427\uff1f\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\u660e\u9274\uff0c\u66f9\u516c\u516c\u662f\u6015\u6bbf\u4e0b\u53d7\u4e86\u60ca\u5413\u3002\u201d\u5979\u8f6c\u5934\u89c1\u5230\u8881\u627f\u5fd7\u7684\u8096\u50cf\uff0c\u5fc3\u4e2d\u4e00\u6014\uff0c\u5fd9\u8f6c\u8fc7\u5934\u6765\uff0c\u4e24\u9053\u773c\u5149\u51dd\u89c6\u7740\u963f\u4e5d\u4e00\u5f20\u660e\u8273\u7684\u8138\u86cb\uff0c\u76ee\u5149\u4e2d\u5c3d\u662f\u4e0d\u6000\u597d\u610f\u7684\u5632\u5f04\u5b09\u7b11\u3002\u963f\u4e5d\u672c\u5c31\u6ee1\u8138\u7ea2\u6655\uff0c\u7ed9\u5979\u77a7\u5f97\u4e0d\u6562\u62ac\u8d77\u5934\u6765\u3002 \u3000\u3000\u66f9\u5316\u6df3\u9053\uff1a\u201c\u6bbf\u4e0b\u8fd9\u91cc\u5e73\u5b89\u65e0\u4e8b\uff0c\u7687\u4e0a\u5c31\u653e\u5fc3\u4e86\u3002\u6211\u4eec\u5230\u522b\u7684\u5730\u65b9\u67e5\u67e5\u53bb\u3002\u201d\u5bf9\u56db\u540d\u5bab\u5973\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u966a\u4f34\u6bbf\u4e0b\uff0c\u4e0d\u8bb8\u7247\u523b\u79bb\u5f00\u3002\u5c31\u662f\u6bbf\u4e0b\u6709\u547d\uff0c\u4e5f\u4e0d\u53ef\u5077\u61d2\u51fa\u53bb\uff0c\u77e5\u9053\u4e48\uff1f\u201d\u56db\u540d\u5bab\u5973\u4fef\u8eab\u9053\uff1a\u201c\u542c\u516c\u516c\u5429\u5490\u3002\u201d\u66f9\u5316\u6df3\u4e0e\u4f55\u94c1\u624b\u53ca\u5176\u4f59\u5bab\u5973\u884c\u793c\u8bf7\u5b89\uff0c\u8f9e\u51fa\u5bdd\u5bab\u3002 \u3000\u3000\u963f\u4e5d\u9053\uff1a\u201c\u653e\u4e0b\u5e10\u5b50\uff0c\u6211\u8981\u7761\u5566\uff01\u201d\u4e24\u540d\u5bab\u5973\u8fc7\u6765\u8f7b\u8f7b\u653e\u4e0b\u7eb1\u5e10\uff0c\u5728\u7089\u4e2d\u52a0\u4e86\u4e9b\u6a80\u9999\uff0c\u5254\u4eae\u7ea2\u70db\uff0c\u4e92\u76f8\u504e\u4f9d\u7740\u5750\u5728\u623f\u89d2\u3002 \u3000\u3000\u963f\u4e5d\u53c8\u662f\u559c\u60a6\uff0c\u53c8\u662f\u5bb3\u7f9e\uff0c\u4e0d\u610f\u4e4b\u95f4\uff0c\u7adf\u4e0e\u65e5\u5915\u76f8\u601d\u7684\u610f\u4e2d\u4eba\u540c\u5e8a\u5408\u887e\uff0c\u4e0d\u7531\u5f97\u5982\u75f4\u5982\u8ff7\uff0c\u773c\u89c1\u51e0\u7f15\u6a80\u9999\u7684\u9752\u70df\u5728\u7eb1\u5e10\u5916\u8885\u8885\u98d8\u8fc7\uff0c\u5979\u4e00\u9897\u5fc3\u4fbf\u4e5f\u5982\u9752\u70df\u4e00\u822c\u5728\u7a7a\u4e2d\u98d8\u8361\u4e0d\u5b9a\u3002 \u3000\u3000\u5979\u4e0d\u6562\u8f6c\u52a8\u8eab\u8eaf\uff0c\u5fc3\u4e2d\u53ea\u662f\u8bf4\uff1a\u201c\u8fd9\u662f\u771f\u7684\u5417\uff1f\u8fd8\u662f\u6211\u53c8\u505a\u68a6\u4e86\uff1f\u201d\u8fc7\u4e86\u826f\u4e45\uff0c\u53ea\u542c\u8881\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u600e\u4e48\u529e\uff1f\u6211\u5f97\u60f3\u6cd5\u51fa\u53bb\uff01\u201d \u3000\u3000\u963f\u4e5d\u55ef\u4e86\u4e00\u58f0\uff0c\u95fb\u5230\u4ed6\u8eab\u4e0a\u7537\u5b50\u7684\u6c14\u606f\uff0c\u4e0d\u89c9\u4e00\u80a1\u559c\u610f\uff0c\u76f4\u751c\u5165\u5fc3\u4e2d\uff0c\u8f7b\u8f7b\u5f80\u4ed6\u8eab\u8fb9\u9760\u53bb\uff0c\u84e6\u5730\u5de6\u81c2\u4e0e\u5de6\u817f\u4e0a\u78b0\u5230\u4e00\u4ef6\u51b0\u51c9\u4e4b\u7269\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u4f38\u624b\u6478\u53bb\uff0c\u7adf\u662f\u4e00\u67c4\u8131\u9798\u7684\u5b9d\u5251\u6a2a\u653e\u5728\u4e24\u4eba\u4e4b\u95f4\uff0c\u5fd9\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u751a\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u8bf4\u4e86\u4f60\u522b\u89c1\u602a\u3002\u201d\u963f\u4e5d\u9053\uff1a\u201c\u8c01\u6765\u602a\u4f60\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u65e0\u610f\u4e2d\u95ef\u8fdb\u4f60\u7684\u5bdd\u5bab\uff0c\u53c8\u88ab\u903c\u5f97\u540c\u887e\u5408\u6795\uff0c\u5b9e\u662f\u4e3a\u52bf\u6240\u8feb\uff0c\u6211\u53ef\u4e0d\u662f\u8f7b\u8584\u65e0\u793c\u4e4b\u4eba\u3002\u201d\u963f\u4e5d\u9053\uff1a\u201c\u8c01\u602a\u4f60\u4e86\u5440\uff01 \u3000\u3000\u628a\u5251\u62ff\u5f00\uff0c\u522b\u5272\u7740\u6211\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u867d\u4ee5\u793c\u81ea\u6301\uff0c\u53ef\u662f\u8ddf\u4f60\u8fd9\u6837\u7684\u7f8e\u8c8c\u59d1\u5a18\u540c\u5367\u4e00\u5e8a\uff0c\u53ea\u6015\u628a\u6301\u4e0d\u4f4f\u2026\u2026\u201d\u963f\u4e5d\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u56e0\u6b64\u4f60\u7528\u5251\u9694\u5728\u4e2d\u95f4\u2026\u2026\u50bb\u2026\u2026\u50bb\u5927\u54e5\uff01\u201d \u3000\u3000\u4e24\u4eba\u751f\u6015\u88ab\u5e10\u5916\u5bab\u5973\u542c\u89c1\uff0c\u90fd\u628a\u5934\u94bb\u5728\u88ab\u4e2d\u6084\u58f0\u8bf4\u8bdd\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53ea\u89c9\u963f\u4e5d\u5439\u6c14\u5982\u5170\uff0c\u5979\u51e0\u4e1d\u67d4\u53d1\u63a0\u5728\u81ea\u5df1\u8138\u4e0a\uff0c\u5fc3\u4e2d\u4e00\u8361\uff0c\u6697\u6697\u81ea\u8b66\uff1a\u201c\u9752\u5f1f\u5bf9\u4f60\u5982\u6b64\u60c5\u610f\uff0c\u600e\u53ef\u522b\u6709\u90aa\u5ff5\uff1f\u8d76\u5feb\u5f97\u627e\u4e9b\u6b63\u7ecf\u5927\u4e8b\u6765\u8bf4\u3002\u201d\u5fd9\u95ee\uff1a\u201c\u8bda\u738b\u7237\u662f\u751a\u4e48\u4eba\uff1f\u201d\u963f\u4e5d\u9053\uff1a\u201c\u662f\u6211\u53d4\u7236\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u5c31\u662f\u4e86\u3002\u4ed6\u4eec\u8981\u62e5\u4ed6\u767b\u57fa\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u963f\u4e5d\u60ca\u9053\uff1a\u201c\u751a\u4e48\uff1f\u8c01\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u66f9\u5316\u6df3\u8ddf\u6ee1\u6d32\u7684\u777f\u4eb2\u738b\u79c1\u901a\uff0c\u60f3\u501f\u6e05\u5175\u6765\u6253\u95ef\u519b\u3002\u201d\u963f\u4e5d\u6012\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u6ee1\u6e05\u4eba\u6709\u751a\u4e48\u597d\uff1f\u8fd8\u4e0d\u662f\u60f3\u54b1\u4eec\u5927\u660e\u6c5f\u5c71\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u662f\u554a\uff0c\u7687\u4e0a\u4e0d\u7b54\u5141\uff0c\u66f9\u5316\u6df3\u4ed6\u4eec\u5c31\u60f3\u62e5\u8bda\u738b\u767b\u4f4d\u2026\u2026\u201d\u963f\u4e5d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8bda\u738b\u7237\u660f\u5eb8\u80e1\u6d82\uff0c\u5b9a\u4f1a\u7b54\u5141\u501f\u5175\u9664\u8d3c\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u53ea\u6015\u4ed6\u4eec\u4eca\u665a\u5c31\u8981\u4e3e\u4e8b\u3002\u201d\u963f\u4e5d\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\uff1f\u90a3\u53ef\u5371\u6025\u5f97\u5f88\u4e86\u3002\u54b1\u4eec\u5feb\u53bb\u7980\u544a\u7236\u7687\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u95ed\u76ee\u4e0d\u8bed\uff0c\u5fc3\u4e0b\u8e0c\u8e87\u3002\u5d07\u796f\u662f\u4ed6\u6740\u7236\u4ec7\u4eba\uff0c\u5341\u591a\u5e74\u6765\uff0c\u65e0\u4e00\u65e5\u4e0d\u5728\u60f3\u4eb2\u624b\u6740\u4e86\uff0c\u4ee5\u62a5\u8840\u6d77\u6c89\u51a4\uff0c\u8fd9\u65f6\u7687\u5bab\u5ffd\u8d77\u5185\u53d8\uff0c\u81ea\u5df1\u4e0d\u8d39\u4e3e\u624b\u4e4b\u52b3\uff0c\u4fbf\u53ef\u773c\u89c1\u4ec7\u4eba\u6bd5\u547d\uff0c\u672c\u662f\u5927\u5feb\u5fc3\u6000\u4e4b\u4e8b\uff1b\u4f46\u5982\u66f9\u5316\u6df3\u7b49\u5978\u8c0b\u6210\u529f\uff0c\u501f\u6e05\u5175\u5165\u5173\uff0c\u95ef\u738b\u4e49\u4e3e\u52bf\u5fc5\u5927\u53d7\u632b\u6298\u3002\u8981\u662f\u6e05\u5175\u957f\u9a71\u76f4\u5165\uff0c\u95ef\u738b\u62b5\u6321\u4e0d\u4f4f\uff0c\u5c82\u975e\u795e\u5dde\u6c89\u6ca6\uff0c\u9ec4\u5e1d\u5b50\u5b59\u90fd\u9677\u4e8e\u80e1\u864f\u4e4b\u624b\uff1f \u3000\u3000\u963f\u4e5d\u5728\u4ed6\u80a9\u5934\u8f7b\u8f7b\u63a8\u4e86\u4e00\u628a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u60f3\u751a\u4e48\u5440\uff1f\u54b1\u4eec\u53ef\u5f97\u62a2\u5728\u5934\u91cc\uff0c\u6251\u706d\u5978\u4eba\u9006\u8c0b\u3002\u201d\u8881\u627f\u5fd7\u4ecd\u662f\u6c89\u541f\u672a\u51b3\u3002\u963f\u4e5d\u6084\u58f0\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e0d\u5fd8\u8bb0\u6211\uff0c\u6211\u2026\u2026\u6211\u603b\u662f\u2026\u2026\u4f60\u7684\u2026\u2026\u54b1\u4eec\u5c06\u6765\u2026\u2026\u8fd8\u6709\u8fd9\u6837\u7684\u65f6\u5019\u3002\u201d\u8bf4\u7740\u6162\u6162\u5c06\u5934\u9760\u8fc7\u53bb\uff0c\u5de6\u988a\u78b0\u5230\u4e86\u4ed6\u53f3\u988a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u51db\u7136\u4e00\u9707\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u5979\u7591\u6211\u8d2a\u604b\u6e29\u67d4\uff0c\u4e0d\u80af\u8d77\u6765\u3002\u597d\u5427\uff0c\u5148\u53bb\u77a7\u77a7\u60c5\u52bf\u518d\u8bf4\u3002\u201d\u6084\u58f0\u9053\uff1a\u201c\u4f60\u628a\u5bab\u5973\u70b9\u4e86\u7a74\u9053\uff0c\u7528\u88ab\u5b50\u8499\u4f4f\u5979\u4eec\u7684\u773c\uff0c\u54b1\u4eec\u597d\u51fa\u53bb\u3002\u201d\u963f\u4e5d\u9053\uff1a\u201c\u70b9\u5728\u54ea\u91cc\u5440\uff1f\u6211\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u65e0\u5948\uff0c\u53ea\u5f97\u62c9\u4f4f\u5979\u7684\u53f3\u624b\uff0c\u5f15\u7740\u5979\u6478\u5230\u81ea\u5df1\u80f8\u524d\u7b2c\u5341\u4e00\u6839\u808b\u9aa8\u4e4b\u7aef\uff0c\u62ff\u7740\u5979\u7684\u624b\u65f6\uff0c\u53ea\u89c9\u6ed1\u817b\u6e29\u8f6f\uff0c\u72b9\u5982\u65e0\u9aa8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u7ae0\u95e8\u7a74\uff0c\u4f60\u7528\u6307\u8282\u5728\u8fd9\u90e8\u4f4d\u6572\u51fb\u4e00\u4e0b\uff0c\u5979\u4eec\u5c31\u4e0d\u80fd\u52a8\u4e86\u3002\u53ef\u522b\u592a\u4f7f\u52b2\uff0c\u514d\u5f97\u4f24\u4e86\u6027\u547d\u3002\u201d \u3000\u3000\u963f\u4e5d\u6302\u5ff5\u7236\u7687\u8eab\u5904\u5371\u5883\uff0c\u75be\u5fd9\u63ed\u5e10\u4e0b\u5e8a\u3002\u56db\u540d\u5bab\u5973\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\u8981\u751a\u4e48\uff1f\u201d\u963f\u4e5d\u8d70\u5230\u9526\u5e37\u4e4b\u540e\uff0c\u628a\u5bab\u5973\u4e00\u4e2a\u4e2a\u5206\u522b\u53eb\u8fc7\u53bb\uff0c\u4f9d\u8881\u627f\u5fd7\u6240\u6388\u4e4b\u6cd5\uff0c\u6253\u4e2d\u4e86\u5404\u4eba\u7a74\u9053\u3002\u6700\u540e\u4e00\u4e2a\u6572\u51fb\u90e8\u4f4d\u4e0d\u51c6\uff0c\u7adf\u5440\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002\u963f\u4e5d\u4e00\u624b\u8499\u4f4f\u5979\u53e3\uff0c\u6478\u51c6\u4e86\u7a74\u9053\u518d\u6253\u4e0b\u53bb\uff0c\u8fd9\u624d\u5c06\u5979\u70b9\u6655\u3002\u5979\u4ece\u9526\u5e37\u540e\u9762\u51fa\u6765\uff0c\u8881\u627f\u5fd7\u5df2\u7a7f\u4e0a\u978b\u5b50\u4e0b\u5e8a\u3002\u4e24\u4eba\u63ed\u5f00\u7a97\u5e18\uff0c\u89c1\u7a97\u5916\u65e0\u4eba\uff0c\u4e00\u9f50\u8dc3\u51fa\u3002 \u3000\u3000\u963f\u4e5d\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u6765\uff01\u201d\u9886\u7740\u8881\u627f\u5fd7\u5f84\u5f80\u4e7e\u6e05\u5bab\u3002\u5c06\u8fd1\u5bab\u95e8\u65f6\uff0c\u9065\u89c1\u524d\u9762\u5f71\u5f71\u7ef0\u7ef0\uff0c\u7ea6\u6709\u6570\u767e\u4eba\u805a\u96c6\u3002\u963f\u4e5d\u60ca\u9053\uff1a\u201c\u9006\u8d3c\u5df2\u56f4\u4e86\u7236\u7687\u5bdd\u5bab\uff0c\u5feb\u53bb\uff01\u201d\u4e24\u4eba\u53d1\u8db3\u6025\u5954\u3002 \u3000\u3000\u8dd1\u51fa\u5341\u4f59\u4e08\uff0c\u4e00\u540d\u592a\u76d1\u8fce\u4e86\u4e0a\u6765\uff0c\u89c1\u662f\u957f\u5e73\u516c\u4e3b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u89c1\u5979\u53ea\u5e26\u7740\u4e00\u540d\u968f\u4ece\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u6bbf\u4e0b\u8fd8\u4e0d\u5b89\u606f\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u548c\u963f\u4e5d\u89c1\u4e7e\u6e05\u5bab\u524d\u540e\u7ad9\u6ee1\u4e86\u592a\u76d1\u4f8d\u536b\uff0c\u4e2a\u4e2a\u624b\u6267\u5175\u5203\uff0c\u77e5\u9053\u4e8b\u5df2\u5371\u6025\u3002\u963f\u4e5d\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d\u53f3\u624b\u4e00\u632f\uff0c\u63a8\u5f00\u90a3\u540d\u592a\u76d1\uff0c\u76f4\u95ef\u8fc7\u53bb\u3002\u5b88\u5728\u5bab\u95e8\u5916\u7684\u51e0\u540d\u4f8d\u536b\u5f85\u8981\u963b\u62e6\uff0c\u90fd\u88ab\u8881\u627f\u5fd7\u63a8\u5f00\u3002\u4f17\u76d1\u536b\u4e0d\u6562\u52a8\u6b66\uff0c\u6025\u5fd9\u62a5\u77e5\u66f9\u5316\u6df3\u3002 \u3000\u3000\u66f9\u5316\u6df3\u7b56\u5212\u62e5\u7acb\u8bda\u738b\uff0c\u81ea\u5df1\u5374\u4e0d\u6562\u51fa\u9762\uff0c\u53ea\u5077\u5077\u5728\u5916\u6307\u6325\uff0c\u542c\u8bf4\u957f\u5e73\u516c\u4e3b\u8fdb\u4e86\u4e7e\u6e05\u5bab\uff0c\u5fc3\u60f3\u8c05\u5979\u4e00\u4e2a\u5c11\u5973\u4e5f\u788d\u4e0d\u4e86\u5927\u4e8b\uff0c\u4f20\u4ee4\u4f17\u4f8d\u536b\u52a0\u7d27\u9632\u5b88\u3002 \u3000\u3000\u963f\u4e5d\u5e26\u7740\u8881\u627f\u5fd7\uff0c\u5f84\u5954\u5d07\u796f\u5e73\u65f6\u6279\u9605\u594f\u7ae0\u7684\u4e66\u623f\u3002 \u3000\u3000\u6765\u5230\u623f\u5916\uff0c\u53ea\u89c1\u623f\u95e8\u53e3\u56f4\u7740\u5341\u591a\u540d\u592a\u76d1\u4f8d\u536b\uff0c\u6ee1\u5730\u9c9c\u8840\uff0c\u8eba\u7740\u4e03\u516b\u5177\u5c38\u9996\uff0c\u60f3\u662f\u5fe0\u4e8e\u7687\u5e1d\u7684\u4f8d\u536b\u88ab\u683c\u6740\u800c\u6b7b\u3002\u4f17\u4eba\u89c1\u5230\u516c\u4e3b\uff0c\u4e00\u5446\u4e4b\u4e0b\uff0c\u963f\u4e5d\u5df2\u62c9\u7740\u8881\u627f\u5fd7\u7684\u624b\u5954\u5165\u4e66\u623f\u3002\u4e00\u540d\u4f8d\u536b\u559d\u9053\uff1a\u201c\u505c\u6b65\uff01\u201d\u4e3e\u5200\u5411\u8881\u627f\u5fd7\u53f3\u81c2\u780d\u53bb\u3002\u8881\u627f\u5fd7\u4fa7\u8eab\u7565\u907f\uff0c\u6325\u638c\u62cd\u5728\u4ed6\u80f8\u53e3\uff0c\u90a3\u4f8d\u536b\u76f4\u8dcc\u51fa\u53bb\uff0c\u8881\u627f\u5fd7\u5df2\u5e26\u4e0a\u4e66\u623f\u623f\u95e8\u3002 \u3000\u3000\u53ea\u89c1\u5ba4\u4e2d\u70db\u5149\u660e\u4eae\uff0c\u5341\u591a\u4eba\u7ad9\u7740\u3002\u963f\u4e5d\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u7236\u7687\uff01\u201d \u3000\u3000\u5411\u4e00\u4e2a\u8eab\u7a7f\u9ec4\u888d\u3001\u5934\u6234\u9ed1\u7f0e\u8f6f\u5e3d\u7684\u4eba\u5954\u53bb\u3002\u8881\u627f\u5fd7\u6253\u91cf\u8fd9\u4eba\uff0c\u89c1\u4ed6\u7ea6\u83ab\u4e09\u5341\u4e94\u516d\u5c81\u5e74\u7eaa\uff0c\u9762\u76ee\u6e05\u79c0\uff0c\u8138\u4e0a\u795e\u8272\u60ca\u6012\u4ea4\u96c6\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4fbf\u662f\u6211\u7684\u6740\u7236\u4ec7\u4eba\u5d07\u796f\u7687\u5e1d\u4e86\u3002\u201d \u3000\u3000\u963f\u4e5d\u5c1a\u672a\u5954\u8fd1\u7687\u5e1d\u8eab\u8fb9\uff0c\u5df2\u6709\u4e24\u540d\u9526\u8863\u536b\u536b\u58eb\u6325\u5200\u62e6\u4f4f\u3002 \u3000\u3000\u5d07\u796f\u5ffd\u89c1\u5973\u513f\u5230\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u5e72\u751a\u4e48\uff1f\u5feb\u51fa\u53bb\u3002\u201d \u3000\u3000\u4e00\u4e2a\u4e09\u5341\u6765\u5c81\u3001\u6ee1\u8138\u6d53\u987b\u7684\u80d6\u5b50\u8bf4\u9053\uff1a\u201c\u8d3c\u5175\u5df2\u7834\u6f7c\u5173\uff0c\u6307\u65e5\u5c31\u5230\u4eac\u5e08\u3002\u4f60\u5230\u8fd9\u65f6\u5019\u8fd8\u662f\u4e0d\u80af\u501f\u5175\u706d\u5bc7\uff0c\u662f\u4f55\u5c45\u5fc3\uff1f\u4f60\u5b9a\u8981\u5c06\u6211\u5927\u660e\u5929\u4e0b\u53cc\u624b\u5949\u9001\u7ed9\u95ef\u8d3c\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u963f\u4e5d\u6012\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u80c6\u6562\u5bf9\u7687\u4e0a\u65e0\u793c\uff01\u201d\u8881\u627f\u5fd7\u5fc3\u77e5\u8fd9\u5c31\u662f\u56fe\u8c0b\u7be1\u4f4d\u7684\u8bda\u738b\u4e86\u3002 \u3000\u3000\u53ea\u542c\u90a3\u80d6\u5b50\u7b11\u9053\uff1a\u201c\u65e0\u793c\uff1f\u4ed6\u8981\u65ad\u9001\u592a\u7956\u7687\u5e1d\u4f20\u4e0b\u6765\u7684\u6c5f\u5c71\uff0c\u54b1\u4eec\u59d3\u6731\u7684\u4e2a\u4e2a\u5bb9\u4ed6\u4e0d\u5f97\u3002\u201d\u5693\u7684\u4e00\u58f0\uff0c\u5c06\u4f69\u5251\u62bd\u51fa\u4e00\u534a\uff0c\u6012\u76ee\u633a\u7709\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u5230\u5e95\u600e\u6837\uff1f\u4e00\u8a00\u800c\u51b3\uff01\u201d \u3000\u3000\u5d07\u796f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6715\u65e0\u5fb7\u65e0\u80fd\uff0c\u81f4\u4f7f\u5929\u4e0b\u5927\u4e71\u3002\u8d3c\u5175\u6765\u4eac\u56fa\u7136\u793e\u7a37\u503e\u8986\uff0c\u501f\u5175\u80e1\u864f\uff0c\u4e5f\u52bf\u5fc5\u5371\u5bb3\u56fd\u5bb6\u3002\u6715\u4e00\u6b7b\u4ee5\u8c22\u56fd\u4eba\uff0c\u539f\u4e0d\u8db3\u60dc\uff0c\u53ea\u662f\u7956\u5b97\u7684\u6c5f\u5c71\u57fa\u4e1a\uff0c\u5c31\u6b64\u62f1\u624b\u8ba9\u4eba\u4e86\u2026\u2026\u201d \u3000\u3000\u8bda\u738b\u62d4\u5251\u51fa\u9798\uff0c\u903c\u8fd1\u4e00\u6b65\uff0c\u559d\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u7acb\u523b\u4e0b\u8bcf\uff0c\u7985\u4f4d\u8ba9\u8d24\u7f62\uff01\u201d\u5d07\u796f\u8eab\u5b50\u53d1\u98a4\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8981\u5f11\u541b\u7be1\u4f4d\u4e48\uff1f\u201d \u3000\u3000\u8bda\u738b\u4e00\u4f7f\u773c\u8272\uff0c\u4e00\u540d\u9526\u8863\u536b\u536b\u58eb\u62d4\u51fa\u957f\u5200\uff0c\u53eb\u9053\uff1a\u201c\u660f\u541b\u65e0\u9053\uff0c\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u4e86\u4ed6\u53e3\u97f3\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u70db\u4e0b\u770b\u5f97\u660e\u767d\uff0c\u539f\u6765\u8fd9\u4eba\u6b63\u662f\u5b89\u5927\u5a18\u7684\u4e08\u592b\u5b89\u5251\u6e05\u3002 \u3000\u3000\u963f\u4e5d\u6012\u53f1\u4e00\u58f0\uff0c\u62a2\u8d77\u6905\u5b50\uff0c\u6321\u5728\u7236\u7687\u8eab\u524d\uff0c\u63a5\u8fde\u67b6\u8fc7\u5b89\u5251\u6e05\u780d\u6765\u7684\u4e09\u5200\u3002\u8bda\u738b\u5e26\u6765\u7684\u4f17\u4f8d\u536b\u7eb7\u7eb7\u62e5\u4e0a\u3002\u8881\u627f\u5fd7\u89c1\u963f\u4e5d\u652f\u6301\u4e0d\u4f4f\uff0c\u62a2\u5165\u4eba\u5708\uff0c\u5de6\u81c2\u8d77\u5904\uff0c\u5c06\u4e24\u540d\u4f8d\u536b\u9707\u51fa\u4e08\u4f59\uff0c\u53f3\u624b\u5c06\u91d1\u86c7\u5251\u9012\u7ed9\u963f\u4e5d\uff0c\u81ea\u5df1\u7ad9\u5728\u5d07\u796f\u8eab\u65c1\u4fdd\u62a4\u3002\u5341\u591a\u540d\u9526\u8863\u536b\u62a2\u4e0a\u6765\u8981\u6740\u7687\u5e1d\uff0c\u90fd\u88ab\u4ed6\u6325\u62f3\u8e22\u8db3\uff0c\u6253\u5f97\u7b4b\u6298\u9aa8\u65ad\u3002\u963f\u4e5d\u5b9d\u5251\u5728\u624b\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u6570\u62db\u95f4\u5df2\u524a\u65ad\u5b89\u5251\u6e05\u7684\u957f\u5200\u3002 \u3000\u3000\u8bda\u738b\u773c\u89c1\u5927\u4e8b\u5df2\u6210\uff0c\u54ea\u77e5\u957f\u5e73\u516c\u4e3b\u5ffd\u7136\u5230\u6765\uff0c\u8fd8\u5e26\u6765\u4e00\u4e2a\u5982\u6b64\u6b66\u827a\u9ad8\u5f3a\u4e4b\u4eba\u62a4\u9a7e\uff0c\u5927\u53eb\uff1a\u201c\u5916\u9762\u7684\u4eba\uff0c\u5feb\u6765\uff01\u201d \u3000\u3000\u4f55\u94c1\u624b\u3001\u4f55\u7ea2\u836f\u3001\u5415\u4e03\u5148\u751f\u53ca\u6e29\u6c0f\u56db\u8001\u5e94\u58f0\u800c\u5165\uff0c\u7a81\u7136\u89c1\u5230\u8881\u627f\u5fd7\uff0c\u65e0\u4e0d\u5927\u60ca\u5931\u8272\u3002\u6e29\u65b9\u8fbe\u773c\u4e2d\u5982\u8981\u55b7\u706b\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5148\u6599\u7406\u8fd9\u5c0f\u5b50\uff01\u201d\u56db\u5144\u5f1f\u56f4\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u963f\u4e5d\u9000\u5230\u7236\u4eb2\u8eab\u8fb9\uff0c\u4ed7\u7740\u5b9d\u5251\u7280\u5229\uff0c\u654c\u5203\u5f53\u8005\u7acb\u65ad\uff0c\u8bda\u738b\u624b\u4e0b\u4eba\u4f17\u4e00\u65f6\u5012\u4e5f\u4e0d\u6562\u653b\u8fd1\u3002\u4f46\u5979\u89c1\u654c\u4eba\u6108\u6765\u6108\u591a\u3002\u8881\u627f\u5fd7\u88ab\u5bf9\u65b9\u4e94\u516d\u540d\u597d\u624b\u7eca\u4f4f\uff0c\u7f13\u4e0d\u51fa\u624b\u6765\u76f8\u52a9\uff0c\u60c5\u52bf\u5341\u5206\u5371\u6025\uff0c\u6b63\u5fc3\u614c\u95f4\uff0c\u5ffd\u89c1\u4e00\u4e2a\u9762\u5bb9\u4e11\u6076\u3001\u4e5e\u5a46\u88c5\u675f\u7684\u8001\u5987\u76ee\u9732\u51f6\u5149\uff0c\u4e3e\u8d77\u53cc\u624b\uff0c\u9732\u51fa\u5c16\u5229\u7684\u5341\u722a\uff0c\u559d\u9053\uff1a\u201c\u628a\u91d1\u86c7\u5251\u8fd8\u6765\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u8fd9\u65f6\u5df2\u6253\u5b9a\u4e3b\u610f\uff0c\u4e8b\u6709\u8f7b\u91cd\u7f13\u6025\uff0c\u773c\u524d\u65e0\u8bba\u5982\u4f55\u8981\u5148\u6551\u7687\u5e1d\uff0c\u4f7f\u5f97\u52fe\u5f15\u6e05\u5175\u5165\u5173\u7684\u9634\u8c0b\u4e0d\u80fd\u5f97\u901e\uff0c\u5f85\u95ef\u738b\u8fdb\u4eac\u4e4b\u540e\uff0c\u518d\u6765\u624b\u5203\u5d07\u796f\u4ee5\u62a5\u7236\u4ec7\uff0c\u8fd9\u662f\u5148\u56fd\u540e\u5bb6\u3001\u5148\u516c\u540e\u79c1\u7684\u5927\u4e49\u3002\u4f46\u6e29\u6c0f\u56db\u8001\u6b66\u529f\u672c\u5df2\u5341\u5206\u9ad8\u5f3a\uff0c\u518d\u52a0\u4e0a\u5415\u4e03\u5148\u751f\u4e0e\u4f55\u94c1\u624b\uff0c\u767b\u65f6\u81ea\u987e\u4e0d\u6687\uff0c\u767e\u5fd9\u4e2d\u89c1\u963f\u4e5d\u5934\u53d1\u6563\u4e71\uff0c\u5b9d\u5251\u72c2\u821e\uff0c\u6e10\u6e10\u62b5\u6321\u4e0d\u4f4f\u4f55\u7ea2\u836f\u7684\u72e0\u653b\uff0c\u7a81\u7136\u7075\u673a\u4e00\u52a8\uff0c\u95ea\u5f97\u51e0\u95ea\uff0c\u907f\u5f00\u4e86\u5415\u4e03\u5148\u751f\u5f53\u5934\u7838\u4e0b\u7684\u70df\u888b\u548c\u6e29\u65b9\u5c71\u6a2a\u626b\u8fc7\u6765\u7684\u94a2\u6756\uff0c\u7a9c\u5230\u4f55\u94c1\u624b\u8ddf\u524d\u3002 \u3000\u3000\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4ee5\u591a\u653b\u5c11\uff0c\u5bf9\u4e0d\u4f4f\u5566\uff01\u201d\u8bf4\u7740\u987a\u624b\u4e00\u94a9\u3002\u8881\u627f\u5fd7\u4fa7\u5934\u907f\u8fc7\uff0c\u559d\u9053\uff1a\u201c\u4f60\u51e0\u5341\u4e2a\u6559\u5f92\u4e0d\u8981\u547d\u4e86\u4e48\uff1f\u201d\u4f55\u94c1\u624b\u4e00\u6014\uff0c\u8dc3\u51fa\u5708\u5b50\uff0c\u8881\u627f\u5fd7\u8ddf\u7740\u4e0a\u524d\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u53cc\u621f\u75be\u523a\u4ed6\u540e\u5fc3\u3002\u8881\u627f\u5fd7\u5bf9\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u6321\u4f4f\u4ed6\u4eec\uff01\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u95ea\u907f\u6e29\u6c0f\u56db\u8001\u4e0e\u5415\u4e03\u5148\u751f\u7684\u5175\u5203\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u89c1\u6211\u90a3\u59d3\u590f\u7684\u5144\u5f1f\uff1f\u201d\u4f55\u94c1\u624b\u81ea\u4ece\u89c1\u4e86\u9752\u9752\u90a3\u4fca\u7f8e\u7684\u6a21\u6837\uff0c\u5df2\u7136\u60c5\u75f4\u98a0\u5012\uff0c\u96be\u4ee5\u81ea\u5df2\uff0c\u5ffd\u7136\u95f4\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u7d27\u6025\u4e2d\u4e0d\u6687\u7ec6\u60f3\uff0c\u56de\u8eab\u8f6c\u81c2\uff0c\u5de6\u624b\u94c1\u94a9\u731b\u5411\u6e29\u65b9\u609f\u5212\u53bb\u3002 \u3000\u3000\u6e29\u65b9\u609f\u600e\u6599\u5f97\u5230\u5979\u4f1a\u9661\u7136\u5012\u6208\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u76ae\u97ad\u5012\u5377\uff0c\u6765\u6321\u5979\u94c1\u94a9\u3002\u4f46\u4f55\u94c1\u624b\u51fa\u62db\u4f55\u7b49\u72e0\u8fa3\uff0c\u53c8\u662f\u653b\u5176\u65e0\u5907\uff0c\u53ea\u4e00\u94a9\uff0c\u5df2\u5728\u6e29\u65b9\u609f\u5de6\u81c2\u4e0a\u5212\u4e86\u4e00\u9053\u53e3\u5b50\u3002\u94a9\u4e0a\u5582\u6709\u5267\u6bd2\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6e29\u65b9\u609f\u8138\u8272\u60e8\u767d\uff0c\u5de6\u81c2\u9ebb\u75f9\uff0c\u8eab\u5b50\u6447\u6447\u6b32\u5760\uff0c\u53f3\u624b\u4e0d\u4f4f\u63c9\u6413\u53cc\u773c\uff0c\u5927\u53eb\uff1a\u201c\u6211\u77a7\u4e0d\u89c1\u5566\u2026\u2026\u6211\u2026\u2026\u6211\u4e2d\u4e86\u6bd2\uff01\u201d\u6e29\u6c0f\u4e09\u8001\u624b\u8db3\u5173\u5fc3\uff0c\u4e0d\u6687\u653b\u654c\uff0c\u75be\u5fd9\u62a2\u4e0a\u53bb\u6276\u6301\u3002 \u3000\u3000\u8881\u627f\u5fd7\u767b\u65f6\u7f13\u51fa\u624b\u6765\uff0c\u89c1\u4f55\u94c1\u624b\u94a9\u4e0a\u4e4b\u6bd2\u5982\u6b64\u5389\u5bb3\uff0c\u4e5f\u4e0d\u89c9\u5fc3\u60ca\uff0c\u4e00\u8f6c\u5934\u89c1\u963f\u4e5d\u6c14\u5598\u8fde\u8fde\uff0c\u62da\u547d\u62b5\u6321\u4f55\u7ea2\u836f\u548c\u5b89\u5251\u6e05\u7684\u5939\u653b\uff0c\u773c\u89c1\u96be\u652f\uff0c\u5f53\u4e0b\u659c\u98de\u800c\u524d\uff0c\u6349\u4f4f\u4f55\u7ea2\u836f\u7684\u80cc\u5fc3\uff0c\u5c06\u5979\u76f4\u63bc\u4e86\u51fa\u53bb\u3002\u5b89\u5251\u6e05\u4e00\u5446\uff0c\u88ab\u963f\u4e5d\u4e00\u5251\u523a\u4e2d\u5de6\u817f\uff0c\u8dcc\u5012\u5728\u5730\u3002 \u3000\u3000\u90a3\u8fb9\u4f55\u94c1\u624b\u5df2\u548c\u5415\u4e03\u5148\u751f\u4ea4\u4e0a\u4e86\u624b\uff0c\u5415\u4e03\u5148\u751f\u89c1\u5230\u6e29\u65b9\u609f\u4e2d\u6bd2\u7684\u60e8\u72b6\uff0c\u8d8a\u6253\u8d8a\u662f\u6c14\u9981\uff0c\u63d0\u8d77\u70df\u7ba1\u731b\u6325\u4e09\u4e0b\uff0c\u8dc3\u51fa\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u8001\u592b\u5931\u966a\u4e86\uff01\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u5415\u4e03\u5148\u751f\uff0c\u518d\u4f1a\uff0c\u518d\u4f1a\uff01\u201d \u3000\u3000\u8fd9\u65f6\u6e29\u65b9\u609f\u6bd2\u53d1\uff0c\u5df2\u660f\u4e86\u8fc7\u53bb\u3002\u6e29\u6c0f\u4e09\u8001\u4e0d\u7531\u5f97\u5fc3\u60ca\u8089\u8df3\uff0c\u4e00\u58f0\u6697\u53f7\uff0c\u6e29\u65b9\u4e49\u62b1\u8d77\u4e94\u5f1f\uff0c\u6e29\u65b9\u8fbe\u3001\u6e29\u65b9\u5c71\u4e00\u4e2a\u5f00\u8def\uff0c\u4e00\u4e2a\u65ad\u540e\uff0c\u51b2\u51fa\u4e66\u623f\u3002\u4f55\u94c1\u624b\u8ffd\u4e86\u51fa\u53bb\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u5305\u4e1c\u897f\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u89e3\u836f\uff0c\u63a5\u7740\u3002\u201d\u6e29\u65b9\u5c71\u8f6c\u8eab\u63a5\u4f4f\u3002\u4f55\u94c1\u624b\u4e00\u7b11\u56de\u5165\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u653b\u5b88\u767b\u65f6\u5f02\u52bf\u3002\u8881\u627f\u5fd7\u548c\u963f\u4e5d\u628a\u9526\u8863\u536b\u6253\u5f97\u4e03\u96f6\u516b\u843d\uff0c\u56db\u6563\u5954\u9003\u3002 \u3000\u3000\u6bbf\u95e8\u5f00\u5904\uff0c\u66f9\u5316\u6df3\u7a81\u7136\u9886\u4e86\u4e00\u6279\u4eac\u8425\u4eb2\u5175\u51b2\u4e86\u8fdb\u6765\u3002\u8881\u627f\u5fd7\u89c1\u654c\u4eba\u52bf\u4f17\uff0c\u53eb\u9053\uff1a\u201c\u963f\u4e5d\u3001\u4f55\u6559\u4e3b\uff0c\u54b1\u4eec\u4fdd\u62a4\u7687\u5e1d\u51b2\u51fa\u53bb\u3002\u201d\u963f\u4e5d\u4e0e\u4f55\u94c1\u624b\u7b54\u5e94\u4e86\u3002\u4e09\u4eba\u5f80\u5d07\u796f\u8eab\u5468\u4e00\u7ad9\uff0c\u6b63\u5f85\u5411\u524d\u593a\u8def\uff0c\u66f9\u5316\u6df3\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u5927\u80c6\u5978\u8d3c\uff0c\u7adf\u6562\u60ca\u52a8\u5fa1\u9a7e\uff0c\u5feb\u7ed9\u6211\u6740\uff01\u201d\u4f17\u4eb2\u5175\u5373\u4e0e\u9526\u8863\u536b\u4ea4\u8d77\u624b\u6765\u3002\u8bda\u738b\u60ca\u5f97\u5446\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u66f9\u516c\u516c\u2026\u2026\u4f60\u2026\u2026\u4f60\u4e0d\u662f\u548c\u6211\u2026\u2026\u201d\u4e00\u8a00\u672a\u6bd5\uff0c\u66f9\u5316\u6df3\u4e00\u5251\u5df2\u5728\u4ed6\u80f8\u53e3\u5bf9\u7a7f\u800c\u8fc7\u3002\u8fd9\u4e00\u6765\u4e0d\u4f46\u4f17\u9526\u8863\u536b\u5927\u60ca\u5931\u8272\uff0c\u8881\u627f\u5fd7\u3001\u4f55\u94c1\u624b\u3001\u963f\u4e5d\u4e09\u4eba\u66f4\u662f\u5947\u602a\uff0c\u53ea\u6709\u5d07\u796f\u5728\u5fc3\u4e2d\u6697\u8d5e\u66f9\u5316\u6df3\u5fe0\u4e49\u3002 \u3000\u3000\u539f\u6765\u66f9\u5316\u6df3\u5728\u5916\u63a2\u542c\u6d88\u606f\uff0c\u77e5\u9053\u5927\u52bf\u5df2\u53bb\uff0c\u5f11\u541b\u5978\u8c0b\u4e0d\u6210\uff0c\u60c5\u6025\u667a\u751f\uff0c\u4fbf\u53bb\u7387\u9886\u4eac\u8425\u7684\u5b88\u5907\u4eb2\u5175\uff0c\u8fdb\u4e7e\u6e05\u5bab\u6765\u6551\u9a7e\u3002 \u3000\u3000\u9526\u8863\u536b\u89c1\u66f9\u5316\u6df3\u53d8\u8ba1\uff0c\u90fd\u629b\u4e0b\u4e86\u5175\u5668\u3002\u66f9\u5316\u6df3\u8fde\u53eb\uff1a\u201c\u62ff\u4e0b\u53bb\uff0c\u62ff\u4e0b\u53bb\uff01\u201d\u4f17\u4eb2\u5175\u5c06\u9526\u8863\u536b\u62ff\u4e0b\u3002\u4e00\u51fa\u6bbf\u95e8\uff0c\u66f9\u5316\u6df3\u53eb\u9053\uff1a\u201c\u780d\u4e86\uff01\u201d\u970e\u65f6\u4e4b\u95f4\uff0c\u53c2\u4e0e\u9006\u8c0b\u7684\u4eba\u90fd\u88ab\u6740\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u90a3\u6b63\u662f\u4ed6\u6740\u4eba\u706d\u53e3\u7684\u6bd2\u8ba1\u3002 \u3000\u3000\u4f55\u94c1\u624b\u89c1\u5c40\u52bf\u5df2\u5b9a\uff0c\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u660e\u65e5\u6211\u5728\u5ba3\u6b66\u95e8\u5916\u5927\u6811\u4e0b\u7b49\u4f60\uff01\u201d\u8bf4\u7740\u643a\u4e86\u4f55\u7ea2\u836f\u7684\u624b\uff0c\u8f6c\u8eab\u800c\u51fa\u3002 \u3000\u3000\u5d07\u796f\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u4ed6\u60f3\u916c\u8c22\u62a4\u9a7e\u4e4b\u529f\uff0c\u4f55\u94c1\u624b\u54ea\u91cc\u7406\u4f1a\uff0c\u5f84\u81ea\u51fa\u5bab\u53bb\u4e86\u3002 \u3000\u3000\u5d07\u796f\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u5973\u513f\u8eab\u4e0a\u6e85\u6ee1\u4e86\u9c9c\u8840\uff0c\u5374\u7b11\u541f\u541f\u7684\u671b\u7740\u8881\u627f\u5fd7\uff0c\u8fd9\u624d\u60ca\u9b42\u7565\u5b9a\uff0c\u5750\u56de\u6905\u4e2d\uff0c\u95ee\u963f\u4e5d\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f \u3000\u3000\u529f\u52b3\u4e0d\u5c0f\uff0c\u6715\u2026\u2026\u6715\u5fc5\u6709\u91cd\u8d4f\u3002\u201d\u4ed6\u6599\u60f3\u8881\u627f\u5fd7\u5fc5\u5b9a\u4f1a\u8dea\u4e0b\u78d5\u5934\uff0c\u54ea\u77e5\u8881\u627f\u5fd7\u6602\u7136\u4e0d\u7406\u3002\u963f\u4e5d\u626f\u626f\u4ed6\u7684\u8863\u88fe\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5feb\u8c22\u6069\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u671b\u7740\u5d07\u796f\uff0c\u60f3\u8d77\u7236\u4eb2\u820d\u547d\u536b\u56fd\uff0c\u7acb\u4e0b\u5927\u529f\uff0c\u5374\u88ab\u8fd9\u7687\u5e1d\u51cc\u8fdf\u800c\u6b7b\uff0c\u5fc3\u4e2d\u60b2\u6124\u75db\u6068\u4e4b\u6781\uff0c\u7ec6\u770b\u8fd9\u6740\u7236\u4ec7\u4eba\u65f6\uff0c\u53ea\u89c1\u4ed6\u4e24\u8fb9\u8138\u988a\u90fd\u51f9\u9677\u8fdb\u53bb\uff0c\u987b\u8fb9\u5df2\u6709\u4e0d\u5c11\u767d\u53d1\uff0c\u773c\u4e2d\u6ee1\u662f\u7ea2\u4e1d\uff0c\u795e\u8272\u751a\u662f\u6194\u60b4\u3002\u6b64\u65f6\u593a\u4f4d\u7684\u5978\u8c0b\u5df2\u7136\u5e73\u5b9a\uff0c\u9996\u6076\u5df2\u9664\uff0c\u4f46\u5d07\u796f\u8138\u4e0a\u53ea\u662f\u663e\u5f97\u70e6\u8e81\u4e0d\u5b89\uff0c\u6b8a\u65e0\u6b22\u6109\u4e4b\u8272\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4ed6\u505a\u7687\u5e1d\u53ea\u662f\u53d7\u7f6a\uff0c\u5fc3\u91cc\u4e00\u70b9\u4e5f\u4e0d\u5feb\u6d3b\uff01\u201d \u3000\u3000\u5d07\u796f\u5374\u54ea\u91cc\u77e5\u9053\u8881\u627f\u5fd7\u5fc3\u4e2d\u8fd9\u8bb8\u591a\u5ff5\u5934\uff0c\u6e29\u8a00\u9053\uff1a\u201c\u4f60\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u5728\u54ea\u91cc\u5f53\u5dee\uff1f\u201d\u4ed6\u89c1\u8881\u627f\u5fd7\u7a7f\u7740\u592a\u76d1\u670d\u8272\uff0c\u8fd8\u9053\u4ed6\u662f\u4e00\u540d\u5c0f\u76d1\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5b9a\u4e86\u5b9a\u795e\uff0c\u51db\u7136\u9053\uff1a\u201c\u6211\u59d3\u8881\uff0c\u662f\u6545\u5175\u90e8\u5c1a\u4e66\u3001\u84df\u8fbd\u7763\u5e08\u8881\u5d07\u7115\u4e4b\u5b50\uff01\u201d\u5d07\u796f\u4e00\u5446\uff0c\u4f3c\u4e4e\u6ca1\u542c\u6e05\u695a\u4ed6\u7684\u8bdd\uff0c\u95ee\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5148\u7236\u6709\u5927\u529f\u4e8e\u56fd\uff0c\u5374\u88ab\u7687\u4e0a\u5904\u6b7b\u3002\u201d\u5d07\u796f\u9ed8\u7136\u534a\u664c\uff0c\u53f9\u9053\uff1a\u201c\u73b0\u4eca\u6211\u4e5f\u9887\u4e3a\u540e\u6094\u4e86\u3002\u201d\u9694\u4e86\u7247\u523b\u9053\uff1a\u201c\u4f60\u8981\u751a\u4e48\u8d4f\u8d50\uff1f\u201d \u3000\u3000\u963f\u4e5d\u5927\u559c\uff0c\u8f7b\u8f7b\u626f\u4e00\u626f\u8881\u627f\u5fd7\u7684\u8863\u88fe\uff0c\u793a\u610f\u8981\u4ed6\u4e58\u673a\u5411\u7687\u4e0a\u6c42\u4e3a\u9a78\u9a6c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6124\u7136\u9053\uff1a\u201c\u6211\u662f\u4e3a\u4e86\u56fd\u5bb6\u800c\u6551\u4f60\uff0c\u8981\u751a\u4e48\u8d4f\u8d50\uff1f\u55ef\uff0c\u662f\u4e86\uff0c\u7687\u4e0a\u65e2\u5df2\u540e\u6094\uff0c\u6c42\u7687\u4e0a\u4e0b\u8bcf\uff0c\u6d17\u96ea\u5148\u7236\u7684\u5927\u51a4\u3002\u201d \u3000\u3000\u5d07\u796f\u6027\u5b50\u521a\u610e\uff0c\u8981\u4ed6\u516c\u7136\u8ba4\u9519\uff0c\u53ef\u6bd4\u751a\u4e48\u90fd\u96be\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u8fd9\u65f6\u66f9\u5316\u6df3\u53c8\u8fdb\u6765\u606d\u95ee\u5723\u5b89\uff0c\u594f\u79f0\u6240\u6709\u53db\u9006\u5df2\u5168\u90e8\u5904\u65a9\uff0c\u5df2\u6d3e\u4eba\u53bb\u6349\u62ff\u9006\u9996\u8bda\u738b\u7684\u5bb6\u5c5e\u3002\u5d07\u796f\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u7a76\u7adf\u662f\u4f60\u5fe0\u5fc3\u3002\u201d \u3000\u3000\u66f9\u5316\u6df3\u89c1\u4e86\u8881\u627f\u5fd7\uff0c\u5fc3\u4e2d\u9e58\u7a81\uff1a\u201c\u8fd9\u4eba\u660e\u660e\u662f\u6ee1\u6e05\u4e5d\u738b\u7684\u4f7f\u8005\uff0c\u600e\u5730\u53cd\u6765\u574f\u6211\u5927\u4e8b\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5f85\u8981\u63ed\u7a7f\u66f9\u5316\u6df3\u7684\u9006\u8c0b\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u95ef\u738b\u4e49\u519b\u65e5\u5185\u5c31\u5230\u4eac\u5e08\uff0c\u4efb\u7531\u8fd9\u5978\u6076\u5c0f\u4eba\u5728\u5bab\u4e2d\u5f53\u6743\uff0c\u5bf9\u4e49\u519b\u6b63\u662f\u5927\u5409\u5927\u5229\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u7406\u4f1a\u7687\u5e1d\uff0c\u5411\u963f\u4e5d\u9053\uff1a\u201c\u8fd9\u5251\u8fd8\u7ed9\u6211\u5427\u3002\u6211\u8981\u53bb\u4e86\uff01\u201d \u3000\u3000\u963f\u4e5d\u5927\u6025\uff0c\u987e\u4e0d\u5f97\u7236\u7687\u4e0e\u66f9\u5316\u6df3\u90fd\u5728\u8eab\u8fb9\uff0c\u51b2\u53e3\u800c\u51fa\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u518d\u6765\u77a7\u6211\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6bbf\u4e0b\u4fdd\u91cd\u3002\u201d\u4f38\u51fa\u624b\u8981\u53bb\u62ff\u5251\u3002\u963f\u4e5d\u624b\u4e00\u7f29\uff0c\u9053\uff1a\u201c\u8fd9\u5251\u6682\u4e14\u653e\u5728\u6211\u8fd9\u91cc\uff0c\u4e0b\u6b21\u89c1\u9762\u518d\u8fd8\u4f60\u3002\u201d\u8bf4\u7740\u51dd\u89c6\u7740\u8881\u627f\u5fd7\u7684\u8138\uff0c\u773c\u5149\u4e2d\u7684\u542b\u610f\u751a\u662f\u660e\u663e\uff1a\u201c\u4f60\u8981\u65e9\u4e9b\u6765\uff0c\u6211\u65e5\u65e5\u591c\u591c\u5728\u76fc\u671b\u7740\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5d07\u796f\u4e0e\u66f9\u5316\u6df3\u90fd\u8138\u9732\u8be7\u5f02\u4e4b\u8272\uff0c\u4e0d\u4fbf\u591a\u8bf4\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8f6c\u8eab\u51fa\u53bb\u3002 \u3000\u3000\u963f\u4e5d\u8ffd\u5230\u6bbf\u95e8\u4e4b\u5916\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u6c38\u4e0d\u8d1f\u4f60\u3002\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\u773c\u4e0b\u4e0d\u662f\u89e3\u91ca\u4e4b\u65f6\uff0c\u4e5f\u975e\u7ec6\u8c08\u4e4b\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u5c06\u6709\u5927\u53d8\uff0c\u8eab\u5c45\u6df1\u5bab\uff0c\u4e0d\u5982\u8fdc\u6d89\u6c5f\u6e56\uff0c\u4f60\u8981\u8bb0\u5f97\u6211\u8fd9\u53e5\u8bdd\u3002\u201d\u4ed6\u77e5\u95ef\u738b\u5373\u5c06\u8fdb\u4eac\uff0c\u5175\u8352\u9a6c\u4e71\u4e4b\u9645\uff0c\u7687\u5bab\u5b9e\u662f\u6700\u5371\u9669\u7684\u5730\u65b9\uff0c\u662f\u4ee5\u8981\u5979\u51fa\u5bab\u907f\u7978\u3002 \u3000\u3000\u54ea\u77e5\u963f\u4e5d\u6df1\u60c5\u6b3e\u6b3e\uff0c\u4f1a\u9519\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u4f4e\u4e0b\u4e86\u5934\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5b81\u613f\u968f\u4f60\u5728\u6c5f\u6e56\u4e0a\u56db\u5904\u4e3a\u5bb6\uff0c\u8fdc\u80dc\u5728\u5bab\u91cc\u4eab\u798f\u3002\u4f60\u4e0b\u6b21\u6765\u65f6\uff0c\u54b1\u4eec\u2026\u2026\u54b1\u4eec\u4ed4\u7ec6\u5546\u91cf\u5427\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u8f7b\u53f9\u4e00\u58f0\uff0c\u4e0d\u518d\u591a\u8bf4\uff0c\u6325\u624b\u9053\u522b\uff0c\u8d8a\u5899\u51fa\u5bab\u3002\u53ea\u89c1\u5230\u5904\u706b\u628a\u7167\u8000\uff0c\u53f7\u4ee4\u4f20\u547c\uff0c\u6b63\u5728\u5927\u6355\u9006\u515a\u4ece\u5c5e\u3002 \u3000\u3000\u4ed6\u6302\u5ff5\u9752\u9752\uff0c\u6025\u5954\u56de\u5230\u6b63\u6761\u5b50\u80e1\u540c\uff0c\u89c1\u9752\u9752\u3001\u7126\u5b9b\u513f\u3001\u7f57\u7acb\u5982\u4e09\u4eba\u5df2\u5b89\u7136\u56de\u6765\uff0c\u8fd9\u624d\u653e\u5fc3\u3002\u4ed6\u4e00\u665a\u52b3\u987f\uff0c\u56de\u623f\u5012\u5934\u4fbf\u7761\u3002 \u3000\u3000\u9192\u6765\u65f6\u5df2\u662f\u5df3\u724c\u65f6\u5206\uff0c\u51fa\u5f97\u5385\u6765\uff0c\u89c1\u6c34\u4e91\u3001\u95f5\u5b50\u534e\u7387\u9886\u7740\u5341\u516d\u540d\u4ed9\u90fd\u5f1f\u5b50\u5728\u5385\u4e0a\u76f8\u5019\u3002\u539f\u6765\u4ed6\u4eec\u5f97\u6089\u8881\u627f\u5fd7\u5e9c\u4e0a\u906d\u4e94\u6bd2\u6559\u5077\u88ad\uff0c\u662f\u4ee5\u8fc7\u6765\u76f8\u52a9\u3002\u8881\u627f\u5fd7\u9053\u4e86\u52b3\uff0c\u544a\u77e5\u9ec4\u6728\u9053\u4eba\u591a\u534a\u5c1a\u5728\u4eba\u95f4\u3002\u4ed9\u90fd\u4f17\u4eba\u5927\u559c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8bf7\u4ed6\u4eec\u5728\u5b85\u4e2d\u5b88\u62a4\u7740\u4f24\u8005\uff0c\u5f84\u51fa\u5ba3\u6b66\u95e8\u6765\uff0c\u884c\u4e0d\u591a\u65f6\uff0c\u8fdc\u8fdc\u671b\u89c1\u4f55\u94c1\u624b\u7ad9\u5728\u6811\u4e0b\u3002 \u3000\u3000\u5979\u7b11\u76c8\u76c8\u7684\u8fce\u4e0a\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u6211\u6628\u665a\u7389\u6210\u4f60\u7684\u7f8e\u4e8b\uff0c\u591f\u4e0d\u591f\u670b\u53cb\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6628\u665a\u5f62\u52bf\u6781\u662f\u5371\u6025\uff0c\u5e78\u5f97\u4f55\u6559\u4e3b\u4ed7\u4e49\u76f8\u52a9\uff0c\u8fd9\u624d\u6ca1\u95f9\u6210\u5927\u4e71\u5b50\u3002\u5144\u5f1f\u5b9e\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\u771f\u662f\u8273\u798f\u4e0d\u6d45\uff0c\u6709\u8fd9\u6837\u4e00\u4f4d\u82b1\u5bb9\u6708\u8c8c\u7684\u516c\u4e3b\u5782\u9752\u76f8\u7231\uff0c\u5c06\u6765\u5c01\u4e86\u9a78\u9a6c\u7237\uff0c\u8fd8\u8ba4\u5f97\u6211\u4eec\u8fd9\u79cd\u6c5f\u6e56\u670b\u53cb\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u6b63\u8272\u9053\uff1a\u201c\u4f55\u6559\u4e3b\u522b\u5f00\u73a9\u7b11\u3002\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u554a\u54df\uff0c\u8fd8\u8d56\u54e9\uff01\u5979\u8fd9\u6837\u542b\u60c5\u8109\u8109\u7684\u671b\u7740\u4f60\uff0c\u8c01\u77a7\u4e0d\u51fa\u6765\u5462\uff1f\u518d\u8bf4\uff0c\u4f60\u8981\u662f\u4e0d\u7231\u5979\uff0c\u600e\u4f1a\u628a\u91d1\u86c7\u5251\u7ed9\u5979\uff1f\u53c8\u8fd9\u4e48\u62da\u547d\u7684\u53bb\u6551\u5979\u7236\u7687\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u662f\u4e3a\u4e86\u56fd\u5bb6\u5927\u4e49\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u62bf\u5634\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u8ddf\u4eba\u5bb6\u540c\u5e8a\u5408\u88ab\uff0c\u4f60\u601c\u6211\u7231\uff0c\u90a3\u4e5f\u662f\u4e3a\u4e86\u56fd\u5bb6\u5927\u4e49\u3002\u563b\u563b\uff01\u201d\u8881\u627f\u5fd7\u767b\u65f6\u6ee1\u8138\u901a\u7ea2\uff0c\u624b\u8db3\u5931\u63aa\uff0c\u9053\uff1a\u201c\u751a\u2026\u2026\u751a\u4e48\uff1f\u4f60\u600e\u4e48\u2026\u2026\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u516c\u4e3b\u88ab\u5b50\u91cc\u660e\u660e\u85cf\u7740\u4e00\u4eba\uff0c\u6211\u4eec\u8fd9\u4e9b\u6c5f\u6e56\u4e0a\u6df7\u7684\u4eba\uff0c\u96be\u9053\u4f1a\u778e\u4e86\u773c\u4e48\uff1f\u563b\u563b\uff0c\u6211\u6b63\u60f3\u6296\u4e86\u51fa\u6765\uff0c\u5e78\u597d\u773c\u775b\u4e00\u6643\uff0c\u89c1\u5230\u8881\u76f8\u516c\u7684\u8096\u50cf\u3002\u8fd9\u4e2a\u4ea4\u60c5\uff0c\u5c82\u53ef\u4e0d\u653e\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\u539f\u6765\u662f\u90a3\u5e45\u8096\u50cf\u6ca1\u6536\u597d\uff0c\u4ee5\u81f4\u7ed9\u5979\u77a7\u4e86\u51fa\u6765\uff1b\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u53c8\u6697\u53eb\u60ed\u6127\uff0c\u82e5\u4e0d\u662f\u90a3\u5e45\u8096\u50cf\uff0c\u4f55\u94c1\u624b\u63ed\u5f00\u88ab\u6765\uff0c\u90a3\u662f\u66f4\u52a0\u7cdf\u7cd5\u4e86\u3002 \u3000\u3000\u4f55\u94c1\u624b\u89c1\u4ed6\u8138\u4e0a\u4e00\u76f4\u7ea2\u5230\u4e86\u8033\u6839\u5b50\u91cc\uff0c\u77e5\u4ed6\u9762\u5ae9\uff0c\u6362\u8fc7\u8bdd\u9898\uff0c\u95ee\u9053\uff1a\u201c\u590f\u76f8\u516c\u5df2\u5e73\u5b89\u56de\u53bb\u4e86\u5427\uff1f\u201d\u8881\u627f\u5fd7\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u53bb\u7ed9\u8d35\u6559\u7684\u670b\u53cb\u4eec\u89e3\u7a74\u5427\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u5728\u524d\u9886\u8def\uff0c\u7ee7\u7eed\u5411\u897f\uff0c\u4e00\u8def\u4e0a\u79f0\u8d5e\u963f\u4e5d\u7f8e\u4e3d\u7edd\u4f26\uff0c\u751f\u5e73\u4ece\u6240\u672a\u89c1\uff0c\u53c8\u8bf4\u77a7\u4e0d\u51fa\u4e00\u4f4d\u91d1\u679d\u7389\u53f6\u7684\u5999\u9f84\u516c\u4e3b\uff0c\u7adf\u7136\u662f\u4e00\u8eab\u6b66\u529f\uff0c\u90a3\u5b9a\u7136\u662f\u8881\u627f\u5fd7\u4eb2\u624b\u6559\u7684\u4e86\uff0c\u660e\u5e08\u624b\u4e0b\u51fa\u9ad8\u5f92\uff0c\u5f53\u7136\u5982\u6b64\uff0c\u4f55\u51b5\u8fd9\u4f4d\u660e\u5e08\u5bf9\u9ad8\u5f92\u53c8\u662f\u52a0\u610f\u7684\u53e6\u773c\u76f8\u770b\u3002\u8881\u627f\u5fd7\u4efb\u5979\u563b\u563b\u54c8\u54c8\u7684\u5570\u5506\u4e0d\u4f11\uff0c\u5e76\u4e0d\u7f6e\u7b54\u3002\u884c\u4e86\u4e94\u91cc\u591a\u8def\uff0c\u6765\u5230\u4e00\u5ea7\u53e4\u5239\u534e\u4e25\u5bfa\u524d\u3002 \u3000\u3000\u5bfa\u5916\u6709\u4e94\u6bd2\u6559\u7684\u6559\u4f17\u5b88\u536b\uff0c\u89c1\u5230\u8881\u627f\u5fd7\u65f6\u90fd\u6012\u76ee\u800c\u89c6\u3002\u8881\u627f\u5fd7\u4e5f\u4e0d\u7406\u4f1a\uff0c\u8fdb\u5bfa\u540e\u89c1\u5927\u96c4\u5b9d\u6bbf\u4e0a\u94fa\u4e86\u8349\u5e2d\uff0c\u88ab\u4ed6\u6253\u4f24\u7684\u6559\u5f92\u4e00\u6392\u6392\u7684\u8eba\u7740\u3002\u8881\u627f\u5fd7\u9010\u4e00\u7ed9\u5404\u4eba\u89e3\u5f00\u7a74\u9053\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u4e0e\u5404\u4f4d\u672c\u65e0\u51a4\u4ec7\uff0c\u7531\u4e8e\u5c0f\u5c0f\u8bef\u4f1a\uff0c\u4ee5\u81f4\u5f97\u7f6a\u3002\u8fd9\u91cc\u5411\u5404\u4f4d\u8d54\u7f6a\u4e86\u3002\u201d\u8bf4\u7740\u56e2\u56e2\u4f5c\u4e86\u4e00\u63d6\u3002\u4f17\u4eba\u6389\u5934\u4e0d\u7406\uff0c\u65e2\u4e0d\u8fd8\u793c\uff0c\u4ea6\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\u793c\u6570\u5df2\u5230\uff0c\u4e5f\u4e0d\u591a\u8bf4\uff0c\u8f6c\u8eab\u51fa\u6765\uff0c\u4e00\u56de\u5934\uff0c\u5ffd\u89c1\u4e00\u53cc\u6bd2\u773c\u6076\u72e0\u72e0\u7684\u51dd\u89c6\u7740\u4f55\u94c1\u624b\u3002\u8fd9\u4eba\u9690\u8eab\u6bbf\u9685\u6697\u5904\uff0c\u8eab\u5f62\u4e00\u65f6\u77a7\u4e0d\u6e05\u695a\uff0c\u53ea\u89c1\u5230\u53cc\u773c\u78a7\u6cb9\u6cb9\u7684\u653e\u5149\u3002\u8881\u627f\u5fd7\u4e00\u60ca\uff0c\u5fc3\u60f3\u8fd9\u773c\u5149\u4e2d\u5145\u6ee1\u4e86\u6028\u6bd2\u6124\u6fc0\uff0c\u6b64\u4eba\u662f\u8c01\uff1f\u51dd\u76ee\u518d\u77a7\uff0c\u90a3\u4eba\u5df2\u95ea\u8eab\u5165\u5185\uff0c\u8eab\u5f62\u4e00\u52a8\uff0c\u7acb\u5373\u8ba4\u51fa\u539f\u6765\u662f\u8001\u4e5e\u5a46\u4f55\u7ea2\u836f\u3002 \u3000\u3000\u4f55\u94c1\u624b\u76f8\u9001\u51fa\u5bfa\u3002\u8881\u627f\u5fd7\u89c1\u5979\u8138\u8272\u6709\u5f02\uff0c\u4e0e\u9002\u624d\u8a00\u7b11\u664f\u664f\u7684\u795e\u60c5\u5927\u4e0d\u76f8\u540c\uff0c\u9887\u4e3a\u7591\u60d1\u3002\u4e24\u4eba\u5728\u5bfa\u95e8\u5916\u884c\u793c\u800c\u522b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4ece\u6765\u8def\u56de\u53bb\uff0c\u8d70\u51fa\u91cc\u8bb8\uff0c\u8d8a\u60f3\u7591\u5fc3\u8d8a\u751a\uff0c\u5bfb\u601d\u83ab\u975e\u4ed6\u4eec\u53e6\u6709\u5978\u8ba1\uff1f\u53ea\u6015\u5404\u4eba\u7a74\u9053\u89e3\u5f00\u4e4b\u540e\uff0c\u6b7b\u5fc3\u4e0d\u606f\uff0c\u518d\u6765\u9a9a\u6270\uff0c\u4e0d\u5982\u5148\u63a2\u5230\u5bf9\u65b9\u56fe\u8c0b\uff0c\u4ee5\u4fbf\u5148\u6709\u9632\u5907\u3002\u5f53\u4e0b\u6298\u5411\u5357\u884c\uff0c\u8fdc\u8fdc\u8d70\u5230\u534e\u4e25\u5bfa\u4e4b\u540e\uff0c\u56db\u671b\u65e0\u4eba\uff0c\u4ece\u540e\u5899\u8dc3\u4e86\u8fdb\u53bb\uff0c\u5ffd\u542c\u5f97\u5618\u6e9c\u6e9c\u54e8\u58f0\u5927\u4f5c\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u662f\u4e94\u6bd2\u6559\u805a\u4f17\u96c6\u4f1a\u7684\u8baf\u53f7\uff0c\u4e8e\u662f\u5728\u4e00\u682a\u5927\u6811\u540e\u9690\u533f\u7247\u523b\uff0c\u4f30\u91cf\u6559\u4f17\u90fd\u5df2\u4f1a\u96c6\uff0c\u7136\u540e\u6084\u6084\u63a9\u5230\u5927\u96c4\u5b9d\u6bbf\u4e4b\u540e\uff0c\u53ea\u542c\u5f97\u6bbf\u91cc\u4f20\u51fa\u4e00\u9635\u6fc0\u70c8\u7684\u4e89\u8fa9\u4e4b\u58f0\u3002 \u3000\u3000\u4ed6\u8d34\u8033\u5728\u95e8\u7f1d\u4e0a\u503e\u542c\uff0c\u4f55\u7ea2\u836f\u58f0\u97f3\u5c16\u9510\uff0c\u9f50\u4e91\u7488\u55d3\u95e8\u7c97\u5927\uff0c\u4e24\u4eba\u4f60\u5531\u6211\u548c\uff0c\u6570\u8bf4\u4f55\u94c1\u624b\u7684\u7f6a\u6106\u3002\u4e00\u4e2a\u8bf4\u5979\u8d2a\u604b\u60c5\u6b32\uff0c\u5fd8\u4e86\u6559\u4e2d\u6df1\u4ec7\uff0c\u53cd\u4e0e\u672c\u6559\u4e3a\u654c\uff1b\u53e6\u4e00\u4e2a\u8bf4\u5979\u4e0e\u654c\u8054\u624b\uff0c\u574f\u4e86\u62e5\u7acb\u65b0\u541b\u3001\u4e58\u673a\u5149\u5927\u672c\u6559\u7684\u5927\u4e8b\u3002 \u3000\u3000\u4f55\u94c1\u624b\u5fae\u5fae\u51b7\u7b11\uff0c\u542c\u4e8c\u4eba\u8bf4\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981\u5f85\u600e\u6837\uff1f\u201d\u4f17\u4eba\u767b\u65f6\u9ed8\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u9694\u4e86\u597d\u4e00\u4f1a\uff0c\u4f55\u7ea2\u836f\u5ffd\u9053\uff1a\u201c\u53e6\u7acb\u6559\u4e3b\uff01\u201d \u3000\u3000\u4f55\u94c1\u624b\u51db\u7136\u9053\uff1a\u201c\u54b1\u4eec\u6570\u767e\u5e74\u6765\u6559\u89c4\uff0c\u53ea\u6709\u8001\u6559\u4e3b\u8fc7\u4e16\u4e4b\u540e\uff0c\u624d\u80fd\u53e6\u7acb\u65b0\u6559\u4e3b\u3002\u90a3\u4e48\u4f60\u662f\u8981\u6211\u6b7b\u4e86\uff1f\u201d\u4f17\u4eba\u6c89\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u8c01\u60f3\u5f53\u65b0\u6559\u4e3b\uff1f\u201d\u5979\u8fde\u95ee\u4e09\u58f0\uff0c\u6559\u4f17\u65e0\u4eba\u56de\u7b54\u3002\u4f55\u94c1\u624b\u51b7\u7b11\u9053\uff1a\u201c\u54ea\u4e00\u4e2a\u81ea\u91cf\u80dc\u5f97\u4e86\u6211\u7684\uff0c\u51fa\u6765\u62a2\u6559\u4e3b\u7f62\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u53f3\u76ee\u8d34\u5230\u95e8\u7f1d\u4e0a\u5f80\u91cc\u5f20\u671b\uff0c\u89c1\u4f55\u94c1\u624b\u4e00\u4eba\u5750\u5728\u6905\u4e0a\uff0c\u6570\u5341\u540d\u6559\u4f17\u90fd\u7ad9\u5f97\u8fdc\u8fdc\u5730\uff0c\u663e\u662f\u5bf9\u5979\u9887\u4e3a\u5fcc\u60ee\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u4e94\u6bd2\u6559\u8fd9\u4e9b\u4eba\uff0c\u6211\u6bcf\u4e2a\u90fd\u4ea4\u8fc7\u624b\uff0c\u6ca1\u4e00\u4eba\u53ca\u5f97\u4e0a\u5979\u4e00\u534a\u672c\u4e8b\u3002\u4f46\u5355\u51ed\u6b66\u529b\u538b\u4eba\uff0c\u53ea\u6015\u8fd9\u6559\u4e3b\u4e5f\u505a\u4e0d\u957f\u4e45\u3002\u201d\u773c\u89c1\u4e94\u6bd2\u6559\u5185\u54c4\uff0c\u5e76\u975e\u56fe\u8c0b\u5411\u4ed6\u4e0e\u9752\u9752\u5bfb\u4ec7\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u7406\u4f1a\uff0c\u6b63\u5f85\u62bd\u8eab\u51fa\u5bfa\uff0c\u5ffd\u4e86\u5f00\u6765\uff0c\u679c\u7136\u72b9\u5982\u526a\u5200\u6a21\u6837\uff0c\u53ea\u662f\u526a\u5203\u5185\u5f2f\uff0c\u66f4\u50cf\u4e00\u628a\u94b3\u5b50\u3002 \u3000\u3000\u4f55\u94c1\u624b\u5fae\u5fae\u51b7\u7b11\uff0c\u5750\u5728\u6905\u4e2d\u4e0d\u52a8\u3002\u4f55\u7ea2\u836f\u7eb5\u8eab\u4e0a\u524d\uff0c\u541e\u541e\u4e24\u58f0\uff0c\u526a\u5b50\u5df2\u8fde\u5939\u4e24\u4e0b\u3002\u5979\u5fcc\u60ee\u4f55\u94c1\u624b\u6b66\u529f\u5389\u5bb3\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u7acb\u5373\u8dc3\u5f00\u3002\u4f55\u94c1\u624b\u7aef\u5750\u6905\u4e2d\uff0c\u53ea\u5728\u4f55\u7ea2\u836f\u653b\u4e0a\u6765\u65f6\u7565\u52a0\u95ea\u907f\uff0c\u5374\u4e0d\u8fd8\u51fb\u3002\u8881\u627f\u5fd7\u6b63\u611f\u5947\u602a\uff0c\u76ee\u5149\u4e00\u659c\uff0c\u89c1\u6570\u5341\u540d\u6559\u4f17\u5404\u6267\u5175\u5203\uff0c\u6e10\u6e10\u903c\u62e2\uff0c\u624d\u77e5\u4f55\u94c1\u624b\u5b88\u7d27\u95e8\u6237\uff0c\u9632\u8303\u4f17\u4eba\u56f4\u653b\u3002\u4ed6\u56e0\u95e8\u7f1d\u72ed\u7a84\uff0c\u53ea\u89c1\u5f97\u5230\u6bbf\u4e2d\u7684\u4e00\u6761\u5730\u65b9\uff0c\u60f3\u6765\u6559\u4f17\u5df2\u5728\u56db\u9762\u516b\u65b9\u56f4\u4f4f\u4e86\u5979\u3002 \u3000\u3000\u4f17\u4eba\u50f5\u6301\u7247\u523b\uff0c\u8c01\u4e5f\u4e0d\u6562\u8e81\u8fdb\u3002\u4f55\u7ea2\u836f\u53eb\u9053\uff1a\u201c\u6ca1\u7528\u7684\u4e1c\u897f\uff0c\u6015\u751a\u4e48\uff1f\u5927\u4f19\u513f\u4e0a\u5440\uff01\u201d\u5979\u5de8\u526a\u4e00\u6325\uff0c\u4f17\u4eba\u5450\u558a\u4e0a\u524d\u3002\u4f55\u94c1\u624b\u500f\u5730\u8dc3\u8d77\uff0c\u53ea\u542c\u5f97\u4e52\u4e53\u58f0\u54cd\uff0c\u5750\u6905\u5df2\u88ab\u6570\u4ef6\u5175\u5203\u51fb\u5f97\u7c89\u788e\u3002\u4e24\u540d\u6559\u4f17\u63a5\u8fde\u60e8\u53eb\uff0c\u4e2d\u94a9\u53d7\u4f24\u3002\u5927\u6bbf\u4e0a\u5c18\u571f\u98de\u626c\uff0c\u4f55\u94c1\u624b\u4e00\u4e2a\u767d\u5f71\u5728\u4eba\u7fa4\u4e2d\u7eb5\u6a2a\u6765\u53bb\uff0c\u767b\u65f6\u6597\u5f97\u731b\u6076\u5df2\u6781\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5bdf\u770b\u6bbf\u4e2d\u4f17\u4eba\u76f8\u6597\u60c5\u72b6\uff0c\u8bf8\u6559\u4f17\u9664\u4f55\u7ea2\u836f\u4e4b\u5916\u90fd\u66fe\u88ab\u4ed6\u70b9\u4e86\u7a74\u9053\uff0c\u59d4\u987f\u591a\u65f6\uff0c\u8fd9\u65f6\u7a74\u9053\u752b\u89e3\uff0c\u4e2a\u4e2a\u7ecf\u8109\u672a\u7545\uff0c\u884c\u52a8\u7a92\u6ede\u3002\u4f55\u94c1\u624b\u82e5\u8981\u8131\u8eab\u800c\u51fa\uff0c\u8be5\u5f53\u5e76\u4e0d\u4e3a\u96be\uff0c\u7136\u800c\u5979\u7adf\u4e0d\u51b2\u51fa\uff0c\u4f3c\u60f3\u4ee5\u6b66\u529b\u538b\u670d\u6559\u4f17\uff0c\u60e9\u6cbb\u53db\u9996\u3002 \u3000\u3000\u518d\u62c6\u6570\u5341\u62db\uff0c\u5ffd\u89c1\u4eba\u7fa4\u4e2d\u4e00\u4eba\u884c\u52a8\u8be1\u5f02\u3002\u8fd9\u4eba\u867d\u4e5f\u968f\u4f17\u653b\u6253\uff0c\u4f46\u811a\u6b65\u8fdf\u7f13\uff0c\u624b\u4e2d\u6367\u7740\u4e00\u4ef6\u751a\u4e48\u4e1c\u897f\uff0c\u6162\u6162\u5411\u4f55\u94c1\u624b\u903c\u8fd1\u3002\u8881\u627f\u5fd7\u770b\u4ed4\u7ec6\u65f6\uff0c\u539f\u6765\u6b64\u4eba\u6b63\u662f\u9526\u8863\u6bd2\u4e10\u9f50\u4e91\u7488\u3002\u84e6\u5730\u91cc\u53ea\u542c\u4ed6\u5927\u53eb\u4e00\u58f0\uff0c\u53cc\u624b\u4e00\u9001\uff0c\u4e00\u7f15\u9ec4\u5149\u5411\u4f55\u94c1\u624b\u63b7\u53bb\u3002 \u3000\u3000\u4f55\u94c1\u624b\u4fa7\u8eab\u95ea\u5f00\uff0c\u54ea\u77e5\u8fd9\u4ef6\u6697\u5668\u53e4\u602a\u4e4b\u6781\uff0c\u7adf\u80fd\u5728\u7a7a\u4e2d\u8f6c\u5f2f\u8ffd\u9010\u3002\u5176\u65f6\u6570\u4ef6\u5175\u5203\u53c8\u540c\u65f6\u653b\u5230\uff0c\u4f55\u94c1\u624b\u5c16\u53eb\u4e00\u58f0\uff0c\u5df2\u4e3a\u6697\u5668\u6240\u4e2d\u3002\u8fd9\u65f6\u8881\u627f\u5fd7\u4e5f\u5df2\u770b\u5f97\u6e05\u695a\uff0c\u8fd9\u4ef6\u6d3b\u6697\u5668\u4fbf\u662f\u90a3\u6761\u5c0f\u91d1\u86c7\u3002\u4f55\u94c1\u624b\u8eab\u5b50\u4e00\u6643\uff0c\u75be\u5fd9\u4f38\u624b\u626f\u8131\u54ac\u4f4f\u80a9\u5934\u7684\u91d1\u86c7\uff0c\u6454\u5728\u5730\u4e0b\uff0c\u72e0\u72e0\u4e24\u94a9\uff0c\u6740\u4e86\u4e24\u540d\u6559\u4f17\u3002\u4f55\u7ea2\u836f\u5927\u53eb\uff1a\u201c\u8fd9\u8d31\u5a62\u7ed9\u91d1\u86c7\u54ac\u4e2d\u5566\u3002\u5927\u4f19\u513f\u7eca\u4f4f\u5979\uff0c\u6bd2\u6027\u5c31\u8981\u53d1\u4f5c\u5566\uff01\u201d \u3000\u3000\u4f55\u94c1\u624b\u8dcc\u8dcc\u649e\u649e\uff0c\u51b2\u5411\u540e\u6bbf\u3002\u5979\u867d\u4e2d\u6bd2\uff0c\u5a01\u52bf\u72b9\u5728\uff0c\u6559\u4f17\u4e00\u65f6\u90fd\u4e0d\u6562\u5192\u9669\u963b\u62e6\u3002\u4f55\u7ea2\u836f\u7eb5\u8eab\u4e0a\u524d\uff0c\u53cc\u526a\u5982\u98ce\uff0c\u5f84\u5f80\u5979\u8111\u540e\u5939\u53bb\u3002\u4f55\u94c1\u624b\u4e00\u4f4e\u5934\uff0c\u8fd8\u4e86\u4e00\u94a9\u3002\u6f58\u79c0\u8fbe\u4e0e\u5c91\u5176\u65af\u5df2\u62e6\u4f4f\u5979\u53bb\u8def\u3002\u4f55\u94c1\u624b\u53f3\u8098\u5728\u8170\u65c1\u8f7b\u6309\uff0c\u201c\u542b\u6c99\u5c04\u5f71\u201d\u7684\u6bd2\u9488\u6fc0\u5c04\u800c\u51fa\u3002\u6f58\u79c0\u8fbe\u95ea\u907f\u4e0d\u9051\uff0c\u672a\u53ca\u53eb\u558a\uff0c\u5df2\u7136\u6bd9\u547d\u3002\u4f55\u94c1\u624b\u80a9\u4e0a\u6bd2\u53d1\uff0c\u795e\u667a\u660f\u8ff7\uff0c\u94c1\u94a9\u4e71\u821e\uff0c\u4f7f\u51fa\u6765\u5df2\u4e0d\u6210\u5bb6\u6570\u3002 \u3000\u3000\u8881\u627f\u5fd7\u773c\u89c1\u5979\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4fbf\u8981\u6b7b\u4e8e\u8fd9\u6279\u9634\u72e0\u6bd2\u8fa3\u7684\u6559\u4f17\u4e4b\u624b\uff0c\u5fc3\u60f3\u6628\u665a\u5728\u5bab\u4e2d\u95ee\u5979\u8981\u4e0d\u8981\u89c1\u9752\u5f1f\uff0c\u5b9e\u662f\u6709\u610f\u76f8\u6b3a\uff0c\u867d\u8bf4\u4e8b\u6025\u884c\u6743\uff0c\u6bd5\u7adf\u4e0d\u662f\u5149\u660e\u78ca\u843d\u7684\u5927\u4e08\u592b\u884c\u5f84\uff0c\u4e0d\u514d\u5fc3\u6709\u6b49\u610f\uff0c\u5979\u773c\u4e0b\u6240\u4ee5\u4f17\u53db\u4eb2\u79bb\uff0c\u5b9e\u56e0\u6211\u6628\u665a\u90a3\u53e5\u8bdd\u800c\u8d77\uff0c\u6b64\u65f6\u4eb2\u773c\u89c1\u5230\uff0c\u5c82\u53ef\u8896\u624b\u4e0d\u7406\uff1f\u5ffd\u5730\u8dc3\u51fa\uff0c\u5927\u53eb\uff1a\u201c\u5927\u5bb6\u4f4f\u624b\uff01\u201d \u3000\u3000\u6559\u4f17\u89c1\u4ed6\u7a81\u7136\u51fa\u73b0\uff0c\u65e0\u4e0d\u5927\u60ca\uff0c\u4e00\u9f50\u9000\u5f00\u3002 \u3000\u3000\u4f55\u94c1\u624b\u8fd9\u65f6\u5df2\u66f4\u52a0\u80e1\u6d82\uff0c\u6325\u94a9\u5411\u8881\u627f\u5fd7\u8fce\u9762\u5212\u6765\u3002\u8881\u627f\u5fd7\u4e00\u4fa7\u8eab\uff0c\u5de6\u624b\u4f38\u51fa\uff0c\u53cd\u62ff\u5979\u624b\u8155\u3002\u54ea\u77e5\u5979\u6b66\u529f\u6df1\u6e5b\uff0c\u8fdb\u9000\u8d8b\u907f\u4e4b\u9645\u5df2\u6210\u81ea\u7136\uff0c\u867d\u7136\u773c\u524d\u91d1\u661f\u4e71\u821e\uff0c\u4f46\u624b\u8155\u4e00\u78b0\u5230\u8881\u627f\u5fd7\u7684\u624b\u6307\uff0c\u5de6\u81c2\u7acb\u6c89\uff0c\u94c1\u94a9\u5012\u7ad6\uff0c\u4e00\u62db\u201c\u9ec4\u8702\u523a\u201d\u5411\u4e0a\u75be\u523a\uff0c\u4ecd\u662f\u65e2\u72e0\u4e14\u51c6\u3002\u8881\u627f\u5fd7\u4e00\u62ff\u4e0d\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u6211\u6765\u6551\u4f60\uff01\u201d\u4f55\u94c1\u624b\u5018\u82e5\u4e0d\u95fb\uff0c\u53cc\u94a9\u5982\u72c2\u98ce\u9aa4\u96e8\u822c\u653b\u6765\u3002\u8881\u627f\u5fd7\u89e3\u62c6\u6570\u62db\uff0c\u53f3\u811a\u5728\u5979\u5c0f\u817f\u4e00\u52fe\uff0c\u4f55\u94c1\u624b\u6251\u5730\u5012\u4e0b\uff0c\u7a81\u7136\u7741\u773c\uff0c\u60ca\u53eb\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u6211\u6b7b\u4e86\u4e48\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u51fa\u53bb\uff01\u201d\u62c9\u4f4f\u5979\u624b\u81c2\u63d0\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8bf8\u6559\u4f17\u672c\u5728\u65c1\u89c2\u4e24\u4eba\u76f8\u6597\uff0c\u89c1\u8881\u627f\u5fd7\u6276\u7740\u5979\u6025\u5954\u800c\u51fa\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u7eb7\u7eb7\u62e5\u4e0a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8f6c\u8eab\u53eb\u9053\uff1a\u201c\u8c01\u6562\u4e0a\u6765\uff01\u201d\u6559\u4f17\u4e2a\u4e2a\u662f\u60ca\u5f13\u4e4b\u9e1f\uff0c\u4e0d\u77e5\u8c01\u5148\u53d1\u558a\uff0c\u5ffd\u5730\u4e00\u7a9d\u8702\u7684\u8f6c\u8eab\u9003\u5165\u6bbf\u5185\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u5173\u4e0a\u4e86\u6bbf\u95e8\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u4eec\u5bf9\u81ea\u5df1\u6015\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e0d\u89c9\u597d\u7b11\uff0c\u4fef\u8eab\u770b\u4f55\u94c1\u624b\u65f6\uff0c\u89c1\u5979\u5de6\u80a9\u9ad8\u80bf\uff0c\u96ea\u767d\u7684\u9762\u988a\u4e0a\u5df2\u7f69\u4e0a\u4e86\u4e00\u5c42\u9ed1\u6c14\uff0c\u77e5\u5979\u4e2d\u6bd2\u5df2\u6df1\uff0c\u4f46\u60f3\u5979\u65e5\u5915\u4e0e\u6bd2\u7269\u4e3a\u4f0d\uff0c\u6297\u529b\u751a\u5f3a\uff0c\u603b\u8fd8\u80fd\u652f\u6301\u4e00\u4f1a\uff0c\u4e8e\u662f\u62b1\u8d77\u5979\u5954\u56de\u5bd3\u6240\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u5ffd\u7136\u64d2\u4e86\u4f55\u94c1\u624b\u800c\u6765\uff0c\u90fd\u611f\u60ca\u5947\u3002\u9752\u9752\u55d4\u9053\uff1a\u201c\u4f60\u62b1\u7740\u5979\u5e72\u4e48\uff1f\u8fd8\u4e0d\u653e\u624b\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5feb\u62ff\u51b0\u87fe\u6551\u5979\u3002\u201d\u7126\u5b9b\u513f\u6276\u7740\u4f55\u94c1\u624b\u8d70\u8fdb\u5185\u5ba4\u65bd\u6551\u3002\u6c34\u4e91\u7b49\u5374\u751a\u662f\u6c14\u607c\uff0c\u4ea6\u89c9\u4e0d\u89e3\u3002\u8881\u627f\u5fd7\u628a\u524d\u56e0\u540e\u679c\u8bf4\u4e86\uff0c\u5e76\u9053\uff1a\u201c\u4ee4\u5e08\u9ec4\u6728\u9053\u4eba\u7684\u4e8b\uff0c\u7b49\u5979\u9192\u8f6c\u540e\uff0c\u81ea\u5f53\u67e5\u95ee\u660e\u767d\u3002\u201d\u4ed9\u90fd\u5f1f\u5b50\u4e00\u9f50\u62dc\u8c22\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u987f\u996d\u65f6\u5206\uff0c\u7126\u5b9b\u513f\u51fa\u6765\u8bf4\u9053\uff1a\u201c\u5979\u6bd2\u6c14\u6162\u6162\u9000\u4e86\uff0c\u4f46\u4ecd\u662f\u660f\u8ff7\u4e0d\u9192\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u7ed9\u5979\u670d\u4e9b\u89e3\u6bd2\u836f\uff0c\u8ba9\u5979\u7761\u4e00\u4f1a\u513f\u5427\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u5e94\u4e86\uff0c\u6b63\u8981\u8fdb\u53bb\uff0c\u7f57\u7acb\u5982\u4ece\u5916\u9762\u5306\u5306\u5954\u8fdb\uff0c\u53eb\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u5927\u559c\u5927\u559c\uff01\u201d\u9752\u9752\u7b11\u9053\uff1a\u201c\u4f60\u624d\u5927\u559c\u5440\uff01\u201d\u7f57\u7acb\u5982\u9053\uff1a\u201c\u95ef\u738b\u5927\u519b\u6253\u4e0b\u4e86\u5b81\u6b66\u5173\u3002\u201d\u4f17\u4eba\u4e00\u9f50\u6b22\u547c\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u8baf\u606f\u662f\u5426\u786e\u5b9e\uff1f\u201d\u7f57\u7acb\u5982\u9053\uff1a\u201c\u6211\u4eec\u5e2e\u91cc\u7684\u5f20\u5144\u5f1f\u672c\u6765\u5949\u547d\u53bb\u8ffd\u5bfb\u2026\u2026\u5bfb\u8fd9\u4f4d\u95f5\u4e8c\u7237\u7684\uff0c\u6070\u597d\u9047\u4e0a\u95ef\u519b\u653b\u5173\uff0c\u653b\u5b88\u53cc\u65b9\u6253\u5f97\u751a\u662f\u60e8\u70c8\uff0c\u8d70\u4e0d\u8fc7\u53bb\u3002\u540e\u6765\u4ed6\u773c\u89c1\u660e\u519b\u5927\u8d25\uff0c\u5b88\u57ce\u7684\u603b\u5175\u5468\u9047\u5409\u4e5f\u7ed9\u6740\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u90a3\u597d\u6781\u5566\uff0c\u4e49\u519b\u4e0d\u65e5\u5c31\u6765\u4eac\u5e08\uff0c\u54b1\u4eec\u7ed9\u4ed6\u6765\u4e2a\u91cc\u5e94\u5916\u5408\u3002\u201d \u3000\u3000\u6b64\u540e\u6570\u65e5\u4e4b\u4e2d\uff0c\u8881\u627f\u5fd7\u81ea\u671d\u81f3\u665a\uff0c\u5341\u5206\u5fd9\u788c\uff0c\u4f1a\u89c1\u4eac\u4e2d\u5404\u8def\u8c6a\u6770\uff0c\u5206\u6d3e\u90e8\u7f72\uff0c\u53ea\u5f85\u4e49\u519b\u5175\u4e34\u57ce\u4e0b\uff0c\u4e3e\u4e8b\u54cd\u5e94\u3002 \u3000\u3000\u8fd9\u5929\u51fa\u5916\u8bae\u4e8b\u56de\u6765\uff0c\u7126\u5b9b\u513f\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u90a3\u4f55\u6559\u4e3b\u4ecd\u662f\u660f\u8ff7\u4e0d\u9192\u3002\u201d\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5df2\u7ecf\u6709\u8bb8\u591a\u5929\u5566\uff0c\u600e\u4e48\u8fd8\u4e0d\u597d\uff1f\u201d\u5fd9\u968f\u7740\u7126\u5b9b\u513f\u5165\u5185\u63a2\u671b\uff0c\u53ea\u89c1\u4f55\u94c1\u624b\u9762\u8272\u6194\u60b4\uff0c\u8138\u65e0\u8840\u8272\uff0c\u5df2\u662f\u5944\u5944\u4e00\u606f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6c89\u601d\u7247\u523b\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u554a\u54df\uff01\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e38\u4eba\u4e2d\u6bd2\u4e4b\u540e\uff0c\u6bd2\u6c14\u9000\u5c3d\uff0c\u81ea\u7136\u6162\u6162\u5eb7\u590d\u3002\u4f46\u5979\u4ece\u5c0f\u73a9\u5f04\u6bd2\u7269\uff0c\u5e73\u65f6\u591a\u534a\u53c8\u670d\u7528\u751a\u4e48\u53e4\u602a\u836f\u6599\uff0c\u5bfb\u5e38\u6bd2\u7269\u4f24\u5979\u4e0d\u5f97\uff0c\u7136\u800c\u4e00\u65e6\u4e2d\u6bd2\uff0c\u5374\u6700\u662f\u5389\u5bb3\u4e0d\u8fc7\u3002\u6211\u8fde\u65e5\u5fd9\u788c\uff0c\u7adf\u6ca1\u60f3\u5230\u8fd9\u5c42\u3002\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u90a3\u600e\u4e48\u529e\uff1f\u201d\u8881\u627f\u5fd7\u8e0c\u8e87\u9053\uff1a\u201c\u9664\u975e\u628a\u90a3\u51b0\u87fe\u7ed9\u5979\u670d\u4e86\uff0c\u6216\u8bb8\u8fd8\u53ef\u6709\u6551\u2026\u2026\u4e0d\u8fc7\u6211\u4eec\u9760\u6b64\u81f3\u5b9d\u89e3\u6bd2\uff0c\u8981\u662f\u518d\u53d7\u4e94\u6bd2\u6559\u7684\u4f24\u5bb3\uff0c\u53ea\u6709\u675f\u624b\u5f85\u6bd9\u4e86\u3002\u201d\u7126\u5b9b\u513f\u4e5f\u611f\u597d\u751f\u4e3a\u96be\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e00\u62cd\u5927\u817f\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u867d\u7136\u8ddf\u54b1\u4eec\u65e0\u4eb2\u65e0\u6545\uff0c\u4f46\u5982\u6b64\u773c\u7741\u7741\u7684\u89c1\u5979\u9001\u547d\uff0c\u7ec8\u662f\u4e0d\u5fcd\uff0c\u7ed9\u5979\u670d\u4e86\u518d\u8bf4\u3002\u201d\u7126\u5b9b\u513f\u89c9\u5f97\u6b64\u4e8b\u751a\u9669\uff0c\u9887\u4e3a\u4e0d\u5b89\uff0c\u4f46\u8881\u627f\u5fd7\u65e2\u5982\u6b64\u5429\u5490\uff0c\u81ea\u5f53\u9075\u4ece\uff0c\u4e8e\u662f\u7814\u788e\u51b0\u87fe\uff0c\u7528\u9152\u8c03\u4e86\uff0c\u7ed9\u5979\u670d\u4e0b\u53bb\u3002\u8fc7\u4e0d\u5230\u4e00\u987f\u996d\u65f6\u5206\uff0c\u4f55\u94c1\u624b\u8138\u8272\u7531\u9752\u8f6c\u767d\uff0c\u547c\u5438\u4e5f\u5df2\u4e0d\u518d\u6c14\u82e5\u6e38\u4e1d\uff0c\u6162\u6162\u7c97\u91cd\u8d77\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u77e5\u9053\u5979\u8fd9\u6761\u547d\u662f\u6551\u56de\u6765\u4e86\uff0c\u9000\u4e86\u51fa\u53bb\u3002\u6d2a\u80dc\u6d77\u6b63\u5728\u627e\u4ed6\uff0c\u4e00\u89c1\u5230\uff0c\u5fd9\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4e94\u6bd2\u6559\u627e\u4e0a\u95e8\u6765\u5566\uff01\u201d\u8881\u627f\u5fd7\u7709\u5934\u4e00\u76b1\uff0c\u95ee\u9053\uff1a\u201c\u6709\u591a\u5c11\u4eba\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u4eba\u5df2\u5230\u4e86\u95e8\u5916\uff0c\u4e0d\u77e5\u540e\u9762\u8fd8\u6709\u591a\u5c11\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5bfb\u601d\uff1a\u201c\u4e94\u6bd2\u6559\u4e2d\u9664\u4f55\u6559\u4e3b\u4e00\u4eba\u4e4b\u5916\uff0c\u4f59\u4e0b\u7684\u6b66\u529f\u5747\u4e0d\u5982\u4f55\u9ad8\u5f3a\uff0c\u53ea\u662f\u9634\u72e0\u6bd2\u8fa3\uff0c\u65e0\u6240\u4e0d\u7528\u5176\u6781\u3002\u4ed6\u4eec\u672c\u6765\u89c1\u4e86\u6211\u5c31\u671b\u98ce\u800c\u9003\uff0c\u73b0\u4e0b\u5c45\u7136\u627e\u4e0a\u95e8\u6765\uff0c\u5b9a\u662f\u6709\u6043\u65e0\u6050\u3002\u90a3\u51b0\u87fe\u81f3\u5b9d\u53c8\u7ed9\u4f55\u94c1\u624b\u670d\u4e86\uff0c\u8981\u662f\u6709\u8c01\u518d\u4e2d\u4e86\u6bd2\uff0c\u90a3\u662f\u65e0\u53ef\u6551\u6cbb\u7684\u4e86\u3002\u201d\u5bf9\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u4f60\u53bb\u53eb\u5927\u4f19\u513f\u90fd\u805a\u96c6\u5927\u5385\uff0c\u4e0d\u5f97\u6211\u53f7\u4ee4\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u51fa\u6218\u3002\u201d\u6d2a\u80dc\u6d77\u5e94\u58f0\u53bb\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5feb\u6b65\u51fa\u5802\uff0c\u62a2\u51fa\u95e8\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u8d64\u4e86\u4e0a\u8eab\uff0c\u4e0b\u8eab\u7a7f\u7740\u4e00\u6761\u7834\u88e4\uff0c\u53cc\u624b\u6309\u5730\uff0c\u5934\u4e0b\u811a\u4e0a\u7684\u5012\u7acb\u5728\u95e8\u53e3\u3002\u8881\u627f\u5fd7\u89c1\u8fc7\u4e94\u6bd2\u6559\u6559\u4f17\u7684\u8bb8\u591a\u602a\u6a21\u6837\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u4ee5\u4e3a\u5f02\uff0c\u773c\u5149\u5f80\u4e0b\u671b\u65f6\uff0c\u89c1\u662f\u9526\u8863\u6bd2\u4e10\u9f50\u4e91\u7488\u3002\u53ea\u89c1\u4ed6\u80a9\u5934\u3001\u80cc\u4e0a\u3001\u53cc\u81c2\u4e00\u5171\u63d2\u4e86\u4e5d\u67c4\u660e\u6643\u6643\u7684\u5c3a\u6765\u957f\u5c16\u5200\uff0c\u6bcf\u628a\u5200\u90fd\u6df1\u5165\u8089\u91cc\uff0c\u5374\u65e0\u9c9c\u8840\u6d41\u51fa\u3002\u8fd9\u65f6\u9526\u8863\u6bd2\u4e10\u5374\u6210\u4e86\u70c2\u88e4\u6bd2\u4e10\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e25\u52a0\u9632\u8303\uff0c\u4e0d\u77e5\u4ed6\u4f7f\u4f55\u5996\u6cd5\uff0c\u559d\u95ee\uff1a\u201c\u4f60\u6765\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u9f50\u4e91\u7488\u4e0d\u7b54\uff0c\u5927\u58f0\u5ff5\u9053\uff1a\u201c\u4e5d\u5200\u7a7f\u6d1e\uff0c\u4e3a\u5974\u5c3d\u5fe0\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u8ddf\u8d35\u6559\u4ee5\u540e\u5404\u8d70\u5404\u8def\u3002\u4f60\u4eec\u522b\u6765\u7ea0\u7f20\uff0c\u6211\u4e5f\u4e0d\u4e0e\u4f60\u4eec\u4e3a\u96be\u3002 \u3000\u3000\u4f60\u5feb\u8d70\u5427\uff01\u201d\u9f50\u4e91\u7488\u72b9\u5982\u4e2d\u90aa\u7740\u9b54\u4e00\u822c\uff0c\u4e0d\u4f4f\u7684\u5ff5\uff1a\u201c\u4e5d\u5200\u7a7f\u6d1e\uff0c\u4e3a\u5974\u5c3d\u5fe0\uff01\u201d\u8881\u627f\u5fd7\u4ed4\u7ec6\u518d\u770b\uff0c\u89c1\u6bcf\u628a\u5200\u7684\u5200\u67c4\u4e0a\u90fd\u7f1a\u7740\u4e00\u4ef6\u6bd2\u7269\uff0c\u6709\u7684\u662f\u874e\u5b50\uff0c\u6709\u7684\u662f\u8708\u86a3\uff0c\u90fd\u5728\u8815\u8815\u800c\u52a8\u3002 \u3000\u3000\u8fd9\u65f6\u6d2a\u80dc\u6d77\u5df2\u9080\u96c6\u4f17\u4eba\uff0c\u805a\u5728\u5385\u4e2d\uff0c\u4ed6\u72ec\u81ea\u51fa\u6765\u5bdf\u770b\u3002\u8881\u627f\u5fd7\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u6d2a\u80dc\u6d77\u4f1a\u610f\uff0c\u542c\u6e05\u695a\u4e86\u9f50\u4e91\u7488\u7684\u8bdd\uff0c\u8fd4\u5954\u5165\u5185\uff0c\u4e0e\u7126\u5b9b\u513f\u4e00\u540c\u6765\u5230\u4f55\u94c1\u624b\u5ba4\u4e2d\uff0c\u95ee\u9053\uff1a\u201c\u4f55\u6559\u4e3b\uff0c\u2018\u4e5d\u5200\u7a7f\u6d1e\uff0c\u4e3a\u5974\u5c3d\u5fe0\u2019\uff0c\u90a3\u662f\u751a\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4f55\u94c1\u624b\u670d\u4e86\u51b0\u87fe\u4e4b\u540e\uff0c\u795e\u667a\u6e10\u590d\uff0c\u542c\u5f97\u6d2a\u80dc\u6d77\u7684\u8bdd\uff0c\u5fd9\u5373\u5750\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u8c01\u6765\u4e86\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u4e00\u4e2a\u4e0a\u8eab\u4e0d\u7a7f\u8863\u670d\u7684\u53eb\u5316\u5b50\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u597d\u3002\u4f60\u8fd9\u4f4d\u59d1\u5a18\uff0c\u8bf7\u4f60\u6276\u6211\u51fa\u53bb\u3002\u201d\u7126\u5b9b\u513f\u89c1\u5979\u91cd\u75c5\u521d\u6709\u8d77\u8272\uff0c\u4e0d\u5b9c\u4fbf\u5373\u8d77\u5e8a\uff0c\u6b63\u60f3\u529d\u963b\uff0c\u4f55\u94c1\u624b\u6446\u6446\u624b\u547d\u6d2a\u80dc\u6d77\u51fa\u623f\uff0c\u5750\u8d77\u8eab\u6765\uff0c\u6162\u6162\u7a7f\u4e0a\u957f\u8863\u3002\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u51fa\u53bb\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u6276\u6211\u4e00\u628a\u3002\u201d\u7126\u5b9b\u513f\u4f38\u624b\u76f8\u6276\u3002 \u3000\u3000\u4f55\u94c1\u624b\u53f3\u624b\u4e00\u7ffb\uff0c\u5df2\u62ff\u4f4f\u4e86\u5979\u624b\u8155\u3002\u7126\u5b9b\u513f\u5403\u4e86\u4e00\u60ca\uff0c\u624b\u4e0a\u767b\u5982\u5957\u4e86\u4e00\u53ea\u94a2\u7b8d\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u968f\u5979\u8d70\u5230\u95e8\u53e3\uff0c\u4e0d\u7531\u5f97\u53c8\u662f\u5bb3\u6015\uff0c\u53c8\u662f\u94a6\u4f69\u3002 \u3000\u3000\u4f55\u94c1\u624b\u8de8\u51fa\u5927\u95e8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u77a7\u77a7\uff0c\u6211\u4e0d\u662f\u597d\u597d\u6d3b\u7740\u4e48\uff1f\u201d \u3000\u3000\u9f50\u4e91\u7488\u8138\u73b0\u559c\u8272\uff0c\u53cc\u624b\u4e00\u633a\uff0c\u5728\u7a7a\u4e2d\u7ffb\u4e86\u4e2a\u7b4b\u6597\uff0c\u4ecd\u7136\u5934\u4e0b\u811a\u4e0a\u7684\u5012\u7acb\u3002 \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u53c8\u4e3a\u751a\u4e48\u6765\u4e86\uff1f\u4f60\u82e5\u4e0d\u662f\u8d70\u6295\u65e0\u8def\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u540e\u6094\u3002\u201d\u9f50\u4e91\u7488\u9053\uff1a\u201c\u6559\u4e3b\u660e\u9274\uff0c\u5c0f\u7684\u7f6a\u8be5\u4e07\u6b7b\uff0c\u4f24\u4e86\u6559\u4e3b\u5c0a\u4f53\uff0c\u591a\u8499\u4e09\u7956\u4e03\u5b50\u4fdd\u4f51\uff0c\u6559\u4e3b\u65e0\u6059\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u559d\u9053\uff1a\u201c\u4f60\u53ea\u9053\u7528\u91d1\u86c7\u4f24\u4e86\u6211\uff0c\u6211\u52bf\u5fc5\u4e27\u547d\uff0c\u6309\u672c\u6559\u89c4\u77e9\uff0c\u4f60\u4fbf\u662f\u6559\u4e3b\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u9f50\u4e91\u7488\u9053\uff1a\u201c\u5c0f\u7684\u8be5\u53d7\u4e07\u86c7\u566c\u8eab\u5927\u7f6a\uff0c\u53ea\u6c42\u6559\u4e3b\u5f00\u6069\u5bbd\u8d66\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u53bb\u5427\uff01\u201d\u9f50\u4e91\u7488\u53cc\u81c2\u4e00\u5c48\u4e00\u4f38\uff0c\u989d\u89d2\u4e0d\u4f4f\u78b0\u5728\u5730\u4e0a\u884c\u793c\uff0c\u7830\u7830\u6709\u58f0\u3002\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u6765\u8c22\u7f6a\uff1f\u201d\u9f50\u4e91\u7488\u9053\uff1a\u201c\u5c0f\u7684\u4e0d\u6562\u76f8\u7792\u6559\u4e3b\u3002\u7167\u6559\u4e2d\u89c4\u77e9\uff0c\u539f\u8be5\u7531\u5c0f\u7684\u7ee7\u4efb\u6559\u4e3b\uff0c\u4f46\u90a3\u8001\u4e5e\u5a46\u4e0e\u5c0f\u7684\u76f8\u4e89\uff0c\u5c0f\u7684\u654c\u4ed6\u4e0d\u8fc7\u2026\u2026\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u4f60\u4e0d\u5b89\u597d\u5fc3\uff0c\u73b0\u4eca\u65e2\u5df2\u5bf9\u6211\u5f52\u670d\u5c3d\u5fe0\uff0c\u4fbf\u9976\u4f60\u4e00\u547d\u3002\u201d\u8bf4\u7740\u4fef\u8eab\u5728\u4ed6\u80a9\u5934\u62d4\u8d77\u4e00\u5200\u3002\u9f50\u4e91\u7488\u5927\u559c\uff0c\u884c\u4e86\u4e00\u793c\uff0c\u7ffb\u8eab\u76f4\u7acb\uff0c\u5927\u8e0f\u6b65\u53bb\u4e86\u3002 \u3000\u3000\u4f55\u94c1\u624b\u6276\u7740\u7126\u5b9b\u513f\u56de\u5230\u5385\u4e2d\uff0c\u4f17\u4eba\u90fd\u5bf9\u521a\u624d\u7684\u602a\u4e8b\u4e0d\u660e\u6240\u4ee5\u3002\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u4ed6\u7ed9\u903c\u5230\u4e86\u7a77\u9014\u672b\u8def\uff0c\u5728\u6559\u91cc\u5df2\u5bb9\u8eab\u4e0d\u5f97\uff0c\u624d\u6765\u6c42\u6211\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u8fd9\u4e9b\u5200\u5b50\u5e72\u751a\u4e48\u5440\uff1f\u201d \u3000\u3000\u4f55\u94c1\u624b\u628a\u5200\u4e0a\u7f1a\u7740\u7684\u4e00\u53ea\u874e\u5b50\u53d6\u4e86\u4e0b\u6765\uff0c\u62ff\u624b\u5e15\u5305\u4e86\u51e0\u91cd\uff0c\u653e\u5165\u6000\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u7684\u90aa\u6cd5\uff0c\u5404\u4f4d\u4e0d\u8981\u89c1\u7b11\u3002\u4e5d\u67c4\u5200\u4e0a\u90fd\u6709\u866b\u8c78\u7684\u5267\u6bd2\uff0c\u6bcf\u4e00\u6761\u866b\u6bd2\u6027\u4e0d\u540c\uff0c\u4ee5\u6bd2\u653b\u6bd2\uff0c\u53ea\u6709\u7528\u539f\u6765\u866b\u8c78\u7684\u6bd2\u6c41\uff0c\u518d\u548c\u4e0a\u522b\u7684\u836f\u6750\uff0c\u65b9\u80fd\u6cbb\u597d\u3002\u6211\u6bcf\u5929\u7ed9\u4ed6\u62d4\u4e00\u67c4\u5200\uff0c\u5200\u4e0a\u6bd2\u866b\u5c31\u7531\u6211\u6536\u4e86\u8d77\u6765\uff0c\u4ee5\u540e\u6bcf\u5e74\u7aef\u5348\uff0c\u4ed6\u4f53\u5185\u6bd2\u53d1\uff0c\u6211\u5c31\u7ed9\u4ed6\u670d\u4e00\u5242\u89e3\u836f\u3002\u201d\u9752\u9752\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u6837\u4ed6\u6c38\u8fdc\u505a\u4f60\u7684\u5974\u4ec6\uff0c\u4e0d\u6562\u8d77\u53cd\u53db\u4e4b\u5fc3\u3002\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u590f\u76f8\u516c\u6599\u5f97\u4e0d\u9519\u3002\u201d \u3000\u3000\u9752\u9752\u53c8\u95ee\uff1a\u201c\u90a3\u4e48\u4ed6\u81ea\u5df1\u628a\u5200\u62d4\u4e0b\u6765\u4e0d\u6210\u4e48\uff1f\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u90a3\u4e9b\u5200\u662f\u4ed6\u81ea\u5df1\u63d2\u4e0a\u53bb\u7684\u3002\u4ed6\u6765\u6c42\u6211\u62d4\uff0c\u5c31\u662f\u5411\u6211\u5f52\u987a\u3002\u4ed6\u66fe\u7528\u91d1\u86c7\u4f24\u6211\uff0c\u5982\u4e0d\u7528\u8fd9\u4e5d\u5200\u5927\u6cd5\uff0c\u77e5\u9053\u6211\u51b3\u4e0d\u80fd\u9976\u8d66\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u5e72\u4e48\u4e0d\u4e00\u6b21\u7ed9\u4ed6\u62d4\u4e0b\u6765\uff1f\u4ed6\u8eab\u4e0a\u8fd8\u6709\u516b\u67c4\u5200\uff0c\u5c82\u4e0d\u662f\u8fd8\u5f97\u75db\u4e0a\u516b\u5929\uff1f\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u53ef\u6076\uff0c\u5c31\u662f\u8981\u4ed6\u591a\u5403\u70b9\u82e6\u5934\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u8981\u662f\u590f\u76f8\u516c\u9976\u4e86\u4ed6\uff0c\u660e\u513f\u6211\u5c31\u4e00\u9f50\u62d4\u4e86\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u7531\u5f97\u4f60\u5427\u3002\u6211\u4e5f\u4e0d\u53ef\u601c\u8fd9\u79cd\u6076\u4eba\uff01\u201d \u3000\u3000\u6c34\u4e91\u5f85\u5979\u4eec\u8c08\u5f97\u544a\u4e86\u4e00\u4e2a\u6bb5\u843d\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u4e3e\u624b\u4e3a\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4f55\u6559\u4e3b\uff0c\u6211\u4eec\u5e08\u7236\u7684\u4e8b\uff0c\u8bf7\u60a8\u77a7\u5728\u8881\u76f8\u516c\u4efd\u4e0a\uff0c\u660e\u767d\u8d50\u544a\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4ed9\u90fd\u4f17\u5f1f\u5b50\u90fd\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u4f55\u94c1\u624b\u51b7\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\u4e8e\u6211\u6709\u6069\uff0c\u8ddf\u4f60\u4eec\u4ed9\u90fd\u6d3e\u53ef\u6ca1\u5e72\u7cfb\u3002\u6211\u8eab\u5b50\u8fd8\u6ca1\u590d\u539f\uff0c\u4f60\u4eec\u662f\u4e0d\u662f\u8981\u4e58\u4eba\u4e4b\u5371\uff1f\u6211\u4f55\u94c1\u624b\u4e5f\u4e0d\u5728\u4e4e\u3002\u201d\u5979\u5982\u6b64\u6a2a\u86ee\u65e0\u793c\uff0c\u53ef\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5411\u6c34\u4e91\u7b49\u4e00\u4f7f\u773c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f55\u6559\u4e3b\u8eab\u5b50\u4e0d\u9002\uff0c\u54b1\u4eec\u6162\u6162\u518d\u8c08\u3002\u201d\u4f55\u94c1\u624b\u54fc\u4e86\u4e00\u58f0\uff0c\u6276\u7740\u7126\u5b9b\u513f\u8fdb\u623f\u53bb\u4e86\u3002\u4ed9\u90fd\u8bf8\u5f1f\u5b50\u6c14\u52bf\u6c79\u6c79\uff0c\u4e03\u5634\u516b\u820c\u7684\u8bae\u8bba\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u4e8b\u4ea4\u5728\u5144\u5f1f\u8eab\u4e0a\u3002\u9ec4\u6728\u9053\u957f\u7684\u4e0b\u843d\uff0c\u6211\u8d1f\u8d23\u6253\u63a2\u51fa\u6765\u4fbf\u662f\u3002\u201d\u4ed9\u90fd\u8bf8\u4eba\u8fd9\u624d\u5e73\u606f\u3002 \u3000\u3000\u6b21\u65e5\u9f50\u4e91\u7488\u53c8\u6765\uff0c\u4f55\u94c1\u624b\u7ed9\u4ed6\u62d4\u4e86\u4e00\u5200\uff0c\u63a5\u8fde\u6570\u65e5\u90fd\u662f\u5982\u6b64\u3002 \u3000\u3000\u8fd9\u6570\u65e5\u4e2d\uff0c\u95ef\u519b\u6377\u62a5\u72b9\u5982\u6d41\u6c34\u4ef7\u62a5\u6765\uff1a\u660e\u519b\u603b\u5175\u59dc\u73ae\u6295\u964d\uff0c\u95ef\u519b\u514b\u5927\u540c\uff1b\u603b\u5175\u738b\u627f\u80e4\u3001\u76d1\u519b\u592a\u76d1\u675c\u52cb\u6295\u964d\uff0c\u95ef\u519b\u514b\u5ba3\u5e9c\uff1b\u603b\u5175\u5510\u901a\u3001\u76d1\u519b\u592a\u76d1\u675c\u4e4b\u79e9\u6295\u964d\uff0c\u95ef\u519b\u514b\u5c45\u5eb8\u3002 \u3000\u3000\u90a3\u5927\u540c\u3001\u5ba3\u5e9c\u3001\u5c45\u5eb8\uff0c\u90fd\u662f\u4eac\u5e08\u5916\u56f4\u8981\u585e\uff0c\u5411\u6765\u9a7b\u6709\u91cd\u5175\u9632\u5b88\u3002\u6bcf\u4e00\u540d\u603b\u5175\u5747\u7edf\u5e26\u7cbe\u5175\u6570\u4e07\u3002\u5d07\u796f\u4e0d\u4fe1\u6b66\u5c06\uff0c\u6bcf\u519b\u90fd\u6d3e\u6709\u4eb2\u4fe1\u592a\u76d1\u76d1\u519b\uff0c\u6743\u529b\u5728\u603b\u5175\u4e4b\u4e0a\u3002\u4f46\u95ef\u519b\u4e00\u5230\uff0c\u76d1\u519b\u592a\u76d1\u548c\u603b\u5175\u5b98\u4e00\u9f50\u6295\u964d\u3002\u91cd\u9547\u8981\u5730\uff0c\u95ef\u519b\u90fd\u662f\u4e0d\u8d39\u4e00\u5175\u4e00\u5352\u800c\u4e0b\u3002 \u3000\u3000\u6570\u65e5\u4e4b\u95f4\uff0c\u660e\u519b\u571f\u5d29\u74e6\u89e3\uff0c\u5317\u4eac\u57ce\u4e2d\uff0c\u4e71\u6210\u4e00\u7247\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u8baf\u606f\u4f20\u6765\uff0c\u95ef\u519b\u5df2\u514b\u660c\u5e73\uff0c\u5317\u4eac\u57ce\u5916\u4eac\u8425\u4e09\u5927\u8425\u4e00\u9f50\u6e83\u6563\uff0c\u773c\u89c1\u95ef\u519b\u5df2\u53ef\u553e\u624b\u800c\u53d6\u5317\u4eac\u3002 \u3000\u3000\u53c8\u8fc7\u6570\u5929\uff0c\u9f50\u4e91\u7488\u8eab\u4e0a\u53ea\u4f59\u4e0b\u4e00\u67c4\u6bd2\u5200\u672a\u62d4\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u6765\u5230\u95e8\u5916\u3002\u6d2a\u80dc\u6d77\u7980\u62a5\u8fdb\u53bb\u3002\u8fd9\u65f6\u4f55\u94c1\u624b\u5df2\u6bd2\u6e05\u75ca\u6108\uff0c\u4f17\u4eba\u60f3\u770b\u9f50\u4e91\u7488\u8eab\u4e0a\u6bd2\u5200\u62d4\u9664\u4e4b\u540e\uff0c\u4f55\u94c1\u624b\u5982\u4f55\u5bf9\u4ed6\uff0c\u90fd\u8ddf\u5979\u8d70\u51fa\u5927\u95e8\u3002\u4f55\u94c1\u624b\u8f6c\u5934\u5bf9\u9752\u9752\u7b11\u9053\uff1a\u201c\u590f\u76f8\u516c\uff0c\u8fd9\u4eba\u867d\u7136\u672c\u6027\u6076\u52a3\uff0c\u4f46\u6b66\u529f\u5374\u5f3a\uff0c\u6211\u9001\u7ed9\u4f60\u505a\u5974\u4ec6\u597d\u4e0d\u597d\uff1f\u4f60\u6709\u89e3\u836f\u5728\u624b\uff0c\u4ed6\u7ec8\u8eab\u4e0d\u6562\u8fdd\u80cc\u4f60\u534a\u53e5\u8bdd\u3002\u201d \u3000\u3000\u9752\u9752\u6120\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u5973\u5b69\u513f\u5bb6\uff0c\u8981\u8fd9\u81ed\u7537\u4eba\u8ddf\u5728\u8eab\u65c1\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u4f55\u94c1\u624b\u5927\u5403\u4e00\u60ca\uff0c\u81ea\u8bc6\u9752\u9752\u4ee5\u6765\uff0c\u89c1\u5979\u59cb\u7ec8\u7a7f\u7740\u7537\u88c5\uff0c\u8d8a\u77a7\u8d8a\u662f\u5fc3\u7231\uff0c\u7adf\u6ca1\u77a7\u51fa\u5979\u662f\u5973\u5b50\u6240\u626e\u3002\u65c1\u4eba\u660e\u77e5\u4f55\u94c1\u624b\u8bef\u4f1a\uff0c\u4f46\u90fd\u6015\u5979\u72e0\u6bd2\u5389\u5bb3\uff0c\u8c01\u4e5f\u4e0d\u6562\u7a0d\u9732\u53e3\u98ce\u3002\u8881\u627f\u5fd7\u8fde\u65e5\u5fd9\u4e8e\u8fce\u63a5\u95ef\u519b\u7684\u5927\u4e8b\uff0c\u5168\u6ca1\u60f3\u5230\u6b64\u8282\u3002\u4ee5\u81f4\u4f55\u94c1\u624b\u4e00\u76f4\u8499\u5728\u9f13\u91cc\uff0c\u8fd9\u65f6\u542c\u9752\u9752\u4e00\u8bf4\uff0c\u5446\u4e86\u534a\u664c\uff0c\u95ee\u9053\uff1a\u201c\u751a\u2026\u2026\u751a\u4e48\uff1f\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u4e0d\u8981\u3002\u201d\u4f55\u94c1\u624b\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u751a\u4e48\u5973\u5b69\u513f\u5bb6\uff1f\u201d \u3000\u3000\u7126\u5b9b\u513f\u9000\u5f00\u4e24\u6b65\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f55\u6559\u4e3b\uff0c\u8fd9\u4f4d\u662f\u590f\u59d1\u5a18\u554a\u3002 \u3000\u3000\u5979\u4ece\u5c0f\u7231\u7a7f\u7537\u88c5\uff0c\u522b\u8bf4\u4f60\u8ba4\u4e0d\u51fa\u6765\uff0c\u6211\u4eec\u5927\u5bb6\u521d\u6b21\u89c1\u5230\uff0c\u4e5f\u603b\u5f53\u662f\u4e00\u4f4d\u76f8\u516c\u3002\u201d \u3000\u3000\u4f55\u94c1\u624b\u773c\u524d\u4e00\u82b1\uff0c\u5934\u8111\u4e2d\u4e00\u9635\u6655\u7729\uff0c\u5b9a\u795e\u7ec6\u770b\uff0c\u89c1\u9752\u9752\u9762\u8272\u767d\u817b\uff0c\u53cc\u7709\u5f2f\u5f2f\uff0c\u786e\u662f\u4e00\u4e2a\u7f8e\u8c8c\u5973\u5b50\uff0c\u4e0d\u7981\u53c8\u6c14\u53c8\u6068\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u600e\u4e48\u5982\u6b64\u80e1\u6d82\uff0c\u7adf\u4e3a\u4e00\u4e2a\u5973\u5b50\u800c\u53db\u6559\uff1f\u5f04\u5f97\u8eab\u8d25\u540d\u88c2\uff0c\u6211\u2026\u2026\u6211\u4e5f\u4e0d\u8981\u6d3b\u4e86\u3002\u201d\u5979\u6027\u5b50\u521a\u786c\uff0c\u5fc3\u4e2d\u8d8a\u6c14\uff0c\u8138\u4e0a\u8d8a\u662f\u9732\u51fa\u7b11\u5bb9\uff0c\u5c0f\u5634\u4e00\u5f20\uff0c\u5de6\u988a\u9732\u51fa\u4e00\u4e2a\u9152\u7a9d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u771f\u662f\u80e1\u6d82\u5566\uff01\u201d\u8d70\u4e0b\u9636\u77f3\uff0c\u4fef\u8eab\u53bb\u62d4\u9f50\u4e91\u7488\u80cc\u4e0a\u6700\u540e\u4e00\u67c4\u6bd2\u5200\u3002\u4f46\u9976\u662f\u5979\u8981\u5f3a\u597d\u80dc\uff0c\u7ec8\u7a76\u500f\u906d\u5927\u53d8\uff0c\u5fc3\u795e\u4e0d\u5b9a\uff0c\u4e0d\u7531\u5f97\u53cc\u8db3\u53d1\u8f6f\uff0c\u8eab\u5b50\u4e00\u4e0b\u6447\u6643\u3002 \u3000\u3000\u7126\u5b9b\u513f\u6b63\u8981\u4e0a\u524d\u76f8\u6276\uff0c\u7a81\u7136\u8def\u65c1\u4e00\u58f0\u5389\u53eb\uff0c\u4e00\u4eba\u84e6\u5730\u7a9c\u5c06\u51fa\u6765\uff0c\u7eb5\u5230\u9f50\u4e91\u7488\u8eab\u540e\uff0c\u4e00\u5f2f\u8170\uff0c\u53c8\u7eb5\u4e86\u5f00\u53bb\u3002\u53ea\u542c\u9f50\u4e91\u7488\u72c2\u558a\u4e00\u58f0\uff0c\u4fef\u4f0f\u5728\u5730\uff0c\u80cc\u540e\u90a3\u67c4\u5c3a\u6765\u957f\u7684\u6bd2\u5200\u5df2\u6df1\u5165\u80cc\u5fc3\uff0c\u76f4\u6ca1\u81f3\u5200\u67c4\u3002\u8fd9\u4e00\u4e0b\u72b9\u5982\u6674\u7a7a\u9739\u96f3\uff0c\u6b63\u6240\u8c13\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\uff0c\u867d\u6709\u8881\u627f\u5fd7\u3001\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u3001\u54d1\u5df4\u7b49\u8bb8\u591a\u9ad8\u624b\u5728\u65c1\uff0c\u4f46\u6ca1\u4e00\u4e2a\u6765\u5f97\u53ca\u65bd\u6551\u3002 \u3000\u3000\u4f17\u4eba\u9f50\u58f0\u60ca\u547c\uff0c\u770b\u90a3\u7a81\u65bd\u6bd2\u624b\u7684\u4eba\u65f6\uff0c\u6b63\u662f\u8001\u4e5e\u5a46\u4f55\u7ea2\u836f\u3002\u5374\u89c1\u5979\u554a\u554a\u602a\u53eb\uff0c\u5de6\u624b\u6325\u821e\uff0c\u53cc\u8db3\u4e71\u8df3\uff0c\u5374\u603b\u662f\u6454\u4e0d\u5f00\u54ac\u5728\u5979\u624b\u80cc\u4e0a\u7684\u4e00\u6761\u5c0f\u91d1\u86c7\u3002\u9f50\u4e91\u7488\u62ac\u5934\u53eb\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d\u8eab\u5b50\u4e00\u9635\u626d\u52a8\uff0c\u5782\u9996\u800c\u6b7b\u3002\u4f17\u4eba\u77a7\u7740\u4f55\u7ea2\u836f\uff0c\u53ea\u89c1\u5979\u8138\u4e0a\u5c3d\u662f\u6016\u60e7\u4e4b\u8272\uff0c\u4e00\u5f20\u672c\u5c31\u6ee1\u662f\u4f24\u75a4\u7684\u8138\uff0c\u66f4\u52a0\u4ee4\u4eba\u4e0d\u5fcd\u591a\u77a7\u4e00\u773c\u3002 \u3000\u3000\u5979\u53f3\u624b\u51e0\u756a\u4f38\u51fa\uff0c\u60f3\u53bb\u62c9\u626f\u91d1\u86c7\uff0c\u521a\u8981\u78b0\u5230\u65f6\u53c8\u5373\u7f29\u56de\uff0c\u4f3c\u4e4e\u4e00\u78b0\u91d1\u86c7\u7684\u8eab\u5b50\u4fbf\u6709\u5927\u7978\u4e34\u5934\u4e00\u822c\u3002 \u3000\u3000\u4f55\u94c1\u624b\u53ea\u662f\u563b\u563b\u800c\u7b11\uff0c\u8896\u624b\u4e0d\u8bed\u3002\u4f55\u7ea2\u836f\u767d\u773c\u4e00\u7ffb\uff0c\u5ffd\u5730\u4ece\u6000\u91cc\u6478\u51fa\u4e00\u67c4\u5229\u5203\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u5693\u4e00\u58f0\uff0c\u5df2\u628a\u81ea\u5df1\u5de6\u624b\u780d\u4e0b\uff0c\u6025\u901f\u6495\u4e0b\u8863\u895f\u5305\u4f4f\u4f24\u53e3\uff0c\u72c2\u5954\u800c\u53bb\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u5230\u8fd9\u60ca\u5fc3\u52a8\u9b44\u7684\u4e00\u5e55\uff0c\u90fd\u5446\u4f4f\u4e86\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4f55\u94c1\u624b\u5f2f\u4e0b\u8170\u53bb\uff0c\u5728\u9f50\u4e91\u7488\u8eab\u4e0a\u6478\u51fa\u4e00\u4e2a\u94c1\u7b52\uff0c\u7f69\u5728\u91d1\u86c7\u8eab\u4e0a\uff0c\u5de6\u624b\u94c1\u94a9\u5728\u4f55\u7ea2\u836f\u7684\u65ad\u624b\u4e0a\u4e00\u5212\uff0c\u5207\u4e0b\u91d1\u86c7\u54ac\u4f4f\u7684\u624b\u80cc\u8089\uff0c\u8fde\u8089\u548c\u86c7\u5012\u5728\u7b52\u91cc\uff0c\u76d6\u4e0a\u585e\u5b50\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u8fd9\u91d1\u86c7\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d\u4f55\u94c1\u624b\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u59d3\u9f50\u7684\u867d\u7136\u6c42\u6211\u6536\u7559\uff0c\u4f46\u603b\u4e0d\u653e\u5fc3\uff0c\u6015\u6211\u89c1\u5bb3\uff0c\u56e0\u6b64\u5728\u7b2c\u4e5d\u67c4\u5200\u65c1\u6697\u85cf\u91d1\u86c7\u3002\u5018\u82e5\u6211\u7ed9\u4ed6\u62d4\u5200\uff0c\u90a3\u5c31\u7f62\u4e86\uff0c\u5982\u6709\u52a0\u5bb3\u4e4b\u610f\uff0c\u4ed6\u4fbf\u4ee5\u91d1\u86c7\u53cd\u51fb\u3002\u54fc\u54fc\uff0c\u54ea\u77e5\u59d1\u59d1\u5374\u653e\u4ed6\u4e0d\u8fc7\u3002\u603b\u7b97\u5979\u5fc3\u72e0\u5f97\u4e0b\uff0c\u5207\u4e0b\u4e86\u81ea\u5df1\u7684\u624b\uff0c\u518d\u8fdf\u7247\u523b\uff0c\u5c31\u4e0d\u53ef\u6551\u4e86\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u4f60\u7684\u5de6\u624b\uff0c\u4e5f\u662f\u8fd9\u6837\u5272\u65ad\u7684\u4e48\uff1f\u201d\u4f55\u94c1\u624b\u6a2a\u4e86\u5979\u4e00\u773c\uff0c\u5e76\u4e0d\u56de\u7b54\uff0c\u5ffd\u5730\u63a9\u9762\u5954\u5165\u3002\u9752\u9752\u78b0\u4e86\u4e00\u4e2a\u9489\u5b50\uff0c\u6c14\u9053\uff1a\u201c\u8fd9\u4eba\u4e5f\u771f\u602a\u3002\u201d \u3000\u3000\u7126\u5b9b\u513f\u8138\u73b0\u5fe7\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u53bb\u966a\u966a\u5979\uff0c\u522b\u51fa\u751a\u4e48\u4e71\u5b50\u3002\u201d \u3000\u3000\u5165\u5185\u7247\u523b\uff0c\u968f\u5373\u5306\u5306\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4f55\u6559\u4e3b\u5173\u5728\u623f\u91cc\uff0c\u6211\u53eb\u5979\u603b\u662f\u4e0d\u7406\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8ba9\u5979\u4f11\u606f\u4e00\u4f1a\u5427\u3002\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u77a7\u60c5\u5f62\u4e0d\u5bf9\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u77a7\u77a7\u5979\u53bb\u3002\u201d \u3000\u3000\u4e09\u4eba\u6765\u5230\u4f55\u94c1\u624b\u623f\u5916\uff0c\u7126\u5b9b\u513f\u4f38\u624b\u62cd\u95e8\uff0c\u91cc\u9762\u5bc2\u65e0\u56de\u97f3\u3002 \u3000\u3000\u7126\u5b9b\u513f\u7ed5\u5230\u7a97\u53e3\uff0c\u5f80\u91cc\u4e00\u5f20\uff0c\u7a81\u7136\u5927\u53eb\uff1a\u201c\u4e0d\u597d\u5566\uff0c\u8881\u76f8\u516c\uff0c\u5feb\u6765\uff01\u201d\u5979\u8bed\u58f0\u752b\u6bd5\uff0c\u53cc\u638c\u5df2\u63a8\u5f00\u6728\u7a97\uff0c\u98de\u8eab\u5165\u53bb\u3002\u8881\u627f\u5fd7\u548c\u9752\u9752\u8ddf\u7740\u8dc3\u8fdb\u3002\u53ea\u89c1\u4f55\u94c1\u624b\u89e3\u5f00\u8863\u895f\uff0c\u8dea\u5728\u4e00\u5c0a\u5c0f\u5c0f\u7684\u6728\u96d5\u50cf\u9762\u524d\uff0c\u53f3\u624b\u62ff\u4f4f\u91d1\u86c7\uff0c\u6b63\u8981\u653e\u5230\u81ea\u5df1\u5589\u5934\u3002\u8881\u627f\u5fd7\u53f3\u624b\u75be\u6325\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u4e00\u679a\u94dc\u94b1\u7834\u7a7a\u800c\u53bb\uff0c\u6253\u5165\u91d1\u86c7\u53e3\u4e2d\u3002\u4f55\u94c1\u624b\u4e00\u60ca\uff0c\u653e\u4e0b\u91d1\u86c7\uff0c\u4f0f\u5728\u684c\u4e0a\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u9752\u9752\u62a2\u8fc7\u94c1\u7ba1\uff0c\u628a\u91d1\u86c7\u6536\u5165\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5e72\u4e48\u8981\u81ea\u5bfb\u77ed\u89c1\uff1f \u3000\u3000\u4f60\u6559\u4e2d\u90a3\u4e9b\u5bb6\u4f19\u4e0d\u542c\u4f60\u8bdd\uff0c\u4f60\u8ddf\u6211\u4eec\u5728\u4e00\u8d77\u4e0d\u597d\u4e48\uff1f\u201d\u4f55\u94c1\u624b\u53ea\u662f\u54ed\u6ce3\u3002\u8881\u627f\u5fd7\u529d\u9053\uff1a\u201c\u4f55\u6559\u4e3b\uff0c\u4e94\u6bd2\u6559\u672c\u662f\u5bb3\u4eba\u90aa\u6559\u3002\u4f60\u5f03\u90aa\u5f52\u6b63\uff0c\u8ddf\u4e94\u6bd2\u6559\u4e00\u5200\u4e24\u65ad\uff0c\u90a3\u662f\u4f55\u7b49\u7f8e\u4e8b\uff0c\u53c8\u4f55\u5fc5\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u7a0b\u9752\u7af9\u7b49\u95fb\u58f0\uff0c\u4e5f\u90fd\u8fc7\u6765\u529d\u6170\u3002 \u3000\u3000\u4f55\u94c1\u624b\u6127\u6068\u96be\u5f53\uff0c\u672c\u60f3\u4e00\u6b7b\u4e86\u4e4b\uff0c\u4f46\u5728\u751f\u547d\u5173\u5934\u7a81\u7136\u5f97\u4eba\u76f8\u6551\uff0c\u8fd9\u6c42\u6b7b\u7684\u5ff5\u5934\u4fbf\u5373\u6d88\u4e86\uff0c\u53cc\u7738\u4ef0\u89c6\uff0c\u7cbe\u5149\u56db\u5c04\uff0c\u7b11\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4f60\u5982\u80af\u7b54\u5e94\u4e00\u4ef6\u4e8b\uff0c\u6211\u5c31\u4e0d\u6b7b\u5566\u3002\u201d \u3000\u3000\u9752\u9752\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u7247\u523b\u4e4b\u524d\u6b63\u8981\u81ea\u6740\uff0c\u54ed\u4e86\u4e00\u573a\uff0c\u5ffd\u7136\u53c8\u7b11\uff0c\u5979\u8981\u5927\u54e5\u751a\u4e48\u5462\uff1f\u554a\u54df\u4e0d\u5bf9\uff0c\u83ab\u975e\u662f\u770b\u4e2d\u4e86\u4ed6\uff01\u201d\u5fd9\u95ee\uff1a\u201c\u4f60\u8981\u4ed6\u7b54\u5e94\u751a\u4e48\uff1f\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u8881\u76f8\u516c\u4f60\u5148\u8bf4\u80af\u4e0d\u80af\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u77e5\u4f55\u6559\u4e3b\u8981\u5144\u5f1f\u529e\u751a\u4e48\u4e8b\u3002\u201d\u4ed6\u4e5f\u8d77\u4e86\u7591\u5fc3\uff0c\u4e0d\u5373\u7b54\u5e94\u3002 \u3000\u3000\u4f55\u94c1\u624b\u5411\u9752\u9752\u3001\u7126\u5b9b\u513f\u4e00\u7b11\uff0c\u5ffd\u5730\u5728\u8881\u627f\u5fd7\u9762\u524d\u8dea\u4e0b\uff0c\u8fde\u8fde\u78d5\u5934\u3002\u8881\u627f\u5fd7\u5927\u60ca\uff0c\u5fd9\u4f5c\u63d6\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u522b\u8fd9\u6837\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u4e0d\u6536\u6211\u505a\u5f92\u5f1f\uff0c\u6211\u5c31\u8d56\u7740\u4e0d\u8d77\u6765\u5566\u3002\u201d \u3000\u3000\u9752\u9752\u5fc3\u5934\u5927\u5bbd\uff0c\u7b11\u9053\uff1a\u201c\u4f55\u6559\u4e3b\u8fd9\u4e48\u5389\u5bb3\u7684\u529f\u592b\uff0c\u8c01\u80fd\u505a\u4f60\u5e08\u7236\u554a\uff1f\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u4e0d\u6536\u6211\u8fd9\u5f92\u5f1f\uff0c\u6211\u5728\u8fd9\u91cc\u8dea\u4e00\u8f88\u5b50\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u51fa\u5e08\u95e8\u4e0d\u5230\u4e00\u5e74\uff0c\u600e\u80fd\u6388\u5f92\uff1f\u4f55\u6559\u4e3b\u5982\u4e0d\u5acc\u6211\u672c\u9886\u4f4e\u5fae\uff0c\u54b1\u4eec\u53ef\u4ee5\u4e92\u76f8\u5207\u8e49\uff0c\u7814\u8ba8\u6b66\u827a\u3002\u62dc\u5e08\u4e4b\u8bf4\uff0c\u518d\u4e5f\u4f11\u63d0\u3002\u201d\u4f55\u94c1\u624b\u76f4\u633a\u633a\u7684\u8dea\u7740\uff0c\u53ea\u662f\u4e0d\u80af\u8d77\u8eab\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4f38\u624b\u76f8\u6276\u3002\u4f55\u94c1\u624b\u624b\u8098\u4e00\u7f29\uff0c\u7b11\u9053\uff1a\u201c\u6211\u624b\u4e0a\u6709\u6bd2\uff01\u201d\u4e4c\u5149\u4e00\u95ea\uff0c\u94c1\u94a9\u5f80\u4ed6\u624b\u638c\u4e0a\u94a9\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53cc\u624b\u5e76\u4e0d\u9000\u907f\uff0c\u53cd\u800c\u524d\u4f38\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u5df2\u62a2\u5728\u5934\u91cc\uff0c\u5728\u5979\u624b\u8098\u4e0a\u4e00\u6258\uff0c\u4f55\u94c1\u624b\u8eab\u4e0d\u7531\u81ea\u4e3b\u7684\u817e\u7a7a\u800c\u8d77\u3002 \u3000\u3000\u4f46\u5979\u6b66\u529f\u4e5f\u771f\u4e86\u5f97\uff0c\u5728\u7a7a\u4e2d\u542b\u80f8\u7f29\u8170\uff0c\u9661\u7136\u95f4\u8eab\u5b50\u5411\u540e\u9000\u5f00\u4e24\u5c3a\uff0c\u843d\u4e0b\u5730\u6765\uff0c\u4ecd\u662f\u8dea\u7740\u3002\u65c1\u89c2\u4f17\u4eba\u89c1\u4e24\u4eba\u5404\u81ea\u9732\u4e86\u4e00\u624b\u4e0a\u4e58\u6b66\u529f\uff0c\u4e0d\u81ea\u7981\u7684\u9f50\u58f0\u559d\u5f69\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f55\u6559\u4e3b\u4f11\u606f\u4e00\u4f1a\u513f\u5427\uff0c\u6211\u8981\u51fa\u53bb\u4f1a\u5ba2\u3002\u201d\u8bf4\u7740\u8f6c\u8eab\u51fa\u95e8\u3002\u4f55\u94c1\u624b\u5927\u6025\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u80af\u6536\u6211\u4e3a\u5f92\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u6562\u5f53\u3002\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u597d\uff01\u590f\u59d1\u5a18\uff0c\u6211\u8bb2\u4e2a\u6545\u4e8b\u7ed9\u4f60\u542c\uff0c\u6709\u4eba\u534a\u591c\u91cc\u628a\u56fe\u753b\u653e\u5728\u5e8a\u8fb9\u3002\u201d\u5979\u4e00\u77e5\u9752\u9752\u662f\u5973\u5b50\uff0c\u7acb\u65f6\u5bdf\u89c9\u5979\u5bf9\u8881\u627f\u5fd7\u949f\u60c5\u751a\u6df1\uff0c\u800c\u8881\u627f\u5fd7\u5bf9\u9752\u9752\u7684\u795e\u6001\u4e5f\u662f\u975e\u540c\u5bfb\u5e38\uff0c\u4fbf\u60f3\u5230\u5e8a\u8fb9\u8096\u50cf\u4e4b\u4e8b\u5927\u662f\u5947\u8d27\u53ef\u5c45\u3002 \u3000\u3000\u9752\u9752\u6115\u7136\u4e0d\u89e3\u3002\u8881\u627f\u5fd7\u5374\u5df2\u6ee1\u8138\u901a\u7ea2\uff0c\u5fc3\u60f3\u8fd9\u4f55\u94c1\u624b\u65e0\u6cd5\u65e0\u5929\uff0c\u751a\u4e48\u4e8b\u90fd\u505a\u5f97\u51fa\uff0c\u81ea\u5df1\u4e0e\u963f\u4e5d\u7684\u4e8b\u672c\u6765\u95ee\u5fc3\u65e0\u6127\uff0c\u4f46\u9752\u5e74\u7537\u5973\u6df1\u591c\u540c\u7761\u4e00\u5e8a\uff0c\u8fd9\u4e8b\u7ed9\u5979\u4f20\u626c\u5f00\u6765\uff0c\u4e0d\u4f46\u9752\u9752\u751f\u6c14\uff0c\u4e5f\u8d25\u574f\u4e86\u81ea\u5df1\u548c\u963f\u4e5d\u7684\u540d\u58f0\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u5927\u6025\uff0c\u8fde\u8fde\u6413\u624b\u3002 \u3000\u3000\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd8\u662f\u7b54\u5e94\u4e86\u7684\u597d\u3002\u201d\u8881\u627f\u5fd7\u65e0\u5948\uff0c\u652f\u543e\u9053\uff1a\u201c\u5514\uff0c\u5514\u3002\u201d\u4f55\u94c1\u624b\u5927\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u7b54\u5e94\u4e86\u3002\u201d\u53cc\u819d\u4e00\u633a\uff0c\u8eab\u5b50\u8f7b\u8f7b\u843d\u5728\u4ed6\u9762\u524d\uff0c\u76c8\u76c8\u62dc\u5012\uff0c\u884c\u8d77\u5927\u793c\u6765\u3002\u8881\u627f\u5fd7\u4e3a\u52bf\u6240\u8feb\uff0c\u53ea\u5f97\u8fd8\u4e86\u534a\u793c\u3002\u4f17\u4eba\u7eb7\u7eb7\u8fc7\u6765\u9053\u8d3a\u3002 \u3000\u3000\u9752\u9752\u6ee1\u8179\u7591\u7aa6\uff0c\u95ee\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u8bb2\u751a\u4e48\u6545\u4e8b\uff1f\u201d\u4f55\u94c1\u624b\u7b11\u9053\uff1a\u201c\u6211\u4eec\u6559\u91cc\u6709\u95e8\u90aa\u6cd5\uff0c\u53ea\u8981\u753b\u4e86\u4e00\u4e2a\u4eba\u7684\u8096\u50cf\u653e\u5728\u5e8a\u8fb9\uff0c\u5411\u7740\u8096\u50cf\u78d5\u5934\uff0c\u884c\u8d77\u6cd5\u6765\uff0c\u90a3\u4eba\u5c31\u4f1a\u5fc3\u75db\u5934\u75db\uff0c\u4e00\u8fde\u4e09\u4e2a\u6708\u4e0d\u4f1a\u597d\u3002\u5148\u524d\u5e08\u7236\u4e0d\u80af\u6536\u6211\uff0c\u6211\u5c31\u5413\u4ed6\u8981\u884c\u6b64\u6cd5\u3002\u201d\u9752\u9752\u89c9\u6b64\u8bdd\u96be\u4fe1\uff0c\u5374\u4e5f\u65e0\u53ef\u76f8\u9a73\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u4f55\u94c1\u624b\u6492\u8c0e\uff0c\u8fd9\u624d\u653e\u5fc3\uff0c\u5fc3\u60f3\uff1a\u201c\u5929\u4e0b\u62dc\u5e08\u4e5f\u6ca1\u8fd9\u822c\u8981\u80c1\u7684\u3002\u5982\u5979\u5fc3\u672f\u4e0d\u6539\uff0c\u51b3\u4e0d\u4f20\u5979\u6b66\u827a\u3002\u201d\u5f53\u4e0b\u6b63\u8272\u9053\uff1a\u201c\u5176\u5b9e\u6211\u5e76\u65e0\u672c\u9886\u6536\u5f92\u4f20\u827a\uff0c\u65e2\u7136\u4f60\u4e00\u756a\u8bda\u610f\uff0c\u54b1\u4eec\u6682\u4e14\u6302\u4e86\u8fd9\u4e2a\u540d\uff0c\u7b49\u6211\u7980\u660e\u5e08\u7236\uff0c\u4ed6\u8001\u4eba\u5bb6\u7b54\u5141\u4e4b\u540e\uff0c\u6211\u624d\u80fd\u4f20\u4f60\u534e\u5c71\u6d3e\u672c\u95e8\u6b66\u529f\u3002\u201d\u4f55\u94c1\u624b\u7709\u82b1\u773c\u7b11\uff0c\u6ca1\u53e3\u5b50\u7684\u7b54\u5e94\u3002 \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u4f55\u6559\u4e3b\u2026\u2026\u201d\u4f55\u94c1\u624b\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u518d\u53eb\u6211\u4f5c\u6559\u4e3b\u5566\u3002\u5e08\u7236\uff0c\u8bf7\u60a8\u7ed9\u6211\u6539\u4e2a\u540d\u513f\u3002\u201d\u8881\u627f\u5fd7\u60f3\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bfb\u4e66\u4e0d\u591a\uff0c\u60f3\u4e0d\u51fa\u751a\u4e48\u597d\u540d\u5b57\u3002\u5c31\u53eb\u2018\u60d5\u5b88\u2019\u5982\u4f55\uff1f\u60d5\u662f\u8b66\u60d5\u7740\u522b\u505a\u574f\u4e8b\uff0c\u5b88\u662f\u4e25\u5b88\u89c4\u77e9\u3001\u6b63\u6b63\u6d3e\u6d3e\u7684\u610f\u601d\u3002\u201d\u4f55\u94c1\u624b\u559c\u9053\uff1a\u201c\u597d\u597d\uff0c\u590f\u5e08\u53d4\uff0c\u4f60\u5c31\u53eb\u6211\u60d5\u5b88\u5427\u3002\u201d\u9752\u9752\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u6bd4\u6211\u5927\uff0c\u672c\u9886\u53c8\u6bd4\u6211\u9ad8\uff0c\u600e\u4e48\u53eb\u6211\u5e08\u53d4\uff1f\u201d\u4f55\u60d5\u5b88\u5728\u5979\u8033\u8fb9\u6084\u58f0\u9053\uff1a\u201c\u73b0\u4e0b\u53eb\u4f60\u5e08\u53d4\uff0c\u8fc7\u4e9b\u65e5\u5b50\u53eb\u4f60\u5e08\u6bcd\u5462\uff01\u201d \u3000\u3000\u9752\u9752\u53cc\u988a\u6655\u7ea2\uff0c\u82b3\u5fc3\u7a83\u559c\uff0c\u6b63\u8981\u5550\u5979\uff0c\u5ffd\u542c\u5f97\u6c34\u4e91\u4e0e\u95f5\u5b50\u534e\u4e24\u4eba\u6765\u5230\u623f\u5916\u3002\u4f17\u4eba\u8d70\u4e86\u51fa\u53bb\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u9ec4\u6728\u9053\u957f\u7684\u4e0b\u843d\uff0c\u4f60\u5bf9\u4e24\u4f4d\u8bf4\u4e86\u5427\u3002\u201d\u4f55\u60d5\u5b88\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u5728\u4e91\u5357\u5927\u2026\u2026\u201d \u3000\u3000\u4e00\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u731b\u542c\u5f97\u8f70\u5929\u4ef7\u4e00\u58f0\u5de8\u54cd\uff0c\u53ea\u9707\u5f97\u95e8\u7a97\u9f50\u52a8\u3002\u4f17\u4eba\u53ea\u89c9\u811a\u4e0b\u5730\u9762\u4e5f\u90fd\u6447\u52a8\uff0c\u65e0\u4e0d\u60ca\u8bb6\uff0c\u4f46\u542c\u5f97\u54cd\u58f0\u63a5\u8fde\u4e0d\u65ad\uff0c\u5374\u53c8\u4e0d\u662f\u7126\u96f7\u9739\u96f3\u3002\u7a0b\u9752\u7af9\u9053\uff1a\u201c\u90a3\u662f\u70ae\u58f0\u3002\u201d \u3000\u3000\u4f17\u4eba\u6d8c\u5230\u5385\u4e0a\u3002\u6d2a\u80dc\u6d77\u4ece\u5927\u95e8\u53e3\u76f4\u51b2\u8fdb\u6765\uff0c\u53eb\u9053\uff1a\u201c\u95ef\u738b\u5927\u519b\u5230\u5566\uff01\u201d\u53ea\u542c\u70ae\u58f0\u4e0d\u7edd\uff0c\u9065\u671b\u57ce\u5916\u706b\u5149\u70db\u5929\uff0c\u6740\u58f0\u5927\u9707\uff0c\u95ef\u738b\u4e49\u519b\u5df2\u653b\u5230\u4e86\u5317\u4eac\u57ce\u5916\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5bf9\u6c34\u4e91\u9053\uff1a\u201c\u9053\u957f\uff0c\u5979\u5df2\u62dc\u6211\u4e3a\u5e08\u3002\u5c0a\u5e08\u7684\u4e8b\uff0c\u54b1\u4eec\u6162\u4e00\u6b65\u518d\u8bf4\u2026\u2026\u201d\uff0c\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u9ec4\u6728\u9053\u957f\u88ab\u6211\u59d1\u59d1\u5173\u5728\u4e91\u5357\u5927\u7406\u7075\u86c7\u5c71\u6bd2\u9f99\u6d1e\u91cc\u3002\u4f60\u4eec\u62ff\u8fd9\u4e2a\u53bb\u653e\u4ed6\u51fa\u6765\u5427\u3002\u201d\u8bf4\u7740\u62ff\u51fa\u4e00\u4e2a\u4e4c\u9ed1\u7684\u86c7\u5f62\u94c1\u54e8\u6765\u3002\u6c34\u4e91\u4e0e\u95f5\u5b50\u534e\u542c\u8bf4\u5e08\u7236\u65e0\u6059\uff0c\u5927\u559c\u8fc7\u671b\uff0c\u8fde\u5fd9\u8c22\u8fc7\uff0c\u63a5\u4e86\u54e8\u5b50\u3002\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4ee4\u7b26\u3002 \u3000\u3000\u4f60\u4eec\u9a6c\u4e0a\u8d76\u53bb\uff0c\u53ea\u8981\u62a2\u5728\u5934\u91cc\uff0c\u4e91\u5357\u6559\u4f17\u8fd8\u4e0d\u77e5\u6211\u5df2\u53db\u6559\uff0c\u89c1\u4e86\u8fd9\u4e2a\u4ee4\u7b26\uff0c\u81ea\u4f1a\u653e\u5c0a\u5e08\u51fa\u6765\u3002\u201d\u6c34\u4e91\u4e0e\u95f5\u5b50\u534e\u5306\u5306\u53bb\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u4e86\u4e0d\u4e45\uff0c\u5317\u4eac\u57ce\u91cc\u5404\u8def\u8c6a\u6770\u9f50\u6765\u542c\u8881\u627f\u5fd7\u53f7\u4ee4\u3002\u8881\u627f\u5fd7\u4e8b\u5148\u65e9\u6709\u5e03\u7f6e\uff0c\u8c01\u653e\u706b\uff0c\u8c01\u63a5\u5e94\uff0c\u5df2\u5206\u6d3e\u5f97\u4e95\u4e95\u6709\u6761\u3002 \u3000\u3000\u95ef\u519b\u5982\u4f55\u653b\u57ce\uff0c\u660e\u519b\u5982\u4f55\u5b88\u5fa1\uff0c\u5404\u5904\u63a2\u5b50\u4e0d\u4f4f\u62a5\u6765\u3002\u8fc7\u5f97\u4e00\u4f1a\uff0c\u4e00\u540d\u6c49\u5b50\u9001\u4e86\u4e00\u5c01\u4fe1\u6765\uff0c\u662f\u674e\u5ca9\u547d\u4eba\u6df7\u8fdb\u57ce\u6765\u9012\u9001\u7684\uff0c\u539f\u6765\u4ed6\u7edf\u519b\u5df2\u5230\u57ce\u5916\u3002\u8881\u627f\u5fd7\u5927\u559c\uff0c\u5f53\u5373\u6d3e\u4eba\u56db\u51fa\u884c\u4e8b\u3002 \u3000\u3000\u9ec4\u660f\u95f4\uff0c\u5404\u4eba\u5df2\u5c06\u6b4c\u8c23\u5230\u5904\u4f20\u64ad\uff0c\u53ea\u542c\u897f\u57ce\u4f17\u95f2\u4eba\u4e0e\u5c0f\u513f\u4eec\u5531\u4e86\u8d77\u6765\uff1a\u201c\u671d\u6c42\u5347\uff0c\u66ae\u6c42\u5408\uff0c\u8fd1\u6765\u8d2b\u6c49\u96be\u5b58\u6d3b\uff0c\u65e9\u65e9\u5f00\u95e8\u62dc\u95ef\u738b\uff0c\u7ba1\u6559\u5927\u5c0f\u90fd\u6b22\u60a6\uff01\u201d\u53c8\u542c\u4e1c\u57ce\u7684\u95f2\u6c49\u4eec\u5531\u9053\uff1a\u201c\u5403\u4ed6\u5a18\uff0c\u7740\u4ed6\u5a18\uff0c\u5403\u7740\u4e0d\u5c3d\u6709\u95ef\u738b\uff0c\u4e0d\u5f53\u5dee\uff0c\u4e0d\u7eb3\u7cae\uff01\u201d\u57ce\u4e2d\u5b98\u5175\u65e9\u5df2\u5927\u4e71\uff0c\u5404\u81ea\u6253\u7b97\u5982\u4f55\u9003\u547d\uff0c\u53c8\u6709\u8c01\u53bb\u7406\u4f1a\uff1f\u542c\u7740\u8fd9\u4e9b\u6b4c\u8c23\uff0c\u66f4\u662f\u4eba\u5fc3\u60f6\u60f6\u3002 \u3000\u3000\u6b21\u65e5\u662f\u4e09\u6708\u5341\u516b\uff0c\u8881\u627f\u5fd7\u4e0e\u9752\u9752\u3001\u4f55\u60d5\u5b88\u3001\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u7b49\u5316\u88c5\u660e\u5175\uff0c\u9f50\u5230\u57ce\u5934\u773a\u671b\uff0c\u53ea\u89c1\u4e49\u519b\u90fd\u7a7f\u9ed1\u8863\u9ed1\u7532\uff0c\u6570\u5341\u4e07\u4eba\u72b9\u5982\u4e4c\u4e91\u853d\u91ce\uff0c\u4e0d\u89c1\u5c3d\u5904\u3002\u70ae\u706b\u7fbd\u7bad\uff0c\u4e0d\u4f4f\u5f80\u57ce\u4e0a\u5c04\u6765\u3002\u5b88\u519b\u9635\u52bf\u65e9\u4e71\uff0c\u54ea\u91cc\u62b5\u654c\u5f97\u4f4f\uff1f \u3000\u3000\u5ffd\u7136\u95f4\u5927\u98ce\u9661\u8d77\uff0c\u9ec4\u6c99\u853d\u5929\uff0c\u65e5\u8272\u660f\u6697\uff0c\u96f7\u58f0\u9707\u52a8\uff0c\u5927\u96e8\u5939\u7740\u51b0\u96f9\u503e\u76c6\u800c\u4e0b\u3002\u57ce\u4e0a\u57ce\u4e0b\uff0c\u4f17\u5175\u5c06\u8863\u5c65\u5c3d\u6e7f\u3002 \u3000\u3000\u9752\u9752\u7b49\u89c1\u5230\u8fd9\u822c\u5929\u5730\u5927\u53d8\u7684\u60c5\u72b6\uff0c\u4e0d\u7981\u5fc3\u4e2d\u5747\u611f\u6817\u6817\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b49\u56de\u4e0b\u57ce\u6765\uff0c\u6307\u6325\u4eba\u4f17\uff0c\u5728\u57ce\u4e2d\u56db\u4e0b\u91cc\u653e\u706b\uff0c\u622a\u6740\u5b98\u5175\u3002\u5404\u5904\u8857\u5df7\u4e2d\u7684\u6d41\u6c13\u68cd\u5f92\u4fbf\u4e58\u673a\u52ab\u63a0\uff0c\u54ed\u58f0\u53eb\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\u3002 \u3000\u3000\u7fa4\u96c4\u6b63\u81ea\u5927\u547c\u9163\u6597\uff0c\u5ffd\u89c1\u4e00\u961f\u5b98\u5175\u62e5\u7740\u4e00\u4e2a\u9526\u8863\u592a\u76d1\uff0c\u547c\u559d\u800c\u6765\u3002\u8881\u627f\u5fd7\u4e8e\u706b\u5149\u4e2d\u8fdc\u8fdc\u671b\u89c1\u6b63\u662f\u66f9\u5316\u6df3\uff0c\u5fc3\u5934\u4e00\u559c\uff0c\u53eb\u9053\uff1a\u201c\u8ddf\u6211\u6765\uff0c\u62ff\u4e0b\u8fd9\u5978\u8d3c\u3002\u201d\u94c1\u7f57\u6c49\u4e0e\u4f55\u60d5\u5b88\u5f53\u5148\u5f00\u8def\uff0c\u76f4\u51b2\u8fc7\u53bb\uff0c\u5b98\u5175\u54ea\u91cc\u963b\u62e6\u5f97\u4f4f\uff1f\u66f9\u5316\u6df3\u89c1\u52bf\u5934\u4e0d\u5bf9\uff0c\u62e8\u8f6c\u9a6c\u5934\u60f3\u9003\u3002\u8881\u627f\u5fd7\u4e00\u8dc3\u800c\u524d\uff0c\u626f\u4f4f\u4ed6\u7684\u811a\u4e00\u62c9\uff0c\u63d0\u4e0b\u9a6c\u6765\uff0c\u559d\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u66f9\u5316\u6df3\u9053\uff1a\u201c\u7687\u2026\u2026\u7687\u4e0a\u2026\u2026\u547d\u4e2a\u4eba\u7763\u2026\u2026\u7763\u6218\u5f70\u4e49\u95e8\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\uff0c\u5230\u5f70\u4e49\u95e8\u53bb\u3002\u201d \u3000\u3000\u7fa4\u96c4\u62e5\u7740\u66f9\u5316\u6df3\u76f4\u4e0a\u57ce\u5934\uff0c\u9065\u9065\u671b\u89c1\u57ce\u5916\u4e00\u9762\u5927\u65d7\u8fce\u98ce\u98d8\u626c\uff0c\u65d7\u4e0b\u4e00\u4eba\u5934\u6234\u6be1\u7b20\uff0c\u8de8\u7740\u4e4c\u9a73\u9a6c\u5f80\u6765\u9a70\u9a8b\u6307\u6325\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u6b63\u662f\u95ef\u738b\u674e\u81ea\u6210\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u5feb\u5f00\u57ce\u95e8\uff0c\u8fce\u63a5\u95ef\u738b\uff01\u201d\u8bf4\u7740\u624b\u4e0a\u4e00\u7528\u52b2\uff0c\u66f9\u5316\u6df3\u75db\u5f97\u9669\u4e9b\u6655\u4e86\u8fc7\u53bb\u3002\u4ed6\u547d\u60ac\u4eba\u624b\uff0c\u54ea\u6562\u8fdd\u6297\uff1f\u4f55\u51b5\u773c\u89c1\u5927\u52bf\u5df2\u53bb\uff0c\u53cd\u60f3\u8fce\u63a5\u65b0\u4e3b\uff0c\u91cd\u56fe\u5bcc\u8d35\uff0c\u5f53\u5373\u4f20\u4e0b\u4ee4\u6765\uff0c\u5f70\u4e49\u95e8\u5927\u5f00\u3002\u57ce\u5916\u95ef\u519b\u6b22\u58f0\u96f7\u52a8\uff0c\u76f4\u51b2\u8fdb\u6765\u3002\u6210\u5343\u6210\u4e07\u8eab\u62ab\u9ed1\u7532\u7684\u5175\u5c06\u6d8c\u5165\u57ce\u95e8\u3002\u8881\u627f\u5fd7\u7ad9\u5728\u57ce\u5934\u5411\u4e0b\u671b\u53bb\uff0c\u89c1\u95ef\u519b\u4fbf\u5982\u4e00\u6761\u5927\u9ed1\u9f99\u873f\u8712\u800c\u8fdb\u5317\u4eac\uff0c\u5a01\u4e0d\u53ef\u5f53\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7387\u9886\u4f17\u4eba\uff0c\u968f\u7740\u8d25\u5175\u9000\u8fdb\u4e86\u5185\u57ce\u3002\u5185\u57ce\u5b88\u5175\u5c1a\u4f17\uff0c\u52a0\u4e0a\u4ece\u5916\u57ce\u6e83\u9000\u8fdb\u6765\u7684\u8d25\u5175\uff0c\u91cd\u91cd\u53e0\u53e0\uff0c\u6324\u6ee1\u4e86\u57ce\u5934\u3002\u8fd9\u65f6\u5929\u8272\u5df2\u665a\uff0c\u5916\u57ce\u95ef\u519b\u9e23\u91d1\u4f11\u606f\u3002\u8881\u627f\u5fd7\u7b49\u5728\u4e71\u519b\u4e2d\u4e5f\u9000\u56de\u5c45\u6240\u3002\u57ce\u8fb9\u94b2\u9f13\u58f0\u3001\u5450\u558a\u58f0\u4e71\u6210\u4e00\u7247\u3002\u7edf\u5175\u7684\u5c06\u5b98\u6709\u7684\u9003\u8dd1\uff0c\u6709\u7684\u5728\u57ce\u5934\u7763\u6218\uff0c\u8c01\u4e5f\u987e\u4e0d\u5230\u4ed6\u4eec\u8fd9\u4e00\u4f19\u4eba\u3002 \u3000\u3000\u7fa4\u96c4\u9000\u56de\u6b63\u6761\u5b50\u80e1\u540c\uff0c\u6362\u4e0b\u8eab\u4e0a\u8840\u8863\uff0c\u9971\u9910\u5df2\u6bd5\uff0c\u7ad9\u5728\u5c4b\u9876\u77ad\u671b\uff0c\u53ea\u89c1\u57ce\u5185\u5904\u5904\u706b\u5149\u3002 \u3000\u3000\u8881\u627f\u5fd7\u559c\u9053\uff1a\u201c\u5185\u57ce\u660e\u65e5\u6e05\u6668\u5fc5\u7834\u3002\u95ef\u738b\u6cbb\u56fd\uff0c\u5927\u516c\u65e0\u79c1\uff0c\u4ece\u6b64\u5929\u4e0b\u767e\u59d3\uff0c\u53ef\u4ee5\u8fc7\u5403\u9971\u7740\u6696\u7684\u592a\u5e73\u65e5\u5b50\u3002\u4eca\u665a\u662f\u6211\u624b\u5203\u4ec7\u4eba\u7684\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u77e5\u4ed6\u8981\u53bb\u523a\u6740\u5d07\u796f\u4e3a\u7236\u62a5\u4ec7\uff0c\u90fd\u613f\u968f\u540c\u5165\u5bab\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5404\u4f4d\u8f9b\u82e6\u4e86\u4e00\u65e5\uff0c\u4eca\u665a\u597d\u597d\u4f11\u606f\uff0c\u660e\u6668\u5c1a\u6709\u8bb8\u591a\u5927\u4e8b\u8981\u529e\u3002\u5175\u8352\u9a6c\u4e71\u4e4b\u9645\uff0c\u7687\u5bab\u6212\u5907\u5fc5\u758f\uff0c\u523a\u6740\u660f\u541b\u53ea\u662f\u4e00\u4e3e\u624b\u4e4b\u52b3\uff0c\u8fd8\u662f\u5144\u5f1f\u4e00\u4e2a\u53bb\u529e\u7f62\u3002\u201d\u5404\u4eba\u5fc3\u60f3\u4ed6\u7edd\u4e16\u6b66\u529f\uff0c\u73b0\u4e0b\u7687\u5e1d\u7684\u4f8d\u536b\u53ea\u6015\u90fd\u5df2\u9003\u5149\uff0c\u8981\u53bb\u523a\u6740\u8fd9\u4e2a\u5b64\u5bb6\u5be1\u4eba\uff0c\u5b9e\u662f\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u4ff1\u90fd\u9075\u4ece\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8981\u9752\u9752\u70b9\u8d77\u9999\u70db\uff0c\u5199\u4e86\u201c\u5148\u541b\u6545\u5175\u90e8\u5c1a\u4e66\u84df\u8fbd\u7763\u5e08\u8881\u201d\u7684\u7075\u724c\uff0c\u5b89\u6392\u4e86\u7075\u4f4d\uff0c\u53ea\u5f85\u5272\u4e86\u5d07\u796f\u7684\u5934\u6765\u796d\u4e86\u7236\u4eb2\uff0c\u7136\u540e\u628a\u9996\u7ea7\u62ff\u5230\u57ce\u5934\uff0c\u767b\u9ad8\u4e00\u547c\uff0c\u5185\u57ce\u5b88\u519b\u81ea\u7136\u6e83\u8d25\u3002\u4ed6\u5e26\u4e86\u4e00\u4e2a\u9769\u56ca\uff0c\u4ee5\u5907\u76db\u653e\u5d07\u796f\u7684\u9996\u7ea7\uff0c\u8170\u95f4\u85cf\u4e86\u4e00\u67c4\u5c3a\u6765\u957f\u7684\u5c16\u5200\uff0c\u5f84\u5411\u7687\u5bab\u5954\u53bb\u3002 \u3000\u3000\u4e00\u8def\u706b\u5149\u70db\u5929\uff0c\u6e83\u5175\u8d25\u5c06\uff0c\u5230\u5904\u5728\u4e58\u4e71\u62a2\u63a0\u3002\u8881\u627f\u5fd7\u6b63\u884c\u4e4b\u95f4\uff0c\u53ea\u89c1\u4e03\u516b\u540d\u5b98\u5175\u62d6\u4e86\u51e0\u540d\u5927\u54ed\u5927\u53eb\u7684\u5987\u5973\u8d70\u8fc7\uff0c\u60f3\u8d77\u963f\u4e5d\u5b64\u8eab\u4e00\u4e2a\u5c11\u5973\uff0c\u4e0d\u77e5\u5982\u4f55\u81ea\u5904\uff0c\u53c8\u60f3\u5230\u5979\u5bf9\u81ea\u5df1\u7684\u4e00\u756a\u60c5\u610f\uff0c\u8bda\u631a\u6df1\u5207\uff0c\u4ee4\u4eba\u5fc3\u611f\uff0c\u4f46\u6b64\u751f\u5374\u5df2\u65e0\u53ef\u62a5\u7b54\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u5185\u5fc3\u6d8c\u8d77\u4e00\u9635\u60c6\u6005\uff0c\u4e00\u9635\u9178\u695a\u3002\u4ed6\u76f4\u5165\u5bab\u95e8\uff0c\u5b88\u95e8\u7684\u536b\u5175\u5bab\u76d1\u65e9\u5df2\u9003\u5f97\u4e0d\u77e5\u53bb\u5411\u3002\u773c\u89c1\u7687\u5bab\u4e2d\u51b7\u6e05\u6e05\u7684\u4e00\u7247\uff0c\u4e0d\u89c9\u4e00\u60ca\uff1a\u201c\u5d07\u796f\u8981\u662f\u85cf\u533f\u8d77\u6765\uff0c\u4e0d\u77e5\u53bb\u5411\uff0c\u90a3\u53ef\u529f\u4e8f\u4e00\u7bd1\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u76f4\u5954\u4e7e\u6e05\u5bab\u3002 \u3000\u3000\u6765\u5230\u95e8\u5916\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5973\u4eba\u58f0\u97f3\u54ed\u6ce3\u751a\u54c0\u3002\u8881\u627f\u5fd7\u95ea\u5728\u95e8\u8fb9\uff0c\u5f80\u91cc\u4e00\u5f20\uff0c\u5fc3\u5934\u5927\u559c\uff0c\u539f\u6765\u5d07\u796f\u6b63\u5750\u5728\u6905\u4e0a\u3002\u4e00\u4e2a\u7a7f\u7687\u540e\u88c5\u675f\u7684\u5973\u4eba\u7ad9\u7740\uff0c\u4e00\u9762\u54ed\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u5341\u516d\u5e74\u6765\uff0c\u965b\u4e0b\u4e0d\u80af\u542c\u81e3\u59be\u4e00\u53e5\u8bdd\u3002\u4eca\u65e5\u5230\u6b64\u7530\u5730\uff0c\u5f97\u4e0e\u965b\u4e0b\u540c\u6b7b\u793e\u7a37\uff0c\u4ea6\u65e0\u6240\u61be\u3002\u201d\u5d07\u796f\u4fef\u9996\u5782\u6cea\u3002\u7687\u540e\u54ed\u4e86\u4e00\u9635\uff0c\u63a9\u9762\u5954\u51fa\u3002 \u3000\u3000\u8881\u627f\u5fd7\u6b63\u8981\u62a2\u8fdb\u53bb\u52a8\u624b\uff0c\u5ffd\u7136\u6bbf\u65c1\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u4e2a\u5c11\u5973\u63d0\u5251\u8dc3\u5230\u5d07\u796f\u9762\u524d\uff0c\u53eb\u9053\uff1a\u201c\u7236\u7687\uff0c\u65f6\u52bf\u7d27\u8feb\uff0c\u8d76\u5feb\u51fa\u5bab\u5427\u3002\u201d \u3000\u3000\u6b63\u662f\u957f\u5e73\u516c\u4e3b\u963f\u4e5d\u3002\u5979\u8f6c\u5934\u5bf9\u4e00\u540d\u592a\u76d1\u9053\uff1a\u201c\u738b\u516c\u516c\uff0c\u4f60\u597d\u597d\u670d\u4f8d\u965b\u4e0b\u3002\u201d\u90a3\u592a\u76d1\u540d\u53eb\u738b\u627f\u6069\uff0c\u5782\u6cea\u9053\uff1a\u201c\u662f\uff0c\u516c\u4e3b\u6bbf\u4e0b\u4e00\u8d77\u8d70\u5427\u3002\u201d\u963f\u4e5d\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8fd8\u8981\u5728\u5bab\u91cc\u803d\u4e00\u4f1a\u513f\u3002\u201d\u738b\u627f\u6069\u9053\uff1a\u201c\u5185\u57ce\u8f6c\u773c\u5c31\u7834\uff0c\u6bbf\u4e0b\u7559\u5728\u5bab\u91cc\u5f88\u662f\u5371\u9669\u3002\u201d\u963f\u4e5d\u9053\uff1a\u201c\u6211\u8981\u7b49\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5d07\u796f\u53d8\u8272\u9053\uff1a\u201c\u4f60\u8981\u7b49\u8881\u5d07\u7115\u7684\u513f\u5b50\uff1f\u201d\u963f\u4e5d\u8138\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u662f\uff0c\u513f\u81e3\u4eca\u65e5\u548c\u965b\u4e0b\u544a\u522b\u4e86\u3002\u201d\u5d07\u796f\u9053\uff1a\u201c\u4f60\u7b49\u4ed6\u5e72\u751a\u4e48\uff1f\u201d\u963f\u4e5d\u9053\uff1a\u201c\u4ed6\u7b54\u5e94\u8fc7\u6211\uff0c\u4e00\u5b9a\u4f1a\u6765\u7684\u3002\u201d\u5d07\u796f\u9053\uff1a\u201c\u628a\u5251\u7ed9\u6211\u3002\u201d\u63a5\u8fc7\u963f\u4e5d\u624b\u4e2d\u90a3\u67c4\u91d1\u86c7\u5b9d\u5251\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u513f\uff0c\u4f60\u4e3a\u751a\u4e48\u751f\u5728\u6211\u5bb6\u91cc\u2026\u2026\u201d\u5ffd\u5730\u624b\u8d77\u5251\u843d\uff0c\u4e4c\u5149\u4e00\u95ea\uff0c\u5b9d\u5251\u5411\u5979\u5934\u9876\u76f4\u5288\u4e0b\u53bb\u3002 \u3000\u3000\u963f\u4e5d\u60ca\u53eb\u4e00\u58f0\uff0c\u8eab\u5b50\u4e00\u6643\u3002\u8881\u627f\u5fd7\u5927\u5403\u4e00\u60ca\uff0c\u4e07\u60f3\u4e0d\u5230\u5d07\u796f\u7adf\u4f1a\u5bf9\u4eb2\u751f\u5973\u513f\u5ffd\u4e0b\u6bd2\u624b\u3002\u4ed6\u4e0e\u4e24\u4eba\u9694\u5f97\u5c1a\u8fdc\uff0c\u9661\u89c1\u5f62\u52bf\u5371\u6025\uff0c\u5fd9\u98de\u8eab\u6251\u4e0a\u76f8\u6551\uff0c\u8dc3\u5230\u534a\u8def\uff0c\u963f\u4e5d\u5df2\u7ecf\u8dcc\u5012\u3002 \u3000\u3000\u5d07\u796f\u63d0\u5251\u6b63\u5f85\u518d\u780d\uff0c\u8881\u627f\u5fd7\u5df2\u7136\u62a2\u5230\uff0c\u5de6\u624b\u63a2\u51fa\uff0c\u5728\u4ed6\u53f3\u8155\u4e0a\u529b\u62cd\uff0c\u5d07\u796f\u54ea\u91cc\u8fd8\u63e1\u5f97\u4f4f\u5251\uff0c\u91d1\u86c7\u5251\u76f4\u98de\u4e0a\u53bb\u3002\u8881\u627f\u5fd7\u5de6\u624b\u7ffb\u8f6c\uff0c\u5df2\u6293\u4f4f\u5d07\u796f\u624b\u8155\uff0c\u53f3\u624b\u63a5\u4f4f\u843d\u4e0b\u6765\u7684\u5b9d\u5251\uff0c\u56de\u5934\u770b\u963f\u4e5d\u65f6\uff0c\u53ea\u89c1\u5979\u660f\u5012\u5728\u8840\u6cca\u4e4b\u4e2d\uff0c\u5de6\u81c2\u5df2\u88ab\u780d\u65ad\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u72e0\u5fc3\u6bd2\u8fa3\u7684\u660f\u541b\uff0c\u7adf\u662f\u751a\u4e48\u4eba\u90fd\u6740\uff0c\u65e2\u5bb3\u6211\u7236\u4eb2\uff0c\u53c8\u6740\u4f60\u81ea\u5df1\u5973\u513f\u3002\u6211\u4eca\u65e5\u53d6\u4f60\u6027\u547d\uff01\u201d \u3000\u3000\u5d07\u796f\u89c1\u5230\u662f\u4ed6\uff0c\u53f9\u9053\uff1a\u201c\u4f60\u52a8\u624b\u5427\uff01\u201d\u8bf4\u7f62\u95ed\u76ee\u5f85\u6b7b\u3002\u4e24\u540d\u5185\u76d1\u62a2\u4e0a\u6765\u60f3\u6551\uff0c\u88ab\u8881\u627f\u5fd7\u4e00\u811a\u4e00\u4e2a\uff0c\u8e22\u5f97\u76f4\u98de\u51fa\u53bb\u3002\u8881\u627f\u5fd7\u4e3e\u8d77\u5251\u6765\uff0c\u6b63\u8981\u5f80\u5d07\u796f\u5934\u4e0a\u780d\u843d\u3002\u963f\u4e5d\u6070\u597d\u7741\u5f00\u773c\u775b\uff0c\u5f53\u5373\u594b\u529b\u8dc3\u8d77\uff0c\u6321\u5230\u5d07\u796f\u8eab\u524d\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u522b\u6740\u6211\u7236\u7687\uff0c\u6c42\u4f60\u2026\u2026\u201d\u8138\u4e0a\u6ee1\u662f\u54c0\u6073\u7684\u8138\u8272\uff0c\u671b\u7740\u8881\u627f\u5fd7\uff0c\u4e00\u8bed\u672a\u6bd5\uff0c\u53c8\u5df2\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5979\u65ad\u81c2\u5904\u8840\u5982\u6cc9\u6d8c\uff0c\u5927\u4e3a\u4e0d\u5fcd\uff0c\u5de6\u624b\u4e00\u63a8\uff0c\u5d07\u796f\u4ef0\u5929\u4e00\u4ea4\u76f4\u8dcc\u51fa\u53bb\u3002\u4ed6\u4fef\u8eab\u6276\u8d77\u963f\u4e5d\uff0c\u70b9\u4e86\u5979\u5de6\u80a9\u548c\u80cc\u5fc3\u5404\u5904\u901a\u8840\u8109\u7684\u7a74\u9053\uff0c\u8840\u6d41\u7a0d\u7f13\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u91d1\u521b\u836f\u6577\u5728\u4f24\u53e3\uff0c\u6495\u4e0b\u8863\u88fe\u624e\u4f4f\u3002\u963f\u4e5d\u6162\u6162\u9192\u8f6c\u3002 \u3000\u3000\u738b\u627f\u6069\u7b49\u6570\u540d\u592a\u76d1\u6276\u8d77\u5d07\u796f\uff0c\u4e0b\u6bbf\u8d8b\u51fa\u3002\u8881\u627f\u5fd7\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u653e\u4e0b\u963f\u4e5d\u3002\u8981\u5f85\u8ffd\u8d76\u3002\u963f\u4e5d\u53f3\u624b\u6402\u4f4f\u4ed6\u8116\u5b50\uff0c\u54ed\u53eb\uff1a\u201c\u522b\u4f24\u6211\u7236\u7687\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u8f6c\u5ff5\u4e00\u60f3\uff0c\u57ce\u7834\u5728\u5373\uff0c\u6599\u6765\u5d07\u796f\u4e5f\u9003\u4e0d\u4e86\u6027\u547d\uff0c\u867d\u975e\u4eb2\u81ea\u624b\u5203\uff0c\u7236\u4ec7\u603b\u662f\u62a5\u4e86\uff0c\u4e5f\u514d\u5f97\u4f24\u963f\u4e5d\u4e4b\u5fc3\uff0c\u5f53\u4e0b\u70b9\u5934\u9053\uff1a\u201c\u597d\uff01\u201d\u963f\u4e5d\u5fc3\u5934\u4e00\u5bbd\uff0c\u53c8\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5404\u5904\u5927\u4e71\uff0c\u5fc3\u60f3\u5979\u8eab\u53d7\u91cd\u4f24\uff0c\u65e0\u4eba\u7167\u6599\uff0c\u52bf\u5fc5\u4e27\u547d\uff0c\u53ea\u6709\u5c06\u5979\u6551\u56de\u81ea\u5df1\u4f4f\u5904\u518d\u8bf4\u3002\u5f53\u4e0b\u62b1\u8d77\u4e86\u5979\uff0c\u51fa\u5bab\u65f6\u5df2\u4ea4\u4e09\u66f4\uff0c\u62ac\u5934\u89c1\u706b\u5149\u7167\u5f97\u534a\u5929\u901a\u7ea2\uff0c\u5230\u5904\u662f\u54ed\u58f0\u558a\u58f0\u3002 \u3000\u3000\u5230\u5f97\u6b63\u6761\u5b50\u80e1\u540c\uff0c\u4f17\u4eba\u6b63\u5750\u7740\u7b49\u5019\u3002\u9752\u9752\u89c1\u4ed6\u53c8\u62b1\u4e86\u4e00\u4e2a\u5973\u5b50\u56de\u6765\uff0c\u5148\u5df2\u4e0d\u60a6\uff0c\u8d70\u8fd1\u4e00\u770b\uff0c\u7adf\u662f\u963f\u4e5d\uff0c\u677f\u8d77\u8138\u95ee\u9053\uff1a\u201c\u7687\u5e1d\u7684\u9996\u7ea7\u5462\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u6ca1\u6740\u4ed6\u3002\u7126\u59d1\u5a18\uff0c\u8bf7\u4f60\u8d39\u5fc3\u7167\u6599\u5979\u3002\u201d\u7126\u5b9b\u513f\u7b54\u5e94\u4e86\uff0c\u628a\u963f\u4e5d\u62b1\u8fdb\u5185\u5ba4\u3002 \u3000\u3000\u9752\u9752\u53c8\u95ee\uff1a\u201c\u5e72\u4e48\u4e0d\u6740\uff1f\u201d\u8881\u627f\u5fd7\u7565\u4e00\u8fdf\u7591\uff0c\u5411\u5185\u4e00\u6307\uff0c\u9053\uff1a\u201c\u5979\u6c42\u6211\u4e0d\u6740\uff01\u201d\u9752\u9752\u6012\u9053\uff1a\u201c\u5979\uff0c\u5979\u662f\u8c01\uff1f\u4f60\u5e72\u4e48\u8fd9\u6837\u542c\u5979\u8bdd\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5c1a\u672a\u56de\u7b54\uff0c\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u5509\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u8fd9\u4f4d\u7f8e\u516c\u4e3b\u600e\u4f1a\u65ad\u4e86\u4e00\u6761\u624b\u81c2\uff1f\u5e08\u7236\uff0c\u5979\u753b\u7684\u90a3\u5e45\u8096\u50cf\u5462\uff1f\u6709\u6ca1\u5e26\u51fa\u6765\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u8fde\u4f7f\u773c\u8272\uff0c\u4f55\u60d5\u5b88\u8fd8\u60f3\u8bf4\u4e0b\u53bb\uff0c\u89c1\u8881\u627f\u5fd7\u4e0e\u9752\u9752\u4e24\u4eba\u8138\u8272\u90fd\u5f88\u4e25\u91cd\uff0c\u4fbf\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u9752\u9752\u95ee\u9053\uff1a\u201c\u751a\u4e48\u516c\u4e3b\uff1f\u751a\u4e48\u8096\u50cf\uff1f\u201d\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u516c\u4e3b\u4f1a\u753b\u753b\uff0c\u6211\u89c1\u8fc7\u5979\u753b\u7684\u81ea\u5df1\u4e00\u5e45\u5c0f\u7167\uff0c\u753b\u5f97\u771f\u597d\u3002\u201d\u9752\u9752\u6a2a\u4e86\u5979\u4e00\u773c\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u8f6c\u8eab\u5165\u5185\u53bb\u4e86\u3002\u4f55\u60d5\u5b88\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u5e2e\u4f60\u6551\u516c\u4e3b\u53bb\u3002\u201d\u8bf4\u7740\u5954\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u6ce8\uff1a\u66f9\u5316\u6df3\u6b32\u7acb\u8bda\u738b\u4e3a\u5e1d\uff0c\u5e76\u975e\u53f2\u5b9e\uff0c\u7eaf\u7cfb\u5c0f\u8bf4\u4f5c\u8005\u4e4b\u675c\u64b0\u7a7f\u63d2\uff0c\u300a\u660e\u53f2\u300b\u4e2d\u4ea6\u65e0\u8bda\u738b\u5176\u4eba\u3002\u5176\u4ed6\u4e0e\u5d07\u796f\u6709\u5173\u4e4b\u53d9\u8ff0\uff0c\u5219\u5927\u81f4\u6839\u636e\u53f2\u4e66\u6240\u8f7d\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e5d\u56de\u3000\u55df\u4e4e\u5174\u5723\u4e3b \u4ea6\u590d\u82e6\u751f\u6c11",content:(0,t.jsx)(t.Fragment,{children:"\u8881\u627f\u5fd7\u56de\u623f\u5047\u5bd0\u7247\u523b\u3002\u5929\u5c06\u660e\u65f6\uff0c\u6d2a\u80dc\u6d77\u5306\u5306\u8d70\u8fdb\u623f\u6765\uff0c\u53eb\u9053\uff1a\u201c\u76f8\u516c\uff0c\u6c99\u5be8\u4e3b\u62ff\u4f4f\u4e86\u592a\u76d1\u738b\u76f8\u5c27\uff0c\u5df2\u7387\u4eba\u6253\u5f00\u4e86\u5ba3\u6b66\u95e8\uff01\u201d\u8881\u627f\u5fd7\u4e00\u8dc3\u800c\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u4e49\u519b\u8fdb\u57ce\u4e86\u4e48\uff1f\u201d\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u5218\u5b97\u654f\u5c06\u519b\u5df2\u5e26\u961f\u8fdb\u6765\u4e86\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u54b1\u4eec\u5feb\u53bb\u8fce\u63a5\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d70\u5230\u5385\u4e0a\u3002\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u4f1a\u7167\u987e\u5979\u4eec\u3002\u201d\u8881\u627f\u5fd7\u70b9\u4e86\u70b9\u5934\u3002\u8fd9\u65f6\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u4e0e\u94c1\u7f57\u6c49\u51fa\u5916\u672a\u5f52\uff0c\u8881\u627f\u5fd7\u5e26\u9886\u54d1\u5df4\u3001\u80e1\u6842\u5357\u3001\u6d2a\u80dc\u6d77\uff0c\u56db\u4eba\u5f80\u5927\u660e\u95e8\u6765\u3002 \u3000\u3000\u53ea\u89c1\u9634\u4e91\u56db\u5408\uff0c\u767d\u96ea\u5fae\u98d8\uff0c\u8857\u9053\u4e0a\u6e83\u5175\u8d25\u5352\uff0c\u56db\u6563\u5954\u9003\u3002 \u3000\u3000\u6709\u4eba\u5927\u547c\u800c\u8fc7\uff1a\u201c\u6b63\u9633\u95e8\uff0c\u9f50\u5316\u95e8\uff0c\u4e1c\u76f4\u95e8\u90fd\u6253\u5f00\u5566\uff01\u201d\u8d70\u4e86\u4e00\u9635\uff0c\u8d25\u5175\u6e10\u5c11\u3002\u4f17\u767e\u59d3\u5728\u95e8\u4e0a\u8d34\u4e86\u201c\u6c38\u660c\u5143\u5e74\u5927\u987a\u738b\u4e07\u4e07\u5c81\u201d\u7684\u9ec4\u7eb8\uff0c\u95e8\u53e3\u6446\u4e86\u9999\u6848\uff0c\u6709\u7684\u8fd8\u5728\u95e8\u53e3\u653e\u4e86\u9152\u6d46\u52b3\u519b\u3002\u8881\u627f\u5fd7\u5bf9\u80e1\u6842\u5357\u9053\uff1a\u201c\u4eba\u5fc3\u5982\u6b64\uff0c\u95ef\u738b\u54ea\u5f97\u4e0d\u6210\u5927\u4e8b\uff1f\u201d \u3000\u3000\u53c8\u8d70\u4e00\u9635\uff0c\u524d\u9762\u53f7\u89d2\u9f50\u9e23\uff0c\u6570\u767e\u4eba\u5feb\u6b65\u8fc7\u6765\uff0c\u5f53\u5148\u6b63\u662f\u6c99\u5929\u5e7f\u4e0e\u94c1\u7f57\u6c49\u3002\u4e24\u4eba\u7387\u9886\u5317\u4eac\u57ce\u5185\u7684\u8c6a\u6770\u622a\u6740\u660e\u5175\uff0c\u89c1\u4e86\u8881\u627f\u5fd7\u90fd\u5927\u58f0\u6b22\u547c\u8d77\u6765\u3002\u94c1\u7f57\u6c49\u53eb\u9053\uff1a\u201c\u95ef\u738b\u5c31\u8981\u6765\u5566\uff01\u201d\u4e00\u8a00\u65b9\u6bd5\uff0c\u524d\u9762\u6570\u9a91\u6025\u5954\u800c\u81f3\u3002\u4e00\u540d\u5927\u6c49\u4e3e\u7740\u4e00\u9762\u5927\u65d7\uff0c\u4e0a\u9762\u5199\u7740\u201c\u5927\u987a\u5236\u5c06\u519b\u674e\u201d\u516d\u4e2a\u5927\u5b57\u3002\u674e\u5ca9\u8eab\u7a7f\u9752\u886b\uff0c\u7eb5\u9a6c\u9a70\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff01\u201d\u8dc3\u5230\u9a6c\u524d\u3002 \u3000\u3000\u674e\u5ca9\u4e00\u6014\uff0c\u5f53\u5373\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u559c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u7834\u57ce\u4e4b\u529f\uff0c\u751a\u662f\u4e0d\u5c0f\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u95ef\u738b\u5927\u519b\u5230\u5904\uff0c\u660e\u5175\u671b\u98ce\u800c\u964d\uff0c\u5c0f\u5f1f\u6709\u4f55\u529f\u52b3\uff1f\u201d\u4e24\u4eba\u6267\u624b\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u4ee5\u524d\u5728\u5723\u5cf0\u5d82\u89c1\u8fc7\u7684\u5218\u82b3\u4eae\u3001\u7530\u89c1\u79c0\u7b49\u4eba\u4e00\u65f6\u4ff1\u5230\u3002\u4f17\u4eba\u6267\u624b\u8a00\u6b22\u3002 \u3000\u3000\u7a81\u7136\u53f7\u89d2\u58f0\u54cd\uff0c\u4f17\u519b\u5927\u547c\uff1a\u201c\u5927\u738b\u5230\u5566\uff0c\u5927\u738b\u5230\u5566\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u7b49\u95ea\u5728\u4e00\u65c1\uff0c\u53ea\u89c1\u7cbe\u9a91\u767e\u4f59\u524d\u5bfc\uff0c\u674e\u81ea\u6210\u6be1\u7b20\u7f25\u8863\uff0c\u4e58\u4e4c\u9a73\u9a6c\u75be\u9a70\u800c\u6765\u3002 \u3000\u3000\u674e\u5ca9\u8fc7\u53bb\u4f4e\u8bed\u51e0\u53e5\u3002\u674e\u81ea\u6210\u7b11\u9053\uff1a\u201c\u597d\u6781\u4e86\uff01\u8881\u5144\u5f1f\u8fc7\u6765\u3002\u201d \u3000\u3000\u674e\u5ca9\u62db\u62db\u624b\uff0c\u8881\u627f\u5fd7\u8d70\u5230\u4e24\u4eba\u9a6c\u524d\u3002\u674e\u81ea\u6210\u7b11\u9053\uff1a\u201c\u8881\u5144\u5f1f\uff0c\u4f60\u7acb\u4e86\u5927\u529f\uff01\u4f60\u6ca1\u9a6c\u4e48\uff1f\u201d\u8bf4\u7740\u4e00\u8dc3\u4e0b\u5730\uff0c\u628a\u5750\u9a91\u7684\u9a6c\u7f30\u4ea4\u7ed9\u4e86\u4ed6\u3002\u8881\u627f\u5fd7\u8fde\u5fd9\u62dc\u8c22\u3002 \u3000\u3000\u674e\u81ea\u6210\u8d70\u4e0a\u57ce\u5934\uff0c\u773c\u671b\u57ce\u5916\uff0c\u4f46\u89c1\u6210\u5343\u6210\u4e07\u90e8\u5c06\u58eb\u5352\u6b63\u4ece\u5404\u5904\u57ce\u95e8\u5165\u57ce\uff0c\u5f53\u6b64\u4e4b\u65f6\uff0c\u4e0d\u7531\u5f97\u5fd7\u5f97\u610f\u6ee1\u3002\u95ef\u519b\u89c1\u5230\u5927\u738b\uff0c\u56db\u4e0b\u91cc\u6b22\u58f0\u96f7\u52a8\u3002 \u3000\u3000\u674e\u81ea\u6210\u4ece\u7bad\u888b\u91cc\u53d6\u51fa\u4e09\u652f\u7bad\u6765\uff0c\u6273\u4e0b\u4e86\u7bad\u7c07\uff0c\u5f2f\u5f13\u642d\u7bad\uff0c\u5c06\u4e09\u7bad\u5c04\u4e0b\u57ce\u53bb\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f17\u5c06\u5b98\u5175\u58eb\u542c\u7740\uff0c\u5165\u57ce\u4e4b\u540e\uff0c\u6709\u4eba\u5984\u81ea\u6740\u4f24\u767e\u59d3\u3001\u5978\u6deb\u63b3\u63a0\u7684\uff0c\u4e00\u6982\u65a9\u9996\uff0c\u51b3\u4e0d\u5bbd\u5bb9\uff01\u201d\u57ce\u4e0b\u5341\u4f59\u4e07\u5175\u5c06\u9f50\u58f0\u5927\u547c\uff1a\u201c\u9075\u5949\u5927\u738b\u53f7\u4ee4\uff01\u5927\u738b\u4e07\u5c81\u3001\u4e07\u5c81\u3001\u4e07\u4e07\u5c81\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4ef0\u671b\u674e\u81ea\u6210\u795e\u5a01\u51db\u51db\u7684\u6a21\u6837\uff0c\u5fc3\u4e0b\u94a6\u4f69\u4e4b\u6781\uff0c\u5fcd\u4e0d\u4f4f\u4e5f\u9ad8\u58f0\u5927\u53eb\uff1a\u201c\u5927\u738b\u4e07\u5c81\u3001\u4e07\u5c81\u3001\u4e07\u4e07\u5c81\uff01\u201d \u3000\u3000\u674e\u81ea\u6210\u4e0b\u5f97\u57ce\u5934\uff0c\u6362\u4e86\u4e00\u5339\u9a6c\uff0c\u5728\u4f17\u4eba\u62e5\u536b\u4e0b\u8d70\u5411\u627f\u5929\u95e8\u3002\u4ed6\u8f6c\u5934\u5bf9\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u627f\u7236\u4e4b\u5fd7\uff0c\u6211\u662f\u627f\u5929\uff01\u201d\u5f2f\u5f13\u642d\u7bad\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u7fbd\u7bad\u98de\u51fa\uff0c\u6b63\u4e2d\u201c\u5929\u201d\u5b57\u4e4b\u4e0b\u3002\u4ed6\u8182\u529b\u5f3a\u52b2\uff0c\u8fd9\u4e00\u7bad\u76f4\u63d2\u5165\u57ce\u5899\uff0c\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u6b22\u547c\u3002 \u3000\u3000\u6765\u5230\u5fb7\u80dc\u95e8\u65f6\uff0c\u592a\u76d1\u738b\u5fb7\u5316\u7387\u9886\u4e86\u4e09\u767e\u4f59\u540d\u5185\u76d1\u4f0f\u5730\u8fce\u63a5\u3002\u674e\u81ea\u6210\u6295\u97ad\u5927\u7b11\uff0c\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u53bb\u5e74\u5728\u9655\u897f\u89c1\u5230\u6211\u65f6\uff0c\u53ef\u60f3\u5230\u4f1a\u6709\u4eca\u65e5\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u738b\u514b\u6210\u5927\u4e1a\uff0c\u5929\u4e0b\u767e\u59d3\u65e9\u90fd\u77e5\u9053\u4e86\u3002\u53ea\u662f\u4e07\u60f3\u4e0d\u5230\u4f1a\u5982\u6b64\u4e4b\u5feb\u3002\u201d\u674e\u81ea\u6210\u62ca\u638c\u5927\u7b11\u3002 \u3000\u3000\u5ffd\u6709\u4e00\u4eba\u75be\u5954\u800c\u6765\uff0c\u5411\u674e\u81ea\u6210\u62a5\u9053\uff1a\u201c\u5927\u738b\uff0c\u6709\u4e00\u4e2a\u592a\u76d1\u8bf4\uff0c\u89c1\u5230\u5d07\u796f\u9003\u5230\u7164\u5c71\u90a3\u8fb9\u53bb\u4e86\u3002\u201d\u674e\u81ea\u6210\u8f6c\u5934\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u5feb\u5e26\u4eba\u53bb\u62ff\u6765\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u662f\uff01\u201d\u624b\u4e00\u6446\uff0c\u7387\u9886\u4e86\u80e1\u6842\u5357\u7b49\u4eba\u9a70\u5411\u7164\u5c71\u3002 \u3000\u3000\u90a3\u7164\u5c71\u53ea\u662f\u4e2a\u5c0f\u4e18\uff0c\u4f17\u4eba\u4e0a\u5f97\u5c71\u6765\uff0c\u4e0d\u7981\u4e00\u60ca\u3002\u53ea\u89c1\u5927\u6811\u4e0b\u540a\u7740\u4e24\u4eba\uff0c\u968f\u98ce\u6447\u6643\u3002\u4e00\u4eba\u62ab\u53d1\u906e\u9762\uff0c\u8eab\u7a7f\u767d\u5939\u77ed\u84dd\u8863\uff0c\u7384\u8272\u9576\u8fb9\uff0c\u767d\u7ef5\u7ef8\u80cc\u5fc3\uff0c\u767d\ue837\u88e4\uff0c\u5de6\u811a\u8d64\u88f8\uff0c\u53f3\u811a\u7740\u4e86\u7eeb\u889c\u4e0e\u7ea2\u8272\u65b9\u5934\u978b\u3002\u8881\u627f\u5fd7\u62ab\u5f00\u4ed6\u5934\u53d1\u4e00\u770b\uff0c\u7adf\u7136\u4fbf\u662f\u5d07\u796f\u7687\u5e1d\u3002 \u3000\u3000\u4ed6\u8863\u524d\u7528\u8840\u5199\u7740\u51e0\u884c\u5b57\u9053\uff1a\u201c\u6715\u767b\u6781\u5341\u4e03\u5e74\uff0c\u81f4\u654c\u5165\u5185\u5730\u56db\u6b21\uff0c\u9006\u8d3c\u76f4\u903c\u4eac\u5e08\uff0c\u867d\u6715\u8584\u5fb7\u532a\u8eac\uff0c\u4e0a\u5e72\u5929\u548e\uff0c\u7136\u7686\u8bf8\u81e3\u4e4b\u8bef\u6715\u4e5f\u3002\u6715\u6b7b\uff0c\u65e0\u9762\u76ee\u89c1\u7956\u5b97\u4e8e\u5730\u4e0b\uff0c\u53bb\u6715\u51a0\u5195\uff0c\u4ee5\u53d1\u8986\u9762\uff0c\u4efb\u8d3c\u5206\u88c2\u6715\u5c38\uff0c\u52ff\u4f24\u767e\u59d3\u4e00\u4eba\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u62ff\u4e86\u8fd9\u5f20\u8840\u8bcf\uff0c\u9887\u611f\u6005\u60d8\uff0c\u4e8c\u5341\u5e74\u6765\u5927\u4ec7\u4eca\u65e5\u5f97\u62a5\uff0c\u672c\u662f\u559c\u4e8b\uff0c\u4f46\u89c1\u4ec7\u4eba\u5982\u6b64\u51c4\u60e8\u4e0b\u573a\uff0c\u4e0d\u7981\u607b\u7136\u4e45\u4e4b\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u8bdd\u5012\u8bf4\u5f97\u6f02\u4eae\uff0c\u751a\u4e48\u52ff\u4f24\u767e\u59d3\u4e00\u4eba\u3002\u8981\u662f\u4f60\u65e9\u77e5\u7231\u60dc\u767e\u59d3\uff0c\u4e0d\u662f\u903c\u5f97\u5929\u4e0b\u9965\u6c11\u65e0\u8def\u53ef\u8d70\uff0c\u53c8\u600e\u4f1a\u5230\u4eca\u65e5\u8fd9\u6b65\u7530\u5730\u3002\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u90a3\u8fb9\u540a\u6b7b\u7684\u662f\u4e2a\u592a\u76d1\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u7687\u5e1d\u6b7b\u65f6\u53ea\u6709\u4e00\u4e2a\u592a\u76d1\u76f8\u966a\uff0c\u771f\u53eb\u505a\u4f17\u53db\u4eb2\u79bb\u4e86\u3002\u628a\u5c38\u9996\u62ac\u4e86\u53bb\uff0c\u522b\u8ba9\u4eba\u4fb5\u4fae\u3002\u201d\u6d2a\u80dc\u6d77\u5e94\u4e86\u3002\u8881\u627f\u5fd7\u9a70\u56de\u7980\u62a5\u3002 \u3000\u3000\u8fd9\u65f6\u674e\u81ea\u6210\u5df2\u8fdb\u7687\u5bab\u3002\u5b88\u95e8\u7684\u95ef\u519b\u8ba4\u5f97\u8881\u627f\u5fd7\uff0c\u5f15\u4ed6\u8fdb\u5bab\u3002\u53ea\u89c1\u674e\u81ea\u6210\u5750\u5728\u9f99\u6905\u4e4b\u4e0a\uff0c\u8eab\u65c1\u7ad9\u7740\u5341\u51e0\u540d\u90e8\u5c06\u4ece\u5b98\uff0c\u4e00\u4e2a\u8863\u51a0\u4e0d\u6574\u7684\u5c11\u5e74\u7ad9\u5728\u6bbf\u4e0b\u3002 \u3000\u3000\u674e\u81ea\u6210\u89c1\u8881\u627f\u5fd7\u8fdb\u6765\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff01\u7687\u5e1d\u5462\uff0c\u5e26\u4ed6\u4e0a\u6765\u5427\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5d07\u796f\u81ea\u7f22\u6b7b\u4e86\u3002\u201d\u674e\u81ea\u6210\u4e00\u5446\uff0c\u63a5\u8fc7\u5d07\u796f\u7684\u9057\u8bcf\u89c2\u770b\u3002 \u3000\u3000\u65c1\u7acb\u7684\u5c11\u5e74\u5ffd\u7136\u4f0f\u5730\u5927\u54ed\uff0c\u51e0\u4e4e\u660f\u53a5\u4e86\u8fc7\u53bb\u3002\u674e\u81ea\u6210\u9053\uff1a\u201c\u90a3\u662f\u592a\u5b50\uff01\u201d\u8881\u627f\u5fd7\u6276\u4e86\u4ed6\u8d77\u6765\u3002\u674e\u81ea\u6210\u95ee\u9053\uff1a\u201c\u4f60\u5bb6\u4e3a\u751a\u4e48\u4f1a\u5931\u5929\u4e0b\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u592a\u5b50\u54ed\u9053\uff1a\u201c\u53ea\u56e0\u8bef\u7528\u5978\u81e3\u6e29\u4f53\u4ec1\u3001\u5468\u5ef6\u5112\u7b49\u4eba\u3002\u201d\u674e\u81ea\u6210\u7b11\u9053\uff1a\u201c\u539f\u6765\u5c0f\u5c0f\u5b69\u7ae5\uff0c\u5012\u4e5f\u660e\u767d\u3002\u201d\u968f\u5373\u6b63\u8272\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u8bf4\uff0c\u4f60\u7236\u7687\u53c8\u80e1\u6d82\u53c8\u5fcd\u5fc3\uff0c\u5bb3\u5f97\u5929\u4e0b\u767e\u59d3\u597d\u82e6\u3002\u4f60\u7236\u7687\u4eca\u65e5\u540a\u6b7b\uff0c\u56fa\u7136\u5f88\u60e8\uff0c\u4f46\u4ed6\u5728\u4f4d\u4e00\u5341\u4e03\u5e74\uff0c\u5929\u4e0b\u767e\u59d3\u88ab\u903c\u5f97\u540a\u6b7b\u7684\u53c8\u4e0d\u77e5\u6709\u51e0\u5343\u51e0\u4e07\uff0c\u90a3\u53ef\u66f4\u60e8\u5f97\u591a\u4e86\u3002\u201d\u592a\u5b50\u4fef\u9996\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u4f1a\u9053\uff1a\u201c\u90a3\u4f60\u5feb\u6740\u6211\u5427\u3002\u201d\u8881\u627f\u5fd7\u89c1\u4ed6\u5014\u5f3a\uff0c\u4e0d\u7981\u4e3a\u4ed6\u62c5\u5fc3\u3002 \u3000\u3000\u674e\u81ea\u6210\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u5b69\u5b50\uff0c\u5e76\u6ca1\u72af\u7f6a\uff0c\u6211\u54ea\u4f1a\u4e71\u6740\u4eba\u3002\u201d\u592a\u5b50\u9053\uff1a\u201c\u90a3\u4e48\u6211\u6c42\u4f60\u51e0\u4ef6\u4e8b\u3002\u201d\u674e\u81ea\u6210\u9053\uff1a\u201c\u4f60\u8bf4\u6765\u542c\u542c\u3002\u201d\u592a\u5b50\u9053\uff1a\u201c\u6c42\u4f60\u4e0d\u8981\u60ca\u52a8\u6211\u7956\u5b97\u9675\u5893\uff0c\u597d\u597d\u846c\u6211\u7236\u7687\u6bcd\u540e\u3002\u201d\u674e\u81ea\u6210\u9053\uff1a\u201c\u5f53\u7136\uff0c\u90a3\u4f55\u5fc5\u8981\u4f60\u6c42\u6211\uff1f\u201d\u592a\u5b50\u9053\uff1a\u201c\u8fd8\u6c42\u4f60\u522b\u6740\u767e\u59d3\u3002\u201d\u674e\u81ea\u6210\u5475\u5475\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5b69\u5b50\u4e0d\u61c2\u4e8b\u3002\u6211\u5c31\u662f\u8001\u767e\u59d3\uff01\u662f\u6211\u4eec\u767e\u59d3\u653b\u7834\u4f60\u7684\u4eac\u57ce\uff0c\u4f60\u61c2\u4e86\u4e48\uff1f\u201d \u3000\u3000\u592a\u5b50\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u4e0d\u6740\u767e\u59d3\u7684\u4e86\uff1f\u201d\u674e\u81ea\u6210\u500f\u5730\u89e3\u5f00\u81ea\u5df1\u4e0a\u8eab\u8863\u670d\uff0c\u53ea\u89c1\u4ed6\u80f8\u524d\u80a9\u5934\u6591\u6591\u9a73\u9a73\uff0c\u90fd\u662f\u97ad\u7b1e\u7684\u4f24\u75d5\uff0c\u4f17\u4eba\u4e0d\u7981\u9a87\u7136\u3002\u674e\u81ea\u6210\u9053\uff1a\u201c\u6211\u672c\u662f\u597d\u597d\u7684\u767e\u59d3\uff0c\u7ed9\u8d2a\u5b98\u6c61\u540f\u8fd9\u4e00\u987f\u6253\uff0c\u624d\u5fcd\u65e0\u53ef\u5fcd\uff0c\u8d77\u6765\u9020\u53cd\u3002\u54fc\uff0c\u4f60\u7236\u5b50\u4fe9\u5047\u4ec1\u5047\u4e49\uff0c\u8bf4\u751a\u4e48\u7231\u60dc\u767e\u59d3\u3002\u6211\u519b\u4e2d\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u54ea\u4e00\u4e2a\u4e0d\u5403\u8fc7\u4f60\u4eec\u7684\u82e6\u5934\uff1f\u201d \u3000\u3000\u592a\u5b50\u9ed8\u7136\u4f4e\u5934\u3002\u674e\u81ea\u6210\u7a7f\u56de\u8863\u670d\uff0c\u9053\uff1a\u201c\u4f60\u4e0b\u53bb\u5427\u3002\u5ff5\u4f60\u662f\u5148\u7687\u7684\u592a\u5b50\uff0c\u6211\u5c01\u4f60\u4e00\u4e2a\u738b\uff0c\u8ba9\u4f60\u77e5\u9053\u6211\u4eec\u8001\u767e\u59d3\u4e0d\u5ff5\u65e7\u6076\u3002\u5c01\u4f60\u751a\u4e48\u738b\uff1f\u55ef\uff0c\u4f60\u7236\u4eb2\u628a\u6c5f\u5c71\u9001\u5728\u6211\u624b\u91cc\uff0c\u5c31\u5c01\u4f60\u4e3a\u5b8b\u738b\u5427\u3002\u201d \u3000\u3000\u592a\u76d1\u66f9\u5316\u6df3\u7ad9\u5728\u4e00\u65c1\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u5411\u965b\u4e0b\u78d5\u5934\u8c22\u6069\u3002\u201d\u592a\u5b50\u6012\u76ee\u800c\u89c6\uff0c\u5ffd\u5730\u56de\u624b\u4e00\u638c\uff0c\u556a\u5730\u4e00\u58f0\uff0c\u66f9\u5316\u6df3\u9762\u988a\u4e0a\u767b\u65f6\u8d77\u4e86\u4e94\u4e2a\u624b\u6307\u5370\u3002 \u3000\u3000\u674e\u81ea\u6210\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u79cd\u4e0d\u5fe0\u4e0d\u4e49\u7684\u5978\u8d3c\uff0c\u6253\u5f97\u597d\u3002\u6765\u5440\uff0c\u5e26\u4e0b\u53bb\u780d\u4e86\uff01\u201d\u66f9\u5316\u6df3\u5413\u5f97\u8138\u5982\u571f\u8272\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u8dea\u5728\u5730\u4e0b\u8fde\u78d5\u54cd\u5934\uff0c\u989d\u89d2\u4e0a\u8840\u90fd\u78b0\u4e86\u51fa\u6765\u3002\u674e\u81ea\u6210\u4e00\u811a\u628a\u4ed6\u8e22\u4e86\u4e2a\u7b4b\u6597\uff0c\u559d\u9053\uff1a\u201c\u6eda\u51fa\u53bb\uff0c\u4ee5\u540e\u4f60\u518d\u6562\u89c1\u6211\u7684\u9762\uff0c\u628a\u4f60\u5250\u4e86\uff01\u201d\u592a\u5b50\u968f\u540e\u6602\u9996\u8d70\u51fa\u3002 \u3000\u3000\u674e\u81ea\u6210\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u5014\u5f3a\u3002\u6211\u559c\u6b22\u6709\u9aa8\u6c14\u7684\u5b69\u5b50\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e1e\u76f8\u725b\u91d1\u661f\u9053\uff1a\u201c\u4e3b\u4e0a\u5927\u4e8b\u5df2\u5b9a\u3002\u660e\u671d\u4eba\u5fc3\u5c3d\u5931\uff0c\u4f46\u6b7b\u7070\u590d\u71c3\uff0c\u5374\u4e5f\u4e0d\u53ef\u4e0d\u9632\u3002\u8fd9\u5b69\u5b50\u5341\u5206\u5014\u5f3a\uff0c\u51b3\u8ba1\u4e0d\u80af\u5f52\u987a\u5723\u671d\uff0c\u53ea\u6015\u6709\u4eba\u4f1a\u501f\u7528\u4ed6\u7684\u540d\u5934\u4f5c\u4e71\u3002\u4e0d\u5982\u9664\u4e86\uff0c\u4ee5\u514d\u540e\u60a3\u3002\u201d\u674e\u81ea\u6210\u8e0c\u8e87\u9053\uff1a\u201c\u8fd9\u4e5f\u8bf4\u5f97\u662f\u3002\u8fd9\u4ef6\u4e8b\u4f60\u53bb\u529e\u4e86\u5427\u3002\u201d\u8f6c\u5934\u5bf9\u8eab\u540e\u7684\u77ee\u5b50\u519b\u5e08\u5b8b\u732e\u7b56\u9053\uff1a\u201c\u542c\u8bf4\u7687\u5e1d\u8fd8\u6709\u4e2a\u516c\u4e3b\uff0c\u5374\u4e0d\u77e5\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u63a5\u53e3\u9053\uff1a\u201c\u7687\u5e1d\u628a\u5979\u780d\u53bb\u4e86\u4e00\u6761\u81c2\u8180\uff0c\u662f\u6211\u63a5\u4e86\u516c\u4e3b\u5728\u5bb6\u91cc\u517b\u4f24\u3002\u5f85\u5979\u4f24\u6108\uff0c\u518d\u5e26\u5979\u6765\u53e9\u89c1\u5927\u738b\u3002\u201d\u674e\u81ea\u6210\u7b11\u9053\uff1a\u201c\u597d\u597d\uff01\u4f60\u529f\u52b3\u4e0d\u5c0f\uff0c\u6211\u6b63\u60f3\u4e0d\u51fa\u8be5\u8d4f\u4f60\u751a\u4e48\uff0c\u8fd9\u516c\u4e3b\u5c31\u8d4f\u4e86\u4f60\u5427\u3002\u201d\u8881\u627f\u5fd7\u7a98\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u90a3\u2026\u2026\u5012\u662f\u90a3\u4e2a\u592a\u5b50\uff0c\u8fd8\u6c42\u5927\u738b\u9976\u4e86\u4ed6\u6027\u547d\u3002\u201d\u725b\u91d1\u661f\u7b11\u9053\uff1a\u201c\u8881\u5144\u5f1f\uff0c\u5bb3\u751a\u4e48\u81ca\uff1f\u7a76\u7adf\u662f\u82f1\u96c4\u51fa\u5728\u5c11\u5e74\u3002\u5218\u5c06\u519b\u4ed6\u4eec\u529f\u52b3\u867d\u5927\uff0c\u5927\u738b\u4e5f\u53ea\u8d4f\u4ed6\u4eec\u51e0\u540d\u5bab\u5a25\u5462\u3002\u4f60\u9a78\u9a6c\u7237\u8fd8\u6ca1\u505a\uff0c\u5012\u7231\u60dc\u8d77\u5c0f\u8205\u5b50\u6765\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u4ed6\u8bdd\u4e2d\u6709\u523a\uff0c\u9887\u4e3a\u4e0d\u5feb\uff0c\u5fc3\u60f3\uff1a\u201c\u592a\u5b50\u8fd9\u5c0f\u5c0f\u5b69\u7ae5\uff0c\u4f55\u5fc5\u6740\u4ed6\uff1f\u201d \u3000\u3000\u674e\u81ea\u6210\u9053\uff1a\u201c\u8881\u5144\u5f1f\uff0c\u6211\u90e8\u4e0b\u6b66\u5b98\uff0c\u5206\u4e3a\u4e5d\u54c1\u3002\u5218\u5b97\u654f\u662f\u4e00\u54c1\u6743\u5c06\u519b\uff0c\u4f60\u4e49\u5144\u674e\u5ca9\u662f\u4e8c\u54c1\u5236\u5c06\u519b\u3002\u6211\u5c01\u4f60\u4e3a\u4e09\u54c1\u679c\u6bc5\u5c06\u519b\u5427\u3002\u201d\u8881\u627f\u5fd7\u8eac\u8eab\u9053\uff1a\u201c\u591a\u8c22\u5927\u738b\u3002\u8881\u627f\u5fd7\u8a93\u6b7b\u4e3a\u5927\u738b\u6548\u529b\uff0c\u4e0d\u613f\u4e3a\u5b98\u3002\u201d \u3000\u3000\u725b\u91d1\u661f\u5fae\u7b11\u9053\uff1a\u201c\u8881\u5144\u5f1f\u662f\u4e03\u7701\u6b66\u6797\u76df\u4e3b\uff0c\u662f\u4e0d\u662f\u5acc\u8fd9\u4e09\u54c1\u5c06\u519b\u804c\u4f4d\u592a\u4f4e\u4e86\u5462\uff1f\u5927\u738b\u4e00\u7edf\u5929\u4e0b\uff0c\u7387\u571f\u4e4b\u6c11\uff0c\u83ab\u975e\u738b\u81e3\u3002 \u3000\u3000\u751a\u4e48\u4e03\u7701\u76df\u4e3b\u3001\u516b\u7701\u76df\u4e3b\u8fd9\u4e9b\u79c1\u76f8\u6388\u53d7\u7684\u540d\u53f7\uff0c\u81ea\u4eca\u800c\u540e\uff0c\u90fd\u662f\u8981\u4e25\u52a0\u7981\u6b62\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u81ea\u6210\u542c\u4ed6\u8a00\u8bed\u592a\u91cd\uff0c\u62cd\u62cd\u8881\u627f\u5fd7\u7684\u80a9\u5934\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u5e74\u8f7b\u5f97\u5f88\uff0c\u529f\u52b3\u867d\u662f\u4e0d\u5c0f\uff0c\u7ec8\u7a76\u968f\u6211\u65f6\u65e5\u8fd8\u77ed\uff0c\u4ee5\u540e\u5347\u8fc1\uff0c\u8fd8\u6015\u6ca1\u673a\u4f1a\u5417\uff1f\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5c5e\u4e0b\u51b3\u975e\u4e3a\u4e86\u804c\u4f4d\u9ad8\u4f4e\uff0c\u5b9e\u56e0\u8349\u83bd\u5339\u592b\uff0c\u505a\u4e0d\u6765\u5b98\u3002\u201d\u674e\u81ea\u6210\u5475\u5475\u5927\u7b11\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6211\u96be\u9053\u4e0d\u662f\u8349\u83bd\u5339\u592b\u4e86\uff1f\u8fde\u7687\u5e1d\u90fd\u8981\u505a\u5462\u3002\u201d\u8881\u627f\u5fd7\u4e0d\u4fbf\u518d\u8bf4\uff0c\u8f9e\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5f53\u4e0b\u56de\u6b63\u6761\u5b50\u80e1\u540c\u6765\uff0c\u4e00\u8fdb\u80e1\u540c\uff0c\u5c31\u542c\u5f97\u5175\u5203\u76f8\u4ea4\u3001\u547c\u559d\u65a5\u9a82\u4e4b\u58f0\uff0c\u968f\u89c1\u6570\u5341\u540d\u95ef\u519b\u624b\u6267\u5175\u5203\uff0c\u6025\u5954\u51fa\u6765\u3002\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u8fd9\u8bb8\u591a\u95ef\u519b\u5728\u8fd9\u91cc\u5e72\u751a\u4e48\uff1f\u201d\u52a0\u5feb\u811a\u6b65\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u53ea\u89c1\u4f55\u60d5\u5b88\u6325\u94a9\u4e71\u6740\uff0c\u628a\u5341\u591a\u540d\u56f0\u5728\u5c4b\u91cc\u9003\u4e0d\u51fa\u6765\u7684\u95ef\u519b\u6253\u5f97\u4e1c\u5954\u897f\u7a9c\u3002\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff0c\u4f4f\u624b\uff01\u90fd\u662f\u81ea\u5df1\u4eba\uff01\u201d\u4f55\u60d5\u5b88\u53eb\u4e86\u58f0\uff1a\u201c\u5e08\u7236\u3002\u201d\u95ea\u5728\u4e00\u65c1\u3002 \u3000\u3000\u4f17\u95ef\u519b\u5ffd\u89c1\u6709\u8def\u53ef\u9003\uff0c\u8702\u6d8c\u800c\u51fa\u3002\u4e00\u540d\u519b\u5b98\u5954\u5230\u8881\u627f\u5fd7\u8ddf\u524d\uff0c\u4e00\u5446\u4e4b\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u4e5f\u662f\u6211\u4eec\u5927\u738b\u624b\u4e0b\u7684\u5417\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6b63\u662f\u3002\u5927\u5bb6\u8bef\u4f1a\uff0c\u8001\u5144\u83ab\u602a\u3002\u201d\u90a3\u519b\u5b98\u6124\u6124\u7684\u9053\uff1a\u201c\u8bef\u4f1a\uff01\u54fc\uff0c\u4f60\u77a7\uff0c\u4f60\u5f92\u513f\u6740\u4e86\u6211\u4eec\u8fd9\u8bb8\u591a\u5f1f\u5144\u3002\u201d\u8bf4\u7740\u4e00\u6307\u5730\u4e0b\u7684\u4e03\u516b\u5177\u5c38\u9996\u3002 \u3000\u3000\u94c1\u7f57\u6c49\u5954\u4e86\u51fa\u6765\uff0c\u9a82\u9053\uff1a\u201c\u5165\u4f60\u5a18\u7684\uff01\u4f60\u4eec\u4e00\u8fdb\u5c4b\u6765\uff0c\u4f38\u624b\u5c31\u62a2\u4e1c\u897f\uff0c\u53c8\u8bf4\u4e0d\u4ea4\u91d1\u94f6\uff0c\u5c31\u653e\u706b\u70e7\u5c4b\u5b50\u3002\u89c1\u5230\u4f55\u59d1\u5a18\u7f8e\u8c8c\uff0c\u767b\u65f6\u52a8\u624b\u52a8\u811a\uff0c\u8bf4\u5979\u662f\u5978\u7ec6\uff0c\u8981\u5e26\u4e86\u8d70\u3002\u6df7\u5e10\u738b\u516b\u86cb\uff0c\u4f60\u4eec\u8ddf\u660e\u671d\u7684\u5b98\u5175\u6709\u751a\u4e48\u5206\u522b\u4e86\uff1f\u201d\u8bf4\u7740\u4e00\u62f3\u6325\u51fa\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u628a\u90a3\u519b\u5b98\u6253\u5f97\u76f4\u98de\u51fa\u53bb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d70\u8fdb\u5385\u4e2d\u3002\u7a0b\u9752\u7af9\u3001\u80e1\u6842\u5357\u7b49\u4eba\u90fd\u6c14\u6124\u6124\u7684\u8ff0\u8bf4\u5e02\u4e0a\u6240\u89c1\uff0c\u8bf4\u9053\u95ef\u519b\u5165\u57ce\u4e4b\u540e\uff0c\u5360\u4f4f\u6c11\u623f\uff0c\u5978\u6deb\u63b3\u63a0\uff0c\u65e0\u6240\u4e0d\u4e3a\u3002\u8881\u627f\u5fd7\u5fc3\u4e0b\u5403\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u505a\u6cd5\uff0c\u6c11\u5fc3\u5927\u5931\u3002\u6211\u4eb2\u773c\u89c1\u5230\u5927\u738b\u5728\u57ce\u5934\u5c04\u4e86\u4e09\u7bad\uff0c\u4e25\u7981\u6740\u4eba\u63b3\u63a0\uff0c\u5b9a\u662f\u5927\u738b\u5c1a\u4e0d\u77e5\u60c5\u3002\u6211\u8fd9\u5c31\u53bb\u7980\u62a5\uff0c\u8bf7\u4ed6\u4e0b\u4ee4\u7981\u6b62\u3002\u201d\u7a0b\u9752\u7af9\u529d\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u95ef\u738b\u90e8\u4e0b\u6709\u8bb8\u591a\u672c\u662f\u76d7\u8d3c\u51fa\u8eab\uff0c\u6765\u5230\u8fd9\u5e1d\u738b\u4e4b\u90fd\uff0c\u82b1\u82b1\u4e16\u754c\uff0c\u54ea\u6709\u4e0d\u653e\u8086\u4e00\u756a\u7684\uff1f\u4e14\u8fc7\u5f97\u51e0\u5929\uff0c\u518d\u5411\u5927\u738b\u8fdb\u8a00\u5427\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u8fc7\u5f97\u51e0\u5929\uff0c\u5317\u4eac\u57ce\u91cc\u8001\u767e\u59d3\u90fd\u7ed9\u4ed6\u4eec\u5bb3\u82e6\u4e86\u3002\u6551\u6c11\u5982\u6551\u706b\uff0c\u600e\u80fd\u7b49\u5f97\uff1f\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u7136\u5916\u9762\u558a\u58f0\u5927\u9707\u3002\u8881\u627f\u5fd7\u7b49\u5403\u4e86\u4e00\u60ca\uff0c\u5954\u5230\u95e8\u5916\uff0c\u53ea\u89c1\u65e0\u6570\u4eba\u9a6c\u62e5\u5728\u6b63\u6761\u5b50\u80e1\u540c\u51fa\u53e3\u3002\u5148\u524d\u7ed9\u94c1\u7f57\u6c49\u6253\u8d70\u7684\u90a3\u519b\u5b98\u9a91\u5728\u9a6c\u4e0a\uff0c\u624b\u6267\u5927\u5200\uff0c\u53eb\u9053\uff1a\u201c\u8881\u627f\u5fd7\uff0c\u6743\u5c06\u519b\u53eb\u4f60\u53bb\u8bf4\u8bdd\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5f53\u771f\u662f\u6743\u5c06\u519b\u5429\u5490\u5417\uff1f\u201d\u53e6\u4e00\u540d\u519b\u5b98\u53d6\u51fa\u4e00\u652f\u4ee4\u7bad\uff0c\u9053\uff1a\u201c\u6709\u6743\u5c06\u519b\u7684\u4ee4\u7bad\u5728\u6b64\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u4e0d\u53bb\uff0c\u4f24\u4e86\u5144\u5f1f\u95f4\u7684\u548c\u6c14\u3002\u89c1\u5230\u6743\u5c06\u519b\uff0c\u6b63\u53ef\u529d\u4ed6\u7ea6\u675f\u90e8\u5c5e\uff0c\u4e0d\u53ef\u80e1\u4f5c\u975e\u4e3a\u3002\u201d\u4fbf\u70b9\u5934\u9053\uff1a\u201c\u597d\uff01\u6211\u540c\u4f60\u53bb\u4fbf\u662f\u3002\u201d\u90a3\u519b\u5b98\u559d\u9053\uff1a\u201c\u7ed1\u4e86\uff01\u201d\u4fbf\u6709\u4e03\u516b\u540d\u58eb\u5175\u62e5\u4e0a\u524d\u6765\uff0c\u53d6\u51fa\u7ef3\u7d22\u8981\u7ed1\u3002\u8881\u627f\u5fd7\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u4e0d\u62b5\u62d2\uff0c\u53cd\u624b\u5728\u80cc\u540e\uff0c\u4efb\u7531\u7ed1\u7f1a\u3002\u94c1\u7f57\u6c49\u3001\u6c99\u5929\u5e7f\u7b49\u9f50\u58f0\u547c\u559d\uff1a\u201c\u8c01\u6562\u52a8\u624b\uff1f\u201d\u51b2\u4e0a\u53bb\u4fbf\u8981\u6253\u4eba\u3002\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u4e0d\u53ef\u52a8\u7c97\uff0c\u6211\u89c1\u4e86\u6743\u5c06\u519b\u81ea\u6709\u5206\u8fa9\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u6307\u7740\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u5d07\u796f\u7687\u5e1d\u7684\u516c\u4e3b\uff0c\u65ad\u4e86\u4e00\u53ea\u624b\u7684\u3002\u6743\u5c06\u519b\u6307\u660e\u8981\u8fd9\u4eba\uff0c\u628a\u5979\u5e26\u4e86\u53bb\u3002\u201d\u4f17\u519b\u58eb\u4fbf\u5411\u4f55\u60d5\u5b88\u5954\u6765\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u91d1\u94a9\u4e00\u5212\uff0c\u963b\u4f4f\u4f17\u519b\u58eb\u8fd1\u524d\uff0c\u7b11\u95ee\uff1a\u201c\u6743\u5c06\u519b\u8981\u6211\u53bb\u5e72\u751a\u4e48\uff1f\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u6253\u7834\u5317\u4eac\uff0c\u6743\u5c06\u519b\u529f\u52b3\u7b2c\u4e00\u3002\u5d07\u796f\u7684\u516c\u4e3b\uff0c\u81ea\u7136\u5f52\u6743\u5c06\u519b\u6240\u6709\u3002\u5feb\u4e56\u4e56\u7684\u6765\u5427\uff0c\u4ee5\u540e\u4e00\u751f\u5bcc\u8d35\uff0c\u5305\u4f60\u4eab\u7528\u4e0d\u5c3d\u3002\u201d\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u90a3\u5012\u5999\u5f97\u5f88\u3002\u8981\u662f\u6211\u4e0d\u80af\u8ddf\u4f60\u53bb\u5462\uff1f\u201d\u90a3\u519b\u5b98\u559d\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u4e48\u591a\u5570\u5506\u7684\uff1f\u5e26\u4e86\u53bb\uff01\u201d\u4f55\u60d5\u5b88\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u90a3\u4e2a\u6743\u5c06\u519b\u8981\u62a2\u6211\u53bb\u505a\u5c0f\u8001\u5a46\u5462\u3002\u4f60\u8bf4\u6211\u53bb\u662f\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u5012\u662f\u96be\u4ee5\u56de\u7b54\u3002\u4f46\u89c1\u51e0\u540d\u58eb\u5352\u62e5\u4e0a\u53bb\u5411\u4f55\u60d5\u5b88\u4fbf\u62c9\u3002\u4f55\u60d5\u5b88\u53ea\u662f\u683c\u683c\u5a07\u7b11\uff0c\u5e76\u4e0d\u52a8\u624b\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u62c9\u5979\u7684\u58eb\u5352\u4ef0\u5929\u4fbf\u5012\uff0c\u7a0d\u4e00\u626d\u52a8\uff0c\u4fbf\u5747\u6bd9\u547d\u3002\u539f\u6765\u4f55\u60d5\u5b88\u8863\u886b\u4e4b\u4e0a\uff0c\u5c3d\u662f\u5267\u6bd2\u3002\u90a3\u519b\u5b98\u5927\u60ca\u4e4b\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\u3002\u524d\u660e\u4f59\u5b7d\uff0c\u6297\u62d2\u4e49\u519b\uff0c\u6740\u554a\uff01\u201d\u5200\u67aa\u7eb7\u4e3e\uff0c\u5411\u94c1\u7f57\u6c49\u7b49\u4eba\u5934\u4e0a\u780d\u843d\u3002 \u3000\u3000\u7fa4\u96c4\u5230\u6b64\u5730\u6b65\uff0c\u5c82\u6709\u675f\u624b\u5f85\u6bd9\u4e4b\u7406\uff1f\u62a2\u8fc7\u5200\u67aa\uff0c\u53cd\u6740\u8fc7\u53bb\uff0c\u4e00\u9635\u683c\u6597\uff0c\u95ef\u519b\u5b98\u5175\u4e71\u6210\u4e00\u56e2\uff0c\u62e5\u5728\u80e1\u540c\u4e2d\u8fdb\u9000\u4e0d\u5f97\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u56de\u62a5\u6743\u5c06\u519b\uff0c\u5927\u5bb6\u540c\u5230\u5927\u738b\u8ddf\u524d\uff0c\u5206\u8fa9\u662f\u975e\u66f2\u76f4\u3002\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u7ed1\u5728\u4ed6\u624b\u8155\u4e0a\u7684\u7ef3\u7d22\u767b\u65f6\u65ad\u4e86\uff0c\u7eb5\u8eab\u800c\u8d77\uff0c\u53cc\u624b\u6293\u4f4f\u4e24\u540d\u519b\u5b98\uff0c\u626f\u4e0b\u9a6c\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5f53\u5b98\u7684\u7559\u7740\uff0c\u58eb\u5175\u90fd\u56de\u8425\u53bb\u3002\u201d\u4f17\u5175\u89c1\u957f\u5b98\u88ab\u64d2\uff0c\u4e0d\u6562\u518d\u6597\uff0c\u63a8\u63a8\u62e5\u62e5\u7684\u8d70\u4e86\u3002 \u3000\u3000\u8881\u627f\u5fd7\u957f\u53f9\u4e00\u58f0\uff0c\u6447\u4e86\u6447\u5934\uff0c\u547d\u80e1\u6842\u5357\u548c\u6d2a\u80dc\u6d77\u62bc\u4e86\u4e24\u540d\u519b\u5b98\uff0c\u53bb\u89c1\u674e\u81ea\u6210\u3002 \u3000\u3000\u8fdb\u5f97\u5bab\u6765\uff0c\u53ea\u89c1\u6bbf\u4e0a\u8bbe\u4e86\u76db\u5bb4\uff0c\u674e\u81ea\u6210\u6b63\u5728\u5927\u5bb4\u8bf8\u5c06\uff0c\u4e1d\u7af9\u76c8\u8033\uff0c\u9152\u8089\u6d41\u6c34\u4ef7\u9001\u5c06\u4e0a\u6765\u3002\u674e\u81ea\u6210\u5df2\u559d\u5f97\u5fae\u91ba\uff0c\u89c1\u5230\u8881\u627f\u5fd7\uff0c\u559c\u9053\uff1a\u201c\u597d\uff0c\u8881\u627f\u5fd7\uff0c\u4f60\u4e5f\u8fc7\u6765\u559d\u4e00\u676f\uff01\u201d\u8881\u627f\u5fd7\u8eac\u8eab\u9053\uff1a\u201c\u662f\uff01\u201d\u8d70\u8fd1\u53bb\u63a5\u8fc7\u674e\u81ea\u6210\u624b\u4e2d\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u5750\u5728\u674e\u81ea\u6210\u5de6\u4fa7\u7684\u4e00\u540d\u5c06\u519b\u970d\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u559d\u9053\uff1a\u201c\u8881\u627f\u5fd7\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff0c\u4ed7\u4e86\u8c01\u7684\u52bf\u529b\uff0c\u6562\u6740\u6211\u90e8\u5c5e\uff1f\u201d\u8881\u627f\u5fd7\u89c1\u8fd9\u4eba\u6ee1\u8138\u6d53\u9aef\uff0c\u795e\u6001\u7c97\u8c6a\uff0c\u60f3\u6765\u4fbf\u662f\u6743\u5c06\u519b\u5218\u5b97\u654f\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6743\u5c06\u519b\u4e48\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u6b63\u662f\u3002\u5927\u738b\u4e0d\u8fc7\u5c01\u4e86\u4f60\u4e00\u4e2a\u5c0f\u5c0f\u679c\u6bc5\u5c06\u519b\uff0c\u4f60\u5c31\u4e0d\u628a\u6211\u6743\u5c06\u519b\u77a7\u5728\u773c\u91cc\u4e86\uff0c\u7adf\u6562\u6740\u6211\u90e8\u4e0b\uff01\u201d \u3000\u3000\u8bf4\u7740\u4f38\u624b\u6293\u4f4f\u5200\u67c4\uff0c\u5c06\u5200\u62d4\u51fa\u4e00\u534a\uff0c\u556a\u5730\u4e00\u58f0\uff0c\u53c8\u9001\u5200\u5165\u9798\u3002 \u3000\u3000\u970e\u65f6\u4e4b\u95f4\uff0c\u6bbf\u4e0a\u6570\u767e\u4eba\u5bc2\u9759\u65e0\u58f0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u738b\u5165\u57ce\u4e4b\u65f6\u66fe\u6709\u53f7\u4ee4\uff0c\u6709\u8c01\u6740\u4f24\u767e\u59d3\uff0c\u5978\u6deb\u63b3\u63a0\uff0c\u4e00\u6982\u65a9\u9996\u3002\u5728\u4e0b\u89c1\u5230\u672c\u519b\u5144\u5f1f\u6b63\u5728\u8650\u6740\u767e\u59d3\uff0c\u8fd9\u624d\u51fa\u624b\u963b\u6b62\uff0c\u5b9e\u975e\u6709\u610f\u5f97\u7f6a\uff0c\u8fd8\u8bf7\u6743\u5c06\u519b\u89c1\u8c05\u3002\u201d \u3000\u3000\u5218\u5b97\u654f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5929\u4e0b\u662f\u5927\u738b\u7684\u5929\u4e0b\uff0c\u662f\u6211\u4eec\u8001\u5144\u5f1f\u51fa\u6b7b\u5165\u751f\u3001\u4ece\u5200\u5c71\u67aa\u6797\u91cc\u6253\u51fa\u6765\u7684\u5929\u4e0b\u3002\u6211\u4eec\u4f1a\u6253\u6c5f\u5c71\uff0c\u96be\u9053\u4e0d\u4f1a\u5750\u6c5f\u5c71\u4e48\uff1f\u4f60\u6765\u8ba8\u597d\u767e\u59d3\uff0c\u6536\u7f57\u4eba\u5fc3\uff0c\u5230\u5e95\u662f\u751a\u4e48\u5c45\u5fc3\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u738b\u521a\u624d\u8bf4\u8fc7\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5c31\u662f\u767e\u59d3\u3002\u201d\u5218\u5b97\u654f\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u738b\u6253\u6c5f\u5c71\u7684\u65f6\u5019\u662f\u767e\u59d3\u3002\u4eca\u65e5\u5f97\u4e86\u5929\u4e0b\uff0c\u5750\u4e86\u9f99\u5ef7\uff0c\u4fbf\u662f\u771f\u547d\u5929\u5b50\u4e86\uff0c\u96be\u9053\u8fd8\u662f\u8001\u767e\u59d3\u5417\uff1f\u4f60\u8fd9\u5c0f\u5b50\u80e1\u8bf4\u516b\u9053\u3002\u201d\u8881\u627f\u5fd7\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u674e\u81ea\u6210\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u597d\u5566\uff01\u5927\u5bb6\u81ea\u5df1\u5144\u5f1f\uff0c\u522b\u4e3a\u8fd9\u4e9b\u5c0f\u4e8b\u4f24\u4e86\u548c\u6c14\u3002\u6765\u6765\u6765\uff0c\u4f60\u4eec\u4e24\u4e2a\u5e72\u4e00\u676f\u3002\u5b97\u654f\uff0c\u6211\u77e5\u4f60\u53ea\u56e0\u8881\u627f\u5fd7\u5f97\u4e86\u516c\u4e3b\uff0c\u4e3a\u6b64\u5403\u918b\u3002\u7687\u5bab\u91cc\u7f8e\u5973\u8981\u591a\u5c11\u6709\u591a\u5c11\uff0c\u5f85\u4f1a\u4f60\u81ea\u5df1\u53bb\u62e3\u4fbf\u662f\u3002\u201d\u5218\u5b97\u654f\u9053\uff1a\u201c\u5927\u738b\uff0c\u5d07\u796f\u7684\u516c\u4e3b\u5374\u53ea\u6709\u4e00\u4e2a\u3002\u201d\u674e\u81ea\u6210\u5411\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4ed6\u5b9a\u8981\u4f60\u7684\u516c\u4e3b\uff0c\u4f60\u5c31\u77a7\u5728\u6211\u9762\u4e0a\uff0c\u8ba9\u4e86\u7ed9\u4ed6\u7f62\u3002\u4f60\u4eec\u4e00\u6bbf\u4e3a\u81e3\uff0c\u548c\u6c14\u8981\u7d27\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u542c\uff0c\u4e0d\u7531\u5f97\u6115\u7136\uff0c\u5fc3\u4e2d\u832b\u7136\u82e5\u5931\uff0c\u624b\u4e00\u677e\uff0c\u9152\u676f\u6389\u5728\u5730\u4e0b\uff0c\u767b\u6210\u788e\u7247\u3002\u674e\u81ea\u6210\u6012\u9053\uff1a\u201c\u4f60\u5c31\u7b97\u4e0d\u80af\uff0c\u4e5f\u4e0d\u7528\u5411\u6211\u53d1\u813e\u6c14\u3002\u201d\u8881\u627f\u5fd7\u4e00\u60ca\uff0c\u5fd9\u8eac\u8eab\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u6562\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e1d\u7af9\u58f0\u54cd\uff0c\u51e0\u540d\u519b\u5b98\u62e5\u7740\u4e00\u4e2a\u5973\u5b50\u8d70\u4e0a\u6bbf\u6765\u3002\u90a3\u5973\u5b50\u5411\u674e\u81ea\u6210\u76c8\u76c8\u62dc\u5012\uff0c\u62dc\u6bd5\u7ad9\u8d77\uff0c\u70db\u5149\u6620\u5230\u5979\u8138\u4e0a\uff0c\u4f17\u4eba\u90fd\u4e0d\u7ea6\u800c\u540c\u7684\u201c\u54e6\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u81ea\u7ec3\u4e86\u6df7\u5143\u529f\u540e\uff0c\u7cbe\u795e\u6781\u662f\u628a\u6301\u5f97\u5b9a\uff0c\u867d\u4e0e\u963f\u4e5d\u540c\u887e\u5171\u6795\uff0c\u4ea6\u65e0\u975e\u793c\u4e4b\u884c\uff0c\u4f46\u6b64\u523b\u4e00\u89c1\u8fd9\u5973\u5b50\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u5929\u4e0b\u7adf\u6709\u8fd9\u7b49\u7f8e\u8c8c\u7684\u5973\u5b50\uff01\u201d \u3000\u3000\u90a3\u5973\u5b50\u76ee\u5149\u6d41\u8f6c\uff0c\u4ece\u4f17\u4eba\u8138\u4e0a\u63a0\u8fc7\uff0c\u6bcf\u4e2a\u4eba\u548c\u5979\u773c\u6ce2\u4e00\u89e6\uff0c\u90fd\u5982\u5168\u8eab\u6d78\u5728\u6696\u6d0b\u6d0b\u7684\u6e29\u6c34\u4e2d\u4e00\u822c\uff0c\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\u53d7\u7528\u3002 \u3000\u3000\u53ea\u542c\u5979\u83ba\u58f0\u5456\u5456\u7684\u8bf4\u9053\uff1a\u201c\u8d31\u59be\u9648\u5706\u5706\u62dc\u89c1\u5927\u738b\uff0c\u613f\u5927\u738b\u4e07\u5c81\u3001\u4e07\u5c81\u3001\u4e07\u4e07\u5c81\u3002\u201d \u3000\u3000\u674e\u81ea\u6210\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\u7f8e\u8c8c\u7684\u5a18\u513f\uff01\u201d\u5218\u5b97\u654f\u9053\uff1a\u201c\u5927\u738b\uff0c\u90a3\u5d07\u796f\u7684\u516c\u4e3b\uff0c\u5c0f\u5c06\u4e5f\u4e0d\u8981\u4e86\u3002\u4f60\u628a\u8fd9\u5a18\u513f\u8d50\u4e86\u7ed9\u6211\u7f62\u3002\u201d \u3000\u3000\u725b\u91d1\u661f\u9053\uff1a\u201c\u5218\u5c06\u519b\uff0c\u8fd9\u9648\u5706\u5706\u662f\u9547\u5b88\u5c71\u6d77\u5173\u603b\u5175\u5b98\u5434\u4e09\u6842\u7684\u7231\u59be\uff0c\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u7f8e\u4eba\u3002\u5927\u738b\u7279\u5730\u53ec\u6765\u7684\uff0c\u600e\u80fd\u7ed9\u4f60\uff1f\u201d\u5218\u5b97\u654f\u542c\u5f97\u662f\u674e\u81ea\u6210\u81ea\u5df1\u8981\uff0c\u4e0d\u6562\u518d\u8bf4\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u77aa\u89c6\u7740\u9648\u5706\u5706\uff0c\u9aa8\u90fd\u4e00\u58f0\uff0c\u541e\u4e86\u4e00\u5927\u53e3\u998b\u6d8e\u3002 \u3000\u3000\u7687\u6781\u6bbf\u4e0a\u4e00\u65f6\u5bc2\u9759\u65e0\u58f0\uff0c\u5ffd\u7136\u95f4\u5f53\u5577\u4e00\u58f0\uff0c\u6709\u4eba\u624b\u4e2d\u9152\u676f\u843d\u5730\uff0c\u63a5\u7740\u53c8\u662f\u5f53\u5577\u3001\u5f53\u5577\u4e24\u54cd\uff0c\u53c8\u6709\u4eba\u9152\u676f\u843d\u5730\u3002\u9002\u624d\u8881\u627f\u5fd7\u7684\u9152\u676f\u6389\u5728\u5730\u4e0b\uff0c\u674e\u81ea\u6210\u751a\u662f\u607c\u6012\uff0c\u6b64\u523b\u4eba\u4eba\u77a7\u7740\u9648\u5706\u5706\u7684\u4e3d\u5bb9\u5a9a\u6001\uff0c\u7adf\u662f\u8c01\u4e5f\u6ca1\u7559\u795e\u5230\u522b\u7684\u3002 \u3000\u3000\u5ffd\u7136\u95f4\u5750\u5728\u4e0b\u9996\u7684\u4e00\u540d\u5c0f\u5c06\u53e3\u4e2d\u53d1\u51fa\u5475\u5475\u4f4e\u58f0\uff0c\u722c\u5728\u5730\u4e0b\uff0c\u4fbf\u53bb\u62b1\u9648\u5706\u5706\u7684\u817f\u3002\u9648\u5706\u5706\u4e00\u58f0\u5c16\u53eb\uff0c\u907f\u4e86\u5f00\u53bb\u3002\u90a3\u8fb9\u4e00\u540d\u5c06\u519b\u53eb\u9053\uff1a\u201c\u597d\u70ed\uff0c\u597d\u70ed\uff01\u201d\u55e4\u7684\u4e00\u58f0\uff0c\u6495\u5f00\u4e86\u81ea\u5df1\u8863\u886b\u3002 \u3000\u3000\u53c8\u6709\u4e00\u540d\u5c06\u5b98\u53eb\u9053\uff1a\u201c\u7f8e\u4eba\u513f\uff0c\u4f60\u559d\u4e86\u6211\u624b\u91cc\u8fd9\u676f\u9152\uff0c\u6211\u5c31\u6b7b\u4e5f\u7518\u5fc3\uff01\u201d\u4e3e\u7740\u9152\u676f\uff0c\u51d1\u5230\u9648\u5706\u5706\u5507\u8fb9\u3002 \u3000\u3000\u4e00\u65f6\u4eba\u5fc3\u6d6e\u52a8\uff0c\u6ee1\u6bbf\u8eab\u7ecf\u767e\u6218\u7684\u608d\u5c06\u90fd\u4e3a\u9648\u5706\u5706\u7684\u7f8e\u8272\u6240\u8ff7\u3002 \u3000\u3000\u8881\u627f\u5fd7\u53ea\u770b\u5f97\u6697\u6697\u6447\u5934\uff0c\u4fbf\u6b32\u51fa\u6bbf\uff0c\u5ffd\u542c\u5f97\u674e\u5ca9\u5927\u58f0\u559d\u9053\uff1a\u201c\u5927\u738b\u9a7e\u524d\uff0c\u4f17\u5144\u5f1f\u4e0d\u5f97\u65e0\u793c\u3002\u201d\u4e00\u540d\u5c06\u519b\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4f38\u4e00\u4e2a\u5c0f\u6307\u5934\u513f\uff0c\u6478\u4e00\u6478\u7f8e\u4eba\u513f\u7684\u96ea\u767d\u8138\u86cb\uff0c\u90a3\u4e5f\u4e0d\u6253\u7d27\u5427\uff01\u201d\u8bf4\u7740\u4f38\u51fa\u624b\u6307\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u5411\u9648\u5706\u5706\u8d70\u53bb\u3002 \u3000\u3000\u674e\u81ea\u6210\u559d\u9053\uff1a\u201c\u628a\u7f8e\u4eba\u513f\u9001\u5230\u540e\u5bab\u53bb\u3002\u5b8b\u732e\u7b56\uff0c\u4f60\u5e26\u5175\u770b\u5b88\u3002\u201d\u5b8b\u732e\u7b56\u7b54\u5e94\u4e86\uff0c\u9886\u7740\u9648\u5706\u5706\u5165\u5185\u3002 \u3000\u3000\u6570\u5341\u540d\u519b\u5b98\u4e00\u9f50\u8702\u6d8c\u8fc7\u53bb\uff0c\u4e89\u7740\u8981\u591a\u770b\u4e00\u773c\uff0c\u76f4\u5230\u9648\u5706\u5706\u7684\u540e\u5f71\u4e5f\u77a7\u4e0d\u89c1\u4e86\uff0c\u624d\u604b\u604b\u4e0d\u820d\u7684\u6162\u6162\u5f52\u5ea7\u3002\u4e00\u4eba\u4e3e\u9f3b\u72c2\u55c5\uff0c\u8bf4\u9053\uff1a\u201c\u7f8e\u4eba\u513f\u7684\u9999\u6c14\uff0c\u95fb\u4e00\u95fb\u4e5f\u662f\u524d\u4e16\u4fee\u6765\u7684\u3002\u201d\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4eba\uff0c\u662f\u72d0\u72f8\u7cbe\u53d8\u7684\uff0c\u5927\u738b\u4e0d\u53ef\u6536\u7528\u3002\u201d\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u5c31\u7b97\u662f\u5403\u4eba\u5996\u9b54\uff0c\u6211\u53ea\u8981\u62b1\u5979\u4e00\u62b1\uff0c\u7acb\u523b\u7ed9\u5979\u5403\u4e86\uff0c\u90a3\u4e5f\u5feb\u6d3b\u5f97\u5f88\u3002\u201d \u3000\u3000\u674e\u81ea\u6210\u4e00\u53e3\u4e00\u53e3\u559d\u9152\uff0c\u8138\u4e0a\u795e\u8272\u663e\u662f\u4e50\u4e0d\u53ef\u652f\uff0c\u5bf9\u4f17\u5c06\u5b98\u7684\u4e11\u6001\u5168\u6ca1\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u674e\u5ca9\u8d70\u4e0a\u51e0\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u738b\uff0c\u5434\u4e09\u6842\u62e5\u5175\u5c71\u6d77\u5173\uff0c\u6709\u7cbe\u5175\u56db\u4e07\uff0c\u53c8\u6709\u8fbd\u6c11\u516b\u4e07\uff0c\u90fd\u662f\u7cbe\u608d\u5584\u6218\u3002\u5927\u738b\u65e2\u5df2\u6d3e\u4eba\u62db\u964d\uff0c\u4ed6\u7684\u5c0f\u59be\uff0c\u8fd8\u662f\u653e\u8fd8\u4ed6\u5e9c\u4e2d\uff0c\u4ee5\u5b89\u5176\u5fc3\u4e3a\u662f\u3002\u201d\u5218\u5b97\u654f\u51b7\u7b11\u9053\uff1a\u201c\u5434\u4e09\u6842\u56db\u4e07\u5175\u9a6c\uff0c\u6709\u4e2a\u5c41\u7528\uff1f\u5317\u4eac\u57ce\u91cc\u5d07\u796f\u5341\u591a\u4e07\u5b98\u5175\uff0c\u9047\u4e0a\u4e86\u54b1\u4eec\uff0c\u8fd8\u4e0d\u662f\u5e0c\u54e9\u82b1\u5566\u7684\u4e00\u53e4\u8111\u513f\u90fd\u57ae\u4e86\u3002\u201d\u674e\u81ea\u6210\u70b9\u5934\u9053\uff1a\u201c\u5434\u4e09\u6842\u5c0f\u4e8b\u4e00\u6869\uff0c\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\u3002\u4ed6\u82e5\u6295\u964d\uff0c\u90a3\u662f\u8bc6\u597d\u6b79\u7684\uff0c\u5426\u5219\u7684\u6d3b\uff0c\u8fd8\u4e0d\u662f\u624b\u5230\u64d2\u6765\uff1f\u5434\u4e09\u6842\u96be\u9053\u6bd4\u5b59\u4f20\u5ead\u3001\u5468\u9047\u5409\u8fd8\u5389\u5bb3\u4e48\uff1f\u201d \u3000\u3000\u674e\u5ca9\u9053\uff1a\u201c\u5927\u738b\u867d\u5df2\u5f97\u4e86\u5317\u4eac\uff0c\u4f46\u6c5f\u5357\u672a\u5b9a\u2026\u2026\u201d\u674e\u81ea\u6210\u6325\u624b\u9053\uff1a\u201c\u5927\u5bb6\u559d\u9152\uff0c\u5927\u5bb6\u559d\u9152\uff01\u6b64\u523b\u4e0d\u662f\u8bf4\u56fd\u5bb6\u5927\u4e8b\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u674e\u5ca9\u53ea\u5f97\u9053\uff1a\u201c\u662f\u3002\u201d\u9000\u4e86\u4e0b\u53bb\uff0c\u5750\u5728\u8881\u627f\u5fd7\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e00\u5207\u5c0f\u5fc3\uff0c\u987b\u9632\u6743\u5c06\u519b\u5bf9\u4f60\u4e0d\u5229\u3002\u201d\u8881\u627f\u5fd7\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53ea\u89c1\u674e\u81ea\u6210\u559d\u4e86\u51e0\u676f\u9152\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5927\u4f19\u513f\u6563\u4e86\u7f62\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u7ffb\u4e86\u684c\u5b50\uff0c\u8f6c\u8eab\u800c\u5165\u3002\u4f17\u5c06\u4e00\u54c4\u800c\u6563\u3002 \u3000\u3000\u8881\u627f\u5fd7\u968f\u7740\u674e\u5ca9\u51fa\u6bbf\uff0c\u5728\u5bab\u95e8\u5916\u9047\u5230\u80e1\u6842\u5357\u548c\u6d2a\u80dc\u6d77\uff0c\u5429\u5490\u5c06\u4e24\u540d\u519b\u5b98\u653e\u4e86\u3002 \u3000\u3000\u56db\u4eba\u521a\u8f6c\u8fc7\u4e00\u6761\u8857\uff0c\u4fbf\u89c1\u6570\u5341\u540d\u95ef\u519b\u6b63\u5728\u4e00\u6240\u5927\u5b85\u4e2d\u63b3\u63a0\uff0c\u62d6\u4e86\u4e24\u540d\u5e74\u8f7b\u5987\u5973\u51fa\u6765\u3002\u4e24\u540d\u5973\u5b50\u53ea\u662f\u54ed\u53eb\uff0c\u6323\u624e\u7740\u4e0d\u80af\u8d70\u3002\u674e\u5ca9\u5927\u6012\uff0c\u559d\u4ee4\u90e8\u5c5e\u4e0a\u524d\u62ff\u95ee\u3002\u4f17\u95ef\u519b\u89c1\u662f\u5236\u5c06\u519b\u5230\u6765\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u629b\u4e0b\u5987\u5973\u8d22\u7269\u4fbf\u9003\u8d70\u4e86\u3002 \u3000\u3000\u4e00\u8def\u884c\u53bb\uff0c\u53ea\u542c\u5f97\u5230\u5904\u90fd\u662f\u519b\u58eb\u547c\u559d\u5b09\u7b11\u3001\u767e\u59d3\u54ed\u558a\u54c0\u547c\u4e4b\u58f0\u3002\u5927\u8857\u5c0f\u5df7\uff0c\u95ef\u519b\u58eb\u5352\u5954\u9a70\u6765\u53bb\uff0c\u6709\u7684\u80cc\u8d1f\u8d22\u7269\uff0c\u6709\u7684\u62b1\u4e86\u5987\u5973\u516c\u7136\u800c\u884c\u3002\u674e\u5ca9\u89c1\u7981\u4e0d\u80dc\u7981\uff0c\u62ff\u4e0d\u80dc\u62ff\uff0c\u53ea\u6709\u6d69\u53f9\u3002 \u3000\u3000\u8881\u627f\u5fd7\u672c\u6765\u4e00\u5fc3\u60f3\u671b\u674e\u81ea\u6210\u5f97\u4e86\u5929\u4e0b\u4e4b\u540e\uff0c\u4ece\u6b64\u559c\u89c1\u5347\u5e73\uff0c\u767e\u59d3\u5b89\u5c45\u4e50\u4e1a\uff0c\u4f46\u773c\u89c1\u4eca\u65e5\u674e\u81ea\u6210\u548c\u5218\u5b97\u654f\u7684\u8a00\u884c\uff0c\u53c8\u89c1\u5230\u6ee1\u57ce\u58eb\u5352\u5927\u63a0\u7684\u60e8\u51b5\uff0c\u6bd4\u4e4b\u5d07\u796f\u5728\u4f4d\uff0c\u53c8\u597d\u5f97\u4e86\u751a\u4e48\uff1f\u6ee1\u8154\u70ed\u671b\uff0c\u767b\u65f6\u5316\u4e3a\u4e4c\u6709\u3002 \u3000\u3000\u518d\u8d70\u5f97\u51e0\u6b65\uff0c\u53ea\u89c1\u5730\u4e0b\u8eba\u7740\u51e0\u5177\u5c38\u9996\uff0c\u4e24\u5177\u5973\u5c38\u5168\u8eab\u8d64\u88f8\u3002\u4f17\u5c38\u8eab\u4e0a\u4f24\u53e3\u4e2d\u5140\u81ea\u6d41\u8840\u672a\u6b62\u3002\u8881\u627f\u5fd7\u8fd9\u65f6\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u63e1\u4f4f\u674e\u5ca9\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8bf4\u95ef\u738b\u4e3a\u6c11\u4f38\u51a4\uff0c\u4e3a\u2026\u2026\u4e3a\u767e\u59d3\u51fa\u6c14\uff0c\u5c31\u662f\u8fd9\u6837\u4e48\uff1f\u201d\u8bf4\u7740\u7a81\u7136\u5750\u5012\u5728\u5730\uff0c\u653e\u58f0\u5927\u54ed\u3002 \u3000\u3000\u674e\u5ca9\u4e5f\u662f\u60b2\u6124\u4e0d\u5df2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u5c31\u53bb\u6c42\u89c1\u5927\u738b\uff0c\u8bf7\u4ed6\u975e\u7acb\u5373\u4e0b\u4ee4\u7981\u6b62\u63b3\u63a0\u4e0d\u53ef\u3002\u201d\u62c9\u8d77\u8881\u627f\u5fd7\uff0c\u56de\u5230\u7687\u5bab\uff0c\u5411\u536b\u58eb\u8bf4\u6709\u6025\u4e8b\u6c42\u89c1\u95ef\u738b\u3002\u201d \u3000\u3000\u536b\u58eb\u7980\u62a5\u8fdb\u53bb\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u51fa\u6765\u8bf4\u9053\uff1a\u201c\u5236\u5c06\u519b\uff0c\u5927\u738b\u5df2\u7ecf\u7761\u4e86\uff0c\u8c01\u4e5f\u4e0d\u6562\u60ca\u52a8\u3002\u8bf7\u5c06\u519b\u660e\u5929\u6765\u5427\u3002\u201d\u674e\u5ca9\u9053\uff1a\u201c\u6211\u8ddf\u968f\u5927\u738b\u591a\u5e74\uff0c\u6709\u4e8b\u6c42\u89c1\uff0c\u5927\u738b\u6df1\u66f4\u534a\u591c\u4e5f\u5fc5\u63a5\u89c1\u3002\u4f60\u518d\u53bb\u7980\u62a5\u7f62\u3002\u201d\u90a3\u536b\u58eb\u53c8\u8fdb\u53bb\u534a\u664c\uff0c\u51fa\u6765\u65f6\u6ee1\u8138\u60ca\u60f6\u4e4b\u8272\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5927\u738b\u5927\u53d1\u813e\u6c14\uff0c\u8bf4\u5c0f\u4eba\u82e5\u662f\u518d\u53bb\u5570\u5506\uff0c\u7acb\u523b\u780d\u4e86\u6211\u7684\u8111\u888b\u3002\u201d \u3000\u3000\u674e\u5ca9\u9053\uff1a\u201c\u597d\uff0c\u6211\u4fbf\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u7b49\u5927\u738b\u9192\u4e86\u4e4b\u540e\u518d\u89c1\u3002\u201d\u5bf9\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u5148\u56de\u53bb\u4f11\u606f\u5427\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u966a\u4f34\u5927\u54e5\u3002\u201d\u8981\u80e1\u6842\u5357\u3001\u6d2a\u80dc\u6d77\u4e8c\u4eba\u5148\u56de\uff0c\u4ee5\u514d\u9752\u9752\u7b49\u6302\u5ff5\u3002 \u3000\u3000\u4e24\u4eba\u7b49\u5230\u5929\u8272\u5927\u660e\uff0c\u624d\u89c1\u4e00\u540d\u536b\u58eb\u4ece\u5185\u5bab\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u738b\u53ec\u89c1\u3002\u201d\u4e24\u4eba\u8ddf\u7740\u4ed6\u6765\u5230\u4e00\u95f4\u623f\u4e2d\uff0c\u90a3\u536b\u58eb\u4fbf\u51fa\u53bb\u4e86\u3002\u76f4\u7b49\u4e86\u4e24\u4e2a\u591a\u65f6\u8fb0\uff0c\u773c\u89c1\u5348\u65f6\u5df2\u8fc7\uff0c\u674e\u81ea\u6210\u59cb\u7ec8\u4e0d\u51fa\u6765\u3002\u4e24\u4eba\u4f60\u77a7\u7740\u6211\uff0c\u6211\u77a7\u7740\u4f60\uff0c\u90fd\u662f\u5341\u5206\u7126\u6025\u3002 \u3000\u3000\u773c\u89c1\u65e5\u5934\u504f\u897f\uff0c\u5df2\u5230\u672a\u65f6\uff0c\u5ffd\u89c1\u5b8b\u732e\u7b56\u63a8\u95e8\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u5c06\u519b\uff0c\u8881\u5c06\u519b\uff0c\u4e24\u4f4d\u600e\u4e48\u5728\u8fd9\u91cc\uff1f\u201d\u674e\u5ca9\u9053\uff1a\u201c\u6211\u4eec\u6c42\u89c1\u5927\u738b\uff0c\u536b\u58eb\u8bf4\u9053\u5927\u738b\u53ec\u89c1\u3002\u53ef\u662f\u4ece\u6e05\u65e9\u76f4\u7b49\u5230\u8fd9\u65f6\u5019\uff0c\u5927\u738b\u59cb\u7ec8\u6ca1\u51fa\u6765\u3002\u201d\u5b8b\u732e\u7b56\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4eca\u65e5\u4e0a\u5348\uff0c\u5927\u738b\u53ec\u96c6\u8bf8\u5c06\u96c6\u8bae\uff0c\u5374\u8ba9\u4e24\u4f4d\u5728\u8fd9\u91cc\u82e6\u7b49\u3002\u201d\u674e\u5ca9\u60ca\u9053\uff1a\u201c\u5374\u662f\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b8b\u732e\u7b56\u9053\uff1a\u201c\u725b\u91d1\u661f\u90a3\u53ae\u4e0d\u65ad\u5728\u5927\u738b\u8ddf\u524d\u8bf4\u4f60\u7684\u574f\u8bdd\uff0c\u4e5f\u8bf4\u6211\u7684\u574f\u8bdd\u3002\u201d\u674e\u5ca9\u6012\u9053\uff1a\u201c\u4f60\u6211\u4e8c\u4eba\u884c\u5f97\u6b63\uff0c\u5750\u5f97\u6b63\uff0c\u6709\u751a\u4e48\u574f\u8bdd\u597d\u8bf4\uff1f\u201d \u3000\u3000\u5b8b\u732e\u7b56\u9053\uff1a\u201c\u5927\u738b\u5728\u6cb3\u5357\u4e4b\u65f6\uff0c\u4eba\u5fc3\u4e0d\u9644\uff0c\u90a3\u65f6\u6211\u60f3\u4e86\u4e2a\u8ba1\u8bae\u51fa\u6765\uff0c\u9020\u4e86\u4e00\u53e5\u8c36\u8bed\uff0c\u8bf4\u662f\u2018\u5341\u516b\u5b69\u513f\u4e3b\u795e\u5668\u2019\uff0c\u53eb\u4eba\u5230\u5904\u4f20\u64ad\u3002\u5341\u516b\u5b69\u513f\uff0c\u62da\u8d77\u6765\u662f\u4e2a\u2018\u674e\u2019\u5b57\uff0c\u4fbf\u662f\u8bf4\u5927\u738b\u5e94\u6709\u5929\u4e0b\u3002\u611a\u592b\u611a\u5987\u542c\u5230\u4e86\uff0c\u4ee5\u4e3a\u5927\u738b\u5929\u547d\u6538\u5f52\uff0c\u5927\u5bb6\u90fd\u6765\u5f52\u9644\uff0c\u54b1\u4eec\u7684\u58f0\u52bf\u767b\u65f6\u5927\u4e86\u8d77\u6765\u3002\u674e\u5c06\u519b\u53ef\u8fd8\u8bb0\u5f97\u4e48\uff1f\u201d\u674e\u5ca9\u9053\uff1a\u201c\u600e\u4e0d\u8bb0\u5f97\uff1f\u6211\u4f5c\u513f\u6b4c\uff0c\u4f60\u4f5c\u8c36\u8bed\uff0c\u52a8\u6447\u660e\u671d\u7684\u4eba\u5fc3\uff0c\u53ef\u4e5f\u6709\u4e9b\u529f\u52b3\u554a\u3002\u201d\u5b8b\u732e\u7b56\u6447\u5934\u9053\uff1a\u201c\u725b\u91d1\u661f\u5bf9\u5927\u738b\u8fdb\u8c17\uff0c\u8bf4\u90a3\u53e5\u2018\u5341\u516b\u5b69\u513f\u4e3b\u795e\u5668\u2019\uff0c\u4e0d\u662f\u6307\u5927\u738b\uff0c\u800c\u662f\u6307\u4f60\u674e\u5c06\u519b\uff01\u201d \u3000\u3000\u674e\u5ca9\u5fc3\u5934\u5927\u9707\uff0c\u5f53\u5373\u7ad9\u8d77\u3002\u4ed6\u77e5\u81ea\u6765\u5e1d\u7687\u6700\u5fcc\u4e4b\u4e8b\uff0c\u83ab\u8fc7\u4e8e\u6709\u4eba\u89ca\u89ce\u4ed6\u7684\u5b9d\u5ea7\u3002\u5386\u671d\u5f00\u56fd\u82f1\u4e3b\u6240\u4ee5\u5c60\u622e\u529f\u81e3\uff0c\u5982\u6c49\u9ad8\u7956\u3001\u660e\u592a\u7956\u7b49\u628a\u624b\u4e0b\u5927\u5c06\u6740\u5f97\u4e03\u96f6\u516b\u843d\uff0c\u4fbf\u662f\u6015\u4ed6\u4eec\u8c0b\u671d\u7be1\u4f4d\uff0c\u674e\u81ea\u6210\u8981\u662f\u4fe1\u4e86\u8fd9\u53e5\u8bdd\uff0c\u90a3\u53ef\u7cdf\u4e86\uff0c\u4e0d\u7531\u5f97\u98a4\u58f0\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u201d \u3000\u3000\u5b8b\u732e\u7b56\u9053\uff1a\u201c\u5927\u738b\u82f1\u660e\uff0c\u672a\u5fc5\u5c31\u4fe1\u4e86\uff0c\u5236\u5c06\u519b\u4e5f\u4e0d\u7528\u62c5\u5fc3\u3002 \u3000\u3000\u4e0d\u8fc7\u4eca\u65e5\u8bf8\u5c06\u5927\u4f1a\uff0c\u4f1a\u4e2d\u5218\u5c06\u519b\u3001\u5f20\u5c06\u519b\u3001\u8c37\u5c06\u519b\u3001\u7f57\u5c06\u519b\u4ed6\u4eec\uff0c\u4f17\u53e3\u4e00\u8f9e\u7684\u90fd\u8bf4\u5236\u5c06\u519b\u81ea\u9e23\u6e05\u9ad8\uff0c\u77a7\u4e0d\u8d77\u53cb\u519b\uff0c\u8bf4\u4ed6\u4eec\u90e8\u5c5e\u501f\u4f4f\u6c11\u623f\uff0c\u8ddf\u8001\u767e\u59d3\u501f\u51e0\u4e24\u94f6\u5b50\uff0c\u8ddf\u5927\u5a18\u95fa\u5973\u4eec\u8bf4\u51e0\u53e5\u8bdd\uff0c\u5236\u5c06\u519b\u7684\u90e8\u4e0b\u5c31\u53bb\u547c\u559d\u5e72\u6d89\u3002\u725b\u91d1\u661f\u5374\u9053\uff0c\u5236\u5c06\u519b\u8fd9\u4e0d\u662f\u81ea\u9e23\u6e05\u9ad8\uff0c\u800c\u662f\u6536\u7f57\u4eba\u5fc3\uff0c\u80f8\u6000\u5927\u5fd7\u3002\u201d \u3000\u3000\u674e\u5ca9\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u8138\u8272\u53d1\u767d\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u91cd\u91cd\u5750\u5728\u6905\u4e2d\u3002 \u3000\u3000\u5b8b\u732e\u7b56\u9053\uff1a\u201c\u6211\u4e3a\u5236\u5c06\u519b\u5206\u8fa9\u5f97\u51e0\u53e5\uff0c\u4f17\u5c06\u5c31\u5927\u9a82\u6211\u5b8b\u77ee\u5b50\u4e09\u5206\u4e0d\u50cf\u4eba\uff0c\u4e03\u5206\u5012\u50cf\u9b3c\uff0c\u6700\u4f1a\u80e1\u8bf4\u516b\u9053\u3002\u6211\u6c14\u4e0d\u8fc7\uff0c\u5c31\u51fa\u6765\u4e86\uff0c\u542c\u5bab\u95e8\u53e3\u536b\u58eb\u8bf4\uff0c\u4e24\u4f4d\u5c06\u519b\u5728\u6b64\uff0c\u56e0\u6b64\u8fc7\u6765\u77a7\u77a7\u3002\u5927\u738b\u6b64\u523b\u5fc3\u4e2d\u4e0d\u5feb\uff0c\u4e24\u4f4d\u4e0d\u5fc5\u7b49\u5019\u4e86\u3002\u201d \u3000\u3000\u674e\u5ca9\u62f1\u624b\u9053\uff1a\u201c\u591a\u627f\u5b8b\u519b\u5e08\u89c1\u7231\uff0c\u5144\u5f1f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u5b8b\u732e\u7b56\u53f9\u9053\uff1a\u201c\u54b1\u4eec\u867d\u7136\u6253\u4e0b\u4e86\u5317\u4eac\uff0c\u53ef\u662f\u6c5f\u5357\u672a\u5e73\uff0c\u5434\u4e09\u6842\u672a\u964d\uff0c\u6ee1\u6d32\u9791\u5b50\u864e\u89c6\u7708\u7708\uff0c\u66f4\u662f\u4e00\u5927\u9690\u5fe7\u3002\u4f46\u4eca\u65e5\u8bf8\u5c06\u5927\u4f1a\uff0c\u9664\u4e86\u7f16\u6392\u5236\u5c06\u519b\u7684\u4e0d\u662f\u4e4b\u5916\uff0c\u5c31\u662f\u5546\u91cf\u5982\u4f55\u62f7\u63a0\u660e\u671d\u6295\u964d\u7684\u5927\u5b98\u5bcc\u6237\uff0c\u8981\u4ed6\u4eec\u732e\u51fa\u91d1\u94f6\u8d22\u5b9d\u3002\u5509\uff0c\u6210\u5927\u4e8b\u7684\u4eba\uff0c\u773c\u754c\u4e5f\u672a\u514d\u592a\u5c0f\u4e86\u554a\u3002\u201d\u4e09\u4eba\u76f8\u5bf9\u53f9\u606f\uff0c\u51fa\u5bab\u800c\u522b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u542c\u4e86\u5b8b\u732e\u7b56\u4e00\u756a\u8bdd\uff0c\u89c1\u4ed6\u867d\u7136\u8eab\u9ad8\u4e0d\u6ee1\u4e09\u5c3a\uff0c\u5f62\u82e5\u72dd\u7334\uff0c\u5bb9\u8c8c\u4e11\u964b\uff0c\u8bf4\u8bdd\u5374\u662f\u6781\u6709\u89c1\u8bc6\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u4f4d\u5b8b\u519b\u5e08\u5b9e\u662f\u4e2a\u4eba\u624d\u3002\u201d\u674e\u5ca9\u9053\uff1a\u201c\u4ed6\u8db3\u667a\u591a\u8c0b\uff0c\u5f88\u4e86\u4e0d\u8d77\u3002\u53ea\u662f\u5927\u738b\u7231\u542c\u725b\u91d1\u661f\u7684\u8bdd\uff0c\u4e0d\u80af\u91cd\u7528\u5b8b\u519b\u5e08\u3002\u5176\u5b9e\u5927\u738b\u8bb8\u591a\u653b\u57ce\u63a0\u5730\u7684\u65b9\u7565\uff0c\u90fd\u662f\u51fa\u4e8e\u5b8b\u519b\u5e08\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u4e24\u4eba\u9ed8\u9ed8\u65e0\u8a00\u7684\u643a\u624b\u540c\u884c\uff0c\u8d70\u4e86\u6570\u767e\u6b65\u3002 \u3000\u3000\u674e\u5ca9\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u5927\u738b\u867d\u5df2\u6709\u7591\u6211\u4e4b\u610f\uff0c\u4f46\u4e3a\u81e3\u5c3d\u5fe0\uff0c\u4e3a\u53cb\u5c3d\u4e49\u3002\u6211\u7ec8\u4e0d\u80fd\u773c\u89c1\u5927\u738b\u5927\u4e1a\u8d25\u574f\uff0c\u95ed\u53e3\u4e0d\u8a00\u3002\u4f60\u5374\u4e0d\u7528\u5728\u671d\u4e2d\u53d7\u6c14\u4e86\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6b63\u662f\u3002\u5144\u5f1f\u662f\u505a\u4e0d\u6765\u5b98\u7684\u3002\u5927\u54e5\u5f53\u65e5\u66fe\u8bf4\uff0c\u5927\u529f\u544a\u6210\u4e4b\u540e\uff0c\u4f60\u6211\u9690\u5c45\u5c71\u6797\uff0c\u996e\u9152\u957f\u8c08\u4e3a\u4e50\u3002\u4f55\u4e0d\u5c31\u6b64\u8f9e\u5b98\u544a\u9000\uff0c\u4e5f\u514d\u5f97\u6210\u4e86\u65c1\u4eba\u773c\u4e2d\u4e4b\u9489\uff1f\u201d\u674e\u5ca9\u9053\uff1a\u201c\u5927\u738b\u773c\u524d\u5c1a\u6709\u8bb8\u591a\u5927\u4e8b\u8981\u529e\uff0c\u603b\u987b\u5e73\u4e86\u6c5f\u5357\uff0c\u4e00\u7edf\u5929\u4e0b\u4e4b\u540e\uff0c\u6211\u624d\u80fd\u5f52\u9690\u3002\u5927\u738b\u6614\u5e74\u5f85\u6211\u751a\u539a\uff0c\u773c\u89c1\u4ed6\u524d\u9014\u5371\u96be\u91cd\u91cd\uff0c\u6b63\u662f\u6211\u5c3d\u5fc3\u7aed\u529b\u3001\u4ee5\u6b7b\u76f8\u62a5\u4e4b\u65f6\u3002\u5c0f\u4eba\u6d41\u8a00\uff0c\u6211\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u4e24\u4eba\u53c8\u643a\u624b\u8d70\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u897f\u5317\u89d2\u4e0a\u706b\u5149\u51b2\u5929\u800c\u8d77\uff0c\u6599\u662f\u95ef\u519b\u53c8\u5728\u711a\u70e7\u6c11\u5c45\u3002\u674e\u5ca9\u4e0e\u8881\u627f\u5fd7\u8fd9\u51e0\u5929\u6765\u89c1\u5f97\u591a\u4e86\uff0c\u76f8\u5bf9\u6447\u5934\u53f9\u606f\u3002\u66ae\u972d\u82cd\u832b\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u524d\u9762\u5c0f\u5df7\u4e2d\u6709\u4eba\u54bf\u54bf\u5440\u5440\u7684\u62c9\u7740\u80e1\u7434\uff0c\u4e00\u4e2a\u82cd\u8001\u5636\u54d1\u7684\u58f0\u97f3\u5531\u4e86\u8d77\u6765\uff0c\u542c\u4ed6\u5531\u9053\uff1a\u201c\u65e0\u5b98\u65b9\u662f\u4e00\u8eab\u8f7b\uff0c\u4f34\u541b\u4f34\u864e\u81ea\u53e4\u4e91\u3002\u5f52\u5bb6\u4fbf\u662f\u4e09\u751f\u5e78\uff0c\u9e1f\u5c3d\u5f13\u85cf\u8d70\u72d7\u70f9\u2026\u2026\u201d \u3000\u3000\u53ea\u89c1\u5df7\u5b50\u4e2d\u8d70\u51fa\u4e00\u4e2a\u5e74\u8001\u76f2\u8005\uff0c\u7f13\u6b65\u800c\u884c\uff0c\u81ea\u62c9\u81ea\u5531\uff0c\u63a5\u7740\u5531\u9053\uff1a\u201c\u5b50\u80e5\u529f\u9ad8\u5434\u738b\u5fcc\uff0c\u6587\u79cd\u706d\u5434\u8eab\u9996\u5206\u3002\u53ef\u60dc\u4e86\u6dee\u9634\u547d\uff0c\u7a7a\u7559\u4e0b\u6b66\u7a46\u540d\u3002\u5927\u529f\u8c01\u53ca\u5f90\u5c06\u519b\uff1f\u795e\u673a\u5999\u7b97\u5218\u4f2f\u6e29\uff0c\u7b97\u4e0d\u5230\uff1a\u5927\u660e\u5929\u5b50\u5750\u9f99\u5ef7\uff0c\u6587\u6b66\u529f\u81e3\u547d\u5f52\u9634\u3002\u56e0\u6b64\u4e0a\uff0c\u6025\u56de\u5934\u6b7b\u91cc\u9003\u751f\uff1b\u56e0\u6b64\u4e0a\uff0c\u6025\u56de\u5934\u6b7b\u91cc\u9003\u751f\u2026\u2026\u201d \u3000\u3000\u674e\u5ca9\u542c\u5230\u8fd9\u91cc\uff0c\u5927\u6709\u611f\u89e6\uff0c\u5bfb\u601d\uff1a\u201c\u660e\u671d\u5f00\u56fd\u529f\u81e3\uff0c\u5f90\u8fbe\u3001\u5218\u57fa\u7b49\u4eba\u5c3d\u4e3a\u592a\u7956\u5bb3\u6b7b\u3002\u8fd9\u778e\u5b50\u4e5f\u77e5\u5df2\u7ecf\u6539\u671d\u6362\u4ee3\uff0c\u5426\u5219\u600e\u6562\u5531\u8fd9\u66f2\u5b50\uff1f\u201d\u77a7\u8fd9\u76f2\u4eba\u8863\u886b\u8934\u891b\uff0c\u662f\u4e2a\u5356\u5531\u7684\uff0c\u4f46\u5f53\u6b64\u4eba\u4eba\u96be\u4ee5\u81ea\u4fdd\u4e4b\u9645\uff0c\u54ea\u4e00\u4e2a\u6709\u5fc3\u7eea\u6765\u51fa\u94b1\u542c\u66f2\uff1f\u53ea\u542c\u4ed6\u63a5\u7740\u5531\u9053\uff1a\u201c\u541b\u738b\u4e0b\u65e8\u62ff\u529f\u81e3\uff0c\u5251\u62e5\u5175\u56f4\uff0c\u7ef3\u7f20\u7d22\u7ed1\uff0c\u8089\u98a4\u5fc3\u60ca\u3002\u6068\u4e0d\u80fd\uff0c\u5f97\u4fbf\u5904\u6295\u6cb3\u8df3\u4e95\uff1b\u6094\u4e0d\u53ca\uff0c\u8d77\u521d\u65f6\u8bc8\u6b7b\u57cb\u540d\u3002\u4eca\u65e5\u7684\u4e00\u7f15\u82f1\u9b42\uff0c\u6628\u65e5\u7684\u4e07\u91cc\u957f\u57ce\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u9762\u5531\uff0c\u4e00\u9762\u6f2b\u6b65\u8d70\u8fc7\u674e\u5ca9\u4e0e\u8881\u627f\u5fd7\u8eab\u8fb9\uff0c\u8f6c\u5165\u4e86\u53e6\u4e00\u6761\u5c0f\u5df7\u4e4b\u4e2d\uff0c\u6b4c\u58f0\u6e10\u6e10\u8fdc\u53bb\uff0c\u8bf4\u4e0d\u5c3d\u7684\u51c4\u60f6\u82cd\u51c9\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u60c5\u90c1\u90c1\uff0c\u56de\u5230\u4f4f\u5904\uff0c\u53ea\u89c1\u5927\u5385\u4e2d\u5750\u7740\u4e00\u4eba\u3002\u90a3\u4eba\u4e00\u89c1\u8881\u627f\u5fd7\uff0c\u4fbf\u5954\u5230\u5385\u53e3\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u4f60\u56de\u6765\u5566\u3002\u201d\u90a3\u4eba\u7c97\u8863\u8349\u5c65\uff0c\u80cc\u63d2\u957f\u5200\uff0c\u6b63\u662f\u5d14\u79cb\u5c71\u4e4b\u4f84\u5d14\u5e0c\u654f\u3002\u8881\u627f\u5fd7\u559c\u9053\uff1a\u201c\u4f60\u4e5f\u6765\u4e86\u3002\u6709\u751a\u4e48\u4e8b\uff1f\u201d\u5d14\u5e0c\u654f\u4ece\u8eab\u8fb9\u53d6\u51fa\u4e00\u5c01\u4fe1\u6765\uff0c\u53cc\u624b\u5448\u4e0a\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5c01\u76ae\u4e0a\u5199\u7740\u201c\u5b57\u8c15\u8bf8\u5f1f\u5b50\u201d\u5b57\u6837\uff0c\u8ba4\u5f97\u662f\u5e08\u7236\u7b14\u8ff9\uff0c\u5148\u4f5c\u4e86\u4e00\u63d6\uff0c\u7136\u540e\u606d\u606d\u656c\u656c\u7684\u63a5\u8fc7\u6765\uff0c\u62bd\u51fa\u4fe1\u7eb8\uff0c\u89c1\u4fe1\u4e0a\u5199\u9053\uff1a\u201c\u543e\u534e\u5c71\u6d3e\u5386\u6765\u95e8\u89c4\uff0c\u4e0d\u5f97\u5728\u671d\u5c45\u5b98\u4efb\u804c\u3002\u4eca\u95ef\u738b\u5927\u4e1a\u514b\u5c31\uff0c\u543e\u6d3e\u5f1f\u5b50\u529f\u6210\u8eab\u9000\uff0c\u5176\u4e8e\u56db\u6708\u6708\u5706\u4e4b\u5915\uff0c\u9f50\u96c6\u534e\u5c71\u4e4b\u5dc5\u3002\u201d \u3000\u3000\u4e0b\u9762\u7b7e\u7740\u4e2a\u201c\u6e05\u201d\u5b57\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u554a\uff0c\u8ddd\u4f1a\u671f\u5df2\u4e0d\u5230\u4e00\u6708\uff0c\u54b1\u4eec\u5c31\u5f97\u52a8\u8eab\u3002\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6211\u53d4\u53d4\u3001\u5b89\u5927\u5a18\u3001\u5c0f\u6167\u4e5f\u90fd\u8981\u53bb\u5462\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5165\u5185\u5bf9\u4f17\u4eba\u8bf4\u4e86\uff0c\u5374\u4e0d\u89c1\u9752\u9752\uff0c\u95ee\u7126\u5b9b\u513f\u9053\uff1a\u201c\u590f\u59d1\u5a18\u5462\uff1f\u201d\u7126\u5b9b\u513f\u9053\uff1a\u201c\u597d\u4e00\u4f1a\u6ca1\u89c1\u5979\u5566\uff0c\u6211\u53bb\u77a7\u77a7\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u53bb\u53eb\u5979\u3002\u201d\u8d70\u5230\u9752\u9752\u623f\u5916\uff0c\u5728\u95e8\u4e0a\u7528\u624b\u6307\u5f39\u4e86\u51e0\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u662f\u6211\u3002\u201d\u623f\u5185\u5e76\u65e0\u58f0\u606f\uff0c\u5019\u4e86\u7247\u523b\uff0c\u53c8\u8f7b\u8f7b\u62cd\u95e8\uff0c\u4ecd\u65e0\u56de\u97f3\u3002 \u3000\u3000\u8881\u627f\u5fd7\u628a\u95e8\u4e00\u63a8\uff0c\u623f\u95e8\u5e76\u672a\u4e0a\u95e9\uff0c\u5f80\u91cc\u5f20\u671b\uff0c\u53ea\u89c1\u623f\u5185\u7a7a\u65e0\u6240\u6709\uff0c\u8fdb\u5f97\u623f\u53bb\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u539f\u6765\u5979\u8863\u56ca\u3001\u957f\u5251\u7b49\u7269\u90fd\u5df2\u4e0d\u89c1\uff0c\u8fde\u5979\u6bcd\u4eb2\u7684\u9aa8\u7070\u7f50\u4e5f\u5e26\u8d70\u4e86\uff0c\u770b\u6765\u4f3c\u5df2\u8fdc\u884c\u3002\u8881\u627f\u5fd7\u5927\u6025\uff0c\u5728\u5404\u5904\u7ffb\u5bfb\uff0c\u5728\u5979\u6795\u4e0b\u627e\u5230\u4e00\u5f20\u5b57\u6761\uff0c\u4e0a\u9762\u5199\u9053\uff1a\u201c\u65e2\u6709\u91d1\u679d\u7389\u53f6\uff0c\u4f55\u5fc5\u8981\u6211\u5bfb\u5e38\u767e\u59d3\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u671b\u7740\u5b57\u6761\u5446\u5446\u7684\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u5fc3\u4e2d\u5343\u5934\u4e07\u7eea\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u81ea\u601d\uff1a\u201c\u6211\u5f85\u5979\u4e00\u7247\u771f\u5fc3\u8bda\u610f\uff0c\u5979\u603b\u662f\u5c0f\u5fc3\u773c\u513f\uff0c\u5904\u5904\u7591\u6211\u3002\u7537\u5b50\u6c49\u5927\u4e08\u592b\u505a\u4e8b\u5149\u660e\u78ca\u843d\uff0c\u4f46\u6c42\u5fc3\u4e4b\u6240\u5b89\u3002\u6211\u4eec\u6bcf\u65e5\u5728\u5200\u5c71\u67aa\u6797\u4e2d\u51fa\u6b7b\u5165\u751f\uff0c\u53c8\u600e\u80fd\u987e\u5f97\u5230\u79cd\u79cd\u5acc\u7591\uff1f\u9752\u5f1f\uff0c\u9752\u5f1f\uff0c\u4f60\u5b9e\u5728\u592a\u4e0d\u77e5\u6211\u7684\u5fc3\u4e86\u3002\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u4e00\u9635\u5fc3\u9178\uff0c\u53c8\u60f3\uff1a\u201c\u5979\u4e0a\u6b21\u8d1f\u6c14\u51fa\u8d70\uff0c\u9669\u4e9b\u513f\u5931\u95ea\u5728\u6d0b\u5175\u624b\u91cc\uff0c\u8fd9\u65f6\u5019\u5175\u8352\u9a6c\u4e71\uff0c\u5374\u53c8\u4e0d\u77e5\u5230\u4e86\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4ed6\u5446\u5446\u5750\u5728\u5e8a\u4e0a\uff0c\u5927\u4e3a\u6cae\u4e27\u3002\u7126\u5b9b\u513f\u8f7b\u8f7b\u8d70\u8fdb\u623f\u6765\uff0c\u89c1\u4ed6\u72b9\u5982\u5931\u9b42\u843d\u9b44\u4e00\u822c\uff0c\u4e0d\u89c9\u5403\u60ca\u3002\u4f17\u4eba\u5f97\u77e5\u8baf\u606f\u540e\uff0c\u90fd\u6d8c\u8fdb\u623f\u6765\uff0c\u4e03\u5634\u516b\u820c\uff0c\u6709\u7684\u529d\u6170\uff0c\u6709\u7684\u51fa\u4e3b\u610f\u3002 \u3000\u3000\u7126\u5b9b\u513f\u5e74\u7eaa\u867d\u5c0f\uff0c\u5bf9\u4e8b\u60c5\u5374\u6700\u628a\u6301\u5f97\u5b9a\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4f60\u6025\u4e5f\u65e0\u7528\u3002\u590f\u59d1\u5a18\u4e00\u8eab\u6b66\u827a\uff0c\u6709\u8c01\u6562\u6b3a\u4fae\u5979\uff1f\u8fd9\u6837\u7f62\uff0c\u4f60\u4f1a\u671f\u5df2\u8fd1\uff0c\u8fd8\u662f\u548c\u54d1\u5df4\u53d4\u53d4\u3001\u4f55\u59ca\u59ca\u7b49\u4e00\u8d77\u4e0a\u534e\u5c71\u53bb\u3002 \u3000\u3000\u7a0b\u4f2f\u4f2f\u548c\u6211\u7559\u5728\u8fd9\u91cc\u770b\u62a4\u963f\u4e5d\u59b9\u5b50\u3002\u6c99\u53d4\u53d4\u3001\u94c1\u8001\u5e08\u3001\u80e1\u53d4\u53d4\u548c\u6211\u4eec\u91d1\u9f99\u5e2e\u7684\uff0c\u5927\u4f19\u513f\u51fa\u53bb\u627e\u590f\u59d1\u5a18\uff0c\u518d\u4f20\u51fa\u6c5f\u6e56\u4ee4\u724c\uff0c\u547d\u4e03\u7701\u8c6a\u6770\u5e2e\u540c\u5bfb\u8bbf\u3002\u627e\u5230\u4e4b\u540e\uff0c\u7acb\u5373\u966a\u5979\u4e0a\u534e\u5c71\u6765\u76f8\u4f1a\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u8fde\u8fde\u70b9\u5934\uff0c\u9053\uff1a\u201c\u7126\u59d1\u5a18\u7684\u4e3b\u610f\u5f88\u9ad8\uff0c\u5c31\u8fd9\u4e48\u529e\u3002 \u3000\u3000\u7a0b\u8001\u592b\u5b50\u548c\u7126\u59d1\u5a18\u6700\u597d\u966a\u540c\u516c\u4e3b\u51fa\u4eac\u8fdc\u907f\uff0c\u7559\u5728\u4eac\u4e2d\u53ef\u4e0d\u5927\u7a33\u4fbf\u3002\u60d5\u5b88\u8fd8\u6ca1\u6b63\u5f0f\u5165\u6211\u95e8\u4e2d\uff0c\u5f85\u6211\u7980\u660e\u5e08\u7236\u4e4b\u540e\u518d\u8bf4\u3002\u8fd9\u4e00\u6b21\u4e0d\u5fc5\u540c\u4e0a\u534e\u5c71\u4e86\u3002\u201d\u4f55\u60d5\u5b88\u773c\u775b\u4e00\u6e9c\uff0c\u6b63\u60f3\u6c42\u6073\uff0c\u5ffd\u60f3\u9752\u9752\u4e5f\u66fe\u6709\u7591\u5df1\u4e4b\u610f\uff0c\u548c\u8881\u627f\u5fd7\u540c\u884c\u53ea\u6015\u4e0d\u751a\u59a5\u5f53\uff0c\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u5c31\u4e0d\u8a00\u8bed\u4e86\uff0c\u5bfb\u601d\uff1a\u201c\u4f60\u4e0d\u8ba9\u6211\u53bb\u534e\u5c71\uff0c\u6211\u504f\u504f\u81ea\u5df1\u6765\u3002\u201d\u5979\u505a\u60ef\u4e86\u90aa\u6559\u6559\u4e3b\uff0c\u8fd1\u6765\u867d\u5df2\u5927\u4e3a\u6536\u655b\uff0c\u6bd5\u7adf\u91ce\u6027\u672a\u9664\uff0c\u4e5f\u4e0d\u7406\u8881\u627f\u5fd7\u7684\u5429\u5490\uff0c\u53ea\u7ba1\u7b79\u5212\u81ea\u884c\u4e0a\u534e\u5c71\u62dc\u89c1\u7956\u5e08\u7684\u4e8b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5b89\u6392\u5df2\u6bd5\uff0c\u6b21\u65e5\u5411\u95ef\u738b\u4e0e\u4e49\u5144\u674e\u5ca9\u8f9e\u522b\u3002\u674e\u81ea\u6210\u773c\u89c1\u7559\u4ed6\u4e0d\u4f4f\uff0c\u4fbf\u8d4f\u8d50\u4e86\u8bb8\u591a\u5927\u5185\u73cd\u5b9d\u3002\u8881\u627f\u5fd7\u8981\u5f85\u63a8\u8f9e\uff0c\u674e\u5ca9\u8fde\u4f7f\u773c\u8272\uff0c\u8881\u627f\u5fd7\u53ea\u5f97\u8c22\u8fc7\u53d7\u4e86\u3002 \u3000\u3000\u674e\u5ca9\u9001\u51fa\u5bab\u95e8\uff0c\u53f9\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u529f\u6210\u8eab\u9000\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u2026\u2026\u201d\u8bf4\u7740\u795e\u8272\u9eef\u7136\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5927\u54e5\u4f60\u591a\u591a\u4fdd\u91cd\u3002\u5982\u6709\u5371\u96be\uff0c\u5c0f\u5f1f\u867d\u5728\u4e07\u91cc\u4e4b\u5916\uff0c\u4e00\u5f97\u8baf\u606f\uff0c\u4e5f\u5fc5\u661f\u591c\u8d76\u6765\u3002\u201d\u4e24\u4eba\u6d12\u6cea\u800c\u522b\u3002 \u3000\u3000\u5f53\u65e5\u4e0b\u5348\uff0c\u8881\u627f\u5fd7\u4e0e\u54d1\u5df4\u3001\u5d14\u79cb\u5c71\u3001\u5d14\u5e0c\u654f\u3001\u5b89\u5927\u5a18\u3001\u5b89\u5c0f\u6167\u3001\u6d2a\u80dc\u6d77\u516d\u4eba\u53d6\u9053\u5411\u897f\uff0c\u5f80\u534e\u5c71\u8fdb\u53d1\u3002\u5404\u4eba\u4e58\u5750\u7684\u90fd\u662f\u9a8f\u9a6c\uff0c\u811a\u7a0b\u6781\u5feb\uff0c\u4e0d\u591a\u65f6\u5df2\u5230\u4e86\u5b9b\u5e73\u3002 \u3000\u3000\u4f17\u4eba\u8fdb\u996d\u5e97\u6253\u5c16\uff0c\u7528\u5b8c\u996d\u6b63\u8981\u4e0a\u9a6c\uff0c\u6d2a\u80dc\u6d77\u77a5\u773c\u95f4\u5ffd\u89c1\u5899\u89d2\u91cc\u6709\u4e00\u53ea\u874e\u5b50\u3001\u4e00\u6761\u8708\u86a3\uff0c\u90fd\u7528\u94c1\u9489\u9489\u5728\u5899\u811a\u3002\u4ed6\u5fae\u89c9\u5947\u602a\uff0c\u8f7b\u626f\u8881\u627f\u5fd7\u7684\u8863\u670d\u3002\u8881\u627f\u5fd7\u51dd\u773c\u4e00\u770b\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u60f3\u8fd9\u5fc5\u4e0e\u4e94\u6bd2\u6559\u6709\u5173\uff0c\u53ef\u60dc\u4f55\u60d5\u5b88\u6ca1\u540c\u6765\uff0c\u4e0d\u77e5\u8fd9\u4e24\u4e2a\u8bb0\u53f7\u662f\u751a\u4e48\u610f\u601d\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u501f\u6545\u4e0e\u5e97\u5c0f\u4e8c\u6500\u8c08\u4e86\u51e0\u53e5\uff0c\u6de1\u6de1\u7684\u9053\uff1a\u201c\u90a3\u5899\u811a\u4e0b\u7684\u4e24\u4ef6\u6bd2\u7269\uff0c\u5012\u6709\u4e9b\u53e4\u602a\u3002\u201d\u5e97\u5c0f\u4e8c\u7b11\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\u6536\u4e86\u94f6\u5b50\uff0c\u771f\u8981\u628a\u8fd9\u4e24\u6837\u9b3c\u4e1c\u897f\u4e22\u4e86\u3002\u70e6\u6b7b\u4eba\uff01\u201d\u4ed6\u4e00\u9762\u8bf4\u4e00\u9762\u6273\u624b\u6307\uff0c\u7b11\u9053\uff1a\u201c\u4e24\u5929\u4e0d\u5230\uff0c\u95ee\u8d77\u8fd9\u52b3\u751a\u5b50\u7684\uff0c\u8fde\u4f60\u8fbe\u5b98\u7237\u4e0d\u77e5\u662f\u7b2c\u5341\u51e0\u4f4d\u4e86\u3002\u201d\u6d2a\u80dc\u6d77\u5fd9\u95ee\uff1a\u201c\u662f\u8c01\u9489\u7684\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4fbf\u662f\u90a3\u4e2a\u8001\u4e5e\u5a46\u554a\uff01\u201d\u6d2a\u80dc\u6d77\u5411\u8881\u627f\u5fd7\u671b\u4e86\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u662f\u54ea\u4e9b\u4eba\u95ee\u8fc7\u5462\uff1f\u201d\u8bf4\u7740\u62ff\u4e86\u5757\u788e\u94f6\u5b50\u585e\u5728\u5e97\u5c0f\u4e8c\u624b\u91cc\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u53e3\u4e2d\u63a8\u8f9e\uff0c\u4f38\u624b\u63a5\u4e86\u94f6\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u53eb\u5316\u4e10\u5934\uff0c\u5c31\u662f\u5149\u68cd\u6df7\u6df7\u513f\uff0c\u54ea\u77e5\u9053\u4f60\u8fbe\u5b98\u7237\u4e5f\u95ee\u8fd9\u4e2a\u2026\u2026\u563f\u563f\uff0c\u53ef\u53eb\u4f60\u8001\u4eba\u5bb6\u7834\u8d39\u5566\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u63d2\u53e3\u9053\uff1a\u201c\u90a3\u8001\u4e5e\u5a46\u9489\u6bd2\u7269\u4e4b\u65f6\uff0c\u8fd8\u6709\u8c01\u5728\u4e00\u65c1\u5417\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u90a3\u5929\u7684\u4e8b\u4e5f\u771f\u900f\u7740\u5e0c\u5947\uff0c\u5148\u662f\u4e00\u4e2a\u9752\u5e74\u6807\u81f4\u76f8\u516c\u72ec\u4e2a\u513f\u6765\u559d\u9152\u2026\u2026\u201d\u8881\u627f\u5fd7\u6025\u95ee\uff1a\u201c\u591a\u5927\u5e74\u7eaa\uff1f\u600e\u7b49\u6253\u626e\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u77a7\u6a21\u6837\u513f\u6bd4\u4f60\u76f8\u516c\u8fd8\u5c0f\u7740\u51e0\u5c81\uff0c\u751f\u5f97\u8fd9\u4e48\u4fca\uff0c\u6211\u8fd8\u9053\u662f\u5531\u5c0f\u65e6\u7684\u620f\u5b50\u513f\u5462\uff0c\u540e\u6765\u89c1\u4ed6\u8170\u91cc\u5e26\u7740\u628a\u5b9d\u5251\uff0c\u90a3\u53ef\u5c31\u4e0d\u77e5\u662f\u751a\u4e48\u8def\u6570\u4e86\u3002\u4ed6\u597d\u4f3c\u5bb6\u91cc\u6b7b\u4e86\u4eba\u4f3c\u7684\uff0c\u6101\u7709\u82e6\u8138\uff0c\u559d\u559d\u9152\uff0c\u773c\u5708\u513f\u5c31\u7ea2\u4e86\uff0c\u771f\u53eb\u4eba\u77a7\u7740\u5fc3\u91cc\u76f4\u75bc\u2026\u2026\u201d\u4f17\u4eba\u77e5\u9053\u8fd9\u5fc5\u662f\u9752\u9752\u65e0\u7591\u3002\u5d14\u5e0c\u654f\u6012\u9053\uff1a\u201c\u4f60\u522b\u53e3\u91cc\u4e0d\u5e72\u4e0d\u51c0\u7684\u3002\u201d\u5e97\u5c0f\u4e8c\u5413\u4e86\u4e00\u8df3\uff0c\u62b9\u4e86\u62b9\u684c\u5b50\uff0c\u9053\uff1a\u201c\u7237\u4eec\u8981\u4e0a\u9053\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u540e\u6765\u600e\u6837\uff1f\u201d\u5e97\u5c0f\u4e8c\u671b\u4e86\u5d14\u5e0c\u654f\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u9752\u5e74\u76f8\u516c\u559d\u4e86\u4e00\u4f1a\u9152\uff0c\u5ffd\u7136\u697c\u68af\u4e0a\u811a\u6b65\u54cd\uff0c\u4e0a\u6765\u4e86\u4e00\u4f4d\u8001\u7237\u5b50\uff0c\u522b\u77a7\u4ed6\u5934\u53d1\u80e1\u5b50\u767d\u5f97\u94f6\u5b50\u4e00\u822c\uff0c\u53ef\u771f\u900f\u7740\u7cbe\u795e\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u6839\u9f99\u5934\u62d0\u6756\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u5f80\u5730\u4e0b\u4e00\u767b\uff0c\u684c\u4e0a\u7684\u7897\u513f\u76cf\u513f\u4fbf\u90fd\u8df3\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u5927\u6025\uff1a\u201c\u6e29\u65b9\u5c71\u90a3\u8001\u513f\u548c\u5979\u9047\u4e0a\u4e86\uff0c\u9752\u5f1f\u600e\u80fd\u9003\u51fa\u4ed6\u7684\u6bd2\u624b\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u53c8\u9053\uff1a\u201c\u90a3\u8001\u7237\u5b50\u5750\u4e86\u4e0b\u6765\uff0c\u8981\u4e86\u9152\u83dc\u3002\u4ed6\u521a\u5750\u5b9a\uff0c\u53c8\u4e0a\u6765\u4e00\u4f4d\u8001\u7237\u5b50\u3002\u90a3\u771f\u53eb\u53e4\u602a\uff0c\u524d\u524d\u540e\u540e\u4e00\u5171\u6765\u4e86\u56db\u4e2a\uff0c\u90fd\u662f\u767d\u5934\u53d1\u3001\u767d\u80e1\u5b50\u3001\u7ea2\u8138\u5b54\uff0c\u5012\u50cf\u662f\u4e00\u4e2a\u6a21\u5b50\u91cc\u6d47\u51fa\u6765\u7684\u4e00\u822c\uff0c\u8981\u627e\u8fd9\u56db\u4e2a\u4e00\u6a21\u4e00\u6837\u7684\u8001\u7237\u5b50\uff0c\u90a3\u771f\u662f\u4e0d\u5bb9\u6613\u5f97\u7d27\u4e86\u3002\u8fd9\u56db\u4eba\u6709\u7684\u62ff\u7740\u4e00\u5bf9\u77ed\u621f\uff0c\u6709\u7684\u62ff\u7740\u4e00\u6839\u76ae\u97ad\u3002\u4ed6\u4eec\u8c01\u4e5f\u4e0d\u671b\u8c01\uff0c\u5404\u81ea\u5f00\u4e86\u4e00\u5f20\u684c\u5b50\uff0c\u56db\u4e2a\u8001\u513f\u628a\u90a3\u4f4d\u5e74\u8f7b\u76f8\u516c\u56f4\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u6211\u8d8a\u77a7\u8d8a\u900f\u7740\u90aa\u95e8\uff0c\u518d\u8fc7\u4e00\u4f1a\u513f\uff0c\u90a3\u8001\u4e5e\u5a46\u5c31\u6765\u5566\u3002\u638c\u67dc\u7684\u8981\u8d76\u5979\u51fa\u53bb\uff0c\u54ea\u77e5\u5f53\u5730\u4e00\u58f0\uff0c\u563f\uff0c\u4f60\u9053\u751a\u4e48\uff1f\u201d\u5d14\u5e0c\u654f\u5fd9\u95ee\uff1a\u201c\u751a\u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u8fd9\u53eb\u505a\u8d22\u795e\u7237\u7237\u7740\u70c2\u886b\uff0c\u4eba\u4e0d\u53ef\u4ee5\u8c8c\u76f8\u3002 \u3000\u3000\u5f53\u7684\u4e00\u58f0\uff0c\u5979\u629b\u4e86\u4e00\u5927\u952d\u94f6\u5b50\u5728\u67dc\u4e0a\uff0c\u5411\u7740\u90a3\u56db\u4e2a\u8001\u5934\u548c\u90a3\u76f8\u516c\u4e00\u6307\uff0c\u53eb\u9053\uff1a\u2018\u8fd9\u51e0\u4f4d\u5403\u7684\uff0c\u90fd\u7b97\u5728\u6211\u5e10\u4e0a\uff01\u2019\u4f60\u8001\uff0c\u4f60\u53ef\u89c1\u8fc7\u8fd9\u6837\u9614\u7ef0\u7684\u53eb\u5316\u5a46\u4e48\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u8d8a\u542c\u8d8a\u6025\uff0c\u5fc3\u60f3\uff1a\u201c\u6e29\u6c0f\u56db\u8001\u5df2\u7ecf\u96be\u654c\uff0c\u518d\u9047\u4e0a\u4f55\u7ea2\u836f\uff0c\u53ef\u5982\u4f55\u5f97\u4e86\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u8d8a\u8bf4\u5174\u81f4\u8d8a\u597d\uff0c\u53e3\u6cab\u6a2a\u98de\u7684\u9053\uff1a\u201c\u54ea\u77e5\u4ed6\u4eec\u7406\u4e5f\u4e0d\u7406\uff0c\u81ea\u987e\u81ea\u7684\u996e\u9152\u3002\u90a3\u8001\u4e5e\u5a46\u607c\u4e86\uff0c\u53eb\u4e86\u4e00\u58f0\uff0c\u4e00\u5f20\u624b\uff0c\u4e00\u9053\u767d\u5149\uff0c\u76f4\u5f80\u90a3\u62ff\u62d0\u6756\u7684\u8001\u513f\u5c04\u53bb\u3002\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u4f60\u522b\u778e\u626f\u5566\uff0c\u96be\u9053\u5979\u8fd8\u771f\u4f1a\u653e\u98de\u5251\u4e0d\u6210\uff1f\u201d\u5e97\u5c0f\u4e8c\u6025\u9053\uff1a\u201c\u6211\u5e72\u4e48\u778e\u626f\uff1f \u3000\u3000\u867d\u7136\u4e0d\u662f\u98de\u5251\uff0c\u53ef\u4e5f\u662f\u51e0\u6210\u513f\u4e0d\u79bb\u3002\u53ea\u89c1\u90a3\u8001\u513f\u4f38\u51fa\u7b77\u5b50\uff0c\u53ee\u53ee\u5f53\u5f53\u4e00\u9635\u54cd\uff0c\u7b77\u5b50\u4e0a\u5957\u4e86\u660e\u6643\u6643\u7684\u4e00\u4e32\u3002\u6211\u5077\u5077\u8e69\u8fc7\u53bb\u4e00\u5f20\uff0c\u563f\uff0c\u4f60\u9053\u662f\u751a\u4e48\uff1f\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u539f\u6765\u662f\u4e00\u4e32\u6307\u7532\u5957\u5b50\uff0c\u90fd\u6559\u90a3\u8001\u513f\u7528\u7b77\u5b50\u5957\u4f4f\u5566\u3002\u6211\u521a\u559d\u5f97\u4e00\u58f0\u5f69\uff0c\u53ea\u542c\u5f97\u6ce2\u7684\u4e00\u58f0\uff0c\u4f60\u9053\u662f\u751a\u4e48\uff1f\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u62c9\u7740\u4ed6\u8d70\u5230\u4e00\u5f20\u684c\u5b50\u65c1\uff0c\u9053\uff1a\u201c\u4f60\u77a7\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u684c\u5b50\u6709\u4e2a\u5c0f\u5b54\uff0c\u5e97\u5c0f\u4e8c\u62ff\u8d77\u4e00\u6839\u7b77\u5b50\u63d2\u5165\u5c0f\u5b54\uff0c\u521a\u521a\u5408\u5f0f\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u8001\u513f\u63d0\u8d77\u7b77\u5b50\uff0c\u5c31\u63d2\u8fdb\u4e86\u684c\u9762\u3002\u8fd9\u624b\u529f\u592b\u53ef\u4e0d\u542b\u7cca\u5427\uff1f\u6211\u662f\u4e0d\u4f1a\uff0c\u53ef\u4e0d\u77e5\u4f60\u8001\u4eba\u5bb6\u4f1a\u4e0d\u4f1a\u3002\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u3002\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u539f\u6765\u4f60\u8001\u4eba\u5bb6\u4e5f\u4e0d\u4f1a\uff0c\u90a3\u4e5f\u4e0d\u8981\u7d27\u3002 \u3000\u3000\u8001\u4e5e\u5a46\u77e5\u9053\u654c\u4ed6\u4e0d\u8fc7\uff0c\u4e00\u58f0\u4e0d\u542d\uff0c\u602a\u773c\u4e00\u7ffb\uff0c\u5c31\u5954\u4e86\u51fa\u53bb\u3002\u540e\u6765\u90a3\u9752\u5e74\u76f8\u516c\u8ddf\u7740\u56db\u4e2a\u8001\u5934\u5b50\u4e00\u8d77\u8d70\u4e86\u3002\u539f\u6765\u4ed6\u4eec\u662f\u4e00\u8def\uff0c\u6446\u597d\u4e86\u9635\u52bf\u5bf9\u4ed8\u90a3\u53eb\u5316\u5a46\u7684\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u5411\u54ea\u91cc\u53bb\u7684\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5411\u897f\u5357\uff0c\u53bb\u826f\u4e61\u3002\u4e94\u4e2a\u4eba\u8d70\u4e86\u4e0d\u591a\u4f1a\u513f\uff0c\u53eb\u5316\u5a46\u53c8\u56de\u8f6c\u6765\uff0c\u5728\u5899\u8fb9\u9489\u4e86\u8fd9\u4e24\u4ef6\u602a\u4e1c\u897f\uff0c\u7ed9\u4e86\u6211\u4e00\u5757\u94f6\u5b50\uff0c\u53eb\u6211\u597d\u597d\u4f8d\u5019\u8fd9\u4e24\u53ea\u6bd2\u866b\uff0c\u522b\u8ba9\u4eba\u52a8\u4e86\u3002\u8fd9\u51e0\u65e5\u56db\u4e0b\u5927\u4e71\uff0c\u6211\u4eec\u638c\u67dc\u7684\u8bf4\u8981\u6536\u94fa\u51e0\u65e5\uff0c\u522b\u505a\u751f\u610f\u3002\u8001\u677f\u5a18\u4e00\u5b9a\u4e0d\u80af\uff0c\u8fd9\u624d\u5f00\u5e02\uff0c\u5012\u8ba9\u6211\u8d5a\u4e86\u4e00\u7b14\u5916\u5feb\u2026\u2026\u201d\u4ed6\u8fd8\u5728\u5520\u5520\u53e8\u53e8\u5730\u8bf4\u4e0b\u53bb\uff0c\u8881\u627f\u5fd7\u5df2\u62a2\u51fa\u95e8\u53bb\uff0c\u8dc3\u4e0a\u9a6c\u80cc\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u8ffd\uff01\u201d \u3000\u3000\u9752\u9752\u81ea\u89c1\u8881\u627f\u5fd7\u628a\u963f\u4e5d\u62b1\u56de\u5bb6\u91cc\uff0c\u8d8a\u60f3\u8d8a\u662f\u4e0d\u5bf9\uff0c\u963f\u4e5d\u5bb9\u8c8c\u7f8e\u4e3d\uff0c\u5df1\u6240\u4e0d\u53ca\uff0c\u4f55\u51b5\u5979\u662f\u516c\u4e3b\uff0c\u81ea\u5df1\u5374\u662f\u4e2a\u6765\u5386\u4e0d\u660e\u7684\u79c1\u751f\u5973\uff0c\u8ddf\u5979\u5929\u5dee\u5730\u8fdc\uff0c\u8881\u627f\u5fd7\u81ea\u662f\u975e\u79fb\u60c5\u522b\u7231\u4e0d\u53ef\u3002\u82e5\u4e0d\u662f\u7231\u4e0a\u4e86\u5979\uff0c\u600e\u4f1a\u7d27\u7d27\u5730\u62b1\u4f4f\u4e86\u5979\uff0c\u56de\u5230\u4e86\u5bb6\u91cc\uff0c\u5728\u4f17\u4eba\u4e4b\u524d\u5140\u81ea\u820d\u4e0d\u5f97\u653e\u624b\uff1f\u540e\u6765\u53c8\u542c\u4eba\u8bf4\u9053\uff0c\u674e\u81ea\u6210\u5c06\u963f\u4e5d\u8d50\u4e86\u7ed9\u8881\u627f\u5fd7\uff0c\u6743\u5c06\u519b\u5218\u5b97\u654f\u559d\u918b\uff0c\u4e24\u4e2a\u4eba\u9669\u4e9b\u513f\u4fbf\u5728\u91d1\u6bbf\u4e0a\u4e89\u98ce\u6253\u67b6\uff0c\u8bf4\u5230\u52a8\u6b66\u6253\u67b6\uff0c\u53c8\u6709\u8c01\u6253\u5f97\u8fc7\u4ed6\uff1f\u81ea\u7136\u662f\u4ed6\u4e89\u8d62\u4e86\u3002 \u3000\u3000\u5d07\u796f\u662f\u4ed6\u7684\u6740\u7236\u5927\u4ec7\uff0c\u4ed6\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u8981\u62a5\u4ec7\uff0c\u53ef\u662f\u963f\u4e5d\u53ea\u8bf4\u5f97\u4e00\u53e5\u8981\u4ed6\u522b\u6740\u5979\u7239\u7239\uff0c\u4ed6\u7acb\u523b\u5c31\u4e56\u4e56\u7684\u542c\u8bdd\u3002\u201c\u6211\u7684\u8a00\u8bed\uff0c\u4ed6\u51e0\u65f6\u8fd9\u4e48\u542c\u4ece\u4e86\uff1f\u53ea\u6709\u4ed6\u6765\u9a82\u6211\uff0c\u90a3\u624d\u662f\u5e38\u4e8b\u3002\u201d\u601d\u524d\u60f3\u540e\uff0c\u7ec8\u4e8e\u786c\u8d77\u5fc3\u80a0\u79bb\u4eac\uff0c\u5fc3\u91cc\u4f24\u75db\u5f02\u5e38\uff0c\u51b3\u610f\u628a\u6bcd\u4eb2\u9aa8\u7070\u5e26\u5230\u534e\u5c71\u4e4b\u5dc5\u4e0e\u7236\u4eb2\u9ab8\u9aa8\u5408\u846c\uff0c\u7136\u540e\u5728\u7236\u6bcd\u5c38\u9aa8\u4e4b\u65c1\u56fe\u4e2a\u81ea\u5c3d\uff0c\u60f3\u5230\u5b51\u7136\u4e00\u8eab\uff0c\u4e2a\u90ce\u8584\u5e78\uff0c\u843d\u5f97\u5982\u6b64\u4e0b\u573a\uff0c\u4e0d\u7981\u81ea\u4f24\u81ea\u601c\u3002 \u3000\u3000\u8fd9\u65e5\u5728\u5b9b\u5e73\u6253\u5c16\uff0c\u7adf\u4e0d\u610f\u4e0e\u6e29\u6c0f\u56db\u8001\u53ca\u4f55\u7ea2\u836f\u76f8\u9047\u3002\u6e29\u65b9\u5c71\u9732\u4e86\u4e00\u624b\u5185\u529f\uff0c\u4f55\u7ea2\u836f\u81ea\u77e5\u4e0d\u654c\uff0c\u5f84\u81ea\u9000\u5f00\u3002\u9752\u9752\u5df2\u62b1\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u5012\u4e5f\u5e76\u4e0d\u60ca\u60e7\uff0c\u6015\u7684\u662f\u56db\u8001\u5f53\u573a\u628a\u5979\u5904\u6b7b\uff0c\u90a3\u4e48\u6bcd\u4eb2\u7684\u9057\u5fd7\u5c31\u4e0d\u80fd\u5949\u884c\u4e86\uff0c\u5f53\u4e0b\u5ff5\u5934\u4e00\u8f6c\uff0c\u8ba1\u8c0b\u5df2\u751f\uff0c\u8d70\u5230\u6e29\u65b9\u8fbe\u8ddf\u524d\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u53eb\u58f0\uff1a\u201c\u5927\u7237\u7237\uff01\u201d\u7136\u540e\u9010\u4e00\u5411\u5176\u4f59\u4e09\u8001\u89c1\u793c\u3002 \u3000\u3000\u6e29\u6c0f\u56db\u8001\u89c1\u5979\u5766\u7136\u4e0d\u60e7\uff0c\u5012\u4e5f\u9887\u51fa\u610f\u5916\u3002 \u3000\u3000\u9752\u9752\u7b11\u95ee\uff1a\u201c\u56db\u4f4d\u7237\u7237\u53bb\u54ea\u91cc\uff1f\u201d\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u4f60\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u6211\u8ddf\u90a3\u59d3\u8881\u7684\u670b\u53cb\u7ea6\u597d\u4e86\uff0c\u5728\u8fd9\u91cc\u4f1a\u9762\uff0c\u54ea\u77e5\u4ed6\u5230\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6765\u3002\u201d \u3000\u3000\u56db\u8001\u542c\u5f97\u8881\u627f\u5fd7\u8981\u6765\uff0c\u4eba\u4eba\u90fd\u662f\u5fc3\u5934\u5927\u9707\uff0c\u54ea\u6562\u518d\u6709\u7247\u523b\u505c\u7559\uff1f\u6e29\u65b9\u4e49\u559d\u9053\uff1a\u201c\u8ddf\u6211\u4eec\u53bb\u3002\u201d\u9752\u9752\u5047\u610f\u9053\uff1a\u201c\u6211\u8981\u7b49\u4eba\u5462\u3002\u201d\u6e29\u65b9\u4e49\u624b\u4e00\u4f38\uff0c\u5df2\u9694\u8863\u53e9\u4f4f\u5979\u624b\u8155\uff0c\u62c9\u51fa\u5e97\u95e8\uff0c\u4e24\u4eba\u5171\u4e58\u4e00\u9a91\u3002\u56db\u8001\u5c3d\u5f80\u8352\u50fb\u65e0\u4eba\u4e4b\u5904\u9a70\u53bb\uff0c\u773c\u89c1\u79bb\u57ce\u5df2\u8fdc\uff0c\u8fd9\u624d\u8df3\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u6e29\u65b9\u4e49\u628a\u9752\u9752\u4e00\u6454\uff0c\u63a8\u5728\u5730\u4e0b\uff0c\u9a82\u9053\uff1a\u201c\u65e0\u803b\u5c0f\u8d31\u4eba\uff0c\u4eca\u65e5\u6559\u4f60\u649e\u5728\u6211\u4eec\u624b\u91cc\u3002\u201d \u3000\u3000\u9752\u9752\u54ed\u9053\uff1a\u201c\u56db\u4f4d\u7237\u7237\uff0c\u6211\u505a\u9519\u4e86\u751a\u4e48\uff1f\u4f60\u4eec\u9976\u4e86\u6211\uff0c\u6211\u4ee5\u540e\u90fd\u542c\u4f60\u4eec\u7684\u8bdd\u3002\u201d\u6e29\u65b9\u4e49\u9a82\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u6d3b\u547d\uff1f\u201d\u64e6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u4e00\u67c4\u5315\u9996\u3002\u9752\u9752\u54ed\u9053\uff1a\u201c\u4e8c\u7237\u7237\uff0c\u4f60\u8981\u6740\u6211\u4e48\uff1f\u201d\u6e29\u65b9\u609f\u9053\uff1a\u201c\u4f60\u8fd9\u53eb\u662f\u8be5\u6b7b\uff01\u201d\u9752\u9752\u9053\uff1a\u201c\u4e09\u7237\u7237\uff0c\u6211\u5988\u662f\u4f60\u4eb2\u751f\u5973\u513f\uff0c\u6211\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d\u6e29\u65b9\u5c71\u94c1\u9752\u7740\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u6d3b\u547d\u90a3\u662f\u4f11\u60f3\uff01\u201d \u3000\u3000\u9752\u9752\u54ed\u9053\uff1a\u201c\u6211\u6b7b\u4e4b\u540e\uff0c\u6c42\u4f60\u9001\u4e2a\u4fe1\u7ed9\u6211\u90a3\u59d3\u8881\u7684\u670b\u53cb\uff0c\u53eb\u4ed6\u72ec\u4e2a\u513f\u53bb\u627e\u5b9d\u8d1d\u5427\uff0c\u522b\u7b49\u6211\u4e86\u3002\u201d \u3000\u3000\u56db\u8001\u4e00\u542c\u5230\u201c\u627e\u5b9d\u8d1d\u201d\u4e09\u5b57\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u9f50\u58f0\u95ee\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u9752\u9752\u54ed\u9053\uff1a\u201c\u6211\u53cd\u6b63\u662f\u6b7b\uff0c\u8fd9\u79d8\u5bc6\u662f\u4e0d\u80fd\u8bf4\u7684\u3002\u6211\u53ea\u6c42\u4f60\u4eec\u9001\u8fd9\u5c01\u4fe1\u53bb\u3002\u201d\u8bf4\u7740\u4ece\u886b\u4e0a\u6495\u4e0b\u4e00\u5757\u8863\u89d2\uff0c\u53c8\u4ece\u6000\u91cc\u9488\u7ebf\u5305\u5185\u53d6\u51fa\u4e00\u6839\u9488\u6765\uff0c\u523a\u7834\u624b\u6307\uff0c\u70b9\u4e86\u9c9c\u8840\uff0c\u5728\u8863\u89d2\u4e0a\u5199\u8d77\u6765\u3002 \u3000\u3000\u56db\u8001\u4e0d\u4f4f\u95ee\u5979\u627e\u751a\u4e48\u5b9d\u8d1d\uff0c\u5979\u53ea\u662f\u4e0d\u7406\uff0c\u5199\u597d\u4e4b\u540e\uff0c\u4ea4\u7ed9\u6e29\u65b9\u5c71\u9053\uff1a\u201c\u4e09\u7237\u7237\uff0c\u4f60\u4e5f\u4e0d\u7528\u89c1\u4ed6\uff0c\u6258\u4eba\u634e\u53bb\u5b9b\u5e73\u57ce\u91cc\u521a\u624d\u54b1\u4eec\u76f8\u4f1a\u7684\u90a3\u5904\u9152\u697c\uff0c\u8fd9\u5c31\u5f97\u5566\uff01\u201d\u5979\u867d\u662f\u505a\u4f5c\uff0c\u4f46\u60f3\u8d77\u8881\u627f\u5fd7\u65e0\u826f\u5fc3\uff0c\u53c8\u4e0d\u7981\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u56db\u8001\u89c1\u4e86\u5979\u4f24\u5fc3\u6b32\u7edd\u7684\u795e\u60c5\uff0c\u786e\u975e\u4f5c\u4f2a\uff0c\u4e00\u9f50\u56f4\u89c2\uff0c\u53ea\u89c1\u8863\u89d2\u4e0a\u5199\u9053\uff1a\u201c\u4eca\u751f\u4e0d\u80fd\u518d\u89c1\uff0c\u6211\u7236\u91cd\u5b9d\uff0c\u5747\u8d60\u4e88\u4f60\uff0c\u8bf7\u81ea\u5f80\u6316\u53d6\uff0c\u4e0d\u5fc5\u7b49\u6211\u3002\u9752\u59b9\u6ce3\u767d\u3002\u201d \u3000\u3000\u6e29\u65b9\u4e49\u559d\u9053\uff1a\u201c\u751a\u4e48\u5b9d\u8d1d\uff1f\u96be\u9053\u4f60\u771f\u77e5\u9053\u85cf\u5b9d\u7684\u6240\u5728\uff1f\u201d\u9752\u9752\u54ed\u9053\uff1a\u201c\u6211\u751a\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u53cd\u6b63\u6211\u8bf4\u4e5f\u662f\u6b7b\uff0c\u4e0d\u8bf4\u4e5f\u662f\u6b7b\u3002\u201d \u3000\u3000\u6e29\u65b9\u609f\u9053\uff1a\u201c\u5478\uff0c\u538b\u6839\u513f\u5c31\u6ca1\u751a\u4e48\u5b9d\u8d1d\u3002\u4f60\u90a3\u6b7b\u9b3c\u7236\u4eb2\u9a97\u4e86\u6211\u4eec\u4e00\u573a\uff0c\u73b0\u5728\u4f60\u53c8\u60f3\u6765\u641e\u9b3c\u3002\u201d \u3000\u3000\u9752\u9752\u5782\u5934\u4e0d\u8bed\uff0c\u6697\u6697\u4f38\u624b\u5165\u6000\uff0c\u89e3\u5f00\u4e86\u4e00\u5bf9\u7389\u8776\u7684\u4e1d\u7ee6\u3002 \u3000\u3000\u8fd9\u672c\u662f\u94c1\u7bb1\u4e2d\u4e4b\u7269\uff0c\u5f53\u552e\u5b9d\u53d8\u94b1\u4e4b\u65f6\uff0c\u5979\u89c1\u8fd9\u5bf9\u7389\u8776\u7cbe\u81f4\u7075\u52a8\uff0c\u5c31\u53d6\u6765\u7cfb\u5728\u8eab\u4e0a\uff0c\u90a3\u662f\u7eaa\u5ff5\u5979\u4e0e\u8881\u627f\u5fd7\u5171\u540c\u5f97\u5b9d\u4e4b\u610f\uff0c\u5341\u7bb1\u73cd\u5b9d\u4e0d\u8ba1\u5176\u6570\uff0c\u4e5f\u4e0d\u5c11\u4e86\u8fd9\u5bf9\u5c0f\u5c0f\u7389\u8776\u3002\u5979\u7a81\u7136\u7ad9\u8d77\u8eab\u6765\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u4fe1\u9001\u4e0d\u9001\u4e5f\u7531\u4f60\u4eec\u4e86\uff0c\u8fd9\u5c31\u6740\u4e86\u6211\u5427\uff01\u201d\u53ea\u542c\u53ee\u53ee\u4e24\u58f0\u6e05\u8106\u4e4b\u97f3\uff0c\u4e00\u5bf9\u7389\u8776\u843d\u5728\u5730\u4e0b\u3002\u9752\u9752\u4fef\u8eab\u8981\u62fe\uff0c\u6e29\u65b9\u609f\u5df2\u62a2\u5148\u6361\u4e86\u8d77\u6765\u3002\u56db\u8001\u6570\u5341\u5e74\u4e3a\u76d7\uff0c\u5c82\u6709\u4e0d\u8bc6\u5b9d\u8d27\u4e4b\u7406\uff1f\u89c1\u7389\u8776\u5982\u6b64\u73cd\u8d35\uff0c\u773c\u90fd\u7ea2\u4e86\u3002\u56db\u4eba\u5fc3\u4e2d\u7a81\u7a81\u4e71\u8df3\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u9752\u9752\u53ea\u662f\u4e0d\u8bed\u3002\u6e29\u65b9\u5c71\u9053\uff1a\u201c\u4f60\u597d\u597d\u8bf4\u51fa\u6765\uff0c\u6216\u8005\u5c31\u9976\u4e86\u4f60\u4e00\u6761\u5c0f\u547d\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u5c31\u662f\u90a3\u6279\u73cd\u5b9d\u91cc\u7684\u3002\u6211\u548c\u8881\u5927\u54e5\u7167\u7740\u7239\u7239\u7559\u4e0b\u6765\u7684\u90a3\u5f20\u5730\u56fe\uff0c\u6316\u5230\u4e86\u5341\u53ea\u94c1\u7bb1\uff0c\u91cc\u9762\u90fd\u662f\u73cd\u5947\u5b9d\u7269\u3002\u4e1c\u897f\u5b9e\u5728\u592a\u591a\uff0c\u5e26\u4e0d\u4e86\uff0c\u6211\u53ea\u6361\u4e86\u8fd9\u5bf9\u7389\u8776\u6765\u73a9\u3002\u6211\u4eec\u8bf4\u597d\uff0c\u8fd9\u6b21\u8981\u53bb\u5168\u90fd\u6316\u4e86\u51fa\u6765\uff0c\u54ea\u77e5\u4f60\u4eec\u2026\u2026\u201d\u8bf4\u7740\u53c8\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u56db\u8001\u8d70\u5230\u4e00\u65c1\uff0c\u4f4e\u58f0\u5546\u8bae\u3002\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u770b\u6765\u5b9d\u85cf\u4e4b\u4e8b\u5012\u662f\u4e0d\u5047\u3002\u201d\u6e29\u65b9\u4e49\u9053\uff1a\u201c\u903c\u5979\u9886\u8def\u53bb\u53d6\u3002\u201d\u4e09\u8001\u90fd\u70b9\u4e86\u70b9\u5934\u3002\u6e29\u65b9\u5c71\u9053\uff1a\u201c\u5148\u9a97\u5979\u8bf4\u9976\u547d\u4e0d\u6740\uff0c\u7b49\u627e\u5230\u5b9d\u8d1d\uff0c\u518d\u6765\u597d\u597d\u6574\u6cbb\u8fd9\u5c0f\u8d31\u4eba\u3002\u201d\u6e29\u65b9\u609f\u9053\uff1a\u201c\u6211\u6709\u4e2a\u4e3b\u610f\uff1a\u54b1\u4eec\u6398\u51fa\u4e86\u73cd\u5b9d\uff0c\u5c31\u628a\u8fd9\u5c0f\u8d31\u4eba\u57cb\u5728\u5b9d\u7a9f\u4e4b\u4e2d\uff0c\u7b49\u90a3\u59d3\u8881\u7684\u5c0f\u755c\u751f\u6765\u6398\u5b9d\uff0c\u4e00\u6398\u6398\u5230\u8fd9\u4e2a\u6b7b\u5b9d\u8d1d\uff0c\u5c82\u4e0d\u662f\u597d\uff1f\u201d\u4e09\u8001\u540c\u58f0\u5927\u7b11\uff0c\u90fd\u8bf4\uff1a\u201c\u4e94\u5f1f\u8fd9\u4e3b\u610f\u6700\u9ad8\u3002\u201d \u3000\u3000\u56db\u4eba\u5546\u8bae\u5df2\u6bd5\uff0c\u5174\u9ad8\u91c7\u70c8\u7684\u56de\u6765\u5a01\u903c\u9752\u9752\u3002\u9752\u9752\u8d77\u5148\u5047\u610f\u4e0d\u80af\uff0c\u540e\u6765\u88c5\u4f5c\u5b9e\u5728\u53d7\u903c\u4e0d\u8fc7\uff0c\u53ea\u5f97\u8bf4\u51fa\u85cf\u5b9d\u4e4b\u5730\u662f\u5728\u534e\u5c71\u4e4b\u5dc5\u3002\u5979\u662f\u8981\u56db\u8001\u5e26\u5979\u53bb\u534e\u5c71\uff0c\u627e\u5230\u7236\u4eb2\u57cb\u9aa8\u7684\u6240\u5728\uff0c\u8d81\u4ed6\u4eec\u5728\u8352\u5c71\u4e2d\u4e71\u6316\u4e71\u6398\u4e4b\u65f6\uff0c\u81ea\u5df1\u4fbf\u53ef\u628a\u6bcd\u4eb2\u9aa8\u7070\u548c\u7236\u4eb2\u7684\u9ab8\u9aa8\u5408\u846c\u4e00\u8d77\uff0c\u7136\u540e\u6a2a\u5251\u81ea\u520e\u3002\u54ea\u77e5\u5979\u8fd9\u53e5\u8c0e\u8bdd\u4e00\u8bf4\uff0c\u56db\u8001\u5374\u66f4\u6df1\u4fe1\u4e0d\u7591\u3002\u5f53\u5e74\u6e29\u6c0f\u4e94\u8001\u64d2\u4f4f\u91d1\u86c7\u90ce\u541b\uff0c\u4ed6\u4e5f\u662f\u5c06\u4ed6\u4eec\u5e26\u4e0a\u534e\u5c71\u3002\u5b9d\u8d1d\u867d\u6ca1\u627e\u5230\uff0c\u91d1\u86c7\u90ce\u541b\u53c8\u7a81\u7136\u5931\u8e2a\uff0c\u4f46\u4ed6\u4eec\u8111\u6d77\u4e4b\u4e2d\uff0c\u5374\u5df2\u6df1\u5370\u4e86\u5b9d\u7269\u5fc5\u5728\u534e\u5c71\u7684\u5ff5\u5934\u3002\u5f53\u65e5\u5f20\u6625\u4e5d\u548c\u90a3\u79c3\u5934\u6240\u4ee5\u4e0a\u534e\u5c71\u6765\u641c\u7d22\uff0c\u4e5f\u662f\u56e0\u6b64\u3002 \u3000\u3000\u5f53\u4e0b\u56db\u8001\u5e26\u4e86\u9752\u9752\uff0c\u8fde\u65e5\u9a6c\u4e0d\u505c\u8e44\u7684\u8d76\u8def\uff0c\u53ea\u6015\u8881\u627f\u5fd7\u8ffd\u5230\uff0c\u90a3\u65f6\u975e\u4f46\u5b9d\u7269\u5f97\u4e0d\u5230\u624b\uff0c\u8fde\u56db\u6761\u8001\u547d\u4e5f\u8fd8\u96be\u4fdd\u3002 \u3000\u3000\u8fd9\u5929\u6765\u5230\u5c71\u897f\u754c\u5185\uff0c\u4e94\u4eba\u5954\u9a70\u4e86\u4e00\u65e5\uff0c\u5df2\u662f\u9887\u4e3a\u75b2\u7d2f\uff0c\u5728\u4e00\u5bb6\u5ba2\u5e97\u4e2d\u6b47\u4e86\u3002\u6e29\u65b9\u4e49\u4eba\u6700\u7c97\u58ee\uff0c\u98df\u91cf\u6700\u5927\uff0c\u4e00\u53e0\u58f0\u7684\u6025\u53eb\uff1a\u201c\u7092\u83dc\u3001\u7b5b\u9152\uff0c\u8d76\u9762\u6761\u513f\uff01\u201d\u7b49\u5e97\u4f19\u7aef\u4e86\u996d\u83dc\u4e0a\u6765\uff0c\u4ed6\u5c31\u548c\u5f80\u5e38\u4e00\u822c\uff0c\u62a2\u5148\u7a00\u91cc\u547c\u565c\u7684\u5403\u4e86\u8d77\u6765\u3002\u4e09\u8001\u548c\u9752\u9752\u6b63\u8981\u8ddf\u7740\u52a8\u7b77\uff0c\u6e29\u65b9\u4e49\u5ffd\u4ece\u9762\u6c64\u4e2d\u6311\u8d77\u4e00\u7269\uff0c\u60ca\u53eb\u4e00\u58f0\uff0c\u767b\u65f6\u76f4\u50f5\u50f5\u7684\u4e0d\u52a8\u4e86\u3002\u56db\u4eba\u5927\u60ca\uff0c\u770b\u4ed6\u6240\u6311\u8d77\u7684\uff0c\u8d6b\u7136\u662f\u4e00\u53ea\u6781\u5927\u7684\u9ed1\u8272\u8718\u86db\u3002\u6e29\u65b9\u8fbe\u4e00\u6478\u5144\u5f1f\u7684\u624b\uff0c\u5df2\u65e0\u8109\u640f\uff0c\u8138\u8272\u53d1\u9ed1\uff0c\u9f3b\u5b54\u91cc\u4e5f\u6ca1\u6c14\u4e86\u3002 \u3000\u3000\u6e29\u65b9\u609f\u60ca\u6012\u4ea4\u96c6\uff0c\u6293\u8d77\u5e97\u5c0f\u4e8c\u5f80\u5730\u4e0b\u731b\u529b\u4e00\u6454\uff0c\u5580\u5587\u4e24\u58f0\uff0c\u5e97\u5c0f\u4e8c\u817f\u9aa8\u7acb\u65ad\uff0c\u6655\u6b7b\u4e86\u8fc7\u53bb\u3002\u6e29\u65b9\u5c71\u62a2\u51fa\u53bb\uff0c\u4e00\u628a\u6293\u4f4f\u638c\u67dc\u7684\u80f8\u53e3\uff0c\u7528\u7b77\u5b50\u631f\u8d77\u8718\u86db\uff0c\u559d\u9053\uff1a\u201c\u597d\u5927\u7684\u80c6\u5b50\uff0c\u7adf\u6562\u8c0b\u8d22\u5bb3\u547d\uff0c\u8fd9\u662f\u751a\u4e48\uff1f\u201d\u90a3\u638c\u67dc\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u5c0f\u5e97\u2026\u2026\u5c0f\u5e97\u662f\u4e03\u5341\u591a\u5e74\u7684\u8001\u5e97\uff0c\u53a8\u623f\u53c8\u662f\u5e72\u51c0\u4e0d\u8fc7\uff0c\u600e\u2026\u2026\u600e\u4e48\u6709\u8fd9\u2026\u2026\u8fd9\u4e1c\u897f\u2026\u2026\u201d\u6e29\u65b9\u5c71\u5de6\u624b\u5728\u4ed6\u9762\u988a\u4e0a\u4e00\u634f\uff0c\u90a3\u638c\u67dc\u4e0b\u988f\u8dcc\u4e0b\uff0c\u518d\u4e5f\u5408\u4e0d\u62e2\u53e3\u3002\u6e29\u65b9\u5c71\u624b\u4e00\u4f38\uff0c\u628a\u8718\u86db\u585e\u5165\u4e86\u4ed6\u7684\u53e3\u91cc\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u90a3\u638c\u67dc\u4fbf\u5373\u6bd9\u547d\u3002\u8fd9\u65f6\u5e97\u4e2d\u5df2\u7ecf\u5927\u4e71\uff0c\u6e29\u65b9\u8fbe\u53f3\u624b\u62ff\u4f4f\u9752\u9752\u624b\u8155\uff0c\u9632\u5979\u9003\u8d70\uff0c\u5de6\u624b\u62b1\u8d77\u5144\u5f1f\u5c38\u8eab\u3002\u65b9\u5c71\u3001\u65b9\u609f\u4e24\u4eba\u4e52\u4e52\u4e53\u4e53\u4e00\u9635\u4e71\u6253\uff0c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\uff0c\u628a\u4f4f\u5ba2\u548c\u5e97\u4f19\u6253\u6b7b\u4e86\u4e03\u516b\u4e2a\uff0c\u968f\u5373\u5728\u5ba2\u5e97\u4e2d\u653e\u8d77\u706b\u6765\u3002\u65c1\u4eba\u89c1\u4ed6\u4eec\u901e\u51f6\uff0c\u54ea\u6562\u8fc7\u6765\uff1f \u3000\u3000\u4e09\u8001\u5c06\u6e29\u65b9\u4e49\u7684\u5c38\u8eab\u5e26\u5230\u91ce\u5916\u846c\u4e86\uff0c\u53c8\u662f\u60b2\u75db\uff0c\u53c8\u662f\u5fff\u6012\uff0c\u731c\u4e0d\u900f\u4e00\u53ea\u8718\u86db\u600e\u4f1a\u5982\u6b64\u5267\u6bd2\u3002\u9752\u9752\u89c1\u8fc7\u4e94\u6bd2\u6559\u7684\u4f0e\u4fe9\uff0c\u5bfb\u601d\uff1a\u201c\u539f\u6765\u90a3\u8001\u4e5e\u5a46\u6697\u4e2d\u8e51\u4e0a\u6211\u4eec\u5566\u3002\u201d \u3000\u3000\u6b21\u65e5\u56db\u4eba\u5728\u5ba2\u5e97\u5403\u996d\uff0c\u903c\u7740\u5e97\u4f19\u5148\u5c1d\u51e0\u53e3\uff0c\u7b49\u4ed6\u65e0\u4e8b\uff0c\u8fd9\u624d\u653e\u80c6\u5403\u559d\u3002 \u3000\u3000\u884c\u4e86\u6570\u65e5\uff0c\u4e00\u665a\u5ba2\u5e97\u4e2d\u5ffd\u7136\u4eba\u58f0\u5608\u6742\uff0c\u6709\u4eba\u5927\u547c\u5077\u9a6c\u3002\u6e29\u65b9\u609f\u8d77\u8eab\u67e5\u770b\uff0c\u5c06\u5230\u9a6c\u53a9\u65f6\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u55e4\u7684\u4e00\u58f0\uff0c\u4e00\u80a1\u6c34\u7bad\u8fce\u9762\u5c04\u6765\u3002\u4ed6\u6025\u7f29\u8eab\u95ea\u907f\uff0c\u5df2\u7136\u4e0d\u53ca\uff0c\u767b\u65f6\u55b7\u5f97\u6ee1\u8138\u90fd\u662f\uff0c\u53ea\u89c9\u5947\u8165\u523a\u9f3b\uff0c\u77e5\u9053\u4e0d\u5999\u3002\u4ed6\u773c\u775b\u5df2\u7ecf\u7741\u4e0d\u5f00\u6765\uff0c\u542c\u58f0\u8fa8\u5f62\uff0c\u957f\u97ad\u6325\u51fa\uff0c\u628a\u5077\u65bd\u6697\u88ad\u4e4b\u4eba\u6253\u5f97\u80cc\u810a\u6298\u65ad\u3002\u53e6\u4e00\u4eba\u559d\u9053\uff1a\u201c\u8001\u513f\u8fd8\u8981\u901e\u51f6\uff01\u201d\u4e3e\u65a7\u5288\u6765\u3002\u6e29\u65b9\u609f\u957f\u97ad\u5012\u8f6c\uff0c\u5c06\u90a3\u4eba\u8fde\u4eba\u5e26\u65a7\u5377\u8d77\uff0c\u7528\u529b\u4e00\u6325\uff0c\u90a3\u4eba\u4e00\u5934\u649e\u5728\u5899\u4e0a\uff0c\u8111\u6d46\u8ff8\u88c2\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u3001\u6e29\u65b9\u5c71\u4ee5\u4e3a\u533a\u533a\u51e0\u4e2a\u6bdb\u8d3c\uff0c\u5144\u5f1f\u5fc5\u53ef\u6599\u7406\u5f97\u4e86\uff0c\u5f85\u5f97\u542c\u89c1\u6e29\u65b9\u609f\u543c\u53eb\u8fde\u8fde\uff0c\u5fd9\u62a2\u51fa\u53bb\u770b\u65f6\uff0c\u53ea\u89c1\u4ed6\u53cc\u624b\u5728\u81ea\u5df1\u8138\u4e0a\u4e71\u6293\u4e71\u6316\uff0c\u624d\u77e5\u4e0d\u5999\u3002\u6e29\u65b9\u8fbe\u4e00\u628a\u5c06\u4ed6\u62b1\u4f4f\u3002\u6e29\u65b9\u5c71\u7eb5\u8eab\u51fa\u5916\u67e5\u770b\u654c\u8e2a\uff0c\u4e00\u65e0\u6240\u89c1\uff0c\u56de\u8fdb\u5e97\u623f\u65f6\uff0c\u89c1\u5144\u957f\u62b1\u4f4f\u4e86\u4e94\u5f1f\u7684\u8eab\u4f53\u5927\u54ed\uff0c\u539f\u6765\u6e29\u65b9\u609f\u5df2\u7136\u6c14\u7edd\u800c\u4ea1\uff0c\u987b\u7709\u8138\u988a\uff0c\u4ff1\u5df2\u4e2d\u6bd2\u6e83\u70c2\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u6ce3\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u90a3\u91d1\u86c7\u6076\u8d3c\u4ece\u6211\u4eec\u624b\u91cc\u9003\u4e86\u51fa\u53bb\uff0c\u90a3\u65f6\u4ed6\u7b4b\u8109\u5df2\u65ad\uff0c\u6210\u4e3a\u5e9f\u4eba\uff0c\u8eab\u8fb9\u6bd2\u836f\u4e5f\u65e9\u7ed9\u6211\u4eec\u641c\u51fa\uff0c\u53ef\u662f\u5d06\u5cd2\u6d3e\u7684\u4e24\u4f4d\u9053\u5144\u5374\u8eab\u4e2d\u5267\u6bd2\u800c\u4ea1\uff0c\u83ab\u975e\u5f53\u65f6\u5c31\u662f\u4e94\u6bd2\u6559\u6551\u4e86\u4ed6\u2026\u2026\u201d\u6e29\u65b9\u5c71\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u539f\u6765\u4e94\u6bd2\u6559\u6697\u4e2d\u5728\u8ddf\u54b1\u4eec\u4f5c\u5bf9\u3002\u8fd9\u6b21\u5927\u5bb6\u540c\u53d7\u66f9\u5316\u6df3\u4e4b\u8058\uff0c\u56fe\u8c0b\u5927\u4e8b\uff0c\u773c\u89c1\u5df2\u7136\u6210\u529f\uff0c\u90a3\u4e94\u6bd2\u6559\u6559\u4e3b\u4f55\u94c1\u624b\u7a81\u7136\u53cd\u8138\uff0c\u4ee5\u81f4\u529f\u8d25\u5782\u6210\u3002\u76f4\u5230\u73b0\u5728\uff0c\u6211\u4ecd\u4e0d\u77e5\u662f\u751a\u4e48\u7f18\u6545\u3002\u201d\u6e29\u65b9\u8fbe\u6c89\u601d\u7247\u523b\uff0c\u5ffd\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u91d1\u86c7\u6076\u8d3c\u6240\u7528\u6bd2\u836f\u5982\u6b64\u5389\u5bb3\uff0c\u770b\u6765\u4ed6\u5c31\u662f\u4e94\u6bd2\u6559\u7684\uff1f\u201d\u6e29\u65b9\u5c71\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u5fc5\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u4e24\u4eba\u60f3\u5230\u5f53\u5e74\u91d1\u86c7\u90ce\u541b\u6765\u77f3\u6881\u62a5\u4ec7\u7684\u72e0\u6bd2\uff0c\u4e0d\u89c9\u6817\u6817\u5371\u60e7\uff0c\u5f53\u4e0b\u628a\u6e29\u65b9\u609f\u7684\u5c38\u8eab\u57cb\u846c\u4e86\uff0c\u5546\u91cf\u4e86\u534a\u5929\uff0c\u51b3\u5fc3\u5148\u4e0a\u534e\u5c71\uff0c\u6398\u5230\u5b9d\u85cf\u4e4b\u540e\uff0c\u518d\u627e\u4e94\u6bd2\u6559\u62a5\u4ec7\uff0c\u53ea\u662f\u5bb3\u6015\u4ed6\u4eec\u6697\u4e2d\u52a0\u5bb3\uff0c\u4e0d\u4f46\u996e\u98df\u7279\u522b\u5c0f\u5fc3\uff0c\u665a\u4e0a\u8fde\u5ba2\u5e97\u4e5f\u4e0d\u6562\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u5929\u4e24\u5144\u5f1f\u5e26\u4e86\u9752\u9752\uff0c\u5bbf\u5728\u4e00\u5ea7\u53e4\u5e99\u7684\u7834\u6bbf\u4e4b\u4e2d\u3002\u6e29\u65b9\u8fbe\u5e74\u7eaa\u867d\u8001\uff0c\u4ecd\u5177\u795e\u529b\uff0c\u642c\u4e86\u4e24\u53ea\u5927\u77f3\u81fc\uff0c\u4e00\u53ea\u6491\u4f4f\u524d\u95e8\uff0c\u4e00\u53ea\u6491\u4f4f\u540e\u95e8\uff0c\u65b9\u624d\u5b89\u5fc3\u7761\u89c9\u3002\u7761\u5230\u4e2d\u591c\uff0c\u4f5b\u50cf\u4e4b\u540e\u5ffd\u7136\u6089\u6089\u6570\u58f0\uff0c\u4e24\u4eba\u767b\u65f6\u9192\u89c9\uff0c\u53ea\u5f53\u662f\u8001\u9f20\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u610f\u3002 \u3000\u3000\u6e29\u65b9\u5c71\u6726\u80e7\u95f4\u6b63\u8981\u518d\u7761\uff0c\u5ffd\u7136\u9f3b\u7ba1\u4e2d\u94bb\u5165\u4e00\u7f15\u5f02\u9999\uff0c\u987f\u89c9\u8eab\u5fc3\u8212\u6cf0\uff0c\u5feb\u7f8e\u5f02\u5e38\uff0c\u5168\u8eab\u98d8\u98d8\u8361\u8361\u7684\u4f3c\u4e4e\u795e\u6e38\u592a\u865a\uff0c\u7f6e\u8eab\u6781\u4e50\u3002\u4ed6\u5fc3\u795e\u4e00\u8361\uff0c\u7acb\u5373\u9192\u609f\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u867d\u7136\u4e8b\u8d77\u4ed3\u5352\uff0c\u4f46\u7a76\u662f\u6570\u5341\u5e74\u7684\u8001\u6c5f\u6e56\uff0c\u89c1\u673a\u6781\u5feb\uff0c\u62c9\u4f4f\u9752\u9752\u7684\u624b\uff0c\u63d0\u7740\u5979\u8dc3\u4e0a\u4e86\u4f9b\u684c\u3002\u661f\u5149\u71b9\u5fae\u4e0b\uff0c\u53ea\u89c1\u6e29\u65b9\u5c71\u624b\u821e\u94a2\u6756\uff0c\u4f7f\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u84e6\u5730\u91cc\u9707\u5929\u4ef7\u4e00\u58f0\u5de8\u54cd\uff0c\u4f5b\u50cf\u88ab\u94a2\u6756\u6253\u53bb\u4e86\u4e00\u622a\u3002\u4f5b\u50cf\u540e\u9762\u8dc3\u51fa\u4e24\u540d\u9ec4\u8863\u7ae5\u5b50\uff0c\u4e00\u4eba\u4f7f\u5200\u5411\u6e29\u65b9\u5c71\u653b\u53bb\uff0c\u53e6\u4e00\u4eba\u624b\u6267\u55b7\u7b52\uff0c\u53c8\u8981\u55b7\u5c04\u6bd2\u96fe\u3002\u6e29\u65b9\u8fbe\u624b\u4e00\u626c\uff0c\u6ce2\u6ce2\u4e24\u58f0\uff0c\u4e24\u652f\u8896\u7bad\u5f53\u573a\u628a\u4e24\u540d\u7ae5\u5b50\u7a7f\u80f8\u9489\u6b7b\u3002\u6e29\u65b9\u5c71\u5e76\u4e0d\u4f4f\u624b\uff0c\u4ecd\u5728\u4e71\u821e\u4e71\u6253\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u53eb\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u6ca1\u654c\u4eba\u5566\uff01\u201d\u6e29\u65b9\u5c71\u7adf\u662f\u5145\u8033\u4e0d\u95fb\uff0c\u4ed6\u795e\u667a\u5df2\u4e3a\u6bd2\u96fe\u6240\u8ff7\uff0c\u94a2\u6756\u8d8a\u4f7f\u8d8a\u6025\u3002\u6e29\u65b9\u8fbe\u77a7\u51fa\u4e0d\u5bf9\uff0c\u62a2\u4e0a\u53bb\u8981\u593a\u4ed6\u5175\u5203\u3002\u6e29\u65b9\u5c71\u628a\u94a2\u6756\u821e\u6210\u4e00\u56e2\u94f6\u5149\uff0c\u6025\u5207\u95f4\u54ea\u91cc\u62a2\u5f97\u5165\u53bb\uff1f\u7a81\u7136\u95f4\u6e29\u65b9\u5c71\u5927\u53eb\u4e00\u58f0\uff0c\u6756\u67c4\u5012\u8f6c\uff0c\u6756\u9876\u9f99\u5934\u649e\u5728\u81ea\u5df1\u80f8\u524d\uff0c\u9c9c\u8840\u76f4\u55b7\uff0c\u53cc\u811a\u4e00\u633a\uff0c\u773c\u89c1\u6d3b\u4e0d\u4e86\u3002 \u3000\u3000\u9752\u9752\u89c1\u4e09\u4f4d\u7237\u7237\u6570\u65e5\u4e4b\u5185\u90fd\u88ab\u4e94\u6bd2\u6559\u5bb3\u6b7b\uff0c\u6e29\u65b9\u5c71\u662f\u5979\u4eb2\u5916\u516c\uff0c\u5411\u6765\u5f85\u5979\u6bd4\u522b\u7684\u56db\u4f4d\u7237\u7237\u90fd\u597d\u4e9b\uff0c\u8fd9\u65f6\u4e0d\u7981\u6d12\u4e86\u51e0\u70b9\u773c\u6cea\u3002\u6e29\u65b9\u8fbe\u4e00\u58f0\u4e0d\u54cd\uff0c\u628a\u6e29\u65b9\u5c71\u7684\u5c38\u8eab\u62b1\u51fa\u53bb\u846c\u4e86\uff0c\u5728\u575f\u524d\u62dc\u4e86\u51e0\u62dc\uff0c\u5bf9\u9752\u9752\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d\u9752\u9752\u4e0d\u6562\u8fdd\u62d7\uff0c\u53ea\u5f97\u966a\u7740\u4ed6\u8fde\u591c\u8d76\u8def\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u4e00\u8def\u9632\u5907\u66f4\u52a0\u5468\u5bc6\u3002\u5165\u9655\u897f\u5883\u540e\uff0c\u66fe\u6709\u4e00\u540d\u7ea2\u8863\u7ae5\u5b50\u6328\u8fd1\u4ed6\u8eab\u8fb9\uff0c\u88ab\u4ed6\u624b\u8d77\u4e00\u638c\uff0c\u767b\u65f6\u9707\u7834\u4e86\u5929\u7075\u76d6\u3002\u9752\u9752\u89c1\u4e86\u4ed6\u94c1\u9752\u4e86\u8138\uff0c\u8d8a\u6765\u8d8a\u662f\u4e56\u623e\uff0c\u8fde\u8bdd\u4e5f\u4e0d\u6562\u591a\u8bf4\u4e00\u53e5\u3002 \u3000\u3000\u8fd9\u65e5\u5feb\u5230\u534e\u5c71\u811a\u4e0b\uff0c\u4e24\u4eba\u8d76\u4e86\u534a\u5929\u8def\uff0c\u5f88\u662f\u53e3\u6e34\uff0c\u5728\u4e00\u5ea7\u51c9\u4ead\u4e2d\u6b47\u8db3\u996e\u6c34\uff0c\u8ba9\u9a6c\u5339\u51c9\u4e00\u51c9\u6c57\u3002\u53ea\u89c1\u4e00\u540d\u4e61\u519c\u8d70\u8fdb\u4ead\u6765\uff0c\u6253\u7740\u9655\u897f\u571f\u8154\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6e29\u8001\u7237\u5b50\u5427\uff1f\u201d\u6e29\u65b9\u8fbe\u559d\u9053\uff1a\u201c\u4f60\u8981\u5e72\u751a\u4e48\uff1f\u201d\u90a3\u4e61\u519c\u9053\uff1a\u201c\u521a\u624d\u6709\u4eba\u7ed9\u4e86\u6211\u4e24\u540a\u94b1\uff0c\u53eb\u6211\u9001\u4fe1\u6765\u7ed9\u4f60\u3002\u201d\u6e29\u65b9\u8fbe\u9053\uff1a\u201c\u90a3\u4eba\u5462\uff1f\u201d\u4e61\u519c\u9053\uff1a\u201c\u4ed6\u5df2\u9a91\u9a6c\u8d70\u4e86\u3002\u201d \u3000\u3000\u6e29\u65b9\u8fbe\u6015\u6709\u8be1\u8ba1\uff0c\u547d\u9752\u9752\u53d6\u4fe1\u62c6\u5f00\uff0c\u89c1\u65e0\u5f02\u72b6\uff0c\u624d\u63a5\u8fc7\u4fe1\u7b3a\uff0c\u53ea\u89c1\u5171\u6709\u4e09\u9875\uff0c\u7b2c\u4e00\u9875\u4e0a\u5199\u9053\uff1a\u201c\u6e29\u8001\u5927\uff1a\u4f60\u4e09\u4e2a\u5144\u5f1f\u56e0\u4f55\u800c\u6b7b\uff0c\u6b32\u77e5\u8be6\u60c5\uff0c\u53ef\u770b\u4e0b\u9875\u3002\u201d\u6e29\u65b9\u8fbe\u9a82\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff01\u201d \u3000\u3000\u5fd9\u5c55\u7b2c\u4e8c\u9875\u89c2\u770b\uff0c\u51e0\u9875\u4fe1\u7eb8\u6025\u5207\u95f4\u5374\u63ed\u4e0d\u5f00\u6765\u3002\u4ed6\u4f38\u624b\u5165\u5634\uff0c\u6cbe\u4e86\u4e9b\u553e\u6db2\uff0c\u7ffb\u5f00\u7b2c\u4e8c\u9875\u6765\uff0c\u89c1\u7b3a\u4e0a\u5199\u9053\uff1a\u201c\u4f60\u6b7b\u671f\u4e5f\u5df2\u5230\u4e86\uff0c\u5982\u679c\u4e0d\u4fe1\uff0c\u518d\u770b\u7b2c\u4e09\u9875\u3002\u201d\u6e29\u65b9\u8fbe\u6108\u6012\uff0c\u968f\u624b\u53c8\u5728\u5634\u4e2d\u4e00\u6e7f\uff0c\u63ed\u5f00\u7b2c\u4e09\u9875\uff0c\u53ea\u89c1\u7b3a\u4e0a\u753b\u4e86\u4e00\u6761\u5927\u8708\u86a3\uff0c\u4e00\u4e2a\u9ab7\u9ac5\u5934\uff0c\u518d\u65e0\u5b57\u8ff9\u3002\u6c14\u607c\u4e2d\u628a\u7eb8\u7b3a\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u5ffd\u89c9\u53f3\u624b\u98df\u6307\u4e0e\u820c\u5934\u4e0a\u4f3c\u4e4e\u5fae\u5fae\u9ebb\u6728\uff0c\u5b9a\u795e\u4e00\u60f3\uff0c\u4e0d\u89c9\u51b7\u6c57\u76f4\u5192\u3002 \u3000\u3000\u539f\u6765\u4e09\u5f20\u7eb8\u7b3a\u4e0a\u5747\u6d78\u4e86\u5267\u6bd2\u6c41\u6db2\uff0c\u7eb8\u7b3a\u7a0d\u7a0d\u7c98\u4f4f\uff0c\u7b3a\u4e0a\u5199\u4e86\u6fc0\u4eba\u6124\u6012\u7684\u8a00\u8f9e\uff0c\u4f7f\u4eba\u72c2\u6012\u4e4b\u9645\u4e0d\u52a0\u63d0\u9632\uff0c\u4ee5\u624b\u6307\u6cbe\u6e7f\u553e\u6db2\uff0c\u5c31\u6b64\u628a\u5267\u6bd2\u5e26\u5165\u53e3\u4e2d\u3002\u8fd9\u662f\u4e94\u6bd2\u6559\u4e0b\u6bd2\u7684\u4e09\u5341\u516d\u5927\u6cd5\u4e4b\u4e00\u3002\u91d1\u86c7\u90ce\u541b\u5f53\u5e74\u4ece\u4f55\u7ea2\u836f\u5904\u5b66\u5f97\uff0c\u7528\u5728\u5047\u79d8\u7b08\u4e4b\u4e0a\uff0c\u5f20\u6625\u4e5d\u5373\u56e0\u6b64\u800c\u4e2d\u6bd2\u6bd9\u547d\u3002 \u3000\u3000\u6e29\u65b9\u8fbe\u60ca\u60f6\u4e2d\u62ac\u8d77\u5934\u6765\uff0c\u89c1\u90a3\u4e61\u519c\u5df2\u5954\u51fa\u6570\u5341\u6b65\u3002\u4ed6\u607c\u6012\u5df2\u6781\uff0c\u8d76\u51fa\u4ead\u6765\uff0c\u53ea\u89c9\u5934\u8111\u4e00\u9635\u6655\u7729\uff0c\u60c5\u77e5\u4e0d\u5999\uff0c\u5f85\u8981\u9547\u6151\u5fc3\u795e\uff0c\u66f4\u662f\u5934\u75db\u6b32\u88c2\uff0c\u5f53\u4e0b\u594b\u8d77\u795e\u5a01\uff0c\u98de\u621f\u76f4\u5f80\u90a3\u4e61\u519c\u540e\u5fc3\u63b7\u53bb\u3002\u90a3\u4eba\u6b63\u662f\u4e94\u6bd2\u6559\u5f92\uff0c\u53ea\u9053\u5df2\u7136\u5f97\u624b\uff0c\u54ea\u77e5\u77ed\u621f\u63b7\u6765\uff0c\u5982\u98ce\u4f3c\u7535\uff0c\u72c2\u53eb\u4e00\u58f0\uff0c\u94c1\u621f\u7a7f\u80f8\u800c\u8fc7\uff0c\u8eab\u5b50\u7adf\u88ab\u9489\u5728\u5730\u4e0b\u3002\u6e29\u65b9\u8fbe\u60e8\u7b11\u6570\u58f0\uff0c\u5f80\u540e\u4fbf\u5012\u3002 \u3000\u3000\u9752\u9752\u53eb\u9053\uff1a\u201c\u5927\u7237\u7237\uff0c\u4f60\u600e\u4e48\u5566\uff01\u201d\u4fef\u8eab\u53bb\u770b\u3002\u6e29\u65b9\u8fbe\u5de6\u624b\u4e00\u4f38\uff0c\u5ffd\u5730\u633a\u621f\u5f80\u5979\u80f8\u53e3\u523a\u5230\u3002\u9752\u9752\u4e07\u60f3\u4e0d\u5230\u4ed6\u4e34\u6b7b\u65f6\u8fd8\u8981\u4e0b\u6b64\u6bd2\u624b\uff0c\u53ea\u89c9\u773c\u524d\u94f6\u5149\u95ea\u8000\uff0c\u621f\u5c16\u5df2\u523a\u5230\u80f8\u53e3\uff0c\u8fd9\u65f6\u9000\u907f\u5df2\u7ecf\u4e0d\u53ca\uff0c\u53ea\u6709\u95ed\u76ee\u5f85\u6b7b\u3002\u5ffd\u542c\u5f53\u7684\u4e00\u58f0\uff0c\u811a\u80cc\u4e0a\u4e00\u9635\u5267\u75db\uff0c\u7741\u773c\u770b\u65f6\uff0c\u77ed\u621f\u5df2\u88ab\u4eba\u6253\u843d\u5728\u5730\uff0c\u621f\u67c4\u649e\u4e2d\u4e86\u81ea\u5df1\u811a\u80cc\u3002 \u3000\u3000\u5979\u8f6c\u8eab\u8981\u770b\u662f\u8c01\u51fa\u624b\u76f8\u6551\uff0c\u7a81\u89c9\u80cc\u5fc3\u5df2\u88ab\u4eba\u7262\u7262\u63ea\u4f4f\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u90a3\u4eba\u53d6\u51fa\u76ae\u7d22\uff0c\u5c06\u5979\u53cc\u624b\u53cd\u80cc\u7f1a\u4f4f\uff0c\u8fd9\u624d\u8f6c\u5230\u5979\u7684\u9762\u524d\uff0c\u6b63\u662f\u4e94\u6bd2\u6559\u7684\u8001\u4e5e\u5a46\u4f55\u7ea2\u836f\u3002 \u3000\u3000\u9752\u9752\u4e00\u80a1\u51c9\u6c14\u4ece\u4e39\u7530\u4e2d\u76f4\u5192\u4e0a\u6765\uff0c\u5fc3\u60f3\u843d\u5165\u8fd9\u6076\u4eba\u624b\u91cc\uff0c\u6b7b\u5f97\u4e0d\u77e5\u5c06\u5982\u4f55\u60e8\u9177\uff0c\u5012\u662f\u7ed9\u5927\u7237\u7237\u4e00\u621f\u523a\u6b7b\u75db\u5feb\u5f97\u591a\u4e86\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u9634\u607b\u607b\u7684\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6211\u4e00\u5200\u6740\u4e86\u4f60\u5462\uff0c\u8fd8\u662f\u559c\u6b22\u7ed9\u4e00\u5343\u6761\u65e0\u6bd2\u5c0f\u86c7\u6765\u54ac\u4f60\u4e03\u4e03\u56db\u5341\u4e5d\u5929\u624d\u6b7b\uff1f\u201d\u9752\u9752\u95ed\u76ee\u4e0d\u7b54\u3002\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u4f60\u5e26\u6211\u53bb\u627e\u4f60\u90a3\u8d1f\u5fc3\u7684\u7236\u4eb2\uff0c\u5c31\u4e0d\u8ba9\u4f60\u96f6\u788e\u53d7\u82e6\u3002\u201d \u3000\u3000\u9752\u9752\u5fc3\u60f3\uff1a\u201c\u53cd\u6b63\u6211\u662f\u8981\u53bb\u627e\u7239\u7239\u7684\u57cb\u9aa8\u4e4b\u5730\uff0c\u5c31\u8ba9\u5979\u5e26\u6211\u53bb\u597d\u4e86\u3002\u201d\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u8981\u53bb\u5bfb\u7239\u7239\uff0c\u4f60\u548c\u6211\u4e00\u540c\u53bb\u5427\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u89c1\u5979\u7b54\u5e94\u5f97\u723d\u5feb\uff0c\u4e0d\u7981\u8d77\u4e86\u7591\u5fc3\uff0c\u4f46\u60f3\u91d1\u86c7\u90ce\u541b\u5df2\u6210\u5e9f\u4eba\uff0c\u6b66\u529f\u5168\u5931\uff0c\u4e5f\u4e0d\u6015\u4ed6\u600e\u7684\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5e26\u8def\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u653e\u5f00\u6211\uff0c\u8ba9\u6211\u5148\u846c\u4e86\u5927\u7237\u7237\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u653e\u5f00\u4f60\uff1f\u54fc\uff01\u201d\u62fe\u8d77\u6e29\u65b9\u8fbe\u7684\u77ed\u621f\uff0c\u5728\u8def\u65c1\u6398\u4e86\u4e2a\u5927\u5751\uff0c\u628a\u6e29\u65b9\u8fbe\u548c\u90a3\u540d\u4e94\u6bd2\u6559\u5f92\u4e24\u4eba\u7684\u5c38\u8eab\u90fd\u6295\u5728\u5751\u91cc\uff0c\u76d6\u4e0a\u4e86\u6ce5\u571f\uff0c\u4e00\u9762\u63a9\u57cb\uff0c\u4e00\u9762\u5583\u5583\u5492\u9a82\uff1a\u201c\u4f60\u7236\u4eb2\u867d\u662f\u574f\u86cb\uff0c\u53ef\u662f\u6211\u4e0d\u8bb8\u522b\u4eba\u6298\u8fb1\u4ed6\u3002\u8fd9\u56db\u4e2a\u8001\u5934\u513f\u5f04\u5f97\u4ed6\u6b7b\u4e0d\u6b7b\u3001\u6d3b\u4e0d\u6d3b\u7684\uff0c\u6211\u65e9\u5c31\u8981\u627e\u4ed6\u4eec\u7684\u6666\u6c14\u4e86\u3002\u76f4\u5230\u4eca\u65e5\uff0c\u65b9\u6cc4\u4e86\u5fc3\u5934\u4e4b\u6068\u3002\u600e\u4e48\u4f60\u53c8\u53eb\u4ed6\u4eec\u505a\u7237\u7237\uff1f\u201d \u3000\u3000\u9752\u9752\u4e0d\u7b54\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u4e00\u8bf4\uff0c\u4f60\u53c8\u8981\u9a82\u6211\u5988\u5988\u3002\u201d \u3000\u3000\u8fd9\u5929\u4e24\u4eba\u8d70\u4e86\u56db\u4e94\u5341\u91cc\uff0c\u5728\u534a\u5c71\u8170\u91cc\u6b47\u4e86\u3002\u4f55\u7ea2\u836f\u665a\u4e0a\u7528\u76ae\u7d22\u628a\u9752\u9752\u53cc\u8db3\u7262\u7262\u7f1a\u4f4f\uff0c\u9632\u5979\u9003\u8d70\u3002\u6b21\u65e5\u4e00\u65e9\uff0c\u5929\u521a\u5fae\u660e\uff0c\u4f55\u7ea2\u836f\u89e3\u5f00\u9752\u9752\u811a\u4e0a\u76ae\u7d22\uff0c\u4e24\u4eba\u53c8\u518d\u4e0a\u5c71\u3002\u5c71\u8def\u6108\u6765\u6108\u9661\uff0c\u5230\u540e\u6765\u987b\u5f97\u624b\u8db3\u5e76\u7528\uff0c\u6500\u85e4\u9644\u845b\uff0c\u65b9\u80fd\u4e0a\u53bb\u3002\u4f55\u7ea2\u836f\u5de6\u624b\u5df2\u65ad\uff0c\u65e0\u6cd5\u62c9\u626f\u9752\u9752\uff0c\u4e8e\u662f\u89e3\u53bb\u5979\u624b\u4e0a\u76ae\u7d22\uff0c\u8981\u5979\u8d70\u5728\u524d\u5934\uff0c\u81ea\u5df1\u5728\u540e\u76d1\u89c6\u3002\u9752\u9752\u4ece\u672a\u6765\u8fc7\u534e\u5c71\uff0c\u53cd\u987b\u4f55\u7ea2\u836f\u6307\u70b9\u8def\u5f84\u3002 \u3000\u3000\u5f53\u665a\u4e24\u4eba\u5728\u4e00\u68f5\u5927\u6811\u4e0b\u6b47\u5bbf\u3002\u9752\u9752\u8eab\u5904\u8352\u5c71\uff0c\u547d\u60ac\u654c\u624b\uff0c\u773c\u89c1\u660e\u6708\u5728\u5929\uff0c\u8033\u542c\u733f\u557c\u4e8e\u8c37\uff0c\u601d\u6f6e\u8d77\u4f0f\uff0c\u53c8\u60b2\u53c8\u6015\uff0c\u54ea\u91cc\u8fd8\u7761\u5f97\u7740\uff1f \u3000\u3000\u6b21\u6668\u53c8\u884c\uff0c\u76f4\u81f3\u7b2c\u4e09\u5929\u508d\u665a\uff0c\u624d\u4e0a\u534e\u5c71\u7edd\u9876\u3002\u9752\u9752\u542c\u8881\u627f\u5fd7\u8be6\u7ec6\u8bf4\u8fc7\u7236\u4eb2\u57cb\u9aa8\u4e4b\u6240\u56db\u5468\u7684\u666f\u7269\uff0c\u8fd9\u65f6\u62ac\u5934\u671b\u89c1\u5ced\u58c1\uff0c\u89c1\u77f3\u58c1\u65c1\u5b64\u677e\u602a\u77f3\uff0c\u6d41\u6cc9\u98de\u7011\uff0c\u6b63\u548c\u8881\u627f\u5fd7\u6240\u8bf4\u7684\u4e00\u6a21\u4e00\u6837\uff0c\u4e0d\u7981\u4e00\u9635\u5fc3\u9178\uff0c\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u5389\u58f0\u9053\uff1a\u201c\u4ed6\u8eb2\u5728\u54ea\u91cc\uff1f\u201d\u9752\u9752\u5411\u5ced\u58c1\u4e00\u6307\u9053\uff1a\u201c\u90a3\u77f3\u58c1\u4e0a\u6709\u4e00\u4e2a\u6d1e\uff0c\u7239\u7239\u5c31\u4f4f\u5728\u8fd9\u91cc\u9762\u3002\u201d\u4f55\u7ea2\u836f\u4fa7\u5934\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bb0\u5f97\u5f53\u5e74\u91d1\u86c7\u90ce\u541b\u85cf\u8eab\u4e4b\u5904\u786e\u662f\u5728\u6b64\u5de6\u8fd1\uff0c\u54ac\u7259\u5207\u9f7f\u5730\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u4e0a\u53bb\u89c1\u4ed6\u3002\u201d\u9752\u9752\u89c1\u5979\u795e\u8272\u751a\u662f\u53ef\u6016\uff0c\u867d\u7136\u81ea\u5df1\u6b7b\u5fd7\u5df2\u51b3\uff0c\u5374\u4e5f\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u4e24\u4eba\u7ed5\u9053\u76d8\u5411\u5ced\u58c1\u9876\u4e0a\uff0c\u8d70\u51fa\u6570\u5341\u6b65\uff0c\u5ffd\u542c\u5f97\u8f6c\u89d2\u5904\u4f20\u6765\u7b11\u8bed\u4e4b\u58f0\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u62c9\u7740\u9752\u9752\u5f80\u8349\u4e1b\u91cc\u4e00\u7f29\uff0c\u53f3\u624b\u4e94\u6839\u5e26\u7740\u94a2\u5957\u7684\u6307\u7532\u62b5\u4f4f\u5979\u54bd\u5589\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u4f5c\u58f0\uff01\u201d\u4ece\u8349\u4e1b\u4e2d\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u9053\u548c\u4e00\u4e2a\u4e2d\u5e74\u4eba\u8c08\u7b11\u800c\u6765\u3002 \u3000\u3000\u9752\u9752\u8ba4\u5f97\u662f\u6728\u6851\u9053\u4eba\u548c\u8881\u627f\u5fd7\u7684\u5927\u5e08\u5144\u94dc\u7b14\u94c1\u7b97\u76d8\u9ec4\u771f\uff0c\u8fd9\u4e24\u4eba\u6b66\u529f\u90fd\u8fdc\u80dc\u4f55\u7ea2\u836f\uff0c\u4f46\u81ea\u5df1\u53ea\u8981\u4e00\u52a8\uff0c\u4e94\u679a\u6bd2\u6307\u7532\u4e0d\u514d\u7acb\u65f6\u5d4c\u5165\u5589\u5934\uff0c\u53ea\u542c\u9ec4\u771f\u7b11\u9053\uff1a\u201c\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u8fd9\u51e0\u5929\u5c31\u5feb\u4e0a\u5c71\u5566\u3002\u5c0f\u5e08\u5f1f\u603b\u4e5f\u662f\u65e5\u5185\u4fbf\u5230\u3002\u9053\u957f\u4e0d\u6101\u6ca1\u4e0b\u68cb\u7684\u5bf9\u624b\u3002\u201d\u6728\u6851\u7b11\u9053\uff1a\u201c\u8981\u4e0d\u662f\u8d2a\u4e0b\u68cb\uff0c\u4f60\u4eec\u534e\u5c71\u6d3e\u805a\u4f1a\uff0c\u6211\u8001\u9053\u5df4\u5df4\u7684\u8d76\u6765\u5e72\u4e48\u5440\uff1f\u51d1\u70ed\u95f9\u4e48\uff1f\u201d\u4e24\u4eba\u4e00\u8def\u8bf4\u7b11\uff0c\u9010\u6e10\u8fdc\u53bb\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u6df1\u77e5\u534e\u5c71\u6d3e\u7684\u5389\u5bb3\uff0c\u542c\u8bf4\u4ed6\u4eec\u8981\u5728\u6b64\u805a\u4f1a\uff0c\u5fc3\u60f3\u9669\u5730\u4e0d\u53ef\u591a\u803d\uff0c\u5f53\u4e0b\u4f0f\u4f4e\u8eab\u5b50\uff0c\u6162\u6162\u722c\u5230\u5ced\u58c1\u4e4b\u4fa7\uff0c\u4ece\u80cc\u56ca\u91cc\u53d6\u51fa\u7ef3\u7d22\uff0c\u4e00\u7aef\u7f1a\u4f4f\u4e86\u4e00\u68f5\u8001\u6811\uff0c\u53e6\u4e00\u7aef\u7f1a\u7740\u81ea\u5df1\u548c\u9752\u9752\uff0c\u7f13\u7f13\u7f12\u4e0b\u3002\u9752\u9752\u5ffd\u7136\u89c1\u5230\u5ced\u58c1\u4e0a\u7684\u6d1e\u7a74\uff0c\u53eb\u9053\uff1a\u201c\u662f\u8fd9\u91cc\u4e86\uff01\u201d \u3000\u3000\u4f55\u7ea2\u836f\u5fc3\u4e2d\u7a81\u7a81\u4e71\u8df3\uff0c\u6570\u5341\u5e74\u6765\uff0c\u957f\u65e5\u51dd\u601d\uff0c\u6df1\u5bb5\u68a6\u56de\uff0c\u65e0\u4e00\u523b\u4e0d\u662f\u60f3\u5230\u4e0e\u8fd9\u8d1f\u5fc3\u4eba\u91cd\u884c\u4f1a\u9762\u7684\u60c5\u666f\uff0c\u6216\u8bb8\uff0c\u8981\u72e0\u72e0\u6298\u78e8\u4ed6\u4e00\u756a\uff0c\u518d\u5c06\u4ed6\u6253\u6b7b\uff0c\u53c8\u6216\u8bb8\uff0c\u7adf\u4f1a\u786c\u4e0d\u8d77\u5fc3\u80a0\u800c\u9976\u4e86\u4ed6\uff0c\u5185\u5fc3\u6df1\u5904\uff0c\u5b9e\u76fc\u4ed6\u80fd\u56de\u5fc3\u8f6c\u610f\uff0c\u53c8\u548c\u81ea\u5df1\u91cd\u5706\u65e7\u68a6\uff0c\u5373\u4f7f\u4ed6\u8981\u72e0\u72e0\u7684\u97ad\u6253\u81ea\u5df1\u4e00\u987f\u51fa\u6c14\uff0c\u90a3\u4e5f\u7531\u5f97\u4ed6\uff0c\u8fd9\u65f6\u76f8\u89c1\u5728\u5373\uff0c\u53ea\u89c9\u8eab\u5b50\u53d1\u98a4\uff0c\u624b\u5fc3\u91cc\u90fd\u662f\u51b7\u6c57\u3002 \u3000\u3000\u5979\u53f3\u624b\u4e71\u6316\u4e71\u64ac\uff0c\u628a\u6d1e\u7a74\u5468\u56f4\u7684\u7816\u77f3\u9752\u8349\u62e8\u5f00\u3002\u4f55\u7ea2\u836f\u547d\u9752\u9752\u5148\u8fdb\u6d1e\u53bb\uff0c\u638c\u5fc3\u4e2d\u6263\u4e86\u5267\u6bd2\u94a2\u5957\uff0c\u8c28\u9632\u91d1\u86c7\u90ce\u541b\u7a81\u88ad\u3002 \u3000\u3000\u9752\u9752\u8fdb\u6d1e\u4e4b\u540e\uff0c\u65e9\u5df2\u6cea\u5982\u96e8\u4e0b\uff0c\u8d8a\u5411\u5185\u8d70\uff0c\u8d8a\u662f\u54ed\u5f97\u62bd\u62bd\u564e\u564e\u3002\u8fdb\u4e0d\u6570\u6b65\uff0c\u6d1e\u5185\u5df2\u662f\u4e00\u56e2\u6f06\u9ed1\u3002\u4f55\u7ea2\u836f\u6253\u4eae\u706b\u6298\uff0c\u70b9\u71c3\u4e86\u7ef3\u7d22\uff0c\u547d\u9752\u9752\u62ff\u5728\u624b\u91cc\uff0c\u7167\u4eae\u8def\u5f84\u3002\u9752\u9752\u4e00\u5446\uff0c\u5fc3\u60f3\uff1a\u201c\u70e7\u4e86\u7ef3\u7d22\uff0c\u600e\u751f\u56de\u4e0a\u53bb\uff1f\u6211\u53cd\u6b63\u662f\u6b7b\u5728\u8fd9\u91cc\u966a\u7239\u7239\u5988\u5988\u7684\u4e86\uff0c\u96be\u9053\u5979\u4e5f\u4e0d\u56de\u53bb\uff1f\u201d \u3000\u3000\u4f55\u7ea2\u836f\u6108\u5411\u5185\u8d70\uff0c\u6108\u89c9\u5c71\u6d1e\u4e0d\u662f\u6709\u4eba\u5c45\u4f4f\u7684\u6a21\u6837\uff0c\u7591\u5fc3\u5927\u76db\uff0c\u7a81\u7136\u4e00\u628a\u53c9\u4f4f\u9752\u9752\u7684\u8116\u5b50\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5bf9\u8001\u5a18\u6363\u9b3c\uff0c\u53ef\u6559\u4f60\u4e0d\u5f97\u597d\u6b7b\uff01\u201d \u3000\u3000\u84e6\u5730\u91cc\u5bd2\u98ce\u98d2\u7136\u88ad\u4f53\uff0c\u706b\u5149\u98a4\u52a8\uff0c\u6765\u5230\u4e86\u7a7a\u5ed3\u4e4b\u5904\uff0c\u6709\u5982\u4e00\u95f4\u77f3\u5ba4\u3002\u4f55\u7ea2\u836f\u5fc3\u4e2d\u4e00\u9707\uff0c\u4e3e\u8d77\u7ef3\u7d22\u56db\u4e0b\u7167\u770b\uff0c\u53ea\u89c1\u56db\u58c1\u523b\u7740\u65e0\u6570\u6b66\u529f\u56fe\u5f62\uff0c\u4e00\u884c\u5b57\u5199\u9053\uff1a\u201c\u91cd\u5b9d\u79d8\u672f\uff0c\u4ed8\u4e0e\u6709\u7f18\uff0c\u5165\u6211\u95e8\u6765\uff0c\u9047\u7978\u83ab\u6028\u3002\u201d\u91d1\u86c7\u90ce\u541b\u548c\u5979\u867d\u7136\u76f8\u5904\u65f6\u65e5\u4e0d\u591a\uff0c\u4f46\u7ed9\u5979\u7ed8\u8fc7\u8096\u50cf\uff0c\u9898\u8fc7\u5b57\uff0c\u4ed6\u7684\u7b14\u8ff9\u65e9\u5df2\u6df1\u5370\u5fc3\u91cc\uff0c\u8fd9\u56db\u884c\u5b57\u679c\u7136\u662f\u4ed6\u624b\u7b14\uff0c\u53ea\u662f\u6587\u5b57\u5728\u58c1\uff0c\u4eba\u5374\u4e0d\u89c1\uff0c\u4e0d\u89c9\u5fc3\u75db\u5982\u7ede\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u96ea\u5b9c\uff0c\u4f60\u51fa\u6765\uff01\u6211\u51b3\u4e0d\u4f24\u4f60\u3002\u201d\u8fd9\u4e00\u58f0\u53eb\u558a\uff0c\u53ea\u9707\u5f97\u6ce5\u5c18\u56db\u4e0b\u6251\u758f\u758f\u7684\u4e71\u843d\u3002 \u3000\u3000\u5979\u56de\u5934\u5389\u58f0\u95ee\u9752\u9752\u9053\uff1a\u201c\u4ed6\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u9752\u9752\u54ed\u7740\u5f80\u5730\u4e0b\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4ed6\u5728\u8fd9\u91cc\uff01\u201d\u4f55\u7ea2\u836f\u773c\u524d\u4e00\u9ed1\uff0c\u4f38\u624b\u6293\u4f4f\u9752\u9752\u624b\u8155\uff0c\u9669\u4e9b\u513f\u6655\u5012\uff0c\u5636\u54d1\u4e86\u55d3\u5b50\u95ee\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u7239\u7239\u846c\u5728\u8fd9\u91cc\u3002\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u54e6\u2026\u2026\u539f\u6765\u2026\u2026\u4ed6\u2026\u2026\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d\u8fd9\u65f6\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u8dcc\u5750\u5728\u91d1\u86c7\u90ce\u541b\u5e73\u6614\u6253\u5750\u7684\u90a3\u5757\u5ca9\u77f3\u4e0a\uff0c\u53f3\u624b\u629a\u4f4f\u4e86\u5934\uff0c\u5fc3\u4e2d\u60b2\u82e6\u4e4b\u6781\uff0c\u6570\u5341\u5e74\u8574\u79ef\u7684\u6028\u6bd2\u4e00\u65f6\u5c3d\u89e3\uff0c\u65e7\u65f6\u7684\u67d4\u60c5\u871c\u610f\u9661\u7136\u95f4\u53c8\u56de\u5230\u4e86\u5fc3\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u51fa\u53bb\u5427\uff0c\u6211\u9976\u4e86\u4f60\u5566\uff01\u201d \u3000\u3000\u9752\u9752\u89c1\u5979\u5982\u6b64\u60b2\u82e6\uff0c\u4e0d\u89c9\u601c\u60dc\u4e4b\u60c5\u6cb9\u7136\u800c\u751f\uff0c\u60f3\u8d77\u7239\u7239\u5bf9\u5979\u4e0d\u8d77\uff0c\u8881\u627f\u5fd7\u4e5f\u662f\u8fd9\u822c\u8d1f\u5fc3\uff0c\u4e24\u4eba\u5b9e\u662f\u540c\u75c5\u76f8\u601c\uff0c\u5ffd\u7136\u6251\u8fc7\u53bb\u62b1\u4f4f\u4e86\u5979\uff0c\u653e\u58f0\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u5feb\u51fa\u53bb\uff0c\u7ef3\u5b50\u518d\u70e7\u4e00\u9635\uff0c\u4f60\u6c38\u8fdc\u56de\u4e0d\u4e0a\u53bb\u4e86\u3002\u201d \u3000\u3000\u9752\u9752\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u4f55\u7ea2\u836f\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u966a\u4f60\u7239\u7239\uff01\u201d\u9752\u9752\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u4e0a\u53bb\u4e86\u3002\u201d\u4f55\u7ea2\u836f\u9677\u5165\u6c89\u601d\uff0c\u5bf9\u9752\u9752\u4e0d\u518d\u7406\u4f1a\uff0c\u5ffd\u7136\u4f38\u624b\u5728\u5730\u4e0b\u5982\u75f4\u5982\u72c2\u822c\u6316\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9752\u9752\u60ca\u9053\uff1a\u201c\u4f60\u5e72\u751a\u4e48\uff1f\u201d\u4f55\u7ea2\u836f\u51c4\u7136\u9053\uff1a\u201c\u6211\u60f3\u4e86\u4ed6\u4e8c\u5341\u5e74\uff0c\u4eba\u89c1\u4e0d\u5230\uff0c\u89c1\u89c1\u4ed6\u7684\u9aa8\u5934\u4e5f\u662f\u597d\u7684\u3002\u201d\u9752\u9752\u89c1\u5979\u795e\u8272\u5927\u53d8\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6015\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u4e00\u53ea\u53f3\u638c\u72b9\u5982\u4e00\u628a\u94c1\u9539\uff0c\u4e0d\u4f4f\u5728\u6ce5\u571f\u4e2d\u638f\u6316\uff0c\u6316\u4e86\u597d\u4e00\u9635\uff0c\u5751\u4e2d\u5df2\u9732\u51fa\u4e00\u5806\u9aa8\u6b96\uff0c\u6b63\u662f\u8881\u627f\u5fd7\u5f53\u5e74\u6240\u846c\u7684\u91d1\u86c7\u90ce\u541b\u9ab8\u9aa8\u3002\u9752\u9752\u6251\u5728\u7236\u4eb2\u7684\u9057\u9aa8\u4e0a\uff0c\u7eb5\u58f0\u75db\u54ed\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u518d\u6316\u4e00\u9635\uff0c\u500f\u5730\u5728\u571f\u5751\u4e2d\u6367\u8d77\u4e00\u4e2a\u9ab7\u9ac5\u5934\u6765\uff0c\u62b1\u5728\u6000\u91cc\uff0c\u53c8\u54ed\u53c8\u4eb2\uff0c\u53eb\u9053\uff1a\u201c\u590f\u90ce\uff0c\u590f\u90ce\uff0c\u6211\u6765\u77a7\u4f60\u5566\uff01\u201d\u4e00\u4f1a\u53c8\u4f4e\u4f4e\u7684\u5531\u6b4c\uff0c\u5531\u7684\u662f\u6446\u5937\u5c0f\u66f2\uff0c\u9752\u9752\u4e00\u53e5\u4e0d\u61c2\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u95f9\u4e86\u4e00\u9635\uff0c\u628a\u9ab7\u9ac5\u51d1\u5230\u5634\u8fb9\u72c2\u543b\uff1b\u7a81\u7136\u60ca\u547c\uff0c\u53ea\u89c9\u9762\u988a\u4e0a\u88ab\u5c16\u5229\u4e4b\u7269\u523a\u4e86\u4e00\u4e0b\u3002\u5979\u628a\u9ab7\u9ac5\u5f80\u5916\u4e00\u632a\uff0c\u5728\u706b\u5149\u4e0b\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u9ab7\u9ac5\u7684\u7259\u9f7f\u4e2d\u7262\u7262\u54ac\u7740\u4e00\u6839\u5c0f\u5c0f\u91d1\u9497\u3002\u91d1\u9497\u6781\u77ed\uff0c\u521d\u65f6\u7adf\u6ca1\u77a7\u89c1\u3002\u4f55\u7ea2\u836f\u4f38\u624b\u53bb\u62d4\uff0c\u7adf\u62d4\u4e0d\u4e0b\u6765\uff0c\u60f3\u662f\u91d1\u86c7\u90ce\u541b\u4e34\u6b7b\u65f6\u7528\u529b\u54ac\u4f4f\uff0c\u76f4\u5230\u808c\u8089\u70c2\u5b8c\uff0c\u91d1\u9497\u4ecd\u7136\u54ac\u5728\u5634\u91cc\u3002\u4f55\u7ea2\u836f\u4f38\u6307\u63d2\u5230\u9ab7\u9ac5\u53e3\u4e2d\u7528\u529b\u6273\u52a8\uff0c\u9ab7\u9ac5\u7259\u9f7f\u8131\u843d\uff0c\u91d1\u9497\u8dcc\u5728\u5730\u4e0b\u3002\u5979\u6361\u4e86\u8d77\u6765\uff0c\u62ed\u53bb\u5c18\u571f\uff0c\u4e0d\u7531\u5f97\u8138\u8272\u5927\u53d8\uff0c\u5389\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u5988\u5988\u540d\u53eb\u2018\u6e29\u4eea\u2019\uff1f\u201d\u9752\u9752\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u60b2\u6012\u4ea4\u96c6\uff0c\u54ac\u7259\u5207\u9f7f\u7684\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u4e34\u6b7b\u8fd8\u662f\u8bb0\u7740\u90a3\u4e2a\u8d31\u5a62\uff0c\u628a\u5979\u7684\u9497\u5b50\u54ac\u5728\u53e3\u91cc\uff01\u201d\u671b\u7740\u91d1\u9497\u4e0a\u523b\u7740\u7684\u201c\u6e29\u4eea\u201d\u4e24\u5b57\uff0c\u773c\u4e2d\u5982\u8981\u55b7\u51fa\u706b\u6765\uff0c\u7a81\u7136\u628a\u9497\u5b50\u653e\u5165\u53e3\u91cc\uff0c\u4e71\u54ac\u4e71\u56bc\uff0c\u53ea\u523a\u5f97\u6ee1\u53e3\u90fd\u662f\u9c9c\u8840\u3002 \u3000\u3000\u9752\u9752\u89c1\u5979\u5982\u75af\u4f3c\u72c2\uff0c\u795e\u667a\u5df2\u4e71\uff0c\u5fc3\u60f3\u4e24\u4eba\u6bd5\u547d\u4e4b\u671f\u4fbf\u5728\u773c\u524d\uff0c\u4ece\u80cc\u56ca\u4e2d\u53d6\u51fa\u6bcd\u4eb2\u7684\u9aa8\u7070\u575b\uff0c\u89e3\u5f00\u575b\u4e0a\u7f1a\u7740\u7684\u725b\u76ae\uff0c\u5012\u8f6c\u575b\u5b50\uff0c\u628a\u9aa8\u7070\u7f13\u7f13\u503e\u5165\u5751\u4e2d\u3002\u4f55\u7ea2\u836f\u5446\u4e86\u4e00\u5446\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u751a\u4e48\uff1f\u201d\u9752\u9752\u4e0d\u7b54\uff0c\u5012\u5b8c\u9aa8\u7070\u540e\uff0c\u628a\u6ce5\u571f\u6252\u7740\u63a9\u4e0a\uff0c\u5fc3\u4e2d\u9ed8\u9ed8\u7977\u795d\uff1a\u201c\u7239\u5a18\u5728\u5929\u4e4b\u7075\u6709\u77e5\uff0c\u5973\u513f\u5df2\u5b8c\u6210\u4e86\u4f60\u4eec\u5408\u846c\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u4f55\u7ea2\u836f\u593a\u8fc7\u7070\u575b\u4e00\u77a7\uff0c\u604d\u7136\u800c\u609f\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u6bcd\u4eb2\u7684\u9aa8\u7070\uff1f\u201d\u9752\u9752\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\u3002\u4f55\u7ea2\u836f\u53cd\u624b\u4e00\u638c\uff0c\u9752\u9752\u8eab\u5b50\u4e00\u7f29\uff0c\u6ca1\u80fd\u907f\u5f00\uff0c\u8fd9\u4e00\u638c\u6b63\u6253\u5728\u5979\u80a9\u5934\u4e4b\u4e0a\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u4e9b\u513f\u8dcc\u5012\u3002\u4f55\u7ea2\u836f\u72c2\u53eb\uff1a\u201c\u4e0d\u8bb8\u4f60\u4eec\u5408\u846c\uff0c\u4e0d\u8bb8\u4f60\u4eec\u5408\u846c\uff01\u201d\u7528\u624b\u4e71\u6252\uff0c\u4f46\u9aa8\u7070\u5df2\u4e0e\u6ce5\u571f\u6df7\u5408\u5728\u4e00\u8d77\uff0c\u518d\u4e5f\u5206\u62c6\u4e0d\u5f00\u3002\u5979\u5992\u5ff5\u5982\u70bd\uff0c\u628a\u9ab8\u9aa8\u4ece\u5751\u4e2d\u6361\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6211\u628a\u4f60\u70e7\u6210\u7070\uff0c\u70e7\u6210\u7070\uff0c\u6492\u5728\u534e\u5c71\u811a\u4e0b\uff0c\u6559\u4f60\u56db\u6563\u98de\u626c\uff0c\u56db\u6563\u98de\u626c\uff01\u6c38\u8fdc\u4e0d\u80fd\u8ddf\u90a3\u8d31\u5a62\u76f8\u805a\uff01\u201d \u3000\u3000\u9752\u9752\u5927\u6025\uff0c\u62a2\u4e0a\u4e89\u593a\uff0c\u62c6\u4e0d\u6570\u62db\uff0c\u4fbf\u7ed9\u6253\u5012\u5728\u5730\u3002\u4f55\u7ea2\u836f\u8131\u4e0b\u5916\u8863\u94fa\u5728\u5730\u4e0b\uff0c\u628a\u9ab8\u9aa8\u5806\u5728\u8863\u4e0a\uff0c\u7528\u706b\u70b9\u71c3\u8863\u670d\u3002\u5979\u5de6\u8098\u62b5\u4f4f\u9752\u9752\uff0c\u4e0d\u8ba9\u5979\u52a8\u5f39\uff0c\u53f3\u638c\u62e8\u706b\u4f7f\u65fa\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u9ab8\u9aa8\u5df2\u7ecf\u71c3\u7740\uff0c\u77f3\u6d1e\u4e2d\u6d53\u70df\u5f25\u6f2b\u3002 \u3000\u3000\u4f55\u7ea2\u836f\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u7136\u9f3b\u5b54\u4e2d\u94bb\u8fdb\u4e00\u80a1\u5f02\u5473\uff0c\u60ca\u6115\u4e4b\u4e0b\uff0c\u767b\u65f6\u7701\u609f\uff0c\u5927\u53eb\uff1a\u201c\u590f\u90ce\uff0c\u4f60\u597d\u6bd2\u5440\uff01\u201d \u3000\u3000\u9752\u9752\u4e5f\u89c9\u4e00\u80a1\u5f02\u9999\u731b\u6251\u9f3b\u7aef\uff0c\u6b63\u8be7\u5f02\u95f4\uff0c\u7a81\u89c9\u5934\u8111\u4e00\u9635\u6655\u7729\uff0c\u53ea\u89c1\u4f55\u7ea2\u836f\u6251\u5728\u71c3\u7740\u7684\u9ab8\u9aa8\u5806\u4e0a\uff0c\u731b\u529b\u5438\u6c14\uff0c\u4e71\u53eb\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u672c\u6765\u8981\u8ddf\u4f60\u6b7b\u5728\u4e00\u8d77\u3002\u90a3\u6700\u597d\uff0c\u597d\u6781\u4e86\uff01\u201d\u9661\u7136\u62ac\u8d77\u5934\u6765\uff0c\u51dd\u671b\u9752\u9752\uff0c\u8138\u8272\u6050\u6016\u4e4b\u6781\u3002 \u3000\u3000\u9752\u9752\u5927\u53eb\u4e00\u58f0\uff0c\u5f80\u5916\u9003\u51fa\uff0c\u5954\u51fa\u6570\u4e08\uff0c\u795e\u667a\u9010\u6e10\u80e1\u6d82\uff0c\u817f\u811a\u9178\u8f6f\uff0c\u8dcc\u5012\u5728\u5730\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5728\u996d\u5e97\u4e2d\u89c1\u5230\u4f55\u7ea2\u836f\u9489\u5728\u5899\u89d2\u7684\u8bb0\u53f7\uff0c\u77e5\u5979\u53ec\u96c6\u6559\u4f17\uff0c\u5927\u4e3e\u8ffd\u51fb\uff0c\u540c\u65f6\u9752\u9752\u53c8\u843d\u5728\u6e29\u6c0f\u56db\u8001\u624b\u91cc\uff0c\u4e0d\u8bba\u54ea\u4e00\u8fb9\u5f97\u80dc\uff0c\u9752\u9752\u90fd\u662f\u65e0\u5e78\uff0c\u7126\u6025\u4e07\u5206\uff0c\u7acb\u5373\u7eb5\u9a91\u75be\u9a70\uff0c\u6cbf\u8def\u5bfb\u8bbf\u3002\u4e0d\u4e45\u67e5\u77e5\u6e29\u6c0f\u56db\u8001\u4e2d\u5df2\u6709\u4e09\u4eba\u4e2d\u6bd2\u800c\u6b7b\uff0c\u8fd9\u4e00\u6765\u66f4\u662f\u6302\u8651\uff0c\u5f53\u771f\u662f\u65e5\u91cc\u98df\u4e0d\u7518\u5473\uff0c\u665a\u95f4\u7761\u4e0d\u5b89\u6795\uff0c\u5e78\u559c\u8fd9\u4e00\u6279\u4eba\u7684\u8e2a\u8ff9\u662f\u5411\u534e\u5c71\u800c\u53bb\uff0c\u5012\u4e5f\u4e0d\u81f4\u56e0\u8ffd\u8e2a\u800c\u8bef\u4e86\u4f1a\u671f\u3002 \u3000\u3000\u8d76\u5230\u534e\u5c71\u811a\u4e0b\u65f6\uff0c\u6d2a\u80dc\u6d77\u5728\u51c9\u4ead\u8fb9\u53d1\u73b0\u6709\u4e00\u7247\u6ce5\u571f\u9887\u6709\u5f02\u72b6\uff0c\u7528\u5175\u5203\u64ac\u571f\uff0c\u6316\u51fa\u6765\u7684\u8d6b\u7136\u662f\u6e29\u65b9\u8fbe\u548c\u53e6\u4e00\u4eba\u7684\u5c38\u9996\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u9752\u5f1f\u5fc5\u5df2\u843d\u5165\u4e94\u6bd2\u6559\u624b\u91cc\uff0c\u54b1\u4eec\u5feb\u4e0a\u5c71\u3002\u201d\u5b89\u5927\u5a18\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u8fd9\u65f6\u6b63\u662f\u534e\u5c71\u6d3e\u7684\u4f1a\u671f\uff0c\u7a46\u8001\u5e08\u7236\u5c31\u7b97\u8fd8\u6ca1\u5230\uff0c\u53ea\u8981\u9ec4\u5e08\u5144\u3001\u5f52\u5e08\u5144\u54ea\u4e00\u4f4d\u5230\u4e86\uff0c\u5b9a\u4f1a\u51fa\u624b\u76f8\u6551\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e94\u6bd2\u6559\u80c6\u6562\u95ef\u4e0a\u534e\u5c71\uff0c\u5fc5\u662f\u6709\u5907\u800c\u6765\uff0c\u53ef\u522b\u8ba9\u5e08\u4f84\u4eec\u906d\u4e86\u6bd2\u624b\u3002\u201d\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u8fde\u7956\u5e08\u7237\u4e5f\u5230\u4e86\uff0c\u6015\u4ed6\u4eec\u600e\u7684\uff1f\u5927\u5bb6\u5feb\u4e0a\u5c71\u554a\uff01\u201d \u3000\u3000\u4f17\u4eba\u628a\u9a6c\u5339\u5bc4\u5b58\u5728\u4e61\u4eba\u5bb6\u91cc\uff0c\u6025\u8d76\u4e0a\u5c71\u3002\u5feb\u5230\u5c71\u9876\u65f6\uff0c\u5ffd\u542c\u5f97\u55e4\u55e4\u55e4\u4e00\u9635\u54cd\uff0c\u6570\u7c92\u6697\u5668\u5212\u8fc7\u5929\u7a7a\u3002\u8881\u627f\u5fd7\u559c\u9053\uff1a\u201c\u6728\u6851\u9053\u957f\u5728\u4e0a\u9762\uff0c\u4ed6\u5728\u62db\u547c\u54b1\u4eec\u4e86\u3002\u201d\u5f53\u5373\u4ece\u8863\u56ca\u91cc\u6478\u51fa\u4e09\u679a\u94dc\u94b1\uff0c\u5411\u5929\u731b\u63b7\uff0c\u53ea\u89c1\u4e09\u9897\u9ec4\u70b9\u6d88\u5931\u5728\u4e91\u6c14\u4e4b\u4e2d\uff0c\u60a0\u7136\u800c\u901d\uff0c\u9694\u4e86\u597d\u4e00\u9635\u65b9\u624d\u843d\u4e0b\u3002\u5d14\u5e0c\u654f\u8d5e\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u8fd9\u4e00\u4e0b\u52b2\u9053\u597d\u8db3\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u6b63\u8981\u8dc3\u51fa\u53bb\u63a5\u8fd8\u94dc\u94b1\uff0c\u7a81\u7136\u5c71\u8170\u4e2d\u63b7\u51fa\u4e00\u4e2a\u9ed1\u9edd\u9edd\u7684\u7b97\u76d8\uff0c\u98de\u5c06\u4e0a\u53bb\u515c\u4f4f\u4e86\u4e09\u679a\u94dc\u94b1\uff0c\u8fd9\u624d\u843d\u4e0b\u3002\u4e00\u4eba\u4ece\u6811\u540e\u7a9c\u51fa\uff0c\u63a5\u4f4f\u7b97\u76d8\uff0c\u4e5e\u64e6\u4e5e\u64e6\u7684\u6447\u6643\uff0c\u5927\u7b11\u800c\u6765\uff0c\u6b63\u662f\u94dc\u7b14\u94c1\u7b97\u76d8\u9ec4\u771f\uff0c\u7b11\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u597d\u9614\u6c14\uff0c\u94dc\u94b1\u94f6\u5b50\u4e5f\u968f\u624b\u4e71\u63b7\uff0c\u8fd9\u53ef\u4e0d\u662f\u6325\u91d1\u5982\u571f\u5417\uff1f\u6211\u4eec\u751f\u610f\u4eba\u77a7\u7740\u53ef\u7740\u5b9e\u8089\u75db\u3002\u505a\u751f\u610f\u7684\u94b1\u4e00\u5165\u624b\uff0c\u53ef\u4e0d\u80fd\u8fd8\u4f60\u4e86\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u5927\u53eb\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8001\u4eba\u5bb6\u5148\u5230\u5566\uff01\u201d\u62a2\u4e0a\u53bb\u549a\u549a\u549a\u7684\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\u3002\u4ed6\u4e5f\u4e0d\u7406\u4f1a\u662f\u751a\u4e48\u5730\u65b9\uff0c\u5fc3\u4e2d\u9ad8\u5174\uff0c\u8fd9\u51e0\u4e2a\u5934\u78d5\u5f97\u52a0\u500d\u7528\u529b\uff0c\u7ad9\u8d77\u6765\u65f6\uff0c\u989d\u89d2\u5df2\u7ed9\u5ca9\u77f3\u649e\u80bf\u4e86\u9ad8\u9ad8\u4e00\u5757\u3002\u5b89\u5c0f\u6167\u53c8\u662f\u601c\u60dc\uff0c\u53c8\u662f\u6c14\u607c\uff0c\u4e0d\u4f4f\u4f4e\u58f0\u57cb\u6028\u3002\u5d14\u5e0c\u654f\u53ea\u662f\u50bb\u7b11\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b49\u4e5f\u90fd\u4e0a\u53bb\u89c1\u4e86\u793c\u3002\u5404\u4eba\u4e92\u9053\u522b\u6765\u60c5\u4e8b\u3002\u8881\u627f\u5fd7\u60ac\u5ff5\u9752\u9752\uff0c\u6b63\u60f3\u8be2\u95ee\u5927\u5e08\u54e5\u6709\u6ca1\u89c1\u5230\u5979\u8e2a\u8ff9\uff0c\u5ffd\u7136\u95f4\u6811\u4e1b\u91cc\u6251\u51fa\u4e24\u5934\u7329\u7329\uff0c\u4e00\u9f50\u7d27\u7d27\u6402\u4f4f\u4e86\u8881\u627f\u5fd7\u3002\u5d14\u5e0c\u654f\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u597d\uff01\u201d\u4f38\u62f3\u4fbf\u6253\u3002\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u5927\u5a01\uff0c\u5c0f\u4e56\uff0c\u4f60\u4eec\u597d\uff01\u201d\u4f38\u624b\u8f7b\u8f7b\u683c\u5f00\u5d14\u5e0c\u654f\u6253\u6765\u7684\u4e00\u62f3\u3002\u4e24\u5934\u7329\u7329\u7a81\u7136\u5431\u5431\u4e71\u53eb\uff0c\u653e\u5f00\u4e86\u8881\u627f\u5fd7\uff0c\u731b\u5f80\u5c71\u58c1\u4e0a\u7a9c\u53bb\u3002\u5d14\u5e0c\u654f\u9053\uff1a\u201c\u662f\u5c0f\u5e08\u53d4\u517b\u7684\u5417\uff1f\u7cdf\u7cd5\uff0c\u7329\u7329\u751f\u6c14\u4e86\uff01\u201d\u773c\u89c1\u4e24\u5934\u7329\u7329\u8d8a\u722c\u8d8a\u9ad8\uff0c\u8eab\u5f62\u6e10\u5c0f\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u9053\uff1a\u201c\u5927\u5a01\u3001\u5c0f\u4e56\u5b9a\u662f\u85cf\u7740\u751a\u4e48\u597d\u4e1c\u897f\uff0c\u89c1\u6211\u56de\u6765\uff0c\u8981\u53d6\u51fa\u6765\u7ed9\u6211\u3002\u201d\u671b\u4e86\u4e00\u9635\uff0c\u5ffd\u89c1\u5ced\u58c1\u4e0a\u5192\u51fa\u9635\u9635\u70df\u6765\uff0c\u90a3\u5904\u6240\u6b63\u662f\u57cb\u846c\u91d1\u86c7\u90ce\u541b\u7684\u6d1e\u7a74\uff0c\u4e0d\u89c9\u4e00\u60ca\uff0c\u53c8\u89c1\u4e24\u5934\u7329\u7329\u5728\u9ad8\u5904\u6307\u624b\u5212\u811a\uff0c\u5927\u6253\u624b\u52bf\uff0c\u4f3c\u5728\u62db\u547c\u81ea\u5df1\u8fc7\u53bb\u3002 \u3000\u3000\u5b89\u5c0f\u6167\u4e5f\u770b\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u627f\u5fd7\u5927\u54e5\uff0c\u4e24\u5934\u7329\u7329\u5728\u53eb\u4f60\u5462\uff01\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u5411\u54d1\u5df4\u6253\u4e86\u51e0\u4e0b\u624b\u52bf\uff0c\u54d1\u5df4\u70b9\u5934\u4f1a\u610f\uff0c\u5954\u5411\u77f3\u5ba4\u53d6\u4e86\u706b\u628a\u957f\u7d22\uff0c\u4e0e\u4f17\u4eba\u7ed5\u9053\u4e0a\u4e86\u5ced\u58c1\u4e4b\u9876\u3002 \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6d1e\u91cc\u7684\u8def\u5f84\u53ea\u6709\u6211\u719f\u3002\u6211\u4e00\u4e2a\u4eba\u8fdb\u53bb\u5427\u3002\u201d\u5728\u8863\u4e0a\u6495\u4e0b\u4e24\u7247\u5c0f\u5e03\uff0c\u585e\u4f4f\u9f3b\u5b54\uff0c\u70b9\u71c3\u706b\u628a\uff0c\u7f12\u7ef3\u4e0b\u53bb\u3002\u4e24\u5934\u7329\u7329\u5728\u5ced\u58c1\u4e0a\u4e71\u53eb\u4e71\u8df3\uff0c\u6414\u5934\u6316\u8033\uff0c\u4f3c\u4e4e\u5341\u5206\u7126\u6025\u3002 \u3000\u3000\u8881\u627f\u5fd7\u521a\u5230\u6d1e\u53e3\uff0c\u4fbf\u89c1\u4e00\u9635\u6d53\u70df\u5192\u51fa\uff0c\u5f53\u4e0b\u5c4f\u9664\u547c\u5438\uff0c\u76f4\u51b2\u8fdb\u53bb\uff0c\u5954\u81f3\u72ed\u9053\uff0c\u53ea\u89c1\u4e00\u4eba\u6a2a\u5367\u5728\u5730\uff0c\u51d1\u8fd1\u4e00\u770b\uff0c\u7adf\u662f\u9752\u9752\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u60ca\u559c\u4ea4\u96c6\uff0c\u5fd9\u6478\u5979\u53e3\u9f3b\uff0c\u547c\u5438\u5df2\u751a\u4e3a\u5fae\u5f31\u3002\u773c\u89c1\u5185\u6d1e\u5fae\u6709\u706b\u5149\uff0c\u5c1a\u6709\u4e00\u4eba\u8eba\u5728\u90a3\u91cc\uff0c\u6b63\u662f\u4f55\u7ea2\u836f\uff0c\u8fd8\u60f3\u5165\u53bb\u76f8\u6551\uff0c\u7a81\u7136\u95f4\u4e00\u4e2a\u8e09\u8dc4\uff0c\u80f8\u53e3\u4f5c\u6076\uff0c\u5934\u8111\u6655\u7729\uff0c\u767b\u65f6\u4fbf\u8981\u660f\u5012\uff0c\u77e5\u9053\u70df\u96fe\u4e2d\u542b\u6709\u5267\u6bd2\uff0c\u5fd9\u5f2f\u8eab\u62b1\u8d77\u9752\u9752\uff0c\u5954\u51fa\u6d1e\u6765\uff0c\u6293\u4f4f\u7ef3\u5b50\u3002 \u3000\u3000\u54d1\u5df4\u548c\u6d2a\u80dc\u6d77\u4e00\u9f50\u7528\u529b\uff0c\u628a\u4e24\u4eba\u540a\u5c06\u4e0a\u6765\u3002\u8881\u627f\u5fd7\u89c1\u56db\u5468\u5df2\u65e0\u6bd2\u70df\uff0c\u624d\u6df1\u6df1\u5438\u4e86\u4e24\u53e3\u6c14\uff0c\u5ffd\u89c9\u809a\u91cc\u96be\u53d7\u4e4b\u6781\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5728\u534a\u7a7a\u4e2d\u5927\u5455\u8d77\u6765\u3002 \u3000\u3000\u4f17\u4eba\u5728\u5ced\u58c1\u4e0a\u751a\u662f\u62c5\u5fe7\uff0c\u53ea\u6015\u4ed6\u4e2d\u4e86\u7634\u6c14\u6bd2\u96fe\uff0c\u4e00\u4e2a\u5931\u624b\uff0c\u4e24\u4eba\u90fd\u8dcc\u5165\u6df1\u8c37\u4e4b\u4e2d\u3002\u54d1\u5df4\u548c\u6d2a\u80dc\u6d77\u6218\u6218\u5162\u5162\u7684\u5411\u4e0a\u63d0\u62c9\uff0c\u5d14\u79cb\u5c71\u3001\u5d14\u5e0c\u654f\u53d4\u4f84\u5728\u65c1\u62a4\u6301\u3002 \u3000\u3000\u773c\u89c1\u62c9\u7740\u4e24\u4eba\u5c06\u5230\u5c71\u9876\uff0c\u7a81\u7136\u5ced\u58c1\u6d1e\u7a74\u5185\u9707\u5929\u4ef7\u4e00\u9635\u5de8\u54cd\uff0c\u70df\u96fe\u7030\u6f2b\uff0c\u5c71\u77f3\u6a2a\u98de\u3002\u4f17\u4eba\u90fd\u5927\u5403\u4e00\u60ca\u3002\u6d2a\u80dc\u6d77\u4e00\u5413\u4e4b\u4e0b\uff0c\u53cc\u624b\u677e\u4e86\u7ef3\u7d22\u3002\u5e78\u5f97\u54d1\u5df4\u8033\u804b\uff0c\u5e76\u672a\u542c\u89c1\uff0c\u517c\u4e4b\u795e\u529b\u60ca\u4eba\uff0c\u53cc\u624b\u4ea4\u4e92\u62c9\u626f\uff0c\u5c06\u4e8c\u4eba\u63d0\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u8881\u627f\u5fd7\u811a\u4e00\u7740\u5730\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u767b\u65f6\u8f6f\u5012\u3002\u6728\u6851\u5fd9\u7ed9\u4e24\u4eba\u63a8\u5bab\u8fc7\u6c14\u3002\u8fd9\u65f6\u5ced\u58c1\u4e2d\u7206\u70b8\u58f0\u4e00\u9635\u63a5\u7740\u4e00\u9635\uff0c\u4e0d\u77e5\u5c71\u6d1e\u4e4b\u4e2d\u600e\u4f1a\u85cf\u7740\u8fd9\u8bb8\u591a\u706b\u836f\uff0c\u53c8\u4e0d\u77e5\u8c01\u5728\u5185\u4e2d\u6363\u9b3c\uff0c\u5404\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u832b\u7136\u4e0d\u89e3\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8881\u627f\u5fd7\u60a0\u7136\u9192\u6765\uff0c\u8c03\u5300\u547c\u5438\uff0c\u53ea\u89c9\u5026\u4e4f\u4e07\u5206\uff0c\u8fde\u8bf4\uff1a\u201c\u597d\u9669\uff01\u201d\u53c8\u8fc7\u4e00\u9635\uff0c\u9752\u9752\u4e5f\u9192\u6765\u4e86\uff0c\u89c1\u4e86\u8881\u627f\u5fd7\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u54ed\u4e86\u51fa\u6765\u3002\u4f17\u4eba\u89c1\u4e24\u4eba\u9192\u8f6c\uff0c\u8fd9\u624d\u653e\u5fc3\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u7206\u70b8\u58f0\u5168\u7136\u505c\u606f\uff0c\u5d14\u5e0c\u654f\u81ea\u544a\u594b\u52c7\uff0c\u8981\u4e0b\u53bb\u67e5\u770b\u3002\u5d14\u79cb\u5c71\u628a\u7ef3\u7d22\u7262\u7262\u7cfb\u5728\u4ed6\u8170\u4e0a\uff0c\u7f13\u7f13\u7f12\u4e86\u4e0b\u53bb\u3002\u5d14\u5e0c\u654f\u89c1\u6d1e\u53e3\u5df2\u88ab\u70b8\u51fa\u6765\u7684\u788e\u77f3\u5de8\u5ca9\u5c01\u4f4f\uff0c\u518d\u4e5f\u65e0\u6cd5\u5165\u6d1e\uff0c\u53ea\u5f97\u56de\u4e0a\u3002\u9752\u9752\u795e\u667a\u6e10\u590d\uff0c\u65ad\u65ad\u7eed\u7eed\u7684\u628a\u6d1e\u4e2d\u60c5\u7531\u8bf4\u4e86\u3002\u201d \u3000\u3000\u6728\u6851\u53f9\u9053\uff1a\u201c\u5f53\u5e74\u6211\u89c1\u91d1\u86c7\u90ce\u541b\u5728\u94c1\u5323\u4e2d\u85cf\u7bad\uff0c\u5df2\u60ca\u8be7\u4ed6\u5fc3\u8ba1\u4e4b\u5de5\uff0c\u54ea\u77e5\u8fd8\u8fdc\u4e0d\u6b62\u6b64\u3002\u8fd9\u70b8\u836f\u5982\u6b64\u5a01\u731b\uff0c\u76f8\u8f83\u4e4b\u4e0b\uff0c\u94c1\u5323\u85cf\u7bad\u53ef\u8bf4\u662f\u5fae\u4e0d\u8db3\u9053\u4e86\u3002\u201d \u3000\u3000\u9ec4\u771f\u9053\uff1a\u201c\u4ed6\u7adf\u4f1a\u5728\u81ea\u5df1\u9ab8\u9aa8\u4e4b\u4e2d\u79cd\u4e0b\u6bd2\u836f\uff0c\u8fd9\u53c8\u6709\u8c01\u80fd\u60f3\u5f97\u5230\uff1f\u201d\u5d14\u5e0c\u654f\u7741\u5927\u4e86\u4e00\u53cc\u5706\u5706\u7684\u773c\u775b\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4ed6\u5728\u9ab8\u9aa8\u4e2d\u79cd\u6bd2\uff1f\u4ed6\u4eba\u5df2\u6b7b\u4e86\uff0c\u53d8\u6210\u4e86\u67af\u9aa8\uff0c\u600e\u4e48\u8fd8\u80fd\u5728\u81ea\u5df1\u9aa8\u5934\u4e2d\u4e0b\u6bd2\uff1f\u201d\u9ec4\u771f\u7b11\u9a82\uff1a\u201c\u597d\uff0c\u7b49\u4f60\u8001\u4eba\u5bb6\u5347\u5929\u5f52\u4f4d\u4e4b\u540e\uff0c\u4f60\u5012\u5728\u81ea\u5df1\u7684\u50bb\u9aa8\u5934\u91cc\uff0c\u653e\u70b9\u513f\u6bd2\u836f\u77a7\u77a7\uff01\u201d\u4f17\u4eba\u90fd\u54c4\u7b11\u8d77\u6765\u3002 \u3000\u3000\u5d14\u5e0c\u654f\u6485\u8d77\u4e86\u5634\u5507\uff1b\u9053\uff1a\u201c\u4eba\u5bb6\u4e0d\u77e5\u9053\u624d\u95ee\u5462\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u91d1\u86c7\u90ce\u541b\u590f\u8001\u5e08\u662f\u4e2a\u6781\u7cbe\u5e72\u8ba1\u7b97\u4e4b\u4eba\uff0c\u4ed6\u81ea\u77e5\u4e00\u751f\u7ed3\u4ec7\u592a\u591a\uff0c\u6b7b\u540e\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u4eba\u635f\u6bc1\u4ed6\u7684\u9057\u4f53\u3002\u4ed6\u5584\u4e8e\u7528\u6bd2\uff0c\u4e34\u7ec8\u4e4b\u65f6\uff0c\u5fc5\u5b9a\u670d\u4e86\u4e00\u79cd\u6df1\u5165\u9aa8\u9ad3\u7684\u5267\u6bd2\u836f\u5242\u3002\u201d \u3000\u3000\u5d14\u5e0c\u654f\u4e00\u62cd\u5927\u817f\uff0c\u604d\u7136\u5927\u609f\uff0c\u53eb\u9053\uff1a\u201c\u6211\u77e5\u9053\u5566\uff0c\u8981\u662f\u6709\u4eba\u70e7\u4ed6\u9057\u9aa8\uff0c\u70e7\u51fa\u6765\u7684\u6bd2\u70df\u5c31\u80fd\u5bb3\u6b7b\u4eba\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u6d1e\u91cc\u600e\u4e48\u53c8\u4f1a\u7206\u70b8\uff1f\u96be\u9053\u4ed6\u8fd8\u5403\u4e86\u70b8\u836f\uff0c\u8ba9\u70b8\u836f\u94bb\u5165\u9ab8\u9aa8\uff1f\u201d\u5b89\u5c0f\u6167\u6015\u4eba\u7b11\u4ed6\uff0c\u5fd9\u9053\uff1a\u201c\u70b8\u836f\u5fc5\u662f\u9884\u5148\u57cb\u5728\u7095\u4e2d\u7684\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u9eef\u7136\u70b9\u5934\uff0c\u53f9\u9053\uff1a\u201c\u9752\u5f1f\u7684\u6bcd\u4eb2\u9057\u547d\u8981\u548c\u4e08\u592b\u5408\u846c\uff0c\u73b0\u5728\u4e24\u4eba\u867d\u7136\u5c38\u9aa8\u5316\u7070\uff0c\u4f46\u7ec8\u4e8e\u5408\u846c\u5728\u4e00\u8d77\u4e86\u3002\u201d\u5d14\u5e0c\u654f\u4f38\u51fa\u4e86\u820c\u5934\uff0c\u4e0d\u4f4f\u60ca\u53f9\uff1a\u201c\u8fd9\u4eba\u597d\u5389\u5bb3\uff0c\u6b7b\u4e86\u51e0\u5341\u5e74\u4e4b\u540e\uff0c\u8fd8\u80fd\u5bf9\u4ed8\u53bb\u5bb3\u4ed6\u7684\u4eba\u3002\u6d3b\u7740\u4e4b\u65f6\uff0c\u90a3\u8fd8\u4e86\u5f97\uff1f\u90a3\u4e94\u6bd2\u6559\u7684\u6076\u5a46\u4e5f\u662f\u6b7b\u6709\u5e94\u5f97\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u5979\u867d\u7136\u6028\u6bd2\u592a\u8fc7\uff0c\u4f46\u4e00\u5f80\u60c5\u6df1\uff0c\u4e5f\u662f\u4e2a\u82e6\u547d\u4e4b\u4eba\u3002\u201d \u3000\u3000\u5b89\u5c0f\u6167\u629a\u6478\u7740\u4e24\u5934\u7329\u7329\u5934\u9876\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u662f\u5927\u5a01\u548c\u5c0f\u4e56\u53d1\u73b0\u5f97\u65e9\uff0c\u518d\u6162\u4e00\u6b65\uff0c\u4e0d\u4f46\u9752\u59ca\u59ca\u6551\u4e0d\u51fa\u6765\uff0c\u53ea\u6015\u627f\u5fd7\u5927\u54e5\u4e5f\u4f1a\u7ed9\u70b8\u5728\u5c71\u6d1e\u4e4b\u4e2d\u3002\u201d\u4f17\u4eba\u90fd\u8bf4\u7684\u786e\u597d\u9669\uff0c\u5e78\u4e8f\u755c\u751f\u7684\u77e5\u89c9\u7075\u654f\uff0c\u8fdc\u8fdc\u7684\u5c31\u5bdf\u89c9\u6709\u5f02\u3002\u4f17\u4eba\u4e00\u8def\u8c08\u8bba\u9002\u624d\u7684\u9669\u4e8b\uff0c\u4e00\u8def\u4e0a\u5c71\u3002\u5b89\u5927\u5a18\u548c\u5b89\u5c0f\u6167\u6276\u9752\u9752\u8d70\u8fdb\u77f3\u5ba4\uff0c\u7ed9\u5979\u6d17\u8138\u6362\u8863\uff0c\u6276\u4e0a\u5e8a\u53bb\u4f11\u606f\u3002 \u3000\u3000\u9752\u9752\u4e2d\u6bd2\u751a\u6df1\uff0c\u6728\u6851\u9053\u4eba\u867d\u7ed9\u5979\u670d\u4e86\u89e3\u6bd2\u7075\u4e39\uff0c\u4f46\u56e0\u91d1\u86c7\u90ce\u541b\u6240\u7528\u7684\u6bd2\u836f\u5f97\u81ea\u4e94\u6bd2\u6559\u79d8\u65b9\uff0c\u5bfb\u5e38\u89e3\u836f\u89c1\u4e0d\u4e86\u529f\u3002\u5979\u7761\u4e86\u4e00\u665a\u4e4b\u540e\uff0c\u6b21\u65e5\u8138\u4e0a\u5e03\u6ee1\u9ed1\u6c14\uff0c\u75c5\u52bf\u66f4\u89c1\u6c89\u91cd\uff0c\u6709\u65f6\u795e\u667a\u80e1\u6d82\u8d77\u6765\uff0c\u53c8\u54ed\u53c8\u95f9\uff0c\u660f\u8ff7\u4e2d\u53ea\u9a82\u8881\u627f\u5fd7\u8d1f\u5fc3\u65e0\u4e49\uff0c\u559c\u65b0\u5f03\u65e7\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u8881\u627f\u5fd7\u4e00\u526f\u5c34\u5c2c\u6a21\u6837\uff0c\u53c8\u662f\u597d\u7b11\uff0c\u53c8\u662f\u62c5\u5fc3\uff0c\u6015\u4ed6\u4e3a\u96be\uff0c\u90fd\u6084\u6084\u9000\u4e86\u51fa\u53bb\u3002\u8881\u627f\u5fd7\u67d4\u58f0\u5b89\u6170\uff0c\u575a\u79f0\u77e2\u5fd7\u9761\u4ed6\uff0c\u51b3\u4e0d\u79fb\u7231\u65c1\u4eba\u3002\u9752\u9752\u8138\u4e0a\u4e00\u9635\u7ea2\u4e00\u9635\u9ed1\uff0c\u4e0d\u4f4f\u5455\u5410\u9ed1\u6c34\u3002\u8881\u627f\u5fd7\u5230\u4e86\u8fd9\u4e2a\u5730\u6b65\uff0c\u4e5f\u662f\u675f\u624b\u65e0\u7b56\uff0c\u53ea\u6709\u5728\u5367\u69bb\u65c1\u5782\u6cea\u7684\u4efd\u513f\u3002 \u3000\u3000\u4f17\u4eba\u5728\u5916\u9762\u7eb7\u7eb7\u8bae\u8bba\uff0c\u6709\u7684\u8bf4\u91d1\u86c7\u90ce\u541b\u7528\u5fc3\u72e0\u6bd2\uff0c\u81ea\u53d7\u5176\u62a5\uff0c\u53cd\u800c\u5bb3\u4e86\u81ea\u5df1\u7684\u5973\u513f\uff1b\u6709\u7684\u8bf4\u9752\u9752\u8fd9\u6837\u4e00\u4e2a\u597d\u59d1\u5a18\uff0c\u867d\u7136\u7231\u4f7f\u5c0f\u6027\u5b50\uff0c\u5fc3\u5730\u5374\u597d\uff0c\u82e5\u662f\u5c31\u6b64\u4e0d\u6cbb\uff0c\u5b9e\u5728\u6559\u4eba\u96be\u8fc7\u3002\u4f17\u4eba\u5509\u58f0\u53f9\u6c14\uff0c\u6100\u7136\u4e0d\u4e50\u3002 \u3000\u3000\u5c06\u5230\u9ec4\u660f\uff0c\u4e24\u5934\u7329\u7329\u5148\u53eb\u4e86\u8d77\u6765\uff0c\u5916\u9762\u4e00\u9635\u4eba\u58f0\u55a7\u6270\uff0c\u539f\u6765\u662f\u5f52\u8f9b\u6811\u592b\u5987\u9886\u7740\u6885\u5251\u548c\u3001\u5218\u57f9\u751f\u3001\u5b59\u4ef2\u541b\u7b49\u516d\u540d\u5f1f\u5b50\u5230\u4e86\u3002\u5f52\u4e8c\u5a18\u62b1\u7740\u513f\u5b50\u5f52\u949f\uff0c\u5c0f\u5b69\u513f\u7b11\u5f97\u50bb\u91cc\u50bb\u6c14\u7684\uff0c\u8eab\u5b50\u53ef\u5927\u597d\u4e86\u3002\u5979\u542c\u8bf4\u9752\u9752\u4e2d\u6bd2\uff0c\u5fd9\u628a\u513f\u5b50\u672a\u670d\u5b8c\u7684\u832f\u82d3\u9996\u4e4c\u4e38\u62ff\u51fa\u6765\u7ed9\u5979\u670d\u4e0b\u3002\u9752\u9752\u5b89\u9759\u4e86\u4e00\u9635\uff0c\u6c89\u6c89\u7761\u53bb\u3002 \u3000\u3000\u5929\u9ed1\u540e\uff0c\u9ec4\u771f\u7684\u5927\u5f1f\u5b50\u9886\u7740\u516b\u540d\u5e08\u5f1f\u3001\u4e24\u4e2a\u513f\u5b50\u5230\u4e86\u5c71\u4e0a\u3002\u4ed6\u5148\u5411\u6728\u6851\u9053\u4eba\u884c\u793c\uff0c\u7136\u540e\u53e9\u89c1\u5e08\u7236\u3001\u4e8c\u5e08\u53d4\u3001\u4e8c\u5e08\u5a18\u3002 \u3000\u3000\u4ed6\u89c1\u8881\u627f\u5fd7\u5e74\u7eaa\u751a\u8f7b\uff0c\u81ea\u5df1\u5927\u513f\u5b50\u8fd8\u5927\u8fc7\u4ed6\uff0c\u8981\u8dea\u4e0b\u5411\u4ed6\u78d5\u5934\uff0c\u5b9e\u5728\u6709\u70b9\u4e0d\u613f\uff0c\u53eb\u4e86\u4e00\u58f0\u201c\u5e08\u53d4\uff01\u201d\u4e0d\u7981\u6709\u70b9\u8fdf\u7591\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u8fd9\u5e08\u4f84\u56db\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u864e\u80cc\u718a\u8170\uff0c\u7b4b\u9aa8\u4f3c\u94c1\uff0c\u7ad9\u7740\u51e0\u4e4e\u9ad8\u8fc7\u81ea\u5df1\u4e00\u4e2a\u5934\uff0c\u5148\u6697\u6697\u559d\u4e86\u4e00\u58f0\u5f69\uff0c\u5fc3\u60f3\u5927\u5e08\u54e5\u5982\u6b64\u82f1\u96c4\uff0c\u786e\u8981\u8fd9\u6837\u5a01\u98ce\u7684\u4eba\u7269\u624d\u80fd\u505a\u4ed6\u638c\u95e8\u5f1f\u5b50\uff0c\u5d14\u5e0c\u654f\u4eba\u65e2\u83bd\u649e\uff0c\u6b66\u529f\u53c8\u5dee\uff0c\u548c\u8fd9\u4f4d\u5e08\u4f84\u53ef\u5dee\u5f97\u8fdc\u4e86\uff0c\u89c1\u4ed6\u4f5c\u52bf\u8981\u8dea\uff0c\u5fd9\u4f38\u624b\u62e6\u4f4f\uff0c\u5411\u9ec4\u771f\u5176\u4f59\u516b\u540d\u5f1f\u5b50\u6446\u4e86\u6446\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u522b\u591a\u793c\u5566\uff01\u201d\u5d14\u5e0c\u654f\u5728\u4e00\u65c1\u4ecb\u7ecd\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u5927\u5e08\u5144\u59d3\u51af\u540d\u96be\u654c\uff0c\u6c5f\u6e56\u4e0a\u4eba\u79f0\u516b\u9762\u5a01\u98ce\u3002\u201d\u8881\u627f\u5fd7\u9053\uff1a\u201c\u51af\u5144\u5b9a\u662f\u5f97\u7740\u5927\u5e08\u54e5\u771f\u4f20\u4e86\u3002\u201d \u3000\u3000\u9ec4\u771f\u773c\u89c1\u51af\u96be\u654c\u4e0d\u80af\u5bf9\u5c0f\u5e08\u53d4\u4e0b\u8dea\uff0c\u5fc3\u60f3\u4ed6\u5df2\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u4e5f\u5c31\u4e0d\u52a0\u52c9\u5f3a\u3002\u4ed6\u5411\u6765\u6ed1\u7a3d\u73a9\u4e16\uff0c\u4e8e\u8fd9\u4e9b\u793c\u6570\u4e5f\u5e76\u4e0d\u8003\u7a76\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u5e08\u7236\u7b97\u76d8\u7cbe\uff0c\u6559\u51fa\u6765\u7684\u5f92\u513f\u4e5f\u5c31\u7231\u5360\u4fbf\u5b9c\uff0c\u5411\u5c0f\u5e08\u53d4\u78d5\u51e0\u4e2a\u5934\uff0c\u53ef\u5c31\u592a\u5403\u4e8f\u4e86\u3002\u201d \u3000\u3000\u51af\u96be\u654c\u7ed9\u5e08\u7236\u8bf4\u5f97\u4e0d\u597d\u610f\u601d\uff0c\u4fbf\u8981\u5411\u8881\u627f\u5fd7\u8dea\u5012\u3002\u8881\u627f\u5fd7\u6025\u5fd9\u62e6\u4f4f\u3002\u51af\u96be\u654c\u5f53\u4e0b\u547d\u5927\u513f\u5b50\u51af\u4e0d\u7834\u3001\u4e8c\u513f\u5b50\u51af\u4e0d\u6467\u5411\u6728\u6851\u9053\u4eba\u4e0e\u5f52\u3001\u8881\u4e24\u4f4d\u5e08\u53d4\u7956\u3001\u4ee5\u53ca\u6885\u5251\u548c\u7b49\u5e08\u53d4\u4f9d\u6b21\u62dc\u89c1\u4e86\u3002 \u3000\u3000\u51af\u4e0d\u7834\u4eca\u5e74\u4e8c\u5341\u4e09\u5c81\uff0c\u51af\u4e0d\u6467\u4e8c\u5341\u4e00\u5c81\uff0c\u4e24\u4eba\u5728\u7518\u51c9\u4e00\u5e26\u4ed7\u7740\u7236\u4eb2\u7684\u540d\u5934\uff0c\u6b66\u6797\u4e2d\u4e2a\u4e2a\u8ba9\u4ed6\u54e5\u513f\u4fe9\u4e09\u5206\u3002\u4ed6\u4e8c\u4eba\u624b\u4e0b\u4e5f\u786e\u6709\u70b9\u771f\u529f\u592b\uff0c\u8fd9\u65f6\u5019\u89c1\u8881\u627f\u5fd7\u4e0d\u8fc7\u4e8c\u5341\u5c81\u5de6\u53f3\uff0c\u5c45\u7136\u957f\u7740\u81ea\u5df1\u4e24\u8f88\uff0c\u5fc3\u4e2d\u597d\u4e0d\u670d\u6c14\uff0c\u53c8\u89c1\u4ed6\u7ea2\u80bf\u4e86\u53cc\u773c\uff0c\u51fa\u6765\u89c1\u5ba2\u65f6\u6cea\u75d5\u672a\u5e72\uff0c\u5fc3\u60f3\u6b64\u4eba\u4e0d\u77e5\u751a\u4e48\u4e8b\u5403\u4e86\u4e8f\uff0c\u8fd9\u822c\u54ed\u54ed\u557c\u557c\u7684\uff0c\u8113\u5305\u4e4b\u6781\uff0c\u82f1\u96c4\u597d\u6c49\u6253\u843d\u4e86\u7259\u9f7f\u548c\u8840\u541e\uff0c\u54ea\u6709\u53d7\u4e86\u4eba\u6b3a\u4fae\u4fbf\u54ed\u7684\uff1f\u5bf9\u4ed6\u66f4\u52a0\u77a7\u4e0d\u5728\u773c\u91cc\u3002\u4ed6\u4e8c\u4eba\u548c\u5f52\u8f9b\u6811\u95e8\u4e0b\u7684\u5f1f\u5b50\u4e2a\u4e2a\u4ea4\u597d\uff0c\u77e5\u9053\u5c31\u4e2d\u5b59\u4ef2\u541b\u6700\u662f\u5fc3\u50b2\u597d\u80dc\uff0c\u6b66\u529f\u4e5f\u5f3a\u3002\u5f53\u665a\u54e5\u513f\u4fe9\u5077\u5077\u5546\u8bae\uff0c\u8981\u6311\u62e8\u5b59\u5e08\u59d1\u53bb\u548c\u8fd9\u5c0f\u5e08\u53d4\u7956\u6bd4\u8bd5\u4e00\u573a\uff0c\u53eb\u4ed6\u51fa\u4e00\u4e2a\u4e11\uff0c\u4e07\u4e00\u7ed9\u7236\u4eb2\u6216\u5e08\u7956\u77e5\u9053\u4e86\uff0c\u4e5f\u602a\u4e0d\u5230\u5144\u5f1f\u4fe9\u5934\u4e0a\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e24\u5144\u5f1f\u4e00\u65e9\u8d77\u6765\uff0c\u6e9c\u5230\u5916\u9762\u53bb\u627e\u5b59\u4ef2\u541b\uff0c\u8fce\u9762\u649e\u89c1\u516b\u5e08\u53d4\u77f3\u9a8f\u3002\u4ed6\u4e5f\u662f\u4e2a\u5e74\u5c11\u597d\u4e8b\u4e4b\u4eba\uff0c\u6b66\u529f\u548c\u51af\u6c0f\u5144\u5f1f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u559d\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u54e5\u513f\u4fe9\u63a2\u5934\u63a2\u8111\u7684\u627e\u751a\u4e48\uff1f\u201d\u51af\u4e0d\u6467\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5728\u627e\u5b59\u5e08\u59d1\u5462\uff0c\u542c\u8bf4\u5979\u5728\u5c71\u4e1c\u5e72\u6389\u4e86\u4e0d\u5c11\u6e24\u6d77\u6d3e\u7684\u4eba\uff0c\u8981\u8bf7\u5979\u8bf4\u6765\u542c\u542c\u3002\u201d\u77f3\u9a8f\u559c\u9053\uff1a\u201c\u597d\u554a\uff0c\u521a\u624d\u6211\u89c1\u5979\u5728\u5c71\u90a3\u8fb9\uff0c\u6b63\u8ddf\u6885\u5e08\u54e5\u7ec3\u6b66\u5462\u3002\u201d \u3000\u3000\u4e09\u4eba\u5174\u51b2\u51b2\u7684\u8d76\u5f80\u5c71\u540e\u3002\u51af\u6c0f\u5144\u5f1f\u5fc3\u4e2d\u76d8\u7b97\uff0c\u7528\u751a\u4e48\u8bdd\u6765\u6311\u52a8\u5b59\u4ef2\u541b\u53bb\u627e\u90a3\u8881\u5c0f\u5e08\u53d4\u7956\u6bd4\u6b66\u3002\u51af\u4e0d\u6467\u6084\u58f0\u9053\uff1a\u201c\u8981\u662f\u5b59\u5e08\u59d1\u8fd8\u5728\u7ec3\u5251\uff0c\u54b1\u4eec\u5c31\u8bf4\u662f\u90a3\u59d3\u8881\u7684\u8bf4\u7684\uff0c\u8fd9\u4e00\u8def\u3001\u90a3\u4e00\u8def\u90fd\u4f7f\u5f97\u4e0d\u5bf9\u3002\u201d\u51af\u4e0d\u7834\u7b11\u7740\u70b9\u5934\u3002 \u3000\u3000\u521a\u8f6c\u5230\u5c71\u540e\uff0c\u5ffd\u542c\u5f97\u5b59\u4ef2\u541b\u6b63\u5728\u5389\u58f0\u53eb\u9a82\uff0c\u8fd9\u4e00\u4e0b\u5927\u51fa\u4e09\u4eba\u610f\u5916\uff0c\u5fd9\u62d4\u8db3\u8d76\u53bb\uff0c\u53ea\u89c1\u5b59\u4ef2\u541b\u633a\u7740\u5355\u94a9\uff0c\u6b63\u5728\u8ffd\u9010\u4e00\u4eba\u3002 \u3000\u3000\u6ce8\uff1a\u674e\u81ea\u6210\u653b\u7834\u5317\u4eac\u4e8b\u8ff9\uff0c\u5f53\u65f6\u6587\u58eb\u7b14\u5f55\u89c1\u95fb\u800c\u6d41\u4f20\u540e\u4e16\u8005\u751a\u591a\u3002\u8bf8\u4e66\u4f5c\u8005\u5bf9\u674e\u81ea\u6210\u65e0\u4e0d\u6781\u4e3a\u4ec7\u89c6\uff0c\u6587\u4e2d\u81ea\u591a\u5938\u5f20\u53ca\u8bec\u8511\uff0c\u672a\u53ef\u5c3d\u4fe1\u3002\u4f46\u95ef\u519b\u521d\u65f6\u7eaa\u5f8b\u4e25\u660e\uff0c\u8fdb\u5317\u4eac\u540e\u4fbf\u5373\u8150\u8d25\uff0c\u5f53\u5c5e\u4e8b\u5b9e\uff0c\u5426\u5219\u4e0d\u81f4\u6210\u529f\u540e\u4fbf\u5373\u4e00\u8d25\u6d82\u5730\u3002\u4ee5\u4e0b\u6240\u5f55\u4e3a\u300a\u660e\u5b63\u5317\u7565\u300b\u4e00\u4e66\u4e2d\u82e5\u5e72\u8bb0\u8f7d\uff1a\uff08\u6587\u4e2d\u6240\u8c13\u201c\u8d3c\u201d\u6307\u95ef\u519b\u800c\u8a00\uff0c\u53ef\u89c1\u4f5c\u8005\u6781\u6709\u504f\u89c1\u3002\uff09 \u3000\u3000\u25cb\u6627\u723d\uff0c\u9634\u4e91\u56db\u5408\uff0c\u57ce\u5916\u70df\u7130\u969c\u5929\uff0c\u5fae\u96e8\u4e0d\u7edd\uff0c\u96fe\u8ff7\uff0c\u4fc4\u5fae\u96ea\uff0c\u57ce\u9677\u3002\u6216\u8c13\u5148\u6709\u4eba\u4f0f\u5185\uff0c\u901a\u592a\u76d1\u66f9\u5316\u6df3\u5f1f\u66f9\u4e8c\u516c\u5185\u5e94\u5f00\u95e8\uff1b\u4e00\u4e91\uff1a\u592a\u76d1\u738b\u76f8\u5c27\u7387\u5185\u5175\u5343\u4eba\u51fa\u8fce\u8d3c\u3002 \u3000\u3000\u8d3c\u5c06\u5218\u5b97\u654f\u6574\u519b\u5165\uff0c\u519b\u4e2d\u751a\u8083\u3002\u2026\u2026\u592a\u76d1\u66f9\u5316\u6df3\u540c\u5175\u90e8\u5c1a\u4e66\u5f20\u7f19\u5f66\u5f00\u5f70\u4e49\u95e8\u8fce\u8d3c\u3002\u2026\u2026\u5927\u62b5\u4eac\u57ce\u4e4b\u9677\uff0c\u591a\u7531\u5978\u4eba\u5185\u5e94\u8033\u3002\u2026\u2026\u5df2\u800c\u8d3c\u5927\u547c\u5f00\u95e8\u8005\u4e0d\u6740\uff0c\u4e8e\u662f\u58eb\u6c11\u5404\u6267\u9999\u7acb\u95e8\uff0c\u8d3c\u8fc7\uff0c\u4f0f\u8fce\uff0c\u95e8\u4e0a\u4ff1\u7c98\u201c\u987a\u6c11\u201d\uff0c\u5927\u4e66\u201c\u6c38\u660c\u5143\u5e74\u987a\u5929\u738b\u4e07\u4e07\u5c81\u201d\u3002 \u3000\u3000\u25cb\u8d3c\u5c3d\u653e\u9a6c\u5175\u5165\u57ce\uff0c\u4e71\u5165\u4eba\u5bb6\u3002\u8bf8\u5c06\u519b\u671b\u9ad8\u95e8\u5927\u7b2c\uff0c\u5373\u5165\u636e\u4e4b\u3002\u5218\u5b97\u654f\u636e\u7530\u5b8f\u7b2c\uff0c\u674e\u725f\u636e\u5468\u594e\u7b2c\u3002 \u3000\u3000\u25cb\u638c\u4e66\u5bab\u4eba\u675c\u6c0f\u3001\u9648\u6c0f\u3001\u7aa6\u6c0f\u4e3a\u81ea\u6210\u6240\u53d6\uff0c\u800c\u7aa6\u6c0f\u5c24\u5ba0\uff0c\u53f7\u7aa6\u5983\u3002\u53c8\u6709\u5f20\u6c0f\uff0c\u4ea6\u5b16\u4e4b\u3002\u81ea\u6210\u96c6\u5bab\u5973\u5206\u8d50\u968f\u6765\u8bf8\u8d3c\uff0c\u6bcf\u8d3c\u5404\u4e09\u5341\u4eba\u3002\u725b\u91d1\u661f\u3001\u5b8b\u732e\u7b56\u7b49\u4ea6\u5404\u6570\u4eba\u3002 \u3000\u3000\u25cb\u56db\u6708\u521d\u4e00\u65e5\uff0c\u5b8b\u732e\u7b56\u4e91\uff1a\u201c\u5929\u8c61\u60e8\u5217\uff0c\u65e5\u8272\u65e0\u5149\uff0c\u4e9f\u5b9c\u505c\u5211\u3002\u201d\u521d\u4e03\u65e5\uff0c\u81ea\u6210\u8fc7\u5b97\u654f\u7b2c\uff0c\u89c1\u5ead\u9662\u5939\u4e09\u767e\u591a\u4eba\uff0c\u54c0\u53f7\u534a\u7edd\u3002\u81ea\u6210\u4e91\uff1a\u201c\u5929\u8c61\u793a\u8b66\uff0c\u5b8b\u519b\u5e08\u8a00\u5f53\u7701\u5211\uff0c\u5b9c\u914c\u653e\u4e4b\u3002\u201d\u6b64\u4e2d\u7f19\u7ec5\u5341\u4e00\uff0c\u4f59\u7686\u6742\u6d41\u6b66\u5f01\u53ca\u6548\u52b3\u529e\u4e8b\u4eba\u3002\u91ca\u5343\u4f59\u4eba\uff0c\u7136\u6b7b\u8005\u8fc7\u534a\u77e3\u3002 \u3000\u3000\u25cb\u8d3c\u521d\u5165\u57ce\uff0c\u4e0d\u751a\u6740\u622e\u3002\u6570\u65e5\u540e\u5927\u8086\u6740\u622e\u2026\u2026\u8d3c\u5175\u6ee1\u8def\uff0c\u624b\u643a\u9ebb\u7d22\uff0c\u89c1\u9762\u7a0d\u9b41\u80a5\uff0c\u5373\u7591\u6709\u8d22\uff0c\u7cfb\u9888\u5f81\u8d3f\u3002\u6709\u4e2d\u9014\u501f\u8d37\u800c\u91ca\u8005\uff0c\u4ea6\u6709\u62bc\u81f3\u5176\u5bb6\uff0c\u4efb\u5176\u62e3\u62e9\u800c\u540e\u91ca\u8005\u3002\u82e5\u7f1a\u81f3\u5218\u5b97\u654f\u4f2a\u5e9c\u4fbf\u65e0\u751f\u7406\u3002 \u3000\u3000\u25cb\u8d3c\u521d\u5165\u57ce\u65f6\uff0c\u5148\u5047\u5f20\u6740\u622e\u4e4b\u7981\uff0c\u5982\u6709\u6deb\u63a0\u6c11\u95f4\u8005\uff0c\u7acb\u884c\u51cc\u8fdf\u3002\u5047\u5c06\u72af\u7f6a\u4e4b\u5bc7\u6740\u6b7b\u56db\u4eba\uff0c\u5206\u4e3a\u4e94\u6bb5\uff0c\u636e\u79f0\u4ee5\u6deb\u6740\u4e4b\u6545\u4e5f\u3002\u6c11\u95f4\u8bef\u4fe1\uff0c\u9042\u5b89\u5fc3\u5f00\u5e97\u5e02\uff0c\u563b\u563b\u81ea\u82e5\u2026\u2026 \u3000\u3000\u56db\u4e94\u65e5\u540e\u6063\u884c\u6740\u63a0\u3002\u5148\u4ee4\u5341\u5bb6\u4e00\u4fdd\uff0c\u5982\u6709\u4e00\u5bb6\u9003\u4ea1\uff0c\u5341\u5bb6\u540c\u65a9\u3002\u5341\u5bb6\u4e4b\u5185\u6709\u5bcc\u6237\u8005\uff0c\u95ef\u8d3c\u81ea\u884c\u70b9\u53d6\u7c4d\u6ca1\uff0c\u5176\u4e2d\u4e0b\u4e4b\u5bb6\uff0c\u542c\u5404\u8d3c\u5206\u63a0\u3002\u53c8\u6c11\u95f4\u9a6c\u9aa1\u94dc\u5668\uff0c\u4ff1\u8d23\u4ee4\u8f93\u8425\uff0c\u4e8e\u662f\u6ee1\u57ce\u767e\u59d3\uff0c\u5bb6\u5bb6\u503e\u7aed\u3002 \u3000\u3000\u25cb\u8d3c\u5175\u521d\u5165\u4eba\u5bb6\uff0c\u66f0\u501f\u9505\u7228\u3002\u5c11\u7109\uff0c\u66f0\u501f\u5e8a\u7720\u3002\u9877\u4e4b\uff0c\u66f0\u501f\u6c5d\u59bb\u5973\u59ca\u59b9\u4f5c\u4f34\u3002\u85cf\u533f\u8005\uff0c\u62bc\u7537\u5b50\uff0c\u904d\u641c\uff0c\u4e0d\u5f97\u4e0d\u6b62\u3002\u7231\u5219\u7f6e\u697c\u9a6c\u4e0a\u3002\u6709\u4e00\u8d3c\u631f\u4e09\u56db\u4eba\u8005\uff0c\u53c8\u6709\u8eab\u6402\u4e00\u4eba\u800c\u4f59\u9a6c\u631f\u5e26\u4e8c\u4e09\u4eba\u8005\u3002\u4e0d\u4ece\u5219\u6b7b\uff0c\u4ece\u800c\u4e0d\u5f53\u610f\u8005\u4ea6\u6b7b\u3002\u4e00\u4eba\u800c\u4e0d\u582a\u4f17\u5b32\u8005\u4ea6\u6b7b\u3002\u5b89\u798f\u80e1\u540c\u4e00\u591c\u5987\u5973\u6b7b\u8005\u4e09\u767e\u4e03\u5341\u4f59\u4eba\u3002\u964d\u5b98\u59bb\u59be\uff0c\u4ff1\u4e0d\u80fd\u514d\u3002\u2026\u2026\u8d3c\u5c06\u5404\u8e1e\u5de8\u5ba4\u3002 \u3000\u3000\u7c4d\u6ca1\u5b50\u5973\u4e3a\u4e50\uff0c\u800c\u58eb\u5175\u5145\u585e\u5df7\u964c\uff0c\u4ee5\u641c\u9a6c\u641c\u94dc\u4e3a\u540d\uff0c\u6cbf\u95e8\u6deb\u63a0\u3002\u7a0d\u8fdd\u8005\uff0c\u5175\u52a0\u5176\u9888\u3002\u95e8\u536b\u751a\u4e25\uff0c\u5373\u6b32\u8131\u514d\uff0c\u4e0d\u53ef\u5f97\u4e5f\u3002\u4e0d\u987e\u9752\u5929\u767d\u65e5\uff0c\u6063\u884c\u6deb\u620f\u3002 \u3000\u3000\u25cb\u8d3c\u65e0\u4ed6\u4f0e\u4fe9\uff0c\u5230\u5904\u5148\u7528\u8d3c\u515a\u626e\u4f5c\u5f80\u6765\u5ba2\u5546\uff0c\u56db\u5904\u4f20\u5e03\uff0c\u8bf4\u8d3c\u201c\u4e0d\u6740\u4eba\uff0c\u4e0d\u7231\u8d22\u3002\u4e0d\u5978\u6deb\uff0c\u4e0d\u62a2\u63a0\uff0c\u5e73\u4e70\u5e73\u5356\uff0c\u8832\u514d\u94b1\u7cae\uff0c\u4e14\u5c06\u5b98\u5bb6\u94f6\u94b1\u5206\u8d48\u7a77\u6c11\uff0c\u9887\u7231\u65af\u6587\u79c0\u624d\uff0c\u8fce\u8005\u5148\u8d4f\u94f6\u5e01\uff0c\u55e3\u5373\u8003\u6821\uff0c\u4e00\u7b49\u4f5c\u5e9c\uff0c\u4e8c\u7b49\u4f5c\u53bf\u3002\u201d \u3000\u3000\u2026\u2026\u4e8e\u662f\u4e0d\u901a\u79c0\u624d\u7686\u671b\u505a\u5b98\uff1b\u65e0\u77e5\u7a77\u6c11\u7686\u671b\u5f97\u94b1\uff1b\u62d6\u6b20\u94b1\u7cae\u8005\u7686\u671b\u8832\u514d\u3002\u771f\u4fdd\u95f4\u6c11\u8c23\u6709\u201c\u5f00\u4e86\u5927\u95e8\u8fce\u95ef\u738b\uff0c\u95ef\u738b\u6765\u65f6\u4e0d\u7eb3\u7cae\u201d\u7b49\u8bed\uff0c\u56e0\u6b64\u8d3c\u8ba1\u5f97\u552e\u3002 \u3000\u3000\u25cb\u8d3c\u5175\u5165\u57ce\u8005\u56db\u5341\u4f59\u4e07\uff0c\u5404\u8086\u63b3\u63a0\u3002\u81ea\u6210\u6216\u7981\u6b62\uff0c\u8f84\u54d7\u66f0\uff1a\u201c\u7687\u5e1d\u8ba9\u6c5d\u505a\uff0c\u91d1\u94f6\u5987\u5973\u4e0d\u8ba9\u6211\u8f88\u8036\uff1f\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c \u4e8c\u5341 \u56de\u3000\u7a7a\u8d1f\u5b89\u90a6\u5fd7 \u9042\u541f\u53bb\u56fd\u884c",content:(0,t.jsx)(t.Fragment,{children:"\u90a3\u4eba\u662f\u4e2a\u4e09\u5341\u4f59\u5c81\u7684\u7537\u5b50\uff0c\u795e\u8272\u6124\u6fc0\uff0c\u4e00\u9762\u201c\u8d3c\u5a46\u5a18\uff0c\u6076\u8d31\u4eba\u201d\u7684\u7834\u53e3\u4e71\u9a82\uff0c\u4e00\u9762\u6301\u5200\u72e0\u6597\u3002\u8fd9\u4eba\u6b66\u529f\u4e0d\u53ca\u5b59\u4ef2\u541b\uff0c\u6253\u4e00\u9635\uff0c\u9003\u4e00\u9635\uff0c\u53ef\u662f\u5e76\u4e0d\u5954\u9003\u4e0b\u5c71\uff0c\u53ea\u8981\u7a0d\u89c1\u7a7a\u9699\uff0c\u53c8\u56de\u8eab\u62da\u547d\u731b\u780d\u72e0\u6740\u3002\u51af\u4e0d\u6467\u9053\uff1a\u201c\u54b1\u4eec\u4e0a\u53bb\u622a\u4f4f\u8fd9\u5c0f\u5b50\uff0c\u522b\u8ba9\u4ed6\u8dd1\u4e86\uff01\u201d\u77f3\u9a8f\u9053\uff1a\u201c\u5b59\u5e08\u59ca\u4e0d\u7231\u522b\u4eba\u5e2e\u624b\uff0c\u8fd9\u5c0f\u5b50\u5979\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u90a3\u4eba\u72c2\u53eb\uff1a\u201c\u4f60\u6740\u4e86\u6211\u59bb\u5b50\u548c\u4e09\u4e2a\u513f\u5973\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u600e\u4e48\u8fde\u6211\u4e03\u5341\u591a\u5c81\u7684\u8001\u5a18\u4e5f\u90fd\u5bb3\u4e86\uff1f\u201d\u5b59\u4ef2\u541b\u8138\u4e0a\u72b9\u5982\u7f69\u4e86\u4e00\u5c42\u4e25\u971c\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u79cd\u65e0\u803b\u72c2\u5f92\uff0c\u5bb6\u91cc\u4eba\u518d\u591a\u4e9b\uff0c\u4e5f\u4e00\u8d77\u6740\u4e86\uff01\u201d\u4e24\u4eba\u6108\u6597\u6108\u70c8\u3002 \u3000\u3000\u51af\u4e0d\u7834\u5ffd\u9053\uff1a\u201c\u5b59\u5e08\u59d1\u600e\u4e48\u4e0d\u7528\u5251\uff1f\u8fd9\u5355\u94a9\u4f7f\u6765\u597d\u50cf\u5f88\u4e0d\u987a\u624b\u3002\u201d\u77f3\u9a8f\u4e5f\u89c1\u5230\u5979\u5175\u5203\u751a\u4e0d\u5408\u7528\uff0c\u5012\u8f6c\u81ea\u5df1\u957f\u5251\uff0c\u67c4\u524d\u5203\u5185\u53eb\u9053\uff1a\u201c\u5b59\u5e08\u59ca\uff0c\u63a5\u5251\uff01\u201d\u957f\u5251\u5411\u5b59\u4ef2\u541b\u63b7\u53bb\u3002 \u3000\u3000\u5ffd\u5730\u4e00\u4eba\u4ece\u65c1\u8fb9\u6811\u4e1b\u4e2d\u8dc3\u51fa\uff0c\u4f38\u624b\u5728\u534a\u8def\u4e0a\u5c06\u5251\u63a5\u4e86\u8fc7\u53bb\u3002\u4e09\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u89c1\u90a3\u4eba\u8f7b\u8eab\u529f\u592b\u8fc5\u901f\u7f8e\u5999\uff0c\u7ad9\u5b9a\u8eab\u5b50\u540e\uff0c\u770b\u6e05\u695a\u539f\u6765\u662f\u5f52\u6c0f\u95e8\u4e0b\u7684\u6ca1\u5f71\u5b50\u6885\u5251\u548c\u3002\u77f3\u9a8f\u53eb\u4e86\u58f0\uff1a\u201c\u6885\u5e08\u54e5\uff01\u201d\u6885\u5251\u548c\u70b9\u4e86\u70b9\u5934\uff0c\u5c06\u5251\u63b7\u8fd8\u7ed9\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u5b59\u5e08\u59b9\u53e6\u7ec3\u5175\u5203\uff0c\u5979\u4e0d\u7528\u5251\uff01\u201d\u77f3\u9a8f\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4ed6\u4e0d\u77e5\u5b59\u4ef2\u541b\u56e0\u6ee5\u4f24\u65e0\u8f9c\uff0c\u5df2\u88ab\u7a46\u8001\u7956\u7981\u6b62\u7528\u5251\u3002 \u3000\u3000\u77f3\u9a8f\u518d\u770b\u76f8\u6597\u7684\u4e24\u4eba\u65f6\uff0c\u90a3\u7537\u5b50\u867d\u7136\u60c5\u6025\u62da\u547d\uff0c\u6bd5\u7adf\u6b66\u529f\u900a\u4e86\u4e00\u7b79\uff0c\u6e10\u6e10\u5200\u6cd5\u6563\u4e71\u3002\u6597\u5230\u9163\u5904\uff0c\u5b59\u4ef2\u541b\u98de\u8d77\u5de6\u8db3\uff0c\u6b63\u4e2d\u4ed6\u53f3\u624b\u624b\u8155\uff0c\u4ed6\u624b\u4e2d\u5355\u5200\u76f4\u98de\u8d77\u6765\u3002\u5b59\u4ef2\u541b\u94a9\u5c16\u5df2\u62b5\u5728\u4ed6\u80f8\u524d\uff0c\u5f85\u8981\u5411\u524d\u523a\u51fa\uff0c\u6885\u5251\u548c\u6025\u53eb\uff1a\u201c\u4f4f\u624b\uff01\u201d\u5b59\u4ef2\u541b\u4e00\u6014\uff0c\u90a3\u4eba\u6025\u5411\u65c1\u95ea\uff0c\u5411\u5c71\u4e0b\u9003\u53bb\u3002\u6885\u5251\u548c\u7b11\u9053\uff1a\u201c\u9976\u4e86\u4ed6\u5427\uff0c\u597d\u8ba9\u5e08\u7956\u5938\u5956\u4f60\u4e00\u756a\u3002\u201d\u5b59\u4ef2\u541b\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u4e0d\u6599\u90a3\u4eba\u9003\u51fa\u6570\u5341\u6b65\uff0c\u6307\u7740\u5b59\u4ef2\u541b\u53c8\u662f\u201c\u8d3c\u5a46\u5a18\uff0c\u81ed\u8d31\u4eba\u201d\u7684\u6bd2\u9a82\u8d77\u6765\u3002\u8fd9\u4e00\u6765\uff0c\u8fde\u6885\u5251\u548c\u3001\u77f3\u9a8f\u7b49\u4eba\u90fd\u52a8\u4e86\u6012\u3002\u51af\u4e0d\u6467\u559d\u9053\uff1a\u201c\u751a\u4e48\u4e1c\u897f\uff0c\u5230\u534e\u5c71\u6765\u6492\u91ce\uff01\u201d\u63d0\u8d77\u94c1\u97ad\u8ffd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u66f4\u662f\u6012\u706b\u5927\u70bd\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u6740\u8fd9\u755c\u751f\u8a93\u4e0d\u4e3a\u4eba\uff0c\u5b81\u53ef\u518d\u7ed9\u5e08\u7956\u524a\u6389\u4e00\u6839\u6307\u5934\uff01\u201d\u633a\u9493\u53c8\u8ffd\u3002\u6885\u5251\u548c\u6015\u5979\u518d\u53c8\u6740\u4eba\u53d7\u8d23\uff0c\u5fc3\u60f3\u5148\u6293\u4f4f\u90a3\u5bb6\u4f19\u9971\u6253\u4e00\u987f\uff0c\u8ba9\u5e08\u59b9\u51fa\u4e86\u8fd9\u53e3\u6076\u6c14\uff0c\u4e5f\u5c31\u662f\u4e86\uff0c\u5f53\u4e0b\u659c\u523a\u91cc\u515c\u622a\u51fa\u53bb\u3002\u4ed6\u8f7b\u8eab\u529f\u592b\u8fdc\u80dc\u8bf8\u4eba\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u6284\u5728\u90a3\u4eba\u5934\u91cc\u3002 \u3000\u3000\u90a3\u4eba\u89c1\u52bf\u5934\u4e0d\u5bf9\uff0c\u5ffd\u5730\u6298\u5411\u5de6\u8fb9\u5c94\u8def\u3002\u77f3\u9a8f\u4e0e\u51af\u6c0f\u5144\u5f1f\u6697\u5668\u7eb7\u7eb7\u51fa\u624b\u3002\u51af\u4e0d\u7834\u4e00\u679a\u98de\u8757\u77f3\u5411\u4ed6\u540e\u5fc3\u63b7\u53bb\u3002\u90a3\u4eba\u8eab\u624b\u4e5f\u751a\u77eb\u5065\uff0c\u542c\u98ce\u8fa8\u5668\uff0c\u5f80\u53f3\u907f\u8ba9\uff0c\u4f46\u55e4\u7684\u4e00\u58f0\uff0c\u540e\u80ef\u4e0a\u7ec8\u4e8e\u4e2d\u4e86\u77f3\u9a8f\u7684\u8896\u7bad\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8dcc\u5012\u5728\u5730\u3002 \u3000\u3000\u6885\u5251\u548c\u62a2\u4e0a\u524d\u53bb\uff0c\u4f38\u624b\u6309\u4e0b\uff0c\u7a81\u7136\u95f4\u8eab\u65c1\u98ce\u58f0\u54cd\u5904\uff0c\u90a3\u4eba\u5ffd\u5730\u817e\u8eab\u98de\u51fa\u3002\u6885\u5251\u548c\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u8eab\u5b50\u4e00\u7f29\uff0c\u8fd9\u624d\u770b\u660e\u767d\uff0c\u539f\u6765\u90a3\u4eba\u662f\u88ab\u4eba\u7528\u6570\u5341\u6761\u7ef3\u7d22\u7f20\u4f4f\uff0c\u626f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5b59\u4ef2\u541b\u7b49\u4eba\u4e5f\u5df2\u8d76\u5230\uff0c\u53ea\u89c1\u51fa\u624b\u76f8\u6551\u7684\u7adf\u662f\u4e2a\u7f8e\u8c8c\u5973\u5b50\u3002\u4f46\u89c1\u5979\u4e00\u8eab\u96ea\u767d\u8863\u886b\uff0c\u957f\u53d1\u5782\u80a9\uff0c\u8d64\u7740\u53cc\u8db3\uff0c\u624b\u8155\u4e0a\u8db3\u8e1d\u4e0a\u90fd\u6234\u4e86\u9ec4\u91d1\u956f\u5b50\uff0c\u6253\u626e\u975e\u6c49\u975e\u5937\uff0c\u7b11\u541f\u541f\u7684\u7ad9\u7740\uff0c\u53f3\u624b\u768e\u767d\u5982\u96ea\uff0c\u63e1\u7740\u4e00\u675f\u975e\u4e1d\u975e\u9769\u7684\u6570\u5341\u6761\u7ef3\u7d22\u3002\u8eab\u540e\u7ad9\u7740\u4e00\u4e2a\u5999\u9f84\u5c11\u5973\uff0c\u5168\u8eab\u88f9\u5728\u4e00\u88ad\u767d\u72d0\u88d8\u4e4b\u4e2d\uff0c\u5934\u4e0a\u4e5f\u6234\u4e86\u767d\u72d0\u76ae\u5e3d\u5b50\u3002\u867d\u662f\u7709\u76ee\u5982\u753b\uff0c\u6e05\u4e3d\u7edd\u4f26\uff0c\u4f46\u5bb9\u8272\u751a\u662f\u6194\u60b4\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u6b63\u662f\u4f55\u60d5\u5b88\u548c\u963f\u4e5d\u3002 \u3000\u3000\u8881\u627f\u5fd7\u7b49\u79bb\u4eac\u6b21\u65e5\uff0c\u80e1\u6842\u5357\u4fbf\u5373\u67e5\u8bbf\u5230\u5b9b\u5e73\u996d\u5e97\u4e2d\u6e29\u6c0f\u56db\u8001\u548c\u4f55\u7ea2\u836f\u3001\u9752\u9752\u7b49\u4eba\u4e4b\u4e8b\uff0c\u56de\u6765\u5411\u5927\u5bb6\u8bf4\u8d77\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u77e5\u9053\u5728\u5899\u89d2\u9489\u4ee5\u6bd2\u7269\uff0c\u662f\u4e94\u6bd2\u6559\u53ec\u96c6\u4eba\u4f17\u5e94\u63f4\u7684\u8baf\u53f7\uff0c\u53ea\u6015\u9752\u9752\u906d\u4e86\u6bd2\u624b\uff0c\u987b\u5f97\u7acb\u5373\u8d76\u53bb\u76f8\u6551\uff0c\u4f55\u51b5\u8881\u627f\u5fd7\u66fe\u5631\u5490\u8981\u643a\u540c\u963f\u4e5d\u79bb\u4eac\u907f\u96be\uff0c\u53ea\u662f\u5979\u4e0d\u613f\u548c\u7a0b\u9752\u7af9\u7b49\u4eba\u5055\u884c\uff0c\u548c\u963f\u4e5d\u4e00\u5546\u91cf\uff0c\u963f\u4e5d\u613f\u610f\u968f\u5979\u524d\u53bb\u6551\u4eba\u3002\u5f53\u665a\u4e24\u4eba\u7559\u4e86\u5c01\u4fe1\uff0c\u6084\u7136\u51fa\u4eac\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u60f3\u96c7\u8f86\u9aa1\u8f66\u7ed9\u963f\u4e5d\u4e58\u5750\uff0c\u4f46\u5175\u8352\u9a6c\u4e71\u4e4b\u9645\uff0c\u518d\u4e5f\u6ca1\u8f66\u592b\u505a\u8fd9\u751f\u610f\u3002\u4f55\u60d5\u5b88\u89c1\u5230\u6709\u4eba\u4e58\u8f66\u51fa\u4eac\uff0c\u4e0d\u7ba1\u4e09\u4e03\u4e8c\u5341\u4e00\uff0c\u628a\u4e58\u5ba2\u8d76\u4e0b\u8f66\u6765\uff0c\u5f3a\u8feb\u8f66\u592b\u9a7e\u8f66\u897f\u884c\u3002\u963f\u4e5d\u867d\u7136\u8eab\u53d7\u91cd\u4f24\uff0c\u4f46\u4f55\u60d5\u5b88\u662f\u6c5f\u6e56\u5927\u884c\u5bb6\uff0c\u51fa\u5f97\u95e8\u6765\u5904\u5904\u90fd\u5360\u4fbf\u5b9c\uff0c\u4e00\u8def\u4e0a\u5374\u4e5f\u672a\u53d7\u98ce\u971c\u4e4b\u82e6\u3002\u4f55\u60d5\u5b88\u9887\u8bc6\u533b\u836f\uff0c\u66f4\u5f53\u5979\u662f\u5c0f\u59b9\u5b50\u822c\u5475\u62a4\u670d\u4f8d\uff0c\u963f\u4e5d\u7684\u81c2\u4f24\u5728\u9014\u4e2d\u9010\u6e10\u75ca\u53ef\u3002\u5065\u9aa1\u8f7b\u8f66\uff0c\u5230\u4e86\u534e\u5c71\u811a\u4e0b\u3002\u4f55\u60d5\u5b88\u5c06\u963f\u4e5d\u8d1f\u5728\u80cc\u4e0a\uff0c\u5c55\u5f00\u8f7b\u529f\uff0c\u8d70\u5f97\u53c8\u5feb\u53c8\u7a33\u3002 \u3000\u3000\u4e0a\u5f97\u5c71\u6765\uff0c\u6b63\u9022\u6d2a\u80dc\u6d77\u88ab\u6697\u5668\u6253\u5012\uff0c\u4f55\u60d5\u5b88\u4fbf\u6325\u51fa\u8f6f\u7ea2\u86db\u7d22\u76f8\u6551\u3002 \u3000\u3000\u6885\u5251\u548c\u4e0e\u5b59\u4ef2\u541b\u7b49\u4e0d\u77e5\u6d2a\u80dc\u6d77\u5df2\u8ddf\u968f\u8881\u627f\u5fd7\uff0c\u66f4\u4e0d\u77e5\u4f55\u60d5\u5b88\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u773c\u89c1\u5979\u602a\u6a21\u602a\u6837\uff0c\u663e\u662f\u5996\u90aa\u4e00\u6d41\uff0c\u5ffd\u4e0a\u534e\u5c71\u6765\u653e\u8086\u6363\u4e71\uff0c\u90fd\u662f\u751a\u4e3a\u607c\u6012\u3002\u5b59\u4ef2\u541b\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u751a\u4e48\u8def\u9053\uff1f\u90fd\u662f\u6e24\u6d77\u6d3e\u7684\u4e48\uff1f\u201d\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u59ca\u59ca\u9ad8\u59d3\u5927\u540d\uff1f\u4e0d\u77e5\u8fd9\u4f4d\u670b\u53cb\u751a\u4e48\u5730\u65b9\u5f97\u7f6a\u4e86\u59ca\u59ca\uff0c\u5c0f\u59b9\u7ed9\u4e24\u4f4d\u8bf4\u548c\u6210\u4e48\uff1f\u201d\u5b59\u4ef2\u541b\u542c\u5979\u8bf4\u8bdd\u5a07\u58f0\u55f2\u6c14\uff0c\u663e\u975e\u7aef\u4eba\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u662f\u751a\u4e48\u90aa\u6559\u5996\u4eba\uff1f\u53ef\u77e5\u9053\u8fd9\u662f\u751a\u4e48\u5730\u65b9\uff1f\u201d\u4f55\u60d5\u5b88\u7b11\u7b11\u4e0d\u7b54\u3002 \u3000\u3000\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u4f55\u59d1\u5a18\uff0c\u8fd9\u8d3c\u5a46\u6700\u662f\u72e0\u6bd2\uff0c\u53eb\u505a\u98de\u5929\u9b54\u5973\u3002\u6211\u8001\u5a46\u548c\u4e09\u4e2a\u513f\u5973\uff0c\u8fd8\u6709\u4e03\u5341\u591a\u5c81\u7684\u8001\u5a18\uff0c\u90fd\u662f\u7ed9\u5979\u4e0b\u6bd2\u624b\u6740\u6b7b\u7684\uff01\u201d\u8bf4\u65f6\u54ac\u7259\u5207\u9f7f\uff0c\u773c\u4e2d\u5982\u8981\u55b7\u51fa\u706b\u6765\u3002 \u3000\u3000\u6885\u5251\u548c\u81ea\u4ece\u90a3\u6b21\u5728\u8881\u627f\u5fd7\u624b\u4e0b\u53d7\u4e86\u4e00\u6b21\u91cd\u5927\u6559\u8bad\u4e4b\u540e\uff0c\u50b2\u6162\u4e4b\u6027\u5df2\u5927\u4e3a\u6536\u655b\uff0c\u4e14\u77e5\u5e08\u7956\u4eca\u65e5\u5fc5\u5230\uff0c\u4e0d\u613f\u591a\u60f9\u4e8b\u7aef\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u4e0b\u5c71\u53bb\u5427\uff0c\u522b\u5728\u8fd9\u91cc\u5570\u5506\u3002\u201d\u51af\u4e0d\u6467\u53eb\u9053\uff1a\u201c\u6211\u5e08\u53d4\u7684\u8bdd\u4f60\u4eec\u542c\u89c1\u4e86\u4e48\uff1f\u5feb\u8d70\u5feb\u8d70\uff01\u201d\u62a2\u5230\u963f\u4e5d\u7684\u8eab\u65c1\uff0c\u4f5c\u52bf\u8981\u8d76\u3002 \u3000\u3000\u963f\u4e5d\u53f3\u624b\u62c4\u7740\u4e00\u6839\u9752\u7af9\u6756\uff0c\u5411\u4ed6\u68ee\u7136\u4e00\u671b\u3002\u5979\u51fa\u8eab\u5e1d\u7687\u4e4b\u5bb6\uff0c\u81ea\u5e7c\u513f\u9890\u6307\u6c14\u4f7f\u60ef\u4e86\u7684\uff0c\u795e\u8272\u95f4\u81ea\u7136\u800c\u7136\u6709\u4e00\u80a1\u5c0a\u8d35\u6c14\u5ea6\u3002\u51af\u4e0d\u6467\u4e0d\u7981\u4e00\u51db\uff0c\u968f\u5373\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u6765\u4f5c\u6b7b\uff01\u201d\u4f38\u624b\u4fbf\u5411\u963f\u4e5d\u63a8\u53bb\u3002\u963f\u4e5d\u53d7\u7a0b\u9752\u7af9\u7684\u70b9\u62e8\u6559\u5bfc\uff0c\u6b66\u529f\u5df2\u9887\u6709\u6839\u5e95\uff0c\u5f53\u5373\u9752\u7af9\u6756\u4e00\u5212\u4e00\u52fe\u3002\u51af\u4e0d\u6467\u5168\u6ca1\u9632\u5907\uff0c\u54ea\u60f3\u5230\u8fd9\u4e2a\u5f31\u4e0d\u7981\u98ce\u7684\u5c0f\u59d1\u5a18\u51fa\u624b\u5982\u6b64\u4e4b\u5feb\uff0c\u4e00\u4e2a\u7acb\u8db3\u4e0d\u7a33\uff0c\u6251\u5730\u5012\u4e86\u3002\u4ed6\u6b66\u529f\u672c\u4e5f\u4e0d\u5f31\u4e8e\u963f\u4e5d\uff0c\u53ea\u662f\u51fa\u5176\u4e0d\u610f\uff0c\u624d\u7740\u4e86\u9053\u513f\uff0c\u80cc\u810a\u521a\u4e00\u7740\u5730\uff0c\u7acb\u5373\u633a\u8eab\u8df3\u8d77\uff0c\u5c11\u5e74\u4eba\u6700\u662f\u8981\u5f3a\u597d\u80dc\uff0c\u8fd9\u4e00\u4e0b\u8138\u4e0a\u5982\u4f55\u6302\u5f97\u4f4f\uff1f\u94c1\u97ad\u4e00\u4e3e\uff0c\u6251\u4e0a\u53bb\u5c31\u8981\u53ae\u62da\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u662f\u534e\u5c71\u6d3e\u7684\u5427\uff1f\u54b1\u4eec\u90fd\u662f\u81ea\u5df1\u4eba\u5440\uff01\u201d \u3000\u3000\u51af\u4e0d\u7834\u559d\u9053\uff1a\u201c\u8c01\u8ddf\u4f60\u8fd9\u5996\u5973\u662f\u81ea\u5df1\u4eba\u4e86\uff1f\u201d \u3000\u3000\u6885\u5251\u548c\u5728\u6c5f\u6e56\u4e0a\u9605\u5386\u4e45\u4e86\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u89c1\u4f55\u60d5\u5b88\u521a\u624d\u6325\u7d22\u76f8\u6551\u6d2a\u80dc\u6d77\uff0c\u624b\u6cd5\u4e0d\u4fd7\uff0c\u51b3\u975e\u6ca1\u6765\u5386\u4e4b\u4eba\uff0c\u5f53\u4e0b\u5411\u51af\u6c0f\u5144\u5f1f\u4f7f\u4e2a\u773c\u8272\uff0c\u95ee\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u5c0a\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u6211\u5e08\u7236\u59d3\u8881\uff0c\u540d\u53eb\u8881\u627f\u5fd7\uff0c\u597d\u50cf\u662f\u534e\u5c71\u6d3e\u95e8\u4e0b\u3002\u4e5f\u4e0d\u77e5\u662f\u771f\u7684\uff0c\u8fd8\u662f\u5192\u5145\u7684\u3002\u201d\u6885\u5251\u548c\u4e0e\u5b59\u4ef2\u541b\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5c06\u4fe1\u5c06\u7591\u3002\u77f3\u9a8f\u7b11\u9053\uff1a\u201c\u8881\u5e08\u53d4\u81ea\u5df1\u8fd8\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u672c\u95e8\u529f\u592b\u4e0d\u77e5\u5df2\u5b66\u4f1a\u4e86\u4e09\u5957\u6ca1\u6709\uff0c\u600e\u4e48\u4f1a\u6536\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u662f\u4e48\uff1f\u90a3\u53ef\u771f\u7684\u6709\u70b9\u513f\u5e0c\u5947\u53e4\u602a\u4e86\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u6211\u90a3\u5c0f\u5e08\u7236\u662f\u4e2a\u5192\u724c\u8d27\uff0c\u563b\u563b\uff01\u5bf9\u5566\uff01\u6211\u77a7\u4f60\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u7684\u6b66\u529f\uff0c\u5c31\u6bd4\u6211\u90a3\u5c0f\u5e08\u7236\u9ad8\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u5b59\u4ef2\u541b\u5728\u8881\u627f\u5fd7\u624b\u91cc\u5403\u8fc7\u5927\u4e8f\uff0c\u540e\u6765\u88ab\u5e08\u7956\u8d23\u7f5a\uff0c\u524a\u53bb\u624b\u6307\uff0c\u63a8\u672c\u6eaf\u6e90\uff0c\u53ef\u8bf4\u90fd\u56e0\u4ed6\u800c\u8d77\uff0c\u4e00\u60f3\u5230\u8fd9\u4e2a\u5c0f\u5e08\u53d4\u5c31\u6068\u5f97\u7259\u75d2\u75d2\u5730\uff0c\u53ea\u662f\u4e00\u6765\u4ed6\u672c\u9886\u9ad8\u5f3a\uff0c\u8f88\u4efd\u53c8\u5c0a\uff0c\u4e8c\u6765\u4ed6\u6551\u8fc7\u5e08\u7236\u7231\u5b50\u7684\u6027\u547d\uff0c\u5e08\u7236\u5e08\u6bcd\u63d0\u5230\u4ed6\u65f6\u603b\u662f\u611f\u6fc0\u4e07\u5206\uff0c\u81ea\u5df1\u53ea\u597d\u5fc3\u91cc\u607c\u6068\u800c\u5df2\uff0c\u8fd9\u65f6\u542c\u4f55\u60d5\u5b88\u81ea\u79f0\u662f\u8881\u627f\u5fd7\u7684\u5f92\u5f1f\uff0c\u4e0d\u89c9\u6012\u706b\u76f4\u5192\u4e0a\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5982\u662f\u534e\u5c71\u6d3e\u5f1f\u5b50\uff0c\u600e\u4e48\u8ddf\u8fd9\u79cd\u65e0\u803b\u72c2\u5f92\u5728\u4e00\u8d77\uff1f\u201d\u4f55\u60d5\u5b88\u5fae\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u6211\u5e08\u7236\u7684\u957f\u968f\uff0c\u4e0d\u89c1\u5f97\u6709\u751a\u4e48\u65e0\u803b\u554a\u3002\u80dc\u6d77\uff0c\u4f60\u600e\u4e48\u5bf9\u8fd9\u4f4d\u59d1\u5a18\u65e0\u803b\u4e86\uff1f\u5f53\u771f\u65e0\u803b\u5f97\u5f88\u4e48\uff1f\u5509\uff0c\u6211\u53ef\u4e0d\u77e5\u9053\u4f60\u8fd9\u4e48\u4e0d\u6015\u96be\u4e3a\u60c5\u3002\u201d\u8bf4\u7740\u62bf\u5634\u800c\u7b11\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u66f4\u662f\u5927\u6012\uff0c\u4e00\u65f6\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u51e0\u4eba\u5728\u5c71\u540e\u4e89\u6597\u53e3\u89d2\uff0c\u58f0\u97f3\u4f20\u4e86\u51fa\u53bb\uff0c\u4e0d\u4e45\u51af\u96be\u654c\u3001\u5218\u57f9\u751f\u7b49\u8bf8\u5f1f\u5b50\u90fd\u9646\u7eed\u8d76\u5230\u3002 \u3000\u3000\u51af\u4e0d\u7834\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u4e2a\u5973\u4eba\u8bf4\u5979\u662f\u59d3\u8881\u7684\u5c0f\u2026\u2026\u5c0f\u5e08\u53d4\u7956\u7684\u5f1f\u5b50\u3002\u201d\u51af\u96be\u654c\u54fc\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u5435\u751a\u4e48\uff1f\u201d\u51af\u4e0d\u6467\u62a2\u7740\u628a\u521a\u624d\u7684\u4e8b\u8bf4\u4e86\u3002\u534e\u5c71\u6d3e\u7b2c\u4e09\u4ee3\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u51af\u96be\u654c\u5e74\u7eaa\u6700\u5927\uff0c\u5165\u95e8\u6700\u65e9\uff0c\u6c5f\u6e56\u4e0a\u5a01\u540d\u53c8\u76db\uff0c\u9690\u7136\u662f\u8bf8\u5f1f\u5b50\u7684\u9886\u8896\uff0c\u542c\u4e86\u513f\u5b50\u7684\u8bdd\u540e\uff0c\u8f6c\u5934\u95ee\u5b59\u4ef2\u541b\u9053\uff1a\u201c\u5b59\u5e08\u59b9\uff0c\u8fd9\u4eba\u600e\u4e48\u5f97\u7f6a\u4f60\u4e86\uff1f\u201d \u3000\u3000\u5b59\u4ef2\u541b\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u6885\u5251\u548c\u9053\uff1a\u201c\u8fd9\u72c2\u5f92\u6709\u4e2a\u628a\u5144\uff0c\u4e5f\u4e0d\u81ea\u5df1\u7167\u7167\u955c\u5b50\uff0c\u5c45\u7136\u4e0d\u8bc6\u597d\u6b79\uff0c\u8001\u4e86\u8138\u76ae\u6765\u5411\u5b59\u5e08\u59b9\u6c42\u4eb2\uff0c\u7ed9\u5b59\u5e08\u59b9\u9a82\u56de\u53bb\u4e86\u2026\u2026\u201d\u6d2a\u80dc\u6d77\u63d2\u53e3\u9053\uff1a\u201c\u7b54\u4e0d\u7b54\u5141\u5728\u5979\uff0c\u53ef\u662f\u5e72\u4e48\u628a\u6211\u4e49\u5144\u4e24\u53ea\u8033\u6735\u90fd\u524a\u4e86\u53bb\u2026\u2026\u201d\u51af\u96be\u654c\u53cc\u773c\u4e00\u77aa\uff0c\u559d\u9053\uff1a\u201c\u8c01\u95ee\u4f60\u4e86\uff1f\u201d \u3000\u3000\u6885\u5251\u548c\u6307\u7740\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u54ea\u77e5\u8fd9\u72c2\u5f92\u7ea6\u4e86\u8bb8\u591a\u5e2e\u624b\uff0c\u4e58\u5b59\u5e08\u59b9\u843d\u4e86\u5355\uff0c\u7adf\u628a\u5979\u7ed1\u67b6\u4e86\u53bb\uff0c\u5e78\u597d\u6211\u5e08\u5a18\u8fde\u591c\u8d76\u5230\uff0c\u624d\u628a\u5979\u6551\u51fa\u6765\u3002\u201d\u51af\u96be\u654c\u7738\u5b50\u4e00\u7ffb\uff0c\u7cbe\u5149\u56db\u5c04\uff0c\u559d\u9053\uff1a\u201c\u597d\u5927\u7684\u80c6\u5b50\uff0c\u4f60\u8fd8\u60f3\u7ea0\u7f20\u4e0d\u6e05\uff1f\u201d \u3000\u3000\u6d2a\u80dc\u6d77\u51db\u7136\u4e0d\u60e7\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u6740\u4e86\u6211\u4e49\u5144\uff0c\u8fd8\u4e0d\u591f\u4e48\uff1f\u201d \u3000\u3000\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u63b3\u4eba\u903c\u4eb2\uff0c\u786e\u662f\u4ed6\u4eec\u4e0d\u597d\u3002\u4e0d\u8fc7\u8fd9\u4f4d\u5b59\u59ca\u59ca\u65e2\u5df2\u5c06\u4ed6\u4e49\u5144\u6740\u6b7b\uff0c\u4e5f\u5df2\u51fa\u4e86\u6c14\uff0c\u4f55\u51b5\u53c8\u6ca1\u62dc\u5802\u6210\u4eb2\uff0c\u6ca1\u77ed\u4e86\u751a\u4e48\u554a\u3002\u518d\u8bf4\uff0c\u4eba\u5bb6\u77a7\u4e2d\u4f60\u5b59\u59ca\u59ca\uff0c\u662f\u8bf4\u4f60\u7f8e\u5f97\u5929\u4ed9\u4e00\u822c\uff0c\u600e\u4e48\u4eba\u5bb6\u504f\u504f\u53c8\u77a7\u4e0d\u4e2d\u6211\u5462\uff1f\u5b59\u59ca\u59ca\u4ee5\u6028\u62a5\u5fb7\uff0c\u627e\u4e0a\u4ed6\u5bb6\u91cc\u53bb\uff0c\u6740\u4e86\u4ed6\u4e00\u5bb6\u4e94\u53e3\uff0c\u8fd9\u4e0d\u662f\u8fa3\u624b\u4e86\u70b9\u513f\u5417\uff1f\u6740\u4eba\u867d\u7136\u597d\u73a9\uff0c\u603b\u5f97\u62e3\u6709\u6b66\u529f\u7684\u4eba\u6765\u6740\u3002\u4ed6\u7684\u4e03\u5341\u5c81\u8001\u6bcd\u597d\u50cf\u6ca1\u751a\u4e48\u6b66\u529f\uff0c\u4e5f\u6ca1\u72af\u751a\u4e48\u7f6a\uff0c\u6700\u591a\u4e0d\u8fc7\u662f\u751f\u4e86\u4e2a\u513f\u5b50\u6709\u70b9\u513f\u65e0\u803b\u3002\u4ed6\u7684\u59bb\u5b50\u548c\u4e09\u4e2a\u5c0f\u513f\u5973\uff0c\u66f4\u4e0d\u77e5\u662f\u72af\u4e86\u751a\u4e48\u5f25\u5929\u5927\u7f6a\uff1f\u6740\u8fd9\u4e9b\u4eba\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u534e\u5c71\u6d3e\u7684\u89c4\u77e9\uff1f\u201d \u3000\u3000\u4f17\u4eba\u4e00\u542c\uff0c\u89c9\u5f97\u5b59\u4ef2\u541b\u6ee5\u4f24\u65e0\u8f9c\uff0c\u5df2\u72af\u4e86\u672c\u6d3e\u5927\u6212\uff0c\u90fd\u4e0d\u7981\u76b1\u8d77\u4e86\u7709\u5934\u3002\u51af\u96be\u654c\u5bf9\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u8d77\u56e0\u603b\u662f\u4f60\u81ea\u5df1\u4e0d\u597d\uff01\u73b0\u4eca\u4eba\u5df2\u6740\u4e86\uff0c\u53c8\u5f85\u600e\u6837\uff1f\u201d \u3000\u3000\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u633a\u7231\u6ee5\u6740\u597d\u4eba\u7684\uff0c\u81ea\u4ece\u62dc\u4e86\u8881\u627f\u5fd7\u8fd9\u4e2a\u5c0f\u5e08\u7236\u4e4b\u540e\uff0c\u4ed6\u8bf4\u4e86\u4e00\u5927\u5806\u5570\u91cc\u5570\u5506\u7684\u534e\u5c71\u6d3e\u95e8\u89c4\uff0c\u8bf4\u751a\u4e48\u5343\u4e07\u4e0d\u53ef\u6ee5\u6740\u65e0\u8f9c\u3002\u53ef\u662f\u6211\u77a7\u5b59\u59ca\u59ca\u80e1\u4e71\u6740\u4eba\uff0c\u4e0d\u4e5f\u534a\u70b9\u6ca1\u4e8b\u4e48\uff1f\u6211\u8fd9\u53ef\u6709\u70b9\u80e1\u6d82\u4e86\u3002\u5f85\u6211\u89c1\u8fc7\u5c0f\u5b69\u5b50\u5e08\u7236\uff0c\u8bf7\u4ed6\u793a\u4e0b\u5427\u3002\u201d \u3000\u3000\u5218\u57f9\u751f\u9053\uff1a\u201c\u8881\u5e08\u53d4\u4ed6\u4eec\u6b63\u5fd9\u7740\uff0c\u6015\u6ca1\u7a7a\u3002\u201d\u6885\u5251\u548c\u9053\uff1a\u201c\u5e08\u7236\u5462\uff1f\u201d\u5218\u57f9\u751f\u9053\uff1a\u201c\u5e08\u7236\u3001\u5e08\u5a18\u3001\u5e08\u4f2f\u3001\u5e08\u53d4\u56db\u4f4d\uff0c\u8fd8\u6709\u6728\u6851\u8001\u9053\u957f\uff0c\u6b63\u5728\u5546\u91cf\u6551\u6cbb\u90a3\u4e2a\u59d1\u5a18\u3002\u201d\u51af\u96be\u654c\u9053\uff1a\u201c\u65e2\u7136\u8fd9\u6837\uff0c\u5148\u628a\u8fd9\u4eba\u6346\u8d77\u6765\uff0c\u5f85\u4f1a\u513f\u518d\u5411\u5e08\u7236\u3001\u5e08\u53d4\u8bf7\u793a\u3002\u201d\u51af\u4e0d\u7834\u3001\u51af\u4e0d\u6467\u9f50\u58f0\u7b54\u5e94\uff0c\u4e0a\u524d\u5c31\u8981\u62ff\u4eba\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u89c1\u8fd9\u4e00\u5e72\u4eba\u6beb\u4e0d\u5c06\u81ea\u5df1\u653e\u5728\u773c\u91cc\uff0c\u5979\u662f\u72ec\u9738\u4e00\u65b9\u3001\u505a\u60ef\u4e86\u6559\u4e3b\u7684\uff0c\u8fd9\u5982\u4f55\u5fcd\u5f97\uff1f\u7b11\u541f\u541f\u9053\uff1a\u201c\u8981\u7f1a\u4eba\u5417\uff1f\u6211\u8fd9\u91cc\u6709\u7ef3\u5b50\uff01\u201d\u63d0\u8d77\u4e00\u675f\u8f6f\u7ea2\u86db\u7d22\uff0c\u4f38\u51fa\u624b\u53bb\u3002\u51af\u4e0d\u6467\u6a2a\u5979\u4e00\u773c\u9053\uff1a\u201c\u8c01\u8981\u4f60\u7684\uff01\u201d\u5f84\u81ea\u8d70\u5411\u6d2a\u80dc\u6d77\u8eab\u8fb9\u3002 \u3000\u3000\u4e24\u5144\u5f1f\u521a\u8981\u52a8\u624b\uff0c\u5ffd\u542c\u8eab\u65c1\u5657\u54e7\u4e00\u7b11\uff0c\u811a\u4e0a\u540c\u65f6\u4e00\u7d27\uff0c\u8eab\u5b50\u7a81\u7136\u4e34\u7a7a\u800c\u8d77\uff0c\u72b9\u5982\u817e\u4e91\u9a7e\u96fe\u822c\u76f4\u98de\u51fa\u53bb\u3002\u4e24\u4eba\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u8eab\u5728\u534a\u7a7a\uff0c\u604d\u60da\u542c\u5f97\u4f55\u60d5\u5b88\u5a07\u5a9a\u7684\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u554a\u54df\uff0c\u5bf9\u4e0d\u4f4f\u5566\uff01\u5feb\u4f7f\u2018\u9ca4\u9c7c\u7ffb\u8eab\u2019\uff01\u201d\u51af\u4e0d\u7834\u4f9d\u8a00\u4e00\u62db\u201c\u9ca4\u9c7c\u7ffb\u8eab\u201d\uff0c\u53cc\u811a\u843d\u5730\uff0c\u6014\u6014\u7684\u7ad9\u7740\u3002\u51af\u4e0d\u6467\u5e74\u5e7c\u5014\u5f3a\uff0c\u504f\u4e0d\u4f9d\u8a00\uff0c\u60f3\u4f7f\u4e00\u62db\u201c\u98de\u7011\u6d41\u6cc9\u201d\uff0c\u659c\u523a\u91cc\u8dc3\u51fa\u53bb\u7ad9\u4f4f\uff0c\u9732\u4e2a\u59ff\u52bf\u7f8e\u5999\u7684\u8eab\u6bb5\uff0c\u54ea\u77e5\u4e0b\u5815\u4e4b\u52bf\u5feb\u6377\u5f02\u5e38\uff0c\u8170\u95f4\u521a\u4f7f\u51fa\u529b\u91cf\uff0c\u5df2\u7136\u817e\u7684\u4e00\u58f0\uff0c\u5750\u5728\u5730\u4e0b\uff0c\u4e0d\u7531\u5f97\u53c8\u7f9e\u53c8\u75bc\uff0c\u4e00\u5f20\u8138\u76f4\u7ea2\u5230\u4e86\u8116\u5b50\u91cc\u53bb\u3002 \u3000\u3000\u51af\u96be\u654c\u89c1\u7231\u5b50\u53d7\u6b3a\uff0c\u5fc3\u4e2d\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5973\uff0c\u5148\u524d\u81ea\u79f0\u662f\u672c\u95e8\u5f1f\u5b50\uff0c\u6211\u4eec\u8fd8\u4fe1\u4e86\u4f60\u4e09\u5206\u3002\u53ef\u662f\u4f60\u8fd9\u624b\u4e0b\u8d31\u529f\u592b\uff0c\u600e\u4f1a\u662f\u672c\u95e8\u4e2d\u7684\uff1f\u4f60\u8fc7\u6765\uff01\u201d\u4ed6\u4e0d\u6687\u89e3\u5f00\u8863\u6263\uff0c\u5de6\u624b\u5728\u8863\u895f\u4e0a\u4e00\u62c9\uff0c\u5657\u5657\u5657\u6570\u58f0\uff0c\u4e00\u6392\u8863\u6263\u767b\u65f6\u626f\u65ad\uff0c\u4e00\u4ef6\u957f\u8863\u7529\u4e86\u4e0b\u6765\uff0c\u9732\u51fa\u9752\u5e03\u7d27\u8eab\u8863\u88e4\uff0c\u795e\u6001\u5a01\u58ee\uff0c\u72b9\u5982\u4e00\u5ea7\u94c1\u5854\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u60a8\u8fd9\u4f4d\u5e08\u5144\u8981\u8ddf\u5c0f\u59b9\u8fc7\u51e0\u62db\uff0c\u662f\u4e0d\u662f\uff1f\u90a3\u597d\u5440\uff0c\u540c\u95e8\u5e08\u5144\u59b9\u6bd4\u5212\u6bd4\u5212\uff0c\u5012\u4e5f\u4e0d\u9519\uff0c\u4e14\u770b\u6211\u90a3\u5c0f\u5b69\u5b50\u5e08\u7236\u6559\u7684\u73a9\u827a\u513f\u6210\u4e0d\u6210\u3002\u54b1\u4eec\u6253\u751a\u4e48\u8d4c\u554a\uff1f\u201d \u3000\u3000\u51af\u96be\u654c\u867d\u89c1\u5979\u521a\u624d\u51fa\u624b\u8fc5\u6377\uff0c\u4f46\u81ea\u6043\u6df1\u5f97\u5e08\u95e8\u7edd\u827a\u771f\u4f20\uff0c\u5a01\u9547\u897f\u51c9\uff0c\u54ea\u628a\u8fd9\u5c11\u5973\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u89c1\u5979\u4e00\u526f\u5a07\u602f\u602f\u7684\u6a21\u6837\uff0c\u6012\u6c14\u6e10\u606f\uff0c\u5584\u5ff5\u987f\u751f\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e9b\u4eba\u8fd8\u597d\u8bf4\u8bdd\uff0c\u5f85\u4f1a\u5f52\u4e8c\u5a18\u51fa\u6765\uff0c\u5979\u5ac9\u6076\u5982\u4ec7\uff0c\u89c1\u4e86\u4f60\u8fd9\u79cd\u5996\u4eba\u4e00\u5b9a\u653e\u4e0d\u8fc7\u3002\u8fd8\u662f\u5feb\u5feb\u8d70\u5427\uff01\u201d\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u6211\u7684\u5c0f\u5b69\u5b50\u5e08\u7236\uff0c\u51ed\u751a\u4e48\u53eb\u6211\u8d70\uff1f\u201d \u3000\u3000\u51af\u4e0d\u6467\u521a\u624d\u80e1\u91cc\u80e1\u6d82\u8fde\u6454\u4e24\u4ea4\uff0c\u7f9e\u6068\u96be\u5f53\uff0c\u548c\u54e5\u54e5\u4e00\u4f7f\u773c\u8272\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u6765\u771f\u7684\uff0c\u522b\u4f7f\u8be1\u8ba1\u5f04\u9b3c\uff01\u201d\u4e24\u5144\u5f1f\u5404\u4e3e\u94c1\u97ad\uff0c\u53c8\u6251\u4e0a\u6765\u3002\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u7ad9\u7740\u4e0d\u52a8\uff0c\u4e5f\u4e0d\u8fd8\u624b\uff0c\u600e\u4e48\u6837\uff1f\u201d\u628a\u8f6f\u7ea2\u86db\u7d22\u5f80\u8170\u95f4\u4e00\u7f20\uff0c\u53cc\u624b\u62e2\u5728\u8896\u91cc\u3002 \u3000\u3000\u51af\u6c0f\u5144\u5f1f\u53cc\u97ad\u9f50\u4e0b\uff0c\u89c1\u5979\u4e0d\u95ea\u4e0d\u907f\uff0c\u94c1\u97ad\u5c06\u53ca\u5979\u9876\u95e8\u65f6\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u500f\u5730\u6536\u56de\u3002\u4e24\u4eba\u5e7c\u53d7\u5ead\u8bad\uff0c\u867d\u7136\u5e74\u5c11\u5364\u83bd\uff0c\u5374\u4ece\u6765\u4e0d\u6562\u65e0\u6545\u4f24\u4eba\u3002\u51af\u4e0d\u6467\u9053\uff1a\u201c\u5feb\u53d6\u5175\u5203\u51fa\u6765\uff01\u201d \u3000\u3000\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u6211\u662f\u4f60\u54e5\u513f\u4fe9\u7684\u5e08\u59d1\uff0c\u8ddf\u4f60\u4eec\u600e\u80fd\u52a8\u5175\u5203\uff1f \u3000\u3000\u4f60\u4eec\u8981\u5546\u91cf\u4e8e\u6211\uff0c\u8fd9\u5c31\u4e0a\u7f62\uff01\u53ea\u8981\u6211\u6709\u4e00\u53ea\u811a\u632a\u52a8\u534a\u6b65\uff0c\u6216\u8005\u6211\u7684\u624b\u4f38\u51fa\u4e86\u8896\u5b50\uff0c\u90fd\u7b97\u6211\u8f93\u4e86\uff0c\u597d\u4e0d\u597d\u5462\uff1f\u201d\u51af\u4e0d\u7834\u9053\uff1a\u201c\u6211\u5144\u5f1f\u5931\u624b\u4f24\u4f60\uff0c\u90a3\u53ef\u6028\u602a\u4e0d\u5f97\uff01\u201d\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u8fdb\u62db\u5427\uff0c\u5c0f\u4f19\u5b50\u5570\u91cc\u5570\u5506\u7684\u4e0d\u723d\u5feb\u3002\u201d\u51af\u4e0d\u7834\u8138\u4e0a\u4e00\u7ea2\uff0c\u4e00\u97ad\u201c\u656c\u5fb7\u5378\u7532\u201d\uff0c\u659c\u7838\u4e0b\u6765\uff0c\u4f55\u60d5\u5b88\u8eab\u5b50\u5fae\u4fa7\uff0c\u94c1\u97ad\u7838\u7a7a\u3002\u51af\u4e0d\u6467\u6068\u5979\u6454\u4e86\u81ea\u5df1\u4e00\u4ea4\uff0c\u66f4\u662f\u4f7f\u8db3\u5168\u529b\uff0c\u94c1\u97ad\u5411\u5979\u80a9\u5934\u626b\u53bb\uff0c\u54ea\u77e5\u97ad\u68a2\u521a\u5230\uff0c\u5bf9\u624b\u65e9\u5df2\u907f\u8fc7\u3002\u4f55\u60d5\u5b88\u53cc\u8db3\u7262\u7262\u9489\u5728\u5730\u4e0a\uff0c\u8eab\u5b50\u5374\u4e1c\u4fa7\u897f\u907f\uff0c\u5728\u94c1\u97ad\u5f71\u91cc\u72b9\u5982\u82b1\u679d\u4e71\u98a4\u3002\u51af\u6c0f\u5144\u5f1f\u53cc\u97ad\u8d8a\u4f7f\u8d8a\u6025\uff0c\u4f55\u60d5\u5b88\u4ecd\u7136\u5b09\u7b11\u81ea\u82e5\uff0c\u53cc\u97ad\u59cb\u7ec8\u6253\u4e0d\u5230\u5979\u8863\u895f\u4e00\u89d2\u3002 \u3000\u3000\u534e\u5c71\u6d3e\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u77e5\u8fd9\u4e2a\u5973\u5b50\u662f\u4f55\u8def\u9053\uff0c\u5979\u81ea\u79f0\u662f\u672c\u95e8\u5f1f\u5b50\uff0c\u4f46\u8eab\u6cd5\u6b66\u529f\uff0c\u54ea\u6709\u534a\u70b9\u534e\u5c71\u6d3e\u7684\u5f71\u5b50\uff0c\u6b66\u529f\u5374\u53c8\u5982\u6b64\u7cbe\u5f3a\u3002 \u3000\u3000\u4e09\u4eba\u518d\u62c6\u6570\u5341\u62db\uff0c\u51af\u6c0f\u5144\u5f1f\u4e00\u58f0\u547c\u54e8\uff0c\u53cc\u97ad\u7740\u5730\u626b\u53bb\uff0c\u5747\u60f3\u4f60\u811a\u6b65\u5982\u771f\u4e0d\u79fb\uff0c\u90a3\u53c8\u5982\u4f55\u62b5\u6321\uff1f\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u5c0f\u5fc3\u5566\uff01\u201d \u3000\u3000\u8eab\u5b50\u4e00\u5f2f\uff0c\u5de6\u8098\u5728\u51af\u4e0d\u7834\u8eab\u4e0a\u4e00\u63a8\uff0c\u53f3\u8098\u5728\u51af\u4e0d\u6467\u80cc\u4e0a\u4e00\u649e\u3002 \u3000\u3000\u4e24\u5144\u5f1f\u53ea\u611f\u5168\u8eab\u4e00\u9635\u9178\u9ebb\uff0c\u53cc\u97ad\u843d\u5730\uff0c\u8e09\u8e09\u8dc4\u8dc4\u7684\u8dcc\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u51af\u96be\u654c\u4f4e\u58f0\u9053\uff1a\u201c\u6885\u5e08\u5f1f\uff0c\u8fd9\u5973\u4eba\u53e4\u602a\uff0c\u6211\u5148\u4e0a\u53bb\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u6885\u5251\u548c\u70b9\u70b9\u5934\u3002\u51af\u96be\u654c\u7eb5\u8eab\u8dc3\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u6211\u6765\u9886\u6559\u3002\u201d \u3000\u3000\u4f55\u60d5\u5b88\u89c1\u4ed6\u811a\u6b65\u51dd\u91cd\uff0c\u77e5\u4ed6\u6b66\u529f\u9020\u8be3\u751a\u6df1\uff0c\u8138\u4e0a\u4ecd\u7136\u7b11\u772f\u772f\u7684\u9732\u51fa\u4e00\u4e2a\u9152\u6da1\uff0c\u5fc3\u4e2d\u5374\u4e25\u52a0\u6212\u5907\uff0c\u7b11\u9053\uff1a\u201c\u6211\u63a5\u4e0d\u4f4f\u65f6\uff0c\u4f60\u53ef\u522b\u7b11\u8bdd\u3002\u201d\u51af\u96be\u654c\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u8d50\u62db\u5427\uff01\u201d\u8eab\u5b50\u5fae\u5fae\u4e00\u5f13\uff0c\u53f3\u62f3\u5de6\u638c\uff0c\u5408\u7740\u4e00\u63d6\uff0c\u62f3\u98ce\u51cc\u5389\uff0c\u6b63\u662f\u201c\u7834\u7389\u62f3\u201d\u7684\u8d77\u624b\u5f0f\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u88e3\u887d\u4e07\u798f\uff0c\u8fd8\u4e86\u4e00\u793c\uff0c\u8f7b\u8f7b\u628a\u8fd9\u4e00\u62db\u6321\u56de\u53bb\u3002 \u3000\u3000\u51af\u96be\u654c\u5fc3\u4e2d\u6697\u53eb\uff1a\u201c\u597d\u672c\u4e8b\uff01\u201d\u6b63\u8981\u8ddf\u7740\u8fdb\u62db\uff0c\u5ffd\u542c\u5f97\u5c71\u8170\u91cc\u4f20\u6765\u4e00\u9635\u547c\u559d\u53eb\u558a\u4e4b\u58f0\uff0c\u6709\u4eba\u4e89\u6597\u8ffd\u9010\uff0c\u4fbf\u5411\u4f55\u60d5\u5b88\u671b\u4e86\u4e00\u773c\u3002\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u4f60\u7591\u5fc3\u6211\u5e26\u4e86\u5e2e\u624b\u4e48\uff1f\u54b1\u4eec\u5148\u77a7\u4e2a\u6e05\u695a\u518d\u6bd4\u5212\uff0c\u4f60\u8bf4\u597d\u4e48\uff1f\u201d \u3000\u3000\u51af\u96be\u654c\u542c\u547c\u559d\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4e2d\u95f4\u5939\u7740\u4e00\u4e2a\u5973\u5b50\u7684\u6025\u6012\u53eb\u9a82\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e5f\u597d\u3002\u201d \u3000\u3000\u4f17\u4eba\u5954\u5230\u5d16\u8fb9\uff0c\u5411\u4e0b\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u7a7f\u7ea2\u8863\u7684\u5973\u5b50\u6b63\u5728\u5411\u5c71\u4e0a\u6025\u5954\uff0c\u56db\u6761\u5927\u6c49\u624b\u6267\u5175\u5203\u5728\u540e\u8ffd\u8d76\u3002\u90a3\u5973\u5b50\u89c1\u5c71\u9876\u6709\u4eba\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u6025\u901f\u5954\u4e0a\uff0c\u8fdc\u8fdc\u671b\u89c1\u51af\u96be\u654c\u9b41\u4f1f\u7684\u8eab\u8eaf\uff0c\u53eb\u9053\uff1a\u201c\u516b\u9762\u5a01\u98ce\uff0c\u5feb\u6551\u6211\uff01\u201d\u51af\u96be\u654c\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u554a\uff0c\u662f\u7ea2\u5a18\u5b50\uff01\u201d\u5954\u4e0a\u76f8\u8fce\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u8138\u4e0a\u5168\u662f\u9c9c\u8840\u3002\u8fd9\u65f6\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u6655\u5012\u5728\u5730\u3002\u8ddf\u7740\u56db\u4eba\u8d76\u4e0a\u5c71\u6765\uff0c\u4e5f\u4e0d\u7406\u4f1a\u4f17\u4eba\uff0c\u6076\u72e0\u72e0\u7684\u5c31\u8981\u62a2\u4e0a\u64d2\u62ff\u3002\u51af\u96be\u654c\u5de6\u81c2\u4e00\u4f38\uff0c\u4f38\u638c\u5f80\u4e3a\u9996\u4e00\u4eba\u63a8\u53bb\uff0c\u559d\u9053\uff1a\u201c\u670b\u53cb\uff0c\u653e\u660e\u767d\u4e9b\uff01\u8fd9\u662f\u751a\u4e48\u5730\u65b9\uff1f\u201d\u90a3\u4eba\u4f38\u638c\u76f8\u62b5\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u5404\u81ea\u9707\u5f00\u6570\u6b65\uff0c\u90a3\u4eba\u7684\u6b66\u529f\u5012\u4e5f\u9887\u4e3a\u4e86\u5f97\u3002\u4e24\u4eba\u4e92\u76f8\u6253\u91cf\u4e00\u773c\uff0c\u5747\u6709\u60ca\u7591\u4e4b\u610f\u3002\u90a3\u4eba\u559d\u9053\uff1a\u201c\u5949\u5927\u987a\u7687\u5e1d\u5ea7\u4e0b\u6743\u5c06\u519b\u53f7\u4ee4\uff0c\u6349\u62ff\u53db\u9006\u674e\u5ca9\u4e4b\u59bb\uff0c\u4f60\u4f55\u6562\u963b\u62e6\uff1f\u201d \u3000\u3000\u4f55\u60d5\u5b88\u77e5\u9053\u674e\u5ca9\u662f\u5e08\u7236\u7684\u4e49\u5144\uff0c\u5fc3\u60f3\u8fd9\u7ea2\u8863\u5973\u5b50\u65e2\u662f\u674e\u5ca9\u4e4b\u59bb\uff0c\u6211\u5982\u4f55\u4e0d\u6551\uff0c\u633a\u8eab\u800c\u51fa\uff0c\u7b11\u9053\uff1a\u201c\u674e\u5ca9\u5c06\u519b\u662f\u5927\u5927\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u5929\u4e0b\u8c01\u4e0d\u77e5\u95fb\uff1f\u5404\u4f4d\u522b\u96be\u4e3a\u8fd9\u4f4d\u5a18\u5b50\u5427\uff01\u201d \u3000\u3000\u90a3\u4eba\u795e\u8272\u5028\u50b2\uff0c\u81ea\u6043\u6b66\u827a\u9ad8\u5f3a\uff0c\u5728\u5218\u5b97\u654f\u624b\u4e0b\u9887\u6709\u6743\u52bf\uff0c\u54ea\u53bb\u7406\u4f1a\u4f55\u60d5\u5b88\u4e00\u4e2a\u5c0f\u5c0f\u5973\u5b50\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u5de6\u624b\u4e00\u6446\uff0c\u547d\u4e09\u540d\u52a9\u624b\u4e0a\u6765\u6346\u4eba\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u4e0d\u8981\u547d\u5566\uff01\u201d\u53f3\u624b\u5728\u8170\u95f4\u673a\u62ec\u4e0a\u4e00\u6309\uff0c\u201c\u542b\u6c99\u5c04\u5f71\u201d\u7684\u6bd2\u9488\u6fc0\u5c04\u800c\u51fa\u3002\u90a3\u4e09\u4eba\u6b66\u529f\u867d\u975e\u5bfb\u5e38\uff0c\u5374\u600e\u80fd\u9632\u8fd9\u95e8\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u6697\u5668\uff0c\u5f53\u5148\u4e00\u4eba\u767b\u65f6\u8138\u4e0a\u88ab\u4e03\u516b\u679a\u6bd2\u9488\u6253\u4e86\u8fdb\u53bb\uff0c\u53eb\u4e5f\u4e0d\u53eb\u4e00\u58f0\uff0c\u7acb\u65f6\u6bd9\u547d\u3002\u5176\u4f59\u4e09\u4eba\u8138\u8272\u60e8\u53d8\uff0c\u9f50\u58f0\u559d\u95ee\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u4f55\u60d5\u5b88\u5de6\u624b\u94c1\u94a9\u672c\u6765\u7f29\u5728\u957f\u8896\u4e4b\u5185\uff0c\u4e0e\u51af\u6c0f\u5144\u5f1f\u52a8\u624b\u65f6\u4e00\u76f4\u9690\u85cf\u4e0d\u9732\uff0c\u8fd9\u65f6\u957f\u8896\u8f7b\u6325\uff0c\u9732\u51fa\u94c1\u94a9\uff0c\u4e3a\u9996\u90a3\u4eba\u5413\u5f97\u8138\u767d\u5982\u7eb8\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u662f\u4e94\u2026\u2026\u4e94\u2026\u2026\u4f55\u2026\u2026\u4f55\u2026\u2026\u201d\u4f55\u60d5\u5b88\u5fae\u5fae\u4e00\u7b11\uff0c\u53f3\u624b\u91d1\u94a9\u53c8\u662f\u4e00\u6643\u3002\u4e09\u4eba\u9b42\u4e0d\u9644\u4f53\uff0c\u56de\u5934\u5c31\u9003\u3002\u4e00\u4eba\u8fc7\u4e8e\u5bb3\u6015\uff0c\u5728\u5d16\u8fb9\u4e00\u4e2a\u5931\u8db3\uff0c\u9aa8\u788c\u788c\u7684\u76f4\u6eda\u4e0b\u53bb\u3002 \u3000\u3000\u51af\u96be\u654c\u7b49\u90fd\u662f\u5341\u5206\u60ca\u5947\uff0c\u5fc3\u60f3\u8fd9\u4e09\u6761\u5927\u6c49\u600e\u4f1a\u5bf9\u5979\u6015\u5f97\u8fd9\u6837\u5389\u5bb3\uff0c\u5979\u9002\u624d\u6740\u4e86\u90a3\u4eba\uff0c\u53c8\u4e0d\u77e5\u4f7f\u7684\u662f\u751a\u4e48\u53e4\u602a\u6cd5\u95e8\u3002 \u3000\u3000\u51af\u96be\u654c\u6276\u8d77\u4e86\u7ea2\u5a18\u5b50\uff0c\u6b63\u8981\u8be2\u95ee\uff0c\u7a81\u89c1\u5c71\u5d16\u8fb9\u8f6c\u51fa\u4e00\u4e2a\u8eab\u6750\u9ad8\u7626\u7684\u9053\u4eba\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u534e\u5c71\u6d3e\u7684\u4eba\uff0c\u90fd\u5728\u8fd9\u91cc\u4e48\uff1f\u201d\u8fd9\u4e00\u559d\u58f0\u5982\u6d2a\u949f\uff0c\u53ea\u9707\u5f97\u5c71\u8c37\u9e23\u54cd\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u8fd9\u9053\u4eba\u8eab\u4e0a\u9053\u888d\u845b\u4e2d\u5939\u4e1d\uff0c\u707f\u70c2\u534e\u8d35\uff0c\u9053\u51a0\u4e0a\u9576\u7740\u4e00\u5757\u6676\u83b9\u767d\u7389\uff0c\u5149\u534e\u56db\u5c04\uff0c\u80cc\u8d1f\u957f\u5251\uff0c\u98d8\u98d8\u7136\u6709\u51fa\u5c18\u4e4b\u6982\uff0c\u7ea6\u83ab\u56db\u4e94\u5341\u5c81\u5e74\u7eaa\uff0c\u4e00\u8eab\u6e05\u6c14\uff0c\u663e\u662f\u4e00\u4f4d\u5f97\u9053\u9ad8\u4eba\u3002 \u3000\u3000\u51af\u96be\u654c\u4e0a\u524d\u62b1\u62f3\u884c\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u6559\u9053\u957f\u6cd5\u53f7\uff0c\u53ef\u662f\u655d\u6d3e\u7956\u5e08\u7684\u670b\u53cb\u4e48\uff1f\u201d \u3000\u3000\u90a3\u9053\u4eba\u5e76\u4e0d\u8fd8\u793c\uff0c\u53f3\u624b\u62c2\u5c18\u4e00\u6325\uff0c\u5411\u4f17\u4eba\u6253\u91cf\u4e86\u51e0\u773c\uff0c\u95ee\u9053\uff1a\u201c\u662f\u534e\u5c71\u6d3e\u7684\uff1f\u201d\u51af\u96be\u654c\u9053\uff1a\u201c\u6b63\u662f\u3002\u9053\u957f\u6709\u4f55\u89c1\u6559\uff1f\u201d\u90a3\u9053\u4eba\u9053\uff1a\u201c\u55ef\uff0c\u7a46\u4eba\u6e05\u6765\u4e86\u4e48\uff1f\u201d\u51af\u96be\u654c\u542c\u4ed6\u968f\u53e3\u547c\u53eb\u7956\u5e08\u540d\u8bb3\uff0c\u4f3c\u662f\u6781\u719f\u7684\u670b\u53cb\uff0c\u66f4\u52a0\u4e0d\u6562\u6020\u6162\uff0c\u8bf4\u9053\uff1a\u201c\u7956\u5e08\u8fd8\u672a\u9a7e\u4e34\u3002\u201d \u3000\u3000\u90a3\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u62c2\u5c18\u5411\u5b59\u4ef2\u541b\u3001\u4f55\u60d5\u5b88\u3001\u963f\u4e5d\u4e09\u4eba\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u7a46\u8001\u7334\u513f\u5012\u6536\u4e86\u4e0d\u5c11\u7f8e\u8c8c\u5973\u5f92\uff0c\u8273\u798f\u4e0d\u6d45\u3002\u5582\uff0c\u4f60\u4eec\u4e09\u4eba\u8fc7\u6765\u7ed9\u6211\u77a7\u77a7\uff01\u201d\u4f17\u4eba\u542c\u4ed6\u51fa\u8a00\u4e0d\u900a\uff0c\u90fd\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u6012\u9053\uff1a\u201c\u4f60\u662f\u751a\u4e48\u4eba\uff1f\u201d\u90a3\u9053\u4eba\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8ddf\u9053\u7237\u56de\u53bb\uff0c\u6211\u6162\u6162\u8bf4\u7ed9\u4f60\u77e5\u9053\u3002\u201d\u5b59\u4ef2\u541b\u89c1\u4ed6\u795e\u6001\u8f7b\u8584\uff0c\u767b\u65f6\u5927\u6012\uff0c\u8d70\u4e0a\u4e00\u6b65\uff0c\u559d\u9053\uff1a\u201c\u751a\u4e48\u4e1c\u897f\uff0c\u6562\u5728\u8fd9\u91cc\u6492\u91ce\uff01\u201d\u90a3\u9053\u4eba\u7b11\u563b\u563b\u7684\u5728\u5979\u8138\u4e0a\u6478\u4e86\u4e00\u628a\uff0c\u62ff\u56de\u6765\u5728\u9f3b\u7aef\u4e0a\u55c5\u4e86\u4e00\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u597d\u9999\uff01\u201d\u4ed6\u5de6\u624b\u8fd9\u4e48\u4e00\u4f38\u4e00\u7f29\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u5982\u4f55\u8fc5\u901f\uff0c\u5b59\u4ef2\u541b\u7adf\u6ca1\u80fd\u907f\u5f00\u3002\u5979\u5fc3\u4e2d\u6012\u6781\uff0c\u987a\u624b\u633a\u94a9\u523a\u53bb\u3002\u90a3\u9053\u4eba\u53f3\u624b\u8f7b\u6321\uff0c\u53cd\u8fc7\u624b\u6765\u5df2\u6293\u4f4f\u5979\u624b\u8155\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u8109\u95e8\u88ab\u4ed6\u6263\u4f4f\uff0c\u767b\u89c9\u5168\u8eab\u9178\u8f6f\uff0c\u4f7f\u4e0d\u51fa\u534a\u70b9\u529b\u6c14\u3002 \u3000\u3000\u90a3\u9053\u4eba\u4e00\u628a\u5c06\u5979\u6402\u5728\u6000\u91cc\uff0c\u53c8\u5728\u8138\u988a\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u8d5e\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u4e0d\u574f\uff01\u201d \u3000\u3000\u51af\u96be\u654c\u3001\u6885\u5251\u548c\u3001\u5218\u57f9\u751f\u7b49\u4e2a\u4e2a\u60ca\u6012\u5931\u8272\uff0c\u4e00\u9f50\u51b2\u4e0a\u3002 \u3000\u3000\u90a3\u9053\u4eba\u62d4\u8d77\u8eab\u5b50\uff0c\u6597\u7136\u9000\u5f00\u6570\u6b65\u3002\u4f17\u4eba\u89c1\u4ed6\u5de6\u624b\u4ecd\u7136\u6402\u4f4f\u5b59\u4ef2\u541b\u4e0d\u653e\uff0c\u4f46\u4e00\u8dc3\u4e00\u843d\uff0c\u6bd4\u5bfb\u5e38\u5355\u72ec\u4e00\u4eba\u8fd8\u8981\u7075\u4fbf\u6f47\u6d12\uff0c\u4e0d\u7531\u5f97\u5c3d\u7686\u9a87\u7136\uff0c\u4f46\u89c1\u5b59\u4ef2\u541b\u88ab\u4ed6\u62b1\u4f4f\u4e86\u52a8\u5f39\u4e0d\u5f97\uff0c\u660e\u77e5\u4e0d\u654c\uff0c\u4e5f\u4e0d\u80fd\u8896\u624b\u4e0d\u7406\uff0c\u5404\u4eba\u62d4\u51fa\u5175\u5203\uff0c\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u90a3\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u53f3\u624b\u7ffb\u5230\u80a9\u5934\uff0c\u7a81\u7136\u95f4\u9752\u5149\u8000\u773c\uff0c\u80cc\u4e0a\u7684\u957f\u5251\u5df2\u62d4\u5728\u624b\u91cc\u3002 \u3000\u3000\u6885\u5251\u548c\u5bf9\u5b59\u4ef2\u541b\u6700\u4e3a\u5173\u5fc3\uff0c\u9996\u5148\u4ed7\u5251\u75be\u653b\u3002\u4ed6\u89c1\u4e86\u90a3\u9053\u4eba\u957f\u5251\u7684\u6a21\u6837\uff0c\u77e5\u662f\u4e00\u67c4\u5229\u5668\uff0c\u4e0d\u6562\u6b63\u9762\u76f8\u78b0\uff0c\u5237\u5237\u5237\u8fde\u523a\u4e09\u5251\uff0c\u90fd\u662f\u5bfb\u7455\u62b5\u9699\u800c\u5165\u3002\u53bb\u5e74\u4ed6\u5728\u5357\u4eac\u548c\u8881\u627f\u5fd7\u6bd4\u5251\uff0c\u4e00\u8fde\u51e0\u67c4\u5251\u5c3d\u88ab\u9707\u65ad\uff0c\u624d\u77e5\u672c\u95e8\u6b66\u529f\u7cbe\u5965\u5f02\u5e38\uff0c\u81ea\u5df1\u53ea\u662f\u5f97\u4e86\u4e00\u70b9\u76ae\u6bdb\u800c\u5df2\uff0c\u4e0d\u7531\u5f97\u72c2\u50b2\u4e4b\u6c14\u987f\u51cf\uff0c\u518d\u5411\u5e08\u7236\u8ba8\u6559\u5251\u6cd5\uff0c\u534a\u5e74\u4e2d\u8db3\u4e0d\u51fa\u6237\uff0c\u82e6\u5fc3\u7814\u4e60\uff0c\u679c\u7136\u5251\u6cd5\u5927\u8fdb\uff0c\u9002\u624d\u8fd9\u4e09\u5251\u662f\u4ed6\u751f\u5e73\u7edd\u5b66\uff0c\u8fc5\u6377\u608d\u72e0\uff0c\u5df2\u5f97\u534e\u5c71\u6d3e\u5251\u6cd5\u7684\u7cbe\u8981\u3002 \u3000\u3000\u90a3\u9053\u4eba\u8d5e\u9053\uff1a\u201c\u4e0d\u574f\uff01\u201d\u8bed\u58f0\u672a\u6bd5\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5df2\u5c06\u6885\u5251\u548c\u7684\u957f\u5251\u524a\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u6885\u5251\u548c\u5413\u4e86\u4e00\u8df3\uff0c\u4f9d\u7167\u6b66\u5b66\u60ef\u4f8b\uff0c\u7acb\u5373\u8981\u5c06\u65ad\u5251\u5411\u654c\u4eba\u63b7\u53bb\uff0c\u4ee5\u9632\u5bf9\u65b9\u4e58\u52bf\u731b\u653b\uff0c\u7136\u540e\u907f\u5f00\uff0c\u518d\u7b79\u5fa1\u654c\u4e4b\u7b56\uff0c\u4f46\u4ed6\u6015\u8bef\u4f24\u5e08\u59b9\uff0c\u4e0d\u6562\u63b7\u5251\uff0c\u5251\u65ad\u5373\u9000\uff0c\u9976\u662f\u4ed6\u8f7b\u8eab\u529f\u592b\u5f02\u5e38\u4e86\u5f97\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u5934\u9876\u675f\u53d1\u7684\u5e03\u5e26\u5df2\u88ab\u5272\u65ad\u3002\u8fd9\u6570\u62db\u53ea\u662f\u4e00\u5239\u90a3\u4e4b\u95f4\u7684\u4e8b\uff0c\u6885\u5251\u548c\u5fc3\u60ca\u80c6\u6218\u4e4b\u9645\uff0c\u51af\u96be\u654c\u3001\u5218\u57f9\u751f\u3001\u77f3\u9a8f\u3001\u51af\u4e0d\u7834\u3001\u51af\u4e0d\u6467\uff0c\u4ee5\u53ca\u9ec4\u771f\u7684\u56db\u5f1f\u5b50\u3001\u516d\u5f1f\u5b50\u4e00\u9f50\u653b\u4e0a\uff0c\u5200\u67aa\u5251\u621f\uff0c\u540c\u65f6\u5e76\u4e3e\uff0c\u53ea\u5218\u57f9\u751f\u662f\u7a7a\u624b\u4f7f\u62f3\u3002 \u3000\u3000\u90a3\u9053\u4eba\u957f\u5251\u4f7f\u4e86\u5f00\u6765\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e00\u9635\u4e71\u54cd\uff0c\u6709\u7684\u5175\u5203\u88ab\u622a\uff0c\u6709\u7684\u8fde\u4eba\u5e26\u5200\u7ed9\u4ed6\u4e00\u811a\u8e22\u98de\uff0c\u53ea\u5269\u4e0b\u51af\u96be\u654c\u4e0e\u5218\u57f9\u751f\u4e24\u4e2a\u6b66\u529f\u6700\u9ad8\u7684\u52c9\u529b\u652f\u6491\u3002\u6885\u5251\u548c\u4ece\u5730\u4e0b\u6361\u8d77\u4e00\u67c4\u5251\u62a2\u4e0a\u5939\u653b\u3002\u90a3\u9053\u4eba\u5de6\u624b\u4ecd\u662f\u6402\u7740\u5b59\u4ef2\u541b\uff0c\u53f3\u624b\u957f\u5251\u654c\u4f4f\u4e8c\u4eba\uff0c\u7b11\u563b\u563b\u5730\u6d51\u4e0d\u5728\u610f\uff0c\u62bd\u7a7a\u8fd8\u5728\u5b59\u4ef2\u541b\u8138\u988a\u4e00\u543b\uff0c\u53ea\u628a\u5b59\u4ef2\u541b\u6c14\u5f97\u51e0\u6b32\u6655\u53bb\u3002 \u3000\u3000\u62c6\u4e86\u6570\u62db\uff0c\u90a3\u9053\u4eba\u5ffd\u5730\u5c06\u957f\u5251\u629b\u5411\u7a7a\u4e2d\u3002\u5218\u57f9\u751f\u4e00\u6014\uff0c\u4e0d\u77e5\u4ed6\u4f7f\u751a\u4e48\u5947\u7279\u62db\u6570\u3002\u6885\u5251\u548c\u6025\u53eb\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u53ea\u542c\u84ec\u7684\u4e00\u58f0\uff0c\u5218\u57f9\u751f\u80f8\u53e3\u5df2\u4e2d\u4e86\u4e00\u62f3\uff0c\u9000\u51fa\u6570\u6b65\uff0c\u5750\u5012\u5728\u5730\u3002\u90a3\u9053\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u81ea\u4ee5\u4e3a\u62f3\u6cd5\u4e86\u5f97\uff0c\u6211\u7528\u5175\u5668\u4f24\u4f60\uff0c\u8c05\u4f60\u4e0d\u670d\uff01\u201d\u987a\u624b\u63a5\u4f4f\u7a7a\u4e2d\u843d\u4e0b\u6765\u7684\u5b9d\u5251\uff0c\u5f53\u5577\u4e00\u54cd\uff0c\u53c8\u628a\u6885\u5251\u548c\u7684\u5251\u524a\u65ad\uff0c\u5f2f\u8fc7\u624b\u81c2\u53f3\u8098\u63a8\u51fa\uff0c\u6b63\u649e\u5728\u51af\u96be\u654c\u7684\u5de6\u80c1\u4e4b\u4e0a\u3002\u51af\u96be\u654c\u53ea\u89c9\u5947\u75db\u5165\u9aa8\uff0c\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u817e\u817e\u817e\u8fde\u9000\u6570\u6b65\u3002 \u3000\u3000\u90a3\u9053\u4eba\u5c06\u534e\u5c71\u4f17\u5f1f\u5b50\u6253\u5f97\u4e00\u8d25\u6d82\u5730\uff0c\u65e0\u4eba\u6562\u518d\u4e0a\u6765\uff0c\u6602\u7136\u56db\u987e\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u7a46\u81ea\u5938\u62f3\u5251\u5929\u4e0b\u65e0\u53cc\uff0c\u6559\u51fa\u6765\u7684\u5f1f\u5b50\u5374\u8fd9\u822c\u4e0d\u6210\u5668\uff01\u4f60\u4eec\u5e08\u7956\u95ee\u8d77\uff0c\u5c31\u8bf4\u7389\u771f\u5b50\u6765\u62dc\u8bbf\u8fc7\u4e86\uff0c\u89c1\u4ed6\u5f92\u5f1f\u6559\u5f97\u4e0d\u597d\uff0c\u5e26\u4e86\u4e09\u4e2a\u5973\u5f92\u513f\u53bb\u4ee3\u4ed6\u6559\u5bfc\u3002\u4e09\u5e74\u4e4b\u540e\uff0c\u6211\u6559\u538c\u4e86\uff0c\u81ea\u4f1a\u9001\u8fd8\uff01\u201d\u987a\u624b\u5411\u540e\u4e00\u6325\uff0c\u773c\u73e0\u4e5f\u6ca1\u8f6c\u4e0a\u4e00\u8f6c\uff0c\u4fbf\u5df2\u5c06\u957f\u5251\u63d2\u5165\u4e86\u80cc\u4e0a\u7684\u5251\u9798\uff0c\u5355\u662f\u8fd9\u624b\u529f\u592b\uff0c\u4fbf\u5df2\u8bf4\u5f97\u4e0a\u60ca\u4e16\u9a87\u4fd7\u3002\u4ed6\u4ecd\u662f\u6402\u7740\u5b59\u4ef2\u541b\uff0c\u8d70\u5411\u4f55\u60d5\u5b88\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u8ddf\u6211\u53bb\uff01\u201d \u3000\u3000\u4f55\u60d5\u5b88\u81ea\u77e5\u62b5\u654c\u4e0d\u8fc7\uff0c\u5bf9\u6d2a\u80dc\u6d77\u9053\uff1a\u201c\u5feb\u53bb\u8bf7\u5e08\u7236\u3002\u201d\u7b49\u6d2a\u80dc\u6d77\u8f6c\u8eab\u8d70\u5f00\uff0c\u90a3\u9053\u4eba\u4e5f\u5df2\u8d70\u5230\u8ddf\u524d\u3002\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u529f\u592b\u771f\u4fca\u3002\u60a8\u9053\u53f7\u662f\u751a\u4e48\u5440\uff1f\u201d \u3000\u3000\u90a3\u9053\u4eba\u89c1\u5979\u7b11\u541f\u541f\u7684\u6beb\u4e0d\u754f\u60e7\uff0c\u5012\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u89c1\u5979\u5bb9\u8c8c\u5a07\u5a9a\uff0c\u53cc\u8db3\u5982\u96ea\uff0c\u8a00\u7b11\u4e4b\u95f4\u5c24\u5176\u52a8\u4eba\u5fc3\u9b44\uff0c\u4e0d\u7531\u5f97\u9aa8\u5934\u4e5f\u9165\u4e86\uff0c\u53c8\u8d70\u4e0a\u4e00\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u6211\u53eb\u7389\u771f\u5b50\uff0c\u4f60\u8fd9\u5b69\u5b50\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u4f60\u8bf4\u6211\u529f\u592b\u597d\uff0c\u90a3\u4e48\u8ddf\u6211\u56de\u53bb\uff0c\u6211\u6162\u6162\u6559\u4f60\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u9a97\u4eba\uff1f\u54b1\u4eec\u8bf4\u8fc7\u4e86\u7684\u8bdd\uff0c\u53ef\u4e0d\u8bb8\u4e0d\u7b97\u3002\u201d\u7389\u771f\u5b50\u7b11\u9053\uff1a\u201c\u8c01\u6765\u9a97\u4f60\uff0c\u8d70\u5427\uff01\u201d\u4f38\u624b\u4fbf\u6765\u62c9\u5979\u7684\u624b\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u9000\u4e86\u4e00\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u6162\u7740\uff0c\u7b49\u6211\u5e08\u7236\u6765\u4e86\uff0c\u5148\u95ee\u95ee\u4ed6\u884c\u4e0d\u884c\u3002\u201d\u7389\u771f\u5b50\u9053\uff1a\u201c\u54fc\uff0c\u8ddf\u7740\u4f60\u5e08\u7236\uff0c\u5c31\u7b97\u5b66\u5f97\u672c\u9886\u8ddf\u4ed6\u4e00\u6837\uff0c\u53c8\u6709\u751a\u4e48\u7528\uff1f\u8fd9\u6837\u7684\u996d\u6876\u5e08\u7236\uff0c\u8fd8\u662f\u522b\u7406\u4f1a\u4e86\u5427\uff0c\u54c8\u54c8\uff01\u201d\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u6211\u5e08\u7236\u672c\u9886\u5927\u5f97\u5f88\u5462\uff0c\u8981\u662f\u77e5\u9053\u6211\u8ddf\u4f60\u8d70\u4e86\uff0c\u4ed6\u8981\u4e0d\u4f9d\u7684\u3002\u201d \u3000\u3000\u51af\u96be\u654c\u7b49\u89c1\u5b59\u4ef2\u541b\u7ed9\u90a3\u9053\u4eba\u6402\u5728\u6000\u91cc\u52a8\u5f39\u4e0d\u5f97\uff0c\u90a3\u5996\u5973\u5374\u8ddf\u4ed6\u7709\u82b1\u773c\u7b11\u7684\u6253\u60c5\u9a82\u6084\uff0c\u4e2a\u4e2a\u6c14\u5f97\u6012\u706b\u586b\u81ba\u3002\u6885\u5251\u548c\u53eb\u9053\uff1a\u201c\u597d\u8d3c\u9053\uff0c\u8ddf\u4f60\u62da\u4e86\u3002\u201d\u63d0\u5251\u53c8\u4e0a\u3002 \u3000\u3000\u7389\u771f\u5b50\u5934\u4e5f\u4e0d\u56de\uff0c\u5bf9\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u6211\u518d\u9732\u4e00\u624b\u529f\u592b\u7ed9\u4f60\u77a7\u77a7\u3002\u770b\u662f\u4f60\u5e08\u7236\u9ad8\u660e\u5462\uff0c\u8fd8\u662f\u6211\u5389\u5bb3\u3002\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u95ea\u907f\u6885\u5251\u548c\u7684\u6765\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u50cf\u4ed6\u8fd9\u822c\u7684\u5251\u6cd5\uff0c\u5728\u4f60\u4eec\u534e\u5c71\u6d3e\u91cc\u603b\u4e5f\u7b97\u662f\u5c11\u6709\u7684\u9ad8\u624b\u4e86\uff0c\u7136\u800c\u78b0\u5230\u4e86\u6211\uff0c\u54fc\u54fc\uff01\u4f60\u6570\u7740\uff0c\u4ece\u4e00\u6570\u5230\u5341\uff0c\u6211\u4e00\u53ea\u7a7a\u624b\u5c31\u628a\u4ed6\u5251\u593a\u4e0b\u6765\u3002\u201d\u6885\u5251\u548c\u89c1\u4ed6\u5982\u6b64\u8f7b\u89c6\u81ea\u5df1\uff0c\u66f4\u662f\u6c14\u607c\uff0c\u4e00\u67c4\u5251\u8d8a\u52a0\u4f7f\u5f97\u51cc\u5389\u8fc5\u6377\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u4ece\u4e00\u6570\u5230\u5341\u4e48\uff1f\u597d\uff0c\u4e00\uff0c\u4e8c\uff0c\u4e09\uff0c\u56db\uff0c\u4e94\u2026\u2026\u201d\u7a81\u7136\u4e00\u53e3\u6c14\u4e0d\u505c\uff0c\u5feb\u901f\u5f02\u5e38\u7684\u6570\u4e0b\u53bb\u3002\u7389\u771f\u5b50\u7b11\u9053\uff1a\u201c\u5c0f\u59ae\u5b50\u771f\u574f\uff0c\u77a7\u771f\u4e86\uff01\u201d\u6885\u5251\u548c\u633a\u5251\u523a\u51fa\uff0c\u7a81\u89c1\u654c\u4eba\u8eab\u5b50\u7565\u4fa7\uff0c\u957f\u81c2\u76f4\u4f38\uff0c\u53cc\u6307\u5df2\u6307\u53ca\u81ea\u5df1\u4e24\u773c\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u6570\u5bf8\uff0c\u4e0d\u7531\u5f97\u5927\u60ca\uff0c\u5de6\u624b\u75be\u5fd9\u4e0a\u683c\u3002\u7389\u771f\u5b50\u624b\u81c2\u65e9\u5df2\u7f29\u56de\uff0c\u624b\u8098\u987a\u52bf\u5728\u4ed6\u8155\u4e0a\u4e00\u649e\u3002\u6885\u5251\u548c\u624b\u6307\u4e00\u9ebb\uff0c\u957f\u5251\u8131\u624b\uff0c\u5df2\u88ab\u7389\u771f\u5b50\u5feb\u5982\u95ea\u7535\u822c\u593a\u4e86\u8fc7\u53bb\uff0c\u90a3\u65f6\u4f55\u60d5\u5b88\u8fd8\u53ea\u6570\u5230\u201c\u4e5d\u201d\u5b57\u3002 \u3000\u3000\u7389\u771f\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u5de6\u624b\u6301\u5251\uff0c\u53f3\u624b\u98df\u4e2d\u4e24\u6307\u5939\u4f4f\u5251\u5c16\uff0c\u5411\u4e0b\u4e00\u6273\uff0c\u5580\u7684\u4e00\u58f0\uff0c\u5251\u5c16\u767b\u65f6\u62d7\u4e86\u4e0b\u6765\u3002\u53ea\u542c\u5f97\u5580\u5580\u5580\u54cd\u58f0\u4e0d\u7edd\uff0c\u4e00\u67c4\u957f\u5251\u5df2\u88ab\u62d7\u6210\u4e00\u5bf8\u5bf8\u7684\u5e9f\u94c1\u3002 \u3000\u3000\u7389\u771f\u5b50\u628a\u5269\u4e0b\u7684\u6570\u5bf8\u5251\u67c4\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u4e00\u58f0\u957f\u5578\uff0c\u4f38\u624b\u6765\u53c8\u62c9\u4f55\u60d5\u5b88\u7684\u624b\u8155\u3002\u4f55\u60d5\u5b88\u4e00\u76f4\u4ee5\u7f13\u5175\u4e4b\u8ba1\u8ddf\u4ed6\u62d6\u5ef6\uff0c\u4f46\u8881\u627f\u5fd7\u59cb\u7ec8\u4e0d\u5230\uff0c\u8fd9\u65f6\u65e0\u53ef\u518d\u62d6\uff0c\u5de6\u624b\u8f7b\u62ac\uff0c\u8ba9\u4ed6\u63e1\u4f4f\u3002\u7389\u771f\u5b50\u6ee1\u62df\u6293\u5230\u4e00\u53ea\u6e29\u9999\u8f6f\u7389\u7684\u7ea4\u7ea4\u67d4\u8351\uff0c\u7a81\u89c9\u63e1\u5230\u4e00\u4ef6\u575a\u786c\u51b0\u51b7\u4e4b\u7269\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u75be\u5fd9\u653e\u624b\uff0c\u773c\u524d\u91d1\u5149\u95ea\u52a8\uff0c\u91d1\u94a9\u7684\u94a9\u5c16\u5df2\u5212\u5411\u7709\u5fc3\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u8fd9\u4e00\u4e0b\u53d1\u96be\u53c8\u5feb\u53c8\u51c6\uff0c\u7389\u771f\u5b50\u7eb5\u7136\u6b66\u529f\u5353\u7edd\uff0c\u4e5f\u9669\u4e9b\u4e2d\u94a9\uff0c\u5371\u6025\u4e2d\u8111\u888b\u5411\u540e\u75be\u633a\uff0c\u98ce\u58f0\u98d2\u7136\uff0c\u94a9\u5c16\u4ece\u9f3b\u7aef\u64e6\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c9\u4e00\u80a1\u8165\u6c14\u76f4\u51b2\u9f3b\u5b54\uff0c\u539f\u6765\u94a9\u4e0a\u5582\u4e86\u5267\u6bd2\u3002\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e2a\u5a07\u6ef4\u6ef4\u7684\u59d1\u5a18\u51fa\u624b\u7adf\u4f1a\u5982\u6b64\u6bd2\u8fa3\uff0c\u800c\u534e\u5c71\u6d3e\u95e8\u4eba\u5175\u5668\u4e0a\u53c8\u7adf\u4f1a\u5582\u6bd2\uff0c\u4e0d\u7981\u5413\u5f97\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u5fae\u5fae\u4e00\u6014\uff0c\u5bf9\u65b9\u94c1\u94a9\u53c8\u5230\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u94c1\u94a9\u8fde\u8fdb\u56db\u62db\u3002 \u3000\u3000\u7389\u771f\u5b50\u624b\u4e2d\u6ca1\u6709\u5175\u5668\uff0c\u5de6\u81c2\u53c8\u62b1\u7740\u4eba\uff0c\u4e00\u65f6\u88ab\u5979\u653b\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u53d1\u52b2\u628a\u5b59\u4ef2\u541b\u5411\u524d\u4e00\u63a8\uff0c\u7eb5\u5f00\u4e09\u6b65\uff0c\u62d4\u51fa\u957f\u5251\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u77a7\u4f60\u4e0d\u51fa\uff0c\u5c45\u7136\u8fd8\u6709\u4e24\u4e0b\u5b50\u3002\u597d\u597d\u597d\uff0c\u54b1\u4eec\u518d\u6765\u3002\u201d \u3000\u3000\u4f55\u60d5\u5b88\u9002\u624d\u51fa\u654c\u4e0d\u610f\uff0c\u653b\u5176\u65e0\u5907\uff0c\u624d\u5360\u4e86\u4e0a\u98ce\uff0c\u8981\u8bb2\u771f\u6253\uff0c\u539f\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4f46\u5b9e\u903c\u5904\u6b64\uff0c\u4e0d\u80fd\u4e0d\u633a\u8eab\u76f8\u6597\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u4e0d\u80fd\u8ddf\u6211\u5f53\u771f\u7684\uff0c\u54b1\u4eec\u95f9\u7740\u73a9\u513f\u3002\u201d \u3000\u3000\u7389\u771f\u5b50\u5df2\u77e5\u8fd9\u5973\u5b50\u5916\u8c8c\u5a07\u5a9a\uff0c\u8a00\u8bed\u53ef\u559c\uff0c\u51fa\u624b\u5374\u662f\u6beb\u4e0d\u5bb9\u60c5\uff0c\u4f46\u81ea\u6043\u6b66\u529f\u5929\u4e0b\u65e0\u654c\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8f93\u4e86\u53ef\u5f97\u8ddf\u6211\u56de\u53bb\u3002\u201d\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u4f60\u8f93\u4e86\u5462\uff1f\u6211\u53ef\u4e0d\u8981\u4f60\u8ddf\u7740\u3002\u201d\u53cc\u94a9\u970d\u970d\uff0c\u75be\u653b\u800c\u4e0a\u3002\u7389\u771f\u5b50\u4e0d\u6562\u5927\u610f\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u5f53\u5373\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u6885\u5251\u548c\u62a2\u4e0a\u53bb\u6276\u8d77\u5b59\u4ef2\u541b\u3002\u4f17\u4eba\u5148\u524d\u89c1\u4f55\u60d5\u5b88\u6253\u5012\u51af\u6c0f\u5144\u5f1f\uff0c\u8fd8\u9053\u4e24\u4e2a\u5c11\u5e74\u5b66\u827a\u672a\u7cbe\uff0c\u8fd9\u65f6\u89c1\u5979\u529b\u654c\u6076\u9053\uff0c\u8eab\u6cd5\u8f7b\u7075\uff0c\u62db\u6cd5\u602a\u5f02\uff0c\u53cc\u94a9\u5316\u6210\u4e86\u4e00\u9053\u9ec4\u5149\uff0c\u4e00\u6761\u9ed1\u6c14\uff0c\u594b\u529b\u62b5\u4f4f\u7389\u771f\u5b50\u7684\u957f\u5251\uff0c\u90fd\u4e0d\u7981\u6697\u6697\u548b\u820c\u3002\u5404\u4eba\u5f85\u8981\u4e0a\u524d\u76f8\u52a9\uff0c\u4f46\u89c1\u4e8c\u4eba\u6597\u5f97\u5982\u6b64\u6fc0\u70c8\uff0c\u8fdb\u9000\u8d8b\u907f\uff0c\u5175\u5203\u5288\u98ce\uff0c\u8fc5\u6377\u65e0\u4f26\uff0c\u81ea\u5fd6\u6b66\u827a\u8fdc\u8fdc\u4e0d\u53ca\uff0c\u90fd\u4e0d\u6562\u63d2\u624b\u3002 \u3000\u3000\u4e24\u4eba\u6597\u5230\u9163\u5904\uff0c\u62db\u672f\u8d8a\u6765\u8d8a\u5feb\uff0c\u7a81\u7136\u95f4\u53ee\u7684\u4e00\u58f0\uff0c\u91d1\u94a9\u88ab\u7389\u771f\u5b50\u5b9d\u5251\u524a\u53bb\u4e86\u4e00\u622a\u3002\u4f55\u60d5\u5b88\u8896\u5b50\u4e00\u6325\uff0c\u8896\u53e3\u4e2d\u98de\u51fa\u4e00\u679a\u6697\u5668\uff0c\u6ce2\u7684\u4e00\u54cd\uff0c\u5728\u7389\u771f\u5b50\u9762\u524d\u6563\u5f00\uff0c\u5316\u6210\u4e00\u56e2\u7c89\u7ea2\u8272\u7684\u70df\u96fe\u3002\u8fd9\u65f6\u6668\u66e6\u521d\u4e0a\uff0c\u7167\u5c04\u4e4b\u4e0b\uff0c\u66f4\u662f\u7f8e\u8273\u65e0\u6bd4\u3002 \u3000\u3000\u7389\u771f\u5b50\u659c\u523a\u91cc\u8dc3\u5f00\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u4e94\u6bd2\u90aa\u6559\u7684\u4e48\uff1f\u600e\u5730\u6df7\u5728\u8fd9\u91cc\uff1f\u201d\u4e00\u9635\u98ce\u6765\uff0c\u77f3\u9a8f\u548c\u51af\u4e0d\u6467\u4e24\u4eba\u7ad9\u5728\u4e0b\u98ce\uff0c\u987f\u89c9\u5934\u8111\u6655\u7729\uff0c\u660f\u5012\u5728\u5730\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u7b11\u9053\uff1a\u201c\u6211\u73b0\u4eca\u6539\u90aa\u5f52\u6b63\u5566\uff0c\u5165\u4e86\u534e\u5c71\u6d3e\u7684\u95e8\u5899\u3002\u4f60\u4e5f\u6539\u90aa\u5f52\u6b63\uff0c\u62dc\u6211\u4e3a\u5e08\uff0c\u597d\u4e0d\u597d\u5462\uff1f\u6211\u8bf4\u5c0f\u9053\u58eb\u554a\uff0c\u4f60\u8fd8\u662f\u5feb\u78d5\u5934\u7f62\uff01\u201d \u3000\u3000\u7389\u771f\u5b50\u8fd0\u638c\u6210\u98ce\uff0c\u547c\u547c\u4e24\u58f0\uff0c\u638c\u98ce\u63a8\u5f00\u9762\u524d\u7edb\u96fe\uff0c\u8ddf\u7740\u4e00\u638c\uff0c\u6392\u5c71\u5012\u6d77\u822c\u6253\u4e86\u8fc7\u6765\u3002\u4f55\u60d5\u5b88\u89c1\u4ed6\u5251\u6cd5\u7cbe\u5999\uff0c\u5c82\u77e5\u638c\u529b\u540c\u6837\u5389\u5bb3\uff0c\u8155\u5e95\u4e00\u7ffb\uff0c\u5df2\u5c06\u874e\u5c3e\u97ad\u62ff\u5728\u624b\u4e2d\uff0c\u4fa7\u8eab\u907f\u5f00\u638c\u529b\uff0c\u97ad\u68a2\u5f80\u4ed6\u624b\u8155\u4e0a\u5377\u53bb\u3002 \u3000\u3000\u7389\u771f\u5b50\u5fc3\u60f3\uff0c\u4eca\u65e5\u4e0a\u5f97\u5c71\u6765\uff0c\u539f\u662f\u8981\u4ee5\u5b64\u8eab\u5355\u5251\u6311\u4e86\u534e\u5c71\u6d3e\uff0c\u54ea\u77e5\u6b63\u4e3b\u513f\u672a\u89c1\uff0c\u4fbf\u8ba9\u8fd9\u5973\u5b69\u5b50\u63a5\u4e86\u8fd9\u8bb8\u591a\u62db\u53bb\uff0c\u8fd9\u6b21\u518d\u4e0d\u5bb9\u5979\u62c6\u4e0a\u4e09\u62db\u4e4b\u5916\uff0c\u770b\u51c6\u97ad\u68a2\u6765\u52bf\uff0c\u500f\u5730\u4f38\u51fa\u5de6\u624b\uff0c\u98df\u4e2d\u4e24\u6307\u5df2\u5c06\u874e\u5c3e\u97ad\u7262\u7262\u94b3\u4f4f\u3002\u4ed6\u6307\u4e0a\u6234\u6709\u94a2\u5957\uff0c\u4e0d\u6015\u97ad\u4e0a\u6bd2\u523a\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u4e00\u5e26\u6ca1\u5e26\u52a8\uff0c\u5bf9\u65b9\u957f\u5251\u5df2\u9012\u4e86\u8fc7\u6765\uff0c\u75be\u5fd9\u64a4\u97ad\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8f93\u4e86\uff0c\u8fd9\u5c31\u62dc\u4f60\u4e3a\u5e08\u7f62\uff01\u201d\u8bf4\u7740\u76c8\u76c8\u62dc\u5012\u3002\u7389\u771f\u5b50\u5475\u5475\u5927\u7b11\uff0c\u628a\u874e\u5c3e\u97ad\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u7a81\u7136\u773c\u524d\u9752\u5149\u95ea\u8000\uff0c\u5fc3\u77e5\u4e0d\u5999\uff0c\u888d\u8896\u6025\u62c2\uff0c\u500f\u5730\u8dc3\u8d77\uff0c\u4e00\u9635\u7ec6\u5fae\u7684\u94a2\u9488\uff0c\u55e4\u55e4\u55e4\u7684\u90fd\u6253\u8fdb\u4e86\u8349\u91cc\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u5728\u62dc\u5012\u65f6\u6f5c\u53d1\u201c\u542b\u6c99\u5c04\u5f71\u201d\u7684\u6697\u5668\uff0c\u8fd9\u4e00\u4e0b\u53d8\u8d77\u4fc4\u9877\uff0c\u4e8b\u5148\u6beb\u65e0\u534a\u70b9\u5f81\u5146\uff0c\u672c\u6765\u975e\u4e2d\u4e0d\u53ef\uff0c\u54ea\u77e5\u7389\u771f\u5b50\u6b66\u6280\u8fc7\u4eba\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u7adf\u5c14\u907f\u4e86\u5f00\u53bb\uff0c\u53ea\u662f\u751f\u6b7b\u4e5f\u53ea\u76f8\u5dee\u4e00\u7ebf\u3002\u4ed6\u60ca\u6012\u4ea4\u96c6\uff0c\u8eab\u5728\u534a\u7a7a\uff0c\u4fbf\u5373\u524d\u6251\uff0c\u5982\u82cd\u9e70\u822c\u5411\u4f55\u60d5\u5b88\u6251\u51fb\u4e0b\u6765\u3002 \u3000\u3000\u963f\u4e5d\u5728\u65c1\u89c2\u6218\uff0c\u65f6\u65f6\u523b\u523b\u63d0\u5fc3\u540a\u80c6\uff0c\u4e3a\u4f55\u60d5\u5b88\u62c5\u5fc3\uff0c\u82e6\u4e8e\u81ea\u5df1\u81c2\u4f24\u672a\u6108\uff0c\u6b66\u529f\u53c8\u592a\u5dee\uff0c\u4e0d\u80fd\u51fa\u624b\u76f8\u52a9\uff0c\u773c\u89c1\u7389\u771f\u5b50\u6765\u52bf\u731b\u6076\uff0c\u5f53\u5373\u4e00\u626c\u624b\uff0c\u4e24\u652f\u9752\u7af9\u9556\u5411\u4ed6\u6fc0\u5c04\u8fc7\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u63a5\u7740\uff01\u201d\u628a\u91d1\u86c7\u5251\u5411\u4f55\u60d5\u5b88\u63b7\u53bb\u3002\u7389\u771f\u5b50\u957f\u8896\u4e00\u62c2\uff0c\u53cd\u5e26\u7af9\u9556\u5c04\u5411\u4f55\u60d5\u5b88\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u907f\u638c\u3001\u63a5\u5251\u3001\u7838\u9556\u3001\u8fdb\u62db\uff0c\u56db\u4ef6\u4e8b\u4e00\u6c14\u5475\u6210\uff0c\u8f6c\u77ac\u95f4\u53c8\u4e0e\u654c\u4eba\u4ea4\u4e0a\u4e86\u624b\u3002\u8fd9\u65f6\u5979\u624b\u4e2d\u62ff\u7684\u662f\u4e00\u628a\u780d\u91d1\u65ad\u7389\u7684\u5b9d\u5251\uff0c\u53f3\u624b\u5251\uff0c\u5de6\u624b\u94a9\uff0c\u5175\u5203\u4e0a\u5927\u5360\u4fbf\u5b9c\u3002 \u3000\u3000\u7389\u771f\u5b50\u4e45\u6218\u4e0d\u4e0b\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u5f53\u5373\u5de6\u624b\u62d4\u51fa\u62c2\u5c18\u52a9\u653b\uff0c\u8fd9\u4e00\u6765\u5175\u5203\u4e2d\u6709\u521a\u6709\u67d4\uff0c\u5a01\u52bf\u5927\u632f\u3002\u4f55\u60d5\u5b88\u7528\u5251\u672c\u4e0d\u64c5\u957f\uff0c\u5de6\u624b\u94c1\u94a9\u5c1a\u53ef\u52c9\u5f3a\u652f\u6491\uff0c\u53f3\u624b\u7684\u91d1\u86c7\u5251\u5374\u9010\u6e10\u88ab\u4ed6\u514b\u5236\u4f4f\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u5f62\u52bf\u5371\u6025\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u90fd\u62e5\u4e0a\u76f8\u52a9\u3002\u53ea\u542c\u62c2\u5c18\u5237\u7684\u4e00\u58f0\uff0c\u5218\u57f9\u751f\u80a9\u5934\u5267\u75db\u5165\u9aa8\u3002\u539f\u6765\u4ed6\u62c2\u5c18\u4e1d\u4e2d\u5939\u6709\u91d1\u7ebf\uff0c\u518d\u52a0\u4e0a\u6d51\u539a\u5185\u529b\uff0c\u8981\u662f\u6362\u4e86\u6b66\u529f\u7a0d\u5dee\u4e4b\u4eba\uff0c\u8fd9\u4e00\u4e0b\u5f53\u573a\u5c31\u5f97\u7ed9\u4ed6\u626b\u5012\u3002\u6885\u5251\u548c\u5411\u5b59\u4ef2\u541b\u9053\uff1a\u201c\u5feb\u53bb\u8bf7\u5e08\u7236\u3001\u5e08\u5a18\u3001\u5e08\u4f2f\u3001\u5e08\u53d4\u6765\u3002\u201d\u4ed6\u89c1\u7389\u771f\u5b50\u6b66\u529f\u4e4b\u9ad8\uff0c\u751f\u5e73\u7f55\u89c1\uff0c\u53ea\u6015\u8981\u6570\u540d\u9ad8\u624b\u5408\u529b\uff0c\u624d\u5236\u5f97\u4f4f\u4ed6\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u5e94\u58f0\u8f6c\u8eab\uff0c\u5ffd\u7136\u5927\u559c\u53eb\u9053\uff1a\u201c\u9053\u957f\uff0c\u5feb\u6765\uff0c\u5feb\u6765\u3002\u201d \u3000\u3000\u4f17\u4eba\u6597\u5f97\u6b63\u7d27\uff0c\u4e0d\u6687\u56de\u5934\uff0c\u53ea\u542c\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u662f\u4f60\u6765\u5566\uff01\u201d \u3000\u3000\u7389\u771f\u5b50\u5237\u5237\u6570\u5251\uff0c\u628a\u4f17\u4eba\u903c\u5f00\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u51b7\u7136\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u60a8\u597d\u5440\u3002\u201d \u3000\u3000\u4f17\u4eba\u8fd9\u624d\u56de\u8fc7\u8eab\u6765\uff0c\u53ea\u89c1\u6728\u6851\u9053\u4eba\u63e1\u4e86\u4e00\u53ea\u68cb\u76d8\uff0c\u4e24\u56ca\u68cb\u5b50\uff0c\u7ad9\u5728\u540e\u9762\u3002 \u3000\u3000\u4f17\u5f1f\u5b50\u77e5\u9053\u6728\u6851\u9053\u4eba\u662f\u5e08\u7956\u7684\u597d\u53cb\uff0c\u6b66\u529f\u4e0e\u5e08\u7956\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u6709\u4ed6\u51fa\u624b\uff0c\u591a\u5389\u5bb3\u7684\u5bf9\u5934\u4e5f\u8ba8\u4e0d\u4e86\u597d\u53bb\uff0c\u4f46\u542c\u7389\u771f\u5b50\u7adf\u53eb\u4ed6\u505a\u5e08\u54e5\uff0c\u53c8\u90fd\u5341\u5206\u60ca\u5947\u3002 \u3000\u3000\u6728\u6851\u94c1\u9752\u4e86\u8138\uff0c\u68ee\u7136\u95ee\u9053\uff1a\u201c\u4f60\u5230\u8fd9\u91cc\u6765\u5e72\u751a\u4e48\uff1f\u201d\u7389\u771f\u5b50\u7b11\u9053\uff1a\u201c\u6211\u6765\u627e\u4eba\uff0c\u8981\u8ddf\u534e\u5c71\u6d3e\u4e00\u4e2a\u59d3\u8881\u7684\u5c11\u5e74\u7b97\u4e00\u7b14\u5e10\uff0c\u4e58\u4fbf\u8fd8\u8981\u6536\u4e09\u4e2a\u5973\u5f92\u5f1f\u3002\u201d \u3000\u3000\u6728\u6851\u76b1\u4e86\u7709\u5934\u9053\uff1a\u201c\u5341\u591a\u5e74\u6765\uff0c\u813e\u6c14\u7adf\u662f\u4e00\u70b9\u4e0d\u6539\u4e48\uff1f\u5feb\u5feb\u4e0b\u5c71\u53bb\u5427\u3002\u201d\u7389\u771f\u5b50\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5f53\u5e74\u5e08\u7236\u4e5f\u4e0d\u7ba1\u6211\uff0c\u5012\u8981\u5e08\u54e5\u8d39\u8d77\u5fc3\u6765\u5566\uff01\u201d\u6728\u6851\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u60f3\u60f3\uff0c\u8fd9\u4e9b\u5e74\u6765\u505a\u4e86\u591a\u5c11\u4f24\u5929\u5bb3\u7406\u4e4b\u4e8b\u3002\u6211\u65e9\u5c31\u60f3\u5230\u897f\u85cf\u6765\u627e\u4f60\u2026\u2026\u201d\u7389\u771f\u5b50\u7b11\u9053\uff1a\u201c\u90a3\u597d\u5440\uff0c\u54b1\u54e5\u513f\u4fe9\u5f88\u4e45\u6ca1\u89c1\u9762\u4e86\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u4eca\u65e5\u6211\u6700\u540e\u529d\u4f60\u4e00\u6b21\uff0c\u4f60\u518d\u6019\u6076\u4e0d\u609b\uff0c\u53ef\u83ab\u602a\u505a\u5e08\u5144\u7684\u65e0\u60c5\u3002\u201d \u3000\u3000\u7389\u771f\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e00\u4eba\u4e00\u5251\u6a2a\u884c\u5929\u4e0b\uff0c\u4ece\u6765\u6ca1\u4eba\u5bf9\u6211\u6709\u534a\u53e5\u65e0\u793c\u4e4b\u8a00\u3002\u201d\u6728\u6851\u9053\uff1a\u201c\u534e\u5c71\u6d3e\u8ddf\u4f60\u6cb3\u6c34\u4e0d\u72af\u4e95\u6c34\uff0c\u4f60\u628a\u4ed6\u4eec\u95e8\u4e0b\u5f1f\u5b50\u4f24\u6210\u8fd9\u6837\u3002\u7a46\u5e08\u5144\u56de\u6765\uff0c\u6559\u6211\u5982\u4f55\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u7389\u771f\u5b50\u563f\u563f\u4e00\u9635\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8c01\u4e0d\u77e5\u6211\u8ddf\u4f60\u65e9\u5df2\u60c5\u65ad\u4e49\u7edd\u3002\u7a46\u4eba\u6e05\u6d6a\u5f97\u865a\u540d\uff0c\u65c1\u4eba\u6015\u4ed6\uff0c\u6211\u7389\u771f\u5b50\u65e2\u6709\u80c6\u5b50\u4e0a\u5f97\u534e\u5c71\uff0c\u5c31\u6ca1\u628a\u8fd9\u795e\u5251\u9b3c\u5251\u7684\u8001\u7334\u513f\u653e\u5728\u5fc3\u4e0a\u3002\u8c01\u8bf4\u534e\u5c71\u6d3e\u8ddf\u6211\u6cb3\u6c34\u4e0d\u72af\u4e95\u6c34\u4e86\uff1f\u6211\u53c8\u6ca1\u5f97\u7f6a\u7a46\u8001\u7334\u513f\uff0c\u4ed6\u5e72\u4e48\u6d3e\u4eba\u5230\u76db\u4eac\u53bb\u8ddf\u6211\u6363\u86cb\uff1f\u201d \u3000\u3000\u6728\u6851\u4e0d\u77e5\u8881\u627f\u5fd7\u8ddf\u4ed6\u5728\u6c88\u9633\u66fe\u4ea4\u8fc7\u4e00\u756a\u624b\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u591a\u95ee\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u63d0\u8d77\u68cb\u76d8\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4e24\u4eba\u7ec8\u4e8e\u53c8\u8981\u52a8\u624b\uff0c\u8fd9\u4e00\u6b21\u4f60\u53ef\u522b\u6307\u671b\u6211\u518d\u9976\u4f60\u4e86\u3002\u4e0a\u5427\uff01\u201d\u7389\u771f\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8981\u8ddf\u6211\u52a8\u624b\uff0c\u54fc\uff0c\u8fd9\u662f\u751a\u4e48\uff1f\u201d\u4f38\u624b\u5165\u6000\uff0c\u6478\u51fa\u4e00\u67c4\u5c0f\u5c0f\u94c1\u5251\uff0c\u9ad8\u4e3e\u8fc7\u5934\u3002 \u3000\u3000\u6728\u6851\u5411\u94c1\u5251\u51dd\u89c6\u534a\u664c\uff0c\u8138\u4e0a\u767b\u65f6\u53d8\u8272\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u597d\u597d\uff0c\u4e0d\u6789\u4f60\u5728\u897f\u85cf\u8fd9\u4e9b\u5e74\uff0c\u679c\u7136\u5f97\u5230\u4e86\u3002\u201d\u7389\u771f\u5b50\u5389\u58f0\u559d\u9053\uff1a\u201c\u6728\u6851\u9053\u4eba\uff0c\u89c1\u4e86\u5e08\u95e8\u94c1\u5251\u8fd8\u4e0d\u4e0b\u8dea\uff1f\u201d \u3000\u3000\u6728\u6851\u653e\u4e0b\u68cb\u76d8\u68cb\u5b50\uff0c\u606d\u606d\u656c\u656c\u7684\u5411\u7389\u771f\u5b50\u62dc\u5012\u78d5\u5934\u3002 \u3000\u3000\u4f17\u5f1f\u5b50\u672c\u62df\u6728\u6851\u5230\u6765\u4e4b\u540e\u6536\u4f0f\u6076\u9053\uff0c\u54ea\u77e5\u53cd\u800c\u5411\u4ed6\u78d5\u5934\u793c\u62dc\uff0c\u4e2a\u4e2a\u60ca\u8bb6\u5931\u671b\u3002 \u3000\u3000\u7389\u771f\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6570\u6b21\u6298\u8fb1\u4e8e\u6211\u3002\u5148\u524d\u6211\u8fd8\u5f53\u4f60\u662f\u5e08\u5144\uff0c\u6bcf\u6b21\u8ba9\u4f60\u3002\u5982\u4eca\u5374\u53c8\u5982\u4f55\uff1f\u201d\u6728\u6851\u4fef\u9996\u4e0d\u7b54\u3002\u7389\u771f\u5b50\u5de6\u638c\u4e00\u8d77\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5e26\u7740\u4e00\u80a1\u52b2\u98ce\u76f4\u5288\u4e0b\u6765\u3002\u6728\u6851\u65e2\u4e0d\u8fd8\u624b\uff0c\u4ea6\u4e0d\u95ea\u907f\uff0c\u8fd0\u6c14\u4e8e\u80cc\uff0c\u62da\u529b\u62b5\u62d2\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u53ea\u6253\u5f97\u8863\u886b\u7834\u88c2\uff0c\u7247\u7247\u98de\u821e\u3002\u4ed6\u8eab\u5b50\u4e00\u6643\uff0c\u4ecd\u7136\u8dea\u7740\u3002\u7389\u771f\u5b50\u94c1\u9752\u4e86\u8138\uff0c\u53c8\u662f\u4e00\u638c\uff0c\u6253\u5728\u6728\u6851\u80a9\u5934\uff0c\u8fd9\u4e00\u638c\u5374\u65e0\u534a\u70b9\u58f0\u606f\uff0c\u8863\u886b\u4e5f\u672a\u7834\u88c2\uff0c\u5c82\u77e5\u8fd9\u4e00\u638c\u5185\u52b2\u5947\u5927\uff0c\u66f4\u4e0d\u597d\u53d7\u3002\u6728\u6851\u8eab\u5b50\u5411\u524d\u4e00\u4fef\uff0c\u4e00\u5927\u53e3\u9c9c\u8840\u55b7\u5c04\u5728\u5c71\u77f3\u4e4b\u4e0a\u3002\u7389\u771f\u5b50\u5168\u7136\u65e0\u52a8\u4e8e\u4e2d\uff0c\u63d0\u8d77\u624b\u638c\uff0c\u5f84\u5411\u4ed6\u5934\u9876\u62cd\u4e0b\u3002 \u3000\u3000\u4f17\u4eba\u6697\u53eb\u4e0d\u597d\uff0c\u8fd9\u4e00\u638c\u4e0b\u53bb\uff0c\u6728\u6851\u5fc5\u7136\u4e27\u547d\uff0c\u5404\u4eba\u6697\u5668\u7eb7\u7eb7\u51fa\u624b\uff0c\u9f50\u5f80\u7389\u771f\u5b50\u6253\u53bb\u3002\u7389\u771f\u5b50\u624b\u638c\u72b9\u5982\u4e00\u628a\u94c1\u6247\uff0c\u8fde\u8fde\u6325\u52a8\uff0c\u5c06\u6697\u5668\u4e00\u4e00\u62e8\u843d\uff0c\u968f\u5373\u53c8\u63d0\u8d77\u638c\u6765\u3002 \u3000\u3000\u963f\u4e5d\u548c\u6728\u6851\u7ad9\u5f97\u6700\u8fd1\uff0c\u89c1\u4ed6\u987b\u53d1\u5982\u94f6\uff0c\u5374\u5982\u6b64\u53d7\u6b3a\uff0c\u6fc0\u52a8\u4e86\u4fa0\u4e49\u5fc3\u80a0\uff0c\u548c\u8eab\u7eb5\u4e0a\uff0c\u53f3\u81c2\u62b1\u4f4f\u4e86\u6728\u6851\u5934\u9888\uff0c\u4ee5\u81ea\u5df1\u8eab\u5b50\u62a4\u4f4f\u4ed6\u9876\u95e8\u3002 \u3000\u3000\u7389\u771f\u5b50\u4e00\u5446\uff0c\u51dd\u638c\u4e0d\u843d\uff0c\u7a81\u7136\u8eab\u540e\u4e00\u58f0\u54b3\u55fd\uff0c\u8f6c\u51fa\u4e00\u4e2a\u5112\u88c5\u6253\u626e\u7684\u8001\u4eba\u6765\u3002 \u3000\u3000\u4f55\u60d5\u5b88\u89c1\u8fd9\u4eba\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u5ffd\u7136\u5728\u963f\u4e5d\u8eab\u65c1\u51fa\u73b0\uff0c\u8eab\u6cd5\u4e4b\u5feb\uff0c\u4ece\u6240\u7f55\u89c1\uff0c\u53ea\u9053\u654c\u4eba\u53c8\u6765\u4e86\u9ad8\u624b\uff0c\u751f\u6015\u963f\u4e5d\u53d7\u5bb3\uff0c\u8dc3\u8d77\u8eab\u5b50\uff0c\u53f3\u638c\u5f80\u90a3\u8001\u4eba\u6253\u53bb\uff0c\u559d\u9053\uff1a\u201c\u6eda\u5f00\uff01\u201d \u3000\u3000\u90a3\u8001\u4eba\u5de6\u81c2\u4e00\u632f\uff0c\u4f55\u60d5\u5b88\u53ea\u89c9\u4e00\u80a1\u5de8\u5927\u4e4b\u6781\u7684\u529b\u9053\u6d8c\u5230\uff0c\u518d\u4e5f\u7acb\u8db3\u4e0d\u5b9a\uff0c\u63a5\u8fde\u9000\u51fa\u6570\u6b65\uff0c\u8fd9\u624d\u51dd\u529b\u7ad9\u5b9a\uff0c\u60ca\u60e7\u4ea4\u96c6\u4e4b\u9645\uff0c\u5f85\u8981\u53d1\u5c04\u6697\u5668\uff0c\u5374\u89c1\u534e\u5c71\u6d3e\u5f1f\u5b50\u4e2a\u4e2a\u62dc\u5012\u884c\u793c\uff0c\u9f50\u53eb\uff1a\u201c\u5e08\u7956\u201d\u3002\u539f\u6765\u7adf\u662f\u795e\u5251\u4ed9\u733f\u7a46\u4eba\u6e05\u5230\u4e86\u3002\u4f55\u60d5\u5b88\u53c8\u60ca\u53c8\u7f9e\uff0c\u6697\u53eb\u201c\u7cdf\u7cd5\u201d\uff0c\u8fd9\u4e00\u4e0b\u5bf9\u5e08\u7956\u5982\u6b64\u65e0\u793c\uff0c\u53ea\u6015\u518d\u4e5f\u5165\u4e0d\u4e86\u534e\u5c71\u6d3e\u4e4b\u95e8\uff0c\u4e00\u65f6\u4e0d\u77e5\u662f\u5426\u4e5f\u8be5\u8dea\u5012\u3002 \u3000\u3000\u8fd9\u65f6\u6728\u6851\u5df2\u7ad9\u8d77\u9000\u5f00\uff0c\u5de6\u624b\u6276\u5728\u963f\u4e5d\u80a9\u5934\uff0c\u52aa\u529b\u8c03\u5300\u547c\u5438\uff0c\u4f46\u4ecd\u662f\u4e0d\u4f4f\u55b7\u8840\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u5411\u7389\u771f\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u5b9a\u662f\u7389\u771f\u9053\u957f\u4e86\uff0c\u5bf9\u81ea\u5df1\u5e08\u5144\u4e5f\u80fd\u4e0b\u5982\u6b64\u6bd2\u624b\u3002\u597d\u597d\u597d\uff0c\u6211\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u966a\u9053\u957f\u8fc7\u62db\u5427\uff01\u201d \u3000\u3000\u7389\u771f\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4eba\u5bb6\u5e38\u95ee\u6211\uff1a\u2018\u7389\u771f\u9053\u957f\uff0c\u7a46\u4eba\u6e05\u81ea\u79f0\u5929\u4e0b\u62f3\u5251\u65e0\u53cc\uff0c\u8ddf\u4f60\u76f8\u6bd4\uff0c\u5230\u5e95\u8c01\u9ad8\u8c01\u4f4e\uff1f\u2019\u6211\u603b\u662f\u8bf4\uff1a\u2018\u4e0d\u77e5\u9053\uff0c\u51e0\u65f6\u6709\u7a7a\uff0c\u5f97\u8ddf\u7a46\u4eba\u6e05\u6bd4\u5212\u6bd4\u5212\u3002\u2019\u81ea\u4eca\u800c\u540e\uff0c\u5230\u5e95\u5f53\u4e16\u8c01\u662f\u6b66\u529f\u7b2c\u4e00\uff0c\u90a3\u5c31\u5206\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u4f17\u5f1f\u5b50\u89c1\u5e08\u7956\u4eb2\u81ea\u8981\u548c\u6076\u9053\u52a8\u624b\uff0c\u4e2a\u4e2a\u53c8\u60ca\u53c8\u559c\uff0c\u4ed6\u4eec\u5927\u90fd\u4ece\u672a\u89c1\u8fc7\u5e08\u7956\u7684\u6b66\u529f\uff0c\u5fc3\u60f3\u8fd9\u771f\u662f\u751f\u5e73\u96be\u9047\u7684\u826f\u673a\u3002 \u3000\u3000\u5218\u57f9\u751f\u5374\u60f3\u5e08\u7956\u5e74\u8fc8\uff0c\u6b66\u5b66\u4fee\u4e3a\u867d\u9ad8\uff0c\u53ea\u6015\u7cbe\u795e\u6c14\u529b\u4e0d\u5982\u8fd9\u6b63\u5f53\u76db\u5e74\u7684\u6076\u9053\uff0c\u5fd9\u5954\u56de\u53bb\u8bf7\u5e08\u7236\u5e08\u5a18\u3002\u4e00\u8fdb\u77f3\u5c4b\uff0c\u53ea\u89c1\u8881\u627f\u5fd7\u6cea\u75d5\u6ee1\u9762\uff0c\u7ad9\u5728\u5e8a\u524d\uff0c\u5e08\u4f2f\u3001\u5e08\u7236\u3001\u5e08\u5a18\uff0c\u4ee5\u53ca\u6d2a\u80dc\u6d77\u3001\u54d1\u5df4\u7b49\u90fd\u662f\u8138\u8272\u60e8\u7136\uff0c\u5e08\u5a18\u66f4\u4e0d\u65ad\u7684\u5728\u6d41\u6cea\u3002\u5218\u57f9\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u8d70\u8fd1\u770b\u65f6\uff0c\u89c1\u9752\u9752\u53cc\u76ee\u6df1\u9677\uff0c\u8138\u8272\u9edd\u9ed1\uff0c\u51fa\u6c14\u591a\u8fdb\u6c14\u5c11\uff0c\u773c\u89c1\u662f\u4e0d\u6210\u7684\u4e86\u3002\u5916\u9762\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u4ed6\u4eec\u5374\u59cb\u7ec8\u7559\u5728\u5c4b\u5185\uff0c\u539f\u6765\u662f\u9752\u9752\u75c5\u5371\uff0c\u4e0d\u80fd\u5206\u51fa\u8eab\u6765\u5bdf\u770b\u3002\u5218\u57f9\u751f\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u90a3\u6076\u9053\u5389\u5bb3\u5f97\u7d27\uff0c\u5e08\u7956\u4eb2\u81ea\u4e0b\u573a\u4e86\u3002\u201d \u3000\u3000\u5f52\u8f9b\u6811\u89c1\u5218\u57f9\u751f\u795e\u6001\u4e25\u91cd\uff0c\u77e5\u9053\u5bf9\u624b\u5927\u662f\u52b2\u654c\uff0c\u5fc3\u4e2d\u60ac\u5ff5\u5e08\u7236\uff0c\u5f53\u5373\u5954\u51fa\u3002\u9ec4\u771f\u5bf9\u5f52\u4e8c\u5a18\u548c\u8881\u627f\u5fd7\u9053\uff1a\u201c\u54b1\u4eec\u90fd\u53bb\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4fef\u8eab\u62b1\u8d77\u9752\u9752\uff0c\u548c\u4f17\u4eba\u4e00\u9f50\u5feb\u6b65\u51fa\u6765\u3002 \u3000\u3000\u4f17\u4eba\u6765\u5230\u540e\u5c71\uff0c\u53ea\u89c1\u7a46\u4eba\u6e05\u624b\u6301\u957f\u5251\uff0c\u7389\u771f\u5b50\u53f3\u624b\u5b9d\u5251\uff0c\u5de6\u624b\u62c2\u5c18\uff0c\u8fdc\u8fdc\u7684\u76f8\u5411\u800c\u7acb\uff0c\u6b63\u8981\u4ea4\u624b\u3002\u8881\u627f\u5fd7\u4e00\u89c1\u6b64\u4eba\uff0c\u6b63\u662f\u53bb\u5e74\u79cb\u5929\u5728\u76db\u4eac\u4e24\u5ea6\u4ea4\u624b\u7684\u7389\u771f\u5b50\uff0c\u7b2c\u4e00\u6b21\u81ea\u5df1\u7ed9\u4ed6\u70b9\u4e2d\u4e86\u4e09\u6307\uff0c\u7b2c\u4e8c\u6b21\u81ea\u5df1\u6253\u4e86\u4ed6\u4e00\u62f3\u4e00\u638c\uff0c\u8e22\u4e86\u4ed6\u4e00\u811a\uff0c\u4f46\u4e24\u6b21\u8f83\u91cf\u5747\u662f\u60c5\u666f\u7279\u5f02\uff0c\u4e0d\u80fd\u8bf4\u5206\u4e86\u80dc\u8d25\uff0c\u5f53\u5373\u5927\u53eb\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u6765\u5bf9\u4ed8\u4ed6\uff01\u201d \u3000\u3000\u7a46\u4eba\u6e05\u548c\u7389\u771f\u5b50\u90fd\u77e5\u5bf9\u65b9\u662f\u6b66\u6797\u5927\u9ad8\u624b\uff0c\u8fd9\u4e00\u6218\u53ea\u8981\u7a0d\u6709\u758f\u865e\uff0c\u4e00\u4e16\u82f1\u540d\u56fa\u7136\u4ed8\u4e8e\u6d41\u6c34\uff0c\u8fde\u6027\u547d\u4e5f\u96be\u4e8e\u4fdd\u5168\uff0c\u8fd9\u65f6\u90fd\u662f\u5168\u795e\u8d2f\u6ce8\uff0c\u5bf9\u8881\u627f\u5fd7\u7684\u558a\u58f0\u7adf\u5982\u672a\u95fb\u3002 \u3000\u3000\u8881\u627f\u5fd7\u628a\u9752\u9752\u5f80\u4f55\u60d5\u5b88\u624b\u91cc\u4e00\u653e\uff0c\u521a\u8bf4\u5f97\u4e00\u58f0\uff1a\u201c\u4f60\u77a7\u7740\u5979\u3002\u201d\u53ea\u89c1\u7389\u771f\u5b50\u62c2\u5c18\u4e00\u6446\uff0c\u500f\u5730\u5f80\u7a46\u4eba\u6e05\u5de6\u80a9\u6325\u6765\u3002\u4ed6\u77e5\u9053\u8fd9\u4e24\u4e2a\u9ad8\u624b\u4e00\u4ea4\u4e0a\u4e86\u624b\uff0c\u5c31\u7edd\u96be\u62c6\u89e3\u5f97\u5f00\uff0c\u81ea\u53e4\u9053\u6709\u4e8b\u5f1f\u5b50\u670d\u5176\u52b3\uff0c\u5c82\u53ef\u8ba9\u5e08\u7236\u4eb2\u81ea\u5bf9\u654c\uff1f\u53cc\u8db3\u4e00\u767b\uff0c\u5982\u5de8\u9e6b\u822c\u5411\u7389\u771f\u5b50\u6251\u53bb\u3002\u4ed6\u662f\u8fd9\u526f\u5fc3\u601d\uff0c\u9ec4\u771f\u548c\u5f52\u8f9b\u6811\u4e5f\u662f\u8fd9\u4e48\u60f3\uff0c\u4e09\u4eba\u4e0d\u7ea6\u800c\u540c\uff0c\u9f50\u5411\u7389\u771f\u5b50\u653b\u5230\u3002 \u3000\u3000\u7389\u771f\u5b50\u62c2\u5c18\u6536\u8f6c\uff0c\u5012\u9000\u4e24\u6b65\uff0c\u53ea\u542c\u5f97\u98ce\u58f0\u98d2\u7136\uff0c\u4e00\u4eba\u4ece\u5934\u9876\u8dc3\u8fc7\u3002\u4ed6\u5934\u9888\u4e00\u7f29\uff0c\u7a81\u611f\u9876\u5fc3\u751f\u51c9\uff0c\u5934\u9876\u9053\u51a0\u7adf\u88ab\u4eba\u6293\u4e86\u53bb\u3002\u4ed6\u5fc3\u4e2d\u5927\u6012\uff0c\u957f\u5251\u4e00\u62db\u201c\u9f99\u5377\u66b4\u4f38\u201d\uff0c\u75be\u5411\u654c\u4eba\u5de6\u81c2\u524a\u53bb\u3002\u8fd9\u4e00\u62db\u6bd2\u6781\u9669\u6781\uff0c\u8881\u627f\u5fd7\u5728\u7a7a\u4e2d\u95ea\u907f\u4e0d\u53ca\uff0c\u624b\u81c2\u6025\u7f29\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u4e00\u53ea\u8896\u5b50\u5df2\u88ab\u5251\u5272\u4e0b\uff0c\u8863\u8896\u662f\u67d4\u8f6f\u4e4b\u7269\uff0c\u5728\u7a7a\u4e2d\u6beb\u4e0d\u53d7\u529b\uff0c\u4f46\u7adf\u88ab\u5b9d\u5251\u5272\u65ad\uff0c\u53ef\u89c1\u4ed6\u8fd9\u67c4\u5251\u4e0d\u4f46\u5229\u5230\u6781\u5904\uff0c\u800c\u4e14\u5185\u52b2\u529f\u529b\u4e5f\u7740\u5b9e\u60ca\u4eba\u3002\u8881\u627f\u5fd7\u4e00\u843d\u4e0b\u5730\uff0c\u5e08\u5144\u5f1f\u4e09\u4eba\u5e76\u5217\u5728\u5e08\u7236\u8eab\u524d\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4e24\u4eba\u521a\u624d\u4ea4\u4e86\u8fd9\u4e00\u62db\uff0c\u5f53\u65f6\u8fc5\u901f\u4e4b\u6781\uff0c\u5154\u8d77\u9e58\u843d\uff0c\u4e00\u95ea\u5df2\u8fc7\uff0c\u5f85\u5f97\u56de\u60f3\u9002\u624d\u60c5\u666f\uff0c\u65e0\u4e0d\u634f\u4e86\u4e00\u628a\u51b7\u6c57\u3002\u7389\u771f\u5b50\u53ea\u8981\u907f\u5f97\u6162\u4e86\u4e00\u77ac\uff0c\u5934\u76d6\u5df2\u88ab\u8881\u627f\u5fd7\u638c\u529b\u9707\u7834\uff0c\u800c\u8881\u627f\u5fd7\u7684\u624b\u81c2\u5982\u4e0d\u662f\u9000\u7f29\u5982\u7535\uff0c\u4e5f\u5df2\u88ab\u5229\u5203\u5207\u65ad\u3002 \u3000\u3000\u7389\u771f\u5b50\u4ed7\u7740\u5e08\u4f20\u7edd\u827a\uff0c\u5728\u897f\u85cf\u53c8\u5f97\u5f02\u9047\uff0c\u8fd1\u5e74\u6765\u6b66\u529f\u5927\u8fdb\uff0c\u81ea\u4fe1\u5929\u4e0b\u65e0\u4eba\u80fd\u654c\uff0c\u7eb5\u7136\u5e08\u5144\u6728\u6851\u9053\u4eba\uff0c\u4e5f\u5df2\u4e0d\u53ca\u81ea\u5df1\uff0c\u867d\u7136\u7d20\u77e5\u7a46\u4eba\u6e05\u5a01\u540d\uff0c\u4f46\u60f3\u4ed6\u5e74\u8fc8\u529b\u8870\uff0c\u53ea\u8981\u5b88\u7d27\u95e8\u6237\uff0c\u4e0e\u4ed6\u4e45\u6218\u5bf9\u8017\uff0c\u65f6\u5019\u4e00\u957f\uff0c\u5fc5\u53ef\u5360\u4ed6\u4e0a\u98ce\uff0c\u54ea\u77e5\u7a81\u7136\u95f4\u7adf\u9047\u9ad8\u624b\u5077\u88ad\uff0c\u5b9a\u795e\u4e00\u77a7\uff0c\u89c1\u5bf9\u65b9\u6b63\u662f\u53bb\u5e74\u5728\u76db\u4eac\u5c06\u81ea\u5df1\u6253\u5f97\u91cd\u4f24\u7684\u8881\u627f\u5fd7\uff0c\u90a3\u65e5\u5bb3\u5f97\u81ea\u5df1\u4e00\u4e1d\u4e0d\u6302\u3001\u4ef0\u5929\u7ffb\u5012\u5728\u7687\u592a\u6781\u4e0e\u6570\u767e\u540d\u5e03\u5e93\u6b66\u58eb\u4e4b\u524d\uff0c\u51fa\u4e11\u4e4b\u751a\uff0c\u65e0\u903e\u4e8e\u6b64\uff0c\u5f53\u665a\u7687\u592a\u6781\u201c\u65e0\u75be\u800c\u7ec8\u201d\uff0c\u4e5d\u738b\u7237\u7adf\u8bf4\u662f\u81ea\u5df1\u602a\u6a21\u602a\u6837\uff0c\u6c14\u6b7b\u4e86\u7687\u4e0a\uff0c\u8fd8\u8981\u62ff\u4ed6\u6cbb\u7f6a\uff0c\u5f53\u65f6\u91cd\u4f24\u4e4b\u4e0b\u65e0\u529b\u62b5\u6297\uff0c\u53ea\u5f97\u8bbe\u6cd5\u9003\u8d70\uff0c\u8fd9\u65f6\u4ec7\u4eba\u76f8\u89c1\uff0c\u4e0d\u7531\u5f97\u6012\u6c14\u4e0d\u53ef\u6291\u5236\uff0c\u5927\u53eb\uff1a\u201c\u8881\u627f\u5fd7\uff0c\u6211\u4eca\u65e5\u6b63\u6765\u627e\u4f60\uff0c\u5feb\u8fc7\u6765\u7eb3\u547d\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u6b64\u523b\u5012\u5df2\u7a7f\u4e0a\u4e86\u8863\u886b\uff0c\u54b1\u4eec\u597d\u597d\u7684\u6765\u6253\u4e00\u67b6\u3002\u201d \u3000\u3000\u4f55\u60d5\u5b88\u628a\u91d1\u86c7\u5251\u4ea4\u7ed9\u963f\u4e5d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u7ed9\u4ed6\u3002\u201d\u963f\u4e5d\u63d0\u5251\u8d70\u5230\u8881\u627f\u5fd7\u9762\u524d\u3002\u8881\u627f\u5fd7\u6597\u7136\u89c1\u5230\u4e86\u5979\uff0c\u4e0d\u89c9\u4e00\u6014\u3002\u963f\u4e5d\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u8bed\u97f3\u54fd\u54bd\uff0c\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002\u8881\u627f\u5fd7\u63a5\u8fc7\u5b9d\u5251\uff0c\u963f\u4e5d\u500f\u5730\u9000\u5f00\u3002 \u3000\u3000\u8fd9\u65f6\u6d53\u96fe\u521d\u6563\uff0c\u7ea2\u65e5\u6ee1\u5c71\u3002\u4f17\u4eba\u56e2\u56e2\u56f4\u4e86\u4e00\u4e2a\u5927\u5708\u5b50\u3002\u7a46\u4eba\u6e05\u5728\u4e00\u65c1\u7ed9\u6728\u6851\u63a8\u62ff\u6cbb\u4f24\u3002\u9ec4\u771f\u548c\u5f52\u8f9b\u6811\u4e00\u4e2a\u62ff\u7740\u94dc\u7b14\u94c1\u7b97\u76d8\uff0c\u4e00\u4e2a\u63d0\u7740\u70b9\u7a74\u94a2\u6293\uff0c\u7ad9\u5728\u5185\u5708\u63a0\u9635\u3002 \u3000\u3000\u7389\u771f\u5b50\u54ac\u7259\u5207\u9f7f\u7684\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u5c0f\u5077\u513f\u5462\uff1f\u6559\u4ed6\u4e00\u5757\u51fa\u6765\u9886\u6b7b\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4ed6\u5077\u4eba\u7684\u8863\u886b\u53bb\u5566\uff01\u201d\u4e4c\u5149\u95ea\u5904\uff0c\u91d1\u86c7\u5251\u5df2\u70b9\u5411\u4ed6\u9762\u95e8\u3002\u7389\u771f\u5b50\u4f5b\u5c18\u4e00\u6321\uff0c\u5de6\u624b\u5251\u5c06\u8981\u9012\u51fa\uff0c\u84e6\u89c1\u5bf9\u65b9\u5175\u5203\u5df2\u5982\u95ea\u7535\u822c\u6536\u56de\uff0c\u5251\u5c16\u5df2\u7f69\u4f4f\u4e86\u81ea\u5df1\u80f8\u53e3\u4e94\u5904\u5927\u7a74\uff0c\u53ea\u8981\u81ea\u5df1\u957f\u5251\u523a\u51fa\uff0c\u654c\u5251\u7acb\u5373\u4e58\u865a\u800c\u5165\u3002\u4ed6\u8eab\u5b50\u4e00\u6643\uff0c\u5411\u5de6\u6025\u95ea\u3002\u8881\u627f\u5fd7\u77e5\u9053\u4ed6\u8fd9\u4e00\u4e0b\u5b88\u4e2d\u5e26\u653b\uff0c\u53ea\u5f85\u91d1\u86c7\u5251\u523a\u51fa\uff0c\u4ed6\u5c31\u4f1a\u75be\u653b\u81ea\u5df1\u53f3\u4fa7\uff0c\u5f53\u4e0b\u6a2a\u8fc7\u5b9d\u5251\uff0c\u5148\u62a4\u81ea\u8eab\u3002\u4ed6\u77e5\u5bf9\u65b9\u6781\u5f3a\uff0c\u52a1\u5f53\u9075\u7167\u5e08\u8bad\uff0c\u5148\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u4ee5\u6c42\u654c\u4e4b\u53ef\u80dc\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u5251\uff0c\u60c5\u52bf\u53c8\u81ea\u4e0d\u540c\uff0c\u4e24\u4eba\u4efb\u4f55\u90e8\u4f4d\u4e00\u52a8\uff0c\u5bf9\u65b9\u4fbf\u77e5\u7528\u610f\u6240\u5728\u3002\u65c1\u89c2\u4f17\u4eba\u4e2d\u6b66\u529f\u8f83\u6d45\u7684\uff0c\u89c1\u4e24\u4eba\u53cc\u76ee\u4e92\u89c6\uff0c\u8eab\u6cd5\u5446\u6ede\uff0c\u51fa\u62db\u4f3c\u4e4e\u5341\u5206\u677e\u61c8\uff0c\u5c82\u77e5\u80dc\u8d1f\u51b3\u4e8e\u77ac\u606f\uff0c\u751f\u547d\u60ac\u4e8e\u4e00\u53d1\uff0c\u6bd4\u4e4b\u72c2\u547c\u9163\u6218\uff0c\u5b9e\u53c8\u51f6\u9669\u5f97\u591a\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u6068\u6781\u7389\u771f\u5b50\u521a\u624d\u4fae\u8fb1\u81ea\u5df1\uff0c\u6c14\u6124\u96be\u5f53\uff0c\u89c1\u4e24\u4eba\u51dd\u795e\u76f8\u6597\uff0c\u633a\u8d77\u5355\u94a9\uff0c\u60f3\u62a2\u4e0a\u53bb\u523a\u8fd9\u6076\u9053\u4e00\u94a9\u3002\u6885\u5251\u548c\u89c1\u5979\u4e3e\u94a9\u4e0a\u524d\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u5fd9\u4f38\u624b\u62c9\u4f4f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8981\u547d\u4e48\uff1f\u5e72\u751a\u4e48\uff1f\u201d\u5b59\u4ef2\u541b\u6012\u9053\uff1a\u201c\u522b\u7ba1\u6211\u3002\u6211\u8ddf\u8d3c\u9053\u62da\u4e86\u3002\u201d\u6885\u5251\u548c\u9053\uff1a\u201c\u8d3c\u9053\u5df2\u77e5\u5c0f\u5e08\u53d4\u7684\u5389\u5bb3\uff0c\u6b63\u7528\u6700\u4e0a\u4e58\u5251\u6cd5\u62a4\u4f4f\u4e86\u5168\u8eab\uff0c\u4f60\u4e0a\u53bb\u662f\u767d\u9001\u6027\u547d\u3002\u201d\u5b59\u4ef2\u541b\u7528\u529b\u7529\u8131\u4ed6\u624b\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\uff0c\u6211\u53bb\u5e2e\u5e08\u53d4\u3002\u201d\u5979\u4ee5\u524d\u607c\u6068\u8881\u627f\u5fd7\uff0c\u4ece\u6765\u4e0d\u63d0\u201c\u5e08\u53d4\u201d\u4e24\u5b57\uff0c\u8fd9\u65f6\u89c1\u4ed6\u4e0e\u6076\u9053\u4e3a\u654c\uff0c\u7adf\u7136\u4e8e\u9877\u523b\u95f4\u5bbf\u6028\u5c3d\u6d88\u3002\u6885\u5251\u548c\u9053\uff1a\u201c\u90a3\u4f60\u53d1\u4e00\u4ef6\u6697\u5668\u8bd5\u8bd5\uff01\u201d\u5b59\u4ef2\u541b\u53d6\u51fa\u91d1\u9556\uff0c\u8fd0\u52b2\u5f80\u7389\u771f\u5b50\u80cc\u540e\u63b7\u53bb\u3002\u7389\u771f\u5b50\u5168\u795e\u51dd\u89c6\u8881\u627f\u5fd7\u7684\u5251\u5c16\uff0c\u91d1\u9556\u98de\u6765\uff0c\u72b9\u5982\u672a\u89c9\u3002\u5b59\u4ef2\u541b\u6b63\u559c\u5f97\u624b\uff0c\u7a81\u542c\u5f53\u7684\u4e00\u58f0\uff0c\u6885\u5251\u548c\u5931\u58f0\u5927\u53eb\uff1a\u201c\u4e0d\u597d\uff01\u201d\u62b1\u4f4f\u5979\u8eab\u5b50\u5f80\u4e0b\u4fbf\u5012\u3002 \u3000\u3000\u5b59\u4ef2\u541b\u521a\u6251\u4e0b\u5730\uff0c\u53ea\u89c1\u521a\u624d\u53d1\u51fa\u7684\u91d1\u9556\u9556\u5c16\u5df2\u5c04\u5411\u81ea\u5df1\u80f8\u524d\uff0c\u5168\u6ca1\u770b\u6e05\u90a3\u6076\u9053\u5982\u4f55\u4f1a\u628a\u9556\u6fc0\u6253\u56de\u6765\uff0c\u5176\u65f6\u5df2\u4e0d\u53ca\u95ea\u907f\u62b5\u6321\uff0c\u53ea\u6709\u7741\u76ee\u5f85\u6b7b\uff0c\u4fbf\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u767d\u5f71\u4e00\u6643\uff0c\u4e00\u53ea\u7ea4\u7ea4\u7d20\u624b\u5ffd\u5730\u4f38\u4e86\u8fc7\u6765\uff0c\u53cc\u6307\u5939\u4f4f\u9556\u540e\u7ea2\u5e03\uff0c\u62c9\u4f4f\u4e86\u91d1\u9556\u3002\u6885\u5251\u548c\u4e0e\u5b59\u4ef2\u541b\u5fc3\u4e2d\u535c\u535c\u4e71\u8df3\uff0c\u8df3\u8d77\u8eab\u6765\uff0c\u624d\u77e5\u6551\u5979\u6027\u547d\u7684\u539f\u6765\u662f\u4f55\u60d5\u5b88\uff0c\u4e0d\u7981\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u540c\u65f6\u70b9\u5934\u793a\u8c22\u3002 \u3000\u3000\u8fd9\u65f6\u8881\u627f\u5fd7\u548c\u7389\u771f\u5b50\u5251\u6cd5\u5ffd\u53d8\uff0c\u4e24\u4eba\u90fd\u662f\u4ee5\u5feb\u6253\u5feb\uff0c\u5168\u529b\u62a2\u653b\u3002\u4f46\u89c1\u8881\u627f\u5fd7\u5c06\u4e00\u67c4\u91d1\u86c7\u5251\u4f7f\u5c06\u5f00\u6765\uff0c\u516b\u6210\u662f\u534e\u5c71\u6b63\u5b97\u5251\u6cd5\uff0c\u5076\u5c14\u5939\u7740\u4e00\u4e24\u4e0b\u8be1\u5f02\u62db\u5f0f\uff0c\u4e8e\u5802\u5802\u4e4b\u9635\u4e2d\u5947\u5175\u7a81\u51fa\uff0c\u8fde\u7a46\u4eba\u6e05\u7adf\u7136\u4e5f\u89c9\u773c\u754c\u5927\u5f00\uff0c\u53ea\u770b\u5f97\u4e0d\u4f4f\u70b9\u5934\u3002\u6728\u6851\u8138\u9732\u5fae\u7b11\uff0c\u5583\u5583\u9053\uff1a\u201c\u597d\u68cb\uff0c\u597d\u68cb\uff0c\u5999\u7740\u6a2a\u751f\uff01\u201d\u9ec4\u771f\u3001\u5f52\u8f9b\u6811\u3001\u5f52\u4e8c\u5a18\u5fc3\u4e0b\u94a6\u4f69\u3002\u5176\u4f59\u534e\u5c71\u6d3e\u5f1f\u5b50\u81ea\u51af\u96be\u654c\u4ee5\u4e0b\u65e0\u4e0d\u773c\u82b1\u7f2d\u4e71\uff0c\u6322\u820c\u4e0d\u4e0b\u3002\u6597\u5230\u5206\u9645\uff0c\u4e24\u4eba\u90fd\u4f7f\u51fa\u201c\u795e\u884c\u767e\u53d8\u201d\u529f\u592b\u6765\u3002\u7389\u771f\u5b50\u5728\u76db\u4eac\u89c1\u8881\u627f\u5fd7\u4f1a\u8fd9\u95e8\u8f7b\u529f\uff0c\u81ea\u5fc5\u662f\u6728\u6851\u7684\u4f20\u4eba\uff0c\u4ed6\u867d\u662f\u534e\u5c71\u95e8\u4e0b\uff0c\u4f46\u81ea\u4e5f\u7b97\u662f\u94c1\u5251\u95e8\u95e8\u4eba\uff0c\u6b64\u756a\u6765\u5230\u534e\u5c71\uff0c\u539f\u662f\u60f3\u6043\u94c1\u5251\u800c\u53d6\u4ed6\u6027\u547d\uff0c\u4ee5\u96ea\u53bb\u5e74\u7684\u5947\u803b\u5927\u8fb1\u3002\u4e24\u4eba\u73af\u7ed5\u8f6c\u6298\uff0c\u6597\u4e86\u6570\u5341\u5408\uff0c\u7389\u771f\u5b50\u5ffd\u5730\u8df3\u5f00\uff0c\u53d6\u51fa\u5c0f\u94c1\u5251\u4e00\u626c\uff0c\u559d\u9053\uff1a\u201c\u4f60\u65e2\u662f\u94c1\u5251\u95e8\u5f1f\u5b50\uff0c\u89c1\u4e86\u94c1\u5251\u8fd8\u4e0d\u8dea\u4e0b\uff1f\u201d \u3000\u3000\u8881\u627f\u5fd7\u9053\uff1a\u201c\u6211\u662f\u534e\u5c71\u6d3e\u95e8\u4e0b\u3002\u201d\u7389\u771f\u5b50\u559d\u9053\uff1a\u201c\u4f60\u5982\u4e0d\u662f\u6728\u6851\u7684\u5f1f\u5b50\uff0c\u600e\u4f1a\u61c2\u5f97\u795e\u884c\u767e\u53d8\u529f\u592b\uff1f\u4f60\u662f\u4ed6\u5f1f\u5b50\uff0c\u81ea\u7136\u662f\u94c1\u5251\u95e8\u4e2d\u4eba\u4e86\u3002\u94c1\u5251\u5728\u6211\u624b\u4e2d\uff0c\u5feb\u8dea\u4e0b\u542c\u7531\u5904\u5206\u3002\u201d\u8881\u627f\u5fd7\u7b11\u9053\uff1a\u201c\u4f60\u5feb\u8dea\u4e0b\uff0c\u542c\u6211\u5904\u5206\uff01\u201d\u7389\u771f\u5b50\u8f6c\u5934\u95ee\u6728\u6851\u9053\uff1a\u201c\u4ed6\u7684\u795e\u884c\u767e\u53d8\u8f7b\u529f\uff0c\u96be\u9053\u4e0d\u662f\u4f60\u4f20\u6388\u7684\u4e48\uff1f\u201d\u6728\u6851\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eb2\u6388\u7684\u3002\u201d\u7389\u771f\u5b50\u77e5\u9053\u5e08\u5144\u4ece\u6765\u4e0d\u6253\u8bf3\u8bed\uff0c\u5fc3\u4e2d\u5927\u5947\uff0c\u5fae\u4e00\u6c89\u541f\uff0c\u8fdb\u8eab\u51fa\u62db\uff0c\u4e24\u4eba\u53c8\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u8881\u627f\u5fd7\u653b\u5b88\u8fdb\u62d2\uff0c\u5fc3\u4e2d\u7422\u78e8\u4ed6\u521a\u624d\u7684\u51e0\u53e5\u8bdd\uff0c\u5ffd\u7136\u60f3\u8d77\uff1a\u201c\u6728\u6851\u9053\u957f\u4ece\u524d\u4f20\u6211\u6280\u827a\uff0c\u53ea\u5f53\u662f\u5728\u56f4\u68cb\u4e0a\u8f93\u4e86\u800c\u7ed9\u7684\u5f69\u5934\uff0c\u51b3\u4e0d\u8bb8\u6211\u53eb\u4ed6\u5e08\u7236\u3002\u540e\u6765\u8fd9\u795e\u884c\u767e\u53d8\u8f7b\u529f\u53c8\u547d\u9752\u5f1f\u8f6c\u6388\u3002\u539f\u6765\u5176\u4e2d\u53e6\u6709\u6df1\u610f\uff0c\u5012\u5e76\u975e\u5168\u662f\u6ed1\u7a3d\u53e4\u602a\u3002\u201d \u3000\u3000\u4ed6\u60f3\u5230\u9752\u9752\uff0c\u60c5\u5207\u5173\u5fc3\uff0c\u4e0d\u7531\u5f97\u8f6c\u5934\u5411\u5979\u4e00\u671b\uff0c\u53ea\u89c1\u5979\u501a\u5728\u4e00\u5757\u5927\u77f3\u4e4b\u65c1\uff0c\u53e3\u4e2d\u542b\u4e86\u4e00\u5757\u6731\u7ea2\u8272\u7684\u836f\u997c\uff0c\u4f55\u60d5\u5b88\u6b63\u5728\u5272\u7834\u5979\u624b\u8155\u653e\u8840\u89e3\u6bd2\u3002\u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u559c\u4ece\u5929\u964d\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u4e2d\u4e86\u4e94\u6bd2\u6559\u7684\u5267\u6bd2\uff0c\u60d5\u5b88\u81ea\u7136\u77e5\u9053\u89e3\u6cd5\uff0c\u8fd9\u4e00\u6765\u53ef\u6709\u6551\u4e86\u3002\u201d \u3000\u3000\u4f46\u9ad8\u624b\u6bd4\u6b66\uff0c\u54ea\u5bb9\u5f97\u5fc3\u6709\u65c1\u9a9b\uff1f\u4ed6\u7a81\u7136\u5927\u559c\uff0c\u5fc3\u795e\u4e0d\u4e13\uff0c\u5de6\u80a9\u4fa7\u52a8\u5fae\u6162\uff0c\u7389\u771f\u5b50\u597d\u5bb9\u6613\u5f97\u5230\u8fd9\u4e2a\u7a7a\u9699\uff0c\u7acb\u5373\u4e58\u673a\u76f4\u4e0a\uff0c\u5237\u7684\u4e00\u5251\uff0c\u6b63\u523a\u5728\u4ed6\u5de6\u80c1\u3002\u4f17\u4eba\u9f50\u58f0\u60ca\u547c\uff0c\u5c82\u77e5\u7389\u771f\u5b50\u4e00\u60ca\u66f4\u751a\uff0c\u539f\u6765\u8fd9\u4e00\u5251\u7adf\u7136\u523a\u4e0d\u8fdb\u53bb\uff0c\u88ab\u4ed6\u8eab\u5b50\u53cd\u5f39\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7389\u771f\u5b50\u5f53\u5e74\u8ddf\u6728\u6851\u52a8\u624b\uff0c\u4e5f\u66fe\u5ffd\u4f7f\u602a\u62db\uff0c\u4e00\u5251\u523a\u4e2d\u4e86\u5e08\u5144\uff0c\u5374\u88ab\u5200\u5251\u4e0d\u5165\u7684\u91d1\u4e1d\u80cc\u5fc3\u53cd\u5f39\u51fa\u6765\uff0c\u4ee5\u81f4\u53cd\u4e3a\u6240\u5236\u3002\u6728\u6851\u77a7\u5728\u540c\u95e8\u60c5\u8c0a\uff0c\u8fd9\u624d\u9976\u4e86\u4ed6\u3002\u6b64\u523b\u65e7\u4e8b\u91cd\u6f14\uff0c\u7389\u771f\u5b50\u6025\u6012\u4ea4\u8fdb\uff0c\u60c5\u77e5\u53c8\u662f\u6728\u6851\u6363\u9b3c\uff0c\u6697\u60f3\u8fd9\u5c11\u5e74\u6b66\u529f\u5947\u9ad8\uff0c\u4e0d\u5728\u6211\u4e0b\uff0c\u73b0\u4e0b\u6211\u523a\u4ed6\u4e0d\u4f24\uff0c\u5c82\u4e0d\u6210\u4e86\u6709\u8d25\u65e0\u80dc\u7684\u5c40\u9762\uff0c\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u89c9\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u9752\u9752\u795e\u667a\u521d\u590d\uff0c\u5ffd\u89c1\u8881\u627f\u5fd7\u4e2d\u5251\uff0c\u6012\u9053\uff1a\u201c\u4f60\u523a\u6211\u5927\u54e5\uff01\u201d \u3000\u3000\u4ece\u6000\u91cc\u638f\u51fa\u94c1\u7ba1\uff0c\u62d4\u53bb\u585e\u5b50\uff0c\u594b\u529b\u5411\u7389\u771f\u5b50\u4e00\u6296\u3002\u5c0f\u91d1\u86c7\u6fc0\u5c04\u800c\u51fa\uff0c\u5f20\u5634\u5f80\u7389\u771f\u5b50\u54ac\u53bb\u3002 \u3000\u3000\u7389\u771f\u5b50\u6025\u5fd9\u4f4e\u5934\u95ea\u907f\uff0c\u54ea\u77e5\u5c0f\u91d1\u86c7\u5177\u6709\u7075\u6027\uff0c\u5728\u7a7a\u4e2d\u5f80\u4e0b\u4e00\u51b2\uff0c\u53c8\u5f80\u4ed6\u5934\u4e0a\u54ac\u6765\u3002\u8981\u662f\u6362\u4e86\u65c1\u4eba\uff0c\u5c0f\u91d1\u86c7\u8fd9\u4e00\u51b2\u4e00\u54ac\u7edd\u96be\u907f\u8fc7\uff0c\u4f46\u7389\u771f\u5b50\u4f55\u7b49\u529f\u592b\uff0c\u62c2\u5c18\u4e00\u6296\uff0c\u5df2\u5377\u4f4f\u91d1\u86c7\uff0c\u5fc3\u77e5\u5982\u518d\u8fd0\u52b2\u63b7\u51fa\u91d1\u86c7\uff0c\u5bf9\u624b\u5b9a\u4f1a\u4e58\u865a\u653b\u8fdb\uff0c\u767e\u5fd9\u4e2d\u8fde\u62c2\u5c18\u5e26\u86c7\u5f80\u5730\u4e0b\u4e00\u629b\uff0c\u7eb5\u51fa\u6570\u6b65\u3002 \u3000\u3000\u8881\u627f\u5fd7\u4e45\u6218\u4e0d\u4e0b\uff0c\u6b63\u60f3\u4e0d\u51fa\u7528\u4f55\u79cd\u5251\u6cd5\u80dc\u4ed6\uff0c\u8fd9\u65f6\u5ffd\u89c1\u91d1\u86c7\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u8d77\u5f53\u65e5\u86c7\u4e10\u96ea\u5730\u76f8\u6597\uff0c\u90a3\u5c0f\u86c7\u7075\u52a8\u5de7\u5999\u7684\u8eab\u6cd5\uff0c\u8ddf\u91d1\u86c7\u90ce\u541b\u6240\u4f20\u7684\u4e00\u5957\u5251\u6cd5\u9887\u6709\u6697\u5408\u4e4b\u5904\uff0c\u5f53\u4e0b\u4e0d\u53ca\u7ec6\u60f3\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u7ef5\u7ef5\u800c\u4e0a\u3002 \u3000\u3000\u7389\u771f\u5b50\u89c1\u4ed6\u8eab\u6cd5\u5947\u8be1\uff0c\u5df2\u5168\u975e\u94c1\u5251\u95e8\u7684\u201c\u795e\u884c\u767e\u53d8\u201d\u529f\u592b\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u62da\u529b\u62b5\u62d2\uff0c\u4f46\u5bf9\u65b9\u5251\u62db\u8eab\u6cd5\uff0c\u751f\u5e73\u4ece\u6240\u672a\u89c1\uff0c\u602a\u62db\u5982\u5265\u8327\u62bd\u4e1d\uff0c\u6c38\u65e0\u6b62\u6b47\uff0c\u60ca\u60f6\u4e2d\u53ea\u5f97\u8fde\u8fde\u5012\u9000\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u4ed6\u6b65\u6cd5\u5fae\u4e71\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u731b\u653b\u6570\u62db\uff0c\u91d1\u86c7\u5251\u4f7f\u51fa\u4e00\u62db\u201c\u91d1\u86c7\u4e07\u9053\u201d\uff0c\u8fd9\u62db\u5251\u6cd5\u867d\u662f\u4e00\u62db\uff0c\u5176\u4e2d\u4fbf\u5982\u6709\u5343\u767e\u62db\u540c\u65f6\u53d1\u51fa\u4e00\u822c\u3002\u7389\u771f\u5b50\u77a7\u4e0d\u6e05\u654c\u62db\u6765\u8def\uff0c\u53ea\u5f97\u75be\u9000\u95ea\u907f\u3002\u8881\u627f\u5fd7\u4e58\u52bf\u800c\u4e0a\uff0c\u91d1\u86c7\u5251\u81ea\u5de6\u800c\u53f3\u7684\u63a0\u53bb\u3002\u7389\u771f\u5b50\u5927\u9a87\uff0c\u6025\u5fd9\u4f4e\u5934\u76f8\u907f\uff0c\u55e4\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u5934\u53d1\u5df2\u88ab\u524a\u53bb\u4e86\u4e00\u622a\u3002\u8881\u627f\u5fd7\u5de6\u638c\u968f\u51fa\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6253\u5728\u4ed6\u80f8\u524d\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u5374\u662f\u534e\u5c71\u6d3e\u672c\u95e8\u5ae1\u4f20\u7684\u6df7\u5143\u638c\u529f\u592b\u3002\u7389\u771f\u5b50\u53e3\u55b7\u9c9c\u8840\uff0c\u5411\u540e\u4fbf\u8dcc\uff0c\u7a81\u89c9\u9888\u4e0a\u4e00\u75db\uff0c\u5374\u662f\u88ab\u4ed6\u6454\u5728\u5730\u4e0b\u7684\u5c0f\u91d1\u86c7\u7262\u7262\u54ac\u4f4f\u4e86\u3002\u4ed6\u5185\u529f\u6df1\u539a\uff0c\u53d7\u4e86\u8881\u627f\u5fd7\u8fd9\u638c\u53ea\u662f\u91cd\u4f24\uff0c\u5c1a\u4e0d\u81f4\u547d\uff0c\u4f46\u91d1\u86c7\u5947\u6bd2\uff0c\u53c8\u54ac\u4f4f\u540e\u9888\u7684\u201c\u5929\u67f1\u7a74\u201d\u8981\u7a74\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5168\u8eab\u53d1\u9ed1\u800c\u6b7b\u3002 \u3000\u3000\u4f17\u5f1f\u5b50\u89c1\u8881\u627f\u5fd7\u6253\u8d25\u52b2\u654c\uff0c\u65e0\u4e0d\u94a6\u4f69\u4e07\u5206\u3002\u51af\u96be\u654c\u4e0a\u524d\u62dc\u5012\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u5e08\u53d4\uff0c\u8bf7\u6055\u5f1f\u5b50\u6628\u65e5\u65e0\u793c\u3002\u201d\u8881\u627f\u5fd7\u5df2\u7d2f\u5f97\u5168\u8eab\u5927\u6c57\u6dcb\u6f13\uff0c\u6025\u5fd9\u6276\u8d77\uff0c\u5374\u5c06\u6c57\u6c34\u6ef4\u4e86\u51af\u96be\u654c\u6ee1\u5934\u3002\u5b59\u4ef2\u541b\u62fe\u8d77\u51e0\u5757\u5927\u77f3\uff0c\u7838\u5728\u7389\u771f\u5b50\u5c38\u8eab\u4e4b\u4e0a\uff0c\u8f6c\u5934\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u8881\u5e08\u53d4\u7ed9\u6211\u51fa\u6c14\u3002\u201d \u3000\u3000\u6728\u6851\u8fde\u8fde\u53f9\u606f\uff0c\u547d\u54d1\u5df4\u5c06\u7389\u771f\u5b50\u6536\u6b93\u5b89\u846c\uff0c\u624b\u629a\u94c1\u5251\uff0c\u8bf4\u51fa\u4e00\u6bb5\u5f80\u4e8b\u3002 \u3000\u3000\u539f\u6765\u7389\u771f\u5b50\u548c\u4ed6\u5f53\u5e74\u540c\u95e8\u5b66\u827a\uff0c\u4ed6\u4eec\u8fd9\u4e00\u6d3e\u79f0\u4e3a\u94c1\u5251\u95e8\uff0c\u5f00\u5c71\u7956\u5e08\u6240\u7528\u7684\u94c1\u5251\u4ee3\u4ee3\u76f8\u4f20\uff0c\u79f0\u4e3a\u201c\u638c\u95e8\u4e4b\u5b9d\u201d\u3002\u6709\u4e00\u5e74\u4ed6\u4eec\u5e08\u7236\u5728\u897f\u85cf\u901d\u4e16\uff0c\u94c1\u5251\u4ece\u6b64\u4e0d\u77e5\u4e0b\u843d\u3002 \u3000\u3000\u7389\u771f\u5b50\u521d\u65f6\u52e4\u4e8e\u5b66\u6b66\uff0c\u4e3a\u4eba\u6b63\u6d3e\uff0c\u4e0d\u6599\u5e08\u7236\u4e00\u6b7b\uff0c\u6ca1\u4eba\u7ba1\u675f\uff0c\u7ed3\u4ea4\u635f\u53cb\uff0c\u7adf\u5982\u5b8c\u5168\u53d8\u4e86\u4e00\u4e2a\u4eba\u3002\u4ed6\u81ea\u5e7c\u51fa\u5bb6\uff0c\u4e0d\u8fd1\u5973\u8272\uff0c\u8fd9\u65f6\u5374\u5978\u76d7\u6ee5\u6740\uff0c\u65e0\u6076\u4e0d\u4f5c\u3002\u4ed6\u6b66\u827a\u53c8\u9ad8\uff0c\u7adf\u6ca1\u4eba\u5948\u4f55\u5f97\u4e86\u4ed6\u3002\u6728\u6851\u548c\u4ed6\u95f9\u4e86\u4e00\u573a\uff0c\u6597\u4e86\u4e24\u6b21\uff0c\u5e08\u5144\u5e08\u5f1f\u5212\u5730\u7edd\u4ea4\u3002 \u3000\u3000\u7389\u771f\u5b50\u6597\u4e0d\u8fc7\u5e08\u5144\uff0c\u8fdc\u53bb\u897f\u85cf\uff0c\u4e00\u9762\u52e4\u7ec3\u6b66\u529f\uff0c\u4e00\u9762\u5bfb\u8bbf\u94c1\u5251\uff0c\u540e\u6765\u7ec8\u4e8e\u88ab\u4ed6\u627e\u5230\u3002\u6309\u7167\u4ed6\u4eec\u95e8\u4e2d\u89c4\u77e9\uff0c\u89c1\u94c1\u5251\u5982\u89c1\u7956\u5e08\uff0c\u638c\u6267\u94c1\u5251\u7684\u5c31\u662f\u672c\u95e8\u638c\u95e8\u4eba\uff0c\u53ea\u8981\u662f\u672c\u95e8\u4e2d\u4eba\uff0c\u8c01\u90fd\u5f97\u542c\u4ed6\u53f7\u4ee4\u5904\u5206\u3002\u6728\u6851\u5728\u5357\u4eac\u4e0e\u8881\u627f\u5fd7\u76f8\u89c1\u4e4b\u65f6\uff0c\u5df2\u542c\u5f97\u8baf\u606f\uff0c\u8bf4\u7389\u771f\u5b50\u5df2\u5728\u897f\u85cf\u627e\u5230\u4e86\u94c1\u5251\uff0c\u77e5\u9053\u6b64\u4e8b\u4e3a\u7978\u4e0d\u5c11\uff0c\u51b3\u610f\u8d76\u53bb\uff0c\u8bbe\u6cd5\u6697\u4e2d\u593a\u5c06\u8fc7\u6765\u3002\u54ea\u77e5\u4ed6\u897f\u884c\u4e0d\u4e45\uff0c\u4fbf\u5728\u9ec4\u5c71\u9047\u4e0a\u4e00\u4e2a\u56f4\u68cb\u597d\u624b\uff0c\u4e00\u5f08\u4e4b\u4e0b\uff0c\u6728\u6851\u5168\u519b\u5c3d\u6ca1\u3002\u4ed6\u8d8a\u8f93\u8d8a\u662f\u4e0d\u670d\uff0c\u7f20\u4e0a\u4e86\u8fde\u5955\u6570\u6708\uff0c\u90a3\u9ad8\u68cb\u4e4b\u4eba\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u5f97\u5047\u610f\u8f93\u4e86\u4e24\u5c40\uff0c\u6728\u6851\u624d\u653e\u4ed6\u8131\u8eab\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u4fbf\u5c06\u8fd9\u4ef6\u5927\u4e8b\u7ed9\u803d\u6401\u4e86\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u542c\u4e86\u8fd9\u756a\u8bdd\uff0c\u4e0d\u7981\u559f\u7136\u800c\u53f9\uff0c\u8f6c\u5934\u95ee\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u5e72\u4e48\u8ffd\u4f60\u554a\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6251\u5730\u8dea\u5012\uff0c\u54ed\u9053\uff1a\u201c\u8bf7\u7a46\u8001\u7237\u5b50\u6551\u6211\u4e08\u592b\u6027\u547d\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u542c\u4e86\u8fd9\u8bdd\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5fd9\u4f38\u624b\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5ac2\u5ac2\u8bf7\u8d77\u3002\u5927\u54e5\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u5434\u4e09\u6842\u52fe\u7ed3\u6ee1\u6e05\u9791\u5b50\uff0c\u653b\u8fdb\u4e86\u5c71\u6d77\u5173\u3002\u95ef\u738b\u63a5\u6218\u4e0d\u5229\uff0c\u5e26\u961f\u9000\u51fa\u5317\u4eac\uff0c\u73b0\u4eca\u662f\u5728\u897f\u5b89\u3002\u4e0d\u6599\u4e1e\u76f8\u725b\u91d1\u661f\u548c\u6743\u5c06\u519b\u5218\u5b97\u654f\u5411\u95ef\u738b\u6311\u62e8\u662f\u975e\uff0c\u8bec\u9677\u674e\u5c06\u519b\u56fe\u8c0b\u81ea\u7acb\uff0c\u95ef\u738b\u4fbf\u8981\u902e\u62ff\u674e\u5c06\u519b\u6cbb\u7f6a\u3002\u6211\u9003\u51fa\u6765\u6c42\u6551\uff0c\u90a3\u5218\u5b97\u654f\u4e00\u8def\u6d3e\u4eba\u8ffd\u6211\u2026\u2026\u201d \u3000\u3000\u4f17\u4eba\u542c\u8bf4\u6e05\u5175\u8fdb\u5173\uff0c\u5317\u4eac\u5931\u9677\uff0c\u90fd\u5982\u7a81\u7136\u95f4\u6674\u5929\u6253\u4e86\u4e00\u4e2a\u9739\u96f3\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u5927\u6025\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u53bb\u6551\uff0c\u8fdf\u4e00\u6b65\u53ea\u6015\u6765\u4e0d\u53ca\u4e86\uff01\u201d\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd9\u6b21\u5e08\u7236\u53ec\u96c6\u95e8\u4eba\u805a\u4f1a\u534e\u5c71\uff0c\u5fc5\u6709\u8981\u4e8b\u76f8\u5546\uff0c\u8fd9\u5982\u4f55\u662f\u597d\uff1f\u671b\u7740\u5e08\u7236\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e71\u5982\u9ebb\u3002\u4ed6\u5e74\u7eaa\u8f7b\uff0c\u9605\u5386\u5c11\uff0c\u539f\u65e0\u591a\u5927\u5e94\u53d8\u4e4b\u80fd\uff0c\u4e4d\u9022\u96be\u4e8b\uff0c\u4e00\u65f6\u95f4\u5fac\u5fa8\u5931\u63aa\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u5404\u4eba\u5df2\u7ecf\u5230\u9f50\uff0c\u54b1\u4eec\u4fbf\u5c3d\u5feb\u628a\u4e8b\u60c5\u529e\u4e86\u7f62\uff01\u201d \u3000\u3000\u8bf4\u7740\u8bf7\u51fa\u98ce\u5e08\u7956\u9057\u5bb9\uff0c\u6446\u4e86\u9999\u6848\uff0c\u70b9\u4e0a\u9999\u70db\u3002\u4f17\u5f1f\u5b50\u4e00\u4e00\u8dea\u4e0b\u3002\u4f55\u60d5\u5b88\u7f29\u5728\u4e00\u89d2\uff0c\u5077\u773c\u671b\u7740\u8881\u627f\u5fd7\u3002 \u3000\u3000\u7a46\u4eba\u6e05\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u575a\u8981\u5165\u6211\u95e8\u4e2d\uff0c\u5176\u5b9e\u4ee5\u4f60\u6b66\u529f\uff0c\u65e9\u5df2\u591f\u5f97\u7eb5\u6a2a\u6c5f\u6e56\u4e86\u3002\u9002\u624d\u6211\u5728\u6811\u540e\u77a7\u4f60\u8ddf\u7389\u771f\u5b50\u76f8\u6597\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u4f60\uff0c\u6211\u8fd9\u4e9b\u5f92\u5b59\u4e2a\u4e2a\u975e\u5012\u5927\u9709\u4e0d\u53ef\u3002\u4f60\u53eb\u6211\u6eda\u86cb\uff0c\u54c8\u54c8\uff0c\u6211\u504f\u504f\u4e0d\u6eda\uff0c\u8fd9\u4e00\u63a8\u624b\uff0c\u4f60\u53ea\u8dcc\u51fa\u56db\u6b65\uff0c\u4fbf\u5373\u7ad9\u7a33\u3002\u6211\u95e8\u4e2d\u9664\u4e86\u4e09\u4e2a\u4eb2\u4f20\u5f1f\u5b50\uff0c\u8fd8\u6ca1\u7b2c\u56db\u4eba\u6709\u8fd9\u529f\u529b\u5462\u3002\u597d\u597d\u597d\uff0c\u4f60\u4e5f\u8dea\u4e0b\u5427\uff01\u201d\u4f55\u60d5\u5b88\u5927\u559c\uff0c\u8ddf\u5728\u8881\u627f\u5fd7\u4e4b\u540e\uff0c\u5411\u98ce\u5e08\u7956\u9057\u5bb9\u78d5\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4f4d\u7956\u5e08\u7237\u8bf4\u8bdd\u6709\u8da3\uff0c\u5012\u5f88\u6148\u548c\u3002\u201d \u3000\u3000\u884c\u793c\u5df2\u6bd5\uff0c\u7a46\u4eba\u6e05\u7ad9\u5728\u6b63\u4e2d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5e74\u4e8b\u5df2\u9ad8\uff0c\u4e0d\u80fd\u518d\u7406\u4e16\u4e8b\u4fd7\u52a1\u3002\u534e\u5c71\u6d3e\u95e8\u6237\u4e8b\u5b9c\uff0c\u4ece\u4eca\u65e5\u8d77\u7531\u5927\u5f1f\u5b50\u9ec4\u771f\u6267\u638c\u3002\u201d \u3000\u3000\u9ec4\u771f\u609a\u7136\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u5f1f\u5b50\u6b66\u529f\u8fdc\u4e0d\u53ca\u4e8c\u5e08\u5f1f\u3001\u4e09\u5e08\u5f1f\u2026\u2026\u201d\u7a46\u4eba\u6e05\u9053\uff1a\u201c\u638c\u63e1\u95e8\u6237\uff0c\u4f46\u6c42\u7763\u8d23\u8bf8\u5f1f\u5b50\u4e25\u5b88\u6212\u5f8b\uff0c\u884c\u4fa0\u4ed7\u4e49\u3002\u4f60\u597d\u597d\u505a\u5427\uff01\u201d\u9ec4\u771f\u4e0d\u6562\u518d\u8f9e\uff0c\u91cd\u884c\u78d5\u62dc\u7956\u5e08\u548c\u5e08\u7236\uff0c\u53d7\u4e86\u638c\u95e8\u7684\u7b26\u5370\u3002\u672c\u95e8\u5f1f\u5b50\u53c2\u89c1\u638c\u95e8\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u5927\u4e8b\u5df2\u4e86\uff0c\u60ac\u5ff5\u4e49\u5144\uff0c\u4fbf\u6b32\u8981\u4e0b\u5c71\uff0c\u5bf9\u9752\u9752\u9053\uff1a\u201c\u9752\u5f1f\uff0c\u4f60\u5728\u8fd9\u91cc\u4f11\u517b\uff0c\u6211\u6551\u4e49\u5144\u540e\u5373\u6765\u77a7\u4f60\u3002\u201d\u9752\u9752\u4e0d\u7b54\uff0c\u53ea\u662f\u77a7\u7740\u963f\u4e5d\uff0c\u5fc3\u4e2d\u6c14\u6124\uff0c\u773c\u5708\u4e00\u7ea2\uff0c\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u963f\u4e5d\u7a81\u7136\u8d70\u5230\u5979\u8ddf\u524d\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u9752\u59ca\u59ca\uff0c\u4f60\u4e0d\u518d\u6068\u6211\u4e86\u5427\uff1f\u201d\u4f38\u624b\u62c9\u4e0b\u76ae\u5e3d\uff0c\u9732\u51fa\u4e00\u4e2a\u5149\u5934\u3002\u539f\u6765\u5979\u7236\u4e27\u56fd\u4ea1\uff0c\u53c8\u4ece\u4f55\u60d5\u5b88\u53e3\u4e2d\u5f97\u77e5\u4e86\u8881\u627f\u5fd7\u5bf9\u9752\u9752\u7684\u4e00\u7247\u60c5\u610f\uff0c\u5fc3\u7070\u610f\u61d2\uff0c\u5728\u534a\u8def\u4e0a\u6084\u6084\u81ea\u884c\u524a\u53d1\uff0c\u51fa\u5bb6\u4e3a\u5c3c\u3002\u4f17\u4eba\u89c1\u5979\u5982\u6b64\uff0c\u90fd\u5927\u611f\u610f\u5916\u3002\u9752\u9752\u66f4\u662f\u5fc3\u4e2d\u60ed\u6127\u3002\u8881\u627f\u5fd7\u5fc3\u795e\u5927\u4e71\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u5f85\u8981\u8bf4\u51e0\u53e5\u8bdd\u76f8\u6170\uff0c\u5374\u53c8\u6709\u751a\u4e48\u8bdd\u597d\u8bf4\uff1f \u3000\u3000\u6728\u6851\u5ffd\u9053\uff1a\u201c\u8001\u9053\u4ee5\u5e08\u95e8\u591a\u6545\uff0c\u5fc3\u6709\u987e\u5fcc\uff0c\u56e0\u6b64\u4e00\u751f\u672a\u6536\u95e8\u4eba\u3002\u73b0\u4e0b\u6211\u95e8\u6237\u5df2\u6e05\uff0c\u8fd9\u4f4d\u59d1\u5a18\u9002\u624d\u6551\u6211\u6027\u547d\uff0c\u5982\u4e0d\u5acc\u5f03\uff0c\u6388\u4f60\u51e0\u624b\u529f\u592b\u5982\u4f55\uff1f\u201d\u963f\u4e5d\u8138\u9732\u559c\u8272\uff0c\u8fc7\u53bb\u76c8\u76c8\u62dc\u5012\u3002\u540e\u6765\u5979\u5c3d\u5f97\u6728\u6851\u7edd\u827a\uff0c\u6210\u4e3a\u6e05\u521d\u4e00\u4ee3\u5927\u4fa0\uff0c\u65e5\u540e\u5eb7\u7199\u521d\u5e74\u7684\u5947\u4eba\u97e6\u5c0f\u5b9d\uff08\u89c1\u300a\u9e7f\u9f0e\u8bb0\u300b\uff09\u3001\u96cd\u6b63\u5e74\u95f4\u7684\u8457\u540d\u82f1\u4fa0\u7518\u51e4\u6c60\u3001\u767d\u6cf0\u5b98\u3001\u5415\u56db\u5a18\u7b49\u4eba\u90fd\u51fa\u81ea\u5979\u7684\u95e8\u4e0b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5411\u5e08\u7236\u548c\u638c\u95e8\u5927\u5e08\u5144\u7980\u544a\u8981\u53bb\u76f8\u6551\u674e\u5ca9\u3002\u7a46\u4eba\u6e05\u6c89\u541f\u9053\uff1a\u201c\u674e\u5c06\u519b\u4e3a\u5978\u4eba\u4e2d\u4f24\uff0c\u81f4\u95ef\u738b\u6709\u76f8\u7591\u4e4b\u610f\uff0c\u8fd9\u4e8b\u82e5\u662f\u5904\u7406\u4e0d\u5584\uff0c\u4e0d\u4f46\u5f97\u7f6a\u4e86\u95ef\u738b\uff0c\u4f24\u4e86\u54b1\u4eec\u591a\u5e74\u76f8\u4ea4\u7684\u4e49\u6c14\uff0c\u800c\u4e14\u5f15\u8d77\u95ef\u519b\u5185\u90e8\u4e0d\u548c\uff0c\u6709\u8bef\u5927\u4e1a\u3002\u5434\u4e09\u6842\u5f15\u6ee1\u6e05\u5175\u5165\u5173\uff0c\u95ef\u738b\u6b63\u5904\u9006\u5883\u3002\u4f60\u548c\u674e\u5c06\u519b\u867d\u7136\u4ea4\u60c5\u6781\u597d\uff0c\u8bf8\u4e8b\u987b\u5f53\u4ee5\u5927\u5c40\u4e3a\u91cd\u3002\u201d\u9ec4\u771f\u9053\uff1a\u201c\u5e08\u5f1f\u4e07\u4e8b\u4fdd\u91cd\u3002\u54b1\u4eec\u505a\u751f\u610f\u2026\u2026\u201d\uff0c\u8bf4\u522b\u8fd9\u91cc\uff0c\u7a81\u7136\u4f4f\u53e3\uff0c\u60f3\u8d77\u5df2\u505a\u4e86\u638c\u95e8\u4eba\uff0c\u4e0d\u80fd\u968f\u53e3\u518d\u8bf4\u7b11\u8bdd\uff0c\u4e00\u65f6\u9887\u89c9\u4e0d\u60ef\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8eac\u8eab\u5e94\u547d\uff0c\u4e8e\u662f\u966a\u540c\u7ea2\u5a18\u5b50\uff0c\u7387\u9886\u4f55\u60d5\u5b88\u3001\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u4e09\u4eba\u544a\u8f9e\u3002\u9752\u9752\u575a\u6b32\u540c\u53bb\uff0c\u8bf4\u9053\u5728\u9053\u517b\u4f24\uff0c\u8fc7\u5f97\u51e0\u5929\uff0c\u4e5f\u5c31\u597d\u4e86\u3002\u4f55\u60d5\u5b88\u77e5\u5979\u5140\u81ea\u4e0d\u653e\u5fc3\uff0c\u4e00\u529b\u64ba\u6387\uff0c\u8bf4\u5979\u4f59\u6bd2\u672a\u6e05\uff0c\u53ea\u6709\u81ea\u5df1\u7ee7\u7eed\u6cbb\u7597\uff0c\u65b9\u80fd\u75ca\u6108\u3002\u8881\u627f\u5fd7\u4e5f\u53ea\u5f97\u5141\u4e86\u3002\u5d14\u79cb\u5c71\u3001\u5d14\u5e0c\u654f\u53d4\u4f84\uff0c\u5b89\u5927\u5a18\u3001\u5b89\u5c0f\u6167\u6bcd\u5973\u4e5f\u6c42\u5055\u884c\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8d70\u5230\u963f\u4e5d\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u963f\u4e5d\u59b9\u5b50\uff0c\u4f60\u2026\u2026\u4f60\u4e00\u5207\u4fdd\u91cd\u3002\u201d\u963f\u4e5d\u5782\u4e0b\u4e86\u5934\u4e0d\u8bed\uff0c\u8fc7\u4e86\u826f\u4e45\uff0c\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u6211\u662f\u51fa\u5bb6\u4eba\u4e86\uff0c\u6cd5\u540d\u53eb\u4f5c\u2018\u4e5d\u96be\u2019\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u4f60\u4e5f\u4e00\u5207\u4fdd\u91cd\uff01\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u884c\u5341\u4eba\u79bb\u4e86\u534e\u5c71\uff0c\u75be\u8d8b\u897f\u5b89\u3002\u5404\u4eba\u4e3a\u6551\u674e\u5ca9\uff0c\u65e5\u591c\u4e0d\u505c\uff0c\u52a0\u6025\u8d76\u8def\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u5c06\u5230\u6e2d\u5357\uff0c\u5ffd\u542c\u5f97\u5406\u559d\u55a7\u54d7\uff0c\u5343\u4f59\u540d\u95ef\u519b\u8d76\u4e86\u4e00\u5927\u961f\u6c11\u4f15\uff0c\u6b63\u5411\u897f\u884c\u3002\u6c11\u4f15\u4e2a\u4e2a\u6311\u4e86\u91cd\u62c5\uff0c\u8d70\u5f97\u6c14\u5598\u5401\u5401\u3002\u4f17\u519b\u58eb\u624b\u6301\u76ae\u97ad\uff0c\u4e0d\u4f4f\u559d\u9a82\u50ac\u8d76\uff0c\u4fbf\u5982\u8d76\u7272\u53e3\u76f8\u4f3c\u3002\u4e00\u540d\u5e74\u8001\u6c11\u4f15\u811a\u6b65\u8e52\u8dda\uff0c\u6251\u5730\u5012\u4e86\uff0c\u62c5\u5b50\u6563\u5f00\uff0c\u6eda\u51fa\u8bb8\u591a\u91d1\u94f6\u5668\u76bf\u3001\u5987\u5973\u9970\u7269\u3002\u4e00\u540d\u5c0f\u519b\u5b98\u5927\u6012\uff0c\u72e0\u72e0\u4e00\u811a\uff0c\u8e22\u5f97\u90a3\u6c11\u4f15\u53e3\u55b7\u9c9c\u8840\u3002 \u3000\u3000\u9752\u9752\u770b\u5f97\u6781\u662f\u6c14\u6124\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u6b3a\u4fae\u8001\u767e\u59d3\uff0c\u8fd8\u7b97\u662f\u4e49\u519b\uff1f\u201d \u3000\u3000\u4f55\u60d5\u5b88\u9053\uff1a\u201c\u8fd9\u4e9b\u91d1\u94f6\u8d22\u5b9d\uff0c\u8fd8\u4e0d\u662f\u4ece\u767e\u59d3\u5bb6\u91cc\u62a2\u6765\u7684\u3002\u201d\u5979\u8bf4\u5f97\u58f0\u97f3\u8f83\u54cd\uff0c\u51e0\u540d\u95ef\u519b\u542c\u89c1\u4e86\uff0c\u6076\u72e0\u72e0\u7684\u56de\u5934\u559d\u9a82\u3002\u4e00\u540d\u519b\u58eb\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u662f\u5978\u7ec6\uff0c\u90fd\u62ff\u4e0b\u4e86\u3002\u201d\u5341\u4f59\u540d\u519b\u58eb\u5927\u58f0\u6b22\u547c\uff0c\u4fbf\u6765\u62c9\u626f\u9752\u9752\u3001\u4f55\u60d5\u5b88\u3001\u5b89\u5927\u5a18\u3001\u5b89\u5c0f\u6167\u3001\u7ea2\u5a18\u5b50\u4e94\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u6b63\u6ee1\u8154\u60b2\u6124\uff0c\u62d4\u5200\u4fbf\u780d\u7ffb\u4e86\u4e24\u540d\u519b\u58eb\u3002\u8881\u627f\u5fd7\u53eb\u9053\uff1a\u201c\u5927\u4f19\u513f\u5feb\u8d70\u7f62\uff01\u201d\u5728\u9a6c\u4e0a\u4fef\u8eab\u63d0\u8d77\u4f17\u519b\u58eb\u4e71\u63b7\uff0c\u5e26\u9886\u4f17\u4eba\u8d70\u4e86\u3002\u95ef\u519b\u4e0d\u80af\u820d\u4e86\u91d1\u94f6\u6765\u8ffd\uff0c\u53ea\u662f\u5728\u540e\u9ad8\u58f0\u53eb\u9a82\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u6c14\u5fff\u5fff\u7684\u9053\uff1a\u201c\u54b1\u4eec\u7684\u519b\u961f\u4e00\u8fdb\u4e86\u5317\u4eac\uff0c\u519b\u7eaa\u5927\u574f\uff0c\u53ea\u987e\u5f97\u63b3\u52ab\u8d22\u7269\uff0c\u5f3a\u62a2\u6c11\u5973\u3002\u6bd4\u4e4b\u660e\u671d\uff0c\u53c8\u597d\u5f97\u4e86\u751a\u4e48\uff1f\u201d\u5d14\u79cb\u5c71\u6447\u5934\u9053\uff1a\u201c\u95ef\u738b\u600e\u4e0d\u7ba1\u7ba1\uff0c\u4e5f\u771f\u5947\u602a\u3002\u201d\u7ea2\u5a18\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u81ea\u5df1\u4fbf\u62a2\u4e86\u5434\u4e09\u6842\u7684\u7231\u59be\u9648\u5706\u5706\uff0c\u4e0a\u6881\u4e0d\u6b63\u4e0b\u6881\u6b6a\uff0c\u53c8\u600e\u7ba1\u5f97\u4e86\u90e8\u4e0b\uff1f\u5434\u4e09\u6842\u672c\u6765\u5df2\u7ecf\u6295\u964d\uff0c\u5927\u4e8b\u5df2\u5b9a\uff0c\u542c\u5f97\u7231\u59be\u7ed9\u95ef\u738b\u62a2\u4e86\u53bb\uff0c\u8fd9\u624d\u4e00\u6012\u800c\u52fe\u5f15\u9791\u5b50\u5175\u5165\u5173\u3002\u9791\u5b50\u5175\u548c\u5434\u4e09\u6842\u8054\u519b\u6253\u8fdb\u6765\u3002\u95ef\u738b\u5e26\u5175\u51fa\u53bb\u4ea4\u950b\uff0c\u4e24\u519b\u5728\u4e00\u7247\u77f3\u5927\u6218\u3002\u6211\u519b\u6bd4\u654c\u5175\u591a\u4e86\u597d\u51e0\u500d\uff0c\u53ef\u662f\u5927\u5bb6\u8bb0\u6302\u7740\u62a2\u6765\u7684\u8d22\u5b9d\u5987\u5973\uff0c\u4e0d\u80af\u62da\u547d\uff0c\u8fd9\u4e00\u4ed7\u82e5\u662f\u4e0d\u8f93\uff0c\u90a3\u771f\u662f\u6ca1\u5929\u7406\u4e86\u3002\u201d \u3000\u3000\u884c\u4e0d\u591a\u65f6\uff0c\u53ea\u89c1\u8def\u65c1\u6709\u4e2a\u8001\u5987\u4eba\u5728\u653e\u58f0\u75db\u54ed\uff0c\u8eab\u65c1\u6709\u56db\u5177\u5c38\u9996\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u8fd8\u6709\u4e24\u4e2a\u5c0f\u5b69\uff0c\u8eab\u4e0a\u4f24\u53e3\u4e2d\u5140\u81ea\u6d41\u8840\u4e0d\u6b62\uff0c\u663e\u662f\u88ab\u6740\u4e0d\u4e45\u3002\u53ea\u542c\u90a3\u8001\u5987\u54ed\u53eb\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60\u8fd9\u5927\u9a97\u5b50\uff0c\u4f60\u8bf4\u751a\u4e48\u2018\u65e9\u65e9\u5f00\u95e8\u62dc\u95ef\u738b\uff0c\u7ba1\u6559\u5927\u5c0f\u90fd\u6b22\u60a6\u2019\uff0c\u6211\u4eec\u4e00\u5bb6\u5f00\u95e8\u62dc\u95ef\u738b\uff0c\u95ef\u738b\u624b\u4e0b\u7684\u571f\u532a\u8d3c\u5f3a\u76d7\uff0c\u5374\u6765\u5f3a\u5978\u6211\u5ab3\u5987\uff0c\u6740\u4e86\u6211\u513f\u5b50\u5b59\u513f\uff01\u6211\u4e00\u5bb6\u5927\u5c0f\u90fd\u5728\u8fd9\u91cc\uff0c\u674e\u516c\u5b50\uff0c\u4f60\u6765\u77a7\u77a7\uff0c\u662f\u4e0d\u662f\u5927\u5c0f\u90fd\u6b22\u60a6\u554a\uff01\u6211\u62dc\u4e86\u516d\u5341\u5e74\u83e9\u8428\u3002\u89c2\u97f3\u83e9\u8428\uff0c\u4f60\u4fdd\u4f51\u6211\u8001\u592a\u5a46\u597d\u5f97\u5f88\u554a\uff01\u89c2\u97f3\u83e9\u8428\uff0c\u4f60\u4e0d\u80af\u4fdd\u4f51\u4eba\uff0c\u4f60\u8ddf\u95ef\u738b\u7684\u571f\u532a\u8d3c\u5f3a\u76d7\u662f\u4e00\u4f19\uff01\u201d\u8881\u627f\u5fd7\u7b49\u4e0d\u5fcd\u591a\u542c\uff0c\u6599\u60f3\u524d\u9762\u5927\u8def\u4e0a\u60e8\u4e8b\u5c1a\u591a\uff0c\u5f53\u4e0b\u7ed5\u5c0f\u9053\u800c\u884c\u3002 \u3000\u3000\u8d76\u4e86\u4e00\u4f1a\u8def\uff0c\u773c\u89c1\u79bb\u6e2d\u5357\u5df2\u7ecf\u4e0d\u8fdc\uff0c\u5ffd\u542c\u5f97\u5175\u5203\u649e\u51fb\uff0c\u6709\u4eba\u4ea4\u950b\u3002\u4f17\u4eba\u62cd\u9a6c\u4e0a\u524d\uff0c\u53ea\u89c1\u4e8c\u5341\u4f59\u540d\u95ef\u519b\u56f4\u4f4f\u4e86\u4e09\u4eba\u780d\u6740\u3002 \u3000\u3000\u4e09\u4eba\u4e2d\u53ea\u6709\u4e00\u4eba\u4f1a\u6b66\uff0c\u5de6\u652f\u53f3\u7ecc\uff0c\u751a\u662f\u72fc\u72c8\u3002 \u3000\u3000\u4f17\u95ef\u519b\u5927\u53eb\uff1a\u201c\u6740\u5978\u7ec6\u554a\uff0c\u5978\u7ec6\u8eab\u4e0a\u91d1\u94f6\u751a\u591a\uff0c\u54ea\u4e00\u4e2a\u5148\u7acb\u529f\u7684\uff0c\u591a\u5206\u4e00\u4efd\u3002\u201d\u5d14\u5e0c\u654f\u6012\u9053\uff1a\u201c\u751a\u4e48\u591a\u5206\u4e00\u4efd\uff1f\u8fd9\u4e0d\u662f\u5f3a\u76d7\u6076\u8d3c\u4e48\uff1f\u201d\u75be\u51b2\u800c\u524d\uff0c\u62d4\u5200\u5411\u95ef\u519b\u780d\u53bb\u3002\u54d1\u5df4\u3001\u6d2a\u80dc\u6d77\u3001\u5d14\u79cb\u5c71\u4e09\u4eba\u8ddf\u7740\u4e0a\u524d\uff0c\u5c06\u4e8c\u5341\u4f59\u540d\u95ef\u519b\u90fd\u8d76\u5f00\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u4e09\u4eba\u90fd\u5df2\u5e26\u4f24\uff0c\u90a3\u4f1a\u6b66\u7684\u6295\u5200\u4e8e\u5730\uff0c\u8eac\u8eab\u62dc\u8c22\uff0c\u7a81\u7136\u5411\u5d14\u79cb\u5c71\u51dd\u89c6\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u5c0a\u9a7e\u53ef\u662f\u59d3\u5d14\u4e48\uff1f\u201d\u5d14\u79cb\u5c71\u9053\uff1a\u201c\u6b63\u662f\u3002\u5c0a\u5144\u9ad8\u59d3\uff0c\u4e0d\u77e5\u5982\u4f55\u8bc6\u5f97\u5728\u4e0b\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u5c0f\u4eba\u6768\u9e4f\u4e3e\uff0c\u8fd9\u4f4d\u662f\u5f20\u671d\u5510\u5f20\u516c\u5b50\u3002\u5341\u591a\u5e74\u524d\uff0c\u6211\u4eec\u4e09\u4eba\u66fe\u5728\u5e7f\u4e1c\u5723\u5cf0\u5d82\u796d\u5960\u8881\u7763\u5e08\uff0c\u66fe\u89c1\u5d14\u5927\u4fa0\u5927\u732e\u8eab\u624b\uff0c\u64d2\u83b7\u5978\u7ec6\u3002\u867d\u7136\u4e8b\u9694\u591a\u5e74\uff0c\u4f46\u5d14\u5927\u4fa0\u7684\u62f3\u6cd5\u638c\u6cd5\uff0c\u5c0f\u4eba\u770b\u4e86\u4e4b\u540e\uff0c\u7262\u7262\u4e0d\u5fd8\u3002\u201d \u3000\u3000\u5d14\u79cb\u5c71\u559c\u9053\uff1a\u201c\u539f\u6765\u662f\u2018\u5c71\u5b97\u2019\u7684\u670b\u53cb\uff0c\u4f60\u4eec\u5feb\u6765\u89c1\u8fc7\u8881\u516c\u5b50\u5427\u3002\u201d \u3000\u3000\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u4e0a\u524d\u62dc\u89c1\u8881\u627f\u5fd7\uff0c\u8bf4\u8d77\u81ea\u5df1\u5e76\u975e\u8881\u7763\u5e08\u7684\u65e7\u90e8\uff0c\u53ea\u662f\u66fe\u968f\u5b59\u4ef2\u5bff\u3001\u5e94\u677e\u7b49\u4eba\u4e0a\u8fc7\u5723\u5cf0\u5d82\u3002\u8881\u627f\u5fd7\u9053\uff1a\u201c\u554a\uff0c\u662f\u4e86\u3002\u90a3\u65e5\u5f20\u516c\u5b50\u4e3a\u5148\u7236\u5199\u8fc7\u4e00\u7bc7\u796d\u6587\u3002\u2018\u9ec4\u9f99\u672a\u6363\uff0c\u6b66\u7a46\u8499\u51a4\uff1b\u6c49\u795a\u5f85\u590d\uff0c\u8bf8\u845b\u661f\u6b92\u2019\uff0c\u8fd9\u5341\u516d\u5b57\u8d5e\u8bed\uff0c\u5148\u7236\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u4e5f\u611f\u5149\u5ba0\u3002\u201d\u5f20\u671d\u5510\u60f3\u4e0d\u5230\u81ea\u5df1\u5f53\u65e5\u60c5\u6025\u4e4b\u4e0b\u6240\u5199\u7684\u8fd9\u5341\u516d\u4e2a\u5b57\uff0c\u8881\u627f\u5fd7\u5c45\u7136\u8fd8\u8bb0\u5728\u5fc3\u4e2d\uff0c\u4e5f\u81ea\u559c\u6b22\u3002 \u3000\u3000\u8881\u627f\u5fd7\u95ee\u8d77\u4e3a\u95ef\u519b\u56f4\u653b\u7684\u60c5\u7531\u3002\u5f20\u671d\u5510\u9053\uff1a\u201c\u5c0f\u4eba\u8fdc\u5728\u6d77\u5916\u6d61\u6ce5\u56fd\uff0c\u4e00\u4e2a\u591a\u6708\u524d\uff0c\u542c\u5f97\u6d77\u5ba2\u8bf4\u8d77\uff0c\u95ef\u738b\u674e\u81ea\u6210\u4e49\u519b\u58f0\u52bf\u5927\u632f\uff0c\u6240\u5230\u4e4b\u5904\uff0c\u52bf\u5982\u7834\u7af9\uff0c\u6307\u65e5\u653b\u514b\u5317\u4eac\uff0c\u4e2d\u534e\u4ece\u6b64\u592a\u5e73\u3002\u5c0f\u4eba\u4e0d\u80dc\u96c0\u8dc3\uff0c\u7980\u660e\u5bb6\u7236\uff0c\u968f\u540c\u8fd9\u4f4d\u6768\u5144\uff0c\u643a\u4e86\u4e00\u540d\u4ece\u4ec6\uff0c\u542f\u7a0b\u91cd\u6765\u6545\u56fd\uff0c\u8981\u89c1\u89c1\u592a\u5e73\u76db\u4e16\u7684\u98ce\u5149\u3002\u5509\uff0c\u54ea\u77e5\u6765\u5230\u5317\u76f4\u96b6\u5883\u5185\uff0c\u5374\u542c\u8bf4\u95ef\u738b\u5f97\u4e86\u5317\u4eac\u4e4b\u540e\uff0c\u767b\u4f4d\u79f0\u5e1d\uff0c\u53c8\u7ed9\u6ee1\u6e05\u5175\u6253\u4e86\u51fa\u6765\uff0c\u9003\u5230\u4e86\u897f\u5b89\uff0c\u6ee1\u6e05\u5175\u4e00\u8def\u8ffd\u6765\u3002\u6211\u4eec\u4e09\u4eba\u4e5f\u53ea\u5f97\u897f\u4e0a\u907f\u96be\u3002\u54ea\u60f3\u5230\u4eca\u65e5\u5728\u8fd9\u91cc\u9047\u89c1\u95ef\u519b\uff0c\u7adf\u8bf4\u6211\u4eec\u662f\u5978\u7ec6\uff0c\u8981\u641c\u67e5\u5168\u8eab\u3002\u6211\u4eec\u4e5f\u4efb\u7531\u641c\u67e5\uff0c\u8fd9\u4e9b\u519b\u58eb\u89c1\u5230\u6211\u4eec\u643a\u5e26\u7684\u8def\u8d39\uff0c\u4fbf\u5373\u773c\u7ea2\uff0c\u4e0d\u7531\u5206\u8bf4\uff0c\u4e3e\u5200\u4fbf\u780d\u3002\u82e5\u4e0d\u662f\u4f17\u4f4d\u76f8\u6551\uff0c\u6211\u4eec\u4e09\u4eba\u65e9\u5df2\u6210\u4e3a\u5200\u4e0b\u4e4b\u9b3c\u4e86\u3002\u5509\uff0c\u592a\u5e73\u76db\u4e16\uff0c\u592a\u5e73\u76db\u4e16\uff01\u201d \u3000\u3000\u8bf4\u7740\u82e6\u7b11\u6447\u5934\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e0b\u4e0d\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u53bb\u4e00\u8def\u4e4b\u4e0a\uff0c\u53ea\u6015\u4ecd\u7136\u4e0d\u5927\u592a\u5e73\u3002\u4e09\u4f4d\u4e14\u968f\u6211\u4eec\u540c\u5f80\u897f\u5b89\uff0c\u518d\u5b9a\u884c\u6b62\u5982\u4f55\uff1f\u201d\u5f20\u671d\u5510\u548c\u6768\u9e4f\u4e3e\u9f50\u58f0\u79f0\u8c22\u3002\u90a3\u7ae5\u513f\u5f20\u5eb7\u6b64\u523b\u5df2\u7136\u6210\u4eba\uff0c\u8d1f\u8d77\u4e86\u5305\u88f9\uff0c\u8bf4\u9053\uff1a\u201c\u5341\u591a\u5e74\u524d\uff0c\u6211\u4eec\u7b2c\u4e00\u6b21\u56de\u5230\u4e2d\u56fd\uff0c\u5b98\u5175\u8bf4\u6211\u4eec\u662f\u5f3a\u76d7\uff0c\u8981\u8c0b\u8d22\u5bb3\u547d\u3002\u8fd9\u4e00\u6b21\u518d\u6765\u4e2d\u56fd\uff0c\u4e49\u519b\u8bf4\u6211\u4eec\u662f\u5978\u7ec6\uff0c\u4ecd\u662f\u8981\u8c0b\u8d22\u5bb3\u547d\u3002\u6211\u8bf4\u516c\u5b50\u7237\uff0c\u4e0b\u4e00\u6b21\u6211\u4eec\u53ef\u522b\u518d\u6765\u4e86\u7f62\u3002\u201d\u5f20\u671d\u5510\u9053\uff1a\u201c\u4e2d\u56fd\u8fd8\u662f\u597d\u4eba\u591a\uff0c\u54b1\u4eec\u53ef\u53c8\u4e0d\u662f\u9022\u51f6\u5316\u5409\u4e86\u5417\uff1f\u201d \u3000\u3000\u6b21\u65e5\u4f17\u4eba\u7eb5\u9a6c\u75be\u9a70\uff0c\u8d76\u5230\u897f\u5b89\u57ce\u4e1c\u7684\u575d\u6865\u3002\u53ea\u89c1\u4e00\u961f\u961f\u95ef\u519b\u6392\u597d\u4e86\u9635\u52bf\uff0c\u4e0e\u5bf9\u9762\u5927\u961f\u95ef\u519b\u5bf9\u5cd9\uff0c\u53cc\u65b9\u5f2f\u5f13\u642d\u7bad\uff0c\u6218\u4e8b\u4e00\u89e6\u5373\u53d1\u3002\u8881\u627f\u5fd7\u5927\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u600e\u4e48\u81ea\u5df1\u4eba\u6253\u4e86\u8d77\u6765\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e00\u540d\u519b\u5b98\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e07\u5c81\u7237\u6709\u65e8\uff0c\u53ea\u62ff\u53db\u9006\u674e\u5ca9\u4e00\u4eba\uff0c\u4f59\u4eba\u65e0\u5e72\uff0c\u5feb\u5feb\u6563\u53bb\uff0c\u82e5\u662f\u8fdd\u6297\u65e8\u610f\uff0c\u4e00\u6982\u683c\u6740\u4e0d\u8bba\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u4e00\u559c\uff1a\u201c\u5927\u54e5\u672a\u906d\u6bd2\u624b\u3002\u54b1\u4eec\u53ef\u6ca1\u6765\u8fdf\u4e86\u3002\u201d\u5fd9\u6325\u624b\u547d\u4f17\u4eba\u8f6c\u8eab\uff0c\u7ed5\u8fc7\u4e24\u519b\uff0c\u4ece\u4fa7\u7ffc\u8fdc\u8fdc\u515c\u4e86\u4e24\u4e2a\u5708\u5b50\uff0c\u8d70\u5411\u674e\u5ca9\u6240\u5c5e\u7684\u90e8\u961f\u3002\u7edf\u5e26\u524d\u54e8\u7684\u519b\u5b98\u89c1\u5230\u674e\u592b\u4eba\u5230\u6765\uff0c\u5fd9\u5f15\u5bfc\u4f17\u4eba\u53bb\u4e2d\u519b\u5927\u5e10\u3002 \u3000\u3000\u6765\u5230\u5e10\u5916\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u9635\u4e1d\u7af9\u58f0\u4f20\u4e86\u51fa\u6765\uff0c\u4f17\u4eba\u90fd\u611f\u5947\u602a\u3002\u7ea2\u5a18\u5b50\u4e0e\u8881\u627f\u5fd7\u5e76\u80a9\u8fdb\u5e10\uff0c\u5374\u89c1\u5e10\u4e2d\u5927\u5f20\u7b75\u5e2d\uff0c\u6570\u767e\u540d\u519b\u5b98\u5e2d\u5730\u800c\u5750\uff0c\u674e\u5ca9\u72ec\u81ea\u5750\u5728\u5c45\u4e2d\u4e00\u5e2d\uff0c\u6b63\u81ea\u4e3e\u676f\u996e\u9152\u3002 \u3000\u3000\u4ed6\u5ffd\u89c1\u59bb\u5b50\u548c\u8881\u627f\u5fd7\u5230\u6765\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u62a2\u6b65\u4e0a\u524d\uff0c\u5de6\u624b\u62c9\u4f4f\u59bb\u5b50\uff0c\u53f3\u624b\u643a\u4e86\u8881\u627f\u5fd7\u7684\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6765\u5f97\u6b63\u597d\uff0c\u8001\u5929\u6bd5\u7adf\u5f85\u6211\u4e0d\u8584\u3002\u201d\u8ba9\u4e8c\u4eba\u5206\u5750\u5de6\u53f3\uff0c\u53c8\u547d\u90e8\u5c5e\u53e6\u5f00\u4e00\u5e2d\uff0c\u63a5\u5f85\u5d14\u79cb\u5c71\u3001\u5b89\u5927\u5a18\u3001\u9752\u9752\u3001\u4f55\u60d5\u5b88\u7b49\u4eba\u5c31\u5750\u3002 \u3000\u3000\u8881\u627f\u5fd7\u89c1\u674e\u5ca9\u597d\u6574\u4ee5\u6687\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u653e\u5fc3\uff0c\u6570\u65e5\u6765\u7684\u62c5\u5fe7\uff0c\u767b\u65f6\u4e00\u626b\u800c\u7a7a\uff0c\u5411\u7ea2\u5a18\u5b50\u671b\u4e86\u4e00\u773c\uff0c\u5fae\u5fae\u800c\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u53ef\u5413\u5f97\u6211\u597d\u5389\u5bb3\uff01\u201d \u3000\u3000\u674e\u5ca9\u7ad9\u8d77\u8eab\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u90fd\u662f\u6211\u7684\u597d\u5144\u5f1f\uff0c\u597d\u670b\u53cb\u3002\u8fd9\u4e9b\u5e74\u6765\u54b1\u4eec\u51fa\u6b7b\u5165\u751f\uff0c\u7518\u82e6\u4e0e\u5171\uff0c\u53ea\u76fc\u4ece\u4eca\u800c\u540e\uff0c\u5927\u4e1a\u544a\u6210\uff0c\u5929\u4e0b\u592a\u5e73\u3002\u54ea\u77e5\u9053\u4e07\u5c81\u7237\u542c\u4fe1\u4e86\u5978\u4eba\u7684\u8c17\u8a00\u3002\u8bf4\u751a\u4e48\u2018\u5341\u516b\u5b69\u513f\u4e3b\u795e\u5668\u2019\u90a3\u53e5\u8bdd\uff0c\u662f\u6211\u674e\u67d0\u4eba\u8981\u505a\u7687\u5e1d\u3002\u521a\u624d\u4e07\u5c81\u7237\u4e0b\u4e86\u65e8\u610f\uff0c\u8d50\u674e\u67d0\u4eba\u7684\u6b7b\uff0c\u54c8\u54c8\uff0c\u8fd9\u4ef6\u4e8b\u771f\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff1f\u201d \u3000\u3000\u4f17\u5c06\u7ad9\u8d77\u8eab\u6765\uff0c\u7eb7\u7eb7\u9053\uff1a\u201c\u8fd9\u662f\u5978\u4eba\u5047\u4f20\u5723\u65e8\u3002\u4e07\u5c81\u7237\u7d20\u6765\u4fe1\u4efb\u5c06\u519b\u3002\u5c06\u519b\u4e0d\u5fc5\u7406\u4f1a\u3002\u54b1\u4eec\u9f50\u53bb\u897f\u5b89\u57ce\u91cc\uff0c\u9762\u89c1\u4e07\u5c81\u7237\u5206\u8fa9\u662f\u975e\u4fbf\u4e86\u3002\u201d\u5404\u4eba\u795e\u8272\u6124\u6168\uff0c\u6709\u7684\u8bf4\u674e\u5c06\u519b\u7acb\u4e0b\u5927\u529f\uff0c\u5bf9\u7687\u4e0a\u5fe0\u5fc3\u803f\u803f\uff0c\u54ea\u6709\u9020\u53cd\u4e4b\u7406\uff1b\u6709\u7684\u8bf4\u672c\u519b\u7eaa\u5f8b\u4e25\u660e\uff0c\u7231\u6c11\u5982\u5b50\uff0c\u5f15\u8d77\u4e86\u53cb\u519b\u7684\u5ac9\u5fcc\uff1b\u66f4\u6709\u7684\u8bf4\u4e07\u5c81\u7237\u82e5\u662f\u4e0d\u542c\u5206\u8fa9\uff0c\u5927\u4f19\u513f\u5e26\u961f\u53bb\u81ea\u5df1\u5e72\u81ea\u5df1\u7684\uff0c\u53cd\u6b63\u73b0\u4e0b\u95ef\u519b\u80e1\u4f5c\u975e\u4e3a\uff0c\u5927\u5931\u6c11\u5fc3\uff0c\u8ddf\u7740\u4e07\u5c81\u7237\u4e5f\u6ca1\u751a\u4e48\u597d\u7ed3\u679c\u4e86\u3002 \u3000\u3000\u674e\u5ca9\u53d6\u51fa\u4e00\u5f20\u9ec4\u7eb8\u6765\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4e07\u5c81\u7237\u7684\u4eb2\u7b14\uff0c\u5199\u7740\uff1a\u2018\u5236\u5c06\u519b\u674e\u5ca9\u9020\u53cd\uff0c\u8981\u81ea\u7acb\u4e3a\u5e1d\uff0c\u5927\u9006\u4e0d\u9053\u3002\u7740\u5373\u6b63\u6cd5\uff0c\u901f\u901f\u4e0d\u8bef\u3002\u2019\u8fd9\u4e0d\u662f\u65c1\u4eba\u5047\u4f20\u5723\u65e8\uff0c\u5c31\u7b97\u89c1\u4e86\u4e07\u5c81\u7237\uff0c\u4e5f\u5206\u8fa9\u4e0d\u51fa\u7684\u3002\u201d\u4f17\u5c06\u594b\u81c2\u5927\u547c\uff1a\u201c\u613f\u968f\u5c06\u519b\uff0c\u51b3\u4e00\u6b7b\u6218\uff01\u201d\u4e00\u540d\u5c06\u5b98\u8bf4\u9053\uff1a\u201c\u4e07\u5c81\u7237\u5df2\u6d3e\u4e86\u5de6\u8425\u3001\u524d\u8425\u3001\u540e\u8425\uff0c\u628a\u54b1\u4eec\u4e09\u9762\u56f4\u4f4f\u4e86\uff0c\u90a3\u4e0d\u662f\u8981\u6740\u674e\u5c06\u519b\u4e00\u4eba\uff0c\u662f\u8981\u6740\u54b1\u4eec\u5168\u519b\u3002\u201d\u4f17\u5c06\u53eb\u9053\uff1a\u201c\u4e07\u5c81\u903c\u54b1\u4eec\u9020\u53cd\uff0c\u90a3\u5c31\u771f\u7684\u53cd\u4e86\u7f62\uff01\u201d \u3000\u3000\u674e\u5ca9\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u5750\u4e0b\uff0c\u6211\u81ea\u6709\u4e3b\u5f20\uff0c\u4e07\u5c81\u7237\u5f85\u6211\u4e0d\u8584\uff0c\u2018\u9020\u53cd\u2019\u4e8c\u5b57\uff0c\u4e07\u4e07\u4e0d\u53ef\u63d0\u8d77\u3002\u6765\uff0c\u559d\u9152\uff01\u201d\u4f17\u5c06\u7d20\u77e5\u4ed6\u8db3\u667a\u591a\u8c0b\uff0c\u89c1\u4ed6\u5982\u6b64\u9547\u5b9a\uff0c\u6599\u60f3\u5fc5\u6709\u5947\u7b56\u5e94\u53d8\uff0c\u4e8e\u662f\u9010\u4e00\u5750\u4e0b\uff0c\u4ea4\u5934\u63a5\u8033\uff0c\u4f4e\u58f0\u8bae\u8bba\u3002 \u3000\u3000\u674e\u5ca9\u659f\u4e86\u4e00\u676f\u9152\uff0c\u7b11\u9053\uff1a\u201c\u4eba\u751f\u6570\u5341\u5e74\uff0c\u5b9b\u5982\u6625\u68a6\u4e00\u573a\u3002\u201d \u3000\u3000\u5c06\u9152\u4e00\u5e72\u800c\u5c3d\uff0c\u5de6\u624b\u62cd\u684c\uff0c\u5ffd\u7136\u5927\u58f0\u5531\u8d77\u6b4c\u6765\uff1a\u201c\u65e9\u65e9\u5f00\u95e8\u62dc\u95ef\u738b\uff0c\u7ba1\u6559\u5927\u5c0f\u90fd\u6b22\u60a6\uff0c\u7ba1\u6559\u5927\u5c0f\u90fd\u2026\u2026\u201d\u90a3\u6b63\u662f\u4ed6\u5f53\u5e74\u6240\u4f5c\u7684\u6b4c\u8c23\uff0c\u6d41\u4f20\u5929\u4e0b\uff0c\u5927\u52a9\u674e\u81ea\u6210\u53d6\u5f97\u6c11\u5fc3\u5f52\u987a\u3002\u53ea\u542c\u4ed6\u5531\u5230\u90a3\u201c\u90fd\u201d\u5b57\u65f6\uff0c\u7a81\u7136\u65e0\u58f0\uff0c\u8eab\u5b50\u7f13\u7f13\u4fef\u5728\u684c\u4e0a\uff0c\u518d\u4e5f\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u548c\u8881\u627f\u5fd7\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u53bb\u76f8\u6276\uff0c\u5374\u89c1\u674e\u5ca9\u5df2\u7136\u6c14\u7edd\u3002\u539f\u6765\u4ed6\u5de6\u624b\u6697\u85cf\u5315\u9996\uff0c\u5df2\u4e00\u5200\u523a\u5728\u81ea\u5df1\u5fc3\u7a9d\u4e4b\u4e2d\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d\u62d4\u51fa\u8170\u5200\uff0c\u81ea\u520e\u800c\u6b7b\u3002 \u3000\u3000\u8881\u627f\u5fd7\u8fd1\u5728\u8eab\u65c1\uff0c\u82e5\u8981\u963b\u62e6\uff0c\u539f\u53ef\u6551\u5f97\uff0c\u53ea\u662f\u4ed6\u60b2\u75db\u4ea4\u96c6\uff0c\u4e00\u65f6\u81ea\u5df1\u4e5f\u60f3\u4e00\u6b7b\u4e86\u4e4b\uff0c\u7adf\u65e0\u76f8\u6551\u4e4b\u610f\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u8033\u8fb9\u4f3c\u4e4e\u54cd\u8d77\u4e86\u5f53\u65e5\u5728\u5317\u4eac\u57ce\u4e2d\u4e0e\u674e\u5ca9\u4e00\u540c\u542c\u5230\u7684\u90a3\u8001\u76f2\u4eba\u7684\u6b4c\u58f0\uff1a\u201c\u4eca\u65e5\u7684\u4e00\u7f15\u82f1\u9b42\uff0c\u6628\u65e5\u7684\u4e07\u91cc\u957f\u57ce\u2026\u2026\u201d \u3000\u3000\u4f17\u5c06\u89c1\u4e3b\u5e05\u592b\u5987\u9f50\u6b7b\uff0c\u8425\u4e2d\u767b\u65f6\u5927\u4e71\uff0c\u987b\u81fe\u4e4b\u95f4\uff0c\u6570\u4e07\u5b98\u5175\u6563\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u8881\u627f\u5fd7\u5fc3\u4e2d\u60b2\u75db\uff0c\u610f\u5174\u8427\u7d22\u3002\u8fd9\u65e5\u5f20\u671d\u5510\u548c\u4ed6\u8c08\u8d77\u6d61\u6ce5\u56fd\u6c11\u98ce\u6df3\u6734\uff0c\u5b89\u9759\u592a\u5e73\uff0c\u8bf4\u9053\uff1a\u201c\u4e2d\u539f\u5927\u4e71\uff0c\u516c\u5b50\u5fc3\u7eea\u4e0d\u4f73\uff0c\u4f55\u4e0d\u5230\u6d61\u6ce5\u56fd\u53bb\u6563\u6563\u5fc3\uff1f\u201d\u8881\u627f\u5fd7\u5fc3\u60f3\u5bc4\u4eba\u7bf1\u4e0b\uff0c\u4e5f\u65e0\u610f\u8da3\uff0c\u5ffd\u7136\u60f3\u8d77\u90a3\u897f\u6d0b\u519b\u5b98\u6240\u8d60\u7684\u4e00\u5f20\u6d77\u5c9b\u56fe\uff0c\u4e8e\u662f\u53d6\u4e86\u51fa\u6765\uff0c\u8be2\u95ee\u6b64\u662f\u4f55\u5730\u3002\u5f20\u671d\u5510\u9053\uff1a\u201c\u90a3\u662f\u5728\u6d61\u6ce5\u56fd\u5de6\u8fd1\u7684\u4e00\u5ea7\u5927\u5c9b\u5c7f\uff0c\u773c\u4e0b\u4e3a\u7ea2\u6bdb\u56fd\u6d77\u76d7\u76d8\u8e1e\uff0c\u9a9a\u6270\u6d77\u5ba2\u3002\u201d \u3000\u3000\u8881\u627f\u5fd7\u4e00\u542c\u4e4b\u4e0b\uff0c\u795e\u6e38\u6d77\u5916\uff0c\u58ee\u5fd7\u987f\u5174\uff0c\u4e0d\u7981\u62cd\u6848\u957f\u5578\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u53bb\u5c06\u7ea2\u6bdb\u6d77\u76d7\u9a71\u8d70\uff0c\u5230\u8fd9\u6d77\u5c9b\u4e0a\u53bb\u505a\u5316\u5916\u4e4b\u6c11\u7f62\u3002\u201d\u5f53\u4e0b\u7387\u9886\u9752\u9752\u3001\u4f55\u60d5\u5b88\u3001\u54d1\u5df4\u3001\u5d14\u5e0c\u654f\u7b49\u4eba\uff0c\u518d\u53ec\u96c6\u5b59\u4ef2\u5bff\u7b49\u201c\u5c71\u5b97\u201d\u65e7\u4eba\u3001\u5b5f\u4f2f\u98de\u7236\u5b50\u3001\u7f57\u7acb\u5982\u3001\u7126\u5b9b\u513f\u3001\u7a0b\u9752\u7af9\u3001\u6c99\u5929\u5e7f\u3001\u80e1\u6842\u5357\u3001\u94c1\u7f57\u6c49\u7b49\u8c6a\u6770\uff0c\u5f97\u4e86\u5f20\u671d\u5510\u3001\u6768\u9e4f\u4e3e\u7b49\u4eba\u4e4b\u52a9\uff0c\u8fdc\u5f81\u5f02\u57df\uff0c\u7ec8\u4e8e\u5728\u6d77\u5916\u5f00\u8f9f\u4e86\u4e00\u4e2a\u65b0\u5929\u5730\u3002\u6b63\u662f\uff1a\u4e07\u91cc\u971c\u70df\u56de\u7eff\u9b13\u5341\u5e74\u5175\u7532\u8bef\u82cd\u751f \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0},{name:"\u8881\u5d07\u7115\u8bc4\u4f20",content:(0,t.jsx)(t.Fragment,{children:"\u6bcf\u4e00\u8282\u6587\u672b\u7684\u6ce8\u91ca\u53ea\u662f\u8868\u793a\uff1a\u6587\u4e2d\u7684\u4e8b\u5b9e\u5168\u90e8\u90fd\u6709\u6839\u636e\uff0c\u5e76\u4e0d\u662f\u5c0f\u8bf4\u3002\u5bf9\u5386\u53f2\u7814\u7a76\u6ca1\u6709\u5174\u8da3\u7684\u8bfb\u8005\u4eec\u5927\u53ef\u7565\u8fc7\u6ce8\u91ca\u4e0d\u8bfb\u3002 \u3000\u3000\u5728\u8ddd\u79bb\u9999\u6e2f\u4e0d\u5230\u4e00\u767e\u4e94\u5341\u516c\u91cc\u7684\u5730\u533a\u4e4b\u4e2d\uff0c\u8fc7\u53bb\u4e09\u767e\u591a\u5e74\u5185\u51fa\u4e86\u4e24\u4f4d\u4e0e\u4e2d\u56fd\u5386\u53f2\u6709\u91cd\u5927\u5173\u7cfb\u7684\u4eba\u7269\u3002\u6700\u91cd\u8981\u7684\u5f53\u7136\u662f\u5b59\u4e2d\u5c71\u5148\u751f\u3002\u53e6\u4e00\u4f4d\u662f\u51fa\u751f\u5728\u5e7f\u4e1c\u4e1c\u839e\u53bf\u7684\u8881\u5d07\u7115\u3002 \u3000\u3000\u6211\u5728\u9605\u8bfb\u8881\u5d07\u7115\u6240\u5199\u7684\u594f\u7ae0\u3001\u6240\u4f5c\u7684\u8bd7\u53e5\u3001\u4ee5\u53ca\u4e0e\u4ed6\u6709\u5173\u7684\u53f2\u6599\u4e4b\u65f6\uff0c\u65f6\u65f6\u89c9\u5f97\u4f3c\u4e4e\u662f\u5728\u8bfb\u53e4\u5e0c\u814a\u5267\u4f5c\u5bb6\u6538\u91cc\u6bd4\u7b2c\u65af\u3001\u6c99\u798f\u514b\u91cc\u65af\u7b49\u4eba\u7684\u60b2\u5267\u3002\u8881\u5d07\u7115\u771f\u50cf\u662f\u4e00\u4e2a\u53e4\u5e0c\u814a\u7684\u60b2\u5267\u82f1\u96c4\uff0c\u4ed6\u6709\u5de8\u5927\u7684\u52c7\u6c14\uff0c\u548c\u654c\u4eba\u4f5c\u6218\u7684\u52c7\u6c14\uff0c\u9053\u5fb7\u4e0a\u7684\u52c7\u6c14\u3002\u4ed6\u51b2\u5929\u7684\u5e72\u52b2\uff0c\u6267\u62d7\u7684\u86ee\u52b2\uff0c\u521a\u70c8\u7684\u72e0\u52b2\uff0c\u5728\u5f53\u65f6\u7325\u7410\u840e\u9761\u7684\u660e\u672b\u671d\u5ef7\u4e2d\uff0c\u52a0\u500d\u7684\u663e\u5f97\u7a81\u51fa\u3002 \u3000\u3000\u8881\u5d07\u7115\uff0c\u5b57\u5143\u7d20\uff0c\u53f7\u81ea\u5982\u3002\u201c\u7115\u201d\uff0c\u662f\u706b\u5149\uff0c\u662f\u660e\u4eae\u663e\u8d6b\u3001\u5149\u5f69\u8f89\u714c\uff1b\u201c\u7d20\u201d\u662f\u76f4\u7387\u7684\u8d28\u6734\uff0c\u662f\u81ea\u7136\u7684\u672c\u6027\u3002\u4ed6\u5927\u706b\u718a\u718a\u822c\u7684\u4e00\u751f\uff0c\u6211\u884c\u6211\u7d20\u7684\u6027\u683c\uff0c\u6325\u6d12\u81ea\u5982\u7684\u4f5c\u98ce\uff0c\u7684\u786e\u662f\u4eba\u5982\u5176\u540d\u3002\u8fd9\u6837\u7684\u6027\u683c\uff0c\u548c\u4ed6\u6240\u751f\u957f\u7684\u90a3\u4e0d\u5e78\u7684\u65f6\u4ee3\u6784\u6210\u4e86\u5f3a\u70c8\u7684\u77db\u76fe\u51b2\u7a81\u3002\u53e4\u5e0c\u814a\u82f1\u96c4\u62da\u547d\u6323\u624e\u594b\u6597\uff0c\u7ec8\u4e8e\u654c\u4e0d\u8fc7\u547d\u8fd0\u7684\u529b\u91cf\u800c\u57ae\u4e86\u4e0b\u6765\u3002\u6253\u51fb\u8881\u5d07\u7115\u7684\u4e0d\u662f\u547d\u8fd0\uff0c\u800c\u662f\u65f6\u52bf\u3002\u867d\u7136\uff0c\u5728\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\u6765\uff0c\u65f6\u52bf\u4e5f\u5c31\u662f\u547d\u8fd0\u3002\u50cf\u5e0c\u814a\u53f2\u8bd7\u4e0e\u60b2\u5267\u4e2d\u90a3\u4e9b\u82f1\u96c4\u4eec\u4e00\u6837\uff0c\u4ed6\u8f70\u8f70\u70c8\u70c8\u7684\u6218\u6597\u4e86\uff0c\u4f46\u6bcf\u4e00\u573a\u6218\u6597\uff0c\u90fd\u662f\u5728\u4e00\u6b65\u6b65\u8d70\u5411\u4e0d\u53ef\u907f\u514d\u7684\u60b2\u5267\u7ed3\u5c40\u3002 \u3000\u3000\u5e0c\u814a\u53f2\u8bd7\u300a\u4f0a\u91cc\u4e9a\u7279\u300b\u8bb0\u8ff0\u8d6b\u514b\u6258\u548c\u4e9a\u5951\u529b\u65af\u7ed5\u57ce\u5927\u6218\u8fd9\u4e00\u6bb5\u4e2d\uff0c\u63cf\u5199\u4f17\u5929\u795e\u62ff\u4e86\u5929\u5e73\u6765\u79e4\u8fd9\u4e24\u4e2a\u82f1\u96c4\u7684\u547d\u8fd0\uff0c\u5c0f\u65f6\u5019\u6211\u8bfb\u5230\u8d6b\u514b\u6258\u8fd9\u4e00\u7aef\u6c89\u4e86\u4e0b\u53bb\uff0c\u5929\u795e\u4eec\u51b3\u5b9a\u4ed6\u5fc5\u987b\u6218\u8d25\u800c\u6b7b\uff0c\u611f\u5230\u975e\u5e38\u96be\u8fc7\uff0c\u201c\u90a3\u4e0d\u516c\u5e73\uff01\u90a3\u4e0d\u516c\u5e73\uff01\u201d\u8fc7\u4e86\u8bb8\u591a\u5c81\u6708\uff0c\u5f53\u6211\u8bfb\u5230\u6ee1\u6e05\u7684\u7687\u592a\u6781\u600e\u6837\u8bbe\u53cd\u95f4\u8ba1\u3001\u5d07\u796f\u548c\u4ed6\u7684\u5927\u81e3\u4eec\u600e\u6837\u5546\u91cf\u8981\u4e0d\u8981\u6740\u6b7b\u8881\u5d07\u7115\uff0c\u540c\u6837\u6709\u5267\u70c8\u7684\u51c4\u6006\u4e4b\u611f\u3002 \u3000\u3000\u5386\u53f2\u5bb6\u8bc4\u8bba\u8881\u5d07\u7115\uff0c\u7740\u773c\u70b9\u5728\u4e8e\u4ed6\u7684\u529f\u4e1a\u3001\u4ed6\u5bf9\u5f53\u65f6\u53ca\u540e\u4e16\u7684\u5f71\u54cd\u3001\u4ed6\u5728\u660e\u6e05\u4e24\u4e2a\u671d\u4ee3\u8986\u4ea1\u4e0e\u5174\u8d77\u4e4b\u9645\u6240\u8d77\u7684\u4f5c\u7528\u3002\u8fd1\u5341\u591a\u5e74\u6765\uff0c\u6211\u51e0\u4e4e\u6bcf\u5929\u90fd\u5199\u4e00\u6bb5\u5c0f\u8bf4\uff0c\u53c8\u5199\u4e00\u6bb5\u62a5\u4e0a\u7684\u793e\u8bc4\uff0c\u56e0\u6b64\u5bf9\u5386\u53f2\u3001\u653f\u6cbb\u4e0e\u5c0f\u8bf4\u662f\u540c\u6837\u7684\u611f\u5230\u5174\u8da3\uff0c\u7136\u800c\u5728\u7814\u7a76\u8881\u5d07\u7115\u7684\u4e00\u751f\u4e4b\u65f6\uff0c\u4ed6\u5f3a\u70c8\u7684\u6027\u683c\u6bd4\u4e4b\u4ed6\u7684\u529f\u4e1a\u66f4\u52a0\u5438\u5f15\u6211\u7684\u6ce8\u610f\u3002 \u3000\u3000\u6574\u4f53\u8bf4\u6765\uff0c\u6e05\u671d\u6bd4\u660e\u671d\u597d\u5f97\u591a\u3002\u4ece\u6e05\u592a\u7956\u7b97\u8d77\u7684\u6e05\u671d\u5341\u4e8c\u4e2a\u541b\u4e3b\uff0c\u4ed6\u4eec\u7684\u603b\u5e73\u5747\u5206\u6570\u548c\u660e\u671d\u5341\u516d\u4e2a\u7687\u5e1d\u76f8\u6bd4\uff0c\u6211\u4ee5\u4e3a\u5728\u6570\u5b66\u4e0a\u7b80\u76f4\u4e0d\u80fd\u6bd4\uff0c\u56e0\u4e3a\u524d\u8005\u7684\u662f\u76f8\u5f53\u9ad8\u7684\u6b63\u6570\uff0c\u540e\u8005\u662f\u76f8\u5f53\u9ad8\u7684\u8d1f\u6570\u3002\u5bf9\u4e8e\u6ee1\u6d32\u4eba\u5165\u4e3b\u4e2d\u56fd\u4e00\u4e8b\uff0c\u8fd1\u4ee3\u7684\u8bc4\u4ef7\u4e0e\u524d\u4eba\u4e5f\u9887\u6709\u6539\u53d8\u3002\u6240\u4ee5\u8881\u5d07\u7115\u7684\u529f\u4e1a\uff0c\u4e0d\u514d\u968f\u7740\u65f6\u4ee3\u7684\u8fdb\u5c55\u800c\u6e10\u6e10\u5931\u5374\u5149\u5f69\u3002\u4f46\u4ed6\u82f1\u96c4\u6c14\u6982\u7684\u98ce\u534e\u5374\u6c38\u8fdc\u4e0d\u4f1a\u6cef\u706d\u3002\u6b63\u5982\u5f53\u5e74\u4e03\u56fd\u7eb7\u4e89\u7684\u662f\u975e\u6210\u8d25\uff0c\u5728\u4eca\u5929\u5df2\u6ca1\u6709\u591a\u5927\u610f\u4e49\u4e86\uff0c\u4f46\u8346\u8f72\u3001\u5c48\u539f\u3001\u853a\u76f8\u5982\u3001\u5ec9\u9887\u3001\u4fe1\u9675\u541b\u7b49\u7b49\u8fd9\u4e9b\u4eba\u7269\u7684\u751f\u547d\uff0c\u5374\u8d85\u8d8a\u4e86\u5386\u53f2\u4e0e\u653f\u6cbb\u3002 \u3000\u3000\u300a\u78a7\u8840\u5251\u300b\u4e2d\u7684\u8881\u627f\u5fd7\uff0c\u5728\u6027\u683c\u4e0a\u53ea\u662f\u4e00\u4e2a\u5e73\u51e1\u4eba\u7269\u3002\u4ed6\u6ca1\u6709\u6297\u62d2\u8270\u96be\u65f6\u4e16\u7684\u52c7\u6c14\uff0c\u53d7\u4e86\u632b\u6298\u540e\u9003\u907f\u6d77\u5916\uff0c\u5c31\u50cf\u6211\u4eec\u5927\u591a\u6570\u5728\u6d77\u5916\u7684\u4eba\u4e00\u6837\u3002 \u3000\u3000\u8881\u5d07\u7115\u5374\u662f\u771f\u6b63\u7684\u82f1\u96c4\uff0c\u5927\u624d\u8c6a\u6c14\uff0c\u7b3c\u76d6\u5f53\u4e16\uff0c\u5373\u4f7f\u4ed6\u7684\u7f3a\u70b9\uff0c\u4e5f\u662f\u82f1\u96c4\u5f0f\u7684\u60ca\u4e16\u9a87\u4fd7\u3002\u4ed6\u6bd4\u5c0f\u8bf4\u4e2d\u865a\u6784\u7684\u82f1\u96c4\u4eba\u7269\uff0c\u6709\u66f4\u591a\u7684\u82f1\u96c4\u6c14\u6982\u3002 \u3000\u3000\u4ed6\u7684\u6027\u683c\u50cf\u662f\u4e00\u67c4\u950b\u9510\u7edd\u4f26\u3001\u7cbe\u521a\u65e0\u4fe6\u7684\u5b9d\u5251\u3002\u5f53\u6e05\u548c\u5347\u5e73\u7684\u65f6\u65e5\uff0c\u60ac\u5728\u58c1\u4e0a\uff0c\u4e0d\u514d\u4f1a\u4e2d\u591c\u81ea\u5578\uff0c\u8dc3\u51fa\u5251\u5323\u3002\u5728\u5929\u660f\u5730\u6697\u7684\u4e71\u4e16\uff0c\u5219\u5c60\u9f99\u6740\u864e\u4e4b\u540e\uff0c\u7ec8\u4e8e\u5bf8\u5bf8\u65ad\u6298\u3002 \u3000\u3000\u5728\u660e\u672b\u90a3\u6bb5\u4e0d\u5e78\u7684\u65e5\u5b50\u4e2d\uff0c\u4efb\u4f55\u4eba\u90fd\u662f\u4e0d\u5e78\u7684\u3002\u6bcf\u4e00\u4e2a\u541b\u4e3b\u5728\u4e34\u6b7b\u4e4b\u65f6\uff0c\u90fd\u6df1\u6df1\u611f\u5230\u4e86\u5931\u8d25\u7684\u5c48\u8fb1\uff1a\u5d07\u796f\u3001\u6e05\u592a\u7956\u52aa\u5c14\u54c8\u8d64\u3001\u6e05\u592a\u5b97\u7687\u592a\u6781\uff08\u5982\u679c\u4ed6\u4e0d\u662f\u88ab\u4eba\u8c0b\u6740\u7684\uff0c\u90a3\u4e48\u662f\u60df\u4e00\u7684\u4f8b\u5916\uff09\u3001\u8499\u53e4\u4eba\u7684\u9996\u9886\u6797\u4e39\u6c57\u3001\u671d\u9c9c\u56fd\u738b\u674e\u4f51\uff1b\u59cb\u7ec8\u662f\u6b7b\u8def\u4e00\u6761\u7684\u5c06\u519b\u548c\u5927\u81e3\uff08\u594b\u52c7\u6297\u654c\u7684\u5c06\u519b\u4e0e\u964d\u654c\u505a\u6c49\u5978\u7684\u5c06\u519b\uff0c\u5fe0\u9ca0\u6b63\u76f4\u7684\u5927\u81e3\u4e0e\u5978\u4f5e\u65e0\u803b\u7684\u5927\u81e3\uff0c\u547d\u8fd0\u90fd\u6ca1\u751a\u4e48\u5206\u522b\uff0c\u4f46\u5728\u4e00\u4e2a\u6bd4\u8f83\u6e29\u548c\u7684\u65f6\u4ee3\uff0c\u5978\u81e3\u5374\u5e38\u5e38\u80fd\u5f97\u5584\u7ec8\uff0c\u4f8b\u5982\u79e6\u6867\uff09\uff1b\u6124\u6012\u4e0d\u5e73\u7684\u77e5\u8bc6\u4efd\u5b50\uff0c\u9886\u4e0d\u5230\u7cae\u9977\u7684\u5175\u5352\uff0c\u751f\u547d\u671d\u4e0d\u4fdd\u5915\u7684\u201c\u6d41\u5bc7\u201d\uff0c\u9965\u997f\u6d41\u79bb\u7684\u767e\u59d3\uff0c\u4ee5\u53ca\u6709\u5de8\u5927\u624d\u80fd\u4e0e\u52c7\u6c14\u7684\u82f1\u96c4\u4eba\u7269\uff1a\u6768\u6d9f\u3001\u718a\u5ef7\u5f3c\u3001\u5b59\u627f\u5b97\u3001\u674e\u81ea\u6210\u3001\u8881\u5d07\u7115\u3002 \u3000\u3000\u5728\u90a3\u4e2a\u65f6\u4ee3\u4e2d\uff0c\u4eba\u4eba\u90fd\u906d\u5230\u4e86\u5728\u592a\u5e73\u5e74\u6708\u4e2d\u6240\u65e0\u6cd5\u60f3\u8c61\u7684\u82e6\u96be\u3002\u5728\u5c71\u4e1c\u7684\u5927\u9965\u8352\u4e2d\uff0c\u4e08\u592b\u5403\u4e86\u59bb\u5b50\u7684\u5c38\u4f53\uff0c\u6bcd\u4eb2\u5403\u4e86\u513f\u5b50\u7684\u5c38\u4f53\u3002\u90a3\u662f\u5c0f\u4eba\u7269\u7684\u60b2\u5267\uff0c\u4ed6\u4eec\u5fc3\u4e2d\u7684\u60b2\u75db\uff0c\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u6bd4\u82f1\u96c4\u4eec\u8f7b\u3002\u4e0d\u8fc7\u5c0f\u4eba\u7269\u53ea\u662f\u9ed8\u9ed8\u7684\u5fcd\u53d7\uff0c\u82f1\u96c4\u4eec\u5374\u52c7\u6562\u5730\u594b\u6218\u4e86\u4e00\u573a\uff0c\u5728\u5386\u53f2\u4e0a\u7559\u4e0b\u4e86\u75d5\u8ff9\u3002\u82f1\u96c4\u7684\u5c0a\u4e25\u4e0e\u4f1f\u70c8\uff0c\u7ecf\u8fc7\u4e86\u65e0\u6570\u65f6\u65e5\u4e4b\u540e\uff0c\u4ecd\u5728\u540e\u4eba\u5fc3\u4e2d\u6fc0\u8d77\u6ce2\u6f9c\u3002 \u3000\u3000\u4e00 \u3000\u3000\u8fd9\u4e2a\u4e0d\u5e78\u7684\u65f6\u4ee3\uff0c\u662f\u6570\u5341\u5e74\u8150\u8d25\u8fbe\u4e8e\u6781\u70b9\u7684\u653f\u6cbb\u63aa\u65bd\u6240\u7d2f\u79ef\u800c\u6210\u7684\u3002 \u3000\u3000\u6211\u4e66\u67b6\u4e0a\u6709\u4e00\u90e8\u82f1\u56fd\u5386\u53f2\u5bb6\u5409\u670b\u7684\u300a\u7f57\u9a6c\u5e1d\u56fd\u8870\u4ea1\u53f2\u300b\uff0c\u662f\u4e09\u5377\u6ce8\u91ca\u672c\u2460\u3002\u4e66\u810a\u4e0a\u7ed8\u7740\u7f57\u9a6c\u5f0f\u5efa\u7b51\u7684\u4e24\u6839\u5927\u7406\u77f3\u67f1\u5b50\uff0c\u7b2c\u4e00\u5377\u7684\u67f1\u5b50\uff0c\u67f1\u5934\u4e0a\u6709\u4e9b\u6b8b\u7f3a\u7834\u635f\uff0c\u7b2c\u4e8c\u5377\u7684\u67f1\u5b50\u6b8b\u635f\u66f4\u591a\uff0c\u7b2c\u4e09\u5377\u7684\u67f1\u5b50\u5b8c\u5168\u57ae\u4e86\u3002\u8fd9\u8c61\u5f81\u4e00\u4e2a\u5e1d\u56fd\u7684\u8870\u8d25\u548c\u706d\u4ea1\uff0c\u5982\u4f55\u4e00\u6b65\u6b65\u7684\u53d1\u5c55\u3002 \u3000\u3000\u660e\u671d\u7684\u8870\u4ea1\u4e5f\u662f\u8fd9\u6837\u3002 \u3000\u3000\u660e\u671d\u7684\u8986\u706d\uff0c\u5f00\u59cb\u4e8e\u795e\u5b97\u2461\u3002 \u3000\u3000\u795e\u5b97\u5e74\u53f7\u4e07\u5386\uff0c\u662f\u660e\u671d\u8bf8\u5e1d\u4e2d\u5728\u4f4d\u6700\u4e45\u7684\uff0c\u4e00\u5171\u505a\u4e86\u56db\u5341\u516b\u5e74\u7687\u5e1d\u3002\u53ea\u56e0\u4e3a\u4ed6\u505a\u7687\u5e1d\u7684\u65f6\u5019\u5b9e\u5728\u592a\u4e45\uff0c\u6240\u4ee5\u5bf9\u56fd\u5bb6\u4eba\u6c11\u6240\u9020\u6210\u7684\u7978\u5bb3\u4e5f\u7279\u522b\u5927\u3002\u4ed6\u6b7b\u65f6\u4e94\u5341\u516b\u5c81\uff0c\u672c\u6765\u5e76\u4e0d\u7b97\u8001\uff0c\u4ed6\u7684\u7956\u5b97\u660e\u592a\u7956\u6d3b\u5230\u4e03\u5341\u4e00\u5c81\uff0c\u6210\u7956\u516d\u5341\u4e94\u5c81\uff0c\u4e16\u5b97\u516d\u5341\u5c81\u3002\u53ef\u662f\u795e\u5b97\u672a\u8001\u5148\u8870\uff0c\u540e\u6765\u66f4\u62bd\u4e0a\u4e86\u9e26\u7247\u3002\u9e26\u7247\u6ca1\u6709\u7f29\u77ed\u4ed6\u7684\u5bff\u547d\uff0c\u5374\u6bd2\u5bb3\u4e86\u4ed6\u7684\u7cbe\u795e\u3002\u4ed6\u7684\u8d2a\u5a6a\u5927\u6982\u662f\u5929\u751f\u7684\u672c\u6027\uff0c\u4f46\u532a\u5937\u6240\u601d\u7684\u61d2\u60f0\uff0c\u4e00\u5b9a\u662f\u51fa\u4e8e\u9e26\u7247\u7684\u5f71\u54cd\u3002 \u3000\u3000\u7136\u800c\u4e07\u5386\u521d\u5e74\uff0c\u5374\u662f\u4e2d\u56fd\u5386\u53f2\u4e0a\u6700\u5149\u5f69\u8f89\u714c\u7684\u65f6\u671f\u4e4b\u4e00\u3002\u8fd1\u4ee3\u4e2d\u897f\u5b66\u8005\u7814\u7a76\u74f7\u5668\u53ca\u5176\u4ed6\u624b\u5de5\u827a\u54c1\uff0c\u6709\u8fd9\u6837\u4e00\u4e2a\u5171\u901a\u7684\u610f\u89c1\uff1a\u5728\u4e2d\u56fd\u56fd\u529b\u6700\u5174\u76db\u7684\u65f6\u671f\uff0c\u6240\u5236\u4f5c\u7684\u74f7\u5668\u6700\u7cbe\u91c7\u3002\u4e07\u5386\u5e74\u95f4\u7684\u74f7\u5668\u548c\u73d0\u7405\u5668\u707f\u70c2\u534e\u7f8e\uff0c\u7cbe\u5de7\u96c5\u81f4\uff0c\u6d35\u4e3a\u7f55\u89c1\u7684\u6770\u4f5c\u3002\u56e0\u4e3a\u4e07\u5386\u6700\u521d\u5341\u5e74\uff0c\u5f20\u5c45\u6b63\u5f53\u56fd\uff0c\u4ed6\u662f\u4e2d\u56fd\u5386\u53f2\u4e0a\u96be\u5f97\u4e00\u89c1\u7684\u7cbe\u660e\u80fd\u5e72\u7684\u5927\u653f\u6cbb\u5bb6\u3002 \u3000\u3000\u795e\u5b97\u63a5\u4f4d\u65f6\u53ea\u6709\u5341\u5c81\uff0c\u4e00\u5207\u542c\u6bcd\u4eb2\u7684\u8bdd\u3002\u4e24\u5bab\u592a\u540e\u5f88\u4fe1\u4efb\u5f20\u5c45\u6b63\uff0c\u653f\u6cbb\u4e0a\u6743\u529b\u6781\u5927\u7684\u53f8\u793c\u592a\u76d1\u51af\u4fdd\u53c8\u7ed9\u5f20\u5c45\u6b63\u7b3c\u7edc\u5f97\u5f88\u597d\uff0c\u8fd9\u4e9b\u6709\u5229\u7684\u6761\u4ef6\u52a0\u5728\u4e00\u8d77\uff0c\u5f20\u5c45\u6b63\u4fbf\u80fd\u653e\u624b\u529e\u4e8b\u3002\u660e\u671d\u81ea\u660e\u592a\u7956\u665a\u5e74\u8d77\u5c31\u4e0d\u518d\u6709\u5bb0\u76f8\uff0c\u5f20\u5c45\u6b63\u662f\u5927\u5b66\u58eb\uff0c\u540d\u4e49\u662f\u9996\u8f85\uff0c\u7b49\u4e8e\u662f\u5bb0\u76f8\u3002 \u3000\u3000\u4ece\u4e07\u5386\u5143\u5e74\u5230\u5341\u5e74\uff0c\u5f20\u5c45\u6b63\u7684\u653f\u7ee9\u707f\u7136\u53ef\u89c2\u3002\u4ed6\u91cd\u7528\u540d\u5c06\u674e\u6210\u6881\u3001\u621a\u7ee7\u5149\u3001\u738b\u5d07\u53e4\uff0c\u4f7f\u5f97\u4e3b\u8981\u662f\u8499\u53e4\u4eba\u7684\u5317\u65b9\u5f02\u65cf\u6bcf\u6b21\u5165\u4fb5\u90fd\u5927\u8d25\u800c\u5f52\uff0c\u53ea\u5f97\u5b89\u5206\u5b88\u5df1\u800c\u548c\u660e\u671d\u8fdb\u884c\u548c\u5e73\u8d38\u6613\u3002\u5357\u65b9\u5c11\u6570\u6c11\u65cf\u7684\u6b66\u88c5\u66b4\u52a8\uff0c\u4e5f\u90fd\u4e00\u4e00\u7ed9\u4ed6\u6d3e\u4eba\u5e73\u5b9a\u3002\u56fd\u5bb6\u5bcc\u5f3a\uff0c\u50a8\u5907\u7684\u7cae\u98df\u53ef\u7528\u5341\u5e74\uff0c\u5e93\u5b58\u7684\u76c8\u4f59\u8d85\u8fc7\u4e86\u5168\u56fd\u4e00\u5e74\u7684\u5c81\u51fa\u3002\u4ea4\u901a\u90ae\u4f20\u529e\u5f97\u4e95\u4e95\u6709\u6761\u3002\u6e05\u4e08\u5168\u56fd\u7530\u4ea9\u9762\u79ef\uff0c\u4f7f\u5f97\u7a0e\u6536\u516c\u5e73\uff0c\u4e0d\u81f4\u50cf\u4ee5\u524d\u90a3\u6837\u7531\u7a77\u4eba\u8d1f\u62c5\u8fc7\u5206\u7684\u94b1\u7cae\u800c\u5b98\u50da\u8c6a\u5f3a\u5374\u4e0d\u4ea4\u7a0e\u3002\u4ed6\u5168\u529b\u652f\u6301\u5de5\u90e8\u5c1a\u4e66\u6f58\u5b63\u9a6f\uff0c\u5c06\u6cdb\u6ee5\u6210\u707e\u7684\u9ec4\u6cb3\u4e0e\u6dee\u6cb3\u6cbb\u597d\uff0c\u5c06\u6c34\u9000\u540e\u7684\u8352\u5730\u5206\u7ed9\u707e\u6c11\u5f00\u57a6\uff0c\u514d\u7a0e\u4e09\u5e74\u3002\u5b98\u50da\u7684\u5347\u964d\u5236\u5ea6\u6267\u884c\u5f97\u5f88\u4e25\u683c\uff0c\u4e25\u5389\u60e9\u529e\u8d2a\u6c61\u3002 \u3000\u3000\u5728\u90a3\u65f6\u5019\uff0c\u4e2d\u56fd\u662f\u5168\u4e16\u754c\u6700\u5148\u8fdb\u3001\u6700\u5bcc\u5f3a\u7684\u5927\u56fd\u3002\u6b27\u6d32\u7684\u6587\u4eba\u5b66\u58eb\u5728\u63d0\u5230\u4e2d\u56fd\u7684\u65f6\u5019\uff0c\u65e0\u4e0d\u6b23\u6155\u5411\u5f80\u3002\u4ed6\u4eec\u4f69\u670d\u4e2d\u56fd\u7684\u6587\u6cbb\u6559\u5316\u3001\u4e2d\u56fd\u7684\u8003\u8bd5\u4e0e\u6587\u5b98\u5236\u5ea6\uff0c\u4f69\u670d\u4e2d\u56fd\u7684\u9053\u8def\u56db\u901a\u516b\u8fbe\u2462\uff0c\u4f69\u670d\u4e2d\u56fd\u7684\u8001\u767e\u59d3\u751f\u6d3b\u5f97\u6bd4\u6b27\u6d32\u8d2b\u6c11\u597d\u5f97\u591a\u3002\u4e07\u5386\u5341\u5e74\u662f\u516c\u5143\u4e00\u4e94\u516b\u4e8c\u5e74\u3002\u8981\u5728\u516d\u5e74\u4e4b\u540e\uff0c\u82f1\u56fd\u624d\u6253\u8d25\u897f\u73ed\u7259\u7684\u65e0\u654c\u8230\u961f\uff1b\u518d\u8fc7\u4e09\u5341\u516b\u5e74\uff0c\u82f1\u56fd\u7684\u6e05\u6559\u5f92\u624d\u4e58\u201c\u4e94\u6708\u82b1\u53f7\u201d\u5230\u8fbe\u7f8e\u6d32\uff1b\u518d\u8fc7\u516d\u5341\u4e00\u5e74\uff0c\u4e94\u5c81\u7684\u8def\u6613\u5341\u56db\u624d\u767b\u4e0a\u6cd5\u56fd\u7684\u738b\u5ea7\u3002\u90a3\u65f6\u838e\u58eb\u6bd4\u4e9a\u53ea\u6709\u5341\u516d\u5c81\uff0c\u8fd8\u5728\u82f1\u56fd\u7684\u6811\u6797\u91cc\u5077\u4eba\u5bb6\u7684\u9e7f\u3002\u76f4\u5230\u516b\u5341\u4e09\u5e74\u4e4b\u540e\uff0c\u4f26\u6566\u8fd8\u7531\u4e8e\u592a\u6c61\u79fd\u3001\u592a\u4e0d\u536b\u751f\uff0c\u7206\u53d1\u4e86\u6050\u6016\u7684\u5927\u761f\u75ab\u3002\u5728\u4e07\u5386\u521d\u5e74\uff0c\u5317\u4eac\u3001\u5357\u4eac\u3001\u626c\u5dde\u3001\u676d\u5dde\u8fd9\u4e9b\u5c31\u50cf\u4e07\u5386\u5f69\u74f7\u90a3\u6837\u534e\u7f8e\u7684\u5927\u57ce\u5e02\uff0c\u5728\u5916\u56fd\u4eba\u5fc3\u76ee\u4e2d\u771f\u50cf\u662f\u5929\u5802\u4e00\u6837\u3002 \u3000\u3000\u4e2d\u56fd\u7684\u7ecf\u6d4e\u4e5f\u5728\u8fc5\u901f\u53d1\u5c55\uff0c\u624b\u5de5\u4e1a\u548c\u6280\u672f\u975e\u5e38\u5148\u8fdb\u3002\u5728\u5341\u4e94\u4e16\u7eaa\u65f6\uff0c\u4e2d\u56fd\u662f\u4e16\u754c\u4e0a\u6700\u91cd\u8981\u7684\u4ea7\u68c9\u533a\u4e4b\u4e00\u3002\u7531\u4e8e\u5728\u6b63\u5fb7\u5e74\u95f4\u5f00\u59cb\u91c7\u7528\u4e86\u8d8a\u5357\u7684\u4f18\u826f\u7a3b\u79cd\uff0c\u519c\u7530\u52a0\u8f9f\uff0c\u7c73\u4ea7\u5927\u589e\uff0c\u5c24\u5176\u662f\u5e7f\u4e1c\u4e00\u5e26\u3002\u56e0\u4e3a\u63a8\u5e7f\u79cd\u690d\u6c34\u7a3b\uff0c\u6c34\u7530\u4e2d\u5927\u91cf\u517b\u9c7c\uff0c\u759f\u868a\u5927\u51cf\u2463\uff0c\u5cad\u5357\u5411\u6765\u79f0\u4e3a\u7634\u75a0\u7684\u759f\u75be\u5df2\u4e0d\u50cf\u8fc7\u53bb\u90a3\u6837\u53ef\u6015\uff0c\u6240\u4ee5\u4e24\u5e7f\u7684\u7ecf\u6d4e\u6587\u5316\u4e5f\u5f00\u59cb\u8fc5\u901f\u53d1\u5c55\u3002 \u3000\u3000\u53ef\u662f\u541b\u4e3b\u96c6\u6743\u7684\u7edd\u5bf9\u4e13\u5236\u5236\u5ea6\uff0c\u518d\u52a0\u4e0a\u8fde\u7eed\u56db\u4e2a\u660f\u5eb8\u8150\u8d25\u7684\u7687\u5e1d\uff0c\u5c06\u8fd9\u5bcc\u4e8e\u6587\u5316\u6559\u517b\u800c\u52e4\u52b3\u806a\u660e\u7684\u4e00\u4ebf\u4eba\u6c11\u3001\u8fd9\u4e3e\u4e16\u65e0\u53cc\u7684\u5bcc\u5f3a\u5927\u56fd\u63a8\u5165\u4e86\u75db\u82e6\u7684\u6df1\u6e0a\u3002 \u3000\u3000\u5f20\u5c45\u6b63\u4e8e\u4e07\u5386\u5341\u5e74\u901d\u4e16\uff0c\u4e8c\u5341\u5c81\u7684\u9752\u5e74\u7687\u5e1d\u81ea\u5df1\u6765\u6267\u653f\u4e86\u3002\u7687\u5e1d\u8ffd\u593a\u5f20\u5c45\u6b63\u7684\u5b98\u7235\uff0c\u5c06\u4ed6\u5bb6\u4ea7\u5145\u516c\uff0c\u5bb6\u5c5e\u5145\u519b\uff0c\u5c06\u4ed6\u957f\u5b50\u903c\u5f97\u81ea\u6740\u3002 \u3000\u3000\u795e\u5b97\u662f\u76f8\u5f53\u806a\u660e\u7684\u3002\u4e2d\u56fd\u5386\u53f2\u4e0a\u7684\u660f\u541b\u5927\u90fd\u6709\u4e9b\u5c0f\u806a\u660e\uff0c\u968b\u7080\u5e1d\u3001\u5b8b\u5fbd\u5b97\u3001\u674e\u540e\u4e3b\uff0c\u90fd\u662f\u6587\u91c7\u6590\u7136\u3002\u660e\u795e\u5b97\u7684\u806a\u660e\u4e4b\u4e0a\uff0c\u6240\u9644\u52a0\u7684\u4e0d\u662f\u6587\u91c7\uff0c\u800c\u662f\u4e0d\u53ef\u601d\u8bae\u7684\u61d2\u60f0\uff0c\u4e0d\u53ef\u601d\u8bae\u7684\u8d2a\u5a6a\u3002\u7687\u5e1d\u61d2\u60f0\u672c\u6765\u5e76\u4e0d\u662f\u592a\u4e25\u91cd\u7684\u6bdb\u75c5\uff0c\u4ed6\u53ea\u987b\u4efb\u7528\u4e00\u4e24\u4e2a\u80fd\u5e72\u7684\u5927\u81e3\uff0c\u751a\u4e48\u4e8b\u60c5\u90fd\u4ea4\u7ed9\u4ed6\u4eec\u53bb\u529e\u5c31\u662f\u4e86\uff0c\u591a\u534a\u653f\u6cbb\u53ea\u6709\u66f4\u52a0\u4e0a\u8f68\u9053\u4e9b\uff0c\u4e2d\u56fd\u5386\u53f2\u4e0a\u4e0d\u4e4f\u201c\u4e3b\u660f\u4e8e\u4e0a\uff0c\u653f\u6e05\u4e8e\u4e0b\u201d\u7684\u5148\u4f8b\u3002\u7136\u800c\u795e\u5b97\u61d2\u60f0\u4e4b\u5916\u8fd8\u52a0\u4e0a\u8981\u6293\u6743\uff0c\u51e0\u5341\u5e74\u4e2d\u81ea\u5df1\u4e0d\u529e\u4e8b\uff0c\u4e5f\u7edd\u5bf9\u4e0d\u8ba9\u5927\u81e3\u529e\u4e8b\u3002\u8fd9\u5728\u4e16\u754c\u5386\u53f2\u4e0a\u56fa\u7136\u7a7a\u524d\uff0c\u76f8\u4fe1\u4e5f\u5fc5\u7edd\u540e\u3002 \u3000\u3000\u505a\u4e86\u7687\u5e1d\uff0c\u8981\u751a\u4e48\u6709\u751a\u4e48\uff0c\u4f46\u795e\u5b97\u6240\u8981\u7684\uff0c\u504f\u504f\u53ea\u662f\u5bf9\u4ed6\u6700\u65e0\u7528\u5904\u7684\u91d1\u94b1\u3002\u5982\u679c\u4ed6\u4e0d\u662f\u7687\u5e1d\uff0c\u4e00\u5b9a\u662f\u4e2a\u6210\u529f\u7684\u5546\u4eba\uff0c\u4ed6\u8840\u6db2\u4e2d\u6709\u4e00\u80a1\u4e0d\u53ef\u6291\u5236\u7684\u8d2a\u6027\u3002\u4ed6\u90a3\u4e9b\u7956\u5b97\u7687\u5e1d\u4eec\u6709\u7684\u9634\u72e0\u6bd2\u8fa3\uff0c\u6709\u7684\u80e1\u95f9\u8352\u5510\uff0c\u4f46\u6ca1\u6709\u4e00\u4e2a\u662f\u8fd9\u6837\u96be\u4ee5\u5f62\u5bb9\u7684\u8d2a\u5a6a\u3002\u56e0\u6b64\u8fd1\u4ee3\u6709\u4e00\u4f4d\u5386\u53f2\u5b66\u8005\u63a8\u60f3\uff0c\u4ed6\u8fd9\u6027\u683c\u662f\u51fa\u4e8e\u6bcd\u7cfb\u7684\u9057\u4f20\u3002\u4ed6\u6bcd\u4eb2\u662f\u4e00\u4e2a\u5c0f\u519c\u7684\u5973\u513f\u2464\u3002 \u3000\u3000\u7687\u5e1d\u8d2a\u94b1\uff0c\u6700\u65b9\u4fbf\u6709\u6548\u7684\u6cd5\u5b50\u5f53\u7136\u662f\u52a0\u7a0e\u3002\u795e\u5b97\u6240\u52a0\u7684\u7a0e\u4e0d\u6536\u5165\u56fd\u5e93\uff0c\u800c\u662f\u6536\u5165\u81ea\u5df1\u7684\u79c1\u4eba\u5e93\u623f\uff0c\u79f0\u4e3a\u201c\u5185\u5e93\u201d\u3002\u4ed6\u52a0\u7d27\u5f81\u6536\u5546\u7a0e\uff0c\u90a3\u662f\u672c\u6765\u6709\u7684\uff0c\u9664\u4e86\u4e66\u7c4d\u4e0e\u519c\u5177\u514d\u7a0e\u4e4b\u5916\uff0c\u4e00\u5207\u5546\u54c1\u4ea4\u6613\u90fd\u6536\u7a0e\u767e\u5206\u4e4b\u4e09\u3002\u4ed6\u53e6\u5916\u53c8\u53d1\u660e\u4e86\u4e00\u79cd\u201c\u77ff\u7a0e\u201d\u3002 \u3000\u3000\u5927\u6279\u6ca1\u6709\u53d7\u8fc7\u6559\u80b2\u3001\u56e0\u6b8b\u5e9f\u800c\u5fc3\u7406\u4e0a\u591a\u591a\u5c11\u5c11\u4e0d\u6b63\u5e38\u7684\u592a\u76d1\uff0c\u4f5c\u4e3a\u7687\u5e1d\u7684\u79c1\u4eba\u5f81\u7a0e\u4ee3\u8868\uff0c\u56db\u9762\u516b\u65b9\u7684\u51fa\u53bb\u6536\u77ff\u7a0e\u3002\u53ea\u8981\u201c\u77ff\u7a0e\u4f7f\u201d\u8ba4\u4e3a\u751a\u4e48\u5730\u65b9\u53ef\u4ee5\u5f00\u77ff\uff0c\u5c31\u8981\u5730\u4ea7\u7684\u6240\u6709\u4eba\u4ea4\u77ff\u7a0e\u3002\u8fd9\u4e9b\u592a\u76d1\u65e0\u6076\u4e0d\u4f5c\uff0c\u968f\u5e26\u592a\u6279\u6d41\u6c13\u6076\u68cd\uff0c\u5230\u5904\u6572\u8bc8\u52d2\u7d22\uff0c\u4e71\u6307\u4eba\u5bb6\u7684\u7956\u5b97\u575f\u5893\u3001\u4f4f\u5b85\u3001\u5546\u5e97\u3001\u4f5c\u574a\u3001\u7530\u5730\uff0c\u8bf4\u5730\u4e0b\u6709\u77ff\u85cf\uff0c\u8981\u4ea4\u77ff\u7a0e\u2465\u3002\u7ed3\u679c\u5929\u4e0b\u9a9a\u52a8\uff0c\u6fc0\u8d77\u4e86\u6570\u4e0d\u5c3d\u7684\u6c11\u53d8\u3002\u8fd9\u4e9b\u5fa1\u7528\u5f81\u7a0e\u7684\u592a\u76d1\u6743\u529b\u65e2\u5927\uff0c\u81ea\u7136\u5c31\u5f3a\u6a2a\u4e0d\u6cd5\uff0c\u5f80\u5f80\u64c5\u6740\u548c\u62f7\u6253\u6587\u6b66\u5b98\u540f\u3002\u6709\u4e00\u4e2a\u592a\u76d1\u9ad8\u6dee\u5949\u65e8\u53bb\u8fbd\u4e1c\u5f81\u77ff\u7a0e\u3001\u5546\u7a0e\uff0c\u641c\u62ec\u4e86\u58eb\u6c11\u7684\u8d22\u7269\u6570\u5341\u4e07\u4e24\uff0c\u902e\u6355\u4e86\u4e0d\u80af\u7f34\u7a0e\u7684\u79c0\u624d\u6570\u5341\u4eba\uff0c\u6253\u6b7b\u6307\u6325\uff0c\u8bec\u9677\u603b\u5175\u5b98\u72af\u6cd5\u3002\u795e\u5b97\u5f88\u61d2\uff0c\u751a\u4e48\u594f\u7ae0\u90fd\u4e0d\u7406\u4f1a\uff0c\u4f46\u53ea\u8981\u662f\u548c\u77ff\u7a0e\u6709\u5173\u7684\uff0c\u5fa1\u7528\u7a0e\u76d1\u5448\u62a5\u4e0a\u6765\uff0c\u4ed6\u7acb\u523b\u6279\u51c6\u3002 \u3000\u3000\u641c\u62ec\u7684\u89c4\u6a21\u4e4b\u5927\u5b9e\u662f\u9a87\u4eba\u542c\u95fb\u3002\u5728\u4e07\u5386\u521d\u5e74\u5f20\u5c45\u6b63\u5f53\u56fd\u4e4b\u65f6\uff0c\u5168\u5e74\u5c81\u5165\u662f\u56db\u767e\u4e07\u4e24\u5de6\u53f3\u2466\uff0c\u7687\u5bab\u7684\u8d39\u7528\u6bcf\u5e74\u6709\u5b9a\u989d\u4e00\u767e\u4e8c\u5341\u4e07\u4e24\uff0c\u5df2\u51e0\u5360\u5c81\u5165\u7684\u4e09\u5206\u4e4b\u4e00\u3002\u53ef\u662f\u5355\u5728\u4e07\u5386\u4e8c\u5341\u4e03\u5e74\u7684\u4e94\u5929\u4e4b\u5185\uff0c\u5c31\u641c\u62ec\u4e86\u77ff\u7a0e\u5546\u7a0e\u4e8c\u767e\u4e07\u4e24\u3002\u8fd9\u8fd8\u662f\u7f34\u5165\u7687\u5e1d\u5185\u5e93\u7684\u6570\u76ee\uff0c\u592a\u76d1\u548c\u968f\u4ece\u541e\u6ca1\u7684\u94b1\u8d22\uff0c\u53c8\u6bd4\u8fd9\u6570\u5b57\u5927\u5f97\u591a\u3002\u636e\u5f53\u65f6\u540f\u90e8\u5c1a\u4e66\u674e\u6234\u7684\u4f30\u8ba1\uff0c\u7f34\u5165\u5185\u5e93\u7684\u53ea\u5341\u5206\u4e4b\u4e00\u3001\u592a\u76d1\u514b\u6263\u7684\u662f\u5341\u5206\u4e4b\u4e8c\u3001\u968f\u4ece\u74dc\u5206\u7684\u662f\u5341\u5206\u4e4b\u4e09\u3001\u6d41\u6c13\u68cd\u5f92\u4e58\u673a\u5411\u826f\u6c11\u52d2\u7d22\u7684\u662f\u5341\u5206\u4e4b\u56db\u3002 \u3000\u3000\u53ef\u548c\u795e\u5b97\u7684\u8d2a\u5a6a\u5e76\u9a7e\u9f50\u9a71\u7684\u662f\u4ed6\u7684\u61d2\u3002 \u3000\u3000\u5728\u4ed6\u4e8c\u5341\u516b\u5c81\u90a3\u5e74\uff0c\u5927\u5b66\u58eb\u738b\u5bb6\u5c4f\u5c31\u4e0a\u594f\u7ae0\u8bf4\uff1a\u4e00\u5e74\u4e4b\u95f4\uff0c\u81e3\u53ea\u89c1\u5230\u5929\u989c\u4e24\u6b21\uff0c\u5076\u7136\u63d0\u51fa\u4e00\u4e9b\u5efa\u8bae\uff0c\u4e5f\u548c\u522b\u7684\u5b98\u5458\u7684\u594f\u7ae0\u4e00\u6837\uff0c\u7687\u4e0a\u5b8c\u5168\u4e0d\u7406\u3002 \u3000\u3000\u8fd9\u79cd\u60c5\u5f62\u8d8a\u6765\u8d8a\u6076\u5316\uff0c\u5230\u4e07\u5386\u56db\u5341\u4e8c\u5e74\uff0c\u9996\u8f85\u53f6\u5411\u9ad8\u594f\u79f0\uff1a\u516d\u90e8\u5c1a\u4e66\u4e2d\uff0c\u73b0\u5728\u53ea\u5269\u4e0b\u4e00\u90e8\u6709\u5c1a\u4e66\u4e86\uff0c\u5168\u56fd\u7684\u5de1\u629a\u3001\u5de1\u6309\u5fa1\u53f2\u3001\u5404\u5e9c\u5dde\u53bf\u7684\u77e5\u4e8b\u5df2\u7f3a\u4e86\u4e00\u534a\u4ee5\u4e0a\u3002\u4ed6\u7684\u594f\u7ae0\u5199\u5f97\u5341\u5206\u6fc0\u6602\uff0c\u8bf4\u73b0\u5728\u5df2\u7ecf\u4e2d\u5916\u79bb\u5fc3\uff0c\u4eac\u57ce\u91cc\u6028\u58f0\u8f7d\u9053\uff0c\u5927\u7978\u5df2\u5728\u773c\u524d\uff0c\u7687\u4e0a\u8fd8\u81ea\u4ee5\u4e3a\u4e0d\u89c1\u81e3\u5b50\u662f\u795e\u660e\u5999\u7528\uff0c\u6050\u6015\u81ea\u53e4\u4ee5\u6765\u7684\u5723\u5e1d\u660e\u738b\u90fd\u6ca1\u6709\u8fd9\u6837\u5999\u6cd5\u5427\u2467\u3002\u795e\u5b97\u62bd\u9971\u4e86\u9e26\u7247\uff0c\u5df2\u7ecf\u706b\u6c14\u5168\u65e0\u3002\u8fd9\u6837\u7684\u594f\u7ae0\uff0c\u5982\u679c\u843d\u5728\u5f00\u56fd\u7684\u592a\u7956\u3001\u6210\u7956\u3001\u672b\u4ee3\u7684\u601d\u5b97\u624b\u91cc\uff0c\u53f6\u5411\u9ad8\u975e\u6740\u5934\u4e0d\u53ef\u3002\u4f46\u795e\u5b97\u53ea\u8981\u6709\u94b1\u53ef\u62ec\uff0c\u7ed9\u5927\u81e3\u8ba5\u8bbd\u51e0\u53e5\u3001\u751a\u81f3\u9a82\u4e0a\u4e00\u987f\uff0c\u90fd\u65e0\u6240\u8c13\u3002 \u3000\u3000\u4e07\u5386\u5e74\u95f4\u7684\u4f17\u5927\u81e3\u8bf4\u5f97\u4e0a\u662f\u77e5\u65e0\u4e0d\u8a00\uff0c\u8a00\u65e0\u4e0d\u5c3d\u3002\u6709\u4eba\u4e0a\u594f\uff0c\u8bf4\u7687\u4e0a\u8fd9\u6837\u641e\u6cd5\uff0c\u52bf\u5fc5\u6c11\u7a77\u8d22\u5c3d\uff0c\u5929\u4e0b\u5927\u4e71\u2468\uff1b\u6709\u4eba\u8bf4\u965b\u4e0b\u662f\u653e\u4e86\u7b3c\u4e2d\u7684\u864e\u8c79\u8c7a\u72fc\u53bb\u541e\u98df\u767e\u59d3\u247d\uff1b\u6709\u4eba\u8bf4\u4e00\u65e6\u767e\u59d3\u9020\u53cd\uff0c\u965b\u4e0b\u5c31\u7b97\u6ee1\u5c4b\u5b50\u90fd\u662f\u91d1\u94f6\u73e0\u5b9d\uff0c\u53c8\u6709\u8c01\u6765\u7ed9\u4f60\u770b\u5b88\u247e\uff1f\u6709\u7684\u6307\u8d23\u8bf4\uff0c\u7687\u4e0a\u6b3a\u9a97\u767e\u59d3\uff0c\u4e0d\u514d\u7c7b\u4f3c\u6840\u7ea3\u660f\u541b\u247f\uff1b\u6709\u7684\u76f4\u6307\u4ed6\u4efb\u7528\u8086\u65e0\u5fcc\u60ee\u4e4b\u4eba\uff0c\u53bb\u5e72\u6ca1\u6709\u5929\u7406\u738b\u6cd5\u4e4b\u4e8b\u2480\uff1b\u6709\u7684\u8d23\u5907\u4ed6\u8bf4\u8bdd\u6beb\u65e0\u4fe1\u7528\u2481\u3002\u81e3\u5b50\u5c45\u7136\u80c6\u6562\u8fd9\u6837\u516c\u7136\u4e0a\u594f\u75db\u9a82\u7687\u5e1d\uff0c\u4e0d\u662f\u4e00\u4e24\u4e2a\u4e0d\u6015\u6b7b\u7684\u5fe0\u81e3\u9a82\uff0c\u800c\u662f\u5927\u5bb6\u90fd\u9a82\uff0c\u90a3\u4e5f\u662f\u7a7a\u524d\u7edd\u540e\u3001\u4ee4\u4eba\u96be\u4ee5\u60f3\u8c61\u7684\u4e8b\u3002\u7136\u800c\u8a00\u8005\u8c06\u8c06\uff0c\u542c\u8005\u85d0\u85d0\uff0c\u795e\u5b97\u5bf9\u8fd9\u4e9b\u6279\u8bc4\u5168\u4e0d\u7406\u776c\u3002\u6b63\u53f2\u4e0a\u7684\u8bb0\u8f7d\uff0c\u5f80\u5f80\u8bf4\u201c\u758f\u5165\uff0c\u4e0a\u6012\uff0c\u7559\u4e2d\u4e0d\u62a5\u201d\u3002\u7559\u4e2d\uff0c\u5c31\u662f\u4e0d\u6279\u590d\u3002\u6216\u8bb8\u4ed6\u61d2\u5f97\u8fde\u7f5a\u4eba\u4e5f\u4e0d\u60f3\u7f5a\u4e86\uff0c\u56e0\u4e3a\u7f5a\u4eba\u4e5f\u603b\u5f97\u4e0b\u4e00\u9053\u5723\u65e8\u624d\u884c\u3002\u4f46\u76f4\u5230\u4ed6\u6b7b\uff0c\u62da\u547d\u641c\u62ec\u7684\u4f5c\u98ce\u4e1d\u6beb\u4e0d\u6539\u3002\u540c\u65f6\u4e3a\u4e86\u5bf9\u6ee1\u6e05\u7528\u5175\uff0c\u53c8\u4e00\u518d\u589e\u52a0\u7530\u8d4b\u3002\u7687\u5e1d\u641c\u62ec\u6240\u5f97\u90fd\u5b58\u4e8e\u79c1\u4eba\u5e93\u623f\uff08\u5185\u5e93\uff09\uff0c\u653f\u5e9c\u7684\u516c\u5bb6\u5e93\u623f\uff08\u5916\u5e93\uff09\u5374\u603b\u662f\u4e0d\u591f\uff0c\u7ed3\u679c\u662f\u5185\u5e93\u592a\u5b9e\uff0c\u5916\u5e93\u592a\u865a\u2482\u3002 \u3000\u3000\u5728\u8fd9\u6837\u7a77\u51f6\u6781\u6076\u7684\u538b\u69a8\u4e0b\uff0c\u767e\u59d3\u7684\u751f\u6d3b\u5f53\u7136\u662f\u75db\u82e6\u8fbe\u4e8e\u6781\u70b9\u3002 \u3000\u3000\u795e\u5b97\u9664\u4e86\u4e13\u5fc3\u641c\u62ec\u4e4b\u5916\uff0c\u5bf9\u5176\u4ed6\u653f\u52a1\u59cb\u7ec8\u662f\u7edd\u5bf9\u7684\u7f6e\u4e4b\u5ea6\u5916\u3002\u4e07\u5386\u56db\u5341\u4e09\u5e74\u5341\u4e00\u6708\uff0c\u5fa1\u53f2\u7fdf\u51e4\u7fbd\uff08\u6ce8\uff1a\u5e94\u4e3a\u201c\u7fc0\u201d\u5b57\uff09\u4e2d\u7684\u594f\u7ae0\u4e2d\u8bf4\uff1a\u7687\u4e0a\u4e0d\u89c1\u5ef7\u81e3\uff0c\u5df2\u6709\u4e8c\u5341\u4e94\u5e74\u4e86\u3002 \u3000\u3000\u2460Edward Gibbon:The Decline and Fall of the Roman Empire,The Heritage Press,New York.\uff08\u6ce8\uff1a\u539f\u6765\u5168\u4e3a\u5168\u89d2\u82f1\u6587\u5b57\u6bcd\uff1a\uff25\uff44\uff57\uff41\uff52\uff44\uff27\uff49\uff42\uff42\uff4f\uff4e\uff1a\uff34\uff48\uff45\uff24\uff45\uff43\uff4c\uff49\uff4e\uff45\uff41\uff4e\uff44\uff26\uff41\uff4c\uff4c\uff4f\uff46\uff54\uff48\uff45\uff32\uff4f\uff4d\uff41\uff4e\uff25\uff4d\uff50\uff49\uff52\uff45\uff0c\uff34\uff48\uff45\uff28\uff45\uff52\uff49\uff54\uff41\uff47\uff45\uff30\uff52\uff45\uff53\uff53\uff0c\uff2e\uff45\uff57\uff39\uff4f\uff52\uff4b\uff0e\uff09 \u3000\u3000\u2461\u8fd9\u662f\u540e\u4e16\u8bba\u8005\u7684\u5171\u540c\u610f\u89c1\u3002\u300a\u660e\u53f2\xb7\u795e\u5b97\u672c\u7eaa\u300b\uff1a\u201c\u6545\u8bba\u8003\u8c13\uff1a\u660e\u4e4b\u4ea1\u5b9e\u4ea1\u4e8e\u795e\u5b97\u3002\u201d\u8d75\u7ffc\u300a\u5eff\u4e8c\u53f2\u7b54\uff08\u6ce8\uff1a\u5c31\u4e3a\u201c\u5284\u201d\u5b57\uff09\u8bb0\xb7\u4e07\u5386\u4e2d\u77ff\u7a0e\u4e4b\u5bb3\u300b\uff1a\u201c\u8bba\u8005\u8c13\u660e\u4e4b\u4ea1\uff0c\u4e0d\u4ea1\u4e8e\u5d07\u796f\u800c\u4ea1\u4e8e\u4e07\u5386\u4e91\u3002\u201d\u6e05\u9ad8\u5b97\u9898\u660e\u957f\u9675\u795e\u529f\u5723\u5fb7\u7891\uff1a\u201c\u660e\u4e4b\u4ea1\u975e\u4ea1\u4e8e\u6d41\u5bc7\uff0c\u800c\u4ea1\u4e8e\u795e\u5b97\u4e4b\u8352\u5510\uff0c\u53ca\u5929\u542f\u65f6\u9609\u5ba6\u4e4b\u4e13\u6a2a\uff0c\u5927\u81e3\u5fd7\u5728\u7984\u4f4d\u91d1\u94b1\uff0c\u767e\u5b98\u4e13\u52a1\u94bb\u8425\u963f\u8c00\u3002\u53ca\u601d\u5b97\u5373\u4f4d\uff0c\u9006\u9609\u867d\u8bdb\uff0c\u800c\u5929\u4e0b\u4e4b\u52bf\uff0c\u5df2\u5982\u6cb3\u51b3\u4e0d\u53ef\u590d\u585e\uff0c\u9c7c\u70c2\u4e0d\u53ef\u590d\u6536\u77e3\u3002\u800c\u53c8\u82db\u5bdf\u592a\u751a\uff0c\u4eba\u6000\u81ea\u514d\u4e4b\u5fc3\u3002\u5c0f\u6c11\u75be\u82e6\u800c\u65e0\u544a\uff0c\u6545\u76f8\u805a\u4e3a\u76d7\uff0c\u95ef\u8d3c\u4e58\u4e4b\uff0c\u800c\u660e\u793e\u9042\u5c4b\u3002\u545c\u547c\uff01\u6709\u5929\u4e0b\u8005\uff0c\u53ef\u4e0d\u77e5\u6240\u6212\u60e7\u54c9\uff1f\u201d \u3000\u3000\u2462\u5341\u516d\u4e16\u7eaa\u540e\u671f\u6765\u5230\u4e2d\u56fd\u6e38\u5386\u7684\u6b27\u6d32\u4eba\uff0c\u5982G.Pereira,G.da Gruz,M.de Rade\uff08\u6ce8\uff1a\uff27\uff0e\uff30\uff45\uff52\uff45\uff49\uff52\uff41\uff0c\uff27\uff0e\uff44\uff41\uff27\uff52\uff55\uff5a\uff0c\uff2d\uff0e\uff44\uff45\uff32\uff41\uff44\uff45\uff09\u7b49\u4eba\u8457\u4e66\u76db\u8d5e\u4e2d\u56fd\u3002\u4ed6\u4eec\u62ff\u4e2d\u56fd\u7684\u9053\u8def\u3001\u57ce\u5e02\u3001\u571f\u5730\u3001\u536b\u751f\u3001\u8d2b\u6c11\u751f\u6d3b\u7b49\u548c\u6b27\u6d32\u6bd4\u8f83\uff0c\u8ba4\u4e3a\u4e2d\u56fd\u597d\u5f97\u591a\u3002\u89c1A.P.Newton,ed.,Travel and Travellers of the Middle Ages;C.R.Boxer,South China in the 16th Century\uff08\u6ce8\uff1a\uff21\uff0e\uff30\uff0e\uff2e\uff45\uff57\uff54\uff4f\uff4e\uff0c\uff45\uff44\uff0e\uff0c\uff34\uff52\uff41\uff56\uff45\uff4c\uff41\uff4e\uff44\uff34\uff52\uff41\uff56\uff45\uff4c\uff4c\uff45\uff52\uff53\uff4f\uff46\uff54\uff48\uff45\uff2d\uff49\uff44\uff44\uff4c\uff45\uff21\uff47\uff45\uff53\uff1b\uff23\uff0e\uff32\uff0e\uff22\uff4f\uff58\uff45\uff52\uff0c\uff33\uff4f\uff55\uff54\uff48\uff23\uff48\uff49\uff4e\uff41\uff49\uff4e\uff54\uff48\uff45\uff11\uff16\uff54\uff48\uff27\uff45\uff4e\uff54\uff55\uff52\uff59\uff09\u7b49\u4e66\u3002\u76f4\u5230\u4e00\u4e03\u4e5d\u516b\u5e74\uff0c\u9a6c\u5c14\u585e\u65af\u5728\u300a\u4eba\u53e3\u8bba\u7b2c\u4e00\u7bc7\u300b\u4e2d\u8fd8\u8bf4\u4e2d\u56fd\u662f\u5168\u4e16\u754c\u6700\u5bcc\u5eb6\u7684\u56fd\u5bb6\u3002\u4e07\u5386\u5e74\u95f4\u6765\u5230\u4e2d\u56fd\u7684\u5929\u4e3b\u6559\u6559\u58eb\u5229\u9a6c\u7aa6\u7b49\u4eba\u66f4\u76db\u8d5e\u4e2d\u56fd\u7684\u6587\u6cbb\u5236\u5ea6\uff0c\u8ba4\u4e3a\u4e3e\u4e16\u51fa\u65e0\u5176\u53f3\u3002\u53c2\u9605L.J.Gallagher,S.J.tr.,China in the Sixteenth Century.\uff08\u6ce8\uff1a\uff2c\uff0e\uff2a\uff0e\uff27\uff41\uff4c\uff4c\uff41\uff47\uff48\uff45\uff52\uff0c\uff33\uff0e\uff2a\uff0e\uff54\uff52\uff0e\uff0c\uff23\uff48\uff49\uff4e\uff41\uff49\uff4e\uff54\uff48\uff45\uff33\uff49\uff58\uff0d\uff54\uff45\uff45\uff4e\uff54\uff48\uff23\uff45\uff4e\uff54\uff55\uff52\uff59\uff0e\uff09 \u3000\u3000\u2463Wolfram Eberhard:A History of China,p.249.\uff08\u6ce8\uff1a\uff37\uff4f\uff4c\uff46\uff52\uff41\uff4d\uff25\uff42\uff45\uff52\uff48\uff41\uff52\uff44\uff1a\uff21\uff28\uff49\uff53\uff54\uff4f\uff52\uff59\uff4f\uff46\uff23\uff48\uff49\uff4e\uff41\uff0c\uff50\uff0e\uff12\uff14\uff19\uff0e\uff09 \u3000\u3000\u2464\u6731\u4e1c\u6da6\u300a\u5f20\u5c45\u6b63\u5927\u4f20\u300b\uff1a\u201c\u4ece\u660e\u592a\u7956\u5230\u795e\u5b97\u8fd9\u4e00\u4e2a\u8840\u8109\u91cc\uff0c\u5145\u6ee1\u504f\u6267\u548c\u9ad8\u50b2\u2026\u2026\u5230\u4e86\u795e\u5b97\uff0c\u53c8\u5728\u8fd9\u9ad8\u50b2\u7684\u8840\u6db2\u91cc\uff0c\u589e\u52a0\u65b0\u7684\u6210\u5206\u3002\u4ed6\u5e95\u6bcd\u4eb2\u662f\u5c71\u897f\u4e00\u4e2a\u5c0f\u519c\u5e95\u5973\u513f\u3002\u5c0f\u519c\u6709\u90a3\u4e00\u80a1\u8d2a\u5229\u52a1\u5f97\u7684\u6c14\u606f\uff0c\u5728\u4e00\u5347\u9ea6\u79cd\u4e0b\u571f\u4ee5\u540e\uff0c\u4ed6\u957f\u65e5\u5df4\u5df4\u5730\u5728\u90a3\u91cc\u8ba1\u7b97\u8981\u957f\u6210\u4e00\u659b\u3001\u4e00\u77f3\u3001\u53c8\u786c\u3001\u53c8\u597d\u7684\u5c0f\u9ea6\u3002\u6210\u65e5\u7684\u7cbe\u795e\uff0c\u96c6\u4e2d\u5728\u8fd9\u4e00\u70b9\u4e0a\u9762\u3002\u2026\u2026\u660e\u671d\u5e95\u7687\u5e1d\uff0c\u53ea\u6709\u795e\u5b97\u55dc\u5229\uff0c\u51fa\u4e8e\u5929\u6027\uff0c\u4e5f\u8bb8\u53ea\u53ef\u8fd9\u6837\u5730\u89e3\u91ca\u3002\u201d\uff08\u4e09\u4e00\u4e03\u9875\uff09\u4f46\u8bf4\u5c0f\u519c\u55dc\u5229\uff0c\u4f3c\u4e4e\u4e0d\u5927\u59a5\u5f53\u3002\u5c0f\u519c\u79cd\u9ea6\u800c\u76fc\u671b\u6536\u6210\uff0c\u65e2\u662f\u81ea\u7136\u800c\u5408\u7406\u7684\u671f\u5f85\uff0c\u53c8\u662f\u751f\u6d3b\u7684\u552f\u4e00\u8d44\u6599\uff0c\u4e0d\u80fd\u8bf4\u662f\u55dc\u5229\u3002 \u3000\u3000\u2465\u77ff\u7a0e\u7684\u7a0e\u7387\u662f\u80e1\u4e71\u6307\u5b9a\u7684\uff0c\u5728L.Carrington Goodrich,A Short History of the Chinese People\uff08\u6ce8\uff1a\uff2c\uff0e\uff23\uff41\uff52\uff52\uff49\uff4e\uff47\uff54\uff4f\uff4e\uff27\uff4f\uff4f\uff44\uff52\uff49\uff43\uff48\uff0c\uff21\uff33\uff48\uff4f\uff52\uff54\uff28\uff49\uff53\uff54\uff4f\uff52\uff59\uff4f\uff46\uff54\uff48\uff45\uff23\uff48\uff49\uff4e\uff45\uff53\uff45\uff30\uff45\uff4f\uff50\uff4c\uff45\uff09\u4e2d\uff0c\u8bf4\u4e07\u5386\u65f6\u7684\u77ff\u7a0e\u662f\u77ff\u4ea7\u4ef7\u503c\u7684\u767e\u5206\u4e4b\u56db\u5341\uff0c\u5373\u4f7f\u77ff\u573a\u5df2\u7ecf\u505c\u95ed\uff0c\u77ff\u4e3b\u6bcf\u5e74\u4ecd\u987b\u6309\u65e7\u7a0e\u7387\u7f34\u7a0e\u3002p.199.\uff08\u6ce8\uff1a\uff50\uff0e\uff11\uff19\uff19\uff0e\uff09 \u3000\u3000\u2466\u636e\u5f20\u5c45\u6b63\u594f\u758f\u300a\u770b\u8be6\u6237\u90e8\u8fdb\u5448\u63ed\u5e16\u758f\u300b\uff1a\u4e07\u5386\u4e94\u5e74\uff0c\u5c81\u5165\u56db\u767e\u4e09\u5341\u4e94\u4e07\u4e5d\u5343\u56db\u767e\u4f59\u4e24\uff0c\u5c81\u51fa\u4e09\u767e\u56db\u5341\u4e5d\u4e07\u56db\u5343\u4e8c\u767e\u4f59\u4e24\u3002 \u3000\u3000\u2467\u53f6\u5411\u9ad8\u594f\uff1a\u201c\u4e2d\u5916\u79bb\u5fc3\uff0c\u8f87\u6bc2\u8098\u814b\u95f4\u6028\u58f0\u6124\u76c8\uff0c\u7978\u673a\u4e0d\u6d4b\uff0c\u800c\u965b\u4e0b\u52a1\u4e0e\u81e3\u4e0b\u9694\u7edd\u3002\u5e37\u5e44\u4e0d\u5f97\u5173\u5176\u5fe0\uff0c\u516d\u66f9\u4e0d\u5f97\u4e3e\u5176\u804c\u3002\u4e3e\u5929\u4e0b\u65e0\u4e00\u53ef\u4fe1\u4e4b\u4eba\uff0c\u800c\u81ea\u4ee5\u4e3a\u795e\u660e\u4e4b\u5999\u7528\u3002\u81e3\u6050\u81ea\u53e4\u5723\u5e1d\u660e\u738b\uff0c\u65e0\u6b64\u6cd5\u4e5f\u3002\u201d \u3000\u3000\u2468\u4e8c\u5341\u4e03\u5e74\uff0c\u540f\u90e8\u4f8d\u90ce\u51af\u7426\u594f\uff1a\u201c\u81ea\u77ff\u7a0e\u4f7f\u51fa\uff0c\u6c11\u82e6\u66f4\u751a\u3002\u52a0\u4ee5\u6c34\u65f1\u8757\u707e\uff0c\u6d41\u79bb\u8f7d\u9053\uff0c\u757f\u8f85\u8fd1\u5730\uff0c\u76d7\u8d3c\u516c\u884c\uff0c\u6b64\u975e\u7ec6\u6545\u4e5f\u3002\u4e2d\u4f7f\u8854\u547d\uff0c\u6240\u968f\u5978\u5f92\u5343\u767e\u2026\u2026\u9042\u4eca\u72e1\u733e\u4e4b\u5f92\uff0c\u64cd\u751f\u6b7b\u4e4b\u67c4\u2026\u2026\u4e94\u65e5\u4e4b\u5185\uff0c\u641c\u62ec\u516c\u79c1\u94f6\u5df2\u4e8c\u767e\u4e07\u3002\u5978\u5185\u751f\u5978\uff0c\u4f8b\u5916\u521b\u4f8b\uff0c\u4e0d\u81f3\u6c11\u56f0\u8d22\u6b9a\uff0c\u6fc0\u6210\u5927\u4e71\u4e0d\u6b62\u3002\u4f0f\u671b\u6025\u56fe\u4fee\u5f2d\uff0c\u65e0\u4ee4\u8d64\u5b50\u7ed3\u6028\uff0c\u9752\u53f2\u8d3b\u8ba5\u3002\u201d \u3000\u3000\u247d\u5de5\u79d1\u7ed9\u4e8b\u4e2d\u738b\u5fb7\u5b8c\u594f\uff1a\u201c\u4ee4\u51fa\u67d9\u4e2d\u4e4b\u864e\u5155\u4ee5\u541e\u990d\u7fa4\u9ece\uff0c\u9038\u5708\u5185\u4e4b\u8c7a\u72fc\u4ee5\u640f\u566c\u767e\u59d3\uff0c\u6028\u6124\u65e0\u5904\u5f97\u4f38\uff0c\u90c1\u7ed3\u65e0\u65f6\u53ef\u89e3\u3002\u201d \u3000\u3000\u247e\u51e4\u9633\u5de1\u629a\u674e\u4e09\u624d\u594f\uff1a\u201c\u965b\u4e0b\u7231\u73e0\u7389\uff0c\u6c11\u4ea6\u6155\u6e29\u9971\uff0c\u965b\u4e0b\u7231\u5b50\u5b59\uff0c\u6c11\u4ea6\u604b\u59bb\u5b65\u3002\u5948\u4f55\u5d07\u805a\u8d22\u8d3f\uff0c\u800c\u4f7f\u5c0f\u6c11\u65e0\u671d\u5915\u4e4b\u5b89\uff1f\u201d\u53c8\u8a00\uff1a\u201c\u8fd1\u65e5\u594f\u7ae0\uff0c\u51e1\u53ca\u77ff\u7a0e\uff0c\u6089\u7f6e\u4e0d\u7701\u3002\u6b64\u5b97\u793e\u5b58\u4ea1\u6240\u5173\uff0c\u4e00\u65e6\u4f17\u53db\u571f\u5d29\uff0c\u5c0f\u6c11\u7686\u4e3a\u654c\u56fd\uff0c\u965b\u4e0b\u5373\u9ec4\u91d1\u76c8\u7bb1\uff0c\u660e\u73e0\u586b\u5c4b\uff0c\u8c01\u4e3a\u5b88\u4e4b\uff1f\u201d \u3000\u3000\u247f\u7ed9\u4e8b\u4e2d\u7530\u5927\u76ca\u594f\uff1a\u201c\u5185\u81e3\u52a1\u4e3a\u52ab\u593a\u4ee5\u5e94\u4e0a\u6c42\uff0c\u77ff\u4e0d\u5fc5\u7a74\u800c\u7a0e\u4e0d\u5fc5\u5546\uff0c\u6c11\u95f4\u4e18\u9647\u9621\u964c\u7686\u77ff\u4e5f\uff0c\u5b98\u540f\u519c\u5de5\u7686\u5165\u7a0e\u4e4b\u4eba\u4e5f\uff0c\u516c\u79c1\u9a9a\u7136\uff0c\u8102\u818f\u6b9a\u7aed\uff0c\u5411\u6240\u8c13\u519b\u56fd\u6b63\u7528\uff0c\u53cd\u81f4\u7f3a\u635f\u3002\u2026\u2026\u56db\u6d77\u4e4b\u4eba\u65b9\u53cd\u5507\u5207\u9f7f\uff0c\u800c\u5180\u4ee5\u8ba1\u667a\u7518\u8a00\u63a9\u5929\u4e0b\u8033\u76ee\uff0c\u5176\u53ef\u5f97\u4e4e\uff1f\u965b\u4e0b\u77dc\u594b\u81ea\u8d24\uff0c\u6c89\u8ff7\u4e0d\u8fd4\uff0c\u4ee5\u8c6a\u515a\u5978\u5f01\u4e3a\u8179\u5fc3\uff0c\u4ee5\u91d1\u94b1\u73e0\u7389\u4e3a\u547d\u8109\u2026\u2026\u5373\u4ee4\u9022\u5e72\u5256\u5fc3\uff0c\u768b\u5914\u8fdb\u8c0f\uff0c\u4ea6\u5b89\u80fd\u89e3\u5176\u60d1\u54c9\uff1f\u201d\u53c8\u8a00\uff1a\u201c\u965b\u4e0b\u9a71\u7387\u72fc\u864e\uff0c\u98de\u800c\u98df\u4eba\u2026\u2026\u592b\u5929\u4e0b\u81f3\u8d35\u800c\u91d1\u7389\u73e0\u5b9d\u81f3\u8d31\u4e5f\u3002\u79ef\u91d1\u7389\u73e0\u5b9d\u82e5\u6cf0\u5c71\uff0c\u4e0d\u53ef\u5e02\u5929\u4e0b\u5c3a\u5bf8\u5730\uff0c\u800c\u5931\u5929\u4e0b\uff0c\u53c8\u4f55\u7528\u91d1\u7389\u73e0\u5b9d\u54c9\uff1f\u201d \u3000\u3000\u2480\u540f\u90e8\u5c1a\u4e66\u674e\u6234\u594f\uff1a\u201c\u4eca\u4e09\u8f85\u55f7\u55f7\uff0c\u6c11\u4e0d\u804a\u751f\uff1b\u8349\u6728\u65e2\u5c3d\uff0c\u5265\u53ca\u6811\u76ae\uff1b\u591c\u7a83\u6210\u7fa4\uff0c\u517c\u4ee5\u663c\u52ab\uff1b\u9053\u747e\uff08\u6ce8\uff1a\u5e94\u4e3a\u201c\u6ba3\u201d\u5b57\uff09\u76f8\u671b\uff0c\u6751\u7a7a\u65e0\u70df\u3002\u2026\u2026\u4f7f\u767e\u59d3\u5750\u800c\u5f85\u6b7b\uff0c\u66f4\u4f55\u5fcd\u8a00\uff1f\u2026\u2026\u6b64\u65f6\u8d4b\u7a0e\u4e4b\u5f79\uff0c\u6bd4\u4e8c\u5341\u5e74\u524d\u4e0d\u557b\u500d\u77e3\u2026\u2026\u6307\u5176\u5c4b\u800c\u631f\u4e4b\u66f0\u2018\u5f7c\u6709\u77ff\u2019\uff0c\u5219\u5bb6\u7acb\u7834\u77e3\uff1b\u2018\u5f7c\u6f0f\u7a0e\u2019\uff0c\u5219\u6a50\u7acb\u503e\u77e3\u3002\u4ee5\u65e0\u53ef\u67e5\u7a3d\u4e4b\u6570\uff0c\u7528\u65e0\u6240\u987e\u754f\u4e4b\u4eba\uff0c\u884c\u65e0\u5929\u7406\u738b\u6cd5\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u2481\u6237\u90e8\u5c1a\u4e66\u8d75\u4e16\u537f\u4e0a\u758f\u8a00\uff1a\u201c\u5929\u5b50\u4e4b\u4ee4\uff0c\u4fe1\u5982\u56db\u65f6\u3002\u4e09\u8f7d\u524d\u5c1d\u66f0\uff1a\u2018\u6715\u5fc3\u4ec1\u7231\uff0c\u81ea\u6709\u505c\u6b62\u4e4b\u65f6\u3002\u2019\u4eca\u5e74\u590d\u4e00\u5e74\uff0c\u66f4\u5f85\u4f55\u65e5\uff1f\u5929\u5b50\u6709\u620f\u8a00\uff0c\u738b\u547d\u59d4\u8349\u83bd\u3002\u201d \u3000\u3000\u2482\u4e07\u5386\u56db\u5341\u56db\u5e74\uff0c\u7ed9\u4e8b\u4e2d\u718a\u660e\u9047\u758f\uff1a\u201c\u5185\u5e93\u592a\u5b9e\uff0c\u5916\u5e93\u592a\u865a\u3002\u201d\uff08\u4ee5\u4e0a\u2467\u81f3\u2482\u5404\u594f\u758f\u4e2d\u7684\u6587\u5b57\u6563\u89c1\u300a\u660e\u53f2\u300b\u6216\u300a\u660e\u901a\u9274\u300b\u3002\uff09 \u3000\u3000\u4e8c \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u6ee1\u6e05\u5f00\u59cb\u5d1b\u8d77\u3002\u4e07\u5386\u56db\u5341\u4e94\u5e74\uff0c\u52aa\u5c14\u54c8\u8d64\u4ee5\u4e03\u5927\u6068\u544a\u5929\uff0c\u53d1\u5175\u653b\u660e\uff0c\u6b21\u5e74\u653b\u5360\u8fbd\u4e1c\u91cd\u9547\u629a\u987a\u3002\u660e\u5175\u5927\u8d25\uff0c\u603b\u5175\u5b98\u5f20\u627f\u836b\u6218\u6b7b\uff0c\u4e07\u4f59\u5175\u5c06\u5168\u519b\u8986\u6ca1\uff0c\u4e3e\u671d\u9707\u9a87\u3002 \u3000\u3000\u56db\u5341\u4e03\u5e74\uff0c\u8fbd\u4e1c\u7ecf\u7565\u6768\u9550\u7387\u660e\u519b\u5341\u516b\u4e07\uff0c\u53f6\u8d6b\uff08\u6ee1\u6e05\u7684\u4e16\u4ec7\uff09\u5175\u4e8c\u4e07\uff0c\u671d\u9c9c\uff08\u4e2d\u56fd\u7684\u5c5e\u56fd\uff09\u5175\u4e8c\u4e07\uff0c\u5175\u5206\u56db\u8def\uff0c\u5927\u4e3e\u653b\u6e05\u3002\u6e05\u5175\u516b\u65d7\u5175\u7ea6\u516d\u4e07\u4eba\uff0c\u96c6\u4e2d\u5175\u529b\uff0c\u4e13\u653b\u897f\u8def\u4e00\u8def\u3002\u897f\u8def\u519b\u7684\u603b\u5175\u5b98\u675c\u677e\u662f\u660e\u519b\u7684\u52c7\u5c06\uff0c\u5e73\u65f6\u6700\u559c\u6b22\u505a\u7684\u4e8b\uff0c\u5c31\u662f\u8131\u53bb\u8863\u886b\uff0c\u5c06\u6ee1\u8eab\u7684\u7d2f\u7d2f\u5200\u67aa\u7622\u75d5\u5411\u4eba\u5938\u793a\u3002\u51fa\u5175\u4e4b\u65f6\uff0c\u4ed6\u8131\u53bb\u4e0a\u8eab\u8863\u886b\uff0c\u5728\u57ce\u4e2d\u6e38\u8857\uff0c\u767e\u59d3\u9f13\u638c\u559d\u5f69\u3002 \u3000\u3000\u897f\u8def\u8fd9\u4e00\u4ed7\uff0c\u79f0\u4e3a\u201c\u8428\u5c14\u6d52\u4e4b\u5f79\u201d\uff0c\u660e\u519b\u6709\u706b\u5668\u94a2\u70ae\uff0c\u519b\u706b\u9510\u5229\u5f97\u591a\u3002\u4f46\u675c\u677e\u6709\u52c7\u65e0\u8c0b\uff0c\u4ed6\u662f\u7edf\u5175\u516d\u4e07\u7684\u5175\u56e2\u53f8\u4ee4\uff0c\u5374\u6253\u4e86\u8d64\u818a\uff0c\u9732\u51fa\u5168\u8eab\u4f24\u75a4\uff0c\u4e00\u9a6c\u5f53\u5148\u7684\u51b2\u950b\u3002\u5927\u6982\u4ed6\u662f\u300a\u4e09\u56fd\u6f14\u4e49\u300b\u7684\u8bfb\u8005\uff0c\u5f88\u7fa1\u6155\u201c\u864e\u75f4\u201d\u8bb8\u891a\u7684\u52c7\u731b\u3002\u5728\u201c\u8bb8\u891a\u88f8\u8863\u6597\u9a6c\u8d85\u201d\u8fd9\u56de\u4e66\u4e2d\uff0c\u63cf\u5199\u8bb8\u891a\u201c\u5378\u4e86\u76d4\u7532\uff0c\u6d51\u8eab\u7b4b\u7a81\uff0c\u8d64\u4f53\u63d0\u5200\uff0c\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u6765\u4e0e\u9a6c\u8d85\u51b3\u6218\u3002\u201d\u679c\u7136\u5a01\u98ce\u5f97\u7d27\u3002\u4f46\u4e0d\u77e5\u4ed6\u8bb0\u4e0d\u8bb0\u5f97\u8bb8\u891a\u8fd9\u573a\u72e0\u6597\uff0c\u7ed3\u679c\u662f\u201c\u64cd\u5175\u5927\u4e71\uff0c\u8bb8\u891a\u80cc\u4e2d\u4e24\u7bad\u201d\uff1f\u6709\u8da3\u7684\u662f\uff0c\u5c0f\u8bf4\u7684\u8bc4\u6ce8\u8005\u8bc4\u9053\uff1a\u201c\u8c01\u53eb\u6c5d\u8d64\u818a\uff1f\u201d \u3000\u3000\u660e\u6e05\u4e24\u519b\u5217\u9635\u4ea4\u950b\u4e4b\u65f6\uff0c\u7a81\u7136\u5929\u660f\u5730\u6697\uff0c\u6570\u5c3a\u4e4b\u5916\u5c31\u751a\u4e48\u4e5f\u77a7\u4e0d\u89c1\u4e86\u3002\u675c\u677e\u53c8\u72af\u4e86\u4e00\u4e2a\u5927\u9519\u8bef\uff0c\u4e0b\u4ee4\u4f17\u519b\u70b9\u8d77\u706b\u628a\u3002\u8fd9\u4e00\u6765\uff0c\u660e\u519b\u5728\u5149\u800c\u6e05\u519b\u5728\u6697\uff0c\u660e\u519b\u7167\u4eae\u4e86\u81ea\u8eab\uff0c\u6210\u4e3a\u6e05\u5175\u7684\u7bad\u9776\u5b50\u3002\u52aa\u5c14\u54c8\u8d64\u7edf\u5175\u516d\u65d7\u4f5c\u4e3b\u529b\u731b\u653b\uff0c\u4ed6\u513f\u5b50\u4ee3\u5584\u548c\u7687\u592a\u6781\u5404\u7edf\u4e00\u65d7\u5728\u53f3\u7ffc\u4fa7\u653b\u3002\u7ed3\u679c\u675c\u677e\u7684\u906d\u9047\u6bd4\u8bb8\u891a\u60e8\u5f97\u591a\uff0c\u8eab\u4e2d\u5341\u516b\u7bad\u800c\u6b7b\uff0c\u5f53\u771f\u662f\u201c\u8c01\u53eb\u6c5d\u8d64\u818a\uff1f\u201d\u603b\u5175\u5b98\u9635\u4ea1\uff0c\u660e\u519b\u5927\u4e71\uff0c\u516d\u4e07\u5175\u5168\u519b\u8986\u6ca1\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u91c7\u53d6\u4e86\u201c\u96c6\u4e2d\u4e3b\u529b\uff0c\u5404\u4e2a\u51fb\u7834\u201d\u7684\u6b63\u786e\u6218\u7565\uff0c\u4e00\u4e2a\u6218\u5f79\u3001\u4e00\u4e2a\u6218\u5f79\u7684\u5206\u5f00\u6765\u6253\u3002\u660e\u519b\u5317\u8def\u603b\u5175\u5b98\u9a6c\u6797\u3001\u4e1c\u8def\u603b\u5175\u5b98\u5218\u7d4d\u90fd\u5927\u8d25\u9635\u4ea1\uff0c\u671d\u9c9c\u90fd\u5143\u5e05\u7387\u4f17\u964d\u6e05\u3002 \u3000\u3000\u5218\u7d4d\u662f\u5f53\u65f6\u660e\u671d\u7b2c\u4e00\u5927\u9a81\u5c06\uff0c\u6253\u8fc7\u7f05\u7538\u3001\u502d\u5bc7\uff0c\u66fe\u7387\u5175\u63f4\u52a9\u671d\u9c9c\u5bf9\u6297\u65e5\u672c\u5165\u4fb5\uff0c\u5927\u5c0f\u6570\u767e\u6218\uff0c\u5a01\u540d\u9707\u6d77\u5185\u3002\u4ed6\u6240\u7528\u7684\u9554\u94c1\u5200\u91cd\u4e00\u767e\u4e8c\u5341\u65a4\uff0c\u9a6c\u4e0a\u8f6e\u8f6c\u5982\u98de\uff0c\u5929\u4e0b\u79f0\u4e3a\u201c\u5218\u5927\u5200\u201d\u3002\u4ed6\u7684\u5927\u5200\u6bd4\u5173\u7fbd\u7684\u516b\u5341\u4e00\u65a4\u9752\u9f99\u5043\u6708\u5200\u8fd8\u91cd\u4e86\u4e09\u5341\u4e5d\u65a4\u3002\u636e\u8bf4\u4ed6\u80fd\u5355\u624b\u4e3e\u8d77\u4e00\u5f20\u6446\u6ee1\u4e86\u9152\u83dc\u7897\u7b77\u7684\u67cf\u6728\u516b\u4ed9\u684c\uff0c\u5728\u5927\u5385\u4e2d\u7ed5\u884c\u4e09\u5708\u3002\u8fde\u675c\u677e\u3001\u5218\u7d4d\u8fd9\u6837\u7684\u9a81\u5c06\u90fd\u88ab\u6e05\u5175\u6253\u6b7b\uff0c\u660e\u519b\u5c06\u58eb\u5fc3\u7406\u4e0a\u53d7\u5230\u7684\u6253\u51fb\u81ea\u7136\u6c89\u91cd\u4e4b\u6781\uff0c\u63d0\u5230\u6ee1\u6e05\u201c\u8fab\u5b50\u5175\u201d\u65f6\u4e0d\u514d\u8c08\u864e\u8272\u53d8\u3002 \u3000\u3000\u8fd9\u573a\u5927\u6218\u662f\u660e\u6e05\u4e24\u671d\u5174\u4ea1\u7684\u5927\u5173\u952e\uff0c\u800c\u80dc\u8d25\u7684\u5173\u952e\u5728\u4e8e\uff1a\u7b2c\u4e00\u3001\u660e\u65b9\u7684\u4e3b\u5e05\u6768\u9550\u662f\u6587\u5b98\uff0c\u5b8c\u5168\u4e0d\u61c2\u519b\u4e8b\u3002\u7b2c\u4e8c\u3001\u660e\u671d\u653f\u4e8b\u8150\u8d25\u5df2\u8fbe\u6781\u70b9\uff0c\u8fde\u5e26\u7684\u519b\u653f\u4e5f\u5e9f\u5f1b\u4e0d\u582a\uff0c\u519b\u961f\u4e45\u65e0\u8bad\u7ec3\uff0c\u5b8c\u5168\u6ca1\u6709\u5fc5\u8981\u7684\u519b\u4e8b\u51c6\u5907\u2460\u3002 \u3000\u3000\u6768\u9550\u5168\u519b\u8986\u6ca1\uff0c\u671d\u5ef7\u6d3e\u718a\u5ef7\u5f3c\u53bb\u5b88\u8fbd\u4e1c\u3002 \u3000\u3000\u4e07\u5386\u56db\u5341\u516d\u5e74\u4e03\u6708\uff0c\u718a\u5ef7\u5f3c\u521a\u51fa\u5c71\u6d77\u5173\uff0c\u94c1\u5cad\u5df2\u7ecf\u5931\u9677\uff0c\u6c88\u9633\u53ca\u9644\u8fd1\u8bf8\u57ce\u5821\u7684\u519b\u6c11\u7eb7\u7eb7\u9003\u7a9c\u3002\u718a\u5ef7\u5f3c\u517c\u7a0b\u8fdb\u5165\u8fbd\u9633\u3002\u7ecf\u8fc7\u795e\u5b97\u6570\u5341\u5e74\u6765\u7684\u767e\u4e8b\u4e0d\u7406\uff0c\u519b\u961f\u7eaa\u5f8b\u8361\u7136\uff0c\u58eb\u65e0\u6597\u5fd7\uff0c\u9a91\u5175\u6545\u610f\u5c06\u9a6c\u5339\u5f04\u6b7b\uff0c\u4ee5\u907f\u514d\u51fa\u6218\uff0c\u53ea\u8981\u542c\u5230\u654c\u519b\u6765\u653b\uff0c\u6ee1\u8425\u5175\u5352\u5c31\u4e00\u54c4\u800c\u6563\u3002\u718a\u5ef7\u5f3c\u9762\u4e34\u7684\u5c40\u9762\u5b9e\u5728\u56f0\u96be\u5df2\u6781\u2461\u3002\u519b\u9977\u672c\u5df2\u5341\u5206\u5fae\u8584\uff0c\u4f46\u7687\u5e1d\u8fd8\u662f\u62da\u547d\u62d6\u6b20\uff0c\u4e0d\u80af\u53d1\u9977\u2462\u3002 \u3000\u3000\u795e\u5b97\u89c1\u8fb9\u5173\u4e0a\u8ffd\u9977\u8d8a\u8feb\u8d8a\u6025\uff0c\u77e5\u9053\u6328\u4e0d\u4e0b\u53bb\u4e86\uff0c\u53ef\u662f\u59cb\u7ec8\u4e0d\u80af\u638f\u81ea\u5df1\u8170\u5305\uff0c\u7ed3\u679c\u60f3\u51fa\u4e86\u4e00\u4e2a\u5bf9\u7b56\uff1a\u518d\u52a0\u7530\u8d4b\u767e\u5206\u4e4b\u4e8c\u3002\u8fde\u540c\u4ee5\u524d\u4e24\u6b21\uff0c\u5df2\u5171\u52a0\u767e\u5206\u4e4b\u4e5d\uff0c\u7136\u800c\u5411\u767e\u59d3\u591a\u5f81\u7684\u7530\u8d4b\uff0c\u672a\u5fc5\u5c31\u62ff\u6765\u53d1\u519b\u9977\uff0c\u7687\u5e1d\u7684\u57fa\u672c\u5174\u8da3\u662f\u5c06\u94f6\u5b50\u85cf\u4e4b\u4e8e\u5185\u5e93\u3002 \u3000\u3000\u8fb9\u754c\u4e0a\u7684\u8b66\u62a5\u4e0d\u65ad\u4f20\u6765\uff0c\u7fa4\u81e3\u65e5\u65e5\u8bf7\u6c42\u7687\u5e1d\u4e34\u671d\uff0c\u4f1a\u5546\u6218\u5b88\u65b9\u7565\u3002\u7687\u5e1d\u603b\u662f\u6d3e\u592a\u76d1\u51fa\u6765\u4f20\u8c15\uff1a\u201c\u7687\u4e0a\u6709\u75c5\u3002\u201d\u540f\u90e8\u5c1a\u4e66\u8d75\u7115\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u4e0a\u594f\u7ae0\u8bf4\uff1a\u201c\u5c06\u6765\u654c\u4eba\u94c1\u9a91\u6765\u5230\u5317\u4eac\u57ce\u5916\uff0c\u965b\u4e0b\u4e5f\u80fd\u5728\u6df1\u5bab\u4e2d\u63a8\u8bf4\u6709\u75c5\uff0c\u5c31\u6b64\u4ee4\u654c\u4eba\u9000\u5175\u5417\uff1f\u201d\u2463\u795e\u5b97\u770b\u4e86\u8fd9\u9053\u8bbd\u523a\u8f9b\u8fa3\u3001\u5b9e\u5df2\u8fd1\u4e4e\u8c29\u9a82\u7684\u594f\u7ae0\uff0c\u53ea\u662f\u5fc3\u4e2d\u6000\u6068\uff0c\u5374\u8bf4\u751a\u4e48\u4e5f\u4e0d\u80af\u53ec\u5f00\u4e00\u6b21\u56fd\u9632\u4f1a\u8bae\u3002 \u3000\u3000\u795e\u5b97\u641c\u62ec\u7684\u94f6\u952d\u5806\u79ef\u5728\u5185\u5e93\uff0c\u5e74\u6df1\u6708\u4e45\uff0c\u5927\u8d77\u6c27\u5316\u4f5c\u7528\uff0c\u6709\u7684\u9ed1\u5f97\u50cf\u6f06\uff0c\u6709\u7684\u8106\u8150\u5982\u6ce5\u571f\u2464\uff0c\u5c31\u662f\u4e0d\u80af\u62ff\u51fa\u6765\u7528\u3002\u4f46\u4ed6\u7ec8\u4e8e\u6b7b\u4e86\uff0c\u5343\u5343\u4e07\u4e07\u7684\u94f6\u4e24\uff0c\u4e00\u4e24\u4e5f\u5e26\u4e0d\u53bb\u2465\u3002 \u3000\u3000\u795e\u5b97\uff0c\u795e\u5b97\uff0c\u771f\u662f\u201c\u795e\u201d\u5f97\u5f88\uff0c\u795e\u7ecf\u5f97\u5f88\uff01 \u3000\u3000\u2460\u5d07\u796f\u65f6\u4efb\u5927\u5b66\u58eb\u7684\u5f90\u5149\u542f\u5728\u300a\u5e96\u8a00\u300b\u4e2d\u8bf4\uff1a\u6ee1\u6d32\u4eba\u65e7\u90fd\u5317\u95e8\uff0c\u5c45\u4f4f\u7684\u5927\u90fd\u662f\u94c1\u5320\uff0c\u5ef6\u88a4\u6570\u91cc\u3002\u5728\u5f53\u65f6\u90a3\u4fbf\u662f\u4e00\u4e2a\u89c4\u6a21\u5e9e\u5927\u7684\u5175\u5de5\u5382\u7ec4\u5408\u4e86\u3002\u56e0\u6b64\u6ee1\u6d32\u5175\u7684\u76d4\u7532\u7cbe\u826f\uff0c\u5934\u76d4\u3001\u9762\u5177\u3001\u62a4\u81c2\u3001\u62a4\u624b\uff0c\u90fd\u662f\u7cbe\u94c1\u6240\u5236\uff0c\u9a6c\u5339\u7684\u8981\u5bb3\u5904\u4e5f\u6709\u7cbe\u94c1\u62a4\u5177\u3002\u4f46\u660e\u5175\u76d4\u7532\u5374\u5341\u5206\u7b80\u964b\uff0c\u9664\u4e86\u80f8\u80cc\u6709\u7532\u4e4b\u5916\uff0c\u5176\u4f59\u90e8\u5206\u5168\u65e0\u4fdd\u62a4\u3002\u6ee1\u6d32\u5175\u51b2\u5230\u8fd1\u5904\uff0c\u4e13\u5c04\u660e\u5175\u7684\u8138\u53ca\u80c1\uff0c\u4e2d\u7bad\u5fc5\u6b7b\u3002\u53c8\u636e\u5f53\u65f6\u660e\u4eba\u7a0b\u4ee4\u540d\u8bf4\uff0c\u52aa\u5c14\u54c8\u8d64\u6240\u5c45\u7684\u90fd\u57ce\u201c\u5317\u95e8\u5916\u5219\u94c1\u5320\u5c45\u4e4b\uff0c\u4e13\u6cbb\u94e0\u7532\uff1b\u5357\u95e8\u5916\u5219\u5f13\u4eba\u3001\u7bad\u4eba\u5c45\u4e4b\uff0c\u4e13\u9020\u5f27\u77e2\u3002\u201d \u3000\u3000\u2461\u718a\u5ef7\u5f3c\u4e8e\u516b\u6708\u4e8c\u5341\u4e5d\u65e5\u4e0a\u4e66\u671d\u5ef7\uff0c\u9648\u8ff0\u8fbd\u4e1c\u660e\u519b\u60c5\u51b5\uff1a\u201c\u6b8b\u5175\u2026\u2026\u8eab\u65e0\u7247\u7532\uff0c\u624b\u65e0\u5bf8\u68b0\uff0c\u968f\u8425\u7cdc\u9977\uff0c\u88c5\u6b7b\u626e\u6d3b\uff0c\u4e0d\u80af\u51fa\u6218\u2026\u2026\u70b9\u518c\u6709\u540d\uff0c\u53ca\u6d3e\u5de5\u5f79\u800c\u5ffd\u53bb\u5176\u534a\uff1b\u9886\u9977\u6709\u540d\uff0c\u53ca\u95fb\u8b66\u544a\u800c\u53c8\u53bb\u5176\u534a\u2026\u2026\u5c06\u9886\u7686\u5c61\u6b21\u5f81\u6218\u5b58\u5269\u3001\u53ca\u65b0\u8d25\u4e45\u5e9f\u4e4b\u4eba\uff0c\u4e00\u95fb\u8b66\u62a5\uff0c\u65e0\u4e0d\u5fc3\u60ca\u80c6\u4e27\u8005\u2026\u2026\u89c1\u5728\u9a6c\u4e00\u4e07\u4f59\u5339\uff0c\u591a\u534a\u7626\u635f\uff0c\u7387\u7531\u519b\u58eb\u6545\u610f\u65ad\u7edd\u8349\u6599\uff0c\u8bbe\u6cd5\u81f4\u6b7b\uff0c\u5907\u5145\u6b65\u5175\uff0c\u4ee5\u514d\u51fa\u6218\uff0c\u751a\u6709\u65e0\u6545\u7528\u5200\u523a\u6b7b\u8005\u3002\u2026\u2026\u575a\u7532\u5229\u5203\uff0c\u957f\u67aa\u706b\u5668\uff0c\u4e27\u5931\u4ff1\u5c3d\u3002\u4eca\u519b\u58eb\u6240\u6301\u5f13\u7686\u65ad\u80cc\u65ad\u5f26\uff0c\u6240\u6301\u7bad\u7686\u65e0\u7fbd\u65e0\u955e\uff0c\u5200\u7686\u7f3a\u949d\uff0c\u67aa\u7686\u987d\u79c3\u3002\u751a\u6709\u5168\u65e0\u4e00\u7269\u800c\u501f\u4ed6\u4eba\u4ee5\u5e94\u70b9\u8005\u3002\u53c8\u7686\u7a7a\u5934\u8d64\u4f53\uff0c\u65e0\u4e00\u76d4\u7532\u906e\u853d\u3002\u2026\u2026\u95fb\u98ce\u800c\u9003\uff0c\u671b\u9635\u800c\u9003\uff0c\u60e7\u6218\u800c\u9003\u3002\u9877\u95fb\u5317\u5173\u4fe1\u606f\uff0c\u5404\u8425\u9003\u8005\u65e5\u4ee5\u5343\u767e\u8ba1\u3002\u5982\u9003\u6b62\u4e00\u4e8c\u8425\u6216\u6570\u5341\u767e\u4eba\uff0c\u81e3\u72b9\u53ef\u4ee5\u91cd\u6cd5\u7ef3\u4e4b\u3002\u4eca\u4e94\u516d\u4e07\u4eba\uff0c\u4eba\u4eba\u8981\u9003\u3002\u867d\u6709\u5b59\u5434\u519b\u4ee4\uff0c\u4ea6\u96be\u7981\u6b62\u3002\u201d \u3000\u3000\u2462\u4e07\u5386\u56db\u5341\u516b\u5e74\u4e09\u6708\uff0c\u718a\u5ef7\u5f3c\u4e0a\u594f\uff1a\u201c\u56db\u5341\u4e03\u5e74\u5341\u4e8c\uff08\u7591\u4e3a\u201c\u4e00\u201d\u5b57\uff09\u6708\u8d74\u6237\u90e8\uff0c\u9886\u9977\u4e8c\u5341\u4e07\u4e24\uff0c\u5341\u4e8c\u6708\u9886\u9977\u5341\u4e07\u4e24\uff0c\u56db\u5341\u516b\u5e74\u6b63\u6708\u9886\u9977\u5341\u4e94\u4e07\u4e24\uff0c\u4ff1\u65e0\u53d1\u7ed9\u2026\u2026\u5c82\u519b\u5230\u4eca\u65e5\u5c1a\u4e0d\u997f\uff0c\u9a6c\u5230\u4eca\u65e5\u5c1a\u4e0d\u7626\u4e0d\u6b7b\uff0c\u800c\u8fb9\u4e8b\u5230\u4eca\u65e5\u5c1a\u4e0b\u6025\u8036\uff1f\u519b\u5175\u65e0\u7cae\uff0c\u5982\u4f55\u4e0d\u5356\u8884\u88e4\u6742\u7269\uff1f\u5982\u4f55\u4e0d\u593a\u6c11\u95f4\u7cae\u7a96\uff1f\u5982\u4f55\u4e0d\u593a\u9a6c\u6599\u517b\u81ea\u5df1\u6027\u547d\uff0c\u9a6c\u5339\u5982\u4f55\u4e0d\u7626\u4e0d\u6b7b\uff1f\u800c\u6237\u90e8\u72b9\u6f20\u7136\u4e0d\u4e00\u52a8\u5ff5\u3002\u201d\u4ed6\u8bf4\u6237\u90e8\u72b9\u6f20\u7136\u4e0d\u4e00\u52a8\u5ff5\uff0c\u662f\u5ba2\u6c14\u7684\u8bf4\u6cd5\uff0c\u6f20\u7136\u4e0d\u52a8\u4e00\u5ff5\u7684\uff0c\u5f53\u7136\u662f\u7687\u5e1d\u81ea\u5df1\u3002 \u3000\u3000\u2463\u201c\u4ed6\u65e5\u84df\u95e8\u8e42\u8e8f\uff0c\u94c1\u9a91\u4e34\u90ca\uff0c\u965b\u4e0b\u80fd\u9ad8\u62f1\u6df1\u5bab\uff0c\u79f0\u75be\u5374\u4e4b\u4e4e\uff1f\u201d \u3000\u3000\u2464\u6237\u79d1\u7ed9\u4e8b\u4e2d\u5b98\u5e94\u9707\u8a00\uff1a\u201c\u5185\u5e93\u5341\u4e07\u4e24\u5185\u4e94\u4e07\u4e5d\u5343\u4e24\uff0c\u6216\u9ed1\u5982\u6f06\uff0c\u6216\u8106\u5982\u571f\uff0c\u76d6\u4e3a\u4e0d\u7528\u673d\u8839\u4e4b\u8c61\u3002\u201d \u3000\u3000\u2465\u5927\u9646\u8003\u53e4\u5de5\u4f5c\u8005\u53d1\u6398\u5e1d\u7687\u575f\u5893\uff0c\u504f\u504f\u62e3\u4e2d\u4e86\u795e\u5b97\u7684\u201c\u5b9a\u9675\u201d\uff0c\u6539\u5efa\u4e3a\u535a\u7269\u9986\uff0c\u79f0\u4e3a\u201c\u5730\u4e0b\u5bab\u6bbf\u201d\u3002 \u3000\u3000\u4e09 \u3000\u3000\u795e\u5b97\u6b7b\u540e\uff0c\u513f\u5b50\u5149\u5b97\u53ea\u505a\u4e86\u4e00\u4e2a\u6708\u7687\u5e1d\u5c31\u56e0\u8bef\u670d\u836f\u7269\u800c\u6b7b\u3002\u5149\u5b97\u7684\u513f\u5b50\u6731\u7531\u6821\u63a5\u4f4d\uff0c\u5386\u53f2\u4e0a\u79f0\u4e3a\u71b9\u5b97\uff0c\u5e74\u53f7\u5929\u542f\u3002 \u3000\u3000\u5149\u5b97\u505a\u7687\u5e1d\u7684\u65f6\u95f4\u6781\u77ed\uff0c\u7559\u4e0b\u7684\u9ebb\u70e6\u5374\u6781\u5927\uff0c\u660e\u672b\u4e09\u5927\u6848\u6883\u51fb\u3001\u7ea2\u4e38\u3001\u79fb\u5bab\uff0c\u90fd\u548c\u4ed6\u7684\u7687\u4f4d\u53ca\u751f\u6b7b\u6709\u5173\u3002\u4f17\u5927\u81e3\u5206\u6210\u4e24\u6d3e\uff0c\u7eb7\u4e89\u4e0d\u5df2\u3002\u7eb7\u4e89\u7275\u6d89\u5230\u65c1\u7684\u4e00\u5207\u4e8b\u60c5\u4e0a\uff0c\u53ea\u8981\u662f\u5bf9\u65b9\u4e00\u6d3e\u4e4b\u4eba\u6240\u505a\u7684\u4e8b\uff0c\u4e0d\u8bba\u662f\u5bf9\u662f\u9519\uff0c\u603b\u662f\u62ff\u6765\u6fc0\u70c8\u653b\u51fb\u4e00\u756a\u3002 \u3000\u3000\u71b9\u5b97\u63a5\u4f4d\u65f6\u865a\u5c81\u5341\u516d\u5c81\uff0c\u5176\u5b9e\u4e0d\u6ee1\u5341\u4e94\u5c81\uff0c\u8fd8\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u4ed6\u5bf9\u4e73\u6bcd\u5ba2\u6c0f\u5f88\u4f9d\u604b\u3002\u8fd9\u4e2a\u5ba2\u6c0f\u5f88\u559c\u6b22\u5f04\u6743\uff0c\u5728\u5bab\u91cc\u548c\u592a\u76d1\u9b4f\u5fe0\u8d24\u6709\u70b9\u53e4\u602a\u7684\u6027\u5173\u7cfb\u3002\u5bab\u91cc\u592a\u76d1\u548c\u5bab\u5973\u5f88\u591a\uff0c\u4e3a\u4e86\u5bc2\u5bde\u800c\u4e92\u76f8\u5b89\u6170\uff0c\u5927\u5bb6\u79c1\u4e0b\u604b\u7231\uff0c\u7136\u800c\u592a\u76d1\u662f\u9609\u5272\u4e86\u6027\u673a\u80fd\u7684\u9634\u9633\u4eba\uff0c\u6240\u4ee5\u8fd9\u65e2\u4e0d\u662f\u5f02\u6027\u604b\u7231\uff0c\u53c8\u4e0d\u662f\u540c\u6027\u604b\uff0c\u5f53\u65f6\u79f0\u4e3a\u201c\u5bf9\u98df\u201d\uff0c\u610f\u601d\u8bf4\u4e0d\u80fd\u540c\u5e8a\uff0c\u53ea\u4e0d\u8fc7\u76f8\u5bf9\u5403\u996d\uff0c\u4e92\u6170\u5b64\u5bc2\u800c\u5df2\u3002\u9b4f\u5fe0\u8d24\u505a\u4e86\u5ba2\u6c0f\u7684\u5bf9\u98df\uff0c\u6e10\u6e10\u638c\u63e1\u4e86\u5927\u6743\u3002 \u3000\u3000\u71b9\u5b97\u662f\u4e2a\u5929\u751f\u7684\u6728\u5320\uff0c\u6700\u559c\u6b22\u505a\u7684\u4e8b\uff0c\u83ab\u8fc7\u4e8e\u952f\u6728\u3001\u5228\u6728\u3001\u6cb9\u6f06\u800c\u505a\u6728\u5de5\uff0c\u624b\u827a\u9ad8\u660e\u5f97\u5f88\u3002\u9b4f\u5fe0\u8d24\u603b\u662f\u4e58\u4ed6\u505a\u6728\u5de5\u505a\u5f97\u5168\u795e\u8d2f\u6ce8\u4e4b\u65f6\uff0c\u62ff\u91cd\u8981\u594f\u7ae0\u53bb\u8bf7\u4ed6\u6279\u9605\u3002\u71b9\u5b97\u600e\u80af\u653e\u4e0b\u5fc3\u7231\u7684\u6728\u5de5\u4e0d\u7406\uff1f\u628a\u624b\u4e00\u6325\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u6765\u6253\u6270\uff0c\u4f60\u77a7\u7740\u529e\u53bb\u5427\u3002\u201d\u4e8e\u662f\u9b4f\u5fe0\u8d24\u5c31\u53bb\u77a7\u7740\u529e\u4e86\uff0c\u8d8a\u6765\u8d8a\u65e0\u6cd5\u65e0\u5929\u3002 \u3000\u3000\u671d\u91cc\u81ea\u6709\u4e00\u6279\u8c04\u8c00\u65e0\u803b\u4e4b\u5f92\u53bb\u5949\u627f\u4ed6\uff0c\u5230\u540e\u6765\uff0c\u9b4f\u5fe0\u8d24\u6210\u4e86\u5b9e\u9645\u4e0a\u7684\u7687\u5e1d\u3002\u71b9\u5b97\u662f\u201c\u4e07\u5c81\u201d\uff0c\u6709\u4e9b\u5b98\u5458\u89c1\u4e86\u9b4f\u5fe0\u8d24\u53eb\u201c\u4e5d\u5343\u5c81\u201d\uff0c\u8868\u793a\u4ed6\u53ea\u6bd4\u7687\u5e1d\u5dee\u4e86\u4e00\u70b9\u513f\u3002\u5230\u540e\u6765\uff0c\u4e2a\u4eba\u5d07\u62dc\u66f4\u662f\u5927\u5f20\u65d7\u9f13\uff0c\u641e\u5f97\u5982\u706b\u5982\u837c\uff0c\u5168\u56fd\u5404\u5730\u4e3a\u9b4f\u5fe0\u8d24\u5efa\u751f\u7960\u3002\u672c\u6765\uff0c\u4eba\u6b7b\u4e86\u624d\u5165\u7960\u5802\uff0c\u53ef\u662f\u4ed6\u201c\u4e5d\u5343\u5c81\u201d\u8001\u4eba\u5bb6\u6d3b\u7740\u7684\u65f6\u5019\u5c31\u8d77\u7960\u5802\uff0c\u7960\u4e2d\u7684\u795e\u50cf\u7528\u771f\u91d1\u88c5\u8eab\uff0c\u6d3e\u6b66\u5b98\u5b88\u7960\uff0c\u767e\u5b98\u8fdb\u7960\u8981\u5bf9\u4ed6\u795e\u50cf\u8dea\u62dc\uff0c\u90a3\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u4e2a\u4eba\u5d07\u62dc\u3002 \u3000\u3000\u9b4f\u5fe0\u8d24\u672c\u6765\u662f\u4e2a\u65e0\u8d56\u6d41\u6c13\uff0c\u5e74\u8f7b\u65f6\u548c\u4eba\u8d4c\u94b1\uff0c\u5927\u8f93\u7279\u8f93\uff0c\u6b20\u4e86\u8d4c\u5e10\u8fd8\u4e0d\u51fa\uff0c\u7ed9\u4eba\u4fae\u8fb1\u8ffd\u8ba8\uff0c\u5b9e\u5728\u5403\u4e0d\u6d88\u4e86\uff0c\u6124\u800c\u81ea\u5df1\u9609\u5272\uff0c\u8fdb\u5bab\u505a\u4e86\u592a\u76d1\u3002\u4ed6\u4e0d\u8bc6\u5b57\uff0c\u4f46\u8bb0\u6027\u5f88\u597d\uff0c\u662f\u4e2a\u5b8c\u5168\u6ca1\u6709\u53d7\u8fc7\u6559\u80b2\u7684\u8d4c\u68cd\u3002\u5f53\u4e16\u7b2c\u4e00\u5927\u56fd\u7684\u519b\u653f\u5927\u6743\u5374\u843d\u5728\u8fd9\u6837\u7684\u4eba\u624b\u91cc\u3002 \u3000\u3000\u718a\u5ef7\u5f3c\u5728\u8fbd\u4e1c\u7ec3\u5175\u5b88\u57ce\uff0c\u62db\u629a\u96be\u6c11\uff0c\u6574\u8083\u519b\u7eaa\uff0c\u4fee\u6cbb\u5668\u68b0\uff0c\u628a\u5c40\u9762\u7a33\u5b9a\u4e0b\u6765\u3002\u4ed6\u6240\u63a5\u624b\u7684\u90a3\u4e2a\u70c2\u644a\u5b50\uff0c\u7ed9\u4ed6\u6574\u987f\u5f97\u6709\u4e9b\u50cf\u6837\u4e86\u3002\u6ee1\u6e05\u89c1\u5bf9\u65b9\u6709\u4e86\u51c6\u5907\uff0c\u5c31\u4e0d\u6562\u8d38\u7136\u6765\u653b\u3002\u4f46\u671d\u91cc\u654c\u5bf9\u4e00\u6d3e\u7684\u5927\u81e3\u5374\u6765\u8ddf\u4ed6\u8fc7\u4e0d\u53bb\uff0c\u4e0d\u65ad\u4e0a\u594f\u7ae0\u653b\u51fb\uff0c\u8bf4\u4ed6\u80c6\u5c0f\uff0c\u4e0d\u6562\u51fa\u6218\uff1b\u8bf4\u4ed6\u65e0\u80fd\uff0c\u4e0d\u80fd\u5c3d\u590d\u5931\u5730\u3002\u4e8e\u662f\u671d\u5ef7\u9769\u4e86\u718a\u5ef7\u5f3c\u7684\u804c\uff0c\u542c\u5019\u67e5\u529e\uff0c\u6539\u7528\u8881\u5e94\u6cf0\u505a\u7edf\u5e05\u3002 \u3000\u3000\u8881\u5e94\u6cf0\u662f\u7b2c\u4e00\u6d41\u7684\u6c34\u5229\u5de5\u7a0b\u4eba\u624d\uff0c\u4e00\u751f\u4fee\u5824\u6cbb\u6c34\uff0c\u6551\u6d4e\u707e\u6c11\uff0c\u5927\u6709\u529f\u52b3\u3002\u4ed6\u6027\u683c\u5bbd\u4ec1\uff0c\u529e\u4e8b\u52e4\u52c9\uff0c\u6253\u4ed7\u5374\u5b8c\u5168\u4e0d\u4f1a\u3002\u6ee1\u6e05\u52aa\u5c14\u54c8\u8d64\u5f97\u77e5\u718a\u5ef7\u5f3c\u53bb\u804c\uff0c\u5927\u559c\u8fc7\u671b\uff0c\u4fbf\u9886\u5175\u6765\u653b\u3002\u8881\u5e94\u6cf0\u7387\u519b\u5e94\u6218\uff0c\u4e03\u4e07\u5175\u5927\u6e83\u3002\u6e05\u5175\u5360\u9886\u6c88\u9633\uff0c\u53c8\u51fb\u7834\u4e86\u660e\u519b\u7684\u4e24\u8def\u63f4\u519b\uff0c\u518d\u653b\u8fbd\u9633\u3002\u660e\u5175\u53c8\u5927\u8d25\uff0c\u6ee1\u5175\u53d6\u5f97\u519b\u4e8b\u8981\u585e\u8fbd\u9633\u3002 \u3000\u3000\u519b\u4e8b\u5c40\u52bf\u7cdf\u7cd5\u4e4b\u6781\uff0c\u671d\u5ef7\u675f\u624b\u65e0\u7b56\uff0c\u53ea\u597d\u518d\u53bb\u8bf7\u718a\u5ef7\u5f3c\u51fa\u6765\uff0c\u60e9\u7f5a\u4e86\u4e00\u6279\u4e0a\u6b21\u653b\u51fb\u4ed6\u7684\u5b98\u5458\uff0c\u7b97\u662f\u7ed9\u4ed6\u5e73\u6c14\u3002\u53ef\u662f\u5175\u90e8\u5c1a\u4e66\u5f20\u9e64\u9e23\u548c\u718a\u5ef7\u5f3c\u610f\u89c1\u4e0d\u5408\uff0c\u53ea\u559c\u6b22\u9a6c\u5c41\u5927\u738b\u5de1\u629a\u738b\u5316\u8d1e\uff0c\u5631\u5490\u738b\u5316\u8d1e\u4e0d\u5fc5\u670d\u4ece\u718a\u5ef7\u5f3c\u6307\u6325\u3002 \u3000\u3000\u738b\u5316\u8d1e\u5411\u671d\u5ef7\u5439\u725b\uff0c\u53ea\u987b\u516d\u4e07\u5175\u5c31\u53ef\u5c06\u6ee1\u6e05\u4e00\u4e3e\u8361\u5e73\u3002\u671d\u5ef7\u5c45\u7136\u4fe1\u4e86\u4ed6\u7684\u3002\u718a\u5ef7\u5f3c\u6781\u529b\u8ba4\u4e3a\u51c6\u5907\u4e0d\u8db3\uff0c\u4e0d\u53ef\u8fdb\u653b\u3002\u5175\u90e8\u5c1a\u4e66\u5374\u4e00\u5473\u8892\u62a4\u738b\u5316\u8d1e\u3002\u4e8e\u662f\u738b\u5316\u8d1e\u9886\u5175\u5341\u56db\u4e07\u51fa\u6218\uff0c\u4e00\u4ea4\u950b\u5168\u519b\u6e83\u6ca1\u3002\u6e05\u5175\u653b\u5360\u575a\u57ce\u5e7f\u5b81\u3002\u603b\u7b97\u718a\u5ef7\u5f3c\u9886\u4e86\u4e94\u5343\u5175\u6bbf\u540e\uff0c\u4fdd\u62a4\u96be\u6c11\u548c\u8d25\u5175\u6570\u5341\u4e07\u9000\u5165\u5c71\u6d77\u5173\u3002\u671d\u5ef7\u4e0d\u5206\u9752\u7ea2\u7682\u767d\uff0c\u5c06\u738b\u5316\u8d1e\u548c\u718a\u5ef7\u5f3c\u4e00\u8d77\u902e\u6355\u3002\u5f20\u9e64\u9e23\u514d\u804c\u3002 \u3000\u3000\u5230\u8fd9\u65f6\u4e3a\u6b62\uff0c\u660e\u6e05\u4ea4\u950b\uff0c\u5df2\u6253\u4e86\u4e09\u573a\u5927\u4ed7\u3002\u6bcf\u4e00\u4ed7\u660e\u519b\u90fd\u662f\u5927\u8d25\u3002 \u3000\u3000\u660e\u5175\u7684\u6218\u6597\u529b\u56fa\u7136\u4e0d\u53ca\u6e05\u5175\uff0c\u4f46\u4e5f\u4e0d\u662f\u4e0d\u80fd\u6253\uff0c\u4e0d\u80af\u6253\u3002\u6bcf\u4e00\u4e2a\u5927\u6218\u5f79\uff0c\u603b\u5175\u5b98\u90fd\u9635\u4ea1\uff0c\u526f\u5c06\u3001\u53c2\u5c06\u4e5f\u5927\u90fd\u9635\u4ea1\u3002\u660e\u5175\u4eba\u6570\u90fd\u8d85\u8fc7\u6e05\u5175\u6570\u500d\uff0c\u6b66\u5668\u66f4\u5148\u8fdb\u5f97\u591a\uff0c\u6709\u706b\u5668\u3002\u4e09\u4e2a\u5927\u6218\u5f79\u7684\u5931\u8d25\uff0c\u4e3b\u56e0\u90fd\u662f\u5728\u4e8e\u519b\u961f\u6ca1\u6709\u51c6\u5907\u3001\u7f3a\u4e4f\u8bad\u7ec3\uff0c\u4ee5\u53ca\u4e3b\u5e05\u6218\u7565\u4e0d\u5f53\uff0c\u6307\u6325\u9519\u8bef\u3002\u519b\u52a1\u5e9f\u5f1b\uff0c\u58eb\u6c14\u4f4e\u843d\uff0c\u5f53\u7136\u4e5f\u662f\u7531\u4e8e\u7edf\u5e05\u5931\u8d23\u3002 \u3000\u3000\u4ee5\u4e2d\u56fd\u4e4b\u5927\uff0c\u4e3a\u751a\u4e48\u7ecf\u5e38\u7f3a\u4e4f\u6709\u624d\u80fd\u7684\u7edf\u5e05\uff1f\u6839\u672c\u75c7\u7ed3\u662f\u5728\u660e\u671d\u4e00\u4e2a\u7edd\u5bf9\u8352\u8c2c\u7684\u5236\u5ea6\uff1a\u7531\u6587\u5b98\u6307\u6325\u6218\u5f79\u3002 \u3000\u3000\u8fd9\u4e2a\u5236\u5ea6\u7684\u6839\u6e90\uff0c\u5728\u4e8e\u7687\u5e1d\u4e0d\u4fe1\u4efb\u6b66\u5b98\u3002\u660e\u671d\u7687\u5e1d\u4e0d\u4fe1\u4efb\u6b66\u5c06\uff0c\u6015\u4ed6\u4eec\u624b\u91cc\u6709\u4e86\u6b66\u529b\uff0c\u5c31\u4f1a\u62a2\u593a\u7687\u5e1d\u7684\u5b9d\u5ea7\uff0c\u5148\u662f\u6d3e\u6587\u5b98\u53bb\u519b\u4e2d\u76d1\u89c6\uff0c\u540e\u6765\u7d22\u6027\u53eb\u6587\u5b98\u505a\u603b\u6307\u6325\uff0c\u5230\u540e\u6765\u8fde\u6587\u5b98\u4e5f\u4e0d\u4fe1\u4efb\u4e86\uff0c\u4e8e\u662f\u518d\u52a0\u6d3e\u592a\u76d1\u4f5c\u76d1\u519b\u3002\u592a\u76d1\u65e2\u662f\u7687\u5e1d\u7684\u5fc3\u8179\u4eb2\u4fe1\uff0c\u53e6\u6709\u4e00\u6837\u597d\u5904\uff0c\u592a\u76d1\u6ca1\u6709\u513f\u5b50\uff0c\u7be1\u4f4d\u7684\u53ef\u80fd\u6027\u5c31\u5f88\u5c0f\u3002\u505a\u4e86\u7687\u5e1d\u800c\u4e0d\u80fd\u4f20\u4e8e\u5b50\u5b59\uff0c\u505a\u7687\u5e1d\u7684\u5174\u8da3\u5c31\u5927\u6253\u6298\u6263\u4e86\u3002 \u3000\u3000\u660e\u671d\u5fa1\u53f2\u7684\u6743\u529b\u5f88\u5927\uff0c\u6709\u6743\u76d1\u5bdf\u5404\u884c\u653f\u90e8\u95e8\u3002\u5927\u5b66\u58eb\u4ee3\u7687\u5e1d\u62df\u7684\u5723\u65e8\u3001\u516d\u90e8\u5c1a\u4e66\u6240\u4e0b\u7684\u51b3\u5b9a\uff0c\u5fa1\u53f2\u90fd\u53ef\u653e\u8a00\u6279\u8bc4\uff0c\u800c\u4e14\u6279\u8bc4\u7ecf\u5e38\u53d1\u751f\u6548\u529b\u3002\u7687\u5e1d\u6d3e\u53bb\u76d1\u5bdf\u6b66\u5c06\u7684\u201c\u603b\u7763\u201d\u3001\u201c\u5de1\u629a\u201d\uff0c\u540e\u6765\u5c31\u53d8\u6210\u4e86\u603b\u53f8\u4ee4\u3001\u603b\u6307\u6325\u3002 \u3000\u3000\u4f46\u8981\u505a\u5230\u5fa1\u53f2\uff0c\u901a\u5e38\u975e\u4e2d\u8fdb\u58eb\u4e0d\u53ef\u3002\u8981\u4e2d\u8fdb\u58eb\uff0c\u5fc5\u987b\u8bfb\u719f\u56db\u4e66\u4e94\u7ecf\uff0c\u4e66\u6cd5\u6f02\u4eae\uff0c\u4f1a\u505a\u8d77\u627f\u8f6c\u5408\u7684\u516b\u80a1\u6587\u3002\u660e\u671d\u8bfb\u4e66\u4eba\u5982\u4f55\u5e9f\u5bdd\u5fd8\u98df\u7684\u5b66\u516b\u80a1\u6587\u3001\u8003\u8fdb\u58eb\uff0c\u8bfb\u4e00\u4e0b\u300a\u5112\u6797\u5916\u53f2\u300b\u5c31\u5f88\u6e05\u695a\u4e86\u3002\u660e\u671d\u6d3e\u53bb\u5e26\u5175\u3001\u6307\u6325\u5927\u519b\uff0c\u548c\u6e05\u519b\u731b\u5c06\u9510\u5352\u5bf9\u6297\u7684\uff0c\u5374\u90fd\u662f\u8fd9\u6279\u719f\u8bfb\u8bd7\u4e91\u5b50\u66f0\u3001\u516b\u80a1\u6587\u505a\u5f97\u5f88\u597d\u7684\u8fdb\u58eb\u3002 \u3000\u3000\u660e\u672b\u6297\u6e05\u6709\u4e09\u4e2a\u540d\u5c06\uff0c\u529f\u52cb\u5353\u8457\uff1a\u718a\u5ef7\u5f3c\u662f\u4e07\u5386\u4e8c\u5341\u4e94\u5e74\u7684\u89e3\u5143\uff08\u5510\u4f2f\u864e\u4e00\u7c7b\u8eab\u5206\uff09\uff0c\u4e07\u5386\u4e8c\u5341\u516d\u5e74\u7684\u8fdb\u58eb\u3002\u5b59\u627f\u5b97\u662f\u4e07\u5386\u4e09\u5341\u4e8c\u5e74\u7684\u8fdb\u58eb\u7b2c\u4e8c\u540d\uff08\u699c\u773c\uff09\u3002\u8881\u5d07\u7115\u662f\u4e07\u5386\u56db\u5341\u4e03\u5e74\u8fdb\u58eb\u3002\u4ed6\u4eec\u4e09\u4e2a\u662f\u6587\u5b98\uff0c\u5e78\u4e8f\u78b0\u5de7\u6709\u7528\u5175\u7684\u624d\u80fd\u3002\u672c\u6765\u660e\u672b\u7687\u5e1d\u7684\u8fd0\u6c14\u4e0d\u574f\uff0c\u505a\u516b\u80a1\u6587\u8003\u4e2d\u8fdb\u58eb\u7684\u6587\u4eba\u4e4b\u4e2d\u5c45\u7136\u51fa\u73b0\u4e86\u4e09\u4e2a\u519b\u4e8b\u4e13\u5bb6\u3002\u7136\u800c\u6587\u5b98\u4f1a\u5e26\u5175\uff0c\u90a3\u5c31\u662f\u5371\u9669\u4eba\u7269\u3002\u660e\u671d\u7687\u5e1d\u7f62\u65a5\u4e86\u5176\u4e2d\u4e00\u4e2a\uff0c\u6740\u6b7b\u4e86\u53e6\u5916\u4e24\u4e2a\u3002 \u3000\u3000\u522b\u7684\u5949\u547d\u7edf\u5175\u6297\u6e05\u7684\u516b\u80a1\u6587\u4e13\u5bb6\u4eec\u53ef\u5c31\u6ca1\u6709\u519b\u4e8b\u624d\u80fd\u4e86\u3002\u6768\u9550\uff0c\u4e07\u5386\u516b\u5e74\u8fdb\u58eb\uff0c\u6307\u6325\u5927\u519b\uff0c\u5168\u519b\u8986\u6ca1\u3002\u8881\u5e94\u6cf0\uff0c\u4e07\u5386\u4e8c\u5341\u4e09\u5e74\u8fdb\u58eb\uff0c\u6307\u6325\u5927\u519b\uff0c\u5168\u519b\u8986\u6ca1\u3002\u738b\u5316\u8d1e\uff0c\u4e07\u5386\u56db\u5341\u4e00\u5e74\u8fdb\u58eb\uff0c\u6307\u6325\u5927\u519b\uff0c\u5168\u519b\u8986\u6ca1\u3002 \u3000\u3000\u8881\u5d07\u7115\u662f\u5728\u8fd9\u6837\u7684\u653f\u6cbb\u3001\u7ecf\u6d4e\u3001\u519b\u4e8b\u80cc\u666f\u4e4b\u4e0b\uff0c\u53bb\u5e94\u4ed8\u8fbd\u4e1c\u8270\u5de8\u7684\u5c40\u9762\u3002\u5f53\u7136\uff0c\u66f4\u8270\u5de8\u7684\uff0c\u662f\u5e94\u4ed8\u5317\u4eac\u671d\u5ef7\u4e2d\u7684\u5c40\u9762\u3002 \u3000\u3000\u80cc\u540e\u662f\u660f\u6126\u80e1\u6d82\u7684\u7687\u5e1d\u3001\u5c48\u6740\u5fe0\u826f\u7684\u6743\u5978\u3001\u5ac9\u529f\u5992\u80fd\u7684\u8a00\u5b98\uff1b\u624b\u4e0b\u662f\u4e00\u6279\u9965\u997f\u7fb8\u5f31\u7684\u5175\u5352\u548c\u9a6c\u5339\uff0c\u5c06\u5b98\u4e0d\u5168\uff0c\u5175\u5668\u6b8b\u7f3a\uff0c\u9886\u4e0d\u5230\u7cae\uff0c\u9886\u4e0d\u5230\u9977\uff0c\u6240\u9762\u5bf9\u7684\u654c\u4eba\uff0c\u5374\u662f\u81ea\u6210\u5409\u601d\u6c57\u4ee5\u6765\uff0c\u56db\u767e\u591a\u5e74\u4e2d\u5168\u4e16\u754c\u4ece\u672a\u51fa\u73b0\u8fc7\u7684\u519b\u4e8b\u5929\u624d\u52aa\u5c14\u54c8\u8d64\u3002\u8fd9\u4e2a\u7528\u5175\u5982\u795e\u7684\u7edf\u5e05\uff0c\u4f20\u4e0b\u4e86\u4e25\u5bc6\u7684\u519b\u4e8b\u5236\u5ea6\u548c\u7eaa\u5f8b\uff0c\u4f7f\u5f97\u4ed6\u624b\u4e0b\u90a3\u6279\u6218\u58eb\uff0c\u6b64\u540e\u4e24\u767e\u5e74\u95f4\u5728\u5168\u4e16\u754c\u6240\u5411\u65e0\u654c\u3002\u94c1\u9a91\u5954\u9a70\u4e8e\u5317\u57f5\u5927\u6f20\uff0c\u5357\u7586\u9ad8\u539f\uff0c\u7684\u7684\u786e\u786e\u662f\u5a01\u884c\u7edd\u57df\uff0c\u9707\u6151\u56db\u90bb\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u4ee5\u7956\u5b97\u9057\u4e0b\u7684\u5341\u4e09\u526f\u7532\u80c4\u8d77\u5bb6\uff0c\u5e26\u9886\u4e86\u6570\u767e\u540d\u65cf\u4eba\u4e1c\u5f81\u897f\u8ba8\uff0c\u5efa\u7acb\u4e86\u4e2d\u56fd\u5386\u53f2\u4e0a\u7586\u57df\u6700\u5927\u7684\u5927\u5e1d\u56fd\uff08\u5143\u671d\u7684\u8499\u53e4\u5e1d\u56fd\u6a2a\u8de8\u6b27\u4e9a\uff0c\u4e0d\u80fd\u8bf4\u4e2d\u534e\u5e1d\u56fd\u7684\u9886\u571f\u7adf\u6709\u8fd9\u4e48\u5927\u3002\u8499\u53e4\u5927\u5e1d\u56fd\u7684\u4e2d\u56fd\u90e8\u5206\uff0c\u8fdc\u6bd4\u6e05\u671d\u7684\u7586\u57df\u4e3a\u5c0f\uff09\u3002\u6e05\u671d\u7684\u7586\u57df\u6bd4\u6c49\u671d\u3001\u5510\u671d\u5168\u76db\u65f6\u4ee3\u90fd\u5927\u5f97\u591a\uff0c\u5b8b\u660e\u4e24\u671d\u66f4\u4e0d\u80fd\u4e0e\u4e4b\u76f8\u6bd4\u3002\u5f53\u65f6\u5916\u8499\u53e4\u3001\u671d\u9c9c\u3001\u8d8a\u5357\u3001\u7409\u7403\u3001\u4eca\u65e5\u82cf\u8054\u4e1c\u90e8\u7684\u5927\u7247\u571f\u5730\u90fd\u662f\u4e2d\u56fd\u7684\u9886\u571f\u6216\u5c5e\u5730\u3002\u6e05\u671d\u5168\u76db\u65f6\u671f\u7684\u9886\u571f\uff0c\u6bd4\u73b0\u5728\u7684\u4e2d\u56fd\u5927\u5f97\u591a\u4e86\u3002 \u3000\u3000\u6ee1\u6d32\u6218\u58eb\u540e\u6765\u6253\u8d25\u4e86\u4fc4\u7f57\u65af\u5e1d\u56fd\u7684\u9a91\u5175\uff0c\u6253\u8d25\u4e86\u5c3c\u6cca\u5c14\u7684\u5579\u5580\u5175\uff0c\u6253\u8d25\u4e86\u8499\u53e4\u5175\uff0c\u6253\u8d25\u4e86\u671d\u9c9c\u5175\uff0c\u6253\u8d25\u4e86\u8d8a\u5357\u5175\uff0c\u95f4\u63a5\u6253\u8d25\u8377\u5170\u5175\uff08\u90d1\u6210\u529f\u5148\u6253\u8d25\u8377\u5170\u5175\uff0c\u653b\u5360\u53f0\u6e7e\uff0c\u6ee1\u6d32\u5175\u518d\u6253\u8d25\u90d1\u6210\u529f\u7684\u5b59\u5b50\uff09\uff0c\u5728\u5341\u4e03\u4e16\u7eaa\u3001\u5341\u516b\u4e16\u7eaa\u7684\u4e24\u767e\u5e74\u4e2d\uff0c\u65e0\u654c\u4e8e\u5929\u4e0b\u3002 \u3000\u3000\u81f3\u4e8e\u5f53\u65f6\u548c\u660e\u5e1d\u56fd\u4ea4\u6218\uff0c\u5df2\u63a5\u8fde\u4e09\u6b21\u6740\u5f97\u660e\u519b\u5168\u519b\u8986\u6ca1\uff0c\u6bcf\u4e00\u4e2a\u6218\u5f79\u90fd\u662f\u4ee5\u5c11\u80dc\u591a\u3002\u52aa\u5c14\u54c8\u8d64\u5174\u5175\u4ee5\u6765\uff0c\u8fc4\u6b64\u65f6\u4e3a\u6b62\uff0c\u767e\u6218\u767e\u80dc\uff0c\u4ece\u672a\u5403\u8fc7\u4e00\u4e2a\u8d25\u4ed7\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u5e7c\u65f6\u5728\u660e\u671d\u5927\u5c06\u674e\u6210\u6881\u5bb6\u4e2d\u4e3a\u5974\uff0c\u8bc6\u5f97\u6c49\u8bed\u6c49\u6587\uff0c\u559c\u8bfb\u300a\u4e09\u56fd\u6f14\u4e49\u300b\u4e0e\u300a\u6c34\u6d52\u4f20\u300b\u3002\u4ed6\u7684\u667a\u7565\u4e00\u90e8\u5206\u662f\u5929\u751f\uff0c\u4e00\u90e8\u5206\u5f53\u662f\u4ece\u8fd9\u4e24\u90e8\u5c0f\u8bf4\u4e2d\u5f97\u6765\u7684\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u81ea\u5df1\u56fa\u7136\u667a\u52c7\u53cc\u5168\uff0c\u4ed6\u8fd8\u6709\u4e00\u5927\u6279\u7cbe\u660e\u9a81\u52c7\u7684\u5b50\u4f84\u2460\uff0c\u527d\u608d\u51f6\u731b\u7684\u5c06\u9886\uff0c\u90e8\u52d2\u4e25\u6574\u7684\u6218\u58eb\u3002 \u3000\u3000\u5f53\u65f6\u6709\u4e00\u53e5\u8c1a\u8bed\u8bf4\uff1a\u201c\u5973\u771f\u4e0d\u6ee1\u4e07\uff0c\u6ee1\u4e07\u4e0d\u53ef\u654c\u3002\u201d\u56e0\u4e3a\u5973\u771f\u4eba\u719f\u4e60\u5f13\u9a6c\uff0c\u5f3a\u608d\u5584\u6218\uff0c\u6c49\u4eba\u5411\u6765\u4e0d\u662f\u4ed6\u4eec\u7684\u654c\u624b\u3002\u8fd9\u65f6\u5973\u771f\u7cbe\u5175\u516b\u65d7\uff0c\u6bcf\u65d7\u4e03\u5343\u4e94\u767e\u4eba\uff0c\u5df2\u6709\u516d\u4e07\u4e4b\u4f17\u4e86\u3002 \u3000\u3000\u8881\u5d07\u7115\u6240\u9762\u5bf9\u7684\u662f\u8fd9\u6837\u4e86\u4e0d\u8d77\u7684\u5927\u654c\uff0c\u800c\u4ed6\u5374\u662f\u4e2a\u4e66\u751f\u3002\u4ed6\u4f1a\u505a\u8bd7\uff0c\u5b57\u5199\u5f97\u5f88\u597d\uff0c\u6587\u7ae0\u6709\u6c14\u52bf\u2461\uff0c\u65e2\u7136\u4e2d\u4e86\u8fdb\u58eb\uff0c\u516b\u80a1\u6587\u5f53\u7136\u4e5f\u505a\u5f97\u4e0d\u9519\uff0c\u8bd7\u4e91\u5b50\u66f0\u80cc\u5f97\u5f88\u719f\u3002\u76f8\u4fe1\u4ed6\u4e0d\u4f1a\u5c04\u7bad\uff0c\u5b81\u8fdc\u7b2c\u4e8c\u6b21\u5927\u6218\u65f6\uff0c\u4ed6\u81ea\u79f0\u53ea\u662f\u5728\u57ce\u5934\u5927\u58f0\u5450\u558a\u2462\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u4e0e\u8881\u5d07\u7115\u6b63\u9762\u4ea4\u950b\u4e4b\u65f6\uff0c\u6ee1\u6e05\u7684\u5175\u52bf\u6b63\u5904\u4e8e\u5dc5\u5cf0\u72b6\u6001\uff0c\u800c\u660e\u671d\u7684\u653f\u6cbb\u4e0e\u519b\u4e8b\u4e5f\u6b63\u5904\u4e8e\u8150\u8d25\u7edd\u9876\u7684\u72b6\u6001\u3002 \u3000\u3000\u4ee5\u8fd9\u6837\u4e00\u4e2a\u6587\u5f31\u4e66\u751f\uff0c\u5728\u8fd9\u6837\u4e0d\u5229\u7684\u5c40\u9762\u4e4b\u4e0b\uff0c\u800c\u53bb\u548c\u4e00\u4e2a\u7eb5\u6a2a\u65e0\u654c\u7684\u5927\u82f1\u96c4\u5bf9\u6297\uff0c\u5c45\u7136\u628a\u52aa\u5c14\u54c8\u8d64\u6253\u6b7b\u4e86\uff0c\u6253\u4e09\u573a\u5927\u6218\uff0c\u80dc\u4e86\u4e09\u573a\uff0c\u8881\u5d07\u7115\u7684\u82f1\u96c4\u6c14\u6982\uff0c\u5728\u6574\u4e2a\u4eba\u7c7b\u5386\u53f2\u4e2d\u90fd\u662f\u5341\u5206\u7f55\u6709\u7684\u3002 \u3000\u3000\u2460\u52aa\u5c14\u54c8\u8d64\u6709\u5341\u516d\u4e2a\u513f\u5b50\uff0c\u4e2a\u4e2a\u662f\u6709\u540d\u7684\u52c7\u5c06\u3002\u4e24\u4e2a\u4f84\u513f\u963f\u654f\u4e0e\u6d4e\u5c14\u54c8\u6717\u4e5f\u5341\u5206\u5389\u5bb3\u3002 \u3000\u3000\u2461\u5eb7\u6709\u4e3a\u300a\u8881\u7763\u5e08\u9057\u96c6\u5e8f\u300b\u76db\u79f0\u5176\u6587\u5b57\u96c4\u5947\uff1a\u201c\u592b\u8881\u7763\u5e08\u4e4b\u96c4\u624d\u5927\u7565\uff0c\u5fe0\u70c8\u6b66\u68f1\uff0c\u53e4\u4eca\u5be1\u6bd4\u3002\u5176\u9057\u6587\u867d\u5be5\u843d\uff0c\u800c\u594b\u626c\u8e48\u5389\uff0c\u9e64\u7acb\u8679\u5e03\uff0c\u72b9\u60f3\u89c1\u9c81\u9633\u6325\u6208\u3001\u5d06\u5cd2\u501a\u5251\u4e4b\u795e\u91c7\u7109\u3002\u201d \u3000\u3000\u2462\u300a\u660e\u53f2\u300b\u8bf4\u718a\u5ef7\u5f3c\u5de6\u53f3\u624b\u90fd\u4f1a\u5c04\u7bad\uff0c\u4f46\u6ca1\u6709\u63d0\u5230\u8881\u5d07\u7115\u4f1a\u6b66\u3002 \u3000\u3000\u56db \u3000\u3000\u8881\u5d07\u7115\uff0c\u5e7f\u4e1c\u4e1c\u839e\u4eba\uff0c\u7956\u4e0a\u539f\u7c4d\u5e7f\u897f\u68a7\u5dde\u85e4\u53bf\u3002\u751f\u4e8e\u54ea\u4e00\u5e74\u65e0\u6cd5\u67e5\u8003\u3002 \u3000\u3000\u4ed6\u4e3a\u4eba\u6177\u6168\uff0c\u5bcc\u4e8e\u80c6\u7565\uff0c\u559c\u6b22\u548c\u4eba\u8c08\u8bba\u519b\u4e8b\uff0c\u9047\u5230\u5e74\u8001\u9000\u4f0d\u7684\u519b\u5b98\u58eb\u5352\uff0c\u603b\u662f\u5411\u4ed6\u4eec\u8bf7\u95ee\u8fb9\u7586\u4e0a\u7684\u519b\u4e8b\u60c5\u51b5\uff0c\u5728\u5e74\u8f7b\u65f6\u5019\u5c31\u6709\u5fd7\u4e8e\u53bb\u529e\u7406\u8fb9\u7586\u4e8b\u52a1\u2460\u3002 \u3000\u3000\u4ed6\u5c11\u5e74\u65f6\u4fbf\u4ee5\u201c\u8c6a\u58eb\u201d\u81ea\u8bb8\u2461\uff0c\u559c\u6b22\u65c5\u884c\u3002\u4ed6\u4e2d\u4e86\u4e3e\u4eba\u540e\u518d\u8003\u8fdb\u58eb\uff0c\u591a\u6b21\u843d\u7b2c\uff0c\u6bcf\u6b21\u4e0a\u5317\u4eac\u5e94\u8bd5\uff0c\u603b\u662f\u4e58\u673a\u6e38\u5386\uff0c\u51e0\u4e4e\u8e0f\u904d\u4e86\u534a\u4e2a\u4e2d\u56fd\u2462\u3002\u6700\u559c\u6b22\u548c\u597d\u670b\u53cb\u901a\u5bb5\u4e0d\u7761\u7684\u8c08\u5929\u8bf4\u5730\uff0c\u8c08\u8bdd\u7684\u5185\u5bb9\u5f80\u5f80\u6d89\u53ca\u5175\u6208\u6218\u9635\u4e4b\u4e8b\u2463\u3002 \u3000\u3000\u660e\u671d\u5236\u5ea6\uff0c\u6bcf\u4e09\u5e74\u8003\u4e00\u6b21\u8fdb\u58eb\uff0c\u4f1a\u8bd5\u5728\u4e8c\u6708\u521d\u4e5d\u5f00\u59cb\uff0c\u5341\u4e94\u7ed3\u675f\u3002\u4e09\u6708\u521d\u4e00\u5ef7\u8bd5\u3002\u8881\u5d07\u7115\u4e8e\u4e07\u5386\u56db\u5341\u4e03\u5e74\u5728\u5317\u4eac\u53c2\u52a0\u5ef7\u8bd5\u800c\u4e2d\u8fdb\u58eb\u3002\u6768\u9550\u4e8e\u8be5\u5e74\u4e8c\u6708\u8a93\u5e08\u8fbd\u9633\uff0c\u4e09\u6708\u95f4\u56db\u8def\u4e27\u5e08\u3002\u65b0\u4e2d\u8fdb\u58eb\u548c\u5927\u6218\u6e83\u8d25\u8fd9\u4e24\u4ef6\u4e8b\u5728\u540c\u4e00\u4e2a\u65f6\u5019\u53d1\u751f\uff0c\u8881\u5d07\u7115\u8fd9\u4e2a\u5411\u6765\u5173\u5fc3\u8fb9\u9632\u7684\u65b0\u8fdb\u58eb\u4e00\u559c\u4e00\u5fe7\uff0c\u5fc3\u60c5\u4e00\u5b9a\u5f88\u590d\u6742\u3002\u4ed6\u90a3\u65f6\u5728\u4eac\u57ce\uff0c\u5f53\u7136\u542c\u5230\u4e0d\u5c11\u8fbd\u4e1c\u6218\u4e8b\u7684\u6d88\u606f\u3002 \u3000\u3000\u4ed6\u4e2d\u8fdb\u58eb\u540e\uff0c\u88ab\u5206\u6d3e\u5230\u798f\u5efa\u90b5\u6b66\u53bb\u505a\u77e5\u53bf\u3002 \u3000\u3000\u5929\u542f\u4e8c\u5e74\uff0c\u4ed6\u5230\u5317\u4eac\u6765\u62a5\u544a\u804c\u52a1\u3002\u4ed6\u5e73\u65e5\u662f\u5f88\u559c\u6b22\u9ad8\u8c08\u9614\u8bba\u7684\uff0c\u5927\u6982\u5728\u5317\u4eac\u548c\u53cb\u4eba\u8c08\u8bdd\u65f6\uff0c\u53d1\u8868\u4e86\u4e00\u4e9b\u5bf9\u8fbd\u4e1c\u519b\u4e8b\u7684\u89c1\u89e3\uff0c\u5f88\u662f\u4e2d\u80af\uff0c\u5f15\u8d77\u4e86\u5fa1\u53f2\u4faf\u6042\uff08\u624d\u5b50\u4faf\u65b9\u57df\u7684\u7236\u4eb2\uff09\u7684\u6ce8\u610f\uff0c\u4fbf\u5411\u671d\u5ef7\u4fdd\u8350\u4ed6\u6709\u519b\u4e8b\u624d\u80fd\uff0c\u4e8e\u662f\u83b7\u5347\u4e3a\u5175\u90e8\u804c\u65b9\u53f8\u4e3b\u4e8b\uff08\u81ea\u6b63\u4e03\u54c1\u7684\u77e5\u53bf\u5347\u4e3a\u6b63\u516d\u54c1\u7684\u4e3b\u4e8b\uff09\u3002\u4e0d\u505a\u5730\u65b9\u5b98\u4e86\uff0c\u88ab\u6d3e\u5230\u4e2d\u592e\u653f\u5e9c\u7684\u56fd\u9632\u90e8\u53bb\u529e\u4e8b\u3002 \u3000\u3000\u660e\u671d\u5b98\u5236\uff0c\u5175\u90e8\uff08\u56fd\u9632\u90e8\uff09\u5c1a\u4e66\uff08\u90e8\u957f\uff09\u4e00\u4eba\uff0c\u5de6\u53f3\u4f8d\u90ce\uff08\u526f\u90e8\u957f\uff09\u5404\u4e00\u4eba\uff0c\u4e0b\u9762\u5206\u8bbe\u56db\u4e2a\u53f8\uff1a\u6b66\u9009\uff08\u6b66\u5b98\u4eba\u4e8b\uff09\u3001\u804c\u65b9\uff08\u519b\u653f\u3001\u519b\u4ee4\uff09\u3001\u8f66\u9a7e\uff08\u8b66\u5907\u3001\u901a\u8baf\u3001\u9a6c\u5339\uff09\u3001\u6b66\u5e93\uff08\u540e\u52e4\u3001\u8bad\u7ec3\uff09\u3002\u804c\u65b9\u53f8\u7b49\u4e8e\u73b0\u4ee3\u7684\u603b\u53c2\u8c0b\u90e8\uff0c\u804c\u65b9\u53f8\u6709\u90ce\u4e2d\u4e00\u4eba\u3001\u5458\u5916\u90ce\u4e00\u4eba\u3001\u4e3b\u4e8b\u4e8c\u4eba\u3002\u4e3b\u4e8b\u5927\u6982\u76f8\u5f53\u4e8e\u603b\u53c2\u8c0b\u90e8\u4e2d\u7684\u6587\u804c\u4e2d\u6821\u526f\u5904\u957f\u3002 \u3000\u3000\u8881\u5d07\u7115\u4efb\u5175\u90e8\u4e3b\u4e8b\u4e0d\u4e45\uff0c\u738b\u5316\u8d1e\u5927\u519b\u5728\u5e7f\u5b81\u8986\u6ca1\uff0c\u6ee1\u671d\u60ca\u60f6\u5931\u63aa\u3002 \u3000\u3000\u6e05\u5175\u52bf\u5982\u7834\u7af9\uff0c\u9510\u4e0d\u53ef\u5f53\uff0c\u81ea\u4e07\u5386\u56db\u5341\u516d\u5e74\u5230\u90a3\u65f6\uff0c\u56db\u5e74\u591a\u7684\u65f6\u95f4\u5185\uff0c\u8986\u6ca1\u4e86\u660e\u519b\u6570\u5341\u4e07\uff0c\u653b\u5360\u629a\u987a\u3001\u5f00\u539f\u3001\u94c1\u5cad\u3001\u6c88\u9633\u3001\u8fbd\u9633\uff0c\u76f4\u903c\u5c71\u6d77\u5173\u3002\u660e\u519b\u6253\u4e00\u4ed7\uff0c\u8d25\u4e00\u4ed7\uff0c\u5c71\u6d77\u5173\u662f\u4e0d\u662f\u5b88\u5f97\u4f4f\uff0c\u8c01\u90fd\u4e0d\u6562\u8bf4\u3002\u5c71\u6d77\u5173\u4e00\u5931\uff0c\u6e05\u5175\u5c31\u957f\u9a71\u800c\u5230\u5317\u4eac\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u5317\u4eac\u5ba3\u5e03\u6212\u4e25\uff0c\u8fdb\u5165\u7d27\u6025\u72b6\u6001\u3002 \u3000\u3000\u53ef\u662f\u5173\u5916\u7684\u5c40\u52bf\u5230\u5e95\u600e\u6837\uff0c\u4f20\u5230\u5317\u4eac\u7684\u8bf4\u6cd5\u591a\u5f97\u5f88\uff0c\u5c40\u52bf\u8d8a\u662f\u4e0d\u5229\uff0c\u8c23\u8a00\u8d8a\u591a\uff0c\u8fd9\u662f\u4eba\u7c7b\u793e\u4f1a\u7684\u901a\u4f8b\u3002\u8c23\u8a00\u6ee1\u5929\u98de\uff0c\u8c01\u4e5f\u65e0\u6cd5\u8fa8\u522b\u771f\u5047\u3002\u5c31\u5728\u8fd9\u4eac\u5e08\u4e2d\u4eba\u5fc3\u60f6\u60f6\u7684\u65f6\u5019\uff0c\u8881\u5d07\u7115\u9a91\u4e86\u4e00\u5339\u9a6c\uff0c\u5b64\u8eab\u4e00\u4eba\u51fa\u5173\u53bb\u8003\u5bdf\u3002\u5175\u90e8\u4e2d\u5ffd\u7136\u4e0d\u89c1\u4e86\u8881\u4e3b\u4e8b\uff0c\u5927\u5bb6\u5341\u5206\u60ca\u8bb6\uff0c\u5bb6\u4eba\u4e5f\u4e0d\u77e5\u4ed6\u5230\u4e86\u54ea\u91cc\u3002\u4e0d\u4e45\u4ed6\u56de\u5230\u5317\u4eac\uff0c\u5411\u4e0a\u53f8\u8be6\u7ec6\u62a5\u544a\u5173\u4e0a\u5f62\u52bf\uff0c\u5ba3\u79f0\uff1a\u201c\u53ea\u8981\u7ed9\u6211\u5175\u9a6c\u7cae\u9977\uff0c\u6211\u4e00\u4eba\u8db3\u53ef\u5b88\u5f97\u4f4f\u5c71\u6d77\u5173\u3002\u201d \u3000\u3000\u8fd9\u4ef6\u4e8b\u5145\u5206\u8868\u73b0\u4e86\u4ed6\u884c\u4e8b\u4efb\u6027\uff0c\u5f88\u6709\u80c6\u8bc6\uff0c\u6562\u4f5c\u6562\u4e3a\u800c\u811a\u8e0f\u5b9e\u5730\uff0c\u4f46\u72c2\u6c14\u4e5f\u662f\u5341\u8db3\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u4ed6\u4e0a\u53f8\u591a\u534a\u8981\u65a5\u8d23\u4ed6\u64c5\u79bb\u804c\u5b88\uff0c\u7f62\u4ed6\u7684\u5b98\uff0c\u4f46\u8fd9\u65f6\u671d\u5ef7\u6b63\u5728\u5fe7\u6025\u5f77\u5fa8\u4e4b\u9645\uff0c\u542c\u4ed6\u8bf4\u5f97\u5934\u5934\u662f\u9053\uff0c\u4fbf\u5347\u4ed6\u4e3a\u5175\u5907\u4f65\u4e8b\uff0c\u90a3\u662f\u90fd\u5bdf\u9662\u7684\u5b98\uff0c\u5927\u6982\u76f8\u5f53\u4e8e\u73b0\u4ee3\u6587\u804c\u7684\u4e0a\u6821\u653f\u6cbb\u4e3b\u4efb\u4e4b\u7c7b\uff0c\u6d3e\u4ed6\u53bb\u52a9\u5b88\u5c71\u6d77\u5173\u3002\u8881\u5d07\u7115\u7ec8\u4e8e\u5f97\u5230\u4e86\u4ed6\u68a6\u60f3\u5df2\u4e45\u7684\u673a\u4f1a\uff0c\u96c4\u5fc3\u52c3\u52c3\u7684\u5230\u56fd\u9632\u524d\u7ebf\u53bb\u6548\u529b\u3002 \u3000\u3000\u4ed6\u7684\u8c6a\u8bed\u4e00\u5b9a\u4f7f\u671d\u4e2d\u5927\u5b98\u4eec\u5370\u8c61\u5341\u5206\u6df1\u523b\uff0c\u6240\u4ee5\u5f97\u5230\u671d\u5ef7\u7684\u652f\u6301\uff0c\u4ece\u4ed6\u5bb6\u4e61\u62db\u52df\u4e86\u4e00\u6279\u5175\u5458\u53bb\u2464\u3002\u5f53\u65f6\u5b88\u5c71\u6d77\u5173\u7684\u4e3b\u8981\u662f\u65b0\u5230\u7684\u6d59\u6c5f\u5175\u3002\u53e6\u6709\u4e09\u5343\u540d\u5e7f\u4e1c\u6c34\u5175\uff0c\u5728\u8881\u5d07\u7115\u4e4b\u540e\u5230\u8fbe\u3002\u8881\u5d07\u7115\u8ba4\u4e3a\u5e7f\u4e1c\u6b65\u5175\u52c7\u6377\u5584\u6218\uff0c\u63a8\u8350\u4ed6\u53d4\u7236\u8881\u7389\u4f69\u8d1f\u8d23\u62db\u52df\u4e09\u5343\u540d\uff0c\u5176\u4e2d\u5305\u62ec\u8881\u5d07\u7115\u5e73\u751f\u6240\u7ed3\u7eb3\u7684\u6b7b\u58eb\u8c22\u5c1a\u653f\u3001\u6d2a\u5b89\u6f9c\u7b49\u4eba\u3002\u4ed6\u53c8\u8ba4\u4e3a\u5e7f\u897f\u72fc\u5175\u96c4\u4e8e\u5929\u4e0b\uff0c\u51b2\u950b\u9677\u9635\uff0c\u606c\u4e0d\u754f\u6b7b\uff0c\u7533\u8bf7\u4e8e\u7530\u5dde\u3001\u6cd7\u57ce\u5dde\u3001\u9f99\u82f1\u5dde\u5404\u8c03\u4e8c\u5343\u540d\uff0c\u7531\u4ed6\u81f3\u621a\u6177\u6168\u77e5\u540d\u3001\u4e14\u5584\u6b66\u827a\u7684\u6797\u7fd4\u51e4\u5e26\u9886\u3002\u671d\u5ef7\u4e00\u4e00\u6279\u51c6\u2465\u3002 \u3000\u3000\u4ed6\u5230\u5c71\u6d77\u5173\u540e\uff0c\u4f5c\u4e3a\u8fbd\u4e1c\u7ecf\u7565\uff08\u4e1c\u5317\u519b\u533a\u603b\u53f8\u4ee4\uff09\u738b\u5728\u664b\u7684\u4e0b\u5c5e\uff0c\u521d\u65f6\u5728\u5173\u5185\u529e\u4e8b\u3002\u738b\u5728\u664b\u89c1\u4ed6\u4efb\u4e8b\u5e72\u7ec3\uff0c\u5f88\u662f\u501a\u91cd\uff0c\u6d3e\u4ed6\u51fa\u5173\u5230\u524d\u5c6f\u536b\u53bb\u6536\u629a\u6d41\u79bb\u5931\u6240\u7684\u96be\u6c11\u3002\u8881\u5d07\u7115\u5949\u547d\u4e4b\u540e\uff0c\u5f53\u591c\u51fa\u53d1\uff0c\u5728\u8346\u68d8\u864e\u8c79\u4e4b\u4e2d\u591c\u884c\uff0c\u56db\u66f4\u5929\u65f6\u5230\u8fbe\u3002\u524d\u5c6f\u57ce\u4e2d\u5c06\u58eb\u65e0\u4e0d\u4f69\u670d\u3002\u8881\u5d07\u7115\u672c\u662f\u4e66\u751f\uff0c\u8fd9\u4e00\u6765\uff0c\u5175\u5c06\u90fd\u670d\u4e86\u4ed6\u4e86\u3002 \u3000\u3000\u738b\u5728\u664b\u594f\u8bf7\u6b63\u5f0f\u4efb\u4ed6\u4e3a\u5b81\u524d\u5175\u5907\u4f65\u4e8b\u3002\u8881\u5d07\u7115\u672c\u6765\u662f\u6ca1\u6709\u4e13\u8d23\u7684\u6563\u5b98\uff0c\u73b0\u5728\u6709\u4e86\u9a7b\u5730\uff0c\u76f8\u5f53\u4e8e\u5b81\u8fdc\u3001\u524d\u5c6f\u536b\u4e8c\u57ce\u7684\u57ce\u9632\u53f8\u4ee4\u90e8\u653f\u6cbb\u59d4\u5458\uff0c\u8eab\u5f53\u5c71\u6d77\u5173\u5916\u6297\u5fa1\u6e05\u5175\u7684\u7b2c\u4e00\u9053\u9632\u7ebf\u3002\u5b81\u8fdc\u5728\u6700\u524d\u7ebf\uff0c\u524d\u5c6f\u536b\u7a0d\u540e\u3002\u4e0d\u8fc7\u4ed6\u867d\u8d1f\u8d23\u9632\u5b88\u5b81\u8fdc\u3001\u524d\u5c6f\u536b\uff0c\u7b2c\u4e00\u7ebf\u7684\u5b81\u8fdc\u5374\u6ca1\u6709\u57ce\u5899\uff0c\u6ca1\u6709\u9632\u5fa1\u5de5\u4e8b\uff0c\u6839\u672c\u65e0\u57ce\u53ef\u5b88\u3002\u4ed6\u53ea\u5f97\u9a7b\u5b88\u5728\u524d\u5c6f\u536b\u3002 \u3000\u3000\u81f3\u4e8e\u660e\u519b\u4e00\u5207\u5b88\u5fa1\u8bbe\u65bd\uff0c\u90fd\u96c6\u4e2d\u5728\u5c71\u6d77\u5173\u3002\u5c71\u6d77\u5173\u662f\u201c\u5929\u4e0b\u7b2c\u4e00\u5173\u201d\uff0c\u9632\u5b88\u4eac\u5e08\u7684\u7b2c\u4e00\u5927\u8981\u585e\uff0c\u7136\u800c\u5b83\u6ca1\u6709\u5916\u56f4\u9635\u5730\u3002\u6e05\u5175\u82e5\u662f\u6765\u653b\uff0c\u7acb\u523b\u5c31\u51b2\u5230\u5173\u95e8\u4e4b\u524d\u3002 \u3000\u3000\u7a0d\u6709\u519b\u4e8b\u5e38\u8bc6\u7684\u4eba\u90fd\u7acb\u523b\u4f1a\u770b\u51fa\u6765\uff0c\u5355\u662f\u5b88\u5fa1\u5c71\u6d77\u5173\uff0c\u672a\u514d\u592a\u8fc7\u5371\u9669\uff0c\u6ca1\u6709\u4e1d\u6beb\u9000\u6b65\u7684\u4f59\u5730\u3002\u53ea\u8981\u4e00\u4ed7\u6253\u8d25\uff0c\u8fd9\u4e2a\u5927\u8981\u585e\u5c31\u5931\u5b88\uff0c\u654c\u519b\u4fbf\u653b\u5230\u5317\u4eac\u3002\u6240\u4ee5\u5728\u6218\u7565\u5f62\u52bf\u4e0a\uff0c\u5fc5\u987b\u5c06\u9632\u7ebf\u5411\u5317\u79fb\uff0c\u8d8a\u662f\u63a8\u5411\u5317\u65b9\uff0c\u5c71\u6d77\u5173\u8d8a\u5b89\u5168\uff0c\u5317\u4eac\u4e5f\u8d8a\u5b89\u5168\u3002 \u3000\u3000\u8881\u5d07\u7115\u4e00\u518d\u5411\u4e0a\u53f8\u63d0\u51fa\u8fd9\u4e2a\u5173\u952e\u95ee\u9898\u3002\u738b\u5728\u664b\u662f\u4e07\u5386\u4e8c\u5341\u5e74\u8fdb\u58eb\uff0c\u6c5f\u82cf\u592a\u4ed3\u4eba\u7684\u6587\u5f31\u4e66\u751f\uff0c\u6839\u672c\u4e0d\u61c2\u519b\u4e8b\uff0c\u773c\u5149\u77ed\u6d45\uff0c\u80c6\u5b50\u53c8\u5c0f\uff0c\u542c\u8881\u5d07\u7115\u8bf4\u8981\u5728\u5173\u5916\u5b88\u5173\uff0c\u60f3\u60f3\u9053\u7406\u5012\u4e5f\u662f\u5bf9\u7684\uff0c\u4fbf\u4e3b\u5f20\u5728\u5c71\u6d77\u5173\u5916\u516b\u91cc\u7684\u516b\u91cc\u94fa\u7b51\u57ce\u5b88\u5fa1\u3002\u4ed6\u4e00\u5b9a\u60f3\uff0c\u79bb\u5c71\u6d77\u5173\u592a\u8fdc\uff0c\u9003\u4e0d\u56de\u6765\uff0c\u90a3\u600e\u4e48\u5f97\u4e86\uff1f\u8881\u5d07\u7115\u8ba4\u4e3a\u53ea\u5b88\u516b\u91cc\u7684\u571f\u5730\u6ca1\u6709\u7528\uff0c\u5916\u56f4\u9635\u5730\u592a\u7a84\uff0c\u8d77\u4e0d\u4e86\u5c4f\u969c\u5c71\u6d77\u5173\u7684\u4f5c\u7528\uff0c\u548c\u738b\u5728\u664b\u4e89\u8bba\uff0c\u738b\u4e0d\u91c7\u7eb3\u4ed6\u7684\u610f\u89c1\u3002\u4e8e\u662f\u8881\u5d07\u7115\u53bb\u5411\u9996\u8f85\u53f6\u5411\u9ad8\u7533\u8bf7\uff0c\u53f6\u4e5f\u4e0d\u7406\u3002 \u3000\u3000\u8881\u5d07\u7115\u7684\u4e3b\u5f20\u867d\u7136\u6b63\u786e\uff0c\u7136\u800c\u548c\u9876\u5934\u4e0a\u53f8\u4e89\u8bba\u4e86\u4e00\u573a\u4e4b\u540e\uff0c\u610f\u89c1\u4e0d\u8499\u91c7\u7eb3\uff0c\u7adf\u5f84\u81ea\u53bb\u5411\u6700\u9ad8\u884c\u653f\u9996\u957f\u6295\u8bc9\u3002\u8d8a\u7ea7\u5448\u62a5\u662f\u5b98\u573a\u5927\u5fcc\uff0c\u4ed6\u505a\u5b98\u7684\u65b9\u5f0f\u5374\u5927\u5927\u4e0d\u5bf9\u4e86\u3002\u8fd9\u53c8\u662f\u4ed6\u86ee\u52b2\u7684\u8868\u73b0\u4e4b\u4e00\u3002 \u3000\u3000\u8fd9\u65f6\u5b81\u8fdc\u4e4b\u5317\u7684\u5341\u4e09\u5c71\u6709\u8d25\u5352\u96be\u6c11\u5341\u4f59\u4e07\u4eba\uff0c\u7ed9\u6e05\u5175\u56f0\u4f4f\u4e86\u4e0d\u80fd\u51fa\u6765\u3002\u671d\u5ef7\u53eb\u5927\u5b66\u58eb\u5b59\u627f\u5b97\u8bbe\u6cd5\u89e3\u6551\u3002\u8881\u5d07\u7115\u7533\u8bf7\u7531\u81ea\u5df1\u5e26\u5175\u4e94\u5343\u8fdb\u9a7b\u5b81\u8fdc\u4f5c\u58f0\u63f4\u3002\u53e6\u6d3e\u9a81\u5c06\u5230\u5341\u4e09\u5c71\u53bb\u6551\u56de\u6e83\u6563\u4e86\u7684\u90e8\u961f\u548c\u96be\u6c11\u3002\u738b\u5728\u664b\u89c9\u5f97\u8fd9\u4e2a\u519b\u4e8b\u884c\u52a8\u592a\u5192\u9669\uff0c\u4e0d\u52a0\u91c7\u7eb3\u3002\u7ed3\u679c\u5341\u4f59\u4e07\u8d25\u5352\u96be\u6c11\u90fd\u88ab\u6e05\u5175\u4fd8\u864f\uff0c\u53ea\u6709\u516d\u5343\u4eba\u9003\u56de\u3002 \u3000\u3000\u6ee1\u6e05\u8fd9\u65f6\u5728\u7ecf\u6d4e\u4e0a\u5b9e\u884c\u5974\u96b6\u5236\u5ea6\uff0c\u5973\u771f\u4eba\u5f53\u5175\u6253\u4ed7\uff0c\u4ee5\u62a2\u52ab\u8d22\u7269\u4e3a\u4e3b\u8981\u5de5\u4f5c\uff0c\u8ba4\u4e3a\u7537\u5b50\u6c49\u8015\u7530\u79cd\u5730\u662f\u803b\u8fb1\uff0c\u6240\u4ee5\u4fd8\u864f\u4e86\u6c49\u4eba\u548c\u671d\u9c9c\u4eba\u6765\u8015\u79cd\u3002\u6c49\u4eba\u3001\u671d\u9c9c\u4eba\u7684\u5974\u96b6\u662f\u53ef\u4ee5\u4e70\u5356\u7684\uff0c\u5f53\u65f6\u4ef7\u683c\u662f\u6bcf\u4e2a\u7cbe\u58ee\u6c49\u4eba\u7ea6\u4e3a\u5341\u516b\u4e24\u94f6\u5b50\uff0c\u6216\u6362\u8015\u725b\u4e00\u5934\u2466\u3002\u5341\u4e09\u5c71\u7684\u5341\u591a\u4e07\u6c49\u4eba\u88ab\u4fd8\u864f\u4e86\u53bb\uff0c\u90fd\u6210\u4e3a\u5974\u96b6\uff0c\u56fa\u7136\u53d7\u82e6\u4e0d\u582a\uff0c\u540c\u65f6\u66f4\u5927\u5927\u589e\u52a0\u4e86\u6ee1\u6e05\u7684\u7ecf\u6d4e\u529b\u91cf\u3002 \u3000\u3000\u90a3\u65f6\u8881\u5d07\u7115\u4ecd\u662f\u6781\u529b\u4e3b\u5f20\u7b51\u57ce\u5b81\u8fdc\u3002\u671d\u5ef7\u4e2d\u7684\u5927\u81e3\u90fd\u53cd\u5bf9\uff0c\u8ba4\u4e3a\u5b81\u8fdc\u592a\u8fdc\uff0c\u5b88\u4e0d\u4f4f\u3002\u5927\u5b66\u58eb\u5b59\u627f\u5b97\u662f\u4e2a\u6709\u89c1\u8bc6\u4e4b\u4eba\uff0c\u4eb2\u81ea\u51fa\u5173\u5de1\u89c6\uff0c\u4e86\u89e3\u5177\u4f53\u60c5\u51b5\uff0c\u63a5\u53d7\u4e86\u8881\u5d07\u7115\u7684\u770b\u6cd5\u3002 \u3000\u3000\u4e0d\u4e45\u5b59\u627f\u5b97\u4ee3\u738b\u5728\u664b\u4f5c\u8fbd\u4e1c\u4e3b\u5e05\u3002\u5929\u542f\u4e8c\u5e74\u4e5d\u6708\uff0c\u5b59\u627f\u5b97\u6d3e\u8881\u5d07\u7115\u4e0e\u526f\u5c06\u6ee1\u6842\u5e26\u5175\u9a7b\u5b88\u5b81\u8fdc\uff0c\u8fd9\u662f\u8881\u5d07\u7115\u9886\u519b\u7684\u5f00\u59cb\u3002 \u3000\u3000\u6ee1\u6842\u662f\u8499\u53e4\u4eba\uff0c\u9a81\u52c7\u5584\u6218\u3002\u4ece\u90a3\u65f6\u8d77\uff0c\u4ed6\u548c\u8881\u5d07\u7115\u7684\u547d\u8fd0\u5c31\u6c38\u8fdc\u7ed3\u5408\u5728\u4e00\u8d77\uff0c\u518d\u4e5f\u5206\u4e0d\u5f00\u4e86\u3002\u4e00\u4e2a\u8499\u53e4\u6b66\u5c06\uff0c\u4e00\u4e2a\u5e7f\u4e1c\u7edf\u5e05\uff0c\u90fd\u662f\u5341\u5206\u521a\u786c\u3001\u5341\u5206\u5014\u5f3a\u7684\u813e\u6c14\u3002\u4e24\u4eba\u4e00\u8d77\u7ecf\u5386\u4e86\u591a\u6b21\u751f\u6b7b\u60a3\u96be\uff0c\u4e5f\u6709\u8fc7\u4e0d\u77e5\u591a\u5c11\u6b21\u6fc0\u70c8\u7684\u4e89\u5435\u3002\u4e00\u76f4\u5230\u6b7b\uff0c\u4e24\u4eba\u4ecd\u662f\u5728\u4e89\u5435\u3002\u4f46\u5728\u4e24\u4eba\u7684\u5185\u5fc3\uff0c\u5374\u53c8\u4e00\u5b9a\u662f\u4e92\u76f8\u94a6\u4f69\u3002\u90a3\u65e2\u662f\u82f1\u96c4\u91cd\u82f1\u96c4\u7684\u5fc3\u60c5\uff0c\u53c8\u77e5\u9053\u5728\u6297\u62d2\u6e05\u5175\u5927\u654c\u4e4b\u65f6\uff0c\u975e\u4ef0\u4ed7\u5bf9\u65b9\u7684\u529b\u91cf\u4e0d\u53ef\u3002\u9ad8\u660e\u7684\u7ec4\u7ec7\u624d\u80fd\u548c\u6b63\u786e\u7684\u6218\u7565\u51b3\u7b56\u662f\u5fc5\u8981\u7684\uff0c\u4eb2\u4e34\u524d\u654c\u3001\u6b8a\u6b7b\u51b3\u6218\u7684\u521a\u52c7\u4e5f\u662f\u5fc5\u8981\u7684\u3002 \u3000\u3000\u5b81\u8fdc\u5728\u5c71\u6d77\u5173\u5916\u4e8c\u767e\u4f59\u91cc\uff0c\u53ea\u5b88\u516b\u91cc\u548c\u5b88\u5230\u4e8c\u767e\u591a\u91cc\u4ee5\u5916\uff0c\u6218\u7565\u5f62\u52bf\u5f53\u7136\u5927\u6709\u533a\u522b\u3002 \u3000\u3000\u5b81\u8fdc\u73b0\u5728\u53eb\u4f5c\u5174\u57ce\uff0c\u6709\u94c1\u8def\u7ecf\u8fc7\uff0c\u662f\u9526\u5dde\u4e0e\u5c71\u6d77\u5173\u4e4b\u95f4\u7684\u4e2d\u95f4\u7ad9\u3002\u5730\u6ee8\u8fde\u5c71\u6e7e\uff0c\u4e0e\u846b\u82a6\u5c9b\u76f8\u8ddd\u751a\u8fd1\u3002\u6211\u771f\u76fc\u671b\u5c06\u6765\u603b\u6709\u4e00\u65e5\u80fd\u5230\u5174\u57ce\u53bb\u4f4f\u51e0\u5929\uff0c\u597d\u597d\u7684\u770b\u770b\u8fd9\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u5929\u542f\u4e09\u5e74\u4e5d\u6708\uff0c\u8881\u5d07\u7115\u5230\u8fbe\u5b81\u8fdc\u3002 \u3000\u3000\u672c\u6765\uff0c\u5b59\u627f\u5b97\u5df2\u6d3e\u6e38\u51fb\u7956\u5927\u5bff\u5728\u5b81\u8fdc\u7b51\u57ce\uff0c\u4f46\u7956\u5927\u5bff\u6599\u60f3\u660e\u519b\u4e00\u5b9a\u5b88\u4e0d\u4f4f\u7684\uff0c\u53ea\u7b51\u4e86\u5341\u5206\u4e4b\u4e00\uff0c\u6577\u884d\u4e86\u4e8b\u3002 \u3000\u3000\u8881\u5d07\u7115\u5230\u540e\uff0c\u5f53\u5373\u5927\u5f20\u65d7\u9f13\u3001\u96f7\u5389\u98ce\u884c\u7684\u8fdb\u884c\u7b51\u57ce\uff0c\u7acb\u4e86\u89c4\u683c\uff1a\u57ce\u5899\u9ad8\u4e09\u4e08\u4e8c\u5c3a\uff0c\u57ce\u96c9\u518d\u9ad8\u516d\u5c3a\uff0c\u57ce\u5899\u5899\u5740\u5e7f\u4e09\u4e08\uff0c\u6d3e\u7956\u5927\u5bff\u7b49\u7763\u5de5\u3002\u8881\u5d07\u7115\u4e0e\u5c06\u58eb\u540c\u7518\u5171\u82e6\uff0c\u5584\u5f85\u767e\u59d3\uff0c\u5f53\u4ed6\u4eec\u662f\u5bb6\u4eba\u7236\u5144\u4e00\u822c\uff0c\u6240\u4ee5\u7b51\u57ce\u65f6\u4eba\u4eba\u5c3d\u529b\u3002\u6b21\u5e74\u5b8c\u5de5\uff0c\u57ce\u9ad8\u5899\u539a\uff0c\u6210\u4e3a\u5173\u5916\u7684\u91cd\u9547\u3002\u8fd9\u5ea7\u57ce\u5899\u662f\u8881\u5d07\u7115\u4e00\u751f\u529f\u4e1a\u7684\u57fa\u7840\u3002\u8fd9\u5ea7\u57ce\u5899\u628a\u6ee1\u6e05\u91cd\u5175\u6321\u5728\u5c71\u6d77\u5173\u5916\u8fbe\u4e8c\u5341\u4e00\u5e74\u4e4b\u4e45\uff0c\u5982\u679c\u4e0d\u662f\u5434\u4e09\u6842\u628a\u6e05\u5175\u5f15\u8fdb\u5173\u6765\uff0c\u4e0d\u77e5\u9053\u8fd8\u8981\u963b\u6321\u591a\u5c11\u5e74\u3002 \u3000\u3000\u5173\u5916\u7ec8\u4e8e\u6709\u4e86\u4e00\u4e2a\u5b89\u5168\u7684\u5730\u65b9\u3002\u8fd9\u4e9b\u5e74\u6765\uff0c\u8fbd\u4e1c\u8fbd\u897f\u7684\u6c49\u4eba\u6d41\u79bb\u5931\u6240\uff0c\u82e5\u662f\u7ed9\u6ee1\u6d32\u4eba\u63b3\u53bb\uff0c\u4fbf\u6210\u4e86\u5974\u96b6\uff0c\u4e8e\u662f\u5173\u5916\u7684\u6c49\u4eba\u7eb7\u7eb7\u6d8c\u5230\uff0c\u8fdc\u8fd1\u89c6\u4e3a\u4e50\u571f\uff0c\u4eba\u53e3\u5927\u589e\u3002\u5b81\u8fdc\u57ce\u4e00\u7b51\u6210\uff0c\u660e\u671d\u7684\u56fd\u9632\u524d\u7ebf\u5411\u5317\u63a8\u79fb\u4e86\u4e8c\u767e\u4f59\u91cc\u3002 \u3000\u3000\u8881\u5d07\u7115\u540c\u65f6\u5f00\u59cb\u6574\u996c\u519b\u7eaa\uff0c\u4ed6\u53d1\u73b0\u4e00\u540d\u6821\u5b98\u865a\u62a5\u5175\u989d\uff0c\u541e\u6ca1\u7cae\u9977\uff0c\u86ee\u5b50\u813e\u6c14\u53d1\u4f5c\uff0c\u5f53\u5373\u5c06\u4ed6\u6740\u4e86\u3002\u4f46\u6309\u7167\u89c4\u5b9a\uff0c\u4ed6\u662f\u65e0\u6743\u64c5\u81ea\u5904\u65a9\u519b\u5b98\u7684\u3002\u5b59\u627f\u5b97\u5927\u6012\uff0c\u9a82\u4ed6\u8d8a\u6743\u3002\u8881\u5d07\u7115\u53e9\u5934\u8c22\u7f6a\u3002\u5b59\u627f\u5b97\u4e5f\u5c31\u7b97\u4e86\u3002\u4ed6\u540e\u6765\u64c5\u6740\u6bdb\u6587\u9f99\uff0c\u5728\u8fd9\u65f6\u53ef\u8bf4\u5df2\u4f0f\u4e0b\u4e86\u56e0\u5b50\u3002 \u3000\u3000\u5b59\u627f\u5b97\u4e5f\u662f\u4e2a\u79ef\u6781\u8fdb\u53d6\u578b\u7684\u4eba\u7269\uff0c\u8fd9\u65f6\u5411\u671d\u5ef7\u8bf7\u9977\u4e8c\u5341\u56db\u4e07\u4e24\uff0c\u51c6\u5907\u5bf9\u6e05\u519b\u53d1\u52a8\u8fdb\u653b\u3002\u5b59\u627f\u5b97\u662f\u6559\u5929\u542f\u7687\u5e1d\u8bfb\u4e66\u7684\u8001\u5e08\uff0c\u5929\u542f\u5bf9\u8001\u5e08\u5f88\u4e0d\u9519\uff0c\u7acb\u523b\u5c31\u6279\u51c6\u4e86\u3002\u4f46\u5175\u90e8\u5c1a\u4e66\u4e0e\u5de5\u90e8\u5c1a\u4e66\u4e92\u76f8\u5546\u8bae\u8bf4\uff1a\u201c\u519b\u9977\u4e00\u8db3\uff0c\u6b64\u4eba\u5c31\u8981\u5984\u52a8\u4e86\u3002\u201d\u6240\u4ee5\u51b3\u5b9a\u4e0d\u8ba9\u4ed6\u201c\u9977\u8db3\u201d\uff0c\u91c7\u53d6\u516c\u6587\u65c5\u884c\u7684\u62d6\u5ef6\u529e\u6cd5\uff0c\u4f7f\u5b59\u627f\u5b97\u7684\u6218\u7565\u65e0\u6cd5\u8fdb\u884c\u3002\u5b59\u627f\u5b97\u4e8e\u662f\u8fdb\u884c\u5c6f\u7530\u653f\u7b56\uff0c\u7531\u519b\u58eb\u81ea\u8015\u81ea\u98df\uff0c\u5374\u4e5f\u5f97\u5230\u5f88\u5927\u7684\u6210\u6548\u3002 \u3000\u3000\u5929\u542f\u56db\u5e74\uff0c\u8881\u5d07\u7115\u4e0e\u5927\u5c06\u9a6c\u4e16\u9f99\u3001\u738b\u4e16\u94a6\u7b49\u7387\u9886\u4e00\u4e07\u4e8c\u5343\u540d\u9a91\u5175\u6b65\u5175\u4e1c\u5de1\u5e7f\u5b81\u3002\u5e7f\u5b81\u5373\u4eca\u5317\u9547\u53bf\uff0c\u5728\u9526\u5dde\u4e4b\u5317\uff0c\u79bb\u6ee1\u6e05\u91cd\u9547\u6c88\u9633\u5df2\u4e0d\u8fdc\u4e86\u3002\u8881\u5d07\u7115\u8fd8\u6ca1\u6709\u548c\u6e05\u5175\u4ea4\u8fc7\u624b\uff0c\u8fd9\u6b21\u5df2\u542b\u6709\u4e3b\u52a8\u6311\u6218\u7684\u610f\u5473\u3002\u4f46\u6e05\u5175\u6ca1\u6709\u5e94\u6218\u3002\u8881\u5d07\u7115\u4e00\u519b\u7ecf\u5927\u51cc\u6cb3\u7684\u51fa\u53e3\u5341\u4e09\u5c71\uff0c\u4ece\u6d77\u9053\u8fd8\u5b81\u8fdc\u3002\u8fd9\u65f6\u6e05\u5175\u5df2\u9000\u51fa\u5341\u4e09\u5c71\u3002 \u3000\u3000\u8881\u5d07\u7115\u8fd9\u6b21\u9646\u6d77\u51fa\u5de1\uff0c\u5199\u4e86\u4e00\u9996\u8bd7\uff0c\u9898\u76ee\u662f\u300a\u5055\u8bf8\u5c06\u6e38\u6d77\u5c9b\u300b\uff0c\u4e0d\u8bf4\u201c\u7387\u8bf8\u5c06\u201d\u800c\u8bf4\u201c\u5055\u8bf8\u5c06\u201d\uff0c\u4e0d\u8bf4\u201c\u5de1\u6d77\u5c9b\u201d\u800c\u8bf4\u201c\u6e38\u6d77\u5c9b\u201d\uff0c\u9887\u6709\u5112\u5c06\u7684\u96c5\u91cf\u9ad8\u81f4\u3002\u8bd7\u4e2d\u5f88\u6e05\u695a\u7684\u6292\u5199\u4e86\u4ed6\u7684\u5fc3\u60c5\uff1a\u662f\u6218\u662f\u5b88\u7684\u65b9\u7565\u82e6\u53d7\u671d\u5ef7\u7275\u5236\uff0c\u4e0d\u80fd\u81ea\u7531\uff0c\u89c1\u5230\u5927\u597d\u6cb3\u5c71\uff0c\u66f4\u52a0\u6df1\u4e86\u5fe7\u6101\u3002\u5bf9\u8363\u534e\u5bcc\u8d35\u6211\u65e9\u5df2\u770b\u5f97\u6781\u6de1\uff0c\u6ee1\u8154\u5fe0\u6124\uff0c\u5374\u53ea\u6015\u522b\u4eba\u8981\u8bf4\u662f\u675e\u4eba\u5fe7\u5929\u3002\u5916\u654c\u7684\u4fb5\u72af\u6700\u540e\u603b\u662f\u80fd\u5e73\u5b9a\u7684\uff0c\u4f46\u671d\u5ef7\u4e2d\u4e89\u6743\u593a\u5229\u7684\u6597\u4e89\u5374\u5b9e\u662f\u5927\u60a3\uff0c\u4e0d\u77e5\u51e0\u65f6\u65b9\u80fd\u505c\u6b62\uff1f\u770b\u5230\u5929\u4e0a\u6d6e\u4e91\uff0c\u51b7\u6e05\u6e05\u7684\u6708\u4eae\uff0c\u53c8\u60f3\u5230\u6211\u7236\u4eb2\u901d\u4e16\uff0c\u4f24\u5fc3\u5f97\u80a0\u4e5f\u8981\u65ad\u4e86\u2467\u3002 \u3000\u3000\u77ed\u77ed\u4e09\u56db\u5e74\u4e4b\u95f4\uff0c\u4ece\u4eac\u5e08\u6212\u4e25\u5230\u4e1c\u5de1\u5e7f\u5b81\uff0c\u519b\u4e8b\u4ece\u5b88\u52bf\u8f6c\u4e3a\u653b\u52bf\uff0c\u8fd9\u4e3b\u8981\u662f\u5b59\u627f\u5b97\u4e3b\u6301\u4e4b\u529f\uff0c\u800c\u8881\u5d07\u7115\u4e5f\u8d21\u732e\u4e86\u5f88\u591a\u65b9\u7565\u3002 \u3000\u3000\u5b59\u627f\u5b97\u5f88\u8d4f\u8bc6\u4ed6\uff0c\u5c3d\u529b\u52a0\u4ee5\u63d0\u62d4\u3002\u8881\u5d07\u7115\u56e0\u529f\u5347\u4e3a\u5175\u5907\u526f\u4f7f\uff0c\u518d\u5347\u53f3\u53c2\u653f\u3002\u5b59\u627f\u5b97\u5bf9\u4ed6\u8a00\u542c\u8ba1\u4ece\uff0c\u59d4\u4efb\u751a\u4e13\u3002 \u3000\u3000\u5929\u542f\u4e94\u5e74\u590f\uff0c\u4e00\u5207\u51c6\u5907\u5c31\u7eea\uff0c\u5b59\u627f\u5b97\u6839\u636e\u8881\u5d07\u7115\u7684\u7b56\u5212\uff0c\u6d3e\u9063\u8bf8\u5c06\u5206\u5c6f\u9526\u5dde\u3001\u677e\u5c71\u3001\u674f\u5c71\u3001\u53f3\u5c6f\u3001\u5927\u51cc\u6cb3\u3001\u5c0f\u51cc\u6cb3\u8bf8\u8981\u585e\uff0c\u53c8\u5411\u5317\u63a8\u8fdb\u4e86\u4e8c\u767e\u91cc\uff0c\u51e0\u4e4e\u5b8c\u5168\u6536\u590d\u4e86\u8fbd\u6cb3\u4ee5\u897f\u7684\u65e7\u5730\uff0c\u8fd9\u65f6\u5b81\u8fdc\u53c8\u53d8\u6210\u5185\u5730\u4e86\u3002 \u3000\u3000\u6e05\u5175\u89c1\u654c\u4eba\u7a33\u624e\u7a33\u6253\uff0c\u6b65\u6b65\u4e3a\u8425\u7684\u63a8\u8fdb\uff0c\u56db\u5e74\u4e4b\u4e2d\u4e5f\u4e0d\u6562\u6765\u72af\u3002\u7136\u800c\u8fdb\u653b\u7684\u51c6\u5907\u5de5\u4f5c\u5374\u505a\u5f97\u5341\u5206\u79ef\u6781\uff0c\u52aa\u5c14\u54c8\u8d64\u5c06\u4eac\u57ce\u4ece\u592a\u5b50\u6cb3\u53f3\u5cb8\u7684\u4e1c\u4eac\u57ce\u79fb\u5230\u4e86\u6c88\u9633\uff0c\u4ee5\u4fbf\u4e8e\u5357\u4e0b\u653b\u660e\u3001\u897f\u53d6\u8499\u53e4\uff0c\u4fdd\u6301\u5145\u5206\u7684\u51fa\u51fb\u59ff\u6001\u3002 \u3000\u3000\u5b59\u627f\u5b97\u6709\u624d\u8bc6\uff0c\u6709\u62c5\u5f53\uff0c\u6709\u6c14\u9b44\uff0c\u8881\u5d07\u7115\u5bf9\u4ed6\u65e2\u94a6\u4f69\uff0c\u53c8\u6709\u77e5\u9047\u7684\u611f\u6fc0\uff0c\u8fd9\u6837\u7684\u4e0a\u53f8\u662f\u6781\u96be\u9047\u5230\u7684\u3002\u773c\u89c1\u4ed6\u548c\u5b59\u627f\u5b97\u7684\u5171\u540c\u8ba1\u5212\u6b63\u5728\u4e00\u6b65\u6b65\u7684\u5b9e\u73b0\uff0c\u6309\u90e8\u5c31\u73ed\u7684\u6536\u590d\u5931\u5730\uff0c\u8fd9\u51e0\u5e74\u8881\u5d07\u7115\u4e00\u5b9a\u8fc7\u5f97\u5341\u5206\u5feb\u4e50\u3002\u4ed6\u548c\u624b\u4e0b\u5c06\u9886\u6ee1\u6842\u3001\u5de6\u8f85\u3001\u6731\u6885\u3001\u7956\u5927\u5bff\u3001\u4f55\u53ef\u7eb2\u3001\u8d75\u7387\u6559\u3001\u5b59\u7956\u5bff\u7b49\u4eba\u7684\u6218\u6597\u53cb\u8c0a\uff0c\u4e5f\u5728\u8fd9\u4e9b\u65e5\u5b50\u4e2d\u4e0d\u65ad\u52a0\u6df1\u3002 \u3000\u3000\u53ef\u662f\u597d\u666f\u4e0d\u5e38\uff0c\u65f6\u5c40\u6e10\u6e10\u53d8\u574f\u3002\u5929\u542f\u7687\u5e1d\u71b9\u5b97\u8d8a\u6765\u8d8a\u559c\u6b22\u505a\u6728\u5de5\u3002\u9b4f\u5fe0\u8d24\u7684\u6743\u529b\u8d8a\u6765\u8d8a\u5927\uff0c\u5c3d\u91cf\u53d1\u6325\u4ed6\u5730\u75de\u6d41\u6c13\u6027\u683c\u4e2d\u7684\u65e0\u8d56\u3001\u65e0\u77e5\u3001\u65e0\u803b\u3001\u4ee5\u53ca\u65e0\u6cd5\u65e0\u5929\u3002 \u3000\u3000\u5929\u542f\u4e94\u5e74\uff0c\u9b4f\u5fe0\u8d24\u5927\u4e3e\u5c60\u622e\u671d\u5ef7\u91cc\u7684\u6b63\u4eba\u541b\u5b50\uff0c\u5c06\u5f39\u52be\u4ed6\u4e8c\u5341\u56db\u6761\u5927\u7f6a\u7684\u6768\u6d9f\u4e0b\u72f1\u3002\u540c\u65f6\u4e0b\u72f1\u7684\u6709\u5de6\u5149\u6597\u3001\u9b4f\u5927\u4e2d\u3001\u8881\u5316\u4e2d\u7b49\u5927\u81e3\uff0c\u6240\u8bec\u9677\u7684\u7f6a\u540d\u662f\u8d2a\u6c61\u3002\u767e\u59d3\u5927\u6124\uff0c\u6570\u4e07\u58eb\u6c11\u5728\u5317\u4eac\u8857\u9053\u4e0a\u547c\u53eb\u5927\u54ed\u3002\u9b4f\u5fe0\u8d24\u4e0d\u6562\u6b63\u5f0f\u5ba1\u8baf\uff0c\u547d\u72f1\u5352\u5728\u76d1\u72f1\u4e2d\u6253\u6b7b\u4e86\u8fd9\u4e9b\u5927\u81e3\u3002\u6768\u6d9f\u6b7b\u5f97\u6700\u60e8\uff0c\u571f\u56ca\u538b\u8eab\uff0c\u94c1\u9489\u8d2f\u8033\u3002 \u3000\u3000\u4e0d\u4e45\uff0c\u9b4f\u5fe0\u8d24\u53c8\u6740\u718a\u5ef7\u5f3c\u3002 \u3000\u3000\u718a\u5ef7\u5f3c\u5728\u8fbd\u4e1c\u7acb\u6709\u5927\u529f\uff0c\u8499\u51a4\u5165\u72f1\uff0c\u767e\u59d3\u90fd\u5f88\u540c\u60c5\u4ed6\u3002\u6c11\u95f4\u6d41\u4f20\u4e00\u90e8\u7ee3\u50cf\u6f14\u4e49\u5c0f\u8bf4\u300a\u8fbd\u4e1c\u4f20\u300b\uff0c\u63cf\u5199\u718a\u5ef7\u5f3c\u5b88\u8fbd\u4e1c\u7684\u82f1\u52c7\u4e8b\u8ff9\u3002\u9b4f\u5fe0\u8d24\u7684\u5f92\u515a\u4e2d\u6709\u4e00\u4e2a\u540d\u53eb\u51af\u94e8\u7684\uff0c\u4ed6\u7236\u4eb2\u5f53\u5e74\u5728\u8fbd\u4e1c\u4f5c\u5e03\u653f\u7684\u5b98\uff0c\u6e05\u5175\u672a\u5230\uff0c\u5148\u5c31\u9f20\u7a9c\u5357\u9003\u3002\u300a\u8fbd\u4e1c\u4f20\u300b\u7b2c\u56db\u5341\u516b\u56de\u6709\u201c\u51af\u5e03\u653f\u7236\u5b50\u5954\u9003\u201d\u4e00\u8282\uff0c\u63cf\u5199\u51af\u94e8\u7236\u5b50\u5f03\u804c\u800c\u9003\u7684\u72fc\u72c8\u4e11\u6001\uff0c\u53ef\u8bf4\u662f\u5f53\u65f6\u7684\u201c\u65b0\u95fb\u4f53\u5c0f\u8bf4\u201d\u3002 \u3000\u3000\u51af\u94e8\u5bf9\u8fd9\u4e8b\u6df1\u4e3a\u6000\u6068\uff0c\u53c8\u8981\u8ba8\u597d\u9b4f\u5fe0\u8d24\uff0c\u4e8e\u662f\u4e70\u4e86\u4e00\u90e8\u300a\u8fbd\u4e1c\u4f20\u300b\u653e\u5728\u8863\u8896\u91cc\uff0c\u89c1\u5230\u71b9\u5b97\u540e\uff0c\u628a\u5c0f\u8bf4\u62ff\u51fa\u6765\uff0c\u8bec\u544a\u8bf4\uff1a\u201c\u8fd9\u90e8\u6f14\u4e49\u5c0f\u8bf4\u662f\u718a\u5ef7\u5f3c\u4f5c\u7684\uff0c\u4ed6\u5439\u5618\u81ea\u5df1\u7684\u529f\u52b3\uff0c\u60f3\u8981\u514d\u7f6a\u3002\u201d\u71b9\u5b97\u4fe1\u4ee5\u4e3a\u771f\uff0c\u767b\u65f6\u5927\u6012\u3002\u5927\u6982\u4ed6\u770b\u5230\u5c0f\u8bf4\u4e2d\u7684\u7ee3\u50cf\u5c06\u718a\u5ef7\u5f3c\u753b\u5f97\u5a01\u98ce\u51db\u51db\uff0c\u800c\u6587\u5b57\u4e2d\u6216\u8bb8\u5bf9\u7687\u5e1d\u8fd8\u9887\u6709\u8bbd\u523a\uff0c\u4e8e\u662f\u5373\u523b\u4e0b\u65e8\u5c06\u718a\u5ef7\u5f3c\u65a9\u9996\uff0c\u8fd8\u5c06\u4ed6\u7684\u9996\u7ea7\u9001\u5230\u5404\u5904\u8fb9\u754c\u4e0a\u53bb\u7ed9\u5b88\u519b\u89c2\u770b\uff0c\u90a3\u5c31\u53eb\u505a\u201c\u4f20\u9996\u4e5d\u8fb9\u201d\uff0c\u8bf4\u4ed6\u72af\u4e86\u4e0d\u6218\u7684\u5927\u7f6a\u3002\u7136\u800c\u771f\u6b63\u5e94\u5f53\u8d1f\u8d23\u7684\u738b\u5316\u8d1e\u53cd\u800c\u4e0d\u6740\u3002 \u3000\u3000\u6587\u5b57\u72f1\u4e5f\u5f00\u59cb\u53d1\u5c55\u3002\u6c5f\u82cf\u592a\u4ed3\u7684\u4e24\u4e2a\u6587\u4eba\u4f5c\u8bd7\u54c0\u60bc\u718a\u5ef7\u5f3c\uff0c\u90fd\u88ab\u52a0\u4ee5\u201c\u8bfd\u8c24\u201d\u7f6a\u540d\u800c\u5904\u65a9\u3002 \u3000\u3000\u9b4f\u5fe0\u8d24\u559c\u6b22\u6587\u5b98\u6b66\u5c06\u9001\u4ed6\u8d3f\u8d42\uff0c\u8d8a\u591a\u8d8a\u597d\u3002\u5b59\u627f\u5b97\u5e26\u5175\u5341\u591a\u4e07\uff0c\u7cae\u9977\u5f88\u591a\uff0c\u5e94\u5f53\u5927\u91cf\u514b\u6263\u4e0b\u6765\u8f6c\u5949\u7ed9\u4ed6\u201c\u4e5d\u5343\u5c81\u201d\u624d\u662f\u3002\u5b59\u627f\u5b97\u4e0d\u80af\u8fd9\u6837\u529e\uff0c\u9b4f\u5fe0\u8d24\u81ea\u7136\u4e0d\u559c\u6b22\uff0c\u4e8e\u662f\u6d3e\u4e86\u4e2a\u5439\u725b\u62cd\u9a6c\u7684\u5c0f\u4eba\u9ad8\u7b2c\u53bb\u4ee3\u5b59\u627f\u5b97\u4f5c\u8fbd\u4e1c\u7ecf\u7565\u3002\u9ad8\u7b2c\u4e00\u5230\u4efb\uff0c\u7acb\u523b\u5c31\u8bf4\u5173\u5916\u4e4b\u5730\u4e0d\u53ef\u5b88\uff0c\u8981\u64a4\u53bb\u5173\u5916\u5404\u57ce\u7684\u5b88\u5fa1\uff0c\u5c06\u90e8\u961f\u5168\u90e8\u64a4\u5165\u5c71\u6d77\u5173\u3002 \u3000\u3000\u8fd9\u6218\u7565\u4e4b\u80e1\u6d82\uff0c\u771f\u662f\u4e0d\u53ef\u7406\u55bb\u3002\u90a3\u65f6\u6e05\u5175\u53c8\u6ca1\u6709\u6765\u653b\uff0c\u5b8c\u5168\u6ca1\u6709\u64a4\u5175\u9003\u547d\u7684\u5fc5\u8981\u3002\u5927\u6982\u4ed6\u662f\u6015\u4e00\u65e6\u6765\u653b\uff0c\u975e\u8d25\u4e0d\u53ef\uff0c\u8fd8\u662f\u5148\u884c\u64a4\u5175\u6bd4\u8f83\u5b89\u5168\u3002 \u3000\u3000\u8881\u5d07\u7115\u5f53\u7136\u6781\u529b\u53cd\u5bf9\uff0c\u5bf9\u9ad8\u7b2c\u8bf4\uff1a\u201c\u5175\u6cd5\u6709\u8fdb\u65e0\u9000\u3002\u8bf8\u57ce\u65e2\u5df2\u6536\u590d\uff0c\u600e\u53ef\u968f\u4fbf\u64a4\u9000\uff1f\u9526\u5dde\u3001\u53f3\u5c6f\u536b\u4e00\u52a8\u6447\uff0c\u5b81\u524d\u5c31\u9707\u60ca\uff0c\u5c71\u6d77\u5173\u4e5f\u5931\u4e86\u4fdd\u969c\u3002\u8fd9\u4e9b\u5916\u536b\u57ce\u6c60\u53ea\u8981\u6d3e\u826f\u5c06\u5b88\u5fa1\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u6709\u5371\u9669\u7684\u3002\u201d\u9ad8\u7b2c\u4e0d\u542c\uff0c\u4e0b\u4ee4\u5b81\u8fdc\u3001\u524d\u5c6f\u536b\u4e5f\u64a4\u5175\u3002 \u3000\u3000\u8881\u5d07\u7115\u5014\u5f3a\u5f97\u5f88\uff0c\u6297\u547d\u4e0d\u542c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u505a\u7684\u662f\u5b81\u524d\u9053\u7684\u5b98\uff0c\u5b88\u571f\u6709\u8d23\uff0c\u4e0e\u57ce\u5171\u5b58\u4ea1\uff0c\u51b3\u8ba1\u4e0d\u64a4\u3002\u201d \u3000\u3000\u9ad8\u7b2c\u662f\u80c6\u5c0f\u7684\u4e66\u751f\uff0c\u8881\u5d07\u7115\u867d\u662f\u4ed6\u90e8\u5c5e\uff0c\u4f46\u89c1\u4ed6\u86ee\u52b2\u53d1\u4f5c\uff0c\u58f0\u8272\u4ff1\u5389\u7684\u4e0d\u670d\u4ece\u547d\u4ee4\uff0c\u4e5f\u5c31\u4e0d\u6562\u5bf9\u4ed6\u600e\u6837\uff0c\u53ea\u662f\u4e0b\u4ee4\u5c06\u9526\u5dde\u3001\u53f3\u5c6f\u3001\u5927\u5c0f\u51cc\u6cb3\u3001\u677e\u5c71\u3001\u674f\u5c71\u7684\u5b88\u5175\u90fd\u64a4\u53bb\u4e86\uff0c\u653e\u5f03\u4e86\u7cae\u98df\u5341\u4f59\u4e07\u77f3\u3002\u64a4\u9000\u6beb\u65e0\u79e9\u5e8f\uff0c\u519b\u6c11\u6b7b\u4ea1\u8f7d\u9053\uff0c\u54ed\u58f0\u9707\u91ce\uff0c\u767e\u59d3\u548c\u5c06\u58eb\u90fd\u662f\u6c14\u6124\u96be\u5f53\u3002 \u3000\u3000\u8881\u5d07\u7115\u7684\u7236\u4eb2\u65e9\u4e00\u5e74\u6b7b\u4e86\uff0c\u6309\u7167\u89c4\u77e9\uff0c\u513f\u5b50\u5fc5\u987b\u56de\u5bb6\u5b88\u4e27\u3002\u5f53\u65f6\u671d\u5ef7\u4ee5\u519b\u4e8b\u7d27\u6025\uff0c\u4e0b\u65e8\u4e0d\u8bb8\u4ed6\u56de\u5bb6\uff0c\u547d\u4ed6\u5728\u804c\u5b88\u5236\uff0c\u79f0\u4e3a\u201c\u593a\u60c5\u201d\u3002\u8fd9\u65f6\u8881\u5d07\u7115\u5927\u6012\uff0c\u4e0a\u594f\u7ae0\u8981\u56de\u5bb6\u5b88\u5236\u3002\u671d\u5ef7\u4e0d\u51c6\uff0c\u4e3a\u4e86\u6170\u629a\u4ed6\uff0c\u5347\u4ed6\u4e3a\u6309\u5bdf\u4f7f\u3002\u4f46\u8fd9\u6837\u4e00\u6765\uff0c\u6570\u5e74\u8f9b\u8f9b\u82e6\u82e6\u7684\u7ecf\u8425\u6bc1\u4e8e\u4e00\u671d\u3002\u867d\u7136\u5347\u5b98\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u5f00\u5fc3\u3002 \u3000\u3000\u53ef\u4ee5\u60f3\u8c61\u5f97\u5230\uff0c\u8881\u5d07\u7115\u5728\u8fd9\u6bb5\u65f6\u671f\u4e2d\uff0c\u201c\xd7\u4ed6\u5988\u201d\u7684\u5e7f\u4e1c\u4e09\u5b57\u7ecf\u4e0d\u77e5\u9a82\u4e86\u51e0\u5343\u767e\u53e5\u3002\u4ed6\u662f\u8fdb\u58eb\uff0c\u7136\u800c\u4ee5\u4ed6\u7684\u6027\u683c\u800c\u9047\u4e0a\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4e0d\u9a82\u4e09\u5b57\u7ecf\u4f55\u4ee5\u6cc4\u5fc3\u4e2d\u4e4b\u6124\uff1f\u6216\u8bb8\u9ad8\u7b2c\u4e0d\u6562\u89c1\u4ed6\u7684\u9762\uff0c\u5426\u5219\u88ab\u4ed6\u9971\u4ee5\u8001\u62f3\u3001\u6bb4\u6253\u4e0a\u53f8\u7684\u4e8b\u90fd\u53ef\u80fd\u53d1\u751f\u3002 \u3000\u3000\u9ad8\u7b2c\uff0c\u5b57\u767b\u4e4b\uff0c\u4e07\u5386\u5341\u4e03\u5e74\u8fdb\u58eb\u3002\u4ed6\u8003\u8bd5\u679c\u7136\u201c\u9ad8\u7b2c\u767b\u4e4b\u201d\uff0c\u4f46\u505a\u5927\u519b\u7edf\u5e05\uff0c\u5374\u662f\u201c\u8981\u5730\u5f03\u4e4b\u201d\u3002 \u3000\u3000\u519b\u4e8b\u4e0a\u8fd9\u6837\u8352\u8c2c\u7684\u51b3\u7b56\uff0c\u5927\u6982\u53ea\u6709\u5f53\u4ee3\u5357\u8d8a\u962e\u6587\u7ecd\u4e3b\u52a8\u653e\u5f03\u987a\u5316\u3001\u5c98\u6e2f\uff0c\u5f03\u519b\u56db\u5341\u4e07\uff0c\u56e0\u800c\u5f15\u81f4\u5357\u8d8a\u5168\u9762\u6e83\u8d25\u4e00\u4e8b\uff0c\u53ef\u4ee5\u4e0e\u4e4b\u201c\u5ab2\u7f8e\u201d\u3002 \u3000\u3000\u2460\u5173\u4e8e\u8881\u5d07\u7115\u7684\u4e8b\u8ff9\uff0c\u5982\u672a\u6ce8\u660e\u51fa\u5904\uff0c\u4e3b\u8981\u7cfb\u4f9d\u636e\u300a\u660e\u53f2\xb7\u8881\u5d07\u7115\u4f20\u300b\u6240\u8f7d\u3002 \u3000\u3000\u2461\u8881\u5d07\u7115\u8003\u4e3e\u4eba\u65f6\uff0c\u6709\u201c\u79cb\u95f1\u8d4f\u6708\u201d\u8bd7\uff0c\u6709\u53e5\uff1a\u201c\u7af9\u53f6\u559c\u6dfb\u8c6a\u58eb\u5fd7\uff0c\u6842\u82b1\u9999\u63d2\u5c11\u5e74\u5934\u3002\u201d \u3000\u3000\u2462\u8881\u5d07\u7115\u300a\u52df\u4fee\u7f57\u6d6e\u8bf8\u540d\u80dc\u758f\u300b\uff1a\u201c\u4f59\u751f\u5e73\u6709\u5c71\u6c34\u4e4b\u7656\uff0c\u5373\u4e00\u4e18\u4e00\u58d1\uff0c\u4ff1\u4f4e\u5f8a\u4e0d\u5fcd\u53bb\u3002\u6545\u5341\u56db\u516c\u8f66\uff0c\u5f3a\u534a\u5728\u5916\uff0c\u8db3\u8ff9\u51e0\u904d\u5b87\u5185\u3002\u201d\u300a\u4e0b\u7b2c\u300b\u8bd7\u6709\u4e91\uff1a\u201c\u9047\u4e3b\u4eba\u5b81\u6613\uff0c\u9022\u65f6\u6211\u72ec\u96be\u3002\u516b\u5343\u601c\u5ba2\u8def\uff0c\u4e09\u5341\u5c1a\u5112\u51a0\u3002\u201d\u4ece\u4e1c\u839e\u5230\u5317\u4eac\uff0c\u7ea6\u516b\u5343\u91cc\u3002 \u3000\u3000\u2463\u4ed6\u5230\u6d59\u6c5f\u5d4a\u53bf\u6e38\u89c8\u65f6\uff0c\u4e0e\u597d\u53cb\u79e6\u516d\u90ce\u4e2d\u5bb5\u957f\u8c08\uff0c\u6709\u300a\u8bdd\u522b\u79e6\u516d\u90ce\u300b\u8bd7\uff1a\u201c\u6d77\u9cc4\u6ce2\u9cb8\u591c\u4e0d\u557e\uff0c\u6545\u4eba\u8c08\u5251\u5261\u6eaa\u5934\u3002\u8a00\u6df1\u591c\u534a\u72b9\u7591\u663c\uff0c\u9152\u51b7\u51c9\u751f\u59cb\u89c9\u79cb\u3002\u6c34\u56fd\u8299\u84c9\u4f4e\u7761\u6708\uff0c\u6c5f\u6e44\u6768\u67f3\u8f6f\u7ef4\u821f\u3002\u81ea\u601c\u4f5c\u8d4b\u975e\u738b\u7cb2\uff0c\u621b\u7389\u9e23\u91d1\u6709\u5c11\u6e38\u3002\u201d \u3000\u3000\u2464\u8881\u5d07\u7115\u5728\u300a\u5929\u542f\u4e8c\u5e74\u64e2\u4f65\u4e8b\u76d1\u519b\u594f\u65b9\u7565\u758f\u300b\u4e2d\u63d0\u51fa\u62db\u52df\u5175\u5458\u7684\u8981\u6c42\uff0c\u5ba3\u79f0\uff1a\u201c\u4ed6\u65e5\u6218\u4e4b\u4e0d\u529b\uff0c\u5373\u65a9\u81e3\u4e8e\u884c\u519b\u4e4b\u524d\uff0c\u4ee5\u4e3a\u8f7b\u4e8b\u8005\u6212\u3002\u201d\u6700\u540e\u8bf4\uff1a\u201c\u5982\u542c\u81e3\u4e4b\u8a00\uff0c\u884c\u81e3\u4e4b\u5fe0\uff0c\u81e3\u5fc5\u6548\u529b\u4ee5\u8212\u4eba\u795e\u4e4b\u6124\u3002\u4e0d\u4f46\u5de9\u56fa\u5c71\u6d77\uff0c\u5373\u5df2\u5931\u4e4b\u5c01\u7586\uff0c\u884c\u5c06\u590d\u4e4b\u3002\u8c0b\u5b9a\u800c\u6218\uff0c\u81e3\u6709\u5fae\u957f\u4e5f\u3002\u201d\u4ed6\u4e0a\u4efb\u540e\u7684\u7b2c\u4e00\u9053\u594f\u7ae0\uff0c\u4fbf\u63d0\u51fa\u4e86\u201c\u8c0b\u5b9a\u800c\u6218\u201d\u7684\u56db\u5b57\u8981\u8bc0\uff0c\u540c\u65f6\u4e5f\u81ea\u8c6a\u800c\u81ea\u4fe1\u7684\u8bf4\uff1a\u201c\u81e3\u6709\u5fae\u957f\u4e5f\u3002\u201d \u3000\u3000\u2465\u62db\u52df\u548c\u8c03\u96c6\u4e09\u5343\u540d\u5e7f\u4e1c\u5175\u3001\u516d\u5343\u540d\u5e7f\u897f\u5175\uff0c\u4e00\u5171\u5927\u7ea6\u82b1\u4e86\u4e8c\u5341\u4e07\u4e24\u94f6\u5b50\u3002\u636e\u8881\u5d07\u7115\u6240\u7533\u8bf7\u7684\u9884\u7b97\uff0c\u5e7f\u4e1c\u5175\u8981\u5b89\u5bb6\u3001\u884c\u7cae\u3001\u8863\u7532\u3001\u5668\u68b0\u7b49\u8d39\uff0c\u6bcf\u4eba\u4e8c\u5341\u4f59\u4e24\u3002\u5e7f\u897f\u72fc\u5175\u672c\u6765\u5c31\u662f\u5175\uff0c\u6240\u4ee5\u4e0d\u53d1\u5b89\u5bb6\u3001\u5175\u7532\u8d39\u7528\uff0c\u53ea\u9700\u4ece\u5e7f\u897f\u5230\u5173\u5916\u7684\u884c\u7cae\u6bcf\u4eba\u516d\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u2466\u8be6\u89c1\u738b\u949f\u7ff0\u300a\u6ee1\u65cf\u5728\u52aa\u5c14\u54c8\u9f50\u65f6\u4ee3\u7684\u793e\u4f1a\u7ecf\u6d4e\u5f62\u6001\u300b\u3001\u300a\u7687\u592a\u6781\u65f6\u4ee3\u6ee1\u65cf\u5411\u5c01\u5efa\u5236\u7684\u8fc7\u6e21\u300b\u3002 \u3000\u3000\u2467\u539f\u8bd7\u662f\uff1a\u201c\u6218\u5b88\u9036\u8fe4\u4e0d\u81ea\u7531\uff0c\u504f\u56e0\u80dc\u5730\u91cd\u6df1\u6101\u3002\u8363\u534e\u6211\u5df2\u77e5\u5e84\u68a6\uff0c\u5fe0\u6124\u4eba\u5c06\u8c13\u675e\u5fe7\u3002\u8fb9\u8845\u4e45\u5f00\u7ec8\u662f\u5b9a\uff0c\u5ba4\u6208\u65b9\u64cd\u51e0\u65f6\u4f11\uff1f\u7247\u4e91\u5b64\u6708\u5e94\u80a0\u65ad\uff0c\u6869\u6811\u51cb\u96f6\u53c8\u4e00\u79cb\u3002\u201d \u3000\u3000\u4e94 \u3000\u3000\u6ee1\u6e05\u770b\u51fa\u4e86\u660e\u671d\u7684\u865a\u5b9e\uff0c\u77e5\u9053\u9ad8\u7ecf\u7565\u65e0\u7528\uff0c\u8881\u5d07\u7115\u65e0\u4eba\u652f\u6301\uff0c\u4e8e\u5929\u542f\u516d\u5e74\u6b63\u6708\u5927\u4e3e\u6e21\u8fbd\u6cb3\u653b\u5b81\u8fdc\uff0c\u5175\u5341\u4e09\u4e07\uff08\u5728\u8fd9\u51e0\u5e74\u4e2d\uff0c\u6e05\u519b\u7684\u5b9e\u529b\u5df2\u6269\u5145\u4e86\u4e00\u500d\uff09\uff0c\u53f7\u79f0\u4e8c\u5341\u4e07\u3002\u4e8c\u5341\u4e09\u65e5\u653b\u62b5\u5b81\u8fdc\u3002 \u3000\u3000\u5927\u654c\u7ec8\u4e8e\u653b\u6765\u4e86\u3002 \u3000\u3000\u671d\u5ef7\u8352\u5510\uff0c\u4e3b\u5e05\u8352\u8c2c\uff0c\u63f4\u519b\u662f\u4e00\u5b9a\u4e0d\u4f1a\u6709\u7684\u3002\u90a3\u600e\u4e48\u529e\uff1f\u5f03\u57ce\u800c\u9000\u662f\u670d\u4ece\u4e3b\u5e05\u547d\u4ee4\uff1b\u5b88\u57ce\u7f62\uff0c\u5b81\u8fdc\u4e00\u57ce\u5b64\u519b\uff0c\u600e\u80fd\u6321\u6ee1\u6e05\u7684\u503e\u56fd\u4e4b\u5e08\uff1f \u3000\u3000\u5728\u8fd9\u7d27\u6025\u5173\u5934\uff0c\u8881\u5d07\u7115\u594b\u53d1\u4e86\u82f1\u96c4\u4e4b\u6c14\uff0c\u51b3\u610f\u6297\u654c\u3002 \u3000\u3000\u4ed6\u548c\u5927\u5c06\u6ee1\u6842\u3001\u526f\u5c06\u5de6\u8f85\u3001\u6731\u6885\uff0c\u53c2\u5c06\u7956\u5927\u5bff\u3001\u4f55\u53ef\u7eb2\u7b49\uff0c\u96c6\u5c06\u58eb\u8a93\u6b7b\u5b88\u57ce\u3002\u8881\u5d07\u7115\u523a\u51fa\u81ea\u5df1\u9c9c\u8840\uff0c\u5199\u6210\u6587\u544a\uff0c\u8ba9\u5c06\u58eb\u4f20\u9605\uff0c\u66f4\u5411\u58eb\u5352\u4e0b\u62dc\uff0c\u6fc0\u4ee5\u5fe0\u4e49\u3002\u5168\u519b\u4e0a\u4e0b\u5728\u4ed6\u7684\u6fc0\u52b1\u4e0b\u4eba\u4eba\u70ed\u8840\u6cb8\u817e\uff0c\u51b3\u5fc3\u6b7b\u6218\u3002 \u3000\u3000\u4ed6\u53c8\u4e0b\u4ee4\u524d\u5c6f\u5b88\u5c06\u8d75\u7387\u6559\u3001\u5c71\u6d77\u5173\u5b88\u5c06\u6768\u9e92\uff0c\u51e1\u662f\u5b81\u8fdc\u6709\u5175\u5c06\u9003\u56de\u6765\uff0c\u4e00\u6982\u6293\u4f4f\u65a9\u9996\u3002\u5c71\u6d77\u5173\u6709\u4ed6\u7684\u4e0a\u53f8\u8fbd\u4e1c\u7ecf\u7565\u9ad8\u7b2c\u9547\u5b88\uff0c\u8881\u5d07\u7115\u7684\u804c\u6743\u672c\u6765\u53ea\u80fd\u7ba1\u5230\u5b81\u8fdc\u548c\u524d\u5c6f\uff0c\u5c71\u6d77\u5173\u603b\u5175\u6768\u9e92\u4ed6\u662f\u7ba1\u4e0d\u7740\u7684\u3002\u4f46\u8fd9\u65f6\u8fd8\u7ba1\u4ed6\u751a\u4e48\u4e0a\u53f8\u4e0d\u4e0a\u53f8\uff0c\u804c\u6743\u4e0d\u804c\u6743\uff0c\u201c\xd7\u4ed6\u5988\uff0c\u9876\u786c\u4e0a\uff0c\u51e0\u5927\u5c31\u51e0\u5927\uff01\u201d\uff08\u6dde\u6caa\u4e4b\u6218\u65f6\uff0c\u5341\u4e5d\u8def\u519b\u5e7f\u4e1c\u5175\u5b88\u4e0a\u6d77\uff0c\u6297\u5fa1\u65e5\u519b\u4fb5\u7565\uff0c\u5f53\u65f6\u201c\xd7\u4ed6\u5988\uff0c\u9876\u786c\u4e0a\u201d\u7684\u5e7f\u4e1c\u4e09\u5b57\u7ecf\uff0c\u5728\u6c5f\u5357\u4e00\u5e26\u8d62\u5f97\u4e86\u4eba\u6c11\u7684\u70ed\u70c8\u5d07\u656c\u3002\u56e0\u4e3a\u5927\u5bb6\u90fd\u8bf4\uff1a\u5e7f\u4e1c\u5175\u4e00\u9a82\u201c\xd7\u4ed6\u5988\uff01\u201d\u5c31\u633a\u67aa\u51b2\u950b\uff0c\u5411\u65e5\u519b\u6740\u53bb\u4e86\u3002\uff09 \u3000\u3000\u4ed6\u6bcd\u4eb2\u548c\u59bb\u5b50\u8fd9\u65f6\u4e5f\u5728\u8fbd\u897f\uff0c\u5927\u6982\u4f4f\u5728\u5c71\u6d77\u5173\u6216\u524d\u5c6f\u536b\u540e\u65b9\u3002\u4ed6\u5c06\u6bcd\u4eb2\u548c\u59bb\u5b50\u90fd\u642c\u5230\u5b81\u8fdc\u57ce\u4e2d\u6765\u4f4f\u3002\u5168\u5bb6\u548c\u5b81\u8fdc\u5171\u5b58\u4ea1\u7684\u51b3\u5fc3\uff0c\u8868\u73b0\u5f97\u518d\u6e05\u695a\u4e5f\u6ca1\u6709\u4e86\u2460\u3002 \u3000\u3000\u4e8c\u5341\u56db\u65e5\uff0c\u6e05\u5175\u5230\u8fbe\u57ce\u4e0b\u3002\u8881\u5d07\u7115\u521d\u6b21\u89c1\u5230\u201c\u8fab\u5b50\u5175\u201d\u7684\u5a01\u731b\u3002 \u3000\u3000\u6e05\u5175\u90fd\u6709\u8fab\u5b50\uff0c\u5728\u90a3\u65f6\uff0c\u6c49\u4eba\u53ea\u8981\u542c\u5230\u201c\u8fab\u5b50\u5175\u201d\u4e09\u5b57\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u5c31\u80c6\u6218\u5fc3\u60ca\uff0c\u76f4\u5230\u5341\u4f59\u5e74\u540e\u4ecd\u662f\u5982\u6b64\u3002\u674e\u81ea\u6210\u90e8\u4e0b\u90fd\u662f\u8eab\u7ecf\u767e\u6218\u7684\u608d\u5c06\u5065\u5352\uff0c\u5e2d\u5377\u800c\u4e1c\uff0c\u653b\u7834\u5317\u4eac\uff0c\u5728\u5c71\u6d77\u5173\u524d\u7684\u4e00\u7247\u77f3\u548c\u5434\u4e09\u6842\u90e8\u5927\u6218\u65f6\uff0c\u4e1d\u6beb\u4e0d\u843d\u4e0b\u98ce\u3002\u4f46\u6e05\u5175\u7a81\u7136\u51fa\u73b0\uff0c\u674e\u81ea\u6210\u519b\u4e2d\u54cd\u8d77\u201c\u8fab\u5b50\u5175\u6765\u4e86\uff01\u8fab\u5b50\u5175\u6765\u4e86\uff01\u201d\u7684\u60ca\u547c\uff0c\u4e8c\u5341\u4e07\u5927\u519b\u5c31\u6b64\u5168\u519b\u5927\u6e83\uff0c\u4e00\u8d25\u6d82\u5730\u3002\u674e\u81ea\u6210\u9003\u51fa\u5317\u4eac\uff0c\u5411\u897f\u6025\u7a9c\uff0c\u201c\u5927\u987a\u201d\u671d\u7ec8\u4e8e\u8986\u706d\u3002\u5728\u90a3\u65f6\u5019\uff0c\u201c\u8fab\u5b50\u5175\u201d\u5c31\u662f\u201c\u65e0\u654c\u96c4\u5e08\u201d\u7684\u4ee3\u540d\u8bcd\u3002 \u3000\u3000\u8881\u5d07\u7115\u5e76\u4e0d\u662f\u6bd4\u674e\u81ea\u6210\u66f4\u4f1a\u6253\u4ed7\uff0c\u4ed6\u90e8\u4e0b\u7684\u5175\u5c06\u4e5f\u5e76\u4e0d\u66f4\u4e3a\u52c7\u731b\u3002\u4f46\u4ed6\u66f4\u52a0\u9547\u5b9a\uff0c\u66f4\u52a0\u575a\u51b3\uff0c\u4ed6\u6ca1\u6709\u4e2a\u4eba\u7684\u81ea\u79c1\u6b32\u671b\uff0c\u4e0d\u50cf\u674e\u81ea\u6210\u90a3\u6837\u60f3\u505a\u7687\u5e1d\u3002\u771f\u6240\u8c13\u201c\u65e0\u6b32\u5219\u521a\u201d\uff0c\u6240\u4ee5\u4ed6\u6bd4\u674e\u81ea\u6210\u66f4\u521a\u5f3a\u3002 \u3000\u3000\u4ed6\u662f\u201c\xd7\u4ed6\u5988\uff0c\u9876\u786c\u4e0a\u201d\u7684\u82f1\u96c4\u3002 \u3000\u3000\u4f46\u4ed6\u90e8\u4e0b\u7684\u5175\u5c06\u4e0d\u662f\u5e7f\u4e1c\u4eba\uff0c\u4e3b\u8981\u662f\u8fbd\u6cb3\u4e24\u5cb8\u7684\u5173\u5916\u5065\u513f\uff0c\u5176\u4ed6\u5404\u7701\u7684\u90fd\u6709\u3002\u53ea\u56e0\u4e3a\u4e3b\u5e05\u6709\u201c\u9876\u786c\u4e0a\u201d\u7684\u82f1\u9510\u4e4b\u6c14\uff0c\u90e8\u5c5e\u4e5f\u90fd\u8ddf\u7740\u4ed6\u201c\u9876\u786c\u4e0a\u201d\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5b81\u8fdc\u5b88\u5175\u7ea6\u4e00\u4e07\uff0c\u800c\u6e05\u5175\u6709\u5341\u4e09\u4e07\u3002\u5411\u6765\u660e\u6e05\u4ea4\u6218\uff0c\u603b\u662f\u660e\u5175\u591a\u800c\u6e05\u5175\u5c11\uff0c\u8fd9\u6b21\u5374\u4f17\u5be1\u6613\u52bf\uff0c\u5927\u519b\u90fd\u5728\u7ecf\u7565\u9ad8\u7b2c\u624b\u4e2d\u3002\u9ad8\u7b2c\u5168\u519b\u636e\u5b88\u5c71\u6d77\u5173\uff0c\u679c\u7136\u5e76\u4e0d\u6d3e\u5175\u6765\u6551\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u5148\u5206\u9063\u90e8\u961f\u7ed5\u8fc7\u5b81\u8fdc\uff0c\u5728\u57ce\u5357\u4e94\u91cc\u5904\u5207\u65ad\u4e86\u901a\u5411\u5c71\u6d77\u5173\u7684\u5927\u8def\uff0c\u7136\u540e\u653e\u51e0\u540d\u4fd8\u864f\u6765\u7684\u6c49\u4eba\u53bb\u5b81\u8fdc\u5411\u8881\u5d07\u7115\u4f20\u8bdd\uff1a\u201c\u6211\u8fd9\u6b21\u5e26\u4e86\u4e8c\u5341\u4e07\u5927\u519b\u6765\u653b\uff0c\u5b81\u8fdc\u975e\u7834\u4e0d\u53ef\u3002\u5b88\u57ce\u5b98\u5982\u6295\u964d\uff0c\u6211\u4e00\u5b9a\u5927\u52a0\u4f18\u5f85\uff0c\u5c01\u4e3a\u5927\u5b98\u3002\u201d\u8881\u5d07\u7115\u56de\u7b54\u8bf4\uff1a\u201c\u4f60\u7a81\u7136\u9886\u5175\u6765\u653b\uff0c\u90a3\u662f\u751a\u4e48\u9053\u7406\uff1f\u9526\u5dde\u4e0e\u5b81\u8fdc\u4e24\u57ce\uff0c\u4f60\u672c\u6765\u5df2\u7ecf\u5360\u9886\uff0c\u53c8\u518d\u653e\u5f03\u3002\u6211\u4fee\u7b51\u597d\u4e86\u6765\u4f4f\uff0c\u81ea\u7136\u8981\u6b7b\u5b88\uff0c\u600e\u80af\u6295\u964d\uff1f\u4f60\u8bf4\u6709\u4e8c\u5341\u4e07\u5175\uff0c\u672a\u514d\u5938\u5927\u3002\u4f60\u771f\u6b63\u7684\u5175\u529b\u5927\u7ea6\u662f\u5341\u4e09\u4e07\uff0c\u6211\u5012\u4e5f\u4e0d\u4ee5\u4e3a\u6765\u5175\u592a\u5c11\u4e86\u3002\u201d\u2461\u52aa\u5c14\u54c8\u8d64\u4e8e\u662f\u5927\u4e3e\u653b\u57ce\u3002 \u3000\u3000\u5f53\u65f6\u671d\u9c9c\u4f7f\u8005\u5e26\u540c\u7ffb\u8bd1\u5b98\u97e9\u7457\u53bb\u5317\u4eac\u671d\u89c1\u7687\u5e1d\uff0c\u521a\u5230\u8fbe\u5b81\u8fdc\u3002\u8881\u5d07\u7115\u5f88\u9ad8\u5174\u7684\u62db\u5f85\u4f7f\u8282\u53ca\u5176\u968f\u4ece\u3002\u671d\u9c9c\u4f7f\u8282\u89c1\u5b88\u519b\u751a\u662f\u9547\u5b9a\uff0c\u6697\u6697\u611f\u5230\u5947\u602a\u3002\u8881\u5d07\u7115\u548c\u4e09\u6570\u5e55\u50da\u95f2\u8c08\uff0c\u53ca\u62a5\u6e05\u5175\u653b\u5230\uff0c\u8881\u5d07\u7115\u4e58\u8f7f\u81f3\u6218\u697c\uff0c\u53c8\u4e0e\u97e9\u7457\u7b49\u8c08\u53e4\u8bba\u4eca\uff0c\u6cf0\u7136\u81ea\u82e5\uff0c\u5168\u65e0\u5fe7\u8272\u3002\u8fc7\u4e86\u4e0d\u4e45\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5927\u70ae\uff0c\u58f0\u52a8\u5929\u5730\u3002\u97e9\u7457\u5927\u60ca\uff0c\u53ea\u5413\u5f97\u4f4e\u4e0b\u4e86\u5934\u62ac\u4e0d\u8d77\u6765\u3002\u8881\u5d07\u7115\u7b11\u9053\uff1a\u201c\u8d3c\u5175\u6765\u4e86\uff01\u201d\u6253\u5f00\u57ce\u5934\u654c\u697c\u7684\u7a97\u5b50\uff0c\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u6e05\u5175\u853d\u91ce\u800c\u6765\u3002\u57ce\u4e2d\u5374\u58f0\u606f\u5168\u65e0\u3002 \u3000\u3000\u6210\u5343\u6210\u4e07\u7684\u8fab\u5b50\u5175\u51b2\u5230\u4e86\u57ce\u8fb9\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u57ce\u5934\u4e3e\u8d77\u5343\u5343\u4e07\u4e07\u706b\u628a\uff0c\u77e2\u77f3\u5982\u96e8\u822c\u6295\u4e0b\u57ce\u53bb\u3002\u6218\u4e8b\u8d8a\u6765\u8d8a\u6fc0\u70c8\uff0c\u660e\u519b\u5ffd\u7136\u4ece\u57ce\u5934\u7684\u6bcf\u4e00\u4e2a\u77f3\u581e\u95f4\u63a8\u51fa\u4e00\u4e2a\u53c8\u957f\u53c8\u5927\u7684\u6728\u67dc\uff0c\u8fd9\u4e9b\u5927\u6728\u67dc\u4e00\u534a\u5728\u581e\u5185\uff0c\u4e00\u534a\u63a2\u51fa\u57ce\u5916\uff0c\u5927\u67dc\u4e2d\u4f0f\u6709\u7532\u58eb\uff0c\u4fef\u8eab\u5c04\u7bad\u6295\u77f3\uff0c\u6295\u5b8c\u4e86\u4fbf\u5c06\u5927\u672c\u67dc\u62c9\u8fdb\u6765\uff0c\u518d\u88c5\u77e2\u77f3\u51fa\u53bb\u6295\u63b7\u3002\u8ddf\u7740\u5730\u96f7\u7206\u53d1\uff0c\u571f\u77f3\u98de\u626c\uff0c\u65e0\u6570\u6e05\u5175\u548c\u9a6c\u5339\u88ab\u9707\u4e0a\u534a\u7a7a\u2462\u3002 \u3000\u3000\u653b\u57ce\u6e05\u5175\u7684\u5148\u950b\u90e8\u961f\u662f\u94c1\u7532\u519b\uff0c\u6bcf\u4eba\u8eab\u4e0a\u90fd\u62ab\u4e24\u5c42\u94c1\u7532\uff0c\u79f0\u4e3a\u201c\u94c1\u5934\u5b50\u201d\u3002\u6e05\u5175\u4ee5\u575a\u8f66\u653b\u57ce\uff0c\u8f66\u9876\u4ee5\u751f\u725b\u76ae\u8499\u4f4f\uff0c\u77e2\u77f3\u4e0d\u80fd\u4f24\u3002\u57ce\u5185\u67b6\u8d77\u897f\u6d0b\u5927\u70ae\u5341\u4e00\u95e8\uff0c\u5728\u57ce\u5934\u8f6e\u6d41\u8f70\u51fb\uff0c\u6bcf\u4e00\u70ae\u6253\u51fa\u53bb\uff0c\u7834\u574f\u6740\u4f24\u53ca\u4e8e\u6570\u91cc\u2463\u3002 \u3000\u3000\u6e05\u5175\u594b\u52c7\u8feb\u8fd1\uff0c\u63a8\u4e86\u94c1\u88f9\u8f66\u731b\u649e\u57ce\u5899\uff0c\u58f0\u97f3\u8f70\u9686\u8f70\u9686\uff0c\u52bf\u9053\u60ca\u4eba\uff0c\u649e\u51fb\u4e86\u5f88\u4e45\uff0c\u57ce\u5899\u649e\u7834\u7684\u5730\u65b9\u5f88\u591a\u3002\u6e05\u5175\u518d\u7528\u50cf\u4e91\u68af\u90a3\u6837\u7684\u88f9\u94c1\u9ad8\u8f66\u6765\u649e\u51fb\u57ce\u5899\u9ad8\u5904\u3002\u968f\u540e\u53c8\u628a\u88f9\u94c1\u8f66\u63a8\u5230\u57ce\u5899\u8fb9\uff0c\u4e0a\u9762\u7528\u6728\u677f\u906e\u4f4f\uff0c\u4ee5\u6321\u57ce\u5934\u6295\u4e0b\u7684\u77e2\u77f3\uff0c\u8f66\u91cc\u85cf\u4e86\u5175\u58eb\uff0c\u7528\u94c1\u9539\u6316\u6398\u57ce\u5899\u5899\u811a\u3002\u6e05\u5175\u653b\u8fdb\u4e86\u57ce\u5899\u4e0b\u7684\u6b7b\u89d2\uff0c\u5927\u70ae\u5df2\u6253\u4ed6\u4eec\u4e0d\u5230\u3002\u5728\u8fd9\u5371\u6025\u4e4b\u65f6\uff0c\u5b88\u519b\u60f3\u5230\u4e86\u8ba1\u7b56\uff0c\u62ac\u4e86\u5c4b\u5b50\u524d\u7684\u957f\u6761\u5927\u9636\u6cbf\u77f3\u4ece\u57ce\u4e0a\u6295\u4e0b\u53bb\u3002\u9636\u77f3\u5341\u5206\u6c89\u91cd\uff0c\u94c1\u8f66\u4e0a\u7684\u6728\u677f\u6321\u4e0d\u4f4f\uff0c\u538b\u6b7b\u4e86\u4e0d\u5c11\u6e05\u5175\u3002 \u3000\u3000\u653b\u57ce\u65f6\u5019\u7ecf\u5386\u5f88\u4e45\uff0c\u57ce\u57fa\u88ab\u6e05\u5175\u6316\u6210\u4e86\u4e00\u4e2a\u4e2a\u51f9\u9f9b\uff0c\u6e05\u5175\u8eb2\u5728\u57ce\u5899\u6d1e\u5185\u5411\u91cc\u6316\u6398\uff0c\u57ce\u4e0a\u518d\u6295\u5927\u77f3\u4e0b\u53bb\uff0c\u5c31\u6253\u4e0d\u5230\u4e86\u3002\u8fd9\u65f6\u5b81\u8fdc\u56db\u5468\u5341\u4f59\u91cc\u7684\u57ce\u5899\u5899\u811a\u5df2\u88ab\u6316\u5f97\u5343\u5b54\u767e\u75ae\uff0c\u773c\u770b\u57ce\u7834\u5728\u5373\uff0c\u6ee1\u57ce\u767e\u59d3\u60ca\u60f6\u5f97\u5f88\uff0c\u90fd\u62b1\u6028\u8bf4\uff1a\u201c\u8881\u7237\u4e3a\u4e86\u4ed6\u81ea\u5df1\u4e00\u4eba\uff0c\u5bb3\u6b7b\u4e86\u6211\u4eec\u6ee1\u57ce\u767e\u59d3\u3002\u201d \u3000\u3000\u5927\u5bb6\u6b63\u5728\u8dcb\u5fa8\u65e0\u7b56\u4e4b\u65f6\uff0c\u901a\u5224\u91d1\u542f\u5b97\uff08\u6ce8\uff1a\u5e94\u4e3a\u201c\u5027\u201d\u5b57\uff09\uff08\u6d59\u6c5f\u4eba\uff09\u4e34\u65f6\u60f3\u51fa\u4e86\u51e0\u4ef6\u65b0\u5f0f\u6b66\u5668\uff0c\u5c06\u706b\u836f\u6492\u5728\u82a6\u82b1\u8925\u5b50\u548c\u88ab\u5355\u4e0a\uff0c\u7eb7\u7eb7\u6295\u5230\u57ce\u4e0b\u53bb\u3002\u4ed6\u5c06\u8fd9\u4ef6\u65b0\u5f0f\u6b66\u5668\u53d6\u540d\u4e3a\u201c\u4e07\u4eba\u654c\u201d\u3002\u5f53\u65f6\u662f\u6b63\u6708\uff0c\u6c14\u5019\u9177\u5bd2\uff0c\u653b\u57ce\u6e05\u5175\u89c1\u5230\u88ab\u8925\uff0c\u5c31\u90fd\u6765\u62a2\u593a\uff0c\u57ce\u4e0a\u5c06\u706b\u7bad\u3001\u785d\u78fa\u7b49\u5f15\u706b\u7269\u6295\u4e0b\u53bb\uff0c\u201c\u4e07\u4eba\u654c\u201d\u7acb\u5373\u71c3\u70e7\uff0c\u70e7\u6b7b\u4e86\u65e0\u6570\u6e05\u5175\u3002\u53e6\u6709\u4e00\u79cd\u201c\u4e07\u4eba\u654c\u201d\u662f\u5c06\u706b\u836f\u653e\u5728\u7a7a\u5fc3\u7684\u5927\u6ce5\u56e2\u4e2d\uff0c\u5916\u9762\u56f4\u4ee5\u6728\u6846\uff0c\u70b9\u71c3\u4e86\u836f\u5f15\u6295\u4e0b\u57ce\u53bb\uff0c\u6ce5\u56e2\u4e0d\u65ad\u65cb\u8f6c\u55b7\u706b\uff0c\u70e7\u6b7b\u654c\u5175\u3002\u90a3\u4f4d\u901a\u5224\u5728\u8d76\u5236\u201c\u4e07\u4eba\u654c\u201d\u4e4b\u65f6\uff0c\u706b\u836f\u78b0\u5230\u706b\u661f\uff0c\u4e0d\u5e78\u88ab\u70e7\u6b7b\u4e86\u2464\u3002 \u3000\u3000\u8fd9\u65f6\u57ce\u5899\u88ab\u649e\u57ae\u4e86\u4e00\u4e08\u591a\uff0c\u8881\u5d07\u7115\u4e0d\u80fd\u518d\u6cf0\u7136\u81ea\u82e5\u4e86\uff0c\u4eb2\u81ea\u642c\u77f3\u6765\u5835\u585e\u7f3a\u53e3\uff0c\u8fde\u53d7\u4e86\u4e24\u6b21\u4f24\u3002\u90e8\u5c06\u529d\u4ed6\u4fdd\u91cd\u3002\u4ed6\u5389\u58f0\u9053\uff1a\u201c\u5b81\u8fdc\u867d\u53ea\u533a\u533a\u4e00\u57ce\uff0c\u4f46\u4e0e\u4e2d\u56fd\u7684\u5b58\u4ea1\u6709\u5173\u3002\u5b81\u8fdc\u8981\u662f\u4e0d\u5b88\uff0c\u6570\u5e74\u4e4b\u540e\uff0c\u54b1\u4eec\u7684\u7236\u6bcd\u5144\u5f1f\u90fd\u6210\u4e3a\u9791\u5b50\u7684\u5974\u96b6\u4e86\u3002\u6211\u82e5\u80c6\u5c0f\u6015\u6b7b\uff0c\u5c31\u7b97\u4fa5\u5e78\u4fdd\u5f97\u4e00\u547d\uff0c\u53c8\u6709\u751a\u4e48\u4e50\u8da3\uff1f\u201d\u6495\u4e0b\u6218\u888d\u6765\u88f9\u4e86\u5de6\u81c2\u7684\u4f24\u53e3\u53c8\u6218\u3002\u5c06\u58eb\u5728\u4ed6\u7684\u699c\u6837\u4e4b\u4e0b\uff0c\u4eba\u4eba\u594b\u52c7\uff0c\u7ec8\u4e8e\u5835\u4e0a\u4e86\u7f3a\u53e3\u2465\u3002 \u3000\u3000\u4e8c\u5341\u4e94\u65e5\u6e05\u5175\u53c8\u731b\u653b\uff0c\u8881\u5d07\u7115\u7763\u5c06\u58eb\u6b7b\u6218\u3002\u6e05\u592a\u7956\u52aa\u5c14\u54c8\u8d64\u4e5f\u53d7\u4e86\u4f24\u3002\u8840\u6218\u4e09\u65e5\uff0c\u6e05\u5175\u635f\u5931\u60e8\u91cd\uff0c\u7ec8\u4e8e\u4e0d\u5f97\u4e0d\u4e0b\u4ee4\u9000\u5175\u3002 \u3000\u3000\u6b64\u5f79\u6740\u6b7b\u4e86\u6e05\u519b\u4e2d\u7740\u9526\u8863\u7684\u519b\u5b98\u5341\u4f59\u4eba\uff0c\u5373\u6ee1\u6d32\u4eba\u79f0\u4e3a\u201c\u725b\u5f55\u989d\u771f\u201d\u7684\u3002\u6e05\u5175\u9000\u53bb\u540e\uff0c\u5b88\u519b\u5c06\u4e94\u5341\u540d\u6562\u6b7b\u961f\u7528\u957f\u7ef3\u7f12\u5230\u57ce\u4e0b\uff0c\u62fe\u5230\u4e86\u5341\u4f59\u4e07\u652f\u7bad\u3002\u57ce\u5899\u4e0a\u7ed9\u6e05\u5175\u6316\u51fa\u7684\u6d1e\u7a74\u6709\u4e03\u5341\u4f59\u4e2a\u3002\u8fd9\u65f6\u70b9\u67e5\u706b\u836f\u5e93\uff0c\u706b\u836f\u4e5f\u7528\u5c3d\u4e86\uff0c\u5c40\u9762\u771f\u662f\u5371\u9669\u5f97\u5f88\u3002 \u3000\u3000\u654c\u519b\u89e3\u56f4\u800c\u53bb\u4e4b\u540e\uff0c\u767e\u59d3\u611f\u5230\u5b89\u5168\u4e86\uff0c\u6ee1\u57ce\u5927\u54ed\uff0c\u7eb7\u7eb7\u53bb\u62dc\u8c22\u8881\u5d07\u7115\u4e0e\u6ee1\u6842\u7684\u6551\u547d\u4e4b\u6069\u3002\u4e3a\u751a\u4e48\u8981\u201c\u6ee1\u57ce\u5927\u54ed\u201d\uff1f\u60f3\u6765\u662f\u65e2\u611f\u6fc0\u53c8\u60ed\u6127\uff0c\u53c8\u662f\u8bf4\u4e0d\u51fa\u7684\u6b23\u559c\u7f62\uff1f \u3000\u3000\u7b2c\u4e8c\u5929\u65e9\u6668\uff0c\u6e05\u5175\u5927\u961f\u4eba\u9a6c\u62e5\u805a\u5728\u57ce\u5916\u5927\u5e73\u539f\u4e00\u8fb9\u3002\u8881\u5d07\u7115\u6d3e\u9063\u4e00\u540d\u4f7f\u8005\uff0c\u5907\u4e86\u793c\u7269\u53bb\u9001\u7ed9\u52aa\u5c14\u54c8\u8d64\uff0c\u5bf9\u4ed6\u8bf4\uff1a\u201c\u8001\u5c06\u6a2a\u884c\u5929\u4e0b\u4e3a\u65f6\u5df2\u4e45\uff0c\u4eca\u65e5\u8d25\u4e8e\u5c0f\u5b50\u4e4b\u624b\uff0c\u53ea\u6015\u662f\u5929\u610f\u4e86\u3002\u201d\u52aa\u5c14\u54c8\u8d64\u5df2\u53d7\u91cd\u4f24\uff0c\u4e8e\u662f\u56de\u9001\u793c\u7269\u53ca\u540d\u9a6c\uff0c\u7ea6\u671f\u518d\u6218\u3002 \u3000\u3000\u6240\u8c13\u201c\u7ea6\u671f\u518d\u6218\u201d\uff0c\u53ea\u662f\u63a9\u9970\u9762\u5b50\u7684\u8bdd\u3002\u52aa\u5c14\u54c8\u8d64\u4e0d\u6562\u518d\u653b\u5b81\u8fdc\uff0c\u8f6c\u800c\u53bb\u653b\u89c9\u534e\u5c9b\u6cc4\u6124\u3002 \u3000\u3000\u8881\u5d07\u7115\u62db\u52df\u6765\u7684\u4e24\u5e7f\u5b50\u5f1f\u5175\uff0c\u5728\u5b81\u8fdc\u4e4b\u6218\u4e2d\u4f3c\u4e4e\u5e76\u672a\u53d1\u751f\u5982\u4f55\u91cd\u5927\u7684\u4f5c\u7528\u3002\u636e\u6211\u731c\u60f3\uff0c\u6781\u53ef\u80fd\u662f\u8881\u5d07\u7115\u6d3e\u4e86\u5e7f\u4e1c\u6c34\u5e08\u5b88\u89c9\u534e\u5c9b\u3002\u89c9\u534e\u5c9b\u73b0\u5728\u53eb\u505a\u83ca\u82b1\u5c9b\uff0c\u5728\u5b81\u8fdc\u6d77\u5916\uff0c\u5f53\u65f6\u662f\u5173\u5916\u5c6f\u805a\u7cae\u8349\u7684\u91cd\u5730\uff0c\u56e0\u4e3a\u5173\u5916\u519b\u7cae\u9760\u6d77\u8fd0\u63a5\u6d4e\uff0c\u5728\u89c9\u534e\u5c9b\u8d77\u5378\u6700\u65b9\u4fbf\u3002\u5bd2\u51ac\u4e4b\u9645\uff0c\u6d77\u9762\u7ed3\u4e86\u539a\u51b0\uff0c\u53d8\u6210\u4e86\u9646\u5730\uff0c\u5e7f\u4e1c\u5175\u6240\u64c5\u957f\u7684\u6c34\u6218\u5b8c\u5168\u7528\u4e0d\u4e0a\uff0c\u53ea\u5f97\u628a\u8f66\u8f86\u6392\u8d77\u6765\u5f53\u9632\u5fa1\u5de5\u4e8b\uff0c\u5728\u51b0\u4e0a\u548c\u6e05\u5175\u6253\u9646\u6218\uff0c\u7ed3\u679c\u5168\u519b\u8986\u6ca1\uff0c\u5c9b\u4e0a\u5341\u4f59\u4e07\u77f3\u7cae\u98df\u5c3d\u88ab\u711a\u6bc1\u3002\u8fd9\u51e0\u5343\u540d\u5e7f\u4e1c\u6d77\u519b\uff0c\u5927\u6982\u591a\u6570\u5728\u8fd9\u4e00\u5f79\u4e2d\u727a\u7272\u4e86\u2466\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u5bf9\u8bf8\u8d1d\u52d2\u8bf4\uff1a\u201c\u6211\u81ea\u4e8c\u5341\u4e94\u5c81\u4ee5\u6765\uff0c\u6218\u65e0\u4e0d\u80dc\uff0c\u653b\u65e0\u4e0d\u514b\u3002\u4e3a\u751a\u4e48\u5355\u662f\u5b81\u8fdc\u4e00\u57ce\u5c31\u6253\u4e0d\u4e0b\u6765\uff1f\u201d\u5fc3\u4e2d\u5341\u5206\u607c\u6012\u3002\u6b64\u540e\u4f24\u52bf\u4e00\u76f4\u672a\u6108\uff0c\u4e03\u6708\u95f4\u5230\u6e05\u6cb3\u6e29\u6cc9\u7597\u517b\uff0c\u6d3e\u4eba\u53bb\u53ec\u5927\u798f\u664b\uff08\u6b63\u5983\uff09\u6765\uff0c\u540c\u56de\u6c88\u9633\uff0c\u5728\u79bb\u6c88\u9633\u56db\u5341\u91cc\u5904\u7684\u53c6\u9e21\u5821\u901d\u4e16\uff0c\u5e74\u516d\u5341\u516b\u5c81\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u4e00\u751f\u53ea\u6253\u4e86\u8fd9\u4e00\u4e2a\u5927\u8d25\u4ed7\u3002\u6e05\u4eba\u4ece\u6b64\u5bf9\u8881\u5d07\u7115\u5341\u5206\u656c\u754f\u2467\u3002 \u3000\u3000\u8881\u5d07\u7115\u6307\u6325\u8fd9\u4e2a\u6218\u5f79\u5f88\u6709\u5112\u5c06\u98ce\u5ea6\uff0c\u5750\u8f7f\u5b50\u5728\u57ce\u5934\u654c\u697c\u4e2d\u7763\u6218\uff0c\u6253\u4e86\u80dc\u4ed7\u4e4b\u540e\uff0c\u6d3e\u4f7f\u8005\u9001\u793c\u7269\u7ed9\u52aa\u5c14\u54c8\u8d64\uff0c\u9887\u6709\u300a\u4e09\u56fd\u6f14\u4e49\u300b\u4e2d\u8bf8\u845b\u4eae\u4e0e\u5468\u745c\u7fbd\u6247\u7eb6\u5dfe\u3001\u8c08\u7b11\u7528\u5175\u7684\u6c14\u6d3e\uff1b\u4e5f\u4f3c\u5357\u671d\u6881\u671d\u5927\u5c06\u97e6\u777f\u4e34\u9635\u65f6\u8f7b\u888d\u7f13\u5e26\uff0c\u4e58\u8206\u5750\u6905\uff0c\u624b\u6301\u7af9\u5982\u610f\u6307\u6325\u519b\u961f\u3002\u97e6\u777f\u8eab\u5b50\u7626\u5f31\uff0c\u4f46\u6218\u65e0\u4e0d\u80dc\uff0c\u654c\u519b\u754f\u4e4b\u5982\u864e\uff0c\u79f0\u4e3a\u201c\u97e6\u864e\u201d\u3002\u4e0d\u8fc7\u5230\u4e86\u5f53\u771f\u5371\u6025\u4e4b\u65f6\uff0c\u8881\u5d07\u7115\u4e5f\u4e0d\u80fd\u518d\u626e\u5112\u5c06\u4e86\uff0c\u53ea\u5f97\u4ee5\u201c\u86ee\u5b50\u201d\u59ff\u6001\u6765\u6b7b\u62da\u3002 \u3000\u3000\u2460\u89c1\u674e\u5149\u6d9b\u300a\u6e05\u5165\u5173\u524d\u4e4b\u771f\u8c61\u300b\u3002\u4f46\u6b64\u8282\u4e0d\u89c1\u4e8e\u5176\u4ed6\u8bb0\u8f7d\uff0c\u4e0d\u77e5\u674e\u5148\u751f\u6709\u4f55\u6839\u636e\u3002 \u3000\u3000\u2461\u300a\u6e05\u592a\u7956\u5b9e\u5f55\u300b\u5377\u5341\u3002 \u3000\u3000\u2462\u636e\u65e5\u4eba\u7a3b\u53f6\u541b\u5c71\u300a\u6e05\u671d\u5168\u53f2\u300b\u4e2d\u6240\u5f15\u8ff0\u671d\u9c9c\u4f7f\u8005\u5f53\u65f6\u5728\u5b81\u8fdc\u57ce\u5934\u7684\u76ee\u7779\u8bb0\u3002 \u3000\u3000\u2463\u636e\u300a\u80ea\u5929\u9882\u7b14\u300b\u3002 \u3000\u3000\u2464\u636e\u8ba1\u516d\u5947\u300a\u660e\u5b63\u5317\u7565\u300b\u4e2d\u5f15\u5b81\u8fdc\u56f4\u57ce\u65f6\u5728\u9f13\u697c\u524d\u5f00\u5e97\u7684\u4e00\u540d\u82b1\u6912\u5546\u4eba\u6240\u8ff0\u3002 \u3000\u3000\u2465\u636e\u6881\u542f\u8d85\u300a\u8881\u5d07\u7115\u4f20\u300b\u3002\u8be5\u4f20\u4e2d\u53d9\u8ff0\u6e05\u5175\u8d25\u9000\u540e\uff0c\u201c\u5d07\u7115\u590d\u5f00\u5792\u88ad\u51fb\uff0c\u8ffd\u5317\u4e09\u5341\u4f59\u91cc\uff0c\u6e05\u519b\u5927\u4e71\uff0c\u6b7b\u8005\u903e\u4e07\u4eba\u3002\u201d\u4e0e\u5176\u4ed6\u8d44\u6599\u4e0d\u7b26\uff0c\u4eca\u4e0d\u53d6\u3002 \u3000\u3000\u2466\u8881\u5d07\u7115\u300a\u796d\u89c9\u534e\u5c9b\u9635\u4ea1\u5175\u5c06\u6587\u300b\uff1a\u201c\u6168\u81ea\u6218\u5b88\u4e56\u65b9\uff0c\u5c61\u5931\u7586\u571f\uff0c\u5929\u5b50\u8d6b\u7136\u9707\u6012\uff0c\u8c03\u5357\u5317\u6c34\u9646\u821f\u5e08\uff0c\u8c13\u5c14\u4e58\u8239\u5982\u9a6c\uff0c\u9042\u8c03\u4e4b\u6765\u4e3a\u8fdb\u53d6\u4e5f\u3002\u636e\u5c14\u7b49\u95f4\u5173\u8fdc\u81f3\uff0c\u5c82\u4e0d\u6b32\u706d\u6b64\u671d\u98df\uff0c\u4e00\u822a\u800c\u91d1\u590d\u5f52\uff0c\u518d\u822a\u800c\u9ec4\u9f99\u626b\u54c9\uff1f\u5948\u672a\u5c3d\u5176\u7528\u800c\u654c\u5373\u6765\u3002\u51b1\u5bd2\u4e4b\u6708\uff0c\u51b0\u7ed3\u821f\u80f6\uff0c\u7a98\u5c14\u4e4b\u6240\u957f\uff0c\u4e4c\u5f97\u4e0d\u53ca\u4e8e\u96be\uff1f\u8bf4\u8005\u8c13\u8c0b\u4e4b\u4e0d\u81e7\u3002\u4e0d\u81e7\u56fa\u4e0d\u81e7\u77e3\uff0c\u7136\u6392\u5c71\u5012\u6d77\u4e4b\u52bf\uff0c\u4ee5\u5341\u516b\u4e07\u800c\u4e34\u6570\u5343\u4e4b\u6c34\u5352\uff0c\u5373\u81e7\u53ef\u5948\u4f55\uff1f\u800c\u5c14\u7b49\u8ba1\u65e0\u590d\u4e4b\uff0c\u6124\u7136\u4ee5\u6b7b\uff0c\u7565\u65e0\u82a5\u8482\uff0c\u89c6\u5f53\u5e74\u4e4b\u5f03\u66f3\u5012\u5954\u8005\uff0c\u52a0\u4e00\u7b49\u4e5f\u3002\u4eba\u4e4b\u7f6a\u81f3\u6b7b\u800c\u514d\uff0c\u4eba\u4e4b\u54c1\u81f3\u6b7b\u800c\u5b9a\u3002\u4eca\u5c06\u7565\u5c14\u7f6a\u800c\u5609\u4e43\u5fe0\uff0c\u8bf7\u547d\u4e8e\u5929\u5b50\uff0c\u8c05\u4e3a\u4e4b\u6064\uff0c\u6240\u4ee5\u4e0d\u6ca1\u6c5d\u7b49\u8005\uff0c\u826f\u6709\u5728\u4e5f\u3002\u5401\u55df\uff0c\u5de8\u6d6a\u832b\u832b\uff0c\u7a7a\u5c71\u5bc2\u5bc2\uff0c\u7686\u6c5d\u7b49\u5fe0\u7075\u4e4b\u6240\u6816\u8361\u4e5f\uff0c\u671b\u6545\u4e61\u4ee5\u4f55\u65e5\uff1f\u5373\u8f6c\u52ab\u800c\u65e0\u671f\uff0c\u82d2\u82d2\u6e38\u9b42\uff0c\u4f55\u4e0d\u76f8\u7ed3\u4e3a\u5389\uff0c\u6b7c\u4ec7\u6cc4\u6124\uff1f\u5728\u751f\u4e4b\u5fd7\uff0c\u501f\u6b7b\u4ee5\u4f38\uff0c\u5219\u867d\u6b7b\u4e4b\u65e5\uff0c\u72b9\u751f\u4e4b\u5e74\u4e5f\uff0c\u5c14\u5176\u52c9\u4e4b\u3002\u4e0d\u8146\u4e4b\u5960\uff0c\u6d95\u4e0e\u4ff1\u4e4b\u3002\u5c1a\u98e8\u3002\u201d \u3000\u3000\u300a\u53e4\u4eca\u56fe\u4e66\u96c6\u6210\xb7\u804c\u65b9\u5178\uff0c\u5e7f\u5dde\u5e9c\u90e8\xb7\u7960\u5e99\u8003\u300b\u4e2d\uff0c\u8bb0\u8f7d\u4e1c\u839e\u53bf\u6709\u4e00\u5ea7\u6555\u5efa\u5fe0\u610d\u7960\uff0c\u201c\u5929\u542f\u4e03\u5e74\uff0c\u5949\u6555\u5efa\uff0c\u4e3a\u8fbd\u5c06\u6b7b\u4e8b\u9635\u7b56\uff0c\u5728\u6559\u573a\u5c3e\u3002\u201d\u9648\u7b56\u4e0d\u77e5\u600e\u6837\u5728\u8fbd\u897f\u727a\u7272\uff0c\u76f8\u4fe1\u4ed6\u662f\u8881\u5d07\u7115\u4ece\u6545\u4e61\u5e26\u53bb\u7684\u5b50\u5f1f\u5175\u4e4b\u4e00\u3002\u5929\u542f\u4e03\u5e74\u7684\u51ac\u5929\uff0c\u8881\u5d07\u7115\u5df2\u56de\u4e1c\u839e\uff0c\u8fd9\u5ea7\u5fe0\u610d\u7960\u5f88\u53ef\u80fd\u662f\u4ed6\u5411\u671d\u5ef7\u7533\u8bf7\uff0c\u7531\u7687\u5e1d\u4e0b\u6555\u5efa\u9020\uff0c\u4ee5\u7eaa\u5ff5\u4ed6\u5728\u5173\u5916\u6b89\u56fd\u7684\u65e7\u90e8\u3002 \u3000\u3000\u2467\u6e05\u4eba\u6240\u4fee\u7684\u300a\u660e\u53f2\xb7\u8881\u5d07\u7115\u4f20\u300b\u4e2d\u8bf4\uff1a\u201c\u6211\u5927\u6e05\u4e3e\u5175\u6240\u5411\uff0c\u65e0\u4e0d\u6467\u7834\u3002\u8bf8\u5c06\u7f54\u6562\u8bae\u6218\u5b88\u3002\u8bae\u6218\u5b88\u81ea\u5d07\u7115\u59cb\u3002\u201d \u3000\u3000\u516d \u3000\u3000\u5f53\u671d\u4e2d\u5f97\u5230\u6e05\u5175\u5927\u4e3e\u6765\u653b\u7684\u8baf\u606f\u65f6\uff0c\u767e\u5b98\u60ca\u60f6\u4e4b\u6781\u3002\u5175\u90e8\u5c1a\u4e66\u738b\u4e4b\u5149\u4e0e\u5ef7\u81e3\u5546\u8bae\uff0c\u4eba\u4eba\u675f\u624b\u65e0\u7b56\uff0c\u4ee5\u4e3a\u8fd9\u4e00\u6b21\u5b81\u8fdc\u4e00\u5b9a\u8981\u5931\u4e86\uff0c\u4e0d\u77e5\u5c71\u6d77\u5173\u662f\u5426\u80fd\u4fdd\u5f97\u4f4f\u3002\u540e\u6765\u5f97\u5230\u6377\u62a5\uff0c\u671d\u91ce\u81ea\u7136\u559c\u51fa\u671b\u5916\uff0c\u8c22\u5929\u8c22\u5730\u3002 \u3000\u3000\u9ad8\u7b2c\u56e0\u4e0d\u63f4\u5b81\u8fdc\u800c\u514d\u804c\uff0c\u4ee5\u738b\u4e4b\u81e3\u4ee3\u3002\u8881\u5d07\u7115\u5347\u4e3a\u53f3\u4f65\u90fd\u5fa1\u53f2\u3002\u90a3\u662f\u6b63\u56db\u54c1\u7684\u5b98\u3002 \u3000\u3000\u4e09\u6708\uff0c\u590d\u8bbe\u8fbd\u4e1c\u5de1\u629a\uff0c\u7531\u8881\u5d07\u7115\u5347\u4efb\u3002\u4f46\u9b4f\u5fe0\u8d24\u89c1\u4ed6\u5730\u4f4d\u91cd\u8981\u4e86\u8d77\u6765\uff0c\u5f00\u59cb\u5bf9\u4ed6\u63d0\u9632\uff0c\u6d3e\u4e86\u4e24\u540d\u4eb2\u4fe1\u592a\u76d1\u5218\u5e94\u5764\u4e0e\u7eaa\u7528\u53bb\u5b81\u8fdc\u76d1\u519b\u3002\u7687\u5e1d\u6d3e\u7279\u52a1\u76d1\u89c6\u90e8\u961f\u957f\u5b98\uff0c\u662f\u5386\u4ee3\u653f\u6cbb\u8150\u8d25\u65f6\u5e38\u5e38\u51fa\u73b0\u7684\u60c5\u5f62\u3002\u7279\u52a1\u5e72\u9884\u519b\u4e8b\uff0c\u540e\u679c\u4e00\u5b9a\u6781\u5dee\uff0c\u6240\u4ee5\u8881\u5d07\u7115\u4e0a\u758f\u53cd\u5bf9\uff0c\u4f46\u6297\u8bae\u65e0\u6548\uff0c\u7279\u52a1\u592a\u76d1\u975e\u6765\u4e0d\u53ef\u3002\u671d\u5ef7\u4e3a\u4e86\u5b89\u629a\u4ed6\uff0c\u52a0\u4ed6\u4e00\u4e2a\u5175\u90e8\u53f3\u4f8d\u90ce\uff08\u6b63\u4e09\u54c1\uff0c\u76f8\u5f53\u4e8e\u56fd\u9632\u90e8\u7b2c\u4e8c\u526f\u90e8\u957f\uff09\u7684\u5934\u8854\uff0c\u5e76\u8d4f\u94f6\u5e01\uff0c\u5b50\u5b59\u4e16\u88ad\u9526\u8863\u5343\u6237\u3002 \u3000\u3000\u5728\u8fd9\u65f6\u5019\uff0c\u8881\u5d07\u7115\u4e0e\u5927\u5c06\u6ee1\u6842\u4e4b\u95f4\uff0c\u53d1\u751f\u4e86\u6fc0\u70c8\u51b2\u7a81\uff0c\u51b2\u7a81\u7684\u539f\u56e0\u5728\u4e8e\u53e6\u4e00\u4e2a\u5927\u5c06\u8d75\u7387\u6559\u3002 \u3000\u3000\u6ee1\u6842\u548c\u8d75\u7387\u6559\u90fd\u662f\u7b2c\u4e00\u6d41\u7684\u5c06\u9886\uff0c\u4f46\u6027\u683c\u5f88\u4e0d\u540c\u2460\u3002\u6ee1\u6842\u662f\u8499\u53e4\u4eba\uff0c\u975e\u5e38\u7684\u6206\u76f4\uff0c\u7b80\u76f4\u6709\u4e9b\u50bb\u91cc\u50bb\u6c14\u3002\u8d75\u7387\u6559\u5374\u5341\u5206\u7684\u673a\u7075\u7cbe\u4e56\uff0c\u76f8\u4fe1\u4ed6\u4e00\u5b9a\u5f88\u4f1a\u8ba8\u597d\u4e0a\u53f8\uff0c\u6240\u4ee5\u6bcf\u4e00\u4e2a\u8fbd\u4e1c\u7edf\u5e05\u81ea\u8881\u5e94\u6cf0\u3001\u738b\u5728\u664b\u3001\u5b59\u627f\u5b97\u3001\u9ad8\u7b2c\u3001\u4ee5\u81f3\u8881\u5d07\u7115\uff0c\u4e2a\u4e2a\u90fd\u5f88\u559c\u6b22\u4ed6\uff08\u5728\u300a\u78a7\u8840\u5251\u300b\u5c0f\u8bf4\u91cc\uff0c\u5f53\u8881\u627f\u5fd7\u5468\u5c81\u65f6\u9001\u91d1\u9879\u5708\u7684\u5c31\u662f\u4ed6\uff09\u3002 \u3000\u3000\u6ee1\u6842\u548c\u4ed6\u672c\u6765\u662f\u975e\u5e38\u8981\u597d\u7684\u670b\u53cb\u3002\u5f53\u6e05\u5175\u5927\u4e3e\u6765\u653b\u5b81\u8fdc\u65f6\uff0c\u8d75\u7387\u6559\u5728\u524d\u5c6f\u536b\u9547\u5b88\uff0c\u6d3e\u4e86\u4e00\u540d\u90fd\u53f8\u3001\u56db\u540d\u5b88\u5907\u5e26\u5175\u6765\u63f4\u3002\u5f53\u65f6\u5927\u654c\u538b\u5883\uff0c\u8d75\u7387\u6559\u81ea\u5df1\u4e0d\u6765\u548c\u4e0a\u53f8\u53ca\u597d\u670b\u53cb\u5171\u8d74\u60a3\u96be\uff0c\u6240\u6d3e\u7684\u63f4\u5175\u53c8\u5230\u5f97\u5f88\u8fdf\uff0c\u6ee1\u6842\u5927\u5927\u4e0d\u9ad8\u5174\uff0c\u4e0d\u8bb8\u63f4\u5175\u8fdb\u57ce\uff0c\u540e\u6765\u56e0\u8881\u5d07\u7115\u7684\u547d\u4ee4\u624d\u653e\u4ed6\u4eec\u8fdb\u6765\u3002\u7b49\u5230\u5b81\u8fdc\u89e3\u56f4\uff0c\u8d75\u7387\u6559\u60f3\u5206\u529f\u3002\u6ee1\u6842\u4e0d\u8bb8\uff0c\u53c8\u9a82\u4ed6\u4e3a\u751a\u4e48\u81ea\u5df1\u4e0d\u6765\u6551\u63f4\uff0c\u592a\u6ca1\u6709\u4e49\u6c14\u3002\u4e24\u4eba\u4e3a\u6b64\u5927\u5435\u3002\u5927\u6982\u6ee1\u6842\u7684\u6001\u5ea6\u5341\u5206\u7c97\u9c81\uff0c\u8499\u53e4\u4e09\u5b57\u7ecf\u9a82\u4e4b\u4e0d\u5df2\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u60f3\u51fa\u62f3\u6253\u4eba\uff0c\u8881\u5d07\u7115\u4fbf\u8892\u62a4\u8d75\u7387\u6559\u3002 \u3000\u3000\u51b2\u7a81\u8f6c\u79fb\u5230\u4e86\u8881\u3001\u6ee1\u4e8c\u4eba\u4e4b\u95f4\uff0c\u6216\u8bb8\u6ee1\u6842\u5bf9\u4e0a\u53f8\u4e0d\u591f\u5c0a\u656c\uff0c\u4e8e\u662f\u8881\u5d07\u7115\u8981\u6c42\u5c06\u6ee1\u6842\u8c03\u8d70\u2461\u3002 \u3000\u3000\u671d\u5ef7\u7fa4\u81e3\u90fd\u77e5\u9053\u6ee1\u6842\u6253\u4ed7\u7684\u672c\u4e8b\uff0c\u4f46\u5c06\u5e05\u4e0d\u548c\u603b\u662f\u4e0d\u5bf9\uff0c\u4fbf\u4f9d\u4ece\u4e86\u3002\u53ef\u662f\u7ecf\u7565\u738b\u4e4b\u81e3\u6781\u529b\u8ba4\u4e3a\u6ee1\u6842\u51b3\u4e0d\u53ef\u53bb\u3002\u671d\u5ef7\u53ec\u8fd8\u6ee1\u6842\u7684\u547d\u4ee4\u5df2\u9881\u4e0b\u4e86\uff0c\u4e8e\u662f\u542c\u4e86\u738b\u4e4b\u81e3\u7684\u4e3b\u5f20\uff0c\u518d\u547d\u6ee1\u6842\u9547\u5b88\u5c71\u6d77\u5173\u3002\u8881\u5d07\u7115\u575a\u51b3\u4e0d\u63a5\u53d7\u3002\u671d\u5ef7\u65e0\u6cd5\uff0c\u53ea\u5f97\u5c06\u6ee1\u6842\u8c03\u56de\u5317\u4eac\uff0c\u4fdd\u7559\u5de6\u90fd\u7763\u539f\u5b98\uff0c\u6d3e\u5728\u56fd\u9632\u673a\u6784\u529e\u4e8b\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u60c5\u663e\u7136\u662f\u8881\u5d07\u7115\u7684\u86ee\u5b50\u813e\u6c14\u53d1\u4f5c\uff0c\u51b2\u52a8\u8d77\u6765\uff0c\u4f5c\u51fa\u4e86\u8fdd\u53cd\u7406\u667a\u7684\u51b3\u5b9a\u3002\u7531\u4e8e\u738b\u4e4b\u81e3\u8892\u62a4\u6ee1\u6842\uff0c\u8881\u5d07\u7115\u53c8\u53bb\u548c\u738b\u4e4b\u81e3\u5435\u95f9\u3002\u671d\u5ef7\u6015\u738b\u4e4b\u81e3\u4e0e\u8881\u5d07\u7115\u4e0d\u65ad\u51b2\u7a81\uff0c\u574f\u4e86\u5927\u4e8b\uff0c\u4e8e\u662f\u5c06\u6307\u6325\u6743\u5212\u5206\u4e3a\u4e8c\uff1a\u5173\u5185\u7684\u90e8\u961f\u7531\u8fbd\u4e1c\u7ecf\u7565\u738b\u4e4b\u81e3\u6307\u6325\uff0c\u5173\u5916\u90e8\u961f\u5219\u7531\u8fbd\u4e1c\u5de1\u629a\u8881\u5d07\u7115\u6307\u6325\u3002\u7ecf\u7565\u7684\u5b98\u6bd4\u5de1\u629a\u5927\uff0c\u4f46\u8fd9\u65f6\u8881\u5d07\u7115\u5df2\u4e0d\u5c5e\u8fbd\u4e1c\u7ecf\u7565\u7ba1\u4e86\u3002 \u3000\u3000\u8881\u5d07\u7115\u6bd5\u7adf\u662f\u4e2a\u5149\u660e\u78ca\u843d\u7684\u5927\u4e08\u592b\uff0c\u51b7\u9759\u4e0b\u6765\u4e4b\u540e\uff0c\u77e5\u9053\u662f\u81ea\u5df1\u7684\u4e0d\u5bf9\uff0c\u4e8e\u662f\u4e0a\u594f\u8bf7\u518d\u7528\u6ee1\u6842\u3002\u671d\u5ef7\u5f53\u7136\u6279\u51c6\uff0c\u6d3e\u6ee1\u6842\u517c\u7edf\u5173\u5185\u5916\u5175\u9a6c\uff0c\u8d50\u5c1a\u65b9\u5251\u3002\u738b\u4e4b\u81e3\u548c\u8881\u5d07\u7115\u662f\u6587\u5b98\uff0c\u7b49\u4e8e\u73b0\u5728\u7684\u653f\u59d4\uff1b\u6ee1\u6842\u662f\u6b66\u5c06\uff0c\u662f\u90e8\u961f\u53f8\u4ee4\u3002\u6b66\u5c06\u53d7\u6587\u5b98\u6307\u6325\u3002 \u3000\u3000\u5e78\u4e8f\u8881\u5d07\u7115\u4e0d\u575a\u6301\u9519\u8bef\uff0c\u5426\u5219\u4e8c\u6b21\u5b81\u8fdc\u5927\u6218\uff0c\u5c31\u4e0d\u80fd\u5f97\u5230\u6ee1\u6842\u8fd9\u6837\u7684\u5927\u5c06\u6765\u4e3b\u6301\u57ce\u9632\u3002 \u3000\u3000\u5728\u8fd9\u65f6\u5019\uff0c\u8881\u5d07\u7115\u4e0a\u4e86\u4e00\u9053\u594f\u7ae0\uff0c\u63d0\u51fa\u5b88\u8fbd\u7684\u57fa\u672c\u6218\u7565\uff0c\u8fd9\u9053\u594f\u7ae0\u6709\u5f88\u5927\u7684\u91cd\u8981\u6027\u3002\u5176\u4e2d\u4e3b\u5f20\uff1a\u4e00\u3001\u7528\u8fbd\u4eba\u5b88\u8fbd\u571f\uff1b\u4e8c\u3001\u5c6f\u7530\uff0c\u4ee5\u8fbd\u571f\u517b\u519b\u961f\uff1b\u4e09\u3001\u4ee5\u5b88\u4e3a\u4e3b\uff0c\u7b49\u5f85\u673a\u4f1a\u518d\u51fa\u51fb\u3002\u4ed6\u6700\u62c5\u5fc3\u7684\u4e8b\uff0c\u662f\u7acb\u4e86\u529f\u52b3\u4e4b\u540e\uff0c\u654c\u4eba\u5fc5\u5b9a\u8981\u4f7f\u53cd\u95f4\u8ba1\uff0c\u6563\u64ad\u8c23\u8a00\uff0c\u800c\u672c\u56fd\u5fc5\u5b9a\u6709\u4eba\u5992\u5fcc\u6bc1\u8c24\u2462\u3002 \u3000\u3000\u4ed6\u6df1\u77e5\u660e\u519b\u7684\u6218\u6597\u529b\u4e0d\u5982\u6e05\u519b\uff0c\u91ce\u6218\u4e0d\u5229\uff0c\u53ea\u6709\u7528\u5df1\u4e4b\u957f\uff0c\u6240\u4ee5\u63d0\u51fa\u4e86\u6218\u672f\u7684\u57fa\u672c\u539f\u5219\uff1a\u201c\u5175\u4e0d\u5229\u91ce\u6218\uff0c\u53ea\u6709\u51ed\u575a\u57ce\u3001\u7528\u5927\u70ae\u4e00\u7b56\u3002\u201d \u3000\u3000\u6240\u7edf\u5e26\u7684\u90e8\u961f\u65e0\u529b\u6253\u91ce\u6218\uff0c\u4f5c\u4e3a\u4e3b\u5e05\uff0c\u81ea\u7136\u6df1\u611f\u68d8\u624b\u3002\u4f46\u8bad\u7ec3\u4e00\u652f\u5584\u6253\u91ce\u6218\u7684\u52b2\u65c5\uff0c\u975e\u4e00\u671d\u4e00\u5915\u4e4b\u529f\uff0c\u90a3\u662f\u65e0\u53ef\u5948\u4f55\u7684\uff1b\u800c\u5bf9\u4e8e\u52bf\u6240\u5fc5\u81f3\u7684\u671d\u81e3\u5fcc\u529f\u4e2d\u4f24\uff0c\u66f4\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u6709\u76fc\u671b\u7687\u5e1d\u548c\u5927\u81e3\u4eec\u80fd\u52a0\u4ee5\u7167\u987e\u4e86\u3002 \u3000\u3000\u8881\u5d07\u7115\u4e5f\u4e0d\u662f\u4e00\u5473\u7684\u86ee\u5e72\uff0c\u6709\u65f6\u4e5f\u6709\u4ed6\u673a\u7075\u7684\u4e00\u9762\u3002\u4ed6\u5bf9\u9b4f\u5fe0\u8d24\u6d3e\u53bb\u76d1\u89c6\u4ed6\u7684\u4e24\u540d\u7279\u52a1\u592a\u76d1\u6577\u884d\u5f97\u5f88\u597d\u3002\u5f53\u5e74\u51ac\u5929\uff0c\u4ed6\u5e26\u540c\u8d75\u7387\u6559\u4ee5\u53ca\u4e24\u540d\u7279\u52a1\u592a\u76d1\u5218\u5e94\u5764\u3001\u7eaa\u7528\uff0c\u5174\u529e\u9632\u5fa1\u5de5\u4e8b\u53ca\u5c6f\u7530\uff0c\u6e10\u6e10\u53c8\u518d\u6536\u590d\u4e86\u9ad8\u7b2c\u6240\u653e\u5f03\u7684\u571f\u5730\u3002 \u3000\u3000\u4ed6\u5728\u594f\u7ae0\u4e2d\u5c06\u8fd9\u4e24\u540d\u592a\u76d1\u7684\u529f\u52b3\u5439\u5618\u4e86\u4e00\u756a\uff0c\u6240\u4ee5\u9b4f\u5fe0\u8d24\u548c\u5218\u5e94\u5764\u3001\u7eaa\u7528\u4e09\u4eba\u90fd\u5f97\u5230\u4e86\u5c01\u8d4f\u3002\u5218\u3001\u7eaa\u4e8c\u4eba\u4f3c\u4e4e\u4e5f\u4e0d\u662f\u574f\u592a\u76d1\uff0c\u5e76\u6ca1\u6709\u5bf9\u8881\u5d07\u7115\u63a3\u8098\u963b\u6320\uff0c\u540e\u6765\u5b81\u9526\u5927\u6218\uff0c\u5218\u5e94\u5764\u5728\u5b81\u8fdc\u4e0a\u57ce\u7763\u6218\uff0c\u7eaa\u7528\u5728\u9526\u5dde\u4e0a\u57ce\u7763\u6218\uff0c\u90fd\u52c7\u6562\u5f97\u5f88\u3002\u5927\u6982\u4e8c\u4eba\u4e3a\u8881\u5d07\u7115\u7684\u5fe0\u52c7\u6240\u611f\u53ec\uff0c\u4e5f\u53d8\u5f97\u5fe0\u52c7\u8d77\u6765\u3002\u53ef\u89c1\u4e5f\u4e0d\u662f\u6240\u6709\u7684\u592a\u76d1\u90fd\u662f\u574f\u4eba\uff0c\u4e3b\u8981\u8fd8\u5728\u9886\u5bfc\u8005\u5982\u4f55\u9886\u5bfc\u3002 \u3000\u3000\u2460\u300a\u660e\u53f2\xb7\u6ee1\u6842\u4f20\u300b\uff1a\u201c\u6842\u690e\u9c81\u751a\uff0c\u7136\u5fe0\u52c7\u7edd\u4f26\uff0c\u4e0d\u597d\u58f0\u8272\uff0c\u4e0e\u58eb\u5352\u540c\u7518\u82e6\u3002\u201d\u300a\u660e\u53f2\xb7\u8d75\u7387\u6559\u4f20\u300b\uff1a\u201c\u7387\u6559\u4e3a\u5c06\u5ec9\u52c7\uff0c\u5f85\u58eb\u6709\u6069\uff0c\u52e4\u8eab\u5949\u516c\uff0c\u52b3\u800c\u4e0d\u61c8\uff0c\u4e0e\u6ee1\u6842\u5e76\u79f0\u826f\u5c06\u3002\u4e8c\u4eba\u65e2\u6b81\uff0c\u76ca\u65e0\u80fd\u529e\u4e1c\u4e8b\u8005\u3002\u201d \u3000\u3000\u2461\u8881\u5d07\u7115\u594f\u7ae0\u4e2d\u8bf4\u6ee1\u6842\u201c\u610f\u6c14\u9a84\u77dc\uff0c\u8c29\u9a82\u50da\u5c5e\uff0c\u6050\u574f\u5c01\u7586\u5927\u8ba1\uff0c\u4e5e\u79fb\u4e4b\u522b\u9547\uff0c\u4ee5\u5173\u5916\u4e8b\u6743\u5f52\u7387\u6559\u3002\u201d \u3000\u3000\u2462\u300a\u660e\u53f2\xb7\u8881\u5d07\u7115\u4f20\u300b\u5f15\u8ff0\u4ed6\u7684\u594f\u7ae0\uff1a\u201c\u965b\u4e0b\u4ee5\u5173\u5185\u5916\u5206\u8d23\u4e8c\u81e3\u3002\u7528\u8fbd\u4eba\u5b88\u8fbd\u571f\uff0c\u4e14\u5b88\u4e14\u6218\uff0c\u4e14\u7b51\u4e14\u5c6f\u3002\u5c6f\u79cd\u6240\u5165\uff0c\u53ef\u6e10\u51cf\u6d77\u8fd0\u3002\u5927\u8981\u575a\u58c1\u6e05\u91ce\u4ee5\u4e3a\u4f53\uff0c\u4e58\u95f4\u51fb\u7455\u4ee5\u4e3a\u7528\u3002\u6218\u867d\u4e0d\u8db3\uff0c\u5b88\u5219\u6709\u4f59\u3002\u5b88\u65e2\u6709\u4f59\uff0c\u6218\u65e0\u4e0d\u8db3\u3002\u987e\u52c7\u731b\u56fe\u654c\uff0c\u654c\u5fc5\u4ec7\uff0c\u594b\u8fc5\u7acb\u529f\uff0c\u4f17\u5fc5\u5fcc\u3002\u4efb\u52b3\u5219\u5fc5\u53ec\u6028\uff0c\u8499\u7f6a\u59cb\u53ef\u6709\u529f\u3002\u6028\u4e0d\u6df1\u5219\u52b3\u4e0d\u8457\uff0c\u7f6a\u4e0d\u5927\u5219\u529f\u4e0d\u6210\u3002\u8c24\u4e66\u76c8\u7ba7\uff0c\u6bc1\u8a00\u65e5\u81f3\uff0c\u81ea\u53e4\u5df2\u7136\uff0c\u60df\u5723\u660e\u4e0e\u5ef7\u81e3\u59cb\u7ec8\u4e4b\u3002\u201d \u3000\u3000\u4e03 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u6b7b\u540e\uff0c\u7b2c\u516b\u5b50\u7687\u592a\u6781\u63a5\u4f4d\u3002 \u3000\u3000\u7687\u592a\u6781\u7684\u667a\u8c0b\u6b66\u7565\uff0c\u5b9e\u662f\u4e2d\u56fd\u5386\u4ee3\u5e1d\u7687\u4e2d\u4e0d\u53ef\u591a\u89c1\u7684\u4eba\u7269\uff0c\u672c\u8eab\u7684\u624d\u5e72\u89c1\u8bc6\uff0c\u4e0d\u5728\u5218\u90a6\u3001\u5218\u79c0\u3001\u674e\u4e16\u6c11\u3001\u6731\u5143\u748b\u4e4b\u4e0b\u3002\u4e2d\u56fd\u5386\u53f2\u5bb6\u5927\u6982\u56e0\u4ed6\u662f\u6ee1\u6e05\u7687\u5e1d\uff0c\u7531\u4e8e\u79cd\u65cf\u504f\u89c1\uff0c\u5411\u6765\u6ca1\u6709\u7ed9\u4ed6\u4ee5\u5e94\u5f97\u7684\u6781\u9ad8\u8bc4\u4ef7\u3002\u5176\u5b9e\u4ee5\u4ed6\u7684\u77e5\u4eba\u5584\u4efb\u3001\u8c41\u8fbe\u5927\u5ea6\u3001\u9ad8\u77bb\u8fdc\u77a9\u3001\u660e\u65ad\u679c\u51b3\uff0c\u81ea\u5510\u592a\u5b97\u4ee5\u540e\uff0c\u4e2d\u56fd\u5386\u671d\u5e1d\u7687\u6ca1\u6709\u51e0\u4e2a\u80fd\u53ca\u5f97\u4e0a\u2460\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u662f\u7f55\u6709\u7684\u519b\u4e8b\u5929\u624d\uff0c\u8fd9\u4e2a\u8001\u5c06\u7ec8\u4e8e\u6b7b\u4e86\uff0c\u7ee7\u627f\u4eba\u662f\u4e00\u4e2a\u540c\u6837\u5389\u5bb3\u7684\u4eba\u7269\u3002\u7687\u592a\u6781\u7684\u519b\u4e8b\u5929\u624d\u867d\u4e0d\u53ca\u7236\u4eb2\uff0c\u653f\u6cbb\u624d\u80fd\u5374\u72b9\u6709\u8fc7\u4e4b\u3002\u8881\u5d07\u7115\u6240\u53d7\u5230\u7684\u538b\u529b\u4e00\u70b9\u4e5f\u6ca1\u6709\u51cf\u8f7b\u3002\u7687\u592a\u6781\u63a5\u4f4d\u4e4b\u65f6\uff0c\u6ee1\u6d32\u6b63\u906d\u9022\u6781\u5927\u7684\u56f0\u96be\u3002\u52aa\u5c14\u54c8\u8d64\u65b0\u6b7b\uff0c\u6ee1\u6d32\u5185\u90e8\u4eba\u5fc3\u52a8\u8361\u3002\u52aa\u5c14\u54c8\u8d64\u9057\u547d\u662f\u56db\u5927\u8d1d\u52d2\u540c\u65f6\u6267\u653f\uff0c\u884c\u7684\u662f\u96c6\u4f53\u9886\u5bfc\u5236\uff0c\u7687\u592a\u6781\u7684\u6743\u4f4d\u5f88\u4e0d\u5de9\u56fa\u3002\u5728\u7ecf\u6d4e\u4e0a\uff0c\u56e0\u4e3a\u4e0e\u660e\u671d\u5f00\u6218\uff0c\u4eba\u53c2\u3001\u8c82\u76ae\u7b49\u7279\u4ea7\u5931\u53bb\u4e86\u4f20\u7edf\u5e02\u573a\u3002\u6ee1\u6d32\u5f53\u65f6\u5728\u751f\u4ea7\u4e0a\u662f\u5974\u96b6\u5236\uff0c\u63b3\u63a0\u4e86\u5927\u6279\u6c49\u4eba\u6765\u519c\u8015\uff0c\u751f\u4ea7\u529b\u76f8\u5f53\u4f4e\u3002\u4f46\u519b\u961f\u5927\u52a0\u6269\u5145\uff0c\u8fd9\u65f6\u5df2\u8fbe\u5341\u4e94\u4e07\u4eba\uff0c\u519b\u9700\u8865\u7ed9\u53d1\u751f\u5f88\u5927\u95ee\u9898\uff0c\u504f\u504f\u53c8\u9047\u4e0a\u4e25\u91cd\u7684\u5929\u707e\uff0c\u8fbd\u4e1c\u53d1\u751f\u9965\u8352\u2461\u3002\u5982\u5411\u5173\u5185\u4fb5\u7565\uff0c\u5374\u53c8\u6253\u4e0d\u7834\u8881\u5d07\u7115\u8fd9\u4e00\u5173\u3002 \u3000\u3000\u5728\u8fd9\u65f6\u5019\uff0c\u7687\u592a\u6781\u5b9a\u4e0b\u4e86\u6b63\u786e\u7684\u6218\u7565\uff1a\u4fb5\u7565\u671d\u9c9c\u3002 \u3000\u3000\u671d\u9c9c\u7269\u4ea7\u4e30\u5bcc\u800c\u5175\u529b\u8584\u5f31\uff0c\u6b63\u662f\u7406\u60f3\u7684\u63a0\u593a\u5bf9\u8c61\u3002\u5728\u5916\u4ea4\u4e0a\uff0c\u671d\u9c9c\u91c7\u53d6\u7684\u662f\u201c\u4e8b\u5927\uff08\u5bf9\u660e\uff09\u4ea4\u90bb\uff08\u5bf9\u65e5\u672c\u3001\u6ee1\u6e05\uff09\u201d\u653f\u7b56\u3002\u660e\u6e05\u4ea4\u6218\u65f6\uff0c\u671d\u9c9c\u51fa\u5175\u52a9\u660e\uff0c\u53c8\u4f9b\u7ed9\u660e\u519b\u76ae\u5c9b\u603b\u5175\u5b98\u6bdb\u6587\u9f99\u7cae\u98df\uff0c\u6210\u4e3a\u6ee1\u6e05\u540e\u65b9\u7684\u4e00\u4e2a\u7275\u5236\u3002\u7687\u592a\u6781\u8fdb\u653b\u671d\u9c9c\uff0c\u53ef\u4ee5\u89e3\u51b3\u7ecf\u6d4e\u4e0a\u3001\u6218\u7565\u4e0a\u7684\u53cc\u91cd\u56f0\u96be\uff0c\u540c\u65f6\u5728\u5fc5\u5b9a\u53ef\u4ee5\u5f97\u5230\u7684\u519b\u4e8b\u80dc\u5229\u4e4b\u4e2d\u6811\u7acb\u5a01\u671b\uff0c\u5de9\u56fa\u6743\u4f4d\u3002 \u3000\u3000\u660e\u671d\u65b9\u9762\u7684\u56f0\u96be\u4e5f\u76f8\u5f53\u4e0d\u5c0f\u3002 \u3000\u3000\u8bad\u7ec3\u4e00\u652f\u65e2\u80fd\u5b88\uff0c\u53c8\u80fd\u6218\uff0c\u518d\u80fd\u8fdb\u4e00\u6b65\u6536\u590d\u5931\u5730\u7684\u7cbe\u9510\u91ce\u6218\u519b\uff0c\u9700\u8981\u76f8\u5f53\u65f6\u95f4\u3002 \u3000\u3000\u8881\u5d07\u7115\u4efb\u5b81\u524d\u9053\u4f65\u4e8b\u65f6\uff0c\u5c71\u6d77\u5173\u5916\u56db\u57ce\uff0c\u7eb5\u6df1\u7ea6\u4e8c\u767e\u91cc\uff0c\u5e7f\u7ea6\u56db\u5341\u91cc\uff0c\u5c6f\u5175\u516d\u4e07\u4f59\u4eba\uff0c\u7cae\u9977\u5168\u9760\u5173\u5185\u652f\u7ed9\u3002\u540e\u6765\u5728\u5b59\u627f\u5b97\u3001\u8881\u5d07\u7115\u4e3b\u6301\u4e0b\uff0c\u6062\u590d\u9526\u5dde\u3001\u4e2d\u5c6f\u3001\u5927\u51cc\u6cb3\u8bf8\u57ce\uff0c\u56fd\u9632\u524d\u7ebf\u5411\u5317\u63a8\u5c55\uff0c\u5c6f\u7530\u6570\u5343\u9877\uff0c\u5175\u58eb\u8db3\u98df\u3002\u9ad8\u7b2c\u4ee3\u5b59\u627f\u5b97\u4e3a\u7ecf\u7565\uff0c\u5c3d\u5f03\u9526\u5dde\u8bf8\u57ce\uff0c\u5b81\u8fdc\u6ca1\u6709\u4e86\u5916\u536b\uff0c\u4e5f\u6ca1\u6709\u4e86\u7cae\u6e90\u3002\u9760\u671d\u5ef7\u63a5\u6d4e\u662f\u5f88\u9760\u4e0d\u4f4f\u7684\uff0c\u671d\u5ef7\u5bf9\u4e8e\u62d6\u6b20\u7cae\u9977\u5411\u6765\u5174\u8da3\u6d53\u539a\u3002\u8881\u5d07\u7115\u505a\u8fbd\u4e1c\u5de1\u629a\uff0c\u9996\u8981\u76ee\u6807\u662f\u4fee\u590d\u9526\u5dde\u3001\u5927\u51cc\u6cb3\u7b49\u57ce\u5821\u7684\u5b88\u5907\uff0c\u7136\u540e\u5c6f\u7530\u8015\u79cd\u3002\u4f46\u7b51\u57ce\u5de5\u7a0b\u8d39\u65f6\u751a\u4e45\uff0c\u53c8\u4e0d\u80fd\u53d7\u5230\u654c\u4eba\u5e72\u6270\uff0c\u5728\u548c\u6ee1\u6e05\u5904\u4e8e\u6218\u4e89\u72b6\u6001\u4e4b\u65f6\u65e0\u6cd5\u8fdb\u884c\u3002 \u3000\u3000\u6240\u4ee5\u660e\u6e05\u53cc\u65b9\uff0c\u90fd\u671f\u671b\u6709\u4e00\u6bb5\u4f11\u6218\u7684\u65f6\u671f\uff0c\u4ee5\u4fbf\u8fdb\u884c\u81ea\u5df1\u7684\u8ba1\u5212\u3002\u660e\u65b9\u662f\u7ec3\u5175\u3001\u7b51\u57ce\u3001\u5c6f\u7530\uff0c\u6e05\u65b9\u662f\u8fdb\u653b\u671d\u9c9c\uff0c\u5de9\u56fa\u7edf\u6cbb\u3002\u5728\u8fd9\u6837\u7684\u5c40\u52bf\u4e0b\uff0c\u5177\u5907\u4e86\u8bae\u548c\u7684\u6761\u4ef6\u3002 \u3000\u3000\u660e\u65b9\u7684\u8bae\u548c\u662f\u653b\u52bf\u7684\uff0c\u6700\u540e\u76ee\u6807\u662f\u6d88\u706d\u6ee1\u6e05\uff0c\u6536\u590d\u5168\u90e8\u8fbd\u4e1c\u5931\u5730\u3002\u6e05\u65b9\u7684\u8bae\u548c\u4e3b\u8981\u662f\u5b88\u52bf\uff0c\u76ee\u7684\u5728\u5de9\u56fa\u5df2\u5f97\u7684\u571f\u5730\uff0c\u8981\u660e\u65b9\u627f\u8ba4\u53cc\u65b9\u7684\u73b0\u6709\u7586\u754c\uff0c\u53cc\u65b9\u548c\u5e73\u5171\u5904\uff0c\u8fdb\u884c\u8d38\u6613\u3002 \u3000\u3000\u56e0\u4e3a\u660e\u6e05\u53cc\u65b9\u7684\u56fd\u529b\u5b9e\u5728\u592a\u8fc7\u60ac\u6b8a\u3002\u660e\u65b9\u90a3\u65f6\u7684\u4eba\u53e3\uff0c\u5b98\u65b9\u7684\u7eaa\u5f55\u662f\u516d\u5343\u591a\u4e07\uff0c\u5b9e\u9645\u4e0a\u8fdc\u4e0d\u6b62\u6b64\u6570\uff0c\u5f53\u65f6\u7537\u4e01\u8981\u88ab\u653f\u5e9c\u5f81\u53bb\u4e49\u52a1\u52b3\u52a8\uff0c\u4e0d\u53c2\u52a0\u7684\u8981\u7f34\u94b1\u4ee3\u66ff\uff0c\u6240\u4ee5\u767e\u59d3\u5c3d\u53ef\u80fd\u7684\u7792\u62a5\u4eba\u53e3\u3002\u5916\u56fd\u5b66\u8005\u4eec\u7684\u4f30\u8ba1\u76f8\u4e92\u5dee\u8ddd\u5f88\u5927\uff0c\u6700\u9ad8\u7684\u4f30\u8ba1\u8ba4\u4e3a\u90a3\u65f6\u4e2d\u56fd\u4eba\u53e3\u662f\u4e00\u4ebf\u4e94\u5343\u4e07\u4eba\u3002\u6211\u76f8\u4fe1\u51b3\u4e0d\u4f1a\u5c11\u4e8e\u4e00\u4ebf\u4eba\u2462\u3002\u5973\u771f\u4eba\u5927\u6982\u4e0d\u5230\u4e94\u5341\u4e07\u4eba\u2463\u3002\u4eba\u53e3\u7684\u5bf9\u6bd4\u662f\u4e8c\u767e\u6bd4\u4e00\u751a\u81f3\u4e09\u767e\u6bd4\u4e00\u3002\u6ee1\u6e05\u6240\u5360\u7684\u571f\u5730\uff0c\u53ea\u662f\u4eca\u65e5\u5409\u6797\u3001\u8fbd\u5b81\u3001\u9ed1\u9f99\u6c5f\u7684\u4e00\u90e8\u4efd\uff0c\u4e0e\u660e\u65b9\u76f8\u6bd4\u4e5f\u662f\u76f8\u5dee\u6781\u8fdc\u3002\u660e\u65b9\u706b\u5668\u7280\u5229\uff0c\u8461\u8404\u7259\u5927\u70ae\u5c24\u5176\u975e\u6e05\u5175\u6240\u80fd\u62b5\u6321\u3002 \u3000\u3000\u6e05\u65b9\u7684\u957f\u5904\uff0c\u4e3b\u8981\u53ea\u662f\u201c\u660e\u671d\u672c\u8eab\u7684\u8150\u8d25\u201d\uff0c\u4ee5\u53ca\u6e05\u519b\u6218\u6597\u529b\u5f3a\u52b2\u548c\u7edf\u5e05\u90e8\u9ad8\u660e\u7684\u519b\u4e8b\u624d\u80fd\u3002\u53ea\u8981\u8881\u5d07\u7115\u9547\u5b88\u5b81\u8fdc\uff0c\u6e05\u65b9\u7684\u957f\u5904\u5c31\u53d1\u6325\u4e0d\u51fa\u4e86\u3002\u6301\u4e45\u7684\u7f20\u6597\u4e0b\u53bb\uff0c\u6ee1\u6e05\u52bf\u5fc5\u96be\u4ee5\u652f\u6301\u3002 \u3000\u3000\u8881\u5d07\u7115\u5b81\u8fdc\u5927\u6377\uff0c\u5728\u519b\u4e8b\u4e0a\u5e76\u65e0\u5341\u5206\u91cd\u8981\u7684\u610f\u4e49\uff0c\u56e0\u4e3a\u5e76\u6ca1\u6709\u6467\u6bc1\u6e05\u519b\u7684\u4e3b\u529b\uff0c\u751a\u81f3\u6ca1\u6709\u524a\u5f31\u6e05\u519b\u7684\u6218\u6597\u529b\u3002\u7136\u800c\u5728\u653f\u6cbb\u4e0a\uff0c\u5bf9\u58eb\u6c14\u4e0e\u6c11\u5fc3\u5374\u6709\u975e\u5e38\u5de8\u5927\u7684\u632f\u594b\u4f5c\u7528\uff0c\u8fd9\u4f7f\u4e2d\u56fd\u519b\u6c11\u77e5\u9053\u6e05\u519b\u4e5f\u4e0d\u662f\u4e0d\u4f1a\u6253\u8d25\u4ed7\u7684\u3002\u7ecf\u6b64\u4e00\u5f79\u4e4b\u540e\uff0c\u672c\u6765\u6295\u964d\u4e86\u6ee1\u6e05\u7684\u8bb8\u591a\u6c49\u4eba\u5b98\u540f\u548c\u58eb\u5352\u53c8\u9003\u56de\u6765\u4e86\u3002\u5b81\u8fdc\u57ce\u5934\u7684\u5927\u70ae\uff0c\u8f70\u788e\u4e86\u201c\u5973\u771f\u6ee1\u4e07\u4e0d\u53ef\u654c\u201d\u7684\u795e\u8bdd\u2464\u3002 \u3000\u3000\u6e05\u65b9\u4ece\u6765\u6ca1\u6709\u671f\u671b\u771f\u80fd\u5f81\u670d\u660e\u671d\u3002\u52aa\u5c14\u54c8\u8d64\u548c\u7687\u592a\u6781\u7684\u7956\u5b97\uff0c\u957f\u671f\u6765\u505a\u660e\u671d\u6240\u5c01\u7684\u8fb9\u7586\u5c0f\u5b98\u3002\u52aa\u5c14\u54c8\u8d64\u5e7c\u65f6\u4f4f\u5728\u660e\u671d\u5927\u5c06\u674e\u6210\u6881\u5bb6\u91cc\uff0c\u7c7b\u4f3c\u7ae5\u4ec6\u5974\u96b6\u3002\u6240\u4ee5\u4ed6\u4eec\u5bf9\u4e8e\u660e\u671d\u6709\u5148\u5929\u6027\u7684\u656c\u754f\uff0c\u81ea\u5351\u611f\u5f88\u6df1\u3002\u5b81\u8fdc\u4e4b\u6218\uff0c\u4f7f\u4ed6\u4eec\u4e0b\u610f\u8bc6\u4e2d\u9690\u4f0f\u7740\u7684\u81ea\u5351\u611f\u53c8\u5f00\u59cb\u62ac\u5934\u3002 \u3000\u3000\u660e\u671d\u662f\u81ea\u5df1\u8986\u706d\u7684\uff0c\u5e76\u975e\u7ed9\u6ee1\u6e05\u6240\u6253\u57ae\u3002 \u3000\u3000\u6ee1\u6e05\u4e0e\u660e\u519b\u4ea4\u6218\uff0c\u59cb\u7ec8\u5f3a\u8c03\u201c\u4e03\u5927\u6068\u201d\uff0c\u6ee1\u6e05\u8ba4\u4e3a\u660e\u671d\u6709\u4e03\u4ef6\u5927\u4e8b\u6b3a\u4fae\u5973\u771f\u4eba\uff0c\u903c\u5f97\u4ed6\u4eec\u5fcd\u65e0\u53ef\u5fcd\uff0c\u624d\u8d77\u5175\u53cd\u6297\u2465\u3002\u6ee1\u6e05\u4e00\u76f4\u6ca1\u6709\u81ea\u5c45\u80fd\u4e0e\u660e\u671d\u5904\u4e8e\u5e73\u7b49\u5730\u4f4d\u3002\u201c\u4e03\u5927\u6068\u201d\u7684\u57fa\u672c\u601d\u60f3\uff0c\u662f\u62b1\u6028\u660e\u671d\u4f5c\u4e3a\u6700\u9ad8\u7edf\u6cbb\u8005\uff0c\u5374\u5728\u52aa\u5c14\u54c8\u8d64\u4e0e\u654c\u5bf9\u90e8\u65cf\u53d1\u751f\u4e89\u6267\u65f6\u8892\u62a4\u5bf9\u65b9\uff0c\u6ca1\u6709\u516c\u5e73\u5904\u7406\uff0c\u90a3\u662f\u4e0b\u7ea7\u5bf9\u4e0a\u7ea7\u7684\u7533\u8bc9\u3002\u4f8b\u5982\u7b2c\u4e94\u5927\u6068\u7684\u201c\u8001\u5973\u4e8b\u4ef6\u201d\uff0c\u53f6\u8d6b\u90e8\u7684\u4e00\u4e2a\u738b\u516c\u672c\u6765\u7b54\u5e94\u628a\u4ed6\u5341\u56db\u5c81\u7684\u59b9\u59b9\u9001\u7ed9\u52aa\u5c14\u54c8\u8d64\u4e3a\u59be\uff0c\u4f46\u4e8c\u5341\u5e74\u540e\uff0c\u8fd9\u4e2a\u4e09\u5341\u516d\u5c81\u7684\u201c\u8001\u5973\u201d\u6539\u5ac1\u7ed9\u8499\u53e4\u738b\u5b50\uff0c\u52aa\u5c14\u54c8\u8d64\u8ba4\u5b9a\u662f\u51fa\u4e8e\u660e\u671d\u7684\u6388\u610f\uff0c\u8eab\u4e3a\u4e0a\u7ea7\u800c\u4e0d\u79c9\u516c\u65ad\u4e8b\u3002 \u3000\u3000\u5dee\u4e0d\u591a\u5728\u6bcf\u4e2a\u6218\u5f79\u4e4b\u540e\uff0c\u6e05\u65b9\u603b\u662f\u5efa\u8bae\u8c08\u548c\u3002\u56e0\u4e3a\u4ed6\u4eec\u5bf9\u4e8e\u76ee\u524d\u7684\u6210\u5c31\u65e9\u5c31\u559c\u51fa\u671b\u5916\uff0c\u672c\u6765\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\uff0c\u53ea\u6c42\u660e\u65b9\u6b63\u5f0f\u627f\u8ba4\u4ed6\u4eec\u6240\u5360\u7684\u571f\u5730\uff0c\u8ba9\u4ed6\u4eec\u80fd\u6c38\u4e45\u4fdd\u6709\uff0c\u5c31\u5df2\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u4f46\u660e\u671d\u4ece\u6765\u7f6e\u4e4b\u4e0d\u7406\uff0c\u8ba4\u4e3a\u5bf9\u65b9\u6839\u672c\u6ca1\u6709\u8c08\u548c\u7684\u8d44\u683c\u3002\u660e\u671d\u7684\u6001\u5ea6\u662f\u8fd9\u6837\uff1a\u201c\u4f60\u4eec\u662f\u671d\u5ef7\u7684\u90e8\u5c5e\uff0c\u53ea\u80fd\u670d\u4ece\u547d\u4ee4\uff0c\u600e\u4e48\u80fd\u8981\u6c42\u8c08\u5224\u548c\u5e73\uff1f\u201d\u8fd9\u79cd\u6b7b\u8981\u9762\u5b50\u7684\u5fc3\u7406\uff0c\u4f7f\u5f97\u660e\u671d\u59cb\u7ec8\u6ca1\u6709\u80fd\u4e89\u53d6\u5230\u4e00\u6bb5\u5598\u606f\u7684\u65f6\u95f4\u6765\u6574\u987f\u519b\u5907\u3001\u5de9\u56fa\u9632\u5fa1\u3002 \u3000\u3000\u8881\u5d07\u7115\u5145\u5206\u4e86\u89e3\u5230\u4e89\u53d6\u6682\u65f6\u548c\u5e73\u7684\u5fc5\u8981\u3002\u52aa\u5c14\u54c8\u8d64\u7684\u901d\u4e16\u6b63\u662f\u4e00\u4e2a\u597d\u673a\u4f1a\u3002\u8fd9\u65f6\u521a\u597d\u6709\u4e00\u4e2a\u4e94\u53f0\u5c71\u7684\u5587\u561b\u674e\u5587\u561b\u6765\u5230\u5b81\u8fdc\u3002\u6ee1\u6d32\u4eba\u4fe1\u4f5b\u6559\uff0c\u5c0a\u5d07\u5587\u561b\uff0c\u8881\u5d07\u7115\u5c31\u8bf7\u674e\u5587\u561b\u4f5c\u5c45\u95f4\u7684\u4f7f\u8005\uff0c\u6d3e\u4e86\u4e24\u540d\u90fd\u53f8\u548c\u968f\u4ece\u7b49\u4e09\u5341\u4e09\u4eba\uff0c\u4e8e\u5929\u542f\u516d\u5e74\u5341\u6708\u53bb\u6c88\u9633\u540a\u796d\u52aa\u5c14\u54c8\u8d64\u4e4b\u4e27\uff0c\u4f5c\u521d\u6b65\u7684\u548c\u5e73\u8bd5\u63a2\u3002\u4f46\u4ed6\u77e5\u9053\u671d\u5ef7\u7edd\u4e0d\u559c\u6b22\u63d0\u201c\u8bae\u548c\u201d\u4e24\u5b57\uff0c\u6240\u4ee5\u62a5\u544a\u671d\u5ef7\u65f6\uff0c\u53ea\u8bf4\u662f\u6d3e\u4eba\u53bb\u7aa5\u63a2\u865a\u5b9e\uff0c\u4ee5\u51b3\u5b9a\u5bf9\u4e4b\u5f81\u8ba8\u5462\uff0c\u8fd8\u662f\u62db\u5b89\u2466\u3002\u8fd9\u79cd\u5938\u5927\u7684\u8bf4\u6cd5\uff0c\u76ee\u7684\u81ea\u5728\u6ee1\u8db3\u7687\u5e1d\u548c\u5927\u81e3\u7684\u865a\u8363\u5fc3\u3002 \u3000\u3000\u660e\u6e05\u53cc\u65b9\u7edf\u5e05\u90fd\u719f\u77e5\u300a\u4e09\u56fd\u6f14\u4e49\u300b\u4e2d\u7684\u6545\u4e8b\uff0c\u8881\u5d07\u7115\u8fd9\u51fa\u201c\u67f4\u6851\u53e3\u5367\u9f99\u540a\u4e27\u201d\uff0c\u7687\u592a\u6781\u5982\u4f55\u4f1a\u4e0d\u7701\u5f97\uff1f\u4ed6\u5c06\u8ba1\u5c31\u8ba1\uff0c\u4e8e\u5341\u4e00\u6708\u6d3e\u4e86\u4e24\u540d\u4f7f\u8005\uff0c\u4e0e\u674e\u5587\u561b\u4e00\u8d77\u6765\u5230\u5b81\u8fdc\uff0c\u81f4\u4e66\u8881\u5d07\u7115\uff0c\u8868\u793a\u4e86\u548c\u5e73\u7684\u610f\u5411\u3002\u5176\u4e2d\u8bf4\uff1a\u201c\u4f60\u505c\u606f\u5e72\u6208\uff0c\u6d3e\u674e\u5587\u561b\u6765\u540a\u4e27\uff0c\u5e76\u8d3a\u65b0\u541b\u767b\u4f4d\u3002\u4f60\u65e2\u4ee5\u793c\u6765\uff0c\u6211\u4e5f\u5f53\u4ee5\u793c\u5f80\uff0c\u6240\u4ee5\u6d3e\u5b98\u6765\u9053\u8c22\u3002\u81f3\u4e8e\u548c\u8bae\u4e00\u4e8b\uff0c\u6211\u7236\u4eb2\u4e0a\u6b21\u6765\u5b81\u8fdc\u65f6\uff0c\u66fe\u6709\u6587\u4e66\u7ed9\u660e\u671d\u671d\u5ef7\uff0c\u8bf7\u4f60\u8f6c\u5448\uff0c\u4f46\u8fc4\u4eca\u6ca1\u6709\u7b54\u590d\u3002\u4f60\u7684\u541b\u4e3b\u5982\u679c\u7b54\u5e94\u524d\u4e66\uff0c\u613f\u610f\u548c\u5e73\uff0c\u5e94\u5f53\u4ee5\u8bda\u4fe1\u4e3a\u5148\u3002\u201d \u3000\u3000\u4e66\u4fe1\u4e2d\u5c06\u91d1\u56fd\uff08\u5f53\u65f6\u6ee1\u6e05\u7684\u6b63\u5f0f\u56fd\u53f7\u662f\u201c\u91d1\u201d\uff0c\u540e\u6765\u624d\u6539\u4e3a\u201c\u5927\u6e05\u201d\u3002\u2467\uff09\u4e0e\u4e2d\u56fd\u5e73\u5934\u5e76\u5217\u3002\u8881\u5d07\u7115\u6df1\u523b\u4e86\u89e3\u671d\u5ef7\u81ea\u9ad8\u81ea\u5927\uff0c\u5bf9\u4e8e\u6587\u4e66\u7684\u4f53\u4f8b\u5341\u5206\u770b\u91cd\uff0c\u5982\u5c06\u6765\u4fe1\u8f6c\u5448\uff0c\u5fc5\u5b9a\u8981\u78b0\u5927\u9489\u5b50\uff0c\u540c\u65f6\u89c1\u5230\u4fe1\u4e2d\u8bed\u6c14\u4e5f\u4e0d\u5927\u5ba2\u6c14\uff0c\u4fbf\u544a\u77e5\u4f7f\u8005\u8bf4\uff0c\u6b64\u4fe1\u683c\u5f0f\u4e0d\u5408\uff0c\u788d\u96be\u5165\u594f\uff0c\u5c06\u539f\u4fe1\u4ea4\u7ed9\u4f7f\u8005\u9000\u56de\u3002\u7687\u592a\u6781\u6539\u5199\u4e86\u4fe1\u5c01\u4e0a\u7684\u683c\u5f0f\uff0c\u8881\u5d07\u7115\u8ba4\u4e3a\u4ecd\u7136\u4e0d\u5bf9\uff0c\u53c8\u518d\u9000\u56de\u3002\u7687\u592a\u6781\u7b2c\u4e09\u6b21\u6539\u5199\uff0c\u81ea\u5904\u4e8e\u8f83\u4f4e\u5730\u4f4d\uff0c\u8881\u5d07\u7115\u624d\u6536\u4e86\u4fe1\u3002\u4f46\u660e\u671d\u4ecd\u662f\u4e00\u8d2f\u7684\u4e0d\u7b54\u3002 \u3000\u3000\u7b2c\u4e8c\u5e74\u6b63\u6708\uff08\u5728\u91d1\u56fd\u662f\u5929\u806a\u5143\u5e74\uff09\uff0c\u7687\u592a\u6781\u518d\u9063\u524d\u4f7f\uff0c\u81f4\u4e66\u8881\u5d07\u7115\u6c42\u548c\uff0c\u4fe1\u4e2d\u8bf4\uff1a\u201c\u4e24\u56fd\u6240\u4ee5\u6784\u5175\uff0c\u5728\u4e8e\u4ee5\u524d\u660e\u671d\u6d3e\u5230\u8fbd\u4e1c\u7684\u5b98\u5458\u8ba4\u4e3a\u4e2d\u56fd\u7687\u5e1d\u662f\u5728\u5929\u4e0a\uff0c\u81ea\u9ad8\u81ea\u5927\uff0c\u6b3a\u538b\u5f31\u5c0f\u90e8\u65cf\uff0c\u6211\u4eec\u5fcd\u65e0\u53ef\u5fcd\uff0c\u624d\u8d77\u5175\u53cd\u6297\u3002\u201d\u4e0b\u9762\u7167\u4f8b\u5217\u4e3e\u4e03\u5927\u6068\uff0c\u7136\u540e\u63d0\u8bae\u8bb2\u548c\u3002\u8bb2\u548c\u8981\u9001\u793c\uff0c\u8981\u6c42\u6700\u521d\u7f14\u7ed3\u548c\u7ea6\u65f6\u4e2d\u56fd\u9001\u7ed9\u91d1\u56fd\u91d1\u5341\u4e07\u4e24\uff0c\u94f6\u767e\u4e07\u4e24\u3001\u7f0e\u767e\u4e07\u5339\u3001\u5e03\u5343\u4e07\u5339\u3002\u7f14\u7ea6\u540e\u4e24\u56fd\u6bcf\u5e74\u4ea4\u6362\u793c\u7269\uff0c\u91d1\u56fd\u9001\u793c\uff1a\u4e1c\u73e0\u5341\u9897\uff0c\u8c82\u76ae\u5343\u5f20\u3001\u4eba\u53c2\u5343\u65a4\u3002\u4e2d\u56fd\u9001\u793c\uff1a\u91d1\u4e00\u4e07\u4e24\u3001\u94f6\u5341\u4e07\u4e24\u3001\u7f0e\u5341\u4e07\u5339\u3001\u5e03\u4e09\u5341\u4e07\u5339\u3002\u4e24\u56fd\u7f14\u7ed3\u548c\u7ea6\u540e\uff0c\u5c31\u5bf9\u5929\u53d1\u8a93\uff0c\u6c38\u8fdc\u4fe1\u5b88\u3002 \u3000\u3000\u6240\u63d0\u7684\u8981\u6c42\u662f\u7ecf\u6d4e\u6027\u7684\uff0c\u53ef\u89c1\u5f53\u65f6\u6ee1\u6e05\u6df1\u611f\u8d22\u653f\u56f0\u96be\uff0c\u5bf9\u5e03\u5339\u7684\u9700\u8981\u5c24\u5176\u6bb7\u5207\u3002 \u3000\u3000\u5927\u6982\u8881\u5d07\u7115\u8981\u594f\u62a5\u671d\u5ef7\uff0c\u7b49\u5019\u6279\u590d\uff0c\u6240\u4ee5\u9694\u4e86\u4e24\u4e2a\u6708\u91d1\u56fd\u4f7f\u8005\u624d\u56de\u53bb\uff0c\u968f\u540c\u660e\u65b9\u4f7f\u8005\uff0c\u5e26\u53bb\u8881\u5d07\u7115\u53ca\u674e\u5587\u561b\u7684\u4e66\u4fe1\u5404\u4e00\uff1b\u731c\u60f3\u671d\u5ef7\u5bf9\u91d1\u65b9\u7684\u8981\u6c42\u5168\u90e8\u62d2\u7edd\uff0c\u6240\u4ee5\u8881\u5d07\u7115\u65e0\u6cd5\u4f5c\u51fa\u4efb\u4f55\u8ba9\u6b65\uff0c\u4ed6\u7684\u56de\u4fe1\u5185\u5bb9\u96c4\u8fa9\uff0c\u6587\u91c7\u7115\u53d1\uff0c\u8bf4\u9053\uff1a\u8fc7\u53bb\u7684\u7ea0\u7eb7\uff0c\u90fd\u662f\u56e0\u53cc\u65b9\u8fb9\u5883\u5c0f\u6c11\u53e3\u820c\u4e89\u7ade\u800c\u8d77\uff0c\u8fd9\u4e9b\u4eba\u90fd\u5df2\u53d7\u5230\u4e86\u5e94\u5f97\u7684\u60e9\u7f5a\uff0c\u518d\u8981\u8ffd\u7a76\u662f\u975e\uff0c\u4e5f\u5df2\u65e0\u6cd5\u5230\u9634\u4e16\u5730\u5e9c\u53bb\u7ec6\u67e5\uff0c\u53ea\u76fc\u53cc\u65b9\u90fd\u5fd8\u8bb0\u4e86\u5427\u3002\u4f60\u5341\u5e74\u82e6\u6218\uff0c\u65e2\u7136\u4e3a\u7684\u53ea\u662f\u8fd9\u4e03\u4ef6\u4e8b\uff0c\u73b0\u5728\u4f60\u7684\u4ec7\u654c\u53f6\u8d6b\u7b49\u7b49\u90fd\u65e9\u7ed9\u4f60\u706d\u4e86\u3002\u4e3a\u4e86\u4f60\u4eec\u7528\u5175\uff0c\u8fbd\u6cb3\u4e24\u5cb8\u6b7b\u8005\u5c82\u6b62\u5341\u4eba\uff1f\u4ef3\u79bb\u6539\u5ac1\u7684\u54ea\u91cc\u53ea\u6709\u8001\u5973\u4e00\u4eba\uff1f\u8fbd\u6c88\u754c\u5185\u4eba\u6c11\u7684\u6027\u547d\u90fd\u4e0d\u80fd\u81ea\u4fdd\uff0c\u8fd8\u8bf4\u751a\u4e48\u8d22\u7269\uff1f\u4f60\u7684\u4ec7\u6028\u65e9\u90fd\u96ea\u4e86\uff0c\u65e9\u5df2\u5fd7\u5f97\u610f\u6ee1\u3002\u53ea\u4e0d\u8fc7\u8fd9\u4e9b\u6781\u60e8\u6781\u75db\u4e4b\u4e8b\uff0c\u6211\u4eec\u660e\u671d\u96be\u4ee5\u5fcd\u53d7\u7f62\u4e86\u3002\u4eca\u540e\u82e5\u8981\u4fee\u597d\uff0c\u90a3\u4e48\u8bf7\u95ee\uff1a\u4f60\u5982\u4f55\u9000\u51fa\u5df2\u5360\u53bb\u7684\u57ce\u6c60\u5730\u65b9\uff1f\u5982\u4f55\u9001\u8fd8\u4fd8\u864f\u53bb\u7684\u7537\u5973\u767e\u59d3\uff1f\u53ea\u6709\u76fc\u4f60\u4ec1\u660e\u6148\u60e0\u3001\u656c\u5929\u7231\u4eba\u800c\u4f5c\u51fa\u51b3\u5b9a\u4e86\u3002\u4f60\u6240\u8981\u6c42\u7684\u8d22\u7269\uff0c\u4ee5\u4e2d\u56fd\u7269\u8d44\u7684\u4e30\u5bcc\uff0c\u672c\u6765\u4e0d\u4f1a\u5c0f\u6c14\uff0c\u53ea\u662f\u8fc7\u53bb\u6ca1\u6709\u6210\u4f8b\uff0c\u591a\u53d6\u4e5f\u4e0d\u5408\u5929\u610f\uff0c\u8fd8\u662f\u8bf7\u4f60\u91cd\u884c\u659f\u914c\u7f62\u3002\u548c\u8c08\u6b63\u5728\u8fdb\u884c\uff0c\u4f60\u4e3a\u751a\u4e48\u53c8\u5bf9\u671d\u9c9c\u7528\u5175\uff1f\u6211\u4eec\u6587\u6b66\u5b98\u5c5e\u4e0d\u514d\u6000\u7591\u4f60\u8a00\u4e0d\u7531\u8877\u4e86\u3002\u5e0c\u671b\u4f60\u64a4\u5175\uff0c\u4ee5\u8bc1\u660e\u4f60\u7684\u76db\u5fb7\u3002 \u3000\u3000\u674e\u5587\u561b\u7684\u4fe1\u4e2d\u8bf4\uff1a\u8881\u5de1\u629a\u662f\u6d3b\u4f5b\u51fa\u4e16\uff0c\u5bf9\u4e8e\u662f\u975e\u9053\u7406\uff0c\u5fc3\u4e0b\u5341\u5206\u5206\u660e\uff0c\u8fd9\u6837\u7684\u597d\u4eba\u662f\u4e0d\u5bb9\u6613\u9047\u5230\u7684\uff0c\u613f\u6c57\u4e0e\u5404\u738b\u5b50\u4e00\u5207\u90fd\u653e\u5f00\u4e86\u5427\uff0c\u4f5b\u8bf4\uff1a\u201c\u82e6\u6d77\u65e0\u8fb9\uff0c\u56de\u5934\u662f\u5cb8\u201d\u3002 \u3000\u3000\u7687\u592a\u6781\u56de\u4fe1\u7ed9\u8881\u5d07\u7115\u8bf4\uff1a\u8fc7\u53bb\u7684\u6028\u4ec7\uff0c\u5f53\u7136\u662f\u7b97\u4e86\uff0c\u5426\u5219\u53c8\u4f55\u5fc5\u8bae\u548c\u4fee\u597d\uff1f\u4f60\u4eec\u7684\u571f\u5730\u4eba\u6c11\u5f52\u6211\u4e4b\u540e\uff0c\u90fd\u5df2\u5b89\u5b9a\uff0c\u8fd9\u662f\u5929\u610f\uff0c\u5982\u679c\u91cd\u884c\u5f52\u8fd8\uff0c\u90a3\u65e2\u8fdd\u53cd\u5929\u610f\uff0c\u53c8\u5bf9\u4e0d\u8d77\u4eba\u6c11\u3002\u91d1\u56fd\u6240\u4ee5\u8981\u51fa\u5175\u671d\u9c9c\uff0c\u5b8c\u5168\u662f\u7531\u4e8e\u671d\u9c9c\u4e0d\u5bf9\uff0c\u73b0\u5728\u5df2\u8bb2\u548c\u4e86\u3002\u8bf4\u5230\u201c\u8a00\u4e0d\u7531\u8877\u201d\uff0c\u4e3a\u751a\u4e48\u4f60\u4e00\u9762\u8bf4\u8981\u4fee\u597d\uff0c\u4e00\u9762\u53c8\u6d3e\u54e8\u5352\u6765\u6211\u65b9\u4fa6\u5bdf\uff0c\u6536\u7eb3\u6211\u65b9\u9003\u4ea1\uff0c\u90e8\u961f\u903c\u8fd1\u6211\u8fb9\u754c\uff0c\u4fee\u7b51\u57ce\u5821\uff1f\u5176\u5b9e\u662f\u4f60\u624d\u201c\u8a00\u4e0d\u7531\u8877\u201d\uff0c\u6211\u56fd\u5c06\u5e05\u5bf9\u4f60\u4e5f\u5927\u6709\u6000\u7591\u3002\u81f3\u4e8e\u6240\u8981\u6c42\u7684\u201c\u521d\u548c\u4e4b\u793c\u201d\uff0c\u91d1\u94f6\u7b49\u53ef\u4ee5\u51cf\u534a\uff0c\u7f0e\u5e03\u53ea\u8981\u539f\u6765\u8981\u6c42\u7684\u534a\u6210\u3002\u6211\u65b9\u4e5f\u4ee5\u4e1c\u73e0\u3001\u4eba\u53c2\u3001\u72d0\u76ae\u3001\u8c82\u76ae\u7b49\u7269\u8fd8\u8d60\uff0c\u8868\u793a\u53cc\u65b9\u5b8c\u5168\u516c\u5e73\u3002\u65e2\u548c\u4e4b\u540e\uff0c\u53cc\u65b9\u4e92\u8d60\u4ecd\u5982\u524d\u8bae\u3002\u5982\u679c\u540c\u610f\uff0c\u5e0c\u671b\u529e\u5f97\u8d8a\u5feb\u8d8a\u597d\u3002 \u3000\u3000\u5173\u4e8e\u6765\u5f80\u4e66\u4fe1\u7684\u683c\u5f0f\uff0c\u7687\u592a\u6781\u63d0\u8bae\uff1a\u201c\u5929\u201d\u5b57\u6700\u9ad8\uff0c\u660e\u671d\u7687\u5e1d\u4f4e\u201c\u5929\u201d\u4e00\u5b57\uff0c\u91d1\u56fd\u6c57\u4f4e\u660e\u671d\u7687\u5e1d\u4e00\u5b57\uff0c\u660e\u671d\u8bf8\u81e3\u4f4e\u91d1\u56fd\u6c57\u4e00\u5b57\u3002 \u3000\u3000\u4ed6\u7b54\u590d\u674e\u5587\u561b\u7684\u4fe1\u4e2d\uff0c\u62b1\u6028\u660e\u671d\u7687\u5e1d\u5bf9\u4ed6\u7684\u4e66\u4fe1\u4ece\u6765\u4e0d\u52a0\u7406\u776c\uff1b\u53c8\u8bf4\uff1a\u4f60\u529d\u6211\u201c\u82e6\u6d77\u65e0\u8fb9\uff0c\u56de\u5934\u662f\u5cb8\u201d\uff0c\u8fd9\u8bdd\u5f88\u5bf9\uff0c\u4f46\u4e3a\u751a\u4e48\u53ea\u529d\u6211\u800c\u4e0d\u53bb\u529d\u660e\u671d\u7687\u5e1d\uff1f\u5982\u679c\u53cc\u65b9\u90fd\u56de\u5934\u4fee\u597d\uff0c\u5c82\u4e0d\u751a\u5584\uff1f \u3000\u3000\u540e\u6765\u7687\u592a\u6781\u53c8\u81f4\u4e66\u8881\u5d07\u7115\uff0c\u6297\u8bae\u4ed6\u4fee\u7b51\u5854\u5c71\u3001\u5927\u51cc\u6cb3\u3001\u9526\u5dde\u7b49\u57ce\u7684\u9632\u5fa1\u5de5\u4e8b\uff0c\u8ba4\u4e3a\u662f\u7f3a\u4e4f\u548c\u5e73\u8bda\u610f\uff0c\u5e76\u63d0\u8bae\u5212\u5b9a\u7586\u754c\u3002 \u3000\u3000\u5e73\u5fc3\u800c\u8bba\uff0c\u660e\u671d\u671d\u5ef7\u77a7\u4e0d\u8d77\u91d1\u56fd\uff0c\u4e8e\u5bf9\u65b9\u6765\u4fe1\u4e00\u6982\u4e0d\u7b54\uff0c\u53ea\u7531\u5730\u65b9\u5b98\u548c\u5bf9\u65b9\u901a\u4fe1\uff0c\u91d1\u56fd\u4e5f\u96be\u514d\u6c14\u6124\u3002\u91d1\u56fd\u7684\u7ecf\u6d4e\u8981\u6c42\uff0c\u867d\u8bf4\u662f\u53cc\u65b9\u4e92\u8d60\uff0c\u5b9e\u8d28\u4e0a\u5f53\u7136\u662f\u91d1\u65b9\u5927\u5360\u4fbf\u5b9c\u3002\u91d1\u65b9\u7b54\u5e94\u8d60\u9001\u7684\u4e1c\u73e0\u3001\u4eba\u53c2\u3001\u8c82\u76ae\u7b49\u7269\uff0c\u5927\u6982\u6700\u591a\u53ea\u80fd\u62b5\u8fc7\u7ef8\u7f0e\u5e03\u5339\u7684\u4ef7\u503c\uff0c\u660e\u65b9\u4ed8\u51fa\u7684\u6bcf\u5e74\u4e00\u4e07\u4e24\u9ec4\u91d1\u3001\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u7b49\u4e8e\u662f\u65e0\u507f\u8d60\u4e0e\u3002\u90a3\u65f6\u4e00\u4e24\u9ec4\u91d1\u7ea6\u7b49\u4e8e\u5341\u4e24\u94f6\u5b50\uff08\u660e\u521d\u7b49\u4e8e\u56db\u4e24\uff0c\u540e\u6765\u91d1\u8d35\u94f6\u8d31\uff09\uff0c\u660e\u671d\u6bcf\u5e74\u4ee5\u4e8c\u5341\u4e07\u4e24\u94f6\u5b50\u4e70\u5f97\u4e00\u5e74\u548c\u5e73\uff0c\u4ee3\u4ef7\u4f4e\u5ec9\u4e4b\u81f3\u3002\u718a\u5ef7\u5f3c\u5b88\u8fbd\u4e4b\u65f6\uff0c\u5355\u662f\u4ed6\u4e00\u519b\u6bcf\u4e2a\u6708\u7684\u9977\u94f6\u5c31\u9700\u5341\u591a\u4e07\u4e24\u94f6\u5b50\u3002\u5982\u679c\u6709\u4e86\u5341\u5e74\u548c\u5e73\uff0c\u5927\u52a0\u6574\u7f16\u519b\u961f\uff0c\u518d\u51fa\u5175\u6311\u6218\uff0c\u4e3b\u52a8\u4e0e\u88ab\u52a8\u7684\u5f62\u52bf\u5c31\u8f6c\u8fc7\u6765\u4e86\u3002 \u3000\u3000\u7687\u592a\u6781\u5bf9\u4e8e\u7f0e\u5e03\u7684\u8981\u6c42\u4e00\u4e0b\u5b50\u5c31\u51cf\u5c11\u4e86\u767e\u5206\u4e4b\u4e5d\u5341\u4e94\uff0c\u800c\u4e14\u53c8\u5efa\u8bae\u4ee5\u9002\u5f53\u793c\u7269\u8fd8\u62a5\uff0c\u5e0c\u671b\u548c\u8bae\u5c3d\u5feb\u529e\u7406\uff0c\u53ef\u89c1\u5bf9\u4e8e\u7f14\u7ed3\u548c\u5e73\u7684\u786e\u5177\u6709\u6781\u5927\u8bda\u610f\u3002\u4ed6\u81ea\u77e5\u4eba\u53e3\u4e0e\u5175\u529b\u6709\u9650\uff0c\u7ecf\u4e0d\u8d77\u957f\u671f\u7684\u6d88\u8017\u6218\u2468\u3002\u6b64\u540e\u6bcf\u53d1\u751f\u4e00\u6b21\u6218\u4e89\uff0c\u4fbf\u63d0\u4e00\u6b21\u548c\u5e73\u8981\u6c42\u3002 \u3000\u3000\u5f53\u65f6\u8bae\u548c\u7684\u969c\u788d\uff0c\u4e3b\u8981\u662f\u5728\u660e\u671d\u7684\u6587\u5b98\u3002 \u3000\u3000\u660e\u671d\u7684\u5927\u81e3\u719f\u6089\u53f2\u4e8b\uff0c\u4e00\u63d0\u5230\u4e0e\u91d1\u4eba\u8bae\u548c\uff0c\u7acb\u523b\u60f3\u5230\u7684\u5c31\u662f\u5357\u5b8b\u548c\u91d1\u56fd\u7684\u548c\u8bae\uff0c\u4eba\u4eba\u90fd\u6015\u505a\u79e6\u6867\u3002\u5927\u5bb6\u62b1\u7740\u540c\u6837\u7684\u5fc3\u7406\uff1a\u8d5e\u6210\u548c\u91d1\u4eba\u8bae\u548c\uff0c\u5c31\u662f\u5927\u6c49\u5978\u79e6\u6867\u3002\u8fd9\u662f\u5f53\u65f6\u8bfb\u4e66\u4eba\u5fc3\u4e2d\u7684\u201c\u6761\u4ef6\u53cd\u5c04\u201d\u3002 \u3000\u3000\u8881\u5d07\u7115\u4ece\u5b9e\u9645\u60c5\u51b5\u51fa\u53d1\u4e3b\u5f20\u8bae\u548c\uff0c\u671d\u81e3\u90fd\u4e0d\u9644\u548c\u3002\u8fbd\u4e1c\u7ecf\u7565\u738b\u4e4b\u81e3\u66f4\u4e3a\u6b64\u4e00\u518d\u5f39\u52be\u8881\u5d07\u7115\uff0c\u8bf4\u8fd9\u79cd\u4e3b\u5f20\u5c31\u50cf\u5b8b\u4eba\u548c\u91d1\u4eba\u8bae\u548c\u90a3\u6837\u611a\u8822\u81ea\u8bef\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u660e\u671d\u5f53\u65f6\u4e0e\u5b8b\u671d\u7684\u60c5\u51b5\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u5728\u5357\u5b8b\u65f6\uff0c\u91d1\u5175\u5df2\u5360\u9886\u4e86\u4e2d\u56fd\u5317\u65b9\u7684\u5168\u90e8\uff0c\u8bae\u548c\u7b49\u4e8e\u662f\u653e\u5f03\u6536\u590d\u5931\u5730\u3002\u4f46\u5728\u660e\u671d\u5929\u542f\u5e74\u95f4\uff0c\u91d1\u4eba\u53ea\u5360\u9886\u4e86\u8fbd\u4e1c\uff0c\u8fbd\u897f\u7684\u5357\u90e8\u5728\u660e\u4eba\u624b\u4e2d\uff0c\u6682\u65f6\u8bae\u548c\uff0c\u5f71\u54cd\u751a\u5c0f\u3002 \u3000\u3000\u5357\u5b8b\u4e4b\u65f6\uff0c\u5cb3\u98de\u3001\u97e9\u4e16\u5fe0\u3001\u5218\u9449\u3001\u5f20\u4fca\u3001\u5434\u7498\u3001\u5434\u73a0\u7b49\u5927\u5c06\uff0c\u90fd\u662f\u5175\u7cbe\u80fd\u6218\uff0c\u91d1\u4eba\u540e\u65b9\u4e0d\u7a33\uff0c\u5f62\u52bf\u4e0a\u5229\u4e8e\u5317\u4f10\uff0c\u8bae\u548c\u662f\u5931\u5374\u4e86\u6062\u590d\u7684\u826f\u673a\u3002\u660e\u672b\u519b\u961f\u7684\u6218\u6597\u529b\u8fdc\u4e0d\u53ca\u91d1\u5175\uff0c\u60df\u4e00\u53ef\u4ee5\u4f9d\u8d56\u7684\u53ea\u6709\u897f\u6d0b\u5927\u70ae\u3002\u4f46\u5f53\u65f6\u7684\u5927\u70ae\u5341\u5206\u7b28\u91cd\uff0c\u4e0d\u6613\u642c\u52a8\uff0c\u53ea\u80fd\u7528\u4e8e\u5b88\u57ce\uff0c\u4e0d\u80fd\u7528\u4e8e\u8fd0\u52a8\u6218\u3002 \u3000\u3000\u5bf9\u4e8e\u660e\u671d\u6700\u91cd\u8981\u7684\u662f\uff0c\u5b8b\u91d1\u8bae\u548c\uff0c\u5b8b\u65b9\u7edd\u5bf9\u5c48\u8fb1\uff0c\u6bcf\u5e74\u7247\u9762\u8fdb\u8d21\u91d1\u5e1b\uff0c\u5e76\u975e\u53cc\u65b9\u4e92\u8d60\u3002\u5b8b\u671d\u7687\u5e1d\u5bf9\u91d1\u79f0\u81e3\u247d\u3002\u7136\u800c\u7687\u592a\u6781\u5374\u7518\u613f\u4f4e\u4e8e\u660e\u671d\u7687\u5e1d\u4e00\u7ea7\uff0c\u53ea\u8981\u6c42\u6bd4\u660e\u671d\u7684\u8bf8\u81e3\u9ad8\u4e00\u7ea7\u3002\u7687\u592a\u6781\u4e00\u518d\u8868\u793a\uff0c\u91d1\u56fd\u4e0d\u6562\u4e0e\u4e2d\u56fd\u5e76\u5217\uff0c\u53ea\u5e0c\u671b\u5730\u4f4d\u6bd4\u5bdf\u54c8\u5c14\u8499\u53e4\u4eba\u9ad8\u4e00\u7b49\u5c31\u6ee1\u8db3\u4e86\u247e\u3002\u4ed6\u548c\u8881\u5d07\u7115\u4e66\u4fe1\u6765\u5f80\uff0c\u6001\u5ea6\u4e0a\u662f\u5f88\u660e\u663e\u7684\u8c26\u606d\u247f\u3002 \u3000\u3000\u53ef\u89c1\u5b8b\u91d1\u8bae\u548c\u4e0e\u660e\u91d1\u8bae\u548c\u4e24\u4e8b\uff0c\u6839\u672c\u4e0d\u80fd\u76f8\u63d0\u5e76\u8bba\u3002\u7687\u592a\u6781\u660e\u767d\u660e\u4eba\u7684\u60f3\u6cd5\uff0c\u6240\u4ee5\u540e\u6765\u7d22\u6027\u6539\u4e86\u56fd\u53f7\uff0c\u4e0d\u79f0\u91d1\u56fd\uff0c\u800c\u79f0\u201c\u5927\u6e05\u201d\uff0c\u4ee5\u514d\u5f15\u8d77\u6c49\u4eba\u5fc3\u7406\u4e0a\u654c\u5bf9\u6027\u7684\u8fde\u9501\u53cd\u5e94\u2480\u3002 \u3000\u3000\u8881\u5d07\u7115\u548c\u7687\u592a\u6781\u4fe1\u4f7f\u5f80\u6765\uff0c\u4f46\u56e0\u671d\u4e2d\u5927\u81e3\u89c6\u548c\u8bae\u5982\u6d2a\u6c34\u731b\u517d\uff0c\u8c08\u5224\u5168\u65e0\u7ed3\u679c\u3002 \u3000\u3000\u5f53\u65f6\u4e3b\u5f20\u548c\u91d1\u4eba\u8bae\u548c\uff0c\u975e\u4f46\u5192\u4e3e\u56fd\u4e4b\u5927\u4e0d\u97ea\uff0c\u800c\u4e14\u662f\u5192\u5386\u53f2\u4e0a\u4e4b\u5927\u4e0d\u97ea\u3002\u4e2d\u56fd\u8fc7\u53bb\u53d7\u5230\u5916\u65cf\u7684\u519b\u4e8b\u538b\u529b\u800c\u8bae\u548c\uff0c\u901a\u5e38\u603b\u662f\u5c48\u8fb1\u6027\u7684\uff0c\u6c49\u4eba\u5bf9\u8fd9\u4ef6\u4e8b\u5177\u6709\u5148\u5929\u6027\u7684\u53cd\u611f\uff0c\u975e\u5e38\u65b9\u4fbf\u7684\u5c31\u5c06\u201c\u8bae\u548c\u201d\u3001\u201c\u6295\u964d\u201d\u3001\u201c\u6c49\u5978\u201d\u4e09\u4ef6\u4e8b\u8054\u7cfb\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5f53\u519b\u4e8b\u4e0a\u51c6\u5907\u6ca1\u6709\u5145\u5206\u4e4b\u65f6\uff0c\u6682\u65f6\u4e0e\u5916\u654c\u8bae\u548c\u4ee5\u4e89\u53d6\u65f6\u95f4\uff0c\u4e2d\u56fd\u5386\u53f2\u4e0a\u4e24\u4e2a\u6700\u51fa\u540d\u7684\u82f1\u4e3b\u90fd\u66fe\u505a\u8fc7\u3002\u6c49\u9ad8\u7956\u5218\u90a6\u66fe\u4e0e\u5308\u5974\u8bae\u548c\uff0c\u4e89\u53d6\u65f6\u95f4\u6765\u57f9\u517b\u56fd\u529b\uff0c\u5230\u6c49\u6b66\u5e1d\u65f6\u624d\u5927\u4e3e\u53cd\u51fb\u3002\u5510\u592a\u5b97\u674e\u4e16\u6c11\u66fe\u4e0e\u7a81\u53a5\u8bae\u548c\uff08\u90a3\u65f6\u662f\u4ed6\u7236\u4eb2\u674e\u6e0a\u505a\u7687\u5e1d\uff0c\u4f46\u548c\u8bae\u5b9e\u9645\u4e0a\u662f\u674e\u4e16\u6c11\u6240\u51b3\u5b9a\uff09\uff0c\u7b49\u5230\u6574\u987f\u597d\u519b\u961f\u540e\u624d\u6d3e\u674e\u9756\u5317\u4f10\uff0c\u5927\u7834\u7a81\u53a5\u3002\u4e0d\u8fc7\u8fd9\u4e0d\u662f\u4e2d\u56fd\u5386\u53f2\u4e0a\u4f20\u7edf\u89c2\u5ff5\u7684\u4e3b\u6d41\u3002\u4e3b\u6d41\u601d\u60f3\u662f\uff1a\u201c\u4e0e\u4fb5\u7565\u672c\u56fd\u7684\u5916\u654c\u8bae\u548c\u662f\u6295\u964d\uff0c\u662f\u6c49\u5978\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u540c\u662f\u8bae\u548c\uff0c\u5374\u6709\u6027\u8d28\u4e0a\u7684\u4e0d\u540c\uff0c\u51b3\u4e0d\u80fd\u4e00\u6982\u800c\u8bba\u3002\u57fa\u672c\u5173\u952e\u5728\u4e8e\uff1a\u8bae\u548c\u662f\u6c38\u4e45\u6027\u7684\u6295\u964d\uff1f\u8fd8\u662f\u6682\u65f6\u59a5\u534f\u3001\u79ef\u6781\u51c6\u5907\u800c\u7ec8\u4e8e\u5927\u4e3e\u53cd\u653b\u3001\u5f97\u5230\u6700\u540e\u80dc\u5229\uff1f\u8bae\u548c\u505c\u6218\u53ea\u662f\u7b56\u7565\uff0c\u51b3\u4e0d\u7b49\u4e8e\u6295\u964d\u3002\u7136\u800c\u660e\u672b\u5f53\u56fd\u7684\u541b\u81e3\u90fd\u662f\u5eb8\u624d\uff0c\u5bf9\u4e8e\u654c\u6211\u53cc\u65b9\u529b\u91cf\u7684\u5bf9\u6bd4\u3001\u5927\u5c40\u53d1\u5c55\u7684\u524d\u9014\u90fd\u662f\u832b\u65e0\u6240\u77e5\uff0c\u65e2\u65e0\u51b3\u6218\u7684\u521a\u52c7\uff0c\u4e5f\u65e0\u7b49\u5f85\u7684\u97e7\u529b\u3002\u8881\u5d07\u7115\u7cbe\u660e\u6b63\u786e\u7684\u6218\u7565\u89c1\u89e3\uff0c\u671d\u5ef7\u4e2d\u4e0b\u610f\u8bc6\u7684\u8ba4\u4e3a\u662f\u201c\u6c49\u5978\u601d\u60f3\u201d\u3002 \u3000\u3000\u8881\u5d07\u7115\u5f53\u7136\u77e5\u9053\u5982\u6b64\u529b\u6392\u4f17\u8bae\uff0c\u5bf9\u4e8e\u81ea\u8eab\u975e\u5e38\u4e0d\u5229\uff0c\u7136\u800c\u4ed6\u5df2\u5c06\u81ea\u8eab\u5b89\u5371\u5168\u7136\u7f6e\u4e4b\u5ea6\u5916\uff0c\u53ea\u662f\u4ee5\u5927\u5c40\u4e3a\u91cd\u2481\u3002\u4ee5\u4ed6\u5982\u6b64\u521a\u70c8\u4e4b\u4eba\uff0c\u5bf9\u58f0\u540d\u81ea\u7136\u975e\u5e38\u7231\u60dc\uff0c\u7ed9\u4eba\u9a82\u201c\u6c49\u5978\u201d\uff0c\u90a3\u662f\u6700\u75db\u82e6\u7684\u4e8b\u3002\u6bd4\u8f83\u8d77\u6765\uff0c\u6b7b\u5b88\u5b81\u8fdc\u3001\u6297\u62d2\u5927\u654c\uff0c\u5728\u4ed6\u5e76\u4e0d\u7b97\u662f\u96be\u4e8b\uff0c\u6700\u591a\u6253\u4e0d\u8fc7\uff0c\u4e00\u6b7b\u6b89\u56fd\u4fbf\u662f\uff0c\u90a3\u662f\u5fc3\u5b89\u7406\u5f97\u7684\u3002\u4f46\u8981\u8d1f\u62c5\u201c\u5386\u53f2\u7f6a\u4eba\u3001\u6c11\u65cf\u7f6a\u4eba\u3001\u540d\u6559\u7f6a\u4eba\u201d\u7684\u8d23\u4efb\uff0c\u53ef\u8270\u5de8\u5f97\u591a\u4e86\u3002\u8d8a\u662f\u4e0d\u81ea\u79c1\u7684\u4eba\uff0c\u8d8a\u662f\u521a\u5f3a\u7684\u4eba\uff0c\u8d8a\u662f\u4e0d\u91cd\u89c6\u6027\u547d\u800c\u4e0d\u80af\u5fcd\u53d7\u803b\u8fb1\u3002\u8d8a\u662f\u5112\u5bb6\u7684\u4e66\u8bfb\u5f97\u591a\uff0c\u5fc3\u4e2d\u5386\u53f2\u611f\u6781\u5176\u6df1\u539a\u7684\u4eba\uff0c\u8d8a\u662f\u5b9d\u8d35\u81ea\u5df1\u7684\u540d\u8282\u3002\u6587\u5929\u7965\u300a\u6b63\u6c14\u6b4c\u300b\u4e2d\u6240\u4e3e\u90a3\u4e9b\u6177\u6168\u6fc0\u70c8\u7684\u4e8b\u8ff9\uff0c\u5982\u5f20\u5de1\u7762\u9633\u6b7b\u5b88\uff0c\u989c\u6772\u537f\u5e38\u5c71\u9a82\u8d3c\uff0c\u8881\u5d07\u7115\u505a\u8d77\u6765\u5e76\u4e0d\u56f0\u96be\u3002\u5bf9\u4e8e\u6027\u683c\u67d4\u548c\u7684\u4eba\uff0c\u5f53\u7136\u662f\u59d4\u66f2\u6c42\u5168\u6613\u800c\u6177\u6168\u5c31\u4e49\u96be\uff0c\u5728\u8881\u5d07\u7115\u8fd9\u6837\u7684\u4f1f\u70c8\u4e4b\u58eb\uff0c\u5374\u662f\u5b88\u5b81\u8fdc\u6613\u800c\u4e3b\u548c\u8bae\u96be\u3002\u4e3b\u5f20\u8bae\u548c\uff0c\u4ed6\u5fc5\u987b\u8fdd\u53cd\u5386\u53f2\u4f20\u7edf\u3001\u8fdd\u53cd\u4e3e\u56fd\u8206\u8bba\u3001\u8fdd\u53cd\u671d\u5ef7\u51b3\u7b56\u3001\u66f4\u8fdd\u53cd\u81ea\u5df1\u7684\u6027\u683c\u3002\u4e0a\u4e0b\u53e4\u4eca\uff0c\u4e00\u5207\u90fd\u53cd\uff0c\u8fde\u81ea\u5df1\u90fd\u53cd\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u51b2\u52a8\u7684\u70ed\u60c5\u7684\u8c6a\u6770\uff0c\u662f\u201c\u5b81\u4e3a\u76f4\u6298\u5251\u3001\u72b9\u80dc\u66f2\u5168\u94a9\u201d\u7684\u521a\u58eb\uff0c\u662f\u884c\u4e8b\u4e0d\u987e\u4e00\u5207\u3001\u201c\u51e0\u5927\u5c31\u51e0\u5927\u201d\u7684\u86ee\u5b50\uff0c\u53ef\u662f\u4ed6\u7ec8\u4e8e\u51b3\u5b9a\uff1a\u201c\u5fcd\u8fb1\u8d1f\u91cd\u201d\u3002 \u3000\u3000\u5728\u4ed6\u90a3\u4e2a\u65f6\u4ee3\uff0c\u7edd\u65e0\u5c0a\u91cd\u5c11\u6570\u4eba\u610f\u89c1\u7684\u4e60\u60ef\u4e0e\u98ce\u5ea6\u3002\u8fde\u8881\u5d07\u7115\u81ea\u5df1\u5728\u5185\uff0c\u90fd\u76f8\u4fe1\u201c\u56fd\u4eba\u7686\u66f0\u53ef\u6740\u201d\u591a\u534a\u4fbf\u662f\u201c\u53ef\u6740\u201d\u3002\u90a3\u662f\u4e00\u4e2a\u975e\u6b64\u5373\u5f7c\u3001\u51b3\u4e0d\u5bb9\u5fcd\u5f02\u89c1\u7684\u65f6\u4ee3\uff0c\u662f\u6b63\u4eba\u541b\u5b50\u7eb7\u7eb7\u727a\u7272\u751f\u547d\u800c\u63d0\u51fa\u6b63\u4e49\u89c1\u89e3\u7684\u65f6\u4ee3\u3002\u5351\u9119\u7684\u5978\u515a\u8d8a\u662f\u5728\u671d\u4e2d\u4f5c\u5a01\u4f5c\u798f\uff0c\u58eb\u6797\u4e2d\u5bf9\u98ce\u9aa8\u548c\u8282\u64cd\u8d8a\u662f\u770b\u91cd\u3002\u4e1c\u6c49\u548c\u660e\u672b\uff0c\u662f\u4e2d\u56fd\u5386\u53f2\u4e0a\u8bfb\u4e66\u4eba\u9053\u5fb7\u4ef7\u503c\u6700\u53d7\u91cd\u89c6\u7684\u4e24\u4e2a\u65f6\u671f\u3002\u5c81\u5bd2\u575a\u8282\uff0c\u51b0\u96ea\u6e05\u64cd\uff0c\u5728\u5f53\u65f6\u7684\u9053\u5fb7\u89c2\u5ff5\u4e2d\uff0c\u4e0e\u201c\u5fe0\u201d\u3001\u201c\u5b5d\u201d\u5177\u6709\u76f8\u540c\u7684\u7b2c\u4e00\u7b49\u5730\u4f4d\u3002\u4ed6\u5f88\u7231\u4ea4\u670b\u53cb\uff0c\u77e5\u4ea4\u4e2d\u6709\u4e0d\u5c11\u662f\u6e05\u6d41\u6d3e\u7684\u4eba\u3002\u5982\u679c\u4ed6\u7ec8\u4e8e\u56e0\u4e3b\u548c\u800c\u4e3a\u5929\u4e0b\u58eb\u8bba\u6240\u4e0d\u9f7f\uff0c\u5bf9\u4ed6\u5c06\u662f\u591a\u4e48\u4e25\u91cd\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5bf9\u91d1\u4eba\u7684\u548c\u8c08\u5e76\u4e0d\u662f\u516c\u5f00\u8fdb\u884c\u7684\uff0c\u56e0\u6b64\u5e76\u6ca1\u6709\u53d7\u5230\u666e\u904d\u7684\u62a8\u51fb\uff0c\u4f46\u4ed6\u5f53\u7136\u9884\u6599\u5230\u5c06\u6765\u7ec8\u4e8e\u8981\u516c\u5f00\uff0c\u6e05\u8bae\u548c\u77e5\u53cb\u7684\u8c34\u8d23\u4e0d\u53ef\u907f\u514d\u7684\u4f1a\u843d\u5230\u5934\u4e0a\u3002 \u3000\u3000\u5728\u8881\u5d07\u7115\u6b7b\u540e\u5341\u4e09\u5e74\u7684\u5d07\u796f\u5341\u4e94\u5e74\uff0c\u660e\u671d\u5c40\u52bf\u5df2\u7cdc\u70c2\u4e0d\u53ef\u6536\u62fe\u3002\u6d2a\u627f\u7574\u4e8e\u6240\u7edf\u5927\u519b\u5168\u519b\u8986\u6ca1\u540e\u6295\u964d\u6ee1\u6e05\u3002\u677e\u5c71\u3001\u9526\u5dde\u5931\u5b88\u3002\u5d07\u796f\u4fbf\u60f3\u548c\u6ee1\u6e05\u8bae\u548c\uff0c\u4ee5\u4fbf\u4e13\u5fc3\u5bf9\u4ed8\u674e\u81ea\u6210\u3001\u5f20\u732e\u5fe0\u7b49\u6c11\u519b\u3002\u5175\u90e8\u5c1a\u4e66\u9648\u65b0\u7532\u66f4\u660e\u767d\u65e0\u529b\u4e24\u7ebf\u4f5c\u6218\uff0c\u6697\u4e2d\u4e0e\u7687\u5e1d\u7b79\u5212\u5bf9\u6ee1\u6e05\u8bb2\u548c\u3002\u5d07\u796f\u548c\u9648\u65b0\u7532\u4e0d\u65ad\u5546\u8bae\uff0c\u671d\u4e2d\u5176\u4ed6\u5927\u81e3\u542c\u5230\u4e86\u98ce\u58f0\uff0c\u4fbf\u7eb7\u7eb7\u4e0a\u594f\uff0c\u53cd\u5bf9\u548c\u8bae\u3002\u5d07\u796f\u77e2\u53e3\u4e0d\u8ba4\uff0c\u8bf4\u6839\u672c\u6ca1\u6709\u8bae\u548c\u7684\u4e8b\uff0c\u4f60\u4eec\u53cd\u5bf9\u751a\u4e48\uff1f\u5d07\u796f\u6bcf\u6b21\u4eb2\u7b14\u5199\u624b\u8bcf\u7ed9\u9648\u65b0\u7532\uff0c\u603b\u662f\u90d1\u91cd\u8b66\u8beb\uff1a\u8fd9\u662f\u5929\u5927\u673a\u5bc6\uff0c\u5343\u4e07\u4e0d\u53ef\u6cc4\u6f0f\u800c\u8ba9\u7fa4\u81e3\u77e5\u9053\u4e86\u3002 \u3000\u3000\u8be5\u5e74\u516b\u6708\uff0c\u5d07\u796f\u6d3e\u4eb2\u4fe1\u53c8\u9001\u4e00\u9053\u4eb2\u7b14\u8bcf\u4e66\u53bb\u7ed9\u9648\u65b0\u7532\uff0c\u50ac\u4ed6\u5c3d\u5feb\u8bbe\u6cd5\u548c\u6ee1\u6e05\u8bae\u548c\u3002\u9648\u65b0\u7532\u51fa\u5916\u529e\u4e8b\u53bb\u4e86\uff0c\u4e0d\u5728\u5bb6\uff0c\u90a3\u4eba\u4fbf\u5c06\u7687\u5e1d\u7684\u5bc6\u8bcf\u7559\u5728\u4ed6\u4e66\u623f\u4e2d\u7684\u51e0\u4e0a\u800c\u53bb\u3002\u9648\u65b0\u7532\u7684\u5bb6\u7ae5\u8bef\u4ee5\u4e3a\u662f\u666e\u901a\u7684\u300a\u5858\u62a5\u300b\uff08\u5404\u7701\u6d3e\u5458\u5728\u4eac\u6240\u6284\u5f55\u7684\u4e00\u822c\u6027\u4e0a\u8c15\u4e0e\u594f\u7ae0\uff0c\u79f0\u4e3a\u300a\u5858\u62a5\u300b\uff09\uff0c\u62ff\u51fa\u53bb\u4ea4\u7ed9\u5404\u7701\u9a7b\u4eac\u529e\u4e8b\u5904\u4f20\u6284\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u7687\u5e1d\u6697\u4e2d\u5728\u4e3b\u6301\u548c\u8bae\u7684\u4e8b\u5c31\u516c\u5f00\u4e86\u51fa\u6765\uff0c\u7fa4\u81e3\u62ff\u5230\u4e86\u8bc1\u636e\uff0c\u767b\u65f6\u54d7\u7136\uff0c\u7acb\u523b\u4e0a\u594f\u7ae0\u53cd\u5bf9\u3002 \u3000\u3000\u7687\u5e1d\u518d\u4e5f\u65e0\u6cd5\u62b5\u8d56\uff0c\u607c\u6012\u4e4b\u6781\uff0c\u4e0b\u8bcf\u8981\u9648\u65b0\u7532\u89e3\u91ca\uff0c\u8d23\u95ee\u4ed6\u4e3a\u751a\u4e48\u4e3b\u5f20\u8bae\u548c\uff0c\u7f6a\u5927\u6076\u6781\u4e4b\u81f3\u3002\u9648\u65b0\u7532\u7684\u58f0\u8fa9\u4e66\u4e2d\u5f15\u8ff0\u4e86\u4e0d\u5c11\u7687\u5e1d\u624b\u8bcf\u4e2d\u7684\u53e5\u5b50\uff0c\u8bc1\u660e\u8fd9\u662f\u51fa\u4e8e\u7687\u4e0a\u7684\u5723\u610f\u3002\u5d07\u796f\u66f4\u5931\u9762\u5b50\uff0c\u8001\u7f9e\u6210\u6012\uff0c\u4e0b\u65e8\uff1a\u9648\u65b0\u7532\u7740\u5373\u65a9\u51b3\u3002\u7406\u7531\u662f\u6d41\u5bc7\u7834\u57ce\uff0c\u5bb3\u6b7b\u7687\u5e1d\u7684\u4eb2\u85e9\uff0c\u5175\u90e8\u5c1a\u4e66\u5e94\u8d1f\u5168\u8d23\u3002 \u3000\u3000\u90a3\u65f6\u8ddd\u660e\u671d\u4e4b\u4ea1\u5df2\u4e0d\u8fc7\u4e00\u5e74\u534a\uff0c\u5c40\u9762\u7684\u6076\u52a3\u53ef\u60f3\u800c\u77e5\uff0c\u4f46\u7fa4\u81e3\u8fd8\u662f\u575a\u51b3\u53cd\u5bf9\u8bae\u548c\uff0c\u8fde\u7687\u5e1d\u4e5f\u4e0d\u5f97\u4e0d\u5077\u5077\u548c\u56fd\u9632\u90e8\u957f\u6697\u4e2d\u5546\u91cf\uff0c\u8868\u9762\u4e0a\u575a\u51b3\u4e0d\u80af\u627f\u8ba4\uff0c\u6700\u540e\u6d88\u606f\u6cc4\u6f0f\uff0c\u4fbf\u6740\u4e86\u56fd\u9632\u90e8\u957f\u4ee5\u5378\u81ea\u5df1\u8d23\u4efb\u3002\u4ece\u8fd9\u4ef6\u4e8b\u4e2d\uff0c\u53ef\u4ee5\u89c1\u5230\u5f53\u65f6\u5bf9\u201c\u8bae\u548c\u201d\u662f\u5982\u4f55\u7684\u5fcc\u8bb3\uff0c\u8206\u8bba\u538b\u529b\u662f\u5982\u4f55\u6c89\u91cd\u3002\u8fde\u5d07\u796f\u8fd9\u6837\u72e0\u8fa3\u7684\u7687\u5e1d\uff0c\u4e5f\u4e0d\u6562\u5bf9\u7fa4\u81e3\u627f\u8ba4\u6709\u8bae\u548c\u4e4b\u610f\u3002 \u3000\u3000\u8881\u5d07\u7115\u5374\u80c6\u6562\u8fdb\u884c\u8bae\u548c\u3002\u90a3\u6b63\u662f\u51fa\u4e8e\u66fe\u5b50\u6240\u8bf4\u201c\u53ea\u8981\u6df1\u4fe1\u81ea\u5df1\u7684\u9053\u7406\u5bf9\uff0c\u867d\u6709\u5343\u4e07\u4eba\u53cd\u5bf9\uff0c\u6211\u8fd8\u662f\u5e72\u4e86\u201d\u90a3\u79cd\u6d69\u7136\u4e4b\u6c14\u2482\u3002 \u3000\u3000\u8bf8\u845b\u4eae\u51fa\u5e08\u5317\u4f10\uff0c\u5929\u4e0b\u7686\u79f0\u5176\u5fe0\u3002\u5cb3\u98de\u82e6\u6218\u6297\u654c\uff0c\u5929\u4e0b\u7686\u77e5\u5176\u52c7\u3002\u8881\u5d07\u7115\u7684\u529f\u4e1a\u6216\u8bb8\u6bd4\u4e0d\u4e0a\u8bf8\u845b\u4eae\u548c\u5cb3\u98de\uff0c\u867d\u7136\uff0c\u90a3\u4e5f\u662f\u5f88\u96be\u771f\u6b63\u6bd4\u8f83\u7684\uff0c\u7136\u800c\u4ed6\u8eab\u5904\u5acc\u7591\u4e4b\u5730\u800c\u884c\u4e3e\u4e16\u5acc\u7591\u4e4b\u4e8b\uff0c\u8fd9\u79cd\u7cbe\u795e\u4e0a\u7684\u75db\u82e6\u8d1f\u62c5\uff0c\u8bf8\u845b\u4eae\u548c\u5cb3\u98de\u5374\u5e78\u800c\u4e0d\u5fc5\u7ecf\u53d7\u3002 \u3000\u3000\u8881\u5d07\u7115\u6709\u4e00\u53e5\u8bd7\uff1a\u201c\u5fc3\u82e6\u540e\u4eba\u77e5\u201d\u3002\u5f53\u771f\u662f\u82f1\u96c4\u5bc2\u5bde\uff0c\u58ee\u58eb\u60b2\u6b4c\u3002\u4ed6\u660e\u77e5\u4e0d\u80fd\u5f97\u5230\u5f53\u65f6\u7684\u8c05\u89e3\uff0c\u53ea\u76fc\u671b\u81ea\u5df1\u8fd9\u756a\u82e6\u5fc3\u5b64\u8be3\u80fd\u4e3a\u540e\u4eba\u6240\u77e5\u3002\u5f53\u6211\u5199\u5230\u8fd9\u4e00\u6bb5\u6587\u5b57\u65f6\uff0c\u60f3\u5230\u4ed6\u7684\u803f\u803f\u4e4b\u6000\uff0c\u60a0\u60a0\u4e4b\u5fc3\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u611f\u5230\u4e86\u5267\u70c8\u7684\u5fc3\u9178\uff0c\u611f\u5230\u4e86\u4ed6\u82f1\u96c4\u6027\u683c\u4e2d\u5de8\u5927\u7684\u60b2\u58ee\u7f8e\uff0c\u6df1\u523b\u7684\u51c4\u6006\u610f\u3002 \u3000\u3000\u6b63\u786e\u7684\u6218\u7565\u51b3\u7b56\u65e0\u6cd5\u6267\u884c\uff0c\u671d\u653f\u8d8a\u6765\u8d8a\u8150\u8d25\uff0c\u5728\u9b4f\u5fe0\u8d24\u7b3c\u7f69\u4e00\u5207\u7684\u90aa\u6076\u52bf\u529b\u4e0b\u505a\u5b98\uff0c\u5929\u5929\u90fd\u53ef\u4ee5\u9001\u6389\u4e86\u6027\u547d\u3002\u5173\u5916\u9177\u5bd2\u7684\u5929\u6c14\uff0c\u751f\u957f\u4e8e\u4e9a\u70ed\u5e26\u7684\u5e7f\u4e1c\u4eba\u5b9e\u5728\u611f\u5230\u5f88\u96be\u62b5\u53d7\u3002\u5728\u8fd9\u671f\u95f4\uff0c\u8881\u5d07\u7115\u4ece\u5e7f\u4e1c\u62db\u52df\u6765\u7684\u4eba\u5458\u4e2d\u6709\u4eba\u8981\u56de\u6545\u4e61\u53bb\u4e86\uff0c\u4e34\u522b\u65f6\u95ee\u4ed6\uff1a\u4f60\u7559\u5728\u8fd9\u91cc\u7ee7\u7eed\u62c5\u5f53\u8270\u5371\u5462\uff0c\u8fd8\u662f\u56de\u4e61\u4ee5\u6c42\u5e73\u5b89\uff1f\u4ed6\u5199\u4e86\u4e00\u9996\u8bd7\u56de\u7b54\uff1a\u6211\u548c\u4f60\u66fe\u540c\u751f\u5171\u6b7b\uff0c\u6211\u7684\u5185\u5fc3\u4f60\u8fd8\u4e0d\u660e\u767d\u5417\uff1f\u53c8\u4f55\u5fc5\u95ee\u5b89\u5371\u53bb\u7559\uff1f\u6211\u5728\u8fd9\u91cc\u594b\u4e0d\u987e\u8eab\uff0c\u672c\u6765\u4e0d\u662f\u4e3a\u4e86\u5bcc\u8d35\u3002\u6545\u4e61\u7684\u4eb2\u53cb\u4eec\u5982\u679c\u95ee\u8d77\uff0c\u8bf7\u4f60\u8f6c\u544a\uff1a\u8fb9\u754c\u8fd8\u6ca1\u6709\u5e73\u9756\uff0c\u6211\u53ea\u6709\u611f\u5230\u60ed\u6127\uff0c\u5f53\u7136\u8981\u7ee7\u7eed\u5e72\u4e0b\u53bb\u2483\u3002 \u3000\u3000\u8881\u5d07\u7115\u662f\u4e09\u5144\u5f1f\u4e2d\u7684\u8001\u4e8c\u3002\u5927\u54e5\u5d07\u707f\u5f53\u4ed6\u5728\u5173\u5916\u65f6\u5728\u6545\u4e61\u901d\u4e16\u3002\u4e09\u5f1f\u5d07\u715c\u968f\u7740\u4ed6\u5728\u519b\u4e2d\u529e\u4e8b\uff0c\u540e\u6765\u4e5f\u544a\u8f9e\u56de\u4e61\u3002\u8881\u5d07\u7115\u4ece\u5b81\u8fdc\u9001\u4ed6\u5230\u5c71\u6d77\u5173\u800c\u5206\u624b\uff0c\u5199\u4e86\u4e24\u9996\u8bd7\u7ed9\u4ed6\uff0c\u8bf4\uff1a\u8fb9\u7586\u9700\u8981\u4eba\u5b88\u5fa1\uff0c\u5347\u5e73\u8fd8\u6ca1\u6709\u5f97\u5230\uff0c\u6211\u65e9\u5df2\u51b3\u5fc3\u62a5\u56fd\uff0c\u5b89\u5371\u53bb\u7559\u7684\u95ee\u9898\u4e0d\u5fc5\u63d0\u4e86\u2484\u3002 \u3000\u3000\u2460\u7687\u592a\u6781\u5728\u897f\u65b9\u4eba\u7684\u4e66\u4e2d\u5199\u4f5cAbahai\uff08\u6ce8\uff1a\uff21\uff42\uff41\uff48\uff41\uff49\uff09\uff0c\u6cd5\u56fd\u5b66\u8005\u683c\u5965\u8d5b\uff08Ren\xe9 Grousset\uff09\uff08\u6ce8\uff1a\uff32\uff45\uff4e\xe9\uff27\uff52\uff4f\uff55\uff53\uff53\uff45\uff54\uff09\u5728\u300a\u4e2d\u534e\u5e1d\u56fd\u7684\u5174\u8d77\u4e0e\u8f89\u714c\u300b\u4e00\u4e66\u4e2d\u6709\u201c\u4e00\u516d\u56db\u56db\u5e74\u7684\u5927\u53d8\u201d\u4e00\u7ae0\uff0c\u5176\u4e2d\u8bf4\uff1a\u201c\u7687\u592a\u6781\u662f\u86ee\u4eba\u4e2d\u7684\u4e00\u4e2a\u5929\u624d\uff0c\u4ed6\u628a\u672c\u65cf\u4eba\u6c11\u7684\u519b\u4e8b\u624d\u80fd\uff0c\u548c\u5bf9\u6587\u660e\u751f\u6d3b\u7684\u5929\u751f\u7406\u89e3\u76f8\u7ed3\u5408\u8d77\u6765\u3002\u201d \u3000\u3000\u2461\u6e05\u300a\u592a\u5b97\u5b9e\u5f55\u5377\u4e09\u300b\uff1a\u5929\u806a\u5143\u5e74\uff0c\u201c\u65f6\u56fd\u4e2d\u5927\u9965\uff0c\u6597\u7c73\u4ef7\u94f6\u516b\u4e24\uff0c\u4eba\u6709\u76f8\u98df\u8005\u3002\u56fd\u4e2d\u94f6\u4e24\u867d\u591a\uff0c\u65e0\u5916\u8d38\u6613\uff0c\u662f\u4ee5\u94f6\u8d31\u800c\u8bf8\u7269\u817e\u8d35\u3002\u826f\u9a6c\uff0c\u94f6\u4e09\u767e\u4e24\u3002\u725b\u4e00\uff0c\u94f6\u767e\u4e24\u3002\u87d2\u7f0e\u4e00\uff0c\u94f6\u767e\u4e94\u5341\u4e24\u3002\u5e03\u5339\u4e00\uff0c\u94f6\u4e5d\u4e24\u3002\u76d7\u8d3c\u7e41\u5174\uff0c\u5077\u7a83\u725b\u9a6c\uff0c\u6216\u884c\u52ab\u6740\u3002\u4e8e\u662f\u8bf8\u81e3\u5165\u594f\u66f0\uff1a\u76d7\u8d3c\u82e5\u4e0d\u6309\u5f8b\u4e25\u60e9\uff0c\u6050\u4e0d\u80fd\u6b62\u606f\u3002\u4e0a\u607b\u7136\uff0c\u8c15\u66f0\uff1a\u4eca\u5c81\u56fd\u4e2d\u56e0\u5e74\u9965\u4e4f\u98df\uff0c\u81f4\u6c11\u4e0d\u5f97\u5df2\u800c\u4e3a\u76d7\u8033\u3002\u7f09\u83b7\u8005\uff0c\u97ad\u800c\u91ca\u4e4b\u53ef\u4e5f\u3002\u9042\u4e0b\u4ee4\uff0c\u662f\u5c81\u8c33\u72f1\uff0c\u59d1\u4ece\u5bbd\u5178\u3002\u4ecd\u5927\u53d1\u5e11\u91d1\uff0c\u6563\u8d48\u9965\u6c11\u3002\u201d\u4ed6\u5bbd\u5f85\u56e0\u9965\u997f\u800c\u4e3a\u76d7\u7684\u767e\u59d3\uff0c\u4e0e\u5d07\u796f\u7763\u4fc3\u90e8\u5c06\u201c\u9650\u671f\u7834\u8d3c\u3001\u6740\u8d3c\u7acb\u529f\u201d\u7684\u653f\u7b56\u6070\u6b63\u76f8\u53cd\u3002 \u3000\u3000\u2462\u4f55\u67c4\u68e3\uff1aThe Ladder of Success in Imperial China,Aspects of Social Mobility,1368-1911\uff08\u6ce8\uff1a\uff34\uff48\uff45\uff2c\uff41\uff44\uff44\uff45\uff52\uff4f\uff46\uff33\uff55\uff43\uff43\uff45\uff53\uff53\uff49\uff4e\uff29\uff4d\uff50\uff45\uff52\uff49\uff41\uff4c\uff23\uff48\uff49\uff4e\uff41\uff0c\uff21\uff53\uff50\uff45\uff43\uff54\uff53\uff4f\uff46\uff33\uff4f\uff43\uff49\uff41\uff4c\uff2d\uff4f\uff42\uff49\uff4c\uff49\uff54\uff59\uff0c\uff11\uff13\uff16\uff18\u2014\uff11\uff19\uff11\uff11\uff09\u4e00\u4e66\u4e2d\uff0c\u8ba4\u4e3a\u660e\u521d\u4eba\u53e3\u516d\u5343\u4e94\u767e\u4e07\uff0c\u5230\u660e\u672b\u65f6\u5df2\u6da8\u4e86\u4e00\u500d\u4ee5\u4e0a\u3002 \u3000\u3000\u2463\u738b\u949f\u7ff0\uff1a\u300a\u6ee1\u65cf\u5728\u52aa\u5c14\u54c8\u8d64\u65f6\u4ee3\u7684\u793e\u4f1a\u7ecf\u6d4e\u5f62\u6001\u300b\u4e00\u6587\u4e2d\uff0c\u6839\u636e\u671d\u9c9c\u300a\u5174\u4eac\u4e8c\u9053\u6cb3\u5b50\u65e7\u8001\u57ce\u300b\u7684\u8d44\u6599\uff0c\u8ba4\u4e3a\u4e00\u516d\u4e8c\u4e00\u5e74\u65f6\uff0c\u52aa\u5c14\u54c8\u8d64\u7684\u5175\u6570\u4e8c\u5341\u4e07\uff0c\u518d\u52a0\u4e0a\u5987\u5973\u8001\u5c11\uff0c\u201c\u5168\u4eba\u6570\u5f53\u5728\u56db\u3001\u4e94\u5341\u4e07\u5de6\u53f3\u3002\u201d \u3000\u3000\u2464\u300a\u5929\u806a\u5b9e\u5f55\u7a3f\u300b\u5143\u5e74\u4e09\u6708\u521d\u4e8c\u65e5\uff0c\u201c\u79c0\u624d\u5cb3\u8d77\u9e3e\u66f0\uff1a\u6211\u56fd\u5b9c\u4e0e\u660e\u671d\u8bb2\u548c\u3002\u82e5\u4e0d\u8bb2\u548c\uff0c\u5219\u6211\u56fd\u4eba\u6c11\u6b7b\u6563\u6b86\u5c3d\u3002\u201d\u300a\u660e\u6e05\u53f2\u6599\u300b\u7532\u7f16\uff0c\u5929\u806a\u4e8c\u5e74\u516b\u6708\u201c\u4e8b\u5c40\u672a\u5b9a\u201d\u594f\u758f\uff1a\u201c\u5357\u671d\u867d\u5e08\u8001\u8d22\u532e\uff0c\u7136\u4ee5\u5929\u4e0b\u4e4b\u5168\u529b\uff0c\u6bd5\u6ce8\u4e8e\u4e00\u9685\u4e4b\u95f4\uff0c\u76d6\u72b9\u88d5\u5982\u4e5f\u3002\u201d\u300a\u4e1c\u534e\u5f55\u300b\u8f7d\u5929\u806a\u4e09\u5e74\u516b\u6708\u620a\u8fb0\uff0c\u201c\u5927\u81e3\u540c\u8c0b\u5021\u9003\u201d\u3002\u300a\u660e\u6e05\u53f2\u6599\u300b\u4e59\u7f16\u8f7d\uff0c\u5d07\u796f\u4e8c\u5e74\u4e8c\u6708\u4e8c\u5341\u4e00\uff0c\u8881\u5d07\u7115\u5858\u62a5\uff1a\u201c\u4e00\u65e5\u4e4b\u5185\uff0c\u964d\u8005\u7adf\u524d\u540e\u63a5\u8e35\u800c\u81f3\u3002\u201d \u3000\u3000\u2465\u201c\u4e03\u5927\u6068\u201d\uff1a\u4e00\u3001\u660e\u671d\u6740\u5bb3\u91d1\u4eba\u7684\u4e8c\u7956\uff1b\u4e8c\u3001\u8892\u62a4\u91d1\u4eba\u7684\u4ec7\u654c\u54c8\u8fbe\uff1b\u4e09\u3001\u8d8a\u754c\u51fa\u5175\uff0c\u52a9\u91d1\u4eba\u7684\u4e16\u4ec7\u53f6\u8d6b\u6297\u91d1\uff1b\u56db\u3001\u660e\u4eba\u8d8a\u754c\uff0c\u91d1\u4eba\u6839\u636e\u8a93\u7ea6\u6740\u4e86\uff0c\u660e\u671d\u52d2\u7d22\u91d1\u65b9\u4ea4\u51fa\u5341\u4eba\u6765\u6740\u6b7b\uff0c\u4ee5\u8d44\u62a5\u590d\uff1b\u4e94\u3001\u660e\u671d\u9020\u6210\u8001\u5973\u6539\u5ac1\uff1b\u516d\u3001\u79fb\u7f6e\u754c\u7891\uff0c\u62a2\u593a\u91d1\u56fd\u7684\u4eba\u53c2\u3001\u8c82\u76ae\uff1b\u4e03\u3001\u542c\u4fe1\u53f6\u8d6b\uff0c\u5199\u4fe1\u6765\u8fb1\u9a82\u4fae\u6162\u3002 \u3000\u3000\u2466\u201c\u89c2\u5176\u5411\u80cc\u79bb\u5408\u4e4b\u610f\uff0c\u4ee5\u5b9a\u5f81\u8ba8\u629a\u5b9a\u4e4b\u8ba1\u3002\u201d\u89c1\u300a\u4e24\u671d\u4ece\u4fe1\u5f55\u300b\u3002 \u3000\u3000\u2467\u5f53\u65f6\u6ee1\u6e05\u7684\u6b63\u5f0f\u56fd\u53f7\u662f\u201c\u91d1\u201d\uff0c\u53f2\u4e66\u4e0a\u79f0\u4e3a\u201c\u540e\u91d1\u201d\uff0c\u4ee5\u4e0e\u5b8b\u671d\u65f6\u7684\u201c\u91d1\u201d\u6709\u6240\u5206\u522b\u3002\u5230\u5929\u806a\u5341\u5e74\uff08\u660e\u5d07\u796f\u4e5d\u5e74\uff09\u624d\u6539\u4e3a\u201c\u5927\u6e05\u201d\u3002\u6240\u4ee5\u672c\u6587\u4e2d\u7684\u6ee1\u6e05\uff0c\u5176\u5b9e\u90fd\u5e94\u79f0\u201c\u91d1\u201d\u3002\u201c\u6ee1\u6d32\u201d\u7684\u540d\u79f0\uff0c\u4e5f\u8981\u5230\u6539\u4e86\u201c\u5927\u6e05\u201d\u7684\u56fd\u53f7\u4e4b\u540e\u624d\u51fa\u73b0\uff0c\u4ee5\u524d\u79f0\u201c\u5efa\u5dde\u201d\u6216\u201c\u5973\u771f\u201d\u3002\u591a\u6570\u5b66\u8005\u8ba4\u4e3a\uff0c\u201c\u6ee1\u6d32\u201d\u662f\u6587\u6b8a\u83e9\u8428\u7684\u201c\u6587\u6b8a\u3001\u66fc\u6b8a\u201d\u7684\u97f3\u8f6c\u3002\u4e3a\u4e86\u4fbf\u4e8e\u8bfb\u8005\uff0c\u672c\u6587\u4e2d\u4e0d\u5c06\u201c\u91d1\u3001\u6e05\u201d\u201c\u5efa\u6d32\u3001\u6ee1\u6d32\u201d\u7b49\u79f0\u547c\u6839\u636e\u5386\u53f2\u5e74\u4ee3\u800c\u4f5c\u5206\u522b\u3002 \u3000\u3000\u2468\u300a\u592a\u5b97\u5b9e\u5f55\u7a3f\u300b\uff1a\u5929\u806a\u4e03\u5e74\u5341\u6708\uff0c\u7687\u592a\u6781\u8d23\u9a82\u4e3b\u5f20\u51fa\u5175\u5357\u653b\u4e4b\u4eba\uff1a\u201c\u5929\u4e88\u6211\u6709\u6570\u4e4b\u5175\uff0c\u82e5\u7a0d\u4e8f\u635f\uff0c\u4f55\u4ee5\u524d\u56fe\uff1f\u201d \u3000\u3000\u247d\u5b8b\u9ad8\u5b97\u7ecd\u5174\u5341\u4e00\u5e74\u5341\u4e8c\u6708\u6740\u5cb3\u98de\u3002\u5341\u4e8c\u5e74\u6b63\u6708\uff0c\u5b8b\u91d1\u548c\u8bae\u8fbe\u6210\uff0c\u9ad8\u5b97\u8d75\u6784\u5411\u91d1\u56fd\u4e0a\u8868\u79f0\u81e3\uff0c\u8868\u4e2d\u8bf4\uff1a\u201c\u81e3\u6784\u8a00\uff1a\u65e2\u8499\u6069\u9020\uff0c\u8bb8\u5907\u85e9\u65b9\uff0c\u4e16\u4e16\u5b50\u5b59\uff0c\u8c28\u5b88\u81e3\u8282\u3002\u6bcf\u5e74\u7687\u5e1d\u751f\u65e5\u5e76\u6b63\u65e6\uff0c\u9063\u4f7f\u79f0\u8d3a\u4e0d\u7edd\u3002\u5c81\u8d21\u94f6\u4e8c\u5341\u4e94\u4e07\u4e24\uff0c\u7ee2\u4e8c\u5341\u4e94\u4e07\u5339\u3002\u201d \u3000\u3000\u247e\u300a\u592a\u5b97\u5b9e\u5f55\u300b\u5377\u5341\u4e8c\uff0c\u5929\u806a\u516d\u5e74\u516d\u6708\uff0c\u7687\u592a\u6781\u81f4\u4e66\u5927\u540c\u5b88\u5c06\u6c42\u548c\uff0c\u4fe1\u4e2d\u8bf4\uff1a\u201c\u548c\u4e8b\u65e2\u6210\uff0c\u81ea\u5f53\u900a\u5c14\u5927\u56fd\uff0c\u5c14\u7b49\u4ea6\u89c6\u6211\u5c45\u5bdf\u54c8\u5c14\u4e4b\u4e0a\u53ef\u4e5f\u3002\u201d \u3000\u3000\u247f\u7687\u592a\u6781\u6765\u4fe1\u7684\u5f00\u5934\u662f\uff08\u6839\u636e\u539f\u4fe1\uff09\uff1a\u201c\u6c57\u81f4\u4e66\u8881\u8001\u5148\u751f\u5927\u4eba\u201d\u3002\uff08\u540e\u6765\u4e7e\u9686\u65f6\u4fee\u8ba2\u300a\u592a\u5b97\u5b9e\u5f55\u300b\u89c9\u5f97\u8bed\u6c14\u592a\u5351\uff0c\u624d\u6539\u4e3a\u300a\u7687\u5e1d\u81f4\u4e66\u8881\u5de1\u629a\u300b\uff0c\u4f46\u5f53\u65f6\u7687\u592a\u6781\u672a\u79f0\u5e1d\uff0c\u51b3\u4e0d\u53ef\u80fd\u6709\u201c\u7687\u5e1d\u201d\u7684\u79f0\u547c\u3002\uff09\u8881\u5d07\u7115\u4e66\u4fe1\u7684\u5f00\u5934\u662f\uff1a\u201c\u8fbd\u4e1c\u63d0\u7763\u90e8\u9662\uff0c\u81f4\u4e66\u4e8e\u6c57\u5e10\u4e0b\uff1a\u518d\u8fb1\u4e66\u6559\uff0c\u77e5\u6c57\u6e10\u6b32\u606d\u987a\u5929\u671d\uff0c\u606f\u5175\u6208\u4ee5\u4f11\u517b\u90e8\u843d\uff0c\u5373\u6b64\u4e00\u5ff5\u597d\u751f\uff0c\u5929\u81ea\u9274\u4e4b\uff0c\u5c06\u6765\u6240\u4ee5\u4f51\u6c57\u800c\u660c\u5927\u4e4b\u8005\uff0c\u5c1a\u65e0\u91cf\u4e5f\u3002\u201d \u3000\u3000\u2480\u540e\u6765\u7687\u592a\u6781\u5728\u5199\u7ed9\u7956\u5927\u5bff\u7684\u4fe1\u4e2d\uff08\u90a3\u65f6\u8881\u5d07\u7115\u5df2\u6b7b\uff09\uff0c\u66fe\u8bf4\uff1a\u201c\u5c14\u56fd\u541b\u81e3\uff0c\u60df\u4ee5\u5b8b\u671d\u6545\u4e8b\u4e3a\u9274\uff0c\u4ea6\u65e0\u4e00\u8a00\u590d\u6211\u3002\u7136\u5c14\u660e\u4e3b\u975e\u5b8b\u4e4b\u82d7\u88d4\uff0c\u6715\u4ea6\u975e\u91d1\u4e4b\u5b50\u5b59\u3002\u5f7c\u4e00\u65f6\uff0c\u6b64\u4e00\u65f6\uff0c\u5929\u65f6\u4eba\u5fc3\uff0c\u5404\u6709\u4e0d\u540c\u3002\u5c14\u5927\u56fd\u5c82\u65e0\u667a\u6167\u4e4b\u65f6\u6d41\uff0c\u4f55\u4e0d\u80fd\u56e0\u65f6\u5236\u5b9c\u4e4e\uff1f\u201d\u5176\u5b9e\u52aa\u5c14\u54c8\u8d64\u3001\u7687\u592a\u6781\u7b49\u4e00\u76f4\u81ea\u8ba4\u662f\u91d1\u7684\u5b50\u5b59\uff0c\u4ed6\u4e3a\u4e86\u6c42\u548c\uff0c\u8fde\u7956\u5b97\u4e5f\u4e0d\u8ba4\u4e86\u3002 \u3000\u3000\u2481\u4ed6\u540e\u6765\u5728\u5199\u7ed9\u5d07\u796f\u7684\u594f\u7ae0\u4e2d\u8bf4\uff1a\u201c\u8bf8\u6709\u5229\u4e8e\u5c01\u7586\u8005\uff0c\u7686\u4e0d\u5229\u4e8e\u6b64\u8eab\u8005\u4e5f\u3002\u201d\u6240\u4ee5\u4ed6\u7684\u77e5\u5df1\u7a0b\u672c\u76f4\u8bf4\uff1a\u201c\u4e3e\u4e16\u7686\u5de7\u4eba\uff0c\u800c\u8881\u516c\u4e00\u5927\u75f4\u6c49\u4e5f\u3002\u552f\u5176\u75f4\uff0c\u6545\u4e3e\u4e16\u6700\u7231\u8005\u94b1\uff0c\u8881\u516c\u4e0d\u77e5\u7231\u4e5f\u3002\u552f\u5176\u75f4\uff0c\u6545\u4e3e\u4e16\u6700\u60dc\u8005\u6b7b\uff0c\u8881\u516c\u4e0d\u77e5\u6015\u4e5f\u3002\u4e8e\u662f\u4e4e\u4e3e\u4e16\u6240\u4e0d\u6562\u4efb\u4e4b\u52b3\u6028\uff0c\u8881\u516c\u76f4\u4efb\u4e4b\u800c\u5f17\u8f9e\u4e5f\u3002\u4e8e\u662f\u4e4e\u4e3e\u4e16\u6240\u4e0d\u5f97\u4e0d\u907f\u4e4b\u5acc\u7591\uff0c\u8881\u516c\u76f4\u4e0d\u907f\u4e4b\u800c\u72ec\u884c\u4e5f\u3002\u201d\u6240\u8c13\u201c\u4e3e\u4e16\u6240\u4e0d\u5f97\u4e0d\u907f\u4e4b\u5acc\u7591\u201d\uff0c\u5c31\u662f\u4e0e\u91d1\u4eba\u8bae\u548c\u3002 \u3000\u3000\u2482\u300a\u5b5f\u5b50\xb7\u516c\u5b59\u4e11\u300b\uff1a\u201c\u6614\u8005\u66fe\u5b50\u8c13\u5b50\u8944\u66f0\uff1a\u2018\u2026\u2026\u81ea\u53cd\u800c\u7f29\uff0c\u867d\u5343\u4e07\u4eba\uff0c\u543e\u5f80\u77e3\u3002\u2019\u201d \u3000\u3000\u2483\u8881\u5d07\u7115\u300a\u8fb9\u4e2d\u9001\u522b\u300b\uff1a\u201c\u4e94\u8f7d\u79bb\u5bb6\u522b\u8def\u60a0\uff0c\u9001\u541b\u5bd2\u6d78\u5b9d\u5200\u5934\u3002\u6b32\u77e5\u80ba\u8151\u540c\u751f\u6b7b\uff0c\u4f55\u7528\u5b89\u5371\u95ee\u53bb\u7559\uff1f\u7b56\u6756\u53ea\u56e0\u56fe\u96ea\u803b\uff0c\u6a2a\u6208\u539f\u4e0d\u4e3a\u5c01\u4faf\u3002\u6545\u56ed\u4eb2\u4fa3\u5982\u76f8\u95ee\uff0c\u6127\u6211\u8fb9\u5c18\u5c1a\u672a\u6536\u3002\u201d \u3000\u3000\u2484\u8881\u5d07\u7115\u300a\u5c71\u6d77\u5173\u9001\u5b63\u5f1f\u5357\u8fd8\u300b\uff1a\u201c\u516c\u8f66\u72b9\u8bb0\u6614\u5e74\u60c5\uff0c\u4e07\u91cc\u4ece\u6211\u585e\u4e0a\u5f81\u3002\u7267\u5709\u6b64\u65f6\u72b9\u634d\u5fa1\uff0c\u9a70\u9a71\u4f55\u65e5\u6170\u5347\u5e73\uff1f\u7531\u6765\u53cb\u7231\u949f\u543e\u8f88\uff0c\u80af\u628a\u987b\u7709\u8d1f\u6b64\u751f\uff1f\u53bb\u4f4f\u5b89\u5371\u4ff1\u83ab\u95ee\uff0c\u71d5\u7136\u66fe\u52d2\u53e4\u4eba\u540d\u3002\u201d\u201c\u5f1f\u5144\u4e8e\u6c5d\u500d\u5173\u60c5\uff0c\u6b64\u65e5\u4e34\u6b67\u611f\u6168\u751f\u3002\u78ca\u843d\u4e08\u592b\u8c01\u597d\u5251\uff1f\u7262\u9a9a\u7537\u5b50\u5c14\u80fd\u5175\u3002\u624d\u582a\u9010\u7535\u4e09\u9a71\u6377\uff0c\u8eab\u4e0a\u98de\u9e4f\u4e00\u7fbd\u8f7b\u3002\u884c\u77e3\u4e61\u90a6\u91cd\u52aa\u529b\uff0c\u83ab\u803d\u758f\u61d2\u5815\u65f6\u540d\u3002\u201d\u5176\u4e2d\u201c\u78ca\u843d\u4e08\u592b\u8c01\u597d\u5251\uff1f\u7262\u9a9a\u7537\u5b50\u5c14\u80fd\u5175\u201d\u4e24\u53e5\uff0c\u5199\u51fa\u4e86\u4ed6\u4e24\u5144\u5f1f\u8c6a\u8fc8\u7684\u6027\u683c\uff0c\u5c31\u8bd7\u800c\u8bba\uff0c\u4e5f\u662f\u8c6a\u8fc8\u7684\u597d\u8bd7\u3002 \u3000\u3000\u516b \u3000\u3000\u5728\u8fd9\u6bb5\u65f6\u671f\u4e2d\uff0c\u7687\u592a\u6781\u8fdb\u653b\u671d\u9c9c\uff0c\u6253\u4e86\u51e0\u4e2a\u80dc\u4ed7\u540e\uff0c\u671d\u9c9c\u6295\u964d\uff0c\u8ba2\u7acb\u4e86\u5bf9\u6ee1\u6e05\u5341\u5206\u6709\u5229\u7684\u548c\u7ea6\uff0c\u6bcf\u5e74\u4ece\u671d\u9c9c\u5f97\u5230\u7cae\u98df\u3001\u91d1\u94b1\u548c\u7269\u54c1\u7684\u4f9b\u5e94\u3002\u7687\u592a\u6781\u672c\u6765\u63d0\u51fa\u4e09\u4e2a\u6761\u4ef6\uff1a\u5272\u5730\u3001\u64d2\u6bdb\u6587\u9f99\u3001\u6d3e\u5175\u4e00\u4e07\u52a9\u653b\u4e2d\u56fd\u3002\u671d\u9c9c\u5bf9\u8fd9\u4e09\u4e2a\u6761\u4ef6\u65e0\u6cd5\u63a5\u7eb3\uff0c\u4f46\u5728\u7ecf\u6d4e\u4e0a\u5c3d\u91cf\u6ee1\u8db3\u6ee1\u6e05\u7684\u8981\u6c42\u3002\u540c\u65f6\u5728\u6b64\u540e\u7684\u660e\u6e05\u6218\u4e89\u4e2d\uff0c\u671d\u9c9c\u6539\u5b88\u4e2d\u7acb\uff0c\u4f7f\u6ee1\u6e05\u53bb\u4e86\u540e\u987e\u4e4b\u5fe7\u3002 \u3000\u3000\u5728\u7687\u592a\u6781\u5bf9\u671d\u9c9c\u7528\u5175\u4e4b\u65f6\uff0c\u8881\u5d07\u7115\u52a0\u7d27\u4fee\u7b51\u9526\u5dde\u3001\u4e2d\u5de6\u3001\u5927\u51cc\u6cb3\u4e09\u57ce\u7684\u9632\u5fa1\u5de5\u4e8b\uff0c\u6d3e\u6c34\u5e08\u53bb\u652f\u63f4\u76ae\u5c9b\u7684\u6bdb\u6587\u9f99\uff0c\u53e6\u6d3e\u8d75\u7387\u6559\u3001\u6731\u6885\u7b49\u4e5d\u5458\u5c06\u9886\u7387\u5175\u4e5d\u5343\uff0c\u8fdb\u5175\u4e09\u5c94\u6cb3\uff0c\u7275\u5236\u6e05\u519b\uff0c\u4f5c\u671d\u9c9c\u7684\u58f0\u63f4\u3002\u4f46\u671d\u9c9c\u4e0d\u4e45\u5c31\u548c\u6ee1\u6e05\u8ba2\u4e86\u57ce\u4e0b\u4e4b\u76df\uff0c\u8d75\u7387\u6559\u7b49\u9886\u5175\u800c\u56de\uff0c\u5e76\u672a\u548c\u6e05\u519b\u63a5\u89e6\u3002 \u3000\u3000\u7687\u592a\u6781\u65e0\u6cd5\u548c\u660e\u671d\u8fbe\u6210\u548c\u8bae\uff0c\u5374\u89c1\u8881\u5d07\u7115\u4fee\u7b51\u57ce\u5821\u7684\u5de5\u4f5c\u8fdb\u884c\u5f97\u5341\u5206\u79ef\u6781\uff0c\u65f6\u95f4\u8d8a\u4e45\uff0c\u4eca\u540e\u8fdb\u653b\u4f1a\u66f4\u52a0\u56f0\u96be\uff0c\u4e8e\u662f\u51b3\u5b9a\u201c\u4ee5\u6218\u6c42\u548c\u201d\uff0c\u5bf9\u5b81\u8fdc\u53d1\u52a8\u653b\u51fb\u3002 \u3000\u3000\u5929\u542f\u4e03\u5e74\u4e94\u6708\uff0c\u7687\u592a\u6781\u4eb2\u7387\u4e24\u9ec4\u65d7\u3001\u4e24\u767d\u65d7\u7cbe\u5175\uff0c\u8fdb\u653b\u8fbd\u897f\u8bf8\u57ce\u5821\uff0c\u653b\u9677\u660e\u65b9\u5927\u51cc\u6cb3\u3001\u5c0f\u51cc\u6cb3\u4e24\u4e2a\u8981\u585e\uff0c\u968f\u5373\u8fdb\u653b\u5b81\u8fdc\u7684\u5916\u56f4\u8981\u585e\u9526\u5dde\u3002 \u3000\u3000\u4e94\u6708\u5341\u4e00\uff0c\u7687\u592a\u6781\u6240\u7387\u5927\u519b\u653b\u62b5\u9526\u5dde\uff0c\u56db\u9762\u5408\u56f4\u3002\u8fd9\u65f6\u5b88\u9526\u5dde\u7684\u662f\u8d75\u7387\u6559\uff0c\u4ed6\u548c\u76d1\u519b\u592a\u76d1\u7eaa\u7528\u5b88\u57ce\uff0c\u6d3e\u4eba\u53bb\u4e0e\u7687\u592a\u6781\u8bae\u548c\uff0c\u90a3\u81ea\u662f\u7f13\u5175\u4e4b\u8ba1\uff0c\u4ee5\u5f85\u6551\u5175\u3002\u7687\u592a\u6781\u4e0d\u4e2d\u8ba1\uff0c\u653b\u57ce\u6108\u6025\u3002 \u3000\u3000\u8881\u5d07\u7115\u6d3e\u9063\u7956\u5927\u5bff\u548c\u5c24\u4e16\u7984\u5e26\u4e86\u56db\u5343\u7cbe\u5175\uff0c\u7ed5\u5230\u6e05\u519b\u540e\u8def\u53bb\u5305\u6284\uff0c\u53c8\u6d3e\u6c34\u5e08\u53bb\u653b\u4e1c\u8def\u4f5c\u4e3a\u7275\u5236\u3002\u8fd9\u65f6\u5929\u70ed\uff0c\u6d77\u4e0a\u4e0d\u7ed3\u51b0\uff0c\u6c34\u5e08\u7528\u5f97\u7740\u4e86\u3002 \u3000\u3000\u8d75\u7387\u6559\u662f\u9655\u897f\u4eba\uff0c\u8fd9\u4eba\u7684\u4eba\u54c1\u672c\u6765\u662f\u76f8\u5f53\u4e0d\u9ad8\u7684\u3002\u52aa\u5c14\u54c8\u8d64\u653b\u8fbd\u9633\u65f6\uff0c\u8d75\u7387\u6559\u662f\u4e3b\u5e05\u8881\u5e94\u6cf0\u7684\u4e2d\u519b\uff08\u53c2\u8c0b\u957f\uff09\u3002\u8881\u5e94\u6cf0\u662f\u4e0d\u61c2\u519b\u4e8b\u7684\u6587\u5b98\uff0c\u8d75\u7387\u6559\u5374\u6ca1\u6709\u5c3d\u4ed6\u505a\u53c2\u8c0b\u957f\u7684\u8d23\u4efb\uff0c\u8fd9\u4e2a\u6218\u5f79\u6307\u6325\u5f97\u4e00\u584c\u80e1\u6d82\u3002\u6e05\u519b\u653b\u7834\u8fbd\u9633\uff0c\u8881\u5e94\u6cf0\u6b89\u96be\uff0c\u8d75\u7387\u6559\u5374\u5077\u5077\u9003\u8d70\u4e86\uff0c\u8bba\u6cd5\u5f53\u65a9\uff0c\u4e0d\u77e5\u5982\u4f55\u5f97\u4ee5\u5e78\u514d\uff0c\u60f3\u6765\u662f\u8d3f\u8d42\u4e86\u4e0a\u5b98\u3002\u540e\u6765\u738b\u5316\u8d1e\u5927\u8d25\uff0c\u5173\u5916\u5404\u57ce\u90fd\u6210\u4e3a\u65e0\u4eba\u7ba1\u7684\u5730\u65b9\uff0c\u8d75\u7387\u6559\u7533\u8bf7\u6234\u7f6a\u7acb\u529f\uff0c\u5e26\u9886\u4e86\u5bb6\u4e01\u524d\u53bb\u63a5\u6536\u524d\u5c6f\u536b\uff0c\u4f46\u5230\u8fbe\u65f6\u53d1\u89c9\u5df2\u88ab\u8499\u53e4\u4eba\u5360\u4f4f\uff0c\u4ed6\u4fbf\u4e0d\u6562\u518d\u8fdb\u3002\u52aa\u5c14\u54c8\u8d64\u653b\u5b81\u8fdc\uff0c\u8d75\u7387\u6559\u5728\u524d\u5c6f\u536b\uff0c\u8ddd\u79bb\u5f88\u8fd1\uff0c\u81ea\u5df1\u4e0d\u4eb2\u53bb\u8d74\u63f4\uff0c\u540e\u6765\u5b81\u8fdc\u5927\u6377\uff0c\u4ed6\u5374\u60f3\u5206\u529f\uff0c\u4ee5\u81f4\u7ed9\u6ee1\u6842\u75db\u9a82\uff0c\u917f\u6210\u4e86\u5f88\u5927\u98ce\u6ce2\u3002 \u3000\u3000\u548c\u6ee1\u6842\u51b2\u7a81\u65f6\uff0c\u8881\u5d07\u7115\u76f8\u5f53\u652f\u6301\u4ed6\u3002\u8d75\u7387\u6559\u611f\u6069\u56fe\u62a5\uff0c\u53c8\u5f97\u8881\u5d07\u7115\u65f6\u65f6\u52c9\u4ee5\u5fe0\u4e49\uff0c\u5230\u9526\u5dde\u5927\u6218\u65f6\uff0c\u4ed6\u7a81\u7136\u4e4b\u95f4\u4f3c\u4e4e\u53d8\u4e86\u4e00\u4e2a\u4eba\u3002\u4ed6\u548c\u524d\u950b\u603b\u5175\u5de6\u8f85\u3001\u526f\u603b\u5175\u6731\u6885\u7b49\u7387\u5175\u594b\u52c7\u6b7b\u6218\uff0c\u548c\u7687\u592a\u6781\u90e8\u4e0b\u7684\u7cbe\u5175\u5927\u6218\u4e09\u573a\uff0c\u80dc\u4e86\u4e09\u573a\uff0c\u5c0f\u6218\u4e8c\u5341\u4e94\u573a\uff0c\u4e5f\u662f\u6bcf\u6218\u90fd\u80dc\u3002\u4ece\u4e94\u6708\u5341\u4e00\u6253\u5230\u516d\u6708\u521d\u56db\uff0c\u4e8c\u5341\u56db\u5929\u4e4b\u4e2d\uff0c\u65e0\u65e5\u4e0d\u6218\uff0c\u6218\u51b5\u7684\u6fc0\u70c8\uff0c\u4e0d\u4e0b\u4e8e\u5f53\u5e74\u5b81\u8fdc\u5927\u6218\u3002\u516d\u6708\u521d\u56db\u90a3\u5929\uff0c\u7687\u592a\u6781\u589e\u5175\u731b\u653b\u3002\u9526\u5dde\u57ce\u4e2d\u653e\u897f\u6d0b\u5927\u70ae\uff0c\u53c8\u653e\u706b\u70ae\u3001\u706b\u5f39\u548c\u77e2\u77f3\uff0c\u6e05\u5175\u53d7\u521b\u6781\u91cd\u3002\u653b\u5230\u5929\u660e\u65f6\uff0c\u7687\u592a\u6781\u89c1\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u53ea\u5f97\u9000\u5175\uff0c\u9000\u5230\u5c0f\u51cc\u6cb3\u624e\u8425\uff0c\u7b49\u5019\u5404\u8def\u5175\u9a6c\u96c6\u4e2d\u6574\u7f16\u3002 \u3000\u3000\u8d75\u7387\u6559\u8f6c\u602f\u4e3a\u52c7\uff0c\u81ea\u89c1\u654c\u6f5c\u9003\u5230\u62da\u6b7b\u5b88\u57ce\uff0c\u81ea\u754f\u7f29\u4e0d\u524d\u5230\u6fc0\u6218\u4e8c\u5341\u56db\u65e5\uff0c\u5230\u540e\u6765\u66f4\u5728\u4fdd\u536b\u5317\u4eac\u4e4b\u5f79\u4e2d\u8840\u6218\u9635\u4ea1\uff0c\u7ec8\u4e8e\u5728\u5386\u53f2\u4e0a\u4e0e\u6ee1\u6842\u9f50\u540d\uff0c\u6210\u4e3a\u5f53\u65f6\u7684\u4e24\u5927\u826f\u5c06\u3002\u4ed6\u8fd9\u4e2a\u91cd\u5927\u8f6c\u53d8\uff0c\u975e\u5e38\u7a81\u51fa\u7684\u8bc1\u660e\u4e86\u8881\u5d07\u7115\u7684\u9886\u5bfc\u624d\u80fd\u3002 \u3000\u3000\u7687\u592a\u6781\u6574\u7406\u597d\u4e86\u90e8\u961f\uff0c\u8f6c\u800c\u53bb\u653b\u5b81\u8fdc\u3002 \u3000\u3000\u6e05\u519b\u4e0a\u6b21\u5728\u5b81\u8fdc\u5403\u8fc7\u8d25\u4ed7\uff0c\u5175\u5c06\u5fc3\u4e2d\u5bf9\u8881\u5d07\u7115\u90fd\u662f\u5f88\u5fcc\u60ee\u7684\u3002\u5927\u8d1d\u52d2\u4ee3\u5584\u89c1\u57ce\u4e2d\u6709\u5907\uff0c\u5c31\u52d2\u5175\u4e0d\u653b\u3002\u7687\u592a\u6781\u5bf9\u8bf8\u5c06\u8bf4\uff1a\u201c\u5148\u6c57\u653b\u5b81\u8fdc\u4e0d\u514b\uff0c\u8fd9\u6b21\u6211\u653b\u9526\u5dde\u53c8\u4e0d\u514b\uff0c\u82e5\u518d\u653b\u4e0d\u4e0b\u5b81\u8fdc\uff0c\u6211\u53ef\u8981\u58f0\u540d\u626b\u5730\u4e86\u3002\u201d\u4e8e\u662f\u4e0b\u4ee4\u603b\u653b\uff0c\u51fb\u7834\u57ce\u4e0b\u660e\u519b\u9a91\u5175\uff0c\u76f4\u8584\u57ce\u58c1\u3002 \u3000\u3000\u6bd4\u4e4b\u7b2c\u4e00\u6b21\u5b81\u8fdc\u4e4b\u6218\uff0c\u8881\u5d07\u7115\u90e8\u7684\u6218\u6597\u529b\u5df2\u6709\u589e\u5f3a\uff0c\u6562\u4e8e\u5230\u57ce\u5916\u51b3\u6218\u4e86\u3002\u4e0a\u6b21\u8981\u6e05\u519b\u9000\u540e\uff0c\u624d\u6d3e\u4e94\u5341\u540d\u6562\u6b7b\u961f\u7f12\u5230\u57ce\u4e0b\u62fe\u7bad\u679d\uff0c\u53ef\u89c1\u4e0d\u6562\u5f00\u57ce\u95e8\u3002 \u3000\u3000\u6ee1\u6842\u7387\u9886\u660e\u519b\u5728\u57ce\u5357\u4e8c\u91cc\u5217\u9635\uff0c\u57ce\u5899\u4e0b\u73af\u5217\u67aa\u70ae\u3002\u7687\u592a\u6781\u4f6f\u8d25\uff0c\u60f3\u5f15\u660e\u519b\u6765\u653b\uff0c\u7136\u540e\u4f0f\u5175\u9f50\u8d77\u3002\u4f46\u660e\u519b\u6ca1\u6709\u4e0a\u5f53\uff0c\u5b88\u5792\u4e0d\u8ffd\u3002\u7687\u592a\u6781\u4e8e\u662f\u56de\u519b\u518d\u6218\u3002 \u3000\u3000\u8881\u5d07\u7115\u4eb2\u4e0a\u57ce\u5934\u7763\u6218\uff0c\u5927\u58f0\u547c\u53eb\u3002\u6ee1\u6842\u6218\u4e8e\u57ce\u5916\u3002\u7956\u5927\u5bff\u3001\u5c24\u4e16\u7984\u56de\u5e08\u653b\u51fb\u6e05\u5175\u540e\u8def\u3002\u53cc\u65b9\u6b7b\u4f24\u5747\u91cd\uff0c\u6ee1\u6842\u8eab\u4e2d\u6570\u7bad\u3002\u660e\u519b\u91ce\u6218\u7ec8\u4e8e\u6253\u4e0d\u8fc7\u6e05\u519b\uff0c\u4e8e\u662f\u9000\u5165\u57ce\u4e2d\u636e\u5b88\u3002\u8fd9\u573a\u5927\u6218\u6253\u5f97\u5341\u5206\u60e8\u70c8\uff0c\u57ce\u58d5\u4e2d\u586b\u6ee1\u4e86\u4e24\u65b9\u519b\u58eb\u7684\u6b7b\u5c38\u3002 \u3000\u3000\u5b88\u519b\u53c8\u4ee5\u8461\u8404\u7259\u5927\u70ae\u8f70\u51fb\uff0c\u51fb\u788e\u6e05\u65b9\u5927\u8425\u5e10\u4e00\u5ea7\u53ca\u7687\u592a\u6781\u7684\u767d\u9f99\u65d7\uff0c\u6740\u4f24\u6e05\u5175\u4e0d\u5c11\u3002\u660e\u65b9\u7684\u62a5\u544a\u8bf4\uff0c\u7687\u592a\u6781\u957f\u5b50\u53ec\u529b\u5154\u8d1d\u52d2\u80f8\u53e3\u4e2d\u7bad\uff0c\u53e6\u4e00\u5b50\u6d6a\u8361\u5b81\u53e4\u8d1d\u52d2\u5728\u9635\u4e0a\u88ab\u660e\u519b\u5c04\u6740\uff0c\u53c8\u6740\u56fa\u5c71\uff08\u9886\u4e03\u5343\u4e94\u767e\u4eba\uff09\u56db\u4eba\u3001\u725b\u5f55\uff08\u9886\u4e09\u767e\u4eba\uff09\u4e09\u5341\u4f59\u540d\u3002\u8fd9\u62a5\u544a\u5931\u4e4b\u5938\u5927\uff0c\u4e8b\u5b9e\u4e0a\u5e76\u65e0\u7687\u592a\u6781\u7684\u513f\u5b50\u5728\u6b64\u5f79\u4e2d\u9635\u4ea1\u3002\u4f46\u6e05\u65b9\u7eaa\u5f55\u4e2d\u4e5f\u8bf4\uff1a\u6d4e\u5c14\u54c8\u6717\u8d1d\u52d2\u3001\u8428\u54c8\u5ec9\u8d1d\u52d2\u3001\u5927\u5c06\u74e6\u514b\u8fbe\u3001\u963f\u683c\u7b49\u5747\u53d7\u4f24\u3002 \u3000\u3000\u7687\u592a\u6781\u89c1\u90e8\u961f\u635f\u5931\u91cd\u5927\uff0c\u53ea\u5f97\u9000\u5175\uff0c\u518d\u653b\u9526\u5dde\u5357\u9762\uff0c\u4ea6\u4e0d\u80fd\u62d4\uff0c\u5c06\u58eb\u53c8\u906d\u5230\u4e0d\u5c11\u4f24\u4ea1\uff0c\u5c06\u9886\u89c9\u591a\u62dc\u5c71\u3001\u5df4\u5e0c\u7b49\u9635\u4ea1\u3002\u4e03\u6708\uff0c\u6e05\u5175\u8d25\u56de\u6c88\u9633\u3002 \u3000\u3000\u8fd9\u4e00\u5f79\u660e\u671d\u79f0\u4e3a\u201c\u5b81\u9526\u5927\u6377\u201d\uff0c\u662f\u660e\u519b\u5bf9\u6e05\u519b\u7b2c\u4e8c\u6b21\u8840\u6218\u80dc\u5229\u3002 \u3000\u3000\u8881\u5d07\u7115\u5728\u62a5\u529f\u7684\u594f\u7ae0\u4e2d\uff0c\u529b\u79f0\u529f\u52b3\u6700\u5927\u7684\u662f\u6ee1\u6842\u2460\u3002\u4ed6\u548c\u6ee1\u6842\u5411\u6765\u9887\u6709\u610f\u89c1\u51b2\u7a81\uff0c\u4f46\u5728\u594f\u7ae0\u4e2d\u529b\u79f0\u5b81\u8fdc\u5927\u6377\u4ee5\u6ee1\u6842\u4e4b\u529f\u5c45\u591a\uff0c\u53ef\u89c1\u5149\u660e\u78ca\u843d\uff0c\u5927\u516c\u65e0\u79c1\u3002 \u3000\u3000\u7b2c\u4e00\u6b21\u5b81\u8fdc\u5927\u6377\u662f\u5929\u542f\u516d\u5e74\u6b63\u6708\uff0c\u7b2c\u4e8c\u6b21\u5b81\u9526\u5927\u6377\u662f\u4e03\u5e74\u4e94\u6708\uff0c\u76f8\u9694\u4e00\u5e74\u96f6\u56db\u4e2a\u6708\u3002\u5728\u8fd9\u77ed\u77ed\u7684\u5341\u516d\u4e2a\u6708\u4e4b\u95f4\uff0c\u8881\u5d07\u7115\u52a0\u5f3a\u4e86\u660e\u519b\u7684\u6218\u6597\u529b\uff0c\u62a2\u7b51\u4e86\u9526\u5dde\u7684\u9632\u5fa1\u5de5\u4e8b\uff0c\u56fa\u5b88\u5728\u6e05\u519b\u7684\u540e\u8def\uff0c\u4f7f\u7687\u592a\u6781\u6709\u540e\u987e\u4e4b\u5fe7\uff0c\u4e0d\u6562\u4e45\u653b\u5b81\u8fdc\u3002\u540c\u65f6\u6e05\u519b\u5148\u653b\u9526\u5dde\u4e0d\u514b\uff0c\u518d\u653b\u5b81\u8fdc\uff0c\u6c14\u52bf\u5df2\u632b\u3002\u53ef\u89c1\u8881\u5d07\u7115\u8fd9\u5341\u516d\u4e2a\u6708\u4e2d\u7684\u51c6\u5907\u5de5\u4f5c\u6536\u5230\u4e86\u5f88\u5927\u6210\u6548\u3002\u5982\u679c\u80fd\u591a\u4e00\u4e9b\u548c\u5e73\u65f6\u671f\uff0c\u5c40\u9762\u5f53\u7136\u66f4\u6709\u6539\u8fdb\u3002 \u3000\u3000\u8fd9\u4e00\u4ed7\u5927\u6377\uff0c\u519b\u4e8b\u4e0a\u7684\u4e3b\u8981\u56e0\u7d20\u4e4b\u4e00\uff0c\u8fd8\u662f\u9760\u4e86\u8461\u8404\u7259\u7684\u7ea2\u8863\u5927\u70ae\u3002\u660e\u671d\u8fd9\u65f6\u672c\u6765\u5df2\u9a71\u9010\u4e86\u8461\u8404\u7259\u4eba\u7684\u5929\u4e3b\u6559\u4f20\u6559\u58eb\u3002\u4f20\u6559\u58eb\u6ce2\u5c14\u3001\u7c73\u514b\u8033\u4e24\u4eba\u89c1\u5230\u660e\u6e05\u4ea4\u5175\uff0c\u6709\u673a\u53ef\u4e58\uff0c\u4fbf\u53d1\u52a8\u6fb3\u95e8\u7684\u8461\u4eba\uff0c\u5411\u660e\u671d\u63d0\u4f9b\u519b\u8d39\u548c\u70ae\u624b\u3002\u660e\u671d\u4e8e\u662f\u53ec\u8fd8\u5df2\u9a71\u9010\u4e86\u7684\u6559\u58eb\u3002\u672c\u6765\u79d8\u5bc6\u4f20\u6559\u53d8\u6210\u4e86\u516c\u5f00\uff0c\u5927\u6279\u8461\u8404\u7259\u6559\u58eb\u548c\u70ae\u624b\u8fdb\u5165\u4e2d\u56fd\u2461\u3002\u540e\u6765\u4e2d\u56fd\u5728\u5916\u56fd\u6559\u58eb\u548c\u6280\u5e08\u6307\u5bfc\u4e4b\u4e0b\u81ea\u884c\u94f8\u70ae\u3002\u6240\u94f8\u6210\u7684\u5927\u70ae\u4e5f\u5c01\u4e86\u5b98\uff0c\u79f0\u4e3a\u201c\u5b89\u56fd\u5168\u519b\u5e73\u8fbd\u9756\u864f\u5c06\u519b\u201d\uff0c\u8fd8\u6d3e\u5b98\u796d\u70ae\uff0c\u8bf7\u5c06\u519b\u53d1\u5a01\u7834\u654c\u3002\u91d1\u4eba\u8981\u76f4\u5230\u6570\u5e74\u4e4b\u540e\uff0c\u624d\u56e0\u6295\u964d\u7684\u660e\u4eba\u4e4b\u52a9\u800c\u5f00\u59cb\u94f8\u9020\u5927\u70ae\u3002 \u3000\u3000\u8881\u5d07\u7115\u5728\u653f\u6cbb\u4e0a\u5c5e\u4e8e\u9b4f\u5fe0\u8d24\u7684\u654c\u5bf9\u6d3e\u7cfb\u3002\u4ed6\u4e2d\u8fdb\u58eb\u7684\u4e3b\u8003\u5b98\u97e9p\u3001\u4fdd\u8350\u4ed6\u7684\u5fa1\u53f2\u4faf\u6042\u7b49\u90fd\u662f\u4e1c\u6797\u515a\u7684\u5de8\u5934\u3002\u8881\u5d07\u7115\u5f53\u7136\u4e0d\u80af\u514b\u6263\u519b\u9977\u53bb\u5b5d\u656c\u9b4f\u5fe0\u8d24\u3002\u4f46\u4e3a\u4e86\u5927\u76ee\u6807\u662f\u5b88\u5fa1\u9526\u5dde\u3001\u5b81\u8fdc\uff0c\u4ed6\u4e5f\u76f8\u5f53\u7684\u59d4\u66f2\u6c42\u5168\u3002\u5404\u7701\u7763\u629a\u90fd\u4e3a\u9b4f\u5fe0\u8d24\u5efa\u751f\u7960\uff0c\u8881\u5d07\u7115\u5982\u679c\u4e0d\u9644\u548c\uff0c\u7acb\u523b\u5c31\u4f1a\u7f62\u5b98\uff0c\u5b88\u5fa1\u56fd\u571f\u7684\u5927\u5fd7\u65e0\u6cd5\u5f97\u4f38\uff0c\u56e0\u6b64\u5f53\u65f6\u4e5f\u53ea\u5f97\u5728\u84df\u8fbd\u4e3a\u9b4f\u5fe0\u8d24\u5efa\u751f\u7960\u3002 \u3000\u3000\u4f46\u9b4f\u5fe0\u8d24\u4ecd\u662f\u4e0d\u6ee1\u610f\u3002\u6240\u4ee5\u867d\u6709\u5b81\u9526\u5927\u6377\uff0c\u8881\u5d07\u7115\u5374\u5f97\u4e0d\u5230\u751a\u4e48\u91cd\u8d4f\uff0c\u53ea\u5347\u5b98\u4e00\u7ea7\u3002\u5949\u627f\u9b4f\u5fe0\u8d24\u7684\u5b98\u5458\u5374\u6709\u6570\u767e\u4eba\u56e0\u6b64\u5927\u6377\u800c\u5347\u5b98\uff0c\u7406\u7531\u662f\u5728\u671d\u4e2d\u7b56\u5212\u6709\u529f\uff0c\u8fde\u9b4f\u5fe0\u8d24\u4e00\u4e2a\u5c1a\u5728\u8941\u8913\u4e2d\u7684\u5a74\u513f\u4ece\u5b59\uff0c\u4e5f\u56e0\u6b64\u800c\u5c01\u4e86\u4f2f\u7235\u3002\u9b4f\u5fe0\u8d24\u662f\u592a\u76d1\uff0c\u6ca1\u6709\u513f\u5b50\uff0c\u53ea\u597d\u5927\u5c01\u4ed6\u4f84\u513f\uff0c\u5c01\u4ed6\u4f84\u513f\u7684\u513f\u5b50\u3002 \u3000\u3000\u9b4f\u5fe0\u8d24\u8fd9\u65f6\u66f4\u53eb\u4e00\u540d\u8a00\u5b98\u5f39\u988f\u8881\u5d07\u7115\uff0c\u8bf4\u4ed6\u6ca1\u6709\u53bb\u6551\u9526\u5dde\u4e3a\u201c\u66ae\u6c14\u201d\u3002\u8881\u5d07\u7115\u5728\u8fd9\u6837\u7684\u538b\u529b\u4e4b\u4e0b\uff0c\u53ea\u5f97\u81ea\u79f0\u6709\u75c5\uff0c\u8bf7\u6c42\u8f9e\u804c\u3002\u9b4f\u5fe0\u8d24\u7acb\u523b\u6279\u51c6\uff0c\u6d3e\u5175\u90e8\u5c1a\u4e66\u738b\u4e4b\u81e3\u53bb\u63a5\u66ff\u3002 \u3000\u3000\u7687\u592a\u6781\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u5f53\u7136\u662f\u5927\u559c\u82e5\u72c2\uff0c\u800c\u542c\u5230\u52a0\u7ed9\u8881\u5d07\u7115\u7684\u7f6a\u540d\u4e0e\u8bc4\u8bed\u7adf\u662f\u201c\u66ae\u6c14\u201d\u4e24\u5b57\uff0c\u6050\u6015\u5927\u559c\u4e4b\u4f59\uff0c\u5374\u4e5f\u4e0d\u514d\u6115\u7136\u826f\u4e45\u5427\uff1f\u8881\u5d07\u7115\u8fd9\u6837\u7684\u4eba\u7adf\u7b97\u201c\u66ae\u6c14\u6c89\u6c89\u201d\uff0c\u5374\u4e0d\u77e5\u8c01\u624d\u662f\u201c\u671d\u6c14\u84ec\u52c3\u201d\uff1f \u3000\u3000\u8881\u5d07\u7115\u79bb\u5f00\u5b81\u8fdc\u65f6\uff0c\u5fc3\u4e2d\u611f\u6168\u4e07\u5343\uff0c\u53ef\u60f3\u800c\u77e5\u3002\u90a3\u65f6\u4ed6\u8fd8\u53ea\u56db\u5341\u5c81\u5de6\u53f3\uff0c\u65b9\u5f53\u58ee\u76db\u7684\u82f1\u5e74\uff0c\u6b63\u662f\u8981\u5927\u5c55\u62b1\u8d1f\u7684\u65f6\u5019\u3002\u7acb\u4e86\u5927\u529f\u53cd\u800c\u88ab\u8feb\u9000\u4f11\uff0c\u4ed6\u7684\u90e8\u5c5e\u5c06\u58eb\u65e2\u611f\u8be7\u5f02\uff0c\u66f4\u662f\u5fff\u5fff\u4e0d\u5e73\u3002\u4ed6\u5199\u4e86\u4e00\u9996\u8bd7\u7ed9\u4e00\u4e2a\u90e8\u5c06\uff0c\u8bd7\u4e2d\u8bf4\uff1a\u6211\u4eec\u6177\u6168\u540c\u4ec7\uff0c\u95f4\u5173\u767e\u6218\uff0c\u529f\u52b3\u4e0d\u5c0f\uff0c\u7687\u4e0a\u7684\u6069\u9047\u4e5f\u91cd\u3002\u4f46\u6211\u7684\u82e6\u5fc3\uff0c\u5374\u53ea\u6709\u540e\u4eba\u77e5\u9053\u4e86\u3002\u5efa\u529f\u7acb\u4e1a\u56fa\u7136\u5f88\u597d\uff0c\u56de\u5bb6\u4f11\u517b\u4e5f\u662f\u4e0d\u9519\u3002\u5bf9\u4e8e\u6211\u7684\u53bb\u7559\uff0c\u5927\u5bb6\u4e0d\u5fc5\u611f\u5230\u4e0d\u5e73\u7f62\u3002\u8fd9\u9996\u8bd7\u663e\u5f97\u5f88\u6709\u6c14\u5ea6\u2462\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u5bf9\u4e8e\u5929\u542f\u7687\u5e1d\uff0c\u8fd8\u662f\u5341\u5206\u611f\u6fc0\u7684\u3002\u4ed6\u672c\u6765\u662f\u4e00\u4e2a\u4e03\u54c1\u77e5\u53bf\uff0c\u81ea\u5929\u542f\u4e8c\u5e74\u5230\u4e03\u5e74\u590f\u5929\uff0c\u77ed\u77ed\u7684\u4e94\u5e74\u534a\u4e4b\u95f4\uff0c\u51e0\u4e4e\u5e74\u5e74\u5347\u5b98\uff0c\u4e2d\u95f4\u8fd8\u8df3\u7ea7\uff0c\u76f4\u5347\u5230\u201c\u5de1\u629a\u8fbd\u4e1c\u3001\u5175\u90e8\u53f3\u4f8d\u90ce\u3001\u517c\u90fd\u5bdf\u9662\u53f3\u4f65\u90fd\u5fa1\u53f2\u201d\uff0c\u5b9e\u5728\u7b97\u662f\u98de\u9ec4\u817e\u8fbe\u3002\u4ed6\u81ea\u89c9\u5347\u5b98\u592a\u5feb\uff0c\u66fe\u4e0a\u758f\u8f9e\u8c22\u3002\u4ed6\u8bf4\u5728\u8bf8\u540c\u5e74\u4e2d\uff0c\u5b98\u804c\u6700\u9ad8\u4e4b\u4eba\u548c\u4ed6\u4e5f\u5dee\u7740\u597d\u51e0\u7ea7\uff0c\u4e3a\u4e86\u8981\u505a\u90e8\u5c5e\u6b66\u5c06\u7684\u699c\u6837\uff0c\u8bf7\u7687\u5e1d\u6536\u56de\u5347\u8d4f\u7684\u6210\u547d\u3002\u7687\u5e1d\u6279\u590d\u8bf4\uff1a\u4f60\u63a5\u8fde\u4e09\u6b21\u8c26\u8f9e\uff0c\u54c1\u5fb7\u5f88\u597d\uff0c\u4f46\u4f60\u529f\u52b3\u5927\uff0c\u5347\u5b98\u662f\u5e94\u8be5\u7684\u2463\u3002 \u3000\u3000\u4ed6\u5728\u56de\u5e7f\u4e1c\u6545\u4e61\u9014\u4e2d\uff0c\u7ecf\u8fc7\u5927\u5ebe\u5cad\u65f6\u5199\u4e86\u4e00\u9996\u8bd7\uff0c\u611f\u5ff5\u5929\u542f\u5bf9\u4ed6\u7684\u77e5\u9047\u4e4b\u6069\u2464\u3002\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u5929\u542f\u662f\u4e2a\u660f\u541b\uff0c\u53ef\u662f\u5bf9\u5f85\u81ea\u5df1\u5b9e\u5728\u5f88\u597d\u3002 \u3000\u3000\u4ed6\u5230\u4e86\u5e7f\u5dde\uff0c\u53bb\u5149\u5b5d\u5bfa\u6e38\u89c8\uff0c\u8e0f\u8db3\u4f5b\u5730\uff0c\u4e0d\u7981\u60f3\u5230\u751f\u5e73\u6740\u4eba\u751a\u591a\uff0c\u548c\u73af\u5883\u5927\u4e0d\u8c03\u548c\u2465\uff0c\u7136\u800c\u90a3\u4e5f\u53ea\u662f\u611f\u5230\u4e0d\u8c03\u548c\u800c\u5df2\u3002\u82f1\u96c4\u8c6a\u6770\uff0c\u4e00\u5f80\u65e0\u6094\uff0c\u5374\u4e5f\u65e0\u987b\u5bf9\u83e9\u8428\u4f4e\u5934\uff0c\u4e0d\u5fc5\u5bf9\u6740\u4e86\u8be5\u6740\u4e4b\u4eba\u6709\u751a\u4e48\u9057\u61be\u3002 \u3000\u3000\u2460\u8881\u5d07\u7115\u7684\u594f\u7ae0\u4e2d\u8bf4\uff1a\u201c\u5341\u5e74\u6765\uff0c\u5c3d\u5929\u4e0b\u4e4b\u5175\uff0c\u672a\u5c1d\u6562\u4e0e\u5974\u5408\u9a6c\u4ea4\u950b\uff0c\u5373\u81e3\u53bb\u5e74\uff0c\u4ea6\u81ea\u57ce\u4e0a\u800c\u4e0b\u653b\u3002\u81ea\u4eca\u59cb\u4e00\u5200\u4e00\u67aa\uff0c\u4e0b\u800c\u62da\u547d\uff0c\u4e0d\u987e\u5937\u4e4b\u51f6\u72e0\u527d\u608d\u3002\u81e3\u590d\u51ed\u581e\u5927\u547c\uff0c\u5206\u8def\u8fdb\u8ffd\u3002\u8bf8\u519b\u5fff\u6068\uff0c\u8a93\u4e00\u6218\u4ee5\u632b\u6b64\u8d3c\u3002\u6b64\u7686\u5c06\u519b\u6ee1\u6842\u4e4b\u529f\u5c45\u591a\u3002\u201d \u3000\u3000\u2461\u9a6c\u8033\u4e01\u7684\u300a\u9791\u977c\u6218\u8bb0\u300b\u4e2d\u5927\u5439\u8461\u8404\u7259\u4f20\u6559\u7684\u529f\u52b3\uff0c\u53c8\u8bf4\uff1a\u201c\u4e0a\u5e1d\u5bf9\u4e8e\u4fe1\u4ef0\u57fa\u7763\u6559\u7684\u7687\u5e1d\u5fc5\u4e88\u798f\u4f51\uff0c\u6240\u4ee5\u4e2d\u56fd\u7687\u5e1d\u5bf9\u9791\u977c\u4eba\uff08\u6307\u6ee1\u6e05\uff09\u4f5c\u6218\u5927\u80dc\u3002\u201d\u5176\u5b9e\u5929\u542f\u7687\u5e1d\u4fe1\u4ef0\u7684\u662f\u9c81\u73ed\u5148\u5e08\uff0c\u5e76\u6ca1\u6709\u4fe1\u4ef0\u57fa\u7763\u6559\u7684\u4e0a\u5e1d\u3002 \u3000\u3000\u636e\u51af\u627f\u94a7\u8bd1\u3001\u6c99\u4e0d\u5217\u64b0\uff1a\u300a\u660e\u672b\u5949\u4f7f\u7f57\u9a6c\u6559\u5ef7\u8036\u7a23\u4f1a\u58eb\u535c\u5f25\u683c\u4f20\u300b\uff1a\u5d07\u796f\u4e09\u5e74\uff0c\u6fb3\u95e8\u8461\u4eba\u961f\u957f\u7387\u58eb\u5352\u56db\u767e\u3001\u5927\u70ae\u5341\u5c0a\u5165\u5883\u6548\u529b\u3002\u5e7f\u5dde\u5de8\u5546\u6050\u5931\u5784\u65ad\u4e2d\u897f\u8d38\u6613\u4e4b\u5229\uff0c\u539a\u8d42\u671d\u81e3\uff0c\u52a0\u4ee5\u963b\u6320\u3002\u540e\u8461\u519b\u961f\u957f\u516c\u6c99\u7684\u897f\u52b3\u9635\u4ea1\u4e8e\u767b\u83b1\u3002\u300a\u78a7\u8840\u5251\u300b\u5c0f\u8bf4\u7565\u53d6\u5176\u610f\u3002 \u3000\u3000\u2462\u8881\u5d07\u7115\u300a\u5357\u8fd8\u522b\u9648\u7ffc\u6240\u603b\u620e\u300b\uff1a\u201c\u6177\u6168\u540c\u4ec7\u65e5\uff0c\u95f4\u5173\u767e\u6218\u65f6\uff0c\u529f\u9ad8\u660e\u4e3b\u7737\uff0c\u5fc3\u82e6\u540e\u4eba\u77e5\u3002\u9e8b\u9e7f\u8fd8\u5c71\u4fbf\uff0c\u9e92\u9e9f\u7ed8\u9601\u5b9c\u3002\u53bb\u7559\u90fd\u83ab\u8bb6\uff0c\u79cb\u8349\u6b63\u79bb\u79bb\u3002\u201d\u5176\u4e2d\u201c\u529f\u9ad8\u660e\u4e3b\u7737\u201d\u8fd9\u4e00\u53e5\uff0c\u4e0d\u514d\u542b\u6709\u82e6\u6da9\u7684\u610f\u5473\u3002\u5929\u542f\u51b3\u4e0d\u662f\u660e\u4e3b\uff0c\u5929\u4e0b\u7686\u77e5\uff0c\u81ea\u5df1\u529f\u9ad8\u5982\u6b64\uff0c\u7ed3\u679c\u5374\u5f97\u4e86\u8fd9\u6837\u7684\u201c\u7737\u201d\uff0c\u8fd9\u4f4d\u201c\u660e\u4e3b\u201d\uff0c\u771f\u662f\u201c\u660e\u201d\u5f97\u5f88\u4e86\u3002 \u3000\u3000\u2463\u8881\u5d07\u7115\u300a\u5929\u542f\u516d\u5e74\u516d\u6708\u521d\u5341\u65e5\u8c22\u5347\u836b\u758f\u300b\u4e2d\u8bf4\uff1a\u201c\u4e14\u6b66\u4eba\u5954\u7ade\uff0c\u5c11\u7ad6\u7acb\u4fbf\u6b32\u539a\u8fc1\uff0c\u7a0d\u4e0d\u5408\u8f84\u601d\u6fc0\u53bb\uff0c\u8981\u631f\u671d\u5ef7\uff0c\u5f00\u8845\u540c\u7c7b\uff0c\u4eca\u8fb9\u7586\u59cb\u7ec8\u4e0d\u5f97\u4e00\u4eba\u4e4b\u7528\uff0c\u81e3\u6700\u75be\u4e4b\u3002\u81e3\u4eca\u65e5\u4e0d\u81ea\u5904\u4e8e\u606c\uff0c\u4f55\u4ee5\u6d88\u8bf8\u5c06\u4e4b\u7ade\uff1f\u51b5\u81e3\u539f\u65e0\u5bcc\u8d35\u4e4b\u5fc3\uff0c\u53c8\u7687\u4e0a\u6240\u9274\u4e5f\u3002\u201d\u5bf9\u8fd9\u4e2a\u8f9e\u8d4f\u7684\u594f\u7ae0\uff0c\u671d\u5ef7\u7684\u6279\u7b54\u662f\uff1a\u201c\u5949\u5723\u65e8\uff1a\u8881\u5d07\u7115\u5b58\u57ce\u529f\u9ad8\uff0c\u52a0\u6069\u793a\u916c\uff0c\u539f\u4e0d\u4e3a\u8fc7\uff1b\u4e43\u4e09\u758f\u63a7\u8f9e\uff0c\u6108\u5f81\u514b\u8ba9\u3002\u8fd8\u7740\u9075\u65e8\u7947\u627f\u3002\u8be5\u90e8\u77e5\u9053\u3002\u201d \u3000\u3000\u2464\u8881\u5d07\u7115\u300a\u5f52\u5ebe\u5cad\u300b\uff1a\u201c\u529f\u540d\u52b3\u5341\u8f7d\uff0c\u5fc3\u8ff9\u6e10\u4f9d\u8fdd\u3002\u5fcd\u8bf4\u8fd8\u5c71\u662f\uff1f\u96be\u8a00\u51fa\u585e\u975e\u3002\u4e3b\u6069\u5929\u5730\u91cd\uff0c\u81e3\u9047\u53e4\u4eca\u7a00\u3002\u6570\u5377\u5c01\u7ae0\u5916\uff0c\u6d51\u7136\u65e7\u65e5\u5f52\u3002\u201d \u3000\u3000\u2465\u8881\u5d07\u7115\u300a\u9047\u8bc3\u6797\u5bfa\u53e3\u5360\u300b\uff1a\u201c\u56db\u5341\u5e74\u6765\u8fc7\u534a\u8eab\uff0c\u671b\u4e2d\u7947\u6811\u9694\u7ea2\u5c18\u3002\u5982\u4eca\u7740\u8db3\u7a7a\u738b\u5730\uff0c\u591a\u4e86\u4ece\u524d\u5b66\u6740\u4eba\u3002\u201d \u3000\u3000\u4e5d \u3000\u3000\u5929\u542f\u7687\u5e1d\u71b9\u5b97\u6349\u4e86\u51e0\u5e74\u8ff7\u85cf\uff08\u4ed6\u521d\u505a\u7687\u5e1d\u65f6\uff0c\u7231\u548c\u5c0f\u592a\u76d1\u6349\u8ff7\u85cf\uff09\uff0c\u505a\u4e86\u51e0\u5e74\u6728\u5de5\uff08\u4e0d\u662f\u505a\u7687\u5e1d\uff09\uff0c\u5929\u542f\u4e03\u5e74\u516b\u6708\uff0c\u5728\u4e8c\u5341\u4e09\u5c81\u4e0a\u6b7b\u4e86\u3002 \u3000\u3000\u5929\u542f\u7684\u513f\u5b50\u90fd\u5df2\u592d\u6298\uff0c\u6709\u4e9b\u540e\u5983\u6000\u4e86\u5b55\uff0c\u4e5f\u90fd\u88ab\u5ba2\u6c0f\u548c\u9b4f\u5fe0\u8d24\u8bbe\u6cd5\u5f04\u5f97\u6d41\u4ea7\uff0c\u6240\u4ee5\u6ca1\u6709\u513f\u5b50\u3002\u7531\u4ed6\u4eb2\u5f1f\u5f1f\u4fe1\u738b\u7531\u68c0\u63a5\u4f4d\uff0c\u5e74\u53f7\u5d07\u796f\u3002 \u3000\u3000\u6731\u7531\u68c0\u5f53\u65f6\u865a\u5c81\u662f\u5341\u516b\u5c81\u3002\u4ed6\u751f\u4e8e\u4e07\u5386\u4e09\u5341\u516b\u5e74\u5341\u4e8c\u6708\uff0c\u5176\u5b9e\u53ea\u5341\u516d\u5c81\u53e6\u516b\u4e2a\u6708\u3002\u8fd9\u4e2a\u5341\u4e03\u5c81\u7684\u5c11\u5e74\u7687\u5e1d\u4e0d\u52a8\u58f0\u8272\u7684\u5bf9\u4ed8\u9b4f\u5fe0\u8d24\uff0c\u5148\u5c06\u4ed6\u7684\u515a\u7fbd\u6162\u6162\u6536\u62fe\uff0c\u7136\u540e\u903c\u5f97\u4ed6\u81ea\u6740\u3002\u8fd9\u573a\u6743\u529b\u6597\u4e89\u5904\u7406\u5f97\u5341\u5206\u7cbe\u91c7\u3002 \u3000\u3000\u9b4f\u5fe0\u8d24\u6b7b\u540e\uff0c\u9644\u548c\u4ed6\u7684\u65e0\u803b\u5927\u81e3\u88ab\u79f0\u4e3a\u201c\u9006\u515a\u201d\uff0c\u6216\u6740\u5934\uff0c\u6216\u5145\u519b\uff0c\u6216\u514d\u804c\uff0c\u4eba\u5fc3\u5927\u5feb\uff0c\u5728\u201c\u5b81\u9526\u5927\u6377\u201d\u4e2d\u5192\u529f\u7684\u4eba\u4e5f\u90fd\u88ab\u6e05\u9664\u4e86\u3002 \u3000\u3000\u88ab\u9b4f\u5fe0\u8d24\u9006\u515a\u6392\u6324\u7f62\u5b98\u7684\u5927\u81e3\u53c8\u518d\u8d77\u7528\uff0c\u4ed6\u4eec\u90fd\u4e3b\u5f20\u53ec\u56de\u8881\u5d07\u7115\u3002\u5929\u542f\u4e03\u5e74\u5341\u4e00\u6708\uff0c\u5347\u8881\u5d07\u7115\u4e3a\u53f3\u90fd\u5fa1\u53f2\u3001\u89c6\u5175\u90e8\u6dfb\u6ce8\u5de6\u4f8d\u90ce\u4e8b\u3002\u5d07\u796f\u5143\u5e74\u56db\u6708\uff0c\u518d\u5347\u4ed6\u4e3a\u5175\u90e8\u5c1a\u4e66\u3001\u517c\u53f3\u526f\u90fd\u5fa1\u53f2\u3001\u7763\u5e08\u84df\u8fbd\u3001\u517c\u7763\u767b\u83b1\u5929\u6d25\u519b\u52a1\u3002\u5175\u90e8\u5c1a\u4e66\u662f\u6b63\u4e8c\u54c1\u7684\u5927\u5b98\uff0c\u6240\u8f96\u7684\u519b\u533a\uff0c\u540d\u4e49\u4e0a\u4e5f\u6269\u5927\u5230\u5317\u76f4\u96b6\uff08\u6cb3\u5317\uff09\u5317\u90e8\u548c\u5c71\u4e1c\u5317\u90e8\u6cbf\u6d77\uff0c\u6210\u4e3a\u6297\u6e05\u603b\u53f8\u4ee4\u3002\u4e0d\u8fc7\u84df\u5dde\u3001\u5929\u6d25\u3001\u767b\u83b1\u5404\u5730\u53e6\u6709\u5de1\u629a\u4e13\u8d23\uff0c\u6240\u4ee5\u8881\u5d07\u7115\u6240\u7ba1\u7684\u5b9e\u9645\u8fd8\u662f\u5c71\u6d77\u5173\u53ca\u5173\u5916\u9526\u5b81\u7684\u9632\u52a1\u3002 \u3000\u3000\u660e\u672b\u519b\u5236\uff0c\u5728\u5916\u5e26\u5175\u7684\u6587\u81e3\uff0c\u5934\u8854\u6700\u9ad8\u7684\u662f\u7763\u5e08\uff0c\u901a\u5e38\u4ee5\u6728\u5b66\u58eb\u517c\u4efb\uff0c\u5bb0\u76f8\u51fa\u5916\u5e26\u5175\uff0c\u624d\u79f0\u7763\u5e08\uff1b\u5176\u6b21\u662f\u603b\u7763\u6216\u7ecf\u7565\uff0c\u7531\u5175\u90e8\u5c1a\u4e66\u6216\u4f8d\u90ce\u517c\u4efb\uff1b\u66f4\u5176\u6b21\u662f\u5de1\u629a\uff1b\u5de1\u629a\u4e4b\u4e0b\u624d\u662f\u6b66\u5c06\u4e2d\u6700\u9ad8\u7684\u603b\u5175\u5b98\u3002\u8881\u5d07\u7115\u4e0d\u662f\u5927\u5b66\u58eb\uff0c\u5374\u6709\u4e86\u5927\u5b66\u58eb\u65b9\u80fd\u5f97\u5230\u7684\u519b\u4e8b\u6700\u9ad8\u5b98\u8854\u3002\u4ee5\u524d\u8fbd\u4e1c\u5386\u4efb\u519b\u4e8b\u957f\u5b98\u90fd\u53ea\u662f\u7ecf\u7565\u6216\u5de1\u629a\u3002\u90a3\u65f6\u8ddd\u4ed6\u505a\u77e5\u53bf\u4e4b\u65f6\u8fd8\u53ea\u516d\u5e74\u3002 \u3000\u3000\u8881\u5d07\u7115\u5728\u5e7f\u4e1c\u5bb6\u5c45\u8fd9\u51e0\u4e2a\u6708\u4e2d\uff0c\u4e0e\u4e00\u822c\u6587\u4eba\u8bd7\u9152\u5531\u548c\uff0c\u5176\u4e2d\u6700\u8457\u540d\u7684\u670b\u53cb\u662f\u9648\u5b50\u58ee\u3002 \u3000\u3000\u9648\u5b50\u58ee\u662f\u5e7f\u4e1c\u5357\u6d77\u4eba\uff0c\u548c\u8881\u540c\u79d1\u4e2d\u8fdb\u58eb\uff0c\u9648\u662f\u63a2\u82b1\u3002\u4ed6\u5728\u4f5c\u6d59\u6c5f\u4e3b\u8003\u5b98\u65f6\u51fa\u9898\u76ee\u8bbd\u523a\u9b4f\u5fe0\u8d24\uff0c\u56e0\u800c\u88ab\u7f62\u5b98\u3002\u8881\u9648\u4e24\u4eba\u540c\u4e61\u540c\u5e74\uff0c\u53c8\u5fd7\u540c\u9053\u5408\uff0c\u4ea4\u60c5\u81ea\u7136\u975e\u540c\u5bfb\u5e38\u3002\u9648\u5b50\u58ee\u5728\u5d07\u796f\u65f6\u8d77\u590d\uff0c\u505a\u5230\u793c\u90e8\u4f8d\u90ce\uff0c\u540e\u6765\u5728\u5e7f\u4e1c\u4e5d\u6c5f\u8d77\u5175\u6297\u6e05\uff0c\u6218\u8d25\u88ab\u4fd8\uff0c\u4e0d\u964d\u800c\u6b7b\uff0c\u4e5f\u662f\u5e7f\u4e1c\u8457\u540d\u7684\u6c11\u65cf\u82f1\u96c4\u3002\u5f53\u65f6\u4e0e\u8881\u65f6\u5e38\u5728\u4e00\u8d77\u805a\u4f1a\u7684\uff0c\u8fd8\u6709\u51e0\u4e2a\u4f1a\u505a\u8bd7\u7684\u548c\u5c1a\u3002 \u3000\u3000\u8881\u5d07\u7115\u5e94\u5d07\u796f\u7684\u5f81\u53ec\u4e0a\u5317\u4eac\u65f6\uff0c\u4ed6\u5728\u5e7f\u4e1c\u7684\u670b\u53cb\u4eec\u66ff\u4ed6\u996f\u884c\u3002\u753b\u5bb6\u8d75\u853c\u592b\u753b\u4e86\u4e00\u5e45\u753b\uff0c\u56fe\u4e2d\u4e00\u5e06\u8fdc\u884c\uff0c\u5cb8\u4e0a\u6709\u5987\u5973\u4e09\u4eba\u3001\u5c0f\u5b69\u4e00\u4eba\u76f8\u9001\u3002\u9648\u5b50\u58ee\u5728\u56fe\u4e0a\u9898\u4e86\u56db\u4e2a\u5927\u5b57\uff1a\u201c\u80a4\u516c\u96c5\u594f\u201d\uff0c\u201c\u80a4\u516c\u201d\u5373\u201c\u80a4\u529f\u201d\uff0c\u795d\u8d3a\u4ed6\u201c\u514b\u594f\u80a4\u529f\u201d\u7684\u610f\u601d\u3002\u56fe\u540e\u6709\u8bb8\u591a\u4eba\u7684\u9898\u8bd7\uff0c\u7b2c\u4e00\u4e2a\u9898\u7684\u5c31\u662f\u9648\u5b50\u58ee\u3002\u8fd9\u5e45\u753b\u672c\u6765\u6709\u4e0a\u6b3e\uff0c\u540e\u6765\u8881\u5d07\u7115\u88ab\u5904\u6b7b\uff0c\u4e0a\u6b3e\u7ed9\u6536\u85cf\u8005\u6316\u53bb\u4e86\uff0c\u591a\u6b21\u6613\u624b\u6d41\u8f6c\uff0c\u5230\u5149\u7eea\u5e74\u95f4\u624d\u7531\u738b\u9e4f\u8fd0\u8003\u660e\u771f\u76f8\u3002\u4e00\u7fa4\u5e7f\u4e1c\u6587\u4eba\u540e\u6765\u5c06\u56fe\u4e0e\u8bd7\u5f71\u5370\u6210\u4e00\u672c\u518c\u5b50\uff0c\u627f\u4e00\u4f4d\u670b\u53cb\u9001\u4e86\u6211\u4e00\u672c\u3002\u539f\u56fe\u76ee\u524d\u662f\u5728\u9999\u6e2f\u3002 \u3000\u3000\u201c\u80a4\u516c\u96c5\u594f\u56fe\u201d\u4e0a\u7684\u9898\u8bd7\uff0c\u5927\u90fd\u662f\u79f0\u8a89\u8881\u5d07\u7115\u7684\u6297\u6e05\u529f\u7ee9\uff0c\u9884\u6599\u6b64\u53bb\u5b9a\u53ef\u626b\u5e73\u80e1\u5c18\u3001\u71d5\u7136\u52d2\u77f3\uff0c\u9e9f\u9601\u9898\u540d\u7b49\u7b49\u3002\u597d\u51e0\u4eba\u8bd7\u53e5\u4e2d\u90fd\u63d0\u5230\u8881\u5d07\u7115\u7684\u201c\u8c08\u950b\u201d\u3001\u201c\u9ad8\u8c08\u201d\u3001\u201c\u7b11\u8c08\u201d\u2460\u3002\u559c\u4e0e\u670b\u53cb\u4eec\u9ad8\u8c08\u9614\u8bba\uff0c\u4e00\u5b9a\u662f\u4ed6\u4e2a\u6027\u4e2d\u5f88\u663e\u8457\u7684\u7279\u70b9\u3002 \u3000\u3000\u5728\u8fd9\u5e45\u753b\u4e0a\u9898\u8bd7\u7684\u5171\u6709\u5341\u4e5d\u4eba\uff0c\u5176\u4e2d\u6709\u548c\u5c1a\u4e09\u4eba\uff0c\u6709\u51e0\u4e2a\u662f\u8881\u7684\u5e55\u50da\u3002\u503c\u5f97\u6ce8\u610f\u7684\u662f\uff0c\u6709\u516b\u4e2a\u4eba\u5728\u5341\u5904\u5730\u65b9\u63d0\u5230\u4e86\u9ec4\u77f3\u516c\u3001\u8d64\u677e\u5b50\u3001\u572f\u4e0a\u3001\u7d20\u4e66\u7684\u5178\u6545\uff0c\u8fd9\u51b3\u4e0d\u4f1a\u662f\u5076\u7136\u73b0\u8c61\u3002\u8fd9\u5178\u6545\u662f\u8bf4\u5f20\u826f\u7acb\u4e86\u5927\u529f\u4e4b\u540e\uff0c\u968f\u5373\u9000\u9690\uff0c\u624d\u907f\u514d\u7ed9\u731c\u5fcc\u6b8b\u5fcd\u7684\u5218\u90a6\u6240\u6740\u3002\u5728\u8fd9\u6b21\u996f\u522b\u5bb4\u4e2d\uff0c\u8881\u5d07\u7115\u7684\u670b\u53cb\u4eec\u4e00\u5b9a\u5f3a\u8c03\u5fc5\u987b\u201c\u529f\u6210\u8eab\u9000\u201d\uff0c\u5927\u5bb6\u5bf9\u4e8e\u7687\u5e1d\u7684\u72e0\u6bd2\u624b\u6bb5\u90fd\u6df1\u5177\u6212\u5fc3\uff0c\u6240\u4ee5\u5728\u8bd7\u4e2d\u4e00\u518d\u8b66\u6212\u2461\u3002 \u3000\u3000\u4e03\u6708\uff0c\u8881\u5d07\u7115\u5230\u8fbe\u5317\u4eac\uff0c\u5d07\u796f\u2462\u53ec\u89c1\u4e8e\u5e73\u53f0\uff0c\u90a3\u662f\u5728\u660e\u5b98\u5de6\u5b89\u95e8\u2463\u3002 \u3000\u3000\u5d07\u796f\u89c1\u5230\u8881\u5d07\u7115\u540e\uff0c\u5148\u5927\u52a0\u6170\u52b3\uff0c\u7136\u540e\u8bf4\u9053\uff1a\u201c\u5efa\u90e8\u8df3\u6881\uff0c\u5df2\u6709\u5341\u5e74\u4e86\uff0c\u56fd\u571f\u6ca6\u9677\uff0c\u8fbd\u6c11\u6d82\u70ad\u3002\u537f\u4e07\u91cc\u8d74\u53ec\uff0c\u5fe0\u52c7\u53ef\u5609\uff0c\u6240\u6709\u5e73\u8fbd\u65b9\u7565\uff0c\u53ef\u5177\u5b9e\u594f\u6765\uff01\u201d \u3000\u3000\u8881\u5d07\u7115\u594f\u9053\uff1a\u201c\u6240\u6709\u65b9\u7565\uff0c\u90fd\u5df2\u5199\u5728\u594f\u7ae0\u91cc\u3002\u81e3\u4eca\u53d7\u7687\u4e0a\u7279\u8fbe\u4e4b\u77e5\uff0c\u8bf7\u7ed9\u6211\u653e\u624b\u53bb\u5e72\u7684\u6743\u529b\uff0c\u9884\u8ba1\u4e94\u5e74\u800c\u5efa\u90e8\u53ef\u5e73\uff0c\u5168\u8fbd\u53ef\u4ee5\u6062\u590d\u3002\u201d \u3000\u3000\u5d07\u796f\u9053\uff1a\u201c\u4e94\u5e74\u590d\u8fbd\uff0c\u4fbf\u662f\u65b9\u7565\uff0c\u6715\u4e0d\u541d\u5c01\u4faf\u4e4b\u8d4f\u3002\u537f\u5176\u52aa\u529b\u4ee5\u89e3\u5929\u4e0b\u5012\u60ac\u4e4b\u82e6\uff01\u537f\u5b50\u5b59\u4ea6\u53d7\u5176\u798f\u3002\u201d\u8881\u5d07\u7115\u8c22\u6069\u5f52\u73ed\u3002\u5d07\u796f\u6682\u9000\u5c11\u61a9\u3002 \u3000\u3000\u7ed9\u4e8b\u8bb8\u8a89\u537f\u5c31\u53bb\u95ee\u8881\u5d07\u7115\uff0c\u7528\u751a\u4e48\u65b9\u7565\u53ef\u4ee5\u5728\u4e94\u5e74\u4e4b\u5185\u5e73\u8fbd\u3002\u8881\u5d07\u7115\u9053\uff1a\u201c\u6211\u8fd9\u6837\u8bf4\uff0c\u662f\u60f3\u8981\u5bbd\u6170\u7687\u4e0a\u3002\u201d\u8bb8\u8a89\u537f\u5df2\u670d\u4f8d\u5d07\u796f\u5c06\u8fd1\u4e00\u5e74\uff0c\u660e\u767d\u7687\u5e1d\u7684\u4e2a\u6027\uff0c\u8881\u5d07\u7115\u5374\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u7687\u5e1d\u3002\u8bb8\u8a89\u537f\u4e8e\u662f\u63d0\u9192\u4ed6\uff1a\u201c\u7687\u4e0a\u662f\u82f1\u660e\u5f97\u5f88\u7684\uff0c\u5c82\u53ef\u968f\u4fbf\u594f\u5bf9\uff1f\u5230\u4e94\u5e74\u671f\u6ee1\uff0c\u90a3\u65f6\u4f60\u8fd8\u6ca1\u6709\u5e73\u8fbd\uff0c\u90a3\u600e\u4e48\u5f97\u4e86\uff1f\u201d\u8881\u5d07\u7115\u4e00\u542c\u4e4b\u4e0b\uff0c\u723d\u7136\u81ea\u5931\uff0c\u77e5\u9053\u521a\u624d\u7684\u8bdd\u8bf4\u5f97\u6709\u4e9b\u5938\u5f20\u4e86\u3002 \u3000\u3000\u4ed6\u7b54\u5e94\u5d07\u796f\u4e94\u5e74\u4e4b\u5185\u53ef\u4ee5\u5e73\u5b9a\u6ee1\u6e05\u3001\u6062\u590d\u5168\u8fbd\uff0c\u5b9e\u5728\u662f\u4e00\u65f6\u51b2\u52a8\u7684\u53e3\u4e0d\u62e9\u8a00\uff0c\u4e8b\u5b9e\u4e0a\u90a3\u662f\u6839\u672c\u4e0d\u53ef\u80fd\u7684\u3002\u8881\u5d07\u7115\u548c\u5d07\u796f\u7b2c\u4e00\u6b21\u89c1\u9762\uff0c\u5c31\u72af\u4e86\u4e00\u4e2a\u5927\u9519\u8bef\u3002\u5927\u6982\u4ed6\u89c1\u8fd9\u4f4d\u5341\u4e03\u5c81\u534a\u7684\u5c11\u5e74\u7687\u5e1d\u5f88\u7740\u6025\uff0c\u5c31\u968f\u53e3\u5b89\u6170\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7687\u5e1d\u53c8\u51fa\u6765\u3002\u8881\u5d07\u7115\u4e8e\u662f\u53c8\u594f\u9053\uff1a\u201c\u5efa\u5dde\u5df2\u5904\u5fc3\u79ef\u8651\u7684\u51c6\u5907\u4e86\u56db\u5341\u5e74\uff0c\u8fd9\u5c40\u9762\u539f\u662f\u5f88\u4e0d\u6613\u5904\u7406\u7684\u3002\u4f46\u7687\u4e0a\u6ce8\u610f\u8fb9\u7586\u4e8b\u52a1\uff0c\u65e5\u591c\u5fe7\u5fc3\uff0c\u81e3\u53c8\u600e\u6562\u8bf4\u96be\uff1f\u8fd9\u4e94\u5e74\u4e4b\u4e2d\uff0c\u5fc5\u987b\u4e8b\u4e8b\u5e94\u624b\uff0c\u9996\u5148\u662f\u94b1\u7cae\u3002\u201d\u5d07\u796f\u7acb\u5373\u8c15\u77e5\u4ee3\u7406\u6237\u90e8\u5c1a\u4e66\u7684\u53f3\u4f8d\u90ce\u738b\u5bb6\u6862\uff0c\u5fc5\u987b\u7740\u529b\u63aa\u529e\uff0c\u4e0d\u53ef\u4ee4\u5f97\u5173\u8fbd\u519b\u4e2d\u94b1\u7cae\u4e0d\u8db3\u3002 \u3000\u3000\u8881\u5d07\u7115\u53c8\u8bf7\u5668\u68b0\uff0c\u8bf4\uff1a\u201c\u5efa\u5dde\u51c6\u5907\u5145\u5206\uff0c\u5668\u68b0\u7280\u5229\uff0c\u9a6c\u5339\u58ee\u5065\uff0c\u4e45\u7ecf\u8bad\u7ec3\u3002\u4eca\u540e\u89e3\u5230\u8fb9\u7586\u53bb\u7684\u5f13\u7532\u7b49\u9879\uff0c\u4e5f\u987b\u7cbe\u5229\u3002\u201d\u5d07\u796f\u5373\u8c15\u4ee3\u7406\u5de5\u90e8\u5c1a\u4e66\u7684\u5de6\u4f8d\u90ce\u5f20\u7ef4\u67a2\uff1a\u201c\u4eca\u540e\u89e3\u53bb\u5173\u8fbd\u7684\u5668\u68b0\uff0c\u5fc5\u987b\u94f8\u660e\u76d1\u9020\u53f8\u5b98\u548c\u5de5\u5320\u7684\u59d3\u540d\uff0c\u5982\u6709\u8106\u8584\u4e0d\u582a\u4f7f\u7528\u7684\uff0c\u5c31\u53ef\u8ffd\u7a76\u67e5\u529e\u3002\u201d \u3000\u3000\u8881\u5d07\u7115\u53c8\u594f\uff1a\u201c\u4e94\u5e74\u4e4b\u4e2d\uff0c\u53d8\u5316\u5f88\u5927\u3002\u5fc5\u987b\u540f\u90e8\u4e0e\u5175\u90e8\u4e0e\u81e3\u5145\u5206\u5408\u4f5c\u3002\u5e94\u5f53\u9009\u7528\u7684\u4eba\u5458\u4fbf\u5373\u4efb\u547d\uff0c\u4e0d\u5e94\u5f53\u4efb\u7528\u7684\uff0c\u4e0d\u53ef\u968f\u4fbf\u6d3e\u4e0b\u6765\u3002\u201d\u5d07\u796f\u5373\u53ec\u540f\u90e8\u5c1a\u4e66\u738b\u6c38\u5149\u3001\u5175\u90e8\u5c1a\u4e66\u738b\u5728\u664b\uff0c\u5c06\u8881\u5d07\u7115\u7684\u8981\u6c42\u8c15\u77e5\u3002 \u3000\u3000\u8881\u5d07\u7115\u53c8\u594f\uff1a\u201c\u4ee5\u81e3\u7684\u529b\u91cf\uff0c\u5236\u5168\u8fbd\u662f\u6709\u4f59\u7684\uff0c\u4f46\u8981\u5e73\u606f\u4f17\u4eba\u7684\u7eb7\u7eb7\u8bae\u8bba\uff0c\u90a3\u5c31\u4e0d\u8db3\u4e86\u3002\u81e3\u4e00\u51fa\u4eac\u57ce\uff0c\u4e0e\u7687\u4e0a\u5c31\u9694\u5f97\u5f88\u8fdc\uff0c\u5fcc\u529f\u5992\u80fd\u7684\u4eba\u4e00\u5b9a\u4f1a\u6709\u7684\u3002\u8fd9\u4e9b\u4eba\u5373\u4f7f\u656c\u60e7\u7687\u4e0a\u7684\u6cd5\u5ea6\uff0c\u4e0d\u6562\u4e71\u7528\u6743\u529b\u6765\u6363\u4e71\u81e3\u7684\u4e8b\u52a1\uff0c\u4f46\u4e0d\u514d\u4f1a\u5927\u53d1\u8bae\u8bba\uff0c\u6270\u4e71\u81e3\u7684\u65b9\u7565\u3002\u201d\u5d07\u796f\u7ad9\u8d77\u8eab\u6765\uff0c\u503e\u542c\u4ed6\u7684\u8bf4\u8bdd\uff0c\u542c\u4e86\u5f88\u4e45\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u63d0\u51fa\u7684\u65b9\u7565\u4e95\u4e95\u6709\u6761\uff0c\u4e0d\u5fc5\u8c26\u900a\uff0c\u6715\u81ea\u6709\u4e3b\u6301\u3002\u201d \u3000\u3000\u5927\u5b66\u58eb\u5218\u9e3f\u8bad\u7b49\u90fd\u594f\uff0c\u8bf7\u7ed9\u8881\u5d07\u7115\u5927\u6743\uff0c\u8d50\u7ed9\u4ed6\u5c1a\u65b9\u5b9d\u5251\uff0c\u81f3\u4e8e\u738b\u4e4b\u81e3\u4e0e\u6ee1\u6842\u7684\u5c1a\u65b9\u5251\u5219\u5e94\u64a4\u56de\uff0c\u4ee5\u7edf\u4e00\u4e8b\u6743\u3002\u5d07\u796f\u8ba4\u4e3a\u5bf9\u6781\u3002\u5e94\u4e88\u7167\u529e\u3002\u8c08\u5b8c\u5927\u4e8b\u540e\uff0c\u8d50\u8881\u5d07\u7115\u9152\u9994\u3002 \u3000\u3000\u8881\u5d07\u7115\u8f9e\u51fa\u4e4b\u540e\uff0c\u4e0a\u4e86\u4e00\u9053\u594f\u7ae0\uff0c\u63d0\u51fa\u4e86\u5173\u8fbd\u519b\u52a1\u57fa\u672c\u6218\u7565\u7684\u4e09\u4e2a\u539f\u5219\u2464\uff1a\u201c\u4ee5\u8fbd\u4eba\u5b88\u8fbd\u571f\uff0c\u4ee5\u8fbd\u571f\u517b\u8fbd\u4eba\u201d\u2014\u2014\u660e\u4ee3\u5175\u5236\uff0c\u4e00\u65b9\u6709\u4e8b\uff0c\u4ece\u5404\u65b9\u8c03\u5175\u524d\u5f80\u3002\u56e0\u6b64\u5b88\u8fbd\u7684\u90e8\u961f\u6765\u81ea\u56db\u9762\u516b\u65b9\uff0c\u56db\u5ddd\u3001\u6e56\u5e7f\u3001\u6d59\u6c5f\u5747\u6709\u3002\u8fd9\u4e9b\u58eb\u5352\u9996\u5148\u5bf9\u5b88\u5fa1\u5173\u8fbd\u4e0d\u5927\u5173\u5fc3\uff0c\u6218\u6597\u529b\u65e2\u4e0d\u5f3a\uff0c\u53c8\u6015\u51b7\uff0c\u5728\u5173\u5916\u9a7b\u5b88\u4e00\u6bb5\u77ed\u65f6\u671f\uff0c\u4fbf\u9063\u56de\u5bb6\u4e61\uff0c\u53e6\u8c03\u65b0\u5175\u524d\u6765\u3002\u8881\u5d07\u7115\u8ba4\u4e3a\u5fc5\u987b\u7528\u8fbd\u5175\uff0c\u4ed6\u4eec\u4e3a\u4e86\u4fdd\u62a4\u5bb6\u4e61\uff0c\u6297\u654c\u52c7\u6562\uff0c\u53c8\u4e60\u4e8e\u5bd2\u51b7\u6c14\u5019\u3002\u8bad\u7ec3\u4e00\u652f\u7cbe\u5175\uff0c\u5fc5\u987b\u5175\u5c06\u76f8\u4e60\uff0c\u975e\u957f\u671f\u718f\u9676\u4e0d\u4e3a\u529f\uff0c\u4e0d\u80fd\u4eca\u5929\u8c03\u6765\uff0c\u660e\u5929\u53c8\u53e6\u8c03\u4e00\u6279\u65b0\u5175\u6765\u66ff\u6362\u3002\u4ed6\u4e3b\u5f20\u5728\u5173\u5916\u7b51\u57ce\u5c6f\u7530\uff0c\u9010\u6b65\u6269\u5927\u9632\u5b88\u5730\u57df\uff0c\u65e2\u7701\u7cae\u9977\uff0c\u53c8\u53ef\u4e0d\u65ad\u7684\u6536\u590d\u5931\u5730\u3002 \u3000\u3000\u201c\u5b88\u4e3a\u6b63\u7740\uff0c\u6218\u4e3a\u5947\u7740\uff0c\u548c\u4e3a\u65c1\u3002\u201d\u2014\u2014\u660e\u5175\u6253\u91ce\u6218\u7684\u6218\u6597\u529b\u4e0d\u53ca\u4e60\u4e8e\u9a91\u5c04\u7684\u6e05\u5175\uff0c\u8fd9\u662f\u5148\u5929\u7684\u9650\u5236\uff0c\u4e0d\u6613\u77ed\u671f\u5185\u626d\u8f6c\u8fc7\u6765\uff0c\u4f46\u5927\u70ae\u7684\u5a01\u529b\u5374\u975e\u6e05\u5175\u6240\u53ca\u3002\u6240\u4ee5\u8981\u820d\u5df1\u4e4b\u77ed\uff0c\u7528\u5df1\u6240\u957f\uff0c\u5b88\u575a\u57ce\u800c\u7528\u5927\u70ae\uff0c\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002\u53ea\u6709\u5728\u9700\u8981\u5947\u5175\u7a81\u51fa\u3001\u653b\u654c\u4e0d\u610f\u4e4b\u65f6\uff0c\u624d\u548c\u6e05\u5175\u6253\u91ce\u6218\u3002\u4e3a\u4e86\u4e89\u53d6\u65f6\u95f4\u6765\u8bad\u7ec3\u519b\u961f\u3001\u52a0\u5f3a\u57ce\u9632\uff0c\u6709\u65f6\u8fd8\u987b\u5728\u9002\u5f53\u65f6\u673a\u4e2d\u4e0e\u654c\u65b9\u8bae\u548c\uff0c\u8fd9\u662f\u8f85\u52a9\u6027\u7684\u6218\u7565\u3002 \u3000\u3000\u201c\u6cd5\u5728\u6e10\u4e0d\u5728\u9aa4\uff0c\u5728\u5b9e\u4e0d\u5728\u865a\u201d\u2014\u2014\u6267\u884c\u4e0a\u8ff0\u65b9\u7b56\u4e4b\u65f6\uff0c\u4e0d\u53ef\u6c42\u6025\u529f\u8fd1\u5229\uff0c\u5fc5\u987b\u7a33\u624e\u7a33\u6253\uff0c\u811a\u8e0f\u5b9e\u5730\uff0c\u6162\u6162\u7684\u63a8\u8fdb\u3002\u7edd\u5bf9\u4e0d\u53ef\u5192\u9669\u8f7b\u8fdb\uff0c\u4ee5\u81f4\u7ed9\u654c\u4eba\u4ee5\u53ef\u4e58\u4e4b\u673a\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u57fa\u672c\u6218\u7565\uff0c\u662f\u4ed6\u603b\u7ed3\u4e86\u660e\u6e05\u4e4b\u95f4\u6570\u6b21\u5927\u6218\u5f79\u800c\u5f97\u51fa\u6765\u7684\u7ed3\u8bba\u3002\u660e\u519b\u4e09\u6b21\u5927\u8d25\uff0c\u90fd\u8d25\u4e8e\u91ce\u6218\uff0c\u4ee5\u81f4\u5168\u519b\u8986\u6ca1\uff1b\u5b81\u8fdc\u4e24\u6b21\u5927\u6377\uff0c\u90fd\u5728\u4e8e\u5b88\u575a\u57ce\u3001\u7528\u5927\u70ae\u3002 \u3000\u3000\u8fd9\u57fa\u672c\u6218\u7565\u6301\u4e45\u7684\u63a8\u884c\u4e0b\u53bb\uff0c\u5c31\u53ef\u9010\u6b65\u626d\u8f6c\u5f62\u52bf\uff0c\u8f6c\u5b88\u4e3a\u653b\u3002\u4f46\u4ed6\u62c5\u5fc3\u4e24\u4ef6\u4e8b\u3002\u4e00\u662f\u7687\u5e1d\u548c\u671d\u4e2d\u5927\u81e3\u5bf9\u4ed6\u4e0d\u4fe1\u4efb\uff0c\u4e8c\u662f\u654c\u4eba\u6311\u62e8\u79bb\u95f4\uff0c\u6563\u5e03\u8c23\u8a00\u3002\u56e0\u6b64\u5728\u4e0a\u4efb\u4e4b\u521d\uff0c\u5bf9\u6b64\u7279\u522b\u5f3a\u8c03\u3002\u4ed6\u58f0\u660e\u5728\u5148\uff0c\u519b\u961f\u4e2d\u5e0c\u5947\u53e4\u602a\u4e4b\u4e8b\u591a\u5f97\u5f88\uff0c\u4e0d\u53ef\u80fd\u4e8b\u4e8b\u90fd\u67e5\u7a76\u660e\u767d\u3002\u4ed6\u53c8\u81ea\u77e5\u6709\u4e00\u80a1\u86ee\u52b2\uff0c\u5e72\u4e8b\u4e0d\u4f9d\u5e38\u89c4\uff0c\u8981\u4ed6\u4e00\u5207\u90fd\u505a\u5f97\u56db\u5e73\u516b\u7a33\uff0c\u9762\u9762\u4ff1\u5706\uff0c\u90a3\u53ef\u4e0d\u884c\u3002\u603b\u800c\u8a00\u4e4b\uff1a\u201c\u6211\u4e0d\u987e\u81ea\u5df1\u6027\u547d\uff0c\u7ed9\u7687\u4e0a\u529e\u6210\u5927\u4e8b\u5c31\u662f\u4e86\uff0c\u5c0f\u4e8b\u60c5\u8bf7\u7687\u4e0a\u4e0d\u5fc5\u7406\u4f1a\u7f62\u3002\u201d \u3000\u3000\u5d07\u796f\u63a5\u5230\u8fd9\u9053\u594f\u7ae0\uff0c\u518d\u52a0\u5956\u52c9\uff0c\u8d50\u4ed6\u87d2\u888d\u3001\u7389\u5e26\u4e0e\u94f6\u5e01\u3002\u8881\u5d07\u7115\u9886\u4e86\u94f6\u5e01\uff0c\u4f46\u4ee5\u672a\u7acb\u529f\u52cb\uff0c\u4e0d\u6562\u53d7\u87d2\u888d\u7389\u5e26\u4e4b\u8d50\uff0c\u4e0a\u758f\u8f9e\u8c22\u4e86\u3002 \u3000\u3000\u5d07\u796f\u8fd9\u6b21\u53ec\u89c1\u8881\u5d07\u7115\uff0c\u5bf9\u4ed6\u8a00\u542c\u8ba1\u4ece\uff0c\u4fe1\u4efb\u4e4b\u4e13\uff0c\u6069\u9047\u4e4b\u9686\uff0c\u5b9e\u662f\u7f55\u89c1\u3002\u4f46\u4e0d\u5e78\u5f97\u5f88\uff0c\u8881\u5d07\u7115\u8fd9\u594f\u7ae0\u4e2d\u6240\u8bf4\u7684\u8bdd\uff0c\u4e00\u53e5\u53e5\u65e0\u4e0d\u6599\u4e2d\uff0c\u7ec8\u4e8e\u88ab\u5904\u6781\u5211\u3002\u8fd9\u4f7f\u6211\u60f3\u8d77\u6587\u5f81\u660e\u7684\u4e00\u9996\u8bcd\u6765\u3002\u4ed6\u89c1\u5230\u5b8b\u9ad8\u5b97\u4eb2\u7b14\u5199\u7ed9\u5cb3\u98de\u7684\u6555\u4e66\uff0c\u4e66\u4e2d\u8a00\u8f9e\u4eb2\u5207\u65e0\u6bd4\uff0c\u6709\u611f\u800c\u4f5c\u4e86\u4e00\u9996\u201c\u6ee1\u6c5f\u7ea2\u201d\uff0c\u5176\u4e2d\u6709\u4e00\u53e5\uff1a\u201c\u6168\u5f53\u521d\u501a\u98de\u4f55\u91cd\uff1f\u540e\u6765\u4f55\u9177\uff1f\u201d\u5d07\u796f\u5bf9\u5f85\u8881\u5d07\u7115\uff0c\u5b9e\u4e5f\u4ee4\u4eba\u6168\u5f53\u521d\u501a\u4e4b\u4f55\u91cd\uff0c\u540e\u6765\u4f55\u9177\u3002 \u3000\u3000\u5176\u95f4\u7684\u5206\u522b\u662f\uff0c\u5cb3\u98de\u5f53\u65f6\u5bf9\u81ea\u5df1\u540e\u6765\u7684\u547d\u8fd0\u5b8c\u5168\u6599\u60f3\u4e0d\u5230\uff0c\u8881\u5d07\u7115\u5374\u662f\u65e9\u5df2\u6599\u5230\u4e86\u7684\u3002\u660e\u77e5\u5c06\u6765\u96be\u514d\u8981\u53d7\u5230\u7687\u5e1d\u731c\u7591\uff0c\u8981\u4e2d\u654c\u4eba\u7684\u79bb\u95f4\u4e4b\u8ba1\uff0c\u5374\u8fd8\u662f\u8981\u53bb\u62c5\u4efb\u8270\u5371\uff0c\u8fd9\u756a\u820d\u8eab\u8d74\u96be\u7684\u5fc3\u60c5\uff0c\u66f4\u4ee4\u540e\u4eba\u6df1\u6df1\u53f9\u606f\u3002 \u3000\u3000\u2460\u9648\u5b50\u58ee\uff1a\u201c\u66fe\u95fb\u7f13\u5e26\u9ad8\u8c08\u65e5\uff0c\u9ec4\u77f3\u5175\u7b79\u5728\u63e1\u5947\u3002\u201d\u6881\u56fd\u680b\uff1a\u201c\u7b11\u501a\u620e\u8f66\u514b\u58ee\u7337\uff0c\u5173\u524d\u6c1b\u7972\u4ed7\u8c01\u6536\uff1f\u5ffb\u770b\u5316\u65e5\u56de\u6625\u65e5\uff0c\u518d\u4e0a\u90a2\u5dde\u62a4\u9526\u5dde\u3002\u201d\u5085\u4e8e\u4eae\uff1a\u201c\u5929\u5c71\u81ea\u6614\u51ed\u4e09\u7bad\uff0c\u8fbd\u5de6\u800c\u4eca\u4ed7\u4e00\u592b\u3002\u79c9\u94ba\u7eb7\u7eb7\u8bba\u5236\u80dc\uff0c\u7b11\u8c08\u5c0a\u4fce\u4f3c\u541b\u65e0\uff1f\u201d\u9093\u6862\uff1a\u201c\u51a0\u52a0\u8350\u89d2\u5ce8\u5e94\u751a\uff0c\u8d50\u6709\u9f99\u6587\u8bb8\u81ea\u4e13\uff08\u6307\u5c1a\u65b9\u5251\uff09\u3002\u501f\u7bb8\u72ec\u5f53\u5929\u4e0b\u8ba1\uff0c\u6298\u51b2\u968f\u8fd0\u638c\u4e2d\u6743\u3002\u201d\u909d\u745e\u9732\uff1a\u201c\u884c\u77e3\u83ab\u5fd8\u9ec4\u77f3\u8bed\uff0c\u9e92\u9e9f\u56de\u9996\u5373\u6c5f\u6e56\u3002\u201d\u201c\u4f9b\u5e10\u591c\u60ac\u5357\u6d77\u6708\uff0c\u8c08\u950b\u6625\u843d\u5927\u6c5f\u6f6e\u3002\u201d\u201c\u8863\u5e03\u5c1a\u601c\u5929\u4e0b\u58eb\uff0c\u9ad8\u6b4c\u8c01\u662f\u773c\u4e2d\u4eba\uff1f\u201d\u909d\u745e\u9732\u5373\u909d\u6e5b\u82e5\uff0c\u5e7f\u4e1c\u540d\u58eb\uff0c\u5357\u6d77\u4eba\uff0c\u540e\u52a9\u5b88\u5e7f\u5dde\uff0c\u6e05\u5175\u7834\u57ce\u65f6\u4e0d\u5c48\u800c\u6b7b\u3002 \u3000\u3000\u2461\u8fd1\u4eba\u53f6\u606d\u7ef0\u9898\u8881\u5d07\u7115\u5893\u6709\u53e5\u4e91\uff1a\u201c\u6e38\u4ed9\u9ec4\u77f3\u7a7a\u4f59\u613f\u201d\u3002\u81ea\u6ce8\uff1a\u201c\u8881\u518d\u8d77\u7763\u5e08\uff0c\u8bf8\u53cb\u996f\u522b\u8bd7\u591a\u4ee5\u9ec4\u77f3\u3001\u8d64\u677e\u4e3a\u8a00\uff0c\u7591\u6709\u6240\u8bbd\uff0c\u60dc\u8881\u4e0d\u609f\u3002\u201d\u5176\u5b9e\u4e0d\u662f\u8881\u5d07\u7115\u4e0d\u609f\uff1b\u5f20\u826f\u662f\u529f\u6210\u8eab\u9000\u800c\u4ece\u8d64\u677e\u5b50\u6e38\uff0c\u8881\u5d07\u7115\u6839\u672c\u6ca1\u6709\u673a\u4f1a\u201c\u529f\u6210\u201d\uff0c\u81ea\u7136\u8c08\u4e0d\u4e0a\u201c\u8eab\u9000\u201d\u3002\u4e0d\u8fc7\u4ee5\u4ed6\u7684\u70ed\u8840\u70ed\u80a0\uff0c\u5373\u4f7f\u662f\u529f\u6210\u4e86\uff0c\u591a\u534a\u8fd8\u662f\u4e0d\u80af\u8eab\u9000\u7684\uff0c\u52bf\u5fc5\u662f\u97a0\u8eac\u5c3d\u7601\uff0c\u6b7b\u800c\u540e\u5df2\u3002 \u3000\u3000\u2462\u5bf9\u5d07\u796f\u672c\u5e94\u79f0\u6731\u7531\u68c0\u3001\u601d\u5b97\u3001\u5e84\u70c8\u5e1d\u3001\u6000\u5b97\u3001\u6bc5\u5b97\uff0c\u6216\u5d07\u796f\u7687\u5e1d\u3002\u672c\u6587\u4ee5\u4ed6\u5e74\u53f7\u79f0\u547c\uff0c\u662f\u4e60\u60ef\u4e0a\u7684\u901a\u4fd7\u65b9\u5f0f\uff0c\u6709\u5982\u79f0\u6e05\u5723\u7956\u4e3a\u5eb7\u7199\u3001\u6e05\u9ad8\u5b97\u4e3a\u4e7e\u9686\u3002 \u3000\u3000\u2463\u5d07\u796f\u53ec\u89c1\u8881\u5d07\u7115\u7684\u60c5\u5f62\u4e0e\u5bf9\u8bdd\uff0c\u6839\u636e\u674e\u900a\u4e4b\u6240\u8457\u300a\u4e09\u671d\u91ce\u8bb0\u300b\u4e0e\u6587\u79c9\u6240\u8457\u300a\u70c8\u7687\u5c0f\u8bc6\u300b\u4e24\u4e66\uff0c\u5176\u540e\u5468\u5ef6\u5112\u5bf9\u8881\u5d07\u7115\u7684\u4e2d\u4f24\uff0c\u4e5f\u6839\u636e\u8fd9\u4e24\u4e66\u6240\u8f7d\u3002\u674e\u900a\u4e4b\u7684\u7236\u4eb2\u674e\u5e94\u7657\u662f\u53cd\u5bf9\u9b4f\u5fe0\u8d24\u800c\u88ab\u5bb3\u6b7b\u7684\u8457\u540d\u5fe0\u81e3\u674e\u5fe0\u6bc5\u516c\u3002\u6587\u79c9\u662f\u6587\u5f81\u660e\u7684\u7384\u5b59\uff0c\u4ed6\u7236\u4eb2\u6587\u9707\u5b5f\u5728\u5d07\u796f\u65f6\u4efb\u5927\u5b66\u58eb\u3002\u6587\u9707\u5b5f\u6700\u51fa\u540d\u7684\u4e8b\uff0c\u662f\u5728\u5929\u542f\u5e74\u95f4\u4e0a\u594f\uff0c\u76f4\u6307\u7687\u5e1d\u8bf8\u4e8b\u4e0d\u7406\uff0c\u72b9\u5982\u201c\u5080\u5121\u767b\u573a\u201d\uff0c\u671d\u653f\u5168\u7531\u9b4f\u5fe0\u8d24\u6446\u5e03\u3002\u9b4f\u5fe0\u8d24\u4e8e\u662f\u53eb\u4e86\u4e00\u73ed\u5080\u5121\u620f\uff0c\u5230\u5bab\u4e2d\u6f14\u7ed9\u71b9\u5b97\u770b\uff0c\u71b9\u5b97\u770b\u5f97\u5927\u4e50\u3002\u9b4f\u5fe0\u8d24\u4fbf\u8bf4\uff1a\u201c\u6587\u9707\u5b5f\u8bf4\u7687\u4e0a\u662f\u5080\u5121\u767b\u573a\uff0c\u90a3\u5c31\u662f\u8fd9\u6837\u5b50\u4e86\u3002\u201d\u71b9\u5b97\u5f53\u7136\u5927\u6012\uff0c\u5c06\u6587\u9707\u5b5f\u5728\u671d\u5ef7\u4e0a\u6253\u4e86\u516b\u5341\u68cd\u3002\u674e\u900a\u4e4b\u548c\u6587\u79c9\u4e8c\u4eba\u662f\u540d\u7236\u4e4b\u5b50\uff0c\u4ed6\u4eec\u8bb0\u8f7d\u671d\u4e2d\u5927\u4e8b\uff0c\u5e94\u8be5\u76f8\u5f53\u53ef\u9760\u3002 \u3000\u3000\u2464\u300a\u660e\u53f2\xb7\u8881\u5d07\u7115\u4f20\u300b\u4e2d\u5f15\u8ff0\u4ed6\u7684\u594f\u7ae0\uff1a\u201c\u6062\u590d\u4e4b\u8ba1\uff0c\u4e0d\u5916\u81e3\u6614\u5e74\u2018\u4ee5\u8fbd\u4eba\u5b88\u8fbd\u571f\uff0c\u4ee5\u8fbd\u571f\u517b\u8fbd\u4eba\uff1b\u5b88\u4e3a\u6b63\u7740\uff0c\u6218\u4e3a\u5947\u7740\uff0c\u548c\u4e3a\u65c1\u7740\u2019\u4e4b\u8bf4\u3002\u6cd5\u5728\u6e10\u4e0d\u5728\u9aa4\uff0c\u5728\u5b9e\u4e0d\u5728\u865a\u3002\u6b64\u81e3\u4e0e\u8bf8\u8fb9\u81e3\u6240\u80fd\u4e3a\u3002\u81f3\u7528\u4eba\u4e4b\u4eba\uff0c\u4e0e\u4e3a\u4eba\u7528\u4e4b\u4eba\uff0c\u7686\u81f3\u5c0a\u53f8\u5176\u94a5\u3002\u4f55\u4ee5\u4efb\u800c\u52ff\u8d30\uff0c\u4fe1\u800c\u52ff\u7591\uff1f\u76d6\u9a6d\u8fb9\u81e3\u4e0e\u5ef7\u81e3\u5f02\u3002\u519b\u4e2d\u53ef\u60ca\u53ef\u7591\u8005\u6b8a\u591a\uff0c\u4f46\u5f53\u8bba\u6210\u8d25\u4e4b\u5927\u5c40\uff0c\u4e0d\u5fc5\u6458\u4e00\u8a00\u4e00\u884c\u4e4b\u5fae\u7455\u3002\u4e8b\u4efb\u65e2\u91cd\uff0c\u4e3a\u6028\u5b9e\u591a\uff0c\u8bf8\u6709\u5229\u4e8e\u5c01\u7586\u8005\uff0c\u7686\u4e0d\u5229\u4e8e\u6b64\u8eab\u8005\u4e5f\u3002\u51b5\u56fe\u654c\u4e4b\u6025\uff0c\u654c\u4ea6\u4ece\u800c\u95f4\u4e4b\uff0c\u662f\u4ee5\u4e3a\u8fb9\u81e3\u751a\u96be\u3002\u965b\u4e0b\u7231\u81e3\u77e5\u81e3\uff0c\u81e3\u4f55\u5fc5\u8fc7\u7591\u60e7\uff1f\u4f46\u4e2d\u6709\u6240\u5371\uff0c\u4e0d\u6562\u4e0d\u544a\u3002\u201d \u3000\u3000\u5341 \u3000\u3000\u8881\u5d07\u7115\u8fd8\u6ca1\u6709\u5230\u4efb\uff0c\u5b81\u8fdc\u5df2\u53d1\u751f\u4e86\u5175\u53d8\u3002 \u3000\u3000\u5175\u53d8\u662f\u56e0\u6b20\u9977\u56db\u4e2a\u6708\u800c\u8d77\uff0c\u8d77\u4e8b\u7684\u662f\u56db\u5ddd\u5175\u4e0e\u6e56\u5357\u3001\u6e56\u5317\u7684\u6e56\u5e7f\u5175\u3002\u5175\u5352\u628a\u5de1\u629a\u6bd5\u81ea\u8083\u3001\u603b\u5175\u5b98\u6731\u6885\u7b49\u7f1a\u5728\u8c2f\u697c\u4e0a\u3002\u5175\u5907\u526f\u4f7f\u628a\u5b98\u8859\u5e93\u623f\u4e2d\u6240\u6709\u7684\u4e8c\u4e07\u4e24\u94f6\u5b50\u90fd\u62ff\u51fa\u6765\u53d1\u9977\uff0c\u76f8\u5dee\u8fd8\u662f\u5f88\u591a\uff0c\u53c8\u5411\u5b81\u8fdc\u5546\u6c11\u501f\u4e86\u4e94\u4e07\u4e24\uff0c\u5175\u58eb\u624d\u4e0d\u5435\u4e86\u3002\u6bd5\u81ea\u8083\u81ea\u89c9\u6cbb\u519b\u4e0d\u4e25\u6709\u7f6a\uff0c\u4e0a\u540a\u81ea\u6740\u3002\u5175\u58eb\u7684\u7cae\u9977\u672c\u5c31\u5f88\u5c11\uff0c\u62d6\u6b20\u56db\u4e2a\u6708\uff0c\u53eb\u4ed6\u4eec\u5982\u4f55\u8fc7\u65e5\u5b50\uff1f\u8fd9\u6839\u672c\u662f\u4e2d\u592e\u653f\u5e9c\u8d22\u653f\u90e8\u7684\u4e8b\u3002\u8fde\u5b81\u8fdc\u8fd9\u6837\u7684\u56fd\u9632\u7b2c\u4e00\u8981\u5730\uff0c\u6b20\u9977\u90fd\u8fbe\u56db\u4e2a\u6708\u4e4b\u4e45\uff0c\u53ef\u89c1\u5f53\u65f6\u653f\u6cbb\u7684\u8150\u8d25\u3002\u6bd5\u81ea\u8083\u5728\u4e8c\u6b21\u5b81\u8fdc\u5927\u6218\u65f6\u662f\u5175\u5907\u526f\u4f7f\uff0c\u5b88\u57ce\u6709\u529f\uff0c\u56e0\u5175\u53d8\u800c\u81ea\u6740\uff0c\u5b9e\u5728\u662f\u6b7b\u5f97\u5f88\u51a4\u6789\u7684\u3002 \u3000\u3000\u8881\u5d07\u7115\u4e8e\u516b\u6708\u521d\u5230\u8fbe\uff0c\u60e9\u7f5a\u4e86\u51e0\u540d\u519b\u5b98\uff0c\u5176\u4e2d\u4e4b\u4e00\u662f\u540e\u6765\u5927\u5927\u6709\u540d\u7684\u5de6\u826f\u7389\uff0c\u5f53\u65f6\u662f\u90fd\u53f8\uff1b\u53c8\u6740\u4e86\u77e5\u9053\u5175\u53d8\u9884\u8c0b\u800c\u4e0d\u62a5\u7684\u4e2d\u519b\uff0c\u5c06\u5175\u53d8\u5e73\u5b9a\u4e86\u3002 \u3000\u3000\u4f46\u4eac\u91cc\u7684\u9977\u94f6\u4ecd\u662f\u4e0d\u53d1\u6765\uff0c\u9526\u5dde\u4e0e\u84df\u9547\u7684\u5175\u58eb\u53c8\u54d7\u53d8\u3002\u5982\u679c\u8fd9\u65f6\u6e05\u519b\u6765\u653b\uff0c\u5b81\u8fdc\u4e0e\u9526\u5dde\u600e\u4e48\u5b88\u5f97\u4f4f\uff1f\u5c40\u52bf\u5b9e\u5728\u5371\u9669\u4e4b\u81f3\u3002\u8881\u5d07\u7115\u6709\u751a\u4e48\u6cd5\u5b50\uff1f\u53ea\u6709\u4e0d\u65ad\u7684\u4e0a\u594f\u7ae0\uff0c\u5411\u5317\u4eac\u8bf7\u9977\u3002 \u3000\u3000\u5d07\u796f\u7684\u6027\u683c\u4e4b\u4e2d\uff0c\u4e5f\u6709\u4ed6\u7956\u7236\u795e\u5b97\u7684\u9057\u4f20\u3002\u4ed6\u4e00\u65b9\u9762\u63a5\u53d7\u8d22\u653f\u90e8\u957f\u7684\u63d0\u8bae\uff0c\u589e\u52a0\u8d4b\u7a0e\uff0c\u53e6\u4e00\u65b9\u9762\u5bf9\u4e8e\u4f38\u624b\u6765\u8981\u94b1\u4e4b\u4eba\u5927\u5927\u7684\u4e0d\u9ad8\u5174\u3002 \u3000\u3000\u8881\u5d07\u7115\u5c61\u6b21\u4e0a\u758f\u8bf7\u9977\uff0c\u5d07\u796f\u5bf9\u8bf8\u81e3\u8bf4\uff1a\u201c\u8881\u5d07\u7115\u5728\u6715\u524d\uff0c\u4ee5\u4e94\u5e74\u590d\u8fbd\u3001\u53ca\u6e05\u614e\u4e3a\u5df1\u4efb\uff0c\u8fd9\u7f3a\u9977\u4e8b\uff0c\u987b\u8bb2\u6c42\u957f\u7b56\u3002\u201d\u53c8\u8bf4\uff1a\u201c\u5173\u5175\u52a8\u8f84\u9f13\u566a\uff0c\u541d\u8fb9\u6548\u5c24\uff0c\u5982\u4f55\u5f97\u4e86\uff1f\u201d \u3000\u3000\u793c\u90e8\u53f3\u4f8d\u90ce\u5468\u5ef6\u5112\u594f\u9053\uff1a\u201c\u519b\u58eb\u8981\u631f\uff0c\u4e0d\u5355\u5355\u662f\u4e3a\u4e86\u5c11\u9977\uff0c\u4e00\u5b9a\u53e6\u6709\u9690\u60c5\u3002\u53e4\u4eba\u867d\u7f57\u96c0\u6398\u9f20\uff0c\u800c\u519b\u5fc3\u4e0d\u53d8\u3002\u73b0\u5728\u5404\u5904\u5175\u5352\u4e3a\u751a\u4e48\u52a8\u8f84\u9f13\u566a\uff0c\u5176\u4e2d\u5fc5\u6709\u539f\u6545\u3002\u201d\u5d07\u796f\u9053\uff1a\u201c\u6b63\u5982\u6b64\u8bf4\u3002\u53e4\u4eba\u5c1a\u6709\u7f57\u96c0\u6398\u9f20\u7684\u3002\u4eca\u867d\u7f3a\u9977\uff0c\u54ea\u91cc\u53c8\u4f1a\u5230\u8fd9\u5730\u6b65\u5462\uff1f\u201d \u3000\u3000\u201c\u7f57\u96c0\u6398\u9f20\u201d\u8fd9\u56db\u5b57\u5d07\u796f\u542c\u5f97\u5341\u5206\u5165\u8033\u3002\u5468\u5ef6\u5112\u7531\u4e8e\u8fd9\u56db\u4e2a\u5b57\uff0c\u5411\u7740\u9996\u8f85\u7684\u4f4d\u5b50\u8fc8\u8fdb\u4e86\u4e00\u6b65\u3002\u5468\u5ef6\u5112\u662f\u6c5f\u82cf\u5b9c\u5174\u4eba\uff0c\u76f8\u8c8c\u5341\u5206\u6f02\u4eae\uff0c\u4e8c\u5341\u5c81\u8fde\u4e2d\u4f1a\u5143\u72b6\u5143\uff0c\u8fd9\u4e2a\u6c5f\u5357\u624d\u5b50\u5c0f\u767d\u8138\uff0c\u771f\u662f\u5c0f\u8bf4\u4e0e\u620f\u5267\u4e2d\u7684\u6807\u51c6\u5c0f\u751f\uff0c\u53ef\u60dc\u4eba\u54c1\u592a\u5dee\uff0c\u5728\u300a\u660e\u53f2\u300b\u4e2d\u88ab\u5217\u5165\u300a\u5978\u81e3\u4f20\u300b\u3002\u672c\u6765\u8fd9\u4eba\u4e5f\u4e0d\u7b97\u771f\u7684\u5341\u5206\u5978\u6076\uff0c\u4ed6\u540e\u6765\u505a\u9996\u8f85\uff0c\u4e5f\u505a\u4e86\u4e9b\u597d\u4e8b\u7684\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4e8b\u4e8b\u8fce\u5408\u5d07\u796f\u7684\u5fc3\u610f\u3002\u5468\u5ef6\u5112\u4e4b\u5978\uff0c\u4e3b\u8981\u662f\u5d07\u796f\u6027\u683c\u7684\u53cd\u6620\u3002\u4f46\u201c\u9022\u4e3b\u4e4b\u6076\u201d\u5f53\u7136\u4e5f\u5c31\u662f\u5978\u3002\u8fd9\u4e2a\u4eba\u548c\u8881\u5d07\u7115\u6070\u662f\u4e24\u4e2a\u6781\u7aef\u3002\u8881\u5d07\u7115\u8003\u8fdb\u58eb\u8003\u4e86\u8bb8\u591a\u6b21\u624d\u53d6\uff0c\u76f8\u8c8c\u76f8\u5f53\u4e0d\u6f02\u4eae\u2460\uff0c\u6027\u683c\u5219\u662f\u5341\u5206\u7684\u9ca0\u76f4\u521a\u5f3a\u3002 \u3000\u3000\u201c\u7f57\u96c0\u6398\u9f20\u201d\u662f\u5510\u5f20\u5de1\u7684\u5178\u6545\u3002\u5f20\u5de1\u5728\u7762\u9633\u88ab\u5b89\u7984\u5c71\u56f4\u56f0\uff0c\u82e6\u5b88\u65e5\u4e45\uff0c\u519b\u4e2d\u65e0\u98df\uff0c\u53ea\u5f97\u5f20\u7f51\u6349\u96c0\u3001\u6398\u7a74\u6355\u9f20\u6765\u5145\u9965\uff0c\u4f46\u4ecd\u662f\u6b7b\u5b88\u4e0d\u5c48\u3002\u7f57\u96c0\u6398\u9f20\u662f\u4e0d\u5f97\u5df2\u65f6\u7684\u82e6\u6cd5\u5b50\uff0c\u53d7\u5230\u654c\u4eba\u5305\u56f4\uff0c\u53ea\u5f97\u82e6\u6328\uff0c\u4f46\u600e\u80fd\u671f\u671b\u5175\u58eb\u5728\u5e73\u65f6\u90fd\u6709\u8fd9\u79cd\u7cbe\u795e\uff1f \u3000\u3000\u5468\u5ef6\u5112\u4e58\u673a\u4e2d\u4f24\uff0c\u5d07\u796f\u5728\u8fd9\u65f6\u5df2\u5f00\u59cb\u5bf9\u8881\u5d07\u7115\u4fe1\u5fc3\u52a8\u6447\u3002\u4ed6\u63d0\u5230\u8881\u5d07\u7115\u4ee5\u201c\u6e05\u614e\u4e3a\u5df1\u4efb\u201d\uff0c\u4f3c\u4e4e\u5bf9\u4ed6\u7684\u201c\u6e05\u201d\u4e5f\u6709\u4e86\u6000\u7591\u3002\u5d07\u796f\u5fc3\u4e2d\u4f3c\u4e4e\u8fd9\u6837\u60f3\uff1a\u201c\u4ed6\u81ea\u79f0\u662f\u6e05\u5b98\uff0c\u4e3a\u751a\u4e48\u5374\u4e0d\u65ad\u7684\u5411\u6211\u8981\u94b1\uff1f\u201d \u3000\u3000\u8881\u5d07\u7115\u53c8\u5230\u9526\u5dde\u53bb\u5b89\u629a\u5175\u53d8\uff0c\u8fde\u758f\u8bf7\u9977\u3002\u5341\u6708\u521d\u4e8c\uff0c\u5d07\u796f\u5728\u6587\u534e\u6bbf\u96c6\u7fa4\u81e3\u5546\u8bae\uff0c\u8bf4\u9053\uff1a\u201c\u5d07\u7115\u5148\u524d\u8bf4\u9053\u2018\u5b89\u629a\u9526\u5dde\uff0c\u5175\u53d8\u53ef\u5f25\u2019\uff0c\u73b0\u5728\u5374\u8bf4\u2018\u519b\u6b32\u9f13\u566a\uff0c\u6c42\u53d1\u5185\u5e11\u2019\uff0c\u4e3a\u751a\u4e48\u4e0e\u524d\u758f\u8fd9\u6837\u77db\u76fe\uff1f\u537f\u7b49\u594f\u6765\u3002\u201d \u3000\u3000\u201c\u5185\u5e11\u201d\u662f\u7687\u5e1d\u79c1\u5bb6\u5e93\u623f\u7684\u94b1\u3002\u56e0\u4e3a\u6237\u90e8\u7b54\u590d\u8881\u5d07\u7115\u8bf4\uff0c\u56fd\u5e93\u91cc\u5b9e\u5728\u6ca1\u6709\u94b1\uff0c\u6240\u4ee5\u8881\u5d07\u7115\u8bf7\u7687\u5e1d\u638f\u79c1\u4eba\u8170\u5305\u6765\u53d1\u6b20\u9977\u3002\u518d\u52a0\u4e0a\u8bf4\u5175\u58eb\u9f13\u566a\u800c\u63d0\u51fa\u8981\u6c42\uff0c\u4f3c\u4e4e\u9690\u542b\u5a01\u80c1\uff0c\u5d07\u796f\u81ea\u7136\u66f4\u52a0\u751f\u6c14\u3002 \u3000\u3000\u54ea\u77e5\u767e\u5b98\u4f17\u53e3\u4e00\u8f9e\uff0c\u90fd\u8bf7\u7687\u4e0a\u53d1\u5185\u5e11\u3002\u65b0\u4efb\u7684\u6237\u90e8\u5c1a\u4e66\u6781\u8a00\u6237\u90e8\u65e0\u94b1\uff0c\u53ea\u6709\u9646\u7eed\u7b79\u63aa\u53d1\u7ed9\u3002\u5d07\u796f\u8bf4\uff1a\u201c\u5c06\u5175\u8005\u679c\u80fd\u5f85\u90e8\u5c5e\u5982\u5bb6\u4eba\u7236\u5b50\uff0c\u5175\u5352\u81ea\u4e0d\u6562\u53db\uff0c\u4e0d\u5fcd\u53db\uff1b\u4e0d\u6562\u53db\u8005\u754f\u5176\u5a01\uff0c\u4e0d\u5fcd\u53db\u8005\u6000\u5176\u5fb7\uff0c\u5982\u4f55\u6709\u9f13\u566a\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u201c\u7f57\u96c0\u6398\u9f20\u201d\u548c\u201c\u5bb6\u4eba\u7236\u5b50\u201d\u8fd9\u4e24\u53e5\u8bdd\uff0c\u5145\u5206\u8868\u73b0\u4e86\u5d07\u796f\u5b8c\u5168\u4e0d\u987e\u65c1\u4eba\u6b7b\u6d3b\u7684\u81ea\u79c1\u6027\u683c\u3002\u5175\u58eb\u6709\u56db\u4e2a\u6708\u9886\u4e0d\u5230\u7cae\u9977\uff0c\u5435\u4e86\u8d77\u6765\u3002\u5d07\u796f\u4e0d\u602a\u81ea\u5df1\u4e0d\u53d1\u9977\uff0c\u5374\u602a\u5e26\u5175\u7684\u5c06\u5e05\u5bf9\u5f85\u58eb\u5175\u7684\u6001\u5ea6\u4e0d\u5982\u5bb6\u4eba\u7236\u5b50\u3002\u4ed6\u4f3c\u4e4e\u8ba4\u4e3a\uff0c\u4e3b\u5e05\u82e5\u80fd\u5f85\u58eb\u5175\u5982\u5bb6\u4eba\u7236\u5b50\uff0c\u6ca1\u6709\u7cae\u9977\uff0c\u58eb\u5175\u997f\u6b7b\u4e5f\u662f\u4e0d\u4f1a\u5435\u7684\u3002\u4fd7\u8bed\u90fd\u8bf4\uff1a\u201c\u7687\u5e1d\u4e0d\u5dee\u997f\u5175\u3002\u201d\u5d07\u796f\u5374\u8ba4\u4e3a\u997f\u5175\u53ef\u4ee5\u81ea\u5df1\u6349\u8001\u9f20\u5403\u3002 \u3000\u3000\u5468\u5ef6\u5112\u63e3\u6469\u5230\u4e86\u5d07\u796f\u5fc3\u610f\uff0c\u53c8\u4e58\u673a\u4e2d\u4f24\uff0c\u8bf4\u9053\uff1a\u201c\u81e3\u4e0d\u6562\u963b\u6b62\u7687\u4e0a\u53d1\u5185\u5e11\u3002\u73b0\u5728\u5b89\u5371\u5728\u547c\u5438\u4e4b\u95f4\uff0c\u6025\u5219\u6cbb\u6807\uff0c\u53ea\u597d\u53d1\u7ed9\u4ed6\u3002\u7136\u800c\u51b3\u975e\u957f\u7b56\uff0c\u8fd8\u8bf7\u7687\u4e0a\u4e0e\u5ef7\u81e3\u5b9a\u4e00\u7ecf\u4e45\u7684\u65b9\u7b56\u3002\u201d\u5d07\u796f\u5927\u4e3a\u8d5e\u6210\uff1a\u201c\u6b64\u8bf4\u826f\u662f\u3002\u82e5\u662f\u52a8\u4e0d\u52a8\u5c31\u6765\u8bf7\u53d1\u5185\u5e11\uff0c\u5404\u5904\u8fb9\u9632\u519b\u90fd\u5b66\u6837\uff0c\u8fd9\u5185\u5e11\u5c82\u6709\u4e0d\u5e72\u6db8\u7684\uff1f\u201d\u5d07\u796f\u8d8a\u8bf4\u8d8a\u6012\uff0c\u53c8\u662f\u5fe7\u5f62\u4e8e\u8272\uff0c\u6240\u6709\u5927\u81e3\u4e2a\u4e2a\u5413\u5f97\u6218\u6218\u5162\u5162\uff0c\u8c01\u4e5f\u4e0d\u6562\u8bf4\u8bdd\u2461\u3002 \u3000\u3000\u8881\u5d07\u7115\u8bf7\u53d1\u5185\u5e11\uff0c\u5176\u5b9e\u6b63\u662f\u4ed6\u4e0d\u7231\u60dc\u81ea\u5df1\u3001\u4e0d\u6015\u5f00\u7f6a\u7687\u5e1d\u3001\u800c\u5f85\u58eb\u5175\u5982\u5bb6\u4eba\u7236\u5b50\u3002\u672c\u6765\uff0c\u4ed6\u53ea\u987b\u7533\u8bf7\u53d1\u9977\uff0c\u81f3\u4e8e\u94b1\u4ece\u4f55\u5904\u6765\uff0c\u6839\u672c\u4e0d\u662f\u4ed6\u7684\u8d23\u4efb\u3002\u56fd\u5e93\u65e0\u94b1\uff0c\u81ea\u6709\u522b\u7684\u5927\u81e3\u4f1a\u63d0\u51fa\u8bf7\u53d1\u5185\u5e11\uff0c\u5d07\u796f\u618e\u6068\u7684\u5bf9\u8c61\u5c31\u4f1a\u662f\u90a3\u4e2a\u8bf7\u53d1\u5185\u5e11\u4e4b\u4eba\u3002\u4ee5\u8881\u5d07\u7115\u7684\u624d\u667a\uff0c\u51b3\u4e0d\u4f1a\u4e0d\u660e\u767d\u5176\u4e2d\u7684\u5173\u952e\uff0c\u4f46\u4ed6\u7231\u60dc\u5175\u58eb\uff0c\u5f97\u7f6a\u7687\u5e1d\u4e5f\u4e0d\u7ba1\u4e86\u3002\u8bf4\u4e0d\u5b9a\u671d\u4e2d\u5927\u81e3\u4eba\u4eba\u4e0d\u6562\u5f97\u7f6a\u7687\u5e1d\uff0c\u9977\u94f6\u5c31\u59cb\u7ec8\u53d1\u4e0d\u4e0b\u6765\uff0c\u90a3\u4e48\u5c31\u7531\u6211\u5f00\u53e3\u597d\u4e86\u3002 \u3000\u3000\u5f53\u8881\u5d07\u7115\u7f62\u5b98\u5bb6\u5c45\u4e4b\u65f6\uff0c\u7687\u592a\u6781\u89c1\u52b2\u654c\u65e2\u53bb\uff0c\u7acb\u523b\u8086\u65e0\u5fcc\u60ee\uff0c\u4e0d\u518d\u79f0\u6c57\u800c\u6539\u79f0\u7687\u5e1d\u3002 \u3000\u3000\u8881\u5d07\u7115\u56de\u4efb\u4e4b\u540e\uff0c\u5b81\u8fdc\u3001\u9526\u5dde\u3001\u84df\u5dde\u90fd\u56e0\u6b20\u9977\u800c\u53d1\u751f\u5175\u53d8\uff0c\u5f53\u65f6\u81ea\u7136\u4e0d\u80fd\u4e0e\u6e05\u5175\u5f00\u4ed7\uff0c\u4e8e\u662f\u4e0e\u7687\u592a\u6781\u53c8\u5f00\u59cb\u4e86\u548c\u8c08\uff0c\u7528\u4ee5\u62d6\u5ef6\u65f6\u95f4\u3002\u7687\u592a\u6781\u5bf9\u548c\u8c08\u5411\u6765\u6781\u6709\u5174\u8da3\uff0c\u7acb\u5373\u4f5c\u51fa\u6709\u5229\u7684\u53cd\u5e94\u3002\u8881\u5d07\u7115\u63d0\u51fa\u7684\u5148\u51b3\u6761\u4ef6\uff0c\u662f\u8981\u4ed6\u5148\u9664\u53bb\u5e1d\u53f7\uff0c\u6062\u590d\u79f0\u201c\u6c57\u201d\u3002\u7687\u592a\u6781\u5c45\u7136\u7b54\u5141\uff0c\u4f46\u8981\u6c42\u660e\u671d\u7687\u5e1d\u8d50\u4e00\u9897\u5370\u7ed9\u4ed6\uff0c\u8868\u793a\u6b63\u5f0f\u627f\u8ba4\u4ed6\u201c\u6c57\u201d\u7684\u5730\u4f4d\u3002\u8fd9\u662f\u81ea\u5c45\u4e3a\u660e\u671d\u85e9\u90a6\uff0c\u539f\u662f\u5bf9\u660e\u671d\u6781\u6709\u5229\u7684\u3002\u4f46\u660e\u671d\u671d\u5ef7\u4e0d\u4f30\u8ba1\u5f62\u52bf\uff0c\u4e0d\u7814\u7a76\u53cc\u65b9\u529b\u91cf\u7684\u5bf9\u6bd4\uff0c\u575a\u6301\u975e\u6d88\u706d\u6ee1\u6e05\u4e0d\u53ef\uff0c\u5f53\u5373\u62d2\u7edd\u4e86\u8fd9\u4e2a\u8981\u6c42\u2462\u3002 \u3000\u3000\u7687\u592a\u6781\u4e00\u76f4\u5230\u6b7b\uff0c\u59cb\u7ec8\u5343\u65b9\u767e\u8ba1\u7684\u5728\u6c42\u548c\uff0c\u4e0d\u4f46\u81ea\u5df1\u4e0d\u505c\u7684\u5199\u4fe1\u7ed9\u660e\u671d\u8fb9\u754c\u4e0a\u7684\u5b98\u5458\uff0c\u53c8\u6258\u671d\u9c9c\u5c45\u95f4\u65a1\u65cb\uff0c\u8981\u8499\u53e4\u738b\u516c\u4e0a\u4e66\u660e\u671d\u63d0\u51fa\u529d\u544a\u3002\u6bcf\u4e00\u4e2a\u6218\u5f79\u7684\u57fa\u672c\u76ee\u6807\uff0c\u90fd\u662f\u201c\u4ee5\u6218\u6c42\u548c\u201d\u2463\u3002\u4ed6\u6e05\u695a\u7684\u8ba4\u8bc6\u5230\uff0c\u6ee1\u6e05\u51b3\u8ba1\u4e0d\u662f\u660e\u671d\u7684\u654c\u624b\uff0c\u660e\u671d\u7684\u653f\u6cbb\u53ea\u8981\u7a0d\u4e0a\u8f68\u9053\uff0c\u6ee1\u6e05\u5c31\u975e\u4ea1\u56fd\u706d\u79cd\u4e0d\u53ef\u3002\u6ee1\u65cf\u7684\u7ecf\u6d4e\u529b\u91cf\u5f88\u662f\u8584\u5f31\uff0c\u4e0d\u4f1a\u7eba\u7ec7\uff0c\u4e3b\u8981\u7684\u6536\u5165\u662f\u9760\u62a2\u52ab\u2464\u3002\u7687\u592a\u6781\u5199\u7ed9\u5d07\u796f\u7684\u4fe1\uff0c\u53ef\u8bf4\u8c26\u5351\u5230\u4e86\u6781\u70b9\u2465\u3002 \u3000\u3000\u7136\u800c\u5d07\u796f\u7684\u72c2\u5984\u81ea\u5927\u6bd4\u4ed6\u54e5\u54e5\u5929\u542f\u66f4\u5389\u5bb3\u5f97\u591a\uff0c\u5bf9\u6ee1\u6e05\u59cb\u7ec8\u575a\u6301\u201c\u4e0d\u627f\u8ba4\u653f\u7b56\u201d\uff0c\u4e0d\u627f\u8ba4\u5b83\u6709\u72ec\u7acb\u81ea\u4e3b\u7684\u8d44\u683c\uff0c\u51b3\u4e0d\u4e0e\u5b83\u6253\u4efb\u4f55\u4ea4\u9053\u2466\u3002 \u3000\u3000\u4e3a\u4e86\u4e0e\u6ee1\u6e05\u4f5c\u6218\uff0c\u4e07\u5386\u672b\u5e74\u5df2\u52a0\u91cd\u4e86\u5bf9\u6c11\u95f4\u7684\u641c\u62ec\uff0c\u5929\u542f\u65f6\u518d\u52a0\uff0c\u5230\u5d07\u796f\u624b\u91cc\u66f4\u5927\u52a0\u800c\u7279\u52a0\uff0c\u5230\u672b\u5e74\u65f6\u52a0\u6d3e\u8fbd\u9977\u4e5d\u767e\u4e07\u4e24\uff0c\u7ec3\u9977\u4e03\u767e\u4e09\u5341\u4f59\u4e07\u4e24\uff0c\u4e00\u5e74\u4e4b\u4e2d\u5355\u662f\u519b\u8d39\u5c31\u8fbe\u5230\u4e8c\u5343\u4e07\u4e24\uff08\u4e07\u5386\u521d\u5e74\u5168\u56fd\u5c81\u51fa\u4e0d\u8fc7\u56db\u767e\u4e07\u4e24\u5de6\u53f3\uff09\uff0c\u56fd\u5bb6\u8d22\u653f\u548c\u5168\u56fd\u7ecf\u6d4e\u5728\u8fd9\u538b\u529b\u4e0b\u90fd\u5df2\u6fd2\u4e8e\u5d29\u6e83\u3002\u660e\u672b\u6c11\u53d8\u56db\u8d77\uff0c\u4e3b\u8981\u539f\u56e0\u4fbf\u5728\u767e\u59d3\u8d1f\u62c5\u4e0d\u8d77\u8fd9\u6c89\u91cd\u7684\u519b\u8d39\u5f00\u652f\u2467\u3002 \u3000\u3000\u654c\u4eba\u63d0\u51fa\u548c\u5e73\u5efa\u8bae\uff0c\u662f\u4e0d\u662f\u53ef\u4ee5\u63a5\u53d7\uff0c\u4e0d\u80fd\u4e00\u6982\u800c\u8bba\u3002\u6211\u4ee5\u4e3a\u5e94\u5f53\u6839\u636e\u8fd9\u6837\u7684\u539f\u5219\u6765\u52a0\u4ee5\u8003\u8651\uff1a\u654c\u4eba\u7684\u548c\u8bae\u4e0d\u8fc7\u662f\u4e00\u79cd\u9634\u8c0b\u624b\u6bb5\uff0c\u76ee\u7684\u5728\u6574\u4e2a\u706d\u4ea1\u6211\u4eec\uff1f\u8fd8\u662f\u654c\u4eba\u56e0\u7ecf\u6d4e\u3001\u653f\u6cbb\u3001\u519b\u4e8b\u3001\u6216\u793e\u4f1a\u7684\u539f\u56e0\u800c\u786e\u6709\u548c\u5e73\u8bda\u610f\uff1f \u3000\u3000\u5fc5\u987b\u5047\u5b9a\u7f14\u7ed3\u548c\u7ea6\u53ea\u662f\u6682\u65f6\u4f11\u6218\uff0c\u53cc\u65b9\u968f\u65f6\u53ef\u4ee5\u7834\u574f\u548c\u5e73\u800c\u91cd\u542f\u6218\u7aef\u3002\u76ee\u524d\u4e00\u76f4\u6253\u4e0b\u53bb\u5bf9\u6211\u65b9\u6bd4\u8f83\u6709\u5229\uff1f\u8fd8\u662f\u4f11\u6218\u4e00\u6bb5\u65f6\u671f\u518d\u6253\u6bd4\u8f83\u6709\u5229\uff1f \u3000\u3000\u7f14\u7ed3\u548c\u7ea6\u6216\u8fdb\u884c\u548c\u5e73\u8c08\u5224\uff0c\u4f1a\u524a\u5f31\u672c\u56fd\u7684\u58eb\u6c14\u6c11\u5fc3\u3001\u9020\u6210\u793e\u4f1a\u6df7\u4e71\u3001\u635f\u5bb3\u4f5c\u6218\u52aa\u529b\u3001\u7834\u574f\u8054\u76df\u5173\u7cfb\u3001\u5f71\u54cd\u653f\u5e9c\u58f0\u8a89\uff1f\u8fd8\u662f\u5e76\u65e0\u91cd\u5927\u4e0d\u826f\u540e\u679c\uff1f \u3000\u3000\u548c\u7ea6\u6761\u6b3e\u662f\u7247\u9762\u5bf9\u654c\u4eba\u6709\u5229\uff1f\u8fd8\u662f\u53cc\u65b9\u5e73\u7b49\uff0c\u6216\u5229\u5bb3\u53c2\u534a\uff0c\u751a\u81f3\u5bf9\u6211\u65b9\u6709\u5229\uff1f \u3000\u3000\u5982\u679c\u662f\u524d\u8005\uff0c\u5f53\u7136\u5e94\u5f53\u65ad\u7136\u62d2\u7edd\uff1b\u82e5\u662f\u540e\u8005\uff0c\u5c31\u53ef\u8003\u8651\u63a5\u53d7\uff0c\u5fc5\u8981\u65f6\u751a\u81f3\u8fd8\u987b\u52aa\u529b\u4e89\u53d6\u3002\u5728\u5f53\u65f6\u7684\u5c40\u52bf\u4e0b\uff0c\u6210\u7acb\u548c\u8bae\u663e\u7136\u4e8e\u660e\u671d\u6709\u91cd\u5927\u5229\u76ca\u3002\u4e0d\u8bba\u4ece\u653f\u7565\u3001\u6218\u7565\u3001\u7ecf\u6d4e\u3001\u4eba\u6c11\u751f\u6d3b\u54ea\u4e00\u65b9\u9762\u6765\u8003\u8651\uff0c\u90fd\u5e94\u4e0e\u6ee1\u6e05\u8bae\u548c\u3002 \u3000\u3000\u62d2\u7edd\u548c\u6ee1\u6e05\u8bae\u548c\uff0c\u662f\u5d07\u796f\u4e00\u751f\u6700\u5927\u7684\u611a\u8822\u3002\u4ed6\u521d\u5373\u4f4d\u65f6\u6e05\u9664\u9b4f\u5fe0\u8d24\u9006\u515a\uff0c\u5904\u7406\u5f97\u5341\u5206\u7cbe\u660e\uff0c\u4e8e\u662f\u81e3\u4e0b\u5927\u6367\u4ed6\u4e3a\u201c\u82f1\u4e3b\u201d\u3002\u4ed6\u4ece\u6b64\u5c31\u98d8\u98d8\u7136\u4e86\uff0c\u771f\u7684\u4ee5\u201c\u82f1\u4e3b\u201d\u81ea\u5c45\uff0c\u8ba4\u4e3a\u201c\u82f1\u4e3b\u201d\u51b3\u4e0d\u80fd\u548c\u53db\u9006\u7684\u201c\u5efa\u5dde\u536b\u201d\u59a5\u534f\u3002\u5728\u660e\u671d\u541b\u81e3\u7684\u89c2\u5ff5\u4e2d\uff0c\u201c\u5efa\u5dde\u536b\u201d\u59cb\u7ec8\u662f\u4e2d\u56fd\u7687\u5e1d\u5c5e\u4e0b\u4e00\u4e2a\u5c0f\u5b98\u7684\u9886\u5730\uff0c\u7687\u5e1d\u51b3\u4e0d\u80fd\u8ddf\u5c0f\u5b98\u8c08\u548c\u3002\u81f3\u4e8e\u4f7f\u5f97\u5168\u56fd\u4ebf\u4e07\u4eba\u6c11\u6d3b\u4e0d\u4e0b\u53bb\uff0c\u90a3\u662f\u53e6\u4e00\u56de\u4e8b\uff0c\u7687\u5e1d\u7684\u5c0a\u4e25\u4e0d\u80fd\u6709\u4e1d\u6beb\u635f\u5bb3\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u548c\u5bdf\u54c8\u5c14\u8499\u53e4\u4eba\u8c08\u548c\uff0c\u4ed8\u7ed9\u91d1\u94f6\u4ee5\u6362\u53d6\u548c\u5e73\u3002\u56e0\u4e3a\u660e\u671d\u7684\u6c5f\u5c71\u662f\u4ece\u8499\u53e4\u4eba\u624b\u91cc\u593a\u6765\u7684\uff0c\u660e\u671d\u627f\u8ba4\u8499\u53e4\u662f\u654c\u56fd\u3002 \u3000\u3000\u575a\u6301\u653f\u6cbb\u539f\u5219\uff0c\u672c\u6765\u4e0d\u9519\u3002\u7136\u800c\u653f\u6cbb\u539f\u5219\u662f\u8981\u4ee5\u6b63\u786e\u7684\u7b56\u7565\u6765\u8d2f\u5f7b\u7684\u3002\u5b8c\u5168\u5ffd\u89c6\u5b9e\u9645\u60c5\u5f62\uff0c\u628a\u56fd\u5bb6\u4e0e\u4eba\u6c11\u7684\u751f\u6b7b\u5b58\u4ea1\u7f6e\u4e4b\u4e0d\u987e\uff0c\u548c\u201c\u82f1\u4e3b\u201d\u4e24\u5b57\u53ef\u76f8\u5dee\u5341\u4e07\u516b\u5343\u91cc\u4e86\u3002 \u3000\u3000\u8881\u5d07\u7115\u548c\u7687\u592a\u6781\u4e00\u756a\u4ea4\u6d89\uff0c\u4f7f\u5f97\u7687\u592a\u6781\u81ea\u52a8\u9664\u53bb\u4e86\u5e1d\u53f7\uff0c\u672c\u6765\u662f\u5916\u4ea4\u4e0a\u7684\u91cd\u5927\u80dc\u5229\u3002\u4f46\u5d07\u796f\u5374\u8ba4\u4e3a\u662f\u548c\u201c\u53db\u5f92\u201d\u79c1\u81ea\u8bae\u548c\uff0c\u6709\u8fb1\u56fd\u4f53\uff0c\u5fc3\u4e2d\u6781\u4e0d\u6ee1\u610f\uff0c\u5f53\u65f6\u5bf9\u8881\u5d07\u7115\u501a\u8d56\u5f88\u91cd\uff0c\u9690\u5fcd\u4e0d\u53d1\uff0c\u540e\u6765\u5374\u7ec8\u4e8e\u6210\u4e3a\u6740\u4ed6\u7684\u4e3b\u8981\u7f6a\u72b6\u3002 \u3000\u3000\u2460\u300a\u660e\u53f2\xb7\u94b1\u9f99\u9521\u4f20\u300b\uff1a\u201c\u9f99\u9521\u594f\u8fa9\uff0c\u8a00\uff1a\u2018\u5d07\u7115\u965b\u89c1\u65f6\uff0c\u81e3\u89c1\u5176\u8c8c\u5bdd\uff0c\u9000\u8c13\u540c\u5b98\uff1a\u6b64\u4eba\u6050\u4e0d\u80dc\u4efb\u3002\u2019\u201d\u94b1\u9f99\u9521\u8fd9\u8bdd\u4e5f\u662f\u80e1\u8bf4\u516b\u9053\uff0c\u600e\u80fd\u89c1\u4eba\u5bb6\u76f8\u8c8c\u96be\u770b\uff0c\u4fbf\u8bf4\u4ed6\u4e0d\u80fd\u62c5\u5f53\u5927\u4e8b\uff1f \u3000\u3000\u2461\u300a\u70c8\u7687\u5c0f\u8bc6\u300b\uff1a\u201c\u65f6\u5929\u5a01\u9707\u8fc5\uff0c\u5fe7\u5f62\u4e8e\u8272\u3002\u5927\u5c0f\u81e3\u5de5\u7686\u6218\u60e7\u4e0d\u80fd\u4ef0\u5bf9\uff0c\u800c\u5ef6\u5112\u7531\u6b64\u8377\u5723\u7737\u77e3\u3002\u201d \u3000\u3000\u2462\u5173\u4e8e\u8fd9\u573a\u4ea4\u6d89\uff0c\u56e0\u7687\u592a\u6781\u79f0\u5e1d\u4e4b\u540e\u518d\u81ea\u52a8\u9664\u53bb\uff0c\u53c8\u5411\u660e\u671d\u8981\u6c42\u53d1\u5370\u800c\u4e0d\u5f97\uff0c\u5728\u6ee1\u6e05\u65b9\u9762\u662f\u53d7\u5230\u91cd\u5927\u5c48\u8fb1\uff0c\u6240\u4ee5\u6e05\u65b9\u5b98\u6587\u4e66\u4e2d\u90fd\u65e0\u8bb0\u8f7d\uff0c\u6216\u6709\u8bb0\u8f7d\u800c\u540e\u6765\u90fd\u5220\u53bb\u4e86\u3002\u4f46\u6e05\u5185\u9601\u6863\u6848\u4e2d\u8fd8\u7559\u5b58\u7687\u592a\u6781\u5929\u806a\u56db\u5e74\u9881\u793a\u7684\u4e00\u9053\u6728\u520a\u8c15\u6587\uff0c\u5176\u4e2d\u516c\u5f00\u627f\u8ba4\u8fd9\u4ef6\u4e8b\uff1a\u201c\u902e\u81f3\u6715\u8eac\uff0c\u5b9e\u6b32\u7f62\u5175\u6208\uff0c\u4eab\u592a\u5e73\uff0c\u6545\u5c61\u5c61\u5dee\u4eba\u8bb2\u8bf4\u3002\u65e0\u5948\u5929\u542f\u3001\u5d07\u796f\u4e8c\u5e1d\u6e3a\u6211\u76ca\u751a\uff0c\u903c\u4ee4\u9000\u5730\uff0c\u4e14\u6559\u524a\u53bb\u5e1d\uff08\u53f7\uff09\uff0c\u53ca\u7981\u7528\u56fd\u5b9d\u3002\u6715\u4ee5\u4e3a\u5929\u4e0e\u571f\u5730\uff0c\u4f55\u6562\u8f7b\u4e0e\uff1f\u5176\u5e1d\u53f7\u56fd\u5b9d\uff0c\u4e00\u4e00\u9075\u4f9d\uff0c\u6613\u6c57\u8bf7\u5370\uff0c\u59d4\u66f2\u81f3\u6b64\uff0c\u4ecd\u590d\u4e0d\u5141\u3002\u201d \u3000\u3000\u2463\u300a\u660e\u6e05\u53f2\u6599\u300b\u4e19\u7f16\uff0c\u7687\u592a\u6781\u8c15\u8bf8\u5c06\u58eb\uff1a\u201c\u5c14\u8bf8\u5c06\u58eb\u4e34\u9635\uff0c\u5404\u81ea\u594b\u52c7\u524d\u5f80\uff0c\u4f55\u5fc5\u4e89\u53d6\u8863\u7269\uff1f\u7eb5\u5f97\u4e9b\u7834\u574f\u8863\u7269\uff0c\u5c1a\u4e0d\u80fd\u8d44\u4e00\u5e74\u4e4b\u7528\u3002\u5c14\u5c06\u58eb\u5982\u679c\u594b\u52c7\u76f4\u524d\uff0c\u654c\u4eba\u529b\u4e0d\u80fd\u652f\uff0c\u975e\u4e0e\u6211\u56fd\u8bb2\u548c\uff0c\u5fc5\u662f\u8d25\u4e8e\u6211\u4eec\u3002\u90a3\u65f6\u7a7f\u5403\u81ea\u7136\u957f\u8fdc\uff0c\u65e9\u65e9\u89e3\u76d4\u5378\u7532\uff0c\u5171\u4eab\u592a\u5e73\uff0c\u5c82\u4e0d\u7f8e\u54c9\uff1f\u201d \u3000\u3000\u2464\u300a\u5929\u806a\u5b9e\u5f55\u7a3f\u300b\uff0c\u4e03\u5e74\u4e5d\u6708\u5341\u56db\u65e5\uff0c\u6e05\u592a\u5b97\u81f4\u671d\u9c9c\u56fd\u738b\u4fe1\uff1a\u201c\u8d35\u56fd\u65ad\u5e02\uff0c\u4e0d\u8fc7\u4ee5\u6211\u56fd\u65e0\u8863\uff0c\u56e0\u6b32\u56f0\u6211\u3002\u6211\u4e0e\u8d35\u56fd\u672a\u5e02\u4e4b\u524d\uff0c\u5c82\u66fe\u8d64\u8eab\u88f8\u4f53\u8036\uff1f\u5373\u98de\u79bd\u8d70\u517d\uff0c\u4ea6\u81ea\u5404\u6709\u7fbd\u6bdb\u2026\u2026\u6ee1\u6d32\u3001\u8499\u53e4\u56fa\u4ee5\u62a2\u63a0\u4e3a\u751f\uff0c\u8d35\u56fd\u56fa\u4ee5\u81ea\u5b88\u4e3a\u7d20\u3002\u201d \u3000\u3000\u2465\u300a\u5929\u806a\u5b9e\u5f55\u7a3f\u300b\u516d\u5e74\u516d\u6708\uff0c\u6e05\u592a\u5b97\u81f4\u5d07\u796f\u7687\u5e1d\u4fe1\uff1a\u201c\u6ee1\u6d32\u56fd\u6c57\u8c28\u594f\u5927\u660e\u56fd\u7687\u5e1d\uff1a\u5c0f\u56fd\u8d77\u5175\uff0c\u539f\u975e\u81ea\u4e0d\u77e5\u8db3\uff0c\u5e0c\u56fe\u5927\u4f4d\uff0c\u800c\u8d77\u6b64\u5ff5\u4e5f\u3002\u53ea\u56e0\u8fb9\u5b98\u4f5c\u8df5\u592a\u751a\uff0c\u5c0f\u56fd\u607c\u6068\uff0c\u53c8\u4e0d\u5f97\u4e0a\u8fbe\u2026\u2026\u4eca\u6b32\u5c06\u607c\u6068\u5907\u6089\u4e0a\u95fb\uff0c\u53c8\u6050\u4ee5\u4e3a\u5c0f\u56fd\u4e0d\u89e3\u65e7\u6028\uff0c\u56e0\u800c\u751f\u7591\uff0c\u6240\u4ee5\u4e0d\u6562\u8be6\u9648\u4e5f\u3002\u5c0f\u56fd\u4e0b\u60c5\uff0c\u7687\u4e0a\u82e5\u6b32\u5782\u542c\uff0c\u5dee\u4e00\u597d\u4eba\u6765\uff0c\u4ffe\u5c0f\u56fd\u5c3d\u4e3a\u7533\u594f\u3002\u82e5\u8c13\u4e1a\u5df2\u8bb2\u548c\uff0c\u4f55\u5fc5\u53c8\u63d0\u607c\u6068\uff0c\u60df\u4efb\u7687\u5e1d\u4e4b\u547d\u800c\u5df2\u3002\u592b\u5c0f\u56fd\u4e4b\u4eba\uff0c\u548c\u597d\u544a\u6210\u65f6\uff0c\u5f97\u4e9b\u8d22\u7269\uff0c\u6253\u730e\u653e\u9e70\uff0c\u4fbf\u662f\u5feb\u4e50\u5904\u3002\u8c28\u594f\u3002\u201d\u6700\u540e\u8fd9\u53e5\u8bdd\u751a\u662f\u8d28\u6734\u52a8\u4eba\u3002 \u3000\u3000\u2466\u5d07\u796f\u4e94\u5e74\uff0c\u5ba3\u5e9c\u5de1\u629a\u6c88\u68e8\u548c\u6e05\u519b\u7acb\u7ea6\u4e92\u4e0d\u4fb5\u72af\uff0c\u5d07\u796f\u4fbf\u628a\u5175\u90e8\u5c1a\u4e66\u718a\u660e\u9047\u9769\u804c\u67e5\u529e\uff0c\u6c88\u68e8\u4e0b\u72f1\u3002\u6b64\u540e\u4ed6\u66f4\u4e0b\u65e8\u7ed9\u5b88\u8fb9\u7684\u5b98\u5458\uff0c\u4efb\u4f55\u4eba\u4e0d\u5f97\u4e0e\u6e80\u6e05\u6709\u7247\u7eb8\u53ea\u5b57\u7684\u4ea4\u901a\u3002 \u3000\u3000\u2467\u300a\u660e\u53f2\xb7\u98df\u8d27\u5fd7\u300b\uff1a\u201c\u81ea\u53e4\u6709\u4e00\u5e74\u800c\u62ec\u4e8c\u5343\u4e07\u4ee5\u8f93\u4eac\u5e08\uff0c\u53c8\u62ec\u4eac\u5e08\u4e8c\u5343\u4e07\u4ee5\u8f93\u8fb9\u4e4e\uff1f\u201d \u3000\u3000\u5341\u4e00 \u3000\u3000\u5d07\u796f\u5bf9\u8881\u5d07\u7115\u7684\u731c\u5fcc\uff0c\u4ece\u201c\u8bf7\u53d1\u5185\u5e11\u4e8b\u4ef6\u201d\u5f00\u59cb\u3002\u5e26\u5175\u7684\u7edf\u5e05\u8ffd\u8ba8\u6b20\u9977\uff0c\u672c\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\uff0c\u4f46\u503a\u6237\u5bf9\u4e8e\u503a\u4e3b\u8ffd\u8ba8\u6b20\u6b3e\uff0c\u4e0d\u8bba\u503a\u4e3b\u7684\u7406\u7531\u5982\u4f55\u5145\u8db3\uff0c\u503a\u6237\u81ea\u7136\u800c\u7136\u7684\u4f1a\u5bf9\u4ed6\u5341\u5206\u618e\u6068\uff0c\u5982\u679c\u503a\u4e3b\u5a01\u540d\u9707\u4e8e\u5929\u4e0b\u800c\u53c8\u63e1\u6709\u6b66\u529b\uff0c\u5341\u51e0\u5c81\u7684\u5c11\u5e74\u503a\u6237\u9664\u4e86\u618e\u6068\u4e4b\u5916\u8fd8\u4f1a\u6050\u60e7\u3002\u5d07\u796f\u53c8\u4e0d\u6562\u60e9\u7f5a\u8881\u5d07\u7115\u548c\u7687\u592a\u6781\u8c08\u548c\u3002\u8fd9\u201c\u4e0d\u6562\u201d\u4e24\u5b57\u4e4b\u4e2d\uff0c\u81ea\u7136\u9690\u4f0f\u4e86\u201c\u5c06\u6765\u548c\u4f60\u7b97\u5e10\u201d\u7684\u5fc3\u7406\u56e0\u7d20\u3002 \u3000\u3000\u8be5\u5e74\u95f0\u56db\u6708\uff0c\u52a0\u8881\u5d07\u7115\u592a\u5b50\u592a\u4fdd\u7684\u5934\u8854\uff0c\u90a3\u662f\u4ece\u4e00\u54c1\uff0c\u6bd4\u5175\u90e8\u5c1a\u4e66\u53c8\u9ad8\u4e86\u4e00\u7ea7\u3002\u5230\u4e86\u4e0b\u4e2a\u6708\uff0c\u4fbf\u53d1\u751f\u4e86\u6740\u6bdb\u6587\u9f99\u4e8b\u4ef6\uff0c\u8fd9\u53c8\u589e\u52a0\u4e86\u5d07\u796f\u5185\u5fc3\u5bf9\u4ed6\u7684\u4e0d\u6ee1\u548c\u6050\u60e7\u3002 \u3000\u3000\u6bdb\u6587\u9f99\u662f\u6d59\u6c5f\u676d\u5dde\u4eba\u3002\u8881\u5d07\u7115\u6740\u6bdb\u6587\u9f99\u5728\u5d07\u796f\u4e8c\u5e74\uff08\u516c\u5143\u4e00\u516d\u4e8c\u4e5d\uff09\uff0c\u90a3\u662f\u5df1\u5df3\u5e74\u3002\u65e9\u4e86\u4e00\u767e\u516b\u5341\u5e74\uff08\u4e00\u56db\u56db\u4e5d\uff09\uff0c\u540c\u6837\u662f\u5df1\u5df3\u5e74\uff0c\u6211\u53e6\u4e00\u4f4d\u540c\u4e61\u676d\u5dde\u4eba\u4e8e\u8c26\u4e3a\u660e\u671d\u7acb\u4e86\u5b89\u90a6\u5b9a\u56fd\u7684\u5927\u529f\u3002\u90a3\u4e00\u5e74\u53d1\u751f\u571f\u6728\u5821\u4e4b\u53d8\uff0c\u7687\u5e1d\u88ab\u8499\u53e4\u4eba\u63b3\u53bb\uff0c\u4e8e\u8c26\u51fb\u9000\u5916\u654c\uff0c\u5b89\u5b9a\u4e86\u56fd\u5bb6\u3002\u4e8e\u8c26\u548c\u8881\u5d07\u7115\u90fd\u662f\u5175\u90e8\u5c1a\u4e66\uff0c\u4e8e\u505a\u603b\u7763\uff0c\u8881\u505a\u7763\u5e08\uff0c\u5730\u4f4d\u76f8\u7b49\u2460\u3002\u4e24\u4eba\u540e\u6765\u90fd\u88ab\u7687\u5e1d\u5904\u6b7b\uff0c\u90fd\u662f\u660e\u671d\u51fa\u540d\u7684\u5927\u5fe0\u81e3\u3002 \u3000\u3000\u676d\u5dde\u4eba\u5728\u6c5f\u5357\u867d\u7136\u6709\u201c\u676d\u94c1\u5934\u201d\u4e4b\u79f0\uff0c\u7136\u800c\u90a3\u662f\u4e0e\u6027\u683c\u67d4\u548c\u7684\u82cf\u5dde\u4eba\u201c\u82cf\u7a7a\u5934\u201d\u76f8\u5bf9\u800c\u8a00\uff0c\u5f88\u5c11\u53bb\u5f53\u5175\u6253\u4ed7\u7684\u3002\u621a\u7ee7\u5149\u7387\u9886\u6765\u5e73\u5b9a\u502d\u5bc7\u3001\u5b88\u5fa1\u5317\u8fb9\uff0c\u540e\u6765\u5728\u621a\u6b7b\u540e\u53c8\u53bb\u6297\u65e5\u63f4\u671d\u7684\u6d59\u6c5f\u5175\uff0c\u90fd\u662f\u6d59\u4e1c\u4e49\u4e4c\u4e00\u5e26\u7684\u4eba\u3002 \u3000\u3000\u6bdb\u6587\u9f99\u6240\u4ee5\u6295\u519b\uff0c\u4e3b\u8981\u7531\u4e8e\u4ed6\u6709\u4e2a\u8205\u8205\u5728\u5175\u90e8\u505a\u5b98\u3002\u6bdb\u6587\u9f99\u559c\u6b22\u4e0b\u56f4\u68cb\uff0c\u5e38\u901a\u5bb5\u4e0b\u68cb\uff0c\u7231\u8bf4\uff1a\u201c\u6740\u5f97\u5317\u6597\u5f52\u5357\u3002\u201d\u6367\u4ed6\u573a\u7684\u4eba\uff0c\u8bf4\u4ed6\u7684\u68cb\u53cb\u4e2d\u6709\u4e00\u4e2a\u9053\u4eba\uff0c\u4ece\u56f4\u68cb\u4e2d\u4f20\u6388\u4e86\u4ed6\u5175\u6cd5\u3002\u5982\u679c\u771f\u6709\u8fd9\u6837\u7684\u4e8b\uff0c\u6bdb\u6587\u9f99\u7684\u68cb\u529b\u4e00\u5b9a\u76f8\u5f53\u4f4e\uff0c\u56e0\u4e3a\u4ed6\u7684\u5175\u6cd5\u5b9e\u5728\u5e76\u4e0d\u9ad8\u660e\u3002\u53c8\u6709\u4e00\u4e2a\u4f20\u8bf4\uff1a\u4ed6\u4e0a\u4eac\u53bb\u6295\u9760\u8205\u8205\u7684\u524d\u5915\uff0c\u7761\u5728\u4e8e\u5e99\uff08\u4e8e\u8c26\u7684\u5e99\uff0c\u5728\u676d\u5dde\u4e0e\u5cb3\u5e99\u5e76\u79f0\uff09\u91cc\u7948\u68a6\uff0c\u68a6\u5230\u4e8e\u8c26\u5199\u4e86\u5341\u516d\u4e2a\u5b57\u7ed9\u4ed6\uff1a\u201c\u6b32\u6548\u6dee\u9634\uff0c\u8001\u4e86\u4e00\u534a\u3002\u597d\u4e2a\u7530\u6a2a\uff0c\u65e0\u4eba\u4f5c\u4f34\u3002\u201d\u8fd9\u5341\u516d\u4e2a\u5b57\u540e\u6765\u679c\u7136\u201c\u5e94\u9a8c\u201d\u4e86\uff1a\u97e9\u4fe1\u4e8c\u5341\u4e03\u5c81\u4e3a\u5927\u5c06\uff0c\u6bdb\u6587\u9f99\u4e3a\u5927\u5c06\u65f6\u4e94\u5341\u4e8c\u5c81\uff1b\u7530\u6a2a\u5728\u5c9b\u4e0a\u81ea\u6740\u65f6\uff0c\u6709\u4e94\u767e\u58eb\u81ea\u520e\u800c\u6b89\uff0c\u6bdb\u6587\u9f99\u5728\u5c9b\u4e0a\u88ab\u6740\uff0c\u6b7b\u7684\u53ea\u4ed6\u4e00\u4eba\u3002\u8fd9\u5f53\u7136\u662f\u597d\u4e8b\u4e4b\u5f92\u4e8b\u540e\u634f\u9020\u51fa\u6765\u7684\u3002\u4e8e\u8c26\u89c1\u8bc6\u4f55\u7b49\u8d85\u5353\uff0c\u53c8\u600e\u4f1a\u5c06\u4ed6\u8fd9\u4e2a\u65e0\u804a\u540c\u4e61\u53bb\u548c\u97e9\u4fe1\u3001\u7530\u6a2a\u76f8\u6bd4\uff1f \u3000\u3000\u6bdb\u6587\u9f99\u5230\u5317\u4eac\u540e\uff0c\u5f97\u4ed6\u8205\u8205\u63a8\u8350\uff0c\u5230\u8fbd\u4e1c\u53bb\u6295\u6548\u603b\u5175\u674e\u6210\u6881\uff0c\u540e\u6765\u5728\u8881\u5e94\u6cf0\u3001\u738b\u5316\u8d1e\u4e24\u4eba\u624b\u4e0b\uff0c\u5347\u5230\u4e86\u5927\u7ea6\u76f8\u5f53\u4e8e\u56e2\u957f\u7684\u804c\u4f4d\u3002\u4ed6\u7684\u529f\u7ee9\u4e3b\u8981\u662f\u9020\u706b\u836f\u8d85\u989d\u5b8c\u6210\u4efb\u52a1\u548c\u7ec3\u5175\uff0c\u53ef\u89c1\u6b64\u4eba\u662f\u4e00\u4e2a\u80fd\u5e72\u7684\u540e\u52e4\u4eba\u5458\u3002\u8fbd\u4e1c\u5931\u9677\u540e\uff0c\u4ed6\u5e26\u4e86\u4e00\u6279\u90e8\u961f\uff0c\u5728\u6cbf\u6d77\u5404\u5c9b\u548c\u8fbd\u4e1c\u3001\u671d\u9c9c\u8fb9\u533a\u6df7\u6765\u6df7\u53bb\uff0c\u6253\u6253\u6e38\u51fb\u3002\u4ed6\u7684\u6839\u636e\u5730\u662f\u5728\u671d\u9c9c\uff0c\u62db\u7eb3\u8fbd\u4e1c\u6e83\u6563\u4e0b\u6765\u7684\u4e2d\u56fd\u8d25\u5175\u548c\u96be\u6c11\uff0c\u52bf\u529b\u6e10\u6e10\u6269\u5145\uff0c\u7ec8\u4e8e\u627e\u5230\u4e86\u4e00\u4e2a\u673a\u4f1a\uff0c\u5e26\u9886\u4e86\u4e5d\u5341\u516b\u4eba\uff0c\u6e21\u9e2d\u7eff\u6c5f\u88ad\u51fb\u9547\u6c5f\u57ce\u2461\uff0c\u4fd8\u864f\u4e86\u6e05\u519b\u5b88\u5c06\u3002\u8fd9\u662f\u660e\u519b\u6253\u8d25\u6e05\u5175\u7684\u7f55\u6709\u4e8b\u4ef6\uff0c\u738b\u5316\u8d1e\u5927\u4e3a\u9ad8\u5174\uff0c\u6781\u529b\u63a8\u8350\uff0c\u5347\u4ed6\u7684\u5b98\uff0c\u9a7b\u5728\u9547\u6c5f\u57ce\u3002\u4f46\u4e0d\u4e45\u6e05\u5175\u5927\u519b\u53cd\u653b\uff0c\u9547\u6c5f\u57ce\u5c31\u5931\u53bb\u4e86\u3002\u6bdb\u6587\u9f99\u5c06\u6839\u636e\u5730\u8fc1\u5230\u671d\u9c9c\u7684\u76ae\u5c9b\uff0c\u81ea\u5df1\u4ecd\u5728\u8fbd\u4e1c\u671d\u9c9c\u8fb9\u533a\u6253\u6e38\u51fb\u3002 \u3000\u3000\u76ae\u5c9b\u5728\u9e2d\u7eff\u6c5f\u53e3\uff0c\u4e0e\u671d\u9c9c\u672c\u571f\u53ea\u4e00\u6c34\u4e4b\u9694\uff0c\u6c34\u9762\u8ddd\u79bb\u53ea\u4e0d\u8fc7\u76f8\u5f53\u4e8e\u8fc7\u4e00\u6761\u957f\u6c5f\u800c\u5df2\uff0c\u5317\u5cb8\u4fbf\u662f\u671d\u9c9c\u7684\u5ba3\u5ddd\u3001\u94c1\u5c71\u2462\u3002\u5f53\u65f6\u671d\u9c9c\u7684\u4e49\u5dde\u3001\u5b89\u5dde\u3001\u94c1\u5c71\u4e00\u5e26\uff0c\u56e0\u4e3a\u90bb\u8fd1\u4e2d\u56fd\uff0c\u4ece\u8fbd\u4e1c\u9003\u51fa\u6765\u7684\u6c49\u4eba\u96be\u6c11\u548c\u8d25\u5175\u7eb7\u7eb7\u6d8c\u5230\uff0c\u55a7\u5bbe\u593a\u4e3b\uff0c\u6c49\u4eba\u5360\u4e86\u5c45\u6c11\u5341\u5206\u4e4b\u4e03\uff0c\u671d\u9c9c\u4eba\u53ea\u5341\u5206\u4e4b\u4e09\u3002\u76ae\u5c9b\u6a2a\u7ea6\u516b\u5341\u91cc\uff0c\u9003\u5230\u5c9b\u4e0a\u7684\u6c49\u4eba\u4e3a\u6570\u4e0d\u5c11\u3002\u6bdb\u6587\u9f99\u4f5c\u4e3a\u6839\u636e\u5730\u540e\uff0c\u518d\u62db\u7eb3\u6c49\u4eba\uff0c\u58f0\u52bf\u6e10\u76db\u3002\u660e\u671d\u7279\u522b\u4e3a\u4ed6\u8bbe\u7acb\u4e00\u4e2a\u519b\u533a\uff0c\u53eb\u4f5c\u4e1c\u6c5f\u9547\uff0c\u5347\u6bdb\u6587\u9f99\u4e3a\u603b\u5175\u3002 \u3000\u3000\u90a3\u65f6\u8881\u5d07\u7115\u521a\u51fa\u5c71\u6d77\u5173\uff0c\u8fd8\u672a\u5efa\u529f\u3002\u660e\u671d\u552f\u4e00\u80fd\u4e0e\u6e05\u5175\u6253\u4e00\u4e0b\u7684\uff0c\u53ea\u6709\u6bdb\u6587\u9f99\u4e00\u519b\uff0c\u6240\u4ee5\u4ed6\u540d\u6c14\u76f8\u5f53\u5927\u3002\u5f53\u65f6\u8463\u5176\u660c\u66fe\u4e0a\u594f\u8bf4\uff1a\u56fd\u5bb6\u53ea\u8981\u6709\u4e24\u4e2a\u6bdb\u6587\u9f99\uff0c\u52aa\u5c14\u54c8\u8d64\u53ef\u64d2\uff0c\u8fbd\u5730\u53ef\u590d\u3002\u4ed6\u8fd9\u9053\u594f\u7ae0\uff0c\u5f53\u7136\u53ea\u6709\u4e66\u6cd5\u4e0a\u7684\u4ef7\u503c\uff0c\u4f46\u7531\u6b64\u4e5f\u53ef\u89c1\u5230\u4e00\u822c\u671d\u81e3\u5bf9\u6bdb\u6587\u9f99\u7684\u89c2\u611f\u3002\u6bdb\u6587\u9f99\u4e0d\u65ad\u5347\u5b98\uff0c\u5347\u5230\u5de6\u90fd\u7763\uff0c\u6302\u5c06\u519b\u5370\uff0c\u8d50\u5c1a\u65b9\u5251\u3002\u5929\u542f\u7687\u5e1d\u63d0\u5230\u4ed6\u65f6\u79f0\u4e3a\u201c\u6bdb\u5e05\u201d\uff0c\u4e0d\u53eb\u540d\u5b57\u3002 \u3000\u3000\u5929\u542f\u56db\u5e74\u4e94\u6708\uff0c\u6bdb\u6587\u9f99\u9063\u5c06\u6cbf\u9e2d\u7eff\u6c5f\u3001\u8d8a\u957f\u767d\u5c71\uff0c\u653b\u5165\u6ee1\u6e05\u4e1c\u90e8\uff0c\u88ab\u5b88\u5c06\u51fb\u8d25\uff0c\u5168\u519b\u8986\u6ca1\uff1b\u4e94\u5e74\u516d\u6708\u53ca\u516d\u5e74\u4e94\u6708\uff0c\u66fe\u4e24\u6b21\u6d3e\u5175\u88ad\u51fb\u6ee1\u6e05\u57ce\u5be8\uff0c\u4e24\u6b21\u90fd\u4e27\u5e08\u8d25\u5f52\u3002\u6bdb\u6587\u9f99\u6253\u4ed7\u662f\u4e0d\u884c\u7684\uff0c\u53ef\u662f\u8fde\u5e74\u88ad\u51fb\u6ee1\u6e05\u8179\u5730\uff0c\u4e0d\u5931\u4e3a\u6709\u7275\u5236\u4f5c\u7528\u3002\u90a3\u65f6\u5019\u660e\u519b\u4e00\u89c1\u6e05\u5175\u5c31\u671b\u98ce\u800c\u9041\uff0c\u6bdb\u6587\u9f99\u80c6\u6562\u4e3b\u52a8\u51fa\u51fb\uff0c\u5e94\u5f53\u8bf4\u52c7\u6c14\u53ef\u5609\u3002 \u3000\u3000\u5929\u542f\u4e03\u5e74\u6b63\u6708\uff0c\u6e05\u5175\u5f81\u671d\u9c9c\uff0c\u56e0\u4e3a\u6bdb\u6587\u9f99\u4e0d\u65ad\u5728\u540e\u65b9\u9a9a\u6270\uff0c\u4e8e\u662f\u5206\u5175\u53bb\u653b\u4ed6\u6240\u9a7b\u5b88\u7684\u94c1\u5c71\u3002\u6bdb\u6587\u9f99\u5927\u8d25\uff0c\u9003\u4e0a\u4e86\u76ae\u5c9b\u3002 \u3000\u3000\u4ed6\u5728\u4e2d\u671d\u8fb9\u533a\u6253\u6e38\u51fb\u65f6\uff0c\u867d\u7136\u5c61\u6218\u5c61\u8d25\uff0c\u5374\u4e5f\u80fd\u5c61\u8d25\u5c61\u6218\u3002\u4e0a\u4e86\u76ae\u5c9b\u4e4b\u540e\uff0c\u6709\u4e86\u5927\u6d77\u7684\u963b\u9694\uff0c\u6e05\u519b\u6ca1\u6709\u6c34\u5e08\uff0c\u5b89\u5168\u611f\u5927\u589e\uff0c\u52a0\u4e4b\u53c8\u4e0a\u4e86\u5e74\u7eaa\uff0c\u5f88\u5feb\u5c31\u8150\u5316\u8d77\u6765\u2463\u3002 \u3000\u3000\u4ed6\u5f00\u59cb\u53d1\u6325\u540e\u52e4\u624d\u80fd\uff0c\u5728\u76ae\u5c9b\u5927\u505a\u751f\u610f\uff0c\u5f81\u6536\u5546\u8239\u901a\u884c\u7a0e\uff0c\u90a3\u4fbf\u662f\u6d77\u4e0a\u4e70\u8def\u94b1\uff0c\u6d3e\u4eba\u53bb\u8fbd\u4e1c\u548c\u671d\u9c9c\u6316\u4eba\u53c2\u3002\u4e00\u65b9\u9762\u5411\u671d\u5ef7\u8981\u7cae\u8981\u9977\uff0c\u53c8\u5411\u671d\u9c9c\u8981\u7cae\u98df\uff0c\u7406\u7531\u662f\u5e2e\u671d\u9c9c\u62b5\u6297\u6e05\u5175\uff0c\u8981\u6536\u4fdd\u62a4\u8d39\u3002\u671d\u9c9c\u4e5f\u53ea\u5f97\u65f6\u65f6\u8fd0\u7cae\u7ed9\u4ed6\u3002\u4ed6\u5347\u5b98\u53d1\u8d22\u4e4b\u540e\uff0c\u5bf9\u6253\u4ed7\u66f4\u52a0\u6ca1\u6709\u5174\u8da3\u4e86\u3002\u5f53\u65f6\u76ae\u5c9b\u9a7b\u519b\u6709\u4e8c\u4e07\u516b\u5343\uff0c\u6218\u9a6c\u4e09\u5343\u4f59\u5339\uff0c\u76ae\u5c9b\u4e4b\u4e1c\u7684\u8eab\u5f25\u5c9b\u9a7b\u5175\u5343\u4f59\uff0c\u4f5c\u4e3a\u76ae\u5c9b\u7684\u5916\u56f4\uff0c\u5b81\u9526\u5927\u6218\u4e4b\u65f6\uff0c\u6bdb\u6587\u9f99\u624b\u62e5\u91cd\u5175\u5728\u65c1\uff0c\u7adf\u4e0d\u53d1\u4e00\u5175\u4e00\u5352\u53bb\u652f\u63f4\uff0c\u4e5f\u4e0d\u653b\u51fb\u6e05\u5175\u540e\u65b9\u4f5c\u7275\u5236\u3002\u8881\u5d07\u7115\u5f53\u7136\u6781\u4e0d\u6ee1\u610f\uff0c\u4f46\u56e0\u7ba1\u4ed6\u4e0d\u7740\uff0c\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u5929\u542f\u5e74\u95f4\uff0c\u6bdb\u6587\u9f99\u4e0d\u65ad\u4ee5\u5927\u91cf\u8d3f\u8d42\u9001\u7ed9\u9b4f\u5fe0\u8d24\u548c\u5176\u4ed6\u592a\u76d1\u3001\u5927\u81e3\uff0c\u5bf9\u671d\u4e2d\u5f53\u6743\u6d3e\u7684\u516c\u5171\u5173\u7cfb\u505a\u5f97\u6781\u597d\u3002\u5929\u542f\u4e94\u5e74\uff0c\u5fa1\u53f2\u9ea6\u4e4b\u4ee4\u5f39\u52be\u6bdb\u6587\u9f99\uff0c\u8ba4\u4e3a\u4ed6\u65e0\u7528\uff0c\u8fbd\u4e1c\u519b\u52a1\u4e0d\u80fd\u4f9d\u9760\u4ed6\u3002\u9b4f\u5fe0\u8d24\u6781\u529b\u8892\u6bdb\uff0c\u8bf4\u9ea6\u4e4b\u4ee4\u662f\u718a\u5ef7\u5f3c\u7684\u540c\u515a\uff0c\u5c06\u4ed6\u6740\u4e86\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u6240\u6709\u53cd\u5bf9\u9b4f\u5fe0\u8d24\u7684\u4e1c\u6797\u515a\u6e05\u6d41\u6d3e\u90fd\u6068\u4e0a\u4e86\u6bdb\u6587\u9f99\u3002 \u3000\u3000\u5d07\u796f\u63a5\u4f4d\u540e\uff0c\u6bdb\u6587\u9f99\u4f5c\u98ce\u4e0d\u6539\u3002\u671d\u5ef7\u89c9\u5f97\u76ae\u5c9b\u8017\u8d39\u7cae\u9977\u592a\u591a\uff0c\u8981\u6d3e\u4eba\u53bb\u6838\u6570\u67e5\u5e10\u3002\u6bdb\u6587\u9f99\u591a\u65b9\u63a8\u6258\uff0c\u603b\u4e4b\u662f\u4e0d\u6b22\u8fce\u5fa1\u7528\u4f1a\u8ba1\u5e08\u9a7e\u4e34\u3002 \u3000\u3000\u8881\u5d07\u7115\u7684\u65b0\u4efb\u547d\uff0c\u7406\u8bba\u4e0a\u662f\u6709\u6743\u7ba1\u5230\u76ae\u5c9b\u4e1c\u6c5f\u9547\u7684\u3002\u671d\u4e2d\u4e8e\u662f\u6709\u4eba\u5efa\u8bae\u76ae\u5c9b\u7684\u7cae\u9977\u7ecf\u7531\u5b81\u8fdc\u8f6c\u8fd0\uff0c\u610f\u601d\u662f\u4ea4\u7531\u8881\u5d07\u7115\u63a7\u5236\u3002\u751a\u81f3\u6709\u4eba\u4e3b\u5f20\u64a4\u9000\u76ae\u5c9b\u5b88\u519b\uff0c\u5168\u90e8\u8c03\u53bb\u5b81\u8fdc\u3002\u8fd9\u4e9b\u4e3b\u5f20\uff0c\u90fd\u906d\u5230\u6bdb\u6587\u9f99\u7684\u6297\u62d2\uff0c\u800c\u5175\u90e8\u53c8\u5bf9\u6bdb\u76f8\u5f53\u652f\u6301\u3002 \u3000\u3000\u8881\u5d07\u7115\u5199\u4fe1\u7ed9\u9996\u8f85\u94b1\u9f99\u9521\u5546\u91cf\uff0c\u8981\u6740\u6bdb\u6587\u9f99\u3002\u94b1\u56de\u4fe1\u529d\u4ed6\u4e00\u5207\u614e\u91cd\u3002\u8881\u5728\u5317\u4eac\u65f6\uff0c\u4e5f\u66fe\u548c\u94b1\u9f99\u9521\u5546\u8bae\u8fc7\u6740\u6bdb\u7684\u4e8b\uff0c\u5f53\u65f6\u8881\u5bf9\u94b1\u9f99\u9521\u8bf4\uff0c\u8981\u6062\u590d\u8fbd\u4e1c\uff0c\u5fc5\u987b\u4ece\u6574\u8083\u4e1c\u6c5f\u9547\u7684\u519b\u7eaa\u5f00\u59cb\u3002 \u3000\u3000\u8881\u5d07\u7115\u51b3\u5fc3\u8981\u89e3\u51b3\u8fd9\u4ef6\u4e8b\u3002\u5d07\u796f\u4e8c\u5e74\u4e94\u6708\u4e8c\u5341\u4e8c\u65e5\uff0c\u8881\u5d07\u7115\u79bb\u5b81\u8fdc\uff0c\u53bb\u548c\u6bdb\u6587\u9f99\u4f1a\u8c08\uff0c\u7ea6\u5b9a\u4e86\u5728\u65c5\u987a\u9644\u8fd1\u7684\u4e00\u4e2a\u5c0f\u5c9b\u4e0a\u76f8\u4f1a\uff0c\u8fd9\u5c0f\u5c9b\u53eb\u505a\u5c9b\u5c71\u2464\u3002\u4ece\u5b81\u8fdc\u7ecf\u6e24\u6d77\u5230\u65c5\u987a\uff0c\u548c\u4ece\u76ae\u5c9b\u7ecf\u9ec4\u6d77\u5230\u65c5\u987a\uff0c\u6d77\u7a0b\u5927\u81f4\u76f8\u7b49\uff0c\u6240\u4ee5\u65c5\u987a\u662f\u4e00\u4e2a\u4e2d\u95f4\u5730\u70b9\uff0c\u4e5f\u53ef\u8bf4\u662f\u4e2d\u7acb\u5730\u5e26\u3002\u90a3\u65f6\u6bdb\u6587\u9f99\u5bf9\u8881\u5d07\u7115\u5df2\u5fc3\u5b58\u7591\u5fcc\uff0c\u5982\u9080\u4ed6\u5230\u5b81\u8fdc\u76f8\u4f1a\uff0c\u4ed6\u662f\u4e0d\u80af\u6765\u7684\u3002\u8881\u5d07\u7115\u5982\u53bb\u76ae\u5c9b\uff0c\u5374\u53c8\u662f\u8eab\u5165\u9669\u5730\u3002 \u3000\u3000\u8881\u5d07\u7115\u9664\u5ea7\u8239\u5916\uff0c\u5e26\u8239\u4e09\u5341\u516b\u8258\uff0c\u51fa\u53d1\u524d\u5148\u8bd5\u653e\u897f\u6d0b\u5927\u70ae\uff0c\u5c04\u7a0b\u8fdc\u7684\u4e94\u516d\u91cc\uff0c\u8fd1\u7684\u4e09\u56db\u91cc\u3002\u4e8c\u5341\u516d\u65e5\u5230\u53cc\u5c9b\uff0c\u767b\u5dde\u7684\u519b\u5b98\u5e26\u4e86\u5175\u8239\u56db\u5341\u516b\u8258\u6765\u4f1a\u3002\u4e8c\u5341\u4e03\u65e5\u5230\u5c9b\u5c71\u505c\u6cca\uff0c\u65c5\u987a\u7684\u519b\u5b98\u524d\u6765\u53c2\u89c1\u3002\u8881\u5d07\u7115\u5e26\u4f17\u5c06\u4e0a\u5c71\uff0c\u5230\u9f99\u738b\u5e99\u53bb\u62dc\u9f99\u738b\uff0c\u5bf9\u4f17\u5c06\u8bad\u8bdd\uff1a\u201c\u672c\u671d\u5f00\u56fd\uff0c\u4e2d\u5c71\u738b\u5f90\u8fbe\u3001\u5f00\u5e73\u738b\u5e38\u9047\u6625\u8bf8\u541b\u8d77\u521d\u5728\u9131\u9633\u6e56\u3001\u91c7\u77f3\u77f6\u5927\u6218\uff0c\u540e\u6765\u4e00\u76f4\u6253\u5230\u6f20\u5317\uff0c\u6c34\u6218\u56fa\u7136\u80dc\uff0c\u9a6c\u6b65\u6218\u4e5f\u80dc\uff0c\u624d\u80fd\u9a71\u9010\u80e1\u5143\uff0c\u7edf\u4e00\u4e2d\u56fd\u3002\u73b0\u5728\u4f60\u4eec\u7684\u6c34\u5e08\u53ea\u80fd\u4ee5\u7ea2\u8239\u5728\u6c34\u4e0a\u81ea\u5b88\uff0c\u6ee1\u6e05\u9791\u5b50\u4e0d\u4e0b\u6d77\uff0c\u96be\u9053\u80fd\u8d76\u4ed6\u4eec\u5165\u6d77\u6253\u6c34\u6218\u4e48\uff1f\u6240\u4ee5\u6c34\u5e08\u5fc5\u987b\u4e5f\u80fd\u9646\u6218\u3002\u201d\u4ed6\u7684\u62b1\u8d1f\u662f\u8981\u5c06\u6c34\u5e08\u8bad\u7ec3\u6210\u4e3a\u6d77\u519b\u9646\u6218\u961f\u3002 \u3000\u3000\u516d\u6708\u521d\u4e00\uff0c\u6bdb\u6587\u9f99\u7387\u9886\u5c06\u58eb\u5230\u8fbe\u5c9b\u5c71\uff0c\u4e0e\u8881\u4e92\u76f8\u4ea4\u62dc\u3002\u6bdb\u6587\u9f99\u5448\u4e0a\u793c\u5e16\u4e09\u5c01\u548c\u4e09\u684c\u7b75\u5e2d\u3002\u5728\u8239\u4e2d\u5403\u8fc7\uff0c\u8881\u5d07\u7115\u548c\u4ed6\u8c08\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u8fbd\u4e1c\u6d77\u5916\uff0c\u53ea\u6709\u6211\u548c\u8d35\u9547\u4e8c\u4eba\uff0c\u52a1\u5fc5\u540c\u5fc3\u5171\u6d4e\uff0c\u65b9\u80fd\u6210\u529f\u3002\u6211\u5386\u9669\u6765\u6b64\uff0c\u65e8\u5728\u5546\u8bae\u8fdb\u53d6\u3002\u519b\u56fd\u5927\u4e8b\uff0c\u5728\u6b64\u4e00\u4e3e\u3002\u6211\u6709\u4e00\u4e2a\u826f\u65b9\uff0c\u53ea\u4e0d\u77e5\u751f\u75c5\u7684\u4eba\u80af\u4e0d\u80af\u670d\u8fd9\u4e00\u5e16\u836f\u3002\u201d\u5f53\u665a\u4e24\u4eba\u76f4\u8c08\u5230\u4e8c\u66f4\u3002\u521d\u4e8c\u8881\u5d07\u7115\u4e0a\u5c9b\uff0c\u7292\u8d4f\u6bdb\u7684\u90e8\u5c5e\uff0c\u548c\u6bdb\u53c8\u5bc6\u8c08\u5230\u4e09\u66f4\u3002\u521d\u4e09\u65e5\u53c8\u518d\u8c08\uff0c\u8881\u5d07\u7115\u8981\u6c42\u76ae\u5c9b\u8bbe\u6587\u5b98\u76d1\u519b\uff0c\u7cae\u9977\u7531\u5b81\u8fdc\u8f6c\u53d1\uff0c\u6539\u7f16\u90e8\u961f\uff0c\u8fde\u8c08\u4e09\u65e5\u4e09\u591c\uff0c\u6bdb\u6587\u9f99\u59cb\u7ec8\u4e0d\u540c\u610f\uff0c\u5230\u8fd9\u65f6\u8c08\u5224\u7ec8\u4e8e\u7834\u88c2\u3002\u8881\u5d07\u7115\u7ed9\u4ed6\u6700\u540e\u4e00\u4e2a\u673a\u4f1a\uff0c\u529d\u4ed6\u8f9e\u804c\u56de\u4e61\u3002\u6bdb\u6587\u9f99\u8bf4\uff1a\u201c\u8f9e\u804c\u56de\u4e61\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e00\u76f4\u662f\u5728\u76fc\u671b\u7684\u3002\u53ea\u4e0d\u8fc7\u6211\u5bf9\u8fbd\u4e1c\u4e8b\u52a1\u5f88\u719f\u6089\uff0c\u89e3\u51b3\u4e86\u6ee1\u6d32\u4e4b\u540e\uff0c\u53ef\u987a\u52bf\u88ad\u53d6\u671d\u9c9c\u4e86\u3002\u201d\u8881\u5d07\u7115\u542c\u4ed6\u5927\u8a00\u4e0d\u60ed\uff0c\u66f4\u662f\u4e0d\u6ee1\u2465\u3002\u9152\u6563\u540e\uff0c\u8881\u4f20\u526f\u5c06\u6c6a\u7fe5\u4e0a\u8239\u5bc6\u8bae\uff0c\u4e94\u66f4\u65b9\u6bd5\u3002\u901a\u5bb5\u90e8\u7f72\uff0c\u8981\u6740\u6bdb\u6587\u9f99\u4e86\u3002 \u3000\u3000\u521d\u56db\u65e5\uff0c\u8881\u5d07\u7115\u7292\u8d4f\u6bdb\u90e8\u5175\u5c06\u5171\u4e09\u5343\u4e94\u767e\u4e03\u5341\u4e94\u540d\uff0c\u519b\u5b98\u6bcf\u540d\u4e09\u4e94\u4e24\u4e0d\u7b49\uff0c\u5175\u6bcf\u540d\u6570\u94b1\uff0c\u53c8\u5c06\u5e26\u6765\u7684\u9977\u94f6\u5341\u4e07\u4e24\u4ea4\u5378\u3002\u540c\u65f6\u548c\u6bdb\u5212\u5206\u804c\u6743\uff0c\u6b64\u540e\u65c5\u987a\u4ee5\u4e1c\u7531\u6bdb\u6307\u6325\uff0c\u65c5\u987a\u4ee5\u897f\u7531\u8881\u6307\u6325\u3002\u6bdb\u6587\u9f99\u6536\u5230\u5927\u7b14\u94f6\u5b50\uff0c\u5bf9\u6307\u6325\u6743\u7684\u533a\u5212\u53c8\u5341\u5206\u6ee1\u610f\uff0c\u51cf\u5c11\u4e86\u63d0\u9632\u8b66\u60d5\u3002 \u3000\u3000\u521d\u4e94\u65e5\uff0c\u8881\u5d07\u7115\u9080\u6bdb\u6587\u9f99\u4e00\u8d77\u68c0\u9605\u5c06\u58eb\u6bd4\u8d5b\u5c04\u7bad\u3002\u76f8\u89c1\u540e\uff0c\u8881\u5d07\u7115\u8bf4\uff1a\u201c\u6211\u660e\u5929\u8981\u56de\u5b81\u8fdc\u4e86\u3002\u8d35\u9547\u8eab\u5f53\u56fd\u5bb6\u6d77\u5916\u91cd\u5bc4\uff0c\u8bf7\u53d7\u6211\u4e00\u62dc\u3002\u201d\u8bf4\u7740\u4e0b\u62dc\uff0c\u6bdb\u6587\u9f99\u8dea\u4e0b\u8fd8\u793c\u3002\u5927\u5bb6\u4e0a\u5c71\u540e\uff0c\u8881\u7684\u4eb2\u4fe1\u53c2\u5c06\u8c22\u5c1a\u653f\u6307\u6325\u5404\u8425\u58eb\u5175\u5e03\u6210\u4e00\u4e2a\u5927\u56f4\u3002\u6bdb\u6587\u9f99\u548c\u968f\u4ece\u5b98\u5458\u767e\u4f59\u540d\u5728\u56f4\u5185\uff0c\u5c06\u6bdb\u90e8\u5175\u4e01\u90fd\u9694\u5728\u56f4\u5916\u3002 \u3000\u3000\u8881\u5d07\u7115\u95ee\u8d77\u6bdb\u6587\u9f99\u624b\u4e0b\u5c06\u5b98\u7684\u59d3\u540d\uff0c\u5c45\u7136\u5927\u591a\u6570\u59d3\u6bdb\u3002\u8881\u5d07\u7115\u89c9\u5f97\u5947\u602a\u3002\u6bdb\u6587\u9f99\u8bf4\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u6211\u7684\u4e49\u5b59\u3002\u201d\u2466\u8881\u5d07\u7115\u7b11\u4e86\u8d77\u6765\uff0c\u8ddf\u7740\u5bf9\u6bdb\u90e8\u4f17\u5c06\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5728\u6d77\u5916\u8f9b\u82e6\uff0c\u5175\u58eb\u6bcf\u4e2a\u6708\u53ea\u6709\u4e94\u6597\u7c73\u7684\u7cae\uff0c\u751a\u81f3\u5bb6\u4e2d\u51e0\u53e3\u4eba\u90fd\u5206\u98df\u6b64\u7cae\uff0c\u60f3\u8d77\u6765\u4ee4\u4eba\u75db\u5fc3\u3002\u8bf7\u5927\u5bb6\u53d7\u6211\u4e00\u62dc\uff0c\u611f\u8c22\u4f60\u4eec\u4e3a\u56fd\u5bb6\u5c3d\u529b\uff0c\u4ee5\u540e\u5927\u5bb6\u4e0d\u5fc5\u62c5\u5fc3\u6ca1\u6709\u7cae\u9977\u3002\u201d\u5f53\u5373\u4e0b\u62dc\u3002\u4f17\u5c06\u78d5\u5934\u7b54\u793c\uff0c\u751a\u662f\u611f\u52a8\u3002 \u3000\u3000\u8881\u5d07\u7115\u968f\u5373\u63d0\u51fa\u51e0\u4ef6\u4e8b\u6765\u8d23\u95ee\u6bdb\u6587\u9f99\uff0c\u6bdb\u6587\u9f99\u6297\u8fa9\u3002\u8881\u5d07\u7115\u4e0d\u5ba2\u6c14\u4e86\uff0c\u65a5\u8d23\u9053\uff1a\u201c\u672c\u90e8\u9662\u62ab\u809d\u6ca5\u80c6\uff0c\u4e0e\u4f60\u8bf4\u4e86\u4e09\u65e5\uff0c\u53ea\u9053\u4f60\u56de\u5934\u662f\u5cb8\uff0c\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u54ea\u6653\u5f97\u4f60\u72fc\u5b50\u91ce\u5fc3\uff0c\u603b\u662f\u4e00\u7247\u6b3a\u8bf3\u5230\u5e95\u3002\u4f60\u76ee\u4e2d\u6ca1\u6709\u672c\u90e8\u9662\uff0c\u90a3\u4e5f\u7f62\u4e86\u3002\u65b9\u4eca\u5723\u5929\u5b50\u82f1\u6b66\u5929\u7eb5\uff0c\u56fd\u6cd5\u5c82\u5bb9\u5f97\u4f60\uff1f\u201d\u547d\u4eba\u9664\u4e0b\u4ed6\u8863\u51a0\uff0c\u7ed1\u4e86\u8d77\u6765\u3002\u6bdb\u6587\u9f99\u7684\u6001\u5ea6\u4ecd\u662f\u5341\u5206\u5014\u5f3a\uff0c\u81ea\u79f0\u65e0\u7f6a\u6709\u529f\u3002 \u3000\u3000\u8881\u5d07\u7115\u5389\u58f0\u9053\uff1a\u201c\u4f60\u9053\u672c\u90e8\u9662\u662f\u4e2a\u4e66\u751f\uff0c\u77a7\u6211\u4e0d\u8d77\u3002\u672c\u90e8\u9662\u5374\u662f\u80fd\u7ba1\u5c06\u5b98\u4e4b\u4eba\u3002\u4f60\u8bf4\u6ca1\u6709\u7f6a\u4e48\uff1f\u4f60\u72af\u4e86\u5341\u4e8c\u5927\u7f6a\uff0c\u6211\u6570\u7ed9\u4f60\u542c\uff1a\u201c\u4e00\u3001\u660e\u671d\u7684\u5236\u5ea6\uff0c\u5927\u5c06\u5728\u5916\uff0c\u5fc5\u7531\u6587\u81e3\u76d1\u7763\uff0c\u4f60\u4e13\u5236\u4e00\u65b9\uff0c\u519b\u9a6c\u94b1\u7cae\u4e0d\u80af\u53d7\u6838\u3002\u4e8c\u3001\u6740\u622e\u964d\u4eba\u96be\u6c11\uff0c\u8c0e\u62a5\u5192\u529f\uff0c\u8bf4\u6740\u7684\u662f\u6e05\u5175\u3002\u4e09\u3001\u5ba3\u79f0\u5982\u679c\u5357\u4e0b\uff0c\u53d6\u767b\u5dde\u548c\u5357\u4eac\u72b9\u5982\u53cd\u638c\u3002\u56db\u3001\u6bcf\u5c81\u9977\u94f6\u6570\u5341\u4e07\uff0c\u4f46\u53d1\u7ed9\u5175\u58eb\u7684\u7cae\u9977\u6bcf\u6708\u53ea\u6709\u4e09\u6597\u534a\uff0c\u4fb5\u76d7\u519b\u7cae\u3002\u4e94\u3001\u5728\u76ae\u5c9b\u5f00\u9a6c\u5e02\uff0c\u64c5\u81ea\u4e0e\u5916\u56fd\u8d38\u6613\u3002\u516d\u3001\u90e8\u5c06\u6570\u5343\u540d\u90fd\u5192\u79f0\u59d3\u6bdb\uff0c\u64c5\u81ea\u5c01\u5b98\u3002\u4e03\u3001\u8d25\u9000\u65f6\u527d\u63a0\u5546\u8239\u3002\u516b\u3001\u4f60\u81ea\u5df1\u5f3a\u62a2\u826f\u5bb6\u5987\u5973\uff0c\u90e8\u4e0b\u6548\u5c24\u3002\u4e5d\u3001\u9a71\u7b56\u96be\u6c11\u5230\u8fbd\u4e1c\u53bb\u5077\u6316\u4eba\u53c2\uff0c\u4e0d\u80af\u53bb\u7684\u5c31\u4e0d\u53d1\u7cae\u98df\uff0c\u8ba9\u4ed6\u4eec\u5927\u6279\u5728\u5c9b\u4e0a\u997f\u6b7b\u3002\u5341\u3001\u5c06\u5927\u91cf\u91d1\u94f6\u9001\u53bb\u4eac\u5e08\u8d3f\u8d42\uff0c\u62dc\u9b4f\u5fe0\u8d24\u4e3a\u4e49\u7236\uff0c\u5728\u5c9b\u4e0a\u66ff\u9b4f\u5fe0\u8d24\u5851\u50cf\u3002\u5341\u4e00\u3001\u94c1\u5c71\u4e00\u4ed7\uff0c\u5927\u8d25\u4e27\u5e08\uff0c\u5374\u62a5\u79f0\u6709\u529f\u3002\u5341\u4e8c\u3001\u8bbe\u7acb\u519b\u533a\u5df2\u8fbe\u516b\u5e74\uff0c\u4e0d\u80fd\u6062\u590d\u5bf8\u571f\uff0c\u89c2\u671b\u517b\u654c\u3002\u201d \u3000\u3000\u8fd9\u5341\u4e8c\u6761\u7f6a\u72b6\u6570\u4e86\u51fa\u6765\uff0c\u6bdb\u6587\u9f99\u9b42\u4e0d\u9644\u4f53\uff0c\u53ea\u6709\u53e9\u5934\u6c42\u9976\u3002 \u3000\u3000\u8881\u5d07\u7115\u95ee\u6bdb\u7684\u90e8\u5c06\uff1a\u201c\u6bdb\u6587\u9f99\u8be5\u65a9\u4e48\uff1f\u201d\u8bf8\u5c06\u90fd\u5413\u5f97\u4e0d\u6562\u4f5c\u58f0\u3002\u6709\u4eba\u8bf4\u6bdb\u6587\u9f99\u8fd9\u4e9b\u5e74\u6765\u867d\u65e0\u529f\u52b3\uff0c\u4f46\u4e5f\u8f9b\u82e6\u51fa\u529b\u3002\u8881\u5d07\u7115\u53f1\u9053\uff1a\u201c\u6bdb\u6587\u9f99\u672c\u6765\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5bfb\u5e38\u767e\u59d3\uff0c\u73b0\u4eca\u5b98\u5c45\u6781\u54c1\uff0c\u6ee1\u95e8\u5c01\u836b\uff0c\u5df2\u8db3\u591f\u916c\u7b54\u4ed6\u7684\u8f9b\u52b3\u4e86\uff0c\u4e3a\u751a\u4e48\u4ed6\u8fd8\u8fd9\u6837\u6096\u9006\uff1f\u201d \u3000\u3000\u4e8e\u662f\u5411\u7740\u5317\u4eac\u53e9\u5934\uff0c\u5ba3\u79f0\uff1a\u201c\u81e3\u4eca\u5929\u8bdb\u6bdb\u6587\u9f99\u4ee5\u6574\u8083\u519b\u7eaa\uff0c\u8bf8\u5c06\u4e2d\u82e5\u6709\u884c\u4e3a\u5982\u6bdb\u6587\u9f99\u7684\uff0c\u4e5f\u4e00\u6982\u5904\u51b3\u3002\u81e3\u5982\u4e0d\u80fd\u6210\u529f\uff0c\u8bf7\u7687\u4e0a\u4e5f\u50cf\u8bdb\u6bdb\u6587\u9f99\u4e00\u6837\u7684\u5904\u51b3\u81e3\uff01\u201d\u8bf7\u51fa\u5c1a\u65b9\u5251\u6765\uff0c\u547d\u65d7\u724c\u5b98\u5c06\u6bdb\u6587\u9f99\u5728\u5e10\u524d\u65a9\u51b3\uff0c\u5411\u6bdb\u6587\u9f99\u90e8\u5c5e\u8c15\u793a\uff1a\u201c\u53ea\u8bdb\u6bdb\u6587\u9f99\u4e00\u4eba\uff0c\u5176\u4f59\u5404\u4eba\u4e00\u6982\u65e0\u7f6a\u3002\u201d\u6bdb\u6587\u9f99\u9ebe\u4e0b\u5c06\u58eb\u65e0\u4e00\u6562\u52a8\u3002\u8881\u5d07\u7115\u547d\u4eba\u6536\u6b93\u6bdb\u6587\u9f99\uff0c\u6b21\u65e5\u5f00\u540a\u62dc\u5960\uff0c\u8bf4\uff1a\u201c\u6628\u65e5\u65a9\u4f60\uff0c\u662f\u4e3a\u4e86\u671d\u5ef7\u5927\u6cd5\u3002\u4eca\u65e5\u796d\u4f60\uff0c\u662f\u4e3a\u4e86\u50da\u53cb\u79c1\u60c5\u3002\u201d \u3000\u3000\u968f\u5373\u5c06\u6bdb\u90e8\u5206\u4e3a\u56db\u961f\uff0c\u6d3e\u6bdb\u6587\u9f99\u7684\u513f\u5b50\u6bdb\u627f\u7984\u3001\u526f\u5c06\u9648\u7ee7\u76db\u7b49\u56db\u4eba\u5206\u9886\uff0c\u7292\u8d4f\u519b\u58eb\uff0c\u5c3d\u9664\u76ae\u5c9b\u6bdb\u6587\u9f99\u7684\u8650\u653f\u3002\u56de\u5b81\u8fdc\u540e\u4e0a\u594f\u7980\u62a5\uff0c\u6700\u540e\u8bf4\uff1a\u6bdb\u6587\u9f99\u662f\u5927\u5c06\uff0c\u4e0d\u662f\u81e3\u6709\u6743\u53ef\u4ee5\u64c5\u81ea\u8bdb\u6740\u7684\u3002\u81e3\u72af\u4e86\u6b7b\u7f6a\uff0c\u8c28\u5019\u7687\u4e0a\u60e9\u5904\u3002 \u3000\u3000\u5d07\u796f\u5f97\u8baf\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u975e\u5e38\u4e0d\u4ee5\u4e3a\u7136\u3002\u4f46\u60f3\u6bdb\u6587\u9f99\u5df2\u7ecf\u6b7b\u4e86\uff0c\u76ee\u524d\u53c8\u6b63\u501a\u8d56\u8881\u5d07\u7115\u5c3d\u529b\uff0c\u53ea\u5f97\u4e0b\u65e8\u5609\u5956\u4ed6\u4e00\u756a\uff0c\u53c8\u4e0b\u65e8\u516c\u5e03\u6bdb\u6587\u9f99\u7684\u7f6a\u72b6\uff0c\u902e\u6355\u6bdb\u6587\u9f99\u7684\u9a7b\u4eac\u529e\u4e8b\u5904\u4e3b\u4efb\uff0c\u4ee5\u5b89\u8881\u5d07\u7115\u4e4b\u5fc3\u3002 \u3000\u3000\u8881\u5d07\u7115\u62c5\u5fc3\u6bdb\u6587\u9f99\u7684\u90e8\u4e0b\u751f\u53d8\uff0c\u594f\u8bf7\u589e\u52a0\u9977\u94f6\u3002\u4f46\u67e5\u6838\u90e8\u961f\u5b9e\u6570\uff0c\u5175\u5458\u6bd4\u6bdb\u6587\u9f99\u865a\u62a5\u65f6\u5c11\u5f97\u591a\u4e86\u3002\u5d07\u796f\u89c1\u5175\u5458\u5c11\u4e86\uff0c\u9977\u94f6\u53cd\u589e\uff0c\u9887\u4e3a\u6000\u7591\uff0c\u4f46\u90fd\u4e00\u4e00\u6279\u51c6\u3002\u4ee5\u5d07\u796f\u8fd9\u6837\u521a\u5f3a\u7684\u6027\u683c\uff0c\u8fd9\u65f6\u8feb\u4e8e\u5f62\u52bf\u800c\u4e0d\u6562\u5f97\u7f6a\u8881\u5d07\u7115\uff0c\u5b9e\u5df2\u6df1\u6df1\u4f0f\u4e0b\u4e86\u6740\u673a\u3002 \u3000\u3000\u6bdb\u6587\u9f99\u5728\u76ae\u5c9b\uff0c\u4fe8\u7136\u662f\u72ec\u7acb\u4e3a\u738b\u7684\u6a21\u6837\uff0c\u4e0d\u63a5\u53d7\u671d\u5ef7\u6d3e\u6587\u5b98\u76d1\u5bdf\u6838\u6570\u3001\u6ee5\u6740\u96be\u6c11\u5192\u529f\u3001\u4fb5\u541e\u519b\u7cae\u3001\u519b\u7eaa\u4e0d\u8083\uff0c\u7684\u786e\u6709\u7f6a\u3002\u4f46\u8881\u5d07\u7115\u4ee5\u5c1a\u65b9\u5251\u65a9\u4ed6\u7684\u65b9\u5f0f\uff0c\u5374\u4e5f\u672a\u514d\u592a\u620f\u5267\u5316\u4e86\u4e9b\u3002\u660e\u671d\u8d50\u5c1a\u65b9\u5251\u7ed9\u4e3b\u5e05\uff0c\u7528\u610f\u662f\u7ed9\u4e3b\u5e05\u4ee5\u7edd\u5bf9\u6743\u5a01\uff0c\u90e8\u5c06\u5982\u4e0d\u542c\u6307\u6325\uff0c\u7acb\u5373\u53ef\u4ee5\u8bdb\u6740\u3002\u7136\u800c\u6bdb\u6587\u9f99\u7684\u7f6a\u884c\u90fd\u975e\u7d27\u6025\uff0c\u4e5f\u4e0d\u662f\u53cd\u53db\u4f5c\u4e71\u3002\u4f55\u51b5\u6bdb\u6587\u9f99\u4e5f\u662f\u53d7\u8d50\u5c1a\u65b9\u5251\u7684\u3002 \u3000\u3000\u6bdb\u6587\u9f99\u5728\u76ae\u5c9b\uff0c\u6bd5\u7adf\u66fe\u5c61\u6b21\u51fa\u5175\uff0c\u9a9a\u6270\u6ee1\u6e05\u540e\u65b9\uff0c\u662f\u5f53\u65f6\u6d77\u4e0a\u60df\u4e00\u7684\u4e00\u652f\u673a\u52a8\u6e38\u51fb\u961f\uff0c\u6ee1\u6e05\u5bf9\u4ed6\u4e5f\u4e00\u76f4\u9887\u4e3a\u91cd\u89c6\u5fcc\u60ee\u3002 \u3000\u3000\u8fd9\u5341\u4e8c\u6761\u7f6a\u72b6\u4e2d\uff0c\u6709\u51e0\u6761\u5e73\u5fc3\u800c\u8bba\u5e76\u4e0d\u80fd\u6210\u7acb\u3002\u6bdb\u6587\u9f99\u8bf4\u53d6\u767b\u5dde\u3001\u5357\u4eac\u5982\u53cd\u638c\uff0c\u53ea\u4e0d\u8fc7\u4e00\u65f6\u5938\u53e3\uff0c\u5e76\u975e\u771f\u7684\u8981\u9020\u53cd\uff1b\u5411\u5916\u56fd\u4e70\u9a6c\uff0c\u5f53\u662f\u519b\u4e2d\u9700\u8981\uff1b\u64c5\u81ea\u5c01\u5b98\u662f\u5f97\u5230\u671d\u5ef7\u6388\u6743\u7684\uff0c\u90e8\u5c06\u559c\u6b22\u59d3\u6bdb\uff0c\u65e8\u5728\u62cd\u4e3b\u5e05\u7684\u9a6c\u5c41\uff0c\u4e5f\u6ca1\u6709\u751a\u4e48\u5927\u4e0d\u4e86\uff1b\u4e0d\u80fd\u6062\u590d\u5bf8\u571f\uff0c\u53ea\u80fd\u8bf4\u4ed6\u65e0\u80fd\uff0c\u5374\u975e\u6709\u7f6a\uff0c\u8981\u6253\u8d25\u6e05\u5175\uff0c\u6062\u590d\u5931\u5730\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f\u5728\u5c9b\u4e0a\u4e3a\u9b4f\u5fe0\u8d24\u5851\u50cf\uff0c\u66f4\u96be\u4ee5\u52a0\u4ed6\u7f6a\u540d\u3002\u5929\u542f\u5e74\u95f4\uff0c\u9b4f\u5fe0\u8d24\u6743\u52bf\u718f\u5929\uff0c\u5404\u7701\u7763\u629a\u90fd\u4e3a\u9b4f\u5fe0\u8d24\u5efa\u751f\u7960\u3001\u5851\u50cf\u800c\u5411\u4ed6\u8dea\u62dc\u3002\u5f53\u65f6\u8881\u5d07\u7115\u5728\u5b81\u8fdc\u4e5f\u5efa\u4e86\u9b4f\u5fe0\u8d24\u7684\u751f\u7960\u3002\u65f6\u52bf\u6240\u7136\uff0c\u4eba\u4eba\u96be\u514d\u3002 \u3000\u3000\u6bdb\u6587\u9f99\u6b7b\u540e\uff0c\u90e8\u5c06\u5fc3\u4e2d\u4e0d\u670d\uff0c\u9887\u6709\u9010\u6e10\u53db\u53bb\u7684\uff0c\u5176\u4e2d\u91cd\u8981\u7684\u53db\u5c06\u6709\u5b54\u6709\u5fb7\u3001\u803f\u4ef2\u660e\u3001\u5c1a\u53ef\u559c\u3002\u8fd9\u4e09\u4eba\u6295\u964d\u6ee1\u6e05\uff0c\u4e3a\u6e05\u671d\u51fa\u4e86\u5f88\u5927\u529b\u6c14\uff0c\u540e\u6765\u90fd\u5c01\u738b\u3002\u6e05\u521d\u56db\u5927\u964d\u738b\uff0c\u9664\u5434\u4e09\u6842\u5916\uff0c\u5176\u4f59\u5b54\u3001\u803f\u3001\u5c1a\u4e09\u4eba\u90fd\u662f\u6bdb\u6587\u9f99\u7684\u65e7\u90e8\u3002\u4e0d\u8fc7\u8fd9\u4e5f\u4e0d\u80fd\u8bf4\u662f\u8881\u5d07\u7115\u7684\u8fc7\u5931\u2467\u3002 \u3000\u3000\u5bf9\u4e8e\u201c\u6740\u6bdb\u4e8b\u4ef6\u201d\uff0c\u5f53\u65f6\u8206\u8bba\u5927\u90fd\u540c\u60c5\u6bdb\u3002\u4e00\u822c\u671d\u81e3\u8ba4\u4e3a\uff0c\u6bdb\u6587\u9f99\u5373\u4f7f\u6709\u7f6a\uff0c\u4ed6\u662f\u4e00\u4e2a\u5927\u519b\u533a\u53f8\u4ee4\uff0c\u4e5f\u53ea\u80fd\u7531\u7687\u5e1d\u4e0b\u65e8\u8bdb\u6740\u3002\u7687\u5e1d\u7684\u7edf\u6cbb\u624b\u6bb5\uff0c\u4e3b\u8981\u53ea\u662f\u8d4f\u4e0e\u7f5a\u3002\u8881\u5d07\u7115\u64c5\u6740\u5927\u5c06\uff0c\u662f\u4e25\u91cd\u7684\u4fb5\u72af\u4e86\u541b\u6743\u3002 \u3000\u3000\u6211\u4e5f\u89c9\u5f97\u8881\u5d07\u7115\u8fd9\u4ef6\u4e8b\u505a\u5f97\u4e0d\u5bf9\uff0c\u8fc7\u5206\u7684\u6a2a\u86ee\u3002\u5c06\u6bdb\u6587\u9f99\u902e\u6355\uff0c\u62bc\u89e3\u5317\u4eac\uff0c\u4ea4\u7531\u7687\u5e1d\u53bb\u5904\u7f6e\uff0c\u624d\u662f\u5408\u7406\u7684\u65b9\u5f0f\u3002\u5f53\u65f6\u5c0f\u8bf4\u76db\u884c\uff0c\u6709\u4eba\u505a\u4e86\u5c0f\u8bf4\u6765\u79f0\u8a89\u6bdb\u6587\u9f99\u3002\u4e00\u90e8\u662f\u56db\u5341\u56de\u7684\u300a\u8fbd\u6d77\u4e39\u5fe0\u5f55\u300b\uff0c\u662f\u676d\u5dde\u4eba\u9646\u4e91\u9f99\u6240\u4f5c\uff0c\u5927\u6367\u5411\u4e61\u6bdb\u5e05\u3002\u53e6\u4e00\u90e8\u662f\u4f5c\u8005\u4e0d\u7f72\u540d\u7684\u300a\u94c1\u51a0\u56fe\u300b\uff08\u4e0d\u662f\u8bb2\u674e\u81ea\u6210\u4e8b\u8ff9\u7684\u90a3\u4e00\u90e8\uff09\uff0c\u4ee5\u6bdb\u6587\u9f99\u4e3a\u4e3b\u89d2\u3002 \u3000\u3000\u5f53\u65f6\u5927\u540d\u58eb\u9648\u7709\u516c\u5bf9\u201c\u6740\u6bdb\u4e8b\u4ef6\u201d\u62a8\u51fb\u751a\u70c8\u3002\u53e6\u4e00\u4e2a\u5927\u540d\u58eb\u94b1\u8c26\u76ca\u662f\u6bdb\u6587\u9f99\u7684\u670b\u53cb\uff0c\u5bf9\u671d\u91ce\u8206\u8bba\u5f53\u7136\u4e5f\u6709\u5f71\u54cd\u3002\u300a\u660e\u5b63\u5317\u7565\u300b\u751a\u81f3\u8bf4\uff1a\u8881\u5d07\u7115\u634f\u9020\u5341\u4e8c\u6761\u7f6a\u540d\u6765\u5bb3\u6b7b\u4e86\u6bdb\u6587\u9f99\uff0c\u4e0e\u79e6\u6867\u4ee5\u5341\u4e8c\u9053\u91d1\u724c\u6765\u5bb3\u6b7b\u4e86\u5cb3\u98de\u5b8c\u5168\u4e00\u6837\u3002\u5374\u53c8\u662f\u8fc7\u5206\u7684\u6279\u8bc4\u4e86\u3002 \u3000\u3000\u63a8\u6d4b\u8881\u5d07\u7115\u6240\u4ee5\u7528\u8fd9\u6837\u7684\u65ad\u7136\u624b\u6bb5\u6740\u6bdb\uff0c\u9996\u5148\u662f\u51fa\u4e8e\u4ed6\u521a\u5f3a\u679c\u51b3\u7684\u6027\u683c\u3002\u5176\u6b21\uff0c\u6587\u4eba\u5e26\u5175\uff0c\u4e00\u5b9a\u719f\u8bfb\u5b59\u5b50\u5175\u6cd5\uff0c\u5bf9\u4e8e\u5b59\u5b50\u6740\u5434\u738b\u7231\u59ec\u4e8c\u4eba\u3001\u56e0\u800c\u4f7f\u5f97\u5bab\u4e2d\u7f8e\u5973\u5c3d\u7686\u51db\u9075\u519b\u6cd5\u7684\u6545\u4e8b\uff0c\u5bf9\u4e8e\u201c\u5c06\u5728\u519b\uff0c\u541b\u547d\u6709\u6240\u4e0d\u53d7\u201d\u7684\u519b\u6cd5\u89c2\u5ff5\uff0c\u4e00\u5b9a\u5370\u8c61\u5341\u5206\u6df1\u523b\u3002\u90a3\u65f6\u5019\u5b81\u8fdc\u3001\u9526\u5dde\u3001\u84df\u5dde\u5404\u5904\u519b\u4e8b\u8981\u5730\u90fd\u66fe\u53d1\u751f\u5175\u53d8\uff0c\u5982\u4e0d\u6574\u996c\u519b\u7eaa\uff0c\u6839\u672c\u4e0d\u80fd\u6253\u4ed7\u3002\u8881\u5d07\u7115\u660e\u77e5\u8fd9\u6837\u505a\u4e0d\u5bf9\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u6740\u6bdb\uff0c\u63a8\u60f3\u8d77\u6765\uff0c\u4e5f\u6709\u81ea\u6043\u5d07\u796f\u5948\u4f55\u4ed6\u4e0d\u5f97\u7684\u6210\u5206\u3002\u6700\u540e\uff0c\u6bdb\u6587\u9f99\u63a5\u8fd1\u9b4f\u5fe0\u8d24\uff0c\u8881\u5d07\u7115\u63a5\u8fd1\u4e1c\u6797\u6e05\u6d41\uff0c\u5176\u4e2d\u4e5f\u96be\u514d\u6709\u4e9b\u515a\u6d3e\u6210\u89c1\u3002 \u3000\u3000\u2460\u7763\u5e08\u672c\u6765\u6bd4\u603b\u7763\u7565\u9ad8\uff0c\u4f46\u5728\u4e8e\u8c26\u7684\u65f6\u5019\u8fd8\u6ca1\u6709\u8bbe\u7763\u5e08\u5f53\u65f6\u603b\u7763\u662f\u5730\u4f4d\u6700\u9ad8\u7684\u5e26\u5175\u6587\u5b98\u3002\u89c1\u5434\u6657\uff1a\u300a\u660e\u4ee3\u7684\u519b\u5175\u300b\u3002 \u3000\u3000\u2461\u5373\u4eca\u8fbd\u5b81\u7701\u5b89\u4e1c\u4e4b\u5317\u7684\u4e5d\u8fde\u57ce\uff0c\u4e0e\u671d\u9c9c\u7684\u4e49\u5dde\u9694\u9e2d\u7eff\u6c5f\u76f8\u5bf9\u3002 \u3000\u3000\u2462\u76ae\u5c9b\u5728\u671d\u9c9c\u5199\u4f5c\u6934\u5c9b\u3002\u8fd9\u4e2a\u201c\u6934\u201d\u5b57\uff0c\u6c49\u6587\u97f3\u201c\u9a7e\u201d\uff0c\u4f46\u671d\u9c9c\u4eba\u8bfb\u4f5c\uff30\uff49\u97f3\uff0c\u6240\u4ee5\u4e2d\u56fd\u4eba\u5c31\u7b80\u79f0\u4e3a\u76ae\u5c9b\u3002\u6709\u4e00\u672c\u76f8\u5f53\u6d41\u884c\u7684\u8bb2\u6e05\u53f2\u7684\u901a\u4fd7\u8457\u4f5c\u8bf4\u76ae\u5c9b\u5373\u6d77\u6d0b\u5c9b\uff0c\u5730\u7406\u5f04\u9519\u4e86\u3002\u6d77\u6d0b\u5c9b\u5728\u76ae\u5c9b\u548c\u5927\u8fde\u4e4b\u95f4\uff0c\u79bb\u76ae\u5c9b\u7ea6\u4e00\u767e\u6d77\u91cc\u3002\u76ae\u5c9b\u662f\u671d\u9c9c\u5730\u65b9\uff0c\u6d77\u6d0b\u5c9b\u662f\u4e2d\u56fd\u5730\u65b9\u3002 \u3000\u3000\u2463\u636e\u671d\u9c9c\u6d3e\u53bb\u76ae\u5c9b\u7684\u4f7f\u8005\u8bb0\u8f7d\uff1a\u6bdb\u6587\u9f99\u6bcf\u5929\u5403\u4e94\u9910\uff0c\u5176\u4e2d\u4e09\u9910\u6709\u83dc\u80b4\u4e94\u516d\u5341\u54c1\uff0c\u5ba0\u59be\u516b\u4e5d\u4eba\uff0c\u73e0\u7fe0\u6ee1\u8eab\uff0c\u4f8d\u5973\u751a\u591a\u3002 \u3000\u3000\u2464\u4e00\u822c\u4e66\u7c4d\uff08\u5305\u62ec\u300a\u660e\u53f2\u300b\uff09\u4e0a\u8bb0\u8f7d\uff0c\u90fd\u8bf4\u8881\u6bdb\u7684\u4f1a\u6664\u5730\u662f\u5728\u53cc\u5c9b\u3002\u300a\u8346\u9a7c\u9038\u53f2\u300b\u4e2d\u8f91\u6709\u300a\u8881\u7763\u5e08\u8ba1\u65a9\u6bdb\u6587\u9f99\u59cb\u672b\u8bb0\u300b\u4e00\u6587\uff0c\u91c7\u7528\u7684\u662f\u65e5\u8bb0\u4f53\uff0c\u4ece\u4e94\u6708\u4e8c\u5341\u4e8c\u65e5\u8881\u5d07\u7115\u51fa\u53d1\u5230\u516d\u6708\u5341\u4e00\u65e5\u56de\u5b81\u8fdc\uff0c\u9010\u65e5\u8bb0\u5f55\u6d77\u7a0b\u3001\u6240\u7ecf\u5c9b\u5c7f\u3001\u98ce\u52bf\u3001\u8239\u53ea\u3001\u5175\u5458\u3001\u5b98\u5458\u59d3\u540d\u7b49\u7b49\uff0c\u5341\u5206\u8be6\u5c3d\uff0c\u4f5c\u8005\u663e\u7136\u662f\u8881\u5d07\u7115\u968f\u884c\u7684\u5e55\u50da\u6216\u90e8\u5c5e\u3002\u4ed6\u5199\u4f5c\u6001\u5ea6\u5f02\u5e38\u5fe0\u5b9e\uff0c\u5bf9\u4e8e\u8881\u6bdb\u5bc6\u8c08\u4e09\u65e5\u4e09\u591c\uff0c\u53ea\u8bb0\u4e24\u4eba\u201c\u4e8c\u66f4\u540e\u65b9\u6563\u201d\u3001\u201c\u5bc6\u8bed\u4e09\u66f4\u65b9\u6563\u201d\uff0c\u8bb0\u5f55\u4e24\u4eba\u5bc6\u8c08\u540e\u7684\u795e\u8272\uff0c\u5374\u4e0d\u8bb0\u5bc6\u8bed\u5185\u5bb9\uff0c\u5168\u5929\u51ed\u7a7a\u63a8\u6d4b\u7684\u8a00\u8f9e\uff0c\u5408\u4e8e\u73b0\u4ee3\u8981\u6c42\u6700\u4e25\u683c\u7684\u62a5\u5bfc\u4f53\u3002\u8be5\u4e66\u8bb0\u8f7d\u8881\u6bdb\u76f8\u4f1a\u7684\u5730\u70b9\u662f\u5728\u5c9b\u5c71\uff0c\u79bb\u65c5\u987a\u9646\u8def\u5341\u516b\u91cc\uff0c\u6c34\u8def\u56db\u5341\u91cc\uff0c\u8ddd\u53cc\u5c9b\u6709\u534a\u65e5\u6c34\u7a0b\uff0c\u4e2d\u95f4\u9694\u4e86\u677e\u6728\u5c9b\u3001\u732a\u5c9b\u3001\u86c7\u5c9b\u3001\u867e\u87c6\u5c9b\u7b49\u8bb8\u591a\u5c9b\u5c7f\u3002\u6211\u6bd4\u8f83\u5404\u79cd\u8d44\u6599\uff0c\u89c9\u5f97\u5c9b\u5c71\u7684\u8bf4\u6cd5\u66f4\u4e3a\u53ef\u4fe1\u3002 \u3000\u3000\u2465\u300a\u59cb\u672b\u8bb0\u300b\u8bb0\u8f7d\u5f53\u65f6\u60c5\u5f62\u8bf4\uff1a\u201c\u9152\u53d9\u81f3\u7ec8\uff0c\uff08\u8881\uff09\u65b9\u6709\u50b2\u72b6\uff0c\u6bdb\u5e05\u6709\u4e0d\u60a6\u610f\u6001\u3002\u201d \u3000\u3000\u2466\u540e\u6765\u5927\u5927\u6709\u540d\u7684\u5b54\u6709\u5fb7\u3001\u803f\u7cbe\u5fe0\u3001\u5c1a\u53ef\u559c\u90fd\u662f\u6bdb\u6587\u9f99\u7684\u4e49\u5b59\uff0c\u90a3\u65f6\u53eb\u505a\u6bdb\u6709\u5fb7\u3001\u6bdb\u7cbe\u5fe0\u3001\u6bdb\u53ef\u559c\u3002 \u3000\u3000\u2467\u6881\u542f\u8d85\u5728\u300a\u8881\u5d07\u7115\u4f20\u300b\u4e2d\u8bf4\uff1a\u201c\u543e\u4ee5\u4e3a\u6b64\u4ea6\u5b58\u4e4e\u5176\u4eba\u8033\u3002\u6bdb\u6587\u9f99\u4e0d\u6b7b\uff0c\u5b89\u77e5\u5176\u4e0d\u6267\u68b1\uff08\u6ce8\uff1a\u539f\u5b57\u5b9e\u5728\u662f\u6253\u4e0d\u3000\u51fa\u6765\uff0c\u5e94\u8be5\u5728\u201c\u56f0\u201d\u5b57\u90e8\u9996\u4e0b\u52a0\u4e00\u8d70\u4e4b\u513f\uff0c\u4fd7\u79f0\u8d70\u4e4b\u5e95\u3002\u539f\u5904\u5904\u7406\u4e3a\u7a7a\uff09\u4e3a\u8bf8\u964d\u738b\u957f\uff1f\u201d\u610f\u601d\u8bf4\uff0c\u6bdb\u6587\u9f99\u5982\u679c\u4e0d\u6b7b\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u53cd\u800c\u662f\u7b2c\u4e00\u5927\u964d\u738b\u5462\u3002\u7136\u800c\u8fd9\u4e5f\u662f\u63e3\u6d4b\u4e4b\u8f9e\u4e86\u3002 \u3000\u3000\u5341\u4e8c \u3000\u3000\u8fd9\u65f6\u5019\u671d\u5ef7\u53c8\u6b20\u9977\u4e0d\u53d1\u4e86\u3002\u8881\u5d07\u7115\u518d\u4e0a\u594f\u7ae0\uff0c\u6df1\u6df1\u5fe7\u8651\u53c8\u4f1a\u53d1\u751f\u5175\u53d8\uff0c\u66f4\u5fe7\u8651\u5175\u5352\u54d7\u53d8\u540e\u4e0d\u518d\u63a5\u53d7\u5b89\u629a\uff0c\u4ece\u6b64\u53d8\u4e3a\u201c\u5927\u76d7\u201d\u3002\u4ed6\u8bf4\u4e00\u5b9a\u8981\u53d1\u751f\u4e00\u6b21\u5175\u53d8\uff0c\u624d\u53d1\u4e00\u6b21\u6b20\u9977\uff0c\u800c\u53d1\u4e86\u6b20\u9977\u4e4b\u540e\uff0c\u53c8\u4e00\u5b9a\u5c06\u8d1f\u8d23\u5b98\u5458\u6349\u53bb\u6740\u4e86\u4e00\u6279\uff0c\u8fd9\u6837\u4e0b\u53bb\uff0c\u6c38\u8fdc\u662f\u201c\u6b20\u9977\u2014\u2014\u5175\u53d8\u2014\u2014\u53d1\u9977\u2014\u2014\u6740\u5b98\u2014\u2014\u6b20\u9977\u201d\u7684\u5faa\u73af\u2460\u3002\u8fd9\u9053\u594f\u7ae0\uff0c\u5f53\u7136\u53ea\u6709\u518d\u5ea6\u52a0\u6df1\u5d07\u796f\u5bf9\u4ed6\u7684\u618e\u6068\u3002 \u3000\u3000\u5d07\u796f\u4e8c\u5e74\u6625\uff0c\u8881\u5d07\u7115\u4e0a\u594f\uff0c\u8bf4\u5c71\u6d77\u5173\u4e00\u5e26\u9632\u52a1\u5de9\u56fa\uff0c\u5df2\u4e0d\u8db3\u8651\uff0c\u4f46\u84df\u95e8\u5355\u5f31\uff0c\u987b\u9632\u654c\u4eba\u4ece\u897f\u8def\u8fdb\u653b\u3002\u8fd9\u65f6\u84df\u8fbd\u603b\u7763\u662f\u5218\u7b56\uff0c\u61e6\u5f31\u800c\u4e0d\u61c2\u519b\u4e8b\u3002\u8881\u5d07\u7115\u770b\u5230\u4e86\u9632\u52a1\u5f31\u70b9\u7684\u6240\u5728\uff0c\u7b2c\u4e00\u9053\u594f\u7ae0\u4e0a\u53bb\uff0c\u671d\u5ef7\u6ca1\u6709\u591a\u52a0\u7406\u4f1a\uff0c\u4ed6\u518d\u4e0a\u7b2c\u4e8c\u9053\u3001\u7b2c\u4e09\u9053\u3002\u5d07\u796f\u4e0b\u65e8\u4ea4\u7531\u90e8\u79d1\u5546\u8bae\u529e\u7406\uff0c\u4f46\u59cb\u7ec8\u8fc1\u5ef6\u4e0d\u884c\u3002\u62d6\u5230\u5341\u6708\uff0c\u6e05\u5175\u679c\u7136\u5927\u4e3e\u4ece\u897f\u8def\u5165\u72af\uff0c\u6b63\u5728\u8881\u5d07\u7115\u6599\u4e2d\u3002\u9996\u5f53\u5176\u51b2\u7684\uff0c\u6b63\u662f\u521a\u521a\u53d1\u751f\u8fc7\u7d22\u9977\u5175\u53d8\u7684\u9075\u5316\u3002 \u3000\u3000\u660e\u671d\u521d\u5e74\u4e3a\u4e86\u9632\u5907\u8499\u53e4\u4eba\uff0c\u5bf9\u5317\u65b9\u8fb9\u9632\u662f\u5168\u529b\u6ce8\u610f\u7684\uff0c\u597d\u597d\u4fee\u7b51\u4e86\u957f\u57ce\uff0c\u8bbe\u7acb\u8fbd\u4e1c\u3001\u84df\u5dde\u3001\u5ba3\u5e9c\u3001\u5927\u540c\u3001\u592a\u539f\uff08\u7edf\u504f\u5934\u3001\u5b81\u6b66\u3001\u96c1\u95e8\u4e09\u5173\uff09\u3001\u9655\u897f\u3001\u5ef6\u7ee5\u3001\u5b81\u590f\u3001\u7518\u8083\u4e5d\u5927\u8fb9\u9632\u519b\u533a\uff0c\u90a3\u4fbf\u662f\u6240\u8c13\u201c\u4e5d\u8fb9\u201d\u3002\u4e1c\u8d77\u9e2d\u7eff\u6c5f\uff0c\u897f\u81f3\u9152\u6cc9\uff0c\u7ef5\u5ef6\u6570\u5343\u91cc\u4e2d\uff0c\u4e00\u5821\u4e00\u5be8\u90fd\u5206\u5175\u9a7b\u5b88\u3002\u4f46\u540e\u6765\u6ce8\u610f\u529b\u96c6\u4e2d\u4e8e\u8fbd\u4e1c\uff0c\u5176\u4ed6\u516b\u9547\u7684\u9632\u52a1\u5c31\u5e9f\u5f1b\u4e86\u3002 \u3000\u3000\u660e\u592a\u7956\u672c\u6765\u5efa\u90fd\u5357\u4eac\uff0c\u6210\u7956\u56e0\u4e3a\u5728\u5317\u4eac\u8d77\u5bb6\uff0c\u5c06\u90fd\u57ce\u8fc1\u4e86\u8fc7\u53bb\u3002\u5728\u4e2d\u56fd\u6574\u4e2a\u5730\u5f62\u4e0a\uff0c\u5317\u4eac\u504f\u4e8e\u4e1c\u5317\uff0c\u548c\u8d22\u8d4b\u6765\u6e90\u7684\u4e1c\u5357\u76f8\u8ddd\u751a\u8fdc\u3002\u6700\u4e0d\u5229\u7684\u662f\uff0c\u5317\u4eac\u79bb\u56fd\u9632\u7b2c\u4e00\u7ebf\u7684\u957f\u57ce\u53ea\u6709\u4e00\u767e\u591a\u91cc\uff0c\u654c\u519b\u4e00\u653b\u7834\u957f\u57ce\uff0c\u5feb\u9a6c\u5954\u9a70\u534a\u5929\uff0c\u5c31\u5175\u4e34\u5317\u4eac\u57ce\u4e0b\u3002\u91d1\u5143\u4e24\u671d\u4ee5\u5317\u4eac\u4e3a\u9996\u90fd\uff0c\u56e0\u4e3a\u5b83\u4eec\u662f\u6765\u81ea\u5317\u65b9\u7684\u6e38\u7267\u6c11\u65cf\uff0c\u4e0d\u6562\u6df1\u5165\u4e2d\u539f\uff0c\u4e00\u65e6\u6709\u53d8\uff0c\u53ef\u4ee5\u7acb\u523b\u8f6c\u8eab\u9003\u56de\u672c\u571f\u3002\u660e\u671d\u7684\u60c5\u51b5\u5374\u6839\u672c\u4e0d\u540c\u3002\u6210\u7956\u5bf9\u8499\u53e4\u91c7\u53d6\u653b\u52bf\uff0c\u5efa\u90fd\u5317\u4eac\u4fbf\u4e8e\u8fdb\u653b\uff0c\u540e\u6765\u5175\u529b\u8870\u5f31\uff0c\u5317\u4eac\u5730\u52bf\u4e0a\u7684\u5f31\u70b9\u7acb\u523b\u66b4\u9732\u65e0\u9057\u2461\u3002\u672c\u6765\uff0c\u4e24\u4e2a\u4e92\u76f8\u654c\u5bf9\u7684\u793e\u4f1a\u662f\u4e0d\u53ef\u80fd\u957f\u671f\u5bf9\u5cd9\u7684\uff0c\u50f5\u6301\u4e00\u6bb5\u65f6\u671f\u4e4b\u540e\uff0c\u7ec8\u7a76\u662f\u975e\u8fdb\u5219\u9000\u2462\u3002\u660e\u671d\u65e2\u575a\u51b3\u4e0d\u80af\u548c\u6ee1\u6e05\u8bae\u548c\uff0c\u5f62\u52bf\u4e0a\u53c8\u65e0\u529b\u8fdb\u653b\uff0c\u518d\u5c06\u4eac\u57ce\u66b4\u9732\u5728\u654c\u4eba\u5927\u5175\u56e2\u671d\u53d1\u5915\u81f3\u7684\u6781\u8fd1\u8ddd\u79bb\u4e4b\u5185\uff0c\u6839\u672c\u6218\u7565\u5b8c\u5168\u9519\u8bef\u3002\u4ee5\u6c49\u4eba\u4e3a\u4e3b\u7684\u4e2d\u534e\u6c11\u65cf\u6240\u4ee5\u4f1f\u5927\uff0c\u4e3b\u8981\u662f\u5728\u6587\u6cbb\u6559\u5316\uff0c\u5f81\u6218\u672c\u975e\u6240\u957f\u2463\uff0c\u5982\u679c\u57fa\u672c\u6218\u7565\u4e00\u9519\uff0c\u5c40\u52bf\u5c31\u96be\u4ee5\u6536\u62fe\u4e86\u3002 \u3000\u3000\u8fd9\u6b21\u8fdb\u519b\u7687\u592a\u6781\u4eb2\u81ea\u5e26\u5175\uff0c\u96c6\u5175\u5341\u4f59\u4e07\uff0c\u77e5\u9053\u8881\u5d07\u7115\u5b88\u5728\u4e1c\u8def\uff0c\u653b\u6253\u4e0d\u8fdb\uff0c\u4e8e\u662f\u7531\u8499\u53e4\u5175\u4f5c\u5148\u5bfc\uff0c\u7ed5\u9053\u897f\u8def\u8fdb\u653b\u3002\u51fa\u53d1\u524d\u5bf9\u738b\u516c\u5927\u81e3\u8bf4\uff1a\u201c\u660e\u671d\u82e5\u662f\u80af\u548c\uff0c\u6211\u4eec\u91c7\u53c2\u5f00\u77ff\uff0c\u4e0e\u4ed6\u4eec\u4ea4\u6613\uff0c\u6362\u6765\u5e03\u5339\uff0c\u5927\u5bb6\u5171\u4eab\u592a\u5e73\uff0c\u5c82\u4e0d\u6781\u597d\uff1f\u4f46\u6211\u51e0\u6b21\u4e09\u756a\u7684\u6c42\u548c\uff0c\u660e\u671d\u603b\u662f\u4e0d\u5141\uff0c\u8fd9\u6b21\u975e\u72e0\u72e0\u6253\u4e00\u4ed7\u4e0d\u53ef\u3002\u201d\u5341\u6708\u521d\u4e94\uff0c\u62b5\u8fbe\u5580\u5587\u6c81\u7684\u9752\u57ce\u3002\u8fd9\u6761\u8def\u5f88\u8fdc\uff0c\u884c\u519b\u4e0d\u4fbf\uff0c\u8bf8\u5c06\u89c1\u5230\u4e86\u524d\u9014\u7684\u8270\u96be\uff0c\u4e0d\u5c11\u4eba\u4fbf\u4e3b\u5f20\u9000\u5175\uff0c\u5176\u4e2d\u4ee5\u4ee3\u5584\u53ca\u83bd\u53e4\u5c14\u6cf0\u4e24\u5927\u8d1d\u52d2\u4e3b\u5f20\u6700\u529b\uff0c\u8ba4\u4e3a\uff1a\u6df1\u5165\u654c\u5883\uff0c\u52b3\u5e08\u88ad\u8fdc\uff0c\u5982\u679c\u7cae\u532e\u9a6c\u75b2\uff0c\u53c8\u600e\u4e48\u56de\u5f97\u53bb\uff1f\u7eb5\u4f7f\u653b\u8fdb\u4e86\u957f\u57ce\uff0c\u660e\u4eba\u52bf\u5fc5\u805a\u96c6\u5404\u8def\u5175\u9a6c\u56f4\u653b\uff0c\u6211\u4eec\u4fbf\u4f17\u5be1\u4e0d\u654c\uff0c\u8981\u662f\u540e\u8def\u906d\u5230\u5835\u622a\uff0c\u6050\u65e0\u5f52\u8def\u3002\u91d1\u4eba\u7684\u6839\u672c\u662f\u5728\u8fbd\u5b81\u3001\u5409\u6797\u4e00\u5e26\u3002\u4ece\u5c71\u6d77\u5173\u8fdb\u653b\u5317\u4eac\uff0c\u90a3\u662f\u5b89\u5168\u7684\u8fdb\u519b\u8def\u7ebf\uff0c\u5982\u679c\u6253\u4e0d\u80dc\uff0c\u9000\u56de\u53bb\u5c31\u662f\u4e86\u3002\u73b0\u5728\u8fdc\u8fdc\u7684\u7ed5\u9053\u8499\u53e4\uff0c\u5f53\u65f6\u8fd0\u8f93\u5de5\u5177\u7b80\u964b\uff0c\u7cae\u8349\u5f88\u5bb9\u6613\u63a5\u6d4e\u4e0d\u4e0a\u3002\u90a3\u65f6\u4ee3\u5584\u56db\u5341\u4e5d\u5c81\uff0c\u662f\u7687\u592a\u6781\u7684\u4e8c\u54e5\uff0c\u83bd\u53e4\u5c14\u6cf0\u56db\u5341\u4e09\u5c81\uff0c\u662f\u7687\u592a\u6781\u7684\u4e94\u54e5\uff0c\u4e24\u4eba\u6bd4\u8f83\u8001\u6210\u6301\u91cd\u3002 \u3000\u3000\u5c11\u58ee\u6d3e\u5927\u5c06\u5cb3\u6258\u4e0e\u6d4e\u5c14\u54c8\u6717\u7b49\u4eba\u5219\u652f\u6301\u7687\u592a\u6781\uff08\u5f53\u65f6\u4e09\u5341\u516b\u5c81\uff0c\u6392\u884c\u7b2c\u516b\uff09\u7684\u8fdb\u519b\u4e3b\u5f20\u3002\u5cb3\u6258\u662f\u4ee3\u5584\u7684\u513f\u5b50\uff0c\u5f53\u65f6\u5e74\u9f84\u4e0d\u8be6\uff0c\u76f8\u4fe1\u6700\u591a\u4e09\u5341\u5c81\uff0c\u6d4e\u5c14\u54c8\u6717\u662f\u7687\u592a\u6781\u7684\u5802\u5f1f\uff0c\u4e09\u5341\u56db\u5c81\uff0c\u90fd\u662f\u52c7\u6c14\u5341\u8db3\u3002\u90a3\u65e5\u5f00\u519b\u4e8b\u4f1a\u8bae\u5bc6\u5546\uff0c\u76f4\u5f00\u5230\u6df1\u591c\uff0c\u5728\u7687\u592a\u6781\u7684\u575a\u6301\u4e0b\u51b3\u5b9a\u7ee7\u7eed\u8fdb\u653b\u3002\u4f46\u7687\u592a\u6781\u4e5f\u77e5\u9053\u6b64\u884c\u6781\u9669\uff0c\u7b2c\u4e8c\u65e5\u65e9\u6668\u91cd\u7533\u519b\u4ee4\uff0c\u4e0d\u51c6\u5403\u660e\u4eba\u7684\u719f\u98df\uff0c\u4ee5\u9632\u4e0b\u6bd2\uff0c\u4e0d\u51c6\u9157\u9152\uff0c\u91c7\u53d6\u67f4\u8349\u65f6\u5fc5\u987b\u4f17\u4eba\u540c\u884c\uff0c\u4e0d\u53ef\u843d\u5355\uff0c\u5145\u5206\u663e\u9732\u4e86\u6218\u6218\u5162\u5162\u7684\u5fc3\u60c5\u3002\u7687\u592a\u6781\u7231\u8bfb\u300a\u4e09\u56fd\u6f14\u4e49\u300b\uff0c\u8fd9\u6b21\u51fa\u5e08\uff0c\u5f88\u6709\u9093\u827e\u4f10\u8700\u3001\u6df1\u5165\u9669\u5730\u7684\u610f\u5473\u2464\u3002 \u3000\u3000\u81ea\u9752\u57ce\u884c\u4e86\u56db\u5929\uff0c\u5230\u8001\u6cb3\uff0c\u5175\u5206\u4e09\u8def\uff0c\u7687\u592a\u6781\u547d\u5cb3\u6258\u3001\u6d4e\u5c14\u54c8\u6717\u7387\u53f3\u7ffc\u56db\u65d7\u548c\u53f3\u7ffc\u8bf8\u90e8\u8499\u53e4\u5175\u653b\u5927\u5b89\u53e3\uff1b\u4e03\u54e5\u963f\u5df4\u6cf0\u3001\u5341\u4e8c\u5f1f\u963f\u6d4e\u683c\u7387\u5de6\u7ffc\u56db\u65d7\u53ca\u5de6\u7ffc\u8bf8\u90e8\u8499\u53e4\u5175\u653b\u9f99\u4e95\u5173\uff1b\u4ed6\u81ea\u5df1\u4eb2\u7387\u4e2d\u519b\u653b\u6d2a\u5c71\u53e3\u3002\u4e09\u8def\u5148\u540e\u653b\u514b\uff0c\u8fdb\u5165\u957f\u57ce\uff0c\u8fdb\u8feb\u9075\u5316\u3002 \u3000\u3000\u8881\u5d07\u7115\u4e8e\u5341\u6708\u4e8c\u5341\u516b\u65e5\u5f97\u8baf\uff0c\u7acb\u5373\u5175\u5206\u4e24\u8def\uff0c\u5317\u8def\u6d3e\u9547\u5b88\u5c71\u6d77\u5173\u7684\u8d75\u7387\u6559\u5e26\u9a91\u5175\u56db\u5343\u897f\u4e0a\u5835\u622a\u3002\u4ed6\u81ea\u5df1\u7387\u540c\u7956\u5927\u5bff\u3001\u4f55\u53ef\u7eb2\u7b49\u5927\u5c06\u4ece\u5357\u8def\u897f\u53bb\u4fdd\u536b\u5317\u4eac\u3002\u6cbf\u9014\u6240\u7ecf\u629a\u5b81\u3001\u6c38\u5e73\u3001\u8fc1\u5b89\u3001\u4e30\u6da6\u3001\u7389\u7530\u8bf8\u5730\uff0c\u90fd\u7559\u5175\u5e03\u9632\uff0c\u51c6\u5907\u622a\u65ad\u6e05\u5175\u7684\u5f52\u8def\u3002 \u3000\u3000\u5d07\u796f\u6b63\u5728\u60f6\u6025\u4e07\u72b6\u4e4b\u9645\uff0c\u542c\u5f97\u8881\u5d07\u7115\u6765\u63f4\uff0c\u81ea\u7136\u662f\u559c\u4ece\u5929\u964d\uff0c\u5927\u5927\u5609\u5956\uff0c\u53d1\u5185\u5e11\u52b3\u519b\uff08\u8fd9\u6b21\u662f\u5fc3\u7518\u60c5\u613f\u4e86\uff09\uff0c\u53d1\u8868\u8881\u5d07\u7115\u4f5c\u5404\u8def\u63f4\u519b\u603b\u53f8\u4ee4\u2465\u3002 \u3000\u3000\u8881\u5d07\u7115\u90e8\u5341\u4e00\u6708\u521d\u8d76\u5230\u84df\u5dde\uff0c\u5341\u4e00\u3001\u5341\u4e8c\u3001\u5341\u4e09\uff0c\u4e09\u5929\u4e2d\u4e0e\u6e05\u5175\u5728\u9a6c\u5347\u6865\u7b49\u8981\u9698\u63a5\u4ed7\uff0c\u6bcf\u4e00\u4ed7\u90fd\u80dc\u3002\u6e05\u519b\u534a\u591c\u91cc\u9000\u5175\u3002 \u3000\u3000\u4f46\u5317\u8def\u63f4\u519b\u5374\u906d\u5230\u4e86\u91cd\u5927\u632b\u8d25\u3002\u8d75\u7387\u6559\u6025\u9a70\u897f\u63f4\uff0c\u5230\u8fbe\u4e09\u5c6f\u8425\u65f6\uff0c\u603b\u5175\u6731\u56fd\u5f66\u7adf\u7d27\u95ed\u57ce\u95e8\uff0c\u4e0d\u8ba9\u4ed6\u90e8\u961f\u8fdb\u57ce\u3002\u8d75\u7387\u6559\u65e0\u5948\uff0c\u53ea\u5f97\u9886\u5175\u5411\u897f\u8fce\u654c\uff0c\u5728\u9075\u5316\u57ce\u5916\u5927\u6218\uff0c\u88ab\u6e05\u519b\u963f\u6d4e\u683c\u6240\u90e8\u7684\u5de6\u8def\u519b\u5305\u56f4\u6b7c\u706d\uff0c\u8d75\u7387\u6559\u4e2d\u7bad\u9635\u4ea1\u3002\u9075\u5316\u9677\u843d\uff0c\u5de1\u629a\u738b\u5143\u96c5\u81ea\u6740\u3002 \u3000\u3000\u6e05\u519b\u8d8a\u4e09\u6cb3\uff0c\u7565\u987a\u4e49\uff0c\u81f3\u901a\u5dde\uff0c\u6e21\u6cb3\uff0c\u8fdb\u519b\u7267\u9a6c\u5382\uff0c\u5175\u52bf\u5982\u98ce\uff0c\u653b\u5411\u5317\u4eac\u3002\u5927\u540c\u603b\u5175\u6ee1\u6842\u3001\u5ba3\u5e9c\u603b\u5175\u4faf\u4e16\u7984\u4e2d\u9014\u5835\u622a\uff0c\u90fd\u88ab\u51fb\u6e83\u3002\u6ee1\u3001\u4faf\u4e24\u90e8\u5175\u9a6c\u9000\u4fdd\u5317\u4eac\u3002 \u3000\u3000\u8881\u5d07\u7115\u5f97\u5230\u8d75\u7387\u6559\u9635\u4ea1\u3001\u9075\u5316\u9677\u843d\u7684\u6d88\u606f\uff0c\u65e2\u4f24\u5fc3\u7231\u5c06\u4e4b\u6b7b\uff0c\u53c8\u77e5\u5c40\u9762\u4e25\u91cd\uff0c\u4e8e\u662f\u4e24\u65e5\u4e24\u591c\u6025\u884c\u519b\u4e09\u767e\u4f59\u91cc\uff0c\u6bd4\u6e05\u519b\u65e9\u5230\u4e86\u4e8c\u5929\uff0c\u9a7b\u519b\u4e8e\u5317\u4eac\u5e7f\u6e20\u95e8\u5916\u3002 \u3000\u3000\u8881\u5d07\u7115\u4e00\u5230\uff0c\u5d07\u796f\u7acb\u5373\u53ec\u89c1\uff0c\u5927\u52a0\u6170\u52b3\uff0c\u8981\u4ed6\u594f\u660e\u5bf9\u4ed8\u6e05\u5175\u7684\u65b9\u7565\uff0c\u8d50\u5fa1\u9994\u548c\u8c82\u88d8\u3002\u540c\u65f6\u53ec\u89c1\u7684\u8fd8\u6709\u6ee1\u6842\u3002\u4ed6\u89e3\u53bb\u8863\u670d\uff0c\u5c06\u5168\u8eab\u7d2f\u7d2f\u4f24\u75a4\u7ed9\u7687\u5e1d\u770b\uff0c\u5d07\u796f\u5927\u4e3a\u8d5e\u53f9\u3002\u8881\u5d07\u7115\u4ee5\u58eb\u9a6c\u75b2\u52b3\uff0c\u8981\u6c42\u5165\u57ce\u4f11\u606f\u3002\u4f46\u5d07\u796f\u5fc3\u4e2d\u9887\u6709\u7591\u5fcc\uff0c\u4e0d\u8bb8\u4ed6\u90e8\u961f\u5165\u57ce\u3002\u8881\u5d07\u7115\u8981\u6c42\u5c6f\u5175\u5916\u57ce\uff0c\u5d07\u796f\u4e5f\u4e0d\u51c6\uff0c\u4e00\u5b9a\u8981\u4ed6\u4eec\u5728\u57ce\u5916\u91ce\u6218\u3002 \u3000\u3000\u6e05\u5175\u4e1c\u653b\uff0c\u4e00\u8def\u4e0a\u52bf\u5982\u7834\u7af9\uff0c\u5728\u9ad8\u5bc6\u5e97\u4fa6\u77e5\u8881\u519b\u5df2\u5230\uff0c\u90fd\u662f\u5927\u60ca\u5931\u8272\uff0c\u4e07\u4e07\u60f3\u4e0d\u5230\u8881\u5d07\u7115\u4f1a\u6765\u5f97\u8fd9\u6837\u5feb\u3002 \u3000\u3000\u4e8c\u5341\u65e5\uff0c\u4e24\u519b\u5728\u5e7f\u6e20\u95e8\u5916\u5927\u6218\u3002\u8881\u5d07\u7115\u8fd9\u65f6\u5019\u4e0d\u80fd\u518d\u8f7b\u888d\u7f13\u5e26\u3001\u8c08\u7b11\u7528\u5175\u4e86\uff0c\u4ed6\u7a7f\u4e86\u7532\u80c4\uff0c\u4eb2\u81ea\u4e0a\u9635\u7763\u6218\u3002\u4ece\u4e0a\u5348\u516b\u65f6\u6253\u5230\u4e0b\u5348\u56db\u65f6\uff0c\u6076\u6597\u516b\u5c0f\u65f6\uff0c\u80dc\u8d1f\u4e0d\u51b3\u3002 \u3000\u3000\u6ee1\u6842\u7387\u5175\u4e94\u5343\u5b88\u5fb7\u80dc\u95e8\u3002\u5f53\u65f6\u5317\u4eac\u519b\u6c11\u5728\u57ce\u5934\u89c2\u6218\uff0c\u4f46\u89c1\u6e05\u5175\u51b2\u7a81\u800c\u897f\uff0c\u4ece\u57ce\u4e0a\u671b\u4e0b\u6765\uff0c\u5982\u9ed1\u4e91\u4e07\u6735\uff0c\u631f\u8fc5\u98ce\u800c\u9a70\uff0c\u987b\u81fe\u5df2\u8fc7\u3002\u4e00\u573a\u6fc0\u6218\uff0c\u6ee1\u6842\u53d7\u4f24\uff0c\u8840\u67d3\u5f81\u888d\uff0c\u4e94\u5343\u5175\u53ea\u5269\u4e0b\u4e86\u4e09\u5343\u4eba\u3002\u6e05\u5175\u5a01\u731b\u5982\u6b64\uff0c\u5317\u4eac\u4eba\u81ea\u7136\u770b\u5f97\u5fc3\u60ca\u80c6\u88c2\u3002\u5317\u4eac\u57ce\u5934\u5b88\u519b\u653e\u5927\u70ae\u652f\u63f4\u6ee1\u6842\uff0c\u4f46\u70ae\u672f\u5947\u5dee\uff0c\u70ae\u5f39\u6253\u5165\u6ee1\u6842\u519b\u4e2d\uff0c\u6740\u4f24\u4e86\u4e0d\u5c11\u58eb\u5352\u3002 \u3000\u3000\u4e3b\u6218\u573a\u662f\u5728\u5e7f\u6e20\u95e8\u3002\u8881\u5d07\u7115\u548c\u6e05\u5175\u6253\u5230\u508d\u665a\uff08\u5e78\u597d\u57ce\u5934\u5b88\u519b\u6ca1\u6709\u653e\u70ae\u652f\u63f4\u8881\u519b\uff09\uff0c\u6e05\u5175\u7ec8\u4e8e\u4e0d\u652f\u8d25\u9000\uff0c\u9000\u4e86\u5341\u4f59\u91cc\u3002\u8881\u519b\u76f4\u8ffd\u6740\u5230\u8fd0\u6cb3\u8fb9\u4e0a\u3002\u8fd9\u573a\u8840\u6218\uff0c\u6e05\u519b\u52b2\u65c5\u963f\u5df4\u6cf0\u3001\u963f\u6d4e\u683c\u3001\u601d\u683c\u5c14\u4e09\u90e8\u90fd\u88ab\u51fb\u6e83\u3002\u8881\u5d07\u7115\u4e5f\u4e2d\u7bad\u53d7\u4f24\u2466\u3002 \u3000\u3000\u8fd9\u4e00\u5f79\u4e4b\u540e\uff0c\u6e05\u5175\u4f17\u8d1d\u52d2\u5f00\u4f1a\u68c0\u8ba8\u3002\u7687\u592a\u6781\u7684\u4e03\u54e5\u963f\u5df4\u6cf0\u6309\u519b\u5f8b\u8981\u524a\u7235\u3002\u7687\u592a\u6781\u8bf4\uff1a\u201c\u963f\u5df4\u6cf0\u5728\u6218\u9635\u548c\u4ed6\u4e24\u4e2a\u513f\u5b50\u76f8\u5931\uff0c\u4e3a\u4e86\u6551\u513f\u5b50\uff0c\u624d\u6ca1\u6709\u6309\u7167\u9884\u5b9a\u7684\u8ba1\u5212\u4f5c\u6218\uff0c\u7136\u800c\u5e76\u4e0d\u662f\u80c6\u602f\u3002\u6211\u600e\u4e48\u53ef\u4ee5\u5b9a\u6211\u4eb2\u54e5\u54e5\u7684\u7f6a\uff1f\u201d\u4fbf\u5bbd\u5ba5\u4e86\u4ed6\u2467\u3002\u53ef\u89c1\u8fd9\u4e00\u4ed7\u6e05\u519b\u8d25\u5f97\u5f88\u72fc\u72c8\u3002 \u3000\u3000\u7687\u592a\u6781\u4e0e\u8bf8\u8d1d\u52d2\u90fd\u8bf4\uff1a\u201c\u5341\u4e94\u5e74\u6765\uff0c\u4ece\u672a\u9047\u5230\u8fc7\u8881\u5d07\u7115\u8fd9\u6837\u7684\u52b2\u654c\u3002\u201d\u4e8e\u662f\u4e0d\u6562\u518d\u903c\u8fd1\u5317\u4eac\uff0c\u9a7b\u5175\u5728\u6d77\u5b50\u3001\u91c7\u56ff\u4e4b\u95f4\u3002 \u3000\u3000\u8881\u5d07\u7115\u6765\u63f4\u5317\u4eac\u65f6\uff0c\u56e0\u5341\u4e07\u706b\u6025\uff0c\u53ea\u5e26\u4e86\u9a6c\u519b\u4e94\u5343\u4f5c\u5148\u5934\u90e8\u961f\uff0c\u5176\u540e\u53c8\u5230\u4e86\u9a91\u5175\u56db\u5343\uff0c\u5e7f\u6e20\u95e8\u8fd9\u573a\u5927\u6218\uff0c\u662f\u4ee5\u4e5d\u5343\u5175\u5f53\u5341\u4f59\u4e07\u5927\u519b\uff0c\u5176\u5b9e\u662f\u80dc\u5f97\u5341\u5206\u4fa5\u5e78\u7684\u3002\u5f53\u65f6\u4e00\u6765\u8881\u519b\u4e00\u9f13\u4f5c\u6c14\uff0c\u594b\u52c7\u6297\u654c\uff0c\u4e8c\u6765\u6e05\u519b\u7a81\u7136\u9047\u5230\u8881\u519b\uff0c\u5fc3\u4e2d\u5148\u5df2\u602f\u4e86\uff0c\u6597\u5fd7\u4e0d\u575a\u3002 \u3000\u3000\u8881\u5d07\u7115\u77e5\u9053\u8fd9\u4e00\u4ed7\u4fa5\u5e78\u83b7\u80dc\uff0c\u5728\u519b\u4e8b\u4e0a\u5e76\u4e0d\u53ef\u53d6\uff0c\u5c24\u5176\u5728\u4eac\u57ce\u5916\u6253\u4ed7\uff0c\u66f4\u4e0d\u80fd\u8d2a\u56fe\u4fa5\u5e78\u3002\u4ed6\u5bf9\u90e8\u5c5e\u8bf4\uff1a\u201c\u6309\u7167\u5175\u6cd5\uff0c\u4fa5\u5e78\u5f97\u80dc\uff0c\u6bd4\u6253\u8d25\u4ed7\u8fd8\u8981\u4e0d\u597d\u3002\u201d\u56e0\u4e3a\u78b0\u8fd0\u6c14\u800c\u6253\u80dc\uff0c\u4e5f\u53ef\u56e0\u8fd0\u6c14\u4e0d\u597d\u800c\u8d25\uff0c\u4e00\u8d25\u5c31\u4e0d\u53ef\u6536\u62fe\u3002\u4f46\u5982\u8c0b\u5b9a\u800c\u540e\u6218\uff0c\u4e8b\u5148\u7b79\u5212\u597d\u7b2c\u4e8c\u4e2a\u6b65\u9aa4\uff0c\u5373\u4f7f\u8d25\u4e86\u4e00\u4ed7\uff0c\u4e5f\u65e0\u5927\u60a3\u3002\u53ef\u662f\u5d07\u796f\u89c1\u6e05\u5175\u6ca1\u6709\u8fdc\u9000\uff0c\u4e0d\u65ad\u7684\u50ac\u4fc3\u8881\u5d07\u7115\u51fa\u6218\u3002\u8881\u5d07\u7115\u8bf4\uff0c\u4f30\u8ba1\u5173\u5b81\u6b65\u5175\u5168\u519b\u4e8e\u5341\u4e8c\u6708\u521d\u4e09\u3001\u521d\u56db\u53ef\u5230\u3002\u4e00\u7b49\u5927\u519b\u5230\u8fbe\uff0c\u5c31\u53ef\u548c\u6e05\u5175\u51b3\u6218\u3002 \u3000\u3000\u8fd9\u65f6\u6e05\u519b\u4e2d\u7684\u5927\u5c06\u89c1\u5230\u8881\u5d07\u7115\u5175\u5c11\uff0c\u4e3b\u5f20\u7acb\u523b\u653b\u57ce\u3002\u7687\u592a\u6781\u7ec8\u662f\u5fcc\u60ee\u8881\u5d07\u7115\uff0c\u4e0d\u80af\u653b\u57ce\uff0c\u63a8\u6258\u8bf4\u662f\u6015\u635f\u5931\u826f\u5c06\u3002 \u3000\u3000\u5176\u5b9e\u5373\u4f7f\u5728\u8881\u5d07\u7115\u6b65\u519b\u5927\u961f\u5f00\u5230\u4e4b\u540e\uff0c\u8fd8\u662f\u4e0d\u5e94\u548c\u6e05\u5175\u51b3\u6218\u3002\u660e\u519b\u7684\u6218\u6597\u529b\u8fdc\u4e0d\u5982\u6e05\u5175\uff0c\u53cc\u65b9\u4eba\u6570\u5982\u7ea6\u7565\u76f8\u7b49\uff0c\u660e\u519b\u80dc\u5c11\u8d25\u591a\u3002\u5728\u4eac\u57ce\u5916\u51b3\u6218\uff0c\u5728\u660e\u65b9\u662f\u592a\u8fc7\u5192\u9669\uff0c\u4e07\u4e00\uff08\u5176\u5b9e\u4e0d\u662f\u4e07\u4e00\uff0c\u800c\u662f\u6781\u6709\u53ef\u80fd\uff09\u8881\u519b\u6e83\u8d25\uff0c\u751a\u81f3\u5168\u519b\u8986\u6ca1\uff0c\u5317\u4eac\u7acb\u523b\u5931\u9677\uff0c\u5d07\u796f\u5c31\u5f97\u63d0\u524d\u5341\u4e94\u5e74\u4e0a\u540a\u4e86\u3002\u51b3\u4e0d\u80fd\u62ff\u4eac\u5e08\u548c\u7687\u5e1d\u6765\u5b64\u6ce8\u4e00\u63b7\uff0c\u4f5c\u4e3a\u8d4c\u6ce8\u3002\u4f46\u591a\u8fc7\u5f97\u4e00\u5929\uff0c\u660e\u519b\u4ece\u56db\u9762\u516b\u65b9\u8d76\u6765\u7684\u52e4\u738b\u4e4b\u5e08\u4fbf\u591a\u5230\u4e00\u6279\u3002\u4efb\u4f55\u5e73\u5eb8\u7684\u5c06\u624d\u4e5f\u770b\u5f97\u5230\uff1a\u5e94\u5f53\u5927\u519b\u5728\u57ce\u5916\u575a\u5b88\u4e0d\u6218\uff0c\u6d3e\u6e38\u519b\u53bb\u622a\u65ad\u6e05\u5175\u7684\u7cae\u9053\uff0c\u711a\u70e7\u6e05\u5175\u7cae\u8349\uff0c\u518d\u6d3e\u5175\u53bb\u5360\u9886\u957f\u57ce\u5404\u5904\u8981\u9698\uff0c\u4f7f\u6e05\u5175\u5b8c\u5168\u6ca1\u6709\u9000\u8def\uff0c\u7136\u540e\u4e0e\u6e05\u5175\u6301\u4e45\u5bf9\u6297\u3002\u7b80\u5355\u8bf4\u6765\uff0c\u5c31\u662f\u201c\u575a\u58c1\u6e05\u91ce\u201d\u3002 \u3000\u3000\u5728\u4efb\u4f55\u5730\u65b9\u6253\u4ed7\uff0c\u90fd\u987b\u8bbe\u6cd5\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002\u5728\u4eac\u57ce\u6297\u654c\uff0c\u66f4\u662f\u7edd\u5bf9\u8981\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002\u9664\u975e\u5148\u5c06\u7687\u5e1d\u4e0e\u7edf\u5e05\u90e8\u5148\u884c\u64a4\u51fa\u4eac\u57ce\u3002 \u3000\u3000\u65f6\u95f4\u4e00\u4e45\uff0c\u6e05\u519b\u8eab\u5728\u9669\u5730\uff0c\u519b\u5fc3\u5fc5\u7136\u52a8\u6447\uff0c\u56f0\u5728\u5317\u4eac\u90ca\u5916\uff0c\u8fdb\u662f\u8fdb\u4e0d\u5f97\uff0c\u9000\u53c8\u9000\u4e0d\u4e86\uff0c\u53d8\u6210\u4e86\u74ee\u4e2d\u4e4b\u9cd6\u3002\u8fd9\u65f6\u8881\u5d07\u7115\u5175\u6743\u7edf\u4e00\uff0c\u53ea\u5f85\u63f4\u519b\u4e91\u96c6\uff0c\u5c31\u53ef\u5bf9\u6e05\u519b\u56db\u9762\u91cd\u91cd\u56f4\u56f0\u3002\u4e24\u519b\u4ea4\u6218\uff0c\u80dc\u8d25\u4e4b\u5206\u5168\u5728\u4e4e\u4e00\u80a1\u6c14\u52bf\u3002\u660e\u519b\u6218\u6597\u529b\u867d\u7136\u4e0d\u884c\uff0c\u4f46\u773c\u89c1\u5fc5\u80dc\uff0c\u5175\u5c06\u90fd\u60f3\u7acb\u529f\uff0c\u81ea\u7136\u4e0d\u4f1a\u4e00\u89e6\u5373\u6e83\u3002\u4e09\u4e2a\u6708\u3001\u56db\u4e2a\u6708\u7684\u6253\u4e0b\u6765\uff0c\u6e05\u5175\u975e\u8986\u6ca1\u4e0d\u53ef\u3002 \u3000\u3000\u5728\u8fd9\u5176\u95f4\uff0c\u660e\u519b\u5e94\u5f53\u518d\u6d3e\u5175\u8fdb\u653b\u8fbd\u9633\u3001\u6c88\u9633\u3002\u6e05\u5175\u503e\u5de2\u800c\u51fa\uff0c\u672c\u90e8\u5168\u7136\u7a7a\u865a\u3002\u660e\u519b\u8981\u653b\u5360\u8fbd\u6c88\u51b3\u975e\u96be\u4e8b\u3002\u53d6\u5f97\u8fbd\u6c88\u540e\uff0c\u5c06\u4e00\u4e9b\u6e05\u519b\u7684\u5bb6\u5c5e\u9001\u53bb\u6e05\u519b\u8425\u4e2d\uff0c\u6e05\u5175\u54ea\u91cc\u8fd8\u6709\u6597\u5fd7\uff1f \u3000\u3000\u4e8b\u5b9e\u4e0a\u5f53\u7136\u4e0d\u80fd\u8fd9\u6837\u987a\u5229\u3002\u7687\u592a\u6781\u548c\u4f17\u8d1d\u52d2\u5584\u4e8e\u7528\u5175\uff0c\u7acb\u523b\u5c31\u4f1a\u5168\u519b\u6025\u9000\uff0c\u51b2\u51fa\u957f\u57ce\uff0c\u5982\u679c\u9000\u5f97\u65e9\uff0c\u9000\u5f97\u5feb\uff0c\u660e\u519b\u5c1a\u672a\u5408\u56f4\uff0c\u76f8\u4fe1\u8881\u5d07\u7115\u62e6\u4ed6\u4eec\u4e0d\u4f4f\u3002\u4f46\u897f\u8def\u6cbf\u9014\u8ffd\u51fb\uff0c\u4e1c\u8def\u53e6\u51fa\u5927\u519b\u53bb\u653b\u8fbd\u6c88\u800c\u4f5c\u7275\u5236\uff0c\u6e05\u5175\u5927\u519b\u867d\u80fd\u9000\u56de\u672c\u90e8\uff0c\u5374\u975e\u8f93\u5f97\u4e00\u8d25\u6d82\u5730\u4e0d\u53ef\u3002 \u3000\u3000\u7687\u592a\u6781\u8fd9\u6b21\u5077\u88ad\u5b9e\u5728\u5341\u5206\u5192\u9669\u3002\u5b59\u5b50\u5175\u6cd5\u7684\u91cd\u8981\u539f\u5219\u662f\uff1a\u8bbe\u6cd5\u5f15\u654c\u4eba\u8fdb\u5165\u4e8e\u6211\u6709\u5229\u7684\u9635\u5730\uff1b\u8ba9\u654c\u4eba\u8f9b\u8f9b\u82e6\u82e6\u7684\u8fdc\u9053\u6765\u653b\uff0c\u6211\u4ee5\u9038\u5f85\u52b3\uff1b\u654c\u4eba\u521d\u6765\u65f6\u5175\u52bf\u950b\u9510\uff0c\u5e94\u5f53\u6301\u91cd\u4e0d\u6218\uff0c\u5f85\u5f97\u654c\u4eba\u56f0\u987f\u6020\u61c8\u800c\u60f3\u9000\u5175\u4e4b\u65f6\uff0c\u4fbf\u4e58\u673a\u8fdb\u51fb\u2468\u3002\u8fd9\u4e9b\u6c42\u4e4b\u4e0d\u5f97\u7684\u826f\u673a\uff0c\u7a81\u7136\u4e4b\u95f4\u90fd\u51fa\u73b0\u4e86\u3002\u8881\u5d07\u7115\u719f\u8bfb\u5b59\u5b50\u5175\u6cd5\uff0c\u4ee5\u4ed6\u7684\u5927\u624d\uff0c\u5f53\u7136\u80fd\u5584\u4e8e\u5229\u7528\uff0c\u5c31\u7b97\u4e0d\u80fd\u4e00\u4e3e\u800c\u706d\u4e86\u6ee1\u6e05\uff0c\u81f3\u5c11\u4e5f\u53ef\u4ee5\u4ee4\u6e05\u5175\u5341\u4f59\u5e74\u4e0d\u6562\u518d\u6765\u8fdb\u72af\u3002 \u3000\u3000\u4e8c\u6b21\u4e16\u754c\u5927\u6218\u65f6\u5fb7\u519b\u731b\u653b\u65af\u5927\u6797\u683c\u52d2\u3002\u82cf\u519b\u4e00\u9762\u627c\u5b88\u575a\u57ce\uff0c\u4e00\u9762\u53e6\u9063\u5927\u519b\u6284\u5fb7\u519b\u540e\u8def\uff0c\u7ec8\u4e8e\u805a\u6b7c\u5fb7\u519b\u4e09\u5341\u4e09\u4e07\u4eba\u3002\u7ecf\u6b64\u5f79\u540e\uff0c\u5fb7\u519b\u5c31\u6b64\u4e00\u8e76\u4e0d\u632f\u3002\u82cf\u519b\u5143\u5e05\u6731\u53ef\u592b\u7684\u6218\u7565\uff0c\u57fa\u672c\u539f\u5219\u4e5f\u4e0d\u8fc7\u662f\u201c\u5b88\u575a\u57ce\uff0c\u6284\u540e\u8def\uff0c\u805a\u6b7c\u4e4b\u201d\u4e5d\u5b57\u800c\u5df2\u3002 \u3000\u3000\u7136\u800c\u5d07\u796f\u662f\u4e2a\u5341\u5206\u6025\u8e81\u3001\u6beb\u65e0\u97e7\u529b\u7684\u9752\u5e74\uff0c\u90a3\u65f6\u8fd8\u6ca1\u6ee1\u5341\u4e5d\u5c81\uff0c\u4e00\u89c1\u8881\u5d07\u7115\u6309\u5175\u4e0d\u52a8\uff0c\u767b\u65f6\u4fbf\u4e0d\u8010\u70e6\u8d77\u6765\uff0c\u4e0d\u4f4f\u7684\u50ac\u4ed6\u51fa\u6218\u3002\u8881\u5d07\u7115\u4e00\u518d\u8bf4\uff0c\u8981\u7b49\u6b65\u5175\u5168\u519b\u5230\u8fbe\u624d\u53ef\u8fdb\u653b\uff0c\u73b0\u5728\u53ea\u6709\u4e5d\u5343\u9a91\u5175\uff0c\u548c\u654c\u5175\u5341\u4f59\u4e07\u51b3\u6218\uff0c\u96be\u6c42\u5fc5\u80dc\u3002\u6599\u60f3\u5d07\u796f\u5c31\u6000\u7591\u8d77\u6765\u4e86\uff1a\u201c\u4f60\u4e0d\u80af\u51fa\u6218\uff0c\u5230\u5e95\u662f\u751a\u4e48\u5c45\u5fc3\uff1f\u60f3\u7be1\u4f4d\u4e48\uff1f\u60f3\u80c1\u8feb\u6211\u7b54\u5e94\u8bae\u548c\u4e48\uff1f\u4f60\u4ece\u524d\u4e0d\u65ad\u548c\u7687\u592a\u6781\u4e66\u4fe1\u5f80\u6765\uff0c\u5230\u5e95\u6709\u751a\u4e48\u5bc6\u8c0b\uff1f\u4f60\u4e3a\u751a\u4e48\u4e00\u65e9\u5c31\u6599\u5230\u91d1\u5175\u8981\u4ece\u897f\u8def\u6765\u653b\u5317\u4eac\uff1f\u201d\u4ed6\u7684\u6027\u683c\u672c\u6765\u5c31\u5341\u5206\u591a\u7591\uff0c\u654c\u519b\u5175\u4e34\u57ce\u4e0b\uff0c\u53c8\u60ca\u53c8\u6015\u4e4b\u9645\uff0c\u60f3\u8c61\u529b\u5b9a\u7136\u5341\u5206\u4e30\u5bcc\u3002 \u3000\u3000\u8fd9\u65f6\u53c8\u6709\u5c24\u4e16\u5a01\u4e00\u8def\u63f4\u5175\u5230\u8fbe\uff0c\u53e6\u6709\u4faf\u4e16\u7984\u90e8\u4e00\u519b\uff0c\u4e24\u8def\u90e8\u961f\u4eba\u6570\u4e0d\u591a\uff0c\u6218\u6597\u529b\u4e5f\u4e0d\u5f3a\uff0c\u5982\u6d3e\u53bb\u548c\u6e05\u5175\u4ea4\u950b\uff0c\u4e00\u6218\u5373\u6e83\uff0c\u53cd\u800c\u6270\u4e71\u5168\u519b\u519b\u5fc3\uff0c\u5f71\u54cd\u4eac\u5e08\u57ce\u9632\u3002\u8881\u5d07\u7115\u6d3e\u5c24\u4e16\u5a01\u90e8\u53bb\u5b88\u660c\u5e73\uff0c\u90a3\u662f\u660e\u6210\u7956\u4ee5\u6765\u5386\u4ee3\u7687\u5e1d\u7684\u9675\u5bdd\u6240\u5728\uff0c\u5982\u679c\u7ed9\u6e05\u5175\u653b\u5360\uff0c\u6398\u4e86\u7687\u5e1d\u7956\u5b97\u7684\u575f\u5893\uff0c\u6b64\u4e8b\u975e\u540c\u5c0f\u53ef\u3002\u4ed6\u6d3e\u4faf\u4e16\u7984\u90e8\u53bb\u5b88\u4e09\u6cb3\uff0c\u4ee5\u4f5c\u84df\u5dde\u7684\u540e\u5e94\uff0c\u76ee\u7684\u662f\u7275\u5236\u6e05\u519b\uff0c\u4e58\u673a\u53ef\u622a\u65ad\u6e05\u5175\u5f52\u8def\u3002\u5317\u4eac\u7684\u536b\u620d\u90e8\u961f\u672c\u6765\u6709\u6240\u8c13\u201c\u4eac\u8425\u201d\uff0c\u5728\u660e\u592a\u7956\u65f6\u662f\u5168\u56fd\u8bf8\u519b\u4e4b\u51a0\uff0c\u7cbe\u9510\u4e4b\u6781\uff0c\u53ef\u662f\u8fd9\u65f6\u4e45\u672a\u8bad\u7ec3\uff0c\u65e9\u5df2\u65e0\u7528\u247d\uff0c\u6240\u4ee5\u8881\u5d07\u7115\u6d3e\u6ee1\u6842\u548c\u81ea\u5df1\u6240\u5e26\u7684\u4e5d\u5343\u9a91\u5175\u5b88\u5317\u4eac\u3002 \u3000\u3000\u5d07\u796f\u89c1\u4ed6\u5e76\u4e0d\u5c06\u6240\u6709\u63f4\u5175\u90fd\u8c03\u6765\u5b88\u5317\u4eac\uff0c\u66f4\u52a0\u5fe7\u8651\u91cd\u91cd\u3002\u603b\u4e4b\uff0c\u4ed6\u89c1\u6e05\u5175\u6765\u653b\uff0c\u5df2\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u53ea\u76fc\u671b\u6240\u6709\u63f4\u519b\u7684\u4e00\u5175\u4e00\u5352\uff0c\u90fd\u5728\u5317\u4eac\u57ce\u5916\u4fdd\u536b\u4ed6\u7687\u4e0a\u4e07\u5c81\u4e00\u4e2a\u4eba\u3002\u4ed6\u5b8c\u5168\u4e0d\u660e\u767d\u6253\u4ed7\u7684\u9053\u7406\u3002\u4e00\u652f\u90e8\u961f\u5982\u679c\u6d3e\u51fa\u53bb\u653b\u51fb\u654c\u519b\u540e\u8def\uff0c\u6240\u53d1\u751f\u7684\u4f5c\u7528\uff0c\u5f80\u5f80\u6bd4\u5b88\u5728\u5317\u4eac\u57ce\u5916\u8981\u5927\u5f97\u591a\u3002 \u3000\u3000\u6e05\u5175\u4e8e\u5341\u4e00\u6708\u4e8c\u5341\u4e03\u65e5\u9000\u5230\u5357\u6d77\u5b50\uff0c\u6e83\u8d25\u4e4b\u540e\uff0c\u5fc3\u4e2d\u4e0d\u5fff\uff0c\u4fbf\u5728\u5317\u4eac\u90ca\u5916\u5927\u4e3e\u70e7\u6740\u51fa\u6c14\u3002\u5317\u4eac\u57ce\u91cc\u5c45\u6c11\u7684\u5fc3\u7406\u662f\u548c\u7687\u5e1d\u4e00\u6837\u7684\uff0c\u987e\u5230\u7684\u53ea\u662f\u81ea\u5df1\u8eab\u5bb6\u6027\u547d\uff0c\u5927\u5bb6\u542c\u4fe1\u4e86\u8c23\u8a00\uff0c\u8bf4\u8881\u5d07\u7115\u4e0d\u80af\u51fa\u6218\uff0c\u522b\u6709\u7528\u5fc3\u3002\u8bb8\u591a\u4eba\u8bf4\u6e05\u5175\u662f\u4ed6\u5f15\u6765\u7684\uff0c\u76ee\u7684\u5728\u201c\u80c1\u548c\u201d\uff0c\u4f7f\u7687\u5e1d\u4e0d\u5f97\u4e0d\u63a5\u53d7\u4ed6\u4e00\u5411\u6240\u4e3b\u5f20\u7684\u548c\u8bae\u3002\u4e8e\u662f\u6709\u4eba\u5728\u57ce\u5934\u5411\u57ce\u4e0b\u7684\u8881\u90e8\u9a91\u5175\u629b\u63b7\u77f3\u5934\uff0c\u9a82\u4ed6\u4eec\u662f\u201c\u6c49\u5978\u5175\u201d\u3002\u77f3\u5934\u7838\u6b7b\u4e86\u51e0\u540d\u5175\u58eb\u3002 \u3000\u3000\u8fd9\u79cd\u76f2\u76ee\u7684\u7fa4\u4f17\u5fc3\u7406\uff0c\u5b9e\u5728\u662f\u5f88\u53ef\u6015\u7684\uff0c\u8fd1\u4ee3\u7684\u7fa4\u4f17\u5fc3\u7406\u5b66\u4e66\u7c4d\u4e2d\u5e38\u6709\u63d0\u5230\u3002\u7b2c\u4e00\u6b21\u5b81\u8fdc\u5927\u6218\uff0c\u6e05\u5175\u731b\u653b\uff0c\u773c\u89c1\u57ce\u7834\u5728\u5373\uff0c\u767e\u59d3\u5c31\u5927\u9a82\u8881\u5d07\u7115\u5bb3\u4eba\uff0c\u6e05\u5175\u9000\u540e\uff0c\u4fbf\u5373\u5927\u54ed\u62dc\u8c22\u3002\u636e\u52a8\u7269\u5b66\u5bb6\u7684\u8c03\u67e5\u62a5\u544a\uff0c\u5408\u7fa4\u7684\u52a8\u7269\uff08\u5982\u8001\u9f20\uff09\u5728\u9047\u5230\u5371\u96be\u65f6\uff0c\u5f80\u5f80\u4f1a\u6495\u6740\u540c\u7c7b\uff0c\u6216\u8bb8\u662f\u51fa\u4e8e\u540c\u4e00\u5fc3\u7406\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u6e05\u5175\u6349\u5230\u4e86\u4e24\u540d\u660e\u5b98\u6d3e\u5728\u57ce\u5916\u8d1f\u8d23\u517b\u9a6c\u7684\u592a\u76d1\uff0c\u4e00\u4e2a\u53eb\u6768\u6625\uff0c\u4e00\u4e2a\u53eb\u738b\u6210\u5fb7\u3002\u7687\u592a\u6781\u5fc3\u751f\u4e00\u8ba1\uff0c\u6d3e\u4e86\u526f\u5c06\u9ad8\u9e3f\u4e2d\u3001\u53c2\u5c06\u9c8d\u627f\u5148\u3001\u5b81\u5b8c\u6211\u3001\u5df4\u514b\u751a\u3001\u8fbe\u6d77\u7b49\u4eba\u76d1\u5b88\u3002\u4fd8\u864f\u4e86\u4e24\u540d\u5c0f\u5c0f\u592a\u76d1\uff0c\u4f55\u5fc5\u8981\u6d3e\u4e94\u540d\u5c06\u9886\u6765\u76d1\u5b88\uff1f\u5176\u4e2d\u5f53\u7136\u6709\u8ba1\u3002\u9ad8\u3001\u9c8d\u3001\u5b81\u4e09\u4eba\u662f\u6295\u964d\u6ee1\u6e05\u7684\u6c49\u4eba\u3002\u5230\u5f97\u665a\u4e0a\uff0c\u9c8d\u627f\u5148\u4e0e\u5b81\u5b8c\u6211\u4e8c\u4eba\u4f9d\u7167\u7687\u592a\u6781\u6240\u6388\u7684\u5bc6\u8ba1\uff0c\u5927\u58f0\u201c\u8033\u8bed\u201d\uff0c\u4e92\u76f8\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u64a4\u5175\uff0c\u5e76\u4e0d\u662f\u6211\u4eec\u6253\u4e86\u8d25\u4ed7\uff0c\u90a3\u662f\u7687\u4e0a\u7684\u5999\u8ba1\u3002\u4f60\u4e0d\u89c1\u5230\u4e48\uff1f\u7687\u4e0a\u5355\u72ec\u9a91\u4e86\u9a6c\u903c\u8fd1\u654c\u4eba\uff0c\u654c\u4eba\u519b\u4e2d\u6709\u4e24\u540d\u519b\u5b98\u8fc7\u6765\uff0c\u53c2\u89c1\u7687\u4e0a\uff0c\u5546\u91cf\u4e86\u597d\u4e45\uff0c\u90a3\u4e24\u540d\u519b\u5b98\u5c31\u56de\u53bb\u4e86\u3002\u7687\u4e0a\u548c\u8881\u7763\u5e08\u5df2\u6709\u5bc6\u7ea6\uff0c\u5927\u4e8b\u4e0d\u4e45\u5c31\u53ef\u6210\u529f\u3002\u201d \u3000\u3000\u8fd9\u4e24\u540d\u592a\u76d1\u7761\u5728\u65c1\u8fb9\uff0c\u5c06\u4e24\u4eba\u7684\u8bdd\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u5341\u4e00\u6708\u4e09\u5341\u65e5\uff0c\u7687\u592a\u6781\u547d\u5b88\u8005\u5047\u610f\u758f\u5ffd\uff0c\u8ba9\u6768\u6625\u9003\u56de\u5317\u4eac\u3002\u6768\u6625\u5c06\u542c\u5230\u7684\u8bdd\u4e00\u4e94\u4e00\u5341\u7684\u7980\u62a5\u4e86\u5d07\u796f\u247e\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5341\u4e8c\u6708\u521d\u4e00\uff0c\u5d07\u796f\u53ec\u8881\u5d07\u7115\u548c\u7956\u592a\u5bff\u8fdb\u5bab\uff0c\u95ee\u4e0d\u4e86\u51e0\u53e5\uff0c\u5c31\u559d\u4ee4\u5c06\u8881\u5d07\u7115\u902e\u6355\uff0c\u56da\u5165\u5fa1\u7262\u3002 \u3000\u3000\u7956\u5927\u5bff\u773c\u89c1\u4e4b\u4e0b\uff0c\u5413\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u51fa\u5317\u4eac\u57ce\u540e\u7b49\u4e86\u4e09\u5929\uff0c\u89c1\u8881\u5d07\u7115\u59cb\u7ec8\u6ca1\u6709\u83b7\u91ca\u3002\u5d07\u796f\u6d3e\u592a\u76d1\u5411\u57ce\u5916\u8881\u90e8\u5ba3\u8bfb\u5723\u65e8\uff0c\u8bf4\u8881\u5d07\u7115\u8c0b\u53db\uff0c\u53ea\u7f6a\u4e00\u4eba\uff0c\u4e0e\u4f17\u5c06\u58eb\u65e0\u6d89\u3002\u4f17\u5175\u5c06\u5728\u57ce\u4e0b\u5927\u54ed\u3002\u7956\u5927\u5bff\u4e0e\u4f55\u53ef\u7eb2\u60ca\u6012\u4ea4\u96c6\uff0c\u7acb\u5373\u5e26\u4e86\u90e8\u961f\u56de\u9526\u5dde\u53bb\u4e86\u247f\u3002\u6b63\u5728\u517c\u7a0b\u5357\u4e0b\u8d74\u63f4\u7684\u8881\u90e8\u4e3b\u529b\u90e8\u961f\uff0c\u5728\u9014\u4e2d\u5f97\u6089\u4e3b\u5e05\u65e0\u7f6a\u88ab\u6355\uff0c\u5317\u4eac\u57ce\u4e2d\u7687\u5e1d\u548c\u767e\u59d3\u90fd\u8bf4\u4ed6\u4eec\u662f\u201c\u6c49\u5978\u5175\u201d\uff0c\u5f53\u7136\u4e5f\u5c31\u6389\u5934\u800c\u56de\u3002 \u3000\u3000\u4e2d\u56fd\u5386\u53f2\u4e0a\u751a\u4e48\u5343\u5947\u767e\u602a\u7684\u4e8b\u90fd\u6709\uff0c\u4f46\u654c\u519b\u5175\u4e34\u57ce\u4e0b\u800c\u5c06\u57ce\u9632\u603b\u53f8\u4ee4\u4e0b\u72f1\uff0c\u5374\u662f\u7b2c\u4e00\u6b21\u53d1\u751f\u3002 \u3000\u3000\u5d07\u796f\u89c1\u7956\u5927\u5bff\u5e26\u9886\u7cbe\u5175\u8d70\u4e86\uff0c\u4e0d\u7406\u5317\u4eac\u7684\u9632\u52a1\uff0c\u8fd9\u4e00\u4e0b\u53ef\u6025\u8d77\u6765\u4e86\uff0c\u5fd9\u6d3e\u4e86\u5185\u9601\u5168\u4f53\u5927\u5b66\u58eb\u4e0e\u4e5d\u537f\u5230\u72f1\u4e2d\uff0c\u8981\u8881\u5d07\u7115\u5199\u4fe1\u62db\u7956\u5927\u5bff\u56de\u6765\u3002\u8881\u5d07\u7115\u5fc3\u4e2d\u4e0d\u670d\uff0c\u4e0d\u80af\u5199\uff0c\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u5982\u6709\u8bcf\u4e66\uff0c\u8981\u6211\u5199\u4fe1\uff0c\u6211\u5f53\u7136\u5949\u65e8\u3002\u518d\u8bf4\uff0c\u6211\u672c\u6765\u662f\u7763\u5e08\uff0c\u7956\u5927\u5bff\u542c\u6211\u547d\u4ee4\u3002\u73b0\u4eca\u6211\u662f\u76d1\u72f1\u91cc\u7684\u72af\u4eba\uff0c\u5c31\u7b97\u5199\u4e86\u4fe1\uff0c\u7956\u5927\u5bff\u4e5f\u4e0d\u4f1a\u91cd\u89c6\u3002\u201d\u4f46\u5d07\u796f\u4e0d\u80af\u4f4e\u5934\uff0c\u4e0d\u80af\u6b63\u5f0f\u4e0b\u65e8\u547d\u4ed6\u5199\u4fe1\uff0c\u53ea\u662f\u4e0d\u65ad\u6d3e\u592a\u76d1\u51fa\u6765\u50ac\u4fc3\u3002\u540e\u6765\u5175\u90e8\u804c\u65b9\u53f8\u90ce\u4e2d\u4f59\u5927\u6210\u529d\u8881\u5d07\u7115\u8bf4\uff1a\u201c\u4f60\u7684\u5fe0\u5fc3\u548c\u5927\u529f\uff0c\u5929\u4e0b\u7686\u77e5\u3002\u541b\u8981\u81e3\u6b7b\uff0c\u4e0d\u5f97\u4e0d\u6b7b\uff0c\u7ec8\u987b\u4ee5\u56fd\u5bb6\u4e3a\u91cd\u3002\u201d\u8881\u5d07\u7115\u60f3\u5230\u4e86\u201c\u4ee5\u56fd\u5bb6\u4e3a\u91cd\u201d\u4e94\u5b57\uff0c\u4e8e\u662f\u514b\u5236\u4e86\u81ea\u5df1\u7684\u5014\u5f3a\u813e\u6c14\uff0c\u5199\u4e86\u4e00\u5c01\u6781\u8bda\u6073\u7684\u4fe1\uff0c\u8981\u7956\u5927\u5bff\u56de\u5175\u9632\u5b88\u5317\u4eac\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u7956\u5927\u5bff\u5df2\u51b2\u51fa\u5c71\u6d77\u5173\u5317\u53bb\uff0c\u5d07\u796f\u6d3e\u4eba\u98de\u9a91\u8ffd\u53bb\u9001\u4fe1\u3002\u8ffd\u5230\u519b\u524d\uff0c\u7956\u5927\u5bff\u519b\u4e2d\u559d\u4ee4\u653e\u7bad\uff0c\u8fd9\u65f6\u8881\u90e8\u5c06\u58eb\u6012\u4e0d\u53ef\u904f\uff0c\u5df2\u628a\u5d07\u796f\u5f53\u654c\u4eba\u4e86\u3002\u9001\u4fe1\u7684\u4eba\u5927\u53eb\uff1a\u201c\u6211\u5949\u8881\u7763\u5e08\u4e4b\u547d\uff0c\u9001\u4fe1\u6765\u7ed9\u7956\u603b\u5175\uff0c\u4e0d\u662f\u671d\u5ef7\u7684\u8ffd\u5175\u3002\u201d\u7956\u5927\u5bff\u9a91\u5728\u9a6c\u4e0a\uff0c\u7b49\u4ed6\u8fc7\u6765\u3002\u4f7f\u8005\u9012\u8fc7\u4fe1\u53bb\u3002\u7956\u5927\u5bff\u8bfb\u4e86\u4fe1\u540e\uff0c\u4e0b\u9a6c\u6367\u4fe1\u5927\u54ed\uff0c\u4e00\u519b\u90fd\u5927\u54ed\u3002\u7956\u5927\u5bff\u5bf9\u6bcd\u4eb2\u5f88\u5b5d\u987a\uff0c\u4ed6\u6bcd\u4eb2\u53c8\u5f88\u52c7\u6562\uff0c\u513f\u5b50\u884c\u519b\u6253\u4ed7\uff0c\u516b\u5341\u591a\u5c81\u7684\u8001\u592a\u592a\u5e38\u5e38\u8ddf\u7740\u90e8\u961f\u3002\u8fd9\u65f6\u5979\u529d\u513f\u5b50\u8bf4\uff1a\u201c\u672c\u6765\u4ee5\u4e3a\u7763\u5e08\u5df2\u7ecf\u6b7b\u4e86\uff0c\u54b1\u4eec\u624d\u53cd\u51fa\u5173\u6765\uff0c\u8c22\u5929\u8c22\u5730\uff0c\u539f\u6765\u7763\u5e08\u5e76\u6ca1\u6709\u6b7b\u3002\u4f60\u6253\u51e0\u4e2a\u80dc\u4ed7\uff0c\u518d\u53bb\u6c42\u7687\u4e0a\u8d66\u514d\u7763\u519b\uff0c\u7687\u4e0a\u5c31\u4f1a\u7b54\u5141\u3002\u73b0\u4eca\u8fd9\u6837\u53cd\u4e86\u51fa\u53bb\uff0c\u53ea\u6709\u52a0\u91cd\u7763\u5e08\u7684\u7f6a\u540d\u3002\u201d \u3000\u3000\u7956\u5927\u5bff\u89c9\u5f97\u6bcd\u4eb2\u7684\u8bdd\u5f88\u5bf9\uff0c\u5f53\u5373\u56de\u5e08\u5165\u5173\uff0c\u548c\u6e05\u5175\u63a5\u6218\uff0c\u6536\u590d\u4e86\u6c38\u5e73\u3001\u9075\u5316\u4e00\u5e26\u3002\u4e5f\u5373\u662f\u5207\u65ad\u4e86\u6e05\u5175\u7684\u4e24\u6761\u91cd\u8981\u9000\u8def\u2480\u3002 \u3000\u3000\u5982\u679c\u8fd9\u65f6\u5d07\u796f\u7acb\u523b\u6094\u609f\uff0c\u653e\u8881\u5d07\u7115\u51fa\u6765\u91cd\u884c\u5e26\u5175\uff0c\u4ecd\u7136\u5927\u6709\u51fb\u7834\u6e05\u5175\u7684\u673a\u4f1a\u3002\u4f46\u5d07\u796f\u53ea\u662f\u4e00\u5473\u6025\u8e81\u6c42\u6218\uff0c\u4e0b\u65e8\u5206\u8bbe\u6587\u6b66\u4e24\u7ecf\u7565\u3002\u8fd9\u53c8\u662f\u4e8b\u6743\u4e0d\u7edf\u4e00\u7684\u5927\u9519\u8bef\uff0c\u5927\u6982\u4ed6\u4ee5\u4e3a\u6587\u6b66\u5206\u6743\uff0c\u603b\u4e0d\u80fd\u4e24\u4e2a\u7ecf\u7565\u4e00\u8d77\u9020\u53cd\u3002\u6587\u7ecf\u7565\u662f\u5175\u90e8\u5c1a\u4e66\u6881\u5ef7\u680b\uff0c\u6b66\u7ecf\u7565\u662f\u6ee1\u6842\u3002 \u3000\u3000\u6e05\u5175\u4e8e\u5341\u4e8c\u6708\u521d\u4e00\u653b\u514b\u826f\u4e61\uff0c\u5f97\u5230\u8881\u5d07\u7115\u4e0b\u72f1\u7684\u6d88\u606f\uff0c\u7687\u592a\u6781\u5927\u559c\uff0c\u7acb\u5373\u81ea\u826f\u4e61\u56de\u519b\uff0c\u81f3\u82a6\u6c9f\u6865\uff0c\u51fb\u7834\u660e\u526f\u603b\u5175\u7533\u752b\u7684\u8f66\u8425\uff0c\u8feb\u8fd1\u5317\u4eac\u6c38\u5b9a\u95e8\u3002 \u3000\u3000\u7533\u752b\u7684\u6240\u8c13\u201c\u8f66\u8425\u201d\uff0c\u662f\u5d07\u796f\u5728\u60f6\u6025\u4e2d\u6240\u505a\u7684\u8bb8\u591a\u53ef\u7b11\u4e8b\u60c5\u4e4b\u4e00\u3002\u7533\u752b\u672c\u6765\u662f\u4e2a\u548c\u5c1a\uff0c\u5f02\u60f3\u5929\u5f00\u7684\u201c\u53d1\u660e\u201d\u4e86\u8bb8\u591a\u65b0\u5f0f\u6b66\u5668\uff0c\u5305\u62ec\u72ec\u8f6e\u706b\u8f66\u3001\u517d\u8f66\u3001\u6728\u5236\u897f\u5f0f\u67aa\u70ae\u7b49\u7b49\uff0c\u81ea\u5439\u6548\u529b\u5b8f\u5927\u3002\u5d07\u796f\u4fe1\u4ee5\u4e3a\u771f\uff0c\u7acb\u5373\u5347\u4ed6\u4e3a\u526f\u603b\u5175\uff0c\u53d1\u94b1\u7ed9\u4ed6\u5728\u5317\u4eac\u57ce\u91cc\u62db\u52df\u4e86\u6570\u5343\u540d\u5e02\u4e95\u6d41\u6c13\uff0c\u6210\u7acb\u65b0\u5f0f\u6b66\u5668\u7684\u6218\u8f66\u90e8\u961f\u3002\u5927\u5b66\u58eb\u6210\u57fa\u547d\u53bb\u68c0\u9605\u65b0\u519b\uff0c\u8ba4\u4e3a\u51b3\u4e0d\u53ef\u7528\uff0c\u5d07\u796f\u4e0d\u542c\u3002\u7687\u592a\u6781\u56de\u5e08\u653b\u6765\u65f6\uff0c\u8fd9\u4e2a\u6218\u8f66\u90e8\u961f\u51fa\u57ce\u4ea4\u950b\uff0c\u4e00\u89e6\u5373\u6e83\uff0c\u6728\u5236\u5927\u70ae\u81ea\u884c\u7206\u70b8\uff0c\u548c\u5c1a\u53d1\u660e\u5bb6\u9635\u4ea1\u3002 \u3000\u3000\u6ee1\u6842\u8eab\u7ecf\u767e\u6218\uff0c\u6df1\u77e5\u5e94\u5f53\u6301\u91cd\uff0c\u4e0d\u53ef\u5192\u9669\u6c42\u6218\uff0c\u4f46\u7687\u5e1d\u50ac\u5f97\u6025\u8feb\u4e4b\u81f3\uff0c\u82e5\u4e0d\u51fa\u6218\uff0c\u52bf\u5fc5\u4e0e\u8881\u5d07\u7115\u4e00\u6837\uff0c\u65e0\u53ef\u5948\u4f55\u4e4b\u4e0b\uff0c\u53ea\u5f97\u4e0e\u603b\u5175\u5b59\u7956\u5bff\u3001\u9ebb\u767b\u4e91\u3001\u9ed1\u4e91\u9f99\u7b49\u96c6\u9a91\u5175\u3001\u6b65\u5175\u56db\u4e07\u5217\u9635\u3002\u7687\u592a\u6781\u4ee4\u90e8\u5c5e\u5192\u7a7f\u660e\u5175\u670d\u88c5\uff0c\u62ff\u4e86\u660e\u519b\u65d7\u5e1c\uff0c\u9ece\u660e\u65f6\u5206\u7a81\u7136\u653b\u8fd1\u3002\u660e\u519b\u4e0d\u5206\u53cb\u654c\uff0c\u767b\u65f6\u5927\u4e71\uff0c\u6ee1\u6842\u3001\u5b59\u7956\u5bff\u90fd\u6218\u6b7b\uff0c\u9ed1\u4e91\u9f99\u3001\u9ebb\u767b\u4e91\u88ab\u64d2\u3002\u4eac\u5e08\u5927\u9707\u3002 \u3000\u3000\u8fd9\u65f6\u7956\u5927\u5bff\u3001\u4f55\u53ef\u7eb2\u7b49\u5f97\u5230\u8881\u5d07\u7115\u72f1\u4e2d\u624b\u4e66\uff0c\u53c8\u8fd8\u5175\u6765\u6551\u3002\u7687\u592a\u6781\u5bf9\u8881\u90e8\u7ec8\u662f\u5fcc\u60ee\uff0c\u611f\u5230\u540e\u8def\u6240\u53d7\u5230\u7684\u5a01\u80c1\u4e25\u91cd\uff0c\u4e8e\u662f\u5e76\u4e0d\u8fdb\u653b\u5317\u4eac\uff0c\u5199\u4e86\u4e24\u5c01\u8bae\u548c\u7684\u4fe1\uff0c\u653e\u5728\u5b89\u5b9a\u95e8\u548c\u5fb7\u80dc\u95e8\u57ce\u95e8\u53e3\uff0c\u53d6\u9053\u51b7\u53e3\u800c\u8fd8\u8fbd\u4e1c\u3002 \u3000\u3000\u5f53\u6e05\u5175\u56f4\u57ce\u65f6\uff0c\u5d07\u796f\u7684\u5f20\u7687\u5931\u63aa\uff0c\u4e0d\u5355\u8868\u73b0\u5728\u5c06\u8881\u5d07\u7115\u4e0b\u72f1\u4e00\u4e8b\u4e0a\uff0c\u6b64\u5916\u5012\u9709\u7684\u5927\u81e3\u8fd8\u6709\u4e0d\u5c11\u3002\u4ed6\u8ba4\u4e3a\u5175\u90e8\u5c1a\u4e66\u738b\u6d3d\u5904\u7f6e\u4e0d\u5584\uff0c\u4e0b\u72f1\u3002\u738b\u6d3d\u76f8\u8c8c\u5802\u5802\uff0c\u9b41\u68a7\u5a01\u731b\uff0c\u5f53\u65f6\u662f\u5f88\u51fa\u540d\u7684\u3002\u5d07\u796f\u7528\u4ed6\u505a\u5175\u90e8\u5c1a\u4e66\uff0c\u5c31\u662f\u770b\u4e2d\u4e86\u4ed6\u7684\u76f8\u8c8c\uff0c\u8bf4\u4ed6\u50cf\u4e2a\u201c\u95e8\u795e\u201d\u3002\u5f53\u65f6\u5317\u4eac\u4eba\u79c1\u4e0b\u8bf4\uff0c\u95e8\u795e\u4e00\u5e74\u4e00\u6362\uff0c\u8fd9\u4e2a\u738b\u95e8\u795e\u7684\u5175\u90e8\u5c1a\u4e66\u4e00\u5b9a\u505a\u4e0d\u957f\u4e45\u3002\u679c\u7136\u4e0d\u5230\u8fc7\u5e74\uff0c\u95e8\u795e\u5c31\u9664\u4e0b\u6765\u4e86\u3002\u56f4\u57ce\u65f6\u4e00\u5207\u6df7\u4e71\uff0c\u76d1\u72f1\u4e2d\u7684\u56da\u72af\u4e58\u673a\u5927\u4e3e\u8d8a\u72f1\uff0c\u4e8e\u662f\u5211\u90e8\u5c1a\u4e66\u548c\u4f8d\u90ce\u4e0b\u72f1\u3002\u5d07\u796f\u53c8\u201c\u53d1\u89c9\u201d\u5317\u4eac\u7684\u57ce\u5899\u4e0d\u5927\u575a\u56fa\uff0c\u4f3c\u4e4e\u6321\u4e0d\u4f4f\u6e05\u5175\u731b\u653b\uff0c\u5176\u5b9e\uff0c\u90a3\u65f6\u57ce\u5899\u5c31\u7b97\u575a\u56fa\u4e4b\u6781\uff0c\u4ed6\u4e5f\u4f1a\u89c9\u5f97\u8fd8\u4e0d\u591f\u575a\u56fa\uff0c\u4e8e\u662f\u5c06\u5de5\u90e8\u5c1a\u4e66\u548c\u5de5\u90e8\u51e0\u540d\u90ce\u4e2d\u4e00\u8d77\u5728\u671d\u5ef7\u4e0a\u5404\u6253\u516b\u5341\u68cd\u518d\u4e0b\u72f1\u3002\u4e09\u4e2a\u90ce\u4e2d\u4e24\u4e2a\u5e74\u8001\u3001\u4e00\u4e2a\u4f53\u5f31\uff0c\u90fd\u5728\u6bbf\u4e0a\u5f53\u573a\u6d3b\u6d3b\u6253\u6b7b\u4e86\u3002\u81f3\u4e8e\u90a3\u4e2a\u84df\u8fbd\u603b\u7763\u5218\u7b56\uff0c\u4ed6\u8d1f\u8d23\u7684\u957f\u57ce\u9632\u7ebf\u88ab\u6e05\u5175\u653b\u7834\uff0c\u5d07\u796f\u5c06\u4ed6\u5904\u6b7b\uff0c\u66f4\u662f\u4e0d\u5728\u8bdd\u4e0b\u3002 \u3000\u3000\u5f53\u65f6\u5404\u5730\u6765\u5317\u4eac\u52e4\u738b\u7684\u90e8\u961f\u7740\u5b9e\u4e0d\u5c11\uff0c\u672c\u6765\u7531\u8881\u5d07\u7115\u7edf\u4e00\u6307\u6325\uff0c\u5927\u53ef\u53d1\u6325\u5a01\u529b\u3002\u8881\u5d07\u7115\u4e00\u4e0b\u72f1\uff0c\u5404\u8def\u5175\u9a6c\u519b\u5fc3\u5927\u4e71\uff0c\u518d\u52a0\u4e0a\u6b20\u9977\u548c\u6307\u6325\u6df7\u4e71\uff0c\u5c71\u897f\u548c\u9655\u897f\u7684\u4e24\u8def\u63f4\u519b\u90fd\u6e83\u6563\u56de\u4e61\uff0c\u6210\u4e3a\u201c\u6d41\u5bc7\u201d\u7684\u9aa8\u5e72\u3002\u201c\u6d41\u5bc7\u201d\u672c\u6765\u90fd\u662f\u9965\u6c11\uff0c\u53ea\u4f1a\u62a2\u7cae\uff0c\u4e0d\u4f1a\u6253\u4ed7\uff0c\u8fd9\u4e9b\u6e83\u5175\u4e00\u52a0\u5165\uff0c\u6709\u4e86\u519b\u4e8b\u4e0a\u7684\u9886\u5bfc\uff0c\u60c5\u5f62\u5b8c\u5168\u4e0d\u540c\u4e86\u3002\u201c\u6d41\u5bc7\u201d\u771f\u6b63\u6210\u4e3a\u660e\u671d\u7684\u5a01\u80c1\uff0c\u5c31\u4ece\u90a3\u65f6\u5f00\u59cb\u3002 \u3000\u3000\u2460\u300a\u660e\u6e05\u53f2\u6599\u300b\u7532\u7f16\uff0c\u5d07\u796f\u4e8c\u5e74\u4e94\u6708\uff0c\u8881\u5d07\u7115\u594f\uff1a\u201c\u4eca\u5404\u8fb9\u5175\u9977\uff0c\u5386\u8fc7\u672a\u7ed9\u4e8c\u767e\u4f59\u4e07\u3002\u51e1\u8bf7\u9977\u4e4b\u758f\uff0c\u4ff1\u672a\u8499\u6e29\u8c15\uff0c\u800c\u7d22\u9977\u5175\u54d7\uff0c\u5219\u91cd\u5904\u4efb\u4e8b\u4e4b\u81e3\u3002\u4e00\u756a\u5171\u54d7\uff0c\u4e00\u756a\u53d1\u7ed9\uff0c\u4e00\u756a\u902e\u6cbb\u3002\u54d7\u5219\u9977\uff0c\u4e0d\u54d7\u5219\u4e0d\u5f97\u9977\u3002\u53bb\u5e74\u4e4b\u5b81\u8fdc\uff0c\u4eca\u5e74\u4e4b\u9075\u5316\uff0c\u8c13\u54d7\u4e0d\u7531\u9977\u4e4e\uff1f\u8fd1\u5404\u9547\u591a\u4ee5\u54d7\u77e3\u3002\u54d7\u4e0d\u80dc\u54d7\uff0c\u8bdb\u4e0d\u80dc\u8bdb\uff0c\u5916\u9632\u864f\u8ba7\uff0c\u5185\u9632\u5175\u6e83\u3002\u5982\u79e6\u4e4b\u5927\u76d7\uff0c\u54d7\u5175\u4e3a\u5021\uff0c\u53ef\u9274\u4e5f\u3002\u201d \u3000\u3000\u2461\u9ec4\u5b97\u7fb2\u300a\u660e\u5937\u5f85\u8bbf\u5f55\xb7\u5efa\u90fd\u300b\uff1a\u201c\u5317\u90fd\u4e4b\u4ea1\u5ffd\u7109\uff0c\u5176\u6545\u4f55\u4e5f\uff1f\u66f0\uff1a\u4ea1\u4e4b\u9053\u4e0d\u4e00\uff0c\u800c\u5efa\u90fd\u5931\u7b97\uff0c\u6240\u4ee5\u4e0d\u53ef\u6551\u4e5f\u2026\u2026\u6709\u660e\u90fd\u71d5\u4e0d\u8fc7\u4e8c\u767e\u5e74\uff0c\u800c\u82f1\u5b97\u72e9\u4e8e\u571f\u6728\uff0c\u6b66\u5b97\u56f0\u4e8e\u9633\u548c\uff0c\u666f\u6cf0\u521d\u4eac\u57ce\u53d7\u56f4\uff0c\u5609\u9756\u4e8c\u5341\u516b\u5e74\u53d7\u56f4\uff0c\u56db\u5341\u4e09\u5e74\u8fb9\u4eba\u9611\u5165\u3002\u5d07\u796f\u95f4\u4eac\u57ce\u5c81\u5c81\u6212\u4e25\uff0c\u4e0a\u4e0b\u7cbe\u795e\u6bd9\u4e8e\u5bc7\u81f3\uff0c\u65e5\u4ee5\u5931\u5929\u4e0b\u4e3a\u4e8b\uff0c\u800c\u793c\u4e50\u653f\u6559\u72b9\u8db3\u89c2\u4e4e\uff1f\u201d \u3000\u3000C.P.Fitzgerald:China,A Short Cultural History\uff08\u6ce8\uff1a\uff23\uff0e\uff30\uff0e\uff26\uff49\uff54\uff5a\uff47\uff45\uff52\uff41\uff4c\uff44\uff1a\uff23\uff48\uff49\uff4e\uff41\uff0c\uff21\uff33\uff48\uff4f\uff52\uff54\uff23\uff55\uff4c\uff54\uff55\uff52\uff41\uff4c\uff28\uff49\uff53\uff54\uff4f\uff52\uff59\uff09\uff08\u4e2d\u56fd\u6587\u5316\u7b80\u53f2\uff09\uff1a\u201c\u9996\u90fd\u7684\u5730\u4f4d\uff0c\u662f\u660e\u671d\u4e3b\u8981\u7684\u5f31\u70b9\u4e4b\u4e00\uff0c\u662f\u5b83\u8986\u4ea1\u7684\u4e3b\u8981\u539f\u56e0\u3002\u201d\u8be5\u4e66\u5bf9\u660e\u671d\u5efa\u90fd\u5317\u4eac\u7684\u4e0d\u5229\u6709\u8be6\u7ec6\u5206\u6790\uff0c\u89c1P.463-464\uff08\u6ce8\uff1a\uff30\uff0e\uff14\uff16\uff13\uff0d\uff14\uff16\uff14\uff09\u3002 \u3000\u3000\u2462Arnold Toynbee:A Study of History\uff08\u6ce8\uff1a\uff21\uff52\uff4e\uff4f\uff4c\uff44\uff34\uff4f\uff59\uff4e\uff42\uff45\uff45\uff1a\uff21\uff33\uff54\uff55\uff44\uff59\uff4f\uff46\uff28\uff49\uff53\uff54\uff4f\uff52\uff59\uff09\uff08\u5386\u53f2\u7814\u7a76\uff09\u7684\u5f15\u8bba\u4e2d\u8bf4\uff1a\u201c\u4e00\u4e2a\u6bd4\u8f83\u6587\u660e\u7684\u793e\u4f1a\u4e0e\u4e00\u4e2a\u6bd4\u8f83\u843d\u540e\u7684\u793e\u4f1a\u4e4b\u95f4\u7684\u7586\u754c\uff0c\u5982\u679c\u4e0d\u518d\u63a8\u79fb\uff0c\u7586\u754c\u4e0d\u4f1a\u5c31\u6b64\u5e73\u8861\u7a33\u5b9a\uff0c\u65f6\u95f4\u8fc7\u53bb\uff0c\u53d1\u5c55\u4f1a\u503e\u5411\u4e8e\u5bf9\u6bd4\u8f83\u843d\u540e\u7684\u793e\u4f1a\u6709\u5229\u3002\u201d \u3000\u3000\u2463Bertrand Russell:The Problem of China\uff08\u6ce8\uff1a\uff22\uff45\uff52\uff54\uff52\uff41\uff4e\uff44\uff32\uff55\uff53\uff53\uff45\uff4c\uff4c\uff1a\uff34\uff48\uff45\uff30\uff52\uff4f\uff42\uff4c\uff45\uff4d\uff4f\uff46\uff23\uff48\uff49\uff4e\uff41\uff09\uff08\u4e2d\u56fd\u95ee\u9898\uff09\uff1a\u201c\u4e2d\u534e\u5e1d\u56fd\u6240\u4ee5\u80fd\u591f\u4e00\u76f4\u6301\u7eed\u5230\u4eca\u65e5\uff0c\u5e76\u975e\u7531\u4e8e\u4efb\u4f55\u519b\u4e8b\u6280\u672f\uff1b\u76f8\u53cd\u7684\uff0c\u4ee5\u5b83\u7684\u7586\u57df\u548c\u8d44\u6e90\u6765\u8bf4\uff0c\u5728\u5927\u591a\u6570\u65f6\u95f4\u4e2d\uff0c\u5b83\u5728\u6218\u4e89\u4e2d\u7684\u8868\u73b0\u90fd\u662f\u8870\u5f31\u65e0\u80fd\u7684\u3002\u201d \u3000\u3000\u2464\u7687\u592a\u6781\u5728\u56de\u519b\u7684\u8c15\u793a\u4e2d\u8bf4\uff0c\u6b64\u884c\u662f\u201c\u6e21\u9648\u4ed3\u3001\u9634\u5e73\u4e4b\u9053\uff0c\uff08\u5b9a\uff09\u7834\u91dc\u6c89\u821f\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u2465\u300a\u5d07\u796f\u957f\u7f16\u300b\uff0c\u5341\u4e00\u6708\u5341\u4e94\u65e5\u5175\u90e8\u6709\u758f\u4e91\uff1a\u201c\u757f\u4e1c\u5dde\u53bf\uff0c\u98ce\u9e64\u76f8\u60ca\uff0c\u4eba\u65e0\u56fa\u5fd7\u3002\u81ea\u7763\u5e08\u63d0\u5175\u5165\u63f4\uff0c\u5206\u6d3e\u9a7b\u9632\uff0c\u9042\u5c79\u7136\u65e0\u6059\u3002\u201d\u5f97\u65e8\uff1a\u201c\u8c15\u5175\u90e8\uff1a\u8881\u5d07\u7115\u5165\u5173\u8d74\u63f4\uff0c\u9a7b\u5e08\u4e30\u6da6\uff0c\u4e0e\u84df\u519b\u4e1c\u897f\u7317\u89d2\uff0c\u6715\u751a\u5609\u6170\u3002\u5373\u4f20\u8c15\u5d07\u7115\uff0c\u591a\u65b9\u7b79\u5212\uff0c\u8ba1\u51fa\u4e07\u5168\uff0c\u901f\u5efa\u5947\u529f\uff0c\u4ee5\u81ba\u61cb\u8d4f\u3002\u201d\u53c8\u8c15\uff1a\u201c\u5404\u8def\u63f4\u5175\uff0c\u5168\u542c\u7763\u5e08\u8881\u5d07\u7115\u8c03\u5ea6\u3002\u201d\u5d07\u796f\u8fd9\u9053\u4e0a\u8c15\u4e2d\uff0c\u201c\u8ba1\u51fa\u4e07\u5168\u201d\u4e0e\u201c\u901f\u5efa\u5947\u529f\u201d\u4e24\u4ef6\u4e8b\u6839\u672c\u662f\u5927\u5927\u77db\u76fe\u7684\u3002 \u3000\u3000\u2466\u671d\u9c9c\u5bf9\u660e\u6e05\u6218\u4e8b\u5bc6\u5207\u6ce8\u610f\uff0c\u6240\u4ee5\u671d\u9c9c\u65b9\u9762\u7684\u8bb0\u8f7d\u4e5f\u5f88\u6709\u53c2\u8003\u4ef7\u503c\u3002\u636e\u671d\u9c9c\u300a\u4ec1\u7956\u5b9e\u5f55\u300b\u5377\u4e8c\u5341\u4e8c\uff1a\u201c\uff08\u8881\uff09\u519b\u95e8\u9886\u8bf8\u5c06\u53ca\u4e00\u4e07\u56db\u5343\u5175\u2026\u2026\u7531\u95f4\u8def\u9a70\u8fdb\u5317\u4eac\uff0c\u4e0e\u8d3c\u5bf9\u9635\u4e8e\u7687\u57ce\u9f50\u5316\u95e8\u3002\u8d3c\u76f4\u5230\u6c99\u7a9d\u95e8\u3002\u8881\u519b\u95e8\u3001\u7956\u603b\u5175\u7b49\uff0c\u81ea\u5348\u81f3\u9149\uff0c\u9b54\u6218\u5341\u6570\u5408\uff0c\u81f3\u4e8e\u4e2d\u7bad\uff0c\u5e78\u800c\u5f97\u6377\uff0c\u8d3c\u9000\u5175\u4e09\u5341\u91cc\u3002\u8d3c\u4e4b\u5f97\u4e0d\u653b\u9677\u4eac\u57ce\u8005\uff0c\u76d6\u56e0\u4e24\u5c06\u529b\u6218\u4e4b\u529f\u4e5f\u3002\u201d \u3000\u3000\u2467\u300a\u6e05\u53f2\u7a3f\xb7\u963f\u5df4\u6cf0\u4f20\u300b\u3002 \u3000\u3000\u2468\u300a\u5b59\u5b50\u300b\uff1a\u201c\u6545\u5584\u6218\u8005\uff0c\u81f4\u4eba\u800c\u4e0d\u81f4\u4e8e\u4eba\u3002\u201d\u201c\u4ee5\u8fd1\u5f85\u8fdc\uff0c\u4ee5\u4f5a\u5f85\u52b3\u3002\u201d\u201c\u6545\u5584\u7528\u5175\u8005\uff0c\u907f\u5176\u9510\u6c14\uff0c\u51fb\u5176\u60f0\u5f52\u3002\u201d \u3000\u3000\u247d\u300a\u5d07\u796f\u957f\u7f16\u300b\u4e8c\u5e74\u5341\u4e00\u6708\u5341\u4e03\u65e5\uff0c\u5175\u79d1\u7ed9\u4e8b\u4e2d\u9676\u5d07\u9053\u758f\u8a00\uff1a\u201c\u6628\u5de5\u90e8\u5c1a\u4e66\u5f20\u98ce\u7fd4\u4eb2\u81f3\u57ce\u5934\uff0c\u4e0e\u81e3\u540c\u9605\u706b\u5668\uff0c\u89c1\u57ce\u697c\u6240\u79ef\u8005\uff0c\u6709\u5176\u5177\u800c\u4e0d\u77e5\u5176\u540d\uff0c\u6709\u5176\u540d\u800c\u4e0d\u77e5\u5176\u7528\uff0c\u8be2\u4e4b\u5c06\u9886\uff0c\u7686\u5404\u832b\u7136\uff0c\u95ee\u4e4b\u58eb\u5352\uff0c\u767e\u65e0\u4e00\u8bc6\u3002\u6709\u5176\u5668\u800c\u4e0d\u80fd\u7528\uff0c\u4e0e\u65e0\u5668\u540c\uff1b\u65e0\u5176\u5668\u4ee5\u4e58\u57ce\uff0c\u4e0e\u65e0\u57ce\u540c\u3002\u81e3\u7b49\u80fd\u4e0d\u4e3a\u4e4b\u5fc3\u5bd2\u4e4e\uff1f\u201d\u660e\u519b\u5b88\u57ce\uff0c\u4e3b\u8981\u662f\u9760\u706b\u5668\uff0c\u5b88\u57ce\u5c06\u58eb\u8fde\u706b\u5668\u90fd\u4e0d\u4f1a\u4f7f\u7528\uff0c\u7531\u653e\u5927\u70ae\u53cd\u800c\u6740\u4f24\u6ee1\u6842\u90e8\u961f\u53ef\u77e5\u3002\u5982\u679c\u6ca1\u6709\u8881\u5d07\u7115\u6765\u63f4\uff0c\u5317\u4eac\u975e\u7ed9\u6e05\u5175\u653b\u9677\u4e0d\u53ef\u3002 \u3000\u3000\u247e\u636e\u738b\u6c0f\u300a\u4e1c\u534e\u5f55\u300b\u5929\u806a\u4e09\u5e74\u6240\u8f7d\u3002\u53c8\u636e\u300a\u5d07\u796f\u957f\u7f16\u300b\u4e8c\u5e74\u5341\u4e8c\u6708\u7532\u5b50\uff1a\u201c\u5927\u6e05\u5175\u9a7b\u5357\u6d77\u5b50\uff0c\u63d0\u7763\u5927\u575d\u9a6c\u623f\u592a\u76d1\u6768\u6625\u3001\u738b\u6210\u5fb7\u4e3a\u5927\u6e05\u5175\u6240\u83b7\uff0c\u53e3\u79f0\uff1a\u2018\u6211\u662f\u4e07\u5c81\u7237\u517b\u9a6c\u7684\u5b98\u513f\u3002\u2019\u5927\u6e05\u5175\u5c06\u6768\u6625\u7b49\u5e26\u81f3\u5fb7\u80dc\u95e8\u9c8d\u59d3\u7b49\u4eba\u770b\u5b88\u3002\u201d \u3000\u3000\u247f\u5d07\u796f\u4e8c\u5e74\u5341\u4e8c\u6708\u7532\u620c\uff0c\u7956\u5927\u5bff\u758f\u8a00\uff1a\u201c\u6bd4\u56e0\u8881\u5d07\u7115\u88ab\u62ff\uff0c\u5ba3\u8bfb\u5723\u8c15\uff0c\u4e09\u519b\u653e\u58f0\u5927\u54ed\uff0c\u81e3\u7528\u597d\u8a00\u6170\u6b62\uff0c\u4e14\u4ee4\u594b\u52c7\u56fe\u529f\u4ee5\u8d4e\u7763\u5e08\u4e4b\u7f6a\uff0c\u6b64\u6367\u65e8\u5185\u81e3\u53ca\u57ce\u4e0a\u4eba\u6240\u5171\u95fb\u5171\u89c1\u8005\uff0c\u5948\u8bb9\u8a00\u65e5\u70bd\uff0c\u5175\u5fc3\u5df2\u4f24\u3002\u521d\u4e09\u65e5\uff0c\u591c\u54e8\u89c1\u6d77\u5b50\u5916\u8425\u706b\uff0c\u53d1\u5175\u591c\u51fb\uff0c\u672c\u6b32\u62da\u547d\u4e00\u6218\uff0c\u671f\u5efa\u5947\u529f\uff0c\u4ee5\u91ca\u5185\u5916\u4e4b\u7591\uff0c\u4e0d\u6599\u5175\u5ffd\u4e1c\u5954\u2026\u2026\u201d\u7956\u5927\u5bff\u6b64\u758f\u5f53\u7136\u6709\u5378\u514d\u81ea\u5df1\u8d23\u4efb\u7684\u7528\u610f\uff0c\u4f46\u5f53\u65f6\u58eb\u5352\u6124\u6168\u4e07\u5206\uff0c\u81ea\u52a8\u4e1c\u5954\u7684\u60c5\u5f62\u4e5f\u5fc5\u5b58\u5728\u3002 \u3000\u3000\u2480\u8881\u5d07\u7115\u72f1\u4e2d\u5199\u4fe1\u3001\u7956\u5927\u5bff\u63a5\u4fe1\u540e\u56de\u5e08\u7b49\u60c5\u72b6\u89c1\u4f59\u5927\u6210\u300a\u5256\u809d\u5f55\u300b\u3002\u6c38\u5e73\u5373\u4eca\u5362\u9f99\u53bf\uff0c\u5f53\u65f6\u4e3a\u5e9c\u6cbb\u3002 \u3000\u3000\u5341\u4e09 \u3000\u3000\u8881\u5d07\u7115\u8499\u51a4\u4e0b\u72f1\uff0c\u671d\u4e2d\u7fa4\u81e3\u5927\u90fd\u77e5\u4ed6\u51a4\u6789\u3002\u5185\u9601\u5927\u5b66\u58eb\u5468\u5ef6\u5112\u548c\u6210\u57fa\u547d\u3001\u540f\u90e8\u5c1a\u4e66\u738b\u6765\u5149\u90fd\u4e0a\u758f\u89e3\u6551\u3002\u603b\u5175\u7956\u5927\u5bff\u4e0a\u4e66\uff0c\u613f\u524a\u804c\u4e3a\u6c11\uff0c\u4e3a\u7687\u5e1d\u6b7b\u6218\u5c3d\u529b\uff0c\u4ee5\u5b98\u9636\u8d60\u836b\u8bf7\u8d4e\u8881\u5d07\u7115\u4e4b\u201c\u7f6a\u201d\u3002\u8881\u5d07\u7115\u7684\u90e8\u5c5e\u4f55\u4e4b\u58c1\u7387\u540c\u5168\u5bb6\u56db\u5341\u4f59\u53e3\uff0c\u5230\u5bab\u5916\u7533\u8bf7\uff0c\u613f\u610f\u5168\u5bb6\u5165\u72f1\uff0c\u4ee3\u66ff\u8881\u5d07\u7115\u51fa\u6765\u3002\u5d07\u796f\u4e00\u6982\u4e0d\u51c6\u3002 \u3000\u3000\u5d07\u796f\u4e00\u5b9a\u5f88\u6e05\u695a\u7684\u77e5\u9053\uff0c\u5355\u51ed\u6768\u592a\u76d1\u4ece\u6e05\u519b\u90a3\u91cc\u542c\u6765\u7684\u51e0\u53e5\u8bdd\uff0c\u5c31\u6b64\u5224\u5b9a\u8881\u5d07\u7115\u6709\u7f6a\uff0c\u90a3\u662f\u4e0d\u80fd\u4ee4\u4eba\u4fe1\u670d\u7684\uff0c\u4f55\u51b5\u8fd9\u201c\u7fa4\u82f1\u4f1a\u848b\u5e72\u4e2d\u8ba1\u201d\u7684\u6545\u4e8b\uff0c\u4eba\u4eba\u7686\u77e5\u3002\u7687\u5e1d\u800c\u6210\u4e86\u5927\u767d\u8138\u66f9\u64cd\uff0c\u592a\u4e5f\u53ef\u7f9e\u3002\u8fd9\u65f6\u53d1\u751f\u4e86\u4e00\u4ef6\u5947\u602a\u7684\u4e8b\uff1a\u5fa1\u53f2\u66f9\u6c38\u795a\u5ffd\u7136\u6349\u5230\u4e86\u5978\u7ec6\u5218\u6587\u745e\u7b49\u4e03\u4eba\uff0c\u81ea\u79f0\u5949\u8881\u5d07\u7115\u4e4b\u547d\u901a\u654c\uff0c\u9001\u4fe1\u53bb\u7ed9\u6e05\u519b\u3002\u8fd9\u4e03\u540d\u5978\u7ec6\u4ea4\u7ed9\u9526\u8863\u536b\u62bc\u7ba1\u3002\u5d07\u796f\u547d\u8bf8\u5927\u81e3\u4f1a\u5ba1\uff0c\u4e0d\u6599\u5230\u7b2c\u4e8c\u5929\u8fb0\u523b\uff0c\u8bf8\u5927\u81e3\u4f1a\u9f50\u5ba1\u8baf\uff0c\u9526\u8863\u536b\u62a5\u79f0\uff1a\u4e03\u540d\u5978\u7ec6\u90fd\u9003\u8d70\u4e86\u3002\u4f17\u5927\u81e3\u76f8\u987e\u6115\u7136\uff0c\u5fc3\u4e2d\u81ea\u7136\u96ea\u4eae\uff0c\u7687\u4e0a\u51b3\u5fc3\u8981\u6740\u8881\u5d07\u7115\u3002\u9526\u8863\u536b\u662f\u7687\u5e1d\u7684\u5fa1\u7528\u8b66\u5bdf\uff0c\u653e\u8d70\u8fd9\u4e03\u540d\u201c\u5978\u7ec6\u201d\uff0c\u81ea\u7136\u662f\u51fa\u4e8e\u7687\u5e1d\u7684\u5bc6\u65e8\u3002\u731c\u60f3\u8d77\u6765\uff0c\u90a3\u5fa1\u53f2\u66f9\u6c38\u795a\u672c\u6765\u60f3\u9644\u548c\u7687\u5e1d\uff0c\u5b89\u6392\u4e86\u4e03\u540d\u5047\u5978\u7ec6\u6765\u8bec\u9677\u8881\u5d07\u7115\uff0c\u4f46\u4e0d\u77e5\u5982\u4f55\uff0c\u90e8\u7f72\u65e0\u6cd5\u5468\u5bc6\uff0c\u9884\u6599\u4f17\u5927\u81e3\u4f1a\u5ba1\u4e00\u5b9a\u4f1a\u9732\u51fa\u9a6c\u811a\u3002\u5d07\u796f\u5c31\u5429\u5490\u9526\u8863\u536b\u5c06\u4e03\u540d\u5978\u7ec6\u653e\u4e86\uff0c\u66f4\u53ef\u80fd\u662f\u6084\u6084\u6740\u4e86\u706d\u53e3\u3002 \u3000\u3000\u5bf9\u4e8e\u8fd9\u4ef6\u4e8b\uff0c\u8d1f\u8d23\u76d1\u5bdf\u67e5\u6838\u519b\u52a1\u7684\u5175\u79d1\u7ed9\u4e8b\u4e2d\u94b1\u5bb6\u4fee\u5411\u7687\u5e1d\u6307\u51fa\u4e86\u4e25\u91cd\u8d23\u95ee\u3002\u5d07\u796f\u96be\u4ee5\u8fa9\u9a73\uff0c\u53ea\u5f97\u6577\u884d\u4ed6\u8bf4\uff0c\u5f85\u5c06\u8881\u5d07\u7115\u5ba1\u95ee\u660e\u767d\u540e\uff0c\u4fbf\u5373\u6d3e\u53bb\u8fb9\u7586\u529e\u4e8b\u7acb\u529f\uff0c\u8fd8\u51c6\u5907\u5347\u4ed6\u7684\u5b98\u3002\u5d07\u796f\u8fd9\u4e2a\u7b54\u590d\uff0c\u5176\u5b9e\u5df2\u7b49\u4e8e\u627f\u8ba4\u8881\u5d07\u7115\u65e0\u7f6a\u2460\u3002 \u3000\u3000\u5175\u90e8\u804c\u65b9\u53f8\u4e3b\u7ba1\u519b\u4ee4\u3001\u519b\u653f\uff0c\u5bf9\u519b\u52a1\u5185\u60c5\u77e5\u9053\u5f97\u6700\u6e05\u695a\u3002\u804c\u65b9\u53f8\u90ce\u4e2d\uff08\u53f8\u957f\uff09\u4f59\u5927\u6210\u6781\u529b\u4e3a\u8881\u5d07\u7115\u8fa9\u767d\uff0c\u4e0e\u5175\u90e8\u5c1a\u4e66\u6881\u5ef7\u680b\u51e0\u4e4e\u65e5\u65e5\u4e3a\u6b64\u4e8b\u4e89\u6267\u3002\u5f53\u65f6\u671d\u5ef7\u52a0\u5728\u8881\u5d07\u7115\u5934\u4e0a\u7684\u7f6a\u540d\u6709\u4e24\u6761\uff0c\u4e00\u662f\u201c\u53db\u9006\u201d\uff0c\u4e8c\u662f\u201c\u64c5\u4e3b\u548c\u8bae\u201d\u3002\u6240\u8c13\u53db\u9006\uff0c\u60df\u4e00\u7684\u8bc1\u636e\u662f\u64c5\u6740\u6bdb\u6587\u9f99\uff0c\u53bb\u654c\u6240\u5fcc\u3002\u8881\u5d07\u7115\u64c5\u6740\u6bdb\u6587\u9f99\uff0c\u624b\u7eed\u4e0a\u56fa\u6709\u9519\u8bef\uff0c\u53ef\u662f\u6bdb\u6b7b\u4e4b\u540e\uff0c\u5d07\u796f\u660e\u4ee4\u516c\u5e03\u6bdb\u6587\u9f99\u7684\u7f6a\u72b6\uff0c\u53c8\u516c\u5f00\u5609\u5956\u8881\u5d07\u7115\u6740\u5f97\u5bf9\uff0c\u5c31\u7b97\u5f53\u771f\u6740\u9519\uff0c\u8d23\u4efb\u4e5f\u662f\u5728\u7687\u5e1d\u4e86\uff0c\u5df2\u4e0d\u80fd\u4f5c\u4e3a\u8881\u5d07\u7115\u7684\u7f6a\u540d\u2461\u3002 \u3000\u3000\u5609\u9756\u5e74\u95f4\uff0c\u66fe\u6709\u8fc7\u4e00\u4e2a\u7c7b\u4f3c\u7684\u6709\u540d\u4f8b\u5b50\uff1a\u5728\u5f90\u9636\u7684\u4e3b\u6301\u4e0b\uff0c\u7ec8\u4e8e\u6273\u5012\u4e86\u5927\u5978\u81e3\u4e25\u5d69\u3001\u4e25\u4e16\u8543\u7236\u5b50\u3002\u4e25\u4e16\u8543\u5341\u5206\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u5728\u72f1\u4e2d\u8bbe\u6cd5\u653e\u51fa\u7a7a\u6c14\uff0c\u8bf4\u522b\u7684\u4e8b\u60c5\u6211\u90fd\u4e0d\u6015\uff0c\u4f46\u5982\u8bf4\u6211\u5bb3\u6b7b\u6c88\u70bc\u3001\u6768\u7ee7\u76db\uff0c\u6211\u7236\u5b50\u5c31\u96be\u9003\u4e00\u6b7b\u3002\u4e09\u6cd5\u53f8\u542c\u5230\u4e86\uff0c\u679c\u7136\u4e2d\u8ba1\uff0c\u4fbf\u4ee5\u6b64\u5b9a\u4e3a\u4ed6\u7684\u4e3b\u8981\u7f6a\u540d\u3002\u5f90\u9636\u770b\u4e86\u5ba1\u6848\u7684\u5b9a\u7a3f\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u9053\u594f\u7ae0\u4e00\u4e0a\u53bb\uff0c\u4e25\u516c\u5b50\u5c31\u65e0\u7f6a\u91ca\u653e\u4e86\u3002\u201d\u4e09\u6cd5\u53f8\u5fd9\u95ee\u539f\u56e0\u3002\u5f90\u9636\u89e3\u91ca\u7406\u7531\uff1a\u6740\u6c88\u6768\u4e8c\u4eba\uff0c\u662f\u5609\u9756\u7687\u5e1d\u4e0b\u7684\u7279\u65e8\uff0c\u4f60\u4eec\u8bf4\u6c88\u6768\u4e8c\u4eba\u6740\u9519\u4e86\uff0c\u90a3\u5c31\u662f\u6307\u8d23\u7687\u4e0a\u7684\u4e0d\u662f\u3002\u7687\u4e0a\u600e\u80af\u8ba4\u9519\uff1f\u7ed3\u679c\u5f53\u7136\u91ca\u653e\u4e25\u4e16\u8543\uff0c\u4ee5\u8bc1\u660e\u7687\u5e1d\u6c38\u8fdc\u6b63\u786e\u3002\u4e09\u6cd5\u53f8\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u4e8e\u662f\u80e1\u4e71\u52a0\u4e86\u4e00\u4e2a\u201c\u79c1\u901a\u502d\u5bc7\u201d\u7684\u7f6a\u540d\uff0c\u5c31\u6b64\u6740\u4e86\u4e25\u4e16\u8543\u3002 \u3000\u3000\u4f46\u5d07\u796f\u5bf9\u4e8e\u8fd9\u6837\u6027\u8d28\u76f8\u540c\u7684\u7b80\u5355\u63a8\u8bba\uff0c\u7adf\u662f\u5b8c\u5168\u4e0d\u987e\u3002 \u3000\u3000\u81f3\u4e8e\u201c\u64c5\u4e3b\u548c\u8bae\u201d\uff0c\u4e5f\u4e0d\u8fc7\u662f\u8fdb\u884c\u548c\u5e73\u8bd5\u63a2\u800c\u5df2\uff0c\u5e76\u975e\u201c\u64c5\u7f14\u548c\u7ea6\u201d\u3002\u8881\u5d07\u7115\u63d0\u51fa\u7f14\u548c\u5efa\u8bae\u800c\u7ed9\u671d\u5ef7\u5426\u51b3\uff0c\u5d07\u796f\u5982\u679c\u8ba4\u4e3a\u4ed6\u201c\u64c5\u4e3b\u548c\u8bae\u201d\u662f\u8fc7\u5931\uff0c\u5f53\u65f6\u5c31\u5e94\u52a0\u4ee5\u60e9\u5904\uff0c\u4f46\u53cd\u800c\u52a0\u4ed6\u592a\u5b50\u592a\u4fdd\u7684\u5b98\u8854\uff0c\u81ea\u4e8c\u54c1\u5b98\u5347\u4e3a\u4ece\u4e00\u54c1\uff0c\u53c8\u8d50\u7ed9\u4ed6\u87d2\u888d\u3001\u7389\u5e26\u548c\u94f6\u5e01\u3002\u53c8\u5347\u53c8\u8d4f\uff0c\u201c\u64c5\u4e3b\u548c\u8bae\u201d\u8fd9\u4ef6\u4e8b\u5f53\u7136\u5c31\u4e0d\u7b97\u7f6a\u884c\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5173\u5916\u7684\u5c06\u540f\u58eb\u6c11\u4e0d\u65ad\u5230\u603b\u7763\u5b59\u627f\u5b97\u7684\u8859\u95e8\u53bb\u53f7\u54ed\uff0c\u4e3a\u8881\u5d07\u7115\u547c\u51a4\uff0c\u613f\u4ee5\u8eab\u4ee3\u3002\u5b59\u627f\u5b97\u6df1\u4fe1\u8881\u5d07\u7115\u662f\u65e0\u7f6a\u7684\uff0c\u6781\u529b\u5b89\u629a\u7956\u5927\u5bff\uff0c\u529d\u4ed6\u7acb\u529f\uff0c\u540c\u65f6\u4e0a\u4e66\u5d07\u796f\uff0c\u76fc\u671b\u4ee5\u7956\u5927\u5bff\u4e4b\u529f\u6765\u8d4e\u8881\u5d07\u7115\u4e4b\u201c\u8fc7\u201d\u3002\u5d07\u796f\u4e0d\u4e88\u7406\u776c\u3002 \u3000\u3000\u6709\u4e00\u4e2a\u6ca1\u6709\u4efb\u4f55\u529f\u540d\u804c\u4f4d\u7684\u5e03\u8863\u7a0b\u672c\u76f4\uff0c\u5728\u8fd9\u65f6\u5019\u663e\u793a\u4e86\u7f55\u6709\u7684\u4fa0\u4e49\u7cbe\u795e\u3002\u8fd9\u6837\u7684\u4e8b\uff0c\u7eb5\u7136\u5728\u8f7b\u751f\u91cd\u4e49\u7684\u6218\u56fd\u65f6\u4ee3\uff0c\u4e5f\u8db3\u4ee5\u8f70\u4f20\u5929\u4e0b\u3002 \u3000\u3000\u7a0b\u672c\u76f4\u4e0e\u8881\u5d07\u7115\u7d20\u65e0\u6e0a\u6e90\uff0c\u66fe\u4e09\u6b21\u6c42\u89c1\u90fd\u89c1\u4e0d\u7740\uff0c\u5230\u540e\u6765\u7ec8\u4e8e\u89c1\u5230\u4e86\uff0c\u4ed6\u5bf9\u8881\u94a6\u4f69\u5df2\u6781\uff0c\u4fbf\u6295\u5728\u8881\u90e8\u4e0b\u529e\u4e8b\uff0c\u62dc\u8881\u4e3a\u8001\u5e08\u3002\u8881\u88ab\u6355\u540e\uff0c\u7a0b\u672c\u76f4\u4e0a\u4e66\u7687\u5e1d\uff0c\u5217\u4e3e\u79cd\u79cd\u4e8b\u5b9e\uff0c\u4e3a\u8881\u5d07\u7115\u8fa9\u767d\uff0c\u8bf7\u6c42\u91ca\u653e\uff0c\u8ba9\u4ed6\u5e26\u5175\u536b\u56fd\u3002\u8fd9\u9053\u767d\u51a4\u758f\u5199\u5f97\u6028\u6c14\u51b2\u5929\uff0c\u6700\u540e\u7533\u8bf7\u4e3a\u8881\u5d07\u7115\u800c\u6b7b\u2462\u3002\u5d07\u796f\u5927\u6012\uff0c\u5c06\u4ed6\u4e0b\u72f1\uff0c\u540e\u6765\u7ec8\u4e8e\u5c06\u4ed6\u6740\u4e86\uff0c\u5b8c\u6210\u4ed6\u7684\u5fd7\u613f\u3002 \u3000\u3000\u5927\u5b66\u58eb\u97e9\u3000\uff08\u6253\u4e0d\u51fa\u6765\uff0c\u5de6\u53f3\u7ed3\u6784\uff0c\u5de6\u8fb9\u201c\u706b\u201d\uff0c\u53f3\u8fb9\u201c\u5e7f\u201d\uff09\u662f\u8881\u5d07\u7115\u8003\u4e2d\u8fdb\u58eb\u7684\u4e3b\u8003\u5b98\uff0c\u662f\u8881\u540d\u4e49\u4e0a\u7684\u8001\u5e08\uff0c\u56e0\u6b64\u800c\u88ab\u8feb\u8f9e\u804c\u3002\u5fa1\u53f2\u7f57\u4e07\u7235\u7533\u8fa9\u8881\u5d07\u7115\u5e76\u975e\u53db\u9006\uff0c\u56e0\u800c\u524a\u804c\u4e0b\u72f1\u3002\u5fa1\u53f2\u6bdb\u7fbd\u5065\u66fe\u548c\u8881\u5d07\u7115\u8be6\u7ec6\u8ba8\u8bba\u8fc7\u4e94\u5e74\u5e73\u8fbd\u7684\u53ef\u80fd\u6027\uff0c\u56e0\u6b64\u800c\u7f62\u5b98\u5145\u519b\u3002 \u3000\u3000\u5f53\u65f6\u671d\u81e3\u4e4b\u4e2d\uff0c\u5927\u7ea6\u4e03\u6210\u540c\u60c5\u8881\u5d07\u7115\uff0c\u5176\u4f59\u4e09\u6210\u5219\u9644\u548c\u7687\u5e1d\u7684\u610f\u601d\uff0c\u5176\u4e2d\u4e3b\u5f20\u6740\u8881\u5d07\u7115\u6700\u529b\u7684\u662f\u9996\u8f85\u6e29\u4f53\u4ec1\u548c\u5175\u90e8\u5c1a\u4e66\u6881\u5ef7\u680b\u3002 \u3000\u3000\u6e29\u4f53\u4ec1\u662f\u6d59\u6c5f\u4e4c\u7a0b\uff08\u5434\u5174\uff09\u4eba\uff0c\u5728\u300a\u660e\u53f2\u300b\u4e2d\u5217\u4e8e\u300a\u5978\u81e3\u4f20\u300b\u3002\u4ed6\u548c\u6bdb\u6587\u9f99\u662f\u5927\u540c\u4e61\uff0c\u4e00\u5fc3\u8981\u4e3a\u6bdb\u62a5\u4ec7\u3002\u6881\u5ef7\u680b\u548c\u8881\u5d07\u7115\u662f\u540c\u5e74\uff0c\u540c\u662f\u4e07\u5386\u56db\u5341\u4e03\u5e74\u7684\u8fdb\u58eb\uff0c\u53c8\u66fe\u5728\u8fbd\u4e1c\u5171\u4e8b\u3002\u5f53\u65f6\u8881\u5d07\u7115\u662f\u4ed6\u4e0a\u53f8\uff0c\u5f97\u7f6a\u8fc7\u4ed6\u3002\u4ed6\u5fc3\u4e2d\u8bb0\u6068\uff0c\u65e2\u60f3\u62a5\u4ec7\uff0c\u53c8\u8981\u8ba8\u597d\u7687\u5e1d\u3002 \u3000\u3000\u5d07\u796f\u8eab\u8fb9\u638c\u6743\u7684\u592a\u76d1\uff0c\u5927\u90fd\u5728\u5317\u4eac\u57ce\u90ca\u6709\u5e84\u56ed\u5e97\u94fa\u79c1\u4ea7\uff0c\u6e05\u5175\u653b\u5230\uff0c\u711a\u70e7\u52ab\u63a0\uff0c\u4f17\u592a\u76d1\u635f\u5931\u5f88\u5927\uff0c\u5927\u5bb6\u90fd\u8bf4\u8881\u5d07\u7115\u5f15\u654c\u5175\u8fdb\u6765\u3002\u6bdb\u6587\u9f99\u5728\u76ae\u5c9b\u5f53\u4e1c\u6c5f\u9547\u603b\u5175\u4e4b\u65f6\uff0c\u6bcf\u5e74\u9977\u91d1\u6570\u5341\u4e07\uff0c\u5176\u4e2d\u4e00\u5927\u90e8\u5206\u6839\u672c\u4e0d\u8fd0\u51fa\u5317\u4eac\uff0c\u4fbf\u5728\u4eac\u57ce\u4e2d\u5206\u7ed9\u4e86\u7687\u5e1d\u8eab\u8fb9\u7684\u7528\u4e8b\u592a\u76d1\u3002\u6bdb\u6587\u9f99\u4e00\u6b7b\uff0c\u4f17\u592a\u76d1\u8fd9\u4e9b\u5927\u6536\u5165\u90fd\u65ad\u7edd\u4e86\u3002 \u3000\u3000\u6b64\u5916\u8fd8\u6709\u51e0\u540d\u5fa1\u53f2\u9ad8\u6377\u3001\u8881\u5f18\u52cb\u3001\u53f2\u8303\u571f\u7b49\u4eba\uff0c\u4e5f\u4e3b\u5f20\u6740\u8881\u5d07\u7115\uff0c\u4ed6\u4eec\u5374\u53e6\u6709\u79c1\u5fc3\u3002\u5f53\u8881\u5d07\u7115\u4e0b\u72f1\u4e4b\u65f6\uff0c\u9996\u8f85\u662f\u94b1\u9f99\u9521\uff0c\u4ed6\u867d\u66fe\u6279\u8bc4\u8881\u5d07\u7115\u76f8\u8c8c\u4e0d\u4f73\uff0c\u4f46\u4e00\u5411\u5bf9\u8881\u5f88\u652f\u6301\u3002\u9ad8\u6377\u7b49\u4eba\u5728\u5929\u542f\u671d\u9644\u548c\u9b4f\u5fe0\u8d24\u3002\u60e9\u529e\u9b4f\u5fe0\u8d24\u4e00\u4f19\u5978\u515a\u7684\u6848\u5b50\u53eb\u505a\u201c\u9006\u6848\u201d\uff0c\u9ad8\u6377\u3001\u53f2\uff08\u6ce8\uff1a\u4e0a\u4e0b\u7ed3\u6784\uff0c\u4e0a\u9762\u4e00\u4e2a\u201c\u8303\u201d\uff0c\u4e0b\u9762\u4e00\u4e2a\u201c\u571f\u201d\uff09\u7b49\u6848\u4e2d\u6709\u540d\uff0c\u53ea\u4e0d\u8fc7\u7f6a\u540d\u4e0d\u91cd\uff0c\u8fd8\u662f\u6709\u5b98\u505a\u3002\u94b1\u9f99\u9521\u662f\u529e\u7406\u201c\u9006\u6848\u201d\u7684\u4e3b\u8981\u4eba\u7269\u4e4b\u4e00\u3002\u9ad8\u6377\u4e00\u4f19\u60f3\u628a\u8881\u5d07\u7115\u8fd9\u6848\u5b50\u641e\u6210\u4e00\u4e2a\u201c\u65b0\u9006\u6848\u201d\uff0c\u628a\u94b1\u9f99\u9521\u6500\u8fdb\u5728\u5185\u3002\u56e0\u4e3a\u8881\u5d07\u7115\u66fe\u4e0e\u94b1\u9f99\u9521\u5546\u91cf\u8fc7\u6740\u6bdb\u6587\u9f99\u7684\u4e8b\uff0c\u94b1\u5e76\u4e0d\u53cd\u5bf9\uff0c\u53ea\u529d\u4ed6\u614e\u91cd\u5904\u7406\u3002\u201c\u65b0\u9006\u6848\u201d\u4e00\u6210\uff0c\u628a\u8bb8\u591a\u5927\u5b98\u8bec\u6500\u5728\u5185\uff0c\u8001\u9006\u6848\u7684\u81ed\u6c14\u5c31\u53ef\u51b2\u6de1\u4e86\u3002\u7ed3\u679c\u65b0\u9006\u6848\u6ca1\u6709\u641e\u6210\uff0c\u4f46\u94b1\u9f99\u9521\u4e5f\u4e22\u5b98\u4e0b\u72f1\uff0c\u5b9a\u4e86\u6b7b\u7f6a\uff0c\u540e\u6765\u51cf\u4e3a\u5145\u519b\u3002 \u3000\u3000\u6ee1\u6842\u90e8\u961f\u6700\u521d\u8d25\u9000\u5230\u5317\u4eac\u65f6\uff0c\u519b\u7eaa\u4e0d\u4f73\uff0c\u5728\u57ce\u5916\u6270\u6c11\uff0c\u5317\u4eac\u767e\u59d3\u4e0d\u5206\u9752\u7ea2\u7682\u767d\uff0c\u628a\u7f6a\u540d\u90fd\u52a0\u5728\u8881\u5d07\u7115\u5934\u4e0a\u3002 \u3000\u3000\u4e2a\u4eba\u7684\u79c1\u6028\u3001\u5992\u5fcc\u3001\u515a\u6d3e\u51b2\u7a81\u3001\u8c23\u8a00\uff0c\u4ea4\u7ec7\u6210\u4e86\u4e00\u5f20\u8bec\u9677\u7684\u7f57\u7f51\uff0c\u800c\u6700\u4ee4\u4eba\u611f\u5230\u75db\u5fc3\u7684\uff0c\u662f\u8881\u5d07\u7115\u4eb2\u4fe1\u8c22\u5c1a\u653f\u7684\u53db\u5356\u3002\u8c22\u5c1a\u653f\u662f\u4e1c\u839e\u4eba\uff0c\u6b66\u4e3e\uff0c\u8881\u5d07\u7115\u7b2c\u4e00\u6b21\u5230\u5c71\u6d77\u5173\u3001\u7b2c\u4e00\u6b21\u4e0a\u594f\u7ae0\u5c31\u4fdd\u8350\u4ed6\uff0c\u8bf4\u662f\u81ea\u5df1\u5e73\u751f\u6240\u7ed3\u7684\u201c\u6b7b\u58eb\u201d\uff0c\u53ef\u89c1\u662f\u8881\u5d07\u7115\u5e74\u8f7b\u65f6\u5c31\u7ed3\u4ea4\u7684\u597d\u670b\u53cb\u3002\u4ed6\u5728\u8881\u7684\u63d0\u62d4\u4e0b\u5347\u5230\u53c2\u5c06\u3002\u8881\u6740\u6bdb\u6587\u9f99\uff0c\u5c31\u662f\u8fd9\u4e2a\u8c22\u53c2\u5c06\u5e26\u5175\u628a\u6bdb\u90e8\u58eb\u5352\u9694\u5728\u56f4\u5916\u3002\u5175\u90e8\u5c1a\u4e66\u6881\u5ef7\u680b\u603b\u89c9\u5f97\u8981\u6740\u8881\u6ca1\u6709\u751a\u4e48\u5145\u5206\u7406\u7531\uff0c\u4fbf\u6388\u610f\u8c22\u5c1a\u653f\u8bec\u544a\uff0c\u7b54\u5141\u4ed6\u6784\u6210\u8881\u7684\u7f6a\u540d\u4e4b\u540e\u53ef\u4ee5\u5347\u4ed6\u4e3a\u798f\u5efa\u603b\u5175\u3002\u8c22\u5c1a\u653f\u5229\u6b32\u718f\u5fc3\uff0c\u5c45\u7136\u5c31\u51fa\u5934\u8bec\u544a\u8fd9\u4e2a\u5e73\u751f\u5f85\u4ed6\u6069\u4e49\u6700\u6df1\u7684\u4e3b\u5e05\u3002 \u3000\u3000\u4ee5\u8881\u5d07\u7115\u77e5\u4eba\u4e4b\u660e\uff0c\u6bd5\u7adf\u8fd8\u662f\u770b\u9519\u4e86\u8c22\u5c1a\u653f\u3002\u8981\u4e86\u89e3\u4e00\u4e2a\u4eba\uff0c\u90a3\u662f\u591a\u4e48\u7684\u56f0\u96be\uff01\u8881\u5d07\u7115\u5bf9\u5d07\u796f\u7684\u80e1\u6d82\u4e0e\u5978\u81e3\u7684\u8bec\u9677\uff0c\u6216\u8bb8\u5e76\u4e0d\u75db\u6068\uff0c\u56e0\u4e3a\u5d07\u796f\u4e0e\u4f17\u5978\u81e3\u672c\u6765\u5c31\u662f\u90a3\u6837\u7684\u4eba\uff0c\u4f46\u5bf9\u4e8e\u8c22\u5c1a\u653f\u7684\u5fd8\u6069\u8d1f\u4e49\uff0c\u4e00\u5b9a\u662f\u803f\u803f\u4e8e\u6000\u5427\uff1f\u6216\u8bb8\uff0c\u4ed6\u4e5f\u66fe\u60f3\u5230\u4e86\uff0c\u5c31\u7b97\u662f\u5cb3\u98de\uff0c\u4e5f\u88ab\u90e8\u4e0b\u5927\u5c06\u738b\u8d35\u6240\u8bec\u544a\uff0c\u56e0\u800c\u6784\u6210\u4e86\u98ce\u6ce2\u4ead\u4e4b\u72f1\u3002\u53ea\u662f\u738b\u8d35\u8bec\u544a\uff0c\u662f\u7531\u4e8e\u79e6\u6867\u3001\u5f20\u4fca\u7684\u5a01\u8feb\uff0c\u8c22\u5c1a\u653f\u5374\u662f\u53d7\u4e86\u5229\u8bf1\uff0c\u6bd4\u8f83\u8d77\u6765\uff0c\u8c22\u5c1a\u653f\u53c8\u5351\u9119\u4e9b\u3002\u53ef\u662f\u8c22\u5c1a\u653f\u6789\u4f5c\u5c0f\u4eba\uff0c\u4ed6\u7684\u603b\u5175\u68a6\u5e76\u6ca1\u6709\u505a\u6210\uff0c\u4e0d\u4e45\u6881\u5ef7\u680b\u4ee5\u8d2a\u6c61\u7f6a\u57ae\u53f0\uff0c\u67e5\u5230\u8c22\u5c1a\u653f\u662f\u8d3f\u8d42\u8005\u4e4b\u4e00\uff0c\u8c22\u4e5f\u56e0\u6b64\u9769\u804c\u3002 \u3000\u3000\u8881\u5d07\u7115\u7684\u7f6a\u540d\u7ec8\u4e8e\u786e\u5b9a\u4e86\uff0c\u662f\u80e1\u91cc\u80e1\u6d82\u7684\u6240\u8c13\u201c\u8c0b\u53db\u201d\u3002\u5d07\u796f\u59cb\u7ec8\u6ca1\u6709\u53eb\u6768\u592a\u76d1\u51fa\u6765\u4f5c\u8bc1\u3002\u64c5\u6740\u6bdb\u6587\u9f99\u548c\u64c5\u4e3b\u548c\u8bae\u4e24\u4ef6\u4e8b\u7406\u7531\u592a\u4e0d\u5145\u5206\uff0c\u5d07\u796f\u65e0\u8bba\u5982\u4f55\u96be\u4ee5\u81ea\u5706\u5176\u8bf4\uff0c\u7ec8\u4e8e\u4e5f\u4e0d\u63d0\u4e86\u3002\u672c\u6765\u5b9a\u7684\u5904\u5211\u662f\u201c\u5937\u4e09\u65cf\u201d\uff0c\u8981\u5c06\u8881\u5d07\u7115\u5168\u5bb6\u3001\u6bcd\u4eb2\u7684\u5168\u5bb6\u3001\u59bb\u5b50\u7684\u5168\u5bb6\u90fd\u6ee1\u95e8\u6284\u65a9\u3002\u4f59\u5927\u6210\u53bb\u5a01\u5413\u4e3b\u7406\u8fd9\u4e2a\u6848\u5b50\u7684\u5175\u90e8\u5c1a\u4e66\u6881\u5ef7\u680b\uff1a\u201c\u8881\u5d07\u7115\u5e76\u975e\u771f\u7684\u6709\u7f6a\uff0c\u53ea\u4e0d\u8fc7\u6e05\u5175\u56f4\u57ce\uff0c\u7687\u4e0a\u9707\u6012\u3002\u6211\u5728\u5175\u90e8\u505a\u90ce\u4e2d\uff0c\u5df2\u6362\u4e86\u516d\u4f4d\u5c1a\u4e66\uff0c\u4eb2\u773c\u89c1\u5230\u6ca1\u4e00\u4e2a\u5c1a\u4e66\u6709\u597d\u4e0b\u573a\u3002\u4f60\u505a\u5175\u90e8\u5c1a\u4e66\uff0c\u600e\u80fd\u4fdd\u5f97\u5b9a\u4eca\u540e\u6e05\u5175\u4e0d\u518d\u6765\u72af\uff1f\u4eca\u65e5\u8bdb\u706d\u8881\u5d07\u7115\u4e09\u65cf\uff0c\u9020\u6210\u4e86\u5148\u4f8b\uff0c\u6e05\u5175\u82e5\u662f\u518d\u6765\uff0c\u6881\u5c1a\u4e66\uff0c\u4f60\u987e\u4e00\u4e0b\u81ea\u5df1\u7684\u4e09\u65cf\u7f62\u3002\u201d \u3000\u3000\u6881\u5ef7\u680b\u7ed9\u8fd9\u756a\u8bdd\u5413\u6015\u4e86\uff0c\u4e8e\u662f\u548c\u6e29\u4f53\u4ec1\u5546\u8bae\u8bbe\u6cd5\u51cf\u8f7b\u5904\u5211\uff0c\u6539\u4e3a\u8881\u5d07\u7115\u51cc\u8fdf\uff0c\u4e03\u5341\u51e0\u5c81\u7684\u6bcd\u4eb2\u3001\u5f1f\u5f1f\u3001\u59bb\u5b50\uff0c\u51e0\u5c81\u7684\u5c0f\u5973\u513f\u5145\u519b\u4e09\u5343\u91cc\u3002\u6bcd\u5bb6\u3001\u59bb\u5bb6\u7684\u4eba\u5c31\u4e0d\u7275\u7d2f\u4e86\u2463\u3002 \u3000\u3000\u201c\u51cc\u8fdf\u201d\u89c4\u5b9a\u8981\u5272\u4e00\u5343\u5200\uff0c\u8981\u5230\u7b2c\u4e00\u5343\u5200\u4e0a\u624d\u80fd\u5c06\u4eba\u6740\u6b7b\uff0c\u5426\u5219\u523d\u5b50\u624b\u6709\u7f6a\uff0c\u90a3\u5c31\u662f\u6240\u8c13\u201c\u5343\u5200\u4e07\u5250\u201d\u3002\u6240\u4ee5\u9a82\u4eba\u201c\u6740\u5343\u5200\u201d\u662f\u6700\u6076\u6bd2\u7684\u5492\u9a82\u3002 \u3000\u3000\u8881\u5d07\u7115\u88ab\u7ed1\u4e0a\u5211\u573a\uff0c\u523d\u5b50\u624b\u8fd8\u6ca1\u6709\u52a8\u624b\uff0c\u5317\u4eac\u7684\u4f17\u767e\u59d3\u5c31\u6251\u4e0a\u53bb\u62a2\u7740\u54ac\u4ed6\u7684\u8089\uff0c\u76f4\u54ac\u5230\u4e86\u5185\u810f\u3002\u523d\u5b50\u624b\u4f9d\u7167\u89c4\u5b9a\uff0c\u4e00\u5200\u5200\u7684\u5c06\u4ed6\u8eab\u4e0a\u808c\u8089\u5272\u4e0b\u6765\u3002\u4f17\u767e\u59d3\u56f4\u5728\u65c1\u8fb9\uff0c\u7eb7\u7eb7\u53eb\u9a82\uff0c\u51fa\u94b1\u4e70\u4ed6\u7684\u8089\uff0c\u4e00\u94b1\u94f6\u5b50\u53ea\u80fd\u4e70\u5230\u4e00\u7247\uff0c\u4e70\u5230\u540e\u54ac\u4e00\u53e3\uff0c\u9a82\u4e00\u58f0\uff1a\u201c\u6c49\u5978\uff01\u201d\u2464\u56e0\u4e3a\u5317\u4eac\u57ce\u7684\u767e\u59d3\u8ba4\u5b9a\uff0c\u53bb\u5e74\u6e05\u5175\u56f4\u57ce\u662f\u4ed6\u6545\u610f\u5f15\u6765\u7684\u3002\u5f88\u96be\u8bf4\u8fd9\u6837\u7684\u8c23\u8a00\u4ece\u4f55\u800c\u6765\uff0c\u662f\u75db\u6068\u8881\u5d07\u7115\u7684\u5927\u81e3\u4e0e\u592a\u76d1\u4eec\u6563\u64ad\u51fa\u53bb\u7684\uff1f\u8fd8\u662f\u4e00\u822c\u7fa4\u4f17\u5929\u751f\u7684\u559c\u6b22\u542c\u4fe1\u8c23\u8a00\uff1f\u53c8\u6216\u8bb8\uff0c\u53d7\u5230\u4e86\u91cd\u5927\u60ca\u6050\u548c\u635f\u5931\u7684\u5317\u4eac\u767e\u59d3\u9700\u8981\u4e00\u4e2a\u53d1\u6cc4\u7684\u5bf9\u8c61\uff1f \u3000\u3000\u4ece\u957f\u8fdc\u6765\u8bf4\uff0c\u4eba\u6c11\u7684\u773c\u775b\u786e\u662f\u96ea\u4eae\u7684\uff0c\u7136\u800c\u5f53\u4ed6\u4eec\u53d7\u5230\u6b3a\u8499\u4e4b\u65f6\uff0c\u76f2\u76ee\u800c\u51b2\u52a8\u7684\u7fa4\u4f17\uff0c\u53ef\u4ee5\u548c\u66b4\u541b\u4e00\u6837\u7684\u80e1\u6d82\uff0c\u4e00\u6837\u7684\u6b8b\u9177\u3002\u4f46\u9694\u5f97\u8fdc\u4e86\u4e00\u4e9b\uff0c\u81ea\u5df1\u7684\u751f\u547d\u8d22\u4ea7\u5e76\u4e0d\u53d7\u5230\u76f4\u63a5\u7684\u5f71\u54cd\u65f6\uff0c\u4eba\u4eec\u5c31\u53ef\u4ee5\u51b7\u9759\u5730\u601d\u8003\u4e86\uff0c\u6240\u4ee5\u9664\u4e86\u5317\u4eac\u57ce\u91cc\u4e00\u6279\u53d7\u4e86\u6b3a\u9a97\u7684\u767e\u59d3\uff0c\u5929\u4e0b\u90fd\u77e5\u9053\u8881\u5d07\u7115\u662f\u51a4\u6789\u7684\uff0c\u8fde\u671d\u9c9c\u7684\u541b\u81e3\u767e\u59d3\u4e5f\u77e5\u9053\u4ed6\u7684\u51a4\u6789\uff0c\u4e3a\u4ed6\u7684\u88ab\u5bb3\u611f\u5230\u4e0d\u5e73\u2465\u3002 \u3000\u3000\u8881\u5d07\u7115\u6b7b\u540e\uff0c\u9ab8\u9aa8\u5f03\u5728\u5730\u4e0b\uff0c\u65e0\u4eba\u6562\u53bb\u6536\u846c\u3002\u4ed6\u6709\u4e00\u4e2a\u59d3\u4f59\u7684\u4ec6\u4eba\uff0c\u987a\u5fb7\u9a6c\u6c5f\u4eba\uff0c\u534a\u591c\u91cc\u53bb\u5077\u4e86\u9ab8\u9aa8\uff0c\u6536\u846c\u5728\u5e7f\u6e20\u95e8\u5185\u7684\u5e7f\u4e1c\u4e49\u56ed\u3002\u9694\u4e00\u9053\u57ce\u5899\uff0c\u5e7f\u6e20\u95e8\u5916\u7684\u4e00\u7247\u5e7f\u573a\u4e4b\u4e0a\u3001\u57ce\u58d5\u4e4b\u4e2d\uff0c\u4fbf\u662f\u516b\u4e2a\u534a\u6708\u4e4b\u524d\u8881\u5d07\u7115\u7387\u9886\u5c06\u58eb\u5927\u547c\u9163\u6218\u7684\u5730\u65b9\u3002\u4ed6\u62da\u4e86\u6027\u547d\u51fb\u9000\u6765\u72af\u7684\u5341\u500d\u654c\u519b\uff0c\u4fdd\u536b\u4e86\u7687\u5e1d\u548c\u5317\u4eac\u57ce\u4e2d\u767e\u59d3\u7684\u6027\u547d\u3002\u7687\u5e1d\u548c\u5317\u4eac\u57ce\u7684\u767e\u59d3\u5219\u5c06\u4ed6\u5272\u6210\u4e86\u788e\u5757\u3002 \u3000\u3000\u90a3\u59d3\u4f59\u7684\u4e49\u4ec6\u7ec8\u8eab\u5b88\u5893\u4e0d\u53bb\uff0c\u6b7b\u540e\u5c31\u846c\u5728\u8881\u5893\u4e4b\u65c1\u3002\u975e\u5e38\u5947\u602a\u7684\u662f\uff0c\u4f59\u541b\u7684\u5b50\u5b59\u4e16\u4e16\u4ee3\u4ee3\u90fd\u5728\u8881\u5d07\u7115\u5893\u65c1\u770b\u5b88\u3002\u76f4\u5230\u6c11\u56fd\u4e94\u5e74\uff0c\u770b\u5b88\u8881\u5893\u7684\u4ecd\u662f\u4f59\u541b\u7684\u5b50\u5b59\uff0c\u4ed6\u4eec\u8bf4\u662f\u4e3a\u4e86\u9075\u5b88\u7956\u5b97\u7684\u9057\u8bad\u2466\u3002 \u3000\u3000\u7a0b\u672c\u76f4\u3001\u4f59\u4ec6\u7684\u884c\u4e3a\u8868\u73b0\u4e86\u4eba\u6027\u4e2d\u9ad8\u8d35\u7684\u4e00\u9762\u3002\u8c22\u5c1a\u653f\u7684\u884c\u4e3a\u8868\u73b0\u4e86\u4eba\u6027\u4e2d\u5351\u52a3\u7684\u4e00\u9762\u3002\u8881\u5d07\u7115\u7684\u6b7b\u6cd5\uff0c\u5374\u53c8\u663e\u793a\u4e86\u7fa4\u4f17\u5728\u53d7\u5230\u5ba3\u4f20\u7684\u611a\u5f04\u3001\u5931\u5374\u4e86\u7406\u6027\u4e4b\u540e\uff0c\u4f1a\u53d8\u5f97\u5982\u4f55\u72c2\u66b4\u53ef\u6016\u3002\u8881\u5d07\u7115\u662f\u4e00\u56e2\u706b\u4e00\u6837\u7684\u4eba\uff0c\u5728\u4ed6\u5468\u56f4\uff0c\u71c3\u70e7\u7684\u662f\u9ad8\u8d35\u7684\u706b\u7130\u3001\u90aa\u6076\u7684\u706b\u7130\u3001\u72c2\u66b4\u7684\u706b\u7130\u3002\u8fd9\u4e9b\u706b\u7130\u5c31\u50cf\u4ed6\u672c\u4eba\u7075\u9b42\u4e2d\u7684\u706b\u7130\u90a3\u6837\uff0c\u90fd\u662f\u731b\u70c8\u5730\u95ea\u4eae\u7684\u3002 \u3000\u3000\u8881\u5d07\u7115\u6b7b\u540e\uff0c\u65e7\u90e8\u7956\u5927\u5bff\u3001\u4f55\u53ef\u7eb2\u7387\u519b\u9a7b\u5b88\u9526\u5dde\u3001\u5b81\u8fdc\u3001\u5927\u51cc\u6cb3\u8981\u585e\uff0c\u6e05\u519b\u59cb\u7ec8\u4e0d\u80fd\u8d8a\u96f7\u6c60\u4e00\u6b65\u3002\u5d07\u796f\u56db\u5e74\u516b\u6708\uff0c\u7687\u592a\u6781\u4ee5\u503e\u56fd\u4e4b\u5e08\uff0c\u5728\u5927\u51cc\u6cb3\u5c06\u7956\u5927\u5bff\u7d27\u7d27\u5305\u56f4\uff0c\u5341\u6708\u95f4\u7956\u5927\u5bff\u4e0d\u652f\u6295\u964d\u3002\u526f\u5c06\u4f55\u53ef\u7eb2\u4e0d\u964d\uff0c\u88ab\u6740\u3002\u7956\u5927\u5bff\u9a97\u7687\u592a\u6781\u8bf4\u53ef\u4e3a\u6ee1\u6e05\u53bb\u53d6\u9526\u5dde\uff0c\u4f46\u4e00\u5230\u9526\u5dde\uff0c\u7acb\u5373\u5c31\u5b88\u57ce\uff0c\u6b64\u540e\u7687\u592a\u6781\u6d3e\u5927\u5c06\u51e0\u6b21\u8fdb\u653b\u90fd\u6253\u4e0d\u4e0b\u6765\u3002\u7687\u592a\u6781\u4e24\u6b21\u5fa1\u9a7e\u4eb2\u5f81\uff0c\u653b\u9526\u5dde\u3001\u653b\u5b81\u8fdc\uff0c\u90fd\u65e0\u529f\u800c\u9000\u3002\u76f4\u5230\u5d07\u796f\u5341\u56db\u5e74\u4e09\u6708\uff0c\u6e05\u5175\u5927\u519b\u518d\u56f4\u9526\u5dde\uff0c\u6574\u6574\u56f4\u653b\u4e00\u5e74\uff0c\u5230\u7b2c\u4e8c\u5e74\u4e09\u6708\uff0c\u5148\u51fb\u6e83\u4e86\u6d2a\u627f\u7574\u5341\u56db\u4e07\u5927\u519b\uff0c\u7956\u5927\u5bff\u7cae\u5c3d\u63f4\u7edd\uff0c\u53c8\u518d\u6295\u964d\u3002\u7956\u5927\u5bff\u5230\u987a\u6cbb\u5341\u4e09\u5e74\u624d\u6b7b\uff0c\u59cb\u7ec8\u4e0d\u66fe\u4e3a\u6ee1\u6e05\u6253\u8fc7\u4e00\u4ed7\uff0c\u5927\u6982\u662f\u5b66\u4e86\u300a\u4e09\u56fd\u6f14\u4e49\u300b\u4e2d\u201c\u8eab\u5728\u66f9\u8425\u5fc3\u5728\u6c49\u201d\u7684\u5b97\u65e8\uff0c\u6ee1\u6e05\u4e5f\u6ca1\u6709\u5c01\u4ed6\u751a\u4e48\u5b98\u3002\u6bd4\u4e4b\u6ee1\u6842\u3001\u8d75\u7387\u6559\u3001\u4f55\u53ef\u7eb2\u3001\u5b59\u7956\u5bff\u7b49\u4eba\uff0c\u7956\u5927\u5bff\u6709\u6240\u4e0d\u5982\uff0c\u4f46\u6bd4\u4e4b\u5176\u4f59\u7684\u964d\u6e05\u5927\u5c06\u5374\u53c8\u8fdc\u80dc\u4e86\u3002 \u3000\u3000\u5434\u4e09\u6842\u662f\u7956\u5927\u5bff\u7684\u5916\u7525\u3002\u5434\u7684\u7236\u4eb2\u5434\u8944\u66fe\u505a\u5b81\u8fdc\u603b\u5175\uff0c\u548c\u7956\u5927\u5bff\u662f\u5173\u8fbd\u519b\u4e2d\u540c\u888d\uff0c\u90fd\u662f\u8881\u5d07\u7115\u7684\u90e8\u5c5e\u3002\u5f53\u660e\u6e05\u4e4b\u9645\uff0c\u6c49\u4eba\u7684\u7edf\u5175\u5927\u5c06\u5341\u4e4b\u4e03\u516b\u662f\u5173\u8fbd\u4e00\u7cfb\u7684\u90e8\u961f\u3002\u5434\u4e09\u6842\u3001\u5b54\u6709\u5fb7\u3001\u803f\u4ef2\u660e\u3001\u5c1a\u53ef\u559c\u3001\u5de6\u826f\u7389\u3001\u66f9\u6587\u8bcf\u3001\u66f9\u53d8\u86df\u3001\u9ec4\u5f97\u529f\u3001\u5218\u6cfd\u6e05\u7b49\u90fd\u662f\u3002\u8fd9\u4e9b\u4eba\u6709\u7684\u6295\u964d\u6ee1\u6e05\uff0c\u6709\u7684\u4e3a\u660e\u671d\u6218\u6b7b\uff0c\u90fd\u662f\u6781\u6709\u5c06\u624d\u4e4b\u4eba\uff0c\u9ebe\u4e0b\u90fd\u662f\u608d\u5352\u5065\u58eb\u3002\u8881\u5d07\u7115\u82e5\u662f\u4e0d\u6b7b\u800c\u7edf\u7387\u8fd9\u4e00\u6279\u7cbe\u5175\u731b\u5c06\uff0c\u519b\u4e8b\u5c40\u9762\u5f53\u7136\u5b8c\u5168\u4e0d\u540c\u4e86\u3002\u5434\u4e09\u6842\u5982\u662f\u8881\u5d07\u7115\u7684\u90e8\u5c06\uff0c\u6700\u591a\u4e0d\u8fc7\u662f\u201c\u62b1\u5934\u75db\u54ed\u4e3a\u7ea2\u989c\u201d\u800c\u5df2\uff0c\u6839\u672c\u6ca1\u6709\u673a\u4f1a\u8ba9\u4ed6\u201c\u51b2\u51a0\u4e00\u6012\u201d\uff0c\u4e3a\u4e86\u9648\u5706\u5706\u800c\u5f15\u6e05\u5175\u5165\u5173\u3002 \u3000\u3000\u8881\u5d07\u7115\u65e0\u7f6a\u88ab\u6740\uff0c\u5bf9\u4e8e\u660e\u671d\u6574\u4e2a\u519b\u961f\u58eb\u6c14\u6253\u51fb\u975e\u5e38\u6c89\u91cd\u3002\u4ece\u90a3\u65f6\u5f00\u59cb\uff0c\u660e\u671d\u624d\u6709\u6574\u4e2a\u90e8\u961f\u5411\u6ee1\u6e05\u6295\u964d\u7684\u4e8b\u3002\u66f4\u6709\u4eba\u5e26\u4e86\u897f\u6d0b\u5927\u70ae\u8fc7\u53bb\uff0c\u6ee1\u6e05\u5f00\u59cb\u81ea\u884c\u94f8\u70ae\u3002\u8fbd\u4e1c\u5c06\u58eb\u90fd\u8bf4\uff1a\u201c\u8881\u7763\u5e08\u8fd9\u6837\u5fe0\u52c7\uff0c\u8fd8\u4e0d\u80fd\u514d\uff0c\u6211\u4eec\u5728\u8fd9\u91cc\u53c8\u5e72\u751a\u4e48\uff1f\u201d\u2467\u964d\u6e05\u7684\u5c06\u58eb\u5199\u4fe1\u7ed9\u660e\u5c06\uff0c\u603b\u662f\u6307\u8d23\u660e\u671d\u660f\u541b\u5978\u81e3\u9677\u5bb3\u5fe0\u826f\u2468\u3002 \u3000\u3000\u8881\u5d07\u7115\u4e0d\u662f\u9ad8\u77bb\u767e\u4e16\u7684\u54f2\u4eba\uff0c\u4e0d\u662f\u7cbe\u660e\u80fd\u5e72\u7684\u653f\u6cbb\u5bb6\uff0c\u751a\u81f3\u4ee5\u4e25\u683c\u7684\u519b\u4e8b\u89c2\u70b9\u6765\u770b\uff0c\u4ed6\u4e5f\u4e0d\u662f\u97e9\u4fe1\u3001\u5cb3\u98de\u3001\u5f90\u8fbe\u90a3\u6837\u5584\u4e8e\u7528\u5175\u7684\u5927\u519b\u4e8b\u5bb6\u3002\u4ed6\u884c\u4e8b\u64cd\u5207\uff0c\u6027\u683c\u4e2d\u6709\u91cd\u5927\u7f3a\u70b9\uff0c\u7136\u800c\u4ed6\u51ed\u7740\u6c38\u4e0d\u8870\u7aed\u7684\u70ed\u8bda\uff0c\u4e00\u5f80\u65e0\u524d\u7684\u8c6a\u60c5\uff0c\u6fc0\u52b1\u4e86\u6240\u6709\u7684\u5c06\u58eb\uff0c\u5c06\u4ed6\u7684\u82f1\u96c4\u6c14\u6982\u5e26\u5230\u4e86\u6bcf\u4e00\u4e2a\u90e8\u5c5e\u8eab\u4e0a\u3002\u4ed6\u662f\u4e00\u56e2\u718a\u718a\u70c8\u706b\uff0c\u628a\u90e8\u5c5e\u8eab\u4e0a\u7684\u8840\u90fd\u70e7\u70ed\u4e86\uff0c\u5c06\u4e00\u7fa4\u840e\u9761\u4e0d\u632f\u7684\u6b8b\u5175\u8d25\u5c06\uff0c\u70e7\u70bc\u6210\u4e86\u4e00\u652f\u6b7b\u6218\u4e0d\u5c48\u7684\u7cbe\u9510\u4e4b\u5e08\u3002\u4ed6\u7684\u77e5\u5df1\u7a0b\u672c\u76f4\u79f0\u4ed6\u662f\u201c\u75f4\u5fc3\u4eba\u201d\uff0c\u662f\u201c\u6cfc\u80c6\u6c49\u201d\uff0c\u5168\u56fd\u60df\u4e00\u80af\u62c5\u5f53\u8d23\u4efb\u7684\u597d\u6c49\u247d\u3002\u8881\u5d07\u7115\u5374\u81ea\u79f0\u662f\u5927\u660e\u56fd\u91cc\u7684\u4e00\u4e2a\u4ea1\u547d\u5f92\u247e\u3002\u4ea1\u547d\u5f92\u662f\u6ca1\u6709\u5bb6\u5ead\u5e78\u798f\u7684\uff0c\u65e5\u65e5\u591c\u591c\u4e0d\u5f97\u5e73\u5b89\u3002\u5b98\u5c45\u4e00\u54c1\uff0c\u8fc7\u7684\u5374\u662f\u4ea1\u547d\u5f92\u751f\u6daf\uff0c\u53ea\u56e0\u8fd9\u5341\u5e74\u4e4b\u4e2d\uff0c\u4ed6\u751f\u547d\u4e4b\u706b\u5728\u4e0d\u65ad\u7684\u731b\u70c8\u71c3\u70e7\u3002 \u3000\u3000\u53f8\u9a6c\u8fc1\u5728\u300a\u7559\u4faf\u4e16\u5bb6\u300b\u4e2d\u8bf4\uff0c\u672c\u6765\u4ee5\u4e3a\u5f20\u826f\u7684\u76f8\u8c8c\u4e00\u5b9a\u9b41\u68a7\u5947\u4f1f\uff0c\u4f46\u89c1\u5230\u4ed6\u7684\u56fe\u5f62\uff0c\u5bb9\u8c8c\u5374\u5982\u7f8e\u5973\u4e00\u822c\u3002\u6211\u4eec\u770b\u5230\u8881\u5d07\u7115\u7684\u9057\u50cf\u65f6\uff0c\u6050\u6015\u4e5f\u4f1a\u6709\u8fd9\u6837\u7684\u611f\u89c9\u3002\u56fe\u50cf\u4e2d\u7684\u8881\u5d07\u7115\u867d\u4e0d\u600e\u6837\u4fca\u7f8e\uff0c\u4f46\u6d35\u6d35\u5112\u96c5\uff0c\u5f88\u96be\u60f3\u50cf\u8fd9\u6837\u7684\u4e00\u4e2a\u4eba\u7adf\u4f1a\u5982\u6b64\u521a\u5f3a\u4fa0\u70c8\u3002 \u3000\u3000\u2460\u94b1\u5bb6\u4fee\u300a\u767d\u51a4\u758f\u300b\uff1a\u201c\u55df\u55df\uff01\u9526\u8863\u4f55\u5730\uff1f\u5978\u7ec6\u4f55\u4eba\uff1f\u7adf\u8896\u624b\u800c\u4e03\u4eba\u7adf\u8d70\u8036\uff1f\u6291\u4e03\u4eba\u4ff1\u6709\u7ffc\u800c\u80fd\u4e0a\u98de\u8036\uff1f\u603b\u6b32\u6740\u4e00\u5d07\u7115\uff0c\u6545\u4e0d\u60dc\u4e92\u4e3a\u9677\u9631\u3002\u201d\u5176\u4e2d\u53c8\u8bf4\uff1a\u201c\u65b9\u5929\u542f\u5e74\u95f4\uff0c\u8bf8\u9633\u5931\u536b\uff0c\u5c71\u6d77\u5b64\u5bd2\u3002\u5f53\u6b64\u65f6\u8c01\u80fd\u751f\u6b7b\u5fd8\u5fc3\uff0c\u8eab\u5bb6\u4e0d\u987e\uff1f\u72ec\u5d07\u7115\u4ee5\u516b\u95fd\u5c0f\u540f\uff0c\u62a5\u6548\u800c\u4e1c\uff0c\u5c65\u5386\u98ce\u971c\uff0c\u5907\u5c1d\u9669\u963b\uff0c\u4e0a\u65e0\u7236\u6bcd\uff0c\u4e0b\u4e4f\u59bb\u5b65\uff0c\u591c\u9759\u80e1\u7b33\uff0c\u5f81\u4eba\u6cea\u843d\u3002\u7115\u72ec\u4f55\u5fc3\uff0c\u4ea6\u582a\u6b64\u54c9\uff1f\u6bcb\u4ea6\u541b\u7236\u4e4b\u96be\uff0c\u6709\u4e0d\u5f97\u4e0d\u7136\u8005\u8033\u3002\u201d\u5d07\u796f\u6279\u7b54\uff1a\u201c\u6279\u89c8\u537f\u594f\uff0c\u5177\u89c1\u5fe0\u7231\u3002\u8881\u5d07\u7115\u97ab\u95ee\u660e\u767d\uff0c\u5373\u7740\u524d\u53bb\u8fb9\u585e\u7acb\u529f\uff0c\u53e6\u8bae\u64e2\u7528\u3002\u201d \u3000\u3000\u2461\u8881\u5d07\u7115\u4e0b\u72f1\u540e\uff0c\u6bdb\u6587\u9f99\u7684\u670b\u53cb\u4e58\u673a\u8981\u6c42\u4e3a\u6bdb\u7ffb\u6848\uff0c\u8bf7\u6c42\u8d50\u8ba0\u76d2\u629a\u6064\u3002\u5d07\u796f\u4e0d\u51c6\uff0c\u8bf4\u6bdb\u4e4b\u6b7b\u662f\u201c\u7f6a\u6709\u5e94\u5f97\u201d\uff0c\u4e0d\u51c6\u4ee5\u8881\u5d07\u7115\u4e3a\u501f\u53e3\u800c\u7ffb\u6848\u3002\u89c1\u7a0b\u672c\u76f4\uff1a\u300a\u6f29\u58f0\u300b\u3002 \u3000\u3000\u2462\u7a0b\u672c\u76f4\u300a\u767d\u51a4\u758f\u300b\u4e2d\u8bf4\uff1a\u201c\u603b\u4e4b\uff0c\u5d07\u7115\u6043\u6069\u592a\u8fc7\uff0c\u4efb\u4e8b\u592a\u70e6\uff0c\u800c\u62b1\u5fc3\u592a\u70ed\uff0c\u5e73\u65e5\u4efb\u52b3\u4efb\u6028\uff0c\u65e2\u6240\u4e0d\u8f9e\uff0c\u4eca\u65e5\u6765\u8c24\u6765\u7591\uff0c\u5b9c\u5176\u81ea\u53d6\u3002\u72ec\u5ff5\u5d07\u7115\u5c31\u6267\uff0c\u5c06\u58eb\u60ca\u60f6\uff0c\u5f7b\u591c\u53f7\u557c\uff0c\u83ab\u77e5\u6240\u5904\uff0c\u800c\u57ce\u5934\u70ae\u77f3\uff0c\u4e71\u6253\u591a\u5175\uff0c\u9a82\u8a48\u4e4b\u8a00\uff0c\u9a87\u4eba\u542c\u95fb\uff0c\u9042\u4ee5\u4e07\u4f59\u7cbe\u9510\uff0c\u4e00\u6e83\u800c\u6563\u3002\u201d\u6700\u540e\u8bf4\uff1a\u201c\u81e3\u4e8e\u5d07\u7115\uff0c\u95e8\u751f\u4e5f\u3002\u751f\u5e73\u610f\u6c14\u8c6a\u6770\u76f8\u8bb8\u3002\u5d07\u7115\u51a4\u6b7b\uff0c\u4e49\u4e0d\u72ec\u751f\u3002\u4f0f\u4e5e\u7687\u4e0a\u9a88\u6536\u81e3\u4e8e\u72f1\uff0c\u4ffe\u4e0e\u5d07\u7115\u9a88\u65a9\u4e8e\u5e02\u3002\u5d07\u7115\u4e3a\u5c01\u7586\u793e\u7a37\u81e3\uff0c\u4e0d\u5931\u5fe0\u3002\u81e3\u4e3a\u4e49\u6c14\u7eb2\u5e38\u58eb\uff0c\u4e0d\u5931\u4e49\u3002\u81e3\u4e0e\u5d07\u7115\u867d\u8499\u51a4\u5730\u4e0b\uff0c\u542b\u7b11\u6709\u4f59\u8363\u77e3\u3002\u201d \u3000\u3000\u2463\u671d\u5ef7\u6284\u8881\u5d07\u7115\u7684\u5bb6\uff0c\u5bb6\u91cc\u7a77\u5f97\u5f88\uff0c\u6ca1\u6709\u4e1d\u6beb\u591a\u4f59\u7684\u8d22\u4ea7\u3002\u4ed6\u5728\u8fbd\u897f\u7684\u5bb6\u5c5e\u5145\u519b\u5230\u6d59\u6c5f\uff0c\u540e\u6765\u6539\u5145\u519b\u5230\u8d35\u5dde\uff0c\u5728\u5e7f\u4e1c\u4e1c\u839e\u7684\u5145\u519b\u5230\u798f\u5efa\u3002\u300a\u660e\u53f2\u300b\u8bf4\u8881\u5d07\u7115\u6ca1\u6709\u5b50\u5b59\u3002\u8fd1\u4eba\u53f6\u606d\u7ef0\u5219\u8bf4\uff1a\u201c\u8881\u540e\u88d4\u4e0d\u77e5\u4ee5\u4f55\u7f18\u5165\u9ed1\u9f99\u6c5f\u6c49\u519b\u65d7\u7c4d\u3002\u201d\u5f53\u65f6\u6ee1\u6e05\u63b3\u63a0\u5927\u91cf\u6c49\u4eba\u81f3\u8fbd\u4e1c\u4e3a\u5974\uff0c\u6211\u731c\u60f3\u8881\u5d07\u7115\u7684\u5b50\u5b59\u591a\u534a\u662f\u7ed9\u6ee1\u6e05\u63b3\u63a0\u4e86\u53bb\uff0c\u5230\u9ed1\u9f99\u6c5f\u82e6\u5bd2\u4e4b\u5730\u4f5c\u519c\u5974\uff0c\u56e0\u800c\u7f16\u5165\u6c49\u519b\u65d7\u7c4d\u3002\u8881\u5d07\u7115\u7684\u51a4\u72f1\uff0c\u5230\u6e05\u671d\u4e7e\u9686\u5e74\u95f4\u65b9\u624d\u5f97\u4ee5\u771f\u76f8\u5927\u767d\u3002\u300a\u660e\u53f2\u300b\u5b8c\u6210\u4e8e\u4e7e\u9686\u56db\u5e74\u4e03\u6708\uff0c\u5176\u4e2d\u300a\u8881\u5d07\u7115\u4f20\u300b\u4e2d\uff0c\u6839\u636e\u6e05\u65b9\u7684\u6863\u6848\u7eaa\u5f55\uff0c\u76f4\u8a00\u7687\u592a\u6781\u5982\u4f55\u7528\u53cd\u95f4\u8ba1\u7684\u7ecf\u8fc7\u3002\u4e7e\u9686\u7687\u5e1d\u9694\u4e86\u51e0\u5341\u5e74\uff0c\u624d\u8bfb\u5230\u300a\u660e\u53f2\u300b\u4e2d\u5173\u4e8e\u8881\u5d07\u7115\u7684\u8bb0\u8f7d\uff0c\u5bf9\u8881\u7684\u906d\u9047\u5f88\u662f\u540c\u60c5\uff0c\u4e0b\u65e8\u67e5\u5bdf\u8881\u5d07\u7115\u6709\u65e0\u5b50\u5b59\uff0c\u7ed3\u679c\u67e5\u5230\u53ea\u6709\u65c1\u7cfb\u7684\u8fdc\u623f\u5b50\u5b59\uff0c\u4e7e\u9686\u4fbf\u5c01\u4e86\u4ed6\u4eec\u4e00\u4e9b\u5c0f\u5b98\uff0c\u90a3\u5df2\u662f\u4e7e\u9686\u56db\u5341\u516b\u5e74\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u2464\u89c1\u300a\u660e\u5b63\u5317\u7565\u300b\u3002 \u3000\u3000\u2465\u6e05\u4eba\u6240\u4fee\u7684\u300a\u660e\u53f2\xb7\u8881\u5d07\u7115\u4f20\u300b\u8bf4\uff1a\u201c\u9042\u78d4\u5d07\u7115\u4e8e\u5e02\u2026\u2026\u5929\u4e0b\u51a4\u4e4b\u3002\u201d\u671d\u9c9c\u300a\u4ec1\u7956\u5b9e\u5f55\u300b\u516b\u5e74\u4e8c\u6708\u4e01\u4e11\u8f7d\uff1a\u671d\u9c9c\u7684\u4f7f\u8005\u6734\u5170\u82f1\u5230\u6c88\u9633\uff0c\u6ee1\u6e05\u7684\u738b\u516c\u5f53\u7740\u4ed6\u9762\u4e92\u76f8\u201c\u8033\u8bed\u201d\uff0c\u8bf4\u8881\u7ecf\u7565\u679c\u7136\u548c\u6211\u4eec\u540c\u5fc3\uff0c\u53ea\u53ef\u60dc\u4e8b\u60c5\u8d25\u9732\u800c\u88ab\u902e\u6355\u3002\u8fd9\u6837\u7684\u56fd\u5bb6\u673a\u5bc6\uff0c\u600e\u4f1a\u5f53\u7740\u5916\u56fd\u4f7f\u81e3\u7684\u9762\u800c\u4e92\u76f8\u8033\u8bed\uff0c\u6545\u610f\u8ba9\u4ed6\u542c\u5230\uff1f\u6734\u5170\u82f1\u660e\u767d\u4ed6\u4eec\u7684\u7528\u610f\uff0c\u53ea\u4e0d\u8fc7\u60f3\u501f\u4ed6\u800c\u4f20\u8a00\u5230\u660e\u671d\u53bb\uff0c\u4ee5\u4fbf\u5c3d\u5feb\u6740\u4e86\u8881\u5d07\u7115\uff0c\u6240\u4ee5\u4ed6\u5728\u7ed9\u671d\u9c9c\u56fd\u738b\u7684\u594f\u7ae0\u4e2d\u8bf4\uff1a\u201c\u6b64\u5fc5\u884c\u95f4\u4e4b\u8a00\u4e5f\u3002\u201d\u76f4\u5230\u4e00\u767e\u5e74\u4e4b\u540e\uff0c\u671d\u9c9c\u7684\u541b\u81e3\u4eec\u5728\u8ba8\u8bba\u660e\u671d\u8986\u4ea1\u7684\u539f\u56e0\u65f6\uff0c\u8fd8\u8bf4\u4e3b\u8981\u539f\u56e0\u662f\u6740\u8881\u5d07\u7115\uff08\u89c1\u671d\u9c9c\u300a\u82f1\u5b97\u5b9e\u5f55\u300b\u516d\u5e74\u5341\u4e00\u6708\u8f9b\u672a\uff0c\u5373\u96cd\u6b63\u516b\u5e74\uff0c\u516c\u5143\u4e00\u4e03\u4e09\u25cb\u5e74\uff09\u3002 \u3000\u3000\u2466\u6c11\u56fd\u4e94\u5e74\uff0c\u4e1c\u839e\u4eba\u5f20\u4f2f\u6862\u7684\u513f\u5b50\u6b7b\u4e86\uff0c\u4ed6\u4f69\u670d\u8881\u5d07\u7115\uff0c\u5c06\u513f\u5b50\u846c\u5728\u8881\u5893\u7684\u65c1\u8fb9\u3002\u5f53\u65f6\u770b\u5b88\u8881\u5893\u7684\u4ecd\u662f\u4f58\u6c0f\u5b50\u5b59\uff0c\u53eb\u505a\u4f59\u6dc7\u3002\u5f20\u4f2f\u6862\u4e3a\u8881\u5d07\u7115\u7684\u4e49\u4ec6\u4e5f\u7acb\u4e86\u7891\u3002 \u3000\u3000\u2467\u6768\u58eb\u806a\u300a\u4e94\u5802\u835f\u8bb0\u300b\u5377\u4e8c\uff1a\u201c\u8881\u65e2\u88ab\u6267\uff0c\u8fbd\u4e1c\u5175\u6e83\u6570\u591a\uff0c\u7686\u8a00\uff1a\u2018\u4ee5\u7763\u5e08\u4e4b\u5fe0\uff0c\u5c1a\u4e0d\u80fd\u81ea\u514d\uff0c\u6211\u8f88\u5728\u6b64\u4f55\u4e3a\uff1f\u2019\u2026\u2026\u5c01\u7586\u4e4b\u4e8b\uff0c\u81ea\u6b64\u4e0d\u53ef\u95ee\u77e3\u3002\u201d\u300a\u660e\u53f2\xb7\u8881\u5d07\u7115\u4f20\u300b\uff1a\u201c\u81ea\u5d07\u7115\u6b7b\uff0c\u8fb9\u4e8b\u76ca\u65e0\u4eba\uff0c\u660e\u4ea1\u5f81\u51b3\u77e3\u3002\u201d \u3000\u3000\u2468\u300a\u660e\u6e05\u53f2\u6599\u300b\u4e19\u7f16\uff0c\u8fbd\u5c06\u81ea\u79f0\u201c\u5728\u6b64\u7acb\u529f\u4f55\u7528\u201d\uff0c\u6545\u201c\u5317\u53bb\u80e1\u201d\u800c\u6295\u964d\u6ee1\u6e05\uff0c\u5176\u4e2d\u6709\u4eba\u81f4\u4e66\u65c5\u987a\u660e\u5c06\uff1a\u201c\u5357\u671d\u4e3b\u660f\u81e3\u5978\uff0c\u9677\u5bb3\u5fe0\u826f\u3002\u201d \u3000\u3000\u247d\u7a0b\u672c\u76f4\u300a\u6f29\u58f0\u300b\uff1a\u201c\u6380\u7ffb\u4e24\u76f4\u96b6\uff0c\u8e0f\u904d\u4e00\u5341\u4e09\u7701\uff0c\u6c42\u5176\u6d51\u8eab\u62c5\u8377\u3001\u5f7b\u91cc\u627f\u5f53\u5982\u8881\u516c\u8005\uff0c\u6b63\u6050\u4e0d\u53ef\u518d\u5f97\u4e5f\u3002\u6b64\u6240\u4ee5\u8881\u516c\u503c\u5f97\u7a0b\u672c\u76f4\u4e00\u6b7b\u4e5f\u3002\u201d \u3000\u3000\u247e\u7a0b\u672c\u76f4\u300a\u6f29\u58f0\u300b\u4e2d\u5f15\u8881\u5d07\u7115\u7684\u8bdd\u8bf4\uff1a\u201c\u5b50\u4f55\u4eba\u54c9\uff1f\u5341\u5e74\u4ee5\u6765\uff0c\u7236\u6bcd\u4e0d\u5f97\u4ee5\u4e3a\u5b50\uff0c\u59bb\u5b65\u4e0d\u5f97\u4ee5\u4e3a\u592b\uff0c\u624b\u8db3\u4e0d\u5f97\u4ee5\u4e3a\u5144\u5f1f\uff0c\u4ea4\u6e38\u4e0d\u5f97\u4ee5\u4e3a\u670b\u53cb\uff0c\u4e88\u4f55\u4eba\u54c9\uff1f\u76f4\u8c13\u4e4b\u66f0\uff1a\u2018\u5927\u660e\u56fd\u91cc\u4e00\u4ea1\u547d\u4e4b\u5f92\u4e5f\u2019\u53ef\u4e5f\u3002\u201d \u3000\u3000\u5341\u56db \u3000\u3000\u5d07\u796f\u6240\u4ee5\u6740\u8881\u5d07\u7115\uff0c\u5e76\u4e0d\u53ea\u662f\u4e2d\u4e86\u7687\u592a\u6781\u7684\u53cd\u95f4\u8ba1\u90a3\u4e48\u7b80\u5355\u3002\u5982\u679c\u662f\u51fa\u4e8e\u4e00\u65f6\u8bef\u4fe1\uff0c\u53ef\u8bf4\u4ed6\u53ea\u662f\u611a\u8822\u3002\u300a\u4e09\u56fd\u6f14\u4e49\u300b\u5199\u66f9\u64cd\u8bef\u4e2d\u5468\u745c\u53cd\u95f4\u8ba1\uff0c\u542c\u4fe1\u848b\u5e72\u7684\u5bc6\u62a5\uff0c\u7acb\u523b\u5c31\u6740\u4e86\u6c34\u519b\u90fd\u7763\u8521\u7441\u3001\u5f20\u5141\uff0c\u7b49\u5230\u4e24\u4eba\u7684\u9996\u7ea7\u732e\u5230\u5e10\u4e0b\uff0c\u66f9\u64cd\u767b\u65f6\u5c31\u7701\u609f\u4e86\uff0c\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u6211\u4e2d\u8ba1\u4e86\uff01\u201d\u90a3\u53ea\u662f\u7247\u523b\u4e4b\u95f4\u7684\u4e8b\u3002\u7136\u800c\u5d07\u796f\u4e8e\u5341\u4e8c\u6708\u521d\u4e00\u5c06\u8881\u5d07\u7115\u4e0b\u72f1\uff0c\u5230\u660e\u5e74\u516b\u6708\u5341\u516d\u624d\u5904\u6b7b\uff0c\u4e2d\u95f4\u6709\u516b\u4e2a\u534a\u6708\u65f6\u95f4\u6df1\u601d\u719f\u8651\u3002\u4ed6\u66fe\u51e0\u6b21\u60f3\u653e\u4e86\u8881\u5d07\u7115\uff0c\u8981\u4ed6\u518d\u53bb\u5b88\u8fbd\uff0c\u56e0\u6b64\u6709\u201c\u5b88\u8fbd\u975e\u86ee\u5b50\u4e0d\u53ef\u201d\u7684\u8bdd\uff0c\u4ece\u5bab\u4e2d\u4f20\u5230\u5916\u671d\u6765\u2460\u3002\u65e2\u7136\u6709\u8fd9\u6837\u7684\u8bdd\uff0c\u5f53\u7136\u5df2\u5145\u5206\u660e\u767d\u7687\u592a\u6781\u7684\u53cd\u95f4\u8ba1\u3002\u4ed6\u79f0\u8881\u5d07\u7115\u4e3a\u201c\u86ee\u5b50\u201d\uff0c\u90a3\u662f\u65e2\u8ba8\u538c\u4ed6\u7684\u5014\u5f3a\uff0c\u5374\u53c8\u4e0d\u7981\u4f69\u670d\u4ed6\u7684\u5e72\u52b2\u548c\u624d\u80fd\u3002 \u3000\u3000\u7136\u800c\u4e3a\u751a\u4e48\u7ec8\u4e8e\u6740\u4e86\u4ed6\uff1f\u663e\u7136\uff0c\u5d07\u796f\u4e0d\u80af\u8ba4\u9519\uff0c\u4e0d\u80af\u627f\u8ba4\u5f53\u65f6\u8bef\u4e2d\u53cd\u95f4\u8ba1\u7684\u611a\u8822\u3002\u6740\u8881\u5d07\u7115\uff0c\u5e76\u4e0d\u662f\u5fc3\u4e2d\u771f\u7684\u6000\u7591\u4ed6\u53db\u9006\uff0c\u53ea\u4e0d\u8fc7\u8981\u9690\u7792\u81ea\u5df1\u7684\u611a\u8822\u3002\u4ee5\u6c38\u8fdc\u7684\u5351\u9119\u6765\u63a9\u9970\u4e00\u65f6\u7684\u611a\u8822\uff01 \u3000\u3000\u4e3a\u751a\u4e48\u9694\u4e86\u8fd9\u4e48\u4e45\u624d\u6740\u4ed6\uff1f\u56e0\u4e3a\u6e05\u5175\u4e00\u76f4\u5360\u9886\u7740\u5180\u4e1c\u6c38\u5e73\u7b49\u8981\u5730\uff0c\u5a01\u80c1\u5317\u4eac\uff0c\u76f4\u5230\u516d\u6708\u95f4\u624d\u5168\u90e8\u9000\u51fa\u957f\u57ce\uff0c\u5728\u6b64\u4ee5\u524d\uff0c\u5d07\u796f\u4e0d\u6562\u5f97\u7f6a\u5173\u8fbd\u90e8\u961f\u3002\u8981\u7b49\u5230\u4eac\u5e08\u7684\u5b89\u5168\u7edd\u5bf9\u6ca1\u6709\u4e86\u95ee\u9898\u624d\u52a8\u624b\u3002\u5728\u6b64\u4ee5\u524d\uff0c\u4ed6\u4e0d\u662f\u4e0d\u5fcd\u6740\uff0c\u800c\u662f\u4e0d\u6562\u6740\u3002 \u3000\u3000\u5d07\u796f\u5728\u4f4d\u5341\u4e03\u5e74\uff0c\u6362\u4e86\u4e94\u5341\u4e2a\u5927\u5b66\u58eb\uff08\u76f8\u5f53\u4e8e\u5bb0\u76f8\u6216\u526f\u5bb0\u76f8\uff09\uff0c\u5341\u56db\u4e2a\u5175\u90e8\u5c1a\u4e66\uff08\u90a3\u662f\u6307\u6b63\u5f0f\u7684\u5175\u90e8\u5c1a\u4e66\uff0c\u50cf\u8881\u5d07\u7115\u8fd9\u6837\u52a0\u5175\u90e8\u5c1a\u4e66\u8854\u7684\u4e0d\u7b97\uff09\u3002\u4ed6\u6740\u6b7b\u6216\u903c\u5f97\u81ea\u6740\u7684\u7763\u5e08\u6216\u603b\u7763\uff0c\u9664\u8881\u5d07\u7115\u5916\u8fd8\u6709\u5341\u4eba\uff0c\u6740\u6b7b\u5de1\u629a\u5341\u4e00\u4eba\u3001\u903c\u6b7b\u4e00\u4eba\u3002\u5341\u56db\u4e2a\u5175\u90e8\u5c1a\u4e66\u4e2d\uff0c\u738b\u6d3d\u4e0b\u72f1\u6b7b\uff0c\u5f20\u51e4\u7ffc\u3001\u6881\u5ef7\u680b\u670d\u6bd2\u6b7b\uff0c\u6768\u55e3\u660c\u81ea\u7f22\u6b7b\uff0c\u9648\u65b0\u7532\u65a9\u9996\uff0c\u5085\u5b97\u9f99\u3001\u5f20\u56fd\u7ef4\u9769\u804c\u4e0b\u72f1\uff0c\u738b\u5728\u664b\u3001\u718a\u660e\u9047\u9769\u804c\u67e5\u529e\u3002\u53ef\u89c1\u5904\u6b7b\u5927\u81e3\uff0c\u5728\u4ed6\u539f\u4e0d\u5f53\u662f\u4e00\u4ef6\u5927\u4e8b\u3002\u8fd9\u4e9b\u5175\u90e8\u5c1a\u4e66\u4e2d\uff0c\u6709\u4e9b\u660f\u6126\u80e1\u6d82\uff0c\u6709\u4e9b\u5374\u4e5f\u5fe0\u803f\u5e72\u7ec3\uff0c\u4f8b\u5982\u5085\u5b97\u9f99\uff0c\u53ea\u56e0\u4e3a\u5411\u5d07\u796f\u594f\u7980\u5929\u4e0b\u6c11\u7a77\u8d22\u5c3d\u7684\u60e8\u72b6\uff0c\u5d07\u796f\u5c31\u5927\u4e3a\u751f\u6c14\uff0c\u8d23\u5907\u4ed6\u9053\uff1a\u201c\u4f60\u662f\u5175\u90e8\u5c1a\u4e66\uff0c\u53ea\u987b\u7ba1\u519b\u4e8b\u597d\u4e86\uff0c\u8fd9\u4e9b\u9648\u8154\u6ee5\u8c03\uff0c\u8bf4\u5b83\u5e72\u751a\u4e48\uff1f\u201d\u540e\u6765\u4fbf\u5c06\u4ed6\u5173\u5165\u72f1\u4e2d\uff0c\u5173\u4e86\u4e24\u5e74\u3002 \u3000\u3000\u5d07\u796f\u4f20\u4e0b\u6765\u7684\u7b14\u8ff9\uff0c\u6211\u53ea\u89c1\u5230\u4e00\u4e2a\u7528\u5728\u6555\u4e66\u4e0a\u7684\u82b1\u62bc\uff0c\u4ee5\u53ca\u201c\u4e5d\u601d\u201d\u4e24\u4e2a\u5927\u5b57\u3002\u201c\u4e5d\u601d\u201d\u51fa\u4e8e\u300a\u8bba\u8bed\u300b\u3002\u5b54\u5b50\u8bf4\uff1a\u541b\u5b50\u6709\u4e5d\u79cd\u8003\u8651\uff1a\u770b\u7684\u65f6\u5019\uff0c\u8003\u8651\u770b\u660e\u767d\u4e86\u6ca1\u6709\uff1b\u542c\u7684\u65f6\u5019\uff0c\u8003\u8651\u542c\u6e05\u695a\u4e86\u6ca1\u6709\uff1b\u8003\u8651\u81ea\u5df1\u7684\u8868\u60c5\u6e29\u548c\u4e48\uff1f\u6001\u5ea6\u5e84\u91cd\u4e48\uff1f\u8bf4\u8bdd\u8bda\u6073\u8001\u5b9e\u4e48\uff1f\u5de5\u4f5c\u4e25\u8083\u8ba4\u771f\u4e48\uff1f\u9047\u5230\u7591\u96be\uff0c\u8003\u8651\u600e\u6837\u53bb\u5411\u4eba\u5bb6\u8bf7\u6559\uff1b\u8981\u53d1\u6012\u4e86\uff0c\u8003\u8651\u6709\u6ca1\u6709\u540e\u60a3\uff1b\u5728\u53ef\u4ee5\u5f97\u5230\u5229\u76ca\u7684\u65f6\u5019\uff0c\u8003\u8651\u662f\u4e0d\u662f\u8be5\u5f97\u3002\u8fd9\u5c31\u662f\u6240\u8c13\u201c\u4e5d\u601d\u201d\u2461\u3002\u6b64\u4eba\u5927\u4e66\u201c\u4e5d\u601d\u201d\uff0c\u4f46\u81ea\u5df1\u663e\u7136\u4e00\u601d\u4e5f\u4e0d\u601d\u3002\u5012\u662f\u5728\u6b7b\u540e\uff0c\u5f97\u4e86\u4e2a\u201c\u601d\u5b97\u201d\u7684\u8c25\u6cd5\uff0c\u603b\u7b97\u6709\u4e86\u4e00\u601d\u3002 \u3000\u3000\u6211\u4e5d\u5c81\u90a3\u4e00\u5e74\u7684\u65e7\u5386\u4e94\u6708\u4e8c\u5341\uff0c\u5728\u6545\u4e61\u6d77\u5b81\u770b\u9f99\u738b\u620f\u3002\u770b\u5230\u4e00\u4e2a\u620f\u5b50\u60b2\u6006\u51c4\u51c9\u7684\u6f14\u51fa\uff0c\u4ed6\u62ab\u5934\u6563\u53d1\u7684\u4e0a\u540a\u800c\u6b7b\uff0c\u4e34\u6b7b\u65f6\u628a\u9774\u5b50\u7529\u8131\u4e86\uff0c\u76f4\u7529\u5230\u4e86\u620f\u53f0\u7af9\u68da\u7684\u9876\u4e0a\u3002\u6211\u4ece\u6728\u724c\u5b50\u4e0a\u5199\u7684\u620f\u540d\u4e2d\uff0c\u77e5\u9053\u8fd9\u51fa\u620f\u53eb\u4f5c\u300a\u660e\u672b\u9057\u6068\u300b\u3002\u54e5\u54e5\u5bf9\u6211\u8bf4\uff0c\u4ed6\u662f\u660e\u671d\u7684\u672b\u4ee3\u7687\u5e1d\u5d07\u796f\u3002\u5f53\u65f6\u6211\u53ea\u89c9\u5f97\u8fd9\u7687\u5e1d\u6709\u4e9b\u53ef\u601c\u3002 \u3000\u3000\u4e00\u4e5d\u4e94\u25cb\u5e74\u79cb\u5929\uff0c\u6211\u5728\u5317\u4eac\u4f4f\u4e86\u4e00\u6bb5\u65f6\u5019\uff0c\u66fe\u53bb\u4e86\u5d07\u796f\u540a\u6b7b\u7684\u7164\u5c71\uff0c\u671b\u5230\u7687\u5bab\u91d1\u9ec4\u8272\u7684\u7409\u7483\u74e6\uff0c\u5728\u5317\u4eac\u79cb\u65e5\u7684\u8273\u9633\u4e0b\u6620\u51fa\u7480\u74a8\u5149\u5f69\uff0c\u60f3\u5230\u5d07\u796f\u5728\u540a\u6b7b\u4e4b\u524d\u7684\u4e00\u5239\u90a3\u66fe\u7ad9\u5728\u8fd9\u4e2a\u5730\u65b9\uff0c\u4e00\u5b9a\u4e5f\u5411\u7687\u5bab\u7684\u5c4b\u9876\u51dd\u89c6\u8fc7\u4e86\uff0c\u5c3d\u7ba1\u8fd9\u4eba\u5351\u9119\u72e0\u6bd2\uff0c\u5374\u4e5f\u4e0d\u514d\u5bf9\u4ed6\u6709\u4e00\u4e9b\u60b2\u60af\u4e4b\u60c5\u3002 \u3000\u3000\u4ed6\u5b64\u72ec\u5f97\u5f88\uff0c\u8eab\u8fb9\u6ca1\u6709\u4e00\u4e2a\u4eba\u53ef\u4ee5\u5546\u91cf\uff0c\u56e0\u4e3a\u4ed6\u4efb\u4f55\u4eba\u90fd\u4e0d\u76f8\u4fe1\u3002\u5d07\u796f\u5341\u4e03\u5e74\u4e09\u6708\u5341\u4e03\u65e5\uff0c\u5317\u4eac\u5728\u674e\u81ea\u6210\u731b\u653b\u4e0b\u773c\u89c1\u5b88\u4e0d\u4f4f\u4e86\uff0c\u4ed6\u53ec\u96c6\u6587\u6b66\u767e\u5b98\u5546\u8bae\uff0c\u541b\u81e3\u76f8\u5bf9\u800c\u6ce3\uff0c\u675f\u624b\u65e0\u7b56\u3002\u4ed6\u7528\u624b\u6307\u5728\u6848\u4e0a\u5199\u4e86\u201c\u6587\u81e3\u4e2a\u4e2a\u53ef\u6740\u201d\u516d\u4e2a\u5b57\uff0c\u7ed9\u8eab\u8fb9\u7684\u8fd1\u4f8d\u592a\u76d1\u770b\u4e86\uff0c\u5f53\u5373\u62b9\u53bb\u3002\u4ed6\u5728\u81ea\u6740\u4e4b\u524d\uff0c\u7528\u8840\u5199\u4e86\u4e00\u9053\u8bcf\u4e66\uff0c\u7559\u5728\u5bab\u4e2d\uff0c\u5bf9\u674e\u81ea\u6210\u8bf4\uff0c\u8fd9\u4e00\u5207\u90fd\u662f\u7fa4\u81e3\u8bef\u6211\u7684\uff0c\u4f60\u53ef\u4ee5\u788e\u88c2\u6211\u7684\u5c38\u4f53\uff0c\u53ef\u4ee5\u5c06\u6211\u7684\u6587\u6b66\u767e\u5b98\u5c3d\u6570\u6740\u6b7b\u2462\u3002\u53ef\u89c1\u4ed6\u59cb\u7ec8\u4ee5\u4e3a\u4e00\u5207\u8fc7\u5931\u90fd\u662f\u5728\u6587\u6b66\u767e\u5b98\uff0c\u75db\u6068\u6240\u6709\u4e3a\u4ed6\u529e\u4e8b\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u54e5\u54e5\u5929\u542f\u4ece\u505a\u6728\u5de5\u4e2d\u5f97\u5230\u6781\u5927\u4e50\u8da3\uff0c\u4f9d\u604b\u4e73\u5a18\uff0c\u76f8\u4fe1\u9b4f\u5fe0\u8d24\u4e00\u5207\u90fd\u662f\u5bf9\u7684\uff0c\u7cbe\u795e\u4e0a\u5012\u5f88\u5e73\u5b89\u3002\u5d07\u796f\u5374\u53ea\u662f\u70e6\u8e81\u3001\u5fe7\u8651\u3001\u7591\u60d1\u3001\u8dcb\u5fa8\uff0c\u505a\u5341\u4e03\u5e74\u7687\u5e1d\uff0c\u8fc7\u4e86\u5341\u4e03\u5e74\u75db\u82e6\u7684\u65e5\u5b50\u3002\u62da\u547d\u60f3\u529e\u597d\u56fd\u5bb6\u5927\u4e8b\uff0c\u5374\u5b8c\u5168\u4e0d\u77e5\u9053\u600e\u4e48\u529e\u624d\u662f\u3002 \u3000\u3000\u7687\u5e1d\u662f\u4e0d\u80fd\u8f9e\u804c\u7684\uff01 \u3000\u3000\u4ed6\u6ca1\u6709\u4e00\u4e2a\u771f\u6b63\u4eb2\u4fe1\u7684\u4eba\uff0c\u4ed6\u8fde\u9b4f\u5fe0\u8d24\u90fd\u6ca1\u6709\u3002\u4ed6\u6ca1\u6709\u7cbe\u795e\u4e0a\u7684\u4fe1\u4ef0\uff0c\u4e00\u5ea6\u542c\u4e86\u5f90\u5149\u542f\u7684\u529d\u544a\u800c\u4fe1\u5949\u5929\u4e3b\u6559\uff0c\u4f46\u4ed6\u7684\u7231\u5b50\u60bc\u7075\u738b\u751f\u75c5\uff0c\u5929\u4e3b\u6ca1\u6709\u6551\u6d3b\u5b69\u5b50\u7684\u6027\u547d\uff0c\u4ed6\u4fbf\u5bf9\u5929\u4e3b\u5931\u5374\u4e86\u4fe1\u5fc3\u3002\u4ed6\u6ca1\u6709\u771f\u6b63\u7684\u7231\u597d\u3002\u4ed6\u4e0d\u597d\u8272\uff0c\u8fde\u9648\u5706\u5706\u8fd9\u6837\u7684\u7f8e\u5973\u9001\u8fdb\u5bab\u53bb\uff0c\u4ed6\u90fd\u4e0d\u611f\u5174\u8da3\u800c\u9063\u51fa\u5bab\u6765\u3002 \u3000\u3000\u5728\u4e2d\u56fd\u51e0\u5343\u5e74\u5386\u53f2\u4e2d\uff0c\u541b\u4e3b\u88ab\u654c\u4eba\u4fd8\u864f\u6216\u6740\u6b7b\u7684\u5f88\u591a\uff0c\u5728\u653f\u53d8\u4e2d\u88ab\u6740\u7684\u66f4\u591a\uff0c\u4f46\u4e34\u5371\u81ea\u6740\u7684\u5374\u53ea\u6709\u5d07\u796f\u4e00\u4eba\u3002\u7531\u4e8e\u4ed6\u7684\u81ea\u6740\uff0c\u540e\u4eba\u5bf9\u4ed6\u7684\u8bc4\u4ef7\u4fbf\u6bd4\u4ed6\u5b9e\u9645\u5e94\u5f97\u7684\u597d\u5f97\u591a\u3002\u53ea\u56e0\u4ed6\u4e0d\u597d\u9152\u8272\uff0c\u52e4\u4e8e\u653f\u4e8b\uff0c\u540e\u4eba\u5c31\u4ee5\u4e3a\u4ed6\u672c\u8eab\u662f\u4e2a\u597d\u7687\u5e1d\u3002\u751a\u81f3\u674e\u81ea\u6210\u7684\u6a84\u6587\u4e2d\u4e5f\u8bf4\u4ed6\u5e76\u4e0d\u771f\u7684\u5341\u5206\u80e1\u6d82\uff0c\u53ea\u4e0d\u8fc7\u53d7\u5230\u6b3a\u8499\uff0c\u4e00\u5207\u574f\u4e8b\u90fd\u662f\u7fa4\u81e3\u5e72\u7684\u2463\u3002\u53ea\u56e0\u4ed6\u9057\u8bcf\u4e2d\u8981\u6c42\u674e\u81ea\u6210\u4e0d\u8981\u6740\u6b7b\u4e00\u4e2a\u767e\u59d3\uff0c\u540e\u4eba\u4fbf\u4ee5\u4e3a\u4ed6\u771f\u7684\u7231\u767e\u59d3\uff08\u96be\u9053\u4ed6\u5341\u4e03\u5e74\u4e2d\u6240\u6740\u7684\u767e\u59d3\u8fd8\u5c11\u4e86\uff1f\uff09\uff0c\u53ea\u56e0\u4ed6\u8bf4\u8fc7\u201c\u6715\u975e\u4ea1\u56fd\u4e4b\u541b\uff0c\u8bf8\u81e3\u7686\u4ea1\u56fd\u4e4b\u81e3\u201d\uff0c\u540e\u4eba\u4fbf\u4ee5\u4e3a\u660e\u671d\u6240\u4ee5\u4ea1\uff0c\u8d23\u4efb\u662f\u5728\u7fa4\u81e3\u8eab\u4e0a\u3002\u5176\u5b9e\u4ed6\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u5c31\u8868\u660e\u4ed6\u662f\u5408\u7406\u7684\u4ea1\u56fd\u4e4b\u541b\u3002\u4ed6\u62e5\u6709\u7edd\u5bf9\u7684\u6743\u529b\uff0c\u5374\u5c06\u4e2d\u5174\u4e4b\u81e3\u3001\u6cbb\u56fd\u5e73\u5929\u4e0b\u4e4b\u81e3\u6740\u7684\u6740\u3001\u7f62\u7684\u7f62\uff0c\u5c06\u4e00\u6279\u4ea1\u56fd\u4e4b\u81e3\u8d70\u9a6c\u706f\u822c\u6362\u6765\u6362\u53bb\uff0c\u90a3\u4fbf\u6784\u6210\u4e86\u4ea1\u56fd\u4e4b\u541b\u7684\u6761\u4ef6\u3002 \u3000\u3000\u660e\u671d\u662f\u4e2d\u56fd\u5386\u53f2\u4e0a\u6700\u4e13\u5236\u3001\u6700\u8150\u8d25\u3001\u7edf\u6cbb\u8005\u6700\u6b8b\u66b4\u7684\u671d\u4ee3\uff0c\u5230\u660e\u672b\u66f4\u6210\u4e3a\u4e2d\u56fd\u6570\u5343\u5e74\u4e2d\u6700\u9ed1\u6697\u7684\u65f6\u671f\u4e4b\u4e00\u3002\u660e\u671d\u5f53\u7136\u5e94\u8be5\u4ea1\uff0c\u5bf9\u4e8e\u4e2d\u56fd\u4eba\u6c11\uff0c\u6e05\u671d\u6bd4\u660e\u671d\u597d\u5f97\u591a\u3002 \u3000\u3000\u7136\u800c\u8881\u5d07\u7115\u6297\u62d2\u6ee1\u6e05\u5165\u4fb5\uff0c\u5374\u4e0d\u80fd\u8bf4\u662f\u9519\u4e86\u3002\u5f53\u65f6\u6ee1\u6e05\u5bf9\u660e\u671d\u800c\u8a00\u662f\u5f02\u65cf\uff0c\u662f\u5916\u56fd\uff0c\u6e05\u5175\u5c06\u6c49\u4eba\u6570\u5341\u4e07\u3001\u6570\u5341\u4e07\u7684\u4fd8\u864f\u53bb\uff0c\u90fd\u662f\u4f5c\u4e3a\u5974\u96b6\u6216\u519c\u5974\u3002\u6e05\u5175\u5360\u9886\u4e86\u4e2d\u56fd\u7684\u571f\u5730\u57ce\u5e02\uff0c\u603b\u662f\u70e7\u6740\u52ab\u63a0\u3001\u6781\u6b8b\u9177\u7684\u8650\u5f85\u6c49\u4eba\u3002\u4e0d\u80fd\u7531\u4e8e\u540e\u4ee3\u6ee1\u6e05\u7edf\u6cbb\u80dc\u8fc7\u4e86\u660e\u671d\uff0c\u73b0\u5728\u6ee1\u65cf\u53c8\u6210\u4e3a\u4e2d\u534e\u6c11\u65cf\u4e2d\u4e00\u4e2a\u4e0d\u53ef\u5206\u79bb\u7684\u90e8\u5206\uff0c\u5c31\u62b9\u715e\u4e86\u8881\u5d07\u7115\u5f53\u65f6\u6297\u5fa1\u5916\u65cf\u5165\u4fb5\u7684\u91cd\u5927\u610f\u4e49\u3002\u6b63\u5982\u5c06\u6765\u4e16\u754c\u5927\u540c\u4e4b\u540e\uff0c\u4e5f\u4e0d\u80fd\u5426\u5b9a\u76ee\u524d\u5404\u56fd\u4fdd\u6301\u72ec\u7acb\u548c\u9886\u571f\u4e3b\u6743\u5b8c\u6574\u7684\u4e3b\u5f20\u3002\u6e05\u671d\u6bd4\u660e\u671d\u597d\uff0c\u53ea\u4e0d\u8fc7\u4e2d\u56fd\u4eba\u8fd0\u6c14\u597d\uff0c\u78b0\u5230\u4e86\u51e0\u4e2a\u4e2d\u56fd\u5386\u53f2\u4e0a\u6700\u597d\u7684\u7687\u5e1d\u3002\u7136\u800c\u8881\u5d07\u7115\u5f53\u65f6\u662f\u4e0d\u4f1a\u77e5\u9053\u7684\u3002 \u3000\u3000\u53ea\u8981\u4e13\u5236\u72ec\u88c1\u7684\u5236\u5ea6\u5b58\u5728\u4e00\u5929\uff0c\u5927\u5bb6\u5c31\u53ea\u597d\u78b0\u8fd0\u6c14\u3002\u8881\u5d07\u7115\u548c\u4ebf\u4e07\u4e2d\u56fd\u4eba\u6c11\u8fd0\u6c14\u4e0d\u597d\uff0c\u9047\u4e0a\u4e86\u5d07\u796f\u3002\u5d07\u796f\u8fd0\u6c14\u4e0d\u597d\uff0c\u505a\u4e0a\u4e86\u7687\u5e1d\u3002\u4ed6\u4ed3\u7687\u51fa\u5bab\u90a3\u4e00\u665a\uff0c\u63d0\u8d77\u5251\u6765\u5411\u5973\u513f\u957f\u5e73\u516c\u4e3b\u65a9\u843d\u65f6\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u751f\u5728\u6211\u5bb6\uff1f\u201d\u6b63\u662f\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u5fc3\u610f\u3002\u4ed6\u7684\u6027\u683c\u3001\u624d\u80fd\u3001\u5e74\u9f84\uff0c\u90fd\u4e0d\u914d\u505a\u638c\u63e1\u5168\u56fd\u519b\u653f\u5927\u6743\u7684\u7687\u5e1d\u3002\u5f52\u6839\u7ed3\u5e95\uff0c\u662f\u4e13\u5236\u5236\u5ea6\u5bb3\u4e86\u4ed6\uff0c\u4e5f\u5bb3\u4e86\u5343\u5343\u4e07\u4e07\u4e2d\u56fd\u4eba\u6c11\u3002 \u3000\u3000\u5728\u5408\u7406\u7684\u653f\u6cbb\u5236\u5ea6\u4e0e\u793e\u4f1a\u5236\u5ea6\u4e0b\uff0c\u4e07\u5386\u53ef\u4ee5\u6210\u4e3a\u4e00\u4e2a\u7cbe\u660e\u7684\u5546\u4eba\uff0c\u6700\u540e\u88ab\u9001\u5165\u6212\u6bd2\u6240\u3002\u5929\u542f\u662f\u4e00\u4e2a\u7cbe\u5de7\u7684\u6728\u5320\u3002\u5d07\u796f\u505a\u751a\u4e48\u597d\u5462\uff1f\u4ed6\u6b8b\u5fcd\u55dc\u6740\uff0c\u66b4\u8e81\u591a\u7591\uff0c\u6027\u683c\u4e2d\u6709\u5f3a\u70c8\u7684\u72af\u7f6a\u503e\u5411\uff0c\u5728\u73b0\u4ee3\u793e\u4f1a\u4e2d\u6781\u53ef\u80fd\u6210\u4e3a\u4e00\u4e2a\u72af\u7f6a\u7684\u4e0d\u826f\u9752\u5e74\uff0c\u4f46\u5982\u52a0\u4ee5\u9002\u5f53\u7684\u6559\u80b2\u4e0e\u8bad\u7ec3\uff0c\u53ef\u4ee5\u5728\u5c60\u5bb0\u573a\u4e2d\u505a\u5c60\u592b\uff08\u6211\u5f53\u7136\u5e76\u4e0d\u662f\u8bf4\u5c60\u592b\u6709\u72af\u7f6a\u503e\u5411\uff09\uff0c\u90a3\u4e5f\u662f\u5bf9\u793e\u4f1a\u6709\u8d21\u732e\u7684\uff0c\u4ed6\u4e0d\u80fd\u505a\u730e\u4eba\uff0c\u56e0\u4e3a\u5b8c\u5168\u7f3a\u4e4f\u8010\u5fc3\u3002 \u3000\u3000\u540e\u4e16\u7684\u8bc4\u8bba\u8005\u5927\u90fd\u8ba4\u4e3a\uff0c\u8881\u5d07\u7115\u5982\u679c\u4e0d\u6b7b\uff0c\u6ee1\u6e05\u4e0d\u80fd\u5f81\u670d\u4e2d\u56fd\u2464\u3002\u6211\u4ee5\u4e3a\u8fd9\u79cd\u8bf4\u6cd5\u662f\u4e0d\u5bf9\u7684\u3002\u53ea\u8981\u5d07\u796f\u662f\u7687\u5e1d\uff0c\u8881\u5d07\u7115\u4fbf\u6709\u5929\u5927\u7684\u672c\u4e8b\u4e5f\u6539\u53d8\u4e0d\u4e86\u57fa\u672c\u5c40\u9762\uff0c\u9664\u975e\u4ed6\u6740\u4e86\u5d07\u796f\u800c\u81ea\u5df1\u6765\u505a\u7687\u5e1d\uff0c\u8fd9\u5f53\u7136\u4e0d\u7b26\u5408\u4ed6\u7684\u6027\u683c\u3002\u5728\u541b\u4e3b\u4e13\u5236\u72ec\u88c1\u7684\u5236\u5ea6\u4e4b\u4e0b\uff0c\u6743\u529b\u5728\u7687\u5e1d\u624b\u91cc\u3002 \u3000\u3000\u8881\u5d07\u7115\u6b7b\u540e\u4e8c\u767e\u4e09\u5341\u516d\u5e74\uff0c\u90a3\u65f6\u6e05\u671d\u4e5f\u5df2\u8150\u70c2\u5f97\u4e0d\u53ef\u6536\u62fe\u4e86\uff0c\u5728\u79bb\u5f00\u8881\u5d07\u7115\u5bb6\u4e61\u4e0d\u8fdc\u7684\u5730\u65b9\uff0c\u8bde\u751f\u4e86\u5b59\u4e2d\u5c71\u5148\u751f\u3002\u4ed6\u5411\u4e2d\u56fd\u4eba\u6307\u660e\uff1a\u5fc5\u987b\u7531\u89c1\u8bc6\u9ad8\u660e\u3001\u624d\u80fd\u5353\u8d8a\u3001\u54c1\u683c\u9ad8\u5c1a\u7684\u4eba\u6765\u7ba1\u7406\u56fd\u5bb6\u5927\u4e8b\u3002\u4e00\u65e6\u6709\u624d\u5e72\u7684\u4eba\u56e0\u8eab\u5c45\u9ad8\u4f4d\u800c\u53d7\u4e86\u6743\u529b\u7684\u8150\u5316\uff0c\u53d8\u6210\u4e13\u6a2a\u72ec\u65ad\u3001\u6b3a\u538b\u4eba\u6c11\u65f6\uff0c\u4eba\u6c11\u7acb\u523b\u5c31\u987b\u64a4\u6362\u4ed6\u3002 \u3000\u3000\u8881\u5d07\u7115\u548c\u5d07\u796f\u7684\u60b2\u5267\uff0c\u660e\u672b\u4e2d\u56fd\u4ebf\u4e07\u4eba\u6c11\u7684\u60b2\u5267\uff0c\u4e0d\u4f1a\u53d1\u751f\u4e8e\u4e00\u4e2a\u5177\u6709\u771f\u6b63\u6c11\u4e3b\u5236\u5ea6\u7684\u56fd\u5bb6\u4e2d\u3002\u628a\u51b3\u5b9a\u5343\u5343\u4e07\u4e07\u4eba\u6c11\u751f\u6b7b\u7978\u798f\u7684\u5927\u6743\u4ea4\u5728\u4e00\u4e2a\u4eba\u624b\u91cc\uff0c\u662f\u4e2d\u56fd\u6570\u5343\u5e74\u5386\u53f2\u4e2d\u4e00\u5207\u707e\u96be\u7684\u57fa\u672c\u6839\u6e90\u3002\u8fc7\u53bb\u6211\u4eec\u4e0d\u77e5\u9053\u5982\u4f55\u907f\u514d\u8fd9\u79cd\u707e\u96be\uff0c\u53ea\u76fc\u671b\u4e0a\u5929\u751f\u4e0b\u4e00\u4f4d\u5723\u4e3b\u8d24\u541b\uff0c\u8fd9\u613f\u671b\u7ecf\u5e38\u843d\u7a7a\u3002\u90a3\u662f\u5386\u53f2\u6761\u4ef6\u7684\u9650\u5236\uff0c\u662f\u4e2d\u56fd\u4eba\u7684\u4e0d\u5e78\u3002\u5b59\u4e2d\u5c71\u5148\u751f\u4e0d\u4f46\u8bf4\u660e\u4e86\u8fd9\u4e2a\u9053\u7406\uff0c\u66f4\u6bd5\u751f\u4e3a\u4e86\u94f2\u9664\u8fd9\u4e2a\u707e\u7978\u6839\u6e90\u800c\u52aa\u529b\u3002 \u3000\u3000\u5728\u8881\u5d07\u7115\u7684\u65f6\u4ee3\uff0c\u9ad8\u8d35\u52c7\u6562\u7684\u4eba\u53bb\u6297\u654c\u5165\u4fb5\uff0c\u4fdd\u536b\u4eba\u6c11\uff1b\u5728\u5b59\u4e2d\u5c71\u5148\u751f\u7684\u65f6\u4ee3\uff0c\u9ad8\u8d35\u52c7\u6562\u7684\u4eba\u53bb\u53cd\u6297\u4e13\u5236\uff0c\u4e3a\u4eba\u6c11\u4e89\u53d6\u6c11\u4e3b\u81ea\u7531\u3002\u5728\u6bcf\u4e00\u4e2a\u65f6\u4ee3\u4e2d\uff0c\u6211\u4eec\u603b\u89c1\u5230\u4e00\u4e9b\u9ad8\u8d35\u7684\u52c7\u6562\u7684\u4eba\uff0c\u4e3a\u4e86\u4eba\u7fa4\u800c\u732e\u51fa\u81ea\u5df1\u7684\u4e00\u751f\uff0c\u4ed6\u4eec\u7684\u529f\u4e1a\u6709\u5927\u6709\u5c0f\uff0c\u5b59\u4e2d\u5c71\u5148\u751f\u7684\u529f\u4e1a\u6781\u5927\uff0c\u8881\u5d07\u7115\u5f53\u7136\u5c0f\u5f97\u591a\uff0c\u7136\u800c\u4ed6\u4eec\u90fd\u662f\u594b\u4e0d\u987e\u8eab\uff0c\u5c3d\u529b\u800c\u4e3a\u3002\u65f6\u4ee3\u4e0d\u65ad\u5728\u53d8\u8fc1\uff0c\u9053\u5fb7\u89c2\u5ff5\u3001\u5386\u53f2\u89c2\u70b9\u3001\u529f\u8fc7\u7684\u8bc4\u4ef7\u4e5f\u4e0d\u65ad\u6539\u53d8\uff0c\u7136\u800c\u4ece\u9ad8\u8d35\u7684\u4eba\u6027\u4e2d\u95ea\u8000\u51fa\u6765\u7684\u7470\u4e3d\u5149\u5f69\uff0c\u90a3\u4e9b\u5927\u5927\u5c0f\u5c0f\u7684\u706b\u82b1\uff0c\u5373\u4f7f\u5728\u6700\u9ed1\u6697\u7684\u65f6\u671f\u4e4b\u4e2d\uff0c\u4e5f\u7167\u4eae\u4e86\u4eba\u7c7b\u5386\u53f2\u7684\u9053\u8def\u3002 \u3000\u3000\u5386\u53f2\u4e0a\u6709\u8bb8\u591a\u4eba\u4e3a\u4eba\u7fa4\u7acb\u4e86\u5927\u529f\u4e1a\uff0c\u4ee4\u6211\u4eec\u611f\u8c22\uff1b\u6709\u8bb8\u591a\u4eba\u5efa\u7acb\u4e86\u5927\u5e1d\u56fd\u548c\u957f\u4e45\u7684\u7687\u671d\uff0c\u4ee4\u6211\u4eec\u60ca\u53f9\u3002\u7136\u800c\u8881\u5d07\u7115\u201c\u4ea1\u547d\u5f92\u201d\u5f0f\u7684\u52aa\u529b\u548c\u82e6\u5fc3\uff0c\u4ed6\u6781\u5ea6\u60b2\u60e8\u7684\u906d\u9047\uff0c\u8fd9\u4e2a\u751f\u6b7b\u4ee5\u4e4b\u7684\u201c\u75f4\u5fc3\u4eba\u201d\uff0c\u8fd9\u4e2a\u65e0\u6cd5\u65e0\u5929\u7684\u201c\u6cfc\u80c6\u6c49\u201d\uff0c\u5374\u66f4\u52a0\u5f3a\u70c8\u7684\u6fc0\u8361\u4e86\u6211\u4eec\u7684\u5fc3\u3002 \u3000\u3000\u5d07\u796f\u548c\u8881\u5d07\u7115\u4e24\u4eba\u7684\u6027\u683c\uff0c\u4f7f\u5f97\u8fd9\u60b2\u5267\u4e0d\u53ef\u80fd\u6709\u522b\u7684\u7ed3\u5c40\u3002\u4e24\u4eba\u7b2c\u4e00\u6b21\u5e73\u53f0\u76f8\u89c1\uff0c\u8881\u5d07\u7115\u63d0\u51fa\u201c\u4e94\u5e74\u5e73\u8fbd\u201d\u7684\u8bfa\u8a00\uff0c\u6740\u673a\u5c31\u5df2\u7ecf\u4f0f\u4e0b\u4e86\u3002\u4ee5\u540e\u4ed6\u8bf7\u5185\u5e11\u3001\u4e3b\u548c\u8bae\u3001\u6740\u6bdb\u6587\u9f99\uff0c\u60b2\u5267\u4e00\u6b65\u6b65\u7684\u5c55\u5f00\uff0c\u6740\u673a\u4e00\u5c42\u5c42\u7684\u52a0\u6df1\uff0c\u5230\u6e05\u519b\u5175\u4e34\u5317\u4eac\u57ce\u4e0b\u800c\u5230\u8fbe\u9ad8\u6f6e\u3002\u5728\u8fd9\u60b2\u5267\u7684\u9ad8\u6f6e\u4e2d\uff0c\u5d07\u796f\u4e0d\u8bb8\u8881\u90e8\u5165\u57ce\u662f\u7b2c\u4e00\u4e2a\u6ce2\u6d6a\uff1b\u8881\u90e8\u82e6\u6218\u5f97\u80dc\uff0c\u5d07\u796f\u50ac\u903c\u4ed6\u53bb\u8ffd\u51fb\u5341\u500d\u5175\u529b\u7684\u6e05\u519b\uff0c\u662f\u7b2c\u4e8c\u4e2a\u6ce2\u6d6a\uff1b\u5317\u4eac\u57ce\u91cc\u6bc1\u8c24\u8881\u5d07\u7115\u7684\u8c23\u8bfc\u7eb7\u4f20\u662f\u7b2c\u4e09\u4e2a\u6ce2\u6d6a\uff1b\u7ec8\u4e8e\uff0c\u7687\u592a\u6781\u4f7f\u53cd\u95f4\u8ba1\u800c\u5d07\u796f\u4e2d\u8ba1\u3002\u81f3\u4e8e\u540e\u6765\u7684\u51cc\u8fdf\uff0c\u5df2\u662f\u620f\u5267\u7ed3\u6784\u4e0a\u7684\u8361\u6f3e\u4f59\u6ce2\u2465\u4e86\u3002 \u3000\u3000\u5373\u4f7f\u6ca1\u6709\u7687\u592a\u6781\u7684\u53cd\u95f4\u8ba1\uff0c\u5d07\u796f\u7ec8\u4e8e\u8fd8\u662f\u4f1a\u56e0\u522b\u7684\u4e8b\u4ef6\u3001\u7528\u522b\u7684\u501f\u53e3\u6765\u6740\u4e86\u4ed6\u7684\u3002 \u3000\u3000\u6211\u4eec\u60f3\u8c61\u5d07\u796f\u4e8c\u5e74\u814a\u6708\u4e2d\u56fd\u5317\u65b9\u7684\u60c5\u5f62\uff1a\u5728\u6c38\u5e73\u3001\u6ee6\u5dde\u3001\u8fc1\u5b89\u3001\u9075\u5316\u4e00\u5e26\u7684\u57ce\u5185\u548c\u90ca\u5916\uff0c\u6e05\u5175\u7684\u957f\u5200\u6b63\u5728\u780d\u5411\u6bcf\u4e00\u4e2a\u6c49\u4eba\u8eab\u4e0a\uff0c\u6ee1\u57ce\u90fd\u662f\u9c9c\u8840\uff0c\u6ee1\u5730\u90fd\u662f\u5c38\u9996\u2466\u2026\u2026 \u3000\u3000\u5728\u901a\u5411\u957f\u57ce\u5173\u53e3\u7684\u5927\u9053\u4e0a\uff0c\u6570\u5341\u4e07\u6c49\u4eba\u7537\u5973\u54ed\u54ed\u557c\u557c\u7684\u884c\u8d70\uff0c\u9a91\u5728\u9a6c\u4e0a\u7684\u6e05\u5175\u6325\u821e\u97ad\u5b50\u5728\u9a71\u8d76\u3002\u6e05\u5175\u4e0d\u65ad\u7684\u6b22\u547c\u5927\u53eb\uff0c\u8fd9\u4e9b\u6c49\u4eba\u662f\u4ed6\u4eec\u4fd8\u864f\u6765\u7684\u5974\u96b6\uff0c\u7537\u7684\u62bc\u53bb\u8fbd\u4e1c\u4e3a\u4ed6\u4eec\u505a\u82e6\u5de5\uff0c\u5973\u7684\u5206\u7ed9\u5175\u5c06\u6deb\u4e50\u2467\u2026\u2026 \u3000\u3000\u5728\u9655\u897f\uff0c\u707e\u8352\u6b63\u5728\u5927\u6d41\u884c\u3002\u6811\u76ae\u8349\u6839\u90fd\u5403\u5b8c\u4e86\uff0c\u9965\u997f\u7684\u7236\u6bcd\u517b\u4e0d\u6d3b\u513f\u5973\uff0c\u53ea\u597d\u5c06\u4ed6\u4eec\u629b\u5728\u57ce\u89d2\u7684\u7a7a\u573a\u4e0a\uff0c\u8fd9\u4e9b\u5b69\u5b50\u6709\u7684\u5728\u54ed\u53f7\uff0c\u547c\u53eb\uff1a\u201c\u7238\u7238\uff0c\u5988\u5988\uff01\u201d\u6709\u7684\u62fe\u8d77\u4e86\u7caa\u4fbf\u5728\u5403\u3002\u5230\u7b2c\u4e8c\u5929\uff0c\u8fd9\u4e9b\u5b69\u5b50\u90fd\u6b7b\u4e86\u3002\u4f46\u53c8\u6709\u7236\u6bcd\u62b1\u4e86\u5b69\u5b50\u6765\u629b\u5f03\u3002\u505a\u6bcd\u4eb2\u7684\u770b\u7740\u6ee1\u5730\u6b7b\u513f\uff0c\u820d\u5f97\u628a\u624b\u91cc\u7684\u5b69\u5b50\u629b\u4e0b\u6765\u5417\uff1f\u4f46\u5982\u5e26\u56de\u5bb6\u53bb\uff0c\u96be\u9053\u773c\u770b\u4ed6\u6d3b\u6d3b\u7684\u997f\u6b7b\u2468\u2026\u2026 \u3000\u3000\u6d41\u79bb\u5728\u9053\u8def\u4e0a\u7684\u9965\u6c11\u4e0d\u77e5\u9053\u602a\u8c01\u624d\u597d\uff0c\u53ea\u6709\u602a\u5929\u3002\u4ed6\u4eec\u5411\u6765\u5bf9\u8001\u5929\u7237\u53c8\u656c\u53c8\u6015\uff0c\u8fd9\u65f6\u53cd\u6b63\u8981\u6b7b\u4e86\uff0c\u5c31\u7b97\u5728\u5730\u72f1\u4e2d\u4e0a\u5200\u5c71\u3001\u4e0b\u6cb9\u9505\u4e5f\u4e0d\u7ba1\u4e86\uff0c\u4ed6\u4eec\u7834\u53e3\u5927\u9a82\u8001\u5929\u7237\uff0c\u6709\u6c14\u65e0\u529b\u7684\u5492\u9a82\uff0c\u7ec8\u4e8e\u5012\u5728\u5730\u4e0b\uff0c\u518d\u4e5f\u8d77\u4e0d\u6765\u247d\u2026\u2026 \u3000\u3000\u5728\u5317\u4eac\u57ce\u7684\u6df1\u5bab\u91cc\uff0c\u5341\u516b\u5c81\u7684\u5c11\u5e74\u7687\u5e1d\u5728\u62cd\u7740\u684c\u5b50\u53d1\u813e\u6c14\u3002\u4ed6\u53c8\u662f\u7126\u6025\uff0c\u53c8\u662f\u5bb3\u6015\uff0c\u4e0d\u65ad\u7684\u95ee\u592a\u76d1\uff1a\u201c\u8881\u86ee\u5b50\u5199\u4e86\u4fe1\u6ca1\u6709\uff1f\u600e\u4e48\u8fd8\u4e0d\u5199\u597d\uff1f\u8fd9\u5bb6\u4f19\u8ddf\u6211\u8fc7\u4e0d\u53bb\uff0c\u975e\u5c06\u4ed6\u5343\u5200\u4e07\u5250\u4e0d\u53ef\u3002\u4f60\u4eec\u518d\u53bb\u50ac\uff0c\u53eb\u4ed6\u5feb\u5199\u4fe1\u7ed9\u7956\u5927\u5bff\uff01\u201d\u4ed6\u6194\u60b4\u82cd\u767d\u7684\u8138\u4e0a\u6cdb\u8d77\u4e86\u6f6e\u7ea2\uff0c\u773c\u4e2d\u5e03\u6ee1\u4e86\u7ea2\u4e1d\uff0c\u4e0d\u65ad\u7684\u8bf4\uff1a\u201c\u6740\u4e86\u4ed6\uff01\u6740\u4e86\u4ed6\uff01\u201d\u2026\u2026 \u3000\u3000\u5728\u9634\u68ee\u5bd2\u51b7\u7684\u5fa1\u7262\u91cc\uff0c\u8881\u5d07\u7115\u63d0\u7b14\u5728\u5199\u4fe1\u7ed9\u7956\u5927\u5bff\uff0c\u781a\u53f0\u91cc\u4f1a\u7ed3\u51b0\u5427\uff1f\u4ed6\u7684\u624b\u4f1a\u51bb\u5f97\u50f5\u786c\u5417\uff1f\u4f1a\u56e0\u6124\u6012\u800c\u98a4\u6296\u5417\uff1f\u4ed6\u7684\u4fe1\u91cc\u5199\u7684\u662f\u4e9b\u751a\u4e48\u53e5\u5b50\uff1f\u6cea\u6c34\u4e00\u5b9a\u6ef4\u4e0a\u4e86\u4fe1\u7b3a\u7f62\uff1f \u3000\u3000\u7687\u5e1d\u7684\u4fe1\u4f7f\u5feb\u9a6c\u9a70\u51fa\u5c71\u6d77\u5173\u5916\uff0c\u5c06\u8fd9\u5c01\u4fe1\u4ea4\u5728\u7956\u5927\u5bff\u7684\u624b\u91cc\u3002\u7956\u5927\u5bff\u8bfb\u4fe1\u4e4b\u540e\uff0c\u4f0f\u5730\u5927\u54ed\u3002\u8baf\u606f\u4f20\u4e86\u5f00\u53bb\uff1a\u201c\u7763\u5e08\u6709\u4fe1\u6765\uff01\u201d \u3000\u3000\u8fbd\u6cb3\u5927\u5e73\u539f\u4e0a\u767d\u832b\u832b\u7684\u4e00\u7247\u51b0\u96ea\u3002\u6570\u4e07\u540d\u95f4\u5173\u767e\u6218\u3001\u6ee1\u8eab\u7d2f\u7d2f\u67aa\u4f24\u7bad\u75a4\u7684\u5173\u4e1c\u5927\u6c49\uff0c\u4f0f\u5728\u5730\u4e0b\u5411\u7740\u5317\u4eac\u53f7\u5555\u75db\u54ed\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u7763\u5e08\u5feb\u8981\u88ab\u7687\u5e1d\u6740\u6b7b\u4e86\u3002\u6218\u9a6c\u60b2\u5636\uff0c\u6714\u98ce\u547c\u5578\uff0c\u7ef5\u5ef6\u6570\u91cc\u7684\u96ea\u5730\u91cc\u5c3d\u662f\u4f0f\u7740\u6124\u6012\u4f24\u5fc3\u7684\u8c6a\u58eb\uff0c\u767d\u96ea\u4e0d\u65ad\u7684\u843d\u5728\u4ed6\u4eec\u7684\u94c1\u76d4\u4e0a\u3001\u94c1\u7532\u4e0a\u2026\u2026 \u3000\u3000\u2460\u89c1\u4f59\u5927\u6210\u300a\u5256\u809d\u5f55\u300b\u3002 \u3000\u3000\u2461\u300a\u8bba\u8bed\xb7\u5b63\u6c0f\u300b\uff1a\u201c\u5b54\u5b50\u66f0\uff1a\u2018\u541b\u5b50\u6709\u4e5d\u601d\uff1a\u89c6\u601d\u660e\uff0c\u542c\u601d\u806a\uff0c\u8272\u601d\u6e29\uff0c\u8c8c\u601d\u606d\uff0c\u8a00\u601d\u5fe0\uff0c\u4e8b\u601d\u656c\uff0c\u7591\u601d\u95ee\uff0c\u5fff\u601d\u96be\uff0c\u89c1\u5f97\u601d\u4e49\u3002\u2019\u201d \u3000\u3000\u5d07\u796f\u6b7b\u540e\uff0c\u56e0\u4e3a\u6ca1\u6709\u786e\u5b9a\u7684\u63a5\u73ed\u4eba\uff0c\u4e5f\u5c31\u6ca1\u6709\u786e\u5b9a\u7684\u8c25\u6cd5\uff0c\u6709\u6bc5\u5b97\u3001\u5e84\u70c8\u5e1d\u3001\u6000\u5e1d\u3001\u610d\u5e1d\u3001\u601d\u5b97\u7b49\u8c25\u3002\u601d\u5b97\u7684\u201c\u601d\u201d\u5b57\uff0c\u4e0d\u662f\u7f8e\u8c25\uff0c\u300a\u9038\u5468\u4e66\u300b\u7684\u8c25\u6cd5\u89e3\u4e2d\u8bf4\uff1a\u201c\u9053\u5fb7\u7eaf\u4e00\u66f0\u601d\uff0c\u5927\u7701\uff08\u5373\u201c\u771a\u201d\uff0c\u707e\u5bb3\u7684\u610f\u601d\uff09\u5146\u6c11\u66f0\u601d\uff0c\u8ffd\u6094\u524d\u8fc7\u66f0\u601d\uff0c\u5916\u5185\u601d\u7d22\u66f0\u601d\u3002\u201d \u3000\u3000\u6c49\u671d\u7684\u738b\u9038\u4f5c\u8fc7\u4e00\u7bc7\u695a\u8f9e\uff0c\u53eb\u4f5c\u300a\u4e5d\u601d\u300b\uff0c\u662f\u54c0\u60bc\u5c48\u539f\u7684\uff0c\u5171\u6709\u4e5d\u7ae0\uff1a\u9022\u5c24\u3001\u6028\u4e0a\u3001\u75be\u4e16\u3001\u60af\u4e0a\u3001\u906d\u5384\u3001\u60bc\u4e71\u3001\u4f24\u65f6\u3001\u54c0\u5c81\u3001\u5b88\u5fd7\u3002\u6240\u8bf4\u7684\u60bc\u4e71\u4f24\u65f6\uff0c\u75be\u4e16\u54c0\u5c81\uff0c\u9022\u5c24\u906d\u5384\uff0c\u548c\u8881\u5d07\u7115\u7684\u5fc3\u5883\u548c\u906d\u9047\u5012\u4e5f\u5dee\u4e0d\u591a\u3002\u4f46\u5d07\u796f\u5199\u8fd9\u300a\u4e5d\u601d\u300b\u4e8c\u5b57\u65f6\uff0c\u6240\u60f3\u5230\u7684\u5f53\u7136\u4e0d\u4f1a\u662f\u738b\u9038\u7684\u300a\u4e5d\u601d\u300b\u3002 \u3000\u3000\u2462\u5d07\u796f\u9057\u8bcf\uff1a\u201c\u6715\u81ea\u767b\u6781\u5341\u4e03\u5e74\uff0c\u4e0a\u9080\u5929\u7f6a\uff0c\u81f4\u864f\u9677\u5730\u4e09\u6b21\uff0c\u9006\u8d3c\u76f4\u903c\u4eac\u5e08\uff0c\u7686\u8bf8\u81e3\u8bef\u6715\u4e5f\u3002\u4efb\u5c14\u5206\u88c2\u6715\u5c38\uff0c\u53ef\u5c06\u6587\u6b66\u5c3d\u7686\u6740\u6b7b\uff0c\u52ff\u574f\u9675\u5bdd\uff0c\u52ff\u4f24\u6211\u767e\u59d3\u4e00\u4eba\u3002\u201d\u8fd9\u9053\u9057\u8bcf\uff0c\u548c\u76f8\u4f20\u7559\u5728\u4ed6\u8eab\u4e0a\u7684\u9057\u4e66\u6587\u5b57\u7a0d\u6709\u4e0d\u540c\u3002 \u3000\u3000\u2463\u201c\u541b\u975e\u751a\u959b\uff08\u6ce8\uff1a\u5c31\u4e3a\u201c\u95c7\u201d\uff0c\u5916\u6846\u662f\u201c\u95e8\u201d\u5b57\u5934\uff09\uff0c\u5b64\u7acb\u800c\u7080\u853d\u6052\u591a\uff1b\u81e3\u5c3d\u884c\u79c1\uff0c\u6bd4\u515a\u800c\u516c\u5fe0\u7edd\u5c11\u3002\u201d \u3000\u3000\u2464\u6881\u542f\u8d85\u5728\u300a\u8881\u5d07\u7115\u4f20\u300b\u7684\u9898\u76ee\u4e0a\uff0c\u52a0\u4e86\u201c\u660e\u5b63\u7b2c\u4e00\u91cd\u8981\u4eba\u7269\u201d\u7684\u5f62\u5bb9\u8bcd\uff0c\u4f20\u4e2d\u8bf4\uff1a\u5e7f\u4e1c\u5d0e\u5c96\u5cad\u8868\uff0c\u6570\u5343\u5e74\u6765\u4e0e\u4e2d\u539f\u7684\u5173\u7cfb\u5f88\u6d45\u8584\uff0c\u5386\u53f2\u4e0a\u5f71\u54cd\u5230\u5168\u4e2d\u56fd\u7684\u4eba\u7269\u6781\u5c11\uff0c\u53ea\u6709\u5510\u671d\u516d\u7956\u6167\u80fd\u5149\u5927\u4e86\u7985\u5b97\uff0c\u660e\u671d\u9648\u767d\u6c99\u5728\u54f2\u5b66\u4e0a\u5021\u660e\u552f\u5fc3\u8bba\uff0c\u6210\u4e3a\u738b\u9633\u660e\u7684\u5148\u9a71\uff0c\u800c\u201c\u4ee5\u4e00\u8eab\u4e4b\u8a00\u52a8\u3001\u8fdb\u9000\u3001\u751f\u6b7b\uff0c\u5173\u7cfb\u56fd\u5bb6\u4e4b\u5b89\u5371\u3001\u6c11\u65cf\u4e4b\u9686\u66ff\u8005\u201d\uff0c\u53ea\u6709\u8881\u5d07\u7115\u4e00\u4eba\u3002\uff08\u5176\u5b9e\uff0c\u4ed6\u5373\u4f7f\u4e0d\u63d0\u5230\u5b59\u4e2d\u5c71\u5148\u751f\uff0c\u4e5f\u5e94\u5f53\u63d0\u6d2a\u79c0\u5168\u3002\uff09\u53c8\u8bf4\uff1a\u201c\u6545\u8881\u7763\u5e08\u4e00\u65e5\u4e0d\u53bb\uff0c\u5219\u6ee1\u6d32\u4e07\u4e0d\u80fd\u5f97\u5fd7\u4e8e\u4e2d\u56fd\u3002\u201d\u5eb7\u6709\u4e3a\u5728\u300a\u8881\u7763\u5e08\u9057\u96c6\u5e8f\u300b\u4e2d\u8bf4\uff1a\u201c\u82e5\u543e\u7ca4\u8881\u7763\u5e08\u4e4b\u4e27\u4e8e\u8c17\u95f4\u4e5f\uff0c\u5929\u4e0b\u9707\u52a8\uff0c\u9b3c\u795e\u53f7\u6ce3\uff0c\u660e\u793e\u9042\u5c4b\uff0c\u4f59\u7978\u70c8\u70c8\uff0c\u6ce2\u8361\u81f3\u4eca\u3002\u545c\u547c\uff0c\u5929\u4e0b\u624d\u81e3\u540d\u5c06\u591a\u77e3\uff0c\u8c17\u6b7b\u4ea6\u81f3\u4f19\uff0c\u800c\u607b\u607b\u4e8e\u4eba\u5fc3\uff0c\u9707\u60d5\u4e8e\u654c\u56fd\uff0c\u975e\u6b62\u4ee5\u4e00\u8eab\u4e4b\u751f\u6b7b\u7cfb\u4e00\u59d3\u4e4b\u5b58\u4ea1\uff0c\u5b9e\u4ee5\u4e00\u8eab\u4e4b\u751f\u547d\u5173\u4e2d\u56fd\u4e4b\u5168\u5c40\uff0c\u5219\u5c82\u60df\u675c\u90ae\u3001\u949f\u5ba4\u3001\u51c9\u98ce\u3001\u91d1\u724c\u4e4b\u51c4\u611f\u4e5f\u3002\u2026\u2026\u5047\u82e5\u95f4\u4e0d\u884c\u800c\u80fd\u5c3d\u5176\u624d\uff0c\u660e\u6216\u4e0d\u4ea1\u3002\u201d\u4ed6\u8ba4\u4e3a\u767d\u8d77\u3001\u97e9\u4fe1\u3001\u659b\u5f8b\u5149\u3001\u5cb3\u98de\u56db\u4eba\u88ab\u8c17\u800c\u6b7b\uff0c\u867d\u4ee4\u4eba\u611f\u53f9\uff0c\u4f46\u4e8e\u56fd\u5bb6\u5b58\u4ea1\u65e0\u5173\uff0c\u4e0d\u53ca\u8881\u5d07\u7115\u4e8b\u4ef6\u5f71\u54cd\u6df1\u8fdc\u3002 \u3000\u3000\u674e\u6d4e\u6df1\u300a\u91cd\u4fee\u660e\u7763\u5e08\u8881\u5d07\u7115\u8bcd\u5893\u7891\u300b\uff1a\u201c\u8bba\u660e\u6e05\u95f4\u4e8b\u8005\uff0c\u4f65\u4ee5\u4e3a\u7763\u5e08\u4e0d\u6b7b\uff0c\u6ee1\u6e05\u4e0d\u80fd\u5165\u4e3b\u4e2d\u539f\u3002\u201d\u53f6\u606d\u7ef0\u8c12\u8881\u5d07\u7115\u5893\u8bd7\uff1a\u201c\u53f2\u7b14\u53ea\u4eca\u91cd\u8bba\u5b9a\uff0c\u597d\u7533\u6b63\u6c14\u606f\u7fa4\u7eb7\u3002\u201d\u6ce8\u4e91\uff1a\u201c\u8fd1\u65e5\u53f2\u5b66\u5bb6\u94a9\u7a3d\u4e8b\u5b9e\uff0c\u8bc1\u660e\u8881\u5982\u4e0d\u6b7b\uff0c\u6ee1\u6d32\u4e0d\u80fd\u5750\u5927\uff0c\u5373\u672a\u5fc5\u514b\u5165\u4e3b\u4e2d\u539f\uff0c\u6545\u8881\u6b7b\u6240\u5173\u4e4b\u91cd\uff0c\u6709\u540c\u5cb3\u98de\u4e8e\u5b8b\u3002\u6587\u5929\u7965\u8f88\u5c1a\u975e\u5176\u6bd4\u4e5f\u3002\u201d \u3000\u3000\u2465\u620f\u5267\u7ed3\u6784\u4e0a\u9ad8\u6f6e\u8fc7\u540e\u7684\u4f59\u6ce2\uff08anti-climax\uff09\uff08\u6ce8\uff1a\uff41\uff4e\uff54\uff49\uff0d\uff43\uff4c\uff49\uff4d\uff41\uff58\uff09\uff0c\u901a\u5e38\u8bd1\u4f5c\u201c\u53cd\u9ad8\u6f6e\u201d\uff0c\u4f3c\u4e0d\u751a\u8d34\u5207\u3002 \u3000\u3000\u2466\u300a\u6e05\u53f2\u5217\u4f20\u300b\u5377\u4e09\uff1a\u201c\u5cb3\u6258\uff08\u6ee1\u6e05\u5927\u5c06\uff0c\u4ee3\u5584\u4e4b\u5b50\uff0c\u7687\u592a\u6781\u7684\u4f84\u513f\uff09\u66f0\uff1a\u8fbd\u4e1c\u4ee5\u4e45\u4e0d\u964d\uff0c\u6545\u8bdb\u4e4b\u3002\u6740\u6c38\u5e73\u4eba\uff0c\u4e43\u8d1d\u52d2\u963f\u654f\u6240\u4e3a\u2026\u2026\u516d\u5e74\u6b63\u6708\uff0c\uff08\u5cb3\u6258\uff09\u594f\u8a00\uff1a\u524d\u514b\u8fbd\u4e1c\u3001\u5e7f\u5b81\uff0c\u6c49\u4eba\u62d2\u547d\u8005\u8bdb\u4e4b\uff0c\u590d\u5c60\u6c38\u5e73\u3001\u6ee6\u5dde\u6c49\u4eba\u3002\u201d \u3000\u3000\u2467\u6ee1\u6e05\u6bcf\u6b21\u51fa\u5175\uff0c\u90fd\u4fd8\u864f\u5927\u91cf\u6c49\u4eba\u53bb\u505a\u751f\u4ea7\u5de5\u5177\u3002\u8fd9\u6b21\u8fdb\u653b\u5317\u4eac\u4e4b\u5f79\u4fd8\u864f\u7684\u5b9e\u6570\u65e0\u8bb0\u5f55\uff0c\u4f46\u77e5\u963f\u5df4\u6cf0\u653b\u63a0\u5c71\u4e1c\u4e4b\u5f79\uff08\u300a\u78a7\u8840\u5251\u300b\u4e2d\u63d0\u5230\u7684\u90a3\u4e00\u6b21\uff09\u201c\u4fd8\u83b7\u4eba\u6c11\u4e09\u5341\u516d\u4e07\u4e5d\u5343\u540d\u53e3\u3002\u201d\u76f8\u4fe1\u5d07\u796f\u4e8c\u5e74\u4e00\u5f79\u4e2d\u4fd8\u864f\u6c49\u4eba\u4e5f\u5fc5\u8fbe\u6570\u5341\u4e07\uff0c\u300a\u592a\u5b97\u5b9e\u5f55\u300b\u5377\u516d\uff1a\u201c\u4e0a\u56e0\u95ee\u8fbe\u6d77\uff08\u5949\u547d\u76d1\u5b88\u660e\u5bab\u592a\u76d1\u800c\u4f7f\u53cd\u95f4\u8ba1\u7684\u4e94\u5c06\u4e4b\u4e00\uff09\u7b49\uff1a\u2018\u662f\u5f79\u4fd8\u83b7\u89c6\u524d\u4e8c\u6b21\u5982\u4f55\uff1f\u2019\u5bf9\u66f0\uff1a\u2018\u6b64\u884c\u4fd8\u83b7\u4eba\u53e3\uff0c\u8f83\u524d\u751a\u591a\uff01\u2019\u4e0a\u66f0\uff1a\u2018\u91d1\u94f6\u5e01\u5e1b\uff0c\u867d\u591a\u5f97\u4e0d\u8db3\u559c\uff0c\u60df\u591a\u5f97\u4eba\u53e3\u4e3a\u53ef\u559c\u8033\uff01\u2019\u201d \u3000\u3000\u2468\u300a\u9655\u897f\u901a\u5fd7\u300b\uff0c\u5d07\u796f\u4e8c\u5e74\u9a6c\u61cb\u624d\u300a\u5907\u9648\u707e\u53d8\u758f\u300b\uff1a\u201c\u6b86\u5e74\u7ec8\u800c\u6811\u76ae\u5c3d\u77e3\uff0c\u5219\u53c8\u6398\u5c71\u4e2d\u77f3\u5757\u800c\u98df\u2026\u2026\u5b89\u585e\u57ce\u897f\uff0c\u6709\u7caa\u573a\u4e00\u5904\uff0c\u6bcf\u6668\u5fc5\u5f03\u4e8c\u4e09\u5a74\u513f\u4e8e\u5176\u4e2d\uff0c\u6709\u6d95\u6ce3\u8005\uff0c\u6709\u53eb\u53f7\u8005\uff0c\u6709\u547c\u5176\u7236\u6bcd\u8005\uff0c\u6709\u98df\u5176\u7caa\u8005\u3002\u201d \u3000\u3000\u247d\u8427\u4e00\u5c71\u300a\u6e05\u4ee3\u901a\u53f2\u300b\u5377\u4e0a\uff1a\u201c\u5d07\u796f\u95f4\u6709\u6c11\u8c23\u66f0\uff1a\u2018\u8001\u5929\u7237\uff0c\u4f60\u5e74\u7eaa\u5927\uff0c\u8033\u53c8\u804b\u6765\u773c\u53c8\u82b1\u3002\u4e3a\u975e\u4f5c\u6b79\u7684\u4eab\u5c3d\u8363\u534e\uff0c\u6301\u658b\u884c\u5584\u7684\u6d3b\u6d3b\u997f\u715e\u3002\u8001\u5929\u7237\uff0c\u4f60\u5e74\u7eaa\u5927\u3002\u4f60\u4e0d\u4f1a\u4f5c\u5929\uff0c\u4f60\u584c\u4e86\u7f62\uff01\u2019\u6b64\u79cd\u65f6\u65e5\u66f7\u4e27\u4e4b\u5fc3\u7406\uff0c\u975e\u4eba\u6c11\u75db\u82e6\u81f3\u6781\u8005\uff0c\u5b81\u5fcd\u51fa\u6b64\uff1f\u201d \u3000\u3000\u6e05\u8fdc\u9644\u8a00\uff1a\u672c\u6587\u5185\u5bb9\u592a\u957f\uff08\u8fd1\u516b\u4e07\u5b57\uff09\uff0c\u5982\u679c\u4e0d\u662f\u771f\u7684\u60f3\u4e86\u89e3\u66f4\u591a\u6545\u4e8b\u80cc\u666f\u7684\u8bdd\u662f\u4e0d\u5927\u4f1a\u770b\u7684\uff0c\u800c\u770b\u4e5f\u53ef\u80fd\u591a\u6570\u4ec5\u4f1a\u5927\u81f4\u4e86\u89e3\u4e00\u4e0b\u3002\u60a8\u6240\u770b\u5230\u7684\u8fd9\u4e2a\u662f\u6765\u81ea\u300a\u78a7\u8840\u5251\u300b\u4e2d\u7684\u7535\u5b50\u7248\u672c\uff0c\u6211\u53ea\u8df3\u7740\u5feb\u901f\u8fc7\u4e86\u4e00\u904d\uff0c\u505a\u4e86\u6821\u6b63\u5176\u4e2d\u5c11\u6570\u4e71\u7801\u3001\u4e0e\u539f\u6587\u4e0d\u7b26\u7684\u6bb5\u843d\u7f16\u6392\u65b9\u5f0f\u3001\u6f0f\u6389\u7684\u5927\u6bb5\u843d\u95f4\u7684\u7a7a\u884c\uff0c\u4ee5\u53ca\u4e00\u4e9b\u6709\u7684\u8fde\uff27\uff22\uff2b\u5b57\u5e93\u4e2d\u90fd\u6ca1\u6709\u7684\u5b57\u7b49\u3002\u80fd\u5982\u6b64\u505a\uff0c\u4e3b\u8981\u662f\u5f97\u5b9c\u4e8e\u539f\u59cb\u7248\u672c\u7684\u8d28\u91cf\u633a\u597d\uff08\u5c31\u6211\u6240\u4e86\u89e3\u7684\u3002\u800c\u82e5\u662f\u8981\u4ece\u5934\u5230\u5c3e\u751a\u81f3\u8fd8\u5bf9\u7740\u4e66\u6765\u8fc7\u4e00\u904d\uff0c\u5219\u592a\u8017\u65f6\u8d39\u529b\u4e86\uff09\uff0c\u6211\u4e5f\u53ef\u4ee5\u7701\u529f\u3002 \u3000 \u540e\u8bb0 \u3000\u3000\u300a\u78a7\u8840\u5251\u300b\u662f\u6211\u7684\u7b2c\u4e8c\u90e8\u5c0f\u8bf4\uff0c\u4f5c\u4e8e\u4e00\u4e5d\u4e94\u516d\u5e74\u3002 \u3000\u3000\u300a\u78a7\u8840\u5251\u300b\u7684\u771f\u6b63\u4e3b\u89d2\u5176\u5b9e\u662f\u8881\u5d07\u7115\uff0c\u5176\u6b21\u662f\u91d1\u86c7\u90ce\u541b\uff0c\u4e24\u4e2a\u5728\u4e66\u4e2d\u6ca1\u6709\u6b63\u5f0f\u51fa\u573a\u7684\u4eba\u7269\u3002\u8881\u627f\u5fd7\u7684\u6027\u683c\u5e76\u4e0d\u9c9c\u660e\u3002\u4e0d\u8fc7\u8881\u5d07\u7115\u4e5f\u6ca1\u6709\u5199\u597d\uff0c\u6240\u4ee5\u5728\u4e00\u4e5d\u4e03\u4e94\u5e74\u4e94\u516d\u6708\u95f4\u53c8\u5199\u4e86\u4e00\u7bc7\u300a\u8881\u5d07\u7115\u8bc4\u4f20\u300b\u4f5c\u4e3a\u8865\u5145\u3002 \u3000\u3000\u300a\u78a7\u8840\u5251\u300b\u66fe\u4f5c\u4e86\u4e24\u6b21\u9887\u5927\u4fee\u6539\uff0c\u589e\u52a0\u4e86\u4e94\u5206\u4e4b\u4e00\u5de6\u53f3\u7684\u7bc7\u5e45\u3002\u4fee\u8ba2\u7684\u5fc3\u529b\uff0c\u5728\u8fd9\u90e8\u4e66\u4e0a\u4ed8\u51fa\u6700\u591a\u3002 \u3000\u3000\u300a\u8881\u5d07\u7115\u8bc4\u4f20\u300b\u662f\u6211\u4e00\u4e2a\u65b0\u7684\u5c1d\u8bd5\uff0c\u76ee\u6807\u662f\u5728\u6b63\u6587\u4e2d\u4e0d\u76f4\u63a5\u5f15\u8ff0\u522b\u4eba\u7684\u8bdd\u800c\u5199\u5386\u53f2\u6587\u5b57\uff0c\u540c\u65f6\u81ea\u5df1\u5e76\u4e0d\u5b8c\u5168\u7ad9\u5728\u51b7\u773c\u65c1\u89c2\u7684\u5730\u4f4d\u3002\u8fd9\u7bc7\u201c\u8bc4\u4f20\u201d\u7684\u4e3b\u8981\u521b\u89c1\uff0c\u662f\u8ba4\u4e3a\u5d07\u796f\u6240\u4ee5\u6740\u8881\u5d07\u7115\uff0c\u6839\u672c\u539f\u56e0\u5e76\u4e0d\u662f\u7531\u4e8e\u4e2d\u4e86\u53cd\u95f4\u8ba1\uff0c\u800c\u662f\u5728\u4e8e\u8fd9\u4e24\u4e2a\u4eba\u6027\u683c\u7684\u51b2\u7a81\u3002\u8fd9\u4e00\u70b9\uff0c\u524d\u4eba\u4ece\u672a\u6307\u51fa\u8fc7\u3002 \u3000\u3000\u8fd9\u7bc7\u6587\u5b57\u5e76\u65e0\u591a\u5927\u5b66\u672f\u4e0a\u7684\u4ef7\u503c\uff0c\u6240\u53c2\u8003\u7684\u4e66\u7c4d\u90fd\u662f\u6211\u624b\u5934\u6240\u6709\u7684\uff0c\u6570\u91cf\u5341\u5206\u6709\u9650\u3002\u51fa\u81ea\u300a\u592a\u5b97\u5b9e\u5f55\u300b\u3001\u300a\u5d07\u796f\u957f\u7f16\u300b\u7b49\u4e66\u7684\u82e5\u5e72\u8d44\u6599\u90fd\u662f\u95f4\u63a5\u5f15\u8ff0\uff0c\u672a\u80fd\u6838\u5bf9\u539f\u6765\u7684\u51fa\u5904\uff0c\u6216\u8bb8\u4f1a\u6709\u8c2c\u8bef\u3002\u8fd9\u7bc7\u6587\u5b57\u5982\u679c\u6709\u751a\u4e48\u610f\u4e49\uff0c\u6050\u6015\u662f\u5728\u4e8e\u5b83\u7684\u201c\u53ef\u8bfb\u6027\u201d\u3002\u6211\u4ee5\u76f8\u5f53\u91cd\u5927\u7684\u52aa\u529b\uff0c\u907f\u514d\u4e86\u4e00\u822c\u5386\u53f2\u6587\u5b57\u4e2d\u7684\u8270\u6df1\u6666\u6da9\u3002\u73b0\u5728\u7684\u9762\u76ee\uff0c\u6bd4\u4e4b\u5728\u300a\u660e\u62a5\u300b\u4e0a\u6240\u53d1\u8868\u7684\u521d\u7a3f\u300a\u5e7f\u4e1c\u82f1\u96c4\u8881\u86ee\u5b50\u300b\uff0c\u6587\u5b57\u4e0a\u8981\u987a\u7545\u4e86\u4e9b\u3002 \u3000\u3000\u4e00\u4e5d\u4e03\u4e94\xb7\u516d"}),showOnHomepage:!0}]}}]);