"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[31791],{65639:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u5973\u5e1d\u5947\u82f1\u4f20",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u56de\u3000\u91cf\u624d\u7389\u5973\u60ca\u8eab\u4e16 \u3000\u3000\u201c\u5251\u9601\u5f00\u5929\u9669\uff0c\u2014\u2014\u770b\u5251\uff01\u201d \u3000\u3000\u201c\u524a\u58c1\u6309\u9752\u5929\uff0c\u2014\u2014\u5947\u54c9\uff01\u201d \u3000\u3000\u201c\u98de\u9e1f\u98de\u96be\u8fc7\uff0c\u7334\u4e86\u9501\u7709\u5c16\uff0c\u2014\u2014\u597d\u5440\uff0c\u597d\u6b65\u6cd5\uff01\u201d \u3000\u3000\u201c\u4f4e\u5934\u671b\u5c71\u8c37\uff0c\u767d\u4e91\u811a\u4e0b\u60ac\u3002\u2014\u2014\u6211\u7684\u597d\u5c0f\u59d0\uff0c\u4f60\u53ef\u522b\u770b\u5566\uff01\u201d \u3000\u3000\u201c\u563f\u3001\u563f\u3001\u563f\u3001\u54c8\u3001\u54c8\u3001\u54c8\uff01\u770b\u5251\uff0c\u770b\u5251\uff01\u63a5\u62db\uff0c\u63a5\u62db\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u662f\u4e00\u5bf9\u5144\u59b9\uff0c\u8986\u59d3\u201c\u957f\u5b59\u201d\uff0c\u54e5\u54e5\u53eb\u505a\u957f\u5b59\u6cf0\uff0c\u59b9\u59b9\u53eb\u505a\u957f\u5b59\u58c1\uff0c\u4ed6\u4eec\u6b63\u5728 \u6bd4\u5251\u3002 \u3000\u3000\u5982\u679c\u4f60\u5728\u8fd9\u513f\uff0c\u5982\u679c\u4f60\u770b\u5230\u4ed6\u4eec\u6bd4\u5251\uff0c\u5305\u7ba1\u4f60\u4f1a\u77a0\u76ee\u7ed3\u820c\uff0c\u8fde\u5927\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\uff01 \u3000\u3000\u4f60\u9053\u4ed6\u4eec\u5728\u4ec0\u4e48\u5730\u65b9\u6597\u5251\uff1f\u4ed6\u4eec\u662f\u5728\u8700\u4e2d\u4eba\u9669\u7684\u201c\u6d45\u9053\u201d\u4e4b\u4e0a\uff01 \u3000\u3000\u201c\u8700\u9053\u96be\uff0c\u96be\u4e8e\u4e0a\u9752\u5929\uff01\u201d\u800c\u5251\u9601\u4e0a\u7684\u201c\u6808\u9053\u201d\uff0c\u66f4\u662f\u6700\u9669\u7684\u6240\u5728\uff0c\u201c\u6808\u9053\u201d\u4e43\u662f \u5728\u60ac\u5d16\u524a\u58c1\u4e0a\u5f00\u5c71\u51ff\u77f3\uff0c\u5f00\u8f9f\u51fa\u7c73\u7684\u7f8a\u80a0\u5c0f\u5f84\uff0c\u6709\u4e9b\u5730\u65b9\u6839\u672c\u65e0\u8def\u53ef\u8d70\uff0c\u7adf\u5728\u524a\u58c1\u5343\u4ede \u5904\u51ff\u7a74\u67b6\u6728\uff0c\u5730\u4e0a\u67b6\u8d77\u51cc\u7a7a\u7684\u9053\u8def\uff1b\u6709\u4e9b\u5730\u65b9\u5219\u6cbf\u7740\u5c71\u58c1\uff0c\u51ff\u6210\u513f\u5343\u6b65\u7684\u68af\u7ea7\uff1b\u6614\u65f6\u695a \u6c49\u76f8\u4e89\uff0c\u5218\u90a6\u7528\u97e9\u4fe1\u4e4b\u8ba1\uff0c\u660e\u4fee\u6808\u9053\uff0c\u6697\u6e21\u9648\u4ed3\uff0c\u9a97\u8fc7\u4e86\u76d6\u4e16\u82f1\u96c4\u7684\u695a\u9738\u738b\uff0c\u4ed6\u7edd\u4e0d\u4fe1 \u6808\u9053\u80fd\u4fee\uff0c\u5374\u6599\u4e0d\u5230\u654c\u4eba\u5df2\u4ece\u9648\u4ed3\u6697\u6e21\uff0c\u7ec8\u4e8e\u5f04\u5230\u529b\u80fd\u62d4\u5c71\u4e3e\u9f0e\u7684\u695a\u9738\u738b\u81ea\u520e\u4e4c\u6c5f\u3002\u6808 \u9053\u4e4b\u9669\uff0c\u4e8e\u5179\u53ef\u89c1\u3002 \u3000\u3000\u8fd9\u65f6\u5144\u59b9\uff0c\u4e0d\u4f46\u5728\u6808\u9053\u4e0a\u6bd4\u5251\uff0c\u800c\u4e14\u4f60\u5531\u4e00\u53e5\uff0c\u6211\u548c\u4e00\u53e5\uff0c\u563b\u563b\u54c8\u54c8\u7684\u5f00\u73a9\u7b11\uff01\u4f46 \u89c1\u4ed6\u4eec\u76d8\u65cb\u8fdb\u9000\uff0c\u6377\u4f3c\u7075\u733f\uff0c\u5251\u6c14\u7eb5\u6a2a\uff0c\u8fc5\u5982\u63a3\u7535\uff0c\u8c01\u8981\u662f\u8e0f\u5dee\u534a\u6b65\uff0c\u5b9a\u4f1a\u7c89\u8eab\u788e\u9aa8\uff0c \u4ed6\u4eec\u5374\u6ee1\u4e0d\u5728\u4e4e\uff0c\u4ece\u5bb9\u6bd4\u5212\uff01 \u3000\u3000\u8fd9\u6837\u7684\u6bd4\u5251\uff0c\u5373\u7b97\u5728\u6b66\u6797\u9ad8\u624b\u4e4b\u4e2d\uff0c\u4e5f\u662f\u96be\u5f97\u4e00\u89c1\uff0c\u7136\u800c\u8fd9\u91cc\u5374\u6709\u4e00\u4e2a\u5c0f\u59d1\u5a18\uff0c\u5979 \u5750\u5728\u5c71\u77f3\uff0c\u6367\u7740\u4e00\u90e8\u8bd7\u96c6\uff0c\u8bfb\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u6b63\u773c\u513f\u4e5f\u4e0d\u5411\u6808\u9053\u90a3\u8fb9\u4e00\u77a7\u3002 \u3000\u3000\u8fd9\u5c0f\u59d1\u5a18\u7ea6\u83ab\u5341\u56db\u4e94\u5c81\u5e74\u7eaa\uff0c\u957f\u5f97\u7709\u6e05\u76ee\u79c0\uff0c\u5a07\u5c0f\u73b2\u73d1\uff0c\u5979\u5bf9\u5f53\u524d\u8fd9\u7b49\u5947\u5999\u7684\u5251\u672f\uff0c \u6beb\u4e0d\u52a8\u5fc3\uff0c\u53ea\u5728\u542c\u5230\u957f\u5b59\u5144\u59b9\u5531\u548c\u4e4b\u65f6\uff0c\u624d\u7a0d\u7a0d\u505c\u4e86\u4e00\u505c\uff0c\u5fc3\u4e2d\u6697\u60f3\uff1a\u201c\u6cf0\u54e5\u8bfb\u4e86\u8fd9\u4e48 \u591a\u5e74\u7684\u4e66\uff0c\u505a\u7c73\u505a\u53bb\uff0c\u5374\u8fd8\u662f\u53ea\u80fd\u505a\u6253\u6cb9\u8bd7\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u9996\u5373\u666f\u7684\u81fc\u63cf\u8bd7\uff0c\u8fd8\u7b97\u8131\u4fd7\u81ea\u7136\uff0c \u4e5f\u96be\u4e3a\u4e86\u4ed6\u4e86\u3002\u201d \u3000\u3000\u4e24\u5144\u59b9\u5728\u6808\u9053\u4e4b\u4e0a\uff0c\u77ac\u606f\u62c6\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u54e5\u54e5\u6e10\u6e10\u5360\u4e86\u4e0a\u98ce\uff0c\u5c06\u59b9\u59b9\u8feb\u5f97\u4e86\u5fd9\u811a\u4e71\uff0c \u957f\u5b59\u58c1\u53eb\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u600e\u4e48\u4e0d\u6765\u5e2e\u6211\uff1f\u201d\u957f\u5b59\u6cf0\u53eb\uff1a\u201c\u7559\u5fc3\uff0c\u8fd9\u4e00\u62db\u767d\u8679\u8d2f\u65e5\uff0c\u62c6\u5f97 \u4e0d\u597d\uff0c\u4e0d\u6b7b\u5fc5\u4f24\uff01\u201d\u957f\u5b59\u58c1\u7528\u4e86\u4e00\u62db\u201c\u56de\u98ce\u821e\u67f3\u201d\uff0c\u5a07\u8eaf\u8f7b\u6446\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u907f\u8fc7 \u4e86\u957f\u5b59\u6cf0\u8fd9\u5f53\u80f8\u4e00\u5251\uff0c\u5927\u547c\u5c0f\u53eb\u7740\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u518d\u4e0d\u6765\uff0c\u6211\u4eca\u65e5\u53ef\u8981\u8d25\u5728\u54e5\u54e5\u624b\u4e0b\u5566\uff01\u201d \u8fd9\u5c0f\u59d1\u5a18\u4ecd\u7136\u53ea\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff01 \u3000\u3000\u957f\u5b59\u58c1\u5374\u662f\u5fc3\u5ff5\u4e00\u52a8\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u7075\u7cbe\u7684\u4e2b\u5934\uff0c\u4e0d\u4e0a\u5f53\u5566\uff01\u201d\u5f80\u53e3\u5979\u7528\u8fd9\u4e2a\u6cd5 \u51e0\uff0c\u5a49\u513f\u5fc5\u5b9a\u524d\u6765\u76f8\u52a9\uff0c\u4eca\u756a\u624d\u4e00\u6b21\u4e0d\u7075\u3002\u201d \u3000\u3000\u8fd9\u5c0f\u59d1\u5a18\u8986\u59d3\u4e0a\u5b98\uff0c\u540d\u53eb\u5a49\u513f\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u6211\u6b63\u5728\u505a\u4eca\u65e5\u7684\u8bd7\u8bfe\uff0c\u6055\u6211 \u4e0d\u966a\u4f60\u4eec\u7ec3\u5251\u4e86\u3002\u201d\u539f\u6765\u5979\u5df2\u770b\u7834\u957f\u5b59\u58c1\u7684\u5fc3\u601d\uff0c\u90a3\u662f\u6545\u610f\u8bc8\u8d25\uff0c\u597d\u8bf1\u5979\u4e00\u540c\u7ec3\u5251\u7684\uff0c \u770b\u5979\u9002\u624d\u90a3\u4e00\u62db\u201c\u56de\u98ce\u821e\u67f3\u201d\u4e4b\u5999\uff0c\u5251\u672f\u5b9e\u4e0d\u5728\u5979\u54e5\u54e5\u4e4b\u4e0b\u3002 \u3000\u3000\u4e24\u5144\u59b9\u4e00\u7b11\u7f62\u624b\uff0c\u4ece\u67b6\u7a7a\u7684\u6808\u9053\u4e0a\u8df3\u4e0b\u6765\uff0c\u957f\u5b59\u58c1\u9053\uff1a\u201c\u4f60\u6574\u5927\u53ea\u662f\u6302\u7740\u4f5c\u8bd7\uff0c\u518d \u8fc7\u51e0\u5e74\uff0c\u53ea\u6015\u738b\u3001\u6768\u3001\u5362\u3001\u9a86\u8fd9\u56db\u4f4d\u5927\u8bd7\u5bb6\u89c1\u5230\u4f60\uff0c\u4e5f\u8981\u62f1\u624b\u81e3\u670d\u4e86\uff01\u201d\u738b\u662f\u738b\u52c3\uff0c\u6768 \u662f\u6768\u70af\uff0c\u5362\u662f\u5362\u7167\u9f84\uff0c\u9a86\u662f\u9a86\u5bbe\u738b\uff0c\u5e76\u79f0\u521d\u5510\u56db\u6770\uff0c\u8bd7\u540d\u7c4d\u751a\uff0c\u98ce\u9761\u4e00\u65f6\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5374\u4f3c\u610f\u6b8a\u4e0d\u5c51\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u56db\u6770\u4e4b\u4e2d\uff0c\u738b\u52c3\u5c0f\u6709\u624d\u534e\uff0c\u5176\u4ed6\u4e09\u4eba\u4e5f\u4e0d\u89c1 \u82e5\u4f55\u7279\u51fa\uff0c\u5c24\u5176\u90a3\u9a86\u5bbe\u738b\uff0c\u6700\u559c\u7528\u6570\u5b57\u5165\u8bd7\uff0c\u6545\u610f\u5356\u5f04\uff0c\u4ec0\u4e48\u2018\u79e6\u585e\u91cd\u5173\u4e00\u767e\u4e8c\uff0c\u6c49\u5bb6 \u79bb\u5bab\u4e09\u5341\u516d\u2019\uff0c\u4ec0\u4e48\u2018\u5c0f\u5802\u7eee\u638c\u4e09\u5343\u4e07\uff0c\u5927\u9053\u9752\u697c\u5341\u4e8c\u91cd\u2019\u3002\u7f57\u54e9\u7f57\u5506\uff0c\u6211\u6700\u4e0d\u559c\u6b22\u3002 \u4ed6\u7684\u6587\u7ae0\u6bd4\u4ed6\u7684\u8bd7\u597d\u5f97\u591a\u3002\u201d \u3000\u3000\u957f\u5b59\u58c1\u548b\u820c\u7b11\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff0c\u5f53\u4eca\u7687\u5e1d\u5728\u4f4d\uff0c\u542c\u8bf4\u8981\u5f00\u8bbe\u5973\u79d1\uff0c\u8fd9\u4e2a\u81ea\u53e4\u4ee5\u6765 \u7684\u7b2c\u4e00\u4e2a\u5973\u72b6\u5143\uff0c\u5fc5\u5c06\u975e\u4f60\u83ab\u5c5e\u4e86\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u610f\u6001\u4e4b\u95f4\uff0c\u66f4\u662f\u4e0d\u5c51\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u7b11\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u4f60\u8fd9\u8bdd\u8bf4\u9519\u4e86\u3002\u5a49\u513f\u53ef\u8981\u607c\u4f60\u77a7\u4e0d\u8d77\u5979\u5462\uff01\u201d\u957f\u5b59\u58c1\u6014\u4e86\u4e00 \u6014\uff0c\u968f\u5373\u610f\u4f1a\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u60f3\u8fd9\u666e\u706d\u4e4b\u4e0b\uff0c\u8c01\u914d\u6765\u8003\u6211\u4eec\u7684\u5a49\u513f\uff1f\u82e5\u662f\u5c06\u6765\u679c \u6709\u5973\u79d1\u7684\u62a1\u6750\u5927\u5178\uff0c\u5a49\u51e0\u8981\u505a\u5c31\u53ea\u80fd\u505a\u4e3b\u8003\uff0c\u53ef\u7edd\u4e2a\u80fd\u8d2c\u4f4e\u8eab\u4efd\u53bb\u8003\u72b6\u5143\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a \u201c\u542c\u8bf4\u4e0a\u5b98\u4f2f\u6bcd\u751f\u4f60\u7684\u65f6\u5019\uff0c\u89c1\u5929\u795e\u68a6\u9001\u4e00\u628a\u7389\u5c3a\uff0c\u4e00\u628a\u5927\u79e4\u6765\uff0c\u4f60\u5de6\u4e4e\u6267\u5c3a\uff0c\u53f3\u624b\u638c \u79e4\uff0c\u5929\u516c\u65e9\u5df2\u6ce8\u5b9a\u4e86\u4f60\u8981\u8861\u91cf\u5929\u4e0b\u7684\u624d\u4eba\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u607c\u9053\uff1a\u201c\u522b\u8ba2\u73a9\u7b11\u5566\uff0c\u6211\u5373\u7b97\u6709 \u5fc3\u53bb\u8861\u91cf\u5929\u4e0b\u4e4b\u58eb\uff0c\u4e5f\u4e0d\u5c51\u505a\u6b66\u5219\u5929\u7684\u4e3b\u8003\u5b98\uff01\u201d \u3000\u3000\u957f\u5b59\u6cf0\u773c\u73e0\u4e00\u8f6c\uff0c\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b66\u5219\u5929\u7b97\u5f97\u4ec0\u4e48\u771f\u547d\u706d\u5b50\uff0c\u5979\u53ea\u662f\u7be1\u593a\u5927\u5510 \u7687\u4f4d\u7684\u5973\u9b54\u738b\uff01\u597d\uff0c\u54b1\u4eec\u4e0d\u63d0\u5979\u5566\u3002\u5a49\u51e0\uff0c\u4f60\u521a\u624d\u505a\u7684\u8bd7\u5ff5\u7ed9\u6211\u542c\u542c\uff0c\u597d\u4e48\uff1f\u201d\u4e0a\u5b98\u5a49 \u513f\u629b\u5f00\u8bd7\u5377\uff0c\u7fd8\u9996\u957f\u7a7a\uff0c\u7f13\u7f13\u5ff5\u9053\uff1a \u3000\u3000\u53f6\u4e0b\u6d1e\u5ead\u521d\uff0c\u601d\u541b\u4e07\u91cc\u4f59\u3002 \u3000\u3000\u9732\u6d53\u9999\u88ab\u51b7\uff0c\u6708\u843d\u9526\u5c4f\u865a\u3002 \u3000\u3000\u6b32\u594f\u6c5f\u5357\u8c03\uff0c\u8d2b\u5c01\u84df\u5317\u8bd7\u3002 \u3000\u3000\u4e66\u4e2d\u65e0\u522b\u610f\uff0c\u4f46\u6005\u4e45\u79bb\u5c45\u3002 \u3000\u3000\u8bd7\u4e2d\u4e00\u7247\u4f18\u90c1\u7684\u60c5\u6000\uff0c\u597d\u4f3c\u5728\u6000\u5ff5\u8fdc\u4eba\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002\u957f\u5b59\u6cf0\u5446\u5446\u53d1\u6115\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u5979\u6765\u5230\u6211\u5bb6\u4e4b\u65f6\uff0c\u53ea\u6709\u4e03\u5c81\uff0c\u4e03\u5c81\u7684\u5b69\u5b50\u80fd\u61c2\u5f97\u4ec0\u4e48\uff1f\u5373\u7b97\u5341\u56db\u5c81\u7684\u59d1\u5a18\uff0c\u4e5f\u4e0d\u5e94\u6709 \u8fd9\u79cd\u5fc3\u4e8b\u3002\u201d\u77a7\u77a7\u4e0a\u5b98\u5a49\u513f\u7684\u8138\u8272\uff0c\u89c9\u5f97\u5947\u602a\u6781\u4e86\uff01 \u3000\u3000\u957f\u5b59\u58c1\u8d5e\u9053\uff1a\u201c\u8bf7\u8bcd\u4e3d\u53e5\uff0c\u98d8\u9038\u7edd\u4fd7\u3002\u597d\u8bd7\uff0c\u597d\u8bd7\uff01\u53ea\u662f\u611a\u59d0\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u5012\u8981\u8bf7 \u6559\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u59d0\u59d0\u8bf7\u8bf4\u3002\u201d\u957f\u5b59\u58c1\u7b11\u9053\uff1a\u201c\u53f6\u4e0b\u6d1e\u5ead\u521d\uff0c\u601d\u541b\u4e07\u91cc\u4f59\uff0c\u4e0d\u77e5\u8d24 \u6627\u6240\u601d\u7684\uff0c\u662f\u6d1e\u5ead\u6e56\u6ee8\u54ea\u4e00\u4f4d\u6709\u798f\u6c14\u7684\u513f\u90ce\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7b11\u5f2f\u4e86\u8170\uff0c\u626d\u9996\u957f\u5b59\u58c1\u9053\uff1a\u201c\u59d0\u59d0\u4f60\u600e\u4e48\u8fd9\u7b49\u6cb9\u5634\u6ed1\u820c\uff0c\u65e0\u7406\u53d6\u95f9\uff1f\u6211\u662f \u501f\u6e58\u541b\u3001\u6e58\u592b\u4eba\u7684\u5178\u6545\uff0c\u5728\u6000\u5ff5\u5927\u821c\u7687\u5e1d\u5440\uff01\u201d\u821c\u5e1d\u5357\u5de1\uff0c\u6b7b\u4e8e\u82cd\u609f\u4e4b\u91ce\uff0c\uff08\u82cd\u68a7\u4e0d\u662f \u5e7f\u897f\u7684\u90a3\u4e2a\u82cd\u68a7\u53bf\uff0c\u800c\u662f\u5c71\u540d\uff0c\u5728\u4eca\u6e56\u5357\u7701\u5b81\u8fdc\u53bf\u4e1c\u5357\uff0c\u53c8\u540d\u4e5d\u7591\u5c71\uff09\u3002\u4ed6\u7684\u540e\u5983\u6e58\u541b\u3001 \u6e58\u592b\u4eba\u54ed\u4ed6\uff0c\u8840\u6cea\u67d3\u6210\u4e86\u6591\u7af9\uff0c\u79f0\u4e3a\u6e58\u5983\u7af9\u3002\u4e0a\u5b98\u5a49\u513f\u8fd9\u4e24\u53e5\u8bd7\uff0c\u60dc\u7528\u8fd9\u4e2a\u5178\u6545\u6765\u6000\u5ff5 \u5148\u5e1d\uff0c\u4ee5\u8868\u6545\u56fd\u4e4b\u601d\uff0c\u672c\u6765\u4e5f\u8bb2\u5f97\u901a\uff0c\u4f46\u957f\u5b59\u6cf0\u5374\u603b\u662f\u7591\u5fc3\u4e0d\u91ca\uff0c\u5fc3\u4e2d\u5ba3\u5728\u7422\u78e8\uff1a\u201c\u5a49 \u513f\uff0c\u5979\uff0c\u5979\u5728\u601d\u5ff5\u8c01\u5462\uff1f\u201d \u3000\u3000\u957f\u5b59\u58c1\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u89e3\u6cd5\uff0c\u5b9e\u5728\u51fa\u4e4e\u6211\u7684\u610f\u5916\uff0c\u5440\uff0c\u4f60\u7684\u8bd7\u592a\u542b\u84c4\u4e86\uff0c\u7b80\u76f4\u6bd4\u7239\u7239 \u6240\u6559\u7684\u5251\u6cd5\u8fd8\u8981\u96be\u61c2\uff0c\u6211\u81ea\u8ba4\u7b28\u4eba\uff0c\u4e0d\u6562\u548c\u4f60\u518d\u8c08\u8bd7\u4e86\uff0c\u6765\uff0c\u6765\uff0c\u6765\uff01\u4f60\u4eca\u65e5\u8fd8\u6ca1\u6709\u548c \u6211\u7ec3\u5251\u5462\uff01\u201d \u3000\u3000\u957f\u5b59\u6cf0\u4e3a\u5a49\u513f\u8fd9\u9996\u8bd7\u611f\u5230\u5947\u602a\uff0c\u4e0a\u5b98\u5a49\u513f\u5374\u4e3a\u957f\u5b59\u5144\u59b9\u5b9a\u8981\u8feb\u5979\u7ec3\u5251\u800c\u611f\u5230\u5947\u602a\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u6027\u559c\u6587\u5b66\uff0c\u4e0d\u8fd1\u6b66\u529f\uff0c\u4ed6\u4eec\u4e0d\u662f\u4e0d\u77e5\uff0c\u5374\u4e3a\u4ec0\u4e48\u8001\u662f\u7f20\u6211\u7ec3\u6b66\uff1f\u201d\u7591\u5fc3 \u4e00\u8d77\uff0c\u4e03\u5e74\u6765\u538b\u5728\u5fc3\u5934\u4e0a\u7684\u7591\u4e91\uff0c\u8d8a\u6765\u8d8a\u91cd\u4e86\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7684\u7956\u7236\u548c\u7236\u4eb2\u90fd\u662f\u5510\u671d\u7684\u5927\u5b98\uff0c\u5728\u5979\u4e03\u5c81\u90a3\u5e74\uff0c\u6709\u4e00\u5929\u5979\u5bb6\u7684\u8001\u4ec6\u4eba\u738b\u5b89 \u548c\u5979\u7684\u4e73\u6bcd\u7a81\u7136\u5e26\u5979\u79bb\u5f00\u4eac\u90fd\uff0c\u9001\u5979\u5230\u957f\u5b59\u4f2f\u4f2f\u5bb6\u91cc\u3002\u5230\u4e86\u957f\u5b59\u5bb6\u4e2d\uff0c\u624d\u544a\u8bc9\u5979\uff0c\u5979\u7684 \u7956\u7236\u548c\u7236\u6bcd\u5df1\u6b7b\u4e86\uff0c\u8981\u5979\u4ece\u4eca\u4ee5\u540e\uff0c\u597d\u6253\u542c\u957f\u5b59\u4f2f\u4f2f\u7684\u6559\u8bf2\u3002\u5979\u7684\u7956\u7236\u4e0a\u5b98\u4eea\u662f\u592a\u5b50\u592a \u535a\uff0c\u7236\u4eb2\u4e0a\u5b98\u5ead\u829d\u4e5f\u662f\u5bab\u5ef7\u4e2d\u7684\u6587\u5b66\u4f8d\u4ece\uff0c\u7ecf\u5e38\u5728\u5bab\u4e2d\u4f4f\u5bbf\uff0c\u4e0d\u5927\u56de\u5bb6\u3002\u4ed6\u4eec\u662f\u5982\u4f55\u6b7b \u7684\uff0c\u4e0a\u5b98\u5a49\u513f\u81ea\u662f\u4e0d\u77e5\uff0c\u4f46\u5979\u5374\u6d88\u6e05\u695a\u695a\u7684\u8bb0\uff0c\u5c31\u5728\u5979\u79bb\u5bb6\u7684\u90a3\u4e00\u5929\u65e9\u4e0a\uff0c\u5979\u7684\u6bcd\u4eb2\u8fd8 \u662f\u597d\u597d\u7684\uff0c\u6b63\u8981\u8fdb\u5bab\u53bb\u63a2\u671b\u5979\u7684\u7236\u4eb2\uff0c\u4e3a\u4ec0\u4e48\u738b\u5b89\u4e0d\u7b49\u6bcd\u4eb2\u56de\u6765\u5c31\u62b1\u5979\u8d70\u4e86\uff0c\u5979\u6bcd\u4eb2\u53c8 \u600e\u7684\u4f1a\u7a81\u7136\u6b7b\u4e86\uff1f\u738b\u5b89\u544a\u8bc9\u5979\u8bf4\uff0c\u90a3\u662f\u56e0\u4e3a\u5bab\u4e2d\u53d1\u751f\u4e86\u5389\u75ab\uff0c\u5979\u7684\u7956\u7236\u3001\u7236\u4eb2\u66b4\u75c5\u800c\u4ea1\uff0c \u5979\u7684\u6bcd\u4eb2\u5165\u5bab\u63a2\u75c5\uff0c\u67d3\u4e0a\u5389\u75ab\uff0c\u4ea6\u544a\u4e0d\u6cbb\u3002\u4ed6\u8981\u5979\u8d76\u5feb\u79bb\u5f00\u4eac\u90fd\uff0c\u5c31\u662f\u8981\u907f\u5f00\u90a3\u4e00\u573a\u53ef \u6015\u7684\u5389\u75ab\u3002\u738b\u5b89\u662f\u4ed6\u5bb6\u51e0\u5341\u5e74\u7684\u8001\u4ec6\u4eba\uff0c\u5fe0\u5fc3\u803f\u803f\uff0c\u4e0a\u5b98\u5a49\u513f\u90a3\u65f6\u5e74\u5e7c\uff0c\u81ea\u7136\u4e0d\u4f1a\u6000\u7591 \u738b\u5b89\u8bf4\u8c0e\u3002\u53ef\u662f\u5e74\u7eaa\u6e10\u957f\u4e4b\u540e\uff0c\u7591\u5fc3\u4e5f\u5c31\u6e10\u6e10\u589e\u957f\uff0c\u5979\u8bb0\u8d77\u4e86\u51fa\u8d70\u4e4b\u65f6\uff0c\u738b\u5b89\u548c\u4e73\u6bcd\u7684 \u795e\u8272\u90fd\u663e\u5f97\u614c\u5fd9\u548c\u7d27\u5f20\uff0c\u51e0\u4e4e\u4ec0\u4e48\u4e1c\u897f\u90fd\u6ca1\u6709\u6536\u62fe\uff0c\u5373\u7b97\u9003\u907f\u5386\u75ab\uff0c\u4e5f\u4e0d\u8be5\u5982\u6b64\uff01\u8fd8\u6709\uff0c \u957f\u5b59\u4f2f\u4f2f\u662f\u5979\u7236\u4eb2\u6700\u8981\u597d\u7684\u670b\u53cb\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u4e03\u5e74\u6765\u603b\u4e0d\u80af\u5e26\u5979\u56de\u4e61\u53bb\u796d\u626b\u5979\u7236\u6bcd\u7684\u575f\u5893\u3002 \u53ef\u60dc\u5979\u61c2\u5f97\u8fd9\u6837\u7591\u5fc3\u4e4b\u65f6\uff0c\u738b\u5b89\u548c\u4e73\u6bcd\u4e5f\u65e9\u5df2\u6b7b\u4e86\u3002\u8fd9\u4e9b\u7591\u56e2\u5c31\u4e00\u76f4\u7559\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u66f4\u5927\u7684\u7591\u56e2\u2014\u2014 \u3000\u3000\u5979\u7684\u957f\u5b59\u4f2f\u4f2f\u53cc\u540d\u5747\u91cf\uff0c\u6587\u6b66\u5168\u624d\uff0c\u592a\u5b97\u674e\u4e16\u6c11\u5728\u4f4d\u4e4b\u65f6\uff0c\u4ed6\u66fe\u505a\u5230\u6bbf\u524d\u68c0\u70b9\u4e4b\u804c\u3002 \u5176\u540e\u9ad8\u5b97\u7ee7\u4f4d\uff0c\u6b66\u540e\u638c\u6743\uff0c\u4ed6\u5373\u6302\u51a0\u6c42\u9000\uff0c\u5728\u5251\u9601\u4e4b\u4e0a\u7ed3\u5e90\u9690\u5c45\u3002\u4e0a\u5b98\u5a49\u513f\u4e03\u5c81\u6765\u5230\u4ed6 \u5bb6\uff0c\u5982\u4eca\u5341\u56db\u5c81\u4e86\u3002\u8fd9\u4e03\u5e74\u4e2d\uff0c\u957f\u5b59\u5747\u91cf\u5bf9\u5979\u771f\u662f\u7231\u62a4\u5907\u81f3\uff0c\u89c6\u540c\u5df2\u51fa\uff0c\u53eb\u5979\u548c\u81ea\u5df1\u7684 \u513f\u5973\u4e00\u9053\uff0c\u65e5\u95f4\u4e60\u6b66\uff0c\u591c\u95f4\u4e60\u6587\uff0c\u7279\u522b\u662f\u6559\u5979\u6b66\u827a\u4e4b\u65f6\uff0c\u7b80\u76f4\u6bd4\u6559\u513f\u5973\u8fd8\u8981\u7528\u5fc3\u3002 \u3000\u3000\u53ef\u60dc\u4e0a\u5b98\u5a49\u513f\u6027\u559c\u6587\u5b66\uff0c\u4e0d\u8fd1\u6b66\u529f\uff0c\u5e38\u4ee4\u957f\u5b59\u5747\u91cf\u5931\u671b\u3002\u4e0a\u5b98\u5a49\u513f\u8fd8\u8bb0\u5f97\u6709\u4e00\u4e2a\u665a \u4e0a\uff0c\u5979\u5199\u4e86\u4e09\u9996\u65b0\u8bd7\uff0c\u7ed9\u4f2f\u4f2f\u8bc4\u9605\uff0c\u957f\u5b59\u5747\u91cf\u62cd\u6848\u53eb\u7edd\uff0c\u5374\u5ffd\u800c\u957f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u82e5\u4e13 \u5fc3\u6587\u5b66\uff0c\u5b9a\u53ef\u6210\u4e3a\u5929\u4e0b\u7b2c\u4e00\u624d\u5973\uff0c\u5509\uff0c\u6211\u5374\u4f46\u613f\u4f60\u4e0d\u8981\u8fd9\u6837\u806a\u660e\u624d\u597d\uff0c\u4f60\u505a\u51fa\u8fd9\u6837\u7684\u597d \u8bd7\uff0c\u53eb\u6211\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u4f24\u5fc3\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u751a\u662f\u4e0d\u89e3\uff0c\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u6cf0\u54e5\u58c1\u59d0\u4f20\u4f60\u7684\u6b66 \u529f\uff0c\u6211\u4f20\u4f60\u7684\u6587\u5b66\uff0c\u4f60\u8001\u4eba\u5bb6\u5728\u4e49\u6b66\u4e24\u65b9\u9762\u90fd\u6709\u4f20\u4eba\uff0c\u5c82\u4e0d\u4e5f\u597d\uff1f\u201d\u957f\u5b59\u5747\u91cf\u9ed8\u7136\u534a\u664c\uff0c \u559f\u7136\u53f9\u9053\uff1a\u201c\u4f60\u7684\u624d\u534e\u5b66\u95ee\u73b0\u5728\u5df2\u8fdc\u80dc\u4e8e\u6211\uff0c\u5c82\u6b62\u53ea\u662f\u6211\u7684\u4f20\u4eba\uff1f\u53ef\u60dc\u8bd7\u53e5\u867d\u5de5\uff0c\u5bf9\u4f60 \u7a76\u65e0\u5927\u7528\uff0c\u5251\u672f\u96be\u4ee5\u901f\u6210\uff0c\u660e\u65e5\u8d77\u4f60\u517c\u7ec3\u6697\u5668\u5427\u3002\u201d\u8bf4\u6765\u8bf4\u53bb\uff0c\u8fd8\u662f\u8981\u5979\u7528\u5fc3\u7ec3\u6b66\uff0c\u800c \u4e14\u4e34\u8d70\u4e4b\u65f6\uff0c\u4e0a\u5b98\u5a49\u513f\u8fd8\u9690\u7ea6\u770b\u5230\u5979\u7684\u4f2f\u4f2f\u773c\u4e2d\u8574\u6cea\uff0c\u5982\u6709\u91cd\u5fe7\u3002 \u3000\u3000\u51e0\u5e74\u6765\u4e0a\u5b98\u5a49\u513f\u767e\u601d\u83ab\u89e3\uff0c\u957f\u5b59\u4f2f\u4f2f\u8981\u5979\u6587\u6b66\u53cc\u4fee\uff0c\u90a3\u81ea\u662f\u4e00\u756a\u597d\u610f\uff0c\u7136\u800c\u5374\u4e5f\u4e0d \u5fc5\u90a3\u6837\u4f24\u5fc3\uff01\u201c\u6211\u4e00\u4e2a\u5973\u5b69\u513f\u5bb6\uff0c\u8981\u8fd9\u6837\u597d\u7684\u6b66\u529f\u505a\u751a\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u60f3\u662f\u8fd9\u6837\u7684\u60f3\uff0c\u4e3a \u4e86\u987a\u4ece\u4f2f\u4f2f\u7684\u610f\u601d\uff0c\u5979\u8fd8\u662f\u6bcf\u5927\u8ddf\u957f\u5b59\u5144\u59b9\u7ec3\u6b66\u3002\u4e2a\u8fc7\u5374\u5e38\u5e38\u5728\u7ec3\u6b66\u7684\u65f6\u95f4\uff0c\u6084\u6084\u8eb2\u5728 \u4e00\u65c1\uff0c\u8bfb\u5979\u5fc3\u7231\u7684\u8bd7\u7bc7\u3002\u957f\u5b59\u5144\u59b9\u62ff\u5979\u6ca1\u6cd5\uff0c\u53ea\u597d\u60f3\u5c3d\u6cd5\u513f\uff0c\u8bf1\u5979\u7ec3\u6b66\u3002 \u3000\u3000\u5982\u4eca\u957f\u5b59\u58c1\u4e49\u78e8\u7740\u5979\u7ec3\u5251\u4e86\uff0c\u800c\u4e14\u8fd9\u51e0\u5929\u6765\u90fd\u8981\u5979\u7ec3\u4e00\u51fa\u624b\u5c31\u4ee4\u654c\u4eba\u4f24\u6b8b\u7684\u5251\u6cd5\uff0c \u4e0a\u5b98\u5a49\u513f\u6447\u5934\u7b11\u9053\uff1a\u201c\u6211\u4f46\u6c42\u4e60\u6b66\u5f3a\u8eab\uff0c\u4e0d\u60f3\u5b66\u8fd9\u6837\u9738\u9053\u6740\u4eba\u7684\u672c\u9886\u3002\u201d\u957f\u5b59\u58c1\u8f7b\u629a\u5979 \u7684\u5934\u53d1\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u4eca\u662f\u7239\u7239\u4e00\u5e74\u4e00\u5ea6\u5bf9\u6211\u4eec\u7684\u8003\u8f83\u4e4b\u671f\u4e48\uff1f\u6765\uff0c\u6765\uff0c\u6765\uff01 \u3000\u3000\u4f60\u6700\u5c11\u4e5f\u5f97\u5b66\u4f1a\u523a\u7a74\u7684\u8fde\u73af\u4e09\u5251\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u8fd9\u624d\u84e6\u5730\u60f3\u8d77\uff0c\u4eca\u65e5\u4e0d\u4f46\u662f\u957f\u5b59\u4f2f\u4f2f \u8003\u8f83\u4e4b\u671f\uff0c\u800c\u4e14\u662f\u5979\u7236\u6bcd\u7684\u5fcc\u8fb0\uff0c\u957f\u5b59\u4f2f\u4f2f\u6311\u9009\u8fd9\u4e2a\u53e3\u5b50\u4f5c\u4e3a\u4e00\u5e74\u4e00\u5ea6\u7684\u8003\u671f\uff0c\u4e0d\u77e5\u5176 \u4e2d\u53ef\u6709\u6df1\u610f\uff1f \u3000\u3000\u5929\u4e0a\u7a81\u7136\u98de\u6765\u4e24\u53ea\u5140\u9e70\uff0c\u53cc\u7fc5\u5c55\u5f00\uff0c\u51e0\u8fbe\u4e00\u4e08\uff0c\u4e0a\u5b98\u5a49\u513f\u4e00\u770b\uff0c\u539f\u6765\u8fd9\u4e24\u53ea\u5140\u9e70\u6b63 \u5728\u8ffd\u9010\u5c71\u4e2d\u91ce\u5154\uff0c\u4e0a\u5b98\u5a49\u513f\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u5c31\u7ec3\u4e00\u624b\u6697\u5668\u7684\u529f\u592b\uff0c\u4e5f\u597d\u6551\u8fd9\u53ea\u5c0f\u767d\u5154 \u7684\u6027\u9762\u3002\u201d\u4e4e\u8155\u4e00\u62ac\uff0c\u4e00\u67c4\u5315\u9996\u4f3c\u7535\u822c\u7684\u5c04\u51fa\uff0c\u957f\u5b59\u6cf0\u53eb\u9053\uff1a\u201c\u53d6\u5b83\u5de6\u76ee\u201d\u82cd\u9e70\u5e94\u58f0\u800c \u843d\u3002\u957f\u5b59\u58c1\u8dd1\u53bb\u4e00\u770b\uff0c\u4f46\u89c1\u90a3\u67c4\u5c0f\u5315\u9996\u679c\u7136\u6d1e\u7a7f\u4e86\u82cd\u9e70\u7684\u5de6\u76ee\uff0c\u6df1\u6df1\u523a\u5165\u4e86\u5b83\u7684\u5934\u9aa8\uff0c \u5c06\u5b83\u9489\u5728\u5730\u4e0a\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u62cd\u624b\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u767e\u6b65\u7a7f\u6768\u7684\u795e\u6280\u3002\u518d\u53d6\u8fd9\u53ea\u82cd\u9e70\u7684\u53f3\u76ee\uff01\u201d\u8fd9\u53ea\u82cd\u9e70\u751a \u6709\u7075\u6027\uff0c\u4f3c\u662f\u77e5\u9053\u906d\u9022\u5f3a\u654c\uff0c\u8d34\u5730\u4f4e\u98de\uff0c\u501f\u90a3\u524a\u58c1\u5cfb\u5d16\uff0c\u63a9\u62a4\u81ea\u5df1\uff0c\u731b\u7136\u95f4\u4e00\u4f38\u9e70\u722a\uff0c \u6293\u8d77\u4e00\u53ea\u5c0f\u5154\uff0c\u53cc\u7fc5\u4e00\u817e\uff0c\u5c31\u60f3\u98de\u4e0b\u5c71\u8c37\u3002\u4e0a\u5b98\u5a49\u513f\u89c1\u5b83\u5982\u6b64\u51f6\u6b8b\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5315\u9996\u75be \u98de\u800c\u51fa\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u9ed1\u5f71\u7a81\u7136\u4ece\u5ca9\u77f3\u540e\u8df3\u4e86\u51fa\u6765\uff0c\u628a\u4e0a\u5b98\u5a49\u513f\u7684\u5315\u9996\u63a5\u5230\u624b\u4e2d\uff0c \u5239\u90a3\u95f4\uff0c\u9e70\u6c89\u8c37\u5e95\uff0c\u4eba\u5230\u8ddf\u524d\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u51e0\u62ac\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u9762\u524d\u7ad9\u7740\u7684\u662f\u4e00\u4e2a\u866c\u9aef\u5927\u6c49\uff0c\u4ed6\u63a5\u5315\u9996\u7684\u672c\u9886\u5df2\u662f\u4ee4\u4eba\u5403 \u60ca\uff0c\u800c\u66f4\u4ee4\u4eba\u9707\u9a87\u7684\u662f\uff0c\u4ed6\u8fd8\u80cc\u7740\u4e00\u4e2a\u534e\u670d\u8001\u8005\uff0c\u5c45\u7136\u80fd\u5728\u6808\u9053\u4e0a\u8df3\u8dc3\u5982\u98de\uff0c\u8fd8\u63a5\u4e86\u5979 \u7684\u5315\u9996\uff01 \u3000\u3000\u90a3\u6c49\u5b50\u53cc\u76ee\u4e00\u5f20\uff0c\u6717\u58f0\u95ee\u9053\uff1a\u201c\u957f\u5b59\u5747\u91cf\u53ef\u662f\u4f4f\u5728\u8fd9\u513f\uff1f\u201d\u957f\u5b59\u6cf0\u5ffd\u5730\u8fc8\u524d\u4e00\u6b65\uff0c \u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u80cc\u7684\u53ef\u662f\u90d1\u6e29\u4f2f\u4f2f\uff1f\u201d\u90d1\u6e29\u662f\u671d\u4e2d\u7684\u5fa1\u53f2\u5927\u592b\uff0c\u4e0e\u4e0a\u5b98\u5a49\u51e0\u7684\u7956\u7236\u540c\u662f \u4e00\u6bbf\u4e4b\u81e3\u3002\u4e0a\u5b98\u5a49\u513f\u7741\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u4ed6\u80cc\u4e0a\u7684\u90a3\u4e2a\u8001\u4eba\u7d27\u95ed\u53cc\u76ee\uff0c\u9762\u5982\u91d1\u7eb8\uff0c\u770b\u4ed6\u76f8\u8c8c\uff0c \u4f9d\u7a00\u8bb0\u5f97\u6b63\u662f\u5979\u5e7c\u5e74\u4e4b\u65f6\uff0c\u90a3\u4e2a\u5e38\u6765\u5979\u5bb6\uff0c\u4e0e\u5979\u7956\u7236\u8c08\u8bd7\u8bba\u6587\u7684\u90a3\u4e2a\u90d1\u6e29\uff01 \u3000\u3000\u957f\u5b59\u6cf0\u8bdd\u58f0\u6765\u4e86\uff0c\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u662f\u90d1\u5927\u54e5\u6765\u4e86\u4e48\uff1f\u201d\u4eba\u5f71\u672a\u89c1\uff0c \u58f0\u97f3\u5374\u5982\u5728\u8033\u8fb9\uff0c\u90a3\u866c\u9aef\u5927\u6c49\u6025\u5fd9\u653e\u4e0b\u8001\u4eba\uff0c\u76f4\u633a\u633a\u7684\u8dea\u5728\u5730\u4e0a\uff0c\u81ea\u62a5\u59d3\u540d\uff1a\u201c\u901a\u5dde\u674e \u5143\u4e13\u8bda\u62dc\u8c12\uff0c\u6073\u6c42\u957f\u5b59\u5927\u4eba\u6551\u90d1\u5927\u4eba\u4e00\u547d\u3002\u201d\u674e\u5143\u867d\u7136\u672a\u89c1\u8fc7\u957f\u5b59\u5747\u91cf\uff0c\u4f46\u542c\u5f97\u8fd9\u79cd\u4f20 \u97f3\u5165\u5bc6\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u5df2\u77e5\u9053\u5fc5\u662f\u957f\u5b59\u5747\u91cf\u65e0\u7591\u3002 \u3000\u3000\u8bdd\u8bed\u65b9\u505c\uff0c\u4eba\u5f71\u5df2\u5230\u3002\u6765\u7684\u679c\u7136\u662f\u957f\u5b59\u5747\u91cf\uff0c\u4ed6\u5df2\u516d\u5341\u6709\u591a\uff0c\u53cc\u9b13\u5c3d\u767d\uff0c\u4ecd\u662f\u5065\u70c1 \u975e\u5e38\uff0c\u53cc\u773c\u795e\u5149\u70af\u70af\uff0c\u6253\u91cf\u4e86\u674e\u5143\u4e00\u773c\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u674e\u5144\u5feb\u5feb\u8d77\u6765\uff0c\u90d1\u5927\u4eba\u4e0e\u6211\u6570\u5341 \u8f7d\u77e5\u4ea4\uff0c\u6211\u7109\u80fd\u4e0d\u6551\uff1f\u5f85\u6211\u770b\u770b\u53d7\u7684\u662f\u4ec0\u4e48\u4f24\uff1f\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u53ea\u89c1\u957f\u5b59\u5747\u91cf\u9762\u8272\u5927\u53d8\uff0c\u4f38\u624b\u4e00\u6293\uff0c\u6293\u7740\u4e86\u674e\u5143\u7684\u80f8\u812f\uff0c\u53cc\u6307\u4e00\u5212\uff0c\u58f0\u5982 \u88c2\u5e1b\uff0c\u767b\u65f6\u628a\u674e\u5143\u7684\u80f8\u8863\u6495\u5f00\uff0c\u8fd9\u4e2a\u7a81\u5982\u5176\u6765\u7684\u4e3e\u52a8\uff0c\u4e0d\u4f46\u5927\u51fa\u957f\u5b59\u5144\u59b9\u610f\u5916\uff0c\u674e\u5143\u66f4 \u662f\u5403\u60ca\u975e\u5c0f\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u6211\u662f\u4fdd\u62a4\u90d1\u5927\u4eba\u5165\u8700\u7684\u9556\u5e08\uff0c\u8001\u5148\u751f\u4f11\u8981\u8bef\u4f1a\uff01\u201d \u3000\u3000\u957f\u5b59\u5747\u91cf\u5782\u624b\u957f\u53f9\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5bf9\u4f60\u7591\u5fc3\uff0c\u6211\u662f\u5bf9\u90a3\u4e24\u4e2a\u9b54\u5934\u7591\u5fc3\uff0c\u90d1\u5927\u4eba\u5728 \u671d\u4e3a\u5b98\uff0c\u7edd\u4e0d\u5475\u80fd\u4e0e\u4ed6\u4eec\u7ed3\u6709\u51a4\u4ec7\uff0c\u4ed6\u95e8\u4e3a\u4ec0\u4e48\u8fd9\u7b49\u72e0\u5fc3\u8fa3\u624b\uff01\u201d\u628a\u90d1\u6e29\u7684\u5934\u53d1\u62e8\u5f00\uff0c \u53ea\u89c1\u5de6\u53f3\u4e24\u8fb9\u7684\u592a\u9633\u7a74\u4e0a\uff0c\u90fd\u6709\u4e00\u4e2a\u9488\u5b54\u822c\u5927\u5c0f\u7684\u4f24\u53e3\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u957f\u5b59\u5747\u91cf\u53c8\u9053\uff1a\u201c\u4f60\u518d\u770b\u770b\u4f60\u7684\u80f8\u819b\uff01\u201d\u674e\u5143\u4fef\u5b98\u4e00\u77a7\uff0c\u4f46\u89c1\u4e24\u65c1\u4e73\u707e\u7a74\u4e4b\u4e0b\uff0c\u90fd \u6709\u4e00\u4e2a\u91d1\u94b1\u822c\u5927\u5c0f\u7684\u7ea2\u5370\u3002\u767b\u65f6\u9762\u5982\u6b7b\u7070\uff0c\u8e72\u5728\u5730\u4e0a\u3002 \u3000\u3000\u957f\u5b59\u5144\u59b9\u548c\u4e0a\u5b98\u5a49\u513f\u4e0d\u80dc\u9a87\u5f02\uff0c\u56f4\u535c\u6765\u770b\uff0c\u53ea\u542c\u5f97\u674e\u5143\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u6211\u4eec\u4e2d\u7684\uff0c\u662f \u4e0d\u662f\u6bd2\u89c2\u97f3\u548c\u6076\u884c\u8005\u7684\u6697\u5668\uff1a\u900f\u7a74\u795e\u9488\u548c\u788e\u9aa8\u94b1\u9556\uff1f\u201d\u957f\u5b59\u5747\u91cf\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u65af\uff0c \u8001\u592b\u53ea\u597d\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u90d1\u5927\u4eba\u4e2d\u7684\u662f\u900f\u7a74\u795e\u9488\uff0c\u4f60\u4e2d\u7684\u662f\u788e\u9aa8\u94b1\u8198\u3002\u662f\u5426\u80fd\u591f\u89e3\u6551\uff0c\u8001\u592b \u6b8a\u65e0\u628a\u63e1\uff0c\u53ea\u6709\u5c3d\u529b\u800c\u4e3a\u3002\u201d \u3000\u3000\u674e\u5143\u5ffd\u5730\u4e00\u58f0\u60e8\u7b11\uff0c\u8dc3\u8d77\u8bf4\u9053\uff1a\u201c\u89c2\u97f3\u52fe\u9b42\uff0c\u884c\u8005\u593a\u547d\uff0c\u4e2d\u4e86\u8fd9\u4e24\u4e2a\u9b54\u5934\u7684\u6697\u5668\uff0c \u6211\u4ea6\u81ea\u77e5\u5140\u836f\u53ef\u533b\u3002\u8001\u5148\u751f\u4e0d\u5fc5\u5bbd\u6170\u6211\u4e86\u3002\u53ea\u662f\u6211\u4fdd\u62a4\u90d1\u5927\u4eba\u5165\u8700\uff0c\u672a\u80fd\u5c3d\u804c\uff0c\u6b7b\u96be\u7791 \u76ee\u3002\u5c1a\u671b\u8001\u5148\u751f\u4e3a\u90d1\u5927\u4eba\u4e86\u6765\u4e86\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u7ea6\u5728\u5341\u591a\u5e74\u524d\uff0c\u6c5f\u6e56\u4e0a\u51fa\u73b0\u4e86\u7537\u5973\u4e24\u4e2a\u9b54\u5934\uff0c\u7537\u7684\u662f\u4e2a\u5934\u9640\uff0c\u5584\u4f7f\u5929\u7f61\u5200\u6cd5\uff0c\u53e6\u6709 \u4e00\u79cd\u6781\u5389\u5bb3\u7684\u6697\u5668\uff0c\u53eb\u505a\u788e\u9aa8\u94b1\u9556\uff0c\u867d\u7136\u662f\u666e\u666e\u901a\u901a\u7684\u91d1\u94b1\u9556\uff0c\u4f46\u88ab\u4ed6\u7528\u6bd2\u836f\u70bc\u8fc7\u52a0\u4e0a \u5185\u529f\u8fd0\u7528\uff0c\u6240\u4e2d\u4e4b\u513f\u9aa8\u788e\u7b4b\u6790\u3002\u800c\u4e14\u6700\u5947\u7684\u662f\uff0c\u521d\u65f6\u5e76\u65e0\u75db\u695a\uff0c\u836f\u6027\u8513\u5ef6\uff0c\u7b4b\u9aa8\u8150\u8680\uff0c \u5168\u8eab\u7684\u9aa8\u9abc\u5c31\u50cf\u7ed9\u767d\u8681\u86c0\u7a7a\u4e00\u6837\uff0c\u5230\u80f8\u9aa8\u788e\u88c2\u4e4b\u65f6\uff0c\u4fbf\u662f\u795e\u4ed9\u4e5f\u96be\u6d3b\u547d\uff01\u90a3\u5973\u9b54\u5934\u66f4\u5229 \u5bb3\uff0c\u5979\u64c5\u7528\u6885\u82b1\u9488\u5c04\u4eba\u7a74\u9053\uff0c\u8fd9\u6885\u82b1\u9488\u4e5f\u662f\u7528\u6bd2\u836f\u70bc\u8fc7\u7684\uff0c\u5faa\u7740\u7a74\u9053\uff0c\u653b\u81f3\u5fc3\u5934\u4e4b\u65f6\uff0c \u795e\u4ed9\u96be\u6551\u3002\u56e0\u4e3a\u8fd9\u4e24\u4e2a\u7537\u5973\u9b54\u5934\u5fc3\u72e0\u4e4e\u8fa3\uff0c\u6545\u6b64\u88ab\u79f0\u4e3a\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u3002\u5341\u5e74\u524d\u5404\u6b63\u6d3e \u95e8\u4e0b\uff0c\u66fe\u805a\u96c6\u4e86\u6570\u5341\u9ad8\u624b\uff0c\u7530\u653b\u4ed6\u4eec\uff0c\u5c06\u4ed6\u4eec\u9010\u5230\u6f20\u5317\u3002\u5341\u5e74\u6765\u9500\u58f0\u533f\u8ff9\uff0c\u4ece\u672a\u6709\u4eba\u5728 \u4e2d\u571f\u89c1\u8fc7\u4ed6\u4eec\u3002\u5374\u4e0d\u6599\u800c\u4eca\u7adf\u7136\u5728\u6b64\u51fa\u73b0\u3002\u4e00\u51fa\u624b\u5c31\u4f24\u4e86\u671d\u5ef7\u7684\u5411\u5bab\u548c\u4fdd\u62a4\u547d\u5b98\u7684\u9556\u5e08\u3002 \u3000\u3000\u957f\u5b59\u5747\u91cf\u4e5f\u662f\u5341\u5e74\u4e4b\u524d\uff0c\u53c2\u52a0\u8fc7\u56f4\u653b\u4ed6\u4eec\u7684\u9ad8\u624b\u4e4b\u5217\uff0c\u8fd9\u65f6\u8d8a\u60f3\u8d8a\u5947\uff0c\u518d\u5ba1\u89c6\u4e86\u4e00 \u4e0b\u674e\u5143\u7684\u4f24\u52bf\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u4f24\u52bf\u8f83\u8f7b\uff0c\u672a\u5fc5\u5168\u7136\u7edd\u671b\u3002\u8fd9\u4e8b\u60c5\u6709\u8e4a\u8df7\uff0c\u4f60\u4eec\u662f\u600e\u4e48\u78b0 \u5230\u8fd9\u5bf9\u9b54\u5934\u7684\uff1f\u201d \u3000\u3000\u674e\u5143\u9053\uff1a\u201c\u90d1\u5927\u4eba\u5949\u547d\u5230\u5df4\u5dde\u6765\u63a2\u671b\u592a\u5b50\u2026\u2026\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\uff0c\u592a\u5b50\u7adf\u5728\u5df4\u5dde\uff1f\u201d\u674e\u5143\u9053\uff1a\u201c\u7ae0\u6000\u592a\u5b50\u5df2\u88ab\u5e9f\u4e86\uff0c\u88ab\u8d2c\u5df4\u5dde\uff0c\u4e5f\u5c06\u8fd1\u534a\u5e74 \u4e86\u3002\u201d\u957f\u5b59\u5747\u91cf\u6068\u6068\u8bf4\u9053\uff1a\u201c\u5148\u592a\u5b50\u88ab\u6bd2\uff0c\u4eca\u592a\u5b50\u88ab\u5e9f\u3002\u54fc\u54fc\uff01\u864e\u6bd2\u4e0d\u98df\u513f\uff0c\u770b\u6765\u6b66\u5219 \u5929\u7684\u5fc3\u80a0\uff0c\u7adf\u6bd4\u864e\u72fc\u8fd8\u72e0\uff01\u201d\u539f\u6765\u5148\u592a\u5b50\u674e\u5f18\u662f\u6b66\u5219\u5929\u7684\u5927\u513f\u5b50\uff0c\u6709\u4e00\u5929\u5728\u5408\u58c1\u5bab\u4f60\uff0c \u5ffd\u7136\u83ab\u7136\u5176\u5999\u7684\u6b7b\u6389\uff0c\u6b7b\u65f6\u4e03\u7a8d\u6d41\u8840\uff0c\u4e3a\u72b6\u6781\u60e8\uff0c\u5bab\u5ef7\u4e2d\u6d41\u8a00\u871a\u8bed\uff0c\u90fd\u8bf4\u4ed6\u662f\u88ab\u6b66\u540e\u6bd2 \u6b7b\u7684\uff0c\u73b0\u5728\u7684\u592a\u5b50\u540d\u53eb\u674e\u8d24\uff0c\u56e0\u4e3a\u53cd\u5bf9\u6b66\u5219\u5929\u7684\u65bd\u653f\uff0c\u9042\u88ab\u6cfc\u7acb\uff0c\u5f53\u65f6\u66fe\u662d\u544a\u5929\u4e0b\uff0c\u4e0d \u8fc7\u957f\u5b59\u5747\u91cf\u56e0\u4e3a\u9690\u5c45\u5251\u9601\uff0c\u5374\u8fd8\u672a\u77e5\u9053\u4ed6\u5df2\u88ab\u8d2c\u5df4\u5dde\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u542c\u5f97\u6bdb\u9aa8\u8038\u7136\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u957f\u5b59\u4f2f\u4f2f\u5e38\u8bf4\u6b66\u5219\u5929\u662f\u4e2a\u5973\u9b54\u738b\uff0c\u5f53 \u771f\u662f\u6bd4\u6076\u884c\u8005\u548c\u6bd2\u89c2\u97f3\u8fd9\u4e24\u5927\u9b54\u5934\u8fd8\u66f4\u53ef\u6015\uff01\u201d \u3000\u3000\u674e\u5143\u7eed\u9053\uff1a\u201c\u6211\u5728\u6d1b\u9633\u5f00\u8bbe\u9556\u5c40\uff0c\u90d1\u5927\u4eba\u4ee5\u524d\u505a\u76d1\u8fd0\uff0c\u5e38\u5e38\u8bf7\u6211\u4fdd\u9556\uff0c\u5f88\u6709\u4ea4\u60c5\u3002 \u8fd9\u6b21\u4ed6\u5949\u547d\u5230\u5df4\u5dde\u63a2\u671b\u592a\u5b50\uff0c\u6211\u77e5\u9053\u8700\u4e2d\u65b0\u8fd1\u51fa\u73b0\u4e86\u51e0\u5904\u5de8\u76d7\uff0c\u81ea\u613f\u62a4\u9001\u4ed6\u5230\u5df4\u5dde\uff0c\u4e00 \u8def\u4e0a\u8fde\u5c0f\u8d3c\u4e5f\u6ca1\u9047\u4e0a\u4e2a\uff0c\u65b9\u81ea\u5e86\u5e78\uff1b\u90a3\u6599\u6628\u65e5\u5230\u4e86\u5e7f\u5143\uff0c\u8ddd\u79bb\u5251\u95e8\u5173\u7ea6\u83ab\u6709\u4e09\u5341\u591a\u91cc\u7684 \u5904\u6240\uff0c\u90a3\u91cc\u5730\u5f62\u9669\u5cfb\uff0c\u5c71\u9053\u5d0e\u5c96\uff0c\u6211\u5728\u524d\u9762\u5f00\u8def\uff0c\u5ffd\u542c\u5f97\u5c71\u4e0a\u4e00\u58f0\u602a\u5578\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea \u89c1\u90d1\u5927\u4eba\u5df2\u8dcc\u5012\u9a6c\u4e0b\u3002 \u3000\u3000\u6211\u8fd9\u4e00\u60ca\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u62e8\u8f6c\u9a6c\u5934\uff0c\u56de\u8eab\u6765\u6551\uff0c\u90a3\u77e5\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u6211\u7684\u5750\u9a91 \u5ffd\u7684\u4e00\u58f0\u957f\u5636\uff0c\u5c06\u6211\u629b\u8d77\uff0c\u540c\u65f6\u4ece\u6811\u6797\u4e2d\u98de\u51fa\u4e86\u51e0\u679a\u94b1\u9556\uff0c\u6211\u4eba\u5728\u534a\u7a7a\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u8eb2 \u95ea\u672f\u4e86\uff0c\u6043\u7740\u81ea\u5df1\u6709\u94c1\u5e03\u886b\u7684\u529f\u592b\uff0c\u786c\u51b2\u800c\u8fc7\uff0c\u770b\u90d1\u5927\u4eba\u65f6\uff0c\u4ed6\u5df2\u662f\u660f\u8ff7\u4e0d\u9192\u3002 \u3000\u3000\u6211\u4eec\u90a3\u4e24\u5339\u9a6c\u5219\u762b\u5728\u5730\u4e0a\uff0c\u7adf\u50cf\u7ed9\u4eba\u7528\u91cd\u624b\u6cd5\u51fb\u6bd9\u4e00\u822c\uff0c\u4f46\u53c8\u770b\u4e0d\u51fa\u662f\u4e2d\u4e86\u4ec0\u4e48\u6697 \u5668\u3002\u6211\u77e5\u9053\u662f\u9047\u4e0a\u4e86\u7edd\u9876\u7684\u9ad8\u624b\uff0c\u6b63\u51c6\u5907\u62fc\u4e86\u6027\u547d\u548c\u5f3a\u4eba\u6b7b\u6218\uff0c\u53ef\u602a\u7684\u662f\uff0c\u5f3a\u4eba\u7adf\u6ca1\u73b0 \u8eab\uff0c\u4f46\u542c\u5f97\u6797\u4e2d\u602a\u7b11\u4e4b\u58f0\uff0c\u8d8a\u79bb\u8d8a\u8fdc\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u597d\u50cf\u5230\u4e86\u6570\u91cc\u4e4b\u5916\uff01\u201d\u674e\u5143\u4f3c\u662f\u4f59 \u60b8\u72b9\u5b58\uff0c\u505c\u4e86\u7247\u523b\uff0c\u65b9\u59cb\u98a4\u58f0\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u6211\u54ea\u91cc\u8fd8\u6562\u8ffd\u8d76\uff01\u6211\u4ed4\u7ec6\u5ba1\u89c6\uff0c\u90d1\u5927\u4eba\u8eab\u4e0a \u4e00\u65e0\u4f24\u75d5\uff0c\u4f46\u6478\u4ed6\u8109\u606f\uff0c\u53c8\u5206\u660e\u662f\u91cd\u4f24\u4e4b\u50cf\u3002\u8352\u5c71\u91ce\u5cad\uff0c\u65e0\u5904\u6c42\u533b\uff0c\u597d\u5728\u6211\u8bb0\u5f97\u90d1\u5927\u4eba \u8bf4\u8fc7\uff0c\u8bf4\u957f\u5b59\u5927\u4eba\u5c31\u5728\u5251\u9601\u9690\u5c45\uff0c\u6ca1\u5948\u4f55\u6211\u53ea\u597d\u6765\u6c42\u4f60\u4e86\u3002\u5440\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u6bd2\u89c2\u97f3\u548c\u6076 \u884c\u8005\u8fd9\u4e24\u5927\u9b54\u5934\uff01\u66f4\u60f3\u4e0d\u5230\u6211\u4e2d\u4e86\u788e\u9aa8\u94b1\u9556\uff0c\u81ea\u5df1\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u542c\u4e86\uff0c\u4f46\u89c9\u8fd9\u4ef6\u4e8b\u60c5\u79bb\u5947\u4e4b\u6781\uff0c\u90a3\u4e24\u4e2a\u9b54\u5934\u65e2\u975e\u52ab\u8d22\uff0c\u4ea6\u65e0\u5bbf\u6028\uff0c\u600e\u4e48\u65e0 \u7aef\u7aef\u7684\u5411\u4e00\u4e2a\u671d\u5ef7\u547d\u5b98\u65bd\u5c55\u6740\u624b\uff01\u770b\u957f\u5b59\u5747\u91cf\u65f6\uff0c\u53ea\u89c1\u4ed6\u773c\u73e0\u95ea\u52a8\uff0c\u4f3c\u4e4e\u4e5f\u6b63\u5728\u7422\u78e8\u8fd9 \u4ef6\u79bb\u5947\u96be\u89e3\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u674e\u5143\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u6307\u671b\u6d3b\u4e86\uff0c\u4f46\u90d1\u5927\u4eba\u6765\u4e86\u4e4b\u4e8b\u8fd8\u671b\u8001\u5148\u751f\u5e2e\u5fd9\u3002\u201d \u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u4ec0\u4e48\u672a\u4e86\u4e4b\u4e8b\uff1f\u201d\u674e\u5143\u9053\uff1a\u201c\u5929\u540e\u6258\u90d1\u5927\u4eba\u9001\u7ed9\u5e9f\u592a\u5b50\u7684\u4e66\u4fe1\u8fd8\u672a\u9001\u5230 \u5df4\u5dde\uff0c\u542c\u90d1\u5927\u4eba\u8bf4\u4eba\u540e\u5bf9\u5e9f\u592a\u5b50\u601d\u5ff5\u5f97\u5f88\uff0c\u8d2c\u4ed6\u5230\u5df4\u5dde\u4e43\u662f\u4e0d\u5f97\u5df2\u4e4b\u4e8b\uff0c\u8ba9\u4eba\u5b50\u5f97\u8fd9\u4e00 \u5c01\u4fe1\uff0c\u4e5f\u597d\u8ba9\u4ed6\u5b89\u5fc3\u3002\u201d \u3000\u3000\u957f\u5b59\u5747\u91cf\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u732b\u54ed\u8001\u9f20\u5047\u6148\u60b2\uff01\u6b66\u5219\u5929\u6068\u4e0d\u5f97\u628a\u674e\u5411\u5b97\u5ba4\uff0c\u5168\u90e8\u65a9 \u5c3d\u6740\u7edd\uff0c\u8fde\u81ea\u5df1\u7684\u513f\u5b50\u4e5f\u4e0d\u653e\u8fc7\uff0c\u6211\u5c31\u4e0d\u4fe1\u5979\u5bf9\u592a\u5b50\u8fd8\u6709\u534a\u70b9\u6148\u6bcd\u4e4b\u5fc3\uff01\u201d \u3000\u3000\u674e\u5143\u4e0d\u6562\u4f5c\u58f0\uff0c\u957f\u5b59\u5747\u91cf\u5ffd\u9053\uff1a\u201c\u662f\u6b66\u5219\u5929\u81ea\u5df1\u7684\u4e3b\u610f\uff0c\u521a\u90d1\u5927\u4eba\u4e0a\u63a2\u671b\u592a\u5b50\u7684\uff0c \u8fd8\u662f\u90d1\u5927\u4eba\u81ea\u5df1\u4e0a\u758f\u6c42\u53bb\uff0c\u7136\u540e\u6b66\u5219\u5929\u518d\u6d3e\u9063\u4ed6\u7684\uff1f\u201d\u674e\u5143\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff01\u201d\u957f\u5b59\u5747 \u91cf\u6c89\u541f\u8bf4\u9053\uff1a\u201c\u6211\u770b\u4e5d\u6210\u662f\u90d1\u5927\u4eba\u81ea\u5df1\u4e0a\u758f\u8bf7\u6c42\u8bb8\u4ed6\u53bb\u63a2\u671b\u592a\u5b50\u7684\u3002\u201d\u5ffd\u5730\u9ad8\u58f0\u53eb\u9053\uff1a \u201c\u5b9a\u662f\u8fd9\u6837\uff0c\u90a3\u4e24\u4e2a\u9b54\u5934\u662f\u6b66\u5219\u5929\u6d3e\u9063\u6765\u6740\u90d1\u5927\u4eba\u7684\uff01\u201d\u8fd9\u63a8\u60f3\u592a\u8fc7\u5947\u602a\uff0c\u8fde\u4e0a\u5b98\u5a49\u513f \u4e5f\u89c9\u96be\u4ee5\u7f6e\u4fe1\uff0c\u4f46\u770b\u957f\u5b59\u5747\u91cf\u7684\u795e\u60c5\uff0c\u5374\u662f\u8bf4\u5f97\u5341\u5206\u80af\u5b9a\u3002 \u3000\u3000\u674e\u5143\u6b63\u81ea\u60f4\u60f4\u4e0d\u5b89\uff0c\u5ffd\u89c1\u957f\u5b59\u5747\u91cf\u9762\u8272\u5927\u53d8\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6cf0\u513f\u3001\u58c1\u513f\u3001\u5a49\u513f\uff0c\u4f60\u4eec\u8d76\u5feb\u56de\u5bb6\uff0c\u53ea\u6015\u8fd9\u4e24\u4e2a\u5927\u9b54\u5934\u5c31\u8981\u6765\u4e86\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u957f\u5b59\u5747\u91cf\u770b\u4e86\u674e\u5143\u4e00\u773c\uff0c\u4f3c\u662f\u6709\u8bdd\u60f3\u8bf4\uff0c\u5374\u53c8 \u4e0d\u5fcd\u51fa\u53e3\u3002\u674e\u5143\u6115\u7b11\u9053\uff1a\u201c\u8fd9\u65f6\u5019\u8fd8\u6709\u4ec0\u4e48\u987e\u5fcc\uff1f\u6211\u7ed9\u8001\u4f2f\u8bf4\u4e86\u5427\u3002\u60f3\u90a3\u4e24\u4e2a\u9b54\u5934\u4f55\u7b49 \u529f\u592b\uff0c\u82e5\u7136\u8981\u53d6\u6211\u4e0e\u90d1\u5927\u4eba\u7684\u6027\u547d\uff0c\u90a3\u771f\u662f\u6613\u5982\u53cd\u638c\uff01\u7136\u800c\u4ed6\u5374\u6545\u610f\u8ba9\u6211\u4eec\u9003\u751f\uff0c\u8fd9\uff0c \u8fd9\u2014\u2014\u957f\u5b59\u58c1\u9053\uff1a\u201c\u8fd9\u4ec0\u4e48\uff1f\u201d\u957f\u5b59\u5747\u91cf\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6545\u610f\u8981\u8ba9\u674e\u5927\u54e5\u9003\u5230\u54b1\u4eec\u5bb6 \u6765\u3002\u201d \u3000\u3000\u674e\u5143\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u9b54\u5934\u7528\u5fc3\u6076\u6bd2\uff0c\u53ef\u60dc\u6211\u5f53\u65f6\u60f3\u4e0d\u5230\u662f\u4ed6\u4eec\uff0c\u8981\u4e0d\u7136\u6211\u4e5f\u4e0d \u4f1a\u6765\u8fde\u7d2f\u8001\u4f2f\u4e86\u3002\u5982\u4eca\u7ecf\u8001\u4eec\u70b9\u9192\uff0c\u6211\u624d\u77e5\u9053\u4e0a\u4e86\u4ed6\u4eec\u7684\u5708\u5957\uff0c\u505a\u4e86\u4ed6\u4eec\u7684\u5f15\u8def\u4e4b\u4eba\uff01\u201d \u957f\u5b59\u5747\u91cf\u9053\uff1a \u3000\u3000\u201c\u674e\u5144\u4e0d\u5fc5\u5f15\u548e\uff0c\u6211\u65e9\u84c4\u610f\u8981\u6597\u6597\u8fd9\u4e24\u4e2a\u9b54\u5934\u4e86\u3002\u770b\u8fd9\u60c5\u5f62\uff0c\u90a3\u4e24\u4e2a\u9b54\u5934\u662f\u6b66\u5219\u5929 \u6d3e\u6765\u7684\uff0c\u66f4\u65e0\u7591\u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u6b66\u5219\u5929\u7be1\u593a\u4e86\u674e\u5510\u5e1d\u4f4d\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u4ece\u6ca1\u6709\u5973\u4eba\u505a\u7687\u5e1d\u7684\uff0c\u8fd9\u771f\u662f \u4e00\u5927\u5996\u5b7d\u3002\u7687\u5e1d\u5b50\u5b59\uff0c\u524d\u671d\u5927\u81e3\uff0c\u5341\u4e4b\u516b\u4e5d\u90fd\u662f\u6548\u5fe0\u5510\u5ba4\uff0c\u4e0d\u613f\u81e3\u670d\u4e8e\u5979\uff0c\u5979\u5f53\u7136\u4e5f\u77e5 \u9053\u6211\u4eec\u8fd9\u73ed\u4eba\u6697\u4e2d\u53cd\u5bf9\u5979\uff0c\u6240\u4ee5\u5386\u5e74\u6765\u6240\u4f5c\u6240\u4e3a\uff0c\u6781\u5c3d\u8bdb\u9504\u5f02\u5df1\u7684\u80fd\u4e8b\u3002\u8bd5\u60f3\u8fde\u513f\u4e86\u90c1 \u53ef\u4ee5\u6bd2\u6740\uff0c\u8fd8\u6709\u8c01\u4e0d\u80fd\u6740\uff1f\u6545\u6b64\u6211\u6599\u60f3\u90d1\u4eba\u4eba\u5fc5\u5b9a\u662f\u81ea\u5df1\u4e0a\u758f\uff0c\u6c42\u5979\u51c6\u8bb8\u4eba\u63a2\u671b\u513f\u5b50\uff0c \u5979\u77e5\u9053\u90d1\u5927\u4eba\u5fc3\u5b58\u674e\u5510\u6b63\u7edf\uff0c\u4e8e\u662f\u5c31\u6697\u5bb3\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u5979\u82e5\u8981\u6740\u90d1\u5927\u4eba\uff0c\u4f55\u987b\u8fd9\u6837\u8d39\u4e8b\uff1f\u800c\u4e14\u8fd8\u6258\u90d1\u5927\u4eba\u5e26\u4fe1\u7ed9\u5979\u7684\u513f\u5b50\uff1f\u201d \u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u5979\u624b\u6bb5\u9ad8\u660e\u4e4b\u5916\uff0c\u6545\u4f5c\u4f2a\u5584\uff0c\u7b3c\u7edc\u4eba\u5fc3\u3002\u6211\u662f\u524d\u671d\u5927\u81e3\uff0c\u5979\u4e00\u638c \u6743\uff0c\u6211\u4fbf\u9690\u5c45\u4e0d\u4ed5\uff0c\u60f3\u6765\u5979\u65e9\u5df2\u6068\u6211\u5207\u9aa8\u3002\u54fc\uff0c\u90a3\u4e24\u4e2a\u9b54\u5934\u4e00\u5b9a\u662f\u5979\u5dee\u9063\u7684\uff01\u201d \u3000\u3000\u8fd9\u4e03\u5e74\u7c73\uff0c\u957f\u5b59\u5747\u91cf\u51e0\u4e4e\u6bcf\u65e5\u90fd\u5411\u4e0a\u5b98\u5a49\u513f\u8bf4\u6b66\u5219\u5929\u7684\u574f\u8bdd\uff0c\u6559\u513f\u5973\u4ec7\u6068\u5973\u7687\u5e1d\u3002 \u4e0a\u5b98\u5a49\u513f\u5982\u4eca\u542c\u4e86\u4ed6\u8fd9\u756a\u63a8\u60f3\uff0c\u867d\u89c9\u6709\u70b9\u7275\u5f3a\uff0c\u4e5f\u4fe1\u4e86\u4e03\u516b\u6210\uff0c\u53ea\u662f\u6709\u4e00\u70b9\u4e0d\u5927\u670d\u6c14\uff1a \u201c\u7537\u4eba\u5973\u4eba\u90fd\u662f\u4e00\u6837\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u7537\u4eba\u505a\u7687\u5e1d\u5219\u706d\u516c\u5730\u9053\uff0c\u5973\u4eba\u505a\u4e86\u7687\u5e1d\u5c31\u8981\u88ab\u9a82\u4e3a\u5996 \u5b7d\uff1f\u201d\u5f53\u7136\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u4e0a\u5b98\u5a49\u513f\u53ea\u662f\u7559\u5728\u5fc3\u91cc\uff0c\u65ad\u4e0d\u6562\u5728\u957f\u5b59\u4f2f\u4f2f\u7684\u9762\u524d\u5410\u9732\u51fa\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u6b63\u5728\u81ea\u601d\u81ea\u60f3\uff0c\u53ea\u89c1\u957f\u5b59\u5747\u91cf\u9762\u5411\u7740\u513f\u5973\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6cf0\u513f\uff0c\u58c1\u513f\uff0c\u4f60\u8001\u7236\u7684\u6027\u547d\u4e5f\u8bb8\u8fc7\u4e0d\u4e86\u660e\u671d\uff0c\u6545\u6b64\u6211\u5982\u4eca\u591a\u8d39\u5507\u820c\uff0c\u628a\u8bdd\u8bf4\u660e\uff0c \u597d\u53eb\u4f60\u4eec\u77e5\u9053\u8c01\u662f\u54b1\u5bb6\u7684\u5927\u4ec7\u4eba\u3002\u597d\uff0c\u5982\u4eca\u8bdd\u5df2\u8bf4\u660e\uff0c\u4f60\u4eec\u8d76\u5feb\u56de\u5bb6\u53bb\uff0c\u4e0d\u8bba\u6709\u4ec0\u4e48\u4e8b \u60c5\uff0c\u90fd\u4e0d\u53ef\u4ee5\u51fa\u6765\u3002 \u3000\u3000\u5a49\u513f\uff0c\u4f60\u7a0d\u61c2\u533b\u9053\uff0c\u5c06\u90d1\u5927\u4eba\u642c\u56de\u5bb6\u53bb\uff0c\u7528\u6d88\u6bd2\u6563\u5916\u6577\uff0c\u7389\u9732\u4e5d\u5f00\u6c34\u5185\u670d\u3002\u674e\u5144\uff0c \u4f60\u2014\u2014\u201d\u674e\u5143\u53eb\u9053\uff1a\u201c\u6211\u4e2d\u4e86\u788e\u9aa8\u94b1\u9556\uff0c\u6027\u547d\u8fc7\u4e0d\u4e86\u4e09\u5173\uff0c\u53cd\u6b63\u662f\u6b7b\uff0c\u5c31\u4e0e\u4f60\u4e00\u540c\u4e0e\u90a3 \u4e24\u4e2a\u9b54\u5934\u62fc\u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e0e\u957f\u5b59\u5144\u59b9\u56de\u5230\u5bb6\u5185\uff0c\u521a\u521a\u5c06\u7389\u9732\u4e38\u8c03\u6c34\u704c\u90d1\u6e29\u670d\u4e0b\uff0c\u4fbf\u542c\u5f97\u957f\u5b59\u58c1\u5618\u58f0 \u8bf4\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff0c\u90a3\u4e24\u4e2a\u9b54\u5934\u771f\u7684\u6765\u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4ece\u95e8\u7f1d\u5f20\u671b\u51fa\u53bb\uff0c\u4f46\u542c\u5f97\u4e24\u58f0\u602a\u5578\uff0c\u4e00\u58f0\u91cf\u5927\u97f3\u5b8f\uff0c\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c \u53e6\u4e00\u58f0\u5374\u5982\u9ec4\u83ba\u51fa\u8c37\uff0c\u6e05\u8106\u975e\u5e38\uff0c\u523a\u5165\u8033\u819c\uff0c\u4ee4\u4eba\u795e\u98d8\u610f\u8361\u3002\u770b\u6d88\u695a\u65f6\uff0c\u5c71\u5761\u4e0a\u5df1\u6765\u4e86 \u4e00\u7537\u4e00\u5973\uff0c\u7537\u7684\u662f\u4e2a\u62ab\u5934\u5934\u9640\uff0c\u866c\u9aef\u5982\u621f\uff0c\u76f8\u8c8c\u72f0\u72de\uff1b\u5973\u7684\u5934\u7f20\u767d\u5dfe\uff0c\u6253\u4e86\u4e00\u53cc\u8774\u8776\u7ed3 \u4e86\uff0c\u957f\u7709\u5165\u9b13\uff0c\u59ff\u5bb9\u51b6\u8273\uff0c\u8361\u610f\u64a9\u4eba\u3002\u8fd9\u4e00\u7537\u4e00\u5973\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u662f\u6076\u884c\u8005\u548c\u6bd2\u89c2\u97f3\u4e86\u3002 \u3000\u3000\u6076\u884c\u8005\u6012\u543c\u5982\u96f7\uff0c\u8eab\u5f62\u4e00\u73b0\uff0c\u5c31\u51b2\u7740\u957f\u5b59\u5747\u91cf\u559d\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u539f\u6765\u4f60\u8fd8\u6ca1\u6b7b\uff0c\u6d12 \u5bb6\u6765\u7d22\u5341\u5e74\u524d\u7684\u65e7\u503a\u4e86\uff01\u201d\u90a3\u6bd2\u89c2\u97f3\u5374\u5a07\u58f0\u5456\u5456\u7684\u8bf4\u9053\uff1a\u201c\u957f\u5b59\u5148\u751f\uff0c\u5341\u5e74\u6765\u89c1\uff0c\u4f60\u8001 \u4eba\u5bb6\u5065\u70c1\u5982\u524d\uff0c\u53ef\u559c\u53ef\u8d3a\u3002\u597d\u5728\u4f60\u6ca1\u6709\u6b7b\uff0c\u82e5\u662f\u6b7b\u4e86\uff0c\u90a3\u624d\u53eb\u6211\u4f24\u5fc3\u5462\uff01\u60f3\u5f53\u5e74\uff0c\u4f60\u7387 \u6570\u5341\u9ad8\u624b\u56f4\u653b\u6211\u4eec\uff0c\u53ef\u60dc\u4eba\u591a\u6df7\u6218\uff0c\u6211\u8fd8\u672a\u5f97\u597d\u597d\u9886\u6559\u4f60\u7684\u5ce8\u5d4b\u5251\u6cd5\uff0c\u4eca\u65e5\u5e78\u4f1a\u6545\u4eba\uff0c \u5f97\u507f\u5bbf\u613f\uff0c\u5feb\u6170\u4f55\u5982\uff01\u201d\u957f\u5b59\u5747\u91cf\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8981\u4e0a\u4fbf\u4e0a\uff0c\u4f55\u5fc5\u591a\u8a00\uff0c\u8001\u592b\u7b49\u5019\u4f60\u95e8\u5bfb \u6b7b\uff0c\u4e5f\u7b49\u4e86\u5341\u5e74\u4e86\uff01\u201d \u3000\u3000\u6bd2\u89c2\u97f3\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u65e2\u7136\u5982\u6b64\uff0c\u6211\u53ef\u6709\u4e00\u4e8b\u8981\u63d0\u9192\u4f60\u8001\u5148\u751f\uff0c\u5341\u5e74\u524d\u4f60\u4eec\u4eba \u591a\u52bf\u4f17\uff0c\u8981\u628a\u6211\u7f6e\u4e8e\u6b7b\u5730\uff0c\u8be5\u60f3\u4e0d\u5230\u6211\u8fd8\u6d3b\u5230\u4eca\u5929\u5427\uff1f\u4eca\u5929\u4f60\u5b64\u8eab\u4e00\u4eba\uff0c\u8981\u60f3\u50cf\u6211\u5f53\u5e74 \u4e00\u6837\u7684\u8131\u8eab\u800c\u8d70\uff0c\u6050\u6015\u4e07\u4e07\u4e0d\u80fd\u4e86\uff01\u4f60\u5bf9\u5bb6\u4eba\u5b50\u5973\u4ea4\u4ee3\u4e86\u540e\u4e8b\u6ca1\u6709\uff1f\u6709\u4ec0\u4e48\u672a\u4e86\u7ed3\u7684\u4e8b \u8981\u5c0f\u59b9\u6548\u52b3\u4e48\uff1f\u201d\u6bb7\u6bb7\u5782\u95ee\uff0c\u7adf\u4f3c\u5bf9\u8001\u670b\u53cb\u4e00\u822c\uff0c\u5341\u5206\u5173\u6000\u3002 \u3000\u3000\u957f\u5b59\u5747\u91cf\u7ed9\u5979\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u4e86\u9752\u950b\u5b9d\u5251\uff0c\u53ea\u89c1\u90a3\u6bd2\u89c2\u97f3\u659c\u773c\u4e00\u77a5\uff0c \u53c8\u662f\u201c\u683c\u683c\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e0d\u662f\u5b64\u8eab\u4e00\u4eba\uff0c\u8fd8\u6709\u4e2a\u670b\u53cb\u5728\u8fd9\u513f\u3002\u54ce\u54df\uff0c\u6211\u9053\u662f \u8c01\uff0c\u539f\u6765\u8db3\u674e\u5927\u9556\u5e08\u3002\u4f60\u4e2d\u4e86\u6211\u5e08\u5144\u7684\u788e\u9aa8\u94b1\u9556\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u4f60\u4e0d\u52a8\u6012\uff0c\u4e0d\u52b3\u795e\uff0c\u8fd8\u53ef \u4ee5\u6d3b\u5230\u540e\u5929\uff0c\u542c\u6211\u7684\u8bdd\uff0c\u597d\u597d\u7684\u8eba\u5728\u5e8a\u4e0a\u7b49\u6b7b\u5427\uff0c\u8fd9\u6837\u6b7b\u4e5f\u6b7b\u5f97\u8212\u670d\u4e00\u4e9b\u3002\u82e5\u7136\u4f60\u8fd8\u8981 \u6253\u67b6\uff0c\u4e00\u52a8\u771f\u529b\uff0c\u5168\u8eab\u9aa8\u788e\uff0c\u5440\uff0c\u90a3\u624d\u662f\u75db\u82e6\u975e\u5e38\u54e9\uff01\u6211\u4e00\u7247\u597d\u5fc3\uff0c\u6307\u70b9\u4e8e\u4f60\uff0c\u4e0d\u542c\u826f \u8a00\uff0c\u540e\u6094\u83ab\u53ca\uff01\u201d \u3000\u3000\u674e\u5143\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u72e0\u6bd2\u7684\u5973\u9b54\u5934\uff0c\u90d1\u5927\u4eba\u4e0e\u4f60\u4f55\u51a4\u4f55\u4ec7\uff0c\u65bd\u6b64\u8fa3\u624b\uff01\u6211\u4eca\u65e5\u62da\u4e86 \u7c89\u9aa8\u788e\u8eab\uff0c\u4e5f\u8981\u6597\u4f60\u4e00\u6597\uff01\u201d\u6bd2\u89c2\u97f3\u7eb5\u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u6c49\u4e86\uff01\u6211\u5e73\u65e5\u6740\u4eba\uff0c\u4ece\u6765\u4e0d \u8bb2\u9053\u7406\uff0c\u4eca\u65e5\u770b\u5728\u4f60\u8fd9\u70b9\u786c\u4efd\uff0c\u7834\u4f8b\u548c\u4f60\u8bf4\u8bf4\u3002\u4f60\u95ee\u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u9093\u5927\u4eba\u5417\uff1f\u90a3\u662f\u5929\u540e \u548c\u6211\u7684\u4e00\u7247\u597d\u5fc3\uff0c\u5929\u540e\u8bf4\u90d1\u5927\u4eba\u767d\u53d1\u82cd\u82cd\uff0c\u4e07\u91cc\u8fe2\u8fe2\uff0c\u897f\u884c\u5165\u8700\uff0c\u4ec6\u4ec6\u98ce\u5c18\uff0c\u592a\u8f9b\u82e6\uff01 \u6240\u4ee5\u6211\u624d\u5949\u9001\u4ed6\u4e24\u679a\u900f\u7a74\u795e\u9488\uff0c\u7701\u5f97\u4ed6\u8981\u591a\u8d70\u4e00\u6bb5\u6808\u9053\u7684\u5954\u6ce2\u4e4b\u82e6\uff01\u201d \u3000\u3000\u957f\u5b98\u5a49\u513f\u542c\u5f97\u5206\u660e\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u957f\u5b59\u4f2f\u4f2f\u679c\u7136\u6ca1\u6709\u6599\u9519\uff0c\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u5f53 \u771f\u662f\u6b66\u5219\u5929\u6d3e\u6765\u7684\uff01\u201d\u4f46\u968f\u5373\u5fc3\u4e0a\u53c8\u8d77\u4e86\u6de1\u6de1\u7684\u7591\u4e91\u3002\u770b\u8fd9\u6bd2\u89c2\u97f3\u88c5\u6a21\u4f5c\u6837\uff0c\u8bf4\u4e1c\u8bdd\u897f\uff0c \u8fd9\u4e00\u756a\u8bdd\uff0c\u7adf\u4f3c\u662f\u6709\u610f\u8bf4\u7ed9\u957f\u5b59\u5747\u91cf\u542c\u7684\uff01\u60f3\u6b66\u5219\u5929\u8981\u5979\u6697\u6740\u5927\u81e3\uff0c\u8fd9\u4e8b\u4f55\u7b49\u79d8\u5bc6\uff1f\u600e \u7684\u5979\u5374\u597d\u4f3c\u6015\u4eba\u4e0d\u77e5\uff0c\u5148\u884c\u5410\u9732\uff1f \u3000\u3000\u957f\u5b59\u5747\u91cf\u65e9\u5df2\u8ba4\u5b9a\u662f\u6b66\u5219\u5927\u7684\u4e3b\u4f7f\uff0c\u542c\u4e86\u6b64\u8bdd\uff0c\u66b4\u6012\u559d\u9053\uff1a \u3000\u3000\u201c\u6b66\u5219\u5929\u662f\u4eba\u9b54\u5934\uff0c\u4f60\u4eec\u4e24\u4e2a\u662f\u5c0f\u9b54\u5934\uff0c\u5927\u9b54\u5934\u6211\u96be\u5948\u5979\u4f55\uff0c\u4eca\u65e5\u5148\u548c\u4f60\u4eec\u8fd9\u4e24\u4e2a \u5c0f\u9b54\u5934\u62fc\u62fc\uff01\u6076\u884c\u8005\uff0c\u6bd2\u89c2\u97f3\uff01\u4f60\u4eec\u662f\u4e00\u9f50\u4e0a\u8fd8\u662f\u534a\u8f6e\u6218\uff1f\u201d\u6bd2\u89c2\u541d\u683c\u683c\u7b11\u9053\uff1a\u201c\u5341\u5e74 \u524d\u4f60\u4eec\u6043\u591a\u4e3a\u80dc\uff0c\u4f55\u66fe\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u781a\u77e9\u4e86\uff1f\u4e0d\u8fc7\u770b\u5728\u4f60\u5e74\u8001\u4efd\u4e0a\uff0c\u8ba9\u4f60\u548c\u5e08\u5144\u5148\u6597\uff0c\u7701 \u529b\u4e00\u70b9\uff0c\u5230\u4f60\u5c06\u8fd1\u7b4b\u75b2\u529b\u7aed\u4e4b\u65f6\uff0c\u6211\u518d\u60f3\u4e00\u4e2a\u597d\u6cd5\u513f\uff0c\u7ed9\u4f60\u9001\u7ec8\uff0c\u8ba1\u4f60\u5c11\u53d7\u75db\u82e6\uff01\u201d \u3000\u3000\u6076\u884c\u8005\u4eae\u51fa\u6212\u5200\uff0c\u53eb\u9053\uff1a\u201c\u5bf9\u8fd9\u8001\u8d3c\uff0c\u4f55\u5fc5\u6148\u60b2\uff1f\u5e08\u59b9\uff0c\u4f60\u7ed9\u6211\u63a0\u9635\uff0c\u8ba9\u6211\u4e00\u5200\u5c06 \u4ed6\u65ab\u6389\u4fbf\u662f\uff01\u201d\u4e00\u58f0\u5927\u559d\uff0c\u6212\u5200\u75be\u8d77\uff0c\u6402\u5934\u4fbf\u65ab\uff0c\u957f\u5b59\u5747\u91cf\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u957f\u5251\u6296 \u5904\uff0c\u5251\u5149\u95ea\u70c1\uff0c\u5237\u7684\u4fbf\u662f\u53cd\u624b\u4e00\u5251\uff0c\u8fd9\u4e00\u5251\u8fde\u95ea\u5e26\u653b\uff0c\u6b65\u6cd5\u548c\u65b9\u4f4d\u90fd\u6070\u5230\u597d\u5904\uff0c\u6b63\u662f\u957f \u5b59\u5747\u91cf\u7684\u4e4e\u751f\u7edd\u5b66\uff0c\u6309\u8bf4\u6076\u884c\u8005\u6212\u5200\u5b9a\u7136\u65ab\u7a7a\uff0c\u800c\u4ed6\u90a3\u4e00\u5251\u6076\u884c\u8005\u975e\u4e2d\u4e0d\u53ef\uff0c\u54ea\u6599\u6076\u884c \u8005\u624b\u81c2\u4e00\u4f38\uff0c\u9aa8\u9abc\u683c\u683c\u4f5c\u54cd\uff0c\u84e6\u7136\u95ee\u4ed6\u7684\u624b\u81c2\u597d\u50cf\u7a81\u7136\u957f\u4e86\u51e0\u5bf8\uff0c\u5200\u950b\u4e00\u8f6c\uff0c\u7adf\u7136\u5288\u5230 \u957f\u5b59\u5747\u91cf\u7684\u80f8\u524d\u3002\u9ad8\u624b\u6bd4\u6597\uff0c\u53ea\u4e89\u6beb\u9ecd\uff0c\u6076\u884c\u8005\u8fd9\u4e00\u7edd\u62db\uff0c\u5927\u51fa\u957f\u5b59\u5747\u91cf\u610f\u5916\u3002\u5e78\u800c\u4ed6 \u7684\u5251\u5b66\u7cbe\u6e5b\uff0c\u957f\u5251\u4e00\u62ab\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u661f\u98de\u6e85\u3002\u957f\u5b59\u5747\u91cf\u864e\u53e3\u75bc\u75db\uff0c\u90a3\u6076\u884c \u8005\u4e5f\u8e09\u8e09\u8dc4\u8dc4\u7684\u5012\u9000\u51e0\u6b65\u3002 \u3000\u3000\u6bd2\u89c2\u97f3\u5a07\u7b11\u9053\uff1a\u201c\u5e08\u5144\u4e2a\u53ef\u8f7b\u654c\uff01\u957f\u5b59\u5148\u751f\u662f\u592a\u5b97\u7687\u5e1d\u8d4f\u8bc6\u7684\u4eba\uff0c\u6614\u975e\u6b66\u529f\u8d85\u7edd\uff0c \u600e\u505a\u5f97\u5230\u6bbf\u524d\u68c0\u70b9\uff1f\u201d\u6076\u884c\u8005\u4e00\u58f0\u6012\u543c\uff0c\u53c8\u518d\u6251\u4e0a\uff0c\u5200\u5149\u95ea\u95ea\uff0c\u5200\u98ce\u547c\u547c\uff0c\u4ed6\u90a3\u8def\u5929\u7f61 \u5200\u6cd5\u4e43\u662f\u6c72\u521a\u731b\u7684\u5200\u6cd5\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u628a\u957f\u5b59\u5747\u91cf\u7b3c\u7f69\u5728\u5200\u5149\u4e4b\u4e0b\uff01\u4e0a\u5b98\u5a49\u513f\u5728\u95e8\u7f1d\u91cc \u5077\u77a7\uff0c\u76f4\u5413\u5f97\u624b\u5fc3\u6dcc\u6c57\uff0c\u770b\u957f\u5b59\u5144\u86db\u65f6\uff0c\u867d\u7136\u4e5f\u5728\u7d27\u5f20\u7684\u5077\u770b\uff0c\u4eec\u5374\u4e0d\u600e\u6837\u60ca\u60f4\u3002\u957f\u5b59 \u58c1\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u6076\u884c\u8005\u8fd8\u672a\u77e5\u9053\u6211\u7239\u7239\u7684\u5389\u5bb3\uff0c\u6211\u7239\u7239\u7684\u5251\u6cd5\u4e13\u80fd\u4ee5\u9759\u5236\u52a8\uff0c\u4ee5\u9038\u5f85 \u52b3\u3002\u201d \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u4f46\u89c1\u6076\u884c\u8005\u8fde\u58f0\u6012\u53eb\uff0c\u4e00\u5200\u7d27\u8fc7\u4e00\u5200\uff0c\u6709\u5982\u5de8\u6d6a\u72c2\u98d9\uff0c\u8fde\u756a\u5377\u5230\u3002\u4f46\u770b \u957f\u5b59\u5747\u91cf\uff0c\u5374\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u5728\u5200\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5140\u7acb\u5982\u5c71\uff0c\u4efb\u4ed6\u6d6a\u9aa4\u98ce\u72c2\uff0c\u4e1d\u6beb\u4e0d\u4e3a\u6240\u52a8\uff0c \u4e00\u67c4\u9752\u94a2\u5251\uff0c\u592d\u77eb\u5982\u9f99\uff0c\u5728\u5982\u5c71\u7684\u5200\u5f71\u4e4b\u4e2d\uff0c\u76f4\u900f\u51fa\u6765\uff0c\u4e0d\u75be\u4e0d\u5f90\uff0c\u6709\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u6781 \u5f97\u8f69\u7075\u7fd4\u52a8\u4e4b\u5999\uff0c\u6597\u4e86\u7ea6\u534a\u4e2a\u65f6\u8fb0\uff0c\u5140\u81ea\u4e0d\u5206\u80dc\u8d1f\u3002\u9661\u7136\u95f4\uff0c\u5ffd\u542c\u5f97\u957f\u5b59\u5747\u91cf\u4e00\u58f0\u957f\u7b11\uff0c \u4e00\u9053\u5251\u5149\uff0c\u51b2\u7834\u5343\u5c42\u5200\u5f71\uff0c\u53cd\u7f69\u4e0b\u6765\uff0c\u987f\u65f6\u95f4\uff0c\u5251\u82b1\u6735\u6735\uff0c\u53c8\u5982\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\uff0c \u98de\u6d12\u4e0b\u6765\u3002\u6076\u884c\u8005\u4e00\u58f0\u5389\u53eb\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u94ee\u94ee\u58f0\u54cd\uff0c\u539f\u7c73\u4ed6\u5df2\u53d1\u51fa\u4e86\u788e\u9aa8\u94b1\u9556\uff01 \u3000\u3000\u4f46\u89c1\u957f\u5b59\u5747\u91cf\u8eab\u56de\u52bf\u8f6c\uff0c\u4e24\u679a\u788e\u9aa8\u94b1\u9556\u8d34\u7740\u808b\u65c1\uff0c\u500f\u7136\u7a7f\u8fc7\uff1b\u63a5\u7740\u4e00\u6837\u5229\u5251\uff0c\u5c06\u5954 \u5411\u592a\u9633\u7a74\u7684\u4e00\u679a\u94b1\u9556\u78d5\u5f00\uff0c\u7acb\u5373\u811a\u5c16\u4e00\u70b9\uff0c\u65bd\u5c55\u8f7b\u529f\u63d0\u7eb5\u672f\u201c\u4e00\u9e64\u51b2\u5929\u201d\u7684\u7edd\u6280\uff0c\u5c06\u54c1 \u5b57\u5f62\u98de\u6765\u3001\u5954\u5411\u4e0b\u76d8\u7684\u4e09\u679a\u788e\u9aa8\u94b1\u9556\u4e5f\u4e00\u5e76\u8ba9\u8fc7\u4e86\uff01 \u3000\u3000\u5c4b\u5185\u7684\u957f\u5b59\u5144\u59b9\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u53ea\u542c\u5f97\u6bd2\u89c2\u97f3\u9ad8\u5362\u559d\u91c7\uff0c\u8d5e\u9053\uff1a\u201c\u957f\u5b59\u5148\u751f\uff0c\u95ea\u907f \u6697\u5668\u7684\u529f\u592b\uff0c\u8981\u63a8\u4f60\u72ec\u6b65\u6b66\u6797\u4e86\uff01\u201d\u957f\u5b59\u5747\u91cf\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u6ce8\u5b9a\u6076\u884c\u8005\u7684\u624b\u81c2\uff0c \u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5fae\u4e00\u6296\u624b\uff0c\u602a\u58f0\u6447\u66f3\uff0c\u53c8\u662f\u4e09\u679a\u94b1\u9556\uff0c\u8054\u7fe9\u6253\u81f3\u3002\u6bd2\u89c2\u97f3\u7684\u8bf4\u8bdd\uff0c\u662f\u6545\u610f\u60f3 \u5f15\u957f\u5b59\u5747\u91cf\u5206\u5fc3\uff0c\u957f\u5b59\u5747\u91cf\u53ef\u4e0d\u4e0a\u5f53\uff0c\u51dd\u795e\u5e94\u654c\uff0c\u8f9f\u6e05\u94b1\u9556\u7c73\u52bf\uff0c\u4e00\u4e2a\u201c\u956b\u91cc\u85cf\u8eab\u201d\u95ea \u8fc7\u7b2c\u4e00\u94b1\u9556\uff0c\u53cd\u5251\u4e00\u8361\uff0c\u8fce\u5411\u7b2c\u4e8c\u679a\u94b1\u9556\uff0c\u970e\u7136\u95f4\uff0c\u201c\u94ee\u201d\u4e00\u58f0\uff0c\u7b2c\u4e09\u679a\u94b1\u9556\u7adf\u662f\u540e\u53d1 \u5148\u81f3\uff0c\u4e0e\u7b2c\u4e8c\u679a\u4e00\u64e6\uff0c\u7acb\u5373\u6539\u4e86\u65b9\u5411\uff0c\u95ea\u7535\u822c\u7684\u659c\u98de\u52b2\u5c04\uff0c\u88ad\u5411\u957f\u5b59\u5747\u91cf\u9888\u540e\u7684\u201c\u4e2d\u6ce8 \u7a74\uff01\u201d\u957f\u5b59\u5747\u91cf\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u4f46\u89c9\u51c9\u98ce\u63a0\u9876\u800c\u8fc7\uff0c\u65e0\u6687\u5ba1\u89c6\uff0c\u5251\u628a\u5012\u7ffb\uff0c\u5c06\u7b2c \u4e8c\u679a\u94b1\u9556\u6253\u843d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6bd2\u89c2\u97f3\u54c8\u54c8\u5927\u7b11\uff0c\u8fd9\u65f6\u957f\u5b59\u5747\u91cf\u624d\u53d1\u89c9\u81ea\u5df1\u7684\u5934\u53d1\u3002 \u3000\u3000\u5df2\u88ab\u950b\u5229\u7684\u996f\u9556\u524a\u53bb\u4e00\u7f15\uff0c\u957f\u5b59\u5747\u91cf\u52c3\u7136\u5927\u6012\uff0c\u559d\u4e00\u58f0\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u201d\u5f80 \u6697\u5668\u56ca\u4e2d\u4e00\u63a2\uff0c\u4e00\u626c\u624b\u4e5f\u98de\u51fa\u4e86\u4e09\u67c4\u5315\u9996\uff0c\u540c\u65f6\u8eab\u5f62\u75be\u8d77\uff0c\u4e00\u62db\u201c\u5929\u6cb3\u5012\u6302\u201d\uff0c\u957f\u5251\u51cc \u7a7a\u51fb\u4e0b\uff0c\u51e0\u4e4e\u4e0e\u90a3\u51e0\u67c4\u5315\u9996\uff0c\u540c\u65f6\u5230\u8fbe\uff01 \u3000\u3000\u6076\u884c\u8005\u6599\u4e0d\u5230\u957f\u5b59\u5747\u91cf\u7c73\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u4ed6\u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u521a\u521a\u5c06\u90a3\u4e09\u67c4\u5315\u9996\u51fb \u843d\uff0c\u957f\u5b59\u5747\u91cf\u7684\u957f\u5251\u5df2\u523a\u5230\u80f8\u524d\u3002\u4f46\u542c\u5f97\u53c8\u662f\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u957f\u5b59\u5747\u91cf\u8d81\u7740 \u4ed6\u90a3\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\u62db\u6570\u5df1\u8001\uff0c\u5982\u540c\u5f3a\u5f29\u4e4b\u672a\u4e4b\u9645\uff0c\u731b\u7684\u51cc\u7a7a\u4e0b\u51fb\uff0c\u4e00\u5251\u9707\u5f00\u4ed6\u7684\u6212\u5200\uff0c \u6296\u624b\u4e4b\u95f4\uff0c\u5251\u5c16\u75be\u70b9\u4ed6\u8eab\u4e0a\u7684\u4e09\u5904\u5927\u7a74\uff01 \u3000\u3000\u6076\u884c\u8005\u8fde\u8fde\u543c\u53eb\uff0c\u6709\u5982\u72fc\u66dd\uff0c\u4f0f\u5728\u5730\u535c\u6eda\u7ffb\uff0c\u7ffb\u51fa\u4e09\u4e08\u591a\u8fdc\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8dc3 \u4e86\u8d77\u7c73\uff0c\u5c45\u7136\u53c8\u662f\u4e00\u628a\u94b1\u9556\u6253\u51fa\u3002\u539f\u6765\u6076\u884c\u8005\u548c\u6bd2\u89c2\u97f3\u90e8\u6709\u201c\u79fb\u5bab\u6362\u7a74\u201d\u7684\u529f\u5927\uff0c\u5927\u7a74 \u867d\u88ab\u523a\u4e2d\uff0c\u5374\u53ea\u4e0d\u8fc7\u53d7\u4e86\u5916\u4f24\uff0c\u5e76\u672a\u80fd\u5236\u4ed6\u6b7b\u547d\uff01 \u3000\u3000\u4f46\u89c1\u94b1\u9556\u75be\u81f3\uff0c\u6709\u5982\u51b0\u96f9\u4e71\u843d\uff0c\u957f\u5b59\u5747\u91cf\u6599\u4e0d\u5230\u6076\u884c\u8005\u7adf\u6709\u8fd9\u6837\u7684\u529f\u592b\uff0c\u88ab\u4ed6\u6253\u5f97 \u624b\u5fd9\u811a\u4e71\u3002\u5e78\u800c\u957f\u5b59\u5747\u91cf\u7684\u5185\u529f\u3001\u8f7b\u529f\u548c\u507f\u8fd8\u6cd5\u90fd\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u6216\u7528\u8896\u62c2\uff0c\u6216 \u7528\u5251\u5288\uff0c\u6216\u4ee5\u4fca\u5de7\u7684\u8eab\u5f62\u907f\u5f00\uff0c\u6076\u884c\u8005\u90a3\u4e00\u628a\u94b1\u9556\uff0c\u7adf\u7136\u65e0\u5948\u4ed6\u4f55\u3002\u53ef\u662f\u957f\u5b59\u5747\u91cf\u4e5f\u5df2 \u7d2f\u5f97\u5598\u6c14\u4e86\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u6bd2\u89c2\u97f3\u5ffd\u5730\u683c\u683c\u4e00\u7b11\uff0c\u79fb\u6b65\u5411\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u957f\u5b59\u5148\u751f\u597d\u672c\u4e8b\uff0c\u8ba9\u6211\u4e5f\u6765 \u9886\u6559\u3002\u6211\u7684\u900f\u7a74\u795e\u9488\u548c\u4ed6\u7684\u788e\u9aa8\u94b1\u9556\u5927\u4e0d\u76f8\u540c\uff0c\u900f\u7a74\u795e\u9488\u7ec6\u5982\u725b\u6bdb\uff0c\u5c04\u51fa\u4e4b\u65f6\u65e0\u58f0\u65e0\u606f\uff0c \u751a\u4e0d\u597d\u6321\u3002\u957f\u5b59\u5148\u751f\uff0c\u4f60\u53ef\u8981\u591a\u4e9b\u5c0f\u5fc3\u624d\u597d\uff01\u201d\u8bf4\u7684\u8bdd\u6bd2\u8fa3\u65e0\u6bd4\uff0c\u4f46\u5374\u8bed\u610f\u6bb7\u6bb7\uff0c\u5173\u6000 \u5907\u4e88\u3002\u4e0a\u5b98\u5a49\u513f\u542c\u5f97\u6bdb\u9aa8\u8038\u7136\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5973\u9b54\u5934\u8c8c\u7f8e\u5fc3\u72e0\uff0c\u679c\u7136\u4e0d\u6127\u6bd2\u89c2\u97f3\u7684\u79f0\u53f7\uff01\u201d \u3000\u3000\u6bd2\u89c2\u97f3\u90a3\u201c\u5c0f\u5fc3\u201d\u4e24\u5b87\u521a\u521a\u51fa\u53e3\uff0c\u624b\u8155\u500f\u7ffb\uff0c\u628a\u5251\u4e00\u6325\uff0c\u5176\u75be\u5982\u7535\uff0c\u5237\u7684\u4e00\u62db\u201c\u9f99 \u5973\u7a7f\u9488\u201d\u4fbf\u5954\u957f\u5b59\u5747\u91cf\u7684\u201c\u80a9\u4e95\u7a74\u201d\u75be\u523a\u3002\u8fd9\u4e00\u62db\u9aa4\u7136\u53d1\u96be\u7684\u51cc\u5389\u5251\u62db\uff0c\u6362\u662f\u4ed6\u4eba\uff0c\u975e \u7acb\u5373\u6bd9\u4e8e\u5251\u4e0b\u4e0d\u53ef\uff0c\u5e78\u800c\u957f\u5b59\u5747\u91cf\u65e9\u77e5\u9053\u6bd2\u89c2\u97f3\u7684\u9b3c\u9b45\u4f0e\u4fe9\uff0c\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u7acb\u5373\u80a9\u5934\u4e00 \u8038\uff0c\u6bd2\u89c2\u97f3\u7684\u957f\u5251\u523a\u4e86\u4e2a\u7a7a\uff0c\u5251\u5c16\u6070\u6070\u4ece\u79bb\u80a9\u4e09\u5bf8\u4e4b\u5904\u5b88\u8fc7\u3002\u957f\u5b59\u5747\u91cf\u5237\u5730\u4e00\u5251\u6233\u4e0b\uff0c \u8fd9\u4e00\u5251\u8001\u8fa3\u975e\u5e38\uff0c\u62ff\u634f\u65f6\u5019\u3002\u6070\u5230\u597d\u5904\uff0c\u957f\u5b59\u58c1\u5728\u95e8\u5185\u77a7\u5f97\u559c\u5f62\u4e8e\u8272\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9 \u4e00\u5251\u51c6\u80fd\u628a\u8fd9\u5973\u9b54\u5934\u7684\u624b\u81c2\u5207\u4e0b\uff01\u201d \u3000\u3000\u5df2\u77e5\u8fd9\u4e24\u4eba\u7684\u5251\u6cd5\u90fd\u662f\u795e\u5947\u83ab\u6d4b\uff0c\u957f\u5b59\u58c1\u5fc3\u5ff5\u65b9\u52a8\uff0c\u4f46\u89c1\u5251\u5149\u8fde\u95ea\uff0c\u6bd2\u89c2\u9752\u4e00\u58f0\u5a07 \u7b11\uff1a\u201c\u597d\u4fca\u7684\u8eab\u624b\uff0c\u597d\u4fca\u7684\u5251\u6cd5\uff01\u201d\u5251\u5149\u4eba\u5f71\u4e4b\u4e2d\uff0c\u957f\u5b59\u58c1\u770b\u4e5f\u770b\u4e0d\u6e05\u695a\uff0c\u4ed6\u4eec\u4e24\u4eba\u5df1 \u4ea4\u6362\u4e86\u56db\u4e94\u8fa3\u62db\uff0c\u500f\u7136\u95f4\u53c8\u7531\u5408\u800c\u5206\uff0c\u62b1\u5251\u5bf9\u7acb\u3002 \u3000\u3000\u4f46\u542c\u5f97\u6bd2\u89c2\u9752\u7eb5\u58f0\u957f\u7b11\uff1a\u201c\u957f\u5b59\u5148\u751f\u3001\u8fd9\u56de\u4f60\u53ef\u5f53\u771f\u8981\u5c0f\u5fc3\u4e86\uff01\u201d\u957f\u5b59\u5747\u91cf\u864e\u76ee\u5706 \u7741\uff0c\u84e6\u5730\u4e00\u58f0\u5927\u559d\uff0c\u5149\u53d1\u5236\u4eba\uff0c\u957f\u5251\u5982\u98ce\u3002 \u3000\u3000\u6b3a\u8eab\u75be\u8fdb\uff0c\u201c\u91d1\u9e21\u593a\u7c9f\u201d\u201c\u54ea\u54a4\u95f9\u6d77\u201d\uff0c\u4e00\u8fde\u4e24\u8bb0\u6740\u624b\u795e\u62db\uff0c\u4e0a\u523a\u53cc\u76ee\uff0c\u4e0b\u523a\u80f8\u819b\u3002 \u6bd2\u89c2\u97f3\u4e00\u58f0\u5a07\u7b11\uff0c\u7565\u4e00\u6643\u80a9\uff0c\u8f7b\u98d8\u98d8\u7684\u968f\u7740\u5251\u98ce\u76f4\u6643\u4e0b\u53bb\uff0c\u500f\u5730\u53cd\u624b\u4e00\u5251\uff0c\u559d\u4e00\u58f0\uff1a \u201c\u7740\uff01\u201d\u5251\u5149\u4e2d\u6742\u4e86\u51e0\u679a\u900f\u7a74\u795e\u9488\uff0c\u540c\u65f6\u5c04\u51fa\uff01 \u3000\u3000\u957f\u5b59\u5747\u91cf\u65e9\u6599\u5979\u6709\u6b64\u4e00\u7740\uff0c\u4ed6\u90a3\u4e24\u62db\u6740\u6cd5\u867d\u7136\u51cc\u5389\uff0c\u5b9e\u662f\u653b\u4e2d\u5e26\u5b88\uff0c\u4e25\u5bc6\u975e\u5e38\uff0c\u4e00 \u89c1\u52bf\u5934\u4e0d\u5bf9\uff0c\u4e09\u5c3a\u9752\u950b\uff0c\u65e9\u5c31\u5708\u4e86\u56de\u6765\uff0c\u4fe8\u5982\u6d8c\u8d77\u4e86\u4e00\u56fd\u62a4\u8eab\u7684\u94f6\u8679\uff0c\u4f46\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c \u6bd2\u89c2\u97f3\u90a3\u51e0\u679a\u900f\u7a74\u795e\u9488\uff0c\u4e00\u4eba\u5251\u5149\u5708\u91cc\uff0c\u5df2\u88ab\u7ede\u6210\u7c89\u5c51\u3002\u957f\u5b59\u5747\u91cf\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u900f\u7a74\u4f38 \u4ec0\uff0c\u4e0d\u8fc7\u5982\u65af\uff01\u9ed4\u9a74\u6280\u5c3d\uff0c\u4f55\u4f59\u8001\u592b\uff01\u201d \u3000\u3000\u6bd2\u89c2\u97f3\u9762\u8272\u4e00\u6c89\uff0c\u968f\u5373\u53c8\u5a07\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u7b11\u4f60\u4e95\u5e95\u4e4b\u86d9\uff0c\u4f60\u53cd\u7b11\u6211\u9ed4\u9a74\u6280\u5c3d\uff0c\u6211\u7eb5 \u662f\u4e00\u7247\u6148\u60b2\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u65bd\u5c55\u6740\u624b\u4e86\uff01\u201d\u957f\u5251\u7eb5\u6a2a\u6325\u970d\uff0c\u75be\u5982\u98ce\u96e8\uff0c\u900f\u7a74\u7985\u9488\uff0c\u4e5f\u4e0d\u65ad\u7684 \u6742\u5728\u5251\u5149\u4e4b\u4e2d\u53d1\u51fa\u3002\u4f46\u89c1\u5979\u624b\u6307\u8fde\u5f39\uff0c\u6709\u65f6\u58f0\u4e1c\u6253\u897f\uff0c\u6709\u65f6\u6307\u5357\u6253\u5317\uff0c\u55e4\u55e4\u4e4b\u58f0\uff0c\u4e0d\u7edd \u4e8e\u8033\u3002\u957f\u5b59\u5747\u91cf\u51dd\u7985\u5bf9\u4ed8\uff0c\u4ed7\u7740\u6781\u7cbe\u7eaf\u7684\u542c\u98ce\u8fa8\u5668\u4e4b\u672f\uff0c\u542c\u90a3\u6781\u5fae\u7ec6\u800c\u53c8\u6781\u6df7\u6742\u7684\u55e4\u55e4 \u58f0\u54cd\uff0c\u6709\u65f6\u4e5f\u5f04\u4e0d\u6e05\u5979\u7684\u65b9\u5411\uff0c\u4e0d\u7981\u5fc3\u795e\u6e10\u4e71\u3002 \u3000\u3000\u957f\u5b59\u5747\u91cf\u4e0e\u6076\u884c\u8005\u6076\u6597\u4e4b\u65f6\uff0c\u5df2\u8017\u4e86\u4e0d\u5c11\u771f\u529b\uff0c\u8fd9\u65f6\u4e3a\u5382\u62b5\u5fa1\u90a3\u900f\u7a74\u795e\u9488\uff0c\u53ea\u6709\u65bd \u5c55\u5185\u5bb6\u771f\u529b\uff0c\u5c06\u5251\u5149\u5c3d\u91cf\u5c55\u5e03\uff0c\u53d8\u6210\u62a4\u8eab\u7684\u5149\u7eb2\uff0c\u66f4\u662f\u8017\u8d39\u7cbe\u529b\u3002\u6bd2\u89c2\u97f3\u4e0d\u4f46\u6697\u5668\u5389\u5bb3\uff0c \u5251\u6cd5\u4ea6\u6781\u51cc\u5389\u3002\u53ea\u653b\u4e0d\u5b88\uff0c\u5a01\u529b\u66f4\u5f3a\u3002\u6597\u4e86\u4e94\u5341\u7c73\u62db\uff0c\u5df2\u662f\u62a2\u4e86\u4e0a\u98ce\uff0c\u8feb\u5f97\u957f\u5b59\u5747\u91cf\u8fde \u8fde\u540e\u9000\u3002\u6bd2\u89c2\u97f3\u5982\u5f71\u968f\u5f62\uff0c\u6b65\u6b65\u7d27\u8feb\uff0c\u5251\u5251\u4e0d\u79bb\u957f\u5b59\u5747\u91cf\u8981\u5bb3\uff0c\u84e6\u7136\u95f4\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u8001 \u5934\u513f\uff0c\u4eca\u65e5\u4f60\u7684\u6b7b\u671f\u5230\u4e86\uff01\u201d \u3000\u3000\u957f\u5b59\u5747\u91cf\u4e0e\u6bd2\u89c2\u97f3\u6fc0\u6218\u4e4b\u65f6\uff0c\u6076\u884c\u8005\u5df2\u8c03\u5300\u547c\u5438\uff0c\u7406\u597d\u521b\u4f24\uff0c\u8fd9\u65f6\u6b63\u62e6\u7740\u957f\u5b59\u5747\u91cf \u7684\u9000\u8def\u3002\u6bd2\u89c2\u97f3\u90a3\u4e00\u5362\u957f\u7b11\uff0c\u6b63\u662f\u7ed9\u4ed6\u7684\u6697\u53f7\uff0c\u7b11\u58f0\u4e00\u53d1\uff0c\u6076\u884c\u8005\u7acb\u5373\u817e\u8eab\u98de\u8d77\uff0c\u94ee\u94ee \u94ee\uff0c\u4e09\u679a\u788e\u9aa8\u94b1\u9556\u5148\u53d1\uff0c\u968f\u5373\u6212\u5200\u5288\u4e0b\uff1b\u800c\u4e0e\u6b64\u540c\u65f6\uff0c\u6bd2\u89c2\u97f3\u624b\u638c\u4e00\u626c\uff0c\u628a\u638c\u4e2d\u7684\u6570\u5341 \u679a\u900f\u7a74\u795e\u9488\uff0c\u4e00\u9f50\u5c04\u51fa\uff0c\u4fe8\u5982\u4e00\u84ec\u94f6\u96e8\uff0c\u5f53\u5934\u7f69\u4e0b\uff01\u8fd9\u4e00\u6765\uff0c\u957f\u5b59\u5747\u91cf\u88ab\u4e24\u5927\u9b54\u5934\u524d\u540e \u5939\u653b\uff0c\u7eb5\u6709\u5929\u5927\u795e\u901a\uff0c\u4e5f\u96be\u6d3b\u547d\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u72c2\u7b11\uff0c\u63a5\u7740\u4e00\u58f0\u60e8\u547c\uff0c\u4e00\u6761\u9ed1\u5f71\uff0c\u75be\u5982\u5954\u9a6c\uff0c\u5ffd\u5730\u6251 \u5728\u957f\u5b59\u5747\u5de8\u8eab\u4e0a\uff0c\u66ff\u4ed6\u6321\u4e86\u90a3\u4e00\u84ec\u900f\u7a74\u795e\u9488\uff0c\u53cd\u811a\u4e00\u52fe\uff0c\u53c8\u628a\u6076\u884c\u8005\u52fe\u8dcc\uff0c\u8fd9\u4eba\u6b63\u662f\u9556 \u5e08\u674e\u5143\uff0c\u4ed6\u62fc\u4e86\u6027\u547d\uff0c\u62a4\u53cb\u4f24\u654c\uff0c\u4e24\u5927\u9b54\u5934\uff0c\u4e5f\u4e0d\u7981\u5927\u60ca\u5931\u8272\uff01 \u3000\u3000\u95e8\u5185\u7684\u957f\u5b59\u5144\u59b9\u4e0e\u4e0a\u5b98\u5a49\u513f\u4ea6\u662f\u5927\u60ca\u5931\u8272\uff0c\u957f\u5b59\u6cf0\u201c\u7830\u201d\u7684\u4e00\u62f3\uff0c\u6253\u5f00\u5927\u95e8\uff0c\u518d\u4e5f \u987e\u4e0d\u5f97\u8001\u7236\u7684\u5429\u5490\uff0c\u51b2\u4e86\u51fa\u6765\uff0c\u4f46\u542c\u5f97\u6bd2\u89c2\u97f3\u4e00\u58f0\u5389\u7b11\uff0c\u62d6\u4e86\u6076\u884c\u6709\u8df3\u6492\u90a3\u6a2a\u8fc7\u5c71\u8c37\u7684 \u67b6\u7a7a\u6808\u9053\uff0c\u75be\u5954\u800c\u4e0b\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4e0d\u89c1\u8e2a\u5f71\u3002\u674e\u5143\u8eba\u5728\u5730\u4e0a\uff0c\u8eab\u4f53\u63d2\u6ee1\u94f6\u9488\uff0c\u6b7b\u72b6\u6781\u60e8\uff01 \u7236\u4eb2\u9762\u8272\u60e8\u767d\uff0c\u4e0d\u77e5\u6709\u5426\u53d7\u4f24\uff1f \u3000\u3000\u957f\u5b59\u5747\u91cf\u62db\u4e86\u62db\u4e01\uff0c\u628a\u4e00\u53cc\u513f\u5973\u5524\u5230\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u628a\u8fd9\u4f4d\u4e49\u58eb\u57cb\u4e86\uff0c\u8bb0\u7740\u4ee5 \u540e\u5e74\u5e74\u4eca\u65e5\uff0c\u7ed9\u4ed6\u4e0a\u575f\u201d\u201d\u56de\u8fc7\u5934\u6765\uff0c\u5bf9\u4e0a\u5b98\u5a49\u513f\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u548c\u6211\u5230\u5c4b\u5b50\u91cc\u4e48\u8bf4 \u8bdd\u3002\u201d\u795e\u60c5\u6c89\u91cd\u4e4b\u6781\uff0c\u770b\u6765\u662f\u6709\u6781\u91cd\u5927\u7684\u4e8b\u60c5\u5429\u5490\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u516d\u4e0a\u516b\u843d\uff0c\u548c\u957f\u5b59\u5747\u91cf\u56de\u5230\u5bb6\u4e2d\uff0c\u957f\u5b59\u5747\u91cf\u5148\u770b\u90a3\u8eba\u5728\u5e8a\u4e0a\u7684\u90d1\u6e29\uff0c \u90d1\u6e29\u5fae\u7af9\u5598\u606f\uff0c\u4ecd\u7136\u672a\u9192\u3002\u957f\u5b59\u5747\u91cf\u51c4\u6006\u8bf4\u9053\uff1a\u201c\u8001\u670b\u53cb\uff0c\u6211\u987e\u4e0d\u5f97\u4f60\u4e86\uff01\u201d\u968f\u5373\u628a\u5927 \u95e8\u7d27\u95ed\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c\u8fd9\u4e8b\u60c5\u6211\u672c\u60f3\u518d\u8fc7\u4e24\u5e74\uff0c\u5f85\u4f60\u6210\u5e74\uff0c\u518d\u544a\u8bc9\u4f60\uff0c\u73b0\u5728\u662f\u7b49 \u4e0d\u53ca\u4e86\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u60ca\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u6211\u5df2\u4e2d\u4e86\u4e24\u679a\u900f\u7a74\u795e\u8ba1\uff0c\u7eb5\u662f\u4e0d \u6b7b\uff0c\u4ea6\u6210\u6b8b\u5e9f\uff0c\u800c\u4e14\u975e\u6709\u5341\u5e74\u4e4b\u529b\uff0c\u4e0d\u80fd\u6062\u590d\u6b66\u529f\u3002\u8fd9\u8fd8\u662f\u4e49\u58eb\u674e\u5143\uff0c\u66ff\u6211\u6321\u4e86\u4e00\u6321\uff0c \u624d\u80fd\u4fa5\u5e78\u9003\u751f\u3002\u201d\u4e0a\u5b98\u5a49\u51e0\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u60ca\u5f97\u5446\u4e86\u3002\u957f\u5b59\u5747\u91cf\u7eed\u9053\uff1a\u201c\u4e3a\u4e86\u9632\u5907\u90a3\u5973 \u9b54\u5934\u5189\u6765\uff0c\u660e\u65e5\u6211\u4fbf\u642c\u5bb6\uff0c\u6211\u4e0e\u4f60\u53ea\u6709\u4eca\u65e5\u76f8\u805a\u4e86\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201d\u4f2f\u4f2f\u642c\u5230\u54ea\u91cc\uff0c \u4f84\u5973\u81ea\u5f53\u968f\u53bb\u4f8d\u5949\u3002\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u662f\u6211\u4e0d\u8981\u4f60\uff0c\u4f60\u6709\u66f4\u7d27\u8981\u7684\u4e8b\u60c5\u4e48\u529e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u72c2\u8df3\uff0c\u6697\u6697\u731c\u5230\u8fd9\u5fc5\u5b9a\u548c\u5979\u7684\u8eab\u4e16\u6709\u5173\uff0c\u679c\u7136\u957f\u5b59\u5747\u91cf\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c \u4f60\u77e5\u9053\u4f60\u7956\u7236\u548c\u7236\u4eb2\u662f\u600e\u6837\u6b7b\u7684\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u542c\u738b\u5b89\u8bf4\uff0c\u662f\u5389\u75ab\u6b7b\u7684\uff0c\u201d\u957f\u5b59\u5747 \u91cf\u53f9\u53e3\u6c14\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u90a3\u662f\u4e00\u573a\u5389\u75ab\uff0c\u6b66\u5219\u5929\u4fbf\u662f\u64ad\u75ab\u7684\u5973\u9b54\u3002\u8fd9\u4e00\u573a\u6240\u75ab\u5bb3\u6b7b\u5510\u5ba4\u65e0 \u6570\u738b\u5b59\u8d35\u65cf\uff0c\u4e49\u58eb\u5fe0\u81e3\uff0c\u4e5f\u5bb3\u6b7b\u4e86\u4f60\u7684\u7956\u7236\u3001\u7236\u4eb2\uff01\u4ed6\u4eec\u90fd\u662f\u6b66\u5219\u5929\u6740\u6389\u7684\uff01\u201d \u3000\u3000\u4e03\u5e74\u6765\u7684\u7591\u56e2\u500f\u7136\u6311\u7834\uff0c\u7aef\u7684\u6709\u5982\u6674\u5929\u9739\u96f3\uff0c\u9707\u5f97\u4e0a\u5b98\u5a49\u513f\u51e0\u4e4e\u5931\u4e86\u77e5\u89c9\uff0c\u5446\u5446\u7684 \u671b\u7740\u957f\u5b59\u5747\u91cf\uff0c\u7adf\u81ea\u54ed\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4e03\u5e74\u6765\u957f\u5b59\u5747\u91cf\u5728\u4e0a\u5b98\u5a49\u513f\u9762\u524d\uff0c\u53cd\u590d\u7684\u6570\u8bf4\u6b66\u5219\u5929\u7684\u7f6a\u6076\uff0c\u5df2\u4e0d\u77e5\u8bf4\u4e86\u51e0\u5343\u4e07\u904d\uff0c \u4e0a\u5b98\u5a49\u513f\u5bf9\u6b66\u5219\u5929\u81ea\u65e0\u597d\u611f\uff0c\u4f46\u5979\u81ea\u8d1f\u662f\u8d85\u8d8a\u7537\u513f\u7684\u5973\u4e2d\u624d\u5b50\uff0c\u6545\u6b64\u5bf9\u4e8e\u4e00\u4e2a\u80fd\u538b\u5012\u5929 \u4e0b\u7537\u4eba\uff0c\u505a\u5230\u5973\u7687\u5e1d\u7684\u6b66\u5219\u5929\u5374\u4e5f\u7981\u4e0d\u4f4f\u5728\u5fc3\u5e95\u91cc\u6697\u6697\u4f69\u670d\uff0c\u7136\u800c\u6599\u4e0d\u5230\u8fd9\u4e2a\u65e2\u4ee4\u81ea\u5df1 \u618e\u6068\uff0c\u4e49\u4ee4\u81ea\u5df1\u4f69\u670d\u7684\u5973\u7687\u5e1d\uff0c\u7adf\u662f\u81ea\u5df1\u7684\u6740\u7236\u4ec7\u4eba\uff01 \u3000\u3000\u957f\u5b59\u5747\u91cf\u629a\u7740\u4e0a\u5b98\u5a49\u513f\u7684\u5934\u53d1\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e03\u5e74\u4e4b\u524d\uff0c\u4f60\u7684\u7956\u7236\u4e0a\u5b98\u4eea\u5b98\u62dc\u897f\u53f0 \u6043\u90ce\uff0c\u7236\u4eb2\u4e0a\u5b98\u5ead\u829d\u662f\u592a\u5b50\u4f34\u8bfb\uff0c\u90a3\u65f6\u5148\u592a\u5b50\u674e\u5f18\u8fd8\u5728\uff0c\u770b\u4e0d\u8fc7\u6b66\u5219\u5929\u6b3a\u538b\u4ed6\u7684\u7236\u7687\uff0c \u66f4\u6050\u60e7\u6bcd\u4eb2\u4e13\u6743\uff0c\u884c\u5c06\u7be1\u593a\u674e\u5bb6\u7684\u5927\u4e0b\uff0c\u56e0\u6b64\u5b81\u613f\u5192\u4e0d\u5b5d\u4e4b\u540d\uff0c\u6697\u4e2d\u529d\u7236\u7687\u5e9f\u7acb\u6bcd\u540e\uff0c \u5e76\u548c\u4e00\u73ed\u4eb2\u4fe1\u7684\u5927\u81e3\u5546\u8bae\uff0c\u51c6\u5907\u4e00\u4e3e\u5c3d\u6b7c\u6bcd\u540e\u7684\u515a\u7fbd\uff0c\u9ad8\u5b97\u7687\u5e1d\u7ed9\u592a\u5b50\u8bf4\u52a8\uff0c\u53eb\u4f60\u7956\u7236 \u8d77\u8349\u5e9f\u7acb\u7684\u8bcf\u4e66\uff0c\u90a3\u6599\u4e8b\u673a\u4e0d\u5bc6\uff0c\u88ab\u6b66\u5219\u5929\u77e5\u9053\uff0c\u6df1\u591c\u641c\u5bab\uff0c\u5f53\u7740\u9ad8\u5b97\u7687\u5e1d\u9762\u524d\uff0c\u5728\u4f60 \u7956\u7236\u8eab\u4e0a\u5c06\u8bcf\u4e66\u641c\u51fa\uff0c\u7b2c\u4e8c\u65e5\u4f60\u7956\u7236\u3001\u7236\u4eb2\u5c31\u5e76\u906d\u8bdb\u622e\uff0c\u4f60\u6bcd\u4eb2\u4e5f\u88ab\u6ca1\u5165\u5bab\u4e2d\u4e3a\u5974\uff0c\u4f60 \u672c\u6765\u4e5f\u5c06\u4e0d\u514d\uff0c\u5e78\u5f97\u738b\u5b89\u65e9\u77e5\u6d88\u606f\uff0c\u624d\u5e26\u4f60\u9003\u51fa\u6765\uff01\u201d\uff08\u636e\u5510\u53f2\u6240\u8f7d\uff0c\u4e0a\u5b98\u4eea\u7236\u5b50\u88ab\u6740 \u540e\uff0c\u4e0a\u5b98\u5a49\u513f\u4e5f\u88ab\u6ca1\u5165\u5bab\u4e2d\u4e3a\u5974\uff0c\u81f3\u5341\u56db\u5c81\u65f6\uff0c\u59cb\u88ab\u6b66\u5219\u5927\u53d1\u73b0\u5176\u624d\uff0c\u547d\u4e3a\u8bb0\u5ba4\uff0c\u5341\u5206 \u91cd\u7528\u3002\u4f46\u4e0a\u5b98\u5a49\u513f\u5929\u624d\u6a2a\u9698\uff0c\u5e7c\u8d1f\u8bd7\u540d\uff0c\u6b66\u5219\u5929\u4f55\u4ee5\u81f3\u5979\u5341\u56db\u5c81\u65f6\u59cb\u53d1\u73b0\uff1f\u6cbb\u53f2\u8005\u4ea6\u6709 \u4eba\u6000\u7591\u3002\u6211\u5199\u4e0a\u5b98\u5a49\u513f\u8fd9\u4e03\u5e74\u4e2d\u907f\u96be\u957f\u5b59\u5747\u91cf\u4e4b\u5bb6\uff0c\u5f53\u7136\u662f\u201c\u5c0f\u8bf4\u5bb6\u8a00\u201d\uff0c\u4e0d\u80fd\u4f5c\u4e3a\u4fe1 \u53f2\uff0c\u4f46\u4e5f\u662f\u6839\u636e\u8fd9\u4e2a\u6000\u7591\u51fa\u53d1\u7684\u3002\uff09 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u6211\u7684\u6bcd\u4eb2\u2026\u2026\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u738b\u5b89\u8bf4\u4f60\u6bcd\u4eb2\u4e5f\u5728\u5389\u75be\u4e2d\u6b7b\u53bb\uff0c\u90a3 \u662f\u514d\u4f60\u4f24\u5fc3\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u60f3\u8d77\u7956\u7236\u3001\u7236\u4eb2\u60e8\u906d\u6740\u622e\uff0c\u6bcd\u4eb2\u5165\u5bab\u4e3a\u5974\uff0c\u66f4\u662f\u6b7b\u4e0d\u5982\u751f\uff0c\u5fc3 \u5982\u5200\u5272\uff0c\u62fc\u547d\u54ac\u7740\u5634\u5507\uff0c\u4e0d\u4f7f\u6ef4\u4e0b\u6cea\u6765\uff0c\u5411\u957f\u5b59\u5747\u91cf\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u60b2\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5927\u6069\u4e0d\u8a00\u62a5\uff0c\u5927\u75db\u4e0d\u5f92\u60b2\u3002\u4f2f\u4f2f\u7684\u5927\u6069\u5927\u5fb7\uff0c\u6211\u4e2a\u751f\u662f\u65e0\u6cd5\u62a5\u7b54\u7684\u4e86\uff0c\u4f46\u613f\u80fd\u624b \u5203\u8fd9\u4e2a\u7978\u5bb3\u5929\u4e0b\u7684\u5973\u9b54\u738b\u2026\u2026\u201d\u957f\u5b59\u5747\u91cf\u5c55\u7709\u7b11\u9053\uff1a\u201c\u82e5\u80fd\u5982\u6b64\uff0c\u6211\u548c\u5929\u4e0b\u7684\u5fe0\u81e3\u4e49\u58eb\uff0c \u90fd\u8981\u611f\u8c22\u4e8e\u4f60\uff0c\u4e5f\u4e0d\u6789\u6211\u8fd9\u51e0\u5e74\u6765\u7684\u5fc3\u8840\u4e86\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u5982\u4eca\u6211\u624d\u77e5\u9053\u4f2f \u4f2f\u7684\u82e6\u5fc3\uff0c\u53ef\u60dc\u6211\u4e00\u5411\u4e0d\u542c\u4f60\u7684\u6559\u8bf2\uff0c\u6ca1\u6709\u5b66\u5230\u4f60\u7684\u6b66\u529f\u3002\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u5e72\u8fd9\u7b49\u5927 \u4e8b\uff0c\u6700\u8981\u6c89\u7740\u575a\u6bc5\uff0c\u4e5f\u4e0d\u662f\u5f92\u6043\u6b66\u529f\u7684\u3002\u58c1\u513f\u3001\u6cf0\u513f\u7684\u5251\u6cd5\u6bd4\u4f60\u5f3a\uff0c\u4f46\u82e5\u8bf4\u5230\u8981\u523a\u6740\u4e07 \u4e58\u4e4b\u541b\uff0c\u4ed6\u4eec\u5c31\u6311\u4e0d\u8d77\u8fd9\u526f\u62c5\u5b50\uff01\u597d\uff0c\u5a49\u513f\uff0c\u4f60\u4eca\u65e5\u5c31\u8d70\u5427\uff0c\u6211\u8fd9\u67c4\u968f\u8eab\u7684\u5b9d\u5251\u9001\u7ed9\u4f60 \u4e86\u3002\u201d\u89e3\u4e0b\u5b9d\u5251\u4e4b\u65f6\uff0c\u540c\u65f6\u6389\u4e0b\u4e86\u4e00\u5c01\u4fe1\u3002 \u3000\u3000\u90a3\u662f\u6b66\u5219\u5929\u6258\u90d1\u6e29\u4ea4\u7ed9\u5e9f\u592a\u5b50\u674e\u8d24\u7684\u4e66\u4fe1\uff0c\u674e\u5143\u518d\u8f6c\u6258\u957f\u5b59\u5747\u91cf\u8f6c\u4ea4\u7684\uff0c\u957f\u5b59\u5747\u91cf \u6068\u6068\u7684\u5c06\u90a3\u5c01\u4fe1\u62fe\u4e86\u8d77\u6765\uff0c\u6b63\u5f85\u628a\u5b83\u6495\u4e2a\u7a00\u70c2\uff0c\u4ee5\u6cc4\u5fc3\u5934\u4e4b\u6124\uff0c\u4e0a\u5b98\u5a49\u513f\u4e00\u65f6\u597d\u5947\uff0c\u9053\uff1a \u201c\u4e14\u77a7\u77a7\u5979\u5199\u4e9b\u4ec0\u4e48\uff1f\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u5c31\u8ba9\u4f60\u8ba4\u5f97\u8fd9\u5973\u9b54\u738b\u7684\u5b57\u8ff9\uff0c\u5c06\u6765\u6216\u8bb8 \u6709\u7528\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5c06\u4fe1\u62c6\u5f00\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u5b57\u4ed8\u8d24\u513f\u5982\u6664\uff1a\u4f60\u5e7c\u597d\u8bfb\u4e66\uff0c\u672c\u5f53\u5609\u8bb8\u3002\u6240 \u60dc\u8005\u4f60\u4e0d\u77e5\u6d3b\u8bfb\u53e4\u5360\u4e66\uff0c\u800c\u53cd\u4e3a\u53e4\u4e66\u6240\u540c\uff0c\u4f60\u5e94\u77e5\u5148\u7687\u4e4b\u9053\uff0c\u672a\u5fc5\u80fd\u884c\u4e8e\u4eca\u4e16\uff0c\u82e5\u4f7f\u4f60 \u4e3a\u5e1d\uff0c\u6ce5\u53e4\u4e0d\u5316\uff0c\u7978\u5bb3\u5929\u4e0b\uff0c\u6bd4\u4ece\u4e0d\u8bfb\u4e66\u8005\u4e4b\u60af\u66f4\u70c8\uff0c\u53ef\u4e0d\u614e\u54c9\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7b2c\u4e00\u4e2a\u5ff5\u5934\u662f\uff1a\u201c\u5979\u81ea\u5df1\u7978\u5bb3\u5929\u4e0b\uff0c\u53cd\u800c\u62ff\u6765\u6559\u8beb\u513f\u5b50\uff01\u201d\u518d\u800c\u4e00\u60f3\uff0c\u8fd9 \u4e9b\u8bdd\u7adf\u662f\u5927\u6709\u89c1\u8bc6\uff0c\u4e0d\u80fd\u56e0\u4eba\u5e9f\u8a00\u3002\u518d\u770b\u4e0b\u53bb\u9053\uff1a\u201c\u4f60\u5e7c\u957f\u5bab\u4e2d\uff0c\u4e0d\u77e5\u7a3c\u6a2f\u8270\u96be\uff0c\u4e0d\u77e5 \u6c11\u95f4\u75be\u82e6\uff0c\u53d7\u7fa4\u5c0f\u4e4b\u5305\u56f4\uff0c\u6240\u601d\u8005\u552f\u6b32\u638c\u5929\u4e0b\u4e4b\u6743\uff0c\u4eab\u5929\u4e0b\u4e4b\u798f\uff0c\u543e\u53c8\u5fd9\u4e8e\u56fd\u4e8b\uff0c\u65e0\u6687 \u7ba1\u6559\uff0c\u4ee4\u4f60\u5982\u6b64\uff0c\u65e2\u6127\u4e14\u4f18\u3002\u5df4\u8700\u4eba\u60c5\u98ce\u4fd7\uff0c\u52e4\u52b3\u6734\u7d20\uff0c\u5947\u5c71\u5f02\u6c34\uff0c\u5929\u4e0b\u72ec\u7edd\uff0c\u6211\u4ee4\u4f60 \u8fdc\u9002\u5df4\u8700\uff0c\u5b9e\u671b\u4f60\u5584\u4f53\u543e\u5fc3\u3002\u52e4\u4ec6\u6c11\u60c5\uff0c\u53ef\u6d17\u4f60\u7ea8\u7ed4\u4e4b\u6c14\uff0c\u5947\u5c71\u5f02\u6c34\uff0c\u53ef\u5f00\u62d3\u4f60\u72ed\u7a84\u4e4b \u5fc3\u80f8\uff0c\u7236\u6bcd\u7231\u4e86\uff0c\u7231\u4ee5\u4e49\u65b9\uff0c\u4f60\u5f53\u6df1\u591c\u81ea\u601d\uff0c\u81ea\u52c9\u81ea\u52b1\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8bfb\u5230\u6b64\u6bb5\uff0c\u5446\u5446\u53d1\u6115\uff0c\u5fc3\u9053\uff1a\u201c\u6b66\u5219\u5929\u82e5\u771f\u5982\u6b64\uff0c\u5c82\u975e\u662f\u5723\u5e1d\u660e\u541b\uff1f\u4e0d\uff0c \u4e0d\uff0c\u5929\u4e0b\u7684\u5927\u5978\u5927\u6076\uff0c\u90fd\u662f\u8a00\u4f2a\u800c\u8fa9\u7684\u3002 \u3000\u3000\u6211\u600e\u80fd\u51ed\u5979\u4e00\u5c01\u4e66\u4fe1\uff0c\u5c31\u5fd8\u4e86\u7236\u6bcd\u4e4b\u4ec7\uff1f\u201d\u4f46\u518d\u4e00\u60f3\uff0c\u6b66\u5219\u5929\u5199\u8fd9\u4fe1\u65f6\uff0c\u7edd\u6599\u4e0d\u5230 \u4f1a\u7ed9\u5979\u4e0a\u5b98\u5a49\u513f\u770b\u5230\uff0c\u5979\u4f55\u5fc5\u6545\u4f5c\u6016\u8f9e\uff1f\u800c\u4e14\u6b66\u5219\u5929\u7684\u6587\u7b14\u867d\u662f\u6734\u5b9e\u65e0\u534e\uff0c\u5374\u4f3c\u5b57\u5b57\u51fa \u4e8e\u80ba\u8151\uff0c\u4e0a\u5b98\u5a49\u513f\u4e0d\u89c9\u4e00\u7247\u832b\u7136\uff0c\u518d\u8bfb\u4e0b\u53bb\u9053\uff1a\u201c\u6211\u5e74\u6e10\u8001\u8fc8\uff0c\u7231\u5b50\u8fdc\u79bb\uff0c\u5c82\u80fd\u65e0\u4f24\uff1f \u552f\u671b\u4f60\u6210\u6750\uff0c\u4e0d\u5f97\u4e0d\u5c14\uff0c\u6240\u613f\u8005\u4f60\u5584\u4f53\u543e\u610f\uff0c\u65e9\u65e5\u6210\u6751\uff0c\u5219\u6211\u4ed8\u6258\u6709\u4eba\uff0c\u518d\u4ea8\u5929\u4f26\u4e4b\u4e50\uff0c \u65af\u4e3a\u771f\u4e50\u3002\u8d24\u513f\uff0c\u52c9\u4e4e\u54c9\uff01\u53c8\uff0c\u4f60\u773c\u75be\u5982\u4f55\uff1f\u6bcf\u65e5\u6d17\u773c\uff0c\u4e0d\u53ef\u7a0d\u8f8d\uff0c\u8747\u5934\u5c0f\u5b57\uff0c\u66f4\u4e0d\u5b9c \u591a\u8bfb\u3002\u6bcd\u5631\u3002\u201d\u7231\u5b50\u4e4b\u60c5\uff0c\u6d0b\u6ea2\u7eb8\u4e0a\u3002\u82e5\u975e\u4e0a\u5b98\u5a49\u513f\u542c\u8fc7\u6b66\u5219\u5929\u66fe\u6bd2\u5bb3\u4eb2\u513f\u4e4b\u4e8b\uff0c\u8bfb\u4e86 \u8fd9\u4e00\u5c01\u4fe1\uff0c\u771f\u8981\u5f53\u5979\u662f\u96be\u5f97\u7684\u6148\u6bcd\uff01\u5982\u4eca\uff0c\u867d\u6709\u5148\u4eba\u4e4b\u5360\uff0c\u5979\u8fd8\u662f\u6367\u9996\u8fd9\u5c01\u4fe1\u6014\u7740\u4e86\u3002 \u3000\u3000\u5fcd\u89c1\u90d1\u6e29\u5728\u5e8a\u4e0a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5589\u5934\u5495\u5495\u4f5c\u54cd\uff0c\u957f\u5b59\u5747\u91cf\u795e\u8272\u60e8\u7136\uff0c\u77e5\u9053\u8fd9\u662f\u56de\u5149\u53cd\u7167 \u4e4b\u8c61\uff0c\u5fd9\u53eb\u5a49\u513f\u4e0a\u524d\uff0c\u5c06\u4ed6\u6276\u8d77\uff0c\u4e0a\u5b98\u5a49\u513f\u968f\u624b\u5c06\u4fe1\u585e\u5165\u8863\u5185\uff0c\u628a\u90d1\u6e29\u6276\u8d77\uff0c\u53ea\u89c1\u4ed6\u53cc \u773c\u5fae\u542f\uff0c\u4f4e\u58f0\u53f9\u9053\uff0c\u201c\u5929\u540e\u965b\u4e0b\uff0c\u6211\u8d1f\u4e86\u4f60\u7684\u5631\u6258\u4e86\u3002\u55ef\uff0c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u957f\u5b59\u5747\u91cf \u53eb\u9053\uff1a\u201c\u90d1\u5144\uff0c\u6211\u5728\u8fd9\u513f\uff01\u201d\u90d1\u6e29\u6162\u6162\u5f20\u5f00\u773c\u775b\uff0c\u77a7\u6d88\u695a\u4e86\u957f\u5b59\u5747\u91cf\uff0c\u4e5f\u4e0d\u77e5\u662f\u54ea\u91cc\u6765 \u7684\u6c14\u529b\uff0c\u6025\u5730\u6293\u5b9e\u4e86\u957f\u5b59\u5747\u91cf\u53cc\u624b\uff0c\u7528\u529b\u8bf4\u9053\uff1a\u201c\u957f\u5b59\u5144\uff0c\u6211\u4eec\u90fd\u9519\u4e86\uff01\u201d \u3000\u3000\u60f3\u4e0d\u5230\u90d1\u6e29\u4e00\u9192\uff0c\u5c31\u8bf4\u4e86\u8fd9\u6837\u4e00\u53e5\u6ca1\u5934\u6ca1\u8111\u7684\u6d3b\uff0c\u957f\u5b59\u5747\u91cf\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48 \u9519\u4e86\uff1f\u201d\u90d1\u6e29\u53cc\u4e86\u6500\u7740\u5e8a\u6cbf\uff0c\u597d\u50cf\u7aed\u529b\u652f\u6491\u81ea\u5df1\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u8be5\u53cd\u5bf9\u5929\u540e\uff0c\u6211 \u5982\u4eca\u65b9\u624d\u660e\u767d\uff0c\u6cbb\u7406\u5929\u4e0b\u8fd9\u4ed8\u91cd\u62c5\u5b50\uff0c\u53ea\u6709\u5927\u540e\u624d\u80fd\u6311\u5f97\u8d77\u6765\uff01\u201d\u957f\u5b59\u5747\u91cf\u7741\u5927\u4e86\u773c\u775b\uff0c \u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u53ea\u542c\u5f97\u90d1\u6e29\u53c8\u65ad\u65ad\u7eed\u7eed\u7684\u8bf4\u9053\uff0c\u201c\u957f\u5b59\u5144\uff0c\u6211\u81ea\u77e5\u6b7b\u671f\u4e0d\u8fdc\uff0c \u6211\u53ea\u6c42\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff01\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u90d1\u5144\u5429\u5490\uff0c\u5f1f\u8d74\u6c64\u8e48\u706b\u5728\u6240\u4e0d\u8f9e\uff0c\u90d1\u5144\uff0c\u4f60\u8bf7\u653e \u5fc3\u3002\u201d \u3000\u3000\u90d1\u6e29\u8138\u4e0a\u73b0\u51fa\u7b11\u5bb9\uff0c\u8bf4\u900d\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u7b54\u5e94\u4e86\uff1f\u6211\u6c42\u4f60\u51fa\u5c71\u8f85\u4f50\u5929\u540e\u965b\u4e0b\uff0c\u5929\u540e\u965b \u4e0b\u6ca1\u6709\u5fd8\u8bb0\u4f60\uff0c\u5979\u8bf4\u4f60\u662f\u4e00\u4e2a\u6709\u672c\u9886\u7684\u4eba\uff0c\u5c31\u53ef\u60dc\u773c\u5149\u5927\u77ed\u5c0f\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u5e76\u4e0d\u8981\u7d27\uff0c \u53ea\u8981\u4f60\u5728\u5929\u540e\u8eab\u8fb9\uff0c\u6e10\u6e10\u4f60\u5c31\u4f1a\u660e\u767d\u8fc7\u6765\u4e86\u3002\u201d\u957f\u5b59\u5747\u91cf\u6012\u6c14\u4e0a\u51b2\uff0c\u82e5\u975e\u90d1\u6e29\u662f\u4ed6\u7684\u8001 \u670b\u53cb\uff0c\u800c\u5de8\u53c8\u662f\u4e2a\u5782\u6b7b\u7684\u4eba\uff0c\u4ed6\u51e0\u4e4e\u5c31\u8981\u7834\u53e3\u5927\u9a82\uff0c\u4ed6\u659c\u773c\u4e00\u77a5\uff0c\u4f46\u89c1\u90d1\u6e29\u8138\u4e0a\u9732\u51fa\u671f \u5f85\u4e0e\u6073\u6c42\u7684\u795e\u60c5\uff0c\u800c\u4e14\u201c\u5929\u540e\u201d\u8fd9\u4e24\u4e2a\u5b57\u5728\u4ed6\u53e3\u4e2d\u8bf4\u51fa\uff0c\u7adf\u662f\u90a3\u6837\u7684\u81ea\u7136\uff0c\u90a3\u6837\u7684\u8654\u656c\uff01 \u957f\u5b59\u5747\u91cf\u54ac\u7d27\u5634\u5507\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u90d1\u5144\uff0c\u6211\u4ee5\u4e3a\u4f60\u662f\u6c42\u6211\u66ff\u4f60\u62a5\u4ec7\uff0c\u51b0\u77e5\u4e0d\u77e5\u9053\u662f\u8c01\u6697 \u51fb\u4f60\u7684\uff0c\u90a3\u5c31\u662f\u4f60\u7684\u5929\u540e\u965b\u535c\u201d\u90d1\u6e29\u5636\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u4f60\u6740\u4e86\u6211\u4e5f\u4e2a\u4fe1\uff0c\u5440\uff0c\u957f\u5b59 \u5144\uff0c\u4f60\u5230\u5e95\u8fd8\u662f\u56fa\u6267\u6210\u89c1\uff0c\u4e0d\u80af\u7b54\u5e94\u6211\u4e86\uff1f\u6211\uff0c\u6211\uff0c\u6b7b\u4e0d\u7791\u76ee\uff01\u201d\u529b\u7aed\u58f0\u5636\uff0c\u8bf4\u5b8c\u4e86\u8fd9 \u53e5\u8bdd\uff0c\u7adf\u5c14\u9616\u7136\u957f\u901d\uff01 \u3000\u3000\u957f\u5b59\u5747\u65e6\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u90d1\u5144\uff0c\u4f60\u7684\u786e\u662f\u6b7b\u4e0d\u7791\u76ee\uff0c\u8fde\u8c01\u662f\u4f60\u7684\u4ec7\u4eba\uff0c\u90fd\u4e0d\u77e5 \u9053\uff01\u4f60\u662f\u4e34\u6b7b\u7cca\u6d82\uff0c\u8ff7\u5931\u4e86\u672c\u6027\u4e86\uff01\u201d \u3000\u3000\u7136\u800c\u4e0a\u5b98\u5a49\u513f\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u90d1\u6e29\u4e34\u6b7b\u4e4b\u65f6\uff0c\u4e00\u70b9\u4e5f\u4e0d\u7cca\u6d82\uff0c\u5374\u53cd\u800c\u4ee4\u5f97\u4e0a\u5b98\u5a49\u513f \u7cca\u6d82\u4e86\uff01\u5979\u521a\u521a\u89e3\u5f00\u4e86\u4e03\u5e74\u6765\u6a2a\u585e\u80f8\u81c6\u7684\u7591\u56e2\uff0c\u660e\u767d\u4e86\u81ea\u5df1\u7684\u8eab\u4e16\u4e4b\u8c1c\uff1b\u5982\u4eca\u53c8\u538b\u4e0a\u4e86 \u66f4\u91cd\u7684\u7591\u4e91\uff0c\u9762\u5bf9\u7740\u4e00\u4e2a\u66f4\u590d\u6742\u96be\u89e3\u4e4b\u8c1c\uff1a\u6b66\u5219\u5929\uff0c\u5979\u5230\u5e95\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba\uff1f \u3000\u3000\u4e3a\u4ec0\u4e48\u90d1\u6e29\u5728\u4e34\u6b7b\u4e4b\u65f6\uff0c\u4e0d\u5148\u8ffd\u67e5\u81ea\u5df1\u7684\u4ec7\u4eba\uff1f\u751a\u81f3\u5bf9\u7740\u81ea\u5df1\u7684\u77e5\u5df1\uff0c\u8fde\u4e00\u70b9\u540e\u4e8b \u4e5f\u4e0d\u4ea4\u5f85\uff1f\u4e0d\u6302\u5ff5\u81ea\u5df1\u7684\u5bb6\u4eba\uff0c\u5374\u53cd\u800c\u6302\u5ff5\u6b66\u5219\u5929\uff1f\u4e3a\u4ec0\u4e48\u6b66\u5219\u5929\u80fd\u4ee4\u4ed6\u8fd9\u6837\u5fc3\u60a6\u8bda\u670d\uff1f \u4e00\u4e2a\u4eba\uff0c\u80fd\u4ee4\u522b\u4eba\u6b7b\u4e5f\u4e0d\u80fd\u5fd8\u8bb0\u7684\u4eba\uff0c\u600e\u4e48\u4f6f\u4e5f\u8be5\u6709\u70b9\u597d\u5904\u5427\uff1f\u4f46\u662f\u6b66\u5219\u5929\u5728\u957f\u5b59\u4f2f\u4f2f \u7684\u53e3\u4e2d\uff0c\u5374\u662f\u4e2a\u4e07\u6076\u4e0d\u8d66\u7684\u5973\u9b54\u738b\uff1f \u3000\u3000\u800c\u4e14\uff0c\u6700\u91cd\u8981\u7684\uff0c\u5979\u8fd8\u662f\u6740\u4e86\u81ea\u5df1\u7956\u7236\u548c\u7236\u4eb2\u7684\u4ec7\u4eba\uff0c\u82e5\u8bf4\u6b66\u5219\u5929\u662f\u4e2a\u597d\u4eba\uff0c\u90a3\u4e48\uff0c \u96be\u9053\u81ea\u5df1\u7684\u7956\u7236\u548c\u7236\u4eb2\u53cd\u800c\u662f\u574f\u4eba\u4e86\uff1f\u201c\u4e0d\uff0c\u4e0d\uff01\u7237\u7237\u548c\u7239\u7239\u65e0\u8bba\u5982\u4f55\u4e2a\u662f\u574f\u4eba\uff01\u201d\u5979 \u5fc6\u8d77\u4e86\u7956\u7236\u6148\u7965\u7684\u9762\u8c8c\uff0c\u7236\u4eb2\u5e7c\u65f6\u5019\u5bf9\u5979\u7684\u6559\u8bf2\u3002\u5979\u6240\u63a5\u89e6\u8fc7\u7684\uff0c\u8c01\u90fd\u79f0\u8d5e\u4ed6\u7684\u7956\u7236\u548c \u7236\u4eb2\u662f\u65e2\u535a\u5b66\u800c\u53c8\u6b63\u76f4\u7684\u5927\u81e3\uff0c\u81f3\u4e8e\u957f\u5b59\u4f2f\u4f2f\uff0c\u5979\u4e03\u5e74\u6765\u548c\u4ed6\u6829\u5904\uff0c\u8877\u5fc3\u4f69\u670d\uff0c\u82e5\u8bf4\u957f \u5b59\u6015\u6015\u662f\u4e2a\u574f\u4eba\uff0c\u5979\u6b7b\u4e5f\u4e0d\u80fd\u76f8\u4fe1\uff01 \u3000\u3000\u957f\u5b59\u5747\u91cf\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u56fd\u5bb6\u5c06\u4ea1\uff0c\u5fc5\u6709\u5996\u5b7d\u3002\u592a\u5b97\u7687\u5e1d\u4e1c\u5f81\u897f\u8ba8\uff0c\u5357\u5f81\u5317\u4f10\uff0c\u626b\u5e73 \u5341\u516b\u8def\u53cd\u571f\uff0c\u8d39\u5c3d\u65e0\u7a77\u5fc3\u529b\uff0c\u6323\u6765\u7684\u5927\u5510\u5929\u4e0b\uff0c\u94c1\u6876\u6c5f\u5c71\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u8fd9\u6837\u8f7b\u8f7b\u6613\u6613\u7684 \u4e27\u9001\u5728\u6b66\u5219\u5929\u5e72\u4e0a\u3002\u6211\u5fdd\u4e3a\u5148\u5e1d\u5927\u81e3\uff0c\u5c82\u80af\u5411\u8fd9\u5996\u5b7d\u4f4e\u5934\uff1f\u6211\u4e5f\u771f\u4e3a\u592a\u5b97\u7687\u5e1d\u4e0d\u503c\uff0c\u4ed6 \u8fd9\u6837\u82f1\u660e\uff0c\u5728\u665a\u5e74\u7684\u65f6\u5019\uff0c\u7adf\u4f1a\u88ab\u6b66\u5219\u5929\u8ff7\u60d1\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u542c\u8bf4\u6b66\u5219\u5929\u66fe\u505a\u8fc7\u592a\u5b97\u7687\u5e1d\u7684\u5983\u5ad4\uff0c\u90a3\u662f\u771f\u7684\u5417\uff1f\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a \u201c\u600e\u4e48\u4e0d\u771f\uff1f\u5979\u6700\u521d\u5165\u5bab\u7684\u65f6\u5019\uff0c\u88ab\u5c01\u4e3a\u2018\u624d\u4eba\u2019\uff0c\u6ca1\u591a\u4e45\uff0c\u592a\u5b97\u7687\u5e1d\u6b7b\u4e86\uff0c\u5979\u548c\u4e00\u4e9b \u5983\u5ad4\u88ab\u64b5\u51fa\u5bab\u5ef7\uff0c\u5728\u611f\u4e1a\u5bfa\u505a\u5c3c\u59d1\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u9ad8\u5b97\u7687\u5e1d\u4f1a\u770b\u4e0a\u5979\uff0c\u5c06\u5979\u4ece\u611f\u4e1a\u5bfa\u63a5\u56de \u6765\uff0c\u53c8\u5c01\u4e3a\u2018\u662d\u4eea\u2019\uff0c\u9ad8\u5b97\u7687\u5e1d\u662f\u592a\u5b97\u7687\u5e1d\u7684\u4eb2\u751f\u513f\u5b50\uff0c\u513f\u5b50\u8981\u7236\u4eb2\u7684\u59ec\u59be\u505a\u59bb\uff0c\u8fd9\u4e43 \u662f\u672c\u671d\u7684\u4e00\u5927\u4e11\u4e8b\uff0c\u6211\u5f53\u65f6\u8fd8\u5728\u671d\u4e3a\u5b98\uff0c\u5c31\u56e0\u4e3a\u6c14\u5979\u4e0d\u8fc7\uff0c\u624d\u544a\u8001\u56de\u5bb6\u3002\u201d \u3000\u3000\u957f\u5b59\u5747\u91cf\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8\u9053\uff1a\u201c\u82e5\u7136\u9ad8\u5b97\u7687\u5e1d\u53ea\u628a\u5979\u5f53\u4f5c\u5ba0\u5983\u770b\u5f85\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4ed6\u5374 \u628a\u56fd\u5bb6\u7684\u5927\u6743\u90fd\u4ea4\u4ed8\u7ed9\u5979\uff0c\u5c06\u6b63\u5bab\u5a18\u5a18\u5e9f\u4e86\uff0c\u7acb\u5979\u4e3a\u540e\uff0c\u5982\u4eca\u8fde\u6c5f\u5c71\u4e5f\u6539\u4e86\u59d3\u6b66\u7684\u4e86\u3002\u201d \u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u4e5f\u6240\u7239\u7239\u8bf4\u8fc7\uff0c\u542c\u8bf4\u662f\u738b\u7687\u540e\u5148\u9677\u5bb3\u5979\u3002\u201d \u3000\u3000\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u662f\u56e0\u4e3a\u738b\u7687\u540e\u5df1\u770b\u51fa\u5979\u7684\u91ce\u5fc3\uff0c\u60f3\u628a\u5979\u9664\u6389\u3002\u53ef\u60dc\u738b\u7687\u540e \u6240\u7528\u7684\u65b9\u6cd5\u592a\u7b28\u4e86\uff0c\u5979\u542c\u4fe1\u672f\u58eb\u4e4b\u8a00\uff0c\u96d5\u4e86\u4e00\u4e2a\u6728\u5076\uff0c\u5f53\u4f5c\u6b66\u5219\u5929\u7684\u66ff\u8eab\uff0c\u4ee5\u4e3a\u7528\u7b26\u5492 \u53ef\u4ee5\u5c06\u5979\u5492\u6b7b\uff0c\u90a3\u77e5\u53cd\u800c\u7ed9\u6b66\u5219\u5929\u62ff\u4f4f\u4e86\u628a\u67c4\uff0c\u8feb\u9ad8\u5b97\u7687\u5e1d\u5c06\u5979\u5e9f\u4e86\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8 \u9053\uff1a\u201c\u6b66\u5219\u5927\u7684\u5fc3\u72e0\u624b\u8fa3\uff0c\u771f\u662f\u51fa\u4e8e\u5e38\u4eba\u60f3\u50cf\u4e4b\u5916\uff0c\u5979\u7684\u59d0\u59d0\u97e9\u56fd\u592b\u4eba\u79c1\u901a\u7687\u5e1d\uff0c\u88ab\u5979 \u77e5\u9053\uff0c\u7acb\u523b\u628a\u5979\u7684\u59d0\u59d0\u6bd2\u6b7b\u4e86\u3002\u513f\u5b50\u53cd\u5bf9\u5979\uff0c\u8fde\u513f\u5b50\u4e5f\u6bd2\u6b7b\u4e86\u3002\u8fd9\u4f4d\u88ab\u6bd2\u6b7b\u7684\u592a\u4e86\u662f\u5979 \u7684\u5927\u513f\u5b50\u674e\u5f18\uff0c\u73b0\u5728\u88ab\u7728\u5230\u5df4\u5dde\u7684\u5e9f\u592a\u5b50\u662f\u5979\u7684\u6b21\u4e8e\u674e\u8d24\u3002\u7b2c\u4e8c\u5b50\u674e\u54f2\u505a\u4e86\u51e0\u5929\u7687\u5e1d\uff0c \u53c8\u88ab\u5979\u8d2c\u4e3a\u5362\u9675\u738b\u8fdc\u6ef4\u6f5e\u5dde\u3002\u73b0\u5728\u5728\u5979\u8eab\u8fb9\u7684\u662f\u7b2c\u56db\u4e2a\u513f\u4e86\uff0c\u540d\u53eb\u674e\u65e6\u3002\u542c\u8bf4\u4e5f\u5df2\u88ab\u8d2c \u4e3a\u9884\u4e0a\uff0c\u5e76\u4e14\u8981\u4ed6\u6539\u59d3\u6b66\uff0c\u65b9\u8bb8\u4ed6\u505a\u201c\u7687\u55e3\u201d\uff0c\u771f\u771f\u662f\u8352\u8c2c\u4e4b\u6781\uff01\u5979\u638c\u6743\u4ee5\u6765\uff0c\u6740\u4e86\u4e09 \u5341\u4eba\u5bb6\u8d35\u65cf\u5927\u81e3\uff0c\u6211\u7684\u5802\u5144\u957f\u5b59\u65e0\u5fcc\u548c\u4f60\u7684\u7956\u7236\u3001\u7236\u4eb2\u5c31\u662f\u5979\u6740\u7684\uff01\u201d \u3000\u3000\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u672c\u6765\u6709\u5927\u534a\u662f\u4e0a\u5b98\u5a49\u513f\u65e9\u5df2\u77e5\u9053\u7684\uff0c\u73b0\u5728\u518d\u542c\u4e00\u901a\uff0c\u66f4\u89c9\u5165\u8033\u60ca\u5fc3\uff0c\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u6b66\u5219\u5927\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u5f53\u771f\u662f\u7f44\u5357\u5c71\u4e4b\u7af9\uff0c\u4e66\u7f6a\u65e0\u7a77\uff01\u626c\u4e1c\u6d77\u4e4b\u6ce2\uff0c\u6da4\u6076\u4e0d\u5c3d\uff01 \u600e\u6837\u4e5f\u8fa8\u89e3\u4e0d\u4e86\u6c34\u3002\u6211\u5c82\u80fd\u56da\u4e00\u5c01\u4e66\u4fe1\u548c\u90d1\u6e29\u4e34\u6b7b\u4e4b\u8a00\uff0c\u5c31\u5c06\u5979\u9976\u6055\uff1f\u201d\u5fc3\u5fd7\u4e00\u51b3\uff0c\u6602 \u5934\u8bf4\u9053\uff1a\u201c\u6211\u542c\u4f2f\u4f2f\u7684\u8bdd\uff0c\u4e00\u5b9a\u8981\u5c06\u5979\u624b\u5203\uff0c\u4e3a\u7236\u6bcd\u62a5\u4ec7\uff01\u201d \u3000\u3000\u957f\u5b59\u5747\u91cf\u5fae\u7b11\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u53bb\u5427\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u62dc\u4e86\u56db\u62dc\u3002 \u3000\u3000\u4ece\u540e\u95e8\u51fa\u53bb\uff0c\u6b63\u4e0b\u5c71\u7684\u8def\u4e0a\uff0c\u56de\u5934\u9065\u671b\uff0c\u5fc3\u4e2d\u4e07\u611f\u4ea4\u96c6\uff0c\u4e0d\u80dc\u8f9b\u9178\u3002\u8fd9\u65f6\u957f\u5b59\u5144\u59b9 \u6b63\u5728\u5c71\u4e0a\u7ed9\u674e\u5143\u5014\u571f\u3002 \u3000\u3000\u4e0a\u5bab\u5a49\u513f\u60f3\u8d77\u957f\u5b59\u5144\u59b9\u5bf9\u5979\u7684\u597d\u5904\uff0c\u60f3\u56de\u53bb\u4e0e\u4ed6\u4eec\u9053\u522b\uff0c\u53c8\u6050\u614c\u66f4\u60f9\u4f24\u5fc3\uff0c\u60f3\u4e86\u4e00 \u60f3\uff0c\u8fd8\u662f\u8d70\u4e86\u3002\u80cc\u540e\u9690\u9690\u4f20\u6765\u957f\u5b59\u6cf0\u7684\u957f\u541f\uff1a\u201c\u53f6\u4e0b\u6d1e\u5ead\u521d\uff0c\u601d\u541b\u4e07\u91cc\u4f59\uff0c\u9732\u6d53\u9999\u88ab\u51b7\uff0c \u6708\u843d\u9526\u5c4f\u865a\u2026\u2026\u201d\u541f\u8bf5\u7684\u7adf\u7136\u5c31\u662f\u5979\u65e9\u4e0a\u6240\u505a\u7684\u8bd7\u53e5\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u4e00\u7247\u6005\u60af\uff0c\u6025\u6025\u4e0b \u5c71\u3002 \u3000\u3000\u65f6\u5e8f\u6b63\u662f\u66ae\u6625\u4e09\u6708\uff0c\u83ba\u98de\u8349\u957f\uff0c\u51fa\u4ea9\u95f4\u79be\u524d\u8301\u5bc6\uff0c\u4e00\u7247\u9752\u78a7\uff0c\u4e0a\u5b98\u5a49\u513f\u8fd9\u4e03\u5e74\u6765\u5e7d \u5c45\u5c71\u4e0a\uff0c\u51e0\u66fe\u89c1\u8fc7\u8fd9\u7b49\u7f8e\u5999\u81ea\u7136\u7684\u4e61\u6751\u666f\u8272\uff0c\u5fc3\u60c5\u7a0d\u7a0d\u5bbd\u8212\uff0c\u653e\u76ee\u6d4f\u89c8\uff0c\u5c71\u6e05\u6c34\u79c0\uff0c\u7530 \u4ea9\u7eb5\u6a2a\uff0c\u5c71\u95f4\u6709\u91c7\u8336\u59d1\u5a18\u7684\u6b4c\u58f0\uff0c\u7530\u5934\u4e0a\u6709\u513f\u7ae5\u5b09\u620f\uff0c\u6a35\u4e8e\u8377\u9504\uff0c\u519c\u592b\u628a\u7281\uff0c\u6cbf\u9014\u6240\u89c1\uff0c \u7adf\u662f\u4e00\u7247\u592a\u5e73\u7684\u666f\u8c61\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u7a0b\uff0c\u8def\u65c1\u6709\u4e00\u5ea7\u8336\u4ead\uff0c\u4e0a\u5b98\u5a49\u513f\u5fae\u611f\u75b2\u6e34\uff0c\u4fbf\u8fdb\u8309\u4ead\u6b47\u811a\uff0c\u5356\u8336\u7684\u662f\u4e2a\u767d\u53d1 \u8427\u8427\u7684\u8001\u4eba\uff0c\u7cbe\u795e\u5374\u5f88\u5065\u70c1\uff0c\u62db\u547c\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u59d1\u5a18\u662f\u54ea\u4e2a\u6751\u5b50\u7684\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u80e1\u8bcc \u9053\uff1a\u201c\u6211\u662f\u4ece\u5e7f\u5143\u6765\uff0c\u5230\u5df4\u5dde\u4eba\u6295\u4eb2\u7684\u3002\u201d\u90a3\u8001\u4eba\u7b11\u9053\uff0c\u201c\u602a\u4e0d\u5f97\u9762\u751f\uff0c\u539f\u6765\u662f\u5916\u53bf\u6765 \u7684\u3002\u8fd9\u4e24\u5e74\u6bd4\u8f83\u592a\u5e73\uff0c\u82e5\u5728\u4ee5\u524d\uff0c\u5355\u8eab\u7684\u59d1\u5a18\uff0c\u4e0d\u6562\u51fa\u8fdc\u95e8\u5462\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u548c\u4ed6\u95f2\u804a\uff0c\u7b11\u800c\u95ee\u9053\uff1a\u201c\u542c\u8001\u4e08\u6240\u8bf4\uff0c\u5149\u666f\u8fc7\u5f97\u8fd8\u4e0d\u9519\u5427\uff1f\u201d \u90a3\u8001\u4eba\u70b9\u70b9\u5934\u9053\uff0c\u201c\u8bf4\u600e\u6837\u597d\u4e5f\u4e0d\u89c1\u5f97\uff0c\u4e0d\u8fc7\u4e24\u9910\u7c97\u8336\u6de1\u996d\uff0c\u5012\u662f\u4e0d\u7528\u6101\u4e86\u3002\u55ef\uff0c\u6211\u5e74 \u7eaa\u5df2\u8001\uff0c\u6709\u4e24\u987f\u996d\u5403\uff0c\u4e5f\u5f88\u6ee1\u610f\u5566\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6bd4\u8d77\u4ee5\u524d\uff0c\u90a3\u662f\u597d\u5f97\u591a\u4e86\u3002\u201d\u4e0a\u5b98\u5a49\u513f \u7b11\u9053\uff1a\u201c\u542c\u4f60\u6240\u8bf4\uff0c\u5f53\u4eca\u7684\u5973\u7687\u5e1d\u53cd\u800c\u6bd4\u4ee5\u524d\u7684\u7537\u7687\u5e1d\u597d\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u4eba\u4e5f\u7b11\u9053\uff1a\u201c\u53ef\u4e2a\u662f\u5417\uff1f\u6211\u4eec\u6751\u5b50\u91cc\u6709\u597d\u4e9b\u8bfb\u4e66\u7684\u5148\u751f\u90fd\u5728\u5492\u9a82\u5f53\u4eca\u7684\u5973\u7687\u5e1d\uff0c \u6211\u4eec\u5e84\u7a3c\u6c49\u5374\u4f46\u613f\u8001\u5929\u4fdd\u4f51\u5979\u591a\u6d3b\u51e0\u5e74\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u90a3\u540d\u4eba\u9053\uff1a\u201c\u6211 \u4eec\u8001\u767e\u59d3\u4e0d\u7ba1\u8c01\u505a\u7687\u5e1d\uff0c\u7537\u7684\u4e5f\u597d\uff0c\u5973\u7684\u4e5f\u597d\uff0c\u4f46\u6c42\u65e5\u5b50\u8fc7\u5f97\u7a0d\u4e3a\u597d\u4e9b\uff0c\u5c31\u5fc3\u6ee1\u610f\u8db3\u3002 \u4ee5\u524d\u6536\u5272\u4e00\u77f3\u8c37\u5b50\u8981\u7eb3\u4e09\u6597\u79df\u60a6\uff0c\u73b0\u5728\u53ea\u8981\u4e00\u6597\u534a\uff0c\u6bd4\u4ee5\u524d\u5c11\u4e86\u4e00\u534a\u54e9\u3002\u6700\u597d\u7684\u662f\uff0c\u73b0 \u5728\u4e0d\u51c6\u5bcc\u8c6a\u4e4b\u5bb6\u5f3a\u5356\u5f3a\u4e70\uff0c\u4e0d\u8bba\u4f60\u600e\u6837\u7a77\uff0c\u4e00\u4efd\u53e3\u5206\u7530\u603b\u662f\u6709\u7684\uff0c\u53ea\u8981\u52e4\u8015\u5584\u7ec7\uff0c\u65e5\u5b50 \u4e5f\u5c31\u53ef\u4ee5\u5bf9\u4ed8\u8fc7\u4e86\u3002\u201d\u539f\u6765\u5510\u592a\u5b97\u5f00\u56fd\u521d\u5e74\uff0c\u56e0\u4e3a\u5730\u5e7f\u4eba\u7a00\uff0c\u65bd\u884c\u7684\u662f\u201c\u5747\u7530\u5236\u5ea6\u201d\uff0c \u7537\u5b50\u5341\u516b\u5c81\u4ee5\u4e0a\u7ed9\u7530\u4e00\u767e\u524d\uff0c\u516b\u5341\u82d7\u662f\u201c\u53e3\u5206\u7530\u201d\uff0c\u4e8c\u5341\u4ea9\u662f\u201c\u6c38\u6536\u7530\u201d\uff0c\u6c38\u4e1a\u7530\u5728\u8eab \u6b7b\u4e4b\u540e\u53ef\u4ee5\u7531\u5b50\u5b59\u7ee7\u627f\uff0c\u53e3\u5206\u7530\u5219\u7531\u5b98\u5bb6\u6536\u56de\u8f6c\u7ed9\u522b\u4eba\uff0c\u540e\u6765\u8c6a\u5f3a\u517c\u5e76\uff0c\u5747\u7530\u5236\u65bd\u884c\u6ca1 \u6709\u591a\u4e45\u4fbf\u540d\u5b58\u5b9e\u4ea1\uff0c\u6240\u6709\u7530\u5730\u51c6\u8bb8\u81ea\u7531\u4e70\u5356\uff0c\u8bb8\u591a\u7a77\u4eba\u8fde\u201c\u53e3\u5206\u7530\u201d\u4e5f\u5f7c\u5bcc\u8c6a\u4e4b\u5bb6\u6043\u52bf \u5f3a\u4e70\u53bb\u4e86\u3002\u5230\u4e86\u6b66\u5219\u5929\u638c\u6743\uff0c\u4e25\u7981\u4e70\u5356\u7530\u5730\uff0c\u53e6\u5916\u5be1\u5987\u65e0\u4f9d\u7684\u4e5f\u6709\u4e09\u5341\u4ea9\u201c\u53e3\u5206\u7530\u201d\u5206\uff0c \u56e0\u6b64\u5728\u6709\u5510\u4e00\u4ee3\uff0c\u4ee5\u6b66\u5219\u5929\u7684\u65f6\u671f\uff0c\u519c\u6751\u6700\u4e3a\u5174\u65fa\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u542c\u4e86\u8fd9\u4e00\u756a\u8bdd\uff0c\u4e0d\u89c9\u5446\u5446\u53d1\u6115\u3002 \u3000\u3000\u90a3\u8336\u4ead\u4e3b\u4eba\u53c8\u7b11\u9053\uff1a\u201c\u5f53\u4eca\u5973\u7687\u5e1d\u5728\u4f4d\uff0c\u4f60\u4eec\u59d1\u5a18\u4eec\u53ef\u5f97\u610f\u5566\uff0c\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a \u201c\u5979\u505a\u4e86\u5973\u7687\u5e1d\uff0c\u96be\u9053\u5929\u4e0b\u7684\u5973\u4eba\u90fd\u6cbe\u4e86\u5979\u7684\u5149\u4e0d\u6210\uff0c\u4e3a\u4ec0\u4e48\u5f97\u610f\uff1f\u201d\u90a3\u8001\u4eba\u7b11\u9053\uff1a \u201c\u54c8\uff0c\u5c31\u662f\u6cbe\u4e86\u5979\u7684\u5149\u3002\u59d1\u5a18\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u6211\u542c\u54b1\u6751\u5b50\u91cc\u7684\u6559\u4e66\u5148\u751f\u8bf4\uff0c\u5929\u540e\u5df2\u4e0b \u4e86\u547d\u4ee4\uff0c\u5973\u4eba\u6709\u672c\u9886\u7684\uff0c\u4e5f\u4e00\u6837\u53ef\u4ee5\u505a\u5b98\uff0c\u542c\u8bf4\u5c06\u6765\u8fd8\u8981\u5f00\u5973\u79d1\u5462\u3002\u54b1\u6751\u5b50\u91cc\u6709\u4e9b\u59d1\u5a18\uff0c \u5df2\u5435\u7740\u8981\u5ff5\u4e66\u4e86\uff0c\u5c06\u6765\u597d\u53bb\u5e94\u8003\uff0c\u8bfb\u4e66\u7684\u5148\u751f\u4eec\u5927\u6447\u5176\u5934\uff0c\u8bf4\u4ec0\u4e48\u4ee5\u524d\u7684\u5723\u8d24\u6709\u8bdd\uff0c\u5973 \u5b50\u65e0\u624d\u4fbf\u662f\u5fb7\uff0c\u6b66\u5219\u5927\u505a\u4e86\u7687\u5e1d\uff0c\u5929\u7ffb\u5730\u8986\uff0c\u8fde\u5723\u8d24\u7684\u8bdd\u4e5f\u53cd\u8fc7\u6765\u4e86\u3002\u8fd8\u6709\u54e9\uff0c\u4ee5\u524d\u5728 \u54b1\u4eec\u6751\u5b50\u91cc\uff0c\u505a\u4e08\u592b\u7684\u6253\u8001\u5a46\uff0c\u90a3\u662f\u7a00\u677e\u5bfb\u5e38\u7684\u4e8b\u60c5\uff0c\u73b0\u5728\u561b\uff0c\u5a46\u5a18\u4eec\u53ee\u795e\u6c14\u8d77\u6765\u4e86\uff0c \u8bf4\u5973\u4eba\u8fde\u7687\u5e1d\u90fd\u53ef\u4ee5\u505a\u5f97\uff0c\u4e3a\u4ec0\u4e48\u8981\u53d7\u7537\u4eba\u7684\u6b3a\u8d1f\uff0c\u8fd9\u4e24\u5e74\u6765\uff0c\u6751\u5b50\u91cc\u6253\u8001\u5a46\u7684\u4e8b\u60c5\u4e5f \u5c11\u4e86\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6751\u4e86\u91cc\u7684\u8bfb\u4e66\u5149\u751f\u5927\u7ea6\u53c8\u8981\u4e0d\u773c\u6c14\u4e86\uff1f\u201d\u90a3\u8001\u4eba\u9053\uff1a \u201c\u53ef\u4e0d\u662f\u5417\uff1f\u4ed6\u4eec\u8bf4\u4ec0\u4e48\u4e09\u7eb2\u4e94\u5e38\u4e4b\u4e2d\uff0c\u4fbf\u6709\u4e00\u6761\u662f\u2018\u592b\u4e3a\u59bb\u7eb2\u2019\uff0c\u73b0\u5728\u4e5f\u53cd\u8fc7\u6765\u5566\u3002 \u4e0d\u6b62\u8bfb\u4e66\u5148\u751f\uff0c\u6709\u597d\u4e9b\u7537\u5b50\u6c49\u4e5f\u4e0d\u670d\u6c14\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u7b11\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u90a3\u8001\u4eba\u54c8\u5495\u7b11\u9053\uff1a \u201c\u6211\u7684\u8001\u4f34\u513f\u65e9\u6b7b\u6389\u4e86\uff0c\u518d\u8bf4\uff0c\u5979\u751f\u524d\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u6ca1\u6709\u548c\u5979\u6253\u8fc7\u67b6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5477\u4e86\u53e3\u8336\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u6751\u5b50\u91cc\u7684\u8bfb\u4e66\u5148\u751f\uff0c\u8fd8\u6709\u4ec0\u4e48\u9a82\u6b66\u5219\u5929\u7684\uff1f\u201d\u90a3 \u8001\u4eba\u9053\uff1a\u201c\u8fd9\u53ef\u591a\u4e86\u3002\u4e0d\u8fc7\u9a82\u5f97\u6700\u51f6\u7684\u6709\u4e24\u4ef6\u4e8b\u60c5\uff0c\u7b2c\u4e00\u662f\u9a82\u5979\u8352\u6deb\u65e0\u9053\uff0c\u7528\u4ed6\u4eec\u7684\u8bdd \u8bf4\uff0c\u5c31\u662f\u2018\u79fd\u4e71\u5bab\u5ef7\u2019\uff0c\u7528\u6211\u4eec\u7684\u8bdd\u8bf4\uff0c\u5c31\u662f\u516c\u5f00\u517b\u6c49\u3002\u7b2c\u4e8c\u4ef6\u5462\u662f\u8bf4\u5979\u6b8b\u66b4\uff0c\u4e71\u6740\u4eba\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u674f\u8138\u98de\u7ea2\uff0c\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u4e24\u4ef6\u4e8b\u60c5\uff0c\u603b\u4e0d\u80fd\u8bf4\u5979\u597d\u4e86\uff1f\u201d\u90a3\u8001\u4eba\u9053\uff1a \u201c\u5973\u7687\u5e1d\u517b\u4e0d\u517b\u6c49\u5b50\u6211\u4eec\u4e0b\u77e5\u9053\u3002\u4e0d\u8fc7\u6211\u4eec\u5e84\u7a3c\u6c49\u5012\u662f\u53e6\u6709\u8bae\u8bba\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff0c\u201c\u600e \u4e48\uff1f\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u4ee5\u524d\u7684\u7537\u7687\u5e1d\u9664\u4e86\u4e09\u5b98\u516d\u9662\uff0c\u8fd8\u6709\u65e0\u6570\u5bab\u5a25\uff0c\u6bcf\u4e09\u5e74\u8fd8\u8981\u6311\u9009\u79c0\u5973\uff0c \u54c8\uff0c\u90a3\u65f6\u5019\u6bcf\u9022\u6311\u9009\u79c0\u5973\u4e4b\u671f\uff0c\u53ef\u628a\u6211\u4eec\u5bb3\u60e8\u5566\uff0c\u505a\u7236\u6bcd\u7684\u5fd9\u7740\u5ac1\u5973\u513f\uff0c\u8fd8\u5f97\u5e94\u4ed8\u5b98\u5e9c \u7684\u52d2\u7d22\u3002\u73b0\u5728\u5973\u7687\u5e1d\uff0c\u7eb5\u7b97\u5979\u517b\u4e86\u51e0\u4e2a\u6c49\u5b50\uff0c\u603b\u6ca1\u6709\u6311\u9009\u79c0\u7537\u5440\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u4e00\u4e07\u4e2a\u4e0d\u4ee5\u4e3a\u7136\uff0c\u4f46\u5374\u4e5f\u4e0d\u7981\u7fdf\u7136\u800c\u60ca\uff1b\u539f\u6765\u8001\u767e\u59d3\u7684\u770b\u6cd5\u4e0e\u8bfb\u4e66\u4eba \u7684\u770b\u6cd5\uff0c\u5305\u62ec\u957f\u5b59\u4f2f\u4f2f\u4e0e\u5979\u81ea\u5df1\u5728\u5185\uff0c\u6709\u8fd9\u6837\u5927\u7684\u5dee\u522b\uff01 \u3000\u3000\u90a3\u8001\u4eba\u53c8\u9053\uff1a\u201c\u8bf4\u5230\u4e71\u6740\u4eba\u561b\uff0c\u542c\u8bf4\u5979\u6740\u7684\u90fd\u662f\u738b\u5b59\u8d35\u65cf\uff0c\u6216\u8005\u505a\u5927\u5b98\u7684\u4eba\u3002\u522b\u5904 \u5730\u65b9\u6211\u4e0d\u77e5\u9053\uff0c\u5728\u54b1\u4eec\u8fd9\u4e2a\u53bf\u5b50\u91cc\uff0c\u51e0\u5e74\u6765\u5012\u6ca1\u6709\u542c\u8bf4\u6740\u51a4\u6789\u8fc7\u4e00\u4e2a\u8001\u767e\u59d3\u3002\u5012\u662f\u4e09\u5e74 \u524d\u6709\u4e00\u4e2a\u8d2a\u5b98\u53eb\u505a\u66fe\u5265\u76ae\u7684\u88ab\u5979\u6740\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8c08\u4e86\u534a\u5929\uff0c\u5fc3\u4e2d\u8d8a\u6765\u8d8a\u4e71\uff0c\u8d70\u51fa\u8336\u4ead\uff0c\u4e00\u7247\u60d8\u7136\u3002 \u3000\u3000\u6b66\u5219\u5929\uff0c\u5979\u5230\u5e95\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba\uff1f\u8fd9\u95ee\u9898\u59cb\u7ec8\u60f3\u4e0d\u6e05\u695a\u3002\u4f46\u5979\u60f3\u8d77\u4e86\u7236\u6bcd\u7684\u6df1\u4ec7\uff0c \u54ac\u4e86\u54ac\u7259\uff0c\u8fd8\u662f\u6602\u8d77\u5934\u5411\u524d\u8d70\u4e86\u3002\u7530\u91ce\u91cc\u4e00\u7247\u9633\u5149\uff0c\u5979\u5fc3\u4e2d\u5374\u662f\u9634\u973e\u5bc6\u5e03\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u843d\u62d3\u738b\u5b59\u620f\u4e3d\u59b9 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u56de\u3000\u843d\u62d3\u738b\u5b59\u620f\u4e3d\u59b9 \u3000\u3000\u66ae\u6625\u4e09\u6708\uff0c\u7eff\u904d\u7530\u91ce\uff0c\u6742\u82b1\u751f\u6811\uff0c\u7fa4\u9e3e\u4e71\u98de\uff0c\u5927\u5730\u4e0a\u4e00\u7247\u9633\u548c\u666f\u8c61\uff0c\u4ece\u5251\u9601\u5230\u5df4\u5dde \u53bb\u7684\u8def\u4e0a\uff0c\u5374\u6709\u4e00\u4e2a\u5c11\u5973\uff0c\u5728\u9752\u9a74\u80cc\u4e0a\uff0c\u4ef0\u5929\u957f\u5578\uff0c\u597d\u4f3c\u6ee1\u6000\u5fc3\u4e8b\uff0c\u90c1\u90c1\u4e0d\u6b22\u3002\u8fd9\u4e2a\u5c11 \u5973\u6b63\u662f\u4e0a\u5b98\u5a49\u513f\u3002\u5979\u79bb\u5f00\u4e86\u90a3\u4e2a\u8336\u4ead\u540e\uff0c\u5c31\u5728\u5c0f\u9547\u4e0a\u4e70\u4e86\u4e00\u5339\u9752\u9a74\u4ee3\u6b65\uff0c\u5df2\u7ecf\u8d76\u4e86\u4e09\u5929 \u8def\u7a0b\u4e86\u3002\u8fd9\u4e09\u5929\u6765\uff0c\u90a3\u8336\u4ead\u4e3b\u4eba\u7684\u8bdd\u8001\u662f\u5728\u70e6\u6270\u7740\u5979\uff0c\u5979\u60f3\u4e0d\u5230\u957f\u5b59\u4f2f\u4f2f\u773c\u4e2d\u7684\u5973\u9b54\u738b\uff0c \u7adf\u662f\u8001\u767e\u59d3\u773c\u4e2d\u7684\u597d\u7687\u5e1d\uff0c\u800c\u5979\uff0c\u8d1f\u7740\u7236\u6bcd\u7684\u6df1\u4ec7\uff0c\u5374\u6b63\u8981\u53bb\u523a\u6740\u5979\u3002 \u3000\u3000\u8fd9\u65e5\u5979\u5df2\u8fc7\u4e86\u95fe\u4e2d\uff0c\u508d\u7740\u5609\u9675\u6c5f\u8d70\uff0c\u8def\u65c1\u662f\u4e00\u5e26\u957f\u6797\uff0c\u98ce\u666f\u751a\u7f8e\uff0c\u5730\u5f62\u5374\u4e5f\u751a\u4e3a\u9669 \u5cfb\u3002\u5ffd\u542c\u5f97\u80cc\u540e\u8e44\u58f0\u5f97\u5f97\uff0e\u6709\u4e24\u9a91\u5feb\u9a6c\u8d76\u4e86\u4e0a\u6765\uff0c\u9a6c\u4e0a\u7684\u9a91\u5ba2\u4e43\u662f\u4e24\u4e2a\u866c\u9aef\u6c49\u5b50\uff0c\u76f8\u8c8c \u9887\u4e3a\u7c97\u8c6a\u3002\u4e0a\u5b98\u5a49\u513f\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u7a0b\uff0c\u90a3\u4e24\u9a91\u9a6c\u5ffd\u7136\u4ece\u524d\u9762\u6298\u56de\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e00\u52a8\uff0c\u60f3\u8d77\u957f\u5b59\u4f2f\u4f2f\u548c\u5979\u8bf4\u8fc7\u7684 \u6c5f\u6e56\u52fe\u5f53\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u83ab\u975e\u662f\u7eff\u6797\u9053\u4e0a\u7684\u8e29\u76d8\u4e86\u4e48\uff1f\u201d\u7eff\u6797\u597d\u6c49\u5728\u8fdb\u884c\u4e00\u4ef6\u5927\u52ab\u6848\u4e4b\u524d\uff0c \u5fc5\u5148\u6d3e\u4eba\u4fa6\u5bdf\u865a\u5b9e\uff0c\u6c5f\u6e56\u4e0a\u7684\u9ed1\u8bed\u5c31\u53eb\u505a\u201c\u8e29\u76d8\u5b50\u201d\u3002\u4e0a\u5b98\u5a49\u513f\u4e0d\u7531\u5f97\u591a\u770b\u4e86\u4ed6\u4eec\u4e24\u773c\uff0c \u90a3\u4e24\u9a91\u5feb\u9a6c\u4ece\u5979\u8eab\u8fb9\u64e6\u8fc7\uff0c\u7a81\u7136\u7206\u51fa\u4e00\u9635\u54c8\u54c8\u7684\u7b11\u58f0\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u6709\u6c14\uff0c\u60f3\u8981\u65a5\u8d23\u4ed6 \u4eec\u65e0\u793c\uff0c\u8f6c\u5ff5\u4e00\u60f3\u3002\u4f55\u82e6\u591a\u60f9\u95f2\u4e8b\uff0c\u59d1\u4e14\u5fcd\u4f4f\uff0c\u90a3\u4e24\u9a91\u5feb\u9a6c\u4e5f\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u518d\u8d70\u4e00\u4f1a\uff0c\u524d\u9762\u53c8\u662f\u4e24\u9a91\u5feb\u9a6c\u51fa\u7c73\uff0c\u4e0a\u5b98\u5a49\u513f\u60f3\u9053\uff1a\u201c\u82e5\u7136\u771f\u662f\u8e29\u76d8\u5b50\u7684\u8bdd\uff0c\u90a3\u5c31 \u662f\u6709\u4e24\u62e8\u5f3a\u4eba\u6253\u540c\u4e00\u7684\u4e3b\u610f\u4e86\u3002\u201d\u770b\u8fd9\u4e24\u4e58\u9a91\u5ba2\uff0c\u90fd\u60ac\u6709\u8170\u5200\uff0c\u6302\u6709\u5f13\u7bad\uff0c\u4e0a\u5b98\u5a49\u513f\u8d8a \u60f3\u8d8a\u89c9\u5f97\u81ea\u5df1\u731c\u5f97\u4e0d\u9519\u3002 \u3000\u3000\u518d\u5f80\u524d\u8d70\uff0c\u8fdb\u5165\u4e86\u4e00\u6761\u66f2\u66f2\u6298\u6298\u7684\u5c71\u8def\uff0c\u8d70\u4e86\u5927\u534a\u4e2a\u65f6\u8fb0\uff0c\u78b0\u4e0d\u89c1\u4eba\uff0c\u4e0a\u5b98\u5a49\u513f\u6b63 \u5728\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u7b2c\u4e00\u62e8\u7684\u4e24\u9a91\u5feb\u9a6c\uff0c\u53bb\u4e86\u4e0d\u4e45\u4fbf\u5c31\u6298\u56de\uff0c\u82e5\u662f\u8e29\u76d2\u5b50\u7684\u8bdd\uff0c\u524d\u9762\u8be5\u6709 \u8c6a\u5bcc\u5ba2\u5546\uff0c\u5982\u4f55\u81f3\u4eca\u672a\u89c1\uff1f\u201d\u5ffd\u542c\u5f97\u4fa7\u9762\u6797\u4e2d\uff0c\u6709\u94ee\u94ee\u8e2a\u8e2a\u7684\u53e4\u7434\u4e4b\u58f0\u4f20\u51fa\uff0c\u751a\u662f\u82cd\u51c9\uff0c \u4e0a\u5b98\u5a49\u513f\u5fc3\u60c5\u672c\u6765\u6291\u90c1\uff0c\u88ab\u8fd9\u7434\u58f0\u4e00\u6311\uff0c\u66f4\u89c9\u60b2\u4ece\u4e2d\u7c73\uff0c\u4e0d\u53ef\u65ad\u7edd\u3002\u4f46\u542c\u5f97\u6797\u4e2d\u6709\u4eba\u6b4c \u9053\uff1a\u201c\u524d\u4e0d\u89c1\u53e4\u4eba\uff0c\u540e\u4e0d\u89c1\u6765\u8005\uff0c\u5ff5\u5929\u5730\u4e4b\u60a0\u60a0\uff0c\u72ec\u6006\u7136\u800c\u6d95\u4e0b\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u60f3\u9053\uff1a \u201c\u539f\u6765\u5929\u5730\u4e4b\u95f4\uff0c\u9664\u6211\u4e4b\u5916\uff0c\u4e5f\u8fd8\u6709\u4f24\u5fc3\u4e4b\u4eba\u3002\u201d\u89e6\u8d77\u540c\u611f\uff0c\u4fbf\u4e0b\u4e86\u9752\u9a74\uff0c\u7f13\u7f13\u8d70\u5165\u6797 \u4e2d\u3002 \u3000\u3000\u4f46\u89c1\u6797\u4e2d\u4e00\u4e2a\u5e74\u5c11\u4e66\u751f\uff0c\u5112\u51a0\u7d20\u670d\uff0c\u6b63\u5728\u629a\u7434\u957f\u53f9\uff0c\u770b\u6765\u4f3c\u662f\u4e00\u4e2a\u843d\u62d3\u4e0d\u7f81\u7684\u58eb\u5b50\uff0c \u6797\u4e2d\u7cfb\u6709\u4e00\u5339\u7626\u9a6c\uff0c\u9a6c\u80cc\u4e0a\u53ea\u6709\u4e2a\u7834\u65e7\u7684\u4e66\u7bee\uff0c\u51e0\u5377\u65e7\u4e66\uff0c\u4e00\u76ee\u77ad\u7136\uff0c\u6b64\u5916\u522b\u65e0\u4ed6\u7269\u3002 \u4e0a\u5b98\u5a49\u513f\u5fc3\u9053\uff1a\u201c\u5f3a\u4eba\u60f3\u52ab\u7684\u7edd\u4e0d\u4f1a\u662f\u8fd9\u4e2a\u7a77\u9178\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4e66\u751f\u660e\u660e\u770b\u89c1\u4e0a\u5b98\u5a49\u513f\u5411\u4ed6\u8d70\u6765\uff0c\u5374\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u4ecd\u7136\u4e13\u5fc3\u4e00\u610f \u7684\u5728\u5f39\u594f\u53e4\u7434\uff0c\u8c03\u5b50\u8d8a\u6765\u8d8a\u51c4\u6006\u4e86\u3002 \u3000\u3000\u6797\u4e2d\u9e1f\u8bed\u82b1\u9999\uff0c\u6625\u5149\u660e\u5a9a\uff0c\u4e0e\u4e66\u751f\u5f39\u594f\u7684\u51c4\u4ed6\u7684\u7434\u97f5\uff0c\u7edd\u4e0d\u8c10\u548c\u3002\u4e0a\u5b98\u5a49\u513f\u66fc\u58f0\u541f \u9053\uff1a\u201c\u5927\u5730\u6625\u56de\u82b1\u4f3c\u9526\uff0c\u95ee\u541b\u4f55\u4e8b\u72ec\u4f24\u5fc3\uff1f\u201d\u5176\u5b9e\u5979\u81ea\u5df1\u4f55\u5c1d\u4e5f\u4e0d\u4f24\u5fc3\uff0c\u4e0d\u8fc7\u662f\u60f3\u6545\u610f \u6311\u90a3\u4e66\u751f\u8bf4\u8bdd\u7f62\u4e86\u3002 \u3000\u3000\u90a3\u4e66\u751f\u5374\u5e76\u4e0d\u7b54\u5979\u7684\u8bdd\uff0c\u4fe1\u624b\u4e00\u5f39\uff0c\u4e5f\u66fc\u58f0\u541f\u9053\uff1a\u201c\u82b1\u81ea\u98d8\u96f6\u6c34\u81ea\u6d41\uff0c\u5c82\u7f18\u65e0\u8d56\u5f3a \u5360\u6101\uff1f\u201d\u7434\u97f3\u4e00\u53d8\uff0c\u5ffd\u5982\u6625\u90ca\u653e\u9a6c\uff0c\u73e0\u843d\u7389\u76d8\u3001\u9e3e\u8bed\u95ee\u5173\u3001\u6d41\u6cc9\u4e0b\u6ee9\uff0c\u53d8\u5c3d\u60b2\u82e6\u4e4b\u97f3\uff0c \u6613\u4e3a\u6b22\u7545\u4e4b\u97f5\u3002\u4e0a\u5b98\u5a49\u513f\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u4ed6\u968f\u7740\u7434\u65e8\u6b4c\u9053\uff1a\u201c\u6b65\u8f87\u51fa\u62ab\u9999\uff0c\u6e05\u6b4c\u4e34\u592a \u6db2\u3002\u6653\u6811\u6d41\u9e3e\u6ee1\uff0c\u6625\u5824\u82b3\u8349\u79ef\u3002\u98ce\u5149\u7ffb\u9732\u6587\uff0c\u96ea\u534e\u4e0a\u7a7a\u78a7\u3002 \u3000\u3000\u82b1\u8776\u672a\u6765\u5df2\uff0c\u5c71\u5149\u6696\u5c06\u5915\u3002\u201d \u3000\u3000\u4e0a\u5bab\u5a49\u513f\u5446\u5446\u53d1\u695e\uff0c\u539f\u6765\u8fd9\u4e00\u9996\u8bd7\u4e43\u662f\u5979\u7956\u7236\u4e0a\u5b98\u4eea\u6240\u505a\u7684\uff0c\u5979\u7684\u7956\u7236\u4ee5\u5584\u5199\u201c\u5bab \u8bcd\u201d\u8457\u540d\uff0c\u8fd9\u9996\u8bd7\u6709\u4e00\u6bb5\u6545\u4e8b\uff0c\u90a3\u8fd8\u662f\u5510\u592a\u5b97\u5728\u4e16\u7684\u65f6\u5019\uff0c\u6709\u4e00\u6b21\u6625\u65e5\u62db\u5bb4\u5404\u5927\u81e3\uff0c\u4e0a \u5b98\u4eea\u5949\u547d\u505a\u7684\uff0c\u6240\u4ee5\u8fd9\u9996\u8bd7\u7684\u9898\u540c\u5c31\u53eb\u505a\u201c\u65e9\u6625\u6842\u6797\u6bbf\u5e94\u8bcf\u201d\u3002\u8fd9\u9996\u8bd7\u5199\u5fa1\u82d1\u9752\u5149\uff0c\u7eee \u4e3d\u9ad8\u534e\uff0c\u751a\u5f97\u592a\u5b97\u7687\u5e1d\u7684\u6b22\u5fc3\uff0c\u5f53\u65f6\u8d4f\u8d50\u4e86\u4e0a\u5b98\u4eea\u4e00\u659b\u73cd\u73e0\u3002\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u7591\u4e91\u987f\u8d77\uff1a \u201c\u6211\u8d5e\u8d4f\u5c71\u6797\u7684\u6625\u5149\uff0c\u4ed6\u7acb\u5373\u8c31\u594f\u5fa1\u82d1\u7684\u6625\u5149\uff0c\u800c\u4e14\u6070\u662f\u6211\u7956\u7236\u5199\u7684\u5bab\u8bcd\uff0c\u83ab\u975e\u4ed6\u5df2\u77e5 \u9053\u6211\u7684\u6765\u5386\u4e86\u4e48\uff1f\u201d\u7ee7\u800c\u4e00\u60f3\uff0c\u5979\u7956\u7236\u7684\u8bd7\u4f20\u8bf5\u4e00\u65f6\uff0c\u5510\u521d\u201c\u5bab\u4f53\u8bd7\u201d\u76db\u884c\uff0c\u751a\u81f3\u8fd8\u6709 \u8bb8\u591a\u4eba\u7adf\u76f8\u6a21\u62df\uff0c\u88ab\u65f6\u4eba\u79f0\u4e3a\u201c\u4e0a\u5b98\u4f53\u201d\uff0c\u90a3\u4e48\u8fd9\u4e66\u751f\u4fe1\u624b\u5f39\u51fa\u5979\u7956\u7236\u663e\u8457\u540d\u7684\u4e00\u9996\u5bab \u8bcd\uff0c\u4e5f\u4e0d\u8db3\u4e3a\u602a\u3002\u53ea\u4e0d\u77e5\u4ed6\u662f\u65e0\u610f\u8fd8\u662f\u6709\u5fc3\uff1f \u3000\u3000\u66f2\u65e2\u7ec8\uff0c\u9093\u4e66\u751f\u63a8\u7434\u800c\u8d77\uff0c\u4ef0\u5929\u72c2\u7b11\uff0c\u7b11\u58f0\u4e2d\u5374\u53c8\u6709\u51c4\u51c9\u7684\u51b5\u5473\uff0c\u4e0a\u5b98\u5a49\u513f\u9053\uff1a \u201c\u54c0\u4e50\u65e0\u7aef\uff0c\u5374\u4e3a\u4f55\u6765\uff1f\u201d\u90a3\u4e66\u751f\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\u65e2\u7136\u6b22\u559c\u542c\u6b22\u4e50\u7684\u8c03\u5b50\uff0c\u6211\u6562\u4e0d\u4ece\u547d\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u7b11\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u4f60\u8fd9\u4e00\u9996\u5bab\u4f53\u8bd7\u662f\u4e13\u4e3a\u5f39\u594f\u7ed9\u6211\u542c\u7684\uff0c\u6211\u5374\u8981\u602a\u4f60\u5462\uff01\u201d\u9093\u4e66\u751f\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4f60\u521a\u624d\u5f39\u7ed9\u81ea\u5df1\u542c\u7684\u90a3\u9996\u66f2\u5b50\uff0c\u5f39\u7684\u662f\u9648\u5b50\u6602\u7684\u201c\u767b\u5e7d\u5dde\u53f0\u6b4c\u201d\u5427\uff1f\u7434 \u594f\u51c4\u7edd\uff0c\u611f\u4eba\u6781\u6df1\uff0c\u663e\u7136\u662f\u4eba\u7434\u5408\u4e00\uff0c\u7cbe\u795e\u8d2f\u6ce8\u624d\u80fd\u5f39\u594f\u51fa\u7c73\uff1b\u8fd9\u4e00\u9996\u8bd7\uff0c\u5f39\u5f97\u867d\u7136\u7f8e \u5999\uff0c\u7ec8\u662f\u4e0d\u5927\u81ea\u7136\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u62ac\u8d77\u5934\u6765\uff0c\u6014\u6014\u7684\u671b\u7740\u4e0a\u5b98\u5a49\u513f\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u59d1\u5a18\u7adf\u662f\u5999\u89e3\u97f3\u5f8b\u7684\u65b9 \u5bb6\uff0c\u5931\u656c\u5931\u656c\uff01\u53ea\u662f\u59d1\u5a18\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\uff0c\u6211\u672c\u6765\u4e0d\u662f\u6b22\u4e50\u4e2d\u4eba\uff0c\u600e\u5f39\u5f97\u51fa\u6b22\u6109\u66f2 \u8bcd\uff1f\u201d \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u63a5\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u4e00\u51db\uff01\u8fd9\u4e66\u751f\u7684\u76f8\u8c8c\u597d\u719f\uff0c\u7adf\u7136\u50cf\u662f\u90a3\u513f\u89c1\u8fc7\u4f3c\u7684\u3002 \u56de\u60f3\u513f\u65f6\u76f8\u8bc6\uff0c\u5374\u600e\u4e48\u4e5f\u60f3\u4e0d\u8d77\u6765\u3002\u90a3\u4e66\u751f\u4e3e\u8d77\u53e4\u7434\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u629b\u7816\u5f15\u7389\uff0c\u613f\u8046\u59d1 \u5a18\u96c5\u594f\u3002\u201d\u770b\u4ed6\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u4e5f\u4f3c\u4e4e\u6709\u51e0\u5206\u8be7\u5f02\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u63a5\u8fc7\u53e4\u7434\uff0c\u5979\u5fc3\u4e2d\u5145\u6ee1\u590d\u4ec7\u4e4b\u5ff5\uff0c\u7ea4\u6307\u4e00\u62e8\uff0c\u4e0d\u81ea\u89c9\u7684\u5f39\u51fa\u9ad8\u4ea2\u6fc0\u6602\u4e4b\u8c03\uff0c \u90a3\u5c11\u5e74\u4e66\u751f\u5251\u7709\u4e00\u626c\uff0c\u8038\u7136\u52a8\u5bb9\uff0c\u542c\u51fa\u5979\u5f39\u7684\u4e43\u662f\u5f53\u4ee3\u8bd7\u4eba\u6768\u70af\u6240\u4f5c\u7684\u4e00\u9053\u201c\u4ece\u519b\u884c\u201d\u3002 \u7434\u97f3\u5982\u94c1\u9a91\u7a81\u51fa\uff0c\u5200\u67aa\u94ff\u9e23\uff0c\u4e0a\u5b98\u5a49\u513f\u968f\u7740\u7434\u97f3\u6b4c\u9053\uff1a \u3000\u3000\u70fd\u706b\u7167\u897f\u4eac\uff0c\u5fc3\u4e2d\u81ea\u4e0d\u5e73\u3002 \u3000\u3000\u7259\u748b\u8f9e\u98ce\u9619\uff0c\u94c1\u9a91\u7ed5\u9f99\u57ce\u3002 \u3000\u3000\u96ea\u6697\u51cb\u65d7\u753b\uff0c\u98ce\u591a\u6742\u9f13\u58f0\u3002 \u3000\u3000\u5b81\u4e3a\u767e\u592b\u957f\uff0c\u80dc\u4f5c\u4e00\u4e66\u751f\u3002 \u3000\u3000\u90a3\u4e66\u751f\u9762\u8272\u500f\u53d8\uff0c\u5ffd\u5730\u4ef0\u706d\u72c2\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u5b81\u4e3a\u767e\u592b\u957f\uff0c\u80dc\u4f5c\u4e00 \u4e66\u751f\uff01\u5f53\u4eca\u4e4b\u4e16\uff0c\u5927\u4e08\u592b\u81ea\u5f53\u94c1\u9a6c\u91d1\u6208\uff0c\u7eb5\u6a2a\u5929\u4e0b\uff01\u5c82\u53ef\u53ea\u5bfb\u7ae0\u89c5\u53e5\uff0c\u4f5c\u4e2a\u767e\u65e0\u4e00\u7528\u7684 \u4e66\u751f\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6709\u5fc3\u8bf4\u4f60\u7684\u3002\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u7768\u4e86\u5979\u4e00\u773c\uff0c\u773c\u5149\u4e2d \u7adf\u4f3c\u9887\u6709\u731c\u7591\u4e4b\u610f\uff0c\u63a5\u56de\u53e4\u7434\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8bf4\u8005\u65e0\u5fc3\u3002\u542c\u8005\u7af9\u610f\u3002\u6211\u6709\u6211\u7684\u611f\u89e6\uff0c\u4f60 \u4e0d\u5fc5\u4ecb\u6000\u3002\u201d\u9a91\u58eb\u7626\u9a6c\uff0c\u4e5f\u4e0d\u548c\u4e0a\u5b98\u5a49\u513f\u9053\u522b\uff0c\u5f84\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u9053\uff1a\u201c\u8fd9\u4e66\u751f\u8c8c\u4f3c\u4f6f\u72c2\uff0c\u602a\u91cc\u602a\u6c14\uff0c\u83ab\u975e\u662f\u4f24\u5fc3\u4eba\u522b\u6709\u6000\u62b1\u4e48\uff1f\u201d\u6025\u5fd9 \u8de8\u4e0a\u9752\u9a74\uff0c\u8ffd\u4e0a\u53bb\u9053\uff1a\u201c\u76f8\u516c\uff0c\u4f60\u5f80\u90a3\u513f\uff1f\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u6211\u5f80\u5df4\u5dde\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u559c\u9053\uff1a \u201c\u5de7\u6781\u4e86\uff0c\u6211\u4e5f\u662f\u524d\u5f80\u5df4\u5dde\u3002\u201d\u6ee1\u62df\u90a3\u4e66\u751f\u4f1a\u9080\u5979\u540c\u884c\uff0c\u5c82\u6599\u90a3\u4e66\u751f\u53c8\u53ea\u662f\u6de1\u6de1\u7684\u8bf4\u9053\uff1a \u3000\u3000\u201c\u662f\u4e48\uff1f\u201d\u5728\u9a6c\u80cc\u4e0a\u5934\u4e5f\u4e0d\u56de\uff0c\u5f84\u81ea\u626c\u97ad\u8d76\u8def\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u597d\u751f\u6709\u6c14\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f60\u4e0d\u7406\u6211\uff0c\u6211\u504f\u8981\u7406\u4f60\u3002\u201d\u50ac\u52a8\u9752\u9a74\uff0c\u7d27\u7d27\u8ddf\u5728 \u9a6c\u540e\uff0c\u90a3\u5c11\u5e74\u4e66\u751f\u53ea\u5f53\u4e0d\u77e5\uff0c\u8d70\u4e86\u534a\u5929\uff0c\u7adf\u4e0d\u548c\u4e0a\u5b98\u5a49\u513f\u8bf4\u4e00\u53e5\u8bdd\u3002\u4e0a\u5b98\u5a49\u513f\u81ea\u601d\u81ea\u60f3\uff1a \u201c\u4e3a\u4ec0\u4e48\u4ed6\u542c\u6211\u5f39\u4e86\u8fd9\u66f2\u4ece\u4e2d\u884c\uff0c\u6001\u5ea6\u4fbf\u7a81\u53d8\u5982\u65af\uff1f\u542c\u90a3\u8336\u4ead\u7684\u4e3b\u4eba\u8bf4\uff0c\u6b66\u5219\u5929\u5012\u662f\u9887 \u80fd\u7528\u4eba\uff0c\u5929\u4e0b\u4e5f\u592a\u5e73\u65e0\u4e8b\uff0c\u8fde\u4ed6\u6751\u5e72\u91cc\u7684\u59d1\u5a18\u4eec\u90fd\u5435\u7740\u8981\u8bfb\u4e66\u3002\u4e3a\u4ec0\u4e48\u8fd9\u4e66\u751f\u5374\u81ea\u53f9\u4e66 \u751f\u65e0\u7528\uff1f\u6211\u662f\u56e0\u4e3a\u5fc3\u5207\u590d\u4ec7\uff0c\u624d\u5f39\u51fa\u91d1\u6208\u94c1\u9a6c\u7684\u6740\u4f10\u4e4b\u58f0\uff0c\u96be\u9053\u4ed6\u4e5f\u6709\u540c\u611f\uff1f\u201d\u5fc3\u4e2d\u7591 \u56e2\u83ab\u91ca\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u90a3\u4e66\u751f\u4e0d\u662f\u5e38\u4eba\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u7a0b\uff0c\u524d\u9762\u53c8\u6709\u4e24\u9a91\u5feb\u9a6c\u5954\u6765\uff0c\u9a6c\u4e0a\u4e5f\u662f\u4e24\u4e2a\u76f8\u8c8c\u7c97\u8c6a\u7684\u9a91\u5ba2\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d \u4e00\u52a8\uff1a\u201c\u83ab\u975e\u53c8\u662f\u8e29\u76d8\u5b50\u7684\uff1f\u90a3\u4e48\u5148\u540e\u5c31\u662f\u4e09\u62e8\u4eba\u4e86\u3002\u201d\u8fd9\u65f6\u4ed6\u4eec\u6b63\u8d70\u5165\u4e24\u5c71\u5939\u5cd9\u4e4b\u4e2d \u7684\u4e00\u6761\u7f8a\u80a0\u5c0f\u9053\uff0c\u5c0f\u9053\u4e0a\u6700\u591a\u53ef\u5bb9\u4e24\u9a91\u9a6c\u5e76\u8f94\u800c\u884c\uff0c\u90a3\u4e24\u9a91\u5feb\u9a6c\u65cb\u98ce\u822c\u7684\u51b2\u8fc7\u6765\uff0c\u5176\u4e2d \u4e00\u9a91\u5ffd\u5730\u4e00\u58f0\u957f\u5636\uff0c\u524d\u8e44\u4eba\u7acb\uff0c\u4f3c\u4e4e\u662f\u5076\u7136\u5931\u8db3\uff0c\u8e22\u7740\u4e86\u77f3\u5934\uff0c\u9a6c\u4e0a\u7684\u9a91\u5ba2\u559d\u9053\uff1a\u201c\u755c \u751f\u60f3\u4f5c\u6b7b\u4e48\uff1f\u201d\u5237\u7684\u4e00\u97ad\u626b\u4e0b\uff0c\u4e0d\u77e5\u662f\u6709\u610f\u8fd8\u662f\u65e0\u610f\uff0c\u90a3\u5339\u9a6c\u659c\u91cc\u4e00\u51b2\uff0c\u8fd9\u4e00\u97ad\u7adf\u5237\u5230 \u4e86\u4e66\u751f\u7684\u8eab\u4e0a\uff01\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u4e0a\u5b98\u5a49\u513f\u95ea\u7535\u822c\u7684\u4e5f\u662f\u4e00\u97ad\u626b\u51fa\uff0c\u6070\u6070\u5c06\u90a3\u6761\u957f\u97ad \u5377\u7740\uff0c\u4f46\u89c9\u6765\u4eba\u8155\u529b\u6c89\u96c4\uff0c\u81ea\u5df1\u8fd9\u6761\u9a6c\u97ad\u9669\u7ed9\u4ed6\u593a\u51fa\u624b\u53bb\uff01 \u3000\u3000\u5e78\u800c\u4e0a\u5b98\u5a49\u513f\u624b\u6cd5\u7075\u5de7\uff0c\u4e00\u89c1\u4e0d\u5999\uff0c\u7acb\u5373\u65bd\u5c55\u501f\u529b\u6253\u529b\u7684\u6b66\u529f\u8bc0\u7a8d\uff0c\u9a6c\u97ad\u4e00\u62d6\uff0c\u5f80 \u5916\u4e00\u5e26\uff0c\u6b63\u8981\u4e58\u52bf\u53cd\u62bd\uff0c\u90a3\u4eba\u7a81\u7136\u6536\u97ad\u8d54\u7f6a\uff0c\u6ee1\u9762\u60f6\u6050\u7684\u795e\u60c5\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u51e0\u4e4e\u5931\u624b \u6253\u7740\u59d1\u5a18\uff0c\u6055\u7f6a\u6055\u7f6a\u3002\u201d\u4e00\u63d0\u9a6c\u7f30\uff0c\u75be\u9a70\u800c\u8fc7\u3002\u770b\u90a3\u4e66\u751f\u65f6\uff0c\u53ea\u89c1\u4ed6\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u76d7 \u9a91\u5df2\u8fc7\uff0c\u4ed6\u624d\u201c\u5440\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u597d\u9669\uff0c\u597d\u9669\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7b11\u9053\uff1a\u201c\u6ca1\u4e8b\u4e86\uff0c\u53ef\u4ee5\u8d70\u5566\uff01\u201d\u6ee1\u4ee5\u4e3a\u8fd9\u4e00\u56de\u4ed6\u5b9a\u7136\u9053\u8c22\uff0c\u90a3\u77e5\u8fd9\u4e66\u751f\u597d \u50cf\u60ca\u9b42\u5207\u5b9a\u7684\u6837\u5b50\uff0c\u53cc\u76ee\u65e0\u795e\uff0c\u970d\u5730\u5750\u7a33\u8eab\u5b50\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7684\u8bf4\u9053\uff1a\u201c\u5929\uff0c\u5929\u516c\u4fdd\u4f51\uff0c\u4fa5 \u5e78\u6ca1\u4e8b\uff0c\u662f\uff0c\u662f\u53ef\u4ee5\u8d70\u5566\uff01\u201d\u5237\u7684\u4e00\u97ad\uff0c\u50ac\u9093\u7626\u9a6c\u626c\u8e44\u75be\u8d70\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u771f\u662f\u4e2a\u4e0d\u582a\u4e00\u5413\u7684\u6ca1\u7528\u4e66\u751f\u3002\u201d\u968f\u5373\u53c8\u8d77\u7591\u56e2\uff1a \u201c\u8fd9\u76d7\u5f92\u660e\u660e\u662f\u60f3\u6253\u4ed6\uff0c\u96be\u9053\u4ed6\u8eab\u4e0a\u6709\u4ec0\u4e48\u503c\u5f97\u4e00\u52ab\u4e4b\u7269\uff1f\u201d\u518d\u770b\u4e00\u904d\uff0c\u9664\u4e86\u51e0\u5377\u7834\u4e66\uff0c \u4e00\u5f20\u53e4\u7434\uff0c\u8fd9\u4e66\u751f\u786e\u5b9e\u53ef\u4ee5\u8bf4\u5f97\u662f\u8eab\u65e0\u957f\u7269\u3002\u201c\u96be\u9053\u5f3a\u76d7\u4e5f\u89e3\u98ce\u96c5\uff0c\u60f3\u52ab\u4ed6\u7684\u53e4\u7434\uff1f\u8fd9 \u53e4\u7434\u4e5f\u503c\u4e0d\u4e86\u51e0\u4e2a\u94b1\u5440\uff01\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u9ec4\u660f\u65f6\u5206\uff0c\u6070\u597d\u8d70\u5230\u4e00\u4e2a\u5e02\u9547\uff0c\u5c11\u5e74\u4e66\u751f\u5230\u9547\u4e0a\u6700\u5927\u7684\u4e00\u5bb6\u5ba2\u5e97\u6295\u5bbf\uff0c\u4e0a\u5b98\u5a49\u513f\u4e5f \u8ddf\u4e86\u8fdb\u53bb\uff0c\u5e97\u5c0f\u4e8c\u95ee\u9053\uff1a\u201c\u662f\u4e00\u8d77\u7684\u4e48\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u8138\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u7ed9\u6211\u53e6\u627e \u4e00\u95f4\u4e0a\u623f\uff0c\u6709\u6ca1\u6709\u5411\u5357\u7684\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u6709\uff0c\u6709\u3002\u201d\u4ed6\u4f3c\u4e4e\u9887\u7231\u8bf4\u8bdd\uff0c\u7b54\u5e94\u4e4b\u540e\uff0c\u53c8 \u9053\uff1a\u201c\u5e78\u4e8f\u5ba2\u5b98\u4eec\u662f\u4eca\u5929\u6765\uff0c\u8981\u662f\u6628\u5929\uff0c\u90a3\u5c31\u8fde\u9a6c\u623f\u4e5f\u627e\u4e0d\u5230\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4e3a\u4ec0 \u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u6628\u5929\u5de6\u91d1\u543e\u4e18\u5927\u5c06\u519b\u8fc7\u5883\uff0c\u5927\u5c06\u519b\u548c\u5b98\u957f\u4eec\u5c31\u5728\u5c0f\u5e97\u4f4f\u5bbf\u3002\u4f60\u770b\uff0c \u9a6c\u7caa\u90fd\u8fd8\u6ca1\u6709\u626b\u5e72\u51c0\u5462\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u4e00\u770b\uff0c\u9662\u5b50\u91cc\u679c\u7136\u6b63\u5728\u6e05\u626b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e66\u751f\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u4e18\u5c06\u519b\uff0c\u662f\u4e18\u795e\u52cb\u5417\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u89c1\u4ed6\u7684\u624b\u4e0b\u4eba\u5f20\u8d34\u5e03\u544a\uff0c\u6211\u8ba4\u4e0d\u5f97\u90a3\u4e2a\u2018\u52cb\u2019\u5b57\uff0c\u540e\u6765\u95ee\u4e86\u4eba\u624d\u77e5\u9053\uff0c \u662f\u5ff5\u4f5c\u4e18\u795e\u52cb\u3002\u6829\u516c\uff0c\u4f60\u8ba4\u5f97\u5320\u5c06\u519b\uff1f\u201d\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e00\u4e2a\u7a77\u4e66\u58f0\uff0c\u600e\u4f1a\u8ddf\u5c06 \u519b\u8ba4\u8bc6\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u7b11\u9053\uff1a\u201c\u5de6\u91d1\u543e\u5b98\u4f4d\u4e0d\u5c0f\uff0c\u5929\u4e0b\u53ea\u6709\u4e00\u4e2a\u3002\u4e66\u751f\u4e0d\u51fa\u95e8\uff0c\u80fd\u77e5\u5929\u4e0b \u4e8b\u3002\u5de6\u91d1\u543e\u5c06\u519b\u59d3\u751a\u540d\u51c6\uff0c\u4ed6\u8fd8\u80fd\u4e0d\u77e5\uff1f\u201d\u968f\u5373\u5fc3\u4e2d\u4e49\u518d\u8d77\u7591\uff1a\u201c\u8fd9\u4e66\u751f\u597d\u5927\u7684\u6c14\u6d3e\uff0c \u5bf9\u5de6\u91d1\u543e\u5927\u5c06\u519b\u4e5f\u662f\u76f4\u547c\u5176\u540d\u3002\u201d \u3000\u3000\u90a3\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u5230\u5e95\u8bfb\u4e66\u4eba\u6bd4\u6211\u4eec\u61c2\u5f97\u591a\u3002\u201d\u4f46\u63a5\u7740\u53c8\u4f3c\u70ab\u8000\u81ea\u5df1\u6240\u77e5\u7684 \u5b9e\u4e5f\u4e0d\u5c11\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4f4d\u4e18\u5927\u5c06\u519b\u662f\u5949\u4e86\u5929\u540e\u4e4b\u547d\u5230\u5df4\u5dde\u53bb\u63a2\u671b\u592a\u5b50\u7684\u3002\u201d\u4e0a\u5b98\u5a49 \u513f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6b66\u5219\u5927\u521a\u6d3e\u4e86\u90d1\u6e29\u524d\u53bb\uff0c\u73b0\u5728\u53c8\u6d3e\u4e18\u795e\u52cb\u53bb\uff0c\u770b\u6765\u5979\u5bf9\u513f\u5b50\u5012\u662f\u9887\u4e3a\u5173\u6ce8 \u5462\u3002\u90a3\u4e66\u751f\u5374\u4f3c\u4e0d\u611f\u5174\u8da3\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u5f00\u4e86\u623f\u95f4\uff0c\u4fbf\u8fdb\u53bb\u6b47\u606f\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e0e\u90a3\u4e66\u751f\u9694\u90bb\uff0c\u6b47\u4e86\u4e00\u4f1a\uff0c\u6b63\u5f85\u5429\u5490\u5e97\u5c0f\u4e8c\u5f00\u996d\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u9a6c\u5636\u4eba\u8bed\uff0c \u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u4e00\u9707\uff1a\u201c\u83ab\u975e\u662f\u5f3a\u76d7\u4e0a\u95e8\u6765\u4e86\uff1f\u201d \u3000\u3000\u63ed\u5e18\u4e00\u770b\uff0c\u4f46\u89c1\u5916\u9762\u6765\u4e86\u4e09\u9a91\uff0c\u540e\u9762\u4e24\u9a91\u662f\u516c\u5dee\uff0c\u524d\u9762\u4e00\u9a91\u5374\u662f\u4e2a\u8863\u88f3\u8934\u697c\u7684\u6c49\u5b50\uff0c \u770b\u6837\u4e86\u662f\u4e2a\u6734\u5b9e\u7684\u4e61\u4e0b\u4eba\uff0c\u4e0a\u5b98\u5a49\u513f\u4e0d\u7981\u5927\u5947\uff0c\u82e5\u8bf4\u8fd9\u6c49\u5b50\u662f\u516c\u5dee\u62bc\u89e3\u7684\u72af\u4eba\uff0c\u5374\u53c8\u4e0d \u89c1\u4e0a\u7ed1\u3001\u800c\u4e14\u9a91\u7684\u8fd8\u662f\u9ad8\u5934\u5927\u9a6c\uff0c\u6bd4\u90a3\u4e24\u4e2a\u516c\u5dee\u7684\u5750\u9a91\u795e\u6c14\u5f97\u591a\u3002\u4f46\u89c1\u8fd9\u4e24\u4e2a\u516c\u5dee\u4e00\u5230 \u95e8\u524d\uff0c\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u4fbf\u5411\u5e97\u5c0f\u4e8c\u5429\u5490\u9053\uff1a\u201c\u7ed9\u8fd9\u4f4d\u5f20\u5927\u7239\u6708\u4e0a\u623f\u3002\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c \u5c0f\u4eba\u7406\u4f1a\u5f97\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5f85\u90a3\u5e97\u5c0f\u4e8c\u5fd9\u5b8c\u4e4b\u540e\uff0c\u53eb\u4ed6\u5f00\u996d\u8fdb\u6765\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u5f20\u5927\u7239\u662f\u4ec0\u4e48\u4eba\u7269\uff1f\u201d \u5e97\u5c0f\u4e8c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ed6\u6b63\u662f\u548c\u6211\u4e00\u4e2a\u6751\u5b50\u7684\u3002\u4e00\u5411\u662f\u79cd\u7530\u7684\u3002\u4e0d\u8fc7\uff0c\u8fd9\u51e0\u5929\u5012\u53ef\u4ee5\u8fc7\u8fc7 \u4e94\u54c1\u5b98\u7684\u763e\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u5947\u602a\u4e4b\u6781\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u77e5\u9053\u4e48\uff1f \u5929\u540e\u965b\u4e0b\u65e9\u6709\u547d\u4ee4\uff0c\u51e1\u662f\u8fdb\u4eac\u544a\u5bc6\u7684\uff0c\u4e0d\u7ba1\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u6cbf\u9014\u90fd\u53d7\u4e94\u54c1\u5b98\u7684\u5f85\u9047\u3002\u201d\u4e0a \u5b98\u5a49\u513f\u9053\uff1a\u201c\u544a\u4ec0\u4e48\u5bc6\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4ec0\u4e48\u90fd\u53ef\u4ee5\u544a\uff0c\u6bd4\u5982\u5b98\u5e9c\u4e0d\u6cd5\u5440\uff0c\u8eab\u53d7\u51a4\u6789\u5440\uff0c \u6709\u751a\u4e48\u4eba\u60f3\u9020\u53cd\u5440\u7b49\u7b49\uff0c\u8001\u767e\u59d3\u90fd\u53ef\u4ee5\u4e0a\u4eac\u544a\u5bc6\u3002\u8fd9\u4f4d\u5f20\u8001\u4e09\u60f3\u544a\u7684\u5bc6\uff0c\u6211\u7565\u77e5\u4e00\u4e8c\u3002\u201d \u4e0a\u5b98\u5a49\u513f\u6253\u8d4f\u4e86\u4ed6\u4e00\u4e24\u94f6\u4e86\uff0c\u5e97\u5c0f\u4e8c\u7709\u5f00\u773c\u7b11\u7684\u8bf4\u9053\uff0c\u201c\u59d1\u5a18\u4e0d\u8981\u8bf4\u7ed9\u522b\u4eba\u542c\uff0c\u5f20\u8001\u4e09 \u60f3\u8981\u544a\u4e00\u4e2a\u6076\u9738\u3002\u8fd9\u6076\u9738\u7684\u5802\u53d4\u662f\u505a\u8fc7\u77e5\u5dde\u7684\u5927\u5b98\uff0c\u5f20\u8001\u4e8c\u6709\u4e00\u4e2a\u672a\u8fc7\u95e8\u7684\u5ab3\u5987\u88ab\u6076\u9738 \u62a2\u4e86\uff0c\u6076\u9738\u80c1\u8feb\u8fd9\u5973\u5b50\u7684\u7236\u4eb2\u6539\u4e86\u5a5a\u4e66\uff0c\u5f20\u8001\u4e09\u544a\u5230\u5e9c\u91cc\uff0c\u5e9c\u91cc\u4ee5\u5a5a\u4e66\u4e3a\u51ed\uff0c\u9a73\u56de\u4e0d\u51c6\uff0c \u5f20\u8001\u4e09\u54bd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\uff0c\u662f\u4ee5\u626c\u8a00\u544a\u5bc6\uff0c\u5176\u5b9e\u662f\u60f3\u8fdb\u4eac\u6253\u5b98\u53f8\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u6076\u9738\u80af \u653e\u8fc7\u4ed6\u5417\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u6076\u9738\u4e5f\u731c\u5230\u4ed6\u662f\u60f3\u8fdb\u4eac\u544a\u72b6\uff0c\u53ef\u662f\u5929\u540e\u6709\u547d\uff0c\u51e1\u8fdb\u4eac\u544a\u5bc6\u8005\uff0c \u90fd\u53d7\u5b98\u5e9c\u4fdd\u62a4\uff0c\u5b98\u5e9c\u600e\u77e5\u4ed6\u544a\u7684\u662f\u4ec0\u4e48\u5bc6\uff1f\u4e5f\u8bb8\u662f\u519b\u56fd\u4eba\u4e8b\u5462\uff01\u8c01\u6562\u963b\u62e6\u3002\u4e0d\u8fc7\uff0c\u90a3\u6076 \u9738\u6709\u5973\u5b50\u7236\u4eb2\u7b7e\u7f72\u7684\u5a5a\u4e66\uff0c\u5f20\u8001\u4e09\u8fd9\u573a\u5b98\u53f8\u5f97\u4e0d\u5f97\u76f4\uff0c\u53ef\u8981\u770b\u5929\u540e\u600e\u4e48\u5224\u65ad\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u53ea\u9053\u662f\u4ec0\u4e48\u673a\u5bc6\u4e4b\u4e8b\uff0c\u5374\u539f\u6765\u4e00\u4ef6\u666e\u901a\u7684\u6848\u5b50\uff0c\u6709\u70b9\u5931\u671b\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u56e0\u6b64 \u5f15\u8d77\u611f\u6168\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u5728\u4ece\u524d\uff0c\u6076\u9738\u5f3a\u62a2\u6c11\u5973\uff0c\u90a3\u662f\u5e73\u5e38\u4e4b\u6781\uff0c\u4f55\u987b\u8d39\u5c3d\u5fc3\u673a\u53bb\u5f04 \u4ec0\u4e48\u5a5a\u4e66\uff1f\u6b66\u5219\u5929\u51c6\u8bb8\u767e\u59d3\u5230\u4eac\u544a\u5bc6\uff0c\u867d\u8bf4\u53ef\u80fd\u6709\u5201\u6c11\u8bec\u544a\u4e4b\u5f0a\uff0c\u5230\u5e95\u662f\u5229\u591a\u5f0a\u5c11\u3002\u201d \u5979\u5fc3\u60c5\u77db\u76fe\u4e4b\u6781\uff0c\u5979\u4f46\u613f\u6b66\u5219\u5929\u662f\u4e2a\u4eba\u795e\u5171\u6124\u7684\u5973\u9b54\u738b\uff0c\u5374\u4e0d\u6599\u4e00\u8def\u6240\u89c1\u6240\u95fb\uff0c\u7adf\u662f\u597d \u4e8b\u591a\u4e8e\u574f\u4e8b\u3002 \u3000\u3000\u5fc3\u4e2d\u6b63\u81ea\u832b\u7136\uff0c\u5ffd\u542c\u5f97\u9694\u90bb\u90a3\u5c11\u5e74\u4e66\u751f\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0a\u5b98\u5a49\u513f\u60f3\u9053\uff1a\u201c\u6562\u60c5\u4ed6\u4e5f \u542c\u5230\u6211\u8fd9\u8fb9\u7684\u8bf4\u8bdd\u4e86\uff1f\u4ed6\u4e3a\u4ec0\u4e48\u53f9\u6c14\uff1f\u201d\u5e97\u5c0f\u4e8c\u5019\u5979\u5403\u5b8c\u665a\u996d\uff0c\u6536\u62fe\u4e1c\u897f\u51fa\u53bb\uff0c\u4fe1\u624b\u5173 \u4e0a\u623f\u95e8\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u65e9\u4e9b\u5b89\u6b47\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u6211\u518d\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5374\u54ea\u91cc\u7761\u5f97\u7740\u89c9\uff0c\u4e00\u76f4\u60f3\u770b\u90a3\u4e66\u751f\u7684\u53e4\u602a\u884c\u5f84\uff0c\u8033\u542c\u9f13\u6253\u4e09\u66f4\uff0c\u5fc3\u4e2d\u70e6\u8e81\uff0c \u62ab\u8863\u800c\u8d77\uff0c\u5230\u9662\u5b50\u91cc\u6563\u6b65\uff0c\u53ea\u89c1\u9694\u90bb\u706f\u706b\u672a\u706d\uff0c\u7eb8\u7cca\u7684\u7a97\u4e0a\uff0c\u73b0\u51fa\u5c11\u5e74\u4e66\u751f\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4e0a\u7ba1\u5a49\u513f\u51d1\u8fd1\u7a97\u5b50\u53bb\u770b\uff0c\u53ea\u542c\u5f97\u90a3\u4e66\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u8f7b\u8f7b\u5ff5\u9053\uff1a\u201c\u65e0\u8ba1\u53ef\u9664\u6101\uff0c\u601d\u91cf \u552f\u5165\u68a6\u3002\u201d\u4e00\u9762\u89e3\u957f\u886b\u7684\u94ae\u6263\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u4f3c\u662f\u521a\u6b32\u5bbd\u8863\u5c31\u5bdd\uff0c\u4e0a\u5b98\u5a49\u513f\u6b63\u60f3\u79bb\u5f00\uff0c\u5ffd \u7136\u5413\u4e86\u4e00\u8df3\uff0c\u4f46\u89c1\u4ed6\u5c06\u5e3d\u4e86\u8131\u4e0b\uff0c\u968f\u624b\u653e\u5728\u684c\u4e0a\uff0c\u5e3d\u53e3\u671d\u5929\uff0c\u5e3d\u5b50\u91cc\u7adf\u7136\u7f00\u6709\u5341\u51e0\u7c92\u591c \u660e\u73e0\uff0c\u7cbe\u5149\u8000\u773c\uff0c\u684c\u4e0a\u7684\u6cb9\u706f\u4e5f\u7ed9\u5b83\u6bd4\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u90a3\u4e09\u62e8\u5f3a\u76d7\uff0c\u679c\u7136\u662f\u4e3a\u4ed6\u800c\u6765\u3002\u5440\uff0c\u8fd9\u4e66\u751f\u4e5f\u592a \u5927\u610f\u4e86\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u56f4\u5899\u5916\u6709\u201c\u64e6\u64e6\u201d\u7684\u58f0\u97f3\uff0c\u58f0\u97f3\u5176\u5fae\uff0c\u8981\u4e0d\u8db3\u4e0a\u5b98\u5a49\u513f\u5fc3 \u4e2d\u65e9\u5c31\u6349\u9632\u5f3a\u76d7\u7edd\u5bf9\u4e0d\u4f1a\u7559\u795e\u3002 \u3000\u3000\u9662\u5b50\u91cc\u6709\u68f5\u68a7\u6d1e\uff0c\u4e0a\u5b98\u5a49\u513f\u811a\u5c16\u4e00\u70b9\uff0c\u98de\u8eab\u4e0a\u6811\u3002\u5979\u6b66\u529f\u867d\u4e0d\u5f88\u5f3a\uff0c\u4f46\u81ea\u5c0f\u5728\u6808\u9053 \u4e0a\u7ec3\u4e60\u8f7b\u529f\uff0c\u98de\u8eab\u4e0a\u6811\uff0c\u6811\u679d\u52a8\u4e5f\u4e0d\u52a8\uff0c\u90a3\u4e66\u751f\u4e1d\u6beb\u6ca1\u6709\u5bdf\u89c9\u3002\u4e0a\u5b98\u5a49\u513f\u85cf\u597d\u8eab\u5b50\uff0c\u53ea \u89c1\u623f\u4e2d\u706f\u706b\u5df2\u706d\uff0c\u684c\u5b50\u4e0a\u7684\u591c\u660e\u73e0\u5149\u534e\u66f4\u9732\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u9053\uff1a\u201c\u4f60\u5012\u5b89\u5fc3\u7761\u89c9\uff0c\u53ef\u8981\u7d2f \u6211\u4e3a\u4f60\u62c5\u5fc3\uff0c\u201d\u7728\u773c\u4e4b\u95f4\uff0c\u4f46\u542c\u5f97\u8863\u895f\u5e26\u98ce\u4e4b\u58f0\uff0c\u4e24\u6761\u4eba\u5f71\u98de\u4e0a\u5899\u5934\uff0c\u6b63\u662f\u9014\u4e2d\u6240\u9047\u7684 \u7b2c\u4e00\u62e8\u5f3a\u76d7\uff0c\u90a3\u4e24\u4e2a\u5f3a\u76d7\u5728\u5899\u5934\u4e0a\u4e00\u4f0f\uff0c\u6b63\u6b63\u5bf9\u7740\u4e66\u751f\u7684\u623f\u95f4\u3002\u4e0a\u5b98\u5a49\u513f\u634f\u7d27\u5315\u9996\uff0c\u53ea \u5f85\u90a3\u4e24\u4e2a\u5f3a\u76d7\u7a9c\u8fdb\u53bb\u884c\u52ab\uff0c\u5979\u5c31\u8981\u63b7\u51fa\u98de\u5200\u3002 \u3000\u3000\u53ef\u662f\u90a3\u4e24\u4e2a\u5f3a\u76d7\u5374\u5e76\u4e0d\u8fdb\u53bb\u884c\u52a8\uff0c\u4f19\u5728\u5899\u5934\u4e0a\u5527\u5527\u79c1\u8bed\u3002\u4e0a\u5b98\u5a49\u513f\u81ea\u5c0f\u7ec3\u4e60\u6697\u5668\uff0c \u8033\u97f3\u6781\u7075\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5f3a\u76d7\u8bf4\u9053\uff1a\u201c\u6211\u770b\u9f99\u4e94\u7239\u8981\u54b1\u4eec\u8fce\u63a5\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u662f\u90a3\u4e2a\u9178\u4e01\u3002\u201d \u53e6\u4e00\u4e2a\u5f3a\u76d7\u9053\uff1a \u3000\u3000\u201c\u8ff9\u8c61\u7a0d\u6709\u53ef\u7591\uff0c\u795e\u6c14\u7ec8\u662f\u4e0d\u4f3c\uff0c\u201d\u5148\u524d\u90a3\u4e2a\u5f3a\u76d7\u9053\uff1a\u201c\u4e0d\u8fc7\u54b1\u4eec\u4e5f\u6ca1\u6709\u767d\u6765\uff0c\u542c \u8bf4\u6709\u4e2a\u8981\u4e0a\u4eac\u544a\u5bc6\u7684\u4e61\u6c49\uff0c\u4eca\u665a\u5c31\u5728\u8fd9\u5e97\u4e2d\u6295\u5bbf\u3002\u201d\u4ed6\u540c\u4f34\u9053\uff1a\u201c\u6211\u5df2\u63a2\u6e05\u695a\u4e86\uff0c\u5c31\u4f4f \u5728\u4e1c\u9762\u7b2c\u4e09\u95f4\u623f\u95f4\u3002\u53ea\u4e0d\u77e5\u4ed6\u8981\u544a\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u5148\u5934\u90a3\u5f3a\u76d7\u9053\uff1a\u201c\u7ba1\u4ed6\u4ec0\u4e48\u4e8b\u60c5\uff0c \u5c06\u4ed6\u5e72\u6389\u4e86\u603b\u4e0d\u4f1a\u9519\uff0c\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u4e24\u4eba\u4fbf\u5728\u5899\u535c\u722c\u52a8\uff0c\u722c\u5230\u4e1c\u9762\uff0c\u8eab\u5f62\u4e00\u957f\uff0c\u4fbf\u8981\u7a9c \u5165\u5f20\u8001\u4e09\u6240\u4f4f\u7684\u90a3\u95f4\u623f\u95f4\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7591\u60d1\u4e4b\u6781\uff0c\u5979\u6700\u521d\u4ee5\u4e3a\u90a3\u4e24\u4e2a\u5f3a\u76d7\uff0c\u5b9a\u662f\u6765\u6253\u52ab\u8fd9\u5c11\u5e74\u4e66\u751f\uff0c\u8c01\u77e5\u4e0d\u662f\uff0c \u7ee7\u800c\u53c8\u4ee5\u4e3a\u662f\u6076\u9738\u6d3e\u6765\u6740\u5f20\u8001\u4e09\u7684\uff0c\u4f46\u542c\u4ed6\u4eec\u7684\u53e3\u6c14\uff0c\u5374\u53c8\u4e0d\u4f3c\u662f\u6076\u9738\u6240\u5dee\u3002\u5f85\u8981\u4e0d\u7ba1\uff0c \u8f6c\u5ff5\u4e00\u60f3\uff1a \u3000\u3000\u201c\u5f20\u8001\u4e09\u662f\u4e2a\u82e6\u4eba\uff0c\u6211\u65e2\u89c1\u5230\u6b64\u4e8b\uff0c\u7109\u80fd\u4e0d\u7406\uff1f\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u8fd9\u4e24\u4e2a\u5f3a\u76d7\u98de\u8eab\u7a9c\u4e0b\u7684\u65f6\u5019\uff0c\u4e0a\u5b98\u5a49\u513f\u4e24\u67c4\u5315\u9996\u7834\u7a7a\u98de\u51fa\u3002 \u4e0a\u5b98\u5a49\u513f\u8fd9\u51e0\u5e74\u6765\u5728\u5251\u9601\u4e0a\u7ec3\u98de\u5200\u4e4b\u6280\uff0c\u5929\u4e0a\u98de\u8fc7\u7684\u5140\u9e70\uff0c\u4e5f\u53ea\u662f\u4e00\u5200\u4fbf\u4e2d\uff0c\u6ee1\u62df\u8fd9\u4e24 \u4e2a\u5f3a\u76d7\u5b9a\u4f1a\u7ed9\u5979\u68da\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u54ea\u77e5\u8fd9\u4e24\u4e0a\u5f3a\u76d7\u8eab\u5f62\u8fd8\u672a\u843d\u5730\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7adf \u7136\u628a\u5979\u6240\u53d1\u7684\u4e24\u67c4\u5315\u9996\u90fd\u63a5\u7740\u4e86\uff0c\u5c31\u50cf\u80cc\u540e\u957f\u7740\u773c\u775b\u4e00\u822c\u3002\u4e0a\u5b98\u5a49\u513f\u4e0d\u7981\u5927\u60ca\u5931\u8272\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5f3a\u76d7\u4e5f\u4f3c\u9887\u611f\u610f\u5916\uff0c\u5fae\u5fae\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u500f\u7684\u53c8\u8df3\u4e0a\u5899\u5934\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u4e0a\u5b98 \u5a49\u513f\u5c4f\u606f\u547c\u5438\uff0c\u770b\u4ed6\u4eec\u52a8\u9759\u3002\u9661\u7136\u95f4\u53ea\u89c1\u4ed6\u4eec\u53cc\u624b\u9f50\u626c\uff0c\u4e24\u67c4\u5315\u9996\u95ea\u7535\u822c\u7684\u5411\u6811\u4e0a\u98de\u6765\uff0c \u4e0a\u5b98\u5a49\u513f\u5939\u5728\u4e24\u682a\u4ea4\u7ed3\u7684\u6811\u4e4b\u95f4\uff0c\u95ea\u52a8\u4e0d\u4fbf\uff0c\u773c\u89c1\u4e24\u67c4\u5315\u9996\u98de\u5230\u8ddf\u524d\uff0c\u542c\u90a3\u631f\u98ce\u547c\u5578\u4e4b \u58f0\uff0c\u529b\u9053\u6781\u5f3a\uff0c\u53c8\u4e0d\u6562\u4ef0\u624b\u53bb\u63a5\u3002\u5fc3\u4e2d\u521a\u53eb\u5f97\u4e00\u58f0\u201c\u4e0d\u5999\uff01\u201d\u5ffd\u5730\u90a3\u4e24\u652f\u5315\u9996\u597d\u4f3c\u7ed9\u4ec0 \u4e48\u4e1c\u897f\u649e\u4e86\u4e00\u4e0b\u4f3c\u7684\uff0c\u5931\u4e86\u51c6\u5934\uff0c\u556a\u5566\u4e24\u58f0\u3002\u63d2\u5728\u6811\u6860\u4e0a\uff0c\u79bb\u4e0a\u5b98\u5a49\u513f\u7684\u8033\u95e8\u4e0d\u5230\u4e94\u5bf8\u3002 \u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u201c\u7830\u7830\u201d\u4e24\u58f0\uff0c\u4e24\u4e2a\u5f3a\u76d7\u90fd\u4ece\u5899\u5934\u4e0a\u8dcc\u4e0b\u53bb\u4e86\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u51e0\u5446\u5446\u53d1\u6115\uff0c\u5e97\u5c0f\u4e8c\u542c\u5f97\u58f0\u606f\uff0c\u8d76\u51fa\u6765\u770b\uff0c\u53ea\u89c1\u90a3\u4e66\u751f\u62ab\u7740\u7761\u888d\uff0c\u610f\u6001\u60a0\u95f2 \u7684\u501a\u5728\u95e8\u524d\uff0c\u4e00\u89c1\u5e97\u5c0f\u4e8c\u4fbf\u62b1\u6028\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u5e97\u5b50\u91cc\u7684\u8001\u9f20\u600e\u7684\u8fd9\u4e48\u591a\uff0c\u6709\u51e0\u53ea\u8001\u9f20\u5728\u6211\u5411\u524d\u516c\u7136\u6253\u67b6\uff0c\u5608\u5f97\u6211\u7761\u4e0d\u7740\u89c9\u3002\u201d \u5e97\u5c0f\u4e8c\u7b11\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u8001\u9f20\u6253\u67b6\uff0c\u76f8\u516c\u4f60\u6253\u8001\u9f20\uff1f\u201d\u4e66\u751f\u9053\uff1a\u201c\u662f\u5440\uff0c\u53ef\u60dc\u6253\u5b83\u4e0d \u7740\u3002\u201d\u5e97\u5c0f\u4e8c\u5931\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u9f20\u7a83\u5462\uff0c\u539f\u6765\u662f\u76f8\u516c\u6253\u8001\u9f20\u53d1\u51fa\u7684\u58f0\u54cd\uff0c\u591a\u591a\u5305\u6db5\uff0c \u591a\u591a\u5305\u6db5\u3002\u201d\u642d\u8baa\u4e00\u9635\uff0c\u4fbf\u81ea\u8d70\u4e86\u3002\u90a3\u5c11\u5e74\u6602\u9996\u5411\u5929\uff0c\u66fc\u58f0\u541f\u9053\uff1a \u3000\u3000\u201c\u826f\u591c\u8fe2\u8fe2\u6765\u9f20\u5b50\uff0c\u6270\u4eba\u6e05\u68a6\u4e0d\u6210\u7720\u3002\u53ef\u6068\uff0c\u5475\u6068\uff01\u201d\u81ea\u8bf4\u81ea\u8bdd\u4e00\u4f1a\uff0c\u4e5f\u8fdb\u53bb\u7761\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u597d\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u9632\u76d7\uff0c\u4f60\u5374\u8fde\u6211\u4e5f\u9a82\u5728\u91cc\u5934\u3002\u201d\u6697\u81ea\u5bfb\u601d\uff1a \u201c\u83ab\u975e\u9002\u624d\u662f\u4ed6\u6697\u4e2d\u52a9\u6211\uff1f\u201d\u518d\u4e00\u60f3\uff1a\u201c\u4ed6\u4eba\u5728\u623f\u4e2d\uff0c\u82e5\u7136\u80fd\u4e0d\u52a8\u58f0\u606f\u5c31\u628a\u8fd9\u4e24\u4e2a\u5f3a\u76d7 \u6253\u4e0b\u5899\u5934\uff0c\u672c\u9886\u592a\u4e0d\u53ef\u601d\u8bae\u3002\u201d\u53c8\u4e0d\u4fe1\u662f\u8fd9\u4e66\u751f\u6240\u4e3a\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u7ec8\u662f\u6000\u7591\u4e0d\u5b9a\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\u8d77\u6765\uff0c\u90a3\u4e66\u751f\u597d\u4f3c\u5b8c\u5168\u4e0d\u77e5\u6628\u5bb5\u4e8b\u60c5\uff0c\u89c1\u7740\u4e0a\u5b98\u8155\u513f\uff0c\u95ee\u4e5f\u4e0d\u95ee\u4e00\u53e5\uff0c \u7ed3\u4e86\u623f\u996d\u94b1\u4fbf\u81ea\u8d70\u4e86\u3002\u4e0a\u5b98\u5a49\u513f\u5fc3\u9053\uff1a \u3000\u3000\u201c\u6211\u8ddf\u5b9a\u4e86\u4f60\uff0c\u7ec8\u8981\u6253\u7834\u8fd9\u4e2a\u7591\u56e2\u3002\u201d\u4fbf\u4e5f\u5306\u5306\u79bb\u5f00\u5ba2\u5e97\u3002\u9a91\u4e0a\u9752\u9a74\uff0c\u4e0d\u5373\u4e0d\u79bb\uff0c \u968f\u5728\u4e66\u751f\u9a6c\u540e\u3002 \u3000\u3000\u90a3\u4e66\u751f\u4ecd\u4f3c\u6628\u5929\u4e00\u6837\uff0c\u5e76\u4e0d\u548c\u5979\u4ea4\u8c08\uff0c\u8d70\u4e86\u4e00\u7a0b\uff0c\u53c8\u8fdb\u5165\u5d0e\u5c96\u7684\u5c71\u9053\uff0c\u90a3\u4e66\u751f\u6234\u6b63 \u5e3d\u5b50\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u56db\u4e0b\u65e0\u4eba\uff0c\u5c71\u5f62\u9669\u5cfb\uff0c\u82e5\u5728\u8fd9\u91cc\u9047\u4e0a\u5f3a\u4eba\uff0c\u600e\u751f\u5f97\u4e86\uff1f\u201d\u8bdd\u72b9\u672a \u4e86\uff0c\u5ffd\u542c\u5f97\u677e\u6797\u5185\u51e0\u58f0\u547c\u5578\uff0c\u679c\u7136\u51fa\u6765\u4e00\u6279\u5f3a\u4eba\u3002\u4e3a\u9996\u7684\u4e24\u4e2a\uff0c\u6b63\u662f\u4e0a\u5b98\u5a49\u513f\u6628\u65e5\u9047\u4e0a \u7684\u7b2c\u4e8c\u62e8\u5f3a\u76d7\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u52d2\u4f4f\u9752\u9a74\uff0c\u5fc3\u9053\uff1a\u201c\u4e14\u5148\u770b\u4f60\u5982\u4f55\u5bf9\u4ed8\uff1f\u201d\u53ea\u662f\u90a3\u4f19\u5f3a\u4eba\u62e6\u7740\u9a6c\u5934\uff0c\u6253\u91cf \u4e86\u4e66\u751f\u4e00\u4e0b\uff0c\u5ffd\u7136\u7eb3\u5934\u9f50\u62dc\u3002\u4e3a\u9996\u7684\u90a3\u4e24\u4e2a\u76d7\u9b41\u606d\u8c28\u4e4b\u6781\uff0c\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\uff0c \u4e0d\u77e5\u662f\u516c\u5b50\u5230\u7c73\uff0c\u6709\u5931\u8fdc\u8fce\uff0c\u4e07\u671b\u6055\u7f6a\u3002\u201d\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u54a6\uff0c\u5929\u4e0b\u53ea\u6709\u5949\u627f\u6709\u94b1\u7684\uff0c \u6211\u8eab\u65e0\u957f\u7269\uff0c\u4f60\u4eec\u5949\u627f\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u4e24\u4e2a\u76d7\u9996\u5bf9\u671b\u4e00\u773c\uff0c\u53c8\u518d\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u52ff \u89c1\u5916\uff0c\u6211\u4eec\u662f\u996e\u9a6c\u5be8\u7684\uff0c\u9f99\u4e94\u7239\u65e9\u5c31\u6709\u4fe1\u901a\u77e5\uff0c\u53eb\u6211\u4eec\u8fce\u63a5\u516c\u5b50\u3002\u201d\u5c11\u5e74\u4e66\u751f\u53eb\u9053\uff1a \u201c\u4ec0\u4e48\u5be8\u7684\uff1f\u4e0d\u5999\uff0c\u4e0d\u5999\uff0c\u4f60\u4eec\u662f\u5f3a\u76d7\u5417\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u76d7\u9b41\u9762\u9762\u76f8\u89d1\uff0c\u731c\u4e0d\u900f\u90a3\u4e66\u751f\u662f\u5426\u8bf4\u7b11\u3002\u6b63\u5728\u5c34\u5c2c\u4e4b\u9645\uff0c\u53ea\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c \u53c8\u662f\u4e24\u9a91\u5feb\u9a6c\u5954\u6765\uff0c\u4e0a\u5b98\u5a49\u513f\u4e00\u770b\uff0c\u6b63\u662f\u6628\u5929\u6240\u9047\u7684\u7b2c\u4e09\u62e8\u5f3a\u76d7\uff0c\u5176\u4e2d\u4e4b\u4e00\uff0c\u4e5f\u5c31\u662f\u7528 \u9a6c\u97ad\u6253\u5979\u7684\u4eba\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u4e24\u4e2a\u76d7\u5f92\u98de\u9a91\u5954\u5230\uff0c\u7acb\u5373\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u90b9\u4e09\u54e5\uff0c\u674e\u4e03\u54e5\uff0c\u4f60\u4eec\u8ba4 \u9519\u4e86\u4eba\u5566\uff01\u201d\u88ab\u5524\u4f5c\u201c\u90b9\u4e09\u54e5\u201d\u201c\u674e\u4e03\u54e5\u201d\u90a3\u4e24\u4e2a\u76d7\u9b41\uff0c\u609a\u7136\u4e00\u60ca\uff0c\u773c\u775b\u4e2d\u6ee1\u662f\u7591\u60d1\u7684 \u795e\u8272\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u96be\u9053\u4ed6\u771f\u7684\u4e0d\u662f\u2014\u2014\u201d\u90a3\u4e24\u4e2a\u76d7\u5f92\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\u3002\u8bd5\u60f3\u82e5\u4ed6\u4fbf \u662f\u9f99\u4e94\u7239\u6697\u5631\u6211\u4eec\u8fce\u63a5\u7684\u4eba\uff0c\u4ed6\u6628\u665a\u5c82\u4f1a\u5728\u5ba2\u5e97\u4e4b\u4e2d\u51fa\u624b\uff0c\u4f24\u4e86\u516d\u6a1f\u5c71\u7684\u4e24\u4f4d\u5be8\u4e3b\uff1f\u201d \u3000\u3000\u4e0a\u5bab\u5a49\u513f\u66f4\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u673d\u751f\u679c\u7136\u771f\u662f\u6709\u8eab\u6000\u7edd\u679d\u7684\u4eba\uff1f\u6628\u665a\u6697\u52a9 \u6211\u7684\u679c\u7136\u662f\u4ed6\u3002\u201d\u5fc3\u4e2d\u5c06\u4fe1\u5c06\u7591\uff0c\u770b\u90a3\u5c11\u5e74\u4e66\u751f\uff0c\u53ea\u89c1\u4ed6\u8d1f\u624b\u65c1\u89c2\uff0c\u60a0\u7136\u81ea\u5f97\uff0c\u9759\u542c\u90a3 \u4e24\u5e2e\u76d7\u5f92\u8bae\u8bba\uff0c\u597d\u50cf\u662f\u542c\u4ed6\u4eec\u8bae\u8bba\u522b\u4eba\u7684\u4e8b\u60c5\u4e00\u6837\u3002 \u3000\u3000\u90a3\u88ab\u5524\u4f5c\u201c\u90b9\u4e09\u54e5\u201d\u7684\u76d7\u9b41\u4ecd\u7136\u7528\u5145\u6ee1\u6000\u7591\u7684\u53e3\u543b\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e5f\u8bb8\u4ed6\u4e2a\u77e5\u9053\u2014\u2014\u201d\u540e\u6765\u7684\u90a3\u4e2a\u76d7\u5f92\u8bf4\u9053\uff1a\u201c\u5373\u7b97\u4ed6\u4e0d\u77e5\u9053\u662f\u516d\u6a1f\u5c71\u7684\u8521\u4f55\u4e24\u4f4d \u5be8\u4e3b\uff0c\u4f46\u603b\u8be5\u77e5\u9053\u4ed6\u4eec\u6240\u8981\u523a\u6740\u7684\u4e43\u662f\u90a3\u4e2a\u544a\u5bc6\u6c49\u5b50\uff0c\u4ed6\u6697\u4e2d\u6551\u4e86\u90a3\u4e2a\u6c49\u5b50\uff0c\u5206\u660e\u662f\u7ad9 \u5728\u671d\u5ef7\u8fd9\u8fb9\uff0c\u600e\u4f1a\u662f\u54b1\u4eec\u4e00\u8def\u7684\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u542c\u5f97\u83ab\u540d\u5176\u5999\uff0c\u6b63\u81ea\u63e3\u5ea6\u5c11\u5e74\u4e66\u751f\u7684\u8eab\u4efd\uff0c\u90a3\u88ab\u5524\u4f5c\u201c\u674e\u4e03\u54e5\u201d\u7684\u76d7\u9b41\u5df2 \u5148\u95ee\u4e86\u51fa\u6765\uff1a\u201c\u5218\u56db\u54e5\uff0c\u90a3\u4e48\u8fd9\u7a77\u9178\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u8fd9\u201c\u5218\u56db\u54e5\u201d\u6b63\u662f\u6628\u5929\u7528\u9a6c\u97ad\u6253 \u4e0a\u5b98\u5a49\u513f\u7684\u4eba\u3002\u4f46\u542c\u5f97\u4ed6\u4e00\u9635\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u4f60\u53c8\u8d70\u4e86\u773c\u4e86\uff0c\u8fd9\u5bb6\u4f19\u662f\u4f55\u7b49\u6837\u4eba\uff0c \u6211\u4e0d\u77e5\u9053\uff1b\u4f46\u6211\u5374\u77e5\u9053\u4ed6\u8eab\u4e0a\u6240\u6709\uff0c\u6700\u5c11\u503c\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u7edd\u4e0d\u662f\u4f60\u8bf4\u7684\u7a77\u9178\uff01\u201d\u6b64\u8bdd\u4e00 \u51fa\uff0c\u90b9\u4e09\u674e\u4e03\u90fd\u53d8\u4e86\u795e\u8272\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u9053\uff1a\u201c\u8fd9\u5f3a\u76d7\u5012\u662f\u4e00\u4e2a\u8bc6\u5b9d\u4e4b\u4eba\uff0c\u4e66\u751f\u5e3d\u5b50\u91cc\u90a3 \u5341\u51e0\u9897\u591c\u660e\u73e0\uff0c\u6bcf\u9897\u6700\u5c11\u503c\u4e00\u4e07\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u201c\u5218\u56db\u54e5\u201d\u957f\u97ad\u4e00\u6307\uff0c\u5411\u5c11\u5e74\u4e66\u751f\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8bc6\u76f8\u7684\u5feb\u62ff\u51fa\u6765\uff0c\u8fd8\u8981\u4f60\u8001\u7237\u4eb2\u81ea \u52a8\u624b\u5417\uff1f\u201d\u4ed6\u7684\u4f19\u4f34\u4e5f\u7eb5\u8eab\u4e0a\u524d\uff0c\u5bf9\u90a3\u5c11\u5e74\u91c7\u4e86\u5305\u56f4\u4e4b\u52bf\u3002\u90b9\u4e09\u674e\u4e03\u5bf9\u671b\u4e00\u773c\uff0c\u90b9\u4e09\u7684 \u795e\u8272\u4ecd\u4f3c\u6000\u7591\u4e0d\u5b9a\uff0c\u674e\u4e03\u5374\u8e0f\u4e0a\u4e86\u4e00\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u867d\u662f\u770b\u9519\u4e86\u4eba\uff0c\u5374\u4e5f\u6b6a\u6253\u6b63\u7740\uff0c \u6b63\u597d\u987a\u624b\u53d1\u4e00\u7b14\u5c0f\u8d22\u3002\u201d\u7eff\u6797\u4e2d\u7684\u89c4\u77e9\uff0c\u9053\u4e0a\u505a\u6848\uff0c\u8d76\u6765\u53c2\u52a0\u8005\u90fd\u6709\u4e00\u4efd\uff0c\u674e\u4e03\u62d4\u5200\u4e0a \u524d\uff0c\u81ea\u7136\u662f\u60f3\u5206\u80a5\u7684\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e66\u751f\u795e\u8272\u81ea\u5982\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u6211\u8eab\u65e0\u957f\u7269\uff0c\u4f60\u4eec\u8981\u62a2\u4ec0\u4e48\uff1f\u8fd9\u51e0\u5377\u7834\u4e66\u4f60\u4eec \u4e0d\u4f1a\u8bfb\uff0c\u8fd9\u4e00\u5f20\u53e4\u7434\u4f60\u4eec\u4e0d\u4f1a\u5f39\uff0c\u54c8\u54c8\uff0c\u83ab\u975e\u60f3\u62a2\u6211\u8fd9\u9876\u7834\u5e3d\u5b50\u4e48\uff1f\u201d\u597d\u50cf\u6015\u5f3a\u76d7\u4e0d\u77e5 \u9053\u4ed6\u7684\u5b9d\u8d1d\u6240\u5728\u4f3c\u7684\uff0c\u6545\u610f\u6296\u9732\u51fa\u6765\u3002\u4e0a\u5b98\u5a49\u513f\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e66\u751f\u82e5\u975e\u8eab\u6000\u7edd\u6280\uff0c\u90a3\u5c31\u4e00 \u5b9a\u662f\u795e\u7ecf\u75c5\u4e86\u3002\u201d \u3000\u3000\u90a3\u88ab\u5524\u4f5c\u201c\u5218\u9644\u54e5\u201d\u7684\u76d7\u9b41\u4e00\u58f0\u5927\u559d\uff1a\u201c\u5c31\u662f\u8981\u4f60\u8fd9\u9876\u5e3d\u4e86\uff01\u201d\u500f\u7136\u95f4\u4e09\u4e2a\u5f3a\u76d7\u90fd \u4eae\u51fa\u4e86\u5175\u5668\uff0c\u957f\u97ad\u75be\u5377\uff0c\u5355\u5200\u76f4\u65a9\uff0c\u94c1\u5c3a\u6a2a\u626b\uff0c\u4e09\u822c\u5175\u5668\uff0c\u4e00\u9f50\u5411\u90a3\u4e66\u751f\u8eab\u4e0a\u62db\u547c\uff01\u4e0a \u5b98\u5a49\u513f\u4e0d\u77e5\u90a3\u5c11\u5e74\u4e66\u751f\u662f\u5426\u771f\u61c2\u6b66\u529f\uff0c\u7d27\u6025\u4e4b\u9645\uff0c\u65e0\u6687\u601d\u91cf\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u5728\u9752\u9a74\u4e0a\u4e00\u63a0 \u800c\u8d77\uff0c\u5a07\u58f0\u65a5\u9053\uff1a\u201c\u767d\u65e5\u9752\u5929\uff0c\u8c0b\u8d22\u5bb3\u547d\uff0c\u5929\u7406\u4e0d\u5bb9\uff01\u201d\u4f46\u89c1\u5200\u5149\u5251\u5f71\u4e4b\u4e2d\uff0c\u53ee\u53ee\u5f53\u5f53 \u51e0\u58f0\u8fde\u73e0\u5bc6\u54cd\uff0c\u5355\u5200\u3001\u94c1\u5c3a\u90fd\u88ab\u622a\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u53ea\u6709\u5218\u56db\u7684\u957f\u97ad\u62bd\u64a4\u5f97\u5feb\uff0c\u6ca1\u6709\u7ed9\u5b9d\u5251 \u78b0\u7740\u3002 \u3000\u3000\u5218\u56db\u9a82\u9053\uff1a\u201c\u53c8\u662f\u4f60\u8fd9\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e2b\u5934\uff01\u5237\u7684\u4e00\u97ad\u626b\u51fa\uff0c\u7136\u540e\u5411\u4f19\u4f34\u8bf4\u9053\uff1a\u201c\u8fd9 \u5c0f\u4e2b\u5934\u53ea\u6709\u8fd9\u628a\u5b9d\u5251\u5389\u5bb3\uff0c\u672c\u4e8b\u5374\u662f\u7a00\u677e\u5e73\u5e38\uff0c\u4e0d\u5fc5\u60e7\u5979\u3002\u201d\u4e00\u97ad\u4e0d\u4e2d\uff0c\u53c8\u4f7f\u51fa\u201c\u8fde\u73af \u4e09\u97ad\u201d\u201c\u56de\u98ce\u626b\u67f3\u201d\u7684\u7edd\u6280\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff0c\u98ce\u58f0\u547c\u54cd\uff0c\u5377\u8d77\u4e86\u4e00\u56e2\u97ad\u5f71\uff0c\u65cb\u98ce\u822c\u731b\u626b\u8fc7 \u7c73\uff0c\u674e\u4e03\u5200\u5149\u95ea\u95ea\uff0c\u4e5f\u8fce\u9762\u5241\u5230\uff0c\u53e6\u4e00\u4e2a\u76d7\u5f92\u7684\u94c1\u5c3a\uff0c\u5219\u89d1\u51c6\u4e86\u4e0a\u5b98\u5a49\u513f\u7684\u7834\u7efd\u7528\u529b\u78d5 \u5979\u7684\u819d\u76d6\u3002 \u3000\u3000\u5c82\u77e5\u4e0a\u5b98\u5a49\u513f\u7684\u6b66\u529f\u867d\u7136\u4e0d\u9ad8\uff0c\u8f7b\u529f\u5374\u662f\u6781\u597d\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u6ef4\u6e9c\u6e9c\u7684\u968f\u7740\u97ad\u6084\u76f4\u8f6c \u51fa\u53bb\uff0c\u63a5\u770b\u4e00\u63d0\u8170\u52b2\uff0c\u4f7f\u4e2a\u201c\u71d5\u4e8e\u94bb\u4e91\u201d\u7684\u8eab\u6cd5\uff0c\u51ed\u7a7a\u8df3\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u957f\u5251\u51cc\u7a7a\u523a\u4e0b\uff0c \u674e\u4e03\u731d\u4e0d\u53ca\u9632\uff0c\u7adf\u88ab\u5979\u5237\u7684\u4e00\u5251\uff0c\u5728\u80a9\u5934\u4e0a\u624e\u4e86\u4e00\u9053\u4f24\u53e3\uff0c\u843d\u4e0b\u6765\u65f6\uff0c\u5f13\u978b\u4e00\u8e0f\uff0c\u53c8\u8e39 \u4e2d\u4e86\u4f7f\u94c1\u5c3a\u90a3\u4e2a\u76d7\u6127\u7684\u5f69\u76d6\uff0c\u867d\u7136\u529b\u9053\u4e0d\u5f3a\uff0c\u8e0f\u6b63\u5173\u8282\uff0c\u5374\u4e5f\u75db\u5f97\u90a3\u76d7\u9b41\u54ce\u54df\u547c\u53eb\u3002\u5c11 \u5e74\u4e66\u751f\u62cd\u624b\u7b11\u9053\uff1a\u201c\u77eb\u82e5\u6e38\u9f99\uff0c\u7fe9\u5982\u60ca\u9e3f\u3002\u5999\u5475\uff0c\u5999\u5475\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5728\u767e\u5fd9\u4e2d\u62bd\u773c\u770b\u90a3\u90a3\u4e66\u751f\uff0c\u4f46\u89c1\u4ed6\u4ecd\u662f\u8d1f\u624b\u95f2\u7acb\uff0c\u610f\u6001\u60a0\u7136\u3002\u90a3\u4e2a\u88ab\u5524\u4f5c \u201c\u90b9\u4e09\u54e5\u201d\u7684\u76d7\u9b41\u63d0\u7740\u4e00\u67c4\u72fc\u7259\u68d2\uff0c\u5c31\u5728\u4ed6\u7684\u8eab\u8fb9\u76d1\u89c6\uff0c\u8fd9\u4e2a\u76d7\u9b41\u662f\u4e2a\u8001\u6c5f\u6e56\uff0c\u884c\u4e8b\u7a33 \u91cd\uff0c\u4ed6\u5728\u672a\u5f04\u6e05\u5c11\u5e74\u4e66\u751f\u7684\u8eab\u4efd\u4e4b\u524d\uff0c\u4e0d\u80af\u5192\u6627\u51fa\u624b\uff0c\u968f\u6765\u7684\u76d7\u5f92\u90fd\u662f\u996e\u9a6c\u5be8\u7684\u4eba\uff0c\u89c1 \u9996\u9886\u4e0d\u52a8\uff0c\u4ed6\u4eec\u4fbf\u4e5f\u6563\u5f00\uff0c\u4ec5\u4ec5\u5bf9\u4e66\u751f\u53d6\u4e86\u5305\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u5218\u56db\u5728\u56db\u4e2a\u76d7\u9b41\u4e4b\u4e2d\u6b66\u529f\u6700\u9ad8\uff0c\u89c1\u81ea\u5df1\u4e24\u4e2a\u4f19\u4f34\u7adf\u88ab\u4e0a\u5b98\u5a49\u513f\u4f24\u4e86\uff0c\u6c14\u5f97\u9a82\u9053\uff1a \u201c\u8fde\u4e00\u4e2a\u5c0f\u4e2b\u5934\u90fd\u6536\u62fe\u4e0d\u4e86\uff0c\u8fd8\u5728\u9ed1\u9053\u4e0a\u6df7\u4ec0\u4e48\u996d\u5403\uff01\u4e0d\u8981\u7406\u5979\u7334\u8df3\uff0c\u9632\u5979\u4e4e\u4e2d\u5b9d\u5251\uff0c \u968f\u7740\u6211\u7684\u97ad\u68a2\u6240\u6307\uff0c\u653b\u5979\u7a7a\u95e8\u3002\u201d\u957f\u97ad\u4e00\u6296\uff0c\u500f\u5730\u4e00\u62db\u201c\u795e\u9f99\u5165\u6d77\u201d\uff0c\u5377\u5979\u67f3\u8170\uff0c\u4e0a\u5b98 \u5a49\u513f\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\u907f\u4e07\uff0c\u8df3\u5411\u5de6\u8fb9\uff0c\u5218\u56db\u7684\u97ad\u68a2\u4e00\u98a4\uff0c\u9884\u5148\u6307\u5411\u5979\u53f3\u8fb9\u9632\u5907\u4e0d\u5230\u7684 \u7a7a\u4f4d\u3002\u5218\u56db\u90a3\u4e24\u4e2a\u4f19\u4f34\u867d\u7136\u4e3a\u4ed6\u6240\u9a82\uff0c\u5bf9\u4ed6\u7075\u6d3b\u7684\u97ad\u6cd5\uff0c\u5374\u662f\u4e0d\u5f97\u4e0d\u670d\uff0c\u4fbf\u4f9d\u7167\u5218\u56db\u7684 \u6307\u793a\uff0c\u62a1\u5706\u94c1\u5c3a\uff0c\u821e\u52a8\u5355\u5200\uff0c\u653b\u4e0a\u5b98\u5a49\u513f\u53f3\u9762\u7a7a\u95e8\uff0c\u8fd9\u4e00\u6765\uff0c\u4e0a\u5b98\u5a49\u513f\u5168\u7136\u88ab\u52a8\uff0c\u5218\u56db \u90a3\u6761\u957f\u97ad\u66f4\u662f\u4f7f\u5f97\u5f97\u5fc3\u5e94\u4e4e\uff0c\u864e\u864e\u751f\u98ce\uff01\u4e0a\u5b98\u5a49\u513f\u672c\u8eab\u7684\u6b66\u529f\u672c\u6765\u5c31\u4e0d\u53ca\u90a3\u4e09\u4e2a\u76d7\u9b41\uff0c \u52a0\u4ee5\u662f\u7b2c\u4e00\u6b21\u5bf9\u654c\uff0c\u5904\u52a3\u52bf\uff0c\u66f4\u4e3a\u614c\u4e71\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u63a5\u8fde\u9047\u4e86\u597d\u51e0\u6b21\u9669\u62db\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u53c8\u60ca\u53c8\u6c14\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e66\u751f\u771f\u771f\u53ef\u6076\uff0c\u6211\u4e3a\u4ed6\u62fc\u547d\uff0c\u4ed6\u5374\u6ca1\u4e8b\u4eba\u4f3c\u7684\u3002\u201d \u7a0d\u4e00\u5206\u795e\uff0c\u9669\u9669\u7ed9\u674e\u4e03\u5355\u5200\u5288\u4e2d\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u76d7\u9b41\u4e45\u6218\u4e0d\u4e0b\uff0c\u4ea6\u662f\u5fc3\u4e2d\u7126\u8e81\uff0c\u5218\u56db\u547c\u547c\u4e24\u97ad\uff0c\u5c06\u4e0a\u5b98\u5a49\u513f\u903c\u9000\u4e09\u6b65\uff0c\u51b7\u51b7 \u7b11\u9053\uff1a\u201c\u7eff\u6797\u4e2d\u8bb2\u7684\u662f\u2018\u4e49\u6c14\u2019\u8fd9\u4e24\u4e2a\u5b57\uff0c\u4e3a\u670b\u53cb\u4e0d\u8f9e\u4e24\u808b\u63d2\u5200\u3002\u800c\u4eca\u5973\u738b\u5f53\u4f4d\uff0c\u9634\u9633 \u98a0\u5012\uff0c\u4e16\u9053\u5168\u975e\uff0c\u8fde\u7eff\u6797\u4e2d\u7684\u98ce\u6c14\u4e5f\u53d8\u5566\uff01\u201d\u8fd9\u8bdd\u663e\u660e\u662f\u6697\u8bbd\u90a3\u4e2a\u88ab\u5524\u4f5c\u201c\u90b9\u4e09\u54e5\u201d\u7684 \u76d7\u9b41\u7684\uff0c\u90b9\u4e09\u4e00\u76f4\u76d1\u89c6\u7740\u90a3\u5c11\u5e74\u4e66\u751f\uff0c\u6b8a\u65e0\u51fa\u624b\u4e4b\u610f\u3002 \u3000\u3000\u674e\u4e03\u662f\u90b9\u4e09\u7684\u526f\u624b\uff0c\u4ed6\u5403\u4e86\u4e0a\u5b98\u5a49\u513f\u4e00\u5251\uff0c\u6068\u4e0d\u5f97\u65e9\u70b9\u5c06\u5979\u6536\u62fe\uff0c\u5bf9\u90b9\u4e09\u7684\u8896\u624b\u65c1 \u89c2\uff0c\u4ea6\u662f\u9887\u4e3a\u4e0d\u6ee1\uff0c\u8ddf\u7740\u4e5f\u9053\uff1a\u201c\u662f\u5440\uff0c\u5927\u4e08\u592b\u8bf4\u5e72\u5c31\u5e72\uff0c\u5c82\u80fd\u50cf\u5a18\u513f\u822c\u7684\u754f\u9996\u754f\u5c3e\uff1f\u201d \u3000\u3000\u90b9\u4e09\u7ed3\u81ea\u5df1\u7684\u4f19\u4f34\u8bf4\u8bdd\u6324\u8feb\uff0c\u9762\u5b50\u4e0a\u6302\u4e0d\u4e0b\u4e86\uff0c\u4f46\u4ed6\u8fd8\u662f\u4e0d\u80af\u5411\u90a3\u5c11\u5e74\u4e66\u751f\u52a8\u624b\uff0c \u5374\u5c06\u72fc\u7259\u68d2\u4e00\u6446\uff0c\u4e0a\u524d\u5939\u653b\u4e0a\u5b98\u5a49\u513f\u3002 \u3000\u3000\u90b9\u4e09\u7684\u6b66\u529f\u4e0d\u5728\u5218\u56db\u4e4b\u4e0b\uff0c\u800c\u4e14\u4ed6\u7684\u72fc\u7259\u68d2\u91cd\u8fbe\u56db\u5341\u4e8c\u65a4\uff0c\u529b\u5927\u68d2\u6c89\uff0c\u4e0d\u754f\u5b9d\u5251\uff0c \u8fd9\u4e00\u6765\u4e0a\u5b98\u5a49\u513f\u66f4\u662f\u96be\u4e8e\u5e94\u4ed8\uff0c\u9669\u8c61\u73af\u751f\uff0c\u6c14\u5f97\u9a82\u9053\uff1a\u201c\u7eff\u6797\u4e2d\u4e5f\u8bb2\u4e49\u6c14\uff0c\u8bfb\u4e66\u7684\u53cd\u4e0d \u5982\u5f3a\u76d7\uff01\u201d\u5979\u8fd9\u8bdd\u5374\u662f\u660e\u663e\u7684\u5728\u9a82\u5c11\u5e74\u4e66\u751f\u3002\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4e0a\u5b98\u5a49\u513f\u8bf4\u8bdd\u5206\u4f38\uff0c\u624b\u4e2d \u7684\u5b9d\u5251\u88ab\u90b9\u4e09\u4e00\u68d2\u78d5\u6b6a\uff0c\u5218\u56db\u7684\u8f6f\u97ad\u767b\u65f6\u4f3c\u957f\u86c7\u822c\u7684\u62e6\u8170\u5377\u5230\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u5c11\u5e74\u4e66\u751f\u4e00\u58f0\u957f\u5578\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u5dfe\u5e3c\u6709\u82f1\u8c6a\uff0c\u6127\u715e\u987b\u7709\u6c49\uff01\u54fc\uff0c\u56db\u4e2a\u5927 \u7537\u4eba\uff0c\u6b3a\u4fae\u4e00\u4e2a\u5f31\u5973\u5b50\uff0c\u5f53\u771f\u662f\u8fde\u6211\u4e5f\u770b\u4e0d\u8fc7\u773c\u4e86\uff01\u201d\u957f\u5578\u58f0\u4e2d\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u7167\u9762\u4e4b\u95f4\uff0c \u4fbf\u5c06\u90b9\u4e09\u7684\u72fc\u7259\u68d2\u5288\u624b\u593a\u53bb\uff0c\u957f\u8896\u4e00\u5377\uff0c\u674e\u4e03\u7684\u5355\u5200\u98de\u4e0a\u4e86\u534a\u5929\uff0c\u5218\u56db\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c \u957f\u97ad\u4e00\u62db\u201c\u9a7e\u4e58\u516d\u9f99\u201d\u521a\u521a\u6296\u52a8\uff0c\u90a3\u4e66\u751f\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u6700\u53ef\u6076\uff01\u201d\u4e94\u6307\u4e00\u62ff\uff0c\u6293\u7740\u4e86 \u97ad\u68a2\uff0c\u4ed6\u8fd9\u52a8\u4f5c\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u5218\u56db\u6765\u4e0d\u53ca\u677e\u624b\uff0c\u5df2\u88ab\u4ed6\u6325\u4e86\u8d77\u6765\uff0c\u55d2\u817f\u4e00\u58f0\uff0c\u63b7\u51fa\u4e09\u4e08 \u5f00\u5916\uff0c\u5c11\u5e74\u4e66\u751f\u54c8\u54c8\u5927\u7b11\uff0c\u8f6c\u8eab\u4e00\u4e2a\u8e6c\u811a\uff0c\u53c8\u5c06\u90a3\u4e2a\u4f7f\u94c1\u5c3a\u7684\u76d7\u9b41\u8e22\u7ffb\u4e86\u3002 \u3000\u3000\u7fa4\u76d7\u5927\u60ca\uff0c\u7eb7\u7eb7\u6d8c\u4e0a\uff0c\u5c11\u5e74\u4e66\u751f\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u73ed\u5b9d\u8d1d\uff0c\u4e22\u5c3d\u4e86\u7eff\u6797\u7684\u9762\u5b50\u3002\u628a\u5175 \u5668\u7ed9\u6211\u7559\u4e0b\uff0c\u901a\u901a\u90fd\u6eda\u51fa\u53bb\uff01\u201d\u4f46\u89c1\u4ed6\u638c\u5288\u3001\u811a\u8e22\u3001\u8896\u5377\uff0c\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c \u7ed9\u4ed6\u6cbe\u7740\u7684\uff0c\u5175\u5668\u65e0\u4e0d\u8131\u624b\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5200\u67aa\u5251\u621f\uff0c\u5806\u6ee1\u4e00\u5730\uff0c\u6240\u6709\u76d7\u5f92\uff0c\u53ea\u6068\u7239\u5a18\u5c11 \u751f\u4e86\u4e24\u6761\u817f\uff0c\u8fde\u8dd1\u5e26\u8db4\u7684\u90fd\u9003\u5f97\u5e72\u5e72\u51c0\u51c0\uff01 \u3000\u3000\u4e0a\u6709\u5a49\u513f\u53c8\u60ca\u53c8\u559c\uff0c\u5446\u5446\u7684\u671b\u7740\u5c11\u5e74\u4e66\u751f\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u53ea\u89c1\u90a3\u5c11\u5e74 \u4e66\u751f\u72c2\u7b11\u4e4b\u540e\uff0c\u5ffd\u800c\u54ed\u51fa\u58f0\u6765\uff0c\u545c\u54bd\u541f\u9053\uff1a\u201c\u5c71\u6c34\u867d\u96c4\u5947\uff0c\u8c6a\u6770\u96be\u5bfb\u89c5\uff0c\u65e5\u66ae\u6b32\u4f55\u4e4b\uff1f \u543e\u5fc3\u81ea\u5bc2\u5bc2\uff01\u201d\u4ed6\u9769\u4eba\u7a7a\u624b\uff0c\u6253\u8d25\u7fa4\u76d7\uff0c\u5374\u53cd\u800c\u8c6a\u6c14\u5c3d\u6d88\uff0c\u4f24\u5fc3\u6d41\u6d95\uff0c\u771f\u662f\u5927\u51fa\u60c5\u7406\u4e4b \u5916\u3001\u4efb\u662f\u4e0a\u5b98\u5a49\u513f\u7edd\u4e16\u806a\u660e\uff0c\u4ea6\u89c9\u96be\u89e3\uff01 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u5c11\u5e74\u4e66\u751f\u7684\u54ed\u58f0\u624d\u6e10\u6e10\u4f4e\u6c89\u4e0b\u7c73\uff0c\u4e0a\u5b98\u5a49\u51e0\u8fd9\u65f6\u5fc3\u795e\u7a0d\u5b9a\uff0c\u8d70\u4e0a\u53bb \u9053\uff1a\u201c\u4f60\u4eca\u65e5\u5927\u83b7\u5168\u80dc\uff0c\u5374\u4f55\u6545\u4f24\u5fc3\uff1f\u201d\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u5c31\u51fa\u4e3a\u8fd9\u73ed\u5f3a\u76d7\u592a\u8fc7\u4e0d\u6210\u6c14\u5019\uff01 \u545c\u547c\uff0c\u970d\u5b50\u5b5f\u4e4b\u4e0d\u4f5c\uff0c\u6731\u865a\u4faf\u4e4b\u5df2\u4ea1\u3002\u4f24\u5fc3\u5b87\u5185\u82f1\u8c6a\uff0c\u5c3d\u5f52\u65b0\u4e3b\uff1b\u5fcd\u89c1\u5929\u4eac\u795e\u5668\uff0c\u7adf\u5c5e \u4ed6\u5bb6\uff01\u201d\u970d\u5b50\u5b5f\u5373\u6c49\u521d\u7684\u540d\u5c06\u970d\u53bb\u75c5\uff0c\u4ed6\u66fe\u8f85\u4f50\u5e7c\u4e3b\u767b\u57fa\uff0c\u4fdd\u5168\u6c49\u5ba4\uff1b\u6731\u865a\u4faf\u662f\u6c49\u5b97\u5ba4 \u5218\u7ae0\u7684\u5c01\u53f7\uff0c\u5728\u6c49\u9ad8\u7956\u5218\u90a6\u6b7b\u540e\uff0c\u5415\u540e\u7be1\u6743\uff0c\u6b8b\u6740\u5b97\u5ba4\uff0c\u5218\u7ae0\u524a\u5e73\u8bf8\u5415\uff0c\u91cd\u65b0\u5b89\u5b9a\u4e86\u5218 \u5bb6\u7684\u5929\u4e0b\u3002\u4e0a\u5b98\u5a49\u513f\u542c\u4e66\u751f\u8bf4\u51fa\u4e86\u8fd9\u51e0\u53e5\u6d3b\uff0c\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u9707\uff01 \u3000\u3000\u62ac\u8d77\u5934\u6765\uff0c\u5ffd\u89c1\u90a3\u4e66\u751f\u53c8\u6362\u4e86\u4e00\u526f\u795e\u6c14\uff0c\u795e\u91c7\u5955\u5955\uff0c\u773c\u6ce2\u6d41\u8f6c\uff0c\u4e5f\u6b63\u5728\u671b\u7740\u81ea\u5df1\uff0c \u4e0a\u5b98\u5a49\u513f\u8138\u4e0a\u4e00\u7ea2\uff0c\u53ea\u542c\u5f97\u90a3\u4e66\u751f\u53c8\u541f\u9053\uff1a\u201c\u4e16\u8fd0\u867d\u79fb\u8c6a\u6770\u5fd7\uff0c\u5e78\u9022\u77e5\u5df1\u5c5e\u7ea2\u989c\uff01\u201d\u4e0a \u5b98\u5a49\u513f\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u5440\uff0c\u54ed\u54ed\u7b11\u7b11\uff0c\u771f\u662f\u4ee4\u4eba\u83ab\u540d\u5176\u5999\uff01\u8c01\u4eba\u662f\u4f60\u7684\u7ea2\u989c\u77e5\u5df1\u3002\u201d\u90a3\u4e66 \u751f\u7a81\u7136\u5c06\u5979\u624b\u665a\u4e00\u5e26\uff0c\u5de6\u624b\u4e00\u4e3e\uff0c\u8f7b\u8f7b\u62e8\u5f00\u5979\u8986\u989d\u7684\u4e91\u9b13\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u6027\u60c5\u867d\u7136\u8131\u4fd7\uff0c\u5374\u4e5f\u7ed9\u8fd9\u4e66\u751f\u7a81\u5982\u5176\u6765\u7684\u4e3e\u52a8\u6014\u7740\u4e86\uff0c\u767b\u65f6\u5fc3\u5934\u9e7f\u8df3\uff0c\u60f3 \u53f1\u9a82\u4ed6\u8f7b\u8584\u65e0\u793c\uff0c\u5374\u662f\u820c\u5934\u6253\u7ed3\uff0c\u9a82\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4e66\u751f\u54c8\u54c8\u4e00\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u679c\u7136\u4e0d\u9519\uff0c\u4f60\u662f\u5a49\u513f\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u4e00\u6014\u4e4b\u4e0b\uff0c\u4e00\u4e2a\u76f8\u8bc6 \u7684\u5f71\u5b50\u95ea\u7535\u822c\u5728\u5fc3\u5934\u63a0\u8fc7\uff0c\u5c31\u5728\u540c\u4e00\u65f6\u5019\uff0c\u4e0a\u5b98\u5a49\u513f\u4e5f\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u4e16\u5b50\uff01\u201d \u3000\u3000\u90a3\u4e66\u751f\u653e\u5f00\u4e86\u4e0a\u5bab\u5a49\u513f\uff0c\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u524d\u65e5\u7b2c\u4e00\u6b21\u89c1\u4f60\u65f6\uff0c\u5c31\u89c9\u5f97\u597d\u751f\u773c\u719f\uff0c \u50cf\u662f\u5728\u54ea\u513f\u89c1\u8fc7\u4f3c\u7684\uff01\u4f46\u82e5\u975e\u77a7\u89c1\u4f60\u989d\u89d2\u4e0a\u7684\u6591\u75d5\uff0c\u6211\u8fd8\u4e0d\u6562\u8ba4\u4f60\u5462\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u60ca\u559c \u4ea4\u96c6\uff0c\u6025\u5fd9\u95ee\u9053\uff1a \u3000\u3000\u201c\u4e16\u5b50\uff0c\u4f60\u600e\u7684\u4e0d\u5728\u4eac\u4e2d\uff0c\u5374\u626e\u6210\u8fd9\u526f\u6a21\u7965\uff0c\u5728\u6c5f\u6e56\u4e0a\u6d6a\u8361\uff1f\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u82e6\u7b11\u9053\uff1a \u201c\u5982\u4eca\u6c5f\u5c71\u5df2\u6539\u59d3\u6b66\u7684\u4e86\uff0c\u4f60\u8fd8\u79f0\u547c\u6211\u505a\u4e16\u5b50\u505a\u4ec0\u4e48\uff1f\u6211\u4e0e\u4f60\u4e00\u6837\uff0c\u90fd\u662f\u5929\u6daf\u6ca6\u843d\u4e4b\u4eba\uff0c \u6211\u53eb\u4f60\u5a49\u513f\uff0c\u4f60\u53eb\u6211\u674e\u9038\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u674e\u9038\u4e43\u662f\u5510\u671d\u5b97\u5ba4\uff0c\u4ed6\u7684\u7956\u7236\u5efa\u6210\u662f\u5510\u592a\u5b97\u674e\u4e16\u6c11\u7684\u957f\u5144\uff0c\u4ed6\u548c\u6b66\u5219\u5929\u7684 \u513f\u5b50\u674e\u5f18\u674e\u8d24\u7b49\u4eba\u662f\u5802\u5144\u5f1f\u8f88\u3002\u674e\u4e16\u6c11\u7684\u5e1d\u4f4d\u662f\u4ece\u4ed6\u54e5\u5efa\u6210\u624b\u4e2d\u593a\u6765\u7684\uff0c\u5b57\u540e\u5185\u759a\u4e8e\u5fc3\uff0c \u6545\u6b64\u5bf9\u54e5\u54e5\u7684\u540e\u4eba\u751a\u4e3a\u4f18\u5f85\u3002\u674e\u9038\u81ea\u5c0f\u4fbf\u957f\u5728\u5bab\u4e2d\u3002\u4e0a\u5b98\u5a49\u513f\u7684\u7956\u7236\u3002\u7236\u4eb2\u90fd\u662f\u5bab\u5ef7\u4e2d \u7684\u6587\u5b66\u6043\u4ece\uff0c\u4e0a\u5b98\u5a49\u513f\u5c0f\u65f6\u4e5f\u5e38\u51fa\u5165\u5bab\u7981\uff0c\u662f\u4ee5\u548c\u674e\u9038\u8ba4\u5f97\uff0c\u674e\u9038\u6bd4\u5a49\u513f\u5e74\u957f\u4e03\u5c81\uff0c\u5c0f \u65f6\u5019\u6700\u559c\u6b22\u9017\u5a49\u513f\u73a9\u800d\u3002 \u3000\u3000\u6709\u4e00\u6b21\u6349\u8ff7\u85cf\uff0c\u5a49\u513f\u7528\u624b\u5e15\u8499\u4e86\u773c\u775b\uff0c\u53bb\u6349\u674e\u9038\uff0c\u6454\u4e86\u4e00\u8de4\uff0c\u989d\u89d2\u4e0a\u7559\u4e0b\u4e86\u4e00\u4e2a\u75a4 \u75d5\uff0c\u674e\u9038\u521a\u624d\u62e8\u5f00\u5979\u7684\u4e91\u9b13\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u77a7\u5979\u989d\u89d2\u4e0a\u6709\u6ca1\u6709\u75a4\u75d5\u3002 \u3000\u3000\u5f80\u65e5\u7981\u82d1\u7e41\u534e\uff0c\u604d\u4f3c\u5357\u67ef\u4e00\u68a6\uff1b\u4eca\u65e5\u6c5f\u6e56\u843d\u62d3\uff0c\u4fe8\u5982\u9694\u4e16\u91cd\u9022\u3002\u4e07\u8bed\u5343\u8a00\uff0c\u4e0d\u77e5\u4ece \u4f55\u8bf4\u8d77\u2014\u2014 \u3000\u3000\u8fc7\u4e86\u534a\u54cd\uff0c\u4e0a\u5b98\u5a49\u513f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u7956\u7236\u548c\u7236\u4eb2\u88ab\u6740\u7684\u4e8b\u60c5\uff0c\u60f3\u6765\u4f60\u662f\u65e9\u5df2\u77e5\u9053\u7684 \u4e86\uff1f\u201d\u674e\u9038\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u5c31\u662f\u5728\u90a3\u4e00\u4e8b\u4ef6\u4e4b\u540e\uff0c\u9003\u51fa\u5bab\u7684\u3002\u5e78\u800c\u6211\u53ca\u65e9\u89c1\u673a\uff0c\u8981\u4e0d\u7136 \u7109\u6709\u547d\u5728\uff1f\u5440\uff0c\u4f60\u4e5f\u8bb8\u8fd8\u4e0d\u77e5\u9053\uff0c\u5c31\u5728\u8fd9\u4e03\u5e74\u4e4b\u4e2d\uff0c\u90a3\u5973\u9b54\u738b\u63a5\u8fde\u6740\u4e86\u4e09\u5341\u516d\u5bb6\u738b\u4eb2\u56fd \u621a\uff0c\u7687\u5e1d\u5b97\u5ba4\u88ab\u6740\u7684\u66f4\u591a\uff0c\u8fde\u5979\u81ea\u5df1\u7684\u513f\u5b50\u4e5f\u4e0d\u80fd\u5e78\u514d\uff0c\u6216\u88ab\u8d2c\u6ef4\uff0c\u6216\u88ab\u6bd2\u6740\uff0c\u601d\u4e4b\u4ee4 \u4eba\u5bd2\u5fc3\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u6211\u4e5f\u542c\u957f\u5b59\u4f2f\u4f2f\u8bf4\u8fc7\u4e86\u3002\u54b3\uff0c\u771f\u60f3\u4e0d\u5230\u4f60\u4e5f\u662f\u7ed9 \u90a3\u5973\u7ed9\u6b66\u5219\u5929\u8feb\u5f97\u9003\u4ea1\u7684\u3002\u201d\u5979\u672c\u6765\u60f3\u8ddf\u7740\u674e\u9038\uff0c\u5c06\u6b66\u5219\u5929\u79f0\u505a\u201c\u5973\u9b54\u738b\u201d\uff0c\u5374\u4e0d\u77e5\u600e \u7684\uff0c\u8bdd\u5230\u53e3\u8fb9\u5374\u53c8\u6539\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u4e92\u76f8\u8bc9\u8bf4\u522b\u540e\u7684\u60c5\u51b5\uff0c\u539f\u5939\u674e\u9038\u7684\u906d\u9047\u4e5f\u6b63\u50cf\u4e0a\u5bab\u5a49\u513f\u4e00\u6837\uff0c\u9003\u5230\u4e00\u4f4d\u5148\u5e1d\u5927 \u81e3\u7684\u5bb6\u91cc\uff0c\u8fd9\u4f4d\u5927\u81e3\u540d\u53eb\u5c09\u8fdf\u70af\uff0c\u4e43\u662f\u5510\u521d\u5f00\u56fd\u529f\u81e3\u5c09\u8fdf\u606d\u4e4b\u540e\uff0c\u6b66\u529f\u5353\u7edd\uff0c\u4e0d\u5728\u957f\u5b59 \u5747\u91cf\u4e4b\u4e0b\uff0c\u4ea4\u6e38\u5e7f\u9614\u5219\u80dc\u8fc7\u957f\u5b59\u5747\u91cf\u591a\u591a\u3002\u662f\u4ee5\u8fd9\u4e03\u5e74\u6765\uff0c\u674e\u9003\u4e0d\u4f46\u5b66\u4e86\u5c09\u8fdf\u70af\u7684\u6b66\u529f\u3001 \u8fd8\u5f97\u4e86\u8bb8\u591a\u540d\u5bb6\u6388\u4ed6\u6b66\u827a\u3002 \u3000\u3000\u674e\u9038\u5636\u4e0a\u5b98\u5a49\u513f\u8bf4\u662f\u8981\u53bb\u523a\u6740\u6b66\u5219\u5929\uff0c\u6c89\u541f\u4e2a\u664c\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5bab\u4e2d\u9632\u8303\u68ee\u4e25\uff0c\u4e0b\u624b\u4e0d\u6613\u3002\u518d\u8bf4\uff0c\u5979\u7fbd\u7ffc\u5df2\u6210\uff0c\u4f60\u6740\u5979\u4e00\u4eba\uff0c\u4ea6\u662f\u65e0\u6d4e\u4e8e\u4e8b\u3002\u201d \u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4f60\u5374\u6253\u7b97\u5982\u4f55\uff1f\u201d\u674e\u9038\u4ef0\u5929\u5578\u9053\uff1a\u201c\u6211\u6b32\u7ea0\u96c6\u5929\u4e0b\u4e49\u5175\uff0c\u626b\u5e73\u5996\u5b7d\uff01\u201d \u4e0a\u5b98\u5a49\u513f\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u4e3e\u5175\uff1f\u201d\u60f3\u8d77\u6cbf\u9014\u6240\u89c1\u7684\u592a\u5e73\u666f\u8c61\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u7136\u674e\u5bb6\u4e3a\u4e86\u4e89\u56de\u5e1d\u4f4d\uff0c \u90a3\u53c8\u5f97\u5bb3\u82e6\u4e86\u591a\u5c11\u9ece\u6c11\uff1f\u201d \u3000\u3000\u674e\u9038\u84e6\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4e5f\u77e5\u9053\u6709\u8bb8\u591a\u4eba\u62e5\u62a4\u8fd9\u4e2a\u5973\u9b54\u738b\uff0c\u4f46\u81ea\u5f00\u5929\u8f9f\u5730\u4ee5 \u6765\u3001\u54ea\u6709\u5973\u4eba\u79f0\u5e1d\u4e4b\u7406\uff0c\u4e0d\u8981\u8bf4\u6211\u5bb6\u4e0e\u5979\u4ec7\u6df1\u4f3c\u6d77\uff0c\u7eb5\u662f\u65e0\u51a4\u65e0\u4ec7\uff0c\u6211\u4ee5\u6602\u85cf\u4e03\u5c3a\u4e4b\u8eaf\uff0c \u4e5f\u65ad\u65ad\u4e0d\u80fd\u5411\u4e00\u4e2a\u5987\u4eba\u5357\u9762\u79f0\u81e3\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u542c\u4e86\u5fc3\u9053\uff1a\u201c\u8fd9\u95e8\u6c14\u548c\u6211\u7684\u957f\u5b59\u4f2f\u4f2f\u5012\u662f \u4e00\u6a21\u4e00\u6837\u3002\u201d\u60f3\u8d77\u4e86\u90a3\u8336\u4ead\u4e3b\u4eba\u7684\u8bdd\uff0c\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u4f60\u4eec\u4e0d\u670d\u6c14\u5973\u4eba\u79f0\u5b64\u9053\u5be1\uff0c\u4ed6\u4eec\u8001 \u767e\u59d3\u5374\u5f88\u670d\u8d34\u5462\uff01\u201d\u60f3\u5230\u6b64\u5904\uff0c\u5ffd\u89c9\u8fd9\u5e76\u4e0d\u662f\u4ec0\u4e48\u597d\u7b11\u7684\u4e8b\u60c5\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u9690\u9690\u4f5c\u75db\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4f60\u521a\u624d\u7528\u970d\u5b50\u5b5f\u548c\u6731\u865a\u4faf\u7684\u5178\u6545\uff0c\u628a\u6b66\u5219\u5929\u6bd4\u4f5c\u6c49\u671d\u7684\u5415\u540e\uff0c\u6211\u770b \u662f\u6bd4\u9519\u4e86\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u7684\u89c1\u8bc6\u4e0d\u5dee\uff0c\u53ef\u662f\u4f60\u53ea\u77e5\u5177\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a \u201c\u600e\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a \u3000\u3000\u201c\u6c49\u671d\u7684\u5415\u540e\uff0c\u4e0d\u5b66\u65e0\u672f\uff0c\u5b64\u964b\u5be1\u95fb\uff0c\u90a3\u786e\u662f\u4e0d\u80fd\u4e0e\u6b66\u5219\u5929\u76f8\u6bd4\u3002 \u3000\u3000\u6b66\u5219\u5929\u5584\u4e8e\u7528\u4eba\uff0c\u96c4\u624d\u5927\u7565\uff0c\u4e0d\u8f93\u4e8e\u592a\u5b97\u7687\u5e1d\u5f53\u5e74\uff0c\u8fd9\u4e00\u70b9\uff0c\u5979\u7684\u654c\u4eba\uff0c\u8fde\u6211\u5728\u5185\uff0c \u4e5f\u90fd\u4f69\u670d\uff1a\u552f\u5176\u5982\u6b64\uff0c\u8fd9\u5996\u5b7d\u82e5\u4e0d\u65e9\u9664\uff0c\u5927\u5510\u5929\u4e0b\uff0c\u6c38\u65e0\u6062\u590d\u4e4b\u53e3\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a \u201c\u6b66\u5219\u5929\u662f\u6bd4\u5415\u540e\u5389\u5bb3\u5f97\u591a\uff0c\u53ef\u662f\u6709\u4e00\u79cd\u60c5\u5f62\uff0c\u5979\u5374\u662f\u548c\u5415\u540e\u76f8\u540c\uff0c\u5979\u7684\u6743\u52bf\u5e76\u4e0d\u5de9\u56fa\uff01\u201d \u4e0a\u5b98\u5a49\u513f\u60f3\u8d77\u81ea\u5df1\u7684\u6240\u89c1\u6240\u95fb\uff0c\u5bf9\u674e\u9038\u7684\u8bdd\uff0c\u534a\u7591\u534a\u501f\uff0c\u4f46\u5374\u9ed8\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u674e\u9038\u9053\uff0c\u201c\u4f60\u4e0d\u4fe1\u4e48\uff1f\u4f60\u8bd5\u60f3\u6b66\u5219\u5929\u867d\u7136\u5389\u5bb3\uff0c\u5979\u5c82\u80fd\u6740\u5c3d\u5148\u671d\u7684\u5927\u81e3\uff1f\u6709\u8bb8\u591a\u624b \u63e1\u91cd\u5175\u7684\u5927\u81e3\u4fbf\u4e0d\u670d\u5979\u3002\u6211\u8fd9\u6b21\u4ece\u626c\u5dde\u6765\uff0c\u5750\u9547\u626c\u5dde\u7684\u82f1\u56fd\u516c\u5f90\u656c\u4e1a\u5df2\u5b9a\u597d\u4e86\u79cb\u540e\u4fbf\u8981 \u4e3e\u5175\u3002\u6211\u6765\u7684\u65f6\u5019\uff0c\u542c\u8bf4\u4ed6\u6b63\u8981\u9a86\u5bbe\u738b\u7ed9\u4ed6\u5199\u8ba8\u6b66\u5219\u5929\u7684\u6a84\u6587\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u542c\u674e\u9038\u8bf4\u5f97 \u8d8a\u6765\u8d8a\u786e\u5b9e\u4e86\uff0c\u5fc3\u4e2d\u4f46\u611f\u4e00\u7247\u832b\u7136\u3002\u4e0d\u9519\uff0c\u5979\u662f\u60f3\u523a\u6740\u6b66\u5219\u5929\uff0c\u4f46\u8fd9\u6837\u7684\u5927\u52a8\u5e72\u6208\uff0c\u7a76 \u7adf\u5e94\u4e0d\u5e94\u8be5\uff0c\u5979\u5374\u662f\u5927\u6709\u7591\u95ee\u3002 \u3000\u3000\u674e\u9038\u53c8\u9053\uff1a\u201c\u82f1\u56fd\u516c\u6015\u72ec\u6728\u96be\u652f\uff0c\u662f\u4ee5\u60f3\u6211\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u4f55\u7b49\u806a\u660e\uff0c \u7565\u4e00\u60f3\uff0c\u5bf9\u674e\u9038\u9014\u4e2d\u8be1\u5f02\u7684\u884c\u4e3a\uff0c\u660e\u767d\u4e86\u5927\u534a\uff0c\u7b11\u9053\uff0c\u201c\u6562\u60c5\u4f60\u524d\u6765\u5df4\u8700\uff0c\u5c31\u662f\u60f3\u7269\u8272 \u8349\u83bd\u82f1\u96c4\uff0c\u52a9\u4f60\u6210\u4e8b\uff1f\u8fd9\u51e0\u5e2e\u76d7\u5f92\u5e76\u4e0d\u662f\u60f3\u52ab\u4f60\u7684\u73e0\u5b9d\u7684\uff0c\u800c\u662f\u6253\u542c\u5230\u4e86\u8fd9\u6837\u7684\u4e2a\u6d88\u606f\uff0c \u60f3\u7ed9\u4f60\u505a\u5f00\u56fd\u529f\u81e3\u6765\u7684\uff0c\u53ef\u60dc\u4ed6\u4eec\u5f53\u9762\u9519\u8fc7\u4e86\uff01\u201d\u674e\u9038\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6240\u4ee5\u8fd9\u624d\u53eb\u6211\u7070\u5fc3\uff0c \u8fd9\u4e9b\u7eff\u6797\u4e2d\u7684\u4e4c\u5408\u4e4b\u4f17\u7eb5\u80fd\u4e3a\u6211\u6240\u7528\uff0c\u53c8\u80fd\u6210\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d\u4e0a\u5bab\u5a49\u513f\u7b11\u9053\uff1a\u201c\u8fd9\u73ed\u5f3a\u76d7 \u5012\u662f\u6000\u7740\u5bf9\u4f60\u7684\u4e00\u7247\u5fe0\u5fc3\u800c\u6765\u3002\u6211\u731c\u4ed6\u4eec\u4e4b\u6240\u4ee5\u8981\u6697\u6740\u5f20\u8001\u4e09\uff0c\u5927\u7ea6\u662f\u56e0\u4e3a\u542c\u8bf4\u4ed6\u8981\u4e0a \u4eac\u544a\u5bc6\uff0c\u5374\u4e0d\u77e5\u4ed6\u8981\u544a\u7684\u662f\u4ec0\u4e48\u673a\u5bc6\u4e4b\u4e8b\uff0c\u8bda\u6050\u4e0d\u5229\u4e8e\u4f60\uff0c\u5374\u4e0d\u6599\u4f60\u53cd\u800c\u628a\u5f20\u8001\u4e09\u6551\u4e86\u3002\u201d \u674e\u9038\u9053\uff1a\u201c\u5f20\u8001\u4e09\u662f\u4e2a\u82e6\u4eba\uff0c\u6211\u5c82\u80fd\u89c1\u6b7b\u4e0d\u6551\uff1f\u4e0d\u6599\u56e0\u6b64\u4ed6\u4eec\u4fbf\u53cd\u800c\u4ee5\u4e3a\u6211\u662f\u671d\u5ef7\u7684\u4eba\u3002\u201d \u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u90a3\u4e48\u6b66\u5219\u5929\u7684\u6240\u4f5c\u6240\u4e3a\u4e5f\u5e76\u4e0d\u662f\u5168\u7136\u9519\u4e86\u3002\u201d\u674e\u9038\u970d\u7136\u4e00\u60ca\uff0c\u5374\u9053\uff1a \u201c\u82e5\u7136\u5979\u4e0d\u7b3c\u7edc\u6c11\u5fc3\uff0c\u5979\u53c8\u5c82\u80fd\u8f7b\u6613\u593a\u5f97\u6211\u674e\u5510\u7684\u5929\u4e0b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u95ee\u9053\uff1a\u201c\u4f60\u53bb\u5df4\u5dde\uff0c\u662f\u4e0d\u662f\u62df\u63a2\u671b\u4f60\u7684\u5802\u5144\u3001\u5e9f\u592a\u5b50\u674e\u8d24\uff1f\u201d\u674e\u9038\u9053\uff1a \u201c\u662f\u6709\u8fd9\u4e2a\u610f\u601d\u3002\u53ef\u60dc\u674e\u8d24\u4e66\u5446\u5b50\u7684\u6c14\u5473\u592a\u91cd\uff0c\u867d\u6709\u53cd\u6297\u6bcd\u540e\u4e4b\u5fc3\uff0c\u5374\u662f\u5eb8\u624d\u4e00\u4e2a\u3002\u201d \u5ffd\u800c\u53c8\u53f9\u53e3\u6c14\u9053\uff1a \u3000\u3000\u201c\u4e0d\u63d0\u8fd9\u4e9b\u4e86\uff0c\u8d8a\u8bf4\u8d8a\u662f\u5fc3\u70e6\u3002\u5a49\u513f\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u53ef\u66fe\u601d\u5ff5\u6211\u4e48\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a \u201c\u6211\u51e0\u65e5\u524d\u624d\u505a\u4e86\u9996\u8bd7\uff0c\u5ff5\u7ed9\u4f60\u542c\u3002\u201d\u5c31\u662f\u90a3\u65e5\u5728\u5251\u9601\u6240\u505a\u7684\u8bd7\uff0c\u674e\u9038\u542c\u5979\u5ff5\u9053\uff1a\u201c\u53f6 \u4e0b\u6d1e\u5ead\u521d\uff0c\u601d\u541b\u4e07\u91cc\u4f59\u2026\u2026\u201d\u7b11\u9053\uff1a\u201c\u4eba\u4e16\u4e4b\u4e66\uff0c\u5b9e\u662f\u96be\u6599\uff0c\u672c\u6765\u76f8\u9694\u4e07\u91cc\uff0c\u73b0\u5728\u5374\u7ed3 \u4f34\u540c\u884c\u3002\u201d\u518d\u542c\u5979\u5ff5\u4e0b\u53bb\u9053\uff1a\u201c\u9732\u6d53\u9999\u88ab\u51b7\uff0c\u6708\u843d\u9526\u5c4f\u865a\u2026\u2026\u201d\u5e10\u7136\u8bf4\u9053\uff1a\u201c\u7389\u5802\u91d1\u9a6c\uff0c \u9999\u88ab\u9526\u5c4f\uff0c\u8fd9\u4e9b\u90fd\u662f\u955c\u82b1\u6c34\u6708\u4e86\u3002\u201d\u518d\u542c\u4e0b\u53bb\u662f\uff1a\u201c\u6b32\u594f\u6c5f\u5357\u8c03\uff0c\u8d2b\u5c01\u84df\u5317\u4e66\u3002\u4e66\u4e2d\u65e0 \u522b\u610f\uff0c\u4f46\u6005\u4e45\u79bb\u5c45\u3002\u201d\u4e0d\u89c9\u6f78\u7136\u6cea\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u5357\u84df\u5317\uff0c\u4ec6\u4ec6\u98ce\u5c18\uff0c\u4f55\u65e5\u91cd\u6e29\uff1f\u786e\u662f \u4ee4\u4eba\u60c6\u6005\u3002\u201d\u4e0a\u5bab\u5a49\u513f\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\u4e0d\u63d0\u8fd9\u4e9b\u5fc3\u70e6\u4e4b\u4e8b\uff0c\u5374\u53c8\u6765\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e24\u4eba\u7ed3\u4f34\u540c\u884c\uff0c\u524d\u5f80\u5df4\u5dde\u3002\u4e00\u8def\u4e4b\u4e0a\uff0c\u674e\u9038\u65f6\u800c\u8c6a\u60c5\u52c3\u53d1\uff0c\u65f6\u800c\u90c1\u90c1\u5be1\u6b22\uff0c\u8fd9 \u79cd\u81ea\u8d1f\u662f\u7edd\u4e16\u82f1\u96c4\uff0c\u5374\u53c8\u662f\u843d\u62d3\u738b\u5b59\u7684\u5fc3\u60c5\uff0c\u4e5f\u53ea\u6709\u4e0a\u5b98\u5a49\u513f\uff0c\u80fd\u591f\u7a0d\u7a0d\u7406\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u5df4\u5dde\u591c\u542c\u5bab\u95f1\u79d8 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e09\u56de\u3000\u5df4\u5dde\u591c\u542c\u5bab\u95f1\u79d8 \u3000\u3000\u8d70\u4e86\u4e24\u5929\uff0c\u79bb\u5df4\u5dde\u53ea\u6709\u767e\u4f59\u91cc\u4e86\uff0c\u9053\u8def\u4e5f\u5e73\u5766\u5f97\u591a\uff0c\u674e\u9038\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6284\u5c0f\u8def\u8d70\uff0c \u7a0d\u7a0d\u5206\u5f00\u4e00\u4e9b\uff0c\u4e0d\u8981\u8ba9\u4eba\u770b\u51fa\u54b1\u4eec\u662f\u540c\u4e00\u8def\u7684\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u4f55\u7b49\u806a\u660e\uff0c\u4e00\u542c\u4fbf\u77e5\u5176\u610f\uff0c \u7b11\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u518d\u5f80\u9a7f\u8fb9\u4e00\u8d70\uff0c\u5c31\u8981\u78b0\u5230\u4e18\u795e\u52cb\u7684\u5927\u519b\u4e86\uff0c\u4f60\u662f\u738b\u5b59\u8eab\u4efd\uff0c\u81ea\u53d1\u907f\u5f00\u4e3a \u5999\u3002\u201d \u3000\u3000\u674e\u9038\u5237\u7684\u4e00\u97ad\uff0c\u50ac\u5f97\u90a3\u5339\u7626\u9a6c\u56db\u8e44\u75be\u8d70\uff0c\u4e0a\u5bab\u5a49\u513f\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u7626\u9a6c\u5176\u8c8c\u4e0d\u626c\uff0c\u8dd1\u5f97\u5374\u662f\u751a\u5feb\uff01\u201d\u674e\u9038\u6447\u624b\u793a\u610f\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u8ddd\u79bb\u5341 \u6570\u4e08\u5730\uff0c\u4e0a\u5b98\u5a49\u513f\u50ac\u52a8\u9752\u9a74\uff0c\u8ddf\u5728\u4ed6\u7684\u540e\u9762\uff0c\u59cb\u7ec8\u4fdd\u6301\u7740\u5341\u6570\u4e08\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u524d\u9762\u662f\u4e00\u5ea7\u5c0f\u5c71\uff0c\u9a7f\u9053\u5728\u5c71\u7684\u5357\u9762\uff0c\u5c0f\u8def\u5219\u5728\u5c71\u7684\u5317\u9762\uff0c\u4e0a\u5b98\u5a49\u513f\u7ed5\u7740\u5c0f\u5c71\uff0c\u7b56\u9a74 \u75be\u8d70\uff0c\u9690\u542c\u5f97\u5c71\u7684\u90a3\u8fb9\uff0c\u6218\u9a6c\u5636\u9e23\uff0c\u5927\u519b\u884c\u8fdb\u7684\u58f0\u97f3\u3002\u5fc3\u4e2d\u60f3\u9053\uff1b\u201c\u4ed6\u8981\u6211\u4e0e\u4ed6\u7a0d\u7a0d\u5206 \u5f00\uff0c\u60f3\u5fc5\u662f\u6015\u8fde\u7d2f\u4e8e\u6211\uff0c\u5440\uff0c\u6211\u8eab\u8d1f\u8840\u6d77\u6df1\u4ec7\uff0c\u6b20\u5fd7\u523a\u6740\u4ec7\u654c\uff0c\u8fd8\u6015\u4f60\u8fde\u7d2f\u4ec0\u4e48\uff1f\u5012\u662f \u4f60\u8981\u8d77\u5175\u8ba8\u4f10\u6b66\u5219\u5929\uff0c\u5374\u771f\u8981\u8fde\u7d2f\u8001\u767e\u59d3\u5462\u3002\u201d \u3000\u3000\u4e24\u4eba\u8f7b\u9a91\u75be\u8fdb\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u7ed5\u8fc7\u4e86\u90a3\u5ea7\u5c0f\u5c71\uff0c\u4e0a\u5b98\u5a49\u513f\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u65cc\u65d7\u62db\u5c55\uff0c \u5927\u519b\u5c31\u5728\u80cc\u540e\u6570\u91cc\u4e4b\u9065\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u5728\u54b1\u4eec\u5df2\u8d76\u8fc7\u524d\u5934\uff0c\u5426\u5219\u7eb5\u65e0\u610f\u5916\uff0c\u884c\u7a0b\u4ea6\u5c06\u53d7\u963b\u3002\u201d \u5fc3\u5ff5\u65b9\u52a8\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u53f7\u89d2\uff0c\u4e00\u5458\u6b66\u5c06\u5e26\u4e24\u9a91\u5feb\u9a6c\uff0c\u75be\u8ffd\u4e0a\u6765\uff0c\u90a3\u6b66\u5c06\u5927\u58f0\u559d\u9053\uff0c\u201c\u524d \u9762\u8d70\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u7ed9\u6211\u7559\u4e0b\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u6012\u9053\uff1a\u201c\u5927\u8def\u4e4b\u4e0a\uff0c\u8c01\u8d70\u4e0d\u5f97\uff1f\u6211\u53c8\u6ca1\u72af\u738b\u6cd5\uff0c\u4f60\u51ed\u4ec0\u4e48\u7559\u6211\uff1f\u201d\u90a3\u6b66\u5c06 \u65a5\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5201\u5634\u7684\u4e2b\u5934\uff01\u201d\u5f13\u5f26\u4e00\u54cd\uff0c\u5229\u7bad\u7a7f\u7a7a\uff0c\u7adf\u7136\u5411\u4e0a\u5b98\u5a49\u513f\u5c04\u6765\uff0c\u4e0a\u5b98\u5a49\u513f \u5927\u6012\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u3000\u3000\u201c\u8033\u95fb\u662f\u5047\uff0c\u76ee\u89c1\u4e3a\u771f\uff0c\u6b66\u5219\u5929\u624b\u4e0b\u7684\u5c06\u519b\uff0c\u5374\u539f\u6765\u662f\u8fd9\u6837\u6b3a\u51cc\u767e\u59d3\uff01\u201d\u53cd\u624b\u4e00\u626c\uff0c \u4e00\u67c4\u5315\u9996\u98de\u4e86\u51fa\u53bb\uff0c\u4f46\u542c\u5f97\u94ee\u7684\u4e00\u58f0\uff0c\u5315\u9996\u7adf\u7136\u7ed9\u6253\u7bad\u5c04\u843d\uff1a\u90a3\u5229\u7bad\u7ed9\u78b0\u6b6a\u4e86\u51c6\u5934\uff0c\u659c \u659c\u7684\u843d\u5728\u9752\u9a74\u811a\u4e0b\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u5927\u9a87\uff1a\u8fd9\u6b66\u5c06\u597d\u5927\u7684\u624b\u52b2\u3002\u6025\u97ad\u9752\u9a74\uff0c\u90a3\u9a74\u53d7\u4e86\u60ca\u5413\uff0c\u7adf\u7136\u79bb\u5f00\u4e86\u5927 \u8def\uff0c\u8dd1\u5230\u8def\u65c1\u7684\u519c\u5730\u53bb\u4e86\u3002\u90a3\u6b66\u5c06\u7b56\u9a6c\u8ffd\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u4e0d\u505c\u4e0b\u6765\u5417\uff1f\u201d\u5f13\u5982\u9739\u96f3\uff0c\u7bad \u53bb\u5f26\u60ca\uff0c\u55d6\u7684\u53c8\u662f\u4e00\u7bad\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u6b63\u5f85\u62d4\u7bad\u53d1\u5c04\uff0c\u5ffd\u89c1\u8517\u7530\u91cc\u8df3\u51fa\u4e00\u4e2a\u519c\u592b\uff0c\u6012\u58f0\u65a5\u9053\uff0c\u201c\u5929\u540e\u6709\u4ee4\uff0c\u4fdd\u62a4 \u519c\u7530\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8df5\u8e0f\u6211\u7684\u8517\u7530\uff1f\u62ab\u4e0a\u4e86\u8001\u864e\u76ae\uff0c\u5c31\u4e0d\u8bb2\u7406\u4e86\u4e48\uff1f\u201d\u62fe\u8d77\u4e24\u5757\u77f3\u5934\uff0c\u5411\u90a3 \u5c06\u519b\u4fbf\u6253\u3002\u7b2c\u4e00\u5757\u77f3\u5934\u6253\u843d\u4e86\u5c04\u5411\u4e0a\u5b98\u8155\u513f\u7684\u5229\u7bad\uff0c\u7b2c\u4e8c\u5757\u77f3\u5934\u6253\u4e2d\u90a3\u5339\u6218\u9a6c\u3002 \u3000\u3000\u6218\u6597\u4e00\u58f0\u5386\u9e23\uff0c\u56db\u817f\u8dea\u5730\uff0c\u767b\u65f6\u628a\u90a3\u4e2a\u5c06\u519b\u6454\u5012\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8fd9\u4e00\u60ca\u66f4\u751a\uff0c\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4e00\u4e2a\u666e\u901a\u7684\u519c\u592b\u7adf\u6709\u8fd9\u6837\u9ad8\u5f3a\u7684\u6b66\u529f\uff0c\u65b9\u81ea \u4e00\u6123\uff0c\u540e\u9762\u90a3\u4e24\u9a91\u5feb\u9a6c\u4e5f\u5df2\u8ffd\u5230\u4e86\u3002 \u3000\u3000\u90a3\u519c\u592b\u53eb\u9053\uff1a\u201c\u597d\u54c7\uff0c\u8fd9\u51e0\u5e74\u6765\u6211\u672a\u66fe\u89c1\u8fc7\u5982\u6b64\u4e0d\u8bb2\u9053\u7406\u7684\u5927\u5175\uff0c\u6211\u63ea\u4f60\u5230\u4e3b\u5e05\u9762 \u524d\u8bb2\u7406\u53bb\uff01\u201d\u8fce\u7740\u90a3\u4e24\u9a91\u5feb\u9a6c\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u4e24\u9a91\u5feb\u9a6c\u98de\u5954\u800c\u6765\uff0c\u51b2\u529b\u4f55\u6b62\u5343\u65a4\uff0c\u7ed9\u4ed6\u4e24 \u81c2\u4e00\u62e6\u4e00\u52d2\uff0c\u7adf\u7136\u90fd\u7ffb\u5012\u4e86\u3002\u90a3\u634e\u519b\u62d4\u51fa\u9a6c\u5200\uff0c\u5c31\u8981\u52a8\u624b\uff0c\u5ffd\u542c\u53f7\u89d2\u957f\u9e23\uff0c\u56de\u5934\u4e00\u671b\uff0c \u53ea\u89c1\u4e00\u4e2a\u7259\u5c06\uff0c\u6325\u821e\u7740\u4e00\u9762\u5927\u65d7\uff0c\u90a3\u662f\u62db\u4ed6\u4eec\u5f52\u961f\u7684\u8baf\u53f7\uff0c\u5c06\u519b\u9762\u9c7c\u4e00\u53d8\uff0c\u5c06\u9a6c\u62c9\u8d77\uff0c \u6025\u6025\u4e0a\u9a6c\uff0c\u98de\u5954\u800c\u56de\u3002\u4ed6\u7684\u4e24\u4e2a\u968f\u4ece\u6478\u51fa\u4e86\u51e0\u94b1\u788e\u94f6\uff0c\u629b\u5728\u5730\u4e0a\u9053\uff1a\u201c\u522b\u56b7\uff0c\u522b\u56b7\u3002\u7b97 \u6211\u4eec\u6015\u4e86\u4f60\uff0c\u8df5\u8e0f\u4e86\u4f60\u7684\u8517\u7530\uff0c\u8fd9\u662f\u8d54\u7ed9\u4f60\u7684\uff0c\u201d\u90a3\u519c\u5927\u5495\u5495\u565c\u565c\u7684\u56b7\u9053\uff1a \u3000\u3000\u201c\u51e0\u94b1\u94f6\u5b50\u5c31\u60f3\u5c01\u4f4f\u6211\u7684\u5634\u4e48\uff1f\u201d\u542c\u6765\u7adf\u8fd8\u662f\u4e0d\u670d\u6c14\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7684\u9752\u9a74\u5df2\u9a70\u51fa\u4e00\u7bad\u4e4b\u9065\uff0c\u542c\u540e\u9762\u7684\u58f0\u606f\uff0c\u519c\u592b\u5df2\u628a\u5c06\u519b\u8d76\u8dd1\uff0c\u5979\u672c\u6765\u60f3\u56de \u53bb\u5411\u519c\u592b\u9053\u8c22\uff0c\u4f46\u89c1\u674e\u9038\u5728\u9a6c\u80cc\u4e0a\u8f7b\u8f7b\u6447\u9996\uff1b\u5237\u5237\u5237\u4e00\u8fde\u51e0\u97ad\uff0c\u6253\u5f97\u90a3\u5339\u7626\u9a6c\u8dd1\u5f97\u66f4\u5feb \u4e86\u3002\u4e0a\u5b98\u5a49\u513f\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5927\u519b\u5c31\u5728\u540e\u9762\uff0c\u65e2\u5df2\u8131\u9669\uff0c\u8fd8\u8fc7\u53bb\u60f9\u4ec0\u4e48\u9ebb\u70e6\uff1f\u6b64\u65f6\u5979\u867d\u7136\u77e5 \u9053\u4e86\u8fd9\u4e2a\u519c\u592b\u51b3\u975e\u5e38\u4eba\uff0c\u4e5f\u53ea\u5f97\u6291\u4e0b\u597d\u5947\u4e4b\u5ff5\uff0c\u97ad\u7b56\u9752\u9a74\u8d76\u8def\u3002 \u3000\u3000\u5230\u8fbe\u5df4\u5dde\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u4e0a\u5b98\u5a49\u513f\u88c5\u4f5c\u4e0e\u674e\u9038\u4e0d\u76f8\u8ba4\u8bc6\uff0c\u5f85\u4ed6\u8fdb\u4e86\u5ba2\u6808\u4e4b\u540e\uff0c\u81ea \u5df1\u518d\u5728\u8857\u4e0a\u901b\u4e00\u4f1a\u3002\u4f46\u89c1\u5e02\u5bb9\u6574\u6d01\uff0c\u53ea\u662f\u5404\u5904\u8857\u5934\uff0c\u90fd\u6709\u5175\u58eb\u7ad9\u5c97\uff0c\u60f3\u662f\u51c6\u5907\u8fce\u63a5\u4e18\u795e \u52cb\u7684\u5927\u519b\u7684\u3002\u4e0a\u5b98\u5a49\u513f\u4e0d\u6562\u4e71\u8d70\uff0c\u56de\u5230\u90a3\u5bb6\u5ba2\u5e97\u6295\u5bbf\uff0c\u5374\u4e0d\u77e5\u674e\u9038\u4f4f\u5728\u54ea\u95ee\u623f\u5b50\uff0c\u53c8\u4e0d \u4fbf\u5411\u5e97\u5c0f\u4e8c\u67e5\u95ee\u3002\u5403\u8fc7\u665a\u996d\u4e4b\u540e\uff0c\u6b63\u51c6\u5907\u518d\u51fa\u53bb\u63a2\u671b\uff0c\u5ffd\u5730\u6709\u4eba\u5f71\u5728\u7a97\u4e2a\u4e00\u6643\uff0c\u556a\u631e\u4e00 \u58f0\uff0c\u4e22\u4e86\u4e00\u9897\u77f3\u5b50\u8fdb\u6765\u3002\u4e0a\u5b98\u5a49\u513f\u63a8\u7a97\u4e00\u671b\uff0c\u53ea\u89c1\u674e\u9038\u7684\u80cc\u5f71\u5df2\u8d70\u51fa\u5e97\u95e8\u3002\u4e0a\u5b98\u5a49\u513f\u62fe \u8d77\u90a3\u9897\u77f3\u5b50\uff0c\u77f3\u5b50\u662f\u7528\u7eb8\u5305\u7740\u7684\uff0c\u4e0a\u5b98\u5a49\u51e0\u628a\u90a3\u5f20\u7eb8\u5c55\u5f00\u6765\u770b\uff0c\u4e0a\u9762\u5199\u9053\uff1a\u201c\u6211\u6709\u6025\u4e8b \u51fa\u53bb\uff0c\u4eca\u665a\u672a\u5fc5\u56de\u6765\uff0c\u8bf7\u4f60\u5728\u4e09\u66f4\u4e4b\u524d\uff0c\u52a1\u5fc5\u53bb\u63a2\u671b\u592a\u5b50\uff0c\u53eb\u4ed6\u5c0f\u5fc3\u5728\u610f\uff0c\u4e0d\u53ef\u4e0e\u4e18\u795e \u52cb\u76f8\u89c1\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e18\u795e\u52cb\u5949\u4e86\u6b66\u5219\u5929\u4e4b\u547d\u800c\u6765\uff0c\u592a\u5b50\u5c82\u80fd\u4e0d\u89c1\uff1f\u96be\u9053\u6b66 \u5219\u5929\u771f\u7684\u4f1a\u5bb3\u81ea\u5df1\u7684\u513f\u5b50\u5417\uff1f\u201d\u4e00\u770b\uff0c\u90a3\u5f20\u7eb8\u4e0a\u8fd8\u6709\u8be6\u56fe\uff0c\u6307\u793a\u592a\u5b50\u6240\u4f4f\u7684\u5730\u65b9\u3002 \u3000\u3000\u6b47\u4e86\u4e00\u4f1a\uff0c\u542c\u5f97\u4e8c\u66f4\u9f13\u54cd\uff0c\u4e0a\u5b98\u5a49\u513f\u6362\u4e86\u591c\u884c\u8863\u670d\uff0c\u6084\u6084\u6e9c\u51fa\uff0c\u5916\u9762\u6b63\u4e0b\u7740\u7ec6\u96e8\uff0c \u65e0\u661f\u65e0\u6708\uff0c\u5927\u8272\u6c89\u6697\uff0c\u4e0a\u5b98\u5a49\u513f\u8f7b\u529f\u672c\u9ad8\uff0c\u8fd9\u4e00\u6765\u66f4\u662f\u65e0\u4eba\u53d1\u73b0\uff0c\u4f46\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u5374\u8d70 \u9519\u4e86\u8bb8\u591a\u51a4\u6789\u8def\uff0c\u624d\u627e\u5230\u5e9f\u592a\u5b50\u6240\u4f4f\u7684\u738b\u5e9c\u3002 \u3000\u3000\u5e9f\u592a\u5b50\u674e\u8d24\u56e0\u4e3a\u662f\u88ab\u8d2c\u8c2a\u7684\uff0c\u6b66\u5219\u5929\u53c8\u51b3\u610f\u8981\u4ed6\u78e8\u7ec3\uff0c\u7ed9\u4ed6\u6240\u5efa\u7684\u201c\u7ae0\u6000\u738b\u5e9c\u201d\u5e76 \u4e0d\u5f88\u5927\uff0c\u53ea\u6709\u4e03\u516b\u680b\u623f\u5b50\uff0c\u4e00\u4e2a\u5c0f\u82b1\u56ed\uff0c\u5916\u9762\u867d\u6709\u4e00\u9053\u56f4\u5899\uff0c\u4e5f\u53ea\u6709\u4e00\u4e08\u4e94\u516d\u9ad8\uff0c\u8bba\u6c14 \u6d3e\uff0c\u8fd8\u6bd4\u4e0d\u4e0a\u77e5\u5e9c\u8859\u95e8\u3002\u4e0a\u5b98\u5a49\u513f\u8df3\u5165\u82b1\u56ed\uff0c\u89c1\u82b1\u56ed\u4e1c\u4fa7\u6709\u4e00\u5ea7\u5c0f\u697c\uff0c\u697c\u4e2d\u8fd8\u6709\u706f\u706b\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u592a\u4e8e\u6700\u559c\u8bfb\u4e66\uff0c\u6562\u60c5\u5c31\u662f\u4ed6\u5728\u91cc\u9762\u3002\u201d\u98de\u8eab\u63a0\u4e0a\u697c\u9876\uff0c\u4f7f\u4e00\u4e2a\u201c\u73cd\u73e0\u5012\u5377 \u5e18\u201d\u7684\u59ff\u52bf\uff0c\u52fe\u7740\u98de\u6a90\uff0c\u63a2\u5934\u5185\u671b\u3002 \u3000\u3000\u53ea\u89c1\u623f\u4e2d\u6709\u4e00\u4e2a\u7626\u524a\u7684\u9752\u5e74\uff0c\u4e00\u4e2a\u5e74\u8001\u7684\u5927\u76d1\uff0c\u6848\u5934\u6709\u4e00\u90e8\u7ffb\u5f00\u7684\u300a\u53f2\u8bb0\u300b\uff0c\u4e0a\u5b98 \u5a49\u513f\u5c0f\u65f6\u4e5f\u66fe\u89c1\u8fc7\u592a\u5b50\uff0c\u4f9d\u7a00\u8fd8\u8ba4\u5f97\u51fa\u6765\u3002 \u3000\u3000\u6b63\u60f3\u8fdb\u53bb\uff0c\u5ffd\u542c\u5f97\u5e9f\u592a\u5b50\u674e\u8d24\u8bf4\u9053\uff1a\u201c\u738b\u516c\u516c\uff0c\u8fd9\u4e24\u65e5\u6765\u6211\u603b\u662f\u5fc3\u795e\u4e0d\u5b89\uff0c\u4e18\u795e\u52cb \u7684\u5927\u519b\u5df2\u5230\u57ce\u5916\uff0c\u660e\u66f0\u4e00\u65e9\uff0c\u5fc5\u6765\u89c1\u6211\uff0c\u6211\u770b\u54b1\u4eec\u8fd8\u662f\u8fde\u591c\u9003\u8d70\u4e86\u5427\u3002\u201d \u3000\u3000\u90a3\u8001\u592a\u76d1\u9762\u8272\u6781\u4e3a\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u5929\u540e\u6d3e\u4e18\u5927\u5c06\u519b\u524d\u6765\u63a2\u671b\u4e8e\u4f60\uff0c\u6b63\u662f\u5929\u5927 \u7684\u559c\u8baf\u554a\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u6d3e\u4ed6\u8fce\u4f60\u56de\u671d\uff0c\u4f60\u600e\u4e48\u8fd8\u8981\u9003\u8d70\uff1f\u201d \u3000\u3000\u674e\u8d24\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u6211\u5fc3\u91cc\u5bb3\u6015\u5f97\u5f88\u3002\u6bcd\u540e\u65e9\u5c31\u8981\u6d3e\u90d1\u6e29\u6765\u63a2\u671b\u6211\uff0c\u8bba\u65e5\u7a0b\u90d1\u6e29\u5341 \u5929\u4e4b\u524d\u5c31\u8be5\u5230\u4e86\uff0c\u800c\u4e2a\u4e0d\u89c1\u90d1\u6e29\uff0c\u5374\u53cd\u800c\u6d3e\u4e18\u795e\u52cb\u6765\uff0c\u90d1\u6e29\u662f\u6587\u5b98\uff0c\u6211\u6ca1\u731c\u7591\uff0c\u8fd9\uff0c\u8fd9 \u4e18\u795e\u52cb\u5374\u662f\u6b66\u5c06\uff0c\u4ed6\uff0c\u4ed6\u5e26\u5175\u524d\u6765\uff0c\u2026\u2026\u83ab\u975e\uff0c\u83ab\u975e\u2026\u2026\u201d \u3000\u3000\u90a3\u592a\u76d1\u9053\uff1a\u201c\u6bb4\u4e0b\u6015\u4e18\u5c06\u519b\u5e26\u519b\u524d\u6765\uff0c\u5c06\u6709\u4e0d\u5229\u4e8e\u4f60\u4e48\uff1f\u201d\u674e\u8d24\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u770b\u795e\u60c5\uff0c \u592a\u76d1\u6b63\u8bf4\u4e2d\u4ed6\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u90a3\u5927\u76d1\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u5730\u8dea\u4e0b\u53bb\u5411\u674e\u8d24\u78d5\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5974\u624d\u6709\u4e00\u53e5\u8be5\u6b7b\u7684\u8bdd\uff0c\u8bf7\u6bbf\u4e0b \u6055\u7f6a\uff0c\u5974\u624d\u65b9\u6562\u8bf4\u3002\u201d \u3000\u3000\u674e\u8d24\u6025\u5fd9\u5c06\u4ed6\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u516c\u516c\uff0c\u4f60\u662f\u670d\u4f8d\u8fc7\u6211\u7236\u7687\u7684\u4eba\uff0c\u6211\u5f53\u4f60\u81ea\u5df1\u4eba\u4e00\u6837\uff0c \u6709\u4ec0\u4e48\u6d3b\u4e0d\u53ef\u4ee5\u8bf4\u3002\u201d \u3000\u3000\u90a3\u592a\u76d1\u9053\uff1a\u201c\u5982\u6b64\u6211\u6562\u5192\u6b7b\u8bf7\u95ee\u6bbf\u4e0b\uff0c\u5929\u540e\u5bf9\u5f85\u6bbf\u4e0b\u5982\u4f55\uff1f\u201d\u674e\u8d24\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u770b \u5982\u4f55\uff1f\u201d\u592a\u76d1\u9053\uff1a\u201c\u4f9d\u5974\u624d\u770b\u6765\uff0c\u5929\u540e\u867d\u7136\u5fd9\u4e8e\u671d\u653f\uff0c\u4e0d\u80fd\u5e38\u4e0e\u6bbf\u4e0b\u76f8\u805a\uff0c\u5bf9\u6bbf\u4e0b\u5374\u5f88 \u662f\u4f53\u8d34\u5173\u5fc3\u3002\u201d\u674e\u8d24\u9053\u201c\u6bd4\u8d77\u5bf9\u6211\u7684\u54e5\u54e5\u6765\uff0c\u6bcd\u540e\u5bf9\u6211\u603b\u7b97\u662f\u5bbd\u539a\u7684\u4e86\u3002\u201d \u3000\u3000\u90a3\u592a\u76d1\u9053\uff1a\u201c\u5982\u6b64\u8bf7\u518d\u6055\u6211\u5192\u72af\uff0c\u8bf7\u95ee\u6bbf\u4e0b\u4f55\u6545\u731c\u7591\u6bcd\u540e\uff1f\u201d \u3000\u3000\u674e\u8d24\u9762\u8272\u5927\u53d8\uff0c\u5ffd\u5730\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u516c\u516c\uff0c\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u6211\u662f\u4e0d\u662f\u5979\u7684\u513f\u5b50\uff1f\u201d\u592a\u76d1 \u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6211\u4e0d\u61c2\u6bbf\u4e0b\u7684\u610f\u601d\u3002\u201d\u674e\u8d24\u9053\uff1a \u3000\u3000\u201c\u5b98\u4e2d\u6709\u4eba\u8c08\u8bba\uff0c\u8bf4\u6211\u4e0d\u662f\u5929\u540e\u7684\u4eb2\u751f\u513f\u4e86\uff01\u201d\u90a3\u592a\u76d1\u9053\uff1a\u201c\u55ef\uff0c\u6709\u8fd9\u6837\u7684\u8c08\u8bba\u5417\uff1f\u201d \u542c\u4ed6\u7684\u8bed\u6c14\uff0c\u4f3c\u4e4e\u65e9\u5df2\u77e5\u9053\u5bab\u4e2d\u6709\u8fd9\u6837\u7684\u4f20\u8a00\u3002\u201d \u3000\u3000\u674e\u8d24\u9053\uff1a\u201c\u5979\u4eec\u8bf4\u6211\u7684\u6bcd\u4eb2\u662f\u5929\u540e\u7684\u4eb2\u59d0\u59d0\u97e9\u56fd\u592b\u4eba\uff0c\u6211\u7684\u6bcd\u4eb2\u662f\u7ed9\u5929\u540e\u6bd2\u6b7b\u7684\uff01 \u6211\u7684\u4eb2\u54e5\u54e5\u5148\u592a\u5b50\u674e\u5f18\uff0c\u4ed6\u4e5f\u4e0d\u662f\u5929\u540e\u7684\u513f\u5b50\uff0c\u540e\u6765\u4ed6\u4e5f\u662f\u7ed9\u5929\u540e\u5728\u5408\u58c1\u5bab\u91cc\u6bd2\u6b7b\u7684\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u4e00\u9707\uff0c\u53ea\u542c\u5f97\u674e\u8d24\u98a4\u58f0\u7eed\u9053\uff1a\u201c\u81ea\u4ece\u6211\u542c\u5230\u8fd9\u4ef6\u4e8b\u4e4b\u540e\uff0c\u8fd9\u51e0\u5e74\u6765\u6211 \u9b42\u68a6\u4e0d\u5b89\uff0c\u751f\u6015\u5929\u540e\u4e5f\u8981\u5c06\u6211\u6697\u5bb3\uff0c\u4e8e\u662f\uff0c\u4e8e\u662f\u2014\u2014\u738b\u516c\u516c\uff0c\u6211\u90fd\u5bf9\u4f60\u8bf4\u4e86\u5427\uff0c\u4f60\u77e5\u9053 \u6211\u4e3a\u4ec0\u4e48\u88ab\u5929\u540e\u8d2c\u5230\u5df4\u5dde\uff1f\u201d \u3000\u3000\u90a3\u592a\u76d1\u9053\uff1a\u201c\u5929\u540e\u662f\u60f3\u6bbf\u4e0b\u5230\u6c11\u95f4\u5386\u7ec3\u5386\u7ec3\uff0c\u5c06\u6765\u597d\u6cbb\u56fd\u6cbb\u6c11\u3002\u201d \u3000\u3000\u674e\u8d24\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u56e0\u4e3a\u6211\u6015\u5929\u540e\u6697\u5bb3\u6211\u3002\u6211\u5728\u4e1c\u5b98\u4f0f\u4e0b\u7532\u5175\uff0c\u6211\u60f3\u5148\u4e0b\u624b\u529b\u5f3a\uff0c \u6211\u60f3\u4ece\u5979\u7684\u624b\u4e2d\u593a\u56de\u6211\u674e\u5bb6\u7684\u5929\u4e0b\uff0c\u5440\uff0c\u4e0d\u6599\u4e8b\u673a\u4e0d\u5bc6\uff0c\u7ed9\u5979\u5148\u53d1\u89c9\u4e86\u3002\u201d \u3000\u3000\u90a3\u592a\u76d1\u9053\uff1a\u201c\u6bbf\u4e0b\u5440\uff0c\u4f60\u600e\u80fd\u505a\u51fa\u8fd9\u6837\u7684\u4e8b\u6765\uff1f\u90a3\u4e48\uff0c\u5929\u540e\u5bf9\u4f60\uff0c\u4e5f\u786e\u5b9e\u7b97\u5f97\u662f\u5bbd \u539a\u4e4b\u6781\u4e86\uff01\u201d \u3000\u3000\u674e\u8d24\u9053\uff1a\u201c\u4f60\u4e5f\u5e2e\u5979\u8bf4\u8bdd\uff1f\u201d\u8138\u4e0a\u7684\u795e\u8272\u975e\u5e38\u75db\u82e6\uff0c\u98a4\u58f0\u7eed\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u5979\u5bf9\u6211\u592a \u5bbd\u539a\u4e86\uff0c\u6240\u4ee5\u6709\u65f6\u6211\u53c8\u6000\u7591\u5bab\u4e2d\u7684\u4f20\u8bf4\u4e0d\u662f\u771f\u7684\uff1f\u201d\u6709\u4e00\u6b21\u6211\u60a3\u4e86\u91cd\u75c5\uff0c\u534a\u591c\u9192\u6765\uff0c\u89c1 \u5979\u6cea\u5149\u83b9\u7136\uff0c\u5750\u5728\u6211\u7684\u8eab\u8fb9\uff0c\u51dd\u671b\u7740\u6211\uff0c\u771f\u50cf\u662f\u5929\u4e0b\u6700\u8471\u7231\u7684\u6bcd\u4eb2\uff0c\u5728\u90a3\u4e00\u5239\u90a3\uff0c\u6211\u51e0 \u4e4e\u60f3\u5411\u5979\u6094\u7f6a\uff0c\u628a\u5fc3\u4e2d\u7684\u6000\u7591\u90fd\u5bf9\u5979\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u592a\u76d1\u9053\uff0c\u201c\u90a3\u4e48\u6bbf\u4e0b\u4e3a\u4f55\u4e0d\u8bf4\uff1f\u201d\u674e\u8d24\u9053\uff1a\u201c\u6211\u53c8\u6015\u5979\u662f\u6545\u610f\u88c5\u51fa\u6765\u7684\uff0c\u5440\uff0c\u6211\u7684 \u5fc3\u4e71\u5f97\u5f88\uff0c\u4e71\u5f97\u5f88\uff0c\u771f\u771f\u5047\u5047\uff0c\u662f\u662f\u975e\u975e\uff0c\u90fd\u597d\u50cf\u4e00\u56e2\u8ff7\u96fe\uff01\u201d \u3000\u3000\u90a3\u4eba\u76d1\u5ffd\u5730\u4e00\u58f0\u957f\u53f9\uff0c\u9053\uff1a\u201c\u5bab\u4e2d\u7684\u8c23\u8a00\u6700\u591a\uff0c\u5e78\u559c\u4eca\u5929\u6bbf\u4e0b\u5bf9\u8001\u5974\u8bf4\u4e86\uff0c\u8fd9\u4e9b\u4e8b \u60c5\uff0c\u8001\u5974\u77e5\u9053\u5f97\u6700\u6e05\u695a\uff0c\u201d \u3000\u3000\u674e\u8d24\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u738b\u516c\u516c\uff0c\u4f60\u5feb\u628a\u771f\u5b9e\u7684\u4e8b\u60c5\u8bf4\u7ed9\u6211\u77e5\u9053\uff01 \u3000\u3000\u5929\u540e\u5230\u5e95\u662f\u4e0d\u662f\u6211\u7684\u6bcd\u4eb2\uff1f\u201d \u3000\u3000\u90a3\u592a\u76d1\u9053\uff1a\u201c\u4f60\u548c\u4f60\u7684\u54e5\u54e5\u90fd\u662f\u5929\u540e\u7684\u4eb2\u751f\u513f\u5b50\uff01\u4e0d\u8fc7\u5bab\u4e2d\u7684\u8c23\u8a00\u4e5f\u4e0d\u662f\u65e0\u56e0\u800c\u81f3\uff0c \u672c\u6765\u6211\u4e0d\u6562\u8bf4\uff0c\u4f46\u6bbf\u4e0b\u5bf9\u6bcd\u540e\u5982\u6b64\u731c\u7591\uff0c\u903c\u5f97\u5974\u624d\u975e\u8bf4\u4e0d\u53ef\u4e86\u3002\u4f60\u54e5\u54e5\u5148\u592a\u5b50\u5f18\u662f\u5148\u5e1d \u6c38\u5fbd\u4e09\u5e74\u6b63\u6708\u751f\u7684\uff0c\u4f60\u662f\u540c\u5e74\u5341\u4e8c\u6708\u751f\u7684\u3002\u90a3\u65f6\u5929\u540e\u8fd8\u5728\u611f\u4e1a\u5bfa\u91cc\u505a\u5c3c\u59d1\u3002\u201d\u674e\u8d24\u800c\u4e0a \u4e00\u7ea2\uff0c\u4ed6\u4e5f\u77e5\u9053\u6bcd\u540e\u66fe\u662f\u4ed6\u7956\u7236\u592a\u5b97\u7687\u5e1d\u674e\u4e16\u6c11\u7684\u5983\u4e86\uff0c\u674e\u4e16\u6c11\u6b7b\u540e\uff0c\u6b66\u5219\u5929\u88ab\u8d50\u4ee4\u51fa \u5bb6\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u6bcd\u540e\u8fd8\u5728\u611f\u4e1a\u5bfa\u65f6\uff0c\u5c31\u548c\u6211\u7236\u7687\u79c1\u901a\u4e86\u3002\u201d\u867d\u7136\u592a\u76d1\u8bc1 \u5b9e\u4e86\u6b66\u5219\u706d\u786e\u662f\u4ed6\u7684\u6bcd\u4eb2\uff0c\u4ed6\u4e5f\u5927\u611f\u7f9e\u803b\u3002 \u3000\u3000\u90a3\u592a\u76d1\u8bf4\u9053\uff0c\u201c\u90a3\u65f6\u5148\u5e1d\u8fd8\u6ca1\u6709\u5c06\u5929\u540e\u63a5\u56de\u5bab\u4e2d\uff0c\u6015\u62db\u7269\u8bae\uff0c\u4e8e\u662f\u5c06\u4f60\u4eec\u4e24\u5144\u5f1f\u90fd \u624e\u97e9\u56fd\u592b\u4eba\u629a\u517b\uff0c\u8c23\u8a00\u5c31\u662f\u8fd9\u6837\u751f\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u8d24\u9053\uff1a\u201c\u90a3\u4e48\u6211\u7684\u54e5\u54e5\u5462\uff0c\u4ed6\u662f\u600e\u6837\u6b7b\u7684\uff1f\u201d\u592a\u76d1\u9053\uff1a\u201c\u5341\u591a\u5e74\u524d\u6709\u4e00\u4e2a\u4e4c\u837c\u56fd \u7684\u5a46\u7f57\u95e8\u7ed9\u5148\u5e1d\u914d\u4e86\u4e0d\u6b7b\u836f\uff0c\u5929\u540e\u529d\u963b\u5148\u5e1d\uff0c\u8bf4\u662f\u5343\u4e07\u4e0d\u53ef\u8f7b\u4fe1\u5a46\u7f57\u95e8\u7684\u90aa\u8bf4\uff0c\u4e16\u95f4\u54ea \u6709\u4e0d\u6b7b\u4e4b\u836f\uff1f\u5148\u5e1d\u56e0\u6b64\u5e76\u4e0d\u670d\u98df\uff0c\u4f46\u5374\u628a\u5b83\u85cf\u5728\u5408\u58c1\u5bab\u91cc\u3002\u4e0d\u6599\u4f60\u7684\u54e5\u54e5\u5374\u628a\u5b83\u5077\u53bb\u4e86\uff0c \u4f60\u7684\u54e5\u54e5\u4e5f\u50cf\u4f60\u4e00\u6837\uff0c\u8eab\u5b50\u5f88\u865a\u5f31\uff0c\u5403\u4e86\u5a46\u7f57\u95e8\u7684\u6240\u8c13\u4e0d\u6b7b\u836f\uff0c\u5f53\u665a\u5c31\u4e03\u7a8d\u6d41\u8840\uff0c\u6b7b\u5728 \u5408\u58c1\u5bab\u91cc\uff0c\u8fd9\u4e9b\u90fd\u662f\u5974\u624d\u4eb2\u773c\u89c1\u5230\u7684\u4e8b\u60c5\u3002\u67d0\u4e9b\u4eba\u4e71\u9020\u8c23\u8a00\uff0c\u6c61\u8511\u5929\u540e\uff0c\u771f\u662f\u7f6a\u8be5\u4e07\u6b7b\uff01\u201d \u674e\u8d24\u542c\u4e86\uff0c\u5446\u5446\u53d1\u6123\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u592a\u76d1\u53c8\u9053\uff1a\u201c\u81f3\u4e8e\u97e9\u56fd\u592b\u4eba\u7684\u6b7b\uff0c\u90a3\u66f4\u4e0e\u5929\u540e\u65e0\u5173\u3002\u8bf7\u6055\u5974\u624d\u6597\u80c6\uff0c\u6bbf\u4e0b\u65e2\u7136\u89c1 \u7591\uff0c\u6211\u5c06\u4e0d\u8be5\u8bf4\u7684\u8bf4\u4e86\u5427\uff0c\u8bf4\u8d77\u6765\u90a3\u662f\u5148\u5e1d\u7684\u7cca\u6d82\uff0c\u5929\u540e\u66ff\u4ed6\u7ba1\u7406\u56fd\u5bb6\u5927\u4e8b\uff0c\u4e00\u5929\u5230\u665a\uff0c \u5fd9\u4e2a\u4e0d\u4e86\uff0c\u90a3\u65f6\u97e9\u56fd\u592b\u4eba\u5e38\u5728\u5bab\u4e2d\uff0c\u5148\u5e1d\uff0c\u5440\uff0c\u5148\u5e1d\u548c\u5979\u505a\u51fa\u4e86\u5bf9\u4e0d\u8d77\u5929\u540e\u7684\u4e8b\uff0c\u7ed9\u5929 \u540e\u53d1\u89c9\u4e86\uff0c\u97e9\u56fd\u592b\u4eba\u81ea\u89c9\u65e0\u989c\uff0c\u6127\u5bf9\u59b9\u59b9\uff0c\u4fbf\u670d\u6bd2\u81ea\u5c3d\u4e86\u3002\u201d \u3000\u3000\u674e\u8d24\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u4e9b\u8bdd\u90fd\u662f\u8c23\u8a00\uff1f\u201d\u8001\u592a\u76d1\u53f9\u53e3\u6c14\u9053\uff1a \u3000\u3000\u201c\u5929\u540e\u79f0\u5e1d\uff0c\u4e0d\u77e5\u62db\u4e86\u591a\u5c11\u4eba\u4e4b\u5fcc\uff01\u504f\u504f\u5979\u638c\u7ba1\u56fd\u4e8b\u53c8\u7ba1\u5f97\u597d\uff0c\u90a3\u4e9b\u4eba\u65e0\u6cd5\u8bae\u8bba\u5979\uff0c \u4f7f\u53ea\u597d\u5728\u79c1\u4e8b\u4e0a\u9020\u5979\u7684\u8c23\uff0c\u5440\uff0c\u4e5f\u5c31\u504f\u504f\u6709\u8bb8\u591a\u4e0d\u8bc6\u5927\u4f53\u7684\u4eba\u76f8\u4fe1\uff01\u201d \u3000\u3000\u674e\u8d24\u9762\u4e0a\u4e00\u9635\u7ea2\u4e00\u9635\u767d\uff0c\u5fc3\u4e2d\u6709\u6127\uff0c\u60f3\uff1a\u201c\u8fde\u6211\u505a\u513f\u5b50\u7684\u4e5f\u731c\u7591\u81ea\u5df1\u7684\u6bcd\u4eb2\uff0c\u4f55\u51b5 \u4ed6\u4eba\uff1f\u201d\u53ea\u542c\u5f97\u90a3\u8001\u592a\u76d1\u53c8\u9053\uff1a\u201c\u5974\u624d\u8fd9\u6b21\u4f8d\u5949\u6bbf\u4e0b\uff0c\u51fa\u4eac\u4e4b\u65f6\uff0c\u5929\u540e\u4e5f\u66fe\u6bb7\u6bb7\u5631\u5490\uff0c \u8bf4\u6bb4\u4e0b\u4e0d\u77e5\u9053\u81ea\u5df1\u4fdd\u91cd\u8eab\u4f53\uff0c\u53eb\u5974\u624d\u5c0f\u5fc3\u5728\u610f\uff0c\u529d\u6bbf\u4e0b\u996e\u98df\u8981\u6709\u5b9a\u65f6\uff0c\u8bfb\u4e66\u4e0d\u53ef\u8fc7\u52b3\u3002 \u5927\u540e\u4e5f\u8fd8\u81ea\u6028\u81ea\u827e\uff0c\u8bf4\u81ea\u5df1\u5fd9\u4e8e\u56fd\u4e8b\uff0c\u5bf9\u513f\u5973\u90fd\u7167\u6599\u4e0d\u591f\uff0c\u5974\u624d\u8fd8\u966a\u4e86\u5929\u540e\u4f24\u5fc3\u4e86\u597d\u4e00 \u9635\u3002\u5929\u540e\u53ef\u6ca1\u6709\u534a\u53e5\u8bdd\u63d0\u8d77\u6bbf\u4e0b\u5728\u4e1c\u5bab\u4f0f\u4e0b\u7532\u5175\u7684\u4e8b\u3002\u201d \u3000\u3000\u674e\u8d24\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u8001\u5927\u76d1\u773c\u4e2d\u5df2\u6d8c\u51fa\u6cea\u73e0\uff0c\u4e0d\u7531\u5f97\u53c8\u6127\u53c8\u60b2\uff0c\u8981\u4e0d\u662f\u987e\u7740\u592a\u5b50\u7684 \u8eab\u4efd\uff0c\u771f\u60f3\u62b1\u7740\u90a3\u8001\u592a\u76d1\u5927\u54ed\u4e00\u573a\u3002 \u3000\u3000\u90a3\u8001\u5927\u76d1\u5c06\u8fd9\u8bb8\u591a\u5bab\u95f1\u79d8\u5bc6\u90fd\u8bf4\u4e86\uff0c\u5fc3\u4e2d\u60f4\u60f4\u4e0d\u5b89\uff0c\u5ffd\u542c\u5f97\u674e\u8d24\u8f8d\u6ce3\u4e4b\u58f0\uff0c\u5403\u4e86\u4e00 \u60ca\uff0c\u6025\u9053\uff1a\u201c\u5974\u624d\u8be5\u6b7b\uff0c\u55ef\uff0c\u6bbf\u4e0b\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u674e\u8d24\u5fc3\u60c5\u6fc0\u8361\uff0c\u5ffd\u5730\u6293\u8d77\u7b14\u6765\uff0c\u53eb\u9053\uff0c\u201c\u738b\u516c\u516c\uff0c\u4f60\u4e00\u70b9\u4e5f\u6ca1\u6709\u7f6a\u3002\u8be5\u6b7b\u7684\u662f\u6211\uff01 \u6bcd\u540e\u4e3a\u6211\u64cd\u52b3\u56fd\u4e8b\uff0c\u6211\u5374\u534a\u70b9\u4e5f\u4e0d\u8c05\u89e3\u5979\u7684\u6614\u5fc3\u3002\u5f90\u656c\u4e1a\u8981\u9020\u53cd\uff0c\u4e0a\u4e2a\u6708\u6d3e\u4e86\u5bc6\u4f7f\u6765\u89c1 \u6211\uff0c\u6211\u8fd8\u4e0e\u4ed6\u79c1\u901a\u6d88\u606f\uff0c\u610f\u6b32\u4e0e\u4ed6\u4e00\u540c\u5b66\u5175\u53cd\u6389\u81ea\u5df1\u7684\u6bcd\u4eb2\uff01\u53e4\u5f80\u4eca\u6765\uff0c\u90a3\u6709\u6211\u8fd9\u6837\u4e0d \u5b5d\u7684\u513f\u5b50\uff0c\u54fc\uff0c\u6211\u8fd8\u81ea\u547d\u662f\u8bfb\u4e66\u660e\u7406\u4e4b\u4eba\uff0c\u6211\u5982\u4eca\u4fbf\u8981\u5411\u6bcd\u540e\u8bf7\u7f6a\uff0c\u6211\u8981\u544a\u53d1\u5f90\u656c\u4e1a\uff0c \u6211\u8981\u8bf7\u6bcd\u540e\u7ed9\u6211\u5904\u5206\uff01\u201d \u3000\u3000\u8001\u592a\u76d1\u5927\u60ca\u9053\uff1a\u201c\u82f1\u56fd\u516c\u8981\u9020\u53cd\uff1f\u201d\u674e\u8d24\u624b\u4e0d\u505c\u6325\u7684\u76f4\u5199\u4e0b\u53bb\uff0c\u5934\u4e5f\u4e0d\u62ac\u7684\u8bf4\u9053\uff1a \u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\uff0c\u524d\u4e9b\u65e5\u5b50\uff0c\u8fde\u6211\u4e5f\u60f3\u9020\u53cd\u5462\u3002\u597d\uff0c\u660e\u5929\u6211\u4e00\u5b9a\u8981\u89c1\u4e18\u795e\u52cb\uff0c\u8fd9\u5f20\u594f\u8868 \u6b63\u597d\u8bf7\u4ed6\u5e26\u7ed9\u6bcd\u540e\u3002\u201d \u3000\u3000\u65e0\u610f\u4e4b\u4e2d\uff0c\u5077\u542c\u4e86\u5bab\u95f1\u9690\u79d8\uff0c\u4e0a\u5b98\u5a49\u513f\u4f46\u89c9\u4e00\u7247\u832b\u7136\uff0c\u8fd9\u65f6\u89c1\u5e9f\u592a\u5b50\u4e0a\u8868\u544a\u5bc6\uff0c\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5c82\u4e0d\u574f\u4e86\u6211\u674e\u9038\u54e5\u54e5\u7684\u5927\u4e8b\uff1f\u201d\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5b50\u4e0d\u8c05\u6bcd\uff0c\u5929\u4e0b\u8fd8\u4ec0\u4e48\u4e8b\u60c5 \u6bd4\u8fd9\u4e2a\u66f4\u8981\u96be\u582a\uff1f \u3000\u3000\u800c\u4eca\u5e9f\u592a\u5b50\u674e\u8d24\u5e78\u5f97\u62d4\u5f00\u8ff7\u96fe\uff0c\u7b2c\u4e00\u6b21\u5bf9\u6bcd\u4eb2\u6d41\u9732\u51fa\u771f\u631a\u7684\u611f\u60c5\uff0c\u81ea\u5df1\u600e\u5fcd\u524d\u5f80\u7834 \u574f\uff0c\u867d\u7136\u4ed6\u7684\u6bcd\u4eb2\u5c31\u662f\u81ea\u5df1\u6700\u75db\u6068\u7684\u6b66\u5219\u5929\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u6b63\u81ea\u601d\u6f6e\u6df7\u4e71\uff0c\u5ffd\u542c\u5f97\u697c\u4e0b\u6709\u4eba\u62a5\u9053\uff1a\u201c\u4e18\u5927\u5c06\u519b\u4f7f\u8005\u8fdb\u8c12\u6bbf\u4e0b\u3002\u201d\u63a5\u7740 \u4fbf\u6709\u4e24\u4e2a\u519b\u5b98\u8d70\u4e0a\u697c\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u53c8\u662f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bb0\u8d77\u4e86\u674e\u9038\u7559\u7ed9\u5979\u7684\u5b57\u6761\uff0c\u8981\u5979\u8f6c\u544a\u674e\u8d24\uff0c\u5207\u4e0d\u53ef\u4e0e\u4e18\u795e \u52cb\u76f8\u89c1\u3002\u73b0\u5728\u6b63\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u4e18\u795e\u52cb\u5374\u5148\u6d3e\u4eba\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\uff0c\u4e0a\u5b98\u5a49\u513f\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u7b2c\u4e00\u4e2a\u5ff5\u5934\u662f\u674e\u9038\u7684\u8bdd\uff0c\u963b\u6b62\u674e\u8d24\u63a5\u89c1\u6765\u4eba\uff1b \u7ee7\u800c\u4e00\u60f3\uff0c\u4e3a\u4ec0\u4e48\u8981\u963b\u6b62\u4ed6\uff1f\u96be\u9053\u8fd8\u6015\u4e18\u795e\u52cb\u6d3e\u4eba\u6765\u5bb3\u4ed6\u4e0d\u6210\uff1f\u4e18\u795e\u52cb\u662f\u671d\u5ef7\u7684\u5de6\u91d1\u543e \u5927\u5c06\u519b\uff0c\u4ed6\u82e5\u6697\u5bb3\u592a\u5b50\uff0c\u90a3\u9664\u975e\u662f\u51fa\u4e8e\u6b66\u5219\u5929\u7684\u4e3b\u610f\u3002\u6b64\u65f6\u6b64\u9645\uff0c\u4f53\u8bf4\u592a\u5b50\u65e0\u6bd4\u6000\u7591\uff0c \u5373\u7b97\u4e0a\u5b98\u5a49\u513f\u4e5f\u5df2\u7edd\u4e0d\u76f8\u4fe1\u6b66\u5219\u5929\u4f1a\u6697\u5bb3\u81ea\u5df1\u7684\u513f\u5b50\uff1a\u518d\u800c\u4e00\u60f3\uff0c\u674e\u9038\u672c\u610f\u662f\u6765\u5df4\u5dde\u8054 \u7edc\u592a\u5b50\u4e3e\u5175\u7684\uff0c\u5982\u4eca\u5f62\u52bf\u5927\u53d8\uff0c\u592a\u5b50\u5df2\u7ad9\u5728\u4ed6\u6bcd\u540e\u8fd9\u8fb9\uff0c\u4e5f\u8bb8\u8fd9\u5f20\u594f\u8868\u5c31\u8981\u4ea4\u7ed9\u4e18\u795e\u52cb \u7684\u6765\u4f7f\uff0c\u594f\u8868\u4e00\u4e0a\uff0c\u4e0d\u53ea\u674e\u9038\u7684\u5927\u4e8b\u4e0d\u6210\uff0c\u5510\u671d\u7684\u5fe0\u81e3\u4e5f\u5c06\u6709\u8bb8\u591a\u4eba\u8981\u88ab\u6740\u5bb3\uff1b\u4f46\u518d\u4e00 \u60f3\uff0c\u8981\u662f\u4efb\u7531\u5f90\u656c\u4e1a\u8d77\u5175\uff0c\u53c8\u5c06\u6709\u591a\u5c11\u8001\u767e\u59d3\u5bb6\u6563\u4eba\u4ea1\uff01\u8fd9\u4e9b\u65e0\u8f9c\u53d7\u7d2f\u7684\u8001\u767e\u59d3\uff0c\u6bd4\u8d77 \u5510\u5ba4\u7684\u5fe0\u81e3\u90a3\u4e0d\u77e5\u8981\u591a\u51e0\u5343\u4e07\u500d\uff01\u6b66\u5219\u5929\u7eb5\u7136\u4e0d\u7b97\u5f97\u662f\u5723\u5e1d\u660e\u541b\uff0c\u6700\u5c11\u5728\u8001\u767e\u59d3\u773c\u4e2d\uff0c \u5979\u8fd8\u4e0d\u662f\u4e00\u4e2a\u5f88\u574f\u7684\u7687\u5e1d\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u601d\u5982\u6f6e\uff0c\u5140\u81ea\u51b3\u65ad\u4e0d\u4e0b\uff0c\u5ffd\u542c\u5f97\u5fae\u98ce\u98d2\u7136\uff0c\u6765\u81ea\u8eab\u540e\uff0c\u4e0a\u5b98\u5a49\u513f\u56de\u5934\u4e00 \u671b\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u521a\u521a\u98de\u4e0a\u5899\u5934\uff0c\u4e0a\u5b98\u5a49\u513f\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u89c1\u90a3\u9ed1\u5f71\u4e00\u6307\u674e\u8d24\u7684\u623f\u95f4\uff0c\u7ee7 \u800c\u4e00\u6307\u81ea\u5df1\u7684\u80f8\u53e3\uff0c\u793a\u610f\u53eb\u5979\u8d76\u5feb\u7559\u795e\u623f\u95f4\u91cc\u9762\u7684\u4e8b\u60c5\uff0c\u5e76\u8868\u793a\u81ea\u5df2\u548c\u5979\u662f\u4e00\u6761\u8def\u7684\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u674e\u9038\u5417\uff1f\u4e0a\u5b98\u5a49\u513f\u5df2\u65e0\u6687\u53bb\u4ed4\u7ec6\u8fa8\u8ba4\u4e86\uff0c\u53ea\u542c\u5f97\u970d\u970d\u7684\u811a\u6b65\u58f0\u54cd\uff0c\u90a3\u4e24\u4e2a\u6b66 \u5b98\u5df2\u8e0f\u8fdb\u4e86\u674e\u8d24\u7684\u623f\u95f4\uff0c\u70db\u5149\u4e4b\u4e0b\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5176\u4e2d\u4e4b\u4e00\uff0c\u6b63\u662f\u5728\u8def\u4e0a\u7528\u5f13\u7bad\u5c04\u5979 \u7684\u90a3\u4e2a\u4eba\u3002 \u3000\u3000\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\u53d1\u751f\u4e86\uff0c\u674e\u8d24\u521a\u521a\u8d77\u7acb\u76f8\u8fce\uff0c\u4e3a\u9996\u7684\u90a3\u4e2a\u6b66\u5c06\u5ffd\u5730\u559d\u9053\uff1a\u201c\u674e\u8d24\uff0c \u4f60\u77e5\u7f6a\u5417\uff1f\u201d\u674e\u8d24\u8be7\u9053\uff1a\u201c\u7a0b\u5c06\u519b\uff0c\u6211\u6709\u4f55\u7f6a\uff1f\u201d\u90a3\u6b66\u5c06\u9053\uff1a\u201c\u4ee5\u5b50\u9006\u6bcd\uff0c\u4ee5\u4e0b\u72af\u4e0a\uff0c \u5929\u540e\u6709\u547d\uff0c\u5373\u4e88\u5904\u6b7b\uff01\u201d\u8001\u592a\u76d1\u53eb\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u5929\u540e\u7edd\u5bf9\u4e0d\u4f1a\u4e0b\u8fd9\u9053\u547d\u4ee4\uff01\u201d\u674e\u8d24 \u6014\u4e86\u4e00\u6014\uff0c\u6c89\u9759\u8bf4\u9053\uff1a\u201c\u62ff\u8bcf\u4e66\u6765\uff0c\u82e5\u662f\u6bcd\u540e\u771f\u8981\u6211\u6b7b\uff0c\u6211\u7f6a\u67ef\u5e94\u5f97\uff0c\u767e\u6b7b\u65e0\u8f9e\uff01\u201d\u8001 \u5927\u76d1\u5927\u53eb\u9053\uff1a\u201c\u6bbf\u4e0b\u4e0d\u8981\u4fe1\u4ed6\u7684\u9b3c\u8bdd\uff0c\u7eb5\u6709\u8bcf\u4e66\uff0c\u4e5f\uff0c\u4e5f\u2026\u2026\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u4f46\u542c\u5f97\u201c\u5693\u201d \u4e00\u58f0\uff0c\u5728\u59d3\u7a0b\u80cc\u540e\u7684\u90a3\u4e2a\u519b\u5b98\uff0c\u4e00\u8dc3\u800c\u524d\uff0c\u624b\u8d77\u5200\u843d\uff0c\u5148\u628a\u90a3\u8001\u592a\u76d1\u6740\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u6765\u5f97\u592a\u8fc7\u7a81\u7136\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e71\u5982\u9ebb\uff0c\u65e0\u6687\u601d\u91cf\uff0c\u4e00\u626c\u624b\u4fbf\u53d1\u51fa\u4e24\u67c4\u5315\u9996\uff0c\u7a7f \u7a97\u98de\u5165\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u4f46\u542c\u5f97\u5e9f\u592a\u5b50\u674e\u8d24\u60e8\u53eb\u4e00\u58f0\uff0c\u4ec6\u5012\u5728\u5730\u4e0a\uff0c\u60f3\u662f\u5df2\u906d\u4e86\u59d3\u7a0b\u7684\u6bd2\u624b\uff0c \u90a3\u4e24\u4e2a\u6b66\u5b98\u8eab\u624b\u4e0d\u51e1\uff0c\u5c45\u7136\u5728\u8fd9\u4e01\u65b9\u4e08\u8bb8\u7684\u5c0f\u697c\u4e2d\u95ea\u5f00\u5382\u535c\u5b98\u5a49\u513f\u7684\u6697\u5668\uff0c\u4e24\u4eba\u540c\u65f6\u7eb5 \u8d77\uff0c\u53cc\u5200\u9f50\u51fa\uff0c\u4e0a\u5b98\u5a49\u513f\u6b63\u81ea\u7a97\u53e3\u98de\u8fdb\u6765\uff0c\u6070\u597d\u8fce\u7740\u5200\u950b\uff0c\u4f46\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c \u706b\u661f\u98de\u6e85\uff0c\u6709\u4e2a\u4eba\u5df2\u8dcc\u5012\u697c\u4e0b\uff01 \u3000\u3000\u8dcc\u5012\u697c\u4e0b\u7684\u662f\u4e0a\u5b98\u5a49\u513f\uff0c\u5979\u5251\u6cd5\u867d\u662f\u4e0d\u5f31\uff0c\u529f\u529b\u5374\u4e0e\u90a3\u4e24\u4e2a\u519b\u5b98\u5dee\u5f97\u5927\u8fdc\uff0c\u5200\u5251\u76f8 \u4ea4\uff0c\u4e00\u9707\u4e4b\u4e0b\u7adf\u88ab\u629b\u51fa\u62e6\u6746\uff0c\u5c1a\u5e78\u5979\u811a\u5c16\u6491\u7740\u62e6\u6746\uff0c\u501f\u529b\u4e2a\u7ffb\u8eab\uff0c\u51cf\u8f7b\u4e86\u4e0b\u5760\u4e4b\u52bf\uff0c\u4fef \u8dcc\u5730\u4e0b\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7acb\u5373\u53c8\u8df3\u4e86\u8d77\u6765\u3002\u5979\u7684\u5251\u4ecd\u662f\u5b9d\u5251\uff0c\u5728\u5200\u5251\u76f8\u4ea4\u4e4b\u65f6\uff0c\u4e5f \u628a\u4e00\u4e2a\u519b\u5b98\u7684\u957f\u5200\u524a\u65ad\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e00\u8dc3\u800c\u8d77\uff0c\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u4fbf\u89c1\u4e00\u4e2a\u8499\u9762\u6c49\u5b50\u6301\u8005\u4e00\u6839\u9ed1\u6f06 \u6f06\u7684\u5175\u5668\uff0c\u4e0e\u90a3\u4e24\u4e2a\u519b\u5b98\u6253\u5f97\u975e\u5e38\u70ed\u95f9\u3002\u4e0a\u5b98\u5a49\u513f\u4e00\u77a7\uff0c\u8fd9\u8499\u9762\u4eba\u7684\u9762\u8c8c\u867d\u7136\u770b\u4e0d\u89c1\uff0c \u4f46\u4ece\u8eab\u6750\u6765\u770b\uff0c\u5374\u7edd\u5bf9\u4e0d\u662f\u674e\u9038\uff0c\u4e0a\u5b98\u5a49\u513f\u5fae\u611f\u5931\u671b\uff0c\u62ac\u8d77\u5b9d\u5251\uff0c\u4fbf\u60f3\u4e0a\u524d\u52a9\u6218\u3002 \u3000\u3000\u90a3\u8499\u9762\u4eba\u6c89\u58f0\u8bf4\u9053\uff0c\u201c\u4f60\u627e\u6b7b\u4e48\uff1f\u5feb\u9003\uff0c\u5feb\u9003\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u6014\u4e86\u4e00\u6014\uff0c\u8fd9\u58f0\u97f3\u597d\u50cf \u5728\u54ea\u513f\u542c\u89c1\u8fc7\u4f3c\u7684\u3002\u8499\u800c\u4eba\u7684\u5175\u5668\u751a\u4e3a\u5947\u602a\uff0c\u662f\u4e00\u6839\u70df\u4edf\uff0c\u6562\u60c5\u662f\u94c1\u505a\u7684\uff0c\u4e00\u78b0\u7740\u5200\u53e3\uff0c \u5c31\u662f\u5f53\u7684\u4e00\u58f0\u3002\u90a3\u59d3\u7a0b\u7684\u519b\u5b98\uff0c\u4ed6\u7684\u957f\u5200\u9002\u624d\u95ea\u907f\u5f97\u5b9c\uff0c\u6ca1\u6709\u7ed9\u4e0a\u5b98\u5a49\u513f\u7684\u5b9d\u5251\u524a\u65ad\uff0c \u73b0\u5728\u5374\u7ed9\u8fd9\u4e2a\u8499\u9762\u602a\u5ba2\u7684\u94c1\u70df\u6746\u5c06\u5200\u53e3\u90fd\u78b0\u5230\u5377\u8d77\u6765\u4e86\u3002\u8fd9\u6839\u70df\u6746\u7684\u70df\u9505\u5f88\u5927\uff0c\u70df\u9505\u91cc \u7684\u70df\u65f6\u8fd8\u6ca1\u6709\u70e7\u5b8c\uff0c\u4e0d\u65f6\u8ff8\u51fa\u70b9\u70b9\u706b\u661f\u3002\u8499\u9762\u602a\u5ba2\u6b66\u529f\u5f88\u9ad8\uff0c\u4e00\u6839\u70df\u6746\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357 \u6253\u5317\uff0c\u7adf\u628a\u70df\u9505\u5f53\u4f5c\u5c0f\u82b1\u67aa\u4f7f\u7528\uff0c\u800c\u4e14\u8fd8\u6742\u6709\u6781\u5176\u51cc\u5386\u7684\u70b9\u7a74\u62db\u6556\uff0c\u5c31\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c \u5df2\u628a\u90a3\u4e24\u4e2a\u519b\u5b98\u6740\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u6b63\u81ea\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u8499\u9762\u4eba\u5df2\u5b8c\u5168\u5360\u4e86\u4e0a\u98ce\uff0c\u53ea\u8981\u6211\u7a0d\u52a9\u4e00\u81c2\u4e4b\u529b\uff0c\u4fbf \u53ef\u5c06\u90a3\u4e24\u4e2a\u519b\u5b98\u64d2\u4e86\uff0c\u8ffd\u7a76\u51fa\u771f\u76f8\u6765\u3002\u4e3a\u4f55\u4ed6\u5374\u8981\u6211\u9003\u547d\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u738b\u5e9c\u91cc\u7684\u4eba\u5df2\u88ab\u60ca\u9192\uff0c\u5608\u5608\u6742\u6742\u7684\u58f0\u97f3\u56db\u9762\u4f20\u6765\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u9634\u607b\u607b\u7684\u7b11\u58f0\uff0c \u9634\u51b7\u800c\u53c8\u5a07\u5a9a\uff0c\u53d1\u58f0\u7684\u5730\u65b9\u4f3c\u662f\u79bb\u6b64\u5f88\u8fdc\uff0c\u58f0\u97f3\u4e5f\u4e0d\u54cd\u4eae\uff0c\u5374\u628a\u6240\u6709\u7684\u5608\u58f0\u90fd\u538b\u4e86\u4e0b\u53bb\u3002 \u90a3\u8499\u9762\u4eba\u9661\u7684\u5927\u559d\u4e00\u58f0\uff0c\u70df\u6746\u500f\u7ffb\uff0c\u5c06\u4e00\u4e2a\u519b\u5b98\u523a\u7ffb\uff0c\u70df\u9505\u4e00\u78d5\uff0c\u706b\u661f\u84ec\u98de\u4e4b\u4e2d\uff0c\u53c8\u628a \u53e6\u4e00\u4e2a\u519b\u5b98\u51fb\u5012\uff0c\u70df\u7070\u64a4\u5728\u4ed6\u7684\u800c\u4e0a\uff0c\u70eb\u5f97\u4ed6\u4eba\u547c\u5c0f\u53eb\u3002\u8499\u9762\u4eba\u8fd9\u51e0\u62db\u8fc5\u5982\u7535\u5149\u77f3\u706b\uff0c \u4f46\u5c31\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u90a3\u9669\u51b7\u800c\u4e49\u5a07\u5a9a\u7684\u7b11\u58f0\u5df1\u5230\u4e86\u95e8\u524d\uff0c\u4e0a\u5b98\u5a49\u513f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5979\u8ba4 \u51fa\u4e86\u8fd9\u4e2a\u7b11\u58f0\uff0c\u8fd9\u7b11\u58f0\u7adf\u662f\u6bd2\u89c2\u97f3\u7684\uff01\u8fd9\u65f6\u5979\u624d\u604d\u7136\u5927\u609f\uff0c\u4e3a\u4ec0\u4e48\u8499\u9762\u4eba\u8981\u5979\u8d76\u5feb\u9003\u8dd1\u3002 \u3000\u3000\u8499\u9762\u4eba\u98de\u8eab\u4e00\u60ca\uff0c\u4ece\u4e0a\u5b98\u5a49\u513f\u8eab\u8fb9\u64e6\u8fc7\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0e\u6211\u5206\u8def\u800c\u9003\u3002\u201d\u4e0a\u5b98\u5a49 \u513f\u521a\u521a\u98de\u8eab\u8df3\u51fa\u5507\u5899\uff0c\u53ea\u542c\u5f97\u6bd2\u89c2\u97f3\u5df2\u5728\u56ed\u5343\u91cc\u7b11\u9053\uff1a\u201c\u7a0b\u5c06\u519b\uff0c\u4f60\u600e\u4e48\u4e0d\u7b49\u6211\u6765\u4fbf\u4e0b \u624b\u4e86\uff0c\u6015\u6211\u5206\u4f60\u7684\u529f\u52b3\u5417\uff0c\u54ce\u54df\uff0c\u4f60\u2014\u2014\u201d\u60f3\u662f\u5979\u5df2\u53d1\u73b0\u5904\u7a0b\u7684\u519b\u5b98\u88ab\u51fb\u5012\u5730\u4e0a\uff0c\u8d76\u7740 \u7ed9\u4ed6\u6551\u6cbb\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e0d\u6562\u56de\u5934\uff0c\u8d81\u7740\u8fd9\u4e2a\u673a\u4f1a\uff0c\u5982\u98de\u75be\u8dd1\uff0c\u62d0\u8fc7\u4e86\u51e0\u6761\u8857\uff0c\u5ffd\u542c\u5f97\u9523\u5362\u5927\u54cd\uff0c \u524d\u9762\u4e00\u5927\u961f\u5b98\u5175\u6b63\u56f4\u7740\u5979\u4f4f\u7684\u90a3\u95f4\u5ba2\u5e97\u3002 \u3000\u3000\u4e0a\u5bab\u5a49\u513f\u60f3\u9053\uff1a\u201c\u5e78\u800c\u674e\u9038\u54e5\u54e5\u6d1e\u70db\u5148\u673a\uff0c\u65e9\u51fa\u53bb\u4e86\u3002\u201d\u4fa7\u8033\u7ec6\u542c\uff0c\u53f7\u89d2\u58f0\u58f0\uff1b\u4e3e \u76ee\u9065\u89c2\uff0c\u4eba\u5f71\u7ef0\u7ef0\uff0c\u957f\u8857\u8fdc\u5904\uff0c\u706b\u628a\u5a49\u8712\uff0c\u60f3\u662f\u4e18\u795e\u52cb\u7684\u5927\u519b\u6b63\u9646\u7eed\u8fdb\u57ce\uff0c\u56db\u5904\u641c\u7d22\u3002 \u4e0a\u5b98\u5a49\u513f\u907f\u5165\u4e00\u6761\u50fb\u9759\u7684\u5c0f\u5df7\uff0c\u5b98\u519b\u4e00\u65f6\u641c\u7d22\u4e0d\u5230\u3002\u5929\u4e0a\u65e0\u661f\u65e0\u6708\uff0c\u9634\u6697\u4e4b\u6781\uff0c\u8fd8\u4e0d\u65f6 \u6709\u7a00\u758f\u7684\u5c0f\u96e8\u70b9\uff0c\u98d8\u5230\u4e0a\u6709\u5a49\u513f\u8eab\u4e0a\u3002\u4e0a\u5b98\u5a49\u513f\u7684\u5fc3\u60c5\u4e5f\u6b63\u50cf\u5929\u6c14\u4e00\u6837\uff0c\u9634\u6697\u800c\u53c8\u5bd2\u51b7\u3002 \u3000\u3000\u81ea\u4ece\u5979\u4e0b\u5c71\u4ee5\u6765\uff0c\u5fc3\u60c5\u5c31\u4e00\u76f4\u5728\u52a8\u8361\u4e4b\u4e2d\u3001\u5374\u4ee5\u6b64\u523b\u6fc0\u52a8\u5f97\u6700\u5386\u5bb3\u3002\u6b66\u5219\u5929\uff0c\u5979\u7a76 \u7adf\u662f\u600e\u6837\u4e00\u4e2a\u4eba\u5462\uff1f\u672a\u4e0b\u5c71\u4ee5\u524d\uff0c\u6b66\u5219\u5927\u662f\u5979\u5fc3\u4e2d\u7684\u5973\u9b54\u738b\uff1b\u4e0b\u5c71\u4e4b\u540e\uff0c\u6cbf\u9014\u6240\u89c1\u6240\u95fb\uff0c \u6b66\u5219\u5929\u5e76\u4e0d\u4f3c\u5979\u60f3\u50cf\u4e2d\u7684\u90a3\u6837\u574f\u4e86\uff1b\u7136\u800c\u4eca\u591c\u4e0a\u5b98\u5a49\u513f\u5374\u76ee\u51fb\u4e86\u5e9f\u592a\u5b50\u88ab\u6740\u5bb3\u7684\u4e00\u5e55\u60e8 \u5267\uff0c\u662f\u6b66\u5219\u706d\u7684\u6388\u610f\u5417\uff1f\u82e5\u7136\u4e0d\u662f\uff0c\u4e18\u795e\u52cb\u7684\u90e8\u4e0b\u53c8\u7109\u6562\u8fd9\u6837\u5927\u80c6\uff1b\u5ffd\u7136\u95f4\u5979\u611f\u5230\u4e00\u79cd \u96be\u4ee5\u540d\u8bf4\u7684\u60b2\u54c0\uff0c\u5979\u7fdf\u7136\u4e00\u60ca\uff0c\u5374\u539f\u6765\u81ea\u5df1\u7684\u5fc3\u5e95\u91cc\u662f\u4f69\u670d\u6b66\u5219\u5929\u7684\uff0c\u6b63\u56e0\u4e3a\u8fd9\u6837\uff0c\u6240 \u4ee5\u7531\u81ea\u5df1\u76ee\u51fb\uff0c\u8bc1\u5b9e\u662f\u7531\u5979\u6388\u610f\uff0c\u5f04\u51fa\u4e86\u8fd9\u5e55\u4e0d\u8fd1\u4eba\u6027\u7684\u60e8\u5267\u4e4b\u540e\uff0c\u81ea\u5df1\u624d\u5bf9\u5979\u8fd9\u6837\u7684 \u75db\u6068\u3002\u4e0a\u5b98\u5a49\u513f\u624b\u6478\u5251\u67c4\uff0c\u518d\u4e00\u6b21\u7684\u5728\u5fc3\u91cc\u53d1\u4e86\u91cd\u8a93\uff1a\u4e00\u5b9a\u8981\u6740\u6b66\u5219\u5929\uff01 \u3000\u3000\u5b98\u519b\u5e03\u6ee1\u5927\u8857\uff0c\u5a49\u8712\u7684\u706b\u628a\u4e5f\u6e10\u6e10\u4ece\u5927\u8857\u7a7f\u4eba\u5c0f\u5df7\u4e86\uff0c\u4e0a\u5b98\u5a49\u513f\u60f3\u9003\uff0c\u4f46\u5979\u4e0d\u8ba4\u5f97 \u8def\uff0c\u53ea\u6015\u51fa\u4e86\u5df7\u53e3\uff0c\u5c31\u4f1a\u78b0\u5230\u5b98\u519b\u3002\u5979\u6b63\u5728\u8e0c\u8e87\uff0c\u5ffd\u89c1\u5df7\u53e3\u4eba\u5f71\u4e00\u95ea\uff0c\u6709\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u5feb\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u501f\u7740\u8857\u4e0a\u706b\u628a\u900f\u8fdb\u5c0f\u5df7\u7684\u4eae\u5149\uff0c\u4e0a\u5b98\u5a49\u513f\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u7a7f\u7740\u9ed1\u8272\u591c\u884c\u8863\u670d\u7684 \u6c49\u5b50\uff0c\u7ad9\u5728\u5979\u7684\u9762\u524d\uff0c\u6b63\u662f\u65e5\u95f4\u6240\u9047\u7684\u90a3\u4e2a\u519c\u592b\uff0c\u4e0a\u5b98\u5a49\u513f\u53c8\u60ca\u53c8\u559c\uff0c\u539f\u6765\u521a\u624d\u7684\u8499\u9762 \u602a\u5ba2\u5c31\u662f\u4ed6\uff01 \u3000\u3000\u8fd9\u4e2a\u9ed1\u4eba\u602a\u5ba2\u6781\u4e3a\u719f\u6089\u5df4\u5dde\u7684\u8857\u9053\uff0c\u5e26\u7740\u4e0a\u5b98\u5a49\u513f\u5de6\u7ed5\u53f3\u7ed5\uff0c\u7a7f\u8fc7\u4e86\u5341\u51e0\u6761\u964b\u8857\u5c0f \u5df7\uff0c\u5c45\u7136\u907f\u8fc7\u4e86\u5b98\u519b\u7684\u641c\u7d22\uff0c\u5230\u4e86\u5317\u95e8\u3002\u5b98\u519b\u662f\u4ece\u5357\u95e8\u8fdb\u57ce\u7684\uff0c\u8fd8\u672a\u67ec\u81f3\u5317\u95e8\uff0c\u57ce\u5934\u4e0a \u6d3e\u6709\u51e0\u4e2a\u56e2\u7ec3\u628a\u624b\uff0c\u4e24\u4eba\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u8d8a\u57ce\u800c\u51fa\uff0c\u90a3\u51e0\u4e2a\u56e2\u7ec3\u4f46\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u8fd8\u53ea\u5f53 \u662f\u98de\u9e1f\u63a0\u8fc7\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u51fa\u4e86\u57ce\u95e8\uff0c\u6b63\u60f3\u8bf7\u95f4\u90a3\u602a\u5ba2\u7684\u59d3\u540d\uff0c\u4ed6\u5374\u5bf8\u6b65\u4e0d\u505c\uff0c\u53ea\u662f\u98de\u8dd1\uff0c\u4e0a\u5b98\u5a49\u513f \u6c14\u5598\u5401\u5401\uff0c\u51e0\u4f4f\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u4e00\u76f4\u8dd1\u4e86\u4e2a\u591a\u65f6\u8fb0\uff0c\u4f30\u8ba1\u79bb\u57ce\u5df2\u6709\u4e09\u56db\u5341\u91cc\uff0c\u90a3\u602a\u5ba2\u5140\u662f\u4e0d \u505c\u811a\u6b65\u3002\u4e0a\u5b98\u5a49\u513f\u5fcd\u4e0d\u7740\u53eb\u9053\uff1a\u201c\u53ef\u4ee5\u505c\u4e0b\u6765\u6b47\u6b47\u4e86\u5427\uff1f\u201d \u3000\u3000\u90a3\u602a\u5ba2\u53ea\u7b80\u7b80\u5355\u9769\u7684\u7b54\u8986\u4e86\u4e24\u4e2a\u5b57\uff1a\u201c\u4e0d\u884c\uff01\u201d\u8dd1\u5f97\u6bd4\u524d\u66f4\u5feb\u4e86\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u7740 \u607c\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u4f60\u662f\u8f83\u8003\u6211\u7684\u8f7b\u529f\u6765\u7684\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u6bd2\u89c2\u97f3\u90a3\u9634\u607b\u607b\u7684\u7b11\u58f0 \u53c8\u4ece\u80cc\u540e\u4f20\u6765\uff0c\u968f\u5373\u6709\u4e00\u4e2a\u7834\u9523\u822c\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u5c0f\u5a03\u5a03\uff0c\u8fd9\u91cc\u4e0d\u662f\u5251\u9601\u3002\u4f60\u8fd8\u60f3\u8dd1\u5f97 \u4e86\u5417\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4e00\u4e2a\u6bd2\u89c2\u97f3\u5df1\u96be\u4ee5\u5e94\u4ed8\uff0c\u7adf\u8fd8\u6709\u4e00\u4e2a\u6076\u884c\u8005\u4e5f\u4e0e\u5979 \u540c\u6765\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u78a7\u91ce\u6668\u98ce\u98d8\u843d\u82b1 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u56db\u56de\u3000\u78a7\u91ce\u6668\u98ce\u98d8\u843d\u82b1 \u3000\u3000\u65f7\u91ce\u82cd\u832b\uff0c\u591c\u8272\u660f\u7791\uff0c\u76ee\u529b\u6240\u53ca\uff0c\u6c93\u65e0\u4eba\u5f71\u3002\u770b\u6765\u90a3\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u6700\u5c11\u4e5f\u5728\u6570\u91cc \u4e4b\u5916\uff0c\u800c\u8bf4\u8bdd\u7684\u58f0\u97f3\u5374\u5982\u5728\u8033\u8fb9\u3002\u8981\u77e5\u53ea\u6709\u5177\u6709\u6781\u4e0a\u4e58\u5185\u529f\u7684\u4eba\uff0c\u624d\u80fd\u591f\u9f13\u6c14\u884c\u8fdc\uff0c\u8fdc \u5730\u4f20\u58f0\uff0c\u4e0a\u5b98\u5a49\u513f\u4fee\u4e3a\u867d\u6d45\uff0c\u4ea6\u77e5\u5176\u7406\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u957f\u5b59\u4f2f\u4f2f\u8d25\u5728\u4ed6\u4eec\u624b\u4e0b\uff0c \u53ea\u8fd9\u624b\u4f20\u97f3\u5165\u5bc6\u7684\u529f\u5927\uff0c\u4fbf\u8db3\u4ee5\u5148\u58f0\u593a\u4eba\uff0c\u9a87\u4eba\u5fc3\u9b44\uff01\u201d \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u7684\u811a\u6b65\u58f0\u4ea6\u5df2\u9690\u9690\u53ef\u95fb\uff0c\u4f46\u542c\u5f97\u6bd2\u89c2\u97f3\u53c8\u5a07\u7b11\u9053\uff1a\u201c\u524d \u9762\u8fd9\u4f4d\u670b\u53cb\u83ab\u975e\u662f\u5df4\u5c71\u8015\u9690\u9a6c\u5143\u901a\u4e48\uff1f \u3000\u3000\u60f3\u5f53\u5e74\u4e2d\u539f\u7684\u6b66\u6797\u4eba\u7269\u5bf9\u6211\u4eec\u4e8c\u4eba\u7fa4\u8d77\u56f4\u653b\uff0c\u4f60\u4e5f\u66fe\u5395\u8eab\u5176\u5185\uff0c\u5f53\u65f6\u4f55\u7b49\u5a01\u98ce\uff1f\u4eca \u591c\u5374\u6709\u82e5\u4e27\u5bb6\u4e4b\u72d7\uff01\u563f\u563f\uff0c\u9a6c\u5143\u901a\u5440\u9a6c\u5143\u901a\uff0c\u4f60\u4e0d\u96be\u8fc7\u6211\u4e5f\u66ff\u4f60\u96be\u8fc7\uff01\u6211\u4e3a\u4f60\u8bbe\u60f3\uff0c\u4e0e \u5176\u88ab\u6211\u8feb\u81f3\u7b4b\u75b2\u529b\u7aed\u800c\u6b7b\uff0c\u4f55\u5982\u7559\u70b9\u6c14\u529b\uff0c\u5728\u6b64\u4e00\u62fc\uff0c\u7eb5\u7136\u6218\u6b7b\uff0c\u4e5f\u8fd8\u4e0d\u6127\u82f1\u96c4\u672c\u8272\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e1a\u5df2\u8dd1\u5f97\u6c14\u529b\u5c06\u5c3d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6bd2\u89c2\u97f3\u867d\u7136\u4e0d\u6000\u597d\u610f\uff0c\u8fd9\u8bdd\u5374\u662f\u8bf4\u5f97\u4e0d \u9519\u3002\u201d\u9a6c\u5143\u901a\u5374\u4e0d\u4e3a\u5979\u6240\u6fc0\uff0c\u51b7\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u53ea\u6015\u4f60\u8ffd\u4e0a\u4e4b\u65f6\uff0c\u4fbf\u662f\u4f60\u4e27\u8eab\u4e4b\u523b\uff01\u201d\u811a\u677f\u597d\u50cf\u6cab\u4e86\u6cb9\u4e00\u6837\uff0c\u8dd1\u5f97\u66f4\u5feb\u4e86\uff01\u6bd2\u89c2 \u97f3\u5927\u7b11\u9053\uff1a\u201c\u5f53\u4ee4\u4e4b\u4e16\uff0c\u5c1a\u6709\u4f55\u4eba\u80fd\u4e0e\u6211\u7b49\u8054\u624b\u6297\u8861\uff0c\u4f60\u7eb5\u6709\u4f0f\u5175\uff0c\u6211\u4ea6\u4f55\u60e7\uff01\u201d\u8bf4\u5230 \u672a\u4e86\u4e00\u53e5\uff0c\u90a3\u9634\u51b7\u7684\u7b11\u58f0\u76f4\u523a\u8033\u9f13\uff0c\u5c31\u597d\u50cf\u5230\u4e86\u80cc\u540e\u4e00\u822c\uff0c\u5413\u5f97\u4e0a\u5b98\u5a49\u513f\u4e0d\u5bd2\u800c\u6817\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e0d\u6562\u56de\u5934\uff0c\u597d\u50cf\u662f\u9003\u907f\u9b3c\u9b45\u4f3c\u7684\uff0c\u4e5f\u4e0d\u77e5\u662f\u54ea\u91cc\u6765\u7684\u529b\u91cf\uff0c\u5c45\u7136\u53c8\u8dd1\u4e86\u5341 \u6765\u91cc\u7684\u8def\u7a0b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5df2\u662f\u66d9\u5149\u900f\u73b0\uff0c\u5927\u5730\u597d\u50cf\u5ffd\u7136\u88ab\u63ed\u53bb\u4e86\u4e00\u5c42\u9ed1\u7eb1\u5e10\u5e55\uff0c\u4e00 \u5207\u666f\u7269\uff0c\u8c41\u7136\u663e\u9732\uff0c\u4f46\u89c1\u78a7\u91ce\u5e73\u7574\u3002\u5c55\u5ef6\u5929\u9645\uff0c\u5c71\u6751\u8305\u5e97\uff0c\u9690\u73b0\u6797\u95f4\uff0c\u6625\u98ce\u62c2\u9762\uff0c\u5e26\u6765 \u4e86\u65b0\u7ffb\u6ce5\u571f\u7684\u6c14\u606f\uff0c\u6628\u591c\u51e0\u573a\u758f\u96e8\uff0c\u4f7f\u5f97\u65e9\u6668\u7684\u7a7a\u6c14\uff0c\u5206\u5916\u6e05\u65b0\uff0c\u715e\u98ce\u666f\u7684\u662f\uff0c\u5728\u8fd9\u5b81 \u9759\u7684\u6e05\u6668\uff0c\u5374\u9690\u85cf\u7740\u65e0\u7a77\u7684\u6740\u6c14\uff01 \u3000\u3000\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u7684\u811a\u6b65\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5ffd\u542c\u5f97\u6076\u884c\u8005\u54c8\u54c8\u5927\u7b11\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u53d1\u51fa\u4e86\u4e00 \u679a\u788e\u9aa8\u94b1\u9556\uff0c\u4e0a\u5b98\u5a49\u513f\u6025\u5fd9\u95ea\u907f\uff0c\u53ea\u89c1\u9a6c\u5143\u901a\u53cd\u624b\u4e00\u78d5\uff0c\u94b1\u9556\u6025\u5c04\uff0c\u5374\u662f\u843d\u5904\u65e0\u58f0\uff0c\u539f \u6765\u6b63\u6b63\u6253\u4e2d\u4ed6\u7684\u70df\u9505\uff0c\u88ab\u5438\u4f4f\u4e86\u3002\u6076\u884c\u8005\u53eb\u9053\uff1a\u201c\u597d\u624b\u6cd5\u201d\uff0c\u94ee\u94ee\u4e24\u58f0\uff0c\u53c8\u662f\u4e24\u679a\u94b1\u9556 \u8054\u7fe9\u98de\u51fa\uff01\u9a6c\u5143\u901a\u5927\u53eb\u4e00\u58f0\uff0c\u6492\u4e0b\u70df\u6746\uff0c\u539f\u6765\u662f\u90a3\u4e24\u679a\u94b1\u9556\u6253\u8fdb\u70df\u9505\uff0c\u628a\u4ed6\u7684\u70df\u7ba1\u4e5f\u9707 \u88c2\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u9a6c\u5143\u901a\u548c\u4e0a\u5b98\u5a49\u513f\u6b63\u4ece\u8def\u8fb9\u8dd1\u4e0a\u4e00\u5ea7\u5c0f\u5c71\uff0c\u6ee1\u5c71\u90fd\u662f\u91ce\u82b1\uff0c\u5c71\u9e93\u6709\u4e00\u7247\u6843\u6797\uff0c \u6843\u82b1\u707f\u82e5\u4e91\u971e\uff0c\u6b63\u5728\u76db\u5f00\uff0c\u9a6c\u5143\u901a\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u518d\u8ffd\u8fdb\u6765\uff0c\u8fd9\u7247\u6843\u6797\u4fbf\u662f\u4f60 \u4eec\u7684\u57cb\u9aa8\u4e4b\u6240\uff01\u201d\u6076\u884c\u8005\u5927\u6012\uff0c\u4ee5\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u624b\u6cd5\uff0c\u6492\u51fa\u4e86\u4e00\u628a\u94b1\u9556\uff0c\u5ffd\u5730\u4e00\u9635\u98ce\u522e \u8fc7\uff0c\u98d8\u4e0b\u65e0\u6570\u82b1\u6735\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u4e00\u628a\u6025\u52b2\u75be\u5c04\u7684\u94b1\u9556\uff0c\u7adf\u88ab\u968f\u98ce\u98d8\u821e\u7684\u82b1\u6735\u90fd\u78b0\u843d\u5730 \u4e0a\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5e74\u7eaa\u867d\u8f7b\uff0c\u4e5f\u66fe\u7ecf\u5386\u8fc7\u4e0d\u5c11\u5947\u9669\uff0c\u4f46\u6240\u89c1\u6240\u95fb\uff0c\u5374\u4ece\u65e0\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4f3c\u4eca\u65e5 \u7684\u8fd9\u822c\u5947\u602a\u900f\u9876\uff0c\u82e5\u8bf4\u90a3\u4e9b\u82b1\u6735\u662f\u88ab\u98ce\u5439\u4e0b\u6765\u7684\uff0c\u98ce\u52bf\u4e0d\u5927\uff0c\u7167\u7406\u53ea\u8be5\u98d8\u4e0b\u7247\u7247\u82b1\u74e3\uff0c \u7136\u800c\u73b0\u5728\u6bcf\u4e00\u6735\u82b1\u90fd\u662f\u5b8c\u6574\u65e0\u7f3a\u7684\u98d8\u4e0b\u6765\uff0c\u76f4\u5230\u78b0\u7740\u94b1\u9556\u4e4b\u65f6\uff0c\u82b1\u6735\u624d\u74e3\u74e3\u6563\u5f00\uff0c\u968f\u98ce \u800c\u901d\u3002\u66f4\u5947\u602a\u7684\u662f\u82b1\u6735\u5c45\u7136\u80fd\u6253\u843d\u94b1\u9556\uff0c\u8bd5\u60f3\u8fd9\u4e00\u628a\u788e\u9aa8\u94b1\u9556\uff0c\u7ecf\u6076\u884c\u8005\u53d1\u51fa\uff0c\u90a3\u662f\u4f55 \u7b49\u529f\u529b\uff1f\u7edd\u4e0d\u4e0b\u4e8e\u5f3a\u5f13\u5229\u5f29\uff0c\u5374\u88ab\u4e00\u6735\u5c0f\u5c0f\u7684\u6843\u82b1\u6253\u843d\uff0c\u771f\u662f\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u4e5f\u88ab\u8fd9\u51fa\u5947\u7684\u73b0\u8c61\u60ca\u4f4f\u4e86\uff0c\u5728\u6843\u6797\u5916\u500f\u7136\u4f4f\u6b65\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u4f46\u542c\u5f97 \u4e00\u7247\u94f6\u94c3\u4f3c\u7684\u7b11\u58f0\u4ece\u6843\u82b1\u6797\u91cc\u98d8\u51fa\u6765\uff0c\u4f17\u4eba\u773c\u775b\u84e6\u5730\u4e00\u4eae\uff0c\u53ea\u89c1\u6843\u82b1\u6797\u4e2d\u8d70\u51fa\u4e00\u4e2a\u5341\u516b\u3001 \u4e5d\u5c81\u7684\u5c11\u5973\uff0c\u6e56\u6c34\u78a7\u8272\u7684\u7ec9\u7eb1\u8863\u88f3\uff0c\u767d\u7eeb\u675f\u8170\uff0c\u51e4\u7c2a\u9547\u53d1\uff0c\u79cb\u6c34\u4e3a\u795e\uff0c\u4f0a\u4eba\u4f3c\u7389\uff0c\u957f\u7709 \u5165\u9b13\uff0c\u4f53\u6001\u8f7b\u76c8\uff0c\u624b\u637b\u6843\u679d\uff0c\u5b9b\u5982\u4ed9\u5b50\u51cc\u6ce2\uff0c\u8e0f\u5728\u6ee1\u662f\u843d\u82b1\u7684\u5730\u4e0a\uff0c\u7f13\u7f13\u800c\u51fa\u3002\u6bd2\u89c2\u97f3 \u7d20\u6765\u4ee5\u7f8e\u8273\u81ea\u8d1f\uff0c\u89c1\u4e86\u8fd9\u4e2a\u5c11\u5973\uff0c\u4ea6\u4e0d\u7981\u81ea\u60ed\u5f62\u79fd\u3002\u800c\u4e14\u90a3\u5c11\u5973\u4e0d\u4f46\u7f8e\u5230\u6781\u70b9\uff0c\u7709\u5b57\u4e4b \u95f4\uff0c\u8fd8\u9690\u9690\u6709\u4e00\u80a1\u4ee4\u4eba\u9707\u6151\u7684\u82f1\u6c14\uff0c\u8fd9\u5239\u90a3\u95ee\uff0c\u4e24\u5927\u9b54\u5934\u90fd\u6014\u7740\u4e86\uff0c\u6bd2\u89c2\u97f3\u7b11\u4e0d\u51fa\u53e3\uff0c \u6076\u884c\u8005\u9a82\u4e0d\u51fa\u58f0\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5c11\u5973\u7709\u5934\u4e00\u76b1\uff0c\u4f3c\u7b11\u8fd8\u55d4\u7684\u8bf4\u9053\uff1a\u201c\u9a6c\u5143\u901a\uff0c\u4f60\u53c8\u7ed9\u6211\u60f9\u4e9b\u4ec0\u4e48\u9ebb\u70e6\u6765\u4e86\uff1f\u201d \u9a6c\u5143\u901a\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u6765\u5934\u975e\u5c0f\uff0c\u8bf7\u59d1\u5a18\u6551\u6211\u4e00\u547d\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u9a6c\u5143\u901a \u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4eba\u79f0\uff1a\u89c2\u97f3\u52fe\u9b42\uff0c\u884c\u8005\u593a\u547d\u3002\u8fd9\u4e00\u7537\u4e00\u5973\uff0c\u4fbf\u662f\u6c5f\u6e56\u4e0a\u95fb\u540d\u80c6\u843d\u7684\u6076\u884c\u8005 \u4e0e\u6bd2\u89c2\u97f3\uff01\u201d\u90a3\u5c11\u5973\u683c\u683c\u4e00\u7b11\uff0c\u795e\u6001\u98de\u626c\uff0c\u6843\u679d\u4e00\u6307\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c31\u662f\u8fd9\u4e24\u4e2a\u4e0d\u6210\u6c14\u5019\u7684\u4e1c\u897f\u5417\uff1f\u53ea\u6015\u4e5f\u672a\u5fc5\u80fd\u52fe\u4eba\u7684\u9b42\uff0c\u593a\u4eba\u7684\u547d\uff01\u4e5f\u7f62\uff0c\u4e14\u5f85 \u6211\u518d\u8bd5\u4e00\u8bd5\uff0c\u770b\u662f\u5426\u503c\u5f97\u6211\u4e3a\u4f60\u51fa\u624b\uff1f\u201d \u3000\u3000\u7b11\u58f0\u672a\u6b47\uff0c\u84e6\u5730\u559d\u9053\uff1a\u201c\u4f60\u6253\u6211\u4e5d\u679a\u94b1\u9556\uff0c\u6211\u5949\u8fd8\u4f60\u4e00\u6280\u6843\u7bad\uff01\u201d\u624b\u4e0a\u6843\u679d\uff0c\u84e6\u4f3c \u79bb\u5f26\u4e4b\u7bad\uff0c\u75be\u5c04\u800c\u51fa\uff0c\u6076\u884c\u8005\u542c\u98ce\u8fa8\u5668\uff0c\u7adf\u7136\u4e0d\u6562\u624b\u63a5\uff0c\u62d4\u51fa\u6212\u5200\uff0c\u8fce\u7740\u6843\u679d\u4e00\u78b0\uff0c\u4f46 \u89c1\u90a3\u679d\u6843\u679d\u64e6\u7740\u5200\u80cc\u659c\u98de\u800c\u51fa\uff0c\u9707\u5f97\u90a3\u53e3\u6212\u5200\u55e1\u55e1\u4f5c\u54cd\uff0c\u6076\u884c\u8005\u8fd9\u4e00\u5200\u867d\u7136\u628a\u6843\u679d\u8361\u5f00\uff0c \u5374\u4e5f\u5e76\u6ca1\u6709\u5c06\u5b83\u5288\u4e2d\u3002\u6bd2\u89c2\u97f3\u5a07\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6458\u53f6\u98de\u82b1\u7684\u4e0a\u4e58\u624b\u6cd5\uff01\u201d\u5f85\u90a3\u6843\u679d\u98de\u8fd1\uff0c \u9aa4\u7136\u4f38\u6307\u4e00\u5f39\uff0c\u201c\u535c\u52d2\u201d\u4e00\u58f0\uff0c\u6843\u679d\u4e2d\u5206\u4e3a\u4e8c\uff0c\u6bd2\u89c2\u97f3\u6b63\u81ea\u5f97\u610f\u5a07\u7b11\uff0c\u4e0d\u6599\u6843\u679d\u867d\u65ad\uff0c \u4f59\u52bf\u672a\u8870\uff0c\u6709\u4e00\u679d\u534a\u622a\u6843\u679d\uff0c\u500f\u7684\u4ece\u5979\u7684\u9b13\u8fb9\u98de\u6765\uff0c\u6bd2\u89c2\u97f3\u5413\u5f97\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c \u907f\u662f\u907f\u5f00\u4e86\uff0c\u5934\u4e0a\u7684\u4e00\u80a1\u51e4\u9497\uff0c\u5374\u5df2\u7ed9\u6843\u679d\u5c04\u843d\u3002\u5c11\u5973\u7b11\u9053\uff1a\u201c\u8fd9\u4e11\u5934\u9640\u529f\u529b\u5dee\u4e9b\uff0c\u4e0d \u8fc7\u6211\u53cd\u6b63\u95f2\u7740\u65e0\u804a\uff0c\u4f60\u4eec\u4e24\u4eba\u8fd8\u52c9\u5f3a\u53ef\u4ee5\u548c\u6211\u4e00\u6597\u3002\u201d \u3000\u3000\u6076\u884c\u8005\u51e0\u66fe\u53d7\u8fc7\u8fd9\u822c\u8f7b\u89c6\uff0c\u52c3\u7136\u5927\u6012\uff0c\u970d\u5730\u4e00\u4e2a\u56de\u8eab\u62d7\u6b65\uff0c\u5c55\u51fa\u201c\u53cd\u81c2\u9634\u9556\u201d\u7684\u624b \u6cd5\uff0c\u6323\u7136\u4e00\u58f0\uff0c\u53d1\u51fa\u4e00\u679a\u788e\u9aa8\u94b1\u9556\uff0c\u76f4\u5954\u5c11\u5973\u80f8\u524d\u7684\u201c\u4e91\u53f0\u7a74\u201d\uff01 \u3000\u3000\u6076\u884c\u8005\u8fd9\u4e00\u4e0b\u201c\u53cd\u81c2\u9634\u9556\u201d\uff0c\u5b9e\u662f\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u4ed6\u521a\u624d\u7528\u201c\u5218\u6d77\u6d12\u91d1\u94b1\u201d\u7684\u624b \u6cd5\uff0c\u53d1\u51fa\u4e00\u5927\u628a\u94b1\u9556\uff0c\u5389\u5bb3\u867d\u7136\u5389\u5bb3\uff0c\u53ef\u662f\u9556\u591a\u529b\u5206\uff0c\u5bb9\u6613\u88ab\u4eba\u51fb\u843d\uff0c\u8fd9\u4e00\u4e0b\u5374\u662f\u96c6\u4e2d \u52b2\u529b\uff0c\u4e00\u9556\u6025\u98de\uff0c\u76f8\u8ddd\u53c8\u8fd1\uff0c\u4e0a\u5b98\u5a49\u513f\u4e5f\u4e0d\u7981\u6697\u6697\u4e3a\u5979\u62c5\u5fc3\u3002 \u3000\u3000\u6076\u884c\u8005\u65b9\u81ea\u5728\u60f3\uff0c\u201c\u770b\u4f60\u8fd8\u803f\u4e0d\u6562\u7528\u6843\u82b1\u63a5\u6211\u7684\u94b1\u9556\uff1f\u201d\u5fc3\u5ff5\u65b9\u52a8\uff0c\u4f46\u89c1\u90a3\u5c11\u5973\u6a31 \u5507\u5fae\u80a9\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149\u534e\uff01\u201d\u90a3\u679a\u94b1\u9556\u672c\u662f\u5bf9\u51c6\u5979\u80f8\u53e3\u7684\u201c\u4e91\u53f0\u7a74\u201d \u98de\u6765\uff0c\u5979\u65e2\u4e0d\u95ea\u907f\u3002 \u3000\u3000\u4e5f\u4e0d\u906e\u62e6\uff0c\u51b7\u7b11\u58f0\u4e2d\uff0c\u90a3\u679a\u94b1\u9556\u98de\u5230\u80f8\u524d\u51e0\u5bf8\u4e4b\u5904\uff0c\u7adf\u7136\u5ffd\u5730\u4e00\u4e2a\u62d0\u5f2f\uff0c\u8f6c\u4e86\u65b9\u5411\uff0c \u201c\u556a\u55d2\u201d\u4e00\u58f0\uff0c\u94b1\u9556\u6df1\u5d4c\u5728\u4e00\u68f5\u6843\u6811\u4e4b\u4e0a\uff0c\u76f4\u628a\u4e0a\u5b98\u5a49\u513f\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4f4d \u59d0\u59d0\u957f\u5f97\u5929\u4ed9\u4f3c\u7684\uff0c\u96be\u9053\u771f\u7684\u662f\u4ed9\u5b50\u4e0b\u51e1\uff1f\u8981\u4e0d\u662f\u6709\u795e\u4ed9\u5999\u6cd5\uff0c\u8fd9\u94b1\u9556\u600e\u7684\u65e0\u56e0\u800c\u843d\uff1f\u201d \u3000\u3000\u94b1\u9556\u5f53\u7136\u4e0d\u4f1a\u65e0\u56e0\u800c\u843d\uff0c\u4e0d\u8fc7\u4e0a\u5b98\u5a49\u513f\u770b\u4e0d\u51fa\u6765\u7f62\u4e86\u3002\u843d\u5728\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u8fd9\u6837\u6b66 \u5b66\u7684\u5927\u884c\u5bb6\u773c\u91cc\uff0c\u5374\u4ee4\u4ed6\u4eec\u4e0d\u7531\u5f97\u4e0d\u80c6\u6218\u5fc3\u60ca\uff01\u539f\u6765\u8fd9\u679a\u94b1\u9556\u7adf\u662f\u88ab\u90a3\u5c11\u5973\u8fd0\u6c14\u4e00\u5439\uff0c \u56e0\u800c\u6539\u4e86\u65b9\u5411\u7684\uff0c\u5185\u529f\u4e4b\u5f3a\uff0c\u5b9e\u5df1\u5230\u4e86\u6df1\u4e0d\u53ef\u6d4b\u7684\u5730\u6b65\uff0c\u6bd4\u8d77\u5979\u521a\u624d\u90a3\u624b\u201c\u98de\u82b1\u6458\u65f6\u201d \u7684\u529f\u592b\uff0c\u8fd8\u8981\u5389\u5bb3\u5f97\u591a\uff01\u771f\u4e0d\u77e5\u5979\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u662f\u600e\u4e48\u7ec3\u51fa\u6765\u7684\uff1f \u3000\u3000\u7136\u800c\u8fd9\u4e24\u5927\u9b54\u5934\uff0c\u5c82\u662f\u7518\u5fc3\u5fcd\u8fb1\u4e4b\u8f88\uff1f\u6bd2\u89c2\u97f3\u5a07\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\u5439\u6c14\u5982\u5170\uff0c\u8ba9\u6211\u4e5f \u6765\u4eb2\u8fd1\u4eb2\u8fd1\uff01\u201d\u5e76\u4e0d\u89c1\u5979\u8eab\u5f62\u63a0\u8d77\uff0c\u9661\u7136\u95f4\u811a\u6b65\u4e00\u6ed1\uff0c\u65e0\u58f0\u5140\u606f\u7684\u4fbf\u5230\u4e86\u90a3\u5c11\u5973\u8ddf\u524d\uff0c \u624b\u638c\u4e00\u626c\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u98de\u51fa\u4e86\u4e00\u84ec\u94f6\u9488\uff0c\u4ece\u56db\u9762\u516b\u65b9\u88ad\u5230\uff0c\u94f6\u9488\u4f53\u79ef\u867d\u5c0f\uff0c\u4f46\u5bc6 \u96c6\u5982\u96e8\uff0c\u4e00\u53e3\u6c14\u54ea\u80fd\u5439\u5f97\u51c0\u5c3d\uff0c\u53ea\u8981\u8eab\u4e0a\u4e2d\u4e86\u4e00\u6839\uff0c\u94f6\u4ec0\u4fbf\u4f1a\u5faa\u7740\u7a74\u9053\u653b\u5fc3\uff0c\u7aef\u7684\u662f\u6781 \u5176\u90aa\u6bd2\u7684\u6697\u5668\uff0c\u6bd2\u89c2\u97f3\u4e4b\u6240\u4ee5\u5f97\u540d\uff0c\u4e00\u5927\u534a\u4fbf\u662f\u51fa\u5979\u7684\u201c\u900f\u7a74\u795e\u9488\u201d\u6240\u81f4\u3002 \u3000\u3000\u94f6\u9488\u4e00\u53d1\uff0c\u6bd2\u89c2\u97f3\u540c\u65f6\u5a07\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u4e86\u7559\u795e\u4f60\u90a3\u5439\u5f39\u5f97\u7834\u7684\u8138\u513f\uff01\u201d\u8bdd\u8bed\u6545\u4f5c\u5173 \u5fc3\uff0c\u7b11\u58f0\u751a\u4e3a\u523a\u8033\uff0c\u5b9e\u662f\u6709\u610f\u6270\u4e71\u90a3\u5c11\u5973\u7684\u5fc3\u795e\uff0c\u5c31\u5728\u7b11\u58f0\u523a\u8033\u4e4b\u4e2d\uff0c\u9aa4\u7136\u95f4\u5979\u53c8\u6ed1\u4e0a \u4e24\u6b65\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u638c\u529b\u50ac\u52b2\uff0c\u5c06\u90a3\u4e00\u84ec\u94f6\u9488\u7684\u53bb\u52bf\uff0c\u50ac\u5f97\u66f4\u662f\u6025\u52b2\u65e0\u4f26\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u6bd2\u89c2\u97f3\u53ea\u89c9\u773c\u775b\u4e00\u82b1\uff0c\u773c\u524d\u500f\u7684\u98de\u8d77\u4e86\u4e00\u7247\u5f69\u8679\uff0c\u4f46\u89c1\u90a3 \u5c11\u5973\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u6761\u7ef8\u8f6f\u5e26\uff0c\u5c11\u5973\u67f3\u8170\u4fef\u5730\uff0c\u7ea2\u7ef8\u7ed5\u8eab\u4e00\u5377\uff0c\u6bd2\u89c2\u97f3\u6240\u53d1\u7684\u900f\u7a74\u795e\u9488\uff0c \u4e00\u6839\u4e0d\u5269\u7684\u90fd\u63d2\u5728\u7ea2\u7ef8\u4e4b\u4e0a\uff01 \u3000\u3000\u6bd2\u89c2\u97f3\u5927\u5403\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5144\u6025\u9000\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c11\u5973\u7ea2\u7ef8\u4e00\u632f\uff0c\u63d2\u5728\u7ef8\u5e26 \u4e0a\u7684\u94f6\u9488\u90fd\u53cd\u5c04\u51fa\u6765\uff0c\u6bd2\u89c2\u97f3\u98de\u8eab\u63a0\u8d77\u4e09\u4e08\u591a\u9ad8\uff0c\u94f6\u9488\u556e\u55e4\u7684\u4ece\u5979\u978b\u5e95\u5c04\u8fc7\u3002\u6076\u884c\u8005\u5374 \u6ca1\u6709\u8fd9\u6837\u4fca\u5de7\u7684\u8f7b\u529f\uff0c\u53ea\u5f97\u5c06\u6212\u5200\u6cfc\u98ce\u6025\u821e\uff0c\u867d\u7136\u5982\u6b64\uff0c\u5374\u8fd8\u662f\u4e00\u6839\u900f\u7a74\u795e\u9488\uff0c\u5c04\u4e2d\u4e86 \u4ed6\u81c2\u4e0a\u7684\u201c\u66f2\u5112\u7a74\u201d\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u6bd2\u89c2\u97f3\u5df2\u4eae\u51fa\u957f\u5251\uff0c\u51cc\u7a7a\u523a\u4e0b\uff0c\u4f46\u89c1\u7ea2\u7ef8\u7ffb\u63b7\u3002\u5251\u5149\u4f3c\u7ec3\uff0c\u6bd2\u89c2\u97f3 \u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u5251\u950b\u4ece\u90a3\u5c11\u5973\u7684\u5934\u9876\u4e0a\u4e00\u63a0\u800c\u8fc7\uff0c\u4e0a\u5b98\u5a49\u513f\u770b\u5f97\u5fc3\u80c6\u4ff1\u5bd2\uff0c\u4f46\u542c\u5f97\u90a3\u5c11 \u5973\u8f7b\u65a5\u4e00\u58f0\uff0c\u5251\u5149\u7ef8\u5f71\u4e4b\u4e2d\uff0c\u53c2\u89c2\u97f3\u8f7b\u98d8\u98d8\u7684\u843d\u51fa\u4e08\u8bb8\u4e4b\u5916\u3002\u539f\u6765\u5c31\u5728\u8fd9\u95ea\u7535\u4e4b\u95f4\uff0c\u4e24 \u4eba\u5df1\u4ea4\u6362\u4e86\u51e0\u62db\uff0c\u5728\u4e0a\u5b98\u5a49\u513f\u770b\u6765\uff0c\u662f\u6bd2\u89c2\u97f3\u7684\u957f\u5251\u51e0\u4e4e\u524a\u53bb\u4e86\u5c11\u5973\u7684\u4e91\u9b13\uff0c\u5b9e\u5219\u662f\u90a3 \u5c11\u5973\u7684\u7ea2\u7ef8\uff0c\u51e0\u4e4e\u5377\u7740\u4e86\u6bd2\u89c2\u97f3\u7684\u624b\u8155\uff0c\u8fd9\u513f\u62db\u5404\u906d\u60ca\u9669\uff0c\u6bd4\u5bf9\u8d77\u6765\uff0c\u4ecd\u662f\u90a3\u5c11\u5973\u5360\u4e86 \u4e0a\u98ce\uff0c\u8feb\u5f97\u6bd2\u89c2\u664b\u4e0d\u5f97\u4e0d\u98d8\u8eab\u75be\u95ea\u3002 \u3000\u3000\u6076\u884c\u8005\u770b\u51fa\u4e0d\u5999\uff0c\u6025\u5fd9\u7528\u201c\u79fb\u5bab\u6362\u7a74\u201d\u7684\u529f\u592b\uff0c\u5c06\u201c\u66f2\u5112\u7a74\u201d\u6240\u4e2d\u7684\u90a3\u6839\u201c\u900f\u7a74\u795e \u9488\u201d\u7684\u4e0a\u5347\u4e4b\u52bf\uff0c\u7a0d\u7a0d\u963b\u9047\uff0c\u201c\u900f\u7a74\u795e\u9488\u201d\u867d\u7136\u542b\u6709\u5267\u6bd2\uff0c\u4e00\u65f6\u4e09\u523b\u4e4b\u5185\uff0c\u8fd8\u672a\u81f3\u4e8e\u53d1 \u4f5c\uff0c\u6076\u884c\u8005\u5fc3\u60f3\uff0c\u4e14\u4e0e\u6bd2\u89c2\u97f3\u8054\u624b\u6740\u4e86\u8fd9\u5c11\u5973\u4e4b\u540e\uff0c\u518d\u5411\u5979\u8ba8\u89e3\u836f\u4e0d\u8fdf\u3002\u5f53\u4e0b\u5927\u543c\u4e00\u58f0\uff0c \u62a1\u5200\u6025\u4e0a\uff0c\u90a3\u5c11\u5973\u7ef8\u5e26\u4e00\u6325\uff0c\u5374\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u6bd2\u89c2\u9752\u7684\u5251\u62db\u7adf\u662f\u540e\u53d1\u5148\u81f3\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u89c2\u97f3\u8086\u6bd2\uff0c\u884c\u8005\u901e\u51f6\uff0c\u6211\u4eca\u65e5\u65e6\u6743\u5145\u4e2a\u4f0f\u9b54\u5c0a\u8005\u3002\u201d\u7ea2\u7ef8\u7ffb \u5377\uff0c\u89e3\u5f00\u4e86\u6bd2\u89c2\u97f3\u7684\u5251\u62db\uff0c\u6076\u884c\u8005\u770b\u51fa\u6709\u4fbf\u5b9c\u53ef\u6361\uff0c\u6b3a\u8eab\u75f0\u8fdb\uff0c\u4e00\u5200\u4fbf\u65ab\u8fc7\u6765\u3002 \u3000\u3000\u9661\u7136\u95f4\u5ffd\u89c9\u5bd2\u6c14\u68ee\u68ee\uff0c\u51b7\u5149\u95ea\u95ea\uff0c\u6076\u884c\u8005\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u7f29\u624b\u4e4b\u65f6\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u201d \u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u864e\u53e3\u9178\u9ebb\uff0c\u90a3\u5c11\u5973\u624b\u4e0a\u5df1\u591a\u4e86\u4e00\u67c4\u4e09\u5c3a\u9752\u950b\uff0c\u62d4\u5251\u4e4b\u5feb\u65e0\u4ee5\u5f62\u5bb9\uff0c \u672a\u5f85\u6bd2\u89c2\u97f3\u6325\u5251\u5939\u653b\uff0c\u5979\u5df2\u5237\u7684\u4e00\u5251\uff0c\u5c06\u6076\u884c\u8005\u7684\u6212\u5200\u524a\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002 \u3000\u3000\u5e78\u800c\u6709\u6bd2\u89c2\u97f3\u6321\u5f97\u4e00\u6321\uff0c\u6076\u884c\u8005\u624d\u582a\u582a\u7684\u907f\u5f00\u4e86\u90a3\u5c11\u5973\u7684\u8feb\u98ce\u4e00\u51fb\uff0c\u60ca\u9b42\u7a0d\u5b9a\uff0c\u66b4 \u6012\u559d\u9053\uff1a\u201c\u4e14\u5148\u628a\u8fd9\u5996\u5973\u6bd9\u4e86\u518d\u8bf4\uff01\u201d\u4ed6\u7684\u5916\u5bb6\u529f\u592b\u767b\u5cf0\u9020\u6781\uff0c\u6212\u5200\u62a1\u5f00\uff0c\u9690\u9690\u6709\u98ce\u96f7 \u4e4b\u58f0\uff0c\u800c\u6bd2\u89c2\u97f3\u5219\u4ee5\u9634\u67d4\u98d8\u5ffd\u7684\u5251\u6cd5\u914d\u5408\u8fdb\u653b\uff0c\u767b\u65f6\u5251\u5f71\u5200\u5149\uff0c\u7ea0\u7ed3\u4e00\u7247\uff0c\u6709\u5982\u6ce2\u6d9b\u8d77 \u4f0f\uff0c\u5a01\u52bf\u9a87\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u770b\u5f97\u6697\u6697\u60ca\u5fc3\uff0c\u9093\u5c11\u5973\u5374\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u4e00\u624b\u6325\u7ef8\uff0c\u4e00\u624b\u4f7f\u5251\uff0c\u5251\u5149\u95ea\u95ea\uff0c \u7ef8\u5e26\u98d8\u98d8\uff0c\u7aef\u7684\u662f\u77eb\u82e5\u6e38\u9f99\uff0c\u7fe9\u82e5\u60ca\u9e3f\uff0c\u628a\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u65e8\uff0c\u90fd\u8feb\u5f97\u79bb\u8eab\u6570\u5c3a\u4e4b\u5916\uff01\u66f4 \u96be\u5f97\u7684\u662f\u5979\u4e24\u624b\u5206\u4f7f\u4e24\u822c\u622a\u7136\u4e0d\u540c\u7684\u5175\u5668\uff0c\u4e00\u67d4\u4e00\u521a\uff0c\u5374\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c\u9976\u662f\u6c5f\u6e56\u4e0a \u4e24\u4e2a\u4e45\u8d1f\u76db\u540d\u7684\u5927\u9b54\u5934\uff0c\u4e5f\u88ab\u5979\u5947\u5965\u53d8\u5e7b\u7684\u62db\u6570\u5f04\u5f97\u5934\u6655\u76ee\u7729\uff01 \u3000\u3000\u6218\u5230\u5206\u9645\uff0c\u90a3\u5c11\u5973\u76c8\u76c8\u4e00\u7b11\uff0c\u5251\u62db\u500f\u53d8\uff0c\u7ef8\u5e26\u7ffb\u98de\uff0c\u4f46\u89c1\u5bd2\u5149\u56db\u5c04\uff0c\u5251\u6c14\u5982\u8679\uff0c\u7ef8 \u5e26\u98d8\u98d8\uff0c\u6f2b\u5929\u7ea2\u5f71\u3002\u6076\u884c\u8005\u6c14\u5598\u5401\u5401\uff0c\u90a3\u6839\u900f\u7a74\u795e\u9488\u7684\u6bd2\u6e10\u6e10\u53d1\u4f5c\uff0c\u6212\u5200\u4e4b\u52bf\u7a0d\u7f13\uff0c\u90a3 \u5c11\u5973\u7ea2\u7ef8\u4e00\u5377\u3002 \u3000\u3000\u884c\u8005\u7684\u6212\u5200\u8131\u624b\u800c\u98de\uff0c\u6bd2\u89c2\u9752\u75be\u653b\u6570\u5251\uff0c\u5ffd\u5730\u56de\u8eab\u4e00\u638c\uff0c\u5728\u6076\u884c\u8005\u80cc\u5fc3\u4e00\u62cd\uff0c\u6076\u884c \u8005\u767b\u65f6\u5982\u7bad\u79bb\u5f26\uff0c\u98de\u51fa\u6570\u4e08\uff0c\u4e0a\u5b98\u5a49\u513f\u6b63\u81ea\u83ab\u540d\u5176\u5999\uff0c\u53ea\u89c1\u6bd2\u89c2\u97f3\u8ddf\u7740\u4e5f\u8f6c\u8eab\u75be\u8dd1\uff0c\u8f6c \u8eab\u4e4b\u9645\uff0c\u53c8\u53d1\u51fa\u4e86\u4e00\u84ec\u201c\u900f\u7a74\u795e\u9488\u201d\uff0c\u4e0a\u5b98\u5a49\u513f\u8fd9\u624d\u660e\u767d\u3001\u6bd2\u89c2\u97f3\u4e43\u662f\u7528\u5de7\u529b\u5148\u628a\u6076\u884c \u8005\u9001\u8d70\uff0c\u8fd9\u4e00\u84ec\u94f6\u9488\u4e5f\u662f\u4ffa\u62a4\u4ed6\u4eec\u9003\u8d70\u7684\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7ea2\u7ef8\u4e00\u5377\uff0c\u5c06\u6bd2\u89c2\u9752\u6240\u53d1\u7684\u201c\u900f\u7a74\u795e\u9488\u201d\u5c3d\u90fd\u6536\u4e86\uff0c\u63d2\u5251\u5f52\u9798\uff0c\u7fd8\u914b\u957f\u5929\uff0c \u7eb5\u58f0\u5927\u7b11\uff0c\u610f\u6001\u8c6a\u7edd\u3002 \u3000\u3000\u4e0a\u5bab\u5a49\u513f\u6ee1\u5fc3\u6b22\u559c\uff0c\u4ece\u6843\u6811\u540e\u9762\u8df3\u51fa\u6765\uff0c\u6b63\u8981\u5411\u90a3\u5c11\u5973\u9053\u8c22\uff0c\u90a3\u5c11\u5973\u629a\u7740\u5979\u7684\u5934\u53d1 \u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\u4f60\u53d7\u60ca\u5566\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u7684\u6b66\u529f\u771f\u662f\u597d\u5f97\u51fa\u5947\uff0c\u4e3a\u4f55\u4e0d\u5c06 \u90a3\u4e24\u4e2a\u9b54\u5934\u6740\u4e86\uff1f\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u4e0d\u8fc7\u7663\u75a5\u4e4b\u60a3\uff0c\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u6211\u8fd8 \u6ca1\u6709\u95f2\u529f\u592b\u53bb\u6740\u4ed6\u4eec\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u5982\u6709\u6240\u611f\uff0c\u62ac\u5934\u8bf4\u9053\uff1a \u3000\u3000\u201c\u662f\u5440\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u8fd8\u6709\u6bd4\u4ed6\u4eec\u5389\u5bb3\u4e07\u500d\u7684\u9b54\u5934\uff0c\u5e94\u5f53\u5148\u628a\u90a3\u6bd2\u5bb3\u5929\u4e0b\u7684\u9b54\u5934\u6740\u4e86\uff01\u201d \u3000\u3000\u62ac\u5934\u4e00\u770b\uff0c\u5ffd\u89c1\u90a3\u5c11\u5973\u9762\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u662f\u60f3\u8bf7\u6211\u53bb\u4f5c\u523a\u5ba2\u5417\uff1f\u201d \u7b11\u5f97\u6709\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6b64\u8bdd\u4ee5\u540e\u518d\u8bf4\uff0c\u5143\u901a\u4f60\u8fc7\u6765\uff0c\u201d\u9a6c\u5143\u901a\u8fc7\u6765\u8bf4\u9053\uff1a \u201c\u5e9f\u592a\u5b50\u674e\u8d24\u6628\u591c\u7ed9\u4eba\u6740\u4e86\uff01\u201d\u90a3\u5c11\u5973\u5a07\u8eaf\u4e00\u9707\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u4f60\u8be6\u7ec6\u5bf9\u6211\u8bf4\u8bf4\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u6487\u4e0b\u4e86\u4e0a\u5b98\u5a49\u513f\uff0c\u4e0e\u9a6c\u5143\u901a\u5e76\u80a9\u800c\u884c\uff0c\u4e0a\u5b98\u5a49\u513f\u53ea\u597d\u8ddf\u5728\u4ed6\u4eec\u540e\u9762\u3002\u90a3\u5c11\u5973 \u4f3c\u4e4e\u662f\u5728\u4e13\u5fc3\u7684\u542c\u9a6c\u5143\u901a\u8bf4\u8bdd\uff0c\u628a\u4e0a\u5b98\u5a49\u513f\u51b7\u843d\u4e00\u65c1\uff0c\u4e0a\u5b98\u5a49\u513f\u89c1\u5979\u6beb\u4e0d\u7406\u776c\u81ea\u5df1\uff0c\u597d \u51e0\u6b21\u672c\u60f3\u63d2\u53e3\u4e5f\u4f5c\u7f62\u4e86\u3002\u4ecd\u542c\u5f97\u9a6c\u5143\u901a\u4ece\u6628\u95e8\u9047\u89c1\u5979\u548c\u5348\u9038\u8bf4\u8d77\uff0c\u76f4\u8bf4\u5230\u5e9f\u592a\u5b50\u88ab\u6740\u4ee5 \u53ca\u4ed6\u600e\u6837\u5c06\u81ea\u5df1\u5e26\u5230\u6b64\u95ee\u4e3a\u6b62\uff0c\u8bf4\u5f97\u6781\u4e3a\u8be6\u7ec6\uff0c\u90a3\u5c11\u5973\u53ea\u662f\u51dd\u795e\u9759\u542c\uff0c\u534a\u53e5\u8bdd\u4e5f\u6ca1\u6709\u8bf4\uff0c \u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5df2\u8d70\u51fa\u4e86\u90a3\u7247\u6843\u6797\u3002 \u3000\u3000\u4e0a\u5bab\u5a49\u513f\u5fc3\u5934\u4e03\u535c\u516b\u843d\uff0c\u731c\u4e0d\u900f\u8fd9\u5c11\u5973\u662f\u4f55\u7b49\u4f6f\u4eba\u3002\u4e3a\u4f55\u5979\u521a\u624d\u542c\u4e86\u81ea\u5df1\u90a3\u756a\u8bf4\u6d3b\uff0c \u795e\u8272\u7adf\u662f\u8fd9\u4e48\u5947\u5f02\u3002\u60f3\u7740\uff0c\u60f3\u7740\uff0c\u5ffd\u5730\u7fdf\u7136\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u957f\u5b59\u4f2f\u4f2f\u5c61\u6b21\u5429\u5490\u4e8e\u6211\uff0c\u8bf4 \u662f\u6c5f\u6e56\u9669\u6076\uff0c\u53eb\u6211\u9022\u4eba\u53ea\u8bf4\u4e09\u5206\u8bdd\uff0c\u4e0d\u53ef\u5168\u629b\u4e00\u7247\u5fc3\uff0c\u6211\u5374\u600e\u4e48\u548c\u5979\u7b2c\u4e00\u6b21\u89c1\u9762\uff0c\u5c31\u60f3 \u8bf7\u5979\u53bb\u6740\u6b66\u5219\u5929\uff01\u5c82\u4e0d\u662f\u592a\u8fc7\u5929\u771f\u4e86\u4e48\uff1f\u201d\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd9\u5c11\u5973\u65e2\u7136\u80af\u6551\u81ea\u5df1\uff0c\u6599\u60f3\u4e0d \u662f\u574f\u4eba\u3002 \u3000\u3000\u6843\u6797\u5916\u6709\u4e00\u5e62\u623f\u5b50\uff0c\u7ea2\u5899\u7eff\u74e6\uff0c\u56db\u5468\u90fd\u79cd\u6709\u82b1\u8349\u6811\u6728\uff0c\u751a\u662f\u5e7d\u96c5\uff0c\u76f4\u5230\u6b64\u65f6\uff0c\u90a3\u5c11 \u5973\u624d\u56de\u8fc7\u5934\u6765\uff0c\u5bf9\u4e0a\u5bab\u5a49\u513f\u4e00\u7b11\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u65e2\u7136\u6765\u4e86\uff0c\u5c31\u8fdb\u53bb\u5750\u5750\u5427\u3002\u201d \u3000\u3000\u5c11\u5973\u8f7b\u53e9\u95e8\u6249\uff0c\u4e00\u4e2a\u5c0f\u4e2b\u5934\u5f00\u95e8\u7b11\u9053\uff1b\u201c\u5c0f\u59d0\u4eca\u5929\u6ca1\u6709\u6458\u82b1\u56de\u6765\u5417\uff1f\u201d\u5c11\u5973\u7b11\u9053\uff1a \u201c\u522b\u63d0\u5566\uff0c\u7ed9\u90a3\u4e24\u4e2a\u4ec0\u4e48\u6076\u884c\u8005\u6bd2\u89c2\u97f3\u5927\u715e\u98ce\u666f\uff0c\u628a\u4e00\u6811\u6843\u82b1\u90fd\u7cdf\u584c\u4e86\u3002\u55ef\uff0c\u5982\u610f\u8fd8\u6ca1 \u6709\u56de\u6765\u4e48\uff1f\u201d\u90a3\u4e2b\u5934\u9053\uff1a\u201c\u53ea\u6015\u5c31\u56de\u7c73\u4e86\u3002\u201d\u5c11\u5973\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u4e00\u70b9\u70b9\u5c0f\u4e8b\u60c5\uff0c\u53bb\u4e86\u4e00 \u591c\u8fd8\u6ca1\u6709\u529e\u597d\uff0c\u771f\u662f\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7a7f\u8fc7\u82b1\u5eca\uff0c\u4e0a\u5165\u5ba2\u5385\uff0c\u4e0a\u5b98\u5a49\u513f\u4e00\u770b\uff0c\u5c4b\u5b50\u91cc\u51e0 \u5f20\u6a80\u6728\u684c\u6905\uff0c\u5c4b\u89d2\u56db\u76c6\u58a8\u5170\uff0c\u58c1\u4e0a\u6302\u6709\u4e00\u5e45\u753b\uff0c\u753b\u7684\u4e43\u662f\u201c\u98de\u5929\u201d\uff0c\u753b\u4e2d\u4ed9\u5973\u7ef8\u5e26\u98d8\u98d8\uff0c \u4f3c\u6b32\u51cc\u98ce\u98de\u53bb\uff0c\u610f\u5883\u6df1\u8fdc\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u8d5e\u9053\uff1a\u201c\u8fd9\u5c4b\u5b50\u7684\u4e3b\u4eba\u5927\u662f\u4e0d\u4fd7\uff01\u201d \u3000\u3000\u5750\u5b9a\u4e4b\u540e\uff0c\u90a3\u5c11\u5973\u5ffd\u5730\u5bf9\u9a6c\u5143\u901a\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u5c06\u8fd9\u4f4d\u5c0f\u59b9\u5b50\u5e26\u6765\uff0c\u4f60\u53ef\u77e5 \u9053\u5979\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u9a6c\u5143\u901a\u4e0e\u4e0a\u5b98\u5a49\u513f\u9762\u9762\u76f8\u89d1\uff0c\u5fc3\u4e2d\u90c1\u5728\u60f3\u9053\uff1a\u201c\u5979\u95ee\u8fd9\u53e5\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u53ea\u89c1\u8fd9 \u5c11\u5973\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u5979\u7684\u7237\u7237\u548c\u5e08\u7236\u90fd\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u4eba\u7269\uff0c\u7237\u7237\u662f\u56fd\u521d\u7684\u5927\u8bd7 \u4eba\u4e0a\u5b98\u4eea\uff0c\u5e08\u7236\u662f\u66fe\u505a\u8fc7\u592a\u5b97\u7687\u5e1d\u6bbf\u524d\u68c0\u70b9\u7684\u957f\u5b59\u5747\u91cf\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u5403\u4e86\u4e00\u60ca\u3002\u5fc3\u9053\uff1a \u201c\u6211\u4ece\u672a\u89c1\u8fc7\u5979\uff0c\u5979\u600e\u7684\u77e5\u9053\u5f97\u8fd9\u4e48\u6e05\u695a\uff1f\u201d\u5fc3\u5ff5\u65b9\u52a8\uff0c\u53ea\u542c\u5f97\u9a6c\u5143\u901a\u5df2\u662f\u201c\u5475\u201d\u7684\u4e00 \u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u3001\u6211\u4e0d\u77e5\u9053\uff01\u201d\u58f0\u97f3\u7adf\u662f\u5fae\u5fae\u98a4\u6296\uff01 \u3000\u3000\u90a3\u5c11\u5973\u671b\u4e86\u4e0a\u5b98\u5a49\u513f\u4e00\u773c\uff0c\u7b11\u9053\uff0c\u201c\u6211\u4eec\u8fd9\u4f4d\u5c0f\u59b9\u5b50\u73b0\u5728\u867d\u7136\u6ca1\u6709\u4ec0\u4e48\u540d\u6c14\uff0c\u5c06\u6765 \u5fc5\u5b9a\u80dc\u8fc7\u5e08\u7236\u3002\u5176\u5b9e\u636e\u6211\u770b\u6765\uff0c\u5979\u73b0\u5728\u5df2\u662f\u672c\u671d\u7684\u7b2c\u4e00\u624d\u5973\u3002\u4f60\u8fd9\u6b21\u7684\u4e8b\u60c5\uff0c\u505a\u5f97\u518d\u597d \u4e0d\u8fc7\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u6700\u559c\u6b22\u522b\u4eba\u8d5e\u5979\u6587\u5b66\uff0c\u5fc3\u4e2d\u751c\u4e1d\u4e1d\u7684\uff0c\u5bf9\u8fd9\u5c11\u5973\u5927\u6709\u77e5\u5df1\u4e4b\u611f\uff0c\u9a6c\u5143 \u901a\u4e5f\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u90a3\u5c11\u5973\u53c8\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u5bb6\u5b66\u6e0a\u6e90\uff0c\u806a\u660e\u7edd\u9876\uff0c\u7434\u68cb\u8bd7\u753b\u81ea\u7136\u662f\u4ef6\u4ef6\u7686\u80fd\u7684\u4e86\uff01\u201d \u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u7565\u89e3\u76ae\u6bdb\uff0c\u52c9\u5f3a\u53ef\u4ee5\u5e94\u4ed8\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u8bf7\u4f60\u7ed9\u6211\u753b\u4e00\u5e45\u753b\u3002\u201d \u4e0a\u5b98\u5a49\u513f\u751a\u662f\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u521a\u624d\u8fd8\u8bf4\u6ca1\u6709\u95f2\u529f\u592b\uff0c\u600e\u7684\u73b0\u5728\u5374\u6709\u8fd9\u7b49\u95f2\u60c5\u9038\u81f4\uff0c\u4e00 \u89c1\u9762\u5c31\u53eb\u4eba\u4f5c\u753b\uff1f\u201d\u95ee\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u60f3\u8981\u6211\u753b\u4e9b\u4ec0\u4e48\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u628a\u6628\u665a\u884c\u523a\u5e9f \u592a\u5b50\u7684\u90a3\u4e24\u4e2a\u519b\u5b98\u753b\u51fa\u6765\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u672c\u4ee5\u4e3a\u662f\u8981\u5979\u753b\u5c71\u6c34\u3001\u82b1\u9e1f\u6216\u8005\u4ed5\u5973\u7684\u7f8e\u753b\uff0c\u60f3 \u4e0d\u5230\u53ea\u662f\u8981\u5979\u753b\u4e24\u4e2a\u4eba\u50cf\uff0c\u5fae\u611f\u4e0d\u5feb\uff0c\u4f46\u8fd8\u662f\u753b\u4e86\u3002\u90a3\u5c11\u5973\u9012\u7ed9\u9a6c\u5143\u901a\u770b\uff0c\u9a6c\u5143\u901a\u9053\uff1a \u201c\u6211\u4e0d\u61c2\u753b\uff0c\u4f46\u8fd9\u4e24\u4e2a\u574f\u5bb6\u4f19\u5374\u662f\u753b\u5f97\u771f\u50cf\uff01\u201d \u3000\u3000\u95e8\u5916\u5ffd\u6709\u811a\u6b65\u58f0\u54cd\uff0c\u90a3\u4e2b\u5934\u4e00\u542c\u4fbf\u7b11\u9053\uff1a\u201c\u662f\u5982\u610f\u59d0\u59d0\u56de\u6765\u4e86\uff0c\u5979\u5e26\u4e86\u516d\u4e2a\u4eba\u6765\u3002\u201d \u957f\u5b59\u5747\u91cf\u66fe\u4f20\u6388\u8fc7\u4e0a\u5b98\u5a49\u513f\u201c\u4f0f\u5730\u542c\u58f0\u201d\u7684\u672c\u9886\uff0c\u4f46\u4f3c\u8fd9\u7b49\u5728\u8c08\u7b11\u4e4b\u95f4\u53ea\u4e00\u542c\u4fbf\u77e5\u9053\u6765 \u4eba\u7684\u6570\u76ee\uff0c\u5979\u5374\u662f\u4e07\u4e07\u4e0d\u80fd\u3002\u5fc3\u4e2d\u6697\u6697\u60ed\u6127\uff0c\u60f3\u4e2a\u5230\u4eba\u5bb6\u7684\u4e00\u4e2a\u5c0f\u4e2b\u5934\u4e5f\u6bd4\u81ea\u5df1\u9ad8\u660e\u767e \u500d\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u53eb\u5982\u610f\u4e00\u4e2a\u4eba\u5148\u8fdb\u6765\u3002\u201d\u8fc7\u4e86\u7247\u523b\uff0c\u4e00\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u4e2b\u73af\u8d70\u8fdb\u5c4b\u5b50\uff0c \u80cc\u7740\u4e00\u4e2a\u5305\u88b1\uff0c\u4e00\u67c4\u5355\u5200\uff0c\u7d2b\u8272\u7684\u7f57\u88d9\u4e0a\u6709\u70b9\u70b9\u8840\u8ff9\u3002\u5c11\u5973\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4eba \u4e48\uff1f\u201d\u90a3\u5c0f\u4e2b\u73af\u9053\uff1a\u201c\u6ca1\u6709\u3002\u6211\u53ea\u662f\u95ef\u4e86\u4e09\u5904\u5c71\u5be8\uff0c\u65ab\u4f24\u4e86\u56db\u5341\u516b\u4e2a\u4eba\uff0c\u90fd\u662f\u4e2a\u81f4\u547d\u7684\u3002 \u90a3\u4e09\u5904\u5c71\u5be8\u7684\u516d\u4e2a\u6b63\u526f\u9996\u9886\u5219\u662f\u7ed9\u6211\u7528\u70b9\u7a74\u6cd5\u5236\u670d\u7684\uff0c\u73b0\u5728\u4ed6\u4eec\u90fd\u5df1\u4e56\u4e56\u7684\u6765\u4e86\u3002\u201d\u90a3 \u5c11\u5973\u6de1\u6de1\u8bf4\u9053\uff0c\u201c\u529e\u8fd9\u4e48\u4e00\u70b9\u4e8b\u60c5\uff0c\u5374\u7528\u4e86\u8fd9\u8bb8\u591a\u65f6\u5019\uff01\u201d\u90a3\u5c0f\u4e2b\u73af\u9053\uff1a\u201c\u6211\u8fd8\u8fdb\u57ce\u4e86 \u4e00\u8d9f\uff0c\u4f60\u6240\u8981\u67e5\u95ee\u7684\u90a3\u4e00\u5bf9\u7537\u5973\u90fd\u4e0d\u89c1\u4e86\u3002\u7537\u7684\u4e00\u70b9\u4e1c\u897f\u90e8\u6ca1\u6709\u7559\u4e0b\uff0c\u5973\u7684\u5305\u88b1\u6211\u5219\u987a \u624b\u5e26\u56de\uff0c\u6697\uff0c\u5c31\u662f\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u5475\uff0c\u5979\u8fd9\u624d\u8ba4\u51fa\uff0c\u539f\u6765\u8fd9\u5c0f\u4e2b\u73af\u80cc\u4e0a\u7684\u5305\u88b1\uff0c\u7adf\u7136\u5c31\u662f\u5979\u7684\u3002 \u90a3\u5c11\u5973\u5c06\u5305\u88b1\u63a5\u8fc7\u9012\u7ed9\u4e0a\u5b98\u5a49\u513f\u9053\uff1a \u3000\u3000\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u68c0\u70b9\u4e00\u4e0b\uff0c\u770b\u6709\u5931\u4e86\u4ec0\u4e48\u4e1c\u897f\u6ca1\u6709\uff0c\u55ef\uff0c\u4f60\u7684\u8863\u88f3\u4e5f\u8be5\u6362\u4e00\u6362\u5566\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u773c\u73b2\u73d1\uff0c\u77e5\u9053\u90a3\u51e0\u4e2a\u76d7\u9b41\u5c31\u8981\u8fdb\u6765\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u5979\u5acc\u6211\u5728\u6b64\uff0c\u8bf4\u8bdd \u4e0d\u4fbf\u3002\u8fd9\u4e9b\u6c5f\u6e56\u4e0a\u7684\u7981\u5fcc\uff0c\u957f\u5b59\u4f2f\u4f2f\u4e5f\u66fe\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e00\u6307\u4fa7\u9762\u7684\u623f\u95f4\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u8fdb\u6211\u7684\u5367\u623f\u53bb\u6362\u8863\u88f3\u5427\uff0c\u91cc\u9762\u68b3\u5986\u7684\u7528\u54c1\uff0c\u4e00 \u5e94\u4ff1\u5168\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u6628\u665a\u5728\u96e8\u4e2d\u5954\u8dd1\uff0c\u8863\u88f3\u786e\u662f\u6cbe\u4e86\u6ce5\u6cde\uff0c\u4fbf\u4e5f\u4e0d\u518d\u5ba2\u6c14\uff0c\u63a5\u4e86\u5305\u88b1\uff0c \u9053\u58f0\uff1a\u201c\u591a\u8c22\uff01\u201d\u8fdb\u5165\u623f\u95f4\uff0c\u968f\u624b\u5173\u4e0a\u4e86\u623f\u95e8\uff0c\u4f46\u542c\u5f97\u90a3\u5c11\u5973\u4f3c\u662f\u548c\u5979\u7684\u4e2b\u5934\u8bf4\u4e86\u51e0\u53e5 \u4ec0\u4e48\u6d3b\uff0c\u63a5\u7740\u4fbf\u4f20\u6765\u4e86\u5403\u5403\u7684\u7b11\u58f0\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u601d\u6f6e\u8d77\u4f0f\u4e0d\u5b9a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u7684\u884c\u5f84\u597d\u602a\uff0c\u5ffd\u513f\u5bf9\u6211\u51b7\u6de1\u4e4b\u6781\uff0c \u4e00\u5ffd\u513f\u53c8\u5bf9\u6211\u4eb2\u70ed\u975e\u5e38\uff0c\u771f\u771f\u662f\u4ee4\u4eba\u731c\u60f3\u4e0d\u900f\uff01\u201d\u6253\u5f00\u5305\u88b1\uff0c\u9009\u4e86\u4e00\u4ef6\u7d2b\u7f57\u8863\u88f3\uff0c\u6b63\u5f85 \u6362\u4e0a\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u6211\u7b49\u6709\u4ec0\u4e48\u5730\u65b9\u5f97\u7f6a\u4e86\u5973\u4fa0\uff1f\u8bf7\u5973\u4fa0\u660e\u793a\uff0c\u597d\u8ba9\u6211 \u4eec\u8d54\u7f6a\u3002\u201d \u3000\u3000\u8fd9\u8bf4\u8bdd\u7684\u58f0\u97f3\u597d\u719f\uff0c\u4e0a\u5b98\u5a49\u513f\u7741\u5927\u4e86\u773c\u775b\u8d34\u7740\u95e8\u7f1d\u4e00\u77a7\uff0c\u8fd9\u77a7\u5403\u60ca\u975e\u5c0f\uff0c\u53ea\u89c1\u5802\u524d \u7684\u77f3\u9636\u4e0a\uff0c\u524d\u540e\u4e24\u5217\uff0c\u8dea\u5012\u4e86\u516d\u4e2a\u4eba\uff0c\u4e0a\u662f\u5979\u5728\u5df4\u5dde\u9053\u4e0a\uff0c\u524d\u540e\u6240\u78b0\u5230\u7684\u90a3\u4e09\u6279\u5f3a\u76d7\u3002 \u3000\u3000\u90a3\u5c11\u5973\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5f97\u7f6a\u72b6\u4e48\uff0c\u90a3\u5012\u662f\u6ca1\u6709\u3002\u53ea\u662f\u627e\u5374\u8981\u8bf7\u6559\uff0c\u4f60\u4eec\u626f\u7684\u662f\u4ec0\u4e48\u65d7 \u53f7\uff1f\u201d\u5176\u4e2d\u4e00\u4eba\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u90a3\u65e0\u975e\u662f\u7eff\u6797\u4e2d\u4e00\u53e5\u5957\u8bed\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u8ba4\u5f97\u4ed6\u4fbf\u662f\u5728\u8def\u4e0a \u6253\u8fc7\u81ea\u5df1\u4e00\u97ad\u7684\u90a3\u4e2a\u76d7\u9b41\u5218\u56db\u3002 \u3000\u3000\u5c11\u5973\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u5957\u8bed\uff1f\u4f60\u8bf4\uff01\u201d\u5218\u56db\u5413\u4e86\u4e00\u8df3\uff0c\u9762\u5982\u4e0a\u8272\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u66ff \u5929\u884c\u9053\uff01\u201d\u90a3\u5c11\u5973\u7eb5\u58f0\u4eba\u7b11\uff0c\u5ffd\u5730\u7b11\u58f0\u4e00\u6536\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u52ab\u5bcc\u6d4e\u8d2b\uff0c\u884c\u4fa0\u4ed7\u4e49\uff0c\u90a3\u624d \u914d\u5f97\u4e0a\u8fd9\u51e0\u4e2a\u5b57\u3002 \u3000\u3000\u4f60\u4eec\u52ab\u63a0\u5ba2\u5546\uff0c\u4e3a\u5bb3\u767e\u59d3\uff0c\u5949\u627f\u5927\u6237\uff0c\u6b27\u538b\u826f\u6c11\uff0c\u8fd9\u7b97\u662f\u66ff\u4ec0\u4e48\u5929\uff1f\u884c\u4ec0\u4e48\u9053\uff1f\u201d \u3000\u3000\u90a3\u516d\u4e2a\u76d7\u9b41\u9762\u9762\u76f8\u89d1\uff0c\u597d\u50cf\u5341\u4e8c\u6708\u5929\u65f6\u6d78\u5728\u51b7\u6c34\u91cc\u4e00\u822c\uff0c\u5168\u8eab\u53d1\u6296\uff0c\u7259\u5173\u6253\u6218\u3002\u90a3 \u5c11\u987f\u4e86\u4e00\u9886\uff0c\u56de\u5934\u5411\u5979\u7684\u4e2b\u73af\u9053\uff1a\u201c\u5982\u610f\uff0c\u4f60\u66ff\u6211\u5c06\u4ed6\u4eec\u5e9f\u4e86\u3002\u201d \u3000\u3000\u90a3\u516d\u4e2a\u76d7\u9b41\u4e2d\u8fd8\u662f\u5218\u56db\u8f83\u4e3a\u80c6\u5927\uff0c\u6323\u624e\u7740\u53eb\u9053\uff1a\u201c\u5973\u4fa0\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a \u201c\u5982\u610f\u4e14\u6162\uff0c\u542c\u4ed6\u600e\u8bf4\u3002\u201d\u5218\u56db\u9053\uff1a\u201c\u5973\u4fa0\u8d23\u5907\u5f97\u4e0d\u9519\uff0c\u53ef\u662f\u6211\u95e8\u4e5f\u6709\u82e6\u8877\u3002\u201d\u5c11\u5973\u9053\uff1a \u201c\u4ec0\u4e48\u82e6\u8877\uff1f\u201d\u5218\u56db\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4eec\u90fd\u662f\u60f3\u6548\u5fe0\u524d\u671d\u7684\u4e49\u6c11\uff0c\u8eab\u5728\u7eff\u6797\uff0c\u5fc3\u5b58\u793e \u7a37\uff0c\u4e3a\u4e86\u8981\u6062\u590d\u674e\u5510\u7684\u6c5f\u5c71\uff0c\u4e0d\u80fd\u4e0d\u7b79\u63aa\u519b\u9977\u3002\u81f3\u4e8e\u6211\u4eec\u6240\u8054\u7edc\u7684\u90a3\u6279\u5927\u6237\uff0c\u4e5f\u90fd\u662f\u60f3 \u6548\u5fe0\u524d\u671d\u7684\u4eba\u3002\u201d\u8bf4\u7f62\u5077\u7aa5\u90a3\u5c11\u5973\u7684\u989c\u8272\uff0c\u8981\u77e5\u5f53\u65f6\u7684\u4fa0\u4e49\u9053\u4e2d\uff0c\u4e5f\u5206\u4e3a\u4e24\u6d3e\uff0c\u5176\u4e2d\u4e00 \u6d3e\uff0c\u4fbf\u662f\u8981\u63a8\u7ffb\u6b66\u5219\u706d\u7684\uff0c\u5218\u56db\u7b49\u5982\u8d4c\u535a\u4e00\u822c\uff0c\u4f46\u613f\u90a3\u5c11\u5973\u4e5f\u662f\u8fd9\u4e00\u6d3e\u7684\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5374\u662f\u4e0d\u52a8\u58f0\u8272\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u662f\u6548\u5fe0\u524d\u671d\uff0c\u6709\u4f55\u51ed\u8bc1\uff1f\u201d \u3000\u3000\u5218\u56db\u9053\uff1a\u201c\u51ed\u5f81\u4e48\u5012\u662f\u6ca1\u6709\u3002\u4f46\u524d\u51e0\u5929\u6709\u4e00\u4f4d\u738b\u5b59\u7ecf\u8fc7\uff0c\u6211\u4eec\u66fe\u53bb\u8fce\u63a5\u4ed6\uff0c\u627f\u4ed6\u5141 \u8bfa\uff0c\u5c06\u6765\u4e3e\u5175\u4e4b\u65e5\uff0c\u90fd\u5c01\u6211\u4eec\u505a\u9f99\u9a91\u90fd\u5c09\u3002\u5973\u4fa0\u82e5\u662f\u4e0d\u4fe1\u6b64\u4e8b\uff0c\u4e0b\u4e2a\u6708\u6708\u5706\u4e4b\u591c\uff0c\u53ef\u4ee5 \u81ea\u5df1\u5230\u5ce8\u5d4b\u91d1\u9876\u53bb\u770b\u3002\u201d\u5c11\u5973\u9053\uff1a\u201c\u770b\u4e9b\u4ec0\u4e48\uff1f\u201d\u5218\u56db\u9053\uff1a\u201c\u770b\u8fd9\u4f4d\u738b\u5b59\u4eb2\u81ea\u4e3b\u76df\u82f1\u96c4 \u5927\u4f1a\uff0c\u4fbf\u77e5\u6211\u8a00\u975e\u5047\u3002\u201d\u5c11\u5973\u9053\uff1a\u201c\u9093\u4f4d\u738b\u5b59\u662f\u4e0d\u662f\u540d\u53eb\u674e\u9038\uff1f\u201d\u5218\u56db\u559c\u9053\uff1a\u201c\u5bf9\u5440\uff01 \u539f\u7c73\u4f60\u4e5f\u77e5\u9053\u6b64\u4e8b\uff01\u4ed6\u6b63\u662f\u592a\u5b97\u7687\u5e1d\u7684\u4f84\u5b59\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u542c\u5230\u674e\u9038\u7684\u540d\u5b57\uff0c\u7279\u522b\u7559\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u5218\u56db\u8bf4\u6211\u7684\u674e\u9038\u54e5\u54e5\u5c01\u4ed6\u505a\u4ec0 \u4e48\u5b98\uff0c\u90a3\u81ea\u662f\u80e1\u8bf4\u516b\u9053\u3002\u4e0d\u8fc7\u4ed6\u4e5f\u7684\u786e\u5411\u6211\u8bf4\u8fc7\u60f3\u8054\u7edc\u5404\u5730\u82f1\u96c4\u4e4b\u4e8b\uff0c\u770b\u6765\u5ce8\u5d4b\u91d1\u9876\u4e4b \u4f1a\uff0c\u53ef\u80fd\u4e0d\u5047\u3002\u201d \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u5ffd\u542c\u5f97\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u674e\u9038\u4e43\u662f\u738b\u5b50\u738b\u5b59\u4e4b\u4e2d\uff0c\u6700\u51fa\u7c7b\u62d4\u8403\u7684\u4eba\u7269\uff0c \u5374\u60f3\u4e0d\u5230\u4e5f\u4e0e\u4f60\u4eec\u8fd9\u73ed\u5b9d\u8d1d\u4e00\u822c\u89c1\u8bc6\uff01 \u3000\u3000\u7adf\u628a\u5929\u4e0b\u5f53\u4f5c\u4e00\u5bb6\u4e00\u59d3\u7684\u4e1c\u897f\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u90a3\u5218\u56db\u66f4\u662f\u9707\u9a87\u4e4b\u6781\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u96be\u9053\u4f60\u7adf \u662f\u4f63\u6234\u90a3\u4e3a\u5bb3\u5929\u4e0b\u7684\u5973\u9b54\u738b\uff1f\u201d\u90a3\u5c11\u5973\u7eb5\u58f0\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7537\u4eba\u4eec\u505a\u4e86\u51e0\u5343\u5e74\u7687\u5e1d\uff0c\u4ece \u6765\u6ca1\u4eba\u95f2\u8bdd\uff0c\u4e00\u5230\u6709\u4e2a\u5973\u7687\u5e1d\u51fa\u6765\uff0c\u5c31\u906d\u53d7\u5230\u8bb8\u591a\u4eba\u7684\u5207\u9f7f\u75db\u6068\uff0c\u771f\u4e0d\u77e5\u662f\u4ec0\u4e48\u9053\u7406\uff1f\u201d \u8fd9\u8bdd\u662f\u5bf9\u5979\u7684\u4e2b\u5934\u5982\u610f\u8bf4\u7684\u3002\u5982\u610f\u7b11\u9053\uff1a\u201c\u4ed6\u95e8\u7537\u4eba\u4eec\u6025\u4ee5\u4e3a\u6837\u6837\u6bd4\u6211\u4eec\u5973\u4eba\u9ad8\u660e\uff0c\u5176 \u5b9e\u561b\u4e5f\u4e2a\u5c3d\u7136\uff0c\u50cf\u8fd9\u4e9b\u5b9d\u8d1d\u3002 \u3000\u3000\u6211\u5c31\u4e0d\u5c06\u4ed6\u4eec\u77a7\u5728\u773c\u5185\uff01\u201d \u3000\u3000\u90a3\u4e2a\u53eb\u505a\u674e\u4e03\u7684\u76d7\u9b41\u4e00\u89c1\u5218\u56db\u78b0\u4e86\u9489\u5b50\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u6c5f\u6e56\u9053\u4e0a\uff0c\u5e38\u8a00\u8bf4\u5f97 \u597d\uff0c\u7687\u5e1d\u8f6e\u6d41\u505a\uff0c\u660e\u5e74\u5230\u6211\u5bb6\uff0c\u8c01\u6709\u672c\u9886\u3002 \u3000\u3000\u8c01\u4fbf\u53ef\u505a\uff0c\u7537\u4eba\u5973\u4eba\uff0c\u90fd\u662f\u4e00\u6837\u3002\u6211\u53ef\u7edd\u4e0d\u6562\u53cd\u5bf9\u5929\u540e\uff01\u201d\u90a3\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u51ed\u4f60 \u4eec\u8fd9\u4e9b\u6ca1\u51fa\u606f\u7684\u4e1c\u897f\uff0c\u4e5f\u6562\u8bf4\u8981\u53cd\u5bf9\u5929\u540e\uff0c\u5f53\u771f\u662f\u8981\u53eb\u5929\u4e0b\u82f1\u96c4\u7b11\u7529\u7259\u9f7f\uff01\u201d\u63a5\u7740\u53c8\u9053\uff1a \u201c\u4ed6\u4eec\u603b\u7231\u8bf4\u5929\u540e\u4e3a\u5bb3\u5929\u4e0b\uff0c\u5374\u600e\u7684\u4e0d\u53bb\u542c\u542c\u8001\u767e\u59d3\u7684\u8bdd\uff1f\u5728\u8001\u767e\u59d3\u773c\u4e2d\uff0c\u60f3\u4e3a\u5bb3\u5929\u4e0b \u7684\u4eba\u786e\u662f\u8fd8\u6709\uff0c\u4f46\u5374\u4e0d\u662f\u5f53\u4eca\u5929\u540e\uff01\u201d\u90a3\u51e0\u4e2a\u76d7\u9b41\u4e00\u9f50\u53e9\u9996\u9053\uff1a\u201c\u5c0f\u7684\u4e0d\u6562\uff01\u201d\u5c11\u5973\u9053\uff1a \u201c\u4f60\u4eec\u8fd8\u672a\u6709\u8d44\u683c\u4e3a\u5bb3\u5929\u4e0b\uff0c\u53ef\u662f\u561b\uff0c\u4e3a\u5bb3\u8001\u767e\u59d3\u7684\u5730\u65b9\uff0c\u5374\u4e5f\u4e0d\u5c11\uff01\u201d\u51e4\u773c\u4e00\u7741\uff0c\u4e0d \u6012\u800c\u5a01\uff0c\u5413\u5f97\u8fd9\u51e0\u4e2a\u76d7\u9b41\uff0c\u5fc3\u80c6\u4ff1\u88c2\uff0c\u90fd\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u6c42\u5973\u4fa0\u9976\u547d\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u547d \u53ef\u4ee5\u9976\uff0c\u5374\u4e0d\u80fd\u8ba9\u4f60\u4eec\u518d\u53bb\u4f5c\u6076\u3002\u5982\u610f\uff0c\u628a\u4ed6\u4eec\u7684\u6b66\u529f\u90fd\u7ed9\u6211\u5e9f\u4e86\uff01\u201d\u4fbf\u542c\u5f97\u54c0\u53eb\u4e4b\u58f0 \u6b64\u8d77\u5f7c\u843d\uff0c\u60f3\u662f\u5982\u610f\u5df2\u5f00\u59cb\u7528\u91cd\u624b\u6cd5\u5e9f\u6389\u4ed6\u4eec\u7684\u6b66\u529f\u3002\u4e0a\u5b98\u5a49\u513f\u53eb\u77e5\u9053\u8fd9\u51e0\u4e2a\u76d7\u9b41\u4f5c\u6076 \u591a\u7aef\uff0c\u4f46\u542c\u4ed6\u4eec\u547c\u53f7\u4e4b\u60e8\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u60ca\u8089\u8df3\uff0c\u53c8\u7981\u4e0d\u4f4f\u6697\u6697\u53eb\u82e6\uff1a\u201c\u6211\u521a\u624d\u7adf\u7136\u8fd8\u53eb \u5979\u53bb\u884c\u523a\u6b66\u5219\u5929\uff01\u201d\u5979\u8fdb\u623f\u4e4b\u540e\uff0c\u4e00\u76f4\u7559\u5fc3\u542c\u5916\u95f4\u7684\u8bdd\uff0c\u65e0\u6687\u6e9c\u89c8\u623f\u4e2d\u666f\u7269\uff0c\u8fd9\u65f6\u5076\u4e00 \u62ac\u5934\uff0c\u53ea\u89c1\u5899\u58c1\u5546\u6302\u7740\u4e00\u5e45\u753b\uff0c\u753b\u7684\u6b63\u662f\u6b66\u5219\u5929\u7684\u50cf\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5c0f\u65f6\u5019\u5728\u5bab\u4e2d\u4e5f\u66fe\u89c1\u8fc7\u6b66\u5219\u5929\u4e00\u4e24\u9762\uff0c\u5f53\u65f6\u5e76\u4e0d\u89c9\u5f97\u6b66\u5219\u5929\u6709\u4ec0\u4e48\u7279\u5f02\u4e4b \u5904\uff0c\u53ea\u662f\u542c\u8bf4\u6b66\u5219\u5929\u7684\u5e74\u7eaa\u6bd4\u5979\u7684\u6bcd\u4eb2\u5927\u5f97\u591a\uff0c\u770b\u8d77\u6765\u5374\u4f3c\u6bd4\u5979\u7684\u6bcd\u4eb2\u8fd8\u8981\u5e74\u8f7b\uff0c\u56e0\u6b64 \u5c0f\u65f6\u5019\u7684\u5370\u8c61\u53ea\u662f\u6b66\u5219\u706d\u957f\u5f97\u201c\u597d\u770b\u201d\u800c\u5df2\uff0c\u800c\u4eca\u9aa4\u7136\u89c1\u4e86\u5979\u7684\u753b\u50cf\uff0c\u4f46\u89c9\u795e\u91c7\u5955\u5955\uff0c \u82f1\u6c14\u8feb\u4eba\uff0c\u4ee4\u4eba\u4e0d\u6562\u4ef0\u89c6\uff0c\u786e\u662f\u541b\u4e34\u5929\u4e0b\u4e4b\u8c61\uff01\u4e0d\u7531\u5f97\u6697\u6697\u53f9\u6c14\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u6211\u8fd9 \u8840\u6d77\u6df1\u4ec7\uff0c\u53ea\u6015\u662f\u96be\u4ee5\u62a5\u4e86\uff01\u201d \u3000\u3000\u8f6c\u8fc7\u5934\u672a\uff0c\u53ea\u89c1\u5bf9\u9762\u7684\u5899\u58c1\u4e0a\u4e5f\u6302\u6709\u4e00\u5e45\u753b\uff0c\u753b\u7684\u5374\u662f\u4e00\u4e2a\u5c11\u5973\u5728\u82b1\u95f4\u821e\u5251\uff0c\u753b\u4e0a \u8fd8\u9898\u6709\u4e00\u9996\u8bd7\uff0c\u8bd7\u9053\uff1a\u201c\u6708\u8272\u6eb6\u6eb6\u591c\uff0c\u5bd2\u5149\u970d\u970d\u65f6\u3002\u624b\u6301\u4e09\u5c3a\u5251\uff0c\u4e3a\u62a4\u597d\u82b1\u4f0e\u3002\u4f46\u5f97\u4eba \u540c\u4e50\uff0c\u4f55\u8f9e\u6211\u72ec\u75b2\u3002\u6b64\u4e2d\u6709\u771f\u610f\uff0c\u56fd\u571f\u5c5e\u5a25\u7709\u3002\u201d\u8bd7\u540e\u8fd8\u6709\u4e00\u884c\u9898\u8bb0\uff1a\u201c\u7384\u971c\u4f84\u5973\u6700\u559c \u82b1\u95f4\u821e\u5251\uff0c\u56e0\u547d\u5357\u7530\u4e3a\u4e4b\u4f5c\u753b\uff0c\u5e76\u4ee5\u6b64\u8bd7\u8d60\u4e4b\u3002\u6b66\u7b9c\u3002\u201d \u3000\u3000\u201c\u6b66\u7b9c\u201d\u7684\u201c\u7b9c\u201d\u5b57\u8bfb\u5982\u201c\u7167\u201d\uff0c\u8fd9\u662f\u6b66\u5219\u5929\u81ea\u53d6\u7684\u540d\u5b57\uff0c\u4e5f\u662f\u5979\u81ea\u521b\u7684\u65b0\u5b57\uff0c\u53d6 \u65e5\u6708\u5f53\u7a7a\u4e4b\u4e49\uff0c\u81ea\u8d1f\u4e4b\u5927\uff0c\u53ef\u4ee5\u60f3\u89c1\u3002\u4e0a\u5b98\u5a49\u513f\u8bfb\u4e86\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u90a3\u4e2a\u5c11\u5973\u540d \u53eb\u6b66\u7384\u971c\uff0c\u539f\u6765\u5c31\u662f\u6b66\u5219\u5929\u7684\u4f84\u5973\uff01\u770b\u8fd9\u9898\u8bb0\uff0c\u5357\u7530\u5927\u7ea6\u662f\u5bab\u4e2d\u7684\u753b\u5e08\uff0c\u800c\u8fd9\u4e00\u9996\u8bd7\u5219 \u662f\u6b66\u5219\u5929\u81ea\u5df1\u4f5c\u7684\u3002\u843d\u5728\u4e0a\u5b98\u5a49\u513f\u8fd9\u6837\u7684\u8bd7\u5bb6\u773c\u5750\uff0c\u867d\u7136\u5acc\u5979\u7528\u5b57\u7c97\u6d45\uff0c\u5bf9\u4ed7\u4e5f\u4e0d\u5de5\u6574\uff0c \u5374\u4e5f\u4e0d\u5f97\u4e0d\u4f69\u670d\u5979\u8bd7\u5883\u4e4b\u65b0\uff0c\u201c\u4ed7\u5251\u6269\u82b1\u201d\uff0c\u8fd9\u201c\u82b1\u201d\u5e76\u4e0d\u662f\u5b9e\u6307\u4e00\u822c\u7684\u5316\uff0c\u800c\u662f\u4ee3\u8868 \u4e86\u6240\u6709\u7f8e\u597d\u7684\u4e1c\u4e24\uff0c\u524d\u4eba\u4e4b\u8bd7\uff0c\u201c\u62a4\u82b1\u8005\u201d\u5fc5\u662f\u7537\u5b50\uff0c\u800c\u6b66\u5219\u5929\u7684\u8bd7\uff0c\u62a4\u82b1\u8005\u5374\u662f\u5a25\u7709\uff0c \u8981\u201c\u4ed7\u5251\u62a4\u82b1\u201d\uff0c\u90a3\u81ea\u7136\u662f\u8981\u63d0\u9632\u5f90\u656c\u4e1a\u4e4b\u6d41\u7684\u4f5c\u4e71\u4e86\u3002\u8fd9\u4e00\u9996\u8bd7\u4e0d\u4f46\u662f\u5973\u7687\u5e1d\u7684\u53e3\u6c14\uff0c \u800c\u4e14\u662f\u80f8\u895f\u5bbd\u5e7f\u3001\u773c\u5149\u8fdc\u5927\u7684\u5973\u653f\u6cbb\u5bb6\u7684\u53e3\u6c14\uff0c\u4e0a\u5b98\u5a49\u513f\u867d\u7136\u75db\u6068\u6b66\u5219\u5929\uff0c\u5374\u4e5f\u6697\u6697\u4e3a \u4e4b\u5fc3\u6298\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u51fa\u795e\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u9aa4\u7136\u60f3\u8d77\u4e86\u81ea\u5df2\u5904\u5883\u4e4b\u9669\uff0c\u8fd9\u6b66\u7384\u971c\u7684\u6b66\u529f\uff0c\u80dc\u8fc7\u81ea\u5df1 \u4f55\u6b62\u767e\u500d\uff01\u800c\u5979\u53c8\u77e5\u9053\u81ea\u5df1\u7684\u6765\u5386\uff0c\u800c\u6b64\u523b\u81ea\u5df1\u6b63\u5728\u5979\u7684\u5367\u623f\uff0c\u5440\uff0c\u8fd9\u5f53\u771f\u662f\u81ea\u6295\u7f57\u7f51\uff01 \u4e0a\u5b98\u5a49\u513f\u60f3\u7740\u60f3\u7740\uff0c\u4f46\u89c9\u4e0d\u5bd2\u800c\u6817\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u5916\u9762\u7684\u90a3\u4e2a\u5c0f\u4e2b\u5934\u65a5\u9053\uff1a\u201c\u6eda\u5427\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u5728\u95e8\u7f1d\u91cc\u5f20\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u90a3\u4eba \u4e2a\u76d7\u9b41\u5df2\u8d70\u51fa\u5927\u95e8\uff0c\u547b\u541f\u4e4b\u58f0\u8fd8\u662f\u65ad\u65ad\u7eed\u7eed\u7684\u4f20\u6765\u3002\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u5982\u610f\uff0c\u4f60\u8ddf\u6211\u8fd9\u51e0 \u5e74\uff0c\u4ee5\u4eca\u5929\u7684\u4e8b\u60c5\u529e\u5f97\u6700\u4e3a\u4ee4\u4eba\u75db\u5feb\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u9053\uff1a\u201c\u5979\u529e\u5143\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u60f3\u5fc5\u5c31\u8981\u6765\u5bf9\u4ed8\u6211\u4e86\u3002\u201d\u6b63\u81ea\u5fc3\u4e2d\u60f4\u60f4\u4e0d\u5b89\uff0c \u5ffd\u89c1\u53c8\u662f\u4e00\u4e2a\u5ba2\u4eba\u5230\u6765\uff0c\u8fd9\u4eba\u5374\u662f\u4e00\u4e2a\u519b\u5b98\uff0c\u4e00\u89c1\u6b66\u7384\u971c\u5c31\u8dea\u4e0b\u53bb\u8bf7\u5b89\u9053\uff1a\u201c\u5929\u540e\u53eb\u6211 \u6765\u63a2\u671b\u5c0f\u59d0\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u662f\u4e18\u795e\u52cb\u7684\u90e8\u4e0b\u4e48\uff1f\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4eec\u7684 \u4e18\u5927\u5c06\u519b\u4e3a\u4ec0\u4e48\u6740\u4e86\u5e9f\u592a\u5b50\u674e\u8d24\uff1f\u7ad9\u8d77\u6765\u8bf4\uff01\u201d \u3000\u3000\u90a3\u519b\u5b98\u5413\u5f97\u9762\u9752\u5507\u81ea\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4e18\u5927\u5c06\u519b\u4eca\u65e9\u8fdb\u57ce\uff0c\u7acb\u5373\u5c01\u95ed\u57ce\u95e8\u3002\u6211\u4eec\u90fd\u4e0d \u77e5\u9053\u57ce\u91cc\u95f9\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a \u3000\u3000\u201c\u9664\u4e86\u5c01\u95ed\u57ce\u95e8\uff0c\u4ed6\u8fd8\u505a\u4e9b\u4ec0\u4e48\uff1f\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u53ec\u96c6\u6240\u6709\u7684\u5c06\u6821\u70b9\u540d\uff0c\u6211\u56e0\u4e3a\u662f \u5949\u5929\u540e\u4e4b\u547d\u6765\u63a2\u671b\u59d1\u5a18\uff0c\u7279\u8bb8\u51fa\u5916\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a \u3000\u3000\u201c\u53ef\u6709\u54ea\u51e0\u4e2a\u5c06\u6821\u6ca1\u5230\u4e48\uff1f\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u53ea\u6709\u5de6\u519b\u90fd\u5c09\u7a0b\u52a1\u7532\u548c\u5148\u884c\u5b98\u97e9\u8363\u4e0d\u89c1\u3002 \u55ef\uff0c\u8fd9\u662f\u5929\u540e\u7ed9\u4f60\u7684\u4fe1\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u63a5\u8fc7\u4e86\u4fe1\uff0c\u5374\u4e0d\u5f00\u62c6\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u6211\u8fd9\u4e24\u4e2a\u4f8d\u5973\u7acb\u5373\u56de\u57ce\uff0c\u53bb\u89c1\u4e18\u5c06 \u519b\u3002\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u4e18\u5c06\u519b\u4e5f\u60f3\u8bf7\u59d1\u5a18\u8fdb\u57ce\u4e00\u89c1\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u6349\u5230\u4e86\u90a3\u4e24\u4e2a\u4eba\u4e4b \u540e\u518d\u4f1a\u89c1\u4ed6\u3002\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u6211\u4eca\u65e5\u53ea\u6015\u5c31\u8981\u56de\u4eac\u8986\u547d\uff0c\u4f60\u4e0d\u5199\u5c01\u56de\u4fe1\u7ed9\u5929\u540e\u4e48\uff1f\u5929\u540e \u8bf4\u5979\u5f88\u6302\u5ff5\u4f60\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u6ca1\u529f\u592b\u5566\uff0c\u5c31\u70e6\u4f60\u56de\u7980\u5929\u540e\uff0c\u53ea\u8bf4\u4e00\u53e5\uff0c\u6211\u4e0d\u60f3\u5230\u957f \u5b89\u53bb\uff01\u597d\uff0c\u4f60\u4eec\u8d76\u5feb\u8d70\u5427\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u4e0e\u4e24\u4e2a\u5c0f\u4e2b\u73af\u5148\u8d70\u51fa\u95e8\uff0c\u6b66\u7384\u971c\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u5730\u505c\u4e0b\uff0c\u8f7b\u8f7b\u7684\u5728\u623f\u95e8\u4e0a\u6572\u4e86 \u4e24\u4e0b\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u5927\u9707\uff0c\u624b\u629a\u5251\u67c4\uff0c\u8eb2\u5728\u95e8\u540e\uff0c\u53ea\u5f85\u5979\u63a8\u95e8\u800c\u5165\uff0c\u4fbf\u51c6\u5907\u8c41\u4e86\u6027\u547d\uff0c \u7ed9\u5979\u5f53\u80f8\u4e00\u5251\uff01\u5ffd\u542c\u5f97\u6b66\u7384\u7bb1\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u6362\u597d\u4e86\u8863\u670d\u6ca1\u6709\uff0c\u6211\u6709\u4e8b\u51fa\u53bb\u4e00\u8d9f\uff0c \u4f60\u82e5\u6b22\u559c\u5c31\u5728\u8fd9\u91cc\u6b47\u6b47\uff0c\u7b49\u6211\u56de\u6765\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u7259\u5173\u6253\u6218\uff0c\u5e94\u4e86\u4e00\u58f0\uff0c\u5374\u7b54\u4e0d\u51fa\u8bdd\uff0c\u53ea \u542c\u5f97\u6b66\u7384\u971c\u8bf4\u9053\uff1a\u201c\u9a6c\u5143\u901a\u4f60\u4e5f\u968f\u6211\u8d70\u5427\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u77a7\u7740\u4e24\u4eba\u8d70\u51fa\u5927\u95e8\uff0c\u76f4\u5230\u4e0d\u89c1\u4e86 \u4ed6\u4eec\u7684\u80cc\u5f71\uff0c\u8fd9\u624d\u63d2\u5251\u5f52\u9798\uff0c\u5401\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4e0a\u4e8c\u5b98\u5a49\u513f\u518d\u770b\u4e86\u4e00\u4e0b\u90a3\u5e45\u82b1\u95f4\u821e\u5251\u56fe\u548c\u6b66\u5219\u5929\u7684\u753b\u50cf\uff0c\u597d\u50cf\u7ecf\u5386\u4e86\u4e00\u573a\u6076\u68a6\uff0c\u5fc3 \u5934\u5140\u81ea\u8df3\u4e2a\u4e2a\u4f53\u3002\u8fd9\u4e8b\u60c5\u592a\u51fa\u5979\u610f\u6599\u4e4b\u5916\uff0c\u6b66\u7384\u971c\u660e\u660e\u77e5\u9053\u5979\u60f3\u523a\u6740\u6b66\u5219\u5929\uff0c\u5374\u80af\u7559\u4e0b \u5979\u4e00\u4e2a\u4eba\u5728\u6b64\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8981\u662f\u5979\u60f3\u6740\u6211\uff0c\u5728\u6843\u6797\u4e4b\u4e2d\uff0c\u5f53\u6211\u8bf4\u90a3\u756a\u8bdd\u7684\u65f6\u5019\uff0c\u5979\u4e00\u4e3e \u624b\u5c31\u53ef\u4ee5\u8981\u4e86\u6211\u7684\u6027\u547d\u3002\u5979\uff0c\u5979\u4e3a\u4ec0\u4e48\u4e0d\u8fd9\u6837\u505a\u5462\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u601d\u6f6e\u8d77\u4f0f\uff0c\u731c\u4e0d\u900f\u6b66\u7384 \u971c\u5bf9\u5979\u7a76\u7adf\u662f\u597d\u610f\u4e0d\u662f\u6076\u610f\uff1f \u3000\u3000\u4f46\u4e0d\u7ba1\u662f\u597d\u610f\u4e5f\u7f62\uff0c\u6076\u610f\u4e5f\u7f62\uff0c\u4e0a\u5b98\u5a49\u513f\u4e00\u60f3\u8d77\u5979\u5904\u7f6e\u90a3\u51e0\u4e2a\u76d7\u9b41\u7684\u624b\u6bb5\uff0c\u600e\u4e5f\u4e0d \u6562\u518d\u5728\u5979\u5bb6\u505c\u7559\uff0c\u5306\u5306\u6362\u597d\u8863\u88f3\uff0c\u4f7f\u8d70\u5c71\u8fd9\u4ee4\u4eba\u5fc3\u60b8\u7684\u5c4b\u5b50\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u671d\u9633\u521d\u4e0a\uff0c\u6570\u5341\u767e\u6811\u6843\u82b1\uff0c\u5728\u9633\u5149\u4e0b\u707f\u82e5\u4e91\u971e\uff0c\u6709\u5982\u4e00\u7247\u82b1\u6d77\uff0c\u4e0a\u5b98\u5a49\u513f\u4ece \u6843\u82b1\u6797\u4e2d\u8d70\u8fc7\uff0c\u518d\u4e00\u6b21\u7684\u60f3\u8d77\u6b66\u5219\u5929\u7684\u8bd7\u53e5\uff0c\u5fc3\u5934\u6005\u6005\u60d8\u60d8\uff0c\u5ffd\u7136\u4e00\u9635\u98ce\u5439\u6765\uff0c\u98d8\u4e0b\u4e86 \u7247\u7247\u6843\u82b1\uff0c\u4e0a\u5b98\u5a49\u513f\u75f4\u75f4\u60f3\u9053\uff1a\u201c\u6211\u8be5\u5f80\u54ea\u91cc\u53cb\u5462\uff1f\u662f\u8be5\u53bb\u523a\u6740\u6b66\u5219\u5929\uff1f\u8fd8\u662f\u56de\u5230\u5251\u9601 \u9690\u5c45\uff0c\u4ece\u6b64\u4e0d\u7406\u4eba\u4e16\u4e4b\u4e8b\uff0c\u514d\u5f97\u8bb8\u591a\u70e6\u607c\uff1f\u201d\u53ea\u89c9\u81ea\u5df1\u5c31\u50cf\u90a3\u4e9b\u88ab\u98ce\u522e\u4e0b\u679d\u5934\u7684\u6843\u82b1\u4e00 \u6837\uff0c\u98d8\u6cca\u65e0\u4f9d\uff0c\u4f55\u53bb\u4f55\u4ece\uff0c\u81ea\u5df1\u4e5f\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u5ce8\u5d4b\u91d1\u9876\u82f1\u96c4\u4f1a \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e94\u56de\u3000\u5ce8\u5d4b\u91d1\u9876\u82f1\u96c4\u4f1a \u3000\u3000\u4e00\u4e2a\u6708\u540e\uff0c\u6708\u5706\u4e4b\u591c\uff0c\u5ce8\u5d4b\u5c71\u4e0a\uff0c\u6709\u4e00\u4e2a\u5c11\u5973\u7684\u5029\u5f71\uff0c\u51fa\u6ca1\u4e8e\u5e7d\u6797\u7fe0\u8c37\u4e4b\u95f4\uff0c\u8fd9\u5c11 \u5973\u6b63\u662f\u4e0a\u5b98\u5a49\u513f\u3002\u5979\u7ecf\u8fc7\u4e86\u4e07\u904d\u601d\u91cf\uff0c\u7ec8\u4e8e\u51b3\u5b9a\u4e86\uff1a\u65e2\u4e0d\u8981\u53bb\u957f\u5b89\uff0c\u4e5f\u4e2a\u91cd\u56de\u5251\u9601\uff1b\u800c \u662f\u6765\u5230\u5ce8\u5d4b\u5c71\u627e\u5bfb\u5979\u7684\u674e\u9038\u54e5\u54e5\u3002\u5979\u5728\u6b66\u7384\u971c\u7684\u5bb6\u4e2d\uff0c\u66fe\u542c\u5230\u76d7\u9b41\u5218\u56db\u7684\u53f6\u9732\uff0c\u8bf4\u662f\u674e \u9038\u8981\u5728\u8fd9\u4e2a\u6708\u5706\u4e4b\u591c\uff0c\u5728\u5ce8\u5d4b\u91d1\u9876\uff0c\u4e3b\u76df\u4e00\u4e2a\u4ec0\u4e48\u201c\u82f1\u96c4\u5927\u4f1a\u201d\u3002 \u3000\u3000\u201c\u5ce8\u5d4b\u5929\u4e0b\u79c0\u201d\uff01\u8fd9\u53e5\u810d\u7099\u4eba\u53e3\u7684\u540d\u53e5\uff0c\u8bf4\u660e\u4e86\u5ce8\u5d4b\u5c71\u7684\u5c71\u5bb9\u79c0\u4e3d\uff0c\u4e3a\u5929\u4e0b\u540d\u5c71\u4e4b \u51a0\u3002\u5728\u6708\u591c\u4e0b\u7684\u5ce8\u5d4b\uff1b\u7f8e\u5f97\u66f4\u662f\u96be\u4ee5\u5f62\u5bb9\uff0c\u7fa4\u5cf0\u633a\u79c0\uff0c\u6216\u5982\u9759\u5973\u4e3d\u59b9\uff0c\u6216\u5982\u795e\u50e7\u5f02\u4e10\uff0c \u5ca9\u86d0\u8054\u5c5e\uff0c\u5c3d\u6001\u6781\u598d\u3002\u4e91\u6d77\u82cd\u832b\uff0c\u51b0\u8f6e\u6b63\u6ee1\u3002\u5ce8\u5d4b\u8bf8\u5cf0\uff0c\u5728\u6708\u5149\u4e91\u6c14\u4e4b\u4e0b\uff0c\u90fd\u597d\u50cf\u8499\u4e0a \u4e86\u4e00\u5c42\u8584\u96fe\u51b0\u7eb3\uff0c\u795e\u79d8\u3001\u5e7d\u9088\u3001\u5b81\u9759\uff01 \u3000\u3000\u7136\u800c\u4e0a\u5b98\u5a49\u513f\u7684\u5fc3\u5883\u53ef\u5e76\u4e0d\u5b81\u9759\uff0c\u81ea\u4ece\u5728\u5df4\u5dde\u548c\u674e\u9038\u5206\u6563\u4e4b\u540e\uff0c\u5979\u65e0\u65f6\u4e0d\u5728\u60e6\u5ff5\u7740 \u4ed6\uff0c\u4ed6\u4eca\u665a\u771f\u7684\u4f1a\u6765\u4e48\uff1f\u5728\u8fd9\u6837\u5e7d\u7f8e\u5b81\u9759\u7684\u5ce8\u5d4b\u5c71\u4e0a\uff0c\u4ed6\u771f\u7684\u8981\u6380\u8d77\u4e00\u573a\u6ed4\u5929\u7684\u5de8\u6d6a\u5417\uff1f \u5979\u5ffd\u5730\u611f\u5230\u8ff7\u832b\uff0c\u662f\u7684\uff0c\u5979\u4e0e\u674e\u9038\u4e00\u6837\uff0c\u751a\u81f3\u6bd4\u674e\u9038\u66f4\u75db\u6068\u6b66\u5219\u5929\uff0c\u7136\u800c\u674e\u9038\u8fd9\u6837\u7684\u505a \u6cd5\uff1a\u4e3a\u4e86\u6062\u590d\u4ed6\u674e\u5510\u7684\u6b63\u7edf\uff0c\u5c31\u8981\u6740\u4eba\u76c8\u57ce\uff0c\u6d41\u8840\u904d\u91ce\uff0c\u8fd9\u505a\u6cd5\u662f\u5bf9\u8fd8\u662f\u4e0d\u5bf9\uff0c\u5979\u5fc3\u5e95 \u5c1a\u6709\u6000\u7591\u3002 \u3000\u3000\u5979\u662f\u6628\u5929\u6765\u5230\u7684\uff0c\u5728\u8fd9\u4e24\u5929\u4e2d\uff0c\u5979\u5df2\u6e38\u4e86\u4e00\u904d\u5ce8\u5d4b\u4e0a\uff0c\u719f\u8bc6\u4e86\u5c71\u4e2d\u7684\u9053\u8def\uff0c\u8fd9\u65f6\u5979 \u6b63\u671d\u9996\u5ce8\u5d4b\u7684\u9876\u5cf0\u2014\u2014\u91d1\u9876\u8d70\u53bb\u3002 \u3000\u3000\u6708\u4eae\u6e10\u6e10\u5347\u81f3\u5929\u5fc3\uff0c\u7fa4\u5cf0\u9163\u7761\uff0c\u5076\u5c71\u4f20\u7c73\u4e86\u51e0\u58f0\u864e\u5578\u733f\u557c\uff0c\u4f46\u5374\u6ca1\u6709\u7a7a\u8c37\u8db3\u97f3\uff0c\u4e5f \u6ca1\u6709\u53d1\u73b0\u8352\u5c71\u4eba\u5f71\u3002\u4e0a\u5b98\u5a49\u513f\u5fc3\u9053\uff1a\u201c\u600e\u7684\u8fd8\u6ca1\u6709\u6765\u5462\uff1f\u96be\u9053\u90a3\u5218\u56db\u6240\u8a00\u4e0d\u5b9e\uff1f\u201d\u8fd9\u65f6 \u5979\u5fc3\u60c5\u77db\u76fe\u4e4b\u6781\uff0c\u65e2\u76fc\u671b\u548c\u674e\u9038\u89c1\u4e0a\u4e00\u9762\uff0c\u53c8\u4f46\u613f\u8fd9\u201c\u82f1\u96c4\u5927\u4f1a\u201d\u4e0d\u5f00\u4e5f\u7f62\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5979\u8d70\u8fc7\u4e86\u201c\u7334\u5b50\u5761\u201d\uff0c\u201c\u91d1\u9876\u201d\u9065\u9065\u5728\u671b\uff0c\u5ffd\u89c1\u4e24\u6761\u9ed1\u5f71\uff0c\u4ece\u4fa7\u9762\u7684\u5c71 \u5761\u75be\u5954\u800c\u6765\uff0c\u4e0a\u5b98\u5a49\u513f\u5401\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a \u3000\u3000\u201c\u7ec8\u4e8e\u6765\u4e86\u3002\u201d\u4f46\u770b\u8fd9\u4e24\u4e2a\u591c\u884c\u4eba\u7684\u8eab\u6cd5\uff0c\u5374\u5e76\u4e0d\u662f\u674e\u9038\u3002\u8bba\u8f7b\u8eab\u7684\u529f\u592b\uff0c\u597d\u50cf\u8fd8 \u4e0d\u53ca\u5979\u3002\u4e0a\u5b98\u5a49\u513f\u515c\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u5728\u90a3\u4e24\u4e2a\u4eba\u4e4b\u524d\uff0c\u5148\u5230\u8fbe\u4e86\u91d1\u9876\u3002\u7aa5\u63a2\u4e86\u4ed6\u4eec\u805a\u4f1a\u7684 \u5730\u65b9\uff0c\u6b63\u662f\u5728\u91d1\u9876\u5cf0\u5934\u7684\u5929\u5973\u576a\u4e0a\u3002 \u3000\u3000\u5ce8\u5d4b\u5c71\u6709\u5927\u5ce8\u3001\u4e8c\u5ce8\u3001\u4e09\u5ce8\u3001\u56db\u5ce8\u7b49\u5c71\uff0c\u5927\u5ce8\u4e8c\u5ce8\u4e24\u5c71\u76f8\u5bf9\u5982\u7709\uff0c\u4e00\u8bf4\u5ce8\u5d4b\u5c71\u7684\u540d \u79f0\u5c31\u662f\u7531\u6b64\u800c\u6765\u3002\u5728\u201c\u56db\u5ce8\u201d\u4e2d\uff0c\u5927\u5ce8\u5c71\u6700\u9ad8\uff0c\u5b83\u7684\u4e0a\u9762\u6709\u4e09\u9884\uff1a\u91d1\u9876\u3001\u5343\u4f5b\u9876\u3001\u4e07\u4f5b \u9876\uff0c\u800c\u4ee5\u91d1\u9876\u6700\u8457\u540d\u3002\u91d1\u9876\u5730\u52bf\u8f83\u5e73\uff0c\u7565\u5e26\u503e\u659c\uff0c\u904d\u5730\u957f\u7740\u7f8e\u4e3d\u7684\u51b7\u6749\u548c\u77ee\u5c0f\u7684\u7af9\u6797\uff0c \u5c55\u773c\u671b\u53bb\uff0c\u7eff\u8349\u5982\u8335\uff0c\u5e73\u94fa\u82e5\u9526\uff0c\u7aef\u7684\u662f\u6700\u597d\u4f5c\u805a\u4f1a\u573a\u6240\u7684\u8349\u576a\u3002\u4e0a\u5b98\u5a49\u513f\u89c5\u5f97\u4e86\u4e00\u4e2a \u706d\u7136\u7684\u77f3\u7b0b\uff0c\u77f3\u7b0b\u4e2d\u6709\u88c2\u7f1d\uff0c\u6070\u6070\u53ef\u4ee5\u5bb9\u5979\u85cf\u5728\u91cc\u9762\u3002\u53ea\u89c1\u8fd9\u4e24\u4e2a\u4eba\u5728\u8349\u576a\u5750\u5b9a\u4e4b\u540e\uff0c \u4fbf\u8f7b\u8f7b\u7684\u62cd\u4e86\u51e0\u4e0b\u624b\u638c\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u542c\u5f97\u4e1c\u5357\u897f\u5317\u56db\u9762\u90fd\u6709\u58f0\u76f8\u548c\uff0c\u8fd9\u4e24\u4e2a\u4eba\u76f8\u89c6\u7b11\u9053\uff1a\u201c\u5ddd\u5eb7\u9655\u5317\u4e24\u8def \u7684\u9053\u4e0a\u540c\u6e90\u679c\u7136\u90fd\u5148\u6765\u4e86\u3002\u201d\u4e0d\u591a\u4e00\u4f1a\uff0c\u4fbf\u6709\u4e03\u516b\u4e2a\u4eba\u9646\u7eed\u800c\u6765\uff0c\u5728\u8349\u576a\u4e0a\u5750\u5b9a\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u6ee1\u9762\u866c\u987b\u7684\u6c49\u5b50\uff0c\u5411\u6700\u5148\u6765\u5230\u8349\u576a\u7684\u90a3\u4eba\u95ee\u9053\uff1a \u3000\u3000\u201c\u9b4f\u4e09\u54e5\uff0c\u4eca\u5e74\u7684\u82f1\u96c4\u4f1a\u662f\u5b9a\u5348\u591c\u9f50\u96c6\uff0c\u4e0d\u77e5\u4e09\u54e5\u7ea6\u6211\u4eec\u65e9\u534a\u4e2a\u65f6\u8fb0\u5230\u6765\uff0c\u6709\u4f55\u89c1 \u6559\uff1f\u201d\u90a3\u88ab\u5524\u505a\u201c\u9b4f\u4e09\u54e5\u201d\u7684\u6c49\u5b50\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u542c\u8bf4\u4eca\u5e74\u4e4b\u4f1a\u8981\u63a8\u4e00\u4f4d\u65b0\u7684\u76df\u4e3b\uff0c\u5404\u4f4d\u5927\u6982\u90fd\u662f\u77e5\u9053\u7684\u4e86\uff1f\u201d\u4e00\u4e2a\u9634\u58f0\u602a\u6c14\u7684\u6c49 \u5b50\u8bf4\u9053\uff1a\u201c\u4ee5\u5f80\u7684\u5b9a\u4f8b\uff0c\u76df\u4e3b\u5341\u5e74\u4e00\u4efb\uff0c\u524d\u4efb\u8c37\u795e\u7fc1\u7684\u4efb\u6717\u4eca\u5e74\u521a\u597d\u671f\u6ee1\uff0c\u7167\u4f8b\u662f\u8981\u63a8 \u4e00\u4f4d\u65b0\u7684\u76df\u4e3b\uff0c\u9b4f\u4e09\u54e5\u53ef\u662f\u8981\u6211\u4eec\u5546\u8bae\u63a8\u4e3e\u65b0\u76df\u4e3b\u7684\u4e8b\u4e48\uff1f\u563f\u563f\uff0c\u6211\u770b\u8fd9\u4e2a\u4e0d\u7528\u5546\u91cf\u4e5f \u7f62\u3002\u201d\u9b4f\u4e09\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u90a3\u9634\u58f0\u602a\u6c14\u7684\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u5f53\u4eca\u7684\u6c5f\u6e56\u9053\u4e0a\uff0c\u8bba\u6b66\u529f\uff0c\u8bba\u58f0 \u671b\uff0c\u8fd8\u6709\u96be\u80fd\u80dc\u8fc7\u8c37\u795e\u7fc1\u7684\uff1f\u5f53\u7136\u662f\u7531\u4ed6\u8fde\u4efb\u3002\u201d \u3000\u3000\u9b4f\u4e09\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u795e\u7fc1\u8fde\u4efb\uff0c\u6ca1\u4eba\u6562\u8bf4\u4e0d\u670d\u3002\u53ef\u662f\u8fd9\u4e24\u5e74\u65b0\u51fa\u4e86\u4e00\u4f4d\u5c11\u5e74 \u82f1\u96c4\uff0c\u8bf8\u4f4d\u60f3\u5fc5\u4e5f\u6709\u6240\u95fb\u3002\u201d\u6709\u4eba\u95ee\u9053\uff1a \u3000\u3000\u201c\u662f\u8c01\uff1f\u201d\u9b4f\u4e09\u9053\uff1a\u201c\u674e\u9038\uff01\u201d\u767b\u65f6\u8bae\u8bba\u4e4b\u58f0\u56db\u8d77\uff0c\u201c\u674e\u9038\u662f\u8c01\uff1f\u201d\u201c\u6ca1\u542c\u8fc7\u8fd9\u4e2a \u540d\u5b57\uff01\u201d\u201c\u6211\u5012\u662f\u542c\u8bf4\u8fc7\u7684\uff0c\u542c\u8bf4\u4ed6\u66fe\u5355\u9a91\u5339\u9a6c\uff0c\u8c03\u505c\u4e86\u7389\u9f99\u5c71\u548c\u98de\u864e\u5be8\u7684\u7ea0\u7eb7\u3002\u201d \u201c\u90a3\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u6768\u5be8\u4e3b\u4f60\u8bf4\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u5ea7\u4e2d\u4e00\u4e2a\u8001\u8005\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u7389\u9f99\u5c71\u548c\u98de\u864e\u5be8\u7684\u4e24\u5bb6\u5be8\u4e3b\uff0c\u53bb\u5e74\u4e94\u6708\u4e89\u52ab\u4e00\u9879\u9556\u94f6\uff0c \u76f8\u6301\u4e0d\u4e0b\uff0c\u770b\u770b\u4e24\u4e2a\u5927\u5be8\u4e3b\u5c31\u8981\u706b\u62fc\u3002\u674e\u9038\u8d64\u624b\u7a7a\u62f3\uff0c\u6253\u8d25\u4e86\u7389\u9f99\u5c71\u5468\u5be8\u4e3b\u7684\u4e5d\u8033\u5927\u73af \u5200\uff0c\u53c8\u6253\u8d25\u4e86\u98de\u864e\u5be8\u6a0a\u5be8\u4e3b\u4e07\u5b57\u94f6\u82b1\u593a\uff0c\u4e24\u5bb6\u5be8\u4e3b\u90fd\u5bf9\u4ed6\u5fc3\u670d\u53e3\u670d\uff0c\u8fd9\u9879\u9556\u94f6\u4fbf\u5728\u674e\u9038 \u7684\u4e3b\u6301\u4e0b\u5e73\u5747\u5206\u4e86\u3002\u201d\u8fd9\u756a\u8bdd\u4e00\u8bf4\uff0c\u7fa4\u8c6a\u55b7\u55b7\u79f0\u5f02\uff0c\u770b\u7c73\u90a3\u4e24\u5bb6\u5be8\u4e0a\u5728\u7eff\u6797\u4e2d\u5fc5\u5b9a\u662f\u5927 \u5927\u6709\u540d\u3002\u4f46\u8fd8\u662f\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u53ea\u51ed\u8fd9\u4e00\u6869\u4e8b\u60c5\uff0c\u672a\u5fc5\u5c31\u80fd\u628a\u8c37\u795e\u7fc1\u538b\u4e0b\u53bb\u4e86\uff1f\u201d\u6b64\u8a00\u4e00 \u51fa\uff0c\u5ea7\u4e0a\u7fa4\u8c6a\uff0c\u5341\u6709\u4e03\u516b\uff0c\u8f70\u7136\u79f0\u662f\u3002 \u3000\u3000\u9b4f\u4e09\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u538b\u662f\u538b\u4e0d\u4e0b\u53bb\u7684\uff0c\u53ea\u662f\u5c1a\u6709\u4e00\u4e8b\uff0c\u8bf8\u4f4d\u6050\u672a\u77e5\u9053\uff1a\u201c\u8fd9\u674e\u9038\u662f\u8c37 \u795e\u7fc1\u4eb2\u81ea\u770b\u4e2d\u7684\uff0c\u8c37\u795e\u7fc1\u672c\u4eba\u5c31\u613f\u610f\u8ffd\u968f\u4ed6\uff0c\u201d\u7acb\u5373\u6709\u51e0\u4e2a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u8bdd\u662f\u8c37\u795e\u7fc1 \u4eb2\u81ea\u5bf9\u4f60\u8bf4\u7684\u5417\uff1f\u201d\u8c37\u795e\u7fc1\u8eab\u4e3a\u76df\u4e3b\uff0c\u5c82\u80af\u5bf9\u9b4f\u4e09\u8fd9\u7965\u4e8c\u4e09\u6d41\u7684\u4eba\u7269\u503e\u5410\u5fc3\u4e8b\uff1f\u800c\u4e14\u662f \u8bf4\u4f69\u670d\u4e00\u4e2a\u521d\u51fa\u9053\u7684\u5c11\u5e74\uff1f\u65e0\u602a\u4e4e\u5728\u5ea7\u8bf8\u4eba\u5341\u4e5d\u4e0d\u4fe1\u3002 \u3000\u3000\u9b4f\u4e09\u538b\u4f4e\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8c37\u795e\u7fc1\u81ea\u7136\u4e0d\u4f1a\u4eb2\u53e3\u5bf9\u5144\u5f1f\u8bf4\u8bdd\uff0c\u4f46\u8fd9\u8bdd\u5374\u662f\u6c60\u6700\u4eb2\u4fe1\u7684\u5f1f \u5b50\u9f99\u4e09\u5148\u751f\u8bf4\u7684\uff01\u9f99\u4e09\u5148\u751f\u5c31\u8981\u5230\u6765\uff0c\u8bf8\u4f4d\u4e0d\u4f30\uff0c\u53ef\u4ee5\u95ee\u4ed6\uff01\u201d\u4f17\u4eba\u90fd\u77e5\u9053\u8fd9\u4f4d\u9b4f\u5be8\u4e3b \u662f\u9f99\u4e09\u5148\u751f\u7684\u624b\u4e0b\uff0c\u6b63\u5728\u534a\u4fe1\u534a\u7591\uff0c\u9b4f\u4e09\u5ffd\u53c8\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e00\u4e2a\u6781\u5927\u7684\u79d8\u5bc6\uff01\u201d \u3000\u3000\u542c\u5230\u6b64\u5904\uff0c\u8bdd\u8bed\u7ec6\u4e0d\u53ef\u95fb\uff0c\u4f46\u89c1\u9b4f\u4e09\u4e0e\u4f17\u4eba\u4ea4\u5934\u63a5\u8033\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u7fa4\u8c6a\u6b22\u547c\u53eb\u5578\uff0c \u9b4f\u4e09\u8f7b\u8f7b\u62cd\u4e86\u4e00\u4e0b\u624b\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u6768\u6cf0\u4e3b\u9996\u5148\u8bf4\u9053\uff1a\u201c\u8fd9\u8fd8\u6709\u4ec0\u4e48\u8bf4 \u7684\u3002\u7b49\u4e0b\u54b1\u4eec\u4e00\u81f4\u63a8\u6234\uff0c\u7ed9\u674e\u516c\u5b50\u5927\u58ee\u58f0\u5a01\u4fbf\u662f\u3002\u201d\u90a3\u9634\u58f0\u602a\u6c14\u7684\u6c49\u5b50\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e09\u54e5\uff0c\u591a\u8c22\u4f60\u7684\u6307\u70b9\u3002\u8fd9\u4f4d\u674e\u516c\u5b50\u751f\u5f97\u547d\u597d\uff0c\u5408\u8be5\u4ed6\u505a\u76df\u4e3b\u3002 \u3000\u3000\u54b1\u4eec\u4e5f\u9002\u9022\u5176\u4f1a\uff0c\u5408\u8be5\uff0c\u2026\u2026\u54c8\uff0c\u54c8\uff01\u8be5\u98de\u9ec4\u817e\u8fbe\u7684\u4e86\uff01\u9b4f\u4e09\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u54b1 \u4eec\u6709\u4e86\u8fd9\u4f4d\u65b0\u76df\u4e3b\uff0c\u4e2a\u4e2a\u524d\u9014\u4f3c\u9526\uff01\u201d\u6b64\u8bdd\u4e00\u51fa\uff0c\u4e2a\u4e2a\u5f00\u7709\uff0c\u4eba\u4eba\u6b22\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u806a\u660e\u7edd\u9876\uff0c\u77e5\u9053\u9b4f\u4e09\u6240\u8bf4\u7684\u201c\u79d8\u5bc6\u201d\uff0c\u5b9a\u662f\u5c06\u674e\u9038\u7684\u738b\u5b59\u8eab\u4efd\u63ed\u9732\u65e0\u7591\u3002 \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u662f\u674e\u9038\u54e5\u54e5\u77e5\u9053\u4f17\u4eba\u4e3a\u4e86\u4ed6\u7684\u8eab\u4efd\u624d\u63a8\u6234\u4ed6\uff0c\u4ed6\u672a\u5fc5\u80af\u9886\u8fd9\u4e2a\u60c5\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u56de\uff0c\u53c8\u6709\u4e00\u5e2e\u4eba\u6765\u5230\uff0c\u4e3a\u9996\u7684\u662f\u4e2a\u4e2d\u5e74\u61e6\u58eb\uff0c\u8eab\u7a7f\u957f\u886b\uff0c\u624b\u6447\u6298\u6247\uff0c\u6c14\u6001\u95f2 \u9002\uff0c\u4f17\u4eba\u4e00\u89c1\uff0c\u7eb7\u7eb7\u8d77\u7acb\u76f8\u8fce\uff0c\u9ad8\u53eb\u201c\u9f99\u4e09\u5149\u751f\uff01\u201d\u9b4f\u4e09\u6025\u5fd9\u4e0a\u53bb\u548c\u4ed6\u8bf4\u8bdd\uff0c\u8fd9\u4e2d\u5e74\u5112 \u58eb\u9891\u9891\u70b9\u9996\u9053\u597d\u3002\u5ffd\u5730\u6e38\u76ee\u56db\u987e\uff0c\u95ee\u9053\uff1a\u201c\u90b9\u4e09\u3001\u674e\u4e03\u4ed6\u4eec\u51e0\u4f4d\u5462\uff0c\u600e\u7684\u8fd8\u6ca1\u6709\u6765\uff1f\u201d \u9b4f\u4e09\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u5df2\u901a\u77e5\u4ed6\u4eec\u4e86\uff0c\u4e5f\u8bb8\u8def\u4e0a\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u4e0d\u8fc7\uff0c\u54b1\u4eec\u7684\u4eba\u6570\u5df2\u7ecf \u591f\u591a\uff0c\u5c31\u7f3a\u4ed6\u4eec\u51e0\u4f4d\u4e5f\u4e0d\u6253\u7d27\u3002\u201d \u3000\u3000\u63a5\u7740\u9646\u7eed\u6709\u4eba\u6765\u5230\uff0c\u540e\u6765\u7684\u5165\u4e0e\u5148\u5934\u6765\u8fd9\u4e24\u6279\uff0c\u4f3c\u4e4e\u4e0d\u662f\u4e00\u4f19\uff0c\u4ed6\u4eec\u5bf9\u201c\u9f99\u4e09\u5148\u751f\u201d \u53ea\u662f\u70b9\u5934\u4e3a\u793c\uff0c\u5e76\u4e0d\u7279\u522b\u606d\u656c\u3002\u5230\u4e86\u5348\u591c\u65f6\u5206\uff0c\u8349\u576a\u4e0a\u5df2\u5750\u6ee1\u4e86\u4eba\uff0c\u4f46\u8c37\u795e\u7fc1\u8fd8\u4e0d\u89c1\u6765\uff0c \u4f17\u4eba\u90fd\u5728\u4ea4\u5934\u63a5\u8033\u7684\u8bae\u8bba\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u4f1a\uff0c\u6708\u4eae\u6b63\u6302\u5929\u5fc3\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u4f17\u4eba\u4ff1\u90fd\u8d77\u7acb\uff0c\u90a3\u5578\u58f0\u521d\u8d77\u4e4b\u65f6\uff0c\u597d \u50cf\u8fd8\u5728\u6570\u91cc\u4e4b\u5916\uff0c\u5578\u7737\u4e00\u6b47\uff0c\u8349\u576a\u4e0a\u5df2\u73b0\u51fa\u4e86\u4e24\u4e2a\u4eba\uff0c\u4e00\u8001\u4e00\u5c11\uff0c\u8001\u7684\u662f\u8c37\u795e\u7fc1\uff0c\u5c11\u7684 \u6b63\u662f\u674e\u9038\u3002\u4f17\u4eba\u9ad8\u547c\u201c\u76df\u4e3b\u4e07\u5c81\uff01\u201d\u95ea\u5f00\u4e86\u4e00\u6761\u8def\uff0c\u8ba9\u8c37\u795e\u7fc1\u548c\u674e\u9038\u8d70\u5230\u573a\u5fc3\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u535c\u535c\u4e71\u8df3\uff0c\u4f46\u89c1\u8c37\u4f38\u7fc1\u62f1\u624b\u73af\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u52b3\u5404\u4f4d\u4e45\u7b49\u4e86\uff0c\u6211\u5148\u7ed9\u5404\u4f4d\u5f15\u89c1\u4e00\u4f4d\u5c11\u5e74\u82f1\u96c4\uff01\u201d\u674e\u9038\u4e5f\u62b1\u62f3\u5bf9\u7fa4\u8c6a\u65bd\u793c\uff0c\u8c37\u795e \u7fc1\u63a5\u7740\u8bf4\u9053\uff0c\u201c\u8fd9\u4f4d\u662f\u516b\u81c2\u54ea\u54a4\u5c09\u8fdf\u70af\u7684\u5f97\u610f\u95e8\u4eba\uff0c\u540d\u53eb\u674e\u9038\uff0c\u51fa\u9053\u867d\u7136\u4e0d\u6ee1\u4e00\u5e74\uff0c\u6b66 \u529f\u4eba\u54c1\u5df2\u8db3\u4ee5\u9707\u6151\u6c5f\u6e56\uff0c\u8001\u5927\u865a\u5ea6\u4e86\u6570\u5341\u5bd2\u6691\uff0c\u8fd8\u672a\u66fe\u89c1\u8fc7\u5982\u6b64\u82f1\u96c4\u4eba\u7269\uff01\u201d\u8bdd\u58f0\u672a\u6b47\uff0c \u767b\u65f6\u6709\u8bb8\u591a\u4eba\u6b22\u547c\u62cd\u638c\uff0c\u4e0a\u6709\u5a49\u513f\u7559\u5fc3\u6697\u770b\uff0c\u90fd\u662f\u90a3\u201c\u9f99\u4e09\u5148\u751f\u201d\u9884\u5148\u7ea6\u5b9a\u7684\u4eba\u3002 \u3000\u3000\u638c\u58f0\u96f7\u52a8\u4e4b\u4e2d\uff0c\u5374\u4e5f\u6709\u4e0d\u5c11\u4eba\u7a83\u7a83\u79c1\u8bed\uff0c\u539f\u6765\u4eca\u665a\u53c2\u52a0\u82f1\u96c4\u4eba\u4f1a\u4e4b\u4eba\uff0c\u4e09\u6559\u4e5d\u6d41\uff0c \u65e0\u6240\u4e0d\u6709\u3002\u5927\u7565\u53ef\u5206\u4f5c\u51e0\u6279\uff0c\u4e00\u6279\u662f\u7eff\u6797\u8c6a\u6770\uff0c\u4e00\u6279\u662f\u6b66\u6797\u540d\u5bbf\uff0c\u4e00\u6279\u662f\u540d\u95e8\u6b63\u6d3e\u7684\u4fa0 \u4e49\u9053\uff0c\u4e00\u6279\u662f\u9000\u4f11\u7684\u524d\u671d\u6b66\u5b98\uff0c\u8fd8\u6709\u4e00\u4e9b\u5219\u662f\u60f3\u6765\u4e89\u593a\u76df\u4e3b\u4e4b\u4f4d\u7684\u6c5f\u6e56\u602a\u6770\u3002\u9664\u4e86\u5c11\u6570 \u77e5\u9053\u5185\u60c5\u7684\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u7b49\uff0c\u65e0\u4e0d\u8be7\u5f02\u4e07\u5206\uff01 \u3000\u3000\u6709\u4e9b\u4eba\u77e5\u9053\u8c37\u795e\u7fc1\u4e0e\u5c09\u8fdf\u70af\u4e43\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u60f3\u4e3a\u76df\u4f84\u626c\u540d\u7acb \u4e07\uff0c\u4f46\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u5c82\u4e0d\u662f\u63c4\u626c\u8fc7\u5f53\u4e48\uff1f\u201d \u3000\u3000\u8c37\u795e\u7fc1\u624b\u633d\u674e\u9038\uff0c\u73af\u987e\u4e2a\u573a\uff0c\u4f8d\u5230\u638c\u58f0\u505c\u606f\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u5728\u5341\u5e74\u4e4b\u524d\uff0c\u627f\u8499\u8bf8\u4f4d\u4e0d\u5f03\uff0c\u63a8\u4e3a\u76df\u4e3b\uff0c\u5341\u5e74\u4ee5\u6765\uff0c\u6127\u65e0\u5efa\u6811\u3002\u5982\u4eca\u671f\u6ee1\uff0c\u8001 \u592b\u4ea6\u5782\u5782\u8001\u77e3\uff0c\u7406\u8be5\u8ba9\u8d24\u3002\u60f3\u5f53\u4eca\u5929\u4e0b\uff0c\u6b63\u662f\u591a\u4e8b\u4e4b\u79cb\uff0c\u76df\u4e3b\u4e4b\u4efb\uff0c\u82e5\u5f97\u4e00\u5c11\u5e74\u82f1\u96c4\u62c5 \u5f53\uff0c\u6700\u4e3a\u9002\u5f53\u3002\u8fd9\u4f4d\u674e\u9038\u5144\u5f1f\uff0c\u53bb\u5e74\u4e94\u6708\u66fe\u8c03\u505c\u4e86\u7389\u9f99\u5c71\u4e0e\u98de\u864e\u5be8\u7684\u7ea0\u7eb7\uff0c\u4eca\u5e74\u6b63\u6708\uff0c \u53c8\u66fe\u6253\u8d25\u4e86\u5927\u5185\u7684\u4e24\u4e2a\u9ad8\u624b\uff0c\u5c06\u8d64\u77f3\u5c97\u7684\u7a46\u5be8\u4e3b\u6551\u51fa\u6765\uff0c\u8fd9\u4e24\u4ef6\u4e8b\u60c5\uff0c\u5728\u5ea7\u670b\u53cb\uff0c\u60f3\u5fc5 \u6709\u6240\u77e5\u95fb\u3002\u4f55\u51b5\u6211\u4eec\u8fd9\u4f4d\u674e\u8d24\u5f1f\u719f\u8bfb\u5175\u4e66\uff0c\u80f8\u6000\u58ee\u5fd0\uff0c\u6b63\u597d\u7387\u9886\u6c5f\u6e56\u8c6a\u6770\uff0c\u505a\u4e00\u756a\u52a8\u5730 \u60ca\u5929\u7684\u4e89\u4e1a\u3002\u53e4\u8bed\u4e91\u5df3\uff1a\u6709\u5fb7\u4e0d\u5728\u5e74\u9ad8\uff0c\u65e0\u8c0b\u6789\u6d3b\u767e\u5c81\u3002\u674e\u8d24\u5f1f\u6587\u6b66\u517c\u8d44\uff0c\u5fb7\u624d\u4ff1\u8857\uff0c \u8001\u592b\u4e4b\u610f\uff0c\u4fbf\u662f\u60f3\u63a8\u4e3e\u4ed6\u51fa\u4efb\u76df\u4e3b\u3002\u201d \u3000\u3000\u8c37\u795e\u7fc1\u8fd9\u756a\u8bdd\u8bf4\u5b8c\uff0c\u767b\u65f6\u4eba\u58f0\u5982\u6cb8\uff0c\u9f99\u4e09\u5148\u751f\u90a3\u4e00\u4f19\u4eba\u81ea\u662f\u6b22\u547c\u9f13\u638c\uff0c\u529b\u7aed\u58f0\u5636\uff0c \u7aed\u8bda\u63a8\u6234\uff1a\u5176\u4ed6\u7684\u4eba\u90fd\u5728\u7eb7\u7eb7\u8bae\u8bba\uff0c\u867d\u7136\u788d\u4e8e\u8c37\u795e\u7fc1\u7684\u9762\u5b50\uff0c\u4e0d\u6562\u5927\u58f0\u53cd\u5bf9\uff0c\u4f46\u5374\u663e\u7136 \u662f\u4e0d\u670d\u8fd9\u4e2a\u521d\u51fa\u9053\u7684\u5c11\u5e74\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u5404\u4f4d\u6709\u8bdd\u8bf7\u8bf4\u3002\u201d\u6cb3\u5357\u536b\u57ce\u7684\u5b5f\u5e84\u4e3b\u5b5f\u79cb\u5143\u7ad9\u51fa\u6765\u8bf4\u9053\uff1a\u201c\u76df\u4e3b\u4e4b\u4efb\uff0c \u975e\u540c\u5c0f\u53ef\uff0c\u6162\u6162\u5546\u8bae\u4e0d\u8fdf\u3002\u8fd9\u4f4d\u674e\u5144\u5f1f\u65e2\u7136\u6765\u53c2\u52a0\u82f1\u96c4\u5927\u4f1a\uff0c\u6709\u8bb8\u591a\u4eba\u8fd8\u672a\u8ba4\u8bc6\u4ed6\uff0c\u8001 \u592b\u4ea6\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u6562\u8bf7\u8fd9\u4f4d\u674e\u5144\u5f1f\u6296\u9732\u4e00\u624b\u7edd\u6280\uff0c\u8ba9\u5927\u5bb6\u5f00\u5f00\u773c\u754c\u3002\u201d\u521d\u6765\u53c2\u52a0\u82f1\u96c4\u5927 \u4f1a\u4e4b\u4eba\uff0c\u9664\u975e\u662f\u65e9\u5df2\u6210\u540d\uff0c\u4f17\u7686\u94a6\u670d\u7684\u82f1\u96c4\uff0c\u5426\u5219\u90fd\u8981\u7ecf\u4e0e\u4f1a\u4e4b\u4eba\uff0c\u51fa\u9898\u76f8\u8bd5\uff0c\u53ca\u683c\u65b9 \u53ef\u53c2\u52a0\u3002\u5b5f\u79cb\u5143\u8bf7\u674e\u9038\u81ea\u732e\u6280\u827a\uff0c\u5df2\u662f\u5bf9\u8c37\u795e\u7fc1\u5356\u4e86\u9762\u5b50\uff0c\u9f99\u4e09\u8fd9\u4e00\u4f19\u4eba\u867d\u7136\u6697\u6697\u5600\u5495\uff0c \u5374\u4e5f\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u5c31\u968f\u4fbf\u9732\u4e24\u624b\u5427\u3002\u201d\u674e\u9038\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u53ef\u5fb7\u8584\u624d\u9c9c\uff0c \u8c2c\u627f\u8c37\u8001\u524d\u8f88\u63a8\u8bb8\uff0c\u60ed\u6127\u4e4b\u6781\uff01\u76df\u4e3b\u4e4b\u4f4d\uff0c\u90a3\u662f\u7edd\u4e0d\u6562\u5f53\u3002\u4f46\u65e2\u662f\u957f\u8005\u6709\u547d\uff0c\u5c0f\u53ef\u4e5f\u6b63 \u597d\u8d81\u6b64\u8ba5\u4f1a\uff0c\u5411\u5404\u4f4d\u8bf7\u6559\u3002\u53ea\u6015\u8fd9\u7c97\u6d45\u7684\u529f\u592b\uff0c\u96be\u5165\u65b9\u5bb6\u6cd5\u773c\u3002\u201d\u8bf4\u7f62\u4e00\u5f2f\u8170\u5728\u5730\u4e0a\u62d4 \u8d77\u4e86\u4e00\u4e1b\u8305\u8349\uff0c\u53cc\u6307\u526a\u5934\u526a\u5c3e\uff0c\u526a\u6210\u4e86\u4e94\u5bf8\u6765\u957f\u7684\u4e00\u675f\u8349\u4f0e\u3002\u4ece\u4eba\u5fc3\u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u201d \u53cc\u6307\u526a\u8349\uff0c\u6307\u52b2\u867d\u7136\u4e0d\u5f31\uff0c\u4f46\u5728\u7fa4\u8c6a\u773c\u4e2d\uff0c\u5374\u786e\u5b9e\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u4f46\u89c1\u674e\u9038\u5fae\u5fae\u4e00\u7b11\uff0c\u6602\u9996\u5411\u5929\uff0c\u4f17\u4eba\u968f\u7740\u4e00\u671b\uff0c\u6709\u4eba\u7b11\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u672a\u89c1\u8fc7\u4f5b\u706f\u4e48\uff1f\u201d\u201c\u5c31\u662f\u8fd9\u4e00\u624b\u7edd\u679d\u4e86\u4e48\uff1f\u8fd8\u662f\u7b49\u8d4f\u8fc7\u4f5b\u706f\u4e4b\u540e\u518d\u884c \u732e\u6280\uff1f\u201d\u539f\u6765\u201c\u4f5b\u706f\u201d\u4e43\u662f\u5ce8\u5d4b\u5c71\u4e0a\u7279\u6709\u7684\u80dc\u666f\u3002 \u3000\u3000\u5ce8\u5d4b\u5c71\u5bcc\u4e8e\u78f7\u78fa\uff0c\u5e7b\u6210\u201c\u9b3c\u706b\u201d\uff0c\u7f8e\u5176\u540d\u800c\u66f0\u201c\u4f5b\u706f\u201d\uff0c\u4f5b\u706f\u51fa\u73b0\u5728\u665a\u95f4\uff0c\u521d\u8d77\u65f6 \u604d\u82e5\u4e00\u5c0f\u70b9\u6d41\u661f\uff0c\u6d41\u5165\u6ee1\u5e03\u4e91\u96fe\u7684\u5c71\u8c37\uff0c\u5ffd\u660e\u5ffd\u706d\uff0c\u95ea\u70c1\u4e0d\u5b9a\u3002\u970e\u65f6\u95f4\u63a5\u4e8c\u8fde\u4e09\u51fa\u6765\uff0c \u7531\u6570\u5341\u6570\u767e\u4ee5\u81f3\u4e8e\u660e\u706f\u4e07\u76cf\uff0c\u5c71\u8c37\u4e2d\u53d8\u6210\u6ee1\u5929\u661f\u6597\uff0c\u7aef\u7684\u662f\u522b\u5904\u7f55\u89c1\u7684\u5947\u89c2\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u6709\u7740\u767e\u6570\u5341\u76cf\u201c\u4f5b\u706f\u201d\u5411\u8349\u576a\u98d8\u6765\uff0c\u8981\u77e5\u78f7\u706b\u6709\u6bd2\uff0c\u7ed9\u5b83\u6cbe\u4e0a\uff0c\u867d\u65e0\u5927\u5bb3\uff0c \u4ea6\u662f\u9ebb\u70e6\uff0c\u6545\u6b64\u5728\u5ce8\u5d4b\u5c71\uff0c\u4e60\u4fd7\u76f8\u4f20\uff0c\u78b0\u5230\u201c\u4f5b\u706f\u201d\u51fa\u73b0\uff0c\u987b\u5f97\u8fdc\u8fdc\u907f\u6c57\uff0c\u53ea\u5475\u8fdc\u89c2\uff0c \u4e0d\u80fd\u8fd1\u8d4f\uff0c\u8bf4\u662f\u201c\u656c\u4f5b\u201d\uff0c\u5b9e\u662f\u81ea\u9632\u3002\u4f46\u7fa4\u96c4\u6b63\u5728\u805a\u4f1a\uff0c\u82e5\u662f\u907f\u4e43\uff0c\u54ea\u91cc\u518d\u627e\u8fd9\u6837\u4e00\u7247 \u5929\u7136\u7684\u8349\u576a\uff0c\u800c\u4e14\u4ea6\u5927\u715e\u98ce\u666f\uff01 \u3000\u3000\u674e\u9038\u6602\u9996\u5411\u5929\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5947\u666f\u867d\u7136\u96be\u9047\uff0c\u8fd8\u4ee5\u9001\u8d70\u4e3a\u5999\uff01\u201d\u628a\u624b\u4e00\u626c\u3002 \u5c06\u90a3\u4e00\u675f\u8305\u8349\u5c04\u51fa\uff0c\u8349\u679d\u5982\u7bad\uff0c\u7adf\u7136\u5e26\u7740\u98d2\u98d2\u7684\u98ce\u58f0\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u90a3\u767e\u6570\u5341\u76cf\u201c\u4f5b\u706f\u201d \u5316\u6210\u4e86\u65e0\u6570\u4e00\u7f15\u7f15\u78f7\u706b\uff0c\u7ec6\u82e5\u6e38\u4e1d\uff0c\u968f\u98ce\u98d8\u6563\u3002 \u3000\u3000\u767b\u65f6\u559d\u6765\u4e4b\u58f0\u56db\u8d77\uff0c\u4e0d\u4f46\u662f\u9f99\u4e09\u5148\u751f\u90a3\u4e00\u4f19\u4eba\uff0c\u90a3\u51e0\u4e2a\u89ca\u89ce\u76df\u4e3b\u4e4b\u4f4d\u7684\u4ea6\u90fd\u6697\u6697\u5fc3 \u670d\u3002\u8981\u77e5\u201c\u4f5b\u706f\u201d\u95ea\u70c1\u4e0d\u5b9a\uff0c\u96be\u4e8e\u53d6\u51c6\uff0c\u7528\u6697\u5668\u5c04\u4e2d\u201c\u4f5b\u706f\u201d\u5df2\u4e0d\u5bb9\u6613\uff0c\u7528\u8f7b\u67d4\u7684\u8349\u679d \u5c04\u51fa\u5341\u6570\u4e08\u5916\u66f4\u662f\u8270\u96be\uff0c\u540c\u65f6\u5c04\u4e2d\u8fd9\u4e48\u591a\u201c\u4f5b\u706f\u201d\uff0c\u90a3\u5c31\u66f4\u662f\u532a\u5937\u6240\u601d\u4e86\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u624b\u6458\u53f6\u98de\u82b1\u7684\u529f\u592b\uff01\u5f85\u8001\u5927\u4e5f\u6765\u52a9\u4f60\u3002 \u3000\u3000\u81c2\u4e4b\u529b\u3002\u201d\u5927\u8896\u4e00\u626c\uff0c\u547c\u547c\u98ce\u8d77\uff0c\u767b\u65f6\u628a\u6ee1\u7a7a\u6d41\u6563\u7684\u7f15\u7f15\u78f7\u706b\uff0c\u5439\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u8c37 \u795e\u7fc1\u4ee5\u901a\u81c2\u62f3\u3001\u91d1\u521a\u638c\u3001\u8e51\u4e91\u5251\u4e09\u822c\u7edd\u6280\uff0c\u5a01\u9707\u6c5f\u6e56\uff0c\u8fd9\u4e00\u624b\u98de\u8896\u626c\u98ce\u7684\u529f\u592b\uff0c\u5b9e\u5373\u6697 \u542b\u91d1\u521a\u638c\u529b\uff0c\u4e0a\u5b98\u5a49\u513f\u770b\u4e86\uff0c\u4ea6\u81ea\u5fc3\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u505a\u4e86\u5341\u5e74\u7684\u76df\u4e3b\uff01\u201d \u3000\u3000\u8c37\u795e\u7fc1\u54c8\u54c8\u5927\u7b11\uff0c\u5bf9\u7fa4\u8c6a\u8bf4\u9053\uff1a\u201c\u51ed\u674e\u8d24\u5f1f\u8fd9\u624b\u529f\u592b\uff0c\u6211\u63a8\u4e3e\u4ed6\u505a\u7ee7\u4efb\u7684\u76df\u4e3b\uff0c\u60f3 \u6765\u4e0d\u81f4\u4e8e\u7ed9\u8bf8\u4f4d\u8bf4\u6211\u8be2\u79c1\u4e86\u5427\uff1f\u201d\u5b5f\u79cb\u5143\u9996\u5148\u53eb\u9053\uff0c\u201c\u8c37\u76df\u4e3b\u6cd5\u773c\u65e0\u5dee\uff0c\u5bf9\u8fd9\u4f4d\u5c11\u5e74\u82f1 \u96c4\uff0c\u8001\u5927\u4ea6\u662f\u5fc3\u670d\u53e3\u670d\uff01\u201d\u767b\u65f6\u6b22\u58f0\u96f7\u52a8\uff0c\u8fd9\u56de\u5df1\u4e0d\u6b62\u662f\u9f99\u4e09\u65e9\u7ea6\u5b9a\u7684\u90a3\u4e00\u4f19\u4eba\uff0c\u5341\u4e4b \u516b\u4e5d\uff0c\u90fd\u8868\u793a\u4e86\u613f\u63a8\u6234\u674e\u9038\u4f5c\u4e3a\u76df\u4e3b\u3002 \u3000\u3000\u6599\u4e0d\u5230\u638c\u58f0\u672a\u606f\uff0c\u5374\u6709\u4e00\u4e2a\u58ee\u6c49\u8df3\u4e86\u8d77\u6765\uff0c\u58f0\u82e5\u6d2a\u949f\uff0c\u9707\u52a8\u5168\u573a\uff0c\u4ed6\u8bf4\u7684\u662f\uff1a\u201c\u674e \u5144\u5f1f\u8fd9\u624b\u6697\u5668\u529f\u592b\uff0c\u679c\u7136\u79f0\u5f97\u4e0a\u662f\u9707\u4e16\u9a87\u4fd7\u7684\u7edd\u6280\uff0c\u4f46\u8bf7\u6055\u5c0f\u5f1f\u5192\u6627\uff0c\u6211\u8fd8\u60f3\u9886\u6559\u4e00\u4e0b \u4ed6\u62f3\u811a\u7684\u529f\u592b\u3002\u201d\u8bf4\u8bdd\u7684\u662f\u5c71\u4e1c\u996e\u9a6c\u5ddd\u7684\u9ece\u4e3b\u96c4\u5de8\u9f0e\uff0c\u5f53\u771f\u662f\u540d\u5982\u5176\u4eba\uff0c\u94c1\u5854\u822c\u7684\u8eab\u8eaf\uff0c \u5728\u8349\u576a\u4e2d\u4e00\u7ad9\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u786e\u4f3c\u5de8\u65e0\u9738\u4e00\u822c\u3002\u6709\u4e00\u4e2a\u9634\u607b\u607b\u7684\u58f0\u97f3\u5728\u4eba\u4e1b\u4e2d\u7b11\u9053\uff1a\u201c\u5bf9 \u5440\uff0c\u8981\u505a\u76df\u4e3b\uff0c\u603b\u4e0d\u80fd\u5531\u72ec\u811a\u620f\u5440\uff0c\u6709\u4eba\u51d1\u51d1\u70ed\u95f9\u4e5f\u597d\u3002\u201d\u542c\u8fd9\u8bdd\u7684\u53e3\u6c14\uff0c\u4ed6\u5bf9\u96c4\u5de8\u9f0e \u56fa\u7136\u8f7b\u89c6\uff0c\u5bf9\u674e\u9038\u4ea6\u4e0d\u5fc3\u670d\uff0c\u751a\u81f3\u5bf9\u8c37\u795e\u7fc1\u7684\u5b89\u6392\u4ea6\u6709\u5fae\u8f9e\u3002\u8c37\u4f38\u7fc1\u7741\u773c\u4e00\u77a7\uff0c\u5374\u627e\u4e0d \u5230\u53d1\u8bdd\u4e4b\u4eba\u3002\u6014\u4e86\u4e00\u6014\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u8bf4\u5f97\u5bf9\uff0c\u82f1\u96c4\u4f1a\u4e0a\uff0c\u539f\u5e94\u5f7c\u6b64\u5207\u78cb\u3002\u674e\u8d24 \u4f84\uff0c\u4f60\u5c31\u548c\u96c4\u5be8\u4e3b\u5370\u8bc1\u4e00\u4e0b\u6b66\u529f\uff0c\u96c4\u5be8\u4e3b\u7ef0\u53f7\u8d5b\u5143\u9738\uff0c\u5916\u5bb6\u529f\u5927\u767b\u5cf0\u9020\u6781\uff0c\u4f60\u4eec\u597d\u597d\u6bd4 \u5212\u3002\u201d\u8c37\u795e\u7fc1\u5bf9\u96c4\u5de8\u9f0e\u53ef\u4e0d\u6562\u8f7b\u89c6\uff0c\u6545\u610f\u7528\u8bf4\u8bdd\u70b9\u9192\u674e\u9038\uff0c\u53eb\u4ed6\u5c0f\u5fc3\u5728\u610f\u3002\u8bf4\u8bdd\u4e4b\u95f4\uff0c \u53ea\u89c1\u96c4\u5de8\u9f0e\u7eb5\u8eab\u4e00\u8dc3\uff0c\u8df3\u5230\u4e86\u4e00\u5757\u5927\u77f3\u5934\u4e0a\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u65b9\u81ea\u5947\u602a\uff1a\u201c\u8fd9\u5927\u4e2a\u5b50\u65e2\u8bf4\u8981\u6bd4\u62f3\u811a\u529f\u592b\uff0c\u5728\u5927\u8349\u576a\u4e0a\u4e0d\u6b63\u597d\u6bd4\u8bd5\u4e48\uff1f\u201d \u53ea\u542c\u5f97\u96c4\u5de8\u9f0e\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u4e0a\u8fd9\u77f3\u53f0\u3002\u201d\u674e\u9038\u5fae\u7b11\u9053\uff1a\u201c\u8c28\u4f9d\u5c0a\u547d\u3002\u201d\u5e76\u4e0d\u89c1\u4ed6\u7eb5 \u8dc3\u4f5c\u52bf\uff0c\u53ea\u662f\u811a\u6b65\u4e00\u62ac\uff0c\u8eab\u5b50\u4fbf\u8f7b\u98d8\u98d8\u7684\u4e0a\u4e86\u77f3\u53f0\u3002\u8fd9\u6b63\u662f\u201c\u51cc\u7a7a\u6b65\u201d\u7684\u4e0a\u4e58\u8f7b\u529f\uff0c\u4e0a \u5b98\u5a49\u513f\u4e5f\u81ea\u6127\u4e0d\u5982\u3002\u7fa4\u8c6a\u4e2d\u4e0d\u4e4f\u8bc6\u8d27\u4e4b\u4eba\uff0c\u66f4\u662f\u5927\u58f0\u559d\u91c7\u3002 \u3000\u3000\u96c4\u5de8\u9f0e\u5374\u8db3\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u674e\u9038\u8eab\u6750\u4e5f\u4e0d\u7b97\u77ee\u7ec6\uff0c\u4f46\u5728\u77f3\u4e0a\u4e00\u7ad9\uff0c\u5374\u8db3\u8db3\u6bd4\u4ed6\u77ee\u4e86 \u4e00\u4e2a\u5934\uff0c\u96c4\u5de8\u9f0e\u4fef\u5934\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u5728\u8fd9\u5757\u77f3\u4e0a\u6bd4\u62f3\uff0c\u96be\u82e5\u88ab\u6253\u4e0b\u77f3\u53f0\uff0c\u4fbf\u662f\u8f93\u4e86\uff0c\u4f60 \u4f9d\u5f97\u4e48\uff1f\u201d\u7fa4\u8c6a\u6697\u6697\u597d\u7b11\u3002\u674e\u9038\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u7b28\u4eba\u4e5f\u6709\u7b28\u4e3b\u610f\uff0c\u563f\uff0c\u563f\uff0c\u6211\u6b63\u597d\u8d81\u6b64 \u5c06\u4ed6\u6536\u670d\u3002\u201d\u8981\u77e5\u9053\u8fd9\u5757\u77f3\u53f0\u4e0d\u8fc7\u6570\u5c3a\u89c1\u65b9\uff0c\u82e5\u7167\u96c4\u5de8\u9f0e\u6240\u8bf4\u7684\u6253\u6cd5\uff0c\u4efb\u662f\u591a\u597d\u8f7b\u529f\uff0c \u4e5f\u96be\u65bd\u5c55\uff0c\u62f3\u62f3\u5230\u8089\uff0c\u5c82\u975e\u529b\u6c14\u5927\u7684\u5360\u5c3d\u4fbf\u5b9c\uff1f \u3000\u3000\u96c4\u5de8\u9f0e\u89c1\u674e\u9038\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6211\u8fd9\u91cc\u65e9\u5907\u4e0b\u4e86\u9a73\u7b4b\u63a5\u9aa8\u7684\u5999\u836f\uff0c\u4e0d \u7528\u5bb3\u6015\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u674e\u9038\u82e5\u662f\u4e2d\u62f3\uff0c\u5b9a\u5c06\u4f24\u7b4b\u65ad\u9aa8\u65e0\u7591\u3002\u674e\u9038\u6447\u4e86\u6447\u5934\uff0c\u5fae\u5fae\u7b11\u9053\uff1a \u201c\u8fd9\u6837\u86ee\u6253\uff0c\u6709\u4f55\u610f\u601d\uff1f\u201d\u96c4\u5de8\u9f0e\u53eb\u9053\uff1a\u201c\u8fd9\u624d\u662f\u8f83\u91cf\u771f\u5b9e\u7684\u529f\u592b\uff0c\u5982\u4f55\u7b97\u662f\u86ee\u6253\uff1f\u201d \u674e\u9038\u9053\uff1a\u201c\u8f83\u91cf\u771f\u5b9e\u7684\u529f\u592b\uff0c\u4e5f\u4e0d\u7528\u4f60\u4e00\u62f3\u6211\u4e00\u811a\u7684\u86ee\u6253\u5440\u3002\u201d\u9f99\u4e09\u8fd9\u4e00\u4f19\u4eba\u8f70\u7136\u79f0\u662f\uff0c \u96c4\u5de8\u9f0e\u6da8\u7ea2\u4e86\u8138\uff0c\u9053\uff1a\u201c\u4f9d\u4f60\u5982\u4f55\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u8ba9\u4f60\u5148\u6253\u4e09\u62f3\uff0c\u6211\u4e0d\u8fd8\u624b\uff0c\u4f60\u82e5\u80fd \u628a\u6211\u6253\u4e0b\u77f3\u53f0\uff0c\u6211\u4fbf\u8ba4\u8f93\uff0c\u5df1\u4e0d\u66f4\u4e3a\u5e72\u8106\uff1f\u201d\u96c4\u5de8\u9f0e\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u60f3\u4e0e\u6211\u73a9\u501f\u4e09\u8fd8 \u4e94\u7684\u628a\u620f\uff0c\u597d\uff0c\u6211\u4e0d\u613f\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u8ba9\u4f60\u5148\u6253\u5427\u3002\u201d\u6240\u8c13\u201c\u501f\u4e09\u8fd8\u4e94\u201d\u4fbf\u662f\u5148\u8ba9\u5bf9\u65b9\u6253 \u4e09\u62f3\uff0c\u7136\u540e\u8fd8\u6253\u4e94\u62f3\uff0c\u96c4\u5de8\u9f0e\u81ea\u6043\u94dc\u76ae\u94e3\u9aa8\uff0c\u4e50\u610f\u8ba9\u5bf9\u65b9\u5148\u6253\uff0c\u54ea\u77e5\u674e\u9038\u53c8\u5fae\u7b11\u9053\uff1a \u201c\u6211\u521d\u6765\u662f\u5ba2\uff0c\u5ba2\u4e0d\u6f5c\u4e3b\uff0c\u5f53\u7136\u8ba9\u4f60\u5148\u6253\uff0c\u800c\u4e14\u4e5f\u4e0d\u5fc5\u4f60\u8fd8\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u53ea\u4ee5\u4e3a\u674e\u9038\u662f\u60f3\u9080\u4ed6\u4e0b\u8349\u576a\u6bd4\u8bd5\uff0c\u54ea\u77e5\u4ed6\u5374\u63d0\u8bae\u5982\u6b64\u6253\u6cd5\uff0c\u90ca\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c \u96c4\u5de8\u9f0e\u4e5f\u6014\u6014\u5728\u77f3\u4e0a\uff0c\u4e0d\u597d\u610f\u601d\u51fa\u62f3\u3002 \u3000\u3000\u674e\u9038\u7b11\u9053\uff0c\u201c\u653e\u5fc3\u6253\u5427\uff0c\u53cd\u6b63\u4f60\u5df2\u51c6\u5907\u4e86\u8dcc\u6253\u5999\u836f\uff0c\u6015\u4e9b\u4ec0\u4e48\uff1f\u201d\u8fd9\u8bf4\u8bdd\u7684\u610f\u601d\uff0c \u53ef\u4ee5\u89e3\u91ca\u4f5c\u96c4\u5de8\u9f0e\u6015\u674e\u9038\u53d7\u4f24\uff0c\u4e5f\u53ef\u4ee5\u89e3\u91ca\u4f5c\u96c4\u5de8\u9f0e\u6015\u81ea\u5df1\u53d7\u4f24\uff0c\u5f53\u7136\u4ee5\u540e\u8005\u7684\u610f\u601d\u66f4 \u4e3a\u660e\u663e\u3002\u96c4\u5de8\u9f0e\u52c3\u7136\u5927\u6012\uff0c\u9053\uff1a\u201c\u6211\u82e5\u4e09\u62f3\u4e0d\u80fd\u5c06\u4f60\u6253\u4e0b\u77f3\u53f0\uff0c\u6211\u7ed9\u4f60\u78d5\u5934\uff01\u201d\u201c\u78d5\u5934\u201d \u4e24\u4e2a\u5b57\u521a\u521a\u8bf4\u51fa\uff0c\u7acb\u5373\u63d0\u8d77\u94b5\u5927\u7684\u62f3\u5934\uff0c\u201c\u7830\u201d\u7684\u4e00\u62f3\u6253\u51fa\u3002 \u3000\u3000\u674e\u9038\u80a9\u5934\u5fae\u95ea\uff0c\u63a5\u4e86\u4ed6\u7684\u4e00\u62f3\uff0c\u96c4\u5de8\u9f0e\u4f46\u89c9\u4ed6\u7684\u80a9\u5934\uff0c\u597d\u50cf\u6d82\u4e86\u6cb9\u8102\u4e00\u6837\uff0c\u6ed1\u4e0d\u7559 \u624b\uff0c\u62f3\u5934\u5728\u4ed6\u7684\u80a9\u4e0a\u6ed1\u8fc7\u4e00\u65c1\uff0c\u6beb\u4e0d\u53d7\u529b\uff0c\u539f\u6765\u662f\u674e\u9038\u65bd\u5c55\u4e0a\u4e58\u7684\u5378\u52b2\u529f\u592b\uff0c\u5c06\u4ed6\u7684\u731b \u529b\u7adf\u662f\u8f7b\u63cf\u6de1\u5199\u822c\u7684\u5316\u89e3\u5f00\u4e86\u3002\u96c4\u5de8\u9f0e\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u95ea\u5f97\u597d\uff01\u201d\u770b\u6765\u4ecd\u672a\u5fc3\u773c\uff0c \u674e\u9038\u7b11\u9053\uff1a\u201c\u4f60\u518d\u6253\u5427\uff0c\u6211\u8ba9\u4f60\u518d\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6253\u4e00\u62f3\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u96c4\u5de8\u9f0e\u51b7\u4e0d\u9632\u7684\u4e00 \u62f3\u6253\u51fa\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u4e2d\u674e\u9038\u80f8\u819b\uff01 \u3000\u3000\u4f46\u542c\u5f97\u4e00\u58f0\u5927\u53eb\uff0c\u60ca\u5fc3\u52a8\u9b44\uff0c\u4e0d\u5c11\u4eba\u4ee5\u4e3a\u662f\u674e\u9038\u53d7\u4e86\u91cd\u4f24\u3002 \u3000\u3000\u5b9a\u775b\u770b\u65f6\uff0c\u5374\u89c1\u96c4\u5de8\u9f0e\u6367\u7740\u62f3\u5934\uff0c\u5728\u77f3\u53f0\u7684\u8fb9\u7f18\u5446\u5446\u53d1\u695e\u3002 \u3000\u3000\u539f\u6765\u674e\u9038\u8bd5\u4e86\u4ed6\u7684\u4e00\u62f3\uff0c\u81ea\u6043\u8fd8\u53ef\u4ee5\u62b5\u6321\u5f97\u4f4f\uff0c\u770b\u51c6\u4ed6\u7b2c\u4e8c\u62f3\u7684\u6765\u52bf\uff0c\u5c06\u5168\u8eab\u6c14\u529b \u90fd\u8fd0\u5230\u80f8\u53e3\uff0c\u63a5\u4ed6\u4e00\u62f3\uff0c\u8fd9\u4e00\u6765\u7b49\u4e8e\u53cc\u65b9\u5404\u4ee5\u529f\u529b\u786c\u62fc\uff0c\u6253\u51fb\u4e4b\u529b\u6108\u91cd\uff0c\u53cd\u51fb\u4e4b\u529b\u4ea6\u6108 \u5927\uff0c\u96c4\u5de8\u9f0e\u4e00\u62f3\u6253\u4e0b\uff0c\u5982\u89e6\u94a2\u677f\uff0c\u600e\u4e0d\u75db\u5f97\u4ed6\u53eb\u51fa\u58f0\u6765\uff1f \u3000\u3000\u674e\u9038\u786c\u63a5\u4e86\u8fd9\u4e00\u62f3\uff0c\u80f8\u53e3\u4e5f\u89c9\u9690\u9690\u4f5c\u75db\uff0c\u5fc3\u4e2d\u4ea6\u81ea\u9a87\u7136\uff0c\u60f3\u9053\uff1a\u201c\u8c37\u795e\u7fc1\u8bf4\u4ed6\u7684\u5916 \u5bb6\u529f\u592b\u767b\u5cf0\u9020\u6781\uff0c\u679c\u7136\u4e0d\u9519\uff0c\u5e78\u6211\u6ca1\u6709\u53d7\u5230\u5185\u4f24\u3002\u201d\u5f53\u4e0b\u8fd0\u6c14\u4e00\u8f6c\uff0c\u653e\u677e\u808c\u8089\uff0c\u5fae\u7b11\u8bf4 \u9053\uff1a\u201c\u8d5b\u5143\u9738\u540d\u4e0d\u865a\u4f20\uff0c\u795e\u529b\u60ca\u4eba\uff0c\u5c0f\u5f1f\u4f69\u670d\uff01\u8fd8\u6709\u4e00\u62f3\uff0c\u6253\u662f\u4e0d\u6253\uff1f\u201d\u674e\u9038\u89c1\u4ed6\u8c6a\u723d \u61a8\u76f4\uff0c\u9887\u4e3a\u6b22\u559c\uff0c\u6545\u6b64\u60f3\u7ed9\u4e2a\u673a\u4f1a\uff0c\u8ba9\u4ed6\u4e0b\u53f0\uff0c\u8fd9\u8bf4\u8bdd\u5b9e\u662f\u4e00\u756a\u597d\u610f\u3002\u4e0d\u6599\u96c4\u5de8\u9f0e\u6b63\u5728 \u53c8\u7f9e\u4e49\u6012\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u5374\u5f53\u4f5c\u662f\u674e\u9038\u8c03\u4f83\u4ed6\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u6253\uff1f\u201d\u5de6\u62f3\u75be\u53d1\uff0c\u7528 \u4e86\u5168\u8eab\u6c14\u529b\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6253\u4e2d\u4e86\u674e\u9038\u7684\u5c0f\u8179\u3002 \u3000\u3000\u4f46\u89c9\u4e2d\u62f3\u4e4b\u5904\uff0c\u5176\u8f6f\u5982\u9526\uff0c\u96c4\u5de8\u9f0e\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u62f3\u5934\u4e00\u633a\uff0c\u7adf\u4f3c\u9677\u5165\u68c9\u82b1\u5806\u91cc\uff0c \u52b2\u529b\u5168\u6d88\uff0c\u975e\u4fbf\u6b64\u4e5f\uff0c\u674e\u9038\u7684\u5c0f\u8179\u8fd8\u9690\u9690\u4f3c\u6709\u4e00\u80a1\u5438\u529b\uff0c\u5c06\u4ed6\u7684\u62f3\u5934\u5438\u4f5c\uff0c\u8fde\u62d4\u4e5f\u62d4\u4e0d \u51fa\u6765\u3002\u674e\u9038\u7b11\u9053\uff1a\u201c\u96c4\u5144\u6055\u7f6a\uff01\u201d\u809a\u76ae\u4e00\u633a\uff0c\u767b\u65f6\u628a\u96c4\u53f5\u9f0e\u629b\u4e0b\u77f3\u53f0\uff01 \u3000\u3000\u7fa4\u8c6a\u559d\u91c7\u58f0\u4e2d\uff0c\u53ea\u89c1\u96c4\u5de8\u9f0e\u4e00\u4e2a\u7ffb\u8eab\uff0c\u7acb\u523b\u4fbf\u5411\u674e\u9038\u78d5\u5934\uff0c\u53eb\u9053\uff1a\u201c\u4ffa\u96c4\u5de8\u9f0e\u8fd9\u756a \u670d\u4e86\uff01\u201d\u674e\u9038\u6025\u5fd9\u8dc3\u4e0b\u77f3\u53f0\uff0c\u5c06\u4ed6\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u96c4\u5144\u4e00\u65f6\u4e4b\u5931\uff0c\u975e\u6218\u4e4b\u7f6a\uff0c\u5982\u6b64\u591a\u793c\uff0c \u5c0f\u5f1f\u4e07\u4e0d\u6562\u5f53\uff01\u201d\u96c4\u5de8\u9f0e\u9053\uff1a\u201c\u4ffa\u6709\u8a00\u5728\u5148\uff0c\u4e09\u62f3\u6253\u4f60\u4e0d\u5012\uff0c\u4ffa\u4fbf\u7ed9\u4f60\u78d5\u5934\u3002\u5982\u4eca\u975e\u4f46 \u6253\u4f60\u4e0d\u5012\uff0c\u53cd\u800c\u7ed9\u4f60\u6253\u5012\uff0c\u7406\u8be5\u78d5\u4e24\u4e2a\u54cd\u5934\u624d\u662f\u3002\u201d\u8bf4\u5f97\u7fa4\u96c4\u54c8\u54c8\u5927\u7b11\uff0c\u674e\u9038\u4e5f\u7b11\u9053\uff1a \u201c\u4f60\u82e5\u7167\u6211\u808b\u9aa8\u518d\u6253\u4e00\u62f3\uff0c\u6211\u7edd\u4e0d\u80fd\u786c\u63a5\u3002\u201d \u3000\u3000\u54d7\u7b11\u4e4b\u58f0\u7a0d\u6b62\uff0c\u53ea\u542c\u5f97\u521a\u624d\u90a3\u4e2a\u9634\u607b\u607b\u7684\u58f0\u97f3\u53c8\u7b11\u9053\uff1a\u201c\u6253\u4eba\u7684\u53d8\u4e86\u6371\u8ba2\u7684\uff0c\u597d\u62f3 \u6cd5\u3001\u597d\u5185\u529f\uff01\u8ba9\u4ffa\u4e5f\u6765\u51d1\u51d1\u70ed\u95f9\uff0c\u7ed9\u65b0\u76df\u4e3b\u6367\u6367\u573a\u3002\u201d\u4eba\u4e1b\u4e2d\u8d70\u51fa\u4e00\u4e2a\u4e2d\u5e74\u6587\u4e0a\uff0c\u624b\u6301 \u6298\u6247\uff0c\u4e00\u6447\u4e09\u6446\uff0c\u9178\u6e9c\u6e9c\u7684\u751a\u662f\u6ed1\u7a3d\uff0c\u8c37\u795e\u7fc1\u4e00\u89c1\uff0c\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u600e\u7684\u60f3\u4e0d\u8d77\u662f\u4ed6\uff01\u201d \u6025\u5fd9\u5411\u674e\u9038\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u2014\u2014\u201d\u90a3\u4e2d\u5e74\u6587\u58eb\u5374\u622a\u7740\u8c37\u795e\u7fc1\u7684\u8bdd\u5934\u81ea\u884c\u4ecb\u7ecd\u9053\uff0c\u201c\u5c0f\u53ef \u8d31\u540d\uff0c\u7109\u8db3\u6302\u9f7f\u3002\u9752\u5dde\u4e1c\u65b9\u767d\u7279\u6765\u9886\u6559\u65b0\u76df\u4e3b\u7684\u9ad8\u62db\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u4e1c\u65b9\u767d\u7ef0\u53f7\u960e\u738b\u6247\uff0c\u94c1\u6247\u6253\u7a74\u7684\u529f\u5929\u592b\u76d6\u4e16\u65e0\u53cc\uff0c\u800c\u4e14\u8be1\u8ba1\u591a\u7aef\uff0c\u6c5f\u6e56\u4e0a \u4eba\u89c1\u4eba\u6015\uff0c\u81ea\u8c37\u795e\u7fc1\u5728\u5341\u5e74\u4ee5\u524d\u4ee5\u901a\u81c2\u62f3\u3001\u8e51\u4e91\u5251\u3001\u91d1\u521a\u638c\u4e09\u7edd\u6280\u6253\u8d25\u7fa4\u96c4\uff0c\u593a\u4e86\u76df\u4e3b \u4e4b\u4f4d\u540e\uff0c\u4ed6\u5c31\u9500\u58f0\u533f\u8ff9\uff0c\u6709\u4eba\u8bf4\u662f\u4ed6\u81ea\u77e5\u654c\u4e0d\u8fc7\u8c37\u795e\u7fc1\uff0c\u4f46\u53c8\u60f3\u593a\u76df\u4e3b\u4e4b\u4f4d\uff0c\u6545\u6b64\u89c5\u5730 \u9690\u5c45\uff0c\u51c6\u5907\u4ee5\u5341\u5e74\u7684\u529f\u592b\uff0c\u82e6\u7ec3\u7edd\u6280\uff0c\u7136\u540e\u518d\u51fa\u6765\u4e89\u9738\u7684\u3002 \u3000\u3000\u674e\u9038\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u4e1c\u65b9\u767d\u7684\u6765\u5386\uff0c\u89c1\u4ed6\u9634\u9633\u602a\u6c14\u7684\u6837\u4e86\uff0c\u89c9\u5f97\u6709\u70b9\u8ba8\u538c\uff0c\u4fbf\u9053\uff0c \u201c\u65b0\u76df\u4e3b\u7684\u79f0\u547c\u7edd\u4e0d\u6562\u5f53\uff0c\u5c0f\u5f1f\u6b64\u6765\uff0c\u4e0d\u8fc7\u662f\u60f3\u5411\u5929\u4e0b\u82f1\u96c4\u8ba8\u6559\u7f62\u4e86\u3002\u201d\u4e1c\u65b9\u767d\u7750\u7740\u4e09 \u89d2\u773c\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u592a\u8fc7\u5ba2\u6c14\uff0c\u4eca\u756a\u4e4b\u4f1a\uff0c\u76df\u4e3b\u4e4b\u4f4d\uff0c\u975e\u4f60\u83ab\u5c5e\u3002\u6211\u7ed9\u4f60\u6367\u6367\u573a\uff0c\u8fd8\u8bf7\u9ad8 \u62ac\u8d35\u624b\uff01\u201d\u674e\u9038\u89c1\u4ed6\u5f6c\u5f6c\u6709\u793c\uff0c\u867d\u662f\u8ba8\u538c\uff0c\u4ea6\u4e0d\u6562\u50b2\u6162\uff0c\u5f53\u4e0b\u62b1\u62f3\u7acb\u638c\uff0c\u4f5c\u4e86\u4e2a\u5411\u5bf9\u65b9 \u793c\u8ba9\u7684\u201c\u8d77\u624b\u5f0f\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u627f\u8499\u6307\u6559\uff0c\u4fbf\u8bf7\u8d50\u62db\u3002\u201d\u8c37\u795e\u7fc1\u89c1\u674e\u9038\u5e76\u4e0d\u62d4\u5251\uff0c\u5403\u4e86\u4e00 \u60ca\uff0c\u6b32\u8981\u63d0\u9192\uff0c\u53c8\u6050\u592a\u7740\u75d5\u8ff9\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\u3002 \u3000\u3000\u4e1c\u65b9\u767d\u9053\uff1a\u201c\u6709\u50ed\u201d\uff0c\u94c1\u6247\u4e00\u6307\uff0c\u75be\u5982\u661f\u706b\u822c\u7684\u7acb\u5954\u674e\u9038\u201c\u5c06\u95e8\u7a74\u201d\u70b9\u6765\uff0c\u674e\u9038\u5fae \u5fae\u4e00\u51db\uff0c\u201c\u6765\u5f97\u597d\u5feb\uff01\u201d\u6025\u5fd9\u4f7f\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u582a\u582a\u907f\u8fc7\uff0c\u4e1c\u65b9\u767d \u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u7d27\u8d34\u7740\u674e\u9038\u7684\u8eab\u5f62\uff0c\u4e00\u4e2a\u76d8\u65cb\uff0c\u94c1\u6247\u5b50\u75be\u70b9\u674e\u9038\u5c0f\u817f\u7684\u201c\u73af\u8df3\u7a74\u201d\uff0c \u674e\u9038\u65e9\u6709\u9632\u5907\uff0c\u970d\u5730\u53cd\u624b\u4e00\u6293\uff0c\u4ee5\u201c\u5c0f\u64d2\u62ff\u624b\u201d\u7684\u201c\u5c01\u5173\u201d\u624b\u6cd5\uff0c\u4e09\u6307\u75be\u6263\u4e1c\u65b9\u767d\u624b\u8155 \u7684\u8109\u95e8\uff0c\u8fd9\u4e00\u62db\u8fde\u95ea\u5e26\u653b\uff0c\u786e\u662f\u51cc\u5389\u65e0\u6bd4\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u674e\u9038\u7684\u4e09\u6307\u5373\u5c06\u6263\u4e0b\u4e4b\u9645\uff0c\u4e1c\u65b9\u767d\u6298\u6247\u4e00\u5f00\uff0c\u674e\u9038\u4f46\u89c9\u773c\u524d\u95ea\u95ea\u53d1\u5149\uff0c\u539f \u6765\u4ed6\u8fd9\u628a\u6247\u5b50\u6709\u70b9\u7279\u522b\uff0c\u6247\u9aa8\u6562\u60c5\u90fd\u662f\u7cbe\u94a2\u6253\u7684\uff0c\u5f88\u50cf\u78e8\u5229\u7684\u5200\u7247\uff0c\u674e\u9038\u82e5\u7136\u6263\u4e0b\uff0c\u624b \u6307\u5fc5\u5b9a\u8981\u88ab\u4ed6\u524a\u65ad\u65e0\u7591\uff01 \u3000\u3000\u597d\u4e2a\u674e\u9038\uff0c\u53d8\u62db\u8ba5\u8b66\u4e4b\u6781\uff0c\u89c1\u4ed6\u6298\u6247\u4e00\u5f00\uff0c\u7acb\u523b\u7f29\u6307\u5316\u638c\u3002 \u3000\u3000\u8eab\u79fb\u6b65\u6362\uff0c\u4e00\u8df3\u8df3\u8fc7\u65c1\u8fb9\uff0c\u7acb\u523b\u7528\u201c\u65a9\u9f99\u624b\u201d\uff0c\u6a2a\u638c\u5982\u5200\uff0c\u65a9\u4ed6\u81c2\u5f2f\uff0c\u5de6\u5b50\u4e00\u62ac\uff0c \u9a88\u6307\u5982\u621f\uff0c\u53c8\u70b9\u4ed6\u53cc\u76ee\uff0c\u4e1c\u65b9\u767d\u4e00\u58f0\u957f\u5578\uff0c\u6247\u5b50\u8fce\u98ce\u4e00\u62e8\uff0c\u674e\u9038\u773c\u795e\u7ed9\u4ed6\u4e00\u5f15\uff0c\u4e24\u62db\u90fd \u8d70\u4e86\u4e2a\u7a7a\u3002\u4e1c\u65b9\u767d\u51fa\u624b\u4ea6\u662f\u5feb\u6781\uff0c\u6247\u5b50\u500f\u5f20\u5373\u5408\uff0c\u6307\u4e1c\u6253\u4e24\uff0c\u6307\u5357\u6253\u5317\uff0c\u503e\u523b\u4e4b\u95f4\uff0c\u8fde \u957f\u674e\u9038\u5341\u4e09\u5904\u547d\u95e8\u8981\u7a74\u3002 \u3000\u3000\u674e\u9038\u8fd9\u624d\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u770b\u4ed6\u4e0d\u51fa\uff0c\u679c\u7136\u662f\u6709\u70b9\u529f\u592b\uff01\u201d\u53ea\u5f97\u6296\u64de\u7cbe\u795e\uff0c\u638c\u5288 \u6307\u6233\uff0c\u5e26\u653b\u5e26\u5b88\uff0c\u4e00\u53e3\u6c14\u6c14\u63a5\u4e86\u4e1c\u65b9\u767d\u4e8c\u4e09\u5341\u62db\uff0c\u8fd9\u624d\u6e10\u6e10\u626d\u8f6c\u52a3\u52bf\uff0c\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u53ef\u662f\u674e\u9038\u7ec8\u662f\u5403\u4e86\u6ca1\u6709\u5175\u5668\u7684\u4e8f\uff0c\u4e1c\u65b9\u767d\u90a3\u628a\u6247\u5b50\uff0c\u62db\u6570\u53e4\u602a\u5f02\u5e38\uff0c\u5408\u8d77\u7c73\u65f6\uff0c\u5f53 \u4f5c\u5224\u5b98\u7b14\u7528\uff0c\u5f20\u5f00\u4e4b\u540e\uff0c\u53c8\u53ef\u5f53\u4f5c\u4e94\u884c\u5251\u4f7f\uff0c\u865a\u865a\u5b9e\u5b9e\uff0c\u53d8\u5316\u83ab\u6d4b\uff0c\u674e\u9038\u53ea\u597d\u52a0\u5f3a\u638c\u529b\uff0c \u4ee5\u6700\u521a\u731b\u7684\u4f0f\u9b54\u638c\u8fde\u73af\u4e03\u5341\u4e8c\u5f0f\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u8feb\u5f97\u4e1c\u65b9\u5411\u4e0d\u6562\u6b3a\u8eab\u76f4\u8fdb\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u8868\u9762\u535c\u867d\u7136\u662f\u674e\u9038\u5360\u4e86\u653b\u52bf\uff0c\u4f46\u6c14\u529b\u6d88\u8017\u8fc7\u751a\uff0c\u5904\u5904\u987e\u5fcc\uff0c\u5371\u673a\u9690\u4f0f\uff0c \u800c\u4e1c\u65b9\u767d\u5374\u597d\u6574\u4ee5\u6687\uff0c\u65f6\u4e0d\u65f6\u7684\u89c5\u9699\u8fdb\u653b\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u8d77\u521d\u662f\u6697\u6697\u6447\u5934\uff0c\u4f46\u770b\u4e86\u4e00\u4f1a\uff0c\u5374\u653e\u4e86\u5fc3\u3002\u53ea\u82e6\u4e86\u4e0a\u5b98\u5a49\u51e0\uff0c\u6697\u6697\u4e3a\u674e\u9038\u634f \u4e00\u628a\u6c57\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u548c\u9f99\u4e09\u8fd9\u4f19\u4eba\u90fd\u770b\u4e2a\u51fa\u7c73\uff0c\u674e\u9038\u5374\u5df2\u4f3c\u6709\u6240\u89c9\uff0c\u6697\u6697\u5947\u602a\uff1a\u6709\u597d\u51e0\u6b21\u4e1c\u65b9 \u767d\u90fd\u4f3c\u4e4e\u53ea\u662f\u70b9\u5230\u5373\u6b62\uff0c\u5e76\u672a\u4f7f\u51fa\u6740\u624b\u8fa3\u62db\u3002\u867d\u7136\u4ed6\u82e5\u4f7f\u51fa\u6740\u624b\uff0c\u5fc5\u987b\u6b3a\u8eab\u8fdb\u62db\uff0c\u90a3\u5c31 \u53ef\u80fd\u88ab\u81ea\u5df1\u638c\u529b\u6240\u4f24\uff0c\u4f46\u6743\u8861\u5229\u5bb3\uff0c\u4ee5\u4ed6\u7684\u529f\u529b\uff0c\u5373\u7b97\u88ab\u81ea\u5df1\u6253\u4e86\u4e00\u638c\uff0c\u4ea6\u65e0\u5927\u788d\uff0c\u800c \u81ea\u5df1\u82e5\u88ab\u4ed6\u70b9\u4e2d\u8981\uff0c\u5219\u975e\u5f53\u573a\u683d\u5012\u4e0d\u53ef\uff0c\u6240\u4ee5\u7167\u8fd9\u60c5\u5f62\u770b\u6765\uff0c\u8fd8\u662f\u4ed6\u6545\u610f\u7559\u60c5\u3002\u4e0d\u8fc7\u8fd9 \u4e24\u4eba\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u529f\u592b\uff0c\u8d77\u843d\u8fdb\u9000\uff0c\u98d8\u5ffd\u5982\u98ce\uff0c\u4e00\u62db\u4e00\u5f0f\u4ff1\u662f\u60ca\u9669\u975e\u5e38\uff0c\u7fa4\u96c4\u770b\u5f85\u76ee\u7729 \u795e\u6447\uff0c\u60ca\u5fc3\u52a8\u9b44\uff0c\u9664\u4e86\u8c37\u795e\u7fc1\u7b49\u6709\u9650\u51e0\u4eba\uff0c\u5176\u4ed6\u7684\u54ea\u770b\u5f97\u51fa\u4e2a\u4e2d\u5965\u5999\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u674e\u9038\u4e00\u62db\u201c\u795e\u9f99\u6446\u5c3e\u201d\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u4e1c\u65b9\u767d\u8d81\u52bf\u5411\u4e0b\u6251\u8eab\uff0c\u968f\u5373\u4e00\u4e2a\u76d8\u65cb\uff0c \u94c1\u6247\u6a2a\u6572\uff0c\u649e\u51fb\u674e\u9038\u5c0f\u817f\u7684\u201c\u73af\u8df3\u7a74\u201d\u548c\u201c\u795e\u5c01\u7a74\u201d\uff0c\u674e\u9038\u8981\u5316\u89e3\u8fd9\u62db\uff0c\u53ea\u6709\u6a2a\u638c\u5982\u5200\uff0c \u5411\u4e0b\u75be\u524a\uff0c\u540c\u65f6\u7528\u5de6\u624b\u6293\u4ed6\u624b\u8155\uff0c\u8fd9\u6837\u4e1c\u65b9\u767d\u82e5\u7136\u4e0d\u7acb\u5373\u7f29\u624b\u53d8\u62db\uff0c\u8f7b\u5219\u6247\u4e86\u88ab\u593a\uff0c\u91cd \u5219\u8155\u9aa8\u6298\u788e\u3002 \u3000\u3000\u674e\u9038\u7684\u672c\u610f\u4e0d\u8fc7\u60f3\u903c\u4ed6\u7f29\u624b\u53d8\u62db\u800c\u5df2\uff0c\u54ea\u77e5\u4e00\u638c\u5288\u4e0b\uff0c\u4e1c\u65b9\u767d\u94c1\u6247\u659c\u6307\uff0c\u674e\u9038\u5de6\u624b \u4e00\u6293\u4e4b\u4e0b\uff0c\u7adf\u7136\u628a\u4e1c\u65b9\u767d\u8d56\u4ee5\u6210\u540d\u7684\u6298\u9aa8\u94c1\u6247\u6293\u5230\u5343\u4e2d\u3002\u674e\u9038\u5927\u51fa\u610f\u5916\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u53ea \u542c\u5f97\u4e1c\u65b9\u767d\u5728\u8eab\u65c1\u8f7b\u8f7b\u8bf4\u9053\uff0c\u201c\u8bf7\u9274\u5fae\u610f\uff0c\u613f\u6548\u9a71\u9a70\uff01\u201d\u58f0\u97f3\u4e4b\u7ec6\uff0c\u53ea\u6709\u674e\u9038\u4e00\u4eba\u53ef\u4ee5 \u52c9\u5f3a\u542c\u51fa\u3002\u674e\u9038\u4e00\u7247\u832b\u7136\uff0c\u4e1c\u65b9\u767d\u4e0d\u5f85\u4ed6\u8bf4\u8bdd\uff0c\u7acb\u5373\u659c\u8dc3\u4e09\u6b65\uff0c\u4f5c\u52bf\u7a33\u4f4f\u8eab\u5f62\uff0c\u62f1\u624b\u8bf4 \u9053\uff1a\u201c\u674e\u516c\u5b50\u795e\u6280\u60ca\u4eba\uff0c\u4e1c\u65b9\u5185\u5fc3\u670d\u53e3\u670d\uff01\u201d\u4ed6\u4eec\u8fd9\u51e0\u62db\u8fc5\u5982\u7535\u5149\u77f3\u706b\uff0c\u5373\u5728\u884c\u5bb6\u773c\u91cc\uff0c \u4e5f\u90fd\u662f\u6781\u4e0a\u4e58\u7684\u529f\u592b\u3002\u7fa4\u96c4\u770b\u5f97\u773c\u82b1\u71ce\u4e71\uff0c\u4e00\u5230\u770b\u6e05\u695a\u4e86\u4e1c\u65b9\u767d\u7684\u6298\u6247\u88ab\u593a\uff0c\u4e0d\u7981\u8f70\u7136 \u559d\u91c7\u3002 \u3000\u3000\u539f\u6765\u4e1c\u65b9\u767d\u672c\u610f\u662f\u8981\u6765\u4e89\u593a\u76df\u4e3b\u7684\uff0c\u5230\u573a\u4e4b\u540e\uff0c\u4e00\u77a7\u8fd9\u98ce\u8272\u4e0d\u5bf9\uff0c\u4ed6\u867d\u7136\u4e0d\u662f\u9f99\u4e09 \u8fd9\u4e00\u4f19\u4eba\uff0c\u5374\u77a7\u51fa\u4e86\u4ed6\u4eec\u5e03\u7f6e\u7684\u75d5\u8ff9\uff0c\u8d77\u521d\u9887\u4e3a\u6124\u6012\uff0c\u6240\u4ee5\u51fa\u8a00\u51b7\u6d88\uff0c\u540e\u6765\u63a2\u6089\u4e86\u674e\u9038 \u7684\u8eab\u4efd\uff0c\u7acb\u5373\u6539\u53d8\u4e3b\u610f\uff0c\u6545\u610f\u8ba9\u62db\uff0c\u4f46\u5728\u8ba9\u62db\u4e4b\u524d\uff0c\u4ecd\u7136\u4f7f\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u597d\u53eb\u674e\u9038\u77e5\u9053 \u4ed6\u7684\u771f\u529f\u592b\u3002 \u3000\u3000\u674e\u9038\u4f55\u7b49\u806a\u660e\uff0c\u542c\u4e86\u4ed6\u90a3\u4e24\u53e5\u6d3b\uff0c\u4f53\u4f1a\u51fa\u4ed6\u7684\u610f\u601d\uff0c\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u4e3a\u4e86\u6211 \u738b\u5b59\u7684\u8eab\u4efd\uff0c\u5e0c\u671b\u6211\u5c06\u6765\u80fd\u91cd\u7528\u4ed6\uff0c\u8ba9\u4ed6\u53d6\u5f97\u529f\u540d\u5bcc\u8d35\u800c\u5df2\u3002\u770b\u6765\u5728\u8fd9\u4e2a\u2018\u82f1\u96c4\u4f1a\u2019\u4e0a\uff0c \u771f\u6b63\u79f0\u5f97\u4e0a\u662f\u2018\u82f1\u96c4\u2019\u7684\uff0c\u90a3\u662f\u5c11\u4e4b\u53c8\u5c11\uff01\u201d\u9887\u4e3a\u540e\u6094\u521a\u624d\u6ca1\u6709\u62d4\u5251\uff0c\u4ee5\u81f3\u9886\u4e86\u4ed6\u8fd9\u4e2a \u4eba\u60c5\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6700\u7d27\u8981\u7684\u662f\u63a8\u7ffb\u6b66\u5219\u5929\uff0c\u725b\u9b3c\u86c7\u795e\uff0c\u4e0d\u59a8\u4e00\u6982\u5229\u7528\u3002\u5982\u6b64\u4e00\u60f3\uff0c\u867d \u7136\u5fc3\u91cc\u8fd8\u662f\u8ba8\u538c\u4e1c\u65b9\u767d\uff0c\u800c\u4e0a\u795e\u8272\u5374\u662f\u4e1d\u6beb\u4e0d\u9732\uff0c\u5f53\u4e0b\u628a\u6298\u6247\u8fd8\u7ed9\u4e86\u4e1c\u65b9\u767d\uff0c\u773c\u5149\u4e2d\u793a \u610f\u5bf9\u4ed6\u611f\u8c22\u3002 \u3000\u3000\u4e1c\u65b9\u767d\u9000\u4e0b\u4e4b\u540e\uff0c\u53c8\u6709\u4e24\u4e2a\u9053\u5341\u51fa\u6765\uff0c\u80cc\u8d1f\u957f\u5251\uff0c\u6001\u5ea6\u96cd\u5bb9\uff0c\u7f13\u7f13\u51fa\u573a\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u5c09\u8fdf\u5148\u751f\u4ee5\u5251\u6cd5\u51a0\u7edd\u6b66\u6797\uff0c\u674e\u516c\u5b50\u662f\u4ed6\u5f97\u610f\u4f20\u4eba\uff0c\u8d2b\u9053\u656c\u8bf7\u674e\u516c\u5b50\u6307\u6559\u5251\u672f\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u662f\u6d1b\u9633\u767d\u9a6c\u89c2\u7684\u89c2\u4e3b\u548c\u4ed6\u7684\u5e08\u5f1f\uff0c\u5e08\u5144\u9053\u53f7\u9ec4\u9e64\uff0c\u5e08\u5f1f\u9053\u53f7\u9752\u677e\uff0c\u4e24\u4eba\u9177\u55dc \u5251\u672f\uff0c\u78b0\u5230\u4f1a\u4f7f\u5251\u7684\u540d\u5bb6\uff0c\u4ed6\u4eec\u4e0d\u8ba1\u8f83\u73ed\u8f88\u7684\u9ad8\u4f4e\uff0e\u603b\u662f\u8981\u60f3\u5c3d\u529e\u6cd5\u627e\u4e2a\u673a\u4f1a\u6765\u4ea4\u624b\u4e00 \u4e0b\u3002\u4ed6\u4eec\u4e4b\u6240\u4ee5\u53c2\u52a0\u82f1\u96c4\u5927\u4f1a\uff0c\u4e5f\u4e0d\u662f\u60f3\u4e89\u593a\u76df\u4e3b\uff0c\u800c\u662f\u60f3\u6765\u770b\u770b\uff0c\u770b\u6709\u4ec0\u4e48\u65b0\u8fdb\u7684\u5251 \u672f\u597d\u624b\u3002\u4e0e\u4f1a\u8bf8\u4eba\uff0c\u4eba\u4eba\u90fd\u77e5\u9053\u4ed6\u4eec\u6709\u8fd9\u4e2a\u602a\u813e\u6c14\u3002 \u3000\u3000\u674e\u9038\u4e00\u89c1\u662f\u4ed6\u4eec\u4e24\u4f4d\uff0c\u6025\u5fd9\u62f1\u4e4e\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\uff01\u201d\u9ec4\u9e64\u900d\u4eba\u8bf4\u9053\uff1a\u201c\u5b66\u65e0\u524d\u540e\uff0c \u8fbe\u8005\u4e3a\u5e08\uff0c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u6562\u7684\uff1f\u6b66\u5b66\u4e4b\u9053\uff0c\u5fc3\u987b\u5207\u78cb\u7422\u78e8\uff0c\u624d\u80fd\u6709\u6240\u8fdb\u76ca\uff0c\u8fd9\u9053\u7406\u51b0\u5e08\u7236 \u6ca1\u6709\u548c\u4f60\u8bf4\u8fc7\u4e48\uff1f\u201d\u674e\u9038\u53ea\u5f97\u7b54\u9053\uff1a\u201c\u8bf4\u662f\u8bf4\u8fc7\u7684\u3002\u201d\u9ec4\u9e64\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u8fd8\u6709\u4ec0\u4e48\u987e\u5fcc\uff1f \u3000\u3000\u54b1\u4eec\u8001\u9053\u8fd8\u4e0d\u6015\u8f93\u3001\u96be\u9053\u4f60\u8fd9\u4e2a\u5c0f\u4f19\u5b50\u5012\u6015\u4e22\u8138\u4e0d\u6210\uff1f\u201d\u674e\u9038\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u60f3\u9053\uff1a \u201c\u82e5\u518d\u8c26\u8f9e\uff0c\u90a3\u5012\u663e\u5f97\u81ea\u5df1\u6c14\u91cf\u5c0f\u4e86\u3002\u201d\u53ea\u597d\u62b1\u5251\u7b11\u9053\uff1a\u201c\u89c2\u4e3b\u54ea\u91cc\u8bdd\u6765\uff1f\u665a\u8f88\u8d25\u5728\u89c2 \u4e3b\u624b\u4e0b\uff0c\u8d25\u4e5f\u8d25\u5f97\u5149\u8363\u3002\u201d\u9ec4\u9e64\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u4f19\u5b50\u5012\u771f\u4f1a\u6367\u4eba\u3002\u5b9e\u5728\u544a\u8bc9 \u4f60\uff0c\u6211\u4eec\u4e24\u5e08\u5144\u5f1f\u7ec3\u4e86\u4e00\u5957\u5251\u672f\uff0c\u4e00\u653b\u4e00\u5b88\uff0c\u5171\u6709\u516b\u516b\u516d\u5341\u56db\u4e2a\u62db\u5f0f\uff0c\u53ef\u4e0d\u77e5\u6210\u662f\u4e0d\u6210\uff1f \u672c\u60f3\u627e\u4f60\u7684\u5e08\u7236\u8bb2\u7a76\u4e00\u4e0b\u7684\uff0c\u53ef\u662f\u4f60\u7684\u5e08\u7236\u884c\u8e2a\u65e0\u5b9a\uff0c\u6211\u53c8\u6ca1\u5de5\u592b\u5230\u5904\u627e\u4ed6\uff0c\u56e0\u6b64\u53ea\u597d \u5230\u8fd9\u4e2a\u4f1a\u4e0a\u6765\u5145\u4e00\u5145\u82f1\u96c4\uff0c\u78b0\u4e0a\u4e86\u4f60\uff0c\u90a3\u771f\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u7701\u5f97\u6211\u591a\u8dd1\u8bb8\u591a\u5730\u65b9\u4e86\u3002\u597d\u5427\uff0c \u65f6\u5019\u4e0d\u65e9\uff0c\u4f60\u8d76\u5feb\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u674e\u9038\u653e\u5251\u51fa\u9798\uff0c\u5bd2\u5149\u8000\u65e5\uff0c\u9752\u677e\u9053\u4eba\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u628a\u5b9d\u5251\u3002 \u3000\u3000\u4f60\u5e08\u7236\u80af\u628a\u4ed6\u7684\u968f\u8eab\u5b9d\u5251\u4ea4\u4ed8\u7ed9\u4f60\uff0c\u90a3\u4f60\u7684\u5251\u672f\u5b9a\u6709\u53ef\u89c2\uff0c\u4e0d\u5fc5\u8c26\u865a\u4e86\u3002\u201d\u674e\u9038\u9053\uff1a \u201c\u5bb6\u5e08\u7684\u5251\u672f\uff0c\u665a\u8f88\u8fd8\u672a\u5b66\u5230\u4e00\u6210\uff0c\u8bf7\u4e24\u4f4d\u8001\u524d\u8f88\u4e0d\u8981\u89c1\u7b11\u3002\u201d\u8bdd\u5b8c\u4e4b\u540e\uff0c\u6a2a\u5251\u5f53\u80f8\uff0c \u62b1\u5143\u5b88\u4e00\uff0c\u4eae\u5f00\u4e86\u201c\u8d77\u624b\u5251\u201d\u7684\u62fe\u5f0f\u3002\u8c37\u795e\u7fc1\u5fc3\u4e2d\u53eb\u82e6\uff0c\u6697\u6697\u9a82\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u725b\u9f3b\u5b50\u771f \u4e0d\u901a\u6c14\uff0c\u4f60\u4eec\u8981\u627e\u4ed6\u6bd4\u8bd5\u4e5f\u4e0d\u5fc5\u5728\u8fd9\u4e2a\u4f1a\u4e0a\u5440\uff01\u4f60\u4eec\u4e0d\u60f3\u5f53\u76df\u4e3b\uff0c\u6211\u53ef\u60f3\u6276\u52a9\u4e2a\u9038\u5f53\u4e0a \u76df\u4e3b\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u6211\u7684\u8ba1\u5212\u90fd\u7ed9\u4f60\u4eec\u6253\u7834\u4e86\u3002\u201d\u8981\u77e5\u9ec4\u9e64\u9053\u4eba\u7684\u5251\u672f\uff0c\u540d\u6c14\u4ec5\u4ec5\u5728\u5c09\u8fdf \u70af\u4e0e\u4ed6\u4e4b\u4e0b\uff0c\u4f55\u51b5\u8fd8\u52a0\u4e0a\u4ed6\u7684\u5e05\u5f1f\uff0c\u8c37\u795e\u7fc1\u867d\u7136\u77e5\u9053\u674e\u9038\u5df2\u66fe\u5f97\u4ed6\u5e05\u7236\u7684\u771f\u4f20\uff0c\u8fd8\u662f\u4e0d \u514d\u4e3a\u4ed6\u62c5\u5fc3\uff0c\u53ef\u662f\u81ea\u5df1\u53c8\u4e0d\u597d\u4e0b\u573a\u62e6\u963b\u3002 \u3000\u3000\u9ec4\u9e64\u9053\u4eba\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\u5440\uff0c\u600e\u4e48\u8fd8\u4e0d\u8fdb\u62db\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u665a\u5806\u4e0d\u6562\u6709\u50ed\u3002\u201d\u9ec4 \u9e64\u80a9\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u5e08\u7236\u90a3\u6837\u6d12\u8131\u7684\u4eba\u3002 \u3000\u3000\u504f\u504f\u4f60\u5374\u8981\u8bb2\u7a76\u8fd9\u4e9b\u6b66\u6797\u7684\u81ed\u89c4\u77e9\u3002\u597d\u5427\uff0c\u63a5\u62db\uff01\u201d\u957f\u5251\u4e00\u6296\uff0c\u5251\u5149\u8d77\u5904\uff0c\u5237\u7684\u4fbf \u5954\u674e\u9038\u7684\u201c\u80a9\u4e95\u7a74\u201d\u523a\u6765\uff0c\u8fd9\u4e00\u62db\u865a\u4e2d\u5957\u5b9e\uff0c\u5b9e\u4e2d\u5957\u865a\uff0c\u523a\u7a74\u524a\u81c2\uff0c\u85cf\u6709\u6781\u5389\u5bb3\u7684\u540e\u7740\uff0c \u7aef\u7684\u5389\u5bb3\u3002\u674e\u9038\u5140\u7acb\u5982\u5c71\uff0c\u51dd\u7738\u6ce8\u89c6\u4ed6\u7684\u5251\u5c16\uff0c\u5bb9\u4ed6\u5251\u5c16\u9ad8\u8eab\u4e0d\u5230\u4e94\u5bf8\u4e4b\u9645\uff0c\u500f\u5730\u628a\u5251 \u4e00\u6447\uff0c\u5176\u75be\u5982\u7535\uff0c\u4e00\u62db\u201c\u91d1\u9e4f\u5c55\u7fc5\u201d\uff0c\u7adf\u4e0e\u9ec4\u9e64\u9053\u4eba\u5bf9\u62a2\u653b\u52bf\uff0c\u53cd\u524a\u4ed6\u7684\u624b\u8155\u3002 \u3000\u3000\u674e\u9038\u8fd9\u4e00\u62db\u4f7f\u5f97\u8001\u7ec3\u975e\u5e38\uff0c\u62ff\u634f\u65f6\u5019\uff0c\u6070\u5230\u597d\u5904\uff0c\u573a\u4e2d\u6709\u597d\u51e0\u4f4d\u4f7f\u5251\u7684\u5927\u540d\u5bb6\uff0c\u90fd \u6697\u6697\u70b9\u5934\u3002\u6309\u7167\u5251\u5b66\u7684\u5e38\u7406\uff0c\u9ec4\u9e64\u9053\u4eba\u975e\u64a4\u5251\u56de\u9632\u4e0d\u53ef\uff0c\u90a3\u6599\u4ed6\u53cd\u800c\u8e0f\u4e0a\u4e00\u6b65\uff0c\u8eab\u5f62\u4e0d \u53d8\uff0c\u5251\u950b\u76f4\u5411\u674e\u9038\u624b\u8155\u524a\u4e0b\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u5362\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u539f\u7c73\u674e\u9038\u8fd9\u4e00\u62db\u8f9b\u8fa3\u7684 \u62a2\u653b\uff0c\u5374\u7ed9\u9752\u677e\u9053\u4eba\u683c\u5f00\uff0c\u773c\u770b\u674e\u9038\u82e5\u4e0d\u5f03\u5251\u800c\u9003\uff0c\u624b\u8155\u5c31\u975e\u7ed9\u9ec4\u9e64\u9053\u4eba\u65a9\u65ad\u4e0d\u53ef\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u521a\u9053\u8981\u7cdf\uff0c\u9661\u7136\u95f4\u5ffd\u89c1\u674e\u9038\u5012\u8f6c\u5251\u67c4\uff0c\u5f80\u4e0a\u4e00\u6321\uff0c\u4ed6\u672c\u6765\u662f\u6a2a\u63e1\u5251\u67c4\u7684\uff0c\u8fd9 \u65f6\u5728\u77ac\u606f\u4e4b\u95f4\u5df2\u6539\u53d8\u4e86\u63e1\u5251\u7684\u59ff\u52bf\uff0c\u53ea\u7528\u62c7\u6307\u98df\u6307\uff0c\u7bb1\u4f4f\u5251\u67c4\u53cd\u9762\u7684\u94c1\u73af\uff0c\u5982\u6b64\u6301\u5251\uff0c \u6781\u96be\u8fd0\u529b\uff0c\u5927\u4e0b\u5404\u5bb6\u5404\u6d3e\u5251\u6cd5\u4e4b\u4e2d\uff0c\u4e5f\u4ece\u65e0\u5982\u6b64\u602a\u62db\uff0c\u4f46\u9ec4\u9e64\u9053\u4eba\u90a3\u4e00\u5251\u7adf\u7136\u7ed9\u4ed6\u6321\u5f00\uff0c \u5251\u950b\u53ea\u662f\u5c11\u8bb8\u4fbf\u8981\u524a\u65ad\u4ed6\u7684\u624b\u6307\u3002\u8c37\u4f38\u7fc1\u5401\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u6240\u5f97\u7684\u5251\u6cd5\u679c\u7136\u8fd8 \u5728\u6211\u7684\u8e51\u4e91\u5251\u6cd5\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u674e\u9038\u7528\u4e86\u4e00\u8bb0\u602a\u62db\uff0c\u8eab\u5f62\u75be\u5373\u9000\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u5fc3\u4e2d\u6697\u53eb\uff1a \u3000\u3000\u201c\u597d\u9669\uff01\u201d\u60f3\u9053\uff1a\u201c\u6211\u600e\u7684\u5982\u6b64\u7cca\u6d82\uff1f\u9ec4\u9e64\u9053\u4eba\u65e9\u5df2\u8bf4\u660e\uff0c\u4ed6\u4eec\u8fd9\u5957\u5251\u6cd5\u4e43\u662f\u4e00\u653b \u4e00\u5b88\uff0c\u6211\u5c82\u53ef\u4e0e\u4ed6\u5bf9\u653b\uff1f\u201d \u3000\u3000\u9ec4\u9e64\u9053\u4eba\u53eb\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u4e0d\u4fd7\uff0c\u518d\u6765\uff0c\u518d\u6765\uff01\u201d\u4e0e\u9752\u677e\u9053\u4eba\u5e03\u6210\u7284\u89d2\u4e4b\u52bf\uff0c\u8fd0\u5251\u5982 \u98ce\uff0c\u518d\u5411\u674e\u9038\u5c55\u5f00\u653b\u52bf\uff0c\u5251\u8d77\u5904\uff0c\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u201c\u91d1\u9e21\u593a\u7c9f\u201d\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u622a\u8155\u65a9\u808b\uff0c \u5251\u52bf\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\u3002 \u3000\u3000\u674e\u9038\u541e\u80f8\u51f9\u8179\uff0c\u6ed1\u6b65\u632a\u8eab\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u8fde\u907f\u6570\u62db\uff0c\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u9752\u950b\u4e09\u5c3a\uff0c \u75be\u8d77\u800c\u8fce\uff0c\u5237\u7684\u4e00\u5251\uff0c\u76f4\u523a\u9ec4\u9e64\u9053\u4eba\u54bd\u5589\u3002\u9ec4\u9e64\u9053\u4eba\u5927\u4e3a\u5947\u602a\uff0c\u201c\u600e\u7684\u4ed6\u8fd8\u6562\u4e0e\u6211\u5bf9\u653b\uff1f\u201d \u4ed6\u77e5\u9053\u4e0d\u8bba\u674e\u9038\u7684\u653b\u52bf\u5982\u4f55\u731b\u70c8\uff0c\u5e08\u5f1f\u4e00\u5b9a\u53ef\u4ee5\u7ed9\u81ea\u5df1\u6321\u5f00\uff0c\u56e0\u6b64\u4ed6\u6beb\u65e0\u987e\u5fcc\uff0c\u90a3\u4e00\u62db \u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u4ecd\u7136\u662f\u539f\u5f0f\u4e0d\u53d8\uff0c\u75be\u523a\u51fa\u53bb\u3002\u5ffd\u89c9\u4e00\u5251\u523a\u7a7a\uff0c\u53ea\u89c1\u674e\u9038\u5df2\u662f\u907f\u5f00\u4e86\u4ed6\u7684 \u72e0\u653b\uff0c\u5251\u5c16\u53cd\u6307\u4ed6\u7684\u5e08\u5f1f\u3002 \u3000\u3000\u674e\u9038\u8fd0\u5251\u5982\u98ce\uff0c\u7aef\u7684\u6709\u5982\u9e70\u7fd4\u96bc\u523a\u3002\u4ed6\u8fd9\u4e00\u4e0b\u7a81\u7136\u53d8\u62db\uff0c\u53ea\u6740\u5f97\u9752\u677e\u9053\u4eba\u624b\u5fd9\u811a\u4e71\u3002 \u539f\u6765\u674e\u9038\u770b\u7834\u4e86\u662f\u9ec4\u9e64\u4e3b\u653b\uff0c\u9752\u677e\u4e3b\u5b88\u4e4b\u540e\uff0c\u4ed6\u4e5f\u968f\u673a\u5e94\u53d8\uff0c\u5bf9\u9752\u677e\u653b\u51fb\uff0c\u800c\u5bf9\u9ec4\u9e64\u9632 \u5b88\u3002 \u3000\u3000\u9ec4\u9e64\u9053\u4eba\u7b11\u9053\uff1a\u201c\u597d\u806a\u660e\u7684\u5c0f\u4f19\u5b50\uff01\u201d\u759a\u4f7f\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u66ff\u9752\u677e\u9053\u4eba\u817e\u51fa\u624b \u6765\u8fdb\u653b\uff0c\u8fd9\u4e00\u6765\u4fbf\u53d8\u6210\u4e86\u9752\u677e\u4e3b\u653b\uff0c\u9ec4\u9e64\u4e3b\u5b88\u3002\u674e\u9038\u4f55\u7b49\u673a\u7075\uff0c\u4e00\u89c1\u5bf9\u65b9\u53d8\u4f4d\u6613\u52bf\uff0c\u5251 \u62db\u4e5f\u7acb\u5373\u968f\u4e4b\u800c\u53d8\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u6709\u6253\u5317\uff0c\u95ea\u8fc7\u4e86\u9752\u677e\u9053\u4eba\uff0c\u5374\u6765\u653b\u51fb\u9ec4\u9e64\u9053\u4eba\u3002\u767b\u65f6 \u628a\u4ed6\u4eec\u9884\u5b9a\u7684\u653b\u5b88\u8ba1\u5212\u6253\u4e71\uff0c\u8feb\u5f97\u4ed6\u4eec\u4e0d\u65ad\u7684\u4e92\u6613\u653b\u5b88\u4e4b\u52bf\u3002\u53cc\u65b9\u51fa\u5251\u90fd\u662f\u8f7b\u7075\u5feb\u6377\u4e4b \u6781\uff0c\u674e\u9038\u867d\u7136\u8fd8\u6ca1\u6709\u5360\u5f97\u4fbf\u5b9c\uff0c\u53ef\u662f\u5df2\u53d8\u6210\u4e86\u4e09\u4eba\u6df7\u6545\u4e4b\u5c40\uff0c\u9ec4\u9e64\u9752\u677e\u4e5f\u505a\u4e0d\u6210\u5408\u56f4\u4e4b \u52bf\uff0c\u53ea\u597d\u5404\u81ea\u4e3a\u6218\uff0c\u90a3\u4e00\u5957\u5251\u6cd5\u7684\u5a01\u529b\uff0c\u7adf\u662f\u65e0\u4ece\u65bd\u5c55\uff01 \u3000\u3000\u573a\u4e2d\u5251\u5149\u6325\u970d\uff0c\u5251\u6c14\u7eb5\u6a2a\uff0c\u6597\u5230\u75be\u5904\uff0c\u4f46\u89c1\u767d\u5203\u8000\u773c\uff0c\u65e0\u6570\u4eba\u5f71\u5728\u8349\u576a\u4e0a\u65cb\u8f6c\u98de\u821e\uff0c \u5df2\u5206\u4e0d\u51fa\u8c01\u662f\u674e\u9038\uff0c\u8c01\u662f\u9ec4\u9e64\uff0c\u8c01\u662f\u9752\u677e\uff0c\u867d\u7136\u53ea\u662f\u4e09\u67c4\u5251\u76f8\u6597\uff0c\u5374\u6709\u5982\u5343\u767e\u4e2a\u6b66\u58eb\u5728 \u4ea4\u6218\u4e00\u822c\uff0c\u7fa4\u96c4\u90fd\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u81ea\u53f9\u4e0d\u5982\uff0c\u90a3\u4e2a\u521a\u521a\u548c\u674e\u9038\u4ea4\u8fc7\u624b\u7684\u4e1c\u65b9\u767d\u4ea6\u81ea\u6697\u6697 \u5fc3\u670d\uff1a\u201c\u8981\u662f\u4ed6\u521a\u624d\u4eae\u51fa\u5b9d\u5251\uff0c\u6211\u8fd9\u628a\u94c1\u6247\u5b50\u53ea\u6015\u5f53\u771f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8eb2\u5728\u77f3\u7b0b\u7f1d\u4e2d\uff0c\u4e5f\u662f\u770b\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c\u5979\u7684\u6b66\u529f\u867d\u7136\u4e0e\u5728\u573a\u8bf8\u4eba\u76f8\u53bb\u751a\u8fdc\uff0c \u4f46\u5979\u7684\u5e05\u7236\u957f\u5b59\u5747\u91cf\u4e43\u662f\u5251\u672f\u540d\u5bb6\uff0c\u5979\u4e45\u53d7\u718f\u9676\uff0c\u5374\u770b\u5f97\u51fa\u674e\u9038\u867d\u7136\u6682\u65f6\u62b5\u6321\u5f97\u4f4f\uff0c\u4f46 \u5374\u4e00\u534a\u51ed\u4ed7\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u4e00\u534a\u662f\u9760\u4e86\u4ed6\u7684\u806a\u660e\u673a\u667a\uff0c\u8fd9\u624d\u80fd\u5316\u9669\u4e3a\u5937\u3002\u771f\u6b63\u7684\u4f18\u52bf\uff0c\u8fd8 \u8db3\u5728\u9ec4\u9e64\u9752\u677e\u8fd9\u4e00\u65b9\u3002 \u3000\u3000\u53cc\u65b9\u6b63\u81ea\u6597\u5230\u6781\u5ea6\u7d27\u5f20\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u9707\u5f97\u5404\u4eba\u7684\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c \u9661\u7136\u95ee\u5ffd\u89c1\u9ec4\u9e64\u9752\u677e\u53cc\u53cc\u8df3\u51fa\u5708\u5b50\uff0c\u9ec4\u9e64\u9053\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u4e3d\u6d6a\uff0c\u4e16\u4e0a\u65b0\u4eba \u6362\u65e7\u4eba\uff0c\u8fd9\u8bdd\u771f\u771f\u4e0d\u9519\uff01\u6211\u4eec\u6240\u521b\u7684\u5251\u6cd5\uff0c\u516b\u516b\u516d\u5341\u56db\u62db\u90fd\u5df2\u4f7f\u5b8c\uff0c\u8fd8\u8d62\u4e0d\u4e86\u4f60\u4e00\u62db\u534a \u5f0f\uff0c\u8d2b\u9053\u597d\u751f\u4f69\u670d\uff0c\u8fd9\u573a\u5251\u4e5f\u4e0d\u5fc5\u518d\u6bd4\u5566\uff01\u201d\u674e\u9038\u62b1\u5251\u8fd8\u793c\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u524d\u8f88\u8fd9\u6b21\u4e0d\u60dc \u6307\u70b9\uff0c\u665a\u8f88\u5f97\u76ca\u4e0d\u6d45\uff01\u201d \u3000\u3000\u8c37\u795e\u7fc1\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u8fd9\u65f6\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8d70\u51fa\u573a\u5fc3\uff0c\u643a\u7740\u674e\u9038\u7684\u624b\u8bf4\u9053\uff1a \u201c\u5404\u4f4d\u90fd\u770b\u89c1\u4e86\uff0c\u8fd9\u4f4d\u674e\u516c\u5b50\u7684\u6b66\u529f\uff0c\u8fde\u767d\u9a6c\u89c2\u4e3b\u90fd\u8d5e\u4e0d\u7edd\u53e3\uff0c\u53ef\u4e0d\u662f\u8001\u592b\u6545\u610f\u4e3a\u4ed6\u5938 \u5f20\u5ef6\u8a89\u5427\uff01\u53e6\u5916\u4e00\u4e8b\uff0c\u8bf8\u4f4d\u6216\u8bb8\u5c1a\u6709\u6240\u4e0d\u77e5\uff0c\u6211\u4eec\u8fd9\u4f4d\u674e\u516c\u5b50\u4e43\u662f\u9ad8\u7956\u7687\u5e1d\uff08\u674e\u6e0a\uff09\u7684 \u66fe\u5b59\uff0c\u5927\u5b97\u7687\u5e1d\uff08\u674e\u4e16\u6c11\uff09\u7684\u4f84\u5b59\uff0c\u5f53\u4eca\u5929\u4e0b\u7eb7\u6270\uff0c\u54b1\u4eec\u5c82\u53ef\u7518\u5fc3\u57cb\u6ca1\u4e8e\u8349\u83bd\u4e4b\u95f4\uff1f\u96be \u5f97\u6709\u674e\u516c\u5b50\u8fd9\u6837\u7684\u738b\u5b59\u8d35\u80c4\uff0c\u54b1\u4eec\u6b63\u597d\u8ddf\u968f\u4ed6\u505a\u4e00\u756a\u4e8b\u4e1a\uff01\u201d\u6b64\u8bdd\u4e00\u51fa\uff0c\u5df2\u7ecf\u77e5\u9053\u7684\u56fa \u7136\u662f\u9f13\u638c\u5982\u96f7\uff0c\u672a\u66fe\u77e5\u9053\u7684\u5219\u5404\u5404\u53cd\u5e94\u4e0d\u540c\uff0c\u4e5f\u6709\u4e00\u5927\u534a\u4eba\u968f\u7740\u9f13\u638c\u3002 \u3000\u3000\u540c\u58f0\u9ad8\u547c\uff0c\u62e5\u6234\u674e\u9038\u505a\u4ed6\u4eec\u7684\u76df\u4e3b\u3002 \u3000\u3000\u674e\u9038\u5374\u662f\u6709\u70b9\u4e0d\u5feb\uff0c\u8c37\u795e\u7fc1\u672c\u6765\u548c\u4ed6\u7ea6\u5b9a\uff0c\u8981\u5f85\u4ed6\u593a\u5f97\u76df\u4e3b\u4e4b\u4f4d\u540e\uff0c\u624d\u516c\u5e03\u4ed6\u7684\u8eab \u4efd\u7684\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u6211\u8fd9\u4e2a\u76df\u4e3b\u4e4b\u4f4d\uff0c\u5c82\u4e0d\u662f\u8981\u4ed6\u4eec\u770b\u5728\u6211\u662f\u738b\u5b59\u7684\u4efd\u4e0a\uff0c \u8fd9\u624d\u8ba9\u7ed9\u6211\u7684\uff1f\u201d\u5176\u5b9e\u8c37\u795e\u7fc1\u7684\u786e\u4e5f\u6709\u8fd9\u4e2a\u610f\u601d\uff0c\u4ed6\u77e5\u9053\u573a\u4e2d\u8fd8\u6709\u51e0\u4e2a\u9ad8\u624b\uff0c\u4e0d\u5728\u4e1c\u65b9 \u767d\u4e0e\u9ec4\u9e64\u9752\u677e\u4e4b\u4e0b\uff0c\u82e5\u7136\u4ed6\u4eec\u4e5f\u8981\u4e89\u593a\u76df\u4e3b\u5b9d\u5ea7\uff0c\u53ea\u6015\u674e\u9038\u5e94\u4ed8\u4e0d\u6765\u3002\u4e0d\u8fc7\uff0c\u5ea7\u4e0a\u7fa4\u96c4 \u770b\u4e86\u521a\u624d\u90a3\u4e00\u573a\u6bd4\u5251\uff0c\u867d\u7136\u5c1a\u6709\u51e0\u4eba\u89c9\u5f97\u674e\u9038\u7684\u6b66\u529f\u8fd8\u6765\u5230\u4e00\u6d41\u7edd\u9876\u7684\u5883\u754c\uff0c\u4f46\u8f6c\u5ff5\u4e00 \u60f3\uff0c\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4fbf\u6709\u4e86\u8fd9\u7965\u9020\u8be3\uff0c\u5373\u7b97\u8ba9\u4ed6\u5f53\u4e0a\u76df\u4e3b\uff0c\u4e5f\u4e0d\u4e3a\u8fc7\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u5ba3\u5e03\u4e86\u674e\u9038\u7684\u8eab\u4efd\uff0c\u679c\u7136\u6ca1\u6709\u4eba\u518d\u51fa\u6765\u8ddf\u4ed6\u4e89\u593a\uff0c\u4f46\u56db\u8fb9\u89d2\u843d\u91cc\uff0c\u5374\u540c\u65f6\u6709 \u51e0\u4e2a\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u6562\u95ee\u674e\u516c\u5b50\u8981\u7387\u9886\u54b1\u4eec\u5e72\u4ec0\u4e48\u4e8b\u4e1a\uff1f\u201d \u3000\u3000\u8c37\u795e\u7fc1\u62c8\u987b\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u65e5\u4e4b\u4f1a\uff0c\u540d\u4e3a\u82f1\u96c4\u5927\u4f1a\uff0c\u5728\u5ea7\u8bf8\u541b\uff0c\u90fd\u662f\u82f1\u96c4\uff0c\u65e2\u662f\u82f1 \u96c4\uff0c\u600e\u7518\u96cc\u4f0f\uff1f\u5343\u53e4\u4ee5\u6765\uff0c\u672c\u90fd\u662f\u7537\u5b50\u79f0\u5806\uff0c\u60f3\u4e0d\u5230\u5982\u4eca\u5374\u662f\u5987\u4eba\u541b\u4e34\u5929\u4e0b\uff0c\u4e0d\u77e5\u8bf8\u4f4d \u5982\u4f55\uff1f\u6211\u8c37\u795e\u7fc1\u7b2c\u4e00\u4e2a\u4fbf\u4e0d\u670d\u6c14\uff01\u6240\u4ee5\u6211\u8bf4\uff0c\u662f\u82f1\u96c4\u7684\u4fbf\u8be5\u622e\u529b\u540c\u5fc3\uff0c\u52a9\u674e\u516c\u5b50\u4e00\u81c2\u4e4b \u529b\uff0c\u5c06\u5f53\u671d\u7684\u5973\u5e1d\u63a8\u7ffb\uff0c\u4e3a\u666e\u5929\u4e0b\u7684\u7537\u513f\u626c\u7709\u5410\u6c14\uff01\u201d\u8c37\u795e\u7fc1\u719f\u6089\u8349\u6cfd\u82f1\u8c6a\u7684\u5fc3\u7406\uff0c\u6545 \u610f\u628a\u56fd\u5bb6\u5927\u4e8b\u7528\u8f7b\u677e\u7684\u53e3\u543b\u51fa\u4e4b\uff0c\u679c\u7136\u6bd4\u4e00\u756a\u201c\u4e49\u6b63\u8f9e\u4e25\u201d\u7684\u8bf4\u8bdd\uff0c\u66f4\u53d7\u5230\u70ed\u70c8\u6b22\u8fce\u3002 \u3000\u3000\u4f46\u542c\u5f97\u5728\u54d7\u7b11\u58f0\u4e2d\uff0c\u7fa4\u96c4\u7eb7\u7eb7\u8bf4\u9053\uff1a\u201c\u8001\u76df\u4e3b\u8bf4\u5f97\u662f\uff0c\u54b1\u4eec\u7537\u5b50\u6c49\u5927\u5927\u592b\u5c82\u80fd\u5411\u4e00 \u4e2a\u5987\u4eba\u4f4e\u9996\u79f0\u81e3\uff01\u201d\u201c\u5bf9\u5475\uff0c\u7687\u5e1d\u59d3\u674e\u59d3\u5f20\uff0c\u90fd\u65e0\u6240\u8c13\uff0c\u603b\u4e0d\u80fd\u8ba9\u4e00\u4e2a\u5973\u4eba\u505a\u7687\u5e1d\u6765\u7ba1 \u54b1\u4eec\uff01\u201d\u201c\u597d\u5440\uff0c\u674e\u516c\u5b50\u4ee5\u738b\u5b59\u8eab\u4efd\uff0c\u632f\u81c2\u4e00\u547c\uff0c\u5929\u4e0b\u5fc5\u7136\u54cd\u5e94\uff0c\u54b1\u4eec\u4e2a\u4e2a\u90fd\u662f\u4e2d\u5174\u7684 \u529f\u81e3\uff01\u201d\u201c\u4ffa\u96c4\u5de8\u9f0e\u7b2c\u4e00\u4e2a\u62e5\u62a4\u65b0\u76df\uff0c\u4e00\u8f88\u5b50\u613f\u8ddf\u674e\u516c\u5b50\u7275\u9a6c\u968f\u956b\uff01\u201d \u3000\u3000\u8d3f\u95f9\u4e2d\u5374\u6709\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u7d20\u6027\u95f2\u6563\uff0c\u4f46\u6c42\u9152\u9189\u996d\u9971\uff0c\u5f97\u4ee5\u5f9c\u5f89\u4e91\u6c34\u4e4b\u95f4\uff0c\u4e8e\u613f \u5df1\u8db3\uff0c\u51c6\u505a\u7687\u5e1d\uff0c\u4e0e\u6211\u65e0\u5173\uff01\u201d\u8bf4\u8bdd\u7684\u662f\u592a\u6e56\u9690\u4fa0\u9633\u955c\u660e\u3002\u53c8\u6709\u4eba\u9053\uff1a\u201c\u539f\u6765\u674e\u516c\u5b50\u8981 \u5e72\u7684\u662f\u8fd9\u756a\u4e8b\u4e1a\u3002 \u3000\u3000\u8001\u5927\u540d\u77e3\uff0c\u4e0d\u6562\u6307\u671b\u505a\u4ec0\u4e48\u4e2d\u5174\u529f\u81e3\uff0c\u8001\u592b\u544a\u9000\uff01\u201d\u8fd9\u4eba\u662f\u9752\u57ce\u5c71\u7684\u6731\u51a0\u543e\uff0c\u63a5\u7740 \u53c8\u6709\u51e0\u4e2a\u4eba\u90fd\u5728\u793a\u4e86\u540c\u6837\u7684\u610f\u513f\u8fd9\u4e9b\u4eba\u90fd\u662f\u6b66\u6797\u4e2d\u6709\u8eab\u4efd\u7684\u6b63\u6d3e\u4eba\u7269\uff0c\u8c37\u795e\u7fc1\u6781\u4e3a\u4e0d\u6ee1\uff0c \u5374\u4e5f\u4e0d\u6562\u6b63\u9762\u6307\u65a5\u4ed6\u4eec\uff0c\u53ea\u597d\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u60f3\u9000\u51fa\u7684\u542c\u5176\u81ea\u4fbf\uff0c\u613f\u8ddf\u968f\u65b0\u76df \u4e3b\u7684\u7ad9\u51fa\u6765\uff01\u201d \u3000\u3000\u5c71\u4e1c\u996e\u9a6c\u5ddd\u7684\u6768\u5be8\u4e3b\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u6b66\u5219\u5929\u8fd9\u4e2a\u5973\u9b54\u738b\u505a\u4e86\u7687\u5e1d\uff0c\u54b1\u4eec\u7684\u53e3\u5b50\u8d8a\u8fc7\u8d8a \u96be\uff0c\u54fc\uff0c\u5c31\u7b97\u5979\u662f\u4e2a\u7537\u4eba\uff0c\u6211\u4e5f\u8981\u53cd\u5bf9\u4ed6\uff01\u201d\u6b66\u5219\u5929\u6267\u653f\u4e4b\u540e\uff0c\u5bf9\u5404\u5904\u7eff\u6797\u4eba\u7269\uff0c\u4e00\u9762 \u62db\u629a\uff0c\u4e00\u9762\u56f4\u527f\uff0c\u6069\u5a01\u5e76\u65bd\uff0c\u53cc\u7ba1\u9f50\u4e0b\u3002\u4ee5\u524d\u505a\u5f3a\u76d7\u7684\u591a\u534a\u662f\u4e3a\u4e86\u751f\u6d3b\u56f0\u96be\uff0c\u6b66\u5219\u5929\u6267 \u653f\uff0c\u96f7\u5389\u98ce\u884c\u7684\u6267\u884c\u201c\u5747\u7530\u5236\u5ea6\u201d\uff0c\u5f3a\u76d7\u53d7\u4e86\u62db\u629a\u4e4b\u540e\uff0c\u90fd\u6709\u4e00\u4efd\u7530\u79cd\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u7eff \u6797\u7684\u52bf\u529b\u5927\u51cf\uff0c\u5269\u4e0b\u7684\u4e00\u73ed\u4e0d\u613f\u79cd\u7530\uff0c\u4ecd\u7136\u8981\u6253\u5bb6\u52ab\u820d\u7684\u5f3a\u76d7\uff0c\u65e5\u5b50\u786e\u5b9e\u662f\u8d8a\u8fc7\u8d8a\u96be\u3002 \u6768\u5be8\u4e3b\u8fd9\u756a\u8bf4\u8bdd\u767b\u65f6\u717d\u52a8\u4e86\u5728\u573a\u7684\u9ed1\u9053\u4eba\u7269\uff0c\u4f17\u4eba\u8f70\u7136\u79f0\u662f\u3002\u8fd8\u6709\u4e00\u73ed\u60f3\u8ddf\u674e\u9038\u730e\u53d6\u529f \u540d\u5bcc\u8d35\u7684\uff0c\u4e5f\u968f\u58f0\u9644\u548c\uff0c\u5927\u9a82\u6b66\u5219\u5929\u3002\u7ed3\u679c\u8fd9\u6b21\u53c2\u52a0\u82f1\u96c4\u4f1a\u7684\uff0c\u9664\u4e86\u5341\u591a\u4e2a\u4eba\u9000\u51fa\u4e4b\u5916\uff0c \u5176\u4ed6\u7684\u90fd\u7ad9\u4e86\u51fa\u6765\uff0c\u540c\u58f0\u63a8\u6234\uff0c\u62e5\u62a4\u674e\u9038\u3002 \u3000\u3000\u674e\u9038\u5fc3\u5934\u5374\u662f\u767e\u611f\u4ea4\u96c6\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u4e00\u4e32\u8106\u82e5\u94f6\u94c3\u7684\u7b11\u58f0\uff0c\u4ece\u5c71\u5934\u4e0a\u98d8\u535c\u7c73\uff0c \u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5728\u6f2b\u5929\u78f7\u706b\u4e4b\u4e2d\uff0c\u4e2a\u767d\u8863\u5c11\u5973\uff0c\u4ece\u5bf9\u9762\u7684\u5c71\u5761\u4e0a\u98d8\u7136\u800c\u964d\uff0c\u53cc\u8896\u98de\u626c\uff0c \u78f7\u706b\u6d41\u6563\uff0c\u7aef\u7684\u4f3c\u7389\u865a\u4ed9\u5b50\uff0c\u5728\u7fa4\u661f\u95f4\u5fa1\u98ce\u800c\u884c\uff0c\u4f73\u4eba\u5947\u666f\uff0c\u5e76\u6210\u53cc\u7edd\uff01\u970e\u65f6\u95f4\u5168\u573a\u5bc2 \u9759\u65e0\u58f0\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u9752\u5251\u7ea2\u7ef8\u5973\u4fa0\u6765 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u516d\u56de\u3000\u9752\u5251\u7ea2\u7ef8\u5973\u4fa0\u6765 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u4e00\u9707\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u5979\u6765\u4e86\uff01\u53ea\u6015\u8fd9\u5ce8\u5d4b\u5c71\u9876\uff0c\u7acb\u523b\u8981\u5377\u8d77\u4e00\u573a\u8840\u96e8 \u8165\u98ce\uff01\u201d\u6765\u7684\u4e0d\u662f\u522b\u4eba\uff1b\u6b63\u662f\u4e0a\u5b98\u5a49\u513f\u90a3\u65e5\u5728\u6843\u82b1\u6797\u4e2d\u6240\u9047\u5230\u7684\u6b66\u7384\u971c\u3002\u4e0a\u5b98\u5a49\u513f\u60f3\u8d77 \u5979\u60e9\u6cbb\u76d7\u5f92\u7684\u60e8\u9177\u624b\u6bb5\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u60f4\u60f4\u3002 \u3000\u3000\u4f46\u89c1\u6b66\u7384\u971c\u8863\u8882\u98d8\u98d8\uff0c\u76f4\u95ef\u5230\u82f1\u96c4\u4f1a\u4e0a\uff0c\u5355\u576a\u4e0a\u56f4\u5750\u7740\u7684\u7fa4\u96c4\uff0c\u6bcf\u4e00\u4e2a\u4eba\u7684\u76ee\u5149\u90fd \u968f\u7740\u5979\u7684\u5029\u5f71\u79fb\u52a8\uff0c\u7adf\u7136\u6ca1\u6709\u4e00\u4e2a\u60f3\u8d77\u8981\u62e6\u963b\u5979\uff01 \u3000\u3000\u6b66\u7384\u971c\u63a5\u8fde\u53c8\u7b11\u4e86\u4e09\u58f0\uff0c\u4e00\u58f0\u9ad8\u4f3c\u4e00\u58f0\uff0c\u7fa4\u5cf0\u56de\u54cd\uff0c\u54cd\u904f\u884c\u4e91\uff0c\u7b11\u58f0\u4e2d\u5927\u6709\u9119\u5c51\u4e4b \u610f\u3002\u8c37\u795e\u7fc1\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u600e\u4e48\u8fd9\u4e2a\u5c11\u5973\uff0c\u5185\u529f\u7adf\u662f\u6df1\u539a\u5982\u65af\uff1f\u201d \u3000\u3000\u674e\u9038\u5b9a\u4e86\u5b9a\u5fc3\u795e\uff0c\u62f1\u624b\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u5c0f\u59d0\u56e0\u4f55\u53d1\u7b11\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u7b11\u4f60\u7b49\u8fd9\u4e9b\u4e71 \u4e03\u516b\u7cdf\u7684\u4e4c\u5408\u4e4b\u4f17\uff0c\u7adf\u7136\u4e5f\u6562\u6765\u5f00\u4ec0\u4e48\u82f1\u96c4\u5927\u4f1a\uff01\u201d\u7fa4\u96c4\u4e2d\u4ee5\u96c4\u5de8\u9f0e\u6700\u4e3a\u9c81\u82a5\uff0c\u52c3\u7136\u6012 \u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u8fd9\u4e73\u81ed\u6765\u5e72\u7684\u7684\u5c0f\u4e2b\u5934\u7adf\u6562\u803b\u7b11\u6211\u7b49\u5929\u4e0b\u82f1\u96c4\uff01\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a \u201c\u662f\u4e48\uff1f\u4f60\u7b49\u90fd\u662f\u82f1\u96c4\uff1f\u90a3\u4e48\u5929\u4e0b\u82f1\u96c4\u5c82\u4e0d\u662f\u8f66\u8f7d\u6597\u91cf\uff1f\u201d\u96c4\u5de8\u9f0e\u559d\u9053\uff1a\u201c\u82e5\u975e\u770b\u4f60\u5f31 \u8d28\u5a77\u5a77\uff0c\u4ffa\u4e00\u62f3\u5c31\u628a\u4f60\u6253\u4e2a\u7c89\u788e\u3002\u91ce\u4e2b\u5934\uff0c\u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u201d\u6b66\u7384\u971c\u6beb\u4e0d\u7406\u776c\uff0c\u4ecd\u7136\u7f13\u7f13 \u524d\u884c\uff0c\u96c4\u5de8\u9f0e\u5927\u6012\uff0c\u8df3\u4e0a\u524d\u53bb\uff0c\u4ef0\u51fa\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u671d\u7740\u6b66\u7384\u971c\u5c31\u662f\u4e00\u6293\uff0c\u7528\u7684\u7adf\u662f\u5927 \u529b\u9e70\u722a\u7684\u529f\u592b\uff0c\u8981\u628a\u6b66\u7384\u971c\u786c\u6293\u8d77\u7c73\uff0c\u7529\u51fa\u8349\u576a\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u559d\u9053\uff1a\u201c\u96c4\u5be8\u4e3b\u4e0d\u53ef\u9020\u6b21\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u4e00\u4e2a\u94c1\u5854\u822c\u7684\u8eab\u8eaf\u51cc\u7a7a\u98de\u8d77\uff0c \u8d8a\u8fc7\u4f17\u4eba\u5934\u9876\uff0c\u6454\u4e0b\u8349\u576a\u3002\u88ab\u6454\u5012\u7684\u4e0d\u662f\u201c\u5f31\u8d28\u5a77\u5a77\u201d\u7684\u6b66\u7384\u971c\uff0c\u800c\u662f\u53f7\u79f0\u201c\u8d5b\u5143\u9738\u201d \u7684\u96c4\u5de8\u9f0e\uff01\u96c4\u5de8\u9f0e\u7684\u624b\u6307\u6839\u672c\u5c31\u6ca1\u6709\u78b0\u7740\u5979\u7684\u8eab\u4f53\uff0c\u88ab\u5979\u8863\u8896\u4e00\u62c2\uff0c\u501f\u529b\u6253\u529b\uff0c\u4fbf\u8dcc\u5f97 \u722c\u4e0d\u8d77\u6765\uff01\u674e\u9038\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6b66\u7384\u971c\u4eae\u7684\u8fd9\u624b\uff0c\u6b63\u662f\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u4e0a\u4e58\u529f\u592b\uff01 \u3000\u3000\u4e1c\u65b9\u5c71\u9634\u607b\u607b\u7684\u7b11\u4e86\u4e00\u58f0\uff0c\u5e76\u4e0d\u89c1\u4ed6\u8df3\u8dc3\u4f5c\u52bf\uff0c\u500f\u7136\u95f4\u5c31\u5230\u4e86\u6b66\u7384\u971c\u80cc\u540e\uff0c\u5ffd\u5730\u559d \u9053\uff1a\u201c\u6211\u7b49\u90fd\u4e0d\u662f\u82f1\u96c4\uff0c\u90a3\u4e48\u5f85\u6211\u8bf7\u6559\u59d1\u5a18\u7684\u82f1\u96c4\u624b\u6bb5\uff01\u201d\u62db\u6247\u4e00\u6307\uff0c\u7535\u5149\u77f3\u706b\u822c\u7684\u75be \u70b9\u6b66\u7384\u971c\u7684\u201c\u98ce\u7fbd\u7a74\u201d\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c\u4ee5\u4e1c\u65b9\u767d\u7684\u8eab\u4efd\uff0c\u5411\u4e00\u4e2a\u5c0f\u59d1\u5a18\u5077\u88ad\uff0c\u5b9e\u662f\u6709\u6b20\u5149\u660e\u78ca\u70d9\uff0c \u5ea7\u4e0a\u7fa4\u8c6a\uff0c\u5c0f\u4e4f\u76f4\u5fc3\u773c\u513f\u7684\u786c\u6c49\u5b50\uff0c\u4ed6\u4eec\u5bf9\u6b66\u7384\u971c\u867d\u7136\u6c14\u6124\uff0c\u5374\u4e5f\u4e0d\u503c\u4e1c\u65b9\u767d\u6240\u4e3a\uff0c\u4e0d \u5c11\u4eba\u90fd\u53eb\u51fa\u58f0\u6765\uff0c\u63d0\u9192\u6b66\u7384\u971c\u6ce8\u610f\u3002 \u3000\u3000\u6b66\u7384\u971c\u7adf\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u4e1c\u65b9\u767d\u731c\u60f3\u8fd9\u4f4d\u59d1\u5a18\u5fc5\u662f\u8fdb\u573a\u6363\u4e71\u6765\u7684\uff0c\u4ed6\u610f\u6b32 \u8ba8\u597d\u674e\u9038\uff0c\u8fd9\u4e00\u4e0b\u6109\u88ad\uff0c\u7528\u4e86\u5168\u8eab\u529f\u529b\uff0c\u53c8\u72e0\u53c8\u5feb\uff0c\u773c\u770b\u94c1\u6247\u5df2\u70b9\u5230\u6b66\u7384\u971c\u9888\u9879\u4e0b\u9762\u4e09 \u5bf8\u7684\u201c\u51e4\u7fbd\u7a74\u201d\uff0c\u6b66\u4e48\u971c\u5ffd\u5730\u6447\u4e86\u6447\u5934\uff0c\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5148\u751f\u592a\u62ac\u4e3e\u6211\u4e86\u3002 \u3000\u3000\u6211\u90a3\u6709\u4ec0\u4e48\u82f1\u96c4\u624b\u6bb5\u554a\uff01\u201d\u8bf4\u8bdd\u58f0\u4e2d\uff0c\u4f46\u542c\u5f97\u94ee\u7684\u4e00\u58f0\uff0c\u4e00\u80a1\u94f6\u5149\u7a81\u7136\u98de\u8d77\uff0c\u5c06\u4e1c \u65b9\u767d\u7684\u6247\u9aa8\u6253\u65ad\uff01 \u3000\u3000\u573a\u4e0a\u7fa4\u96c4\uff0c\u53ea\u6709\u8c37\u795e\u7fc1\u770b\u5f97\u660e\u767d\uff0c\u539f\u7c73\u5728\u6b66\u7384\u971c\u6447\u5934\u4e4b\u9645\uff0c\u5934\u4e0a\u7684\u4e00\u652f\u94f6\u7c2a\u6fc0\u5c04\u800c \u51fa\uff0c\u4e1c\u65b9\u767d\u7edd\u5bf9\u610f\u60f3\u4e0d\u5230\u654c\u4eba\u7684\u6697\u5668\u7adf\u4f1a\u5982\u6b64\u98de\u6765\uff0c\u4e0d\u4f46\u94c1\u6247\u7684\u6247\u9aa8\u7acb\u88ab\u6253\u65ad\uff0c\u4ed6\u7684\u864e \u53e3\u4e5f\u88ab\u94f6\u7c2a\u523a\u7a7f\u4e00\u4e2a\u5c0f\u5b54\uff0c\u4e00\u6761\u81c2\u818a\uff0c\u767b\u65f6\u540a\u4e86\u4e0b\u6765\uff0c\u4e0d\u80fd\u52a8\u5f39\u3002\u8fd9\u4e00\u6765\u8fde\u8c37\u795e\u7fc1\u4e5f\u4e0d \u7981\u6697\u6697\u5403\u60ca\uff0c\u8bd5\u60f3\u4e1c\u65b9\u81ea\u662f\u4f55\u7b49\u529f\u529b\uff1f\u94c1\u6247\u53c8\u662f\u7cbe\u94a2\u6253\u6210\uff0c\u800c\u4e14\u53c8\u662f\u51fa\u5176\u4e0d\u610f\u7684\u7a81\u7136\u4e00 \u51fb\uff0c\u7adf\u7136\u88ab\u8fd9\u5c11\u5973\u4e0d\u52a8\u58f0\u8272\u7684\u51fb\u5f97\u4e00\u8d25\u6d82\u5730\uff0c\u6247\u65ad\u4eba\u4f24\uff0c\u8fd9\u7b49\u6b66\u529f\uff0c\u8fde\u8c37\u795e\u7fc1\u81ea\u95ee\u4e5f\u672a \u5fc5\u80fd\u591f\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u6b66\u7384\u971c\u5df1\u8d70\u8fdb\u573a\u5fc3\uff0c\u8c37\u795e\u7fc1\u95ee\u9053\uff1a\u201c\u7ad9\u5a18\u8eab\u6000\u7edd\u6280\uff0c\u83ab\u975e\u662f\u60f3\u6765\u4e89\u593a\u8fd9 \u76df\u4e3b\u4e4b\u4f4d\u4e48\uff1f\u81ea\u6709\u82f1\u96c4\u5927\u4f1a\u4ee5\u6765\uff0c\u53ef\u4ece\u672a\u66fe\u6709\u8fc7\u5973\u5b50\u53c2\u52a0\uff0c\u8457\u662f\u59d1\u5a18\u593a\u5f97\u76df\u4e3b\u7684\u5b9d\u5ea7\uff0c \u54c8\uff0c\u54c8\uff01\u90a3\u4e5f\u53ef\u7b97\u5f97\u662f\u4e00\u4ef6\u6b66\u6797\u4f73\u8bdd\u554a\uff01\u201d\u8c37\u795e\u7fc1\u6b64\u8a00\u5b9e\u662f\u8981\u6fc0\u8d77\u7fa4\u96c4\u7684\u540c\u4ec7\u654c\u5ffe\uff0c\u679c \u7136\u7acb\u523b\u4fbf\u6709\u597d\u51e0\u4e2a\u8dc3\u51fa\uff0c\u8981\u5411\u6b66\u7384\u971c\u6311\u6218\u3002 \u3000\u3000\u6b66\u7384\u971c\u6446\u4e00\u6446\u4e86\uff0c\u6839\u672c\u5c31\u4e0d\u7406\u4f1a\u90a3\u4e00\u4e9b\u4eba\uff0c\u9762\u5411\u674e\u9038\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5e0c\u7f55\u8fd9\u4e2a\u76df \u4e3b\u4e4b\u4f4d\uff0c\u5728\u6211\u770b\u6765\uff0c\u5374\u662f\u4e00\u94b1\u4e0d\u503c\uff01\u6211\u82e5\u60f3\u505a\uff0c\u4e5f\u5f53\u505a\u771f\u6b63\u7684\u82f1\u96c4\u76df\u4e3b\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c \u9a82\u904d\u4e86\u573a\u4e2d\u8bf8\u4eba\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u9762\u8272\u4e00\u7aef\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8fd9\u8bf4\u8bdd\uff0c\u4e0d\u5acc\u592a\u81ea\u8d1f\u4e86\u4e48\uff1f\u8001\u592b\u8001\u77e3\uff0c\u4e0d \u6562\u4e89\u96c4\uff0c\u4f46\u4eca\u65e5\u5728\u573a\u7684\u90fd\u662f\u6b66\u6797\u4fca\u5f66\uff0c\u5176\u4e2d\u66f4\u6709\u597d\u51e0\u6d3e\u638c\u95e8\uff0c\u4f60\u8bf4\u4ed6\u4eec\u4e0d\u662f\u82f1\u96c4\uff0c\u4e0d\u77e5 \u5728\u59d1\u5a18\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u8981\u600e\u6837\u624d\u7b97\u82f1\u96c4\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u50b2\u7136\u4e00\u7b11\uff0c\u4ecd\u7136\u9762\u5bf9\u674e\u9038\u8bf4\u9053\uff1a\u201c\u82f1\u96c4\u5c82\u662f\u53ea\u5f92\u6043\u6b66\u529f\uff1f\u201d\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4e0d \u6043\u6b66\u529f\uff0c\u53c8\u6043\u4ec0\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u82f1\u96c4\u4e4b\u6240\u4ee5\u5f97\u4eba\u5c0a\u656c\uff0c\u6700\u91cd\u8981\u7684\u662f\u4ed6\u6709\u4fa0\u9aa8\u4ec1\u5fc3\uff0c \u82e5\u7136\u5f92\u6043\u6b66\u529f\uff0c\u90a3\u5c82\u4e0d\u6210\u4e86\u597d\u52c7\u6597\u72e0\u7684\u66b4\u5f92\uff1f\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u4f60\u53c8\u600e\u89c1\u5f97\u6211\u4eec\u90fd\u662f\u597d\u52c7 \u6597\u72e0\u4e4b\u5f92\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4f60\u4eec\u7684\u65b0\u76df\u4e3b\u5427\uff1f\u4ed6\u65e2\u662f\u4f60\u4eec\u82f1\u96c4\u4f1a\u4e0a\u516c\u63a8\u51fa\u6765\u7684\u76df \u4e3b\uff0c\u90a3\u4e48\u5e94\u8be5\u6700\u8db3\u4ee5\u4ee3\u8863\u4f60\u4eec\u5fc3\u76ee\u4e2d\u7684\u82f1\u96c4\u4e86\u3002\u8bd5\u95ee\u4ed6\u662f\u4ec0\u4e48\u82f1\u96c4\uff1f\u4ed6\u505a\u4e86\u76df\u4e3b\uff0c\u539f\u6765 \u662f\u60f3\u9a71\u4f7f\u4f60\u4eec\u66ff\u4ed6\u4e00\u5bb6\u4e00\u59d3\u4e89\u593a\u6c5f\u5c71\uff0c\u8fd9\u6837\u4e00\u7c73\u8981\u5bb3\u82e6\u4e86\u591a\u5c11\u767e\u59d3\uff0c\u54ea\u8c08\u5f97\u4e0a\u4ec0\u4e48\u4fa0\u9aa8 \u4ec1\u5fc3\uff1f\u201d \u3000\u3000\u674e\u9038\u6012\u9053\uff1a\u201c\u6b66\u5219\u5929\u8352\u6deb\u65e0\u9053\uff0c\u6b8b\u5bb3\u5fe0\u826f\uff0c\u5979\u6740\u4e86\u591a\u5c11\u4eba\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a \u201c\u5979\u6240\u6740\u7684\u6b63\u662f\u6b3a\u538b\u767e\u59d3\u7684\u4eba\uff0c\u9664\u66b4\u624d\u80fd\u5b89\u826f\uff0c\u6211\u8fd8\u5acc\u5979\u6740\u5f97\u5c11\u4e86\uff01\u201d\u5728\u573a\u7fa4\u8c6a\uff0c\u8fc7\u534a \u6570\u90fd\u662f\u7eff\u6797\u5927\u76d4\uff0c\u6b66\u7384\u971c\u6b64\u8bdd\u6b63\u662f\u5927\u5927\u89e6\u72af\u4e86\u4ed6\u4eec\u7684\u5fcc\u8bb3\uff0c\u767b\u65f6\u559d\u9a82\u4e4b\u58f0\u56db\u8d77\uff0c\u96c4\u5de8\u9f0e \u66f4\u662f\u4eba\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u5996\u5a62\u539f\u6765\u662f\u6b66\u5219\u5929\u6d3e\u6765\u7684\u4eba\uff0c\u4e0d\u8981\u548c\u5979\u591a\u8bf4\u5e9f\u8bdd\uff0c\u5feb\u5feb\u5c06\u5979\u5e72\u6389\u4e86 \u4fbf\u662f\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u539f\u6765\u4f60\u7b49\u82f1\u96c4\uff0c\u5c31\u662f\u4ee5\u4f17\u51cc\u5be1\uff0c\u6043\u5f3a\u6b3a\u5f31\u7684\u4e48\uff1f\u597d\u5427\uff0c \u4f60\u4eec\u65e2\u8981\u7fa4\u6bb4\uff0c\u5c31\u8bf7\u4e0a\u6765\uff0c\u6211\u4e5f\u770b\u770b\u4f60\u4eec\u7a76\u7adf\u662f\u600e\u6837\u7684\u82f1\u96c4\uff1f\u201d \u3000\u3000\u674e\u9038\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u8bf7\u6682\u65f6\u9000\u4e0b\uff0c\u6211\u6765\u9886\u6559\u8fd9\u4f4d\u59d1\u5a18\u7684\u9ad8\u62db\uff01\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a \u201c\u5230\u5e95\u8fd8\u662f\u76df\u4e3b\u6709\u4e9b\u6c14\u5ea6\uff0c\u65e2\u8981\u6bd4\u6b66\uff0c\u90a3\u4e48\u8bf7\u4f60\u5212\u51fa\u9053\u6765\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u59d1\u5a18\u662f\u5ba2\uff0c\u4e3b \u5f53\u8ba9\u5ba2\uff0c\u6089\u4f9d\u5c0a\u610f\u4fbf\u662f\uff0c\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u770b\u4f60\u521a\u624d\u4f7f\u5251\u597d\u4f3c\u8fd8\u4f7f\u5f97\u4e0d\u574f\uff0c\u54b1\u4eec\u5c31\u6bd4\u5251 \u5427\u3002\u4f60\u82e5\u8f93\u4e86\uff0c\u6562\u8bf7\u4f60\u5c06\u8fd9\u4e2a\u4ec0\u4e48\u82f1\u96c4\u5927\u4f1a\u7acb\u523b\u89e3\u6563\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4e07\u4e00\u59d1\u5a18\u5931\u4e4e\uff0c\u6211 \u4fa5\u5e78\u80dc\u4e86\u4e00\u62db\u534a\u5f0f\u5462\uff1f\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u6211\u82e5\u5728\u5341\u62db\u4e4b\u5185\u80dc\u4e0d\u4e86\u4f60\uff0c\u6211\u7ed9\u5728\u573a\u7684\u8bf8\u4f4d\u5927 \u82f1\u96c4\u90fd\u78d5\u4e09\u4e2a\u54cd\u5934\uff01\u201d\u674e\u9038\u672c\u6765\u65e0\u5fc5\u80dc\u7684\u628a\u63e1\uff0c\u542c\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6012\u6781\u53cd\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d \u6781\uff0c\u597d\u6781\uff01\u59d1\u5a18\u82e5\u7136\u5728\u5341\u62db\u4e4b\u5185\u8d62\u5f97\u4e86\u6211\uff0c\u6211\u4e5f\u7ed9\u4f60\u78d5\u4e09\u4e2a\u54cd\u5934\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u53ef \u4e0d\u7a00\u7f55\u4f60\u7684\u54cd\u5934\uff0c\u4f60\u8f93\u4e86\uff0c\u8fd9\u82f1\u96c4\u5927\u4f1a\u4e0d\u4f46\u8981\u7acb\u523b\u7ed3\u675f\uff0c\u5728\u573a\u7684\u8bf8\u4f4d\u5927\u82f1\u96c4\uff0c\u4ee5\u540e\u8bf7\u4e5f \u4e0d\u5fc5\u518d\u5728\u6c5f\u6e56\u4e0a\u4e22\u4eba\u73b0\u4e16\u5566\uff01\u4f60\u4ee5\u76df\u4e3b\u7684\u8d44\u683c\uff0c\u6562\u4ee3\u8868\u4ed6\u4eec\u7b54\u5e94\u4e00\u53e5\u8bdd\u4e48\uff1f\u201d\u573a\u4e2d\u7fa4\u96c4\uff0c \u90fd\u89c1\u8fc7\u674e\u9038\u8d85\u5999\u7684\u5251\u672f\uff0c\u8fde\u8c37\u795e\u7fc1\u5728\u5185\uff0c\u4eba\u4eba\u90fd\u662f\u8fd9\u6837\u60f3\u9053\uff1a\u201c\u5341\u62db\u4e4b\u5185\uff0c\u674e\u9038\u51b3\u65e0\u5931 \u8d25\u4e4b\u7406\uff01\u201d\u7eb7\u7eb7\u53eb\u9053\uff1a \u3000\u3000\u201c\u8fd9\u8bdd\u4f55\u5fc5\u591a\u8bf4\uff0c\u54b1\u4eec\u7684\u76df\u4e3b\u82e5\u90fd\u8f93\u4e86\uff0c\u54b1\u4eec\u8fd8\u6709\u8138\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u4e48\uff1f\u201d \u3000\u3000\u674e\u9038\u5f97\u5230\u4f17\u4eba\u62e5\u62a4\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u201c\u98d5\u201d\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u5b9d\u5251\u3002 \u3000\u3000\u7acb\u4e86\u4e00\u4e2a\u95e8\u6237\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8bdd\u5df2\u8bf4\u660e\u3001\u8bf7\u59d1\u5a18\u8fdb\u62db\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u661f\u7738\u4e00\u76fc\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u5148\u8ba9\u4f60\u4e09\u62db\uff01\u201d\u674e\u9038\u6c14\u9053\uff1a\u201c\u4ec0\u4e48\uff0c \u4f60\u8fd8\u8981\u5148\u8ba9\u4e09\u62db\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5148\u8ba9\u4e09\u62db\uff0c\u770b\u770b\u4f60\u8fd9\u4f4d\u82f1\u96c4\u76df\u4e3b\u7684\u624b\u6bb5\u3002\u6211\u82e5 \u7ed9\u4f60\u4e00\u5251\u523a\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u90a3\u662f\u6211\u6d3b\u8be5\uff0c\u4e0d\u5fc3\u4f60\u6765\u4e3a\u6211\u987e\u8651\u3002\u5927\u82f1\u96c4\uff0c\u4e0d\u5fc5\u5ba2\u6c14\u554a\uff01\u5582\uff0c \u5582\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u8fdb\u62db\uff1f\u201d \u3000\u3000\u674e\u9038\u6db5\u517b\u867d\u597d\uff0c\u4ea6\u81ea\u7ed9\u5979\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\u3002\u957f\u5251\u4e00\u6307\uff0c\u9053\u58f0\uff1a \u3000\u3000\u201c\u770b\u62db\uff01\u201d\u500f\u7684\u4e00\u5251\u523a\u51fa\uff0c\u8981\u6311\u5f00\u5979\u7684\u8863\u5e26\u3002\u6b66\u7384\u971c\u67f3\u8170\u4e00\u626d\uff0c\u4e00\u4e2a\u201c\u98ce\u987a\u843d\u82b1\u201d \u4e4b\u5f0f\uff0c\u8eab\u6cd5\u7f8e\u5999\u4e4b\u6781\uff0c\u8f7b\u8f7b\u7684\u4fbf\u95ea\u8fc7\u4e86\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u76df\u4e3b\u8eab\u4efd\uff0c\u4f7f\u7684\u7adf\u662f\u8fd9\u822c\u8f7b\u8584\u7684 \u5251\u6cd5\u4e48\uff1f\u201d\u674e\u9038\u8fd9\u4e00\u62db\u672a\u51fa\u6740\u624b\uff0c\u6b63\u662f\u4e3a\u4e86\u4ed6\u81ea\u6043\u8eab\u4efd\uff0c\u6545\u6b64\u53ea\u60f3\u4f7f\u5979\u7565\u53d7\u6298\u8fb1\u4fbf\u7b97\uff0c \u54ea\u77e5\u8fd9\u95ea\u7535\u822c\u7684\u4e00\u5251\uff0c\u7adf\u7ed9\u5979\u8f7b\u6613\u95ea\u5f00\uff0c\u8fd8\u906d\u4e86\u5979\u4e00\u756a\u595a\u843d\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u7b2c\u4e8c\u5251\u518d\u4e0d\u7559 \u60c5\uff0c\u4e00\u4e2a\u201c\u4e0a\u6b65\u4e03\u661f\u201d\uff0c\u500f\u7684\u4fbf\u662f\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u5251\u5c16\u6643\u52a8\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u76f4\u523a\u54bd\u5589\u3002 \u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u5251\u8fd8\u6709\u70b9\u9053\u7406\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u674e\u9038\u6dae\u7684\u4e00\u5251\u4ece\u5979\u8863\u8896\u65c1\u8fb9\u524a\u8fc7\uff0c\u5251 \u5149\u7ed9\u5979\u8863\u8896\u4e00\u5e26\uff0c\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u4ecd\u7136\u6ca1\u6709\u523a\u4e2d\u3002\u674e\u9038\u6740\u5f97\u6027\u8d77\uff0c\u7b2c\u4e09\u5251\u8fde\u73af\u653b\u51fa\uff0c\u7528\u7684\u662f \u201c\u98de\u4e91\u63a3\u7535\u201d\u7684\u6740\u624b\u4f38\u62db\uff0c\u5251\u52bf\u72e0\u731b\u51c6\u75be\uff0c\u7aef\u7684\u662f\u6b66\u6797\u4e2d\u7f55\u89c1\u7684\u5251\u6cd5\uff0c\u7fa4\u96c4\u5f20\u5f00\u4e86\u53e3\uff0c \u6b63\u8981\u559d\u91c7\uff0c\u5ffd\u542c\u5f97\u201c\u94ee\u201d\u7684\u58f0\uff0c\u674e\u9038\u4e00\u62db\u521a\u731b\u65e0\u4f26\u7684\u5251\u62db\uff0c\u7adf\u88ab\u6b66\u7384\u971c\u4f38\u51fa\u7ea4\u7ea4\u53cc\u6307\uff0c \u4e00\u5f39\u5f39\u5f00\uff01 \u3000\u3000\u51c6\u5907\u4e3a\u674e\u9038\u559d\u91c7\u7684\u4eba\uff0c\u4f38\u60c5\u6cae\u4e27\uff0c\u4e00\u4e2a\u4e2a\u597d\u4f3c\u6ce5\u6714\u6728\u96d5\u4e00\u822c\uff0c\u5f20\u5927\u4e86\u5634\u5df4\uff0c\u5374\u662f\u534a \u70b9\u58f0\u97f3\u4e5f\u53d1\u4e0d\u51fa\u6765\uff01\u8bb8\u591a\u6b66\u672f\u540d\u5bb6\u5fc3\u4e2d\u90fd\u5728\u6697\u6697\u5600\u5495\uff1a\u201c\u5979\u600e\u4e48\u4e5f\u4f1a\u91d1\u521a\u6307\u7684\u529f\u592b\uff1f\u201d \u91d1\u521a\u6307\u7684\u529f\u592b\uff0c\u6700\u5c11\u8981\u7ec3\u5341\u5e74\u4ee5\u4e0a\uff0c\u624d\u6709\u5c0f\u6210\uff0c\u800c\u4e14\u8fd8\u5f97\u5185\u529f\u5177\u6709\u76f8\u5f53\u57fa\u7840\u4e4b\u540e\uff0c\u624d\u80fd \u5f00\u59cb\u7ec3\u4e60\u3002\u8fd9\u6b66\u7384\u971c\u6700\u591a\u4e0d\u8fc7\u4e8c\u5341\u5c81\uff0c\u5b9e\u4e0d\u77e5\u5979\u662f\u600e\u4e48\u7ec3\u7684\u3002 \u3000\u3000\u5c31\u5728\u7fa4\u96c4\u60ca\u8be7\u3001\u5664\u82e5\u5bd2\u8749\u4e4b\u9645\uff0c\u6b66\u7384\u971c\u4e00\u58f0\u5a07\u7b11\uff0c\u500f\u7684\u4e5f\u62d4\u51fa\u4e86\u4e00\u628a\u4e09\u5c3a\u9752\u950b\uff0c\u67d4 \u58f0\u8bf4\u9053\uff1a\u201c\u76df\u4e3b\u4f60\u5c0f\u5fc3\u63a5\u6211\u5341\u62db\uff01\u201d\u201c\u62db\u201d\u5b57\u521a\u521a\u51fa\u53e3\uff0c\u4f46\u89c1\u9752\u5149\u75be\u95ea\uff0c\u5979\u672a\u66fe\u79fb\u52a8\u534a \u6b65\uff0c\u9661\u7136\u95f4\u4fbf\u662f\u4e00\u5251\u523a\u6765\uff0c\u5251\u52bf\u5947\u8be1\u4e4b\u6781\uff01\u4f46\u89c1\u5979\u5251\u5c16\u98a4\u52a8\uff0c\u7adf\u662f\u5728\u4e00\u62db\u4e4b\u5185\uff0c\u6697\u85cf\u516d \u5f0f\uff0c\u8fde\u523a\u5bf9\u65b9\u4e03\u5904\u5927\u7a74\uff01 \u3000\u3000\u5e78\u4e8f\u674e\u9038\u5728\u5251\u672f\u4e0a\u4e5f\u6709\u7cbe\u6e5b\u7684\u9020\u8be3\uff0c\u767e\u5fd9\u4e2d\u5c55\u51fa\u6700\u7cbe\u5999\u7684\u62a4\u8eab\u5251\u6cd5\uff0c\u4e00\u62db\u201c\u5367\u864e\u51cf \u9f99\u201d\uff0c\u5b9d\u5251\u6296\u8d77\u4e00\u9053\u94f6\u8679\uff0c\u4fe8\u5982\u7389\u87d2\u56f4\u8eab\uff0c\u906e\u62e6\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u800c\u4e14\u5904\u5904\u6697\u85cf\u53cd\u51fb\u4e4b\u529b\u3002 \u53cc\u5251\u76f8\u4ea4\uff0c\u4f46\u542c\u5f97\u79d1\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u9707\u5f97\u5404\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002\u539f\u6765\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u4ed6 \u4eec\u7684\u957f\u5251\u5df2\u63a5\u89e6\u4e86\u4e03\u4e0b\uff0c\u800c\u8fd9\u4e0d\u8fc7\u4ec5\u4ec5\u662f\u7b2c\u4e00\u62db\uff01 \u3000\u3000\u5982\u6b64\u590d\u6742\u591a\u53d8\u800c\u53c8\u8fc5\u82e5\u98d8\u98ce\u7684\u5251\u672f\uff0c\u5728\u573a\u7fa4\u96c4\uff0c\u5f53\u771f\u662f\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\uff01 \u3000\u3000\u8fd8\u6709\u4e00\u6837\u4ee4\u674e\u9038\u5403\u60ca\u4e4b\u5904\uff0c\u4ed6\u7684\u5251\u4e43\u662f\u5927\u5185\u5b9d\u5251\uff0c\u662f\u5f53\u5e74\u592a\u5b97\u7687\u5e1d\u56e0\u4ed6\u5e08\u7236\u62a4\u9a7e\u6709 \u529f\uff0c\u8d50\u7ed9\u4ed6\u5e05\u7236\u7684\u3002\u800c\u6b66\u7384\u971c\u6240\u4f7f\u7684\uff0c\u4e0d\u8fc7\u662f\u4e00\u628a\u666e\u901a\u7684\u9752\u94a2\u5251\uff0c\u63a5\u89e6\u4e86\u4e03\u4e0b\uff0c\u5bf9\u65b9\u7684 \u5251\u7adf\u662f\u4e00\u65e0\u4f24\u635f\uff0c\u770b\u6765\u4e43\u662f\u56e0\u4e3a\u5979\u51fa\u5251\u592a\u5feb\uff0c\u4e00\u6cbe\u5373\u8d70\uff0c\u81ea\u5df1\u7684\u5e7c\u529b\u8fd8\u672a\u900f\u5251\u5c16\uff0c\u52b2\u529b \u6765\u5230\u3001\u5b9d\u5251\u7684\u5a01\u529b\u81ea\u662f\u4e0d\u80fd\u53d1\u6325\u3002 \u3000\u3000\u674e\u9038\u6b63\u81ea\u76d8\u7b97\u62b5\u5fa1\u4e4b\u6cd5\uff0c\u6b66\u7384\u971c\u53c8\u662f\u4e00\u58f0\u5a07\u7b11\uff1a\u201c\u7b2c\u4e8c\u62db\u6765\u5566\uff01\u201d\u674e\u9038\u4e0d\u6562\u548c\u5979\u62a2 \u653b\uff0c\u8fd0\u8db3\u771f\u529b\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u8fd9\u56de\u53cc\u5251\u76f8\u63a5\uff0c\u5374\u662f\u6beb\u65e0\u58f0\u54cd\uff0c\u6b66\u7384\u971c\u7684\u90a3\u628a\u5251\u7adf\u4f3c\u7eb8\u7247\u4e00 \u822c\u7c98\u5728\u4ed6\u7684\u5251\u4e0a\uff0c\u674e\u9038\u5ffd\u89c9\u4e00\u670d\u529b\u9053\u5411\u5916\u7275\u5f15\uff0c\u5b9d\u5251\u4e0d\u7531\u81ea\u5df1\u7684\u88ab\u5979\u7684\u957f\u5251\u5e26\u52a8\uff0c\u8f6c\u4e86 \u51e0\u8f6c\uff0c\u51e0\u4e4e\u5c31\u8981\u8131\u624b\u98de\u53bb\uff01\u674e\u9038\u6025\u5fd9\u4f7f\u4e86\u4e2a\u201c\u5316\u201d\u5b57\u51b3\uff0c\u5b9d\u5251\u5411\u524d\u4e00\u9001\uff0c\u987a\u52bf\u53cd\u62bd\uff0c\u597d \u4e0d\u5bb9\u6613\u624d\u6446\u8131\u4e86\u654c\u4eba\u7684\u7c98\u52b2\uff0c\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff01 \u3000\u3000\u6b66\u7384\u971c\u5fae\u7b11\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4e24\u62db\u8fd8\u7b97\u4e0d\u4fd7\uff01\u201d\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff0c\u8fde\u73af\u4e09 \u62db\uff0c\u5251\u5149\u98d8\u77a5\uff0c\u6307\u4e1c\u6253\u56db\uff0c\u6307\u5357\u6253\u5317\uff0c\u674e\u9038\u51dd\u795e\u5e94\u4ed8\uff0c\u4e09\u62db\u4e00\u8fc7\uff0c\u5df2\u662f\u5927\u6c57\u6dcb\u6f13\uff0c\u6c14\u529b \u8017\u4e86\u4e00\u534a\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u7528\u5fc3\u5e94\u4ed8\uff0c\u8fd8\u6709\u4e94\u62db\uff01\u201d\u7b2c\u516d\u62db\u5251\u52bf\u751a\u7f13\uff0c\u4f46\u90a3\u822c\u538b\u529b\u5374\u662f\u6c89\u91cd\u96be\u5f53\uff0c \u674e\u9038\u54ac\u7d27\u7259\u6839\uff0c\u7528\u4e86\u4e5d\u725b\u4e8c\u864e\u4e4b\u529b\uff0c\u582a\u582a\u5316\u89e3\uff0c\u6b66\u7384\u971c\u5251\u62db\u4e00\u53d8\uff0c\u7b2c\u4e03\u62db\u53c8\u4f3c\u98de\u4e91\u63a3\u7535 \u822c\u7684\u523a\u6765\uff01 \u3000\u3000\u674e\u9038\u6025\u5fd9\u8e0f\u4e2a\u201c\u5012\u8e29\u4e03\u661f\u6b65\u201d\uff0c\u5de6\u811a\u5f80\u53f3\u4e00\u6ed1\uff0c\u5251\u968f\u8eab\u8f6c\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u98de\u7011\u6d41\u8679\u201d\uff0c \u4ed6\u5728\u52bf\u7a77\u529b\u62d9\u4e4b\u65f6\uff0c\u5c45\u7136\u8fd8\u80fd\u4f7f\u51fa\u5982\u6b64\u7cbe\u7eaf\u7684\u5251\u672f\uff0c\u6b66\u7384\u971c\u5fc3\u4e2d\u4e0d\u7981\u6697\u6697\u9053\u4e2a\u201c\u597d\u201d\u5b57\u3002 \u4f46\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u674e\u9038\u7684\u8863\u8896\u5df2\u88ab\u6b66\u7384\u971c\u524a\u53bb\u4e86\u4e00\u6bb5\uff0c\u8fd9\u8fd8\u662f\u7531\u4e8e\u4ed6\u5e94\u4ed8 \u5f97\u5b9c\uff0c\u8981\u4e0d\u7136\u8fd9\u4e00\u5251\u4f5c\u4e2d\u4ed6\u7684\u624b\u8155\u4e2a\u53ef\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u53ea\u6709\u6700\u540e\u4e09\u62db\u5566\uff0c\u4f60\u5c0f\u5fc3\u5e94\u4ed8\uff0c\u63a5\u5f97\u4f4f\u561b\uff0c\u6211\u5411\u4f60\u78d5\u5934\uff1b\u63a5\u4e0d\u4f4f\u561b\uff0c \u563f\u563f\uff0c\u4f60\u8fd9\u73ed\u82f1\u96c4\u4f1a\u4e0a\u7684\u5927\u82f1\u96c4\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u53ef\u522b\u518d\u5728\u6c5f\u6e56\u4e0a\u53bb\u4eba\u73b0\u773c\u5566\uff01\u201d\u674e\u9038\u5fc3\u60c5 \u6c89\u91cd\u4e4b\u6781\uff0c\u4f46\u89c1\u6b66\u7384\u971c\u5251\u950b\u4e00\u5c55\uff0c\u500f\u7136\u538b\u4e0b\uff0c\u674e\u9038\u6a2a\u5251\u4e00\u5c01\uff0c\u6b66\u7384\u971c\u660e\u660e\u77e5\u9053\u4ed6\u662f\u5b9d\u5251\uff0c \u5251\u52bf\u5374\u4e1d\u6beb\u4e0d\u53d8\uff0c\u8f7b\u8f7b\u4e00\u642d\uff0c\u53cc\u5251\u5e73\u4ea4\uff0c\u62ff\u634f\u65f6\u5019\u3002 \u3000\u3000\u6070\u5230\u597d\u5904\uff0c\u674e\u9038\u7adf\u6765\u4e0d\u53ca\u53cd\u5c55\u5251\u950b\u524a\u5979\u7684\u5251\uff0c\u4fbf\u7ed9\u5979\u7684\u5251\u538b\u4f4f \u3000\u3000\u674e\u9038\u8fd0\u8db3\u771f\u529b\uff0c\u60f3\u63a8\u5f00\u5979\u4e00\u5251\uff0c\u90a3\u91cc\u80fd\u591f\uff1f\u4f46\u89c9\u5bf9\u65b9\u90a3\u4e00\u628a\u8584\u5f97\u900f\u660e\u7684\u9752\u94a2\u5251\uff0c\u7adf \u4f3c\u5343\u65a4\u77f3\u67f1\u4e00\u822c\uff0c\u91cd\u91cd\u7684\u538b\u5728\u81ea\u5df1\u7684\u5251\u4e0a\uff0c\u975e\u4f46\u4e0d\u80fd\u63a8\u5f00\uff0c\u751a\u81f3\u60f3\u628a\u5b9d\u5251\u62bd\u51fa\u6765\u4e5f\u4e0d\u53ef \u80fd\uff0c\u53cc\u5251\u7c98\u4f4f\uff0c\u4e24\u80a1\u5927\u529b\u76f8\u690e\u76f8\u538b\uff0c\u7adf\u4f3c\u94f8\u7194\u6210\u4e3a\u4e00\u67c4\u5251\u4e86\u3002 \u3000\u3000\u7fa4\u96c4\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u4f46\u89c1\u6b66\u7384\u971c\u5fae\u9732\u7b11\u5bb9\uff0c\u6c14\u5b9a\u795e\u95f2\uff0c\u66f4\u663e\u5f97\u98ce\u534e\u7edd\u4e16\uff1b\u800c\u674e\u9038\u5219 \u662f\u6c57\u6ef4\u5982\u96e8\uff0c\u6e7f\u900f\u8863\u886b\uff0c\u53cc\u811a\u597d\u4f3c\u6253\u6869\u4e00\u6837\uff0c\u7262\u7262\u9489\u5728\u5730\u4e0a\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u5730\u4e0a\u5df2\u7ed9\u4ed6\u8e0f \u5f97\u51f9\u9677\u6210\u69fd\uff0c\u6ce5\u571f\u63a9\u8fc7\u811a\u80cc\uff0c\u4ed6\u7684\u8eab\u8eaf\u4e5f\u4e0d\u4f4f\u540e\u5f2f\uff0c\u90a3\u628a\u5b9d\u5251\u4e5f\u968f\u4e4b\u6e10\u6e10\u4e0b\u6c89\u3002 \u3000\u3000\u674e\u9038\u7684\u8eab\u8eaf\u5f2f\u540e\u4e00\u5206\uff0c\u7fa4\u96c4\u7684\u5fc3\u60c5\u4e5f\u968f\u7740\u6c89\u91cd\u4e00\u5206\uff0c\u5168\u573a\u5bc2\u9759\u65e0\u58f0\uff0c\u5f53\u771f\u662f\u4e00\u6839\u9488 \u8dcc\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\u3002\u6240\u6709\u7684\u76ee\u5149\u90e8\u6ce8\u89c6\u7740\u8fd9\u4e24\u628a\u5251\uff0c\u8981\u77e5\u9053\u8fd9\u4e00\u4ed7\u4e0d\u4f46\u662f\u5173\u7cfb\u674e\u9038\u7684 \u8363\u8fb1\uff0c\u800c\u4e14\u662f\u5173\u7cfb\u7740\u6240\u6709\u8bf8\u4eba\u7684\u547d\u8fd0\uff01 \u3000\u3000\u9661\u7136\u95f4\u90a3\u9f99\u4e09\u5149\u751f\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u8dc3\u51fa\u573a\u5fc3\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5996\u5a62\u5206\u660e\u662f\u6b66\u5219\u5929\u7684\u4eba\uff0c\u6709\u4e86\u5979\u5c31\u6ca1\u6709\u6211\u4eec\uff0c\u5404\u4f4d\u5f1f\u5144\u8fd8\u548c\u5979\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff1f\u201d \u4ed6\u540c\u4f19\u7684\u4e00\u73ed\u7eff\u6797\u5927\u76d7\uff0c\u8f70\u7136\u79f0\u662f\uff0c\u970e\u65f6\u5200\u67aa\u5e76\u4e3e\uff0c\u5251\u621f\u9f50\u65bd\uff0c\u5341\u51e0\u822c\u5175\u5668\u540c\u65f6\u5411\u6b66\u7384 \u971c\u8eab\u4e0a\u6233\u53bb\uff01\u8c37\u795e\u7fc1\u8fdb\u9000\u4e24\u96be\uff0c\u8981\u60f3\u559d\u6b62\uff0c\u5374\u53c8\u6015\u674e\u9038\u771f\u4e2a\u652f\u6301\u4e0d\u4f4f\uff0c\u5728\u573a\u8bf8\u4eba\uff0c\u8fde\u81ea \u5df1\u5728\u5185\uff0c\u90fd\u8981\u9000\u51fa\u6c5f\u6e56\uff1b\u82e5\u4e0d\u559d\u4e0a\uff0c\u5219\u5927\u5927\u6709\u5931\u81ea\u5df1\u8001\u76df\u4e3b\u7684\u8eab\u4efd\uff0c\u8fd9\u4e2a\u82f1\u96c4\u5927\u4f1a\uff0c\u4e5f \u5c31\u53d8\u6210\u7b11\u8bdd\u4e00\u573a\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u6781\u5ea6\u7d27\u5f20\u4e4b\u9645\uff0c\u6b66\u7384\u971c\u4e00\u58f0\u5a07\u7b11\uff0c\u9752\u950b\u5251\u500f\u7684\u79fb\u5f00\u3002 \u3000\u3000\u674e\u9038\u6b63\u5728\u65bd\u5c55\u5343\u65a4\u5760\u7684\u529f\u592b\u652f\u6301\uff0c\u538b\u529b\u9aa4\u6d88\uff0c\u4ed6\u7684\u5168\u8eab\u52b2\u529b\u90fd\u5411\u5730\u4e0b\u4e18\u53bb\uff0c\u767b\u65f6\u53cc \u811a\u6df1\u9677\uff0c\u8fc7\u4e86\u819d\u76d6\uff01 \u3000\u3000\u4f46\u89c1\u6b66\u7384\u971c\u5728\u5200\u67aa\u5251\u621f\u56f4\u653b\u4e4b\u4e0b\uff0c\u8eab\u5b50\u51cc\u7a7a\u98de\u8d77\uff0c\u6717\u6717\u7b11\u9053\uff0c\u201c\u597d\u5440\uff0c\u6211\u4eca\u65e5\u89c1\u8bc6 \u4f60\u4eec\u8fd9\u73ed\u82f1\u96c4\u7684\u672c\u9886\u4e86\uff01\u201d\u9752\u950b\u5251\u51cc\u7a7a\u523a\u535c\uff0c\u5251\u82b1\u6735\u6735\uff0c\u5b9b\u5982\u9ed1\u591c\u7e41\u661f\uff0c\u6b92\u843d\u5982\u96e8\uff01 \u3000\u3000\u767b\u65f6\u626c\u8d77\u4e86\u4e00\u7247\u60e8\u5389\u7684\u53eb\u58f0\uff0c\u6b66\u7384\u971c\u559d\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u4eec\u7684\u6027\u547d\uff0c\u53ef\u4e5f\u4e0d\u80fd\u518d\u8ba9\u4f60 \u4eec\u4f5c\u6076\uff01\u201d\u539f\u6765\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5979\u5df1\u4e00\u8fde\u523a\u4f24\u4e86\u4e03\u540d\u7a77\u51f6\u6781\u6076\u7684\u7eff\u6797\u5927\u76d7\uff0c\u5979\u51fa\u5251\u7684\u72e0 \u8fa3\uff0c\u7aef\u7684\u662f\u4e16\u7f55\u5176\u4f26\uff0c\u6bcf\u4e00\u5251\u90fd\u662f\u523a\u4e2d\u5bf9\u65b9\u7684\u5173\u8282\u8981\u5bb3\uff0c\u867d\u7136\u4e0d\u8db3\u81f4\u547d\uff0c\u4f46\u90a3\u8eab\u6b66\u529f\u5374 \u5df2\u5e9f\u4e86\u3002 \u3000\u3000\u9f99\u4e09\u53d1\u6025\u5927\u53eb\u9053\uff1a\u201c\u7ebf\u4e0a\u7684\u670b\u53cb\u5e76\u80a9\u4e0a\u554a\uff01\u201d\u56f4\u653b\u8005\u6108\u6765\u6108\u591a\uff0c\u6b66\u7384\u971c\u8fd0\u5251\u5982\u98ce\uff0c \u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u8f6c\u77ac\u95f4\u53c8\u523a\u7ffb\u4e86\u51e0\u540d\u597d\u624b\uff0c\u4f46\u91cd\u91cd\u56f4\u653b\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u4ea6\u662f\u96be\u4ee5\u6740 \u51fa\u91cd\u56f4\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u4e00\u67c4\u5355\u5200\u659c\u523a\u6740\u5230\uff0c\u6b66\u7384\u971c\u53cd\u624b\u4e00\u5251\uff0c\u7adf\u7136\u7ed9\u4ed6\u6321\u5f00\uff0c\u8fd9\u662f\u6d2a\u6cfd\u5e2e\u7684\u5e2e\u4e3b\u5355 \u5929\u96c4\uff0c\u9f99\u4e09\u8d81\u52bf\u4e00\u94a9\uff0c\u5c06\u6b66\u7384\u971c\u80a9\u4e0a\u7684\u8863\u88f3\u7834\u4e00\u7247\u3002\u8fd9\u4e2a\u9f99\u4e09\u4e43\u662f\u8c37\u795e\u7fc1\u7684\u5f97\u610f\u5f1f\u5b50\uff0c \u5de6\u624b\u4f7f\u864e\u5934\u94a9\uff0c\u53f3\u624b\u4f7f\u6448\u94c1\u62d0\uff0c\u6c5f\u6e56\u8c6a\u6770\u4e00\u534a\u662f\u56e0\u4e3a\u4ed6\u7684\u6b66\u529f\u786e\u5b9e\u9ad8\u5f3a\uff0c\u4e00\u534a\u5219\u56e0\u4e3a\u770b \u5728\u8c37\u795e\u7fc1\u7684\u4efd\u4e0a\uff0c\u5bf9\u4ed6\u751a\u4e3a\u5c0a\u656c\uff0c\u4ed6\u7684\u540d\u5b57\u53eb\u505a\u9f99\u7ecd\u5723\uff0c\u6392\u884c\u7b2c\u4e09\uff0c\u5927\u5bb6\u4fbf\u79f0\u547c\u4ed6\u505a\u9f99 \u4e09\u5148\u751f\u800c\u4e0d\u540d\u3002 \u3000\u3000\u6b66\u7384\u971c\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u501a\u591a\u4e3a\u80dc\uff0c\u6211\u975e\u53eb\u4f60\u4eec\u90fd\u7f34\u68b0\u4e0d\u53ef\uff01\u201d\u9752\u94a2\u5251\u6325\u4e86\u4e00\u9053 \u5706\u5f27\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5148\u628a\u5355\u5929\u96c4\u7684\u90a3\u67c4\u5355\u5200\u524a\u65ad\uff0c\u9f99\u7ecd\u5723\u53d8\u62db\u5f97\u5feb\uff0c\u864e\u5934\u94a9\u7684\u94a2\u7259\u4e5f\u88ab\u524a \u53bb\u4e86\u4e24\u9f7f\uff0c\u4e0d\u7981\u9a87\u7136\uff0c\u75be\u9000\u4e09\u6b65\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u7fa4\u8c6a\u9762\u524d\u7a81\u7136\u98de\u8d77\u4e86\u4e00\u7247\u5f69\u971e\uff0c\u6b66\u7384 \u971c\u624b\u4e0a\u5df2\u591a\u4e86\u4e00\u6761\u7ef8\u5e26\uff0c\u7ea2\u7ef8\u7ffb\u5377\uff0c\u773c\u82b1\u7f2d\u4e71\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u6709\u4e03\u516b\u6761\u5175\u5668\u88ab\u5979\u5377\u53bb\uff0c \u63b7\u4e8e\u5730\u4e0a\uff0c\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u9f99\u4e09\u53eb\u9053\uff1a\u201c\u6768\u5be8\u4e3b\uff0c\u845b\u516d\u54e5\uff0c\u9093\u524d\u8f88\uff0c\u4f60\u4eec\u6765\u5440\uff01\u201d\u8fd9\u51e0\u4e2a\u4eba\u90fd\u662f\u5728\u573a\u7684\u4e00\u6d41\u9ad8 \u624b\uff0c\u521a\u624d\u56e0\u4e3a\u987e\u7740\u8eab\u4efd\uff0c\u4e0d\u613f\u53c2\u52a0\u56f4\u653b\u7684\u3002\u8fd9\u65f6\u88ab\u9f99\u4e09\u5148\u751f\u70b9\u540d\u53eb\u5524\uff0c\u788d\u4e8e\u60c5\u9762\uff0c\u53ea\u5f97 \u51fa\u6218\u3002\u518d\u52a0\u4e0a\u5176\u4ed6\u56f4\u653b\u7684\u597d\u624b\uff0c\u7acb\u5373\u628a\u5f62\u52bf\u7a33\u4f4f\uff0c\u53c8\u628a\u6b66\u7384\u971c\u9677\u8fdb\u4e86\u91cd\u56f4\u3002 \u3000\u3000\u6b66\u7384\u971c\u6325\u7ef8\u821e\u5251\uff0c\u5251\u5149\u6325\u970d\uff0c\u7ef8\u5f71\u7ffb\u98de\uff0c\u4e0d\u65f6\u6709\u4eba\u60e8\u53eb\u5012\u5730\uff0c\u5175\u5668\u98de\u7a7a\uff0c\u6218\u5230\u6fc0\u5904\uff0c \u4f46\u89c1\u5251\u6c14\u7eb5\u6a2a\uff0c\u6f2b\u7a7a\u7ef8\u5f71\uff0c\u56db\u9762\u516b\u65b9\u90fd\u662f\u6b66\u7384\u971c\u7684\u5f71\u5b50\uff0c\u4e00\u4e2a\u4eba\u5c31\u4f3c\u5316\u8eab\u6570\u5341\u767e\u4eba\u4e00\u822c\uff0c \u9f99\u7ecd\u5723\u5408\u6570\u5927\u9ad8\u624b\u4e4b\u529b\uff0c\u624d\u582a\u582a\u5835\u622a\u5f97\u4f4f\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u6b66\u7384\u971c\u5ffd\u5730\u53d1\u58f0\u957f\u5578\uff0c\u9877\u523b\u4e4b\u95f4\uff0c\u5bc6\u6797\u6df1\u5904\u4e5f\u6709\u5578\u58f0\u4e0e\u5979\u76f8\u548c\uff0c\u4e00\u957f\u4e00\u77ed\uff0c \u6e05\u8106\u975e\u5e38\uff0c\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u9664\u6076\u52a1\u5c3d\uff0c\u5f85\u6211\u4e5f\u5524\u6765\u4e24\u4e2a\u5e2e\u624b\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u60c5\u8038\u52a8\uff0c \u5fc3\u4e2d\u90fd\u5728\u60f3\u9053\uff1a\u201c\u53ea\u5979\u4e00\u4eba\u5df2\u96be\u5e94\u4ed8\uff0c\u5979\u8bf7\u6765\u7684\u5e2e\u624b\uff0c\u90a3\u5b9a\u7136\u662f\u66f4\u52a0\u5389\u5bb3\u7684\u4e86\uff01\u201d \u3000\u3000\u5578\u58f0\u4e00\u505c\uff0c\u8349\u576a\u4e0a\u73b0\u51fa\u4e24\u4e2a\u5c11\u5973\u7684\u5f71\u5b50\uff0c\u83ba\u58f0\u5456\u5456\u7684\u95ee\u9053\uff1a \u3000\u3000\u201c\u5c0f\u59d0\u547c\u5524\uff0c\u6709\u4f55\u5dee\u9063\uff1f\u201d\u8fd9\u4e24\u4e2a\u5c11\u5973\u5e74\u7eaa\u90fd\u4e0d\u8fc7\u5341\u4e94\u516d\u5c81\u7684\u6837\u5b50\uff0c\u4e0a\u5b98\u5a49\u513f\u5728\u77f3 \u7b0b\u7f1d\u4e2d\u671b\u51fa\uff0c\u4f9d\u7a00\u8ba4\u5f97\u8fd9\u4e24\u4e2a\u4eba\u6b63\u662f\u6b66\u7384\u971c\u7684\u4e2b\u73af\u3002 \u3000\u3000\u7fa4\u96c4\u89c1\u662f\u8fd9\u6837\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\uff0c\u90fd\u4e0d\u7981\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u6b66\u7384\u971c\u5429\u5490\u9053\uff1a\u201c\u660e\u73e0\uff0c\u5982\u610f\uff0c \u4f60\u4e24\u4e2a\u66ff\u6211\u628a\u8fd9\u4e9b\u5927\u82f1\u96c4\u7684\u5175\u5668\u5c3d\u90fd\u7f34\u4e86\u3002\u201d\u8fd9\u6b21\u53c2\u52a0\u82f1\u96c4\u5927\u4f1a\u7684\u7ea6\u6709\u4e00\u767e\u591a\u4eba\uff0c\u9f99\u7ecd \u5723\u5206\u4e86\u4e00\u5c0f\u534a\u4eba\u53bb\u56f4\u653b\u6b66\u7384\u971c\uff0c\u4e00\u542c\u8fd9\u8bdd\uff0c\u624d\u77e5\u9053\u8fd9\u4e24\u4e2a\u5973\u5b69\u5b50\u7adf\u662f\u6b66\u7384\u971c\u7684\u4e2b\u73af\uff0c\u800c \u6b66\u7384\u971c\u7adf\u5429\u5490\u4e2b\u73af\u6765\u7f34\u4ed6\u4eec\u7684\u5175\u5203\uff01\u4ed6\u4eec\u90fd\u662f\u5404\u9738\u4e00\u65b9\u7684\u4eba\u7269\uff0c\u51e0\u66fe\u53d7\u8fc7\u4eba\u5982\u6b64\u85d0\u89c6\uff1f \u767b\u65f6\u7fa4\u60c5\u6c79\u6d8c\uff0c\u7eb7\u7eb7\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u770b\u4f60\u5982\u4f55\u7f34\u6211\u7684\u5175\u5203\uff1f\u201d \u3000\u3000\u4f46\u89c1\u90a3\u4e24\u4e2a\u5c11\u5973\u81ea\u5907\u821e\u8d77\u4e00\u6761\u7ef8\u5e26\uff0c\u4e5f\u5b66\u5979\u4eec\u5c0f\u59d0\u7684\u6837\u5b50\uff0c\u7528\u7ea2\u7ef8\u6765\u5377\u5175\u5203\uff0c\u5979\u4eec \u7684\u529f\u529b\u867d\u7136\u8fdc\u4e0d\u53ca\u6b66\u7384\u971c\uff0c\u4f46\u5728\u573a\u7684\u597d\u624b\uff0c\u5927\u90fd\u53bb\u53c2\u52a0\u56f4\u653b\u6b66\u7384\u971c\uff0c\u5269\u4e0b\u6765\u7684\u4e0d\u8fc7\u662f\u4e9b \u4e8c\u4e09\u6d41\u7684\u89d2\u8272\uff0c\u8fd9\u4e9b\u4eba\u90a3\u91cc\u8bc6\u5f97\u5389\u5bb3\uff0c\u4e00\u6d8c\u800c\u4e0a\uff0c\u660e\u73e0\u5982\u610f\u8fd9\u4e24\u4e2a\u5c0f\u4e2b\u5934\u683c\u683c\u4e00\u7b11\uff0c\u53eb \u9053\uff1a\u201c\u8c28\u9886\u5c0f\u59d0\u5429\u5490\uff0c\u82e5\u662f\u4e0d\u6210\uff0c\u5c0f\u59d0\uff0c\u4f60\u518d\u6765\u5e2e\u5fd9\u3002\u201d\u7b11\u58f0\u4e2d\u4e24\u6761\u7ea2\u7ef8\u76d8\u65cb\u98de\u821e\uff0c\u77e2 \u5a07\u5982\u9f99\uff0c\u5c71\u4e1c\u996e\u9a6c\u5ddd\u6768\u5be8\u4e3b\u7684\u4e00\u67c4\u539a\u80cc\u65ab\u5c71\u5200\u9996\u5148\u88ab\u660e\u73e0\u7684\u7ea2\u7ef8\u5377\u8d70\uff0c\u8fd9\u67c4\u65ab\u5c71\u5200\u91cd\u8fbe \u56db\u5341\u516b\u65a4\uff0c\u88ab\u5979\u4e00\u5377\u63b7\u51fa\uff0c\u542c\u5728\u4e00\u5757\u77f3\u5934\u4e0a\uff0c\u767b\u65f6\u5c06\u77f3\u5934\u5288\u5f00\u4e3a\u4e8c\uff0c\u63a5\u7740\u957f\u67aa\u3001\u5927\u621f\u3001 \u94c1\u62d0\u3001\u91d1\u97ad\u3001\u94a2\u5200\u3001\u94dc\u950f\u4e4b\u7c7b\u7684\u5175\u5668\uff0c\u7eb7\u7eb7\u88ab\u7ea2\u7ef8\u5377\u8d70\uff0c\u5404\u79cd\u5175\u5668\u6ee1\u7a7a\u4e71\u98de\uff0c\u6709\u51e0\u4e2a\u6765 \u4e0d\u53ca\u95ea\u907f\u7684\u7adf\u7ed9\u81ea\u5df1\u4eba\u7684\u5175\u5668\u8bef\u4f24\uff0c\u7fa4\u96c4\u9635\u52bf\u767b\u65f6\u5927\u4e71\uff01 \u3000\u3000\u6b66\u7384\u971c\u7eb5\u58f0\u957f\u7b11\uff0c\u957f\u5251\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u8feb\u5f97\u56f4\u653b\u5979\u7684\u51e0\u4e2a\u597d\u624b\u653e\u5bbd\u5708\u5b50\uff0c\u500f \u5730\u98de\u8eab\u63a0\u51fa\uff0c\u5c55\u5f00\u6781\u8fc5\u75be\u7684\u8eab\u6cd5\uff0c\u6325\u7ef8\u821e\u5251\uff0c\u75db\u4e0b\u6740\u624b\uff0c\u9047\u6709\u90a3\u4e24\u4e2a\u5c0f\u4e2b\u5934\u96be\u4ee5\u5e94\u4ed8\u7684 \u4eba\uff0c\u5979\u5c31\u7a81\u5982\u5176\u6765\u7684\u8f7b\u8f7b\u4e00\u5251\uff0c\u5251\u5c16\u4e00\u5212\uff0c\u5fc5\u7136\u5c06\u5bf9\u65b9\u7684\u864e\u53e3\u5212\u5f00\uff0c\u90a3\u4e24\u4e2a\u4e2b\u5934\u8d81\u52bf\u5377 \u8d70\u4ed6\u7684\u5175\u5668\uff0c\u5f53\u771f\u662f\u52bf\u5982\u7834\u7af9\uff0c\u7247\u523b\u4e4b\u95f4\u5c06\u7fa4\u96c4\u6740\u5f97\u843d\u82b1\u6d41\u6c34\uff0c\u5175\u5668\u64a4\u6ee1\u4e00\u5730\uff01 \u3000\u3000\u6709\u51e0\u4e2a\u4e00\u6d41\u9ad8\u624b\uff0c\u4f8b\u5982\u767d\u9a6c\u89c2\u4e3b\u9ec4\u9e64\u9053\u4eba\uff0c\u5f52\u4e91\u5e84\u4e3b\u519c\u5bb6\u9038\u7b49\u683c\u4e8e\u8eab\u4ef7\uff0c\u59cb\u7ec8\u4e0d\u80af \u53c2\u52a0\u56f4\u653b\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u6447\u5934\u53f9\u9053\uff1a\u201c\u5982\u6b64\u82f1\u96c4\u5927\u4f1a\uff0c\u5f53\u771f\u662f\u7b11\u8bdd\u4e00\u573a\uff0c\u54b1\u4eec \u8fd8\u5728\u8fd9\u513f\u505a\u751a\uff1f \u3000\u3000\u96be\u9053\u6211\u597d\u610f\u601d\u53bb\u8ddf\u522b\u4eba\u7684\u4e2b\u73af\u8f83\u91cf\u5417\uff1f\u201d\u767d\u9a6c\u89c2\u4e3b\u548c\u4ed6\u7684\u5e05\u5f1f\u9996\u5148\u79bb\u573a\uff0c\u63a5\u7740\u6709\u597d \u51e0\u4e2a\u4e00\u6d41\u9ad8\u624b\u4e5f\u8ddf\u7740\u98d8\u7136\u800c\u8d70\uff0c\u8fd9\u4e00\u6765\u5f62\u52bf\u66f4\u4e71\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u9f99\u4e09\u5148\u751f\u594b\u8eab\u800c\u4e0a\uff0c\u5de6\u624b\u864e\u5934\u94a9\u9501\u62ff\u660e\u73e0\u7684\u624b\u8155\uff0c\u53f3\u624b\u9554\u94c1\u62d0\u6a2a\u626b\u5982\u610f\u7684\u67f3 \u8170\uff0c\u8fd9\u4e24\u62db\u7528\u4e86\u5168\u529b\uff0c\u5a01\u731b\u4e4b\u6781\uff0c\u4f46\u5374\u88ab\u6b66\u7384\u971c\u62a2\u5feb\u4e00\u6b65\uff0c\u7ea2\u7ef8\u4e00\u5c55\uff0c\u5c06\u4ed6\u7684\u864e\u5934\u94a9\u9554 \u94c1\u62d0\u5168\u90fd\u5377\u53bb\uff0c\u957f\u5251\u4e00\u5c55\uff0c\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u4ed6\u7684\u80a9\u5934\u523a\u4e86\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u8fde\u7435\u7436\u9aa8\u4e5f\u6311 \u65ad\u4e86\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u70e6\u5934\u7684\u4eba\uff0c\u597d\u5427\uff0c\u6211\u53eb\u4f60\u5728\u5341\u5e74\u4e4b\u5185\uff0c\u4e0d\u80fd\u6062\u590d\u6b66\u529f\u3002\u201d \u3000\u3000\u7fa4\u96c4\u89c1\u6b64\u5a01\u52bf\uff0c\u65e0\u4e0d\u80c6\u5bd2\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff0c\u53ea\u89c1\u8c37\u795e\u7fc1\u9aef\u7709\u6012\u5f20\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u843d \u5728\u573a\u5fc3\uff01 \u3000\u3000\u6b66\u7384\u9738\u7b11\u9053\uff1a\u201c\u5230\u5e95\u628a\u8001\u76df\u4e3b\u8bf7\u51fa\u6765\u4e86\uff0c\u54c8\u54c8\uff0c\u5c0f\u5973\u5b50\u4f55\u5e78\u5f97\u4ee5\u904d\u4f1a\u5929\u4e0b\u82f1\u96c4\uff0c\u8fd9 \u4e00\u4ed7\u53ef\u771f\u6709\u610f\u601d\u3002\u201d\u8c37\u795e\u7fc1\u795e\u5a01\u51db\u51db\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u7ed9\u6211\u9000\u4e0b\uff01\u201d\u53cc\u7738\u70af\u606b\uff0c\u8fce \u7740\u6b66\u7384\u971c\u7684\u76ee\u5149\uff0c\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\u95e8\u4e0b\uff0c\u7236\u6bcd\u662f\u8c01\uff1f\u201d\u6b66\u7384\u971c\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u60f3\u8ba9\u4f4d\u7ed9\u6211\uff0c\u4f55\u987b\u67e5\u6839\u95ee\u5e95\uff1f\u6211\u53ef\u6ca1\u6709\u522b\u4eba\u90a3\u4e48\u9ad8\u8d35\u7684\u8eab\u4efd\uff0c\u201d\u8bed\u6c14\u6697 \u8bbd\u8c37\u795e\u7fc1\u6276\u52a9\u674e\u9038\u505a\u76df\u4e3b\u7684\u4e8b\u3002\u8c37\u795e\u7fc1\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u4e5f\u7f62\uff0c \u54b1\u4eec\u5c31\u516c\u516c\u9053\u9053\u7684\u8f83\u91cf\u4e00\u573a\uff0c\u4f60\u82e5\u80dc\u5f97\u4e86\u6211\uff0c\u6c5f\u6e56\u9053\u4e0a\uff0c\u4ece\u6b64\u5c31\u518d\u4e5f\u6ca1\u6709\u6211\u8c37\u795e\u7fc1\u8fd9\u53f7 \u4eba\u7269\u3002\u4f60\u82e5\u8f93\u4e86\u5462\uff0c\u59d1\u5a18\uff0c\u90a3\u4e48\u5bf9\u4f60\u4e0d\u8d77\uff0c\u6211\u4e5f\u8981\u5e9f\u6389\u4f60\u7684\u6b66\u529f\uff01\u201d\u539f\u6765\u8c37\u795e\u7fc1\u89c1\u5979\u6b66 \u529f\u5982\u6b64\u9ad8\u5f3a\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u731c\u4e0d\u51fa\u5979\u662f\u4f55\u4eba\u95e8\u4e0b\uff0c\u4f46\u5374\u6df1\u77e5\u90a3\u5fc5\u5b9a\u662f\u6781\u96be\u60f9\u7684\u4eba\u7269\uff0c\u6545\u6b64 \u60f3\u5148\u674f\u95ee\u6e05\u695a\u3002 \u3000\u3000\u6b66\u7384\u971c\u4e00\u542c\uff0c\u77e5\u9053\u4ed6\u7684\u610f\u601d\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u6211\u6b63\u60f3\u8bf7\u6559\u95fb\u540d\u5929\u4e0b\u7684\u8e51 \u4e91\u5251\u6cd5\u3002\u4f60\u653e\u5fc3\u5427\uff0c\u6709\u591a\u5c11\u672c\u9886\u5c3d\u7ba1\u65bd\u5c55\u4fbf\u662f\u3002\u6211\u7edd\u4e0d\u4f1a\u8bf7\u5e08\u957f\u4e3a\u6211\u62a5\u4ec7\u3002\u8bf7\u62d4\u5251\uff01\u201d \u8c37\u795e\u7fc1\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u8d62\u5f97\u4e86\u6211\u8fd9\u53cc\u8089\u638c\uff0c\u6211\u518d\u7528\u5251\u4e5f\u8fd8\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u65e2\u7136\u8001\u76df\u4e3b\u5b9a\u8981\u4f38\u91cf\u6211\uff0c\u90a3\u5c31\u8bf7\u6055\u6211\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u7ea2\u7ef8\u4e00\u5c55\uff0c\u75be\u5377\u800c \u6765\uff0c\u8c37\u795e\u7fc1\u6709\u610f\u5356\u5f04\uff0c\u4e94\u6307\u5e76\u62e2\uff0c\u5f85\u90a3\u7ea2\u7ef8\u5377\u5230\uff0c\u84e6\u7136\u4e00\u5212\uff0c\u53ea\u542c\u5f97\u58f0\u5982\u88c2\u5e1b\uff0c\u7ef8\u5e26\u7684 \u4e00\u7aef\uff0c\u88ab\u6495\u6210\u4e94\u6761\uff0c\u53ef\u662f\u8c37\u795e\u7fc1\u7684\u624b\u8155\u4e5f\u88ab\u62c2\u4e86\u4e00\u4e0b\uff0c\u4ea6\u81ea\u611f\u5230\u4e00\u9635\u9178\u9ebb\u3002 \u3000\u3000\u6b66\u7384\u971c\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u91d1\u94a2\u6307\u7684\u529f\u592b\uff01\u201d\u7ea2\u7ef8\u7ffb\u5377\u4e4b\u4e0b\u84e6\u5730\u9752\u5149\u4e00\u95ea\uff0c\u4e00\u62db\u201c\u7389\u5973 \u7a7f\u9488\u201d\uff0c\u4fbf\u671d\u8c37\u795e\u7fc1\u80a9\u540e\u7684\u201c\u98ce\u5e9c\u7a74\u201d\u523a\u5230\uff0c\u4e24\u4eba\u672c\u662f\u5bf9\u9762\u9762\u7acb\uff0c\u6b66\u7384\u971c\u4e00\u4e0b\u5b50\u4fbf\u7ed5\u5230 \u8c37\u795e\u7fc1\u80cc\u540e\uff0c\u8eab\u6cd5\u5251\u6cd5\uff0c\u7aef\u7684\u662f\u5feb\u5f97\u60ca\u4eba\u3002\u90a3\u77e5\u8c37\u795e\u7fc1\u7684\u8e51\u4e91\u6b65\u6cd5\u66f4\u4e3a\u8d85\u5999\uff0c\u6b66\u7384\u971c\u5251 \u5149\u4e00\u95ea\uff0c\u4ed6\u5df2\u79fb\u6b65\u6362\u5f62\uff0c\u53cd\u4e86\u4e00\u62f3\uff0c\u9aa8\u8282\u683c\u683c\u4f5c\u54cd\uff0c\u624b\u81c2\u7a81\u7136\u66b4\u957f\u534a\u5c3a\uff0c\u7adf\u7136\u4ece\u6b66\u7384\u971c \u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u51fb\u6765\u3002\u6b66\u7384\u971c\u673a\u601c\u4e4b\u6781\uff0c\u4e00\u5251\u6eaf\u7a7a\uff0c\u5251\u62db\u7acb\u53d8\uff0c\u4fe8\u5982\u873b\u8713\u63a0\u6c34\uff0c\u71d5\u5b50\u7a7f \u4e91\uff0c\u7adf\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u4ece\u8c37\u795e\u7fc1\u53f3\u4fa7\u7a9c\u51fa\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u5251\u968f\u8eab\u8f6c\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4fbf\u63a5\u8fde \u653b\u4e86\u4e09\u62db\u3002\u8c37\u795e\u7fc1\u89c1\u81ea\u5df1\u8fd9\u7a81\u7136\u556e\u7684\u901a\u81c2\u795e\u62f3\uff0c\u7adf\u7136\u4e5f\u7ed9\u5979\u907f\u8fc7\uff0c\u5fc3\u4e2d\u4ea6\u81ea\u4f69\u670d\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u6b66\u7384\u971c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u660e\u73e0\u3001\u5982\u610f\uff0c\u6211\u53ef\u6ca1\u6709\u53eb\u4f60\u4eec\u4f4f\u624b\u5440\uff0c\u4f60\u4eec\u5446\u5728\u8fd9\u91cc\u505a \u751a\u4e48\uff1f\u201d\u539f\u6765\u573a\u4e0a\u7fa4\u96c4\u4e0e\u53ca\u90a3\u4e24\u4e2a\u5c0f\u4e2b\u5934\uff0c\u90fd\u7ed9\u4ed6\u4eec\u8fd9\u573a\u6bd4\u6b66\u5438\u5f15\u4f4f\u4e86\uff0c\u4e0d\u81ea\u89c9\u7684\u90fd\u505c \u4e0b\u624b\u7c73\u3002\u8fd9\u65f6\u7ed9\u6b66\u7384\u971c\u4e00\u8a00\u63d0\u9192\uff0c\u90a3\u4e24\u4e2a\u5c0f\u4e2b\u5934\u6d51\u52a8\u7ea2\u7ef8\uff0c\u4f8d\u5f97\u7fa4\u96c4\u60ca\u89c9\u4e4b\u65f6\uff0c\u65e9\u53c8\u6709 \u51e0\u6761\u5175\u5203\u88ab\u5979\u4eec\u5377\u8d70\u4e86\u3002 \u3000\u3000\u9163\u6218\u4e4b\u4e2d\u5374\u6709\u4e00\u4e2a\u4eba\u9eef\u7136\u795e\u4f24\uff0c\u6084\u6084\u7684\u4ece\u4eba\u5806\u4e2d\u89c5\u9699\u7a7f\u8fc7\uff0c\u4f3c\u4e4e\u8fd9\u573a\u5927\u6218\u4e0e\u4ed6\u65e0\u5173 \u4f3c\u7684\u3002\u8fd9\u4e2a\u4eba\u7adf\u662f\u88ab\u63a8\u4e3e\u4e3a\u65b0\u76df\u4e3b\u7684\u674e\u9038\uff01 \u3000\u3000\u674e\u9038\u521d\u6765\u4e4b\u65f6\u662f\u8c6a\u6c14\u5e72\u4e91\uff0c\u96c4\u5fc3\u52c3\u52c3\uff0c\u6b64\u9645\u5374\u662f\u7cbe\u795e\u9893\u4e27\u58ee\u5fd7\u51b0\u6d88\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u96c6\u5929\u4e0b\u2018\u82f1\u96c4\u2019\u4e4b\u529b\uff0c\u7eb5\u7136\u6253\u8d25\u4e86\u51e0\u4e2a\u5973\u5b50\uff0c\u53c8\u6709\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u201d\u518d\u4e00\u60f3\u5230\u4eca\u65e5\u6765\u53c2 \u52a0\u201c\u82f1\u96c4\u4f1a\u201d\u7684\uff0c\u5927\u534a\u662f\u4e3a\u4e86\u529f\u540d\u5229\u7984\u800c\u6765\uff0c\u8fd8\u6709\u4e00\u4e9b\u5219\u662f\u6ee5\u828b\u5145\u6570\uff0c\u88ab\u9f99\u4e09\u8fd9\u4e00\u4f19\u4eba \u4e34\u65f6\u62c9\u6765\uff0c\u4ee5\u58ee\u58f0\u52bf\u7684\u89d2\u8272\u3002\u771f\u6b63\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u90a3\u662f\u5c11\u4e4b\u53c8\u5c11\uff01\u6709\u9650\u7684\u51e0\u4e2a\u9ad8\u4eba\uff0c\u5982\u5f52 \u4e91\u5e84\u4e3b\u3001\u767d\u9a6c\u89c2\u4e3b\u7b49\u4eba\u53c8\u5df2\u98d8\u7136\u800c\u8d70\uff0c\u53ea\u5269\u4e0b\u4e00\u4e2a\u8c37\u795e\u7fc1\u5728\u652f\u6491\u573a\u9762\uff0c\u8089\u5df1\u5fc3\u76ee\u4e2d\u8f70\u8f70 \u70c8\u70c8\u7684\u201c\u82f1\u96c4\u4eba\u4f1a\u201d\uff0c\u7adf\u53d8\u6210\u4e86\u7b11\u8bdd\u4e00\u573a\uff0c\u9ed8\u5ff5\uff0c\u201c\u4f24\u5fc3\u5b87\u5185\u82f1\u8c6a\uff0c\u5c3d\u5f52\u65b0\u4e3b\uff1b\u5fcd\u89c1\u5929 \u4eac\u795e\u5668\uff0c\u7adf\u5c5e\u4ed6\u5bb6\uff0c\u201d\u8fd9\u4e24\u53e5\u8bdd\uff0c\u7f05\u6000\u738b\u5ba4\u5149\u8363\uff0c\u60c6\u5e10\u201c\u4e49\u65d7\u201d\u96be\u4e3e\uff0c\u4e0d\u7981\u9eef\u7136\u795e\u4f24\uff0c \u9042\u4e5f\u4fcf\u6084\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u573a\u4e2d\u6df7\u6218\u6b63\u9163\uff0c\u7fa4\u96c4\u88ab\u90a3\u4e24\u4e2a\u4e2b\u5934\u6253\u5f97\u660f\u5934\u660f\u8111\uff0c\u5927\u5bb6\u90fd\u5728\u51dd\u795e\u5e94\u4ed8\uff0c\u7adf\u6ca1\u7559 \u610f\u5230\u4ed6\u4eec\u7684\u65b0\u76df\u4e3b\u51fa\u8d70\u3002\u674e\u9038\u7684\u672c\u9886\u6bd4\u8d77\u6b66\u7384\u971c\u7684\u4e2b\u73af\u81ea\u7136\u662f\u9ad8\u660e\u5f97\u591a\uff0c\u4f46\u4e00\u6765\u4ed6\u5df2\u5fc3 \u7070\u610f\u51b7\uff0c\u4e8c\u6765\u4ed6\u4e5f\u4e0d\u80af\u8d2c\u4f4e\u8eab\u4efd\u53bb\u548c\u6b66\u7384\u971c\u7684\u4e2b\u73af\u8f83\u91cf\uff0c\u56e0\u6b64\u53ea\u4ee5\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u907f\u5f00\u4e86 \u7ea2\u7ef8\u7684\u7ffb\u5377\uff0c\u5200\u67aa\u7684\u7a81\u51fb\uff0c\u5728\u4eba\u4e1b\u4e2d\u89c5\u9699\u7a7f\u8fc7\uff0c\u521a\u521a\u7a7f\u51fa\u6df7\u6218\u7684\u6838\u5fc3\uff0c\u5ffd\u542c\u5f97\u6b66\u7384\u971c\u4e00 \u58f0\u957f\u7b11\uff0c\u8c37\u795e\u7fc1\u6012\u58f0\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eca\u65e5\u8feb\u5f97\u8001\u592b\u7528\u5251\uff0c\u53ef\u83ab\u602a\u6211\u518d\u4e0d\u7559\u60c5\uff01\u201d \u3000\u3000\u539f\u6765\u8c37\u795e\u7fc1\u548c\u6b66\u7384\u971c\u6076\u6218\u4e86\u6570\u5341\u56de\u5408\uff0c\u8c37\u795e\u7fc1\u529f\u529b\u867d\u7136\u8f83\u9ad8\uff0c\u4f46\u5230\u5e95\u662f\u4e0a\u5e74\u7eaa\u7684\u4eba\uff0c \u7a0d\u8f93\u7075\u654f\uff0c\u6b66\u7384\u971c\u53c8\u6e9c\u6ed1\u4e4b\u6781\uff0c\u968f\u673a\u5e94\u53d8\uff0c\u6bcf\u6bcf\u5728\u6781\u51f6\u6781\u9669\u4e4b\u9645\uff0c\u4ee5\u5de7\u62db\u907f\u8fc7\uff0c\u8c37\u795e\u7fc1 \u8eab\u6000\u4e00\u6837\u7edd\u6280\uff1a\u901a\u81c2\u62f3\u3001\u91d1\u94a2\u6307\u548c\u8e51\u4e91\u5251\uff0c\u800c\u4eca\u820d\u5251\u4e0d\u7528\uff0c\u4f46\u51ed\u62f3\u6307\u4e24\u5927\u7edd\u6280\uff0c\u7adf\u662f\u5948 \u4f55\u4e0d\u4e86\u654c\u4eba\u3002\u8c37\u795e\u7fc1\u8fd9\u5341\u5e74\u6765\u4ece\u6ca1\u6709\u7528\u8fc7\u5251\uff0c\u521a\u624d\u53c8\u6709\u8bdd\u5728\u5148\uff0c\u867d\u7136\u660e\u77e5\u82e5\u4e0d\u7528\u5251\uff0c\u5c31 \u514b\u5236\u4e0d\u4e86\u8fd9\u5c11\u5973\uff0c\u5374\u4e5f\u4e0d\u4fbf\u62d4\u5251\u3002\u4e45\u6218\u4e0d\u4e0b\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u7a81\u7136\u65bd\u7528\u9669\u62db\uff0c\u4e0d\u987e\u81ea\u8eab\uff0c\u5de6 \u4e86\u4e00\u62f3\uff0c\u53f3\u6307\u4e00\u5212\uff0c\u540c\u65f6\u65bd\u5c55\u901a\u81c2\u62f3\u91d1\u94a2\u6307\u4e24\u6837\u529f\u592b\uff0c\u773c\u89c1\u6b66\u7384\u971c\u95ea\u907f\u4e0d\u4e86\uff0c\u52bf\u5c06\u4e24\u8d25 \u4ff1\u4f24\uff0c\u5374\u5ffd\u542c\u5f97\u6b66\u7384\u971c\u54ce\u54df\u4e00\u58f0\uff0c\u9752\u5251\u7ea2\u7ef8\u540c\u65f6\u629b\u51fa\uff0c\u6b66\u7384\u971c\u8fd9\u4e00\u602a\u62db\u5927\u51fa\u8c37\u795e\u7fc1\u610f\u5916\uff0c \u5de6\u624b\u624b\u8155\u7adf\u7ed9\u7ea2\u7ef8\u91cd\u91cd\u675f\u7740\uff1a\u8c37\u795e\u7fc1\u8fd0\u8d77\u795e\u529f\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53f3\u6307\u4e00\u5f39\uff0c\u5f39\u5f00\u4e86\u6b66\u7384\u971c\u7684 \u9752\u94a2\u5251\uff0c\u5de6\u81c2\u4e00\u632f\uff0c\u675f\u8155\u7684\u7ea2\u7ef8\u88c2\u6210\u7247\u7247\uff0c\u6b66\u7384\u971c\u98de\u8eab\u4e00\u7eb5\uff0c\u63a5\u8fc7\u4e86\u7a7a\u4e2d\u98de\u6765\u7684\u957f\u5251\uff0c \u5a07\u58f0\u7b11\u9053\uff1a\u201c\u627f\u8ba9\u4e00\u62db\uff0c\u4f46\u6211\u7684\u7ea2\u7ef8\u88ab\u4f60\u88c2\u6210\u788e\u7247\uff0c\u59d1\u4e14\u7b97\u4f60\u626f\u5e73\u4e86\u5427\u3002\u201d\u8c37\u795e\u7fc1\u52c3\u7136 \u5927\u6012\uff0c\u8fd9\u65f6\u4ed6\u624d\u521a\u521a\u62d4\u51fa\u5251\u7c73\u3002 \u3000\u3000\u674e\u9038\u89c1\u8c37\u795e\u7fc1\u7adf\u8981\u653e\u5251\uff0c\u8c23\u4e86\u6447\u5934\uff0c\u4e0d\u6b32\u518d\u89c2\uff0c\u75be\u5411\u524d\u8d70\u3002 \u3000\u3000\u8d70\u51fa\u8349\u576a\uff0c\u521a\u6b32\u767b\u5c71\uff0c\u5ffd\u89c1\u524d\u9762\u7684\u4e00\u5757\u5927\u77f3\uff0c\u77f3\u7f1d\u4e2d\u5341\u5e45\u7ea2\u7ef8\u98d8\u51fa\uff0c\u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c \u53eb\u9053\uff0c\u201c\u8c01\u5728\u91cc\u9762\uff1f\u201d\u53eb\u58f0\u672a\u505c\uff0c\u5315\u5b98\u5a49\u513f\u4e00\u8dc3\u800c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u674e\u9038\u54e5\u54e5\uff0c\u662f\u6211\uff0c\u662f\u6211\uff01\u201d \u3000\u3000\u8fd9\u4e00\u5239\u90a3\uff0c\u674e\u9038\u51e0\u4e4e\u7591\u5fc3\u662f\u5728\u68a6\u4e2d\uff0c\u81ea\u4ece\u90a3\u4e00\u6b21\u5df4\u5dde\u591c\u53d8\uff0c\u4e24\u4eba\u5206\u4e4e\u4ee5\u6765\uff0c\u674e\u9038\u65e0 \u65f6\u65e0\u523b\u4e0d\u5728\u4e3a\u4e0a\u5b98\u5a49\u513f\u63d0\u5fc3\u540a\u80c6\u3002\u539f\u6765\u90a3\u5929\u665a\u4e0a\uff0c\u674e\u9038\u5148\u5230\u5df2\u5dde\uff0c\u5f97\u5230\u9f99\u4e09\u5148\u751f\u7684\u901a\u77e5\uff0c \u53eb\u4ed6\u5230\u57ce\u5916\u4e00\u4e2a\u79d8\u5bc6\u7684\u5730\u65b9\uff0c\u53bb\u4f1a\u89c1\u8c37\u795e\u7fc1\uff0c\u5546\u8c08\u5ce8\u5d4b\u91d1\u9876\u82f1\u96c4\u5927\u4f1a\u4e4b\u4e8b\u3002 \u3000\u3000\u674e\u9038\u4e0d\u4fbf\u544a\u8bc9\u4e0a\u5bab\u5a49\u513f\uff0c\u6545\u6b64\u7b49\u5230\u4e0a\u5b98\u5a49\u513f\u4e5f\u5728\u4ed6\u90a3\u4e00\u95ee\u5ba2\u5e97\u6295\u5bbf\u4e4b\u540e\uff0c\u4ed6\u7559\u4e0b\u4e00 \u4e2a\u201c\u6709\u4e8b\u5916\u51fa\u201d\u7684\u4fbf\u6761\uff0c\u4fbf\u5306\u5306\u8d70\u4e86\u3002\u54ea\u77e5\u5e9f\u592a\u5b50\u674e\u8d24\u5f53\u665a\u4fbf\u88ab\u523a\u6740\uff0c\u800c\u4e14\u6076\u884c\u8005\u4e0e\u6bd2 \u89c2\u97f3\u53c2\u4e0e\u5176\u4e8b\uff0c\u674e\u9038\u4e8b\u540e\u5f97\u77e5\uff0c\u6df1\u6015\u4e0a\u5b98\u5a49\u513f\u4e5f\u88ab\u5377\u5165\u6f29\u6da1\uff0c\u906d\u4e86\u6076\u884c\u97f3\u4e0e\u6bd2\u89c2\u97f3\u7684\u6bd2 \u624b\uff0c\u6bcf\u4e00\u5ff5\u53ca\uff0c\u6df1\u6df1\u81ea\u759a\uff0c\u89c9\u5f97\u81ea\u5df1\u867d\u7136\u6709\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u8be5\u629b\u4e0b\u5979\u4e00\u4eba\u72ec\u5728\u5df2\u5dde\u3002 \u3000\u3000\u8fd9\u4e2a\u591a\u6708\u6765\uff0c\u674e\u9038\u5f53\u771f\u662f\u9b42\u68a6\u4e0d\u5b89\uff0c\u5374\u4e0d\u60f3\u7a81\u7136\u5728\u8fd9\u4e2a\u5730\u65b9\uff0c\u8fd9\u4e2a\u573a\u5408\uff0c\u7adf\u7136\u89c1\u7740 \u4e86\u4e0a\u5b98\u5a49\u513f\u3002\u674e\u9038\u5446\u4e86\u4e00\u5446\uff0c\u201c\u5a49\u513f\u201d\u4e24\u5b57\u8fd8\u672a\u66fe\u53eb\u51fa\uff0c\u5ffd\u6709\u4e00\u4eba\u75be\u5982\u5954\u9a6c\uff0c\u84e6\u5730\u8dd1\u6765\uff0c \u4f38\u51fa\u94b5\u5927\u7684\u62f3\u5934\uff0c\u5411\u4e0a\u5b98\u5a49\u513f\u4fbf\u662f\u62e6\u8170\u4e00\u51fb\uff01 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e43\u662f\u96c4\u5de8\u9f0e\u3002\u4ed6\u90a3\u91cc\u77e5\u9053\u4e0a\u5b98\u5a49\u513f\u4e0e\u674e\u9038\u60c5\u540c\u5144\u59b9\uff0c\u4ed6\u7a81\u7136\u89c1\u5230\u4e0a\u5b98\u5a49\u513f\u4ece \u77f3\u7b0b\u7f1d\u4e2d\u7a9c\u51fa\uff0c\u53ea\u9053\u5979\u4e5f\u662f\u6b66\u7384\u971c\u9884\u5148\u57cb\u4f0f\u7684\u4e2b\u5934\u3002\u4ed6\u5bf9\u674e\u9038\u5fe0\u5fc3\u803f\u803f\uff0c\u751f\u6015\u4e0a\u5b98\u5a49\u513f \u4f1a\u88ad\u51fb\u674e\u9038\uff0c\u6545\u6b64\u5148\u53d1\u5236\u4eba\uff01 \u3000\u3000\u674e\u9038\u6025\u5fd9\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff1f\u53ea\u89c1\u96c4\u5de8\u9f0e\u7684\u62f3\u5934\u5df2\u582a\u582a\u6253\u5230\u4e0a\u5b98\u5a49\u513f \u8eab\u4e0a\uff0c\u674e\u9038\u98de\u8eab\u6251\u6551\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u5ffd\u89c1\u7ea2\u7ef8\u4e00\u95ea\uff0c\u4e00\u4e2a\u5c11\u5973\u6012\u58f0\u65a5\u9053\uff1a\u201c\u8c01\u6562\u5bb3\u6211 \u5c0f\u59d0\u7684\u670b\u53cb\uff1f\u201d\u7ea2\u7ef8\u4e00\u7ffb\u4e00\u5377\uff0c\u767b\u65f6\u628a\u96c4\u5de8\u9f0e\u6c34\u725b\u822c\u7c97\u58ee\u7684\u8eab\u8eaf\u5377\u4e86\u8d77\u6765\uff0c\u6454\u51fa\u6570\u4e08\u5f00 \u5916\uff0c\u53ef\u662f\u4e0a\u5b98\u5a49\u513f\u4e5f\u88ab\u96c4\u5de8\u9f0e\u6253\u6655\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u5c11\u5973\u4e43\u662f\u6b66\u7384\u971c\u7684\u4e2b\u5934\u5982\u610f\uff0c\u5979\u6bd4\u674e\u9038\u5148\u4e00\u6b65\u8d76\u5230\uff0c\u6454\u5012\u4e86\u96c4\u5de8\u9f0e\uff0c\u7acb\u523b\u56de\u8eab \u6765\u6597\u674e\u9038\uff0c\u6012\u58f0\u9a82\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\u7684\u4ec0\u4e48\u82f1\u96c4\u76df\u4e3b\uff0c\u4e3a\u4ec0\u4e48\u6b3a\u8d1f\u4e00\u4e2a\u4e0d\u61c2\u6b66\u529f\u7684\u5c0f\u59d1\u5a18\u3002\u201d \u674e\u9038\u54ea\u6709\u65f6\u95f4\u5206\u8fa9\uff0c\u521a\u521a\u95ea\u5f00\u4e86\u90a3\u4e2b\u5934\u5934\u7684\u51e0\u62db\u6740\u624b\uff0c\u573a\u4e2d\u7fa4\u96c4\u5df2\u6709\u82e5\u5e72\u4eba\u53d1\u73b0\u4e86\u674e\u9038\uff0c \u7eb7\u7eb7\u8dd1\u6765\uff0c\u674e\u9038\u53eb\u9053\uff1a\u201c\u8c01\u90fd\u4e0d\u8bb8\u4f24\u5bb3\u5730\u4e0a\u7684\u8fd9\u4e2a\u5c11\u5973\uff01\u591a\u8c22\u4f60\u4eec\u62e5\u6234\uff0c\u6211\u5374\u6ca1\u6709\u9762\u76ee \u505a\u4f60\u4eec\u7684\u76df\u4e3b\u4e86\uff01\u201d\u98de\u8eab\u4e00\u63a0\uff0c\u4ece\u5982\u610f\u5934\u4e0a\u75be\u98de\u800c\u8fc7\uff0c\u76f4\u4e0a\u5cf0\u5dc5\uff0c\u5982\u610f\u548c\u8ffd\u6765\u7684\u8bf8\u4eba\u90fd \u5927\u611f\u610f\u5916\uff0c\u4f46\u89c1\u674e\u9003\u7684\u80cc\u5f71\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u5df2\u6d88\u5931\u5728\u5bc6\u6797\u8302\u8349\u4e4b\u4e2d\uff0c\u5982\u610f\u8bb0\u8d77\u4e86\u5c0f\u59d0\u7684\u5429 \u5490\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u6325\u52a8\u7ea2\u7ef8\uff0c\u4e49\u6765\u5377\u7fa4\u96c4\u7684\u5175\u5668\uff0c\u5c06\u4ed6\u4eec\u8feb\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u8fdc\u8fdc\u5730\u79bb\u5f00\u4e86\u6655 \u5012\u7684\u4e0a\u5b98\u5a49\u513f\u3002 \u3000\u3000\u674e\u9038\u767b\u4e0a\u4e86\u9ad8\u5cf0\uff0c\u5411\u4e0b\u4fef\u89c6\uff0c\u4f46\u89c1\u573a\u4e2d\u6fc0\u6218\u6b63\u916c\uff0c\u8c37\u795e\u7fc1\u548c\u6b66\u7384\u971c\u7684\u4e24\u67c4\u957f\u5251\u77e2\u77eb \u5982\u9f99\uff0c\u5251\u5149\u7ea0\u7ed3\uff0c\u5251\u6c14\u5f25\u6f2b\uff0c\u6b63\u81ea\u6597\u5f97\u96be\u5206\u96be\u89e3\u3002\u674e\u9038\u957f\u957f\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u672c\u60f3\u4e00\u8d70 \u4e86\u4e4b\uff0c\u4f46\u5374\u4ecd\u7136\u8fd8\u662f\u505c\u4e0b\u4e86\u811a\u6b65\u3002 \u3000\u3000\u8fd9\u65f6\u8c37\u4f38\u7fc1\u548c\u6b66\u7384\u971c\u5df2\u6597\u5230\u767e\u62db\u4ee5\u4e0a\uff0c\u53cc\u65b9\u5251\u6cd5\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c\u8d8a\u6765\u8d8a\u7d27\u3002\u8c37\u795e\u7fc1 \u4ee5\u62f3\u3001\u5251\u3001\u6307\u4e09\u7edd\u4f0e\u79f0\u9738\u6b66\u6797\uff0c\u5c24\u5176\u5728\u5251\u6cd5\u4e0a\u66f4\u6709\u72ec\u7279\u7684\u9020\u8be3\uff0c\u4ed6\u6240\u521b\u7684\u5251\u6cd5\u540d\u53eb\u201c\u8e51 \u4e91\u5251\u6cd5\u201d\uff0c\u5f53\u771f\u662f\u79fb\u6b65\u6362\u5f62\uff0c\u52a8\u5251\u53d8\u62db\uff0c\u8ffd\u98ce\u8e51\u4e91\uff0c\u6781\u5f97\u8f7b\u7075\u7fd4\u52a8\u4e4b\u5999\u3002\u4f46\u6b66\u7384\u971c\u7684\u8eab \u6cd5\u5c55\u5f00\u3002\u4ea6\u662f\u7fe9\u5982\u60ca\u9e3f\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u5251\u52bf\u6709\u5982\u62bd\u4e1d\u5265\u8327\uff0c\u7ef5\u7ef5\u4e0d\u65ad\u3002\u867d\u7136\u7565\u5904\u4e0b\u98ce\uff0c\u4ecd \u7136\u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u662f\u6b66\u6797\u76df\u4e3b\u7684\u8eab\u4efd\uff0c\u8fd9\u5341\u5e74\u6765\uff0c\u4e0d\u8bba\u4e0e\u8c01\u5bf9\u624b\uff0c\u5df2\u4e0d\u5c51\u4f7f\u7528\u5175\u5668\uff0c\u5982\u4eca\u662f\u505a \u4e86\u76df\u4e3b\u4e4b\u540e\uff0c\u7b2c\u4e00\u6b21\u7528\u5251\uff0c\u800c\u4e14\u5bf9\u65b9\u8fd8\u662f\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5973\u5b50\uff0c\u7adf\u7136\u4e45\u6218\u4e0d\u4e0b\uff0c\u6df1\u611f\u9762 \u4e0a\u65e0\u5149\uff0c\u5fc3\u5934\u52a8\u6012\uff0c\u529b\u900f\u5251\u5c16\uff0c\u4e00\u8bb0\u7edd\u62db\u6740\u51fa\uff0c\u6b66\u7384\u971c\u6a2a\u5251\u4e00\u5c01\uff0c\u4f46\u542c\u5f97\u5251\u5c16\u4e0a\u201c\u55e1\u55e1\u201d \u4e00\u9635\u5578\u58f0\uff0c\u4e24\u679d\u5251\u90fd\u7ed9\u5bf9\u65b9\u8361\u4e86\u5f00\u53bb\uff0c\u4e0d\u8fc7\u6b66\u7384\u971c\u7684\u5251\u4e0a\u5374\u591a\u6dfb\u4e86\u4e00\u5904\u7f3a\u53e3\uff0c\u6b66\u7384\u971c\u5403 \u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u8001\u5339\u592b\u7684\u529f\u529b\u679c\u7136\u662f\u8fdc\u80dc\u4e8e\u6211\uff01 \u3000\u3000\u6b66\u7384\u971c\u56fa\u7136\u529b\u60ca\uff0c\u4f46\u8c37\u795e\u7fc1\u7684\u60ca\u8be7\uff0c\u4ea6\u4e0d\u5728\u5979\u4e4b\u4e0b\u3002\u4ed6\u672c\u4ee5\u4e3a\u8fd9\u4e00\u4e0b\u5b9a\u80fd\u5c06\u6b66\u7384\u971c \u7684\u957f\u5251\u9707\u98de\uff0c\u90a3\u77e5\u8fd8\u662f\u7ed9\u5979\u6321\u4f4f\u4e86\uff0c\u4e24\u4eba\u50ac\u7d27\u5251\u6cd5\uff0c\u53c8\u4e2a\u4e86\u5341\u4f59\u5eff\u62db\uff0c\u6b66\u7384\u971c\u673a\u7075\u4e4b\u6781\uff0c \u5251\u52bf\u865a\u591a\u5b9e\u5c11\uff0c\u4e00\u6cbe\u5373\u8d70\uff0c\u77ac\u5373\u767e\u53d8\uff0c\u907f\u514d\u548c\u8c37\u795e\u7fc1\u786c\u6253\u786c\u62fc\uff0c\u8fd9\u6837\u6e38\u6597\u7684\u7ed3\u679c\uff0c\u867d\u7136 \u4ecd\u662f\u8c37\u795e\u7fc1\u5360\u4e0a\u98ce\uff0c\u4f46\u770b\u8fd9\u60c5\u5f62\uff0c\u8c37\u4f38\u7fc1\u4ea6\u81ea\u5fc3\u77e5\uff0c\u975e\u6597\u5230\u4e00\u5343\u62db\u4e4b\u5916\uff0c\u53ea\u6015\u96be\u5206\u80dc\u8d25\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u7136\u542c\u5f97\u5bfc\u58f0\u66f3\u7a7a\uff0c\u4eff\u82e5\u5c24\u541f\u864e\u5578\uff0c\u8c37\u4f38\u7fc1\u5fc3\u5934\u4e00\u51db\uff0c\u4f46\u542c\u5f97\u6709\u4eba\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u8c37\u8001\u5f1f\uff0c\u5341\u5e74\u672a\u89c1\uff0c\u4f60\u7684\u5251\u6cd5\u8fdb\u5883\u5982\u4f55\uff1f\u5c0f\u5144\u6765\u770b\u4f60\u4e86\u3002\u201d\u58f0\u5230\u4eba\u5230\uff0c\u573a\u4e0a\u7fa4\u96c4\uff0c\u9a87 \u7136\u6ce8\u76ee\uff0c\u53ea\u89c1\u6765\u7684\u4eba\u4e00\u88ad\u9752\u5dfe\uff0c\u8eab\u4e0a\u7684\u4e00\u4ef6\u9752\u8272\u957f\u886b\uff0c\u8138\u4e0a\u4e5f\u900f\u51fa\u4e00\u5c42\u9752\u6c14\uff0c\u4e0d\u77e5\u600e\u7684\uff0c \u4e00\u89c1\u4e4b\u4e0b\uff0c\u5c31\u4ee4\u4eba\u89c9\u788d\u60f4\u60f4\u4e0d\u5b89\uff0c\u800c\u4e14\uff0c\u8fd9\u4eba\u7684\u76f8\u8c8c\u770b\u6765\u8fd8\u672a\u5230\u4e94\u5341\u5e74\u7eaa\uff0c\u52be\u4e0b\u6709\u51e0\u6839 \u957f\u9aef\uff0c\u72b6\u5982\u843d\u62d3\u4e0d\u7f81\u7684\u540d\u58eb\uff0c\u8bba\u76f8\u8c8c\uff0c\u4f3c\u6bd4\u8c37\u795e\u7fc1\u5e74\u8f7b\u5f97\u591a\uff0c\u4f46\u4ed6\u5374\u53eb\u8c37\u795e\u7fc1\u505a\u201c\u8001\u5f1f\uff01\u201d \u3000\u3000\u7fa4\u96c4\u6ce8\u76ee\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8c37\u7985\u7fc1\u7684\u9762\u8272\u767d\u91cc\u900f\u7ea2\uff0c\u5251\u62db\u6e10\u89c1\u51cc\u4e71\uff0c\u90a3\u9752\u8863\u4eba\u770b\u4e86\u7247\u523b\uff0c \u6447\u4e86\u6447\u5934\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u795e\u7fc1\u81ea\u8d1f\u8e51\u4e91\u5251\uff0c\u91d1\u9876\u4e89\u96c4\u5f97\u80dc\u65e0\uff1f\u53ea\u6015\u865a\u540d\u771f\u8bef\u4f60\uff0c\u5e73\u6dfb\u7b11 \u8bdd\u843d\u6c5f\u6e56\uff01\u201d \u3000\u3000\u8c37\u79cd\u7fc1\u9762\u8272\u8d8a\u53d1\u6da8\u7ea2\uff0c\u539f\u6765\u8fd9\u4eba\u540d\u53eb\u7b26\u4e0d\u7591\uff0c\u4e43\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u4e2a\u9690\u58eb\uff0c\u884c\u4e8b\u9887\u4e3a\u602a \u8bde\uff0c\u8c37\u795e\u7fc1\u548c\u4ed6\u4ee5\u524d\u751a\u6709\u4ea4\u60c5\uff0c\u53ea\u4e3a\u4e00\u6b21\u4ed6\u8ba5\u8bc4\u8c37\u795e\u7fc1\u7684\u5251\u6cd5\uff0c\u8c37\u795e\u7fc1\u548c\u4ed6\u5435\u4e86\u8d77\u6765\uff0c \u4e24\u4eba\u4e0d\u6b22\u800c\u6563\u3002\u4e00\u522b\u5341\u591a\u5e74\uff0c\u4e0d\u6599\u800c\u4eca\uff0c\u4ed6\u4e5f\u7a81\u7136\u6765\u5230\u4e86\u5ce8\u5d4b\u91d1\u9876\uff0c\u53c8\u6070\u6070\u78b0\u5230\u4e86\u8c37\u795e \u7fc1\u548c\u6b66\u7384\u971c\u6bd4\u5251\uff0c\u56e0\u6b64\u4e00\u5230\u573a\u4fbf\u4f5c\u6253\u6cb9\u8bd7\u6765\u5632\u7b11\u4ed6\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u88ab\u7b26\u4e0d\u7591\u5632\u7b11\u5f97\u9762\u7ea2\u8033\u8d64\uff0c\u9ad8\u624b\u6bd4\u6597\uff0c\u90a3\u5bb9\u5206\u5fc3\uff0c\u53ea\u542c\u5f97\u5693\u7684\u4e00\u58f0\uff0c\u9752\u5149\u95ea \u5904\uff0c\u6b66\u7384\u971c\u4e00\u5251\u4ece\u4ed6\u5934\u9876\u524a\u8fc7\uff0c\u8c37\u795e\u7fc1\u970d\u5730\u4e2a\u4e00\u4e2a\u51e4\u70b9\u5934\uff0c\u582a\u582a\u907f\u5f00\uff0c\u53ea\u5dee\u534a\u5bf8\uff0c\u9669\u4e9b \u5c31\u8981\u7ed9\u5979\u524a\u53bb\u4e00\u5c42\u5934\u76ae\uff0c\u7b26\u4e0d\u7591\u53c8\u5927\u7b11\u559d\u9053\uff1a\u201c\u9669\u4e9b\u9001\u6389\u8001\u5934\u76ae\uff0c\u5982\u4eca\u4f4e\u9996\u62dc\u5a25\u7709\uff01\u201d \u6b66\u7384\u971c\u63a5\u7740\u7b11\u9053\uff1a\u201c\u76df\u4e3b\u96c4\u98ce\u968f\u901d\u6c34\uff0c\u7b11\u715e\u5929\u5c71\u7b26\u4e0d\u7591\u3002\u201d \u3000\u3000\u573a\u4e0a\u7fa4\u8c6a\u5bf9\u7b26\u4e0d\u7591\u662f\u4e45\u95fb\u5176\u540d\uff0c\u5374\u4e0d\u8ba4\u8bc6\u5176\u4eba\uff0c\u800c\u4eca\u4e00\u542c\uff0c\u8fd9\u4e2a\u602a\u5ba2\u7adf\u7136\u662f\u7b26\u4e0d\u7591\uff0c \u90fd\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002\u8c37\u795e\u7fc1\u4e5f\u6697\u6697\u5600\u5495\uff0c\u5fc3\u4e2d\u60f3\u9053\uff0c\u201c\u539f\u672a\u4ed6\u4eec\u662f\u76f8\u8bc6\u7684\u3002\u8fd9\u4e2a\u5973\u5a03\u5b50\u6562 \u76f4\u547c\u5176\u540d\uff0c\u80c6\u91cf\u4e0d\u5c0f\u3002\u5979\u7684\u5e08\u7236\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u201d\u5fc3\u4e2d\u4e0d\u5b81\uff0c\u5251\u6cd5\u66f4\u4e71\uff0c\u4ed6\u672c\u6765\u662f\u80dc\u6b66\u7384 \u971c\u4e00\u7b79\uff0c\u8fd9\u65f6\u5374\u53cd\u800c\u7ed9\u6b66\u7384\u971c\u8feb\u5f97\u6b65\u6b65\u540e\u9000\u3002\u7b26\u4e0d\u7591\u4eba\u7b11\u9053\uff1a\u201c\u8c37\u8001\u5f1f\uff0c\u4f60\u8fd9\u573a\u6bd4\u5251\u65e9 \u5df2\u8f93\u4e86\uff0c\u8fd8\u6bd4\u4ec0\u4e48\uff1f\u4e0d\u5982\u54b1\u4eec\u54e5\u513f\u4fe9\u53bb\u559d\u676f\u9152\u5427\uff01\u201d \u3000\u3000\u8c37\u795e\u7fc1\u89c1\u9080\u6765\u7684\u51e0\u4e2a\u9ad8\u624b\u90fd\u5df1\u98d8\u7136\u800c\u8d70\uff0c\u8fde\u65b0\u76df\u4e3b\u674e\u9038\u4ea6\u4e0d\u77e5\u53bb\u5411\uff0c\u4e00\u60f3\u8fd9\u573a\u6bd4\u5251 \u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u5f53\u4e0b\u5fc3\u7070\u610f\u51b7\uff0c\u683c\u4ec0\u4e86\u6b66\u7384\u971c\u7684\u4e00\u5251\uff0c\u7acb\u523b\u8df3\u51fa\u5708\u5b50\uff0c\u98de\u5954\u4e0b\u5c71\u3002\u7b26\u4e0d \u7591\u53eb\u9053\uff1a\u201c\u5582\uff0c\u7b49\u7b49\u6211\u5440\uff01\u54c8\uff0c\u4f60\u4e0d\u80af\u7b49\u6211\uff1f\u597d\uff0c\u54b1\u4eec\u5c31\u63a5\u7740\u6bd4\u4e00\u573a\u8f7b\u529f\uff01\u201d\u563b\u563b\u54c8\u54c8\uff0c \u8ffd\u8c37\u795e\u7fc1\u53bb\u4e86\u3002\u8fd9\u4e24\u4eba\u8f7b\u529f\u9ad8\u7edd\uff0c\u7b26\u4e0d\u7591\u7684\u7b11\u58f0\u8fd8\u5728\u5c71\u8c37\u4e4b\u4e2d\u56de\u65cb\uff0c\u4ed6\u4eec\u7684\u80cc\u5f71\u5374\u65e9\u5df2 \u4e0d\u89c1\u3002 \u3000\u3000\u65b0\u65e7\u76df\u4e3b\u90fd\u4e0a\u4e86\uff0c\u7fa4\u9f99\u65e0\u9996\uff0c\u573a\u4e2d\u5927\u4e71\u3002\u6b66\u7384\u971c\u53eb\u9053\uff1a\u201c\u660e\u73e0\u3001\u5982\u610f\uff0c\u4f60\u4eec\u8fd8\u6ca1\u6709 \u5c06\u8fd9\u73ed\u5927\u82f1\u96c4\u7684\u5175\u5668\u7f34\u5b8c\u5417\uff1f\u201d\u5982\u610f\u7b54\u9053\u201c\u5dee\u4e0d\u591a\u5566\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u7f34\u5b8c\u4e86\u68b0\uff0c\u5c31\u7ed9 \u6211\u628a\u4ed6\u4eec\u7684\u6b66\u529f\u5168\u90fd\u5e9f\u6389\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u53ea\u542c\u5f97\u54d7\u54d7\u5566\u5566\u4e00\u7247\u58f0\u54cd\uff0c\u8fd8\u672a\u66fe\u88ab\u7f34\u68b0\u7684\u4eba \u90fd\u628a\u5175\u5668\u629b\u6389\uff0c\u56db\u6563\u5954\u9003\uff0c\u53ea\u6068\u7239\u5a18\u751f\u5c11\u4e86\u4e24\u6761\u817f\u3002\u6b66\u7384\u971c\u4ef0\u5929\u5927\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u82f1\u96c4\u5927 \u4f1a\uff0c\u98ce\u6d41\u4e91\u6563\uff0c\u7701\u5374\u54b1\u4eec\u4e00\u756a\u6c14\u529b\uff0c\u5c31\u8ba9\u4ed6\u4eec\u53bb\u5427\u3002\u660e\u73e0\uff0c\u4f60\u7ed9\u6211\u770b\u770b\u4e0a\u5b98\u59b9\u5b50\u53bb\u3002\u201d \u3000\u3000\u674e\u9038\u5728\u5cf0\u7855\u76ee\u7779\uff0c\u89c1\u82f1\u96c4\u5927\u4f1a\u74e6\u89e3\u51b0\u6d88\uff0c\u5fc3\u5934\u60b2\u75db\u4e4b\u6781\u3002\u9eef\u7136\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u573a \u6bd4\u5251\u6211\u662f\u65e9\u5df2\u8f93\u4e86\uff01\u4e0d\u662f\u8f93\u7ed9\u4e86\u8fd9\u4e2a\u5973\u5b50\uff0c\u800c\u662f\u8f93\u7ed9\u4e86\u6b66\u5219\u5929\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c1\u6b66\u7384 \u971c\u4e5f\u5954\u4e0a\u5c71\u6765\u3002\u674e\u9038\u5fc3\u5934\u51b0\u51b7\uff0c\u8c6a\u6c14\u5168\u6d88\uff0c\u4e0d\u613f\u548c\u5979\u518d\u6218\uff0c\u6025\u5fd9\u4ece\u4e43\u4e00\u9762\u4e0b\u5c71\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u88ab\u96c4\u5de8\u9f0e\u51fb\u6655\u4e4b\u540e\uff0c\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u4f3c\u89c9\u6709\u4eba\u7ed9\u81ea\u5df1\u63a8\u8840\u8fc7\u5bab\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a \u4e45\uff0c\u60a0\u60a0\u9192\u8f6c\uff0c\u53ea\u89c1\u9633\u5149\u8000\u773c\uff0c\u5df2\u662f\u7b2c\u4e8c\u5929\u7684\u4e2d\u5348\u65f6\u5206\uff0c\u56de\u5fc6\u6628\u591c\u79cd\u79cd\u60c5\u4e8b\uff0c\u771f\u5982\u505a\u4e86 \u4e00\u573a\u6076\u68a6\u3002\u7741\u773c\u770b\u65f6\uff0c\u8349\u576a\u7a7a\u8361\u8361\u7684\u9664\u4e86\u81ea\u5df1\u4e4b\u5916\uff0c\u8fde\u9b3c\u5f71\u4e5f\u4e0d\u89c1\u4e00\u4e2a\uff0c\u5175\u5668\u5374\u6563\u6ee1\u4e86 \u4e00\u5730\uff0c\u5200\u67aa\u5251\u621f\uff0c\u4ec0\u4e48\u90fd\u6709\u3002\u4e0a\u5b98\u5a49\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u82f1\u96c4\u5927\u4f1a\uff0c\u5982\u6b64\u6563\u4e86\u4e5f \u597d\u3002\u53ea\u662f\u90a3\u4e2a\u6b66\u7384\u971c\uff0c\u5979\u4e3a\u4f55\u5c06\u6211\u6551\u4e86\uff0c\u5374\u53c8\u5c06\u6211\u629b\u5728\u8fd9\u513f\uff1f\u201d\u773c\u5149\u4e00\u77a5\uff0c\u5ffd\u89c1\u8eab\u65c1\u7684 \u4e00\u68f5\u6811\u4e0a\u3002\u6709\u5251\u5c16\u6240\u5212\u7684\u51e0\u884c\u5b57\u8ff9\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e03\u56de\u3000\u523a\u5ba2\u591a\u6101\u611f\u660e\u4e3b \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e03\u56de\u3000\u523a\u5ba2\u591a\u6101\u611f\u660e\u4e3b \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e00\u770b\uff0c\u5199\u7684\u662f\u56db\u53e5\u8bd7\uff0c\u8bd7\u9053\uff1a\u201c\u662f\u975e\u5c82\u96be\u8fa8\uff1f\u771f\u5047\u603b\u5206\u660e\uff01\u6b64\u9645\u6682\u5206\u624b\u3001 \u4ed6\u5e74\u613f\u4e00\u5fc3\u3002\u201d\u8bd7\u540e\u7684\u8457\u540d\u662f\u201c\u7384\u971c\u201d\u4e8c\u5b57\u3002\u4e0a\u5bab\u5a49\u513f\u4f55\u7b49\u806a\u660e\uff0c\u5fc3\u4e2d\u7565\u4e00\u7422\u78e8\uff0c\u4fbf\u77e5 \u8bd7\u610f\uff0c\u60f3\u9053\uff1a\u201c\u5982\u4eca\u5929\u4e0b\u5206\u6210\u4e24\u6d3e\uff0c\u4e00\u6d3e\u53cd\u5bf9\u6b66\u5219\u5929\uff0c\u4e00\u6d3e\u62e5\u62a4\u6b66\u5219\u5929\u3002\u53cd\u5bf9\u5979\u7684\u628a\u5979 \u8bf4\u6210\u662f\u90aa\u9b54\u86c7\u874e\uff0c\u62e5\u62a4\u5979\u7684\u5219\u628a\u5979\u8bf4\u6210\u662f\u5723\u5e1d\u660e\u541b\u3002\u6211\u662f\u524d\u4e00\u6d3e\uff0c\u6b66\u7384\u971c\u5219\u662f\u540e\u4e00\u6d3e\u3002 \u6b66\u7384\u971c\u8ba4\u4e3a\u5979\u662f\u5bf9\u7684\uff0c\u6240\u4ee5\u5979\u8bf4\uff1a\u2018\u662f\u975e\u5c82\u96be\u8fa8\uff0c\u771f\u5047\u603b\u5206\u660e\u3002\u2019\u5979\u73b0\u5728\u4e0d\u613f\u5f3a\u6211\u4ece\u5979\uff0c \u6240\u4ee5\u6682\u65f6\u548c\u6211\u5206\u624b\uff1b\u5979\u5e0c\u671b\u65e5\u540e\u6211\u660e\u767d\u4e86\u771f\u5047\u662f\u975e\uff0c\u4fbf\u4f1a\u4e0e\u5979\u540c\u5fc3\u4e00\u610f\u3002\u201d \u3000\u3000\u8bd7\u610f\u867d\u660e\uff0c\u5fc3\u5934\u5374\u4e71\u3002\u4e0a\u5b98\u5a49\u513f\u60d8\u60d8\u7136\u6709\u5982\u4e71\u4e1d\u585e\u80f8\uff0c\u832b\u65e0\u6761\u7406\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6b66 \u5219\u5929\u7eb5\u7136\u4e0d\u662f\u90aa\u9b54\u86c7\u874e\uff0c\u4f46\u4e5f\u4e0d\u89c1\u5f97\u4fbf\u662f\u5723\u5e1d\u660e\u541b\u3002\u96be\u9053\u5979\u6740\u4e86\u6211\u7956\u7236\u3001\u7236\u4eb2\u4e5f\u662f\u5bf9\u7684 \u4e48\uff1f\u522b\u4eba\u53ef\u4ee5\u62e5\u62a4\u6b66\u5219\u5929\uff0c\u6211\u8fd9\u8840\u6d77\u6df1\u4ec7\uff0c\u5374\u662f\u4e0d\u80fd\u4e0d\u62a5\u3002\u5440\uff0c\u53ef\u60dc\u674e\u9038\u54e5\u54e5\u5df2\u8d70\u5f97\u4e0d \u89c1\u4e86\uff0c\u8981\u4e0d\u7136\u5012\u5475\u4ee5\u548c\u4ed6\u5546\u91cf\u5546\u91cf\u3002\u201d\u601d\u5ff5\u53ca\u6b64\uff0c\u4e00\u770b\u6563\u6ee1\u5730\u4e0a\u7684\u5175\u5668\uff0c\u5374\u53c8\u4e0d\u7981\u54d1\u7136 \u5927\u7b11\uff0c\u5fc3\u77e5\u548c\u674e\u9038\u5546\u91cf\uff0c\u4e5f\u5b9a\u662f\u5546\u91cf\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\u3002\u5979\u548c\u674e\u9038\u867d\u7136\u662f\u540c\u6837\u7684\u75db\u6068\u6b66\u5219\u5929\uff0c \u4f46\u6240\u60f3\u7684\u505a\u6cd5\u5374\u53c8\u4e0d\u540c\u3002\u4e0a\u5b98\u5a49\u513f\u6478\u4e00\u6478\u6697\u5668\u56ca\u4e2d\u7684\u5315\u9996\uff0c\u60f3\u8d77\u4e86\u957f\u5b59\u5747\u91cf\u7684\u5429\u5490\uff0c\u5fc3 \u9053\uff1a\u201c\u6211\u4f55\u5fc5\u7275\u7d2f\u4ed6\u4eba\uff1f\u6211\u5c3d\u6211\u7684\u529b\u91cf\uff0c\u82e5\u5f97\u4e0a\u5929\u4fdd\u4f51\uff0c\u4e00\u628a\u5315\u9996\u5c31\u5c06\u5979\u523a\u6740\u4e86\uff0c\u4e5f\u7701 \u5f97\u5929\u4e0b\u7eb7\u7eb7\u3002\u201d\u5fc3\u610f\u4e00\u8bc0\uff0c\u4e8e\u662f\u4fbf\u8eab\u6000\u5315\u9996\uff0c\u72ec\u4e0a\u957f\u5b89\u3002 \u3000\u3000\u8d70\u4e86\u4e8c\u5341\u591a\u5929\uff0c\u8fd9\u4e00\u65e5\u9ec4\u660f\u65f6\u5206\uff0c\u6765\u5230\u6f7c\u61a7\uff0c\u6893\u6f7c\u662f\u4e00\u4e2a\u5c71\u57ce\uff0c\u5e73\u5e38\u7684\u53e3\u5b50\uff0c\u5165\u9ed1 \u4e4b\u540e\uff0c\u8857\u4e0a\u4fbf\u884c\u4eba\u5be5\u843d\uff0c\u8fd9\u4e00\u5929\u5374\u662f\u4eba\u5934\u7c07\u68da\u3002\u4e0a\u5b98\u5a49\u513f\u8d77\u521d\u8fd8\u4ee5\u4e3a\u662f\u4ec0\u4e48\u8282\u65e5\uff0c\u5411\u4e00 \u4e2a\u8001\u8005\u8bf7\u95ee\uff0c\u51fa\u4e4e\u5979\u7684\u610f\u5916\uff0c\u542c\u5230\u4e86\u4e00\u4e2a\u4ee4\u5979\u53c8\u559c\u53c8\u60ca\u7684\u6d88\u606f\uff01\u539f\u6765\u7adf\u7136\u662f\u6b66\u5219\u5929\u6765\u5230 \u8fd9\u4e2a\u53bf\u57ce\uff01 \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u4e0a\u6708\u5148\u592a\u5b50\u5728\u5df4\u5dde\u88ab\u4eba\u6697\u6740\uff0c\u5de6\u91d1\u543e\u5927\u5c06\u519b\u4e18\u4f38\u52cb\u81ea\u8bf7\u8d2c\u804c\uff0c\u51f6\u624b\u76f4 \u5230\u5982\u4eca\u8fd8\u6ca1\u6709\u6349\u5230\u3002\u542c\u8bf4\u5929\u540e\u6b64\u6b21\u5165\u8700\uff0c\u4e00\u6765\u662f\u4e3a\u4e86\u67e5\u7a76\u8fd9\u4ef6\u6848\u5b50\uff0c\u4e8c\u6765\u4e5f\u8d81\u6b64\u5de1\u89c6\u5907 \u5730\uff0c\u535a\u91c7\u6c11\u60c5\u3002\u5979\u6765\u5230\u8fd9\u91cc\u672a\u591f\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5df2\u7ecf\u63a5\u89c1\u4e86\u597d\u51e0\u4f4d\u5730\u65b9\u7236\u8001\u5462\u3002\u8fd9\u4e9b\u4eba\u6709\u4e9b \u662f\u53bb\u544a\u72b6\u7684\uff0c\u6709\u4e9b\u662f\u76fc\u671b\u80fd\u4e00\u89c1\u5929\u540e\u7684\u989c\u8272\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u51e0\u60f3\u8d77\u4e86\u90a3\u4e00\u665a\u5728\u5df4\u5dde\u6240\u89c1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5979\u6740\u4e86\u81ea\u5df1\u7684\u513f\u5b50\uff0c\u5374\u53c8\u6765\u8ffd\u67e5 \u51f6\u624b\u3002\u96be\u9053\u662f\u6545\u610f\u505a\u4f5c\uff0c\u60f3\u906e\u63a9\u5929\u4e0b\u4eba\u7684\u8033\u76ee\u4e48\uff1f\u201d\u5fc3\u5934\u6000\u7591\u76ca\u751a\uff0c\u95f4\u90a3\u8001\u8005\u9053\uff0c\u201c\u592d \u540e\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u4e5f\u60f3\u53bb\u770b\u770b\u70ed\u95f9\uff0c\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u4f4f\u5728\u4e0e\u53bf\u8859\u76f8\u90bb\u7684\u5b66\u5bab\u3002\u5440\uff0c\u8001 \u592b\u7ecf\u5386\u51e0\u671d\uff0c\u53ef\u8fd8\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u6837\u5e73\u6613\u8fd1\u4eba\u7684\u7687\u5e1d\uff0c\u602a\u4e0d\u5f97\u6709\u8bb8\u591a\u4eba\u9a82\u5979\uff0c\u5374\u6709\u66f4\u591a\u7684 \u4eba\u670d\u5979\u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8c22\u8fc7\u90a3\u4f4d\u8001\u8005\uff0c\u627e\u4e86\u4e00\u95f4\u5ba2\u5e97\u5b89\u6b47\uff0c\u5230\u4e09\u66f4\u65f6\u5206\uff0c\u4fbf\u6362\u4e86\u591c\u884c\u8863\u670d\uff0c\u6000\u4e86 \u5315\u9996\uff0c\u6084\u6084\u7684\u6765\u5230\u6b66\u5219\u5929\u6240\u4f4f\u7684\u5b66\u5bab\uff0c\u51c6\u5907\u5c06\u5979\u523a\u6740\uff01 \u3000\u3000\u4f46\u89c1\u5b66\u5bab\u524d\u9762\u53ea\u6709\u4e00\u4e2a\u770b\u95e8\u7684\u516c\u4eba\uff0c\u800c\u4e14\u4e0d\u5e26\u5175\u5668\uff0c\u5728\u4e0a\u5b98\u5a49\u513f\u60f3\u50cf\u4e4b\u4e2d\uff0c\u4ee5\u4e3a\u5b9a \u662f\u5b88\u536b\u68ee\u4e25\uff0c\u54ea\u77e5\u5374\u662f\u8fd9\u822c\u73b0\u8c61\uff01\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6b66\u5219\u706d\u600e\u7684\u8fd9\u4e48\u5927\u80c6\uff0c\u5979\u7adf\u7136 \u4e0d\u6015\u523a\u5ba2\uff1f\u54c8\uff0c\u8fd9\u53ef\u6b63\u662f\u5929\u8d50\u826f\u673a\uff01\u201d\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u4e00\u6478\u5315\u9996\uff0c\u624b\u6307\u5374\u662f\u5fae\u5fae\u53d1\u6296\uff0c \u5fc3\u4e2d\u4ea6\u81ea\u60f4\u60f4\u4e0d\u5b89\uff0c\u5979\u5012\u613f\u610f\u6b66\u5219\u5929\u662f\u5979\u60f3\u50cf\u4e2d\u7684\u9b54\u541b\uff0c\u8fd9\u624d\u53ef\u4ee5\u4ee4\u5979\u63d0\u5f97\u8d77\u6740\u4eba\u7684\u52c7 \u6c14\u3002\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6b66\u5219\u5929\u7adf\u4f3c\u5168\u65e0\u9632\u8303\uff0c\u8f7b\u8f7b\u6613\u6613\u7684\u4fbf\u8ba9\u523a\u5ba2\u8fdb\u4e86\u5979\u201c\u9a7b\u8df8\u201d\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8c79\u8f7b\u529f\u672c\u6765\u4e86\u5f97\uff0c\u5b66\u5bab\u4e0d\u8fc7\u5341\u591a\u95f4\u623f\u5b50\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5979\u5df2\u524d\u540e\u5de6\u53f3\u8d70\u4e86\u4e00 \u8f6c\uff0c\u5b66\u5bab\u91cc\u867d\u7136\u4e5f\u6709\u5341\u591a\u540d\u6043\u536b\uff0c\u5374\u6ca1\u4e00\u4e2a\u4eba\u53d1\u73b0\u5979\u3002\u4e0a\u5b98\u5a49\u513f\u770b\u6e05\u4e86\u56db\u65b9\u7684\u5f62\u52bf\u4e4b\u540e\uff0c \u4fbf\u5411\u6b63\u4e2d\u7684\u4e00\u5ea7\u623f\u5b50\u6251\u53bb\uff0c\u623f\u4e2d\u706f\u706b\u901a\u660e\uff0c\u91cc\u9762\u6709\u51e0\u4e2a\u4eba\u5f71\uff0c\u4e0a\u5b98\u5a49\u513f\u4e0a\u4e86\u5c4b\u9876\uff0c\u811a\u5c16 \u52fe\u7740\u5c4b\u6a90\uff0c\u7528\u4e00\u4e2a\u201c\u73cd\u73e0\u5012\u5377\u5e18\u201d\u7684\u59ff\u52bf\uff0c\u540a\u4e0b\u4e00\u622a\u8eab\u8eaf\uff0c\u624b\u634f\u5315\u9996\uff0c\u4f38\u5934\u4e00\u7aa5\uff0c\u6b66\u5219 \u5929\u679c\u7136\u5c31\u5728\u8fd9\u623f\u95f4\u91cc\u9762\uff0c\u5979\u7684\u684c\u5b50\u4e0a\u5806\u6ee1\u6587\u5377\uff0c\u4f8d\u7acb\u7684\u4e24\u4eba\uff0c\u4e00\u4e2a\u662f\u8001\u5927\u76d1\uff0c\u8fd8\u6709\u4e00\u4e2a \u5219\u662f\u5e74\u8f7b\u7684\u5bab\u5973\u3002\u6b66\u5219\u5929\u5168\u795e\u8d2f\u6ce8\u7684\u7ffb\u9605\u90a3\u4e9b\u6587\u5377\uff0c\u4e45\u4e0d\u4e45\u62ac\u8d77\u5934\u6765\uff0c\u4e24\u773c\u95ea\u95ea\u653e\u5149\uff0c \u4f3c\u4e4e\u662f\u770b\u5230\u4e86\u7591\u96be\u4e4b\u5904\uff0c\u5728\u5fc3\u4e2d\u4ed4\u7ec6\u7422\u78e8\u4e00\u6837\u3002\u4e0a\u5b98\u5a49\u513f\u597d\u51e0\u6b21\u78b0\u5230\u5979\u7684\u773c\u5149\uff0c\u5fc3\u4e2d\u90fd \u4e0d\u81ea\u7981\u7684\u5fae\u5fae\u53d1\u6296\u3002\u7b97\u6765\u6b66\u5219\u5929\u8be5\u6709\u516d\u5341\u5c81\u4e86\uff0c\u5374\u8fd8\u6ca1\u6709\u534a\u70b9\u9f99\u949f\u8001\u6001\uff0c\u5c24\u5176\u90a3\u53cc\u773c\u775b \u66f4\u662f\u70af\u70af\u6709\u795e\uff0c\u597d\u50cf\u53ef\u4ee5\u770b\u7a7f\u4eba\u7684\u80ba\u8151\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u6b66\u5219\u5929\u7ffb\u6c57\u4e86\u4e00\u5377\u6848\u5b97\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u516c\u516c\u4f60\u66ff\u6211\u628a\u53bf\u4ee4\u53eb\u6765\u3002\u201d\u90a3 \u8001\u592a\u76d1\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\uff0c\u4f60\u5728\u671d\u4e2d\u65e5\u591c\u4e3a\u56fd\u4e8b\u64cd\u52b3\uff0c\u5230\u5730\u65b9\u4e0a\u6765\u5de1\u89c6\uff0c\u4e5f\u8fd8\u662f\u4e0d\u80af\u4f11\u606f\uff0c \u4f60\u4e5f\u8be5\u4fdd\u91cd\u4fdd\u91cd\u554a\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\uff0c\u8001\u767e\u59d3\u4fe1\u8d56\u6211\uff0c\u6211\u600e\u80fd\u8d1f\u4ed6\u4eec\u7684\u671f\u671b\u3002\u6211\u5c11\u7761 \u4e00\u4e9b\u4e0d\u6253\u7d27\uff0c\u8fd9\u4ef6\u6848\u5b50\u53ef\u662f\u5173\u7cfb\u7740\u4e24\u6761\u4eba\u547d\u554a\u3002\u4f60\u4e0d\u5fc5\u591a\u8a00\uff0c\u5feb\u66ff\u6211\u628a\u53bf\u4ee4\u53eb\u6765\uff0c\u201d\u90a3 \u8001\u5927\u76d1\u53f9\u4e86\u53e3\u6c14\uff0c\u65e0\u53ef\u5948\u4f55\u7684\u8d70\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u623f\u4e2d\u53ea\u5269\u4e0b\u4e86\u6b66\u5219\u5929\u548c\u90a3\u5e74\u8f7b\u7684\u5bab\u5973\uff0c\u4e0a\u5b98\u5a49\u513f\u5b50\u634f\u5315\u9996\uff0c\u8fd9\u65f6\u53ea\u8981\u5979\u5315\u9996\u4e00\u53d1\uff0c \u6b66\u5219\u5929\u7684\u6027\u547d\u5df2\u662f\u6fa1\u5728\u5979\u7684\u624b\u4e2d\uff0c\u4f46\u6b64\u9645\u5979\u5fc3\u4e2d\u5ffd\u7136\u8d77\u4e86\u4e00\u4e2a\u597d\u5947\u4e4b\u5ff5\uff0c\u8981\u770b\u770b\u6b66\u5219\u5929 \u600e\u6837\u5ba1\u6848\u3002\u5979\u51e0\u6b21\u6293\u8d77\u4e86\u5315\u9996\uff0c\u7ec8\u4e8e\u53c8\u628a\u5b83\u653e\u56de\u6697\u5668\u56ca\u4e2d\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u8001\u5927\u76d1\u5c06\u53bf\u5b98\u5e26\u4e86\u8fdb\u7c73\uff0c\u539f\u7c73\u5730\u65b9\u4e0a\u7684\u5b98\u5458\u90fd\u77e5\u9053\u6b66\u5219\u5929\u51fa\u5de1\u7684\u4e60\u60ef\uff0c \u5979\u6bcf\u5230\u4e00\u5730\uff0c\u5fc5\u5b9a\u8981\u8c03\u5730\u65b9\u8859\u95e8\u91cc\u7684\u6848\u4ef6\u6765\u5ba1\u9605\uff0c\u53bf\u5b98\u54ea\u91cc\u6562\u7761\uff0c\u4e00\u76f4\u5728\u5916\u9762\u4f8d\u5019\u7740\uff0c \u8fd9\u65f6\u88ab\u6b66\u5219\u5929\u5524\u8fdb\u6765\uff0c\u8138\u8272\u5413\u5f97\u9752\u767d\uff0c\u8dea\u5728\u5730\u4e0a\u8fde\u78d5\u4e86\u5341\u4e03\u516b\u4e2a\u54cd\u5934\u3002 \u3000\u3000\u6b66\u5219\u5929\u5c06\u4e00\u5377\u6848\u5b97\u63b7\u4e86\u4e0b\u6765\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u518d\u770b\u4e00\u770b\u8fd9\u5b97\u6848\u5b50\uff01\u201d \u3000\u3000\u90a3\u53bf\u5b98\u78d5\u5934\u9053\uff1a\u201c\u5351\u804c\u7cca\u6d82\uff0c\u8bf7\u5929\u540e\u965b\u4e0b\u660e\u793a\uff0c\u4e0d\u77e5\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a \u201c\u8fd9\u662f\u4ec0\u4e48\u6848\u5b50\uff1f\u201d\u53bf\u5b98\u6367\u7740\u5377\u5b97\u8bfb\u9053\uff1a\u201c\u6deb\u5c3c\u5999\u7389\uff0c\u4e0d\u8fc7\u6e05\u89c4\uff0c\u6709\u4f24\u98ce\u5316\u6848\u2026\u2026\u201d\u6b66 \u5219\u5929\u9053\uff1a\u201c\u4e0d\u5fc5\u8be6\u8bfb\u63a7\u6587\u4e86\uff0c\u4f60\u7b80\u5355\u8bf4\u8bf4\u6848\u60c5\u3002\u201d\u90a3\u5177\u5b98\u9053\uff0c\u201c\u8fd9\u4ef6\u6848\u5b50\u662f\u738b\u5343\u6237\u544a\u6c34 \u6708\u5eb5\u7684\u5c3c\u59d1\u5999\u7389\u52fe\u5f15\u4ed6\u7684\u513f\u5b50\u738b\u5f6a\uff0c\u901a\u5978\u6210\u5b55\uff0c\u8bf7\u6c42\u53d1\u843d\u6848\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4f60\u600e\u6837\u5224 \u51b3\uff1f\u201d\u53bf\u5b98\u9053\uff1a\u201c\u7740\u5b98\u5a92\u5c06\u80ce\u6253\u843d\uff0c\u7136\u540e\u5c06\u5999\u7389\u9010\u51fa\u6c99\u95e8\uff0c\u6253\u4e94\u5341\u97ad\u3002\u7f5a\u4e3a\u5b98\u5974\u3002\u201d\u6b66 \u5219\u5929\u9053\uff1a\u201c\u5bf9\u738b\u5343\u6237\u7684\u513f\u5b50\u5462\uff1f\u201d\u53bf\u5b98\u9053\uff1a\u201c\u5224\u4ee4\u7531\u4ed6\u7684\u7236\u4eb2\u4e25\u52a0\u7ba1\u6559\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u738b\u5343\u6237\u5bb6\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u53bf\u5b98\u9053\uff1a\u201c\u4f4f\u5728\u897f\u95e8\u3002\u201d \u6b66\u5219\u5929\u9053\uff1a\u201c\u90a3\u4e2a\u5c3c\u59d1\u5462\uff1f\u201d\u53bf\u5b98\u9053\uff1a\u201c\u4f4f\u5728\u57ce\u4e1c\u7684\u6c34\u6708\u5eb5\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e24\u5730\u8ddd\u79bb \u591a\u8fdc\uff1f\u201d\u53bf\u5b98\u9053\uff1a\u201c\u5927\u7ea6\u6709\u5341\u591a\u91cc\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u65e2\u7136\u76f8\u8ddd\u5341\u591a\u91cc\uff0c\u4e00\u4e2a\u5e74\u9752\u7684\u5c3c\u59d1\uff0c \u6562\u4e0a\u95e8\u53bb\u52fe\u5f15\u738b\u5343\u6237\u7684\u513f\u5b50\u5417\uff1f\u201d\u53bf\u5b98\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5728\u6c34\u6708\u5eb5\u901a\u5978\u7684\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u62cd\u4e86\u4e00\u4e0b\u6848\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u7167\u8fd9\u6837\u8bf4\u6765\uff0c\u5373\u7b97\u738b\u5343\u6237\u7684\u513f\u5b50\u4e0d \u662f\u8feb\u5978\uff0c\u6700\u5c11\u4e5f\u662f\u4ed6\u5230\u6c34\u6708\u5eb5\u53bb\u52fe\u5f15\u5999\u7389\u7684\uff0c\u4f60\u4eec\u600e\u4e48\u98a0\u5012\u8fc7\u6765\uff0c\u8bf4\u662f\u5999\u7389\u52fe\u5f15\u4ed6\uff1f\u201d \u53bf\u5b98\u6296\u6296\u7d22\u7d22\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u662f\u5974\u624d\u7cca\u6d82\uff0c\u4e00\u65f6\u5931\u5bdf\u3002\u201d\u6b66\u5219\u5927\u53c8\u9053\uff1a\u201c\u518d\u8bf4\uff0c \u7eb5\u7136\u7236\u6bcd\u6709\u7f6a\uff0c\u8179\u4e2d\u7684\u80ce\u513f\u6709\u4ec0\u4e48\u7f6a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5224\u4ee4\u5c06\u5979\u7684\u80ce\u513f\u6253\u843d\uff1f\u6253\u4e86\u6ca1\u6709\uff1f\u201d \u53bf\u5b98\u9053\uff1a\u201c\u8fd8\uff0c\u8fd8\u6ca1\u6709\u3002\u201d\u6b66\u5219\u5929\u51b7\u7b11\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u7b49\u8349\u83c5\u4eba\u547d\uff0c\u5982\u4f55\u80fd\u4e3a\u6c11\u7236\u6bcd\uff1f\u201d \u53bf\u5b98\u8dea\u5728\u5730\u4e0a\uff0c\u53e9\u5934\u6709\u5982\u6363\u849c\uff0c\u8fde\u8fde\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u5974\u624d\u8be5\u6b7b\uff0c\u5974\u624d\u8be5\u6b7b\uff01\u201d \u3000\u3000\u6b66\u5219\u5929\u9053\uff1a\u201c\u5c06\u90a3\u6848\u5377\u4ea4\u56de\u7ed9\u6211\u3002\u201d\u7acb\u523b\u6293\u8d77\u7f62\u6765\uff0c\u4eb2\u5199\u5224\u4e49\uff0c\u8fb9\u5199\u8fb9\u8bfb\u9053\uff1a\u201c\u738b \u5343\u6237\u7eb5\u5b50\u4e3a\u975e\uff0c\u9769\u804c\u4ed1\u529e\u3002\u738b\u5f6a\u8feb\u5978\u5973\u5c3c\uff0c\u97ad\u4e00\u767e\uff0c\u76d1\u4e09\u5e74\u3002\u5999\u7389\u7740\u4ee4\u8fd8\u4fd7\uff0c\u4efb\u4f55\u4eba\u4e0d \u5f97\u4f24\u5bb3\u5979\u8179\u4e2d\u80ce\u513f\u3002\u201d\u653e\u4e0b\u4e86\u7b14\uff0c\u518d\u7f13\u7f13\u5bf9\u53bf\u5b98\u8bf4\u9053\uff1a\u201c\u81f3\u4e8e\u4f60\u5462\uff0c\u4f60\u5148\u6458\u4e0b\u5934\u4e0a\u7684\u4e4c \u7eb1\uff0c\u767d\u6253\u8033\u5149\u4e8c\u5341\uff0c\u56de\u8859\u95e8\u542c\u5019\u53d1\u843d\uff01\u201d\u53bf\u5b98\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u6458\u4e86\u4e4c\u7eb1\uff0c\u567c\u567c\u556a\u556a\u81ea\u6253 \u8033\u5149\u3002\u7ad9\u5728\u6b66\u5219\u5929\u80cc\u540e\u7684\u90a3\u4e2a\u5bab\u5973\uff0c\u54ac\u7740\u5634\u5507\u5fcd\u7b11\uff0c\u539f\u6765\u90a3\u5177\u5b98\u6253\u5f97\u4e0d\u6562\u505c\u624b\uff0c\u6253\u5f97\u534a \u8fb9\u9762\u90fd\u80bf\u4e86\u8d77\u6765\uff0c\u6b66\u5219\u5929\u53eb\u4ed6\u81ea\u6253\u4e8c\u5341\uff0c\u4ed6\u6253\u591a\u4e24\u500d\u4e5f\u4e0d\u6b62\u4e86\u3002 \u3000\u3000\u6b66\u5219\u5929\u5c06\u90a3\u53bf\u5b98\u65a5\u8d70\u4e86\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u81ea\u53e4\u4ee5\u6765\uff0c\u7537\u4eba\u4eec\u5c31\u4e60\u60ef\u628a\u7f6a\u5b7d\u52a0\u5728\u5973\u4eba\u5934 \u4e0a\uff0c\u9769\u6389\u4e00\u4e2a\u53bf\u5b98\u5bb9\u6613\uff0c\u9769\u6389\u8fd9\u4e2a\u4e60\u60ef\u53ef\u5c31\u96be\u4e86\uff01\u201d\u5477\u4e86\u53e3\u8336\uff0c\u53c8\u5bf9\u8001\u592a\u76d1\u9053\uff1a\u201c\u4e07\u6e90 \u53bf\u6709\u4e00\u4e2a\u4e61\u4e0b\u4eba\u8981\u4e0a\u4eac\u5409\u72b6\uff0c\u6070\u597d\u5728\u8fd9\u91cc\u9047\u4e0a\u6211\u51fa\u5de1\uff0c\u597d\uff0c\u5c31\u53eb\u4ed6\u7c73\u5427\uff0c\u7701\u5f97\u4ed6\u518d\u8dcb\u6d89 \u957f\u9014\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2a\u4e61\u4e0b\u4eba\u624b\u98a4\u811a\u9707\u7684\u4e0a\u8fdb\u6765\uff0c\u4e0a\u5b98\u5a49\u513f\u4e00\u770b\uff0c\u539f\u6765\u5c31\u662f\u5979\u5728\u5df4\u5dde\u9014\u4e2d\u89c1\u8fc7\u7684\u90a3\u4e2a \u5f20\u8001\u4e09\u3002 \u3000\u3000\u5f20\u8001\u4e09\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7687\u5e1d\u4f1a\u53ec\u89c1\u4ed6\uff0c\u76f4\u6253\u54c6\u667e\uff0c\u6b63\u60f3\u8dea\u5730\u78d5\u5934\uff0c\u6b66\u5219\u5929\u9053\uff1a\u201c\u79c1\u5ba4 \u76f8\u89c1\uff0c\u4f60\u53c8\u4e0d\u662f\u6715\u7684\u671d\u81e3\uff0c\u53ef\u4ee5\u514d\u884c\u5927\u793c\u3002\u201d\u53eb\u592a\u76d1\u62c9\u4e86\u4e00\u5f20\u6905\u5b50\uff0c\u8bf7\u4ed6\u5750\u4e0b\uff0c\u95ee\u9053\uff1a \u201c\u4f60\u4eca\u5e74\u591a\u5927\u5e74\u7eaa\u4e86\uff1f\u201d\u5f20\u8001\u4e09\u9053\uff1a\u201c\u4e94\u5341\u6709\u516b\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6bd4\u6211\u5c0f\u4e09\u5c81\uff0c\u8fd8\u4e0d\u7b97 \u8001\u3002\u53bb\u5e74\u5e74\u6210\u597d\u5417\uff1f\u201d\u5f20\u8001\u4e09\u9053\uff1a\u201c\u6bd4\u524d\u5e74\u706f\u3002\u201d\u6b66\u5219\u5929\u53c8\u95ee\u9053\uff1a\u201c\u4eca\u5e74\u7684\u79be\u82d7\u957f\u5f97\u597d \u5417\uff1f\u201d\u5f20\u8001\u4e09\u9053\uff1a\u201c\u5728\u6211\u79bb\u5bb6\u7684\u65f6\u5019\uff0c\u79be\u7530\u91cc\u4e00\u7247\u7eff\u7eff\u6cb9\u6cb9\u7684\uff1b\u82e5\u662f\u6ca1\u6709\u6c34\u65f1\u866b\u707e\uff0c\u6562 \u60c5\u8981\u6bd4\u53bb\u5e74\u8fd8\u597d\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e00\u5e74\u6bd4\u4e00\u5e74\u597d\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u4f60\u4eec\u6bcf\u987f\u80fd\u5403\u4e0a\u5e72\u996d\u4e86\u5427\uff1f\u201d \u5f20\u8001\u4e09\u9053\uff1a\u201c\u6258\u5929\u540e\u965b\u4e0b\u7684\u6d2a\u798f\uff0c\u6bcf\u4e2a\u6708\u53ef\u4ee5\u5403\u4e0a\u4e8c\u5341\u6765\u5929\u7684\u5e72\u996d\u4e86\u3002\u4e0d\u8fc7\u9752\u9ec4\u4e0d\u63a5\u7684 \u65f6\u5019\uff0c\u90a3\u5c31\u8fd8\u8981\u591a\u5403\u51e0\u5929\u6742\u7cae\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u90a3\u8fd8\u662f\u4e0d\u5927\u597d\u5440\uff01\u201d\u5f20\u8001\u4e09\u9053\uff1a\u201c\u4e0d\uff0c \u6bd4\u8fc7\u53bb\u597d\u591a\u4e86\u3002\u8fc7\u53bb\u6536\u6210\u597d\u7684\u5e74\u5934\uff0c\u4e5f\u662f\u4e00\u987f\u5e72\u4e00\u987f\u7a00\u7684\u3002\u201d\u6b66\u5219\u5929\u53f9\u4e86\u53e3\u6c14\u9053\uff1a \u3000\u3000\u201c\u8700\u4e2d\u7d20\u53f7\u5927\u5e9c\u4e4b\u56fd\uff0c\u8001\u767e\u59d3\u5c1a\u4e14\u4e0d\u80fd\u6bcf\u987f\u5403\u996d\uff0c\u8fd9\u90fd\u662f\u8d4b\u60a6\u592a\u91cd\u4e4b\u6545\u3002\u82e5\u662f\u5929\u4e0b \u592a\u5e73\uff0c\u56fd\u5bb6\u53ef\u4ee5\u5c11\u517b\u4e00\u4e9b\u5175\uff0c\u7530\u7a0e\u5c31\u6700\u5c11\u53ef\u4ee5\u518d\u51cf\u4e09\u6210\u3002\u201d \u3000\u3000\u5f20\u8001\u4e09\u8d77\u521d\u5f88\u5bb3\u6015\uff0c\u60f3\u4e0d\u5230\u6b66\u5219\u5929\u5c3d\u662f\u548c\u4ed6\u8c08\u4e9b\u5bb6\u5e38\u95f2\u8bdd\uff0c\u6e10\u6e10\u5c31\u4e0d\u5bb3\u6015\u4e86\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u4eec\u5e84\u7a3c\u6c49\u90fd\u6c42\u8001\u5929\u7237\u4fdd\u4f51\u5929\u540e\u965b\u4e0b\u957f\u547d\u767e\u5c81\uff0c\u8ba9\u6211\u4eec\u8fc7\u5f97\u4e00\u5e74\u6bd4\u4e00\u5e74\u597d\u3002\u201d\u6b66\u5219\u5929 \u9053\uff1a\u201c\u662f\u5417\uff1f\u90a3\u6211\u5f88\u611f\u6fc0\u4f60\u4eec\u3002\u201d\u8fb9\u8bf4\u8fb9\u7ffb\u5f00\u5377\u5b97\uff0c\u9053\uff1a\u201c\u73b0\u5728\u8c08\u5230\u4f60\u8fd9\u4ef6\u6848\u5b50\u4e86\u3002\u4f60 \u544a\u738b\u5bb6\u5f3a\u62a2\u4e86\u4f60\u672a\u8fc7\u95e8\u7684\u5ab3\u5987\uff0c\u6070\u597d\u5df4\u5dde\u7684\u77e5\u5e9c\u521a\u624d\u7528\u5feb\u9a6c\u9001\u6765\u4e86\u6709\u5173\u6b64\u6848\u7684\u5377\u5b97\uff0c\u91cc \u9762\u6709\u4e00\u5f20\u5a5a\u4e66\uff0c\u662f\u90a3\u5973\u5b50\u7236\u4eb2\u6240\u5199\u7684\u3002\u77e5\u5e9c\u4ee5\u5a5a\u4e66\u4e3a\u51ed\uff0c\u62df\u4e86\u4e00\u4e2a\u6279\uff0c\u8981\u9a73\u56de\u4f60\u7684\u72b6\u5b50 \u54e9\uff01\u201d\u5f20\u8001\u4e09\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\u660e\u9274\u4e07\u91cc\uff0c\u90a3\u5a5a\u4e66\u662f\u738b\u5bb6\u8feb\u6211\u7684\u4eb2\u5bb6\u5199\u7684\u5440\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a \u201c\u738b\u5bb6\u5728\u5730\u65b9\u4e0a\u5f88\u6709\u52bf\u529b\u5417\uff1f\u201d\u5f20\u8001\u4e09\u9053\uff1a\u201c\u62a2\u6211\u5ab3\u5987\u90a3\u4e2a\u738b\u5eb7\uff0c\u4ed6\u6709\u4e2a\u505a\u8fc7\u5927\u5b98\u7684\u53d4 \u53d4\u3002\u201d\u6b66\u5219\u5927\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u5b98\uff1f\u201d\u5f20\u8001\u4e09\u9053\uff1a\u201c\u505a\u8fc7\u5df4\u5dde\u7684\u5dde\u5c39\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u54e6\uff0c \u662f\u8fd9\u6837\u7684\u5417\uff1f\u6211\u662f\u4fe1\u4f60\u7684\u8bdd\u7684\u3002\u4e0d\u8fc7\uff0c\u5224\u6848\u4e5f\u4e0d\u80fd\u5355\u51ed\u4e00\u9762\u4e4b\u8f9e\uff0c\u73b0\u5728\u5df4\u5dde\u7684\u674e\u5dde\u5c39\uff0c \u6211\u77e5\u9053\u4ed6\u662f\u4e2a\u597d\u5b98\u3002\u6211\u73b0\u5728\u5199\u4e00\u5c01\u4fe1\u7ed9\u4f60\uff0c\u4f60\u62ff\u53bb\u89c1\u674e\u5dde\u5c39\uff0c\u6211\u53eb\u4ed6\u53bb\u67e5\u660e\uff0c\u4ed6\u7edd\u5bf9\u4e0d \u4f1a\u5305\u5e87\u5730\u65b9\u6076\u9738\u7684\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u3002\u8fd9\u4ef6\u4e8b\u4e5f\u5f88\u5bb9\u6613\u67e5\uff0c\u6211\u6559\u5dde\u5c39\u7684\u59bb\u4e86\u4eb2\u81ea\u53bb\u95ee\u4f60\u90a3\u672a \u8fc7\u95e8\u7684\u5ab3\u5987\uff0c\u662f\u4e0d\u662f\u8feb\u5a5a\uff0c\u9a6c\u4e0a\u5c31\u53ef\u4ee5\u77e5\u9053\u4e86\u3002\u201d\u5f20\u8001\u4e09\u5927\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u90a3\u672a\u8fc7\u95e8\u7684 \u5ab3\u5987\u662f\u4e2a\u8d1e\u70c8\u7684\u5973\u5b50\uff0c\u5979\u88ab\u62a2\u8fc7\u53bb\uff0c\u8a93\u6b7b\u4e0d\u80af\u6210\u5a5a\u3002\u738b\u5bb6\u53c8\u77e5\u9053\u6211\u5728\u6253\u5b98\u53f8\uff0c\u5b98\u53f8\u6ca1\u6709 \u6253\u5b8c\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u592a\u8fc7\u5f3a\u8feb\uff0c\u6682\u65f6\u53ea\u6709\u5c06\u5979\u5173\u8d77\u6765\u5f53\u4f5c\u7ae5\u517b\u5ab3\u3002\u597d\uff0c\u95ee\u6211\u90a3\u4e2a\u672a\u8fc7\u95e8\u7684 \u5ab3\u5987\uff0c\u770b\u5979\u5230\u5e95\u613f\u610f\u5ac1\u51c6\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff01\u201d\u63a5\u8fc7\u6b66\u5219\u5929\u7684\u4e66\u4fe1\uff0c\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u4fbf\u9000 \u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6b66\u5219\u5929\u8212\u4e86\u53e3\u6c14\uff0c\u53c8\u7fe9\u4e86\u4e00\u7ffb\u5806\u5728\u684c\u4e0a\u7684\u6848\u4ef6\uff0c\u5bf9\u592a\u76d1\u9053\uff1a \u3000\u3000\u201c\u4f60\u53bb\u8bf7\u72c4\u4ec1\u6770\u8fdb\u6765\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u542c\u4e86\u8fd9\u4e2a\u540d\u5b57\uff0c\u5fc3\u5934\u5fae\u51db\uff0c\u66f4\u89c9\u60d8\u7136\u3002 \u3000\u3000\u539f\u6765\u8fd9\u72c4\u4ec1\u6770\u4e43\u662f\u4e00\u4f4d\u540d\u81e3\uff0c\u8001\u767e\u59d3\u90fd\u5f88\u94a6\u656c\u4ed6\u3002\u4e0a\u5b98\u5a49\u513f\u66fe\u542c\u957f\u5b59\u5747\u91cf\u8bf4\u8fc7\u4ed6\u7684 \u4e8b\u8ff9\uff0c\u4ed6\u5728\u9ad8\u5b97\u7687\u5e1d\u7684\u65f6\u5019\uff0c\u66fe\u505a\u8fc7\u5927\u7406\u4e1e\uff0c\u5728\u4e00\u5e74\u4e4b\u95f4\uff0c\u6e05\u7406\u4e86\u4e00\u4e07\u4e03\u5343\u5b97\u6848\u4e86\uff0c\u5e73 \u53cd\u7684\u51a4\u72f1\u4e0d\u8ba1\u5176\u6570\u3002\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u50cf\u72c4\u4ec1\u6770\u8fd9\u6837\u7684\u4eba\u4e5f\u7518\u5fc3\u4e3a\u6b66\u5219\u5929\u6240\u7528\uff0c\u602a \u4e0d\u5f97\u674e\u9038\u54e5\u54e5\u8981\u53f9\u606f\uff1a\u2018\u4f24\u5fc3\u5b57\u5185\u82f1\u8c6a\uff0c\u5c3d\u5f52\u65b0\u4e3b\u2019\u4e86\u3002\u6b66\u5219\u5929\u7eb5\u6709\u5343\u822c\u4e0d\u662f\uff0c\u5979\u5584\u4e8e \u7528\u4eba\u8fd9\u4e00\u70b9\u603b\u662f\u4e0d\u80fd\u62b9\u715e\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u72c4\u4ec1\u6770\u95ee\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\uff0c\u53ec\u81e3\u4f55\u4e8b\uff1f\u201d\u6b66\u5219\u706d\u9053\uff1a\u201c\u4f60\u4e14\u5750\u4e0b\uff0c \u6211\u4eca\u5929\u65ad\u4e86\u51e0\u5b97\u6848\u5b50\uff0c\u8bf4\u7ed9\u4f60\u542c\u542c\u3002\u201d\u72c4\u4ec1\u6770\u542c\u5979\u8bf4\u4e86\u4e4b\u540e\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5949\u627f\uff0c\u6b66\u5219\u5929\u9053\uff1a \u201c\u54a6\uff0c\u4f60\u600e\u4e48\u4e0d\u9ad8\u5174\u5462\uff1f\u662f\u4e0d\u662f\u6211\u65ad\u9519\u4e86\u54ea\u4e00\u5b97\u6848\u5b50\uff1f\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\u6709\u5982\u660e \u955c\u9ad8\u60ac\uff0c\u4e1d\u6beb\u4e0d\u9519\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u72c4\u537f\u4f55\u6545\u76b1\u7709\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u6211\u662f\u4e3a\u965b \u4e0b\u62c5\u5fe7\u5440\uff01\u50cf\u8fd9\u7c7b\u7684\u6848\u5b50\uff0c\u5929\u4e0b\u4e0d\u77e5\u591a\u5c11\uff0c\u965b\u4e0b\u4f60\u600e\u7ba1\u5f97\u8fd9\u4e48\u591a\uff1f\u81e3\u95fb\u5c27\u821c\u4e4b\u6cbb\u5929\u4e0b\uff0c \u4ed6\u4eec\u53ef\u5e76\u4e0d\u662f\u6bcf\u4ef6\u4e8b\u60c5\uff0c\u90fd\u8981\u4eb2\u81ea\u53bb\u7406\u7684\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6211\u61c2\u5f97\u4f60\u7684\u610f\u601d\uff0c\u8be5\u6709\u591a\u4e9b \u6709\u624d\u80fd\u7684\u4eba\uff0c\u5e2e\u6211\u529e\u4e8b\u3002\u6211\u6b63\u662f\u4e3a\u4e86\u8fd9\u4e2a\uff0c\u624d\u53eb\u4f60\u8fdb\u6765\u3002\u8fd9\u4e9b\u6848\u5b50\uff0c\u8bf7\u4f60\u5728\u660e\u5929\u4e00\u5929\u4e4b \u5185\uff0c\u90fd\u7ed9\u6211\u5224\u4e86\u3002\u201d \u3000\u3000\u72c4\u4ec1\u6770\u63a5\u8fc7\u4e86\u4e00\u5927\u53e0\u7684\u5377\u5b97\uff0c\u6b66\u5219\u5929\u53c8\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u968f\u6211\u51fa\u5de1\uff0c\u53ef\u53d1\u73b0\u6709\u4ec0\u4e48\u8db3\u4ee5 \u91cd\u7528\u7684\u5730\u65b9\u5b98\u540f\u5417\uff1f\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u81e3\u4e0a\u6b21\u4fdd\u8350\u7684\u4eba\uff0c\u965b\u4e0b\u4e5f\u672a\u66fe\u91cd\u7528\u554a\uff01\u201d\u6b66\u5219\u5929\u8bf4 \u9053\uff1a\u201c\u54ea\u4e00\u4e2a\uff1f\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u8346\u5dde\u957f\u540f\u5f20\u67ec\u4e4b\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6211\u4e0d\u662f\u628a\u4ed6\u5347\u505a\u6f5e\u5dde \u53f8\u9a6c\u4e86\u5417\uff1f\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u5f20\u67ec\u4e4b\u662f\u5bb0\u76f8\u4e4b\u624d\uff0c\u7ed9\u4ed6\u505a\u6f5e\u5dde\u53f8\u9a6c\uff0c\u600e\u80fd\u8bf4\u662f\u91cd\u7528\uff1f\u201d\u6b66 \u5219\u5929\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u53ea\u662f\u4ed6\u5e74\u7eaa\u592a\u5927\u4e86\u3002\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u505a\u5bb0\u76f8\u53c8\u4e0d\u662f\u505a\u4f9b\u5949\uff0c\u965b\u4e0b \u4f55\u5fc5\u95ee\u4ed6\u7684\u8001\u5c11\u7f8e\u4e11\uff1f \u3000\u3000\u5f20\u67ec\u4e4b\u867d\u7136\u5e74\u8001\u8c8c\u4e11\uff0c\u5374\u8981\u80dc\u8fc7\u5f20\u6613\u4e4b\u5f20\u660c\u5b97\u5144\u5f1f\u5343\u4e07\u500d\u3002\u201d\u5f20\u6613\u4e4b\u5144\u5f1f\u5e74\u5c11\u7f8e\u59ff \u5bb9\uff0c\u5584\u97f3\u4e50\uff0c\u88ab\u6b66\u5219\u5929\u53ec\u5165\u5bab\u4efb\u4e3a\u201c\u8fb0\u5185\u4f9b\u5949\u201d\uff0c\u58eb\u592b\u592b\u7269\u8bae\u6cb8\u817e\uff0c\u8bf4\u4e8c\u5f20\u662f\u6b66\u5219\u5929\u7684 \u201c\u7537\u5ba0\u201d\uff0c\u957f\u5b59\u5747\u91cf\u4ee5\u524d\u5bf9\u4e0a\u5b98\u5a49\u513f\u6570\u8bf4\u6b66\u5219\u5929\u7684\u4e11\u4e8b\u65f6\uff0c\u4e5f\u66fe\u628a\u5ba0\u7528\u4e8c\u5f20\uff0c\u4f5c\u4e3a\u6b66\u5219 \u5929\u7684\u7f6a\u72b6\u4e4b\u4e00\u3002\u4e0a\u5b98\u5a49\u513f\u542c\u4e86\u72c4\u4ec1\u6770\u7684\u8bdd\uff0c\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u72c4\u4ec1\u6770\u771f\u7684\u662f\u80c6\u5927\u65e0\u6bd4\uff0c\u5c45 \u7136\u6562\u5bf9\u6b66\u5219\u5929\u5f53\u9762\u8bbd\u523a\u3002 \u3000\u3000\u6b66\u5219\u5929\u53ef\u5e76\u4e0d\u751f\u6c14\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5f20\u6613\u4e4b\u5144\u5f1f\u600e\u80fd\u4e0e\u5f20\u67ec\u4e4b\u76f8\u6bd4\uff1f\u6715\u4e4b\u6240\u4ee5\u8981\u4e8c\u5f20 \u505a\u4f9b\u5949\uff0c\u4e0d\u8fc7\u89c1\u4ed6\u4eec\u61c2\u5f97\u97f3\u4e50\uff0c\u95f2\u6765\u53ef\u4ee5\u7ed9\u6211\u6d88\u6d88\u95f7\u7f62\u4e86\u3002\u7b49\u5982\u591a\u7528\u4e24\u4e2a\u5bab\u5973\u4e00\u822c\uff0c\u6211 \u5df2\u7ecf\u516d\u5341\u6709\u4e00\uff0c\u4e5f\u4e0d\u6015\u8bb2\u95f2\u6d3b\u4e86\u3002\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u867d\u7136\u5982\u6b64\uff0c\u8fd8\u662f\u8fdc\u5c0f\u4eba\u800c\u8fd1\u541b\u4e86\u7684\u597d\u3002\u201d \u6b66\u5219\u5929\u9053\uff1a\u201c\u591a\u8c22\u72c4\u537f\u76f4\u8a00\u3002\u4f60\u6240\u4fdd\u4e3e\u7684\u5f20\u67ec\u4e4b\uff0c\u6211\u56de\u53bb\u4e4b\u540e\uff0c\u518d\u5347\u4ed6\u4e00\u7ea7\u3002\u8003\u5bdf\u4e00\u4e9b \u65f6\u5019\uff0c\u82e5\u662f\u624d\u582a\u5927\u7528\uff0c\u518d\u7ed9\u4ed6\u505a\u5bb0\u76f8\u3002\u201d\u72c4\u4ec1\u6770\u8fd9\u624d\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u6b66\u5219\u5929\u7b11\u9053\uff1a\u201c\u4eca\u5929\u8fd8\u6709\u4e00\u4ef6\u5927\u4e8b\u8981\u4e0e\u4f60\u5546\u91cf\uff0c\u4f60\u4e14\u7b49\u7b49\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u592a\u76d1\u5f15\u4e86 \u4e00\u4e2a\u5c11\u5973\u8fdb\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e00\u770b\uff0c\u6765\u7684\u539f\u6765\u662f\u6b66\u7384\u971c\u7684\u90a3\u4e2a\u5c0f\u4e2b\u73af\u5982\u610f\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\uff0c\u6025\u5fd9\u5c06\u8eab \u5b50\u8737\u7f29\uff0c\u85cf\u5728\u74e6\u69fd\u4e4b\u5185\uff0c\u4e0d\u6562\u9732\u51fa\u534a\u70b9\u58f0\u606f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6b66\u5219\u5929\u95ee\u9053\uff1a\u201c\u7384\u971c\u4e0d\u6765\u5417\uff1f\u201d\u5982\u610f\u9053\uff1a\u201c\u5c0f\u59d0\u6709\u4e00\u5c01\u4fe1\u7ed9\u5929\u540e\u965b\u4e0b\uff0c\u5df4\u5dde \u548c\u5ce8\u5d4b\u5c71\u90a3\u4e24\u4ef6\u4e8b\u60c5\uff0c\u539f\u539f\u672c\u672c\uff0c\u90fd\u5199\u5728\u4fe1\u4e0a\u4e86\u3002\u201d\u8fc7\u4e86\u4e00\u76cf\u8336\u65f6\u523b\uff0c\u6b66\u5219\u5929\u628a\u4fe1\u770b\u5b8c\uff0c \u5fae\u5fae\u7b11\u9053\uff1a\u201c\u539f\u6765\u7384\u971c\u4e5f\u60f3\u505a\u5973\u7687\u5e1d\u54e9\uff01\u201d\u72c4\u4ec1\u6770\u4e00\u6014\uff0c\u6b66\u5219\u5929\u9053\uff1a\u201c\u72c4\u537f\u4e0d\u5fc5\u4e3a\u6211\u62c5 \u5fe7\uff0c\u7384\u971c\u662f\u6211\u7684\u4e00\u4e2a\u4f84\u5973\u513f\uff0c\u5979\u4e0d\u662f\u60f3\u548c\u6211\u4e89\u4f4d\uff0c\u800c\u662f\u60f3\u5728\u6b66\u6797\u4e2d\u505a\u4e00\u4e2a\u6280\u538b\u7fa4\u96c4\u7684\u65e0 \u51a0\u7687\u5e1d\u3002\u8fd9\u5973\u5a03\u5b50\u7684\u5fd7\u6c14\u5012\u4e5f\u4e0d\u5c0f\u5440\uff01\u4e0d\u8fc7\uff0c\u505a\u7687\u5e1d\u53ef\u5e76\u4e0d\u80fd\u5355\u6043\u6b66\u529b\u554a\uff0c\u4f60\u56de\u53bb\u628a\u6211 \u8fd9\u4e2a\u8bdd\u544a\u8bc9\u5979\u3002\u201d\u5982\u610f\u5e94\u4e86\u4e00\u58f0\uff0c\u7980\u9053\uff1a\u201c\u5c0f\u59d0\u53bb\u8ffd\u674e\u9038\uff0c\u5927\u7ea6\u4e0d\u4f1a\u5230\u957f\u5b89\u6765\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u98a4\u9707\uff0c\u60f3\u9053\uff1a\u201c\u602a\u9053\u90a3\u65e5\u6b66\u7384\u971c\u629b\u6211\u800c\u53bb\uff0c\u539f\u6765\u5979\u662f\u53bb\u8ffd\u8d76\u674e\u9038\u54e5\u54e5\uff0c \u6c42\u5929\u5730\u795e\u7075\u4fdd\u4f51\uff0c\u5343\u4e07\u4e0d\u8981\u7ed9\u5979\u8ffd\u4e0a\u624d\u597d\u3002\u201d\u542c\u5230\u674e\u9038\u7684\u540d\u5b57\uff0c\u4e0a\u5b98\u5a49\u513f\u7279\u522b\u5173\u5fc3\uff0c\u7ad6 \u8d77\u4e86\u8033\u6735\uff0c\u4e00\u4e2a\u5b57\u4e5f\u4e0d\u6562\u653e\u8fc7\uff0c\u4f46\u542c\u5f97\u6b66\u5219\u5929\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u89c1\u7740\u4e86\u674e\u9038\u6ca1\u6709\uff1f\u201d\u5982\u610f\u9053\uff1a \u201c\u89c1\u7740\u5566\uff0c\u5728\u5ce8\u5d4b\u91d1\u9876\uff0c\u5c0f\u59d0\u66fe\u548c\u4ed6\u6bd4\u5251\uff0c\u90a3\u65f6\u4ed6\u521a\u521a\u505a\u4e86\u4ec0\u4e48\u2018\u82f1\u96c4\u5927\u4f1a\u2019\u7684\u76df\u4e3b\uff0c \u7ed9\u5c0f\u59d0\u6253\u4e0b\u53f0\u4e86\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u674e\u9038\u4e5f\u53cd\u5bf9\u6211\uff0c\u6211\u4e00\u76f4\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u674e\u5bb6\u5b50\u5b59\u4e2d \u6700\u6709\u89c1\u8bc6\u7684\u4eba\u5462\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u5c06\u90a3\u5c01\u4fe1\u9012\u7ed9\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\u63ed\u9732\u4e86\u5f90\u656c\u4e1a\u7684\u4e00\u4e2a \u5927\u9634\u8c0b\uff0c\u4f60\u62ff\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u63a5\u7740\u6b66\u5219\u5929\u53c8\u95ee\u90a3\u5c0f\u4e2b\u73af\u9053\uff1a\u201c\u4f60\u8ddf\u7384\u971c\u5728\u5ce8\u5d4b\u91d1\u9876\u5927\u95f9\u4e00\u573a\uff0c\u60f3\u5fc5\u75db\u5feb\u5f97\u5f88\uff1f\u201d \u5982\u610f\u7709\u98de\u8272\u821e\u7684\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u6253\u8fc7\u8fd9\u6837\u5389\u5bb3\u7684\u67b6\uff0c\u5c0f\u59d0\u548c\u6211\u4eec\u5c06\u90a3\u73ed\u82f1\u96c4\u6740 \u5f97\u843d\u82b1\u6d41\u6c34\uff0c\u771f\u53eb\u75db\u5feb\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u8d50\u4f60\u4e00\u676f\u8336\u6da6\u6da6\u5589\u5499\uff0c\u4f60\u8bf4\u7ed9\u6211\u542c\u542c\u3002\u201d\u5982\u610f \u559d\u4e86\u4e00\u53e3\u8336\uff0c\u4fbf\u7ed8\u58f0\u7ed8\u5f71\u7684\u5c06\u90a3\u65e5\u5728\u5ce8\u5d4b\u91d1\u9876\u5927\u95f9\u82f1\u96c4\u4f1a\u7684\u4e8b\u60c5\u4ed4\u7ec6\u63cf\u8ff0\uff0c\u4e0a\u5b98\u5a49\u513f\u4e00 \u76f4\u542c\u4e00\u76f4\u63d0\u5fc3\u540a\u80c6\uff0c\u751f\u6015\u5979\u8bf4\u5230\u81ea\u5df1\u8eab\u4e0a\uff0c\u8c01\u77e5\u5982\u610f\u4e00\u76f4\u8bf4\u5b8c\uff0c\u5374\u5e76\u6ca1\u6709\u534a\u53e5\u63d0\u5230\u5979\uff0c \u5012\u662f\u5c06\u4e0a\u5b98\u5a49\u513f\u6655\u5012\u4e4b\u540e\uff0c\u6765\u66fe\u770b\u89c1\u7684\u90a3\u4e00\u6bb5\uff0c\u7b26\u4e0d\u7591\u5c06\u8c37\u795e\u7fc1\u62c9\u8d70\u7684\u534a\u60c5\u8865\u8ff0\u4e86\u3002 \u3000\u3000\u6b66\u5219\u5929\u542c\u5979\u8bf4\u5b8c\u4e4b\u540e\uff0c\u9053\uff0c\u201c\u4f60\u4e00\u8def\u8f9b\u82e6\uff0c\u65e9\u70b9\u53bb\u6b47\u606f\u5427\u3002 \u3000\u3000\u4f60\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u53eb\u4ed6\u4eec\u5c06\u90a3\u4e24\u4e2a\u8c0b\u53cd\u7684\u519b\u5b98\u9001\u8fdb\u6765\u3002\u201d\u5982\u610f\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e24\u4e2a\u4eba\u867d\u7ed9\u5c0f\u59d0\u5e9f\u6389\u6b66\u529f\uff0c\u4f46\u8fd8\u662f\u51f6\u5f97\u5f88\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6211\u548c\u4ed6\u8bb2\u9053\u7406\uff0c\u770b \u4ed6\u80fd\u51f6\u5230\u54ea\u91cc\u53bb\uff1f\u597d\uff0c\u4f60\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u5982\u610f\u8d70\u51fa\u95e8\u65f6\uff0c\u4e0d\u77e5\u662f\u5076\u7136\u8fd8\u662f\u6709\u610f\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u773c\u5149\u5411\u5c4b\u9876\u4e00\u77a5\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u4e0a \u5b98\u5a49\u513f\u4f0f\u5728\u74e6\u69fd\u5185\uff0c\u8fde\u5927\u6c14\u4e5f\u900f\u4e0d\u51fa\u6765\uff0c\u5982\u610f\u4f3c\u4e4e\u5e76\u6ca1\u6709\u53d1\u73b0\u5979\uff0c\u77a5\u4e86\u4e00\u773c\uff0c\u5c31\u5f84\u81ea\u8d70 \u51fa\u53bb\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5f53\u503c\u7684\u6b66\u58eb\u5c06\u4e24\u4e2a\u519b\u5b98\u53cd\u7f1a\u53cc\u624b\u62bc\u89e3\u8fdb\u6765\u3002\u4e0a\u5b98\u5a49\u513f\u8ba4\u5f97\u4ed6\u4eec\u6b63\u662f\u90a3\u4e00 \u665a\u523a\u6740\u592a\u5b50\u674e\u8d24\u7684\u51f6\u624b\u3002\u4e24\u4eba\u90fd\u662f\u4e00\u8138\u4e0d\u5728\u4e4e\u7684\u795e\u6c14\uff0c\u88ab\u63a8\u5230\u6b66\u5219\u5929\u6848\u524d\uff0c\u4ecd\u7136\u633a\u7acb\u4e0d \u8dea\uff0c\u51f6\u795e\u6076\u715e\u822c\u7684\u72de\u89c6\u7740\u6b66\u5219\u5929\u3002\u90a3\u6b66\u58eb\u63d0\u8d77\u811a\u6765\u5728\u4ed6\u4eec\u819d\u5f2f\u4e00\u8e39\uff0c\u4ed6\u4eec\u65e9\u5df2\u88ab\u6b66\u7384\u971c \u5e9f\u6389\u6b66\u529f\uff0c\u8fd9\u4e00\u811a\u7981\u53d7\u4e0d\u8d77\uff0c\u767b\u65f6\u8dea\u5012\u3002\u6b66\u5219\u5929\u5bf9\u6b66\u571f\u9053\uff1a\u201c\u4e0d\u8981\u6253\u9a82\u4ed6\u4eec\uff0c\u5f85\u5ba1\u660e\u4e86 \u7f6a\u72b6\u4e4b\u540e\uff0c\u671d\u5ef7\u7684\u6cd5\u5f8b\uff0c\u81ea\u4f1a\u6709\u516c\u6b63\u7684\u60e9\u6cbb\u3002\u201d\u90a3\u4e24\u4e2a\u519b\u5b98\u672c\u5f85\u62fc\u7740\u4e00\u8eab\u6bd2\u6253\uff0c\u7834\u53e3\u4e71 \u9a82\uff0c\u5ffd\u542c\u5f97\u6b66\u5219\u5929\u5982\u6b64\u8bf4\u6cd5\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u6b66\u5219\u5929\u7684\u773c\u5149\u6709\u5982\u5bd2\u51b0\u5229\u526a\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d \u9707\u6151\uff0c\u53ea\u89c9\u6b66\u5219\u5929\u81ea\u6709\u4e00\u80a1\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u795e\u60c5\uff0c\u4ee4\u5f97\u4ed6\u4eec\u628a\u65e9\u5df2\u60f3\u597d\u7684\uff0c\u60f3\u4fae\u8fb1\u6b66\u5219 \u5929\u7684\u8bf4\u8bdd\u541e\u4e86\u56de\u53bb\uff0c\u4f46\u8138\u4e0a\u4ecd\u4e00\u80a1\u5014\u5f3a\u7684\u795e\u60c5\u3002 \u3000\u3000\u6b66\u5219\u5929\u7ffb\u4e86\u4e00\u7ffb\u7737\u5b97\uff0c\u5f90\u5f90\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e18\u795e\u52cb\u5e10\u4e0b\u7684\u5de6\u519b\u90fd\u5c09\u7a0b\u52a1\u7532\u548c\u5148\u884c\u5b98 \u97e9\u8363\uff0c\u662f\u4e48\uff1f\u201d\u97e9\u8363\u53eb\u9053\uff1a\u201c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u4f55\u987b\u591a\u95ee\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u7a0b\u52a1\u7532\uff0c\u4f60\u662f\u4e0d \u662f\u5927\u5c06\u519b\u7a0b\u52a1\u633a\u7684\u5144\u5f1f\uff1f\u201d\u7a0b\u52a1\u7532\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u662f\u6211\u6740\u4e86\u4f60\u7684 \u5b9d\u8d1d\u513f\u5b50\uff0c\u6740\u5250\u542c\u4fbf\uff0c\u4e0e\u522b\u4eba\u65e0\u5173\uff01\u4f60\u82e5\u60f3\u8bdb\u8fde\u4e5d\u65cf\uff0c\u8001\u5b50\u4e5f\u4e0d\u6015\u4f60\uff0c\u53ea\u6015\u4f60\u5148\u8981\u8d1f\u4e0a \u65e0\u9053\u660f\u541b\u7684\u6076\u540d\uff01\u201d\u6b66\u5219\u5929\u773c\u73e0\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u662f\u4e48\uff1f\u5f53\u771f\u662f\u4e0e\u522b\u4eba\u65e0\u5173\u4e48\uff1f\u6ca1\u6709\u4eba\u6307\u4f7f \u4f60\u4eec\u4e48\uff1f\u201d\u4e00\u8fde\u4e09\u53e5\u95ee\u8bdd\uff0c\u773c\u775b\u7d27\u7d27\u7684\u76ef\u7740\u7a0b\u52a1\u7532\u3002\u7a0b\u52a1\u7532\u5f3a\u5b9a\u5fc3\u60c5\uff0c\u633a\u80f8\u7b54\u9053\uff0c\u201c\u4f60 \u5b9a\u8981\u8ffd\u95ee\u4e3b\u4f7f\u7684\u4eba\uff0c\u597d\uff0c\u90a3\u6211\u4fbf\u544a\u8bc9\u4f60\uff0c\u4e3b\u4f7f\u8005\u4fbf\u662f\u4f60\u6700\u4eb2\u4fe1\u7684\u5de6\u91d1\u543e\u5927\u5c06\u519b\u4e18\u7985\u52cb\uff01\u201d \u6b66\u5219\u5929\u51b7\u51b7\u4e00\u7b11\uff0c\u5bf9\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u4f60\u66ff\u6211\u62df\u4e00\u9053\u6cbc\u4e66\uff0c\u5b89\u6170\u4e18\u795e\u52cb\uff0c\u53eb\u4ed6\u4e0d\u8981\u4e3a\u6b64\u4e8b\u803f \u803f\u4e8e\u5fc3\uff0c\u4f60\u8bf4\u6211\u5df2\u5ba1\u660e\u4e8b\u60c5\u4e0e\u4ed6\u65e0\u5173\u4e86\uff0c\u4ed6\u81ea\u8bf7\u8d2c\u964d\u4e09\u7ea7\uff0c\u5e94\u6bcb\u5eb8\u8bae\uff01\u201d\u72c4\u4ec1\u6770\u5e94\u4e86\u4e00 \u58f0\uff0c\u7b11\u7740\u5bf9\u7a0b\u52a1\u7532\u9053\uff1a\u201c\u5929\u540e\u5723\u660e\uff0c\u4f60\u60f3\u8bec\u9677\u4e18\u5927\u5c06\u519b\uff0c\u8be1\u8ba1\u7109\u80fd\u5f97\u901e\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u8001 \u8001\u5b9e\u5b9e\uff0c\u5b9e\u8bdd\u5b9e\u8bf4\u5427\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u65e2\u8bf4\u4e0e\u522b\u4eba\u65e0\u5173\uff0c\u90a3\u4e48\u6211\u5012\u8981\u8bf7\u95ee\u4f60\u4eec\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u6740\u5bb3 \u6211\u7684\u8d24\u513f\uff1f\u662f\u4e0d\u662f\u4ed6\u505a\u4e86\u4ec0\u4e48\u7978\u56fd\u6b83\u6c11\u7684\u4e8b\uff0c\u4f60\u4eec\u8981\u6740\u4ed6\uff1f\u201d\u7a0b\u52a1\u7532\u907f\u5f00\u4e86\u6b66\u5219\u5929\u7684\u773c \u5149\uff0c\u6076\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7978\u540c\u6b83\u6c11\u7684\u662f\u4f60\uff01\u4f60\u6b8b\u66b4\u4e0d\u4ec1\uff0c\u7be1\u593a\u5e1d\u4f4d\uff0c\u6740\u4e86\u591a\u5c11\u5510\u5ba4\u5fe0\u81e3\uff1f \u3000\u3000\u4f60\u6740\u522b\u4eba\uff0c\u522b\u4eba\u5c31\u4e0d\u80fd\u6740\u4f60\u7684\u513f\u4e86\u5417\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6211\u662f\u4e0d\u662f\u7978\u56fd\u6b83\u6c11\uff0c\u8fd9\u4e2a\u4ee5 \u540e\u518d\u8bf4\u3002\u7eb5\u7136\u6211\u662f\u6709\u7f6a\uff0c\u6211\u513f\u5b50\u65e0\u7f6a\uff0c\u4f60\u4eec\u6740\u4e86\u4ed6\uff0c\u8fd9\u4e8b\u600e\u4e48\u8bf4\u5f97\u8fc7\u53bb\uff1f\u201d\u6b66\u5219\u5929\u6e10\u6e10 \u6124\u6fc0\uff0c\u8d8a\u8bf4\u8d8a\u5feb\uff0c\u7eed\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u6211\u6b8b\u66b4\uff0c\u90a3\u4e48\u8bf7\u95ee\uff0c\u4f60\u4eec\u6740\u4e86\u6211\u7684\u513f\u5b50\uff0c\u5374\u5047\u4f20\u662f\u6211 \u7684\u4e3b\u610f\uff0c\u60f3\u53eb\u5929\u4e0b\u4eba\u4ee5\u4e3a\u6211\u505a\u6bcd\u4eb2\u7684\u6740\u4e86\u81ea\u5df1\u4eb2\u751f\u7684\u513f\u5b50\uff0c\u4f60\u4eec\u4e0d\u4f46\u6740\u5bb3\u4e86\u4e00\u4e2a\u65e0\u8f9c\u7684 \u9752\u5e74\uff0c\u8fd8\u7c89\u788e\u4e86\u505a\u6bcd\u4eb2\u7684\u5fc3\uff0c\u8fd9\u662f\u4e0d\u662f\u6b8b\u66b4\uff1f\u5929\u4e0b\u8fd8\u6709\u4ec0\u4e48\u6bd4\u8fd9\u66f4\u6076\u6bd2\u7684\u4e8b\u60c5\u5417\uff1f\u4f60\u8bf4\uff0c \u4f60\u8bf4\u5440\uff01\u201d \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u8fd9\u4e24\u4e2a\u7a77\u51f6\u6781\u6076\u7684\u523a\u5ba2\uff0c\u7adf\u7136\u88ab\u6b66\u5219\u5929\u95ee\u5f97\u5664\u4e0d\u6562\u58f0\uff0c\u4f4e\u4e0b\u5934\u672a\uff0c\u907f\u6c57 \u4e86\u5979\u7684\u773c\u5149\u3002\u72c4\u4ec1\u6770\u529d\u9053\uff1a\u201c\u8bf7\u965b\u4e0b\u7a0d\u6291\u60b2\u75db\uff0c\u8fd9\u4e24\u4e2a\u51f6\u5f92\u8ba9\u5fae\u81e3\u66ff\u965b\u4e0b\u53d1\u843d\u4fbf\u662f\u3002\u201d \u6b66\u5219\u5929\u9053\uff1a\u201c\u4f60\u5f85\u5982\u4f55\u53d1\u843d\uff1f\u201d\u72c4\u4ec1\u6770\u9041\uff1a\u201c\u5f8b\u6709\u5e38\u5211\uff0c\u6740\u4eba\u8005\u6b7b\uff0c\u8c0b\u6740\u738b\u5b50\uff0c\u7f6a\u52a0\u4e00 \u7b49\uff0c\u7406\u5408\u51cc\u8fdf\u3002\u201d\u6b66\u5219\u5929\u9053\uff0c\u201c\u4e0d\uff0c\u4f60\u6709\u5148\u4eba\u4e4b\u89c1\uff0c\u8fd9\u4ef6\u6848\u5b50\u6211\u4e0d\u653e\u5fc3\u8ba9\u4f60\u5ba1\u4e86\u3002\u201d\u72c4 \u4ec1\u6770\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u965b\u4e0b\u7684\u8d23\u5907\uff0c\u6055\u5fae\u81e3\u611a\u9c81\uff0c\u5c1a\u672a\u9886\u4f1a\uff0c\u8bf7\u965b\u4e0b\u518d\u52a0\u6307\u70b9\u3002\u201d\u6b66\u5219 \u5929\u9053\uff1a\u201c\u4f60\u5148\u5c31\u8ba4\u5b9a\u4e86\u8fd9\u4e24\u4eba\u5fc5\u662f\u6740\u4eba\u7684\u51f6\u624b\uff0c\u672a\u5ba1\u6e05\u695a\uff0c\u5c31\u5148\u5b9a\u4e86\u7f6a\u540d\uff0c\u8fd9\u6837\u4e00\u6765\uff0c \u91cf\u5211\u5c31\u53ef\u80fd\u5931\u5f53\u4e86\u3002\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u65e9\u5df2\u62db\u8ba4\u4e86\u5417\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u8c0b\u6740\u7f6a\u4e5f \u6709\u4e3b\u72af\u4ece\u72af\u4e4b\u5206\uff0c\u7109\u80fd\u4e0d\u95ee\u6e05\u695a\uff1f\u201d\u5477\u4e86\u4e00\u53e3\u70ed\u8336\uff0c\u5bf9\u90a3\u4e24\u4e2a\u519b\u5b98\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7528\u6211\u7684 \u540d\u4e49\uff0c\u6740\u6211\u7684\u513f\u5b50\uff0c\u8fd9\u6076\u6bd2\u7684\u4e3b\u610f\u662f\u8c01\u51fa\u7684\uff1f\u201d\u97e9\u8363\u62ac\u8d77\u5934\u6765\uff0c\u773c\u5149\u95ea\u70c1\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c \u6b66\u5219\u5929\u9053\uff0c\u201c\u4f60\u4eec\u82e5\u4e0d\u628a\u4e3b\u4f7f\u7684\u4eba\u4ece\u5b9e\u7528\u6765\uff0c\u4ee3\u4eba\u53d7\u7f6a\uff0c\u8eab\u53d7\u51cc\u8fdf\uff0c\u503c\u4e0d\u503c\u5f97\uff1f\u201d \u3000\u3000\u7a0b\u52a1\u7532\u53eb\u9053\uff1a\u201c\u6211\u4eec\u6740\u4e86\u4f60\u7684\u513f\u5b50\uff0c\u4f60\u80af\u653e\u8fc7\u6211\u4eec\u5417\uff1b\u6b66\u5219\u5929\u9053\uff1a\u201c\u4ece\u72af\u7f6a\u51cf\u4e00\u7b49\uff0c \u63ed\u9732\u53db\u9006\u6709\u529f\u7684\uff0c\u770b\u529f\u52b3\u7684\u5927\u5c0f\uff0c\u91cf\u60c5\u518d\u51cf\u3002\u4f60\u4eec\u62db\u51fa\u4e3b\u4f7f\u7684\u4eba\uff0c\u4e5f\u8bb8\u8fd8\u8981\u5904\u7f5a\uff0c\u4f46\u6b7b \u7f6a\u603b\u53ef\u514d\u4e86\u3002\u201d\u7a0b\u52a1\u7532\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u8eab\u4e3a\u5929\u5b50\uff0c\u5c82\u6709\u620f\u8a00\uff1f\u201d\u6740\u5bb3 \u592a\u5b50\uff0c\u7f6a\u540d\u5b9e\u5728\u662f\u5927\u5230\u65e0\u53ef\u518d\u5927\uff0c\u8fd9\u4e24\u4eba\u81ea\u4efd\u5fc5\u6b7b\uff0c\u505a\u68a6\u4e5f\u6599\u4e0d\u5230\u8fd8\u5b58\u4e00\u7ebf\u751f\u673a\uff0c\u767b\u65f6 \u51f6\u987d\u4e4b\u6c14\u5927\u51cf\uff0c\u97e9\u8363\u98a4\u58f0\u9053\uff1a\u201c\u6211\u4eec\u4e0a\u4e86\u4e3b\u4f7f\u6709\u7684\u5f53\u4e86\uff0c\u4ed6\u8bf4\u965b\u4e0b\u6b8b\u66b4\u4e2a\u4ec1\uff0c\u7978\u5bb3\u5929\u4e0b\uff0c \u5374\u539f\u6765\u965b\u4e0b\u662f\u8fd9\u822c\u5bbd\u539a\u3002\u201d\u6b66\u5219\u5929\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u96be\u8fc7\uff0c\u628a\u4e3b\u4f7f\u8005\u8bf4\u51fa\u7c73\u5427\u3002\u55ef\uff0c\u662f \u5f90\u656c\u4e1a\u5417\uff1f\u201d\u97e9\u8363\u9053\uff1a\u201c\u4e0d\uff0c\u82f1\u56fd\u516c\u867d\u7136\u610f\u56fe\u8c0b\u53cd\uff0c\u5374\u8fd8\u4e0d\u4f1a\u51fa\u8fd9\u6837\u6076\u6bd2\u7684\u4e3b\u610f\uff0c\u4e3b\u4f7f \u7684\u4eba\u5b9e\u5728\u662f\uff0c\u662f\u2014\u2014\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u7a0b\u52a1\u7532\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u6599\u4e0d\u5230\u5427\uff1f\u4e3b\u4f7f\u7684 \u4eba\u662f\u4e2d\u4e66\u4ee4\u88f4\u708e\uff01\u201d \u3000\u3000\u5510\u4ee3\u7684\u5b98\u5236\uff0c\u4e2d\u673d\u4ee4\u76f8\u5f53\u4e8e\u5bb0\u76f8\uff0c\u6b66\u5219\u5929\u9893\u7136\u8bf4\u9053\uff1a\u201c\u786e\u4e4e\u6599\u60f3\u4e0d\u5230\uff0c\u88f4\u708e\u6ee1\u53e3\u4ec1 \u4e49\u9053\u5fb7\uff0c\u5bf9\u56fd\u4e8b\u4e5f\u5f88\u7528\u5fc3\u7ba1\u7406\uff0c\u5c45\u7136\u662f\u4e2a\u53db\u9006\uff01\u4e0d\u8fc7\u4e5f\u597d\uff0c\u6bd2\u75ae\u53d1\u4f5c\u51fa\u6765\uff0c\u603b\u6bd4\u85cf\u5728\u8eab \u4f53\u5185\u90e8\u4e3a\u5bb3\u7684\u597d\u3002\u201d\u8f6c\u8fc7\u5934\u6765\u5bf9\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u8fd1\u7c73\u6211\u4e5f\u89c9\u5f97\u88f4\u708e\u6709\u70b9\u865a\u4f2a\uff0c\u5374\u8fd8\u6599\u4e0d\u5230 \u4ed6\u5982\u6b64\u4e4b\u574f\u3002\u5440\uff0c\u4f60\u4eec\u90fd\u8d5e\u6211\u77e5\u4eba\u5584\u4efb\uff0c\u5728\u8fd9\u70b9\u4e0a\uff0c\u770b\u6765\u6211\u6bd4\u5927\u5b97\u7687\u5e1d\uff08\u674e\u4e16\u6c11\uff09\u8fd8\u5dee \u5f97\u8fdc\u54ea\uff01\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u965b\u4e0b\u662f\u81ea\u53e4\u5230\u4eca\uff0c\u7b2c\u4e00\u4f4d\u4e34\u671d\u7684\u5723\u6bcd\uff0c\u4ee5\u975e\u5e38\u4e4b\u4eba\uff0c\u4efb\u975e\u5e38\u4e4b \u4efb\uff0c\u53cd\u5bf9\u965b\u4e0b\u7684\u4e5f\u81ea\u7136\u6bd4\u53cd\u5bf9\u592a\u5b97\u7687\u5e1d\u7684\u591a\u5f97\u591a\uff0c\u660e\u7684\u6697\u7684\u90fd\u6709\u3002\u4e0d\u662f\u965b\u4e0b\u4e0d\u53ca\u592a\u5b97\u7687 \u5e1d\uff0c\u800c\u662f\u965b\u4e0b\u7684\u5904\u5883\u6bd4\u592a\u5b97\u7687\u5e1d\u8270\u96be\u5f97\u591a\uff01\u201d\u6b66\u5219\u5929\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u77e5\u6211\u8005\u5176\u552f\u72c4\u537f\u4e4e\uff1f \u5440\u53ef\u60dc\u4f60\u59d3\u72c4\uff01\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u59d3\u674e\u5462\uff1f\u201d \u3000\u3000\u8f6c\u8fc7\u5934\u672a\u5bf9\u90a3\u4e24\u4e2a\u519b\u5b98\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u63ed\u53d1\u88f4\u708e\uff0c\u5927\u5927\u6709\u529f\uff0c\u6b7b\u7f6a\u514d\u4e86\uff01\u54fc\uff0c\u88f4\u708e\u4e3a \u4ec0\u4e48\u8fd9\u6837\u6076\u6bd2\u5bf9\u6211\uff1f\u201d \u3000\u3000\u7a0b\u52a1\u7532\u9053\uff1a\u201c\u82f1\u56fd\u516c\u5bc6\u8c0b\u4e3e\u5175\uff0c\u7ea6\u597d\u4e86\u88f4\u708e\u505a\u5185\u5e94\u3002\u88f4\u708e\u5dee\u9063\u6211\u4eec\u523a\u6740\u592a\u5b50\uff0c\u4e00\u6765 \u53ef\u4f7f\u5929\u540e\u965b\u4e0b\u8499\u53d7\u6076\u540d\uff0c\u4e8c\u6765\u53ef\u4ee4\u965b\u4e0b\u731c\u7591\u4e18\u5927\u5c06\u519b\uff1b\u4e09\u6765\u4ee4\u965b\u4e0b\u6709\u5931\u5b50\u4e4b\u75db\uff0c\u65e0\u5fc3\u518d \u7406\u56fd\u4e8b\u3002\u201d\u6b66\u5219\u5929\u51b7\u7b11\u9053\uff1a\u201c\u4e00\u4e3e\u4e09\u5f97\uff0c\u88f4\u708e\u4ed6\u60f3\u7684\u5012\u597d\uff01\u4e0d\u9519\uff0c\u6bcd\u4eb2\u5931\u4e86\u513f\u5b50\uff0c\u8c01\u4e0d \u4f24\u5fc3\uff1f\u4f46\u82e5\u88f4\u708e\u5f90\u656c\u4e1a\u4e4b\u6d41\u5f97\u901e\uff0c\u5c31\u8981\u6709\u66f4\u591a\u7684\u6bcd\u4eb2\u5931\u6389\u513f\u5b50\uff0c\u66f4\u591a\u7684\u8001\u767e\u59d3\u4f24\u5fc3\uff01\u654c \u4eba\u76fc\u671b\u6211\u7684\uff0c\u6211\u504f\u504f\u8981\u53cd\u5176\u9053\u800c\u884c\u4e4b\uff0c\u56fd\u5bb6\u5927\u4e8b\uff0c\u6211\u662f\u975e\u7406\u4e0d\u53ef\uff01\u201d\u8bf4\u8bdd\u65a9\u9489\u622a\u94c1\uff0c\u82f1 \u6c14\u52c3\u52c3\uff0c\u4e0a\u5b98\u5a49\u513f\u4f0f\u5728\u74e6\u9762\u5077\u542c\uff0c\u4ea6\u81ea\u51db\u7136\uff0c\u634f\u7740\u5315\u9996\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u82e5\u628a\u5979\u523a\u6740\u4e86\uff0c \u56fd\u5bb6\u5927\u4e8b\uff0c\u5c82\u4e0d\u662f\u8981\u8ba9\u88f4\u708e\u4e4b\u6d41\u53bb\u7ba1\uff1f\u4ed6\u4f1a\u7ba1\u5f97\u6bd4\u6b66\u5219\u5929\u597d\u5417\uff1f\u201d\u4f46\u89c9\u90a3\u67c4\u5315\u9996\u7adf\u6709\u5343 \u94a7\u4e4b\u91cd\uff0c\u63d0\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6b66\u5219\u5929\u5429\u5490\u90a3\u8001\u5927\u76d1\u9053\uff1a\u201c\u628a\u8fd9\u4e24\u4eba\u62bc\u51fa\u53bb\uff0c\u53eb\u4ed6\u4eec\u5c0f\u5fc3\u4fdd\u62a4\uff0c\u4eca\u665a\u4e4b\u4e8b\uff0c \u7edd\u5bf9\u4e0d\u8bb8\u6cc4\u6f0f\u3002\u201d\u7a0b\u52a1\u7532\u4e0e\u97e9\u8363\u6ee1\u7736\u6cea\u6c34\uff0c\u53e9\u5934\u8c22\u6069\uff0c\u7a0b\u52a1\u7532\u5ffd\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\uff01\u201d\u6b66 \u5219\u5929\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d\u7a0b\u52a1\u7532\u9053\uff1a\u201c\u8bf7\u965b\u4e0b\u9632\u5907\u523a\u5ba2\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4ec0\u4e48\uff0c \u88f4\u708e\u8fd8\u6d3e\u6709\u4eba\u8981\u884c\u523a\u6211\u5417\uff1f\u201d\u7a0b\u52a1\u7532\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u662f\u6015\u523a\u5ba2\u5c31\u5728\u5c4b\u4e2d\u3002\u201d\u6b66\u5219\u5927\u9053\uff1a \u201c\u80e1\u8bf4\uff0c\u5c4b\u5b50\u91cc\u90fd\u662f\u6211\u7684\u4eb2\u4fe1\uff0c\u54ea\u6765\u523a\u5ba2\uff1f\u201d\u7a0b\u52a1\u7532\u9053\uff1a\u201c\u6211\u6b66\u529f\u867d\u5e9f\uff0c\u8fd8\u542c\u5f97\u51fa\u5c4b\u5b50 \u5916\u9762\u4f3c\u4e4e\u6709\u4eba\u57cb\u4f0f\uff0c\u53ea\u4e0d\u77e5\u9053\u662f\u8f6e\u503c\u7684\u6b66\u58eb\u8fd8\u662f\u523a\u5ba2\uff1f\u965b\u4e0b\u5bf9\u6211\u5bbd\u539a\u65e0\u8fb9\u3002\u6211\u4e0d\u80fd\u4e0d\u63d0 \u9192\u965b\u4e0b\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u90a3\u5fc5\u7136\u662f\u8f6e\u503c\u7684\u6b66\u58eb\u65e0\u7591\u4e86\u3002\u82e5\u663e\u523a\u5ba2\uff0c\u5c82\u6709\u57cb\u4f0f\u8fd9\u4e48\u4e45\u8fd8\u4e0d \u52a8\u624b\u4e4b\u7406\uff0c\u4f55\u51b5\u521a\u624d\u53ea\u6709\u6211\u548c\u5bab\u5973\u5728\u8fd9\u91cc\u5462\u3002\u4e0d\u5fc5\u5927\u60ca\u5c0f\u602a\uff0c\u4f60\u4eec\u51fa\u53bb\u5427\u3002\u201d\u7a0b\u52a1\u7532\u4e00 \u60f3\uff0c\u679c\u7136\u6709\u7406\uff0c\u4e0d\u4fbf\u518d\u591a\u8a00\uff0c\u8ba9\u8001\u592a\u76d1\u5c06\u4ed6\u4eec\u89e3\u51fa\u53bb\u6536\u62bc\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u5f85\u5f97\u5fc3\u795e\u7a0d\u5b9a\uff0c\u518d\u4ece\u74e6\u9699\u7f1d\u4e2d\u5f20\u671b\u4e0b\u53bb\uff0c\u53ea\u89c1\u6b66\u5219\u5929\u62ff\u8d77 \u4e00\u9762\u955c\u5b50\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u8001\u5189\u5189\u5c06\u81f3\u516e\uff0c\u6050\u6015\u6709\u540d\u4e4b\u4e0d\u7acb\u3002\u201d\u8f7b\u63a0\u9b13\u8fb9\uff0c\u4f3c\u4e4e\u662f\u62d4\u6389\u4e86 \u51e0\u6839\u65b0\u6dfb\u7684\u767d\u53d1\uff0c\u505c\u4e86\u4e00\u505c\uff0c\u95ee\u9053\uff1a\u201c\u72c4\u537f\uff0c\u6211\u4eca\u665a\u8fd9\u4ef6\u6848\u5b50\u65ad\u5f97\u600e\u6837\uff1f\u201d\u72c4\u4ec1\u6770\u9053\uff1a \u201c\u965b\u4e0b\u771f\u5982\u79e6\u955c\u9ad8\u60ac\uff0c\u5fae\u81e3\u4ea6\u81ea\u5fc3\u670d\u3002\u4e0d\u8fc7\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u965b\u4e0b\u4eca\u665a\u7684\u5bbd\u539a\uff0c\u5374\u662f\u5927\u51fa\u5fae \u81e3\u610f\u6599\u4e4b\u5916\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u81ea\u5df1\u77e5\u9053\u6211\u5e76\u4e0d\u662f\u4e00\u4e2a\u5bbd\u539a\u7684\u4eba\uff0c\u6211\u4e0d\u8fc7\u79c9\u516c\u529e\u7406 \u7f62\u4e86\u3002\u82e5\u6709\u5371\u53ca\u56fd\u5bb6\uff0c\u5bb3\u53ca\u767e\u59d3\u7684\uff0c\u4e5f\u8bb8\u6211\u8981\u6bd4\u4f60\u66f4\u4e25\u5389\u5462\u3002\u6211\u662f\u4e00\u624b\u62ff\u7740\u955c\u5b50\uff0c\u4e00\u624b \u62ff\u7740\u97ad\u5b50\u7684\u4eba\u3002\u201d\u72c4\u4ec1\u6770\u70b9\u70b9\u5934\u9053\uff1a\u201c\u7ba1\u7406\u56fd\u5bb6\uff0c\u672c\u6765\u5c31\u8981\u4e00\u624b\u62ff\u7740\u955c\u5b50\uff0c\u4e00\u624b\u62ff\u7740\u97ad \u5b50\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6015\u7684\u662f\u8001\u4e4b\u5c06\u81f3\uff0c\u574f\u4eba\u592a\u591a\uff0c\u6211\u4e0d\u591f\u7cbe\u795e\u53bb\u5bf9\u4ed8\u4e86\u3002\u201d\u72c4\u4ec1\u6770\u9053\uff1a \u201c\u965b\u4e0b\u662f\u5927\u64cd\u52b3\u4e86\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8981\u4f60\u66ff\u6211\u5206\u52b3\uff0c\u4eca\u665a\u6211\u5c31\u5c06\u4e00\u6839\u97ad\u5b50\u4ea4\u7ed9\u4f60\uff01\u201d \u8bf4\u7f62\u679c\u7136\u53eb\u5bab\u5973\u62ff\u4e86\u4e00\u6839\u97ad\u5b50\u6765\uff0c\u90a3\u662f\u4e00\u6761\u91d1\u5149\u707f\u70c2\u7684\u957f\u97ad\uff0c\u6b66\u5219\u5929\u5e84\u91cd\u7684\u6367\u5728\u624b\u91cc\uff0c \u7ad9\u4e86\u8d77\u6765\uff0c\u4ea4\u7ed9\u72c4\u4ec1\u6770\u3002 \u3000\u3000\u72c4\u4ec1\u6770\u60f6\u6050\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u965b\u4e0b\u8d50\u97ad\u4e4b\u610f\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u8fd9\u6761\u91d1\u97ad\u662f\u592a\u5b97\u7687\u5e1d\u7559\u7ed9 \u6211\u7684\uff0c\u6211\u73b0\u5728\u90d1\u91cd\u7684\u4ea4\u4ed8\u7ed9\u4f60\u3002\u4f60\u6301\u6b64\u97ad\uff0c\u5982\u6715\u4eb2\u4e34\uff0c\u51e1\u6709\u4e0d\u6cd5\u4e4b\u5f92\uff0c\u4e0d\u8bba\u7687\u4eb2\u56fd\u621a\uff0c \u516c\u4faf\u8d35\u4ecb\uff0c\u4f60\u90fd\u53ef\u4ee5\u5c06\u4ed6\u97ad\u6253\u3002\u8fd9\u513f\u7684\u77e5\u53bf\u5c31\u662f\u4e00\u4e2a\u8be5\u53d7\u97ad\u6253\u7684\u4eba\uff0c\u4f60\u660e\u5929\u53ef\u4ee5\u53bb\u5c06\u4ed6 \u91cd\u91cd\u6253\u4e86\u4e00\u767e\u97ad\u3002\u201d\u72c4\u4ec1\u6770\u63a5\u8fc7\u91d1\u97ad\uff0c\u53e9\u5934\u8c22\u9053\uff1a\u201c\u965b\u4e0b\u5982\u6b64\u4fe1\u4efb\u5c0f\u81e3\uff0c\u7c89\u8eab\u788e\u9aa8\uff0c\u4e0d \u8db3\u56fe\u62a5\u3002\u201d\u53c8\u9053\uff1a\u201c\u4f46\u613f\u8fd9\u6761\u91d1\u97ad\uff0c\u8d8a\u5c11\u7528\u5b83\u8d8a\u597d\u3002\u201d \u3000\u3000\u5916\u9762\u6572\u8d77\u4e86\u56db\u66f4\uff0c\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u965b\u4e0b\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\u4e48\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4ef6\u91cd \u8981\u7684\u4e8b\u60c5\u8981\u4e0e\u4f60\u5546\u91cf\u3002\u72c4\u537f\uff0c\u6211\u4eca\u665a\u786e\u662f\u6709\u70b9\u4f24\u5fc3\uff01\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u592a\u5b50\u60e8\u6b7b\uff0c\u53ef\u5e78\u4e3b \u51f6\u5df2\u7ecf\u5ba1\u51fa\u2026\u2026\u201d \u3000\u3000\u6b66\u5219\u5929\u622a\u7740\u4ed6\u7684\u8bdd\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u665a\u7684\u4f24\u5fc3\uff0c\u4e0d\u5355\u662f\u4e3a\u4e86\u513f\u4e86\uff0c\u4e5f\u4e3a\u4e86\u674e\u9038\uff0c\u60f3\u4e0d\u5230 \u4ed6\u4e5f\u4e0e\u5f90\u656c\u4e1a\u4e00\u9053\u6765\u53cd\u5bf9\u6211\u3002\u201d\u72c4\u4ec1\u6770\u9053\uff1a \u3000\u3000\u201c\u965b\u4e0b\u5728\u957f\u5b89\u65f6\u6d3e\u9063\u4e86\u674e\u5b5d\u9038\u5c06\u519b\u4e3a\u626c\u5dde\u9053\u5927\u603b\u7ba1\uff0c\u4ed6\u7387\u9886\u7684\u4e09\u5341\u4e07\u5927\u519b\u5373\u5c06\u5357\u4e0b\uff0c \u5f90\u656c\u4e1a\u518d\u52a0\u4e0a\u4e00\u4e2a\u674e\u9038\uff0c\u6211\u770b\u4e5f\u7b97\u4e0d\u4ec0\u4e48\u3002\u201d\u6b66\u5219\u5929\u559f\u7136\u53f9\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6015\u674e\u9038\u62a2\u6211\u7684 \u6c5f\u5c71\uff0c\u800c\u662f\u6015\u6211\u8eab\u6b7b\u4e4b\u540e\uff0c\u8fd9\u6c5f\u5c71\u4e0d\u77e5\u4ea4\u4ed8\u4e0e\u8c01\uff1f\u201d \u3000\u3000\u72c4\u4ec1\u6770\u5fd9\u9053\uff1a\u201c\u965b\u4e0b\u6625\u79cb\u6b63\u76db\uff0c\u80e1\u4e3a\u51fa\u6b64\u4e0d\u7965\u4e4b\u8a00\uff1f\u201d\u6b66\u5219\u5929\u5fae\u7b11\u9053\uff1a\u201c\u51e1\u4eba\u7686\u6709 \u4e00\u6b7b\uff0c\u505a\u4e86\u7687\u5e1d\u5c31\u80fd\u514d\u6b7b\u4e48\uff1f\u4f55\u5fc5\u8bb3\u8a00\u3002 \u3000\u3000\u4f60\u662f\u77e5\u9053\u7684\uff0c\u6211\u6709\u56db\u4e2a\u513f\u5b50\uff0c\u5927\u513f\u5b50\u674e\u5f18\u8bef\u670d\u5a46\u7f57\u95e8\u6bd2\u836f\uff0c\u65e9\u5df2\u8eab\u6b7b\uff0c\u4e8c\u5b50\u674e\u8d24\uff0c \u73b0\u5728\u53c8\u88ab\u523a\u6740\uff1b\u4ed6\u662f\u6b7b\u8bfb\u4e66\u7684\u4e66\u5446\u5b50\uff0c\u7eb5\u7136\u4e0d\u6b7b\u4e00\u4e5f\u4e0d\u80fd\u4f20\u4ee5\u5927\u4f4d\u3002\u4e09\u5b50\u674e\u663e\u5eb8\u61e6\u65e0\u80fd\uff0c \u56e0\u6b64\u6211\u624d\u8d2c\u4ed6\u505a\u5362\u9675\u738b\uff1b\u56db\u5b50\u674e\u65e6\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u4e0d\u8fc7\u770b\u6765\u4e5f\u4e0d\u662f\u4e2a\u6709\u624d\u80fd\u7684\u4eba\u3002 \u3000\u3000\u7687\u5ba4\u4e4b\u4e2d\uff0c\u674e\u9038\u662f\u6bd4\u8f83\u6709\u624d\u80fd\u7684\uff0c\u6211\u66fe\u7ecf\u60f3\u8fc7\u5c06\u6765\u4e0d\u4f20\u4f4d\u7ed9\u513f\u5b50\u800c\u4f20\u7ed9\u4ed6\uff0c\u5982\u4eca\u770b \u6765\uff0c\u4ed6\u7684\u624d\u80fd\u4e0d\u8fc7\u662f\u7528\u6765\u66ff\u4ed6\u81ea\u5df1\u4ee5\u53ca\u90a3\u4e9b\u65e7\u65e5\u7684\u738b\u516c\u5de8\u65cf\u593a\u56de\u5931\u53bb\u7684\u5229\u76ca\u800c\u5df2\uff0c\u66f4\u4e0d \u662f\u5408\u9002\u7684\u4eba\u9009\u4e86\u3002 \u3000\u3000\u5509\uff0c\u4f60\u8bf4\u6211\u8fd9\u961c\u4f4d\u8be5\u4f20\u7ed9\u8c01\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u542c\u5f97\u5fc3\u5f26\u98a4\u6296\uff0c\u60f3\u9053\uff1a\u201c\u674e\u9038\u54e5\u54e5\u628a\u4ed6\u5f53\u4f5c\u4e0d\u5171\u6234\u5929\u7684\u5927\u4ec7\u4eba\uff0c\u5979\u5374\u66fe\u7ecf \u60f3\u8fc7\u8981\u628a\u7687\u4f4d\u4f20\u7ed9\u4ed6\uff01\u201d\u53ea\u542c\u5f97\u6b66\u5219\u5929\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u6211\u7684\u4f84\u513f\u6b66\u4e09\u601d\u867d\u7136\u4e5f\u4e0d\u662f\u4ec0\u4e48\u6709 \u624d\u80fd\u7684\u4eba\uff0c\u4f46\u597d\u50cf\u6bd4\u6211\u8fd9\u51e0\u4e2a\u5b9d\u8d1d\u513f\u5b50\u7a0d\u4e3a\u597d\u4e9b\uff0c\u6211\u5c06\u4ed6\u7acb\u4e3a\u7687\u55e3\uff0c\u4f60\u770b\u600e\u6837\uff1f\u201d\u72c4\u4ec1 \u6770\u9053\uff1a\u201c\u965b\u4e0b\u7acb\u55e3\uff0c\u81e3\u5b50\u672c\u4e0d\u8be5\u5e72\u9884\u3002\u4f46\u8bf7\u965b\u4e0b\u4e09\u601d\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u53ea\u6709\u513f\u5b50\u505a\u4e86\u7687\u5e1d\u4e4b \u540e\uff0c\u6bcd\u540e\u53ef\u5165\u7940\u592a\u5e99\uff0c\u672a\u95fb\u6709\u4f84\u513f\u505a\u4e86\u7687\u5e1d\uff0c\u59d1\u6bcd\u53ef\u4ee5\u5165\u7940\u592a\u5e99\u7684\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6211 \u53ea\u6c42\u6c5f\u5c71\u4ed8\u6258\u5f97\u4eba\uff0c\u6211\u8eab\u540e\u7684\u54c0\u8363\uff0c\u65e9\u975e\u6240\u8ba1\uff0c\u5176\u5b9e\uff0c\u6b66\u4e09\u601d\u4e5f\u4e0d\u5f88\u9002\u5b9c\uff0c\u82e5\u80fd\u4efb\u7531\u6211 \u7684\u610f\u601d\uff0c\u6211\u771f\u60f3\u628a\u7687\u4f4d\u4f20\u7ed9\u5916\u59d3\uff01\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u53cc\u7738\u70af\u70af\uff0c\u77a7\u7740\u72c4\u4ec1\u6770\u3002\u72c4\u4ec1\u6770\u6025\u5fd9\u8dea \u4e0b\u53e9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u4e07\u4e07\u4e0d\u53ef\u3002\u201d\u6b66\u5219\u706d\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u53ef\uff1f\u201d\u72c4\u4ec1\u6770\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u6bd4 \u5c27\u821c\u4e4b\u65f6\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u7687\u4f4d\u4e00\u7edf\u7684\u89c2\u5ff5\uff0c\u4e45\u5df2\u6df1\u5165\u4eba\u5fc3\uff0c\u5c27\u821c\u53ef\u4ee5\u7985\u8ba9\uff0c\u965b\u4e0b\u4e0d\u53ef\u7985\u8ba9\uff0c \u82e5\u7136\u4f20\u4e4b\u5916\u59d3\uff0c\u53ea\u6015\u8981\u5f15\u8d77\u6ed4\u5929\u7684\u6218\u7978\uff01\u201d \u3000\u3000\u6b66\u5219\u5929\u9ed8\u7136\u4e0d\u8bed\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u65b9\u59cb\u957f\u957f\u7684\u5401\u4e86\u53e3\u6c14\uff0c\u4ec5\u4ec5\u5410\u51fa\u4e86\u4e09\u4e2a\u5b57\u201c\u6211\u8f93\u4e86\uff01\u201d \u9893\u7136\u5750\u4e0b\uff0c\u970e\u65f6\u95f4\u597d\u50cf\u8001\u4e86\u5341\u5e74\u4e00\u822c\uff01\u72c4\u4ec1\u6770\u662f\u61c2\u5f97\u8fd9\u4e09\u4e2a\u5b57\u7684\u610f\u601d\u7684\uff0c\u4ed6\u77e5\u9053\u6b66\u5219\u5929 \u60f3\u628a\u5e1d\u4f4d\u4f20\u7ed9\u4ed6\uff0c\u7ec8\u4e8e\u7ed9\u4ed6\u7684\u8bf4\u8bdd\u6253\u6d88\u4e86\u3002\u6b66\u5219\u5929\u5e73\u751f\u4e0d\u77e5\u7ecf\u8fc7\u591a\u5c11\u5927\u98ce\u5927\u6d6a\uff0c\u6bcf\u4e00\u6b21 \u5979\u90fd\u4ece\u8270\u96be\u4e4b\u4e2d\u5f97\u5230\u80dc\u5229\uff0c\u7136\u800c\u8fd9\u4e00\u6b21\uff0c\u5728\u7687\u4f4d\u7ee7\u627f\u7684\u95ee\u9898\u4e0a\uff0c\u5979\u7ec8\u4e8e\u4e0d\u80fd\u4e0d\u8ba4\u8f93\u4e86\uff0c \u5c3d\u7ba1\u5979\u60f3\u4e0d\u4f20\u5b50\u800c\u4f20\u8d24\uff0c\u4f46\u5979\u626d\u4e0d\u8f6c\u51e0\u5343\u5e74\u6765\u6839\u6df1\u8482\u56fa\u7684\u89c2\u5ff5\uff01 \u3000\u3000\u72c4\u4ec1\u6770\u5fc3\u4e2d\u65e2\u662f\u611f\u6fc0\u53c8\u662f\u6050\u60e7\uff0c\u4ed6\u61c2\u5f97\u6b66\u5219\u5929\u7684\u610f\u601d\uff0c\u5374\u6781\u529b\u6291\u5236\u81ea\u5df1\u7684\u611f\u60c5\uff0c\u88c5 \u4f5c\u4e0d\u61c2\uff0c\u60f6\u7136\u95ee\u9053\uff1a\u201c\u965b\u4e0b\u662f\u4e0d\u662f\u4e3a\u4e86\u5f90\u656c\u4e1a\u7684\u8c0b\u53cd\u800c\u5fe7\u8651\uff1f\u201d\u6b66\u5219\u5929\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5f90\u656c\u4e1a\u7663\u75a5\u4e4b\u60a3\uff0c\u6709\u4f55\u663e\u8651\uff1f\u9632\u5f53\u7136\u662f\u8981\u9632\u7684\uff0c\u6211\u4e5f\u65e9\u5df2\u6709\u4e86\u5e03\u7f6e\u4e86\u3002\u201d\u505c\u4e86\u4e00\u505c\uff0c \u53c8\u9053\uff1a\u201c\u5f90\u656c\u4e1a\u6211\u5012\u662f\u4e0d\u600e\u6837\u653e\u5728\u5fc3\u4e0a\u3002\u53ea\u542c\u8bf4\u9a86\u5bbe\u738b\u4e5f\u6295\u5165\u4e86\u4ed6\u7684\u5e55\u4e0b\uff0c\u6b64\u4eba\u9887\u6709\u6587 \u540d\uff0c\u5374\u662f\u6709\u70b9\u53ef\u60dc\u3002\u5c06\u6765\u5f90\u656c\u4e1a\u4e3e\u5175\uff0c\u90a3\u7bc7\u8ba8\u4f10\u6211\u7684\u6a84\u6587\uff0c\u5fc5\u5b9a\u662f\u9a86\u5bbe\u738b\u6240\u5199\uff0c\u6211\u5012\u60f3 \u5148\u7779\u4e3a\u5feb\u5462\u3002\u4f60\u52a1\u5fc5\u62ff\u7ed9\u6211\u770b\u3002\u201d\u72c4\u4ec1\u6770\u5e94\u4e86\u4e00\u58f0\uff0c\u518d\u95ee\u9053\uff1a\u201c\u965b\u4e0b\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\u5417\uff1f\u201d \u3000\u3000\u6b66\u5219\u5929\u773c\u73e0\u4e00\u8f6c\uff0c\u4f3c\u662f\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\uff0c\u5374\u6b32\u8a00\u53c8\u6b62\uff0c\u7ec8\u4e8e\u6325\u6325\u624b\u9053\uff1a\u201c\u6ca1\u6709\u4e86\uff0c \u4f60\u6b47\u606f\u53bb\u5427\u3002\u201d\u76ee\u9001\u72c4\u4ec1\u6770\u7684\u80cc\u5f71\uff0c\u5fc3\u4e2d\u5ffd\u89c9\u4e00\u7247\u60d8\u7136\u3002 \u3000\u3000\u72c4\u4ec1\u6770\u8d70\u540e\u3002\u5bab\u5973\u7980\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\uff0c\u65f6\u5019\u4e0d\u65e9\uff0c\u965b\u4e0b\u4e5f\u8bf7\u5b89\u6b47\u53bb\u5427\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a \u201c\u597d\uff0c\u4f60\u4eec\u53bb\u7ed9\u6211\u6536\u62fe\u4e00\u4e0b\u5367\u623f\uff0c\u6211\u518d\u6279\u4e00\u4ef6\u516c\u6587\uff0c\u5c31\u53bb\u7761\u5566\u3002\u201d \u3000\u3000\u5c4b\u5b50\u91cc\u53ea\u5269\u4e0b\u4e86\u6b66\u5219\u5929\u4e00\u4e2a\u4eba\uff0c\u5979\u63d0\u8d77\u7b14\u6765\uff0c\u8fc5\u901f\u7684\u5728\u516c\u6587\u4e0a\u6279\u4e86\u51e0\u4e2a\u5b57\uff0c\u5ffd\u7136\u63b7 \u7b14\u957f\u53f9\uff0c\u79bb\u5ea7\u800c\u8d77\uff0c\u8d70\u5230\u9636\u524d\uff0c\u6765\u56de\u6f2b\u6b65\uff0c\u4ef0\u671b\u6708\u5149\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u5973\u4eba\u505a\u7687\u5e1d\u539f\u7c73\u5c31 \u6709\u8fd9\u4e48\u591a\u96be\u5904\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u634f\u7740\u5315\u9996\uff0c\u5fc3\u5934\u535c\u535c\u7684\u8df3\uff0c\u5979\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u73b0\u5728\u5c31\u5728\u5979\u7684\u773c\u524d\uff0c\u201c\u53ea\u8981\u5315 \u9996\u4e00\u53d1\uff0c\u53ea\u8981\u5315\u9996\u4e00\u53d1\u2026\u2026\u201d\u5929\u5440\uff0c\u5979\u7684\u624b\u6307\u5374\u98a4\u6296\u5f97\u8fd9\u4e48\u5389\u5bb3\uff0c\u5979\u7684\u5fc3\u601d\u77ac\u606f\u767e\u53d8\uff0c \u597d\u51e0\u6b21\u4e0b\u4e86\u6781\u5927\u7684\u51b3\u5fc3\u53d1\u51fa\u5315\u9996\uff0c\u5374\u4ecd\u7136\u53d1\u4e0d\u51fa\u6765\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u6b66\u5219\u5929\u81ea\u8a00\u81ea\u8bed\u8bf4\u9053\uff1a\u201c\u77e5\u6211\u8005\u8c13\u6211\u5fc3\u5fe7\uff0c\u4e0d\u77e5\u6211\u8005\u8c13\u6211\u4f55\u6c42\uff1f\u5509\uff0c\u8fe2\u8fe2\u826f \u591c\uff0c\u53ef\u60dc\u5c31\u6ca1\u6709\u4e00\u4e2a\u4eba\u53ef\u4ee5\u8c08\u5fc3\uff0c\u55ef\uff0c\u8c01\u5728\u8fd9\u513f\uff1f\u201d\u591c\u9759\u66f4\u6df1\uff0c\u4e0a\u5b98\u5a49\u513f\u6296\u7d22\u7684\u58f0\u97f3\u7ec8 \u4e8e\u7ed9\u5bdf\u89c9\u4e86\u3002 \u3000\u3000\u5f53\u5577\u4e00\u58f0\uff0c\u4e0a\u5b98\u5a49\u513f\u7684\u5315\u9996\u8dcc\u4e0b\u5730\u6765\uff0c\u5979\u81ea\u5df1\u4e5f\u968f\u7740\u4e00\u8dc3\u800c\u4e0b\uff0c\u7acb\u5373\u53c8\u634f\u7d27\u4e86\u7b2c\u4e8c \u628a\u5315\u9996\uff01 \u3000\u3000\u6b66\u5219\u5929\u5fae\u9732\u54a4\u5f02\uff0c\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u679c\u7136\u6709\u4e00\u4f4d\u523a\u5ba2\uff01\u201d\u867d\u51fa\u610f\u5916\uff0c\u795e\u8272\u4e0d\u53d8\uff0c\u6253\u91cf\u4e86 \u4e0a\u5b98\u5a49\u513f\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u62ff\u7740\u5315\u9996\uff0c\u5927\u7ea6\u662f\u60f3\u884c\u523a\u6211\u4e86\uff0c\u662f\u5417\uff0c\u6211\u5f88\u60f3\u77e5\u9053\uff0c\u4f60\u4e3a\u4ec0 \u4e48\u8981\u884c\u523a\u6211\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8e0f\u4e0a\u4e00\u6b65\uff0c\u534a\u8d77\u5315\u9996\uff0c\u5315\u9996\u6296\u52a8\u4e0d\u4f11\uff0c\u597d\u50cf\u5c06\u8981\u88ab\u523a\u6740\u7684\u662f\u5979\u800c\u4e0d\u662f\u6b66\u5219 \u5929\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u5979\u7684\u7b2c\u4e09\u628a\u5315\u9996\u53c8\u6389\u4e0b\u5730\u4e86\u3002\u6b66\u5219\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8981 \u5bb3\u6015\uff0c\u6211\u4f1a\u548c\u4f60\u8bb2\u9053\u7406\u7684\u3002\u54a6\uff0c\u4f60\u4e0d\u662f\u4e0a\u5b98\u5a49\u513f\u5417\uff1f\u957f\u5f97\u8fd9\u4e48\u5927\u4e86\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u505a\u68a6\u4e5f \u60f3\u4e0d\u5230\uff0c\u5979\u5c0f\u65f6\u5019\u4ec5\u4ec5\u89c1\u8fc7\u6b66\u5219\u5929\u4e00\u9762\uff0c\u6b66\u5219\u5929\u5c45\u7136\u8fd8\u8bb0\u5f97\u5979\u3002 \u3000\u3000\u6b66\u5219\u5929\u4ed4\u7ec6\u7684\u518d\u770b\u4e86\u4e0a\u5b98\u5a49\u513f\u4e00\u904d\uff0c\u7528\u5145\u6ee1\u559c\u60a6\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u679c\u7136\u662f\u4f60\uff0c \u662f\u8981\u6267\u638c\u5927\u79e4\uff0c\u8861\u91cf\u5929\u4e0b\u7684\u5c0f\u59d1\u5a18\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u964d\u751f\u7684\u524d\u5915\uff0c\u5979\u6bcd\u4eb2\u66fe\u68a6\u89c1\u5929\u4f38\u9001\u6765\u4e00 \u628a\u5927\u6026\uff0c\u8bf4\u5979\u5c06\u751f\u4e0b\u4e00\u4e2a\u4eba\u6765\uff0c\u6267\u638c\u5927\u79e4\uff0c\u8861\u91cf\u5929\u4e0b\u3002\u8fd9\u4ef6\u5f02\u4e8b\u66fe\u5728\u5bab\u4e2d\u666e\u904d\u6d41\u4f20\uff0c\u6545 \u6b64\u6b66\u5219\u5929\u5bf9\u4e0a\u5b98\u5a49\u51e0\u7684\u5370\u8c61\u7279\u522b\u6df1\u523b\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u6124\u6124\u8bf4\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u4f60\uff0c\u73b0\u5728\u4f60\u53ef\u4ee5\u4e0d\u5fc5\u95ee\u4e86\u5427\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a \u201c\u597d\uff0c\u54b1\u4eec\u5750\u4e0b\u6765\u8bf4\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u6413\u7740\u53cc\u624b\uff0c\u7d27\u7d27\u7684\u76ef\u7740\u6b66\u5219\u5929\u3002\u6b66\u5219\u5929\u9053\uff1a\u201c\u554a\uff0c\u4f60 \u5fc3\u91cc\u4e0d\u592a\u5b89\u9759\uff0c\u662f\u5417\uff1f\u4f60\u613f\u610f\u7ad9\u7740\u5c31\u7ad9\u770b\u5427\uff01\u6211\u6740\u4e86\u4f60\u7684\u7956\u7236\uff0c\u4e5f\u6740\u4e86\u4f60\u7684\u7236\u4eb2\uff0c\u56e0\u6b64 \u4f60\u628a\u6211\u5f53\u4f5c\u4e0d\u5171\u6234\u5929\u4e4b\u4ec7\u4eba\uff01\u662f\u4e0d\u662f\u8fd9\u6837\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8feb\u8fd1\u4e00\u6b65\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u6253\u7b97\u62d6\u5ef6\u65f6\u5019\uff0c\u53ef\u4ee5\u53eb\u6b66\u58eb\u8fdb\u6765\u5417\uff1f\u6211\u544a\u8bc9\u4f60\uff0c \u6211\u4e00\u4e3e\u624b\u5c31\u53ef\u4ee5\u6740\u4e86\u4f60\u3002\u201d\u6b66\u5219\u5929\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u5bb3\u6015\u5417\uff1f\u6211\u7ed9\u4f60\u51fa\u4e00\u4e2a\u4e3b\u610f\uff0c\u4f60\u628a \u8fd9\u4e24\u6247\u95e8\u90fd\u5173\u4e0a\uff0c\u6211\u6682\u65f6\u505a\u4f60\u7684\u72af\u4eba\uff0c\u8ba9\u4f60\u5ba1\u95ee\u5427\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u679c\u7136\u4f9d\u8a00\u628a\u4e24\u6247\u95e8\u5173\u4e0a\uff0c \u5728\u5173\u95e8\u7684\u65f6\u5019\uff0c\u773c\u775b\u4e00\u76f4\u4e0d\u79bb\u5f00\u6b66\u5219\u5929\uff0c\u6b66\u5219\u5929\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u9003\u8d70\u7684\uff0c\u6211\u7b49\u4f60\u6765\u95ee\u6211\u8fd9\u756a\u8bf4\u8bdd\uff0c\u5df1\u7b49\u4e86\u597d\u591a\u5e74\u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u5c31\u95ee\u4f60\uff0c\u6211\u7684\u7956\u7236\uff0c\u6211\u6df1\u77e5\u4ed6\u662f\u4e00\u4e2a\u6b63\u76f4\u7684\u4eba\uff0c\u8bd7\u4e5f\u505a \u5f97\u5f88\u597d\uff0c\u4f60\u4e3a\u4ec0\u4e48\u6740\u4e86\u4ed6\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4f60\u7956\u7236\u7684\u8bd7\u53e5\u5199\u5f97\u5f88\u7f8e\u4e3d\uff0c\u867d\u7136\u53ea\u662f\u541f\u98ce\u5f04\u6708\uff0c\u6ca1\u6709\u4ec0\u4e48\u771f\u5b9e\u7684\u611f\u60c5\uff0c\u4f46 \u5728\u540c\u4e00\u8f88\u7684\u8bd7\u4eba\u4e2d\uff0c\u4e5f\u7b97\u662f\u51fa\u8272\u5f53\u884c\u7684\u4e86\u3002\u81f3\u4e8e\u4ed6\u7684\u4e3a\u4eba\u561b\uff0c\u6211\u627f\u8ba4\u4ed6\u4e0d\u662f\u5c0f\u4eba\uff0c\u4f46\u5374 \u4e0d\u662f\u597d\u4eba\uff01\u201d\u4e0a\u5bab\u5a49\u513f\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u600e\u8bf4\uff1f\u65e2\u975e\u5c0f\u4eba\uff0c\u5c31\u662f\u541b\u5b50\uff0c\u53c8\u600e\u8bf4\u4ed6\u4e0d\u662f\u597d\u4eba\uff1f\u201d \u6b66\u5219\u5927\u7b11\u9053\uff1a\u201c\u597d\u574f\u7684\u6807\u51c6\u4e0d\u662f\u8fd9\u6837\u7b80\u5355\u7684\uff0c\u505a\u7684\u4e8b\u5bf9\u5927\u591a\u6570\u4eba\u6709\u597d\u5904\u90a3\u624d\u662f\u597d\u4eba\u3002\u4f60 \u77e5\u9053\u4f60\u7956\u7236\u505a\u4e86\u4e9b\u4ec0\u4e48\u4e8b\u60c5\u5417\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u50cf\u4ed6\u8fd9\u6837\u6b63\u76f4\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u505a\u51fa\u4ec0\u4e48 \u574f\u4e8b\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a \u3000\u3000\u201c\u662f\u7684\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u4ee5\u4e3a\u662f\u574f\u4e8b\uff0c\u4f46\u5374\u786e\u786e\u5b9e\u5b9e\u662f\u574f\u4e8b\u3002\u4ed6\u53cd\u5bf9\u6211\u7684\u65bd\u653f\uff0c\u4ed6\u8981\u631f \u5148\u5e1d\uff0c\u8981\u628a\u6211\u5e9f\u60bc\uff0c\u8fde\u8bcf\u4e66\u4e5f\u7531\u4ed6\u62df\u597d\u4e86\uff0c\u90a3\u662d\u4e66\u7684\u5e95\u7a3f\uff0c\u5c06\u6765\u6211\u53ef\u4ee5\u7ed9\u4f60\u770b\u3002\u4ed6\u6559\u5506 \u6211\u7684\u513f\u5b50\u53cd\u5bf9\u6211\uff0c\u751a\u81f3\u5728\u4e1c\u5bab\u57cb\u8ba4\u7532\u5175\u60f3\u6697\u6740\u627e\u3002\u8fd9\u4e9b\u51ed\u636e\uff0c\u5c06\u6765\u6211\u90fd\u53ef\u4ee5\u4ee5\u7ed9\u4f60\u770b\u3002 \u3000\u3000\u4ed6\u7ed3\u96c6\u515a\u7fbd\u53cd\u5bf9\u6211\uff0c\u8bf4\u6211\u662f\u2018\u672d\u9e21\u53f8\u6668\u2019\uff0c\u8bf4\u6211\u4e0d\u8be5\u7ba1\u7406\u671d\u653f\uff01 \u3000\u3000\u6211\u77e5\u9053\u4ed6\u4eec\u53cd\u5bf9\u6211\u7684\u771f\u6b63\u539f\u56e0\uff0c\u56e0\u662f\u6211\u7684\u65bd\u653f\u5bf9\u5929\u4e0b\u767e\u59d3\u6709\u597d\u5904\uff0c\u5bf9\u4ed6\u4eec\u6ca1\u6709\u597d\u5904\uff0c \u6211\u53d6\u6d88\u4e86\u4e00\u4e9b\u8d35\u65cf\u7684\u7279\u6743\uff0c\u6211\u53d8\u52a8\u7956\u5b97\u7684\u6210\u6cd5\uff0c\u6211\u5e76\u4e0d\u8ba4\u4e3a\u5929\u4e0b\u662f\u4e00\u5bb6\u4e00\u59d3\u7684\u79c1\u4ea7\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u513f\uff0c\u6b66\u5219\u5929\u9887\u4e3a\u6fc0\u52a8\uff0c\u58f0\u97f3\u9ad8\u4ea2\uff0c\u8bdd\u4f3c\u8fde\u73e0\u7684\u7206\u51fa\u6765\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u6211\u4e0d\u8be5\u7ba1 \u7406\u671d\u653f\uff0c\u4f46\u8001\u767e\u59d3\u6ca1\u6709\u53cd\u5bf9\u6211\uff0c\u6211\u5c31\u7ba1\u4e0b\u53bb\uff0c\u4e00\u7ba1\u5c31\u7ba1\u4e86\u4e8c\u5341\u591a\u5e74\uff0c\u6211\u4e0d\u6562\u8bf4\u6211\u7ba1\u5f97\u5f88 \u597d\uff0c\u4e5f\u4e0d\u89c1\u5f97\u6bd4\u4ed6\u4eec\u7537\u4eba\u5dee\u5427\uff1f\u4f60\u7684\u7956\u7236\u662f\u88ab\u7687\u5e1d\u517b\u5728\u5bab\u5ef7\u91cc\u7684\u8bd7\u4eba\uff0c\u8bd7\u4f5c\u5f97\u6ee1\u4e0d\u9519\uff0c \u773c\u5149\u5374\u592a\u72ed\u7a84\u4e86\u3002\u4ed6\u77e5\u9053\u8001\u767e\u59d3\u8fc7\u7684\u662f\u4ec0\u4e48\u65e5\u5b50\u5417\uff1f\u4ed6\u77e5\u9053\u8001\u767e\u59d3\u5728\u60f3\u4e9b\u4ec0\u4e48\u5417\uff1f\u4f60\u662f \u4ece\u5916\u9762\u7c73\u7684\uff0c\u4f60\u8bf4\u5427\uff0c\u5929\u4e0b\u4eba\u5728\u53cd\u5bf9\u6211\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u8336\u4ead\u4e3b\u4eba\u7684\u5f71\u5b50\uff0c\u5f20\u8001\u4e09\u7684\u5f71\u5b50\uff0c\u6893\u6f7c\u53bf\u57ce\u91cc\u90a3\u4e9b\u7236 \u8001\u7684\u5f71\u5b50\uff0c\u5979\u5728\u8def\u4e0a\u63a5\u89e6\u8fc7\u7684\u8bb8\u591a\u8001\u767e\u59d3\u7684\u5f71\u5b50\uff0c\u7eb7\u81f3\u53e0\u6765\uff0c\u8fd9\u4e0d\u662f\u5e7b\u5f71\uff0c\u8fd9\u4e9b\u90fd\u662f\u771f \u5b9e\u7684\u4eba\uff0c\u597d\u50cf\u5806\u6210\u4e86\u4e00\u5ea7\u5c71\u4f3c\u7684\u91cd\u91cd\u7684\u538b\u5728\u5979\u7684\u5fc3\u4e0a\u3002\u5979\u8033\u8fb9\u54cd\u8d77\u4e86\u8336\u4ead\u4e3b\u4eba\u548c\u5f20\u8001\u4e09 \u7684\u58f0\u97f3\uff1a\u201c\u6211\u4eec\u4f46\u613f\u5929\u540e\u965b\u4e0b\u591a\u6d3b\u51e0\u5e74\uff01\u201d \u3000\u3000\u6b66\u5219\u5929\u7684\u58f0\u97f3\u53c8\u54cd\u8d77\u6765\u4e86\uff1a\u201c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u7684\u51fa\u8eab\u662f\u5fae\u8d31\u7684\uff0c\u6211\u7684\u7236\u4eb2\u662f\u4e2a\u505a\u6728\u6750 \u751f\u610f\u7684\u5c0f\u5546\u4eba\uff0c\u6211\u505a\u8fc7\u5bab\u5973\uff0c\u505a\u8fc7\u5c3c\u59d1\uff0c\u505a\u8fc7\u7236\u5b50\u4e24\u4ee3\u7684\u59ec\u59be\uff0c\u4f60\u5fc3\u91cc\u5728\u9a82\u6211\u4e0d\u8981\u8138\u5427\uff1f \u4f60\u5fc3\u91cc\u5927\u7ea6\u5728\u8bf4\uff0c\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u65e9\u4e9b\u6b7b\u6389\uff1f\u4f46\u8fd9\u662f\u6211\u7684\u8fc7\u9519\u5417\uff1f\u51e0\u5343\u5e74\u6765\u5973\u4eba\u6240\u53d7\u7684\u51cc\u8fb1 \u8fd8\u4e0d\u591f\u5417\uff1b\u6211\u6b7b\u4e86\u6709\u4ec0\u4e48\u7528\uff1f\u6240\u4ee5\u6211\u504f\u504f\u4e0d\u6b7b\uff01\u6211\u628a\u6743\u67c4\u6293\u5230\u624b\u91cc\uff0c\u6211\u505a\u8d77\u4e2d\u56fd\u7684\u7b2c\u4e00 \u4e2a\u5973\u7687\u5e1d\u6765\uff01\u8d77\u521d\u6211\u662f\u60f3\u4e3a\u5929\u4e0b\u7684\u5973\u4eba\u5410\u4e00\u53e3\u6c14\uff0c\u6e10\u6e10\u6211\u89c9\u5f97\u8981\u6211\u7ed9\u4ed6\u4eec\u5410\u4e00\u53e3\u7684\u4e0d\u6b62 \u662f\u5973\u4eba\uff0c\u4e5f\u6709\u7537\u4eba\uff0c\u6240\u4ee5\u6211\u4e0d\u8bb8\u8c6a\u5f3a\u6b3a\u538b\u767e\u59d3\uff0c\u6211\u96f7\u5389\u98ce\u884c\u7684\u63a8\u884c\u5747\u7530\u5236\u5ea6\uff0c\u6211\u5f00\u79d1\u53d6 \u58eb\uff0c\u8ba9\u6709\u624d\u80fd\u7684\u4eba\u90fd\u6709\u505a\u5b98\u7684\u673a\u4f1a\uff0c\u4e0d\u50cf\u4ee5\u524d\u4e00\u6837\uff0c\u505a\u5b98\u7684\u4e13\u8bb2\u95e8\u7b2c\uff0c\u8981\u7531\u8d35\u65cf\u5305\u529e\u3002 \u6211\u51c6\u8bb8\u8001\u767e\u59d3\u8fdb\u4eac\u544a\u5bc6\uff0c\u5956\u52b1\u4ed6\u4eec\u653e\u8a00\u65e0\u5fcc\u3002\u6211\u505a\u5f97\u4e0d\u591f\u597d\uff0c\u4f46\u4f60\u80fd\u8bf4\u6211\u8fd9\u4e9b\u90fd\u505a\u9519\u4e86 \u5417\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e00\u7247\u7eb7\u4e71\uff0c\u5979\u77e5\u9053\u6b66\u5219\u5929\u8bf4\u7684\u90fd\u662f\u5b9e\u5728\u7684\u4e8b\u60c5\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u6b66\u5219\u5929\u4e5f\u6ca1\u6709\u505a \u9519\uff0c\u4f46\u5979\u5230\u5e95\u662f\u6740\u4e86\u81ea\u5df1\u7956\u7236\u548c\u7236\u4eb2\u7684\u4ec7\u4eba\uff0c\u8840\u6d77\u6df1\u4ec7\u96be\u9053\u5c31\u8fd9\u6837\u4f5c\u7b97\u4e86\u5417\uff1f\u7eb7\u4e71\u4e2d\u53ea \u542c\u5f97\u6b66\u5219\u5929\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u7956\u7236\u7684\u773c\u5149\u77ed\u5c0f\uff0c\u91ce\u5fc3\u5374\u592a\u5927\u4e86\u3002\u4f60\u7236\u4eb2\u662f\u4e2a\u7cca\u6d82\u866b\uff0c\u53ea\u77e5 \u9053\u611a\u5fe0\u611a\u5b5d\uff0c\u542c\u4f60\u7956\u7236\u7684\u8bdd\uff0c\u4ee5\u4e3a\u80fd\u5c06\u6211\u9664\u6389\uff0c\u5c31\u662f\u5510\u671d\u7684\u5927\u5fe0\u81e3\u3002\u6240\u4ee5\u4ed6\u7236\u5b50\u5408\u8c0b\u7c73 \u5bf9\u4ed8\u6211\u3002\u90a3\u65f6\u5019\u8fd8\u6709\u4e00\u4e2a\u5927\u81e3\u957f\u5b59\u65e0\u5fcc\u662f\u4ed6\u4eec\u7684\u4e3b\u5e05\uff0c\u4ed6\u4eec\u501f\u5321\u6276\u5510\u5ba4\u4e3a\u540d\uff0c\u5176\u5b9e\u662f\u60f3 \u628a\u5929\u4e0b\u5f04\u6210\u4ed6\u4eec\u7684\u5929\u4e0b\uff0c\u4e0d\u7ba1\u8001\u767e\u59d3\u7684\u6b7b\u6d3b\u3002\u6211\u4e0d\u80fd\u5bb9\u5fcd\u4ed6\u4eec\u8fd9\u6837\u505a\uff0c\u4e0d\u5f97\u4e0d\u6740\u6389\u4ed6\u4eec\u3002 \u73b0\u5728\u6211\u5df2\u8bb2\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5047\u5982\u4f60\u8fd8\u8ba4\u4e3a\u6211\u6740\u5f97\u4e0d\u5bf9\uff0c\u90a3\u4e48\u4f60\u5c31\u62fe\u8d77\u5315\u9996\uff0c\u63d2\u8fdb\u6211\u7684\u80f8\u819b \u5427\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5982\u540c\u50f5\u7acb\u7684\u77f3\u50cf\uff0c\u9762\u8272\u60e8\u767d\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u6b66\u5219\u5929\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u4e71\u5f97\u5f88\uff0c\u8fd8 \u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u662f\u5417\uff1f\u597d\uff0c\u6211\u518d\u628a\u4e00\u4e2a\u673a\u4f1a\u7ed9\u4f60\uff0c\u6211\u8bf7\u4f60\u7559\u4e0b\u6765\uff0c\u7559\u5728\u6211\u7684\u8eab\u8fb9\u4e0e\u6211\u4f5c\u4f34\uff0c \u6211\u8fd8\u8981\u9001\u4e00\u628a\u6700\u950b\u5229\u7684\u5315\u9996\u7ed9\u4f60\uff01\u201d\u8bf4\u7f62\u679c\u7136\u62bd\u51fa\u4e00\u628a\u7cbe\u5149\u95ea\u76ee\u5315\u9996\u51fa\u6765\uff01 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u84e6\u5730\u4e00\u6014\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u53ea\u89c1\u6b66\u5219\u5929\u795e\u91c7\u98de\u626c\uff0c\u63d0\u7740\u5315\u9996\u8bf4\u9053\uff1a\u201c\u4f60\u4eca\u5e74\u662f \u5341\u56db\u5c81\u5427\uff1f\u6211\u5728\u4f60\u8fd9\u4e2a\u5e74\u7eaa\u7684\u65f6\u5019\uff0c\u592a\u5b97\u7687\u5e1d\u53ec\u6211\u8fdb\u5bab\uff0c\u90a3\u65f6\u897f\u57df\u4e00\u4e2a\u56fd\u5bb6\u8fdb\u8d21\u6765\u4e00\u5339 \u5b9d\u9a6c\uff0c\u540d\u53eb\u72ee\u5b50\u9aa2\uff0c\u540d\u5b9e\u76f8\u7b26\uff0c\u5f53\u771f\u662f\u50cf\u72ee\u5b50\u4e00\u6837\u731b\u6076\uff0c\u8c01\u4e5f\u4e0d\u80fd\u9a91\u5b83\u3002\u6211\u8bf4\u6211\u80fd\u9a91\u5b83\uff0c \u4f46\u662f\u8981\u4e09\u4ef6\u4e1c\u897f\u3002\u592a\u5b97\u7687\u5e1d\u8bf4\u9053\uff1a\u201c\u6211\u6700\u597d\u7684\u52c7\u58eb\u90fd\u4e0d\u80fd\u9a91\u5b83\uff0c\u4f60\u5c45\u7136\u80fd\u9a91\u5b83\u5417\uff1f\u597d\u5427\uff0c \u6211\u5c31\u8ba9\u4f60\u5c1d\u8bd5\u4e00\u4e0b\uff0c\u4f60\u8981\u54ea\u4e09\u4ef6\u4e1c\u4e24\uff1f\u201d\u6211\u8bf4\uff0c\u6211\u8981\u4e00\u6761\u94c1\u97ad\uff0c\u4e00\u67c4\u94c1\u69cc\uff0c\u4e00\u652f\u5315\u9996\uff01 \u3000\u3000\u9a6c\u4e0d\u542c\u8bdd\uff0c\u627e\u5c31\u7528\u94c1\u97ad\u97ad\u5b83\uff0c\u518d\u4e0d\u542c\u6d3b\uff0c\u6211\u5c31\u7528\u94c1\u69cc\u69cc\u5b83\uff0c\u82e5\u8fd8\u4e0d\u670d\uff0c\u6211\u5c31\u7528\u5315\u9996 \u6740\u5b83\uff01\u5927\u5b97\u7687\u5e1d\u9053\uff1a\u2018\u8fd9\u662f\u4e00\u5339\u65e5\u884c\u5343\u91cc\u7684\u5b9d\u9a6c\uff0c\u6740\u4e86\u5b83\u4e0d\u53ef\u60dc\u5417\uff1f\u2019\u6211\u8bf4\uff1a\u2018\u82e5\u5b83\u59cb \u7ec8\u4e0d\u542c\u4eba\u9a91\uff0c\u65e5\u884c\u5343\u91cc\uff0c\u4ea6\u6709\u4f55\u7528\uff1f\u2019\u7ec8\u4e8e\u90a3\u5339\u9a6c\u7ed9\u6211\u964d\u4f0f\u4e86\uff0c\u4e0d\u5fc5\u5315\u9996\uff0c\u8fde\u94c1\u69cc\u4e5f\u7528 \u4e0d\u7740\uff0c\u4ec5\u4ec5\u52a8\u7528\u4e86\u94c1\u97ad\u3002\u4ece\u6b64\u592a\u5b97\u7687\u5e1d\u5c31\u5341\u5206\u559c\u6b22\u6211\uff0c\u8bf4\u6211\u7684\u6027\u683c\u50cf\u4ed6\u4e00\u6837\u3001\u53ea\u53ef\u60dc\u4e0d \u662f\u7537\u5b50\uff0c\u8981\u4e0d\u7136\u5c31\u662f\u53ef\u4ee5\u97ad\u82d4\u5929\u4e0b\u7684\u96c4\u624d\uff01\u5927\u5b97\u7687\u5e1d\u662f\u6211\u6700\u4f69\u670d\u7684\u7537\u5b50\uff0c\u4f46\u4ed6\u5927\u7ea6\u4e5f\u6599 \u4e0d\u5230\u6211\u4f1a\u505a\u4e86\u7687\u5e1d\u3002 \u3000\u3000\u201c\u592a\u5b97\u7687\u5e1d\u5c06\u90a3\u4e09\u4ef6\u4e1c\u897f\u8d50\u7ed9\u6211\uff0c\u94c1\u97ad\u6362\u4e86\u91d1\u97ad\uff0c\u521a\u624d\u6211\u5df2\u8d50\u7ed9\u72c4\u4ec1\u6770\u4e86\uff1a\u8fd9\u4e00\u67c4 \u5315\u9996\u5219\u8fd8\u662f\u539f\u6765\u7684\u90a3\u67c4\u5315\u9996\uff0c\u8fd9\u662f\u5929\u4e0b\u6700\u950b\u5229\u7684\u5315\u9996\uff0c\u73b0\u5728\u6211\u5c06\u6700\u950b\u5229\u7684\u5315\u9996\u8d50\u7ed9\u4e86\u4f60\uff0c \u4f60\u77e5\u9053\u6211\u7684\u7528\u610f\u5417\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u60f6\u60d1\u6781\u4e86\uff0c\u6014\u6014\u7684\u671b\u7740\u90a3\u67c4\u5315\u9996\u3002\u6b66\u5219\u5929\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u6211\u6b63 \u9700\u8981\u6709\u4e00\u67c4\u5315\u9996\u6765\u76d1\u7763\u6211\uff01\u4f60\u7559\u5728\u6211\u7684\u8eab\u8fb9\uff0c\u82e5\u7136\u4f60\u53d1\u89c9\u4e86\u6211\u53e3\u4e0d\u5bf9\u5fc3\uff0c\u505a\u9519\u4e86\u4e00\u4ef6\u5bf9 \u4e0d\u8d77\u767e\u59d3\u7684\u4e8b\u60c5\uff0c\u6740\u9519\u4e86\u4e00\u4e2a\u597d\u4eba\uff0c\u4f60\u9a6c\u4e0a\u53ef\u4ee5\u7528\u8fd9\u67c4\u5315\u9996\u5c06\u6211\u6740\u6389\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u5f26\u9707\u52a8\uff0c\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u8981\u7559\u4e00\u4e2a\u4ec7\u4eba\u7684\u5973\u513f\u5728\u4f60\u8eab\u8fb9\uff1f\u8eab\u4e0a\u5e26\u7740\u5929\u4e0b \u6700\u950b\u5229\u7684\u5315\u9996\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u56e0\u4e3a\u4f60\u628a\u6211\u5f53\u4f5c\u4ec7\u4eba\uff0c\u4f60\u624d\u662f\u6700\u9002\u5408\u7684\u76d1\u7763\u6211 \u7684\u4eba\uff01\u4f60\u5fc3\u91cc\u4e0d\u662f\u5f88\u4e71\u5417\uff1f\u6740\u6211\u8fd8\u662f\u4e0d\u6740\u6211\uff1f\u5927\u7ea6\u4f60\u4e00\u65f6\u8fd8\u59d4\u51b3\u4e0d\u4e0b\u3002\u6240\u4ee5\u6211\u7ed9\u4f60\u8fd9\u4e2a \u673a\u4f1a\uff0c\u8ba9\u4f60\u968f\u65f6\u53ef\u4ee5\u529f\u7528\u8fd9\u67c4\u5315\u9996\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5168\u8eab\u53d1\u70ed\uff0c\u773c\u6cea\u4e0d\u77e5\u4e0d\u89c9\u7684\u6ef4\u4e86\u51fa\u6765\uff0c\u63a5\u8fc7\u5315\u9996\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211 \u613f\u610f\u670d\u4f8d\u4f60\uff0c\u5230\u6211\u8877\u5fc3\u4f69\u670d\u4f60\u7684\u65f6\u5019\uff0c\u8fd9\u652f\u5315\u9996\u6211\u5c06\u7528\u6765\u5bf9\u4ed8\u4f60\u7684\u654c\u4eba\uff01\u6211\u4e0d\u60f3\u8bf4\u5047\u8bdd \u9a97\u4f60\uff0c\u73b0\u5728\u6211\u5bf9\u4f60\u7684\u4ec7\u6068\u8fd8\u6ca1\u6709\u6d88\u9664\uff0c\u6211\u5bf9\u4f60\u662f\u65e2\u4f69\u670d\u800c\u53c8\u4ec7\u89c6\u7684\uff01\u201d \u3000\u3000\u95e8\u5916\u6709\u811a\u6b65\u58f0\u54cd\uff0c\u5148\u5934\u90a3\u4e2a\u5bab\u5973\u6572\u95e8\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\uff0c\u5367\u5ba4\u6536\u62fe\u597d\u4e86\u3002\u965b\u4e0b\u4f60\u8fd8\u5728 \u548c\u8c01\u8bf4\u8bdd\u5440\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4f60\u628a\u90d1\u5341\u4e09\u5a18\u5524\u8fdb\u6765\u3002\u201d\u8f6c\u8fc7\u5934\u5bf9\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4f60\u4e0d\u53cd \u5bf9\u6211\u628a\u95e8\u6253\u5f00\u4e86\u5427\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u6536\u597d\u5315\u9996\uff0c\u81ea\u5df1\u53bb\u628a\u95e8\u6253\u5f00\uff0c\u4f46\u542c\u5f97\u73af\u4f69\u6447\u66f3\uff0c\u4e00\u4e2a\u7a7f \u7740\u5973\u5b98\u670d\u9970\u7684\u4e2d\u5e74\u5987\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u4e0a\u5b98\u5a49\u513f\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff01 \u3000\u3000\u6b66\u5219\u5929\u5fae\u7b11\u9053\uff1a\u201c\u5341\u4e09\u5a18\uff0c\u4f60\u77a7\u662f\u8c01\u6765\u4e86\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u559c\u6781\u800c\u4f4d\uff0c\u90a3\u5973\u5b98\u53eb\u4e86\u4e00\u58f0 \u201c\u513f\u554a\uff01\u201d\u4e00\u628a\u5c06\u5979\u6402\u5165\u6000\u91cc\u3002\u8fd9\u5973\u5b98\u6b63\u662f\u4e0a\u5b98\u5a49\u513f\u7684\u6bcb\u4eb2\uff0c\u5979\u672c\u6765\u59d3\u90d1\uff0c\u6392\u884c\u5341\u4e09\uff0c \u5165\u5bab\u4e4b\u540e\uff0c\u5b98\u4e2d\u90fd\u79f0\u5979\u505a\u90d1\u5341\u4e09\u6d6a\u3002 \u3000\u3000\u90d1\u5341\u4e09\u5ab2\u773c\u5149\u4e00\u77a5\uff0c\u770b\u89c1\u5730\u4e0a\u7684\u4e24\u67c4\u5315\u9996\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u662f\u600e\u6837\u6765 \u7684\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u6211\u662f\u6000\u7740\u5315\u9996\u6765\u7684\uff01\u201d\u90d1\u5341\u4e09\u5a18\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2026\u2026\u201d\u6b66\u5219 \u5929\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5979\u672c\u6765\u662f\u8981\u884c\u523a\u6211\u7684\uff0c\u73b0\u5728\u5979\u613f\u610f\u7559\u5728\u6211\u7684\u8eab\u8fb9\u4e86\u3002\u4f60\u5e94\u8be5\u4e3a\u6211\u6b22\u559c\uff0c \u6211\u6b63\u9700\u8981\u4e00\u4e2a\u6709\u624d\u80fd\u7684\u5973\u5b50\u5e2e\u52a9\u6211\uff0c\u66f4\u9700\u8981\u4e00\u67c4\u950b\u5229\u7684\u5315\u9996\u76d1\u7763\u6211\uff01\u201d \u3000\u3000\u90d1\u5341\u4e09\u5a18\u60ca\u9b42\u7a0d\u5b9a\uff0c\u8f7b\u8f7b\u66ff\u4e0a\u5b98\u5a49\u513f\u7406\u597d\u84ec\u4e71\u7684\u5934\u53d1\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u771f\u7cca\u6d82\uff0c\u5e78 \u597d\u8fd8\u6ca1\u6709\u505a\u51fa\u7cca\u6d82\u4e8b\u6765\u3002\u662f\u7684\uff0c\u5929\u540e\u965b\u4e0b\u66fe\u6740\u5382\u6211\u7684\u516c\u516c\uff0c\u6211\u7684\u4e08\u5927\uff0c\u6211\u4e5f\u66fe\u50cf\u4f60\u8fd9\u6837 \u6e56\u6d82\u7684\u3002\u7ecf\u8fc7\u4e86\u8fd9\u51e0\u5e74\uff0c\u6211\u6e10\u6e10\u660e\u767d\u8fc7\u6765\u4e86\uff0c\u5929\u540e\u6740\u4ed6\u4eec\u5e76\u4e0d\u662f\u4e3a\u4e86\u79c1\u4ec7\uff0c\u6211\u4eb2\u8eab\u611f\u53d7 \u5230\u5929\u540e\u7684\u4e3a\u516c\u5fd8\u79c1\uff0c\u6211\u80fd\u591f\u628a\u5979\u5f53\u4f5c\u4ec7\u4eba\u770b\u5f85\u5417\uff1f\u662f\u7684\uff0c\u6211\u5931\u6389\u4e86\u4e08\u592b\u662f\u5f88\u60b2\u75db\u7684\uff0c\u4f46 \u6211\u80fd\u57cb\u6028\u8c01\u5462\uff1f\u6211\u53ea\u80fd\u57cb\u6028\u6211\u7684\u4e08\u592b\u4e0d\u660e\u4e8b\u4f53\uff0c\u6211\u53ea\u80fd\u57cb\u6028\u6211\u81ea\u5df1\u7684\u7cca\u6d82\uff0c\u5f53\u65f6\u4e0d\u77e5\u9053 \u529d\u8c0f\u4e08\u592b\u3002\u513f\u554a\uff0c\u73b0\u5728\u6211\u53ea\u6709\u4f60\u4e00\u6761\u547d\u6839\u5b50\u4e86\uff0c\u6211\u53ef\u4e0d\u8bb8\u4f60\u50cf\u4f60\u7239\u7239\u4e00\u6837\uff0c\u7cca\u6d82\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u5988\u4f60\u522b\u8bf4\u5566\u3002\u4f60\u8ba9\u6211\u518d\u770b\u4e00\u4e9b\u65f6\u5019\uff0c\u662f\u975e\u9ed1\u767d\u6211\u76f8\u4fe1\u6211\u4f1a\u770b \u5f97\u6e05\u695a\u3002\u201d \u3000\u3000\u90d1\u5341\u4e09\u5a18\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u613f\u610f\u51b7\u9759\u7684\u770b\uff0c\u90a3\u4e48\u6211\u5c31\u653e\u5fc3\u4e86\uff0c\u6211\u5f53\u521d\u88ab\u5224\u5165\u5bab\u4e3a \u5974\uff0c\u5fc3\u4e2d\u5bf9\u5929\u540e\u75db\u6068\u5f97\u5f88\uff0c\u6ca1\u591a\u4e45\uff0c\u5929\u540e\u5c31\u628a\u90a3\u5224\u51b3\u6539\u4e86\u8fc7\u7c73\uff0c\u5979\u8bf4\u6709\u7f6a\u4e0d\u53ca\u59bb\u5ba4\uff0c\u5e94 \u8be5\u5c06\u8fc7\u53bb\u90a3\u79cd\u682a\u8fde\u5bb6\u5c5e\u7684\u6cd5\u4ee4\u6539\u6b63\u8fc7\u6765\uff0c\u5979\u5c06\u6211\u91ca\u653e\u4e86\uff0c\u95ee\u6211\u613f\u4e0d\u613f\u610f\u5728\u5bab\u4e2d\u6559\u5bab\u5973\u8bfb \u4e66\uff0c\u6211\u62b1\u7740\u548c\u4f60\u73b0\u5728\u4e00\u6837\u7684\u5fc3\u601d\uff0c\u6211\u8981\u770b\u770b\u5929\u540e\u7684\u4e3a\u4eba\uff0c\u6211\u5c31\u7559\u4e0b\u6765\u4e86\u3002\u6211\u4e0d\u662f\u4e3a\u4e86\u5929 \u540e\u5c01\u6211\u505a\u5973\u5b98\u6211\u5c31\u8bf4\u5979\u597d\uff0c\u6211\u662f\u786e\u786e\u5b9e\u5b9e\u770b\u5230\u5979\u4e3a\u767e\u59d3\u7740\u60f3\u7684\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u7b11\u9053\uff0c\u201c\u8fd9\u4e9b\u8bdd\u4f60\u7559\u5f85\u4ee5\u540e\u518d\u8bf4\u5427\u3002\u6700\u597d\u8ba9\u5979\u81ea\u5df1\u53bb\u591a\u770b\u591a\u60f3\u3002\u82e5\u6211\u662f\u4f60\uff0c \u6211\u4e00\u5b9a\u8981\u5148\u95ee\u5979\u8fd9\u51e0\u5e74\u7684\u60c5\u51b5\u3002\u5a49\u513f\u662f\u4e2a\u96be\u5f97\u7684\u5929\u624d\uff0c\u6211\u5f88\u62c5\u5fc3\u5979\u7ec3\u4e86\u6b66\u529f\uff0c\u53ef\u6709\u6ca1\u6709 \u5c06\u4e66\u8bd7\u4e22\u8352\u4e86\uff1f\u201d \u3000\u3000\u90d1\u5341\u4e09\u5a18\u9053\uff1a\u201c\u5929\u540e\u4f60\u771f\u4f53\u8d34\uff0c\u61c2\u5f97\u505a\u6bcd\u4eb2\u7684\u5fc3\u3002\u8fd9\u51e0\u5e74\u6765\uff0c\u6211\u771f\u662f\u5929\u5929\u5728\u6302\u5ff5\u7740 \u4f60\u3002\u4e0d\u77e5\u9053\u4f60\u5b66\u4e86\u4e9b\u4ec0\u4e48\uff0c\u662f\u5b66\u597d\u4e86\u8fd8\u662f\u5b66\u574f\u4e86\uff1f\u4f60\u5c0f\u65f6\u5019\u6700\u6b22\u559c\u4f5c\u8bd7\uff0c\u4ece\u4e94\u5c81\u8d77\u5c31\u61c2 \u5f97\u4f5c\u8bd7\u4e86\uff0c\u4f60\u73b0\u5728\u8fd8\u6709\u4f5c\u8bd7\u5417\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u6211\u8ddf\u7740\u957f\u5b59\u4f2f\u4f2f\uff0c\u65e5\u95f4\u5b66\u6b66\uff0c\u665a\u4e0a\u4e60\u6587\uff0c\u8bd7\u8fd8\u662f\u5e38\u4f5c\u7684\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u9053\uff1a\u201c\u554a\uff0c\u4f60\u7684\u5e05\u7236\u662f\u957f\u5b59\u5747\u91cf\u5417\uff1f\u4ed6\u7684\u6587\u624d\u6b66\u827a\u90fd\u5f88\u51fa\u8272\u5f53\u884c\uff0c\u4f60\u8ddf\u4ed6\u5b66\uff0c \u6211\u5c31\u5f88\u653e\u5fc3\u4e86\u3002\u524d\u4e9b\u65f6\u5019\uff0c\u6211\u8fd8\u60f3\u6d3e\u90d1\u6e29\u53bb\u8bf7\u4ed6\u51fa\u5c71\u5462\u3002\u53ea\u6015\u4ed6\u5e74\u7eaa\u5927\u4e86\uff0c\u8111\u7b4b\u4e00\u65f6\u4e0d \u5bb9\u6613\u6539\u53d8\u8fc7\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e00\u9635\u96be\u8fc7\uff0c\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u957f\u5b59\u4f2f\u4f2f\u8981\u662f\u77e5\u9053\u6211\u8fdd\u80cc\u4e86\u4ed6\u7684\u671f\u671b\uff0c\u4ed6\u4f1a\u600e\u6837 \u5462\uff1f\u201d \u3000\u3000\u6b66\u5219\u5929\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u603b\u4e0d\u80fd\u6574\u5929\u4f3c\u7ef7\u7d27\u7684\u5f13\u5f26\uff0c\u6211\u5c31\u6709\u8fd9\u4e2a\u6bdb\u75c5\uff0c\u540e\u6765\u592a\u5b97\u7687\u5e1d \u6559\u4e86\u6211\u4e00\u4e2a\u6cd5\u5b50\uff0c\u6bcf\u5230\u5fc3\u91cc\u70e6\u4e71\u7684\u65f6\u5019\uff0c\u5c31\u627e\u4e00\u4e9b\u81ea\u5df1\u6b22\u559c\u7684\u4e8b\u60c5\u6765\u505a\uff0c\u4f7f\u5f97\u5fc3\u60c5\u5b81\u9759 \u4e0b\u53bb\u3002\u5a49\u513f\uff0c\u6211\u5012\u5f88\u60f3\u89c1\u8bc6\u89c1\u8bc6\u4f60\u7684\u8bd7\u624d\u5462\u3002\u201d \u3000\u3000\u90d1\u5341\u4e09\u5a18\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\uff0c\u4f60\u7ed9\u5979\u51fa\u4e00\u4e2a\u9898\u76ee\u5427\u3002\u201d\u6b66\u5219\u5929\u6307\u7740\u6848\u5934\u4e0a\u7684\u7eb8\u82b1\u8bf4\u9053\uff1a \u201c\u4f60\u5c31\u4ee5\u2018\u526a\u5f69\u82b1\u2019\u4e3a\u9898\u4f5c\u4e00\u9996\u4e94\u5f8b\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5b9a\u4e86\u4e00\u4e0b\u5fc3\u795e\uff0c\u770b\u4e86\u5979\u7684\u6bcd\u4eb2\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5988\uff0c\u4f60\u8981\u6211\u4f5c\u8bd7\uff0c\u6211\u7684\u8bd7\u4e5f\u4f5c \u597d\u4e86\uff0c\u8bf4\u9519\u4e86\u8bdd\uff0c\u4f60\u53ef\u522b\u602a\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a \u3000\u3000\u201c\u4f5c\u8bd7\u672c\u6765\u5c31\u662f\u8981\u8bf4\u771f\u8bdd\uff0c\u6ca1\u4eba\u4f1a\u602a\u4f60\u7684\uff0c\u4f60\u5ff5\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u66fc\u58f0\u541f\u9053 \u3000\u3000\u5bc6\u53f6\u56e0\u683d\u5410\uff0c\u65b0\u82b1\u9010\u526a\u8212\u3002 \u3000\u3000\u6500\u6761\u867d\u4e0d\u8c2c\uff0c\u6458\u854a\u8bb5\u77e5\u865a\u3002 \u3000\u3000\u6b66\u5219\u5929\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u5bf9\u5f97\u5de5\u5de7\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u7ee7\u7eed\u5ff5\u9053\uff1a \u3000\u3000\u6625\u81f3\u7531\u6765\u53d1\uff0c\u79cb\u8fd8\u672a\u80af\u758f\u3002 \u3000\u3000\u501f\u95ee\u6843\u5c06\u674e\uff0c\u76f8\u4e71\u6b32\u4f55\u5982\uff1f \u3000\u3000\u90d1\u5341\u4e09\u5a18\u9762\u8272\u500f\u53d8\uff0c\u6b66\u5219\u5929\u5fae\u7b11\u9053\uff1a\u201c\u501f\u95ee\u6843\u5c06\u674e\uff0c\u76f8\u4e71\u6b32\u4f55\u5982\uff1f\u8fd9\u610f\u601d\u662f\u2014\u2014\u201d \u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u5047\u7684\u82b1\u5047\u5f97\u592a\u5de7\u5999\u4e86\uff0c\u53ef\u4ee5\u4ee5\u5047\u4e71\u771f\uff01\u201d\u6b66\u5219\u5929\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u61c2\u5f97\u4f60 \u771f\u6b63\u7684\u610f\u601d\uff0c\u4f60\u8fd8\u5728\u6000\u7591\u770b\u6211\uff0c\u4e0d\u8fc7\u6211\u5012\u56e0\u6b64\u5f88\u559c\u6b22\u4f60\uff0c\u4f60\u5f88\u7eaf\u771f\uff0c\u4e0d\u4f1a\u505a\u4f5c\uff0c\u5fc3\u60f3\u4ec0 \u4e48\u4fbf\u8bf4\u4ec0\u4e48\u3002\u597d\u5427\uff0c\u662f\u771f\u662f\u5047\u603b\u4f1a\u5206\u660e\u7684\u3002\u201d\u4e0a\u5bab\u5a49\u513f\u8fd9\u9996\u8bd7\u8bbd\u523a\u6b66\u5219\u5929\u4e0d\u662f\u771f\u547d\u5929\u5b50\uff0c \u53ea\u6015\u662f\u6a61\u5f69\u82b1\u4e00\u6837\uff0c\u4ee5\u5047\u4e71\u771f\u3002\u8fd8\u7279\u522b\u5d4c\u5165\u5510\u671d\u826f\u5e1d\u7684\u59d3\u6c0f\uff0c\u8bf4\u5979\u4e71\u4e86\u5510\u5ba4\uff0c\u90d1\u5341\u4e09\u5a18 \u634f\u4e00\u628a\u6c57\uff0c\u89c1\u6b66\u5219\u5929\u6beb\u4e0d\u8d23\u602a\uff0c\u8fd9\u624d\u5b89\u5fc3\u3002 \u3000\u3000\u5bab\u5973\u53c8\u6765\u50ac\u6b66\u5219\u5929\u5b89\u6b47\uff0c\u6b66\u5219\u5929\u9053\uff1a\u201c\u65f6\u5019\u4e0d\u65e9\uff0c\u5927\u5bb6\u90fd\u5e94\u8be5\u7761\u4e86\u3002\u5341\u4e09\u5a18\uff0c\u4f60\u8eab \u4f53\u4e0d\u5927\u597d\uff0c\u4ee5\u540e\u4f60\u4e24\u6bcd\u5973\u53ef\u4ee5\u65f6\u523b\u4e0d\u79bb\uff0c\u6211\u77e5\u9053\u4f60\u6709\u8c08\u4e0d\u5b8c\u7684\u8bdd\uff0c\u4f46\u4eca\u665a\u4e0d\u8981\u8c08\u4e86\u3002\u5982 \u610f\uff0c\u5a49\u513f\u7684\u623f\u95f4\u4f60\u7ed9\u5979\u5b89\u6392\u597d\u4e86\u5417\uff1f\u201d\u6b66\u7384\u971c\u90a3\u5c0f\u4e2b\u73af\u8fdb\u6765\u8bf4\u9053\uff1a\u201c\u5a49\u513f\u59d0\u59d0\uff0c\u6211\u5e26\u4f60 \u53bb\u7761\u3002\u201d \u3000\u3000\u5982\u610f\u5e26\u5979\u8fdb\u5165\u4e00\u95f4\u7cbe\u81f4\u7684\u623f\u95f4\uff0c\u5e8a\u94fa\u65e9\u5df2\u6536\u62fe\u5f97\u9f50\u9f50\u6574\u6574\u3002 \u3000\u3000\u5982\u610f\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u884c\u523a\u5929\u540e\u7684\uff0c\u6240\u4ee5\u6211\u5f88\u653e\u5fc3\u3002\u6211\u89c1\u4f60\u4ece\u5c4b\u4e0a\u8df3\u4e0b\u6765\uff0c\u6211 \u4fbf\u77e5\u9053\u4f60\u4f1a\u7559\u4e0b\u6765\u4e86\u3002\u56e0\u6b64\u6211\u4fbf\u4e0d\u518d\u7406\u4f60\uff0c\u5f84\u76f4\u6765\u7ed9\u4f60\u6536\u62fe\u5367\u623f\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u4e00\u51db\uff0c \u8fd9\u624d\u77e5\u9053\u5982\u610f\u4e00\u76f4\u5728\u76d1\u89c6\u7740\u5979\u3002\u5982\u610f\u53c8\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5c0f\u59d0\u7559\u7ed9\u4f60\u7684\u8bd7\uff0c\u4eca\u5929\u662f\u5e94\u9a8c\u4e86\u3002 \u5c0f\u59d0\u4e5f\u65e9\u77e5\u9053\u4f60\u548c\u5979\u6700\u540e\u7ec8\u4f1a\u5728\u4e00\u8d77\u7684\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u60f3\u5ff5\u4f60\u7684\u5c0f\u59d0\u3002\u201d\u5982 \u610f\u9053\uff1a\u201c\u5c0f\u59d0\u8ffd\u674e\u9038\u53bb\u4e86\uff0c\u674e\u9038\u90a3\u5929\u5bf9\u4f60\u4e5f\u5f88\u5173\u5fc3\u554a\uff0c\u4f60\u4e0d\u60f3\u5ff5\u4ed6\u5417\uff1f\u201d\u8bf4\u7f62\uff0c\u4f4e\u7709\u4e00 \u7b11\uff0c\u63ed\u5e18\u800c\u51fa\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5fc3\u5934\u6f6e\u6d8c\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u54ea\u91cc\u7761\u5f97\u7740\u3002\u5979\u4ece\u6b66\u7384\u971c\u60f3\u5230\u4e86\u674e\u9038\uff0c\u7761\u773c\u6726\u80e7\u4e2d \u5e7b\u51fa\u674e\u9038\u7684\u5f71\u5b50\uff0c\u4ed6\u6b63\u5728\u8352\u5c71\u4e0a\u7ed9\u6b66\u7384\u971c\u8ffd\u9010\u3002\u674e\u9038\u5c06\u6765\u4f1a\u600e\u4e48\u6837\u5462\uff1f\u4ed6\u4f1a\u4e0d\u4f1a\u602a\u81ea\u5df1 \u505a\u6b66\u5219\u5929\u7684\u5f85\u5973\u5462\uff0c\u5982\u4eca\u5404\u8d70\u4e00\u65b9\uff0c\u8fd9\u4e00\u751f\u8fd8\u4f1a\u4e0d\u4f1a\u53ef\u89c1\u9762\u5462\uff1f\u4e0a\u5b98\u5a49\u513f\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c \u4ece\u7a97\u53e3\u671b\u5c71\u53bb\uff0c\u5929\u8272\u5df1\u6e10\u6e10\u53d1\u767d\u4e86\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u738b\u5b59\u5931\u610f\u9047\u9b54\u5934 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u516b\u56de\u3000\u738b\u5b59\u5931\u610f\u9047\u9b54\u5934 \u3000\u3000\u50cf\u5a49\u513f\u4e00\u6837\uff0c\u674e\u9038\u4e5f\u6b63\u9677\u5728\u6069\u4ec7\u60d8\u60d8\uff0c\u96be\u4ee5\u81ea\u62d4\u4e4b\u4e2d\u3002\u90a3\u65e5\u4ed6\u76ee\u7779\u82f1\u96c4\u5927\u4f1a\u51b0\u6d88\u74e6 \u89e3\uff0c\u4f24\u5fc3\u5931\u610f\uff0c\u5230\u4e86\u6781\u70b9\uff0c\u4e0d\u5f85\u7ec8\u573a\uff0c\u4fbf\u98d8\u7136\u8fdc\u5f15\uff0c\u72ec\u4e0a\u5cf0\u5dc5\u3002\u5cf0\u4e0b\u53ae\u6740\u4e4b\u58f0\uff0c\u6e10\u8fdc\u6e10 \u5bc2\uff0c\u8033\u8fb9\u4f46\u542c\u5f97\u677e\u98ce\u9e1f\u8bed\uff0c\u6d41\u6cc9\u8702\u743c\uff0c\u4e00\u7247\u5929\u7c41\uff0c\u4ee3\u66ff\u4e86\u91d1\u6208\u6740\u4f10\u4e4b\u58f0\u3002\u62ac\u5934\u671b\u53bb\uff0c\u5c71 \u5ce6\u5c42\u663e\uff0c\u96fe\u853c\u8ff7\u8499\uff0c\u4f46\u90a3\u65e5\u8f6e\u7ea2\u5f71\uff0c\u5374\u5df2\u5728\u6d53\u96fe\u4e4b\u4e2d\u900f\u9732\u51fa\u6765\u3002\u9ed1\u591c\u5c06\u901d\uff0c\u5929\u6b63\u9ece\u660e\uff0c \u674e\u9038\u8fce\u7740\u6653\u98ce\uff0c\u5401\u4e86\u53e3\u6c14\uff0c\u604d\u5982\u505a\u4e86\u4e00\u573a\u5669\u68a6\uff0c\u68a6\u91cc\u9192\u6765\uff0c\u70ed\u95f9\u7e41\u534e\uff0c\u65e9\u5df2\u662f\u98ce\u6d41\u4e91\u6563\u3002 \u5c71\u4e2d\u666f\u8272\uff0c\u5e7d\u7f8e\u4e4b\u6781\uff0c\u674e\u9038\u5fc3\u5934\uff0c\u5374\u662f\u7eb7\u4e71\u4e00\u7247\uff0c\u6b8a\u4e0d\u5b81\u9759\u3002\u60f3\u8d77\u81ea\u5df1\u7684\u58ee\u5fd7\u96c4\u56fe\uff0c\u5316 \u6210\u7070\u70ec\uff0c\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\uff0c\u8e48\u8e48\u72ec\u884c\uff0c\u6084\u7136\u541f\u9053\uff1a\u201c\u94c1\u9a6c\u91d1\u6208\u6000\u6545\u56fd\uff0c\u98d8\u96f6\u7434 \u5251\u53c8\u5929\u6daf\uff01\u201d \u3000\u3000\u6668\u98ce\u4e2d\u5ffd\u7136\u9001\u6765\u4e86\u94f6\u94c3\u822c\u7684\u7b11\u58f0\uff0c\u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u767d\u8863\u5c11\u5973\uff0c \u8863\u8882\u98d8\u98d8\uff0c\u4ece\u540e\u9762\u7684\u5c71\u52a9\u95ea\u51fa\uff0c\u6b63\u662f\u6628\u591c\u74e6\u89e3\u4e86\u82f1\u96c4\u5927\u4f1a\u7684\u90a3\u4e2a\u6b66\u7384\u971c\u3002\u53ea\u542c\u5f97\u5979\u683c\u683c \u7b11\u9053\uff1a\u201c\u5927\u82f1\u96c4\uff0c\u65b0\u76df\u4e3b\uff0c\u4f60\u8d70\u5f97\u592a\u5306\u5fd9\u5566\uff01\u201d\u674e\u9038\u6309\u5251\u6012\u9053\uff1a\u201c\u58eb\u53ef\u6740\u4e0d\u53ef\u8fb1\uff0c\u6709\u672c \u4e8b\u4f60\u5c31\u6765\u5c06\u6211\u6740\u4e86\uff0c\u6211\u62fc\u7740\u5251\u65ad\u4eba\u4ea1\uff0c\u51b3\u4e0d\u53d7\u4f60\u6b3a\u4fae\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u6251\u54e7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u597d\u5fc3\u7ed9\u4f60\u9001\u4e1c\u897f\u6765\u5566\uff0c\u8c01\u6b3a\u8d1f\u4f60\uff1f\u201d\u674e\u9038\u4e00\u770b\uff0c\u53ea\u89c1 \u5979\u624b\u4e2d\u6367\u7740\u4e00\u5177\u53e4\u7434\uff0c\u90a3\u6b63\u662f\u4ed6\u968f\u8eab\u80cc\u7740\u7684\u4e1c\u897f\uff0c\u60f3\u662f\u6628\u591c\u6df7\u6218\u4e4b\u65f6\uff0c\u5931\u843d\u5728\u6218\u573a\u4e0a\u7684\u3002 \u6b66\u7384\u971c\u7b11\u9053\uff1a \u3000\u3000\u201c\u5feb\u62ff\u53bb\u5427\uff0c\u8981\u4e0d\u7136\u6709\u5251\u65e0\u7434\uff0c\u4f60\u7684\u8bd7\u4e5f\u4e0d\u5e94\u666f\u5566\u3002\u201d \u3000\u3000\u674e\u9038\u9762\u7ea2\u8033\u70ed\uff0c\u53ea\u89c1\u6b66\u7384\u971c\u7709\u773c\u76c8\u76c8\uff0c\u5bf9\u4ed6\u7adf\u4f3c\u6beb\u65e0\u654c\u610f\uff0c\u674e\u9038\u7684\u813e\u6c14\u4e5f\u53d1\u4e0d\u8d77\u6765\u3002 \u4f46\u4ed6\u6628\u591c\u8d25\u5728\u6b66\u7384\u971c\u624b\u4e0b\uff0c\u5982\u4eca\u5374\u53c8\u600e\u597d\u5728\u5979\u624b\u4e2d\u63a5\u7434\uff0c\u9976\u662f\u674e\u9038\u4e00\u5411\u6f47\u6d12\uff0c\u8fd9\u65f6\u4e5f\u4e2a \u7981\u9732\u51fa\u7a98\u6001\u3002 \u3000\u3000\u6b66\u7384\u971c\u5c06\u53e4\u7434\u4e00\u629b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u5728\u7aef\u7740\u76df\u4e3b\u7684\u67b6\u5b50\u4e48\uff1f\u8fd9\u6837\u7684\u82f1\u96c4\u5927\u4f1a\uff0c\u8fd9\u6837\u7684 \u76df\u4e3b\uff0c\u4e0d\u505a\u4e5f\u7f62\u3002\u8fd9\u53e4\u7434\u5012\u662f\u96be\u5f97\u4e4b\u7269\uff0c\u6211\u529d\u4f60\u5b81\u5f03\u76df\u4ea1\uff0c\u83ab\u5f03\u6b64\u7434\uff01\u201d\u674e\u9038\u4e0d\u7531\u81ea\u4e3b \u7684\u63a5\u8fc7\u4e86\u53e4\u7434\uff0c\u201c\u591a\u8c22\u201d\u8fd9\u4e24\u4e2a\u5b57\u5728\u820c\u5c16\u6253\u6eda\u4e86\u65e0\u6570\u904d\uff0c\u8fd8\u672a\u8bf4\u5f97\u51fa\u6765\uff0c\u7b11\u58f0\u98d8\u8361\uff0c\u6b66 \u7384\u971c\u65e9\u5df1\u8d70\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u674e\u9038\u4e0d\u81ea\u7981\u7684\u76ee\u9001\u5979\u7684\u80cc\u5f71\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e16\u9053\u5927\u53d8\uff0c\u5973\u5b50\u79f0\u738b\uff0c\u671d\u4e0a\u6709\u6b66\u5219\u5929\u505a \u7687\u5e1d\uff0c\u6b66\u6797\u4e2d\u96be\u9053\u4e5f\u8981\u7518\u8ba9\u5a25\u7709\uff1f\u201d\u4ed6\u5fc3\u4e2d\u5c3d\u7ba1\u4e0d\u670d\uff0c\u4f46\u60f3\u8d77\u81ea\u5df1\u6240\u7ed3\u8bc6\u7684\u4e00\u73ed\u201c\u82f1\u96c4\u201d \u82e5\u8981\u6bd4\u8d77\u6b66\u7384\u971c\u6765\uff0c\u5374\u786e\u5b9e\u662f\u6709\u5982\u5c18\u571f\u4e4b\u6bd4\u660e\u73e0\u3002\u60f3\u81f3\u6b64\u5904\uff0c\u674e\u9038\u5fc3\u4e2d\u4e0d\u7981\u4e00\u8361\u3002 \u3000\u3000\u84e6\u7136\u95f4\u4e0a\u5b98\u5a49\u513f\u7684\u5f71\u5b50\u63a5\u7740\u6cdb\u4e0a\u5fc3\u5934\uff0c\u674e\u9038\u597d\u50cf\u6eba\u6c34\u7684\u4eba\u6293\u7740\u82a6\u82c7\u4e00\u6837\uff0c\u6293\u7740\u4e0a\u5b98 \u5a49\u513f\u7684\u5e7b\u5f71\uff0c\u4e00\u4e2a\u662f\u6e29\u67d4\u89e3\u4e8b\u7684\u5973\u4e2d\u624d\u5b50\uff0c\u4e00\u4e2a\u662f\u82f1\u59ff\u98d2\u723d\u7684\u5dfe\u5e3c\u82f1\u96c4\uff0c\u653e\u5728\u4e00\u8d77\uff0c\u786e \u662f\u4e00\u65f6\u745c\u4eae\uff0c\u96be\u5206\u9ad8\u4e0b\uff0c\u674e\u9038\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4eba\u751f\u5f97\u4e00\u77e5\u5df1\uff0c\u6b7b\u4ea6\u65e0\u61be\u3002\u5a49\u513f\u662f\u6211\u7684\u77e5\u5df1\uff0c \u5979\u5374\u662f\u6211\u7684\u4ec7\u4eba\uff01\u201d\u7ec8\u4e8e\u662f\u4e0a\u5b98\u5a49\u513f\u7684\u5f71\u5b50\u5c06\u6b66\u7384\u971c\u538b\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5bf9\u4e0a\u5b98\u5a49\u513f\u7684\u6000\u5ff5\u66f4\u52a0\u91cd\u4e86\u4ed6\u7684\u70e6\u5fe7\uff0c\u201c\u5a49\u513f\uff0c\u5979\u73b0\u5728\u600e\u4e48\u6837\u4e86\uff1f\u5979\u843d\u5165\u4e86\u8c01\u4eba\u7684 \u624b\u4e2d\uff1f\u201d\u4ed6\u56de\u5fc6\u8d77\u6628\u665a\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u4e0a\u5b98\u5a49\u513f\u7a81\u7136\u51fa\u73b0\uff0c\u96c4\u5de8\u9f0e\u53bb\u88ad\u51fb\u5979\uff0c\u96c4\u5de8\u9f0e\u662f\u4e2a \u83bd\u592b\uff0c\u4ed6\u5bf9\u81ea\u5df1\u5fe0\u8bda\uff0c\u4ed6\u4e0d\u77e5\u9053\u5a49\u513f\u7684\u6765\u5386\uff0c\u4ed6\u5927\u7ea6\u662f\u4e3a\u4e86\u62a4\u536b\u81ea\u5df1\u624d\u53bb\u88ad\u51fb\u5979\u7684\u3002\u8fd9 \u4e0d\u5947\u602a\uff0c\u5947\u602a\u7684\u662f\u90a3\u4e2a\u5c0f\u4e2b\u73af\u4e3a\u4ec0\u4e48\u5c06\u4e0a\u5b98\u5a49\u513f\u6551\u4e86\uff1f\u96be\u9053\u5a49\u513f\u548c\u6b66\u7384\u971c\u662f\u76f8\u8bc6\u7684\u5417\uff1f \u5728\u674e\u9038\u7684\u5fc3\u4e2d\uff0c\u6b66\u7384\u971c\u7684\u5f71\u5b50\u672c\u6765\u5df2\u7ecf\u7ed9\u4e0a\u5b98\u5a49\u513f\u538b\u4e0b\u53bb\u4e86\uff0c\u53ef\u662f\u7531\u4e8e\u4e0a\u5b98\u5a49\u513f\uff0c\u5374\u53c8 \u4e0d\u80fd\u4e0d\u4ee4\u4ed6\u60f3\u8d77\u6b66\u7384\u971c\u6765\u3002\u674e\u9038\u867d\u7136\u4e0d\u77e5\u9053\u6b66\u7384\u971c\u7684\u8eab\u4efd\uff0c\u4f46\u6b66\u7384\u971c\u6363\u6bc1\u4e86\u82f1\u96c4\u5927\u4f1a\uff0c \u660e\u663e\u662f\u62e5\u62a4\u6b66\u5219\u5929\u7684\u4eba\u3002\u674e\u9038\u60f3\u9053\uff1a\u201c\u82e5\u7136\u5979\u77e5\u9053\u5a49\u513f\u662f\u4e0a\u5b98\u4eea\u7684\u5b59\u5973\uff0c\u5979\u4f1a\u600e\u6837\u5f85\u5979\uff1f \u4f1a\u4e0d\u4f1a\u5c06\u5a49\u513f\u62ff\u53bb\u732e\u7ed9\u6b66\u5219\u5929\u5462\uff1f\u201d\u6b66\u7384\u971c\u770b\u6765\u4e0d\u4f3c\u662f\u72e0\u6bd2\u7684\u4eba\uff0c\u4f46\u4e0a\u5b98\u5a49\u513f\u843d\u5728\u5979\u7684 \u624b\u4e2d\uff0c\u603b\u662f\u6559\u674e\u9038\u653e\u5fc3\u4e0d\u4e0b\u3002 \u3000\u3000\u60f3\u8d77\u4e86\u4e0a\u5b98\u5a49\u513f\u548c\u81ea\u5df1\u540c\u4e00\u7684\u547d\u8fd0\uff0c\u674e\u9038\u7684\u6ee1\u8154\u6028\u6124\u90fd\u53d1\u6cc4\u5728\u6b66\u5219\u5929\u8eab\u4e0a\uff0c\u662f\u6b66\u5219 \u5929\u4ee4\u5f97\u4ed6\u4eec\u5bb6\u6563\u4eba\u4ea1\uff0c\u662f\u6b66\u5219\u5929\u4ee4\u5f97\u4ed6\u4eec\u98d8\u96f6\u6e56\u6d77\uff0c\u5374\u504f\u504f\u6709\u8fd9\u4e48\u591a\u6709\u80fd\u4e3a\u7684\u4eba\u53bb\u62e5\u62a4 \u5979\uff01\u201c\u4f24\u5fc3\u5b87\u5185\u82f1\u8c6a\u5c3d\u5f52\u65b0\u4e3b\uff0c\u5fcd\u4eca\u5929\u4eac\u795e\u5668\u7adf\u5c5e\u4ed6\u5bb6\uff1f\u201d\u674e\u9038\u4e00\u8154\u90c1\u95f7\uff0c\u96be\u4ee5\u6392\u6e32\uff0c \u6367\u8d77\u53e4\u7434\uff0c\u4fbf\u5728\u68ee\u6797\u5185\u7684\u5c71\u6da7\u65c1\u8fb9\uff0c\u9009\u4e86\u4e00\u5757\u5e73\u6ed1\u7684\u77f3\u5934\uff0c\u6743\u4f5c\u7434\u53f0\uff0c\u7406\u597d\u7434\u5f26\uff0c\u4e34\u6d41 \u5f39\u594f\u3002 \u3000\u3000\u4ed6\u5f39\u7684\u662f\u8bd7\u7ecf\u4e2d\u300a\u9ecd\u79bb\u300b\u90a3\u4e00\u7bc7\uff0c\u968f\u7740\u6c89\u90c1\u7684\u7434\u97f3\u653e\u58f0\u6b4c\u9053\uff0c\u201c\u5f7c\u9ecd\u79bb\u79bb\uff0c\u5f7c\u7a37\u4e4b \u82d7\u3002\u884c\u8fc8\u9761\u9761\uff0c\u4e2d\u5fc3\u9065\u9065\u3002\u77e5\u6211\u8005\u8c13\u6211\u5fc3\u5fe7\uff0c\u4e0d\u77e5\u6211\u8005\u8c13\u6211\u4f55\u6c42\u3002\u60a0\u60a0\u82cd\u5929\uff0c\u6b64\u4f55\u4eba\u54c9\uff1f\u201d \u3000\u3000\u8bd7\u7ecf\u8fd9\u7bc7\u201c\u9ecd\u79bb\u201d\uff0c\u8bf4\u7684\u662f\u5468\u5ba4\u4e1c\u8fc1\u4e4b\u540e\uff0c\u5927\u592b\u884c\u5f79\uff0c\u7ecf\u8fc7\u65e7\u65e5\u4eac\u90fd\uff0c\u89c1\u5bab\u5e99\u5b97\u5ba4\uff0c \u5c3d\u4e3a\u79be\u9ecd\uff0c\u60af\u5468\u5ba4\u4e4b\u98a0\u8986\uff0c\u59a8\u60f6\u4e0d\u5fcd\u53bb\uff0c\u800c\u4f5c\u662f\u8bd7\u3002\u82e5\u8bd1\u6210\u767d\u8bdd\uff0c\u610f\u601d\u4fbf\u662f\uff1a\u201c\u9ecd\u5b50\u9f50 \u9f50\u6574\u6574\uff0c\u9ad8\u6881\u4e00\u7247\u65b0\u82d7\u3002\u6b65\u513f\u6162\u8c29\u817e\u817e\uff0c\u5fc3\u513f\u6643\u6643\u6447\u6447\u3002\u77e5\u9053\u6211\u7684\u8bf4\u6211\u5fc3\u70e6\u607c\uff0c\u4e0d\u77e5\u9053 \u6211\u7684\u95ee\u6211\u628a\u8c01\u627e\u3002\u82cd\u5929\u82cd\u5929\u4f60\u5728\u4e0a\u554a\uff01\u662f\u8c01\u5bb3\u5f97\u6211\u8fd9\u4e2a\u6837\u554a\uff1f\u201d\uff08\u7528\u4f59\u51a0\u82f1\u8bd1\u53e5\uff09 \u3000\u3000\u674e\u9038\u5fc3\u4e2d\u5145\u6ee1\u6545\u56fd\u4e4b\u601d\uff0c\u5f39\u594f\u8d77\u6765\uff0c\u82cd\u51c9\u6c89\u90c1\uff0c\u5f39\u5f97\u6811\u53f6\u6447\u843d\uff0c\u6797\u9e1f\u60ca\u98de\uff0c\u80f8\u4e2d\u95f7 \u6c14\uff0c\u5bf8\u68a2\u7a0d\u5ba3\u6cc4\uff0c\u6b63\u81ea\u5f39\u5230\u4f24\u5fc3\u4e4b\u5904\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u201c\u6251\u54e7\u201d\u4e00\u7b11\uff0c\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c\u6307\u6cd5\u9aa4\u4e71\uff0c\u201c\u94ee\u201d\u7136\u58f0\u54cd\uff0c\u4e00\u66f2\u672a\u7ec8\uff0c \u7434\u5f26\u65ad\u4e86\uff01 \u3000\u3000\u674e\u9038\u63a8\u7434\u800c\u8d77\uff0c\u4e00\u4e2a\u5c11\u5973\u6b63\u81ea\u6797\u4e2d\u7a7f\u8fc7\uff0c\u4e0d\u662f\u6b66\u7384\u971c\u662f\u8c01\uff1f \u3000\u3000\u674e\u9038\u6012\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5012\u5947\u4e86\uff01\u4f60\u6709\u4f60\u54ed\uff0c\u6211\u6709\u6211\u7b11\uff0c\u4e0e \u4f60\u4f55\u5e72\uff1f\u201d\u674e\u9038\u6ee1\u8154\u6012\u6c14\uff0c\u5403\u5979\u95ee\u4f4f\uff0c\u53d1\u4f5c\u4e0d\u6765\u3002 \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u5927\u82f1\u96c4\uff0c\u4f60\u5b89\u9759\u4e9b\u5427\u3002\u5bf9\u4e0d\u4f4f\uff0c\u6211\u5931\u966a\u5566\uff01\u201d\u674e\u9038\u6068\u6068\u8bf4\u9053\uff1a\u201c\u8c01 \u8981\u7559\u4f60\uff0c\u54fc\uff0c\u4f60\u8d70\u5f97\u8d8a\u8fdc\u8d8a\u597d\uff01\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u4f1a\u8d70\u5f97\u592a\u8fdc\uff0c\u4f60\u8981\u77e5\u9053\u6211\u53bb\u54ea\u513f\u4e48\uff1f\u201d \u3000\u3000\u674e\u9038\u6012\u6c14\u672a\u606f\uff0c\u9053\uff1a\u201c\u8c01\u7ba1\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u662f\u5230\u4f60\u6240\u5173\u5fc3\u7684\u5730\u65b9\u53bb \u554a\uff01\u6211\u8981\u5230\u957f\u5b89\u770b\u770b\uff0c\u770b\u4e00\u770b\u957f\u5b89\u7684\u5bab\u6bbf\uff0c\u662f\u4e0d\u662f\u5df1\u6539\u6210\u4e86\u9ecd\u5730\u79be\u7530\uff1f\u201d\u8fd9\u51e0\u53e5\u8bdd\u5b9e\u662f \u9488\u5bf9\u674e\u9038\u6240\u5f39\u594f\u7684\u90a3\u7bc7\u201c\u9ecd\u79bb\u201d\u800c\u53d1\uff0c\u201c\u9ecd\u79bb\u201d\u7bc7\u7684\u6b4c\u8005\uff0c\u4e3a\u5468\u5ba4\u7684\u5bd3\u6bbf\u53d8\u6210\u79be\u7530\u9ecd\u5730 \u9762\u60b2\uff0c\u4f46\u957f\u5b89\u7684\u7e41\u534e\u5374\u66f4\u80dc\u4e8e\u5f80\u6614\uff0c\u8fd9\u660e\u660e\u662f\u8ba5\u523a\u674e\u9038\u62df\u4e8e\u4e0d\u4f26\u3002 \u3000\u3000\u674e\u9038\u60ed\u6012\u4ea4\u8fdb\uff0c\u65b9\u6b32\u53cd\u5507\u76f8\u7a3d\uff0c\u6b66\u7384\u971c\u4e00\u9635\u5927\u7b11\uff0c\u65e9\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002\u674e\u9038\u9759\u4e86\u4e0b\u6765\uff0c \u5ffd\u4f3c\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\u4e00\u6837\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u3000\u3000\u201c\u5979\u7684\u8bbd\u523a\u4e5f\u6709\u51e0\u5206\u9053\u7406\uff0c\u6b66\u5219\u5929\u5e76\u6ca1\u6709\u628a\u957f\u5b89\u6bc1\u706d\uff0c\u6cbb\u7406\u5929\u4e0b\u3002 \u3000\u3000\u4e5f\u786e\u4e4e\u6709\u5979\u7684\u624b\u6bb5\uff0c\u8fd9\u6837\u4e00\u6765\u5c31\u66f4\u53ef\u6015\u4e86\u3002\u201d\u60f3\u8d77\u81ea\u5df1\u5165\u5ddd\uff0c\u4e00\u4e8b\u65e0\u6210\uff0c\u5f90\u656c\u4e1a\u7684 \u8d77\u5175\uff0c\u4ea6\u672a\u5fc5\u8db3\u6043\uff0c\u5fc3\u4e2d\u66f4\u662f\u6005\u60d8\u4e0d\u5b89\uff0c\u6700\u540e\u60f3\u9053\uff1a\u201c\u6211\u6000\u770b\u5b64\u81e3\u5b7d\u5b50\u4e4b\u5fc3\uff0c\u53ea\u5f53\u95ee\u81ea \u5df1\u662f\u5426\u5c3d\u529b\u800c\u4e3a\uff0c\u5b89\u8ba1\u6210\u8d25\uff1f\u6211\u8fd8\u662f\u5230\u626c\u5dde\u627e\u5f90\u656c\u4e1a\u53bb\u5427\u3002\u201d \u3000\u3000\u674e\u9038\u5fc3\u4e8b\u5982\u6f6e\uff0c\u4ece\u91d1\u9876\u5357\u9762\u4e0b\u5c71\uff0c\u4e0b\u5230\u5343\u4f5b\u9876\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u6709\u5a07\u7b11\u4e4b\u58f0\uff0c\u8fce\u9762\u800c \u6765\uff0c\u674e\u9038\u6012\u9053\uff1a\u201c\u4f60\u53c8\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u8981\u4e0d\u662f\u788d\u7740\u6b66\u7384\u971c\u662f\u4e2a\u5973\u5b50\uff0c\u4ed6\u51e0\u4e4e\u5c31\u8981\u7834\u53e3\u5927\u9a82\u3002 \u3000\u3000\u90a3\u77e5\u8fce\u9762\u6765\u7684\u5374\u5e76\u4e0d\u662f\u6b66\u7384\u971c\uff0c\u53ea\u89c1\u7b11\u58f0\u53d1\u5904\uff0c\u73b0\u51fa\u4e24\u4e2a\u90aa\u91cc\u90aa\u6c14\u7684\u7537\u5973\uff0c\u7537\u7684\u662f \u62ab\u53d1\u5934\u9640\uff0c\u866c\u987b\u5982\u621f\uff0c\u5973\u7684\u5374\u662f\u59ff\u5bb9\u51b6\u8273\uff0c\u957f\u7709\u5165\u9b13\uff0c\u8361\u610f\u64a9\u4eba\u3002\u674e\u9038\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u9053\uff1a \u201c\u8fd9\u4e24\u4eba\u4e0d\u662f\u6c5f\u6e56\u4e0a\u6240\u4f20\u8bf4\u7684\u90a3\u4e24\u4e2a\u5927\u9b54\u5934\u2014\u2014\u6076\u884c\u8005\u548c\u6bd2\u89c2\u97f3\u4e48\uff1f\u201d \u3000\u3000\u674e\u9038\u731c\u7684\u4e0d\u9519\uff0c\u8fd9\u4e24\u4eba\u6b63\u662f\u6076\u884c\u8005\u548c\u6bd2\u89c2\u97f3\uff0c\u539f\u6765\u4ed6\u4eec\u4e5f\u662f\u8d76\u6765\u8d74\u4f1a\u7684\u3002\u53ea\u56e0\u6076\u884c \u8005\u66fe\u88ab\u6b66\u7384\u971c\u91cd\u4f24\uff0c\u5341\u5929\u4e4b\u524d\uff0c\u624d\u5f97\u75ca\u6108\uff0c\u6545\u6b64\u6765\u8fdf\u3002 \u3000\u3000\u6bd2\u89c2\u9752\u4e00\u53cc\u5a9a\u670d\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u5411\u674e\u9038\u6253\u91cf\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u674e\u516c\u5b50\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a \u201c\u6211\u662f\u59d3\u674e\uff0c\u600e\u4e48\uff1f\u201d\u6076\u884c\u8005\u5927\u559c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5b9a\u662f\u8c37\u795e\u7fc1\u6240\u8bf4\u7684\u90a3\u4f4d\u5343\u5c81\u7237\u4e86\uff0c\u8bf7\u5bb9 \u6211\u4eec\u53c2\u89c1\u3002\u201d\u674e\u9038\u6ee1\u809a\u76ae\u6076\u6c14\u559d\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4f60\u4eec\u662f\u4e0d\u662f\u4e00\u4e2a\u53eb\u505a\u6076\u884c\u8005\uff0c\u4e00\u4e2a\u53eb\u505a\u6bd2 \u89c2\u97f3\uff0c\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6076\u884c\u8005\u6115\u7136\u4e0d\u77e5\u6240\u7b54\uff0c\u6bd2\u89c2\u97f3\u7b11\u9053\uff1a\u201c\u90a3\u662f\u6c5f\u6e56\u4e0a\u7684\u4ec7\u5bb6\u9001\u7ed9\u6211\u4eec\u7684\u532a\u53f7\uff0c\u5176\u5b9e\u6211 \u4eec\u5bf9\u5f85\u4ec7\u4eba\u624d\u4f1a\u6076\u6bd2\uff0c\u5bf9\u81ea\u5df1\u4eba\u90a3\u662f\u633a\u597d\u4e0d\u8fc7\u3002\u6211\u95e8\u542c\u8bf4\u4eca\u5e74\u5343\u5c81\u7237\u8981\u6765\u4e3b\u6301\u82f1\u96c4\u5927\u4f1a\uff0c \u6068\u4e0d\u5f97\u7239\u5a18\u7ed9\u6211\u4eec\u591a\u751f\u4e24\u6761\u817f\u8d76\u6765\u53c2\u8c12\u5462\uff01\u600e\u4e48\uff0c\u82f1\u96c4\u5927\u4f1a\u8fd9\u6837\u5feb\u5c31\u6563\u4e86\u5417\uff1f\u8c37\u8001\u5149\u751f \u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u674e\u9038\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u548c\u4f60\u4eec\u662f\u81ea\u5df1\u4eba\uff1f\u6211\u6765\u95ee\u4f60\uff0c\u5df4\u5dde\u6697\u6740\u592a\u5b50\u90a3\u4ef6\u6848\u5b50\uff0c\u662f\u4e0d\u662f\u4f60 \u4eec\u5e72\u7684\uff1f\u201d\u6076\u884c\u8005\u5927\u4e3a\u5947\u602a\uff0c\u7c97\u58f0\u7b54\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\u5440\uff0c\u8981\u4e0d\u7136\u6211\u4eec\u600e\u4e48\u6562\u8bf4\u662f\u81ea\u5df1\u4eba\uff1f\u201d\u674e\u9038\u6012\u9053\uff1a\u201c\u4f60\u4eec\u7ed9\u6b66\u5219\u5927\u5dee\u9063\uff0c\u6740 \u4e86\u6211\u7684\u54e5\u54e5\uff0c\u8fd8\u8bf4\u662f\u81ea\u5df1\u4eba\uff1f\u201d\u6bd2\u89c2\u97f3\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\uff0c\u9634\u9633\u602a\u6c14\u7684\u66fc\u58f0\u8bf4\u9053\uff1a\u201c\u5343\u5c81\u7237\uff0c \u539f\u6765\u8c37\u795e\u7fc1\u8fd8\u6ca1\u6709\u544a\u8bc9\u4f60\u4e48\uff1f\u201d \u3000\u3000\u674e\u9038\u5fc3\u4e2d\u4e00\u51db\uff0c\u7591\u4e91\u5927\u8d77\uff0c\u4ed6\u9690\u5fcd\u4e0d\u53d1\uff0c\u6362\u4e86\u4e00\u4ed8\u989c\u8272\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u6211\u5c1a\u672a\u77e5\uff0c \u8bf7\u9053\u5176\u8be6\u3002\u201d\u6bd2\u89c2\u97f3\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u88f4\u8001\u5927\u4eba\u5b9a\u4e0b\u7684\u597d\u8ba1\u7b56\uff0c\u53eb\u4e18\u795e\u52cb\u90e8\u4e0b\u7684\u519b\u5b98\u5047\u5192\u8bcf \u4e66\uff0c\u8feb\u4ee4\u592a\u5b50\u81ea\u5c3d\u3002 \u3000\u3000\u4e0d\u6599\u592a\u5b50\u751f\u7591\uff0c\u575a\u4e0d\u5949\u8bcf\uff0c\u4e00\u5b9a\u8981\u9762\u89c1\u4ed6\u7684\u6bcd\u540e\uff0c\u6ca1\u5948\u4f55\u6211\u4eec\u53ea\u597d\u81ea\u5df1\u52a8\u624b\u4e86\u3002\u201d \u674e\u9038\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u662f\u88f4\u708e\u5dee\u9063\u7684\uff01\u201d\u6076\u884c\u8005\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6bbf\u4e0b\u660e\u767d\uff0c \u90a3\u5c31\u597d\u4e86\u201d\u6bd2\u89c2\u97f3\u4e5f\u5a07\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\u4e5f\u7ed9\u8fd9\u6761\u597d\u8ba1\u9a97\u8fc7\uff0c\u4f55\u51b5\u4ed6\u4eba\uff1f\u7ecf\u8fc7\u8fd9\u4ef6\u4e8b\u540e\uff0c\u60f3 \u5929\u4e0b\u4e4b\u4eba\uff0c\u90fd\u5c06\u8ba4\u5b9a\u662f\u6b66\u5219\u5929\u6240\u4e3a\uff0c\u6211\u7684\u7ef0\u53f7\u4e5f\u8981\u8f6c\u9001\u7ed9\u5979\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u9b54\u5934\u7684\u7b11\u58f0\u597d\u50cf\u5229\u7bad\u4e00\u6837\u7a7f\u8fdb\u674e\u9038\u5fc3\u91cc\uff0c\u4ed6\u505a\u68a6\u4e5f\u6599\u60f3\u4e0d\u5230\uff0c\u50cf\u88f4\u708e\u8fd9\u6837\u6ee1 \u53e3\u4ec1\u4e49\u9053\u5fb7\u3001\u7b54\u5141\u5e2e\u52a9\u4ed6\u6062\u590d\u5510\u5ba4\u6c5f\u5c71\u7684\u201c\u5927\u5fe0\u81e3\u201d\uff0c\u7528\u5fc3\u7adf\u662f\u8fd9\u822c\u72e0\u6bd2\uff01\u4ed6\u4e5f\u60f3\u4e0d\u5230 \u50cf\u8c37\u795e\u7fc1\u8fd9\u6837\u540d\u6ee1\u5929\u4e0b\u7684\u6b66\u6797\u76df\u4e3b\uff0c\u77e5\u9053\u5185\u60c5\uff0c\u5374\u4e5f\u4e0d\u80af\u5bf9\u4ed6\u8bf4\u51fa\u771f\u8bdd\uff0c\u8fd9\u4e2a\u6253\u51fb\u5bf9\u4ed6 \u592a\u6c89\u91cd\u4e86\uff0c\u6bd4\u201c\u82f1\u96c4\u5927\u4f1a\u201d\u7684\u74e6\u89e3\uff0c\u8fd8\u8981\u4ee4\u4ed6\u96be\u53d7\uff01 \u3000\u3000\u8981\u77e5\u674e\u9038\u4e00\u5411\u4ee5\u82f1\u96c4\u81ea\u8d1f\uff0c\u201c\u6b63\u7edf\u201d\u81ea\u5c45\uff0c\u4ed6\u660e\u77e5\u6b66\u5219\u5929\u52bf\u5927\u96c4\u539a\uff0c\u800c\u8fd8\u6562\u548c\u5f90\u656c \u4e1a\u5546\u8bae\u8d77\u5175\u8ba8\u4f10\u5979\uff0c\u5c31\u662f\u62b1\u7740\u201c\u90aa\u4e0d\u80dc\u6b63\u201d\u7684\u5fc3\u7406\uff0c\u5982\u4eca\u4ed6\u5982\u68a6\u521d\u9192\uff0c\u5230\u5e95\u54ea\u65b9\u662f\u201c\u6b63\u201d\uff0c \u54ea\u65b9\u662f\u201c\u90aa\u201d\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u5728\u6000\u7591\u4e86\u3002 \u3000\u3000\u6bd2\u89c2\u97f3\u89c1\u4ed6\u9762\u8272\u6709\u5f02\uff0c\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u4f60\u8be5\u6b22\u559c\u624d\u662f\u554a\uff01\u6b66\u5219\u5929\u7684\u513f\u5b50 \u6b7b\u7684\u6b7b\u4e86\uff0c\u8d2c\u7684\u8d2c\u4e86\uff0c\u8fd8\u6709\u4e00\u4e2a\u5362\u9675\u738b\u53c8\u662f\u5eb8\u788c\u65e0\u80fd\uff0c\u5c06\u6765\u5510\u5ba4\u518d\u5174\uff0c\u5343\u5c81\u4f60\u5c31\u53d8\u6210\u4e07 \u5c81\u5566\uff01\u90a3\u65f6\u53ef\u522b\u5fd8\u8bb0\u4e86\u6211\u4eec\u554a\uff01\u201d \u3000\u3000\u674e\u9038\u54ac\u5b9e\u7259\u6839\uff0c\u5f3a\u5fcd\u6012\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u5f90\u656c\u4e1a\u77e5\u9053\u8fd9\u4e8b\u5417\uff1f\u201d\u58f0\u97f3\u5fae\u5fae\u53d1\u6296\u3002\u6bd2\u89c2\u97f3 \u82e5\u6709\u6df1\u610f\u7684\u671b\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u88f4\u5927\u4eba\u7684\u5b89\u6392\uff0c\u82f1\u56fd\u516c\u4e8b\u524d\u8bc4\u672a\u77e5\u9053\u3002\u82f1\u56fd\u516c\u8981 \u62e5\u7acb\u7684\u662f\u5362\u9675\u738b\uff0c\u88f4\u5927\u4eba\u5219\u5c5e\u610f\u6bbf\u4e0b\uff0c\u6bbf\u4e0b\u662f\u806a\u660e\u4eba\uff0c\u60f3\u5f53\u4f53\u4f1a\u5f97\u5230\u88f4\u5927\u4eba\u7684\u6df1\u610f\u3002\u201d \u674e\u9038\u9053\uff1a\u201c\u8fd8\u671b\u6307\u6559\u3002\u201d\u6bd2\u89c2\u97f3\u7b11\u9053\uff1a\u201c\u5362\u9675\u738b\u4e0e\u5e9f\u592a\u5b50\u674e\u8d24\u90fd\u662f\u6b66\u5219\u5929\u7684\u4eb2\u751f\u513f\u5b50\uff0c \u4e0d\u8bba\u62e5\u7acb\u90a3\u4e00\u4e2a\uff0c\u7ec8\u662f\u7559\u6709\u540e\u60a3\uff0c\u4ed6\u4eec\u4e5f\u672a\u5fc5\u80af\u628a\u6bcd\u4eb2\u6740\u6389\uff0c\u6b64\u5176\u4e00\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u8fd8 \u6709\u5462\uff1f\u201d\u6bd2\u89c2\u97f3\u9053\uff1a \u3000\u3000\u201c\u5f90\u656c\u4e1a\u62e5\u7acb\u5362\u9675\u738b\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u5929\u4e0b\u5927\u6743\uff0c\u5f53\u7136\u662f\u5f52\u4ed6\u638c\u63e1\u3002 \u3000\u3000\u4e0d\u8fc7\u82e5\u679c\u4e0e\u88f4\u5927\u4eba\u540c\u5fc3\uff0c\u7531\u6bbf\u4e0b\u62db\u63fd\u5929\u4e0b\u82f1\u96c4\uff0c\u5206\u8584\u4e86\u5f90\u656c\u4e1a\u7684\u5175\u6743\uff0c\u90a3\u4e48\u5c06\u6765\u5c40 \u9762\u5c31\u4e0d\u540c\u4e86\uff0c\u6bbf\u4e0b\u8bd5\u60f3\uff0c\u4f60\u6709\u5929\u4e0b\u82f1\u96c4\u8f85\u52a9\uff0c\u53c8\u6709\u88f4\u5927\u4eba\u4f5c\u5185\u5e94\uff0c\u5c06\u6765\u4e2d\u5174\u5510\u5ba4\uff0c\u8fd8\u6015 \u5362\u9675\u738b\u62a2\u4e86\u4f60\u7684\u5b9d\u5ea7\u5417\uff1f\u201d \u3000\u3000\u674e\u9038\u6012\u4e0d\u53ef\u6291\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u6765\u66fe\u8d77\u4e8b\uff0c\u4ed6\u4eec\u65e9\u5df2\u5728\u52fe\u5fc3\u6597\u89d2\uff0c\u4e89\u6743\u593a\u5229\u3002\u201d\u773c\u73e0 \u4e00\u8f6c\uff0c\u5f3a\u5b9a\u5fc3\u795e\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u88f4\u5927\u4eba\u4eb2\u4fe1\uff0c\u53c8\u5bf9\u6211\u4e00\u7247\u5fe0\u5fc3\uff0c\u5477\u5f53\u91cd\u91cd\u5c01\u8d4f\uff01\u201d \u6076\u884c\u8005\u5927\u559c\u9053\uff1a \u3000\u3000\u201c\u8c22\u6bbf\u4e0b\u5c01\u8d4f\uff01\u201d\u6b63\u5f85\u8dea\u4e0b\u542c\u5c01\uff0c\u5ffd\u542c\u5f97\u6bd2\u89c2\u97f3\u559d\u9053\uff1a\u201c\u5e08\u54e5\u5c0f\u5fc3\uff01\u201d\u6076\u884c\u8005\u731b\u7136 \u4e00\u60ca\uff0c\u6025\u5fd9\u8df3\u8d77\uff0c\u53ea\u542c\u5f97\u5237\u7684\u4e00\u58f0\uff0c\u674e\u9038\u95ea\u7535\u822c\u62d4\u51fa\u5b9d\u5251\uff0c\u671d\u7740\u6076\u884c\u8005\u54bd\u5589\u4fbf\u523a\uff0c\u5c1a\u5e78 \u6076\u884c\u8005\u5f97\u6bd2\u89c2\u97f3\u63d0\u9192\uff0c\u8df3\u95ea\u5f97\u5feb\uff0c\u674e\u9038\u8fd9\u4e00\u5251\u6070\u597d\u4ece\u4ed6\u7684\u9888\u8fb9\u524a\u8fc7\uff0c\u672a\u66fe\u4f24\u7740\u3002 \u3000\u3000\u6bd2\u89c2\u97f3\u5a07\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u6211\u5462\uff1b\u6211\u4e5f\u8981\u6765\u8ba8\u8d4f\u4e86\uff01\u201d\u674e\u9038\u56de\u5251\u8f6c\u8eab\uff0c\u7b2c\u4e8c\u62db\u5c06\u53d1\u672a\u53d1\uff0c \u9661\u7136\u95f4\u5ffd\u89c1\u6bd2\u89c2\u97f3\u624b\u638c\u4e00\u7ffb\uff0c\u4e00\u84ec\u94f6\u9488\uff0c\u7cbe\u8292\u56db\u5c04\uff0c\u674e\u9038\u77e5\u9053\u662f\u5979\u4ed7\u4ee5\u6210\u540d\u7684\u201c\u900f\u7a74\u795e \u9488\u3002\u201d\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u672a\u6687\u88ad\u654c\uff0c\u5148\u6551\u81ea\u8eab\uff0c\u767e\u5fd9\u4e2d\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u4f7f\u51fa\u4e00\u62db\u201c\u7389\u5e26 \u56f4\u8170\u201d\uff0c\u8fd9\u4e00\u62db\u9632\u8eab\u5251\u6cd5\uff0c\u867d\u7136\u662f\u7cbe\u5999\u5f02\u5e38\uff0c\u4f46\u4e24\u4eba\u8ddd\u79bb\u592a\u8fd1\uff0c\u9632\u4e0d\u80dc\u9632\uff0c\u4f46\u542c\u5f97\u55e4\u55e4 \u58f0\u54cd\uff0c\u5251\u5149\u6fc0\u8361\u4e4b\u4e2d\uff0c\u94f6\u9488\u7ede\u788e\u5982\u96e8\uff0c\u6bd2\u89c2\u97f3\u8fd0\u6c14\u4e00\u5439\uff0c\u674e\u9038\u5ffd\u89c9\u5de6\u201c\u80a9\u4e95\u7a74\u201d\u4e0a\u4e00\u9ebb\uff0c \u72b9\u5982\u7ed9\u5927\u8682\u8681\u53ee\u4e86\u4e00\u53e3\u4f3c\u7684\uff0c\u534a\u6761\u81c2\u818a\uff0c\u767b\u65f6\u8f6c\u52a8\u4e0d\u7075\u3002 \u3000\u3000\u6076\u884c\u8005\u51e0\u4e4e\u88ab\u674e\u9038\u524a\u6389\u5934\u76ae\uff0c\u6012\u4e0d\u53ef\u904f\uff0c\u5927\u58f0\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u6709\u7687\u5e1d \u4e0d\u505a\uff0c\u6559\u4f60\u5230\u9ec4\u6cc9\u627e\u4f60\u7684\u5144\u5f1f\u53bb\uff01\u201d\u4e00\u628a\u94b1\u9556\u98de\u51fa\uff0c\u674e\u9038\u5de6\u80a9\u9ebb\u75c5\uff0c\u8eab\u6cd5\u5446\u6ede\uff0c\u9888\u540e\u7684 \u201c\u4e2d\u67f1\u7a74\u201d\u53c8\u4e2d\u4e86\u4e00\u679a\u201c\u788e\u9aa8\u94b1\u9556\u201d\uff0c\u6076\u884c\u8005\u8155\u52b2\u6781\u5927\uff0c\u8fd9\u4e00\u9556\u6253\u5f97\u4ed6\u75db\u6f88\u5fc3\u80ba\u3002 \u3000\u3000\u674e\u9038\u54ac\u4e00\u54ac\u7259\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u6211\u4eca\u65e5\u5148\u9664\u4e86\u4f60\u8fd9\u4e24\u4e2a\u9b54\u5934\uff01\u201d\u98de\u8eab\u63a0\u8d77\uff0c\u5b9d\u5251\u5316\u6210 \u4e86\u4e00\u9053\u94f6\u8679\uff0c\u51cc\u7a7a\u51fb\u4e0b\u3002\u6076\u884c\u8005\u8fd8\u771f\u6599\u4e0d\u5230\u4ed6\u8fde\u4e2d\u6697\u5668\u4e4b\u540e\uff0c\u4f9d\u7136\u80fd\u4f7f\u51fa\u8fd9\u7b49\u51f6\u72e0\u7684\u5251 \u62db\uff0c\u653e\u51fa\u6212\u5200\u4e00\u6321\uff0c\u4f46\u542c\u5f97\u201c\u54df\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u674e\u9038\u8fd9\u53e3\u5251\u4e43\u662f\u5927\u5185\u5b9d\u5251\uff0c\u4e00\u5251 \u5c31\u628a\u6076\u884c\u8005\u7684\u5200\u5c16\u524a\u53bb\u4e00\u622a\u3002 \u3000\u3000\u6076\u884c\u8005\u5927\u543c\u4e00\u58f0\uff0c\u53cd\u8f6c\u5200\u80cc\uff0c\u659c\u6263\u674e\u9038\u8109\u95e8\uff0c\u90a3\u77e5\u674e\u9038\u5df2\u628a\u6b7b\u751f\u7f6e\u4e8e\u5ea6\u5916\uff0c\u7adf\u662f\u594b \u4e0d\u987e\u8eab\uff0c\u6bd4\u4ed6\u8fd8\u8981\u51f6\u72e0\uff0c\u5251\u950b\u987a\u52bf\u53cd\u5c55\uff0c\u75be\u5982\u9a87\u7535\u5954\u96f7\uff0c\u538b\u4f4f\u4e86\u4ed6\u7684\u6212\u5200\uff0c\u523a\u5230\u4e86\u4ed6\u7684 \u9762\u95e8\u3002\u6076\u884c\u8005\u62a1\u5200\u6025\u6321\uff0c\u674e\u9038\u5b9d\u5251\u970d\u970d\u5c55\u5f00\uff0c\u4e00\u8fde\u51e0\u62db\u62fc\u547d\u7684\u62db\u6570\uff0c\u6740\u5f97\u6076\u884c\u8005\u624b\u5fd9\u811a \u4e71\u3002 \u3000\u3000\u6bd2\u89c2\u97f3\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u597d\u50bb\u554a\uff01\u6211\u7684\u900f\u7a74\u795e\u9488\u5728\u4e00\u65f6\u4e09\u523b\u4e4b\u5185\u4fbf\u4f1a\u6bd2\u53d1\u5168\u8eab\uff0c \u4f60\u7528\u7684\u662f\u54ea\u4e00\u79cd\u94b1\u9556\uff1f\u201d\u6076\u884c\u8005\u9053\uff1a \u3000\u3000\u201c\u6211\u7528\u7684\u4e5f\u662f\u6bd2\u6027\u6700\u5feb\u7684\u90a3\u79cd\u788e\u9aa8\u94b1\u9556\uff0c\u201d\u6bd2\u89c2\u97f3\u7b11\u9053\uff1a\u201c\u7740\u554a\uff01 \u3000\u3000\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u96be\u9053\u5c31\u4e0d\u80fd\u5fcd\u8010\u8fd9\u4e00\u65f6\u4e09\u523b\uff1f\u4f55\u5fc5\u53bb\u4e0e\u4e00\u4e2a\u5c06\u6b7b\u4e4b\u4eba\u62fc\u547d\uff1f\u201d \u3000\u3000\u6076\u884c\u8005\u7ed9\u5979\u4e00\u8a00\u63d0\u9192\uff0c\u6025\u5fd9\u5168\u529b\u4e00\u5200\uff0c\u67b6\u5f00\u5b9d\u5251\uff0c\u7acb\u5373\u8df3\u51fa\u5708\u5b50\uff0c\u674e\u9038\u7d27\u8ffd\u4e0d\u820d\uff0c \u6076\u884c\u8005\u7ed5\u573a\u75be\u8d70\uff0c\u6bd2\u89c2\u97f3\u53c8\u6325\u8896\u6270\u4e71\u674e\u9038\u7684\u773c\u795e\uff0c\u674e\u9038\u8f6c\u8fc7\u5251\u950b\u8ffd\u51fb\u6bd2\u89c2\u97f3\uff0c\u4f46\u6bd2\u89c2\u9752 \u7684\u8f7b\u529f\u5728\u4ed6\u4e4b\u4e0a\uff0c\u4ed6\u867d\u7136\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u5374\u662f\u523a\u5979\u4e0d\u7740\u3002\u6bd2\u89c2\u97f3\u683c\u683c\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60 \u8fd9\u6837\u5f3a\u7528\u771f\u6c14\uff0c\u6bd2\u53d1\u5f97\u5c31\u66f4\u5feb\u4e86\uff01\u6bd2\u53d1\u4e4b\u540e\uff0c\u4f60\u7684\u9aa8\u5934\u8981\u7247\u7247\u788e\u843d\uff0c\u4f38\u4ed9\u96be\u6551\uff0c\u5440\uff0c\u4f60 \u662f\u7687\u5e1d\u5b50\u5b59\uff0c\u53c8\u6709\u6587\u624d\u6b66\u827a\uff0c\u8fd9\u6837\u6b7b\u53bb\uff0c\u6211\u4e5f\u66ff\u4f60\u53ef\u60dc\u554a\uff01\u201d\u674e\u9038\u7ed9\u5979\u4e00\u6c14\uff0c\u773c\u775b\u53d1\u9ed1\uff0c \u72c2\u821e\u5b9d\u5251\uff0c\u66f4\u662f\u780d\u5979\u4e0d\u7740\u3002 \u3000\u3000\u6076\u884c\u8005\u9053\uff1a\u201c\u5582\uff0c\u54b1\u4eec\u628a\u4ed6\u6740\u6b7b\uff0c\u88f4\u8001\u5927\u4eba\u4f1a\u4e0d\u4f1a\u89c1\u602a\uff1f\u201d\u6bd2\u89c2\u97f3\u7b11\u9053\uff1a\u201c\u4ed6\u82e5\u80af \u542c\u88f4\u5927\u4eba\u7684\u8bdd\uff0c\u90a3\u4fbf\u662f\u81ea\u5df1\u4eba\u3002\u4ed6\u4e0d\u80af\u542c\uff0c\u90a3\u4fbf\u662f\u654c\u4eba\u4e86\u3002\u592a\u5b50\u4e00\u6837\u53ef\u6740\uff0c\u4f55\u51b5\u662f\u4ed6\uff1f \u53ea\u662f\u4fbf\u5b9c\u4e86\u5362\u9675\u738b\u4e86\u3002\u201d\u6076\u884c\u8005\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6740\u4e86\u4ed6\u4e4b\u540e\uff0c\u6211\u8981\u4ed6\u8eab\u4e0a\u7684\u73e0\u5b9d\uff0c\u4f60\u8981 \u4ed6\u8fd9\u53e3\u5b9d\u5251\u3002\u201d\u4e24\u4eba\u4e00\u5439\u4e00\u5531\uff0c\u5c31\u4f3c\u5f53\u4f5c\u4ed6\u5df2\u6b7b\u4e00\u822c\u3002 \u3000\u3000\u674e\u9038\u4f46\u89c9\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u5251\u62db\u53d1\u51fa\uff0c\u5df1\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u4e0d\u7531\u5f97\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff1a \u201c\u4e0d\u60f3\u6211\u4eca\u65e5\u547d\u4e27\u5bb5\u5c0f\u4e4b\u624b\uff01\u201d\u4ed6\u8d81\u7740\u89c6\u4e3a\u8fd8\u6ca1\u6709\u5b8c\u5168\u6d88\u5931\uff0c\u9661\u7136\u95f4\u628a\u5168\u8eab\u529f\u529b\uff0c\u51dd\u805a \u5251\u5c16\uff0c\u53f1\u54a4\u4e00\u58f0\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u5411\u6bd2\u89c2\u97f3\u98de\u6251\uff01 \u3000\u3000\u8fd9\u62fc\u547d\u4e00\u51fb\uff0c\u52bf\u9053\u51cc\u5386\u975e\u5e38\uff0c\u4fbf\u542c\u5f97\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u9976\u662f\u6bd2\u89c2\u97f3\u8eb2\u95ea\u5f97\u5feb\uff0c\u8863\u895f\u4e5f \u7ed9\u4ed6\u4e00\u5251\u7a7f\u8fc7\uff0c\u6bd2\u89c2\u97f3\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60\u8fd9\u6837\u8d76\u7740\u53cb\u6b7b\uff0c\u53ef\u662f\u6025\u4e8e\u8981\u89c1\u4f60\u7684\u54e5\u54e5\u4e48\uff1f\u201d \u56de\u8896\u4e00\u62cd\uff0c\u674e\u9038\u6c14\u529b\u5df2\u7ecf\u7528\u5c3d\uff0c\u767b\u65f6\u8dcc\u7ffb\uff0c\u4f46\u89c9\u5730\u8f6c\u5929\u65cb\uff0c\u773c\u524d\u4e00\u7247\u6f06\u9ed1\u3002 \u3000\u3000\u8ff7\u832b\u4e2d\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u6765\u5f97\u6781\u5feb\uff0c\u5578\u58f0\u672a\u6b47\uff0c\u90a3\u6b65\u58f0\u5df2\u5230\u4e86\u8033\u8fb9\uff0c\u4e00\u4e2a\u6e05\u8106\u7684\u58f0 \u97f3\u65a5\u9053\uff1a\u201c\u5140\u4f60\u8fd9\u4e24\u4e2a\u4e0d\u8981\u8138\u7684\u9b54\u5934\u53c8\u5728\u8fd9\u91cc\u5bb3\u4eba\u4e48\uff1f\u201d\u8f6c\u97f3\u7a14\u719f\uff0c\u674e\u9038\u60f3\u7741\u5f00\u773c\u775b\uff0c \u773c\u76ae\u5374\u597d\u4f3c\u6709\u5343\u65a4\u4e4b\u91cd\uff0c\u600e\u6837\u4e5f\u6491\u4e0d\u5f00\uff0c\u4f46\u542c\u5f97\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u597d\u50cf\u5343\u519b\u4e07\u9a6c\uff0c\u5728\u8eab\u8fb9 \u9a70\u9a8b\u4e00\u822c\uff0c\u63a5\u7740\u4e0d\u4e45\u4fbf\u542c\u5f97\u6bd2\u89c2\u97f3\u4e0e\u6076\u884c\u8005\u60e8\u5389\u7684\u53eb\u58f0\uff0c\u604d\u82e5\u53d7\u4f24\u7684\u91ce\u517d\u5728\u54ea\u91cc\u5cf0\u53eb\uff0c \u6495\u4eba\u5fc3\u80ba\uff0c\u674e\u9038\u7684\u7cbe\u795e\u518d\u4e5f\u65e0\u6cd5\u652f\u6301\uff0c\u6e10\u6e10\u5931\u53bb\u4e86\u77e5\u89c9\u3002 \u3000\u3000\u674e\u9038\u597d\u4f3c\u505a\u4e86\u4e00\u573a\u6076\u68a6\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u4ece\u68a6\u4e2d\u9192\u6765\uff0c\u773c\u775b\u8fd8\u672a\u7741\u5f00\uff0c \u4fbf\u89c9\u5f97\u7f15\u7f15\u5e7d\u9999\uff0c\u6c81\u4eba\u813e\u8151\uff0c\u8033\u8fb9\u542c\u5f97\u201c\u5f97\u5f97\u201d\u7684\u8e44\u58f0\uff0c\u597d\u50cf\u662f\u8eba\u5728\u8f66\u4e0a\uff0c\u53c8\u597d\u50cf\u662f\u8eba \u5728\u54ea\u4f4d\u5c0f\u59d0\u7684\u7ee3\u623f\u4e2d\uff0c\u9760\u7740\u539a\u539a\u7684\u9526\u8925\uff0c\u8212\u9002\u6781\u4e86\u3002\u674e\u9038\u5927\u4e3a\u5947\u602a\uff0c\u7528\u529b\u7741\u5f00\u773c\u76ae\uff0c\u9996 \u5148\u63a5\u89e6\u773c\u5e18\u662f\u4e00\u5f20\u4fcf\u4e3d\u7684\u8138\u5b54\uff0c\u662f\u4e00\u5bf9\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u90a3\u5f20\u8138\u5b54\u8d34\u5f97\u5f88\u8fd1\uff0c\u90a3\u65f6\u773c\u775b \u4e5f\u6b63\u5728\u6ce8\u89c6\u7740\u81ea\u5df1\u3002 \u3000\u3000\u674e\u9038\u5b9a\u4e86\u5b9a\u795e\uff0c\u770b\u6e05\u695a\u4e86\uff0c\u4e0d\u7981\u9a87\u7136\u60ca\u547c\uff0c\u5931\u58f0\u53eb\u9053\uff0c\u201c\u4f60\u3002 \u3000\u3000\u4f60\uff0c\u4f60\uff01\u201d\u90a3\u5c11\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u5bb3\u6015\uff0c\u4f60\u8fd9\u6761\u5c0f\u547d\uff0c\u7b97\u662f\u62fe\u56de\u6765\u5566\uff01\u201d \u674e\u9038\u60f3\u5750\u8d77\u6765\uff0c\u4f46\u89c9\u767e\u9ab8\u6b32\u88c2\uff0c\u8eab\u5b50\u5b8c\u5168\u4e0d\u542c\u652f\u4f7f\uff0c\u624b\u811a\u7adf\u4f3c\u662f\u50f5\u786c\u4e86\u3002\u90a3\u5c11\u5973\u53c8\u7b11\u9053\uff1a \u201c\u4f60\u8fd8\u672a\u8ba4\u8bc6\u6211\u5417\uff1f\u54b1\u4eec\u662f\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u6211\u540d\u53eb\u6b66\u7384\u971c\u3002\u201d \u3000\u3000\u674e\u9038\u60f3\u8d77\u4e86\u548c\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u7684\u6fc0\u6218\uff0c\u60f3\u8d77\u4e86\u81ea\u5df1\u7684\u53d7\u4f24\uff0c\u9053\uff0c\u201c\u539f\u6765\u662f\u4f60\u6551\u4e86\u6211 \u4e48\uff1f\u201d\u6b66\u7384\u971c\u672a\u66fe\u56de\u7b54\uff0c\u8f66\u524d\u9762\u6709\u4e00\u4e2a\u5973\u5b69\u5b50\u56de\u8fc7\u5934\u6765\uff0c\u6251\u54e7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u5c0f \u59d0\u6551\u4e86\u4f60\uff0c\u4f60\u8fd8\u6709\u547d\u4e48\uff1f\u4f60\u771f\u662f\u628a\u6211\u4eec\u5413\u6b7b\u4e86\uff0c\u660f\u7761\u4e86\u4e09\u5929\u4e24\u591c\uff0c\u90fd\u8fd8\u672a\u9192\uff01\u201d\u674e\u9038\u60f6 \u6050\u4e4b\u6781\uff0c\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u6551\u6211\uff1f\u201d\u6b66\u7384\u971c\u7b11\u800c\u4e0d\u7b54\uff0c\u90a3\u5c0f\u4e2b\u9b1f\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u4eba\u600e\u4e48 \u8001\u662f\u628a\u4eba\u5f53\u6210\u6740\u7236\u4e4b\u4ec7\u4f3c\u7684\uff1f\u4e00\u9192\u6765\u5c31\u662f\u8fd9\u4e48\u72e0\u72e0\u7684\u76ef\u7740\u4eba\u5bb6\u3002\u8fde\u591a\u8c22\u4e5f\u4e0d\u8bf4\u4e00\u58f0\uff1f\u4f60 \u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u4eec\u5c0f\u59d0\u4e3a\u4e86\u6551\u4f60\uff0c\u4e0d\u77e5\u8d39\u4e86\u591a\u5927\u5fc3\u529b\uff0c\u8fde\u6bd2\u8840\u4e5f\u7ed9\u4f60\u542e\u4e86\u51fa\u7c73\u4e86\u3002\u201d\u6b66\u7384 \u971c\u55d4\u9053\uff1a\u201c\u660e\u73e0\uff0c\u4e0d\u8981\u591a\u8bdd\uff01\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u60ed\u6127\u3001\u611f\u6fc0\u3001\u96be\u8fc7\u2026\u2026\u79cd\u79cd\u9519\u7efc\u590d\u6742\u7684\u60c5\u7eea\u7ea0\u7ed3\u674e\u9038\u5fc3\u5934\uff0c\u674e\u9038\u5e7d\u5e7d\u7684 \u53f9\u4e86\u53e3\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u5982\u4eca\u6211\u5df2\u843d\u5728\u4f60\u7684\u638c\u63e1\u4e4b\u4e2d\uff0c\u4f60\u5f85\u5c06\u6211\u600e\u751f\u5904\u7f6e\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u6211\u5e26\u4f60\u5230\u957f\u5b89\u53bb\u770b\u79be\u7530\u9ecd\u5730\u554a\uff01\u201d\u674e\u9038\u53cc\u773c\u5706\u7741\uff0c\u5ffd\u800c\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c \u6012\u5bb9\u5c3d\u655b\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u7531\u5f97\u4f60\uff0c\u53cd\u6b63\u6211\u5df2\u6b7b\u4e86\u4e00\u6b21\uff0c\u8fd9\u6761\u6027\u547d\u53ea\u5f53\u62fe\u56de\uff0c\u4e5f\u5c31\u4e0d \u6015\u518d\u6b7b\u7b2c\u4e8c\u6b21\u4e86\u3002\u6b7b\u5728\u6b66\u5219\u5929\u624b\u91cc\uff0c\u603b\u6bd4\u6b7b\u5728\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u624b\u91cc\uff0c\u8981\u503c\u5f97\u4e9b\u3002\u201d\u4ed6\u53ea \u9053\u6b66\u7384\u971c\u8981\u5c06\u4ed6\u62ff\u53bb\u732e\u7ed9\u6b66\u5219\u5929\uff0c\u59cb\u800c\u6124\u6012\uff0c\u7ee7\u800c\u4e00\u60f3\uff0c\u8fd9\u6837\u6b7b\u4e86\u66f4\u597d\uff0c\u4e0d\u7528\u9886\u5979\u7684\u60c5\uff0c \u56e0\u4e4b\u4e5f\u5c31\u5904\u4e4b\u6cf0\u7136\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u6837\u5fc3\u60c5\u6fc0\u52a8\uff0c\u80f8\u53e3\u7acb\u5373\u5267\u75db\u5982\u5272\uff0c\u867d\u7136\u54ac\u5b9e\u7259\u6839\uff0c\u4ecd\u7981\u4e0d\u4f4f\u547b\u541f\u51fa\u58f0\u3002\u6b66\u7384 \u971c\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u624b\u638c\u8d34\u5728\u4ed6\u7684\u80f8\u53e3\uff0c\u6765\u56de\u63c9\u6413\uff0c\u674e\u9038\u4f46\u89c9\u4e00\u80a1\u70ed\u6c14\uff0c\u4ece\u4e39\u7530\u5347\u4e0a\uff0c\u5341\u5206 \u53d7\u7528\uff0c\u77e5\u9053\u5979\u6b63\u4ee5\u4e0a\u4e58\u5185\u529f\u7684\u63a8\u62ff\u624b\u6cd5\uff0c\u5e2e\u52a9\u81ea\u5df1\u4f53\u5185\u6c14\u8840\u7684\u8fd0\u884c\u3002\u674e\u9038\u8e59\u7709\u8bf4\u9053\uff1a \u201c\u4f60\u4f55\u5fc5\u8fd9\u6837\u8d39\u795e\uff0c\u8ba9\u6211\u6b7b\u4e86\u4e0d\u662f\u66f4\u597d\u4e48\uff1f\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u60f3\u8bf4\u7684\u8bdd\uff0c\u4f60\u5fc3 \u4e2d\u5b9a\u662f\u5728\u60f3\uff0c\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u72e0\u6bd2\uff0c\u5c06\u6211\u6551\u6d3b\u4e86\u518d\u9001\u7ed9\u6211\u7684\u4ec7\u4eba\uff0c\u8ba9\u6211\u53d7\u8fb1\u800c\u6b7b\uff1f\u539f\u6765\u4f60 \u7adf\u662f\u8fd9\u6837\u7684\u6068\u6211\uff01\u201d\u674e\u9038\u95ed\u76ee\u4e0d\u8a00\u3001\u4ed6\u5fc3\u4e2d\u786e\u662f\u5982\u6b64\u60f3\u6cd5\uff0c\u4f46\u53c8\u9690\u9690\u611f\u5230\u6b66\u7384\u971c\u7684\u6e29\u67d4 \u4e0d\u4f3c\u5047\u60c5\u5047\u610f\uff0c\u4e0d\u7531\u5f97\u4e00\u7247\u8ff7\u832b\uff0c\u731c\u4e0d\u900f\u5979\u5230\u5e95\u8981\u5c06\u81ea\u5df1\u600e\u6837\u5b89\u6392\u3002 \u3000\u3000\u4f46\u542c\u5f97\u90a3\u5c0f\u4e2b\u9b1f\u53c8\u662f\u201c\u6251\u54e7\u201d\u4e00\u7b11\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u5c0f\u59d0\u5728\u8fd9\u4e09\u5929\u4e24\u591c\u4e4b\u4e2d\uff0c \u672a\u66fe\u778c\u8fc7\u7247\u523b\uff0c\u8017\u635f\u529f\u529b\uff0c\u7ed9\u4f60\u5316\u6bd2\u7597\u4f24\uff0c\u4f60\u5374\u6b7b\u5440\u6d3b\u5440\u7684\u57cb\u6028\u5979\uff01\u4f60\u5475\u77e5\u9053\u6076\u884c\u6709\u7684 \u788e\u9aa8\u94b1\u9556\u4e0e\u6bd2\u89c2\u97f3\u7684\u900f\u7a74\u795e\u9488\u4e43\u662f\u5929\u4e0b\u6700\u6bd2\u7684\u6697\u5668\uff1f\u6211\u4eec\u5c0f\u59d0\u8d39\u5c3d\u529f\u529b\uff0c\u6700\u591a\u4e5f\u53ea\u80fd\u4fdd \u4f60\u4e0d\u6b7b\uff0c\u4f60\u8fd9\u8eab\u6b66\u529f\u7b97\u662f\u5e9f\u4e86\u3002\u201d\u6b66\u7384\u971c\u77aa\u773c\u9053\uff1a\u201c\u660e\u73e0\uff0c\u4f60\u4e0d\u8981\u5413\u4ed6\uff01\u201d\u90a3\u5c0f\u4e2b\u9b1f\u73af \u9053\uff1a\u201c\u6211\u4e0d\u7ed9\u4ed6\u8bf4\u4e2a\u660e\u767d\uff0c\u53ea\u6015\u4ed6\u5728\u4eca\u540e\u4e03\u5929\u4e4b\u5185\uff0c\u90fd\u8981\u5728\u5fc3\u4e2d\u57cb\u6028\u4f60\u5462\uff01\u201d\u674e\u9038\u65e9\u628a \u751f\u6b7b\u7f6e\u5ea6\u5916\uff0c\u6b66\u529f\u8fd8\u80fd\u5426\u4fdd\u6301\uff0c\u90a3\u66f4\u662f\u6839\u672c\u4e0d\u653e\u5728\u5fc3\u5185\uff0c\u53ef\u662f\u4ed6\u5fc3\u4e2d\u5374\u5728\u5947\u602a\uff1a\u4e3a\u4ec0\u4e48 \u8fd9\u5c0f\u4e2b\u9b1f\u53ea\u8bf4\u5728\u4eca\u540e\u4e03\u5929\u4e4b\u5185\u5462\uff1f\u5979\u53c8\u600e\u77e5\u9053\u81ea\u5df1\u5728\u4e03\u5929\u4e4b\u540e\u5c31\u4e0d\u4f1a\u57cb\u6028\u5979\u7684\u5c0f\u59d0\uff1f \u3000\u3000\u53ea\u542c\u5f97\u90a3\u5c0f\u4e2b\u73af\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u7684\u5c0f\u59d0\u59d4\u5b9e\u7231\u60dc\u4e8e\u4f60\uff0c\u5979\u4e0d\u4f46\u8981\u6551\u4f60\u7684\u6027 \u547d\uff0c\u8fd8\u8981\u4fdd\u4f4f\u4f60\u7684\u6b66\u529f\u3002\u4e3a\u6b64\u5979\u60f3\u5c3d\u529e\u6cd5\uff0c\u5c06\u4f60\u5b89\u987f\u534a\u4e2d\uff0c\u94fa\u4e0a\u539a\u539a\u7684\u9526\u8925\uff0c\u8ba9\u4f60\u8212\u8212 \u670d\u670d\u7684\u8eba\u7740\uff0c\u514d\u53d7\u98a0\u7c38\u4e4b\u82e6\uff0c\u7136\u540e\u8d74\u5728\u8fd9\u4e03\u5929\u4e4b\u5185\uff0c\u5c06\u4f60\u9001\u5230\u6c13\u5d03\u5c71\uff0c\u8bf7\u4e00\u4f4d\u9ad8\u624b\u7ed9\u4f60 \u6cbb\u7597\u3002\u4f60\u5f53\u5979\u5f53\u771f\u8981\u5c06\u4f60\u9001\u7ed9\u5929\u540e\u965b\u4e0b\u4e48\uff1f\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u5373\u4f7f\u5c06\u4f60 \u9001\u7ed9\u5929\u540e\uff0c\u5929\u540e\u965b\u4e0b\u4e5f\u65ad\u65ad\u4e0d\u4f1a\u5bb3\u4f60\uff0c\u4e0d\u8fc7\u90a3\u4e9b\u5fa1\u533b\u4eec\u53ea\u6015\u6ca1\u6cd5\u533b\u4f60\u7f62\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u8fd9\u624d\u77e5\u9053\u6b66\u7384\u971c\u7684\u82e6\u5fc3\uff0c\u5fc3\u4e2d\u65e0\u9650\u611f\u6fc0\u3002\u53ef\u662f\u4ed6\u542c\u5230\u90a3\u5c0f\u4e2b\u9b1f\u540e\u9762\u7684\u51e0\u53e5\u8bdd\uff0c \u53c8\u84e6\u7136\u8b66\u89c9\uff0c\u4e0d\u8bba\u5982\u4f55\uff0c\u8fd9\u4e2a\u6551\u4e86\u81ea\u5df1\u7684\u6b66\u7384\u971c\uff0c\u7ec8\u662f\u6b66\u5219\u5929\u7684\u4eba\u3002\u987f\u65f6\u95f4\u6069\u4ec7\u60d8\u60d8\uff0c \u4e0d\u77e5\u9053\u662f\u611f\u6fc0\u5979\u8fd8\u662f\u57cb\u6028\u5979\uff01 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u9a6c\u5927\u53d4\uff0c\u8bf7\u4f60\u505c\u4e00\u505c\u8f66\uff0c\u5c06\u90a3\u58f6\u53c2\u6c64\u7ed9\u6211\u3002\u201d\u9a7e\u8f66\u7684\u5e94\u4e86\u4e00\u58f0\uff0c\u5c06\u9a6c \u52d2\u4f4f\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u674e\u9038\u4f46\u89c9\u8fd9\u4eba\u9762\u8c8c\u597d\u719f\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bb0\u8d77\u6765\u4e86\uff0c\u4ed6\u548c\u4e0a\u5b98\u5a49\u513f\u4ee5\u524d\u5728 \u8d74\u5df4\u5dde\u7684\u8def\u4e0a\uff0c\u66fe\u9047\u5230\u4e00\u4e2a\u519c\u592b\uff0c\u5176\u65f6\u4e0a\u5b98\u5a49\u513f\u6b63\u88ab\u4e00\u4e2a\u519b\u5b98\u8ffd\u6355\uff0c\u662f\u8fd9\u4e2a\u519c\u592b\u5c06\u90a3\u4e2a \u519b\u5b98\u8d76\u8dd1\uff0c\u6697\u4e2d\u89e3\u4e86\u4e0a\u5b98\u5a49\u513f\u4e4b\u56f0\u3002\u674e\u9038\u597d\u751f\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u6b66\u529f\u4e0d\u5f31\uff0c\u5374\u6765\u7ed9\u5979 \u9a7e\u8f66\u3002\u201d\u518d\u60f3\u8d77\u8fde\u6b66\u7384\u971c\u7684\u4e2b\u73af\u4e5f\u6562\u5927\u95f9\u82f1\u96c4\u5927\u4f1a\uff0c\u5bf9\u6b66\u7384\u971c\u7684\u6765\u5386\uff0c\u66f4\u89c9\u5f97\u795e\u79d8\u83ab\u6d4b \u4e86\u3002 \u3000\u3000\u90a3\u5c0f\u4e2b\u73af\u7b11\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u5446\u5446\u7684\u77aa\u7740\u9a6c\u5927\u53d4\u505a\u4ec0\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4e0d\u6562\u8bf7\u95ee\u5927\u53d4\u59d3 \u540d\uff1f\u201d\u90a3\u9a7e\u8f66\u7684\u9053\uff1a\u201c\u6211\u53eb\u9a6c\u5143\u901a\u3002\u201d\u674e\u9038\u9053\u58f0\uff1a\u201c\u591a\u8c22\u3002\u201d\u9a6c\u5143\u901a\u9053\uff1a\u201c\u4f60\u591a\u8c22\u6211\u505a \u4ec0\u4e48\uff1f\u4f60\u8be5\u591a\u8c22\u5c0f\u59d0\u3002\u201d\u6b66\u7384\u971c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u591a\u8c22\u4f60\u90a3\u5929\u6551\u4e86\u5a49\u513f\u554a\u3002\u674e\u516c\u5b50\uff0c\u4f60 \u4e5f\u8be5\u591a\u8c22\u660e\u73e0\u5462\uff0c\u8981\u4e0d\u662f\u5979\uff0c\u6628\u665a\u5728\u5ce8\u5d4b\u91d1\u9876\uff0c\u4f60\u7684\u670b\u53cb\u53ea\u6015\u96be\u4ee5\u9003\u8131\u5806\u5de8\u9f0e\u5de8\u7075\u4e4b\u638c \u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u53c8\u662f\u5fc3\u5934\u4e00\u8361\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u5a49\u513f\u5462\uff1f\u4f60\u4eec\u5c06\u5979\u600e\u4e48\u6837\u4e86\uff1f\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a \u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4eec\u6ca1\u6709\u4f24\u7740\u5979\u534a\u4e1d\u6beb\u53d1\uff0c\u4f60\u5f53\u771f\u4ee5\u4e3a\u6211\u4eec\u662f\u6740\u4eba\u4e0d\u7728\u773c\u7684\u9b54\u5934\u4e48\uff1f\u201d\u674e\u9038 \u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u6211\uff0c\u6211\u60f3\u2026\u2026\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u60f3\u77e5\u9053\u5979\u7684\u4e0b\u843d\u4e48\uff1f\u201d\u674e\u9038\u5fae\u5fae \u9894\u9996\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u5979\u6ca1\u6709\u544a\u8bc9\u6211\uff0c\u4e0d\u8fc7\u6211\u5374\u731c\u5230\u51e0\u5206\uff0c\u5979\u662f\u53bb\u884c\u523a\u5929\u540e\u53bb\u4e86\uff01\u201d\u674e\u9038 \u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u5979\u4ee5\u524d\u66fe\u5728\u6211\u7684\u5bb6\u4e2d\u544a\u8bc9\u6211\u7684\uff0c\u5979\u8fd8\u592e \u6c42\u6211\u5e2e\u52a9\u5979\u884c\u523a\u5979\u5462\u3002\u201d\u8bf4\u7f62\u62bf\u5634\u4f4e\u7b11\u3002 \u3000\u3000\u674e\u9038\u53c8\u662f\u60ca\u9a87\u53c8\u662f\u62c5\u5fc3\uff0c\u4f46\u89c9\u5468\u8eab\u9aa8\u9abc\u53c8\u662f\u9690\u9690\u4f5c\u75db\u3002\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u6211\u867d\u7136\u53ea\u89c1 \u8fc7\u5a49\u513f\u4e00\u9762\uff0c\u5374\u5df2\u6df1\u77e5\u5979\u7684\u6027\u60c5\u3002\u5979\u82e5\u7136\u89c1\u4e86\u5929\u540e\uff0c\u5b9a\u662f\u5982\u9c7c\u5f97\u6c34\uff0c\u53ea\u6015\u5979\u62c5\u5fe7\u7684\u5012\u662f \u4f60\u554a\uff01\u201d\u674e\u9038\u6012\u9053\uff1a\u201c\u5a49\u51e0\u8eab\u8d1f\u56fd\u6068\u5bb6\u4ec7\uff0c\u96be\u9053\u8fd8\u4f1a\u89d1\u989c\u4e8b\u654c\u4e48\uff1f\u201d\u4e00\u751f\u6c14\u75db\u5f97\u66f4\u5389\u5bb3 \u4e86\u3002\u6b66\u7384\u971c\u9053\uff1a\u201c\u597d\u5427\uff0c\u672a\u6765\u4e4b\u4e8b\uff0c\u54b1\u4eec\u4e0d\u5fc5\u731c\u6d4b\uff0c\u4f60\u4e14\u559d\u53e3\u53c2\u6c64\u3002\u201d\u674e\u9038\u6b32\u5f85\u4e0d\u559d\uff0c \u4ed6\u8eab\u4f53\u4e0d\u80fd\u8f6c\u52a8\uff0c\u88ab\u6b66\u7384\u971c\u4e00\u634f\u4e0b\u5df4\uff0c\u5634\u5df2\u4e0d\u7531\u81ea\u4e3b\u7684\u5f20\u5f00\uff0c\u6b66\u7384\u971c\u5c06\u6ee1\u58f6\u53c2\u6c64\u90fd\u704c\u7ed9 \u4ed6\u996e\u4e86\uff0c\u996e\u5b8c\u4e4b\u540e\uff0c\u7761\u610f\u5927\u6d53\uff0c\u539f\u6765\u662f\u6b66\u7384\u971c\u6015\u4ed6\u601d\u8651\u52b3\u795e\uff0c\u5728\u53c2\u6c64\u4e2d\u6e17\u6709\u8c03\u795e\u5b89\u606f\u7684 \u836f\u672a\uff0c\u674e\u9038\u4e0d\u4e45\u5c31\u719f\u7761\u4e86\u3002 \u3000\u3000\u4e00\u89c9\u9192\u6765\uff0c\u5df2\u662f\u7b2c\u4e8c\u65e5\u6e05\u6668\uff0c\u6b66\u7384\u971c\u65e2\u4e0d\u548c\u4ed6\u8c08\u6b66\u5219\u5929\uff0c\u4e5f\u4e0d\u63d0\u8d77\u4e0a\u5b98\u5a49\u513f\uff0c\u53ea\u662f \u548c\u4ed6\u8c08\u8bba\u7434\u68cb\u8bd7\u753b\uff0c\u5251\u672f\u62f3\u7ecf\uff0c\u674e\u9038\u9887\u4e3a\u60ca\u8be7\u5979\u7684\u535a\u95fb\u5f3a\u8bb0\uff0c\u5fc3\u4e2d\u4ea6\u81ea\u6709\u611f\u4e8e\u5979\u7684\u6e29\u67d4 \u8c03\u62a4\uff0c\u5bf9\u5979\u6e10\u6709\u597d\u611f\uff0c\u8c08\u5f97\u751a\u662f\u6295\u673a\u3002\u5982\u662f\u8005\u8fc7\u4e86\u4e09\u65e5\uff0c\u6bcf\u65e5\u65e9\u5348\u665a\u4e09\u4e2a\u65f6\u8fb0\uff0c\u6b66\u7384\u971c \u90fd\u4ee5\u4e0a\u4e58\u5185\u529f\uff0c\u52a9\u4ed6\u57f9\u795e\u56fa\u672c\uff0c\u53bb\u6bd2\u7597\u4f24\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u674e\u9038\u5df2\u7ecf\u80fd\u591f\u5750\u8d77\u6765\u4e86\uff0c\u4ed6\u6570\u65e5\u4e0d\u89c1\u9633\u5149\uff0c\u5fcd\u4e0d\u4f4f\u63ed\u5f00\u8f66\u5e18\uff0c\u89c2\u770b\u5916\u9762\u7684\u666f \u8272\uff0c\u5ffd\u89c1\u4e24\u9a91\u5feb\u9a6c\uff0c\u8fce\u9762\u9762\u6765\uff0c\u5750\u5728\u9a6c\u4e0a\u7684\u662f\u4e00\u5bf9\u9752\u5e74\u7537\u5973\uff0c\u90a3\u7537\u7684\u5148\u5230\uff0c\u622a\u4f4f\u9aa1\u8f66\u5927 \u559d\u9053\uff1a\u201c\u8f66\u4e0a\u85cf\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u7ed9\u6211\u505c\u4e0b\uff01\u201d \u3000\u3000\u9a6c\u5143\u901a\u9053\uff1a\u201c\u4e0a\u5dee\u5c5e\u54ea\u4e2a\u8859\u95e8\uff1f\u53ef\u6709\u6d77\u6355\u6587\u4e66\uff0c\u6355\u724c\u4ee4\u7bad\uff1f\u201d\u90a3\u7c97\u8c6a\u5c11\u5e74\u6012\u9053\uff1a \u201c\u4f60\u7684\u773c\u775b\u778e\u4e86\uff0c\u6211\u4e43\u5927\u5510\u767e\u59d3\uff0c\u5c82\u662f\u5b98\u5e9c\u5974\u624d\uff1f\u201d\u6b66\u7384\u7bb1\u5bf9\u674e\u9038\u5fae\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4e24 \u4e2a\u6551\u9a7e\u7684\u6765\u4e86\u3002\u201d\u674e\u9038\u8d77\u521d\u4ee5\u4e3a\u662f\u54ea\u8def\u53c2\u52a0\u82f1\u96c4\u4eba\u4f1a\u800c\u6765\u8fdf\u7684\u4eba\uff0c\u671b\u53bb\u5374\u4e0d\u8ba4\u8bc6\uff0c\u751a\u4e3a \u7eb3\u7f55\u3002 \u3000\u3000\u9a6c\u5143\u901a\u9053\uff0c\u201c\u65e2\u7136\u90fd\u662f\u767e\u59d3\uff0c\u4f60\u4e3a\u4f55\u62e6\u963b\u6211\u7684\u9aa1\u8f66\uff1f\u201d\u90a3\u7c97\u8c6a\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u8f66\u4e2d\u85cf \u7740\u7684\u53ef\u4e0d\u662f\u767e\u59d3\uff01\u201d\u9a6c\u5143\u901a\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u85cf\u7684\u4ec0\u4e48\uff1f\u6211\u4e0d\u72af\u6cd5\uff0c\u4f60\u7ba1\u4e0d\u7740\uff01\u201d\u4ed6\u65e0\u6687\u7ea0 \u7f20\uff0c\u5530\u7684\u4e00\u97ad\uff0c\u50ac\u8f66\u8d76\u8def\uff0c\u90a3\u5c11\u5e74\u559d\u9053\uff1a\u201c\u6211\u504f\u8981\u7ba1\uff01\u201d\u500f\u7684\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u90a3 \u4e24\u5339\u62c9\u8f66\u7684\u5065\u58ee\u6bdb\u9aa1\u524d\u8e44\u5c48\u4e0b\uff0c\u5927\u8f66\u7adf\u7136\u4e0d\u80fd\u79fb\u52a8\u534a\u6b65\u3002 \u3000\u3000\u90a3\u5c0f\u4e2b\u73af\u62bf\u5634\u7b11\u9053\uff1a\u201c\u679c\u7136\u6709\u51e0\u65a4\u86ee\u529b\uff0c\u53ea\u662f\u8fd9\u4e00\u70b9\u672c\u9886\uff0c\u6211\u8fd8\u4e0d\u5c51\u4f38\u624b\u5462\uff01\u9a6c\u5927 \u53d4\u4f60\u5c06\u4ed6\u6253\u53d1\u4e86\u5427\uff01\u201d\u9a6c\u5143\u901a\u62a8\u52a8\u9a6c\u97ad\uff0c\u4e00\u97ad\u6253\u53bb\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u51ed\u7740\u8fd9\u70b9\u672c\u9886\uff0c \u5c45\u7136\u5c31\u6562\u5728\u767d\u65e5\u9752\u5929\uff0c\u505a\u62e6\u8def\u62a2\u52ab\u7684\u52fe\u5f53\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u97ad\u6a2a\u626b\u4e09\u8def\uff0c\u75be\u4f3c\u96f7\u9706\uff0c\u7136\u800c\u5374\u6ca1\u6709\u6253\u7740\u90a3\u4e2a\u5c11\u5e74\uff0c\u53ea\u89c1\u4ed6\u4e00\u4e2a\u9519\u6b65\u95ea\u8eab\uff0c\u5df2 \u62d4\u51fa\u4e00\u652f\u660e\u6643\u6643\u7684\u5229\u5251\uff0c\u5de6\u624b\u6765\u52fe\u9a6c\u5143\u901a\u7684\u624b\u8155\uff0c\u8981\u628a\u4ed6\u786c\u62c9\u4e0b\u9aa1\u8f66\uff0c\u53f3\u624b\u5229\u5251\u5219\u6325\u5411 \u4ed6\u7684\u9888\u9879\uff0c\u9a6c\u5143\u901a\u5927\u6012\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\u4e4e\u638c\u4e00\u7ffb\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\u5c06\u90a3\u5c11\u5e74\u9707\u9000\u4e09 \u6b65\uff0c\u98de\u8eab\u8df3\u4e0b\uff0c\u626c\u97ad\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u54b1\u4eec\u5c31\u597d\u597d\u7684\u6bd4\u5212\u4e00\u573a\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u66f4\u4e0d\u6253\u8bdd\uff0c\u5251\u8d77\u5904\uff0c\u4e00\u62db\u201c\u4e91\u9ebe\u4e09\u821e\u201d\uff0c\u4e0a\u523a\u54bd\u5589\uff0c\u4e2d\u6302\u4e24\u81c2\uff0c\u4e0b\u524a\u819d\u76d6\uff0c \u4e5f\u662f\u4e00\u62db\u4e09\u5f0f\uff0c\u8fde\u653b\u9a6c\u5143\u901a\u7684\u4e0a\u4e2d\u4e0b\u4e09\u8def\uff0c\u597d\u50cf\u662f\u4e3a\u4e86\u62a5\u590d\u9a6c\u5143\u901a\u521a\u624d\u6a2a\u626b\u4e09\u8def\u90a3\u4e00\u97ad \u4f3c\u7684\u3002\u9a6c\u5143\u901a\u90a3\u6761\u9a6c\u97ad\u7f20\u4ee5\u91d1\u4e1d\uff0c\u957f\u8fbe\u4e08\u8bb8\uff0c\u54d7\u5566\u5566\u6296\u5f97\u534a\u76f4\uff0c\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u84e6 \u7136\u95f4\u53cd\u624b\u4e00\u97ad\uff0c\u5237\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u90a3\u5c11\u5e74\u5251\u8d70\u8fde\u73af\uff0c\u4e0d\u5f85\u62db\u6570\u7528\u8001\uff0c\u8eab\u5b50\u65cb\u98ce\u822c\u7684\u968f\u7740\u97ad \u6084\u76f4\u8f6c\u51fa\u53bb\uff0c\u90a3\u97ad\u79bb\u4ed6\u51e0\u5bf8\uff0c\u4ea6\u662f\u6ca1\u6709\u6253\u7740\uff01\u53ea\u89c1\u4ed6\u5251\u8bc0\u4e00\u987f\uff0c\u7acb\u5373\u8d70\u504f\u950b\u659c\u4e0a\uff0c\u8fd8\u4e86 \u4e00\u62db\u201c\u767d\u86c7\u5410\u4fe1\u201d\uff0c\u5251\u5c16\u987a\u7740\u97ad\u68a2\u800c\u4e0b\u3001\u5f84\u524a\u9a6c\u5143\u901a\u7684\u624b\u6307\u3002 \u3000\u3000\u9a6c\u5143\u901a\u90a3\u80fd\u5bb9\u4ed6\u5f97\u901e\uff0c\u8eab\u5f62\u4e00\u7ffb\uff0c\u5012\u7eb5\u51fa\u516b\u5c3a\u5f00\u5916\uff0c\u4f7f\u51fa\u201c\u56de\u98ce\u626b\u67f3\u201d\u7684\u7edd\u6280\uff0c\u5237 \u5237\u5237\u97ad\u58f0\u75be\u54cd\uff0c\u5377\u8d77\u4e86\u4e00\u56e2\u5c18\u96fe\uff0c\u97ad\u957f\u5251\u77ed\uff0c\u5927\u5360\u4fbf\u5b9c\uff0c\u53ef\u662f\u90a3\u5c11\u5e74\u80c6\u5927\u975e\u5e38\uff0c\u4e00\u4e2a\u584c \u8eab\uff0c\u8ba9\u90a3\u6761\u957f\u97ad\u5728\u4ed6\u80cc\u4e0a\u6ef4\u6e9c\u6e9c\u7684\u5377\u8fc7\uff0c\u8d81\u7740\u9a6c\u5143\u901a\u7684\u8f6f\u97ad\u672a\u66fe\u6536\u56de\u4e4b\u9645\uff0c\u5c45\u7136\u7528\u638c\u80cc \u5fae\u6258\u97ad\u8eab\uff0c\u5251\u950b\u53cd\u5c55\uff0c\u7acb\u523b\u53c8\u6cbf\u7740\u957f\u97ad\u659c\u524a\u8fdb\u53bb\uff0c\u4e24\u4eba\u4ee5\u653b\u5bf9\u653b\uff0c\u957f\u97ad\u77ed\u5251\uff0c\u5404\u6709\u64c5\u957f\uff0c \u7adf\u81ea\u6253\u4e86\u4e2a\u96be\u5206\u96be\u89e3\u3002 \u3000\u3000\u674e\u9038\u659c\u501a\u9760\u57ab\uff0c\u4ece\u8f66\u5e18\u5f00\u7f1d\u5916\u671b\u51fa\u53bb\uff0c\u5ffd\u5730\u5fc3\u5934\u5fae\u51db\uff1a\u8fd9\u5251\u6cd5\u597d\u719f\uff0c\u597d\u50cf\u4ee5\u524d\u89c1\u8c01 \u4f7f\u8fc7\u4f3c\u7684\uff0c\u6b63\u5728\u601d\u7d22\uff0c\u5ffd\u542c\u5f97\u6b66\u7384\u971c\u5429\u5490\u90a3\u5c0f\u4e2b\u73af\u9053\uff1a\u201c\u660e\u73e0\uff0c\u4f60\u4e0b\u53bb\u5c06\u4ed6\u4eec\u5206\u5f00\uff0c\u95ee \u95ee\u8fd9\u4e24\u4e2a\u4eba\uff0c\u95ee\u4ed6\u4eec\u4e0e\u957f\u5b59\u5747\u91cf\u662f\u600e\u4e48\u4e2a\u79f0\u547c\uff1f\u201d\u3000\u3000 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u541f\u5230\u6069\u4ec7\u5fc3\u4e8b\u6d8c \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e5d\u56de\u3000\u541f\u5230\u6069\u4ec7\u5fc3\u4e8b\u6d8c \u3000\u3000\u674e\u9038\u7fdf\u7136\u9192\u8d77\uff0c\u4ed6\u66fe\u89c1\u8fc7\u4e0a\u5b98\u5a49\u513f\u4f7f\u8fd9\u8def\u5251\u6cd5\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u773c\u719f\u3002\u4e0a\u5bab\u5a49\u513f\u662f\u957f\u5b59 \u5747\u91cf\u7684\u5f92\u5f1f\uff0c\u8fd9\u5c11\u5e74\u80fd\u591f\u4f7f\u8fd9\u8def\u5251\u6cd5\uff0c\u5f53\u7136\u4e5f\u662f\u548c\u957f\u5b59\u5747\u91cf\u5927\u6709\u5173\u7cfb\u7684\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u9a6c\u5143\u901a\u6b63\u4f7f\u5230\u4e00\u62db\u201c\u4e91\u9f99\u5165\u6d77\u201d\uff0c\u97ad\u52bf\u6307\u4e1c\u6253\u897f\uff0c\u82e5\u865a\u82e5\u5b9e\uff0c\u90a3\u5c11\u5e74\u6b3a\u8eab\u731b\u653b\uff0c \u4e00\u4e2a\u758f\u795e\uff0c\u7adf\u7ed9\u8f6f\u97ad\u7f20\u7740\u5251\u67c4\uff0c\u9a6c\u5143\u901a\u6b63\u5f85\u5c06\u4ed6\u7684\u957f\u5251\u7529\u51fa\u624b\u53bb\uff0c\u53ef\u662f\u90a3\u5c11\u5e74\u795e\u529b\u60ca\u4eba\uff0c \u53cc\u8db3\u9489\u7262\u5730\u4e0a\uff0c\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u9a6c\u5143\u901a\u53cd\u800c\u7ed9\u4ed6\u62c9\u4e0a\u4e09\u6b65\uff0c\u4ed6\u7684\u5251\u950b\u4fbf\u6cbf\u7740\u97ad\u68a2\u5f84\u524a\u9a6c\u5143\u901a \u7684\u624b\u6307\uff0c\u8fd9\u4e00\u4e0b\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u5927\u5360\u4e0a\u98ce\u3002\u4f46\u9a6c\u5143\u901a\u8eab\u7ecf\u767e\u6218\uff0c\u7ecf\u9a8c\u6bd4\u90a3\u5c11\u5e74\u4e30\u5bcc\u5f97\u591a\uff0c\u4e00 \u89c1\u4e0d\u5999\uff0c\u97ad\u6084\u4e00\u6296\uff0c\u4e49\u7f20\u4e0a\u90a3\u5c11\u5e74\u7684\u624b\u8155\uff0c\u52d2\u5f97\u4ed6\u7684\u8155\u9aa8\u75db\u5982\u5200\u5272\uff0c\u5f7c\u6b64\u50f5\u6301\uff0c\u8c01\u90fd\u4e0d \u613f\u653e\u624b\uff0c\u90a3\u5c11\u5e74\u56fa\u7136\u76ae\u4f24\u9aa8\u75db\uff0c\u4f46\u4ed6\u7684\u5229\u5251\u5bf8\u5bf8\u4e0a\u79fb\uff0c\u9a6c\u5143\u901a\u582a\u582a\u5c31\u8981\u7ed9\u4ed6\u523a\u7740\uff0c\u53cc\u65b9 \u90fd\u662f\u60ca\u9669\u975e\u5e38\uff01 \u3000\u3000\u90a3\u5c0f\u4e2b\u73af\u4e00\u770b\u6b63\u662f\u65f6\u5019\uff0c\u5a07\u58f0\u4e00\u7b11\uff0c\u98de\u8eab\u60ca\u4e0b\uff0c\u77ed\u5251\u4e00\u6311\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u4e0e\u5c11\u5e74\u540c\u6765 \u7684\u90a3\u4e2a\u5973\u5b50\u4ea6\u81ea\u98de\u8eab\u63a0\u8d77\uff0c\u4e24\u4eba\u51e0\u4e4e\u662f\u540c\u65f6\u5230\u8fbe\uff0c\u4f46\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u9a6c\u5143\u901a\u7684\u91d1\u4e1d \u8f6f\u97ad\u7ed9\u90a3\u5c11\u5973\u524a\u53bb\u4e86\u4e00\u622a\uff0c\u90a3\u5c11\u5e74\u7684\u957f\u5251\u6ce2\u5c0f\u4e2b\u73af\u7684\u77ed\u5251\u4e00\u7c98\u4e00\u5f15\uff0c\u501f\u529b\u6253\u529b\uff0c\u767b\u65f6\u4e5f \u7acb\u8db3\u4e0d\u7a33\uff0c\u88ab\u5979\u201c\u5e26\u201d\u52a8\uff0c\u659c\u8dc3\u4e09\u6b65\uff0c\u8fd9\u624d\u5b9a\u5f97\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u90a3\u5c11\u5973\u671b\u4e86\u6b66\u7384\u971c\u7684\u4e2b\u73af\u4e00\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5524\u4f60\u5bb6\u7684\u5927\u4eba\u51fa\u6765\u3002\u201d\u5c0f\u4e2b\u73af\u7b11\u9053\uff1a \u201c\u5175\u5bf9\u5175\uff0c\u5c06\u5bf9\u5c06\uff0c\u4f60\u8d62\u5f97\u4e86\u6211\uff0c\u518d\u89c1\u6211\u5bb6\u5c0f\u59d0\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5927\u662f\u4e0d\u5c51\u3002\u90a3 \u5c11\u5973\u79c0\u7709\u4e00\u6311\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u6765\u5427\uff0c\u6211\u5927\u4f60\u5c0f\uff0c\u6211\u5148\u8ba9\u4f60\u4e09\u62db\uff01\u201d\u5979\u662f\u540d\u95e8\u95fa \u79c0\uff0c\u5fc3\u4e2d\u867d\u6012\uff0c\u795e\u8272\u4e0a\u4ecd\u751a\u77dc\u6301\u3002 \u3000\u3000\u90a3\u5c0f\u4e2b\u73af\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6211\u4e0d\u6597\u65e0\u540d\u4e4b\u8f88\uff0c\u5f97\u5148\u95ee\u95ee\u4f60\u7684\u6765\u5386\uff0c\u4f60\u662f\u957f\u5b59\u5747\u91cf\u7684\u4ec0\u4e48 \u4eba\uff1f\u201d\u90a3\u5c11\u5973\u88ab\u5979\u6fc0\u6012\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u865a\u5288\u4e00\u62db\uff0c\u6307\u7740\u90a3\u5c0f\u4e2b\u73af\u9053\uff1a \u201c\u6211\u7236\u4eb2\u7684\u540d\u5b57\u5c82\u662f\u4f60\u53eb\u5f97\u7684\uff1f\u518d\u6cb9\u5634\u6ed1\u820c\uff0c\u6211\u53ef\u8981\u60e9\u6212\u4f60\u4e86\uff0c\u201d \u3000\u3000\u539f\u6765\u8fd9\u5bf9\u5c11\u5e74\u7537\u5973\u6b63\u662f\u957f\u5b59\u6cf0\u4e0e\u957f\u5b59\u58c1\u5144\u59b9\uff0c\u4ed6\u4eec\u7684\u7236\u4eb2\u957f\u5b59\u5747\u91cf\u95fb\u77e5\u8c37\u795e\u7fc1\u5728\u5ce8 \u5d4b\u91d1\u9876\u62db\u5f00\u82f1\u96c4\u5927\u4f1a\uff0c\u4ed6\u548c\u8c37\u795e\u7fc1\u4e43\u662f\u591a\u5e74\u65e7\u53cb\uff0c\u4e0d\u8fc7\u81ea\u4ed6\u9690\u5c45\u5251\u9601\u4e4b\u540e\uff0c\u5c31\u672a\u901a\u97f3\u8baf \u4e86\uff0c\u4ed6\u53ea\u56e0\u81ea\u5df1\u6b66\u529f\u672a\u66fe\u6062\u590d\uff0c\u4e0d\u4fbf\u524d\u5f80\uff0c\u4fbf\u6253\u53d1\u513f\u5973\u51fa\u9053\uff0c\u53bb\u62dc\u89c1\u8c37\u795e\u7fc1\uff0c\u4e5f\u597d\u8ba1\u4ed6 \u95e8\u5f00\u5f00\u773c\u754c\u3002\u4e24\u5144\u59b9\u52a8\u8eab\u7a0d\u8fdf\uff0c\u672a\u5230\u5ce8\u5d4b\uff0c\u82f1\u96c4\u5927\u4f1a\u5df1\u6563\u3002\u4ed6\u4eec\u5728\u9014\u4e2d\u9047\u5230\u4ece\u82f1\u96c4\u4f1a\u4e0a \u6e83\u8d25\u4e0b\u7c73\u7684\u4eba\uff0c\u5f97\u77e5\u82f1\u96c4\u4eba\u4f1a\u88ab\u4e00\u4e2a\u5c11\u5973\u6363\u6bc1\uff0c\u5341\u5206\u60ca\u8be7\uff0c\u4f46\u4ed6\u4eec\u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u4fbf \u4e00\u8def\u8ffd\u8e2a\u4e0b\u53bb\uff0c\u60f3\u627e\u6b66\u4e48\u971c\u8f83\u91cf\uff0c\u8ffd\u5230\u53cc\u6d41\u53bf\u7684\u4e00\u4e2a\u5c0f\u9547\uff0c\u4ece\u4e00\u4e2a\u5ba2\u5e97\u4e3b\u4eba\u7684\u53e3\u4e2d\uff0c\u5f97 \u77e5\u6b66\u7384\u971c\u7684\u9aa1\u8f66\u6628\u65e5\u521a\u521a\u7ecf\u8fc7\uff0c\u4ed6\u4eec\u4e00\u542c\u5e97\u4e3b\u4eba\u7684\u63cf\u8ff0\uff0c\u4e0d\u4f46\u6b66\u7384\u971c\u7684\u5f62\u8c8c\u548c\u90a3\u4e9b\u4eba\u6240 \u8bf4\u7684\u6363\u6bc1\u82f1\u96c4\u5927\u4f1a\u7684\u5c11\u5973\u76f8\u7b26\uff0c\u800c\u4e14\u8f66\u4e2d\u5367\u75c5\u7684\u5c11\u5e74\uff0c\u4e5f\u50cf\u662f\u4ed6\u4eec\u6240\u8bf4\u7684\u90a3\u4f4d\u738b\u5b59\u674e\u9038\u3002 \u4e24\u5144\u59b9\u6025\u5fd9\u5feb\u9a6c\u8ffd\u8d76\uff0c\u8ffd\u4e86\u4e24\u5929\uff0c\u624d\u5728\u6b64\u5730\u76f8\u9047\u3002 \u3000\u3000\u957f\u5b59\u5144\u59b9\u521d\u6b21\u51fa\u9053\uff0c\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u4e00\u5fc3\u60f3\u4e0e\u6b66\u7384\u971c\u5927\u6597\u4e00\u573a\uff0c\u770b\u770b\u8fd9\u4e2a\u6363\u6bc1\u82f1\u96c4\u5927\u4f1a \u7684\u5973\u9b54\u5934\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u4e2a\u5389\u5bb3\u6cd5\uff1f\u54ea\u77e5\u6b66\u7384\u971c\u8fd8\u672a\u66fe\u9732\u9762\uff0c\u53ea\u4e00\u4e2a\u9a7e\u8f66\u7684\u4e61\u4e0b\u6c49\u4fbf\u4e0e\u957f \u5b59\u6cf0\u6253\u6210\u5e73\u624b\uff0c\u5982\u4eca\u5411\u957f\u5b59\u58c1\u6311\u6218\u7684\uff0c\u53c8\u53ea\u662f\u4e00\u4e2a\u7a1a\u9f84\u7684\u5c0f\u4e2b\u9b1f\uff0c\u800c\u4e14\u8fd9\u4e2a\u5c0f\u4e2b\u73af\u8fd8\u5927 \u8a00\u708e\u708e\uff0c\u72c2\u50b2\u975e\u5e38\u3002 \u3000\u3000\u957f\u5b59\u58c1\u6309\u7740\u6012\u6c14\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8ba9\u4f60\u4e09\u62db\uff0c\u8d76\u5feb\u52a8\u624b\u3002\u201d\u90a3\u5c0f\u4e2b\u73af\u4e00\u58f0\u5a07\u7b11\uff0c\u53eb\u9053\uff1a \u201c\u597d\u5440\uff0c\u90a3\u4e48\u5c0f\u5a62\u5b50\u8ba8\u6253\u6765\u4e86\uff01\u201d\u8fd9\u4e43\u662f\u9488\u5bf9\u957f\u5b59\u58c1\u521a\u624d\u8bf4\u8981\u60e9\u6212\u5979\u7684\u8bdd\u800c\u8a00\uff0c\u957f\u5b59\u58c1 \u67f3\u7709\u4e00\u6311\uff0c\u624b\u6309\u5251\u628a\uff0c\u9661\u7136\u95f4\uff0c\u4f46\u89c1\u773c\u524d\u7ea2\u971e\u75be\u6d8c\uff0c\u7ef8\u5f71\u7ffb\u98de\uff0c\u90a3\u5c0f\u4e2b\u73af\u7528\u4e00\u6761\u7ef8\u5e26\u4f5c \u4e3a\u5175\u5668\uff0c\u84e6\u7136\u5728\u5230\uff0c\u957f\u5b59\u58c1\u5403\u4e86\u4e00\u60ca\uff0c\u9053\u58f0\uff1a\u201c\u597d\u5feb\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u968f\u7740\u706f\u7ef8\u98d8\u51fa\u4e8c\u5927 \u4ee5\u5916\u3002\u90a3\u5c0f\u4e2b\u9b1f\u811a\u5c16\u4e00\u70b9\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u53a9\u5251\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u7acb\u5373\u8ddf\u8e2a\u523a\u4e0b\uff0c\u5251\u5149\u4eba\u5f71\u4e4b \u4e2d\uff0c\u4f46\u542c\u5f97\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u77e9\u5251\u5c06\u8def\u8fb9\u7684\u4e00\u682a\u6811\u679d\u524a\u65ad\u4e86\uff01 \u3000\u3000\u90a3\u5c0f\u4e2b\u73af\u8fde\u53d1\u4e24\u62db\uff0c\u90fd\u88ab\u957f\u5b59\u58c1\u7528\u8f7b\u5de7\u7684\u8eab\u6cd5\u907f\u5f00\uff0c\u4e5f\u662f\u5403\u60ca\u975e\u5c0f\uff0c\u5979\u6740\u5f97\u6027\u8d77\uff0c \u7ea2\u7ef8\u4e00\u7ffb\u5026\uff0c\u77ed\u5251\u56de\u65cb\u53cd\u524a\uff0c\u4e00\u67d4\u4e00\u521a\uff0c\u4e00\u62db\u4e4b\u4e2d\uff0c\u542b\u6709\u4e24\u79cd\u622a\u7136\u4e0d\u540c\u7684\u5bb6\u6570\uff0c\u4e8e\u662f\u5979 \u4ece\u6b66\u7384\u971c\u521a\u5b66\u4f1a\u7684\u4e00\u62db\u6700\u5f97\u610f\u7684\u62db\u6570\uff0c\u957f\u5b59\u58c1\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u60ca\u9e3f\u63a0\u71d5\u822c\u7684\u7ed5\u5230 \u90a3\u5c0f\u4e2b\u73af\u80cc\u540e\uff0c\u90a3\u5c0f\u4e2b\u73af\u4f3c\u4e4e\u65e9\u5df2\u6599\u5230\u5979\u6709\u6b64\u4e00\u7740\uff0c\u77ed\u5251\u672a\u66fe\u653e\u5c3d\uff0c\u500f\u7136\u95f4\u5f80\u540e\u4e00\u60ca\uff0c \u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u7adf\u628a\u957f\u5b59\u58c1\u5934\u4e0a\u7684\u51e4\u9497\u524a\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u674e\u9038\u770b\u5f97\u624b\u5fc3\u634f\u4e86\u628a\u6c57\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u770b\u5728\u6211\u7684\u4efd\u4e0a\uff0c\u4e0d\u8981\u4f24\u5bb3\u4ed6\u4eec\u3002\u201d\u8bdd\u4e00 \u51fa\u53e3\uff0c\u5ffd\u5730\u60f3\u8d77\u6b66\u7384\u971c\u4e43\u662f\u81ea\u5df1\u7684\u654c\u4eba\uff0c\u6709\u4f55\u201c\u60c5\u4efd\uff1f\u201d\u4e0d\u7981\u9762\u4e0a\u4e00\u7ea2\uff0c\u6b66\u7384\u971c\u4f3c\u662f\u6ca1 \u6709\u5bdf\u89c9\uff0c\u5ae3\u7136\u7b11\u9053\uff1a \u3000\u3000\u201c\u660e\u73e0\u8fd9\u56de\u78b0\u5230\u5bf9\u624b\u4e86\uff0c\u59b9\u59b9\u7684\u6b66\u529f\u6bd4\u54e5\u54e5\u597d\u5f97\u591a\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u53c8\u60ca\u53c8\u6012\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u9752\u94a2\u5251\u8131\u9798\u800c\u51fa\uff0c\u7acb\u5373\u4e00\u62db\u201c\u76f4\u6307\u5929\u5357\u201d\uff0c\u5251\u5149\u5982\u7ec3\uff0c \u95ea\u7535\u523a\u53bb\uff0c\u90a3\u5c0f\u4e2b\u73af\u8fd8\u4e86\u4e00\u62db\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u8bf4\u9053\uff0c\u201c\u627f\u8ba9\u4e09\u62db\uff0c\u4f69\u670d\u4f69\u670d\uff01\u201d\u5979\u80f8\u65e0 \u57ce\u5e9c\uff0c\u8fd9\u8bdd\u4e43\u662f\u51fa\u81ea\u771f\u5fc3\u3002\u539f\u6765\u5979\u8d77\u521d\u89c1\u957f\u5b59\u6cf0\u7684\u8eab\u5f62\u8fdf\u6ede\uff0c\u53ea\u9053\u59b9\u59b9\u4ea6\u4e0d\u8fc7\u5982\u662f\uff0c\u5979 \u81ea\u5e7c\u8ddf\u968f\u6b66\u7384\u971c\uff0c\u4ee5\u6b66\u7384\u971c\u7684\u672c\u9886\u4f5c\u4e3a\u6807\u51c6\uff0c\u773c\u754c\u81ea\u662f\u751a\u9ad8\uff0c\u6545\u6b64\u4e00\u5f00\u5934\u4fbf\u51fa\u8a00\u8ba5\u7b11\uff0c \u5012\u5e76\u4e0d\u662f\u5979\u7d20\u6027\u9a84\u72c2\u7684\u3002 \u3000\u3000\u5c0f\u4e2b\u9b1f\u867d\u662f\u771f\u5fc3\u79f0\u8d5e\uff0c\u957f\u5b59\u58c1\u542c\u6765\u5374\u8db3\u523a\u8033\u5f97\u5f88\uff0c\u5f53\u4e0b\u542b\u55d4\u4e0d\u8bed\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff01\u53c8 \u662f\u8fde\u73af\u4e09\u5251\uff0c\u5979\u7684\u7236\u4eb2\u957f\u5b59\u5747\u91cf\u4e0e\u8c37\u795e\u7fc1\u5c09\u8fdf\u70af\u9f50\u540d\uff0c\u4e43\u5144\u5f53\u4e16\u4e09\u5927\u5251\u672f\u540d\u5bb6\u4e4b\u4e00\uff0c\u957f \u5b59\u58c1\u5fc3\u7075\u624b\u654f\uff0c\u9664\u4e86\u6c14\u529b\u4e0d\u53ca\u54e5\u54e5\u4e4b\u5916\uff0c\u8f7b\u529f\u548c\u5251\u6cd5\u90fd\u6bd4\u54e5\u54e5\u9ad8\u660e\u5f97\u591a\uff0c\u8fd9\u4e09\u5251\u4e00\u5251\u7d27 \u4f3c\u4e00\u5251\uff0c\u7aef\u7684\u5251\u52bf\u5982\u8679\uff0c\u53d8\u5316\u65e0\u65b9\u3002\u90a3\u5c0f\u4e2b\u73af\u597d\u80dc\u4e4b\u5fc3\u52c3\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u521a\u624d\u6211\u4f7f\u5230\u7b2c\u56db \u62db\u624d\u524a\u65ad\u4f60\u7684\u51e4\u9497\uff0c\u8fd9\u4e2a\u4e0d\u7b97\uff0c\u54b1\u4eec\u5982\u4eca\u518d\u597d\u597d\u7684\u6bd4\u5212\u6bd4\u5212\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u51dd\u795e\u5f85\u654c\uff0c\u8fd9\u65f6\u5979\u54ea\u8fd8\u6562\u56e0\u4e3a\u5bf9\u65b9\u662f\u4e2a\u5c0f\u4e2b\u73af\u800c\u6709\u4e1d\u6beb\u8f7b\u89c6\uff1f\u4f46\u89c1\u90a3\u5c0f\u4e2b\u73af \u5c06\u7ef8\u5e26\u6296\u5f97\u7b14\u76f4\uff0c\u5982\u7bad\u5c04\u6765\uff0c\u5c06\u8fd1\u8eab\u524d\u3002\u84e6\u7136\u4e00\u7ffb\u4e00\u5377\uff0c\u5f53\u6210\u8f6f\u97ad\u6765\u4f7f\uff0c\u957f\u5b59\u58c1\u4f7f\u51fa \u201c\u98de\u9e1f\u6295\u6797\u201d\u7684\u8eab\u6cd5\uff0c\u56de\u8eab\u4e00\u60ca\uff0c\u8863\u8896\u4e00\u62c2\uff0c\u5c06\u5c0f\u4e2b\u73af\u7684\u7ea2\u7ef8\u62c2\u5f00\uff0c\u5251\u8bc0\u4e00\u987f\uff0c\u767b\u65f6\u4e00 \u62db\u201c\u7389\u5973\u7a7f\u9488\u201d\uff0c\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u5251\u5c16\u523a\u5230\u4e86\u5c0f\u4e2b\u73af\u80a9\u540e\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u9093\u5c0f\u4e2b\u73af\u4e00\u4e2a\u8f66\u8eab\uff0c \u7ea2\u7ef8\u6296\u8d77\u4e86\u4e00\u9053\u5f69\u8679\uff0c\u5c06\u957f\u5b59\u58c1\u7684\u5251\u950b\u5f15\u5f00\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u62db\u201c\u4ed9\u4eba\u6362\u5f71\u201d\uff0c\u5251\u5149\u95f2\u95ea\uff0c \u7ef8\u5f71\u98d8\u98d8\uff0c\u76f4\u628a\u501a\u5728\u8f66\u524d\u7684\u9a6c\u5143\u901a\u90fd\u770b\u5f97\u5934\u660f\u76ee\u7729\u3002\u957f\u5b59\u6cf0\u5931\u58f0\u53eb\u9053\uff1a \u3000\u3000\u201c\u59b9\u59b9\uff0c\u5c0f\u5fc3\uff01\u201d\u4f46\u89c1\u957f\u5b59\u58c1\u5c55\u5f00\u5979\u5728\u5251\u9601\u4e0a\u5b66\u5f97\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u968f\u7740\u7ea2\u7ef8\u98d8\u95ea\uff0c\u8fd0 \u5251\u5982\u98ce\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u8fde\u73af\u653b\u4e86\u4e03\u516b\u8bb0\u7cbe\u5999\u7684\u5251\u62db\uff01 \u3000\u3000\u674e\u9038\u8eba\u5728\u8f66\u4e2d\uff0c\u4f46\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u604d\u82e5\u7e41\u5f26\u6025\u7ba1\uff0c\u4ece\u5e18\u5185\u7aa5\u51fa\uff0c\u5df2\u662f\u4e0d\u5927\u60c5\u662f\uff0c \u7981\u4e0d\u4f4f\u5750\u8d77\u8eab\u6765\uff0c\u63ed\u5f00\u4e86\u8f66\u5e18\uff0c\u6b66\u7384\u971c\u5ffd\u5730\u5fae\u5fae\u4e00\u7b11\uff0c\u4e00\u624b\u6309\u5728\u4ed6\u7684\u80f8\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u518d \u8fc7\u56db\u5929\uff0c\u4f60\u4fbf\u53ef\u4ee5\u8d77\u8eab\u884c\u8d70\u4e86\uff0c\u4f55\u5fc5\u5fc3\u6025\u3002\u201d\u674e\u9038\u4e00\u770b\uff0c\u65e5\u5f71\u5f53\u4e2d\uff0c\u6b66\u7384\u971c\u6bcf\u5929\u65e9\u5348\u665a \u4e09\u4e2a\u65f6\u8fb0\uff0c\u90fd\u8981\u6309\u65f6\u6309\u523b\u4e3a\u4ed6\u63a8\u62ff\u7597\u6cbb\uff0c\u6b64\u9645\u6b63\u662f\u6b63\u5348\u65f6\u5206\uff0c\u53c8\u8be5\u662f\u8fd0\u6c14\u7597\u4f24\u7684\u65f6\u5019\u4e86\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u957f\u5b59\u6cf0\u4e00\u773c\u77a5\u89c1\u4e86\u6b66\u7384\u971c\u4e0e\u674e\u9038\u4e8c\u4eba\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6bbf\u4e0b\u5bbd\u5fc3\uff0c\u957f\u5b59\u6cf0 \u63a5\u9a7e\u6765\u4e86\uff01\u201d\u75be\u5954\u800c\u4e0b\uff0c\u6b66\u7384\u971c\u501a\u7740\u8f66\u8fb9\uff0c\u9732\u51fa\u4e0a\u534a\u622a\u8eab\u4e8e\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u53eb\u4f60\u7684\u59b9\u59b9 \u4e00\u9f50\u4e0a\u6765\uff0c\u660e\u73e0\uff0c\u4f60\u4e0d\u662f\u957f\u5b59\u5c0f\u59d0\u7684\u5bf9\u624b\uff0c\u9000\u4e0b\u53bb\u5427\uff01\u201d\u8bdd\u58f0\u672a\u505c\u6b62\uff0c\u957f\u5b59\u58c1\u4e0d\u5f85\u90a3\u5c0f \u4e2b\u73af\u9000\u8ba9\uff0c\u65e9\u5df2\u4e00\u62db\u201c\u795e\u9f99\u6389\u5c3e\u201d\uff0c\u5c06\u5979\u8feb\u5f00\uff0c\u5144\u59b9\u4fe9\u4e00\u5148\u4e00\u540e\uff0c\u53cc\u53cc\u5954\u81f3\uff01 \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u4ee4\u5c0a\u7fc1\u5251\u672f\u540d\u95fb\u5929\u535c\uff0c\u96be\u5f97\u76f8\u9022\uff0c\u8bf7\u8d24\u5144\u59b9\u5c3d\u91cf\u65bd\u5c55\uff0c\u8ba9\u6211\u5f00\u5f00\u773c \u754c\uff01\u201d\u957f\u5b59\u6cf0\u60f3\u4e0d\u5230\u6b66\u7384\u971c\u7adf\u662f\u8fd9\u6837\u7f8e\u8c8c\u7684\u5c11\u5973\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u89c1\u5979\u6f2b\u4e0d\u7ecf\u610f\u7684\u501a\u9996\u8f66 \u4e0a\u7684\u680f\u68da\uff0c\u53ea\u6709\u4e00\u652f\u7ea4\u7ea4\u7389\u624b\u5782\u5728\u8f66\u5916\uff0c\u90a3\u795e\u6c14\u7adf\u662f\u6beb\u4e0d\u628a\u81ea\u5df1\u653e\u5728\u5fc3\u4e0a\uff0c\u4e0d\u7981\u6012\u6c14\u9661 \u751f\uff0c\u5927\u58f0\u559d\u9053\uff0c\u201c\u4f60\u4e0b\u8f66\u6765\uff0c\u54b1\u4eec\u8f83\u91cf\u8f83\u91cf\uff01\u201d\u6b66\u7384\u971c\u6301\u5251\u5728\u624b\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8981\u770b\u62a4\u75c5 \u4eba\uff0c\u6055\u4e0d\u80fd\u4e0b\u8f66\u5949\u966a\uff0c\u8bf7\u8d24\u5144\u59b9\u4e0a\u6765\u5427\u3002\u201d\u957f\u5b59\u58c1\u7acb\u5373\u51cc\u7a7a\u8dc3\u8d77\uff0c\u9752\u94a2\u5251\u633d\u4e86\u4e00\u6735\u5251\u82b1\uff0c \u8fce\u9762\u523a\u6765\uff1b\u957f\u5b59\u6cf0\u5de6\u81c2\u4e00\u4f38\uff0c\u4fbf\u8981\u628a\u6b66\u7384\u971c\u62c9\u4e0b\uff0c\u6b66\u7384\u971c\u4e00\u58f0\u957f\u7b11\uff0c\u5251\u950b\u500f\u7684\u5411\u4e0a\u4e00\u64a9\uff0c \u968f\u5373\u5012\u8f6c\u5251\u67c4\u5f80\u4e0b\u4e00\u649e\uff0c\u957f\u5b59\u58c1\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u597d\u4f3c\u65ad\u7ebf\u98ce\u7b5d\u822c\u7684\u8dcc\u4e86\u4e0b\u6765\uff0c \u957f\u5b59\u6cf0\u5374\u95ea\u907f\u4e0d\u5f00\uff0c\u80c1\u4e0b\u88ab\u5979\u7684\u5251\u67c4\u4e00\u649e\uff0c\u534a\u8fb9\u8eab\u5b50\u767b\u65f6\u9ebb\u8f6f\u3002\u4e24\u5144\u59b9\u53c8\u60ca\u53c8\u6012\uff0c\u957f\u5b59 \u6cf0\u5438\u4e86\u53e3\u6c14\uff0c\u559d\u9053\uff1a\u201c\u770b\u62db\uff01\u201d\u4e24\u67c4\u957f\u5251\u5954\u96f7\u95ea\u7535\u822c\u7684\u6740\u5230\uff0c\u6b66\u7384\u971c\u77ed\u5251\u4e00\u5f15\uff0c\u957f\u5b59\u6cf0 \u4e00\u5251\u5288\u53bb\uff0c\u521a\u597d\u4e0e\u957f\u5b59\u58c1\u7684\u9752\u94a2\u5251\u76f8\u4ea4\u3002 \u3000\u3000\u957f\u5b59\u58c1\u7ed9\u4ed6\u54e5\u54e5\u7684\u731b\u529b\u9707\u9000\u4e09\u6b65\uff0c\u957f\u5b59\u6cf0\u4e5f\u51e0\u4e4e\u7acb\u8db3\u4e0d\u7a33\u3002 \u3000\u3000\u957f\u5b59\u58c1\u77aa\u4e86\u5979\u54e5\u54e5\u4e00\u773c\uff0c\u8d34\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u7528\u5b54\u96c0\u5f00\u5c4f\uff1f\u201d\u8fd9\u4e43\u662f\u602a\u5979 \u54e5\u54e5\u9002\u624d\u51fa\u62db\u51fa\u9519\u4e86\uff0c\u58f0\u97f3\u8bf4\u5f97\u5f88\u8f7b\uff0c\u51fa\u4e8e\u59b9\u59b9\u4e4b\u53e3\uff0c\u5165\u4e8e\u54e5\u54e5\u4e4b\u8033\uff0c\u65c1\u4eba\u51b3\u8ba1\u4e0d\u80fd\u542c \u89c1\u3002\u4e0d\u6599\u4e00\u8a00\u752b\u6bd5\uff0c\u6b66\u7384\u971c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u8d24\u5144\u59b9\u5361\u66fe\u5c3d\u5c55\u6240\u957f\uff0c\u518d\u6765\u518d\u6765\uff01\u201d\u957f\u5b59\u6cf0\u81ca \u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u957f\u5251\u5411\u6b66\u7384\u971c\u4e00\u70b9\uff0c\u5237\u7684\u4fbf\u662f\u4e00\u62db\u201c\u5b54\u96c0\u65a4\u5c4f\u201d\uff0c\u957f\u5b59\u58c1\u4e5f\u62a2\u7740\u653b\u4e86\u4e00\u62db \u201c\u5f69\u98ce\u8212\u7fbd\u201d\uff0c\u53cc\u5251\u9f50\u5230\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u7aef\u7684\u597d\u4f3c\u51e4\u51f0\u5b54\u96c0\uff0c\u5f20\u5f00\u7fc5\u8180\u4e00\u822c\uff0c\u521a\u5065\u8bad\u5a1c\uff0c \u7f8e\u5999\u5f02\u5e38\uff01 \u3000\u3000\u6b66\u7384\u971c\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u987a\u624b\u62db\u67b6\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u4e24\u67c4\u5251\u540c\u65f6\u683c\u5f00\uff0c\u5de6\u624b\u4ecd \u7136\u8d34\u5728\u674e\u9038\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\u4e0a\uff0c\u8f7b\u8f7b\u7ed9\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c\u674e\u9038\u5fc3\u5934\u70e6\u4e71\uff0c\u771f\u6c14\u8fd0\u8f6c\uff0c\u7565\u611f \u4e0d\u8212\uff0c\u6b66\u7384\u971c\u5982\u6709\u6240\u89c9\uff0c\u4f4e\u4e0b\u5934\u6765\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u81ea\u5fc5\u770b\u5728\u4f60\u7684\u4efd\u4e0a\u3002\u201d \u8a00\u4e0b\u4e4b\u610f\uff0c\u4e43\u662f\u7b54\u5e94\u4ed6\u4e0d\u4f24\u5bb3\u957f\u5b59\u5144\u59b9\uff0c\u773c\u5149\u6e29\u67d4\u4e4b\u6781\uff0c\u674e\u9038\u5fc3\u5934\u4e00\u8361\uff0c\u4f46\u89c9\u4e00\u80a1\u70ed\u529b\uff0c \u4ece\u5979\u638c\u5fc3\u5f90\u5f90\u4f20\u5165\uff0c\u5bfc\u6c14\u901a\u5173\uff0c\u767b\u65f6\u5fc3\u80f8\u5b81\u8d34\uff0c\u8212\u670d\u65e0\u6bd4\u3002 \u3000\u3000\u957f\u5b59\u5144\u59b9\u89c1\u5979\u56de\u9996\u8f66\u4e2d\uff0c\u4f4e\u5934\u8bf4\u8bdd\uff0c\u867d\u7136\u770b\u4e0d\u89c1\u8f66\u4e2d\u4eba\u9762\uff0c\u4f46\u4ea6\u731c\u5f97\u5230\u5b9a\u662f\u674e\u9038\u65e0 \u7591\uff0c\u5fc3\u4e2d\u5747\u662f\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u6bbf\u4e0b\u7adf\u7136\u7ed9\u8fd9\u4e2a\u5996\u5a62\u8ff7\u60d1\u4e86\uff1f\u201d\u542c\u5979\u8bdd\u8bed\uff0c\u77a7\u5979\u795e\u6c14\uff0c \u7adf\u662f\u6ee1\u4e0d\u628a\u6bd4\u5251\u5f53\u505a\u4e00\u56de\u4e8b\u60c5\uff0c\u800c\u662f\u5fc3\u795e\u53e6\u6709\u6240\u5c5e\uff0c\u53ea\u987e\u7167\u6599\u8f66\u4e2d\u7684\u75c5\u4eba\u3002\u4e24\u5144\u59b9\u53c8\u6012 \u53c8\u6c14\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u5c55\u5f00\u6700\u8f66\u8fa3\u7684\u8fdb\u624b\u62db\u6570\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u53cc\u5251\u8fde\u73af\u6025\u653b\u3002\u6b66\u7384\u971c\u5934\u4e5f\u4e0d\u56de\uff0c \u53cc\u773c\u53ea\u662f\u51dd\u89c6\u674e\u9038\uff0c\u7528\u6e29\u67d4\u7684\u773c\u5149\u629a\u6170\u4ed6\uff0c\u552f\u6050\u4ed6\u88ab\u5916\u7269\u4e71\u4e86\u5fc3\u795e\uff0c\u4ee5\u81f3\u52a0\u91cd\u4f24\u52bf\u3002\u674e \u9038\u751a\u662f\u611f\u6fc0\uff0c\u6e10\u6e10\u5982\u53d7\u50ac\u7720\uff0c\u679c\u7136\u4e0d\u518d\u7406\u4f1a\u5979\u7684\u6bd4\u5251\uff0c\u987a\u7740\u5979\u624b\u5fc3\u4f20\u6765\u7684\u70ed\u529b\uff0c\u5f90\u5f90\u8fd0 \u6c14\uff0c\u4e0d\u8fc7\u4e00\u76cf\u8336\u65f6\u523b\uff0c\u4fbf\u5df2\u6c14\u901a\u767e\u7a74\uff0c\u900f\u8fc7\u91cd\u5173\uff0c\u6bd4\u5f80\u65e5\u53d7\u76ca\u66f4\u5927\u3002\u8fd9\u65f6\u4f38\u667a\u6e05\u5b81\uff0c\u5410 \u4e86\u53e3\u6c14\uff0c\u53cc\u76ee\u5f20\u5f00\uff0c\u4f46\u542c\u5f97\u5175\u5203\u76f8\u4ea4\u7684\u53ee\u5f53\u4e4b\u51fb\uff0c\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\u3002\u8f66\u53a2\u5916\u957f\u5b59\u5144\u59b9\u4e00\u5251 \u7d27\u4f3c\u4e00\u5251\uff0c\u653b\u5f97\u8d8a\u6765\u8d8a\u6025\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u8212\u4e86\u53e3\u6c14\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5348\u95f4\u7684\u529f\u8bfe\u5b8c\u4e86\u3002\u201d\u84e6\u7136\u56de\u8fc7\u4e86\u5934\uff0c\u5bf9\u957f\u5b59\u5144\u59b9\u7b11 \u9053\uff1a\u201c\u5ce8\u5d4b\u5251\u6cd5\uff0c\u679c\u662f\u9ad8\u660e\uff0c\u5c0f\u59b9\u9886\u6559\u8fc7\u4e86\uff0c\u4e24\u4f4d\u8bf7\u6b47\u6b47\u5427\u3002\u89c1\u5230\u5c0a\u7fc1\u4e4b\u65f6\uff0c\u8bf7\u7ed9\u6211\u95ee \u5019\u3002\u6211\u8fd8\u8981\u8d76\u8def\uff0c\u4e0d\u6562\u518d\u7559\u4e24\u4f4d\u7684\u5927\u9a7e\u4e86\u3002\u201d\u8bdd\u8bed\u4e00\u5b8c\uff0c\u52b2\u900f\u5251\u5c16\uff0c\u5f80\u4e0a\u4e00\u6843\uff0c\u94ee\u7684\u4e00 \u54cd\uff0c\u767b\u65f6\u628a\u957f\u5b59\u6cf0\u7684\u90a3\u67c4\u957f\u5251\u524a\u53bb\u4e86\u4e00\u622a\u3002\u957f\u5b59\u6cf0\u9762\u8272\u7070\u8d25\uff0c\u957f\u5b59\u58c1\u9661\u7684\u8f6c\u8eab\uff0c\u4e00\u8a00\u4e0d \u53d1\uff0c\u7acb\u5373\u8de8\u4e0a\u9a6c\u80cc\uff0c\u5237\u5237\u51e0\u97ad\uff0c\u50ac\u9a6c\u75be\u9a70\uff1b\u957f\u5b59\u6cf0\u5446\u4e86\u4e00\u5446\uff0c\u81ea\u611f\u65e0\u989c\uff0c\u8df3\u4e0a\u9a6c\u80cc\uff0c\u4e5f \u8ffd\u5979\u7684\u59b9\u59b9\u53bb\u4e86\u3002 \u3000\u3000\u674e\u9038\u5750\u8d77\u8eab\u6765\uff0c\u9760\u7740\u8f66\u53a2\uff0c\u76ee\u9001\u957f\u5b59\u5144\u59b9\u7edd\u5c18\u800c\u53bb\uff0c\u5fc3\u5934\u6709\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\u3002\u65e2\u611f\u6b66 \u7384\u971c\u7684\u67d4\u60c5\u4f3c\u6c34\uff0c\u53c8\u4ece\u957f\u5b59\u5144\u59b9\u60f3\u8d77\u4e86\u7687\u7956\u7684\u8001\u81e3\u957f\u5b59\u5747\u91cf\uff0c\u518d\u4ece\u957f\u5b59\u5747\u91cf\u60f3\u8d77\u4e86\u4e0a\u5b98 \u5a49\u513f\uff0c\u4eec\u89c9\u609f\u6000\u5386\u4e71\uff0c\u4e0d\u80fd\u81ea\u5df1\uff01 \u3000\u3000\u6b66\u7384\u971c\u66fc\u58f0\u541f\u9053\uff1a\u201c\u541b\u4e0d\u884c\u516e\u5937\u72b9\uff0c\u8e47\u8c01\u7559\u516e\u4e2d\u6d32\uff1f\u7f8e\u8981\u7707\u516e\u5b9c\u4fee\uff0c\u6c9b\u543e\u4e58\u516e\u6842\u821f\u3002\u201d \u501f\u300a\u695a\u8f9e\u300b\u4e2d\u300a\u6e58\u541b\u300b\u4e00\u7bc7\u7684\u8f9e\u610f\uff0c\u95ee\u4ed6\u6709\u4ec0\u4e48\u5fc3\u4e8b\u72b9\u8c6b\u4e0d\u524d\uff0c\u662f\u4e0d\u662f\u60f3\u5ff5\u4e00\u4f4d\u5999\u4e3d\u7684 \u4f73\u4eba\uff0c\u82e5\u662f\u90a3\u6837\uff0c\u5c31\u8be5\u50ac\u6842\u6728\u505a\u7684\u8239\u5feb\u8d70\u554a\u3002\u90a3\u5c0f\u4e2b\u9b1f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9a6c\u5927\u53d4\uff0c\u5feb\u8d76 \u8f66\uff01\u201d\u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c\u60ca\u8be7\u8fd9\u4e24\u4e3b\u5a62\u600e\u7684\u5982\u6b64\u806a\u660e\uff0c\u7adf\u597d\u50cf\u731c\u5230\u4e86\u81ea\u5df1\u7684\u5fc3\u4e8b\uff1f \u3000\u3000\u968f\u7740\u8f66\u8f6e\u7684\u8f6c\u52a8\uff0c\u674e\u9038\u7684\u5fc3\u60c5\u4e5f\u8d8a\u8f6c\u8d8a\u4e71\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u6211\u7684\u7434\u5462\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a \u201c\u7434\u5251\u65e0\u6059\uff0c\u90fd\u5728\u8fd9\u513f\u3002\u201d \u3000\u3000\u674e\u9038\u659c\u9760\u9526\u57ab\uff0c\u629a\u5f26\u6b4c\u9053\uff1a \u3000\u3000\u65e5\u5c45\u6708\u8bf8\uff0c \u3000\u3000\u80e1\u8fed\u800c\u5fae\uff1f \u3000\u3000\u5fc3\u4e4b\u5fe7\u77e3\uff0c \u3000\u3000\u5982\u532a\u701a\u8863\uff0c \u3000\u3000\u9759\u8a00\u601d\u4e4b\uff0c \u3000\u3000\u4e0d\u80fd\u594b\u98de\u3002 \u3000\u3000\u8fd9\u662f\u8bd7\u7ecf\u4e2d\u7684\u4e00\u7ae0\uff0c\u5199\u7684\u662f\u4e00\u4e2a\u80f8\u6000\u5927\u5fd7\u7684\u4eba\uff0c\u88ab\u7fa4\u5c0f\u6240\u5236\uff0c\u4e0d\u80fd\u594b\u98de\uff0c\u53c8\u4e0d\u7518\u9000 \u8ba9\uff0c\u6000\u7740\u6ee1\u8154\u5fe7\u90c1\uff0c\u65e0\u53ef\u544a\u8bed\uff0c\u56e0\u800c\u6709\u4e86\u8fd9\u4e00\u7bc7\u7f20\u7ef5\u5a49\u8f6c\u7684\u7533\u8bc9\uff0c\u82e5\u8bd1\u6210\u767d\u8bdd\u8bd7\u90a3\u610f\u601d \u5c31\u662f\uff1a\u201c\u95ee\u8fc7\u6708\u4eae\u95ee\u592a\u9633\uff0c\u4e3a\u4f55\u6709\u5149\u50cf\u65e0\u5149\uff1f\u5fc3\u4e0a\u70e6\u607c\u6d17\u4e0d\u51c0\uff0c\u597d\u50cf\u4e00\u5806\u810f\u8863\u88f3\u3002\u6211\u624b \u6309\u80f8\u819b\u7ec6\u7ec6\u60f3\uff0c\u600e\u80fd\u9ad8\u98de\u5c55\u7fc5\u8180\uff1f\u201d\u674e\u9038\u5f39\u8fd9\u7ae0\u8bd7\uff0c\u6b63\u662f\u5bf9\u6b66\u7384\u971c\u95ee\u4ed6\u6709\u4ec0\u4e48\u5fc3\u4e8b\u7684\u7b54 \u8986\uff0c\u4ed6\u5c06\u81ea\u5df1\u6bd4\u4f5c\u90a3\u4f4d\u201c\u4e0d\u80fd\u594b\u98de\u201d\u7684\u201c\u541b\u5b50\u201d\uff0c\u5883\u51b5\u76f8\u540c\uff0c\u60c5\u771f\u610f\u5207\uff0c\u6ee1\u8154\u5fe7\u6124\uff0c\u90fd \u4ece\u7434\u58f0\u4e2d\u53d1\u6cc4\u51fa\u6765\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u8fd8\u4e0d\u6b62\u6b64\u5427\uff1f\u516c\u5b50\u5174\u72b9\u672a\u5c3d\uff0c\u6211\u8fd8\u60f3\u4e24\u542c\u4e00\u9615\u3002\u201d\u674e\u9038\u60f3\u8d77\u4e86\u4e0a\u5b98\u5a49 \u513f\uff0c\u4e0d\u80fd\u81ea\u5df1\uff0c\u53c8\u4e19\u629a\u5f26\u6b4c\u9053\uff1a\u201c \u3000\u3000\u7eff\u516e\u8863\u516e\u3002 \u3000\u3000\u7eff\u8863\u9ec4\u88f3\u3002 \u3000\u3000\u5fc3\u4e4b\u5fe7\u77e3\uff0c \u3000\u3000\u70e8\u7ef4\u5176\u4ea1\uff01 \u3000\u3000\u7eff\u516e\u4e1d\u516e\uff0c \u3000\u3000\u6c5d\u6240\u6cbb\u516e\u3002 \u3000\u3000\u6211\u601d\u6545\u4eba\uff0c \u3000\u3000\u4ef0\u65e0\u5fe7\u516e\u3002 \u3000\u3000\u8fd9\u4e00\u5112\u8bd7\u672c\u6765\u662f\u8bd7\u4eba\u7779\u7269\u6000\u4eba\uff0c\u601d\u5ff5\u6545\u59bb\u7684\u3002\u674e\u9038\u5374\u501f\u6b64\u8bd7\u610f\uff0c\u6765\u6000\u5ff5\u4ed6\u7684\u77e5\u5df1\u4e0a \u5b98\u5a49\u513f\uff0c\u82e5\u8bd1\u6210\u767d\u8bdd\u8bd7\u90a3\u610f\u601d\u5c31\u662f\uff1a \u3000\u3000\u201c\u7eff\u8272\u7684\u4e0a\u8863\u554a\uff0c\u9ec4\u8272\u7684\u88d9\u88f3\u3002\u5fc3\u91cc\u7684\u5fe7\u4f24\u554a\uff0c\u600e\u80fd\u591f\u9057\u5fd8\uff01\u7eff\u8272\u7684\u4e1d\u554a\uff0e\u4f60\u4eb2\u624b \u7406\u8fc7\u3002\u60f3\u5ff5\u7740\u6211\u7684\u6545\u4eba\u554a\uff0c\u7ea0\u6b63\u6211\u591a\u5c11\u5dee\u9519\u3002\u201d\u4ed6\u60f3\u8d77\u4e0a\u5b98\u5a49\u513f\u53bb\u884c\u523a\u6b66\u5219\u5929\uff0c\u5b9a\u7136\u5409 \u5c11\u51f6\u591a\uff0c\u53ea\u6015\u5f53\u771f\u662f\u751f\u79bb\u6b7b\u522b\uff0c\u76f8\u89c1\u65e0\u671f\u3002\u4e0d\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\uff0c\u7434\u58f0\u5f39\u5f97\u5982\u6028\u5982 \u6155\uff0c\u5982\u6ce3\u5982\u8bc9\u3002 \u3000\u3000\u6b66\u7384\u971c\u63a5\u8fc7\u7434\u6765\uff0c\u4e5f\u629a\u5f26\u6b4c\u9053\uff1a \u3000\u3000\u82b3\u4e0e\u6cfd\u5176\u6742\u63c9\u516e\uff0c \u3000\u3000\u7f8c\u82b3\u534e\u81ea\u4e2d\u51fa\uff0c \u3000\u3000\u7eb7\u90c1\u90c1\u5176\u8fdc\u627f\u516e\uff0c \u3000\u3000\u6ee1\u5185\u800c\u5916\u626c\u3002 \u3000\u3000\u60c5\u4e0e\u8d28\u4fe1\u53ef\u4fdd\u516e\uff0c \u3000\u3000\u7f8c\u5c45\u853d\u5176\u95fb\u7ae0\u3002 \u3000\u3000\u518d\u7528\u695a\u4f6f\u201c\u601d\u7f8e\u4eba\u7bc7\u201d\u7684\u8f9e\u610f\uff0c\u7b54\u8986\u674e\u9038\u3002\u610f\u601d\u662f\u8bf4\uff1a\u201c\u9999\u7684\u548c\u81ed\u7684\u6df7\u5728\u4e00\u5757\u513f\uff0c \u50cf\u541b\u4e86\u4e0e\u5c0f\u4eba\u5171\u5904\u4e00\u671d\uff0c\u4f46\u6770\u51fa\u7684\u9999\u82b1\u5728\u51e1\u5349\u4e4b\u4e2d\u4e5f\u80fd\u81ea\u522b\uff0c\u5b83\u7684\u82b3\u9999\u56db\u6ea2\u600e\u4e5f\u4e0d\u4f1a\u6563 \u6d88\u3002\u7f8e\u597d\u7684\u54c1\u8d28\u603b\u80fd\u4fdd\u4f8d\uff0c\u7f8e\u597d\u7684\u58f0\u540d\u5728\u8352\u50fb\u7684\u5730\u65b9\u4e5f\u603b\u80fd\u4f20\u51fa\u53bb\uff0c\u7528\u4e0d\u7740\u4f60\u66ff\u5979\u5fc3\u7126\u3002\u201d \u5979\u628a\u4e0a\u5b98\u5a49\u513f\u6bd4\u4f5c\u6770\u51fa\u7684\u9999\u82b1\uff0c\u7ec8\u5fc5\u80fd\u591f\u4ece\u51e1\u82b1\u4e4b\u4e2d\u628a\u81ea\u5df1\u5206\u522b\u51fa\u6765\uff0c\u61c2\u5f97\u597d\u574f\uff0c\u8bc6\u5f97 \u662f\u975e\u3002\u7531\u4e8e\u5979\u7f8e\u597d\u7684\u54c1\u8d28\uff0c\u5979\u7edd\u5bf9\u4e0d\u4f1a\u88ab\u57cb\u6ca1\u3002\u90a3\u5c31\u662f\u8bf4\u5979\u5fc5\u7136\u4f1a\u7ed9\u6b66\u5219\u5929\u8d4f\u8bc6\u7684\u4e86\u3002 \u3000\u3000\u674e\u9038\u5f88\u4e0d\u60f3\u5979\u63d0\u8d77\u4e0a\u5b98\u5a49\u513f\uff0c\u4f46\u542c\u5979\u501f\u7434\u97f3\u8868\u8fbe\uff0c\u8bf4\u5f97\u90a3\u6837\u80af\u5b9a\uff0c\u597d\u50cf\u4e0a\u5bab\u5a49\u513f\u5c06 \u6765\u7ec8\u4e8e\u4e0e\u4ed6\u80cc\u9053\u800c\u9a70\uff0c\u4e0d\u89c9\u60d8\u7136\u3002 \u3000\u3000\u9aa1\u8f66\u8f98\u8f98\uff0c\u7434\u97f5\u60a0\u626c\uff0c\u674e\u9038\u62ac\u8d77\u5934\u6765\uff0c\u6b63\u597d\u4e0e\u6b66\u7384\u971c\u7684\u76ee\u5149\u76f8\u63a5\uff0c\u674e\u9038\u4e00\u7247\u832b\u7136\uff0c \u4e0d\u89c9\u95ee\u9053\uff1a\u201c\u4f60\u5230\u5e95\u4e3a\u4e86\u4ec0\u4e48\u6551\u6211\uff1f\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u6211\u662f\u8981\u4e3a\u56fd\u5bb6\u4fdd\u5b58\u4e00\u4e2a\u4eba\u624d\uff0c\u4e5f \u597d\u8ba9\u4f60\u5c06\u672a\u53ef\u4ee5\u6709\u673a\u4f1a\u594b\u98de\u554a\uff01\u201d\u674e\u9038\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u90a3\u9664\u975e\u662f\u6ca7\u6851\u6362\u4e86\u3002\u201d\u610f\u601d\u660e\u663e\u5f97 \u5f88\uff0c\u4ed6\u5728\u6b66\u5219\u5929\u7684\u6cbb\u4e0b\u7edd\u4e0d\u80fd\u51fa\u5934\uff0c\u6b66\u5219\u5929\u4e5f\u4e0d\u914d\u7528\u4ed6\u3002\u9664\u975e\u662f\u6062\u590d\u4e86\u674e\u5510\u7684\u6c5f\u5c71\uff0c\u4ed6 \u624d\u53ef\u4ee5\u4e00\u98de\u51b2\u5929\u3002\u6b66\u7384\u971c\u6df1\u6df1\u7684\u770b\u4ed6\u4e00\u773c\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u7684\u77e5\u97f3\u4e4b\u4eba\u4e0d\u5728\u3002\u55ef\uff0c \u6211\u601d\u6545\u4eba\uff0c\u4fd8\u65e0\u5fe7\u516e\u3002\u82e5\u662f\u6709\u8fd9\u6837\u4e00\u4f4d\u6545\u4eba\uff0c\u65f6\u65f6\u601d\u5ff5\uff0c\u5012\u4e5f\u4e0d\u9519\u3002\u201d\u201c\u6211\u601d\u6545\u4eba\uff0c\u4ffe \u65e0\u5fe7\u516e\u201d\u3002\u6b63\u662f\u521a\u624d\u674e\u9038\u6240\u63b8\u594f\u8fc7\u7684\u4e24\u53e5\u8bd7\uff0c\u610f\u601d\u662f\u601d\u5ff5\u6545\u4eba\u53ef\u4ee5\u7ea0\u6b63\u81ea\u5df1\u7684\u5dee\u9519\uff0c\u90a3 \u662f\u674e\u9038\u60f3\u8d77\u4e86\u4e0a\u5b98\u5a49\u513f\u6709\u6240\u611f\u800c\u53d1\u7684\u3002\u5982\u4eca\u6b66\u7384\u971c\u5c31\u7528\u8fd9\u4e24\u53e5\u8bd7\u6765\u6697\u8bbd\u4ed6\uff0c\u610f\u601d\u662f\u8bf4\u9053\uff1a \u3000\u3000\u201c\u82e5\u679c\u4f60\u7684\u77e5\u97f3\u4eba\u4e0a\u5b98\u5a49\u513f\u5728\u8fd9\u91cc\uff0c\u5979\u4e00\u5b9a\u4f1a\u6307\u51fa\u4f60\u7684\u9519\u8bef\u7684\u3002\u201d \u3000\u3000\u674e\u9038\u4e0e\u6b66\u7384\u971c\u5404\u7528\u7434\u58f0\u95ee\u7b54\uff0c\u5404\u7528\u8bf4\u8bdd\u8bd5\u63a2\uff0c\u4f46\u5fc3\u7075\u4e4b\u95f4\uff0c\u603b\u662f\u4e0d\u80fd\u878d\u6d3d\u3002\u542c\u4e86\u6b66 \u7384\u971c\u90a3\u4e24\u53e5\u6d3b\uff0c\u674e\u9038\u518d\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0a\u5b98\u5a49\u513f\u672a\u5fc5\u5c31\u5982\u4f60\u6240\u60f3\u7684\u90a3\u6837\uff0c\u7518 \u5fc3\u5fd8\u4e86\u7236\u6bcd\u4e4b\u4ec7\uff0c\u989c\u4e8b\u654c\u3002\u7eb5\u7136\u5a49\u513f\u53d8\u4e86\uff0c\u627e\u4e5f\u7edd\u4e0d\u4f1a\u5411\u6b66\u5219\u5929\u6298\u8170\uff01\u201d\u6b66\u7384\u971c\u770b\u4ed6\u9762 \u8272\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u8bf4\u8bdd\uff0c\u53ef\u662f\u6709\u4ec0\u4e48\u89e6\u72af\u4f60\u4e86\uff1f\u201d\u674e\u9038\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4e00\u518d \u6307\u70b9\uff0c\u53ef\u662f\u6211\u4e0d\u662f\u4e09\u5c3a\u5c0f\u6beb\uff0c\u9999\u7684\u81ed\u7684\uff0c\u76f8\u4fe1\u81ea\u5df1\u8fd8\u53ef\u4ee5\u5206\u8fa8\u51fa\u6765\u3002\u201d\u6b66\u7384\u971c\u53f9\u53e3\u6c14\u9053\uff1a \u201c\u4f46\u613f\u5982\u6b64\u3002\u201d\u8fd9\u65f6\u9aa1\u8f66\u5df2\u8fdb\u5165\u95f7\u5cf0\u5939\u5cd9\u7684\u8c37\u53e3\uff0c\u5c71\u82b1\u5939\u9053\uff0c\u9e1f\u8bed\u8fce\u4eba\uff0c\u674e\u9038\u7684\u5fc3\u60c5\u7a0d \u7a0d\u5b81\u9759\uff0c\u5ffd\u542c\u5f97\u90a3\u5c0f\u4e2b\u73af\u8bf4\u9053\uff1a\u201c\u6709\u4eba\u8d76\u5728\u6211\u4eec\u7684\u524d\u5934\u5165\u5c71\u53bb\u4e86\uff0c\u54a6\uff0c\u9a6c\u5927\u53d4\u4f60\u770b\u8fd9\u8def \u4e0a\u9a6c\u8e44\u7684\u75d5\u8ff9\uff0c\u6562\u60c5\u5c31\u662f\u521a\u624d\u90a3\u4e00\u5bf9\u957f\u5b59\u5144\u59b9\uff1f\u201d \u3000\u3000\u4e0d\u9519\uff0c\u957f\u5b59\u5144\u59b9\u8fd9\u65f6\u6b63\u5728\u6c13\u5d03\u5c71\u4e2d\uff0c\u610f\u5916\u7684\u89c1\u7740\u4e86\u4e00\u4f4d\u9041\u4e16\u9ad8\u4eba\u3002\u90a3\u65e5\u957f\u5b59\u6cf0\u88ab\u6b66 \u7384\u971c\u524a\u65ad\u4e86\u5251\u5c16\u4e4b\u540e\uff0c\u7f9e\u60cb\u4e4b\u6781\uff0c\u7eb5\u9a6c\u6025\u9a70\uff0c\u8bb8\u4e45\u8bb8\u4e45\uff0c\u624d\u8ffd\u4e0a\u4ed6\u7684\u59b9\u59b9\u3002\u957f\u5b59\u58c1\u6bd4\u5979 \u7684\u54e5\u54e5\u66f4\u4e0d\u6124\u8f93\uff0c\u57cb\u6028\u5979\u7684\u54e5\u54e5\u51fa\u5251\u4e0d\u5feb\uff0c\u53d8\u62db\u4e0d\u7075\u3002\u957f\u5b59\u6cf0\u82e6\u53e3\u82e6\u8138\u7684\u8bf4\u9053\uff1a\u201c\u6211\u4e5f \u4e0d\u77e5\u662f\u600e\u4e48\u6405\u7684\uff1f\u6562\u60c5\u90a3\u5996\u5973\u771f\u7684\u4f1a\u4f7f\u5996\u6cd5\uff0c\u4e0d\u8bba\u54b1\u4eec\u600e\u6837\u6025\u653b\uff0c\u773c\u770b\u5251\u5c16\u5c31\u8981\u523a\u5230\u5979 \u7684\u8eab\u4e0a\uff0c\u5374\u88ab\u5979\u8f7b\u8f7b\u4e00\u6321\u4fbf\u6321\u5f00\u4e86\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u90a3\u662f\u4ec6\u4e48\u5996\u6cd5\uff1f\u8fd9\u90fd\u662f\u4f60\u4e0d\u80fd\u597d\u597d \u7684\u548c\u6211\u914d\u5408\u4e4b\u6545\u3002\u201d\u957f\u5b59\u6cf0\u53ea\u597d\u987a\u7740\u59b9\u59b9\u7684\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u662f\u554a\uff0c\u54b1\u4eec\u5230\u5e95\u662f\u7b2c\u4e00\u6b21\u548c\u654c \u4eba\u4ea4\u624b\uff0c\u5403\u4e8f\u5728\u7ecf\u9a8c\u4e0d\u591f\uff0c\u8981\u4e0d\u7136\u4e5f\u4e0d\u4f1a\u8fd9\u6837\u83ab\u660e\u5176\u5999\u7684\u4fbf\u8f93\u4e86\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u4e00 \u8def\u7422\u78e8\u90a3\u5996\u5973\u7684\u5251\u6cd5\uff0c\u5582\uff0c\u54b1\u4eec\u518d\u62c6\u4e00\u62c6\u521a\u624d\u7684\u62db\u6570\uff0c\u660e\u5929\u8ffd\u4e0a\u53bb\u548c\u5979\u6076\u6597\u4e00\u573a\u3002\u201d\u957f \u5b59\u6cf0\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u59b9\u59b9\u6bd4\u6211\u8fd8\u8981\u597d\u5f3a\u3002\u201d\u53ef\u662f\u4ed6\u4e5f\u60f3\u633d\u56de\u9762\u5b50\uff0c\u800c\u4e14\u77e5\u9053\u59b9\u59b9\u7d20\u6765\u806a\u654f\uff0c \u8bf4\u4e0d\u5b9a\u5979\u771f\u7684\u7422\u78e8\u51fa\u4e86\u6240\u4ee5\u7136\u6765\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u7eb5\u7136\u518d\u6597\u4e5f\u672a\u5fc5\u80dc\u5f97\u4e86\u90a3\u4e2a\u6b66\u7384\u971c\uff0c\u4f46 \u6211\u62c6\u4e00\u62c6\u521a\u624d\u7684\u62db\u6570\u603b\u662f\u597d\u7684\u3002\u201d\u4e8e\u662f\u70b9\u5934\u540c\u610f\uff0c\u4e24\u5144\u59b9\u8df3\u4e0b\u9a6c\u80cc\uff0c\u4fbf\u5728\u5c71\u8fb9\u62c6\u8d77\u62db\u6765\u3002 \u3000\u3000\u54e5\u54e5\u6c14\u529b\u5145\u6c9b\uff0e\u59b9\u59b9\u8eab\u6cd5\u8f7b\u7075\uff0c\u867d\u7136\u53ea\u662f\u62c6\u62db\uff0c\u4e5f\u6253\u5f97\u5341\u5206\u7d27\u5f20\u7cbe\u91c7\u3002\u6253\u5230\u5206\u9645\uff0c \u957f\u5b59\u6cf0\u5c06\u5251\u8bc0\u4e00\u987f\uff0c\u5f2f\u8170\u63d2\u67f3\uff0c\u5251\u5c16\u5728\u5730\u4e0a\u4e00\u6309\uff0c\u500f\u7684\u53cd\u5f39\u524a\u51fa\uff0c\u957f\u5b59\u58c1\u4e3e\u5251\u62e8\u5f00\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u4e00\u62db\u2018\u516d\u8d77\u5deb\u5c71\u2019\u4f7f\u5f97\u4e0d\u5bf9\uff0c\u4f60\u770b\u6211\u7684\u3002\u201d\u62d7\u6b65\u5f2f\u8170\uff0c\u5237\u7684\u4e00\u5251\u523a\u51fa\uff0c\u559d\u9053\uff1a \u201c\u64a4\u624b\uff01\u201d\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u957f\u5b59\u6cf0\u8e6c\u8e6c\u8e6c\u8fde\u9000\u4e09\u6b65\uff0c\u864e\u53e3\u53d1\u9ebb\u3002\u7136\u800c\u90a3\u67c4\u957f\u5251\u5c45 \u7136\u6ca1\u6709\u64a4\u624b\u3002\u957f\u5b59\u58c1\u6ee1\u9762\u901a\u7ea2\uff0c\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u6492\u624b\uff0c\u4f46\u6211\u7684\u6c14\u529b\u6bd4\u4f60\u5927\uff0c\u5374 \u88ab\u4f60\u501f\u529b\u6253\u529b\uff0c\u5c06\u6211\u8feb\u9000\u4e09\u6b65\uff0c\u5df2\u662f\u5341\u5206\u96be\u5f97\u3002\u55ef\uff0c\u8fd9\u4e00\u62db\u786e\u662f\u6bd4\u6211\u9ad8\u660e\u3002\u54b1\u4eec\u521a\u624d\u82e5 \u540c\u65f6\u4f7f\u51fa\u8fd9\u62db\uff0c\u5b81\u53ef\u8d25\u4e2d\u6c42\u80dc\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u53f9\u9053\uff1a\u201c\u8981\u522b\u521b\u65b0\u62db\uff0c\u771f\u662f\u8c08\u4f55\u5bb9\u6613\uff1f\u4ee5\u957f\u5b59\u5747\u91cf\u7684\u5b66\u529b\uff0c \u5ce8\u5d4b\u5251\u6cd5\u7684\u8fd9\u4e00\u7834\u7efd\uff0c\u4e5f\u662f\u81f3\u4eca\u8fd8\u672a\u8865\u597d\u3002\u201d \u3000\u3000\u957f\u5b59\u5144\u59b9\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u6025\u6536\u62fe\uff0c\u53ea\u89c1\u4e00\u4e2a\u767d\u987b\u98d8\u62c2\u7684\u8001\u5934\u513f\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u65f6\u5019\u8d70\u6765 \u7684\uff1f\u8fd9\u65f6\u6b63\u7ad9\u5728\u65c1\u8fb9\uff0c\u770b\u7740\u4ed6\u4eec\u5fae\u7b11\uff0c\u773c\u5149\u4e2d\u5374\u662f\u4e00\u80a1\u82cd\u51c9\u7684\u795e\u8272\u3002\u957f\u5b59\u58c1\u6697\u6697\u5600\u5495\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u7236\u4eb2\u5e38\u8bf4\uff0c\u4e34\u654c\u4e4b\u9645\uff0c\u8981\u773c\u89c2\u53eb\u9762\uff0c\u8033\u542c\u516b\u65b9\u3002\u82e5\u7136\u4ed6\u662f\u654c\u4eba\uff0c\u5728\u80cc\u540e\u5077 \u88ad\uff0c\u5c82\u4e0d\u7cdf\u7cd5\uff1a\u5ce1\uff0c\u5947\u602a\uff0c\u51ed\u6211\u7684\u8033\u529b\uff0c\u600e\u4e48\u542c\u4e0d\u51fa\u4ed6\u7684\u58f0\u606f\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u5fc3\u601d\u6ca1\u6709\u59b9\u59b9\u7075\u654f\uff0c\u4e00\u65f6\u4e4b\u95f4\u7adf\u672a\u60f3\u5230\u522b\u4eba\u80fd\u591f\u8fd9\u6837\u7684\u7a81\u5982\u5176\u672a\uff0c\u5230\u4e86\u9762\u524d\uff0c \u624d\u7ed9\u81ea\u5df1\u53d1\u73b0\uff0c\u6b66\u529f\u5b9a\u662f\u6bd4\u81ea\u5df1\u9ad8\u660e\uff1b\u542c\u4ed6\u8bc4\u8bba\u81ea\u5df1\u7236\u4eb2\u6240\u521b\u7684\u5251\u6cd5\uff0c\u7adf\u4f3c\u610f\u5b58\u8f7b\u89c6\uff0c \u4e0d\u7981\u52c3\u7136\u706b\u8d77\uff0c\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8bf4\u6211\u4eec\u7684\u5ce8\u5d4b\u5251\u6cd5\u751a\u6709\u7834\u7efd\uff0c\u4f60\u5b9a\u7136\u662f\u4e2a\u5927\u884c\u5bb6\u4e86\uff1f \u5c0f\u5b50\u5192\u6627\uff0c\u5012\u8981\u8bf7\u4f60\u4e0b\u573a\u8bd5\u8bd5\uff0c\u8ba9\u6211\u660e\u767d\u7834\u7efd\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u59b9\u59b9\uff0c\u628a\u4f60\u7684\u5251\u7ed9\u4ed6\uff01\u201d\u4ed6 \u89c1\u90a3\u8001\u5934\u6ca1\u6709\u5e26\u5251\uff0c\u4fbf\u53eb\u59b9\u59b9\u5c06\u5251\u7ed9\u4ed6\uff0c\u90a3\u5f53\u7136\u662f\u575a\u6301\u7740\u8981\u548c\u4ed6\u6bd4\u5251\u4e86\u3002 \u3000\u3000\u90a3\u8001\u5934\u513f\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u53d1\u8a93\u7ec8\u751f\u4e0d\u518d\u4f7f\u5251\u548c\u4eba\u52a8\u624b\u4e86\u3002\u4e0d\u8fc7\u4f60\u4eec\u8981\u8bf7\u6211 \u6307\u70b9\uff0c\u6211\u5012\u662f\u4e49\u4e0d\u5bb9\u8f9e\uff01\u201d\u957f\u5b59\u6cf0\u6012\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u90a3\u5c31\u8bf7\u6765\u6307\u70b9\u5427\uff01\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u8001\u524d\u5806\uff0c\u4f60\u662f\u8c01\uff1f\u201d\u4e24\u5144\u59b9\u5404\u6709\u60f3\u6cd5\uff0c\u53e3 \u543b\u4e0d\u540c\uff0c\u4e89\u7740\u8bf4\u8bdd\u3002\u90a3\u8001\u5934\u5fae\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4eec\u4e24\u4eba\u518d\u7ee7\u7eed\u62c6\u62db\uff0c\u5f85\u6211\u770b\u5230\u9ad8\u5174\u7684\u65f6 \u5019\uff0c\u4fbf\u6765\u6307\u70b9\u4f60\u4eec\uff0c\u90a3\u65f6\u4e5f\u8bb8\u4f60\u4eec\u5c31\u4f1a\u77e5\u9053\u6211\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u89c1\u4ed6\u501a\u8001\u5b9e\u8001\uff0c\u751a\u662f\u4e0d\u670d\uff0c\u957f\u5b59\u58c1\u5fd9\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u54b1\u4eec\u518d\u7ec3\u4e00\u7ec3\uff0c\u5582\uff0c\u7559\u5fc3 \u63a5\u62db\uff01\u201d\u5693\u7684\u4e00\u5251\u4fbf\u523a\uff0c\u957f\u5b59\u6cf0\u7d20\u6765\u987a\u4ece\u59b9\u59b9\uff0c\u51b5\u4e14\u5979\u5251\u5df2\u523a\u5230\uff0c\u975e\u63a5\u4e0d\u53ef\uff0c\u53ea\u597d\u548c\u5979 \u518d\u7ee7\u7eed\u62c6\u62db\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u8fd8\u672a\u89c1\u90a3\u8001\u5934\u5f00\u58f0\u6307\u70b9\uff0c\u957f\u5b59\u6cf0\u6b63\u81ea\u4e0d\u8010\uff0c\u957f\u5b59\u58c1\u5374\u662f\u5fc3\u4e2d\u4e00 \u52a8\uff0c\uff0c\u84e6\u7136\u4e00\u8bb0\u201c\u4e91\u8d77\u5deb\u5c71\u201d\u653b\u51fa\uff0c\u5c31\u5728\u8fd9\uff0c\u53ea\u542c\u90a3\u8001\u5934\u513f\u54c8\u54c8\u4e00\u7b11\uff0c\u4e24\u5144\u59b9\u4f46\u89c9\u5fae\u98ce \u98d2\u7136\uff0c\u90a3\u8001\u5934\u513f\u500f\u7684\u62e6\u5728\u4ed6\u4eec\u4e4b\u95f4\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u7b11\u58f0\u672a\u505c\uff0c\u4ed6\u4eec\u7684\u4e24\u67c4\u957f\u5251\u65e9\u5df1\u88ab\u4eba\u5bb6 \u593a\u53bb\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8e51\u4e91\u5251\u8c37\u795e\u7fc1\u5c48\u8001\u4f2f\u4f2f\uff01\u201d\u987b\u77e5\u8e51\u4e91\u5251\u4e43\u5929\u4e0b\u6700\u8f7b\u7075\u98d8\u5ffd \u7684\u5251\u6cd5\uff0c\u8fd9\u6b21\u8c37\u795e\u7fc1\u867d\u7136\u6ca1\u6709\u7528\u5251\uff0c\u4f46\u4ed6\u90a3\u5999\u7edd\u5929\u4e0b\u7684\u201c\u8e51\u4e91\u6b65\u6cd5\u201d\uff0c\u5374\u5df2\u7ed9\u957f\u5b59\u58c1\u8ba4 \u4e86\u51fa\u6765\u3002\u957f\u5b59\u6cf0\u5fc3\u601d\u8f83\u949d\uff0c\u8fd9\u65f6\u4ea6\u5df2\u60f3\u5230\uff1a\u201c\u5bf9\u665a\uff0c\u7239\u7239\u8bf4\u8fc7\uff0c\u80dc\u8fc7\u672c\u6d3e\u8f7b\u529f\u7684\u53ea\u6709\u98d8 \u5ffd\u83ab\u6d4b\u7684\u8e51\u4e91\u6b65\uff0c\u4ed6\u80fd\u591f\u5728\u4e3e\u624b\u4e4b\u95f4\u5c31\u80fd\u593a\u53bb\u4e86\u6211\u4eec\u7684\u4e24\u652f\u957f\u5251\uff0c\u5f53\u7136\u662f\u8c37\u795e\u7fc1\u4e86\uff01\u201d \u60f3\u8d77\u521a\u624d\u8bf4\u8bdd\u66b4\u81ca\uff0c\u751a\u662f\u5c34\u5c2c\uff0c\u53ea\u597d\u4e0a\u524d\u966a\u793c\uff0c\u5c0a\u4e86\u4e00\u58f0\u201c\u8c37\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u8c37\u795e\u7fc1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u627e\u4e0d\u5230\u8001\u5b50\uff0c\u5374\u627e\u5230\u4e86\u513f\u5b50\u4e86\u3002\u201d\u957f\u5b59\u58c1\u95ee\u9053\uff1a \u201c\u8c37\u8001\u4f2f\u66fe\u7ecf\u5230\u8fc7\u5251\u9601\u627e\u5bfb\u5bb6\u7236\u4e48\uff1f\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4f60\u5f53\u7136\u77e5\u9053\u6211\u548c\u4f60\u4eec\u7684\u7239\u7239 \u4ee5\u524d\u662f\u6700\u8981\u597d\u7684\u670b\u53cb\u3002 \u3000\u3000\u5eff\u4e94\u5e74\u524d\uff0c\u6211\u4eec\u5728\u5ce8\u5d4b\u6ca6\u5251\uff0c\u90a3\u65f6\u4f60\u4eec\u90fd\u8fd8\u6ca1\u6709\u51fa\u4e16\uff0c\u4f60\u7239\u7239\u65b0\u521b\u4e86\u4e00\u5957\u5251\u6cd5\uff0c\u5bf9 \u2018\u4e91\u8d77\u5deb\u5c71\u2019\u8fd9\u62db\u5c24\u5176\u5f97\u610f\uff0c\u8fd9\u662f\u8d25\u4e2d\u6c42\u80dc\u7684\u597d\u62db\uff0c\u53d8\u5316\u5947\u5e7b\uff0c\u786e\u5b9e\u6709\u9b3c\u795e\u83ab\u6d4b\u4e4b\u9492\uff0c \u6211\u4e5f\u751a\u4e3a\u4f69\u670d\uff0c\u4f46\u8fd9\u4e00\u62db\u5374\u6709\u4e2a\u6f0f\u6d1e\uff0c\u56e0\u4e3a\u8981\u8d25\u4e2d\u6c42\u80dc\uff0c\u6240\u4ee5\u8d70\u7684\u4f7f\u662f\u5192\u9669\u4e00\u640f\uff0c\u5feb\u901f \u8fdb\u653b\u7684\u8def\u5b50\uff0c\u5df2\u65b9\u5341\u4e09\u8def\u4fbf\u4e0d\u80fd\u4e0d\u9732\u51fa\u7a7a\u95e8\u3002\u5f53\u65f6\u6211\u5411\u4f60\u7239\u7239\u8bf4\u4e86\uff0c\u4f60\u7239\u7239\u8bf4\u8fd9\u8bda\u7136\u662f \u4e2a\u7834\u7efd\uff0c\u4f46\u654c\u4eba\u600e\u80fd\u6599\u5230\u6211\u7a81\u7136\u51fa\u6b64\u5947\u62db\uff1f\u800c\u4e14\u5bf9\u65b9\u5728\u80dc\u62db\u4e4b\u9645\uff0c\u4e5f\u5fc5\u7136\u8981\u4e58\u80dc\u8ffd\u51fb\uff0c \u4ed6\u7684\u4e0b\u76d8\u4e5f\u81ea\u7136\u8981\u9732\u51fa\u7a7a\u95e8\uff0c\u53c8\u600e\u80fd\u62c6\u89e3\u6211\u7684\u62db\u6570\uff0c\u6211\u4e0d\u4ee5\u4e3a\u7136\uff0c\u4f46\u5f53\u65f6\u4e5f\u786e\u5b9e\u60f3\u4e0d\u5230 \u600e\u6837\u53bb\u7834\u4ed6\u8fd9\u4e00\u62db\u3002\u540e\u6765\u6211\u89c1\u4e86\u5c09\u8fdf\u70af\uff0c\u5f7c\u6b64\u7422\u78e8\uff0c\u624d\u60f3\u51fa\u4e86\u7834\u62db\u7684\u5999\u6cd5\u3002\u6240\u4ee5\u521a\u624d\u4f60 \u4eec\u82e5\u4e0d\u662f\u6070\u6070\u4f7f\u5230\u8fd9\u62db\uff0c\u6211\u8fd8\u672a\u5fc5\u80fd\u8fd9\u4f6f\u5feb\u4fbf\u593a\u4e86\u4f60\u4eec\u7684\u5251\u5462\u3002\u8fd9\u6b21\u6211\u56e0\u4e8b\u5165\u8700\uff0c\u542c\u8bf4 \u4f60\u7239\u7239\u9690\u5c45\u5251\u9614\uff0c\u524d\u51e0\u5929\u6211\u4fbf\u53bb\u627e\u4ed6\uff0c\u4e00\u8005\u53d9\u65e7\uff0c\u4e8c\u8005\u60f3\u548c\u4ed6\u518d\u7814\u7a76\u8fd9\u4e00\u62db\uff0c\u5374\u4e0d\u6599\u6251 \u4e86\u4e2a\u7a7a\uff0c\u4ed6\u4e0d\u77e5\u642c\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5bb6\u7236\u5df2\u642c\u5230\u9752\u57ce\u5c71\u7384\u5316\u548c\u5c1a\u7684\u5bfa\u4e2d\u907f\u4ec7\u53bb\u4e86\u3002\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u907f\u4ec7\uff1f \u907f\u4ec0\u4e48\u4ec7\uff1f\u201d\u957f\u5b59\u58c1\u5c06\u7236\u4eb2\u53d7\u4e86\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u6697\u5668\u6240\u4f24\uff0c\u5931\u4e86\u6b66\u529f\u7684\u4e8b\uff0c\u8be6\u7ec6\u8bf4\u4e86\u4e00 \u904d\u3002\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u771f\u662f\u8be5\u6b7b\uff0c\u8fd9\u4e24\u4e2a\u9b54\u5934\u6076\u6027\u5140\u662f\u4e0d\u6539\u3002\u597d\u5728\u627e\u8fd9\u6b21\u6ca1\u6709\u9080\u8bf7\u4ed6\u4eec\u3002\u4e0d \u8fc7\uff0c\u4f60\u7236\u4eb2\u4e5f\u672a\u5fc5\u9700\u8981\u518d\u7ec3\u5341\u5e74\uff0c\u6211\u6709\u4e00\u4f4d\u670b\u53cb\u6216\u8005\u53ef\u4ee5\u52a9\u4ed6\u65e9\u53e3\u5eb7\u590d\u3002\u201d\u957f\u5b59\u5144\u59b9\u6b63 \u8981\u8bf7\u95ee\u662f\u8c01\uff0c\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e00\u4e8b\u672a\u660e\uff0c\u4f60\u4eec\u521a\u624d\u62c6\u62db\u4e4b\u9645\uff0c\u8bf4\u662f\u518d\u8981\u548c\u4ec0\u4e48\u4eba\u5927 \u6597\u4e00\u573a\uff0c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u957f\u5b59\u5144\u59b9\u77e5\u9053\u4e86\u662f\u8c37\u795e\u7fc1\u4e4b\u540e\uff0e\u65e9\u5c31\u60f3\u9080\u4ed6\u53bb\u622a\u52ab\u9aa1\u8f66\uff0c\u518d\u6597\u6b66\u7384\u971c\u4e86\u3002\u4f46\u8f6c\u5ff5\u4e00 \u60f3\uff0c\u4ed6\u4eec\u66fe\u542c\u8fc7\u9053\u8def\u4f20\u8a00\uff0c\u8bf4\u662f\u8fd9\u6b21\u82f1\u96c4\u5927\u4f1a\u4e4b\u6240\u4ee5\u74e6\u89e3\u51b0\u6d88\uff0c\u4fbf\u662f\u56e0\u4e3a\u8c37\u795e\u7fc1\u8d25\u7ed9\u4e86 \u90a3\u4e2a\u5973\u4e86\u3002\u4ed6\u4eec\u4e0d\u77e5\u9053\u662f\u865a\u662f\u5b9e\uff0c\u4f46\u6015\u4f24\u4e86\u8c37\u795e\u7fc1\u7684\u9762\u5b50\uff0c\u6545\u6b64\u8fdf\u8fdf\u4e0d\u6562\u5f00\u53e3\u3002\u5982\u4eca\u8c37 \u4f38\u7fc1\u95ee\u8d77\uff0c\u53ea\u5f97\u5c06\u5b9e\u60c5\u544a\u8bc9\u4e8e\u4ed6\u3002\u8c37\u795e\u7fc1\u957f\u53f9\u4e00\u5362\uff0c\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u201d\u968f\u5373\u53c8\u7126\u6025 \u7684\u95ee\u9053\uff0c\u201c\u4f60\u4eec\u5f53\u771f\u662f\u89c1\u5230\u4e86\u674e\u9038\u88ab\u6293\u5728\u5979\u7684\u624b\u4e2d\u4e48\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u771f\uff1f\u6211\u8fd8\u542c\u5230\u6bbf\u4e0b\u547b\u541f\u7684\u58f0\u97f3\u5462\uff0c\u6562\u60c5\u662f\u4f24\u5f97\u5f88\u91cd\uff0c\u6240\u4ee5\u4e00\u76f4 \u8eba\u5728\u8f66\u4e2d\u6ca1\u6709\u9732\u9762\u3002\u201d\u957f\u5b59\u58c1\u63d2\u53e3\u9053\uff1a \u3000\u3000\u201c\u90a3\u5996\u5973\u5b9a\u662f\u8981\u5c06\u4ed6\u89e3\u4e0a\u957f\u5b89\uff0c\u9886\u529f\u8bf7\u8d4f\uff0c\u54b1\u4eec\u53ef\u5f97\u8d76\u5feb\u53bb\u6551\u3002\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u8f66 \u4e0a\u8fd8\u6709\u4f55\u4eba\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u5c0f\u4e2b\u73af\u548c\u4e00\u4e2a\u9a7e\u8f66\u7684\u6c49\u5b50\u3002\u201d \u3000\u3000\u8c37\u795e\u7fc1\u6c89\u541f\u4e0d\u8bed\uff0c\u4f3c\u6709\u4ec0\u4e48\u5fc3\u4e8b\u4ee4\u4ed6\u5f88\u662f\u4e3a\u96be\uff0c\u957f\u5b59\u6cf0\u5fc3\u76f4\u53e3\u5feb\uff0c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u6211 \u59b9\u59b9\u53ef\u4ee5\u8d62\u5f97\u90a3\u5c0f\u4e2b\u73af\uff0c\u6211\u53ef\u4ee5\u8d62\u5f97\u90a3\u9a7e\u8f66\u7684\u6c49\u5b50\uff0c\u8c37\u8001\u524d\u8f88\uff0c\u4f60\u53ea\u8981\u80fd\u548c\u90a3\u6b66\u7384\u971c\u6597 \u4e0a\u767e\u62db\uff0c\u6211\u4eec\u51fb\u8d25\u4e86\u654c\u4eba\u4e4b\u540e\uff0c\u5c31\u6765\u5e2e\u4f60\uff0c\u4f55\u987b\u60e7\u5979\uff1f\u201d\u8c37\u4f38\u7fc1\u54c8\u54c8\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u751f\u5e73\u7eb5\u6a2a\u5357\u5317\uff0c\u5bf9\u4ed8\u4efb\u4f55\u5f3a\u654c\u3001\u4e5f\u4ece\u6765\u672a\u8bf7\u8fc7\u670b\u53cb\u52a9\u62f3\u3002\u90a3\u4e2b\u5934\u6b66\u529f\u867d\u7136\u5389\u5bb3\uff0c \u5728\u4e00\u5343\u62db\u4e4b\u5185\u6211\u786e\u662f\u6ca1\u628a\u63e1\u80dc\u5979\uff0c\u5230\u4e86\u4e00\u5343\u62db\u4e4b\u5916\uff0c\u563f\u563f\uff0c\u8001\u673d\u81ea\u4fe1\u8fd8\u53ef\u4ee5\u5c06\u5979\u964d\u4f0f\uff01\u201d \u957f\u5b59\u6cf0\u9053\uff1a\u201c\u90a3\u66f4\u597d\u4e86\u3002\u4f55\u4ee5\u5c1a\u6709\u72b9\u7591\uff1f\u201d\u8c37\u795e\u7fc1\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u662f\u6211\u5df2\u7b54\u5e94\u4e86\u4e00\u4f4d\u670b \u53cb\uff0c\u4eca\u540e\u4e0d\u518d\u4f7f\u5251\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u8c37\u795e\u7fc1\u90a3\u65e5\u88ab\u5929\u5c71\u7b26\u4e0d\u7591\u5c06\u4ed6\u5f15\u8d70\uff0c\u4e24\u4eba\u53e6\u5916\u5230\u5ce8\u5d4b\u5343\u4f5b\u9876\u53bb\u6bd4\u4e86\u4e00\u573a\u5251\uff0c\u7ed3 \u679c\u6597\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u662f\u7b26\u4e0d\u7591\u80dc\u4e86\u4e00\u62db\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u53d6\u7b11\u4ed6\u9053\uff1a\u201c\u4f60\u5728\u91d1\u9876\u7684\u82f1\u96c4\u4f1a\u4e0a\u8d62\u4e0d\u4e86\u4e00\u4e2a\u5c0f\u59d1\u5a18\uff0c\u5982\u4eca\u53c8\u6253\u4e0d\u8fc7\u6211\uff0c\u4f60 \u81ea\u5df1\u8bf4\u8be5\u600e\u4e48\u529e\uff1f\u201d\u8c37\u795e\u7fc1\u5728\u82f1\u96c4\u4f1a\u4e4b\u540e\uff0c\u65e9\u5df2\u5fc3\u7070\u6c14\u51b7\uff0c\u5982\u4eca\u4e49\u88ab\u4ed6\u4e00\u6fc0\uff0c\u7acb\u5373\u62d7\u6298 \u957f\u5251\uff0c\u53d1\u8a93\u7ec8\u751f\u4e0d\u518d\u4f7f\u5251\u53bb\u5bf9\u4ed8\u654c\u4eba\u3002 \u3000\u3000\u957f\u5b59\u5144\u59b9\u9762\u9762\u76f8\u89d1\uff0c\u4ed6\u4eec\u77e5\u9053\u50cf\u8c37\u795e\u7fc1\u8fd9\u6837\u5927\u6709\u8eab\u4efd\u7684\u4eba\uff0c\u4e00\u8a00\u65e2\u51fa\uff0c\u90a3\u5c31\u662f\u6c38\u65e0 \u66f4\u6539\u4e4b\u7406\u3002\u5fc3\u4e2d\u5747\u5728\u60f3\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u8c37\u795e\u7fc1\u4e0d\u80af\u5e2e\u5fd9\uff0c\u6211\u4eec\u7684\u62db\u6570\u7ec3\u5f97\u518d\u719f\uff0c\u6050\u6015\u4e5f\u4e0d \u662f\u4eba\u5bb6\u7684\u5bf9\u624b\u3002\u201d\u8981\u77e5\u957f\u5b59\u58c1\u8d77\u521d\u867d\u4e0d\u6124\u8f93\uff0c\u4f46\u5979\u8fd8\u6709\u81ea\u77e5\u4e4b\u53eb\uff0c\u8c37\u795e\u7fc1\u521a\u624d\u5728\u4e3e\u624b\u4e4b \u95f4\u4fbf\u80fd\u5c06\u4ed6\u4eec\u7684\u5251\u593a\u51fa\u4e8e\u53bb\uff0c\u800c\u542c\u8c37\u795e\u7fc1\u81ea\u8a00\uff0c\u975e\u5230\u5343\u62db\u4e4b\u5916\uff0c\u4e0d\u80fd\u8d62\u5f97\u4e86\u90a3\u4e2a\u6b66\u7384\u971c\uff0c \u5982\u6b64\u8bf4\u6765\uff0c\u81ea\u5df1\u5982\u4f55\u80fd\u662f\u4eba\u5bb6\u5bf9\u624b\uff1f \u3000\u3000\u4f46\u89c1\u8c37\u795e\u7fc1\u6c89\u541f\u534a\u664c\uff0c\u5ffd\u5730\u53cc\u76ee\u4e00\u7741\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u9038\u662f\u6211\u6367\u4ed6\u51fa\u6765\u7684\uff0c\u6211\u53ef\u4e0d\u80fd\u8ba9 \u4ed6\u843d\u5728\u6b66\u5219\u5929\u624b\u4e2d\u3002\u6211\u65e2\u4e0d\u4fbf\u52a8\u624b\uff0c\u53ea\u597d\u518d\u53bb\u9ebb\u70e6\u8001\u670b\u53cb\u4e86\u3002\u597d\u5427\uff0c\u4f60\u4eec\u73b0\u5728\u5c31\u8ddf\u6211\u6765\uff01\u201d \u957f\u5b59\u58c1\u95ee\u9053\uff1a \u3000\u3000\u201c\u8c37\u4f2f\u4f2f\u53bb\u9080\u8bf7\u7684\u662f\u54ea\u4f4d\u8001\u524d\u8f88\uff1f\u6765\u5f97\u53ca\u5417\uff1f\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u91d1\u9488\u56fd\u624b\u590f\u4faf\u575a\u5c31\u4f4f \u5728\u8fd9\u6c13\u5d03\u5c71\u4e2d\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u53c8\u60ca\u53c8\u559c\uff0c\u539f\u6765\u8fd9\u590f\u4faf\u575a\u4e5f\u662f\u5979\u7236\u4eb2\u7684\u597d\u53cb\uff0c\u4e0d\u4f46\u533b\u672f\u6781\u4e3a\u9ad8\u660e\uff0c\u6b66\u529f\u4ea6\u662f \u6df1\u4e0d\u53ef\u6d4b\uff0c\u53ea\u662f\u4ed6\u4e3a\u4eba\u6de1\u6cca\uff0c\u4e0d\u6c42\u540d\u5229\uff0c\u884c\u8e2a\u98d8\u5ffd\u65e0\u5b9a\uff0c\u4ed6\u4e5f\u50cf\u8c37\u795e\u7fc1\u4e00\u6837\uff0c\u4e0e\u957f\u5b59\u5747 \u91cf\u6709\u4e8c\u5341\u5e74\u4ee5\u4e0a\u4e0d\u901a\u97f3\u8baf\u7684\u4e86\u3002\u6545\u6b64\uff0c\u957f\u5b59\u5747\u91cf\u53d7\u4f24\u4e4b\u540e\uff0c\u66fe\u5bf9\u513f\u5973\u63d0\u8d77\u6b64\u4eba\uff0c\u8bf4\u662f\u53ea \u6709\u6b64\u4eba\u53ef\u4ee5\u4e3a\u4ed6\u7597\u4f24\uff0c\u53ea\u662f\u82e6\u4e8e\u65e0\u6cd5\u5bfb\u89c5\u3002\u60f3\u4e0d\u5230\u4ed6\u5c31\u4f4f\u5728\u8fd9\u6c13\u5d03\u5c71\u4e2d\u3002\u957f\u5b59\u58c1\u559c\u51fa\u671b \u5916\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u771f\u662f\u53cc\u559c\u9f50\u6765\uff0c\u4e0d\u4f46\u53ef\u4ee5\u8bf7\u4ed6\u53bb\u6551\u674e\u9038\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5\u8bf7\u4ed6\u5e2e\u52a9\u7236\u4eb2\u6062\u590d \u6b66\u529f\u3002\u201d \u3000\u3000\u4e00\u884c\u4e09\u4f17\uff0c\u4fbf\u5373\u767b\u5c71\uff0c\u4f46\u89c1\u5c71\u5ce6\u8d77\u4f0f\uff0c\u5e7d\u6da7\u91cd\u4e91\uff0c\u6e05\u7075\u4e4b\u6c14\uff0c\u4e0d\u51cf\u5ce8\u5d4b\u3002\u5c71\u5761\u4e0a\u51e0 \u5ea7\u5e73\u623f\uff0c\u4f9d\u7740\u5730\u5f62\u8d77\u4f0f\u4e4b\u52bf\u5efa\u9020\uff0c\u5916\u9762\u6709\u7ea2\u5899\u56f4\u7ed5\uff0c\u9752\u85e4\u76d8\u74e6\uff0c\u4e00\u770b\u4fbf\u77e5\u662f\u9ad8\u58eb\u6240\u5c45\u3002 \u6709\u4e00\u6761\u4eba\u884c\u8def\u76f4\u901a\u5230\u95e8\u524d\uff0c\u8def\u8fb9\u79c0\u8349\u6ca1\u5f84\uff0c\u6986\u67f3\u6210\u884c\uff0c\u95e8\u524d\u8fd8\u6709\u4e00\u4e2a\u8349\u576a\uff0c\u82b1\u8349\u6811\u6728\u4fee \u526a\u5f97\u751a\u4e3a\u9f50\u6574\uff0c\u90a3\u81ea\u662f\u4e3b\u4eba\u6709\u610f\u7ecf\u8425\u7684\u4e86\u3002 \u3000\u3000\u56ed\u95e8\u865a\u63a9\uff0c\u957f\u5b59\u5144\u59b9\u968f\u7740\u8c37\u795e\u7fc1\u8fdb\u53bb\uff0c\u89e6\u76ee\u6240\u89c1\uff0c\u7686\u662f\u5947\u82b1\u5f02\u8349\uff0c\u5e7d\u9999\u6251\u9f3b\uff0c\u4e00\u4e2a \u767d\u987b\u8001\u8005\u6b63\u5728\u6307\u6325\u7740\u4e00\u4e2a\u836f\u50ee\uff0c\u5728\u6d47\u6c34\u704c\u82b1\uff0c\u89c2\u8c37\u795e\u7fc1\u4fbf\u5373\u56b7\u9053\uff1a\u201c\u8001\u8c37\uff0c\u4f60\u53c8\u7ed9\u6211\u62db \u63fd\u4e9b\u4ec0\u4e48\u4e8b\u60c5\u6765\u4e86\uff1f\u201d \u3000\u3000\u8c37\u795e\u7fc1\uff1a\u201c\u957f\u5b59\u4e16\u5144\u8bf7\u533b\u751f\u6765\u4e86\u3002\u201d\u957f\u5b59\u5144\u59b9\u4fbf\u5373\u4e0a\u524d\u8bf7\u5b89\uff0c\u590f\u4faf\u575a\u4e00\u542c\u662f\u6545\u4eba\u5b50 \u5973\uff0c\u5341\u5206\u6b22\u559c\uff0c\u54c8\u54c8\u7b11\u9053\uff0c\u201c\u539f\u6765\u5747\u91cf\u5144\u4e5f\u4e0e\u6211\u540c\u9690\u5ddd\u4e2d\uff0c\u8981\u4e0d\u662f\u4f60\u4eec\u5230\u6765\uff0c\u6211\u8fd8\u5f53\u771f \u4e0d\u77e5\u5462\u3002\u6709\u4ec0\u4e48\u4e8b\u8981\u8bf7\u533b\u751f\uff1f\u201d\u957f\u5b59\u6cf0\u5c06\u7236\u4eb2\u53d7\u4f24\u7684\u4e8b\u60c5\u8bf4\u4e86\u4e00\u904d\uff0c\u590f\u4faf\u575a\u518d\u8be6\u7ec6\u8be2\u95ee \u4e86\u4e00\u4e9b\u4f24\u540e\u7684\u75c7\u72b6\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8981\u662f\u4ed6\u521a\u53d7\u4f24\u4e4b\u65f6\u4fbf\u7531\u6211\u533b\u6cbb\uff0c\u90a3\u5c31\u597d\u529e\uff0c\u73b0\u5728\u5374\u662f \u6709\u70b9\u8fdf\u4e86\u3002\u201d\u957f\u5b59\u58c1\u60ca\u9053\uff1a\u201c\u8fde\u8001\u4f2f\u4e5f\u6ca1\u6cd5\u53ef\u60f3\u4e48\uff1f\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u8fd9\u79cd\u6076\u6bd2\u7684\u6697\u5668\uff0c \u82e5\u662f\u53ca\u65e9\u7597\u6cbb\uff0c\u5373\u7b97\u672c\u4eba\u6709\u5185\u529f\u6839\u5e95\uff0c\u4e5f\u8981\u5341\u5929\u624d\u80fd\u6062\u590d\u539f\u72b6\uff0c\u73b0\u5728\u561b\uff0c\u6700\u5c11\u53ef\u4e5f\u5f97\u4e00 \u5e74\u4e86\u3002\u201d\u5728\u590f\u4faf\u575a\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u800d\u533b\u4e0a\u4e00\u5e74\u624d\u80fd\u7ed9\u75c5\u4eba\u533b\u597d\uff0c\u5185\u5fc3\u5df2\u751a\u611f\u4e0d\u5b89\uff0c\u957f\u5b59\u5144 \u59b9\u542c\u4e86\uff0c\u5374\u662f\u5927\u559c\u8fc7\u671b\u3002\u8c37\u795e\u7fc1\u7b11\u9053\uff1a\u201c\u957f\u5b59\u5747\u91cf\u672c\u6765\u8981\u6253\u7b97\u5341\u5e74\u624d\u80fd\u6062\u590d\u6b66\u529f\u5462\u3002\u597d\uff0c \u8fc7\u4e24\u5929\u6211\u4fbf\u53bb\u5c06\u4ed6\u63a5\u672a\uff0c\u8bf7\u4f60\u6089\u5fc3\u8c03\u6cbb\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u6211\u5475\u4ee5\u6709\u4e2a\u8001\u670b \u53cb\u4f5c\u4f34\u4e86\u3002\u201d \u3000\u3000\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6869\u4e8b\u60c5\u8981\u9ebb\u70e6\u4f60\u5462\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\u3002\u201d\u8c37\u795e\u7fc1\u9053\uff1a \u201c\u6551\u5c09\u8fdf\u70af\u7684\u5f92\u5f1f\uff0c\u201d\u590f\u5019\u575a\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u7684\u5f92\u5f1f\u751f\u4e86\u4ec0\u4e48\u602a\u75c5\uff1f\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u4e0d\u662f \u751f\u75c5\uff0c\u662f\u843d\u5165\u4e86\u4ec7\u4eba\u7684\u624b\u4e2d\u3002\u201d\u5c06\u4e8b\u60c5\u8bf4\u4e86\u4e00\u904d\uff0c\u590f\u4faf\u575a\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u53bb\u6551\uff1f\u201d \u8c37\u795e\u7fc1\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u60dc\u6211\u5df2\u7b54\u5e94\u4e86\u5929\u5c71\u8001\u7b26\u4eca\u751f\u4e0d\u518d\u7528\u5251\u4e86\u3002\u201d\u590f\u4faf\u575a\u5927\u7b11\u9053\uff1a\u201c\u4f60 \u4e0d\u5e72\u7684\u4e8b\u60c5\u5374\u63a8\u7ed9\u6211\u5e72\u3002\u4f60\u5982\u4eca\u624d\u9000\u51fa\u6c5f\u6e56\uff0c\u627e\u5219\u662f\u65e9\u4e8c\u5341\u5e74\u524d\u5df2\u9000\u51fa\u6c5f\u6e56\u4e86\u3002\u201d\u8c37\u795e \u7fc1\u6025\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u7684\u5f92\u5f1f\u540d\u53eb\u674e\u9038\uff0c\u4ed6\u4e43\u662f\u5927\u5510\u7684\u738b\u5b59\u3002\u201d\u590f\u4faf\u575a\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1 \u6c5f\u5c71\u662f\u59d3\u674e\u7684\u8fd8\u662f\u59d3\u6b66\u7684\uff0c\u738b\u5b59\u4e5f\u597d\uff0c\u5e73\u6c11\u4e5f\u597d\uff0c\u4e89\u6597\u4e4b\u4e8b\uff0c\u6211\u90fd\u4e0d\u4e88\u7406\u4f1a\u3002\u8001\u8c37\uff0c\u4f60 \u4e5f\u5fd2\u591a\u4e8b\u4e86\uff0c\u6211\u524d\u4e9b\u65f6\u542c\u8bf4\u4f60\u53ec\u5f00\u4ec0\u4e48\u82f1\u96c4\u5927\u4f1a\uff0c\u6211\u5c31\u6781\u4e0d\u8d5e\u6210\u3002\u82f1\u96c4\u4e0d\u6b7b\uff0c\u5927\u4e71\u4e0d\u6b62\uff0c \u5929\u4e0b\u7eb7\u7eb7\uff0c\u4f55\u82e6\u6765\u54c9\uff1f\u6211\u53ea\u6c42\u5b89\u5b89\u9038\u9038\u7684\u6e21\u8fc7\u4e00\u751f\u3002\u201d\u590f\u4faf\u575a\u670d\u81ba\u8001\u5e84\u5b66\u8bf4\uff0c\u4e3b\u5f20\u6e05\u51c0 \u65e0\u4e3a\uff0c\u56e0\u6b64\u867d\u5177\u6709\u7edd\u4e16\u6b66\u529f\uff0c\u5374\u58ee\u5c81\u4fbf\u6df1\u5c71\u5f52\u9690\u3002\u8c37\u795e\u7fc1\u867d\u662f\u4ed6\u7684\u8001\u53cb\uff0c\u5374\u4e5f\u529d\u4ed6\u4e0d\u52a8\u3002 \u3000\u3000\u8c37\u79cd\u7fc1\u6b63\u5728\u82e6\u6c42\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u9690\u9690\u4f20\u6765\u5e74\u8f6e\u8f98\u8f98\u7684\u58f0\u97f3\uff0c\u957f\u5b59\u58c1\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u5b9a\u662f \u90a3\u6b66\u7384\u971c\u8ffd\u8e2a\u6211\u4eec\u6765\u4e86\u3002\u201d\u8c37\u795e\u7fc1\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u522b\u4eba\u5230\u4f60\u95e8\u524d\u751f\u4e8b\uff0c\u770b\u4f60\u7ba1\u662f\u4e0d\u7ba1\uff1f\u201d\u4e00\u628a\u62c9\u7740\u590f\u4faf\u575a\uff0c\u540c\u51fa\u8349\u576a\u53bb\u770b\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u8f86\u9aa1\u5348\u76f4\u4e0a\u5c71\u5761\uff0c\u8d8a\u6765\u8d8a\u8fd1\uff0c\u8f66\u4e0a\u5750\u7740\u7684\u4eba\u5df2\u7ecf\u53ef\u4ee5\u770b\u5f97\u6e05\u6e05\u695a\u695a\u4e86\u3002\u957f\u5b59 \u6cf0\u53cc\u773c\u5706\u7741\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u7136\u53eb\u9053\uff1a \u3000\u3000\u201c\u662f\u5979\uff0c\u679c\u7136\u662f\u5979\uff01\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u590f\u4faf\u5144\uff0c\u4f60\u51fa\u4e0d\u51fa\u624b\uff1f\u201d\u590f\u4faf\u575a\u53f9\u53e3\u6c14\u9053\uff1a \u201c\u6211\u603b\u4e0d\u80fd\u773c\u7741\u7741\u7684\u770b\u7740\u957f\u5b59\u4e16\u5144\u5728\u6211\u95e8\u524d\u53d7\u4eba\u6b3a\u8d1f\u3002\u201d\u8bf4\u6d3b\u4e4b\u95f4\uff0c\u90a3\u8f86\u9aa1\u8f66\u5df2\u81f3\u8349\u576a \u505c\u4e0b\uff0c\u4f46\u89c1\u4e00\u4e2a\u98ce\u534e\u7edd\u4ee3\u7684\u5973\u5b50\u7b11\u76d8\u76c8\u7684\u8df3\u4e0b\u8f66\u6765\uff0c\u6b63\u662f\u6b66\u7384\u971c\u3002 \u3000\u3000\u674e\u9038\u4e00\u8def\u601d\u6f6e\u8d77\u4f0f\uff0c\u5c24\u5176\u5728\u8fdb\u4e86\u6c13\u5d03\u5c71\u540e\uff0c\u5fc3\u60c5\u66f4\u662f\u52a8\u8361\u4e0d\u4f11\u3002\u6b66\u7384\u971c\u8bf4\u8981\u5c06\u4ed6\u4ea4 \u7ed9\u4e00\u4f4d\u795e\u533b\u56fd\u624b\uff0c\u7a76\u662f\u8c01\u5462\uff1f\u674e\u9038\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6076\u884c\u8005\u548c\u6bd2\u89c2\u97f3\u7684\u6697\u5668\uff0c\u4e43\u662f\u5929\u4e0b\u6700\u6bd2 \u7684\u6697\u5668\uff0c\u636e\u6b66\u7384\u971c\u8bf4\uff0c\u90a3\u4f4d\u56fd\u624b\u975e\u4f46\u53ef\u4ee5\u7ed9\u6211\u89e3\u6bd2\u7597\u4f24\uff0c\u800c\u4e14\u53ef\u4ee5\u52a9\u6211\u6062\u590d\u6b66\u529f\uff0c\u8fd9\u6837 \u8bf4\u6765\uff0c\u90a3\u4f4d\u56fd\u624b\uff0c\u672c\u8eab\u4e5f\u975e\u5177\u6709\u6781\u4e0a\u4e58\u7684\u5185\u529f\u4e0d\u53ef\uff0c\u83ab\u975e\u662f\u5979\u7684\u5e08\u7236\u4e0d\u6210\uff1f\u201d\u60f3\u8d77\u6b66\u7384 \u971c\u4e43\u662f\u4e0e\u4ed6\u654c\u5bf9\u7684\u4eba\uff0c\u81ea\u5df1\u6602\u85cf\u4e03\u5c3a\uff0c\u81ea\u8d1f\u82f1\u96c4\uff0c\u5374\u5f04\u5230\u8981\u53d7\u654c\u4eba\u6069\u60e0\uff0c\u60f3\u5230\u6b64\u5904\uff0c\u5927 \u4e3a\u8bc5\u4e27\uff0c\u51e0\u4e4e\u5c31\u60f3\u8df3\u4e0b\u8f66\u53bb\uff1b\u7136\u800c\u53ef\u60f3\u5230\u6b66\u7384\u971c\u5728\u4e00\u8def\u4e4b\u4e0a\uff0c\u5bf9\u81ea\u5df1\u7684\u6bb7\u52e4\u5475\u62a4\uff0c\u4f3c\u6c34 \u67d4\u60c5\uff0c\u611f\u6fc0\u4e4b\u5ff5\uff0c\u53c8\u4e0d\u7981\u6cb9\u7136\u800c\u751f\uff0c\u4f46\u89c9\u6069\u4ec7\u7ea0\u7ed3\uff0c\u6709\u82e5\u4e71\u4e1d\uff0c\u76d8\u585e\u80f8\u4e2d\uff0c\u526a\u5b83\u4e0d\u65ad\uff0c \u7406\u4e5f\u8fd8\u4e71\uff01\u5f53\u771f\u662f\u201c\u522b\u6709\u4e00\u822c\u6ecb\u5473\u5728\u5fc3\u5934\u201d\uff01 \u3000\u3000\u6b63\u5728\u60c5\u601d\u60d8\u60d8\uff0c\u5ffd\u542c\u5f97\u8f66\u58f0\u560e\u7136\u800c\u4e0a\uff0c\u6b66\u7384\u971c\u5bf9\u4ed6\u7b11\u9053\uff1a \u3000\u3000\u201c\u5230\u5566\uff0c\u96be\u5f97\u4f60\u7684\u51e0\u4f4d\u76f8\u8bc6\u90fd\u5728\u8fd9\u513f\u3002\u201d\u674e\u9038\u5750\u4e86\u4e0b\u6765\uff0c\u9760\u7740\u8f66\u57ab\uff0c\u63ed\u5e18\u4e00\u770b\uff0c\u51e0 \u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u4f46\u89c1\u8fce\u800c\u800c\u6765\u7684\u7adf\u7136\u662f\u8c37\u795e\u7fc1\uff0c\u5728\u8c37\u795e\u7fc1\u80cc\u540e\u7684\uff0c\u53c8\u6b63\u662f\u521a\u624d \u5728\u8def\u4e0a\u622a\u52a8\u9aa1\u8f66\u3001\u88ab\u6b66\u7384\u971c\u6253\u8d25\u7684\u90a3\u5bf9\u9752\u5e74\u7537\u5973\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u4f4d\u767d\u987b\u98d8\u62c2\u7684\u8001\u8005\uff0c\u4e5f\u597d \u50cf\u662f\u5728\u54ea\u513f\u89c1\u8fc7\u4e00\u822c\u3002 \u3000\u3000\u590f\u4faf\u575a\u62a2\u5feb\u4e00\u6b65\uff0c\u8fce\u4e0a\u9aa1\u8f66\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u8fd9\u4f4d\u5fc3\u5982\u6b62\u6c34\u7684\u4e16\u5916\u9ad8\u4eba\uff0c\u4e5f\u4e0d\u7981\u8d77\u4e86\u4e00 \u8be7\u610f\uff0c\u4ed6\u66fe\u542c\u8c37\u4f38\u7fc1\u8bf4\u8fc7\u82f1\u96c4\u5927\u4f1a\u7684\u4e8b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u7adf\u662f\u8fd9\u6837\u4e00\u4f4d\u82b1\u6735\u822c\u7684\u5c0f\u59d1 \u5a18\uff0c\u5979\u628a\u5929\u4e0b\u82f1\u96c4\u90fd\u6253\u8d25\u4e86\u3002\u8fde\u8c37\u795e\u7fc1\u7684\u8e51\u4e91\u5251\u6cd5\u90fd\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u76c8\u76c8\u4e00\u7b11\uff0c\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u6b66\u7384\u971c\u62dc\u89c1\u590f\u4faf\u5148\u751f\u3002\u201d\u590f\u4faf\u575a\u53c8\u662f\u4e00\u6115\uff0c\u5fc3 \u9053\uff1a\u201c\u5979\u600e\u4e48\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u201d\u8981\u77e5\u590f\u4faf\u575a\u867d\u7136\u8eab\u6000\u7edd\u6280\uff0c\u4f46\u4ed6\u4e00\u5411\u81ea\u7518\u6de1\u6cca\uff0c\u4ece\u6765\u66fe \u5728\u6c5f\u6e56\u4e0a\u51fa\u8fc7\u98ce\u5934\uff0c\u800c\u4e14\u58ee\u5e74\u5f52\u9690\uff0c\u9664\u4e86\u6781\u6709\u9650\u7684\u51e0\u4f4d\u8001\u670b\u53cb\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u4ec0\u4e48\u4eba\u77e5\u9053 \u4ed6\u3002\u7136\u800c\u8fd9\u4e2a\u770b\u6765\u8fd8\u4e0d\u5230\u4e8c\u5341\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u5374\u4e00\u89c1\u9762\u5c31\u53eb\u51fa\u4e86\u4ed6\u7684\u540d\u5b57\u3002 \u3000\u3000\u590f\u4faf\u575a\u6014\u4e86\u4e00\u6014\uff0c\u7785\u7740\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u9a71\u8f66\u4e0a\u5c71\uff0c\u5c31\u662f\u4e13\u8bda\u4e3a\u4e86\u62dc\u8bbf\u6211\u4e48\uff1f\u201d\u6b66\u7384 \u971c\u9053\uff1a\u201c\u65e0\u4e8b\u4e0d\u767b\u4e09\u5b9d\u6bbf\uff0c\u590f\u4faf\u5148\u751f\uff0c\u4f60\u8eab\u8d1f\u91d1\u9488\u56fd\u624b\u4e4b\u540d\uff0c\u81ea\u5f53\u77e5\u9053\u6211\u7684\u6765\u610f\u3002\u201d\u590f \u4faf\u575a\u5e73\u751f\u786e\u662f\u6cbb\u8fc7\u4e0d\u5c11\u7591\u96be\u602a\u75c7\uff0c\u4f46\u4ed6\u4ece\u6765\u4e0d\u80af\u5411\u75c5\u5bb6\u900f\u9732\u8fc7\u771f\u5b9e\u7684\u59d3\u540d\uff0c\u8fd9\u201c\u91d1\u9488\u540c \u624b\u201d\u7684\u5c01\u53f7\u4e5f\u53ea\u662f\u51e0\u4f4d\u8001\u670b\u53cb\u79c1\u4e0b\u79f0\u547c\u4ed6\u7684\uff0c\u6b66\u7384\u971c\u5374\u8bf4\u5f97\u90a3\u6837\u81ea\u7136\uff0c\u7adf\u4f3c\u65e9\u5c31\u719f\u8bc6\u4e00 \u822c\uff01 \u3000\u3000\u590f\u4faf\u575a\u7591\u5fc3\u4eba\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u662f\u627e\u6211\u770b\u75c5\u6765\u7684\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6709\u4e00 \u4f4d\u670b\u53cb\u4e2d\u4e86\u6076\u884c\u8005\u7684\u4e00\u679a\u788e\u9aa8\u94b1\u9556\uff0c\u53c8\u4e2d\u4e86\u6bd2\u89c2\u97f3\u7684\u4e24\u53e3\u900f\u7a74\u795e\u9488\uff0c\u60f3\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4e86 \u4f60\u8001\u5148\u751f\uff0c\u522b\u4eba\u65ad\u65ad\u4e0d\u80fd\u533b\u6cbb\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5728\u573a\u4eba\u7b49\uff0c\u5747\u611f\u610f\u5916\uff0c\u957f\u5b59\u5144\u59b9\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u4e0d\u662f\u4e3a\u4e86\u8ffd\u6355\u6211\u4eec\u6765\u7684\uff01\u201d \u8c37\u4f38\u7fc1\u5374\u5728\u60f3\u9053\uff1a\u201c\u674e\u9038\u600e\u7684\u4f1a\u7ed9\u90a3\u4e24\u4e2a\u9b54\u5934\u4f24\u4e86\uff1f\u90a3\u4e24\u4e2a\u9b54\u5934\u4e0d\u662f\u53d7\u4e86\u88f4\u708e\u4e4b\u8058\u4e48\uff1f \u600e\u7684\u4f1a\u6253\u8d77\u674e\u9038\u6765\u4e86\uff1f\u82e5\u975e\u674e\u9038\uff0c\u5979\u53c8\u4e3a\u8c01\u6c42\u533b\uff1f\u201d\u539f\u6765\u8c37\u795e\u7fc1\u521a\u624d\u542c\u8bf4\u674e\u9038\u53d7\u4f24\uff0c\u5fc3 \u4e2d\u5c31\u4e00\u76f4\u4ee5\u4e3a\u662f\u6b66\u7384\u971c\u5c06\u4ed6\u6253\u4f24\uff0c\u597d\u62bc\u4e0a\u957f\u5b89\u9886\u529f\u4eba\u7684\u3002 \u3000\u3000\u4f46\u5176\u4e2d\u6700\u611f\u5230\u610f\u5916\u7684\u8fd8\u662f\u674e\u9038\uff0c\u4ed6\u4e00\u8def\u731c\u6d4b\uff0c\u4e0d\u77e5\u6b66\u7384\u971c\u8981\u5c06\u4ed6\u4ea4\u4e0e\u4f55\u4eba\uff0c\u4e0d\u77e5\u8fd8 \u8981\u53d7\u4ec0\u4e48\u6298\u8fb1\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6b66\u4e48\u971c\u6240\u8bf4\u7684\u540d\u533b\uff0c\u539f\u6765\u5c31\u662f\u4e98\u4faf\u575a\uff01\u662f\u4ed6\u5e05\u7236\u51e0\u4e2a\u6700\u597d \u670b\u53cb\u4e4b\u4e00\u7684\u590f\u4faf\u575a\uff0c\u674e\u9038\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\u590f\u4faf\u575a\uff0c\u5374\u66fe\u542c\u5e08\u7236\u63cf\u7ed8\u8fc7\u4ed6\u90a3\u6e05\u5947\u7684\u76f8\u8c8c\uff0c\u5f85 \u6240\u5230\u4e86\u6b66\u7384\u971c\u53eb\u51fa\u590f\u4faf\u575a\u7684\u540d\u5b57\uff0c\u8fd9\u624d\u970d\u7136\u7701\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u597d\u50cf\u5728\u90a3\u513f\u89c1\u8fc7\u4e00\u822c\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u660e\u73e0\uff0c\u4f60\u5c06\u674e\u516c\u5b50\u6276\u4e0b\u8f66\u6765\u3002\u201d\u8f6c\u8fc7\u5934\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u5c06\u4f60\u4ea4\u6258\u7ed9\u590f\u4faf\u575a\u8001\u5148\u751f\uff0c\u4f60\u603b\u53ef\u4ee5\u653e\u5fc3\u4e86\u5427\uff01\u201d\u674e\u9038\u5fc3\u4e0a\u7684\u9634\u973e\u4e00\u626b\u800c\u7a7a\uff0c \u60f3\u9053\uff1a\u201c\u6211\u5f53\u771f\u662f\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\u4e86\uff0c\u539f\u6765\u5979\u7ed9\u6211\u5b89\u6392\u5f97\u8fd9\u4e48\u59a5\u8d34\uff01\u201d\u65e2\u662f\u60ed\u6127\uff0c \u53c8\u662f\u611f\u6fc0\uff0c\u6014\u6014\u7684\u770b\u7740\u6b66\u7384\u971c\uff0c\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u590f\u4faf\u575a\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8001\u673d\u867d\u7136\u7565\u901a\u533b\u672f\uff0c\u5374\u5e76\u672a\u6302\u51fa\u62db\u724c\uff0c\u60ac\u58f6\u6d4e\u4e16\uff0c\u533b\u4e0d \u533b\u75c5\uff0c\u53ef\u884c\u770b\u6211\u559c\u4e0d\u559c\u6b22\u3002\u201d\u674e\u9038\u9887\u611f\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u54a6\uff0c\u96be\u9053\u4ed6\u8fd8\u672a\u66fe\u77e5\u9053\u6211\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u522b\u7684\u75c5\u4eba\uff0c\u4f60\u4e0d\u9ad8\u5174\u533b\u6cbb\u4e5f\u8fd8\u7f62\u4e86\uff0c\u8fd9\u4e2a\u75c5\u4eba\u561b\uff0c\u4f60\u60f3\u4e0d\u6536\uff0c\u53ea\u6015 \u4f60\u7684\u8001\u670b\u53cb\u4e5f\u4e0d\u7b54\u5e94\uff0c\u8c37\u8001\u76df\u4e3b\uff0c\u5e78\u597d\u4f60\u4e5f\u5728\u8fd9\u513f\uff0c\u4f3c\u4e4e\u4e0d\u5fc5\u627e\u591a\u8d39\u5507\u820c\u4e86\u3002\u201d\u8c37\u795e\u7fc1 \u4e00\u65f6\u95f4\u731c\u4e0d\u900f\u590f\u4faf\u575a\u7684\u7528\u610f\uff0c\u8fdf\u7591\u672a\u7b54\u3002\u53ea\u542c\u5f97\u590f\u4faf\u575a\u51b7\u5ced\u8bf4\u9053\uff1a\u201c\u662f\u4f60\u6765\u5411\u6211\u6c42\u533b\u7684\uff0c \u53ef\u662f\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u590f\u5019\u575a\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5c31\u53ea\u51b2\u7740\u4f60\u8bf4\u8bdd\uff0c\u4f60\u7684\u5e05\u7236\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u6b63\u662f\u5927\u5bb6\u65e9\u5df2\u5b58\u5728\u5fc3\u91cc\u7684\u7591\u56e2\uff0c\u8fde\u674e\u9038\u4e5f\u575a\u8d77\u8033\u6735\u6765\u542c\uff0c\u6b66\u7384\u971c\u773c\u5149\u4e00\u626b\uff0c \u4ece\u674e\u9038\u4e0e\u8c37\u795e\u7fc1\u7684\u9762\u4e0a\u626b\u8fc7\uff0c\u6700\u540e\u5728\u590f\u4faf\u575a\u7684\u8eab\u4e0a\uff0e\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u590f\u4faf\u5148\u751f\u662f\u4e16\u5916\u800c\u4eba\uff0c \u96be\u9053\u4e5f\u50cf\u4e16\u4fd7\u533b\u751f\u90a3\u822c\u52bf\u5229\uff0c\u5fc5\u987b\u95ee\u6c42\u533b\u7684\u6709\u4ec0\u4e48\u8db3\u4ee5\u5938\u8000\u7684\u4eb2\u621a\u5e08\u53cb\u624d\u80af\u7559\u533b\u4e48\uff1f\u201d \u590f\u4faf\u575a\u7ed9\u5979\u7528\u8bf4\u4e00\u8feb\uff0c\u957f\u987b\u4e00\u62c2\uff0c\u534a\u664c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u662f\u767d\u767d\u7ed9\u4eba\u770b\u75c5\u7684\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u533b\u751f\u6536\u53d6\u8bca\u91d1\uff0c\u90a3\u662f\u5929\u516c\u5730 \u9053\u7684\u4e66\u3002\u201d\u590f\u5019\u575a\u9053\uff1a\u201c\u91d1\u94f6\u4e8e\u6211\u65e0\u7528\uff0c\u4f46\u6211\u4e5f\u4e0d\u6562\u574f\u4e86\u884c\u89c4\uff0c\u6211\u770b\u4e00\u4e2a\u75c5\u4eba\uff0c\u5c31\u8981\u6536 \u4e00\u4ef6\u793c\u7269\uff0c\u8fd9\u793c\u7269\u53ef\u5f97\u6211\u6b22\u559c\u7684\u624d\u6210\u3002\u4f60\u6709\u4ec0\u4e48\u793c\u7269\u53ef\u4ee5\u9001\u6211\uff1f\u201d \u3000\u3000\u8c37\u795e\u7fc1\u8be7\u5f02\u4e0d\u5df2\uff0c\u4ed6\u542c\u590f\u5019\u575a\u8a00\u4e2d\u4e4b\u610f\uff0c\u5206\u660e\u662f\u7c4d\u6b64\u51fa\u4e2a\u96be\u9898\u6765\u8003\u6b66\u7384\u971c\uff0c\u5fc3\u5185\u60f3 \u9053\uff1a\u201c\u82e5\u7136\u5979\u7684\u793c\u7269\u4e0d\u5408\u4f60\u7684\u5fc3\u610f\uff0c\u96be\u9053\u4f60\u5c31\u8896\u624b\u4e0d\u7ba1\u4e86\u4e48\uff1f\u201d\u8981\u77e5\u8c37\u795e\u7fc1\u4e0e\u6b66\u7384\u971c\u867d \u7136\u662f\u5c45\u4e8e\u654c\u5bf9\u7684\u5730\u4f4d\uff0c\u4f46\u6b64\u9645\u7684\u5fc3\u601d\u5374\u5b8c\u5168\u4e0e\u6b66\u7384\u971c\u76f8\u540c\uff0c\u90a3\u5c31\u662f\u5207\u671b\u590f\u4faf\u575a\u5c06\u674e\u9038\u7559 \u4e0b\u6765\u533b\u6cbb\uff0c\u5374\u4e0d\u77e5\u590f\u4faf\u575a\u4f55\u4ee5\u8981\u4e00\u518d\u5201\u96be\u3002 \u3000\u3000\u4f46\u89c1\u6b66\u7384\u971c\u5fae\u5fae\u4e00\u7b11\uff0c\u4fed\u887d\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u5148\u751f\u4e16\u5916\u9ad8\u4eba\uff0c\u5c0f\u5973\u5b50\u4e0d\u6562\u4ee5\u4e16\u4fd7\u4e4b\u7269\u4eb5 \u6e0e\u5148\u751f\uff0c\u53ea\u597d\u501f\u82b1\u656c\u4f5b\uff0c\u804a\u8868\u5bf8\u5fc3\uff01\u201d\u8bf4\u7f62\uff0c\u89e3\u4e0b\u675f\u8170\u7ef8\u5e26\uff0c\u626c\u7a7a\u4e00\u5377\uff0c\u9644\u8fd1\u662f\u4e00\u68f5\u82b1 \u6811\uff0c\u8f7b\u7ef8\u8fc7\u5904\uff0c\u6709\u5982\u5229\u5200\u5feb\u526a\uff0c\u5c06\u5341\u51e0\u6735\u5927\u7ea2\u82b1\u90fd\u201c\u526a\u201d\u4e86\u4e0b\u6765\uff0c\u7ea2\u7ef8\u4e00\u5377\u4e00\u6536\uff0c\u84e6\u7136 \u6492\u51fa\uff0c\u4f46\u89c1\u6ee1\u7a7a\u82b1\u74e3\uff0c\u8fde\u6210\u4e00\u7ebf\uff0c\u5411\u590f\u4faf\u575a\u6fc0\u5c04\u800c\u6765\uff01 \u3000\u3000\u957f\u5b59\u5144\u59b9\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u8fd9\u624d\u77e5\u9053\u6b66\u7384\u971c\u7684\u529f\u529b\u4e4b\u6df1\uff0c\u8fdc\u975e\u4ed6\u4eec\u6240\u80fd\u6bd4\u62df\u3002\u8c37\u795e\u7fc1 \u4e0e\u674e\u9038\u66f4\u770b\u4e86\u51fa\u7c73\uff1a\u90a3\u6ee1\u7a7a\u82b1\u74e3\u7adf\u662f\u6392\u6210\u4e86\u4e00\u884c\u8349\u5b57\uff0c\u51dd\u795e\u7ec6\u8fa8\uff0c\u9690\u7ea6\u8ba4\u5f97\u51fa\u6392\u7684\u662f\uff1a \u201c\u4e0d\u53ef\u8bf4\uff0c\u4e0d\u53ef\u8bf4\uff01\u201d\u516d\u4e2a\u8349\u4e66\u3002\u4e24\u4eba\u5747\u662f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e0d\u6653\u5f97\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u4f46\u542c\u5f97\u590f\u4faf\u575a\u4e00\u58f0\u957f\u5578\uff0c\u53cc\u62bd\u4e00\u62c2\uff0c\u6ee1\u7a7a\u82b1\u74e3\u767b\u65f6\u6539\u4e86\u65b9\u5411\uff0c\u800c\u4e14\u6392\u6210 \u4e86\u53e6\u5916\u4e00\u884c\u8349\u4e66\uff0c\u8fd9\u65f6\u8fde\u957f\u5b59\u5144\u59b9\u4e5f\u770b\u5f97\u6e05\u6e05\u695a\u695a\u4e86\uff0c\u90a3\u662f\uff1a\u201c\u5982\u4e4b\u4f55\uff1f\u5982\u4e4b\u4f55\uff1f\u201d\u516d \u4e2a\u8349\u5b57\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u731b\u7136\u4e00\u9192\uff0c\u604d\u7136\u5728\u609f\uff0c\u6b66\u7384\u7bb1\u7528\u82b1\u74e3\u6392\u51fa\u7684\u201c\u4e0d\u53ef\u8bf4\uff0c\u4e0d\u53ef\u8bf4\u3002\u201d\u516d\u5b57\uff0c\u6562 \u60c5\u4e43\u662f\u7b54\u8986\u590f\u4faf\u575a\u521a\u624d\u7684\u8be2\u95ee\uff0c\u4e0d\u613f\u900f\u9732\u5979\u5e08\u7236\u7684\u59d3\u540d\uff0c\u4f46\u5979\u5e08\u7236\u7684\u59d3\u540d\uff0c\u5374\u4f55\u4ee5\u201c\u4e0d \u53ef\u8bf4\uff0c\u4e0d\u53ef\u8bf4\u201d\u5462\uff1f\u8fd9\u5c31\u975e\u8c37\u795e\u7fc1\u6240\u80fd\u53c2\u900f\u4e86\u3002\u66f4\u96be\u89e3\u7684\u662f\uff1a\u590f\u4faf\u575a\u90a3\u201c\u5982\u4e4b\u4f55\uff1f\u5982\u4e4b \u4f55\uff1f\u201d\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u4ed6\u4eec\u4e24\u4eba\u6697\u8f83\u6b66\u529f\uff0c\u6240\u6392\u51fa\u7684\u8fd9\u4e24\u884c\u8349\u4e66\uff0c\u53c8\u50cf\u8c1c\u8bed\u4e00\u822c\u7684\u5404\u9690 \u673a\u950b\uff0c\u53c8\u597d\u4f3c\u5404\u81ea\u70b9\u7834\u5bf9\u65b9\u7684\u6765\u5386\uff0c\u5c40\u4e2d\u4eba\u60f3\u6765\u660e\u767d\uff0c\u5c40\u5916\u4eba\u5374\u662f\u4e00\u7247\u832b\u7136\uff01 \u3000\u3000\u8c37\u795e\u7fc1\u4e0e\u590f\u4faf\u575a\u867d\u662f\u4e09\u5341\u5e74\u4ee5\u4e0a\u7684\u670b\u53cb\uff0c\u4f46\u5bf9\u4ed6\u5c11\u5e74\u65f6\u5019\u7684\u4e8b\u60c5\u4ea6\u662f\u4e00\u65e0\u6240\u77e5\uff0c\u89c1 \u6b64\u516d\u5b57\uff0c\u5fc3\u4e2d\u8be7\u5f02\u4e0d\u5df2\uff0c\u5ffd\u542c\u5f97\u590f\u4faf\u575a\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u4e0d\u53ef\u8bf4\u3001\u4e0d\u53ef\u8bf4\u3002\u5982\u4e4b\u4f55\uff1f\u5982\u4e4b \u4f55\uff1f\u201d\u8c37\u795e\u7fc1\u4e00\u51db\uff0c\u77e5\u9053\u590f\u4faf\u575a\u662f\u793a\u610f\u53eb\u81ea\u5df1\u4e0d\u53ef\u53d1\u95ee\uff0c\u5373\u7b97\u95ee\u5979\uff0c\u5979\u4e5f\u662f\u4e0d\u4f1a\u8bf4\u7684\u3002 \u3000\u3000\u590f\u4faf\u575a\u8f7b\u8f7b\u541f\u4e86\u8fd9\u4e48\u4e24\u65ec\uff0c\u53cc\u8896\u53c8\u662f\u4e00\u62c2\uff0c\u6ee1\u7a7a\u82b1\u6735\uff0c\u5982\u9047\u72c2\u98ce\uff0c\u7247\u7247\u98d8\u843d\u3002\u590f\u4faf \u575a\u9edd\u7136\u8bf4\u9053\uff1a\u201c\u75c5\u4eba\u6211\u6536\u7559\u4e86\uff0c\u4f60\u56de\u53bb\u5427\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u95ee\u5019\u4f60\u7684\u5e08\u7236\uff0c \u55ef\uff0c\u4e0d\u95ee\u5019\u4e5f\u7f62\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u5c06\u674e\u9038\u8f7b\u8f7b\u6276\u8d77\uff0c\u4ea4\u7ed9\u590f\u4faf\u575a\uff0c\u590f\u4faf\u575a\u62db\u624b\u53eb\u957f\u5b59\u6cf0\u8fc7\u6765\uff0c\u5c06\u674e\u9038\u80cc\u8d77\uff0c\u674e \u9038\u56de\u5934\u4e00\u77a5\uff0c\u6b63\u597d\u4e0e\u6b66\u7384\u971c\u7684\u773c\u5149\u76f8\u63a5\uff0c\u4f46\u89c9\u90a3\u773c\u5149\u4e2d\u4f3c\u542b\u7740\u65e0\u9650\u7684\u6b23\u6170\uff0c\u53c8\u542b\u7740\u65e0\u9650 \u7684\u60b2\u54c0\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\uff0c\u674e\u9038\u4ea6\u81ea\u5fc3\u5f26\u98a4\u6296\uff0c\u5fc3\u4e8b\u5982\u6f6e\uff01\u8fd9\u771f\u662f\u4e00\u6bb5\u5947\u602a\u7684\u611f\u60c5\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u83ab \u540d\u5176\u5999\uff01\u8fd9\u51e0\u5929\u6765\u4ed6\u4e00\u76f4\u5728\u62c5\u5fc3\u5bb3\u6015\uff0c\u4e0d\u77e5\u6b66\u7384\u971c\u5c06\u4ed6\u600e\u6837\u5904\u7f6e\u3002\u66f4\u5bb3\u6015\u9690\u5165\u6b66\u7384\u971c\u60c5 \u7f51\u4e4b\u4e2d\uff0c\u7126\u8651\u7740\u4e0d\u77e5\u600e\u6837\u624d\u80fd\u8131\u79bb\u6b66\u7384\u971c\u7684\u638c\u63e1\uff1f\u73b0\u5728\u8c1c\u8bed\u63ed\u6653\u4e86\uff0c\u6b66\u7384\u971c\u4e5f\u8981\u79bb\u5f00\u4ed6 \u4e86\uff0c\u4ed6\u53cd\u800c\u6005\u6005\u60d8\u60d8\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u7adf\u662f\u96be\u4ee5\u81ea\u6291\u7684\u751f\u8d77\u4e86\u60dc\u522b\u4e4b\u60c5\u3002 \u3000\u3000\u4ed6\u6025\u5fd9\u907f\u5f00\u4e86\u6b66\u7384\u971c\u7684\u773c\u5149\uff0c\u4f0f\u5728\u957f\u5b59\u6cf0\u7684\u80a9\u5934\u4e0a\u5411\u8c37\u795e\u7fc1\u70b9\u9996\u793a\u610f\uff0c\u7b54\u8c22\u4ed6\u7684\u6170 \u95ee\u3002\u957f\u5b59\u6cf0\u521a\u884c\u5f97\u4e24\u6b65\uff0c\u5ffd\u542c\u5f97\u6b66\u7384\u971c\u7684\u811a\u6b65\u58f0\u53c8\u8ffd\u4e86\u4e0a\u6765\uff0c\u674e\u9038\u4e0d\u7531\u81ea\u5df1\u7684\u53c8\u56de\u8fc7\u53bb\uff0c \u53ea\u89c1\u6b66\u7384\u971c\u4e00\u624b\u62b1\u7740\u4ed6\u7684\u5360\u7434\uff0c\u4e00\u624b\u62ff\u7740\u4ed6\u7684\u5b9d\u5251\uff0c\u51c4\u7136\u7b11\u9053\uff1a\u201c\u6211\u51e0\u4e4e\u5fd8\u8bb0\u4e86\uff0c\u4f60\u7684 \u968f\u8eab\u7434\u5251\uff0c\u8fd8\u7559\u5728\u8f66\u4e2d\u3002\u201d\u674e\u9038\u5589\u5934\u553e\u54bd\uff0c\u820c\u5934\u6253\u7ed3\uff0c\u542b\u542b\u7cca\u7cca\u7684\u8bf4\u4e86\u201c\u591a\u8c22\u201d\u4e24\u5b57\uff0c \u58f0\u97f3\u5982\u6b64\u4e4b\u8f7b\uff0c\u53ea\u6709\u4ed6\u81ea\u5df1\u624d\u542c\u5f97\u89c1\uff0c\u7136\u800c\u4ed6\u5374\u770b\u5230\u6b66\u7384\u971c\u7684\u773c\u775b\u95ea\u8fc7\u4e86\u4e00\u7ebf\u5149\u8292\u3002 \u3000\u3000\u957f\u5b59\u58c1\u66ff\u674e\u9038\u63a5\u8fc7\u4e86\u6b66\u7384\u971c\u624b\u4e2d\u7684\u7434\u5251\uff0c\u5979\u6000\u7740\u6068\u610f\u7684\u77aa\u4e86\u6b66\u7384\u971c\u4e00\u773c\uff0c\u7136\u800c\u6b66\u7384 \u971c\u5374\u4f3c\u4e1d\u6beb\u6ca1\u6709\u7559\u610f\u5979\u3002\u957f\u5b59\u58c1\u770b\u4e86\u4e00\u773c\u674e\u9038\u7684\u795e\u60c5\uff0c\u82e5\u6709\u6240\u611f\u7684\u4f4e\u4e0b\u5934\u6765\u3002 \u3000\u3000\u8f66\u58f0\u8f98\u8f98\uff0c\u6b66\u7384\u971c\u5df2\u4e0a\u4e86\u9aa1\u8f66\u8d70\u4e86\u3002\u674e\u9038\u597d\u4f3c\u4ece\u68a6\u91cc\u9192\u7c73\uff0c\u832b\u7136\u7684\u671b\u7740\u5979\u7684\u9aa1\u8f66\u8fdc \u53bb\u3002\u8fd9\u51e0\u5929\u6765\u771f\u4f3c\u505a\u4e86\u4e00\u573a\u5927\u68a6\uff0c\u90a3\u662f\u4ee4\u4eba\u5fc3\u60b8\u7684\u6076\u68a6\uff0c\u53c8\u662f\u4ee4\u4eba\u4f9d\u604b\u7684\u7f8e\u68a6\uff0c\u7136\u800c\u4e0d \u7ba1\u662f\u6076\u68a6\u4e5f\u597d\uff0c\u662f\u7f8e\u68a6\u4e5f\u597d\uff0c\u8fd9\u573a\u5927\u68a6\u7ec8\u4e8e\u662f\u7ed3\u675f\u4e86\uff0c\u674e\u9038\u5fc3\u4e0a\u5ffd\u7136\u63a0\u8fc7\u4e86\u4e00\u4e2a\u5ff5\u5934\uff0c \u201c\u4eca\u751f\u4eca\u5410\uff0c\u4e0d\u77e5\u8fd8\u80fd\u4e0d\u80fd\u518d\u89c1\u5979\u4e00\u9762\u3002\u201d \u3000\u3000\u6ca1\u6709\u4eba\u5411\u6b66\u7384\u971c\u9053\u522b\uff0c\u4eba\u5bb6\u90fd\u6709\u7740\u4e00\u80a1\u5f02\u6837\u5fc3\u60c5\u3002\u8c37\u795e\u7fc1\u8f7b\u8f7b\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c \u201c\u8fd9\u5973\u5b69\u5b50\u7684\u884c\u4e8b\u771f\u662f\u53e4\u602a\uff0c\u6211\u600e\u4e5f\u60f3\u4e0d\u5230\u5979\u4f1a\u628a\u674e\u9038\u8fd9\u6837\u8f7b\u6613\u7684\u4fbf\u4ea4\u7ed9\u4e86\u6211\u4eec\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u5c06\u674e\u9038\u80cc\u56de\u5c4b\u5185\uff0c\u5b89\u7f6e\u5728\u4e00\u95f4\u9759\u5ba4\u91cc\uff0c\u4f17\u4eba\u73af\u7ed5\u75c5\u69bb\u4e4b\u524d\uff0c\u7126\u8651\u7684\u5728\u770b\u590f\u4faf \u575a\u66ff\u4ed6\u8bca\u6cbb\u7684\u7ed3\u679c\uff0c\u590f\u4faf\u575a\u95ed\u53e3\u51dd\u795e\u3002 \u3000\u3000\u628a\u4e86\u4e00\u4e0b\u8109\u606f\uff0c\u6709\u70b9\u5947\u602a\u7684\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8fdf\u4e86\u4e00\u4e9b\uff1f\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u4e0d\uff0c\u4ed6\u4f53\u5185 \u6c14\u673a\u6d41\u7545\uff0c\u5373\u7b97\u6ca1\u6709\u6211\u66ff\u4ed6\u533b\u6cbb\uff0c\u4e5f\u53ef\u4ee5\u4fdd\u5168\u6027\u547d\u3002\u4e0d\u8fc7\u4e0d\u80fd\u6062\u590d\u6b66\u529f\u7f62\u4e86\u3002\u201d\u8c37\u795e\u7fc1 \u660e\u660e\u77e5\u9053\u674e\u9038\u4e0d\u53ef\u80fd\u6709\u90a3\u6837\u6df1\u6e5b\u7684\u5185\u529f\uff0c\u5927\u611f\u8be7\u5f02\u3002\u674e\u9038\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u90a3\u5927\u7ea6\u662f\u6b66\u7384\u971c \u66ff\u6211\u8c03\u7406\u7684\u3002\u201d\u4ed6\u6781\u529b\u88c5\u4f5c\u6f2b\u4e0d\u7ecf\u610f\u7684\u8bf4\u51fa\u6765\uff0c\u7136\u800c\u4ece\u4ed6\u6545\u4f5c\u5e73\u9759\u7684\u8bed\u8c03\u4e2d\uff0c\u4ecd\u7136\u542c\u5f97 \u51fa\u4ed6\u5fc3\u60c5\u7684\u6fc0\u52a8\u3002 \u3000\u3000\u590f\u4faf\u575a\u5728\u4ed6\u7684\u80a9\u4e95\u7a74\u3001\u5929\u67a2\u7a74\u548c\u98ce\u5e9c\u7a74\u4e0a\u5404\u63d2\u4e86\u4e00\u53e3\u91d1\u9488\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7528\u91d1\u9488\u66ff\u4f60 \u62d4\u9664\u4f59\u6bd2\uff0c\u5927\u7ea6\u534a\u4e2a\u6708\u7684\u65f6\u5149\uff0c\u4f60\u7684\u6b66\u529f\u4fbf\u53ef\u4ee5\u5b8c\u5168\u6062\u590d\u3002\u201d\u8c37\u795e\u7fc1\u82e5\u6709\u6240\u601d\uff0c\u95ee\u590f\u4faf \u575a\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u548c\u4ed6\u8bf4\u8bdd\u5417\uff1f\u201d\u590f\u4faf\u575a\u9053\uff0c\u201c\u4ed6\u7684\u5371\u9669\u65f6\u671f\u5df2\u8fc7\uff0c\u7a0d\u4e3a\u7528\u7528\u5fc3\u795e\u4e5f\u65e0\u59a8 \u788d\u7684\u4e86\u3002\u201d\u8c37\u795e\u7fc1\u671f\u671f\u827e\u827e\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u674e\u8d24\u4f84\uff0c\u6211\u5bf9\u4f60\u751a\u4e3a\u62b1\u6127\u3002\u201d \u3000\u3000\u674e\u9038\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u809a\u4e8b\u53d8\u5316\uff0c\u672c\u6765\u96be\u6d4b\uff0c\u5c3d\u4e86\u4eba\u529b\uff0c\u5929\u610f\u96be\u56de\uff0c\u90a3\u4e5f\u662f\u65e0\u8bdd \u53ef\u8bf4\u7684\u4e86\u3002\u201d\u4ed6\u4ee5\u4e3a\u8c37\u67f3\u7fc1\u6240\u8bf4\u7684\u201c\u62b1\u6127\u201d\uff0c\u4e43\u662f\u6307\u201c\u82f1\u96c4\u5927\u4f1a\u201d\u7684\u5931\u8d25\uff0c\u5f04\u5230\u4ed6\u505a\u4e0d \u6210\u76df\u4e3b\u800c\u8a00\u3002\u8c37\u795e\u7fc1\u5bf9\u8fd9\u4e00\u4ef6\u4e8b\u786e\u5b9e\u4e5f\u662f\u803f\u803f\u4e8e\u5fc3\uff0c\u4e0d\u8fc7\u6b64\u9645\u4ed6\u5374\u662f\u53e6\u6709\u6240\u611f\u3002 \u3000\u3000\u4ed6\u9ed8\u7136\u5140\u8bed\uff0c\u534a\u664c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u600e\u6837\u53d7\u4e86\u90a3\u4e24\u4e2a\u9b54\u5934\u6240\u4f24\u7684\uff1f\u201d\u674e\u9038\u5c06\u90a3\u65e5\u9047\u89c1\u6076 \u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u7684\u4e8b\u544a\u8bc9\u4e86\u4ed6\uff0c\u8c37\u795e\u7fc1\u559f\u7136\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u4e5f\u77e5\u9053\u8fd9\u4e24\u4e2a\u9b54\u5934\u6076\u6027\u96be\u9a6f\uff0c\u53ef\u8fd8\u6ca1\u6709\u6599\u5230\u4ed6\u4eec\u7adf\u6562\u6697\u5bb3\u592a\u5b50\uff0c\u53c8\u6765\u4f24\u4f60\u3002\u5728 \u5df4\u5dde\u90a3\u4e00\u665a\uff0c\u6211\u6ca1\u6709\u5c06\u4ed6\u4eec\u6f5c\u6765\u7684\u6d88\u606f\u544a\u8bc9\u4f60\uff0c\u8fd9\uff0c\u8fd9\u2014\u2014\u201d\u674e\u9038\u622a\u65ad\u4ed6\u7684\u8bdd\u8bf4\u9053\uff1a \u201c\u6211\u660e\u767d\u8001\u4f2f\u7684\u7528\u5fc3\u3002 \u3000\u3000\u4f60\u5927\u7ea6\u662f\u4ee5\u4e3a\u8fd9\u4e24\u4e2a\u9b54\u5934\u6700\u591a\u662f\u5c06\u592a\u5b50\u52ab\u6301\uff0c\u4e0d\u4f1a\u4e0b\u6b64\u6bd2\u4e86\u7684\u3002 \u3000\u3000\u88f4\u708e\u5927\u7ea6\u4e5f\u662f\u60f3\u5982\u6b64\u5e03\u7f6e\uff0c\u60f3\u501f\u592a\u5b50\u7684\u540d\u4e49\u53cd\u5bf9\u6b66\u5219\u5929\u3002\u800c\u4f60\u5462\uff0c\u5219\u662f\u6015\u6211\u4e0d\u8d5e\u540c \u6b64\u4e8b\uff0c\u53ef\u80fd\u4e0e\u90a3\u4e24\u4e2a\u9b54\u5934\u51b2\u7a81\uff0c\u6545\u6b64\u6ca1\u6709\u5c06\u4f60\u6240\u77e5\u7684\u4e00\u4e00\u8a00\u660e\u3002\u201d\u5176\u5b9e\u6697\u6740\u5e9f\u592a\u4e86\u674e\u8d24 \u4e4b\u4e8b\uff0c\u786e\u662f\u88f4\u708e\u6240\u6307\u4f7f\uff0c\u597d\u628a\u8fd9\u7b14\u8d26\u5199\u5728\u6b66\u5219\u5929\u5934\u4e0a\uff0c\u674e\u9038\u4e0e\u8c37\u795e\u7fc1\u4e24\u4eba\u90fd\u8fd8\u672a\u4f30\u8ba1\u5230 \u88f4\u708e\u5982\u662f\u4e4b\u574f\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u53f9\u9053\uff1a\u201c\u53ea\u6b64\u4e00\u4e8b\uff0c\u5df2\u8db3\u89c1\u88f4\u708e\u7528\u5fc3\u7684\u5351\u52a3\uff0c\u6bd4\u5c06\u8d77\u6765\uff0c\u5012\u663e\u5f97\u5979\u4eec\u7684\u5149\u660e \u78ca\u843d\u4e86\u3002\u201d\u201c\u5979\u4eec\u201d\u5f53\u7136\u662f\u6307\u6b66\u5219\u5929\u4e0e\u6b66\u7384\u971c\u800c\u8a00\uff0c\u674e\u9038\u4e00\u7247\u832b\u7136\uff0c\u5fc3\u5934\u6709\u8bf4\u4e0d\u51fa\u7684\u96be \u8fc7\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u6b66\u5219\u5929\u662f\u7a83\u56fd\u795e\u5978\uff0c\u7eb5\u7136\u505a\u4e86\u4e00\u4e9b\u597d\u4e8b\uff0c\u4e5f\u4e0d\u8fc7\u662f\u6cbd\u540d\u9493 \u8a89\u4e4b\u4e3e\u7f62\u4e86\u3002\u5012\u662f\u6b66\u7384\u971c\u8fd9\u4e2a\u5973\u5b50\uff0c\u786e\u4e4e\u5475\u79f0\u5f97\u4e0a\u662f\u5973\u4e2d\u82f1\u6770\u3002\u201d\u4ed6\u672c\u6765\u60f3\u8bf4\u7684\u662f\u201c\u4fa0 \u9aa8\u67d4\u80a0\u201d\u56db\u5b57\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u65b9\u59cb\u6539\u4e3a\u201c\u5973\u4e2d\u82f1\u6770\u201d\u3002\u957f\u5b59\u58c1\u6709\u70b9\u9178\u610f\u3002\u4f46\u5979\u4e0e\u674e\u9038\u521d\u6b21 \u89c1\u9762\uff0c\u800c\u4e14\u674e\u9038\u53c8\u662f\u738b\u5b59\u8eab\u4efd\uff0c\u6b63\u5728\u75c5\u4e2d\u3002\u5979\u5bf9\u674e\u9038\u7684\u8bdd\u867d\u7136\u751a\u4e0d\u8212\u670d\uff0c\u5374\u4e5f\u4e0d\u4fbf\u53cd\u9a73\u3002 \u3000\u3000\u674e\u9038\u53c8\u9053\uff1a\u201c\u5e78\u597d\u82f1\u56fd\u516c\u5f90\u656c\u4e1a\u8fd8\u662f\u4e00\u4e2a\u6b63\u6d3e\u7684\u5fe0\u81e3\u3002\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u662f\u662f\u975e\u975e\uff0c \u6211\u800c\u4eca\u4e5f\u6709\u4e00\u70b9\u7cca\u6d82\u4e86\u3002\u4e0d\u8fc7\u6211\u5df2\u53d1\u8a93\u4e0d\u518d\u4f7f\u5251\uff0c\u4e5f\u4e50\u5f97\u8131\u51fa\u662f\u975e\u4e4b\u573a\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u6211 \u4e0e\u4e16\u5144\u4ea4\u8c0a\u4ecd\u5728\uff0c\u4f46\u5bf9\u4f60\u4eec\u6062\u590d\u6c5f\u5c71\u7684\u5927\u4e1a\uff0c\u8bf7\u6055\u6211\u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u201d\u674e\u9038\u60f3\u4e0d\u5230\u8c37\u795e\u7fc1 \u7adf\u662f\u5982\u6b64\u5fc3\u7070\u610f\u51b7\uff0c\u4e0d\u7981\u5fc3\u60c5\u9eef\u6de1\uff0c\u8fde\u81ea\u5df1\u4e5f\u632f\u4f5c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u82f1\u96c4\u5927\u4f1a\u4e34\u8fd1\u6e83\u6563\u4e4b\u65f6\uff0c\u6709\u4e00\u4e2a\u5973\u5b50\u51fa\u73b0\uff0c\u5403\u4e86\u96c4\u5de8\u9f0e\u4e00 \u62f3\uff0c\u6211\u542c\u4ed6\u4eec\u6240\u63cf\u8ff0\u7684\u90a3\u4e2a\u5973\u5b50\u7684\u76f8\u8c8c\uff0c\u4f3c\u4e4e\u662f\u6211\u7684\u5e08\u59b9\uff0c\u4e0d\u77e5\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u674e\u9038\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u5979\u6b63\u662f\u4e0a\u5b98\u5a49\u51e0\u3002\u201d\u63d0\u5230\u4e0a\u5b98\u5a49\u513f\uff0c\u4ed6\u53cc\u9650\u6e10\u6e10\u6709\u795e\uff0c\u4f3c\u4e4e\u627e\u5230\u4e86\u652f\u6301\u7684\u52c7\u6c14\uff0c \u957f\u5b59\u6cf0\u66f4\u662f\u559c\u5f62\u4e8e\u8272\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u6bbf\u4e0b\u65e9\u5c31\u8ba4\u8bc6\u4e86\u5979\u7684\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u5728\u5979\u516d\u4e03\u5c81 \u7684\u65f6\u5019\uff0c\u5c31\u8ba4\u8bc6\u5979\u4e86\u3002\u201d\u60f3\u8d77\u5728\u8def\u4e0a\u76f8\u9022\uff0c\u7434\u8bd7\u5531\u548c\uff0c\u4e92\u601c\u8eab\u4e16\uff0c\u5f7c\u6b64\u76f8\u6295\uff0c\u56de\u5473\u8d77\u6765\uff0c \u4ecd\u662f\u5982\u75f4\u5982\u9189\u3002\u53ef\u662f\uff0c\u4e0a\u5b98\u5a49\u513f\u7684\u5f71\u5b50\u867d\u7136\u5728\u4ed6\u7684\u5fc3\u5934\u6e10\u6e10\u6269\u5927\uff0c\u5374\u4ecd\u7136\u4e0d\u80fd\u628a\u6b66\u7384\u971c \u7684\u5f71\u5b50\u5b8c\u5168\u906e\u76d6\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u6ca1\u6709\u4ed6\u59b9\u59b9\u90a3\u6837\u7ec6\u5fc3\uff0c\u672a\u66fe\u7559\u610f\u5230\u674e\u9038\u795e\u60c5\u7684\u53d8\u5316\uff0c\u8fd9\u4e2a\u65f6\u5019\uff0c\u4ed6\u4e5f\u6b63\u5728\u6fc0 \u52a8\u4e4b\u4e2d\uff0c\u4ee5\u89c1\u4ed6\u53cc\u773c\u95ea\u95ea\u53d1\u5149\uff0c\u90a3\u4efd\u559c\u60a6\u7684\u795e\u60c5\u5b9e\u4e0d\u5728\u534a\u9038\u4e4b\u4e0b\uff0c\u8de8\u4e0a\u4e00\u6b65\uff0c\u8feb\u4e0d\u53ca\u5f85 \u7684\u95ee\u9053\uff1a\u201c\u540e\u6765\u5462\uff1f\u201d\u674e\u9038\u5fae\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u540e\u6765\u5440\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4e0a\u5b98\u5a49\u513f\uff0c\u5979\uff0c\u5979 \u540e\u6765\u600e\u4e48\u6837\u4e86\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u540e\u6765\u5417\uff1f\u5728\u6df7\u4e71\u4e4b\u4e2d\u6211\u4eec\u79bb\u6563\u4e86\u3002\u201d\u957f\u5b59\u6cf0\u6781\u4e3a\u5931\u671b\uff0c\u98a4 \u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u540e\u5c31\u4e0d\u77e5\u9053\u5979\u7684\u6d88\u606f\u4e86\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u542c\u8bf4\u5979\u53bb\u884c\u523a\u6b66\u5219\u5929\u53bb\u4e86\u3002\u201d \u957f\u5b59\u6cf0\u5927\u60ca\u5931\u8272\uff0c\u9053\uff1a \u3000\u3000\u201c\u771f\u7684\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u8bf4\u8fd9\u4e2a\u6d88\u606f\u7684\u4eba\u662f\u4e00\u4f4d\u5f88\u9760\u5f97\u4f4f\u7684\u670b\u53cb\uff0c\u5979\u8fd8\u8bf4\u4e0d\u5fc5\u4e3a\u5a49\u513f \u63d0\u5fc3\uff0c\u6599\u5979\u5b9a\u53ef\u5e73\u5b89\u65e0\u4e8b\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5a49\u513f\u7d20\u6765\u806a\u654f\u673a\u667a\uff0c\u5f53\u53ef\u89c1\u673a\u800c\u4f5c\uff0c \u8d8b\u5409\u907f\u51f6\u3002\u201d \u3000\u3000\u674e\u9038\u4e0d\u4fbf\u8bf4\u51fa\u6b66\u7384\u971c\u7684\u540d\u5b57\uff0c\u53ea\u8bf4\u662f\u201c\u4e00\u4f4d\u9760\u5f97\u4f4f\u7684\u670b\u53cb\u3002\u201d\u4ed6\u8bf4\u5230\u8fd9\u51e0\u4e2a\u5b57\u65f6\uff0c \u7981\u4e0d\u4f4f\u5fc3\u5934\u52a8\u8361\uff0c\u8138\u4e0a\u5fae\u7ea2\uff0c\u7acb\u5373\u60f3\u9053\uff1a \u3000\u3000\u201c\u6211\u4f46\u613f\u5979\u7684\u8bdd\u5e76\u4e0d\u5168\u7136\u53ef\u9760\uff0c\u82e5\u7136\u5a49\u513f\u771f\u7684\u5982\u5979\u6240\u6599\uff0c\u5f52\u987a\u4e86\u6b66\u5219\u5929\uff0c\u90a3\u4e5f\u5c31\u7b49 \u4e8e\u6b7b\u4e86\u4e00\u822c\uff0c\u540c\u6837\u7684\u4ee4\u4eba\u4f24\u5fc3\u96be\u8fc7\uff01\u201d \u3000\u3000\u957f\u5b59\u6cf0\u867d\u7136\u7ecf\u4ed6\u7684\u59b9\u59b9\u6170\u89e3\uff0c\u4ecd\u662f\u5982\u4f55\u91cd\u5fe7\u3002\u8c37\u795e\u7fc1\u9053\uff1a \u3000\u3000\u201c\u674e\u8d24\u4f84\u7cbe\u795e\u672a\u590d\uff0c\u4e0d\u53ef\u592a\u7528\u5fc3\u795e\uff0c\u6709\u4ec0\u4e48\u8bdd\u4ee5\u540e\u6162\u6162\u518d\u8bf4\u5427\u3002 \u3000\u3000\u590f\u4faf\u5144\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u6211\u6b64\u523b\u4fbf\u5373\u52a8\u8eab\uff0c\u5c06\u957f\u5b59\u5747\u91cf\u63a5\u6765\u4e0e\u4f60\u4f5c\u4f34\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a \u201c\u59b9\u59b9\uff0c\u4f60\u7559\u4e0b\u6765\u670d\u4f8d\u6bbf\u4e0b\uff0c\u6211\u968f\u8c37\u4f2f\u4f2f\u53bb\u63a5\u7239\u7239\uff0c\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u4f60\u987a\u4fbf\u4e5f\u53ef\u4ee5\u63a2\u8bbf \u4e00\u4e0b\u5a49\u513f\u7684\u6d88\u606f\uff0c\u514d\u5f97\u5927\u5bb6\u6302\u5fc3\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6709\u610f\u65e0\u610f\u7684\u5411\u674e\u9038\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u6309\u4e0b\u8c37\u795e\u7fc1\u957f\u5b59\u6cf0\u7b49\u6682\u65f6\u4e0d\u8868\uff0c\u4e14\u8bf4\u674e\u9038\u5728\u590f\u4faf\u575a\u91d1\u9488\u5999\u624b\u7684\u6cbb\u7597\u4e4b\u4e0b\uff0c\u53c8\u5f97\u957f\u5b59 \u58c1\u7684\u5c3d\u5fc3\u8c03\u7406\uff0c\u75c5\u4f11\u4e00\u65e5\u597d\u8fc7\u4e00\u65e5\uff0c\u8fc7\u4e86\u4e8c\u4e03\u4e00\u5341\u56db\u5929\uff0c\u4e0d\u4f46\u53ef\u4ee5\u884c\u52a8\u81ea\u5982\uff0c\u6b66\u529f\u4e5f\u6062 \u590d\u4e86\u5341\u4e4b\u516b\u4e5d\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4ed6\u5728\u9759\u5ba4\u4e4b\u4e2d\u72ec\u5750\u65e0\u804a\uff0c\u60f3\u4e00\u4f1a\u6b66\u7384\u971c\uff0c\u53c8\u60f3\u4e00\u4f1a\u4e0a\u5b98\u5a49\u513f\uff0c\u4f46\u89c9\u60c5\u6000\u6005\u6005\uff0c \u5fc3\u4e8b\u91cd\u91cd\uff0c\u8fd9\u65f6\u5df2\u662f\u521d\u79cb\u65f6\u5206\uff0c\u4ece\u7a97\u5b50\u91cc\u671b\u51fa\u53bb\uff0c\u5ead\u9662\u91cc\u5df2\u662f\u843d\u65f6\u6ee1\u9636\uff0c\u6b8b\u7ea2\u5f85\u626b\uff0c\u674e \u9038\u7fd8\u9996\u957f\u7a7a\uff0c\u7f13\u7f13\u7684\u541f\u51fa\u4e0a\u5b98\u5a49\u513f\u9001\u4ed6\u7684\u90a3\u4e00\u9996\u8bd7\uff1a\u201c\u53f6\u4e0b\u6d1e\u5ead\u521d\uff0c\u601d\u541b\u4e07\u91cc\u9980\u3002\u96fe\u6d53 \u9999\u88ab\u51b7\u3001\u6708\u843d\u9526\u5c4f\u865a\u3002\u6b32\u594f\u6c5f\u5357\u8c03\uff0c\u8d2b\u5c01\u84df\u5317\u8bd7\u3002 \u3000\u3000\u673d\u4e2d\u65e0\u522b\u610f\uff0c\u4f46\u6005\u4e45\u79bb\u5c45\u3002\u201d\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5440\uff0c\u4f46\u6005\u4e45\u79bb\u5c45\u3002\u4f60\u601d\u5ff5\u6211\uff0c\u771f\u7684\u662f\u5982 \u6b64\u4e4b\u6df1\u4e48\uff1f\u201d\u6000\u5ff5\u8fdc\u4eba\uff0c\u66f4\u662f\u4e0d\u80fd\u81ea\u5df1\uff0c\u8c03\u597d\u7434\u5f26\uff0c\u518d\u5f39\u5014\u8bd7\u7ecf\u4e2d\u90a3\u7bc7\u601d\u5ff5\u6545\u4eba\u7684\u201c\u7eff \u8863\u9ec4\u88f3\u201d\uff0c\u4ed6\u60f3\u5ff5\u7684\u662f\u4e0a\u5b98\u5a49\u513f\uff0c\u4f46\u5374\u8bb0\u8d77\u4e86\u8fd9\u4e00\u7bc7\u8bd7\u66fe\u5728\u6b66\u7384\u971c\u9762\u524d\u5f39\u8fc7\uff0c\u4e0d\u7981\u53c8\u60f3 \u8d77\u4e86\u6b66\u7384\u971c\u6765\uff0c\u60f3\u8d77\u6b66\u7384\u971c\u5f53\u65e5\u66fe\u7528\u695a\u8f9e\u6765\u916c\u548c\u4ed6\u7684\u8bd7\u7bc7\uff0c\u6697\u4e2d\u529d\u8c0f\u3002\u60f3\u8d77\u8fd9\u4e9b\u65e7\u4e8b\uff0c \u5fc3\u5982\u4e71\u4e1d\uff0c\u4e8e\u662f\u518d\u629a\u7434\u5f39\u594f\u201c\u79bb\u9a9a\u201d\u4e2d\u81ea\u5df1\u6700\u559c\u6b22\u7684\u90a3\u51e0\u53e5\uff0c\u201c\u65e5\u6708\u5ffd\u5176\u4e0d\u6df9\u516e\uff0c\u6709\u4e0e \u79cb\u5176\u4ee3\u5e8f\u3002 \u3000\u3000\u60df\u8349\u6728\u4e4b\u96f6\u843d\u516e\uff0c\u6050\u7f8e\u4eba\u4e4b\u8fdf\u66ae\uff01\u2026\u2026\u201d\u7434\u97f5\u60a0\u626c\uff0c\u5ffd\u542c\u5f97\u6709\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u7b11\u9053\uff1a \u201c\u5f39\u5f97\u597d\u7434\uff01\u5f39\u5f97\u597d\u7434\uff01\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u56de\u3000\u67d4\u60c5\u4f3c\u6c34\u6700\u96be\u7981 ",content:(0,t.jsx)(t.Fragment,{children:"\u674e\u9038\u63a8\u7434\u800c\u8d77\uff0c\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u4f60\u56de\u6765\u4e86\uff1f\u201d\u8fd9\u5341\u591a\u5929\u6765\uff0c\u4ed6\u5f97\u957f\u5b59\u58c1\u6089\u5fc3\u8c03\u62a4\uff0c\u751a \u4e3a\u611f\u6fc0\uff0c\u52a0\u4ee5\u957f\u5b59\u58c1\u7684\u7236\u4eb2\u53c8\u662f\u524d\u671d\u8001\u81e3\uff0c\u6545\u6b64\u4ed6\u65e9\u5df2\u8981\u957f\u5b59\u58c1\u83ab\u62d8\u541b\u81e3\u793c\u8282\uff0c\u6539\u53e3\u4ee5 \u5144\u59b9\u76f8\u79f0\u3002 \u3000\u3000\u8fd9\u4e00\u56de\u5934\uff0c\u4f46\u89c1\u957f\u5b59\u58c1\u67f3\u7709\u5fae\u8e59\uff0c\u5982\u6709\u6240\u601d\uff0c\u4e0e\u5979\u5e73\u7d20\u7684\u795e\u6001\u5927\u4e0d\u76f8\u540c\u3002\u674e\u9038\u6014\u4e86 \u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u597d\u7684\u6d88\u606f\u4e48\uff1f\u201d\u539f\u6765\u674e\u9038\u867d\u5728\u75c5\u4e2d\uff0c\u4ecd\u5f88\u5173\u5fc3\u5f90\u656c\u4e1a\u8d77\u5175\u7684\u6d88 \u606f\uff0c\u957f\u5b59\u58c1\u6bcf\u5929\u4fbf\u5230\u9547\u4e0a\u4e00\u8d9f\uff0c\u5973\u626e\u7537\u88c5\uff0c\u626e\u6210\u4e00\u4e2a\u4e66\u751f\u6a21\u6837\uff0c\u5728\u8336\u9986\u91cc\u559d\u8336\uff0c\u542c\u8336\u5ba2 \u4eec\u201c\u6446\u9f99\u95e8\u9635\u201d\uff08\u56db\u5ddd\u571f\u8bed\uff0c\u201c\u95f2\u8c08\u53e3\u201d\u4e4b\u610f\uff09\uff0c\u4ee5\u4fbf\u66ff\u674e\u9038\u6253\u542c\u6d88\u606f\u3002 \u3000\u3000\u957f\u5b59\u58c1\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\u7684\u6d88\u606f\u3002\u4e0d\u8fc7\uff0c\u6211\u6709\u4e00\u4e2a\u7591\u95ee\uff0c\u81ea\u5df1\u611a\u5473\u96be\u660e\uff0c\u60f3\u8bf7 \u6bbf\u4e0b\u6307\u6559\u3002\u201d\u674e\u9038\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u806a\u6167\uff0c\u8fd8\u6709\u4ec0\u4e48\u96be\u660e\u4e4b\u4e8b\uff1f\u201d\u957f\u5b59\u58c1\u5fae\u7b11\u9053\uff1a\u201c\u8bf4\u5230 \u806a\u660e\uff0c\u5a49\u513f\u59b9\u59b9\u624d\u662f\u4e16\u4e0a\u6700\u806a\u660e\u7684\u4eba\uff0c\u6211\u54ea\u7b97\u5f97\u4e0a\u5462\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u518d\u8c26\u865a\uff0c\u6211\u53ef\u4e0d \u6562\u548c\u4f60\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u5076\u7136\u60f3\u8d77\u4e00\u4e2a\u53e4\u602a\u7684\u95ee\u9898\uff0c\u4f60\u82e5\u4e0d\u7b11\u8bdd\u6211\uff0c\u6211\u4fbf\u95ee\u4f60\u3002\u201d\u674e\u9038\u9053\uff1a \u201c\u5999\u6781\uff0c\u5999\u6781\uff01\u54b1\u4eec\u95f2\u6765\u65e0\u4e8b\u6b63\u597d\u6446\u6446\u9f99\u95e8\u9635\uff0c\u4f60\u8bf4\u5427\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u4eca\u65e5\u5076\u7136\u542c \u5230\u4e00\u4e2a\u7b11\u8bdd\uff0c\u8bf4\u662f\u4e00\u4e2a\u6c5f\u6d0b\u5927\u76d7\uff0c\u88ab\u63a8\u51fa\u53bb\u65a9\u5934\uff0c\u523d\u5b50\u624b\u5200\u6cd5\u6781\u597d\uff0c\u5200\u51fa\u5982\u98ce\uff0c\u8f7b\u8f7b\u4e00 \u524a\uff0c\u4fbf\u5c06\u4eba\u5934\u65a9\u4e0b\uff0c\u90a3\u4eba\u5934\u5728\u5730\u4e0a\u5140\u81ea\u9053\uff1a\u201c\u597d\u5200\uff0c\u597d\u5200\uff01\u4f60\u8bf4\u8fd9\u4e2a\u88ab\u65a9\u7684\u4eba\u662f\u806a\u660e\u8fd8 \u662f\u611a\u8822\uff1f\u201d \u3000\u3000\u674e\u9038\u5446\u4e86\u4e00\u5446\uff0c\u7acb\u5373\u7b11\u9053\uff1a\u201c\u8fd9\u5f53\u7136\u662f\u611a\u8822\u4e86\uff0c\u4e0d\u8fc7\u6211\u4e0d\u76f8\u4fe1\u4e16\u4e0a\u771f\u6709\u90a3\u6837\u7684\u4eba\uff0c \u88ab\u6740\u4e86\u5934\u8fd8\u4f1a\u5bf9\u523d\u4e8e\u624b\u7684\u5200\u6cd5\u8d5e\u4e0d\u7edd\u53e3\u3002\u8fd9\u5b9a\u662f\u90a3\u4e9b\u5999\u60f3\u5929\u5f00\u7684\u4eba\u7f16\u51fa\u6765\u7684\u3002\u201d\u957f\u5b59\u58c1 \u9053\uff1a\u201c\u6211\u770b\u8fd9\u6837\u7684\u4eba\u591a\u7740\u54e9\uff0c\u4e0d\u8fc7\u6740\u4ed6\u7684\u4eba\u672a\u5fc5\u662f\u7528\u5200\u7f62\u4e86\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u5730\u201c\u5657\u55e4\u201d \u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6216\u8bb8\u662f\u7528\u4e00\u58f0\u5a07\u7b11\uff0c\u6216\u8bb8\u662f\u7528\u4e00\u7f15\u67d4\u60c5\u2026\u2026\u4e8e\u662f\u90a3\u4eba\u5373\u7b97\u6b7b\u4e86\u4ea6\u81ea\u5bf9\u90a3 \u523d\u5b50\u624b\u5ff5\u5ff5\u4e0d\u5fd8\uff01\u201d \u3000\u3000\u674e\u9038\u4f55\u7b49\u806a\u660e\uff0c\u7acb\u77e5\u5176\u610f\u3002\u5fc3\u9053\uff1a\u201c\u6211\u521a\u624d\u5728\u7434\u97f3\u4e2d\u8868\u9732\u51fa\u5bf9\u6b66\u7384\u971c\u7684\u503e\u6155\uff0c\u60f3\u662f \u7ed9\u5979\u542c\u51fa\u6765\u4e86\u3002\u201d\u4e0d\u7981\u8c41\u7136\u4e00\u7701\uff0c\u60f3\u9053\uff1a\u201c\u5979\u867d\u662f\u501f\u9898\u53d1\u6325\u6765\u8ba5\u8bbd\u6211\uff0c\u8fd9\u756a\u8bdd\u5374\u8bf4\u5f97\u751a \u6709\u610f\u601d\uff0c\u4e0d\u7ba1\u600e\u6837\uff0c\u6b66\u7384\u971c\u603b\u662f\u6211\u7684\u654c\u4eba\uff0c\u7eb5\u6709\u5929\u5927\u7684\u672c\u9886\uff0c\u4e5f\u4e0d\u8fc7\u7b49\u4e8e\u523d\u5b50\u624b\u7f62\u4e86\uff0c \u7136\u800c\u5979\u771f\u7684\u662f\u523d\u5b50\u624b\u4e48\uff1f\u201d \u3000\u3000\u674e\u9038\u5446\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u8fd9\u624d\u7a0d\u5b9a\u5fc3\u795e\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u6307\u70b9\uff0c\u4f60\u6bd4\u6211\u806a\u660e\u591a\u4e86\u3002 \u55ef\uff0c\u4eca\u5929\u771f\u7684\u6ca1\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u6d88\u606f\u4e48\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u4f60\u521a\u624d\u95ee\u6709\u6ca1\u6709\u4e0d\u597d\u7684\u6d88\u606f\uff0c\u6ca1 \u6709\uff0c\u4f46\u5374\u6709\u4e00\u4e2a\u7279\u522b\u7684\u6d88\u606f\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u542c\u5f97\u8336\u5ba2\u8c08\u8bba\uff0c \u8bf4\u662f\u6b66\u5219\u5929\u8981\u8003\u5973\u4e2d\u8d24\u624d\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u7279\u522b\uff1f\u6b66\u5219\u5929\u505a\u4e86\u5973\u7687\u5e1d\uff0c\u8981\u9009\u51e0\u4e2a \u5973\u4eba\u505a\u5b98\u4ea6\u662f\u5e94\u6709\u4e4b\u4e49\u3002\u201d\u957f\u5b59\u58c1\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u90a3\u9053\u8bcf\u4e66\u5374\u542c\u8bf4\u662f\u5a49\u513f\u4ee3\u7b14\u7684\uff0c\u5a49 \u513f\u505a\u4e86\u6b66\u5219\u5929\u7684\u56db\u54c1\u5973\u5b98\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u600e\u6837\u8bf4\u7684\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u9694\u90bb\u7684\u8336\u5ba2\u662f\u4e24 \u4e2a\u79c0\u624d\uff0c\u4ed6\u4eec\u521a\u4ece\u957f\u5b89\u5f52\u6765\uff0c\u5728\u8336\u9986\u91cc\u9ad8\u8c08\u9614\u8bba\uff0c\u8bf4\u7684\u4fbf\u662f\u5a49\u513f\u7684\u4e8b\u60c5\u3002\u636e\u4ed6\u4eec\u8bf4\u6b66\u5219 \u5929\u4efb\u7528\u5a49\u513f\u505a\u56db\u54c1\u5973\u5b98\uff0c\u4e13\u804c\u66ff\u5979\u638c\u7ba1\u6587\u9675\uff0c\u6b66\u5219\u5929\u8fd8\u7279\u522b\u4e3a\u5979\u5728\u5bab\u4e2d\u8bbe\u5bb4\uff0c\u53ec\u8bf7\u8bb8\u591a \u5b66\u58eb\u5165\u5bab\u505a\u8bd7\uff0c\u5a49\u513f\u5728\u4e00\u652f\u9999\u7684\u65f6\u523b\u4fbf\u505a\u4e86\u5341\u9996\u8bd7\uff0c\u53c8\u5feb\u53c8\u597d\uff0c\u5c06\u90a3\u73ed\u5b66\u58eb\u90fd\u538b\u5012\u4e86\u3002 \u6b66\u5219\u5929\u8fd9\u624d\u8bf4\u51fa\u5a49\u513f\u4fbf\u662f\u4e0a\u5b98\u4eea\u7684\u5b59\u5973\uff0c\u4ee4\u4ed6\u4eec\u60ca\u6115\u4e0d\u5df2\u3002\u8fd9\u662f\u4e0a\u4e2a\u6708\u7684\u4e8b\u60c5\uff0c\u636e\u8bf4\u73b0 \u4e0b\u5a49\u513f\u5df2\u662f\u624d\u540d\u9e4a\u8d77\uff0c\u540d\u9707\u957f\u5b89\uff0c\u4eba\u4eba\u90fd\u77e5\u9053\u672c\u671d\u53d1\u73b0\u4e86\u4e00\u4f4d\u624d\u5973\uff0c\u6709\u4e00\u4e9b\u62cd\u9a6c\u5c41\u7684\u5b98 \u513f\u8fd8\u4e0a\u8868\u5411\u6b66\u5219\u5929\u606d\u8d3a\u5462\uff01\u90a3\u4e24\u4e2a\u79c0\u624d\uff0c\u8bf4\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u4ed6\u4eec\u4e5f\u5c06\u8fd9\u4ef6\u4e8b\u60c5\u5f53\u4f5c\u672c\u671d \u201c\u4f73\u8bdd\u201d\uff0c\u8fd8\u5938\u8d5e\u6b66\u5219\u5929\u6562\u4e8e\u4efb\u7528\u4ec7\u4eba\u7684\u5b59\u5973\uff0c\u8c41\u8fbe\u5927\u5ea6\uff0c\u5f53\u771f\u662f\u4eba\u4e3b\u7684\u80f8\u895f\u5462\uff01\u201d\u674e \u9038\u9762\u8272\u4e00\u7247\u60e8\u767d\uff0c\u867d\u7136\u4ed6\u65e9\u5df2\u542c\u8fc7\u6b66\u7384\u971c\u7684\u9884\u6d4b\uff0c\u4ecd\u7136\u89c9\u5f97\u8fd9\u662f\u4e0d\u53ef\u60f3\u50cf\u7684\u4e8b\uff0c\u8eab\u8d1f\u8840 \u6d77\u6df1\u4ec7\uff0c\u7acb\u8a93\u8981\u53bb\u884c\u523a\u6b66\u5219\u5929\u7684\u4e0a\u5b98\u5a49\u513f\uff0c\u5374\u7adf\u4f1a\u505a\u4e86\u6b66\u5219\u5929\u7684\u5973\u5b98\uff01 \u3000\u3000\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u674e\u9038\u9eef\u7136\u4e0d\u8bed\uff0c\u79fb\u6b65\u7a97\u524d\uff0c\u60f3\u8d77\u4e86\u4ed6\u521d\u89c1\u4e0a\u5b98\u5a49 \u513f\u4e4b\u65f6\uff0c\u5f7c\u6b64\u4e92\u4f36\u8eab\u4e16\uff0c\u540c\u58f0\u6168\u53f9\u8fc7\uff1a\u201c\u4f24\u5fc3\u5b87\u5185\u82f1\u8c6a\uff0c\u5c3d\u5f52\u65b0\u4e3b\uff1b\u5fcd\u89c1\u5929\u4eac\u795e\u5668\uff0c\u7adf \u5c5e\u4ed6\u5bb6\uff01\u201d\u8fd9\u6837\u7684\u8bdd\uff0c\u600e\u6599\u5230\u522b\u6765\u672a\u4e45\uff0c\u8fde\u5979\u4e5f\u5f52\u4e86\u6b66\u5219\u5929\u4e86\uff01\u60f3\u5230\u4f24\u5fc3\u4e4b\u5904\uff0c\u674e\u9038\u5f53 \u771f\u662f\u6b32\u54ed\u65e0\u6cea\uff0c\u6b32\u8bed\u8fd8\u4f11\u3002 \u3000\u3000\u8ff7\u832b\u4e2d\u5ffd\u89c9\u6709\u79c0\u53d1\u62c2\u7709\uff0c\u67d4\u5937\u5728\u63e1\uff0c\u53ea\u89c1\u957f\u5b59\u58c1\u8f7b\u8f7b\u63e1\u7740\u4ed6\u7684\u624b\u638c\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a \u201c\u6211\u4e5f\u4e0d\u6562\u76f8\u4fe1\u8fd9\u662f\u771f\u7684\u4e8b\u60c5\uff0c\u4f46\u4ed6\u4eec\u5374\u53c8\u8bf4\u5f97\u90a3\u4e48\u786e\u51ff\uff0c\u5f85\u4f60\u5b8c\u5168\u597d\u4e86\u4e4b\u540e\uff0c\u54b1\u4eec\u5230 \u957f\u5b89\u53bb\u63a2\u542c\u4e00\u4e0b\uff0c\u597d\u5417\uff1f\u201d\u674e\u9038\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u5b81\u613f\u6c38\u4e0d\u6233\u7834\u8fd9\u4e2a\u7591\u56e2\u3002\u5440\uff0c\u82e5\u662f\u771f\u7684\uff0c \u90a3\uff0c\u90a3\u600e\u4e48\u597d\uff1f\u201d \u3000\u3000\u957f\u5b59\u58c1\u773c\u5708\u4e00\u7ea2\uff0c\u4e0e\u674e\u9038\u9760\u5f97\u66f4\u7d27\u4e86\u3002\u674e\u9038\u7a0d\u7a0d\u5c06\u5934\u79fb\u5f00\uff0c\u53ea\u542c\u5f97\u957f\u5b59\u58c1\u5728\u4ed6\u8033\u8fb9 \u8bf4\u9053\uff1a\u201c\u5a49\u513f\u4e0e\u6211\u60c5\u540c\u59d0\u59b9\uff0c\u82e5\u662f\u771f\u7684\uff0c\u6211\u600e\u6837\u4e5f\u8981\u628a\u5979\u529d\u56de\u6765\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u82e5\u662f\u529d \u4e0d\u56de\u6765\u5462\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u82e5\u662f\u529d\u4e0d\u56de\u6765\uff0c\u6211\u5c31\u5f53\u5979\uff0c\u5f53\u5979\u6b7b\u4e86\uff01\u6bbf\u4e0b\uff0c\u6211\u77e5\u9053\u4f60\u6781\u4f24 \u5fc3\uff0c\u6211\u7684\u4f24\u5fc3\u4e5f\u4e0d\u5728\u4f60\u4e4b\u4e0b\uff0c\u4f46\u4f60\u662f\u9f99\u5b50\u9f99\u5b59\uff0c\u53c8\u662f\u82f1\u96c4\u8c6a\u6770\uff0c\u5927\u4e08\u592b\u5e94\u5f53\u63d0\u5f97\u8d77\uff0c\u653e \u5f97\u4e0b\uff0c\u96be\u9053\u5929\u4e0b\u4e4b\u5927\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u77e5\u5df1\u4e86\u5417\uff1f\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u8361\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u6b63\u597d\u4e0e\u957f\u5b59\u58c1\u7684\u773c\u5149\u76f8\u63a5\uff0c\u4f46\u89c1\u957f\u5b59\u58c1\u9762\u4e0a\u4e00\u7ea2\uff0c\u653e\u5f00 \u4e86\u624b\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u674e\u9038\u51e0\u4e4e\u60f3\u62b1\u7740\u5979\u75db\u54ed\u4e00\u573a\uff0c\u4f46\u7acb\u5373\u53c8\u5f3a\u884c\u6291\u5236\uff0c\u4f46\u6015\u8fd9\u6837\u4e00\u6765\uff0c\u66f4 \u589e\u52a0\u4e86\u957f\u5b59\u58c1\u7684\u8bef\u4f1a\u3002\u4e00\u4e2a\u6b66\u7384\u971c\u3001\u4e00\u4e2a\u4e0a\u5b98\u5a49\u513f\uff0c\u5df2\u7ed9\u4e86\u4ed6\u65e0\u7a77\u70e6\u607c\uff0c\u5c82\u53ef\u518d\u6dfb\u4e0a\u4e2a \u5929\u771f\u6d3b\u6cfc\u7684\u5c0f\u59d1\u5a18\uff1f \u3000\u3000\u8ff7\u832b\u4e2d\u5ffd\u542c\u5f97\u6709\u4eba\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u4e24\u4eba\u751a\u5730\u4e00\u60ca\uff0c\u4ece\u7a97\u53e3\u671b\u51fa \u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u9053\u58eb\u6b63\u5411\u7740\u4ed6\u4eec\u8fd9\u95f4\u9759\u5ba4\u8d70\u6765\uff0c\u590f\u4faf\u575a\u90a3\u4e24\u4e2a\u836f\u7ae5\u5728\u540e\u9762\u5927\u58f0\u559d\u6b62\uff01 \u3000\u3000\u8fd9\u9053\u58eb\u5e74\u7ea6\u4e94\u65ec\uff0c\u7a7f\u7740\u4e00\u88ad\u6de1\u9752\u8272\u7684\u9053\u888d\uff0c\u7559\u7740\u4e09\u7edc\u957f\u9aef\uff0c\u6001\u5ea6\u4ece\u5bb9\uff0c\u9887\u6709\u51e0\u5206\u6f47 \u6d12\u51fa\u5c18\u4e4b\u6982\u3002\u674e\u9038\u5fc3\u9053\uff1a\u201c\u590f\u4faf\u575a\u4e16\u5916\u9ad8\u4eba\uff0c\u4ed6\u8fd9\u4e24\u4e2a\u836f\u7ae5\u5374\u600e\u5982\u6b64\u4e0d\u61c2\u793c\u8c8c\uff1f\u672a\u66fe\u95ee \u660e\u6765\u5386\uff0c\u4fbf\u5148\u5386\u559d\u4eba\u5bb6\u3002\u201d\u590f\u4faf\u575a\u7684\u82b1\u56ed\u91cc\u82b1\u6728\u8471\u5ba0\uff0c\u85e4\u841d\u7f20\u7ed5\uff0c\u90a3\u9053\u4eba\u5206\u82b1\u62c2\u53f6\uff0c\u4e0d \u7406\u90a3\u4e24\u4e2a\u836f\u7ae5\uff0c\u8ff3\u81ea\u524d\u884c\u3002\u674e\u9038\u65b9\u81ea\u89c9\u5f97\u8fd9\u9053\u4eba\u5947\u602a\uff0c\u5fc3\u5ff5\u672a\u5df1\uff0c\u5ffd\u542c\u5f97\u957f\u5b59\u58c1\u8bf4\u9053\uff1a \u201c\u4f60\u77a7\u8fd9\u9053\u58eb\u771f\u6709\u90aa\u95e8\uff01\u201d\u674e\u9038\u8fd9\u65f6\u65b9\u624d\u53d1\u89c9\uff0c\u4f46\u89c1\u7ecf\u4ed6\u7684\u624b\u62e8\u8fc7\u7684\u82b1\u8349\uff0c\u7247\u523b\u4e4b\u95f4\uff0c \u4fbf\u67af\u840e\u7126\u9ec4\uff0c\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u660e\u767d\u90a3\u4e24\u4e2a\u836f\u7ae5\u4e3a\u4f55\u8981\u5927\u58f0\u5386\u559d\u3002 \u3000\u3000\u90a3\u9053\u58eb\u811a\u5c16\u5e76\u4e0d\u79bb\u5730\uff0c\u6b65\u5c65\u751a\u662f\u5b89\u8be6\uff0c\u4f46\u8f6c\u77ac\u4e4b\u95f4\u4fbf\u5230\u4e86\u9759\u5ba4\u5916\u9762\uff0c\u90a3\u4e24\u4e2a\u836f\u7ae5\u8ffd \u5f97\u6c14\u5598\u5401\u5401\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u518d\u4e0d\u6b62\u6b65\uff0c\u6211\u4eec\u53ef\u8981\u4e0d\u5ba2\u6c14\u5566\uff01\u2019\u90a3\u9053\u58eb\u4ecd\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c \u800c\u4e0d\u95fb\uff0c\u6beb\u4e0d\u7406\u776c\uff0c\u524d\u9762\u90a3\u4e2a\u836f\u7ae5\u6298\u4e86\u4e00\u679d\u6811\u679d\uff0c\u559d\u4e00\u58f0\u201c\u6253\uff01\u201d\u3002\u628a\u624b\u4e00\u626c\uff0c\u4f46\u89c1\u90a3 \u679d\u6811\u679d\uff0c\u5df2\u65ad\u6210\u4e03\u622a\uff0c\u6bcf\u622a\u4e09\u5bf8\u6765\u957f\uff0c\u4ed6\u4eec\u7528\u53d1\u6697\u5668\u95e8\u9489\u7684\u624b\u6cd5\uff0c\u4e03\u6bb5\u6811\u679d\uff0c\u5982\u7bad\u75be\u5c04\uff0c \u800c\u4e14\u6bcf\u4e00\u679d\u90fd\u662f\u5bf9\u51c6\u90a3\u9053\u4eba\u7684\u7a74\u9053\u3002\u674e\u9038\u65b9\u5728\u5fc3\u4e2d\u8d5e\u9053\uff1a\u201c\u597d\u624b\u6cd5\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u53ea\u8d1d\u90a3\u516d\u679d\u201c\u6728\u7bad\u201d\uff0c\u90fd\u5c04\u5230\u4e86\u9053\u4eba\u8eab\u4e0a\uff0c\u521a\u521a\u6cbe\u7740\u4ed6\u7684\u9053\u888d\uff0c\u4fbf\u7eb7\u7eb7\u6389\u843d\uff0c\u597d\u50cf\u662f\u4ed6 \u7684\u9053\u888d\u62b9\u4e86\u6cb9\u4e00\u822c\u3002\u674e\u9038\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u539f\u6765\u8fd9\u602a\u9053\u571f\u7adf\u4f1a\u6cbe\u8863\u5341\u516b\u8dcc\u7684\u4e0a\u4e58\u6b66\u529f\uff01\u201d\u5185 \u529f\u7ec3\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u8eab\u4f53\u6bcf\u4e00\u90e8\u4efd\u90fd\u53ef\u4ee5\u501f\u529b\u6253\u529b\uff0c\u654c\u4eba\u6cbe\u7740\u8863\u88f3\uff0c\u4fbf\u4f1a\u8dcc\u7ffb\uff0c\u6545\u540d \u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\uff0c\u8fd9\u9053\u58eb\u8fde\u5c04\u4e2d\u7a74\u9053\u7684\u6697\u5668\uff0c\u4e5f\u53ef\u4ee5\u501f\u52b2\u5f39\u5f00\uff0c\u90a3\u66f4\u662f\u8fd9\u95e8\u529f\u592b\u7684\u4e2a\u4e2d \u9ad8\u624b\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u540d\u836f\u7ae5\u89c1\u4ed6\u8eab\u4e2d\u4e03\u652f\u201c\u6728\u7bad\u201d\uff0c\u4ecd\u662f\u5b89\u7136\u65e0\u4e8b\uff0c\u4e00\u53d1\u6025\u4f7f\u51fa\u731b\u52b2\uff0c\u6293\u8d77\u4e86\u4e00\u5757 \u5047\u5c71\u77f3\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e24\u4e09\u767e\u65a4\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f60\u7eb5\u6709\u6cbe\u8863\u5341\u516b\u8dcc\u7684\u6b66\u529f\u4e5f\u96be\u4ee5\u5c06\u8fd9\u5757\u5927\u77f3 \u5f39\u5f00\uff01\u201d\u8fd9\u65f6\u90a3\u9053\u58eb\u53c8\u884c\u8fd1\u4e86\u9759\u5ba4\u51e0\u6b65\uff0c\u90a3\u836f\u7ae5\u5927\u559d\u4e00\u58f0\uff0c\u4f7f\u5c3d\u5403\u5976\u6c14\u529b\uff0c\u5c06\u5927\u77f3\u5bf9\u51c6 \u4ed6\u63b7\u53bb\uff0c\u90a3\u602a\u9053\u58eb\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6765\u5f97\u6b63\u597d\uff0c\u4e0d\u5fc5\u6211\u8d39\u529b\u6c14\u6253\u95e8\u4e86\uff01\u201d\u53ea\u89c1\u4ed6\u811a\u6b65 \u4e00\u65cb\uff0c\u4f38\u51fa\u4e86\u4e24\u6839\u6307\u5934\uff0c\u624b\u8155\u4e00\u62ac\uff0c\u90a3\u5757\u5927\u77f3\u6b63\u8fce\u9762\u6253\u5230\uff0c\u4ed6\u4e24\u6839\u6307\u5934\u5728\u77f3\u5934\u65c1\u8fb9\u4e00\u64e6\uff0c \u90a3\u5757\u5927\u77f3\u672c\u6765\u662f\u4ece\u4ed6\u7684\u5de6\u4fa7\u8fb9\u6253\u6765\u7684\uff0c\u8fd9\u65f6\u88ab\u4ed6\u53cc\u6307\u4e00\u5e26\uff0c\u7adf\u7136\u6539\u4e86\u65b9\u5411\uff0c\u9022\u5411\u90a3\u95f4\u9759 \u5ba4\u7684\u7ea2\u6f06\u6728\u95e8\u649e\u53bb\uff0c\u201c\u8f70\u9686\u201d\u4e00\u58f0\u5de8\u54cd\uff0c\u6728\u95e8\u767b\u65f6\u788e\u6210\u4e86\u65e0\u6570\u5c0f\u5757\u3002\u674e\u9038\u6025\u5fd9\u9000\u5230\u5899\u89d2\uff0c \u6293\u8d77\u5b9d\u5251\u3002 \u3000\u3000\u90a3\u9053\u58eb\u7acb\u5373\u95ef\u8fdb\uff0c\u76ef\u7740\u674e\u9038\u4e0e\u957f\u5b59\u58c1\u4e24\u4eba\uff0c\u53cc\u773c\u9732\u51fa\u602a\u5f02\u7684\u5149\u8292\u3002\u8138\u4e0a\u7f69\u7740\u4e00\u5c42\u6de1 \u6de1\u7684\u7d2b\u6c14\uff0c\u795e\u60c5\u4ecd\u662f\u90a3\u4e48\u6f47\u6d12\uff0c\u4f46\u5374\u4ee4\u4eba\u5fc3\u60ca\u8089\u8df3\uff0c\u90a3\u9053\u58eb\u76ef\u4e86\u4e00\u773c\uff0c\u5ffd\u5730\u6307\u7740\u674e\u9038\u8bf4 \u9053\uff1a\u201c\u5947\u8ff9\uff0c\u5947\u8ff9\uff0c\u4f60\u4e2d\u4e86\u6211\u4e24\u4e2a\u5f92\u513f\u7684\u788e\u9aa8\u94b1\u9556\u4e0e\u900f\u7a74\u795e\u9488\uff0c\u7adf\u7136\u80fd\u6d3b\u5230\u5982\u4eca\uff01\u201d\u674e \u9038\u4e0e\u957f\u5b59\u58c1\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u602a\u9053\u58eb\u770b\u6765\u4e0d\u8fc7\u4e94\u65ec\uff0c\u7adf\u7136\u4f1a\u662f\u6076\u884c\u8005\u4e0e\u6bd2\u89c2 \u97f3\u7684\u5e08\u7236\uff01\u674e\u9038\u5f3a\u6444\u5fc3\u795e\uff0c\u65bd\u793c\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u8001\u524d\u8f88\u5230\u6765\uff0c\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u90a3\u602a\u9053\u58eb\u7785\u7740\u674e\u9038\u8bf4\u9053\uff1b\u201c\u6211\u7279\u6765\u770b\u770b\u590f\u4faf\u575a\u91d1\u9488\u62d4\u6bd2\u7684\u672c\u9886\u3002\u54fc\uff0c\u4f60\u5feb\u628a\u8863\u670d\u8131 \u5149\uff0c\u8ba9\u6211\u9a8c\u4e00\u9a8c\u770b\u3002\u201d\u674e\u9038\u51fa\u8eab\u9ad8\u8d35\uff0c\u5373\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u4e5f\u662f\u4eba\u4eba\u5bf9\u4ed6\u4f18\u793c\u6709\u52a0\uff0c\u90a3\u5fcd\u53d7 \u5f97\u4e86\u8fd9\u822c\u4fae\u8c29\uff0c\u4e0d\u7981\u52c3\u7136\u5927\u6012\uff0c\u65a5\u9053\uff1a\u201c\u5996\u9053\u51fa\u8a00\u65e0\u793c\uff0c\u4f60\u6b32\u89c1\u8bc6\u91d1\u9488\u62d4\u6bd2\u7684\u672c\u9886\uff0c\u7406 \u8be5\u53bb\u62dc\u89c1\u91d1\u9488\u56fd\u624b\u672c\u4eba\u3002\u201d \u3000\u3000\u90a3\u9053\u58eb\u88ab\u4ed6\u65a5\u9a82\uff0c\u5e76\u4e0d\u751f\u6c14\uff0c\u53c8\u7785\u4e86\u674e\u9038\u4e00\u773c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u590f\u4faf\u575a\u6211\u5f53\u7136\u4e5f\u8981\u89c1 \u7684\uff0c\u4f46\u6211\u751f\u6765\u6027\u6025\uff0c\u5374\u60f3\u5148\u6765\u770b\u770b\u4f60\u662f\u600e\u4e48\u80fd\u6d3b\u5230\u5982\u4eca\u7684\u3002\u5582\uff0c\u4f60\u81ea\u5df1\u4e0d\u9664\u8863\u670d\uff0c\u8981\u957f \u8005\u7ed9\u4f60\u4ee3\u52b3\u4e48\uff1f\u201d\u84e6\u7136\u8fc8\u524d\u4e00\u6b65\uff0c\u4f38\u51fa\u624b\u81c2\uff0c\u75be\u7684\u5411\u674e\u9038\u5f53\u80f8\u4e00\u6293\uff0c\u674e\u9038\u53cc\u773c\u5706\u7741\uff0c\u62d4 \u51fa\u5b9d\u5251\uff0c\u4e00\u4e2a\u6ed1\u6b65\u56de\u8eab\uff0c\u53cd\u624b\u5c31\u662f\u4e00\u62db\u201c\u795e\u9f99\u6012\u76ee\u201d\uff0c\u8fd9\u4e00\u5251\u4e43\u662f\u6606\u4ed1\u5251\u6cd5\u4e2d\u7684\u4e00\u8bb0\u6740 \u624b\u7edd\u62db\uff0c\u5251\u5c16\u523a\u654c\u4eba\u7684\u201c\u795e\u5ead\u7a74\u201d\uff0c\u5251\u950b\u622a\u654c\u4eba\u7684\u624b\u8155\uff0c\u5251\u67c4\u649e\u654c\u4eba\u7684\u80f8\u819b\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c \u53c8\u5feb\u53c8\u72e0\uff01\u90a3\u9053\u58eb\u5fae\u5fae\u4e00\u7b11\u3002\u65e2\u4e0d\u89c1\u4ed6\u8df3\u8dc3\u95ea\u907f\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u51fa\u624b\u53cd\u51fb\uff0c\u53ea\u662f\u4e0d\u75be\u4e0d\u5f90\u7684 \u5411\u524d\u8de8\u4e0a\u4e00\u6b65\uff0c\u62ff\u634f\u65f6\u5019\uff0c\u5999\u5230\u6beb\u5dc5\uff0c\u674e\u9038\u8fd9\u6781\u5389\u5bb3\u7684\u4e00\u62db\u4e09\u5f0f\uff0c\u7adf\u7136\u90fd\u843d\u7a7a\u4e86\u3002 \u3000\u3000\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u89c1\u90a3\u9053\u58eb\u5df2\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff0c\u4e00\u53cc\u773c\u775b\u597d\u50cf\u5c31\u8981\u8d34\u5230\u4ed6\u7684\u9762\u4e0a\uff0c\u8be1 \u5f02\u4e4b\u6781\uff01\u674e\u9038\u4e0d\u5047\u601d\u7d22\uff0c\u500f\u7684\u53c8\u662f\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u5251\u5c16\u6643\u52a8\uff0c\u5241\u4ed6\u54bd\u5589\uff0c\u4e24\u4eba\u76f8\u8ddd \u4e0d\u5230\u4e09\u5c3a\uff0c\u674e\u9038\u5fc3\u60f3\u7eb5\u7136\u4f24\u4e0d\u4e86\u4ed6\uff0c\u81f3\u5c11\u4e5f\u53ef\u4ee5\u8feb\u5f97\u4ed6\u9000\u540e\u3002\u90a3\u6599\u8fd9\u602a\u9053\u58eb\u7adf\u662f\u51dd\u7acb\u4e0d \u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u5c09\u8fdf\u70af\u7684\u5f92\u5f1f\uff0c\u5251\u6cd5\u4e0d\u4fd7\uff0c\u4e0d\u8fc7\u5374\u5948\u6211\u4f55\uff01\u201d\u773c\u770b\u5251\u5c16\u582a\u582a\u523a\u5230\uff0c \u90a3\u9053\u58eb\u4ecd\u662f\u795e\u8272\u4e0d\u53d8\uff0c\u5ffd\u5730\u4f38\u51fa\u53cc\u6307\uff0c\u8fce\u7740\u5251\u950b\u4fbf\u662f\u4e00\u63a8\uff0c\u674e\u9038\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4efb\u4ed6\u672c\u9886 \u901a\u5929\uff0c\u7a76\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u600e\u80fd\u6321\u5f97\u6211\u7684\u5b9d\u5251\uff1f\u8fd9\u5996\u9053\u867d\u7136\u65e0\u793c\uff0c\u4e5f\u4e0d\u5b9c\u4fbf\u4f24\u4e86\u4ed6\u7684\u6027\u547d\u3002\u201d \u7a0d\u4e00\u8e0c\u8e0c\uff0c\u5ffd\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9053\u58eb\u5728\u5251\u4e0a\u4e00\u5f39\uff0c\u53cc\u6307\u4e00\u79fb\uff0c\u84e6\u5730\u5939\u7740\u5251\u810a\uff0c\u674e\u9038 \u4f46\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u970e\u90a3\u4e4b\u95f4\uff0c\u5b9d\u5251\u5df2\u7ed9\u4ed6\u5288\u624b\u593a\u53bb\uff01 \u3000\u3000\u90a3\u9053\u571f\u50b2\u7136\u4e00\u7b11\uff0c\u63b7\u5251\u4e8e\u5730\uff0c\u518d\u8de8\u4e0a\u4e00\u6b65\uff0c\u674e\u9038\u6025\u5fd9\u4f7f\u4e2a\u201c\u9634\u9633\u53cc\u649e\u638c\u201d\uff0c\u4f7f\u51fa\u6d51 \u8eab\u6c14\u529b\uff0c\u60f3\u628a\u9053\u58eb\u63a8\u5f00\uff0c\u624b\u6307\u8fd8\u672a\u6cbe\u5bf9\u65b9\uff0c\u4fbf\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u674e\u9038\u7684\u4e0a\u8863\u5df2\u7ed9\u4ed6\u6495 \u4e3a\u4e24\u7247\uff0c\u9732\u51fa\u4e86\u96ea\u767d\u7684\u80f8\u812f\u3002\u90a3\u9053\u58eb\u4fa7\u76ee\u659c\u77a7\uff0c\u602a\u58f0\u53eb\u9053\uff1a\u201c\u771f\u662f\u5947\u8ff9\uff0c\u590f\u4faf\u575a\u679c\u7136\u628a \u4f60\u533b\u597d\u4e86\uff01\u597d\uff0c\u4e0d\u8fc7\u6211\u8fd8\u8981\u4eb2\u81ea\u518d\u8bd5\u4e00\u4e0b\u4ed6\u7684\u672c\u9886\u2026\u5f85\u6211\u518d\u6253\u4f60\u4e00\u638c\uff0c\u770b\u4ed6\u80fd\u4e0d\u80fd\u533b\uff1f\u201d \u674e\u9038\u4e00\u51fb\u4e0d\u4e2d\uff0c\u672a\u53ca\u53d8\u62db\uff0c\u90a3\u9053\u58eb\u957f\u8896\u4e00\u5377\uff0c\u65e9\u628a\u4ed6\u53cc\u624b\u5d4c\u4f4f\uff0c\u6709\u5982\u4e00\u9053\u94c1\u7b8d\uff0c\u628a\u674e\u9038 \u7b8d\u5f97\u52a8\u5f39\u4e0d\u5f97\u3002\u4f46\u89c1\u4ed6\u9ad8\u4e3e\u53f3\u624b\uff0c\u9c9c\u7ea2\u7684\u638c\u5fc3\u8f6c\u773c\u95f4\u5c31\u53d8\u6210\u6df1\u7d2b\uff0c\u900f\u51fa\u4e00\u5c42\u9ed1\u6c14\uff0c\u518d\u4e00 \u8f6c\u773c\u6574\u5757\u624b\u638c\u90fd\u53d8\u4e86\u9ed1\u8272\u3002\u9053\u58eb\u54c8\u54c8\u4e00\u7b11\uff0c\u624b\u638c\u6162\u6162\u4e0b\u79fb\uff0c\u5411\u4ed6\u80f8\u819b\u5370\u53bb\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u5c16\u53eb\uff0c\u957f\u5b59\u58c1\u558a\u9053\uff1a\u201c\u4f11\u5f97\u4f24\u6211\u6bbf\u4e0b\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u4e00\u6251\u5c31\u6251\u5728\u674e\u9038\u8eab \u4e0a\u3002 \u3000\u3000\u957f\u5b59\u58c1\u7a81\u7136\u6251\u6765\uff0c\u602a\u9053\u58eb\u4e5f\u9887\u611f\u610f\u5916\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u80c6\u5927\u7684\u5c0f\u59d1 \u5a18\uff0c\u4f60\u60f3\u9001\u6b7b\u5417\uff1f\u8d70\u5f00\uff01\u201d\u957f\u5b59\u58c1\u7d27\u7d27\u62b1\u7740\u674e\u9038\uff0c\u671b\u4e5f\u4e0d\u671b\u90a3\u9053\u58eb\u4e00\u773c\uff0c\u5931\u58f0\u9a82\u9053\uff1a \u201c\u81ed\u9053\u58eb\uff0c\u6211\u5c31\u662f\u6b7b\u4e86\u4e5f\u4e0d\u8d70\u5f00\uff01\u201d\u90a3\u602a\u9053\u58eb\u4f38\u51fa\u4e94\u6307\uff0c\u5374\u5e76\u4e0d\u662f\u771f\u4e2a\u6293\u4e0b\uff0c\u53ea\u5728\u5979\u7684 \u4e91\u9b13\u8fb9\u8f7b\u8f7b\u4e00\u62db\uff0c\u628a\u9f3b\u5b50\u51d1\u4e0a\u53bb\u4e00\u95fb\uff0c\u8361\u58f0\u7b11\u9053\uff1a\u201c\u597d\u9999\uff0c\u597d\u9999\uff01\u6bd4\u8d77\u4f60\u6765\uff0c\u6211\u7684\u786e\u662f \u4e2a\u81ed\u9053\u58eb\u4e86\u3002\u54c8\uff0c\u50cf\u4f60\u8fd9\u6837\u4e00\u4f4d\u5439\u5f39\u5f97\u7834\u7684\u7f8e\u4eba\u513f\uff0c\u6211\u8fd8\u771f\u820d\u4e0d\u5f97\u4e0b\u624b\u5462\uff01\u201d\u4ed6\u5df2\u8fd0\u8d77 \u4e86\u6bd2\u638c\u795e\u529f\uff0c\u53cc\u638c\u89e6\u4eba\u7acb\u6b7b\uff0c\u8fd9\u65f6\u771f\u4e2a\u4e0d\u6562\u78b0\u957f\u5b59\u58c1\u4e00\u4e0b\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u7a81\u7136\u62d4\u4e0b\u9986\u9ae6\u7684 \u5934\u94ae\uff0c\u9694\u7740\u8863\u88f3\uff0c\u4fbf\u5411\u957f\u5b59\u58c1\u814b\u7a9d\u4e00\u70b9\uff0c\u4ed6\u662f\u60f3\u628a\u957f\u5b59\u58c1\u70b9\u5012\u4e4b\u540e\uff0c\u7136\u540e\u518d\u62ff\u674e\u9038\u8bd5\u9a8c \u4ed6\u7684\u6bd2\u638c\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a9\u4e00\u53d1\u4e4b\u65f6\uff0c\u5ffd\u5730\u6709\u4e00\u4e1d\u94f6\u5149\u4e00\u95ea\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u602a\u9053\u58eb\u90a3\u6839\u5934\u94ec\u6253 \u6b6a\uff0c\u602a\u9053\u58eb\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u590f\u4faf\u8001\u5f1f\uff0c\u7ec8\u4e8e\u628a\u4f60\u5f15\u51fa\u6765\u4e86\uff01\u201d\u590f\u4faf\u575a\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u4e0d\u6b7b \u7684\u725b\u9f3b\u5b50\uff0c\u4f60\u81ea\u547d\u662f\u4e00\u4ee3\u5b97\u5e08\uff0c\u600e\u7684\u5982\u6b64\u4e0b\u6d41\uff1f\u201d \u3000\u3000\u90a3\u602a\u9053\u58eb\u653e\u5f00\u4e8c\u4eba\uff0c\u8fd9\u624d\u56de\u8fc7\u5934\u7b11\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd9\u4e00\u4ee3\u9ad8\u4eba\uff0c\u600e\u4e48\u51fa\u53e3\u4fbf\u9a82\u4eba\uff1f\u6211 \u601c\u60dc\u6807\u81f4\u7684\u5c0f\u59d1\u5a18\uff0c\u5c31\u7b49\u5982\u4f60\u7231\u62a4\u597d\u770b\u7684\u82b1\u8349\u4e00\u822c\uff0c\u8fd9\u4e5f\u7b97\u5f97\u662f\u4e0b\u6d41\u4e48\uff1f\u201d\u590f\u4faf\u575a\u9053\uff1a \u201c\u4ee5\u4f60\u7684\u8eab\u4efd\uff0c\u6b3a\u4fae\u5c0f\u8f88\uff0c\u8fd8\u4e0d\u7b97\u662f\u4e0b\u6d41\uff1f\u201d\u90a3\u602a\u9053\u58eb\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5b58\u5fc3\u6b3a\u8d1f\u4ed6\uff0c\u53ea\u4e0d \u8fc7\u60f3\u8bd5\u8bd5\u91d1\u9488\u62d4\u6bd2\u7684\u672c\u9886\u3002\u201d \u3000\u3000\u590f\u4faf\u575a\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u90a3\u602a\u9053\u58eb\u9053\uff1a\u201c\u6211\u81ea\u4fe1\u6211\u79d8\u5236\u7684\u6bd2\u836f\u6697\u5668\uff0c\u5929\u4e0b \u65e0\u4eba\u80fd\u89e3\uff0c\u5374\u4e0d\u6599\u7ed9\u4f60\u89e3\u4e86\u3002\u8fd9\u4e5f\u8bb8\u662f\u6211\u90a3\u4e24\u4e2a\u5f92\u5f1f\u529f\u529b\u592a\u5dee\uff0c\u6697\u5668\u7684\u6bd2\u6027\u4e5f\u672a\u591f\u5389\u5bb3 \u4e4b\u6545\u3002\u6211\u518d\u6253\u4ed6\u4e00\u638c\uff0c\u82e5\u7136\u4f60\u8fd8\u80fd\u5728\u4e09\u4e2a\u6708\u5185\u5c06\u4ed6\u6cbb\u597d\uff0c\u6211\u5c31\u670d\u4e86\u4f60\u4e86\u3002\u201d\u590f\u4faf\u575a\u76b1\u7709 \u8bf4\u9053\uff1a\u201c\u4ee5\u4eba\u547d\u4f5c\u4e3a\u513f\u620f\uff0c\u4f24\u5929\u5bb3\u7406\uff0c\u83ab\u6b64\u4e3a\u751a\uff01\u201d\u90a3\u602a\u9053\u58eb\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u5929\u5730\u4e0d\u4ec1\uff0c \u4ee5\u4e07\u7269\u4e3a\u8c82\u72d7\uff0c\u600e\u89c1\u5f97\u5929\u516c\u7684\u5fc3\u80a0\u5c31\u5fc5\u7136\u662f\u6148\u60b2\u7684\u5462\uff1f\u4f60\u5fd8\u4e86\u6211\u7684\u9053\u53f7\u5417\uff1f\u5176\u5b9e\u6211\u5e76\u4e0d \u7acb\u5fc3\u4f5c\u6076\uff0c\u6211\u53ea\u662f\u987a\u5176\u81ea\u7136\uff0c\u5929\u6709\u96f7\u9706\u4e4b\u5a01\uff0c\u4e5f\u6709\u96e8\u9732\u4e4b\u5fb7\uff0c\u4f60\u81ea\u79f0\u4e16\u5916\u9ad8\u4eba\uff0c\u5374\u600e\u8fd9 \u822c\u8fc2\u8150\uff1f\u6211\u62ff\u4ed6\u8bd5\u4e0b\u6bd2\u638c\uff0c\u82e5\u662f\u4f60\u533b\u597d\u4e86\uff0c\u90a3\u5c31\u662f\u533b\u672f\u4e0a\u7684\u4e00\u5927\u6210\u5c31\uff0c\u82e5\u662f\u4ed6\u7ed9\u6211\u6253\u6b7b \u4e86\uff0c\u90a3\u4e5f\u5c31\u8bc1\u5b9e\u4e86\u6211\u7684\u786e\u4e3a\u6b66\u5b66\u6dfb\u4e86\u7edd\u4e16\u5947\u529f\u3002\u6240\u4ee5\u6211\u7684\u8bd5\u9a8c\uff0c\u4e0d\u8bba\u662f\u6210\u662f\u8d25\uff0c\u4e0d\u8bba\u662f \u4f60\u9ad8\u660e\u8fd8\u662f\u6211\u9ad8\u660e\uff0c\u603b\u4e4b\u90fd\u5927\u6709\u76ca\u5904\u3002\u4e00\u6761\u4eba\u547d\uff0c\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u602a\u9053\u58eb\u540d\u53eb\u201c\u5929\u6076\u9053\u4eba\u201d\uff0c\u5728\u90aa\u6d3e\u4e4b\u4e2d\u662f\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u5c24\u5176\u4ed6\u5bf9\u81ea\u5df1\u7684 \u5582\u6bd2\u6697\u5668\u548c\u6bd2\u638c\u529f\u592b\uff0c\u66f4\u81ea\u8d1f\u662f\u4e16\u4e0a\u65e0\u53cc\u3002\u4e0d\u8fc7\u4ed6\u5374\u7edd\u4e0d\u80af\u8f7b\u6613\u51fa\u624b\uff0c\u8fd9\u56de\u56e0\u4e3a\u542c\u5230\u4e86 \u590f\u4faf\u575a\u7adf\u80fd\u628a\u674e\u9038\u533b\u597d\uff0c\u6240\u4ee5\u624d\u6025\u7740\u8981\u8d77\u6765\u4e00\u8bd5\u3002\u987b\u77e5\u4ed6\u662f\u4f7f\u6bd2\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u4ed6\u53c8\u600e\u5bb9 \u5f97\u4e16\u4e0a\u6709\u4eba\u80fd\u514b\u5236\u4ed6\uff1f \u3000\u3000\u590f\u4faf\u575a\u542c\u4e86\u4ed6\u8fd9\u756a\u6b6a\u7406\uff0c\u77e5\u9053\u8fa9\u4e5f\u65e0\u7528\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u4e09\u5341\u5e74\u524d\u4e0e\u4ed6\u76f8\u4f1a\u4e4b\u65f6\uff0c \u4ed6\u662f\u8fd9\u822c\u5f62\u8c8c\uff0c\u4e09\u5341\u5e74\u540e\uff0c\u4ecd\u7136\u672a\u89c1\u8870\u8001\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u53ef\u60f3\u800c\u77e5\u3002\u201d\u518d\u770b\u4e00\u773c\u4ed6\u90a3\u53cc\u6df1 \u9ed1\u8272\u7684\u624b\u638c\uff0c\u590f\u4faf\u575a\u9976\u662f\u91d1\u9488\u56fd\u624b\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u60ca\u5fc3\uff01 \u3000\u3000\u5929\u6076\u9053\u4eba\u602a\u773c\u4e00\u7741\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u590f\u4faf\u8001\u5f1f\uff0c\u4f60\u7684\u91d1\u9488\u5e26\u6765\u4e86\u6ca1\u6709\uff1f\u6211\u53ef\u8981\u8bd5\u5566\uff01\u201d \u4f5c\u52bf\u4fbf\u8981\u5411\u674e\u9038\u6251\u53bb\uff0c\u590f\u4faf\u575a\u62e6\u5728\u4ed6\u7684\u9762\u524d\uff0c\u53eb\u9053\uff1a\u201c\u9053\u5144\u4e14\u6162\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u5929\u6076\u9053 \u4eba\u9053\uff1a\u201c\u4f60\u60f3\u529d\u6211\u6539\u53d8\u4e3b\u610f\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u80fd\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e5f\u60f3\u89c1\u8bc6\u89c1\u8bc6\u4f60\u8fd9 \u7edd\u4e16\u65e0\u53cc\u7684\u6bd2\u638c\u529f\u592b\uff0c\u4e0d\u8fc7\u8fd9\u4f4d\u674e\u516c\u5b50\u4ed6\u7684\u4f24\u8fd8\u672a\u5c3d\uff1b\u4f60\u5c31\u662f\u4e00\u638c\u5c06\u4ed6\u6bd9\u4e86\uff0c\u4e5f\u663e\u4e0d\u51fa \u4f60\u7684\u5389\u5bb3\uff0c\u600e\u80fd\u8bc1\u5b9e\u4f60\u7684\u6bd2\u638c\u662f\u4e16\u4e0a\u65e0\u53cc\uff1f\u201d\u5929\u6076\u9053\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u4e5f\u6709\u9053 \u7406\uff0c\u4f46\u8feb\u5207\u4e4b\u95f4\uff0c\u5374\u90a3\u513f\u53bb\u627e\u4e00\u4f4d\u9ad8\u624b\u6765\u7ed9\u6211\u8bd5\u638c\uff1f\u201d\u590f\u4faf\u575a\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d \u6562\u4ee5\u9ad8\u624b\u81ea\u5c45\uff0c\u4f46\u81ea\u95ee\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u8fd8\u591f\u575a\u786c\uff0c\u5c31\u7531\u6211\u63a5\u4f60\u4e00\u638c\uff0c\u8bd5\u8bd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u674e\u9038\u521a\u624d\u5728\u751f\u6b7b\u503e\u9877\u4e4b\u9645\uff0c\u5ffd\u7136\u5f97\u957f\u5b59\u58c1\u820d\u8eab\u76f8\u6551\uff0c\u5fc3\u4e2d\u53c8\u662f\u611f\u6fc0\u53c8\u662f\u8ff7\u4e71\uff0c\u957f\u5b59 \u58c1\u4e0e\u4ed6\u5e76\u5750\u5e8a\u4e0a\uff0c\u5140\u81ea\u7d27\u7d27\u7684\u501a\u504e\u7740\u4ed6\uff0c\u67d4\u58f0\u8f6f\u8bed\uff0c\u66ff\u4ed6\u538b\u60ca\uff0c\u6839\u672c\u5c31\u4e0d\u7406\u4f1a\u5929\u6076\u9053\u4eba \u8fd8\u5728\u8eab\u65c1\uff0c\u4e5f\u4e0d\u7406\u4f1a\u4ed6\u4e0e\u590f\u4faf\u575a\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u597d\u50cf\u5728\u8fd9\u6597\u5ba4\u4e4b\u4e2d\uff0c\u53ea\u6709\u4ed6\u4eec\u4e8c\u4eba\u4f3c\u7684\u3002\u674e \u9038\u4e0e\u5979\u8033\u9b13\u53ae\u78e8\uff0c\u5c11\u5973\u8eab\u4f53\u7279\u6709\u7684\u9999\u751c\u6c14\u606f\uff0c\u4e00\u7f15\u7f15\u7684\u4f20\u5165\u4ed6\u7684\u9f3b\u89c2\uff0c\u82b3\u6c81\u813e\u8151\uff0c\u8212\u670d \u4e4b\u6781\uff0c\u4f46\u5374\u53c8\u4ee4\u4ed6\u60f6\u60d1\u4e0d\u5b89\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u4e07\u4e0d\u80fd\u518d\u60f9\u70e6\u607c\uff0c\u5e76\u5bb3\u4eba\u5bb6\u70e6\u607c\u4e86\uff01\u201d\u5fc3 \u795e\u7a0d\u5b9a\uff0c\u6025\u5fd9\u628a\u773c\u5149\u79fb\u5f00\uff0c\u53ea\u89c1\u590f\u4faf\u575a\u8d1f\u624b\u800c\u7acb\uff0c\u5766\u7136\u7684\u7ad9\u5728\u65e0\u6076\u9053\u4eba\u9762\u524d\uff0c\u6b63\u62fc\u7740\u4ee5 \u8840\u8089\u4e4b\u8eaf\uff0c\u6765\u8bd5\u5929\u6076\u9053\u4eba\u7684\u6bd2\u638c\uff01 \u3000\u3000\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u590f\u4faf\u8001\u4f2f\uff0c\u8fd9\u6837\u4e0d\u884c\uff0c\u8fd8\u662f\u8ba9\u6211\u6765\u8bd5\u5427\u3002\u6211\u4f24\u4e86\u6709\u4f60 \u6765\u533b\uff0c\u4f60\u82e5\u4f24\u4e86\uff0c\u5929\u4e0b\u54ea\u8fd8\u80fd\u627e\u51fa\u7b2c\u4e8c\u4f4d\u91d1\u9488\u56fd\u624b\uff1f\u201d\u5929\u6076\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u592a \u4e0d\u81ea\u91cf\uff0c\u4f60\u73b0\u5728\u5c31\u662f\u9001\u4e0a\u6765\u81ea\u613f\u6328\u6253\uff0c\u6211\u4e5f\u4e0d\u5c51\u62ff\u4f60\u8bd5\u638c\u5566\uff01\u201d\u957f\u8896\u4e00\u6325\uff0c\u5c06\u674e\u9038\u5377\u7ffb\uff0c \u201c\u556a\u5566\u201d\u4e00\u58f0\uff0c\u4ecd\u7136\u5c06\u4ed6\u6454\u56de\u5e8a\u4e0a\uff0c\u5374\u5411\u590f\u4faf\u575a\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u8be5\u62ff\u4f60\u8bd5\u8bd5\uff0c\u4f60\u7684 \u6b66\u529f\u867d\u7136\u4e0d\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u4e5f\u7b97\u5f97\u6709\u6570\u7684\u9ad8\u624b\u4e86\uff0c\u81f3\u4e8e\u4f60\u7684\u533b\u672f\uff0c\u90a3\u5374\u7684\u786e\u662f\u5929\u4e0b\u7b2c\u4e00\u7684\uff0c \u62ff\u4f60\u6765\u8bd5\uff0c\u6700\u597d\u4e0d\u8fc7\uff01\u201d \u3000\u3000\u590f\u4faf\u575a\u9053\uff1a\u201c\u6211\u82e5\u80fd\u63a5\u5f97\u4f4f\u4f60\u7684\u6bd2\u638c\u3002\u8fd9\u53c8\u5982\u4f55\uff1f\u201d\u5929\u6076\u9053\u4eba\u6b6a\u7740\u773c\u775b\u53cd\u95ee\u9053\uff1a \u201c\u6709\u751a\u5982\u4f55\uff1f\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u6211\u82e5\u63a5\u5f97\u4f60\u7684\u6bd2\u638c\uff0c\u6562\u8bf7\u4f60\u4ee5\u540e\u5c06\u8fd9\u79cd\u90aa\u6bd2\u7684\u529f\u592b\u6536\u8d77\uff0c \u4e0d\u518d\u7528\u6765\u5bb3\u4eba\u3002\u201d\u5929\u6076\u9053\u4eba\u7b11\u9053\uff1a\u201c\u6211\u624d\u4e0d\u8fd9\u4e48\u7b28\uff0c\u4e3a\u4f60\u7acb\u8fd9\u79cd\u8a93\u7ea6\uff0c\u53d7\u4f60\u7684\u62d8\u675f\uff0c\u4f60 \u82e5\u771f\u80fd\u63a5\u6211\u4e00\u638c\uff0c\u6beb\u65e0\u4f24\u635f\uff0c\u90a3\u53ea\u662f\u8bc1\u5b9e\u6211\u7684\u529f\u592b\u8fd8\u672a\u7ec3\u5f97\u5230\u5bb6\uff0c\u5f85\u6211\u7ec3\u597d\u4e4b\u540e\uff0c\u518d\u627e \u4f60\u6765\u4e00\u8bd5\u4fbf\u662f\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u5728\u4f60\u672a\u7ec3\u597d\u4e4b\u524d\u5462\uff1f\u201d\u5929\u6076\u9053\u4eba\u9053\uff1a\u201c\u90a3\u6211\u5f53\u7136\u65e0\u989c\u518d \u7528\u3002\u201d\u590f\u4faf\u575a\u4e00\u60f3\uff0c\u867d\u7136\u4e0d\u80fd\u7981\u4ed6\u6c38\u8fdc\u4e0d\u7528\uff0c\u4f46\u6700\u5c11\u53ef\u4ee5\u62d8\u67ec\u4ed6\u51e0\u5e74\uff0c\u800c\u4e14\u674e\u9038\u7684\u6027\u547d \u90a3\u662f\u5b9a\u53ef\u4fdd\u5168\u7684\u4e86\uff0c\u4e8e\u662f\u4fbf\u5766\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8fd9\u6837\u5427\u3002\u8bf7\u4f60\u53d1\u638c\uff01\u201d\u5929\u6076\u9053\u4eba\u53cc\u638c\u4e00 \u6413\uff0c\u7d2b\u9ed1\u8272\u7684\u638c\u5fc3\u7adf\u81ea\u53d1\u51fa\u817e\u817e\u70ed\u6c14\uff0c\u5ffd\u5730\u547c\u7684\u4e00\u638c\uff0c\u5411\u590f\u4faf\u575a\u7684\u80f8\u819b\u4fbf\u5373\u62cd\u4e0b\u3002 \u3000\u3000\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5982\u51fb\u8d25\u8349\uff0c\u590f\u4faf\u575a\u9000\u540e\u4e09\u6b65\uff0c\u5929\u6076\u9053\u4eba\u4e5f\u7ed9\u4ed6\u7684\u53cd\u8eab\u4e4b\u529b\uff0c \u9707\u5f97\u4e0a\u8eab\u5fae\u5fae\u6447\u6643\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u674e\u9038\u4e0e\u957f\u5b59\u58c1\u624b\u5fc3\u90fd\u634f\u7740\u4e00\u628a\u51b7\u6c57\uff0c\u7d27\u5f20\u5f97\u8fde\u547c\u5438\u90fd\u900f \u4e0d\u8fc7\u6765\u3002\u4f46\u89c1\u5929\u6076\u9053\u4eba\u4e0e\u590f\u4faf\u575a\u8fce\u9762\u800c\u7acb\uff0c\u5f7c\u6b64\u90fd\u76ee\u4e0d\u8f6c\u775b\u7684\u6253\u91cf\u7740\u5bf9\u65b9\uff0c\u8fc7\u4e86\u534a\u664c\uff0c \u5929\u6076\u9053\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u597d\uff1f\u201d\u590f\u4faf\u575a\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u627f\u5173\u6ce8\uff0c\u6211\u8fd9\u51e0\u6839\u8001\u9aa8\u5934 \u5c1a\u5e78\u800c\u65e0\u4e8b\uff0c\u4f60\u597d\u5417\uff1f\u201d\u674e\u9038\u89c1\u590f\u4faf\u575a\u7684\u9762\u8272\u5df2\u6e10\u6e10\u60ed\u590d\u6b63\u5e38\uff0c\u542c\u4ed6\u7684\u58f0\u97f3\u4e2d\u6c14\u4e5f\u8fd8\u5145 \u6c9b\uff0c\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u597d\u751f\u60ca\u8be7\uff0c\u4ed6\u4ece\u590f\u4faf\u575a\u8fd9\u4e00\u638c\u53cd\u9707\u4e4b\u529b\uff0c\u8bd5\u51fa\u4e86\u4ed6\u7684\u5185\u529f\u6df1\u6e5b\uff0c\u786e\u5b9e\u662f\u6709 \u70b9\u51fa\u4e4e\u81ea\u5df1\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u66f4\u4ee4\u4ed6\u4e0d\u89e3\u7684\u662f\uff0c\u4ed6\u7684\u6bd2\u638c\uff0c\u4e0d\u4f46\u638c\u529b\u53ef\u4ee5\u5f00\u7891\u88c2\u77f3\uff0c\u6bd2\u529b \u4e4b\u731b\uff0c\u66f4\u53ef\u4ee5\u76f4\u900f\u810f\u8151\uff0c\u7eb5\u7b97\u590f\u4faf\u575a\u7684\u5185\u529f\u518d\u597d\uff0c\u4e5f\u603b\u5e94\u8be5\u6709\u6bd2\u6027\u53d1\u4f5c\u7684\u72b6\u51b5\uff0c\u4f46\u73b0\u5728 \u5df2\u9694\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u590f\u4faf\u575a\u7684\u9762\u4e0a\u7adf\u7136\u6ca1\u6709\u900f\u51fa\u534a\u4e1d\u9ed1\u6c14\u3002\u76ee\u5149\u4e5f\u8fd8\u662f\u90a3\u6837\u70af\u70af\u6709\u795e\u3002 \u4ed6\u5374\u4e0d\u77e5\uff0c\u590f\u5019\u575a\u5fc3\u4e2d\u7684\u60ca\u8be7\uff0c\u5176\u5b9e\u5e76\u4e0d\u5728\u4ed6\u4e4b\u4e0b\u3002\u590f\u4faf\u575a\u8fd9\u65f6\u4e5f\u6b63\u5728\u9ed8\u8fd0\u7384\u529f\uff0c\u6536\u655b \u4f53\u5185\u7684\u6bd2\u6c14\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u6253\u91cf\u4e86\u590f\u4faf\u575a\u4e00\u4f1a\uff0c\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u590f\u4faf\u8001\u5f1f\uff0c\u771f\u6709\u4f60\u7684\u3002\u4e0d\u8fc7\uff0c\u6211\u53ef \u8fd8\u672a\u8ba4\u8f93\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5df2\u786c\u63a5\u4e86\u4f60\u7684\u4e00\u638c\u4e48\uff1f\u201d\u5929\u6076\u9053\u4eba\u9053\uff1a\u201c\u6211\u5c31\u4e0d\u4fe1\u4f60 \u672b\u53d7\u5185\u4f24\uff0c\u7109\u77e5\u4f60\u4e0d\u662f\u53ea\u80fd\u575a\u633a\u4e00\u65f6\uff0c\u60f3\u5c06\u6211\u9a97\u8fc7\uff0c\u6211\u504f\u504f\u4e0d\u8d70\u3002\u770b\u770b\u4f60\u7ed3\u679c\u5982\u4f55\uff1f\u201d \u957f\u5b59\u58c1\u6697\u6697\u53eb\u82e6\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u9b54\u5934\u591a\u7559\u4e00\u523b\u5c31\u591a\u4e00\u5206\u5371\u9669\uff01\u201d \u3000\u3000\u590f\u4faf\u575a\u53cc\u773c\u4e00\u7741\uff0c\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u529f\u592b\u966a\u4f60\uff0c\u4f60\u8981\u600e\u6837\u624d\u80fd\u76f8\u4fe1\uff1f\u201d\u5929\u6076\u9053\u4eba\u9053\uff1a \u201c\u54b1\u4eec\u4e0d\u5982\u4e7e\u4e7e\u8106\u8106\uff0c\u5404\u4ee5\u672c\u8eab\u7684\u6b66\u529f\u518d\u6bd4\u4e00\u573a\uff0c\u82e5\u7136\u4f60\u8fd8\u80fd\u591f\u63a5\u6211\u767e\u62db\uff0c\u6211\u7acb\u523b\u8ba4\u8f93 \u4fbf\u8d70\u3002\u201d\u590f\u5019\u575a\u51b7\u7b11\u9053\uff1a\u201c\u62f3\u6765\u811a\u5f80\uff0c\u8fd9\u5c82\u4e0d\u6210\u4e86\u5e02\u4e95\u4e4b\u5f92\uff0c\u54b1\u4eec\u8981\u6bd4\u8bd5\u529f\u592b\uff0c\u4e5f\u7528\u4e0d \u7740\u8fd9\u79cd\u4fd7\u5b50\u51e1\u592b\u7684\u529e\u6cd5\u3002\u201d\u590f\u4faf\u575a\u8fd9\u756a\u8bf4\u8bdd\uff0c\u5728\u674e\u9038\u542c\u6765\uff0c\u4f3c\u4e4e\u5df2\u9732\u51fa\u4e00\u70b9\u602f\u610f\uff0c\u5fc3\u4e2d \u6697\u9053\u7cdf\u7cd5\uff0c\u53ea\u6015\u5929\u6076\u9053\u4eba\u66f4\u8981\u4e58\u673a\u5a01\u80c1\uff0c\u5b9a\u8981\u548c\u590f\u4faf\u575a\u8fc7\u62db\u3002\u54ea\u77e5\u8fd9\u4e00\u756a\u8bdd\u987a\u5e26\u5c06\u5929\u6076 \u9053\u4eba\u6367\u4e86\u4e00\u4e0b\uff0c\u5929\u6076\u9053\u4eba\u542c\u6765\u5341\u5206\u53d7\u7528\uff0c\u5fc3\u60f3\u4ee5\u5f7c\u6b64\u6b66\u5b66\u5927\u5e08\u7684\u8eab\u4efd\uff0c\u786e\u5b9e\u4e0d\u5fc5\u5728\u62f3\u811a \u4e0a\u6765\u663e\u529f\u592b\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u4fbf\u7b11\u800c\u95ee\u9053\uff1a\u201c\u4f60\u6709\u751a\u522b\u81f4\u7684\u65b9\u6cd5\uff1f\u597d\u5427\uff0c\u521a\u624d\u662f\u4f60\u542c\u6211\u7684\uff0c \u793c\u5c1a\u5f80\u6765\uff0c\u73b0\u5728\u6211\u4e5f\u4e3a\u4f60\u5212\u51fa\u9053\u6765\uff0c\u6211\u4e00\u51c6\u4f9d\u4ece\u4fbf\u662f\u3002\u201d \u3000\u3000\u590f\u4faf\u575a\u968f\u624b\u5728\u5e8a\u5934\u62ff\u8d77\u4e86\u4e00\u6761\u7ef3\u7d22\uff0c\u90a3\u662f\u957f\u5b59\u58c1\u5e26\u6765\u51c6\u5907\u66ff\u674e\u9038\u5305\u624e\u8863\u97e7\u7528\u7684\uff0c\u590f \u4faf\u575a\u5c06\u7ef3\u7d22\u4e00\u629b\uff0c\u9053\u58f0\uff1a\u201c\u63a5\u7740\uff01\u201d\u5929\u6076\u9053\u4eba\u63a5\u7740\u4e86\u7ef3\u7d22\u7684\u4e00\u5934\uff0c\u9053\uff1a\u201c\u5982\u4f55\u6bd4\u8bd5\uff1f\u201d \u590f\u4faf\u575a\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u4fe1\u4f60\u672a\u53d7\u5185\u4f24\uff0c\u6211\u53ef\u4ee5\u4ece\u7ef3\u7d22\u8fd9\u4e00\u7aef\u542c\u51fa\u4f60\u7684\u8109\u818a\uff0c\u60f3\u4f60\u5584\u4e8e\u4f7f\u6bd2\uff0c \u8fd9\u6837\u542c\u8109\u7684\u65b9\u6cd5\uff0c\u4f60\u4e5f\u5e94\u61c2\u5f97\u3002\u201d\u5929\u6076\u9053\u4eba\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u975e\u4f46\u53ef\u4ee5\u8fd9\u6761\u7ef3\u7d22\u542c\u51fa\u8109\u606f\uff0c \u8fd8\u53ef\u4ee5\u85c9\u6b64\u8f83\u91cf\u5185\u529f\uff0c\u4f60\u7684\u529e\u6cd5\uff0c\u6211\u540c\u610f\u4e86\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u5f88\u662f\u5947\u602a\uff0c\u5979\u4ee5\u524d\u542c\u7236\u4eb2\u8bf4\u8fc7\uff0c\u5bab\u4e2d\u7684\u540e\u5983\u5728\u751f\u75c5\u4e4b\u65f6\uff0c\u592a\u533b\u5949\u8bcf\u66ff\u5979\u4eec\u8bca \u8109\uff0c\u7167\u4f8b\u662f\u4e0d\u80fd\u7528\u624b\u6307\u63a5\u89e6\u5979\u4eec\u7684\u808c\u80a4\u7684\uff0c\u53ea\u80fd\u7528\u4e00\u6839\u4e1d\u7ebf\uff0c\u7f20\u5728\u5979\u4eec\u7684\u8109\u95e8\u4e0a\uff0c\u592a\u533b \u9694\u7740\u73e0\u5e18\uff0c\u7528\u4e09\u53ea\u6307\u5934\u63a5\u7740\u4e1d\u7ebf\u7684\u53e6\u4e00\u7aef\uff0c\u636e\u8bf4\u5982\u6b64\uff0c\u4fbf\u53ef\u4ee5\u542c\u51fa\u8109\u606f\u4e86\u3002\u5982\u4eca\u590f\u4faf\u575a \u4e0e\u5929\u6076\u9053\u4eba\u5404\u6267\u7ef3\u7d22\u7684\u4e00\u7aef\uff0c\u542c\u5bf9\u65b9\u7684\u5598\u606f\uff0c\u60f3\u5fc5\u4fbf\u662f\u8fd9\u4e2a\u65b9\u6cd5\uff0c\u4f46\u7ef3\u7d22\u8981\u6bd4\u4e1d\u7ebf\u957f\u5f97 \u591a\u7c97\u5f97\u591a\uff0c\u90a3\u66f4\u662f\u795e\u4e4e\u5176\u6280\u4e86\uff01\u800c\u4e14\u4ed6\u4eec\u8fd8\u8981\u7528\u8fd9\u6761\u7ef3\u7d22\u6765\u8f83\u91cf\u5185\u529f\uff0c\u8fd9\u6837\u7684\u6bd4\u8bd5\u529e\u6cd5\uff0c \u957f\u5b59\u58c1\u66f4\u662f\u89c1\u6240\u672b\u89c1\uff0c\u95fb\u6240\u672a\u95fb\uff0c\u771f\u4e0d\u77e5\u5982\u4f55\u8f83\u91cf\uff1f \u3000\u3000\u4f46\u89c1\u590f\u4faf\u575a\u4e0e\u5929\u6076\u9053\u4eba\u76d8\u819d\u800c\u5750\uff0c\u5404\u81ea\u9760\u7740\u4e00\u8fb9\u5899\u58c1\uff0c\u90a3\u6761\u7ef3\u7d22\u7ed9\u4ed6\u4eec\u62c9\u5f97\u7b14\u76f4\uff0c \u4e24\u4eba\u90fd\u662f\u773c\u89c2\u9f3b\uff0c\u9f3b\u89c2\u5fc3\uff0c\u597d\u50cf\u8001\u50e7\u5165\u5b9a\u7684\u6a21\u6837\uff0c\u8fc7\u4e86\u5927\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ecd\u662f\u52a8\u4e5f\u4e0d\u52a8\u3002\u957f \u5b59\u58c1\u83ab\u540d\u5176\u5999\uff0c\u751a\u4e3a\u7eb3\u7f55\uff0c\u770b\u674e\u9038\u65f6\uff0c\u5ffd\u89c1\u4ed6\u7709\u5c16\u6253\u7ed3\uff0c\u73b0\u51fa\u5fe7\u6025\u7684\u795e\u60c5\u3002\u957f\u5b59\u58c1\u518d\u4ed4 \u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u6761\u7ef3\u7d22\u5fae\u5fae\u98a4\u6296\uff0c\u9759\u5ba4\u5185\u6ca1\u6709\u4e00\u4e1d\u5fae\u98ce\uff0c\u590f\u4faf\u575a\u7684\u957f\u9aef\u5374\u5ffd\u7136\u98d8\u62c2\u4e0d\u5b89\uff0c \u957f\u5b59\u58c1\u867d\u7136\u4e0d\u8bc6\u5176\u4e2d\u5965\u5999\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u590f\u4faf\u575a\u5374\u4f4e\u5904\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u674e\u9038\u7684\u795e\u8272\u4e5f\u6e10\u6e10\u6062\u590d\u81ea\u7136\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u7ef3\u7d22\u8df3\u52a8\u4e86\u4e00\u4e0b\uff0c\u65e0\u6076\u9053 \u4eba\u90a3\u6de1\u9752\u8272\u7684\u9053\u888d\u4e5f\u5fae\u5fae\u8d77\u76b1\uff0c\u597d\u50cf\u4e00\u6e56\u5e73\u9759\u7684\u6625\u6c34\uff0c\u5ffd\u7136\u88ab\u5fae\u98ce\u8361\u8d77\u4e86\u6d9f\u6f2a\u3002 \u3000\u3000\u539f\u6765\u8fd9\u65f6\u6b63\u5230\u4e86\u5403\u7d27\u7684\u5173\u5934\uff0c\u4e24\u4eba\u5404\u4ee5\u4e0a\u4e58\u7684\u5185\u529f\u901a\u8fc7\u7ef3\u7d22\uff0c\u8bd5\u63a2\u5bf9\u65b9\u7684\u53cd\u5e94\uff0c\u5929 \u6076\u9053\u4eba\u611f\u89c9\u51fa\u590f\u4faf\u575a\u7684\u8109\u606f\u8d8a\u6765\u8d8a\u5f31\uff0c\u6b63\u81ea\u9ad8\u5174\uff0c\u5ffd\u7136\u590f\u4faf\u575a\u7684\u8109\u606f\u597d\u50cf\u5b8c\u5168\u65ad\u7edd\uff0c\u8fde \u4e00\u4e1d\u4e1d\u7684\u6ce2\u52a8\u90fd\u611f\u4e0d\u5230\u4e86\uff0c\u6309\u8bf4\u5230\u4e86\u8fd9\u4e2a\u65f6\u5019\uff0c\u590f\u4faf\u58c1\u5df2\u5e94\u8be5\u6c14\u7edd\u800c\u6b7b\uff0c\u4f46\u5947\u602a\u5f97\u5f88\uff0c \u4ed6\u7684\u5185\u529b\u8fd8\u662f\u7ef5\u7ef5\u5bc6\u5bc6\uff0c\u4e0d\u65ad\u7684\u4ece\u7ef3\u7d22\u4e2d\u4f20\u8fc7\u6765\uff0c\u5929\u6076\u9053\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u6478\u4e0d\u5230\u590f\u4faf\u575a\u7684 \u6df1\u6d45\uff0c\u5fc3\u5934\u7981\u4e0d\u4f4f\u5fae\u5fae\u4e00\u51db\uff0c\u51e0\u4e4e\u628a\u6301\u4e0d\u4f4f\u3002\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4e3b\u5ba2\u52bf\u6613\uff0c\u7ed9\u590f\u4faf\u575a\u5360 \u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u6025\u5fd9\u51dd\u795e\u8fd0\u6c14\uff0c\u529b\u56fe\u53cd\u51fb\uff0c\u60c5\u5f62\u4e0e\u521a\u624d\u5927\u5927\u4e0d\u540c\uff0c\u4f46\u89c1\u90a3\u6761\u7ef3\u7d22\u4e0d\u4f4f\u7684\u8df3 \u52a8\uff0c\u6e10\u6e10\u7adf\u50cf\u8df3\u7ef3\u4e00\u6837\u3002\u7ef3\u7d22\u4e0d\u4f4f\u7684\u6253\u7740\u5708\u5708\uff0c\u957f\u5b59\u58c1\u770b\u8fd9\u4e24\u4eba\uff0c\u4ecd\u662f\u5404\u81ea\u76d8\u819d\u800c\u5750\uff0c \u5782\u9996\u95ed\u76ee\uff0c\u5404\u4ee5\u4e09\u53ea\u6307\u5934\u6263\u7740\u7ef3\u7d22\u7684\u4e00\u7aef\uff0c\u6307\u5934\u5e76\u672a\u6446\u52a8\u3002\u663e\u89c1\u90a3\u7ef3\u7d22\u7684\u8df3\u52a8\uff0c\u4e43\u662f\u7531 \u4e8e\u5185\u529b\u7684\u9707\u8361\u6240\u81f4\u3002 \u3000\u3000\u8fd9\u65f6\u4e24\u4eba\u90fd\u611f\u5230\u5bf9\u65b9\u7684\u8109\u640f\u6563\u4e71\uff0c\u5404\u81ea\u51dd\u805a\u771f\u529b\uff0c\u4f5c\u6700\u540e\u7684\u4e00\u51fb\uff0c\u8fd9\u60c5\u5f62\u8fde\u957f\u5b59\u58c1 \u4e5f\u770b\u51fa\u6765\u4e86\uff0c\u4f46\u89c1\u90a3\u6761\u7ef3\u7d22\u4e0d\u4f4f\u6253\u7740\u5708\u5708\uff0c\u522e\u5f97\u5730\u4e0a\u7684\u7070\u5c18\u98de\u626c\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u9661\u7136\u95f4\u90a3 \u6761\u7ef3\u7d22\u7ef7\u5f97\u7d27\u4f3c\u5f13\u5f26\uff0c\u201c\u529b\u52d2\u201d\u6570\u58f0\uff0c\u4ece\u4e2d\u95f4\u65ad\u6210\u4e86\u5341\u51e0\u6bb5\u3002\u5929\u6076\u9053\u4eba\u9053\uff1a\u201c\u4f69\u670d\uff0c\u4f69 \u670d\uff0c\u4f60\u63a5\u4e86\u6211\u7684\u6bd2\u638c\uff0c\u529f\u529b\u5c45\u7136\u8fd8\u8db3\u4e0e\u6211\u76f8\u6301\uff0c\u6211\u8ba4\u8f93\u4e86\uff01\u201d\u629b\u5f00\u65ad\u7ef3\uff0c\u7acb\u523b\u8d70\u51fa\u8fd9\u95f4 \u5c4b\u5b50\uff0c\u8f6c\u773c\u4e4b\u95f4\u3002\u53ea\u542c\u5f97\u4ed6\u7684\u5578\u58f0\u5df2\u5728\u767e\u6b65\u4e4b\u5916\u3002\u590f\u4faf\u575a\u4ecd\u7136\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u672a\u6562\u79fb\u52a8\u3002 \u3000\u3000\u674e\u9038\u77e5\u9053\u590f\u4faf\u575a\u6b63\u5728\u8c03\u505c\u547c\u5438\uff0c\u6d3b\u8840\u8212\u7b4b\uff0c\u4e0d\u6562\u53bb\u60ca\u52a8\u4ed6\u3002\u957f\u5b59\u58c1\u9053\uff1a\u201c\u54a6\uff0c\u6211\u597d \u4f3c\u95fb\u5230\u4e00\u80a1\u8165\u81ed\u7684\u6c14\u5473\u3002\u201d\u674e\u9038\u60f3\u9053\uff1a\u201c\u96be\u9053\u90a3\u5929\u6076\u9053\u4eba\u5728\u5ba4\u4e2d\u7559\u4e0b\u4e86\u4ec0\u4e48\u6bd2\u7269\uff1f\u201d\u5ffd \u542c\u5f97\u95e8\u5916\u53c8\u6709\u811a\u6b65\u58f0\u54cd\uff0c\u674e\u9038\u4e0e\u957f\u5b59\u58c1\u4e43\u60ca\u5f13\u4e4b\u9e1f\uff0c\u6025\u5fd9\u62d4\u5251\u8d77\u89c6\uff0c\u539f\u6765\u5374\u662f\u90a3\u4e24\u4e2a\u836f \u7ae5\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u4eec\u4e00\u4e2a\u6367\u7740\u9999\u7089\uff0c\u4e00\u4e2a\u6367\u7740\u51c0\u74f6\uff0c\u7089\u4e2d\u711a\u7684\u4e0d\u77e5\u662f\u4ec0\u4e48\u5f02\u9999\uff0c\u9999\u6c14\u5919\u6c30\uff0c\u4e00 \u55c5\u4e4b\u4e0b\uff0c\u4fbf\u4ee4\u4eba\u6c14\u723d\u795e\u6e05\uff0c\u5fc3\u80f8\u5b81\u9759\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u590f\u4faf\u575a\u53cc\u76ee\u4e00\u5f20\uff0c\u5f90\u5f90\u8d77\u7acb\u3002\u8fde\u58f0\u8bf4 \u9053\uff1a\u201c\u597d\u9669\uff0c\u597d\u9669\uff01\u201d\u6367\u7740\u51c0\u74f6\u7684\u90a3\u4e2a\u836f\u7ae5\u5df2\u4f3a\u5019\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u590f\u4faf\u575a\u53d6\u51fa\u4e00\u679a\u91d1\u9488\uff0c \u5728\u5de6\u624b\u4e2d\u6307\u4e4b\u5c16\u4e00\u5241\uff0c\u5c06\u6bd2\u8840\u6324\u51fa\uff0c\u51e0\u4e4e\u6ce8\u6ee1\u4e86\u90a3\u4e2a\u51c0\u74f6\u3002\u5728\u4ed6\u9760\u8fc7\u7684\u5899\u58c1\u4e0a\u5219\u7559\u4e0b\u4e86 \u4e00\u56e2\u9ed1\u5370\uff0c\u808c\u7eb9\u9690\u73b0\uff0c\u597d\u50cf\u4e00\u662f\u4ed6\u80cc\u4e0a\u7a9c\u6709\u6d53\u58a8\u5370\u4e0a\u53bb\u7684\u4e00\u822c\uff0c\u674e\u9038\u8fd9\u624d\u53d1\u89c9\u90a3\u80a1\u8165\u81ed \u4e4b\u6c14\u4fbf\u662f\u4ece\u5899\u58c1\u4e0a\u8fd9\u56e2\u9ed1\u5370\u53d1\u51fa\u6765\u7684\u3002\u90a3\u4e24\u4e2a\u836f\u7ae5\uff0c\u653e\u4e0b\u4e86\u9999\u3001\u7089\uff0c\u53d6\u51fa\u94c1\u51ff\uff0c\u51ff\u4e0b\u4e86 \u90a3\u51e0\u5757\u7816\u5934\uff0c\u590f\u4faf\u575a\u5429\u5490\u9053\uff1a\u201c\u5c06\u8fd9\u51e0\u5757\u7816\u5934\u548c\u8fd9\u4e2a\u94f6\u74f6\uff0c\u90fd\u62ff\u5230\u5c71\u540e\u57cb\u4e86\uff0c\u8981\u57cb\u5f97\u6df1 \u4e9b\uff0c\u8fd8\u8981\u8bb0\u4f4f\u4e0d\u53ef\u9760\u8fd1\u5c71\u6cc9\u3002\u201d \u3000\u3000\u674e\u9038\u4e0d\u7981\u9a87\u7136\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u5929\u6076\u9053\u4eba\u7684\u6bd2\u638c\u600e\u7684\u8fd9\u822c\u5389\u5bb3\uff1f\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u8981\u4e0d\u662f \u6211\u65e9\u6709\u9632\u5907\uff0c\u4eca\u65e5\u65e9\u5df2\u547d\u4e27\u4ed6\u7684\u624b\u4e2d\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u4f60\u4e0e\u4ed6\u6bd4\u62fc\u5185\u529f\uff0c\u4e0d\u662f\u8d62\u4e86\u4e48\uff1f\u201d \u590f\u4faf\u575a\u9053\uff1a\u201c\u4e0d\u7b97\u5f97\u8d62\uff0c\u6211\u662f\u628a\u4ed6\u5413\u8d70\u7684\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u4f60\u5148\u53d7\u4e86\u4e00\u638c\uff0c\u8fd8\u80fd\u548c\u4ed6\u76f8 \u5f85\u4e86\u4e2a\u591a\u65f6\u8fb0\uff0c\u4ed6\u8d62\u4e0d\u4e86\u4f60\uff0c\u90a3\u5f53\u7136\u5e94\u8be5\u7b97\u662f\u4f60\u8d62\u4ed6\u4e86\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u5c31\u7b97\u662f\u8d62\uff0c\u4e5f \u8d62\u5f97\u4fa5\u5e78\u4e4b\u6781\uff01\u201d\u674e\u9038\u8bf7\u9053\u5176\u8be6\uff0c\u590f\u4faf\u575a\u9053\uff1a\u201c\u6211\u542c\u5f97\u836f\u7ae5\u8bf4\u662f\u4ed6\u6765\uff0c\u9884\u5148\u670d\u4e0b\u4e86\u534a\u74f6 \u7684\u89e3\u6bd2\u7075\u4e39\uff0c\u518d\u7a7f\u4e86\u4e00\u4ef6\u6781\u8584\u7684\u91d1\u4e1d\u8f6f\u7532\uff0c\u8fd9\u624d\u51fa\u6765\u548c\u4ed6\u8d4c\u8d5b\u3002\u54ea\u77e5\u4ed6\u7684\u6bd2\u638c\u4f24\u5bb3\u4e4b\u5904\uff0c \u7adf\u7136\u8fdc\u8fdc\u51fa\u4e4e\u6211\u7684\u610f\u6599\u4e4b\u5916\uff0c\u6211\u4f53\u5185\u7684\u6bd2\u6c14\uff0c\u51e0\u4e4e\u6536\u655b\u4e0d\u4f4f\uff0c\u540e\u6765\u4ed6\u8fd8\u8981\u548c\u6211\u6bd4\u8bd5\uff0c\u6211 \u4fbf\u5c06\u8ba1\u5c31\u8ba1\uff0c\u60f3\u51fa\u4e86\u90a3\u4e2a\u529e\u6cd5\uff0c\u548c\u4ed6\u6bd4\u62fc\u5185\u529f\uff0c\u4ed6\u7684\u529f\u592b\u975e\u5e38\u9738\u9053\uff0c\u82e5\u7136\u771f\u4e2a\u52a8\u624b\u8fc7\u62db\uff0c \u6211\u63a5\u4e0d\u6ee1\u767e\u62db\uff0c\u4f46\u82e5\u5f7c\u6b64\u67d4\u6597\uff0c\u6211\u7684\u5185\u529f\u5374\u8981\u6bd4\u4ed6\u7a0d\u4e3a\u7cbe\u7eaf\u3002\u6211\u4fbf\u501f\u4ed6\u4ece\u7ef3\u7d22\u4e2d\u4f20\u8fc7\u6765 \u7684\u5185\u5bb6\u771f\u529b\uff0c\u53d1\u6563\u6211\u4f53\u5185\u7684\u6bd2\u6c14\uff0c\u5899\u58c1\u4e0a\u90a3\u56e2\u9ed1\u5370\uff0c\u4fbf\u662f\u8fd9\u6837\u6765\u7684\u3002\u4f46\u4ecd\u7136\u4e0d\u80fd\u53d1\u6563\u51c0 \u5c3d\uff0c\u6240\u4ee5\u5728\u4ed6\u8d70\u4e86\u4e4b\u540e\uff0c\u6211\u4ecd\u987b\u518d\u8fd0\u5185\u529f\uff0c\u5c06\u4f59\u6bd2\u51dd\u805a\u6307\u5c16\uff0c\u8fd9\u624d\u6324\u5f97\u4e8e\u5e72\u51c0\u51c0\u3002\u201d\u957f \u5b59\u58c1\u542c\u5f97\u819b\u76ee\u7ed3\u820c\uff0c\u590f\u4faf\u575a\u5fae\u7b11\u9053\uff1a\u201c\u8fd8\u4e0d\u6b62\u6b64\u5462\uff0c\u4e3a\u4e86\u8fd9\u573a\u6bd4\u8d5b\uff0c\u6211\u4e0d\u4f46\u635f\u4e86\u4e09\u5e74\u529f \u529b\uff0c\u800c\u4e14\u4eca\u540e\u8981\u53d8\u6210\u79c3\u5b50\u4e86\u3002\u201d \u3000\u3000\u5c06\u5e3d\u5b50\u63ed\u5f00\uff0c\u6447\u4e00\u6447\u5934\uff0c\u4f46\u89c1\u6ee1\u5934\u5934\u53d1\uff0c\u5c3d\u90fd\u53d8\u6210\u788e\u672a\uff0c\u968f\u98ce\u98de\u6563\u3002\u674e\u9038\u5185\u529f\u5df2\u6709 \u6839\u5e95\uff0c\u77e5\u9053\u8fd9\u662f\u771f\u6c14\u8017\u635f\u592a\u751a\u6240\u81f3\uff0c\u4e0b\u62dc\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e3a\u4e86\u5c0f\u4f84\u5982\u6b64\u727a\u7272\uff0c\u6d3b\u547d\u4e4b\u6069\uff0c \u6ca1\u9f7f\u4e0d\u5fd8\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u8fd9\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u6211\u8fd9\u51e0\u5341\u5e74\uff0c\u82e6\u4fee\u82e6\u7ec3\uff0c\u672c\u6765\u5c31\u51c6\u5907\u4e86\u8981\u548c \u4ed6\u6bd4\u8bd5\u4e00\u573a\u7684\u3002\u201d\u4ed6\u89c1\u674e\u9038\u8fd9\u6837\u60f6\u6050\u4e0d\u5b89\uff0c\u6709\u4e00\u4ef6\u4e8b\u60c5\u8fd8\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u6765\uff0c\u539f\u6765\u4ed6\u7a7f\u7684 \u90a3\u4ef6\u5b9d\u7532\u4e5f\u7ed9\u5929\u6076\u9053\u4eba\u7684\u638c\u529b\u9707\u88c2\u4e86\u3002 \u3000\u3000\u957f\u5b59\u58c1\u9053\uff1a\u201c\u4e16\u4e0a\u7adf\u6709\u8fd9\u822c\u5389\u5bb3\u7684\u4eba\uff0c\u6211\u4ee5\u524d\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u6b66\u6797 \u4e2d\u6709\u8bdd\u662f\uff1a\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002\u8fd9\u8bdd\u534a\u70b9\u4e0d\u9519\u3002\u5929\u6076\u9053\u4eba\u7684\u6bd2\u638c\u4e3e\u4e16\u65e0\u53cc\uff0c\u82e5\u8bba\u5230\u6b66 \u529f\u4e5f\u8fd8\u672a\u5fc5\u662f\u5929\u4e0b\u7b2c\u4e00\u5462\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u522b\u7684\u4eba\u6211\u4e0d\u6015\uff0c\u6700\u6015\u78b0\u5230\u5929\u6076\u9053\u4eba\u90a3\u4e24\u4e2a\u5f92 \u5f1f\uff0c\u5c24\u5176\u662f\u90a3\u4e2a\u6bd2\u89c2\u97f3\uff0c\u5979\u4f1a\u7b11\u563b\u563b\u7684\u51b7\u4e0d\u9632\u5c31\u7ed9\u4f60\u4e00\u679a\u900f\u7a74\u795e\u9488\u3002\u6211\u7239\u7239\u548c\u6bbf\u4e0b\u5c31\u51e0 \u4e4e\u7ed9\u4ed6\u4eec\u5bb3\u6b7b\u3002\u522b\u7684\u4eba\u6b66\u529f\u6709\u591a\u9ad8\u4e5f\u603b\u6709\u4e2a\u9053\u7406\u597d\u8bb2\uff0c\u8fd9\u4e24\u4e2a\u9b54\u5934\u771f\u662f\u4e0d\u53ef\u7406\u55bb\uff0c\u968f\u65f6 \u90fd\u4f1a\u51fa\u624b\u4f24\u4eba\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u73b0\u5728\u90fd\u548c\u5929\u6076\u9053\u4eba\u7684\u95e8\u4e0b\u7ed3\u4e86\u51a4\u4ec7\uff0c\u4ed6\u4eec\u53c8 \u8ba4\u5f97\u4f60\u4eec\u7684\u76f8\u8c8c\uff0c\u5929\u6076\u9053\u4eba\u5728\u8fd9\u4e09\u4e24\u5e74\u5185\u4e5f\u8bb8\u4e0d\u4f1a\u51fa\u6765\uff0c\u4ed6\u8fd9\u4e24\u4e2a\u5f92\u5f1f\u5374\u6b63\u5728\u6380\u98ce\u63ed\u6d6a\u3002 \u5c06\u6765\u4f60\u4eec\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u786e\u662f\u8981\u5c0f\u5fc3\u63d0\u9632\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u5c31\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u63d0\u9632\uff1f\u201d \u590f\u4faf\u575a\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u5c06\u6765\u4f60\u4eec\u8d70\u65f6\uff0c\u6211\u9001\u4e00\u4e9b\u6613\u5bb9\u4e39\u7ed9\u4f60\u4eec\uff0c\u53ef\u4ee5\u968f\u4f60\u4eec\u7684\u5fc3\u610f\uff0c\u6539 \u53d8\u5bb9\u8c8c\u3002\u201d\u957f\u5b59\u58c1\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u597d\u554a\uff01\u4e0d\u8fc7\u6700\u597d\u73b0\u5728\u7ed9\u6211\uff0c\u6211\u8fd9\u51e0\u5929\u6bcf\u5929\u5047\u626e\u7537\u5b50\uff0c \u5230\u8336\u9986\u53bb\u6253\u542c\u6d88\u606f\uff0c\u60f3\u662f\u626e\u5f97\u4e0d\u50cf\uff0c\u597d\u4e9b\u8336\u5ba2\u90fd\u5728\u76ef\u7740\u6211\u5462\uff01\u201d\u590f\u4faf\u575a\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982 \u6b64\uff0c\u7b49\u4e0b\u6211\u53eb\u836f\u7ae5\u62ff\u6765\uff0c\u5e76\u6559\u4f60\u600e\u6837\u4f7f\u7528\u4fbf\u662f\u3002\u201d\u957f\u5b59\u58c1\u5927\u559c\u62dc\u8c22\uff0c\u539f\u6765\u5979\u77e5\u9053\u590f\u4faf\u575a \u6709\u6b64\u5999\u836f\uff0c\u65e9\u5df2\u6253\u7b97\u95ee\u4ed6\u8981\u4e86\u3002 \u3000\u3000\u590f\u4faf\u575a\u4e34\u8d70\u4e4b\u65f6\u66ff\u674e\u9038\u628a\u4e86\u628a\u8109\uff0c\u8bf4\u9053\uff1a\u201c\u518d\u9759\u517b\u4e00\u5929\uff0c\u660e\u5929\u4f60\u4fbf\u53ef\u4ee5\u5b8c\u5168\u597d\u4e86\u3002 \u55ef\uff0c\u6211\u7b97\u4e00\u7b97\u65e5\u671f\uff0c\u8c37\u795e\u7fc1\u53bb\u63a5\u4f60\u7684\u7239\u7239\uff0c\u660e\u5929\u4e5f\u5e94\u8be5\u56de\u6765\u4e86\u3002\u201d\u540e\u9762\u8fd9\u51e0\u53e5\u8bdd\u4e43\u662f\u5411 \u957f\u5b59\u58c1\u8bf4\u7684\u3002 \u3000\u3000\u590f\u4faf\u575a\u8d70\u540e\uff0c\u957f\u5b59\u58c1\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u6700\u5927\u7684\u5fc3\u613f\u4fbf\u662f\u80fd\u89c1\u5510\u5ba4\u4e2d\u5174\uff0c\u660e \u5929\u4ed6\u82e5\u5230\u6765\u3002\u89c1\u5230\u6bbf\u4e0b\uff0c\u4e00\u5b9a\u6b22\u559c\u5f97\u5f88\u3002\u201d\u674e\u9038\u55a7\u7136\u53f9\u9053\uff1a\u201c\u53ea\u6015\u6211\u62c5\u4e0d\u8d77\u4e2d\u5174\u7684\u62c5\u5b50 \u4e86\u3002\u201d\u957f\u5b59\u58c1\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u662f\u4ed6\u542c\u5230\u5a49\u513f\u7684\u6d88\u606f\uff0c\u5374\u4e0d\u77e5\u600e\u6837\u4f24\u5fc3\u5462\uff01\u201d\u674e\u9038 \u5fc3\u5982\u4e71\u4e1d\uff0c\u9eef\u7136\u65e0\u8bed\u3002\u957f\u5b59\u58c1\u770b\u4ed6\u4e00\u773c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8be5\u5728\u6bbf\u4e0b\u9762\u524d\u63d0\u8d77\u5a49\u513f\u2026\u2026\u201d \u773c\u5708\u4e00\u7ea2\uff0c\u5c06\u4e0b\u9762\u7684\u8bdd\u54bd\u4e86\u56de\u53bb\uff0c\u674e\u9038\u5fc3\u5f26\u98a4\u6296\uff0c\u4e0d\u77e5\u600e\u6837\u7b54\u5979\uff0c\u6070\u597d\u8fd9\u65f6\uff0c\u4e00\u4e2a\u836f\u7ae5 \u5c06\u6613\u5bb9\u4e39\u5e26\u6765\u7ed9\u957f\u5b59\u58c1\uff0c\u89e3\u4e86\u674e\u9038\u7684\u7a98\u3002 \u3000\u3000\u836f\u7ae5\u7ed9\u674e\u9038\u8bb2\u6613\u5bb9\u4e39\u7684\u7528\u6cd5\uff0c\u957f\u5b59\u58c1\u611f\u5230\u65b0\u5947\u6709\u8da3\uff0c\u4e0d\u538c\u6c42\u8be6\u7684\u95ee\u6765\u95ee\u53bb\uff0c\u674e\u9038\u5750 \u5728\u4e00\u8fb9\uff0c\u5982\u6709\u6240\u601d\uff0c\u5e76\u4e0d\u63d2\u8bdd\u3002\u836f\u7ae5\u8d70\u65f6\uff0c\u957f\u5b59\u58c1\u89c1\u674e\u9038\u4f3c\u6709\u507f\u60eb\uff0c\u4fbf\u4ea6\u544a\u8f9e\uff0c\u8d70\u5230\u95e8 \u524d\uff0c\u5ffd\u53c8\u56de\u5934\u7b11\u9053\uff1a\u201c\u4f60\u8be5\u6362\u4e00\u4ef6\u8863\u88f3\u4e86\u3002\u201d\u674e\u9038\u60f3\u8d77\u9002\u624d\u88ab\u5929\u6076\u9053\u4eba\u6293\u88c2\u7684\u8863\u88f3\uff0c\u957f \u5b59\u58c1\u6251\u5230\u4ed6\u7684\u8eab\u4e0a\u6551\u4ed6\uff0c\u4e0d\u89c9\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5173\u5fc3\u3002\u201d\u957f\u5b59\u58c1\u60f3\u8d77\u4e00\u4e8b\uff0c \u8d70\u56de\u6765\u5c06\u4e00\u76d2\u6613\u5bb9\u4e39\u653e\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u7559\u4e00\u76d2\u7ed9\u4f60\uff0c\u4e5f\u8bb8\u8fc7\u4e86\u51e0\u5929\uff0c\u54b1\u4eec\u90fd\u7528\u5f97\u7740\u5b83\u5462\u3002\u201d \u8bf4\u7f62\u5ae3\u7136\u4e00\u7b11\uff0c\u8fd9\u624d\u63ed\u5e18\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u674e\u9038\u8f97\u8f6c\u53cd\u4fa7\uff0c\u65e0\u6cd5\u5b89\u7720\uff0c\u5230\u4e86\u5348\u591c\uff0c\u5ffd\u7136\u62ab\u8863\u800c\u8d77\uff0c\u4f0f\u5728\u6848\u524d\uff0c\u5306\u5306\u5fd9\u5fd9 \u7684\u5199\u4e86\u4e00\u5c01\u4fe1\u3002 \u3000\u3000\u8fd9\u5c01\u4fe1\u662f\u5199\u7ed9\u957f\u5b59\u58c1\u7684\uff0c\u674e\u9038\u60f3\u4e86\u8bb8\u4e45\u3001\u7ec8\u4e8e\u51b3\u5b9a\u4e86\u4e0a\u957f\u5b89\u3002\u662f\u7684\uff0c\u4e0a\u5b98\u5a49\u513f\u505a\u4e86 \u5973\u5b98\u7684\u6d88\u606f\uff0c\u66fe\u7ecf\u4ee4\u4ed6\u4f24\u5fc3\u7edd\u671b\uff0c\u4ed6\u751a\u81f3\u5f53\u4f5c\u4e0a\u5b98\u5a49\u513f\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4eca\u751f\u4eca\u4e16\u518d\u4e5f\u4e0d\u8981\u89c1 \u5979\uff01\u7136\u800c\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\uff0c\u5728\u8fd9\u4f24\u5fc3\u7edd\u671b\u4e4b\u4e2d\uff0c\u8574\u85cf\u7740\u5bf9\u5a49\u513f\u7684\u6df1\u6c89\u7684\u6000\u5ff5\uff01\u4ed6\u6015\u89c1\u5a49\u513f\uff0c \u53c8\u6e34\u60f3\u518d\u89c1\u5a49\u513f\uff0c\u4ed6\u4eec\u8eab\u4e16\u76f8\u540c\uff0c\u6c14\u8d28\u76f8\u4f3c\uff0c\u4e0d\u7ba1\u5a49\u513f\u5982\u4f55\uff0c\u4ed6\u662f\u628a\u5979\u5f53\u4f5c\u5e73\u751f\u552f\u4e00\u7684 \u77e5\u5df1\u7684\uff0c\u6b63\u662f\u7531\u4e8e\u8fd9\u79cd\u77db\u76fe\u7684\u5fc3\u60c5\uff0c\u4ed6\u62fc\u7740\u906d\u53d7\u4efb\u4f55\u5371\u9669\uff0c\u4e5f\u8981\u5230\u957f\u5b89\u53bb\u4e00\u89c1\u5a49\u513f\u3002 \u3000\u3000\u800c\u4fc3\u6210\u4e86\u4ed6\u8fd9\u4e00\u51b3\u5b9a\u7684\u5219\u662f\u957f\u5b59\u58c1\uff0c\u5728\u4ed6\u517b\u75c5\u7684\u671f\u95f4\uff0c\u4ed6\u867d\u7136\u611f\u6fc0\u957f\u5b59\u58c1\u5bf9\u4ed6\u7684\u7ec6 \u5fc3\u7167\u6599\uff0c\u5374\u53ea\u5f53\u4f5c\u662f\u5144\u59b9\u7684\u60c5\u8c0a\uff0c\u8fd8\u672a\u6709\u4ec0\u4e48\u7279\u522b\u7684\u611f\u89c9\uff0c\u4eca\u5929\u5374\u84e6\u7136\u53d1\u73b0\u4e86\u5979\u7684\u60c5\u610f\uff0c \u8fd9\u4ee4\u4ed6\u8ff7\u60af\uff0c\u4e5f\u4ee4\u4ed6\u60f6\u6050\u4e0d\u5b89\uff0c\u4ed6\u4e0d\u80fd\u518d\u803d\u6401\u4e0b\u53bb\u4e86\u3002\u4ed6\u7559\u4fe1\u7ed9\u957f\u5b59\u58c1\uff0c\u8bf7\u5979\u539f\u8c05\u81ea\u5df1 \u7684\u4e0d\u8f9e\u800c\u884c\uff0c\u5e76\u529d\u5979\u4e0d\u8981\u5192\u9669\u4e5f\u53bb\u957f\u5b89\uff0c\u529d\u5979\u7559\u5728\u590f\u4faf\u575a\u5bb6\u4e2d\u966a\u4f34\u5979\u7236\u4eb2\u3002\u7136\u800c\u8fd9\u4e9b\u90fd \u4e0d\u662f\u771f\u6b63\u7684\u7406\u7531\uff0c\u771f\u6b63\u7684\u7406\u7531\u4ed6\u6ca1\u6709\u5199\u51fa\u6765\uff0c\u4ed6\u4e0d\u613f\u4e0e\u957f\u5b59\u58c1\u540c\u884c\uff0c\u5176\u5b9e\u662f\u6015\u81ea\u5df1\u6291\u5236 \u4e0d\u4f4f\u81ea\u5df1\uff0c\u518d\u4e00\u6b21\u60f9\u4e0b\u7231\u60c5\u7684\u70e6\u607c\u3002\u4ed6\u6700\u540e\u8bf7\u5979\u8f6c\u544a\u590f\u4faf\u575a\uff0c\u5e76\u591a\u8c22\u4ed6\u7684\u7167\u6599\u4e4b\u6069\u4e0e\u590f \u4faf\u575a\u7684\u518d\u751f\u4e4b\u5fb7\u3002 \u3000\u3000\u5199\u597d\u4e86\u4fe1\uff0c\u4ece\u7a97\u53e3\u671b\u51fa\u53bb\uff0c\u6708\u4eae\u6b63\u5728\u5929\u5fc3\uff0c\u79cb\u98ce\u5439\u6765\uff0c\u5df2\u5e26\u7740\u4e9b\u4e9b\u5bd2\u610f\uff0c\u6709\u4e24\u7247\u9ec4 \u53f6\u5439\u843d\u5728\u4ed6\u7684\u51e0\u524d\uff0c\u4ed6\u60f3\u8d77\u4e0e\u4e0a\u5b98\u5a49\u513f\u521d\u89c1\u4e4b\u65f6\uff0c\u6b63\u662f\u6625\u82b1\u5982\u9526\u7684\u65f6\u8282\uff0c\u90a3\u65f6\u4ed6\u62b1\u7740\u590d \u56fd\u7684\u96c4\u5fc3\uff0c\u4e5f\u6b63\u50cf\u6625\u5929\u7684\u82b1\u6735\u4e00\u6837\uff0c\u5145\u6ee1\u751f\u6c14\uff0c\u66fe\u51e0\u4f55\u65f6\uff1f\u8f6c\u773c\u95f4\u4fbf\u662f\u79cb\u98ce\u8427\u745f\uff0c\u800c\u4ed6 \u7684\u5fc3\u5883\uff0c\u4e5f\u611f\u5230\u4f3c\u9ec4\u53f6\u4e00\u822c\uff0c\u98d8\u96f6\u65e0\u4f9d\u3002 \u3000\u3000\u4ed6\u6253\u5f00\u90a3\u76d2\u6613\u5bb9\u4e39\uff0c\u9009\u4e86\u4e00\u79cd\u53ef\u4ee5\u4ee4\u9762\u8272\u7070\u6697\u7684\u643d\u4e0a\u53bb\uff0c\u6253\u626e\u4e4b\u540e\u5728\u94dc\u955c\u524d\u4e00\u7167\uff0c \u4f46\u89c1\u81ea\u5df1\u597d\u50cf\u5e73\u767d\u8001\u4e86\u4e8c\u5341\u5e74\uff0c\u989d\u4e0a\u6dfb\u4e86\u51e0\u9053\u76b1\u7eb9\uff0c\u5934\u53d1\u4e5f\u6709\u51e0\u6839\u6591\u767d\uff0c\u4ed6\u6362\u4e86\u4e00\u4ef6\u84dd \u8272\u7684\u957f\u886b\uff0c\u8bd5\u5455\u6402\u7740\u80cc\uff0c\u8e31\u4e86\u51e0\u4e0b\u65b9\u6b65\uff0c\u4ece\u955c\u4e2d\u770b\u5230\u7684\u81ea\u5df1\uff0c\u6d3b\u50cf\u4e00\u4e2a\u79d1\u573a\u5931\u610f\u7684\u8001\u5112 \u751f\uff0c\u51e0\u4e4e\u8fde\u81ea\u5df1\u4e5f\u4e0d\u8ba4\u8bc6\u81ea\u5df1\u4e86\u3002\u674e\u9038\u5fc3\u9053\uff1a\u201c\u8fd9\u6837\u6b63\u597d\uff0c\u5373\u7b97\u6df7\u5728\u957f\u5b89\u95f9\u5e02\u4e4b\u4e2d\uff0c\u4e5f \u7edd\u4e0d\u4f1a\u88ab\u4eba\u8bc6\u7834\u6211\u7684\u672c\u6765\u9762\u76ee\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u8f7b\u6253\u5f00\u623f\u95e8\uff0c\u643a\u4e86\u53e4\u7434\u5b9d\u5251\uff0c\u6084\u6084\u51fa\u8d70\uff0c\u957f\u5b59\u58c1\u4f4f\u5728\u82b1\u56ed\u4e1c\u89d2\u7684\u90a3\u5ea7\u5c0f\u623f\uff0c\u4ed6 \u7ecf\u8fc7\u4e4b\u65f6\uff0c\u4fbf\u628a\u90a3\u5c01\u4fe1\u4ece\u7a97\u53e3\u8f7b\u8f7b\u9001\u8fdb\u53bb\u3002\u957f\u5b59\u58c1\u6b63\u5728\u68a6\u4e2d\u548c\u674e\u9038\u5230\u4e86\u957f\u5b89\uff0c\u89c1\u7740\u4e86\u4e0a \u5b98\u5a49\u513f\uff0c\u957f\u5b59\u58c1\u529d\u4e0d\u8f6c\u5a49\u513f\uff0c\u6b63\u5728\u68a6\u4e2d\u53d1\u51fa\u4e00\u58f0\u8f7b\u5fae\u7684\u53f9\u606f\uff01\u674e\u9038\u53ef\u5e76\u4e0d\u77e5\u957f\u5b59\u58c1\u5728\u53d1 \u68a6\uff0c\u542c\u5230\u90a3\u58f0\u53f9\u606f\uff0c\u5446\u4e86\u4e00\u4f1a\uff0c\u7ec8\u4e8e\u4e0d\u6562\u56de\u5934\uff01\u4fbf\u8d70\u51fa\u4e86\u56ed\u5b50\u3002 \u3000\u3000\u4ed6\u4ece\u90a3\u6761\u5c0f\u8def\u8d70\u4e0b\u5c71\u53bb\uff0c\u6b66\u7384\u971c\u90a3\u5929\u6b63\u662f\u4ece\u8fd9\u6761\u8def\u4e0a\u9001\u4ed6\u6765\u7684\uff0c\u677e\u98ce\u63a0\u8fc7\uff0c\u4f9d\u7a00\u8fd8 \u4f3c\u542c\u5f97\u90a3\u8f66\u8f6e\u7684\u9570\u952e\u4e4b\u58f0\u3002\u674e\u9038\u60c5\u601d\u4f97\u4f97\uff0c\u5fc3\u4e8b\u5982\u6f6e\uff0c\u75be\u8dd1\u4e0b\u5c71\uff0c\u4e0d\u89c9\u4e1c\u65b9\u5df2\u767d\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u5047\u4f5c\u771f\u6765\u771f\u4f5c\u5047 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u5341\u4e00\u56de\u3000\u5047\u4f5c\u771f\u6765\u771f\u4f5c\u5047 \u3000\u3000\u5728\u79cb\u98ce\u8427\u745f\u4e4b\u4e2d\uff0c\u674e\u9038\u7ecf\u8fc7\u4e86\u5d0e\u5c96\u7684\u8700\u9053\uff0c\u7ffb\u8fc7\u4e86\u5ddd\u9655\u4ea4\u754c\u7684\u9ad8\u5c71\uff0c\u8fd9\u4e00\u65e5\u6765\u5230\u4e86 \u9102\u53bf\uff0c\u8ddd\u79bb\u957f\u5b89\uff0c\u4e0d\u8fc7\u662f\u4e09\u56db\u65e5\u7684\u8def\u7a0b\u4e86\u3002\u674e\u9038\u5fc3\u6000\u6545\u56fd\uff0c\u897f\u671b\u957f\u5b89\uff0c\u4e0d\u80dc\u611f\u6168\u3002\u8fd9\u6761 \u8def\u56e0\u4e3a\u662f\u901a\u5f80\u957f\u5b89\u7684\u9a7f\u9053\uff0c\u8def\u65c1\u7684\u9152\u8086\u751a\u591a\uff0c\u8d70\u5230\u4e2d\u5348\u65f6\u5206\uff0c\u674e\u9038\u611f\u5230\u6709\u70b9\u9965\u6e34\uff0c\u4fbf\u505c \u4e0b\u9a6c\u6765\uff0c\u8d70\u8fdb\u9152\u8086\uff0c\u8981\u4e86\u534a\u65a4\u5364\u725b\u8089\u548c\u9152\u3002 \u3000\u3000\u90a3\u9152\u8086\u4e3b\u4eba\u5e76\u4e0d\u56e0\u4ed6\u8863\u670d\u5bd2\u9178\u800c\u6709\u6240\u6b67\u89c6\uff0c\u8fd9\u65f6\u9152\u8086\u4e2d\u53ea\u6709\u4ed6\u4e00\u4e2a\u5ba2\u4eba\uff0c\u90a3\u9152\u8086\u4e3b \u4eba\u548c\u4ed6\u642d\u8baa\uff0c\u95fb\u5f97\u4ed6\u5f80\u957f\u5b89\uff0c\u4fbf\u5373\u7b11\u9053\uff1a\u201c\u8001\u5148\u751f\u6562\u60c5\u662f\u4e0a\u957f\u5b89\u6c42\u5b98\u4e48\uff1f\u201d\u674e\u9038\u7b11\u9053\uff1a \u201c\u6211\u5931\u610f\u79d1\u573a\uff0c\u5e74\u5e74\u843d\u7b2c\uff0c\u4eca\u751f\u662f\u6ca1\u6709\u798f\u4efd\u505a\u5b98\u7684\u3002\u201d\u90a3\u5e97\u4e3b\u4eba\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u8bdd\u4e0d\u662f\u8fd9 \u4e48\u8bb2\u6cd5\uff0c\u5468\u516c\u516b\u5341\uff0c\u5c1a\u9047\u6587\u738b\uff0c\u4e00\u65f6\u56f0\u987f\uff0c\u7b97\u5f97\u4e86\u4ec0\u4e48\u3002\u201d\u674e\u9038\u53c8\u7b11\u9053\uff1a\u201c\u4e16\u65e0\u6587\u738b\uff0c \u6211\u4e5f\u4e0d\u662f\u5468\u516c\uff0c\u6211\u6b64\u53bb\u957f\u5b89\uff0c\u4f46\u80fd\u56fe\u4e2a\u6e29\u9971\uff0c\u5df2\u662f\u5fc3\u6ee1\u610f\u8db3\u3002\u201d\u90a3\u5e97\u4e3b\u4eba\u5374\u6b63\u8272\u8bf4\u9053\uff1a \u201c\u6211\u542c\u6751\u5b50\u91cc\u7684\u4e00\u4e9b\u8bfb\u4e66\u4eba\u8bf4\uff0c\u5f53\u4eca\u7687\u5e1d\uff0c\u867d\u7136\u662f\u4e2a\u4e09\u622a\u68b3\u5934\uff0c\u4e24\u622a\u7a7f\u8863\u7684\u5973\u4eba\uff0c\u5374\u8fd8 \u5f88\u80fd\u591f\u7528\u4eba\u5462\u3002\u4e0d\u8fc7\u4f60\u8001\u65e0\u5fc3\u6c42\u5b98\u7f62\u4e86\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u957f\u5b89\u6bd4\u4ee5\u524d\u66f4\u70ed\u95f9\u4e86\uff0c \u4f60\u8001\u7eb5\u975e\u6c42\u5b98\uff0c\u6c42\u4e8b\u4e5f\u5b9a\u80fd\u5982\u5fc3\u6240\u613f\u3002\u201d\u674e\u9038\u60f3\u8d77\u4ee5\u524d\u4e13\u81ea\u5df1\u5728\u6b66\u7384\u971c\u9762\u524d\u5f39\u594f\u8bd7\u7ecf\u4e2d \u90a3\u7bc7\u300a\u9ecd\u79bb\u300b\uff0c\u5f53\u65f6\u6b66\u7384\u971c\u5c31\u66fe\u53d6\u7b11\u8fc7\u4ed6\uff0c\u8bf4\u662f\u8981\u5e26\u4ed6\u5230\u957f\u5b89\u53bb\u770b\u770b\u201c\u9ea6\u7530\u201d\uff0c\u770b\u770b\u957f \u5b89\u7a76\u7adf\u662f\u4e0d\u662f\u50cf\u4ed6\u60f3\u50cf\u4e2d\u90a3\u6837\u8352\u829c\uff0c\u5982\u4eca\u4ed6\u542c\u5f97\u8fd9\u9152\u5f8b\u4e3b\u4eba\u5927\u8c08\u957f\u5b89\u7684\u7e41\u534e\u70ed\u95f9\uff0c\u89e6\u52a8 \u524d\u60c5\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u59cb\u5f3a\u989c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u8d35\u8a00\u3002\u201d\u5fc3\u60c5\u6005\u6005\uff0c\u62ff\u7740\u534a\u676f\u9152\u9eef\u7136\u65e0\u8bed\uff0c \u53ea\u987e\u501a\u680f\u770b\u5c71\u3002 \u3000\u3000\u90a3\u9152\u8086\u4e3b\u4eba\u89c1\u4ed6\u4f3c\u662f\u5fc3\u60c5\u4e0d\u5c5e\uff0c\u501a\u680f\u770b\u5c71\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8001\u5148\u751f\u82e5\u662f\u6709\u5174\u81f4\u7684\u8bdd\uff0c \u5012\u53ef\u4ee5\u4e0a\u5c71\u4e00\u6e38\uff0c\u770b\u770b\u53e4\u8ff9\u3002\u201d\u674e\u9038\u95ee\u9053\uff1a\u201c\u8fd9\u5ea7\u5c71\u6709\u4ec0\u4e48\u53e4\u8ff9\uff1f\u201d\u9152\u8086\u4e3b\u4eba\u9053\uff1a\u201c\u8fd9 \u5ea7\u5c71\u4fbf\u662f\u90a3\u6709\u540d\u7684\u9996\u9633\u5c71\u4e86\uff0c\u5728\u524d\u51e0\u5e74\uff0c\u5e38\u5e38\u6709\u6e38\u4eba\u4e0a\u5c71\u53bb\u89c5\u4f2f\u5937\u53d4\u9f50\u91c7\u85e4\u7684\u53e4\u8ff9\u5462\uff0c \u8fd9\u4e00\u4e24\u5e74\u624d\u5c11\u4e86\u3002\u201d\u4f2f\u5937\u53d4\u9f50\u76f8\u4f20\u662f\u6bb7\u672b\u5468\u521d\u7684\u4e24\u4f4d\u9690\u58eb\uff0c\u5468\u6b66\u738b\u4e3e\u5175\u4f10\u5546\uff0c\u4f2f\u5937\u53d4\u9f50 \u66fe\u62e6\u8fc7\u4ed6\u7684\u9a6c\u5934\u529d\u8c0f\u3002\u540e\u6765\u5546\u4ea1\u4e4b\u540e\uff0c\u8fd9\u4e24\u5144\u8363\u803b\u98df\u5468\u7c9f\uff0c\u5728\u9996\u9633\u5c71\u4e2d\u9690\u5c45\uff0c\u91c7\u853d\u800c\u98df\uff0c \u7ec8\u4e8e\u997f\u6b7b\u3002\u674e\u9038\u542c\u5f97\u9152\u8086\u4e3b\u4eba\u8c08\u8d77\u8fd9\u4e2a\u6545\u4e8b\uff0c\u66f4\u89c9\u9eef\u7136\u795e\u4f24\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5f53\u4eca\u4e4b\u4e16\uff0c \u50cf\u4f2f\u5937\u53d4\u9f50\u8fd9\u6837\u7684\u4eba\u65e9\u5df2\u6ca1\u6709\u4e86\u3002\u602a\u4e0d\u5f97\u636e\u4ed6\u6240\u8a00\uff0c\u8fd9\u4e00\u4e24\u5e74\uff0c\u8fde\u6e38\u5ba2\u4e5f\u51e0\u4e4e\u7edd\u8ff9\u4e86\u3002\u201d \u5bf9\u90a3\u9152\u8086\u4e3b\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u5012\u60f3\u4e0a\u5c71\u4e00\u6e38\uff0c\u53ef\u60dc\u962e\u56ca\u7f9e\u6da9\uff0c\u8981\u8d76\u5f80\u957f\u5b89\u8c0b\u4e8b\uff0c\u6ca1\u6b64\u95f2\u60c5\u9038 \u81f4\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53c8\u6765\u4e86\u4e00\u4e2a\u5ba2\u4eba\uff0c\u8fd9\u4eba\u662f\u4e2a\u5e74\u9752\u7684\u6b66\u58eb\uff0c\u674e\u9038\u4e00\u89c1\uff0c\u4e0d\u89c9\u6014\u4e86\u4e00\u6014\uff0c\u8fd9 \u4eba\u7684\u76f8\u8c8c\u597d\u719f\uff0c\u4f3c\u662f\u5728\u90a3\u513f\u89c1\u8fc7\u7684\uff0c\u4ed4\u7ec6\u60f3\u4e86\u4e00\u60f3\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u539f\u6765\u8fd9\u4e2a\u4eba\u7684\u8eab\u6750 \u548c\u674e\u9038\u5dee\u4e0d\u591a\uff0c\u76f8\u8c8c\u4e5f\u6709\u70b9\u76f8\u4f3c\uff0c\u6240\u4ee5\u674e\u9038\u4e00\u89c1\u4e4b\u4e0b\uff0c\u89c9\u5f97\u597d\u719f\u3002\u8fd9\u4eba\u8863\u670d\u5149\u9c9c\uff0c\u5750\u7684 \u4e5f\u662f\u4e00\u9a91\u9a8f\u9a6c\uff0c\u9762\u4e0a\u5374\u5e26\u7740\u75c5\u5bb9\uff0c\u770b\u6765\u8981\u6bd4\u674e\u9038\u7626\u524a\u4e00\u4e9b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u6b66\u571f\u8d70\u8fdb\u9152\u8086\uff0c\u5429\u5490\u9152\u4fdd\u9053\uff1a\u201c\u6253\u4e09\u65a4\u767d\u9152\uff0c\u5207\u4e24\u65a4\u725b\u8089\u6765\u3002\u201d\u542c\u4ed6\u8bf4\u8bdd\uff0c \u58f0\u97f3\u54cd\u4eae\uff0c\u4e2d\u6c14\u5145\u6c9b\uff0c\u4e0d\u50cf\u662f\u6709\u75c5\u7684\u6837\u5b50\u3002\u674e\u9038\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u6b66\u529f\u5e95\u5b50\u4e0d\u9519\uff0c\u4ed6\u90a3\u7126 \u9ec4\u7684\u8138\u8272\uff0c\u60f3\u5fc5\u662f\u751f\u6765\u5982\u6b64\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6b66\u58eb\u610f\u6001\u751a\u8c6a\uff0c\u559d\u4e86\u4e00\u5927\u76c5\u9152\uff0c\u773c\u5149\u5411\u674e\u9038\u8fd9\u9762\u98d8\u6765\uff0c\u90a3\u9152\u8086\u4e3b\u4eba\u9053\uff1a\u201c\u76f8 \u516c\u662f\u5230\u957f\u5b89\u53bb\u7684\u5417\uff1f\u201d\u90a3\u5c11\u5e74\u6b66\u58eb\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u9152\u8086\u4e3b\u4eba\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5148\u751f\u4e5f \u662f\u5230\u957f\u5b89\u7684\uff0c\u4f60\u4eec\u6b63\u597d\u540c\u8def\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6b66\u58eb\u77a7\u4e86\u674e\u9038\u4e00\u773c\uff0c\u62f1\u624b\u95ee\u9053\uff1a\u201c\u8001\u5148\u751f\u9ad8\u59d3\u5927\u540d\u3002\u201d\u5b6a\u9038\u968f\u4fbf\u634f\u4e86\u4e00\u4e2a\u5047 \u540d\u8bf4\u4e86\uff0c\u90a3\u5c11\u5e74\u6b66\u58eb\u8bf4\u9053\uff1a\u201c\u5f1f\u59d3\u5f20\uff0c\u8d31\u53f7\u4e4b\u5947\uff0c\u5ddd\u897f\u5d4b\u5c71\u4eba\u6c0f\u3002\u6562\u95ee\u8001\u5148\u751f\u53ef\u662f\u53d7\u4e86 \u671d\u5ef7\u7684\u5f81\u8058\u5165\u4eac\u7684\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4ec0\u4e48\u5f81\u8058\uff1f\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u5f53\u4eca\u7684\u5973\u7687\u5e1d\u8bcf\u4ee4\u5929\u4e0b \u5404\u5dde\u53bf\u4fdd\u8350\u8d24\u826f\u65b9\u6b63\u4e4b\u58eb\uff0c\u5947\u6750\u5f02\u80fd\u4e4b\u4eba\u5165\u4eac\u5019\u9009\uff0c\u8001\u5148\u751f\u5c1a\u672a\u77e5\u9053\u4e48\uff1f\u201d\u674e\u9038\u7b11\u9053\uff1a \u201c\u6211\u8eab\u65e0\u4e00\u6280\u4e4b\u957f\uff0c\u54ea\u4f1a\u5f81\u8058\u5230\u6211\uff1f\u6211\u662f\u4e0a\u957f\u5b89\u8c0b\u4e8b\uff0c\u60f3\u6df7\u4e00\u53e3\u996d\u5403\u7684\u3002\u5f20\u5144\u662f\u53d7\u5f81\u8058 \u5165\u4eac\u7684\u4e48\uff1f\u201d \u3000\u3000\u5f20\u4e4b\u5947\u54c8\u54c8\u4e00\u7b11\uff0c\u610f\u6001\u98de\u626c\uff0c\u4e0d\u76f4\u63a5\u7b54\u590d\u674e\u9038\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5374\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8fc7\u5230\u957f \u5b89\u78b0\u78b0\u8fd0\u6c14\u7f62\u4e86\u3002\u5f90\u656c\u4e1a\u5df2\u5728\u626c\u5dde\u4e3e\u5175\u9020\u53cd\uff0c\u6211\u82e5\u7136\u4fa5\u5e78\u5f97\u4e2a\u519b\u529f\uff0c\u4e5f\u597d\u535a\u4e2a\u5c01\u59bb\u836b\u5b50\u3002\u201d \u674e\u9038\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5f20\u5144\u610f\u6b32\u6295\u519b\u53bb\u7684\uff0c\u80f8\u6000\u5927\u5fd7\uff0c\u53ef\u4f69\uff0c\u53ef\u4f69\uff01\u201d\u8bed\u5e26\u8ba5\u8bbd\uff0c\u5f20\u4e4b\u5947 \u5374\u4f3c\u8fd8\u542c\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u674e\u9038\u4e00\u8def\u4e0a\uff0c\u90fd\u542c\u5f97\u6709\u4eba\u8c08\u8bba\u5f90\u656c\u4e1a\u8c0b\u53cd\u7684\u4e8b\uff0c\u8bf4\u6cd5\u7eb7\u7eb7\uff0c\u6218\u60c5\u5b9e\u51b5\u4e0d\u77e5\u5982\u4f55\uff0c\u4fbf \u95ee\u90a3\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u542c\u8bf4\u90a3\u82f1\u56fd\u516c\u5f90\u656c\u4e1a\u4e43\u524d\u671d\u8001\u5c06\uff0c\u5584\u4e8e\u7528\u5175\uff0c\u671d\u5ef7\u5982\u4eca\u8981\u52df\u4eba\u4ece\u519b\uff0c \u662f\u4e0d\u662f\u524d\u65b9\u5df2\u5403\u7d27\u4e86\uff1f\u201d\u5f20\u4e4b\u5947\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f90\u656c\u4e1a\u5175\u5fae\u5c06\u5be1\uff0c\u90a3\u80fd\u6210\u5f97\u5927\u4e8b\uff0c\u542c\u8bf4\u5929 \u540e\u5df2\u6d3e\u4e86\u674e\u5b5d\u9038\u5c06\u519b\u4e3a\u626c\u5dde\u5927\u603b\u7763\uff0c\u9886\u5175\u4e09\u5341\u4e07\u5357\u4e0b\uff1b\u53c8\u6d3e\u4e86\u5de6\u9e70\u626c\u5927\u5c06\u519b\u9ed1\u9f7f\u65e0\u5e38\u4e3a \u6c5f\u5357\u9053\u5927\u603b\u7763\uff0c\u5c6f\u5175\u6c5f\u6dee\uff1b\u53e6\u5916\u53c8\u5c06\u7a0b\u52a1\u633a\u5927\u5c06\u519b\u7531\u5355\u4e8e\u9053\u8c03\u56de\uff0c\u9886\u5175\u5341\u4e07\uff0c\u517c\u7a0b\u5357\u4e0b\u3002 \u4e09\u8def\u5939\u653b\u3002\u5f90\u656c\u4e1a\u6709\u7fc5\u96be\u98de\uff01\u671d\u5ef7\u52df\u519b\uff0c\u542c\u8bf4\u662f\u8981\u62b5\u5fa1\u7a81\u53a5\u7684\u8fdb\u72af\uff0c\u5e76\u975e\u5168\u4e3a\u4e86\u5f90\u656c\u4e1a \u5462\u3002\u201d\u674e\u9038\u662f\u5510\u9ad8\u7956\uff08\u674e\u6e0a\uff09\u7684\u66fe\u5b59\uff0c\u674e\u5b5d\u9038\u7684\u5802\u5144\uff0c\u674e\u9038\u542c\u8bf4\u4ed6\u7adf\u7136\u505a\u4e86\u8ba8\u5f90\u656c\u4e1a\u7684 \u4e3b\u5e05\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u4f24\u5fc3\u3002 \u3000\u3000\u4e24\u4eba\u8bdd\u4e0d\u6295\u673a\uff0c\u674e\u9038\u7684\u51b7\u6de1\u795e\u60c5\u4e0d\u77e5\u4e0d\u89c9\u4ece\u9762\u4e0a\u8868\u9732\u51fa\u6765\u3002\u5f20\u4e4b\u5947\u81ea\u89c9\u65e0\u5473\uff0c\u559d\u5b8c \u4e86\u9152\uff0c\u4e0d\u60f3\u4e0e\u674e\u9038\u540c\u884c\uff0c\u4fbf\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u5fd9\u7740\u8d76\u8def\uff0c\u8bf7\u6055\u6211\u5148\u8d70\u4e00\u6b65\uff0c\u82e5\u662f\u6709\u7f18\uff0c \u957f\u5b89\u518d\u89c1\u3002\u201d \u3000\u3000\u5f20\u4e4b\u5947\u4e00\u8d70\uff0c\u674e\u9038\u4fbf\u5373\u7ed3\u4e86\u9152\u8d26\uff0c\u8de8\u9a6c\u767b\u7a37\u3002\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u524d\u9762\u201c\u545c\uff0c\u545c\uff01\u201d \u7684\u54cd\u7bad\u58f0\uff0c\u674e\u9038\u6025\u5fd9\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u8fd9\u6761\u9a7f\u9053\u4ece\u5d07\u5c71\u5cfb\u5cad\u4e4b\u4e2d\u7a7f\u8fc7\uff0c\u8fd9\u65f6\u6b63\u5230\u4e86\u9669\u5cfb\u7684\u5730\u65b9\uff0c \u6709\u5c71\u62d7\u9694\u7740\uff0c\u770b\u4e0d\u89c1\u524d\u9762\u7684\u60c5\u666f \u3000\u3000\u674e\u9038\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u7acb\u5373\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u8dd1\u4e0a\u5c71\u4e0a\uff0c\u5c71\u4e2d\u8305\u8349\u6ca1\u6f06\uff0c\u602a\u77f3\u5cfb\u5d0e\uff0c\u674e\u9038\u8dc3 \u4e0a\u4e00\u5757\u5de8\u77f3\uff0c\u501f\u7740\u77f3\u7b52\u906e\u853d\u8eab\u5b50\uff0c\u5c45\u9ad8\u4fef\u4e0b\uff0c\u671b\u5c06\u4e0b\u53bb\uff0c\u53ea\u89c1\u90a3\u4e2a\u5f20\u4e4b\u5947\u6b63\u81ea\u7b56\u9a6c\u8f6c\u51fa \u5c71\u62d7\uff0c\u5c71\u8def\u7684\u90a3\u8fb9\u8fce\u9762\u5954\u6765\u4e86\u5341\u51e0\u9a91\u5feb\u9a6c\uff0c\u521a\u624d\u7684\u54cd\u7bad\u4fbf\u662f\u8fd9\u73ed\u5f3a\u76d7\u53d1\u51fa\u6765\u7684\u3002\u674e\u9038\u5fc3 \u9053\uff1a\u201c\u8fd9\u5012\u5947\u4e86\uff0c\u5f20\u4e4b\u5947\u8eab\u4e0a\u6709\u4ec0\u4e48\u6cb9\u6c34\uff0c\u503c\u5f97\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\u5174\u5e08\u52a8\u4f17\uff1f\u201d \u3000\u3000\u5f20\u4e4b\u5947\u52d2\u4f4f\u9a6c\u5934\uff0c\u8f6c\u773c\u95f4\u90a3\u4f19\u4eba\u5df2\u5230\u4e86\u4ed6\u7684\u9762\u524d\u3002\u5f20\u4e4b\u5947\u5927\u6012\u559d\u9053\uff1a\u201c\u6e05\u5e73\u4e16\u754c\uff0c \u6d69\u8361\u4e7e\u5764\uff0c\u4f60\u4eec\u7adf\u6562\u62e6\u9014\u62a2\u52ab\u4e48\uff1f\u201d\u4e3a\u9996\u7684\u90a3\u4e24\u4e2a\u6c49\u5b50\u8df3\u4e0b\u9a6c\u80cc\uff0c\u606d\u606d\u656c\u656c\u7684\u8bf4\u9053\uff1a \u201c\u516c\u5b50\u606f\u6012\uff0c\u6211\u4eec\u4e0d\u662f\u5f3a\u76d7\u3002\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u4e0d\u662f\u5f3a\u76d7\uff0c\u4f55\u6545\u62e6\u7740\u6211\u7684\u53bb\u8def\u3002\u201d\u90a3\u4e24\u4e2a \u6c49\u5b50\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u6211\u5bb6\u4e3b\u4eba\u6709\u8bf7\u3002\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u4f60\u5bb6\u4e3b\u4eba\u662f\u8c01\uff1f\u201d\u90a3\u4e24\u4e2a\u6c49\u5b50\u5bf9\u671b\u4e00 \u773c\uff0c\u597d\u4f3c\u6709\u70b9\u8be7\u610f\uff0c\u5de6\u624b\u7684\u90a3\u4e2a\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u5ce8\u5d4b\u91d1\u9876\u4e4b\u4f1a\uff0c\u516c\u5b50\u5fd8\u8bb0\u4e86\u4e48\uff1f\u6211\u662f\u7a0b\u901a \u5440\uff01\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u8bc6\u4f60\u5440\uff01\u4f60\u8ba4\u9519\u4eba\u5566\uff01\u201d\u7a0b\u901a\u5c34\u5c2c\u4e4b\u6781\uff0c\u53f3\u624b\u7684\u90a3\u4e2a\u6c49\u5b50\u53eb \u9053\uff1a\u201c\u5ce8\u5d4b\u4e4b\u4f1a\uff0c\u4eba\u6570\u4f17\u591a\uff0c\u516c\u5b50\u8bb0\u4e0d\u8d77\u6765\uff0c\u4e5f\u662f\u6709\u7684\u3002\u89c1\u4e86\u6211\u5bb6\u4e3b\u4eba\uff0c\u81ea\u7136\u660e\u767d\u3002\u201d \u5f20\u4e4b\u5947\u9053\uff1a\u201c\u4ec0\u4e48\u5ce8\u5d4b\u4e4b\u4f1a\uff1f\u9752\u5929\u767d\u65e5\uff0c\u778e\u8bf4\u4e00\u901a\uff0c\u4f60\u5bb6\u5f20\u5927\u7237\u53ef\u8fd8\u8981\u8d76\u8def\u3002\u201d\u53f3\u8fb9\u90a3 \u4e2a\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\uff0c\u4f60\u4e0d\u662f\u674e\u3001\u674e\u516c\u5b50\u5417\uff1f\u201d\u7a0b\u901a\u9053\uff1a\u201c\u771f\u4eba\u4e0d\u9732\u76f8\uff0c\u9732\u76f8\u4e0d\u771f\u4eba\u3002 \u597d\uff0c\u5c31\u7b97\u4f60\u59d3\u5f20\u5427\uff0c\u5f20\u5927\u7237\uff0c\u6211\u5bb6\u4e3b\u4eba\u6709\u8bf7\uff01\u201d\u5f20\u4e4b\u5947\u6012\u9053\uff1a\u201c\u4ec0\u4e48\u7b97\u6211\u59d3\u5f20\uff1f\u6211\u660e\u660e \u59d3\u5f20\uff0c\u4f60\u518d\u7ea0\u7f20\uff0c\u5403\u6211\u4e00\u97ad\uff01\u201d \u3000\u3000\u674e\u9038\u542c\u5230\u8fd9\u91cc\uff0c\u604d\u7136\u5927\u609f\uff0c\u6562\u60c5\u662f\u8fd9\u4e24\u4e2a\u4eba\u5c06\u5f20\u4e4b\u5947\u5f53\u4f5c\u4ed6\u4e86\u3002\u4e00\u60f3\u5ce8\u5d4b\u4e4b\u4f1a\uff0c\u679c \u7136\u6709\u7a0b\u901a\u8fd9\u4e2a\u4eba\uff0c\u5f53\u65f6\u8ddf\u5728\u90a3\u4e2a\u9f99\u4e09\u5148\u751f\u7684\u540e\u9762\uff0c\u62a2\u7740\u6324\u5230\u4ed6\u7684\u9762\u524d\uff0c\u5411\u4ed6\u901a\u8fc7\u540d\u59d3\u7684\u3002 \u518d\u4e00\u770b\u5176\u4ed6\u7684\u4eba\uff0c\u6709\u51e0\u4e2a\u4e5f\u6709\u70b9\u9762\u719f\u3002\u6562\u60c5\u4ed6\u521a\u624d\u548c\u5f20\u4e4b\u5947\u5728\u9152\u8086\u559d\u9152\u4e4b\u65f6\uff0c\u4e54\u88c5\u6253\u626e \u7684\u9152\u5ba2\u4e2d\u5c31\u6709\u8fd9\u51e0\u4e2a\u4eba\u5728\u3002\u674e\u9038\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0c\u4ed6\u4eec\u65e9\u5df2\u5728\u65c1\u7aa5\u4f3a\u6211\u4e86\u3002\u6211\u73b0 \u5728\u626e\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u4ed6\u4eec\u5f53\u7136\u8ba4\u4e0d\u5f97\u6211\u3002\u53ef\u662f\u5f20\u4e4b\u5947\u4e0e\u6211\u7684\u672c\u6765\u9762\u76ee\uff0c\u867d\u7136\u6709\u70b9\u76f8\u4f3c\uff0c\u4ea6 \u5e76\u975e\u5f88\u76f8\u4f3c\u5440\uff0c\u4ed6\u90a3\u4ed8\u7126\u9ec4\u7684\u8138\u8272\uff0c\u5c31\u4e0e\u6211\u5927\u5927\u4e0d\u540c\uff0c\u7a0b\u901a\u6ca1\u7406\u7531\u5206\u4e0d\u51fa\u6765\uff0c\u4ed6\u4eec\u7684\u4e3b \u4eba\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u674e\u9038\u8fd9\u4e2a\u7591\u95ee\uff0c\u5f20\u4e4b\u5947\u5df2\u66ff\u4ed6\u8bf4\u4e86\u3002\u90a3\u4e24\u4e2a\u6c49\u5b50\u89c1\u5f20\u4e4b\u5947\u53d1\u6012\uff0c\u4ed6\u4eec\u7684\u8138\u8272\u4e5f\u6ca1\u6709 \u521a\u624d\u90a3\u4e48\u606d\u987a\u4e86\u3002\u53f3\u624b\u7684\u90a3\u4e2a\u6c49\u5b50\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u5b81\u613f\u6371\u4f60\u4e24\u97ad\uff0c\u4e5f\u8981\u5c06\u4f60\u8bf7\u5230\u3002\u6211\u5bb6 \u4e3b\u4eba\u5429\u5490\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u603b\u5f97\u7559\u4f4f\u4f60\u7684\u5927\u9a7e\uff01\u201d\u5f20\u4e4b\u5947\u6c14\u5f80\u4e0a\u51b2\uff0c\u4e00\u97ad\u5237\u4e0b\uff0c\u65a5\u9053\uff1a\u201c\u4f60 \u5bb6\u7684\u4e3b\u4eba\u662f\u5f53\u4eca\u7687\u4e0a\u4e48\uff1f\u6709\u8fd9\u4e48\u9738\u9053\uff01\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u7a0b\u901a\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6625\u96f7\u52a8\u5730\uff01\u201d \u53f3\u8fb9\u90a3\u4e2a\u53cc\u5b50\u6309\u7740\u8bf4\u9053\uff1a\u201c\u98de\u9f99\u5728\u5929\uff01\u201d\u5f20\u4e4b\u5947\u83ab\u540d\u5176\u5999\uff0c\u65a5\u9053\uff1a\u201c\u8c01\u7ba1\u4f60\u4ec0\u4e48\u6625\u96f7\u98de \u9f99\uff0c\u5feb\u5feb\u6eda\u5f00\uff01\u201d\u674e\u9038\u542c\u4e86\uff0c\u5374\u53c8\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u8fd9\u516b\u4e2a\u5b57\u4e43\u662f\u674e\u9038\u548c\u51e0\u4e2a\u4eba\u4e4b\u95f4\u76f8\u7ea6\u5b9a\u7684\u201c\u5207\u53e3\u201d\uff0c\u674e\u9038\u56e0\u4e3a\u8981\u63a8\u7ffb\u6b66\u5219\u5929\u7687 \u5e1d\uff0c\u593a\u56de\u5510\u5ba4\u6c5f\u5c71\uff0c\u548c\u671d\u91ce\u95f4\u51e0\u4e2a\u638c\u6709\u6743\u52bf\u7684\u4eba\u7269\u5bc6\u8c0b\u8d77\u6765\uff0c\u8fd9\u51e0\u4eba\u5728\u671d\u7684\u662f\uff1a\u4e2d\u4e66\u4ee4 \u88f4\u708e\uff0c\u82f1\u56fd\u516c\u5f90\u656c\u4e1a\uff0c\u548c\u5927\u5c06\u519b\u7a0b\u52a1\u633a\uff1b\u5728\u91ce\u7684\u5219\u662f\u6b66\u6797\u7684\u8001\u76df\u4e3b\u8c37\u795e\u7fc1\u548c\u4ed6\u7684\u5e08\u7236\u5c09 \u8fdf\u70af\u3002\u4ed6\u4eec\u7ea6\u5b9a\uff0c\u5c06\u6765\u4e92\u901a\u6d88\u606f\u4e4b\u65f6\uff0c\u4fbf\u4ee5\u8fd9\u201c\u6625\u96f7\u52a8\u5730\uff0c\u98de\u9f99\u5728\u5929\u3002\u201d\u516b\u4e2a\u5b57\u4f5c\u4e3a\u6697 \u53f7\uff0c\u82e5\u662f\u6709\u4eba\u80fd\u8bf4\u51fa\u8fd9\u516b\u4e2a\u5b57\uff0c\u90a3\u4fbf\u662f\u4ed6\u4eec\u6240\u6d3e\u9063\u7684\u201c\u81ea\u5df1\u4eba\u201d\u4e86\u3002\u8fd9\u516b\u4e2a\u5b57\u542b\u6709\u6df1\u610f\uff0c \u8868\u793a\u4ed6\u4eec\u4e00\u65e6\u4e3e\u4e8b\uff0c\u4fbf\u5c06\u5982\u6625\u96f7\u4e4b\u52a8\u5730\uff0c\u86f0\u4f0f\u7684\u795e\u9f99\u4e5f\u5c31\u8981\u98de\u4e0a\u4e5d\u5929\u3002 \u3000\u3000\u674e\u9038\u4e00\u542c\u8fd9\u4e24\u4e2a\u4eba\u5c45\u7136\u8bf4\u5f97\u51fa\u8fd9\u4e24\u53e5\u6697\u53f7\uff0c\u5148\u662f\u4e00\u60ca\uff0c\u7ee7\u800c\u8be7\u5f02\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u662f \u8c01\u6d3e\u4ed6\u4eec\u6765\u63a5\u6211\u7684\u5462\uff1f\u8c37\u795e\u7fc1\u524d\u4e9b\u65e5\u5b50\u8fd8\u548c\u6211\u540c\u5728\u4e00\u5904\uff0c\u73b0\u5728\u6b63\u53bb\u8fce\u6388\u957f\u5b59\u5747\u91cf\uff1b\u6211\u7684 \u5e08\u7236\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\uff1b\u6590\u708e\u4e43\u662f\u5f53\u671d\u5bb0\u76f8\uff0c\u4ed6\u600e\u77e5\u9053\u6211\u5728\u6c5f\u6e56\u4e0a\u7684\u884c\u8e2a\uff1f\u5f90\u656c\u4e1a\u8fdc\u5728\u626c\u5dde\uff0c \u800c\u4e14\u73b0\u5728\u6b63\u662f\u8ba8\u6b66\u5219\u5929\u7684\u4e09\u519b\u4e3b\u5e05\uff0c\u4ed6\u66f4\u6ca1\u6709\u5230\u8fd9\u91cc\u7684\u9053\u7406\uff01\u7a0b\u52a1\u633a\u88ab\u6b66\u5219\u5929\u6d3e\u8ba8\u5f90\u656c \u4e1a\u4e86\uff0c\u5373\u7b97\u4ed6\u9635\u524d\u53cd\u6208\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8fd9\u6837\u5feb\u4fbf\u6253\u56de\u6765\uff0c\u8fd9\u4e24\u4e2a\u4eba\u8981\u6211\u53bb\u89c1\u4ed6\u4eec\u7684\u201c\u4e3b\u4eba\u201d\uff0c \u8fd9\u4e2a\u4e3b\u4eba\u662f\u4e94\u4eba\u4e2d\u7684\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u5f20\u4e4b\u5947\u538b\u6839\u513f\u4e0d\u61c2\u5f97\u8fd9\u516b\u4e2a\u5b57\u7684\u610f\u601d\uff0c\u5f53\u4e0b\u52c3\u7136\u5927\u6012\uff0c\u65a5\u4ee4\u90a3\u73ed\u4eba\u8ba9\u8def\u3002\u7a0b\u901a\u5ffd\u5730 \u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5bb6\u4e3b\u4eba\u8bda\u5fc3\u8bda\u610f\u8981\u7559\u4e0b\u516c\u5b50\u7684\u5927\u9a7e\uff0c\u516c\u5b50\u4f60\u5374\u5f53\u771f\u4e0d\u613f\u610f\u53bb\u89c1\u4ed6 \u4e48\uff1f\u201d\u5f20\u4e4b\u5947\u65a5\u9053\uff1a\u201c\u6211\u8981\u8d76\u5f80\u957f\u5b89\uff0c\u8c01\u8010\u70e6\u548c\u4f60\u4eec\u7ea0\u7f20\u4e0d\u6e05\uff01\u201d\u53f3\u624b\u90a3\u4e2a\u6c49\u5b50\u51b7\u7b11\u8bf4 \u9053\uff1a\u201c\u8fd9\u7965\u770b\u6765\uff0c\u6d41\u8a00\u975e\u5047\uff0c\u674e\u516c\u5b50\u4f60\u7adf\u80cc\u8a93\u5bd2\u76df\uff0c\u60f3\u5165\u957f\u5b89\u53bb\u6c42\u5bcc\u8d35\u53bb\u4e86\uff1f\u201d\u5f20\u4e4b\u5947 \u8d8a\u53d1\u88ab\u4ed6\u4eec\u6fc0\u5f97\u5927\u6012\uff0c\u201c\u5530\u201d\u7684\u53c8\u662f\u4e00\u97ad\u6253\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u8001\u5b50\u59d3\u5f20\uff0c\u4e0d\u9519\uff0c\u8001\u5b50\u6b63\u662f\u8981 \u5165\u957f\u5b89\u53bb\u6c42\u53d6\u529f\u540d\u5bcc\u8d35\uff0c\u4f60\u4eec\u7ba1\u5f97\u7740\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u901a\u53cc\u81c2\u4e00\u632f\uff0c\u5c06\u5f20\u4e4b\u5947\u90a3\u5339\u9a6c\u4e00\u62e6\uff0c\u767b\u65f6\u6309\u4e0b\u4e86\u9a6c\u5934\uff0c\u5f20\u4e4b\u5947\u4e00\u4e2a\u98de\u8eab\u8df3\uff0c\u53f3\u8fb9 \u90a3\u4e2a\u6c49\u5b50\u4e00\u62db\u64d2\u62ff\u624b\u6cd5\uff0c\u7acb\u523b\u671d\u4ed6\u6293\u4e0b\uff0c\u5f20\u4e4b\u5947\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u53f3\u624b\u6325\u52a8\u957f\u97ad\uff0c\u5de6\u624b\u62d4 \u51fa\u4e00\u67c4\u77ed\u5251\uff0c\u957f\u97ad\u5de6\u626b\uff0c\u77ed\u5251\u53f3\u6233\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u540c\u65f6\u88ad\u51fb\u4e24\u4e2a\u654c\u4eba\u3002 \u3000\u3000\u7a0b\u901a\u4f7f\u51fa\u4e00\u5957\u7f57\u6c49\u795e\u62f3\uff0c\u62f3\u98ce\u864e\u864e\uff0c\u521a\u731b\u4e4b\u6781\uff0c\u90a3\u4e2a\u6c49\u5b50\u7684\u64d2\u62ff\u624b\u6cd5\uff0c\u66f4\u662f\u5341\u5206\u4e86 \u5f97\uff0c\u7adf\u5728\u5251\u5149\u97ad\u5f71\u4e4b\u4e2d\u6b3a\u8eab\u8fdb\u6765\uff0c\u5f20\u4e4b\u5947\u7684\u6b66\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u4ee5\u4e00\u654c\u4e8c\uff0c\u5374\u662f\u62b5\u6321\u4e0d\u4f4f\uff0c \u5927\u7ea6\u6253\u5230\u4e09\u5341\u62db\u4e4b\u540e\uff0c\u90a3\u6c49\u5b50\u4e00\u6258\u97ad\u7a0d\uff0c\u84e6\u5730\u4f7f\u4e86\u4e00\u62db\u201c\u656c\u5fb7\u593a\u97ad\u201d\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u624b \u626d\u4f4f\u4e86\u5f20\u4e4b\u5947\u7684\u624b\u8155\uff0c\u7a0b\u901a\u8d81\u52bf\u4e00\u62f3\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u5728\u4ed6\u808b\u4e0b\u6253\u4e86\u4e00\u62f3\uff0c\u5f20\u4e4b\u5947\u7684\u77ed\u5251\u88ab \u4ed6\u6253\u843d\u5730\u4e0a\uff0c\u957f\u97ad\u4e5f\u7ed9\u90a3\u4e2a\u6c49\u5b50\u5288\u624b\u593a\u53bb\uff0c\u5e76\u4e14\u7acb\u5373\u70b9\u4e86\u4ed6\u7684\u54d1\u7a74\uff0c\u4e24\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u5c06 \u5f20\u4e4b\u5947\u53cc\u624b\u53cd\u4e0a\uff0c\u7f1a\u5728\u9a6c\u80cc\u4e0a\uff0c\u4e00\u58f0\u547c\u5578\uff0c\u7adf\u81ea\u62e5\u7740\u5f20\u4e4b\u5947\u8d70\u4e86\u3002 \u3000\u3000\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u662f\u5c06\u5f20\u4e4b\u5947\u8bef\u4f5c\u662f\u6211\uff0c\u5374\u600e\u7684\u5bf9\u4ed6\u5982\u6b64\u65e0\u793c\uff1f \u4ed6\u4eec\u9a82\u6211\u80cc\u8a93\u5bd2\u76df\uff0c\u8fd9\u6d41\u8a00\u53c8\u662f\u600e\u4e48\u6765\u7684\uff1f\u5373\u7b97\u6211\u662f\u80cc\u8a93\u5bd2\u76df\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u8be5\u8fd9\u6837\u901e\u51f6\u6bb4 \u6253\u554a\uff01\u201d\u8981\u77e5\u674e\u9038\u867d\u7136\u662f\u8ba8\u538c\u5f20\u4e4b\u5947\uff0c\u4f46\u5f20\u4e4b\u5947\u906d\u53d7\u4e86\u8fd9\u4e00\u573a\u98de\u6765\u7684\u6a2a\u7978\uff0c\u5230\u5e95\u662f\u56e0\u4ed6 \u800c\u8d77\uff0c\u800c\u4e14\u90a3\u4e9b\u4eba\u8fd9\u6837\u5bf9\u5f85\u4ed6\u7684\u201c\u5047\u8eab\u201d\uff0c\u6bb4\u8fb1\u4e86\u5f20\u4e4b\u5947\u4e5f\u5c31\u7b49\u4e8e\u662f\u6bb4\u8fb1\u4e86\u4ed6\u4e00\u6837\u3002\u674e \u9038\u8d8a\u60f3\u8d8a\u662f\u751f\u6c14\uff0c\u800c\u4e14\u8d8a\u60f3\u8d8a\u89c9\u5f97\u5176\u4e2d\u7591\u7aa6\u751a\u591a\uff0c\u867d\u5219\u4ed6\u6781\u4e0d\u613f\u610f\u60f9\u4e8b\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u67e5\u4e2a \u7a76\u7adf\u4e86\u3002\u674e\u9038\u4ece\u5c71\u4e0a\u5954\u4e0b\uff0c\u4ed6\u90a3\u5339\u9a6c\u5df2\u4e0d\u77e5\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u90a3\u662f\u4ed6\u5728\u8def\u4e0a\u4e70\u6765\u7684\u4e00\u5339\u5ddd \u9a6c\uff0c\u56e0\u4e3a\u8981\u9002\u5408\u81ea\u5df1\u6539\u88c5\u4e4b\u540e\u7684\u5bd2\u5112\u8eab\u4efd\uff0c\u4e70\u7684\u4e0d\u8fc7\u662f\u4e00\u5339\u666e\u666e\u901a\u901a\u7684\u5ddd\u9a6c\uff0c\u5931\u4e86\u4e5f\u4e0d \u8db3\u501f\u3002\u674e\u9038\u6025\u4e8e\u67e5\u77e5\u7a76\u7adf\uff0c\u4e0d\u518d\u53bb\u627e\u56de\u81ea\u5df1\u7684\u5750\u9a91\u4fbf\u5373\u65bd\u5c55\u8f7b\u529f\uff0c\u8ffd\u8e2a\u90a3\u4e00\u73ed\u4eba\u3002 \u3000\u3000\u674e\u9038\u7684\u8f7b\u529f\u867d\u597d\uff0c\u7a76\u7adf\u8d5b\u4e0d\u8fc7\u98de\u5954\u7684\u5065\u9a6c\uff0c\u8ffd\u51fa\u5c71\u53e3\uff0c\u90a3\u73ed\u4eba\u5df2\u53bb\u5f97\u8fdc\u4e86\uff0c\u76ee\u529b\u6240 \u53ca\uff0c\u53ea\u89c1\u51e0\u4e2a\u5f71\uff0c\u518d\u8fc7\u4e9b\u65f6\uff0c\u5f71\u5b50\u4e5f\u4e0d\u89c1\u4e86\u3002\u8fd9\u65f6\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u5728\u7530\u95f4\u64cd\u4f5c\u7684\u519c\u592b\u4e09 \u4e09\u4e94\u4e94\u7684\u8377\u9504\u5f52\u5bb6\uff0c\u674e\u9038\u622a\u7740\u4e00\u4e2a\u8001\u519c\u6500\u8c08\uff0c\u5047\u88c5\u4f5c\u662f\u9519\u8fc7\u5bbf\u5934\u7684\u65c5\u5ba2\uff0c\u90a3\u8001\u519c\u9053\uff1a \u201c\u518d\u8d70\u5341\u91cc\u5149\u666f\uff0c\u524d\u9762\u4fbf\u6709\u4e00\u4e2a\u5c0f\u9547\uff0c\u53ef\u4ee5\u6295\u5bbf\u3002\u201d\u8fd9\u8001\u519c\u592b\u5fc3\u80a0\u5f88\u597d\uff0c\u4ed6\u6253\u91cf\u4e86\u674e\u9038 \u4e00\u773c\uff0c\u53c8\u9053\uff1a\u201c\u76f8\u516c\u662f\u8bfb\u4e66\u4eba\uff0c\u53ea\u6015\u4e0d\u60ef\u8d70\u8def\uff0c\u82e5\u662f\u771f\u7684\u8d70\u4e0d\u52a8\u4e86\uff0c\u4e0d\u5acc\u5f03\u7684\u8bdd\uff0c\u8bf7\u5230 \u820d\u4e0b\u4f4f\u5bbf\u4e00\u665a\u4e5f\u884c\u3002\u201d\u674e\u9038\u8c22\u8fc7\u4e86\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u8d70\uff0c\u6211\u662f\u8d70\u5f97\u52a8\u7684\uff0c\u65e2\u7136\u53ea\u6709\u5341\u91cc\u4e4b\u8def\uff0c \u5165\u9ed1\u4e4b\u540e\uff0c\u8d76\u5230\u9547\u4e0a\u6295\u5bbf\u6b63\u597d\u3002\u53ea\u662f\u6211\u6709\u70b9\u5bb3\u6015\u3002\u201d\u90a3\u519c\u592b\u9053\uff1a\u201c\u76f8\u516c\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d\u674e \u9038\u9053\uff1a\u201c\u6211\u5bb3\u6015\u8def\u4e0a\u6709\u76d7\u8d3c\u3002\u201d \u3000\u3000\u90a3\u519c\u592b\u7b11\u9053\uff1a\u201c\u73b0\u5728\u7684\u4e16\u9053\u6bd4\u4ece\u524d\u597d\u591a\u4e86\uff0c\u4f55\u51b5\u8fd9\u91cc\u5230\u957f\u5b89\u4e0d\u8fc7\u662f\u51e0\u65e5\u7684\u8def\u7a0b\uff0c\u66f4 \u4e0d\u4f1a\u6709\u76d7\u8d3c\u7684\u3002\u201d\u674e\u9038\u987a\u7740\u4ed6\u7684\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8d70\u4e86\u597d\u51e0\u5929\u90fd\u6ca1\u6709\u77a7\u89c1\u8fc7\u76d7\u8d3c\uff0c\u4e0d \u8fc7\u8d8a\u8fd1\u957f\u5b89\uff0c\u53cd\u4f3c\u8d8a\u4e0d\u5b89\u9759\u4e86\u3002\u201d\u90a3\u519c\u592b\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u521a\u624d\u5c31\u78b0\u5230\u4e86\u4e00 \u73ed\u532a\u5f92\uff0c\u5c06\u4e00\u4e2a\u4e0a\u4eac\u6295\u519b\u7684\u4eba\u7f1a\u53bb\u4e86\u3002\u201d\u90a3\u519c\u592b\u5947\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u521a\u4ece\u8fd9\u91cc \u7ecf\u8fc7\uff0c\u96be\u9053\u4f60\u4eec\u6ca1\u6709\u770b\u89c1\u4e48\uff1f\u201d\u90a3\u519c\u592b\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d\u4e86\uff0c\u90a3\u73ed\u4eba\u662f\u88f4\u5bb6\u7684\u5bb6\u4e01\uff0c\u4ed6 \u4eec\u7684\u9a6c\u8dd1\u5f97\u592a\u5feb\uff0c\u6211\u770b\u4e0d\u6e05\u695a\u4ed6\u4eec\u7684\u9a6c\u8fd8\u7f1a\u6709\u4eba\u5462\u3002\u54fc\uff0c\u4ed6\u4eec\u4e5f\u592a\u6043\u52bf\u6b3a\u4eba\u4e86\uff01\u4e0d\u8fc7\u90a3 \u4eba\u4e00\u5b9a\u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\u60c5\u5192\u72af\u4e86\u88f4\u5bb6\u7684\uff0c\u76f8\u516c\u548c\u4ed6\u4eec\u88f4\u5bb6\u65e0\u51a4\u65e0\u4ec7\uff0c\u5374\u662f\u7528\u4e0d\u7740\u5bb3\u6015\u3002\u201d \u674e\u9038\u9053\uff1a\u201c\u88f4\u5bb6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u519c\u592b\u9053\uff1a\u201c\u5f53\u4eca\u7684\u5bb0\u76f8\u88f4\u708e\uff0c\u6b63\u662f\u6211\u4eec\u6751\u5b50\u7684\u4eba\u3002\u201d\u674e \u9038\u9053\uff1a\u201c\u88f4\u708e\u4e0d\u662f\u5728\u957f\u5b89\u5417\uff1f\u201d\u90a3\u519c\u592b\u9053\uff1a\u201c\u4ed6\u8fd8\u6709\u4e00\u4e2a\u5f1f\u5f1f\u770b\u5b88\u8001\u5bb6\uff0c\u672a\u66fe\u642c\u53bb\u957f\u5b89\u3002\u201d \u674e\u9038\u6124\u7136\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u5f53\u4eca\u7684\u5973\u7687\u5e1d\u66fe\u4e0b\u4ee4\u4e0d\u8bb8\u7d0a\u5f3a\u6b3a\u538b\u767e\u59d3\uff0c\u770b\u6765\u8fd9\u79cd\u547d\u4ee4\u4e5f\u53ea\u662f\u4e00 \u7eb8\u5177\u6587\uff0c\u9a97\u9a97\u8001\u767e\u59d3\u7684\u7f62\u4e86\u3002\u201d \u3000\u3000\u90a3\u519c\u592b\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8bdd\u53ef\u4e0d\u80fd\u8fd9\u4e48\u8bf4\u6cd5\u3002\u82e5\u5728\u4ece\u524d\uff0c\u522b\u8bf4\u662f\u5f53\u671d\u5bb0\u76f8\u7684\u4eb2\u5144 \u5f1f\u4e86\uff0c\u4ec5\u4ec5\u4e00\u4e2a\u53bf\u5b98\u7684\u5bb6\u4eba\uff0c\u5728\u4e61\u4e0b\u5c31\u50cf\u7687\u5e1d\u4e00\u822c\uff0c\u6253\u4eba\u9a82\u4eba\uff0c\u90a3\u771f\u662f\u5e73\u5e38\u5f97\u5f88\u3002\u88f4\u5bb6 \u786e\u662f\u6709\u70b9\u6043\u52bf\u6a2a\u884c\uff0c\u4f46\u50cf\u4eca\u5929\u8fd9\u6837\u7684\u516c\u7136\u63b3\u4eba\uff0c\u5374\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002\u5e73\u65e5\u4e00\u4e9b\u4e8b\uff0c\u6211\u4eec\u4e61\u4e0b \u4eba\u5403\u70b9\u4e8f\uff0c\u80fd\u5fcd\u4fbf\u5fcd\uff0c\u8fd9\u5012\u4e0d\u662f\u4e3a\u4e86\u6015\u4ed6\u624d\u4e0d\u6562\u8fdb\u4eac\u544a\u4ed6\uff0c\u800c\u662f\u4e0d\u613f\u62ff\u4e00\u4e9b\u5c0f\u4e8b\u53bb\u9ebb\u70e6 \u5929\u540e\u3002\u201d\u674e\u9038\u672c\u6765\u662f\u60f3\u501f\u8fd9\u4ef6\u4e8b\u6765\u9a82\u6b66\u5219\u5927\uff0c\u4e0d\u6599\u4e61\u4e0b\u4eba\u5bf9\u6b66\u5219\u5929\u5374\u662f\u90a3\u4e48\u62e5\u6234\uff0c\u4e0d\u7531 \u5f97\u5fc3\u4e2d\u4e00\u51c9\uff0c\u597d\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u90a3\u519c\u592b\u671b\u671b\u5929\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5148\u751f\u4f60\u4e0d\u5acc\u5f03\u7684\u8bdd\uff0c\u8fd8\u662f\u8bf7\u5230\u820d\u4e0b\u6b47\u6b47\u5427\uff0c\u5929\u8272\u5df2\u7ecf \u665a\u4e86\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u591a\u8c22\uff0c\u8def\u4e0a\u65e2\u6ca1\u6709\u76d7\u8d3c\uff0c\u6211\u8d70\u4e00\u7a0b\u591c\u8def\u4e5f\u4e0d\u7528\u5bb3\u6015\u4e86\u3002\u6211\u8fd8\u662f\u5230\u524d \u9762\u5c0f\u9547\u6295\u5bbf\u7684\u597d\u3002\u201d\u90a3\u519c\u592b\u89c1\u4ed6\u6267\u610f\u8981\u8d70\uff0c\u53ea\u597d\u7531\u4ed6\u81ea\u53bb\u3002 \u3000\u3000\u674e\u9038\u5728\u6751\u5916\u515c\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u5165\u9ed1\u4e4b\u540e\uff0c\u518d\u6298\u56de\u6765\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u539f\u6765\u662f\u88f4\u708e\u5e72\u7684\u52fe \u5f53\uff0c\u88f4\u708e\u4e3a\u4ec0\u4e48\u8981\u7f1a\u67b6\u6211\u5462\uff1f\u201d\u88f4\u708e\u66fe\u7ecf\u6d3e\u9063\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u53bb\u523a\u6740\u5e9f\u592a\u5b50\u674e\u8d24\uff0c\u674e\u9038 \u5bf9\u8fd9\u4ef6\u4e8b\u4e00\u76f4\u662f\u75db\u6068\u4e8e\u5fc3\uff0c\u518d\u52a0\u4e0a\u4eca\u65e5\u8fd9\u6869\u4e8b\u60c5\uff0c\u4ed6\u8d8a\u53d1\u4e0d\u80fd\u5fcd\u53d7\uff0c\u51b3\u5b9a\u8981\u53bb\u63a2\u4e2a\u660e\u767d\u3002 \u3000\u3000\u88f4\u5bb6\u7684\u5927\u5c4b\u5728\u6751\u5b50\u7684\u4e1c\u5934\uff0c\u501a\u7740\u5c71\u5761\u4fee\u5efa\uff0c\u5c4b\u524d\u5c4b\u540e\uff0c\u6709\u51e0\u4e2a\u6b66\u58eb\u5de1\u6765\u5de1\u53bb\uff0c\u674e\u9038 \u6545\u610f\u5728\u6811\u6797\u91cc\u53d1\u51fa\u602a\u58f0\uff0c\u5f15\u5f97\u90a3\u51e0\u4e2a\u6b66\u58eb\u8dd1\u6765\u5f20\u671b\uff0c\u674e\u9038\u5bf9\u51c6\u6811\u4e0a\u7684\u4e00\u4e2a\u9e1f\u5de2\uff0c\u8f7b\u8f7b\u7684 \u5f39\u51fa\u4e86\u4e00\u7c92\u77f3\u5b50\uff0c\u5c06\u51e0\u53ea\u5927\u9e1f\u8d76\u5f97\u632f\u7fc5\u98de\u8d77\uff0c\u5471\u5471\u5c16\u53eb\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u6b66\u58eb\u56b7\u9053\uff1a\u201c\u539f\u6765 \u662f\u591c\u8885\uff0c\u5478\uff01\u201d\u53e6\u4e00\u4e2a\u6b66\u571f\u9053\uff1a\u201c\u6599\u60f3\u6ca1\u6709\u4eba\u8fd9\u4e48\u5927\u80c6\uff0c\u6562\u6765\u627e\u5458\u5916\u7684\u9ebb\u70e6\u3002\u201d\u53e6\u4e00\u4e2a \u9053\uff1a\u201c\u8fd9\u4e5f\u96be\u8bf4\uff0c\u542c\u8bf4\u4e1e\u76f8\u5f97\u7f6a\u4e86\u5929\u540e\uff0c\u8bf4\u4e0d\u5b9a\u5929\u540e\u6d3e\u9063\u5927\u5185\u536b\u58eb\u6765\u5462\uff0c\u600e\u53ef\u4ee5\u4e0d\u5c0f\u5fc3 \u9632\u5907\uff1f\u201d\u674e\u9038\u542c\u4ed6\u4eec\u8bae\u8bba\u7eb7\u7eb7\uff0c\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u6697\u7b11\uff0c\u7acb\u523b\u65bd\u5c55\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u4e0a\u4e58\u8f7b\u529f\uff0c \u4ece\u6797\u5b50\u7684\u53e6\u4e00\u8fb9\u63a0\u51fa\uff0c\u5f85\u5230\u90a3\u51e0\u4e2a\u536b\u58eb\u8f6c\u8fc7\u8eab\u6765\uff0c\u4ed6\u65e9\u5df2\u98de\u8fc7\u5899\u5934\uff0c\u8fdb\u4e86\u5185\u9662\u3002 \u3000\u3000\u674e\u9038\u5728\u9662\u5b50\u7684\u6697\u89d2\u4f0f\u533f\u4e86\u4e00\u4f1a\uff0c\u89c1\u4e00\u4e2a\u5355\u8eab\u7684\u6b66\u58eb\u63d0\u7740\u706f\u7b3c\u8d70\u8fc7\u6765\uff0c\u674e\u9038\u8eab\u5f62\u4e00\u73b0\uff0c \u660e\u6643\u6643\u7684\u5251\u5c16\u4fbf\u5373\u5bf9\u51c6\u4e86\u4ed6\u7684\u54bd\u5589\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u56b7\u4e00\u56b7\uff0c\u6211\u5c31\u8981\u4f60\u7684\u547d\uff01\u201d\u90a3\u6b66\u58eb \u662f\u4e2a\u884c\u5bb6\uff0c\u4e00\u8d1d\u674e\u9038\u7684\u8eab\u6cd5\u624b\u6cd5\uff0c\u77e5\u9053\u6765\u4eba\u7684\u6b66\u529f\u6bd4\u81ea\u5df1\u4f55\u6b62\u9ad8\u51fa\u5341\u500d\uff0c\u679c\u7136\u4e0d\u6562\u52a8\u5f39\u3002 \u674e\u9038\u5c06\u4ed6\u7684\u706f\u7b3c\u5439\u7184\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5458\u5916\u5728\u54ea\u91cc\uff0c\u5feb\u5e26\u6211\u53bb\u3002\u201d\u90a3\u6b66\u58eb\u4e0d\u6562\u4e0d\u4f9d\uff0c\u5e26\u7740 \u4ed6\u7a7f\u51fa\u4e24\u5904\u89d2\u95e8\uff0c\u6307\u7740\u56ed\u4e2d\u4e00\u95f4\u5c4b\u5b50\u9053\uff1a\u201c\u5c31\u5728\u90a3\u513f\uff0c\u4f60\u81ea\u5df1\u53bb\u5427\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u59d4\u5c48 \u4f60\u8eba\u4e00\u4f1a\u513f\uff0c\u4f60\u8bf4\u7684\u82e5\u662f\u5b9e\u8bdd\uff0c\u6211\u89c1\u4e86\u88f4\u5458\u5916\u4e4b\u540e\uff0c\u56de\u6765\u518d\u653e\u4f60\u3002\u201d\u4fe1\u624b\u70b9\u4e86\u4ed6\u7684\u9ebb\u7a74\uff0c \u5c06\u4ed6\u653e\u5728\u5047\u5c71\u77f3\u7684\u540e\u9762\uff0c\u98de\u8eab\u63a0\u4e0a\u5c4b\u6a90\uff0c\u5411\u5c4b\u5b50\u91cc\u5077\u5077\u5f20\u671b\uff0c\u53ea\u89c1\u53a6\u5185\u706f\u706b\u8f89\u714c\uff0c\u6709\u51e0 \u4e2a\u6b66\u58eb\u4f8d\u7acb\u4e24\u65c1\uff0c\u4e24\u4e2a\u5b98\u5458\u6a21\u6837\u7684\u5750\u5728\u5f53\u4e2d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5176\u4e2d\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u6211\u5927\u54e5\u88ab\u6355\u7684\u6d88\u606f\u4e43\u662f\u5343\u771f\u4e07\u786e\u7684\u4e86\u3002\u738b\u5927\u4eba \u53ef\u77e5\u9053\u4ed6\u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\u60c5\u5f97\u7f6a\u5929\u540e\u7684\u5417\uff1f\u201d\u674e\u9038\u4e00\u542c\uff0c\u4fbf\u77e5\u8fd9\u4eba\u662f\u88f4\u708e\u7684\u5f1f\u5f1f\u88f4\u660c\uff0c\u53e6 \u4e00\u4e2a\u4eba\u7a7f\u7740\u4e09\u54c1\u4eac\u5b98\u7684\u773c\u9970\uff0c\u5782\u5934\u4e27\u6c14\u7684\u8bf4\u9053\uff1a\u201c\u88f4\u5927\u4eba\u7a81\u7136\u88ab\u9f99\u9a91\u90fd\u5c09\u62d8\u6355\uff0c\u5173\u8fdb\u5929 \u7262\u3002\u6211\u4e00\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u8d76\u5fd9\u9003\u51fa\u4eac\u90fd\uff0c\u90a3\u8fd8\u6709\u529f\u592b\u8be6\u7ec6\u67e5\u95ee\u3002\u201d\u88f4\u660c\u9053\uff1a\u201c\u6211\u5927\u54e5\u88ab \u6355\u4e4b\u540e\u591a\u4e45\uff0c\u738b\u5927\u4eba\u624d\u77e5\u9053\u6d88\u606f\u7684\uff1f\u201d\u90a3\u4eac\u5b98\u9053\uff1a\u201c\u88f4\u5927\u4eba\u5728\u665a\u4e0a\u4e09\u66f4\u88ab\u6355\uff0c\u6211\u7b2c\u4e8c\u65e5 \u65e9\u4e0a\u77e5\u9053\u7684\u3002\u201d\u88f4\u660c\u9053\uff1a\u201c\u4e0a\u8fc7\u4e86\u65e9\u671d\u6ca1\u6709\uff1f\u201d\u90a3\u4eac\u5b98\u9053\uff1a\u201c\u6b63\u662f\u5728\u9000\u671d\u4e4b\u540e\uff0c\u5bab\u4e2d\u7684 \u4e00\u4e2a\u5185\u76d1\u5077\u5077\u544a\u8bc9\u6211\u7684\u3002\u4ed6\u4e5f\u4e0d\u77e5\u9053\u5185\u91cc\u60c5\u7531\u3002\u201d\u88f4\u660c\u9053\uff1a\u201c\u6b66\u5219\u5929\u5728\u671d\u5802\u4e4b\u4e0a\u6ca1\u6709\u8bf4 \u4ec0\u4e48\u5417\uff1f\u201d\u90a3\u4eac\u5b98\u9053\uff1a\u201c\u6b66\u5219\u5929\u53ea\u662f\u5fd9\u4e8e\u8c03\u5175\u9063\u5c06\uff0c\u5bf9\u88f4\u5927\u4eba\u7684\u4e8b\u4e00\u53e5\u4e5f\u6ca1\u63d0\u53ca\u3002\u6211\u4eec \u8fd8\u4ee5\u4e3a\u6590\u5927\u4eba\u662f\u56e0\u75c5\u7f3a\u671d\u7684\u5462\u3002\u201d \u3000\u3000\u88f4\u708e\u88ab\u6b66\u5219\u5929\u6253\u5165\u5929\u7262\uff0c\u8fd9\u4e8b\u5927\u51fa\u674e\u9038\u610f\u6599\u4e4b\u5916\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u521a\u624d\u90a3\u4e24\u4e2a \u6b66\u58eb\u62c5\u5fc3\u4f1a\u6709\u5927\u5185\u7684\u536b\u58eb\u5230\u6765\u3002\u201d\u542c\u90a3\u4e2a\u201c\u738b\u5927\u4eba\u201d\u7684\u53e3\u6c14\uff0c\u5927\u7ea6\u4ed6\u662f\u88f4\u708e\u7684\u4e00\u515a\uff0c\u6015 \u53d7\u7275\u7d2f\uff0c\u6545\u6b64\u8fde\u5fd9\u9003\u547d\u3002\u88f4\u660c\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5927\u54e5\u7d20\u5f97\u5929\u540e\u4fe1\u4efb\uff0c\u53ea\u8981\u4e0d\u662f\u8c0b\u53cd \u7684\u4e8b\u60c5\u6cc4\u9732\uff0c\u4e5f\u8bb8\u8fd8\u53ef\u8f6c\u5708\u3002\u201d\u90a3\u4eac\u5b98\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7f6a\u72b6\u6ca1\u6709\u5ba3\u5e03\uff0c\u8fd8\u6709\u4e00\u7ebf\u5e0c\u671b\u3002\u201d \u88f4\u660c\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u53ef\u80fd\u73b0\u5728\u6b63\u5728\u641c\u96c6\u7f6a\u8bc1\uff0c\u4e0d\u53ef\u4e0d\u9632\u3002\u201d\u90a3\u4eac\u5b98\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u6211\u4e00 \u8def\u9a6c\u4e0d\u505c\u8e44\uff0c\u8d76\u6765\u7980\u62a5\uff0c\u4e3a\u7684\u5c31\u662f\u6015\u4f60\u4eec\u5bb6\u4e2d\u85cf\u6709\u4ec0\u4e48\u8c0b\u53cd\u7684\u8bc1\u636e\u3002\u201d\u6590\u660c\u9053\uff1a\u201c\u73b0\u5728 \u5c31\u82e6\u4e8e\u4e0d\u77e5\u4ed6\u56e0\u4f55\u88ab\u6355\u3002\u82e5\u7136\u4e0d\u662f\u4e3a\u4e86\u8c0b\u53cd\uff0c\u5ef7\u5c09\u6765\u65f6\uff0c\u54b1\u4eec\u53ef\u4ee5\u63a5\u8bcf\u3002\u82e5\u662f\u4e3a\u4e86\u8c0b\u53cd\uff0c \u54b1\u4eec\u4e00\u5bb6\u90fd\u662f\u6b7b\u7f6a\uff0c\u90a3\u5c31\u53ea\u6709\u62d2\u6355\u4e86\u3002\u6211\u5df2\u53eb\u5bb6\u4eba\u62fe\u597d\u7ec6\u8f6f\uff0c\u4e07\u4e00\u6709\u53d8\uff0c\u54b1\u4eec\u5373\u523b\u5411\u540e \u5c71\u9003\u8dd1\u3002\u201d\u674e\u9038\u89c1\u88f4\u660c\u5728\u8fd9\u6837\u7d27\u8981\u7684\u5173\u5934\uff0c\u5c45\u7136\u8fd8\u80fd\u51b7\u9759\u5e94\u4ed8\uff0c\u5fc3\u9053\uff1a\u201c\u88f4\u708e\u8001\u5978\u5de8\u6ed1\uff0c \u4ed6\u7684\u5f1f\u5f1f\uff0c\u4e5f\u5b66\u5f97\u51e0\u5206\u3002\u201d \u3000\u3000\u88f4\u660c\u6b47\u4e86\u4e00\u6b47\uff0c\u5429\u5490\u4e00\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u5c06\u90a3\u4f4d\u738b\u5b59\u63d0\u6765\u4e86\u3002\u201d\u8f6c\u8fc7\u5934\u5bf9\u90a3\u4f4d \u201c\u738b\u5927\u4eba\u201d\u9053\uff1a\u201c\u4fa5\u5e78\u4e4b\u6781\uff0c\u674e\u9038\u843d\u5728\u6211\u7684\u624b\u4e2d\uff0c\u518d\u4e5f\u4e0d\u6015\u4ed6\u8fdb\u4eac\u544a\u5bc6\u4e86\u3002\u201d\u90a3\u201c\u738b\u5927 \u4eba\u201d\u9053\uff1a\u201c\u674e\u9038\uff1f\u4ed6\u4e0d\u6b63\u662f\u516b\u5e74\u524d\u5931\u8e2a\u7684\u90a3\u4f4d\u738b\u5b59\u5417\uff1f\u201d\u88f4\u660c\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u8fd9\u6b21\u82f1 \u56fd\u516c\u8d77\u5175\uff0c\u4ed6\u4e5f\u66fe\u53c2\u4e0e\u5927\u4e8b\u3002\u4e0d\u8fc7\uff0c\u6211\u5927\u54e5\u6015\u4ed6\u6000\u6709\u4e8c\u5fc3\uff0c\u65e9\u5df2\u53eb\u6211\u5c0f\u5fc3\u4ed6\u7684\u884c\u8e2a\u3002\u597d \u5728\u4ed6\u8981\u5165\u957f\u5b89\u3002\u5fc5\u5b9a\u8981\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u6211\u5929\u5929\u53eb\u4eba\u5230\u8def\u53e3\u7b49\u5019\uff0c\u679c\u7136\u7ed9\u6211\u7b49\u5230\u4e86\u3002\u201d \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u88f4\u660c\u5c06\u5f20\u4e4b\u5947\u62bc\u6765\uff0c\u5f20\u4e4b\u5947\u5014\u5f3a\u5f97\u5f88\uff0c\u4e00\u8def\u7834\u53e3\u5927\u9a82\u3002\u88f4\u660c\u79bb\u5ea7\u8fce\u6388\uff0c \u5978\u7b11\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\u8fd8\u8ba4\u5f97\u5c0f\u4eba\u4e48\uff1f\u6211\u53eb\u4ed6\u4eec\u8bf7\u4f60\uff0c\u4e0b\u4eba\u4e0d\u77e5\u89c4\u77e9\uff0c\u591a\u591a\u5192\u72af\u4f60\u4e86\u3002\u201d\u5f20 \u4e4b\u5947\u5927\u6012\u9a82\u9053\uff1a\u201c\u8c01\u8ba4\u5f97\u4f60\uff0c\u6211\u4e0e\u4f60\u4f55\u51a4\u4f55\u4ec7\uff0c\u4f60\u5c06\u6211\u63b3\u5230\u8fd9\u91cc\uff1f\u201d\u88f4\u660c\u671d\u5f20\u4e4b\u5947\u9762\u4e0a \u4e00\u671b\uff0c\u4e0d\u89c9\u73b0\u51fa\u4e86\u4e00\u4e1d\u8be7\u5f02\u7684\u795e\u8272\u3002 \u3000\u3000\u7ea6\u5728\u5341\u5e74\u4e4b\u524d\uff0c\u674e\u9038\u5341\u4e00\u3001\u4e8c\u5c81\u7684\u65f6\u5019\uff0c\u6709\u4e00\u6b21\u4ed6\u7684\u7236\u4eb2\u4fe1\u738b\u674e\u9884\u66fe\u5e26\u4ed6\u53bb\u62dc\u8bbf\u88f4 \u708e\uff0c\u88f4\u660c\u5728\u5c4f\u98ce\u540e\u9762\u5077\u5077\u5f20\u671b\uff0c\u5bf9\u674e\u9038\u7559\u6709\u5370\u8c61\u3002\u8fd9\u65f6\u88f4\u660c\u76ef\u7740\u5f20\u4e4b\u5947\u90a3\u4ed8\u7126\u9ec4\u7684\u8138\u76ae\uff0c \u6709\u70b9\u5947\u602a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5f53\u5e74\u90a3\u4e2a\u7c89\u96d5\u7389\u7422\u7684\u5b69\u5b50\uff0c\u957f\u6210\u4e4b\u540e\uff0c\u600e\u7684\u5374\u53d8\u6210\u4e86\u4e2a\u9ec4\u8138\u75c5 \u592b\uff1f\u201d\u7a0b\u901a\u731c\u5230\u4ed6\u7684\u7591\u5fc3\uff0c\u5728\u4ed6\u8033\u8fb9\u5600\u5495\u4e86\u51e0\u53e5\uff0c\u88f4\u660c\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c \u4ed6\u4e2d\u4e86\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u6700\u6076\u6bd2\u7684\u6697\u5668\uff0c\u60f3\u5fc5\u5143\u6c14\u5927\u4f24\uff0c\u96be\u602a\u5f62\u5bb9\u67af\u7a3f\u3002\u201d\u5f20\u4e4b\u5947\u90a3\u8bc6\u5f97 \u5185\u91cc\u60c5\u7531\uff0c\u7834\u53e3\u5927\u9a82\u3002\u88f4\u660c\u5978\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60\u5fd8\u8bb0\u4e86\u6625\u96f7\u52a8\u5730\uff0c\u98de\u9f99\u5728\u5929\u4e4b\u7ea6\u4e48\uff1f\u201d \u5f20\u4e4b\u5947\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u8c01\u662f\u4f60\u7684\u6bbf\u4e0b\uff1f\u4f60\u60f3\u8c0b\u53cd\u4e48\uff1f\u6211\u53ef\u4e0d\u80fd\u53d7\u4f60\u62d6\u7d2f\uff01\u201d\u88f4\u660c\u9762\u8272 \u5927\u53d8\uff0c\u9053\uff1a\u201c\u6211\u5927\u54e5\u4e00\u5fc3\u6276\u52a9\u5510\u5ba4\uff0c\u4f60\u5f53\u771f\u8981\u6069\u5c06\u4ec7\u62a5\uff0c\u4e0a\u4eac\u544a\u5bc6\u4e48\uff1f\u201d\u5f20\u4e4b\u5947\u6012\u9053\uff1a \u201c\u4f60\u4eec\u5230\u5e95\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d\u88f4\u660c\u9053\uff1a\u201c\u4f60\u7eb5\u7136\u8ba4\u4e0d\u5f97\u6211\uff0c\u4e2d\u4e66\u4ee4\u88f4\u708e\uff0c\u4ed6\u662f\u6211\u7684\u5927\u54e5\uff0c \u96be\u9053\u4f60\u4e5f\u4e0d\u8ba4\u5f97\u4ed6\u4e48\uff1f\u201d\u5f20\u4e4b\u5947\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u53cc\u773c\u5706\u7741\uff0c\u9a82\u9053\uff1a\u201c\u88f4\u708e\u662f\u5f53\u671d\u5bb0\u76f8\uff0c \u4ed6\u7684\u5f1f\u5f1f\u5c82\u6709\u4e0d\u61c2\u671d\u5ef7\u5f8b\u4f8b\uff0c\u80e1\u4e71\u63b3\u4eba\u62f7\u6253\u4e4b\u7406\uff1f\u4f60\u8fd9\u5206\u660e\u662f\u5192\u8ba4\u88f4\u76f8\u56fd\u4e4b\u540d\u3002\u201d \u3000\u3000\u88f4\u660c\u8fd9\u65f6\u4e0d\u7531\u5f97\u8d77\u4e86\u7591\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u771f\u662f\u6349\u9519\u4eba\u4e86\uff1f\u201d\u95ee\u9053\uff1a\u201c\u4eca\u5e74\u4e09\u6708\u4e4b\u95f4\uff0c \u4f60\u5728\u5df4\u5dde\u5417\uff1f\u201d\u5f20\u4e4b\u5947\u8d1f\u6c14\u8bf4\u9053\uff1a\u201c\u5728\u53c8\u600e\u6837\uff1f\u4e0d\u5728\u53c8\u600e\u6837\uff1f\u201d\u88f4\u660c\u9053\uff1a\u201c\u5e9f\u592a\u5b50\u674e\u8d24 \u88ab\u4eba\u523a\u6740\uff0c\u4f60\u77e5\u9053\u8fd9\u4e8b\u4e48\uff1f\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u8fd9\u4e8b\u4e0e\u6211\u4f55\u5173\uff1f\u201d\u4ed6\u5bf9\u88f4\u660c\u7684\u8eab\u4efd\u4e5f\u662f\u731c\u6d4b \u4e0d\u900f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u66fe\u542c\u4eba\u8bf4\u8fc7\uff0c\u5e9f\u592a\u5b50\u662f\u7ed9\u5929\u540e\u4e0b\u8bcf\u8d50\u6b7b\u7684\uff0c\u8fd9\u4eba\u8bf4\u662f\u4ed6\u88ab\u523a\u6740\uff0c \u83ab\u975e\u771f\u6709\u6b64\u4e8b\uff1f\u4f46\u8fd9\u4e8b\u53c8\u600e\u80fd\u7275\u8fde\u5230\u6211\u7684\u8eab\u4e0a\u6765\uff1f\u201d\u88f4\u660c\u76ef\u4e86\u4ed6\u4e00\u773c\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u542c\u8bf4 \u4f60\u5bf9\u5e9f\u592a\u5b50\u88ab\u6697\u6740\u7684\u4e8b\uff0c\u751a\u662f\u4e0d\u5e73\uff1f\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u82e5\u7136\u771f\u6709\u6b64\u4e8b\uff0c\u6211\u5f53\u7136\u8981\u4e3a\u5e9f\u592a\u5b50 \u4e0d\u5e73\uff01\u201d\u88f4\u660c\u51b7\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u60f3\u8fdb\u4eac\u544a\u5bc6\uff0c\u4f60\u8fd8\u6562\u4e0d\u8ba4\u4f60\u662f\u674e\u9038\u4e48\uff1f\u201d \u3000\u3000\u5f20\u4e4b\u5947\u867d\u7136\u4e0d\u77e5\u9053\u5176\u4e2d\u9519\u7efc\u590d\u6742\u7684\u60c5\u8282\uff0c\u4f46\u8fd9\u65f6\u5374\u4e5f\u731c\u5230\u4e86\u4ed6\u662f\u8ba4\u9519\u4e86\u4eba\uff0c\u8fde\u5fd9\u53eb \u9053\uff1a\u201c\u5927\u4e08\u592b\u5750\u4e0d\u66f4\u540d\uff0c\u884c\u4e0d\u6539\u59d3\uff0c\u6211\u662f\u5d4b\u5c71\u7684\u75c5\u5c09\u8fdf\u5f20\u4e4b\u5947\uff0c\u8c01\u8bc6\u4f60\u4ec0\u4e48\u674e\u9038\uff01\u201d\u88f4 \u660c\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u59d3\u5f20\uff0c\u4f60\u7684\u8bd1\u540d\u53eb\u505a\u75c5\u5c09\u8fdf\uff1f\u201d\u7a0b\u901a\u7741\u5927\u4e86\u773c\u775b\uff0c\u679c\u7136\u770b\u51fa\u4e86\u6709 \u4e9b\u4e0d\u50cf\uff0c\u4f46\u4ed6\u6015\u88f4\u660c\u602a\u4ed6\u63d0\u9519\u4e86\u4eba\uff0c\u786c\u7740\u5934\u76ae\u8bf4\u9053\uff1a\u201c\u6211\u5728\u5ce8\u5d4b\u91d1\u9876\u548c\u4ed6\u671d\u8fc7\u76f8\uff0c\u7edd\u6ca1 \u6709\u8ba4\u9519\u4eba\u4e4b\u7406\u3002\u4f60\u77a7\u4ed6\u6ee1\u9762\u75c5\u5bb9\uff0c\u6b63\u662f\u4e2d\u4e86\u900f\u7a74\u795e\u9488\u4e4b\u540e\uff0c\u6bd2\u6027\u53d1\u4f5c\uff01\u867d\u7ecf\u540d\u533b\u8c03\u6cbb\uff0c \u4ecd\u7559\u4e0b\u6bd2\u6c81\u76ae\u80a4\u7684\u75c5\u8c61\u3002\u54fc\uff0c\u4f60\u4ee5\u4e3a\u8fd9\u6837\u5c31\u53ef\u4ee5\u7792\u8fc7\u6211\u7684\u773c\u775b\u4e48\uff1f\u201d\u5f20\u4e4b\u5947\u5927\u6012\u9053\uff1a \u201c\u5478\uff0c\u6211\u751f\u6765\u4fbf\u662f\u8fd9\u4ed8\u76f8\u8c8c\uff0c\u8981\u4e0d\u7136\u6c5f\u6e56\u8c6a\u6770\u600e\u4f1a\u9001\u7ed9\u6211\u8fd9\u4e2a\u75c5\u5c09\u8fdf\u7684\u7ef0\u53f7\uff1f\u4eca\u5e74\u4e09\u6708\uff0c \u6211\u4e5f\u4e0d\u5728\u5df4\u5dde\uff0c\u4f60\u4eec\u8ba4\u9519\u4eba\u5566\uff0c\u8001\u5b50\u59d3\u5f20\uff0c\u4e0d\u662f\u59d3\u674e\uff01\u4f60\u4eec\u786c\u8981\u5f20\u51a0\u674e\u6234\u4e48\uff1f\u201d \u3000\u3000\u88f4\u660c\u51b7\u51b7\u7684\u671b\u4e86\u5f20\u4e4b\u5947\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4e0a\u4eac\u505a\u4ec0\u4e48\uff1f\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u5929\u540e\u6311\u9009\u795e\u6b66 \u8425\u536b\u58eb\uff0c\u6211\u662f\u5d4b\u5c71\u90e1\u5b88\u4fdd\u8350\u53bb\u5e94\u8bd5\u7684\uff0c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u6211\u8eab\u4e0a\u8fd8\u6709\u5d4b\u5c71\u90e1\u5b88\u7684\u4fdd\u8350\u6587\u4e66\u3002\u201d \u7a0b\u901a\u5140\u81ea\u53eb\u9053\uff1a\u201c\u5458\u5916\u522b\u4fe1\u4ed6\u7684\u80e1\u8bf4\u516b\u9053\uff0c\u4ed6\u660e\u660e\u4fbf\u662f\u674e\u9038\uff0c\u600e\u4f1a\u59d3\u5f20\uff1f\u201d \u3000\u3000\u5ffd\u5730\u6709\u4e00\u6b66\u58eb\u5306\u5306\u8d70\u5165\uff0c\u5411\u88f4\u660c\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u961f\u9a6c\u961f\u8fdb\u4e86\u6751\u5e84\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u8def\u9053\uff1f\u201d \u90a3\u4e2a\u4eac\u5b98\u5413\u5f97\u515a\u8eab\u98a4\u6218\uff0c\u6e58\u6e58\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u6765\u5f97\u8fd9\u822c\u5feb\uff1f\u5feb\uff0c\u5feb\u6d3e\u4eba\u518d\u53bb\u6253\u542c\uff0c\u662f\u957f\u5b89 \u6765\u7684\uff0c\u8fd8\u662f\u53bf\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u88f4\u660c\u53cc\u773c\u5706\u7741\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u8fd9\u53ae\u662f\u59d3\u5f20\u8fd8\u662f\u59d3\u674e\uff0c\u4ed6\u8981\u505a\u6b66\u5219\u5929\u7684\u5974\u624d\uff0c\u54b1 \u4eec\u4fbf\u5bb9\u4ed6\u4e0d\u5f97\u3002\u7a0b\u901a\uff0c\u4f60\u7559\u4e0b\u6765\u770b\u5b88\u4ed6\uff0c\u4ed4\u7ec6\u641c\u4e00\u641c\u4ed6\uff0c\u518d\u7b49\u5019\u6211\u7684\u53d1\u843d\u3002\u7edd\u4e0d\u80fd\u8ba9\u4ed6 \u8dd1\u4e86\u3002\u201d\u7a0b\u901a\u5e94\u4e86\u4e00\u58f0\u3002\u88f4\u660c\u62c9\u7740\u90a3\u4e2a\u4eac\u5b98\uff0c\u7a81\u7136\u5728\u5899\u58c1\u4e0a\u4e00\u6309\uff0c\u58c1\u4e0a\u5f00\u4e86\u4e00\u9053\u5c0f\u95e8\uff0c \u4e00\u5e72\u4eba\u7b49\uff0c\u7acb\u523b\u8fdb\u4eba\u590d\u58c1\uff0c\u58c1\u4e0a\u7684\u95e8\u4e5f\u7acb\u5373\u5173\u4e0a\u3002\u5927\u5385\u91cc\u9664\u4e86\u5f20\u4e4b\u5947\u4e4b\u5916\uff0c\u4fbf\u53ea\u7559\u4e0b\u4e86 \u7a0b\u901a\u4e0e\u53e6\u5916\u4e00\u4f4d\u6b66\u58eb\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u674e\u9038\u8f6c\u4e86\u51e0\u4e2a\u5ff5\u5934\uff0c\u4ed6\u672c\u6765\u60f3\u7ee7\u7eed\u8ffd\u8e2a\u88f4\u660c\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5f20\u4e4b\u5947\u4ee3\u4ed6 \u53d7\u8fc7\uff0c\u53c8\u89c9\u5f97\u4e8e\u5fc3\u4e0d\u5fcd\uff0c\u4e0d\u9519\uff0c\u5f20\u4e4b\u5947\u5165\u4eac\u662f\u4e3a\u4e86\u5e94\u9009\u795e\u6b66\u8425\u7684\u536b\u58eb\uff0c\u662f\u548c\u81ea\u5df1\u654c\u5bf9\u7684 \u4eba\uff0c\u53ef\u662f\u4ed6\u8fd9\u573a\u7978\u4e8b\uff0c\u4e43\u662f\u56e0\u81ea\u5df1\u800c\u8d77\uff0c\u5927\u4e08\u592b\u505a\u4e8b\u8be5\u5149\u660e\u78ca\u843d\uff0c\u5c82\u53ef\u4e3a\u4e86\u8ba8\u538c\u4ed6\u4fbf\u8ba9 \u4ed6\u5e73\u767d\u8499\u51a4\uff1f \u3000\u3000\u674e\u9038\u6b63\u81ea\u5fc3\u601d\u4e0d\u5b9a\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u88c2\u4eba\u5fc3\u80ba\u7684\u60e8\u53eb\uff0c\u539f\u6765\u662f\u7a0b\u901a\u7a81\u7136\u4e0b\u4e86\u624b\uff0c\u5c06\u5f20\u4e4b \u5947\u7684\u7435\u7436\u9aa8\u634f\u788e\u4e86\u3002\u7a0b\u901a\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\uff0c\u4fdd\u9669\u4ed6\u9003\u8dd1\u4e0d\u4e86\u3002\u4e09\u54e5\uff0c\u4f60\u641c \u4ed6\u7684\u8eab\u5b50\u3002\u201d \u3000\u3000\u7a0b\u901a\u7b11\u58f0\u672a\u7edd\uff0c\u5ffd\u89c1\u4ed6\u7684\u540c\u4f34\u4e00\u8f83\u683d\u5012\uff0c\u7a0b\u901a\u6b66\u529f\u8f83\u9ad8\uff0c\u5fc3\u77e5\u6709\u5f02\uff0c\u7acb\u5373\u659c\u8dc3\u6570\u6b65\uff0c \u53ea\u542c\u5f97\u201c\u5506\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u5757\u5c4b\u74e6\u98de\u6765\uff0c\u63b7\u843d\u5730\u4e0a\uff0c\u788e\u6210\u51e0\u7247\u3002\u5c4b\u4e0a\u7a81\u7136\u8df3\u4e0b\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u7a0b\u901a\u5927\u5403\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u662f\u8c01\uff1f\u201d\u674e\u9038\u51fa\u624b\u5982\u7535\uff0c\u624b\u81c2\u4e00\u4f38\uff0c\u6293\u7740\u4ed6\u80a9\u4e0a\u7684 \u7435\u7436\u9aa8\uff0c\u6c89\u58f0\u559d\u9053\uff1b\u201c\u778e\u773c\u7684\u72d7\u624d\uff0c\u6211\u4fbf\u662f\u674e\u9038\uff01\u201d\u529b\u900f\u6307\u5c16\uff0c\u7528\u529b\u4e00\u634f\uff0c\u767b\u65f6\u4e5f\u628a\u7a0b \u901a\u7684\u7435\u7436\u9aa8\u634f\u788e\uff0c\u7a0b\u901a\u4e00\u58f0\u60e8\u53eb\uff0c\u6655\u6b7b\u8fc7\u53bb\u3002 \u3000\u3000\u674e\u9038\u4e00\u770b\uff0c\u5f20\u4e4b\u5947\u6b63\u75db\u5f97\u5728\u5730\u4e0a\u6253\u6eda\uff0c\u5df2\u5728\u660f\u8ff7\u7684\u72b6\u6001\u4e4b\u4e2d\uff0c\u674e\u9038\u65e0\u6687\u65bd\u6551\uff0c\u4fe1\u624b \u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u6682\u65f6\u53ef\u4ee5\u4ee4\u4ed6\u4e0d\u81f3\u5927\u91cf\u6d41\u8840\uff0c\u968f\u5373\u5c06\u4ed6\u80cc\u8d77\uff0c\u8dd1\u4e0b\u53f0\u9636\uff0c\u53ea\u542c\u5f97\u5916\u9762\u9a6c \u5636\u4eba\u53eb\uff0c\u88f4\u5bb6\u7684\u5bb6\u4e01\u90fd\u5df2\u8dd1\u5230\u56ed\u4e2d\uff0c\u767b\u4e0a\u56f4\u5899\u9632\u5fa1\u3002\u674e\u9038\u4e00\u8def\u5954\u51fa\uff0c\u65e0\u4eba\u963b\u62e6\uff0c\u5230\u4e86\u56ed \u4e2d\uff0c\u4f46\u89c1\u5b98\u519b\u5df2\u7834\u95e8\u800c\u5165\uff0c\u4e3a\u9996\u7684\u4e00\u5458\u6b66\u5c06\u53eb\u9053\uff1a\u201c\u5feb\u53eb\u88f4\u660c\u524d\u6765\u63a5\u65e8\uff01\u201d\u5927\u558a\u4e09\u58f0\uff0c \u65e0\u4eba\u7b54\u5e94\uff0c\u5b98\u519b\u9646\u7eed\u51b2\u5165\uff0c\u88f4\u5bb6\u7684\u6b66\u58eb\u5728\u90a3\u4e2a\u7ba1\u5bb6\u7387\u9886\u4e4b\u4e0b\uff0c\u594b\u529b\u62d2\u6355\uff0c\u90a3\u5c06\u5b98\u5927\u559d\u9053\uff1a \u201c\u88f4\u708e\u8c0b\u53cd\uff0c\u5927\u9006\u4e0d\u9053\uff0c\u4f60\u4eec\u60f3\u8ddf\u7740\u4ed6\u9001\u6b7b\u4e48\uff1f\u201d\u8fd9\u4e00\u559d\u767b\u65f6\u628a\u88f4\u5bb6\u7684\u5bb6\u4e01\u6b66\u58eb\u559d\u6563\u4e86 \u4e00\u534a\u3002 \u3000\u3000\u88f4\u5bb6\u7684\u5bb6\u4e01\u6b66\u58eb\u867d\u7136\u6563\u4e86\u4e00\u534a\uff0c\u4f46\u88f4\u708e\u7acb\u5fc3\u8c0b\u53cd\uff0c\u5bb6\u4e2d\u65e9\u5df2\u517b\u6709\u4e00\u6279\u5fc3\u8179\u6b7b\u58eb\uff0c\u4e2a \u4e2a\u6b66\u827a\u9ad8\u5f3a\uff0c\u8fd9\u6279\u4eba\u5374\u6ca1\u6709\u6563\u53bb\uff0c\u5c31\u5728\u82b1\u56ed\u91cc\u548c\u5b98\u519b\u6df7\u6218\u8d77\u6765\u3002\u674e\u9038\u4f0f\u5728\u540e\u9762\uff0c\u542c\u5f97\u6740 \u58f0\u5982\u96f7\uff0c\u706b\u5149\u8000\u773c\uff0c\u65f6\u4e0d\u65f6\u6709\u60e8\u5389\u7684\u53eb\u58f0\u5212\u8fc7\u957f\u7a7a\uff0c\u53ae\u6740\u8d8a\u6765\u8d8a\u6fc0\u70c8\uff0c\u5b98\u519b\u8d8a\u6765\u8d8a\u8feb\u8fd1\u3002 \u674e\u9038\u6697\u53eb\u4e00\u58f0\uff1a\u201c\u82e6\u4e5f\uff01\u201d\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u5bf9\u4e24\u65b9\u90fd\u662f\u654c\u4eba\uff0c\u5b9e\u662f\u4e0d\u6613\u7a81\u56f4\u800c\u51fa\u3002\u5ffd\u5730\u4e00 \u652f\u51b7\u7bad\u5c04\u6765\uff0c\u674e\u9038\u80cc\u7740\u5f20\u4e4b\u5947\u95ea\u8eab\u4e00\u907f\uff0c\u5f20\u4e4b\u5947\u89e6\u52a8\u4f24\u5904\uff0c\u75db\u5f97\u201c\u54c7\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c \u674e\u9038\u53ea\u597d\u7eb5\u8eab\u8df3\u51fa\uff0c\u88f4\u5bb6\u7684\u603b\u7ba1\u4e00\u773c\u77a5\u89c1\u5f20\u4e4b\u5947\u4f0f\u5728\u4ed6\u7684\u80cc\u4e0a\uff0c\u5927\u54c8\u4e00\u60ca\uff0c\u6025\u5fd9\u53eb\u9053\uff1a \u201c\u5feb\u628a\u8fd9\u4e24\u4eba\u6740\u4e86\uff01\u201d\u539f\u6765\u4ed6\u628a\u5f20\u4e4b\u5947\u5f53\u4f5c\u674e\u9038\uff0c\u5374\u628a\u674e\u9038\u5f53\u6210\u6b66\u5219\u5929\u6d3e\u6765\u7684\u9ad8\u624b\uff0c\u4ed6 \u77e5\u9053\u4e3b\u4eba\u6700\u6015\u7684\u5c31\u662f\u674e\u9038\u8fdb\u4eac\u544a\u5bc6\uff0c\u8bf4\u51fa\u88f4\u708e\u6d3e\u9063\u523a\u5ba2\u6697\u6740\u592a\u5b50\u7684\u4e8b\u60c5\uff0c\u6545\u6b64\u867d\u7136\u5904\u5728 \u5b98\u519b\u731b\u6251\u7684\u5371\u9669\u60c5\u51b5\u4e4b\u4e0b\uff0c\u4ecd\u7136\u5206\u51fa\u4eba\u6765\uff0c\u8981\u5c06\u674e\u9038\u4e0e\u5f20\u4e4b\u5947\u6740\u6b7b\u706d\u53e3\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u674e\u9038\u521a\u521a\u4e00\u811a\u8e0f\u51fa\uff0c\u4fbf\u542c\u5f97\u5237\u7684\u4e00\u58f0\uff0c\u4e00\u53e3\u957f\u5251\u8fce\u9762\u523a\u6765\uff0c\u674e\u9038 \u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u4f7f\u51fa\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u62db\u6570\uff0c\u5728\u90a3\u4eba\u7684\u864e\u53e3\u4e00\u6263\uff0c\u5c06\u90a3\u4eba\u7684\u957f\u5251 \u593a\u8fc7\uff0c\u7529\u624b\u4e00\u63b7\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u63d2\u8fdb\u4e86\u53e6\u4e00\u4e2a\u6b66\u58eb\u7684\u80f8\u819b\uff0c\u811a\u6b65\u4e0d\u505c\uff0c\u7acb\u523b\u5411\u4eba\u5c11\u7684 \u5730\u65b9\u786c\u95ef\u3002 \u3000\u3000\u731b\u542c\u5f97\u80cc\u540e\u91d1\u5200\u5288\u98ce\u4e4b\u58f0\uff0c\u6765\u52bf\u6025\u52b2\uff0c\u674e\u904d\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u88f4\u5bb6\u8fd8\u6709\u8fd9\u7b49 \u9ad8\u624b\uff01\u201d\u4ed6\u65e9\u5df2\u62d4\u51fa\u5b9d\u5251\uff0c\u7acb\u5373\u4e00\u62db\u201c\u82cf\u79e6\u80cc\u5251\u201d\uff0c\u53cd\u624b\u4e00\u524a\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c \u706b\u82b1\u56db\u6e85\uff0c\u674e\u9038\u80cc\u4e0a\u6709\u4eba\uff0c\u8df3\u8dc3\u4e0d\u7075\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u7684\u5200\u950b\u65ab\u4e2d\uff0c\u811a\u8ddf\u672a\u8d70\uff0c\u90a3\u4eba\u65e9\u5df2\u8fc5\u5373 \u6362\u62db\uff0c\u7b2c\u4e8c\u5200\u53c8\u8ddf\u8e2a\u5288\u5230\u3002 \u3000\u3000\u674e\u9038\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u628a\u80cc\u4e0a\u7684\u5f20\u4e4b\u5947\u8f6c\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u731b\u7684\u957f\u5251\u52d2\u4f4f\uff0c\u90a3\u4eba\u7684\u5200 \u53e3\u6b63\u597d\u65ab\u5728\u4ed6\u7684\u5251\u4e0a\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u5207\u7389\u4e4b\u58f0\uff0c\u90a3\u4eba\u7684\u539a\u80cc\u65ab\u5c71\u5200\u7adf\u7136\u7f3a\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u674e\u9038\u8ddf\u7740\u4e00\u62db\u201c\u8155\u5e95\u7ffb\u4e91\u201d\uff0c\u5251\u5149\u75be\u8d77\uff0c\u4f46\u8fd9\u4e00\u62db\u51fa\u624b\u867d\u5feb\uff0c\u5982\u6ca1\u6709\u523a\u7740\u90a3\u4eba\uff0c\u674e \u9038\u62ac\u5934\u4e00\u770b\uff0c\u539f\u6765\u8fd9\u4e2a\u548c\u4ed6\u529b\u654c\u4e09\u62db\u7684\u6c49\u5b50\uff0c\u5c31\u662f\u90a3\u4e2a\u7ba1\u5bb6\u3002\u88f4\u5bb6\u7684\u7ba1\u5bb6\u540d\u53eb\u718a\u767d\u5c71\uff0c \u672c\u662f\u7eff\u6797\u5927\u76d7\u51fa\u8eab\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u7b97\u5f97\u662f\u4e00\u6d41\u597d\u624b\uff0c\u8fd9\u65f6\u89c1\u674e\u9038\u80cc\u4e0a\u6709\u4eba\uff0c\u5251\u6cd5\u5c45\u7136\u8fd8\u662f \u90a3\u4e48\u51cc\u5389\uff0c\u5fc3\u4e2d\u5927\u5403\u4e00\u60ca\uff0c\u53ef\u662f\u4ed6\u6e9c\u6ed1\u5f97\u5f88\uff0c\u4e00\u89c1\u4e0d\u80fd\u529b\u654c\uff0c\u7acb\u523b\u5c55\u5f00\u6e38\u8eab\u516b\u5366\u5200\u7684\u5200 \u6cd5\uff0c\u6b3a\u8d1f\u674e\u9038\u8df3\u8dc3\u4e0d\u7075\uff0c\u4e00\u5200\u7d27\u4f3c\u4e00\u5200\uff0c\u53ea\u662f\u671d\u5f20\u4e4b\u5947\u8eab\u4e0a\u65ab\u53bb\u3002 \u3000\u3000\u674e\u9038\u53ea\u8981\u5c06\u5f20\u4e4b\u5947\u6254\u53bb\uff0c\u7acb\u5373\u53ef\u4ee5\u53cd\u8d25\u4e3a\u80dc\uff0c\u4ed6\u5fc3\u5ff5\u65b9\u52a8\uff0c\u968f\u5373\u60f3\u9053\uff1a\u201c\u4e0d\u53ef\uff0c\u4e0d \u53ef\u3002\u4ed6\u867d\u7136\u8981\u53bb\u6295\u5954\u6b66\u5219\u5929\uff0c\u6309\u8bf4\u4e43\u6211\u654c\u4eba\uff0c\u4f46\u6211\u82e5\u4e34\u5371\u5f03\u4ed6\uff0c\u5374\u4e5f\u4e0d\u662f\u82f1\u96c4\u884c\u5f84\u3002\u201d \u4e8e\u662f\u773c\u795e\u6ce8\u5b9a\u654c\u4eba\u7684\u5200\u950b\uff0c\u5904\u5904\u5148\u4fdd\u62a4\u80cc\u4e0a\u7684\u5f20\u4e4b\u5947\uff0c\u6fc0\u6218\u4e2d\u718a\u767d\u5c71\u4f7f\u4e86\u4e00\u62db\u865a\u62db\uff0c\u5411 \u5f20\u4e4b\u5947\u6302\u7740\u7684\u53cc\u811a\u4e00\u5200\u524a\u53bb\uff0c\u674e\u9038\u88ab\u8feb\u5f97\u4f7f\u4e86\u201c\u6e14\u7fc1\u5782\u9493\u201d\uff0c\u957f\u5251\u5782\u4e0b\u62db\u67b6\uff0c\u718a\u767d\u5c71\u731b \u5730\u559d\u4e00\u58f0\u201c\u7740\uff01\u201d\u201c\u4e0b\u624b\u5200\u201d\u7a81\u7136\u6539\u6210\u4e86\u201c\u4e0a\u624b\u5200\u201d\uff0c\u5200\u5149\u970d\u5730\u4e00\u8f6c\uff0c\u4ece\u674e\u9038\u7684\u80a9\u4e0a\u524a \u8fc7\u3002 \u3000\u3000\u8fd9\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u679a\u94b1\u9556\u88ad\u6765\uff0c\u6b63\u6b63\u6253\u4e2d\u718a\u767d\u5c71\u7684\u624b\u8155\uff0c\u718a\u767d\u5c71 \u5200\u950b\u4e00\u504f\uff0c\u659c\u5288\u800c\u4e0b\uff0c\u6ca1\u6709\u65ab\u4e2d\u674e\u9038\uff0c\u674e\u9038\u817e\u5730\u98de\u8d77\u4e00\u811a\uff0c\u6b63\u4e2d\u5fc3\u7a9d\uff0c\u718a\u767d\u5c71\u54ea\u91cc\u7981\u53d7 \u5f97\u8d77\uff0c\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u767b\u65f6\u6251\u5012\u3002 \u3000\u3000\u90a3\u4e2a\u7528\u94b1\u9556\u6697\u88ad\u718a\u767d\u5c71\u7684\u4eba\uff0c\u4e43\u662f\u5fa1\u6797\u519b\u4e2d\u7684\u4e00\u4e2a\u7edf\u9886\uff0c\u9886\u547d\u6765\u67e5\u6284\u88f4\u5bb6\u7684\u3002\u4ed6\u89c1 \u674e\u9038\u5c06\u718a\u767d\u5c71\u51fb\u5012\uff0c\u9887\u4e3a\u8be7\u5f02\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u8c01\uff1f\u53ef\u662f\u5929\u540e\u6d3e\u6765\u7684\u4e48\uff1f\u201d\u674e\u9038\u811a \u6b65\u4e0d\u505c\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\u4ece\u4ed6\u8eab\u8fb9\u63a0\u8fc7\uff0c\u90a3\u4eba\u5374\u4e5f\u673a\u8b66\uff0c\u4e00\u89c1\u4e0d\u5bf9\uff0c\u7acb\u523b\u53d1\u51fa\u4e09\u679a\u94b1\u9556\uff0c \u90fd\u7ed9\u674e\u9038\u7684\u5b9d\u5251\u62e8\u843d\u4e86\u3002 \u3000\u3000\u8fd1\u7740\u674e\u9038\u7684\u4e24\u4e2a\u5fa1\u6797\u519b\u519b\u5b98\uff0c\u6025\u5fd9\u8fce\u5934\u62e6\u622a\uff0c\u4e00\u4e2a\u4f7f\u4e09\u8282\u68cd\uff0c\u4e00\u4e2a\u4f7f\u5927\u6865\u5200\uff0c\u674e\u9038 \u6beb\u4e0d\u7406\u4f1a\uff0c\u76f4\u51b2\u8fc7\u53bb\uff0c\u90a3\u4e24\u4eba\u559d\u9053\uff1a\u201c\u4f60\u60f3\u9001\u547d\u4e48\uff0c\u4ed6\u4eec\u89c1\u674e\u9038\u63a5\u8fde\u51fb\u5012\u51e0\u4e2a\u88f4\u5bb6\u7684\u6b66 \u58eb\uff0c\u6349\u6478\u4e0d\u900f\u4ed6\u7684\u8eab\u4efd\u3002\u7565\u4e00\u8e0c\u8e87\uff0c\u674e\u9038\u5df2\u51b2\u5230\u4ed6\u4eec\u7684\u9762\u524d\uff0c\u957f\u5251\u4e00\u62ab\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0 \u628a\u90a3\u6839\u4e09\u8282\u5f88\u5f53\u4e2d\u622a\u65ad\uff1b\u4f7f\u5927\u5200\u7684\u4e00\u5200\u5288\u53bb\uff0c\u5288\u4e86\u4e2a\u7a7a\uff0c\u674e\u9038\u65e9\u5df2\u4ece\u4ed6\u7684\u8eab\u8fb9\u6e9c\u8fc7\u3002 \u3000\u3000\u90a3\u7edf\u9886\u53eb\u9053\uff1a\u201c\u4e0d\u7ba1\u662f\u8c01\uff0c\u5148\u628a\u4ed6\u62ff\u4e0b\u3002\u201d\u8fce\u9762\u7acb\u5373\u53c8\u662f\u4e24\u822c\u5175\u5668\u88ad\u6765\uff0c\u4e00\u67c4\u957f\u67aa\uff0c \u4e00\u6761\u94a2\u97ad\uff0c\u6765\u52bf\u90fd\u5f88\u6025\u52b2\u3002\u674e\u9038\u811a\u5c16\u4e00\u70b9\uff0c\u867d\u7136\u80cc\u7740\u4eba\uff0c\u4ecd\u80fd\u8dc3\u8d77\u4e00\u4e08\u591a\u9ad8\u3002\u5de6\u8fb9\u90a3\u4e2a \u519b\u5b98\u4e00\u97ad\u6253\u4e0b\uff0c\u521a\u597d\u7f20\u4e0a\u4e86\u540c\u4f34\u7684\u90a3\u70b3\u957f\u67aa\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u529b\u5927\u5982\u725b\uff0c\u5175\u5668\u4e00\u4ea4\uff0c\u6536\u4e0d\u4f4f \u52bf\uff0c\u90fd\u8dcc\u5012\u4e86\u3002\u674e\u9038\u843d\u4e0b\u6765\u65f6\u3002\u7b2c\u4e09\u4e2a\u519b\u5b98\u53c8\u4e3e\u5200\u5288\u5230\uff0c\u8fd9\u4eba\u6b66\u529f\u5e73\u5e38\uff0c\u88ab\u674e\u9038\u4e00\u5251\u5c06 \u4ed6\u7684\u5355\u5200\u524a\u65ad\uff0c\u5251\u5c16\u4e00\u8f6c\uff0c\u987a\u624b\u4fbf\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u674e\u9038\u5c55\u5f00\u98d8\u5ffd\u65e0\u5b9a\u7684\u8eab\u5f62\uff0c\u5de6\u8fb9\u4e00\u515c\uff0c\u53f3\u9762\u4e00\u7ed5\uff0c\u970e\u5ffd\u5411\u4e1c\uff0c\u970e\u5ffd\u5411\u897f\uff0c\u65e2\u907f\u5f00\u5fa1 \u6797\u519b\u7684\u62e6\u622a\uff0c\u4e5f\u907f\u5f00\u88f4\u5bb6\u6b66\u571f\u7684\u8ffd\u51fb\uff0c\u770b\u770b\u5c31\u8981\u62a2\u5230\u540e\u95e8\uff0c\u731b\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u8fce\u9762\u4e00\u6839\u9f99\u820c\u5927\u67aa\u6311\u6765\uff0c\u4f46\u89c1\u4ed6\u67aa\u5c16\u4e71\u98a4\uff0c\u6296\u8d77\u7897\u5927\u7684\u67aa\u82b1\uff0c\u4e00\u6839\u957f\u67aa\u5c31\u50cf\u5316\u6210\u4e86\u4e00\u7247 \u67aa\u6797\uff0c\u5c06\u674e\u9038\u7684\u53bb\u8def\u5b8c\u5168\u5c01\u4f4f\u3002\u674e\u9038\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u5fa1\u6797\u519b\u4e2d\u7ade\u6709\u8fd9\u6837\u7684\u9ad8\u624b\uff01\u201d\u6025\u5fd9\u8fd0 \u8db3\u771f\u529b\uff0c\u53cd\u624b\u4e00\u5251\u524a\u51fa\uff0c\u201c\u53ee\u201d\u201c\u5f53\u201d\u4e24\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u4e24\u4eba\u90fd\u7ed9\u9707\u9000\u4e09\u6b65\uff0c\u539f\u6765\u8fd9\u4e2a \u519b\u5b98\u4e43\u662f\u7edf\u7387\u5fa1\u6797\u519b\u7684\u9f99\u9a91\u90fd\u5c09\u7ae0\u5927\u7ee5\u3002 \u3000\u3000\u674e\u9038\u4e0d\u60f3\u604b\u6218\uff0c\u7ffb\u8eab\u659c\u8dc3\uff0c\u6070\u597d\u4e00\u4e2a\u88f4\u5bb6\u7684\u6b66\u58eb\u8ffd\u5230\u4ed6\u7684\u8eab\u65c1\uff0c\u674e\u9038\u5de6\u624b\u4e00\u4f38\uff0c\u5c06 \u90a3\u4e2a\u6b66\u571f\u7684\u80cc\u5fc3\u6293\u7740\uff0c\u8fce\u98ce\u4e00\u821e\uff0c\u731b\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u63a5\u4f4f\uff01\u201d\u5c06\u88f4\u5bb6\u90a3\u4e2a\u6b66\u58eb\u5411\u7ae0\u5927\u7ee5 \u5288\u9762\u6454\u53bb\uff0c\u7ae0\u5927\u7f13\u89c1\u4ed6\u5c06\u88f4\u5bb6\u7684\u6b66\u58eb\u7528\u4f5c\u5175\u5668\uff0c\u5927\u51fa\u610f\u5916\uff0c\u4e0d\u77e5\u4ed6\u662f\u53cb\u662f\u654c\uff0c\u767e\u5fd9\u4e2d\u53ea \u5f97\u5148\u628a\u6b66\u58eb\u6253\u7ffb\uff0c\u5c31\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c\u674e\u9038\u53c8\u5df2\u5241\u4f24\u4e86\u597d\u51e0\u4e2a\u4eba\uff0c\u51b2\u5230\u4e86\u82b1\u56ed\u7684\u540e\u95e8\u3002\u7ae0 \u5927\u7ee5\u6025\u5fd9\u633a\u67aa\u8ffd\u6765\uff0c\u674e\u9038\u5927\u53eb\u9053\uff1a\u201c\u88f4\u660c\u5df2\u4ece\u540e\u9762\u7684\u5c71\u8def\u9003\u8d70\u4e86\uff0c\u4f60\u4eec\u4e0d\u53bb\u7f09\u62ff\u94a6\u72af\uff0c \u5374\u6765\u8ffd\u6211\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ae0\u5927\u7ef6\u5e26\u6765\u7684\u5fa1\u6797\u519b\uff0c\u5927\u90e8\u90fd\u7528\u6765\u56f4\u653b\u5e9c\u90e7\uff0c\u540e\u5c71\u867d\u7136\u6709\u4eba\u628a\u5b88\uff0c\u6570\u91cf\u4e0d\u591a\u3002\u8fd9\u65f6 \u5ffd\u7136\u542c\u8bf4\u88f4\u660c\u5df2\u4ece\u540e\u9762\u7684\u5c71\u8def\u9003\u8d70\uff0c\u4e0d\u7981\u970d\u7136\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u82e5\u7136\u88ab\u94a6 \u72af\u9003\u5165\u5c71\u4e2d\uff0c\u641c\u7d22\u786e\u662f\u4e0d\u6613\uff0c\u8fd9\u5012\u4e0d\u53ef\u4e0d\u9632\u3002\u201d\u8fd9\u65f6\u5fa1\u6797\u519b\u5df2\u628a\u88f4\u5bb6\u4e01\u6b66\u58eb\u6253\u5f97\u4e03\u96f6\u516b \u4e71\uff0c\u6709\u4e00\u4e9b\u5c1a\u5728\u56ed\u4e2d\u6df7\u6218\uff0c\u6709\u4e00\u4e9b\u5df2\u9003\u4e86\u51fa\u6765\uff0c\u5fa1\u6797\u519b\u6709\u5982\u6f6e\u6d8c\uff0c\u6b63\u5728\u95ef\u8fdb\u5c4b\u5185\u641c\u67e5\uff0c \u7ae0\u5927\u7ee5\u6025\u5fd9\u4f20\u4e0b\u547d\u4ee4\uff0c\u8c03\u51fa\u4e00\u90e8\u4efd\u4eba\u6765\uff0c\u706b\u901f\u5230\u540e\u5c71\u589e\u9632\u3002 \u3000\u3000\u7ae0\u5927\u7ef6\u6b63\u5fd9\u4e8e\u8c03\u5175\u9063\u5c06\uff0c\u65e0\u6687\u53bb\u8ffd\u6355\u674e\u9038\uff0c\u674e\u9038\u4fbf\u8d81\u4ed6\u4eec\u4e71\u7cdf\u7cdf\u7684\u5f53\u53e3\uff0c\u6740\u51fa\u82b1\u56ed\uff0c \u62a2\u4e86\u4e00\u5339\u6218\u9a6c\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4fbf\u5728\u7530\u91ce\u95f4\u75be\u9a70\u800c\u53bb\uff0c\u540e\u9762\u867d\u7136\u6709\u51e0\u9a91\u8ffd\u6765\uff0c\u5374\u88ab\u674e\u9038\u63a5\u8fc7 \u4ed6\u4eec\u5c04\u6765\u7684\u51b7\u7bad\uff0c\u53cd\u624b\u7529\u51fa\uff0c\u5c06\u4ed6\u4eec\u90fd\u5c04\u5012\u4e86\u3002 \u3000\u3000\u674e\u9038\u8dd1\u4e86\u4e00\u7a0b\uff0c\u4f0f\u5730\u4e00\u542c\uff0c\u542c\u4e0d\u5230\u8ffd\u9a91\u7684\u8e44\u58f0\uff0c\u677e\u4e86\u53e3\u6c14\uff01\u8df3\u4e0b\u9a6c\u80cc\uff0c\u5c06\u5f20\u4e4b\u5947\u62b1 \u8d77\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4ed6\u9762\u5982\u91d1\u7eb8\uff0c\u53cc\u773c\u5fae\u5fae\u5f00\u542f\uff0c\u674e\u9038\u4e00\u542c\u4ed6\u7684\u8109\u606f\uff0c\u5e78\u559c\u5185\u810f\u6ca1\u6709\u53d7 \u4f24\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u5c06\u5f20\u4e4b\u5947\u62b1\u8fdb\u6811\u6797\u91cc\u9762\uff0c\u9009\u4e86\u4e00\u7247\u5e73\u5766\u7684\u8349\u5730\uff0c\u5c06\u4ed6\u653e \u4e0b\u3002\u674e\u9038\u968f\u8eab\u5e26\u6709\u91d1\u521b\u5723\u836f\uff0c\u66ff\u4ed6\u6577\u4e0a\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u770b\u4f24\u53e3\u7684\u8840\u5df2\u7ecf\u51dd\u7ed3\uff0c\u4fbf\u66ff\u4ed6\u89e3\u5f00 \u7a74\u9053\u3002\u5f20\u4e4b\u5947\u60a0\u60a0\u9192\u8f6c\uff0c\u89c1\u6551\u4ed6\u6027\u547d\u7684\u4eba\uff0c\u539f\u6765\u5c31\u662f\u9152\u8086\u4e2d\u76f8\u4f1a\u7684\u201c\u5bd2\u5112\u201d\uff0c\u6709\u70b9\u8be7\u5f02\uff0c \u8bf4\u9053\uff1a\u201c\u539f\u6765\u5148\u751f\u662f\u8eab\u6000\u7edd\u6280\u7684\u9ad8\u4eba\uff0c\u5931\u656c\u5931\u656c\uff0c\u6551\u547d\u4e4b\u6069\uff0c\u94ed\u611f\u4e94\u7533\uff0c\u8bf7\u6055\u6211\u4e0d\u80fd\u8d77 \u8eab\u62dc\u8c22\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u5f20\u5144\uff0c\u4f60\u7684\u4f24\u53ea\u662f\u5916\u4f24\uff0c\u8c03\u517b\u51e0\u65e5\uff0c\u5f53\u53ef\u65e0\u4e8b\uff0c\u4e0d\u5fc5\u62c5\u5fc3\u3002\u201d\u5f20 \u4e4b\u5947\u6068\u6068\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u6211\u8fd9\u8eab\u6b66\u529f\u5df2\u88ab\u5e9f\u4e86\uff0c\u54fc\uff0c\u54fc\uff01\u60f3\u4e0d\u5230\u5d4b\u5c71\u5f20\u4e4b\u5947\u7adf\u5e73\u7a7a\u906d\u5230\u4e86 \u8fd9\u573a\u6a2a\u7978\uff0c\u6b64\u4ec7\u6b64\u6068\uff0c\u4eca\u751f\u96be\u62a5\uff0c\u6b7b\u4e0d\u7791\u76ee\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u6b64\u4ec7\u65e9\u5df2\u6709\u4eba\u66ff\u4f60\u62a5\u4e86\u3002\u201d \u5f20\u4e4b\u5947\u9053\uff1a\u201c\u662f\u5148\u751f\u3001\u4f60\u3001\u4f60\u628a\u90a3\u8001\u8d3c\u6740\u4e86\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u662f\u5b98\u519b\u6740\u6765\uff0c\u60f3 \u6765\u90a3\u8001\u8d3c\u4e5f\u662f\u9003\u4e0d\u8131\u7684\u3002\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u4ed6\u4eec\u771f\u662f\u9020\u53cd\u7684\u9006\u8d3c\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u5927\u7ea6\u662f \u5427\u3002\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u5929\u540e\u5723\u660e\uff0c\u6211\u867d\u4e0d\u80fd\u4e3a\u5979\u6548\u72ac\u9a6c\u4e4b\u52b3\uff0c\u8fd9\u53e3\u51a4\u6c14\u4e5f\u53ef\u6cc4 \u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u542c\u4ed6\u53e3\u53e3\u58f0\u58f0\u9a82\u201c\u9006\u8d3c\u201d\u9882\u201c\u5929\u540e\u201d\uff0c\u5fc3\u4e2d\u6781\u4e0d\u8212\u670d\uff0c\u82e5\u4e0d\u662f\u89c1\u4ed6\u53d7\u4f24\uff0c\u51e0\u4e4e \u5fcd\u4e0d\u4f4f\u8981\u6253\u4ed6\u4e00\u5df4\u638c\uff0c\u5f53\u4e0b\u5ff5\u5934\u4e00\u8f6c\uff0c\u5fc3\u610f\u529b\u51b3\uff0c\u5fcd\u7740\u6c14\u95ee\u9053\uff1a\u201c\u5f20\u5144\u5165\u4eac\uff0c\u6240\u4e3a\u4f55\u4e8b\uff1f\u201d \u4ed6\u8fd9\u662f\u660e\u77e5\u6545\u95ee\u3002\u5f20\u4e4b\u5947\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6069\u516c\u95ee\u53ca\uff0c\u4e0d\u6562\u4e0d\u544a\uff0c\u5929\u540e\u6311\u9009\u795e\u6b66\u8425\u536b \u58eb\uff0c\u6211\u662f\u5d4b\u5c71\u90e1\u5b88\u4fdd\u8350\u53bb\u5e94\u8bd5\u7684\u3002\u5440\uff0c\u5982\u4eca\u6211\u7684\u7435\u7436\u9aa8\u5df2\u88ab\u53cd\u8d3c\u634f\u788e\uff0c\u6b66\u529f\u5168\u5e9f\uff0c\u8fd9\u5927 \u597d\u7684\u524d\u7a0b\uff0c\u4e5f\u4ece\u6b64\u6bc1\u4e86\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u90b5\u5b88\u7684\u4fdd\u8350\u6587\u4e66\uff0c\u5f20\u5144\u5e26\u5728\u8eab\u4e0a\u5427\uff1f\u201d\u5f20\u4e4b\u5947\u9053\uff1a \u201c\u73b0\u5728\u8fd8\u8981\u5b83\u4f55\u7528\uff1f\u201d\u6296\u6296\u7d22\u7d22\u7684\u5728\u8eab\u4e0a\u6478\u51fa\u90a3\u5f20\u6587\u4e66\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u54ac\u4e00\u54ac\u7259\uff0c\u53cc\u624b\u4e00 \u626f\uff0c\u4fbf\u60f3\u628a\u5b83\u6495\u70c2\uff0c\u674e\u9038\u5fc3\u6025\u773c\u5feb\uff0c\u8fde\u5fd9\u5c06\u90a3\u4ef6\u4e49\u4e66\u62a2\u8fc7\u624b\u4e2d\u3002 \u3000\u3000\u5f20\u4e4b\u5947\u53f9\u9053\uff1a\u201c\u6069\u516c\uff0c\u4f60\u4f55\u5fc5\u8fd8\u4e3a\u6211\u73cd\u60dc\u8fd9\u7eb8\u6587\u4e66\uff0c\u6211\u4eca\u751f\u4eca\u4e16\uff0c\u518d\u4e5f\u7528\u4e0d\u7740\u5b83\u4e86\u3002 \u7559\u7740\u5b83\u53ea\u6709\u4f24\u5fc3\u3002\u201d\u674e\u9038\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5409\u4eba\u5929\u76f8\uff0c\u4e5f\u8bb8\u5f20\u5144\u5c06\u6765\u80fd\u591f\u6062\u590d\u6b66\u529f\u5462\uff1f\u201d\u5f20 \u4e4b\u5947\u9053\uff1a\u201c\u90a3\u9664\u975e\u662f\u534e\u9640\u518d\u4e16\uff0c\u6241\u9e4a\u91cd\u751f\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u9ad8\u4eba\u5f02\u58eb\uff0c\u65e0\u4ee3\u65e0\u4e4b\u3002\u5f53\u4eca\u4e4b \u4e16\uff0c\u600e\u89c1\u5f97\u5c31\u6ca1\u6709\u534e\u9640\u6241\u9e4a\uff1f\u201d\u5f20\u4e4b\u5947\u60e8\u7b11\u9053\uff1a\u201c\u9ad8\u4eba\u5f02\u58eb\uff0c\u53ef\u9047\u800c\u4e0d\u53ef\u6c42\u3002\u4f55\u51b5\uff0c\u5373 \u4fa5\u5e78\u9047\u540d\u533b\uff0c\u6211\u7684\u7435\u7436\u9aa8\u5df2\u7ecf\u788e\u4e86\uff0c\u6700\u5c11\u4e5f\u5f97\u6570\u5e74\uff0c\u624d\u80fd\u518d\u7ec3\u6b66\u529f\u3002\u5929\u540e\u8fd9\u4e2a\u6708\u4fbf\u8981\u6311 \u9009\u795e\u6b66\u8425\u536b\u58eb\uff0c\u8fd9\u7eb8\u6587\u4e66\uff0c\u8fd8\u6709\u4f55\u7528\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u5144\u65e2\u7136\u6267\u610f\u4e0d\u8981\u8fd9\u7eb8\u6587\u4e66\uff0c\u90a3\u672b \u6211\u6597\u80c6\u6c42\u4f60\uff0c\u5c06\u5b83\u8f6c\u9001\u7ed9\u6211\u5982\u4f55\uff1f\u201d\u5f20\u4e4b\u5947\u8be7\u9053\uff1a\u201c\u4f60\u8981\u5b83\u4f55\u7528\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u6709\u4e00 \u4e2a\u5f1f\u5f1f\uff0c\u8eab\u6750\u76f8\u8c8c\u4e0e\u6211\u4eff\u4f5b\uff0c\u4e5f\u7565\u61c2\u4e00\u70b9\u6b66\u529f\uff0c\u53ef\u60dc\u65e0\u4eba\u4fdd\u8350\u3002\u6709\u6b64\u673a\u4f1a\uff0c\u6211\u60f3\u53eb\u4ed6\u53bb \u8bd5\u4e00\u8bd5\u3002\u5c06\u6765\u82e5\u80fd\u535a\u5f97\u4e00\u5b98\u534a\u804c\uff0c\u5168\u62dc\u543e\u5144\u6240\u8d50\uff0c\u6211\u4ea6\u611f\u540c\u8eab\u53d7\u4e86\u3002\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u6211 \u8fd9\u6761\u6027\u547d\u4e43\u662f\u6069\u516c\u6551\u7684\uff0c\u518d\u751f\u4e4b\u5fb7\uff0c\u788e\u9aa8\u7c89\u8eab\uff0c\u4e0d\u8db3\u56fe\u62a5\uff0c\u4f55\u51b5\u662f\u8eab\u5916\u4e4b\u7269\uff0c\u4f55\u51b5\u662f\u8fd9 \u4ef6\u5bf9\u6211\u5168\u65e0\u7528\u5904\u7684\u4e00\u7eb8\u6587\u4e66\uff01\u4e0d\u8fc7\u5929\u540e\u6cd5\u5ea6\u751a\u4e25\uff0c\u4f46\u6015\u5c06\u6765\u67e5\u51fa\uff0c\u8fde\u7d2f\u4eca\u5f1f\u3002\u201d\u674e\u9038\u9053\uff1a \u201c\u5c06\u6765\u662f\u7978\u662f\u798f\uff0c\u4e43\u662f\u4ed6\u547d\u4e2d\u6ce8\u5b9a\uff0c\u4e5f\u8bb8\u4ed6\u7acb\u4e86\u519b\u529f\uff0c\u867d\u7136\u67e5\u51fa\uff0c\u5929\u540e\u4e5f\u5bbd\u6055\u4ed6\u5462\uff1f\u5c06 \u6765\u4e8b\u53d1\u4e4b\u65f6\uff0c\u4f60\u5c31\u8bf4\u6587\u4e66\u88ab\u4eba\u52ab\u53bb\uff0c\u6211\u53e6\u5916\u6559\u820d\u5f1f\u4e00\u5957\u53e3\u4f9b\uff0c\u51b3\u4e0d\u81f3\u62d6\u7d2f\u9601\u4e0b\u4fbf\u662f\u3002\u201d \u5f20\u4e4b\u5947\u6168\u7136\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u820d\u4e86\u65e0\u7528\u4e4b\u7269\uff0c\u800c\u6709\u6210\u4eba\u4e4b\u7678\uff0c\u4f55\u4e50\u800c\u4e0d\u4e3a\uff1f\u6211\u7d22\u6027 \u4e0d\u56de\u5d4b\u5c71\uff0c\u8eb2\u5230\u5916\u5dde\u7684\u670b\u53cb\u5bb6\u4e2d\uff0c\u4e07\u4e00\u6709\u4eba\u76d8\u67e5\uff0c\u6211\u4e00\u53e3\u54ac\u5b9a\u662f\u7ed9\u5f3a\u4eba\u62a2\u53bb\u7684\u4fbf\u662f\u4e86\u3002 \u6211\u7684\u7435\u7436\u9aa8\u634f\u788e\uff0c\u6b63\u597d\u4f5c\u4e2a\u8bc1\u660e\u3002\u4ee4\u5f1f\u82e5\u88ab\u67e5\u5230\uff0c\u53e3\u4f9b\u53ef\u8bf4\u662f\u4ece\u5f3a\u4eba\u624b\u4e2d\u8f6c\u62a2\u8fc7\u6765\u7684\u3002 \u5373\u7b97\u5c06\u6765\u5230\u91d1\u6bbf\u5bf9\u8d28\u6211\u4e5f\u4e00\u5b9a\u5e2e\u4ee4\u5f1f\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u674e\u9038\u5bf9\u5f20\u4e4b\u5947\u672c\u6765\u9887\u4e3a\u8ba8\u538c\uff0c\u8fd9\u65f6\u89c1\u4ed6\u6069\u6028\u5206\u660e\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u867d\u7136\u5229\u7984\u718f\u5fc3\uff0c \u60f3\u4e0a\u4eac\u94bb\u8425\u53bb\u505a\u6b66\u5219\u5929\u7684\u5974\u624d\uff0c\u4f46\u5374\u4e5f\u4e0d\u5931\u4e3a\u4e00\u4e2a\u597d\u4eba\u3002\u6211\u7528\u8c0e\u8bdd\u9a97\u4ed6\u7684\u4e1c\u897f\uff0c\u5012\u89c9\u5f97 \u6709\u70b9\u60ed\u6127\u4e86\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u5c31\u5feb\u5929\u4eae\u3002\u5929\u4eae\u4e4b\u540e\uff0c\u519c\u592b\u6a35\u5b50\u51fa\u6765\u8015\u4f5c\uff0c\u6211\u5144\u53ef\u4ee5 \u547c\u6551\uff0c\u4f60\u8981\u94f6\u5b50\u4f7f\u7528\u5417\uff1f\u201d\u5f20\u4e4b\u5947\u9053\uff1a\u201c\u6211\u8eab\u4e0a\u7684\u94f6\u5b50\u8fd8\u672a\u7ed9\u641c\u53bb\uff0c\u591a\u8c22\u4f60\u4e86\u3002\u201d\u5f20\u4e4b \u5947\u5bf9\u674e\u9038\u7684\u820d\u4ed6\u800c\u53bb\uff0c\u6709\u70b9\u4e0d\u5feb\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u82e5\u7136\u4ed6\u966a\u4f34\u81ea\u5df1\uff0c\u5c06\u6765\u4e8b\u53d1\u4e4b\u65f6\uff0c\u96be\u4fdd \u4e0d\u53d7\u7275\u8fde\uff0c\u5982\u6b64\u4e00\u60f3\uff0c\u53cd\u800c\u50ac\u674e\u9038\u5feb\u8d70\u3002\u674e\u9038\u5012\u6709\u70b9\u820d\u4e0d\u5f97\uff0c\u5f53\u4e0b\u95ee\u4e86\u4ed6\u60f3\u53bb\u4f9d\u9760\u7684\u670b \u53cb\u7684\u5730\u5740\uff0c\u51c6\u5907\u5c06\u6765\u627e\u540d\u533b\u66ff\u4ed6\u533b\u6cbb\uff0c\u4e0d\u8fc7\u6b64\u4e8b\u6e3a\u832b\uff0c\u6545\u6b64\u674e\u9038\u5c31\u4e0d\u9884\u5148\u8bf4\u4e86\u3002 \u3000\u3000\u674e\u9038\u79bb\u5f00\u4e86\u5f20\u4e4b\u5947\u4e4b\u540e\uff0c\u75be\u8dd1\u4e00\u7a0b\uff0c\u5929\u8272\u6e10\u53d1\u4eae\uff0c\u674e\u9038\u5728\u4e00\u4e2a\u5c0f\u6eaa\u65c1\u8fb9\u6b47\u8db3\uff0c\u626f\u53bb \u80e1\u9aef\uff0c\u7528\u6eaa\u6c34\u6d17\u8138\uff0c\u518d\u6d82\u4e0a\u53ef\u4ee4\u9762\u8272\u7126\u9ec4\u7684\u6613\u5bb9\u4e39\uff0c\u4e34\u6d41\u4e00\u987e\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u5f20\u51a0\u674e\u6234\u5165\u957f\u5b89 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u5f20\u51a0\u674e\u6234\u5165\u957f\u5b89 \u3000\u3000\u674e\u9038\u4e34\u6d41\u81ea\u7167\uff0c\u53ea\u89c1\u6eaa\u4e2d\u73b0\u5f71\uff0c\u5df2\u662f\u53e6\u4e00\u526f\u989c\u5bb9\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u771f \u771f\u5047\u5047\uff0c\u5047\u5047\u771f\u771f\uff0c\u8fd9\u6613\u5bb9\u4e39\u771f\u662f\u5999\u6781\uff0c\u6628\u65e5\u5f20\u4e4b\u5947\u88ab\u4eba\u5f53\u4f5c\u662f\u6211\uff0c\u4eca\u540e\u6211\u8981\u88ab\u4eba\u5f53\u4f5c \u662f\u5f20\u4e4b\u5947\u4e86\u3002\u201d\u4e09\u65e5\u4e4b\u540e\uff0c\u674e\u9038\u8d76\u5230\u957f\u5b89\uff0c\u4f46\u89c1\u5c4b\u5b87\u8fde\u4e91\uff0c\u9cde\u6b21\u76f8\u6bd4\uff0c\u5e02\u8086\u55a7\u56a3\uff0c\u767e\u8d27 \u5145\u65a5\uff0c\u8857\u4e0a\u884c\u4eba\uff0c\u6469\u80a9\u64e6\u80cc\uff0c\u597d\u4e00\u6d3e\u8c6a\u534e\u6c14\u8c61\uff0c\u679c\u7136\u80dc\u4f3c\u4ece\u524d\u3002\u674e\u9038\u5fc3\u4e2d\u5341\u5206\u611f\u6168\uff0c\u5f53 \u4e0b\u5148\u5230\u4e00\u95f4\u5ba2\u5e97\u4f4f\u4e0b\uff0c\u6362\u8fc7\u4e86\u4e00\u5957\u6b66\u58eb\u7684\u670d\u9970\uff0c\u56e0\u4e3a\u5f20\u4e4b\u5947\u7ef0\u53f7\u75c5\u5c09\u8fdf\uff0c\u4f7f\u7684\u5175\u5668\u662f\u4e00 \u6839\u94a2\u97ad\u548c\u4e00\u67c4\u9752\u94dc\u5251\uff0c\u81ea\u5df1\u7684\u5b9d\u5251\u4e0d\u4fbf\u9732\u670d\uff0c\u4fbf\u53e6\u5916\u518d\u53bb\u7f6e\u529e\u4e86\u8fd9\u4e24\u4ef6\u5175\u5668\uff0c\u5f85\u5f97\u8bf8\u4e8b \u529e\u59a5\uff0c\u7136\u540e\u5411\u795e\u6b66\u8425\u62a5\u5230\u3002 \u3000\u3000\u795e\u6b66\u8425\u7684\u90fd\u5c09\u3002\u672c\u540d\u53eb\u505a\u9ed1\u9f7f\u660e\u4e4b\uff0c\u4e43\u662f\u5927\u5c06\u6c5f\u5357\u9053\u603b\u7ba1\u9ed1\u9f7f\u5e38\u4e4b\u7684\u5f1f\u5f1f\uff0c\u4ed6\u4eec\u4e00 \u5bb6\u672c\u662f\u80e1\u4eba\uff0c\u5510\u592a\u5b97\u674e\u4e16\u6c11\u8d77\u5175\u6253\u5929\u4e0b\u4e4b\u65f6\uff0c\u7528\u4e86\u8bb8\u591a\u80e1\u4eba\uff0c\u4ed6\u4eec\u4e00\u5bb6\u5c61\u7acb\u519b\u529f\uff0c\u5230\u5510 \u9ad8\u5b97\u674e\u6cbb\u6c38\u9686\u5e74\u95f4\uff0c\u4efb\u7528\u9ed1\u9f7f\u660e\u4e4b\u4e3a\u5fa1\u6797\u519b\u7684\u9f99\u9a91\u90fd\u5c09\uff0c\u8d50\u59d3\u4e3a\u674e\uff0c\u81f3\u6b66\u5219\u5929\u767b\u4f4d\uff0c\u5bf9 \u4ed6\u4ecd\u7136\u91cd\u7528\uff0c\u8c03\u4e3a\u795e\u6b66\u8425\u7684\u90fd\u5c09\uff0c\u795e\u6b66\u8425\u7b49\u4e8e\u7687\u5e1d\u7684\u4eb2\u519b\uff0c\u5e73\u65f6\u628a\u5b88\u5bab\u5ef7\uff0c\u6218\u65f6\u6248\u4ece\u5723 \u9a7e\uff0c\u6bd4\u5fa1\u6797\u519b\u8fd8\u8981\u63a5\u8fd1\uff0c\u6240\u4ee5\u90fd\u662f\u5404\u5dde\u4fdd\u8350\u6765\u7684\uff0c\u65e2\u6709\u672c\u9886\u800c\u53c8\u53ef\u9760\u7684\u4eba\u3002\u674e\u9038\u524d\u5f80\u62a5 \u5230\uff0c\u8425\u5b98\u9a8c\u8fc7\u4ed6\u7684\u4fdd\u8350\u6587\u4e66\uff0c\u518d\u5bf9\u8fc7\u5d4b\u5c71\u90e1\u5b88\u9884\u5148\u9001\u6765\u7684\u56fe\u50cf\uff0c\u9a8c\u8fc7\u5bf9\u8fc7\uff01\u5e76\u65e0\u7834\u7efd\uff0c \u4fbf\u5373\u7740\u674e\u9038\u5728\u8425\u4e2d\u4f4f\u4e0b\uff0c\u7b49\u5019\u9009\u62d4\u3002\u8fd9\u6b21\u8981\u8865\u5145\u4e00\u767e\u540d\u795e\u6b66\u8425\u536b\u58eb\uff0c\u5404\u5dde\u53bf\u4fdd\u8350\u6765\u7684\u5171 \u6709\u4e8c\u767e\u591a\u4eba\uff0c\u5927\u7ea6\u662f\u4e24\u4e2a\u4eba\u4e2d\u5f55\u53d6\u4e00\u4eba\uff0c\u673a\u4f1a\u751a\u5927\uff0c\u4ee5\u674e\u9038\u7684\u6b66\u529f\uff0c\u81ea\u7136\u6781\u6709\u628a\u63e1\u3002\u4ed6 \u6240\u62c5\u5fc3\u7684\uff0c\u53ea\u662f\u600e\u6837\u624d\u80fd\u628a\u81ea\u5df1\u7684\u672c\u9886\u663e\u9732\u5f97\u6070\u5230\u597d\u5904\uff1f\u82e5\u662f\u8fc7\u4e8e\u60ca\u4eba\uff0c\u5f15\u8d77\u6ce8\u610f\uff0c\u82e5 \u662f\u5e73\u5e73\u5eb8\u5eb8\uff0c\u90a3\u53c8\u6015\u4e0d\u80fd\u5165\u9009\u4e86\u3002 \u3000\u3000\u5230\u4e86\u9009\u6280\u8003\u8bd5\u90a3\u4e00\u5929\uff0c\u674e\u660e\u4e4b\u4eb2\u81ea\u4e3b\u6301\uff0c\u6bcf\u4e00\u4e2a\u5148\u8bd5\u666e\u901a\u7684\u5f13\u9a6c\u529f\u592b\uff0c\u8fd9\u4e00\u9879\u4e8c\u767e \u591a\u4eba\u5168\u90fd\u5408\u683c\uff1b\u7136\u540e\u518d\u8bd5\u5341\u516b\u822c\u6b66\u827a\u4e2d\u5e94\u8bd5\u8005\u6700\u64c5\u957f\u7684\u4e00\u4e24\u79cd\uff0c\u6700\u540e\u662f\u95ee\u5e94\u8bd5\u8005\u6709\u4ec0\u4e48 \u7279\u957f\u7684\u6280\u80fd\uff0c\u4ee5\u4fbf\u5c06\u6765\u5728\u5206\u914d\u804c\u4f4d\u65f6\u91cf\u624d\u5f55\u7528\u3002\u674e\u9038\u5e94\u8bd5\u7684\u540d\u6b21\u6392\u5728\u4e2d\u95f4\uff0c\u4ed6\u770b\u5404\u5dde\u53bf \u4fdd\u8350\u6765\u7684\u6b66\u58eb\uff0c\u5f13\u9a6c\u867d\u7136\u5ae1\u719f\uff0c\u5176\u4e2d\u6b66\u827a\u8d85\u7fa4\u4e4b\u871a\uff0c\u5374\u662f\u5be5\u5be5\u53ef\u6570\u3002\u770b\u4e86\u4e00\u4f1a\uff0c\u53ea\u6709\u6cb3 \u5357\u79b9\u53bf\u7684\u4e00\u4e2a\u6b66\u4e3e\u6700\u4e3a\u53ef\u53d6\uff0c\u4ed6\u8868\u6f14\u7684\u662f\u795e\u7bad\u529f\u592b\uff0c\u6b63\u9762\u4e09\u7bad\uff0c\u53cd\u624b\u4e09\u7bad\uff0c\u90fd\u4e2d\u7ea2\u5fc3\uff0c \u518d\u53eb\u4e00\u4e2a\u4eba\u4ece\u4ed6\u80cc\u540e\u8fde\u53d1\u4e09\u7bad\uff0c\u4ed6\u5728\u9a6c\u80cc\u4e0a\u5934\u4e5f\u4e0d\u56de\uff0c\u542c\u5230\u5bf9\u65b9\u7684\u5f13\u5f26\u4e00\u54cd\uff0c\u4fbf\u7acb\u5373\u53cd \u624b\u5c04\u51fa\uff0c\u5c4a\u7136\u628a\u5bf9\u65b9\u6240\u5c04\u7684\u4e09\u652f\u5229\u7bad\u2014\u4e00\u78b0\u843d\uff0c\u7bad\u9528\u78b0\u7740\u7bad\u9ab8\uff0c\u6beb\u65e0\u5dee\u9519\uff0c\u535a\u5f97\u6ee1\u573a\u7684 \u91c7\u58f0\u3002\u4f46\u5728\u674e\u9038\u770b\u6765\uff0c\u9664\u4e86\u7bad\u5c04\u5f97\u51c6\u4e4b\u5916\uff0c\u4e0d\u8fc7\u52a0\u4e0a\u4e86\u201c\u542c\u98ce\u8fa8\u5668\u201d\u7684\u672c\u9886\u800c\u5df2\uff0c\u4e5f\u4e0d \u89c9\u5f97\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\u3002\u4e0d\u8fc7\uff0c\u674e\u9038\u6015\u5f15\u4eba\u6ce8\u76ee\uff0c\u4e5f\u968f\u548c\u7740\u4f17\u4eba\u559d\u91c7\u3002\u63a5\u4e0b\u53bb\u662f\u6c5f\u897f\u6cf0\u548c\u53bf \u4e00\u4e2a\u6b66\u4e3e\u4eba\u8868\u6f14\u94c1\u817f\u529f\u592b\uff0c\u674e\u660e\u4e4b\u5429\u5490\u5728\u6821\u573a\u4e0a\u7ad6\u8d77\u6728\u6869\uff0c\u9877\u523b\u95f4\u642c\u6765\u4e86\u5341\u6839\u7897\u53e3\u822c\u7c97 \u5927\u7684\u67af\u6728\uff0c\u6bcf\u6839\u957f\u8fbe\u516b\u5c3a\uff0c\u4e00\u4e2a\u6b66\u58eb\u8d70\u4e86\u51fa\u6765\uff0c\u62b1\u8d77\u4e00\u6839\u6728\u67f1\uff0c\u5f80\u5730\u4e0b\u53e3\u6309\uff0c\u6728\u67f1\u9f50\u8170 \u63d2\u5165\u5730\u4e2d\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u5730\u4e0a\u5c31\u7ad6\u8d77\u4e86\u5341\u6839\u6728\u6869\uff0c\u6574\u6574\u9f50\u9f50\uff0c\u6392\u6210\u4e00\u5217\uff0c\u5e94\u9009\u7684\u5404\u53bf\u82f1\u96c4 \u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u90a3\u6c5f\u897f\u6b66\u4e3e\u4eba\u7684\u94c1\u817f\u529f\u592b\u672b\u66fe\u8868\u6f14\uff0c\u4e0d\u77e5\u5982\u4f55\uff0c\u8fd9\u6b66\u58eb\u7684\u624b\u52b2\u5374\u662f\u975e\u540c\u5c0f \u53ef\u3002 \u3000\u3000\u90a3\u6b66\u4e3e\u4eba\u5411\u4e3b\u8003\u5b98\u97a0\u4e86\u4e2a\u8eac\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8981\u628a\u8fd9\u5341\u6839\u6728\u6869\u8e22\u65ad\uff0c\u82e5\u6709\u4e00\u6839\u4e0d\u65ad\uff0c\u7518 \u5fc3\u53d7\u9edc\u3002\u201d\u8bf4\u7f62\u6765\u5230\u67cf\u6728\u6869\u524d\uff0c\u53f3\u817f\u4e00\u5f39\uff0c\u53ea\u542c\u5f97\u5693\u7684\u4e00\u58f0\uff0c\u7b2c\u4e00\u6839\u6728\u6869\u9732\u5728\u5730\u9762\u7684\u90e8 \u4efd\uff0c\u767b\u65f6\u65ad\u4e86\uff0c\u90a3\u4eba\u8ddf\u8eab\u8fdb\u6b65\uff0c\u5de6\u817f\u4e00\u6a2a\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u7b2c\u4e8c\u6839\u6728\u6869\u53c8\u5012\uff0c\u4fbf\u5728\u559d\u91c7\u58f0\u4e2d\uff0c \u4e00\u8def\u8fde\u73af\u817f\u626b\u53bb\uff0c\u9877\u523b\u4e4b\u95f4\uff0c\u5341\u6839\u6728\u6869\u90fd\u88ab\u4ed6\u8e22\u65ad\uff0c\u5c31\u662f\u7528\u65a7\u5934\u6765\u780d\uff0c\u4e5f\u6ca1\u6709\u8fd9\u6837\u5bb9\u6613\uff0c \u767b\u65f6\u91c7\u58f0\u5982\u96f7\uff0c\u4e45\u4e45\u4e0d\u7edd\uff01 \u3000\u3000\u795e\u6b66\u90fd\u5c09\u674e\u660e\u4e4b\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053:\u201c\u5f39\u817f\u529f\u592b\uff0c\u7ec3\u5230\u8fd9\u6837\uff0c\u5f88\u4e0d\u5bb9\u6613\u4e86\u3002\u201d\u5728\u4ed6\u7684\u540d \u5b57\u4e0a\u625b\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u90a3\u6b66\u4e3e\u4eba\u6ee1\u6000\u9ad8\u5174\uff0c\u674e\u660e\u4e4b\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u80fd\u628a\u5730\u4e0b\u7684\u90a3\u4e00\u6bb5\u6728\u6869\u62d4 \u8d77\u6765\u5417\uff1f\u201d\u90a3\u6b66\u4e3e\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u6e58\u6e58\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\uff0c\u6211\uff0c\u6211\u672a\u8bd5\u8fc7\u2026\u2026\u201d\u674e\u660e\u4e4b \u4e00\u6325\u624b\uff0c\u53eb\u4ed6\u968f\u8eab\u7684\u4e00\u4e2a\u536b\u58eb\u51fa\u6765\uff0c\u4f46\u89c1\u4ed6\u4fef\u8170\u4e00\u6293\uff0c\u7acb\u523b\u5c06\u57cb\u5728\u5730\u4e0b\u7684\u4e00\u6bb5\u6728\u6869\u62d4\u4e86 \u51fa\u6765\uff0c\u624b\u6cd5\u53c8\u5feb\u53c8\u51c6\uff0c\u4e5f\u662f\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u4fbf\u628a\u5341\u6839\u6728\u6869\u5168\u90fd\u6293\u8d77\u3002\u8fd9\u56de \u8fde\u674e\u9038\u4e5f\u81ea\u6709\u70b9\u5403\u60ca\uff0c\u8981\u77e5\u8fd9\u6837\u6293\u8d77\u6728\u6869\u8981\u6bd4\u8e22\u5012\u6728\u6869\u4f55\u6b62\u8270\u8f9b\u5341\u500d\uff0c\u674e\u660e\u4e4b\u8fd9\u4e2a\u536b\u58eb \u4f7f\u7684\u4e43\u662f\u5927\u529b\u9e70\u722a\u529f\u592b\u3002 \u3000\u3000\u674e\u660e\u4e4b\u5bf9\u90a3\u6b66\u4e3e\u4eba\u9053\uff1a\u201c\u4f60\u5f55\u53d6\u4e86\uff0c\u5c31\u5728\u4ed6\u624b\u4e0b\u505a\u4e2a\u5c0f\u961f\u957f\u5427\uff0c\u95f2\u65f6\u4e5f\u53ef\u4ee5\u8ddf\u4ed6\u518d \u7ec3\u7ec3\u529f\u592b\u3002\u201d\u539f\u6765\u4ed6\u89c1\u8fd9\u6b66\u4e3e\u4eba\u9762\u6709\u9a84\u8272\uff0c\u6545\u610f\u8981\u632b\u6298\u4e00\u4e0b\u4ed6\u7684\u6c14\u7130\uff0c\u514d\u5f97\u5c06\u6765\u505a\u4ed6\u957f \u5b98\u7684\u4eba\u96be\u4e8e\u9a7e\u9a6d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u4eba\u4e1b\u4e2d\u6709\u4eba\u53d1\u7b11\u3002\u674e\u660e\u4e4b\u53eb\u90a3\u4e2a\u4eba\u51fa\u6765\uff0c\u95ee\u9053\uff1a\u201c\u83ab\u975e\u4f60\u6709\u66f4\u9ad8 \u660e\u7684\u672c\u9886\u4e48\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u8fd8\u672a\u8f6e\u5230\u6211\u5e94\u8bd5\u3002\u201d\u674e\u660e\u4e4b\u9053\uff1a\u201c\u51c6\u4f60\u73b0\u5728\u5c31\u8bd5\u3002\u201d\u90a3\u4eba\u8981 \u4e86\u4e24\u5347\u7eff\u8c46\uff0c\u9519\u5728\u5730\u4e0a\uff0c\u5728\u7eff\u8c46\u4e0a\u8f7b\u8f7b\u7684\u8e0f\u7740\u65b9\u6b65\uff0c\u8d70\u4e86\u4e00\u5708\uff0c\u5168\u573a\u9759\u5f97\u8fde\u4e00\u6839\u9488\u8dcc\u5728 \u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\uff0c\u4e2a\u4e2a\u7741\u5927\u4e86\u773c\u775b\uff0c\u539f\u6765\u7eff\u8c46\u7ecf\u90a3\u4e2a\u4eba\u8e0f\u8fc7\uff0c\u90fd\u53d8\u6210\u4e86\u8c46\u7c89\uff0c\u8fd9\u79cd\u5185\u5bb6 \u529f\u592b\uff0c\u6bd4\u8d77\u6293\u8d77\u6728\u6869\uff0c\u90a3\u53c8\u8981\u8270\u96be\u5f97\u591a\u4e86\u3002\u674e\u9038\u5fc3\u9053\uff1a\u201c\u5728\u5df2\u5e94\u8bd5\u7684\u8bf8\u4eba\u4e4b\u4e2d\uff0c\u5f53\u4ee5\u8fd9 \u4eba\u7684\u6b66\u529f\u7b2c\u4e00\u4e86\u3002\u201d\u5411\u65c1\u4eba\u6253\u542c\uff0c\u59cb\u77e5\u9053\u4ed6\u662f\u6e56\u5357\u65b0\u5316\u53bf\u7684\u540d\u6b66\u5e08\u5468\u5927\u5e74\u3002 \u3000\u3000\u674e\u660e\u4e4b\u7b11\u9053\uff1a\u201c\u4f60\u6210\u7ee9\u5f88\u597d\uff0c\u4f46\u4f60\u80fd\u591f\u628a\u8fd9\u5730\u4e0a\u7684\u8c46\u7c89\uff0c\u4e00\u70b9\u4e0d\u5269\u90fd\u6536\u8d77\u6765\u5417\uff1f\u201d \u5468\u5927\u5e74\u4e00\u60f3\uff0c\u5373\u7528\u626b\u5e1a\u6765\u626b\uff0c\u4e5f\u672a\u5fc5\u90fd\u6536\u5f97\u4e7e\u4e7e\u51c0\u51c0\uff0c\u89c9\u5f97\u8fd9\u8bdd\u95ee\u5f97\u6709\u70b9\u53e4\u602a\uff0c\u4e00\u65f6\u4e4b \u95f4\uff0c\u672a\u6562\u56de\u7b54\uff0c\u674e\u660e\u4e4b\u62db\u4e00\u62db\u624b\uff0c\u53eb\u4ed6\u4fa7\u8fb9\u4e00\u4e2a\u6267\u638c\u5927\u65d7\u7684\u6b66\u58eb\u8fc7\u6765\u3002 \u3000\u3000\u674e\u660e\u4e4b\u5429\u5490\u9053\uff1a\u201c\u4f60\u628a\u5730\u4e0a\u7684\u8c46\u7c89\u90fd\u66ff\u6211\u6536\u62fe\u8d77\u6765\u3002\u201d\u90a3\u6b66\u58eb\u5e94\u4e86\u4e00\u58f0\uff1a\u201c\u9075\u547d\u3002\u201d \u5c06\u5927\u65d7\u4e00\u5377\uff0c\u79bb\u90a3\u9752\u7816\u5730\u9762\u7ea6\u6709\u4e09\u5c3a\uff0c\u5377\u8d77\u4e86\u4e00\u80a1\u65cb\u98ce\uff0c\u5982\u8679\u5438\u6c34\uff0c\u4f46\u89c1\u5730\u4e0a\u7684\u8c46\u7c89\u88ab \u65cb\u98ce\u5377\u6210\u4e86\u67f1\u72b6\uff0c\u5438\u8fdb\u4e86\u90a3\u7ffb\u817e\u7684\u65d7\u5f71\u4e4b\u4e2d\uff0c\u90a3\u6b66\u58eb\u5c06\u5927\u65d7\u4e00\u6536\uff0c\u5377\u4e86\u8d77\u6765\uff0c\u9752\u7816\u5730\u9762 \u6709\u5982\u626b\u8fc7\u4e00\u822c\uff0c\u4e7e\u4e7e\u51c0\u51c0\u3002\u90a3\u6b66\u571f\u8d70\u5230\u4e3b\u8003\u53f0\u524d\uff0c\u5411\u674e\u660e\u4e4b\u97a0\u4e86\u4e00\u8eac\uff0c\u9053\u58f0\uff1a\u201c\u7f34\u4ee4\u3002\u201d \u628a\u5927\u65d7\u518d\u4e00\u5c55\u5f00\uff0c\u53ea\u89c1\u8c46\u7c89\u5df2\u88ab\u5377\u6210\u4e00\u4e2a\u996d\u7897\u822c\u7c97\u539a\u7684\u7c89\u56e2\uff0c\u8dcc\u5728\u5730\u4e0a\uff0c\u5c45\u7136\u5e76\u4e0d\u6563\u5f00\u3002 \u3000\u3000\u674e\u9038\u770b\u5230\u73b0\u5728\uff0c\u8fd9\u624d\u5927\u5403\u4e00\u60ca\uff0c\u6e56\u5357\u90a3\u4e2a\u6b66\u5e08\u5c06\u7eff\u8c46\u8e0f\u6210\u7c89\u672a\uff0c\u5df2\u7ecf\u662f\u4e86\u4e0d\u8d77\u7684\u529f \u592b\uff0c\u8fd9\u4e2a\u6b66\u58eb\u80fd\u5c06\u672c\u8eab\u771f\u529b\u900f\u8fc7\u5927\u65d7\uff0c\u4e0d\u4f46\u5438\u8d77\u4e86\u5730\u4e0a\u7684\u8c46\u7c89\uff0c\u800c\u4e14\u80fd\u5c06\u8c46\u7c89\u538b\u6210\u7c89\u56e2\uff0c \u6bd4\u8d77\u5468\u5927\u5e74\u90a3\u624b\u529f\u592b\uff0c\u53c8\u4e0d\u77e5\u8981\u8270\u96be\u591a\u5c11\u500d\u4e86\u3002\u674e\u9038\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ee5\u8fd9\u4e2a\u6b66\u58eb\u7684\u529f\u592b\uff0c \u53ea\u6015\u6211\u4e5f\u4e0d\u80fd\u80dc\u4ed6\u3002\u6b66\u5219\u5929\u624b\u4e0b\u6709\u672c\u9886\u7684\u4eba\u770b\u6765\u4e0d\u5c11\uff0c\u6211\u5012\u4e0d\u53ef\u5c0f\u89d1\u4e86\u3002\u201d\u5411\u65c1\u4eba\u6253\u542c\uff0c \u59cb\u77e5\u8fd9\u4e2a\u6b66\u58eb\u4e43\u662f\u795e\u6b66\u8425\u4e2d\u4e09\u5927\u9ad8\u624b\u4e4b\u4e00\uff0c\u540d\u53eb\u79e6\u582a\uff0c\u53e6\u5916\u4e24\u4e2a\u9ad8\u624b\uff0c\u4e00\u4e2a\u53eb\u505a\u5f20\u633a\uff0c \u4fbf\u662f\u521a\u624d\u90a3\u4e2a\u62d4\u8d77\u6728\u6869\u7684\u4eba\uff0c\u8fd8\u6709\u4e00\u4e2a\u590d\u59d3\u897f\u95e8\uff0c\u5355\u540d\u4e3a\u9738\uff0c\u5374\u8fd8\u672a\u89c1\u9732\u9762\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5d4b\u5c71\u5f20\u4e4b\u5947\uff01\u201d\u539f\u6765\u5df2\u8f6e\u5230\u4ed6\u5e94\u8bd5\u3002\u674e\u9038\u5fc3\u4e2d\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u8d70\u5230 \u4e3b\u8003\u53f0\u524d\uff0c\u5411\u674e\u660e\u4e4b\u884c\u8fc7\u4e86\u793c\uff0c\u674e\u660e\u4e4b\u6253\u5f00\u540d\u518c\uff0c\u518c\u4e0a\u9644\u6709\u201c\u4ed6\u201d\u7684\u56fe\u50cf\u548c\u5173\u4e8e\u201c\u4ed6\u201d \u7684\u8d44\u6599\uff0c\u674e\u660e\u4e4b\u5bf9\u4e86\u4e00\u9635\u4e0d\u51fa\u4ec0\u4e48\u7834\u7efd\uff0c\u5fae\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u662f\u5d4b\u5c71\u53bf\u7684\u5f20\u4e4b\u5947\u3002\u6709\u4e2a\u7ef0\u53f7 \u53eb\u75c5\u5c09\u8fdf\uff0c\u662f\u5417\uff1f\u201d\u674e\u9038\u60f3\u4e0d\u5230\u540d\u518c\u4e0a\u8fde\u7ef0\u53f7\u4e5f\u5199\u4e86\u660e\u767d\uff0c\u53ea\u597d\u7b54\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\u3002\u674e \u660e\u4e4b\u9053\uff1a\u201c\u60f3\u5c09\u8fdf\u606d\u4e43\u662f\u5510\u671d\u5f00\u56fd\u7684\u5927\u5c06\uff0c\u4e00\u67c4\u6c34\u78e8\u94a2\u97ad\uff0c\u66fe\u6253\u8fc7\u5341\u516b\u8def\u53cd\u738b\uff0c\u4f60\u7ef0\u53f7 \u75c5\u5c09\u8fdf\uff0c\u60f3\u5fc5\u64c5\u957f\u97ad\u6cd5\u4e86\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u5c0f\u4eba\u7c97\u89e3\u51e0\u8def\u5251\u6cd5\uff0c\u8fd9\u75c5\u5c09\u8fdf\u4e09\u5b57\u4e43\u662f\u4e00\u73ed\u6b66 \u6797\u670b\u53cb\u5f00\u73a9\u7b11\u7ed9\u6211\u53d6\u7684\u3002\u201d\u674e\u660e\u4e4b\u770b\u4e86\u4e00\u4e0b\u6863\u6848\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e0a\u9762\u4e5f\u5199\u660e\u4f60\u80fd\u591f \u4f7f\u5251\u3002\u597d\u5427\uff0c\u4f60\u5c31\u65bd\u5c55\u4e00\u4e0b\u4f60\u7684\u97ad\u6cd5\u548c\u5251\u6cd5\u5427\u3002\u201d \u3000\u3000\u674e\u9038\u5bf9\u97ad\u6cd5\u5176\u5b9e\u5e76\u4e0d\u64c5\u957f\uff0c\u4e0d\u8fc7\u4ed6\u6b66\u529f\u6839\u5e95\u6781\u597d\uff0c\u4f7f\u4e86\u4e00\u5f8b\u516d\u5408\u97ad\u6cd5\uff0c\u5374\u4e5f\u4e2d\u89c4\u4e2d \u77e9\uff0c\u63a5\u7740\u4f7f\u5251\uff0c\u4ed6\u4e0d\u6562\u5c06\u672c\u6765\u6240\u5b66\u7684\u5ce8\u5d4b\u5251\u6cd5\u65bd\u5c55\u51fa\u6765\uff0c\u8d70\u4e86\u4e00\u5957\u5e73\u5e73\u5e38\u5e38\u7684\u516b\u4ed9\u5251\u3002 \u674e\u660e\u4e4b\u9053\uff1a\u201c\u4f60\u80fd\u591f\u540c\u65f6\u4f7f\u4e24\u822c\u5175\u5668\u5417\uff1f\u201d\u674e\u9038\u56e0\u89c1\u5f20\u4e4b\u5947\u5bf9\u654c\u4e4b\u65f6\uff0c\u66fe\u5de6\u624b\u4f7f\u97ad\uff0c\u53f3 \u624b\u4f7f\u5251\uff0c\u4fbf\u5e94\u4e86\u4e00\u58f0\u201c\u80fd\u591f\u3002\u201d\u4e8e\u662f\u4e0b\u573a\u7ec3\u4e86\u4e00\u904d\uff0c\u5c06\u516d\u5408\u97ad\u6cd5\u548c\u516b\u4ed9\u5251\u6cd5\u5168\u90e8\u65bd\u5c55\u51fa \u6765\u3002\u7ec3\u5b8c\u4e4b\u540e\uff0c\u674e\u660e\u4e4b\u53eb\u4ed6\u8d70\u5230\u53f0\u524d\uff0c\u6709\u70b9\u8be7\u5f02\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7ef0\u53f7\u75c5\u5c09\u8fdf\uff0c\u97ad\u6cd5 \u5374\u8fdc\u8fdc\u4e0d\u5982\u5251\u6cd5\uff0c\u540c\u65f6\uff0c\u4f60\u7684\u5251\u6cd5\u4e5f\u597d\u4f3c\u672a\u5c3d\u6240\u957f\uff0c\u6709\u51e0\u62db\u672c\u6765\u53ef\u4ee5\u7ec3\u5f97\u66f4\u597d\u7684\uff0c\u4f60\u5374 \u597d\u50cf\u6709\u4ec0\u4e48\u987e\u5fcc\u4f3c\u7684\uff0c\u4f7f\u51fa\u6765\u7adf\u7136\u5fae\u9732\u7834\u7efd\uff0c\u8fd9\u662f\u4ec0\u4e48\u539f\u6545\uff1f\u201d\u674e\u9038\u6697\u6697\u5403\u60ca\uff0c\u60f3\u4e0d\u81f3 \u674e\u660e\u4e4b\u7adf\u7136\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u773c\u5149\u9510\u5229\u4e4b\u6781\u3002 \u3000\u3000\u5e78\u800c\u674e\u9038\u673a\u8b66\uff0c\u8111\u7b4b\u4e00\u8f6c\uff0c\u4fbf\u5373\u7b54\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u4ec0\u4e48\u539f\u6545\uff0c\u4f46\u89c1\u573a\u4e2d\u51e0\u767e\u53cc\u773c\u775b \u90fd\u76ef\u7740\u6211\uff0c\u6211\u8d8a\u7740\u6025\uff0c\u8d8a\u60f3\u7ec3\u5f97\u597d\u4e9b\uff0c\u8fd9\u67c4\u5251\u5374\u504f\u504f\u4e0d\u542c\u4f7f\u5524\u3002\u201d\u674e\u660e\u4e4b\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3 \u9053\uff1a\u201c\u539f\u6765\u4ed6\u6709\u70b9\u602f\u573a\u7684\u6bdb\u75c5\u3002\u201d\u518d\u95ee\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u7279\u522b\u672c\u9886\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u4f1a \u4f7f\u6697\u5668\u3002\u201d\u674e\u660e\u4e4b\u60f3\u4e86\u4e00\u60f3\uff0c\u53eb\u521a\u624d\u8868\u6f14\u8fc7\u7684\u53e6\u4e00\u4e2a\u795e\u7bad\u624b\u51fa\u6765\uff0c\u5bf9\u674e\u9038\u9053\uff1a\u201c\u597d\u5427\uff0c \u6211\u53eb\u4ed6\u7528\u7389\u5df2\u73e0\u7bad\u6cd5\u5c04\u4f60\uff0c\u4f60\u63a5\u63a5\u770b\uff0c\u8981\u4e0d\u8981\u53bb\u6363\u7bad\u9ab8\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4e0d\u7528\u3002\u201d\u674e\u660e\u4e4b \u9053\uff1a\u201c\u5229\u7bad\u65e0\u60c5\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u4fbf\u6709\u5371\u9669\uff0c\u4f60\u5f53\u771f\u4e0d\u6015\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4ed6\u7528\u7bad\u5c04\u6211\uff0c\u6211 \u773c\u4e2d\u53ea\u89c1\u4ed6\u4e00\u4e2a\u4eba\uff0c\u5fc3\u4fbf\u4e0d\u4f1a\u4e71\u4e86\u3002\u53bb\u6389\u7bad\u9528\uff0c\u53ea\u6015\u4ed6\u4e0d\u80fd\u5c3d\u91cf\u53d1\u6325\u795e\u7bad\u7684\u529f\u592b\u3002\u201d\u674e \u660e\u4e4b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6562\u8bf7\u4f60\u4e5f\u6015\u663e\u4e0d\u51fa\u60ca\u4eba\u7684\u529f\u592b\u4e86\uff1f\u597d\u5427\uff0c\u90a3\u4f60\u4eec\u5c31\u4e0a\u573a\u4e00\u8bd5\u3002\u201d \u3000\u3000\u6821\u5c09\u7275\u6765\u4e86\u4e24\u9a91\u9a8f\u9a6c\uff0c\u4e00\u4eba\u4e00\u9a91\u3002\u5728\u573a\u4e0a\u8dd1\u4e86\u4e00\u5708\uff0c\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u5c0f\u5fc3\u63a5\u7bad\uff01\u201d\u5f13 \u5f26\u4e00\u54cd\uff0c\u201c\u55d6\u201d\u7684\u4e00\u652f\u5229\u7bad\u5c04\u51fa\uff0c\u674e\u9038\u4e00\u4e2a\u201c\u9563\u91cc\u85cf\u8eab\u201d\uff0c\u90a3\u652f\u5229\u7bad\u4ece\u4ed6\u808b\u65c1\u7a7f\u8fc7\uff0c\u88ab \u4ed6\u6284\u7740\u7bad\u5c3e\uff0c\u7529\u5728\u5730\u4e0a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u6b66\u571f\u95ea\u7535\u822c\u7684\u5c04\u51fa\u4e86\u4e09\u652f\u8fde\u73e0\u7bad\uff0c\u674e\u9038\u5728 \u9a6c\u80cc\u4e0a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u53cd\u624b\u4e00\u6284\uff0c\u4e09\u652f\u7bad\u90fd\u843d\u5230\u4e86\u4ed6\u7684\u624b\u4e2d\uff0c\u5c04\u5f97\u5feb\uff0c\u63a5\u5f97\u4e5f\u5feb\uff0c\u4f17\u4eba\u542c\u5f97 \u5f13\u5f26\u4e00\u54cd\uff0c\u7bad\u4fbf\u5230\u4e86\u674e\u9038\u624b\u4e2d\uff0c\u597d\u50cf\u662f\u9012\u8fc7\u53bb\u4f3c\u7684\uff0c\u90fd\u4e0d\u7981\u559d\u8d77\u91c7\u6765\u3002\u53e6\u4e00\u6b66\u58eb\u4ee5\u795e\u7bad \u624b\u81ea\u8d1f\uff0c\u5341\u5206\u597d\u80dc\uff0c\u89c1\u674e\u9038\u63a5\u7ee5\u5b50\u7684\u529f\u592b\u9ad8\u660e\uff0c\u7adf\u5c06\u4e09\u652f\u7bad\u540c\u65f6\u642d\u5728\u5f26\u4e0a\uff0c\u5f20\u5f13\u4e00\u5c04\uff0c \u4e09\u7bad\u9f50\u98de\uff0c\u98de\u81f3\u674e\u9038\u80cc\u540e\uff0c\u4e09\u652f\u4e8c\u7bad\u500f\u7684\u5206\u5f00\uff0c\u4e00\u652f\u5c04\u80cc\u5fc3\uff0c\u4e00\u652f\u5c04\u540e\u8111\uff0c\u8fd8\u6709\u4e00\u652f\u5c04 \u4ed6\u814b\u7a9d\uff0c\u4e09\u652f\u7bad\u4e09\u4e2a\u65b9\u5411\uff0c\u7bad\u6cd5\u7aef\u7684\u60ca\u4eba\uff0c\u573a\u4e2d\u5608\u58f0\u987f\u6b62\uff0c\u4eba\u4eba\u5c4f\u606f\u4ee5\u5f85\uff0c\u4f46\u89c1\u674e\u9038\u5728 \u9a6c\u80cc\u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c\u4e09\u652f\u7bad\u90fd\u4ece\u4ed6\u7684\u811a\u4e0b\u5c04\u8fc7\uff0c\u4ed6\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7ffb\u8eab\u6251\u4e0b\uff0c\u5c06\u4e09\u652f\u7bad\u4e00\u6284 \u90fd\u6284\u5230\u624b\u4e2d\uff0c\u4eba\u4e5f\u521a\u597d\u843d\u5728\u9a6c\u4e0a\u3002\u8fd9\u65f6\u8fde\u4e3b\u8003\u7684\u674e\u660e\u4e4b\u4e5f\u4e0d\u7981\u559d\u8d77\u201c\u597d\u201d\u6765\uff01 \u3000\u3000\u90a3\u6b66\u58eb\u80c0\u7ea2\u4e86\u9762\uff0c\u8d81\u7740\u674e\u9038\u521a\u521a\u843d\u4e0b\uff0c\u7a81\u7136\u53d1\u51fa\u4e24\u652f\u6025\u7bad\uff0c\u8fd9\u56de\u4e0d\u662f\u5c04\u4eba\uff0c\u5374\u662f\u5c04 \u9a6c\uff0c\u800c\u4e14\u5c04\u9a6c\u7684\u540e\u817f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53ea\u8981\u5c04\u5f97\u4f60\u8dcc\u4e0b\u9a6c\u6765\uff0c\u6211\u4fbf\u4e0d\u81f3\u4e8e\u5f53\u573a\u4e22\u9762\uff0c\u674e\u9038 \u9a91\u5728\u9a6c\u4e0a\uff0c\u90a3\u6b66\u58eb\u6599\u4ed6\u51b3\u8ba1\u4e0d\u80fd\u63a5\u5230\uff0c\u54ea\u77e5\u5fc3\u5ff5\u65b9\u52a8\uff0c\u5ffd\u89c1\u674e\u9038\u5728\u9a6c\u80cc\u4e0a\u4e2a\u201c\u9ca4\u9c7c\u7ffb\u8eab\u201d \u53cc\u811a\u52fe\u7740\u9a6c\u978d\uff0c\u7adf\u7136\u5012\u6302\u4e0b\u6765\uff0c\u53cc\u624b\u9f50\u51fa\uff0c\u5c06\u90a3\u4e24\u652f\u7bad\u63a5\u4e86\u3002\u90a3\u6b66\u58eb\u53d1\u7bad\u771f\u5feb\uff0c\u4e00\u89c1\u674e \u9038\u7528\u8fd9\u4e2a\u529e\u6cd5\u63a5\u4ed6\u7684\u7bad\uff0c\u77e5\u9053\u4ed6\u7684\u4e0a\u8eab\u91cd\u5fc3\u4e0d\u7a33\uff0c\u63a5\u8fde\u53c8\u53d1\u51fa\u4e86\u4e24\u652f\u8fde\u73e0\u7bad\u3002\u573a\u4e2d\u5404\u53bf \u6765\u5e94\u8bd5\u7684\u4eba\uff0c\u89c1\u4ed6\u5982\u6b64\u5c04\u6cd5\uff0c\u5fc3\u4e2d\u90fd\u5728\u6697\u9a82\uff1a\u201c\u5927\u5bb6\u6bd4\u8bd5\uff0c\u53c8\u4e0d\u662f\u62fc\u547d\uff0c\u4f55\u5fc5\u51fa\u8fd9\u7965\u72e0 \u6bd2\u7684\u7bad\u6cd5\uff01\u201d\u8fd9\u65f6\u674e\u9038\u521a\u521a\u5c06\u524d\u9762\u90a3\u4e24\u652f\u7bad\u63a5\u4e0b\uff0c\u540e\u9762\u90a3\u4e24\u652f\u7bad\u53c8\u5df2\u5636\u98ce\u5c04\u5230\uff0c\u907f\u65e0\u53ef \u907f\uff0c\u8feb\u5f97\u9732\u51fa\u60ca\u4eba\u7edd\u679d\uff0c\u7a81\u7136\u5f20\u53e3\u4e00\u54ac\uff0c\u5c06\u5c04\u5230\u54bd\u5589\u7684\u90a3\u652f\u7bad\u54ac\u7740\uff0c\u5f20\u53e3\u4e00\u5410\uff0c\u53cd\u5c04\u51fa \u53bb\uff0c\u5c06\u8ddf\u7740\u6765\u7684\u90a3\u652f\u7bad\u4e5f\u78b0\u843d\u4e86\u3002 \u3000\u3000\u573a\u4e2d\u91c7\u58f0\u5982\u96f7\uff0c\u90a3\u540d\u201c\u795e\u7bad\u624b\u201d\u5c06\u94c1\u5f13\u6302\u8d77\uff0c\u56de\u5230\u4e3b\u8003\u53f0\u201c\u7f34\u4ee4\u201d\uff0c\u7980\u9053\uff1a\u201c\u5f20\u4e4b \u5947\u63a5\u7bad\u7684\u529f\u592b\u59d4\u5b9e\u9ad8\u660e\uff0c\u6211\u8ba4\u8f93\u4e86\u3002\u201d\u674e\u9038\u4e5f\u5411\u674e\u660e\u4e4b\u7980\u9053\uff1a\u201c\u5b66\u751f\u529f\u592b\u751f\u758f\uff0c\u6700\u540e\u4e00 \u652f\u7bad\u63a5\u4e0d\u7740\uff0c\u53eb\u5927\u4eba\u89c1\u7b11\u4e86\u3002\u201d\u674e\u660e\u4e4b\u9053\uff1a\u201c\u4f60\u7684\u529f\u592b\u5f88\u4e0d\u9519\u554a\uff01\u4e0d\u4f46\u63a5\u6697\u5668\u7684\u624b\u6cd5\u7eaf \u719f\uff0c\u8f7b\u529f\u3001\u5185\u529f\u4e5f\u5f88\u6709\u6839\u5e95\uff0c\u96be\u5f97\uff0c\u96be\u5f97\uff01\u201d\u8fde\u8fde\u79f0\u8d5e\uff0c\u63d6\u8d77\u6b8a\u7b14\uff0c\u5374\u5728\u534a\u7a7a\u4e2d\u6253\u4e86\u4e2a \u5708\u3002\u5e76\u4e0d\u843d\u4e0b\uff0c\u597d\u50cf\u5728\u8003\u8651\u4ec0\u4e48\u4e8b\u60c5\u4f3c\u7684\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u674e\u9038\u5fc3\u5982\u540a\u6876\uff0c\u4e03\u4e0a\u516b\u843d\u3002\u4ed6\u672c \u6765\u7684\u7528\u610f\u4e0d\u8fc7\u662f\u60f3\u6df7\u8fdb\u795e\u6b66\u8425\u4fbf\u7b97\uff0c\u4ed6\u4e4b\u6240\u4ee5\u8868\u6f14\u63a5\u6697\u5668\u7684\u529f\u592b\uff0c\u4e43\u662f\u5e0c\u671b\u5c06\u6765\u5206\u914d\u804c \u4f4d\u65f6\uff0c\u53ef\u4ee5\u8c03\u8fdb\u5bab\u5185\uff0c\u4e3a\u6b66\u5219\u5929\u9632\u8303\u523a\u5ba2\uff0c\u6709\u63a5\u8fd1\u5979\u7684\u673a\u4f1a\u3002\u4e0d\u6599\u521a\u624d\u90a3\u201c\u795e\u7bad\u624b\u201d\u6700 \u540e\u7684\u4e24\u652f\u8fde\u73e0\u7bad\u8feb\u5f97\u4ed6\u4f7f\u51fa\u4e86\u201c\u556e\u5931\u6cd5\u201d\uff0c\u800c\u4e14\u8feb\u5f97\u4ed6\u4ee5\u53e3\u5410\u7bad\uff0c\u5c04\u843d\u5bf9\u65b9\u7684\u98de\u7bad\uff0c\u8fd9 \u5c31\u4e0d\u80fd\u4e0d\u9732\u51fa\u4e86\u4ed6\u7684\u5185\u529f\u6839\u5e95\u4e86\u3002\u800c\u4ed6\u6b63\u662f\u6015\u81ea\u5df1\u7684\u529f\u592b\u592a\u8fc7\u663e\u9732\uff0c\u5f15\u8d77\u522b\u4eba\u7684\u6ce8\u610f\u3002 \u4e07\u4e00\u67e5\u95ee\u8d77\u6765\uff0c\u6cc4\u9732\u51fa\u672c\u6765\u8eab\u4efd\uff0c\u90a3\u5c31\u662f\u5927\u7978\u4e00\u573a\u3002 \u3000\u3000\u674e\u660e\u4e4b\u6c89\u541f\u534a\u664c\uff0c\u53eb\u90a3\u540d\u795e\u7bad\u624b\u9000\u4e0b\uff0c\u518d\u770b\u4e86\u4e00\u4e0b\u540d\u518c\uff0c\u5bf9\u674e\u9038\u8bf4\u9053\uff1a\u201c\u4f60\u4e14\u6682\u5f85 \u4e00\u4f1a\u3002\u201d\u674e\u9038\u6b63\u81ea\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u4e0b\u4e00\u540d\u5e94\u8003\u7684\u8bd5\u5b50\u5df2\u5949\u53ec\u8d70\u4e09\u53f0\u7684\uff0c\u90a3\u4eba\u53eb\u505a\u5d14\u4ef2\u5143\uff0c\u662f \u6cb3\u5357\u4fe1\u53bf\u4fdd\u8350\u6765\u7684\u3002\u674e\u660e\u4e4b\u5bf9\u5d14\u4ef2\u5143\u9053\uff1a\u201c\u4f60\u662f\u6cb3\u5357\u8457\u540d\u7684\u4f1a\u5ba2\uff0c\u5728\u5251\u672f\u4e0a\u9047\u5230\u8fc7\u5bf9\u624b \u6ca1\u6709\uff1f\u201d\u674e\u9038\u5fc3\u4e2d\u4e00\u51db\uff0c\u539f\u6765\u4ed6\u4e5f\u542c\u8fc7\u5d14\u4ef2\u5143\u7684\u540d\u5b57\uff0c\u77e5\u9053\u5d14\u4ef2\u5143\u662f\u516b\u624b\u4ed9\u733f\u8c22\u8865\u4e4b\u7684 \u5927\u5f1f\u5b50\uff0c\u5728\u5317\u4e94\u7701\u5927\u5927\u6709\u540d\uff0c\u4e0d\u60f3\u4ed6\u4e5f\u6765\u4e86\u3002\u53ea\u4e0d\u77e5\u4f55\u4ee5\u674e\u660e\u4e4b\u5c06\u4ed6\u5524\u6765\uff0c\u5374\u53c8\u4e0d\u5c06\u81ea \u5df1\u53d1\u843d\uff1f \u3000\u3000\u90a3\u5d14\u4ef2\u5143\u662f\u540d\u5bb6\u5f1f\u5b50\uff0c\u5916\u8c26\u5185\u50b2\uff0c\u7b54\u9053\uff1a\u201c\u5929\u4e0b\u5251\u672f\u540d\u5bb6\u5f88\u591a\uff0c\u53ef\u60dc\u5b66\u751f\u6ca1\u6709\u9047\u8fc7\u3002 \u6709\u51e0\u4f4d\u8001\u524d\u8f88\uff0c\u4ed6\u4eec\u5076\u800c\u4e5f\u6307\u6559\u8fc7\u5b66\u751f\u51e0\u62db\uff0c\u5374\u4e5f\u672a\u66fe\u6b63\u5f0f\u4ea4\u624b\u3002\u5176\u4ed6\u7684\u4eba\uff0c\u65e0\u8db3\u6302\u9f7f\uff0c \u5b66\u751f\u4e0e\u4ed6\u6bd4\u8bd5\uff0c\u80dc\u4e86\u4e5f\u4e0d\u8db3\u79f0\u9053\u3002\u201d\u674e\u660e\u4e4b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9664\u4e86\u51e0\u4f4d\u6709\u9650 \u7684\u5927\u540d\u5bb6\uff0c\u4f60\u5728\u5251\u672f\u4e0a\u662f\u4ece\u672a\u9047\u5230\u8fc7\u5bf9\u624b\u7684\u4e86\u3002\u4f60\u521a\u624d\u8bf4\u6709\u51e0\u4f4d\u8001\u524d\u8f88\u5076\u800c\u4e5f\u6307\u6559\u8fc7\u4f60\uff01 \u4ed6\u4eec\u662f\u8c01\uff1f\u201d\u5d14\u4ef2\u5143\u9053\uff1a\u201c\u8e51\u53bb\u5251\u8c37\u795e\u7fc1\u548c\u516b\u4ed9\u8881\u7267\u90fd\u66fe\u5728\u5bb6\u5904\u89c1\u8fc7\u5b66\u751f\uff0c\u8fd9\u662f\u4e94\u516d\u5e74 \u524d\u7684\u4e8b\u60c5\uff0c\u5f53\u65f6\u4ed6\u4eec\u4e00\u65f6\u9ad8\u5174\uff0c\u66fe\u53eb\u5b66\u751f\u7ed9\u4ed6\u4eec\u8fc7\u62db\u3002\u201d\u674e\u660e\u4e4b\u9053\uff1a\u201c\u4f60\u63a5\u5f97\u4ed6\u51e0\u62db\uff1f\u201d \u5d14\u4ef2\u5143\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u8001\u524d\u8f88\u53ea\u662f\u548c\u5f1f\u5b50\u8bd5\u7684\u6027\u8d28\uff0c\u672a\u5c3d\u5168\u529b\u3002\u6211\u52c9\u5f3a\u53ef\u4ee5\u63a5\u81f3\u5341\u62db\u3002\u201d \u674e\u9038\u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u80fd\u63a5\u81f3\u5341\u62db\u5f00\u5916\uff0c\u786e\u4e5f\u4e0d\u7b97\u5f97\u662f\u6d6a\u5f97\u865a\u540d\u4e86\uff01\u201d \u3000\u3000\u90a3\u674e\u660e\u4e4b\u4e5f\u597d\u50cf\u719f\u6089\u6b66\u6797\u7684\u60c5\u5f62\uff0c\u542c\u4e86\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u7684\u5251\u672f\u9020\u8be3\u5f88\u4e0d\u9519\u4e86\u3002 \u6211\u60f3\u89c1\u8bc6\u4e00\u4e0b\u4f60\u7684\u771f\u5b9e\u672c\u9886\uff0c\u53eb\u4e00\u4e2a\u4eba\u548c\u4f60\u6bd4\u8bd5\u597d\u5417\uff1f\u201d\u5d14\u4ef2\u5143\u5f53\u7136\u8bf4\u597d\uff0c\u674e\u660e\u4e4b\u4e00\u6307 \u674e\u9038\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u6211\u5c31\u70b9\u4f60\u548c\u4ed6\u6bd4\u8bd5\u4e00\u4e0b\u5427\u3002\u201d\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u5b66\u751f\u5c1a \u4e0d\u4e4f\u81ea\u77e5\u4e4b\u660e\uff0c\u6211\u600e\u80fd\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u8bf7\u5927\u4eba\u53e6\u70b9\u53e6\u4eba\u5427\uff01\u201d \u3000\u3000\u674e\u660e\u4e4b\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u62c5\u5fc3\u3002\u201d\u53eb\u968f\u4ece\u53d6\u6765\u4e86\u4e24\u67c4\u6728\u5251\uff0c\u5c3a\u5bf8\u957f\u77ed\uff0c\u548c\u666e\u901a\u6b66\u4e0a\u4f69 \u6234\u7684\u9752\u94a2\u5251\u4e00\u6a21\u4e00\u6837\u3002\u53e6\u4e00\u540d\u968f\u4ece\u62ff\u6765\u4e86\u4e00\u6876\u77f3\u7070\uff0c\u5c06\u8fd9\u4e24\u67c4\u6728\u5251\u5728\u77f3\u7070\u4e2d\u4e00\u5206\uff0c\u7136\u540e \u5206\u7ed9\u674e\u9038\u548c\u5d14\u4ef2\u5143\uff0c\u6bcf\u4eba\u4e00\u628a\u3002\u674e\u660e\u4e4b\u773c\u775b\u671b\u7740\u674e\u9038\u8bf4\u9053\uff1a\u201c\u4f60\u521a\u624d\u7684\u5251\u6cd5\u8fd8\u672a\u5c3d\u6240\u957f\uff0c \u6b63\u597d\u8d81\u8fd9\u673a\u4f1a\u518d\u8bd5\u4e00\u8d9f\u3002\u8fd9\u6837\u6bd4\u8bd5\u7edd\u5bf9\u6ca1\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u53cc\u65b9\u53ef\u4ee5\u65e0\u987b\u987e\u5fcc\uff0c\u6bd4\u8d5b\u5b8c\u540e\uff0c \u770b\u8c01\u8eab\u4e0a\u4e2d\u5251\u8f83\u591a\uff0c\u80dc\u8d1f\u4fbf\u53ef\u4ee5\u5224\u660e\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u5176\u5b9e\u5e76\u4e0d\u662f\u5bb3\u6015\u5d14\u4ef2\u5143\uff0c\u800c\u662f\u5bb3\u6015\u7ed9\u4eba\u770b\u51fa\u4ed6\u7684\u5e95\u7ec6\uff0c\u4f46\u674e\u660e\u4e4b\u4ee5\u4e3b\u8003\u7684\u8eab\u4efd\uff0c \u63d0\u51fa\u4e86\u8fd9\u4e2a\u6bd4\u8bd5\u529e\u6cd5\uff0c\u4ed6\u52bf\u4e0d\u80fd\u63a8\u642a\uff0c\u53ea\u597d\u63d0\u5251\u4e0a\u573a\u3002 \u3000\u3000\u5d14\u4ef2\u5143\u96c4\u5fc3\u52c3\u52c3\uff0c\u6839\u672c\u5c31\u6ca1\u628a\u674e\u9038\u653e\u5728\u773c\u5185\uff0c\u5f53\u4e0b\u6a2a\u5251\u5f53\u80f8\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u5f20\u5144 \u6307\u6559\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u5d14\u5144\u662f\u6210\u540d\u7684\u5251\u5ba2\uff0c\u5c0f\u5f1f\u5c82\u6562\u78e8\u8d8a\uff0c\u8fd8\u662f\u8bf7\u5d14\u5144\u5148\u884c\u8d50\u62db\u3002\u201d\u4ed6\u5fc3 \u4e2d\u6b63\u81ea\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u8981\u80dc\u8fd8\u662f\u8981\u8d25\uff1f\u5d14\u4ef2\u5143\u542c\u4ed6\u9178\u6e9c\u6e9c\u7684\u5c3d\u8bf4\u5ba2\u5957\u7684\u8bdd\uff0c\u5fc3 \u4e2d\u65e9\u5df2\u4e0d\u5927\u8010\u70e6\uff0c\u6728\u5251\u4e00\u5c55\u3002\u9053\u58f0\uff1a\u201c\u597d\uff01\u201d\u4e00\u62db\u201c\u6a2a\u6307\u5929\u5357\u201d\uff0c\u4fbf\u5411\u674e\u9038\u8fce\u9762\u4e00\u70b9\uff01 \u3000\u3000\u5d14\u4ef2\u5143\u7684\u5e08\u7236\u540d\u5524\u201c\u516b\u624b\u4ed9\u733f\u201d\uff0c\u6240\u521b\u7684\u5251\u6cd5\u4fbf\u53eb\u505a\u201c\u7075\u733f\u5251\u6cd5\u201d\u4ee5\u8f7b\u7075\u98d8\u5ffd\u89c1\u957f\uff0c \u5d14\u4ef2\u5143\u5df2\u5c3d\u5f97\u5e08\u95e8\u5fc3\u6cd5\uff0c\u8fd9\u4e00\u5251\u5241\u51fa\uff0c\u4f3c\u865a\u4f3c\u5b9e\uff0c\u5f53\u771f\u662f\u8fc5\u903e\u98d8\u98ce\uff0c\u4ee4\u4eba\u96be\u4ee5\u6349\u6478\u3002\u674e \u9038\u5fc3\u4e2d\u4e00\u51db\uff0c\u98d8\u8eab\u4e00\u95ea\uff0c\u4f46\u542c\u5f97\u5237\u7684\u4e00\u58f0\uff0c\u5d14\u4ef2\u5143\u7684\u6728\u5251\u4ece\u4ed6\u80a9\u5934\u5288\u8fc7\uff0c\u573a\u4e2d\u6b66\u58eb\uff0c\u626c \u8d77\u4e86\u4e00\u7247\u54d7\u7b11\u4e4b\u58f0\uff0c\u674e\u9038\u9762\u4e0a\u4e00\u7ea2\uff0c\u77e5\u9053\u5b9a\u662f\u5df2\u88ab\u4ed6\u7684\u5251\u5c16\u70b9\u4e2d\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u674e\u660e\u4e4b \u5fc3\u5185\u5df2\u8d77\u4e86\u731c\u7591\uff0c\u6211\u82e5\u7136\u518d\u6545\u610f\u793a\u5f31\uff0c\u53ea\u6015\u5f04\u5de7\u53cd\u62d9\uff0c\u7ed9\u4ed6\u770b\u7834\uff0c\u66f4\u4e3a\u4e0d\u5999\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5d14\u4ef2\u5143\u51fa\u624b\u5982\u98ce\uff0c\u7b2c\u4e8c\u5251\u53c8\u8fde\u73af\u523a\u5230\uff0c\u674e\u9038\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c \u53cd\u624b\u4e00\u5251\uff0c\u5d14\u4ef2\u5143\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u3002\u674e\u9038\u4f9d\u6837\u753b\u846b\u82a6\uff0c\u4e5f\u662f\u4e00\u62db\u201c\u6a2a\u6307\u5929\u5357\u201d\uff0c\u5728\u4ed6\u80a9\u5934 \u4e0a\u70b9\u4e86\u4e00\u4e0b\uff0c\u5d14\u4ef2\u5143\u53c8\u60ca\u53c8\u6012\uff0c\u5f3a\u81ea\u9547\u6444\u5fc3\u795e\uff0c\u5c06\u8f7b\u654c\u4e4b\u5fc3\u5c3d\u4ea5\uff0c\u534a\u653b\u534a\u5b88\uff0c\u7247\u523b\u4e4b\u95f4 \u548c\u674e\u9038\u62c6\u4e86\u4e8c\u4e09\u5341\u62db\u3002 \u3000\u3000\u573a\u4e2d\u4f17\u6b66\u571f\u770b\u5f97\u773c\u82b1\u7ee6\u4e71\uff0c\u4f46\u89c1\u5d14\u4ef2\u5143\u7eb5\u8dc3\u5982\u98de\uff0c\u4e00\u67c4\u6728\u5251\u5c31\u4f3c\u5316\u6210\u4e86\u5341\u6570\u67c4\u4e00\u822c\uff0c \u5728\u674e\u9038\u7684\u8eab\u524d\u8eab\u540e\uff0c\u8eab\u5de6\u8eab\u53f3\u7a7f\u6765\u63d2\u53bb\u3002\u800c\u674e\u9038\u5219\u4f3c\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5e76\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c \u6240\u4f7f\u7684\u4ecd\u662f\u666e\u666e\u901a\u901a\u7684\u4e00\u5957\u516b\u4ed9\u5251\u6cd5\uff0c\u4e0d\u8fc7\u5c01\u95ed\u5f97\u751a\u4e3a\u4e25\u5bc6\uff0c\u89e3\u62c6\u5bf9\u65b9\u7684\u5251\u52bf\uff0c\u4ea6\u4f3c\u9887 \u89c1\u529f\u592b\u3002\u573a\u4e2d\u6b66\u58eb\uff0c\u5341\u4e4b\u516b\u4e5d\u90fd\u662f\u8fd9\u6837\u60f3\u9053\uff1a\u201c\u8fd9\u5f20\u4e4b\u5947\u7684\u5251\u6cd5\u867d\u7136\u4e0d\u9519\uff0c\u5230\u5e95\u662f\u5d14\u4ef2 \u5143\u80dc\u4ed6\u4e00\u7b79\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u674e\u660e\u4e4b\u4e0b\u4ee4\u505c\u6b62\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u529f\u529b\u6089\u654c\uff0c\u4e0d\u5fc5\u6bd4\u4e86\u3002\u5f20\u4e4b\u5947\u8eab\u4e0a \u4e2d\u5251\u8f83\u591a\uff0c\u4f46\u5d14\u4ef2\u5143\u4e2d\u5251\u7684\u5730\u65b9\uff0c\u5374\u90fd\u662f\u8981\u5bb3\u4e4b\u5904\uff0c\u5251\u6cd5\u5404\u6709\u64c5\u957f\uff0c\u4ee5\u540e\u4f60\u4eec\u4e8c\u4eba\u6b63\u53ef \u4ee5\u591a\u591a\u7422\u78e8\u3002\u201d\u4f17\u6b66\u58eb\u5b9a\u775b\u7ec6\u770b\uff0c\u53ea\u89c1\u674e\u9038\u6d51\u8eab\u4e0a\u4e0b\uff0c\u6591\u6591\u767d\u70b9\uff0c\u4f46\u5d14\u4ef2\u5143\u7684\u5fc3\u7a9d\uff0c\u5374 \u54c1\u5b57\u5f62\u7684\u5e03\u4e86\u4e09\u70b9\u767d\u70b9\uff0c\u82e5\u7136\u4e0d\u662f\u6728\u5251\u7684\u8bdd\uff0c\u4ed6\u7109\u80fd\u8fd8\u6709\u547d\u5728\uff01 \u3000\u3000\u5404\u5dde\u53bf\u524d\u6765\u5e94\u8003\u7684\u6b66\u58eb\u65e0\u4e0d\u60ca\u670d\uff0c\u60f3\u4e0d\u5230\u4e3b\u8003\u5b98\u7684\u773c\u5149\u7adf\u662f\u5982\u6b64\u9510\u5229\uff0c\u4e00\u773c\u4fbf\u770b\u4e86 \u51fa\u6765\u3002\u674e\u660e\u4e4b\u63d0\u8d77\u6b8a\u7b14\uff0c\u5728\u540d\u518c\u4e0a\u5708\u4e86\u4e24\u4e2a\u5708\u5708\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u90fd\u5f55\u53d6\u4e86\uff0c\u5f85\u8003\u8bd5 \u8fc7\u540e\uff0c\u6211\u518d\u548c\u4f60\u4eec\u8c08\u8c08\u3002\u201d \u3000\u3000\u674e\u9038\u9000\u4e0b\u573a\u8fb9\uff0c\u5fc3\u795e\u5140\u81ea\u6014\u6014\u4e0d\u5b9a\uff0c\u60f3\u9053\uff1a\u201c\u674e\u660e\u4e4b\u8981\u548c\u6211\u8c08\u4e9b\u4ec0\u4e48\uff1f\u521a\u624d\u90a3\u573a\u6bd4 \u8bd5\uff0c\u4e0d\u77e5\u4ed6\u8fd8\u770b\u51fa\u4e86\u4e9b\u4ec0\u4e48\u7834\u7efd\uff1f\u201d\u573a\u4e2d\u9646\u7eed\u6709\u4eba\u8868\u6f14\u6b66\u529f\uff0c\u674e\u9038\u5374\u5df2\u65e0\u5fc3\u89c2\u770b\uff0c\u8bb8\u591a \u6b66\u58eb\u6324\u4e86\u4e0a\u6765\uff0c\u674e\u9038\u88ab\u5305\u56f4\u5728\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u573a\u4e2d\u8868\u6f14\u4e9b\u4ec0\u4e48\uff0c\u4ed6\u66f4\u770b\u4e0d\u6e05\u695a\u4e86\u3002 \u3000\u3000\u4eba\u4e1b\u4e2d\u4ecd\u7136\u6709\u4eba\u8c08\u8bba\u674e\u9038\u521a\u624d\u90a3\u573a\u6bd4\u8bd5\uff0c\u674e\u9038\u542c\u5f97\u6709\u4eba\u8c08\u8bba\u81ea\u5df1\uff0c\u4efd\u5916\u7559\u795e\uff0c\u7ad6\u8d77 \u8033\u6735\u6765\u542c\uff0c\u53ea\u542c\u573a\u540e\u9762\u6709\u4eba\u7a83\u7a83\u79c1\u8bae\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4e3b\u8003\u65ad\u5f97\u4e0d\u516c\uff0c\u5e94\u8be5\u662f\u90a3\u59d3\u5f20 \u7684\u83b7\u80dc\u3002\u8bd5\u60f3\u82e5\u662f\u624b\u6267\u5229\u5203\uff0c\u771f\u6b63\u4ea4\u950b\uff0c\u5f20\u4e4b\u5947\u5728\u4ed6\u7684\u5fc3\u7a9d\u5241\u4e86\u4e09\u4e0b\uff0c\u4e0d\u65e9\u5df2\u8981\u4e86\u4ed6\u7684 \u547d\u5417\uff1f\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u7136\uff0c\u82e5\u662f\u771f\u6b63\u4ea4\u950b\uff0c\u5f20\u4e4b\u5947\u65e9\u5df2\u904d\u4f53\u9cde\u4f24\uff0c\u867d\u8bf4\u4e0d\u662f\u4f24\u7740 \u8981\u5bb3\uff0c\u4f46\u4ed6\u600e\u80fd\u8fd8\u6709\u6c14\u529b\u523a\u4e2d\u5bf9\u65b9\u7684\u5fc3\u7a9d\uff1f\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u8bf4\u6cd5\u90fd\u4e0d\u5bf9\u3002\u201d\u4e89 \u8bba\u7684\u8fd9\u4e24\u4e2a\u4eba\u95ee\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\u5462\uff1f\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u65e0\u6cd5\u5224\u65ad\u3002\u5176\u5b9e\u54b1\u4eec\u90fd\u672a\u66fe\u770b\u5f97 \u6e05\u695a\uff0c\u4e0d\u77e5\u90a3\u59d3\u5f20\u7684\u662f\u53d7\u4e86\u51e0\u6b21\u5251\u4f24\u4e4b\u540e\uff0c\u624d\u523a\u4e2d\u5bf9\u65b9\u7684\u5fc3\u7a9d\u7684\uff1f\u201d\u8fd9\u4e00\u53cd\u95ee\uff0c\u767b\u65f6\u628a \u90a3\u4e24\u4e2a\u4eba\u95ee\u5f97\u54d1\u53e3\u65e0\u8a00\u3002\u8981\u77e5\u9ad8\u624b\u6bd4\u6597\uff0c\u82e5\u7136\u5728\u975e\u8981\u5bb3\u7684\u5730\u65b9\u4e2d\u4e86\u51e0\u5251\uff0c\u7acb\u523b\u4f7f\u53cd\u653b\u514b \u654c\uff0c\u91cd\u521b\u5bf9\u65b9\uff0c\u5f53\u7136\u7b97\u662f\u4ed6\u8d62\uff1b\u4f46\u82e5\u662f\u4e2d\u4e86\u51e0\u5341\u5251\u4e4b\u540e\uff0c\u90a3\u5c31\u662f\u8bf4\u4ed6\u5251\u6cd5\u8fdc\u8fdc\u4e0d\u5982\u5bf9\u65b9\uff0c \u65e9\u5df2\u8981\u6492\u5251\u8ba4\u8f93\uff0c\u53c8\u7109\u80fd\u523a\u5f97\u4e2d\u5bf9\u65b9\u7684\u5fc3\u7a9d\u3002\u90a3\u4e9b\u4eba\u65e2\u7136\u770b\u4e0d\u6e05\u695a\uff0c\u4e89\u8bba\u53ea\u597d\u4f5c\u7f62\u3002\u6709 \u4eba\u53eb\u9053\uff1a\u201c\u5feb\u770b\uff0c\u5feb\u770b\uff0c\u573a\u4e2d\u8fd9\u4e2a\u4eba\u4f7f\u516d\u5408\u5927\u67aa\uff0c\u4f7f\u5f97\u771f\u6709\u529f\u592b\uff01\u201d \u3000\u3000\u674e\u9038\u6382\u8d77\u811a\u6765\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u573a\u4e2d\u4e00\u4e2a\u6b66\u58eb\u5c06\u4e00\u6839\u5927\u67aa\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u6b66\u5b66\u4e2d\u6709 \u53e5\u8bdd\u8bf4\uff1a\u201c\u67aa\u6015\u5706\uff0c\u97ad\u6015\u76f4\u3002\u201d\u4f7f\u67aa\u82e5\u7136\u4f3c\u4f7f\u97ad\u4e00\u6837\uff0c\u80fd\u591f\u8f6f\u786c\u968f\u5fc3\u7684\u6296\u8d77\u5706\u5708\u3002\u90a3\u786e \u662f\u9887\u6709\u529f\u529b\u4e86\u3002\u4f46\u674e\u9038\u5fc3\u795e\u4e0d\u5c5e\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u4fbf\u770b\u4e0d\u4e0b\u53bb\uff0c\u5fc3\u4e2d\u8001\u662f\u7422\u78e8\u674e\u660e\u4e4b\u5bf9\u4ed6\u7684 \u8bf4\u8bdd\u3002\u5ffd\u5730\u6709\u4e00\u4e2a\u6ee1\u9762\u866c\u9aef\u7684\u6b66\u58eb\u6324\u5230\u4ed6\u7684\u8ddf\u524d\uff0c\u62cd\u4e86\u4ed6\u4e00\u4e0b\u80a9\u8180\uff0c\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u8001\u5144\u771f\u662f\u6df1\u85cf\u82e5\u865a\uff01\u201d\u674e\u9038\u5413\u4e86\u4e00\u8df3\uff0c\u4f46\u89c1\u8fd9\u4e2a\u866c\u9aef\u6b66\u58eb\u9732\u51fa\u8be1\u5f02\u7684\u7b11\u5bb9\u5f80\u4e0b\u7eed\u9053\uff1a \u201c\u4ee5\u8001\u5144\u7684\u5251\u6cd5\u800c\u8bba\uff0c\u672c\u6765\u53ef\u4ee5\u5b8c\u5168\u4e0d\u8ba9\u5bf9\u65b9\u523a\u4e2d\uff0c\u4f60\u5374\u6545\u610f\u8ba9\u4ed6\u5728\u4f60\u8eab\u4e0a\u6233\u4e86\u65e0\u6570\u767d \u70b9\uff0c\u8fd9\u771f\u662f\u541b\u5b50\u4e4b\u98ce\uff0c\u6210\u4eba\u4e4b\u7f8e\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d\u674e\u9038\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u54ea\u91cc\uff0c\u54ea\u91cc\uff0c\u5d14\u4ef2 \u5143\u7684\u5251\u6cd5\u786e\u5b9e\u5389\u5bb3\uff0c\u8fd8\u662f\u4ed6\u6709\u610f\u8ba9\u6211\u5462\uff01\u201d\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u6211\u82e5\u662f\u5d14\u4ef2\u5143\uff0c\u6211\u65e9\u5df2\u64a4\u5251\u8ba4 \u8f93\u4e86\u3002\u7eb5\u7136\u4ed6\u4e0d\u77e5\u9053\u4f60\u6545\u610f\u8ba9\u4ed6\uff0c\u4f46\u4f60\u5728\u65e0\u5173\u8981\u9752\u7684\u5730\u65b9\u4e2d\u4e86\u4ed6\u56db\u6b21\u5251\u70b9\u4e4b\u540e\uff0c\u5c31\u7acb\u523b \u523a\u4e2d\u4ed6\u7684\u5fc3\u7a9d\uff0c\u4ed6\u662f\u540d\u5bb6\u5f1f\u5b50\uff0c\u5c45\u7136\u8fd8\u597d\u610f\u601d\u518d\u6253\u4e0b\u53bb\uff0c\u8138\u76ae\u771f\u662f\u539a\u5f97\u53ef\u4ee5\uff01\u201d\u674e\u9038\u5fc3 \u5934\u7830\u7136\u4e00\u8df3\uff0c\u731c\u4e0d\u900f\u4ed6\u7684\u6765\u610f\u5982\u4f55\uff1f \u3000\u3000\u90a3\u866c\u9aef\u6b66\u571f\u53c8\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd8\u6709\u4e00\u4e8b\u672a\u660e\uff0c\u8981\u5411\u5144\u53f0\u8bf7\u6559\u3002\u201d\u674e\u9038\u867d\u7136\u6781\u4e0d\u613f\u610f\u4e0e\u4ed6 \u8bf4\u8bdd\uff0c\u5374\u4e5f\u4e0d\u5f97\u4e0d\u865a\u4e0e\u59d4\u86c7\uff0c\u9053\u58f0\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u5144\u53f0\u6240\u4f7f\u7684\u516b\u4ed9\u5251\u6cd5\uff0c\u5176 \u4e2d\u6709\u4e00\u62db\u624b\u6cd5\u751a\u662f\u5947\u5999\uff0c\u4e0d\u77e5\u53eb\u751a\u540d\u79f0\u3002\u201d\u5f53\u4e0b\u5c06\u90a3\u4e00\u62db\u7684\u624b\u6cd5\u53e3\u8bb2\u6307\u5212\u7684\u91cd\u8bf4\u51fa\u6765\uff0c \u674e\u9038\u542c\u4e86\uff0c\u66f4\u89c9\u5fc3\u865a\uff0c\u539f\u6765\u90a3\u4e00\u62db\u662f\u4ed6\u5e08\u7236\u81ea\u5012\u7684\u65b0\u62db\uff0c\u4e0e\u516b\u4ed9\u5251\u6cd5\u4e2d\u201c\u661f\u6d77\u6d6e\u78cb\u201d\u8fd9 \u4e00\u62db\u6781\u4e3a\u76f8\u4f3c\uff0c\u4e0d\u6599\u8fd9\u866c\u9aef\u6b66\u58eb\u7adf\u7136\u770b\u5f97\u51fa\u6765\u3002\u674e\u9038\u6545\u610f\u8bc8\u7b11\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\u6211\u7ed9\u5d14\u4ef2\u5143 \u653b\u51fb\u5f97\u65e0\u6cd5\u62db\u67b6\uff0c\u90a3\u4e00\u62db\u5b9e\u662f\u8feb\u51fa\u6765\u7684\uff0c\u5176\u5b9e\u4e0d\u6210\u7ae0\u6cd5\uff0c\u6559\u5144\u53f0\u89c1\u7b11\u4e86\u3002\u201d\u90a3\u866c\u9aef\u6b66\u58eb \u9053\uff1a\u201c\u539f\u6765\u662f\u5f20\u5144\u4e34\u573a\u81ea\u521b\u7684\u65b0\u62db\uff0c\u53d8\u5316\u7cbe\u5fae\uff0c\u786e\u662f\u4e0a\u4e58\u5251\u6cd5\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d\u53e3\u6c14\u4f3c \u8d5e\u4f3c\u8bbd\uff0c\u5e78\u597d\u8fd9\u65f6\u573a\u4e2d\u6b63\u6709\u7cbe\u91c7\u8868\u6f14\uff0c\u4f17\u6b66\u58eb\u91c7\u58f0\u5982\u96f7\uff0c\u674e\u9038\u652f\u652f\u543e\u543e\u542b\u6df7\u8fc7\u53bb\uff0c\u8d81\u8fd9 \u673a\u4f1a\u518d\u6324\u5230\u524d\u9762\uff0c\u88c5\u4f5c\u81ea\u795e\u770b\u573a\u4e2d\u7684\u8868\u6f14\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u4e00\u770b\u5374\u771f\u7684\u628a\u674e\u9038\u7684\u773c\u5149\u5438\u4f4f\u4e86\uff0c\u53ea\u89c1\u573a\u4e2d\u4e00\u4e2a\u767d\u8863\u6b66\u58eb\uff0c\u6b63\u5728\u8868\u6f14\u201c\u98de\u5200 \u65ad\u6869\u201d\u7684\u7edd\u6280\uff0c\u6821\u573a\u7684\u4e00\u89d2\u63d2\u6709\u5341\u6839\u67cf\u6728\u6869\uff0c\u6bcf\u6839\u6728\u6869\u90fd\u6709\u8336\u676f\u7c97\u7ec6\uff0c\u767d\u8863\u6b66\u58eb\u5728\u79bb\u6728 \u6869\u4e03\u516b\u4e08\u8fdc\u4e4b\u5904\u626c\u624b\u4e00\u67c4\u98de\u5200\uff0c\u4f46\u542c\u5f97\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u6728\u6869\u7acb\u5373\u65ad\u4e86\u4e00\u6839\uff0c\u8fd9\u95e8\u529f\u592b\uff0c \u51c6\u5934\u8fd8\u5728\u5176\u6b21\uff0c\u4ed6\u4ee5\u8f7b\u8584\u7684\u5315\u9996\u800c\u80fd\u524a\u65ad\u6728\u6869\uff0c\u8fd9\u5185\u5bb6\u52b2\u529b\u5374\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u674e\u9038\u5fc3\u4e2d\u6697 \u6697\u559d\u91c7\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u90a3\u767d\u8863\u6b66\u58eb\u5df2\u524a\u65ad\u4e86\u4e03\u6839\u6728\u6869\uff0c\u5ffd\u5730\u53d6\u51fa\u4e09\u67c4\u98de\u5200\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u6700\u540e\u8fd9\u4e09\u67c4\u98de\u5200\uff0c\u6211\u8981\u540c\u65f6\u5c06\u4e09\u6839\u6728\u6869\u524a\u65ad\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u767b\u65f6\u5168\u573a\u8083\u9759\uff0c\u4eba\u4eba\u90fd\u7741 \u5927\u4e86\u773c\u775b\uff0c\u6ce8\u89c6\u767d\u8863\u6b66\u58eb\u7684\u4e09\u67c4\u98de\u5200\uff01 \u3000\u3000\u53ea\u89c1\u4ed6\u628a\u624b\u4e00\u626c\uff0c\u5374\u5e76\u4e0d\u89c1\u98de\u5200\u5411\u524d\u98de\u51fa\uff0c\u4f17\u4eba\u65b9\u89c9\u5947\u602a\uff0c\u9661\u7136\u95f4\u6709\u4eba\u5931\u58f0\u53eb\u9053\uff1a \u201c\u6349\u523a\u5ba2\u554a\uff01\u201d\u539f\u6765\u4ed6\u5411\u524d\u626c\u624b\uff0c\u98de\u5200\u5374\u4ece\u80cc\u540e\u98de\u51fa\uff0c\u4e09\u67c4\u98de\u529b\u90fd\u5230\u4e3b\u8003\u53f0\u4e0a\uff0c\u7adf\u662f\u7acb \u5fc3\u8981\u523a\u6740\u795e\u6b66\u8425\u7684\u90fd\u5c09\u674e\u660e\u4e4b\uff01 \u3000\u3000\u8fd9\u4e8b\u60c5\u6765\u5f97\u592a\u8fc7\u7a81\u7136\uff0c\u4f17\u4eba\u90fd\u6599\u4e0d\u5230\u4ed6\u53d1\u98de\u5200\u7684\u624b\u6cd5\u5982\u6b64\u5947\u5999\uff0c\u5f85\u5230\u8b66\u89c9\u4e4b\u65f6\uff0c\u90a3 \u4e09\u67c4\u98de\u5200\u5df2\u7ed9\u674e\u660e\u4e4b\u6253\u843d\uff0c\u767d\u8863\u6b66\u58eb\u5927\u58f0\u559d\u9053\uff1a\u201c\u6321\u6211\u8005\u6b7b\uff0c\u8ba9\u6211\u8005\u751f\uff01\u201d\u6325\u821e\u957f\u5251\uff0c \u62fc\u6b7b\u95ef\u51fa\u573a\u5916\uff0c\u6709\u4eba\u4e0a\u524d\u62e6\u622a\u7684\uff0c\u4ed6\u626c\u624b\u4fbf\u662f\u4e00\u67c4\u98de\u5200\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5df2\u6709\u4e09\u4e2a\u4eba\u53d7\u4e86\u4ed6 \u7684\u5251\u4f24\uff0c\u4e24\u4e2a\u4eba\u4e2d\u4e86\u4ed6\u7684\u98de\u5200\uff01\u4f17\u4eba\u90fd\u89c1\u8bc6\u8fc7\u4ed6\u98de\u5200\u7684\u5389\u5bb3\uff0c\u767b\u65f6\u5927\u4e71\uff01\u674e\u9038\u6b63\u8981\u95ea\u907f\uff0c \u90a3\u866c\u9aef\u6b66\u58eb\u5ffd\u5730\u5728\u4ed6\u8033\u8fb9\u53eb\u9053\uff1a\u201c\u5feb\u62e6\u4f4f\u523a\u5ba2\u3002\u201d\u970e\u773c\u4e4b\u95f4\u3002\u53ea\u89c1\u90a3\u767d\u8863\u6b66\u58eb\u7adf\u7136\u5411\u7740 \u674e\u9038\u5954\u6765\uff0c\u79bb\u8eab\u4e0d\u5230\u4e09\u4e08\uff0c\u4e00\u542c\u866c\u9aef\u6b66\u58eb\u547c\u53eb\uff0c\u626c\u624b\u4fbf\u662f\u4e00\u67c4\u98de\u5200\uff0c\u866c\u9aef\u6b66\u58eb\u5f2f\u8170\u4e00\u95ea\uff0c \u4e0d\u77e5\u662f\u6709\u610f\u8fd8\u662f\u65e0\u610f\uff0c\u624b\u8098\u5411\u674e\u9038\u4e00\u78b0\uff0c\u674e\u9038\u51b7\u4e0d\u53ca\u9632\uff0c\u7ed9\u4ed6\u649e\u5f97\u79fb\u52a8\u4e24\u7c73\uff0c\u98de\u5200\u6b63\u597d \u5bf9\u51c6\u4ed6\u7684\u5589\u5499\u98de\u6765\uff0c\u674e\u9038\u501f\u90a3\u4e00\u649e\u4e4b\u52bf\uff0c\u5411\u524d\u4e00\u4e2a\u6ed1\u6b65\uff0c\u582a\u582a\u907f\u8fc7\u90a3\u67c4\u98de\u5200\uff0c\u8bf4\u65f6\u8fdf\uff0c \u90a3\u65f6\u5feb\uff0c\u7b2c\u4e8c\u67c4\u98de\u5200\u53c8\u5230\uff0c\u674e\u9038\u62d4\u5251\u4e00\u6325\uff0c\u5c06\u98de\u5200\u6253\u843d\uff0c\u5c31\u5728\u8fd9\u970e\u90a3\u95f4\u767d\u8863\u6b66\u58eb\u5df2\u51b2\u5230 \u4e86\u674e\u9038\u9762\u524d\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u674e\u9038\u5fc3\u4e2d\u5df2\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff1a\u201c\u6349\u4ed6\uff0c\u8fd8\u662f\u4e0d\u6349\u4ed6\uff1f\u201d\u4e00\u65f6\u95f4 \u786e\u662f\u96be\u4ee5\u51b3\u65ad\u3002\u8fd9\u767d\u8863\u6b66\u58eb\u884c\u523a\u674e\u660e\u4e4b\uff0c\u8bf4\u6765\u5e94\u8be5\u662f\u548c\u674e\u9038\u540c\u4e00\u8def\u7684\u4eba\uff0c\u53ef\u662f\u674e\u9038\u4e0d\u6349 \u4ed6\uff0c\u672c\u8eab\u7acb\u5373\u4fbf\u8981\u9732\u51fa\u9a6c\u811a\u3002 \u3000\u3000\u4f46\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u767d\u8863\u6b66\u58eb\u7684\u957f\u5251\u5df2\u8fce\u9762\u523a\u5230\uff0c\u8fd9\u4e00\u5251\u53c8\u5feb\u53c8\u72e0\uff0c\u5251\u5c16\u6307\u7740\u4e86 \u674e\u9038\u7684\u54bd\u5589\uff0c\u5728\u8fd9\u6027\u547d\u503e\u9877\u4e4b\u9645\uff0c\u54ea\u5bb9\u5f97\u674e\u9038\u518d\u52a0\u8003\u8651\uff0c\u51b5\u4e14\u5b66\u6b66\u4e4b\u4eba\uff0c\u53d7\u5230\u654c\u4eba\u653b\u51fb\uff0c \u9632\u5fa1\u4e43\u662f\u672c\u80fd\uff0c\u674e\u9038\u5728\u8fd9\u7d27\u6025\u5173\u5934\uff0c\u4e0d\u81ea\u89c9\u7684\u4f7f\u51fa\u5251\u6cd5\u4e2d\u4e00\u62db\u6700\u7cbe\u5999\u7684\u62db\u6570\uff0c\u9752\u94dc\u5251\u8f7b \u8f7b\u4e00\u6296\uff0c\u7a81\u7136\u53cd\u63b8\u51fa\u53bb\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u5bf9\u65b9\u7684\u957f\u5251\u8361\u5f00\uff0c\u90a3\u767d\u8863\u6b66\u58eb\u7684\u5251\u6cd5\u4e5f\u6781\u5389 \u5bb3\uff0c\u500f\u7136\u95f4\u53c8\u5708\u4e86\u8f6c\u6765\uff0c\u5251\u5149\u8361\u8d77\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u7cbe\u8292\u75be\u8f6c\uff0c\u628a\u674e\u9038\u7684\u4e0a\u534a\u8eab\u5168\u7b3c\u7f69\u5728\u5251 \u5149\u4e4b\u4e0b\uff0c\u674e\u9038\u6025\u5fd9\u7528\u4e86\u4e00\u62db\u201c\u4e58\u98ce\u7834\u6d6a\u201d\uff0c\u9752\u94a2\u5251\u5411\u4e0a\u4e00\u6311\uff0c\u5c06\u5bf9\u65b9\u653b\u52bf\u7834\u53bb\u3002\u4f46\u89c1\u5251 \u5149\u6d41\u6563\uff0c\u6709\u5982\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u76f4\u6d12\u4e0b\u6765\uff0c\u90a3\u767d\u8863\u6b66\u58eb\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u62db\u6570\u53c8\u53d8\uff0c\u5251 \u5c16\u6296\u52a8\uff0c\u58f0\u82e5\u94f6\u86c7\u4e71\u63a3\uff0c\u4e00\u62db\u4e4b\u5185\uff0c\u8fde\u5241\u674e\u9038\u4e03\u5904\u8981\u5bb3\uff0c\u674e\u9038\u7529\u4e86\u4e00\u62db\u5ce8\u5d4b\u5251\u6cd5\u7684\u8d77\u624b \u5f0f\u201c\u62b1\u5143\u5b88\u4e00\u201d\uff0c\u957f\u5251\u4e00\u7acb\uff0c\u4fe8\u5982\u5728\u8eab\u5b50\u5468\u56f4\uff0c\u5e03\u8d77\u4e86\u4e00\u9053\u94a2\u5899\u94c1\u58c1\u3002\u90a3\u767d\u8863\u6b66\u58eb\u653b\u4e0d \u8fdb\u53bb\uff0c\u6b63\u5f85\u53d8\u62db\uff0c\u674e\u9038\u6df1\u6015\u4ed6\u8fd8\u6709\u4ec0\u4e48\u5389\u5bb3\u7684\u6740\u624b\uff0c\u6025\u5fd9\u62a2\u5148\u4e00\u6b65\u3002\u9661\u7136\u653b\u51fa\u3002\u500f\u7684\u4e00 \u5251\uff0c\u523a\u4e2d\u4e86\u90a3\u767d\u8863\u6b66\u58eb\u7684\u624b\u8155\uff01 \u3000\u3000\u4f17\u6b66\u58eb\u89c1\u523a\u5ba2\u88ab\u674e\u9038\u62e6\u4f4f\uff0c\u7eb7\u7eb7\u6d8c\u4e0a\uff0c\u795e\u6b66\u8425\u90a3\u4e24\u5927\u9ad8\u624b\u6700\u5148\u8d76\u5230\uff0c\u4e00\u4e2a\u4f7f\u51fa\u201c\u5927 \u64d2\u62ff\u201d\u624b\u6cd5\uff0c\u5c01\u4f4f\u4e86\u523a\u5ba2\u7684\u53cc\u624b\u3002\u53e6\u4e00\u4e2a\u98de\u8d77\u4e00\u811a\uff0c\u6b63\u4e2d\u8170\u80ef\uff0c\u767b\u65f6\u5c06\u8fd9\u540d\u523a\u5ba2\u8e22\u7ffb\uff0c \u8fd9\u4e43\u662f\u56e0\u4e3a\u5251\u5ba2\u624b\u8155\u53d7\u4f24\uff0c\u51fa\u5251\u65e0\u529b\uff0c\u8981\u4e0d\u7136\u795e\u6b66\u8425\u7684\u4e24\u5927\u9ad8\u624b\u6b66\u529f\u867d\u5f3a\uff0c\u4e5f\u7edd\u4e0d\u53ef\u80fd \u5982\u6b64\u5bb9\u6613\u4fbf\u5c06\u4ed6\u5236\u670d\u3002 \u3000\u3000\u795e\u6b66\u8425\u8fd9\u4e24\u5927\u9ad8\u624b\uff0c\u4e00\u4e2a\u53d6\u51fa\u811a\u9563\u624b\u94d0\u5c06\u523a\u5ba2\u9501\u4e0a\uff0c\u53e6\u4e00\u4e2a\u5219\u5f20\u5f00\u53cc\u624b\u62e6\u4f4f\u4f17\u4eba\uff0c \u6717\u58f0\u8bf4\u9053\uff1a\u201c\u523a\u5ba2\u5c31\u64d2\uff0c\u6ca1\u6709\u4e8b\u5566\u3002\u4f60\u4eec\u90fd\u9000\u56de\u53bb\uff0c\u7b49\u5019\u8003\u8bd5\uff0c\u4e0d\u53ef\u9a9a\u4e71\u3002\u201d\u521a\u624d\u90a3\u4e2a \u4e0e\u674e\u9038\u6bd4\u5251\u7684\u5d14\u4ef2\u5143\u4e5f\u5728\u5176\u4e2d\uff0c\u89c1\u674e\u9038\u5728\u4e09\u62db\u4e4b\u5185\uff0c\u5c06\u523a\u5ba2\u5241\u4f24\uff0c\u8fd9\u624d\u77e5\u9053\u674e\u9038\u7684\u5251\u6cd5 \u5176\u5b9e\u8fd8\u8fdc\u8fdc\u5728\u4ed6\u4e4b\u4e0a\uff0c\u4e0d\u7531\u5f97\u50b2\u6c14\u5168\u6d88\uff0c\u6084\u7136\u9000\u4e0b\u3002 \u3000\u3000\u674e\u9038\u5fc3\u5934\u5374\u662f\u96be\u8fc7\u4e4b\u6781\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u523a\u5ba2\u4e00\u8eab\u662f\u80c6\uff0c\u6b66\u529f\u4e4b\u5f3a\uff0c\u4e0d\u5728\u6211\u4e0b\uff01\u786e\u5b9e\u7b97 \u5f97\u662f\u4e2a\u82f1\u96c4\u4eba\u7269\uff0c\u5982\u4eca\u5374\u88ab\u6211\u5bb3\u4e86\u4ed6\u4e86\u3002\u201d\u770b\u90a3\u523a\u5ba2\uff0c\u53ea\u89c1\u4ed6\u7684\u76ee\u5149\u4e5f\u6b63\u5411\u81ea\u5df1\u5c04\u6765\uff0c \u773c\u5149\u9732\u51fa\u6028\u6bd2\u7684\u795e\u8272\u3002\u674e\u9038\u5fc3\u4e2d\u9178\u75db\uff0c\u626d\u5f00\u4e86\u5934\uff0c\u4e0d\u6562\u770b\u4ed6\u3002\u53ea\u542c\u5f97\u795e\u6b66\u8425\u90a3\u4e24\u5927\u9ad8\u624b \u8bf4\u9053\uff1a\u201c\u4eca\u6b21\u64d2\u4e86\u523a\u5ba2\uff0c\u4f60\u7684\u529f\u52b3\u6700\u5927\uff0c\u6211\u4eec\u7ed9\u4f60\u7980\u660e\uff0c\u674e\u5927\u4eba\u5b9a\u5f53\u6709\u6240\u91cd\u8d4f\u3002\u201d\u674e\u9038 \u81ea\u6028\u81ea\u6127\uff0c\u53ea\u597d\u6de1\u6de1\u7684\u8c22\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u9a9a\u52a8\u505c\u606f\u3002\u8fc7\u4e0d\u591a\u4e45\uff0c\u674e\u660e\u4e4b\u5ba3\u5e03\u4eca\u65e5\u7684\u9009\u62d4\u8bd5\u5b8c\u6bd5\uff0c\u8fd8\u6709\u4e00\u5c0f\u90e8\u4efd\u6765\u8bd5\u7684\uff0c\u660e\u65e5 \u518d\u7eed\u4e3e\u884c\u3002\u674e\u9038\u89c1\u4ed6\u5e76\u6ca1\u6709\u7279\u522b\u53ec\u89c1\u81ea\u5df1\uff0c\u867d\u7136\u6709\u70b9\u7591\u5fc3\uff0c\u5374\u4e5f\u514d\u4e86\u8bb8\u591a\u70e6\u607c\u3002\u5f53\u4e0b\u968f \u7740\u4f17\u6b66\u58eb\u51fa\u573a\uff0c\u4e71\u54c4\u54c4\u4e2d\u53ea\u542c\u5f97\u4f17\u4eba\u8fd8\u5728\u8c08\u8bba\u523a\u5ba2\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u674e\u9038\u6df7\u5728\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u4f4e\u5934\u75be\u8d70\uff0c\u521a\u521a\u8d70\u51fa\u573a\u5b50\uff0c\u80a9\u5934\u5ffd\u5730\u7ed9\u4eba\u62cd\u4e86\u4e00\u4e0b\uff0c\u5374\u539f\u6765\u5c31 \u662f\u90a3\u866c\u9aef\u6b66\u58eb\uff0c\u53ea\u542c\u5f97\u4ed6\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5144\u53f0\u6b66\u529f\u4e4b\u9ad8\uff0c\u5c1a\u5728\u6211\u610f\u6599\u4e4b\u5916\u3002\u5251\u672f\u4e4b\u5999\uff0c\u6211 \u770b\u4fbf\u662f\u5c09\u8fdf\u70af\u590d\u51fa\uff0c\u8c37\u795e\u7fc1\u5728\u573a\uff0c\u4ea6\u4e0d\u8fc7\u5982\u662f\uff0c\u4eca\u65e5\u771f\u662f\u4ee4\u6211\u5927\u5f00\u773c\u754c\u4e86\uff01\u201d\u674e\u9038\u6697\u7779 \u53eb\u82e6\uff0c\u542c\u4ed6\u9996\u5148\u4fbf\u63d0\u51fa\u4e86\u81ea\u5df1\u7684\u5e08\u7236\uff0c\u5fc3\u77e5\u521a\u624d\u5728\u548c\u523a\u5ba2\u6597\u5251\u4e4b\u65f6\uff0c\u88ab\u8feb\u4f7f\u51fa\u5e08\u95e8\u7edd\u62db\uff0c \u5df2\u662f\u9732\u4e86\u5e95\u4e86\u3002\u5f53\u4e0b\u53ea\u597d\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5144\u8bf4\u7b11\u8bdd\u4e86\uff0c\u6211\u600e\u80fd\u548c\u90a3\u4e24\u4f4d\u540d\u5bb6\u76f8\u6bd4 \u5462\uff1f\u201d\u90a3\u866c\u9aef\u6b66\u58eb\u53c8\u9053\uff1a\u201c\u5144\u53f0\u4eca\u65e5\u7acb\u6b64\u5927\u529f\uff0c\u5b9a\u81ba\u91cd\u8d4f\u3002\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u505a\u5929\u540e\u8fd1\u8eab\u7684\u536b \u571f\uff0c\u4e0a\u63a5\u5929\u989c\uff0c\u90a3\u5c31\u66f4\u5bb9\u6613\u98de\u9ec4\u817e\u8fbe\u4e86\u3002\u5c0f\u5f1f\u4ed6\u65e5\u8fd8\u671b\u6211\u5144\u63d0\u643a\u5462\uff01\u201d\u674e\u9038\u542c\u4ed6\u8bdd\u4e2d\u4f3c \u542b\u522b\u610f\uff0c\u83ab\u6d4b\u9ad8\u6df1\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u98df\u541b\u4e4b\u7984\uff0c\u5fe0\u541b\u4e4b\u96cf\uff0c\u64d2\u51f6\u6740\u8d3c\uff0c\u8fd9\u662f\u6211\u8f88\u4efd\u6240\u5f53\u4e3a\uff0c \u5c0f\u5f1f\u54ea\u91cc\u662f\u671b\u4ec0\u4e48\u539a\u8d4f\u5462\uff1f\u201d\u90a3\u866c\u9aef\u6b66\u58eb\u671b\u4e86\u674e\u9038\u4e00\u773c\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u543e\u5144\u5982\u6b64\u5fe0\u5fc3\u7231 \u56fd\uff0c\u66f4\u6559\u5c0f\u5f1f\u4f69\u670d\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u65e0\u6cd5\u6446\u8131\u4ed6\u7684\u7ea0\u7f20\uff0c\u53ea\u597d\u548c\u4ed6\u95f2\u804a\uff0c\u4e92\u901a\u59d3\u540d\uff0c\u59cb\u77e5\u4ed6\u662f\u5c71\u4e1c\u4e34\u6dc4\u4eba\u6c0f\uff0c\u540d\u53eb \u5357\u5bab\u5c1a\uff0c\u518d\u6253\u542c\u90a3\u4e2a\u523a\u5ba2\uff0c\u5374\u662f\u4eac\u57ce\u91cc\u7684\u4eba\uff0c\u540d\u53eb\u767d\u5143\u5316\uff0c\u674e\u9038\u9887\u611f\u610f\u5916\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u8f85\u9996\u53bf\uff0c\u9009\u4eba\u5b9a\u7136\u7279\u522b\u5c0f\u5fc3\uff0c\u5374\u600e\u4fdd\u8350\u51fa\u4e00\u4e2a\u523a\u5ba2\u6765\uff1f\u53ea\u6015\u90a3\u4f4d\u77e5\u53bf\u5927\u4eba\uff0c\u6700\u5c11\u4e5f\u8981 \u88ab\u7275\u7d2f\u4e0b\u72f1\u4e86\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e24\u65e5\uff0c\u795e\u6b66\u8425\u6240\u8981\u8865\u5145\u7684\u4e00\u767e\u540d\u536b\u571f\u5df2\u7ecf\u5168\u90e8\u9009\u62d4\u51fa\u6765\uff0c\u90a3\u5357\u5bab\u5c1a\u4e5f\u5728\u53d6\u5f55\u4e4b \u5217\uff0c\u800c\u4e14\u6070\u597d\u5206\u914d\u4e0e\u674e\u9038\u540c\u5728\u4e00\u8d77\uff0c\u90fd\u662f\u201c\u5916\u5bab\u8f6e\u503c\u536b\u58eb\u201d\uff0c\u7687\u5bab\u5206\u4e3a\u4e24\u4e2a\u90e8\u4efd\uff0c\u5916\u9762 \u7684\u51e0\u5ea7\u5bab\u6bbf\uff0c\u662f\u7687\u5e1d\u63a5\u89c1\u81e3\u5de5\uff0c\u4ee5\u53ca\u6bbf\u9601\u5b66\u58eb\u62df\u7a3f\u7684\u5730\u65b9\uff0c\u6df1\u5bab\u5185\u82d1\uff0c\u5219\u662f\u540e\u5983\u5c45\u4f4f\u7684 \u5730\u65b9\uff0c\u201c\u5916\u5bab\u201d\u548c\u201c\u5185\u82d1\u201d\u95e8\u7981\u68ee\u4e25\uff0c\u4e0d\u80fd\u903e\u8d8a\u3002\u674e\u9038\u53ea\u88ab\u9009\u4f5c\u201c\u5916\u5b98\u8f6e\u503c\u6b66\u58eb\u201d\uff0c\u63a5 \u8fd1\u6b66\u5219\u5929\u7684\u673a\u4f1a\u5fae\u4e4e\u5176\u5fae\uff0c\u5fc3\u4e2d\u9887\u4e3a\u5931\u671b\u3002 \u3000\u3000\u518d\u8fc7\u4e24\u65e5\uff0c\u674e\u9038\u5c1a\u672a\u5f97\u5230\u674e\u660e\u4e4b\u53ec\u89c1\uff0c\u66f4\u751f\u7591\u8651\u3002\u6700\u521d\u4e24\u65e5\uff0c\u8fd8\u672a\u8f6e\u5230\u4ed6\u5f53\u503c\uff0c\u8fd9 \u65e5\u4ed6\u6b63\u5750\u5728\u5bbf\u536b\u623f\u4e2d\uff0c\u95f7\u95f7\u4e0d\u4e50\uff0c\u90a3\u866c\u9aef\u6b66\u571f\u5357\u5b98\u5c1a\u5ffd\u7136\u53c8\u8d70\u8fdb\u6765\uff0c\u548c\u4ed6\u95f2\u804a\uff0c\u8bf4\u9053\uff1a \u201c\u53ef\u60dc\u6211\u4eec\u53ea\u662f\u5916\u5bab\u536b\u58eb\uff0c\u89c1\u4e0d\u5230\u5185\u82d1\u98ce\u5149\uff01\u201d\u674e\u9038\u552f\u552f\u8bfa\u8bfa\uff0c\u5357\u5b98\u5c1a\u53c8\u9053\uff1a\u201c\u542c\u8bf4\u5929 \u540e\u4f4f\u5728\u7981\u82d1\u51cc\u6ce2\u5b9c\u4e2d\uff0c\u6c34\u6728\u6e05\u534e\uff0c\u65e0\u5f02\u4ed9\u5e9c\u3002\u6211\u6709\u4e2a\u670b\u53cb\u662f\u5927\u5185\u536b\u571f\uff0c\u4ed6\u66fe\u7ecf\u8fdb\u53bb\u8fc7\uff0c \u8d5e\u53e3\u4e0d\u7edd\u3002\u51cc\u6ce2\u5bab\u5728\u592a\u6ce2\u6d6a\u8fb9\uff0c\u524d\u9762\u662f\u4ee5\u524d\u5510\u745a\u7687\u5e1d\u4f4f\u7684\u4e7e\u5143\u6bbf\uff0c\u4e7e\u5143\u6bbf\u867d\u7136\u5bcc\u4e3d\u5802\u7687 \u5374\u8fdc\u4e0d\u53ca\u51cc\u6ce2\u5b9c\u7684\u6e05\u96c5\u7edd\u4fd7\u3002\u201d\u8fd9\u4e9b\u5730\u65b9\uff0c\u90fd\u662f\u674e\u9038\u5c0f\u65f6\u5019\u73a9\u800d\u7684\u5730\u65b9\uff0c\u5f53\u7136\u5341\u5206\u719f\u6089\uff0c \u6697\u6697\u5947\u602a\u5357\u5b98\u5c1a\u4e3a\u4ec0\u4e48\u8981\u548c\u4ed6\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u597d\u50cf\u8981\u6545\u610f\u6cc4\u9732\u5929\u540e\u7684\u5c45\u5904\u7ed9\u4ed6\u77e5\u9053\u4f3c\u7684\u3002\u6b63 \u8bf4\u8bdd\u95f4\uff0c\u795e\u6b66\u8425\u90fd\u5c09\u5ffd\u7136\u6d3e\u4eba\u8fdb\u6765\uff0c\u53ec\u674e\u9038\u8fdb\u5bab\uff0c\u674e\u9038\u4e00\u671b\u5929\u8272\uff0c\u5df2\u8fd1\u9ec4\u660f\uff0c\u5fc3\u4e2d\u4e0d\u7981 \u7591\u4e91\u6697\u8d77\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u6005\u60c6\u6069\u4ec7\u96be\u81ea\u89e3 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u5341\u4e09\u56de\u3000\u6005\u60c6\u6069\u4ec7\u96be\u81ea\u89e3 \u3000\u3000\u674e\u9038\u5fc3\u4e2d\u867d\u7136\u6709\u70b9\u8d77\u7591\uff0c\u4f46\u957f\u5b98\u5ba3\u53ec\uff0c\u90a3\u6562\u7a3d\u5ef6\uff0c\u53ea\u597d\u7acb\u5373\u8ddf\u968f\u6765\u4eba\u540c\u5f80\uff0c\u8de8\u6b65\u51fa \u95e8\u4e4b\u65f6\uff0c\u53ea\u89c1\u5357\u5bab\u5c1a\u6697\u6697\u5411\u4ed6\u4f7f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f20\u5144\u673a\u4f1a\u5230\u4e86\uff0c\u597d\u81ea\u4e3a\u4e4b\uff01\u201d \u8bf4\u8bdd\u4f3c\u5e26\u53cc\u5173\uff0c\u674e\u9038\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u9707\u3002 \u3000\u3000\u674e\u660e\u4e4b\u4f4f\u5728\u4e94\u51e4\u697c\u8fb9\u7684\u4e00\u5ea7\u504f\u6bbf\uff0c\u90a3\u662f\u5185\u82d1\u4e0e\u5916\u5bab\u4ea4\u754c\u4e4b\u5904\uff0c\u674e\u9038\u5230\u65f6\uff0c\u674e\u660e\u4e4b\u5728 \u865a\u4f4d\u4ee5\u5f85\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u5403\u8fc7\u665a\u996d\u5427\uff1f\u201d\u4fbf\u5373\u8bf7\u4ed6\u5165\u5e2d\uff0c\u674e\u9038\u89c1\u4ed6\u989c\u8272\u548c\u853c\uff0c\u7a0d\u7a0d \u5fc3\u5bbd\u3002 \u3000\u3000\u674e\u660e\u4e4b\u5f88\u79f0\u8d5e\u4ed6\u7684\u6b66\u529f\uff0c\u63a5\u7740\u53c8\u95ee\u4ed6\u7684\u8eab\u4e16\u548c\u5b66\u827a\u7684\u7ecf\u8fc7\uff0c\u8fd9\u4e9b\u95ee\u9898\u65e9\u5728\u610f\u6599\u4e4b\u4e2d\uff0c \u4ed6\u5411\u5f20\u4e4b\u5947\u8981\u90a3\u4efd\u8350\u4e66\u4e4b\u65f6\uff0c\u4e5f\u65e9\u95ee\u8fc7\u5f20\u4e4b\u5947\u7684\u4e86\u3002\u5f53\u4e0b\u4fbf\u6309\u7167\u81ea\u5df1\u6240\u77e5\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684 \u56de\u7b54\uff0c\u5e78\u597d\u5e76\u6ca1\u9732\u51fa\u4ec0\u4e48\u7834\u7efd\uff0c\u674e\u660e\u4e4b\u4e5f\u4e0d\u600e\u6837\u4ed4\u7ec6\u76d8\u67e5\u3002\u9152\u8fc7\u4e09\u5de1\uff0c\u674e\u660e\u4e4b\u548c\u674e\u9038\u5e72 \u4e86\u4e00\u5927\u676f\u9152\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u90a3\u65e5\u4f60\u5728\u6821\u573a\u4e0a\u6349\u5230\u523a\u5ba2\u7684\u4e8b\u60c5\uff0c\u6211\u5df2\u6577\u544a\u5929\u540e\u4e86\u3002\u7ecf\u8fc7\u5ba1 \u95ee\u8fd9\u523a\u5bb9\u4e43\u662f\u5f90\u656c\u4e1a\u6240\u6307\u4f7f\u7684\uff0c\u73b0\u5728\u6211\u5c31\u8981\u4ea4\u7ed9\u4f60\u4e00\u4ef6\u5dee\u4e8b\u3002\u201d\u674e\u9038\u5fc3\u5934\u201c\u535c\u901a\u201d\u4e00\u8df3\uff0c \u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u4f46\u51ed\u5927\u4eba\u5429\u5490\u3002\u201d\u674e\u660e\u4e4b\u9053\uff1a\u201c\u5929\u540e\u6709\u4ee4\uff0c\u53eb\u6211\u628a\u8fd9\u4e2a\u523a\u5ba2\u9001\u7ed9\u5927\u5185\u603b\u7ba1 \u518d\u52a0\u5ba1\u95ee\uff0c\u4f60\u5c31\u6682\u65f6\u7559\u5728\u603b\u7ba1\u5927\u4eba\u90a3\u513f\uff0c\u4e5f\u8bb8\u5929\u540e\u8fd8\u8981\u53ec\u89c1\u4f60\u5462\u3002\u201d\u674e\u9038\u542c\u4e86\uff0c\u4e00\u5219\u4ee5 \u559c\uff0c\u4e00\u5219\u4ee5\u4ff1\u3002\u559c\u662f\u53ef\u80fd\u6709\u673a\u4f1a\u89c1\u5230\u6b66\u5219\u5929\uff0c\u4ff1\u8005\u662f\u6015\u523a\u5ba2\u5728\u4ed6\u624b\u4e0a\u9001\u4e86\u6027\u547d\u3002 \u3000\u3000\u674e\u660e\u4e4b\u53c8\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4ef6\u79d8\u5bc6\u7684\u5dee\u4f7f\uff0c\u4e0d\u8bb8\u7ed9\u5916\u4eba\u77e5\u9053\u3002\u4f60\u5929\u9ed1\u4e4b\u540e\uff0c\u62bc\u4ed6\u8fdb\u53bb\uff0c \u514d\u5f97\u60f9\u4eba\u6ce8\u76ee\u3002\u56e0\u4e3a\u6050\u6015\u5bab\u5ef7\u5185\u5916\u8fd8\u9690\u6709\u88f4\u708e\u7684\u515a\u7fbd\uff0c\u82e5\u7136\u7ed9\u4ed6\u4eec\u77e5\u9053\u4f60\u662f\u62bc\u89e3\u523a\u5ba2\u7684 \u8bdd\uff0c\u53ea\u6015\u4ed6\u4eec\u4f1a\u4e2d\u9014\u88ad\u51fb\uff0c\u6240\u4ee5\u8981\u5206\u5916\u5c0f\u5fc3\u3002\u597d\u5728\u4f60\u7684\u6b66\u529f\u5728\u523a\u5ba2\u4e4b\u4e0a\uff0c\u82e5\u6709\u4ec0\u4e48\u610f\u5916\uff0c \u4e5f\u5c3d\u53ef\u5236\u5f97\u4f4f\u4ed6\u3002\u201d\u674e\u9038\u8fd9\u624d\u77e5\u9053\uff0c\u4f55\u4ee5\u8981\u5728\u9ec4\u660f\u65f6\u5206\u53ec\u89c1\u4ed6\u524d\u6765\u7684\u9053\u7406\u3002 \u3000\u3000\u63a5\u7740\u674e\u660e\u4e4b\u5c06\u4eca\u665a\u5bab\u4e2d\u5bbf\u536b\u7684\u53e3\u4ee4\uff0c\u4ee5\u53ca\u600e\u6837\u5230\u603b\u7ba1\u5e9c\u4ea4\u5dee\u7b49\u7b49\u624b\u7eed\u8bf4\u4e86\u3002\u4ea4\u4ee3\u6e05 \u695a\uff0c\u4fbf\u53eb\u624b\u4e0b\u7684\u6b66\u58eb\u5c06\u90a3\u540d\u523a\u5ba2\u7275\u51fa\u4e86\u3002\u53ea\u89c1\u4ed6\u773c\u7736\u63a2\u9677\uff0c\u6b65\u5c65\u7792\u73ca\u3002\u60f3\u5fc5\u5728\u8fd9\u4e09\u56db\u5929 \u53d7\u4e86\u8bb8\u591a\u6298\u78e8\u3002 \u3000\u3000\u90a3\u523a\u5ba2\u53cc\u773c\u5706\u7741\uff0c\u72e0\u72e0\u76ef\u7740\u674e\u9038\uff0c\u5634\u5507\u5fae\u5fae\u5f00\u542f\uff0c\u60f3\u662f\u5df2\u88ab\u70b9\u4e86\u54d1\u7a74\u4e0d\u51fa\u58f0\u3002\u674e\u9038 \u751a\u662f\u60b2\u6124\uff0c\u786c\u8d77\u5fc3\u80a0\uff0c\u62d6\u7740\u523a\u5ba2\u7684\u624b\uff0c\u9886\u4e86\u91d1\u724c\uff0c\u4fbf\u62bc\u4ed6\u8fdb\u5bab\u3002 \u3000\u3000\u5bab\u4e2d\u6709\u4eba\u63a5\u5f15\uff0c\u6307\u70b9\u4ed6\u53bb\u7ba1\u5e9c\u53bb\u7684\u8def\u5f84\uff0c\u4fbf\u53eb\u4ed6\u81ea\u53bb\u3002\u674e\u9038\u4ece\u5fa1\u82b1\u56ed\u4e2d\u7a7f\u8fc7\uff0c\u5728\u6de1 \u6708\u758f\u661f\u4e4b\u4e0b\uff0c\u7ed5\u8fc7\u56de\u5eca\u66f2\u68da\uff0c\u5206\u82b1\u62c2\u67f3\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u8e0f\u8fc7\u4ed6\u65e7\u6e38\u4e4b\u5730\uff0c\u5fc3\u4e2d\u65e0\u9650\u60b2\u9178\u3002 \u8d70\u4e86\u4e00\u4f1a\uff0c\u8fc7\u4e86\u4e00\u5ea7\u5047\u5c71\u65c1\u56db\u4e0b\u65e0\u4eba\uff0c\u90a3\u523a\u5ba2\u5ffd\u7136\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u5bb3\u6211\u7684\u547d\uff1f\u201d\u674e\u9038 \u9aa4\u5403\u4e00\u60ca\uff0c\u8fd9\u523a\u5ba2\u7684\u6b66\u529f\uff0c\u81ea\u5df2\u51b2\u5173\u89e3\u7a74\uff0c\u4e0d\u8db3\u4e3a\u5947\uff0c\u53eb\u674e\u904d\u5403\u60ca\u7684\u662f\uff1a\u8fd9\u523a\u5ba2\u7684\u8bdd\u5355 \u5200\u76f4\u5165\uff0c\u5374\u5b9e\u53eb\u4ed6\u96be\u4ee5\u56de\u7b54\u3002 \u3000\u3000\u90a3\u523a\u5ba2\u53c8\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u662f\u60f3\u6c42\u53d6\u529f\u540d\u5bcc\u8d35\u7f62\u4e86\uff0c\u662f\u4e48\uff1f\u4f60\u5bb3\u4e86\u6211\uff0c\u6700\u591a\u662f\u4f60\u505a\u4e00\u4e2a \u7edf\u9886\uff0c\u6216\u8005\u662f\u7ed9\u4f60\u505a\u4e2a\u5927\u5185\u536b\u571f\u3002\u4f60\u80af\u542c\u6211\u7684\u8bdd\u3002\u5305\u4f60\u83b7\u5f97\u66f4\u5927\u7684\u529f\u540d\u66f4\u5927\u7684\u5bcc\u8d35\uff01\u201d \u674e\u9038\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u90a3\u523a\u5ba2\u9053\uff1a\u201c\u54b1\u4eec\u5168\u529b\u5c06\u6b66\u5219\u5929\u6740\u4e86\uff0c\u4f60\u5c31\u662f\u5927\u5510\u590d\u56fd\u7684\u529f\u81e3\uff01\u201d \u3000\u3000\u8fd9\u4e00\u5239\u90a3\uff0c\u674e\u9038\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u529f\u540d\uff0c\u4e0d\u60f3\u5bcc\u8d35\u3002\u201d\u90a3\u523a\u5ba2 \u6014\u4e86\u4e00\u6014\uff0c\u674e\u9038\u5411\u4ed6\u671b\u4e86\u4e00\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u4f46\u6211\u613f\u610f\u653e\u4f60\uff0c\u6211\u4e5f\u613f\u610f\u4e0e\u4f60\u4e00\u540c\u53bb\u523a\u6740\u6b66\u5219 \u5929\uff01\u201d\u90a3\u523a\u5ba2\u970e\u970e\u773c\u775b\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u674e\u9038\u62bd\u51fa\u5b9d\u5251\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u4ed6\u624b\u94d0\u524a\u65ad\uff0c \u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u5c31\u53bb\uff01\u201d\u90a3\u523a\u5ba2\u7741\u5927\u4e86\u773c\u775b\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u90a3\u523a\u5ba2\u9053\uff1a\u201c\u6211\u662f\u4eac\u90fd\u767d\u5143\u5316\uff0c\u5927\u5510\u7684\u5b50\u6c11\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u662f\u9ad8\u7956\u7687\u5e1d\u7684\u66fe\u5b59\uff0c\u6211\u53eb \u674e\u9038\uff01\u201d\u767d\u5143\u5316\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u82f1\u56fd\u516c\u672c\u6765\u53eb\u6211\u6295\u5954\u4f60\u7684\uff0c\u60f3\u4e0d\u5230\u54b1\u4eec\u7adf\u4f1a\u8fd9\u6837 \u89c1\u9762\uff01\u201d \u3000\u3000\u674e\u9038\u62b1\u8d77\u767d\u5143\u5316\u4fbf\u8d70\uff0c\u4ece\u5fa1\u82b1\u56ed\u7a7f\u8fc7\uff0c\u8d70\u5230\u592a\u6db2\u6c60\u8fb9\uff0c\u51cc\u6ce2\u5bab\u5df2\u7ecf\u5728\u671b\u3002\u674e\u9038\u9053\uff1a \u201c\u767d\u5144\uff0c\u4f60\u66ff\u6211\u628a\u98ce\uff0c\u82e5\u7136\u7ed9\u4eba\u53d1\u89c9\uff0c\u4f60\u65bd\u5c55\u4f60\u7684\u98de\u5200\u7edd\u6280\uff0c\u5c06\u4ed6\u6740\u4e86\uff01\u201d\u6478\u51fa\u51e0\u67c4\u5315 \u9996\uff0c\u4ea4\u7ed9\u4e86\u767d\u5143\u5316\uff0c\u90a3\u662f\u4ed6\u65e9\u5c31\u85cf\u5728\u8eab\u4e0a\uff0c\u51c6\u5907\u523a\u6740\u6b66\u5219\u5929\u7684\u3002\u4ed6\u7ed9\u81ea\u5df1\u7559\u4e0b\u4e86\u4e24\u628a\uff0c \u4f59\u4e0b\u7684\u90fd\u4ea4\u7ed9\u4e86\u767d\u65e0\u5316\u3002\u767d\u5143\u5316\u95ee\u9053\uff1a\u201c\u6bbf\u4e0b\u6ca1\u6709\u7ea6\u5176\u4ed6\u7684\u4eba\u540c\u6765\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u5c31 \u662f\u54b1\u4eec\u4e24\u4eba\u4e86\uff0c\u4f60\u5bb3\u6015\u4e48\uff1f\u201d\u767d\u5143\u5316\u7b11\u9053\uff1a\u201c\u6211\u82e5\u662f\u5bb3\u6015\uff0c\u4e5f\u4e0d\u6562\u5728\u6821\u573a\u4e0a\u884c\u523a\u674e\u660e\u4e4b \u4e86\u3002\u201d \u3000\u3000\u51cc\u6ce2\u5bab\u77d7\u7acb\u5728\u592a\u6db2\u6c60\u8fb9\uff0c\u80cc\u540e\u662f\u4e00\u5ea7\u5047\u5c71\u3002\u674e\u9038\u53eb\u767d\u5143\u5316\u85cf\u5728\u5047\u5c71\u5185\u66ff\u4ed6\u628a\u98ce\uff0c\u7acb \u5373\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u4ece\u5047\u5c71\u8df3\u5230\u4e86\u5bab\u6bbf\u7684\u7409\u7483\u74e6\u9762\u3002\u51cc\u6ce2\u5bab\u5185\u662f\u5341\u51e0\u680b\u623f\u5c4b\uff0c\u4e2d\u95f4\u7684\u4e00\u5ea7 \u623f\u5b50\u900f\u51fa\u706f\u706b\u7684\u6fc0\u5149\uff0c\u674e\u9038\u5728\u74e6\u9762\u4e0a\u86c7\u884c\u6ed1\u8d70\uff0c\u8f6c\u77ac\u4e4b\u95f4\u5c31\u6293\u5230\u4e86\u90a3\u95f4\u623f\u5b50\u7684\u6a90\u5934\uff0c\u7559 \u5fc3\u5bdf\u7740\u56db\u5468\u7684\u52a8\u9759\uff0c\u5e76\u4e0d\u89c1\u6709\u536b\u58eb\u5de1\u903b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6b66\u5219\u5929\u7edd\u5bf9\u6599\u4e0d\u5230\u4f1a\u6709\u523a\u5ba2\u95ef\u8fdb \u6df1\u5bab\uff0c\u5979\u5982\u6b64\u5927\u610f\uff0c\u6d3b\u8be5\u547d\u7edd\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u7528\u4e86\u4e00\u4e2a\u201c\u73cd\u73e0\u5012\u5377\u5e18\u201d\u7684\u59ff\u52bf\uff0c\u53cc\u8db3\u6302\u7740\u5c4b\u6a90\uff0c\u63a2\u5934\u5185\u671b\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c \u5f97\u4e00\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5929\u540e\uff0c\u4f60\u592a\u52b3\u795e\u5566\uff01\u201d\u8fd9\u662f\u4e0a\u5b98\u5a49\u513f\u7684\u58f0\u97f3\uff0c\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c \u51e0\u4e4e\u8dcc\u5012\uff0c\u4ed6\u6240\u542c\u5230\u7684\u5173\u4e8e\u5a49\u513f\u7684\u6d88\u606f\u679c\u7136\u662f\u771f\u7684\uff01\u201c\u5a49\u513f\u679c\u7136\u5fd8\u6389\u4e86\u7236\u6bcd\u4e4b\u4ec7\uff0c\u5f52\u987a \u4ec7\u4eba\u4e86\uff01\u201d\u674e\u9038\u65e0\u9650\u5931\u671b\uff0c\u65e0\u9650\u60b2\u75db\uff0c\u4f46\u89c9\u70ed\u8840\u6cb8\u817e\uff0c\u4e0d\u80fd\u81ea\u5df1\uff01 \u3000\u3000\u7136\u800c\u674e\u9038\u518d\u4e00\u5f20\u671b\uff0c\u53c8\u597d\u4f3c\u7ed9\u4e00\u76c6\u51b7\u6c34\u8fce\u5934\u6cfc\u4e0b\uff0c\u767b\u65f6\u53eb\u4ed6\u51b7\u4e86\u534a\u622a\uff0c\u4f46\u89c1\u6b66\u5219\u5929 \u548c\u4e0a\u5b98\u5a49\u513f\u76f8\u5bf9\u800c\u5750\uff0c\u8fd8\u6709\u4e00\u4f4d\u5c11\u5973\u7ad9\u5728\u6b66\u5219\u5929\u7684\u65c1\u8fb9\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u7adf\u662f\u6b66\u7384\u971c\uff01\u674e\u9038 \u4e00\u7247\u832b\u7136\uff0c\u6263\u5728\u624b\u4e2d\u7684\u5315\u9996\u53d1\u4e0d\u51fa\u53bb\uff0c\u6709\u6b66\u7384\u971c\u5728\u6b66\u5219\u5929\u7684\u8eab\u8fb9\uff0c\u4eca\u665a\u662f\u7edd\u5bf9\u4e0d\u80fd\u6210\u4e8b \u4e86\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u6b66\u7384\u971c\u95ee\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u4eca\u665a\u662f\u60f3\u89c1\u90a3\u4e2a\u523a\u5ba2\u4e48\uff1f\u201d \u3000\u3000\u6b66\u5219\u5929\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u89c1\u90a3\u523a\u5ba2\uff0c\u6211\u5012\u662f\u60f3\u89c1\u90a3\u64d2\u4f4f\u523a\u5ba2\u7684\u4eba\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u542c\u8bf4 \u90a3\u4eba\u7684\u5251\u6cd5\u975e\u5e38\u795e\u5999\uff0c\u8fde\u674e\u660e\u4e4b\u4e5f\u770b\u4e0d\u51fa\u4ed6\u7684\u5bb6\u6570\u6765\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6240\u4ee5\u90a3\u523a\u5ba2\u6ca1\u4ec0 \u4e48\u5947\u602a\uff0c\u8fd9\u4e2a\u4eba\u5374\u662f\u6709\u70b9\u5947\u602a\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u542c\u674e\u660e \u4e4b\u8bf4\uff0c\u4ed6\u662f\u5d4b\u5dde\u4eba\u6c0f\uff0c\u53eb\u505a\u5f20\u4e4b\u5947\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u53ef\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b87\u554a\uff01\u201d \u4e0a\u5b98\u5a49\u513f\u95ee\u9053\uff1a\u201c\u5929\u540e\uff0c\u6211\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u751a\u4e0d\u660e\u767d\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u4e0a\u5b98\u5a49\u513f \u9053\uff1a\u201c\u8fd9\u523a\u5ba2\u662f\u4eac\u90fd\u53bf\u4fdd\u8350\u7684\uff0c\u4e3a\u4ec0\u4e48\u4f60\u5bf9\u90a3\u4f4d\u53bf\u5b98\u4e0d\u52a0\u5904\u7f5a\u3002\u201d\u6b66\u5219\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4 \u9053\uff1a\u201c\u6162\u6162\u4f60\u5c31\u4f1a\u61c2\u5f97\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c\u77e5\u9053\u6b66\u5219\u5929\u5df2\u662f\u5bf9\u4ed6\u8d77\u7591\uff0c\u53c8\u89c9\u5f97\u6b66\u5219\u5929\u5904\u7406\u8fd9\u4ef6\u6848\u5b50\uff0c\u6709\u8bb8\u591a\u4e0d \u5408\u5e38\u7406\u7684\u5730\u65b9\uff0c\u672a\u53ca\u601d\u7d22\uff0c\u53ea\u542c\u5f97\u6b66\u5219\u5929\u8bf4\u9053\uff1a\u201c\u523a\u5ba2\u7684\u4e8b\u60c5\uff0c\u4ee5\u540e\u518d\u8c08\u3002\u4f60\u5148\u628a\u5f90\u656c \u4e1a\u90a3\u7bc7\u6a84\u6587\u8bfb\u7ed9\u6211\u542c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e00\u9635\u8e0c\u8e87\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u8fd9\u7bc7\u6a84\u6587\uff0c\u4e0d\u8bfb\u4e5f\u7f62\u3002\u201d\u6b66\u5219\u5929\u7b11\u9053\uff1a\u201c\u65e2\u7136\u662f \u8ba8\u4f10\u6211\u7684\u6a84\u6587\uff0c\u90a3\u5f53\u7136\u662f\u5c06\u6211\u9a82\u5f97\u5f88\u51f6\u7684\u4e86\u3002\u4f60\u6015\u6211\u542c\u4e86\u96be\u53d7\u5417\uff1f\u6211\u82e5\u662f\u6015\u4eba\u9a82\uff0c\u4e5f\u4e0d \u6562\u505a\u5f00\u5929\u8f9f\u5730\u4ee5\u6765\u7b2c\u4e00\u4e2a\u5973\u7687\u5e1d\u4e86\uff01\u5a49\u513f\uff0c\u4f60\u653e\u5fc3\u8bfb\u5427\uff0c\u8fd9\u6a84\u6587\u662f\u9a86\u5bbe\u738b\u505a\u7684\uff0c\u6587\u7b14\u4e00 \u5b9a\u4e0d\u574f\uff0c\u6211\u5012\u60f3\u6b23\u8d4f\u4e00\u4e0b\u5462\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u88ab\u6b66\u5219\u5929\u4e00\u50ac\u518d\u50ac\uff0c\u53ea\u5f97\u638f\u51fa\u90a3\u7bc7\u6a84\u6587\uff0c\u7f13\u7f13\u5ff5\u9053\uff1a\u201c\u4f2a\u4e34\u671d\u6b66\u6c0f\u8005\uff0c\u6027 \u975e\u6d32\u987a\uff0c\u5730\u5b9e\u5bd2\u5fae\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u6587\u7ae0\u8d77\u5f97\u597d\uff0c\u8bdd\u4e5f\u8bf4\u5f97\u5bf9\uff01\u6211\u51fa\u8eab\u672c\u6765\u5fae\u8d31\uff0c \u6211\u7236\u4eb2\u662f\u5356\u6728\u6750\u7684\u5546\u4eba\uff0c\u6211\u4f2f\u7236\u662f\u79cd\u8fc7\u5730\u7684\uff0c\u6211\u7684\u6027\u60c5\u4e5f\u7684\u786e\u4e0d\u662f\u548c\u987a\u7684\u3002\u201d\u4e0a\u5b98\u5a49\u513f \u7ee7\u7eed\u5ff5\u9053\uff1a\u201c\u6614\u5145\u592a\u5b97\u4e0b\u9648\uff0c\u66fe\u4ee5\u66f4\u8863\u4eba\u5f85\uff0c\u6cca\u4e4e\u665a\u8282\uff0c\u79fd\u4e71\u6625\u5bab\u3002\u9690\u5148\u5e1d\u4e4b\u79c1\uff0c\u9634\u56fe \u540e\u623f\u4e4b\u58c1\u3002\u5165\u95e8\u89c1\u5ac9\uff0c\u5a25\u5d4b\u4e0d\u80af\u8ba9\u4eba\uff1b\u63a9\u8896\u5de5\u8c17\uff0c\u72d0\u5a9a\u504f\u80fd\u60d1\u4e3b\u2026\u2026\u201d\u6b66\u5219\u5929\u51fb\u8282\u8d5e\u9053\uff1a \u201c\u8fd9\u4e24\u53e5\u5bf9\u5f97\u5de7\uff01\u6664\uff0c\u90a3\u662f\u8bf4\u6211\u8ff7\u60d1\u5148\u5e1d\uff0c\u8bf4\u6211\u6deb\u8d31\uff1b\u5343\u53e4\u4ee5\u6765\uff0c\u7537\u4eba\u603b\u662f\u8fd9\u6837\u9a82\u5973\u4eba \u7684\uff0c\u4e0d\u8fc7\uff0c\u8c03\u5b50\u867d\u7136\u6709\u70b9\u8001\u5957\uff0c\u6587\u7ae0\u8fd8\u662f\u505a\u5f97\u597d\u7684\uff0c\u518d\u5ff5\uff0c\u518d\u5ff5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8138\u4e0a\u5ffd\u8d77\u4e00\u7247\u7ea2\u4e91\uff0c\u4f4e\u58f0\u5ff5\u9053\uff1a\u201c\u8df5\u5143\u540e\u4e8e\u7fdf\uff0c\u9677\u543e\u541b\u4e8e\u805a\u6248\u3002\u201d\u539f\u6765\u8fd9 \u4e24\u53e5\u662f\u8bf4\u6b66\u5219\u5929\u5148\u540e\u5ac1\u7236\u5b50\u4e24\u4eba\uff0c\u96cc\u517d\u4e3a\u201c\u6248\u201d\uff0c\u201c\u805a\u6248\u201d\u4e43\u662f\u79bd\u517d\u4e71\u4ea4\uff0c\u610f\u601d\u662f\u8bf4\u7531 \u4e8e\u6b66\u5219\u5929\u800c\u9020\u6210\u4e86\u7236\u5b50\u4e24\u4ee3\u7687\u5e1d\u7684\u201c\u79bd\u517d\u884c\u4e3a\u201d\uff0c\u786e\u4e4e\u662f\u9a82\u5f97\u5f88\u6076\u6bd2\u7684\u4e86\u3002\u6b66\u5219\u5929\u5e76\u4e0d \u751f\u6c14\uff0c\u4f46\u5374\u4e5f\u9732\u51fa\u4e86\u4e00\u4e1d\u75db\u82e6\u7684\u795e\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u613f\u610f\u7684\u5417\uff1f\u5148\u5e1d\u5c06\u6211\u4ece\u5c3c\u59d1\u5eb5\u91cc \u63a5\u56de\u6765\uff0c\u8981\u5f3a\u8feb\u6211\u505a\u4ed6\u7684\u5983\u5b50\uff0c\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u6211\u4e4b\u4e0d\u613f\u610f\u6b7b\uff0c\u4e3a\u7684\u5c31\u662f\u4f7f\u5929\u4e0b\u5973\u4eba\uff0c \u4ee5\u540e\u4e0d\u8981\u518d\u53d7\u7537\u5b50\u8fd9\u6837\u7684\u6b3a\u8d1f\uff01\u6211\u53d7\u4e86\u7236\u5b50\u4e24\u4ee3\u7684\u4fae\u8fb1\uff0c\u9a86\u5bbe\u738b\u4e0d\u9a82\u4ed6\u7684\u7687\u5e1d\uff0c\u52ab\u5c06\u7f6a \u540d\u90fd\u63a8\u5230\u6211\u7684\u8eab\u4e0a\uff0c\u8fd9\u5b9e\u5728\u4e0d\u7b97\u5f97\u516c\u5e73\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4e0d\u5fc5\u8bfb\u4e0b\u53bb\u4e86\u5427\uff1f\u53cd\u6b63\u72d7\u5634\u91cc\u957f\u4e0d\u51fa\u8c61\u7259\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\uff01\u4f60 \u8fd9\u6837\u9a82\u9a86\u5bbe\u738b\u4e5f\u662f\u4e0d\u516c\u5e73\u7684\u3002\u58eb\u5927\u592b\u6709\u58eb\u5927\u592b\u7684\u770b\u6cd5\uff0c\u5728\u4ed6\u4eec\u770b\u6765\uff0c\u5973\u4eba\u5c31\u662f\u7978\u6c34\uff0c\u5973 \u4eba\u800c\u505a\u7687\u5e1d\u66f4\u662f\u5996\u5b7d\uff0c\u6240\u4ee5\u4ed6\u8ba4\u4e3a\u4ed6\u662f\u5bf9\u7684\u3002\u4ed6\u5199\u8fd9\u7bc7\u6a84\u6587\u7684\u65f6\u5019\u4e00\u5b9a\u5f88\u5f97\u610f\uff0c\u5e76\u4e0d\u89c9 \u5f97\u8fd9\u662f\u5bf9\u522b\u4eba\u4e00\u79cd\u4e0d\u516c\u5e73\u7684\u4fae\u8fb1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u518d\u542c\u542c\u8fd9\u51e0\u53e5\u3002\u8fd9\u4e0d\u662f\u65e0\u4e2d\u751f\u6709\u5417\uff1f\u201d\u7ee7\u7eed\u5ff5\u4e0b\u53bb\u9053\uff1a \u201c\u52a0\u4ee5\u5c34\u9521\u4e3a\u5fc3\uff0c\u8c7a\u72fc\u6210\u6027\u3002\u8fd1\u62bc\u90aa\u50fb\uff0c\u6b8b\u5bb3\u5fe0\u826f\u3002\u6740\u59d0\u5c60\u5144\uff0c\u5c31\u541b\u9e20\u6bcd\u3002\u4eba\u795e\u4e4b\u6240\u540c \u5ac9\uff0c\u5929\u5730\u4e4b\u6240\u4e0d\u5bb9\uff01\u201d\u6b66\u5219\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u7684\u59d0\u59d0\u662f\u81ea\u6740\u6b7b\u7684\uff0c\u6740\u59d0\u4e00\u4e8b\uff0c\u6216\u8005\u8fd8\u53ef \u4ee5\u6355\u98ce\u6349\u5f71\uff1b\u5c31\u541b\u3001\u9e20\u6bcd\u3001\u5c60\u5144\u7b49\u7b49\uff0c\u5374\u4ece\u4f55\u800c\u6765\uff1f\u6211\u5012\u60f3\u8d77\u4e00\u4e2a\u7b11\u8bdd\u4e86\uff0c\u6709\u4e00\u4e2a\u4e3e\u5b50 \u8003\u8bd5\u7684\u65f6\u5019\uff0c\u505a\u7684\u4e00\u9996\u8bd7\u4e2d\u6709\u4e24\u53e5\u662f\u820d\u5f1f\u6c5f\u5357\u6b7b\uff0c\u5bb6\u5144\u585e\u5317\u4ea1\u3002\u4e3b\u8003\u5b98\u5f55\u53d6\u4e86\u4ed6\uff0c\u53ec\u4ed6 \u8fdb\u89c1\u3002\u5bf9\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u8eab\u4e16\u600e\u4e48\u8fd9\u6837\u60e8\u554a\uff01\u201d\u90a3\u4e3e\u5b50\u9053\uff1a\u201c\u820d\u5f1f\u6c5f\u5357\u6b7b\u662f\u4e8b\u5b9e\uff1b\u81f3\u4e8e \u5bb6\u5144\uff0c\u5219\u73b0\u5728\u8fd8\u597d\u597d\u7684\u6d3b\u7740\uff0c\u6211\u662f\u4e3a\u4e86\u8981\u505a\u597d\u8fd9\u53e5\u5bf9\u4ed7\uff0c\u6ca1\u5948\u4f55\u53ea\u597d\u53eb\u5bb6\u5144\u6b7b\u4e00\u6b21\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7b11\u7684\u6d41\u51fa\u4e86\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u9a86\u5bbe\u738b\u53ea\u6c42\u6587\u7ae0\u5bf9\u5f97\u5de5\u6574\uff0c\u770b\u6765\u548c\u90a3\u4e3e\u5b50\u4e5f\u5dee \u4e0d\u591a\u3002\u201d\u7ee7\u7eed\u5ff5\u9053\uff1a\u201c\u72b9\u590d\u5305\u85cf\u7978\u5fc3\uff0c\u89c4\u7a8d\u795e\u5668\u3002\u541b\u4e4b\u7231\u5b50\uff0c\u5e7d\u4e4b\u4e8e\u522b\u5bab\uff1b\u8d3c\u4e4b\u5b97\u76df\uff0c \u59d4\u4e4b\u4ee5\u91cd\u4efb\u3002\u9e23\u547c\uff0c\u970d\u5b50\u5b5f\u4e4b\u4e0d\u4f5c\uff0c\u6731\u865a\u4faf\u4e4b\u5df2\u4ea1\u3002\u71d5\u5544\u7687\u5b59\uff0c\u77e5\u6c49\u67de\u4e4b\u5c06\u5c3d\u3002\u9f99\u86f0\u5e1d \u540e\uff0c\u8bc6\u590f\u5ead\u4e4b\u9012\u8870\u3002\u201d\u6b66\u5219\u5929\u542c\u5230\u8fd9\u91cc\uff0c\u53c8\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u51e0\u53e5\u662f\u7528\u5415\u540e\u3001\u8d75\u98de\u71d5\u548c\u8912\u5979 \u7684\u5178\u6545\uff0c\u628a\u6211\u548c\u8fd9\u51e0\u4e2a\u574f\u5973\u4eba\u76f8\u6bd4\uff0c\u603b\u4e4b\u662f\u5973\u5b50\uff0c\u56fd\u5bb6\uff0c\u4ed6\u4eec\u4e0d\u53bb\u63a8\u7a76\u5176\u4ed6\u539f\u56e0\uff0c\u800c\u662f \u628a\u4ea1\u56fd\u7684\u7f6a\u8fc7\uff0c\u653e\u5728\u5973\u4eba\u5934\u4e0a\uff01\u54c8\u54c8\uff0c\u8fd9\u771f\u662f\u592a\u7b80\u7ef0\u4e86\u3002\u518d\u5ff5\u4e0b\u53bb\u5427\uff0c\u4e0b\u9762\u5e94\u8be5\u662f\u66ff\u5f90 \u656c\u4e1a\u6765\u5938\u8000\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u7ee7\u7eed\u5ff5\u9053\uff1a\u201c\u656c\u4e1a\u7687\u5510\u65e7\u81e3\uff0c\u516c\u4faf\u5bb6\u5b50\u3002\u5949\u5148\u541b\u4e4b\u6210\u4e1a\uff0c \u8377\u672c\u671d\u4e4b\u539a\u6069\u3002\u5b8b\u5fbd\u5b50\u4e4b\u5174\u60b2\uff0c\u826f\u6709\u4ee5\u4e5f\u3002\u8881\u541b\u5c71\u4e4b\u6d41\u6d95\u3001\u5c82\u5f92\u7136\u54c9\uff1f\u662f\u7528\u6c14\u6124\u98ce\u4e91\uff0c \u5fd7\u5b89\u675c\u7a37\u3002\u56e0\u5929\u79be\u4e0b\u4e4b\u5931\u671b\uff0c\u987a\u5b87\u5185\u4e4b\u63a8\u5fc3\u3002\u7231\u4e3e\u4e49\u65d7\uff0c\u4ee5\u6e05\u5996\u5b7d\uff01\u201d\u6b66\u5219\u5929\u7b11\u9053\uff1a \u201c\u6587\u7ae0\u505a\u5f97\u597d\uff01\u53ea\u662f\u8c01\u5931\u671b\u5462\uff1f\u6211\u505a\u4e86\u7687\u5e1d\uff0c\u4ed6\u4eec\u8fd9\u73ed\u2018\u7687\u540e\u65e7\u81e3\uff0c\u516c\u4faf\u5bb6\u5b50\u2019\u7684\u786e\u662f \u5931\u671b\u7684\u3002\u5929\u4e0b\u7684\u8001\u767e\u59d3\u53ef\u6ca1\u6709\u5931\u671b\u554a\uff01\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u8d77\u548c\u81ea\u5df1\u7b56\u5212\u8d77\u5175\u7684\uff0c\u7684\u786e\u662f\u6b66\u5219\u5929\u6240\u8bf4\u7684\u8fd9\u73ed\u4eba\u3002\u800c\u8001\u767e\u59d3\u9a82 \u5979\u7684\uff0c\u5374\u662f\u5c11\u4e4b\u53c8\u5c11\uff0c\u53ea\u542c\u5f97\u4e0a\u5b98\u5a49\u513f\u5f80\u4e0b\u5ff5\u9053\uff1a\u201c\u5357\u8fde\u767e\u8d8a\uff0c\u5317\u5c3d\u4e09\u6cb3\u3002\u94c1\u9a91\u6210\u7fa4\uff0c \u6d77\u9675\u7ea2\u7c9f\uff0c\u4ed3\u50a8\u4e4b\u654c\u9761\u7a77\u3002\u6c5f\u6d66\u9ec4\u65d7\uff0c\u5321\u590d\u4e4b\u529f\u4f55\u8fdc\uff1f\u73ed\u58f0\u52a8\u800c\u98ce\u8d77\uff0c\u5251\u6c14\u51b2\u800c\u5357\u6597\u4e4e\u3002 \u6697\u9e23\u5219\u5c71\u7a3c\u5d29\u9893\uff0c\u53f1\u54a4\u5219\u98ce\u4e91\u53d8\u8272\uff01\u201d\u6b66\u5219\u5929\u9ad8\u58f0\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u8fd9\u51e0\u53e5\u63cf\u5199\u519b\u5a01\uff0c \u786e\u662f\u6709\u58f0\u6709\u8272\uff01\u4f46\u662f\uff0c\u5a49\u513f\uff0c\u4f60\u4e0d\u89c9\u5f97\u6587\u4eba\u591a\u5927\u8bdd\u5417\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u6b63\u662f\u5462\uff0c\u8fd9\u51e0\u5929\u7684\u4ed7\u6253\u5f97\u600e\u4e48\u6837\u4e86\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u674e\u5b5d\u9038\u8fde\u6218\u4ff1 \u6377\uff0c\u73b0\u5728\u5df2\u628a\u5f90\u656c\u4e1a\u7684\u4eba\u9a6c\u5305\u56f4\u8d77\u6765\u4e86\u3002\u770b\u6765\u4e0d\u51fa\u5341\u65e5\u4e4b\u5185\uff0c\u4fbf\u53ef\u4ee5\u5b8c\u5168\u5e73\u5b9a\u3002\u201d\u674e\u9038 \u5012\u5438\u4e86\u53e3\u51c9\u6c14\uff0c\u542c\u5f97\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u5f90\u656c\u4e1a\u4e5f\u662f\u4e00\u4f4d\u540d\u5c06\uff0c\u600e\u5982\u6b64\u4e0d\u6d4e\u4e8e\u4e8b\uff1f\u201d\u6b66\u5219\u5929 \u9053\uff1a\u201c\u5176\u5b9e\u4ed6\u7684\u8ba1\u5212\u5012\u662f\u633a\u5468\u5bc6\u7684\uff01\u88f4\u708e\u505a\u5185\u5e94\uff0c\u8fd8\u8054\u7edc\u4e86\u6211\u4eec\u5357\u6363\u7684\u5927\u5c06\u519b\u7a0b\u52a1\u633a\uff0c \u8981\u7a0b\u52a1\u633a\u5728\u9635\u524d\u5012\u6208\uff0c\u8fd9\u4e00\u7740\u5f88\u5389\u5bb3\uff0c\u53ef\u60dc\u90fd\u7ed9\u6211\u7834\u83b7\u4e86\u3002\u4f60\u8fd8\u8bb0\u5f97\u90a3\u4e2a\u884c\u523a\u8d24\u513f\u7684\u523a \u5ba2\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53eb\u505a\u7a0b\u52a1\u7532\u7684\u90a3\u4e2a\u4eba\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5f53\u65f6\u6211\u5bbd\u6055 \u4e86\u4ed6\uff0c\u4ed6\u5c31\u628a\u4e3b\u4f7f\u7684\u4eba\u4f9b\u51fa\u6765\u4e86\uff0c\u4ed6\u4fbf\u662f\u7a0b\u52a1\u633a\u7684\u5f1f\u5f1f\uff0c\u8fd9\u56de\u5f97\u4ee5\u7834\u83b7\u7a0b\u52a1\u633a\u8c0b\u53cd\u7684\u6848 \u4ef6\uff0c\u5168\u662f\u4ed6\u7684\u529f\u52b3\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5f90\u656c\u4e1a\u5931\u8d25\u6700\u5927\u7684\u539f\u56e0\uff0c\u8fd8\u662f\u8001\u767e\u59d3 \u4e0d\u5e2e\u4ed6\u3002\u8fd9\u4e24\u4ef6\u6848\u5b50\u7684\u7834\u83b7\uff0c\u53ea\u662f\u4f7f\u4ed6\u5931\u8d25\u5f97\u66f4\u5feb\u7f62\u4e86\u3002\u597d\uff0c\u5a49\u513f\uff0c\u4f60\u518d\u5ff5\u5427\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7ee7\u7eed\u5ff5\u9053\uff1a\u201c\u4ee5\u6b64\u5236\u654c\uff0c\u4f55\u654c\u4e0d\u6467\uff1f\u4ee5\u6b64\u56fe\u529f\uff0c\u4f55\u529f\u4e0d\u514b\uff1f\u516c\u7b49\u6216\u76fe\u6c49\u5730\uff0c \u6216\u53f6\u5468\u4eb2\uff0c\u6216\u81ba\u91cd\u5bc4\u4e8e\u6d3b\u8a00\uff0c\u6216\u53d7\u987e\u547d\u4e8e\u7687\u7a92\u3002\u8a00\u72b9\u5728\u8033\uff0c\u5fe0\u5c82\u5fd8\u5fc3\uff1f\u4e00\u676f\u4e4b\u58eb\u672a\u4e7e\uff0c \u516d\u5c3a\u4e4b\u5b64\u4f55\u6258\u2026\u2026\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6664\uff0c\u8fd9\u4e24\u53e5\u5bf9\u5f97\u5f88\u597d\uff0c\u201c\u4e00\u6298\u4e4b\u58eb\u672a\u4e7e\uff0c\u516d\u5c3a\u4e4b\u5b64\u4f55 \u6258\uff1f\u4e00\u6298\u4e4b\u571f\u6307\u7684\u662f\u9ad8\u5b97\u7687\u5e1d\u7684\u575f\u5893\uff0c\u516d\u5c3a\u4e4b\u5b64\u6307\u7684\u662f\u6211\u90a3\u51e0\u4e2a\u513f\u5b50\u3002\u9a86\u5bbe\u738b\u8981\u4eba\u4eec\u8bb0 \u8d77\u5148\u5e1d\u7684\u575f\u5893\uff0c\u5148\u5e1d\u7684\u513f\u5b50\uff0c\u6765\u5e2e\u4ed6\u6253\u5929\u4e0b\uff0c\u6765\u5e2e\u4ed6\u6062\u590d\u5148\u5e1d\u7684\u6c5f\u5c71\u3002\u8fd9\u4e24\u53e5\u8bdd\u542c\u6765\u5145 \u6ee1\u4e86\u611f\u60c5\uff0c\u53ef\u662f\u6211\u505a\u6bcd\u4eb2\u7684\u8fd8\u6ca1\u6709\u6b7b\uff0c\u600e\u4e48\u80fd\u8bf4\u6211\u7684\u513f\u5973\u662f\u516d\u5c3a\u4e4b\u5b64\u5462\uff1f\u96be\u9053\u4ed6\u4eec\u7684\u5fc3 \u76ee\u4e2d\uff0c\u53ea\u6709\u7236\u4eb2\uff0c\u6ca1\u6709\u6bcd\u4eb2\u7684\u5417\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e00\u6298\u4e4b\u58eb\u4e5f\u8bf4\u4e0d\u4e0a\uff0c\u90a3\u6837\u96c4\u58ee\u7684\u7687\u9675\uff0c \u5c82\u80fd\u8bf4\u662f\u4e00\u6298\u4e4b\u58eb\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u5927\u7ea6\u53c8\u662f\u56e0\u4e3a\u8981\u5bf9\u4ed7\u5de5\u6574\u7684\u539f\u6545\u5427\uff1f\u8fd9\u4e14\u4e0d\u7ba1\u5b83\uff0c \u518d\u5ff5\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7eed\u5ff5\u9053\uff1a\u201c\u5018\u80fd\u8f6c\u7978\u4e3a\u798f\uff0c\u9001\u5f80\u4e8b\u5c45\u3002\u5171\u7acb\u52e4\u738b\u52e4\uff0c\u65e0\u5e9f\u5927\u541b\u4e4b\u547d\u3002\u51e1\u8bf8 \u7235\u8d4f\uff0c\u540c\u6307\u5c71\u6cb3\u3002\u201d\u6b66\u5219\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u521a\u521a\u8d77\u4e8b\uff0c\u5c31\u5728\u8bb2\u88c2\u571f\u5206\u5c01\uff0c\u9ad8\u5b98\u539a\u7984\u4e86\u3002\u539f \u6765\u4ed6\u4eec\u5e76\u4e0d\u662f\u4e3a\u4e86\u767e\u59d3\uff0c\u800c\u662f\u4e3a\u4e86\u81ea\u5df1\u3002\u5374\u53c8\u4f55\u5fc5\u8fd9\u6837\u660e\u663e\u7684\u5199\u51fa\u6765\u5462\uff1f\u8fd9\u6837\u7684\u6a84\u6587\u4e0d \u6015\u5f15\u8d77\u8001\u767e\u59d3\u7684\u53cd\u611f\u5417\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u7eed\u5ff5\u9053\uff1a\u201c\u82e5\u5176\u7737\u604b\u7a77\u57ce\uff0c\u5f98\u5f8a\u6b67\u8def\uff0c\u5750\u6627\u5148\u51e0\u4e4b \u5146\uff0c\u5fc5\u8d3b\u540e\u81f3\u4e4b\u8bdb\u3002\u8bf7\u770b\u4eca\u65e5\u57df\u4e2d\uff0c\u7adf\u662f\u8c01\u5bb6\u4e4b\u5929\u4e0b\uff01\u55ef\uff0c\u8bfb\u5b8c\u4e86\u3002\u201d\u5c06\u6a84\u6587\u63e9\u8d77\uff0c\u9012 \u901e\u7ed9\u6b66\u5219\u5929\u3002 \u3000\u3000\u6b66\u5219\u5929\u63a5\u8fc7\u6a84\u6587\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u7bc7\u6a84\u6587\uff0c\u771f\u662f\u63b7\u5730\u6709\u91d1\u77f3\u4e4b\u58f0\uff0c\u7ed3\u53e5\u5c24\u5176\u7ed3\u5f97\u597d\u6781\uff0c \u5c31\u53ef\u60dc\u4eca\u65e5\u4e4b\u57df\u4e2d\uff0c\u4e0d\u4f1a\u662f\u4ed6\u4eec\u7684\u5929\u4e0b\u7f62\u4e86\u3002\u5a49\u513f\u5440\uff0c\u4f60\u731c\u6211\u542c\u4e86\u8fd9\u7bc7\u8ba8\u4f10\u6211\u7684\u6a84\u6587\uff0c \u7b2c\u4e00\u4e2a\u5ff5\u5934\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff0c\u201c\u5929\u540e\u6240\u60f3\u7684\u4e8b\u60c5\uff0c\u5f80\u5f80\u662f\u51fa\u4e4e\u6211\u4eec\u7684\u610f\u6599\u4e4b\u5916\u7684\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a \u201c\u6211\u542c\u4e86\u8fd9\u7bc7\u6a84\u6587\uff0c\u9996\u5148\u60f3\u5230\u7684\u662f\uff0c\u505a\u5bb0\u76f8\u7684\u5e94\u8be5\u53d7\u5230\u8d23\u5907\u3002\u6709\u8fd9\u6837\u505a\u6587\u7ae0\u7684\u4eba\uff0c\u4e3a\u4ec0 \u4e48\u53cd\u800c\u8ba9\u4ed6\u88ab\u5f90\u656c\u4e1a\u6240\u7528\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u4e0d\u7531\u4e0a\u5b98\u5a49\u513f\u610f\u60f3\u4e0d\u5230\uff0c\u674e\u9038\u66f4\u662f\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u9a86\u5bbe\u738b\u628a\u5979\u9a82\u5f97 \u72d7\u8840\u6dcb\u5934\uff0c\u5979\u4e0d\u4f46\u4e0d\u52a8\u6012\uff0c\u53cd\u800c\u8d23\u602a\u5bb0\u76f8\u4e0d\u5584\u4e8e\u7528\u4eba\uff0c\u8fd9\u5ea6\u91cf\u771f\u975e\u5e38\u4eba\u6240\u53ca\u3002\u6211\u4eec\u4e0e\u5979 \u4e89\u593a\u5929\u4e0b\uff0c\u8fd9\u76d8\u68cb\u53ea\u6015\u662f\u8f93\u5b9a\u7684\u4e86\uff01\u201d\u53ea\u542c\u5f97\u6b66\u5219\u5929\u7b11\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u6587\u7ae0\u867d\u7136\u5199\u5f97 \u5f88\u597d\uff0c\u5bf9\u4ed7\u5de5\u6574\uff0c\u8c03\u5b50\u94ff\u9535\uff0c\u53ef\u662f\u5374\u6beb\u65e0\u529b\u91cf\uff01\u4f60\u4eec\u770b\u4e86\u4ed6\u8fd9\u7bc7\u6587\u7ae0\u53ef\u6709\u4e00\u53e5\u8bdd\u63d0\u5230\u8001 \u767e\u59d3\u4e48\uff1f\u6ca1\u6709\uff01\u4ed6\u7ffb\u6765\u8986\u53bb\uff0c\u53ea\u662f\u653b\u51fb\u6211\u4e2a\u4eba\u7684\u79c1\u5fb7\uff0c\u7528\u5c3d\u4e00\u5207\u6076\u6bd2\u7684\u8a00\u8f9e\u6765\u8bec\u8511\u6211\uff1b \u518d\u5176\u6b21\u5c31\u662f\u8981\u516c\u4faf\u8d35\u65cf\u8ddf\u4ed6\u8d77\u4e8b\uff0c\u5c06\u6765\u53ef\u4ee5\u5f97\u9ad8\u5b98\u539a\u7984\u3002\u4ed6\u4eec\u65e2\u53f7\u79f0\u4e49\u5e08\uff0c\u7406\u8be5\u7528\u6c11\u4f10 \u7f6a\uff0c\u4f46\u4ed6\u4eec\u5374\u4e0d\u66ff\u8001\u767e\u59d3\u8bf4\u4e00\u53e5\u8bdd\uff01\u4ed6\u4eec\u4e0d\u7406\u4f1a\u8001\u767e\u59d3\uff0c\u8001\u767e\u59d3\u53c8\u600e\u4f1a\u5173\u5fc3\u4ed6\u7684\u4e8b\u4e1a\uff1f \u6240\u4ee5\u8fd9\u662f\u4e00\u7bc7\u597d\u6587\u7ae0\uff0c\u5374\u4e0d\u662f\u4e00\u7bc7\u6709\u529b\u91cf\u7684\u6a84\u6587\uff01\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u60f3\u8d77 \u88f4\u884c\u68c0\u4ee5\u524d\u66fe\u54c1\u8bc4\u8fc7\u4ed6\u4eec\uff0c\u8bf4\u2018\u4e0a\u5148\u5668\u8bc6\u800c\u540e\u6587\u827a\u2019\u3002\u8bf4\u4ed6\u4eec\u4e13\u641e\u6587\u827a\uff0c\u89c1\u8bc6\u4e0d\u9ad8\uff0c\u8fd9 \u8bdd\u8bf4\u5f97\u9887\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u5929\u540e\u8981\u4e0d\u8981\u6211\u62df\u4e00\u901a\u8bcf\u4e66\uff0c\u53cd\u9a73\u4ed6\u4eec\uff0c\u5c31\u7528\u4f60\u521a\u624d\u6240\u8bf4\u7684\u90a3\u4e9b\u6765\u8bf4\u3002\u201d \u6b66\u5219\u5929\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u8d44\u6b64\u7b14\u58a8\uff1f\u201d\u4e0a\u5b98\u5a49\u513f\u6709\u70b9\u8ff7\u6084\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u5929\u540e\uff0c\u4f9d\u4f60\u770b\uff0c\u8fd9 \u4e00\u7bc7\u6587\u7ae0\u4f1a\u4e0d\u4f1a\u6d41\u4f20\u540e\u4e16\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u8fd9\u6837\u597d\u7684\u6587\u7ae0\uff0c\u5f53\u7136\u4f1a\u6d41\u4f20\u4e0b\u53bb\u7684\u3002\u8001\u767e\u59d3 \u770b\u4e0d\u61c2\uff0c\u8bfb\u4e66\u4eba\u5374\u4e00\u5b9a\u6b23\u8d4f\u5b83\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u6211\u5c31\u662f\u987e\u8651\u5230\u8fd9\u70b9\uff01\u201d\u6b66\u5219\u5929\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u4e86\u3002\u4f60\u662f\u6015\u9a86\u5bbe\u738b\u8fd9\u7bc7\u6587\u7ae0\u6d41\u4f20\u4e0b\u53bb\uff0c\u5343\u79cb\u4e07\u4e16\u4e4b\u540e\uff0c\u6211\u90fd\u6c38\u8fdc \u8981\u8499\u4e0a\u81ed\u540d\uff01\u540e\u4e16\u7684\u4eba\uff0c\u5c06\u628a\u6211\u770b\u4f5c\u5386\u53f2\u4e0a\u6700\u574f\u6700\u574f\u7684\u5973\u4eba\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u60f3\u4e0d\u5230\u6b66\u5219\u5929\u8bf4\u5f97\u5982\u6b64\u5766\u7387\uff0c\u4e00\u65f6\u95f4\u4e0d\u6562\u4f5c\u58f0\u3002\u6b66\u5219\u5929\u4e00\u7b11\u4e4b\u540e\uff0c\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u6211\u65e2\u7136\u505a\u4e86\u5386\u53f2\u6240\u65e0\u7684\u5973\u7687\u5e1d\uff0c\u82e5\u7136\u7537\u5c0a\u5973\u5351\u7684\u5386\u53f2\u4e0d\u6539\u53d8\uff0c\u6211\u5f53\u7136\u662f\u8981\u6328\u9a82\u7684\uff0c \u8fd9\u65e9\u5728\u610f\u6599\u4e4b\u4e2d\u3002\u4f46\u4f60\u4e5f\u4e0d\u5fc5\u592a\u8fc7\u8651\uff0c\u6211\u6562\u76f8\u4fe1\uff0c\u5c06\u6765\u603b\u4f1a\u6709\u516c\u6b63\u7684\u53f2\u5bb6\uff0c\u4f1a\u51fa\u6765\u66ff\u6211 \u8bf4\u8bdd\u3002\u90a3\u6015\u662f\u5343\u5e74\u4e4b\u540e\uff0c\u4e07\u5e74\u4e4b\u540e\uff0c\u603b\u4f1a\u6709\u8fd9\u6837\u7684\u53f2\u5bb6\u7684\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9ed8\u7136\u4e0d\u8bed\uff0c\u4f46\u4ece \u5979\u7684\u8138\u8272\u770b\u6765\uff0c\u5374\u8fd8\u6709\u4e0d\u4ee5\u4e3a\u7136\u7684\u795e\u6c14\uff0c\u6b66\u5219\u5929\u9053\uff1a\u201c\u5a49\u513f\uff0c\u6211\u5012\u60f3\u4f60\u66ff\u6211\u62df\u4e00\u9053\u8bcf\u4e66\uff0c \u7528\u516b\u767e\u91cc\u5feb\u9a6c\u52a0\u7d27\uff0c\u98de\u9012\u7ed9\u674e\u5b5d\u9038\u53eb\u4ed6\u5343\u4e07\u4e0d\u53ef\u6740\u4e86\u9a86\u5bbe\u738b\uff01\u201d \u3000\u3000\u674e\u9038\u542c\u5230\u8fd9\u91cc\uff0c\u4f46\u89c9\u773c\u524d\u4e00\u7247\u660f\u6697\uff0c\u5fc3\u4e2d\u5b8c\u5168\u7edd\u671b\uff0c\u662f\u8fd9\u6837\u4e00\u4e2a\u6bd4\u7537\u5b50\u8fd8\u8981\u521a\u5f3a\u7684 \u5973\u4eba\uff01\u4ed6\u611f\u5230\u8fde\u81ea\u5df1\u4e5f\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u4e86\u3002\u674e\u9038\u832b\u7136\u5750\u5728\u74e6\u4e0a\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u5ffd\u89c1\u8fdc\u5904\u4f3c\u6709 \u536b\u58eb\u7684\u5f71\u5b50\u5728\u79fb\u52a8\u3002 \u3000\u3000\u674e\u9038\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u4eca\u665a\u6211\u65e2\u4e0d\u80fd\u4e0b\u624b\uff0c\u8fd8\u7559\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u5728\u5c4b\u9876\u4e0a\u671b\u4e0b \u53bb\uff0c\u4f46\u89c1\u5fa1\u6cb3\u5982\u5e26\uff0c\u4e0a\u6797\u82b1\u6728\uff0c\u5b9b\u4f3c\u9526\u7ee3\u7684\u5c4f\u98ce\uff0c\u697c\u53f0\u6bbf\u9601\uff0c\u5728\u82b1\u6728\u63a9\u6620\u4e4b\u4e0b\uff0c\u9519\u843d\u53c2 \u5dee\uff0c\u597d\u50cf\u4e00\u5e45\u753b\u56fe\uff0c\u7f8e\u5f97\u96be\u4ee5\u5f62\u5bb9\u3002\u674e\u9038\u60f3\u8d77\u513f\u65f6\u5728\u5fa1\u82b1\u56ed\u4e2d\u7684\u6e38\u620f\uff0c\u592a\u6db2\u6c60\u8fb9\uff0c\u51cc\u6ce2 \u9601\u5185\uff0c\u90fd\u66fe\u5370\u6709\u4ed6\u7684\u8db3\u8ff9\uff0c\u60f3\u8d77\u4eca\u665a\u884c\u523a\u4e0d\u6210\uff0c\u4ee5\u540e\u662f\u518d\u4e5f\u6ca1\u6709\u673a\u4f1a\u8fdb\u5bab\u7684\u4e86\uff0c\u4e5f\u8bb8\u4ece \u6b64\u4fbf\u8981\u6d41\u6d6a\u6c5f\u6e56\uff0c\u90c1\u90c1\u7ec8\u8001\uff0c\u60f3\u81f3\u6b64\u5904\uff0c\u6005\u6005\u60af\u60af\uff0c\u773c\u7736\u6e05\u6cea\u6b32\u6d41\uff0c\u51e0\u6b21\u60f3\u8981\u6084\u7136\u79bb\u53bb\uff0c \u53c8\u7981\u4e0d\u4f4f\u591a\u770b\u4e00\u773c\u3002 \u3000\u3000\u7136\u800c\u6700\u4ee4\u4ed6\u7559\u604b\uff0c\u6700\u4ee4\u4ed6\u4f24\u5fc3\u7684\uff0c\u8fd8\u4e0d\u662f\u5fa1\u82b1\u56ed\u7684\u666f\u8272\uff0c\u800c\u662f\u5c4b\u5b50\u91cc\u7684\u4e0a\u5b98\u5a49\u513f\u3002 \u201c\u4faf\u95e8\u4e00\u4eba\u6df1\u5982\u6d77\uff0c\u4ece\u6b64\u8427\u90ce\u662f\u8def\u4eba\u3002\u201d\u4f55\u51b5\u4e0a\u5b98\u5a49\u513f\u5165\u7684\u4e0d\u662f\u201c\u4faf\u95e8\u201d\uff0c\u800c\u662f\u6bd4\u201c\u4faf \u95e8\u201d\u8fd8\u8981\u68ee\u4e25\u4e07\u500d\u7684\u5bab\u95e8\uff01\u5a49\u513f\u867d\u7136\u6ca1\u6709\u5ac1\u4eba\uff0c\u4f46\u4ece\u6b64\u80cc\u9053\u800c\u9a70\uff0c\u4ea6\u5df2\u662f\u8427\u90ce\u964c\u8def\uff01\u4ed6 \u4eca\u665a\u89c1\u7740\u4e86\u5a49\u513f\uff0c\u5374\u4e0d\u80fd\u548c\u5979\u8bf4\u4e00\u53e5\u8bdd\u3002\u4ed6\u771f\u820d\u4e0d\u5f97\u79bb\u5f00\uff0c\u4f46\u5728\u8fd9\u6837\u7684\u60c5\u52bf\u4e0b\uff0c\u5374\u53c8\u4e0d \u80fd\u4e0d\u79bb\u5f00\u4e86\u3002\u201c\u5979\u77e5\u9053\u6211\u4eca\u665a\u66fe\u7ecf\u6765\u8fc7\u5417\uff1f\u201d\u201c\u5979\u4f1a\u5728\u68a6\u4e2d\u68a6\u89c1\u6211\u5417\uff1f\u201d \u3000\u3000\u8fd8\u6709\u6b66\u7384\u971c\uff0c\u5bf9\u81ea\u5df1\u6709\u8fc7\u5927\u6069\uff0c\u53c8\u662f\u81ea\u5df1\u654c\u4eba\u7684\u6b66\u7384\u971c\uff0c\u5c31\u662f\u4e3a\u4e86\u5979\u5728\u5bab\u4e2d\uff0c\u4ee5\u81f3 \u4ee4\u4ed6\u4eca\u665a\u4e0d\u80fd\u4e0b\u624b\u7684\u6b66\u7384\u971c\uff01\u4ed6\u4e0d\u77e5\u662f\u8be5\u611f\u6fc0\u5979\uff0c\u8fd8\u662f\u8be5\u6028\u6068\u5979\uff1f\u4ece\u4eca\u4e4b\u540e\uff0c\u53ea\u6015\u4e5f\u662f \u6c38\u8fdc\u4e0d\u80fd\u518d\u89c1\u7740\uff01\u201c\u5979\u4f1a\u60f3\u5ff5\u6211\u5417\uff1f\u201d\u674e\u9038\u5728\u5fc3\u4e2d\u81ea\u8a00\u81ea\u8bed\u3002\u201c\u8fd9\uff0c\u6211\u4e0d\u77e5\u9053\u3002\u4f46\u6211\u77e5 \u9053\uff0c\u6211\u662f\u4f1a\u60f3\u5ff5\u5979\u7684\uff0c\u867d\u7136\u5979\u662f\u6211\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e0a\u5b98\u5a49\u513f\u8bf4\u9053\uff1a\u201c\u90a3\u5c01\u8bcf\u4e66\u5df2\u7ecf\u62df\u597d\u4e86\u3002\u5929\u540e\uff0c\u4f60\u8981\u8fc7\u76ee\u5417\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a \u201c\u4e0d\u5fc5\u4e86\u3002\u5a49\u513f\uff0c\u4f60\u8fd1\u6765\u6709\u4f5c\u8bd7\u5417\uff1f\u6211\u60f3\u8d77\u4f60\u90a3\u665a\u6765\u884c\u523a\u6211\uff0c\u8fd8\u8bb0\u5f97\u4f60\u90a3\u665a\u4f5c\u7684\u8bd7\u5462\u3002 \u501f\u95ee\u6843\u5c06\u674e\uff0c\u76f8\u4e71\u6b32\u4f55\u5982\u3002\u90a3\u65f6\u4f60\u597d\u50cf\u5f88\u6028\u6068\u6211\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u7b11\u9053\uff1a\u201c\u90a3\u65f6\u6211\u5b9e\u5728\u65e0\u77e5\u3002\u201d \u6b66\u5219\u5929\u7b11\u9053\uff1a\u201c\u6211\u7528\u624d\u5012\u4f5c\u4e86\u4e00\u9996\u8bd7\uff0c\u662f\u7b54\u8986\u4f60\u90a3\u9996\u526a\u5f69\u82b1\u7684\u3002\u526a\u5f69\u82b1\u56fa\u7136\u662f\u4eba\u9020\u7684\uff0c \u5176\u5b9e\u4e16\u95f4\u4e00\u5207\u6587\u7269\uff0c\u53c8\u6709\u5201\u6708\u4e66\u6237\u4e0d\u662f\u4eba\u9020\u7684\uff1f\u6211\u8fd9\u9996\u8bd7\u662f\u548f\u871c\u6750\u7684\uff0c\u8bfb\u7ed9\u4f60\u542c\uff0c\u8bf7\u4f60 \u7ed9\u6211\u6da6\u9970\u4e00\u4e0b\u3002\u201d\u7f13\u7f13\u5ff5\u9053\uff1a \u3000\u3000\u871c\u6843\u4eba\u6240\u79cd\uff0c\u4eba\u5b9a\u80dc\u5929\u5de5\u3002 \u3000\u3000\u6708\u7167\u4e5d\u9704\u78a7\uff0c\u65f6\u6765\u56db\u6d77\u7ea2\u3002 \u3000\u3000\u6625\u534e\u660e\u65e6\u65e6\uff0c\u79cb\u5b9e\u4e50\u5f64\u5f64\u3002 \u3000\u3000\u4e07\u53e4\u751f\u673a\u5728\uff0c\u91d1\u8f6e\u8fd0\u4e0d\u7a77\u3002 \u3000\u3000\u6b66\u5219\u5929\u81ea\u53f7\u201c\u91d1\u8f6e\u7687\u5e1d\u201d\uff0c\u8fd9\u9996\u8bd7\u5f3a\u8c03\u4eba\u5b9a\u80dc\u5929\uff0c\u5b8c\u5168\u662f\u5973\u7687\u5e1d\u7684\u53e3\u543b\u3002\u674e\u9038\u5fc3\u9053\uff1a \u201c\u597d\u5927\u7684\u53e3\u6c14\uff01\u201d\u4e0a\u5b98\u5a49\u513f\u51fb\u8282\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u610f\u5883\u3001\u6c14\u9b44\u3001\u97f3\u8c03\u90fd\u597d\uff0c\u8fd9\u9996\u8bd7\u6211\u4e5f \u4f5c\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u4eca\u665a\u5174\u81f4\u600e\u4e48\u8fd9\u6837\u597d\uff1f\u4f60\u5fd8\u8bb0\u4e86\u4eca\u665a\u8fd8\u8981\u5ba1\u95ee\u523a\u5ba2\u4e48\uff1f\u201d \u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u662f\u554a\uff0c\u600e\u4e48\u8fd8\u4e0d\u89c1\u5927\u4e8c\u5185\u603b\u7ba1\u6765\u5462\uff1f\u201d\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u518d\u4e0d\u8d70 \u6050\u6015\u8981\u7ed9\u4ed6\u4eec\u53d1\u73b0\u4e86\u3002\u201d\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u5730\u6709\u4e00\u6761\u9ed1\u5f71\u75be\u98de\u800c\u6765\uff0c\u4e00\u8e0f\u4e0a\u5c4b\u9876\uff0c\u626c\u624b\u4fbf\u662f \u4e24\u67c4\u98de\u5200\uff0c\u5411\u5c4b\u5185\u5c04\u4eba\uff01\u201d \u3000\u3000\u8fd9\u4eba\u7684\u8eab\u6cd5\u5feb\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u76f4\u5230\u4ed6\u98de\u5200\u51fa\u624b\u4e4b\u540e\uff0c\u674e\u9038\u624d\u8ba4\u51fa\u662f\u8c01\u3002\u521d\u65f6\u4ed6\u4ee5\u4e3a\u5b9a \u7136\u662f\u767d\u5143\u5316\uff0c\u4ee5\u4e3a\u4ed6\u66ff\u81ea\u5df1\u628a\u98ce\uff0c\u7b49\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u6545\u6b64\u4eb2\u6765\u52a8\u624b\u3002\u54ea\u77e5\u770b\u6e05\u695a\u4e86\uff0c\u5927\u5927 \u51fa\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u8fd9\u523a\u5ba2\u5e76\u4e0d\u662f\u767d\u5143\u5316\uff0c\u5374\u662f\u4e0e\u4ed6\u540c\u4f4f\u7684\u90a3\u4e2a\u866c\u9aef\u6b66\u58eb\u5357\u5bab\u5c1a\uff01 \u3000\u3000\u4f46\u542c\u5f97\u5c4b\u5b50\u91cc\u4e24\u58f0\u5a07\u7b11\uff0c\u4e0a\u5b98\u5a49\u513f\u4e00\u4f38\u624b\u5c31\u63a5\u4e86\u4e00\u67c4\u98de\u5200\uff0c\u5a49\u513f\u81ea\u5e7c\u5728\u5251\u9601\u4e4b\u4e0a\u7ec3 \u98de\u5200\u523a\u9e1f\u7684\u7edd\u6280\uff0c\u63a5\u98de\u5200\u7684\u624b\u6cd5\u81ea\u662f\u51fa\u8272\u5f53\u884c\uff0c\u5979\u672c\u6765\u60f3\u540c\u65f6\u63a5\u4e24\u67c4\u98de\u5200\u7684\uff0c\u4e0d\u8fc7\u6b66\u7384 \u971c\u51fa\u624b\u6bd4\u5979\u66f4\u5feb\uff0c\u53e6\u4e00\u67c4\u98de\u5200\u88ab\u5979\u626c\u8896\u4e00\u4f5b\uff0c\u98de\u5200\u53cd\u5c04\u800c\u51fa\uff0c\u5693\u7684\u4e00\u58f0\uff0c\u63d2\u5728\u6881\u4e0a\u3002 \u3000\u3000\u6b66\u7384\u971c\u5ffd\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u8fd9\u4e0d\u662f\u4ed6\uff01\u201d\u90a3\u866c\u9aef\u6b66\u58eb\u8eab\u624b\u77eb\u6377\u4e4b\u6781\uff0c \u4e00\u51fb\u4e0d\u4e2d\uff0c\u4fbf\u77e5\u5c4b\u4e2d\u4f0f\u6709\u9ad8\u624b\uff0c\u4e00\u6309\u5c4b\u6a90\uff0c\u7acb\u5373\u7ffb\u8eab\u8df3\u4e0b\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u4f46\u89c1\u767d\u5149\u4e00\u95ea\uff0c \u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u53e6\u4e00\u4e2a\u6b66\u58eb\u5df2\u548c\u523a\u5ba2\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u4e8b\u60c5\u5b8c\u5168\u51fa\u4e4e\u674e\u9038\u610f\u6599\u4e4b\u5916\uff0c\u8fd9\u4e00\u4e2a\u62e6\u622a\u523a\u5ba2\u7684\u6b66\u58eb\u624d\u662f\u767d\u5143\u5316\uff0c\u4ed6\u4e0d\u77e5\u4ece\u4ec0\u4e48\u5730 \u65b9\u53d6\u5f97\u4e86\u4e00\u67c4\u957f\u5251\uff0c\u970e\u9650\u4e4b\u95f4\u5df2\u548c\u5357\u5bab\u5c1a\u62c6\u4e86\u56db\u4e94\u62db\uff0c\u540c\u65f6\u5927\u58f0\u56b7\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u4f0f\u5728 \u5c4b\u4e0a\uff0c\u4ed6\u53eb\u674e\u9038\uff0c\u662f\u674e\u5510\u7687\u5ba4\u7684\u5b50\u5b59\uff01\u201d \u3000\u3000\u674e\u9038\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u662f\u81ea\u5df1\u4e2d\u4e86\u4ed6\u4eec\u7684\u5708\u5957\uff01\u8fd9\u767d\u5143\u5316\u524d\u65e5\u5728\u6821\u573a\u4e0a\u884c\u523a\u674e\u660e\u4e4b\uff0c \u4e0d\u8fc7\u662f\u4e00\u573a\u628a\u620f\uff0c\u8bf1\u4f7f\u674e\u9038\u9732\u51fa\u6b66\u529f\uff0c\u4e5f\u8bf1\u4f7f\u674e\u9038\u5bf9\u4ed6\u9732\u51fa\u771f\u76f8\u3002 \u3000\u3000\u674e\u9038\u6025\u5fd9\u98de\u8eab\u4e0b\u5730\uff0c\u4f46\u89c1\u5357\u5b98\u5c1a\u6325\u821e\u4e00\u67c4\u5355\u5200\uff0c\u5c06\u767d\u5143\u5316\u8feb\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u5927\u58f0\u53eb\u9053\uff1a \u201c\u6211\u7f20\u7740\u4ed6\uff0c\u4f60\u5feb\u9003\uff0c\u5feb\u9003\uff01\u201d\u674e\u9038\u811a\u5c16\u4e00\u70b9\uff0c\u5982\u7bad\u75be\u53d1\uff0c\u201c\u55d6\u201d\u7684\u7a7f\u8fc7\u767d\u5143\u5316\u8eab\u65c1\uff0c \u5b9d\u5251\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\u5411\u767d\u5143\u5316\u75be\u4e0b\u6740\u624b\uff0c\u767d\u5143\u5316\u56de\u5251\u4e00\u6321\uff0c\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u5251\u950b\u5df2\u88ab \u524a\u65ad\uff0c\u4f46\u4ed6\u6b66\u529f\u4e5f\u771f\u662f\u9ad8\u5f3a\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u674e\u9038\u7684\u7b2c\u4e8c\u5251\u5288\u4e86\u4e2a\u7a7a\uff0c\u4ed6\u4ed7\u7740\u534a\u622a\u65ad\u5251\u5f53\u4f5c \u77ed\u5200\u4f7f\u7528\uff0c\u53cd\u624b\u4e00\u6321\uff0c\u5c45\u7136\u53c8\u683c\u5f00\u4e86\u5357\u5b98\u5c1a\u7684\u5355\u5200\u3002 \u3000\u3000\u674e\u9038\u54ea\u91cc\u8fd8\u6709\u5fc3\u604b\u6218\uff0c\u626f\u5357\u5bab\u5c1a\u8863\u8896\uff0c\u53eb\u9053\uff1a\u201c\u8981\u8d70\u54b1\u4eec\u4e00\u8d77\u9003\u8d70\uff01\u201d\u767d\u5143\u5316\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u522b\u505a\u68a6\u4e86\uff0c\u8fd9\u91cc\u65e9\u5df2\u5e03\u4e0b\u4e86\u5929\u7f57\u5730\u7f51\uff0c\u8fd8\u662f\u4e56\u4e56\u7684\u7559\u4e0b\u6765\u5427\uff01\u201d \u3000\u3000\u674e\u9038\u5530\u5530\u4e24\u5251\uff0c\u5c06\u767d\u5143\u5316\u518d\u5ea6\u8feb\u5f00\uff0c\u559d\u9053\uff1a\u201c\u6321\u6211\u8005\u6b7b\uff0c\u8ba9\u6211\u8005\u751f\uff01\u201d\u521a\u8dd1\u5f97\u51e0\u6b65\uff0c \u7a81\u7136\u542c\u5f97\u4e00\u4e2a\u4eba\u5927\u7b11\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff0c\u6211\u504f\u504f\u8981\u6321\u4f60\u4e00\u6321\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u4e00\u80a1\u75be\u98ce\u5148 \u522e\u8fc7\u6765\uff0c\u674e\u9038\u98d8\u8eab\u4e00\u95ea\uff0c\u5b9a\u773c\u770b\u65f6\uff0c\u5374\u539f\u6765\u662f\u795e\u6b66\u8425\u7684\u4e09\u5927\u9ad8\u624b\u4e4b\u4e00\uff0c\u4e5f\u5c31\u662f\u90a3\u7528\u5927\u65d7 \u5377\u8d77\u8c46\u7c89\u7684\u90a3\u4e2a\u79e6\u582a\u3002 \u3000\u3000\u4ed6\u7684\u5175\u5668\u5947\u602a\u4e4b\u6781\uff0c\u4e43\u662f\u4e00\u652f\u4e09\u5c3a\u6765\u957f\u7684\u65d7\u5b50\uff0c\u65d7\u6746\u662f\u7528\u9ec4\u94a2\u505a\u7684\uff0c\u53ef\u4ee5\u5f53\u4f5c\u5224\u5b98 \u7b14\u7528\uff0c\u53c8\u53ef\u4ee5\u5f53\u4f5c\u5c0f\u82b1\u67aa\u4f7f\uff0c\u65d7\u5b50\u5219\u662f\u6781\u7ec6\u7684\u767d\u91d1\u4e1d\u7ec7\u6210\uff0c\u547c\u547c\u7ffb\u5377\uff0c\u4e1d\u6beb\u4e0d\u6015\u5b9d\u5251\u3002 \u3000\u3000\u674e\u9022\u8eab\u5f62\u4e00\u6643\uff0c\u554a\u7684\u4e00\u5251\uff0c\u76f4\u6307\u654c\u4eba\u54bd\u5589\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d\u5c82\u77e5\u79e6\u582a\u7684\u6b66\u529f \u786e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u4f46\u542c\u4ed6\u51b7\u51b7\u4e00\u7b11\uff0c\u4e5f\u559d\u4e86\u4e00\u58f0\u201c\u7559\u4e0b\uff01\u201d\u674e\u9038\u7684\u5b9d\u5251\u75be\u53d1\u5982\u98ce\uff0c\u770b\u770b\u5c31 \u8981\u7a7f\u5589\u800c\u8fc7\uff0c\u5ffd\u89c9\u5251\u5c16\u4e00\u79fb\uff0c\u6ed1\u8fc7\u4e00\u8fb9\uff0c\u4f46\u89c1\u79e6\u582a\u624b\u821e\u7075\u65d7\uff0c\u4e00\u6325\u4e00\u5377\uff0c\u7adf\u7136\u4f7f\u51fa\u4ee5\u67d4 \u514b\u521a\uff0c\u5378\u529b\u53cd\u51fb\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5c06\u674e\u9038\u7684\u5b9d\u5251\u4e00\u62c2\u62c2\u5f00\uff0c\u7075\u65d7\u4e00\u5c55\uff0c\u53cd\u5377\u800c\u4e0b\u3002\u674e\u9038\u5fae\u5403 \u4e00\u60ca\uff0c\u970d\u5730\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u501f\u52bf\u62e7\u8eab\uff0c\u4ee5\u7edd\u9879\u7684\u8f7b\u529f\u914d\u4e0a\u7cbe\u7eaf\u7684\u5251\u6cd5\uff0c\u5f39\u6307\u4e4b\u95f4\uff0c\u8fde\u53d1 \u4e09\u5251\uff0c\u79e6\u582a\u51dd\u8eab\u4e0d\u52a8\uff0c\u7075\u65d7\u5de6\u53f3\u6325\u52a8\uff0c\u8fde\u63a5\u4e09\u62db\u3002\u674e\u9038\u7684\u5251\u5c16\u4e00\u6cbe\u5230\u4ed6\u7684\u65d7\u5b50\u4fbf\u7acb\u523b\u6ed1 \u5f00\uff0c\u65e0\u6cd5\u4f7f\u52b2\u3002\u4f46\u674e\u9038\u7684\u5251\u6cd5\u4e25\u5bc6\u975e\u5e38\uff0c\u79e6\u582a\u60f3\u628a\u4ed6\u7684\u5b9d\u5251\u5377\u51fa\u624b\u53bb\uff0c\u5374\u4e5f\u4e0d\u80fd\u3002\u4e24\u4eba \u5404\u4ee5\u4e0a\u4e58\u6b66\u529f\u76f8\u62fc\uff0c\u5f7c\u6b64\u90fd\u4e0d\u80af\u9000\u8ba9\u534a\u6b65\uff0c\u8f6c\u77ac\u4e4b\u95f4\u5c31\u62c6\u4e86\u4e8c\u5341\u6765\u62db\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u4f46\u542c\u5f97\u5357\u5bab\u5c1a\u4e5f\u5728\u9ad8\u547c\u9163\u6597\uff0c\u674e\u9038\u62bd\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u5357\u5b98\u5c1a\u4e5f\u88ab\u53e6\u4e00\u4e2a\u6b66\u58eb\u7eca \u4f4f\uff0c\u65e0\u6cd5\u8d85\u8fc7\u3002\u8fd9\u4e2a\u6b66\u58eb\u4e43\u662f\u4e0e\u79e6\u582a\u9f50\u540d\u7684\u795e\u6b66\u8425\u4e09\u5927\u9ad8\u624b\u4e4b\u4e00\uff0c\u4e5f\u5c31\u662f\u90a3\u65e5\u5728\u6821\u573a\u4e0a \u624b\u62d4\u6728\u6869\u7684\u90a3\u4e2a\u540d\u53eb\u5f20\u633a\u7684\u4eba\uff0c\u4ed6\u4f7f\u7684\u5175\u5668\u662f\u4e00\u6839\u9752\u94dc\u9f50\u7709\u68cd\uff0c\u62db\u719f\u529b\u6c89\uff0c\u5de6\u53f3\u76d8\u65cb\uff0c \u7eb5\u6a2a\u62e9\u821e\u3002\u94dc\u68cd\u8d77\u5904\uff0c\u52b2\u98ce\u547c\u547c\uff0c\u5357\u5bab\u5c1a\u7684\u5200\u6cd5\u5ae1\u719f\uff0c\u5374\u662f\u5360\u4e0d\u4e86\u534a\u70b9\u4fbf\u5b9c\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u5357\u5bab\u5c1a\u5ffd\u5730\u4e00\u58f0\u5927\u53eb\uff0c\u539f\u6765\u4ed6\u88ab\u5f20\u633a\u7684\u68cd\u5c3e\u70b9\u4e2d\u80da\u9aa8\uff0c\u6447\u6447\u6b32\u5760\uff0c\u5f20\u633a \u54c8\u54c8\u4e00\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u767d\u5143\u5316\uff0c\u8fd9\u4e2a\u523a\u5ba2\u6211\u4ea4\u7ed9\u4f60\u5566\uff01\u201d\u62bd\u51fa\u68cd\u6765\uff0c\u4fbf\u4e0e\u79e6\u582a\u4e00\u9f50\u5408\u653b\u674e \u9038\u3002 \u3000\u3000\u674e\u9038\u72ec\u6218\u79e6\u582a\uff0c\u8fd8\u53ef\u4ee5\u6253\u6210\u5e73\u624b\uff0c\u52a0\u4e0a\u4e86\u4e00\u4e2a\u5f20\u633a\uff0c\u767b\u65f6\u9669\u8c61\u73af\u751f\uff0c\u5f20\u633a\u7684\u90a3\u6839\u94a2 \u68cd\u91cd\u8fbe\u516d\u5341\u4e8c\u65a4\uff0c\u5b9d\u5251\u524a\u5b83\u4e0d\u52a8\u3002\u4f46\u89c1\u6f2b\u7a7a\u65d7\u5f71\uff0c\u88f9\u7740\u4e00\u7247\u94f6\u5149\uff0c\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u4e0d\u7edd \u4e8e\u8033\uff0c\u674e\u9038\u5251\u5149\u7684\u5708\u5b50\u8d8a\u6765\u8d8a\u5c0f\uff0c\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u90a3\u8fb9\u53a2\uff0c\u5357\u5bab\u5c1a\u4e2d\u4e86 \u767d\u5143\u5316\u4e00\u5251\uff0c\u5ffd\u5730\u4f7f\u4e86\u4e00\u62db\u6551\u547d\u7edd\u62db\uff0c\u8131\u624b\u5c06\u5355\u5200\u98de\u51fa\uff0c\u767d\u5143\u5316\u51b7\u4e0d\u53ca\u9632\uff0c\u80a9\u5934\u88ab\u4ed6\u7684 \u98de\u5200\u7a7f\u8fc7\uff0c\u5357\u5bab\u5c1a\u8eab\u5f62\u5982\u7bad\uff0c\u7acb\u5373\u98de\u5954\uff0c\u4e00\u6e9c\u70df\u4f3c\u7684\u7a7f\u5165\u4e86\u82b1\u6728\u8302\u5bc6\u4e4b\u5904\u3002\u5f20\u633a\u7a0d\u4e00\u8e0c \u8e87\uff0c\u5fc3\u4e2d\u6b63\u81ea\u51b3\u65ad\u4e0d\u4e0b\uff0c\u8981\u4e0d\u8981\u5e2e\u767d\u5143\u5316\u53bb\u8ffd\u90a3\u4e2a\u523a\u5ba2\uff1f\u674e\u9038\u4f55\u7b49\u673a\u7075\uff0c\u4e00\u89c1\u6709\u673a\u53ef\u4e58\uff0c \u7acb\u5373\u62fc\u6b7b\u8fdb\u653b\uff0c\u5530\u7684\u4e00\u5251\u628a\u5f20\u633a\u523a\u4f24\uff0c\u6b63\u60f3\u9003\u8d70\uff0c\u79e6\u582a\u5ffd\u5730\u5c06\u65d7\u6746\u5f53\u4f5c\u82b1\u67aa\u4f7f\u7528\uff0c\u6746\u5c16 \u4e00\u6296\uff0c\u4e00\u62db\u201c\u5171\u5de5\u89e6\u5929\u201d\uff0c\u67aa\u5c16\u500f\u7684\u6311\u5230\u674e\u9038\u80f8\u524d\uff0c\u674e\u9038\u77ee\u8eab\u4e00\u907f\uff0c\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u8863 \u895f\u4e5f\u88ab\u4ed6\u7684\u65d7\u6746\u6311\u7834\u3002 \u3000\u3000\u79e6\u582a\u559d\u9053\uff1a\u201c\u522b\u7406\u90a3\u53ae\uff0c\u8fd9\u53ae\u624d\u662f\u6b63\u70b9\u3002\u201d\u5f20\u633a\u4e2d\u4e86\u4e00\u5251\uff0c\u66b4\u6012\u5982\u96f7\uff0c\u5373\u4f7f\u79e6\u582a\u6ca1 \u6709\u53d1\u8bdd\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u674e\u9038\u7684\u4e86\u3002\u4ed6\u53d7\u4f24\u4e4b\u540e\uff0c\u66f4\u4e3a\u9a81\u52c7\uff0c\u62a1\u8d77\u94a2\u68cd\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u628a\u674e \u9038\u6253\u5f97\u51e0\u4e4e\u7ad9\u7acb\u4e0d\u7a33\u3002 \u3000\u3000\u674e\u9038\u65bd\u5c55\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u5140\u662f\u62b5\u6321\u4e0d\u4f4f\uff0c\u81ea\u77e5\u65f6\u95f4\u4e00\u957f\uff0c\u5fc5\u65e0\u5e78\u7406\uff0c\u8fd9\u65f6\u4ed6\u4e07\u5ff5\u4ff1\u7070\uff0c \u5ffd\u5730\u94a2\u7259\u4e00\u54ac\uff0c\u9661\u7136\u8dc3\u8d77\uff0c\u4e00\u62db\u201c\u5929\u6cb3\u5012\u6302\u201d\uff0c\u5251\u82b1\u6735\u6735\uff0c\u98de\u6d12\u4e0b\u6765\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c\u7adf\u4f3c \u95ee\u8d77\u5343\u767e\u9053\u7cbe\u8292\u51b7\u7535\uff0c\u8fd9\u662f\u4e00\u62db\u4e24\u8d25\u4ff1\u4f24\u7684\u51f6\u6b8b\u5251\u6cd5\uff0c\u79e6\u582a\u5927\u559d\u4e00\u58f0\uff0c\u7075\u65d7\u75be\u5c55\uff0c\u672a\u80fd \u5c01\u4f4f\uff0c\u5f20\u633a\u8f7b\u529f\u7a0d\u5dee\uff0c\u88ab\u4ed6\u7684\u5251\u5149\u8feb\u5f97\u773c\u5149\u9563\u4e71\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u9000\u4e86\u51e0\u6b65\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u4f46\u542c\u5f97\u5530\u7684\u4e00\u58f0\uff0c\u674e\u9038\u51cc\u7a7a\u523a\u4e0b\uff0c\u4e00\u5251\u523a\u4e2d\u4e86\u5f20\u633a\u7684\u5c0f\u817f\uff0c\u5f20\u633a\u6492\u624b\u6254\u68cd\uff0c\u4e00\u8de4\u8dcc \u5012\u3002 \u3000\u3000\u4f46\u5f20\u633a\u7a76\u7adf\u662f\u795e\u6b66\u8425\u4e2d\u6709\u540d\u7684\u9ad8\u624b\uff0c\u867d\u7136\u4e2d\u5251\u5012\u5730\uff0c\u8fd9\u4e00\u62db\u4e34\u5371\u642c\u68cd\u529f\u529b\u4ecd\u662f\u6df1\u539a \u975e\u5e38\uff0c\u90a3\u6839\u516b\u5c3a\u591a\u957f\u7684\u9752\u94dc\u68cd\uff0c\u7adf\u4f3c\u98ce\u8f66\u7684\u8f74\u5fc3\u4e00\u6837\uff0c\u6253\u7740\u5708\u5708\uff0c\u76d8\u65cb\u98de\u6765\uff0c\u674e\u9038\u4eba\u672a \u7740\u5730\uff0c\u65e0\u6cd5\u95ea\u907f\uff0c\u63d0\u811a\u4e00\u8e4b\uff0c\u5374\u6d88\u4e0d\u4e86\u90a3\u80a1\u731b\u529b\uff0c\u811a\u540e\u8ddf\u7ed9\u68cd\u5c3e\u6cbe\u4e86\u4e00\u4e0b\uff0c\u75db\u5f7b\u9aa8\u6320\uff0c \u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u79e6\u582a\u7075\u65d7\u518d\u5c55\uff0c\u6d88\u53bb\u4e86\u674e\u9038\u7684\u5251\u52bf\uff0c\u65d7\u6746\u4e00\u6311\uff0c\u4f7f\u51fa\u4e86\u4e00\u62db\u201c\u4e2d\u5e73\u67aa\u201d \u7684\u62db\u6570\uff0c\u770b\u770b\u4fbf\u8981\u523a\u5165\u4e86\u674e\u9038\u7684\u5c0f\u8179\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u5a07\u547c\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u674e\u9038\u811a\u8ddf\u867d\u7136\u7ad9\u7a33\uff0c\u5fc3\u5934\u5374\u662f\u52a8\u8361\u4e0d\u4f11\uff0c \u62ac\u773c\u4e00\u671b\uff0c\u4f46\u89c1\u662f\u4e24\u4e2a\u5c11\u5973\uff0c\u5206\u82b1\u62c2\u67f3\uff0c\u6b63\u81ea\u7b11\u76c8\u76c8\u7684\u5411\u81ea\u5df1\u8d70\u6765\u3002\u90a3\u4e00\u58f0\u5a07\u547c\uff0c\u4e43\u662f \u4e0a\u5b98\u5a49\u513f\u6240\u53d1\u3002\u53e6\u4e00\u4e2a\u5219\u662f\u6b66\u7384\u971c\uff0c\u5979\u8eab\u6cd5\u8f83\u5feb\uff0c\u8fd9\u65f6\u5df2\u5230\u4e86\u8eab\u524d\u4e09\u4e08\u4e4b\u5730\uff0c\u5a07\u58f0\u7b11\u9053\uff1a \u201c\u674e\u516c\u5b50\uff0c\u6211\u4eec\u4e13\u8bda\u7b49\u4f60\uff0c\u5df2\u7b49\u5f97\u4e45\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u5730\u5012\u8f6c\u5251\u67c4\uff0c\u56de\u5251\u5411\u81ea\u5df1\u7684\u54bd\u5589\u4fbf\u523a\uff0c\u6b66\u7384\u971c\u4f3c\u4e4e\u65e9\u5df2\u6599\u5230\u4ed6\u6709 \u6b64\u4e00\u7740\uff0c\u7389\u624b\u4e00\u626c\uff0c\u6263\u5728\u638c\u5fc3\u7684\u4e00\u679a\u91d1\u94b1\u9556\u7535\u5c04\u800c\u51fa\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u674e\u9038\u7684\u5251\u5c16\u6253 \u6b6a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u5c31\u8fd9\u6837\u7684\u6ca1\u51fa\u606f\u5417\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u8fc8\u524d\u4e24\u6b65\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u674e\u9038\u54e5\u54e5\uff0c\u4f60\u968f\u6211\u4eec\u56de\u53bb\u5427\u3002\u201d\u674e\u9038\u7259\u6839\u4e00\u54ac\uff0c \u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u518d\u8d70\u4e0a\u4e09\u6b65\uff0c\u6211\u7acb\u523b\u56de\u5251\u81ea\u520e\uff0c\u5373\u7b97\u6b7b\u4e0d\u4e86\uff0c\u6211\u7684\u5fc3\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4f60\u4eec\u603b \u4e0d\u80fd\u963b\u4f4f\u6211\u7684\u8eaf\u58f3\u4e0d\u6b7b\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9762\u8272\u82cd\u767d\uff0c\u773c\u89d2\u6cea\u5149\u6676\u83b9\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u674e\u9038\u54e5\u54e5\uff0c \u4f60\u4f55\u82e6\u5982\u6b64\uff1f\u55ef\uff0c\u6211\u61c2\u5f97\u4f60\u7684\u5fc3\u4e8b\uff0c\u4f60\u613f\u4e0d\u613f\u610f\u542c\u6211\u7684\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u591a\u5c11\u65e5\u5b50\u4ee5\u6765\uff0c\u674e\u9038\u5c31\u6e34\u671b\u7740\u89c1\u5a49\u513f\u4e00\u9762\uff0c\u6e34\u671b\u7740\u4e0e\u5979\u4e92\u8bc9\u5fc3\u8179\uff0c\u7136\u800c\u5728\u6b64\u65f6\u6b64\u5730\uff0c \u5c24\u5176\u5728\u4ed6\u521a\u521a\u89c1\u4e86\u90a3\u4e00\u5e55\u201c\u8bfb\u6a84\u6587\u201d\u7684\u60c5\u666f\u4e4b\u540e\uff0c\u5ffd\u7136\u95f4\u4ed6\u89c9\u5f97\u5a49\u513f\u79bb\u5f00\u4ed6\u5f88\u8fdc\u5f88\u8fdc\uff0c \u8fdc\u5f97\u5c31\u50cf\u4e00\u4e2a\u964c\u751f\u4eba\u4f3c\u7684\uff0c\u4ed6\u597d\u50cf\u7406\u89e3\u5979\uff0c\u7136\u800c\u53c8\u5b9e\u5728\u4e0d\u7406\u89e3\u5979\u3002\u8fd9\u65f6\uff0c\u7eb5\u6709\u4e07\u8bed\u5343\u8a00\uff0c \u5374\u90fd\u6897\u585e\u5589\u5934\uff0c\u534a\u53e5\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u674e\u9038\u54e5\u54e5\uff0c\u5929\u540e\u5176\u5b9e\u5bf9\u4f60\u5e76\u65e0\u6076\u610f\u2026\u2026\u201d\u674e\u9038\u53cc\u773c\u4e00\u7741\uff0c\u5ffd \u5730\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u8bf4\u554a\uff01\u4f60\u56de\u53bb\u505a\u4f60\u7684\u5973\u5b98\uff0c\u522b\u518d\u7ba1\u6211\uff01\u6211\u66f4\u4e0d\u613f\u610f\u89c1\u5230\u4f60\u5230\u6211\u7684\u8ddf \u524d\u6765\u505a\u8bf4\u5ba2\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9762\u8272\u53d1\u9752\uff0c\u54ac\u7740\u5634\u5df4\uff0c\u6cea\u73e0\u513f\u5728\u775b\u7736\u91cc\u6253\u8f6c\uff0c\u597d\u534a\u54cd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u6b66\u7384\u971c \u9053\uff1a\u201c\u4f60\u5230\u4e86\u4eac\u57ce\uff0c\u8fd9\u91cc\u7684\u60c5\u5f62\uff0c\u4f60\u4e5f\u4eb2\u773c\u770b\u5230\u4e86\uff0c\u4f60\u8fd8\u5728\u8d1f\u6c14\u5417\uff1f\u201d\u674e\u9038\u5fc3\u75db\u5982\u5200\u7ede\uff0c \u773c\u5149\u4e00\u77a5\uff0c\u4f46\u89c1\u4e0a\u5b98\u5a49\u513f\u548c\u6b66\u7384\u971c\u90fd\u5728\u51dd\u7738\u671b\u4ed6\uff0c\u773c\u5149\u4e2d\u5145\u6ee1\u7740\u671f\u5f85\u7684\u6df1\u60c5\u3002\u674e\u9038\u5fcd\u7740 \u60b2\u75db\uff0c\u907f\u5f00\u4e86\u5979\u4eec\u7684\u76ee\u5149\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u5728\u4f60\u4eec\u7684\u638c\u63e1\u4e4b\u4e2d\uff0c\u597d\u5427\uff0c\u6765\u5427\uff01\u4f60 \u662f\u4e0d\u662f\u8981\u5c06\u6211\u62ff\u53bb\u89c1\u4f60\u4eec\u7684\u5929\u540e\uff1f\u201d\u5a49\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u613f\u7559\u4e0b\u4f60\u5c31\u8d70\u5427\uff01\u4f46\u613f \u54b1\u4eec\u4ee5\u540e\u8fd8\u80fd\u591f\u89c1\u9762\u3002\u201d\u6b66\u7384\u971c\u628a\u624b\u4e00\u6325\uff0c\u79e6\u582a\u5f20\u633a\u5de6\u53f3\u9000\u4e0b\uff0c\u8ba9\u5f00\u4e86\u4e00\u6761\u53bb\u8def\u3002 \u3000\u3000\u674e\u9038\u6781\u529b\u6291\u5236\u4f4f\u5fc3\u5934\u7684\u6fc0\u52a8\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u7384\u971c\uff0c\u591a\u8c22\u4f60\u53c8\u4e00\u6b21\u7684\u653e\u4e86\u6211\uff0c\u6211\u53ef\u4e0d \u80fd\u62a5\u7b54\u4f60\u5566\u3002\u5a49\u513f\uff0c\u6211\u540e\u6094\u4e0e\u4f60\u91cd\u9022\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u53ea\u5f53\u8fd9\u4e16\u4e0a\u518d\u6ca1\u6709\u6211\u8fd9\u4e2a\u4eba\uff0c\u6211\u4e5f \u628a\u4f60\u5f53\u505a\u6b7b\u4e86\u3002\u4eca\u751f\u4eca\u4e16\uff0c\u6211\u4e0e\u4f60\u8def\u9694\u4e91\u6ce5\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u518d\u671b\u4e0e\u6211\u89c1\u9762\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u80cc\u8f6c\u4e86\u9762\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u8f7b\u8f7b\u7684\u54ed\u4e86\u51fa\u6765\uff0c\u5979\u77e5\u9053\u9664\u975e\u662f\u81ea\u5df1\u8ddf\u7740\u4e00\u540c \u8d70\uff0c\u5426\u5219\u53ea\u6015\u662f\u771f\u7684\u4e0d\u80fd\u518d\u89c1\u4e86\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u5979\u5fc3\u4e2d\u5df2\u53cd\u53cd\u8986\u8986\u8f6c\u4e86\u65e0\u6570\u6b21\u5ff5\u5934\uff0c\u7ec8\u4e8e \u8fd8\u662f\u7559\u4e0b\u6765\uff0c\u5f85\u5979\u8f6c\u8fc7\u8eab\u65f6\uff0c\u674e\u9038\u5df2\u7ecf\u8d70\u4e86\u3002 \u3000\u3000\u8fdc\u5904\u7684\u5929\u7a7a\u5ffd\u5730\u95ea\u8fc7\u4e00\u6e9c\u84dd\u8272\u7684\u706b\u5149\uff0c\u6b66\u7384\u971c\u6014\u4e86\u4e00\u6014\uff0c\u624b\u6478\u5251\u67c4\uff0c\u53ea\u89c1\u79e6\u582a\u5f20\u633a\uff0c \u65e9\u5df2\u62d4\u811a\u98de\u5954\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u5148\u53bb\u6b47\u5427\uff0c\u6211\u53bb\u53bb\u4fbf\u56de\u3002\u201d\u90a3\u6e9c\u84dd\u706b\uff0c\u4e00\u95f4\u5373\u706d\uff0c \u4e0a\u5b98\u5a49\u513f\u6839\u672c\u6ca1\u6709\u7559\u610f\uff0c\u89c1\u6b66\u7384\u971c\u62d4\u5251\u8981\u8ffd\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u6025\u5fd9\u626f\u7740\u5979\u7684\u8863\u8896\u9053\uff1a\u201c\u59d0\u59d0\uff0c \u5929\u540e\u4e0d\u662f\u8bf4\u8fc7\uff0c\u6216\u53bb\u6216\u7559\uff0c\u90fd\u4e0d\u8981\u52c9\u5f3a\u4ed6\u5417\uff1f\u6211\u77e5\u9053\u4ed6\u7684\u813e\u6c14\uff0c\u522b\u8981\u8ffd\u4ed6\uff0c\u7559\u7740\u4ed6\u4e00\u6761 \u6027\u547d\u5427\uff01\u6b66\u7384\u971c\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8863\u8896\u4e00\u602b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u53bb\u8ffd\u4ed6\uff0c\u6211\u8981\u62a4\u9001\u4ed6\u4e00\u7a0b\uff0c \u4f60\u56de\u53bb\u5427\u3002\u201d\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e0a\u5b98\u5a49\u513f\u5ffd\u89c9\u6b66\u7384\u971c\u9762\u4e0a\u9732\u51fa\u4e00\u79cd\u5f88\u5947\u7279\u7684\u795e\u60c5\uff0c\u90a3\u7b11\u5bb9\u4f3c\u4e4e \u662f\u88c5\u51fa\u6765\u7684\uff0c\u7b11\u5bb9\u4e2d\u6709\u4e00\u4efd\u82cd\u51c9\uff0c\u53c8\u4f3c\u4e4e\u6709\u4e00\u4e1d\u6050\u60e7\uff0c\u4e0a\u5b98\u5a49\u513f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u89c1\u6b66\u7384\u971c \u8eab\u5f62\u500f\u8d77\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5c31\u8ffd\u4e0a\u4e86\u79e6\u582a\u5f20\u633a\uff0c\u4e00\u540c\u5411\u540e\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u7687\u5bab\u7684\u540e\u9762\u4e43\u662f\u7f05\u5c71\uff0c\u79e6\u59cb\u7687\u7684\u65f6\u5019\uff0c\u66fe\u5728\u5c71\u4e0a\u5efa\u9020\u8fc7\u963f\u623f\u5bab\uff0c\u201c\u8986\u538b\u4e09\u767e\u4f59\u91cc\uff0c \u9694\u79bb\u5929\u65e5\u3002\u201d\u540e\u6765\u88ab\u9879\u7fbd\u4ed8\u4e4b\u4e00\u70ac\uff0c\u5c3d\u6210\u7126\u571f\uff0c\u5510\u671d\u5728\u957f\u5b89\u5efa\u90fd\uff0c\u5c71\u4e0a\u4e5f\u4fee\u9020\u4e86\u4e00\u4e9b\u5bab \u6bbf\uff0c\u4f46\u5374\u8fdc\u8fdc\u4e0d\u53ca\u963f\u623f\u5bab\u7684\u89c4\u6a21\uff0c\u8bb8\u591a\u5730\u65b9\u90fd\u8352\u829c\u4e86\u3002\u8fd9\u65f6\uff0c\u674e\u9038\u6b63\u9003\u5165\u4e86\u7f05\u5c71\uff0c\u60f3\u4ece \u5c71\u80cc\u9762\u7ffb\u8fc7\u53bb\u3002\u4ed6\u8d70\u8fc7\u963f\u623f\u5bab\u7684\u9057\u5740\uff0c\u76f4\u4e0a\u5c71\u5934\uff0c\u5fc3\u4e2d\u65e0\u9650\u60b2\u51c9\uff0c\u7eb5\u76ee\u56db\u671b\uff0c\u4f46\u89c1\u4e00\u5f2f \u51b7\u6708\uff0c\u7247\u7247\u677e\u6d9b\uff0c\u56db\u4e0b\u51c4\u6e05\uff0c\u8f89\u714c\u5bcc\u4e3d\u7684\u7687\u5bab\uff0c\u65e9\u5df2\u88ab\u4ed6\u629b\u5728\u80cc\u540e\uff0c\u671b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u674e\u9038\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u4e0b\u5c71\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u4f3c\u542c\u5f97\u6709\u53ae\u6740\u4e4b\u58f0\uff0c\u674e\u9038\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u89c1 \u4e24\u6761\u4eba\u5f71\uff0c\u6377\u5982\u98de\u9e1f\uff0c\u6b63\u5411\u7740\u81ea\u5df1\u8fce\u9762\u800c\u6765\uff01 \u3000\u3000\u524d\u9762\u7684\u4e00\u4eba\u8eab\u6750\u9b41\u68a7\u3002\u6325\u821e\u7740\u4e00\u6761\u957f\u97ad\uff0c\u79bb\u8eab\u5341\u6570\u4e08\u5916\uff0c\u5c31\u542c\u5230\u4ed6\u7684\u97ad\u98ce\u547c\u54cd\uff0c\u66f4 \u5947\u602a\u7684\u662f\u4ed6\u597d\u50cf\u53d7\u4f24\u7684\u91ce\u517d\u4f3c\u7684\uff0c\u4e00\u9762\u62e9\u52a8\u957f\u97ad\uff0c\u4e00\u9762\u53d1\u51fa\u4ee4\u4eba\u5fc3\u60b8\u7684\u568e\u53eb\u3002 \u3000\u3000\u674e\u9038\u4e00\u773c\u671b\u53bb\uff0c\u8ba4\u51fa\u4e86\u4ed6\u662f\u795e\u6b66\u8425\u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\u897f\u95e8\u9738\uff0c\u90a3\u65e5\u5728\u6821\u573a\u6bd4\u6b66\uff0c\u897f\u95e8\u9738 \u5e76\u6ca1\u6709\u9732\u9762\uff0c\u4f46\u674e\u9038\u77e5\u9053\u4ed6\u548c\u79e6\u582a\u5f20\u633a\u4e8c\u4eba\uff0c\u5e76\u79f0\u795e\u6b66\u8425\u4e09\u5927\u9ad8\u624b\uff0c\u800c\u79e6\u5f20\u4e8c\u4eba\u8fd8\u662f\u4ed6 \u7684\u5c5e\u4e0b\uff0c\u542c\u8bf4\u4ed6\u7684\u6b66\u529f\uff0c\u8fdc\u8fdc\u5728\u79e6\u5f20\u4e8c\u4eba\u4e4b\u4e0a\u3002\u53ea\u4e00\u4e2a\u79e6\u582a\uff0c\u5df2\u53ef\u4ee5\u548c\u674e\u9038\u6253\u6210\u5e73\u624b\uff0c \u8fd9\u897f\u95e8\u9738\u7684\u6b66\u529f\uff0c\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u4e86\u3002 \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u6b32\u64d2\u5148\u7eb5\uff0c\u5374\u6545\u610f\u5728\u8fd9\u91cc\u4f0f\u4e0b\u9ad8\u624b\u3002\u54fc\uff0c\u54fc\uff0c\u6b66 \u5219\u5929\u8fd9\u4e00\u624b\u6cd5\uff0c\u8fde\u5982\u4eb2\u4fe1\u7684\u4e0a\u5b98\u5a49\u513f\u4e5f\u7ed9\u5979\u7792\u8fc7\u4e86\u3002\u60f3\u662f\u6b66\u5219\u5929\u60f3\u5a49\u513f\u7ee7\u7eed\u6548\u5fe0\u4e8e\u5979\uff0c \u907f\u514d\u4ee4\u5a49\u513f\u4f24\u5fc3\uff0c\u6545\u6b64\u4e0d\u613f\u5f53\u7740\u5a49\u513f\u7684\u9762\uff0c\u5c06\u6211\u4f24\u5bb3\u3002\u201d\u4ed6\u5c3d\u4ece\u574f\u5904\u7740\u60f3\uff0c\u60f3\u770b\u81ea\u5df1\u53cd \u6b63\u662f\u62fc\u6b7b\u6765\u7684\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u53cd\u800c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u80cc\u540e\u53c8\u5ffd\u7136\u4f20\u6765\u4e86\u6b66\u7384\u971c\u7684\u547c\u53eb\uff1a\u201c\u674e\u516c\u5b50\uff0c\u8d76\u5feb\u56de\u6765\uff01\u201d\u58f0\u97f3\u5728\u591c\u7a7a \u4e2d\u98a4\u6218\uff0c\u663e\u5f97\u6781\u662f\u6050\u614c\u4e0d\u5b89\uff0c\u674e\u9038\u5fc3\u5934\u4e00\u51db\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u5979\u4eec\u8f6f\u786c\u517c\u65bd\uff0c\u76ee\u7684\u4e0d\u5916 \u4e4e\u8feb\u6211\u56de\u53bb\u3002\u6211\u662f\u9876\u5929\u7acb\u5730\u7684\u7537\u5b50\uff0c\u5c82\u80fd\u5c48\u819d\u4e8b\u4ec7\uff0c\u53d7\u4eba\u51cc\u8fb1\u3002\u201d\u5fc3\u5ff5\u672b\u5df2\uff0c\u4f46\u542c\u5f97\u6b66 \u7384\u971c\u7684\u811a\u6b65\u58f0\u5df2\u5230\u8eab\u540e\uff0c\u800c\u897f\u95e8\u9738\u7684\u957f\u97ad\uff0c\u4e5f\u5df2\u5230\u4e86\u8eab\u524d\u3002 \u3000\u3000\u674e\u9038\u8fd9\u65f6\u6b63\u7ad9\u5728\u60ac\u5ca9\u4e4b\u4e0a\uff0c\u6b66\u7384\u971c\u521a\u559d\u4e86\u4e00\u58f0\u201c\u4f4f\u624b\uff01\u201d\u9661\u7136\u95f4\u5ffd\u89c1\u674e\u9038\u98de\u8eab\u4e00\u7eb5\uff0c \u7adf\u4ece\u767e\u4e08\u5371\u5d16\u4e4b\u4e0a\uff0c\u8dc3\u4e0b\u6df1\u8c37\uff01 \u3000\u3000\u6b66\u7384\u971c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u674e\u9038\u7adf\u4f1a\u8f7b\u751f\uff0c\u5f85\u5979\u6e05\u695a\u4e86\u53d1\u751f\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\u65f6\uff0c\u65e9\u5df2\u6765\u4e0d\u53ca \u4e86\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u6b66\u7384\u971c\u4f46\u89c9\u5730\u8f6c\u5929\u65cb\uff0c\u51e0\u4e4e\u4e5f\u8981\u8dcc\u4e0b\u5d16\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e00\u6761\u9ed1\u5f71\u500f\u7684\u98de\u8d8a\u8fc7\u897f\u95e8\u9738\u524d\u5934\uff0c\u4e00\u4ef6\u9ed1\u5ffd\u5ffd\u7684\u5175\u5668\u7a81\u7136\u5411\u6b66\u7384\u971c \u5f53\u5934\u7f69\u4e0b\uff0c\u897f\u95e8\u9738\u6296\u52a8\u957f\u97ad\uff0c\u594b\u529b\u4e00\u6321\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6b66\u59d1\u5a18\uff0c\u5feb\u6765\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff01\u201d \u6b66\u7384\u971c\u4e00\u77a7\uff0c\u4f46\u89c1\u6765\u7684\u662f\u4e2a\u9752\u8863\u9053\u58eb\uff0c\u624b\u821e\u4f5b\u5c18\uff0c\u53ea\u4e00\u4f5b\u5c31\u628a\u897f\u95e8\u9738\u7684\u957f\u97ad\u62c2\u5f00\uff0c\u500f\u7684 \u53c8\u662f\u5f53\u7a7a\u5377\u4e0b\uff0c\u52b2\u98ce\u62c2\u8155\uff0c\u9510\u5229\u5982\u5200\u3002\u4e60\u6b66\u4e4b\u4eba\uff0c\u9632\u536b\u81ea\u8eab\uff0c\u4e43\u662f\u672c\u80fd\uff0c\u6b66\u7384\u971c\u867d\u5728\u4f24 \u75db\u4e4b\u4e2d\uff0c\u4f46\u5904\u6b64\u6027\u547d\u5371\u6b86\u4e4b\u9645\uff0c\u672c\u80fd\u7684\u5c55\u51fa\u4e86\u4e00\u62db\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u5c06\u90a3\u9053\u58eb\u7684\u653b\u52bf\u5316\u5f00\u3002 \u90a3\u9053\u58eb\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6b66\u7384\u971c\u8fd9\u91ce\u4e2b\u5934\u5417\uff1f\u54c8\u54c8\uff0c\u6211\u6b63\u60f3\u5bfb\u4f60\uff0c\u4f60\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u6562\u4f24 \u6211\u7684\u5f92\u513f\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u9752\u8863\u9053\u58eb\u6b63\u662f\u5929\u6076\u9053\u4eba\u3002\u4ed6\u90a3\u65e5\u4e0e\u91d1\u9488\u56fd\u624b\u590f\u4faf\u575a\u8f83\u6280\uff0c\u8f93\u4e86\u4e00\u7740\uff0c\u672c\u62df \u56de\u8f6c\u6606\u4ed1\uff0c\u518d\u7ec3\u7edd\u6280\uff0c\u5374\u88ab\u4ed6\u7684\u4e24\u4e2a\u5f92\u5f1f\u2014\u2014\u6076\u884c\u8005\u548c\u6bd2\u89c2\u97f3\u6311\u5506\u51fa\u6765\uff0c\u540c\u5165\u957f\u5b89\uff0c\u4e00 \u6765\u662f\u60f3\u6551\u88f4\u708e\u51fa\u76d1\uff0c\u4e8c\u6765\u662f\u60f3\u627e\u6b66\u7384\u971c\u4e00\u8f83\u9ad8\u4e0b\u3002 \u3000\u3000\u4ed6\u4eec\u4ece\u5317\u9762\u767b\u5c71\u60f3\u5077\u5165\u5c71\u5357\u9762\u7684\u79bb\u5bab\uff0c\u518d\u6f5c\u5165\u5185\u82d1\uff0c\u795e\u6b66\u8425\u7684\u7b2c\u4e00\u9ad8\u624b\u897f\u95e8\u9738\u6b63\u5728 \u5c71\u4e0a\u628a\u5b88\uff0c\u4e0e\u5929\u6076\u9053\u4eba\u906d\u9047\uff0c\u6076\u6218\u8d77\u6765\uff0c\u897f\u95e8\u9738\u4e0d\u654c\uff0c\u5c04\u51fa\u4e86\u4e00\u652f\u86c7\u7130\u7bad\u62a5\u8b66\uff0c\u5929\u7a7a\u95ea \u8fc7\u7684\u90a3\u4e00\u6e9c\u84dd\u706b\uff0c\u4fbf\u662f\u86c7\u9985\u7bad\u53d1\u7684\u706b\u5149\uff0c\u6b66\u7384\u971c\u662f\u770b\u89c1\u4e86\u897f\u95e8\u9738\u7684\u8fd9\u4e2a\u8baf\u53f7\uff0c\u8d76\u6765\u5e94\u63f4 \u7684\u3002\u674e\u9038\u4ee5\u4e3a\u5979\u662f\u5949\u4e86\u6b66\u5219\u5929\u4e4b\u547d\u6765\u6349\u62ff\u4ed6\uff0c\u90a3\u662f\u5b8c\u5168\u731c\u9519\u4e86\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u662f\u90aa\u6d3e\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u6b66\u529f\u4e4b\u5f3a\uff0c\u4e16\u7f55\u5176\u4f26\uff0c\u6b66\u7384\u971c\u867d\u672a\u89c1\u8fc7\u4ed6\uff0c\u4e5f \u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u63a5\u4e86\u6570\u62db\u4e4b\u540e\uff0c\u4fbf\u77e5\u9053\u4ed6\u662f\u8c01\u4eba\u3002\u674e\u9038\u8df3\u5d16\u4e4b\u540e\uff0c\u6b66\u7384\u971c\u672c\u6765\u8981\u8ddf\u7740\u4e0b \u53bb\uff0c\u5bdf\u770b\u4ed6\u7684\u751f\u6b7b\u7a76\u7adf\uff0c\u4f46\u88ab\u5929\u6076\u9053\u4eba\u62e6\u7740\uff0c\u7247\u523b\u4e4b\u95f4\u63a5\u8fde\u9000\u4e86\u597d\u51e0\u6b21\u9669\u62db\uff0c\u5fc3\u4e2d\u53c8\u6025 \u53c8\u6012\uff0c\u53ea\u597d\u5168\u795e\u5e94\u4ed8\u3002 \u3000\u3000\u897f\u95e8\u9738\u6325\u52a8\u957f\u97ad\u4e0a\u6765\u52a9\u6218\uff0c\u65e0\u6076\u9053\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u6211\u7684\u8150\u9aa8\u795e\u638c\uff0c\u4ee5\u4f60\u7684\u6b66 \u529f\uff0c\u8d76\u5feb\u56de\u53bb\u8c03\u6cbb\uff0c\u6216\u8005\u8fd8\u53ef\u4ee5\u4fdd\u5168\u6027\u547d\uff0c\u4f60\u5374\u8fd8\u8981\u6765\u9001\u6b7b\u4e48\uff1f\u201d\u897f\u95e8\u9738\u6012\u9053\uff1a\u201c\u660e\u5e74 \u4eca\u65e5\uff0c\u4e14\u770b\u662f\u8c01\u7684\u5fcc\u8fb0\uff1f\u201d\u4ed6\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u672a\u4e00\u8d25\uff0c\u8fd9\u56de\u4e2d\u4e86\u6bd2\u638c\uff0c\u4ed7\u7740\u7cbe\u7eaf\u7684\u5185\u529f\uff0c \u95ed\u7740\u4e86\u5168\u8eab\u7a74\u9053\uff0c\u81ea\u4fe1\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4e0d\u4f1a\u53d1\u4f5c\uff0c\u4ed6\u62fc\u7740\u53e3\u6c14\uff0c\u5b9a\u8981\u5148\u62a5\u8fd9\u4e00\u638c\u4e4b\u4ec7\uff0c \u54ea\u77e5\u5929\u6076\u9053\u4eba\u771f\u4e2a\u9ad8\u5f3a\uff0c\u5728\u4e24\u5927\u9ad8\u624b\u5939\u51fb\u4e4b\u4e0b\uff0c\u7adf\u80fd\u5e94\u4ed8\u81ea\u5982\u3002\u4f46\u89c1\u897f\u95e8\u9738\u7684\u957f\u97ad\u672a\u5230\u3002 \u4ed6\u53cc\u80a9\u4e00\u6643\uff0c\u8eab\u5b50\u65cb\u98ce\u4f3c\u7684\uff0c\u968f\u7740\u97ad\u68a2\u4fbf\u8f6c\u51fa\u53bb\uff0c\u866c\u9f99\u97ad\u867d\u7136\u957f\u8fbe\u4e08\u4f59\uff0c\u7adf\u8fde\u4ed6\u7684\u8863\u89d2 \u4e5f\u6ca1\u6709\u6cbe\u7740\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u5929\u6076\u9053\u4eba\u5728\u97ad\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u8eab\u5f62 \u8f6c\u6362\uff0c\u5012\u63d0\u62c2\u5c18\uff0c\u5c18\u67c4\u70b9\u5230\u4e86\u897f\u95e8\u9738\u80a9\u540e\u7684\u98ce\u5e9c\u7a74\uff0c\u6b66\u7384\u971c\u4e00\u4e2a\u95ea\u8eab\uff0c\u5251\u8d77\u5904\u5bd2\u5149\u75be\u5410\uff0c \u4e00\u62db\u201c\u7389\u5973\u7a7f\u9488\u201d\uff0c\u5251\u950b\u4e5f\u523a\u5230\u4e86\u5929\u6076\u9053\u4eba\u80c1\u4e0b\u7684\u613f\u6c14\u7a74\uff0c\u5929\u6076\u9053\u4eba\u6b63\u5728\u653b\u51fb\u897f\u95e8\u9738\uff0c \u80c1\u4e0b\u9732\u51fa\u7a7a\u95e8\uff0c\u8fd9\u4e00\u5251\u672c\u6765\u662f\u975e\u4e2d\u4e0d\u53ef\uff0c\u54ea\u77e5\u5929\u6076\u9053\u4eba\u7684\u6b66\u529f\u786e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u503e\u9877\u4e4b\u95f4\uff0c \u62db\u81f4\u7acb\u53d8\uff0c\u6b66\u7384\u971c\u7684\u5251\u5c16\u582a\u582a\u523a\u5230\uff0c\u5ffd\u89c9\u5251\u5c16\u4e00\u79fb\uff0c\u6ed1\u8fc7\u4e00\u8fb9\uff0c\u4f46\u89c1\u5929\u6076\u9053\u4eba\u624b\u6325\u62c2\u5c18\uff0c \u4e00\u7f20\u4e00\u7ed5\uff0c\u7adf\u7136\u4f7f\u51fa\u501f\u529b\u6253\u529b\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5c06\u5979\u7684\u5b9d\u5251\u7f20\u7740\u3002\u6b66\u7384\u971c\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u8fd0 \u6c14\u4e00\u5439\uff0c\u957f\u5251\u987a\u7740\u4ed6\u62c2\u5c18\u7275\u626f\u4e4b\u52bf\uff0c\u5411\u524d\u4e00\u9001\uff0c\u4e5f\u7528\u501f\u529b\u6253\u529b\u7684\u529f\u592b\uff0c\u5316\u89e3\u4ed6\u90a3\u80a1\u7c98\u5f15 \u4e4b\u52b2\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u897f\u95e8\u9738\u4e5f\u4f7f\u51fa\u4e86\u8fde\u73af\u4e09\u97ad\u3001\u56de\u98ce\u626b\u67f3\u7684\u7edd\u6280\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff01\u98ce\u58f0\u547c\u54cd\uff0c \u5377\u8d77\u4e86\u4e00\u56e2\u97ad\u5f71\uff0c\u5411\u4ed6\u731b\u626b\u3002\u5929\u6076\u9053\u4eba\u82e5\u7136\u8fd8\u8981\u786c\u593a\u6b66\u7384\u971c\u7684\u5b9d\u5251\uff0c\u52bf\u96be\u9003\u90a3\u4e09\u97ad\u706d\u9876 \u4e4b\u707e\u3002\u5929\u6076\u9053\u4eba\u53ea\u597d\u677e\u5f00\u62c2\u5c18\uff0c\u4e00\u63d0\u8170\u52b2\uff0c\u7528\u4e86\u4e2a\u201c\u71d5\u5b50\u94bb\u4e91\u201d\u7684\u8eab\u6cd5\uff0c\u51ed\u7a7a\u8df3\u8d77\u4e09\u4e08 \u591a\u9ad8\uff0c\u7136\u540e\u602b\u5c18\u4e00\u5c55\uff0c\u51cc\u7a7a\u51fb\u4e0b\uff0c\u5c06\u6b66\u7384\u971c\u7684\u957f\u5251\u4e0e\u897f\u95e8\u9738\u7684\u957f\u97ad\u4e00\u9f50\u8361\u5f00\u3002 \u3000\u3000\u53cc\u65b9\u4ea4\u6362\u4e86\u8fd9\u51e0\u8bb0\u6076\u62db\uff0c\u5404\u81ea\u5fc3\u60ca\u3002\u800c\u5929\u6076\u9053\u4eba\u6bd4\u6b66\u7384\u971c\u5403\u60ca\u66f4\u751a\uff01 \u3000\u3000\u6b66\u7384\u971c\u770b\u6765\u4e0d\u8fc7\u4e8c\u5341\u5de6\u53f3\uff0c\u672c\u9886\u4e4b\u9ad8\uff0c\u5374\u662f\u5927\u51fa\u5929\u6076\u9053\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u8fd9\u4e5f\u8fd8\u7f62\u4e86\uff0c \u6700\u4ee4\u5929\u6076\u9053\u4eba\u5403\u60ca\u7684\u662f\u6b66\u7384\u971c\u7684\u5251\u672f\u6b66\u529f\uff0c\u751a\u4f3c\u4e00\u4f4d\u6b66\u6797\u5f02\u4eba\u7684\u5bb6\u6570\uff0c\u5929\u6076\u9053\u4eba\u5929\u4e0d\u6015 \u5730\u4e0d\u6015\uff0c\u5929\u751f\u6700\u5fcc\u60ee\u7684\u5c31\u662f\u8fd9\u4f4d\u6b66\u6797\u5f02\u4eba\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u542c\u5f97\u5c71\u8c37\u4e0b\u9762\u4f20\u6765\u4e00\u58f0\u51c4\u5389\u7684\u53eb\u58f0\uff0c\u6b66\u7384\u971c\u5fc3\u5934\u5927\u9707\uff0c\u53ea\u9053\u8fd9\u662f\u674e\u9038\u7edd\u547d \u7684\u53eb\u58f0\u3002\u4f46\u89c1\u5929\u6076\u9053\u4eba\u4e5f\u597d\u4f3c\u5403\u4e86\u4e00\u60ca\uff0c\u4e3e\u8d77\u4f5b\u5c18\uff0c\u6321\u4f4f\u9762\u524d\uff0c\u975e\u4f46\u4e0d\u4e58\u673a\u8fdb\u653b\uff0c\u53cd\u800c \u597d\u50cf\u6015\u6b66\u7384\u971c\u7a81\u88ad\u4f3c\u7684\u3002\u6b66\u7384\u971c\u5251\u62db\u4e00\u7f13\uff0c\u7acb\u5373\u60f3\u5230\uff0c\u674e\u9038\u8df3\u4e0b\u53bb\u5df2\u6709\u591a\u65f6\uff0c\u82e5\u7136\u5e78\u5f97 \u6454\u4f24\u4e0d\u6b7b\uff0c\u5c82\u6709\u8fd9\u65f6\u5019\u624d\u53d1\u51fa\u7edd\u547d\u7684\u547c\u53eb\uff1f \u3000\u3000\u5929\u6076\u9053\u4eba\u5374\u542c\u51fa\u4e86\u90a3\u662f\u4ed6\u7684\u5f92\u5f1f\u6076\u884c\u8005\u7684\u53eb\u58f0\uff0c\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u4e43\u662f\u5728\u5c71\u8c37\u4e0b\u9762\u66ff \u4ed6\u628a\u98ce\u7684\u3002\u5929\u6076\u9053\u4eba\u5fc3\u795e\u4e00\u4e71\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u5728\u4e0b\u9762\u4e5f\u9047\u5230\u4e86\u4ec0\u4e48\u9ad8\u624b\u4e0d\u6210\uff1f\u201d\u5929 \u6076\u9053\u4eba\u7684\u6b66\u529f\u672c\u6765\u5728\u6b66\u7384\u971c\u4e0e\u897f\u95e8\u9738\u4e4b\u4e0a\uff0c\u8fd9\u65f6\u5fc3\u795e\u5fae\u4e71\uff0c\u88ab\u6b66\u7384\u971c\u5c55\u5251\u53cd\u653b\uff0c\u4e00\u8fde\u51e0 \u62db\u51cc\u5389\u4e4b\u6781\u7684\u6740\u7740\uff0c\u767b\u65f6\u626d\u8f6c\u4e86\u5c40\u52bf\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u795e\u6b66\u8425\u7684\u53e6\u5916\u4e24\u4f4d\u9ad8\u624b\uff0c\u79e6\u582a\u548c\u5f20\u633a\u4ea6\u5df2\u53cc\u53cc\u6765\u5230\uff0c\u5f20\u633a\u662f\u4e2a\u83bd\u592b\uff0c\u6325 \u52a8\u9752\u94dc\u68cd\u9996\u5148\u653b\u4e0a\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u81ed\u9053\u58eb\uff0c\u6562\u5230\u9a8a\u5c71\u4e0a\u6765\u6492\u91ce\uff1f\u201d\u624b\u8d77\u68cd\u843d\uff0c \u4e00\u62db\u201c\u91d1\u94a2\u964d\u9b54\u201d\uff0c\u7acb\u5373\u5411\u5929\u6076\u9053\u4eba\u7684\u80f8\u53e3\u649e\u53bb\uff0c\u5929\u6076\u9053\u4eba\u4e00\u5251\u5c01\u51fa\u5916\u95e8\uff0c\u5de6\u624b\u4e00\u4f38\uff0c \u767b\u65f6\u6293\u7740\u4e86\u68cd\u5934\uff0c\u5f20\u633a\u4e00\u8eab\u795e\u529b\uff0c\u7adf\u7136\u593a\u4e0d\u56de\u6765\uff0c\u4f46\u542c\u5f97\u5929\u6076\u9053\u4eba\u7b11\u58f0\u672b\u7edd\uff0c\u90a3\u6839\u9752\u94dc \u68cd\u5df2\u88ab\u4e24\u80a1\u5de8\u529b\u62d7\u5f97\u5f2f\u66f2\u4e0b\u6765\uff0c\u897f\u95e8\u9738\u6325\u97ad\u731b\u626b\uff0c\u5929\u6076\u9053\u4eba\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6253\u5427\uff01\u201d \u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u633a\u6c34\u725b\u822c\u7684\u8eab\u8eaf\u51cc\u7a7a\u98de\u4e86\u8d77\u6765\uff0c\u5411\u897f\u95e8\u9738\u649e\u53bb\uff0c\u897f\u95e8\u9738\u957f\u97ad\u6025\u6536\uff0c\u62a2 \u4e0a\u53bb\u63a5\uff0c\u6ca1\u6709\u63a5\u7740\uff0c\u5f20\u633a\u4e00\u5934\u649e\u7740\u4e86\u5ca9\u77f3\uff0c\u8111\u76d6\u9aa8\u88c2\u4e3a\u4e24\u7247\uff0c\u773c\u89c1\u4e0d\u80fd\u6d3b\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u53c8\u60ca\u53c8\u6012\uff0c\u624b\u4e2d\u5251\u4e00\u63d0\u4e00\u7ffb\uff0c\u5530\u5530\u4e24\u5251\uff0c\u4e00\u62db\u201c\u6d41\u661f\u98de\u9a76\u201d\uff0c\u4e00\u62db\u201c\u91ce\u9a6c\u64cd \u7530\u201d\uff0c\u4e0a\u523a\u53cc\u76ee\uff0c\u4e0b\u523a\u4e39\u7530\uff0c\u5251\u52bf\u5982\u8679\uff0c\u9510\u4e0d\u53ef\u5f53\uff0c\u897f\u95e8\u9738\u62a1\u97ad\u6025\u4e0a\uff0c\u52bf\u631f\u98ce\u96f7\uff0c\u970d\u5730 \u5411\u4ed6\u7684\u4e0b\u4e09\u8def\u626b\u53bb\uff0c\u5929\u6076\u9053\u4eba\u89c1\u4ed6\u4eec\u5f62\u540c\u62fc\u547d\uff0c\u4e0d\u6562\u8f7b\u654c\uff0c\u4f7f\u4e86\u4e00\u62db\u201c\u4e91\u6a2a\u79e6\u5cad\u201d\uff0c\u5c18 \u5c3e\u6563\u5f00\uff0c\u4e07\u6402\u65e0\u7ec3\uff0c\u5b9b\u5982\u5728\u9762\u524d\u5e03\u4e0b\u4e86\u4e00\u5c42\u94c1\u7f51\u3002\u5929\u6076\u9053\u4eba\u6b63\u5728\u4ee5\u4e0a\u4e58\u7684\u6b66\u529f\u9632\u5fa1\uff0c\u5ffd \u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u9762\u524d\u65d7\u5f71\u4e00\u95ea\uff0c\u90a3\u5343\u7ecf\u4e07\u7f15\u7684\u62c2\u5c18\uff0c\u7adf\u88ab\u5377\u5f00\u4e86\u4e00\u89d2\u7a7a\u9699\uff0c\u6b66\u7384\u971c\u4e00\u62db \u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u7acb\u523b\u4e58\u865a\u800c\u5165\uff0c\u4f46\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u9976\u662f\u5929\u6076\u9053\u4eba\u95ea\u907f\u5f97\u5feb\uff0c\u957f\u8896\u4ea6 \u5df2\u88ab\u5272\u53bb\u4e86\u4e00\u622a\u3002 \u3000\u3000\u539f\u6765\u79e6\u582a\u7684\u6b66\u529f\u867d\u7136\u7a0d\u900a\u4e8e\u897f\u95e8\u9738\uff0c\u4f46\u5374\u6700\u4e3a\u673a\u667a\uff0c\u4ed6\u662f\u4e58\u7740\u5929\u6076\u9053\u4eba\u5168\u529b\u9632\u5fa1\u4e4b \u9645\uff0c\u7a81\u65bd\u6740\u624b\u7684\u3002\u4ed6\u7684\u65d7\u5b50\u662f\u767e\u91d1\u7ec6\u4e1d\u7ec7\u6210\uff0c\u6070\u6070\u662f\u62c2\u5c18\u4e4b\u7c7b\u201c\u8f6f\u5175\u5668\u201d\u7684\u514b\u661f\uff0c\u5728\u5175 \u5668\u4e0a\u5148\u5360\u4e86\u4fbf\u5b9c\u3002 \u3000\u3000\u4e09\u540d\u9ad8\u624b\uff0c\u8054\u624b\u56f4\u653b\uff0c\u4f46\u89c1\u7075\u65d7\u62db\u5c55\uff0c\u97ad\u5f71\u7ffb\u98de\uff0c\u5251\u6c14\u5982\u8679\uff0c\u53f1\u54a4\u5c71\u6447\uff0c\u7802\u98de\u77f3\u8d70\uff0c \u5929\u6076\u9053\u4eba\u7684\u90a3\u67c4\u62c2\u5c18\uff0c\u7adf\u7136\u6e10\u6e10\u88ab\u8feb\u5f97\u65bd\u5c55\u4e0d\u5f00\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u53c8\u6709\u4e00\u6761\u9ed1\u5f71\uff0c\u75be\u5954 \u800c\u6765\uff0c\u8fdc\u8fdc\u7684\u5c31\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7384\u971c\u59d0\u59d0\u4f60\u5728\u548c\u8c01\u4ea4\u624b\u5440\uff1f\u201d\u58f0\u97f3\u6025\u4fc3\u800c\u53c8\u98a4\u6296\uff0c\u6b63\u662f\u4e0a \u5b98\u5a49\u513f\u7684\u53eb\u58f0\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u4e0d\u89c1\u4ed6\u7684\u4e24\u4e2a\u5f92\u5f1f\u4e0a\u6765\uff0c\u5df2\u81ea\u6709\u4e9b\u7591\u4ff1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5bab\u4e2d\u7adf\u6709\u8fd9 \u8bb8\u591a\u9ad8\u624b\uff0c\u6211\u518d\u4e0d\u8d70\uff0c\u53ea\u6015\u4f1a\u8981\u5403\u4e8f\uff01\u201d\u62c2\u5c18\u4e00\u5c55\uff0c\u500f\u7684\u5148\u5411\u6b66\u7384\u971c\u653b\u51fb\u3002\u6b66\u7384\u971c\u4fa7\u8eab \u95ea\u907f\uff0c\u4e3e\u5251\u4e00\u6321\u5929\u6076\u9053\u4eba\u8868\u9762\u4f6f\u653b\uff0c\u5b9e\u662f\u8d70\u52bf\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u7acb\u5373\u7fe9\u7136\u63a0\u51fa\uff0c\u5230\u4e86\u79e6\u582a\u8eab \u65c1\uff0c\u94c1\u62c2\u5c18\u6296\u5f97\u7b14\u76f4\uff0c\u659c\u70b9\u79e6\u582a\u7684\u5173\u5143\u7a74\u3002\u79e6\u582a\u970d\u5730\u6643\u8eab\uff0c\u7528\u4e86\u4e00\u62db\u201c\u62c2\u4e91\u770b\u6708\u201d\uff0c\u7075 \u65d7\u62e6\u811a\u626b\u53bb\uff0c\u5929\u6076\u9053\u4eba\u4e00\u4e2a\u201c\u65f1\u5730\u62d4\u8471\u201d\uff0c\u51ed\u7a7a\u8dc3\u8d77\u6570\u4e08\uff0c\u79e6\u582a\u7684\u65d7\u5b50\u5728\u4ed6\u811a\u4e0b\u63a0\u8fc7\uff0c \u5377\u4e86\u4e2a\u7a7a\uff0c\u7b2c\u4e8c\u62db\u672a\u66fe\u53d1\u51fa\uff0c\u53ea\u89c1\u5929\u6076\u9053\u4eba\u7fe9\u5982\u98de\u9e1f\uff0c\u5728\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u94c1\u62c2\u5c18\u5df2\u662f\u5411 \u897f\u95e8\u9738\u7f69\u4e0b\uff0c\u4f46\u542c\u5f97\u811a\u7684\u4e00\u58f0\uff0c\u5929\u6076\u9053\u4eba\u7684\u62c2\u5c18\u642d\u7740\u97ad\u68a2\uff0c\u501f\u52bf\u62e7\u8eab\uff0c\u6d41\u661f\u6b92\u77f3\u4e00\u822c\u3002 \u843d\u4e0b\u5c71\u5761\u53bb\u4e86\u3002\u4ed6\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u8fde\u7528\u4e09\u79cd\u8eab\u6cd5\uff0c\u4e09\u8bb0\u7edd\u62db\uff0c\u88ad\u51fb\u4e09\u540d\u9ad8\u624b\uff0c\u800c\u4e14\u80fd\u591f\u51b2 \u51fa\u91cd\u56f4\uff0c\u6b66\u529f\u4e4b\u5f3a\u786e\u662f\u4ee4\u4eba\u548b\u820c\u3002 \u3000\u3000\u897f\u95e8\u9738\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6211\u820d\u4e86\u4e00\u6761\u624b\u81c2\uff0c\u4e5f\u7ec8\u987b\u6253\u4e86\u4f60\u4e00\u97ad\uff01\u201d\u7b11\u58f0\u60e8\u5389\u4e4b\u6781\uff0c\u6b66\u7384 \u971c\u9a87\u7136\u60ca\u89c6\uff0c\u53ea\u89c1\u4ed6\u7684\u4e00\u6761\u624b\u81c2\uff0c\u81ea\u81c2\u5f2f\u4ee5\u4e0b\u7684\u534a\u622a\uff0c\u6f06\u9ed1\u5982\u70ad\uff0c\u79e6\u582a\u8fd8\u6765\u4e0d\u53ca\u963b\u62e6\uff0c \u4ed6\u55d6\u7684\u62d4\u51fa\u4f69\u5200\uff0c\u4fbf\u5c06\u8fd9\u534a\u6761\u624b\u81c2\u65a9\u65ad\u4e86\u3002\u539f\u6765\u4ed6\u4e2d\u4e86\u5929\u6076\u9053\u4eba\u7684\u6bd2\u638c\uff0c\u4ed7\u7740\u7cbe\u7eaf\u7684\u5185 \u529f\uff0c\u4f8d\u6bd2\u6c14\u90fd\u8feb\u5230\u5de6\u638c\u638c\u5fc3\uff0c\u53ef\u662f\u521a\u624d\u4e00\u573a\u6076\u6218\uff0c\u4ed6\u771f\u529b\u8017\u635f\u4e0d\u5c11\uff01\u6bd2\u6c14\u53c8\u6e10\u6e10\u4e0a\u5347\uff0c \u4ed6\u81ea\u77e5\u6076\u6218\u4e4b\u540e\uff0c\u65e0\u6cd5\u8fd0\u529f\uff0c\u800c\u5929\u6076\u9053\u4eba\u7684\u6bd2\u638c\uff0c\u53c8\u65e0\u836f\u53ef\u6cbb\uff0c\u662f\u4ee5\u65a9\u65ad\u624b\u81c2\u3002\u4fdd\u5168\u6027 \u547d\u3002 \u3000\u3000\u8fd9\u65f6\u4e0a\u5b98\u5a49\u513f\u521a\u5230\uff0c\u89c1\u72b6\u60ca\u9a8f\u4e4b\u6781\uff0c\u897f\u95e8\u9738\u5355\u81c2\u62b1\u8d77\u4e86\u5f20\u633a\u7684\u5c38\u8eab\uff0c\u60e8\u7b11\u9053\uff1a\u201c\u6b66 \u59d1\u5a18\uff0c\u6211\u8fd9\u5144\u5f1f\u4e4b\u4ec7\uff0c\u4eca\u540e\u53ea\u6709\u671b\u4f60\u62a5\u4e86\u3002\u79e6\u582a\uff0c\u4f60\u966a\u6b66\u59d1\u5a18\u518d\u4e0b\u53bb\u641c\u67e5\u5427\u3002\u201d\u6b66\u7384\u971c \u9053\uff1a\u201c\u4f60\u653e\u5fc3\u56de\u53bb\u51cb\u6cbb\u5427\uff0c\u8fd9\u4ec7\u6211\u62a5\u4e0d\u4e86\u4e5f\u603b\u4f1a\u6709\u4eba\u66ff\u4f60\u62a5\u7684\u3002\u201d\u897f\u95e8\u9738\u9053\uff1a\u201c\u4ee4\u5e08\u82e5 \u80af\u51fa\u6765\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u4e0d\u653e\u5fc3\u7684\u3002\u201d\u62b1\u7740\u5f20\u633a\u7684\u5c38\u9ab8\uff0c\u8fc8\u5f00\u5927\u6b65\uff0c\u4fbf\u5148\u56de\u53bb\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9762\u8272\u60e8\u767d\uff0c\u5446\u5446\u7684\u671b\u7740\u897f\u95e8\u9738\u7684\u80cc\u5f71\uff0c\u6b66\u7384\u971c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u5df2\u8d70\u4e86\uff0c\u5f20 \u633a\u4e0d\u662f\u4ed6\u6740\u7684\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u677e\u4e86\u53e3\u6c14\uff0c\u7acb\u5373\u53c8\u95ee\u9053\uff1a\u201c\u4ed6\u8d70\u4e86\u4e48\uff1f\u4f60\u6ca1\u6709\u8ffd\u4e0a\u4ed6\uff1f\u4ed6\u53ef \u8fd8\u6709\u4ec0\u4e48\u8bdd\u513f\u7559\u4e0b\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u8d70\u5f97\u672b\u8fdc\uff0c\u6211\u4eec\u5230\u4e0b\u9762\u770b\u770b\u5427\u3002\u201d\u5979\u6015\u5a49\u513f \u4f24\u5fc3\uff0c\u4e0d\u6562\u5c06\u674e\u9038\u8df3\u5d16\u7684\u4e8b\u60c5\u544a\u8bc9\u3002\u4f46\u4e0a\u5b98\u5a49\u513f\u4f55\u7b49\u806a\u660e\uff0c\u4ece\u5979\u7684\u795e\u8272\u548c\u58f0\u8c03\u4e2d\u5df2\u9690\u9690 \u611f\u5230\u4e00\u79cd\u51f6\u5146\u3002\u5fc3\u5934\u4e03\u4e0a\u516b\u843d\uff0c\u4e0d\u6562\u518d\u95ee\uff0c\u9ed8\u9ed8\u65e0\u8a00\u7684\u8ddf\u5728\u6b66\u7384\u971c\u540e\u9762\uff0c\u5411\u5c71\u8c37\u4e0b\u9762\u641c \u67e5\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u98d8\u96f6\u7434\u5251\u6cea\u75d5\u591a \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u5341\u56db\u56de\u3000\u98d8\u96f6\u7434\u5251\u6cea\u75d5\u591a \u3000\u3000\u8c37\u6df1\u82d4\u6ed1\uff0c\u5a49\u513f\u6025\u6b65\u524d\u884c\uff0c\u597d\u51e0\u6b21\u9669\u4e9b\u6ed1\u5012\uff0c\u6b66\u7384\u971c\u4f38\u51fa\u624b\u53bb\uff0c\u8f7b\u8f7b\u6276\u7740\u5979\u8d70\uff0c\u6084 \u58f0\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u5b9a\u4e00\u5b9a\u795e\u3002\u201d\u8981\u77e5\u5a49\u513f\u8f7b\u529f\u672c\u6765\u4e0d\u5f31\uff0c\u53ea\u56e0\u5fc3\u4e2d\u614c\u4e71\uff0c\u6c14\u6563\u795e\u6447\uff0c \u811a\u6b65\u4e5f\u5c31\u98d8\u6d6e\u4e0d\u7a33\u4e86\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u95fb\u5f97\u6709\u4e00\u80a1\u8840\u8165\u7684\u6c14\u5473\u6251\u9f3b\u800c\u6765\uff0c\u79e6\u582a\u53eb\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e00\u5177\u6b7b\u5c38\uff01\u201d \u4e0a\u5b98\u5a49\u513f\u597d\u50cf\u5934\u9876\u4e0a\u6253\u4e86\u4e00\u4e2a\u7126\u96f7\uff0c\u9707\u5f97\u4e94\u85cf\u516d\u8151\u4e00\u9f50\u7ffb\u8f6c\uff0c\u6b66\u7384\u971c\u7d27\u7d27\u62b1\u7740\u5979\uff0c\u542c\u5f97 \u79e6\u582a\u53c8\u56b7\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u662f\u4e00\u4e2a\u62ab\u53d1\u5934\u9640\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5b9a\u4e00\u5b9a\u795e\uff0c\u53ea\u89c1\u6cf0\u582a\u5df2\u4eae\u8d77\u706b\u628a\uff0c\u6b66\u7384\u971c\u5b9a\u775b\u4e00\u770b\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u6076 \u884c\u8005\u3002\u201d\u4fef\u8170\u5bdf\u89c6\uff0c\u4f46\u89c1\u6076\u884c\u8005\u8eab\u4e0a\u4e2d\u4e86\u4e94\u516d\u5904\u5251\u4f24\uff0c\u5747\u975e\u8981\u5bb3\uff0c\u53ea\u6709\u80a9\u5934\u4e0a\u7684\u4e00\u5904\u4f24 \u53e3\u9887\u6df1\uff0c\u5374\u4e0d\u4f3c\u5251\u4f24\uff0c\u4f24\u53e3\u8fb9\u6709\u51e0\u9053\u9f7f\u5370\uff0c\u7adf\u4f3c\u662f\u7ed9\u4eba\u54ac\u4f24\u7684\u3002\u6b66\u7384\u971c\u5927\u4e3a\u5947\u602a\uff0c\u5fc3\u9053\uff1a \u201c\u82e5\u662f\u9ad8\u624b\u6bd4\u6597\uff0c\u65ad\u6ca1\u6709\u7528\u53e3\u54ac\u4eba\u7684\u9053\u7406\uff0c\u90a3\u662f\u8c01\u5c06\u6076\u884c\u8005\u6740\u4e86\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u6076\u884c\u8005\u548c\u6bd2\u89c2\u97f3\u51fa\u5165\u76f8\u8c10\uff0c\u7559\u5fc3\u6bd2\u89c2\u97f3\u53d7\u4f24\u672a\u6b7b\uff0c\u85cf\u533f\u6697\u5904\uff0c\u5979\u7684 \u900f\u9aa8\u7a74\u9488\u65e0\u5f71\u65e0\u8e2a\u3002\u201d\u79e6\u582a\u6325\u821e\u65d7\u5b50\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u5411\u524d\u641c\u67e5\uff0c\u8d70\u4e0d\u591a\u8fdc\uff0c\u53c8\u53d1\u89c9\u4e86\u4e00\u5177 \u5c38\u4f53\uff0c\u79e6\u582a\u56b7\u9053\uff1a\u201c\u53c8\u662f\u4e00\u4e2a\u7537\u7684\uff0c\u662f\u4e00\u4e2a\u8eab\u6750\u7c97\u58ee\u7684\u5c11\u5e74\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u4e00\u60f3\uff0c\u674e\u9038\u662f\u4e2a\u6587\u5f31\u4e66\u751f\uff0c\u8eab\u6750\u5e76\u4e0d\u7c97\u58ee\uff0c\u521a\u521a\u677e\u4e86\u53e3\u6c14\uff0c\u5ffd\u542c\u5f97\u6b66\u7384\u971c \u56b7\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u5feb\u6765\u770b\uff0c\u4ed6\uff0c\u4ed6\uff0c\u4ed6\u662f\u4e0d\u662f\u53eb\u505a\u957f\u5b59\u6cf0\u7684\u90a3\u4e2a\u5c11\u5e74\uff1f\u201d \u3000\u3000\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\uff0c\u4e0a\u5b98\u5a49\u513f\u4e00\u77a7\u4e4b\u4e0b\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u8fd9\u5c38\u4f53\u4ef0\u9762\u671d\u5929\uff0c\u6d53\u7709 \u5927\u773c\uff0c\u6b63\u662f\u4e0e\u5979\u4e00\u540c\u957f\u5927\uff0c\u60c5\u5982\u5144\u59b9\u7684\u957f\u5b59\u6cf0\u3002\u4e0a\u5b98\u5a49\u513f\u5c16\u53eb\u4e00\u58f0\uff0c\u597d\u534a\u664c\u54ed\u4e0d\u51fa\u6765\u3002 \u4f46\u89c1\u79e6\u582a\u628a\u8fd9\u5c11\u5e74\u6276\u8d77\uff0c\u6b66\u7384\u971c\u6495\u4e0b\u4e86\u4e00\u5e45\u8863\u895f\uff0c\u6267\u4ed6\u624b\u8155\uff0c\u9053\uff1a\u201c\u8109\u606f\u8fd8\u672a\u5b8c\u5168\u65ad\u7edd\u3002\u201d \u968f\u5373\u6495\u4e0b\u4e86\u4ed6\u7684\u4e0a\u8863\u9053\uff1a\u201c\u4e2d\u4e86\u4e24\u679a\u6bd2\u9488\uff0c\u53e6\u5916\u4e2d\u4e86\u4e00\u638c\u3002\u201d\u62d4\u51fa\u5b9d\u5251\uff0c\u523a\u5f00\u76ae\u8089\uff0c\u5c06 \u90a3\u4e24\u679a\u6bd2\u9488\u6311\u51fa\uff0c\u957f\u5b59\u6cf0\u7adf\u4f3c\u6beb\u65e0\u77e5\u89c9\uff0c\u54fc\u4e5f\u4e0d\u54fc\u4e00\u58f0\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u6551\u4e48\uff1f\u201d\u6b66\u7384\u971c\u91cd\u91cd\u7684\u5728\u4ed6\u8170\u80c1\u4e0a\u4e00\u6233\uff0c\u6240\u70b9\u7684\u90e8\u4f4d\u4e43 \u662f\u4efb\u7763\u4e8c\u8109\u4ea4\u4f1a\u7684\u201c\u8840\u6d77\u7a74\u201d\uff0c\u5373\u7b97\u53d7\u4e86\u5f88\u91cd\u7684\u5185\u4f24\uff0c\u8fd9\u4e00\u6233\u4e5f\u80fd\u6682\u65f6\u5316\u5f00\u7600\u8840\u3002\u957f\u5b59 \u6cf0\u5589\u5934\u54af\u54af\u4f5c\u54cd\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u4e00\u53e3\u5e26\u7740\u7600\u8840\u7684\u6d53\u75f0\uff0c\u53cc\u773c\u5fae\u5f20\uff0c\u89c1\u5230\u4e0a\u5b98\u5a49 \u513f\u5728\u4ed6\u9762\u524d\u3002\u7709\u6bdb\u4e00\u52a8\uff0c\u5e26\u7740\u4e00\u4e1d\u7b11\u610f\uff0c\u968f\u5373\u773c\u775b\u53c8\u55d1\u4e0a\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u79e6\u582a\u3002\u4f60\u628a\u4ed6\u5e26\u56de\u5b98\u53bb\uff0c\u5feb\u8bf7\u592a\u533b\u8bca\u89c6\u3002\u201d\u8981\u77e5\u957f\u5b59\u6cf0\u7684\u5185\u529f\u8fdc\u8fdc\u4e0d \u53ca\u674e\u9038\uff0c\u674e\u9038\u4ee5\u524d\u4e2d\u4e86\u6bd2\u9488\uff0c\u6b66\u7384\u971c\u53ef\u4ee5\u5e26\u4ed6\u5230\u909b\u5d03\u5c71\u6c42\u590f\u4faf\u575a\u533b\u6cbb\uff0c\u957f\u5b59\u6cf0\u7edd\u4e0d\u80fd\u652f \u6301\u8fd9\u8bb8\u591a\u65f6\u65e5\uff0c\u4f55\u51b5\u4ece\u957f\u5b89\u5230\u909b\u5d03\u5c71\u4e5f\u8981\u6bd4\u4ee5\u524d\u674e\u9038\u6240\u8d70\u7684\u8def\u7a0b\u8fdc\u5f97\u591a\u3002\u4e0a\u5b98\u5a49\u513f\u6df1\u77e5 \u6bd2\u89c2\u97f3\u7684\u6bd2\u9488\u5386\u5bb3\uff0c\u5982\u4eca\u5c06\u957f\u5b59\u6cf0\u59d4\u4e4b\u592a\u533b\uff0c\u90a3\u53ea\u662f\u6ca1\u529e\u6cd5\u4e2d\u7684\u529e\u6cd5\uff0c\u53ea\u6709\u542c\u5929\u7531\u547d\u4e86\u3002 \u3000\u3000\u79e6\u582a\u80cc\u8d77\u4e86\u957f\u5b59\u6cf0\uff0c\u8d70\u4e0a\u5c71\u5761\uff0c\u5a49\u513f\u76ee\u9001\u4ed6\u7684\u80cc\u5f71\u5728\u6811\u6728\u4e1b\u4e2d\u6d88\u5931\uff0c\u60f3\u8d77\u957f\u5b59\u5747\u91cf \u7684\u6df1\u6069\uff0c\u60f3\u8d77\u4ed6\u4eec\u5144\u59b9\u7684\u60c5\u8c0a\uff0c\u4e0d\u7981\u6ce3\u7136\u6cea\u4e0b\u3002\u968f\u5373\u60f3\u5230\uff1a\u201c\u6076\u884c\u8005\u7684\u5c38\u4f53\u65e2\u7136\u5728\u8fd9\u91cc \u53d1\u73b0\uff0c\u6cf0\u54e5\u4e2d\u7684\u53c8\u662f\u6bd2\u89c2\u97f3\u7684\u900f\u7a74\u795e\u9488\uff0c\u90a3\u4e48\u674e\u9038\u54e5\u60f3\u5fc5\u4e5f\u4f1a\u78b0\u4e0a\u8fd9\u4e24\u4e2a\u9b54\u5934\u4e86\u3002\u201d\u5fc3 \u5934\u6253\u4e86\u4e00\u4e2a\u5bd2\u51db\uff0c\u53ea\u6015\u51f6\u591a\u5409\u5c11\u3002 \u3000\u3000\u6b66\u7384\u971c\u548c\u5979\u7ee7\u7eed\u641c\u5bfb\uff0c\u76f4\u5230\u65e5\u4e0a\u4e09\u7aff\uff0c\u641c\u904d\u4e86\u6574\u4e2a\u5c71\u8c37\uff0c\u5140\u662f\u4e0d\u89c1\u674e\u9038\u7684\u5f71\u5b50\uff0c\u6b66 \u7384\u971c\u9893\u7136\u8bf4\u9053\uff1a\u201c\u627e\u4e0d\u89c1\u4e86\uff0c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u51fa\u4ec0\u4e48\u4e8b\u5417\uff1f\u59d0\u59d0\uff0c \u4f60\u600e\u4e48\u4f1a\u60f3\u5230\u5728\u8fd9\u5c71\u8c37\u4e4b\u4e2d\u5bfb\u4ed6\uff0c\u542c\u4ed6\u6628\u665a\u7684\u53e3\u6c14\uff0c\u4ed6\u4e0d\u662f\u8bf4\u8981\u4ece\u6b64\u8fdc\u8d70\u9ad8\u98de\uff0c\u6c38\u4e0d\u56de \u6765\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4f46\u613f\u4ed6\u8d70\u7684\u8d8a\u8fdc\u8d8a\u597d\uff01\u201d\u6b66\u7384\u971c\u6781\u529b\u6291\u5236\u4f4f\u81ea\u5df1\u7684\u4f24\u5fc3\uff0c \u4e0d\u6562\u5c06\u674e\u9038\u8df3\u5d16\u7684\u4e8b\u60c5\u544a\u8bc9\u5a49\u513f\uff0c\u4e0d\u613f\u52a0\u91cd\u5979\u5fc3\u5934\u7684\u75db\u82e6\u3002\u5979\u73b0\u5728\u53ea\u6709\u4e00\u4e2a\u5e0c\u671b\uff0c\u5e0c\u671b \u674e\u9038\u88ab\u4eba\u6551\u8d70\uff0c\u7136\u800c\u5728\u767e\u4e08\u9ad8\u5d16\u8df3\u4e0b\uff0c\u4e0d\u6b7b\u4ea6\u53d7\u91cd\u4f24\uff0c\u96be\u9053\u771f\u6709\u90a3\u4e48\u5de7\u6cd5\uff0c\u521a\u521a\u7ed9\u4eba\u63a5 \u7740\uff1f\u8fd9\u5e0c\u671b\u4e5f\u672a\u514d\u592a\u6e3a\u832b\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u610f\u6599\u4e0d\u5230\uff0c\u5f53\u771f\u5c31\u6709\u90a3\u4e48\u5de7\u6cd5\uff0c\u8fd9\u5012\u4e0d\u662f\u674e\u9038\u8df3\u4e0b\u4e4b\u65f6\uff0c\u521a\u597d\u7ed9\u4eba\u63a5\u7740\uff0c\u800c \u662f\u88ab\u5ca9\u77f3\u4e2d\u6a2a\u751f\u51fa\u6765\u7684\u866c\u677e\u6321\u4e86\u4e00\u4e0b\uff0c\u4e60\u6b66\u4e4b\u4eba\uff0c\u9aa4\u7136\u9047\u4e0a\u5371\u9669\uff0c\u6323\u624e\u4e43\u662f\u51fa\u4e8e\u672c\u80fd\uff0c \u4ed6\u89e6\u7740\u677e\u6811\uff0c\u6df1\u539a\u7684\u5185\u529f\u81ea\u7136\u800c\u7136\u7684\u88ab\u6fc0\u53d1\u51fa\u6765\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u5c31\u8fd9\u6837 \u7684\u7f13\u548c\u4e86\u4ed6\u4e0b\u5760\u4e4b\u52bf\u3002\u4e0d\u8fc7\uff0c\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u6454\u843d\u5730\u65f6\uff0c\u4e5f\u88ab\u90a3\u9ad8\u7a7a\u8dcc\u4e0b\u7684\u9707\u8361\u4e4b\u529b\uff0c\u9707 \u5f97\u660f\u8ff7\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u660f\u8ff7\uff0c\u5c31\u662f\u6574\u6574\u7684\u4e00\u5929\uff0c\u674e\u9038\u672c\u8eab\u5f53\u7136\u5e76\u4e0d\u77e5\u9053\u3002\u4ed6\u597d\u50cf\u505a\u4e86\u4e00\u4e2a\u6076\u68a6\uff0c\u4e5f\u4e0d \u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u4f3c\u4e4e\u542c\u5230\u6709\u4eba\u5728\u4ed6\u8eab\u8fb9\u53f9\u606f\uff0c\u5b9a\u4e00\u5b9a\u795e\uff0c\u53c8\u542c\u5230\u8f66\u8f6e\u8f98\u8f98\u4e4b\u58f0\uff0c \u8eab\u5b50\u4e5f\u4f3c\u968f\u7740\u8f66\u8f6e\u8d77\u4f0f\u3002\u6b66\u7384\u971c\u4ee5\u524d\u6551\u4ed6\u7684\u60c5\u666f\u500f\u5730\u6d6e\u5728\u5fc3\u5934\uff0c\u4e5f\u662f\u5728\u9aa1\u8f66\u4e4b\u4e0a\uff0c\u773c\u524d \u540c\u6837\u6709\u4e00\u4e2a\u5c11\u5973\u7684\u5f71\u5b50\uff0c\u674e\u9038\u5c1a\u672a\u5b8c\u5168\u6e05\u9192\uff0c\u5c31\u4e0d\u7981\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7384\u971c\uff0c\u7384\u971c\uff01\u201d\u9aa4\u7136 \u95f4\uff0c\u53d1\u73b0\u90a3\u5c11\u5973\u7684\u8138\u578b\u4e0d\u4f3c\u7384\u971c\uff0c\u4ed6\u53cc\u773c\u4e00\u5f20\uff0c\u8f6c\u53e3\u53eb\u9053\uff1a\u201c\u5a49\u513f\uff0c\u5a49\u513f\uff01\u201d\u5728\u674e\u9038\u7684 \u5fc3\u76ee\u4e4b\u4e2d\uff0c\u4ee5\u4e3a\u6551\u4ed6\u7684\u4eba\u82e5\u7136\u4e0d\u662f\u6b66\u7384\u971c\uff0c\u5c31\u5fc5\u5b9a\u662f\u4e0a\u5b98\u5a49\u513f\u65e0\u7591\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u3002\u674e\u9038\u4f46\u89c9\u4e00\u9897\u51b0\u51b7\u7684\u6cea\u73e0\u6ef4\u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c\u53cc\u773c\u5927\u5f20\uff0c\u8fd9 \u65f6\u624d\u770b\u4e2a\u6e05\u695a\uff0c\u539f\u6765\u773c\u524d\u7684\u5c11\u5973\uff0c\u65e2\u4e0d\u662f\u6b66\u7384\u971c\uff0c\u4e5f\u4e0d\u662f\u4e0a\u5b98\u5a49\u513f\uff0c\u800c\u662f\u957f\u5b59\u58c1\u3002\u4f46\u542c \u5f97\u957f\u5b59\u58c1\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u9038\u54e5\uff0c\u4f60\u4ecd\u662f\u8fd9\u6837\u7684\u60f3\u5ff5\u5979\u4eec\u5417\uff1f\u201d\u968f\u5373\u4f38\u51fa\u4e00\u53ea\u8f6f\u7ef5\u7684\u624b\u638c\u63e1 \u7740\u4ed6\u7684\u624b\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u9192\u9192\u5427\uff0c\u55ef\uff0c\u8fd8\u597d\uff0c\u8fd8\u597d\u4f60\u6ca1\u6709\u53d7\u5230\u91cd\u4f24\u3002\u201d \u3000\u3000\u674e\u9038\u53c8\u60ca\u53c8\u559c\uff0c\u65e2\u60f6\u60d1\uff0c\u4ea6\u60ed\u6127\uff0c\u970d\u5730\u5750\u4e86\u8d77\u6765\uff0c\u95ee\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u4f60\u662f\u600e\u7684\u4e86\uff1f\u201d \u773c\u5149\u4e00\u77a5\uff0c\u4f46\u89c1\u957f\u5b59\u58c1\u989c\u5bb9\u60ec\u6062\uff0c\u8138\u4e0a\u6cea\u75d5\u672b\u5e72\uff0c\u597d\u50cf\u521a\u521a\u7ecf\u8fc7\u4e86\u4e00\u573a\u6781\u4f24\u75db\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u957f\u5b59\u58c1\u63ed\u5f00\u4e86\u524d\u9762\u7684\u8f66\u5e18\uff0c\u54bd\u7740\u773c\u6cea\u8bf4\u9053\uff1a\u201c\u6211\u662f\u548c\u7239\u7239\u6765\u7684\u3002\u201d\u8f66\u5e18\u524d\u5ea7\u4e00\u4e2a\u8001 \u4eba\u56de\u8fc7\u5934\u6765\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\u8fd8\u8ba4\u5f97\u8001\u81e3\u5417\uff1f\u201d\u7b11\u4e2d\u5e26\u6cea\uff0c\u542b\u7740\u65e0\u9650\u51c4\u51c9\uff0c\u8fd9\u8001\u4eba\u6b63 \u662f\u957f\u5b59\u58c1\u7684\u7236\u4eb2\u957f\u5b59\u5747\u91cf\u3002 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u80fd\u89c1\u5230\u4f2f\u4f2f\uff0c\u591a\u8c22\u4f2f\u4f2f\u6551\u547d\u4e4b\u6069\uff0c\u6055\u5c0f\u4f84\u5728\u8f66\u4e0a\u4e0d\u80fd\u884c\u793c\u4e86\u3002\u201d \u4ed6\u751f\u8fd8\u4e4b\u540e\uff0c\u7b2c\u4e00\u4e2a\u4fbf\u89c1\u5230\u5927\u5510\u7684\u5fe0\u81e3\uff0c\u5f53\u771f\u662f\u6bd4\u89c1\u5230\u4eb2\u4eba\u8fd8\u8981\u6b22\u559c\u3002\u5ffd\u5730\u60f3\u8d77\u662f\u957f\u5b59 \u5747\u91cf\u5728\u590f\u4faf\u575a\u5904\u7597\u4f24\uff0c\u60f3\u6765\u6b66\u529f\u672a\u6062\u590d\uff0c\u5374\u600e\u7684\u5192\u9669\u5165\u4eac\uff0c\u800c\u4e14\u8fd8\u5c06\u81ea\u5df1\u6551\u4e86\u3002\u6b63\u6b32\u53d1 \u95ee\uff0c\u957f\u5b59\u5747\u91cf\u90a3\u98a4\u6296\u7684\u58f0\u97f3\u5df2\u6025\u7740\u95ee\u9053\uff1a\u201c\u4f60\u89c1\u5230\u4e86\u5a49\u513f\u5417\uff1f\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u5267\u75db\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u89c1\u5230\u5566\u3002\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u5979\u5728\u5bab\u4e2d\u505a\u4ec0\u4e48\uff1f\u201d\u674e\u9038 \u9053\uff1a\u201c\u5728\u5bab\u4e2d\u66ff\u6b66\u5219\u5929\u8349\u62df\u6587\u544a\uff0c\u966a\u5979\u505a\u505a\u8bd7\uff0c\u5199\u5199\u4e66\u3002\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5a49 \u513f\u771f\u7684\u505a\u4e86\u6b66\u5219\u5929\u7684\u5973\u5b98\u4e86\uff1f\u5979\u5fd8\u8bb0\u4e86\u5979\u7684\u7956\u7236\u3001\u5979\u7684\u7236\u4eb2\u3001\u751a\u81f3\u5979\u8fd8\u5728\u751f\u7684\u6bcd\u4eb2\u4e86\uff1f\u201d \u674e\u9038\u9053\uff1a\u201c\u6211\u770b\u5979\u628a\u4ec0\u4e48\u4eba\u90fd\u5fd8\u8bb0\u5566\uff01\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u4f60\u89c1\u5230\u5979\u65f6\uff0c\u5979\u6b63\u5728\u505a\u4ec0\u4e48\uff1f\u201d \u674e\u9038\u9053\uff1a\u201c\u5979\u6b63\u5728\u8bfb\u9a86\u5bbe\u738b\u90a3\u7bc7\u8ba8\u6b66\u6c0f\u6a84\u6587\u3002\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u8bfb\u7ed9\u8c01\u542c\uff1f\u201d\u674e\u9038\u9053\uff1a \u201c\u8bfb\u7ed9\u6b66\u5219\u5929\u542c\u3002\u201d\u957f\u5b59\u58c1\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u674e\u9038\u9053\uff1a\u201c\u662f\u6b66\u5219\u5929\u81ea\u5df1\u53eb\u5979\u8bfb\u7684\u3002\u201d\u957f\u5b59 \u5747\u91cf\u7a81\u7136\u7eb5\u58f0\u5927\u7b11\uff0c\u597d\u50cf\u8981\u628a\u80f8\u4e2d\u7684\u90c1\u79ef\u90fd\u6563\u53d1\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u5979\u5c45\u7136\u6709\u80c6\u91cf \u8bfb\uff0c\u6b66\u5219\u5929\u4e5f\u5c45\u7136\u6709\u80c6\u91cf\u542c\uff01\u5979\u542c\u4e86\u600e\u4e48\u6837\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6b66\u5219\u5929\u542c\u4e86\u6ee1\u4e0d\u5728\u4e4e\u3002\u201d\u957f \u5b59\u5747\u91cf\u8be7\u9053\uff1a\u201c\u6ee1\u4e0d\u5728\u4e4e\uff1f\u5979\u8bf4\u4e86\u4ec0\u4e48\u6ca1\u6709\uff1f\u201d\u8981\u77e5\u9a86\u5bbe\u738b\u5f53\u65f6\u5199\u4e86\u8fd9\u7bc7\u6a84\u6587\uff0c\u7acb\u5373\u4f17 \u53e3\u4f20\u8bf5\uff0c\u5510\u671d\u7684\u65e7\u81e3\uff0c\u548c\u4e00\u4e9b\u53cd\u5bf9\u6b66\u5219\u5929\u7684\u58eb\u5927\u592b\uff0c\u4eba\u4eba\u542c\u4e86\u90fd\u662f\u7709\u98de\u8272\u821e\uff0c\u611f\u5230\u75db\u5feb \u7433\u6f13\u3002\u4f9d\u957f\u5b59\u5747\u91cf\u60f3\u6765\uff0c\u6b66\u5219\u5929\u542c\u4e86\u6700\u5c11\u4e5f\u5f97\u6c14\u4e2a\u534a\u6b7b\uff0c\u5c82\u77e5\u5979\u5374\u6ee1\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u5979\u542c\u4e86\u4e4b\u540e\uff0c\u7b2c\u4e00\u53e5\u8bdd\u5c31\u662f\u8d23\u5907\u5bb0\u76f8\u4e0d\u5584\u7528\u4eba\u3002\u201d\u957f\u5b59\u5747\u91cf\u70b9\u70b9\u5934\u9053\uff1a \u201c\u9a86\u5bbe\u738b\u672c\u6765\u662f\u4e2a\u4eba\u624d\u3002\u55ef\uff0c\u8fd8\u6709\u4ec0\u4e48\uff0c\u4f60\u90fd\u8bf4\u7ed9\u6211\u542c\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u5979\u8bf4\u8fd9\u662f\u4e00\u7bc7\u597d \u6587\u7ae0\uff0c\u4f46\u4e0d\u662f\u6709\u529b\u91cf\u7684\u6a84\u6587\uff0c\u5979\u5c06\u8fd9\u7bc7\u6a84\u6587\u9a73\u5f97\u4f53\u65e0\u5b8c\u80a4\u3002\u201d\u957f\u5b59\u5747\u91cf\u4e00\u8def\u542c\u4ed6\u8bf4\u4e0b\u53bb\uff0c \u7b11\u5bb9\u5c3d\u655b\uff0c\u8138\u8272\u8d8a\u6765\u8d8a\u53d8\u5f97\u82cd\u767d\uff0c\u672c\u6765\u662f\u795e\u6c14\u52c3\u52c3\u7684\uff0c\u500f\u7136\u95f4\u53d8\u5f97\u8001\u6001\u9f99\u949f\uff0c\u7a81\u7136\u63d2\u53e3 \u95ee\u9053\uff1a\u201c\u5979\u8bf4\u5f90\u656c\u4e1a\u5df2\u88ab\u5305\u56f4\uff0c\u6700\u591a\u4e0d\u51fa\u534a\u4e2a\u6708\uff0c\u5c31\u8981\u88ab\u5b8c\u5168\u6d88\u706d\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u53ea \u6015\u8fd9\u662f\u771f\u7684\u3002\u201d\u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60\u5462\uff1f\u4f60\u4eca\u540e\u600e\u4e48\u6837\uff1f\u201d\u674e\u9038\u5782\u5934\u9053\uff1a\u201c\u6211\u81ea\u5df1 \u4e5f\u4e0d\u77e5\u9053\uff0c\u6b63\u60f3\u8bf7\u4f2f\u4f2f\u6307\u70b9\u8ff7\u6d25\u3002\u201d \u3000\u3000\u957f\u5b59\u5747\u91cf\u5ffd\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\u3002\u5979\u59d4\u5b9e\u662f\u4e2a\u6781\u5389\u5bb3\u7684\u654c\u4eba\uff0c\u8001\u81e3\u4eca\u751f\uff0c \u53ea\u6015\u518d\u4e5f\u4e0d\u80fd\u89c1\u5230\u5510\u5ba4\u91cd\u5149\u4e86\u3002\u201d\u7a81\u7136\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u5a49\u513f\uff0c\u5a49\u513f\uff0c\u4f60\u597d\uff0c\u4f60\u597d\uff01\u201d\u54c7\u7684 \u4e00\u58f0\uff0c\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u767b\u65f6\u4ece\u8f66\u4e0a\u8dcc\u4e86\u4e0b\u53bb\uff01 \u3000\u3000\u8981\u77e5\u957f\u5b59\u5747\u91cf\u6700\u5927\u7684\u5fc3\u613f\u4e43\u662f\u4e2d\u5174\u5510\u5ba4\uff0c\u4ee5\u53ca\u91cd\u632f\u5bb6\u58f0\uff0c\u800c\u4eca\u4ed6\u5df2\u611f\u5230\u5b8c\u5168\u7edd\u671b\uff0c \u800c\u4e14\u66f4\u4ee4\u4ed6\u4f24\u5fc3\u7684\u662f\uff0c\u4ed6\u4e00\u624b\u629a\u517b\u5927\u7684\u4e0a\u5b98\u5a49\u513f\uff0c\u4ed6\u7231\u62a4\u5979\u80dc\u8fc7\u4eb2\u751f\uff0c\u4ed6\u6307\u671b\u5979\u53bb\u523a\u6740 \u6b66\u5219\u5929\u7684\u4e0a\u5b98\u5a49\u513f\uff0c\u5982\u4eca\u7adf\u6210\u4e86\u6b66\u5219\u5929\u7684\u4eb2\u4fe1\u3002\u75c5\u4f53\u672a\u75ca\u7684\u98ce\u70db\u6b8b\u5e74\uff0c\u600e\u7981\u5f97\u8fd9\u8bb8\u591a\u5fc3 \u7075\u6298\u78e8\uff1f\u4ed6\u4e00\u53e3\u6c14\u8f6c\u4e0d\u8fc7\u6765\uff0c\u4f7f\u5373\u5012\u5730\u4e0d\u8d77\u3002 \u3000\u3000\u674e\u9038\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u8df3\u4e0b\u8f66\u6765\uff0c\u6276\u8d77\u957f\u5b59\u5747\u91cf\uff0c\u4f46\u89c1\u4ed6\u9762\u5982\u91d1\u7eb8\uff0c\u6c14\u82e5\u6e38\u4e1d\uff0c \u65ad\u65ad\u7eed\u7eed\u7684\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u5931\u6389\u4e86\u513f\u5b50\uff0c\u53ea\u6709\u8fd9\u4e00\u4e2a\u5973\u513f\u4e86\u3002\u6bbf\u4e0b\uff0c\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u4f60 \u80af\u66ff\u6211\u7167\u987e\u5979\u4e48\uff1f\u201d\u957f\u5b59\u58c1\u773c\u6cea\u8ff8\u6d41\uff0c\u7d27\u7d27\u63e1\u7740\u5979\u7236\u4eb2\u7684\u624b\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e0d\u4f1a\u6b7b\uff0c \u4f60\u4e0d\u80fd\u6b7b\uff0c\u4f60\u517b\u597d\u4f24\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u53bb\u5bfb\u8bbf\u54e5\u54e5\u3002\u201d\u957f\u5b59\u5747\u91cf\u82e6\u7b11\u9053\uff1a\u201c\u8fd8\u80fd\u6371\u5f97\u90a3\u4e48\u957f \u7684\u65f6\u5019\u4e48\uff1f\u4f60\uff0c\u4f60\u2026\u2026\u201d\u8bdd\u58f0\u5fae\u5f31\uff0c\u7ec6\u4e0d\u53ef\u95fb\uff0c\u674e\u9038\u6d4b\u4ed6\u8109\u818a\uff0c\u5ffd\u7c97\u5ffd\u7ec6\uff0c\u5ffd\u800c\u6025\u8df3\uff0c \u5ffd\u800c\u9759\u6b62\uff0c\u674e\u9038\u867d\u7136\u4e0d\u61c2\u533b\u672f\uff0c\u4e5f\u7565\u5177\u4e00\u4e9b\u5e38\u8bc6\uff0c\u89c1\u6b64\u8109\u8c61\uff0c\u77e5\u9053\u4ed6\u4e94\u88c1\u516d\u80ba\uff0c\u5df2\u90fd\u6613 \u4f4d\uff0c\u751f\u673a\u9893\u8d25\uff0c\u7eb5\u6709\u5999\u624b\u795e\u533b\uff0c\u4e5f\u96be\u533b\u6cbb\uff0c\u66f4\u4f55\u51b5\u590f\u4faf\u575a\u79bb\u6b64\u751a\u8fdc\uff0c\u957f\u5b59\u5747\u91cf\u8fd8\u600e\u80fd\u6371 \u5f97\u8d77\u8def\u4e0a\u7684\u5954\u6ce2\uff1f \u3000\u3000\u957f\u5b59\u58c1\u4e00\u53cc\u5931\u795e\u65e0\u52a9\u7684\u773c\u775b\u8f6c\u95ee\u674e\u9038\uff0c\u597d\u50cf\u628a\u4e00\u5207\u5e0c\u671b\u5168\u5bc4\u6258\u5728\u4ed6\u8eab\u4e0a\u4e86\uff0c\u674e\u9038\u6025 \u5fd9\u65bd\u5c55\u95ed\u7a74\u624b\u6cd5\uff0c\u5148\u628a\u957f\u5b59\u5747\u91cf\u7684\u201c\u5929\u62a0\u201d\u201c\u5c06\u53f0\u201d\u201c\u7075\u5e9c\u201d\u4e09\u5904\u5927\u7a74\u5c01\u95ed\uff0c\u4f7f\u4ed6\u6682\u65f6 \u5931\u53bb\u77e5\u89c9\uff0c\u514d\u53d7\u75db\u82e6\uff0c\u5e76\u4f7f\u4ed6\u4f53\u4e2d\u6bd2\u8840\uff0c\u4e0d\u81f3\u5373\u65f6\u6ca5\u4eba\u5fc3\u623f\u3002\u957f\u5b59\u58c1\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u529e\uff1f \u8fd9\u600e\u4e48\u529e\uff1f\u5728\u8fd9\u8352\u50fb\u7684\u5730\u65b9\uff0c\u600e\u5730\u53bb\u8bf7\u4e00\u4e2a\u533b\u751f\uff1f\u201d\u674e\u9038\u5411\u524d\u9762\u4e00\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u524d\u9762\u5c71 \u9e93\uff0c\u6709\u5ea7\u5bfa\u9662\uff0c\u54b1\u4eec\u4e14\u5148\u5230\u5bfa\u4e2d\u501f\u4e00\u95f4\u9759\u5ba4\uff0c\u5c06\u4f2f\u4f2f\u5b89\u987f\u4e0b\u6765\uff0c\u5f90\u56fe\u540e\u8ba1\u3002\u201d\u957f\u5b59\u58c1\u5931 \u4e86\u4e3b\u5f20\uff0c\u4e00\u5207\u90fd\u53ea\u6709\u542c\u4ece\u674e\u9038\u7684\u4e86\u3002 \u3000\u3000\u957f\u5b59\u58c1\u5c06\u7236\u4eb2\u62b1\u5165\u8f66\u4e2d\uff0c\u8ba9\u674e\u9038\u9a7e\u4f7f\u9aa1\u8f66\uff0c\u4e00\u8def\u4e0a\u5411\u674e\u9038\u65ad\u65ad\u7eed\u7eed\u7684\u6ce3\u8bc9\uff0c\u674e\u9038\u8fd9 \u624d\u77e5\u9053\u4e8b\u60c5\u7684\u7ecf\u8fc7\uff0c\u539f\u6765\u5728\u674e\u9038\u4ece\u590f\u4faf\u575a\u5bb6\u4e2d\u51fa\u8d70\u7684\u7b2c\u4e8c\u5929\uff0c\u8c37\u795e\u7fc1\u4e0e\u957f\u5b59\u6cf0\uff0c\u5df2\u5c06\u957f \u5b59\u5747\u91cf\u63a5\u5230\uff0c\u8c37\u795e\u7fc1\u5fc3\u7070\u610f\u51b7\uff0c\u5c06\u8001\u670b\u53cb\u9001\u5230\u590f\u4faf\u575a\u5bb6\u540e\uff0c\u4fbf\u5373\u8d70\u4e86\u3002\u957f\u5b59\u5747\u91cf\u542c\u5f97\u674e \u9038\u72ec\u4e0a\u957f\u5b89\uff0c\u5927\u4e3a\u5fc3\u6025\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u8981\u4e0a\u957f\u5b89\u627e\u4ed6\uff0c\u4ed6\u7684\u7406\u7531\u662f\uff0c\u4ed6\u5728\u4eac\u4e2d\u8fd8\u6709\u4e00\u4e9b \u5b98\u5c45\u8981\u804c\u7684\u65e7\u65e5\u540c\u50da\uff0c\u82e5\u662f\u674e\u9038\u4e0d\u5e78\u88ab\u6355\uff0c\u4ed6\u4e5f\u8bb8\u8fd8\u53ef\u4ee5\u8bbe\u6cd5\u6253\u6551\u3002\u53ef\u662f\u4ed6\u7684\u6b66\u529f\u8981\u4e00 \u5e74\u4e4b\u540e\u65b9\u80fd\u6062\u590d\uff0c\u590f\u4faf\u575a\u5982\u4f55\u80af\u653e\u4ed6\u8d70\uff0c\u4e89\u8bba\u518d\u4e09\uff0c\u62d7\u4ed6\u4e0d\u8fc7\uff0c\u590f\u4faf\u575a\u53ea\u597d\u60f3\u51fa\u4e00\u4e2a\u529e \u6cd5\uff0c\u4e00\u9762\u53eb\u957f\u5b59\u6cf0\u5144\u59b9\u966a\u4ed6\u524d\u53bb\uff0c\u4e00\u9762\u7ed9\u5979\u4e00\u4ed8\u5947\u836f\uff0c\u8fd9\u836f\u4e43\u662f\u4e00\u79cd\u5f3a\u70c8\u7684\u5174\u594b\u5242\uff0c\u670d \u4e0b\u4e4b\u540e\uff0c\u53ef\u4ee5\u6682\u65f6\u6062\u590d\u6b66\u529f\uff0c\u4f46\u540e\u60a3\u751a\u5927\uff0c\u836f\u529b\u6d88\u5931\u4e4b\u540e\uff0c\u672c\u6765\u53ef\u4ee5\u4e00\u5e74\u6062\u590d\u7684\u75c5\u4f53\u5c31 \u8981\u4e09\u5e74\u4e86\u3002\u56e0\u6b64\uff0c\u4e34\u8d70\u4e4b\u65f6\uff0c\u590f\u4faf\u575a\u5343\u53ee\u4e07\u5631\uff0c\u8981\u957f\u5b59\u5747\u91cf\u5c0f\u5fc3\uff0c\u82e5\u975e\u9047\u5230\u9ad8\u624b\uff0c\u8feb\u4e0d \u5f97\u5df2\u975e\u52a8\u624b\u4e0d\u53ef\u7684\u8bdd\uff0c\u5343\u4e07\u4e0d\u53ef\u670d\u836f\u3002 \u3000\u3000\u957f\u5b59\u5747\u91cf\u5230\u4e86\u957f\u5b89\u4e4b\u540e\u4e0d\u4e45\uff0c\u610f\u5916\u7684\u63a2\u542c\u5230\u4e86\u674e\u9038\u7684\u6d88\u606f\u3002\u539f\u6765\u4e0e\u674e\u9038\u540c\u65f6\u5165\u795e\u6b66 \u8425\u7684\u90a3\u4e2a\u866c\u987b\u6c49\u5b50\u5357\u5bab\u5c1a\uff0c\u4e43\u662f\u957f\u5b59\u5747\u91cf\u7684\u4e16\u4f84\u3002\u957f\u5b59\u5747\u91cf\u5728\u592a\u5b97\u7687\u5e1d\uff08\u674e\u4e16\u6c11\uff09\u4e4b\u65f6\uff0c \u66fe\u505a\u8fc7\u6bbf\u524d\u68c0\u70b9\uff0c\u5357\u5bab\u5c1a\u7684\u7236\u4eb2\u6b63\u662f\u4ed6\u6700\u5f97\u529b\u7684\u90e8\u4e0b\u3002 \u3000\u3000\u674e\u9038\u867d\u7136\u6539\u5bb9\u6613\u8c8c\uff0c\u5e76\u5047\u5192\u4e86\u5f20\u4e4b\u5947\u7684\u540d\u5b57\uff0c\u53ef\u662f\u86db\u4e1d\u9a6c\u8ff9\u5b9e\u5728\u53ef\u7591\uff0c\u90fd\u770b\u5728\u5357\u5bab \u5c1a\u7684\u773c\u91cc\uff0c\u5c31\u5728\u674e\u9038\u88ab\u5dee\u9063\u62bc\u89e3\u201c\u523a\u5ba2\u201d\u5165\u5bab\u7684\u90a3\u65e5\uff0c\u5357\u5bab\u5c1a\u78b0\u5230\u4e86\u957f\u5b59\u5747\u91cf\uff0c\u4e00\u8bf4\u8d77 \u6765\uff0c\u6599\u60f3\u8fd9\u4e2a\u201c\u5f20\u4e4b\u5947\u201d\u5fc5\u662f\u674e\u9038\u65e0\u7591\uff0c\u4e5f\u6599\u60f3\u5230\u674e\u9038\u88ab\u5dee\u9063\u5165\u5bab\uff0c\u5176\u4e2d\u5fc5\u6709\u8be1\u8ba1\uff0c\u4f17 \u4eba\u5927\u5927\u5403\u60ca\uff0c\u5546\u8bae\u7ed3\u679c\uff0c\u4fbf\u7531\u5357\u5b98\u5c1a\u6f5c\u5165\u5185\u5bab\u884c\u523a\uff0c\u4e58\u673a\u63a9\u62a4\u674e\u9038\u9003\u8d70\uff0c\u800c\u957f\u5b59\u5747\u91cf\u4e00 \u5bb6\u4e09\u53e3\uff0c\u5219\u5728\u9a8a\u5c71\u540e\u9762\u63a5\u5e94\u3002 \u3000\u3000\u65e0\u5de7\uff0c\u4ed6\u4eec\u5728\u5c71\u8c37\u4e4b\u4e2d\uff0c\u4fbf\u78b0\u5230\u4e86\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\uff0c\u957f\u5b59\u5747\u91cf\u65e0\u5948\uff0c\u53ea\u597d\u54bd\u4e0b\u4e86\u590f \u4faf\u575a\u7ed9\u4ed6\u7684\u5947\u836f\uff0c\u6682\u65f6\u6062\u590d\u4e86\u6b66\u529f\u548c\u90a3\u4e24\u5927\u9b54\u5934\u4f5c\u4e86\u4e00\u573a\u6076\u6597\u3002\u957f\u5b59\u6cf0\u820d\u8eab\u6551\u7236\uff0c\u6251\u4e0a \u53bb\u62b1\u7740\u4e86\u6076\u884c\u8005\uff0c\u54ac\u4f24\u4e86\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u4e0e\u6076\u884c\u8005\u540c\u5f52\u4e8e\u5c3d\uff0c\u6bd2\u89c2\u97f3\u8fde\u4e2d\u4e86\u957f\u5b59\u5747\u91cf\u4e03\u5904 \u5251\u4f24\uff0c\u4e5f\u9003\u8d70\u4e86\u3002\u674e\u9038\u8dcc\u4e0b\u5c71\u8c37\u4e4b\u65f6\uff0c\u6b63\u503c\u4ed6\u4eec\u6253\u5f97\u6700\u6fc0\u70c8\u7684\u65f6\u5019\uff0c\u957f\u5b59\u58c1\u5c06\u674e\u9038\u6551\u8d77\uff0c \u5f85\u5230\u957f\u5b59\u5747\u91cf\u5c06\u6bd2\u89c2\u97f3\u8d76\u8dd1\uff0c\u4ed6\u4eec\u5df2\u542c\u5f97\u5c71\u4e0a\u6b66\u7384\u971c\u7684\u58f0\u97f3\uff0c\u4ed6\u4eec\u6050\u9632\u6b66\u7384\u971c\u7387\u9886\u5927\u5185 \u536b\u4e0a\u524d\u6765\u8ffd\u6355\uff0c\u8feb\u4e0d\u53ca\u5f85\u7684\u80cc\u8d77\u674e\u9038\u4fbf\u5373\u9003\u751f\uff0c\u957f\u5b59\u6cf0\u662f\u6b7b\u662f\u4f24\uff0c\u4ed6\u4eec\u5df2\u65e0\u7455\u53bb\u7167\u987e\u4e86\u3002 \u4e0d\u8fc7\u957f\u5b59\u5747\u91cf\u4eb2\u773c\u89c1\u5230\u957f\u5b59\u6cf0\u4e2d\u4e86\u6076\u884c\u8005\u7684\u6bd2\u638c\uff0c\u53c8\u88ab\u6bd2\u89c2\u97f3\u6253\u4e86\u4e00\u84ec\u900f\u7a74\u9488\uff0c\u6599\u60f3\u51f6 \u591a\u5409\u5c11\uff0c\u5728\u4ed6\u7684\u5fc3\u76ee\u4e2d\uff0c\u81ea\u662f\u628a\u8fd9\u4e2a\u513f\u5b50\u5f53\u505a\u6b7b\u4e86\u3002 \u3000\u3000\u957f\u5b59\u58c1\u65ad\u65ad\u7eed\u7eed\u7684\u628a\u8fd9\u6bb5\u7ecf\u8fc7\u8bf4\u5b8c\uff0c\u773c\u6cea\u65e9\u5df2\u6e7f\u900f\u4e86\u7f57\u8863\uff0c\u674e\u9038\u5fc3\u4e2d\u4e5f\u662f\u4f24\u75db\u4e4b\u6781\uff0c \u60f3\u8d77\u957f\u5b59\u5747\u91cf\u4e3a\u4e86\u81ea\u5df1\uff0c\u5931\u4e86\u513f\u5b50\uff0c\u8fd9\u4e00\u5206\u6df1\u601d\uff0c\u771f\u4e0d\u77e5\u5982\u4f55\u62a5\u7b54\u3002 \u3000\u3000\u4e0d\u4e45\u9aa1\u8f66\u5230\u4e86\u524d\u9762\u5c71\u811a\uff0c\u674e\u9038\u5c06\u957f\u5b59\u5747\u91cf\u80cc\u4e0a\u5c71\uff0c\u957f\u5b59\u58c1\u9ed8\u9ed8\u65e0\u8a00\u7684\u8ddf\u5728\u540e\u9762\uff0c\u4ed6 \u4eec\u90fd\u77e5\u9053\u957f\u5b59\u5747\u91cf\u8fd9\u6761\u6027\u547d\u5df2\u662f\u5f31\u4f3c\u6e38\u4e1d\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u968f\u98ce\u800c\u901d\u3002\u674e\u9038\u7684\u5fc3\u5934\u4e0a\u597d\u50cf\u538b \u4e86\u4e00\u5ea7\u5927\u5c71\uff0c\u611f\u5230\u6c89\u91cd\u4e4b\u6781\uff0c\u597d\u51e0\u6b21\u907f\u5f00\u4e86\u957f\u5b59\u58c1\u7684\u773c\u5149\uff0c\u6015\u7b54\u4e0d\u51fa\u5979\u7684\u95ee\u8bdd\u3002 \u3000\u3000\u5c71\u9e93\u7684\u90a3\u5ea7\u5bfa\u9662\u4e43\u662f\u4e00\u5ea7\u591a\u5e74\u5931\u4fee\u7684\u77f3\u5e99\uff0c\u5e99\u4e2d\u6709\u4e00\u4e2a\u987b\u7709\u7686\u767d\u7684\u4e3b\u6301\uff0c\u548c\u4e00\u4e2a\u70e7 \u706b\u7684\u5c0f\u548c\u5c1a\uff0c\u8001\u4e3b\u6301\u4e3a\u4eba\u5f88\u597d\uff0c\u542c\u8bf4\u6709\u4eba\u5728\u8def\u4e0a\u5f97\u4e86\u6025\u75c5\uff0c\u524d\u6765\u6295\u5bbf\uff0c\u7acb\u5373\u63a5\u7eb3\uff0c\u8ba9\u51fa \u63b8\u623f\u7ed9\u4ed6\u4eec\u4f4f\u5bbf\uff0c\u5e76\u4e14\u53eb\u5c0f\u548c\u5c1a\u7ed9\u4ed6\u4eec\u70e7\u70ed\u6c64\uff0c\u62db\u5f85\u5f97\u5468\u5230\u3002 \u3000\u3000\u957f\u5b59\u58c1\u5c06\u8001\u7236\u5b89\u987f\u5728\u63b8\u623f\u4e2d\u4ec5\u6709\u7684\u4e00\u5f20\u5e8a\u4e0a\uff0c\u4e00\u63a2\u4ed6\u7684\u8109\u606f\uff0c\u6bd4\u8d77\u521a\u624d\u66f4\u5fae\u5f31\u4e86\uff0c \u674e\u9038\u89e3\u5f00\u4e86\u4ed6\u88ab\u5c01\u95ed\u7684\u7a74\u9053\uff0c\u8bd5\u7528\u672c\u8eab\u529f\u529b\u52a9\u4ed6\u6062\u590d\u7cbe\u795e\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u957f\u5b59\u5747\u91cf\u5f20\u5f00\u773c \u775b\uff0c\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u58c1\u513f\uff0c\u4f60\u8fc7\u6765\uff0c\u4f60\u66ff\u6211\u5411\u6bbf\u4e0b\u53e9\u5934\uff01\u201d\u674e\u9038\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u77e5\u6240\u63aa\uff0c\u6025 \u5fd9\u5c06\u957f\u5b59\u58c1\u6276\u8d77\u3002 \u3000\u3000\u53ea\u542c\u5f97\u957f\u5b59\u5747\u91cf\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5982\u4eca\u53ea\u5269\u4e0b\u4e86\u8fd9\u4e2a\u5973\u513f\uff0c\u6211\u8981\u5c06\u5979\u7684\u7ec8\u8eab\u6258\u62dc\u7ed9\u4f60 \u7167\u987e\u4e86\uff0c\u6bbf\u4e0b\uff0c\u4f60\u613f\u610f\u7ed9\u6211\u6311\u8d77\u8fd9\u4ed8\u62c5\u5b50\u5417\uff1f\u201d\u8fd9\u662f\u4ed6\u7b2c\u4e8c\u6b21\u5c06\u5973\u513f\u4ea4\u6258\u7ed9\u674e\u9038\u4e86\uff0c\u8fd9 \u6b21\u8bf4\u5f97\u66f4\u9732\u9aa8\uff0c\u66f4\u660e\u767d\uff0c\u8bf4\u662f\u6258\u4ed6\u7167\u987e\uff0c\u5b9e\u9645\u662f\u8981\u5c06\u5973\u513f\u7684\u7ec8\u8eab\u8bb8\u914d\u7ed9\u4ed6\u3002 \u3000\u3000\u674e\u9038\u5fc3\u60c5\u6fc0\u52a8\uff0c\u7eb7\u5982\u4e71\u4e1d\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u4e0a\u5b98\u5a49\u513f\u7684\u5f71\u5b50\u4e0e\u6b66\u7384\u971c\u7684\u5f71\u5b50\u76f8\u7ee7\u51fa\u73b0\uff0c \u5a49\u513f\u662f\u548c\u4ed6\u6027\u60c5\u6700\u76f8\u8fd1\u7684\u4eba\uff0c\u6b66\u7384\u971c\u5219\u662f\u4ed6\u5fc3\u5e95\u6700\u4f69\u670d\u7684\u4eba\uff0c\u8fd9\u4e24\u4e2a\u4eba\u90fd\u5bf9\u4ed6\u6709\u4e00\u7247\u6df1 \u60c5\uff0c\u6ee1\u6000\u671f\u5f85\uff0c\u7136\u800c\u53c8\u6709\u8bb8\u591a\u6069\u6028\u7ea0\u7f20\uff0c\u7eb5\u6709\u5c16\u5200\u5229\u526a\uff0c\u4e5f\u662f\u526a\u4e0d\u65ad\uff0c\u7406\u8fd8\u4e71\uff01\u674e\u9038\u672c \u6765\u6253\u7b97\u4ece\u6b64\u98d8\u6cca\u6c5f\u6e56\uff0c\u5b64\u96f6\u7ec8\u8001\uff0c\u5fc3\u5982\u7a3f\u6728\uff0c\u610f\u4f3c\u5bd2\u7070\uff0c\u518d\u4e5f\u4e0d\u6cbe\u60c5\u60f9\u7231\u7684\u4e86\uff0c\u7136\u800c\u4ed6 \u505a\u68a6\u4e5f\u6599\u60f3\u4e0d\u5230\uff0c\u957f\u5b59\u5747\u91cf\u7adf\u7136\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u8981\u628a\u5973\u513f\u90d1\u91cd\u7684\u4ea4\u6258\u7ed9\u4ed6\uff01 \u3000\u3000\u957f\u5b59\u58c1\u5bf9\u4ed6\u7684\u4e00\u7247\u6df1\u60c5\uff0c\u4e0d\u5728\u5a49\u513f\u4e0e\u6b66\u7384\u971c\u4e4b\u4e0b\uff0c\u800c\u6700\u4ee4\u4ed6\u4e3a\u96be\u7684\uff0c\u5219\u662f\u600e\u5fcd\u62c2\u9006 \u4e00\u4e2a\u4e34\u6b7b\u7684\u8001\u4eba\u7684\u5631\u6258\uff0c\u8fd9\u4e2a\u8001\u4eba\u6551\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u4e3a\u4e86\u4ed6\u727a\u7272\u4e86\u81ea\u5df1\u552f\u4e00\u7684\u7231\u513f\uff0c\u800c\u4e14 \u8fd9\u4e2a\u8001\u4eba\u53c8\u662f\u6bd5\u751f\u6548\u5fe0\u4e8e\u4ed6\u674e\u59d3\u7687\u671d\u7684\u5927\u5fe0\u81e3\uff01 \u3000\u3000\u674e\u9038\u7684\u5fc3\u597d\u50cf\u7ed9\u5229\u5200\u5212\u8fc7\uff0c\u5272\u7684\u7247\u7247\u788e\u4e86\uff0c\u8fd9\u5a5a\u4e8b\u662f\u7b54\u5e94\u5462\uff0c\u8fd8\u662f\u4e0d\u7b54\u5e94\u5462\uff1f\u957f\u5b59 \u5747\u91cf\u5728\u770b\u7740\u4ed6\uff0c\u957f\u5b59\u58c1\u8f6c\u8fc7\u5934\u4e00\u8fb9\u3002\u4f46\u674e\u9038\u53d1\u73b0\u5979\u90a3\u542b\u7f9e\u5e26\u6127\uff0c\u800c\u53c8\u6df1\u60c5\u8109\u8109\u7684\u773c\u5149\u4e5f \u6b63\u5728\u5077\u770b\u7740\u4ed6\uff0c\u674e\u9038\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u5730\u5728\u75c5\u69bb\u4e4b\u524d\u8dea\u5012\uff0c\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8001 \u4f2f\u4e0d\u5acc\u5f03\u7684\u8bdd\uff0c\u6211\u613f\u610f\uff0c\u613f\u610f\u505a\u4f60\u7684\u513f\u5b50\uff0c\u5bf9\u5f85\u58c1\u59b9\u5c31\u50cf\u4eb2\u751f\u59b9\u59b9\u4e00\u822c\u3002\u201d\u957f\u5b59\u5747\u91cf\u6447 \u6447\u5934\uff0c\u773c\u5149\u4e2d\u5145\u6ee1\u5931\u671b\uff0c\u4e34\u7ec8\u8005\u7edd\u671b\uff0c\u6700\u662f\u4ee4\u4eba\u5fc3\u788e\uff0c\u674e\u9038\u5fcd\u53d7\u4e0d\u4e86\u4ed6\u90a3\u7edd\u671b\u7684\u773c\u5149\uff0c \u201c\u96be\u9053\u6211\u5c31\u5fcd\u5fc3\u4ee4\u4ed6\u6b7b\u4e0d\u7791\u76ee\u5417\uff1f\u201d\u77ac\u65f6\u95f4\u5fc3\u610f\u5df2\u51b3\uff0c\u4e0d\u5f85\u957f\u5b59\u5747\u91cf\u51fa\u58f0\uff0c\u63a5\u7740\u8bf4\u9053\uff1a \u201c\u6211\u8981\u5c06\u58c1\u59b9\u5f53\u4f5c\u59b9\u59b9\uff0c\u82e5\u5979\u4e0d\u5acc\u5f03\u6211\u7684\u8bdd\uff0c\u6211\u66f4\u613f\u5979\u505a\u6211\u7684\u7231\u59bb\u3002\u201d\u957f\u5b59\u5747\u91cf\u53cc\u773c\u4e00 \u5f20\uff0c\u9053\uff1a\u201c\u58c1\u513f\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u201d\u957f\u5b59\u58c1\u9ed8\u7136\u4e0d\u8bed\uff0c\u6cea\u75d5\u6ee1\u9762\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6211\u542c\u51ed\u7239\u7239\u3002\u201d \u957f\u5b59\u5747\u91cf\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u5c06\u58c1\u513f\u4ea4\u7ed9\u4f60\u4e86\u3002\u5979\u813e\u6c14\u4e0d\u597d\uff0c\u4f60\u591a\u591a\u5305\u6db5\u3002\u201d\u674e\u9038\u518d\u8dea\u4e0b \u53bb\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u5524\u4e86\u4e00\u58f0\uff1a\u201c\u5cb3\u7236\uff01\u201d\u957f\u5b59\u5747\u91cf\u73b0\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u53cc\u773c\u5f90\u5f90\u95ed\u4e0a\u3002\u957f\u5b59 \u58c1\u54ed\u9053\uff1a\u201c\u7239\u7239\uff01\u201d\u4e0a\u6765\u5c06\u4ed6\u62b1\u7740\uff0c\u53ea\u542c\u5f97\u957f\u5b59\u5747\u91cf\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8981\u6068\u5a49\u513f\uff0c\u4f60 \u4eec\u8981\u76f8\u4e92\u6276\u6301\uff0c\u767d\u5934\u5055\u8001\u3002\u201d\u8fd9\u662f\u4ed6\u6700\u540e\u7684\u4e24\u53e5\u9057\u8a00\u4e86\uff0c\u4ece\u4ed6\u524d\u4e00\u53e5\u9057\u8a00\uff0c\u53ef\u89c1\u5bf9\u4e0a\u5b98 \u5a49\u513f\u7684\u7231\uff0c\u81f3\u5c11\u4e5f\u548c\u4ed6\u5bf9\u5f85\u513f\u5973\u4e00\u6837\uff1b\u4ece\u540e\u4e00\u53e5\u9057\u8a00\uff0c\u53ef\u89c1\u4ed6\u5bf9\u8fd9\u95e8\u5a5a\u4e8b\u8fd8\u6709\u5fe7\u7591\u3002\u674e \u9038\u4f0f\u5230\u4ed6\u7684\u80f8\u524d\uff0c\u542b\u6cea\u8bf4\u9053\uff1a\u201c\u5cb3\u7236\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u4e00\u5b9a\u597d\u597d\u770b\u5f85\u58c1\u59b9\u3002\u201d\u8bf4\u5b8c\u4e86\u8fd9\u4e00\u53e5 \u8bdd\uff0c\u957f\u5b59\u5747\u91cf\u53cc\u773c\u5168\u95ed\uff0c\u9762\u5e26\u7b11\u5bb9\uff0c\u53cc\u811a\u4e00\u4f38\uff0c\u6c14\u606f\u65ad\u7edd\u3002\u957f\u5b59\u58c1\u653e\u58f0\u5927\u54ed\uff0c\u7d27\u7d27\u63e1\u7740 \u674e\u9038\u53cc\u624b\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u957f\u5b59\u58c1\u62bd\u564e\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u7684\u540e\u4e8b\uff0c\u90fd\u8981\u501a\u9760\u4f60\u6599\u7406\u4e86\u3002\u4f60\u5bf9\u6211\u7239\u7239\u7684 \u597d\u610f\uff0c\u6211\u4e00\u751f\u90fd\u4f1a\u611f\u6fc0\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u8fd9\u662f\u90a3\u91cc\u8bdd\u6765\u3002\u54b1\u4eec\u5982\u4eca\u5df2\u662f\u4e00\u5bb6\u4eba\u4e86\uff0c\u4f60\u8bf4\u8fd9 \u6837\u7684\u8bdd\uff0c\u5c06\u6211\u5f53\u4f5c\u4ec0\u4e48\u4eba\u4e86\u5462\uff1f\u201d\u957f\u5b59\u58c1\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u674e\u9038\u54e5\u54e5\uff0c\u4f60\u4e0d\u8981\u7792\u6211\uff0c\u6211\u77e5\u9053 \u4f60\u7684\u5fc3\u610f\uff0c\u4f60\u662f\u4e3a\u4e86\u6211\u7239\u7239\u53bb\u5f97\u5b89\u5fc3\uff0c\u8fd9\u624d\u8fdd\u80cc\u4e86\u4f60\u81ea\u5df1\u7684\u5fc3\u613f\uff0c\u8981\u6211\u4f5c\u4f60\u59bb\u5b50\u7684\u3002\u674e \u9038\u54e5\u54e5\uff0c\u4f60\u653e\u5fc3\u5427\uff0c\u6211\u4e0d\u4f1a\u5c06\u8fd9\u4ef6\u4e8b\u60c5\u5f53\u771f\u7684\u3002\u4f46\u6c42\u4f60\u628a\u6211\u7239\u7239\u7684\u9057\u4f53\u63a9\u57cb\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c \u6211\u5c31\u4e0d\u4f1a\u518d\u62d6\u7d2f\u4f60\u4e86\u3002\u201d\u674e\u9038\u63e1\u7740\u5979\u7684\u53cc\u624b\uff0c\u4f46\u89c9\u5979\u7684\u624b\u5fc3\u70bd\u70ed\uff0c\u8109\u8c61\u4e0d\u5b81\uff0c\u53cc\u988a\u706b\u7ea2\uff0c \u75c5\u5bb9\u663e\u9732\uff0c\u674e\u9038\u5fc3\u60c5\u6fc0\u52a8\uff0c\u6df1\u6df1\u89c9\u5f97\u5bf9\u4e0d\u8d77\u5979\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u5c06\u5979\u6402\u5165\u6000\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u58c1 \u59b9\uff0c\u4f60\u5207\u83ab\u80e1\u601d\u4e71\u60f3\uff0c\u4eca\u751f\u4eca\u4e16\uff0c\u54b1\u4eec\u5df2\u662f\u540c\u547d\u76f8\u4f9d\uff0c\u7eb5\u662f\u5730\u8986\u5929\u7ffb\uff0c\u54b1\u4eec\u4e5f\u4e0d\u4f1a\u5206\u5f00 \u7684\u4e86\u3002\u4f60\u8981\u81ea\u5df1\u4fdd\u91cd\uff0c\u4e0d\u53ef\u4ee4\u5cb3\u7236\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u8fd8\u8981\u4e3a\u4f60\u6211\u62c5\u5fc3\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u4e43\u662f\u51fa\u4e8e \u4ed6\u7684\u81f3\u8bda\uff0c\u957f\u5b59\u58c1\u4ee5\u8896\u62ed\u6cea\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u5e99\u4e2d\u7684\u4e3b\u6301\u53e4\u9053\u70ed\u80a0\uff0c\u542c\u8bf4\u5ba2\u4eba\u75c5\u6b7b\uff0c\u8fdb\u6765\u6170\u95ee\uff0c\u5e2e\u7740\u674e\u9038\u6536\u6b93\uff0c\u5e76\u5dee\u9063\u90a3\u4e2a\u70e7\u706b \u548c\u5c1a\uff0c\u5230\u9644\u8fd1\u7684\u5c0f\u9547\u53bb\u4e70\u68fa\u6750\u3002\u5e76\u4e14\u81ea\u613f\u66ff\u957f\u5b59\u5747\u91cf\u505a\u4e00\u573a\u6cd5\u4e8b\uff0c\u8d85\u5ea6\u4ea1\u7075\uff0c\u8001\u4e3b\u6301\u4e34 \u8d70\u65f6\u95ee\u8d77\u6b7b\u8005\u7684\u59d3\u540d\uff0c\u51c6\u5907\u505a\u6cd5\u4e8b\u7684\u65f6\u5019\u7ed9\u4ed6\u5ff5\u201c\u5f80\u751f\u5492\u201d\uff0c\u674e\u9038\u65b9\u81ea\u8e0c\u8e87\uff0c\u957f\u5b59\u58c1\u5df2 \u5148\u8bf4\u4e86\u3002\u674e\u9038\u4e00\u60f3\uff0c\u8fd9\u8001\u548c\u5c1a\u76f8\u8c8c\u6148\u7965\uff0c\u800c\u4e14\u4ed6\u4e5f\u672a\u5fc5\u77e5\u9053\u957f\u5b59\u5747\u91cf\u662f\u4ec0\u4e48\u4eba\uff0c\u65e2\u5df2\u8bf4 \u51fa\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002 \u3000\u3000\u8c01\u6599\u798f\u65e0\u53cc\u81f3\uff0c\u7978\u4e0d\u5355\u884c\uff0c\u957f\u5b59\u5747\u91cf\u7684\u9057\u4f53\u672a\u66fe\u6536\u6b93\uff0c\u957f\u5b59\u58c1\u5c31\u75c5\u8d77\u6765\uff0c\u90a3\u8001\u548c\u5c1a \u5c06\u81ea\u5df1\u505a\u529f\u8bfe\u7684\u3001\u5bfa\u4e2d\u552f\u4e00\u7684\u4e00\u95f4\u9759\u5ba4\uff0c\u4e5f\u8ba9\u4e86\u51fa\u6765\uff0c\u7ed9\u75c5\u4eba\u4f4f\u3002\u674e\u9038\u611f\u6fc0\u5f97\u5f88\uff0c\u7b7e\u4e86 \u4e00\u767e\u4e24\u94f6\u5b50\u7684\u201c\u9999\u6cb9\u201d\uff0c\u8001\u4e3b\u6301\u6050\u6015\u4ed6\u4eec\u5728\u65c5\u9014\u4e2d\u4e0d\u591f\u7528\uff0c\u4e0d\u80af\u6536\u53d7\uff0c\u8feb\u5f97\u674e\u9038\u8bf4\u51fa\u8eab \u4e0a\u8fd8\u6709\u4f59\u94b1\uff0c\u4ed6\u624d\u80af\u6536\u4e0b\u3002 \u3000\u3000\u5c0f\u9547\u79bb\u5c71\u811a\u4e0d\u8fc7\u5341\u591a\u91cc\u8def\uff0c\u90a3\u70e7\u706b\u548c\u5c1a\u76f4\u5230\u508d\u665a\u65f6\u5206\u624d\u628a\u68fa\u6750\u642c\u56de\u5bfa\u4e2d\uff0c\u674e\u9038\u6536\u6b93 \u5b8c\u6bd5\uff0c\u6700\u540e\u77bb\u4ef0\u4e86\u4e00\u4e0b\u9057\u5bb9\uff0c\u628a\u68fa\u76d6\u6162\u6162\u76d6\u4e0a\uff0c\u5fc3\u4e2d\u60b2\u75db\u65e0\u9650\uff0c\u60f3\u8d77\u5979\u4eec\u4e24\u7236\u5973\u7684\u751f\u6b7b \u6069\u60c5\uff0c\u81ea\u5df1\u4e5f\u53ea\u6709\u6b7b\u5fc3\u584c\u5730\u7684\u7231\u62a4\u957f\u5b59\u58c1\u624d\u80fd\u591f\u62a5\u7b54\u4e86\u3002 \u3000\u3000\u674e\u9038\u56de\u8f6c\u9759\u5ba4\uff0c\u957f\u5b59\u58c1\u8fd8\u5728\u660f\u660f\u8ff7\u8ff7\uff0c\u4e0d\u65ad\u7684\u53d1\u51fa\u68a6\u8bed\uff0c\u53eb\u4e86\u4e24\u58f0\u201c\u7239\u7239\u201d\uff0c\u8ddf\u7740 \u53c8\u53eb\u674e\u9038\u7684\u540d\u5b57\uff0c\u674e\u9038\u5750\u5728\u5979\u7684\u8eab\u8fb9\u3002\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u6211\u5c31\u5728\u4f60\u7684\u8eab\u8fb9\uff0c\u4f60\u653e\u5fc3\u5427\u3002\u201d \u957f\u5b59\u58c1\u9053\uff1a\u201c\u662f\u8c01\u6765\u4e86\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u662f\u6211\u554a\uff01\u201d\u623f\u5916\u5ffd\u7136\u4e86\u6709\u4eba\u63a5\u58f0\u5e94\u9053\uff1a\u201c\u662f\u6211\u554a\uff01\u201d \u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u89c1\u90a3\u4e2a\u70e7\u706b\u548c\u5c1a\uff0c\u7aef\u7740\u4e00\u7897\u70ed\u817e\u817e\u7684\u836f\u8336\uff0c\u63ed\u5e18\u800c\u5165\u3002\u539f\u6765\u674e\u9038\u5168\u5fc3 \u5168\u610f\u653e\u5728\u957f\u5b59\u58c1\u8eab\u4e0a\uff0c\u53cd\u800c\u662f\u957f\u5b59\u58c1\u5148\u542c\u5230\u90a3\u5c0f\u548c\u5c1a\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u90a3\u5c0f\u548c\u5c1a\u7aef\u7740\u8336\u7897\u9053\uff1a\u201c\u8fd9\u662f\u57f9\u5143\u5065\u813e\u7684\u9999\u7518\u9732\u8336\uff0c\u75c5\u4eba\u559d\u4e86\u53ef\u4ee5\u5b81\u795e\u9759\u6c14\uff0c\u597d\u4eba \u559d\u4e86\u53ef\u4ee5\u589e\u957f\u7cbe\u795e\uff0c\u4e24\u4f4d\u8d35\u5ba2\u5149\u4e34\u5c0f\u5bfa\uff0c\u54b1\u4eec\u4ec0\u4e48\u90fd\u6ca1\u6709\u62db\u5f85\uff0c\u5f88\u662f\u8fc7\u610f\u4e0d\u53bb\u3002\u5e08\u7236\u8bf4 \u8bf7\u4f60\u4eec\u5148\u559d\u4e86\u8fd9\u7897\u7518\u9732\u8336\uff0c\u660e\u513f\u8d76\u65e9\u518d\u8bf7\u4e00\u4f4d\u5927\u592b\u7ed9\u8fd9\u4f4d\u59d1\u5a18\u770b\u75c5\u3002\u201d\u674e\u9038\u89c9\u5f97\u8fd9\u4e2a\u5c0f \u548c\u5c1a\u6709\u70b9\u6cb9\u5634\u6ed1\u820c\uff0c\u548c\u8001\u548c\u5c1a\u7684\u6734\u76f4\u4e0d\u5927\u76f8\u540c\uff0c\u4f46\u4ee5\u4e3a\u8fd9\u662f\u6027\u60c5\u4f7f\u7136\uff0c\u5374\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c \u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4e24\u4f4d\u5e08\u5085\u76db\u60c5\u3002\u5728\u4e0b\u611f\u6fc0\u5f97\u5f88\u3002\u201d\u6b63\u60f3\u4f38\u624b\u63a5\u90a3\u7897\u836f\u8336\uff0c\u5ffd\u542c\u5f97\u4e00\u9635 \u6025\u4fc3\u7684\u811a\u6b65\u58f0\uff0c\u90a3\u8001\u548c\u5c1a\u6c14\u5598\u5401\u5401\u7684\u8dd1\u4e86\u8fdb\u6765\uff0c\u5288\u5934\u9a82\u9053\uff1a\u201c\u5b7d\u755c\uff0c\u4f60\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u957f\u8896\u4e00\u62c2\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u8336\u7897\u5760\u5730\uff0c\u88c2\u4e3a\u56db\u7247\u3002\u90a3\u5c0f\u548c\u5c1a\u5927\u53eb\u4e00\u58f0\uff0c\u5ffd\u5730\u4e00\u62db\u201c\u9646\u5730\u884c\u821f\u201d\uff0c \u53cc\u638c\u5e73\u51fa\uff0c\u5411\u90a3\u8001\u548c\u5c1a\u63a8\u53bb\u3002 \u3000\u3000\u8fd9\u7a81\u5982\u5176\u6765\u7684\u53d8\u5316\uff0c\u4ee4\u5230\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c\u66f4\u60f3\u4e0d\u5230\u7684\u662f\u8fd9\u5c0f\u548c\u5c1a\u5c45\u7136\u61c2\u5f97\u6b66\u529f\uff0c\u800c \u4e14\u8fd9\u4e00\u62db\u201c\u9646\u5730\u884c\u821f\u201d\u7684\u638c\u6cd5\uff0c\u7adf\u7136\u662f\u9655\u5317\u4f0f\u864e\u5e2e\u7684\u9547\u5e2e\u638c\u6cd5\uff0c\u4f0f\u864e\u5e2e\u7684\u5e2e\u4e3b\u662f\u4e00\u4e2a\u6781 \u5389\u5bb3\u7684\u5927\u76d7\uff0c\u4ed6\u7684\u638c\u6cd5\u53ea\u4f20\u672c\u5e2e\u5f1f\u5b50\uff0c\u7edd\u4e0d\u4f1a\u4f20\u53ca\u5916\u4eba\uff0c\u96be\u9053\u8fd9\u5c0f\u548c\u5c1a\u7adf\u662f\u76d7\u5e2e\u4e2d\u4eba\u3002 \u3000\u3000\u8fd9\u4e00\u4e32\u7591\u95ee\u500f\u5730\u4ece\u674e\u9038\u5fc3\u4e2d\u63a0\u8fc7\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u5c0f\u548c\u5c1a\u7684\u53cc\u638c\u5df2\u63a8\u5230\u4ed6\u5e08\u7236 \u7684\u80f8\u524d\uff0c\u674e\u9038\u4f55\u7b49\u6b66\u529f\uff0c\u7109\u80fd\u8ba9\u8fd9\u8001\u548c\u5c1a\u7ed9\u4ed6\u6253\u4e2d\uff0c\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u4e00\u62db\u201c\u5f2f \u6708\u5c04\u864e\u201d\u3002\u638c\u52bf\u540e\u53d1\u5148\u81f3\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u90a3\u5c0f\u548c\u5c1a\u9707\u5f97\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u90a3\u5c0f\u548c\u5c1a\u8d81 \u52bf\u4e00\u4e2a\u201c\u91d1\u9ca4\u7a7f\u6ce2\u201d\uff0c\u4e00\u4e2a\u7b4b\u6597\u7ffb\u51fa\u95e8\u5916\uff0c\u54fc\u4e5f\u4e0d\u54fc\u4e00\u58f0\uff0c\u8df3\u8d77\u6765\u5c31\u8d70\u4e86\u3002\u674e\u9038\u8fd9\u4e00\u638c \u867d\u7136\u53ea\u7528\u4e86\u4e94\u6210\u4e3a\u9053\uff0c\u6b66\u529f\u5e73\u5e38\u4e4b\u58eb\u5df2\u662f\u7edd\u5bf9\u63a5\u53d7\u4e0d\u8d77\uff0c\u8fd9\u5c0f\u548c\u5c1a\u5c45\u7136\u6ca1\u6709\u53d7\u4f24\uff0c\u800c\u4e14 \u8fd8\u80fd\u591f\u5982\u98de\u9003\u8d70\uff0c\u663e\u89c1\u6b66\u529f\u9020\u8be3\u5df2\u662f\u76f8\u5f53\u4e0d\u5f31\u3002 \u3000\u3000\u90a3\u8001\u548c\u5c1a\u9762\u8272\u5927\u53d8\uff0c\u8fde\u9a82\u4e86\u4e24\u58f0\u201c\u5b7d\u755c\u201d\uff0c\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u5c45\u58eb\u5feb\u8d70\u4e86\u5427\uff0c\u6211\u8fd9\u5b7d\u5f92 \u8d3c\u6027\u4e0d\u6539\uff0c\u53ea\u6015\u8fd8\u8981\u518d\u6765\u4f24\u5bb3\u4f60\u4eec\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u90a3\u8001\u548c\u5c1a\u53f9\u4e86\u53e3\u6c14\uff0c \u8bf4\u9053\uff1a\u201c\u4e94\u5e74\u524d\u7684\u4e00\u4e2a\u96ea\u591c\uff0c\u6211\u542c\u5f97\u5bfa\u5916\u6709\u547b\u541f\u4e4b\u58f0\uff0c\u5f00\u95e8\u4e00\u770b\uff0c\u89c1\u4e00\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u5c11 \u5e74\u4eba\u5367\u5728\u96ea\u5730\u4e0a\uff0c\u8eab\u4e0a\u8fd8\u53d7\u4e86\u4f24\uff0c\u662f\u6211\u5c06\u4ed6\u6551\u4e86\u8d77\u6765\uff0c\u7ed9\u4ed6\u8c03\u6cbb\u3002\u4ed6\u81ea\u5df1\u8bf4\u662f\u9014\u4e2d\u9047\u76d7\uff0c \u7236\u6bcd\u53cc\u4ea1\uff0c\u6211\u601c\u60af\u4ed6\u662f\u4e2a\u5b64\u513f\uff0c\u5c31\u5c06\u4ed6\u6536\u4e3a\u5f92\u5f1f\uff0c\u8ba9\u4ed6\u7559\u5728\u5bfa\u4e2d\u505a\u4e2a\u70e7\u706b\u548c\u5c1a\u3002\u540e\u6765\u6211 \u51fa\u53bb\u6253\u542c\uff0c\u5e76\u6ca1\u6709\u50cf\u4ed6\u6240\u8bf4\u7684\u90a3\u6837\u5ba2\u5546\u5728\u9014\u4e2d\u8feb\u5bb3\uff0c\u56de\u6765\u518d\u76d8\u95ee\u4ed6\uff0c\u4ed6\u624d\u8bf4\u51fa\u5b9e\u8bdd\u3002\u539f \u6765\u4ed6\u81ea\u5df1\u624d\u662f\u76d7\u5e2e\u4e2d\u4eba\uff0c\u4ed6\u90a3\u4e00\u515a\u4ee5\u524d\u52ab\u9556\uff0c\u66fe\u6740\u4e86\u664b\u9633\u9556\u7684\u5927\u9556\u5934\uff0c\u5927\u9556\u5934\u7684\u5bb6\u4eba\u8bf7 \u4e86\u4e00\u4f4d\u6781\u6709\u672c\u9886\u7684\u4eba\u51fa\u6765\u8ffd\u6355\u4ed6\u4eec\uff0c\u5c06\u4ed6\u8fd9\u4e2a\u76d7\u5e2e\u5f1f\u6740\u4e86\u5341\u4e4b\u4e03\u4eba\uff0c\u4ed6\u597d\u5728\u9003\u5f97\u5feb\uff0c\u5e78 \u5f97\u4e0d\u6b7b\u3002\u6211\u89c1\u4ed6\u80af\u8bf4\u5b9e\u8bdd\uff0c\u800c\u4e14\u53d1\u8a93\u6539\u8fc7\u81ea\u65b0\uff0c\u5fc3\u5ff5\u5ea6\u5316\u6076\u4eba\uff0c\u4e43\u662f\u4f5b\u95e8\u8981\u4e49\u3002\u56e0\u6b64\u4ecd \u7136\u5c06\u4ed6\u6536\u7559\u4e0b\u6765\uff0c\u54ea\u77e5\u7ecf\u8fc7\u4e94\u5e74\u7684\u718f\u9676\u3002\u4ed6\u4ecd\u662f\u8d3c\u6027\u4e0d\u6539\u3002\u597d\u5728\u8001\u50e7\u53d1\u89c9\u5f97\u65e9\uff0c\u8981\u4e0d\u7136 \u5c31\u5bb3\u4e86\u4f60\u4eec\u4e86\u3002\u5c45\u58eb\uff0c\u65f6\u673a\u7d27\u8feb\uff0c\u4f60\u4eec\u8fd8\u662f\u5148\u9003\u5f00\u5427\u3002\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u6211\u7b49\u4e0e\u4ee4\u5f92\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e0d\u77e5\u4ed6\u4f55\u6545\u52a0\u5bb3\uff1f\u82e5\u7136\u4ed6\u8fd8\u8981\u56de\u6765\uff0c\u90a3\u662f\u6700\u597d\u4e0d \u8fc7\uff0c\u6211\u6b63\u60f3\u95ee\u4ed6\u5462\uff01\u201d\u90a3\u8001\u548c\u5c1a\u4f3c\u4e4e\u751a\u662f\u6015\u4e8b\uff0c\u4e0d\u60f3\u674e\u9038\u518d\u7559\uff0c\u8bf4\u9053\uff1a\u201c\u6b7b\u7684\u662f\u4f60\u7684\u8001 \u4e08\u4eba\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u90a3\u8001\u548c\u5c1a\u9053\uff1a\u201c\u6211\u66ff\u4ee4\u5cb3\u5ff5\u5f80\u751f\u5492\uff0c\u4ed6\u542c\u5230\u4ee4\u5cb3\u7684\u540d\u5b57\uff0c \u66fe\u95ee\u6211\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u4e0d\u662f\u505a\u8fc7\u5927\u5b98\u7684\u90a3\u4e2a\u957f\u5b59\u5747\u91cf\uff1f\u6211\u8bf4\u6211\u4e0d\u77e5\u9053\uff0c\u4ed6\u5600\u5495\u4e86\u4e00\u9635\uff0c\u4fbf \u5f80\u9547\u4e0a\u53bb\u4e86\uff0c\u76f4\u5230\u508d\u665a\u624d\u56de\u6765\u3002\u53ef\u80fd\u4ed6\u4ee5\u4e3a\u662f\u505a\u8fc7\u5927\u5b98\u7684\u4eba\uff0c\u5fc5\u6709\u94b1\u8d22\u9057\u4e0b\uff0c\u6545\u6b64\u60f3\u8981 \u8c0b\u8d22\u5bb3\u547d\u3002\u53ea\u6015\u4ed6\u8fd8\u8981\u4e32\u540c\u76d7\u515a\u518d\u6765\uff0c\u4f60\u4eec\u8fd8\u662f\u5148\u9003\u5427\u3002\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff0c\u201c\u53ea\u662f\u60f3\u62a2\u52ab\u94b1\u8d22\uff0c\u5012\u8fd8\u4e0d\u4ff1\uff0c\u4f46\u4ed6\u77e5\u9053\u4e86\u6211\u5cb3\u7236\u7684\u8eab\u4efd\uff0c \u82e5\u7136\u60ca\u52a8\u4e86\u5b98\u5e9c\u4e2d\u4eba\uff0c\u5374\u662f\u9ebb\u70e6\u3002\u201d\u957f\u5b59\u58c1\u5728\u75c5\u69bb\u4e0a\u7ffb\u8f6c\u8eab\u5b50\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u9038\u54e5\u54e5\uff0c \u54b1\u4eec\u8fd8\u662f\u8d70\u5f00\u7684\u597d\uff0c\u514d\u5f97\u8fde\u7d2f\u4e86\u5bfa\u4e2d\u7684\u4e3b\u6301\u3002\u201d\u674e\u9038\u6c89\u541f\u534a\u664c\uff0c\u90a3\u8001\u548c\u5c1a\u731c\u5230\u4ed6\u7684\u5fc3\u610f\uff0c \u8bf4\u9053\uff1a\u201c\u5c45\u58eb\u662f\u6015\u5b7d\u5f92\u56de\u6765\uff0c\u52a0\u5bb3\u8001\u50e7\u4e48\uff1f\u8001\u50e7\u5bf9\u4ed6\u6709\u51e0\u5e74\u517b\u80b2\u4e4b\u6069\uff0c\u8c05\u4ed6\u8fd8\u4e0d\u6562\u4e0b\u6bd2 \u624b\u3002\u82e5\u662f\u5728\u5bfa\u4e2d\u95f9\u51fa\u547d\u6848\uff0c\u90a3\u5374\u662f\uff0c\u5374\u662f\u6709\u4e9b\u4e0d\u4fbf\uff01\u8001\u50e7\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u4e0d\u80fd\u4fdd\u62a4\u5c45\u58eb\uff0c \u8bf7\u5c45\u571f\u8d70\u5f00\uff0c\u5b9e\u662f\u60ed\u6127\u5f97\u5f88\u3002\u201d\u90a3\u8001\u548c\u5c1a\u5766\u767d\u7684\u8bf4\u51fa\u5fc3\u4e2d\u987e\u8651\uff0c\u674e\u9038\u4e00\u60f3\u679c\u7136\uff0c\u5373\u4f7f\u81ea \u5df1\u5b88\u5728\u5bfa\u4e2d\uff0c\u7b49\u90a3\u4e9b\u76d7\u515a\u6765\u65f6\uff0c\u6740\u5c3d\u4ed6\u4eec\uff0c\u90a3\u65f6\u81ea\u5df1\u4e00\u8d70\u4e86\u4e4b\uff0c\u8fd9\u8001\u548c\u5c1a\u5374\u8981\u89c1\u5b98\u9762\u5e9c\uff0c \u800c\u4e14\u4e8b\u60c5\u63ed\u53d1\uff0c\u4eba\u4eba\u77e5\u9053\u4ed6\u7684\u5f92\u5f1f\u4e43\u662f\u5f3a\u76d7\uff0c\u7eb5\u7136\u514d\u53d7\u682a\u8fde\uff0c\u4e5f\u4f1a\u8d25\u574f\u540d\u58f0\u3002 \u3000\u3000\u674e\u9038\u8003\u8651\u518d\u4e09\uff0c\u7ec8\u4e8e\u63a5\u53d7\u4e86\u4e3b\u6301\u7684\u529d\u544a\uff0c\u5148\u628a\u68fa\u6750\u62ac\u4e0a\u9aa1\u8f66\uff0c\u518d\u628a\u957f\u5b59\u58c1\u5728\u8f66\u53a2\u5b89 \u987f\u597d\u4e86\uff0c\u7136\u540e\u5411\u8001\u548c\u5c1a\u9053\u8c22\uff0c\u4fbf\u5373\u9a71\u8f66\u591c\u8d70\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u4e00\u5f2f\u51b7\u6708\uff0c\u6570\u70b9\u5bd2\u661f\uff0c\u674e\u9038\u4ed3\u7687\u5954\u547d\uff0c\u65e0\u9650\u8f9b\u9178\u3002\u4ed6\u5012\u4e0d\u662f\u6015 \u76d7\u5f92\u62e6\u52ab\uff0c\u800c\u662f\u6015\u957f\u5b59\u58c1\u7684\u75c5\u52a0\u91cd\u3002\u8d70\u4e86\u4e00\u7a0b\uff0c\u4f46\u542c\u5f97\u957f\u5b59\u58c1\u65f6\u4e0d\u65f6\u53d1\u51fa\u547b\u541f\u4e4b\u58f0\uff0c\u6478 \u6478\u5979\u7684\u989d\u89d2\uff0c\u70eb\u5f97\u6015\u4eba\uff0c\u674e\u9038\u6beb\u65e0\u529e\u6cd5\uff0c\u8eab\u4f53\u7075\u67a2\uff0c\u72ec\u5bf9\u75c5\u4eba\uff0c\u7f05\u6000\u8eab\u4e16\uff0c\u98d8\u96f6\u65e0\u4f9d\uff0c \u4f46\u89c9\u5e73\u751f\u906d\u9047\u4e4b\u60e8\uff0c\u83ab\u6b64\u4e3a\u751a\u3002 \u3000\u3000\u5c71\u8def\u5d0e\u5c96\uff0c\u9aa1\u8f66\u52a8\u8361\uff0c\u957f\u5b59\u58c1\u4fa7\u8f6c\u8eab\u5b50\uff0c\u786c\u54bd\u8bf4\u9053\uff1a\u201c\u9038\u54e5\u54e5\uff0c\u6211\u62d6\u7d2f\u4f60\u4e86\u3002\u201d\u674e \u9038\u7d27\u62b1\u7740\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u540c\u547d\u9e33\u9e2f\uff0c\u751f\u6b7b\u4e0e\u5171\uff0c\u4f60\u5343\u4e07\u4e0d\u53ef\u80e1\u601d\u4e71\u60f3\u3002\u201d\u957f\u5b59\u58c1\u4e27\u7236 \u4e27\u5144\uff0c\u8eab\u5728\u75c5\u4e2d\uff0c\u5374\u8fd8\u5904\u5904\u4ee5\u4ed6\u4e3a\u5ff5\uff0c\u674e\u9038\u6781\u4e3a\u611f\u52a8\u3002\u5bf9\u957f\u5b59\u58c1\u7684\u7231\u610f\uff0c\u4e0d\u89c9\u6cb9\u7136\u800c\u751f\uff0c \u8fd9\u65f6\u5a49\u513f\u548c\u6b66\u7384\u971c\u548c\u5f71\u5b50\u90fd\u5728\u957f\u5b59\u58c1\u7684\u6cea\u5149\u4e2d\u6eb6\u5316\u4e86\u3002\u674e\u9038\u4f46\u76fc\u5feb\u5feb\u5929\u660e\uff0c\u597d\u53bb\u6c42\u53d6\u8336 \u6c34\uff0c\u5e76\u8ba9\u75c5\u4eba\u6b47\u606f\u3002 \u3000\u3000\u6f2b\u6f2b\u957f\u591c\uff0c\u597d\u4e0d\u5bb9\u6613\u7b49\u5230\u4e1c\u65b9\u53d1\u767d\uff0c\u8fd9\u65f6\u5927\u7ea6\u8d70\u4e86\u4e09\u5341\u591a\u91cc\uff0c\u5230\u4e86\u4e00\u4e2a\u6797\u5b50\u65c1\u8fb9\uff0c \u674e\u9038\u521a\u521a\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u5ffd\u542c\u5f97\u6797\u4e2d\u4e00\u58f0\u5450\u558a\uff0c\u8df3\u51fa\u4e09\u4e2a\u4eba\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\uff0c\u4fbf\u662f\u90a3\u4e2a\u548c\u5c1a\u3002 \u3000\u3000\u674e\u9038\u6050\u9632\u6270\u53ca\u75c5\u4eba\uff0c\u4e0d\u5f85\u4ed6\u4eec\u8d70\u8fd1\u3002\u7acb\u5373\u4ece\u9aa1\u8f66\u4e0a\u98de\u8eab\u8dc3\u8d77\uff0c\u534a\u7a7a\u4e2d\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb \u8eab\u201d\uff0c\u8f7b\u8f7b\u5de7\u5de7\u7684\u843d\u5728\u90a3\u4e24\u4e2a\u5927\u6c49\u9762\u524d\uff0c\u90a3\u4e24\u4e2a\u5927\u6c49\u89c1\u4ed6\u8f7b\u529f\u8d85\u5353\uff0c\u5fae\u5fae\u5403\u60ca\uff01\u5f53\u524d\u7684 \u90a3\u4e2a\u8c79\u5b50\u5934\u7c97\u6beb\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u957f\u5b59\u5747\u91cf\u7684\u4ec0\u4e48\u4eba\uff1f\u662f\u4ed6\u7684\u513f\u5b50\uff0c\u8fd8\u662f\u4ed6\u7684\u95e8\u4eba\uff1f\u201d \u674e\u9038\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u53ef\u662f\u4f0f\u864e\u5e2e\u7684\u7a0b\u5c11\u5e2e\u4e3b\u4e48\uff1f\u6211\u62a4\u9001\u5cb3\u7236\u7075\u8f66\u56de\u6545\u91cc\uff0c\u4e0d\u77e5\u6709\u4ec0\u4e48 \u4e8b\u60c5\u5f97\u7f6a\u4e86\u8d35\u5e2e\uff1f\u201d\u8fd9\u8c79\u5b50\u5934\u7c97\u8c6a\u6c49\u5b50\u62ff\u7740\u7684\u662f\u4e00\u5bf9\u70b9\u7a74\u6485\uff0c\u6b66\u6797\u4e2d\u6709\u53e5\u8bdd\u8bf4\uff1a\u201c\u4e00\u5bf8 \u77ed\uff0c\u4e00\u5bf8\u9669\u201d\uff0c\u5404\u6d3e\u70b9\u7a74\u540d\u5bb6\uff0c\u6240\u7528\u7684\u70b9\u7a74\u5c31\u6700\u591a\u4e0d\u8fc7\u662f\u4e8c\u5c3a\u4e00\u5bf8\uff0c\u8fd9\u662f\u56e0\u4e3a\u7528\u4f5c\u70b9\u7a74 \u7684\u5175\u5668\uff0c\u8d8a\u77ed\u5c31\u8d8a\u663e\u5f97\u529f\u592b\u7684\u9ad8\u5f3a\uff1b\u53ea\u6709\u4f0f\u864e\u5e2e\u7528\u7684\u70b9\u7a74\u6485\uff0c\u5374\u662f\u957f\u8fbe\u4e09\u5c3a\u516d\u5bf8\uff0c\u4ed6\u4eec \u8bf4\u7684\u662f\uff1b\u201c\u4e00\u5bf8\u957f\uff0c\u4e00\u5bf8\u5f3a\u3002\u201d\u6240\u7528\u7684\u70b9\u5927\u6485\u4e24\u8fb9\u950b\u5229\uff0c\u8fd8\u53ef\u4ee5\u5f53\u4f5c\u4e94\u884c\u5251\u4f7f\uff0c\u6253\u9020\u6837 \u5f0f\uff0c\u4e5f\u4e0e\u5404\u5bb6\u5404\u6d3e\u5927\u4e0d\u76f8\u540c\u3002\u4f0f\u864e\u5e2e\u7684\u8001\u5e2e\u4e3b\u7a0b\u8fbe\u82cf\u4eca\u5df2\u516d\u5341\u591a\u5c81\uff0c\u674e\u9038\u4e00\u89c1\u8fd9\u4e2a\u7c97\u6beb \u6c49\u5b50\u6240\u4f5c\u7684\u70b9\u7a74\u6485\u957f\u8fbe\u4e09\u5c3a\u516d\u5bf8\uff0c\u4fbf\u77e5\u9053\u4ed6\u662f\u4f0f\u864e\u5e2e\u7684\u9ad8\u624b\uff0c\u6545\u6b64\u51fa\u8a00\u8bd5\u63a2\uff0c\u95ee\u4ed6\u662f\u5426 \u4f0f\u864e\u5e2e\u7684\u5c11\u5e2e\u4e3b\u3002 \u3000\u3000\u8fd9\u7c97\u6beb\u6c49\u5b50\u6b63\u662f\u4f0f\u864e\u5e2e\u7684\u5c11\u5e2e\u4e3b\u7a0b\u5efa\u7537\uff0c\u89c1\u674e\u9038\u4e00\u53e3\u9053\u51fa\u4ed6\u7684\u6765\u5386\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3 \u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u7684\u773c\u529b\u771f\u9ad8\uff0c\u4e0d\u50cf\u4e2a\u521d\u51fa\u9053\u7684\u96cf\u513f\u3002\u201d\u5f53\u4e0b\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u539f\u6765\u9601\u4e0b\u662f\u957f\u5b59 \u5747\u91cf\u7684\u7231\u5a7f\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u5cb3\u7236\u524d\u534a\u751f\u5728\u671d\u4e3a\u5b98\uff0c\u540e\u534a\u751f\u9690\u8ff9\u5c71\u6797\uff0c\u4e0e \u6c5f\u6e56\u597d\u6c49\u7d20\u4e4f\u6765\u5f80\uff0c\u60f3\u6765\u4e0d\u81f3\u4e8e\u4e0e\u8d35\u5e2e\u7ed3\u6709\u6881\u5b50\uff1f\u4e0d\u77e5\u5c11\u5be8\u4e3b\u4f55\u4ee5\u8981\u62e6\u963b\u7075\u8f66\uff1f\u201d\u7a0b\u5efa \u7537\u9053\uff1a\u201c\u9601\u4e0b\u8bf4\u5f97\u4e0d\u9519\uff0c\u957f\u5b59\u5927\u4eba\u786e\u662f\u4e0e\u655d\u5e2e\u65e0\u4ec7\u6028\u3002\u6211\u4eec\u4e5f\u4e0d\u6562\u62e6\u963b\u4ed6\u7684\u7075\u8f66\uff0c\u4e0d\u8fc7 \u60f3\u5411\u9601\u4e0b\u501f\u4e00\u4ef6\u4e1c\u897f\uff0c\u9601\u4e0b\u82e5\u7136\u80af\u501f\uff0c\u6211\u4eec\u8fd8\u8981\u5411\u8001\u5927\u4eba\u7684\u7075\u8f66\u53e9\u5934\u81f4\u8c22\u3002\u201d\u674e\u9038\u9053\uff1a \u201c\u655d\u5cb3\u7236\u4e24\u8896\u6e05\u98ce\uff0c\u82e5\u662f\u5404\u4f4d\u6025\u9700\u7684\u8bdd\uff0c\u4e09\u4e94\u767e\u4e24\u94f6\u5b50\uff0c\u5c0f\u53ef\u8fd8\u53ef\u4ee5\u5949\u9001\u3002\u201d\u7a0b\u5efa\u7537\u54c8 \u54c8\u7b11\u9053\uff1a\u201c\u6211\u4eec\u505a\u7684\u867d\u662f\u6ca1\u672c\u94b1\u7684\u751f\u610f\uff0c\u5374\u8fd8\u4e0d\u81f3\u4e8e\u5411\u9601\u4e0b\u501f\u76d8\u7f20\u3002\u9601\u4e0b\u672a\u514d\u592a\u5c0f\u770b\u4eba \u4e86\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u90a3\u4e48\u8bf7\u95ee\u5c11\u5e2e\u4e3b\u8981\u501f\u4ec0\u4e48\uff1f\u201d\u7a0b\u5efa\u7537\u9053\uff1a\u201c\u957f\u5b59\u5927\u4eba\u4e43\u662f\u4e00\u4ee3\u7684\u5251\u672f \u540d\u5bb6\uff0c\u60f3\u5fc5\u9020\u6709\u62f3\u7ecf\u5251\u8c31\u3002\u9601\u4e0b\u6b66\u529f\u5df2\u5c3d\u8db3\u9632\u8eab\uff0c\u60f3\u6765\u4e5f\u4e0d\u9700\u8981\u5728\u6c5f\u6e56\u4e0a\u6df7\u996d\u5403\uff0c\u8fd9\u5251 \u8c31\u561b\uff0c\u5bf9\u6211\u4eec\u6c5f\u6e56\u4e0a\u4eba\u7269\u5374\u662f\u5f88\u6709\u7528\u5904\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u539f\u6765\u8bf8\u4f4d\u60f3\u501f\u5251\u8c31\u6765\u7684\uff0c\u655d\u5cb3\u6613 \u8d3a\u4e4b\u65f6\uff0c\u672a\u66fe\u4ea4\u4ee3\uff0c\u5c0f\u53ef\u5b9e\u662f\u4e0d\u77e5\u3002\u201d\u7a0b\u5efa\u7537\u51b7\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8bf7\u8ba9\u6211\u4eec\u4ee3\u4f60\u641c\u5bfb \u5982\u4f55\uff1f\u201d\u4e00\u6b65\u6b65\u4fbf\u60f3\u4e0a\u8f66\u53bb\u641c\uff0c\u674e\u9038\u8eab\u5f62\u4e00\u6643\uff0c\u5c06\u4ed6\u62e6\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5cb3\u7236\u5c38\u9aa8\u672a\u5bd2\uff0c \u53ef\u4e0d\u613f\u8ba9\u4eba\u60ca\u52a8\u3002\u201d\u7a0b\u5efa\u7537\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4e0d\u8ba9\u641c\uff1f\u8bf4\u4e0d\u5f97\u53ea\u597d\u786c\u501f\u4e86\u3002\u516c\u5b50\u53ef\u522b\u5acc\u6211 \u4eec\u8349\u83bd\u4e4b\u4eba\u4e0d\u61c2\u793c\u8c8c\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u70b9\u7a74\u621f\u5de6\u53f3\u4e00\u5206\u3002\u53cc\u70b9\u674e\u9038\u7684\u201c\u671f\u95e8\u7a74\u201d\uff0c\u674e\u9038\u62d4 \u51fa\u5b9d\u5251\uff0c\u7acb\u523b\u548c\u4ed6\u4eec\u62fc\u6597\u8d77\u6765\u3002 \u3000\u3000\u7a0b\u5efa\u7537\u4e0d\u4f46\u6240\u7528\u7684\u5175\u5668\u7279\u522b\uff0c\u70b9\u7a74\u7684\u624b\u6cd5\u4e5f\u786e\u5b9e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u4e00\u822c\u7684\u70b9\u7a74\u540d\u5bb6\uff0c\u7eb5 \u7136\u51fa\u624b\u8fc5\u6377\uff0c\u53ef\u4ee5\u5728\u4e00\u62db\u4e4b\u95f4\uff0c\u540c\u65f6\u70b9\u51e0\u5904\u7a74\u9053\uff0c\u4f46\u6240\u70b9\u7684\u7a74\u9053\uff0c\u5374\u5fc5\u662f\u805a\u5b9a\u5728\u9644\u8fd1\u7684\uff1b \u4ed6\u6240\u7528\u7684\u70b9\u7a74\u671d\uff0c\u56e0\u4e3a\u5c3a\u5bf8\u7279\u522b\u957f\uff0c\u653b\u51fb\u7684\u8303\u56f4\u4fbf\u5e7f\u9614\u7684\u591a\uff0c\u5e38\u5e38\u5728\u4e00\u62db\u4e4b\u95f4\uff0c\u65e2\u70b9\u624b \u8155\u7684\u201c\u5173\u5143\u7a74\u201d\u201d\uff0c\u53c8\u70b9\u80f8\u90e8\u7684\u201c\u7487\u8ba5\u7a74\u201d\uff0c\u968f\u7740\u8eab\u5f62\u6b65\u6cd5\u7684\u53d8\u6362\uff0c\u6709\u65f6\u751a\u81f3\u8fd8\u70b9\u5230\u817f \u809a\u7684\u201c\u73af\u8df3\u7a74\u201d\uff0c\u4e0a\u76d8\u3001\u4e2d\u76d8\u3001\u4e0b\u76d8\u4e09\u5904\u7684\u5927\u7a74\uff0c\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u51e0\u4e4e\u5168\u90e8\u90fd\u70b9\u5230\uff0c\u5f53\u771f \u662f\u9632\u4e0d\u80dc\u9632\u3002 \u3000\u3000\u674e\u9038\u51dd\u795e\u5e94\u4ed8\uff0c\u5728\u672a\u719f\u6089\u5bf9\u65b9\u7684\u70b9\u7a74\u7684\u624b\u6cd5\u4e4b\u524d\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u4ed6\u7684\u5251\u6cd5\u662f\u5f53\u4e16\u6570\u4e00 \u6570\u4e8c\u7684\u5251\u672f\u540d\u5bb6\u6b27\u9633\u70af\u6240\u6388\uff0c\u672c\u6765\u5c31\u4ee5\u7ef5\u5bc6\u89c1\u957f\uff0c\u66f4\u517c\u4ed6\u7684\u5251\u4e43\u662f\u5927\u5185\u5b9d\u5251\uff0c\u7a0b\u5efa\u7537\u7684 \u70b9\u7a74\u6485\u8fd8\u5f53\u771f\u4e0d\u6562\u548c\u4ed6\u786c\u78b0\uff0c\u674e\u9038\u5c55\u5f00\u4e86\u9632\u8eab\u5251\u672f\u6cd5\uff0c\u4f46\u89c1\u94f6\u5149\u62a4\u4f53\uff0c\u7d2b\u96f7\u98de\u7a7a\uff0c\u5c31\u4f3c \u5728\u8eab\u5b50\u7684\u5468\u56f4\u3002\u780c\u8d77\u4e86\u94dc\u5899\u94c1\u58c1\u4e00\u822c\uff0c\u7a0b\u5efa\u7537\u7684\u70b9\u7a74\u624b\u6cd5\u867d\u7136\u5947\u8be1\u767e\u51fa\uff0c\u5374\u662f\u65e0\u9699\u53ef\u5165\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u674e\u9038\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u7a81\u7136\u51fa\u624b\u53cd\u51fb\uff0c\u4e00\u4e2a\u201c\u9e5e\u5b50\u53cd\u8eab\u201d\uff0c\u53cc\u81c2\u201c\u91d1\u96d5\u5c55\u7fc5\u201d\u3002 \u5b9d\u5251\u75be\u524a\u654c\u4eba\u819d\u76d6\u3002\u7a0b\u5efa\u7537\u6025\u7528\u201c\u6885\u82b1\u843d\u5730\u201d\u5f0f\u5411\u4e0b\u6251\u8eab\uff0c\u5de6\u624b\u7684\u70b9\u7a74\u6485\u5f53\u4f5c\u4e94\u884c\u5251\u4f7f\uff0c \u4e00\u62db\u6a2a\u67b6\u91d1\u6881\uff0c\u6311\u8d77\u4e86\u674e\u9038\u7684\u5b9d\u5251\uff0c\u53f3\u624b\u70b9\u7a74\u6485\u4fbf\u70b9\u674e\u9038\u817f\u809a\u7684\u201c\u73af\u8df3\u7a74\u201d\uff01\u674e\u9038\u8fd9\u4e00 \u62db\u7a81\u7136\u53cd\u51fb\uff0c\u65e9\u5df2\u6599\u5230\u4ed6\u5fc5\u7136\u8981\u5982\u6b64\u51fa\u62db\uff0c\u63d0\u811a\u4e00\u8e39\uff0c\u5c06\u4ed6\u7684\u70b9\u7a74\u6485\u8e39\u4e0b\uff0c\u5251\u5149\u5410\u5904\uff0c \u5530\u7684\u4e00\u58f0\uff0c\u523a\u7a7f\u4e86\u4ed6\u62a4\u80a9\u7684\u8f6f\u7532\uff0c\u8fd9\u8fd8\u662f\u56e0\u4e3a\u674e\u9038\u4e0d\u613f\u4e0e\u4f0f\u864e\u5e2e\u7ed3\u4ec7\uff0c\u8981\u4e0d\u7136\u53ea\u8981\u523a\u4f4e \u4e00\u5bf8\uff0c\u7a0b\u5efa\u7537\u7684\u7435\u7436\u9aa8\u4fbf\u8981\u6d1e\u7a7f\uff0c\u8fd9\u8eab\u6b66\u529f\u4e5f\u8981\u5e9f\u4e86\u3002 \u3000\u3000\u674e\u9038\u62b1\u5251\u8bf4\u9053\uff1a\u201c\u627f\u8ba9\u4e00\u62db\uff0c\u5c11\u5e2e\u4e3b\u53ef\u4ee5\u8ba9\u6211\u5cb3\u7236\u7684\u7075\u8f66\u8fc7\u53bb\u4e86\u5427\uff1f\u201d\u7167\u6c5f\u6e56\u7684\u89c4 \u77e9\uff0c\u7a0b\u5efa\u7537\u65e2\u7136\u8f93\u4e86\uff0c\u7406\u8be5\u8ba9\u9053\uff0c\u54ea\u77e5\u4ed6\u5374\u5168\u7136\u4e0d\u7406\u4f1a\u8fd9\u4e00\u5957\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u5251\u6cd5 \u679c\u7136\u7cbe\u5999\uff0c\u60f3\u5fc5\u662f\u51fa\u4e8e\u957f\u5b59\u5927\u4eba\u751f\u524d\u4eb2\u6388\u5427\uff1f\u8fd9\u66f4\u4ee4\u6211\u4ef0\u6155\u4e86\u3002\u97e9\u5927\u54e5\uff0c\u5e76\u80a9\u5b50\u4e0a\u554a\uff01 \u5bf9\u4e0d\u4f4f\uff0c\u54b1\u4eec\u5fd7\u5728\u53d6\u5f97\u5251\u8c31\uff0c\u53ef\u4e0d\u80fd\u6309\u6b66\u6797\u51b3\u6597\u7684\u89c4\u77e9\uff0c\u53ef\u8981\u501a\u591a\u4e3a\u80dc\u4e86\uff01\u201d \u3000\u3000\u90a3\u4e2a\u4f7f\u957f\u97ad\u7684\u6c49\u5b50\u5e94\u58f0\u800c\u4e0a\uff0c\u957f\u97ad\u4e00\u6296\uff0c\u7acb\u523b\u4fbf\u662f\u201c\u8fde\u73af\u4e09\u97ad\u201d\u201c\u56de\u98ce\u626b\u67f3\u201d\u7684\u7edd \u6280\uff0c\u5506\u3001\u5506\uff01\u98ce\u58f0\u547c\u54cd\uff0c\u5377\u8d77\u4e00\u56e2\u97ad\u5f71\uff0c\u75be\u626b\u8fc7\u6765\u3002\u674e\u9038\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138 \u7684\u5f3a\u76d7\uff01\u201d\u5251\u5149\u970d\u970d\uff0c\u4e5f\u5c55\u5f00\u4e86\u4e00\u6d3e\u9ad8\u624b\u7684\u62db\u6570\u3002 \u3000\u3000\u8fd9\u59d3\u5468\u7684\u6c49\u5b50\u662f\u4f0f\u864e\u5e2e\u8001\u5e2e\u4e3b\u7a0b\u8fbe\u82cf\u7684\u5f97\u610f\u5f1f\u5b50\uff0c\u56e0\u4ed6\u8eab\u6750\u9b41\u4f1f\uff0c\u8f7b\u529f\u7a0d\u5dee\uff0c\u4e0d\u5b9c \u5b66\u70b9\u7a74\u7684\u529f\u592b\uff0c\u7a0b\u8fbe\u82cf\u6539\u4f20\u4e86\u4ed6\u4e00\u8def\u201c\u964d\u9f99\u97ad\u6cd5\u201d\uff0c\u4f0f\u864e\u5e2e\u4ee5\u201c\u964d\u9f99\u97ad\u6cd5\u201d\uff0c\u201c\u4f0f\u9f99\u638c \u6cd5\u201d\u4e0e\u201c\u957f\u8170\u70b9\u7a74\u6cd5\u201d\u5e76\u79f0\u6b66\u6797\u4e09\u7edd\uff0c\u8fd9\u59d3\u5468\u7684\u6c49\u5b50\u97ad\u957f\u529b\u5927\uff0c\u964d\u9f99\u97ad\u6cd5\u4f7f\u5c06\u5f00\u6765\uff0c\u9690 \u9690\u6289\u6709\u98ce\u96f7\u4e4b\u58f0\uff1b\u5a01\u529b\u5947\u5927\uff0c\u674e\u9038\u4ee5\u4e00\u654c\u4e8c\uff0c\u867d\u7136\u4e0d\u81f3\u843d\u8d25\uff0c\u5374\u4e5f\u751a\u611f\u5403\u529b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u4e2a\u70e7\u706b\u548c\u5c1a\u4e5f\u6251\u4e0a\u6765\u4e86\uff0c\u4e0d\u8fc7\u4ed6\u4e0d\u662f\u6251\u5411\u674e\u9038\uff0c\u800c\u662f\u6251\u5411\u9aa1\u8f66\u3002 \u3000\u3000\u8fd9\u4e2a\u70e7\u706b\u548c\u5c1a\u540d\u53eb\u201c\u53bb\u5b7d\u201d\uff0c\u4e43\u662f\u5bfa\u4e2d\u7684\u8001\u4e3b\u6301\u77e5\u9053\u4ed6\u7684\u6765\u5386\u4e4b\u540e\uff0c\u66ff\u4ed6\u53d6\u8fd9\u4e2a\u540d \u5b57\u7684\uff0c\u7528\u610f\u5c31\u662f\u8981\u4ed6\u53bb\u6076\u4ece\u5584\uff0c\u6d88\u9664\u8fc7\u53bb\u7684\u7f6a\u5b7d\u3002\u5c82\u77e5\u4ed6\u6076\u6027\u672a\u6539\uff0c\u524d\u5b7d\u672b\u9664\uff0c\u53c8\u591a\u4e00 \u5b7d\u3002\u4ed6\u4ee5\u524d\u5728\u4f0f\u864e\u5e2e\u4e2d\uff0c\u56e0\u4e3a\u806a\u660e\u4f36\u4fd0\uff0c\u751a\u5f97\u5e2e\u4e3b\u559c\u7231\uff0c\u8fd9\u51e0\u5e74\u88ab\u8001\u4e3b\u6301\u8feb\u4ed6\u5728\u5bfa\u4e2d\u6e05 \u4fee\uff0c\u672c\u5df2\u5341\u5206\u96be\u8010\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u5c11\u5e2e\u4e3b\u7a0b\u5efa\u7537\u521a\u597d\u5728\u4ed6\u5230\u5c0f\u9547\u4e70\u68fa\u6750\u7684\u65f6\u5019\uff0c\u8def\u8fc7\u6b64\u5730\uff0c \u78b0\u89c1\u4e86\u4ed6\uff0c\u4e00\u542c\u8bf4\u662f\u957f\u5b59\u5747\u91cf\u75c5\u6b81\u4ed6\u7684\u5bfa\u4e2d\uff0c\u767b\u65f6\u8d77\u4e86\u6518\u593a\u5251\u8c31\uff0c\u52ab\u63a0\u9057\u7269\u4e4b\u610f\uff0c\u6388\u8ba1 \u53eb\u4ed6\u7528\u8499\u6c57\u836f\u8ff7\u5012\u674e\u9038\u548c\u957f\u5b59\u58c1\uff0c\u5374\u4e0d\u6599\u88ab\u8001\u548c\u5c1a\u649e\u7834\u3002\u53bb\u5b7d\u9003\u4e86\u51fa\u6765\uff0c\u62a5\u77e5\u7a0b\u5efa\u7537\uff0c \u8bf4\u9053\u674e\u9038\u7684\u6b66\u529f\u5341\u5206\u5389\u5bb3\uff0c\u7a0b\u5efa\u7537\u4e5f\u6709\u70b9\u987e\u5fcc\uff0c\u56e0\u6b64\u518d\u53bb\u9080\u4e86\u4ed6\u4eec\u5e2e\u4e2d\u59d3\u5468\u7684\u8fd9\u4e2a\u6c49\u5b50 \u6765\uff0c\u76f4\u5230\u5929\u660e\u65f6\u5206\uff0c\u624d\u8d76\u5230\u6765\u62e6\u622a\u9aa1\u8f66\u3002\u8fd9\u65f6\u53bb\u5b7d\u89c1\u674e\u9038\u5df2\u88ab\u7a0b\u5468\u4e8c\u4eba\u7f20\u7740\uff0c\u77e5\u9053\u8f66\u4e2d \u53ea\u6709\u4e00\u4e2a\u5367\u75c5\u7684\u5973\u5b50\uff0c\u4e0d\u8db3\u754f\u60e7\uff0c\u4e00\u60f3\u673a\u4e0d\u53ef\u5931\uff0c\u4fbf\u7acb\u523b\u6251\u5411\u9aa1\u8f66\uff0c\u8981\u60f3\u4e0a\u8f66\u641c\u7d22\u3002 \u3000\u3000\u674e\u9038\u89c1\u6b64\u60c5\u5f62\uff0c\u53c8\u60ca\u53c8\u6025\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u5c0f\u79c3\u8d3c\uff0c\u4f60\u6562\u60ca\u52a8\u8f66\u4e2d\u7684\u75c5\u4eba\uff0c\u6211\u51b3\u4e0d\u9976 \u4f60\u6027\u547d\uff01\u201d\u7a0b\u5efa\u7537\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u6027\u547d\u5df2\u5728\u6211\u4eec\u638c\u63e1\u4e4b\u4e2d\uff0c\u8fd8\u6562\u53e3\u51fa\u5927\u8a00\uff0c\u6768 \u948a\uff0c\u4e0d\u8981\u6015\u4ed6\uff0c\u4e0a\u8f66\u641c\u5427\uff01\u201d\u6768\u948a\u4e43\u662f\u53bb\u5b7d\u7684\u4fd7\u5bb6\u540d\u5b57\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4ed6\u5df2\u6251\u5230\u4e86\u9aa1\u8f66\u7684 \u524d\u9762\uff0c\u4e00\u53ea\u811a\u5df2\u7ecf\u8e0f\u4e0a\u53bb\u4e86\u3002 \u3000\u3000\u674e\u9038\u53cd\u624b\u4e00\u5251\uff0c\u4e00\u62db\u201c\u795e\u9f99\u6389\u5c3e\u201d\uff0c\u8361\u5f00\u4e86\u7a0b\u5efa\u7537\u7684\u70b9\u7a74\u6485\uff0c\u4fbf\u5f85\u593a\u8def\u5954\u51fa\uff0c\u90a3\u59d3 \u5468\u7684\u6c49\u5b50\u4e00\u6296\u957f\u97ad\uff0c\u65e9\u5df2\u62e6\u8170\u626b\u5230\uff0c\u97ad\u98ce\u52b2\u6025\uff0c\u674e\u9038\u4e0d\u5f97\u4e0d\u659c\u95ea\u907f\u5f00\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u7a0b\u5efa\u7537\u7684\u4e00\u5bf9\u70b9\u7a74\u6485\u53c8\u5df2\u6251\u5230\uff0c\u5de6\u70b9\u201c\u65af\u95e8\u7a74\u201d\uff0c\u53f3\u70b9\u201c\u7cbe\u767d\u7a74\u201d\uff0c\u529b\u731b\u62db\u5feb\uff0c\u674e\u9038\u4e3a \u52bf\u6240\u8feb\uff0c\u53ea\u5f97\u518d\u6b21\u659c\u8eab\u4fa7\u6b65\uff0c\u907f\u654c\u6b63\u950b\uff0c\u8fd9\u6837\u7684\u76f8\u4e92\u7ea0\u7f20\uff0c\u62db\u62db\u9669\u7edd\uff0c\u674e\u9038\u4ed7\u7740\u7cbe\u5999\u7684 \u5251\u6cd5\uff0c\u867d\u7136\u5f97\u4ee5\u4e0d\u4f24\uff0c\u53ef\u662f\u79bb\u5f00\u9aa1\u8f66\u5374\u66f4\u8fdc\u4e86\u3002 \u3000\u3000\u53bb\u5b7d\u4e0a\u9aa1\u8f66\uff0c\u5f97\u610f\u4e4b\u6781\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u6b21\u82e5\u641c\u5230\u5251\u8c31\uff0c\u6211\u7acb\u6b64\u5927\u529f\uff0c\u56de\u5230\u5e2e\u4e2d\uff0c\u6700\u5c11 \u53ef\u4ee5\u5347\u4efb\u4e00\u4e2a\u5206\u8235\u8235\u4e3b\u3002\u201d\u521a\u521a\u624b\u63ed\u8f66\u5e18\uff0c\u5ffd\u5730\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u659c\u523a\u91cc\u5c04\u6765\u4e00\u652f\u51b7\u7bad\uff0c \u6b63\u4e2d\u4ed6\u7684\u624b\u8155\uff0c\u767b\u65f6\u4e00\u4e2a\u5012\u683d\u8471\u8dcc\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u674e\u9038\u6b63\u5728\u60c5\u6025\u62fc\u547d\uff0c\u4ed6\u4e00\u5251\u5212\u7834\u4e86\u7a0b\u5efa\u7537\u7684\u81c2\u818a\uff0c\u81ea\u5df1\u7684\u811a\u8e1d\u4e5f\u4e2d\u59d3\u5468\u7684\u4e00\u97ad\uff0c\u5c31 \u5728\u6b64\u65f6\uff0c\u4fbf\u542c\u5230\u4e86\u90a3\u5c0f\u548c\u5c1a\u60ca\u8bb6\u5760\u5730\u7684\u58f0\u97f3\u3002\u674e\u9038\u773c\u5149\u4e00\u77a5\uff0c\u4f46\u89c9\u4e00\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u9ec4\u8863 \u5c11\u5973\uff0c\u75be\u5954\u800c\u6765\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u674e\u9038\u53c8\u63a0\u53c8\u559c\uff0c\u5982\u5e7b\u5982\u68a6\uff0c\u51e0\u4e4e\u5fd8\u8bb0\u4e86\u51fa\u62db\u3002\u8fd9\u4e2a\u5c11\u5973\u6b63 \u662f\u540d\u53eb\u5982\u610f\u7684\u90a3\u4e2a\u7384\u971c\u7684\u5c0f\u4e2b\u73af\uff01\u6b66\u7384\u971c\u5927\u95f9\u5ce8\u5d4b\u5c71\u7684\u82f1\u96c4\u4f1a\u65f6\uff0c\u5c31\u66fe\u6709\u5979\u4e00\u4efd\u3002 \u3000\u3000\u7a0b\u89c1\u7537\u89c1\u6765\u8005\u662f\u4e2a\u5c0f\u4e2b\u73af\uff0c\u7565\u611f\u610f\u5916\uff0c\u5fc3\u4e2d\u8fd8\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4ed6\u4e58\u7740\u674e\u9038\u62db\u6570\u7a0d\u7f13\uff0c\u70b9 \u7a74\u7f34\u4e58\u9699\u5373\u8fdb\uff0c\u4e00\u62db\u4e4b\u95f4\uff0c\u8fde\u70b9\u674e\u9038\u7684\u201c\u795e\u9053\u201d\u201c\u5c06\u53f0\u201d\u201c\u7075\u67a2\u201d\u4e09\u5904\u5927\u7a74\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4f46\u542c\u5f97\u91d1\u5200\u5288\u98ce\u4e4b\u58f0\uff0c\u5982\u610f\u7684\u5251\u5c16\u4e5f\u5df2\u6307\u5230\u4e86\u7a0b\u5efa\u7537\u80cc\u5fc3\u7684\u201c\u5f52\u88c1\u7a74\u201d\uff0c \u7a0b\u5efa\u7537\u662f\u70b9\u7a74\u597d\u624b\uff0c\u8bc6\u5f97\u5386\u5bb3\uff0c\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u4e00\u4e2a\u5c0f\u4e2b\u5934\u5c45\u7136\u4e5f\u6709\u8fd9\u822c\u672c\u9886\uff01\u201d\u53ea\u5f97\u5206 \u51fa\u4e00\u652f\u70b9\u7a74\u6485\u6321\u5979\u8fd9\u62db\uff0c\u674e\u9038\u538b\u529b\u9aa4\u51cf\uff0c\u5b9d\u5251\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u767b\u65f6\u628a\u7a0b\u5468\u4e8c\u4eba\u90fd\u8feb\u5f00\u4e86\u3002 \u674e\u9038\u4ee5\u4e00\u654c\u4e8c\uff0c\u521a\u521a\u53ef\u4ee5\u6253\u6210\u5e73\u624b\uff0c\u52a0\u4e0a\u4e86\u4e00\u4e2a\u5982\u610f\uff0c\u81ea\u662f\u5927\u5360\u4e0a\u98ce\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u4f46\u542c \u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u7a0b\u5efa\u7537\u7684\u4e00\u652f\u70b9\u7a74\u6485\u7ed9\u674e\u9038\u7684\u5b9d\u5251\u524a\u4e3a\u4e24\u6bb5\uff0c\u90a3\u59d3\u5468\u7684\u6c49\u5b50\u8fd8\u60f3\u8d25\u4e2d \u53d6\u80dc\uff0c\u8fde\u4eba\u5e26\u97ad\u6025\u65cb\u56de\u6765\uff0c\u4e00\u62db\u201c\u795e\u9f99\u6296\u5c3e\u201d\uff0c\u97ad\u68a2\u5411\u5982\u610f\u7684\u4e0b\u4e09\u8def\u6025\u626b\uff0c\u5982\u610f\u529f\u529b\u867d \u7136\u8f83\u5f31\uff0c\u8f7b\u8eab\u7684\u672c\u9886\u5374\u8fdc\u80dc\u4e8e\u4ed6\uff0c\u8fd9\u59d3\u5468\u7684\u6c49\u5b50\u82e5\u662f\u4e0d\u5192\u9669\u6c42\u80dc\uff0c\u8fd8\u53ef\u4ee5\u652f\u6301\u4e00\u4e9b\u65f6\u5019\uff0c \u4ed6\u8fd9\u4e00\u8e81\u8fdb\uff0c\u4e0a\u4e09\u7a7a\u95e8\u6bd5\u9732\uff0c\u5982\u610f\u811a\u5c16\u4e00\u70b9\uff0c\u4f7f\u4e86\u4e2a\u201c\u71d5\u5b50\u94bb\u4e91\u201d\u7684\u8eab\u6cd5\uff0c\u957f\u5251\u51cc\u7a7a\u524a \u4e0b\uff01\u8fd9\u59d3\u5468\u7684\u6c49\u5b50\u62db\u6570\u5df2\u7ecf\u4f7f\u8001\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u957f\u97ad\u64a4\u4e0d\u56de\u6765\u3002\u8feb\u5f97\u7528\u624b\u6765\u6321\uff0c\u5251\u5149\u7ed5\u8fc7\uff0c \u4e24\u53ea\u624b\u90fd\u7ed9\u9f50\u6839\u524a\u65ad\uff0c\u6254\u4e86\u957f\u97ad\uff0c\u7acb\u523b\u98de\u9003\u3002\u7a0b\u5efa\u7537\u89c1\u52bf\u4e0d\u5999\uff0c\u4e5f\u8ddf\u7740\u9003\u8d70\u4e86\u3002 \u3000\u3000\u674e\u9038\u5b9a\u4e86\u5b9a\u795e\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u60f3\u627e\u90a3\u4e2a\u70e7\u706b\u548c\u5c1a\u3002\u54ea\u77e5\u4ed6\u5374\u4e5f\u4e56\u5de7\uff0c\u4e2d\u4e86\u5982\u610f\u7684\u4e00\u652f \u7bad\u4e4b\u540e\uff0c\u77e5\u9053\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5fc5\u8d25\u65e0\u7591\uff0c\u65e9\u5df2\u6084\u6084\u7684\u6e9c\u5165\u6797\u4e2d\u8eb2\u8d77\u6765\u4e86\u3002 \u3000\u3000\u654c\u4eba\u90fd\u5df2\u6253\u9000\uff0c\u53ef\u662f\u674e\u9038\u7684\u5fc3\u60c5\uff0c\u5374\u6bd4\u521a\u624d\u66f4\u8981\u60f6\u6050\u4e0d\u5b89\uff0c\u8fd9\u65f6\u671d\u9732\u672b\u5e72\uff0c\u671d\u9633\u521d \u4e0a\uff0c\u5982\u610f\u7ad9\u5728\u8def\u65c1\uff0c\u4e0d\u77e5\u662f\u56e0\u4e3a\u6fc0\u6218\u4e4b\u540e\u8fd8\u662f\u5fc3\u60c5\u7d27\u5f20\uff0c\u4f46\u89c1\u5979\u8138\u6cdb\u7ea2\u971e\uff0c\u5fae\u5fae\u5598\u6c14\uff0c \u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u6ce8\u89c6\u7740\u674e\u9038\uff0c\u773c\u5149\u4e2d\u4f4e\u542b\u8d23\u5907\uff0c\u66f4\u4f3c\u66ff\u4e3b\u4eba\u4e0d\u5e73\u3002\u9762\u524d\u7684\u8fd9\u4e2a\u5c0f \u4e2b\u73af\uff0c\u5728\u674e\u9038\u7684\u773c\u4e2d\uff0c\u5ffd\u4f3c\u53d8\u6210\u6b66\u7384\u971c\uff0c\u674e\u9038\u4e00\u7247\u60af\u7136\uff0c\u4e0d\u6562\u4ef0\u89c6\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u4f46\u542c\u5f97\u90a3\u5c0f\u4e2b\u73af\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60\u79bb\u5f00\u957f\u5b89\uff0c\u8d70\u5f97\u592a\u5306\u5fd9\u4e86\uff0c\u6709 \u4e00\u4ef6\u4e1c\u897f\u5fd8\u8bb0\u5e26\uff0c\u5c0f\u59d0\u53eb\u6211\u9001\u8fd8\u7ed9\u4f60\u3002\u201d\u674e\u9038\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u5982\u610f\u624b\u4e2d\u62ff\u7740\u4e00\u5177\u53e4\u7434\uff0c \u6b63\u662f\u4ed6\u90a3\u5177\u51e4\u5c3e\u7434\u3002\u90a3\u4e00\u665a\u674e\u9038\u88ab\u674e\u660e\u4e4b\u5dee\u9063\u5165\u5bab\uff0c\u62bc\u89e3\u523a\u5ba2\uff0c\u8fd9\u4e00\u5177\u53e4\u7434\u867d\u7136\u662f\u4ed6\u5fc3 \u7231\u4e4b\u7269\uff0c\u5f53\u65f6\u5374\u4e0d\u4fbf\u643a\u5e26\uff0c\u53ea\u597d\u7559\u5728\u795e\u6b66\u8425\u4e2d\uff0c\u60f3\u4e0d\u5230\u6b66\u7384\u971c\u5374\u5dee\u9063\u4e2b\u5934\u7ed9\u4ed6\u9001\u6765\u4e86\u3002 \u3000\u3000\u674e\u9038\u5fc3\u5f26\u98a4\u52a8\uff0c\u60f3\u8d77\u4e0e\u7384\u971c\u5a49\u513f\u7684\u7434\u9ad3\u76f8\u916c\uff0c\u5f26\u6b4c\u5bc4\u610f\uff0c\u800c\u4eca\u4eba\u7434\u4ff1\u5728\uff0c\u60c5\u4e49\u5df2\u7edd\uff0c \u4f46\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\u3002\u5fc3\u4e2d\u60f3\u5230\uff1a\u201c\u7384\u971c\uff0c\u7384\u971c\uff0c\u4f60\u4f55\u5fc5\u8fd8\u7ed9\u6211\u9001\u8fd9\u7434\u6765\uff1f\u201d\u5bf9\u7740 \u5982\u610f\u624b\u4e2d\u7684\u53e4\u7434\uff0c\u5ffd\u7136\u53c8\u4f3c\u89c9\u5f97\u6709\u4e9b\u9057\u61be\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u6211\u8fdc\u8d70\u5929\u6daf\uff0c\u7434\u5251\u98d8\u96f6\uff0c\u77e5\u97f3\u4f55 \u5728\uff1f\u7384\u971c\uff0c\u7384\u971c\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u6b21\u4e0d\u662f\u4f60\u4eb2\u81ea\u9001\u6765\uff1f\u524d\u540e\u5fc3\u60c5\uff0c\u77db\u76fe\u4e4b\u6781\u3002\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u6b66 \u7384\u971c\u4e5f\u66fe\u7ecf\u957f\u591c\u65e0\u7720\uff0c\u601d\u91cf\u518d\u4e09\uff0c\u6df1\u77e5\u674e\u9038\u4e0d\u4f1a\u56de\u5934\uff0c\u8fd9\u624d\u53eb\u4e2b\u73af\u53bb\u5bfb\u89c5\u4ed6\uff0c\u5e76\u9001\u56de\u53e4 \u7434\uff0c\u514d\u5f97\u81ea\u5df2\u4e0e\u4ed6\u89c1\u9762\uff0c\u5f92\u60f9\u4f24\u5fc3\u3002\u8fd9\u4e00\u756a\u60c5\u610f\uff0c\u76f4\u5230\u8bb8\u591a\u5e74\u4ee5\u540e\uff0c\u674e\u9038\u65b9\u59cb\u660e\u767d\u3002 \u3000\u3000\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u90a3\u5c0f\u4e2b\u73af\u773c\u4e2d\u4e5f\u9690\u6709\u6cea\u5149\uff0c\u4f46\u5374\u662f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60\u628a\u8fd9\u7434\u6536 \u4e86\u5427\uff0c\u6211\u8fd8\u8981\u8d76\u56de\u53bb\u3002\u5411\u5c0f\u59d0\u8986\u547d\u5462\u3002\u201d\u674e\u9038\u5fcd\u7740\u773c\u6cea\uff0c\u8f7b\u8f7b\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u8c22\u8c22\u3002\u201d\u5c06 \u53e4\u7434\u63a5\u4e86\u8fc7\u6765\uff0c\u5ffd\u89c1\u7434\u5f26\u95f4\u63d2\u6709\u4e00\u65b9\u624b\u7ee2\uff0c\u674e\u9038\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5c06\u624b\u7ee2\u6162\u6162\u5c55\u5f00\uff0c\u4f46\u89c1\u624b\u7ee2 \u4e0a\u7ee3\u7684\u662f\u4e00\u53ea\u79bb\u7fa4\u7684\u5b64\u96c1\u3002\u624b\u7ee2\u4e0b\u65b9\uff0c\u7ee3\u7684\u662f\u56db\u884c\u8bd7\uff0c\u8bd7\u9053\uff1a \u3000\u3000\u6c5f\u6e56\u7a7a\u62b1\u5e7d\u5170\u6028\uff0c \u3000\u3000\u5c82\u662f\u79bb\u9a9a\u5c48\u5b50\u5fc3\uff1f \u3000\u3000\u711a\u6cfd\u957f\u5b89\u96be\u5e76\u8bba\uff0c \u3000\u3000\u5929\u6daf\u4f55\u82e6\u4f5c\u884c\u541f\uff1f \u3000\u3000\u8bd7\u610f\u6df1\u8fdc\uff0c\u610f\u601d\u662f\u529d\u4ed6\u4e0d\u8981\u81ea\u6bd4\u5c48\u539f\uff0c\u56e0\u4e3a\u53e4\u4eca\u4e0d\u540c\uff0c\u9645\u9047\u5404\u5f02\uff0c\u5c48\u539f\u6240\u5904\u7684\u73af\u5883 \u662f\u56fd\u5f31\u541b\u5eb8\uff0c\u81ea\u5df1\u53c8\u88ab\u5978\u81e3\u8bfd\u8c24\uff0c\u4e0d\u80fd\u89c1\u7528\uff0c\u56e0\u6b64\u624d\u5fe7\u56fd\u4f24\u6c11\uff0c\u884c\u541f\u6cfd\u7554\uff0c\u611f\u201c\u4e3e\u4e16\u6df7 \u800c\u6211\u72ec\u6e05\uff0c\u4f17\u4eba\u7686\u9189\u800c\u6211\u72ec\u9192\u3002\u201d\u662f\u4ee5\u62b1\u77f3\u81ea\u6c89\uff0c\u9057\u54c0\u540e\u4e16\u3002\u8fd9\u662f\u5c48\u539f\u7684\u906d\u9047\u3002\u4f46\u5f53\u4eca \u4e4b\u4e16\uff0c\u5374\u975e\u5c48\u539f\u7684\u65f6\u4ee3\u6240\u53ef\u76f8\u6bd4\u554a\uff0c\u4f60\u53c8\u4f55\u5fc5\u5b66\u5c48\u539f\u4e00\u6837\uff0c\u98d8\u6cca\u5929\u6daf\uff0c\u60ec\u6062\u884c\u541f\u5462\uff1f \u3000\u3000\u8fd9\u9996\u8bd7\u65e2\u5bf9\u4ed6\u6e29\u67d4\u7684\u529d\u8c0f\uff0c\u53c8\u5bf9\u4ed6\u542b\u6709\u6df1\u6df1\u7684\u671f\u5f85\u3002\u674e\u9038\u6005\u6005\u60af\u60af\uff0c\u5446\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c \u53f9\u53e3\u6c14\u9053\uff1a\u201c\u70e6\u4f60\u56de\u53bb\u544a\u8bc9\u5c0f\u59d0\uff0c\u6211\u591a\u8c22\u5979\u7684\u597d\u610f\uff0c\u4eca\u751f\u4eca\u4e16\u662f\u4e0d\u80fd\u62a5\u7b54\u7684\u4e86\uff01\u201d\u4ed6\u8bf4 \u8fd9\u53e5\u8bdd\u65f6\uff0c\u50cf\u662f\u628a\u9762\u524d\u7684\u5982\u610f\u5f53\u4f5c\u662f\u4ed6\u8981\u4e0e\u4e4b\u51b3\u522b\u7684\u6b66\u7384\u971c\uff0c\u8bf4\u5f97\u771f\u60c5\u6d41\u9732\uff0c\u8f9b\u9178\u51c4\u4fa7\u3002 \u4ed6\u5fcd\u7740\u773c\u6cea\uff0c\u90a3\u5c0f\u4e2b\u73af\u5374\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u8f6c\u8eab\u4fbf\u8d70\uff0c\u674e\u9038\u5ffd\u5730\u8ffd\u4e0a\u4e24\u6b65\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98 \u5c0f\u59d0\u6709\u4ec0\u4e48\u8bdd\u7559\u7ed9\u6211\u5417\uff1f\u201d\u90a3\u5c0f\u4e2b\u73af\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u4e86\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u7a81\u7136 \u95f4\u53c8\u56de\u5934\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u5c0f\u59d0\u548c\u6211\u4eec\u5c0f\u59d0\u610f\u601d\u90fd\u662f\u4e00\u6837\u3002\u674e\u516c\u5b50\u4f60\u81ea\u5df1\u73cd\u91cd\u5427\uff0c\u6211\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u767b\u4e0a\u9aa1\u8f66\uff0c\u56de\u5934\u4e00\u671b\uff0c\u90a3\u5c0f\u4e2b\u73af\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002\u63ed\u5f00\u8f66\u5e18\uff0c\u957f\u5b59\u58c1\u597d\u50cf\u521a\u4ece\u68a6\u4e2d \u9192\u6765\uff0c\u5fae\u7b11\u95ee\u9053\uff1a\u201c\u5f3a\u76d7\u90fd\u6253\u8d70\u4e86\u5427\uff01\u201d\u5979\u6df1\u77e5\u674e\u9038\u7684\u6b66\u529f\uff0c\u4ee5\u4e3a\u62e6\u8def\u622a\u52ab\u7684\u51e0\u4e2a\u5c0f\u5f3a \u76d7\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u6240\u4ee5\u867d\u7136\u5728\u8f66\u53a2\u91cc\u542c\u5f97\u5175\u5668\u78b0\u78d5\u7684\u58f0\u97f3\u5374\u4e5f\u5e76\u4e0d\u6302\u8651\u3002\u674e\u9038\u60f3\u9053\uff1a \u201c\u4f60\u54ea\u77e5\u9053\u8fd9\u573a\u707e\u96be\u53c8\u662f\u6b66\u7384\u971c\u6551\u7684\u3002\u201d\u4e0d\u613f\u5bf9\u5979\u660e\u8a00\uff0c\u5e26\u7740\u51e0\u5206\u6127\u610f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u90fd\u6253\u8d70\u5566\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u773c\u5149\u77a5\u5904\uff0c\u53d1\u73b0\u4ed6\u8eab\u8fb9\u90a3\u5177\u53f3\u7434\uff0c\u6709\u70b9\u8be7\u5f02\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u5f3a\u76d7\u4e2d\u6709\u5973\u7684\u4e48\uff1f\u201d \u674e\u9038\u9053\uff1a\u201c\u6ca1\u6709\u554a\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u521a\u624d\u597d\u50cf\u542c\u5f97\u6709\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u548c\u4f60\u8bf4\u8bdd\u3002\u201d\u674e \u9038\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u6211\u65e2\u662f\u548c\u5979\u5b9a\u4e0b\u4e86\u592b\u59bb\u4e4b\u4efd\uff0c\u600e\u597d\u7792\u5979\uff1f\u201d\u4f46\u53c8\u6015\u5979\u75c5\u4e2d\u8bf8\u591a\u611f\u89e6\uff0c \u60f3\u4e86\u597d\u4e45\uff0c\u957f\u5b59\u58c1\u9053\uff1a\u201c\u90a3\u5973\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u9038\u54e5\u54e5\uff0c\u4f60\u8bf4\u5427\uff0c\u4ec0\u4e48\u4e8b\u60c5\u6211\u90fd\u4e0d\u4f1a\u602a\u4f60\u7684\u3002\u201d \u674e\u9038\u9053\uff1a\u201c\u90a3\u662f\u6b66\u7384\u971c\u7684\u5c0f\u4e2b\u73af\uff0c\u7ed9\u6211\u9001\u7434\u6765\u7684\u3002\u201d\u957f\u5b59\u58c1\u9762\u8272\u82cd\u767d\uff0c\u8f7b\u8f7b\u5598\u6c14\uff0c\u534a\u664c \u8bf4\u9053\uff1a\u201c\u9038\u54e5\u54e5\uff0c\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u5230\u5e95\u540e\u4e0d\u540e\u6094\uff1f\u201d\u674e\u9038\u7d27\u7d27\u5c06\u5979\u62b1\u4f4f\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u76f4\u5230 \u5982\u4eca\u8fd8\u4e0d\u76f8\u4fe1\u6211\u5417\uff1f\u6211\u6709\u4e86\u4f60\uff0c\u8fd8\u540e\u6094\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u957f\u5b59\u58c1\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u6b66\u7384\u971c\u662f\u4f60\u6700\u4f69\u670d\u7684\u5973\u4e2d\u8c6a\u6770\uff0c\u6211\u5374\u662f\u4e00\u4e2a\u5e73\u5eb8\u7684\u5973\u5b50\u3002\u55ef\uff0c \u9038\u54e5\u54e5\uff0c\u4f60\u540e\u6094\u7684\u8bdd\uff0c\u73b0\u5728\u8fd8\u6765\u5f97\u53ca\uff0c\u53ea\u8981\u518d\u62d6\u7d2f\u4f60\u51e0\u5929\uff0c\u5f85\u6211\u8eab\u4f53\u597d\u4e86\uff0c\u6211\u81ea\u4f1a\u57cb\u846c \u6211\u7236\u4eb2\u7684\u9ab8\u9aa8\uff0c\u4f60\uff0c\u4f60\u5c31\u56de\u957f\u5b89\u53bb\u5427\u3002\u201d\u674e\u9038\u4fef\u4e0b\u5934\u6765\uff0c\u773c\u775b\u51e0\u4e4e\u8d34\u5230\u5979\u7684\u8138\u4e0a\uff0c\u4f4e\u58f0 \u8bf4\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u6211\u4e0d\u7792\u4f60\uff0c\u6211\u73b0\u5728\u5bf9\u5979\u8fd8\u662f\u4f69\u670d\u7684\uff0c\u5c31\u50cf\u5bf9\u6b66\u5219\u5929\u4e00\u6837\uff0c\u4f60\u867d\u7136\u662f\u5979\u7684 \u654c\u4eba\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u4f69\u670d\u5979\u7684\u80fd\u5e72\u4e0e\u624d\u534e\u5427\uff1f\u4f46\u8fd9\u4e00\u79cd\u4f69\u670d\u4e4b\u503c\uff0c\u53c8\u600e\u80fd\u51b2\u6de1\u4e86\u56fd\u4ec7\u5bb6\u6068\uff0c \u6211\u4e0e\u4f60\u7684\u547d\u8fd0\u5df2\u7ecf\u8054\u5728\u4e00\u8d77\uff0c\u4ec0\u4e48\u4e5f\u5206\u4e0d\u5f00\u4e86\u3002\u201d\u505c\u4e86\u4e00\u505c\uff0c\u53c8\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u518d\u56de \u957f\u5b89\uff1f\u9664\u975e\u662f\u6c5f\u5c71\u6613\u4e3b\uff0c\u674e\u5510\u91cd\u5149\u3002\u8fd9\u5e0c\u671b\u5df1\u6781\u6e3a\u832b\uff0c\u8bf4\u5b9e\u5728\u8bdd\uff0c\u6211\u4e5f\u65e9\u5df2\u5fc3\u7070\u610f\u51b7\u4e86\u3002 \u58c1\u59b9\uff0c\u4f60\u4e0d\u8981\u602a\u6211\uff0c\u6211\u4eca\u540e\u662f\u4e0d\u6253\u7b97\u62a5\u4ec7\u7684\u4e86\u3002\u5c06\u6765\u57cb\u846c\u4e86\u4f60\u7236\u4eb2\u7684\u9ab8\u9aa8\uff0c\u54b1\u4eec\u5c31\u6b64\u98d8 \u6cca\u5929\u6daf\uff0c\u9ed8\u9ed8\u7ec8\u8001\u5427\u3002\u4f60\u7236\u4eb2\u662f\u5510\u5ba4\u7684\u5927\u5fe0\u81e3\uff0c\u4ed6\u4e3a\u5510\u671d\u5c3d\u5fe0\u800c\u6b7b\uff0c\u6211\u81ea\u77e5\u9053\u8ff9\u7a77\u8352\uff0c \u5b9e\u662f\u6127\u5bf9\u4e8e\u4ed6\uff0c\u4f46\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\uff1f\u58c1\u59b9\uff0c\u4f60\u539f\u8c05\u6211\u5417\uff1f\u201d \u3000\u3000\u4e00\u9897\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u4ece\u674e\u9038\u7684\u773c\u4e2d\u6d41\u51fa\u6765\uff0c\u6ef4\u5728\u957f\u5b59\u58c1\u7684\u8138\u4e0a\uff0c\u957f\u5b59\u58c1\u5fc3\u4e2d\u9635\u9635\u9178\u75db\uff0c \u4f46\u5374\u4e5f\u6709\u4e00\u4efd\u610f\u5916\u7684\u6b23\u6170\uff0c\u674e\u9038\u8bf4\u5f97\u8fd9\u6837\u8bda\u631a\uff0c\u8fd9\u6837\u660e\u767d\uff0c\u957f\u5b59\u58c1\u5bf9\u6b66\u7384\u971c\u7684\u731c\u60f3\u6682\u65f6 \u6487\u5f00\u4e86\uff0c\u662f\u554a\uff0c\u4e0d\u7ba1\u6b66\u7384\u971c\u662f\u600e\u6837\u5177\u6709\u7edd\u4e16\u6b66\u529f\uff0c\u5979\u603b\u662f\u6b66\u5219\u5929\u7684\u4f84\u5973\u513f\uff0c\u662f\u6b66\u5219\u5929\u90a3 \u8fb9\u7684\u4eba\uff0c\u90a3\u5c31\u4e07\u4e07\u4e0d\u80fd\u593a\u8d70\u5979\u7684\u674e\u9038\u54e5\u54e5\u4e86\u3002\u957f\u5b59\u58c1\u6c89\u9ed8\u534a\u664c\uff0c\u4ef0\u9762\u95ee\u9053\uff1a\u201c\u4f60\u6253\u7b97\u53bb \u54ea\u91cc\u5462\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u4f4f\u5728\u5929\u5c71\u811a\u4e0b\uff0c\u6211\u60f3\u5230\u5929\u5c71\u5357\u8def\u53bb\u6295\u9760\u4ed6\u3002\u5f85\u5230\u4f60\u660e\u5e74 \u5b5d\u670d\u6ee1\u4e86\uff0c\u5c31\u8bf7\u4ed6\u66ff\u54b1\u4eec\u4e3b\u5a5a\u3002\u5cb3\u7236\u4e34\u7ec8\u4e4b\u65f6\uff0c\u90d1\u91cd\u7684\u5c06\u4f60\u5631\u6258\u7ed9\u6211\uff0c\u6211\u4f53\u4f1a\u5f97\u4ed6\u8001\u4eba \u5bb6\u7684\u5fc3\u610f\uff0c\u6211\u60f3\u4e0d\u5fc5\u9075\u5b88\u53e4\u793c\uff0c\u5b88\u5b5d\u4e09\u5e74\u518d\u6210\u4eb2\u4e86\u3002\u201d\u957f\u5b59\u58c1\u53c8\u60b2\u53c8\u559c\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e86\u4e00 \u7247\u7ea2\u971e\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5982\u4eca\u4f60\u5df2\u662f\u6211\u552f\u4e00\u7684\u4eb2\u4eba\uff0c\u6211\u4e00\u5207\u90fd\u542c\u4f60\u7684\u4e3b\u610f\u3002\u201d\u542b\u7f9e\u4e00\u7b11\uff0c \u5f90\u5f90\u95ed\u4e0a\u773c\u775b\uff0c\u5979\u5fc3\u4e2d\u5e73\u9759\u4e0b\u6765\uff0c\u4e0d\u4e45\u5c31\u5728\u8f66\u4e2d\u7761\u719f\u4e86\u3002 \u3000\u3000\u674e\u9038\u7684\u5fc3\u53ef\u5e76\u6ca1\u6709\u5e73\u9759\uff0c\u662f\u7684\uff0c\u4ed6\u5df2\u4e0b\u4e86\u51b3\u5fc3\u4e0d\u518d\u53bb\u60f3\u6b66\u7384\u971c\u548c\u4e0a\u5b98\u5a49\u513f\uff0c\u66f4\u7edd\u4e86 \u548c\u5979\u4eec\u7ed3\u5408\u7684\u5ff5\u5934\u4e86\u3002\u53ef\u662f\u5979\u4eec\u7684\u5f71\u5b50\u8fd8\u662f\u538b\u4e0d\u4e0b\u53bb\uff0c\u79bb\u5f00\u957f\u5b89\u8d8a\u8fdc\uff0c\u674e\u9038\u5c31\u66f4\u52a0\u60d8\u6005\uff0c \u8d8a\u6765\u8d8a\u601d\u5ff5\u5979\u4eec\u3002 \u3000\u3000\u4e00\u6708\u4e4b\u540e\uff0c\u4ed6\u4eec\u9a71\u8f66\u8d70\u51fa\u4e86\u7389\u95e8\u5173\uff0c\u6b63\u662f\u51c9\u79cb\u4e5d\u6708\uff0c\u5be8\u5916\u8349\u8870\u7684\u65f6\u5019\uff0c\u773c\u524d\u9ec4\u7802\u6f20 \u6f20\uff0c\u4e00\u7247\u8352\u51c9\uff0c\u674e\u9038\u5ffd\u7136\u60f3\u8d77\u5a49\u513f\u9001\u7ed9\u4ed6\u7684\u90a3\u9996\u8bd7\uff1a\u201c\u53f6\u4e0b\u6d1e\u5ead\u521d\u3002\u601d\u541b\u4e07\u91cc\u9980\uff0c\u9732\u6d53 \u9999\u88ab\u51b7\uff0c\u6708\u843d\u9526\u5c4f\u865a\u2026\u2026\u201d\u90a3\u65f6\uff0c\u5979\u4e0d\u77e5\u81ea\u5df1\u7684\u4e0b\u843d\uff0c\u8fd8\u8981\u7ed9\u81ea\u5df1\u5bc4\u4e66\uff0c\u4eca\u540e\u53ea\u6015\u518d\u96be \u63a5\u5230\u5979\u7684\u7247\u7eb8\u53ea\u5b57\u4e86\u3002\u674e\u9038\u62bd\u51fa\u6b66\u7384\u971c\u90a3\u65b9\u624b\u7ee2\uff0c\u6084\u6084\u7684\u62ed\u4e86\u773c\u8fb9\u7684\u6cea\u73e0\uff0c\u629a\u7434\u60b2\u6b4c\uff0c \u4e0e\u957f\u5b59\u58c1\u8d70\u51fa\u4e86\u7389\u95e8\u5173\uff0c\u5728\u9ec4\u7802\u6f20\u4e4b\u4e2d\uff0c\u5404\u81ea\u6000\u7740\u4e0d\u540c\u7684\u5fc3\u60c5\uff0c\u8fce\u7740\u672a\u6765\u7684\u547d\u8fd0\u3002\u5c06\u6765 \u8fd8\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u8c01\u90fd\u96be\u4ee5\u9884\u6599\uff0c\u53ea\u6709\u90a3\u53bb\u56fd\u6000\u4e61\u7684\u65c5\u6101\uff0c\u5219\u662f\u4e24\u4eba\u90fd\u6df1\u6df1\u611f\u89e6\u5230\u4e86\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e94\u56de\u3000\u701a\u6d77\u51e4\u7802\u8ff7\u671b\u773c \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u5341\u4e94\u56de\u3000\u701a\u6d77\u51e4\u7802\u8ff7\u671b\u773c \u3000\u3000\u6625\u53bb\u6625\u6765\uff0c\u82b1\u5f00\u82b1\u843d\uff0c\u661f\u79fb\u7269\u6362\uff0c\u4e8b\u8fc7\u5883\u8fc1\uff0c\u674e\u9038\u8e0f\u51fa\u7389\u95e8\u5173\u4e4b\u540e\uff0c\u6643\u773c\u95f4\u4fbf\u8fc7\u4e86 \u516b\u5e74\u3002 \u3000\u3000\u516b\u5e74\u4e4b\u540e\uff0c\u540c\u6837\u7684\u662f\u51c9\u79cb\u4e5d\u6708\uff0c\u585e\u5916\u8349\u8870\u7684\u65f6\u5019\uff0c\u5929\u5c71\u5357\u9762\u7684\u8349\u539f\uff0c\u6765\u4e86\u9a91\u767d\u9a6c\uff0c \u9a91\u5728\u9a6c\u4e0a\u7684\u662f\u4e00\u4e2a\u6c49\u65cf\u59d1\u5a18\uff0c\u8e0f\u7740\u516b\u5e74\u524d\u674e\u9038\u8d70\u8fc7\u7684\u8def\uff0c\u5192\u7740\u6251\u9762\u98ce\u7802\uff0c\u626c\u97ad\u75be\u9a70\u3002\u5979 \u8eab\u624b\u77eb\u6377\uff0c\u9a91\u672f\u9ad8\u660e\uff0c\u795e\u60c5\u5374\u662f\u751a\u4e3a\u5fe7\u90c1\u3002\u9a6c\u97ad\u547c\u54cd\u4e4b\u4e2d\uff0c\u65f6\u4e0d\u65f6\u6742\u7740\u51e0\u58f0\u53f9\u606f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u559d\u9053\uff1a\u201c\u5973\u5b50\uff0c\u505c\u4e0b\u9a6c\u6765\uff01\u201d\u539f\u91ce\u4e0a\u98ce\u7802\u6eda\u6eda\uff0c\u9a6c\u8e44\u58f0\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c \u8f6c\u77ac\u95f4\u4fbf\u6709\u56db\u9a91\u5065\u9a6c\uff0c\u8ffd\u5230\u4e86\u8fd9\u5973\u5b50\u7684\u8ddf\u524d\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u52d2\u7740\u9a6c\u97ad\uff0c\u773c\u775b\u4e00\u626b\uff0c\u4f46\u89c1\u6765\u7684\u662f\u56db\u4e2a\u7c97\u8c6a\u7684\u7a81\u53a5\u6b66\u58eb\uff0c\u5176\u4e2d\u4e00\u4e2a\uff0c\u6b63\u5728\u5c55 \u5f00\u4e00\u5f20\u7f8a\u76ae\u753b\u5377\uff0c\u770b\u770b\u753b\u56fe\uff0c\u53c8\u770b\u770b\u5979\u3002 \u3000\u3000\u8fd9\u65f6\u4e2d\u56fd\u7684\u897f\u5317\u90e8\uff0c\u5305\u62ec\u5929\u5c71\u5357\u5317\uff0c\u90fd\u662f\u5728\u7a81\u53a5\u5927\u5e1d\u56fd\u7edf\u6cbb\u4e4b\u4e0b\uff0c\u56fd\u529b\u5f3a\u76db\uff0c\u6b66\u58eb \u8198\u608d\uff0c\u4e3e\u4e16\u77e5\u540d\u3002\u4ee5\u56db\u4e2a\u9aa0\u9a91\u6b66\u58eb\uff0c\u8ffd\u8d76\u4e00\u4e2a\u5355\u8eab\u7684\u59d1\u5a18\uff0c\u5728\u8349\u539f\u4e0a\u786e\u662f\u6781\u4e0d\u5bfb\u5e38\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u8be7\u610f\u672a\u6d88\uff0c\u53ea\u542c\u5f97\u5f53\u524d\u7684\u4e00\u4e2a\u866c\u9aef\u6b66\u58eb\u559d\u5411\u5979\u9053\uff1a\u201c\u4f60\u662f\u4ece\u5927\u5510\u6765\u7684\u5417\uff1f\u201d \u90a3\u5973\u5b50\u5fae\u7b11\u9053\uff1a\u201c\u4e2d\u56fd\u65e9\u5df2\u4e0d\u662f\u5927\u5510\u7684\u5929\u4e0b\u4e86\uff0c\u6211\u662f\u4ece\u5927\u5468\u6765\u7684\u3002\u201d\u539f\u6765\u6b66\u5219\u5929\u79f0\u5e1d\u4e4b \u540e\uff0c\u6539\u56fd\u53f7\u4e3a\u201c\u5468\u201d\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u5386\u65f6\u672a\u4e45\uff0c\u8fb9\u8fdc\u7684\u6c11\u65cf\uff0c\u4e60\u60ef\u4e0a\u8fd8\u662f\u628a\u4e2d\u56fd\u79f0\u505a\u5927\u5510\u3002 \u3000\u3000\u90a3\u7a81\u53a5\u6b66\u58eb\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u662f\u5927\u5510\u6216\u662f\u5927\u5468\uff0c\u603b\u4e4b\u4f60\u662f\u4ece\u4e2d\u56fd\u6765\u7684\uff0c\u662f\u4e48\uff1f\u201d\u90a3\u5973\u5b50 \u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u4eec\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8bf7\u5feb\u5feb\u8bf4\uff0c\u6211\u8fd8\u8981\u8d76\u8def\u3002\u201d\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u54fc\uff0c\u4e0d\u884c\u4e86\uff01 \u5feb\u968f\u6211\u4eec\u53bb\u89c1\u5927\u6c57\u5427\u3002\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6211\u72af\u4e86\u8d35\u56fd\u7684\u4ec0\u4e48\u5f8b\u4ee4\uff1f\u96be\u9053\u4ece\u4e2d\u56fd\u6765\u7684\uff0c\u5c31\u8981 \u62d8\u6355\u4e48\uff1f\u201d \u3000\u3000\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u4f60\u95ee\u6211\u4eec\u7684\u5927\u6c57\u53bb\uff01\u597d\u5440\uff0c\u4f60\u6562\u62d2\u6355\u4e48\uff1f\u4f60\u8d70\u4e0d\u8d70\uff1f\u201d\u90a3\u5973\u5b50\u67f3\u7709\u4e00 \u626c\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u8d35\u56fd\u6c11\u60c5\u6df3\u539a\uff0c\u504f\u504f\u4f60\u4eec\u5c31\u8fd9\u4e48\u4e0d\u8bb2\u7406\u3002\u201d\u90a3\u6b66\u58eb\u51b7\u7b11\u9053\uff1a\u201c\u8981\u8bb2\u7406 \u4f60\u548c\u6211\u4eec\u5927\u6c57\u8bb2\u53bb\uff01\u54fc\uff0c\u8fd8\u4e0d\u6eda\u4e0b\u9a6c\u6765\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6211\u504f\u504f\u4e0d\u8d70\uff0c\u4f60\u4eec\u7684\u5927\u6c57\u8981\u89c1 \u6211\uff0c\u5c31\u8bf7\u4ed6\u81ea\u5df1\u6765\uff01\u2019\u90a3\u7a81\u53a5\u6b66\u58eb\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5c06\u5979\u62ff\u4e0b\uff0c\u5148\u7ed9\u5979\u4e00\u987f\u76ae\u97ad\uff01\u201d \u56db\u9a91\u5065\u9a6c\u4ece\u56db\u4e2a\u65b9\u5411\u56f4\u7740\u4e86\u5979\uff0c\u56db\u6761\u957f\u97ad\u547c\u547c\u98ce\u54cd\uff0c\u770b\u770b\u5c31\u8981\u6253\u5230\u90a3\u5973\u5b50\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u6211\u7684\u654c\u624b\uff0c\u5feb\u6eda\u56de\u53bb\u5427\uff01\u201d\u53cc\u811a\u4e00\u5939\uff0c\u767d\u9a6c\u817e\u5f00\u56db\u8e44\uff0c\u731b \u7684\u5411\u524d\u51b2\u53bb\uff0c\u524d\u9762\u90a3\u4e2a\u9a91\u58eb\uff0c\u63d0\u7f30\u4e00\u95ea\uff0c\u5237\u7684\u4e00\u97ad\u626b\u4e0b\u4e00\u9a6c\u80cc\u4e0a\u5ffd\u7136\u4e0d\u89c1\u4e86\u90a3\u5973\u5b50\uff0c\u4ed6 \u7684\u5750\u9a91\u5374\u84e6\u5730\u4e00\u58f0\u60b2\u545c\uff0c\u56db\u8e44\u5c48\u5730\uff0c\u539f\u6765\u90a3\u5973\u5b50\u65e9\u5df2\u8eb2\u5728\u9a6c\u809a\u5e95\u4e0b\uff0c\u5374\u7528\u9a6c\u97ad\u6253\u65ad\u4e86\u654c \u4eba\u7684\u9a6c\u811a\u3002\u90a3\u6b66\u58eb\u8dcc\u4e0b\u9a6c\u6765\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u5176\u4ed6\u4e09\u4e2a\u6b66\u58eb\u5927\u5927\u5403\u60ca\uff0c\u6025\u5fd9\u4e0a\u524d\u622a\u51fb\u3002 \u3000\u3000\u8fd9\u6c49\u65cf\u59d1\u5a18\u77eb\u6377\u4e4b\u6781\uff0c\u95ea\u7535\u822c\u7684\u7ffb\u4e0a\u9a6c\u80cc\uff0c\u957f\u97ad\u4e00\u5377\uff0c\u201c\u556a\u5566\u201d\u4e00\u58f0\uff0c\u4fbf\u628a\u4e00\u540d\u6b66 \u58eb\u6454\u4e0b\u9a6c\u80cc\uff0c\u7b2c\u4e8c\u540d\u6b66\u58eb\u542c\u5f97\u97ad\u58f0\uff0c\u6b63\u5f85\u62db\u67b6\uff0c\u80cc\u810a\u5df2\u7740\u4e86\u4e00\u97ad\uff0c\u7b2c\u4e09\u540d\u6b66\u58eb\u7b56\u9a6c\u8eb2\u95ea\uff0c \u90a3\u5973\u5b50\u6beb\u4e0d\u653e\u677e\uff0c\u53cd\u624b\u4e00\u97ad\uff0c\u90a3\u540d\u6b66\u58eb\u88ab\u957f\u97ad\u5377\u5f97\u817e\u7a7a\u98de\u8d77\uff0c\u4ed6\u7684\u5750\u9a91\u5374\u8fd8\u672a\u77e5\u9053\u5931\u4e86 \u4e3b\u4eba\uff0c\u9a6c\u4e0d\u505c\u8e44\u7684\u5411\u524d\u76f4\u8dd1\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u56db\u4e2a\u52c7\u731b\u7684\u7a81\u53a5\u6b66\u58eb\u90fd\u88ab\u8fd9\u5973\u5b50\u6253\u5012\uff0c\u800c\u4e14\u90fd\u88ab\u5979\u7528\u97ad\u68a2\u70b9\u4e86\u9ebb\u7a74\u3002\u8fd9 \u5973\u5b50\u8df3\u4e0b\u9a6c\u6765\uff0c\u5728\u866c\u9aef\u6b66\u571f\u7684\u8eab\u4e0a\u641c\u51fa\u4e86\u4e00\u5f20\u753b\u56fe\uff0c\u53ea\u89c1\u7f8a\u76ae\u7eb8\u4e0a\u7684\u753b\u50cf\uff0c\u6b63\u662f\u5979\uff01\u5973 \u5b50\u95ee\u9053\uff1a\u201c\u8c01\u7ed9\u4f60\u8fd9\u5f20\u753b\u50cf\u7684\uff1f\u201d\u7a81\u53a5\u6b66\u58eb\u6700\u4f69\u670d\u672c\u9886\u6bd4\u4ed6\u4eec\u9ad8\u5f3a\u7684\u4eba\uff0c\u89c1\u8fd9\u5973\u5b50\u5728\u7247 \u523b\u4e4b\u95f4\u4fbf\u5c06\u4ed6\u4eec\u56db\u4e2a\u4eba\u4e00\u9f50\u51fb\u5012\uff0c\u4e0d\u6562\u4e0d\u8bf4\u5b9e\u8bdd\u3002\u90a3\u866c\u9aef\u6b66\u58eb\u9053\uff1a\u201c\u8fd9\u662f\u5927\u6c57\u5206\u53d1\u4e0b\u6765 \u7684\uff0c\u5927\u6c57\u5dee\u9063\u4e86\u4e8c\u5341\u56db\u540d\u6b66\u58eb\uff0c\u5206\u5411\u56db\u65b9\u8ffd\u5bfb\uff0c\u6bcf\u56db\u4eba\u4e00\u4e2a\u5c0f\u961f\uff0c\u4f60\u9003\u8fc7\u627e\u4eec\u8fd9\u4e00\u961f\uff0c \u540e\u9762\u8fd8\u6709\u8ffd\u5175\u5462\u3002\u4f60\u672c\u9886\u591a\u597d\uff0c\u4e5f\u9003\u4e0d\u51fa\u8fd9\u5927\u8349\u539f\u7684\uff0c\u4e0d\u5982\u968f\u6211\u4eec\u56de\u53bb\u5427\u3002\u6211\u4eec\u5c0a\u656c\u4f60 \u662f\u4f4d\u5973\u82f1\u96c4\uff0c\u51b3\u4e0d\u4f1a\u8650\u5f85\u4f60\u7684\u3002\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5927\u6c57\u4e3a\u4ec0\u4e48\u8981\u6349\u62ff\u6211\uff1f\u201d\u90a3\u6b66\u571f \u9053\uff1a\u201c\u6211\u4eec\u53ea\u662f\u5949\u547d\u800c\u884c\uff0c\u8c01\u6562\u53bb\u95ee\u5927\u6c57\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u6536\u8d77\u753b\u56fe\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u56de\u53bb\u5427\uff0c\u544a\u8bc9\u5927\u6c57\uff0c\u8bf4\u6211\u73b0\u5728\u8fd8\u6ca1\u6709\u5de5\u592b\u89c1\u4ed6\u3002 \u5f85\u6211\u7684\u4e8b\u60c5\u529e\u5b8c\u4e4b\u540e\uff0c\u4e0d\u52b3\u4ed6\u6d3e\u4eba\u6765\u8bf7\uff0c\u6211\u81ea\u5df1\u5c31\u4f1a\u53bb\u7684\u3002\u201d\u8bf4\u7f62\u5c06\u90a3\u56db\u4e2a\u6b66\u58eb\u9a6c\u80cc\u4e0a \u76db\u6c34\u7684\u76ae\u56ca\u89e3\u4e0b\uff0c\u4e00\u5171\u662f\u56db\u4e2a\u76ae\u56ca\uff0c\u8fd9\u5973\u5b50\u53d6\u53bb\u4e86\u4e09\u4e2a\u76ae\u56ca\uff0c\u53ea\u7ed9\u4ed6\u4eec\u7559\u4e0b\u4e00\u4e2a\uff0c\u8bf4\u9053\uff1a \u201c\u7559\u4e0b\u8fd9\u4e00\u888b\u6c34\uff0c\u4f60\u4eec\u56de\u53bb\u53ef\u4ee5\u591f\u7528\u4e86\u3002\u201d\u539f\u6765\u524d\u9762\u6570\u767e\u91cc\u90fd\u662f\u8352\u6f20\u5730\u5e26\uff0c\u98df\u6c34\u96be\u89c5\uff0c \u8fd9\u5973\u5b50\u53d6\u53bb\u4ed6\u4eec\u7684\u6c34\u56ca\uff0c\u4e43\u662f\u6015\u4ed6\u4eec\u7ee7\u7eed\u8ffd\u8e2a\u3002 \u3000\u3000\u90a3\u5973\u5b50\u7b56\u9a6c\u8dd1\u4e86\u4e00\u7a0b\uff0c\u4e0d\u89c1\u518d\u6709\u4eba\u8ffd\u8e2a\uff0c\u5b9a\u4e0b\u4e86\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u53ef\u6709\u70b9\u5947\u602a\uff0c \u6211\u7684\u884c\u8e2a\u600e\u4f1a\u7ed9\u7a81\u53a5\u53ef\u6c57\u77e5\u9053\uff1f\u4ed6\u7ba1\u7406\u4e00\u4e2a\u5e1d\u56fd\uff0c\u591a\u5c11\u4e8b\u60c5\uff0c\u53c8\u600e\u7684\u4f1a\u6ce8\u610f\u8d77\u6211\u6765\uff1f\u96be \u9053\u4ed6\u77e5\u9053\u6211\u7684\u8eab\u4efd\uff1f\u8fd9\u53c8\u662f\u8c01\u544a\u8bc9\u4ed6\u7684\uff1f\u6211\u5bf9\u4ed6\u53ef\u6ca1\u6709\u4e1d\u6beb\u5371\u5bb3\u554a\uff1f\u201d\u8fd9\u5973\u5b50\u672c\u6765\u5c31\u662f \u5fc3\u4e8b\u91cd\u91cd\uff0c\u8fd9\u65f6\u53c8\u9047\u5230\u610f\u5916\uff0c\u867d\u7136\u5979\u4e0d\u6015\u90a3\u4e9b\u6b66\u58eb\uff0c\u603b\u662f\u6dfb\u4e86\u51e0\u5206\u987e\u8651\u3002\u56e0\u4e3a\u5979\u73b0\u5728\u4e00 \u5fc3\u4e00\u610f\uff0c\u8981\u53bb\u8bbf\u67e5\u4e00\u4e2a\u5979\u5931\u8e2a\u4e86\u591a\u5e74\u7684\u670b\u53cb\uff0c\u5b9e\u4e0d\u613f\u5728\u8def\u9014\u4e0a\u53e6\u751f\u679d\u8282\u3002 \u3000\u3000\u8fd9\u4f4d\u6c49\u65cf\u59d1\u5a18\uff0c\u6b63\u662f\u5f53\u65f6\u4e2d\u56fd\u5973\u7687\u5e1d\u6b66\u5219\u5929\u7684\u4f84\u5973\u2014\u2014\u6b66\u7384\u971c\u3002 \u3000\u3000\u5979\u53c8\u8d70\u4e86\u4e00\u7a0b\uff0c\u524d\u9762\u53d1\u73b0\u4e86\u4e00\u961f\u9a86\u9a7c\u961f\uff0c\u53ea\u6709\u4e09\u5339\u9a86\u9a7c\uff0c\u7537\u5973\u8001\u5e7c\u4e0d\u8fc7\u5341\u591a\u4e2a\u4eba\uff0c \u4f3c\u4e4e\u662f\u4e00\u5bb6\u5927\u5c0f\uff0c\u6b66\u7384\u971c\u7b56\u9a6c\u6765\u5230\uff0c\u4f46\u89c1\u4ed6\u4eec\u7684\u795e\u8272\u6cae\u4e27\u5f97\u5f88\uff0c\u961f\u4e2d\u7684\u4e00\u4e2a\u5c0f\u5b69\u5b50\u671b\u7740 \u5979\u9a6c\u80cc\u4e0a\u7684\u6c34\u56ca\uff0c\u4f38\u51fa\u4e00\u53ea\u624b\u6307\u53eb\u9053\uff1a\u201c\u6c34\uff0c\u6c34\uff0c\u6211\u8981\u6c34\u559d\uff01\u201d \u3000\u3000\u65c1\u8fb9\u4e00\u4e2a\u7ef4\u65cf\u5987\u4eba\uff0c\u4f3c\u662f\u5b69\u5b50\u7684\u6bcd\u4eb2\uff0c\u8bc3\u8d23\u4ed6\u9053\uff1a\u201c\u771f\u4e0d\u61c2\u4e8b\uff0c\u4e00\u5bb6\u4eba\u53ea\u5269\u4e0b\u4e24\u888b \u6c34\u4e86\uff0c\u8fd8\u8981\u6c34\u559d\uff1f\u201d\u6b66\u7384\u971c\u8df3\u4e0b\u9a6c\u80cc\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u6c34\u6709\u7684\u591a\u3002\u201d\u62d4\u4e86\u76ae\u56ca\u7684\u585e\u5b50\uff0c \u8ba9\u90a3\u5b69\u5b50\u5495\u565c\u7684\u559d\u4e86\u4e00\u5927\u53e3\uff0c\u5b69\u5b50\u5446\u6ede\u7684\u773c\u775b\u767b\u65f6\u7075\u6d3b\u8d77\u6765\uff0c\u4ed6\u7684\u7236\u6bcd\u8fde\u5fd9\u5411\u6b66\u7384\u971c\u9053 \u8c22\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u70b9\u6ef4\u4e4b\u6c34\uff0c\u4f55\u8db3\u4ecb\u6000\uff0c\u8bf7\u95ee\u4f60\u4eec\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u90a3\u7ef4\u65cf\u5987\u4eba\u9053\uff1a\u201c\u6211\u4eec \u8981\u5230\u5929\u5c71\u5317\u9762\u53bb\u3002\u201d\u6b66\u7384\u971c\u6709\u70b9\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u5929\u5c71\u5317\u9762\u6bd4\u5357\u9762\u5bd2\u51b7\u5f97\u591a\uff0c\u51ac\u5929\u5c06\u5230\uff0c \u4ed6\u4eec\u4e00\u5bb6\u4eba\u4e0d\u5f80\u5357\u8fb9\u8fdb\u51ac\uff0c\u5374\u5230\u5317\u8fb9\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\u201c\u524d\u9762\u4e00\u7247\u8352\u51c9\uff0c\u6050 \u6015\u8981\u8d70\u8bb8\u591a\u5929\u624d\u627e\u5f97\u5230\u6c34\u6e90\uff0c\u4f60\u4eec\u4e00\u5bb6\u5b50\u53ea\u5269\u4e0b\u4e24\u888b\u6c34\u5982\u4f55\u591f\u7528\uff1f\u6211\u5e26\u5f97\u5f88\u591a\uff0c\u9a6c\u513f\u8dd1 \u8d77\u6765\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u8fd9\u4e09\u888b\u6c34\u90fd\u7ed9\u4e86\u4f60\u4eec\u5427\u3002\u201d\u5c06\u593a\u81ea\u7a81\u53a5\u6b66\u58eb\u7684\u4e09\u4e2a\u76db\u6c34\u76ae\u56ca\uff0c\u90fd\u89e3\u4e0b\u6765 \u9001\u7ed9\u4ed6\u4eec\uff0c\u6c99\u6f20\u4e4b\u4e0a\uff0c\u6ef4\u6c34\u5982\u91d1\uff0c\u6b66\u7384\u971c\u4e00\u9001\u5c31\u662f\u4e09\u5927\u76ae\u56ca\uff0c\u5f53\u771f\u662f\u6bd4\u5341\u4e00\u5339\u9a86\u9a7c\u8fd8\u66f4 \u8d35\u91cd\u7684\u793c\u7269\u3002 \u3000\u3000\u8fd9\u4e00\u5bb6\u4eba\u611f\u6fc0\u6d95\u96f6\uff0c\u8d77\u521d\u8fd8\u4e0d\u6562\u8981\uff0c\u540e\u6765\u89c1\u6b66\u7384\u971c\u51fa\u81ea\u771f\u5fc3\uff0c\u8fd9\u624d\u5343\u591a\u8c22\u4e07\u591a\u8c22\u7684 \u6536\u4e0b\u4e86\u3002\u90a3\u7ef4\u65cf\u5987\u4eba\u62db\u547c\u5979\u5750\u4e0b\u6765\u70e4\u706b\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53c8\u662f\u5f80\u54ea\u53bb\u5462\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a \u201c\u8fd9\u662f\u5230\u5929\u5c71\u5357\u9762\u627e\u4e00\u4f4d\u670b\u53cb\uff0c\u4f60\u4eec\u8981\u5230\u5317\u8fb9\uff0c\u8def\u9014\u53ef\u6bd4\u6211\u8fdc\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u90a3\u5bb6\u4eba\u4e2d\u7684\u4e00\u4e2a\u8001\u7237\u7237\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e00\u8def\u6765\u53ef\u89c1\u6709\u5927\u6c57\u7684\u5df4\u56fe\u9c81\u4e48\uff1f\u201d\u201c\u5df4\u56fe \u9c81\u201d\u5373\u662f\u7a81\u53a5\u7684\u6b66\u58eb\uff0c\u540e\u6765\u53d8\u6210\u4e00\u79cd\u6b66\u5b98\u7684\u5c01\u53f7\u3002\u6b66\u7384\u971c\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u66fe\u9047\u89c1 \u51e0\u4e2a\uff0c\u4e0d\u8fc7\u90fd\u4e0d\u662f\u671d\u8fd9\u4e2a\u65b9\u5411\u3002\u201d\u90a3\u8001\u7237\u7237\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4eec\u90fd\u662f\u9003\u907f\u53ef\u6c57\u7684\u5927\u5175 \u7684\u3002\u201d\u7a81\u53a5\u884c\u7684\u662f\u5f81\u5175\u5236\u5ea6\uff0c\u53ef\u6c57\u5927\u70b9\u5175\u90a3\u5c31\u662f\u8981\u5728\u5168\u56fd\u666e\u904d\u5f81\u5175\uff0c\u51c6\u5907\u6218\u4e89\u7684\u4e86\u3002\u6b66 \u7384\u971c\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u6253\u4ed7\u4e86\uff1f\u201d\u90a3\u8001\u7237\u7237\u9053\uff1a\u201c\u662f\u5440\uff0c\u542c\u8bf4\u662f\u8981\u548c\u4e2d\u56fd\u6253\u4ed7 \u5462\uff01\u201d\u6b66\u7384\u971c\u5fc3\u4e2d\u4e00\u51db\uff0c\u5979\u5728\u957f\u5b89\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u542c\u5f97\u7a81\u53a5\u6709\u5165\u72af\u7684\u98ce\u58f0\uff0c\u4f46\u8fd8\u60f3\u4e0d\u5230\u4f1a \u7206\u53d1\u5f97\u8fd9\u6837\u5feb\u3002 \u3000\u3000\u90a3\u8001\u7237\u7237\u9053\uff1a\u201c\u6211\u4eec\u4e00\u5bb6\u8001\u7684\u8001\uff0c\u5c11\u7684\u5c11\uff0c\u53ea\u6709\u4e24\u4e2a\u5e74\u9752\u529b\u58ee\u7684\u5c0f\u4f19\u5b50\uff0c\u8981\u662f\u90fd\u7ed9 \u62bd\u4e86\u53bb\uff0c\u65e5\u5b50\u5c31\u4e0d\u77e5\u9053\u600e\u4e48\u8fc7\u5566\uff0c\u6240\u4ee5\u6211\u4eec\u5b81\u613f\u7ed9\u4eba\u53eb\u505a\u5112\u592b\uff0c\u4e5f\u5f97\u5728\u5927\u96ea\u5c01\u5c71\u4e4b\u524d\uff0c \u5230\u5317\u8fb9\u53bb\u8eb2\u907f\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4eec\u4e2d\u56fd\u4e5f\u4e0d\u613f\u6253\u4ed7\uff0c\u5927\u5bb6\u5b89\u5b89\u9038\u9038\u7684\u8fc7\u65e5\u5b50\u5c82 \u4e0d\u66f4\u597d\u3002\u201d\u90a3\u8001\u7237\u7237\u9053\uff1a\u201c\u5927\u6c57\u9881\u4e0b\u4e86\u70b9\u5175\u4ee4\u540e\uff0c\u8bb8\u591a\u7267\u6c11\u90e8\u5e26\u7740\u5e10\u5e55\uff0c\u5e26\u770b\u7edc\u9a7c\u5f80\u5317 \u65b9\u53bb\uff0c\u542c\u8bf4\u5927\u6c57\u6d3e\u51fa\u4e86\u8bb8\u591a\u5df4\u56fe\u9c81\u6765\u62e6\u622a\u9003\u4ea1\u7684\u4eba\uff0c\u6211\u4eec\u5c31\u662f\u56e0\u4e3a\u9003\u907f\u8ffd\u5175\uff0c\u8d70\u5931\u4e00\u5339 \u9a86\u9a7c\uff0c\u90a3\u5339\u9a86\u9a7c\u80cc\u4e0a\u6709\u4e94\u76ae\u56ca\u6c34\uff0c\u4e16\u968f\u7740\u5931\u4e86\u3002\u59d1\u5a18\uff0c\u4f60\u771f\u597d\u5fc3\uff0c\u9001\u7ed9\u6211\u4eec\u8fd9\u4e09\u5927\u888b\u6c34\uff0c \u8db3\u591f\u6211\u4eec\u7528\u597d\u591a\u5929\u5566\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u8bf4\u4e86\u4e00\u4e9b\u5b89\u6170\u4ed6\u4eec\u7684\u8bdd\uff0c\u90a3\u7ef4\u65cf\u5987\u4eba\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e00\u4e2a\u4eba\u5728\u6c99\u6f20\u91cc\u8d70\uff0c\u6c99 \u6f20\u6c14\u5019\u53d8\u5e7b\u65e0\u5e38\uff0c\u751a\u662f\u5371\u9669\uff0c\u4e0d\u5982\u4e0e\u6211\u4eec\u4e00\u9053\u5427\uff01\u201d\u6b66\u7384\u971c\u4e00\u60f3\u4ed6\u4eec\u7684\u9a86\u9a7c\u961f\u884c\u5f97\u6162\uff0c \u591a\u8c22\u4e86\u4ed6\u4eec\uff0c\u4ecd\u7136\u4e00\u4e2a\u4eba\u4e58\u7740\u767d\u9a6c\u8d70\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u4e3a\u4e86\u907f\u514d\u9ebb\u70e6\uff0c\u5728\u8def\u4e0a\u7ecf\u8fc7\u4e00\u4e2a\u6e38\u730e\u90e8\u843d\u7684\u65f6\u5019\uff0c\u7528\u5f13\u7bad\u6362\u4e86\u4e00\u5957\u7ef4\u4eba\u7684\u730e \u88c5\uff0c\u625b\u626e\u6210\u4e00\u4e2a\u82f1\u59ff\u98d2\u723d\u7684\u730e\u4eba\u6a21\u6837\uff0c\u5e0c\u671b\u53ef\u4ee5\u51cf\u5c11\u90a3\u4e9b\u8ffd\u8e2a\u5979\u7684\u6b66\u58eb\u7684\u6ce8\u610f\u3002 \u3000\u3000\u5979\u7684\u767d\u9a6c\u8d70\u5f97\u5f88\u5feb\uff0c\u8d70\u4e86\u4e24\u5929\uff0c\u8fdc\u8fdc\u5df2\u53ef\u770b\u89c1\u90a3\u9ad8\u8038\u5165\u4e91\u7684\u5929\u5c71\uff0c\u4e0d\u6599\u8d70\u5230\u7b2c\u4e09\u5929 \u7684\u65f6\u5019\uff0c\u7a81\u7136\u95f4\u5929\u8272\u5927\u53d8\uff0c\u524d\u4e00\u970e\u8fd8\u662f\u4e07\u91cc\u6674\u7a7a\uff0c\u8f6c\u773c\u95f4\u72c2\u98ce\u5c31\u522e\u9762\u800c\u6765\uff0c\u4e00\u671b\u65e0\u9645\u7684 \u5927\u6c99\u6f20\u4e0a\uff0c\u5c3d\u662f\u9ec4\u7070\u8272\u7684\u6c99\u96fe\uff0c\u50cf\u662f\u5343\u4e07\u91cd\u539a\u539a\u7684\u9ec4\u7ed2\u5e10\u5e55\uff0c\u906e\u5929\u853d\u5730\uff0c\u6b66\u7384\u971c\u907f\u5f00\u98ce \u5934\uff0c\u7eb5\u9a6c\u72c2\u5954\uff0c\u5e78\u597d\u8fd9\u9635\u72c2\u98ce\u6765\u5f97\u5feb\u53bb\u5f97\u4e5f\u5feb\uff0c\u6b66\u7384\u971c\u8dd1\u5230\u4e00\u4e2a\u7802\u4e18\u540e\u9762\uff0c\u907f\u4e86\u4e9b\u65f6\uff0c \u5927\u7ea6\u8fc7\u4e86\u4e00\u987f\u996d\u7684\u5149\u666f\uff0c\u98ce\u7802\u6e10\u606f\uff0c\u6b66\u7384\u971c\u51fa\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u6c99\u6f20\u4e0a\u5e73\u6dfb\u4e86\u8bb8\u591a\u7802\u4e18\uff0c\u524d \u9762\u539f\u6765\u6709\u4e24\u5ea7\u6c99\u571f\u5806\u6210\u7684\u5c0f\u5c71\u7684\uff0c\u73b0\u5728\u5374\u4e0d\u89c1\u4e86\u3002\u6b66\u7384\u971c\u5927\u5927\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u5f97 \u679c\u7136\u4e0d\u9519\uff0c\u53ea\u4e00\u9635\u72c2\u9690\u5377\u8fc7\uff0c\u5c31\u53d8\u5f97\u8fd9\u4e48\u5389\u5bb3\uff01\u201d \u3000\u3000\u63a5\u7740\u51e0\u5929\uff0c\u4e0d\u65ad\u9047\u5230\u98ce\u7802\uff0c\u6b66\u7384\u971c\u5728\u6c99\u6f20\u4e0a\u515c\u5708\u5b50\uff0c\u4e3a\u4e86\u907f\u98ce\uff0c\u4e0d\u65f6\u8981\u6539\u53d8\u65b9\u5411\uff0c \u5979\u6240\u7559\u4e0b\u7684\u4e24\u5927\u76ae\u56ca\u7684\u98df\u6c34\uff0c\u4e5f\u5feb\u8981\u7528\u5b8c\u4e86\uff0c\u4eba\u9a6c\u90fd\u6781\u56f0\u4e4f\uff0c\u5230\u4e86\u7b2c\u516d\u5929\u7684\u9ec4\u660f\u65f6\u5206\uff0c \u5979\u5b9e\u652f\u6301\u4e0d\u4f4f\uff0c\u6b63\u60f3\u627e\u4e2a\u5730\u65b9\u6b47\u606f\uff0c\u5ffd\u7136\u811a\u4e0b\u4e00\u9635\u9707\u52a8\uff0c\u8fdc\u8fdc\u4f20\u6765\u8f70\u9e23\u4e4b\u58f0\uff0c\u6b66\u7384\u971c\u60f3 \u9053\uff1a\u201c\u8fd9\u4e0d\u77e5\u662f\u5730\u9707\u8fd8\u662f\u96ea\u5d29\uff1f\u201d\u90a3\u5339\u767d\u9a6c\u4e5f\u4f3c\u4e4e\u53d7\u4e86\u60ca\u5413\uff0c\u4e0d\u80af\u8d70\u52a8\uff0c\u53e3\u4e2d\u76f4\u5410\u767d\u6ce1\uff0c \u6b66\u7384\u971c\u8df3\u4e0b\u9a6c\u6765\uff0c\u4e3e\u76ee\u9065\u671b\uff0c\u5ffd\u7136\u53d1\u73b0\u90a3\u8fb9\u5c71\u811a\u6709\u706b\u5149\u4eba\u5f71\uff0c\u6b66\u7384\u971c\u559c\u51fa\u671b\u5916\uff0c\u4fbf\u7275\u4e86 \u767d\u9a6c\u5411\u90a3\u5806\u706b\u5149\u8d70\u53bb\u3002 \u3000\u3000\u5230\u5f97\u5c71\u811a\uff0c\u4f46\u89c1\u5c71\u53e3\u7684\u9053\u8def\u90fd\u5df2\u88ab\u96ea\u5757\u5c01\u4f4f\uff0c\u6709\u4e00\u961f\u9a86\u9a7c\u961f\u6b63\u5728\u5c71\u811a\u624e\u8425\uff0c\u8425\u5e55\u4e2d \u95f4\u71c3\u8d77\u4e00\u5806\u7bdd\u706b\uff0c\u6b66\u7384\u971c\u672a\u66fe\u8d70\u8fd1\uff0c\u4fbf\u89c1\u6709\u4e00\u4e2a\u8001\u4eba\u8fce\u4e86\u51fa\u6765\uff0c\u539f\u6765\u5374\u662f\u5979\u524d\u51e0\u5929\u9047\u89c1 \u7684\u90a3\u4e00\u5bb6\u4eba\uff0c\u90a3\u7ef4\u65cf\u8001\u4eba\u89c1\u5979\u6539\u4e86\u670d\u88c5\uff0c\u6709\u70b9\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u679c\u7136\u662f\u4f60\uff0c\u6211\u8fd8\u5f53\u8ba4\u9519\u4e86 \u4eba\u5462\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6539\u4e86\u730e\u88c5\uff0c\u5728\u6c99\u6f20\u4e0a\u8d70\u65b9\u4fbf\u4e00\u4e9b\u3002\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u5929\u6c14\u4e0d \u597d\uff0c\u6211\u4e00\u76f4\u4e3a\u4f60\u62c5\u5fc3\uff0c\u5e78\u597d\u4f60\u4e5f\u6765\u5230\u8fd9\u513f\u4e86\u3002\u201d\u6b66\u7384\u971c\u6697\u6697\u60ed\u6127\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u8fd8\u662f\u8ddf\u4ed6 \u4eec\u4e00\u540c\u8d70\u53ef\u4ee5\u514d\u53bb\u8bb8\u591a\u98ce\u9669\uff0c\u90a3\u8001\u4eba\u9053\uff1a\u201c\u6211\u5728\u8def\u4e0a\u4f1a\u5408\u4e86\u597d\u51e0\u4e2a\u9a86\u9a7c\u961f\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f \u9003\u907f\u5175\u5f79\u6765\u7684\uff0c\u4e0d\u5de7\u5f97\u5f88\uff0c\u521a\u521a\u9047\u7740\u96ea\u5d29\uff0c\u770b\u6765\u8981\u5728\u8fd9\u91cc\u6b47\u51e0\u5929\uff0c\u7b49\u626b\u4e86\u79ef\u96ea\uff0c\u624d\u53ef\u4ee5 \u901a\u8fc7\u4e86\u3002\u201d\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u96ea\u5d29\u4e5f\u597d\uff0c\u54b1\u4eec\u53ef\u4ee5\u6eb6\u96ea\u8d2e\u6c34\u3002\u201d\u6b66\u7384\u971c\u559c\u9053\uff1a\u201c\u6211\u7684\u98df\u6c34 \u521a\u521a\u7528\u5b8c\uff0c\u6b63\u662f\u5929\u65e0\u7edd\u4eba\u4e4b\u8def\uff0c\u5e78\u597d\u9047\u5230\u4f60\u4eec\u3002\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u884c\u8def\u4e4b\u4eba\uff0c\u60a3\u96be\u76f8\u6d4e\uff0c \u90a3\u662f\u5e94\u5f53\u7684\u3002\u4f60\u6765\u5427\uff0c\u6211\u4eec\u90a3\u513f\uff0c\u4e0d\u4f46\u6709\u6c34\uff0c\u8fd8\u6709\u9a7c\u9a6c\u7684\u8349\u6599\u3002\u201d \u3000\u3000\u8425\u5e10\u5916\u9762\u6709\u51e0\u5341\u5339\u9a86\u9a7c\uff0c\u56f4\u7740\u706b\u5806\u7684\u5c11\u8bf4\u4e5f\u6709\u767e\u6765\u4eba\uff0c\u6b66\u7384\u971c\u6697\u6697\u6168\u53f9\uff1a\u201c\u7a81\u53a5\u53ef \u6c57\u8981\u6253\u4ed7\uff0c\u5f04\u5f97\u8fd9\u4e48\u591a\u4eba\u8981\u5728\u98ce\u96ea\u4e4b\u4e0b\u9003\u4ea1\uff01\u201d\u90a3\u8001\u4eba\u5e26\u5979\u8fdb\u53bb\uff0c\u5bf9\u4f17\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d \u5927\u54e5\u867d\u7136\u662f\u4e2a\u6c49\u4eba\uff0c\u5fc3\u80a0\u6781\u597d\uff0c\u6211\u4eec\u4e00\u5bb6\u5c31\u6cbe\u8fc7\u4ed6\u7684\u5927\u6069\u3002\u201d\u5c06\u5979\u6177\u6168\u8d60\u6c34\u7684\u4e8b\u5bf9\u4f17\u4eba \u8bf4\u4e86\uff0c\u5927\u5bb6\u90fd\u8868\u793a\u6b22\u8fce\u3002\u8fd9\u8001\u4eba\u89c1\u5979\u6539\u4e86\u7537\u88c5\uff0c\u6599\u60f3\u5176\u4e2d\u5fc5\u6709\u539f\u6545\uff0c\u6545\u6b64\u4e00\u76f4\u6ca1\u6709\u8bf4\u7834\u3002 \u3000\u3000\u4e00\u4e2a\u7ef4\u4eba\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u6c49\u4eba\u662f\u4e0d\u662f\u51c6\u5907\u8981\u548c\u6211\u4eec\u6253\u4ed7\u5440\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff0c \u6211\u521a\u4ece\u4e2d\u56fd\u6765\uff0c\u5e76\u6ca1\u6709\u542c\u8bf4\u8981\u6253\u4ed7\uff0c\u4e00\u8def\u4e0a\u90fd\u662f\u592a\u5e73\u666f\u8c61\u3002\u201d\u53c8\u4e00\u4eba\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u73b0\u5728 \u662f\u4e00\u4e2a\u5973\u4eba\u505a\u4e2d\u56fd\u7684\u7687\u5e1d\uff0c\u662f\u771f\u7684\u5417\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u662f\u771f\u7684\uff0c\u5979\u505a\u7687\u5e1d\u4e5f\u505a\u4e86\u597d\u591a\u5e74 \u4e86\u3002\u201d\u4e00\u4e2a\u7ef4\u65cf\u5987\u4eba\u7b11\u7740\u5bf9\u5979\u7684\u4e08\u592b\u9053\uff1a\u201c\u4f60\u8001\u662f\u770b\u4e0d\u8d77\u5973\u4eba\uff0c\u8bf4\u5973\u4eba\u6837\u6837\u90fd\u6bd4\u4e0d\u4e0a\u7537 \u5b50\uff0c\u4f60\u77a7\u4ed6\u4eec\u5929\u671d\u4e0a\u56fd\uff0c\u4e5f\u8fd8\u662f\u5973\u4eba\u505a\u7687\u5e1d\u54e9\u3002\u201d\u53c8\u9053\uff1a\u201c\u6253\u4ed7\u662f\u7537\u4eba\u6b22\u559c\u7684\u4e8b\u60c5\uff0c\u5973 \u4eba\u505a\u7687\u5e1d\u7684\u5927\u81f4\u603b\u4f1a\u597d\u4e00\u4e9b\uff0c\u4e0d\u81f3\u4e8e\u52a8\u4e0d\u52a8\u5c31\u8981\u5174\u5175\u6253\u4ed7\u4e86\u3002\u201d\u4ed6\u4e08\u592b\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u4e0d \u5bf9\uff0c\u7537\u4eba\u4e5f\u5e76\u4e0d\u559c\u6b22\u6253\u4ed7\uff0c\u8981\u4e0d\uff0c\u54b1\u4eec\u8fd9\u8bb8\u591a\u7537\u4eba\u4e5f\u4e0d\u4f1a\u9003\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d\u987f\u4e86\u4e00\u987f\uff0c \u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u4eec\u5e76\u4e0d\u60e7\u6015\u6253\u4ed7\uff0c\u82e5\u662f\u4f60\u4eec\u6c49\u4eba\u6253\u6765\uff0c\u6211\u662f\u9a6c\u4e0a\u8981\u56de\u53bb\u7684\u3002\u201d\u6b66\u7384\u971c \u9053\uff1a\u201c\u6211\u4eec\u6c49\u4eba\u4e5f\u662f\u8fd9\u6837\u60f3\u6cd5\uff0c\u53ea\u6c42\u5b89\u5c45\u4e50\u4e1a\uff0c\u4e0d\u60f3\u4fb5\u72af\u4ed6\u4eba\u3002\u201d\u6709\u4e2a\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u8fc7 \u6211\u4eec\u603b\u662f\u6709\u70b9\u5bb3\u6015\u4f60\u4eec\u6c49\u4eba\uff0c\u8bb0\u5f97\u51e0\u5341\u5e74\u524d\uff0c\u6211\u5f88\u5c0f\u7684\u65f6\u5019\uff0c\u6709\u4e00\u6b21\u4f60\u4eec\u6c49\u4eba\u7684\u519b\u961f\uff0c \u5c31\u66fe\u6253\u5230\u6211\u4eec\u7684\u548c\u9617\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u90a3\u662f\u8fc7\u53bb\u7684\u4e8b\u4e86\u3002\u73b0\u5728\u7684\u5973\u7687\u5e1d\uff0c\u6bcf\u5e74\u6240\u9881\u53d1\u7684 \u547d\u4ee4\u4e0d\u662f\u529d\u8001\u767e\u59d3\u52e4\u5de5\u52a1\u519c\uff0c\u5c31\u662f\u8bf4\u8981\u5de5\u4eba\u7528\u5fc3\u505a\u5de5\uff0c\u519c\u4eba\u7528\u5fc3\u79cd\u7530\uff0c\u672b\u89c1\u8fc7\u8981\u51c6\u5907\u6253 \u4ed7\u7684\u547d\u4ee4\u3002\u201d\u6709\u4e2a\u5546\u4eba\u6a21\u6837\u7684\u8bf4\u9053\uff1b\u201c\u8fd9\u5012\u662f\u771f\u7684\u3002\u6211\u53bb\u5e74\u5230\u8fc7\u5410\u9ec4\uff08\u5373\u4eca\u897f\u85cf\uff09\uff0c\u4ed6 \u4eec\u7687\u592a\u540e\u662f\u5927\u5510\u7684\u516c\u4e3b\uff0c\u73b0\u5728\u8fd8\u4e00\u76f4\u548c\u4e2d\u56fd\u8d70\u4eb2\u5bb6\u3002\u542c\u8bf4\u90a3\u4f4d\u516c\u4e3b\u5ac1\u6765\u7684\u65f6\u5019\uff0c\u5e26\u6765\u4e86 \u8bb8\u591a\u4e66\u7c4d\u3001\u79cd\u7c7d\u3001\u5de5\u5320\u3001\u4e50\u5e08\u7b49\u7b49\uff0c\u8bb8\u591a\u5410\u9ec4\u4eba\u90fd\u4f1a\u79cd\u7530\uff0c\u6211\u5c31\u66fe\u5728\u62c9\u8428\u9644\u8fd1\u4eb2\u773c\u770b\u5230 \u7530\u91cc\u7684\u79be\u82d7\u7eff\u6cb9\u6cb9\u7684\uff0c\u8981\u662f\u54b1\u4eec\u4e5f\u4f1a\u8015\u79cd\uff0c\u5c31\u4e0d\u7528\u8fd9\u6837\u8f9b\u82e6\u5566\u3002\u6211\u8fd8\u5403\u8fc7\u4ed6\u4eec\u79cd\u7684\u5927\u767d \u83dc\uff0c\u8fd9\u4e9b\u90fd\u662f\u516c\u4e3b\u5e26\u6765\u7684\u79cd\u7c7d\uff0c\u54b1\u4eec\u8fd9\u91cc\u6ca1\u7684\uff0c\u5473\u9053\u597d\u5403\u7684\u5f88\u54e9\uff01\u5410\u9ec4\u4eba\u5f88\u611f\u6fc0\u90a3\u4f4d\u516c \u4e3b\uff0c\u548c\u6c49\u4eba\u975e\u5e38\u7684\u597d\u3002\u201d\u6b66\u7384\u971c\u77e5\u9053\u4ed6\u8bf4\u7684\u662f\u6587\u6210\u516c\u4e3b\uff08\u674e\u4e16\u6c11\u4e4b\u5973\uff09\u5ac1\u7ed9\u85cf\u738b\u677e\u8d5e\u5e72 \u5e03\u7684\u4e8b\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u559c\u6b22\u6253\u4ed7\u7684\u53ea\u662f\u5f88\u5c11\u5f88\u5c11\u7684\u4eba\uff0c\u6211\u4eec\u6c49\u4eba\u672c\u6765\u5c31\u613f\u610f \u548c\u5404\u65cf\u4eba\u548c\u597d\u3002\u201d \u3000\u3000\u5927\u5bb6\u6b63\u5728\u542c\u90a3\u7ef4\u65cf\u5546\u4eba\u8c08\u8bba\u7740\u7684\u65b0\u4e8b\uff0c\u8fd9\u65f6\u53c8\u6765\u4e86\u4e00\u4e2a\u7ef4\u65cf\u5987\u4eba\uff0c\u7c97\u7709\u5927\u773c\uff0c\u9762\u8272 \u9ec4\u4e2d\u5e26\u9ed1\uff0c\u662f\u7ef4\u65cf\u4e2d\u5e38\u89c1\u7684\u90a3\u79cd\u5973\u730e\u6237\uff0c\u5979\u5e26\u7740\u4e00\u4e2a\u5b69\u5b50\uff0c\u5b69\u5b50\u5374\u5f88\u6e05\u79c0\uff0c\u4e0d\u50cf\u4e00\u822c\u7ef4 \u4eba\u7684\u5b69\u5b50\uff0c\u5979\u7275\u7740\u4e00\u5339\u7626\u9a86\u9a7c\uff0c\u6765\u5230\u4e4b\u540e\uff0c\u4e5f\u548c\u5b69\u5b50\u6742\u5728\u4eba\u5806\u4e4b\u4e2d\u70e4\u706b\u3002\u6b66\u7384\u971c\u5ffd\u7136\u53d1 \u89c9\u90a3\u5973\u4eba\u597d\u50cf\u5f88\u6ce8\u610f\u5979\uff0c\u65f6\u4e0d\u65f6\u7528\u773c\u89d2\u8198\u6765\uff0c\u6b66\u7384\u971c\u53ea\u9053\u5979\u5c11\u89c1\u6c49\u65cf\u7537\u5b50\uff0c\u5fc3\u4e2d\u8fd8\u4e0d\u4ee5 \u4e3a\u610f\uff0c\u89c1\u5979\u90a3\u5b69\u5b50\u4f36\u4fd0\u53ef\u7231\uff0c\u5c31\u6328\u8fd1\u53bb\u60f3\u9017\u4ed6\u73a9\uff0c\u5e76\u638f\u51fa\u4e86\u4e00\u7247\u674f\u4ec1\u679c\u94fa\u7ed9\u4ed6\uff0c\u8fd9\u662f\u6b66 \u7384\u971c\u4ece\u957f\u5b89\u5e26\u6765\uff0c\u8fd8\u672a\u5403\u5b8c\u7684\u3002\u90a3\u5b69\u5b50\u6b63\u4f38\u624b\u5f85\u63a5\uff0c\u5979\u6bcd\u4eb2\u7a81\u7136\u4e00\u638c\u6253\u4e0b\uff0c\u5c06\u5b69\u5b50\u7684\u5c0f \u624b\u6253\u5f00\uff0c\u77aa\u773c\u9a82\u9053\uff1a\u201c\u4e0d\u51c6\u8981\u6c49\u4eba\u7684\u4e1c\u897f\uff01\u201d\u90a3\u5b69\u5b50\u5634\u5507\u5f00\u9616\uff0c\u597d\u50cf\u975e\u5e38\u5947\u602a\uff0c\u60f3\u5411\u6bcd \u4eb2\u95ee\u8bdd\u7684\u6837\u5b50\uff0c\u90a3\u7ef4\u65cf\u5987\u4eba\u65a5\u9053\uff1a\u201c\u4e0d\u51c6\u8981\u5c31\u4e0d\u51c6\u8981\uff0c\u4e0d\u51c6\u4f60\u8bf4\u8bdd\uff01\u201d\u8fd9\u4e00\u5239\u90a3\uff0c\u6b66\u7384 \u971c\u5ffd\u7136\u53d1\u73b0\u5979\u7684\u76ee\u5149\u5bf9\u770b\u81ea\u5df1\uff0c\u773c\u5149\u4e2d\u7adf\u7136\u6709\u6781\u5176\u6028\u6bd2\u7684\u795e\u8272\uff01 \u3000\u3000\u6b66\u7384\u971c\u4e00\u751f\u4e0d\u77e5\u78b0\u8fc7\u591a\u5c11\u654c\u4eba\uff0c\u5374\u4ece\u672a\u66fe\u89c1\u8fc7\u6709\u4eba\u7528\u8fd9\u6837\u7684\u773c\u5149\u770b\u5f85\u5979\uff0c\u90a3\u662f\u5145\u6ee1 \u9634\u6c89\u3001\u51b7\u6f20\u3001\u4ec7\u6068\u3001\u6028\u6bd2\u7684\u773c\u5149\u3002\u66f4\u5947\u602a\u7684\u662f\uff1a\u7528\u8fd9\u6837\u773c\u5149\u770b\u5979\u7684\u4eba\uff0c\u5e76\u4e0d\u662f\u5979\u7684\u654c\u4eba\uff0c \u800c\u662f\u4e00\u4e2a\u7d20\u4e0d\u76f8\u8bc6\u7684\u7ef4\u65cf\u5987\u5973\uff01 \u3000\u3000\u90a3\u7ef4\u65cf\u8001\u7237\u7237\u8bf4\u9053\uff1a\u201c\u5927\u5a18\uff0c\u4e0d\u8981\u5bb3\u6015\uff0c\u8fd9\u5c0f\u4f19\u5b50\u662f\u4e2a\u5fc3\u5730\u5f88\u597d\u7684\u6c49\u4eba\u3002\u201d\u521a\u624d\u90a3 \u5546\u4eba\u6478\u6837\u7684\u4eba\u89e3\u91ca\u9053\uff1a\u201c\u6c49\u65cf\u5ba2\u4eba\uff0c\u6211\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u522b\u89c1\u602a\u3002\u4f60\u7684\u5fc3\u5730\u5f88\u597d\uff0c\u53ef\u662f\u4e5f\u6709\u8bb8 \u591a\u4e0d\u597d\u7684\u6c49\u4eba\uff0c\u66fe\u7ecf\u8ba9\u6211\u4eec\u4e0a\u8fc7\u5f53\u3002\u4ed6\u4eec\u62ff\u4e00\u4e9b\u597d\u5403\u7684\u4e1c\u897f\u54c4\u5b69\u5b50\uff0c\u62ff\u4e00\u4e9b\u597d\u770b\u7684\u82b1\u5e03 \u9a97\u59d1\u5a18\uff0c\u5f80\u5f80\u7528\u4e00\u5339\u5e03\u5c31\u6362\u8d70\u4e86\u6211\u4eec\u7684\u4e00\u5339\u9a86\u9a7c\u3002\u6240\u4ee5\u597d\u4e9b\u505a\u7236\u6bcd\u7684\u90fd\u4e0d\u51c6\u5b69\u5b50\u4eec\u548c\u59d1 \u5a18\u4eec\u63a5\u53d7\u6c49\u4eba\u7684\u4e1c\u897f\u3002\u201d\u63a5\u7740\u8f6c\u8fc7\u5934\u6765\u5bf9\u90a3\u7ef4\u65cf\u5987\u4eba\u8bf4\u9053\uff1a\u201c\u6c49\u4eba\u6709\u574f\u7684\u4e5f\u6709\u597d\u7684\uff0c\u8fd9 \u4f4d\u5ba2\u4eba\u662f\u597d\u7684\u6c49\u4eba\uff0c\u4ed6\u524d\u51e0\u5929\u5728\u6c99\u6f20\u91cc\u8fd8\u9001\u8fc7\u4e09\u5927\u76ae\u888b\u7684\u6c34\u7ed9\u5eb7\u5df4\u5927\u7239\uff0c\u6551\u4e86\u4ed6\u4e00\u5bb6\u5b50 \u54e9\u3002\u8fd9\u4f4d\u5ba2\u4eba\u4e0d\u4f1a\u9a97\u4f60\u7684\uff0c\u4ed6\u9001\u4e1c\u897f\u7ed9\u4f60\u5b69\u5b50\u5403\uff0c\u4f60\u5c31\u8981\u4e0b\u6765\u5427\u3002\u201d\u90a3\u7ef4\u65cf\u5987\u4eba\u4e00\u58f0\u4e0d \u54cd\uff0c\u5374\u62c9\u7740\u5979\u7684\u5b69\u5b50\u907f\u5f00\u6b66\u7384\u971c\uff0c\u6324\u5230\u53e6\u4e00\u8fb9\u7684\u4eba\u5806\u91cc\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u5fc3\u4e2d\u77e5\u9053\uff0c\u90a3\u7ef4\u65cf\u5987\u4eba\u82e5\u7136\u53ea\u662f\u4e3a\u4e86\u4e0d\u4fe1\u4efb\u6c49\u4eba\uff0c\u51b3\u8ba1\u4e0d\u4f1a\u7528\u90a3\u6837\u4ec7\u6068\u7684\u773c \u5149\u770b\u5f85\u5979\uff0c\u4ee4\u5979\u5927\u60d1\u4e0d\u89e3\u7684\u662f\uff0c\u5979\u4e3a\u4ec0\u4e48\u8981\u7528\u8fd9\u6837\u7684\u773c\u5149\u770b\u5f85\u5979\u5462\uff1f \u3000\u3000\u4e00\u4e2a\u9762\u6709\u5200\u75a4\u7684\u9752\u5e74\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u7237\u7684\u8bdd\u8bf4\u5f97\u4e0d\u9519\uff0c\u6c49\u4eba\u6709\u597d\u7684\u4e5f\u6709\u574f\u7684\uff0c \u6211\u5c31\u78b0\u8fc7\u4e00\u4e2a\u5f88\u597d\u7684\u6c49\u4eba\uff0c\u4ed6\u662f\u6211\u7684\u6551\u547d\u6069\u4eba\uff01\u4ed6\u6ca1\u6709\u7559\u4e0b\u540d\u5b50\uff0c\u53ef\u662f\u6211\u77e5\u9053\u4ed6\u662f\u8c01\uff0c \u4f60\u4eec\u4e2d\u4e5f\u8bb8\u6709\u4eba\u78b0\u89c1\u8fc7\u4ed6\uff0c\u6ca1\u89c1\u8fc7\u4ed6\uff0c\u4e5f\u53ef\u80fd\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\u3002\u4ed6\u5c31\u662f\u5929\u5c71\u5251\u5ba2\uff01\u201d\u6b64\u8a00 \u4e00\u51fa\uff0c\u8bb8\u591a\u4eba\u90fd\u7eb7\u7eb7\u56b7\u4e86\u8d77\u6765\uff0c\u201c\u5929\u5c71\u5251\u5ba2\uff01\u201d\u201c\u5929\u5c71\u5251\u5ba2\uff01\u201c\u4e0d\u9519\uff0c\u6211\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\u3002\u201d \u201c\u6211\u53d7\u8fc7\u4ed6\u7684\u6069\u5178\u3002\u201d\u201c\u5feb\u8bf4\u5427\uff0c\u5929\u5c71\u5251\u5ba2\u662f\u600e\u6837\u6551\u4e86\u4f60\u7684\u6027\u547d\u7684\uff1f\u201d\u4ece\u4eba\u4eec\u54c4\u52a8\u7684\u60c5 \u5f62\u770b\u6765\uff0c\u6562\u60c5\u90a3\u4f4d\u5929\u5c71\u5251\u5ba2\u4e43\u662f\u4e00\u4f4d\u884c\u4fa0\u4ed7\u4e49\u3001\u6df1\u5f97\u4eba\u5fc3\u7684\u6c49\u65cf\u82f1\u96c4 \u3000\u3000\u6b66\u7384\u971c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53ea\u542c\u5f97\u90a3\u9762\u5e26\u5200\u75d5\u7684\u5c11\u5e74\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u50cf\u8bf8\u4f4d\u4e00\u6837\uff0c\u4e5f\u662f\u4e3a\u4e86 \u9003\u907f\u53ef\u6c57\u7684\u70b9\u5175\u7684\u3002\u6211\u5e26\u4e86\u8001\u6bcd\u5f31\u59b9\uff0c\u9003\u5230\u534a\u8def\uff0c\u4fbf\u78b0\u89c1\u5927\u6c57\u6d3e\u6765\u7684\u5df4\u56fe\u9c81\uff0c\u5171\u6709\u56db\u4eba \u4e4b\u591a\uff0c\u4ed6\u4eec\u4e0d\u4f46\u8981\u6349\u6211\uff0c\u8fd8\u8981\u62a2\u6211\u7684\u59b9\u59b9\uff0c\u6211\u5e76\u4e0d\u6015\u5f53\u5175\uff0c\u4f46\u6211\u5374\u4e0d\u80fd\u4e0d\u4fdd\u62a4\u6211\u7684\u59b9\u59b9\uff0c \u558f\uff0c\u4f60\u4eec\u77a7\uff0c\u6211\u9762\u4e0a\u7684\u8fd9\u4e00\u5200\uff0c\u5c31\u662f\u90a3\u51f6\u6076\u7684\u5df4\u56fe\u9c81\u5288\u7684\uff01\u8fd9\u4e2a\u662f\u6211\u7684\u59b9\u5b50\uff0c\u4f60\u4eec\u77a7\uff0c \u5979\u53d7\u4e86\u90a3\u6b21\u60ca\u5413\u4e4b\u540e\uff0c\u73b0\u5728\u75c5\u8fd8\u672a\u597d\uff01\u201d\u4ed6\u8eab\u8fb9\u4e00\u4e2a\u9762\u9ec4\u808c\u7626\u7684\u5c0f\u59d1\u5a18\u7741\u7740\u602f\u751f\u751f\u7684\u773c \u775b\u770b\u7740\u4eba\u5bb6\uff0c\u773c\u4e2d\u542b\u7740\u6cea\u6c34\uff0c\u90a3\u7ef4\u65cf\u8001\u7237\u7237\u5583\u5583\u5492\u9053\uff1a\u201c\u771f\u53ef\u6068\uff0c\u771f\u53ef\u6068\uff0c\u628a\u4e2a\u5c0f\u59d1\u5a18 \u5413\u6210\u8fd9\u4e2a\u6837\u5b50\uff01\u201d \u3000\u3000\u90a3\u9762\u5e26\u5200\u75a4\u7684\u9752\u5e74\u987f\u4e86\u4e00\u987f\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u597d\u5728\u90a3\u4f4d\u5929\u5c71\u5251\u5ba2\u7a81\u7136\u6765\u5230\uff0c\u521a\u597d\u6551\u4e86 \u6211\u7684\u6027\u547d\u3002\u4e0d\u8fc7\u4e00\u76cf\u8336\u7684\u5de5\u592b\u3002\u90a3\u56db\u4e2a\u5df4\u56fe\u9c81\u90fd\u7ed9\u4ed6\u523a\u4f24\u4e86\u3002\u4ed6\u7528\u4e00\u67c4\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c \u6211\u4ece\u672a\u89c1\u8fc7\uff0c\u751a\u81f3\u4ece\u672a\u542c\u8fc7\u6709\u8fd9\u6837\u950b\u5229\u7684\u5200\u5251\uff0c\u90a3\u56db\u4e2a\u5df4\u56fe\u9c81\u7684\u5175\u5668\u4e00\u78b0\u5230\u5251\u950b\uff0c\u5c31\u88ab \u524a\u65ad\u4e86\uff01\u201d \u3000\u3000\u90a3\u9762\u5e26\u5200\u75a4\u7684\u9752\u5e74\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4e00\u4e2a\u8001\u4eba\u7ad9\u8d77\u6765\u95ee\u9053\uff1a\u201c\u4f60\u6240\u78b0\u5230\u7684\u90a3\u4e2a\u6c49\u4eba\u662f\u4ec0\u4e48 \u6a21\u6837\uff1f\u201d\u90a3\u9752\u5e74\u9053\uff1a\u201c\u662f\u4e2a\u4fca\u79c0\u7684\u4e2d\u5e74\u52c7\u58eb\uff0c\u4ed6\u6b66\u529f\u9ad8\u5f3a\uff0c\u53c8\u6709\u5b9d\u5251\uff0c\u4e0d\u662f\u5929\u5c71\u5251\u5ba2\u8fd8 \u662f\u8c01\uff1f\u201d\u6b66\u7384\u971c\u5219\u5728\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u674e\u9038\u8fd8\u662f\u8c01\uff1f\u201d\u4f46\u89c9\u5fc3\u5f26\u6fc0\u52a8\uff0c\u516b\u5e74\u524d\u7684\u5f80\u4e8b \u970e\u65f6\u95f4\u90fd\u5954\u4e0a\u5fc3\u5934\u3002 \u3000\u3000\u90a3\u8001\u4eba\u7684\u8bf4\u8bdd\u6253\u65ad\u4e86\u6b66\u7384\u971c\u7684\u601d\u8def\uff0c\u53ea\u542c\u5f97\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u66fe\u89c1\u8fc7\u5929\u5c71\u5251\u5ba2\uff0c\u5374\u4e0d \u662f\u4f60\u8bf4\u7684\u6a21\u6837\uff0c\u4ed6\u662f\u50cf\u6211\u4e00\u6837\uff0c\u5916\u8c8c\u770b\u6765\u6709\u70b9\u9f99\u949f\u7684\u8001\u4eba\u3002\u6211\u5bb6\u4e16\u4ee3\u91c7\u836f\u4e3a\u751f\uff0c\u90a3\u4e00\u5e74 \u963f\u5c14\u91d1\u65d7\u7684\u85e9\u738b\u9650\u4e0b\u4e09\u6708\u671f\u9650\uff0c\u8981\u6211\u7f34\u7eb3\u4e00\u6735\u5929\u5c71\u96ea\u83b2\uff0c\u7ed9\u4ed6\u6700\u5fc3\u7231\u7684\u5983\u5b50\u6cbb\u75c5\uff0c\u60f3\u90a3 \u5929\u5c71\u96ea\u83b2\u751f\u957f\u96ea\u5cf0\u4e4b\u4e0a\uff0c\u6781\u4e3a\u7f55\u89c1\uff0c\u6211\u4e00\u751f\u53ea\u91c7\u8fc7\u4e00\u6735\uff0c\u90a3\u8fd8\u662f\u6211\u4e8c\u5341\u6765\u5c81\uff0c\u8eab\u5f3a\u529b\u58ee \u7684\u65f6\u5019\uff0c\u5982\u4eca\u5e74\u8001\u6c14\u8870\uff0c\u9650\u671f\u53c8\u77ed\uff0c\u54ea\u80fd\u627e\u5230\u96ea\u83b2\u3002\u4e09\u4e2a\u6708\u7684\u671f\u9650\u8fc7\u4e86\uff0c\u6211\u88ab\u85e9\u738b\u7684\u624b \u4e0b\u8feb\u5f97\u6781\u7d27\uff0c\u518d\u9650\u4e09\u5929\uff0c\u82e5\u4ea4\u4e0d\u51fa\uff0c\u5168\u5bb6\u90fd\u8981\u6536\u76d1\uff0c\u4e09\u5929\u7684\u671f\u9650\u8f6c\u773c\u5373\u8fc7\uff0c\u6211\u6ca1\u529e\u6cd5\uff0c \u60f3\u60f3\u6d3b\u7740\u53d7\u82e6\uff0c\u4e0d\u5982\u6b7b\u6389\uff0c\u4fbf\u5728\u5929\u5c71\u811a\u4e0b\u4e0a\u540a\uff0c\u60f3\u4e0d\u5230\u6709\u90a3\u4e48\u5de7\u7684\u4e8b\u60c5\uff0c\u5c31\u5728\u6211\u4e0a\u540a\u7684 \u65f6\u5019\uff0c\u5929\u5c71\u5251\u5ba2\u6765\u4e86\uff0c\u4ed6\u4e00\u5251\u524a\u65ad\u7ef3\u7d22\uff0c\u5c06\u6211\u6551\u6d3b\uff0c\u5e76\u5728\u6211\u8eab\u8fb9\u7559\u4e0b\u4e86\u4e00\u6735\u5929\u5c71\u96ea\u83b2\u3002 \u4ed6\u6709\u5b9d\u5251\uff0c\u53c8\u6709\u5929\u5c71\u96ea\u83b2\uff0c\u4f60\u60f3\u9664\u4e86\u662f\u5929\u5c71\u5251\u5ba2\uff0c\u8fd8\u4f1a\u662f\u8c01\uff1f\u201d \u3000\u3000\u63a5\u7740\u53c8\u6709\u51e0\u4e2a\u53d7\u8fc7\u201c\u5929\u5c71\u5251\u5ba2\u201d\u601d\u5178\u7684\u4eba\uff0c\u5404\u81ea\u8c08\u8d77\u4ed6\u4eec\u7684\u6545\u4e8b\uff0c\u5728\u6bcf\u4e2a\u4e0d\u540c\u7684\u6545 \u4e8b\u4e2d\uff0c\u5929\u5c71\u5251\u5ba2\u90fd\u4ee5\u4e0d\u540c\u7684\u76f8\u8c8c\u51fa\u73b0\uff0c\u4f46\u6709\u4e24\u70b9\u76f8\u540c\u7684\u662f\uff1a\u5929\u5c71\u5251\u5ba2\u4e43\u662f\u4e00\u4e2a\u6c49\u4eba\uff0c\u4ed6 \u6709\u4e00\u628a\u5b9d\u5251\u3002\u201c\u5929\u5c71\u5251\u5ba2\u201d\u7a76\u7adf\u662f\u4e0d\u662f\u674e\u9038\uff1f\u6b66\u7384\u971c\u4e5f\u7ed9\u4ed6\u4eec\u5f04\u5f97\u7cca\u6d82\u4e86\uff0c\u5728\u4f17\u4eba\u95f9\u54c4 \u54c4\u7684\u8bae\u8bba\u58f0\u4e2d\uff0c\u516b\u5e74\u524d\u7684\u5f80\u4e8b\u4e00\u5e55\u5e55\u5728\u5979\u7684\u5fc3\u5934\u7ffb\u8fc7\uff0c\u51e0\u756a\u9047\u5408\uff0c\u51e0\u756a\u79bb\u522b\uff0c\u6811\u6797\u4e2d\u7684 \u7434\u6b4c\u916c\u7b54\uff0c\u9a8a\u5c71\u4e0a\u7684\u6069\u6028\u7ea0\u7f20\uff0c\u800c\u6700\u540e\u4e00\u6b21\u7684\u79bb\u522b\uff0c\u5219\u662f\u674e\u9038\u5728\u60ac\u5ca9\u4e0a\u8df3\u4e0b\u53bb\uff0c\u5979\u9063\u5c0f \u4e2b\u73af\u53bb\u63a2\u542c\u4ed6\u7684\u6d88\u606f\uff0c\u4ece\u4e2b\u73af\u5982\u610f\u7684\u53e3\u4e2d\uff0c\u77e5\u9053\u4ed6\u9a71\u8f66\u8fdc\u8d74\u7586\u5916\uff0c\u4ee5\u540e\u5c31\u518d\u4e5f\u542c\u4e0d\u5230\u4ed6 \u7684\u6d88\u606f\u4e86\u3002\u5979\u6b23\u6170\u674e\u9038\u8fd8\u5728\u4eba\u95f4\uff0c\u4f46\u751f\u79bb\u6bd4\u6b7b\u522b\u5374\u66f4\u8981\u4ee4\u5979\u4f24\u5fc3\uff0c\u8fd9\u516b\u5e74\u6765\uff0c\u5979\u5df2\u4e0d\u77e5 \u51e0\u6b21\u72ec\u5411\u897f\u98ce\u6d12\u6cea\uff1f\u5979\u77e5\u9053\u674e\u9038\u662f\u7edd\u4e0d\u80af\u56de\u5934\u7684\u4e86\uff0c\u5979\u4e5f\u4e0d\u6307\u671b\u548c\u674e\u9038\u518d\u89c1\u7684\u4e86\u3002\u7136\u800c \u5929\u4e0b\u5f80\u5f80\u6709\u6599\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u6b66\u5219\u5929\u8fd9\u6b21\u5374\u5dee\u9063\u5979\u53bb\u627e\u5bfb\u674e\u9038\u3002 \u3000\u3000\u6b66\u5219\u5929\u5982\u4eca\u5df2\u662f\u4e03\u5341\u5c81\u7684\u8001\u5987\u4eba\u4e86\uff0c\u5979\u8feb\u5207\u9700\u8981\u51b3\u5b9a\u7ee7\u627f\u5e1d\u4f4d\u7684\u4eba\u9009\u3002\u5979\u7684\u4f84\u513f\u6b66 \u627f\u55e3\u5f88\u60f3\u7ee7\u627f\u5979\u7684\u4f4d\u5b50\uff0c\u5979\u89c9\u5f97\u6b66\u627f\u55e3\u7684\u624d\u6c14\u4e0d\u591f\uff0c\u540c\u65f6\u72c4\u4ec1\u6770\u7b49\u4e00\u73ed\u5927\u81e3\u53c8\u5411\u5979\u529d\u8c0f\uff0c \u5979\u4e00\u60f3\u81ea\u5df1\u7684\u513f\u5b50\u867d\u7136\u662f\u788c\u788c\u65e0\u80fd\uff0c\u4f46\u5e1d\u4f4d\u65e2\u4e0d\u80fd\u4f20\u7ed9\u5916\u4eba\uff0c\u4e0e\u5176\u4f20\u7ed9\u4f84\u513f\u4f1a\u5f15\u8d77\u5927\u4e71\uff0c \u4e0d\u5982\u4f20\u7ed9\u513f\u5b50\uff0c\u8fd9\u624d\u51b3\u5b9a\u4e86\u5c06\u6765\u4ee5\u5362\u9675\u738b\u674e\u663e\u590d\u4f4d\u4e3a\u5e1d\u3002\u5979\u6709\u4e86\u8fd9\u4e2a\u51b3\u5b9a\uff0c\u5374\u5c1a\u672a\u5411\u5927 \u81e3\u5ba3\u5e03\u3002\u7531\u4e8e\u5979\u8fd8\u8981\u627e\u5bfb\u5510\u5b9d\u4e2d\u6709\u624d\u5e72\u7684\u4eba\u8f85\u4f50\u5979\u7684\u513f\u5b50\uff0c\u81ea\u7136\u800c\u7136\u7684\u5c31\u60f3\u8d77\u4e86\u674e\u9038\u3002 \u5979\u8981\u6b66\u7384\u971c\u5fc5\u987b\u4e3a\u5979\u628a\u674e\u9038\u627e\u5230\u3002\u8fd9\u4fbf\u662f\u6b66\u7384\u971c\u6765\u5230\u5929\u5c71\u811a\u4e0b\u7684\u539f\u56e0\u3002 \u3000\u3000\u516b\u5e74\u957f\u7684\u65f6\u95f4\u8fc7\u53bb\u4e86\uff0c\u4ed6\u5bf9\u6b66\u5219\u5929\u7684\u4ec7\u6068\uff0c\u4f1a\u4e0d\u4f1a\u968f\u7740\u65f6\u95f4\u7684\u6d88\u901d\u51b2\u6de1\u4e86\u5462\uff1f\u5982\u679c \u4ed6\u77e5\u9053\u4e86\u6b66\u5219\u5929\u7684\u51b3\u5b9a\uff0c\u77e5\u9053\u6c5f\u5c71\u4ecd\u5c06\u56de\u590d\u674e\u59d3\u7684\u624b\u4e2d\uff0c\u4ed6\u4f1a\u4e0d\u4f1a\u56de\u53bb\u5462\uff1f\u54e6\uff0c\u8fd8\u6709\uff0c \u5728\u8fd9\u6f2b\u957f\u7684\u516b\u5e74\u4e4b\u4e2d\uff0c\u4ed6\u53ef\u4e5f\u66fe\u60f3\u8d77\u8fc7\u6211\u4e48\uff1f\u8fd9\u516b\u5e74\u7684\u65f6\u95f4\u4ed6\u662f\u600e\u4e48\u8fc7\u7684\uff1f\u662f\u5b64\u72ec\u884c\u541f\uff0c \u8fd8\u662f\u5df2\u7ecf\u6709\u4e86\u77e5\u5fc3\u7684\u4f34\u4fa3\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u6b66\u7384\u971c\u6240\u8981\u77e5\u9053\u7684\u95ee\u9898\uff0c\u5979\u5728\u5e7b\u60f3\u7740\u548c\u674e\u9038\u76f8\u89c1\u7684\u65f6\u523b\uff0c\u4e0d\u77e5\u662f\u6b22\u559c\u8fd8\u662f \u60b2\u54c0\uff1f\u6b64\u9645\uff0c\u5979\u6765\u5230\u5929\u5c71\u811a\u4e0b\uff0c\u5df2\u7ecf\u6291\u5236\u4e0d\u4f4f\u5fc3\u60c5\u7684\u6fc0\u52a8\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u5728\u95f9\u54c4\u54c4\u7684\u58f0\u4e2d\uff0c\u4ece\u8c08\u8bba\u201c\u5929\u5c71\u5251\u5ba2\u201d\u53c8\u8bf4\u5230\u4e86\u9003\u907f\u5175\u5f79\u7684\u82e6\u51b5\uff0c\u6709\u51e0\u4e2a\u54c8\u8428 \u514b\u65cf\u7684\u59d1\u5a18\u5f39\u8d77\u4e1c\u4e0d\u62c9\uff0c\u5531\u51fa\u8349\u539f\u6d41\u884c\u7684\u5c0f\u8c03\uff1a \u3000\u3000\u201c\u59b9\u5230\u8349\u539f\u53bb\u7267\u7f8a\uff0c \u3000\u3000\u54e5\u5728\u5bb6\u91cc\u64e6\u5200\u67aa\uff1b \u3000\u3000\u5927\u6c57\u4e0b\u4e86\u70b9\u5175\u4ee4\uff0c \u3000\u3000\u5206\u79bb\u5c31\u8981\u5728\u773c\u524d\u3002 \u3000\u3000\u7f8a\u7fa4\u4e00\u4eba\u96be\u7167\u6599\uff0c \u3000\u3000\u7236\u6bcd\u6709\u8c01\u6765\u4f9b\u517b\uff1f \u3000\u3000\u5929\u5c71\u5317\u9762\u5bd2\u98ce\u70c8\uff0c \u3000\u3000\u4e3a\u4e86\u6c42\u751f\u4e5f\u53ea\u5f97\u9003\u5230\u90a3\u4e00\u8fb9\u3002\u201d \u3000\u3000\u95f9\u54c4\u54c4\u7684\u4eba\u4eec\u9759\u4e0b\u6765\u4e86\uff0c\u8fd9\u6b4c\u58f0\u5531\u51fa\u4e86\u4ed6\u4eec\u5171\u540c\u7684\u906d\u9047\u548c\u5fc3\u5883\uff0c\u6709\u4eba\u5782\u6cea\uff0c\u6709\u4eba\u53f9 \u606f\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u6362\u4e00\u4e2a\u60c5\u6b4c\u5531\u5531\u5427\uff0c\u8ba9\u5927\u5bb6\u6109\u5feb\u4e00\u4e9b\u3002\u201d\u54c8\u8428\u514b\u59d1\u5a18\u91cd\u8c03\u5f26\u7d22\uff0c\u53c8\u5531 \u51fa\u4e86\u4e00\u4e2a\u8349\u539f\u6d41\u884c\u7684\u5c0f\u8c03\uff1a \u3000\u3000\u201c\u5927\u98ce\u5377\u8d77\u4e86\u9ec4\u6c99\uff0c \u3000\u3000\u5929\u8fb9\u7684\u5140\u9e70\u76d8\u65cb\u6b32\u4e0b\uff1b \u3000\u3000\u54e5\u5440\uff0c\u4f60\u5c31\u662f\u5929\u8fb9\u7684\u90a3\u53ea\u5140\u9e70\uff0c \u3000\u3000\u4f60\u867d\u7136\u4e0d\u6015\u98ce\u6c99\uff0c\u4f60\u4e5f\u4e0d\u8981\u4e0b\u6765\u5440\uff01 \u3000\u3000\u5927\u98ce\u5377\u8d77\u4e86\u9ec4\u6c99\uff0c \u3000\u3000\u5929\u8fb9\u7684\u5140\u9e70\u76d8\u65cb\u6b32\u4e0b\uff1b \u3000\u3000\u6211\u4e0d\u662f\u4e0d\u6015\u98ce\u6c99\uff0c \u3000\u3000\u59b9\u5440\uff0c\u6211\u662f\u4e3a\u4e86\u8981\u89c1\u4f60\u7684\u9762\uff0c \u3000\u3000\u6211\u8981\u4e58\u98ce\u6765\u627e\u4f60\u56de\u5bb6\uff01\u201d \u3000\u3000\u6b4c\u58f0\u7c97\u8fc8\u7f20\u7ef5\uff0c\u8868\u73b0\u51fa\u8349\u539f\u7537\u5973\u7684\u771f\u60c5\u631a\u7231\uff0c\u4e0d\u5f88\u6109\u5feb\uff0c\u5374\u6781\u52a8\u4eba\uff0c\u6b66\u7384\u971c\u542c\u5f97\u75f4 \u4e86\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u662f\u5140\u9e70\uff0c\u4f46\u6211\u4e5f\u8981\u5192\u7740\u98ce\u6c99\u6765\u627e\u4ed6\u56de\u5bb6\u3002\u201d \u3000\u3000\u9759\u5bc2\u4e2d\u5ffd\u542c\u5f97\u6709\u9a6c\u8e44\u58f0\u54cd\uff0c\u53ea\u89c1\u4e24\u4e2a\u7a81\u53a5\u6b66\u58eb\u75be\u9a70\u800c\u6765\uff0c\u5df2\u5230\u4e86\u8425\u5e10\u5916\u9762\uff0c\u8fd9\u4e9b\u4eba \u90fd\u662f\u9003\u907f\u5175\u707e\u6765\u7684\uff0c\u60f3\u4e0d\u5230\u5728\u5929\u5c71\u811a\u4e0b\uff0c\u8fd8\u78b0\u5230\u6709\u6b66\u58eb\u8ffd\u6765\uff0c\u90fd\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u6c89\uff0c\u4f46\u968f \u5373\u4eba\u4eba\u90fd\u8fd9\u6837\u60f3\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u91cc\u6709\u4e00\u767e\u591a\u4eba\uff0c\u4ed6\u82e5\u662f\u8981\u6349\u62ff\u6211\u4eec\uff0c\u4e00\u4eba\u4e00\u5757\u77f3\u5934\uff0c\u90fd\u80fd \u628a\u4ed6\u6253\u6b7b\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u571f\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u6b63\u5de7\u6709\u4e00\u5339\u5927\u9a86\u9a7c\u6321\u7740\u4ed6\u4eec\u7684\u53bb\u8def\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u5ffd\u5730\u559d\u9053\uff1a \u201c\u8ba9\u5f00\uff01\u201d\u4e00\u4eba\u62b1\u7740\u9a86\u9a7c\u7684\u811a\u4e00\u4eba\u62b1\u7740\u9a86\u9a7c\u7684\u540e\u811a\uff0c\u7adf\u7136\u628a\u90a3\u5339\u9a86\u9a7c\u4e3e\u4e86\u8d77\u6765\uff0c\u4e00\u6454\u5c31 \u6454\u51fa\u6570\u4e08\u4e4b\u5916\uff0c\u8fd9\u5339\u9a86\u9a7c\u5c11\u8bf4\u4e5f\u6709\u5343\u65a4\u6c14\u529b\uff0c\u7adf\u88ab\u8fd9\u4e24\u4e2a\u6b66\u58eb\u5236\u670d\u5f97\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f17\u4eba\u90fd \u5403\u4e86\u4e00\u60ca\uff0c\u6709\u51e0\u4e2a\u66b4\u8e81\u7684\u5c11\u5e74\u672c\u6765\u60f3\u53d1\u4f5c\u7684\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u6562\u4f5c\u58f0\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u571f\u6324\u8fdb\u5806\uff0c\u6413\u6413\u624b\u9053\uff1a\u201c\u597d\u51b7\uff0c\u597d\u51b7\uff01\u201d\u6ca1\u6709\u4eba\u548c\u4ed6\u4eec\u7b54\u8bdd\uff0c\u5728\u4ed6\u4eec\u4e24\u65c1 \u7684\u4eba\u50cf\u9003\u907f\u761f\u795e\u4f3c\u7684\uff0c\u7ad9\u597d\u4e86\u4f4d\u7f6e\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u597d\u751f\u6ca1\u8da3\uff0c\u539a\u7740\u9762\u76ae\u5750\u4e86\u4e0b\u6765\uff0c\u4f38\u51fa\u624b \u53bb\u70e4\u706b\u3002 \u3000\u3000\u521a\u624d\u95f9\u54c4\u54c4\u7684\u6c14\u6c1b\u970e\u65f6\u95f4\u90fd\u9759\u6b62\u4e0b\u6765\uff0c\u8fde\u5b69\u5b50\u4eec\u4e5f\u53d7\u4e86\u611f\u67d3\uff0c\u6709\u4e00\u7fa4\u5b69\u5b50\u672c\u6765\u5728\u5730 \u4e0a\u5806\u7802\u77f3\u73a9\u201c\u780c\u5b9d\u5854\u201d\u7684\u6e38\u620f\u7684\uff0c\u8fd9\u65f6\u4e5f\u505c\u6b62\u4e86\uff0c\u780c\u5230\u4e00\u534a\u7684\u201c\u5b9d\u5854\u201d\u54d7\u5566\u5566\u5012\u584c\u4e86\uff0c \u6709\u4e2a\u5b69\u5b50\u53eb\u4e86\u4e00\u58f0\uff0c\u78b0\u5230\u90a3\u4e24\u4e2a\u6b66\u58eb\u7684\u76ee\u5149\u4fbf\u4e0d\u6562\u558a\u4e86\u3002\u5b69\u5b50\u4eec\u867d\u7136\u4e0d\u61c2\u4e8b\uff0c\u770b\u5230\u5927\u4eba \u4ff1\u6015\uff0c\u4ed6\u4eec\u4e5f\u60e7\u6015\u8d77\u6765\uff0c\u53ea\u6709\u4e00\u4e2a\u5b69\u5b50\u5168\u65e0\u60e7\u610f\uff0c\u9732\u51fa\u597d\u5947\u7684\u795e\u8272\u76ef\u7740\u90a3\u4e24\u4e2a\u6b66\u58eb\uff0c\u5ffd \u5730\u201c\u5478\u201d\u7684\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u780d\u4f60\u7684\u5934\uff01\u201d\u4e00\u638c\u780d\u5728\u201c\u5b9d\u642d\u201d\u8fd8\u672a\u5012\u4e0b\u7684\u90a3\u5757\u201c\u5854\u57fa\u201d\u7684\u77f3 \u5934\u4e0a\uff0c\u90a3\u5757\u77f3\u5934\u5e94\u58f0\u800c\u843d\uff0c\u98de\u51fa\u4e86\u4e00\u4e08\u5f00\u5916\u3002\u6b66\u7384\u971c\u5fc3\u5934\u4e00\u52a8\uff0c\u770b\u8fd9\u5b69\u5b50\u6700\u591a\u4e0d\u8fc7\u516d\u4e03 \u5c81\uff0c\u5c45\u7136\u6709\u8fd9\u6837\u7684\u638c\u52b2\uff0c\u81ea\u662f\u7ec3\u8fc7\u201c\u7ae5\u5b50\u529f\u201d\u7684\u65e0\u7591\u3002\u90a3\u4e24\u4e2a\u6b66\u58eb\u5e76\u4e0d\u751f\u6c14\u3001\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u597d\uff0c\u597d\u6c14\u529b\u3002\u201d\u8fd9\u4e2a\u5b69\u5b50\u6b63\u662f\u90a3\u7ef4\u65cf\u5987\u4eba\u7684\u5b69\u5b50\u3002 \u3000\u3000\u6240\u6709\u7684\u4eba\u90fd\u5bf9\u90a3\u4e24\u4e2a\u6b66\u58eb\u6000\u6709\u654c\u610f\uff0c\u53ea\u6709\u6b66\u7384\u971c\u90a3\u5339\u767d\u9a6c\uff0c\u5374\u548c\u90a3\u4e24\u4e2a\u6b66\u58eb\u9a91\u6765\u7684 \u6218\u9a6c\u73a9\u5728\u4e00\u8d77\uff0c\u8fd9\u5339\u767d\u9a6c\u5403\u9971\u4e86\u8349\u6599\u4e4b\u540e\uff0c\u6062\u590d\u4e86\u529b\u6c14\uff0c\u89c1\u5230\u540c\u7c7b\u5f88\u662f\u6b22\u559c\uff0c\u4e09\u5339\u9a6c\u6328 \u8fd1\u6765\u4e92\u76f8\u64e6\u6469\uff0c\u8e22\u8e22\u8e44\u5b50\uff0c\u53d1\u58f0\u5636\u9e23\uff0c\u597d\u50cf\u6b22\u8fce\u670b\u53cb\u4e00\u822c\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u5750\u4e86\u4e0b\u6765\uff0c\u76ee\u5149\u6ce8\u89c6\u5230\u6b66\u7384\u971c\u7684\u8eab\u4e0a\uff0c\u6b66\u7384\u971c\u5fc3\u9053\uff1a\u201c\u6562\u60c5\u662f\u6765\u8ffd\u6355\u6211 \u7684\uff1f\u201d\u50b2\u7136\u4e0d\u60e7\uff0c\u5c06\u76ee\u5149\u8fce\u4e86\u4e0a\u53bb\u3002\u4e00\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u8fd9\u5339\u767d\u9a6c\u662f\u5173\u5185\u6765\u7684\u5427\uff1f\u771f\u662f\u4e00\u5339 \u597d\u9a6c\uff0c\u8c01\u9a91\u6765\u7684\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u9a91\u6765\u7684\uff0c\u600e\u4e48\uff1f\u201d\u53e6\u4e00\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u9601\u4e0b\u9760\u4e00\u5339\u9a6c \u5c31\u901a\u8fc7\u4e86\u6c99\u6f20\uff0c\u6765\u5230\u4e86\u5929\u5c71\u811a\u4e0b\u4e48\uff1f\u54c8\u54c8\uff0c\u786e\u662f\u503c\u5f97\u4f69\u670d\uff01\u201d\u62ff\u51fa\u534a\u8fb9\u91ce\u732a\uff0c\u5728\u706b\u4e0a\u70e4 \u719f\uff0c\u62d4\u51fa\u4f69\u5200\uff0c\u5272\u4e0b\u4e00\u5757\uff0c\u9012\u7ed9\u6b66\u7384\u971c\u9053\uff1a\u201c\u9601\u4e0b\u8f9b\u82e6\u4e86\uff0c\u4f60\u8fdc\u6765\u662f\u5ba2\uff0c\u54b1\u4eec\u6ca1\u6709\u4ec0\u4e48 \u6b3e\u5f85\uff0c\u4f60\u4e0d\u5acc\u5f03\uff0c\u8bf7\u8d4f\u4e00\u5757\u91ce\u732a\u5427\u3002\u201d\u6b66\u7384\u971c\u63a5\u8fc7\u6765\u4fbf\u5403\uff0c\u51c6\u5907\u548c\u4ed6\u52a8\u624b\uff0c\u90a3\u6b66\u58eb\u5ba2\u5ba2 \u6c14\u6c14\u7684\u9012\u8fc7\u6765\uff0c\u6b8a\u65e0\u52a8\u624b\u4e4b\u610f\u3002 \u3000\u3000\u90a3\u6b66\u58eb\u53c8\u5272\u4e0b\u51e0\u5757\u91ce\u732a\uff0c\u9001\u7ed9\u51e0\u4f4d\u8001\u5e74\u4eba\u3002\u5927\u5bb6\u5728\u6c99\u6f20\u4e0a\u8d70\u4e86\u5c06\u8fd1\u4e00\u4e2a\u6708\uff0c\u5403\u7684\u662f \u5e72\u7cae\uff0c\u9a86\u9a7c\u4e73\uff0c\u90a3\u91ce\u732a\u70e4\u5f97\u9ec4\u6e5b\u6e5b\u7684\uff0c\u9999\u6cb9\u6b32\u6ef4\uff0c\u5b9e\u8db3\u5f15\u8d77\u98df\u6b32\uff0c\u53ef\u662f\u90a3\u51e0\u4e2a\u8001\u4eba\u8fde\u671b \u4e5f\u4e0d\u671b\uff0c\u4e5f\u4e0d\u4f38\u624b\u53bb\u63a5\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u8baa\u8baa\u7684\u5c06\u624b\u7f29\u56de\uff0c\u628a\u534a\u8fb9\u91ce\u732a\u5207\u5f00\u6765\u81ea\u5df1\u5403\u4e86\u3002\u4e00\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u5927\u96ea\u5c01 \u5c71\u5566\u3002\u201d\u53e6\u4e00\u4e2a\u6b66\u58eb\u9053\uff1a\u2018\u54b1\u4eec\u53bb\u63a2\u4e00\u63a2\u8c37\u53e3\u9053\u8def\u5427\uff0c\u770b\u660e\u5929\u80fd\u4e0d\u80fd\u8d70\uff1f\u201d\u4f17\u4eba\u5fc3\u4e2d\u5927 \u559c\uff0c\u4ee5\u4e3a\u8fd9\u4e24\u4e2a\u6b66\u58eb\u81ea\u89c9\u4e0d\u53d7\u6b22\u8fce\uff0c\u6240\u4ee5\u8d70\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u8d70\u8fc7\u90a3\u7fa4\u5b69\u5b50\u7684\u8eab\u65c1\uff0c\u5176\u4e2d\u4e00\u4e2a\u5c06\u5403\u5269\u7684\u4e00\u5757\u91ce\u732a\u9012\u7ed9\u90a3\u7ef4\u65cf\u5987\u4eba\u7684\u5b69 \u5b50\uff0c\u90a3\u5b69\u5b50\u770b\u4e86\u6bcd\u4eb2\u4e00\u773c\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u7684\u4e1c\u897f\uff01\u201d\u90a3\u6b66\u58eb\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u62b1 \u8d77\u8fd9\u4e2a\u5b69\u5b50\uff0c\u500f\u7684\u8df3\u4e0a\u9a6c\u80cc\uff0c\u90a3\u7ef4\u65cf\u5987\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u7acb\u5373\u98de\u8eab\u8dc3\u8d77\uff01 \u3000\u3000\u8fd9\u4e00\u8fde\u4e32\u4e8b\u60c5\u53d1\u751f\u5f97\u592a\u8fc7\u7a81\u7136\uff0c\u56f4\u5750\u70e4\u706b\u7684\u8fd9\u7fa4\u7ef4\u4eba\u56fa\u7136\u662f\u63aa\u624b\u4e0d\u53ca\uff0c\u672b\u66fe\u963b\u62e6\uff0c \u8fde\u6b66\u7384\u971c\u4e5f\u56e0\u4e3a\u4e8b\u51fa\u610f\u5916\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u4f46\u89c1\u90a3\u4e24\u4e2a\u6b66\u58eb\u5df2\u7b56\u9a6c\u75be\u5954\uff1b \u800c\u90a3\u4e2a\u7ef4\u65cf\u5987\u4eba\u4e5f\u5df2\u98de\u8d8a\u4e86\u4e24\u91cd\u5e10\u5e55\uff0c\u62a2\u4e86\u6b66\u7384\u971c\u90a3\u5339\u767d\u9a6c\uff0c\u8d76\u53bb\u8ffd\u4e86\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3 \u5b69\u5b50\u5c16\u9510\u7684\u53eb\u58f0\u51b2\u7834\u4e86\u51dd\u51b7\u7684\u7a7a\u6c14\uff0c\u4ed6\u5728\u5927\u58f0\u53eb\u5524\u201c\u5988\u5988\u201d\uff0c\u53eb\u51fa\u7684\u7adf\u7136\u662f\u6807\u51c6\u7684\u957f\u5b89 \u53e3\u97f3\uff01 \u3000\u3000\u90a3\u7ef4\u65cf\u5987\u4eba\u7684\u5353\u8d8a\u8f7b\u529f\u5df2\u662f\u4ee4\u6b66\u7384\u971c\u5927\u611f\u60ca\u5f02\uff0c\u800c\u5979\u7684\u5b69\u5b50\u4f1a\u8bf4\u4e2d\u56fd\u8bdd\uff0c\u800c\u4e14\u662f\u6807 \u51c6\u7684\u957f\u5b89\u53e3\u97f3\uff0c\u8fd9\u5c31\u66f4\u4ee4\u6b66\u7384\u971c\u60ca\u5947\u3002\u770b\u90a3\u5b69\u5b50\u6e05\u79c0\u7684\u76f8\u8c8c\uff0c\u83ab\u975e\u4ed6\u672c\u6765\u662f\u6c49\u4eba\u7684\u5b69\u5b50\uff1f \u4f46\u4ed6\u4e3a\u4ec0\u4e48\u521a\u624d\u88c5\u505a\u4e0d\u61c2\u6c49\u8bed\uff0c\u5979\u6bcd\u4eb2\u53c8\u4e0d\u51c6\u4ed6\u63a5\u53d7\u6c49\u4eba\u7684\u4e1c\u897f\uff1f\u8fd9\u5b69\u5b50\u5230\u4e86\u5371\u6025\u7684\u65f6 \u5019\uff0c\u81ea\u81ea\u7136\u7136\u7684\u8bf4\u51fa\u957f\u5b89\u53e3\u97f3\u7684\u9655\u897f\u8bdd\u6765\uff0c\u8db3\u89c1\u4ed6\u5e73\u65e5\u5728\u5bb6\u4e2d\u5fc5\u5b9a\u662f\u548c\u7236\u6bcd\u8bf4\u8fd9\u79cd\u5bb6\u4e61 \u8bdd\u7684\u3002 \u3000\u3000\u6b66\u7384\u971c\u4e00\u6014\u4e4b\u4e0b\uff0c\u4f46\u89c9\u8fd9\u91cc\u9762\u542b\u6709\u8bb8\u591a\u96be\u4e8e\u7d22\u89e3\u7684\u7591\u56e2\uff0c\u4f46\u6b64\u65f6\u6b64\u5730\uff0c\u5df2\u4e0d\u5bb9\u5979\u7ec6 \u5fc3\u63a8\u7a76\u4e86\uff0c\u5c31\u5728\u4f17\u4eba\u7eb7\u7eb7\u8d77\u54c4\u7684\u65f6\u5019\uff0c\u5979\u75be\u5fd9\u5c55\u5f00\u4e86\u7edd\u9876\u8f7b\u529f\uff0c\u5411\u524d\u8ffd\u53bb\uff0c\u4f17\u7ef4\u4eba\u4f46\u89c1 \u5979\u811a\u6b65\u4e00\u8d77\uff0c\u4fbf\u4f3c\u4e00\u9635\u98ce\u7684\u5728\u773c\u524d\u63a0\u8fc7\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u96ea\u5730\u4e0a\u4fbf\u53ea\u89c1\u4e00\u70b9\u9ed1\u70b9\uff0c\u4ed6\u4eec\u90a3\u66fe \u89c1\u8fc7\u8dd1\u5f97\u8fd9\u6837\u5feb\u7684\u4eba\uff1f\u60ca\u6115\u4e0d\u5df2\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u5bf9\u5566\uff01\u8fd9\u4e2a\u5c11\u5e74\u624d\u662f\u5929\u5c71\u5251\u5ba2\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u5df2\u542c\u4e0d\u5230\u80cc\u540e\u7ef4\u4eba\u7684\u8bae\u8bba\u4e86\uff0c\u5979\u4e00\u5fc3\u4e00\u610f\u8ffd\u8d76\u524d\u9762\u90a3\u4e09\u5339\u9a8f\u9a6c\uff0c\u5728\u6700\u521d\u7684\u4e00 \u6bb5\u65f6\u95f4\uff0c\u5341\u591a\u91cc\u7684\u8def\u6f20\u4e4b\u5185\uff0c\u5979\u4e0d\u8fc7\u843d\u540e\u5c11\u5c11\uff0c\u4f46\u4ecd\u7136\u662f\u6697\u5668\u6253\u4e0d\u5230\u7684\u8ddd\u79bb\uff0c\u5979\u5927\u58f0\u547c \u53eb\uff0c\u90a3\u7ef4\u65cf\u5987\u4eba\u4e0d\u77e5\u662f\u6025\u75af\u4e86\uff0c\u8fd8\u662f\u56e0\u4e3a\u5bf9\u5979\u6000\u6709\u654c\u610f\uff0c\u6ca1\u6709\u7b54\u5979\u534a\u53e5\u8bdd\u3002\u8fc7\u4e86\u5341\u591a\u91cc \u540e\uff0c\u6b66\u7384\u971c\u7684\u8f7b\u529f\u7eb5\u7136\u597d\uff0c\u662f\u4e5f\u65e0\u6cd5\u4e0e\u65e5\u884c\u6570\u767e\u91cc\u7684\u9a8f\u9a6c\u76f8\u6bd4\uff0c\u597d\u5728\u96ea\u5730\u4e0a\u9a6c\u8e44\u7684\u75d5\u8ff9 \u975e\u5e38\u6e05\u695a\uff0c\u5979\u8ddf\u7740\u9a6c\u8e44\u7684\u75d5\u8ff9\uff0c\u4e00\u76f4\u8ffd\u5230\u4e86\u524d\u9762\u7684\u5c71\u811a\uff0c\u9a6c\u8e44\u7684\u75d5\u8ff9\u4e0d\u89c1\u4e86\uff0c\u60f3\u5fc5\u90a3\u4e24 \u4e2a\u7a81\u53a5\u6b66\u58eb\u548c\u7ef4\u65cf\u5987\u4eba\u90fd\u5df2\u4e0a\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u5ea7\u5c71\u662f\u5929\u5c71\u5c71\u8109\u7684\u4e00\u90e8\u5206\uff0c\u867d\u7136\u4e0d\u662f\u9ad8\u8038\u5165\u4e91\uff0c\u5374\u4e5f\u751a\u4e3a\u9669\u5cfb\uff0c\u5c24\u5176\u5728\u5927\u96ea\u5c01\u5c71 \u4e4b\u540e\uff0c\u66f4\u4e3a\u96be\u8d70\uff0c\u6b66\u7384\u971c\u65bd\u5c55\u201c\u767b\u840d\u6e21\u6c34\u201d\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u6500\u767b\u4e86\u4e24\u5ea7\u5c71\u5cf0\uff0c\u5230\u4e86\u4e00\u5904\u5c71 \u62d7\uff1b\u5ffd\u542c\u5f97\u524d\u9762\u6709\u5175\u5668\u78b0\u51fb\u7684\u53ae\u6740\u58f0\u97f3\u3002 \u3000\u3000\u6b66\u7384\u971c\u8d70\u51fa\u5c71\u62d7\uff0c\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u524d\u9762\u91cc\u8bb8\u4e4b\u9065\uff0c\u90a3\u7ef4\u65cf\u5987\u4eba\u6b63\u5728\u548c\u4e00\u4e2a\u7a81\u53a5\u6b66\u58eb \u53ae\u6740\uff0c\u90a3\u7ef4\u5987\u7528\u7684\u662f\u4e00\u67c4\u957f\u5251\uff0c\u5251\u6cd5\u5c55\u5f00\uff0c\u6709\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u8f7b\u7075\u8fc5\u6377\uff0c\u843d\u5728\u6b66\u7384\u971c\u8fd9\u6837 \u7684\u884c\u5bb6\u773c\u4e2d\uff0c\u4e00\u770b\u4fbf\u77e5\u9053\u662f\u4e2d\u539f\u6b66\u6797\u7684\u540d\u5bb6\u4e00\u8109\u3002\u90a3\u7a81\u53a5\u6b66\u58eb\u4f7f\u7684\u662f\u4e00\u6761\u957f\u97ad\uff0c\u97ad\u68a2\u5230 \u5904\uff0c\u96ea\u5757\u7eb7\u98de\uff0c\u6b66\u529f\u4ea6\u662f\u751a\u4e3a\u4e0d\u5f31\uff0c\u6bd4\u8d77\u6b66\u7384\u971c\u524d\u9047\u89c1\u7684\u90a3\u51e0\u4e2a\u6b66\u58eb\u9ad8\u51fa\u591a\u591a\uff0c\u6fc0\u6218\u4e2d \u53ea\u542c\u5f97\u90a3\u6b66\u58eb\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u4f1a\u4f24\u5bb3\u4f60\u7684\u513f\u5b50\u7684\uff0c\u4f60\u56de\u53bb\u5427\uff0c\u8fc7\u4e24\u5929\u6211\u4eec\u81ea\u4f1a\u9001\u8fd8\u7ed9\u4f60\uff01\u201d \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u5929\u5c71\u51b0\u96ea\u79cd\u60c5\u6839 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u5341\u516d\u56de\u3000\u5929\u5c71\u51b0\u96ea\u79cd\u60c5\u6839 \u3000\u3000\u8fd9\u8bf4\u8bdd\u6b66\u7384\u971c\u542c\u4e86\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u5fc3\u9053\uff1a\u201c\u5c82\u6709\u62a2\u4e86\u522b\u4eba\u7684\u5b69\u5b50\u95f9\u7740\u73a9\u7684\uff1f\u201d\u90a3\u7ef4\u65cf\u5987 \u4eba\u66f4\u662f\u6025\u5f97\u75af\u4e86\uff0c\u6839\u672c\u4e0d\u7406\u4f1a\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u53ea\u662f\u53eb\u9053\uff1a\u201c\u8fd8\u6211\u513f\u5b50\uff0c\u8fd8\u6211\u513f\u5b50\uff01\u201d\u4e00\u5251 \u7d27\u4f3c\u4e00\u5251\uff0c\u5c71\u8def\u9669\u5cfb\uff0c\u8c01\u8981\u662f\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u90fd\u6709\u8dcc\u4e0b\u60ac\u5ca9\uff0c\u788e\u9aa8\u7c89\u8eab\u4e4b\u9669\u3002\u6fc0\u6218\u4e2d\uff0c\u4f46\u89c1 \u90a3\u7ef4\u65cf\u5987\u4eba\u597d\u50cf\u4e00\u53ea\u8d1f\u4e86\u4f24\u7684\u6bcd\u72ee\u5b50\u4e00\u6837\uff0c\u72c2\u6012\u8fdb\u653b\uff0c\u4e00\u62db\u201c\u76f4\u6307\u5929\u5357\u201d\uff0c\u5251\u5149\u95ea\u5904\uff0c \u5728\u90a3\u7a81\u53a5\u6b66\u571f\u7684\u624b\u81c2\u4e0a\u5212\u4e86\u4e00\u9053\u957f\u957f\u7684\u53e3\u5b50\u3002 \u3000\u3000\u90a3\u7a81\u53a5\u6b66\u571f\u559d\u9053\uff1a\u201c\u4f60\u8ba8\u6b7b\u4e48\uff1f\u518d\u4e0d\u4f4f\u624b\uff0c\u6211\u4fbf\u4e0d\u7559\u60c5\u4e86\u3002\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u90a3\u7ef4\u65cf\u5987 \u4eba\u53c8\u662f\u201c\u5530\u201d\u7684\u4e00\u5251\uff0c\u7a81\u53a5\u6b66\u58eb\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u5934\u4e0a\u7684\u94dc\u8c6b\u88ab\u5251\u5288\u843d\uff0c\u8feb\u5f97\u8fde\u9000 \u51e0\u6b65\uff0c\u5df2\u8e0f\u5230\u4e86\u60ac\u5ca9\u7684\u8fb9\u6cbf\u3002\u7a81\u53a5\u6b66\u58eb\u52c3\u7136\u5927\u6012\uff0c\u957f\u97ad\u731b\u7684\u63a3\u56de\uff0c\u4e00\u4e2a\u2018\u602a\u87d2\u7ffb\u8eab\u201d\uff0c \u5587\u7684\u4e00\u4e2a\u76d8\u625b\uff0c\u957f\u97ad\u5929\u77eb\uff0c\u76f4\u5411\u90a3\u7ef4\u5987\u7684\u53f3\u80a9\u626b\u5230\uff0c\u90a3\u7ef4\u65cf\u5987\u4eba\u7adf\u4e0d\u9000\u8ba9\uff0c\u5251\u950b\u5916\u5c55\uff0c \u4e00\u62db\u201c\u5e73\u6c99\u843d\u96c1\u201d\uff0c\u8d34\u7740\u97ad\u8eab\uff0c\u4e0a\u524a\u654c\u4eba\u7684\u624b\u6307\uff0c\u7a81\u53a5\u6b66\u58eb\u559d\u58f0\uff1a\u201c\u6eda\u4e0b\u53bb\u5427\uff01\u201d\u957f\u97ad \u4e00\u6536\uff0c\u731b\u5730\u4e00\u5377\uff0c\u5377\u7740\u4e86\u8fd9\u7ef4\u65cf\u5987\u4eba\u7684\u9752\u94dc\u5251\uff0c\u7528\u529b\u4e00\u62d6\uff0c\u8fd9\u7ef4\u65cf\u5987\u4eba\u7acb\u8db3\u4e0d\u7a33\uff0c\u201c\u8f70 \u9686\u201d\u4e00\u58f0\uff0c\u8e22\u7ffb\u4e86\u4e00\u5757\u5927\u77f3\u3002\u4eba\u4e5f\u5230\u4e86\u60ac\u5ca9\u7684\u8fb9\u6cbf\uff0c\u8fd9\u65f6\u4e24\u4e2a\u4eba\u7684\u8eab\u8eaf\u90fd\u5728\u60ac\u5ca9\u8fb9\u6cbf\u6447 \u6447\u6643\u6643\uff0c\u5371\u9669\u4e07\u72b6\u3002\u90a3\u7a81\u53a5\u6b66\u58eb\u731b\u5730\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff0c\u5410\u6c14\u5f00\u58f0\uff0c\u5de6\u638c\u95ea\u7535\u80a1\u7684\u5411\u90a3\u7ef4\u65cf \u5987\u4eba\u7684\u5929\u7075\u76d6\u76f4\u51fb\u4e0b\u6765\uff0c\u638c\u98ce\u8d77\u5904\uff0c\u7802\u77f3\u7eb7\u98de\uff0c\u773c\u770b\u8fd9\u7ef4\u65cf\u5987\u4eba\u4fbf\u8981\u4e27\u8eab\u5728\u4ed6\u638c\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u6b66\u7384\u971c\u634f\u8d77\u4e86\u4e00\u56e2\u96ea\u7403\uff0c\u5df2\u662f\u8d76\u5230\u4e86\u79bb\u4ed6\u4eec\u6570\u4e08\u4e4b\u5730\uff0c\u89c1\u72b6 \u4e0d\u5999\uff0c\u96ea\u7403\u7acb\u5373\u98de\u51fa\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u6b63\u6253\u4e2d\u4e86\u90a3\u7a81\u53a5\u6b66\u58eb\u80f8\u53e3\u7684\u201c\u7487\u8ba5\u7a74\u201d\uff0c\u90a3\u7a81 \u53a5\u6b66\u571f\u7a81\u7136\u611f\u5230\u80f8\u53e3\u51b0\u51c9\uff0c\u7acb\u5373\u5168\u8eab\u9178\u9ebb\uff0c\u957f\u97ad\u677e\u5f00\uff0c\u5f80\u540e\u4e00\u4ef0\uff0c\u5760\u4e0b\u5c71\u8c37\u3002\u90a3\u7ef4\u65cf\u5987 \u4eba\u72c2\u53eb\u9053\uff1a\u201c\u6211\u7684\u513f\u5b50\uff0c\u6211\u7684\u513f\u5b50\uff01\u4ed6\u4eec\u62a2\u4e86\u6211\u7684\u513f\u5b50\u4e86\uff01\u201d\u6b66\u7384\u971c\u89c1\u6b64\u60c5\u5f62\uff0c\u60f3\u5fc5\u662f \u53e6\u4e00\u4e2a\u6b66\u571f\u65e9\u5df2\u628a\u5979\u7684\u513f\u5b50\u63b3\u8d70\uff0c\u8fd9\u4e2a\u6b66\u58eb\u4e43\u662f\u7559\u4e0b\u6765\u62e6\u622a\u5979\u7684\u3002\u5f53\u4e0b\u6025\u5fd9\u4e0a\u524d\uff0c\u5c06\u90a3 \u7ef4\u65cf\u5987\u4eba\u6276\u7740\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u5a18\uff0c\u4f60\u9759\u4e00\u9759\uff0c\u54b1\u4eec\u6162\u6162\u5546\u91cf\u3002\u201d \u3000\u3000\u90a3\u7ef4\u65cf\u5987\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u51dd\u89c6\u770b\u6b66\u7384\u971c\u7684\u9762\u5e9e\uff0c\u9732\u51fa\u975e\u5e38\u5947\u5f02\u7684\u795e\u8272\u3002\u7a81\u7136\u53cc\u638c\u4e00\u63a8\uff0c \u5c16\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u8fd1\u6211\uff01\u201d\u6b66\u7384\u971c\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u5934\u4e0a\u7684\u76ae\u5e3d\u9664\u4e0b\uff0c\u53c8\u628a\u4e0a\u8eab\u7684\u730e\u88c5\u8131\u4e86\uff0c \u8bf4\u9053\uff1a\u201c\u5927\u5a18\uff0c\u4e0d\u8981\u5bb3\u6015\uff0c\u6211\u662f\u5973\u7684\uff01\u201d \u3000\u3000\u90a3\u7ef4\u65cf\u5987\u4eba\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\uff0c\u731b\u5730\u53eb\u9053\uff1a\u201c\u6b66\u7384\u971c\uff0c\u6b66\u7384\u971c\uff0c\u6211\u77e5\u9053\u4f60\u662f\u6b66\u7384\u971c\uff01\u597d \u5427\uff0c\u4f60\u773c\u89c1\u6211\u53d7\u4e86\u8fd9\u573a\u707e\u96be\uff0c\u4f60\u5e94\u8be5\u9ad8\u5174\u4e86\u5427\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u5979\u7a81\u7136\u95f4\u6539\u7528\u6c49\u8bed\u8bf4\uff0c\u58f0\u97f3 \u4f3c\u66fe\u76f8\u8bc6\uff0c\u597d\u50cf\u4ee5\u524d\u542c\u8fc7\u4e00\u822c\uff0c\u6b66\u7384\u971c\u5411\u5979\u8138\u4e0a\u4e00\u671b\uff0c\u4e0d\u89c9\u5fc3\u5934\u4e00\u60ca\uff0c\u60ca\u5f97\u5446\u4e86\uff01\u8fd9\u7ef4 \u65cf\u5987\u4eba\u7ade\u662f\u5979\u610f\u60f3\u4e0d\u5230\u7684\u4e00\u4e2a\u719f\u4eba\uff01 \u3000\u3000\u6cea\u6c34\u51b2\u6de1\u4e86\u5979\u9762\u4e0a\u7684\u6cb9\u5f69\uff0c\u7528\u7709\u7b14\u63cf\u753b\u7684\u6d53\u9ed1\u7684\u7709\u6bdb\u4e5f\u88ab\u6cea\u6c34\u6d17\u53bb\u4e86\uff0c\u8fd9\u201c\u7ef4\u65cf\u5987 \u4eba\u201d\u73b0\u51fa\u4e86\u5979\u672c\u6765\u7684\u9762\u76ee\uff0c\u6b66\u7384\u971c\u5446\u4e86\u4e00\u5446\uff0c\u7acb\u523b\u4fbf\u8ba4\u51fa\u4e86\u5979\uff0c\u5979\u662f\u957f\u5b59\u5747\u91cf\u7684\u5973\u513f\u2014 \u2014\u957f\u5b59\u58c1\uff01 \u3000\u3000\u6b66\u7384\u971c\u53c8\u60ca\u53c8\u559c\uff0c\u60f3\u4e0d\u5230\u5728\u5929\u5c71\u811a\u4e0b\u7adf\u7136\u9047\u5230\u4e86\u4e00\u4e2a\u76f8\u8bc6\u7684\u4eba\uff0c\u867d\u7136\u8fd9\u4e2a\u4eba\u662f\u5bf9\u5979 \u6000\u6709\u6210\u89c1\u7684\uff0c\u53ef\u662f\u5728\u8fd9\u6837\u9065\u8fdc\u7684\u5f02\u4e61\uff0c\u53ea\u8981\u662f\u9047\u5230\u4e00\u4e2a\u540c\u80a4\u8272\u7684\u4e2d\u56fd\u4eba\u5df2\u8db3\u4ee5\u4ee4\u5979\u559c\u60a6 \u4e86\uff0c\u4f55\u51b5\u957f\u5b59\u58c1\u8fd8\u662f\u548c\u5979\u6709\u8fc7\u4e00\u6bb5\u6e0a\u6e90\u7684\u719f\u4eba\uff01 \u3000\u3000\u6b66\u7384\u971c\u7d27\u7d27\u63e1\u7740\u5979\u7684\u624b\uff0c\u4e00\u8fde\u4e32\u7684\u95ee\u9898\u4e0d\u77e5\u5148\u95ee\u54ea\u4e00\u6837\u597d\uff0c\u5979\u5b9a\u4e86\u5b9a\u795e\uff0c\u968f\u5373\u6025\u4fc3 \u95ee\u9053\uff1a\u201c\u957f\u5b59\u59d1\u5a18\uff0c\u90a3\u662f\u4f60\u7684\u5b69\u5b50\u5417\uff1f\u4f60\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u6211\u4e00\u5b9a\u60f3\u529e\u6cd5\u66ff\u4f60\u627e\u56de\u6765\u3002\u54a6\uff0c\u4f60 \u600e\u4e48\u5566\uff1f\u5582\uff0c\u5582\uff0c\u6211\u5411\u4f60\u6253\u542c\u4e00\u4e2a\u4eba\uff0c\u542c\u8bf4\u674e\u9038\u4e5f\u9003\u5230\u4e86\u8fd9\u513f\uff0c\u4f60\u77e5\u9053\u4ed6\u7684\u4e0b\u843d\u5417\uff1f\u5582\uff0c \u5582\uff0c\u4f60\u542c\u89c1\u6211\u8bf4\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u957f\u5b59\u58c1\u624b\u8db3\u51b0\u51b7\uff0c\u9762\u8272\u60e8\u767d\uff0c\u795e\u60c5\u662f\u51fa\u4e4e\u610f\u6599\u7684\u51b7\u6f20\uff0c\u7a81\u7136\u95f4\u5979\u773c\u4e2d\u5c04\u51fa\u4ec7\u6068\u7684\u5149 \u8292\uff0c\u4e00\u628a\u7529\u8131\u4e86\u6b66\u7384\u971c\u7684\u624b\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u5047\u6148\u60b2\uff0c\u6211\u7edd\u4e0d\u6cbe\u4eba\u7684\u6069\u60e0\u3002\u4f60\u8981\u627e \u674e\u9038\u4f60\u81ea\u5df1\u627e\u53bb\uff01\u201d \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u9053\u7406\uff1f\u201d\u201c\u4e3a\u4ec0\u4e48\u5979\u5bf9\u6211\u8fd9\u6837\uff1f\u201d\u6b66\u7384\u971c\u6115\u4f4f\u4e86\u3002\u4e0d\u9519\uff0c\u6b66\u7384\u971c\u77e5\u9053\u5979 \u4eec\u7236\u5973\u662f\u6548\u5fe0\u5510\u5ba4\uff0c\u53cd\u5bf9\u6b66\u5219\u5929\u7687\u5e1d\u7684\uff0c\u53ef\u662f\u4ec5\u4ec5\u4e3a\u4e86\u8fd9\u4e2a\u539f\u56e0\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u81f3\u4e8e\u9732\u51fa\u90a3 \u6837\u6028\u6bd2\u7684\u795e\u60c5\u5427\uff1f\u6b66\u7384\u971c\u5fc3\u5934\u4e00\u51c9\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u957f\u5b59\u59d1\u5a18\uff0c\u4f60\u9192\u9192\u5427\u3002\u6211\u5bf9\u4f60\u6ca1\u6709\u4e1d \u6beb\u574f\u610f\uff0c\u4f60\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u8bbf\u5bfb\u674e\u9038\u5417\uff1f\u55ef\uff0c\u4e5f\u8bb8\u4f60\u4f1a\u559c\u6b22\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\u2026\u2026\u201d\u5979\u6b63\u60f3 \u628a\u5219\u5929\u7687\u5e1d\u8981\u4f20\u4f4d\u513f\u5b50\uff0c\u6062\u590d\u5510\u671d\u6b63\u7edf\u7684\u4e8b\u60c5\u8bf4\u7ed9\u5979\u542c\uff0c\u957f\u5b59\u58c1\u5374\u7a81\u7136\u4e00\u58f0\u5c16\u53eb\uff0c\u628a\u5979 \u7684\u8bf4\u8bdd\u6253\u65ad\u4e86\uff0c\u53ea\u542c\u5f97\u957f\u5b59\u58c1\u6068\u6068\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u8981\u89c1\u4f60\u3002\u6211\u4e5f\u4e0d\u8981\u542c\u4f60\u7684\u4efb\u4f55\u8bf4\u8bdd\uff0c \u597d\u5566\uff0c\u6211\u5411\u4f60\u6c42\u60c5\u5566\uff0c\u4f60\uff0c\u4f60\u8d70\u5f00\u5427\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u9000\u540e\u51e0\u6b65\uff0c\u60ca\u7591\u4e0d\u5df2\uff0c\u832b\u7136\u7684\u671b\u7740\u957f\u5b59\u58c1\uff0c\u4e0d\u77e5\u518d\u8bf4\u4ec0\u4e48\u8bdd\u597d\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c \u5ffd\u542c\u5f97\u5728\u5bf9\u9762\u7684\u5c71\u5cf0\u4e0a\u6709\u4eba\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u58c1\u59b9\uff01\u662f\u4f60\u5728\u4e0b\u9762\u5417\uff1f\u5feb\u6765\u5440\uff0c\u6211\u627e\u5230 \u4e00\u6735\u96ea\u83b2\uff01\u654f\u513f\uff0c\u4f60\u542c\u5230\u7239\u7239\u53eb\u4f60\u5417\uff1f\u201d\u6b66\u7384\u971c\u5fc3\u5934\u5927\u9707\uff0c\u662f\u8fd9\u6837\u719f\u6089\u7684\u58f0\u97f3\uff0c\u867d\u7136\u9694 \u4e86\u516b\u5e74\uff0c\u5979\u4e00\u542c\u5c31\u542c\u51fa\u662f\u674e\u9038\u7684\u58f0\u97f3\uff01 \u3000\u3000\u957f\u5b59\u58c1\u4e00\u58f0\u5c16\u53eb\uff0c\u7acb\u523b\u4fbf\u8dd1\u3002\u6b66\u7384\u971c\u5374\u5168\u8eab\u4e4f\u529b\uff0c\u53cc\u8db3\u51e0\u4e4e\u4e0d\u80fd\u7ad9\u7a33\uff0c\u66f4\u4e0d\u8981\u8bf4\u8d70 \u52a8\u4e86\u3002\u8fd9\u5239\u90a3\u95f4\u5979\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\uff0c\u539f\u6765\u90a3\u5b69\u5b50\u4e43\u662f\u674e\u9038\u548c\u957f\u5b59\u58c1\u7684\u5b69\u5b50\uff0c\u4ed6\u4eec\u65e9\u5df2\u7ed3\u4e3a \u592b\u5987\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u5446\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u8111\u4e2d\u7a7a\u7a7a\u6d1e\u6d1e\uff0c\u597d\u50cf\u795e\u7ecf\u5168\u90fd\u9ebb\u6728\uff0c\u4e00\u5207\u90fd\u89c9\u5f97\u832b\u7136\uff0c\u62ac\u8d77 \u5934\u6765\uff0c\u5728\u96ea\u767d\u7684\u5c71\u5cf0\u4e0a\u3002\u9690\u7ea6\u8fd8\u53ef\u4ee5\u770b\u5230\u957f\u5b59\u58c1\u7684\u5f71\u5b50\u3002 \u3000\u3000\u597d\u4e45\uff0c\u597d\u4e45\uff0c\u6b66\u7384\u971c\u597d\u50cf\u4ece\u4e00\u4e2a\u5947\u602a\u7684\u68a6\u4e2d\u9192\u6765\uff0c\u957f\u5b59\u58c1\u7684\u5f71\u5b50\u4e0d\u89c1\u4e86\uff0c\u674e\u9038\u7684\u58f0 \u97f3\u5374\u8fd8\u4f3c\u5728\u5979\u8033\u8fb9\u98d8\u8361\u3002\u4f46\u613f\u8fd9\u4e9b\u662f\u201c\u68a6\u4e2d\u7684\u5e7b\u5f71\u201d\uff0c\u53ef\u60dc\u8fd9\u4e0d\u662f\u68a6\uff0c\u96ea\u5730\u4e0a\u8fd8\u7559\u6709\u957f \u5b59\u58c1\u7684\u8db3\u5370\u3002 \u3000\u3000\u5979\u5343\u8f9b\u4e07\u82e6\u7684\u6765\u627e\u5bfb\u674e\u9038\uff0c\u542c\u5230\u4e86\u4ed6\u7684\u58f0\u97f3\u4e86\uff0c\u5374\u53c8\u8ba9\u4ed6\u8fc7\u53bb\u4e86\u3002\u6b66\u7384\u971c\u7b2c\u4e00\u6b21\u53d1 \u89c9\u4e86\u81ea\u5df1\u7684\u602f\u5f31\uff0c\u4e5f\u53d1\u89c9\u4e86\u957f\u5b59\u58c1\u7684\u602f\u5f31\uff0c\u539f\u6765\u5979\u7528\u90a3\u6837\u6ee1\u6000\u6028\u6068\u7684\u76ee\u5149\u63a5\u5f85\u81ea\u5df1\uff0c\u662f \u56e0\u4e3a\u5979\u63a9\u9970\u4e0d\u4f4f\u5979\u5fc3\u4e2d\u7684\u60e7\u6015\u3002 \u3000\u3000\u96ea\u82b1\u98d8\u843d\u6b66\u7384\u971c\u7684\u8eab\u4e0a\uff0c\u6b66\u7384\u971c\u8fce\u7740\u5bd2\u98ce\uff0c\u5438\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u6e10\u6e10\u6e05\u9192\u8fc7\u6765\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u96be\u9053\u6211\u5c31\u6b64\u4e0d\u518d\u89c1\u4ed6\uff1f\u4e0d\uff0c\u8fc7\u53bb\u7684\u4e8b\u5c31\u8ba9\u5b83\u8fc7\u53bb\u5427\uff0c\u6211\u5e94\u8be5\u6709\u8fd9\u4efd\u52c7\u6c14\u89c1\u4ed6\uff0c\u5c06 \u7687\u5e1d\u7684\u51b3\u5b9a\u544a\u8bc9\u4ed6\uff0c\u4e0d\u7ba1\u4ed6\u613f\u4e0d\u613f\u56de\u6765\u8f85\u4f50\u4ed6\u7684\u5144\u5f1f\uff0c\u4ed6\u542c\u5230\u5510\u5ba4\u6062\u590d\u7684\u6d88\u606f\uff0c\u6700\u5c11\u4e5f \u53ef\u4ee5\u5fc3\u60c5\u6bd4\u8f83\u8212\u5feb\u5427\uff1f\u5373\u7b97\u4e3a\u4e86\u4ed6\u548c\u957f\u5b59\u58c1\u7684\u5e78\u798f\uff0c\u6211\u4e5f\u5e94\u8be5\u8ba9\u4ed6\u4eec\u77e5\u9053\u8fd9\u4e2a\u6d88\u606f\uff0c\u4f7f \u4ed6\u4eec\u4e0d\u81f3\u6c38\u8fdc\u98d8\u6cca\u5929\u6daf\uff0c\u6291\u90c1\u7ec8\u8001\uff01\u201d\u6b66\u7384\u971c\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u6781\u529b\u6291\u5236\u4e0b\u5fc3\u5e95\u7684\u54c0\u4f24\uff0c\u4e00 \u6b65\u4e00\u6b65\uff0c\u8e0f\u7740\u957f\u5b59\u58c1\u7684\u8db3\u5370\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u6b66\u7384\u971c\u5728\u5fcd\u53d7\u7740\u75db\u82e6\u7684\u6298\u78e8\uff0c\u4f46\u957f\u5b59\u58c1\u6240\u611f\u53d7\u7684\u75db\u82e6\u5374\u6bd4\u5979\u8fd8\u8981\u6df1\u91cd\u3002\u8fd9\u516b\u5e74\u6765\u5979 \u7684\u65e5\u5b50\u8fc7\u5f97\u5341\u5206\u751c\u871c\uff0c\u53ef\u662f\u5728\u751c\u871c\u4e4b\u4e2d\uff0c\u5979\u7684\u5fc3\u5e95\u6df1\u5904\u5374\u85cf\u6709\u9690\u5fe7\u3002\u662f\u7684\uff0c\u674e\u9038\u5bf9\u5979\u975e \u5e38\u4f53\u8d34\uff0c\u53ef\u662f\u5979\u611f\u89c9\u5f97\u5230\uff0c\u674e\u9038\u5e76\u4e0d\u5feb\u4e50\uff01\u5979\u66fe\u4e0d\u6b62\u4e00\u6b21\u53d1\u73b0\uff0c\u674e\u9038\u5728\u72ec\u81ea\u6c89\u601d\uff0c\u6216\u8005 \u5728\u5f39\u594f\u53e4\u7434\uff0c\u4ece\u4ed6\u7684\u795e\u8272\u4e0e\u7434\u58f0\u4e4b\u4e2d\uff0c\u4e5f\u61c2\u5f97\u4ed6\u662f\u5728\u8ffd\u6000\u5f80\u4e8b\u3002\u8fd9\u516b\u5e74\u4e2d\uff0c\u674e\u9038\u4ece\u672a\u5728 \u5979\u7684\u9762\u524d\u63d0\u8d77\u8fc7\u6b66\u7384\u971c\uff0c\u4e5f\u6ca1\u6709\u63d0\u8d77\u4e0a\u5b98\u5a49\u513f\u3002\u53ef\u662f\u957f\u5b59\u58c1\u77e5\u9053\u4ed6\u662f\u6c38\u4e0d\u4f1a\u5fd8\u8bb0\u5979\u4eec\u7684\uff01 \u957f\u5b59\u58c1\u5e38\u5e38\u8fd9\u6837\u7684\u60f3\uff1a\u201c\u5982\u679c\u5979\u4eec\u4e4b\u4e2d\u7684\u4efb\u4f55\u4e00\u4e2a\u6765\u5230\u8fd9\u91cc\uff0c\u4ed6\u5c06\u4f1a\u600e\u4e48\u6837\u5462\uff1f\u201d\u6599\u4e0d \u5230\u8981\u6765\u7684\u679c\u7136\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u6765\u5230\u4e86\u5929\u5c71\u4e4b\u540e\u7684\u7b2c\u4e8c\u5e74\u7ed3\u5a5a\uff0c\u90a3\u5e74\u5e74\u5e95\u4fbf\u751f\u4e86\u4e00\u4e2a\u513f\u5b50\uff0c\u53d6\u540d\u5e0c\u654f\u3002\u674e\u9038\u672c \u6765\u6295\u5954\u4ed6\u7684\u5e08\u7236\u7684\uff0c\u4ed6\u7684\u987a\u7236\u5c09\u8fdf\u70af\u81ea\u4ece\u6b66\u5219\u5929\u6267\u653f\u4e4b\u540e\uff0c\u4fbf\u9003\u5230\u5929\u5c71\u9690\u5c45\u4e86\u3002\u867d\u662f\u9690 \u5c45\uff0c\u6709\u65f6\u4e5f\u5728\u8349\u539f\u4e0a\u5e72\u4e9b\u884c\u4fa0\u4ed7\u4e49\u7684\u4e8b\uff0c\u8349\u539f\u4e0a\u7684\u7267\u6c11\uff0c\u90fd\u77e5\u9053\u5929\u5c71\u4e0a\u6709\u4e00\u4f4d\u9690\u5c45\u7684\u6c49 \u65cf\u5f02\u4eba\uff0c\u4fbf\u79f0\u505a\uff1a\u201c\u5929\u5c71\u5251\u5ba2\u201d\u3002\u674e\u9038\u5230\u4e86\u5929\u5c71\u4e4b\u540e\u4e0d\u4e45\u3002\u4ed6\u7684\u5e08\u7236\u75c5\u6b7b\uff0c\u674e\u9038\u627f\u7ee7\u4e86 \u4ed6\u5e08\u7236\u7684\u6b66\u529f\uff0c\u4e5f\u7ef4\u627f\u4e86\u4ed6\u5e08\u7236\u201c\u5929\u5c71\u5251\u5ba2\u201d\u7684\u79f0\u53f7\u3002\u674e\u9038\u8fd8\u4fdd\u5b58\u6709\u590f\u4faf\u575a\u7ed9\u4ed6\u7684\u6613\u5bb9 \u4e39\uff0c\u4e24\u592b\u59bb\u5e38\u5e38\u53d8\u8c8c\u6613\u5bb9\uff0c\u8f6e\u6d41\u4e0b\u5c71\uff0c\u957f\u5b59\u58c1\u7231\u6253\u626e\u6210\u7ef4\u65cf\u5987\u4eba\u7684\u6a21\u6837\uff0c\u674e\u9038\u5219\u603b\u662f\u4ee5 \u6c49\u4eba\u7684\u9762\u76ee\u51fa\u73b0\uff0c\u8fd9\u4e00\u6765\u662f\u4e3a\u4e86\u7eaa\u5ff5\u5e08\u7236\uff0c\u4e8c\u6765\u4e5f\u662f\u4e3a\u4e86\u8ba9\u7267\u6c11\u5bf9\u6c49\u4eba\u4fdd\u6709\u4e00\u4efd\u597d\u611f\uff0c \u56e0\u6b64\u4ed6\u867d\u7136\u4ee5\u6c49\u4eba\u7684\u9762\u76ee\u51fa\u73b0\uff0c\u5374\u4e5f\u6b22\u559c\u626e\u6210\u4e0d\u540c\u5e74\u9f84\u3001\u4e0d\u540c\u76f8\u8c8c\u7684\u6c49\u4eba\u3002\u8fd9\u5c31\u662f\u4e3a\u4ec0 \u4e48\u7267\u6c11\u4e2d\u7684\u201c\u5929\u5c71\u5251\u5ba2\u201d\u5404\u5404\u4e0d\u540c\u7684\u539f\u6545\u3002 \u3000\u3000\u5929\u5c71\u811a\u4e0b\uff0c\u6bcf\u5e74\u5728\u51ac\u5b63\u6765\u4e34\u4e4b\u524d\uff0c\u603b\u6709\u4e00\u4e9b\u5916\u5730\u7684\u5546\u4eba\u4e58\u7740\u9a86\u9a7c\u6765\u5230\u8349\u539f\uff0c\u5e26\u6765\u7267 \u6c11\u4eec\u6240\u9700\u8981\u7684\u8d27\u7269\uff0c\u4ed6\u4eec\u5728\u8349\u539f\u4e0a\u652f\u8d77\u5e10\u5e55\uff0c\u6210\u7acb\u4e00\u4e2a\u77ed\u671f\u7684\u3001\u6d41\u52a8\u7684\u5e02\u96c6\u3002\u957f\u5b59\u58c1\u8fd9 \u6b21\u643a\u5e26\u5b69\u5b50\u4e0b\u5c71\uff0c\u5c31\u662f\u4e3a\u4e86\u5907\u529e\u51ac\u8d27\uff0c\u51c6\u5907\u8fc7\u5e74\u7684\u3002\u6599\u4e0d\u5230\u56e0\u4e3a\u7a81\u53a5\u53ef\u6c57\u7684\u201c\u5927\u70b9\u5175\u201d\uff0c \u6765\u5230\u5929\u5c71\u811a\u4e0b\u7684\u53ea\u6709\u9003\u96be\u7684\u4eba\u7fa4\uff0c\u4eca\u5e74\u4f8b\u5916\u7684\u6ca1\u6709\u5e02\u96c6\u4e86\u3002\u957f\u5b59\u58c1\u975e\u5e38\u626b\u5174\uff0c\u66f4\u60f3\u4e0d\u5230 \u5728\u5f52\u9014\u4e4b\u4e2d\uff0c\u5b69\u5b50\u88ab\u6b66\u58eb\u63b3\u53bb\uff0c\u800c\u4e14\u53c8\u610f\u5916\u7684\u78b0\u5230\u4e86\u6b66\u7384\u971c\u3002 \u3000\u3000\u957f\u5b59\u58c1\u8fd9\u4e00\u751f\u4e2d\u906d\u8fc7\u65e0\u6570\u7684\u707e\u96be\uff0c\u54e5\u54e5\u7684\u5931\u6563\uff0c\u7236\u4eb2\u7684\u6b7b\u4ea1\uff0c\u4e07\u91cc\u9003\u4ea1\uff0c\u8352\u5c71\u7ed3\u5b85\uff0c \u98ce\u971c\u96e8\u96ea\uff0c\u98a0\u6c9b\u6d41\u79bb\uff0c\u8fd9\u4e9b\u82e6\u96be\uff0c\u5979\u90fd\u201c\u633a\u201d\u8fc7\u53bb\u4e86\uff0c\u56e0\u4e3a\u5728\u8fd9\u4e9b\u82e6\u96be\u7684\u65e5\u5b50\u91cc\uff0c\u6709\u5979 \u6700\u4eb2\u7231\u7684\u4eba\u966a\u4f34\u7740\u5979\uff0c\u652f\u6301\u7740\u5979\u7684\u52c7\u6c14\u3002\u8fd9\u6b21\u53c8\u4e00\u4e2a\u66f4\u5927\u7684\u707e\u96be\u964d\u4e34\u4e86\uff0c\u5979\u65e2\u662f\u4f24\u5fc3\uff0c \u53c8\u662f\u5bb3\u6015\uff0c\u4f24\u5fc3\u5b69\u5b50\u7684\u88ab\u63b3\uff0c\u5bb3\u6015\u6b66\u7384\u971c\u593a\u8d70\u5979\u6700\u4eb2\u7231\u7684\u4eba\uff0c\u6b66\u7384\u971c\u4eb2\u53e3\u8bf4\u7684\uff0c\u5979\u6765\u5230 \u8fd9\u513f\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u5bfb\u627e\u674e\u9038\u554a\uff01 \u3000\u3000\u5979\u89c1\u5230\u674e\u9038\u4e4b\u65f6\uff0c\u53ea\u8bf4\u5f97\u4e00\u53e5\u201c\u654f\u513f\u88ab\u4eba\u62a2\u53bb\u4e86\uff01\u201d\u5c31\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u6655\u5012\u674e\u9038\u7684 \u6000\u4e2d\uff0c\u76f4\u5230\u674e\u9038\u5c06\u5979\u62b1\u56de\u5bb6\u4e2d\uff0c\u5979\u624d\u82cf\u9192\u3002 \u3000\u3000\u674e\u9038\u4e5f\u7ed9\u8fd9\u4e2a\u610f\u5916\u7684\u6253\u51fb\u5413\u614c\u4e86\uff0c\u5f85\u5f97\u4ed6\u7684\u59bb\u5b50\u7cbe\u795e\u7a0d\u68a2\u6062\u590d\u4e4b\u540e\uff0c\u4fbf\u5373\u67e5\u95ee\u7ecf\u8fc7\u3002 \u957f\u5b59\u58c1\u628a\u90a3\u4e24\u4e2a\u6b66\u58eb\u63b3\u8d70\u513f\u5b50\u7684\u60c5\u5f62\uff0c\u5411\u674e\u9038\u8bb2\u4e86\u4e00\u904d\uff0c\u4f46\u5979\u5374\u7792\u7740\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5979\u610f\u5916 \u7684\u78b0\u89c1\u4e86\u6b66\u7384\u971c\u3002 \u3000\u3000\u674e\u9038\u975e\u5e38\u8be7\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5728\u8349\u539f\u4e0a\u53ef\u66fe\u6740\u8fc7\u7a81\u53a5\u6b66\u58eb\u4e48\uff1f\u201d\u8fd9\u4e9b\u65e5\u5b50\u91cc\uff0c\u7a81\u53a5\u7684 \u6b66\u58eb\u5e38\u5e38\u5728\u8349\u539f\u8ffd\u6355\u9003\u907f\u5175\u5f79\u7684\u4eba\uff0c\u674e\u9038\u4ee5\u4e3a\u662f\u59bb\u5b50\u8def\u89c1\u4e0d\u5e73\uff0c\u60f9\u4e86\u522b\u7684\u7a81\u53a5\u6b66\u58eb\uff0c\u4ee5 \u5f15\u8d77\u4ed6\u4eec\u540c\u4f34\u7684\u62a5\u590d\u3002\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u4ed6\u4eec\u8ba4\u5f97\u51fa\u4f60\u662f\u6c49\u4eba\u4e48\uff1f\u201d \u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u4eec\u4e0d\u4f1a\u8ba4\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u674e\u9038\u5947\u602a\u6781\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u654f\u513f\u4eca\u5e74\u521a\u521a\u4e03\u5c81\uff0c\u4ed6\u4eec\u5c06\u4ed6\u63b3\u53bb\u6709\u4ec0\u4e48\u7528\uff1f\u7a81\u53a5\u6b66\u58eb\u7eb5 \u7136\u6b8b\u66b4\uff0c\u4e5f\u603b\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u4f24\u5bb3\u4e00\u4e2a\u5b69\u5b50\u7684\u3002\u83ab\u975e\u4ed6\u4eec\u89c1\u654f\u513f\u957f\u5f97\u806a\u660e\u4f36\u4fd0\uff0c\u62b1\u4ed6\u53bb\u73a9 \u5417\uff1f\u58c1\u59b9\uff0c\u4f60\u653e\u5fc3\uff0c\u54b1\u4eec\u603b\u80fd\u5c06\u5b69\u5b50\u627e\u56de\u6765\u7684\u3002\u201d\u4ed6\u4e00\u9762\u5b89\u6170\u957f\u5b59\u58c1\uff0c\u4e00\u9762\u601d\u7d22\u539f\u56e0\uff0c \u201c\u7a81\u53a5\u6b66\u571f\u4e3a\u4ec0\u4e48\u8981\u63b3\u8d70\u6211\u7684\u5b69\u5b50\u5462\uff1f\u201d\u60f3\u6765\u60f3\u53bb\uff0c\u5b9e\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u5916\u9762\u98ce\u96ea\u6b63\u6d53\uff0c\u674e\u9038\u8d1f\u624b\u6c89\u601d\uff0c\u5728\u5c4b\u5b50\u91cc\u8d70\u6765\u8d70\u53bb\u3002\u957f\u5b59\u58c1\u7acb\u5728\u7a97\u53e3\uff0c\u671b\u7740\u5916\u9762\u6f2b \u5929\u7684\u96ea\u82b1\uff0c\u5979\u7684\u5fc3\u4e5f\u597d\u50cf\u51b7\u5f97\u8981\u51dd\u7ed3\u4e86\u3002\u7a81\u7136\u5979\u56de\u8f6c\u4e86\u5934\uff0c\u5e7d\u5e7d\u95ee\u9053\uff1a\u201c\u674e\u9038\u54e5\u54e5\uff0c\u4f60 \u548c\u6211\u7ed3\u4e86\u5a5a\u8fd9\u4e48\u591a\u5e74\uff0c\u4f60\u5f53\u771f\u672a\u66fe\u540e\u6094\u8fc7\u5417\uff1f\u201d\u8fd9\u53e5\u8bdd\u7a81\u5982\u5176\u6765\uff0c\u4ee4\u5f97\u674e\u9038\u83ab\u540d\u5176\u5999\uff0c \u201c\u5979\u4e3a\u4ec0\u4e48\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u5374\u8fd8\u6709\u5fc3\u601d\u60f3\u522b\u7684\u4e8b\u60c5\uff0c\u95ee\u6211\u8fd9\u4e2a\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u63a5\uff0c\u674e\u9038\u89c1\u5979\u795e\u60c5\u75db\u82e6\uff0c\u800c\u4e14\u751a\u662f\u8ba4\u771f\uff0c\u5728\u7b49\u5f85\u7740\u4ed6\u7684\u56de\u7b54\u3002\u674e\u9038\u6697\u53f9 \u4e86\u53e3\u6c14\uff0c\u8d70\u4e0a\u53bb\u8f7b\u8f7b\u629a\u5979\u7684\u5934\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u5e74\u4e86\uff0c\u4f60\u8fd8\u4e0d\u76f8\u4fe1\u6211\u5417\uff1f\u8fd9\u4e00\u53e5\u8bdd\uff0c \u6211\u8bb0\u5f97\u516b\u5e74\u4e4b\u524d\uff0c\u4f60\u5df2\u66fe\u95ee\u8fc7\u6211\u4e86\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u73b0\u5728\u6211\u8fd8\u8981\u518d\u95ee\u4e00\u904d\u3002\u201d\u674e\u9038\u9053\uff1a \u201c\u6211\u7684\u7b54\u590d\u548c\u516b\u5e74\u524d\u5b8c\u5168\u4e00\u6837\u3002\u5f53\u65f6\u6211\u6ca1\u6709\u540e\u6094\uff0c\u73b0\u5728\u66f4\u6ca1\u6709\u540e\u6885\u3002\u58c1\u59b9\uff0c\u4f60\u4f11\u8981\u80e1\u601d \u4e71\u60f3\uff0c\u73b0\u5728\u6700\u7d27\u8981\u7684\u4e8b\u60c5\u662f\u600e\u6837\u60f3\u6cd5\u5b50\u628a\u654f\u513f\u627e\u56de\u6765\u3002\u201d\u957f\u5b59\u58c1\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d \u9519\u3002\u4e00\u5b9a\u8981\u60f3\u6cd5\u5b50\u628a\u654f\u513f\u627e\u56de\u6765\u3002\u201d\u5979\u5fc3\u4e0a\u653e\u4e0b\u4e86\u4e00\u5757\u77f3\u5934\uff0c\u53e6\u4e00\u5757\u77f3\u5934\u53c8\u538b\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u6025\u4fc3\u7684\u811a\u6b65\u58f0\uff0c\u63a5\u7740\u6709\u4eba\u6263\u95e8\uff0c\u957f\u5b59\u58c1\u9762\u8272\u94c1\u9752\uff0c\u60f3\u9053\uff1a \u201c\u6765\u4e86\uff0c\u6765\u4e86\uff01\u5979\u7ec8\u4e8e\u627e\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d\u674e\u9038\u4e5f\u597d\u751f\u5947\u602a\uff0c\u4ed6\u5728\u8fd9\u513f\uff0c\u5e76\u65e0\u670b\u53cb\uff0c\u662f\u8c01 \u6765\u6572\u4ed6\u7684\u95e8\uff1f \u3000\u3000\u95e8\u662f\u865a\u63a9\u7684\uff0c\u674e\u9038\u8fdf\u8fdf\u4e0d\u53bb\u5f00\u95e8\uff0c\u90a3\u4eba\u4fbf\u63a8\u5f00\u4e86\u95e8\u8d70\u8fdb\u6765\u4e86\uff0c\u5927\u5927\u51fa\u4e4e\u957f\u5b59\u58c1\u610f\u6599\uff0c \u6765\u7684\u5e76\u4e0d\u662f\u6b66\u7384\u971c\uff0c\u800c\u662f\u4e00\u4e2a\u9ad8\u5927\u7684\u7a81\u53a5\u6b66\u58eb\uff01 \u3000\u3000\u4e24\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u957f\u5b59\u58c1\u5ffd\u5730\u4e00\u58f0\u5c16\u53eb\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u662f\u4ed6\uff0c\u5c31\u662f\u4ed6\uff01\u662f\u4ed6\u62a2 \u53bb\u4e86\u6211\u4eec\u654f\u513f\uff01\u201d\u90a3\u7a81\u53a5\u6b66\u58eb\u606d\u606d\u656c\u656c\u7684\u5411\u674e\u9038\u65bd\u4e86\u4e00\u793c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u662f\u5949\u53ef\u6c57\u4e4b\u547d\u6765 \u8bf7\u6bbf\u4e0b\u7684\u3002\u5c0f\u6bbf\u4e0b\u6ca1\u6709\u5931\u6389\u4e00\u6839\u6c57\u6bdb\uff0c\u8bf7\u4f60\u4eec\u653e\u5fc3\u3002\u201d\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u957f\u5b59\u58c1\u5df2\u62d4\u5251\u51fa\u9798\uff0c \u674e\u9038\u4f7f\u773c\u8272\u5c06\u5979\u963b\u6b62\u3002 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u5931\u656c\uff0c\u5931\u656c\uff0c\u539f\u6765\u4f60\u662f\u53ef\u6c57\u7684\u4f7f\u8005\u3002\u8bf7\u95ee\u4e3a\u4ec0\u4e48\u8981\u63b3\u8d70\u5c0f\u513f\uff1f\u201d\u90a3\u7a81\u53a5 \u6b66\u58eb\u9053\uff1a\u201c\u8bf7\u6bbf\u4e0b\u53bb\u89c1\u6211\u4eec\u7684\u5927\u6c57\uff0c\u81ea\u7136\u5c31\u4f1a\u660e\u767d\u201d\u3002\u674e\u9038\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e00\u4e2a\u907f\u96be\u9690\u5c45 \u7684\u5c71\u91ce\u4e4b\u6c11\uff0c\u4f60\u8bf7\u54ea\u4e00\u4f4d\u6bbf\u4e0b\uff1f\u201d\u90a3\u7a81\u53a5\u6b66\u58eb\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\u4f55\u5fc5\u9690\u7792\uff0c\u6211\u4eec\u65e9\u5df2\u77e5 \u9053\u4e86\u4f60\u662f\u5927\u5510\u7687\u5ba4\u7684\u9f99\u5b50\u9f99\u5b59\uff0c\u8fd9\u8bb8\u591a\u5e74\u6765\uff0c\u8ba9\u4f60\u51b7\u843d\u8352\u5c71\uff0c\u591a\u591a\u6020\u6162\uff0c\u5b9e\u5c5e\u4e0d\u606d\u3002\u5927 \u6c57\u6015\u8bf7\u4e0d\u52a8\u4f60\u8fd9\u4f4d\u8d35\u5ba2\uff0c\u6240\u4ee5\u53ea\u597d\u5148\u628a\u5c0f\u6bbf\u4e0b\u8bf7\u53bb\uff0c\u671b\u6bbf\u4e0b\u4f53\u8c05\u6211\u4eec\u5927\u6c57\u7684\u8fd9\u7247\u82e6\u5fc3\u3002\u201d \u674e\u9038\u89c1\u8eab\u4efd\u5df2\u88ab\u4ed6\u4eec\u8bc6\u7834\uff0c\u53ea\u597d\u9ed8\u8ba4\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u907f\u96be\u8d35\u56fd\uff0c\u53ea\u6c42\u5b89\u5c45\u3002\u800c \u4e14\u73b0\u5728\u4e2d\u56fd\u5e76\u4e0d\u662f\u5927\u5510\u7684\u5929\u4e0b\uff0c\u6211\u53c8\u4e0d\u662f\u5949\u6709\u7687\u547d\u7684\u4f7f\u8005\uff0c\u8d35\u56fd\u5927\u6c57\u56e0\u4f55\u8981\u89c1\u6211\uff0c\u82e5\u4e0d \u8bf4\u4e2a\u6e05\u695a\uff0c\u674e\u9038\u65ad\u65ad\u4e0d\u6562\u5949\u547d\u3002\u201d \u3000\u3000\u90a3\u7a81\u53a5\u6b66\u58eb\u9732\u51fa\u8be1\u5f02\u7684\u7b11\u5bb9\uff0c\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60\u6d2a\u798f\u9f50\u5929\uff0c\u6211\u4eec\u7684\u5927\u6c57\u51b3\u5fc3\u5e2e\u4f60\u6062 \u590d\u5927\u5510\u5929\u4e0b\uff0c\u8bf7\u4f60\u53bb\u5546\u91cf\u5927\u4e8b\u201d\u3002\u674e\u9038\u8be7\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5927\u6c57\u8981\u5e2e\u6211\u6062\u590d\u5927\u5510\u5929\u4e0b\uff0c\u8fd9\u4ece \u4f55\u8bf4\u8d77\uff1f\u201d\u90a3\u7a81\u53a5\u6b66\u58eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8981\u5e2e\u4f60\u767b\u4e2d\u56fd\u5929\u5b50\u7684\u5b9d\u5ea7\uff0c\u91cd\u5149\u4f60\u5927\u5510\u674e\u59d3\u7684 \u6c5f\u5c71\uff0c\u5b9e\u5728\u544a\u8bc9\u4f60\u5427\uff0c\u4f60\u4eec\u4e2d\u56fd\u73b0\u5728\u7684\u8fd9\u4f4d\u5973\u7687\u5e1d\u592a\u53ef\u6076\u4e86\uff0c\u5979\u8981\u8d77\u5175\u6253\u6211\u4eec\uff0c\u6211\u4eec\u7684 \u5927\u6c57\u53ea\u6709\u5148\u53d1\u5236\u4eba\uff0c\u5148\u6253\u8fdb\u4e2d\u56fd\u53bb\uff0c\u5c06\u5979\u6d88\u706d\u3002\u54c8\u54c8\uff0c\u8fd9\u5c82\u4e0d\u662f\u4f60\u7684\u673a\u4f1a\u6765\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u6c89\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u662f\u7a81\u53a5\u53ef\u6c57\u7528\u8fd9\u7b49\u5a01\u8feb\u5229\u8bf1\u7684\u624b\u6bb5\uff0c\u60f3\u6211\u987a\u4ece\u4e8e\u4ed6\uff0c \u5e2e\u4ed6\u62a2\u593a\u4e2d\u534e\u7684\u9526\u7ee3\u6c5f\u5c71\uff01\u201d\u90a3\u7a81\u53a5\u6b66\u58eb\u7b49\u4e86\u534a\u664c\uff0c\u4e0d\u89c1\u56de\u7b54\uff0c\u8be7\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u8fd9\u771f\u662f \u767e\u8f7d\u96be\u9022\u7684\u673a\u4f1a\u5475\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u7591\u8651\uff1f\u201d\u674e\u9038\u52c3\u7136\u8bf4\u9053\uff1a\u201c\u70e6\u4f60\u56de\u8986\u5927\u6c57\uff0c\u6211\u674e\u9038\u5b81\u6b7b \u4e5f\u4e0d\u4f1a\u4ece\u4ed6\u3002\u201d\u90a3\u7a81\u53a5\u6b66\u58eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5012\u5947\u4e86\uff0c\u6b66\u5219\u5929\u62a2\u593a\u4e86\u4f60\u4eec\u59d3\u674e\u7684\u6c5f\u5c71\uff0c\u4f60\u5c31 \u4e0d\u6068\u5979\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u6068\u6b66\u5219\u5929\u662f\u53e6\u4e00\u4ef6\u4e8b\uff0c\u6211\u82e5\u5f15\u4f60\u4eec\u5165\u5173\uff0c\u5360\u9886\u4e2d\u56fd\u7684\u571f\u5730\uff0c \u8e42\u8e8f\u4e2d\u56fd\u7684\u767e\u59d3\uff0c\u6211\u5c82\u4e0d\u6210\u4e86\u79bd\u517d\u4e0d\u5982\u7684\u53db\u56fd\u4e4b\u5f92\uff1f\u201d\u7a81\u53a5\u6b66\u58eb\u7b11\u9053\uff1a\u201c\u62a2\u6765\u4e86\u4e2d\u56fd\u7687 \u5e1d\u7684\u5b9d\u5ea7\uff0c\u53ef\u662f\u4ea4\u7ed9\u4f60\u5750\u7684\u5475\uff01\u201d\u674e\u9038\u6012\u9053\uff1a\u201c\u6211\u5c82\u662f\u505a\u513f\u7687\u5e1d\u7684\u4eba\uff1f\u4f60\u518d\u591a\u8bf4\uff0c\u5403\u6211 \u4e00\u5251\uff01\u201d \u3000\u3000\u90a3\u7a81\u53a5\u6b66\u58eb\u9000\u540e\u4e00\u6b65\uff0c\u5978\u7b11\u9053\uff1a\u201c\u7687\u5e1d\u7684\u5b9d\u5ea7\u4f60\u53ef\u4ee5\u4e0d\u8981\uff0c\u4f60\u7684\u4eb2\u751f\u513f\u5b50\u4e5f\u4e0d\u8981\u4e86 \u4e48\uff1f\u201d\u674e\u9038\u9762\u8272\u94c1\u9752\uff0c\u53c8\u6c14\u53c8\u6012\uff0c\u957f\u5b59\u58c1\u5ffd\u5730\u4e00\u58f0\u5c16\u53eb\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u500f\u7684\u5c31\u5241\u5c06\u8fc7\u53bb\uff0c \u559d\u9053\uff1a\u201c\u4f60\u62a2\u4e86\u6211\u7684\u513f\u5b50\uff0c\u6211\u5148\u8981\u4f60\u7684\u547d\uff01\u201d\u5979\u6fc0\u52a8\u8fc7\u5ea6\uff0c\u8fd9\u4e00\u5251\u7528\u529b\u592a\u731b\uff0c\u90a3\u7a81\u53a5\u6b66 \u58eb\u4e00\u4e2a\u95ea\u8eab\uff0c\u987a\u624b\u4e00\u5e26\uff0c\u957f\u5b59\u58c1\u7ad9\u7acb\u4e0d\u7a33\uff0c\u5148\u8dcc\u5012\u4e86\uff01 \u3000\u3000\u674e\u9038\u518d\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u7acb\u5373\u4e00\u638c\u62cd\u51fa\uff0c\u90a3\u7a81\u53a5\u6b66\u58eb\u7528\u4e86\u4e00\u62db\u201c\u9738\u738b\u5378\u7532\u201d\uff0c\u53cc\u638c\u56de\u73af \u7275\u5f15\uff0c\u89e3\u62c6\u674e\u9038\u7684\u653b\u52bf\uff0c\u5c82\u77e5\u674e\u9038\u7684\u6b66\u529f\u6bd4\u957f\u5b59\u58c1\u9ad8\u660e\u5f97\u591a\uff0c\u4ed6\u8fd9\u4e00\u638c\u62cd\u51fa\uff0c\u65e9\u5df2\u6599\u5230 \u5bf9\u65b9\u8981\u5982\u6b64\u89e3\u62c6\uff0c\u7acb\u5373\u62a2\u8fdb\u4e00\u6b65\uff0c\u53cd\u624b\u4e00\u52fe\uff0c\u5c06\u7a81\u53a5\u6b66\u58eb\u7684\u624b\u8155\u52fe\u4f4f\uff0c\u5de6\u638c\u7acb\u5373\u8ddf\u7740\u62cd \u51fa\uff0c\u90a3\u7a81\u53a5\u6b66\u571f\u4fa7\u8eab\u4e00\u95ea\uff0c\u5df2\u662f\u95ea\u907f\u4e0d\u5f00\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u88ab\u674e\u9038\u6253\u4e86\u4e00\u8bb0\u8033\u5149\u3002 \u90a3\u7a81\u53a5\u6b66\u58eb\u624b\u8155\u4e00\u6c89\uff0c\u6323\u8131\u4e86\u674e\u9038\u7684\u53cd\u624b\u64d2\u62ff\uff0c\u8e09\u8e09\u8dc4\u8dc4\u7684\u5012\u9000\u4e09\u6b65\u3002 \u3000\u3000\u674e\u9038\u89c1\u4ed6\u6b66\u529f\u4e0d\u5f31\uff0c\u6b63\u5f85\u518d\u65bd\u6740\u624b\u3002\u90a3\u7a81\u53a5\u6b66\u58eb\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6562\u6740\u6211\uff1f\u4f60\u6740 \u4e86\u6211\uff0c\u4f60\u7684\u513f\u5b50\u5c31\u8981\u7ed9\u6211\u507f\u547d\uff01\u5927\u6c57\u65e9\u5df2\u9632\u5230\u4f60\u4eec\u8fd9\u4e9b\u5357\u86ee\u4e0d\u53ef\u9760\uff0c\u6211\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u5c31 \u5bf9\u6211\u8bf4\u9053\uff0c\u4f60\u653e\u5fc3\u4e00\u4e2a\u4eba\u53bb\uff0c\u90a3\u59d3\u674e\u7684\u6562\u52a8\u4f60\u4e00\u6839\u6beb\u6bdb\uff0c\u54fc\uff0c\u54fc\uff0c\u6211\u5c31\u62ff\u90a3\u4e2a\u5c0f\u86ee\u5b50\uff0c \u53bb\u5582\u72fc\u3002\u4f60\u7684\u513f\u5b50\u73b0\u5728\u5df2\u7ecf\u7528\u5feb\u9a6c\u9001\u5230\u5927\u6c57\u90a3\u513f\u53bb\u4e86\uff0c\u4f60\u613f\u559d\u656c\u9152\u8fd8\u662f\u613f\u559d\u7f5a\u9152\uff0c\u968f\u4f60 \u81ea\u4fbf\uff01\u201d\u674e\u9038\u6c14\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u4f46\u4e00\u60f3\u6740\u4e86\u4ed6\u4e5f\u6ca1\u6709\u7528\uff0c\u53ea\u597d\u7531\u4ed6\u53bb\u3002\u90a3\u7a81\u53a5\u6b66\u58eb\u51fa\u95e8\u4e4b \u65f6\uff0c\u8fd8\u5728\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5927\u6c57\u7ed9\u4f60\u4e00\u4e2a\u6708\u7684\u671f\u9650\uff0c\u82e5\u7136\u8fc7\u4e86\u671f\u9650\uff0c\u4e0d\u89c1\u4eba\u9cde\uff0c\u90a3\u53ef\u4f11\u602a\u6211 \u4eec\u65e0\u60c5\u3002\u201d \u3000\u3000\u674e\u9038\u5c06\u59bb\u5b50\u6276\u4e86\u8d77\u6765\uff0c\u90a3\u7a81\u53a5\u6b66\u58eb\u5df2\u8d70\u5f97\u4e0d\u89c1\u4e86\u3002\u957f\u5b59\u58c1\u56b7\u7740\u8981\u8ffd\uff0c\u674e\u9038\u9053\uff1a\u201c\u8ffd \u4ed6\u6709\u4ec0\u4e48\u7528\uff1f\u54fc\uff0c\u54fc\uff0c\u60f3\u4e0d\u5230\u4e00\u56fd\u5927\u6c57\uff0c\u5374\u505a\u51fa\u8fd9\u79cd\u5351\u9119\u52fe\u5f53\uff01\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u7a81\u7136\u4e0b\u4e86 \u51b3\u5fc3\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u81ea\u5df1\u53bb\u89c1\u4ed6\uff01\u201d\u957f\u5b59\u58c1\u60ca\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u53bb\u89c1\u4ed6\uff0c\u4f60\uff0c\u4f60\uff01\u201d\u674e\u9038\u9053\uff1a \u201c\u6211\u5f53\u7136\u4e0d\u662f\u53bb\u987a\u4ece\u4ed6\uff0c\u6211\u662f\u53bb\u60f3\u6cd5\u5b50\u628a\u654f\u513f\u6551\u56de\u6765\u3002\u58c1\u59b9\uff0c\u4f60\u5728\u5bb6\u91cc\u8981\u81ea\u5df1\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u957f\u5b59\u58c1\u9053\uff1a\u201c\u54b1\u4eec\u592b\u59bb\u751f\u6b7b\u4e0e\u5171\uff0c\u51b3\u4e0d\u5206\u79bb\uff0c\u6211\u4e0e\u4f60\u4e00\u540c\u53bb\u3002\u201d\u674e\u9038\u8f7b\u8f7b\u6276\u6478\u5979\u7684 \u79c0\u53d1\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u4f60\u653e\u5fc3\uff0c\u4ed6\u4eec\u8981\u7684\u662f\u6211\u8fd9\u4e2a\u4eba\uff0c\u5373\u7b97\u88ab\u4ed6\u4eec\u64d2\u4f4f\uff0c\u4ed6\u4eec\u4e5f\u4e0d \u4f1a\u5c06\u6211\u6740\u4e86\u7684\uff0c\u6211\u81ea\u4f1a\u76f8\u673a\u800c\u9003\uff0c\u4f55\u51b5\u672a\u5fc5\u4f1a\u843d\u5728\u4ed6\u4eec\u7684\u624b\u4e2d\u5462\u3002\u591a\u5c11\u5927\u98ce\u5927\u6d6a\u6211\u90fd\u7ecf \u8fc7\u6765\u4e86\uff0c\u6b66\u5219\u5929\u6211\u90fd\u4e0d\u6015\uff0c\u8fd8\u6015\u533a\u533a\u4e00\u4e2a\u7a81\u53a5\u53ef\u6c57\u5417\uff1f\u4f60\u53d7\u7684\u9707\u52a8\u8fc7\u751a\uff0c\u7cbe\u795e\u5c1a\u672a\u6062\u590d\uff0c \u8fd8\u662f\u5728\u5bb6\u9759\u517b\u7684\u597d\u3002\u81f3\u8fdf\u4e00\u4e2a\u6708\u540e\uff0c\u6211\u5c31\u4f1a\u4e0e\u654f\u513f\u56de\u6765\u7684\u3002\u4f60\u4e00\u5411\u76f8\u4fe1\u6211\uff0c\u542c\u6211\u7684\u8bdd\uff0c \u8fd9\u56de\u5c31\u4e0d\u76f8\u4fe1\u4e86\u5417\uff1f\u201d\u957f\u5b59\u58c1\u77e5\u9053\u4e08\u592b\u7684\u6b66\u529f\u667a\u8ba1\u90fd\u80dc\u5979\u5341\u500d\uff0c\u82e5\u7136\u540c\u53bb\uff0c\u53ea\u6015\u771f\u7684\u53cd \u4f1a\u62d6\u7d2f\u4e8e\u4ed6\uff0c\u60f3\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u9038\u54e5\u54e5\uff0c\u6211\u76f8\u4fe1\u4f60\u3002\u53ea\u662f\uff0c\u6211\u6015\uff0c\u6211\u6015\u2026\u2026\u201d \u674e\u9038\u5fae\u7b11\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u5931\u6389\u4e86\u54e5\u54e5\uff0c\u5931\u6389\u4e86\u7236\u4eb2\uff0c\u73b0\u5728\u53c8\u5931\u6389\u4e86\u513f \u5b50\uff0c\u6211\u6015\uff0c\u6211\u6015\u8fde\u4f60\u4e5f\u5931\u6389\u4e86\uff01\u201d\u674e\u9038\u7b11\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u5931\u6389\uff1f\u9664\u975e\u7a81\u53a5\u53ef\u6c57\u628a\u6211\u6740\u4e86\u3002 \u6211\u6562\u6597\u4ed6\uff0c\u5c31\u4e0d\u6015\u4ed6\uff01\u4e0d\u8fc7\uff0c\u51e1\u4e8b\u591a\u4e9b\u987e\u8651\u4e5f\u597d\u3002\u4e07\u4e00\u6211\u6709\u4ec0\u4e48\u4e0d\u5e78\uff0c\u58c1\u59b9\uff0c\u4f60\u5343\u4e07\u4e0d \u53ef\u81ea\u5bfb\u77ed\u89c1\uff0c\u4e00\u5b9a\u8981\u66ff\u6211\u62a5\u4ec7\uff01\u201d\u957f\u5b59\u58c1\u773c\u7736\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u8bf4\u8fd9\u4e9b\u4e0d\u5409\u5229\u7684\u8bdd\uff0c \u6211\u4e0d\u662f\u6307\u8fd9\u4e2a\uff0c\u6211\u662f\u6015\u4f60\u8d70\u51fa\u5bb6\u95e8\u4e4b\u540e\uff0c\u4e5f\u8bb8\u5c31\u5fd8\u8bb0\u6211\u4e86\uff0c\u4e5f\u8bb8\u5c31\u4ece\u6b64\u4e0d\u56de\u6765\u4e86\uff01\u201d\u674e \u9038\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u8bf4\u7cca\u6d82\u8bdd\u4e86\uff0c\u6211\u600e\u4e48\u4f1a\u5fd8\u8bb0\u4f60\u5462\uff1f\u6211\u6551\u51fa\u4e86\u654f\u513f\uff0c\u53c8\u600e\u4f1a\u4e0d\u56de\u6765\u5462\uff1f\u58c1 \u59b9\uff0c\u4f60\u5b89\u5fc3\u9759\u517b\u5427\uff0c\u522b\u8981\u80e1\u601d\u4e71\u60f3\u4e86\uff01\u201d\u8bf4\u7f62\uff0c\u8f7b\u8f7b\u7684\u629a\u62cd\u5979\u7684\u9999\u80a9\uff0c\u597d\u50cf\u5927\u4eba\u54c4\u5b69\u5b50 \u4e00\u6837\uff0c\u54c4\u5f97\u957f\u5b59\u58c1\u5b89\u9759\u4e0b\u6765\uff0c\u674e\u9038\u4fbf\u51fa\u95e8\u53bb\u4e86\u3002 \u3000\u3000\u957f\u5b59\u58c1\u8868\u9762\u5df2\u5b89\u9759\u4e0b\u6765\uff0c\u5fc3\u5934\u5374\u662f\u6ce2\u6d9b\u8d77\u4f0f\uff0c\u6b8a\u4e0d\u5b81\u9759\u3002\u5979\u6015\u674e\u9038\u53bb\u89c1\u53ef\u6c57\uff0c\u4f1a\u906d \u9047\u51f6\u9669\uff0c\u66f4\u6015\u4ed6\u5728\u5c71\u4e2d\u4fbf\u9047\u89c1\u4e86\u6b66\u7384\u971c\uff0c\u5979\u6015\u6b66\u7384\u971c\u4f1a\u593a\u8d70\u5979\u7684\u4e08\u592b\uff0c\u66f4\u9274\u4e8e\u6015\u7a81\u53a5\u53ef \u6c57\uff01 \u3000\u3000\u6b66\u7384\u971c\u5728\u96ea\u5730\u4e0a\u8e3d\u8e3d\u72ec\u884c\uff0c\u5979\u7684\u5fc3\u4e2d\u4e5f\u662f\u6ce2\u6d9b\u8d77\u4f0f\uff0c\u6b8a\u4e0d\u5b81\u9759\uff0c\u201c\u53bb\u89c1\u4ed6\u5462\uff0c\u8fd8\u662f \u4e0d\u89c1\u4ed6\uff1f\u201d\u867d\u7136\u5979\u521a\u624d\u5df2\u4e0b\u4e86\u201c\u51b3\u5fc3\u201d\uff0c\u53ef\u662f\u6bcf\u5411\u524d\u884c\u8fdb\u4e00\u6b65\uff0c\u957f\u5b59\u58c1\u7684\u5f71\u5b50\u5c31\u8d8a\u53d1\u9c9c \u660e\uff0c\u90a3\u5e7d\u6028\u7684\u76ee\u5149\u4e5f\u597d\u50cf\u8feb\u5230\u4e86\u5979\u7684\u9762\u524d\uff0c\u4ee4\u5979\u5fc3\u60b8\uff01\u7ec8\u4e8e\u6b66\u7384\u971c\u662f\u8d70\u4e24\u6b65\u505c\u4e00\u6b65\u7684\uff0c \u4f46\u4ecd\u662f\u5f80\u524d\u8d70\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u8d70\u4e86\u4e00\u7a0b\uff0c\u5979\u6ca1\u6709\u78b0\u89c1\u674e\u9038\uff0c\u5374\u78b0\u5230\u4e86\u53e6\u5916\u4e00\u4ef6\u5947\u602a\u7684\u4e8b\uff0c\u5979\u8f6c\u8fc7\u4e86\u4e00\u5904\u5c71 \u62d7\uff0c\u5ffd\u7136\u5728\u4e00\u5757\u5ca9\u77f3\u4e0a\u53d1\u73b0\u6709\u4eba\u753b\u7740\u4e00\u4ed8\u9ab7\u9ac5\u5934\u9aa8\uff0c\u4e0b\u9762\u8fd8\u6709\u4e24\u884c\u6c49\u5b57\uff1a\u201c\u6b32\u5168\u6027\u547d\uff0c \u8d76\u901f\u56de\u4e61\uff01\u201d\u6b66\u7384\u971c\u7cbe\u795e\u9661\u632f\uff0c\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u7a81\u53a5\u6b66\u571f\u539f\u6765\u4e5f\u5b66\u4f1a\u4e86\u6c5f\u6e56\u4e0a\u7684\u8fd9\u4e00\u5957\u6050 \u5413\u624b\u6cd5\uff0c\u62ff\u6765\u5413\u6211\uff0c\u8fd9\u5c82\u4e0d\u592a\u53ef\u7b11\u4e86\u5417\uff1f\u201d\u5979\u8ba4\u5b9a\u8fd9\u662f\u61c2\u5f97\u6c49\u6587\u7684\u7a81\u53a5\u6b66\u571f\u6240\u753b\uff0c\u8bf4\u4e0d \u5b9a\u5c31\u662f\u63b3\u8d70\u4e86\u957f\u5b59\u58c1\u5b69\u5b50\u7684\u90a3\u4e2a\u6b66\u571f\uff0c\u518d\u8d70\u4e00\u7a0b\uff0c\u53c8\u53d1\u73b0\u4e86\u540c\u4e00\u7684\u56fe\u753b\u548c\u6587\u5b57\uff0c\u597d\u50cf\u662f \u7528\u5200\u5251\u65b0\u523b\u4e0a\u53bb\u7684\uff0c\u77f3\u5c51\u8fd8\u6492\u6ee1\u96ea\u5730\u3002\u6b66\u7384\u971c\u60f3\u8d77\u4e86\u4e00\u4e2a\u5999\u6cd5\uff0c\u6298\u4e0b\u4e86\u4e24\u652f\u67af\u679d\uff0c\u8fd0\u7528 \u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u529f\u592b\uff0c\u5c06\u67af\u679d\u201c\u5506\u201d\u7684\u5f39\u51fa\u3002 \u3000\u3000\u90a3\u4e24\u652f\u67af\u679d\u98de\u51fa\u4e86\u5341\u6765\u4e08\u8fdc\uff0c\u4e00\u524d\u4e00\u540e\uff0c\u843d\u5728\u96ea\u5730\u4e4b\u4e0a\uff0c\u53d1\u51fa\u4e86\u6781\u8f7b\u5fae\u7684\u201c\u5693\u5693\u201d \u7684\u58f0\u54cd\uff0c\u5c31\u50cf\u4e00\u4e2a\u5177\u6709\u8f7b\u529f\u672c\u4e8b\u7684\u4eba\uff0c\u8db3\u5c16\u70b9\u5728\u96ea\u5730\u4e0a\u6240\u53d1\u6bd4\u7684\u58f0\u97f3\u4e00\u6837\u3002\u4e0e\u6b64\u540c\u65f6\uff0c \u6b66\u7384\u971c\u5374\u7528\u6700\u4e0a\u4e58\u7684\u201c\u8e0f\u96ea\u65e0\u75d5\u201d\u7684\u529f\u592b\uff0c\u5411\u76f8\u53cd\u7684\u65b9\u5411\uff0c\u6ed1\u51fa\u4e86\u6570\u4e08\uff0c\u4e1d\u6beb\u6ca1\u6709\u58f0\u606f\u3002 \u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u5728\u90a3\u67af\u679d\u5c04\u53bb\u7684\u65b9\u5411\uff0c\u4e00\u5757\u5ca9\u77f3\u540e\u9762\uff0c\u6709\u4e24\u4e2a\u6b66\u58eb\u63a2\u5934\u63a2\u8111\u7684\u51fa\u6765\u5f20\u671b\u3002 \u3000\u3000\u654c\u8e2a\u4e00\u73b0\uff0c\u6b66\u4ea5\u971c\u8eab\u5f62\u7acb\u8d77\uff0c\u6377\u5982\u98de\u9e1f\uff0c\u970e\u773c\u95f4\u5c31\u5230\u4e86\u90a3\u4e24\u4e2a\u6b66\u58eb\u7684\u8eab\u540e\uff0c\u5a07\u53f1\u4e00 \u58f0\uff1a\u201c\u7ad9\u4f4f\uff01\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u7528\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u5411\u90a3\u6b66\u58eb\u7684\u540e\u5fc3\u4fbf\u6293\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u7684\u529f\u592b\u751a\u662f\u4e0d\u5f31\uff0c\u6b66\u7384\u971c\u8fd9\u95ea\u7535\u822c\u7684\u4e00\u6293\uff0c\u7adf\u7136\u843d\u7a7a\u3002\u53ea\u89c1\u90a3\u4e24\u4e2a\u6b66\u58eb \u8eab\u5f62\u4e00\u4fef\uff0c\u500f\u5730\u4e00\u4e2a\u76d8\u65cb\uff0c\u5df2\u662f\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e00\u5bf9\u5224\u5b98\u7b14\u547c\u547c\u631f\u98ce\uff0c\u53cc\u70b9\u6b66\u7384\u971c\u7684\u201c\u671f\u95e8 \u7a74\u201d\uff0c\u53e6\u4e00\u4e2a\u6b66\u58eb\u4e5f\u65e9\u5df2\u62d4\u51fa\u4e86\u4e00\u67c4\u77ed\u5200\uff0c\u5c31\u5728\u6b66\u7384\u971c\u65bd\u5c55\u64d2\u62ff\u624b\u6cd5\u4e4b\u65f6\uff0c\u7a81\u7136\u4fbf\u6b3a\u8eab \u8fdb\u6b65\uff0c\u5200\u950b\u4e00\u5212\uff0c\u524a\u6b66\u7384\u971c\u7684\u624b\u8155\u3002 \u3000\u3000\u6b66\u7384\u971c\u559d\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u5de6\u624b\u4e00\u62db\uff0c\u53f3\u624b\u65e9\u5df2\u62d4\u5251\u8fce\u4e0a\uff0c\u94ee\u94ee\u51e0\u58f0\uff0c\u5c06\u4e00\u67c4\u77ed\u5200 \u4e00\u5bf9\u5224\u5b98\u7b14\u5168\u90fd\u8361\u5f00\uff0c\u5175\u5668\u78b0\u51fb\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u867d\u7136\u7ed9\u5979\u9707\u9000\uff0c\u6b66\u7384\u971c\u7684\u864e\u53e3\u4ea6\u5fae\u5fae\u53d1\u9ebb\u3002 \u8fd9\u4e24\u4e2a\u6b66\u58eb\u4e0d\u4ec5\u80fd\u591f\u786c\u63a5\u6b66\u7384\u971c\u7684\u4e00\u5251\uff0c\u800c\u4e14\u8fd8\u6709\u53cd\u51fb\u4e4b\u529b\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u7b97\u5f97\u662f\u4e00\u6d41\u597d \u624b\u4e86\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8fd9\u4e24\u4e2a\u6b66\u58eb\u4e00\u9000\u5373\u4e0a\uff0c\u5de6\u53f3\u5305\u6284\uff0c\u4f7f\u5224\u5b98\u7b14\u7684\u90a3\u4e2a\u6b66\u58eb\uff0c\u4ee5\u653b\u4e3a \u5b88\uff0c\u53cc\u7b14\u4e00\u6643\uff0c\u731b\u6251\u6b66\u7384\u971c\u7684\u4e2d\u76d8\uff0c\u5de6\u70b9\u201c\u671f\u95e8\u7a74\u201d\uff0c\u53f3\u70b9\u201c\u7cbe\u767d\u7a74\u201d\uff0c\u51fa\u624b\u8fc5\u6377\uff0c\u70b9 \u7a74\u5947\u51c6\uff0c\u6b66\u7384\u971c\u6a2a\u5251\u4e00\u5c01\uff0c\u98d8\u8eab\u95ea\u8fc7\uff0c\u90a3\u4f7f\u77ed\u5200\u7684\u6b66\u58eb\u62a1\u5200\u4fef\u8170\uff0c\u4fbf\u65a9\u6b66\u7384\u971c\u7684\u53cc\u811a\u3002 \u6b66\u7384\u971c\u98de\u8eab\u8dc3\u8d77\uff0c\u9752\u94a2\u5251\u51cc\u7a7a\u4e0b\u51fb\uff0c\u518d\u5ea6\u628a\u8fd9\u4e24\u4e2a\u6b66\u58eb\u6740\u9000\uff0c\u75be\u5fd9\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u662f \u80e1\u4eba\u8fd8\u662f\u6c49\u4eba\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e24\u4e2a\u6b66\u58eb\uff0c\u7a7f\u7684\u867d\u662f\u7a81\u53a5\u6b66\u58eb\u7684\u670d\u9970\uff0c\u5374\u5e76\u4e0d\u662f\u63b3\u8d70\u957f\u5b59\u58c1\u513f\u5b50\u7684\u90a3\u4e24\u4e2a\u6b66 \u58eb\u3002\u6700\u5947\u602a\u7684\u662f\u4ed6\u4eec\u90fd\u7f69\u7740\u9762\u7f69\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u53ea\u662f\u548c\u6b66\u7384\u971c\u54d1\u6597\uff0c\u800c\u4e14\u4ed6\u4eec\u6240\u4f7f\u51fa\u6765\u7684 \u6b66\u529f\uff0c\u4e00\u4e2a\u662f\u9752\u57ce\u6d3e\u7384\u95e8\u6b63\u5b97\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u4e00\u4e2a\u662f\u4e07\u80dc\u95e8\u7684\u201c\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\u201d\uff0c\u90fd\u662f\u4e2d \u539f\u6b66\u6797\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5373\u4f7f\u662f\u8fd9\u4e24\u6d3e\u7684\u529f\u592b\u4f20\u5230\u897f\u57df\uff0c\u4e5f\u51b3\u4e0d\u80fd\u4f7f\u5f97\u5982\u6b64\u7cbe\u5999\u3002\u6b66\u7384\u971c\u66fe \u89c1\u8fc7\u8fd9\u4e24\u6d3e\u7684\u638c\u95e8\uff0c\u62ff\u6765\u4e0e\u8fd9\u4e24\u4e2a\u6b66\u571f\u4e00\u6bd4\uff0c\u4e5f\u5e76\u4e0d\u89c1\u5f97\u6bd4\u8fd9\u4e24\u4e2a\u6b66\u58eb\u9ad8\u660e\uff0c\u4f46\u4ece\u4ed6\u4eec \u7684\u8eab\u5f62\u4f53\u6001\u770b\u6765\uff0c\u53c8\u51b3\u975e\u8fd9\u4e24\u6d3e\u7684\u638c\u95e8\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u88ab\u6b66\u7384\u971c\u4e00\u559d\uff0c\u5728\u9762\u7f69\u4e0a\u9732\u51fa\u6765\u7684\u773c\u775b\u70af\u70af\u53d1\u5149\uff0c\u4f46\u4ecd\u7136\u6ca1\u6709\u7b54\u8bdd\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u514d\u5f97\u81ea\u8bef\uff01\u201d\u90a3\u4e24\u4e2a\u6b66\u58eb\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u77ed\u5200\u98de\u821e\uff0c \u94c1\u7b14\u7a7f\u68ad\uff0c\u4e0d\u9000\u53cd\u8fdb\uff0c\u653b\u5f97\u66f4\u7d27\u4e86\u3002\u4ed6\u4eec\u4ecd\u662f\u95f7\u58f0\u4e0d\u54cd\uff0c\u54d1\u7f20\u54d1\u6597\uff0c\u770b\u4ed6\u4eec\u7684\u795e\u6c14\uff0c\u4f3c \u4e4e\u5e76\u4e0d\u76f8\u4fe1\u6b66\u7384\u971c\u4fbf\u80fd\u6740\u8d25\u4ed6\u4eec\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u9732\u771f\u76f8\uff0c\u53ef\u4f53\u602a\u6211\u5b9d\u5251\u65e0\u60c5\uff01\u201d\u51b7\u7b11\u4e00\u58f0\uff0c\u5251\u62db\u500f\u53d8\uff0c\u5b9d\u5251\u631f \u98ce\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u4ece\u4e24\u4eba\u5934\u9876\u521a\u8fc7\uff0c\u4f7f\u5224\u5b98\u7b14\u7684\u90a3\u4e2a\u6b66\u58eb\u8fd8\u4e86\u4e00\u62db\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u88ab\u6b66\u7384 \u971c\u7684\u5251\u950b\u5212\u8fc7\uff0c\u94ee\u94ee\u58f0\u54cd\uff0c\u6e85\u51fa\u4e86\u70b9\u70b9\u706b\u661f\uff0c\u4f7f\u77ed\u5200\u7684\u90a3\u4e2a\u6b66\u58eb\u89c1\u72b6\u60ca\u5fc3\uff0c\u6025\u5fd9\u62a2\u4e0a\uff0c \u8054\u624b\u9632\u5fa1\uff0c\u594b\u529b\u6321\u5f00\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6b66\u7384\u971c\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u653b\u4e03\u5251\uff0c\u6709\u5982\u957f\u6c5f\u6d6a\u6d8c\uff0c \u524d\u6d6a\u672a\u901d\uff0c\u540e\u6d6a\u53c8\u6765\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u6781\u529b\u89e3\u62c6\uff0c\u4ecd\u662f\u88ab\u5979\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff01 \u3000\u3000\u6fc0\u6218\u4e2d\uff0c\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u6b66\u58eb\u4e00\u62d6\u4e00\u5e26\uff0c\u7b14\u950b\u98a4\u52a8\uff0c\u4e00\u62db\u4e4b\u5185\uff0c\u8fde\u88ad\u6b66\u7384\u971c\u7684\u7075\u53f0\u3001 \u81f3\u9633\u3001\u98ce\u5e9c\u3001\u5468\u8c2c\u3001\u9633\u5931\u3001\u6108\u6c14\u3001\u547d\u95e8\u4e03\u5904\u5927\u7a74\uff0c\u8fd9\u4e03\u5904\u7a74\u9053\u5206\u5e03\u5728\u4e0d\u540c\u7684\u90e8\u4f4d\uff0c\u8ddd\u79bb \u9887\u8fdc\uff0c\u800c\u90a3\u6b66\u58eb\u7528\u5de6\u7b14\u4e00\u62d6\uff0c\u53f3\u7b14\u4e00\u5e26\uff0c\u5c45\u7136\u80fd\u591f\u628a\u6b66\u7384\u971c\u7684\u5b9d\u5251\u6321\u51fa\u5916\u95e8\uff0c\u800c\u4e14\u5c31\u5728 \u8fd9\u77ac\u606f\u4e4b\u95f4\u8fde\u88ad\u4e03\u5904\u4e0d\u540c\u7684\u65b9\u4f4d\uff0c\u7684\u786e\u662f\u4e00\u6d41\u9ad8\u624b\u7684\u70b9\u7a74\u529f\u592b\uff01 \u3000\u3000\u6b66\u7384\u971c\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u7109\u80fd\u7ed9\u4ed6\u70b9\u4e2d\uff0c\u90a3\u6b66\u58eb\u51fa\u624b\u5df2\u7b97\u5feb\u6781\uff0c\u4f46\u5979\u7684\u8eab\u6cd5\u66f4\u662f\u8fc5\u6025\u98d8 \u98ce\uff0c\u4f46\u89c1\u5979\u5f80\u524d\u4e00\u63a2\uff0c\u4e00\u8bb0\u201c\u591c\u53c9\u63a2\u6d77\u201d\uff0c\u89e3\u5f00\u4e86\u654c\u4eba\u7684\u5269\u52b2\uff0c\u5b9d\u5251\u8fc5\u5982\u7535\u9776\uff0c\u626c\u7a7a\u4e00 \u5212\uff0c\u56de\u524a\u4f7f\u77ed\u5200\u90a3\u4e2a\u6b66\u58eb\u7684\u624b\u8155\uff0c\u53c8\u628a\u4ed6\u7684\u653b\u52bf\u89e3\u5f00\u3002\u5979\u8eab\u6cd5\u8f7b\u7075\uff0c\u4fe8\u82e5\u884c\u4e91\u6d41\u6c34\uff0c\u4f7f \u5224\u5b98\u7b14\u7684\u90a3\u4e2a\u6b66\u58eb\u867d\u7136\u4f7f\u51fa\u5e73\u751f\u672c\u9886\uff0c\u7b14\u5c16\u7adf\u7136\u8fde\u5979\u7684\u8863\u88f3\u4e5f\u6ca1\u6709\u6cbe\u7740\uff01 \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u7384\u95e8\u70b9\u7a74\u624b\u6cd5\u7740\u5b9e\u4e0d\u9519\uff0c\u53ef\u662f\u8fd8\u7565\u5acc\u9a73\u800c\u4e0d\u7eaf\uff0c\u5982\u4eca\uff0c\u4f60\u4e5f\u770b \u6211\u7684\u5427\uff01\u6211\u8981\u7528\u5251\u5c16\u523a\u4f60\u80cc\u5fc3\u7684\u7075\u53f0\u7a74\uff0c\u523a\u4ed6\u80f8\u53e3\u7684\u63b0\u73b0\u7a74\uff01\u201d\u523a\u4ec0\u4e48\u7a74\u9053\uff0c\u5148\u8bf4\u51fa\u6765\uff0c \u8fd9\u5df2\u662f\u4e00\u5947\uff0c\u90a3\u4e24\u5904\u7a74\u9053\uff0c\u4e00\u5728\u80cc\u5fc3\uff0c\u4e00\u5728\u524d\u80f8\uff0c\u800c\u4e14\u662f\u540c\u65f6\u5206\u88ad\u4e24\u4eba\uff0c\u6b66\u672f\u4e2d\u5c1a\u672a\u542c \u8fc7\u6709\u8fd9\u7b49\u9a87\u4eba\u542c\u95fb\u7684\u70b9\u7a74\u529f\u592b\uff1f\u4e24\u4e2a\u6b66\u58eb\u8038\u4e86\u8038\u80a9\uff0c\u5404\u81ea\u7528\u624b\u4e2d\u7684\u5175\u5668\u5c01\u7d27\u95e8\u6237\uff0c\u867d\u4e0d \u8bf4\u8bdd\uff0c\u795e\u6001\u8868\u9732\uff0c\u5374\u662f\u7edd\u4e0d\u76f8\u4fe1\uff01 \u3000\u3000\u6b66\u7384\u971c\u7b11\u58f0\u672a\u7edd\uff0c\u957f\u5251\u500f\u7684\u5c55\u5f00\uff0c\u5251\u52bf\u98d8\u5ffd\u65e0\u65b9\uff0c\u4f3c\u662f\u653b\u5411\u4f7f\u5224\u5b98\u7b14\u7684\u90a3\u4e2a\u6b66\u58eb\uff0c \u53c8\u4f3c\u662f\u653b\u5411\u4f7f\u77ed\u5200\u7684\u90a3\u4e2a\u6b66\u58eb\uff0c\u4e24\u4eba\u8fde\u7528\u51e0\u79cd\u8eab\u6cd5\uff0c\u906e\u62e6\u5c01\u95ed\uff0c\u5374\u662f\u5c01\u95ed\u4e0d\u4f4f\uff0c\u4f46\u89c9\u5251 \u6c14\u68ee\u68ee\uff0c\u51b7\u900f\u808c\u80a4\uff0c\u4f7f\u5224\u5b98\u7b14\u7684\u90a3\u4e2a\u6b66\u58eb\uff0c\u4f3c\u89c9\u5251\u5c16\u5c31\u8981\u89e6\u5230\u4ed6\u7684\u80cc\u5fc3\uff0c\u4f7f\u77ed\u5200\u7684\u90a3\u4e2a \u6b66\u58eb\u4e5f\u89c9\u5251\u5c16\u5c31\u8981\u6d4b\u53ca\u4ed6\u7684\u80f8\u53e3\uff0c\u4e24\u4eba\u4f7f\u5c3d\u5e73\u751f\u672c\u9886\uff0c\u600e\u6837\u4e5f\u6446\u8131\u4e0d\u5f00\uff0c\u5413\u5f97\u540c\u58f0\u53eb\u9053\uff1a \u201c\u6b66\u90e1\u4e3b\u624b\u4e0b\u7559\u60c5\uff01\u201d\u54e7\u54e7\u4e00\u7247\u58f0\u54cd\uff0c\u4e24\u4eba\u90fd\u6254\u4e0b\u4e86\u624b\u4e2d\u7684\u5175\u5668\uff01 \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u679c\u662f\u6c49\u4eba\uff0c\u6211\u8fd8\u5f53\u4f60\u4eec\u662f\u54d1\u5427\u9791\u5b50\u5462\uff01\u201d\u5251\u52bf\u4e00\u6536\uff0c\u5374\u7a81 \u7136\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u63ed\u4e0b\u4e86\u4ed6\u4eec\u4e24\u4eba\u7684\u9762\u7f69\uff0c\u8fd9\u4e00\u63ed\u5f00\uff0c\u767b\u65f6\u4ee4\u6b66\u7384\u971c\u4e5f\u5413\u7740\u4e86\uff01 \u3000\u3000\u8fd9\u4e24\u4eba\u62df\u66fe\u76f8\u8bc6\uff0c\u6b66\u7384\u971c\u9661\u7136\u60f3\u8d77\uff0c\u4e43\u662f\u5728\u5802\u5144\u6b66\u627f\u55e3\u5bb6\u4e2d\u89c1\u8fc7\u7684\u95e8\u5ba2\uff0c\u56de\u5fc6\u5f53\u65f6 \u60c5\u72b6\uff0c\u8fd9\u4e24\u4e2a\u4eba\u597d\u50cf\u8fd8\u662f\u5979\u5802\u5144\u7684\u5fc3\u8179\u3002\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u6539\u4e86\u7a81\u53a5\u6b66\u58eb\u7684\u670d\u9970\uff0c\u800c\u4e14\u5c45\u7136 \u6562\u6765\u548c\u6211\u52a8\u624b\uff0c\u83ab\u975e\u662f\u9020\u53cd\u4e86\u4e48\uff1f\u201d\u9976\u662f\u6b66\u7384\u971c\u806a\u660e\u673a\u667a\uff0c\u53ea\u56e0\u8fd9\u4e8b\u60c5\u592a\u4e0d\u5bfb\u5e38\uff0c\u4e00\u65f6 \u95f4\u4e5f\u4ee4\u5979\u731c\u60f3\u4e0d\u900f\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e24\u4e2a\u6b66\u58eb\u73b0\u51fa\u5c34\u5c2c\u7684\u795e\u8272\uff0c\u6254\u6389\u4e86\u6b66\u5668\uff0c\u4fbf\u5728\u96ea\u5730\u4e0a\u8dea\u4e0b\u53bb\uff0c\u5411\u6b66\u7384\u971c\u53e9\u5934 \u7980\u9053\uff1a\u201c\u5c0f\u4eba\u5c01\u7267\u91ce\u795d\u89c1\u7ae0\u53e9\u89c1\u90e1\u4e3b\uff0c\u9002\u624d\u591a\u591a\u5192\u72af\uff0c\u6c42\u90e1\u4e3b\u6055\u7f6a\u3002\u90e1\u4e3b\u5251\u672f\uff0c\u5999\u7edd\u5929 \u4e0b\uff0c\u6211\u7b49\u65e0\u77e5\uff0c\u73ed\u95e8\u5f04\u65a7\uff0c\u5c1a\u671b\u90e1\u4e3b\u4e00\u7b11\u7f6e\u4e4b\u3002\u201d\u8fd9\u4e24\u4e2a\u540d\u5b57\u4e00\u62a5\u51fa\u6765\uff0c\u6b66\u7384\u971c\u5fc3\u9053\uff1a \u201c\u539f\u6765\u4ed6\u4eec\u662f\u548c\u6211\u8bd5\u62db\u6765\u4e86\u3002\u201d\u60f3\u8fd9\u5c01\u7267\u91ce\u4e0e\u795d\u89c1\u7ae0\u4e24\u4eba\u4e43\u662f\u9752\u57ce\u6d3e\u4e0e\u4e07\u80dc\u95e8\u7684\u540d\u5bbf\uff0c \u8bba\u8d77\u6b66\u6797\u4e2d\u7684\u8f88\u4efd\uff0c\u5728\u81ea\u5df1\u4e4b\u4e0a\u3002\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u884c\u5f84\u975e\u5f02\u5e38\u4eba\uff0c\u60f3\u662f\u4ed6\u4eec\u8981\u89c1\u8bc6 \u81ea\u5df2\u7684\u5251\u672f\uff0c\u5374\u53c8\u4e0d\u65b9\u4fbf\u6309\u6b66\u6797\u7684\u89c4\u77e9\uff0c\u6765\u8bf7\u8bd5\u62db\uff0c\u6545\u800c\u8499\u9762\u6539\u88c5\uff0c\u5e03\u6b64\u7591\u9635\uff1f\u4f46\u968f\u5373 \u60f3\u5230\uff0c\u82e5\u662f\u4ed6\u4eec\u6709\u5fc3\u8981\u548c\u81ea\u5df1\u8f83\u91cf\uff0c\u5728\u957f\u5b89\u4e4b\u65f6\uff0c\u5c3d\u591a\u673a\u4f1a\uff0c\u4f55\u987b\u4e07\u91cc\u8ddf\u8e2a\uff0c\u8fdc\u6765\u6f20\u5916\uff1f \u4f55\u51b5\u81ea\u5df1\u8fd9\u6b21\u5949\u4e86\u5929\u540e\u4e4b\u547d\uff0c\u4e8b\u60c5\u6781\u5176\u79d8\u5bc6\uff0c\u4ed6\u4eec\u53c8\u4ece\u4f55\u5f97\u77e5\u81ea\u5df1\u7684\u884c\u8e2a\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u8d77\uff0c\u6211\u867d\u59d3\u6b66\uff0c\u5e76\u672a\u53d7\u5c01\u3002\u54b1\u4eec\u540c\u662f\u6b66\u6797\u4e00\u8109\uff0c\u5c82\u53ef\u884c\u6b64\u5927\u793c\u3002\u201d \u4e24\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u5c01\u7267\u91ce\u8baa\u8baa\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u90e1\u4e3b\u5728\u516b\u5e74\u4e4b\u524d\uff0c\u4e8e\u5ce8\u5d4b\u91d1\u9876\uff0c\u66fe\u5251\u8d25\u7fa4\u96c4\uff0c \u5a01\u9707\u56db\u6d77\uff0c\u9002\u624d\u627f\u8499\u8d50\u62db\uff0c\u679c\u7136\u540d\u4e0b\u65e0\u865a\uff0c\u5974\u624d\u8f93\u5f97\u5fc3\u670d\u53e3\u670d\uff01\u201d\u4ed6\u4eec\u518d\u4e00\u6b21\u89e3\u91ca\u4f55\u4ee5 \u524d\u6765\u8bd5\u62db\u7684\u539f\u56e0\uff0c\u6b66\u7384\u971c\u542c\u4e86\u8d8a\u53d1\u6000\u7591\uff0c\u5f53\u4e0b\u9762\u8272\u4e00\u7aef\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u8bba\u8d77\u6b66\u6797\u8f88\u4efd\uff0c \u8fd8\u5f53\u63a8\u4e24\u4f4d\u4e3a\u5c0a\uff0c\u4ec0\u4e48\u5974\u624d\u90e1\u4e3b\u7684\u79f0\u8c13\uff0c\u8bf7\u5373\u5e9f\u53bb\u3002\u54b1\u4eec\u53ea\u4ee5\u6b66\u6797\u4e4b\u793c\u76f8\u89c1\u3002\u6b66\u6797\u4e4b\u4e2d\uff0c \u5f7c\u6b64\u7422\u78e8\uff0c\u4e8b\u4ea6\u5bfb\u5e38\uff0c\u4f46\u4e24\u4f4d\u6539\u4e86\u7a81\u53a5\u7684\u670d\u9970\uff0c\u4e07\u91cc\u8fdc\u6765\uff0c\u6df1\u5165\u5929\u5c71\uff0c\u96be\u9053\u5c31\u53ea\u662f\u4e3a\u4e86 \u8981\u548c\u6211\u8bd5\u62db\u5417\uff1f\u8fd9\u4e8b\u60c5\u53ef\u5c31\u6709\u70b9\u4e0d\u5bfb\u5e38\u4e86\uff01\u201d\u795d\u89c1\u7ae0\u8bb7\u8bb7\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2014\u2014\u201d\u6b66\u7384 \u971c\u9053\uff1a\u201c\u8fd9\u4e2a\u600e\u6837\uff1f\u73b0\u4e0b\u7a81\u53a5\u6b63\u5728\u5174\u5175\uff0c\u610f\u6b32\u72af\u5883\uff0c\u4e24\u4f4d\u83ab\u975e\u662f\u53db\u6c49\u5f52\u80e1\uff0c\u4ee5\u8bd5\u62db\u4e3a\u540d\uff0c \u5b9e\u662f\u60f3\u6765\u6697\u6740\u6211\u4e48\uff1f\u82e5\u5728\u4e2d\u539f\uff0c\u6211\u81ea\u5f53\u5c0a\u91cd\u4e24\u4f4d\u524d\u8f88\uff0c\u6b64\u65f6\u6b64\u5730\uff0c\u5982\u6b64\u76f8\u9047\uff0c\u8bf7\u4e24\u4f4d\u6055 \u7f6a\uff0c\u6211\u975e\u95ee\u4e2a\u6e05\u695a\u4e0d\u53ef\uff01\u82e5\u6709\u542b\u7cca\uff0c\u4f11\u602a\u65e0\u793c\uff01\u201d\u6b66\u7384\u971c\u7559\u5fc3\u4ed6\u4eec\u7684\u9762\u8272\uff0c\u8fd9\u756a\u8bdd\u4e00\u8bf4 \u51fa\u6765\uff0c\u53ea\u89c1\u795d\u89c1\u7ae0\u500f\u7136\u8272\u53d8\uff0c\u5c01\u7267\u91ce\u4e5f\u5fae\u5fae\u4e00\u6296\uff0c\u4f46\u968f\u5373\u4fbf\u9547\u5b9a\u5982\u5e38\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6b66 \u59d1\u5a18\uff0c\u6709\u751a\u6000\u7591\uff0c\u8bf7\u95ee\u4fbf\u662f\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4eec\u5728\u5ca9\u77f3\u4e0a\u523b\u5b57\u753b\u56fe\uff0c\u8bf7\u95ee\u6b32\u5168\u6027\u547d\uff0c \u8d76\u901f\u56de\u4e61\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u5c01\u7267\u91ce\u9053\uff1a\u201c\u8fd9\u610f\u601d\u660e\u767d\u4e4b\u6781\uff0c\u5c31\u662f\u8981\u8bf7\u6b66\u59d1\u5a18\u901f\u56de\u4e2d\u571f \u5475\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u4eec\u60f3\u6211\u56de\u53bb\uff1f\u201d\u5c01\u7267\u91ce\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u60f3\u4f60\u56de\u53bb\uff0c\u662f\u4f60\u7684 \u7687\u5144\uff0c\u5343\u5c81\u7237\u60f3\u8bf7\u4f60\u56de\u53bb\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u627f\u55e3\u4ed6\u8981\u6211\u56de\u53bb\u4f5c\u751a\uff1f\u201d\u5c01\u7267\u91ce \u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u4eba\u600e\u80fd\u77e5\u9053\uff1f\u597d\u5728\u5343\u5c81\u7237\u6709\u4eb2\u7b14\u4e66\u4fe1\u5728\u6b64\uff0c\u8bf7\u59d1\u5a18\u81ea\u5df1\u770b\u4e2a\u660e\u767d\uff01\u201d \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u6c5f\u6e56\u7a7a\u62b1\u5e7d\u5170\u6028 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u5341\u4e03\u56de\u3000\u6c5f\u6e56\u7a7a\u62b1\u5e7d\u5170\u6028 \u3000\u3000\u6b66\u7384\u971c\u4e00\u770b\uff0c\u679c\u7136\u662f\u5979\u5802\u5144\u6b66\u627f\u55e3\u7684\u5b57\u8ff9\uff0c\u4fe1\u4e0a\u5199\u9053\uff1a\u201c\u60ca\u95fb\u543e\u59b9\u8fdc\u8d74\u6f20\u5317\uff0c\u6b32\u53ec \u56de\u674e\u5510\u9057\u5b7d\uff0c\u4f5c\u65cb\u4e7e\u8f6c\u5764\u4e4b\u8c0b\uff1b\u543e\u59b9\u51b0\u96ea\u806a\u660e\uff0c\u5948\u4f55\u6b32\u81ea\u53ec\u706d\u95e8\u4e4b\u7978\uff1f\u6b64\u4e3a\u611a\u5144\u6240\u5927\u60d1 \u4e0d\u89e3\u8005\u4e5f\uff01\u7687\u5e1d\u6625\u79cb\u5df2\u9ad8\uff0c\u60d1\u4e8e\u72c4\u4ec1\u6770\u4e4b\u90aa\u8bf4\uff0c\u5723\u806a\u5bb9\u6709\u95ed\u585e\uff0c\u5723\u8651\u5bb9\u672a\u5468\u7965\uff0c\u543e\u59b9\u672a \u52a0\u529d\u8c0f\uff0c\u53cd\u4ece\u800c\u52a9\u4e4b\uff0c\u4e07\u4e00\u5f52\u5b97\u674e\u5510\uff0c\u679c\u6210\u4e8b\u5b9e\uff0c\u5219\u4e0d\u4f46\u4eca\u65e5\u4e4b\u7e41\u534e\u5bcc\u8d35\uff0c\u5316\u4f5c\u4e91\u70df\uff0c \u543e\u6b66\u6c0f\u5176\u5c1a\u6709\u77a7\u7c7b\uff1f\u543e\u59b9\u5176\u518d\u601d\u4e09\u601d\uff01\u76fc\u543e\u59b9\u89c1\u6b64\u4fe1\u540e\uff0c\u901f\u8fd4\u957f\u5b89\uff0c\u4ece\u957f\u8ba1\u8bae\u3002\u5144\u627f\u55e3\u3002\u201d \u3000\u3000\u539f\u6765\u81ea\u6b66\u5219\u5929\u79f0\u5e1d\u4e4b\u540e\uff0c\u6539\u5510\u4e3a\u5468\uff0c\u5173\u4e8e\u5e1d\u4f4d\u7ee7\u627f\u7684\u95ee\u9898\uff0c\u4e00\u76f4\u5c31\u5728\u671d\u5ef7\u4e0a\u4e89\u8bba\u5f97 \u5f88\u6fc0\u70c8\u3002\u672c\u6765\u6309\u7167\u201c\u4e00\u5bb6\u5929\u4e0b\u201d\u7684\u89c2\u5ff5\uff0c\u65e2\u7136\u662f\u59d3\u6b66\u7684\u505a\u4e86\u7687\u5e1d\uff0c\u5c31\u8be5\u59d3\u6b66\u7684\u5b50\u5b59\u7ee7\u4f4d\u3002 \u53ef\u662f\u4e00\u73ed\u6709\u529b\u7684\u5927\u81e3\uff0c\u5374\u4e3b\u5f20\u6b66\u5219\u5929\u4f20\u5b50\u4e0d\u4f20\u4f84\u3002\u6b66\u627f\u55e3\u4e00\u5fc3\u60f3\u505a\u7687\u5e1d\uff0c\u53e6\u5916\u4e5f\u52fe\u7ed3\u4e86 \u4e00\u73ed\u5927\u81e3\u62e5\u62a4\u4ed6\u3002\u5728\u6b66\u5219\u5929\u79f0\u4f4d\u7684\u7b2c\u4e8c\u5e74\uff0c\u6b66\u627f\u55e3\u4fbf\u8fd0\u52a8\u4e86\u4e00\u73ed\u4eba\uff0c\u4ee5\u51e4\u9601\u820d\u4eba\u5f20\u5c91\u798f \u4e3a\u9996\uff0c\u51e0\u767e\u4eba\u7b7e\u540d\u4e0a\u8868\uff0c\u8bf7\u6b66\u5219\u5929\u660e\u4ee4\u4ee5\u6b66\u627f\u55e3\u7ee7\u627f\u5e1d\u4f4d\uff0c\u5f53\u65f6\u7684\u5bb0\u76f8\u5c91\u957f\u60c5\u6781\u529b\u53cd\u5bf9\uff0c \u4e8b\u7387\u672a\u6210\u3002\u6b66\u5219\u5929\u4e3a\u4e86\u7f13\u548c\u4e24\u6d3e\u7684\u4e89\u6267\uff0c\u4e00\u65b9\u9762\u4ee5\u81ea\u5df1\u7684\u7b2c\u56db\u4e2a\u513f\u5b50\u674e\u65e6\u6539\u59d3\u6b66\u6c0f\uff0c\u5c01 \u4e3a\u201c\u55e3\u7687\u201d\uff0c\u4e00\u65b9\u9762\u7acb\u5176\u4f84\u513f\u6b66\u627f\u55e3\u4e3a\u9b4f\u738b\uff0c\u6b66\u4e09\u601d\u4e3a\u6881\u738b\uff0c\u5176\u4ed6\u8bf8\u4f84\u7686\u4e3a\u90e1\u738b\uff0c\u59d3\u6b66 \u7684\u52bf\u529b\u5927\u5927\u538b\u5012\u4e86\u59d3\u674e\u7684\u3002\u6b66\u5219\u5929\u672c\u6765\u8981\u5c01\u6b66\u7384\u971c\u505a\u90e1\u4e3b\u7684\uff0c\u6b66\u7384\u971c\u4e0d\u613f\u53d7\u5c01\uff0c\u4f46\u5374\u56e0 \u6b64\u66f4\u5f97\u6b66\u5219\u5929\u7684\u4fe1\u4efb\u3002 \u3000\u3000\u6b66\u5219\u5929\u7684\u7b2c\u56db\u5b50\u674e\u65e6\u867d\u53d7\u5c01\u201c\u55e3\u738b\u201d\uff0c\u987e\u540d\u601d\u4e49\uff0c\u4f3c\u4e4e\u6b66\u5219\u5929\u5df2\u51c6\u5907\u628a\u7687\u4f4d\u4f20\u7ed9\u4ed6\uff0c \u4f46\u674e\u65e6\u6781\u4e3a\u5e73\u5eb8\uff0c\u6b66\u5219\u5929\u59cb\u7ec8\u6ca1\u6709\u660e\u4ee4\u7acb\u4ed6\u4e3a\u592a\u5b50\u3002\u6b66\u627f\u55e3\u4ecd\u7136\u6781\u529b\u56fe\u8c0b\u7ee7\u627f\u5e1d\u4f4d\u3002\u72c4 \u4ec1\u6770\u62c5\u5fc3\u4f1a\u9020\u6210\u5185\u4e71\uff0c\u529d\u6b66\u5219\u5929\u53ec\u56de\u5979\u7684\u7b2c\u4e09\u5b50\u5362\u9675\u738b\u674e\u663e\uff0c\u7acb\u4e3a\u592a\u5b50\u3002\u4ed6\u4e0a\u8868\u9053\uff1a \u201c\u59d1\u4f84\u4e4b\u4e0e\u6bcd\u5b50\u5b70\u4eb2\uff1f\u965b\u4e0b\u7acb\u5b50\uff0c\u5219\u5343\u79cb\u4e07\u5c81\u540e\u914d\u98df\u5927\u5e99\uff0c\u627f\u7ee7\u65e0\u7a77\uff01\u6587\u4f84\uff0c\u5219\u672a\u95fb\u4f84 \u4e3a\u5929\u5b50\uff0c\u800c\u6751\u59d1\u4e8e\u5e99\u8005\u4e5f\u3002\u201d\u4ed6\u660e\u660e\u767d\u767d\u7684\u53cd\u5bf9\u7acb\u6b66\u627f\u55e3\uff0c\u6307\u51fa\u4e86\u5373\u4ee5\u4eb2\u758f\u800c\u8bba\uff0c\u513f\u5b50 \u4e5f\u8981\u6bd4\u4f84\u513f\u4eb2\u5f97\u591a\u3002\u8fd9\u51e0\u53e5\u8bdd\u5f88\u6253\u52a8\u4e86\u6b66\u5219\u5929\u7684\u5fc3\uff0c\u518d\u770b\u4e00\u770b\u5f53\u65f6\u7684\u60c5\u52bf\uff0c\u7acb\u6b66\u6c0f\u4e3a\u5e1d\uff0c \u5185\u4e71\u52bf\u5c06\u4e0d\u514d\uff0c\u518d\u4e00\u60f3\u674e\u663e\u7684\u624d\u80fd\u867d\u7136\u4e5f\u5e76\u4e0d\u9ad8\uff0c\u53ef\u662f\u6b66\u627f\u55e3\u4e5f\u4e0d\u884c\uff0c\u800c\u674e\u663e\u5374\u6709\u4e00\u73ed \u6709\u80fd\u529b\u7684\u5927\u81e3\u62e5\u62a4\u4ed6\uff0c\u6743\u8861\u4e4b\u4e0b\uff0c\u6b66\u5219\u5929\u7ec8\u4e8e\u51b3\u5b9a\u63a5\u7eb3\u72c4\u4ec1\u6770\u7684\u4e3b\u5f20\uff0c\u5c06\u5362\u9675\u738b\u53ec\u56de\uff0c \u51c6\u5907\u5c06\u6765\u7acb\u4ed6\u4e3a\u5929\u5b50\u3002\u6b66\u627f\u55e3\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u628a\u72c4\u4ec1\u6770\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u4f46\u72c4\u4ec1\u6770\u662f\u6b66\u5219 \u5929\u6700\u4fe1\u4efb\u7684\u4eba\uff0c\u6b66\u627f\u55e3\u4e0d\u6562\u52a8\u4ed6\u3002 \u3000\u3000\u6b66\u7384\u971c\u770b\u4e86\u8fd9\u5c01\u4fe1\u540e\uff0c\u5fc3\u5934\u5927\u9707\uff0c\u60f3\u9053\uff1a\u201c\u5c06\u6765\u674e\u6c0f\u4e3a\u5e1d\uff0c\u6b66\u6c0f\u786e\u6709\u706d\u95e8\u4e4b\u7978\uff01\u53ea \u80fd\u770b\u8c01\u505a\u7687\u5e1d\u5bf9\u5929\u4e0b\u8f83\u597d\u4e00\u4e9b\u4e86\uff0c\u4e00\u5bb6\u4e00\u59d3\u7684\u5229\u5bb3\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u6211\u59d1\u59d1\u4e5f\u4e0d\u662f\u66fe\u7ecf \u5c61\u6b21\u8fd9\u6837\u8bf4\u5417\uff1f\u201d\u60f3\u5230\u6b64\u5904\uff0c\u8c41\u7136\u5f00\u6717\uff0c\u628a\u6b66\u627f\u55e3\u7684\u4fe1\u6495\u788e\uff0c\u7eb3\u5165\u53e3\u4e2d\uff0c\u4e00\u53e3\u4fbf\u541e\u4e0b\u53bb \u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u571f\u6115\u7136\u76f8\u987e\uff0c\u731c\u4e0d\u900f\u5979\u5fc3\u610f\u5982\u4f55\u3002\u6b66\u7384\u971c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u56de\u53bb\uff0c\u4f60\u4eec\u662f \u5426\u4fbf\u8981\u53d6\u6211\u7684\u6027\u547d\uff1f\u201d\u5c01\u7267\u91ce\u6025\u5fd9\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff01\u90a3\u4e24\u53e5\u8bdd\u4e0d\u8fc7\u662f\u60f3\u529d\u59d1\u5a18 \u56de\u53bb\u800c\u5df2\u3002\u5343\u5c81\u7237\u4f46\u6c42\u59d1\u5a18\u80fd\u591f\u56de\u53bb\uff0c\u4ed6\u8bf4\uff0c\u6700\u597d\u4e0d\u5fc5\u9732\u9762\uff0c\u4fbf\u80fd\u529d\u963b\u59d1\u5a18\u524d\u884c\u3002\u662f\u5c0f \u4eba\u4eec\u6597\u80c6\uff0c\u7528\u4e86\u6c5f\u6e56\u4e0a\u7684\u865a\u58f0\u606b\u5413\u7684\u624b\u6bb5\u3002\u59d1\u5a18\u4f60\u4e5f\u662f\u60ef\u8d70\u6c5f\u6e56\u7684\u4e86\uff0c\u8fd9\u79cd\u6c5f\u6e56\u4e0a\u7684\u5957 \u8bed\uff0c\u96be\u9053\u8fd8\u4f1a\u653e\u5728\u5fc3\u4e0a\u5417\uff1f\u59d1\u5a18\u82e5\u7136\u89c1\u602a\uff0c\u5c0f\u4eba\u5728\u8fd9\u53a2\u7ed9\u4f60\u8d54\u7f6a\u3002\u201d\u6b66\u7384\u971c\u542c\u4ed6\u8a00\u4e4b\u6210 \u7406\uff0c\u731c\u6d4b\u6b66\u627f\u55e3\u7684\u672c\u610f\uff0c\u5927\u7ea6\u4e5f\u662f\u5e0c\u671b\u975e\u5230\u5fc5\u8981\u4e4b\u65f6\uff0c\u4e0d\u5fc5\u5c06\u8fd9\u5c01\u4fe1\u4ea4\u51fa\u6765\uff0c\u4fbf\u9053\uff1a \u201c\u65e2\u662f\u6211\u54e5\u54e5\u7684\u610f\u601d\uff0c\u4f55\u987b\u4f60\u66ff\u4ed6\u8d54\u7f6a\uff1f\u201d\u773c\u73e0\u4e00\u8f6c\uff0c\u626b\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u795d\u89c1\u7ae0\u9053\uff1a\u201c\u6211 \u4eec\u7a7f\u4e0a\u8fd9\u7a81\u53a5\u6b66\u58eb\u7684\u670d\u9970\uff0c\u59d1\u5a18\u60f3\u5fc5\u89c1\u7591\uff0c\u8fd9\u662f\u4e3a\u4e86\u4fbf\u4e8e\u884c\u8d70\u7684\u539f\u6545\u3002\u201d\u6b66\u7384\u971c\u51b7\u7b11\u9053\uff1a \u201c\u90a3\u662f\u4e3a\u4e86\u4fbf\u4e8e\u8ffd\u8e2a\u7684\u7f18\u6545\u5427\uff1f\u54fc\uff0c\u54fc\uff0c\u4f60\u4eec\u6562\u5192\u7a81\u53a5\u6b66\u58eb\uff0c\u8fd9\u80c6\u5b50\u53ef\u771f\u4e0d\u5c0f\uff01\u82e5\u7136\u78b0 \u4e0a\u4e86\u771f\u7684\u7a81\u53a5\u6b66\u58eb\uff0c\u6216\u8005\u78b0\u4e0a\u4e86\u5929\u5c71\u5251\u5ba2\uff0c\u4f60\u4eec\u53ef\u5c31\u8981\u81ea\u627e\u82e6\u5403\u3002\u4f60\u4eec\u56de\u53bb\u65f6\uff0c\u6362\u4e0a\u4e86 \u8001\u767e\u59d3\u7684\u670d\u88c5\u5427\u3002\u201d\u5c01\u7267\u91ce\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u5904\u5904\u66ff\u6211\u4eec\u7740\u60f3\uff0c\u59d1\u5a18\u91d1\u7389\u826f\u8a00\uff0c\u81ea\u5f53\u9075\u7167\u3002 \u90a3\u4e48\u54b1\u4eec\u662f\u4e0d\u662f\u73b0\u5728\u5c31\u56de\u53bb\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4ec0\u4e48\u54b1\u4eec\uff1f\u4f60\u4eec\u56de\u53bb\u544a\u8bc9\u738b\u7237\uff0c\u5c31\u8bf4\u4ed6\u7684 \u4fe1\u6211\u5df2\u7ecf\u770b\u8fc7\u4e86\uff0c\u4e00\u5207\u542c\u4ece\u5723\u4e0a\uff0c\u8bf7\u4ed6\u4e0d\u8981\u81ea\u4f5c\u4e3b\u5f20\u3002\u201d\u5c01\u7267\u91ce\u4e0e\u795d\u89c1\u7ae0\u9762\u9762\u76f8\u89d1\uff0c\u89c1 \u6b66\u7384\u971c\u6267\u610f\u4e0d\u56de\uff0c\u4ed6\u4eec\u53ea\u597d\u81ea\u5df1\u56de\u53bb\u3002 \u3000\u3000\u6b66\u7384\u971c\u76ee\u9001\u4ed6\u4eec\u7684\u80cc\u5f71\u4e0b\u5c71\uff0c\u957f\u957f\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u59d1\u59d1\u6539\u5510\u4e3a\u5468\uff0c\u505a \u5f00\u5929\u8f9f\u5730\u4ee5\u6765\u7684\u7b2c\u4e00\u4e2a\u5973\u7687\u5e1d\uff0c\u5979\u5c82\u662f\u53ea\u4e3a\u4e00\u5bb6\u4e00\u59d3\u7740\u60f3\uff1f\u627f\u55e3\u4ed6\u4eec\u8fd9\u6837\u80e1\u95f9\uff0c\u4e0d\u4f46\u6b66 \u5bb6\u8981\u8499\u4e0a\u6076\u540d\uff0c\u9ece\u6c11\u4e5f\u8981\u53d7\u4ed6\u707e\u7978\u3002\u4f46\u6c42\u4e0a\u5929\u4fdd\u4f51\uff0c\u8ba9\u6211\u59d1\u59d1\u591a\u6d3b\u51e0\u5e74\uff0c\u6709\u6211\u59d1\u59d1\u5728\u4e16\uff0c \u4ed6\u4e5f\u8bb8\u8fd8\u4e0d\u6562\u4e71\u4f5c\u975e\u4e3a\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u5c1a\u672a\u77e5\u9053\uff0c\u5979\u5802\u5144\u6b66\u627f\u55e3\u4e3a\u4e86\u60f3\u7ee7\u627f\u5e1d\u4f4d\uff0c\u5df2\u7ecf\u548c\u7a81\u53a5\u53ef\u6c57\u6697\u901a\u6d88\u606f\uff0c\u7a81\u53a5 \u53ef\u6c57\u5f97\u77e5\u674e\u9038\u9690\u5c45\u5929\u5c71\uff0c\u4fbf\u662f\u6b66\u627f\u55e3\u6d3e\u4eba\u7ed9\u4ed6\u62a5\u8baf\u7684\u3002\u6b66\u627f\u55e3\u60f3\u7a81\u53a5\u53ef\u6c57\u6740\u6389\u674e\u9038\uff0c\u7a81 \u53a5\u53ef\u6c57\u5374\u53e6\u6709\u6253\u7b97\u3002\u81f3\u4e8e\u90a3\u5c01\u795d\u4e8c\u4eba\uff0c\u4fbf\u662f\u7ed9\u6b66\u627f\u55e3\u9001\u4fe1\u7684\u4eba\uff0c\u4ed6\u4eec\u65e9\u5df2\u89c1\u8fc7\u7a81\u53a5\u53ef\u6c57\uff0c \u4ed6\u4eec\u90a3\u4e00\u8eab\u6b66\u58eb\u670d\u9970\uff0c\u4fbf\u662f\u7a81\u53a5\u53ef\u6c57\u8d50\u7ed9\u4ed6\u4eec\u7684\u3002\u4ed6\u4eec\u9690\u85cf\u9762\u76ee\u672c\u6765\u60f3\u628a\u6b66\u7384\u971c\u64d2\u7740\uff0c \u732e\u7ed9\u7a81\u53a5\u53ef\u6c57\uff0c\u9886\u529f\u6709\u8d4f\uff0c\u4e0d\u6599\u53cd\u800c\u9669\u4e27\u6b66\u7384\u971c\u5251\u4e0b\uff0c\u8fd9\u624d\u8feb\u5f97\u4ed6\u4eec\u732e\u51fa\u6b66\u627f\u55e3\u7684\u4fe1\u4ef6\uff0c \u5c06\u4e8b\u60c5\u90fd\u63a8\u5230\u6b66\u627f\u55e3\u7684\u5934\u4e0a\u3002 \u3000\u3000\u6b66\u7384\u971c\u5c06\u90a3\u4e24\u4e2a\u6b66\u58eb\u6253\u53d1\u4e4b\u540e\uff0c\u7ee7\u7eed\u8ffd\u8e2a\u957f\u5b59\u58c1\u7684\u8db3\u5370\uff0c\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u5230\u4e86\u4e00\u6a31\u5c71\u5cf0\uff0c \u5ffd\u89c1\u4e00\u95f4\u5c4b\u5b50\uff0c\u5ea7\u843d\u5728\u6797\u6728\u4e1b\u4e2d\uff0c\u6b66\u7384\u971c\u5fc3\u5f26\u98a4\u6296\uff0c\u5fc3\u4e71\u5982\u9ebb\uff0c\u60f3\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u7ec8\u4e8e\u9f13 \u8d77\u4e86\u52c7\u6c14\uff0c\u4e0a\u524d\u6572\u95e8\uff0c\u597d\u4e45\uff0c\u542c\u4e0d\u5230\u4eba\u58f0\u56de\u7b54\uff0c\u6b66\u7384\u971c\u5927\u4e3a\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u9664\u4e86\u4ed6\u4eec\uff0c \u8fd8\u6709\u8c01\u4f4f\u5728\u8fd9\u91cc\uff1f\u6216\u8005\u662f\u4ed6\u4eec\u4e0d\u613f\u89c1\u6211\u4e48\uff1f\u201d\u9f13\u8d77\u52c7\u6c14\uff0c\u53eb\u4e86\u4e00\u58f0\u957f\u5b59\u58c1\u3002\u53c8\u53eb\u4e86\u4e00\u58f0 \u674e\u9038\uff0c\u4ecd\u7136\u542c\u4e0d\u89c1\u56de\u7b54\uff0c\u6b66\u7384\u971c\u54ac\u4e86\u54ac\u7259\uff0c\u4e0b\u4e86\u51b3\u5fc3\uff0c\u4e00\u4e0b\u5b50\u4fbf\u628a\u95e8\u63a8\u5f00\u3002 \u3000\u3000\u51b7\u98ce\u6251\u9762\u5439\u6765\uff0c\u5ba4\u4e2d\u6c93\u65e0\u4eba\u5f71\uff0c\u4e0d\u4f46\u6ca1\u6709\u674e\u9038\uff0c\u8fde\u957f\u5b59\u58c1\u4e5f\u4e0d\u89c1\u4e86\u3002\u6b66\u7384\u971c\u5fc3\u5934\u9178 \u75db\uff0c\u60f3\u9053\uff1a\u201c\u4f60\u7adf\u7136\u6ca1\u6709\u4e00\u70b9\u6545\u4eba\u60c5\u4efd\uff0c\u6211\u4e07\u91cc\u8fdc\u6765\uff0c\u4f60\u4e5f\u907f\u800c\u4e0d\u89c1\u4e48\uff1f\u201d\u968f\u5373\u60f3\u5230\uff1b \u201c\u83ab\u975e\u662f\u957f\u5b59\u58c1\u4e0d\u8bb8\u4ed6\u89c1\u6211\uff1f\u957f\u5b59\u58c1\u5440\uff0c\u4f60\u7684\u5fc3\u80f8\u4e5f\u672a\u514d\u592a\u72ed\u7a84\u4e86\uff01\u4f60\u628a\u6211\u6b66\u7384\u971c\u770b\u4f5c \u4f55\u7b49\u4eba\uff1f\u6211\u5c82\u662f\u4e0e\u4f60\u4e89\u6c49\u5b50\u7684\u4eba\uff01\u201d \u3000\u3000\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u5899\u4e0a\u7559\u6709\u51e0\u884c\u5b57\u8ff9\u3002\u90a3\u662f\u4e24\u9996\u7edd\u8bd7\uff0c\u7b2c\u4e00\u9996\u5199\u5f97\u662f\uff1a\u201c\u5341\u5e74\u68a6\u9192\u76f8\u601d \u6cea\uff0c\u4e07\u91cc\u897f\u98ce\u701a\u6d77\u6c99\u3002\u540c\u547d\u9e33\u9e2f\u60b2\u547d\u8584\uff0c\u5929\u6daf\u4f55\u5904\u662f\u6211\u5bb6\uff01\u201d\u7b2c\u4e8c\u9996\u5199\u7684\u662f\uff1a\u201c\u613f\u5c06\u70ed \u8840\u6d12\u80e1\u5c18\uff0c\u65e6\u628a\u6e83\u5d69\u6258\u65e7\u4eba\u3002\u5e94\u5ff5\u674e\u90ce\u5bb6\u56fd\u6068\uff0c\u7559\u4ed6\u540c\u8d4f\u96ea\u5c71\u6625\u3002\u201d \u3000\u3000\u58a8\u8ff9\u72b9\u65b0\uff0c\u8fd9\u662f\u957f\u5b59\u58c1\u521a\u521a\u7559\u4e0b\u7684\u7b14\u8ff9\u3002\u6b66\u7384\u971c\u75f4\u7acb\u58c1\u524d\uff0c\u4e0d\u89c9\u5446\u4e86\u3002\u7ec6\u5473\u8bd7\u4e2d\u4e4b \u610f\uff0c\u7b2c\u4e00\u9996\u662f\u957f\u5b59\u58c1\u7684\u81ea\u60b2\u8eab\u4e16\uff0c\u5979\u628a\u4e0e\u674e\u9038\u7684\u5341\u8f7d\u59fb\u7f18\uff0c\u5f53\u4f5c\u4e00\u573a\u5e7b\u68a6\uff0c\u5982\u4eca\u5e7b\u68a6\u9192 \u6765\uff0c\u552f\u6709\u76f8\u601d\u4e4b\u6cea\u3002\u56e0\u6b64\u5979\u5b81\u613f\u5f03\u5bb6\u51fa\u8d70\uff0c\u5728\u897f\u98ce\u4e07\u91cc\uff0c\u9ec4\u6c99\u6f20\u6f20\u4e4b\u4e2d\u98d8\u6cca\u3002\u8bd7\u53e5\u5e76\u4e0d \u5f88\u5de5\uff0c\u4f46\u5374\u51c4\u607b\u52a8\u4eba\u3002\u6b66\u7384\u971c\u5fc3\u9053\uff1a\u201c\u8fd9\u56fa\u7136\u662f\u957f\u5b59\u58c1\u7684\u81ea\u767d\uff0c\u4f46\u4f55\u5c1d\u4e0d\u4e5f\u662f\u4e3a\u6211\u5199\u7167\uff1f \u6211\u6a2a\u7a7f\u701a\u6d77\uff0c\u72ec\u4e0a\u5929\u5c71\uff0c\u4e0d\u4e5f\u662f\u53ea\u8d62\u5f97\u5341\u5e74\u68a6\u9192\u3002\u518d\u60f3\u7b2c\u4e8c\u9996\u8bd7\uff0c\u90a3\u8bd7\u610f\u5c31\u66f4\u8f9b\u9178\u66f2\u6298 \u4e86\u3002\u4f3c\u4e4e\u662f\u957f\u5b59\u58c1\u7279\u522b\u7559\u7ed9\u5979\u770b\u7684\uff0c\u8bd7\u4e2d\u8bf4\u5979\u201c\u613f\u5c06\u70ed\u8840\u6d12\u6717\u5750\u201d\uff0c\u5927\u7ea6\u662f\u8868\u793a\u5979\u4e3a\u6551 \u7231\u5b50\uff0c\u4e0d\u60dc\u4e00\u6b7b\u3002\u7b2c\u4e8c\u53e5\u201c\u4e14\u628a\u9057\u8a00\u6258\u65e7\u4eba\u201d\uff0c\u90a3\u5c31\u5206\u660e\u662f\u5bf9\u6b66\u7384\u971c\u8bf4\u7684\u4e86\uff0c\u6b66\u7384\u971c\u4e0e \u674e\u9038\u76f8\u8bc6\u5728\u524d\uff0c\u5979\u628a\u6b66\u7384\u971c\u79f0\u4f5c\u674e\u9038\u7684\u201c\u65e7\u4eba\u201d\uff0c\u5b9e\u6709\u53cc\u5173\u4e4b\u4e49\uff0c\u8bd7\u610f\u662f\u8bf4\uff1a\u201c\u597d\u5427\uff0c \u6211\u73b0\u5728\u8d70\u4e86\uff0c\u6211\u62fc\u7740\u8840\u6d12\u80e1\u5c18\uff0c\u8fd9\u4e2a\u5bb6\u6211\u662f\u4e0d\u4f1a\u56de\u6765\u4e86\u3002\u6211\u5c06\u4ed6\u8ba9\u7ed9\u4f60\uff0c\u4f60\u662f\u4ed6\u7684\uff1b\u65e7 \u65f6\u76f8\u8bc6\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\u4ed6\u6709\u5bb6\u56fd\u4e4b\u6068\uff0c\u8bf7\u4f60\u4e0d\u8981\u8feb\u4ed6\u56de\u957f\u5b89\u53bb\uff0c\u90a3\u6837\u505a\u662f\u4f1a\u4ee4\u4ed6\u5fc3\u788e\u7684\uff0c \u4f60\u7231\u4ed6\uff0c\u4f60\u5c31\u7559\u4e0b\u6765\u4f34\u4ed6\u540c\u8d4f\u96ea\u5c71\u7684\u6625\u5929\u5427\u3002\u867d\u7136\u96ea\u5c71\u4e4b\u6625\u90a3\u662f\u8fdc\u8fdc\u6bd4\u4e0d\u4e0a\u4e2d\u539f\u7684\u9633\u6625 \u7f8e\u666f\uff0c\u4f46\u4f60\u5e94\u8be5\u4f53\u5ff5\u5230\u4ed6\u7684\u5fc3\u60c5\u5475\uff01\u201d \u3000\u3000\u8fd9\u9996\u8bd7\u4e0d\u4f46\u900f\u51fa\u4e00\u80a1\u201c\u9178\u201d\u5473\uff0c\u4e5f\u900f\u9732\u51fa\u957f\u5b59\u58c1\u5bf9\u674e\u9038\u7684\u4e00\u7247\u6df1\u60c5\uff0c\u53ef\u4ee5\u60f3\u50cf\uff0c\u5979 \u5728\u5199\u8fd9\u9996\u8bd7\u65f6\uff0c\u5fc3\u4e2d\u60c5\u7eea\u4e00\u5b9a\u590d\u6742\u5f97\u5f88\u3002\u6b66\u7384\u971c\u8bfb\u4e86\u8fd9\u4e24\u9996\u8bd7\uff0c\u4e5f\u4e0d\u89c9\u5fc3\u4f24\u6cea\u4e0b\uff0c\u987f\u65f6 \u95f4\u601d\u6f6e\u8d77\u4f0f\uff0c\u4e00\u7247\u832b\u832b\u3002\u60f3\u4e0d\u5230\u957f\u5b59\u58c1\u5bf9\u5979\u662f\u8fd9\u6837\u8bef\u89e3\uff0c\u5bf9\u6b66\u5219\u5929\u7684\u6539\u5510\u4e3a\u5468\uff0c\u6240\u542b\u7684 \u654c\u610f\u53c8\u662f\u5982\u6b64\u4e4b\u6df1\uff01\u800c\u6700\u4ee4\u5979\u611f\u52a8\u7684\u5219\u662f\u957f\u5b59\u58c1\u5bf9\u674e\u9038\u90a3\u79cd\u6267\u7740\u4e4b\u6781\u7684\u7231\u60c5\u3002\u6b66\u7384\u971c\u5446 \u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u84e6\u5730\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u4f55\u82e6\u59a8\u788d\u4ed6\u4eec\u592b\u5987\u4e4b\u60c5\uff1f\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u5373\u7b97\u662f\u56fd\u5bb6\u5927 \u4e8b\uff0c\u4e5f\u6743\u4e14\u629b\u5728\u540e\u5934\uff0c\u5c31\u8ba9\u4ed6\u4eec\u4e24\u4eba\u5728\u8fd9\u5929\u5c71\u7ec8\u8001\u5427\u3002\u6211\u8fd9\u4e00\u751f\u518d\u4e5f\u4e0d\u8981\u89c1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u6005\u6005\u60af\u60af\uff0c\u5fc3\u4e71\u5982\u9ebb\uff0c\u60f3\u8981\u79bb\u5f00\uff0c\u53cc\u811a\u7adf\u7136\u4e0d\u542c\u4f7f\u5524\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u5ffd\u89c1\u5ba4\u4e2d \u8fd8\u7559\u6709\u674e\u9038\u7684\u90a3\u5177\u53e4\u7434\u3002\u6b66\u7384\u971c\u7a81\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\uff0c\u75f4\u75f4\u7684\u5750\u4e0b\u53bb\uff0c\u4e00\u6ef4\u6cea\u73e0\uff0c \u6d12\u5728\u7434\u5f26\u4e4b\u4e0a\u3002 \u3000\u3000\u6b66\u7384\u971c\u7779\u7269\u601d\u4eba\uff0c\u60f3\u8d77\u4ee5\u524d\u7684\u7434\u6b4c\u4e92\u7b54\uff0c\u66f4\u4e3a\u6005\u60af\uff0c\u60c5\u4e0d\u81ea\u7981\u7684\u624b\u629a\u7434\u5f26\uff0c\u5f39\u8d77\u4e86 \u66fe\u4e3a\u674e\u9038\u594f\u8fc7\u7684\u90a3\u9602\u695a\u8f9e\uff1a\u201c\u541b\u4e0d\u884c\u516e\u5937\u72b9\u3002\u8d5b\u8c01\u7559\u516e\u4e2d\u6d32\uff1f\u7f8e\u8981\u76fc\u516e\u5b9c\u4fee\uff0c\u6c9b\u543e\u4e58\u516e \u6842\u821f\u3002\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ee5\u524d\u6211\u501f\u8fd9\u7434\u9ad3\u6b4c\u58f0\uff0c\u95ee\u4ed6\u6709\u4ec0\u4e48\u5fc3\u4e8b\u72b9\u8c6b\u4e0d\u524d\uff1f\u800c\u4eca\u5374\u95ee\u6211\u81ea \u5df1\u4e86\u3002\u201d \u3000\u3000\u4e00\u66f2\u594f\u7f62\uff0c\u4f59\u9ad3\u8885\u8885\uff0c\u6b66\u7384\u971c\u6b63\u5f85\u63a8\u7434\u800c\u8d77\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u6709\u4e00\u79cd\u6781\u5fae\u7ec6\u7684\u58f0\u97f3\u4f20\u6765\uff0c \u597d\u50cf\u662f\u8e0f\u5728\u96ea\u5730\u4e0a\u6240\u53d1\u51fa\u7684\u201c\u54b3\uff0c\u54b3\u201d\u58f0\u54cd\uff0c\u6b66\u7384\u971c\u5fc3\u5934\u4e00\u9707\uff1a\u201c\u96be\u9053\u662f\u4ed6\u4eec\u53c8\u56de\u6765\u4e86\uff1f\u201d \u501a\u7a97\u9065\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u5c71\u62d7\u5904\u8f6c\u51fa\u4e00\u4eba\u3002\u6b66\u7384\u971c\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u662f\u6bd2\u89c2\u97f3\uff0c\u5728\u5979\u7684\u540e\u9762\u8fd8 \u6709\u4e00\u4e2a\u9752\u8863\u7537\u5b50\uff0c\u521a\u597d\u88ab\u5ca9\u77f3\u6321\u7740\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u770b\u4e0d\u6e05\u695a\u4ed6\u7684\u9762\u5bb9\u3002 \u3000\u3000\u6b66\u7384\u971c\u5728\u8fd9\u91cc\u89c1\u5230\u6bd2\u89c2\u97f3\uff0c\u867d\u7136\u6709\u70b9\u51fa\u4e4e\u610f\u5916\uff0c\u5374\u4e5f\u672a\u66fe\u5c06\u5979\u653e\u5728\u5fc3\u4e0a\uff0c\u4ee4\u5979\u5403\u60ca \u7684\u662f\u540e\u9762\u90a3\u4e2a\u9752\u8863\u7537\u5b50\uff0c\u82e5\u7136\u662f\u6bd2\u89c2\u97f3\u7684\u5e08\u7236\u5929\u6076\u9053\u4eba\u7684\u8bdd\uff0c\u8fd9\u53ef\u4e0d\u6613\u5bf9\u4ed8\u3002\u597d\u5728\u8f6c\u773c \u4e4b\u95f4\uff0c\u90a3\u9752\u8863\u7537\u5b50\u5c31\u8f6c\u51fa\u5c71\u62d7\uff0c\u6b66\u7384\u971c\u770b\u6e05\u695a\u4e86\u4e0d\u662f\u5929\u6076\u9053\u4eba\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u6211 \u4e14\u9759\u4ee5\u5f85\u52a8\uff0c\u770b\u4ed6\u4eec\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u4e8e\u662f\u53c8\u751f\u56de\u7a97\u524d\u5f39\u7434\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u90a3\u4e24\u4eba\u7684\u811a\u6b65\u58f0\u5df2\u5230\u4e86\u95e8\u524d\u3002\u53ea\u542c\u5f97\u6bd2\u89c2\u97f3\u683c\u683c\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60\u597d \u95f2\u60c5\u9038\u81f4\u5475\uff0c\u8001\u670b\u53cb\u6765\u63a2\u671b\u4f60\u5566\u3002\u6b66\u7384\u971c\u4e0d\u7406\u776c\u5979\uff0c\u4ecd\u7136\u7ee7\u7eed\u5f39\u7434\uff0c\u5979\u6b63\u5f39\u81f3\u695a\u8f9e\u4e2d\u7684 \u201c\u65e5\u4e0e\u6708\u5176\u4e0d\u5f85\u516e\uff0c\u6050\u7f8e\u4eba\u4e4b\u8fdf\u66ae\u201d\u7434\u58f0\u51c4\u82e6\uff0c\u5c06\u6bd2\u89c2\u97f3\u7684\u7b11\u58f0\u538b\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6bd2\u89c2\u97f3\u5f85\u5f97\u7434\u58f0\u65ad\u7eed\u4e4b\u9645\uff0c\u53c8\u518d\u626c\u58f0\u7b11\u9053\uff1a\u201c\u8001\u670b\u53cb\u8fd9\u822c\u6162\u5ba2\u5c82\u975e\u592a\u8fc7\u4e0d\u8fd1\u4eba\u60c5\uff1f \u6211\u8fd8\u672b\u89c1\u8fc7\u4f60\u7684\u65b0\u592b\u4eba\u5462\uff0c\u4e3a\u4f55\u4e0d\u8bf7\u6211\u8fdb\u53bb\uff1f\u201d\u9752\u8863\u7537\u4eba\u8bf4\u9053\uff1a\u201c\u53eb\u4ed6\u4e0d\u8981\u518d\u5f39\u4e86\uff0c\u542c \u5f97\u4eba\u6781\u4e0d\u8212\u670d\u3002\u201d\u6bd2\u89c2\u97f3\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u5f39\u7434\u8fce\u5ba2\uff0c\u4e5f\u8be5\u5f39\u4e9b\u597d\u542c\u7684\u8c03\u513f\u3002\u5582\uff0c\u4f60\u5f00\u4e0d \u5f00\u95e8\uff1f\u4f60\u4e0d\u5f00\u95e8\uff0c\u6211\u53ef\u8981\u81ea\u5df1\u95ef\u8fdb\u6765\u4e86\u201d\u3002 \u3000\u3000\u4e24\u6247\u6728\u95e8\u88ab\u6bd2\u89c2\u97f3\u201c\u5440\u201d\u7684\u4e00\u58f0\u63a8\u5f00\uff0c\u6bd2\u89c2\u97f3\u5bf9\u90a3\u7537\u5b50\u9053\uff1a\u201c\u4f60\u4e0d\u8fdb\u6765\u62dc\u89c1\u4e3b\u4eba\u4e48\uff1f\u201d \u90a3\u7537\u5b50\u9053\uff1a\u201c\u4f60\u5c06\u4ed6\u4eec\u63ea\u51fa\u6765\u5c31\u884c\u4e86\uff0c\u6211\u4e0d\u5c51\u4e0e\u5c0f\u8f88\u52a8\u624b\u3002\u201d \u3000\u3000\u6bd2\u89c2\u97f3\u8e0f\u8fdb\u95e8\u6765\uff0c\u671b\u4e86\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u674e\u592b\u4eba\u5728\u5f39\u7434\u3002\u201d\u6b66\u7384\u971c\u62ab\u7740\u6597\u84ec\uff0c \u4f4e\u5934\u5f39\u7434\uff0c\u6bd2\u89c2\u97f3\u4e0e\u5979\u9694\u522b\u591a\u5e74\uff0c\u4e00\u65f6\u95f4\u8ba4\u4e0d\u51fa\u6765\uff0c\u628a\u5979\u5f53\u6210\u4e86\u957f\u5b59\u58c1\u3002\u6bd2\u89c2\u97f3\u53c8\u7b11\u9053\uff1a \u201c\u4ee5\u524d\u7684\u957f\u5b59\u59d1\u5a18\uff0c\u73b0\u5728\u7684\u674e\u592b\u4eba\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\uff0c\u5728\u7ef7\u5c71\u4f60\u4eec\u6740\u4e86\u6211\u7684\u5e08\u5144\uff0c\u8fd9\u4ef6\u4e8b\u60c5 \u4f60\u603b\u8be5\u8fd8\u8bb0\u5f97\u5427\uff1f\u4f60\u522b\u5bb3\u6015\uff0c\u6211\u4e0d\u662f\u5411\u4f60\u8ba8\u547d\u7684\uff0c\u6211\u53ea\u662f\u6765\u8bf7\u4f60\u5230\u4e00\u4e2a\u597d\u5730\u65b9\u53bb\u3002\u4f60\u4e56 \u4e56\u7684\u968f\u6211\u8d70\u5427\uff01\u201d\u6b66\u7384\u971c\u4ecd\u5728\u5f39\u7434\uff0c\u6bd2\u89c2\u97f3\u9634\u607b\u607b\u7b11\u9053\uff1a\u201c\u674e\u592b\u4eba\u4e0d\u80af\u52a8\u8eab\uff1f\u90a3\u4e48\u6211\u53ea \u597d\u4eb2\u81ea\u6765\u8bf7\u4f60\u7684\u5927\u9a7e\u4e86\uff01\u201d\u7f13\u7f13\u7684\u8d70\u5230\u4e86\u6b66\u7384\u971c\u8ddf\u524d\uff0c\u624b\u5c31\u5411\u6b66\u7384\u971c\u4e00\u62c9\u3002 \u3000\u3000\u5979\u4e00\u8fb9\u4f38\u624b\uff0c\u4e00\u8fb9\u7b11\u9053\uff1a\u201c\u597d\u59b9\u5b50\uff0c\u6211\u8fd9\u624b\u4e0a\u53ef\u662f\u6709\u6bd2\u7684\u5475\uff0c\u4f60\u613f\u610f\u8981\u6211\u6400\u6276\u4f60\u4e48\uff1f\u201d \u5979\u624b\u638c\u6709\u5982\u7f8a\u8102\u767d\u7389\uff0c\u8bf4\u8bdd\u6e29\u67d4\u52a8\u542c\uff0c\u786e\u662f\u540d\u5b9e\u76f8\u7b26\uff0c\u4e0d\u6127\u201c\u6bd2\u89c2\u97f3\u201d\u7684\u96c5\u53f7\u3002\u6bd2\u89c2\u97f3 \u7b11\u58f0\u672a\u7edd\uff0c\u5ffd\u7136\u53d1\u89c9\u4e86\u5bf9\u65b9\u662f\u6b66\u7384\u971c\uff01\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u54bd\u5589\u597d\u4f3c\u7a81\u7136\u7ed9\u4eba\u5361\u7740\u4e00\u6837\uff0c \u7b11\u4e0d\u51fa\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e24\u65b9\u540c\u51fa\u624b\uff0c\u4f46\u542c\u5f97\u201c\u556a\u5566\u201d\u4e00\u58f0\uff0c\u6b66\u7384\u971c\u957f\u8896\u4e00\u62c2\uff0c\u6bd2 \u89c2\u97f3\u8fde\u6253\u4e09\u4e2a\u7b4b\u6597\uff0c\u7ffb\u51fa\u5c4b\u5916\uff0c\u8eab\u5f62\u672b\u8d77\uff0c\u7acb\u5373\u4fbf\u5c04\u51fa\u4e00\u62bd\u201c\u900f\u7a74\u795e\u9488\u201d\uff0c\u6b66\u7384\u971c\u62d4\u5251 \u4e00\u6325\uff0c\u94f6\u8679\u4e00\u7ed5\uff0c\u5316\u6210\u4e86\u4e00\u9053\u5149\u5708\uff0c\u5c06\u90a3\u4e00\u84ec\u94f6\u9488\uff0c\u90fd\u7ede\u6210\u4e86\u7c89\u5c51\u3002 \u3000\u3000\u6b66\u7384\u971c\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u96ea\u767d\u7684\u8863\u8896\u4e0a\u5df2\u5370\u4e0a\u4e86\u4e00\u4e2a\u9ed1\u8272\u7684\u638c\u5370\uff0c\u6bd2\u89c2\u97f3\u7684\u624b\u638c\u6709\u5982 \u7f8a\u8102\u767d\u7389\uff0c\u800c\u624b\u638c\u6309\u5904\uff0c\u5c45\u7136\u6cbe\u8863\u5982\u58a8\uff0c\u53ef\u4ee5\u60f3\u89c1\u5979\u624b\u5fc3\u6bd2\u6c14\u7684\u5389\u5bb3\u3002\u6b66\u7384\u971c\u4e5f\u4e0d\u7981\u9a87 \u7136\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u9b54\u5934\u7684\u529f\u529b\u53c8\u9ad8\u8bb8\u591a\u4e86\uff0c\u5e78\u4e8f\u6211\u6ca1\u6709\u8f7b\u654c\uff01\u201d\u5f53\u4e0b\u4e00\u8dc3\u800c\u51fa\uff0c\u6309\u5251\u65a5 \u9053\uff1a\u201c\u6bd2\u89c2\u97f3\u4f60\u5230\u6b64\u4f55\u4e3a\uff1f\u8fd8\u4e0d\u4e0e\u6211\u5b9e\u8bf4\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u90a3\u9752\u8863\u7537\u5b50\u957f\u8896\u4e00\u5377\uff0c\u79bb\u8eab\u4e09\u5c3a\uff0c\u518d\u628a\u6bd2\u89c2\u97f3\u5377\u4e86\u8d77\u6765\uff0c\u4f38\u638c\u5728\u5979 \u80cc\u4e0a\u4e00\u5806\uff0c\u8f7b\u8f7b\u628a\u5979\u9001\u8fc7\u4e00\u8fb9\uff0c\u6bd2\u89c2\u97f3\u7684\u9762\u8272\u672c\u6765\u60e8\u767d\u5982\u6b7b\uff0c\u8f6c\u773c\u95f4\u4fbf\u7ea2\u6da6\u8d77\u6765\u4e86\uff0c\u5598 \u6c14\u7b11\u9053\uff1a\u201c\u6b66\u7384\u971c\u5440\uff0c\u4eca\u65e5\u53ef\u7531\u4e0d\u5f97\u4f60\u901e\u5f3a\u4e86\u3002\u4f60\u5230\u6b64\u4f55\u4e3a\uff1f\u8fd8\u4e0d\u4e0e\u6211\u5b9e\u8bf4\uff01\u201d\u5979\u6562\u8fd9 \u6837\u8bf4\u8bdd\uff0c\u5206\u660e\u662f\u6043\u6709\u9760\u5c71\u3002\u6b66\u7384\u971c\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u5979\u521a\u624d\u4f7f\u51fa\u94c1\u8896\u795e\u529f\uff0c\u6599\u60f3\u90a3\u6bd2\u89c2 \u97f3\u7981\u53d7\u4e0d\u8d77\uff0c\u5c82\u77e5\u88ab\u90a3\u9752\u8863\u7537\u5b50\u5728\u4e3e\u624b\u62c2\u8896\u4e4b\u95f4\uff0c\u4fbf\u628a\u6bd2\u89c2\u97f3\u8eab\u4e0a\u6240\u53d7\u7684\u5185\u5bb6\u771f\u529b\u5378\u5f00\uff0c \u8fd9\u4efd\u529f\u592b\uff0c\u6bd4\u8d77\u6b66\u7384\u971c\u6765\u8fd8\u8981\u80dc\u51fa\u4e00\u7b79\u3002 \u3000\u3000\u90a3\u9752\u8863\u7537\u5b50\u770b\u4e86\u6b66\u7384\u971c\u4e00\u773c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4fbf\u662f\u516b\u5e74\u4e4b\u524d\uff0c\u66fe\u5728\u5ce8\u5d4b\u91d1\u9876\u6363\u6bc1\u82f1 \u96c4\u5927\u4f1a\u7684\u90a3\u4e2a\u5973\u5b50\u4e48\uff1f\u529f\u592b\u679c\u7136\u4e0d\u9519\uff0c\u662f\u4e2a\u53ef\u9020\u4e4b\u6750\uff0c\u4f60\u4e0d\u5982\u62dc\u6211\u4e3a\u5e08\u4e86\u5427\u3002\u201d\u6b66\u7384\u971c \u5927\u6012\uff0c\u4e00\u5251\u523a\u53bb\uff0c\u90a3\u9752\u8863\u7537\u5b50\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u8c01\u6559\u4f60\u7684\u5251\u6cd5\uff1f\u201d\u6b66\u7384\u971c\u51fa\u5251 \u5982\u98ce\uff0c\u8fd9\u77ac\u606f\u95f4\u5251\u5c16\u7802\u5df2\u6307\u5230\u4e86\u4ed6\u7684\u9f3b\u6881\uff0c\u90a3\u9752\u8863\u7537\u5b50\u8170\u5411\u540e\u5f2f\uff0c\u9661\u7136\u95f4\u4f38\u51fa\u5de6\u8db3\u5411\u524d \u4e00\u626b\uff0c\u811a\u5c16\u8e22\u5230\u6b66\u7384\u971c\u6301\u5251\u7684\u864e\u53e3\uff0c\u8170\u5411\u540e\u5f2f\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u5411\u524d\u62c9\u8e22\u51fa\u3002\u529f\u529b\u4e4b\u6df1\u3002\u62db \u6570\u4e4b\u5de7\uff0c\u6b66\u7384\u971c\u89c1\u6240\u672a\u89c1\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u8e22\u4e2d\uff0c\u5e78\u800c\u6b66\u7384\u971c\u8f7b\u529f\u4e86\u5f97\u3002\u53d8\u62db\u673a\u7075\uff0c\u4e00\u4e2a\u201c\u76d8 \u9f99\u7ed5\u6b65\u201d\uff0c\u6709\u5982\u98de\u71d5\u63a0\u6ce2\uff0c\u9752\u971c\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5251\u5c16\u4e0a\u5410\u51fa\u78d0\u83b9\u83b9\u7684\u5bd2\u5149\u3002\u53c8\u523a\u5411\u4ed6\u80cc\u540e \u7684\u98ce\u5e9c\u7a74\u3002 \u3000\u3000\u90a3\u9752\u8863\u7537\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5251\u6cd5\u867d\u597d\uff0c\u5374\u662f\u96be\u5948\u6211\u4f55\u3002\u201d\u672a\u53ca\u8f6c\u8eab\uff0c\u53cd\u624b\u4fbf\u70b9\uff0c \u4ed6\u80cc\u540e\u4f3c\u957f\u7740\u773c\u775b\u4f3c\u7684\uff0c\u624b\u8155\u5fae\u62fe\uff0c\u521a\u521a\u900f\u8fc7\u5251\u950b\uff0c\u4fbf\u70b9\u5230\u4e86\u6b66\u7384\u971c\u53f3\u81c2\u7684\u201c\u66f2\u6c60\u7a74\u201d\uff0c \u624b\u6307\u5f39\u5904\uff0c\u52b2\u98ce\u98d2\u7136\uff0c\u8ba4\u7a74\u5947\u51c6\uff0c\u51cc\u5389\u975e\u51e1\uff0c\u6b66\u7384\u971c\u8fd9\u5f97\u4f7f\u51fa\u79fb\u5f62\u6362\u4f4d\u7684\u529f\u592b\u907f\u5f00\u4e86\u4ed6 \u8fd9\u4e00\u62db\uff0c\u8ddf\u7740\u8fd8\u4e86\u4e00\u5251\uff0c\u523a\u4ed6\u8170\u95f4\u7684\u201c\u9633\u5173\u7a74\u201d\u3002\u4e24\u4eba\u6b64\u6765\u5f7c\u5f80\uff0c\u8f6c\u77ac\u4e4b\u95f4\u4fbf\u4ea4\u6362\u4e86\u5341 \u4f59\u62db\u3002\u6bcf\u4e00\u62db\u90fd\u662f\u6781\u7cbe\u5999\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u628a\u6bd2\u89c2\u97f3\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\u3002 \u3000\u3000\u6bd2\u89c2\u97f3\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u795e\u541b\uff0c\u4f60\u6b3a\u8d1f\u6211\uff0c\u6211\u4e0d\u8ddf\u4f60\u4e86\u3002\u6211\u56de\u53bb\u5411\u5e08\u7236\u8bf4\u53bb\u3002\u201d\u90a3\u9752 \u8863\u7537\u5b50\u7b11\u9053\uff1a\u201c\u6211\u600e\u6837\u6b3a\u8d1f\u4f60\u4e86\uff1f\u201d\u6bd2\u89c2\u97f3\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u66ff\u6211\u51fa\u6c14\uff0c\u8fd8\u8bf4\u8981\u6536\u5979\u505a\u5f92\u5f1f \u5462\uff0c\u597d\u5427\uff0c\u4f60\u8981\u5979\u53bb\uff0c\u6211\u4e0d\u8ddf\u4f60\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u9752\u8863\u7537\u5b50\u540d\u53eb\u706d\u5ea6\u795e\u541b\uff0c\u505a\u4e8b\u4f46\u51ed\u81ea\u5df1\u597d\u6076\uff0c\u4ed6\u4e5f\u7cbe\u4e8e\u533b\u672f\uff0c\u8fd9\u5341\u591a\u5e74\u6765\u5728 \u57df\u5916\u5bfb\u91c7\u51e0\u79cd\u4e2d\u571f\u7f55\u89c1\u7684\u836f\u8349\uff0c\u6240\u4ee5\u90a3\u6b21\u5ce8\u5d4b\u91d1\u9876\u7684\u82f1\u96c4\u5927\u4f1a\u4ed6\u6ca1\u6709\u6765\u3002\u5728\u6b66\u6797\u9690\u9038\u4e4b \u4e2d\uff0c\u4ed6\u4e0e\u201c\u91d1\u9488\u56fd\u624b\u201d\u590f\u4faf\u575a\u662f\u4e24\u4e2a\u7cbe\u901a\u533b\u672f\u7684\u5947\u4eba\uff0c\u540d\u6c14\u4e5f\u4e0d\u76f8\u4e0a\u4e0b\u3002\u4e0d\u8fc7\u590f\u4faf\u575a\u7684 \u533b\u672f\u662f\u7528\u4e8e\u6551\u4eba\uff0c\u800c\u4ed6\u5219\u6709\u65f6\u6551\u4eba\uff0c\u6709\u65f6\u5374\u4e3a\u4e86\u8bd5\u9a8c\u836f\u6027\uff0c\u7528\u6bd2\u836f\u5bb3\u4eba\u3002\u56e0\u6b64\u4ed6\u81ea\u79f0 \u201c\u706d\u5ea6\u795e\u541b\u201d\u3002\u6bd2\u89c2\u97f3\u7684\u5e08\u7236\u5929\u6076\u9053\u4eba\u56e0\u4e3a\u90a3\u6b21\u8bd5\u9a8c\u6bd2\u638c\uff0c\u88ab\u590f\u4faf\u575a\u5999\u8ba1\u7834\u89e3\uff0c\u56de\u53bb\u518d \u95ed\u95e8\u82e6\u7ec3\uff0c\u51c6\u5907\u7528\u5341\u5e74\u7684\u529f\u592b\uff0c\u7ec3\u6210\u5929\u4e0b\u65e0\u654c\u7684\u6bd2\u638c\uff0c\u706d\u5ea6\u795e\u541b\u4ece\u897f\u57df\u91c7\u836f\u56de\u6765\uff0c\u5230\u6606 \u4f26\u661f\u5bbf\u6d77\u53bb\u62dc\u8bbf\u4ed6\u3002\u6b63\u503c\u5929\u6076\u9053\u4eba\u95ed\u95e8\u7ec3\u529f\uff0c\u672a\u6709\u89c1\u4ed6\u3002\u706d\u5ea6\u795e\u541b\u4e43\u662f\u6765\u627e\u5929\u6076\u9053\u4eba\u7814 \u7a76\u4e00\u79cd\u6bd2\u836f\u6027\u80fd\u7684\uff0c\u89c1\u4e0d\u5230\u4ed6\uff0c\u751a\u4e3a\u5931\u671b\u3002\u5374\u5e78\u89c1\u5230\u4e86\u6bd2\u89c2\u97f3\uff0c\u706d\u5ea6\u795e\u541b\u60f3\u5b66\u5929\u6076\u9053\u4eba \u7684\u4f7f\u6bd2\u672c\u9886\uff0c\u6bd2\u89c2\u97f3\u4e5f\u60f3\u5f97\u4e00\u4e2a\u6b66\u529f\u5f3a\u7684\u4eba\u4f5c\u4e3a\u501a\u9760\uff0c\u4f55\u51b5\u5979\u7684\u5e08\u5144\u6076\u884c\u8005\u5df2\u6b7b\uff0c\u5979\u53c8 \u6b63\u5728\u5bc2\u83ab\u4e4b\u4e2d\uff0c\u4e8e\u662f\u4e24\u4eba\u4fbf\u52fe\u642d\u4e0a\u4e86\uff0c\u8fd9\u6b21\u662f\u56e0\u4e3a\u6bd2\u89c2\u97f3\u63a2\u542c\u5f97\u674e\u9038\u4e0e\u957f\u5b59\u58c1\u7ed3\u5a5a\u4e4b\u540e\uff0c \u540c\u9690\u5929\u5c71\uff0c\u6bd2\u89c2\u97f3\u4e00\u6765\u662f\u8981\u627e\u957f\u5b59\u58c1\u62a5\u90a3\u6740\u5e08\u5144\u4e4b\u4ec7\uff0c\u4e8c\u6765\u662f\u8981\u7f1a\u67b6\u674e\u9038\uff0c\u5979\u4e0d\u6015\u957f\u5b59 \u58c1\uff0c\u4fbf\u5374\u6709\u70b9\u5bb3\u6015\u674e\u9038\u7684\u5251\u672f\uff0c\u4e8e\u662f\u4fbf\u9080\u8bf7\u706d\u5ea6\u795e\u541b\u4e0e\u5979\u4e00\u540c\u4e0a\u5c71\u3002\u5374\u4e0d\u6599\u674e\u9038\u592b\u5987\u4e0d \u5728\uff0c\u610f\u5916\u7684\u78b0\u89c1\u4e86\u6b66\u7384\u971c\uff0c\u706d\u5ea6\u795e\u541b\u7684\u6b66\u529f\u5206\u660e\u5728\u6b66\u7384\u971c\u4e4b\u4e0a\uff0c\u5374\u8fdf\u8fdf\u4e0d\u80af\u65bd\u5c55\u6740\u624b\uff0c \u662f\u4ee5\u6bd2\u89c2\u97f3\u51fa\u8a00\u6fc0\u4ed6\u3002\u8981\u77e5\u706d\u5ea6\u795e\u541b\u4e43\u662f\u5929\u6076\u9053\u4eba\u7684\u597d\u53cb\uff0c\u4ea6\u5373\u662f\u6bd2\u89c2\u97f3\u7684\u957f\u8f88\uff0c\u4ed6\u4e0e \u6bd2\u89c2\u97f3\u6697\u4e2d\u52fe\u642d\uff0c\u7ed9\u5929\u6076\u9053\u4eba\u77e5\u9053\uff0c\u4ed6\u81ea\u5df1\u4e5f\u89c9\u5f97\u6709\u70b9\u96be\u4e3a\u60c5\u3002 \u3000\u3000\u8fd9\u65f6\u4e5f\u542c\u5f97\u6bd2\u89c2\u97f3\u5982\u6b64\u8bf4\u6cd5\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u6545\u4f5c\u82e5\u65e0\u5176\u4e8b\u7684\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u5a18\u5b50\uff0c\u4f60 \u918b\u5473\u771f\u5927\uff0c\u6211\u600e\u80fd\u4e0d\u8981\u4f60\u5462\uff1f\u6211\u4e0d\u8fc7\u662f\u7231\u60dc\u5979\u7684\u6b66\u529f\u7f62\u4e86\u3002\u201d\u5176\u5b9e\u4ed6\u89c1\u6b66\u7384\u971c\u5bb9\u8c8c\u6b66\u529f\uff0c \u5747\u80dc\u6bd2\u89c2\u97f3\u5341\u500d\u767e\u500d\u3002\u4e0d\u4f46\u6536\u5979\u505a\u5f1f\u5b50\uff0c\u8fd8\u786e\u6709\u5c06\u6b66\u7384\u971c\u6765\u66ff\u4ee3\u6bd2\u89c2\u97f3\u7684\u610f\u601d\u3002 \u3000\u3000\u6b66\u7384\u971c\u542c\u4ed6\u4eec\u4e00\u95ee\u4e00\u7b54\uff0c\u67f3\u7709\u5012\u575a\uff0c\u52c3\u7136\u5927\u6012\uff0c\u9a82\u9053\uff1a\u201c\u65e0\u803b\u5974\u4eba\uff0c\u5403\u6211\u4e00\u5251\uff01\u201d \u6b66\u7384\u971c\u5251\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u4e00\u53e3\u6c14\u75be\u653b\u4e86\u5341\u4e94\u516d\u62db\uff0c\u706d\u5ea6\u795e\u541b\u7a7a\u624b\u5bf9\u654c\uff0c\u8fd8\u771f\u6709\u70b9 \u96be\u4e8e\u5e94\u4ed8\u3002\u5c31\u5728\u4ed6\u4e0e\u6bd2\u89c2\u97f3\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u7a0d\u7a0d\u5206\u5fc3\uff0c\u7b11\u58f0\u672a\u505c\uff0c\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u8863\u895f\u7adf\u88ab \u6b66\u7384\u971c\u4e00\u5251\u7a7f\u8fc7\u3002 \u3000\u3000\u706d\u5ea6\u795e\u541b\u7a81\u7136\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u5973\u5b50\u662f\u4ed6\u7684\u5f92\u5f1f\uff0c\u602a\u4e0d\u5f97\u5979\u4e0d\u80af\u7406\u6211\uff0c \u6211\u53ef\u5f97\u7ed9\u70b9\u5389\u5bb3\uff0c\u8ba9\u5979\u77a7\u77a7\uff01\u201d\u5f85\u5f97\u6b66\u7384\u971c\u653b\u52bf\u7a0d\u7f13\uff0c\u4ed6\u7a81\u7136\u4e00\u58f0\u957f\u7b11\uff0c\u5728\u80cc\u4e0a\u53d6\u4e0b\u4e86 \u4e00\u67c4\u7cbe\u5149\u95ea\u95ea\u7684\u5175\u5668\uff0c\u9053\uff1a\u201c\u4f60\u8d76\u5feb\u62dc\u6211\u4e3a\u5e08\uff0c\u6211\u8fd8\u53ef\u4ee5\u9976\u4f60\u4e00\u547d\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u4e00\u58f0\u4e0d\u54cd\uff0c\u62db\u6570\u4e00\u53d8\uff0c\u55d6\u7684\u4fbf\u662f\u4e00\u62db\u201c\u6d41\u661f\u8d76\u6708\u201d\u523a\u5c06\u8fc7\u53bb\uff0c\u8fd9\u4e00\u5251\u6233\u80f8\u65a9 \u808b\uff0c\u5389\u5bb3\u975e\u5e38\uff0c\u5374\u88ab\u706d\u5ea6\u795e\u541b\u7684\u5175\u5668\u4e00\u7ffb\u4e00\u6293\uff0c\u6b66\u7384\u971c\u7a81\u89c9\u624b\u8155\u4e00\u7d27\uff0c\u9752\u971c\u5251\u7adf\u7ed9\u4ed6\u7684 \u602a\u5175\u5668\u6293\u7740\uff0c\u593a\u4e0d\u56de\u6765\uff0c\u5e78\u800c\u6b66\u7384\u971c\u5e94\u53d8\u673a\u53d8\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c\u7acb\u5373\u987a\u52bf\u5411\u524d\u4e00\u9001\uff0c\u501f\u529b\u6d88 \u52bf\uff0c\u8fd9\u624d\u628a\u5251\u63a3\u56de\u3002 \u3000\u3000\u706d\u5ea6\u795e\u541b\u7684\u5175\u5668\u4e43\u662f\u4e00\u67c4\u957f\u53ef\u4e09\u5c3a\u7684\u77ed\u9504\uff0c\u540d\u4e3a\u201c\u5288\u4e91\u9504\u201d\uff0c\u662f\u4ed6\u5e73\u65e5\u7528\u4f5c\u91c7\u836f\u7684\uff0c \u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u91d1\u5c5e\u6240\u5236\uff0c\u53d1\u51fa\u523a\u76ee\u7684\u5149\u8292\u3002\u6b66\u7384\u971c\u7684\u9752\u971c\u5251\u867d\u7136\u6bd4\u4e0d\u4e0a\u674e\u9038\u7684\u5927\u5185\u5b9d\u5251\uff0c \u4e5f\u662f\u80fd\u591f\u524a\u91d1\u65a9\u94c1\u7684\u5229\u5668\uff01\u4f46\u4e00\u78b0\u4e0a\u706d\u5ea6\u795e\u541b\u7684\u77ed\u9504\uff0c\u4fbf\u53d1\u51fa\u4e00\u9635\u523a\u8033\u7684\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c \u5bf9\u65b9\u7684\u836f\u9504\u6beb\u65e0\u6240\u635f\u3002\u6b66\u7384\u971c\u7684\u5b9d\u5251\uff0c\u5251\u950b\u53cd\u800c\u5377\u4e86\u3002\u4e0d\u4f46\u5982\u6b64\uff0c\u706d\u5ea6\u795e\u541b\u836f\u9504\u4e0a\u7684\u4e94 \u652f\u5c16\u722a\uff0c\u8fd8\u53ef\u4ee5\u52fe\u62ff\u5175\u5203\uff0c\u53c8\u53ef\u4ee5\u5f53\u4f5c\u70b9\u7a74\u6485\u963b\uff0c\u523a\u5bf9\u65b9\u7684\u7a74\u9053\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u5728\u5175\u5668\u4e0a \u706d\u5ea6\u795e\u541b\u4e5f\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u8f6c\u77ac\u95f4\u53cc\u65b9\u53c8\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u706d\u5ea6\u795e\u541b\u6325\u9504\u4e71\u5288\uff0c\u62db\u6570\u53e4\u602a\u4e4b\u6781\uff0c\u65f6\u800c\u6495\u6293\uff0c\u65f6\u800c \u523a\u7a74\uff0c\u65f6\u800c\u5288\u77fe\uff0c\u7adf\u7136\u597d\u4f3c\u51e0\u79cd\u4e0d\u540c\u6027\u80fd\u7684\u5175\u5668\u540c\u65f6\u5411\u6b66\u7384\u971c\u8fdb\u88ad\u4e00\u822c\uff0c\u6b66\u7384\u971c\u4ed7\u7740\u7edd \u9876\u8f7b\u529f\uff0c\u4e0a\u4e58\u5251\u6cd5\uff0c\u4e5f\u662f\u65e0\u6cd5\u53cd\u653b\u3002\u706d\u5ea6\u795e\u541b\u7684\u653b\u52bf\u6108\u6765\u6108\u7d27\uff0c\u8d8a\u653b\u8d8a\u6025\uff0c\u6b66\u7384\u971c\u7ed9\u4ed6 \u903c\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u6709\u5982\u4e00\u53f6\u8f7b\u821f\uff0c\u5728\u6ce2\u6d9b\u6c79\u6d8c\u3001\u5de8\u6d41\u6025\u6e4d\u4e4b\u4e2d\uff0c\u9707\u5f97\u98d8\u6447\u4e0d\u5b9a\u3002\u6bd2\u89c2\u97f3 \u683c\u683c\u7b11\u9053\uff1a\u201c\u795e\u541b\uff0c\u5979\u90a3\u67c4\u5b9d\u5251\uff0c\u4f60\u7ed9\u4e86\u6211\u5427\u3002\u201d\u5979\u770b\u51c6\u4e86\u6b66\u7384\u971c\u5fc5\u8d25\u65e0\u7591\uff0c\u7adf\u628a\u5979\u90a3 \u67c4\u5b9d\u5251\uff0c\u5f53\u6210\u4e86\u56ca\u4e2d\u4e4b\u7269\u3002 \u3000\u3000\u6b66\u7384\u971c\u94f6\u7259\u4e00\u54ac\uff0c\u81ea\u77e5\u8fd9\u6837\u56f0\u6597\u4e0b\u53bb\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u5fc5\u65e0\u5e78\u7406\uff0c\u53ea\u597d\u62fc\u6b7b\u53cd\u51fb\uff0c\u5251\u62db \u518d\u53d8\uff0c\u628a\u5e73\u751f\u6240\u5b66\u6700\u7cbe\u5999\u7684\u5251\u62db\u65bd\u5c55\u51fa\u6765\uff0c\u98d2\u98d2\u8fde\u58f0\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c\u4fbf\u4f3c\u95ea\u8d77\u5343\u767e\u9053\u7cbe\u8292 \u51b7\u7535\uff0c\u4e0e\u706d\u5ea6\u795e\u541b\u5288\u4e91\u9504\u53d1\u51fa\u7684\u5149\u534e\uff0c\u4e92\u76f8\u7ea0\u7f20\uff0c\u4e92\u76f8\u51b2\u523a\uff0c\u706d\u5ea6\u795e\u541b\u7684\u653b\u52bf\u7a0d\u7a0d\u53d7\u963b\uff0c \u4f46\u5374\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u53ea\u662f\u4f60\u8fd9\u6837\u4e00\u6765\uff0c\u771f\u529b\u6d88\u8017\u592a\u751a\uff0c\u8d25\u5f97\u66f4\u5feb\uff0c\u800c\u4e14\u53ef\u80fd\u8981\u5927 \u75c5\u4e00\u573a\uff0c\u4e0d\u5982\u8d81\u65e9\u670d\u8f93\uff0c\u62dc\u6211\u4e3a\u5e08\u7684\u597d\uff01\u201d\u6bd2\u89c2\u97f3\u7b11\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u8981\u8fd9\u4e2a\u5e08\u59b9\uff01\u6211\u53ea\u60f3 \u8981\u5979\u7684\u5b9d\u5251\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u77e5\u9053\u654c\u4eba\u7684\u8bf4\u8bdd\u5e76\u975e\u865a\u58f0\u606b\u5413\uff0c\u4f46\u5979\u5982\u4f55\u80af\u8ba4\u8d25\u4f0f\u8f93\uff1f\u4ecd\u7136\u6325\u5251\u5bf9\u653b\uff0c\u62fc\u6b7b \u6076\u6597\u3002\u6fc0\u6218\u4e2d\u5ffd\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u6bd2\u89c2\u97f3\u53c8\u5411\u5979\u53d1\u5c04\u201c\u900f\u7a74\u795e\u9488\u201d\uff0c\u82e5\u5728\u4e4e\u65f6\uff0c\u6b66\u7384\u971c\u81ea \u7136\u4e0d\u4ff1\uff0c\u6b64\u9645\uff0c\u5979\u65e2\u8981\u9632\u5fa1\u706d\u5ea6\u795e\u541b\uff0c\u53c8\u8981\u8eb2\u907f\u6bd2\u9488\uff0c\u767b\u65f6\u5251\u6cd5\u5927\u4e71\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u6781\u5ea6\u7d27\u5f20\u7684\u65f6\u5019\uff0c\u6811\u6797\u4e2d\u5ffd\u7136\u53d1\u51fa\u4e00\u58f0\u957f\u5578\uff0c\u58f0\u97f3\u597d\u4f3c\u81ea\u7a7a\u800c\u964d\uff0c\u9707\u5f97\u5c71\u9e21 \u8c37\u5e94\uff0c\u67af\u679d\u6447\u843d\u3002\u706d\u5ea6\u795e\u541b\u4e0e\u6b66\u7384\u971c\u90fd\u5f88\u5403\u60ca\uff0c\u60f3\u5f53\u4e16\u9ad8\u4eba\uff0c\u662f\u8c01\u6709\u8fd9\u6837\u7684\u529f\u529b\uff1f\u5fc3\u5ff5 \u672a\u5df2\uff0c\u53ea\u89c1\u6811\u6797\u4e2d\u7a81\u7136\u7a9c\u51fa\u4e24\u53ea\u602a\u517d\uff0c\u76ae\u6bdb\u4e00\u7247\u91d1\u9ec4\uff0c\u539f\u6765\u662f\u4e24\u53ea\u91d1\u53d1\u72d2\u72d2\u3002 \u3000\u3000\u72d2\u72d2\u662f\u733f\u7334\u7684\u4e00\u79cd\uff0c\u9762\u5f62\u6bd4\u733f\u7334\u66f4\u50cf\u4eba\u7c7b\uff0c\u672c\u6765\u662f\u5728\u70ed\u5e26\u4e1b\u6797\u4e2d\u751f\u957f\u7684\uff0c\u8fd9\u65f6\u5374\u7a81 \u7136\u5728\u96ea\u5c71\u4e4b\u4e0a\u51fa\u73b0\uff0c\u706d\u5ea6\u795e\u541b\u548c\u6b66\u7384\u971c\u90fd\u5927\u4e3a\u5947\u602a\u3002 \u3000\u3000\u8fd9\u4e24\u53ea\u72d2\u72d2\u62ab\u7740\u6ee1\u5934\u91d1\u53d1\uff0c\u5341\u5206\u597d\u770b\uff0c\u706d\u5ea6\u795e\u541b\u867d\u5728\u6fc0\u6218\u4e4b\u4e2d\uff0c\u4e5f\u4e0d\u7981\u5206\u4e86\u5fc3\u795e\uff0c \u770b\u5b83\u4e00\u773c\u3002\u5ffd\u542c\u5f97\u90a3\u4e24\u53ea\u72d2\u72d2\u6012\u543c\u4e00\u58f0\uff0c\u53cc\u53cc\u8dc3\u8d77\uff0c\u4f38\u51fa\u5229\u5203\u4f3c\u7684\u957f\u722a\uff0c\u500f\u7684\u5c31\u5411\u4ed6\u4eec \u5934\u9876\u6293\u4e0b\u3002\u6b66\u7384\u971c\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u821e\u5251\u9632\u8eab\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4f46\u542c\u5f97\u72d2\u72d2\u72c2\u964d\uff0c\u795e\u541b \u9a8f\u53eb\uff0c\u6b66\u7384\u971c\u5b9a\u775b\u4e00\u77a7\uff0c\u53ea\u89c1\u706d\u5ea6\u795e\u541b\u7684\u80a9\u818a\u5df2\u7ed9\u72d2\u72d2\u6495\u53bb\u4e86\u4e00\u7247\u76ae\u8089\uff0c\u800c\u5176\u4e2d\u7684\u4e00\u53ea \u72d2\u72d2\uff0c\u4e5f\u7ed9\u706d\u5ea6\u795e\u541b\u7684\u836f\u9504\u52fe\u88c2\u4e86\u524d\u817f\u3002 \u3000\u3000\u539f\u6765\u706d\u5ea6\u795e\u541b\u81ea\u4f8d\u6b66\u529f\u9ad8\u5f3a\uff0c\u5e76\u4e0d\u628a\u4e24\u53ea\u72d2\u72d2\u653e\u5728\u5fc3\u4e0a\uff0c\u4ed6\u77a7\u7740\u6b66\u7384\u971c\u624b\u5fd9\u811a\u4e71\uff0c \u8d81\u52bf\u8fdb\u62db\uff0c\u4ec5\u4ec5\u6325\u52a8\u957f\u8896\uff0c\u9632\u5fa1\u90a3\u4e24\u53ea\u72d2\u72d2\u6251\u6765\u3002\u706d\u5ea6\u795e\u541b\u8fd0\u8d77\u771f\u529b\uff0c\u957f\u8896\u62c2\u8d77\u4e86\u4e00\u9635 \u52b2\u98ce\uff0c\u529b\u9053\u4e4b\u5f3a\uff0c\u8db3\u53ef\u98de\u7802\u8d70\u77f3\uff0c\u5373\u7b97\u4e00\u822c\u6c5f\u6e56\u4e0a\u7684\u597d\u624b\uff0c\u4e5f\u6321\u4e0d\u4f4f\u4ed6\u8fd9\u4e48\u4e00\u62c2\u3002\u706d\u5ea6 \u795e\u541b\u60f3\u90a3\u4e24\u65e5\u72d2\u72d2\u80fd\u6709\u591a\u5927\u80fd\u4e3a\uff1f\u8fd9\u4e00\u62c2\u4e4b\u4e0b\uff0c\u5b9a\u53ef\u5c06\u5b83\u4eec\u51fb\u6655\u3002\u4e3a\u4e86\u8981\u628a\u8fd9\u4e24\u53ea\u72d2\u72d2 \u751f\u64d2\uff0c\u4ed6\u8fd8\u5bb3\u6015\u4f24\u4e86\u5b83\u4eec\u7684\u6027\u547d\u5462\uff0c\u6240\u4ee5\u53ea\u7528\u4e86\u56db\u4e94\u5206\u5185\u529b\u3002 \u3000\u3000\u5c82\u77e5\u9053\u4e24\u53ea\u72d2\u72d2\u4e43\u662f\u5929\u751f\u5f02\u79cd\uff0c\u53c8\u7ecf\u8fc7\u9ad8\u4eba\u8c03\u6559\uff0c\u706d\u5ea6\u795e\u541b\u7684\u957f\u8896\u4e00\u62c2\uff0c\u7adf\u7136\u6321\u5b83 \u4eec\u4e0d\u4f4f\uff0c\u5f85\u5230\u706d\u5ea6\u795e\u541b\u89c9\u51fa\u4e0d\u5999\uff0c\u72d2\u72d2\u7684\u5229\u722a\u5df2\u6293\u5230\u4e86\u4ed6\u7684\u80a9\u5934\uff0c\u5e78\u800c\u706d\u5ea6\u795e\u541b\u529f\u529b\u6df1 \u6e5b\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c\u7acb\u523b\u8fd0\u7528\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u5c06\u4e24\u53ea\u72d2\u72d2\u5f39\u5f00\uff0c\u540c\u65f6\u53cd\u624b\u4e00\u9504\uff0c \u52fe\u88c2\u4e86\u4e00\u53ea\u72d2\u72d2\u7684\u817f\uff0c\u53ef\u662f\u4ed6\u4e5f\u7ec8\u4e8e\u88ab\u72d2\u72d2\u6293\u4f24\u4e86\u3002 \u3000\u3000\u706d\u5ea6\u795e\u541b\u9a87\u7136\u5931\u8272\uff0c\u4ed6\u662f\u7ec3\u8fc7\u91d1\u949f\u7f69\u7684\u62a4\u8eab\u529f\u592b\u7684\uff0c\u5bfb\u5e38\u5200\u5251\u4e5f\u4f24\u4e0d\u5f97\uff0c\u800c\u8fd9\u72d2\u72d2 \u5c45\u7136\u4e00\u6293\u5c31\u80fd\u5c06\u4ed6\u7684\u80a9\u5934\u6293\u88c2\u3002\u8db3\u89c1\u662f\u53ea\u5929\u751f\u5f02\u517d\uff0c\u529b\u5927\u65e0\u7a77\u3002 \u3000\u3000\u706d\u5ea6\u795e\u541b\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u6211\u4e14\u5148\u6536\u62fe\u4f60\u8fd9\u4e24\u53ea\u755c\u751f\uff01\u201d\u98de\u8eab\u75be\u8d77\uff0c\u5411\u4e00\u53ea\u72d2\u72d2\u6293\u4e0b\uff0c \u8fd9\u72d2\u72d2\u8bc6\u5f97\u5389\u5bb3\uff0c\u7adf\u7136\u597d\u50cf\u9ad8\u624b\u8fc7\u62db\u4e00\u822c\uff0c\u61c2\u5f97\u8d85\u907f\uff0c\u800c\u4e14\u61c2\u5f97\u4e58\u9699\u53cd\u51fb\uff0c\u706d\u5ea6\u795e\u541b\u4e00 \u6293\u6293\u7a7a\uff0c\u4f46\u89c9\u8111\u540e\u98ce\u751f\uff0c\u90a3\u53ea\u72d2\u72d2\u7684\u957f\u722a\u7adf\u7136\u6293\u4e86\u5230\u6765\uff0c\u706d\u5ea6\u795e\u541b\u8fd9\u6b21\u4e0d\u6562\u8f7b\u654c\uff0c\u65e9\u6709 \u9632\u5907\uff0c\u7384\u529f\u9ed8\u8fd0\uff0c\u52a0\u4e0a\u4e09\u5206\u5185\u529b\uff0c\u53c8\u662f\u6325\u8896\u4e00\u5f39\uff0c\u8fd9\u4e00\u4e0b\u90a3\u53ea\u72d2\u72d2\u7981\u53d7\u4e0d\u8d77\uff0c\u8dcc\u51fa\u4e86\u4e00 \u4e08\u5f00\u5916\uff0c\u4f46\u5c45\u7136\u4e00\u8dc3\u53c8\u8d77\uff0c\u8bf4\u65f6\u8fdf\u3001\u90a3\u65f6\u5feb\uff0c\u4e24\u53ea\u72d2\u72d2\u53c8\u4e00\u9f50\u6251\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u6b66\u7384\u971c\u7a81\u7136\u5f97\u5230\u4e24\u53ea\u72d2\u72d2\u52a9\u9635\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u60f3\u9053\uff1a\u201c\u5b83\u4eec\u521a\u624d\u52a9\u6211\u8131\u9669\uff0c\u6211\u5c82\u53ef\u770b \u5b83\u4eec\u53d7\u4f24\uff1f\u201d\u6325\u5251\u4e0a\u524d\uff0c\u5979\u521d\u65f6\u8fd8\u6015\u72d2\u72d2\u4e0d\u8fa8\u654c\u53cb\uff0c\u8fde\u5979\u4e00\u9f50\u653b\u5728\u3002\u540e\u6765\u4e00\u89c1\u72d2\u72d2\u5982\u89e3 \u4eba\u610f\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u5e2e\u5979\u5939\u653b\u706d\u5ea6\u795e\u541b\uff0c\u800c\u4e14\u914d\u5408\u5f97\u975e\u5e38\u4e4b\u5999\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002\u90a3\u4e24\u53ea\u72d2 \u72d2\u8d8b\u95ea\u7075\u6d3b\uff0c\u7eb5\u6709\u7edd\u9876\u8f7b\u529f\u7684\u4eba\u4e5f\u6bd4\u5b83\u4eec\u4e0d\u4e0a\uff0c\u4e0d\u987b\u591a\u65f6\uff0c\u706d\u5ea6\u795e\u541b\u53c8\u88ab\u72d2\u72d2\u6293\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u706d\u5ea6\u795e\u541b\u5927\u6012\uff0c\u836f\u9504\u4e00\u4e3e\uff0c\u201c\u54e8\u201d\u7684\u4e00\u58f0\uff0c\u8361\u5f00\u4e86\u6b66\u7384\u971c\u7684\u957f\u5251\uff0c\u5de6\u638c\u6325\u4e86\u534a\u4e2a\u5706 \u5f27\uff0c\u4e00\u4e2a\u201c\u5708\u638c\u201d\u63a8\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u5693\u556a\u201d\u4e24\u58f0\uff0c\u4e24\u53ea\u72d2\u72d2\u7684\u8111\u76d6\u90fd\u88ab\u4ed6\u6253\u4e86\u4e00\u638c\uff0c\u90a3\u4e24 \u53ea\u72d2\u72d2\u8fc5\u9014\u98d8\u98ce\uff0c\u5c45\u7136\u7ed9\u4ed6\u4ee5\u95ea\u7535\u822c\u7684\u624b\u6cd5\u51fb\u4e2d\uff0c\u6b66\u7384\u971c\u4e5f\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u706d\u5ea6\u795e\u541b\u7684\u638c\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u5bfb\u5e38\u7684\u6b66\u5b66\u4e4b\u58eb\uff0c\u82e5\u7136\u7ed9\u4ed6\u8fd9\u6837\u7684\u51fb\u4e2d\u4e00\u638c\uff0c\u6015\u4e0d\u5f53\u573a \u809d\u8111\u6d82\u5730\uff1f\u5e78\u800c\u90a3\u4e24\u53ea\u72d2\u72d2\u4e43\u662f\u5929\u751f\u5f02\u517d\uff0c\u5468\u8eab\u5200\u67aa\u4e0d\u5165\uff0c\u5929\u7075\u76d6\u90e8\u5206\uff0c\u53c8\u6709\u6d53\u5bc6\u7684\u91d1 \u53d1\u4fdd\u62a4\uff0c\u8fd9\u624d\u5f97\u4ee5\u4e0d\u6b7b\uff0c\u4f46\u5374\u4e5f\u7ed9\u638c\u529b\u971e\u5f97\u8dcc\u51fa\u4e24\u4e09\u4e08\u5916\uff0c\u95f7\u53eb\u4e00\u58f0\uff0c\u6655\u5728\u5730\u4e0b\u3002 \u3000\u3000\u6b66\u7384\u971c\u53ea\u9053\u90a3\u4e24\u53ea\u72d2\u72d2\u5df2\u7ed9\u4ed6\u6253\u6b7b\uff0c\u6697\u53eb\u4e0d\u597d\u3002\u6bd2\u89c2\u97f3\u9053\uff1a\u201c\u8fd9\u4e24\u53ea\u602a\u517d\u4ea4\u7ed9\u6211\u5427\uff0c \u795e\u541b\uff0c\u4f60\u8981\u6b7b\u7684\u8fd8\u662f\u8981\u6d3b\u7684\uff1f\u201d\u706d\u5ea6\u795e\u541b\u9053\uff1a\u201c\u80fd\u6551\u6d3b\u6700\u597d\uff0c\u4f46\u4f60\u53ef\u5f97\u5c0f\u5fc3\uff01\u201d\u6bd2\u89c2\u97f3 \u4e0a\u524d\u4e00\u62e8\uff0c\u90a3\u4e24\u53ea\u72d2\u72d2\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6bd2\u89c2\u97f3\u5947\u9053\uff1a\u201c\u54a6\uff0c\u5929\u7075\u76d6\u5c1a\u672a\u7834\u88c2\uff0c\u600e\u7684\u5c31\u6b7b\u4e86\uff1f\u201d \u6b63\u8981\u62e8\u72d2\u72d2\u5934\u4e0a\u7684\u91d1\u53d1\uff0c\u5c82\u77e5\u90a3\u4e24\u53ea\u72d2\u72d2\u4e43\u662f\u5f89\u6b7b\uff0c\u8fd9\u65f6\u517b\u597d\u4e86\u6c14\u529b\uff0c\u88ab\u6bd2\u89c2\u97f3\u4e00\u62e8\uff0c \u7a81\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u53ea\u542c\u5f97\u201c\u5693\u201d\u4e00\u58f0\uff0c\u72d2\u72d2\u7684\u5229\u722a\uff0c\u6df1\u6df1\u523b\u5165\u4e86\u5979\u7684\u624b\u81c2\uff0c\u7adf\u7136\u6293\u88c2\u4e86\u5979 \u7684\u4e00\u5757\u9aa8\u5934\uff0c\u706d\u5ea6\u795e\u541b\u89c1\u72b6\u5927\u60ca\uff0c\u6025\u5fd9\u4e00\u4e2a\u5288\u7a7a\u638c\u53d1\u51fa\uff0c\u90a3\u4e24\u53ea\u72d2\u72d2\u51cc\u7a7a\u800c\u8d77\uff0c\u4e00\u53ea\u72d2 \u72d2\u5411\u706d\u5ea6\u795e\u541b\u6251\u6765\u3002\u53e6\u4e00\u53ea\u72d2\u4ecd\u7136\u7ee7\u7eed\u8ffd\u6251\u6bd2\u89c2\u97f3\u3002 \u3000\u3000\u6bd2\u89c2\u97f3\u88ab\u5229\u722a\u6293\u88c2\u7b4b\u9aa8\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u767e\u5fd9\u4e2d\u53d1\u51fa\u4e00\u84ec\u6bd2\u9488\uff0c\u90a3\u53ea\u72d2\u72d2\u7adf\u4f3c\u7ecf\u8fc7\u9ad8\u4eba \u8c03\u6559\u4f3c\u7684\uff0c\u8bc6\u5f97\u6bd2\u9488\u5389\u5bb3\uff0c\u957f\u81c2\u4e00\u4f38\uff0c\u6293\u4e0b\u4e86\u4e00\u6761\u67af\u679d\uff0c\u5c45\u7136\u4f7f\u51fa\u5200\u5251\u7684\u62db\u6570\u3002\u67af\u679d\u65cb \u98ce\u4e00\u821e\uff0c\u8eab\u5b50\u4e5f\u8dc3\u5230\u6811\u4e0a\uff0c\u6709\u51e0\u53e3\u6bd2\u9488\u7ed9\u5b83\u62e8\u843d\uff0c\u8fd8\u6709\u7684\u5219\u7ed9\u5b83\u907f\u8fc7\uff0c\u4f46\u542c\u5f97\u5b83\u201c\u54c8\u54c8\u201d \u602a\u53eb\uff0c\u6500\u7740\u6811\u679d\u4e00\u8361\uff0c\u597d\u50cf\u6253\u79cb\u5343\u4e00\u822c\uff0c\u8361\u5230\u4e86\u6bd2\u89c2\u97f3\u7684\u5934\u9876\u4e0a\u7a7a\uff0c\u7a81\u7136\u53c8\u6251\u4e0b\u6765\u3002 \u3000\u3000\u53e6\u4e00\u53ea\u653b\u51fb\u706d\u5ea6\u795e\u541b\u7684\u72d2\u72d2\uff0c\u5403\u8fc7\u4e86\u4e00\u6b21\u4e8f\uff0c\u5b66\u5f97\u4e56\u4e86\uff0c\u5e76\u4e0d\u8fd1\u8eab\uff0c\u53ea\u662f\u548c\u4ed6\u6e38\u6597\uff0c \u706d\u5ea6\u795e\u541b\u51fa\u624b\u867d\u5feb\uff0c\u53ef\u662f\u90a3\u72d2\u72d2\u603b\u662f\u548c\u4ed6\u4fdd\u6301\u4e00\u4e08\u5de6\u53f3\u7684\u8ddd\u79bb\uff0c\u72d2\u72d2\u8d70\u52a8\u7075\u6d3b\uff0c\u800c\u4e14\u53cc \u81c2\u53c8\u957f\uff0c\u4fbf\u4e8e\u653b\u51fb\uff0c\u706d\u5ea6\u795e\u541b\u6253\u4e0d\u4e2d\u5b83\uff0c\u8fd8\u5f97\u9632\u5907\u5b83\u7a81\u7136\u8fdb\u88ad\uff0c\u53ea\u662f\u4e24\u53ea\u72d2\u72d2\u4e5f\u8fd8\u7f62\u4e86\uff0c \u65c1\u8fb9\u53c8\u8fd8\u6709\u4e00\u4e2a\u5251\u6cd5\u975e\u5e38\u7cbe\u5999\u7684\u6b66\u7384\u971c\uff0c\u706d\u5ea6\u795e\u541b\u672c\u5df2\u65e0\u5fc3\u86ee\u6218\uff0c\u8fd9\u65f6\u542c\u5f97\u6bd2\u89c2\u97f3\u5927\u53eb \u6551\u547d\u4e4b\u58f0\uff0c\u66f4\u662f\u7740\u5fd9\uff0c\u6fc0\u6218\u4e2d\u4f46\u89c1\u4ed6\u957f\u8896\u4e00\u6325\uff0c\u836f\u9504\u76d8\u7a7a\u4e00\u821e\uff0c\u500f\u7684\u98de\u8eab\u4fbf\u8d77\uff0c\u6b66\u7384\u971c \u53eb\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff1f\u201d\u5587\u7684\u4e00\u5251\u523a\u51fa\uff0c\u5c82\u77e5\u706d\u5ea6\u795e\u541b\u65e9\u5df2\u6599\u5230\u5979\u6709\u8fd9\u4e00\u62db\u8ffd\u51fb\uff0c\u4f0f\u4e0b\u4e86\u6781 \u5389\u5bb3\u7684\u540e\u7740\uff0c\u4ed6\u8eab\u5728\u534a\u7a7a\uff0c\u5c45\u7136\u786c\u751f\u751f\u7684\u5c06\u8eab\u5f62\u626d\u8f6c\uff0c\u547c\u7684\u4e00\u638c\u62cd\u4e86\u56de\u6765\u3002\u62db\u6570\u53e4\u602a\u7edd \u4f26\uff0c\u6b66\u7384\u971c\u5927\u5403\u4e00\u60ca\uff0c\u6025\u6025\u53d8\u62db\u523a\u51fa\uff0c\u4f46\u542c\u5f97\u201c\u568e\u201d\u7684\u4e00\u58f0\uff0c\u706d\u5ea6\u795e\u541b\u7684\u8db3\u8e1d\u4e2d\u4e86\u4e00\u5251\uff0c \u6b66\u7384\u971c\u7684\u80f8\u4e5f\u7ed9\u4ed6\u7684\u624b\u6307\u62c2\u4e86\u4e00\u4e0b\uff0c\u767b\u65f6\u89c9\u5f97\u4e00\u9635\u9165\u9ebb\uff0c\u51e0\u4e4e\u7a92\u606f\u3002 \u3000\u3000\u6b66\u7384\u971c\u633d\u8d77\u4e00\u6735\u5251\u82b1\uff0c\u62a4\u7740\u8eab\u8eaf\uff0c\u4e0d\u6562\u8ffd\u8d76\uff0c\u4f46\u89c1\u706d\u5ea6\u795e\u541b\u8eab\u5f62\u75be\u8d77\uff0c\u5982\u7bad\u79bb\u5f26\uff0c \u970e\u773c\u95f4\u5c31\u8ffd\u4e0a\u4e86\u6bd2\u89c2\u97f3\uff0c\u90a3\u53ea\u72d2\u72d2\u6b63\u4ece\u6811\u4e0a\u8df3\u4e0b\uff0c\u957f\u81c2\u5229\u722a\u582a\u582a\u5c31\u8981\u6293\u5230\u6bd2\u89c2\u97f3\u7684\u8111\u95e8\uff0c \u706d\u5ea6\u795e\u541b\u547c\u7684\u4e00\u638c\u6253\u53bb\uff0c\u90a3\u72d2\u72d2\u8bc6\u5f97\u5389\u5bb3\uff0c\u53c8\u8df3\u4e0a\u6811\u53bb\u4e86\u3002\u706d\u5ea6\u795e\u541b\u8d76\u8d70\u4e86\u72d2\u72d2\uff0c\u62d6\u7740 \u6bd2\u89c2\u97f3\u4fbf\u9003\uff0c\u4ed6\u811a\u8ddf\u867d\u7136\u4e2d\u4e86\u6b66\u7384\u971c\u4e00\u5251\uff0c\u4ecd\u7136\u884c\u8d70\u5982\u98de\uff0c\u7247\u523b\u95f4\u5c31\u5728\u98ce\u96ea\u4e4b\u4e2d\u6ca1\u4e86\u8e2a \u8ff9\u3002 \u3000\u3000\u6b66\u7384\u971c\u5401\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u597d\u5389\u5bb3\u7684\u638c\u529b\uff0c\u82e5\u7136\u6ca1\u6709\u8fd9\u4e24\u53ea\u72d2\u72d2\uff0c\u53ea\u6015\u6211\u4eca\u65e5\u96be \u514d\u53d7\u8fb1\u3002\u201d\u6b63\u60f3\u8fc7\u53bb\u9017\u90a3\u4e24\u53ea\u72d2\u72d2\uff0c\u5ffd\u542c\u5f97\u6811\u6797\u4e2d\u5578\u58f0\u53c8\u8d77\uff0c\u90a3\u4e24\u53ea\u72d2\u72d2\u597d\u50cf\u542c\u5230\u4e3b\u4eba \u547c\u5524\u4f3c\u7684\uff0c\u90fd\u8dd1\u8fdb\u6811\u6797\u53bb\u4e86\u3002\u6b66\u7384\u971c\u751a\u4e3a\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e24\u53ea\u72d2\u72d2\u662f\u6709\u4eba\u517b\u7684\uff0c \u5b83\u4eec\u7684\u4e3b\u4eba\u5fc5\u662f\u4e16\u5916\u9ad8\u4eba\u3002\u201d\u6291\u5236\u4e0d\u4f4f\u597d\u5947\u4e4b\u5fc3\uff0c\u4fbf\u4e5f\u8d70\u5165\u6797\u4e2d\uff0c\u8ffd\u90a3\u72d2\u72d2\u3002 \u3000\u3000\u6b66\u7384\u971c\u8ddf\u7740\u90a3\u72d2\u72d2\u7684\u8db3\u8ff9\uff0c\u8dd1\u4e86\u4e00\u7a0b\uff1b\u5ffd\u89c9\u80f8\u53e3\u9690\u9690\u95f7\u75db\uff0c\u6b66\u7384\u60c5\u8c03\u505c\u4e86\u4e00\u4e0b\u547c\u5438\uff0c \u5f85\u8981\u4e0d\u8ffd\uff0c\u5ffd\u53c8\u542c\u5f97\u72d2\u72d2\u7684\u53eb\u58f0\uff0c\u6b66\u7384\u971c\u8f6c\u8fc7\u4e00\u5904\u5c71\u62d7\uff0c\u524d\u9762\u8c41\u7136\u5f00\u6717\uff0c\u53ea\u89c1\u4e00\u5757\u9ad8\u903e \u6570\u5341\u4e08\u7684\u51b0\u5ca9\uff0c\u5c31\u50cf\u4e00\u5ea7\u5c4f\u98ce\u822c\u77d7\u7acb\u9762\u524d\uff0c\u90a3\u4e24\u53ea\u72d2\u72d2\u8d34\u7740\u51b0\u58c1\uff0c\u7adf\u4f3c\u201c\u6302\u201d\u5728\u51b0\u5ca9\u4e4b \u4e0a\u4e00\u822c\u3002\u6b66\u7384\u971c\u559c\u9053\uff1a\u201c\u72d2\u72d2\u5927\u54e5\uff0c\u591a\u8c22\u4f60\u5566\uff01\u201d\u5ffd\u542c\u5f97\u6709\u4eba\u5e94\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u7d2f\u5566\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u5403\u4e86\u4e00\u60ca\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u539f\u6765\u6709\u4e2a\u767d\u8863\u7537\u5b50\u7acb\u5728\u51b0\u58c1\u4e4b\u4e0b\uff0c\u800c\u90a3\u4e24\u53ea\u72d2\u72d2\u5219\u8e0f \u5728\u4ed6\u7684\u80a9\u4e0a\u3002\u53ea\u56e0\u4ed6\u8863\u88f3\u5982\u96ea\uff0c\u800c\u90a3\u4e24\u53ea\u72d2\u6392\u6bdb\u8272\u91d1\u9ec4\uff0c\u886c\u6258\u4e4b\u4e0b\uff0c\u6240\u4ee5\u521a\u624d\u6b66\u7384\u971c\u4e00 \u773c\u770b\u53bb\uff0c\u4f46\u89c1\u72d2\u72d2\uff0c\u672b\u66fe\u6ce8\u610f\u5230\u8fd8\u6709\u4e2a\u4eba\u3002\u6b66\u7384\u971c\u89c1\u90a3\u4e24\u53ea\u72d2\u72d2\u8e72\u5728\u4ed6\u7684\u80a9\u5934\uff0c\u60f3\u4ed6\u5f53 \u7136\u662f\u72d2\u72d2\u7684\u4e3b\u4eba\u65e0\u7591\uff0c\u6b63\u8981\u5411\u4ed6\u9053\u8c22\uff0c\u53ea\u89c1\u90a3\u767d\u8863\u6c49\u5b50\u5df2\u5148\u8fce\u7740\u5979\u8d70\u6765\uff0c\u4e24\u9053\u773c\u5149\uff0c\u53e4 \u602a\u6781\u4e86\uff0c\u6ef4\u6e9c\u6e9c\u7684\u5728\u5979\u8eab\u4e0a\u8f6c\u6765\u8f6c\u53bb\u3002\u6b66\u7384\u971c\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff0c\u82e5 \u4ed6\u662f\u4e2a\u574f\u4eba\uff0c\u8fd9\u56de\u53ef\u8981\u7cdf\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u90a3\u767d\u8863\u7537\u5b50\u53eb\u9053\uff1a\u201c\u4f60\u80cc\u8fc7\u8138\u53bb\uff0c\u5feb\u628a\u4e0a\u8863\u670d\u8131\u4e0b\u6765\uff01\u201d\u6b66\u7384\u971c \u5927\u5403\u4e00\u60ca\uff0c\u60ca\u7591\u4e4b\u4e2d\uff0c\u4e0d\u6687\u63a8\u7a76\u4ed6\u7684\u7528\u610f\uff0c\u4f46\u89c1\u4ed6\u65cb\u98ce\u822c\u7684\u5411\u81ea\u5df1\u51b2\u6765\uff0c\u6b66\u7384\u971c\u7acb\u5373\u633d \u4e86\u4e00\u4e2a\u5251\u8bc0\uff0c\u5587\u7684\u4e00\u62db\u201c\u6a2a\u6307\u5929\u5357\u201d\uff0c\u6a2a\u5251\u524a\u51fa\uff0c\u60f3\u628a\u4ed6\u7684\u6765\u52bf\u7f13\u4f4f\uff0c\u518d\u95ee\u6e05\u695a\u3002\u90a3\u7537 \u5b50\u5ffd\u7136\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u968f\u624b\u6298\u4e86\u4e00\u679d\u6811\u679d\uff0c\u6811\u679d\u4e00\u6311\uff0c\u4f3c\u6233\u4f3c\u523a\uff0c\u7adf\u7136\u7a7f\u8fdb\u5251\u5149\u5708\u5b50\uff0c \u76f4\u523a\u5230\u6b66\u7384\u971c\u80f8\u53e3\u7684\u201c\u4e73\u7a81\u7a74\u201d\uff0c\u4f7f\u7684\u7adf\u662f\u4e00\u62db\u6781\u5389\u5bb3\u7684\u5251\u6cd5\uff0c\u6b66\u7384\u971c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c \u60f3\u9053\uff1a\u201c\u679c\u7136\u662f\u4e2a\u574f\u4eba\u3002\u201d\u8fd9\u65f6\u5df2\u4e0d\u5bb9\u5979\u5206\u795e\u8bf4\u8bdd\uff0c\u767e\u5fd9\u4e2d\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u907f\u62db\u8fdb\u62db\uff0c \u4ece\u201c\u6a2a\u6307\u5929\u5357\u201d\u4e00\u53d8\u800c\u4e3a\u201c\u6469\u661f\u6458\u6597\u201d\uff0c\u8fd9\u4e24\u62db\u4e00\u6c14\u5475\u6210\uff0c\u6b63\u662f\u5979\u5e08\u5085\u7684\u6700\u7cbe\u5999\u5251\u6cd5\uff0c \u90a3\u7537\u5b50\u53c8\u201c\u54a6\u201d\u4e00\u58f0\uff0c\u6811\u679d\u6296\u52a8\uff0c\u987a\u7740\u5251\u52bf\uff0c\u5411\u4e0a\u4e00\u6311\uff0c\u500f\u7684\u5c31\u8df3\u51fa\u4e86\u5251\u5708\uff0c\u7adf\u4f3c\u719f\u6089 \u5979\u7684\u5251\u6cd5\u4e00\u822c\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u5341\u516b\u56de\u3000\u5c48\u5b50\u8ff7\u9014\u5c1a\u672a\u8fd8 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u5341\u516b\u56de\u3000\u5c48\u5b50\u8ff7\u9014\u5c1a\u672a\u8fd8 \u3000\u3000\u6b66\u7384\u971c\u5947\u602a\u6781\u4e86\uff0c\u8981\u77e5\u5979\u5e08\u5085\u6388\u5979\u7684\u8fd9\u5957\u5251\u6cd5\uff0c\u4e0d\u4f46\u53d8\u5316\u7cbe\u5fae\uff0c\u800c\u4e14\u62db\u6570\u7e41\u590d\uff0c\u865a \u4e2d\u6709\u5b9e\uff0c\u62db\u91cc\u5957\u62db\uff0c\u5f0f\u4e2d\u5957\u5f0f\uff0c\u5979\u81ea\u51fa\u5e08\u95e8\u4e4b\u540e\uff0c\u4ed7\u7740\u8fd9\u5957\u5251\u6cd5\uff0c\u4e0d\u77e5\u4f1a\u8fc7\u591a\u5c11\u9ad8\u4eba\uff0c \u4ece\u672a\u6709\u4eba\u80fd\u591f\u7834\u89e3\u3002\u5373\u4f7f\u662f\u5929\u6076\u9053\u4eba\u3001\u706d\u5ea6\u795e\u541b\u8fd9\u7b49\u5389\u5bb3\u7684\u5927\u9b54\u5934\uff0c\u4e5f\u4e0d\u8fc7\u51ed\u7740\u529f\u529b\u6bd4 \u5979\u6df1\u539a\uff0c\u5c06\u5979\u6253\u8d25\u800c\u5df2\u3002\u5982\u4eca\u8fd9\u4e2a\u767d\u8863\u7537\u5b50\uff0c\u4ec5\u4ec5\u7528\u4e00\u6839\u6811\u679d\uff0c\u7adf\u7136\u80fd\u591f\u8f7b\u63cf\u6de1\u5199\u7684\u5c06 \u5979\u90a3\u6837\u7e41\u590d\u7684\u5251\u62db\u2014\u4e00\u5316\u5f00\uff0c\u5206\u660e\u6781\u4e3a\u719f\u6089\u5979\u672c\u95e8\u7684\u5251\u6cd5\uff0c\u8fd9\u662f\u4ece\u6765\u65e0\u6709\u7684\u4e8b\u60c5\uff0c\u4f7f\u5f97 \u6b66\u7384\u971c\u5927\u60d1\u4e0d\u89e3\u3002 \u3000\u3000\u90a3\u767d\u8863\u6c49\u5b50\u4f7f\u7684\u867d\u7136\u4ec5\u662f\u4e00\u6839\u6811\u679d\uff0c\u4f46\u51fa\u624b\u5feb\u6377\uff0c\u62db\u6570\u51cc\u5389\uff0c\u800c\u4e14\u5185\u529b\u5145\u6c9b\uff0c\u6325\u52a8 \u8d77\u6765\uff0c\u547c\u547c\u5e26\u98ce\uff0c\u52b2\u9053\u5341\u8db3\uff0c\u82e5\u7ed9\u4ed6\u6233\u4e2d\uff0c\u5b9e\u4e0d\u4e9a\u4e8e\u5200\u5251\u3002\u6b66\u7384\u971c\u54ea\u6562\u6020\u6162\uff0c\u5f53\u4e0b\u5c06\u5e08 \u95e8\u7684\u7cbe\u5999\u5251\u6cd5\u75be\u5c55\u5f00\u6765\uff0c\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u7aef\u7684\u662f\u8f7b\u5982\u67f3\u7d6e\uff0c\u7fe9\u82e5\u60ca\u9e3f\uff0c\u653b\u4f3c\u72c2\u6d9b\u62cd\u5cb8\uff0c \u5b88\u5982\u6c5f\u6d77\u51dd\u5149\u3002\u4f46\u90a3\u767d\u8863\u6c49\u5b50\u53ea\u662f\u968f\u7740\u5979\u7684\u5251\u52bf\uff0c\u6216\u5219\u8f7b\u8f7b\u4e00\u6311\uff0c\u6216\u5219\u5fae\u5fae\u4e00\u6643\uff0c\u4fbf\u5f80 \u5f80\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u5316\u5f00\u4e86\u5979\u7684\u653b\u52bf\uff0c\u907f\u5f00\u4e86\u5979\u7684\u6740\u624b\u3002\u6b66\u7384\u971c\u8d8a\u6218\u8d8a\u89c9\u60ca\u5947\uff0c\u6b63\u6b32\u559d \u95ee\uff0c\u9661\u7136\u95f4\u4f46\u89c1\u90a3\u767d\u8863\u7537\u5b50\u6811\u679d\u4e00\u98a4\uff0c\u6b66\u7384\u971c\u4e00\u5251\u51fb\u7a7a\uff0c\u80cc\u4e0a\u7684\u201c\u7075\u62a0\u201d\u201c\u4e2d\u5e9c\u201d\u201c\u5927 \u690e\u201d\u201c\u7ef4\u9053\u201d\u201c\u5f52\u85cf\u201d\u201c\u9633\u53a5\u201d\u201c\u5c11\u9634\u201d\u4e03\u5904\u7a74\u9053\uff0c\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u90fd\u5df2\u7ed9\u70b9\u4e2d\uff0c\u6b66\u7384\u971c \u624b\u8155\u4e00\u9ebb\uff0c\u957f\u5251\u8dcc\u5728\u5730\u4e0b\u3002 \u3000\u3000\u90a3\u767d\u8863\u6c49\u5b50\u9053\uff1a\u201c\u6b66\u59d1\u5a18\uff0c\u8bf7\u6055\u65e0\u793c\uff0c\u4f60\u8d76\u5feb\u8fd0\u53e3\u771f\u6c14\uff0c\u8f85\u52a9\u4f53\u5185\u90a3\u80a1\u70ed\u6c14\uff0c\u9006\u51b2 \u4e09\u5173\u3002\u201d\u6b66\u7384\u971c\u5ffd\u89c9\u4f53\u5185\u6709\u80a1\u70ed\u6c14\u51b2\u51fb\u5979\u88ab\u70b9\u7684\u4e03\u5904\u7a74\u9053\uff0c\u8bd5\u4f9d\u90a3\u767d\u8863\u6c49\u5b50\u6240\u8bf4\uff0c\u8fd0\u53e3 \u771f\u6c14\uff0c\u8f85\u52a9\u4f53\u5185\u90a3\u80a1\u70ed\u6c14\u3002\u9006\u51b2\u4e09\u5173\uff0c\u7247\u523b\u4e4b\u9645\u4f46\u89c9\u6c14\u8840\u7545\u901a\uff0c\u8212\u9002\u65e0\u6bd4\u3002\u90a3\u767d\u8863\u6c49\u5b50 \u770b\u5979\u9762\u8272\u6e10\u8f6c\u7ea2\u6da6\uff0c\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u706d\u5ea6\u795e\u541b\u4e00\u638c\uff0c\u975e\u5f97\u5982\u6b64\uff0c\u4e0d\u80fd\u5316\u89e3\u4ed6\u90a3\u9634\u6bd2 \u7684\u638c\u529b\uff1f\u201d\u6b66\u7384\u971c\u8fd9\u624d\u660e\u767d\uff0c\u767d\u8863\u7537\u5b50\u7528\u91cd\u624b\u6cd5\u70b9\u5979\u4e03\u5904\u7a74\u9053\uff0c\u4e43\u662f\u52a9\u5979\u6253\u901a\u7ecf\u8109\uff0c\u5316 \u6bd2\u7597\u4f24\u3002\u8fd9\u6837\u770b\u6765\uff0c\u521a\u624d\u4ed6\u53eb\u81ea\u5df1\u80cc\u8138\u89e3\u8863\uff0c\u5927\u7ea6\u4fbf\u662f\u60f3\u66ff\u81ea\u5df1\u7597\u4f24\u7684\uff0c\u53ea\u602a\u81ea\u5df1\u4e00\u65f6 \u8bef\u4f1a\uff0c\u6ca1\u6709\u95ee\u660e\uff0c\u4fbf\u5373\u52a8\u624b\u3002\u53ef\u662f\u6b66\u7384\u971c\u5fc3\u5934\u8fd8\u6709\u7591\u95ee\uff0c\u90a3\u767d\u8863\u7537\u5b50\u7684\u6b66\u529f\u5206\u660e\u6bd4\u5979\u9ad8 \u5f3a\u5f97\u591a\uff0c\u5374\u4f55\u4ee5\u65e2\u4e0d\u660e\u8a00\uff0c\u5374\u53c8\u76f4\u5230\u6570\u5341\u62db\u4e4b\u540e\uff0c\u624d\u4e0b\u624b\u70b9\u5979\u7684\u7a74\u9053\uff0c\u83ab\u975e\u4e5f\u662f\u6709\u610f\u8bd5 \u62db\uff1f \u3000\u3000\u6b66\u7384\u971c\u60f3\u81f3\u6b64\u5904\uff0c\u4fbf\u62fe\u8d77\u5b9d\u5251\uff0c\u5148\u5411\u4ed6\u8c22\u4e86\u4e00\u58f0\u3002\u8ddf\u7740\u95ee\u9053\uff1a\u201c\u6562\u95ee\u6069\u516c\u9ad8\u6027\u5927\u540d\uff0c \u5c0a\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u90a3\u767d\u8863\u6c49\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u6765\uff0c\u4fbf\u4f1a\u77e5\u9053\uff01\u201d\u8bf4\u7f62\u8f6c\u8eab\u4fbf\u8d70\uff0c\u90a3 \u4e24\u53ea\u91d1\u53d1\u72d2\u72d2\u54a7\u5f00\u53e3\u602a\u53eb\uff0c\u4e5f\u597d\u50cf\u6b22\u8fce\u6b66\u7384\u971c\u7684\u6837\u5b50\uff0c\u4f38\u76f4\u4e24\u53cc\u624b\u81c2\uff0c\u5411\u5979\u6253\u4e86\u4e2a\u62f1\uff0c \u4fbf\u4ece\u6811\u4e0a\u8df3\u4e0b\uff0c\u8d70\u5728\u524d\u5934\u5e26\u8def\u3002\u6b66\u7384\u971c\u7591\u60d1\u6781\u4e86\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u66ff\u6211\u7597\u4f24\uff0c\u60f3\u6765 \u5f53\u4e0d\u4f1a\u5b58\u6709\u574f\u610f\u3002\u201d\u4e8e\u662f\u8ddf\u5728\u90a3\u767d\u8863\u6c49\u5b50\u7684\u80cc\u540e\uff0c\u4e24\u4eba\u4e24\u517d\uff0c\u76f4\u5165\u6df1\u5c71\u3002 \u3000\u3000\u96ea\u5cf0\u63d2\u4e91\uff0c\u51b0\u5ddd\u5982\u955c\uff0c\u5929\u5c71\u666f\u8272\uff0c\u58ee\u4e3d\u65e0\u4f26\u3002\u6b66\u7384\u971c\u5c55\u5f00\u201c\u767b\u840d\u6e21\u6c34\u201d\u3001\u201c\u8e0f\u96ea\u65e0 \u75d5\u201d\u7684\u4e0a\u4e58\u8f7b\u529f\uff0c\u7d27\u7d27\u7684\u8ddf\u5728\u90a3\u767d\u8863\u7537\u5b50\u7684\u540e\u9762\uff0c\u4fbf\u89c1\u4ed6\u5728\u51b0\u5ca9\u5ced\u58c1\u4e4b\u4e0a\u4ece\u5bb9\u4e3e\u6b65\uff0c\u597d \u50cf\u6beb\u4e0d\u8d39\u529b\u7684\u6837\u5b50\uff0c\u6b66\u7384\u971c\u7adf\u81ea\u4e0d\u80fd\u8d85\u8d8a\u4ed6\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u8d70\u4e86\u534a\u5929\uff0c\u4f46\u89c9\u6c14\u5019\u6e10\u8f6c\u6e29\u6696\uff0c\u4e0a\u5230\u4e00\u5ea7\u5c71\u5934\uff0c\u53ea\u89c1\u82b1\u8349\u7e41\u8302\uff0c\u9762\u524d\u8c41\u7136\u5f00\u6717\uff0c\u539f \u6765\u5c71\u9876\u4e0a\u8fd8\u6709\u4e00\u4e2a\u5c0f\u6e56\uff0c\u6e56\u5149\u4e91\u5f71\uff0c\u9e1f\u8bed\u82b1\u9999\uff0c\u5728\u51b0\u5c01\u96fe\u9501\u7684\u96ea\u5c71\u4e0a\u7a81\u7136\u89c1\u5230\u6b64\u7b49\u666f\u8272\uff0c \u5f53\u771f\u4f3c\u662f\u6765\u5230\u4ed9\u5883\u4e00\u822c\uff0c\u90a3\u767d\u8863\u7537\u5b50\u9053\uff1a\u201c\u8fd9\u4fbf\u662f\u8457\u540d\u7684\u5929\u6c60\u4e86\u3002\u636e\u8bf4\u6b64\u5730\u672c\u6765\u662f\u4e2a\u706b \u5c71\u53e3\uff0c\u706b\u5c71\u7184\u706d\u4e4b\u540e\uff0c\u706b\u5c71\u53e3\u5316\u4e3a\u6e56\u6cca\uff0c\u6240\u4ee5\u5730\u6c14\u6e29\u6696\u3002\u7ed5\u8fc7\u5929\u6c60\uff0c\u6709\u4e2a\u77f3\u7a9f\uff0c\u90a3\u767d\u8863 \u7537\u5b50\u63a8\u5f00\u5c01\u6d1e\u7684\u77f3\u5934\uff0c\u5411\u6b66\u7384\u971c\u62db\u624b\u9053\uff1a\u201c\u8bf7\u8fdb\u6765\u7f62\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u7565\u4e00\u8fdf\u7591\uff0c\u60f3\u9053\uff1a\u201c\u65e2\u6765\u4e4b\uff0c\u5219\u5b89\u4e4b\u3002\u4ed6\u6b66\u529f\u8fdc\u80dc\u4e8e\u6211\uff0c\u82e5\u8981\u5bb3\u6211\uff0c\u4e5f\u65e0\u987b \u5f15\u6211\u5230\u8fd9\u91cc\u6765\u3002\u201d\u987e\u8651\u4e00\u6d88\uff0c\u8fc8\u6b65\u4fbf\u8fdb\uff0c\u77f3\u7a9f\u91cc\u51ff\u6709\u5c0f\u6d1e\u900f\u5149\uff0c\u6b66\u7384\u971c\u4e3e\u76ee\u4e00\u671b\uff0c\u5ffd\u89c1 \u6d1e\u4e2d\u6709\u5f20\u77f3\u6848\uff0c\u77f3\u6848\u4e0a\u6709\u4e2a\u5c3c\u59d1\uff0c\u76d8\u819d\u800c\u5750\u3002\u5468\u56f4\u56f4\u7740\u900f\u660e\u7684\u7389\u77f3\u5c4f\u98ce\uff0c\u4f3c\u662f\u4e00\u5c0a\u795e\u50cf\uff0c \u4f46\u795e\u8272\u6829\u6829\u5982\u4e3b\uff0c\u5374\u53c8\u7edd\u4e0d\u50cf\u662f\u6ce5\u5851\u6728\u96d5\u7684\u5076\u50cf\uff01 \u3000\u3000\u6b66\u7384\u971c\u597d\u50cf\u53d1\u68a6\u4e00\u822c\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u7136\u53cc\u819d\u8dea\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff0c\u539f\u6765\u4f60\u5728 \u8fd9\u513f\u5440\uff01\u5f92\u513f\u7384\u971c\u6765\u4e86\uff01\u201d\u77f3\u6848\u4e0a\u7684\u5c3c\u59d1\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6b66\u7384\u971c\u5947\u602a\u6781\u4e86\uff0c\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60 \u600e\u4e48\u4e0d\u8bf4\u8bdd\u5440\uff01\u201d\u90a3\u767d\u8863\u7537\u5b50\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5df2\u6b7b\u4e09\u5e74\u4e86\uff01\u6211\u7b49\u5230\u4eca\u5929\uff0c\u624d\u7b49\u7740\u4f60 \u6765\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5979\u8fd8\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6742\uff0c\u6025\u5fd9\u8df3\u8d77\u6765\uff0c\u5c06\u77f3\u6848\u7684\u5c4f\u98ce\u7a0d \u7a0d\u79fb\u5f00\uff0c\u4f38\u624b\u5f80\u91cc\u9762\u4e00\u63a2\uff0c\u4f46\u89c9\u89e6\u624b\u5982\u51b0\uff0c\u5979\u5e08\u7236\u7684\u5c38\u4f53\u65e9\u5df2\u50f5\u786c\uff0c\u6709\u5982\u5316\u77f3\u3002\u6b66\u7384\u971c \u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u9893\u7136\u5012\u5730\uff0c\u597d\u534a\u664c\u624d\u54ed\u5f97\u51fa\u58f0\u6765\u3002 \u3000\u3000\u90a3\u767d\u8863\u7537\u5b50\u5f85\u5979\u54ed\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u65e0\u75be\u800c\u7ec8\uff0c\u53ea\u7b49\u4f60\u6765\uff0c\u4e86\u5374\u5979\u4e00\u6869\u5fc3\u613f\uff0c \u6211\u4eec\u4fbf\u53ef\u9001\u5979\u5165\u5c71\u4e86\u3002\u5e08\u59b9\uff0c\u4f60\u4e0d\u5fc5\u592a\u8fc7\u60b2\u4f24\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u500f\u5730\u8df3\u8d77\uff0c\u51dd\u89c6\u7740\u90a3\u767d\u8863\u7537\u5b50\uff0c\u90a3\u767d\u8863\u7537\u5b50\u9053\uff1a\u201c\u7384\u971c\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u3002\u4f60 \u5341\u5c81\u90a3\u5e74\uff0c\u6211\u89c1\u8fc7\u4f60\uff0c\u5230\u5982\u4eca\u7b97\u6765\u5df2\u6709\u5341\u516d\u5e74\u4e86\u3002\u4e5f\u96be\u602a\u4f60\u8ba4\u4e0d\u5f97\u6211\u4e86\u3002\u82e5\u4e0d\u662f\u521a\u624d\u6211 \u8bd5\u51fa\u4e86\u4f60\u7684\u5251\u6cd5\uff0c\u6211\u4e5f\u4e0d\u6562\u4e0e\u4f60\u76f8\u8ba4\u5462\uff01\u201d\u6b66\u7384\u971c\u62ed\u4e86\u773c\u6cea\uff0c\u518d\u671b\u4ed6\u4e00\u773c\u3002\u8bf4\u9053\uff1a\u201c\u5475\uff0c \u539f\u6765\u4f60\u662f\u88f4\u5927\u54e5\u3002\u201d\u90a3\u7537\u5b50\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u88f4\u53d4\u5ea6\u3002\u5e08\u7236\u4e34\u6b7b\u7684\u65f6\u5019\uff0c\u662f\u6211\u5f85\u5019\u5728 \u5979\u8001\u4eba\u5bb6\u8eab\u8fb9\u3002\u201d\u539f\u6765\u8fd9\u88f4\u53d4\u5ea6\u662f\u6b66\u7384\u971c\u5e08\u7236\u7684\u4eb2\u4f84\u513f\uff0c\u4ed6\u7684\u6b66\u529f\u4e43\u662f\u59d1\u59d1\u6240\u6388\uff0c\u6240\u4ee5 \u4e5f\u79f0\u5979\u4e3a\u5e08\u7236\uff0c\u6b66\u7384\u971c\u5728\u5e08\u7236\u95e8\u4e0b\u7684\u90a3\u51e0\u5e74\uff0c\u4ed6\u65e9\u5df2\u51fa\u5e08\uff0c\u5728\u5916\u95ef\u8361\u6c5f\u6e56\uff0c\u56e0\u6b64\u4e24\u5e08\u5144 \u59b9\u53ea\u5728\u5c0f\u65f6\u5019\u89c1\u8fc7\u4e00\u6b21\u9762\u3002 \u3000\u3000\u6b66\u7384\u971c\u6ee1\u8179\u7591\u56e2\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u7236\u5979\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u5e08\u7236\u7559\u4e0b\u4e86\u4e00\u672c\u8bd7\u6587\u96c6\uff0c\u5631\u4f60\u5e26\u56de\u53bb\u732e\u7ed9\u5929\u540e\uff0c\u5979\u8bf4\u5929\u540e\u662f\u6700\u77e5\u9053\u5979 \u5fc3\u4e8b\u7684\u4eba\u3002\u8fd9\u672c\u8bd7\u6587\u96c6\u4f60\u53ef\u5148\u770b\uff0c\u770b\u4e86\u4e4b\u540e\uff0c\u5c31\u53ef\u4ee5\u77e5\u9053\u5979\u8001\u4eba\u5bb6\u4e3a\u4ec0\u4e48\u5230\u8fd9\u513f\u6765\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u6253\u5f00\u8fd9\u672c\u8bd7\u6587\u96c6\u4e00\u770b\uff0c\u53ea\u89c1\u6249\u9875\u4e0a\u6240\u9898\u7684\u7b2c\u4e00\u9996\u8bd7\u4fbf\u662f\uff1a\u201c\u6b32\u5029\u9752\u79bd\u5bc4\u8bed\u96be\uff0c \u5fc3\u968f\u660e\u6708\u5230\u5929\u5c71\u3002\u4e09\u5341\u5e74\u7269\u6362\u661f\u79fb\u540e\uff0c\u5c48\u5b50\u8ff7\u9014\u5c1a\u672a\u8fd8\u3002\u201d\u6b66\u7384\u971c\u5fc3\u5934\u4e00\u9707\uff0c\u5979\u5bf9\u5e08\u7236 \u7684\u751f\u5e73\u7565\u77e5\u4e00\u4e8c\u3002\u77e5\u9053\u5979\u6709\u8fc7\u4e00\u573a\u60c5\u5b7d\uff0c\u5982\u4eca\u770b\u4e86\u8fd9\u4e00\u9996\u8bd7\uff0c\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u5979\u51e0\u5341\u5e74 \u6765\uff0c\u4e00\u76f4\u6000\u5ff5\u7740\u7684\u90a3\u4e2a\u4eba\uff0c\u5c31\u662f\u674e\u9038\u7684\u5e08\u7236\u5c09\u8fdf\u70af\u3002 \u3000\u3000\u8fd9\u672c\u8bd7\u6587\u96c6\u7684\u8bb8\u591a\u9996\u8bd7\u90fd\u662f\u201c\u7eaa\u4e8b\u8bd7\u201d\uff0c\u6b66\u7384\u971c\u5306\u5306\u4e00\u89c8\uff0c\u5bf9\u5e08\u7236\u7684\u8eab\u4e16\u4e0e\u5979\u66ae\u5e74 \u7684\u5fc3\u5883\u90fd\u5df2\u660e\u767d\uff0c\u5979\u62dc\u7740\u8fd9\u672c\u8bd7\u6587\u96c6\uff0c\u773c\u6cea\u4e0d\u81ea\u7981\u7684\u53c8\u4e00\u9897\u9897\u7684\u6ef4\u4e0b\u6765\u3002 \u3000\u3000\u539f\u6765\u5979\u7684\u5e08\u7236\u4fd7\u5bb6\u540d\u5b57\u53eb\u505a\u88f4\u743c\u9999\uff0c\u5979\u7684\u7236\u4eb2\u88f4\u6587\u5e86\u5728\u5510\u592a\u5b97\u7684\u65f6\u5019\u66fe\u5b98\u5c45\u201c\u4ec6\u5c04\u201d \u4e4b\u804c\uff0c\u662f\u4e2a\u9887\u6709\u540d\u6c14\u7684\u5927\u81e3\u3002\u5f53\u65f6\u793e\u4f1a\u4e0a\u6709\u4e2a\u98ce\u6c14\uff0c\u5bcc\u8d35\u4eba\u5bb6\u7684\u5b50\u5973\u5e38\u5e38\u9001\u5230\u5bfa\u9662\u91cc\u53bb \u505a\u201c\u8bb0\u540d\u5f1f\u5b50\u201d\uff0c\u751a\u81f3\u201c\u5e26\u53d1\u4fee\u884c\u201d\u51e0\u5e74\uff0c\u636e\u8bf4\u8fd9\u6837\u53ef\u4ee5\u501f\u201c\u4f5b\u529e\u201d\u4fdd\u4f51\u5b69\u5b50\u201c\u957f\u547d\u5bcc \u8d35\u201d\uff0c\u88f4\u743c\u9999\u51fa\u751f\u4e4b\u65f6\uff0c\u5979\u6bcd\u4eb2\u7ed9\u5979\u7b97\u547d\uff0c\u6c5f\u6e56\u672f\u58eb\u8bf4\u5979\u201c\u547d\u5b98\u201d\u4e0d\u597d\uff0c\u591a\u707e\u591a\u96be\uff0c\u6240 \u4ee5\u5230\u5979\u516b\u5c81\u90a3\u5e74\uff0c\u5979\u6bcd\u4eb2\u4fbf\u5c06\u5979\u9001\u5230\u4eac\u90fd\u4e00\u95f4\u4e13\u6536\u5bb9\u8d35\u65cf\u5987\u5973\u7684\u5bfa\u9662\u2014\u2014\u611f\u4e1a\u5bfa\u53bb\uff0c\u505a \u4e00\u4e2a\u8bb0\u540d\u5f1f\u5b50\uff0c\u201c\u5e26\u53d1\u4fee\u884c\u201d\u3002 \u3000\u3000\u611f\u4e1a\u5bfa\u6709\u4e2a\u8001\u5c3c\u59d1\u540d\u53eb\u5999\u7389\uff0c\u5979\u7684\u4e08\u592b\u672c\u6765\u662f\u5510\u592a\u5b97\u7684\u5fa1\u524d\u5f85\u536b\uff0c\u6b66\u827a\u9ad8\u5f3a\uff0c\u5251\u672f \u5c24\u5176\u7cbe\u5999\uff0c\u4e0d\u5e78\u5728\u8d1e\u89c2\u5341\u516b\u5e74\u5f81\u9ad8\u4e3d\u4e4b\u5f79\u9635\u4ea1\uff0c\u6ca1\u6709\u5b50\u5973\u9057\u4e0b\uff0c\u4ed6\u7684\u59bb\u5b50\u4fbf\u5728\u611f\u4e1a\u5bfa\u524a \u53d1\u4e3a\u5c3c\uff0c\u6cd5\u53f7\u5999\u7389\u3002\u5999\u7389\u5728\u5bfa\u4e2d\u7cbe\u7814\u5251\u6cd5\uff0c\u8eab\u6000\u7edd\u4e16\u6b66\u529f\uff0c\u4f46\u9601\u5bfa\u4eba\u7b49\uff0c\u5374\u65e0\u4e00\u4eba\u77e5\u9053\u3002 \u5f85\u5230\u88f4\u743c\u9999\u5165\u5bfa\u4e4b\u65f6\uff0c\u5999\u7389\u5df2\u7ecf\u5e74\u8001\uff0c\u4e24\u4eba\u751a\u662f\u6295\u7f18\uff0c\u5999\u7389\u4e5f\u60f3\u7559\u4e0b\u4f20\u4eba\uff0c\u4fbf\u5728\u6697\u4e2d\u4f20 \u6388\u88f4\u743c\u9999\u7684\u5251\u6cd5\u3002 \u3000\u3000\u4e0d\u4e45\uff0c\u5999\u7389\u901d\u4e16\u3002\u90a3\u65f6\u5510\u592a\u5b97\u674e\u4e16\u6c11\u4ea6\u5df2\u901d\u4e16\u3002\u6b66\u5219\u5929\u88ab\u9a71\u9010\u51fa\u5b98\uff0c\u4e5f\u5230\u4e86\u611f\u4e1a\u5bfa \u6765\u505a\u5c3c\u59d1\u3002\u6b66\u5219\u5929\u6000\u6709\u96c4\u5fc3\u58ee\u5fd7\uff0c\u5904\u5904\u7269\u8272\u4eba\u624d\u3002\u88f4\u63a0\u9999\u4e00\u89c1\u4e86\u5979\u3002\u5c31\u77e5\u9053\u5979\u4e0d\u662f\u5e73\u51e1 \u7684\u5973\u5b50\uff0c\u4e24\u4eba\u9042\u503e\u5fc3\u7ed3\u7eb3\uff0c\u6210\u4e3a\u77e5\u5df1\u3002\u6709\u4e00\u6b21\u6b66\u5219\u5929\u7684\u4ec7\u654c\u5165\u5bfa\u884c\u523a\uff0c\u4fbf\u662f\u88f4\u743c\u9999\u6697\u4e2d \u5c06\u523a\u5ba2\u8d76\u8dd1\u7684\u3002 \u3000\u3000\u540e\u6765\u6b66\u5219\u5929\u88ab\u9ad8\u5b97\u7687\u5e1d\uff08\u674e\u4e16\u6c11\u4e4b\u5b50\u674e\u6cbb\uff09\u62d4\u5165\u540e\u5bab\uff0c\u4ece\u201c\u662d\u4eea\u201d\uff08\u6b21\u4e8e\u8d35\u5983\u7684\u4e00 \u79cd\u5c01\u53f7\uff09\u4e00\u76f4\u505a\u5230\u7687\u540e\uff0c\u88f4\u743c\u9999\u5e26\u53d1\u4fee\u884c\u5df2\u6ee1\uff0c\u4e5f\u968f\u6b66\u5219\u5929\u5165\u5b98\u505a\u4e86\u5973\u5b98\u3002\u4e0d\u4e45\u6b66\u5219\u5929 \u5f00\u59cb\u6405\u6743\uff0c\u8d2c\u524a\u738b\u516c\u8d35\u65cf\u3002\u8bb8\u591a\u5927\u81e3\uff0c\u90fd\u9884\u611f\u5230\u5510\u671d\u7684\u6c5f\u5c71\u5fc5\u5c06\u8f6c\u79fb\u5230\u6b66\u5219\u5929\u624b\u4e2d\uff0c\u4e8e \u662f\u7ed3\u6210\u515a\u7fbd\uff0c\u6697\u4e2d\u53cd\u5bf9\u6b66\u5219\u5929\uff0c\u5176\u65f6\u5c09\u8fdf\u70af\u8eab\u4e3a\u795e\u6b66\u8425\u7684\u9f99\u9a91\u90fd\u5c09\uff0c\u4ed6\u4e5f\u662f\u53cd\u5bf9\u6b66\u5219\u5929 \u7684\u4e00\u4e2a\u91cd\u8981\u4eba\u7269\u3002\u4ed6\u53cd\u5bf9\u6b66\u5219\u5929\u4e0d\u6253\u7d27\uff0c\u5374\u5f04\u5230\u4e86\u88f4\u743c\u9999\u7684\u5904\u5883\u6781\u662f\u4e3a\u96be\u3002\u539f\u6765\u4ed6\u4e8c\u4eba \u672c\u662f\u4e2d\u6bd2\u4e4b\u4eb2\uff0c\u800c\u4e14\u81ea\u5e7c\u6709\u4e86\u5a5a\u59fb\u4e4b\u7ea6\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u77e5\u9053\u88f4\u743c\u9999\u751a\u5f97\u6b66\u5219\u5929\u7684\u4fe1\u4efb\uff0c\u4fbf\u627e\u4e2a\u673a\u4f1a\uff0c\u4e0e\u672a\u5a5a\u59bb\u79c1\u4e0b\u4f1a\u9762\uff0c\u6c42\u88f4\u743c\u9999 \u6697\u4e2d\u5e2e\u52a9\u4ed6\u4eec\u3002\u88f4\u743c\u9999\u542c\u5f97\u671d\u4e2d\u7684\u4e00\u73ed\u5927\u81e3\u7ed3\u6210\u515a\u7fbd\uff0c\u5bc6\u8c0b\u8d77\u4e8b\uff0c\u8981\u5c06\u6b66\u5219\u5929\u4e00\u4e3e\u63a8\u7ffb\uff0c \u5403\u60ca\u975e\u5c0f\u3002\u5979\u79bb\u5f00\u4e86\u5c09\u8fdf\u5207\u4e4b\u540e\uff0c\u56de\u5230\u5b98\u4e2d\uff0c\u60f3\u4e86\u6574\u6574\u4e00\u5929\u4e00\u591c\uff0c\u7ec8\u4e8e\u5411\u6b66\u5219\u5929\u544a\u53d1\u3002 \u6b66\u5219\u5929\u4f55\u7b49\u7cbe\u660e\uff0c\u4e0d\u52a8\u58f0\u8272\u7684\u6697\u4e2d\u5e03\u7f6e\uff0c\u5e03\u597d\u4e86\u5929\u7f57\u5730\u7f51\uff0c\u7a81\u7136\u62a2\u5148\u52a8\u624b\uff0c\u5c06\u6700\u91cd\u8981\u7684 \u4e24\u4e2a\u4eba\u7269\u2014\u2014\u56fd\u8205\u957f\u5b59\u65e0\u5fcc\u548c\u897f\u53f0\u4f8d\u90ce\u4e0a\u5b98\u4eea\u6740\u4e86\u3002\u63a5\u7740\u8fde\u6740\u4e86\u4e09\u5341\u516d\u5bb6\u516c\u537f\u8d35\u65cf\u3002\u5c09 \u8fdf\u70af\u6b66\u827a\u9ad8\u5f3a\uff0c\u53c8\u89c1\u673a\u5f97\u65e9\uff0c\u5e78\u800c\u9003\u51fa\u4eac\u57ce\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u53cd\u5bf9\u6b66\u5219\u5929\u7684\u4eba\u7269\uff0c\u5728\u8fd9\u4e00\u5f79 \u4e2d\u51e0\u4e4e\u88ab\u4e00\u7f51\u6253\u5c3d\u3002 \u3000\u3000\u88f4\u743c\u9999\u5e76\u6ca1\u6709\u540e\u4fae\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u6b66\u5219\u5929\u82e5\u7136\u505a\u4e86\u7687\u5e1d\uff0c\u4e0d\u4f46\u5929\u4e0b\u6587\u5b50\u53ef\u4ee5\u626c\u7709\u5410\u6c14\uff0c \u5bf9\u8001\u767e\u59d3\u4e5f\u4f1a\u6709\u597d\u5904\u3002\u53ef\u662f\u5979\u867d\u7136\u6ca1\u6709\u540e\u6094\uff0c\u5374\u4e0d\u80fd\u4e0d\u56e0\u6b64\u4f24\u5fc3\uff0c\u5979\u4fdd\u62a4\u4e86\u6b66\u5219\u5929\uff0c\u5374 \u6c38\u8fdc\u5931\u53bb\u4e86\u5979\u6240\u7231\u7684\u672a\u5a5a\u592b\u4e86\u3002 \u3000\u3000\u88f4\u743c\u9999\u4e0d\u80af\u63a5\u53d7\u6b66\u5219\u5929\u7684\u5c01\u8d4f\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u8fc7\u540e\uff0c\u5979\u4e5f\u79bb\u5f00\u4e86\u6b66\u5219\u5929\uff0c\u6b66\u5219\u5929\u77e5\u9053\u5979 \u7684\u5fc3\u4e8b\uff0c\u8bf7\u5979\u5c06\u5c09\u8fdf\u70af\u529d\u56de\u6765\uff0c\u53ef\u662f\u5c09\u8fdf\u70af\u5df2\u6068\u6781\u4e86\u5979\uff0c\u6839\u672c\u5c31\u4e0d\u613f\u610f\u518d\u89c1\u5979\u4e86\u3002\u88f4\u743c \u9999\u4f24\u5fc3\u4e4b\u4f59\uff0c\u4fbf\u4e5f\u524a\u53d1\u4e3a\u5c3c\uff0c\u56de\u5230\u6545\u4e61\u9690\u5c45\uff0c\u4e00\u9762\u6f5c\u5fc3\u6b66\u5b66\uff0c\u4e00\u9762\u4f20\u6388\u5979\u4f84\u513f\u88f4\u53d4\u5ea6\u7684 \u5251\u6cd5\u3002\u5728\u8fd9\u671f\u95f4\uff0c\u6b66\u5219\u5929\u5230\u5404\u5904\u53bb\u89c6\u5bdf\u6c11\u60c5\uff0c\u4e5f\u66fe\u53bb\u89c1\u8fc7\u88f4\u743c\u9999\u51e0\u6b21\uff0c\u6b66\u5219\u5929\u5f53\u7136\u5e0c\u671b \u88f4\u743c\u9999\u56de\u5230\u5979\u7684\u8eab\u8fb9\uff0c\u88f4\u743c\u9999\u5374\u518d\u4e5f\u4e0d\u613f\u56de\u53bb\uff0c\u4f46\u5979\u548c\u6b66\u5219\u5929\u7684\u60c5\u8c0a\u4ecd\u662f\u975e\u5e38\u6df1\u539a\uff0c\u5979 \u987e\u5ff5\u5230\u6b66\u5219\u5929\u6ca1\u6709\u6700\u4eb2\u4fe1\u7684\u6b66\u529f\u9ad8\u5f3a\u7684\u4eba\u5e2e\u5979\uff0c\u4fbf\u7b54\u5e94\u7ed9\u6b66\u5219\u5929\u8c03\u6559\u51fa\u4e00\u4e2a\u6587\u6b66\u5168\u624d\u7684 \u5973\u5f1f\u5b50\uff0c\u8fd9\u4fbf\u662f\u5979\u540e\u6765\u6536\u6b66\u7384\u971c\u4e3a\u5f92\u7684\u7531\u6765\u3002 \u3000\u3000\u5f85\u5230\u6b66\u7384\u971c\u6388\u6210\u4e4b\u540e,\u88f4\u743c\u9999\u91cd\u5165\u6c5f\u6e56\uff0c\u8bbf\u5bfb\u5c09\u8fdf\u70af\u7684\u6d88\u606f\uff0c\u7ec8\u4e8e\u7ed9\u5979\u6253\u542c\u5230\u5c09\u8fdf\u70af \u5728\u5929\u5c71\u9690\u5c45\uff0c\u4e8e\u662f\u4fbf\u79bb\u5f00\u4e2d\u539f\uff0c\u8fdc\u8d70\u6f20\u5317\uff0c\u8fd9\u65f6\u5019\u6b66\u5219\u5929\u65e9\u5df2\u79f0\u5e1d\uff0c\u800c\u88f4\u743c\u9999\u4e5f\u5df2\u7ecf\u662f \u5c06\u8fd1\u516d\u5341\u5c81\u7684\u8001\u4eba\u4e86\u3002\u5979\u6015\u81ea\u5df1\u4e00\u8eab\u7684\u6b66\u5b66\u5931\u4f20\uff0c\u7b54\u5e94\u4e86\u4f84\u513f\u88f4\u53d4\u5ea6\u7684\u8bf7\u6c42\uff0c\u643a\u4ed6\u540c\u884c\u3002 \u8fd9\u4fbf\u662f\u5979\u548c\u88f4\u53d4\u5ea6\u6765\u5230\u5929\u5c71\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u6b66\u7384\u971c\u770b\u5b8c\u4e86\u5979\u5e08\u7236\u7684\u90a3\u672c\u8bd7\u6587\u96c6\uff0c\u773c\u6cea\u4e0d\u81ea\u7981\u7684\u53c8\u4e00\u9897\u9897\u7684\u6ef4\u535e\u6765\u3002\u5979\u4eec\u4e24\u5e08\u5f92 \u7684\u9645\u9047\u662f\u4f55\u5176\u76f8\u4f3c\u5475\uff01\u5979\u5e08\u7236\u53bb\u627e\u5bfb\u5c09\u8fdf\u70af\uff0c\u800c\u5979\u5219\u5728\u627e\u5bfb\u674e\u9038\u3002\u5982\u4eca\u5c09\u8fdf\u70af\u7684\u9aa8\u5934\u65e9 \u5df2\u5316\u7070\uff0c\u5979\u7684\u5e08\u7236\u4e5f\u6b7b\u4e86\u3002\u674e\u9038\u867d\u7136\u5c1a\u5728\u4eba\u95f4\uff0c\u4f46\u53ea\u6015\u674e\u9038\u4e5f\u50cf\u4ed6\u5e08\u7236\u4e00\u6837\uff0c\u4e0d\u613f\u610f\u518d \u89c1\u5979\u4e86\u3002\u4f55\u51b5\u5728\u674e\u9038\u4e0e\u5979\u4e4b\u95f4\uff0c\u8fd8\u6709\u4e00\u4e2a\u957f\u5b59\u58c1\u3002\u8fd9\u6bd4\u5979\u5e08\u7236\u7684\u60c5\u5f62\uff0c\u66f4\u8981\u590d\u6742\uff0c\u66f4\u8981 \u96be\u89e3\uff0c\u7eb5\u7136\u674e\u9038\u613f\u610f\u89c1\u5979\uff0c\u5979\u81ea\u5df1\u4e5f\u4e0d\u60f3\u5377\u5165\u8fd9\u4e2a\u65cb\u6da1\u53bb\u4e86\u3002\u957f\u5b59\u58c1\u5bf9\u5979\u662f\u5982\u6b64\u731c\u5fcc\uff0c \u5979\u53c8\u5c82\u5fcd\u59a8\u788d\u4e86\u4ed6\u4eec\u592b\u59bb\u4e4b\u95f4\u7684\u5e78\u798f\uff1f\u53c8\u5c82\u5fcd\u4ee4\u957f\u5b59\u58c1\u523b\u9aa8\u4f24\u5fc3\uff1f\u5979\u6367\u7740\u5e08\u7236\u7684\u8bd7\u96c6\uff0c \u597d\u4e45\uff0c\u597d\u4e45\uff0c\u624d\u62ed\u5e72\u773c\u6cea\uff0c\u95ee\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4eec\u5230\u4e86\u5929\u5c71\u4e4b\u540e\uff0c\u53ef\u66fe\u89c1\u8fc7\u5c09\u8fdf\u70af\u4e48\uff1f\u201d \u3000\u3000\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u5927\u7ea6\u662f\u89c1\u7740\u4e86\u3002\u201d\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u600e\u4e48\u8bf4\u662f\u5927\u7ea6\u89c1\u7740\uff1f\u8fde\u4f60\u4e5f\u4e0d\u786e\u5b9e \u77e5\u9053\u4e48\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u6211\u4eec\u6765\u5230\u5929\u5c71\u4e4b\u540e\uff0c\u5728\u5929\u6c60\u65c1\u8fb9\u627e\u5230\u4e86\u8fd9\u4e2a\u77f3\u7a9f\uff0c\u5c31\u4f4f\u4e86\u4e0b\u6765\u3002 \u90a3\u65f6\u6211\u5e76\u4e0d\u77e5\u9053\u59d1\u59d1\u662f\u6765\u627e\u5979\u7684\u672a\u5a5a\u592b\u7684\uff0c\u4e5f\u4e0d\u77e5\u9053\u5c09\u8fdf\u70af\u5c31\u4f4f\u5728\u4e0b\u9762\u3002\u6709\u4e00\u65e0\u665a\u4e0a\uff0c \u5927\u96ea\u8fc7\u540e\uff0c\u6708\u8272\u6e05\u660e\uff0c\u6211\u59d1\u59d1\u8bf4\u8981\u53bb\u89c1\u4e00\u4e2a\u670b\u53cb\uff0c\u53eb\u6211\u5728\u5bb6\u4e2d\u5b88\u95e8\u6237\uff0c\u4e0d\u53ef\u5916\u51fa\u8d70\u52a8\u3002 \u6211\u5f88\u5947\u602a\uff0c\u5728\u8fd9\u6837\u9ad8\u7684\u5929\u5c71\u96ea\u5cf0\u4e4b\u4e0a\uff0c\u59d1\u59d1\u54ea\u91cc\u6765\u7684\u670b\u53cb\uff1f\u90a3\u4e00\u665a\u6211\u542c\u89c1\u59d1\u59d1\u4f20\u97f3\u5165\u5bc6 \u7684\u4e0a\u4e58\u5185\u529f\u5728\u51b0\u5cf0\u4e0a\u957f\u5578\uff0c\u4e0d\u4e45\u5c31\u6709\u53e6\u4e00\u4e2a\u5578\u58f0\u4ece\u4e0b\u9762\u9690\u9690\u4f20\u8fc7\u6765\uff0c\u6211\u9075\u5b88\u59d1\u59d1\u7684\u5429\u5490\uff0c \u4e0d\u6562\u51fa\u53bb\u770b\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5578\u58f0\u4e5f\u5c31\u505c\u6b62\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u665a\u3002\u59d1\u59d1\u6574\u665a\u6ca1\u6709\u56de\u6765\uff0c\u7b2c\u4e8c\u5929\u4e00\u4eae\u56de\u6765\u5c31\u75c5\u5012\u4e86\uff01\u201d\u6b66\u7384\u971c\u8be7\u9053\uff1a\u201c\u6211\u5e08 \u5085\u5185\u529f\u6df1\u539a\uff0c\u5f53\u4e16\u65e0\u654c\uff0c\u5979\u600e\u7684\u4f1a\u75c5\u5012\u4e86\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u59d1\u59d1\u56de\u6765\u4e4b\u540e\uff0c\u7cbe\u795e\u975e\u5e38\u9893 \u4e27\uff0c\u770b\u6765\u5979\u6839\u672c\u5c31\u6ca1\u6709\u8fd0\u7528\u5185\u529f\u6cbb\u75c5\u3002\u5979\u75c5\u5012\u4e4b\u540e\uff0c\u5c31\u9677\u5165\u4e86\u660f\u8ff7\u7684\u72b6\u6001\u4e2d\uff0c\u4e0d\u65ad\u547b\u541f\uff0c \u8bf4\uff1a\u2018\u597d\u51b7\uff0c\u597d\u51b7\uff01\u2019\u6211\u7ed9\u5979\u751f\u706b\u53d6\u6696\uff0c\u5b89\u6170\u5979\u9053\uff1a\u2018\u59d1\u59d1\uff0c\u5f85\u4f60\u75c5\u597d\u4e4b\u540e\uff0c\u54b1\u4eec\u5c31\u56de \u5357\u65b9\u53bb\u5427\u3002\u2019\u59d1\u59d1\u77aa\u7740\u773c\u775b\u671b\u6211\uff0c\u597d\u50cf\u4e0d\u8ba4\u8bc6\u6211\u7684\u6837\u5b50\uff0c\u5ffd\u7136\u5c16\u58f0\u53eb\u9053\uff1a\u2018\u5c09\u8fdf\u54e5\u54e5\uff0c \u4f60\u4e0d\u56de\u53bb\uff0c\u6211\u4e5f\u4e0d\u56de\u53bb\u4e86\uff01\u2019\u6211\u8fd9\u624d\u77e5\u9053\uff0c\u5979\u6628\u665a\u6240\u4f1a\u89c1\u7684\u4eba\u6562\u60c5\u5c31\u662f\u5979\u7684\u672a\u5a5a\u592b\u5c09\u8fdf \u70af\u3002\u59d1\u59d1\u7684\u5a5a\u53d8\uff0c\u6211\u662f\u542c\u957f\u8f88\u8bf4\u8fc7\u7684\uff0c\u6211\u9664\u4e86\u6068\u5c09\u8fdf\u70af\u65e0\u60c5\u4e4b\u5916\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u6cd5\u5b50\u5b89\u6170 \u5979\u3002\u7b2c\u4e8c\u5929\u6211\u51fa\u5916\u53bb\u62fe\u67af\u679d\uff0c\u5728\u96ea\u5730\u4e0a\u8fd8\u770b\u89c1\u51cc\u4e71\u7684\u8db3\u5370\uff0c\u4e00\u4e2a\u662f\u59d1\u59d1\u7684\uff0c\u53e6\u4e00\u4e2a\u8f83\u4e3a \u957f\u5927\u4e9b\uff0c\u770b\u5f97\u51fa\u662f\u7537\u5b50\u7684\u8db3\u5370\u3002\u51cc\u4e71\u7684\u8db3\u5370\u8e0f\u904d\u4e86\u5c71\u5934\u51e0\u91cc\u65b9\u5706\u4e4b\u5730\uff0c\u63a8\u60f3\u4ed6\u4eec\u4e24\u4eba\u7684 \u5fc3\u60c5\uff0c\u4e5f\u4e00\u5b9a\u662f\u50cf\u8db3\u5370\u90a3\u4e48\u51cc\u4e71\u3002\u201d\u6b66\u7384\u971c\u5fc3\u91cc\u53f9\u4e86\u53e3\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u867d\u7136\u4e0d\u80af\u4e0e \u5979\u56de\u53bb\uff0c\u4f46\u80af\u4e0e\u5979\u957f\u591c\u503e\u8c08\uff0c\u4ed6\u5bf9\u5979\u7684\u6028\u60f3\u6765\u4e5f\u8be5\u6d88\u89e3\u4e86\uff1f\u674e\u9038\u5374\u672a\u5fc5\u80af\u63a8\u5fc3\u7f6e\u8179\uff0c\u548c \u6211\u4f5c\u8fd9\u6837\u7684\u5f7b\u591c\u4e4b\u8c08\u5462\u3002\u201d \u3000\u3000\u88f4\u53d4\u5ea6\u6b47\u4e86\u4e00\u6b47\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u59d1\u59d1\u7684\u75c5\u4e00\u5929\u6c89\u91cd\u4e00\u5929\uff0c\u6709\u4e00\u5929\u6211\u5728\u5979\u7684\u75c5\u584c\u4e4b\u65c1 \u5b88\u5019\uff0c\u7ffb\u9605\u5979\u6240\u8457\u7684\u5251\u8c31\uff0c\u770b\u5230\u4e00\u5904\u4e0d\u660e\u767d\u7684\u5730\u65b9\uff0c\u60f3\u8d77\u59d1\u59d1\u82e5\u6709\u4e0d\u6d4b\uff0c\u4ee5\u540e\u4e0d\u77e5\u5411\u8c01 \u8bf7\u6559\uff0c\u773c\u6cea\u4e0d\u81ea\u7981\u7684\u5c31\u6ef4\u7740\u4e0b\u6765\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u59d1\u59d1\u5ffd\u7136\u7741\u5f00\u773c\u775b\u770b\u6211\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u2018\u6211 \u7684\u5251\u8c31\u8fd8\u6ca1\u6709\u5199\u5b8c\uff0c\u6ca1\u529e\u6cd5\u6211\u53ea\u597d\u591a\u6d3b\u51e0\u5e74\u4e86\u3002\u2019\u81ea\u4ece\u90a3\u5929\u8fc7\u540e\uff0c\u59d1\u59d1\u7684\u75c5\u4fbf\u4e00\u5929\u5929\u597d \u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u5927\u7ea6\u53c8\u8fc7\u4e86\u4e00\u4e2a\u6708\u7684\u5149\u666f\uff0c\u59d1\u59d1\u53eb\u6211\u968f\u5979\u53bb\u91c7\u4e86\u8bb8\u591a\u91ce\u82b1\uff0c\u7f16\u6210\u4e24\u4e2a\u82b1\u73af\uff0c\u5979\u62ff \u7740\u82b1\u73af\uff0c\u6211\u8ddf\u5728\u5979\u540e\u9762\uff0c\u5c31\u5728\u51b0\u5cf0\u4e0b\u9762\u7684\u8f6c\u89d2\u4e4b\u5904\uff0c\u53d1\u73b0\u4e86\u4e00\u5ea7\u65b0\u575f\uff0c\u5893\u7891\u4e0a\u523b\u7684\u662f \u2018\u5929\u5c71\u5251\u5ba2\u5c09\u8fdf\u70af\u4e4b\u5893\uff0c\u95e8\u4eba\u674e\u9038\u5055\u59bb\u957f\u5b59\u58c1\u656c\u7acb\u3002\u2019\u59d1\u59d1\u5c06\u82b1\u73af\u653e\u5728\u5893\u524d\uff0c\u9ed8\u9ed8\u65e0\u8a00 \u7684\u62dc\u4e86\u4e09\u62dc\u3002\u8fd9\u65f6\u6211\u624d\u77e5\u9053\u5c09\u8fdf\u70af\u5df2\u7ecf\u75c5\u6b7b\u4e86\u3002\u59d1\u59d1\u884c\u793c\u4e4b\u540e\uff0c\u7a81\u7136\u54ed\u4e86\u51fa\u6765\uff0c\u54fd\u54bd\u8bf4 \u9053\uff1a\u201c\u7384\u971c\uff0c\u7384\u971c\uff0c\u4f60\u4e5f\u597d\u53ef\u601c\u5475\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u5fc3\u5f26\u98a4\u6296\uff0c\u60f3\u8d77\u4e86\u4e00\u4ef6\u4e8b\uff0c\u5f53\u5979\u5b66\u6210\u5251\u672f\uff0c\u62dc\u522b\u5e08\u95e8\u4e4b\u65f6\uff0c\u5e08\u7236\u66fe\u5bf9\u5979\u8a00\u9053\uff1a \u201c\u674e\u5510\u7687\u5ba4\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u4eba\u540d\u53eb\u674e\u9038\uff0c\u6b66\u529f\u4eba\u54c1\uff0c\u90fd\u8fd8\u4e0d\u9519\u3002\u53ea\u662f\u4ed6\u4e00\u5b9a\u53cd\u5bf9\u4f60\u7684\u59d1\u59d1\uff0c \u4f60\u82e5\u78b0\u5230\u4e86\u4ed6\uff0c\u80fd\u529d\u4ed6\u4e0e\u4f60\u540c\u4e00\u8def\u8d70\u56fa\u7136\u6700\u597d\u3002\u82e5\u7136\u4e0d\u80fd\uff0c\u4f60\u4e5f\u8981\u624b\u4e0b\u7559\u60c5\u3002\u201d\u5982\u4eca\u60f3 \u6765\uff0c\u5e08\u7236\u53ef\u80fd\u662f\u56e0\u4e3a\u5979\u548c\u5c09\u8fdf\u70af\u5df2\u65e0\u590d\u5408\u4e4b\u671b\uff0c\u6240\u4ee5\u5e0c\u671b\u4e0b\u4e00\u4ee3\u6210\u4e3a\u597d\u53cb\u3002\u5927\u7ea6\u6211\u548c\u674e \u9038\u4ee5\u540e\u7684\u4e8b\u60c5\uff0c\u5e08\u7236\uff0c\u5979\uff0c\u5979\u4e5f\u77e5\u9053\u4e86\u3002\u8981\u4e0d\u7136\u5979\u4e0d\u4f1a\u5728\u5c09\u8fdf\u70af\u7684\u5893\u524d\u8bf4\u51fa\u90a3\u4e24\u53e5\u8bdd\u6765\u3002 \u88f4\u53d4\u5ea6\u770b\u5979\u4e00\u773c\u3002\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u59d1\u59d1\u65f6\u5e38\u6000\u5ff5\u4e8e\u4f60\uff0c\u5979\u5927\u7ea6\u662f\u611f\u6000\u8eab\u4e16\uff0c\u6240\u4ee5\u53c8\u60f3\u8d77 \u4f60\u6765\u3002\u201d\u5176\u5b9e\u6590\u53d4\u5ea6\u5982\u4eca\u5c1a\u672a\u660e\u767d\uff0c\u4ed6\u7684\u59d1\u59d1\u5728\u81ea\u5df1\u6781\u5ea6\u4f24\u5fc3\u4e4b\u9645\uff0c\u5374\u4e3a\u4ec0\u4e48\u53cd\u800c\u8bf4\u51fa \u53ef\u601c\u7384\u971c\u7684\u8bdd\u8bed\u3002\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u6b66\u7384\u971c\u4e0e\u674e\u9038\u4e4b\u95f4\uff0c\u4e5f\u6709\u4e00\u756a\u60c5\u5b7d\u7ea0\u7f20\uff01 \u3000\u3000\u6b66\u7384\u971c\u7a0d\u5b9a\u5fc3\u795e\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u7236\u5979\u540e\u6765\u600e\u6837\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u4ece\u90a3\u4e00\u5929\u4e0a\u575f\u4e4b\u540e\uff0c \u59d1\u59d1\u5c31\u5728\u7a33\u5c45\u4e4b\u4e2d\u95ed\u95e8\u4e0d\u51fa\uff0c\u82e6\u5fc3\u4fee\u7ec3\u5979\u7684\u5251\u672f\u3002\u8fc7\u4e86\u5c06\u8fd1\u4e94\u5e74\u7684\u65f6\u5149\uff0c\u5979\u7684\u5251\u8c31\u5df2\u7ecf \u5199\u6210\uff0c\u6709\u4e00\u5929\u665a\u4e0a\uff0c\u5979\u5c06\u6211\u53eb\u6765\uff0c\u5429\u5490\u6211\u4e24\u4ef6\u4e8b\u60c5\u3002\u7b2c\u4e00\u4ef6\u662f\uff1a\u82e5\u5979\u53bb\u4e16\u4e4b\u540e\uff0c\u8981\u6211\u6697 \u4e2d\u4fdd\u62a4\u674e\u9038\u592b\u59bb\uff0c\u4f46\u5374\u4e0d\u8bb8\u6211\u4e0e\u4ed6\u4eec\u5f80\u6765\u3002\u7b2c\u4e8c\u4ef6\u662f\uff1a\u8981\u6211\u5728\u8fd9\u91cc\u7b49\u4f60\uff0c\u5979\u8bf4\u4f60\u8fdf\u65e9\u4f1a \u5bfb\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u7b49\u4f60\u6765\u65f6\uff0c\u8981\u6211\u5c06\u5979\u7684\u8bd7\u6587\u96c6\u548c\u5251\u8c31\u4ea4\u7ed9\u4f60\u3002\u5979\u8fd8\u53ee\u5631\u6211\uff0c\u8bf4\u662f\u82e5\u7136\u53d1 \u73b0\u4f60\u5230\u5929\u5c71\uff0c\u6700\u597d\u7acb\u5373\u5f15\u4f60\u5230\u8fd9\u91cc\u6765\uff0c\u4e0d\u8981\u8ba9\u4f60\u7ecf\u8fc7\u4e0b\u9762\u7684\u90a3\u5ea7\u9a86\u9a7c\u5cf0\u3002\u6211\u77e5\u9053\u5c09\u8fdf\u70af \u7684\u6545\u5eb8\u4fbf\u5728\u9a86\u9a7c\u5cf0\u4e0a\uff0c\u770b\u6765\u5979\u662f\u4e0d\u60f3\u4f60\u548c\u90a3\u5bf9\u592b\u59bb\u89c1\u9762\u3002\u6211\u5bf9\u5979\u7684\u5429\u5490\uff0c\u611f\u5230\u5947\u602a\u6781\u4e86\uff0c \u4e3a\u4ec0\u4e48\u8981\u6211\u7acb\u5373\u5c06\u4f60\u5f15\u6765\u8fd9\u91cc\uff0c\u4e0d\u60f3\u4f60\u4e0e\u4ed6\u4eec\u89c1\u9762\uff1f\u201d\u6b66\u7384\u971c\u907f\u5f00\u4ed6\u7684\u773c\u5149\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5e08\u7236\u7684\u7528\u610f\u3002\u201d\u58f0\u97f3\u786c\u54bd\uff0c\u6ee1\u6000\u51c4\u6eb6\u3002\u5176\u5b9e\u5979\u5f53\u7136\u77e5\u9053\u5e08\u7236\u7684\u82e6\u5fc3\uff0c\u4e0d\u8fc7 \u5979\u4e0d\u65b9\u4fbf\u5bf9\u88f4\u53d4\u5ea6\u8bf4\u51fa\u6765\u7f62\u4e86\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u4e5f\u89c9\u5f97\u5979\u7684\u795e\u60c5\u5947\u5f02\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u5f53\u65f6\u5df2\u611f\u5230\u6709\u70b9\u4e0d\u7965\u4e4b\u5146\uff0c\u60f3\u4e0d\u5230\u7b2c \u4e8c\u5929\u6211\u7684\u59d1\u59d1\u679c\u7136\u65e0\u75be\u800c\u7ec8\u3002\u6211\u9075\u7167\u5979\u7684\u5631\u5490\uff0c\u5c06\u5979\u7684\u9057\u4f53\u6d82\u4e0a\u836f\u6599\uff0c\u7b49\u5019\u4f60\u6765\uff0c\u518d\u884c \u9001\u5979\u5982\u571f\u3002\u5929\u5c71\u8fd9\u6837\u5e7f\u5927\uff0c\u6211\u6015\u4f60\u6765\u65f6\u6211\u6ca1\u6709\u53d1\u73b0\uff0c\u4fbf\u5929\u5929\u53eb\u8fd9\u4e24\u53ea\u72d2\u72d2\u51fa\u53bb\u63a2\u671b\u3002\u8fd9 \u4e24\u53ea\u72d2\u72d2\u662f\u6211\u59d1\u59d1\u5728\u5357\u7586\u897f\u53cc\u7248\u7eb3\u4e1b\u6797\u4e4b\u4e2d\u6536\u670d\u7684\uff0c\u6781\u901a\u7075\u6027\uff0c\u6211\u59d1\u59d1\u5c06\u4f60\u5c0f\u65f6\u5019\u7684\u8863 \u7269\u90a3\u4e9b\u4e1c\u897f\uff0c\u5979\u4e00\u76f4\u4fdd\u5b58\u4e0b\u6765\u2014\u2014\u7ed9\u5b83\u95fb\u8fc7\uff0c\u82e5\u662f\u4f60\u6765\uff0c\u5b83\u4eec\u53ef\u4ee5\u95fb\u5230\u4f60\u7684\u6c14\u606f\uff0c\u4fbf\u4f1a \u6765\u62a5\u544a\u6211\u4e86\u3002\u201d\u6b66\u7384\u971c\u542c\u5230\u8fd9\u91cc\uff0c\u8fd9\u624d\u77e5\u9053\u521a\u624d\u90a3\u4e24\u53ea\u72d2\u72d2\uff0c\u4f55\u4ee5\u4f1a\u5e2e\u5979\u6253\u9000\u706d\u5ea6\u795e\u541b\u3002 \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u5144\uff0c\u4f60\u4eec\u867d\u7136\u7528\u5fc3\u826f\u82e6\uff0c\u6211\u5374\u4ecd\u7136\u662f\u89c1\u8fc7\u4e86\u957f\u5b59\u58c1\uff0c\u4e5f\u5230\u8fc7\u9a86\u9a7c \u5cf0\u5c09\u8fdf\u70af\u7684\u6545\u5c45\u4e86\u3002\u201d\u88f4\u53d4\u5ea6\u6b47\u4e86\u4e00\u6b47\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u4ee5\u524d\u8ba4\u8bc6\u674e\u9038\u592b\u5987\u7684\u5417\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u53cc\u988a\u5fae\u73b0\u7ea2\u6655\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u90fd\u8ba4\u8bc6\u7684\u3002\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u6211\u66fe\u5077\u770b\u8fc7\u4ed6\u4eec\u7ec3 \u5251\uff0c\u957f\u5b59\u58c1\u7684\u5251\u672f\uff0c\u597d\u50cf\u662f\u5ce8\u5d4b\u4e00\u6d3e\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u6b63\u662f\u957f\u5b59\u5747\u91cf\u7684\u5973\u513f\u3002\u201d \u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4ed6\u4eec\u4e24\u592b\u5987\u90fd\u662f\u5251\u672f\u540d\u5bb6\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u786e\u662f\u73e0\u8054\u58c1\u5408\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u6291\u4e0b\u5fc3\u5e95\u7684\u8f9b\u9178\uff0c\u542c\u4ed6\u8bf4\u9053\uff1a\u201c\u957f\u5b59\u58c1\u7684\u9020\u8be3\u672a\u6df1\uff0c\u4e0d\u8fc7\uff0c\u82e5\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c \u4e16\u7b97\u5f97\u4e00\u628a\u597d\u624b\u4e86\u3002\u5979\u7684\u4e08\u592b\u6bd4\u5979\u9ad8\u5f97\u591a\uff0c\u6211\u5077\u770b\u8fc7\u4ed6\u51e0\u6b21\uff0c\u4e00\u6b21\u6bd4\u4e00\u6b21\u9ad8\u660e\uff0c\u770b\u6765\u4ed6 \u5df2\u628a\u5e08\u7236\u4e0e\u5cb3\u7236\u8fd9\u4e24\u5927\u5bb6\u7684\u5251\u672f\u878d\u4f1a\u8d2f\u901a\uff0c\u9020\u8be3\u4e4b\u6df1\uff0c\u5dee\u4e0d\u591a\u53ef\u4ee5\u6324\u8fdb\u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\u4e86\u3002\u201d \u6b66\u7384\u971c\u751a\u4e3a\u6b22\u559c\uff0c\u9053\uff1a\u201c\u90a3\u4e0d\u9519\u5440\u3002\u201d\u88f4\u53d4\u5ea6\u5fae\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u6211\u59d1\u59d1\u4e0d\u8bb8\u6211\u4e0e\u4ed6\u4eec\u5f80\u6765\uff0c \u8981\u4e0d\u7136\u76f8\u4e92\u5207\u78cb\uff0c\u5012\u662f\u5f7c\u6b64\u6709\u76ca\u7684\u4e8b\u3002\u4ee5\u4ed6\u73b0\u5728\u7684\u9020\u8be3\u800c\u8bba\uff0c\u518d\u8fc7\u51e0\u5e74\uff0c\u53ea\u6015\u6211\u4e5f\u5f97\u7518 \u62dc\u4e0b\u98ce\u3002\u8fd8\u4f55\u987b\u6211\u6697\u4e2d\u4fdd\u62a4\u4ed6\u4eec\u5462\uff1f\u4f55\u51b5\u4ed6\u4eec\u5728\u5929\u5c71\u9690\u5c45\uff0c\u96be\u9053\u8fd8\u4f1a\u6709\u4ec0\u4e48\u4ec7\u4eba\u5230\u8fd9\u91cc \u6765\u5bfb\u4ed6\u4eec\u5417\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u8fd9\u624d\u77e5\u9053\u5e08\u5144\u521a\u624d\u95ee\u5979\u8ba4\u4e0d\u8ba4\u8bc6\u674e\u9038\u592b\u5987\u7684\u7528\u610f\uff0c\u6562\u60c5\u4e43\u662f\u60f3\u63a2\u542c\u4ed6\u4eec\u6709\u6ca1 \u6709\u4ec0\u4e48\u5389\u5bb3\u7684\u4ec7\u4eba\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u90a3\u6837\u5429\u5490\uff0c\u60f3\u6765\u5fc5\u6709\u7528\u610f\uff0c\u5927\u7ea6\u4f60\u672a\u77e5\u9053\uff0c \u674e\u9038\u4e43\u662f\u5510\u5ba4\u7684\u7687\u5b59\u8eab\u4efd\u3002\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u54e6\uff0c\u662f\u5417\uff1f\u4e0d\u8fc7\u4f9d\u6211\u60f3\u6765\uff0c\u4ed6\u82e5\u662f\u4e0d\u53cd\u5bf9\u5929 \u540e\uff0c\u5929\u540e\u4e5f\u65ad\u4e0d\u4f1a\u6d3e\u4eba\u6765\u523a\u6740\u4ed6\uff0c\u4f60\u662f\u5929\u540e\u7684\u4f84\u5973\uff0c\u5929\u540e\u7684\u4e3a\u4eba\uff0c\u4f60\u5f53\u7136\u6bd4\u6211\u77e5\u9053\u5f97\u66f4 \u6e05\u695a\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u6b64\u6b21\u5c31\u662f\u5949\u5929\u540e\u4e4b\u547d\u6765\u627e\u4ed6\u7684\u3002\u5929\u540e\u60f3\u4f20\u4f4d\u7ed9\u5979\u7684 \u513f\u5b50\u5362\u9675\u738b\u674e\u663e\uff0c\u60f3\u8bf7\u4ed6\u56de\u53bb\u8f85\u52a9\u5462\u3002\u5e08\u7236\u65e2\u7136\u4e0d\u60f3\u6211\u89c1\u4ed6\u4eec\u592b\u5987\uff0c\u8fd9\u4e8b\u60c5\u5c31\u8bf7\u4f60\u8f6c\u8fbe \u597d\u4e48\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u8981\u4e0d\u662f\u89c1\u4f60\u4eca\u5929\u5230\u6765\uff0c\u6211\u51e0\u4e4e\u5c31\u8981\u4e0b\u5c71\u53bb\u5bfb\u627e\u4ed6\u4eec\u4e86\u3002\u6211\u5949\u4e86\u5e08 \u7236\u4e4b\u547d\uff0c\u8981\u6697\u4e2d\u4fdd\u62a4\u4ed6\u4eec\uff0c\u6240\u4ee5\u5f88\u7559\u5fc3\u4ed6\u4eec\u7684\u884c\u8e2a\uff0c\u6628\u5929\u5374\u53d1\u73b0\u4ed6\u4eec\u592b\u5987\u90fd\u5148\u540e\u4e0b\u5c71\u53bb \u4e86\uff0c\u8fd9\u662f\u51e0\u5e74\u6765\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\uff0c\u6211\u60f3\u53bb\u6253\u542c\u4e00\u4e0b\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u6253\u542c\u4e86\u3002\u4ed6\u4eec\u5927\u7ea6\u662f\u53bb\u627e\u7a81\u53a5\u53ef\u6c57\u53bb\u4e86\u3002\u201d\u88f4\u53d4\u5ea6\u5947\u9053\uff1a\u201c\u8fd9 \u5374\u4e3a\u4f55\uff1f\u201d\u6b66\u7384\u971c\u5c06\u5728\u5929\u5c71\u811a\u4e0b\u6240\u78b0\u89c1\u7684\u4e8b\u60c5\u8bf4\u4e86\u4e00\u904d\uff0c\u5374\u7565\u53bb\u5979\u4e0e\u957f\u5b59\u58c1\u79c1\u4e0b\u4f1a\u9762\u7684 \u8fd9\u4ef6\u4e8b\u60c5\u4e0d\u8bf4\uff0c\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u539f\u6765\u662f\u4ed6\u4eec\u7684\u513f\u5b50\u88ab\u7a81\u53a5\u53ef\u6c57\u63b3\u53bb\u4e86\u3002\u65e2\u7136\u8fd8\u6709\u4e00\u4e2a\u6708\u7684 \u671f\u9650\uff0c\u5f85\u6211\u4eec\u57cb\u846c\u4e86\u5e08\u7236\u4e4b\u540e\uff0c\u5c31\u53bb\u52a9\u4ed6\u4eec\u4e00\u81c2\u4e4b\u529b\u5427\u3002\u5e08\u59b9\uff0c\u4f60\u5750\u4e00\u5750\uff0c\u5e08\u7236\u8fd8\u6709\u4e00 \u6837\u4e1c\u897f\u7ed9\u4f60\uff0c\u5f85\u6211\u8fdb\u91cc\u9762\u53bb\u62ff\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u72ec\u81ea\u51dd\u601d\uff0c\u65e2\u611f\u8f9b\u9178\uff0c\u53c8\u89c9\u6b22\u559c\u3002\u60f3\u9053\uff1a\u201c\u6709\u5e08\u5144\u53bb\u6697\u52a9\u4e8e\u4ed6\uff0c\u6211\u53ef\u4ee5\u653e\u4e0b \u5fc3\u4e86\uff0c\u4f46\u6211\u5c31\u771f\u7684\u4ece\u6b64\u4fbf\u518d\u4e0d\u89c1\u4ed6\u4e86\u4e48\uff1f\u201d\u773c\u5149\u53c8\u843d\u5230\u5979\u5e08\u7236\u5728\u6249\u9875\u4e0a\u6240\u9898\u7684\u90a3\u4e00\u9996\u8bd7 \u4e0a\u3002\u5fc3\u91cc\u541f\u9053\uff1a\u201c\u6b32\u60c5\u9752\u79bd\u5bc4\u8bed\u96be\uff0c\u5fc3\u968f\u660e\u6708\u5230\u5929\u5c71\uff0c\u4e09\u5341\u5e74\u7269\u6362\u661f\u79fb\u540e\uff0c\u5c48\u5b50\u8ff7\u9014\u5c1a \u672a\u8fd8\uff01\u55ef\uff0c\u8fd9\u4e00\u9996\u8bd7\u4e5f\u597d\u50cf\u662f\u4e3a\u6211\u5199\u7684\u5440\uff01\u6211\u5728\u957f\u5b89\u4e4b\u65f6\uff0c\u591a\u5c11\u4e2a\u6708\u5706\u4e4b\u591c\uff0c\u4e5f\u66fe\u5fc3\u968f \u660e\u6708\uff0c\u68a6\u5230\u5929\u5c71\u3002\u5982\u4eca\u4e07\u91cc\u8fe2\u8fe2\u6765\u5230\u6b64\u5730\uff0c\u96be\u9053\u5c31\u8fd9\u6837\u7684\u53c8\u56de\u53bb\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u8bfb\u5979\u5e08\u7236\u7684\u8fd9\u9996\u8bd7\uff0c\u81ea\u81ea\u7136\u7136\u7684\u60f3\u8d77\u4e86\u4e0a\u5b98\u5a49\u513f\uff0c\u8fd9\u51e0\u5e74\u6765\uff0c\u5979\u4eec\u4e8c\u4eba\u4eb2\u5982 \u59d0\u59b9\uff0c\u65e0\u8bdd\u4e0d\u8c08\uff0c\u53ea\u9664\u4e86\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5979\u6ca1\u6709\u628a\u5fc3\u4e2d\u5bf9\u674e\u9038\u7684\u7231\u610f\u544a\u8bc9\u5a49\u513f\uff0c\u56e0\u4e3a\u5979\u5bdf\u89c9 \u5a49\u513f\u5bf9\u674e\u9038\u7684\u601d\u5ff5\u4e4b\u60c5\uff0c\u5b9e\u4e0d\u5728\u5979\u4e4b\u4e0b\u3002\u5979\u8bb0\u8d77\u4e86\u5a49\u513f\u6240\u5199\u7684\u90a3\u4e00\u9996\u8bd7\uff1a\u201c\u6c5f\u6e56\u7a7a\u62b1\u5e7d \u5170\u6028\uff0c\u5c82\u662f\u79bb\u9a9a\u5c48\u5b50\u5fc3\uff0c\u695a\u6cfd\u957f\u5b89\u96be\u5e76\u8bba\uff0c\u5929\u6daf\u4f55\u82e6\u4f5c\u884c\u541f\uff1f\u201d\u8fd9\u4e00\u9996\u8bd7\u7684\u610f\u601d\u548c\u5979\u5e08 \u7236\u7684\u7adf\u662f\u5b8c\u5168\u4e00\u6837\uff01\u5f53\u5e74\u5979\u66fe\u628a\u8fd9\u65b9\u8bd7\u7ee2\u63d2\u5728\u53e4\u7434\u4e4b\u4e2d\uff0c\u53eb\u4e2b\u73af\u8ffd\u53bb\uff0c\u9001\u7ed9\u674e\u9038\uff0c\u60f3\u6765 \u674e\u9038\u662f\u5b9a\u7136\u770b\u8fc7\u7684\u4e86\u3002\u60f3\u4e0d\u5230\u7684\u662f\u674e\u9038\u4e5f\u4e0e\u4ed6\u5e08\u7236\u4e00\u6837\uff1a\u8ff7\u9014\u5c48\u5b50\uff0c\u7adf\u4e0d\u77e5\u8fd8\uff01 \u3000\u3000\u5979\u53c8\u60f3\u8d77\u8fd9\u6b21\u51fa\u4eac\u4e4b\u65f6\uff0c\u5a49\u513f\u66fe\u6258\u5979\u5c06\u51e0\u53e5\u8bdd\u5e26\u7ed9\u674e\u9038\uff0c\u5982\u4eca\u5979\u5df2\u4e0d\u613f\u518d\u89c1\u674e\u9038\uff0c \u53ef\u662f\u5a49\u513f\u8fd9\u51e0\u53e5\u8bdd\u5374\u662f\u4e0d\u80fd\u6258\u5e08\u5144\u8f6c\u8fbe\u7684\uff0c\u8fd9\u53c8\u600e\u4e48\u597d\u5462\uff1f\u5979\u53ef\u4ee5\u5fcd\u53d7\u523b\u9aa8\u4f24\u5fc3\uff0c\u5374\u4e0d \u5fcd\u8d1f\u4e86\u5a49\u513f\u4e4b\u6258\u3002 \u3000\u3000\u6b66\u7384\u971c\u4f46\u611f\u6709\u5982\u4e71\u4e1d\u585e\u80f8\uff0c\u6b63\u81ea\u59d4\u51b3\u4e0d\u4e0b\uff0c\u88f4\u53d4\u5ea6\u5df2\u7ecf\u8d70\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u521a\u624d\u90a3 \u672c\u8bd7\u6587\u96c6\u662f\u5e08\u7236\u6258\u4f60\u8f6c\u4ea4\u7ed9\u5929\u540e\u7684\uff0c\u8fd9\u672c\u5251\u8c31\u5219\u662f\u7559\u7ed9\u4f60\u7684\u3002\u4f60\u7684\u806a\u660e\u80dc\u6211\u5341\u500d\uff0c\u5c06\u6765 \u53d1\u626c\u672c\u95e8\u7684\u5251\u672f\uff0c\u7ee7\u627f\u5e08\u7236\u7684\u8863\u4f53\uff0c\u53ef\u5f97\u501a\u4ed7\u4f60\u4e86\u3002\u201d\u6b66\u7384\u971c\u63a5\u8fc7\u5251\u8c31\uff0c\u5411\u5e08\u7236\u7684\u9057\u4f53 \u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u611f\u5230\u987a\u601d\u6df1\u91cd\uff0c\u773c\u6cea\u53c8\u7981\u4e0d\u4f4f\u6ef4\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u6590\u53d4\u5ea6\u9053\uff1a\u201c\u4f60\u9001\u5e08\u7236\u5165\u571f\u4e4b\u540e\uff0c\u5c31\u51c6\u5907\u56de\u53bb\u4e86\u5417\uff1f\u201d\u6b66\u7384\u971c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u662f \u7684\u3002\u674e\u9038\u7684\u4e8b\u60c5\u62dc\u6258\u4f60\u4e86\u3002\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u4f60\u56de\u53bb\u4e5f\u597d\uff0c\u6211\u4e5f\u60f3\u62dc\u6258\u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u2019 \u6b66\u7384\u971c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u91d1\u9488\u56fd\u624b\u590f\u4faf\u575a\u4e48\uff1f\u201d\u6b66\u7384\u971c\u5fc3\u5934\u4e00 \u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u516b\u5e74\u4e4b\u524d\uff0c\u66fe\u89c1\u8fc7\u4ed6\u4e00\u6b21\uff0c\u4ed6\u4e5f\u66fe\u95ee\u8d77\u6211\u4eec\u7684\u5e08\u7236\u5462\uff1f\u201d\u6590\u53d4\u5ea6\u9053\uff1a\u201c\u4f60 \u600e\u4e48\u56de\u7b54\u4ed6\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u51fa\u5e08\u95e8\u4e4b\u65f6\uff0c\u5e08\u7236\u66fe\u5429\u5490\u6211\u4e0d\u8bb8\u5411\u4efb\u4f55\u4eba\u63d0\u53ca\u5979\u7684\u540d\u53f7\uff0c \u6240\u4ee5\u6211\u5c31\u7528\u82b1\u6735\u6392\u51fa\u4e0d\u53ef\u8bf4\u3001\u4e0d\u53ef\u8bf4\u516d\u4e2a\u5927\u5b57\u3002\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u590f\u4faf\u575a\u89c1\u4f60\u8fd9\u6837\u56de\u7b54\uff0c \u4ed6\u53c8\u600e\u4e48\u8bf4\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4ed6\u4e5f\u7528\u82b1\u6735\u6392\u51fa\u5982\u4e4b\u4f55\uff1f\u5982\u4e4b\u4f55\uff1f\u516d\u4e2a\u5927\u5b57\u3002\u201d \u3000\u3000\u88f4\u53d4\u5ea6\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u59d1\u59d1\u5728\u5a5a\u53d8\u4e4b\u540e\uff0c\u4e0e\u590f\u4faf\u575a\u76f8\u8bc6\uff0c\u590f\u4faf\u575a\u5f53\u65f6\u4e0d\u77e5\u9053 \u5979\u6709\u8fd9\u6bb5\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u5bf9\u5979\u975e\u5e38\u503e\u5e55\u3002\u6211\u59d1\u59d1\u5fc3\u4e2d\u53ea\u6709\u4e00\u4e2a\u5c09\u8fdf\u70af\uff0c\u5f53\u7136\u4e0d\u4f1a\u7b54\u5e94\u4ed6\u7684\u6c42 \u5a5a\u3002\u53ef\u662f\u4ed6\u4eec\u4e8c\u4eba\u4e5f\u7ed3\u6210\u4e86\u809d\u80c6\u76f8\u7167\u7684\u670b\u53cb\u3002\u59d1\u59d1\u5728\u5929\u5c71\u51e0\u5e74\uff0c\u66fe\u91c7\u6458\u51e0\u6735\u5929\u5c71\u96ea\u83b2\uff0c \u8fd8\u6709\u51e0\u6837\u5979\u4ee5\u524d\u5728\u5404\u5904\u5404\u5c71\u6240\u6765\u96c6\u7684\u7075\u836f\uff0c\u5979\u4e34\u6b7b\u4e4b\u524d\uff0c\u5c06\u5929\u5c71\u96ea\u83b2\u548c\u8fd9\u51e0\u4e2a\u7075\u836f\u90fd\u653e \u5728\u4e00\u4e2a\u7389\u5323\u4e4b\u4e2d\uff0c\u53eb\u6211\u5c06\u6765\u4ea4\u7ed9\u590f\u4faf\u575a\u3002\u4f60\u53cd\u6b63\u8981\u91cd\u56de\u4e2d\u571f\uff0c\u90a3\u4e48\u5c31\u7701\u5f97\u6211\u591a\u8dd1\u4e00\u8d9f\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u66f4\u89c9\u5fc3\u5934\u6c89\u91cd\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u89c1\u90a3\u4e24\u53ea\u72d2\u72d2\u5728\u6d1e\u53e3\u4f01\u7acb\u8d77\u6765\uff0c\u597d\u50cf\u542c\u5230\u4e86\u4ec0 \u4e48\u58f0\u97f3\u4f3c\u7684\u5ffd\u7136\u53d1\u51fa\u5431\u5431\u7684\u602a\u53eb\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u7b11\u9053\uff1a\u201c\u60f3\u662f\u6709\u4ec0\u4e48\u751f\u4eba\u4e86\u3002\u597d\u5427\uff0c\u4f60\u4eec\u8981\u53bb\uff0c\u5c31\u53bb\u770b\u770b\u5427\uff0c\u53ef\u4e0d\u8bb8\u80e1\u4e71\u4f24 \u4eba\u3002\u201d\u90a3\u4e24\u53ea\u72d2\u72d2\u5949\u4e86\u4e3b\u4eba\u4e4b\u547d\uff0c\u7bad\u4e00\u822c\u7684\u7a9c\u51fa\u77f3\u6d1e\u53bb\u4e86\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u8fd9\u4e24\u53ea\u72d2\u72d2\u55c5\u89c9\u542c\u89c9\u90fd\u975e\u5e38\u7075\u654f\uff0c\u82e5\u6709\u751f\u4eba\u7684\u6c14\u5473\uff0c\u5b83\u5728\u516d\u4e03\u91cc\u5916\uff0c \u5c31\u53ef\u4ee5\u95fb\u5f97\u51fa\u6765\u3002\u201d\u6b66\u7384\u971c\u4e0d\u80dc\u8be7\u5f02\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u91cc\u51b0\u5cf0\u63d2\u4e91\uff0c\u975e\u662f\u6b66\u529f\u9ad8\u5f3a\u4e4b\u58eb\uff0c \u4e0d\u6613\u4e0a\u6765\uff0c\u8fd9\u6765\u7684\u53c8\u662f\u8c01\u5462\uff1f\u662f\u90a3\u9752\u8863\u7537\u5b50\u53bb\u800c\u590d\u8fd4\uff0c\u8fd8\u662f\u674e\u9038\u6765\u4e86\u5462\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a \u201c\u8fd9\u4e24\u53ea\u72d2\u72d2\u7ecf\u8fc7\u6211\u59d1\u59d1\u7684\u591a\u5e74\u8c03\u6559\uff0c\u7eb5\u8bb8\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u4e5f\u672a\u5fc5\u80dc\u5f97\u8fc7\u5b83\u4eec\uff0c \u5e08\u59b9\u53ef\u4ee5\u653e\u5fc3\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8\u7ee7\u7eed\u521a\u624d\u7684\u8bdd\u9898\u8bf4\u9053\uff1a\u201c\u5e78\u800c\u6709\u90a3\u4e2a\u91d1\u9488\u56fd\u624b\u590f\u4faf\u575a\uff0c \u8981\u4e0d\u7136\u4f60\u5c31\u770b\u4e0d\u5230\u5e08\u7236\u7684\u8089\u8eab\u4e86\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u4fdd\u6301\u8089\u8eab\u4e0d\u574f \u7684\u836f\u6750\uff0c\u662f\u590f\u4faf\u575a\u5728\u4e8c\u5341\u5e74\u524d\u9001\u7ed9\u6211\u59d1\u59d1\u7684\u3002\u90a3\u65f6\u59d1\u59d1\u8fd8\u6ca1\u6709\u524a\u53d1\u4e3a\u5c3c\uff0c\u590f\u4faf\u575a\u9001\u7ed9\u5979 \u4e00\u74f6\u9999\u6599\uff0c\u8bf4\u662f\u53ef\u4ee5\u4fdd\u6301\u989c\u5bb9\u4e0d\u8001\uff0c\u6211\u59d1\u59d1\u751f\u524d\u6ca1\u6709\u7528\u5b83\uff0c\u60f3\u4e0d\u5230\u6b7b\u540e\u5374\u7528\u5f97\u7740\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1b\u201c\u8fd9\u4e8b\u60c5\u6211\u4e5f\u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\u3002\u5e08\u7236\u5f53\u65f6\u7b11\u5230\uff0c\u6211\u662f\u51fa\u5bb6\u4e4b \u4eba\uff0c\u8fd9\u79cd\u836f\u6599\u6211\u7528\u4e0d\u7740\uff0c\u4f60\u4eec\u5e74\u8f7b\u7684\u59d1\u5a18\u5012\u662f\u5408\u7528\u3002\u6211\uff0c\u6211\u6ca1\u6709\u8981\u5979\u7684\u3002\u201d\u539f\u6765\u5f53\u65f6\u6b66 \u7384\u971c\u8bf4\u7684\u8bdd\u662f\uff1a\u201c\u54b1\u4eec\u53c8\u4e0d\u662f\u5bfb\u5e38\u7684\u5973\u5b50\uff0c\u4f55\u987b\u4ee5\u8272\u60a6\u4eba\u3002\u201d\u5979\u5e08\u7236\u5f88\u8d5e\u8d4f\u5979\u7684\u89c1\u89e3\u9ad8 \u8d85\uff0c\u56e0\u4e4b\u63d0\u8fc7\u4e4b\u540e\u4e5f\u5c31\u7b97\u4e86\u3002\u8fd9\u4e24\u53e5\u8bdd\uff0c\u6b66\u7384\u971c\u4e0d\u65b9\u4fbf\u5411\u5e08\u5144\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u6b66\u7384\u971c\u60f3\u9053\uff1a\u201c\u5982\u4eca\u60f3\u6765\uff0c\u5e08\u7236\u90a3\u65f6\u5df2\u662f\u5fc3\u5982\u7a3f\u6728\uff0c\u6240\u4ee5\u6ca1\u6709\u7528\u4ed6\u7684\u836f\u3002\u4e0d\u8fc7\uff0c\u590f \u4faf\u575a\u7684\u8fd9\u7247\u6df1\u60c5\uff0c\u4e5f\u7740\u5b9e\u4ee4\u4eba\u611f\u52a8\u3002\u201d\u5979\u5bf9\u5e08\u7236\u4e0e\u590f\u4faf\u575a\u7684\u4ea4\u8c0a\uff0c\u4ee5\u524d\u4e5f\u7565\u77e5\u4e00\u4e8c\uff0c\u6240 \u4ee5\u5728\u516b\u5e74\u4e4b\u524d\uff0c\u624d\u6709\u9001\u674e\u9038\u5230\u590f\u5019\u575a\u95e8\u4e0b\u6c42\u533b\u7684\u4e8b\u3002\u5982\u4eca\u770b\u4e86\u5e08\u7236\u7684\u8bd7\u96c6\uff0c\u5176\u4e2d\u6709\u51e0\u9996 \u4fbf\u662f\u63d0\u5230\u590f\u4faf\u575a\u7684\uff0c\u53c8\u542c\u4e86\u5e08\u5144\u7684\u8fd9\u4e00\u756a\u8bf4\u8bdd\uff0c\u624d\u77e5\u9053\u590f\u5019\u575a\u7684\u4e00\u7247\u6df1\u60c5\uff0c\u8fd8\u8d85\u51fa\u5979\u60f3 \u50cf\u4e4b\u5916\u3002\u60f3\u81f3\u6b64\u5904\uff0c\u518d\u60f3\u8d77\u674e\u9038\uff0c\u5fc3\u4e2d\u6709\u611f\uff0c\u4e0d\u89c9\u832b\u7136\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u4e24\u53ea\u72d2\u72d2\u8fd8\u672a\u89c1\u56de\u6765\uff0c\u88f4\u53d4\u5ea6\u6e10\u6e10\u73b0\u51fa\u5fe7\u8651\u4e4b\u8272\uff0c\u95ee\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60 \u521a\u624d\u78b0\u89c1\u7684\u90a3\u4e24\u4e2a\u654c\u4eba\u662f\u8c01\uff1f\u201d\u6b66\u7384\u971c\u5c06\u90a3\u624b\u4f7f\u836f\u9504\u7684\u9752\u8863\u52c7\u4e8e\u5f62\u8c8c\u63cf\u753b\u4e00\u756a\uff1b\u88f4\u53d4\u5ea6 \u5fae\u6709\u8be7\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u706d\u5ea6\u795e\u541b\uff0c\u8fd8\u6709\u4e00\u4e2a\u5462\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u53e6\u4e00\u4e2a\u662f\u6211\u8ba4\u8bc6\u7684\uff0c \u5979\u662f\u5929\u6076\u9053\u4eba\u7684\u5973\u5f1f\u5b50\uff0c\u5728\u6c5f\u6e56\u4e0a\u6709\u4e2a\u532a\u53f7\u53eb\u505a\u6bd2\u89c2\u97f3\u3002\u201d\u88f4\u53d4\u5ea6\u5931\u58f0\u53eb\u9053\uff1a\u201c\u600e\u4e48\u5979 \u4e5f\u6765\u4e86\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6bd2\u89c2\u97f3\u7684\u6b66\u529f\u5c1a\u5728\u4f60\u6211\u4e4b\u4e0b\uff0c\u600e\u7684\u4f60\u5374\u597d\u50cf\u66f4\u770b\u91cd\u5979\uff1f\u201d \u3000\u3000\u6590\u53d4\u5ea6\u795e\u8272\u6709\u70b9\u4e0d\u5b89\uff0c\u672a\u66fe\u56de\u7b54\uff0c\u5ffd\u542c\u5f97\u90a3\u4e24\u53ea\u72d2\u72d2\u7684\u54c0\u9e23\u4e4b\u58f0\uff0c\u8f6c\u77ac\u95f4\u5c31\u8dd1\u5230\u6d1e \u53e3\u3002\u88f4\u53d4\u5ea6\u773c\u5149\u4e00\u77a5\uff0c\u4e0d\u7981\u60ca\u53eb\u5931\u58f0\uff0c\u539f\u6765\u90a3\u4e24\u53ea\u72d2\u72d2\u7adf\u7136\u53d7\u4e86\u91cd\u4f24\uff0c\u6591\u8840\u4e00\u70b9\u70b9\u6ef4\u4e0b\u3002 \u3000\u3000\u8fd9\u4e24\u53ea\u72d2\u72d2\u4e43\u662f\u5929\u751f\u5f02\u79cd\uff0c\u94dc\u76ae\u94c1\u9aa8\uff0c\u5468\u8eab\u5200\u67aa\u4e0d\u5165\uff0c\u521a\u624d\u706d\u5ea6\u795e\u541b\u4e5f\u4e0d\u80fd\u4ee4\u5b83\u4eec \u53d7\u4f24\uff0c\u53ef\u77e5\u6765\u4eba\u7684\u6b66\u529f\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u6700\u5c11\u4e5f\u5728\u706d\u5ea6\u795e\u541b\u4e4b\u4e0a\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u5c06\u8fd9\u4e24\u53ea\u72d2\u72d2\u5524\u6765\uff0c\u5bdf\u89c6\u4e86\u5b83\u4eec\u8eab\u4e0a\u7684\u4f24\u72b6\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u800c\u517d\u7c7b\u7684\u7ecf\u8109\u7a74\u9053 \u548c\u4eba\u7c7b\u4e0d\u540c\uff0c\u8981\u4e0d\u7136\u90a3\u5267\u6bd2\u5faa\u7740\u7a74\u9053\u653b\u5fc3\uff0c\u8fd9\u4e24\u53ea\u72d2\u72d2\u53ea\u6015\u65e9\u5df2\u6bd9\u5728\u90a3\u4eba\u638c\u4e0b\u3002\u201d\u6b66\u7384 \u971c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u6765\u7684\u662f\u5929\u6076\u9053\u4eba\uff1f\u201d\u53ea\u89c1\u88f4\u53d4\u5ea6\u638f\u51fa\u4e00\u4e2a\u94f6\u74f6\uff0c\u74f6\u4e2d\u76db\u7740\u78a7 \u7eff\u8272\u7684\u4e38\u836f\uff0c\u88f4\u53d4\u5ea6\u56bc\u788e\u4e86\u4e24\u7c92\u4e38\u836f\uff0c\u7ed9\u90a3\u4e24\u53ea\u72d2\u72d2\u6577\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5bb3\u6015\u7684\u4e0d\u662f\u6bd2\u89c2 \u97f3\uff0c\u800c\u662f\u6bd2\u89c2\u97f3\u7684\u5e08\u7236\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u5929\u6076\u9053\u4eba\u7684\u6b66\u529f\uff0c\u786e\u662f\u5728\u4f60\u6211\u4e4b\u4e0a\uff0c\u4e0d\u8fc7\u54b1\u4eec \u4e24\u4eba\u8054\u624b\u6597\u4ed6\uff0c\u4e5f\u4e0d\u89c1\u5f97\u5c31\u8f93\u7ed9\u4ed6\u4e86\u3002\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u4f60\u6597\u8fc7\u5929\u6076\u9053\u4eba\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a \u201c\u516b\u5e74\u4e4b\u524d\uff0c\u6211\u5728\u7ef7\u5c71\u4e4b\u4e0a\uff0c\u4e0e\u5927\u5185\u4e09\u5927\u9ad8\u624b\u5408\u529b\u6597\u4ed6\uff0c\u6253\u6210\u5e73\u624b\u3002\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u4f60 \u6709\u6240\u4e0d\u77e5\uff0c\u5929\u6076\u9053\u4eba\u8fd9\u51e0\u5e74\u6765\u82e6\u7ec3\u6bd2\u638c\uff0c\u542c\u8bf4\u4ed6\u51c6\u5907\u7528\u5341\u5e74\u7684\u529f\u592b\uff0c\u5982\u4eca\u5f00\u5173\u590d\u51fa\uff0c\u60f3 \u5fc5\u662f\u63d0\u524d\u7ec3\u6210\u4e86\u3002\u800c\u4e14\u6211\u6015\u6765\u7684\u8fd8\u4e0d\u53ea\u5929\u6076\u9053\u4eba\uff0c\u4f60\u542c\u8fc7\u57df\u5916\u4e09\u51f6\u7684\u540d\u5b57\u5417\uff1f\u201d\u6b66\u7384\u971c \u9053\uff1a\u201c\u6ca1\u6709\u542c\u8fc7\u3002\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u5929\u6076\u9053\u4eba\u3001\u706d\u5ea6\u795e\u541b\u548c\u53e6\u5916\u4e00\u4e2a\u540d\u53eb\u767e\u5fe7\u4e0a\u4eba\u7684\u548c\u5c1a\uff0c \u5408\u79f0\u57df\u5916\u4e09\u51f6\uff0c\u9664\u4e86\u767e\u5fe7\u4e0a\u4eba\u4e4b\u5916\uff0c\u5929\u6076\u9053\u4eba\u548c\u706d\u5ea6\u795e\u541b\u90fd\u66fe\u8d25\u5728\u6211\u7684\u59d1\u59d1\u5251\u4e0b\uff0c\u636e\u59d1 \u59d1\u8bf4\uff0c\u4e09\u51f6\u4e4b\u4e2d\u4ee5\u767e\u5fe7\u4e0a\u4eba\u7684\u6b66\u529f\u6700\u4e3a\u602a\u5f02\uff0c\u4e5f\u6700\u4e3a\u5389\u5bb3\uff0c\u6211\u59d1\u59d1\u9041\u8ff9\u5929\u5c71\uff0c\u9664\u4e86\u8981\u7ee5 \u8fd1\u5c09\u8fdf\u70af\u4e4b\u5916\uff0c\u53e6\u5916\u4e00\u4e2a\u539f\u56e0\uff0c\u5c31\u662f\u9632\u5907\u57df\u5916\u4e09\u51f6\u6765\u627e\u5979\u5bfb\u4ec7\u3002\u5982\u4eca\u6bd2\u89c2\u97f3\u968f\u7740\u706d\u5ea6\u795e \u541b\u51fa\u73b0\uff0c\u53ea\u6015\u57df\u5916\u4e09\u51f6\u4f1a\u8054\u8882\u800c\u6765\uff01\u201d \u3000\u3000\u521a\u521a\u8bf4\u5230\u8fd9\u91cc\uff0c\u4fbf\u542c\u5f97\u4e00\u58f0\u602a\u5578\u8fdc\u8fdc\u4f20\u6765\uff0c\u521d\u542c\u4e4b\u65f6\uff0c\u597d\u50cf\u8fd8\u9694\u7740\u4e00\u5ea7\u5c71\u5934\uff0c\u8f6c\u77ac \u4e4b\u95f4\uff0c\u56de\u58f0\u9707\u8361\uff0c\u4fbf\u4f3c\u5230\u4e86\u95e8\u5916\uff0c\u6b66\u7384\u971c\u4e0e\u88f4\u53d4\u5ea6\u4e0d\u7ea6\u800c\u540c\uff0c\u8dc3\u51fa\u77f3\u7a9f\uff0c\u88f4\u53d4\u5ea6\u5ffd\u9053\uff1a \u201c\u4e0d\u597d\uff0c\u4e0d\u597d\uff0c\u6765\u7684\u679c\u7136\u4e0d\u6b62\u4e00\u4eba\uff0c\u5e08\u59b9\uff0c\u4f60\u56de\u53bb\u4fdd\u62a4\u5e08\u7236\u7684\u6cd5\u4f53\uff0c\u82e5\u662f\u6211\u62b5\u654c\u4e0d\u4f4f\uff0c \u4f60\u5c31\u62a4\u5f85\u5e08\u7236\u7684\u6cd5\u8eab\uff0c\u4ece\u540e\u6d1e\u9003\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u5c1a\u672a\u53d1\u73b0\u654c\u8e2a\uff0c\u7a0d\u4e00\u8e0c\u8e87\uff0c\u53ea\u89c1\u96ea\u5730\u4e0a\u4e00\u56e2\u9ed1\u5f71\uff0c\u4fe8\u82e5\u661f\u661f\u98de\u9a76\uff0c\u8f6c\u77ac\u95f4\u5c31 \u73b0\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6b63\u662f\u5929\u6076\u9053\u4eba\uff0c\u4f46\u5374\u4e5f\u53ea\u662f\u5929\u6076\u9053\u4eba\uff0c\u6b66\u7384\u971c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u662f\u5e08\u5144\u542c\u9519 \u4e86\uff0c\u5929\u6076\u9053\u4eba\u53ef\u5e76\u6ca1\u6709\u5e2e\u624b\u5475\uff01\u201d \u3000\u3000\u5929\u6076\u9053\u4eba\u6765\u5230\u4e86\u6590\u53d4\u5ea6\u8ddf\u524d\uff0c\u62c2\u5c18\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4f18\u4e91\u8001\u5c3c\u7684\u5f92\u5f1f\u4e48\uff0c\u5feb\u53bb\u7980 \u544a\u4f60\u7684\u5e08\u7236\uff0c\u8bf4\u662f\u4ed6\u7684\u8001\u670b\u53cb\u627e\u5979\u6765\u4e86\u3002\u201d\u8bf4\u7f62\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4e0d\u987b\u4f60\u53bb\u7980\u62a5\uff0c\u5979\u4e5f \u5e94\u8be5\u77e5\u9053\u662f\u6211\u6765\u4e86\u3002\u201d\u63a5\u8fde\u53c8\u602a\u5578\u4e09\u58f0\uff0c\u4e00\u58f0\u9ad8\u4f3c\u4e00\u58f0\uff0c\u9707\u5f97\u6b66\u7384\u971c\u4e5f\u89c9\u5f97\u6709\u70b9\u5fc3\u65cc\u6447 \u6447\uff0c\u597d\u50cf\u5c31\u8981\u795e\u98de\u9b44\u6563\u7684\u6837\u5b50\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5996\u9053\u7684\u529f\u529b\u679c\u7136\u53c8\u9ad8\u4e86\u8bb8\u591a\u4e86\u3002\u201d\u770b\u88f4\u53d4\u5ea6 \u65f6\uff0c\u53ea\u89c1\u4ed6\u6cf0\u7136\u81ea\u82e5\uff0c\u53cd\u800c\u597d\u50cf\u6bd4\u521a\u624d\u8f7b\u677e\u4e86\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u4f60\u8fd9\u6076\u9053\u9b3c\u568e\u4f5c\u751a\uff1f\u6740\u9e21\u7109\u7528\u725b\u5200\uff0c\u770b\u5251\uff01\u201d\u500f\u7684\u5c31\u662f\u4e00\u62db\u201c\u51b0\u5ddd\u5012 \u6cfb\u201d\uff0c\u5251\u5149\u75be\u5c55\uff0c\u5411\u5929\u6076\u9053\u4eba\u75be\u5377\u800c\u6765\u3002 \u3000\u3000\u6b66\u7384\u971c\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u604d\u7136\u5927\u609f\uff0c\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u5e08\u5144\u6545\u610f\u5c06\u8bdd\u8bf4\u5f97\u542b\u7cca\uff0c\u4e0d\u8ba9\u4ed6 \u77e5\u9053\u5e08\u7236\u5df2\u7ecf\u901d\u4e16\uff0c\u597d\u53eb\u4ed6\u6709\u6240\u987e\u5fcc\u3002\u201d \u3000\u3000\u88f4\u53d4\u5ea6\u8fd9\u4e00\u62db\u7cbe\u5999\u975e\u5e38\uff0c\u4f46\u89c1\u5251\u5149\u95ea\u95ea\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u7aef\u7684\u6709\u5982\u7e41\u661f\u6b92\u843d\uff0c\u96ea\u82b1\u7eb7\u98de\uff0c \u5929\u6076\u9053\u4eba\u62c2\u5c18\u4e00\u5377\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u6454\u950b\u4e4b\u58f0\uff0c\u597d\u50cf\u51e0\u5341\u53ea\u624b\u6307\u540c\u65f6\u62e8\u52a8\u7434\u5f26\u4e00\u822c\uff0c\u975e\u5e38\u597d \u542c\uff0c\u968f\u5373\u98de\u8d77\u4e86\u4e00\u7bf7\u5c18\u5c3e\uff0c\u4e71\u8349\u822c\u98de\u821e\u7a7a\u4e2d\u3002\u4e24\u4eba\u5fc3\u4e2d\u90fd\u662f\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765\u5929\u6076\u9053\u4eba\u6697 \u8fd0\u771f\u529f\uff0c\u4f5b\u5c18\u6709\u5982\u5343\u4e1d\u4e07\u7f15\uff0c\u7f69\u5c06\u4e0b\u6765\uff0c\u6bcf\u4e00\u6839\u5c18\u5c3e\u90fd\u786c\u4f3c\u94f6\u9488\uff0c\u6545\u6b64\u4e0e\u5251\u950b\u76f8\u89e6\uff0c\u53d1 \u51fa\u91d1\u5c5e\u822c\u7684\u58f0\u54cd\u3002\u4ed6\u672c\u610f\u8981\u7528\u201c\u62c2\u5c18\u523a\u7a74\u201d\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u4e00\u4e3e\u5c06\u6590\u53d4\u5ea6\u5236\u670d\uff0c\u5c82\u77e5\u88f4\u53d4 \u5ea6\u7684\u8fd9\u4e00\u62db\u5251\u6cd5\uff0c\u795e\u5999\u65e0\u65b9\uff0c\u653b\u5b88\u517c\u5907\uff0c\u5251\u5149\u4e00\u5c55\uff0c\u7acb\u5373\u5c06\u5168\u8eab\u62a4\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u5929\u6076\u9053 \u4eba\u90a3\u4e07\u7f15\u5343\u4e1d\u7684\u62c2\u5c18\u7adf\u7136\u65e0\u9699\u53ef\u5165\uff0c\u53cd\u800c\u88ab\u4ed6\u524a\u65ad\u4e86\u5341\u51e0\u6839\u5c18\u5c3e\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u7684\u5c18\u5c3e\u4e43\u662f\u4e4c\u91d1\u70bc\u6210\u7684\u7384\u4e1d\uff0c\u88f4\u53d4\u5ea6\u4f7f\u7684\u4e0d\u8fc7\u662f\u4e00\u67c4\u666e\u901a\u7684\u9752\u94dc\u5251\uff0c\u5c45\u7136 \u80fd\u5c06\u5b83\u524a\u65ad\uff0c\u4e0d\u4e9a\u4e8e\u524a\u91d1\u622a\u94c1\u3001\u5439\u6bdb\u7acb\u65ad\u7684\u5b9d\u5251\uff0c\u8fd9\u4efd\u5185\u5bb6\u529f\u529b\uff0c\u5b9e\u662f\u4e0d\u5728\u5929\u6076\u9053\u4eba\u4e4b \u4e0b\u3002 \u3000\u3000\u6b66\u7384\u971c\u89c1\u5e08\u5144\u7684\u5251\u672f\u5982\u6b64\u795e\u5947\uff0c\u5fc3\u795e\u7a0d\u5b9a\u3002\u8f6c\u773c\u95f4\uff0c\u5929\u6076\u9053\u4eba\u4e0e\u88f4\u53d4\u5ea6\u5df2\u62c6\u4e86\u4e8c\u4e09 \u5341\u62db\uff0c\u88f4\u53d4\u5ea6\u4e00\u7740\u5f97\u5148\uff0c\u7d27\u63e1\u5148\u624b\uff0c\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u66b4\u98ce\u96e8\u75be\u653b\u800c\u4e0a\uff0c\u5929\u6076\u9053\u4eba\u4ed7\u7740\u4e00 \u67c4\u4f5b\u5c18\uff0c\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u8fde\u8fde\u540e\u9000\u3002\u6b66\u7384\u971c\u5927\u559c\uff0c\u6b63\u62df\u4e0a\u524d\u52a9\u653b\uff0c\u5ffd\u542c\u5f97\u5929\u6076\u9053\u4eba\u4e00\u58f0 \u602a\u5578\uff0c\u4f5b\u5c18\u4e00\u5c55\uff0c\u5316\u5f00\u4e86\u88f4\u53d4\u5ea6\u7684\u5251\u62db\uff0c\u500f\u7684\u5c31\u662f\u4e00\u638c\u6309\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u6309\u4e0b\uff0c\u7acb\u5373\u5377\u8d77\u4e00\u80a1\u8165\u98ce\uff0c\u4e2d\u4eba\u6b32\u5455\uff0c\u88f4\u53d4\u5ea6\u8eab\u8eaf\u4e00\u4fa7\uff0c\u56de\u5251\u8981\u524a\u4ed6\u7684\u624b\u638c\uff0c \u5929\u6076\u9053\u4eba\u7684\u638c\u52bf\u98d8\u5ffd\u4e4b\u6781\uff0c\u88f4\u53d4\u5ea6\u4e00\u5251\u524a\u7a7a\uff0c\u4ed6\u7684\u7b2c\u4e8c\u638c\u53c8\u62c6\u4e86\u8fc7\u6765\uff0c\u638c\u5fc3\u9ed1\u5982\u6d53\u58a8\uff0c \u88f4\u53d4\u5ea6\u4e0d\u7531\u5f97\u518d\u9000\u4e86\u4e00\u6b65\uff0c\u5c31\u8fd9\u6837\u7684\u7f13\u4e86\u4e00\u7f13\uff0c\u7acb\u5373\u88ab\u5929\u6076\u9053\u4eba\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u6539\u5b88\u4e3a\u653b\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u7684\u5251\u6cd5\u867d\u7136\u7cbe\u5999\uff0c\u4f46\u4ed6\u8981\u8fd0\u6c14\u9632\u5fa1\u5929\u6076\u9053\u4eba\u6bd2\u638c\u6240\u5377\u8d77\u7684\u90a3\u80a1\u8165\u98ce\uff0c\u4e00\u5fc3\u4e8c \u7528\uff0c\u4e0d\u514d\u76f8\u5f62\u89c1\u62d9\uff0c\u5929\u6076\u9053\u4eba\u4ee5\u62c2\u5c18\u7f20\u7740\u4ed6\u7684\u5229\u5251\uff0c\u638c\u52bf\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u88f4\u53d4\u5ea6 \u7ed9\u4ed6\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u7684\u6b65\u6cd5\u5251\u6cd5\u4ecd\u7136\u4e1d\u6beb\u4e0d\u4e71\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u5ffd\u7136\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f18\u4e91\u8001\u5c3c\u679c\u7136\u6b7b\u4e86\uff0c\u4f60\u8fd9\u4e2a\u5c0f\u8f88\u4e0d\u662f\u6211\u7684\u654c\u624b\uff0c\u518d \u6597\u4e0b\u53bb\uff0c\u662f\u81ea\u9001\u6b7b\u3002\u4f60\u5c06\u5979\u7684\u5251\u8c31\u4e0e\u5929\u5c71\u96ea\u83b2\u732e\u7ed9\u6211\uff0c\u6216\u8005\u6211\u53ef\u4ee5\u9976\u4f60\u4e00\u547d\u3002\u201d\u88f4\u53d4\u5ea6 \u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u770b\u51fa\u7834\u7efd\u3002\u5929\u6076\u9053\u4eba\u8d81\u7740\u4ed6\u60ca\u60f6\u4e4b\u9645\uff0c\u50ac\u7d27\u638c\u529b\uff0c\u53c8\u662f\u4e00\u8f6e\u6025\u653b\uff0c \u88f4\u53d4\u5ea6\u9669\u9669\u7ed9\u4ed6\u6253\u4e2d\uff0c\u5251\u6cd5\u7a0d\u7a0d\u51cc\u4e71\u3002 \u3000\u3000\u6b66\u7384\u971c\u5403\u4e86\u4e00\u60ca\uff0c\u968f\u624b\u5728\u5730\u4e0a\u6293\u8d77\u4e00\u628a\u77f3\u5b50\uff0c\u7528\u201c\u5218\u6d77\u6d12\u91d1\u94b1\u201d\u7684\u624b\u6cd5\u5411\u5929\u6076\u9053\u4eba \u6d12\u53bb\u3002\u6b66\u7384\u971c\u5df2\u7ec3\u5230\u4e86\u201c\u6458\u53f6\u98de\u82b1\uff0c\u4f24\u4eba\u7acb\u6b7b\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u8fd9\u4e00\u628a\u77f3\u5b50\u6d12\u51fa\uff0c\u5b9e\u4e0d\u4e9a \u4e8e\u6b66\u6797\u9ad8\u624b\u6240\u7528\u7684\u91d1\u94b1\u9556\u3001\u94c1\u83b2\u5b50\u4e4b\u7c7b\u7684\u91d1\u5c5e\u6697\u5668\uff0c\u53ef\u662f\u5929\u6076\u9053\u4eba\u53ea\u662f\u5c06\u62c2\u5c18\u4e00\u626b\uff0c\u4fbf \u5c06\u5979\u6253\u6765\u7684\u4e00\u628a\u788e\u77f3\uff0c\u5c3d\u6570\u4f5b\u5f00\u3002\u4e0d\u8fc7\uff0c\u8fd9\u6837\u7a0d\u7a0d\u68a2\u4e00\u7f13\uff0c\u88f4\u53d4\u5ea6\u4fbf\u5373\u6062\u590d\u4e86\u5e38\u6001\uff0c\u4e00 \u67c4\u9752\u94dc\u5251\u7eb5\u6a2a\u6325\u970d\uff0c\u53c8\u628a\u95e8\u6237\u5c01\u5f97\u975e\u5e38\u4e25\u5bc6\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u5979\u7684\u5e08\u5144\u4e5f\u6b63\u5411\u5979\u671b\u6765\uff0c\u793a\u610f\u53eb\u5979\u56de\u53bb\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u6b66\u7384\u971c \u4e5f\u542c\u51fa\u4e86\u8fdc\u5904\u654c\u4eba\u7684\u58f0\u606f\uff0c\u5929\u6076\u9053\u4eba\u679c\u7136\u8fd8\u6709\u5e2e\u624b\u540c\u6765\uff0c\u6b66\u7384\u971c\u60f3\u9053\uff1a\u201c\u88f4\u5e08\u5144\u5927\u7ea6\u8fd8 \u53ef\u652f\u6301\u4e00\u4f1a\uff0c\u51ed\u7740\u4ed6\u8fd9\u624b\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u7eb5\u7136\u843d\u8d25\uff0c\u5927\u7ea6\u8fd8\u53ef\u4ee5\u9003\u8131\uff0c\u5e08\u7236\u7684\u6cd5\u8eab\u82e5\u7136\u7ed9 \u4eba\u6bc1\u574f\uff0c\u8fd9\u7f6a\u8fc7\u53ef\u662f\u4e0d\u5c0f\u3002\u201d\u6743\u8861\u8f7b\u91cd\uff0c\u53ea\u597d\u820d\u4e86\u5e08\u5144\uff0c\u56de\u8f6c\u77f3\u7a9f\uff0c\u770b\u770b\u60c5\u5f62\uff0c\u518d\u4f5c\u8bba \u5904\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u6325\u638c\u72c2\u653b\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u53c8\u5c06\u88f4\u53d4\u5ea6\u7684\u5251\u6cd5\u6253\u4e71\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u706d\u5ea6\u795e\u541b\uff0c \u6211\u8bf4\u4f18\u4e91\u8001\u5c3c\u5df2\u6b7b\uff0c\u4f60\u4e0d\u76f8\u4fe1\uff0c\u73b0\u5728\u53ef\u4ee5\u76f8\u4fe1\u4e86\u5427\u3002\u8fd8\u4e0d\u5feb\u6765\u6361\u4fbf\u5b9c\u53bb\uff01\u201d\u8bdd\u58f0\u672a\u505c\uff0c \u5c71\u62d7\u8f6c\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u679c\u7136\u662f\u706d\u5ea6\u795e\u541b\u3002 \u3000\u3000\u539f\u6765\u5929\u6076\u9053\u4eba\u4e43\u662f\u4e3a\u4e86\u8bbf\u67e5\u4ed6\u7684\u5973\u5f1f\u5b50\u4e0b\u843d\uff0c\u6bd2\u638c\u529f\u592b\u4e00\u7ec3\u6210\u529f\uff0c\u4fbf\u5373\u8ffd\u8e2a\u800c\u6765\u3002 \u4ed6\u5728\u5929\u5c71\u7684\u9a86\u9a7c\u5cf0\u4e0b\uff0c\u78b0\u5230\u4e86\u706d\u5ea6\u795e\u541b\u4e0e\u6bd2\u89c2\u97f3\u3002\u706d\u5ea6\u795e\u541b\u5927\u662f\u5c34\u5c2c\uff0c\u5929\u6076\u9053\u4eba\u672c\u6b32\u8981 \u5411\u706d\u5ea6\u795e\u541b\u5927\u5174\u95ee\u7f6a\u7684\uff0c\u89c1\u6bd2\u89c2\u97f3\u53d7\u72d2\u72d2\u6293\u4f24\uff0c\u800c\u706d\u5ea6\u795e\u541b\u53c8\u8d25\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u4fbf\u5c06\u95ee\u7f6a \u4e4b\u4e8b\u7f13\u63d0\uff0c\u5148\u95ee\u4ed6\u7684\u7ecf\u8fc7\u3002\u706d\u5ea6\u795e\u541b\u8bf4\u662f\u78b0\u5230\u4e86\u6b66\u7384\u971c\uff0c\u6000\u7591\u5979\u4fbf\u662f\u4f18\u4e91\u8001\u5c3c\u7684\u5f92\u5f1f\uff0c \u5e76\u5c06\u90a3\u4e24\u53ea\u72d2\u72d2\u52a9\u9635\u7684\u60c5\u5f62\u5bf9\u5929\u6076\u9053\u4eba\u8bf4\u4e86\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u4ee5\u524d\u66fe\u89c1\u8fc7\u4f18\u4e91\u8001\u5c3c\u8fd9\u4e24\u53ea\u72d2\u72d2\uff0c\u95fb\u8a00\u53c8\u60ca\u53c8\u559c\uff0c\u539f\u6765\u4ed6\u66fe\u542c\u5f97\u4f20\u95fb\uff0c\u8bf4 \u662f\u4f18\u4e91\u8001\u5c3c\u5df2\u6b7b\uff0c\u4e0d\u8fc7\u672a\u7ecf\u8bc1\u5b9e\uff0c\u7ec8\u662f\u534a\u4fe1\u534a\u7591\u3002\u5982\u4eca\u542c\u8bf4\u8fd9\u4e24\u53ea\u72d2\u72d2\u5728\u5c71\u4e0a\u51fa\u73b0\uff0c\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u53ea\u72d2\u72d2\u4e43\u662f\u8ddf\u968f\u4f18\u4e91\u8001\u5c3c\u7684\u4e24\u53ea\u795e\u517d\uff0c\u65e2\u7136\u5728\u6b64\u51fa\u73b0\uff0c\u4f18\u4e91\u8001\u5c3c\u4e5f\u5fc5\u7136 \u4f4f\u5728\u6b64\u95f4\uff0c\u662f\u6b7b\u662f\u751f\uff0c\u6b64\u8ff9\u5f53\u53ef\u63ed\u7834\u4e86\u3002\u201d\u4ed6\u548c\u706d\u5ea6\u795e\u541b\u90fd\u66fe\u8d25\u5728\u4f18\u4e91\u8001\u5c3c\u7684\u5251\u4e0b\uff0c\u5bf9 \u5979\u751a\u4e3a\u5fcc\u60ee\uff0c\u5929\u6076\u9053\u4eba\u751f\u6015\u4f18\u4e91\u8001\u5c3c\u672a\u6b7b\uff0c\u81ea\u5df1\u72ec\u529b\u96be\u652f\uff0c\u4fbf\u9080\u706d\u5ea6\u795e\u541b\u540c\u53bb\u63a2\u4e2a\u7a76\u7adf\u3002 \u597d\u5728\u6bd2\u89c2\u97f3\u53d7\u4f24\u4e0d\u91cd\uff0c\u4fbf\u7559\u4e0b\u5979\u5728\u5929\u5c71\u811a\u7597\u4f24\u3002\u4e0d\u4e45\uff0c\u90a3\u4e24\u53ea\u72d2\u72d2\u53c8\u6765\uff0c\u88ab\u5929\u6076\u9053\u4eba\u7528 \u6bd2\u638c\u5c06\u5b83\u4eec\u4f24\u4e86\u3002 \u3000\u3000\u706d\u5ea6\u795e\u541b\u7ec8\u662f\u56e0\u4e3a\u60e7\u6015\u8fc7\u751a\uff0c\u5230\u4e86\u5929\u6c60\uff0c\u7adf\u4e0d\u6562\u524d\u8fdb\uff0c\u85c9\u53e3\u8bf4\u662f\u8981\u6697\u4e2d\u76f8\u52a9\u8f83\u5999\uff0c \u5148\u8eb2\u8d77\u6765\uff0c\u5f85\u770b\u5f97\u5206\u660e\u518d\u8bf4\uff0c\u5929\u6076\u9053\u4eba\u867d\u7136\u4e0d\u6ee1\uff0c\u4e5f\u53ea\u597d\u7531\u4ed6\u3002\u5f85\u81f3\u5929\u6076\u9053\u4eba\u4e0e\u88f4\u53d4\u5ea6 \u6fc0\u6218\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u88f4\u53d4\u5ea6\u5df2\u7ecf\u5371\u5728\u77ac\u606f\uff0c\u5374\u5c1a\u672a\u89c1\u4f18\u4e91\u8001\u5c3c\u9732\u9762\uff0c\u706d\u5ea6\u795e\u541b\u5fc3\u60f3\uff1a\u201c\u5929 \u6076\u9053\u4eba\u5c06\u5979\u7684\u4e24\u53ea\u72d2\u72d2\u6253\u4f24\uff0c\u5982\u4eca\u5979\u7684\u5f1f\u5b50\u53c8\u5df2\u4e0d\u654c\uff0c\u773c\u770b\u5c31\u8981\u4f24\u5728\u5929\u6076\u9053\u4eba\u7684\u638c\u4e0b\uff0c \u82e5\u662f\u4f18\u4e91\u8001\u5c3c\u8fd8\u5728\uff0c\u65ad\u65e0\u4e0d\u51fa\u6765\u4e4b\u7406\u3002\u201d\u8fd9\u65f6\u4ed6\u624d\u786e\u4fe1\u4f18\u4e91\u8001\u5c3c\u5df2\u6b7b\uff0c\u4e8e\u662f\u5927\u4e86\u80c6\u5b50\uff0c \u51fa\u6765\u52a9\u9635\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u89c1\u662f\u706d\u5ea6\u795e\u541b\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff0c\u60f3\u9053\uff1a\u201c\u4e24\u53ea\u72d2\u72d2\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u5e08\u59b9\u4e00\u4eba\uff0c \u5982\u4f55\u654c\u5f97\u4f4f\u8fd9\u4e2a\u9b54\u541b\uff1f\u4f46\u76fc\u5979\u80fd\u53ca\u65e9\u89c1\u673a\uff0c\u5feb\u4e9b\u4ece\u540e\u6d1e\u9003\u8d70\u3002\u201d\u9ad8\u624b\u6bd4\u6597\uff0c\u6700\u5fcc\u5206\u6563\u5fc3 \u795e\uff0c\u88f4\u53d4\u5ea6\u6302\u8651\u5e08\u59b9\u7684\u5b89\u5371\uff0c\u4ed6\u81ea\u5df1\u7684\u5f62\u52bf\u4fbf\u66f4\u52a0\u5371\u9669\u4e86\u3002\u5929\u6076\u9053\u4eba\u6bd2\u638c\u6240\u6fc0\u8361\u8d77\u7684\u90a3 \u80a1\u8165\u98ce\u8d8a\u6765\u8d8a\u70c8\uff0c\u88f4\u53d4\u5ea6\u6e10\u89c9\u5934\u6655\u76ee\u7729\uff0c\u5251\u6cd5\u66f4\u663e\u5f97\u51cc\u4e71\u65e0\u7ae0\u3002 \u3000\u3000\u706d\u5ea6\u795e\u541b\u8fd9\u65f6\u786e\u4fe1\u4f18\u4e91\u8001\u5c3c\u5df2\u6b7b\uff0c\u8dd1\u5230\u6d1e\u524d\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b66\u7384\u971c\uff0c\u4f60\u8eb2\u4e5f\u8eb2\u4e0d\u4e86\uff0c \u5feb\u51fa\u6765\u5411\u6211\u78d5\u5934\u5427\uff01\u201d\u4ed6\u4e5f\u662f\u50cf\u88f4\u53d4\u5ea6\u90a3\u6837\u7684\u60f3\u6cd5\uff1a\u4e24\u53ea\u72d2\u72d2\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u53ea\u5269\u6b66\u7384\u971c \u4e00\u4eba\uff0c\u8fd8\u4e0d\u662f\u624b\u5230\u62ff\u6765\uff1f \u3000\u3000\u6d1e\u5185\u9759\u5bc2\u65e0\u58f0\uff0c\u706d\u5ea6\u795e\u541b\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u51fa\u6765\uff0c\u6211\u53ea\u597d\u5c06\u4f60\u638f\u51fa\u6765\u4e86\u3002\u201d\u8de8\u8fdb\u77f3\u7a9f\uff0c \u5ffd\u7136\u597d\u4f3c\u9047\u5230\u4e86\u4ec0\u4e48\u602a\u5f02\u7684\u7269\u4e8b\u4e00\u822c\uff0c\u7b11\u58f0\u7a81\u7136\u4e2d\u65ad\uff0c\u5f20\u76ee\u7ed3\u820c\uff0c\u767b\u65f6\u5446\u4e86\u3002 \u3000\u3000\u4f60\u9053\u4ed6\u770b\u89c1\u4ec0\u4e48\uff1f\u539f\u6765\u4ed6\u770b\u89c1\u77f3\u6848\u4e0a\u4f18\u4e91\u8001\u5c3c\u7684\u8089\u8eab\u9057\u4f53\uff0c\u4ed6\u54ea\u91cc\u77e5\u9053\u8fd9\u662f\u590f\u4faf\u575a \u7684\u7075\u836f\u4e4b\u529f\uff0c\u970e\u773c\u95f4\u4e00\u89c1\u4f18\u4e91\u8001\u5c3c\u989c\u8272\u5982\u751f\uff0c\u4e24\u53ea\u773c\u775b\u534a\u5f00\u534a\u9616\uff0c\u5634\u5507\u5fae\u542f\uff0c\u4f3c\u662f\u6b63\u8981 \u5411\u4ed6\u8bf4\u8bdd\uff0c\u767b\u65f6\u5413\u5f97\u4ed6\u9b42\u98de\u9b44\u6563\uff0c\u5fc3\u4e2d\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u539f\u6765\u4f18\u4e91\u8001\u5c3c\u672a\u6b7b\uff0c\u6211\u4e0a\u4e86\u5929 \u6076\u9053\u4eba\u7684\u5f53\u4e86\u3002\u201d\u4ed6\u4ee5\u524d\u66fe\u88ab\u4f18\u4e91\u795e\u5c3c\u6253\u5f97\u91cd\u4f24\uff0c\u56de\u5c71\u518d\u7ec3\u5341\u5e74\uff0c\u624d\u6062\u590d\u5f97\u539f\u6765\u7684\u529f\u529b\uff0c \u4ed6\u672c\u6765\u662f\u4e0e\u5929\u6076\u9053\u4eba\u3001\u767e\u5fe7\u548c\u5c1a\u8fd9\u4e24\u5927\u9b54\u5934\u5e76\u9a7e\u9f50\u540d\u7684\uff0c\u7ecf\u8fc7\u4e86\u90a3\u4e00\u6b21\u91cd\u4f24\u4e4b\u540e\u5374\u843d\u5728 \u8fd9\u4e24\u5927\u9b54\u5934\u4e4b\u540e\u4e86\u3002\u5f53\u65f6\u4f18\u4e91\u8001\u5c3c\u5c06\u4ed6\u6253\u5f97\u91cd\u4f24\u5927\u8d25\u4e4b\u540e\uff0c\u5e76\u66fe\u5bf9\u4ed6\u8bf4\u8fc7\uff0c\u82e5\u662f\u518d\u78b0\u5230 \u4ed6\uff0c\u5c31\u8981\u5c06\u4ed6\u7435\u7436\u9aa8\u6311\u65ad\uff0c\u5e9f\u6389\u4ed6\u7684\u6b66\u529f\u3002\u6545\u6b64\u706d\u5ea6\u795e\u541b\u5bf9\u4f18\u4e91\u8001\u5c3c\u5b9e\u662f\u6068\u5230\u4e86\u6781\u70b9\uff0c \u8fd9\u65f6\u4e00\u89c1\u4f18\u4e91\u8001\u5c3c\u7684\u8089\u8eab\u9057\u4f53\uff0c\u5fc3\u5934\u5927\u9707\uff0c\u60ca\u6050\u4e4b\u4f59\uff0c\u54ea\u91cc\u80fd\u591f\u5206\u8fa8\u4f18\u4e91\u8001\u5c3c\u662f\u751f\u662f\u6b7b\uff1f \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u706d\u5ea6\u795e\u541b\u5931\u58f0\u60ca\u53eb\uff0c\u8f6c\u8eab\u6b32\u9003\u4e4b\u9645\uff0c\u6b66\u7384\u971c\u7a81\u7136\u4ece\u5e08\u7236\u6cd5\u8eab \u4e4b\u540e\u8dc3\u51fa\uff0c\u4e00\u5251\u98de\u6765\uff0c\u90a3\u4e24\u53ea\u72d2\u72d2\u4e5f\u7a81\u7136\u6251\u4e0a\uff0c\u4f46\u542c\u5f97\u201c\u5580\u54a7\u201d\u4e00\u58f0\uff0c\u706d\u5ea6\u795e\u541b\u7684\u4e24\u5757 \u80a9\u813e\u9aa8\u7ed9\u72d2\u72d2\u7684\u5229\u722a\u6293\u88ad\uff0c\u81c2\u5f2f\u7684\u201c\u66f2\u6c60\u7a74\u201d\u4e5f\u7ed9\u6b66\u7384\u971c\u4e00\u5251\u523a\u4e2d\uff0c\u4e00\u6761\u624b\u81c2\u767b\u65f6\u9ebb\u6728 \u4e0d\u7075\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u5e08\u7236\u4e0d\u5fc5\u4f60\u8001\u4eba\u5bb6\u4eb2\u81ea\u52a8\u624b\u5566\u3002\u201d\u63a5\u7740\u5b66\u5979\u5e08\u7236\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5f92\u513f\uff0c \u4f60\u66ff\u6211\u5c06\u4ed6\u7684\u6b66\u529f\u5e9f\u4e86\u3002\u201d\u6b66\u7384\u971c\u81ea\u5e7c\u8ffd\u968f\u5e08\u7236\uff0c\u58f0\u97f3\u53e3\u543b\uff0c\u5b66\u5f97\u975e\u5e38\u4e4b\u50cf\uff0c\u83ab\u8bf4\u706d\u5ea6 \u795e\u541b\u73b0\u5728\u5df2\u7ecf\u53d7\u4e86\u4f24\uff0c\u5373\u7b97\u672a\u66fe\u53d7\u4f24\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u6562\u8f6c\u8fc7\u5934\u6765\u4e0e\u6b66\u7384\u971c\u518d\u6218\uff0c\u5413\u5f97\u9b42\u98de\u9b44 \u6563\uff0c\u8fde\u6eda\u5e26\u8dcc\u7684\u7a9c\u51fa\u77f3\u7a9f\uff0c\u6ca1\u547d\u98de\u9003\u3002 \u3000\u3000\u6b66\u7384\u971c\u62b9\u4e86\u4e00\u5934\u51b7\u6c57\uff0c\u539f\u6765\u5979\u662f\u6548\u6cd5\u53e4\u4eba\u201c\u6b7b\u8bf8\u845b\u5413\u8d70\u751f\u4ef2\u8fbe\u201d\u7684\u6545\u667a\uff0c\u5c06\u706d\u5ea6\u795e \u541b\u8d76\u8dd1\u7684\u3002\u90a3\u4e24\u53ea\u72d2\u72d2\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u518d\u62a4\u4e3b\u4f24\u654c\uff0c\u8fd9\u65f6\u4e5f\u5012\u5728\u5730\u4e0a\u5598\u606f\u4e0d\u5df2\uff01\u6b66\u7384\u971c\u5b9a \u4e86\u5b9a\u795e\uff0c\u7acb\u5373\u53c8\u751f\u51fa\u4e00\u6761\u5999\u8ba1\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u6cb3\u6881\u8bc0\u522b\u75f4\u6210\u6068 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u5341\u4e5d\u56de\u3000\u6cb3\u6881\u8bc0\u522b\u75f4\u6210\u6068 \u3000\u3000\u6b64\u65f6\u88f4\u53d4\u5ea6\u4e0e\u5929\u6076\u9053\u4eba\u6597\u4e86\u5c06\u8fd1\u767e\u62db\uff0c\u90fd\u5df2\u7cbe\u75b2\u529b\u7aed\uff0c\u66f4\u52a0\u4e0a\u66ae\u638c\u8165\u98ce\u7684\u4fb5\u5bb3\uff0c\u5934 \u6655\u76ee\u7729\uff0c\u66f4\u662f\u96be\u4ee5\u652f\u6301\uff0c\u4f46\u60f3\u5230\u5728\u6b64\u91cd\u8981\u5173\u5934\uff0c\u80fd\u62d6\u5ef6\u5f97\u4e00\u523b\u4fbf\u662f\u4e00\u523b\uff0c\u5426\u5219\u81ea\u5df1\u82e5\u7136 \u88ab\u5929\u6076\u9053\u4eba\u51fb\u5012\uff0c\u4ed6\u4eec\u4e24\u5927\u9b54\u5934\u5408\u529b\u8ffd\u6355\u5e08\u59b9\uff0c\u5e08\u59b9\u53ea\u6015\u66f4\u96be\u9003\u8131\u3002\u88f4\u53d4\u5ea6\u601d\u5ff5\u53ca\u6b64\uff0c \u4fbf\u5f3a\u8fd0\u771f\u6c14\uff0c\u62fc\u6b7b\u652f\u6491\uff0c\u6539\u5b88\u4e3a\u653b\uff0c\u82e6\u82e6\u7f20\u6597\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u80dc\u5238\u5728\u64cd\uff0c\u5374\u662f\u4ece\u5bb9\u4e0d\u8feb\uff0c\u88f4\u53d4\u5ea6\u72c2\u653b\u4e0d\u901e\uff0c\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u5929\u6076\u9053\u4eba\u6ee1 \u6000\u6b22\u559c\uff0c\u6b63\u62df\u4e58\u9699\u800c\u5165\uff0c\u65bd\u5c55\u6740\u624b\uff0c\u5ffd\u542c\u5f97\u706d\u5ea6\u795e\u541b\u9a87\u53eb\u4e4b\u58f0\uff0c\u968f\u5373\u89c1\u5230\u4ed6\u5728\u6d1e\u4e2d\u5982\u98de \u8dd1\u51fa\uff0c\u770b\u60c5\u5f62\u7adf\u4f3c\u53d7\u4e86\u91cd\u4f24\uff0c\u5929\u6076\u9053\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u6b63\u5f85\u559d\u95ee\uff0c\u84e6\u7136\u95f4\u542c\u5f97\u4f18\u4e91\u8001\u5c3c\u7684\u58f0 \u97f3\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5929\u6076\u8d3c\u9053\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff0c\u6562\u8d81\u6211\u95ed\u5173\u7684\u65f6\u5019\uff0c\u5230\u8fd9\u91cc\u6765\u6b3a\u8d1f\u6211\u7684\u5f1f\u5b50 \u4e48\uff1f\u201d\u4f46\u89c1\u6d1e\u95e8\u5f00\u5904\uff0c\u6b66\u7384\u971c\u63a8\u7740\u4e00\u8f86\u72ec\u8f6e\u8f66\u8d70\u51fa\u6765\uff0c\u8f66\u4e2d\u76d8\u819d\u800c\u5750\u7684\uff0c\u53ef\u4e0d\u6b63\u662f\u4f18\u4e91 \u8001\u5c3c\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u9976\u662f\u5929\u6076\u9053\u4eba\u80c6\u5927\uff0c\u4e5f\u81ea\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u201c\u8fd9\u8001\u5c3c\u539f\u6765\u662f\u5728\u5750\u5173\u7ec3\u529f\u3002\u201d\u5fc3 \u5ff5\u672a\u5df2\uff0c\u88f4\u53d4\u5ea6\u84e6\u5730\u4e00\u58f0\u5927\u559d\uff0c\u638c\u5288\u5251\u622a\uff0c\u4e00\u62db\u201c\u661f\u6c49\u6d6e\u78cb\u201d\uff0c\u5251\u5c16\u98a4\u52a8\uff0c\u638c\u98ce\u8361\u5f00\u4e86 \u4ed6\u7684\u62c2\u5c18\uff0c\u5251\u5c16\u8fde\u523a\u4e86\u4ed6\u4e09\u5904\u7a74\u9053\uff01 \u3000\u3000\u5929\u6076\u9053\u4eba\u672c\u6765\u6bd4\u706d\u5ea6\u795e\u541b\u5fc3\u7ec6\u5f97\u591a\uff0c\u521a\u521a\u542c\u51fa\u58f0\u97f3\u6709\u70b9\u4e0d\u5bf9\uff0c\u60ca\u9b42\u672a\u5b9a\uff0c\u4fbf\u53d7\u4e86\u5251 \u4f24\uff0c\u6c14\u5f97\u4ed6\u4e03\u7a8d\u751f\u70df\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u6562\u65bd\u6697\u7b97\uff0c\u4f60\u4e5f\u4f11\u60f3\u6d3b\u547d\uff01\u201d\u500f\u7136\u8f6c\u8fc7\u8eab \u6765\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u52bf\u4f3c\u5954\u96f7\uff0c\u88f4\u53d4\u5ea6\u90a3\u4e00\u5251\u5df2\u662f\u5c3d\u4e86\u5168\u8eab\u6c14\u529b\uff0c\u5e78\u800c\u523a\u4e2d\uff0c\u5fc3\u60c5\u4e00\u677e\uff0c\u771f \u6c14\u6e32\u6cc4\uff0c\u8fd9\u4e00\u638c\u5982\u4f55\u8fd8\u95ea\u907f\u5f97\u5f00\uff1f\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u521a\u521a\u8dc3\u8d77\uff0c\u4fbf\u7ed9\u5929\u6076\u9053\u4eba\u4e00 \u638c\u51fb\u4e2d\u8170\u80ef\uff0c\u9707\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u8fd9\u65f6\u5df2\u770b\u51fa\u4e86\u4f18\u4e91\u8001\u5c3c\u5df2\u6b7b\uff0c\u4f9d\u4ed6\u7684\u5fc3\u610f\uff0c\u672c\u8981\u628a\u6b66\u7384\u971c\u4e5f\u6bd9\u4e8e\u638c\u4e0b\uff0c\u53ef \u662f\u4ed6\u88ab\u6590\u53d4\u5ea6\u523a\u4e2d\u4e86\u4ed6\u4e09\u5904\u7a74\u9053\uff0c\u867d\u7136\u6682\u65f6\u7528\u95ed\u7a74\u4e4b\u6cd5\uff0c\u51dd\u805a\u771f\u6c14\uff0c\u6253\u4e86\u88f4\u53d4\u5ea6\u4e00\u638c\uff0c \u4f46\u8fd9\u4e00\u638c\u6253\u51fa\u4e4b\u540e\uff0c\u4ed6\u7684\u771f\u6c14\u4ea6\u5df2\u6d88\u6563\uff0c\u4f46\u8033\u9e23\u5982\u96f7\uff0c\u76ee\u7729\u91d1\u661f\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u53ea\u5f97 \u518d\u5f3a\u63d0\u53e3\u6c14\uff0c\u75be\u5954\u4e0b\u5c71\uff0c\u8fd9\u65f6\u82e5\u7136\u6b66\u7384\u971c\u6562\u8ffd\u4e0a\u53bb\uff0c\u5929\u6076\u9053\u4eba\u5df2\u662f\u654c\u4e0d\u8fc7\u5979\uff0c\u5b9a\u8981\u88ab\u5979 \u6740\u6b7b\uff0c\u53ef\u662f\u6b66\u7384\u971c\u89c1\u4ed6\u4e2d\u5251\u4e4b\u540e\uff0c\u4ecd\u7136\u80fd\u591f\u4f24\u4eba\uff0c\u600e\u77e5\u4ed6\u4e5f\u53d7\u4e86\u91cd\u4f24\uff0c\u4f55\u51b5\u5979\u7684\u5e08\u5144\u53c8 \u5df2\u5012\u5730\u5782\u5371\uff0c\u5979\u5f53\u7136\u53ea\u597d\u653e\u8fc7\u5929\u6076\u9053\u4eba\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u505c\u4e0b\u4e86\u72ec\u8f6e\u8f66\uff0c\u8dd1\u5230\u5e08\u5144\u8eab\u65c1\uff0c\u53ea\u89c1\u88f4\u53d4\u5ea6\u9762\u5982\u91d1\u7eb8\uff0c\u53e3\u9f3b\u6d41\u51fa\u7600\u8840\uff0c\u5374\u72b9 \u81ea\u9732\u51fa\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u60f3\u5f97\u597d\u5999\u8ba1\uff0c\u9760\u7740\u5e08\u7236\u7684\u795e\u5a01\uff0c\u7ec8\u4e8e\u628a\u8fd9\u5927\u9b54\u5934 \u8d76\u8dd1\u4e86\uff0c\u771f\u9669\uff0c\u771f\u9669\uff01\u201d\u90a3\u8f86\u72ec\u8f6e\u8f66\u4e43\u662f\u88f4\u53d4\u5ea6\u642c\u8fd0\u67f4\u706b\u7528\u7684\uff0c\u6b66\u7384\u971c\u5c06\u5e08\u7236\u7684\u9057\u4f53\u653e \u5728\u8f66\u4e0a\uff0c\u5f53\u6210\u662f\u5e08\u7236\u7684\u5ea7\u8f66\u63a8\u51fa\u6765\uff0c\u5929\u6076\u9053\u4eba\u82e5\u7136\u518d\u9547\u5b9a\u4e00\u4e9b\uff0c\u7acb\u65f6\u4fbf\u53ef\u770b\u51fa\u7834\u7efd\uff0c\u6b66 \u7384\u971c\u4fa5\u5e78\u6210\u529f\uff0c\u8d8a\u60f3\u8d8a\u9669\uff0c\u989d\u4e0a\u7684\u51b7\u6c57\uff0c\u4e0d\u7981\u6d94\u6d94\u800c\u4e0b\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u7684\u8138\u4e0a\u867d\u7136\u9732\u51fa\u7b11\u5bb9\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u5374\u662f\u6e10\u6e10\u5fae\u5f31\uff0c\u8138\u8272\u8d8a\u6765\u8d8a\u662f\u9a87\u4eba\uff0c\u6b66\u7384 \u971c\u5f85\u8981\u7ed9\u4ed6\u628a\u8109\uff0c\u88f4\u53d4\u5ea6\u8fde\u5fd9\u6447\u5934\uff0c\u6323\u624e\u7740\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u6211\u8eab\u4e0a\u90a3\u652f\u5c0f\u94f6\u74f6\u638f\u51fa\u6765\uff0c \u4e0d\u53ef\u89e6\u53ca\u6211\u7684\u76ae\u80a4\u3002\u201d\u6b66\u7384\u971c\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4ed6\u9732\u51fa\u6765\u7684\u76ae\u8089\u53d8\u6210\u4e86\u732a\u809d\u4e00\u822c\u7684\u989c\u8272\uff0c \u90a3\u81ea\u662f\u4e2d\u4e86\u5267\u6bd2\u6240\u81f4\uff0c\u770b\u6765\u4ed6\u7684\u624b\u811a\u90fd\u5df2\u50f5\u786c\uff0c\u4e0d\u80fd\u8f6c\u52a8\u4e86\u3002\u5929\u6076\u9053\u4eba\u7684\u201c\u8150\u9aa8\u795e\u638c\u201d \u7adf\u7136\u5982\u6b64\u5389\u5bb3\uff0c\u6b66\u7384\u971c\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e0d\u7981\u9a87\u7136\uff0c\u540c\u65f6\u5bf9\u5e08\u5144\u6df1\u539a\u7684\u5185\u529f\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u6b66\u7384\u971c\u5c0f\u5fc3\u5728\u610f\uff0c\u53cc\u6307\u4e00\u63a2\uff0c\u5c06\u90a3\u5c0f\u94f6\u74f6\u631f\u4e86\u51fa\u6765\uff0c\u74f6\u5185\u76db\u7740\u51e0\u7c92\u78a7\u7eff\u8272\u7684\u4e39\u4e5d\uff0c \u6590\u53d4\u5ea6\u53c8\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u541e\u4e0b\u4e00\u9897\u3002\u201d\u8bf4\u8fd9\u4e00\u53e5\u8bdd\u65f6\uff0c\u5fae\u7ec6\u5230\u51e0\u4e0d\u53ef\u95fb\uff0c\u6b66\u7384\u971c\u4e43\u662f \u7edd\u9876\u806a\u660e\u7684\u4eba\uff0c\u53c8\u5728\u6c5f\u6e56\u4e0a\u95ef\u8361\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u5f53\u7136\u9886\u4f1a\u4ed6\u7684\u5fc3\u610f\uff0c\u77e5\u9053\u8fd9\u74f6\u4e2d\u662f\u89e3\u6bd2\u4e4b \u836f\u3002\u5e08\u5144\u6015\u5979\u670d\u4f8d\u4ed6\u65f6\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u89e6\u7740\u4ed6\u7684\u8eab\u4f53\u4e86\u4e5f\u4f1a\u4e2d\u6bd2\uff0c\u6545\u6b64\u53eb\u5979\u5148\u541e\u4e0b\u89e3\u836f\uff0c\u6b66 \u7384\u971c\u541e\u4e0b\u4e86\u4e00\u9897\u4e39\u4e38\uff0c\u4f46\u89c9\u4e00\u80a1\u6e05\u9999\uff0c\u6c81\u4eba\u813e\u8151\uff0c\u5468\u56f4\u90a3\u80a1\u8165\u81ed\u6c14\u5473\u767b\u65f6\u6d88\u6563\uff0c\u7cbe\u795e\u4e5f \u7acb\u523b\u723d\u5229\u8d77\u6765\u3002\u8fd9\u65f6\u88f4\u53d4\u5ea6\u5df2\u662f\u53cc\u76ee\u95ed\u4e0a\uff0c\u8fde\u5634\u5507\u4e5f\u5f20\u4e0d\u5f00\u4e86\u3002\u6b66\u7384\u971c\u6316\u5f00\u4ed6\u7684\u7259\u5173\uff0c \u63a5\u8fde\u7ed9\u4ed6\u5582\u4e86\u4e09\u9897\u4e39\u4e5d\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u88f4\u53d4\u5ea6\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u4e00\u5927\u53e3\u8840\u3002\u8840\u8272 \u7531\u9ed1\u6e10\u8f6c\u7ea2\uff0c\u53cc\u76ee\u500f\u5f20\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff0c\u8981\u4e0d\u662f\u5e08\u7236\u9057\u4e0b\u7684\u78a7\u7075\u4e39\uff0c\u6211\u51e0\u4e4e\u4e0d\u80fd\u6d3b \u547d\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u5c06\u5979\u5e08\u7236\u7684\u6cd5\u8eab\u518d\u642c\u56de\u77f3\u7a9f\u3002\u7136\u540e\u5c06\u7a7a\u8f66\u63a8\u51fa\u6765\uff0c\u628a\u5e08\u5144\u653e\u5728\u8f66\u4e0a\uff0c\u63a8\u4ed6\u56de \u53bb\uff0c\u6590\u53d4\u5ea6\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u7d2f\u4e86\u4f60\u4e86\u3002\u6211\u6709\u4e24\u53ea\u72d2\u72d2\u670d\u4f8d\uff0c\u4f60\u6709\u7d27\u8981\u4e8b\u60c5\uff0c\u53ef\u4ee5\u5148 \u4e0b\u5c71\u53bb\u3002\u201d\u4ed6\u4e00\u65f6\u4e4b\u95f4\u672a\u60f3\u8d77\u6765\uff0c\u90a3\u4e24\u53ea\u72d2\u72d2\u4e5f\u53d7\u4e86\u91cd\u4f24\uff0c\u5b83\u4eec\u4e5f\u6b63\u81ea\u8981\u4eba\u8c03\u7406\uff0c\u5982\u4f55 \u8fd8\u80fd\u591f\u670d\u4f8d\u4ed6\uff1f \u3000\u3000\u6b66\u7384\u971c\u77e5\u9053\u4ed6\u6240\u6307\u7684\u4e43\u662f\u8981\u53bb\u6697\u52a9\u674e\u9038\u7684\u4e8b\u60c5\uff0c\u53ef\u662f\u8fd9\u4e2a\u65f6\u5019\uff0c\u5979\u5c82\u80fd\u79bb\u5f00\u5e08\u5144\uff0c \u4fbf\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u4e0d\u8981\u6302\u8651\u6211\u7684\u4e8b\u60c5\uff0c\u5f85\u4f60\u597d\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u53ef\u662f\u5929\u6076\u9053\u4eba\u7684\u6bd2\u638c\u5b9e\u662f\u592a\u8fc7\u5389\u5bb3\uff0c\u6b66\u7384\u971c\u8863\u4e0d\u89e3\u5e26\u5730\u670d\u4f8d\u4e86\u5e08\u5144\u4e09\u5929\uff0c\u88f4\u53d4\u5ea6\u624d \u80fd\u559d\u70b9\u7a00\u7ca5\uff0c\u8eab\u5b50\u4e5f\u624d\u80fd\u5728\u5e8a\u4e0a\u8f6c\u52a8\u3002\u5e78\u800c\u6709\u4f18\u4e91\u8001\u5c3c\u7528\u96ea\u83b2\u5236\u70bc\u7684\u78a7\u7075\u4e39\uff0c\u80fd\u89e3\u767e\u6bd2\uff0c \u8981\u4e0d\u7136\u4ed6\u7684\u5185\u810f\u65e9\u5df2\u5728\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4fbf\u8981\u8150\u70c2\u4e86\u3002 \u3000\u3000\u5012\u662f\u90a3\u4e24\u53ea\u72d2\u72d2\u5148\u597d\u8d77\u6765\uff0c\u5230\u4e86\u7b2c\u4e09\u5929\uff0c\u5b83\u4eec\u5df2\u7ecf\u80fd\u591f\u8d70\u52a8\uff0c\u88f4\u53d4\u5ea6\u53c8\u50ac\u5979\u4e0b\u5c71\uff0c \u6b66\u7384\u971c\u867d\u7136\u6302\u5ff5\u674e\u9038\uff0c\u5374\u662f\u6267\u610f\u4e0d\u80af\uff0c\u5230\u4e86\u7b2c\u4e03\u5929\uff0c\u88f4\u53d4\u5ea6\u8eab\u4e0a\u7684\u6076\u6bd2\u5c3d\u6d88\uff0c\u8fd9\u624d\u80fd\u591f \u4e0b\u5e8a\uff0c\u53ef\u662f\u8eab\u4f53\u8fd8\u865a\u5f31\u5f97\u5f88\uff0c\u8fd9\u4e00\u5929\u6b66\u7384\u971c\u5949\u5e08\u5144\u4e4b\u547d\uff0c\u5c06\u5e08\u7236\u7684\u9057\u4f53\u57cb\u846c\u4e86\u3002\u81f3\u4e8e\u5efa \u5893\u7acb\u7891\u7684\u4e8b\u60c5\uff0c\u5219\u53ea\u597d\u7559\u5f85\u5e08\u5144\u65e5\u540e\u53bb\u529e\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u5f85\u5979\u4e86\u7ed3\u8fd9\u6869\u4e8b\u60c5\u56de\u6765\u4e4b\u65f6\uff0c\u4fbf\u53c8\u5bf9\u5979\u8a00\u9053\uff1a\u201c\u7a81\u53a5\u53ef\u6c57\u7ed9\u674e\u9038\u7684\u4e00\u4e2a\u6708\u9650 \u671f\uff0c\u53c8\u5df2\u8fc7\u4e86\u4e03\u5929\u4e86\u3002\u6211\u5949\u4e86\u5e08\u7236\u9057\u547d\uff0c\u8981\u6697\u4e2d\u4fdd\u62a4\u4ed6\uff0c\u5982\u4eca\u529b\u4e0d\u4ece\u5fc3\uff0c\u53ea\u6709\u8bf7\u4f60\u7ba1\u6211 \u8d70\u4e00\u8d9f\u4e86\u3002\u201d\u6b66\u7384\u971c\u5fc3\u60c5\u975e\u5e38\u70e6\u4e71\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u518d\u670d\u4f8d\u4f60\u4e24\u5929\uff0c\u5f85\u4f60\u597d\u5b9a\u4e86\uff0c \u6211\u624d\u653e\u5fc3\u3002\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u7d2f\u4e86\u4f60\u8fd9\u4e48\u591a\u5929\uff0c\u6211\u5df2\u7ecf\u5f88\u8fc7\u610f\u4e0d\u53bb\uff0c\u4e24\u53ea\u72d2\u72d2\u73b0\u5728\u5df2\u80fd\u884c \u52a8\u5982\u5e38\uff0c\u5b83\u4eec\u53ef\u4ee5\u7167\u6599\u6211\u4e86\uff0c\u4f60\u660e\u5929\u8fd8\u662f\u8d70\u5427\uff01\u201d \u3000\u3000\u5176\u5b9e\uff0c\u6b66\u7384\u971c\u4f55\u5c1d\u4e0d\u4e3a\u674e\u9038\u7684\u4e8b\u60c5\u7126\u6025\uff1f\u4f46\u5979\u4e00\u6765\u89c1\u5e08\u5144\u5c1a\u5728\u75c5\u4e2d\uff0c\u4e0d\u5fcd\u79bb\u53bb\uff1b\u4e8c \u6765\u5979\u5b9e\u5728\u662f\u77db\u76fe\u5f97\u5f88\uff0c\u65e2\u6e34\u671b\u89c1\u674e\u9038\uff0c\u53c8\u4e0d\u60f3\u89c1\u674e\u9038\uff0c\u56e0\u4e3a\u6709\u4e00\u4e2a\u957f\u5b59\u58c1\u5728\u5979\u4e0e\u674e\u9038\u4e4b \u95f4\uff0c\u60c5\u5f62\u5df2\u7ecf\u4e0e\u516b\u5e74\u4e4b\u524d\u5927\u5927\u4e0d\u540c\u4e86\u3002\u5979\u81ea\u4ece\u89c1\u8fc7\u957f\u5b59\u58c1\u4e4b\u540e\uff0c\u5bf9\u8fd9\u4e2a\u95ee\u9898\u5df2\u60f3\u5341\u767e\u6b21\uff0c \u80fd\u591f\u907f\u514d\u518d\u89c1\u674e\u9038\u800c\u628a\u4e8b\u60c5\u529e\u59a5\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u6240\u4ee5\u5979\u5f53\u521d\u624d\u8981\u6c42\u5e08\u5144\u51fa\u9a6c\uff0c\u5e76\u8bf7\u5e08 \u5144\u8f6c\u8fbe\u5219\u5929\u7687\u5e1d\u7684\u610f\u601d\uff0c\u4f46\u73b0\u5728\u5e08\u5144\u6700\u5c11\u8fd8\u5f97\u8c03\u517b\u4e00\u4e2a\u6708\uff0c\u65b9\u80fd\u6062\u590d\u6b66\u529f\u3002\u5979\u6ca1\u6cd5\u907f\u514d\uff0c \u53ea\u80fd\u81ea\u5df1\u53bb\u627e\u674e\u9038\u4e86\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u53c8\u8bf4\u9053\uff1a\u201c\u4f60\u4eca\u5929\u628a\u5e08\u7236\u7684\u5251\u8c31\u4ed4\u7ec6\u4e00\u8bfb\uff0c\u6709\u4ec0\u4e48\u4e0d\u660e\u767d\u7684\u5730\u65b9\uff0c\u4e34\u8d70\u4e4b\u524d \u53ef\u4ee5\u95ee\u6211\u3002\u201d\u6b66\u7384\u971c\u89c1\u5e08\u5144\u5bf9\u5979\u5982\u6b64\u5173\u5fc3\uff0c\u751a\u662f\u611f\u6fc0\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u6b66\u7384\u971c\u5f7b\u591c\u65e0\u7720\uff0c\u601d\u6f6e\u6c79\u6d8c\uff0c\u540e\u6765\u9075\u7167\u5e08\u5144\u7684\u5429\u5490\uff0c\u5c55\u5f00\u4e86\u5e08\u7236\u7684\u5251\u8c31\uff0c\u90a3 \u4e9b\u7cbe\u5999\u7684\u5251\u672f\u62db\u6570\uff0c\u5438\u5f15\u4e86\u5979\u7684\u6ce8\u610f\uff0c\u5fc3\u795e\u624d\u5e73\u9759\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u672c\u5251\u8c31\u7684\u524d\u534a\u90e8\u6b66\u7384\u971c\u4ee5\u524d\u5b66\u8fc7\uff0c\u540e\u534a\u90e8\u5219\u662f\u5979\u5e08\u7236\u5728\u5929\u5c71\u9690\u5c45\u8fd9\u51e0\u5e74\u624d\u5199\u51fa\u6765 \u7684\uff0c\u90a3\u662f\u5979\u5e08\u7236\u540e\u534a\u751f\u7684\u5fc3\u8840\u6240\u805a\uff0c\u6b66\u7384\u971c\u5c31\u672a\u66fe\u5b66\u8fc7\u4e86\u3002\u597d\u5728\u524d\u540e\u4e24\u90e8\u4e43\u662f\u4e00\u8109\u76f8\u627f\uff0c \u4ee5\u6b66\u7384\u971c\u7684\u6b66\u5b66\u6839\u5e95\uff0c\u5e76\u4e0d\u611f\u89c9\u6709\u4ec0\u4e48\u7279\u522b\u96be\u89e3\u7684\u5730\u65b9\uff0c\u53ea\u662f\u6709\u51e0\u62db\u590d\u6742\u7684\u5251\u672f\uff0c\u5979\u4e00 \u65f6\u4e4b\u95f4\u8fd8\u672a\u601d\u7d22\u5f97\u660e\uff0c\u4fbf\u505a\u4e86\u8bb0\u53f7\uff0c\u7559\u5f85\u660e\u5929\u518d\u95ee\u5e08\u5144\u3002 \u3000\u3000\u77f3\u7a9f\u91cc\u672c\u6765\u6709\u4e24\u95f4\u5367\u623f\uff0c\u4e00\u95f4\u662f\u5979\u5e08\u5144\u4f4f\u7684\uff0c\u53e6\u4e00\u95f4\u5219\u662f\u5979\u5e08\u7236\u4ee5\u524d\u4f4f\u7684\uff0c\u4f46\u6b66\u7384 \u971c\u8fd9\u51e0\u5929\u6765\u4e3a\u4e86\u770b\u62a4\u5979\u7684\u5e08\u5144\uff0c\u4e00\u76f4\u7761\u5728\u5979\u5e08\u5144\u7684\u623f\u95e8\u5916\u8fb9\uff0c\u597d\u5728\u4ed6\u4eec\u90fd\u662f\u82f1\u96c4\u513f\u5973\uff0c \u5bf9\u7537\u5973\u4e4b\u5acc\u5e76\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u8fd9\u4e00\u665a\u6b66\u7384\u971c\u4ed4\u7ec6\u8bfb\u5e08\u7236\u7684\u5251\u8c31\uff0c\u65b9\u81ea\u8bfb\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u5076\u4e00 \u56de\u5934\uff0c\u90a3\u623f\u95e8\u672c\u6765\u662f\u6ca1\u63a9\u4e0a\u7684\uff0c\u53ea\u89c1\u5e08\u5144\u53cc\u773c\u70af\u70af\uff0c\u5728\u5e8a\u4e0a\u534a\u501a\u534a\u5367\uff0c\u773c\u5149\u6b63\u5bf9\u7740\u5979\uff0c \u6b66\u7384\u971c\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u600e\u4e48\u8fd8\u6ca1\u6709\u7761\uff1f\u201d\u88f4\u53d4\u5ea6\u5fae\u7b11\u9053\uff1a\u201c\u6211\u7cbe\u795e\u5f88\u597d\uff0c\u4e00\u65f6\u672a\u66fe\u60f3\u7761\u3002 \u4f60\u6709\u4ec0\u4e48\u5730\u65b9\u4e0d\u660e\u767d\u4e48\uff1f\u201d\u6b66\u7384\u971c\u5174\u81f4\u52c3\u52c3\uff0c\u4fbf\u5c06\u90a3\u51e0\u4e2a\u5251\u672f\u4e0a\u7684\u95ee\u9898\u95ee\u4ed6\uff0c\u88f4\u53d4\u5ea6\u2014 \u4e00\u7ed9\u5979\u8bb2\u89e3\uff0c\u8bb2\u5f97\u975e\u5e38\u8be6\u7ec6\u3002\u6b66\u7384\u971c\u8c22\u8fc7\u5e08\u5144\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4ec0\u4e48\u4e0d\u660e\u767d\u7684\u4e86\uff0c\u5e08\u5144\uff0c \u4f60\u8bf7\u5b89\u7761\u5427\u3002\u201d\u8fc7\u4e86\u5927\u534a\u4e2a\u65f6\u8fb0\u3002\u5979\u5076\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u5e08\u5144\u4ecd\u7136\u7741\u5f00\u53cc\u773c\uff0c\u6b66\u7384\u971c\u8be7\u9053\uff1a \u201c\u4f60\u600e\u4e48\u8fd8\u4e0d\u7761\u5440\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u6211\u5728\u60f3\u4e00\u4e9b\u4e8b\u60c5\uff0c\u8fc7\u4e00\u4f1a\u4fbf\u7761\u3002\u65f6\u5019\u4e0d\u65e9\uff0c\u4f60\u660e\u5929 \u8fd8\u8981\u8d76\u8def\uff0c\u4e5f\u8be5\u7761\u5566\u3002\u201d\u6b66\u7384\u971c\u5fc3\u5ff5\u5fae\u52a8\uff0c\u89c9\u5f97\u5e08\u5144\u4eca\u665a\u7684\u795e\u60c5\u6709\u4e9b\u5947\u7279\uff0c\u4fbf\u518d\u529d\u4ed6\u5b89 \u7761\uff0c\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6b66\u7384\u971c\u518d\u770b\u4ed6\u65f6\uff0c\u4ed6\u4e00\u53d1\u89c9\uff0c\u4fbf\u9616\u773c\u5047\u7761\uff0c\u8fd9\u65f6\u5929\u8272\u5df2\u7ecf\u5fae\u660e\uff0c\u6b66\u7384 \u971c\u4e5f\u5c31\u4e0d\u518d\u8bf4\u3002\u8fd9\u4e00\u665a\u6b66\u7384\u971c\u6ca1\u6709\u7761\u89c9\uff0c\u5979\u53d1\u89c9\u5e08\u5144\u8fd9\u4e00\u665a\u4f3c\u4e4e\u4e5f\u672a\u66fe\u7761\u8fc7\u3002 \u3000\u3000\u5929\u660e\u4e4b\u540e\uff0c\u6b66\u7384\u971c\u6536\u62fe\u884c\u88c5\uff0c\u88f4\u53d4\u5ea6\u4e5f\u968f\u7740\u8d77\u5e8a\uff0c\u4ed6\u4e00\u591c\u6ca1\u7761\uff0c\u7cbe\u795e\u5374\u65e0\u840e\u9761\u4e0d\u632f \u7684\u73b0\u8c61\uff0c\u53cd\u800c\u6bd4\u6628\u5929\u5174\u594b\u5f97\u591a\u3002\u4ed6\u628a\u5e08\u7236\u7684\u8bd7\u6587\u96c6\u548c\u90a3\u53ea\u7389\u5323\u4ea4\u7ed9\u4e86\u6b66\u7384\u971c\uff0c\u518d\u90d1\u91cd\u7684 \u53ee\u5631\u4e00\u904d\uff0c\u8bf7\u5979\u8f6c\u4ea4\u7ed9\u5219\u5929\u7687\u5e1d\u4e0e\u590f\u4faf\u575a\uff0c\u597d\u4e86\u7ed3\u5e08\u7236\u751f\u524d\u7684\u5fc3\u613f\uff0c\u7136\u540e\u53c8\u53d6\u51fa\u4e24\u4e2a\u5c0f \u94f6\u74f6\uff0c\u5bf9\u6b66\u7384\u971c\u9053\uff1a\u201c\u8fd9\u4e2a\u957f\u9888\u7684\u74f6\u5b50\u76db\u7740\u7684\u662f\u78a7\u7075\u4e39\uff0c\u4f60\u77e5\u9053\u6211\u8fd9\u6b21\u4e2d\u4e86\u5929\u6076\u9053\u4eba\u7684 \u6bd2\u638c\uff0c\u5c31\u5b8c\u5168\u662f\u9760\u4e86\u5b83\u8d77\u6b7b\u56de\u751f\u7684\uff0c\u4f60\u5e26\u5728\u8eab\u8fb9\u5427\uff0c\u6709\u4e86\u5b83\u5c31\u4e0d\u6015\u4efb\u4f55\u6709\u6bd2\u6697\u5668\u4e86\u3002\u201d \u63a5\u7740\u53c8\u6307\u7740\u53e6\u4e00\u4e2a\u74f6\u5b50\u9053\uff1a\u201c\u8fd9\u4e2a\u5706\u53e3\u7684\u74f6\u5b50\u76db\u7740\u7684\u662f\u6613\u5bb9\u4e39\uff0c\u90a3\u5374\u662f\u4ee5\u524d\u590f\u4faf\u575a\u9001\u7ed9 \u5e08\u7236\u7684\uff0c\u5e08\u7236\u6ca1\u6709\u7528\u8fc7\uff0c\u6211\u5728\u6df1\u5c71\u9690\u4fee\uff0c\u4e5f\u4e0d\u9700\u8981\u7528\u5230\u5b83\uff0c\u4f60\u90fd\u5e26\u53bb\u5427\u3002\u201d\u4ed6\u5411\u6b66\u7384\u971c \u8bb2\u4e86\u6613\u5bb9\u4e39\u7684\u7528\u6cd5\u4e4b\u540e\uff0c\u53c8\u9053\uff1a\u201c\u6613\u5bb9\u4e39\u53ef\u4ee5\u53d8\u8c8c\u6613\u5bb9\uff0c\u8001\u5c11\u7531\u5fc3\uff0c\u598d\u7aef\u968f\u610f\uff0c\u4f46\u53ea\u6709 \u4e00\u6837\u662f\u53d8\u4e0d\u4e86\u7684\uff0c\u90a3\u5c31\u662f\u9762\u4e0a\u7684\u4e00\u5bf9\u773c\u775b\uff0c\u5e74\u9f84\u7684\u5927\u5c0f\u548c\u6b66\u529f\u7684\u6df1\u6d45\u4ece\u773c\u795e\u4e2d\u90fd\u770b\u5f97\u51fa \u6765\uff0c\u4e0d\u8fc7\u4e00\u822c\u666e\u901a\u7684\u4eba\u90a3\u5374\u662f\u4e0d\u4f1a\u6ce8\u610f\u5230\u7684\u3002\u201d\u6b66\u7384\u971c\u542c\u4e86\uff0c\u6697\u6697\u8bb0\u5728\u5fc3\u5934\uff0c\u60f3\u9053\uff1a \u201c\u90a3\u65e5\u957f\u5b59\u58c1\u626e\u6210\u4e00\u4e2a\u5e73\u5e38\u7684\u7a1a\u5987\uff0c\u8fde\u6211\u4e5f\u7ed9\u5979\u7792\u8fc7\uff0c\u60f3\u5fc5\u4e5f\u662f\u7528\u8fd9\u79cd\u6613\u5bb9\u4e39\u7684\u4e86\u3002\u6211 \u6b64\u53bb\u7a81\u53a5\u4eac\u57ce\uff0c\u6b63\u597d\u7528\u5f97\u5b83\u7740\u3002\u201d\u63a5\u8fc7\u8fd9\u4e24\u53ea\u94f6\u74f6\uff0c\u60f3\u8d77\u5e08\u7236\u5e08\u5144\uff0c\u601d\u6000\u6df1\u539a\uff0c\u4e0d\u89c9\u6f78 \u7136\u6cea\u4e0b\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u7684\u773c\u775b\u4e00\u76f4\u6ca1\u6709\u79bb\u5f00\u8fc7\u6b66\u7384\u971c\uff0c\u773c\u7736\u4e2d\u4e5f\u6709\u6cea\u6c34\u6c81\u51fa\uff0c\u8fd9\u65f6\u8bf8\u4e8b\u90fd\u5df2\u4ea4\u4ee3\u5b8c \u6bd5\uff0c\u53f9\u4e86\u6c14\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u670d\u4f8d\u6211\u8fd9\u51e0\u5929\uff0c\u4f60\u4ece\u6b64\u56de\u8f6c\u4e2d\u539f\uff0c\u54b1\u4eec\u4eca\u751f\u5927\u7ea6\u662f\u96be \u4ee5\u518d\u89c1\u4e86\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u795d\u5e08\u5144\u6210\u4e3a\u4e00\u4ee3\u6b66\u5b66\u5927\u5e08\uff0c\u4ed6\u5e74\u6211\u82e5\u6709\u7f18\u518d\u6765\u585e\u5916\uff0c\u4e00\u5b9a \u4e0a\u5c71\u64cd\u8bbf\u5e08\u5144\u3002\u201d\u8bdd\u662f\u5982\u6b64\u8bf4\uff0c\u4f46\u6b66\u7384\u971c\u81ea\u5df1\u4e5f\u77e5\u9053\uff0c\u518d\u6765\u7684\u673a\u4f1a\u6781\u5fae\uff0c\u5373\u8bb8\u518d\u6765\uff0c\u6709 \u674e\u9038\u592b\u5987\u5728\u8fd9\u5c71\u4e0a\uff0c\u5979\u4e5f\u672a\u5fc5\u613f\u610f\u65e7\u5730\u91cd\u4e34\u7684\u4e86\u3002\u5979\u89c1\u5e08\u5144\u5bf9\u5979\u5982\u6b64\u60dc\u522b\uff0c\u4e5f\u81ea\u6709\u70b9\u4f9d \u4f9d\u4e0d\u820d\u4e4b\u60c5\uff0c\u53ea\u662f\u5979\u5374\u5e76\u672b\u5b8c\u5168\u61c2\u5f97\u5e08\u5144\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u81ea\u5df1\u591a\u591a\u4fdd\u91cd\uff0c\u5c0f\u59b9\u62dc\u8f9e\u3002\u201d\u88f4\u53d4\u5ea6\u9ed8\u9ed8\u65e0\u8a00\u7684\u63e1\u7740\u5979\u7684\u624b\uff0c \u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d70\u5427\uff01\u201d\u8bed\u58f0\u4f4e\u54bd\uff0c\u8bf4\u4e86\u4e4b\u540e\uff0c\u4fbf\u5373\u56de\u8fc7\u4e86\u8eab\u3002\u6b66 \u7384\u971c\u8d70\u4e86\u597d\u8fdc\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u5e08\u5144\u8fd8\u501a\u5728\u6d1e\u53e3\uff0c\u5411\u5979\u9065\u671b\u3002 \u3000\u3000\u6b66\u7384\u971c\u5fc3\u4e2d\u51c4\u607b\uff0c\u518d\u8d70\u5230\u5e08\u7236\u7684\u57cb\u9aa8\u4e4b\u5904\uff0c\u55d1\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u5411\u5e08\u7236\u8f9e\u884c\uff0c\u60f3\u8d77\u5e08\u7236 \u4e00\u751f\u4e3a\u60c5\u5b7d\u6240\u7d2f\uff0c\u4e0d\u89c9\u53c8\u5927\u54ed\u4e86\u4e00\uff3c\u573a\u3002 \u3000\u3000\u8d70\u5230\u4e2d\u5348\u65f6\u5206\uff0c\u7ecf\u8fc7\u9a86\u9a7c\u5cf0\u4e0b\uff0c\u6811\u6797\u4e2d\u674e\u9038\u7684\u90a3\u95f4\u77f3\u5c4b\u592e\u5165\u773c\u5e18\uff0c\u6b66\u7384\u971c\u5fc3\u60f3\u6025\u6025 \u8d70\u8fc7\uff0c\u4f46\u53cc\u811a\u5982\u4e0d\u7531\u81ea\u4e3b\u7684\u8d70\u5230\u4e86\u5c4b\u5b50\u5916\u9762\uff0c\u60f3\u5230\u957f\u5b59\u58c1\u4e3a\u4e86\u81ea\u5df1\u800c\u5f03\u5bb6\u8fdc\u8d70\uff0c\u751a\u89c9\u96be \u8fc7\u3002\u773c\u5149\u4e00\u77a5\uff0c\u53d1\u73b0\u90a3\u95f4\u77f3\u5c4b\u7684\u4e24\u6247\u5927\u95e8\u6253\u5f00\uff0c\u6b66\u7384\u971c\u8bb0\u5f97\u90a3\u65e5\u5979\u79bb\u5f00\u4e4b\u65f6\uff0c\u662f\u66fe\u7ecf\u5173 \u4e0a\u7684\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u662f\u957f\u5b59\u58c1\u66fe\u7ecf\u56de\u8fc7\u5bb6\u4e2d\uff1f\u201d\u60c5\u6000\u5386\u4e71\uff0c\u81ea\u5df1\u4e5f\u6291\u5236\u4e0d\u4f4f\uff0c\u4e0d\u77e5\u4e0d\u89c9 \u7684\u4fbf\u8d70\u8fdb\u4e86\u5c4b\u5185\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u5c4b\u4e2d\u7684\u666f\u8c61\uff0c\u4ee4\u5979\u751a\u662f\u60f6\u60d1\u4e0d\u5b89\u3002 \u3000\u3000\u53ea\u89c1\u5ba4\u4e2d\u8863\u7269\u51cc\u4e71\uff0c\u6563\u4e86\u6ee1\u5730\uff0c\u90a3\u5177\u53e4\u7434\uff0c\u5374\u5df2\u4e0d\u89c1\uff0c\u6b66\u7384\u971c\u5446\u4e86\u4e00\u5446\u60f3\u9053\uff1a\u201c\u82e5 \u662f\u957f\u5b59\u58c1\u56de\u6765\u68c0\u53d6\u5979\u7684\u8863\u7269\uff0c\u4f55\u5fc5\u5982\u6b64\u7ffb\u7bb1\u5012\u67dc\uff0c\u4e8b\u540e\u53c8\u4e0d\u52a0\u6536\u62fe\uff1f\u82e5\u662f\u522b\u4eba\uff0c\u4ed6\u53c8\u6765 \u641c\u67e5\u4ec0\u4e48\u5462\uff1f\u4ed6\u53d6\u53bb\u4e86\u53e4\u7434\uff0c\u83ab\u975e\u4e5f\u77e5\u9053\u90a3\u662f\u674e\u9038\u5fc3\u7231\u4e4b\u7269\u4e48\uff1f\u201d\u60f3\u6765\u60f3\u53bb\uff0c\u731c\u4e0d\u900f\u662f \u4ec0\u4e48\u4eba\u66fe\u5230\u8fc7\u5c4b\u5185\u3002 \u3000\u3000\u58c1\u4e0a\u5b57\u8ff9\u72b9\u5b58\uff0c\u6b66\u7384\u971c\u518d\u8bfb\u4e00\u904d\u957f\u5b59\u58c1\u6240\u7559\u7684\u90a3\u9996\u8bd7\uff1a\u201c\u5341\u5e74\u68a6\u9192\u76f8\u601d\u6cea\uff0c\u4e07\u91cc\u897f \u98ce\u701a\u6d77\u6c99\u3002\u540c\u547d\u9e33\u9e2f\u60b2\u547d\u8584\uff0c\u5929\u6daf\u4f55\u5904\u662f\u543e\u5bb6\uff1f\u201d\u4f46\u89c9\u8fd9\u9996\u8bd7\u56fa\u7136\u662f\u957f\u5b59\u58c1\u7684\u81ea\u4f24\u8eab\u4e16\uff0c \u4f46\u4e5f\u4e0d\u557b\u662f\u4e3a\u5979\u800c\u9053\uff0c\u4f24\u611f\u4e86\u597d\u4e00\u4f1a\uff0c\u5fc3\u60f3\uff1a\u201c\u4f46\u613f\u6211\u6b64\u53bb\u80fd\u628a\u674e\u9038\u7684\u513f\u5b50\u6551\u56de\u6765\uff0c\u4eb2 \u624b\u4ea4\u7ed9\u957f\u5b59\u58c1\uff0c\u4ee5\u540e\u5c31\u56de\u8f6c\u4e2d\u539f\uff0c\u6c38\u4e0d\u518d\u6765\uff0c\u53eb\u5979\u77e5\u9053\u6211\u7684\u5fc3\u610f\u3002\u201d\u4e8e\u662f\u62ed\u5e72\u6cea\u75d5\uff0c\u8d70 \u51fa\u8fd9\u95f4\u77f3\u5ba4\u3002 \u3000\u3000\u6b66\u7384\u971c\u65e5\u591c\u8d76\u8def\uff0c\u8d70\u4e86\u534a\u4e2a\u6708\u7684\u5149\u666f\uff0c\u7a7f\u8fc7\u624e\u54c8\u82cf\u53f0\u6c99\u6f20\uff0c\u8ddd\u79bb\u7a81\u53a5\u7684\u4e1c\u90fd\u738b\u5ef7\u4e0d \u8fc7\u662f\u4e94\u516d\u5929\u7684\u8def\u7a0b\u4e86\uff08\u7a81\u53a5\u5728\u5510\u4ee3\u7684\u65f6\u5019\uff0c\u7586\u571f\u751a\u5e7f\uff0c\u5730\u8de8\u6b27\u4e9a\uff0c\u5728\u4e1c\u65b9\u7684\u79f0\u4e3a\u4e1c\u7a81\u53a5\uff0c \u8bbe\u6709\u738b\u5ef7\u5728\u4eca\u4e4b\u4e4c\u9c81\u6728\u9f50\uff09\u3002\u9884\u8ba1\u53ef\u4ee5\u5728\u7a81\u53a5\u738b\u4e00\u4e2a\u6708\u7684\u671f\u9650\u4e4b\u5185\u8d76\u5230\uff0c\u7a0d\u7a0d\u5bbd\u5fc3\u3002\u8fd9 \u4e00\u65e5\u7ecf\u8fc7\u4e86\u5580\u62c9\u6c99\u800c\u6cb3\uff0c\u8fd9\u662f\u4e00\u6761\u957f\u8fbe\u6570\u767e\u91cc\u7684\u6cb3\u6d41\uff1b\u5728\u7a81\u53a5\u5883\u5185\uff0c\u6cb3\u6d41\u6781\u5c11\uff0c\u6b66\u7384\u971c \u521a\u7a7f\u8fc7\u6c99\u6f20\uff0c\u4fbf\u53d1\u73b0\u4e86\u8fd9\u6761\u6cb3\u6d41\uff0c\u5fc3\u60c5\u751a\u4e3a\u8212\u5feb\uff0c\u5f53\u4e0b\u76db\u6ee1\u4e86\u4e24\u4e2a\u6c34\u56ca\uff0c\u6cbf\u7740\u6cb3\u5cb8\u8d76\u8def\u3002 \u6cb3\u7684\u4e24\u5cb8\uff0c\u6811\u6728\u6210\u884c\uff0c\u98ce\u666f\u751a\u7f8e\u3002\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u540e\u9762\u9a7c\u94c3\u58f0\u54cd\uff0c\u5c18\u5934\u5927\u8d77\uff0c\u6b66\u7384\u971c \u53ea\u9053\u662f\u5546\u4eba\u7684\u9a86\u9a7c\u961f\uff0c\u56de\u5934\u770b\u65f6\uff0c\u5374\u662f\u4e00\u961f\u7532\u80c4\u9c9c\u660e\u7684\u7a81\u53a5\u6b66\u58eb\uff0c\u62e5\u6709\u51e0\u5339\u9a86\u9a7c\uff0c\u4e03\u516b \u9a91\u5065\u9a6c\uff0c\u56f4\u62e5\u7740\u4e00\u8f86\u5927\u8f66\uff0c\u4ece\u4e0a\u6e38\u6cb3\u5cb8\u9a70\u6765\uff0c\u90a3\u8f86\u8f66\u5341\u5206\u534e\u4e3d\uff0c\u62c9\u8f66\u7684\u662f\u5339\u6bdb\u8272\u7eaf\u767d\u7684 \u9a8f\u9a6c\uff0c\u6b66\u76f4\u971c\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u662f\u54ea\u4f4d\u738b\u516c\u51fa\u5de1\uff1f\u201d\u6b66\u7384\u971c\u56e0\u4e3a\u6025\u7740\u8d76\u8def\uff0c\u4e00\u8def\u4e0a\u4e0d\u613f\u62db\u60f9 \u4e8b\u7aef\uff0c\u65e2\u7136\u89c1\u4e86\u5927\u961f\u7a81\u53a5\u6b66\u58eb\uff0c\u4fbf\u5373\u907f\u5f00\uff0c\u8eb2\u5728\u79bb\u6cb3\u5cb8\u6570\u5341\u4e08\u7684\u4e00\u4e2a\u6c99\u4e18\u540e\u9762\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\uff0c\u8fd9\u4e00\u961f\u4eba\u5df2\u8d70\u5230\u4e86\u6b66\u7384\u971c\u7684\u9762\u524d\uff0c\u8f66\u4e0a\u4f20\u51fa\u80e1\u97f6\u58f0\u54cd\uff0c\u914d\u5408\u7740\u201c\u4e1c\u4e0d\u62c9\u201d \u7684\u4e50\u58f0\uff0c\u6709\u4e2a\u5973\u90ce\u5f39\u7740\u4e1c\u4e0d\u62c9\uff0c\u5531\u5f97\u975e\u5e38\u51c4\u607b\uff0c\u6b66\u7384\u971c\u4e00\u542c\u8fd9\u4e2a\u6b4c\u66f2\u7684\u8c03\u5b50\u597d\u719f\uff0c\u542c\u4e86 \u4e00\u4f1a\uff0c\u542c\u51fa\u4e86\u5979\u5f39\u7684\u7adf\u662f\u4e2d\u56fd\u4e1c\u6c49\u65f6\u4ee3\u5973\u5b50\u8521\u6587\u59ec\u6240\u521b\u7684\u201c\u80e1\u62e5\u5341\u516b\u62cd\u201d\uff0c\u8521\u6587\u59ec\u5ac1\u7ed9 \u5f53\u65f6\u5308\u5974\u7684\u4e4c\u5b59\u738b\uff0c\u5979\u6240\u521b\u7684\u80e1\u62e5\u5341\u516b\u62cd\u6d41\u4f20\u56de\u7586\uff0c\u81ea\u662f\u4e0d\u8db3\u4e3a\u5947\u3002\u53ef\u662f\u8fd9\u6837\u534e\u8d35\u7684\u9a6c \u8f66\uff0c\u53c8\u4e0d\u8fd9\u4e48\u4e00\u7fa4\u6b66\u58eb\u62a4\u9001\uff0c\u8f66\u4e2d\u7684\u5973\u5b50\uff0c\u8eab\u4efd\u60f3\u6765\u975e\u6bd4\u5bfb\u5e38\uff0c\u5979\u5374\u5f39\u51fa\u8fd9\u6837\u60b2\u82e6\u7684\u66f2 \u5b50\uff0c\u90a3\u5c31\u6709\u70b9\u5947\u602a\u4e86\u3002\u6b66\u7384\u971c\u542c\u5f97\u5979\u7528\u7ef4\u8bed\u51fa\u201c\u543e\u5bb6\u5ac1\u6211\u516e\u5929\u4e00\u65b9\uff0c\u8fdc\u79bb\u5f02\u56fd\u516e\u4e4c\u5b59\u738b\u3002\u201d \u5fc3\u4e2d\u4e5f\u4e0d\u7981\u611f\u5230\u9178\u695a\u3002 \u3000\u3000\u9a6c\u5728\u5cb8\u8fb9\u505c\u4e0b\uff0c\u8f66\u4e2d\u7684\u5973\u5b50\u8bf4\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u6b47\u4e00\u4f1a\u5427\u3002\u201d\u6709\u51e0\u4e2a\u4f8d\u5973\u4e0b\u6765\uff0c\u652f\u8d77\u4e86 \u5e10\u5e55\uff0c\u53e6\u5916\u51e0\u4e2a\u6b66\u58eb\u5230\u6cb3\u8fb9\u76db\u6c34\u9001\u5165\u5e10\u5e55\uff0c\u6b66\u7384\u971c\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u662f\u8981\u5728\u8fd9\u91cc\u6d17\u4e00\u4e2a\u6fa1\u3002\u201d \u5fc3\u5ff5\u672a\u5df2\uff0c\u8f66\u4e2d\u7684\u90a3\u4e2a\u5973\u5b50\u8d70\u4e86\u4e0b\u6765\uff0c\u660e\u7738\u7693\u9f7f\uff0c\u96fe\u9aef\u98ce\u7f72\uff0c\u662f\u4e00\u4e2a\u5341\u5206\u7f8e\u8c8c\u7684\u7ef4\u65cf\u59d1 \u5a18\u3002 \u3000\u3000\u8fd9\u4e2a\u7f8e\u8c8c\u7684\u7ef4\u65cf\u59d1\u5a18\u8d70\u8fdb\u4e86\u5e10\u84ec\uff0c\u6b66\u58eb\u4eec\u4e09\u4e09\u4e94\u4e94\u6563\u5728\u6cb3\u5824\u4e0a\u6b47\u606f\uff0c\u6709\u4e24\u540d\u6b66\u58eb\u6765 \u56de\u6f2b\u6b65\uff0c\u597d\u51e0\u6b21\u8d70\u8fd1\u4e86\u6b66\u7384\u971c\u85cf\u8eab\u4e4b\u5730\uff0c\u6b66\u7384\u971c\u624b\u5fc3\u634f\u7740\u51e0\u9897\u5c0f\u77f3\u5b50\uff0c\u6253\u7b97\u4e00\u7ed9\u4ed6\u4eec\u53d1 \u73b0\uff0c\u4fbf\u5c06\u4ed6\u4eec\u6253\u5012\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9a6c\u8e44\u4e4b\u58f0\uff0c\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c\u6b66\u7384\u971c\u4ece\u6c99\u4e18\u540e\u9762\u671b\u51fa\u53bb\uff0c\u4f46\u89c1\u4e00\u4e2a\u5c11\u5e74\u6b66\u58eb\uff0c \u9a91\u7740\u4e00\u5339\u67a3\u7ea2\u5927\u9a6c\uff0c\u98de\u9a70\u800c\u6765\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u5361\u6d1b\u4e1d\uff01\u201d\u62a4\u9001\u8f66\u8f86\u7684\u7a81\u53a5\u6b66\u58eb \u7eb7\u7eb7\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff0c\u80c6\u6562\u53eb\u6211\u4eec\u53ef\u8d3a\u6566\u7684\u540d\u5b57\uff01\u201d\uff0c\u5341\u51e0\u679d\u7fbd\u7bad\u5c04\u51fa\uff0c\u90a3\u5c11\u5e74\u6b66\u58eb\u8eab \u624b\u4e0d\u51e1\uff0c\u4f46\u89c1\u4ed6\u628a\u624b\u4e00\u62db\uff0c\u4fbf\u5c06\u4e24\u679d\u7bad\u63a5\u7740\uff0c\u968f\u624b\u63b7\u51fa\uff0c\u968f\u4fbf\u968f\u63b7\uff0c\u628a\u5341\u51e0\u679d\u5229\u7bad\u90fd\u629b \u5230\u6cb3\u4e2d\u3002 \u3000\u3000\u6b66\u7384\u971c\u4e00\u6014\uff0c\u539f\u6765\u201c\u53ef\u8d3a\u6566\u201d\u4e43\u662f\u7a81\u53a5\u8bdd\u4e2d\u7684\u201c\u738b\u5983\u201d\u4e4b\u610f\uff0c\u6b66\u7384\u971c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u539f\u6765\u5979\u7adf\u662f\u7a81\u53a5\u53ef\u6c57\u7684\u738b\u5983\uff0c\u65e2\u7136\u662f\u738b\u5983\u7684\u8eab\u4efd\uff0c\u5374\u4e3a\u4f55\u5355\u72ec\u51fa\u5de1\u3002\u79bb\u5f00\u4e86\u4ed6\u4eec\u7684\u738b \u5ef7\u5343\u91cc\u4e4b\u5730\u3002\u8fd9\u4e2a\u5c11\u5e74\u6b66\u58eb\u53c8\u600e\u7684\u8fd9\u4e48\u5927\u80c6\uff0c\u6562\u6765\u8ffd\u738b\u5983\u7684\u5ba1\u9a7e\uff1f\u201d\u4f46\u89c9\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5904 \u5904\u900f\u9732\u7740\u53e4\u602a\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u5c11\u5e74\u6b66\u58eb\u7eb5\u9a6c\u5982\u98ce\uff01\u500f\u5ffd\u4e4b\u95f4\u5c31\u5230\u4e86\u5e10\u5e55\u524d\u9762\u6570\u4e08\u4e4b\u5730\uff0c\u4ecd\u7136 \u5728\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u5361\u6d1b\u4e1d\uff01\u201d\u6709\u4e24\u4e2a\u7a81\u53a5\u6b66\u58eb\u6251\u4e0a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4f60\u75af\u4e86\u5417\uff1f\u201d\u56db \u638c\u9f50\u51fa\uff0c\u6309\u7740\u9a6c\u5934\uff0c\u90a3\u5339\u96c4\u9a6c\u957f\u5636\u4e00\u58f0\uff0c\u5012\u9000\u4eba\u7acb\uff0c\u8fd9\u4e24\u4e2a\u7a81\u53a5\u6b66\u58eb\u80fd\u591f\u529b\u963b\u5954\u9a6c\uff0c\u6c14 \u529b\u786e\u662f\u60ca\u4eba\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u6b66\u58eb\u5728\u9a6c\u80cc\u4e0a\u98de\u8eab\u8dc3\u8d77\uff0c\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff0c\u6211\u8981\u89c1\u5361\u6d1b\u4e1d\uff01\u201d\u597d\u50cf\u4e00\u53ea\u5140\u9e70\uff0c \u4ece\u7a7a\u4e2d\u6251\u4e0b\uff0c\u8fd9\u4e24\u4e2a\u7a81\u53a5\u6b66\u58eb\u54ea\u80af\u8ba9\u4ed6\uff1f\u53cc\u53cc\u51fa\u624b\uff0c\u4e00\u4e2a\u6293\u4ed6\u7684\u53f3\u817f\uff0c\u4e00\u4e2a\u626d\u4ed6\u7684\u5de6\u81c2\uff0c \u60f3\u8d81\u4ed6\u8eab\u5f62\u672a\u7a33\uff0c\u4fbf\u5c06\u4ed6\u8dcc\u7ffb\uff0c\u8fd9\u5c11\u5e74\u6b66\u529f\u751a\u662f\u4e86\u5f97\uff0c\u4f46\u89c1\u4ed6\u811a\u672a\u6cbe\u5730\uff0c\u4fbf\u662f\u4e00\u4e2a\u5f39\u817f \u8e22\u51fa\uff0c\u63a5\u7740\u53cc\u638c\u4e00\u4e2a\u201c\u4ea4\u53c9\u5341\u5b57\u624b\u201d\u65a9\u4e0b\uff0c\u597d\u50cf\u95e8\u95e9\u4e00\u822c\uff0c\u4e00\u65a9\u4e00\u626d\uff0c\u4f46\u542c\u5f97\u201c\u5494\u5693\u201d \u4e00\u58f0\uff0c\u90a3\u4e2a\u60f3\u626d\u4ed6\u624b\u8155\u7684\u6b66\u58eb\uff0c\u81ea\u5df1\u7684\u624b\u81c2\u5374\u5148\u7ed9\u4ed6\u626d\u5f97\u8131\u4e86\u81fc\uff0c\u53e6\u4e00\u4e2a\u6b66\u58eb\u5219\u65e9\u7ed9\u4ed6 \u8e22\u7ffb\u4e86\u3002\u7a81\u53a5\u6b66\u58eb\u6700\u4f69\u670d\u6709\u672c\u9886\u7684\u4eba\uff0c\u6709\u51e0\u4e2a\u7981\u4e0d\u4f4f\u559d\u8d77\u91c7\u6765\uff0c\u597d\u6f02\u4eae\u7684\u6454\u8de4\u529f\u592b\uff01 \u3000\u3000\u84e6\u542c\u4e00\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u60f3\u627e\u6b7b\u5417\uff1f\u201d\u4e00\u4e2a\u5b88\u62a4\u5728\u5e10\u5e55\u524d\u9762\u7684\u866c\u9aef\u6b66\u58eb\uff0c\u8eab\u624b\u77eb \u6377\u4e4b\u6781\u58f0\u53d1\u4eba\u5230\uff0c\u53cc\u638c\u4e00\u5708\uff0c\u90a3\u5c11\u5e74\u6b66\u58eb\u7ed9\u4ed6\u5c01\u7740\uff0c\u56db\u6761\u80f3\u81c2\u4e00\u9635\u7ffb\u817e\uff0c\u4fbf\u542c\u5f97\u201c\u84ec\u201d \u7684\u4e00\u58f0\uff0c\u90a3\u5c11\u5e74\u6b66\u58eb\u8dc4\u8dc4\u8ddf\u8ddf\u7684\u5012\u9000\u51e0\u6b65\u3002\u8fd9\u866c\u9aef\u6b66\u58eb\u4e00\u4e0a\uff0c\u4ed6\u7684\u4f19\u4f34\u4eec\u4fbf\u5373\u9000\u4e0b\uff0c\u770b \u6765\u4ed6\u4e43\u662f\u8fd9\u7fa4\u6b66\u58eb\u7684\u9886\u8896\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u6b66\u58eb\u5140\u81ea\u4e0d\u80af\u9003\u8d70\uff0c\u62d4\u51fa\u4f69\u5200\uff0c\u53c8\u518d\u6251\u4e0a\uff0c\u866c\u9aef\u6b66\u58eb\u4e5f\u62d4\u5200\u76f8\u8fce\uff0c\u53cc\u65b9\u90fd\u4f7f \u5f97\u6ca1\u98ce\u4f3c\u7684\u5feb\u5200\uff0c\u4f46\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u90a3\u5c11\u5e74\u6b66\u58eb\u7684\u4f69\u5200\u7ed9\u65a9\u4e86 \u4e24\u4e2a\u7f3a\u53e3\uff0c\u4ecd\u7136\u662f\u9ad8\u547c\u9163\u6597\uff0c\u594b\u6218\u4e0d\u5df2\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5e10\u6155\u5ffd\u7136\u63ed\u5f00\uff0c\u90a3\u7f8e\u8c8c\u7684\u738b\u5983\u8d70\u4e86\u51fa\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u4f4f\u624b\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6b66\u58eb\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u5361\u6d1b\u4e1d\uff01\u201d\u58f0\u97f3\u98a4\u6296\uff0c\u5145\u6ee1\u4e86\u559c\u60a6\u800c\u53c8\u6fc0\u52a8\u7684\u5fc3\u60c5\u3002\u90a3\u7f8e \u8c8c\u7684\u738b\u5983\u5ffd\u5730\u51b7\u51b7\u4e00\u8bf4\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u4e0d\u8bb8\u5411\u524d\u518d\u8de8\u8fdb\u4e00\u6b65\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6b66\u58eb\u60ca\u6115\u65e0\u6bd4\uff0c\u53eb\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e48\uff1f\u201d\u90a3\u7f8e\u8c8c\u7684\u738b\u5983\u8bf4\u9053\uff1a \u201c\u6c99\u5c14\u6d77\uff0c\u4f60\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u662f\u6211\u7236\u738b\u53eb\u4f60\u6765\u7684\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u6b66\u58eb\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u6211\u62fc \u4e86\u6027\u547d\u6765\u89c1\u4f60\u4e00\u9762\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u90a3\u7f8e\u8c8c\u7684\u738b\u5983\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u6562\u5bf9\u6211\u8bf4\u8fd9\u6837\u7684 \u8bdd\uff0c\u6211\u82e5\u4e0d\u5ff5\u5728\u4f60\u662f\u6211\u5c0f\u65f6\u5019\u7684\u670b\u53cb\uff0c\u6211\u65e9\u5c31\u53eb\u4ed6\u4eec\u6253\u65ad\u4f60\u7684\u817f\u5566\u3002\u201d\u90a3\u5c11\u5e74\u6b66\u58eb\u98a4\u58f0 \u8bf4\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u4f60\uff0c\u4f60\u2014\u2014\u4f60\u53d8\u4e86\u53e6\u4e00\u4e2a\u4eba\u5566\uff0c\u597d\u5440\uff0c\u4f60\u5982\u4eca\u5230\u738b\u5ef7\u53bb\u4eab\u53d7\u8363\u534e\u5bcc\u8d35\uff0c \u6211\u7ed9\u4f60\u9001\u884c\uff0c\u4f60\u4e5f\u4e0d\u4e50\u610f\u4e48\uff1f\u563f\uff0c\u563f\uff0c\u563f\uff0c\u563f\uff01\u54c8\uff0c\u54c8\u3001\u54c8\uff01\u201d\u4ed6\u6124\u6fc0\u4e4b\u6781\uff0c\u51b7\u7b11\u4e0d\u5df2\uff0c \u4e00\u53cc\u773c\u775b\u7d27\u7d27\u7684\u76ef\u7740\u738b\u5983\uff0c\u4ed6\u600e\u4e5f\u6599\u60f3\u4e0d\u5230\uff0c\u4ed6\u7684\u201c\u5361\u6d1b\u4e1d\u201d\u7a81\u7136\u53d8\u4e86\uff0c\u53d8\u5f97\u597d\u50cf\u964c\u751f \u4eba\u4e00\u6837\uff0c\u4ed6\u5b8c\u5168\u4e0d\u8ba4\u8bc6\u4e86\u3002 \u3000\u3000\u90a3\u7f8e\u8c8c\u7684\u738b\u5983\u8eab\u8eaf\u5fae\u5fae\u6296\u4e86\u4e00\u4e0b\uff0c\u7acb\u5373\u53c8\u9547\u5b9a\u5982\u5e38\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u5982\u4eca\u5df2 \u7ecf\u89c1\u8fc7\u6211\u4e86\uff0c\u4f60\u56de\u53bb\u5427\uff01\u201d\u5979\u90a3\u51b7\u9177\u7684\u795e\u60c5\u4ee4\u5f97\u6c99\u5c14\u6d77\u597d\u50cf\u5341\u4e8c\u6708\u5929\u8dcc\u843d\u51b0\u6cb3\u4e00\u6837\uff0c\u51b7 \u610f\u76f4\u900f\u5fc3\u5934\uff0c\u518d\u4e5f\u7b11\u4e0d\u51fa\u6765\u3002\u500f\u7136\u95f4\uff0c\u4ed6\u53cc\u7709\u4e00\u626c\uff0c\u7741\u5927\u773c\u775b\u8bf4\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u4f60\u771f\u7684 \u613f\u610f\u53bb\u505a\u5927\u6c57\u7684\u53ef\u8d3a\u6566\uff1f\u201d\u90a3\u738b\u5983\u8f7b\u8511\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4ee5\u6211\u7684\u7f8e\u8c8c\uff0c\u4ee5\u6211\u7684\u8eab\u4efd\uff0c\u96be\u9053 \u4e0d\u914d\u505a\u53ef\u8d3a\u6566\u4e48\uff1f\u9664\u4e86\u5927\u6c57\uff0c\u8fd8\u6709\u8c01\u914d\u5f97\u4e0a\u6211\uff1f\u201d\u90a3\u5c11\u5e74\u5927\u53eb\u4e00\u58f0\uff0c\u5446\u4e86\u534a\u664c\u3002\u5ffd\u9053\uff1a \u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff01\u6211\u4e0d\u76f8\u4fe1\uff0c\u6211\u4e0d\u76f8\u4fe1\uff01\u201d\u90a3\u7f8e\u8c8c\u7684\u738b\u5983\u628a\u624b\u4e00\u6325\uff0c\u559d\u9053\uff1a\u201c\u628a\u4ed6\u8fd9\u5339\u9a6c \u5c04\u6b7b\uff01\u201d\u4e00\u4e2a\u7a81\u53a5\u6b66\u58eb\u5e94\u58f0\u53d1\u7bad\u3002\u90a3\u5c11\u5e74\u6b66\u571f\u5446\u82e5\u6728\u9e21\uff0c\u90a3\u5339\u9a6c\u672c\u6765\u662f\u4ed6\u6700\u5fc3\u7231\u7684\u5b9d\u9a6c\uff0c \u4e5f\u662f\u4ed6\u7684\u201c\u5361\u6d1b\u4e1d\u201d\u4ee5\u524d\u975e\u5e38\u559c\u6b22\u7684\u4e00\u5339\u9a6c\uff0c\u7136\u800c\u73b0\u5728\u7adf\u4ece\u5361\u6d1b\u4e1d\u7684\u53e3\u4e2d\u53d1\u51fa\u547d\u4ee4\uff0c\u6b7b \u5728\u6b66\u58eb\u7684\u7bad\u4e0b\u4e86\uff01 \u3000\u3000\u90a3\u7f8e\u8c8c\u7684\u738b\u5983\u51b7\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u80fd\u4e0d\u80fd\u518d\u8ffd\u8d76\u6211\uff1f\u4f60\u518d\u4e0d\u8d70\uff0c\u7b2c\u4e8c\u652f\u7bad\u5c31\u8981\u5411\u4f60\u5c04 \u4e86\uff01\u201d\u5c11\u5e74\u6b66\u58eb\u4f24\u5fc3\u4e4b\u6781\uff0c\u9762\u8272\u60e8\u767d\u5982\u7eb8\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u6015\u4e07\u7bad\u7a7f\u5fc3\uff0c\u53ef\u662f\u4f60\u7684\u8bdd\u6bd4\u5229 \u7bad\u5389\u5bb3\u4e07\u500d\uff0c\u6211\u7684\u5fc3\u7b97\u662f\u6b7b\u5566\u3002\u5361\u6d1b\u4e1d\uff0c\u4f60\u81ea\u5df1\u4fdd\u91cd\uff0c\u6c99\u5c14\u6d77\u4e0d\u80fd\u518d\u4f8d\u4faf\u4f60\u4e86\uff01\u201d\u63a9\u9762 \u8f6c\u8eab\uff0c\u5982\u98de\u75be\u8dd1\uff0c\u4f46\u8dd1\u4e86\u5341\u591a\u4e08\u8fdc\uff0c\u5374\u53c8\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u738b\u5983\u8fd8\u7ad9\u5728\u54ea\u91cc\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c \u6c99\u5c14\u6d77\u53c8\u53eb\u4e86\u4e00\u58f0\u201c\u5361\u6d1b\u4e1d\uff01\u201d\u738b\u5983\u5ffd\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u8f6c\u8eab\u5165\u5e10\u7bf7\uff0c\u968f\u5373\u5728\u5e10\u4e2d\u4f20\u51fa\u8bdd\u9053\uff1a \u201c\u62d4\u961f\u8d77\u7a0b\uff01\u201d\u5927\u961f\u7684\u6b66\u58eb\u6536\u62fe\u8d77\u5e10\u7bf7\uff0c\u524d\u547c\u540e\u62e5\uff0c\u5c06\u738b\u5983\u62e5\u4e0a\u9a6c\u8f66\uff0c\u629b\u4e0b\u4e86\u90a3\u5c11\u5e74\u6b66 \u58eb\uff0c\u679c\u7136\u8d70\u4e86\uff01 \u3000\u3000\u6b66\u7384\u971c\u8eb2\u5728\u6c99\u4e18\u540e\u9762\uff0c\u76ee\u7779\u4e86\u8fd9\u4e00\u5e55\u60c5\u666f\uff0c\u751a\u662f\u66ff\u90a3\u5c11\u5e74\u6b66\u58eb\u4e0d\u5e73\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u542c\u4ed6\u4eec\u7684\u8bf4\u8bdd\uff0c\u8fd9\u4e2a\u5361\u6d1b\u4e1d\u539f\u6765\u8fd8\u672a\u4e0e\u53ef\u6c57\u6210\u4eb2\uff0c\u5927\u7ea6\u8fd9\u4e9b\u7a81\u53a5\u6b66\u58eb\u6b63\u662f\u62a4\u9001\u5979\u5230\u7a81 \u53a5\u7684\u738b\u5ef7\u6210\u4eb2\u53bb\u7684\u3002\u8fd9\u4e2a\u6c99\u5c14\u6d77\u5f53\u7136\u662f\u5979\u7684\u60c5\u4eba\uff0c\u4ed6\u6562\u820d\u4e86\u6027\u547d\u524d\u6765\u6c42\u89c1\u4e00\u9762\uff0c\u4e5f\u7b97\u5f97 \u75f4\u60c5\u6781\u4e86\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u8d70\u4e86\u51fa\u6765\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u9690\u9690\u8fd8\u53ef\u4ee5\u671b\u89c1\u90a3\u5c11\u5e74\u6b66\u58eb\u7684\u5f71\u5b50\uff0c\u5728\u6cb3\u5cb8\u6811\u836b\u4e4b\u4e0b\uff0c \u8e3d\u8e3d\u72ec\u884c\u3002\u6b66\u7384\u971c\u5c55\u5f00\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u8f7b\u8eab\u672c\u9886\uff0c\u6084\u6084\u65e0\u58f0\u7684\u6765\u5230\u4ed6\u7684\u80cc\u540e\uff0c\u4f46\u542c\u5f97\u4ed6 \u5140\u81ea\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff01\u6211\u4e0d\u76f8\u4fe1\uff0c\u6211\u4e0d\u76f8\u4fe1\uff01\u201d\u6b66\u7384\u971c\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c \u6211\u4e5f\u4e0d\u4fe1\uff01\u201d\u6c99\u5c14\u6d77\u6115\u7136\u56de\u987e\uff0c\u89c1\u6b66\u7384\u971c\u662f\u4e2a\u7f8e\u8c8c\u7684\u6c49\u65cf\u59d1\u5a18\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u4f60 \u8bf4\u4ec0\u4e48\uff1f\u4f60\u662f\u8c01\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u548c\u5361\u6d1b\u4e1d\u4f1a\u9762\u7684\u60c5\u5f62\uff0c\u6211\u5168\u90fd\u770b\u5230\u4e86\u3002\u4f60\u521a\u624d\u548c\u90a3 \u866c\u9aef\u6b66\u58eb\u6bd4\u5200\uff0c\u4ed6\u6709\u4e00\u5200\u4e0a\u624b\u5200\uff0c\u4e2d\u9014\u5ffd\u7136\u6539\u4e3a\u4e0b\u624b\u5200\uff0c\u90a3\u4e00\u5200\u672c\u6765\u53ef\u4ee5\u65ab\u4e2d\u4f60\u7684\uff0c\u4f46 \u4ed6\u7684\u5200\u950b\u5ffd\u7136\u6b6a\u4e86\u534a\u5bf8\uff0c\u7ed9\u4f60\u6321\u5f00\uff0c\u4f60\u77e5\u9053\u5176\u4e2d\u7684\u539f\u6545\u5417\uff1f\u201d\u6c99\u5c14\u6d77\u542c\u5979\u8bf4\u5f97\u5386\u5386\u5982\u7ed8\uff0c \u60ca\u8be7\u4e0d\u5df2\uff0c\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u5728\u6697\u4e2d\u5e2e\u6211\u7684\u5fd9\u5417\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u7528\u4e00\u7c92\u7802 \u5b50\u5c06\u4ed6\u7684\u5200\u5c16\u5f39\u4e86\u4e00\u4e0b\uff0c\u5e78\u4e8f\u4ed6\u6ca1\u53d1\u89c9\u3002\u201d\u6c99\u5c14\u6d77\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u53d1\u89c9\u5440\uff0c\u4f60\uff0c\u4f60\u662f\u4ec0\u4e48 \u4eba\uff1f\u6709\u8fd9\u4e48\u5927\u7684\u672c\u9886\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5929\u5c71\u5251\u5ba2\uff01\u201d\u201c\u5929\u5c71\u5251\u5ba2\u201d\u7684\u540d\u5b57\u4f20\u904d\u5929\u5c71\u5357\u5317\uff0c \u6b66\u7384\u971c\u6599\u60f3\u4ed6\u66fe\u542c\u8fc7\uff0c\u6c99\u5c14\u6d77\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u5c31\u662f\u5929\u5c71\u5251\u5ba2\uff0c\u602a\u4e0d\u5f97\u6709\u8fd9\u6837\u5927 \u7684\u672c\u9886\uff01\u201d\u4f46\u968f\u5373\u4fbf\u9732\u51fa\u60f6\u60d1\u7684\u795e\u60c5\uff0c\u51dd\u89c6\u7740\u6b66\u7384\u971c\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u5c71\u5251\u5ba2\u542c\u8bf4\u662f\u4e2a\u7537\u7684\uff0c \u539f\u6765\u90a3\u662f\u5047\u7684\u5417\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u90a3\u662f\u6211\u7684\u54e5\u54e5\uff0c\u6211\u4eec\u5144\u59b9\u4e8c\u4eba\uff0c\u540c\u4f4f\u5929\u5c71\uff0c\u751f\u6765\u7231\u7ba1 \u95f2\u4e8b\uff0c\u8f6e\u6d41\u4e0b\u5c71\uff0c\u522b\u4eba\u4e0d\u77e5\uff0c\u5c06\u6211\u4eec\u90fd\u53eb\u505a\u5929\u5c71\u5251\u5ba2\u3002\u201d\u6b66\u7384\u971c\u5047\u5192\u201c\u5929\u5c71\u5251\u5ba2\u201d\u7684\u540d \u5934\uff0c\u4e43\u662f\u60f3\u53d6\u5f97\u4ed6\u7684\u4fe1\u4efb\uff0c\u6c99\u5c14\u6d77\u89c1\u5979\u6709\u8fd9\u6837\u9ad8\u7684\u672c\u9886\uff0c\u4e14\u53c8\u66fe\u6697\u4e2d\u5e2e\u52a9\u81ea\u5df1\uff0c\u679c\u7136\u6df1 \u4fe1\u4e0d\u7591\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u4e0d\u76f8\u4fe1\uff0c\u662f\u4e0d\u76f8\u4fe1\u5361\u6d1b\u4e1d\u4f1a\u8fd9\u6837\u5bf9\u5f85\u4f60\u5417\uff1f\u201d\u6c99\u5c14\u6d77\u9053\uff1a\u201c\u6211 \u4e0d\u76f8\u4fe1\u5979\u4f1a\u5fc3\u7518\u60c5\u613f\u53bb\u505a\u53ef\u6c57\u7684\u5983\u5b50\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u4e0d\u76f8\u4fe1\u5979\u8fd9\u6837\u7f8e\u8c8c\u5982 \u82b1\uff0c\u5fc3\u80a0\u5374\u4f1a\u90a3\u6837\u7684\u72e0\uff01\u53ef\u662f\uff0c\u5979\u505a\u51fa\u7684\u90a3\u4e9b\u4e8b\u60c5\uff0c\u5374\u90fd\u662f\u6211\u4eb2\u773c\u89c1\u7684\uff0c\u771f\u662f\u4ee4\u4eba\u96be\u4ee5 \u76f8\u4fe1\uff01\u201d\u8fd9\u756a\u8bdd\u8bf4\u662f\u201c\u4e0d\u4fe1\u201d\uff0c\u5b9e\u662f\u201c\u76f8\u4fe1\u201d\uff0c\u6c99\u5c14\u6d77\u6fc0\u52a8\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u4e0d\u4f1a\u61c2\u5f97\u7684\uff0c \u6211\u8d70\u5f00\u4e4b\u540e\uff0c\u56de\u5934\u671b\u5979\u4e00\u773c\uff0c\u6211\u4ece\u5979\u7684\u773c\u5149\u4e4b\u4e2d\uff0c\u611f\u89c9\u5230\u662f\u4ee5\u524d\u7684\u5361\u6d1b\u4e1d\uff01\u8fd9\u611f\u89c9\u96be\u4ee5 \u89e3\u91ca\uff0c\u4f60\uff0c\u4f60\u61c2\u5f97\u5417\uff1f\u201d\u6b66\u7384\u971c\u5fae\u54bd\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e4b\u95f4\u7684\u5fc3\u610f\uff0c\u53ea\u6709\u5979\u5fc3\u7231\u7684\u4eba\u624d\u4f1a\u611f \u89c9\u51fa\u6765\u3002\u4f60\u53ef\u4ee5\u5c06\u4f60\u4eec\u7684\u4e8b\u60c5\u544a\u8bc9\u7ed9\u6211\u542c\u5417\uff1f\u4e5f\u8bb8\u6211\u53ef\u4ee5\u5e2e\u4f60\u7684\u5fd9\u3002\u201d \u3000\u3000\u6c99\u5c14\u6d77\u62b9\u5e72\u4e86\u6cea\u75d5\uff0c\u8bf4\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\u7684\u7236\u4eb2\u662f\u7a81\u53a5\u53ef\u6c57\u4e00\u4e2a\u5c5e\u56fd\u7684\u85e9\u738b\uff0c\u6211\u7684\u7236\u4eb2 \u662f\u4ed6\u6700\u4eb2\u4fe1\u7684\u4e00\u4e2a\u6b66\u58eb\uff0c\u6211\u548c\u5361\u6d1b\u4e1d\u81ea\u5e7c\u4e00\u540c\u73a9\u800d\uff0c\u4e00\u540c\u957f\u5927\uff0c\u6bd4\u5144\u59b9\u8fd8\u8981\u4eb2\u5bc6\u3002\u201d\u8bf4 \u5230\u8fd9\u91cc\uff0c\u4ed6\u6709\u70b9\u7f9e\u6da9\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u8fd9\u624d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5979\u597d\u51e0\u6b21\u8bf4\u8fc7\uff0c\u9664\u4e86\u6211\u4e0d\u4f1a\u518d\u5ac1\u522b \u4eba\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5411\u5979\u6c42\u5a5a\uff1f\u201d\u6c99\u5c14\u6d77\u82e6\u7b11\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e00\u4e2a\u666e\u901a\u7684\u6b66\u58eb\uff0c \u4e0e\u5979\u8eab\u4efd\u60ac\u6b8a\uff0c\u600e\u597d\u5411\u53ef\u6c57\u542f\u9f7f\uff1f\u5361\u6d1b\u4e1d\u77e5\u9053\u6211\u7684\u5fc3\u610f\uff0c\u5979\u8bf4\u53cd\u6b63\u54b1\u4eec\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u5979\u613f \u610f\u7b49\u6211\uff0c\u7b49\u6211\u5efa\u529f\u7acb\u4e1a\uff0c\u6709\u4e86\u4e00\u5b98\u534a\u804c\u4e4b\u540e\uff0c\u90a3\u65f6\u518d\u6258\u4eba\uff0c\u5411\u5979\u7236\u738b\u6c42\u5a5a\uff0c\u4e00\u5b9a\u4f1a\u7b54\u5e94\u3002 \u8fd9\u51e0\u5e74\u6765\uff0c\u6709\u8fc7\u4e0d\u5c11\u738b\u516c\u8d35\u65cf\uff0c\u751a\u81f3\u5916\u90a6\u58ec\u5b50\u5411\u5979\u6c42\u5a5a\uff0c\u5979\u90fd\u6ca1\u6709\u7b54\u5e94\uff0c\u679c\u7136\u662f\u4e00\u5fc3\u4e00 \u610f\u7684\u5728\u7b49\u6211\u3002\u8c01\u77e5\u9053\u8fd9\u6b21\u5927\u6c57\u8058\u5979\u53bb\u505a\u738b\u5983\uff0c\u5979\u7adf\u7136\u4e8c\u8bdd\u4e0d\u8bf4\uff0c\u5c31\u8ba9\u5979\u7236\u4eb2\u5c06\u5979\u9001\u8d70\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u8fd9\u4e8b\u4f60\u4e0d\u77e5\u9053\u5417\uff1f\u4f60\u6709\u548c\u5979\u8bf4\u8fc7\u8bdd\u4e48\uff1f\u201d\u6c99\u5c14\u6d77\u9053\uff1a\u201c\u524d\u4e24\u4e2a\u6708\uff0c\u6211 \u56fd\u4e2d\u8003\u9009\u6b66\u58eb\uff0c\u6211\u53d6\u5f97\u4e86\u7b2c\u4e00\u540d\u52c7\u58eb\u7684\u79f0\u53f7\uff0c\u53ef\u6c57\u5bf9\u6211\u8d4f\u8d50\u6709\u52a0\uff0c\u5347\u6211\u505a\u4ed6\u7684\u62a4\u9a7e\u6b66\u58eb\uff0c \u6211\u6b63\u60f3\u8d81\u6b64\u673a\u4f1a\u6258\u6211\u7236\u4eb2\u5411\u53ef\u6c57\u6c42\u5a5a\uff0c\u8c01\u77e5\u4e0d\u4e45\uff0c\u53ef\u6c57\u6d3e\u6211\u4ee3\u8868\u4ed6\u5230\u8fb9\u5883\u53bb\u5de1\u67e5\uff0c\u5f85\u5230 \u5f52\u6765\u4e4b\u65f6\uff0c\u5361\u6d1b\u4e1d\u5df2\u7ecf\u88ab\u5927\u6c57\u8058\u53bb\u505a\u53ef\u8d3a\u6566\u4e86\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0c\u53ef\u6c57\u60f3\u5fc5\u4e5f \u77e5\u9053\u4f60\u4e8b\u60c5\uff0c\u6240\u4ee5\u6709\u610f\u5c06\u4f60\u9063\u8d70\u7684\u3002\u201d\u6c99\u5c14\u6d77\u9053\uff1a\u201c\u4e0d\u4f46\u53ef\u6c57\u77e5\u9053\uff0c\u6211\u7684\u7236\u4eb2\u4e5f\u65e9\u5df2\u770b \u51fa\u6211\u548c\u5361\u6d1b\u4e1d\u7684\u604b\u60c5\uff0c\u6211\u56de\u6765\u4e4b\u540e\uff0c\u4ed6\u5c31\u529d\u6211\u4e0d\u8981\u518d\u75f4\u5fc3\u5984\u60f3\uff0c\u540c\u65f6\uff0c\u53ef\u6c57\u4e5f\u5347\u4e86\u6211\u4e09 \u7ea7\uff0c\u53eb\u6211\u505a\u738b\u5ef7\u6b66\u961f\u957f\u3002\u6211\u77e5\u9053\u4ed6\u662f\u60f3\u5b89\u6170\u6211\uff0c\u53ef\u662f\u5361\u6d1b\u4e1d\u5df2\u7ecf\u8d70\u4e86\u6211\u5373\u7b97\u505a\u5230\u53ef\u6c57\uff0c \u53c8\u6709\u4ec0\u4e48\u610f\u601d\u3002\u201d\u6b66\u7384\u971c\u95ee\u9053\uff1a\u201c\u8fd9\u4e48\u62da\u547d\uff0c\u90a3\u4f60\u662f\u76f4\u5230\u4eca\u5929\uff0c\u624d\u89c1\u7740\u5361\u6d1b\u4e1d\u7684\u4e86\uff01\u201d \u6c99\u5c14\u6d77\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u56de\u6765\u4e4b\u540e\uff0c\u60f3\u4e86\u6574\u6574\u4e00\u4e2a\u665a\u4e0a\uff0c\u6211\u60f3\u5361\u6d1b\u4e1d\u7edd\u5bf9\u4e0d\u662f\u8d2a\u604b \u8363\u534e\u5bcc\u8d35\u7684\u4eba\uff0c\u5979\u4ee5\u524d\u4e5f\u66fe\u5bf9\u6211\u8bf4\u8fc7\uff0c\u82e5\u662f\u6211\u4eec\u80fd\u591f\u5982\u613f\u4ee5\u507f\uff0c\u5979\u5b81\u613f\u653e\u4e0b\u516c\u4e3b\u4e0d\u505a\u3002 \u4e0e\u6211\u5728\u8349\u539f\u98d8\u6cca\u4ea6\u6240\u5fc3\u7518\u3002\u6211\u60f3\u4e86\u53c8\u60f3\uff0c\u5e78\u597d\u5979\u53ea\u8d70\u4e86\u4e09\u5929\uff0c\u6211\u4fbf\u9a91\u4e86\u5343\u91cc\u9a6c\u6765\u8ffd\u5979\uff0c \u53ea\u8981\u5979\u4e0d\u53d8\u5fc3\uff0c\u6211\u820d\u4e86\u6027\u547d\u4e5f\u8981\u5c06\u5979\u6551\u51fa\u6765\uff0c\u5927\u6c57\u867d\u7136\u6709\u767e\u4e07\u96c4\u5175\uff0c\u5a01\u4e34\u4e07\u56fd\uff0c\u53ef\u662f\u8349 \u539f\u5982\u6b64\u5e7f\u5927\uff0c\u54ea\u91cc\u627e\u4e0d\u5230\u85cf\u8eab\u4e4b\u5730\uff1f\u5440\uff0c\u60f3\u662f\u5982\u6b64\u60f3\uff0c\u53ef\u60dc\u5361\u6d1b\u4e1d\u7adf\u7136\u4eb2\u53e3\u8bf4\u51fa\u5979\u613f\u610f \u53bb\u505a\u53ef\u8d3a\u6566\uff0c\u8fd8\u53eb\u4eba\u5c04\u6bd9\u4e86\u6211\u7684\u9a6c\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u7edd\u671b\u4e86\u4e48\uff1f\u201d\u6c99\u5c14\u6d77\u9053\uff1a\u201c\u5979\u867d\u7136\u5982\u6b64\u5bf9\u5f85\u6211\uff0c\u53ef\u662f\u6211\u8fd8\u4e0d\u6562 \u76f8\u4fe1\u8fd9\u662f\u5979\u7684\u771f\u610f\uff0c\u5979\u8981\u662f\u771f\u53d8\u4e86\u5fc3\uff0c\u6211\u56de\u5934\u671b\u5979\u4e4b\u65f6\uff0c\u5979\u5c31\u4e0d\u4f1a\u7528\u90a3\u6837\u773c\u5149\u770b\u6211\u7684\u3002\u201d \u6b66\u7384\u971c\u9053\uff1a\u201c\u90a3\u4f60\u73b0\u5728\u6253\u7b97\u600e\u6837\uff1f\u201d\u6c99\u5c14\u6d77\u4f24\u5fc3\u4e4b\u6781\uff0c\u626d\u7ede\u624b\u6307\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5343\u91cc\u9a6c\u5df2 \u7ed9\u5979\u5c04\u6b7b\u4e86\uff0c\u8981\u8ffd\u4e5f\u8ffd\u4e0d\u4e0a\u5566\uff0c\u6211\u8fd9\u4e00\u751f\u518d\u4e5f\u4e0d\u4f1a\u542c\u5230\u5979\u7684\u771f\u8bdd\u4e86\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4fe1\u4e0d\u4fe1\u6211\uff1f\u201d\u6c99\u5c14\u6d77\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60 \u82e5\u4fe1\u6211\uff0c\u4f60\u4ea4\u4e00\u4ef6\u4fe1\u7269\u7ed9\u6211\uff0c\u6211\u53bb\u89c1\u5361\u6d1b\u4e1d\uff0c\u63a2\u8be2\u5979\u7684\u771f\u610f\u3002\u4f60\u5230\u5927\u6c57\u738b\u57ce\uff0c\u9690\u59d3\u57cb\u540d\uff0c \u7b49\u5f85\u6211\u7684\u597d\u97f3\u3002\u201d\u6c99\u5c14\u6d77\u4e8c\u8bdd\u4e0d\u8bf4\uff0c\u4fbf\u638f\u51fa\u4e86\u4e00\u53ea\u9999\u5305\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5361\u6d1b\u4e1d\u7ee3\u7ed9\u6211\u7684\uff0c \u4f60\u62ff\u53bb\u5427\u3002\u6211\u7236\u4eb2\u6709\u4f4d\u670b\u53cb\u4f4f\u5728\u738b\u57ce\uff0c\u6211\u4f1a\u5230\u4ed6\u5bb6\u4e2d\u501f\u4f4f\uff0c\u6253\u542c\u5361\u6d1b\u4e1d\u7684\u6d88\u606f\u3002\u201d\u968f\u5373 \u5c06\u5730\u5740\u544a\u8bc9\u4e86\u6b66\u7384\u971c\u3002 \u3000\u3000\u6b66\u7384\u971c\u85cf\u597d\u9999\u5305\uff0c\u4e0e\u4ed6\u544a\u522b\u3002\u5f53\u4e0b\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u76f4\u8d76\u5230\u4e09\u66f4\u65f6\u5206\uff0c\u624d\u53d1\u73b0\u90a3\u4e00\u7fa4 \u6b66\u58eb\u7684\u5e10\u5e55\u3002 \u3000\u3000\u6b66\u7384\u971c\u4e00\u770b\uff0c\u5341\u51e0\u5ea7\u5e10\u7bf7\uff0c\u53ea\u6709\u4f4d\u7f6e\u5f53\u4e2d\u7684\u4e00\u5ea7\uff0c\u5916\u9762\u6709\u4e24\u4e2a\u6b66\u58eb\u5b88\u536b\uff0c\u6b66\u7384\u971c\u60f3 \u9053\uff1a\u201c\u5728\u8fd9\u8352\u91ce\u4e4b\u4e0a\uff0c\u4eba\u8ff9\u5c11\u5230\uff0c\u4ed6\u4eec\u5374\u8fd8\u8981\u5c0f\u5fc3\u5b88\u536b\uff0c\u8fd9\u5fc5\u5b9a\u662f\u5361\u6d1b\u4e1d\u7684\u5e10\u7bf7\u4e86\u3002\u201d \u968f\u624b\u634f\u4e86\u4e24\u56e2\u96ea\u5757\uff0c\u5411\u7a7a\u4e2d\u4e00\u63b7\uff0c\u53d1\u51fa\u547c\u5587\u7684\u58f0\u97f3\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u597d\u751f\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9 \u4e48\u665a\u4e86\uff0c\u8fd8\u6709\u5140\u9e70\u98de\u7fd4\u4e48\uff1f\u201d\u62ac\u5934\u89c2\u770b\uff0c\u90a3\u4e24\u56e2\u96ea\u5757\uff0c\u7ed9\u6b66\u7384\u971c\u63b7\u5f97\u5f88\u9ad8\uff0c\u672a\u66fe\u8dcc\u4e0b\uff0c \u534a\u7a7a\u5c31\u6eb6\u5316\u4e86\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u770b\u4e86\u597d\u4e00\u4f1a\uff0c\u751a\u4e48\u90fd\u77a7\u4e0d\u89c1\uff0c\u66f4\u4e3a\u7eb3\u7f55\uff0c\u6b66\u7384\u971c\u65e9\u5c31\u8d81\u8fd9\u4e2a \u673a\u4f1a\uff0c\u6f5c\u5165\u5e10\u7bf7\u3002 \u3000\u3000\u5e10\u5e55\u5185\u8fb9\uff0c\u8fd8\u6709\u7ee3\u5e18\u9694\u5f00\uff0c\u5916\u95f4\u6709\u51e0\u4e2a\u4f8d\u5973\uff0c\u6216\u5750\u6216\u5367\uff0c\u6b66\u7384\u971c\u638c\u5fc3\u65e9\u5df2\u6263\u4e86\u51e0\u7c92 \u7802\u5b50\uff0c\u5979\u4ee5\u6781\u8f7b\u7075\u8fc5\u6377\u7684\u624b\u6cd5\u3002\u63ed\u5f00\u4e86\u5e10\u7bf7\u4e00\u89d2\uff0c\u4e00\u77a7\u6e05\u695a\uff0c\u4fbf\u5c06\u7802\u5b50\u8f7b\u8f7b\u5f39\u51fa\uff0c\u5c06\u90a3 \u51e0\u4e2a\u4f8d\u5973\u7684\u6655\u7761\u7a74\u90fd\u5c01\u4e86\uff0c\u82e5\u975e\u7ecf\u4eba\u89e3\u6551\uff0c\u975e\u5f97\u4e00\u4e2a\u534a\u65f6\uff0c\u4e0d\u80fd\u81ea\u9192\u3002 \u3000\u3000\u7ee3\u5e18\u5185\u9690\u9690\u6709\u70db\u5149\u900f\u51fa\uff0c\u6b66\u7384\u971c\u5728\u7f1d\u9699\u4e00\u77a7\uff0c\u679c\u7136\u662f\u5361\u6d1b\u4e1d\u5728\u91cc\u9762\uff0c\u591c\u5df2\u4e09\u66f4\uff0c\u5979 \u8fd8\u672a\u7761\uff0c\u53ea\u89c1\u5979\u5750\u5728\u9526\u57ab\u4e4b\u4e0a\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u6c99\u5c14\u6d77\uff0c\u6c99\u5c14\u6d77\uff0c\u4f60\u600e \u77e5\u9053\u6211\u7684\u82e6\u5fc3\u554a\uff01\u4f46\u613f\u4f60\u6068\u6211\u602a\u6211\uff0c\u5c31\u5f53\u505a\u8fd9\u4e16\u4e0a\u518d\u4e5f\u6ca1\u6709\u4e86\u6211\u5361\u6d1b\u4e1d\u8fd9\u4e2a\u4eba\uff0c\u8d81\u65e9\u6b7b \u4e86\u8fd9\u6761\u5fc3\uff0c\u4e5f\u514d\u5f97\u95ef\u4e0b\u5927\u7978\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u542c\u5230\u6b64\u5904\uff0c\u5fc3\u4e2d\u5927\u559c\uff1a\u201c\u679c\u7136\u5979\u5bf9\u6c99\u5c14\u6d77\u5e76\u975e\u5fd8\u60c5\u3002\u201d\u4e8e\u662f\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c \u63ed\u7bf7\u800c\u5165\u3002\u5361\u6d1b\u4e1d\u84e6\u7136\u770b\u89c1\u4e00\u4e2a\u964c\u751f\u7684\u6c49\u65cf\u59d1\u5a18\uff0c\u8d70\u5230\u5979\u7684\u9762\u524d\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5f20\u5f00\u4e86\u53e3\uff0c \u6b63\u60f3\u53eb\u558a\uff0c\u6b66\u7384\u971c\u5c06\u90a3\u53ea\u9999\u56ca\u5728\u5979\u9762\u524d\u4e00\u6643\uff0c\u4f4e\u4f4e\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u4f60 \u522b\u5bb3\u6015\uff0c\u6211\u662f\u6c99\u5c14\u6d77\u53eb\u6211\u6765\u770b\u4f60\u7684\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u5b9a\u4e86\u5b9a\u795e\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u6211\u4e0e\u4ed6\u7684\u4e8b\u60c5\uff0c\u6211\u4ece\u6765\u672a\u542c\u6c99\u5c14\u6d77 \u8bf4\u8d77\u8fc7\u4f60\uff01\u201d\u6b66\u7384\u971c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5929\u5c71\u5251\u5ba2\uff0c\u4eca\u65e5\u6c99\u5c14\u6d77\u4e0e\u4f60\u4f1a\u9762\u7684\u60c5\u5f62\uff0c \u6211\u5168\u90fd\u77a7\u89c1\u4e86\u3002\u201d\u63a5\u7740\u53c8\u5c06\u548c\u6c99\u5c14\u6d77\u8c08\u8bdd\u7684\u7ecf\u8fc7\u544a\u8bc9\u5979\u3002\u5361\u6d1b\u4e1d\u4e5f\u66fe\u542c\u5f97\u6c99\u5c14\u6d77\u8bf4\u8fc7 \u201c\u5929\u5c71\u5251\u5ba2\u201d\u7684\u4e8b\u8ff9\uff0c\u53c8\u89c1\u5979\u6709\u81ea\u5df1\u9001\u7ed9\u6c99\u5c14\u6d77\u7684\u90a3\u53ea\u9999\u56ca\uff0c\u7591\u8651\u4e4b\u5fc3\u5c3d\u53bb\uff0c\u4fbf\u8bf7\u6b66\u7384 \u971c\u5750\u4e0b\uff0c\u5e7d\u5e7d\u53f9\u4e86\u4e00\u53e3\u6c14\u95ee\u9053\uff1a\u201c\u6c99\u5c14\u6d77\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u5fc3\u4e48\uff1f\u6211\u53ea\u5f53\u4ed6\u5c06\u6211\u6068\u5165\u9aa8\u9ad3\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u6c99\u5c14\u6d77\u4e00\u70b9\u4e5f\u6ca1\u6709\u6068\u4f60\uff0c\u4ed6\u6df1\u6df1\u77e5\u9053\u4f60\u5fc3\u91cc\u8fd8\u662f\u6b22\u559c\u4ed6\u7684\u3002\u6240\u4ee5\u4ed6\u8981 \u6211\u6765\u63a2\u95ee\u4f60\u7684\u771f\u610f\uff0c\u5012\u662f\u6211\u4e0d\u660e\u767d\uff0c\u4ed6\u5bf9\u4f60\u5982\u6b64\u75f4\u60c5\uff0c\u4f60\u5374\u4e3a\u4f55\u90a3\u6837\u5bf9\u5f85\u4ed6\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u773c\u89d2\u6709\u6676\u83b9\u7684\u6cea\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6bd4\u4ed6\u8fd8\u8981\u75db\u82e6\u4e07\u5206\uff0c\u53ef\u662f\u8bf4\u4e86\u53c8\u6709\u4ec0\u4e48\u7528\uff1f \u5c31\u8ba9\u6211\u4e00\u4e2a\u4eba\u53d7\u82e6\u5427\u3002\u201d\u6b66\u7384\u971c\u7d27\u7d27\u63e1\u7740\u5979\u7684\u624b\uff0c\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u4e5f\u8bb8\u6211\u80fd\u5e2e\u52a9\u4f60\u3002\u5373 \u7b97\u5e2e\u4e0d\u4e86\u5fd9\uff0c\u4f60\u8bf4\u4e86\u51fa\u6765\uff0c\u4e5f\u603b\u6bd4\u95f7\u5728\u5fc3\u5934\u7684\u597d\u3002\u6211\u662f\u4e2a\u6c49\u4eba\uff0c\u548c\u4f60\u4eec\u7684\u4eba\u5168\u90fd\u6ca1\u6709\u5173 \u7cfb\uff0c\u4f60\u8bf4\u7ed9\u6211\u542c\uff0c\u6211\u51b3\u4e0d\u4f1a\u5411\u4efb\u4f55\u4eba\u6cc4\u6f0f\u534a\u53e5\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u95ee\u9053\uff1a\u201c\u4f60\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u53ef\u66fe\u7559\u610f\u5916\u9762\u7684\u4f8d\u5973\uff1f\u4e0d\u77e5\u5979\u4eec\u7761\u4e86\u6ca1\u6709\uff1f\u201d\u6b66\u7384 \u971c\u7b11\u9053\uff1a\u201c\u4e0d\u5230\u6211\u53bb\u5524\u5979\u4eec\uff0c\u5979\u4eec\u51b3\u4e0d\u4f1a\u9192\u6765\u3002\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a \u201c\u56e0\u4e3a\u5979\u4eec\u90fd\u7ed9\u6211\u70b9\u4e86\u6655\u7761\u7684\u7a74\u9053\u3002\u201d\u5c06\u6b66\u5b66\u4e2d\u70b9\u7a74\u7684\u4f5c\u7528\u6548\u679c\u7b80\u7565\u7684\u5bf9\u5361\u6d1b\u4e1d\u8bf4\u4e86\u3002 \u5361\u6d1b\u4e1d\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u4f69\u670d\uff0c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u662f\u4f1a\u4ed9\u6cd5\u7684\u4e48\uff1f\u4f60\u7684\u672c\u9886\u771f\u662f\u5927\u5f97\u4e0d\u53ef \u601d\u4e49\uff01\u201d\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u51e0\u4e2a\u4f8d\u5973\u90fd\u662f\u6211\u7684\u5fc3\u8179\uff0c\u7ed9\u5979\u4eec\u542c\u4e86\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u4e8e\u662f\u5361\u6d1b\u4e1d\u559d\u4e86\u4e00\u65e5\u6d53\u8336\uff0c\u6da6\u6da6\u5589\u5499\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u7236\u4eb2\u7684\u738b\u56fd\u5728\u5927\u6f20\u5317\u8fb9\uff0c\u963f \u5c14\u6cf0\u5c71\u4e4b\u4e0b\uff0c\u738b\u56fd\u5c0f\u5f97\u53ef\u601c\uff0c\u7586\u571f\u53ea\u6709\u4e09\u767e\u91cc\u4e4b\u5730\uff0c\u4eba\u53e3\u4e0d\u8fc7\u5341\u4e07\u4e4b\u4f17\uff0c\u597d\u5728\u56fd\u4e2d\u6c34\u571f \u80a5\u7f8e\uff0c\u6709\u5929\u7136\u7684\u7267\u573a\uff0c\u8fd8\u6709\u91d1\u77ff\uff0c\u6211\u4eec\u5e74\u5e74\u5411\u5927\u6c57\u7eb3\u8d21\uff0c\u65e5\u5b50\u5012\u4e5f\u8fc7\u5f97\u9887\u4e3a\u5b89\u9038\u3002 \u3000\u3000\u4e09\u4e2a\u6708\u524d\uff0c\u5927\u6c57\u7684\u4f7f\u8005\u5230\u6765\uff0c\u5411\u6211\u7684\u7236\u4eb2\u8981\u6c42\u4e00\u4ef6\u8d21\u7269\uff0c\u90a3\u662f\u4ed6\u6700\u6700\u820d\u4e0d\u5f97\u7684\u5fc3\u7231 \u4e4b\u7269\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u662f\u963f\u5c14\u6cf0\u5c71\u7684\u91d1\u77ff\u5417\uff1f\u201d\u5361\u6d1b\u4e1d\u6115\u7136\u9053\uff1a\u201c\u4e0d\u662f\u3002\u82e5\u662f\u8981\u963f\u5c14 \u6cf0\u5c71\u7684\u91d1\u77ff\u90a3\u8fd8\u6613\u529e\u3002\u5927\u6c57\u8981\u7684\u662f\u6211\u3002\u6211\u5f53\u65f6\u542c\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u51e0\u4e4e\u60f3\u6295\u5165\u6211\u4eec\u56fd\u4e2d\u90a3\u4e2a \u5e03\u5c14\u6839\u5927\u6e56\uff0c\u6b7b\u4e86\u5e72\u51c0\u3002\u6211\u662f\u5b81\u613f\u6b7b\u4e86\u4e5f\u4e0d\u613f\u79bb\u5f00\u6c99\u5c14\u6d77\u7684\uff01\u53ef\u662f\u6211\u6b7b\u4e86\u4e5f\u4e0d\u884c\uff0c\u56e0\u4e3a \u6211\u8fd8\u6709\u4e00\u4e2a\u7236\u4eb2\uff0c\u8fd8\u6709\u4e00\u4e2a\u738b\u56fd\u3002\u201d\u6b66\u7384\u971c\u5fc3\u60c5\u6c89\u91cd\uff0c\u8bf4\u9053\uff1a\u201c\u6574\u6574\u4e00\u4e2a\u738b\u56fd\u538b\u5728\u4f60\u7684 \u80a9\u4e0a\uff0c\u602a\u4e0d\u5f97\u4f60\u8981\u524d\u5f80\u53ef\u6c57\u7684\u738b\u5ef7\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7a81\u53a5\u5927\u6c57\u5a01\u4e34\u4e07\u90a6\uff0c\u706d\u56fd\u65e0\u6570\uff0c\u4ed6\u81ea\u79f0\u662f\u4e07\u738b\u4e4b\u738b\uff0c\u53ea\u662f\u4ed6\u7edf \u7387\u7684\u96c4\u5175\uff0c\u5c31\u8d85\u8fc7\u6211\u4eec\u7684\u4eba\u53e3\u5341\u500d\u3002\u6211\u4eec\u4e0d\u8fc7\u662f\u4ed6\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5c5e\u56fd\uff0c\u4ed6\u82e5\u52a8\u6012\uff0c\u53ef\u4ee5\u4e0d \u8d39\u5439\u7070\u4e4b\u529b\uff0c\u5c31\u4ee4\u6211\u4eec\u5168\u56fd\u7389\u77f3\u4ff1\u711a\u3002\u963f\u5c14\u6cf0\u5c71\u7684\u91d1\u77ff\u56fa\u7136\u8981\u843d\u5728\u4ed6\u624b\u4e2d\uff0c\u6211\u7236\u4eb2\u3001\u6c99 \u5c14\u6d77\uff0c\u4ee5\u53ca\u6211\u6240\u6709\u4eb2\u7231\u7684\u4eba\uff0c\u90fd\u96be\u9003\u8fd9\u573a\u6d69\u52ab\u3002\u6ca1\u5948\u4f55\u6211\u53ea\u597d\u542c\u4ece\u7236\u4eb2\u7684\u5b89\u6392\uff0c\u5c06\u6c99\u5c14 \u6d77\u9063\u5f00\uff0c\u63a5\u4e86\u5927\u6c57\u7684\u8058\u793c\uff0c\u5728\u4ed6\u6d3e\u6765\u7684\u6b66\u58eb\u62a4\u9001\u4e4b\u4e0b\uff0c\u524d\u5f80\u5927\u6c57\u7684\u738b\u5ef7\uff0c\u51c6\u5907\u505a\u4ed6\u7684\u738b \u5983\u3002\u53ef\u662f\u6211\u4e5f\u51c6\u5907\u4e86\u4e00\u79cd\u9a8c\u4e0d\u51fa\u6765\u7684\u6bd2\u836f\uff0c\u5728\u5927\u6c57\u903c\u6211\u6210\u5a5a\u4e4b\u65e5\uff0c\u4fbf\u5c06\u662f\u6211\u670d\u6bd2\u81ea\u5c3d\u4e4b \u65f6\u3002\u8fd9\u6837\uff0c\u6211\u6b7b\u5728\u4ed6\u7684\u5b98\u4e2d\uff0c\u4ed6\u53ea\u597d\u81ea\u53f9\u6666\u6c14\uff0c\u4e0d\u80fd\u602a\u6211\u7684\u7236\u4eb2\u4e86\u3002\u201d\u6b66\u7384\u971c\u53eb\u9053\uff1a \u201c\u5361\u6d1b\u4e1d\uff0c\u4f60\u4e0d\u80fd\u8fd9\u6837\u505a\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u60e8\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u60f3\u5343\u4e07\u904d\uff0c\u8fd9\u6837\u505a\u5b9e\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u65e2\u53ef\u4fdd\u5168\u6211\u7684\u7236\u4eb2\uff0c \u6211\u7684\u738b\u56fd\uff0c\u53c8\u53ef\u4ee5\u4fdd\u5168\u4e86\u6c99\u5c14\u6d77\u548c\u6211\u81ea\u5df1\u3002\u6c99\u5c14\u6d77\u8fd9\u50bb\u5b50\uff0c\u4ed6\u5374\u5168\u6ca1\u60f3\u5230\u8fd9\u4e9b\uff0c\u4ed6\u53ea\u60f3 \u5f97\u5230\u6211\uff0c\u4ed6\u53ea\u60f3\u51ed\u4ed6\u7684\u6b66\u827a\uff0c\u5c06\u6211\u62a2\u8d70\uff01\u6240\u4ee5\u6211\u4e0d\u5f97\u4e0d\u50cf\u65e5\u95f4\u90a3\u6837\u7684\u5bf9\u5f85\u4ed6\uff01\u6211\u53eb\u6b66\u58eb \u5c04\u6b7b\u4ed6\u7684\u9a6c\uff0c\u5c31\u662f\u4e0d\u613f\u4ed6\u518d\u8d76\u6765\u7ea0\u7f20\u3002\u4ed6\u867d\u7136\u662f\u6211\u56fd\u4e2d\u7684\u7b2c\u4e00\u597d\u6c49\uff0c\u4f46\u5374\u600e\u6bd4\u5f97\u4e0a\u5927\u6c57 \u8fd9\u4e00\u7fa4\u5982\u72fc\u4f3c\u864e\u7684\u6b66\u58eb\uff1f\u4ed6\u82e5\u86ee\u5e72\uff0c\u53ea\u6015\u4ed6\u672a\u80fd\u5c06\u6211\u62a2\u8d70\uff0c\u65e9\u5df2\u6b7b\u5728\u6b66\u58eb\u7684\u5200\u4e0b\u4e86\u3002\u59d0 \u59d0\uff0c\u73b0\u5728\u4f60\u660e\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u6545\u610f\u88c5\u4f5c\u65e0\u60c5\u65e0\u4e49\uff0c\u90a3\u662f\u8981\u4ee4\u5927\u6c57\u7684\u6b66\u58eb\u4e0d\u8d77\u7591\u5fc3\u3002\u201d \u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4e0d\u4f46\u5982\u6b64\uff0c\u6211\u8fd8\u8981\u6c99\u5c14\u6d77\u4e0d\u8d77\u7591\u5fc3\uff0c\u76f8\u4fe1\u6211\u662f\u771f\u7684\u65e0\u60c5\u65e0\u4e49\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a \u201c\u4f60\u662f\u6015\u4ed6\u6b89\u60c5\u800c\u6b7b\u3002\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u662f\u554a\u3002\u82e5\u7136\u4ed6\u75db\u6068\u6211\u65e0\u60c5\u65e0\u4e49\uff0c\u4ed6\u5c31\u4e0d\u4f1a\u5bfb\u6b7b\u5bfb \u6d3b\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u4e0d\u76f8\u4fe1\u3002\u201d\u5361\u6d1b\u4e1d\u53c8\u662f\u60ca\u559c\u53c8\u662f\u60b2\u4f24\uff0c\u6b22\u559c\u7684\u662f\u6c99\u5c14\u6d77\u5bf9\u5979 \u7684\u7231\u60c5\u7adf\u662f\u751f\u6b7b\u4e0d\u6e1d\uff0c\u600e\u6837\u90fd\u76f8\u4fe1\u5979\uff0c\u60b2\u4f24\u7684\u662f\u4ed6\u4e00\u7247\u75f4\u60c5\u6015\u4ed6\u7978\u4e8b\u3002 \u3000\u3000\u6b66\u7384\u971c\u8f7b\u8f7b\u629a\u6478\u5361\u6d1b\u4e1d\u7684\u79c0\u53d1\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u613f\u610f\u548c\u6c99\u5c14\u6d77\u7ed3\u4e3a\u592b\u5987\u4e48\uff1f\u201d\u5361\u6d1b \u4e1d\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u4f60\u65e0\u987b\u95ee\uff0c\u53ef\u60dc\u6211\u7eb5\u7136\u613f\u610f\uff0c\u4e5f\u53ea\u6709\u671f\u5f85\u6765\u751f\u4e86\u3002\u201d\u6b66\u7384\u971c\u5fae\u5fae\u7b11\u9053\uff1a \u201c\u4e0d\uff0c\u6211\u6709\u529e\u6cd5\u4f7f\u4f60\u4eec\u4eca\u751f\u5982\u613f\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u7741\u5927\u4e86\u773c\u775b\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u6b66\u7384\u971c\u5ffd\u7136\u8131\u4e0b\u4e86\u8eab\u4e0a\u7684\u8863\u88f3\uff0c\u62d4\u4e0b\u4e86 \u9970\u7269\uff0c\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u4f60\u6211\u6362\u8fc7\u670d\u9970\u8bd5\u8bd5\u3002\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u505a\u4ec0\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60 \u5148\u522b\u95ee\uff0c\u4f9d\u6211\u7684\u8bdd\u505a\u4e86\u518d\u8bf4\u3002\u201d\u4e24\u4eba\u6362\u8fc7\u670d\u9970\uff0c\u6b66\u7384\u971c\u638f\u51fa\u4e86\u4e24\u9897\u6613\u5bb9\u4e39\u66ff\u5979\u7740\u610f\u5316\u5986\uff0c \u5361\u6d1b\u4e1d\u53d6\u51fa\u4e00\u9762\u94dc\u955c\uff0c\u4e24\u4eba\u5e76\u80a9\u7167\u955c\uff0c\u53ea\u89c1\u5361\u6d1b\u4e1d\u53d8\u4e86\u4e00\u4e2a\u6c49\u5973\uff0c\u6b66\u7384\u971c\u5219\u53d8\u4e86\u738b\u5983\uff0c \u8138\u578b\u80a4\u8272\u90fd\u4e0e\u4ee5\u524d\u5927\u5927\u4e0d\u540c\u3002 \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u6211\u50cf\u4f60\u4e48\uff1f\u201d\u5361\u6d1b\u4e1d\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\u6709\u70b9\u50cf\uff0c\u4f46\u82e5 \u662f\u4e0e\u6211\u76f8\u719f\u7684\u4eba\uff0c\u4e00\u5b9a\u8fd8\u4f1a\u770b\u5f97\u51fa\u6765\u3002\u201d\u8bf4\u7f62\u53c8\u8fde\u8fde\u6447\u5934\u8bf4\u9053\uff1a\u201c\u6562\u8bf7\u4f60\u662f\u60f3\u5192\u5145\u6211\u53bb \u505a\u738b\u5983\uff1f\u8fd9\u4e0d\u6210\u5440\uff0c\u4e0d\u6210\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u6210\uff1f\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u7a81\u53a5\u6b66\u58eb \u4e0e\u6211\u76f8\u5904\u591a\u5929\uff0c\u4ed6\u4eec\u4f1a\u770b\u5f97\u51fa\u6765\u7684\uff0c\u800c\u4e14\u6211\u4e0d\u4f1a\u6b66\u529f\uff0c\u53c8\u600e\u80fd\u9003\u5f97\u51fa\u53bb\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u82e5\u662f\u672a\u89c1\u8fc7\u4f60\u7684\u9762\u7684\uff0c\u4ed6\u53ea\u51ed\u4f60\u7684\u56fe\u50cf\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5374\u672a\u5fc5\u770b\u5f97\u51fa \u5427\uff1f\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u60f3\u8981\u9a97\u8fc7\u5927\u6c57\u5417\uff1f\u4f60\u626e\u4f5c\u6211\u7684\u6478\u6837\uff0c\u5165\u5bab\u90a3\u5929\uff0c\u4f60\u62ab\u7740 \u9762\u7eb1\uff0c\u6682\u65f6\u95f4\u662f\u9a97\u5f97\u8fc7\u53bb\u7684\u3002\u53ef\u662f\u6b64\u53bb\u738b\u5ef7\uff0c\u6700\u5c11\u8fd8\u5f97\u4e09\u56db\u5929\u7684\u5de5\u592b\u5440\uff0c\u5728\u8def\u4e0a\u53c8\u600e\u4e48 \u7792\u5f97\u8fc7\u8fd9\u7fa4\u6b66\u58eb\uff1f\u201d\u201c\u6b66\u58eb\u4eec\u5bf9\u4f60\u7684\u4f8d\u5973\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u50cf\u4f60\u90a3\u6837\u6ce8\u610f\u5427\uff01\u201d\u5361\u6d1b\u4e1d\u9053\uff1a \u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u82e5\u4f60\u626e\u4f5c\u6211\u7684\u4f8d\u5973\u5012\u8fd8\u80fd\u6df7\u5f97\u8fc7\u53bb\u3002\u53ef\u662f\u4f60\u626e\u4f5c\u4f8d\u5973\u6709\u4ec0\u4e48\u7528\uff1f\u4ecd\u7136\u4e0d\u80fd \u633d\u56de\u6211\u7684\u547d\u8fd0\u5440\uff0c\u4f55\u51b5\u5728\u8def\u4e0a\u7a81\u7136\u591a\u51fa\u4e00\u4eba\uff0c\u6b66\u571f\u4eec\u4e5f\u4e0d\u4f1a\u4e0d\u53d1\u89c9\u7684\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u542c\u6211\u8bf4\u3002\u6211\u8981\u8def\u4e0a\u626e\u4f5c\u4f60\u7684\u4f8d\u5973\uff0c\u5165\u5bab\u4e4b\u65f6\u5c31\u626e\u4f5c\u4f60\u3002\u4f60\u7684\u9a6c\u8f66\u5f88 \u5bbd\u5927\uff0c\u603b\u80fd\u591f\u591a\u85cf\u4e24\u4e09\u4e2a\u4eba\u3002\u201d\u5361\u6d1b\u4e1d\u7ed9\u5979\u4e00\u8a00\u63d0\u9192\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4f60\u53ef\u4ee5\u85cf\u5728\u6211\u9a6c \u8f66\u7684\u5750\u57ab\u4e0b\u9762\uff0c\u54ce\uff0c\u8fd8\u4e0d\u5fc5\u59d4\u5c48\u4f60\u53d7\u82e6\uff0c\u6211\u6bcf\u5929\u53eb\u4e00\u4e2a\u4f8d\u5973\u85cf\u8d77\u6765\uff0c\u4f60\u53ef\u4ee5\u626e\u4f5c\u5979\u7684\u6a21 \u6837\uff0c\u5728\u8f66\u4e0a\u966a\u7740\u6211\uff0c\u6b47\u606f\u4e4b\u65f6\uff0c\u4f60\u4e0d\u4e0b\u8f66\u8d70\u52a8\uff0c\u6b66\u58eb\u4eec\u7edd\u5bf9\u770b\u4e0d\u51fa\u6765\u3002\u201d\u773c\u775b\u9732\u51fa\u5149\u8f89\uff0c \u4f46\u7acb\u5373\u53c8\u5fe7\u5f62\u4e8e\u8272\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u585e\u5916\u76f8\u9022\u53cb\u53d8\u4ec7 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u585e\u5916\u76f8\u9022\u53cb\u53d8\u4ec7 \u3000\u3000\u6b66\u7384\u971c\u8bf4\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u4f60\u522b\u5bb3\u6015\uff0c\u8fd9\u51c6\u80fd\u6210\u529f\u3002\u201d\u5361\u6d1b\u4e1d\u5ffd\u7136\u9053\uff1a\u201c\u4e0d\u6210\uff01\u201d\u6b66 \u7384\u971c\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u6210\uff1f\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u7eb5\u80fd\u7792\u8fc7\u4e00\u65f6\uff0c\u59cb\u7ec8\u4e0d\u514d\u7ed9\u5927\u6c57\u53d1\u89c9\u3002\u4e0d\u4f46\u5927\u6c57 \u4f1a\u518d\u6765\u7d22\u6211\uff0c\u800c\u4e14\u4e5f\u8fde\u7d2f\u4e86\u4f60\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u89c1\u4e86\u5927\u6c57\uff0c\u81ea\u6709\u529e\u6cd5\uff0c\u62c5\u4fdd\u4ed6\u4e0d\u4f1a\u518d \u8ffd\u7a76\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4f60\u662f\u8981\u523a\u6740\u4ed6\u5417\uff1f\u8fd9\u53ef\u4e0d\u597d\u505a\u5440\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u5e76 \u4e0d\u60f3\u6740\u4ed6\uff0c\u6211\u53e6\u5916\u6709\u529e\u6cd5\uff0c\u4f60\u76f8\u4fe1\u6211\u597d\u4e86\u3002\u201d\u5361\u6d1b\u4e1d\u542c\u8bf4\u5979\u662f\u5929\u5c71\u5251\u5ba2\uff0c\u53c8\u89c1\u5979\u663e\u8fc7\u8bf8 \u822c\u672c\u9886\uff0c\u65e2\u662f\u65e0\u6cd5\u53ef\u60f3\uff0c\u4fbf\u53ea\u597d\u4fe1\u8d56\u4e8e\u5979\u3002 \u3000\u3000\u6b66\u7384\u971c\u89c1\u5979\u8fd8\u5e26\u7740\u6000\u7591\u7684\u795e\u8272\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u62c5\u5fc3\u4ec0\u4e48\uff0c\u662f\u4e0d\u662f\u89c9\u7740\u8fd8\u6709\u7834\u7efd\u3002\u201d\u5361 \u6d1b\u4e1d\u9053\uff1a\u201c\u7167\u4f60\u8fd9\u6837\uff0c\u7834\u7efd\u5012\u662f\u6ca1\u6709\u3002\u53ef\u662f\u5012\u4e86\u738b\u5ef7\u4e4b\u540e\uff0c\u6211\u600e\u6837\u8131\u8eab\u56de\u53bb\u201d\u6b66\u7384\u971c\u9053\uff1a \u201c\u6c99\u5c14\u6d77\u5df2\u4e0e\u6211\u7ea6\u597d\uff0c\u54b1\u4eec\u5148\u5230\u738b\u5ef7\uff0c\u4ed6\u968f\u540e\u5c31\u6765\u3002\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u884c\u3002\u54b1\u4eec\u5230 \u738b\u5ef7\uff0c\u5c31\u7b97\u5927\u6c57\u4e0d\u8feb\u6211\u5373\u65e5\u6210\u4eb2\uff0c\u4e5f\u5b9a\u662f\u5c06\u54b1\u4eec\u63a5\u5165\u5ead\u5185\uff0c\u7eb5\u7136\u77e5\u9053\u4e86\u6c99\u5c14\u6d77\u7684\u5730\u5740\uff0c \u4e5f\u4e0d\u80fd\u7ea6\u4ed6\u4f1a\u9762\u3002\u201d\u6b66\u7384\u971c\u4e5f\u89c9\u5f97\u662f\u4e2a\u96be\u9898\uff0c\u6b63\u5728\u601d\u7d22\uff0c\u5361\u6d1b\u4e1d\u81ea\u5df1\u5148\u60f3\u51fa\u4e86\u6cd5\u5b50\uff0c\u8bf4 \u9053\uff1a\u201c\u7167\u6211\u5bb6\u4e61\u7684\u4e60\u4fd7\uff0c\u51fa\u5ac1\u7684\u5973\u513f\u5230\u4e86\u592b\u5bb6\u4e4b\u540e\uff0c\u5c31\u8981\u5c06\u5979\u6240\u7740\u7684\u90a3\u5957\u65b0\u5ac1\u8863\u9001\u53bb\u7ed9 \u6bcd\u4eb2\uff0c\u8868\u793a\u5728\u6b64\u4e4b\u524d\u662f\u9760\u7236\u6bcd\uff0c\u5728\u6b64\u4e4b\u540e\u4fbf\u662f\u9760\u4e08\u592b\u4e86\u3002\u6211\u5230\u4e86\u738b\u5ef7\uff0c\u594f\u8bf7\u5927\u6c57\uff0c\u51c6\u6211 \u5dee\u9063\u4e24\u4e2a\u4f8d\u5973\u5c06\u6211\u7684\u5ac1\u8863\u4e58\u539f\u8f66\u9001\u56de\u53bb\uff0c\u5e76\u7ed9\u6211\u5411\u7236\u6bcd\u62a5\u544a\u5e73\u5b89\u7684\u4e66\u4fe1\uff0c\u6211\u60f3\u5927\u6c57\u65e0\u4e0d \u5e94\u5141\u4e4b\u7406\u3002\u90a3\u65f6\u6211\u4fbf\u7528\u4f60\u7684\u6613\u5bb9\u4e39\uff0c\u626e\u6210\u4e00\u4e2a\u4f8d\u5973\u7684\u6a21\u6837\uff0c\u8131\u51fa\u7262\u7b3c\u3002 \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u6b66\u7384\u971c\u89e3\u4e86\u90a3\u51e0\u4e2a\u4f8d\u5973\u7684\u6655\u7761\u7a74\uff0c\u5979\u4eec\u89c1\u7740\u4e00\u4e2a\u964c\u751f\u7684\u6c49\u65cf\u59d1\u5a18\uff0c\u60ca\u8be7 \u4e0d\u5df2\uff0c\u5e78\u800c\u6709\u5361\u6d1b\u4e1d\u5728\u65c1\uff0c\u7acb\u523b\u8bf4\u660e\uff0c\u5979\u4eec\u624d\u4e0d\u81f3\u4e8e\u53eb\u51fa\u58f0\u6765\u3002\u8fd9\u73ed\u4f8d\u5973\u662f\u957f\u6d1b\u4e1d\u7684\u5fc3 \u8179\uff0c\u5979\u4eec\u5e73\u7d20\u4e5f\u77e5\u9053\u516c\u4e3b\u4e0e\u6c99\u5c14\u6d77\u7684\u604b\u60c5\uff0c\u5bf9\u5979\u751a\u662f\u540c\u60c5\uff0c\u90fd\u613f\u610f\u5192\u4e86\u5371\u9669\uff0c\u4f9d\u7167\u8ba1\u5212 \u884c\u4e8b\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u6b66\u7384\u971c\u626e\u6210\u4e86\u5361\u6d1b\u4e1d\u7684\u4f8d\u5973\uff0c\u966a\u7740\u5979\u540c\u4e58\u4e00\u67b6\u9a6c\u8f66\uff0c\u62a4\u9001\u7684\u6b66\u58eb\u679c\u7136\u65e0\u4e00 \u77e5\u6653\u3002 \u3000\u3000\u4e00\u8def\u5e73\u9759\u65e0\u4e8b\uff0c\u8d70\u4e86\u56db\u5929\uff0c\u4fbf\u5230\u8fbe\u7a81\u53a5\u7684\u90fd\u95e8\uff0c\u8def\u4e0a\u5b81\u9759\uff0c\u53ef\u662f\u6b66\u7384\u971c\u7684\u5fc3\u5934\u5374\u73e0 \u4e0d\u5b89\u5b81\u3002\u539f\u6765\u5979\u662f\u60f3\u501f\u8fd9\u4e2a\u673a\u4f1a\uff0c\u6f5c\u5165\u5927\u6c57\u7684\u738b\u5bab\uff0c\u89c1\u673a\u800c\u4e3a\uff0c\u6551\u51fa\u674e\u9038\u7684\u513f\u5b50\u3002 \u3000\u3000\u8fd9\u65f6\u5979\u5728\u8f66\u4e0a\u9065\u671b\u90fd\u95e8\uff0c\u5fc3\u60c5\u7d27\u5f20\u4e4b\u6781\uff0c\u60f3\u9053\uff1a\u201c\u674e\u9038\u60f3\u5fc5\u65e9\u5df2\u5230\u4e86\u8fd9\u513f\u4e86\uff0c\u4e0d\u77e5 \u4ed6\u7684\u906d\u9047\u5982\u4f55\uff1f\u4f46\u613f\u6211\u4e0d\u8981\u78b0\u89c1\u4ed6\u3002\u201d\u4e00\u60f3\u81ea\u5df1\u6f5c\u5165\u738b\u5bab\uff0c\u5927\u7ea6\u4e0d\u81f3\u4e8e\u5728\u738b\u5bab\u4e4b\u5185\u78b0\u89c1 \u674e\u9038\u3002\u5979\u5fc3\u4e2d\u6253\u4e0b\u4e86\u5982\u610f\u7b97\u76d8\uff0c\u82e5\u80fd\u5c06\u674e\u9038\u7684\u513f\u5b50\u6551\u51fa\uff0c\u5e76\u518d\u4e0a\u4e00\u6b21\u5929\u5c71\uff0c\u5c06\u674e\u9038\u7684\u513f \u5b50\u4ea4\u7ed9\u5979\u7684\u5e08\u5144\uff0c\u8bf7\u4ed6\u9001\u8fd8\u957f\u5b59\u58c1\u3002\u60f3\u6765\u5230\u4e86\u90a3\u4e2a\u65f6\u5019\uff0c\u4ed6\u5e08\u5144\u7684\u4f24\u4e5f\u5e94\u5f53\u5b8c\u5168\u597d\u4e86\u3002 \u3000\u3000\u4e3b\u610f\u867d\u7136\u6253\u597d\uff0c\u53ef\u5fc3\u4e2d\u4ecd\u7136\u5fd0\u5fd1\u4e0d\u5b89\u3002\u8fde\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\uff0c\u662f\u4e3a\u4e86\u5361\u6d1b\u4e1d\u7684\u4e8b\u60c5\u800c \u7d27\u5f20\u5462\uff0c\u8fd8\u662f\u56e0\u4e3a\u674e\u9038\u4e5f\u5728\u8fd9\u4e2a\u57ce\u4e2d\u800c\u5f15\u8d77\u5fc3\u60c5\u7684\u6ce2\u52a8\uff1f \u3000\u3000\u6b66\u7384\u971c\u54ea\u91cc\u77e5\u9053\uff0c\u674e\u9038\u4e5f\u6709\u4e00\u756a\u5947\u602a\u7684\u906d\u9047\uff0c\u6b64\u65f6\u4ed6\u6b63\u5728\u7a81\u53a5\u7684\u738b\u5ef7\uff0c\u9677\u5165\u4e86\u5927\u6c57 \u7684\u7ea7\u7f51\u4e4b\u4e2d\u3002 \u3000\u3000\u5c31\u5728\u6b66\u7384\u971c\u89c1\u5361\u6d1b\u4e1d\u7684\u90a3\u6761\u6cb3\u8fb9\uff0c\u674e\u9038\u5728\u5979\u7684\u524d\u51e0\u5929\u66fe\u5728\u90a3\u7ecf\u8fc7\uff0c\u4ed6\u4e5f\u9047\u89c1\u4e86\u4e94\u4e2a \u610f\u60f3\u4e0d\u5230\u7684\u4eba\u3002 \u3000\u3000\u674e\u9038\u4e0b\u4e86\u5929\u5c71\u4e4b\u540e\uff0c\u5c31\u5728\u5c71\u4e0b\u7684\u730e\u6237\u4eba\u5bb6\uff0c\u4e70\u4e86\u4e00\u5339\u5750\u9a91\uff0c\u6539\u4e86\u88c5\u675f\uff0c\u626e\u6210\u4e00\u4e2a\u730e \u6237\u7684\u6a21\u6837\uff0c\u84c4\u8d77\u4e86\u4e00\u64ae\u80e1\u5b50\uff0c\u9760\u4e86\u6613\u5bb9\u4e39\u4e4b\u52a9\uff0c\u8981\u6bd4\u4ed6\u672c\u6765\u7684\u9762\u76ee\u82cd\u8001\u5341\u5e74\u3002 \u3000\u3000\u4ed6\u4e3a\u4e86\u8981\u8d76\u5230\u7a81\u53a5\u738b\u5ef7\uff0c\u6551\u51fa\u4ed6\u7684\u513f\u5b50\uff0c\u4e00\u8def\u9a6c\u4e0d\u505c\u8e44\uff0c\u8fd9\u65e5\u6765\u5230\u4e86\u5580\u62c9\u6c99\u5c14\u6cb3\u6cb3 \u7554\uff0c\u4ed6\u90a3\u5339\u5750\u9a91\u7ecf\u8fc7\u4e86\u957f\u9014\u9a70\u9a71\uff0c\u53c8\u521a\u521a\u7a7f\u8fc7\u4e00\u6bb5\u6570\u5341\u91cc\u7684\u6c99\u6f20\uff0c\u98df\u6c34\u4e0d\u591f\uff0c\u4eba\u5c1a\u672a\u4e4f\uff0c \u9a6c\u5374\u65e9\u5df2\u7d2f\u5f97\u4e0d\u582a\uff0c\u76f4\u55b7\u53e3\u6cab\uff0c\u5636\u5636\u5598\u6c14\uff0c\u5982\u4eca\u5ffd\u7136\u53d1\u73b0\u4e86\u4e00\u6761\u6cb3\u6d41\uff0c\u5f53\u771f\u662f\u6bd4\u53eb\u5316\u5b50 \u62fe\u5230\u4e86\u91d1\u5b50\u8fd8\u66f4\u9ad8\u5174\uff0c\u4e8e\u662f\u674e\u9038\u8df3\u4e0b\u9a6c\u6765\uff0c\u7275\u7740\u5750\u9a91\uff0c\u5230\u6cb3\u8fb9\u559d\u6c34\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u9a86\u9a7c\u58f0\u54cd\uff0c\u674e\u9038\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u662f\u4e24\u4e2a\u88c5\u675f\u5947\u602a\uff0c\u5934\u7f20\u767d\u5e03\u7684\u6c49\u5b50\uff0c \u5408\u4e58\u4e00\u5339\u9a86\u9a7c\uff0c\u4e5f\u6765\u5230\u4e86\u6cb3\u8fb9\u3002\u770b\u4ed6\u4eec\u7684\u76f8\u8c8c\uff0c\u4e0d\u50cf\u662f\u666e\u901a\u7684\u7ef4\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u8df3\u4e0b\u9a86\u9a7c\uff0c\u62ff\u8d77\u76ae\u56ca\uff0c\u6b63\u5f85\u76db\u6c34\uff0c\u770b\u89c1\u674e\u9038\uff0c\u795e\u60c5\u4f3c\u4e4e\u6709\u70b9\u5f02\u6837\uff0c\u4e00\u9635\u5495 \u565c\uff0c\u53c8\u4ece\u6cb3\u8fb9\u6298\u56de\uff0c\u9a91\u4e0a\u9a86\u80cc\uff0c\u770b\u60c5\u5f62\u4f3c\u662f\u4e0d\u613f\u610f\u548c\u964c\u751f\u7684\u4eba\u540c\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5728\u6c99\u6f20\u4e0a\u7684\u65c5\u4eba\uff0c\u78b0\u5230\u4e86\u540c\u8def\u7684\u65c5\u5ba2\uff0c\u672c\u6765\u662f\u5f88\u9ad8\u5174\u7684\u4e8b\uff0c\u5c24\u5176\u662f\u4eba\u6570\u5c11\u7684\uff0c\u66f4\u613f \u610f\u7ed3\u4f34\u540c\u884c\uff0c\u597d\u5728\u65c5\u9014\u4e0a\u5f7c\u6b64\u6709\u4e2a\u7167\u987e\uff0c\u4f46\u8fd9\u4e24\u4e2a\u6c49\u5b50\u4e0d\u4f46\u6ca1\u6709\u6b22\u60a6\u4e4b\u5bb9\uff0c\u53cd\u800c\u597d\u50cf\u8981 \u907f\u5f00\u674e\u9038\uff0c\u8fd9\u5c31\u4e0d\u80fd\u4e0d\u4ee4\u674e\u9038\u6709\u70b9\u5947\u602a\u4e86\u3002 \u3000\u3000\u674e\u9038\u53bb\u8bd5\u7528\u7ef4\u8bed\u62db\u547c\uff0c\u90a3\u4e24\u4e2a\u6c49\u5b50\u5374\u4f3c\u662f\u542c\u4e0d\u61c2\u4ed6\u7684\u8bf4\u8bdd\uff0c\u53fd\u53fd\u5495\u5495\u7684\u4e00\u9762\u8bf4\u4e00\u9762 \u6447\u5934\uff0c\u4e0d\u5f85\u674e\u9038\u8d70\u8fd1\u8eab\u524d\uff0c\u4fbf\u9a91\u7740\u9a86\u9a7c\u8d70\u4e86\u3002 \u3000\u3000\u674e\u9038\u542c\u4ed6\u4eec\u7684\u53e3\u97f3\uff0c\u770b\u4ed6\u4eec\u7684\u88c5\u675f\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6562\u60c5\u662f\u4e24\u4e2a\u4ece\u82b1\u523a\u5b50\u6a21\u6765 \u7684\u5546\u4eba\u3002\u201d\u82b1\u523a\u5b50\u6a21\u662f\u4e2d\u4e9a\u7684\u4e00\u4e2a\u5927\u56fd\uff0c\u662f\u7a81\u53a5\u52bf\u529b\u6240\u53ca\u7684\u4e00\u4e2a\u56fd\u5bb6\uff0c\u540d\u4e49\u4e0a\u867d\u7136\u4e0d\u662f \u7a81\u53a5\u7684\u5c5e\u56fd\uff0c\u4f46\u4e5f\u5e74\u5e74\u7ed9\u7a81\u53a5\u53ef\u6c57\u7f34\u7eb3\u8d21\u7269\uff0c\u66f2\u610f\u4fee\u597d\uff0c\u6015\u7a81\u53a5\u653b\u6253\u5b83\u3002\u82b1\u523a\u5b50\u6a21\u548c\u7a81 \u53a5\u7684\u5546\u4eba\u65f6\u6709\u6765\u5f80\uff0c\u5728\u7a81\u53a5\u505a\u5546\u7684\u5916\u56fd\u4eba\uff0c\u5341\u6709\u516b\u4e5d\u90fd\u4f1a\u61c2\u5f97\u7ef4\u65cf\u7684\u8bed\u8a00\uff0c\u4f46\u8fd9\u4e24\u4e2a\u6c49 \u4e8e\u5374\u4e0d\u80af\u7528\u7ef4\u8bed\u7b54\u674e\u9038\u7684\u95ee\u8bdd\uff0c\u674e\u9038\u4e5f\u4e0d\u77e5\u4ed6\u4eec\u771f\u7684\u4e0d\u61c2\u8fd8\u662f\u5047\u7684\u4e0d\u61c2\uff0c\u4f46\u4eba\u5bb6\u4e0d\u7406\u4f1a \u4ed6\uff0c\u674e\u9038\u8ba8\u4e86\u4e00\u4e2a\u8001\u5927\u7684\u6ca1\u8da3\uff0c\u4e0d\u4fbf\u4e0e\u4ed6\u4eec\u5520\u8baa\uff0c\u53ea\u597d\u9000\u4e0b\uff0c\u8ba9\u5750\u9a91\u559d\u4e86\u6c34\uff0c\u4fbf\u653e\u5b83\u5728 \u6cb3\u8fb9\u6b47\u606f\u3002\u674e\u9038\u4e5f\u5728\u6811\u836b\u4e0b\u95ed\u76ee\u517b\u795e\u3002 \u3000\u3000\u90a3\u4e00\u5339\u9a86\u9a7c\u8d70\u4e86\u8fd8\u672a\u5230\u534a\u91cc\u4e4b\u5730\uff0c\u5929\u7a7a\u51fa\u73b0\u4e86\u4e24\u53ea\u5140\u9e70\uff0c\u674e\u9038\u542c\u5f97\u5140\u9e70\u7684\u53eb\u58f0\uff0c\u7741 \u5f00\u773c\u77a7\uff0c\u4f46\u89c1\u8fd9\u4e24\u53ea\u5140\u9e70\uff0c\u6b63\u5411\u9a86\u9a7c\u6251\u4e0b\uff0c\u539f\u6765\u9a7c\u80cc\u4e0a\u6302\u6709\u98ce\u5e72\u7684\u725b\u8089\uff0c\u90a3\u4e24\u53ea\u5140\u9e70\u51c6 \u662f\u997f\u5f97\u614c\u4e86\uff0c\u6240\u4ee5\u6251\u4e0b\u6765\u62a2\u8089\u5403\u3002 \u3000\u3000\u8fd9\u79cd\u8349\u539f\u4e0a\u7684\u5140\u9e70\u5927\u5f97\u60ca\u4eba\uff0c\u4e24\u8fb9\u7fc5\u8180\u5f20\u5f00\uff0c\u5c31\u50cf\u4e00\u56e2\u9ed1\u4e91\u4f3c\u7684\uff0c\u6247\u5f97\u5730\u4e0a\u6c99\u98de\u77f3 \u8d70\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u90a3\u4e24\u4e2a\u82b1\u523a\u5b50\u6a21\u7684\u5546\u4eba\u5728\u9a7c\u80cc\u4e0a\u8eab\u5f62\u4e00\u4fa7\uff0c\u9177\u4f3c\u4e2d\u539f\u6b66\u5b66\u4e2d\u201c\u5012\u6302\u91d1\u94a9\u201d \u7684\u8eab\u6cd5\uff0c\u53cc\u8db3\u4e00\u6491\u9a7c\u80cc\uff0c\u907f\u5f00\u4e86\u5140\u9e70\u7684\u5229\u722a\uff0c\u53cc\u5200\u9f50\u51fa\uff0c\u6a2a\u524a\u8fc7\u53bb\uff0c\u4f46\u542c\u5f97\u201c\u54b3\u5506\u201d\u4e00 \u58f0\uff0c\u5148\u6251\u4e0b\u6765\u7684\u90a3\u5934\u5140\u9e70\u7ed9\u5229\u5200\u65a9\u4e86\u4e00\u4e0b\uff0c\u6293\u4e0d\u4e2d\u90a3\u5757\u725b\u8089\uff0c\u5374\u6293\u7834\u4e86\u7f1a\u5728\u9a7c\u80cc\u4e0a\u7684\u4e00 \u4e2a\u5305\u88f9\uff0c\u5305\u88f9\u91cc\u7684\u4e1c\u897f\u54d7\u5566\u5566\u7684\u6389\u4e0b\u4e86\u4e00\u5927\u5806\uff0c\u7b2c\u4e8c\u53ea\u5140\u9e70\u53c8\u6251\u4e0b\u6765\uff0c\u4f46\u89c1\u5200\u5149\u75be\u95ea\uff0c \u7fbd\u6bdb\u7eb7\u98de\uff0c\u90a3\u53ea\u5140\u9e70\u4f3c\u662f\u77e5\u6653\u5389\u5bb3\uff0c\u4e00\u6251\u4e0d\u4e2d\uff0c\u4e5f\u98de\u5f00\u4e86\u3002 \u3000\u3000\u674e\u9038\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u82b1\u523a\u5b50\u6a21\u7684\u5546\u4eba\u8eab\u624b\u4e0d\u4fd7\uff0c\u5c4a\u7136\u5bf9\u4ed8\u5f97\u4e86\u8fd9\u79cd\u8349 \u539f\u4e0a\u7684\u5927\u5140\u9e70\uff01\u201d\u770b\u90a3\u6389\u5728\u5730\u4e0a\u7684\u4e1c\u897f\uff0c\u5374\u539f\u6765\u662f\u4e00\u652f\u652f\u7684\u7280\u725b\u89d2\u3002\u8fd9\u662f\u5f88\u8d35\u91cd\u7684\u836f\u7269\uff0c \u674e\u9038\u604d\u7136\u5927\u609f\uff0c\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u8fd9\u4e24\u4e2a\u5546\u4eba\u4e43\u662f\u505a\u836f\u6750\u751f\u610f\u7684\u5927\u5546\u4eba\uff0c\u4ed6\u4eec\u5927\u7ea6\u6015\u6211\u662f \u4e2a\u5f3a\u76d7\u3002\u4f1a\u62a2\u52ab\u4ed6\u4eec\u8d35\u91cd\u7684\u836f\u6750\uff0c\u6240\u4ee5\u907f\u5f00\u4e86\u6211\u3002\u4f46\u4ed6\u4eec\u65e2\u7136\u5177\u6709\u8fd9\u7b49\u6b66\u529f\uff0c\u5374\u53c8\u4f55\u81f3 \u4e8e\u4ff1\u6015\u5355\u8eab\u7684\u5f3a\u76d7\uff1f\u201d \u3000\u3000\u90a3\u4e24\u53ea\u5140\u9e70\u6293\u4e0d\u7740\u90a3\u5757\u725b\u8089\uff0c\u5fc3\u6709\u672a\u7518\uff0c\u5728\u4e0a\u7a7a\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u53c8\u518d\u6251\u4e0b\uff0c\u8fd9\u4e00\u4e0b \u6765\u52bf\u66f4\u731b\uff0c\u4f46\u90a3\u4e24\u4e2a\u5546\u4eba\u4e5f\u65e9\u6709\u4e86\u9632\u5907\uff0c\u4f46\u89c1\u4ed6\u4eec\u628a\u624b\u4e00\u626c\uff0c\u4e24\u67c4\u98de\u5200\u7834\u7a7a\u800c\u51fa\uff0c\u90a3\u4e24 \u53ea\u5140\u9e70\u4e5f\u771f\u5389\u5bb3\uff0c\u5c45\u7136\u4f38\u722a\u6293\u7740\u98de\u5200\uff0c\u53ef\u662f\u90a3\u4e24\u4e2a\u5546\u4eba\u7684\u98de\u5200\u53d1\u5f97\u5feb\u5982\u7535\u95ea\uff0c\u4e24\u5200\u65b9\u51fa\uff0c \u540e\u9762\u7684\u4e24\u67c4\u98de\u5200\u53c8\u76f8\u7ee7\u800c\u6765\uff0c\u90a3\u4e24\u53ea\u5140\u9e70\u518d\u817e\u51fa\u4e00\u722a\u6293\u7740\uff0c\u5140\u9e70\u5230\u5e95\u4e0d\u5982\u6b66\u5b66\u9ad8\u624b\u7684\u9ad8 \u660e\uff0c\u5b83\u4eec\u6293\u7740\u4e86\u98de\u5200\uff0c\u4e0d\u4f1a\u8fd8\u51fb\u654c\u4eba\uff0c\u5927\u7ea6\u53c8\u7ed9\u98de\u5200\u5272\u4f24\u4e86\u5c11\u8bb8\uff0c\u5728\u7a7a\u4e2d\u5457\u5457\u5927\u53eb\u3002\u56db \u67c4\u98de\u5200\u8fd8\u672a\u5760\u5730\uff0c\u90a3\u4e24\u4e2a\u5546\u4eba\u7b2c\u4e09\u6b21\u53d1\u51fa\u98de\u5200\uff0c\u4f46\u89c1\u94f6\u5149\u75be\u5c04\uff0c\u8fd9\u4e24\u53ea\u5140\u9e70\u5403\u8fc7\u4e00\u6b21\u82e6 \u5934\uff0c\u8fd9\u56de\u4e0d\u6562\u7528\u722a\u518d\u6293\uff0c\u5374\u7528\u7fc5\u8180\u5c06\u98de\u5200\u6247\u843d\uff0c\u4f46\u56e0\u6b64\u8eab\u5f62\u4e5f\u4fbf\u4e0b\u6c89\uff0c\u4f3c\u662f\u56e0\u4e3a\u65e2\u8981\u584c \u5f00\u98de\u5200\uff0c\u53c8\u8981\u5c55\u7fc5\u98de\u817e\uff0c\u4e24\u96be\u517c\u9890\uff0c\u751a\u4e3a\u5403\u529b\u7684\u6478\u6837\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4e24\u4e2a\u5546\u4eba\u7b2c \u56db\u6b21\u53d1\u51fa\u98de\u5200\uff0c\u4f46\u89c1\u5200\u5149\u7535\u5c04\uff0c\u90a3\u4e24\u53ea\u5140\u9e70\u53d1\u51fa\u60b2\u9e23\uff0c\u500f\u7136\u5c55\u7fc5\uff0c\u75be\u98de\u800c\u53bb\uff0c\u4e0d\u6562\u518d\u60f9 \u90a3\u4e24\u4e2a\u5546\u4eba\u3002\u539f\u6765\u6bcf\u53ea\u5140\u9e70\u90fd\u88ab\u98de\u5200\u523a\u778e\u4e86\u4e00\u76ee\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5546\u4eba\u62fe\u8d77\u5730\u4e0a\u7684\u98de\u5200\u548c\u7280\u725b\u89d2\uff0c\u7f1a\u597d\u80cc\u5305\uff0c\u53c8\u518d\u524d\u884c\u3002\u674e\u9038\u4e5f\u6b63\u60f3\u8d77\u7a0b\uff0c\u5ffd \u89c1\u524d\u9762\u4e00\u9a91\u9a8f\u9a6c\uff0c\u8fce\u7740\u90a3\u4e24\u4e2a\u5546\u4eba\u75be\u9a70\u800c\u6765\uff0c\u9a6c\u4e0a\u7684\u9a91\u58eb\u7528\u7ef4\u8bed\u5927\u559d\u9053\uff1a\u201c\u7559\u4e0b\u9a86\u9a7c\uff0c \u8ba9\u4f60\u4eec\u8fc7\u53bb\u3002\u201d\u53e6\u4e24\u4e2a\u5546\u4eba\u97ad\u7b56\u9a86\u9a7c\u5411\u524d\u51b2\u53bb\u60f3\u4ee5\u9a86\u9a7c\u7684\u5de8\u529b\u649e\u7ffb\u90a3\u4e00\u5339\u9a6c\uff0c\u53e6\u4e00\u4e2a\u9a91 \u58eb\u7a81\u7136\u582a\u4e0b\u9a6c\u80cc\uff0c\u53cc\u624b\u4e00\u6309\u3002\u559d\u4e00\u58f0\uff1a\u201c\u53bb\uff01\u201d\u90a3\u5339\u9a86\u9a7c\u7adf\u7136\u7ed9\u4ed6\u6309\u7740\uff0c\u56db\u8e44\u5c48\u5730\uff0c\u4e0d \u80fd\u524d\u8fdb\uff0c\u5c06\u4e24\u4e2a\u5546\u4eba\u5587\u5496\u4e24\u97ad\u626b\u4e0b\uff0c\u90a3\u4e2a\u9a86\u5b50\u571f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8981\u8d27\u8fd8\u662f\u8981\u6027\u547d\uff1f\u201d \u624b\u8155\u4e00\u7ffb\uff0c\u53ea\u662f\u4e00\u4e2a\u7167\u9762\uff0c\u53e6\u4e00\u6761\u8d76\u9a86\u9a7c\u7684\u957f\u97ad\u7adf\u7ed9\u4ed6\u5288\u624b\u593a\u53bb\u3002\u8fd9\u65f6\u674e\u9038\u65b9\u624d\u770b\u5f97\u6e05 \u695a\uff0c\u8fd9\u4e2a\u9a91\u58eb\u539f\u6765\u662f\u4e2a\u6c49\u4eba\u3002\u90a3\u4e2a\u9a91\u4e0a\u593a\u957f\u97ad\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u97ad\u626b\u53bb\uff0c\u97ad\u58f0\u547c\u54cd\u4e4b\u4e2d\uff0c\u4f46 \u89c1\u90a3\u4e24\u4e2a\u82b1\u523a\u5b50\u6a21\u5546\u4eba\u4ece\u9a7c\u80cc\u4e0a\u817e\u7a7a\u98de\u8d77\uff0c\u957f\u97ad\u63a0\u8fc7\u9a7c\u80cc\uff0c\u90a3\u4e24\u4e2a\u5546\u4eba\u5df2\u5012\u7eb5\u51fa\u4e09\u4e08\u5f00 \u5916\u3002 \u3000\u3000\u674e\u9038\u6697\u6697\u559d\u6765\uff1a\u201c\u597d\u4fca\u7684\u8eab\u6cd5\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u56db\u67c4\u98de\u5200\u5df2\u4ece\u56db\u4e2a\u4e0d\u540c\u7684\u65b9\u5411\uff0c \u540c\u65f6\u5411\u90a3\u4e2a\u9a91\u58eb\u88ad\u6765\uff0c\u674e\u9038\u89c1\u8fc7\u4ed6\u4eec\u7684\u98de\u5200\u7edd\u6280\uff0c\u6599\u60f3\u8fd9\u4e2a\u9a91\u58eb\u5c06\u8981\u5927\u5403\u82e6\u5934\uff0c\u90a3\u77e5\u5fc3 \u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u53e6\u4e00\u4e2a\u9a91\u58eb\u5c06\u957f\u97ad\u6253\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u5047\u7684\u5c31\u5377\u7740\u4e86\u4e00\u67c4\u98de\u5200\uff0c\u968f\u5373\u4e00\u6296\u957f\u97ad\uff0c \u98de\u5200\u53cd\u5f39\u98de\u51fa\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u4e0e\u7b2c\u4e8c\u67c4\u98de\u5200\u78b0\u4e2a\u6b63\u7740\uff0c\u4e24\u67c4\u98de\u5200\u5728\u7a7a\u4e2d\u6fc0\u8d77\u4e86\u4e00\u998f\u706b\u82b1\uff0c \u6d41\u57a6\u6b92\u77f3\u822c\u90fd\u6389\u81f3\u8349\u5730\u4e0a\u4e86\u3002\u53e6\u4e00\u9a91\u58eb\u4e00\u4e2a\u7ffb\u8eab\uff0c\u6070\u597d\u8fce\u7740\u7b2c\u4e09\u67c4\u98de\u5200\uff0c\u4f9d\u6cd5\u70ae\u5236\uff0c\u957f \u97ad\u4e00\u5708\u4e00\u6296\uff0c\u53c8\u5c06\u7b2c\u4e09\u67c4\u98de\u5200\u53cd\u63b7\u51fa\u53bb\uff0c\u5c06\u7b2c\u56db\u67c4\u98de\u5200\u4e5f\u6253\u843d\u4e86\u3002 \u3000\u3000\u674e\u9038\u5403\u4e86\u4e00\u60ca\uff0c\u8981\u77e5\u9053\u8fd9\u4e24\u4e2a\u82b1\u523a\u5b50\u6a21\u7684\u5546\u4eba\uff0c\u521a\u624d\u80fd\u7528\u98de\u5200\u523a\u4f24\u5140\u9e70\uff0c\u5200\u7684\u950b\u5229 \u548c\u4ed6\u4eec\u7684\u624b\u52b2\u53ef\u60f3\u800c\u77e5\uff0c\u5982\u4eca\u7adf\u88ab\u4e24\u4e2a\u9a91\u58eb\u7528\u957f\u97ad\u5377\u8d77\uff0c\u501f\u529b\u6253\u529b\uff0c\u8fd9\u79cd\u624b\u6cd5\uff0c\u4e0d\u4f46\u7075 \u5de7\u4e4b\u6781\uff0c\u800c\u62ff\u634f\u65f6\u5019\uff0c\u4e5f\u4f7f\u5f97\u4e0d\u5dee\u5206\u6beb\uff0c\u672c\u8eab\u7684\u529f\u529b\uff0c\u5f53\u7136\u8fdc\u8fdc\u8d85\u4e4e\u654c\u4eba\u4e4b\u4e0a\u3002\u5982\u6b64\u8eab \u624b\uff0c\u5728\u4e2d\u539f\u7684\u6b66\u6797\u4e2d\uff0c\u4e5f\u7b97\u5f97\u662f\u4e00\u7b49\u4e00\u7684\u4e86\u3002 \u3000\u3000\u90a3\u9a91\u58eb\u6325\u821e\u957f\u97ad\uff0c\u6b65\u6b65\u8fdb\u903c\uff0c\u90a3\u4e24\u4e2a\u5546\u4eba\u63a5\u8fde\u53d1\u51fa\u98de\u5200\uff0c\u4f46\u89c1\u5200\u5149\u95ea\u95ea\uff0c\u97ad\u5f71\u7ffb\u98de\uff0c \u5200\u4f3c\u7a7f\u68ad\uff0c\u97ad\u5982\u602a\u87d2\uff0c\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u5200\u98de\u5200\u843d\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u88ab\u90a3\u4e2a\u9a91\u58eb \u6253\u843d\u4e86\u5341\u51e0\u67c4\u98de\u5200\uff0c\u53cc\u65b9\u7684\u8ddd\u79bb\u8d8a\u6765\u8d8a\u8fd1\uff0c\u90a3\u4e24\u4e2a\u5546\u4eba\u53d1\u4e86\u614c\uff0c\u5c06\u5269\u4e0b\u7684\u98de\u5200\u4e00\u53e4\u8111\u513f \u5168\u53d1\u51fa\u53bb\uff0c\u6bcf\u4eba\u7684\u4e00\u53ea\u638c\u5fc3\u6263\u7740\u4e09\u67c4\u98de\u5200\uff0c\u4e24\u4eba\u56db\u638c\uff0c\u4e00\u4e0b\u5b50\u4fbf\u53d1\u51fa\u4e86\u5341\u4e8c\u67c4\u98de\u5200\uff0c\u5728 \u7a7a\u4e2d\u7ec7\u6210\u4e86\u4e00\u7247\u5200\u7f51\u3002\u90a3\u9a91\u58eb\u5c06\u957f\u97ad\u76d8\u5934\u4e00\u821e\uff0c\u4f46\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u7684\u58f0\u97f3\u8fde\u73e0\u5bc6\u54cd\uff0c\u90a3\u6761 \u957f\u97ad\u88ab\u5341\u4e8c\u67c4\u98de\u5200\u524a\u8fc7\uff0c\u5bf8\u5bf8\u65ad\u5f00\uff0c\u5176\u4e2d\u4e00\u67c4\u98de\u5200\uff0c\u4f59\u529b\u672a\u8870\uff0c\u4ece\u9a91\u4e0a\u7684\u80a9\u5934\u659c\u524a\u800c\u8fc7\uff0c \u9976\u662f\u4ed6\u95ea\u907f\u5f97\u5feb\uff0c\u62a4\u80a9\u4e5f\u5df2\u7ed9\u98de\u5200\u524a\u6389\u3002 \u3000\u3000\u90a3\u9a91\u58eb\u52c3\u7136\u5927\u6012\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u8ba9\u4f60\u4eec\u4e5f\u77a7\u77a7\u6211\u7684\u5200\u6cd5\u3002\u201d\u811a\u5c16\u70b9\u5730\uff0c\u4f7f\u4e2a\u201c\u9ec4\u9e64 \u51b2\u5bb5\u201d\u7684\u8eab\u6cd5\uff0c\u4e5f\u50cf\u521a\u624d\u90a3\u4e24\u4e2a\u5546\u4eba\u4e00\u822c\uff0c\u51cc\u7a7a\u98de\u8d77\uff0c\u5c31\u5728\u534a\u7a7a\u4e2d\u63a3\u51fa\u4e86\u4e00\u67c4\u94a2\u5200\uff0c\u4fe8 \u5982\u9965\u9e70\u6251\u5154\u4e00\u822c\u5411\u90a3\u4e24\u4e2a\u5546\u4eba\u5f53\u5934\u5243\u4e0b\u3002 \u3000\u3000\u674e\u9038\u89c1\u8fd9\u4e2a\u9a91\u58eb\u5982\u6b64\u51f6\u72e0\uff0c\u4e0d\u4f46\u8c0b\u8d22\uff0c\u517c\u8981\u5bb3\u547d\uff0c\u4e0d\u7531\u5f97\u52a8\u8d77\u4e86\u4fa0\u4e49\u4e4b\u5fc3\uff0c\u6025\u5fd9\u8df3 \u51fa\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u53ef\u662f\u4ed6\u53d1\u8bdd\u5df2\u7ecf\u8fdf\u4e86\uff0c\u90a3\u9a91\u58eb\u7684\u624b\u6cd5\u5feb\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u53ea\u542c\u5f97\u5f53\u5f53\u4e24\u58f0\uff0c\u90a3\u4e24\u4e2a\u5546\u4eba \u624b\u4e0a\u7684\u6708\u7259\u5f2f\u5200\u5148\u7ed9\u524a\u65ad\uff0c\u63a5\u7740\u662f\u4e24\u58f0\u60e8\u5389\u7684\u547c\u53eb\uff0c\u5f85\u674e\u9038\u8d76\u5230\u4e4b\u65f6\uff0c\u90a3\u4e24\u4e2a\u5546\u4eba\u5df2\u7ecf \u5c38\u6a2a\u5730\u4e0b\u3002 \u3000\u3000\u90a3\u4e2a\u9a91\u58eb\u56de\u8fc7\u5934\u6765\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u77a7\u89c1\u4e86\uff0c\u4f60\u5c31\u8ddf\u4ed6\u4eec\u4e00\u540c\u53bb\u5427\uff01\u201d\u6cfc\u98ce\u822c\u8fde\u73af \u4e09\u5200\u75be\u65ab\u800c\u6765\u3002\u674e\u9038\u4f7f\u4e86\u4e00\u62db\u201c\u9f99\u95e8\u9f13\u6d6a\u201d\uff0c\u4e5f\u662f\u4e00\u62db\u4e09\u5f0f\uff0c\u5feb\u6377\u65e0\u8bba\u3002\u4ed6\u7684\u5251\u4e43\u662f\u5927 \u5185\u5b9d\u7269\uff0c\u4f46\u542c\u5f97\u5f53\u3001\u5f53\u3001\u5f53\uff01\u4e09\u58f0\u54cd\u8fc7\uff0c\u90a3\u4e2a\u9a91\u58eb\u7684\u7ea2\u6bdb\u5b9d\u5200\u635f\u4e86\u4e94\u4e2a\u7f3a\u53e3\u3002 \u3000\u3000\u674e\u9038\u6709\u70b9\u5947\u602a\uff0c\u8fd9\u4e2a\u4eba\u4f3c\u662f\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u4f3c\u7684\uff0c\u542c\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u597d\u50cf\u662f\u81ea\u5df1\u7684 \u4e00\u4e2a\u719f\u4eba\uff0c\u5374\u600e\u4e48\u4e5f\u60f3\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u9a91\u58eb\u7684\u5200\u6cd5\u5feb\u6781\uff0c\u90a3\u5bb9\u5f97\u674e\u9038\u62bd\u7a7a\u601d\u7d22\uff0c\u4ed6\u7684\u7ea2\u6bdb\u5b9d\u5200\u88ab\u674e\u9038\u524a\u4e2a\u7f3a\u53e3\uff0c\u53ea\u542c\u5f97 \u4ed6\u201c\u55b7\u201d\u4e86\u4e00\u58f0\uff0c\u5200\u950b\u4e00\u8f6c\uff0c\u62e9\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u767b\u65f6\u4fbf\u662f\u4e00\u62db\u201c\u591c\u6218\u516b\u65b9\u201d\u6a2a\u524a\u51fa\u53bb\uff0c\u970e \u65f6\u95f4\u5200\u5149\u95ea\u95ea\uff0c\u7adf\u5316\u6210\u4e86\u516b\u53e3\u94a2\u5200\uff0c\u4ece\u56db\u9762\u516b\u65b9\u540c\u65f6\u65a9\u6765\u3002\u674e\u9038\u559d\u4e00\u58f0\u201c\u6765\u5f97\u597d\u201d\uff0c\u6a2a \u5251\u4e00\u5c01\uff0c\u4e00\u62db\u201c\u91d1\u94a2\u62a4\u6cd5\u201d\uff0c\u5b88\u4e2d\u5e26\u653b\uff0c\u968f\u5373\u53d8\u4e3a\u201c\u6a2a\u6307\u5929\u5357\u201d\uff0c\u6325\u5251\u523a\u51fa\uff0c\u4f46\u542c\u5f97\u4e00 \u7247\u65ad\u91d1\u5207\u7389\u4e4b\u58f0\uff0c\u4e00\u5251\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\u4ea4\u4e86\u516b\u4e0b\uff0c\u56e0\u4e3a\u53cc\u65b9\u90fd\u5feb\u5230\u6781\u70b9\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u4ec5\u662f \u7a0d\u7a0d\u6cbe\u4e0a\uff0c\u4fbf\u5373\u63a0\u8fc7\uff0c\u53cc\u65b9\u5185\u52b2\u76f8\u82e5\uff0c\u674e\u9038\u7684\u5b9d\u5251\u867d\u7136\u7a0d\u5360\u4e0a\u98ce\uff0c\u5374\u4e5f\u672a\u80fd\u5c06\u5bf9\u65b9\u7684\u5b9d \u5200\u524a\u65ad\u3002 \u3000\u3000\u8f6c\u773c\u95f4\u53cc\u65b9\u5df2\u62c6\u5341\u62db\uff0c\u674e\u9038\u62a2\u4e86\u5148\u624b\uff0c\u7740\u7740\u8fdb\u653b\uff0c\u4f46\u90a3\u4eba\u7684\u5200\u6cd5\u975e\u5e38\u4e25\u5bc6\uff0c\u6025\u5207\u4e4b \u95f4\uff0c\u674e\u9038\u5374\u4e5f\u65e0\u6cd5\u53d6\u80dc\uff0c\u5fc3\u60f3\uff1a\u201c\u82e5\u4e0d\u662f\u8fd9\u51e0\u5e74\u6765\uff0c\u6211\u5df2\u5c06\u5e08\u7236\u548c\u5cb3\u7236\u7684\u4e24\u5bb6\u5251\u6cd5\uff0c\u878d \u4f1a\u8d2f\u901a\uff0c\u6050\u6015\u8fd8\u672a\u5fc5\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u201d\u5ffd\u542c\u5f97\u53e6\u4e00\u4eba\u559d\u9053\uff1a\u201c\u5484\uff0c\u4f60\u6211\u90fd\u662f\u6c49\u4eba\uff0c\u4f60\u4e3a\u4f55 \u66ff\u52d2\u5b50\u5356\u547d\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u8eab\u672c\u9886\uff0c\u5374\u7531\u4f55\u81f3\u5bdf\u5916\u6765\uff0c\u505a\u8fd9\u52ab\u8d22\u8d8a\u8d27\u7684\u52fe\u5f53\uff1f\u9752 \u5929\u767d\u65e5\uff0c\u4f24\u4eba\u6027\u547d\uff0c\u5b9e\u662f\u5929\u7406\u96be\u5bb9\uff01\u662f\u6c49\u4eba\u5c31\u53ef\u4ee5\u6a2a\u884c\u9738\u9053\u4e48\uff1f\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53e6\u4e00\u4e2a\u6c49 \u5b50\u7a81\u7136\u865a\u6643\u4e00\u5200\uff0c\u98de\u51fa\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u662f\u674e\u6bbf\u4e0b\u4e48\uff1f\u201d\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c\u540c\u65f6 \u53eb\u4e86\u51fa\u6765\uff1a\u201c\u4f60\u662f\u5357\u5bab\u5c1a\u4e48\uff1f\u201d\u53e6\u4e00\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u63b7\u5200\u4e8e\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u6b63\u662f\u5357\u5bab\u5c1a\uff0c \u6bbf\u4e0b\uff0c\u4f60\u9976\u6055\u6211\uff01\u60f3\u4e0d\u5230\u54b1\u4eec\u4e24\u4eba\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u5728\u5f02\u90a6\u76f8\u89c1\uff01\u201d\u8bf4\u7f62\u4fbf\u8981\u4e0a\u6765\u62e5\u62b1\u674e\u9038\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u5357\u5bab\u5c1a\u6b63\u662f\u516b\u5e74\u4e4b\u524d\uff0c\u4e0e\u674e\u9038\u540c\u5728\u4e00\u4e2a\u665a\u4e0a\uff0c\u6f5c\u5165\u6df1\u5bab\uff0c\u884c\u523a\u6b66\u5219\u5929\u7684\u53e6 \u4e00\u6b66\u58eb\u3002\u5f53\u5e74\u674e\u9038\u5728\u795e\u6b66\u8425\u4e2d\uff0c\u88ab\u5206\u6d3e\u505a\u5915\u53e3\u5ef7\u7684\u8f6e\u503c\u536b\u58eb\uff0c\u548c\u5357\u5bab\u5c1a\u6b63\u597d\u540c\u4f4f\u4e00\u95f4\u623f \u5b50\u3002\u540e\u6765\u674e\u9038\u4ece\u4ed6\u7684\u5cb3\u7236\u957f\u5b59\u5747\u91cf\u53e3\u4e2d\uff0c\u624d\u77e5\u9053\u5357\u5bab\u5c1a\u7684\u7236\u4eb2\u5f53\u8fc7\u592a\u5b97\u7687\u5e1d\uff08\u674e\u4e16\u6c11\uff09 \u7684\u7981\u536b\u519b\u526f\u6307\u6325\u4f7f\uff0c\u4ed6\u6df7\u5230\u957f\u5b89\uff0c\u548c\u674e\u9038\u4e00\u822c\uff0c\u540c\u662f\u6000\u7740\u56fd\u4ec7\u5bb6\u6068\uff0c\u60f3\u523a\u6740\u6b66\u5219\u5929\u7684\u3002 \u90a3\u4e00\u4e2a\u665a\u4e0a\u674e\u9038\u884c\u523a\u4e0d\u6210\uff0c\u8df3\u4e0b\u9a8a\u5c71\uff0c\u800c\u5357\u5bab\u5c1a\u4e5f\u7ed9\u5bab\u4e2d\u7684\u536b\u58eb\u53d1\u73b0\uff0c\u674e\u9038\u9003\u547d\u4e4b\u65f6\uff0c \u6b63\u77a7\u89c1\u4ed6\u88ab\u536b\u58eb\u5305\u56f4\uff0c\u5f53\u65f6\u674e\u9038\u8fd8\u4ee5\u4e0d\u80fd\u6551\u4ed6\u800c\u4e3a\u61be\uff0c\u60f3\u4e0d\u5230\u4ed6\u4e5f\u4fdd\u4f4f\u4e86\u6027\u547d\u3002 \u3000\u3000\u5357\u5bab\u5c1a\u4ee5\u524d\u662f\u6ee1\u9762\u866c\u9aef\uff0c\u4f46\u73b0\u5728\u5df2\u662f\u5243\u5f97\u4e7e\u4e7e\u51c0\u51c0\uff0c\u800c\u4e14\u4e8b\u9694\u516b\u5e74\uff0c\u6240\u4ee5\u674e\u9038\u4e00\u65f6 \u8ba4\u4e0d\u51fa\u662f\u4ed6\uff0c\u800c\u674e\u9038\u4e5f\u6539\u5bb9\u6613\u8c8c\uff0c\u5e76\u84c4\u8d77\u4e86\u80e1\u5b50\uff0c\u6240\u4ee5\u5357\u5bab\u5c1a\u4e5f\u8ba4\u4e0d\u51fa\u662f\u4ed6\u3002\u76f4\u5230\u53cc\u65b9 \u90fd\u51fa\u4e86\u58f0\uff0c\u800c\u5357\u5bab\u5c1a\u53c8\u770b\u51fa\u674e\u9038\u7684\u8fd9\u4e00\u624b\u5251\u6cd5\uff0c\u4e24\u4eba\u65b9\u6562\u76f8\u8ba4\u3002 \u3000\u3000\u4ed6\u4eec\u6709\u8fc7\u8fd9\u4e00\u6bb5\u5173\u7cfb\uff0c\u5f02\u56fd\u76f8\u9022\uff0c\u672c\u8be5\u662f\u559c\u51fa\u610f\u5916\uff0c\u53ef\u662f\u674e\u9038\u521a\u521a\u8fd8\u8981\u66ff\u90a3\u4e24\u82b1\u523a \u5b50\u6a21\u7684\u5546\u4eba\u6253\u62b1\u4e0d\u5e73\uff0c\u5ffd\u7136\u8ba4\u51fa\u662f\u4ed6\uff0c\u8fd9\u53ef\u5c31\u6709\u70b9\u5c34\u5c2c\u4e86\u3002 \u3000\u3000\u5357\u5bab\u5c1a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f53\u4eca\u4e71\u4e16\uff0c\u4eba\u547d\u8d31\u5982\u697c\u8681\uff0c\u6210\u738b\u8d25\u5bc7\uff0c\u8c01\u4e0d\u662f\u6740\u4eba\u76c8\u57ce\uff0c\u6740\u4eba \u76c8\u91ce\uff01\u6211\u6740\u6b7b\u4e86\u533a\u533a\u4e24\u4e2a\u5546\u4eba\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d\u674e\u9038\u5fc3\u4e2d\u4e0d\u4ee5\u4e3a\u7136\uff0c\u63d6\u4e8e\u60c5\u9762\uff0c\u4e0d\u597d \u53d1\u4f5c\u3002\u4e0e\u4ed6\u91cd\u65b0\u89c1\u8fc7\u793c\u540e\uff0c\u674e\u9038\u95ee\u9053\uff1a\u201c\u5357\u5bab\u5144\u662f\u51e0\u65f6\u5230\u5317\u5730\u6765\u7684\uff1f\u5374\u4f55\u4ee5\u8981\u6740\u8fd9\u4e24\u4e2a \u5546\u4eba\uff1f\u201d \u3000\u3000\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u6211\u90a3\u6b21\u884c\u523a\u4e0d\u6210\uff0c\u5e78\u800c\u9003\u51fa\u6027\u547d\uff0c\u672c\u6b32\u53bb\u6295\u5954\u56fd\u516c\u7684\uff0c\u672a\u5230\u626c\u5dde\uff0c\u56fd \u516c\u7684\u4e49\u5175\u65e9\u5df2\u5168\u90e8\u74e6\u89e3\uff0c\u671d\u5ef7\u7f09\u6355\u5f97\u7d27\uff0c\u6ca1\u5948\u4f55\u53ea\u597d\u9003\u5230\u585e\u5916\u3002\u4f46\u6211\u867d\u7136\u662f\u4ea1\u547d\u5929\u6daf\uff0c \u53cd\u5468\u590d\u5510\u4e4b\u5fc3\u5374\u672a\u5c1d\u6d88\u706d\u3002\u6bbf\u4e0b\uff0c\u4f60\u662f\u51e0\u65f6\u6765\u7684\uff1f\u53ef\u4e5f\u662f\u6709\u6240\u56fe\u8c0b\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211 \u7684\u5fc3\u4e8b\u5df2\u51b7\u3002\u6211\u4e5f\u662f\u90a3\u6b21\u884c\u523a\u4e0d\u6210\uff0c\u9003\u5230\u6b64\u5730\u7684\uff0c\u7b97\u8d77\u6765\u5df2\u6709\u516b\u5e74\u4e86\u3002\u8fd9\u516b\u5e74\u6765\u6211\u4e00\u76f4 \u50fb\u5904\u5929\u5c71\uff0c\u5df2\u65e0\u5fc3\u518d\u95ee\u5174\u4ea1\u5927\u4e8b\u3002\u201d\u5357\u5bab\u5c1a\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\u4f55\u987b\u5fc3\u7070\u610f\u51b7\uff0c\u5728\u4e0b\u4fbf\u6709\u826f\u673a\uff01\u201d \u674e\u9038\u9053\uff1a\u201c\u6709\u4f55\u826f\u673a\uff01\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u7a81\u53a5\u5927\u6c57\u8981\u5174\u5175\u6253\u5165\u4e2d\u539f\uff0c\u6bbf\u4e0b\u4f60\u5c1a\u672a\u77e5\u9053\u5417\uff1f\u201d \u674e\u9038\u9053\uff1a\u201c\u542c\u5230\u4e00\u4e9b\u98ce\u58f0\uff0c\u8fd9\u4e0e\u4f60\u6211\u6709\u4f55\u5173\u7cfb\u3002\u5357\u5b98\u5c1a\u9053\uff1a\u201c\u600e\u4e48\u6ca1\u6709\u5173\u7cfb\uff1f\u4e34\u671d\u6b66\u6c0f\uff0c \u7be1\u4f4d\u591a\u5e74\uff0c\u7687\u540e\u65e7\u81e3\uff0c\u5374\u5927\u90fd\u672a\u66fe\u6b64\u673a\u4f1a\uff0c\u7406\u5e94\u5916\u4f1a\uff0c\u4f55\u6101\u4f2a\u671d\u4e0d\u5373\u8986\u4ea1\uff01\u201d\u674e\u9038\u5fc3\u5934 \u4e00\u9707\uff0c\u5927\u5927\u4e0d\u4ee5\u4e3a\u7136\uff0c\u53ea\u56e0\u521a\u521a\u4e0e\u4ed6\u4f1a\u9762\uff01\u4e0d\u4fbf\u518d\u884c\u9a73\u65a5\u3002 \u3000\u3000\u5357\u5bab\u5c1a\u5e76\u6ca1\u6709\u7559\u610f\u5230\u674e\u9038\u795e\u8272\u7684\u6539\u53d8\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u65e5\u6740\u8fd9\u4e24\u4e2a\u5546\u4eba\uff0c\u4e5f\u6b63\u662f \u4e3a\u6b64\u3002\u201d\u674e\u9038\u8be7\u9053\uff1a\u201c\u7a81\u53a5\u8981\u548c\u4e2d\u56fd\u5f00\u4ed7\uff0c\u4e0e\u8fd9\u4e24\u4e2a\u82b1\u523a\u5b50\u6a21\u5546\u4eba\u53c8\u6709\u4ec0\u4e48\u5173\u8fde\uff1f\u4f60\u4f55 \u4ee5\u56e0\u6b64\u800c\u8981\u6740\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u7a81\u53a5\u5927\u6c57\u5174\u5175\u5728\u5373\uff0c\u81ea\u8981\u62db\u8d24\u7eb3\u58eb\uff0c\u5e7f\u8058\u80fd\u4eba\u3002\u636e\u6211\u6240\u77e5\uff0c\u5404\u56fd\u6b66\u58eb\uff0c \u95fb\u98ce\u800c\u6765\u8005\uff0c\u5df2\u4e0d\u5728\u5c11\u6570\uff01\u5927\u6c57\u5c31\u5c06\u8d81\u62d4\u9752\u4f73\u8282\uff0c\u5728\u738b\u5ef7\u5f00\u82f1\u8c6a\u5927\u4f1a\u3002\u201d\u201c\u62d4\u9752\u8282\u201d\u662f \u7a81\u53a5\u4e00\u4e2a\u91cd\u5927\u7684\u8282\u65e5\uff0c\u7ea6\u5f53\u4e2d\u56fd\u7684\u4e8c\u6708\u4e2d\u65ec\uff0c\u5176\u65f6\u6625\u98ce\u89e3\u51bb\uff0c\u7267\u91ce\u8349\u957f\uff0c\u7a81\u53a5\u767e\u59d3\uff0c\u62d4 \u8349\u4f97\u755c\uff0c\u5927\u4e8b\u5e86\u795d\uff0c\u6c42\u771f\u795e\u4fdd\u4f51\u725b\u7f8a\u7e41\u6b96\uff0c\u6545\u540d\u201c\u62d4\u9752\u8282\u3002\u201d\u674e\u9038\u4e00\u7b97\u65e5\u671f\uff0c\u5373\u5c06\u6765\u5230\uff0c \u95ee\u9053\uff1a\u201c\u5357\u5bab\u5144\u83ab\u975e\u4e5f\u60f3\u8d74\u4f1a\u4e48\uff1f\u201d \u3000\u3000\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u6211\u8eab\u4e3a\u6c49\u4eba\uff0c\u53ea\u6015\u4ed6\u4eec\u4e0d\u80af\u89c1\u4fe1\uff0c\u6545\u6b64\u9664\u4e86\u8981\u8bf7\u4eba\u8350\u4e4b\u5916\uff0c\u8fd8\u60f3\u89c5\u4e9b \u8fdb\u89c1\u4e4b\u793c\u3002\u6bbf\u4e0b\uff0c\u4f60\u53ef\u77e5\u6211\u8fd9\u51e0\u5e74\u505a\u751a\u8425\u751f\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6211\u5982\u4f55\u5f97\u77e5\uff1f\u201d\u5357\u5bab \u5c1a\u5927\u7b11\u9053\uff1a\u201c\u6211\u505a\u7684\u4fbf\u662f\u65e0\u672c\u94b1\u7684\u4e70\u5356\uff0c\u6211\u9003\u81f3\u6b64\u5730\u4e4b\u4e00\uff0c\u4f1a\u5408\u4e86\u4e00\u6279\u4ece\u4e2d\u539f\u6765\u7684\u6c5f\u6e56 \u52c7\u5ba2\uff0c\u4fbf\u5728\u585e\u5916\u5e72\u8d77\u9ed1\u9053\u4e0a\u7684\u751f\u6daf\u3002\u563f\uff0c\u563f\uff0c\u8fd9\u662f\u4e0d\u5f97\u5df2\u800c\u4e3a\u4e4b\uff0c\u6211\u5c82\u80fd\u957f\u4e3a\u9a6c\u8d3c\u7ec8\u8001\uff1f \u4e0d\u7792\u6bbf\u4e0b\uff0c\u6211\u786e\u662f\u60f3\u53bb\u8d74\u4f1a\u3002\u6211\u95fb\u8bf4\u7a81\u53a5\u7684\u592a\u5e08\uff0c\u5176\u4eba\u751a\u8d2a\u8d22\u8d4f\uff0c\u4ed6\u7684\u513f\u5b50\u53c8\u5728\u60a3\u54ee\u5598 \u75c5\uff0c\u591a\u5e74\u6765\uff0c\u6211\u6b63\u4e3a\u8fdb\u89c1\u4e4b\u793c\u5728\u4f24\u8111\u7b4b\uff0c\u5374\u559c\u6253\u542c\u5f97\u6709\u8fd9\u4e24\u4e2a\u7f8a\u726f\uff0c\u81ea\u4f8d\u6709\u70b9\u6b66\u529f\uff0c\u4e24 \u4eba\u4e00\u9a7c\uff0c\u4fbf\u6562\u4ece\u82b1\u523a\u5b50\u6a21\u6f33\u6765\u5927\u6279\u8d35\u91cd\u7684\u836f\u7269\uff0c\u5176\u4e2d\u4e5f\u6709\u6cbb\u54ee\u5598\u7684\u7075\u836f\u3002\u836f\u6750\u5728\u7a81\u53a5\u751a \u4e3a\u7f3a\u4e4f\uff0c\u4f55\u51b5\u662f\u96be\u5f97\u7684\u8d35\u91cd\u836f\u6750\uff1f\u662f\u4ee5\u6211\u4fbf\u5355\u9a91\u8ffd\u8e2a\uff0c\u5fd7\u5728\u52ab\u7269\uff0c\u60f3\u4e0d\u81f3\u4ed6\u4eec\u7adf\u6562\u4e0e\u6211 \u62fc\u547d\uff0c\u54c8\uff0c\u54c8\uff0c\u4eca\u5929\u53ea\u597d\u7b97\u4ed6\u4eec\u6666\u6c14\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u60f3\u4e0d\u5230\u5357\u5bab\u5c1a\u5982\u6b64\u72e0\u5fc3\u8fa3\u624b\uff0c\u5bf9\u4ed6\u5927\u4e3a\u4e0d\u6ee1\uff0c\u6682\u4e14\u5fcd\u4f4f\u3002\u5357\u5bab\u5c1a\u95ee\u9053\uff1a\u201c\u6bbf\u4e0b \u4f55\u5f80\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u60f3\u5230\u7a81\u53a5\u7684\u738b\u5ef7\u89c2\u5149\u3002\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\uff01\u6bbf\u4e0b\uff0c\u826f \u673a\u4e0d\u53ef\u9519\u8fc7\uff0c\u4f55\u4e0d\u5c31\u4e0e\u6211\u4e00\u9053\uff0c\u53bb\u89c1\u7a81\u53a5\u53ef\u6c57\uff0c\u4ee5\u6bbf\u4e0b\u7684\u8eab\u4efd\uff0c\u7a81\u53a5\u5927\u6c57\u5fc5\u7136\u5927\u8868\u6b22\u8fce\uff0c \u5c06\u6765\u63a8\u7ffb\u4f2a\u5468\u6b66\u6c0f\uff0c\u8fd9\u5927\u5510\u7684\u5b9d\u5ea7\uff0c\u5c31\u662f\u6bbf\u4e0b\u6240\u5750\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u5fc3\u4e2d\u6697\u6697\u51b7\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u7a81\u53a5\u5927\u6c57\u6bd4\u4f60\u806a\u660e\u5f97\u591a\uff0c\u4ed6\u65e9\u5df2\u60f3\u5230\u8981\u5229\u7528\u6211\u8fd9\u4e2a\u4eba \u4e86\uff0c\u4f55\u987b\u4f60\u6765\u9080\u6211\uff1f\u54b1\u4eec\u8981\u63a8\u7ffb\u4f2a\u5468\u6b66\u6c0f\uff0c\u90a3\u662f\u53e6\u4e00\u56de\u4e8b\uff0c\u4e3a\u864e\u4f5c\u6005\uff0c\u52a9\u7a81\u53a5\u4fb5\u7565\u81ea\u5df1 \u7684\u4e61\u90a6\uff0c\u5c82\u4e0d\u6210\u4e86\u7f6a\u4eba\uff1f\u201d\u5f85\u8981\u628a\u8fd9\u756a\u9053\u7406\u5411\u5357\u5bab\u5c1a\u8bb2\u89e3\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u53e6\u6709\u4e3b\u610f\uff0c\u60f3\u9053\uff1a \u201c\u5357\u5bab\u5c1a\u86f0\u4f0f\u585e\u5916\u591a\u5e74\uff0c\u4ed6\u5ff5\u5ff5\u4e0d\u5fd8\u91cd\u8fd4\u4e2d\u539f\uff0c\u518d\u56fe\u5bcc\u8d35\uff0c\u62e5\u6211\u4e3a\u541b\uff0c\u4e5f\u4e0d\u5916\u662f\u6500\u9f99\u9644 \u51e4\uff0c\u60f3\u6062\u590d\u5bb6\u4e1a\uff0c\u91cd\u632f\u5bb6\u58f0\u800c\u5df2\u3002\u770b\u4ed6\u7684\u4e3a\u4eba\uff0c\u6211\u672a\u5fc5\u8bf4\u5f97\u670d\u4ed6\uff0c\u53cd\u800c\u6cc4\u6f0f\u4e86\u6211\u7684\u79d8\u5bc6\u3002 \u6211\u6b63\u8981\u6f5c\u5165\u738b\u5ef7\uff0c\u6551\u51fa\u654f\u513f\uff0c\u4f55\u4e0d\u5c31\u7740\u843d\u5728\u6b64\u4eba\u8eab\u4e0a\uff0c\u60f3\u4e2a\u5999\u6cd5\u3002\u201d \u3000\u3000\u5357\u5bab\u5c1a\u89c1\u674e\u9038\u773c\u5149\u95ea\u70c1\uff0c\u4f3c\u662f\u5fc3\u601d\u672b\u5b9a\uff0c\u518d\u62dc\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u8fd9\u662f\u5343\u8f7d\u4e00\u65f6\u7684\u673a\u9047\uff0c \u9519\u8fc7\u540e\u6094\u4e0d\u53ca\uff0c\u6bbf\u4e0b\u7eb5\u4e0d\u60f3\u4e3a\u5929\u5b50\uff0c\u96be\u9053\u4e0d\u60f3\u5927\u5510\u91cd\u5149\u5417\uff1f\u8bf7\u6bbf\u4e0b\u4e0d\u5fc5\u518d\u72b9\u7591\u4e86\u3002\u201d\u674e \u9038\u76ee\u5149\u805a\u62e2\uff0c\u76ef\u7740\u4ed6\u9053\uff1b\u201c\u5357\u5bab\u5144\uff0c\u4f60\u5bf9\u5510\u5ba4\u5fe0\u5fc3\u803f\u803f\uff0c\u53ef\u4f69\u53ef\u4f69\u3002\u6211\u5c82\u4e0d\u60f3\u5927\u5510\u91cd\u5149\uff1f \u53ea\u662f\u54b1\u4eec\u73b0\u5728\u8fd8\u672a\u77e5\u9053\u7a81\u601d\u5927\u6c57\u7684\u5fc3\u610f\uff0c\u4ee5\u6211\u7684\u8eab\u4efd\uff0c\u5192\u5473\u7684\u53bb\uff0c\u7978\u798f\u96be\u6d4b\uff01\u201d\u5357\u5bab\u5c1a \u9053\uff1a\u201c\u4ee5\u6211\u60f3\u6765\uff0c\u7a81\u53a5\u50fb\u5904\u897f\u9672\uff0c\u4ed6\u6253\u8fdb\u4e86\u4e2d\u539f\uff0c\u4e5f\u96be\u6cbb\u7406\u6574\u4e2a\u4e2d\u56fd\uff0c\u4e00\u5b9a\u8981\u7acb\u5148\u5e1d\u7684 \u5b50\u5b59\u505a\u4e2d\u56fd\u7684\u5929\u5b50\u7684\u3002\u6bbf\u4e0b\u4f55\u5fc5\u72b9\u7591\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u80e1\u4eba\u6027\u60c5\u53cd\u8986\uff0c\u800c\u4e14\u6211 \u53bb\u6c42\u4ed6\uff0c\u4ea6\u662f\u6709\u5931\u8eab\u4efd\u3002\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u53ef\u662f\u826f\u673a\u4e0d\u5bb9\u9519\u8fc7\uff0c\u6bbf\u4e0b\u4e0d\u5982\u5148\u4e0e\u6211\u4e00\u540c\u524d\u5f80\uff0c \u5f85\u63a2\u6e05\u695a\u4e86\u5927\u6c57\u7684\u5fc3\u610f\u4e4b\u540e\uff0c\u6bbf\u4e0b\u518d\u8868\u9732\u8eab\u4efd\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u674e\u9038\u76ee\u5149\u70af\u70af\uff0c\u76ef\u7740\u674e\u9038\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u4e0e\u4f60\u540c\u53bb\uff0c\u53ea\u662f\u4f60\u5f97\u4f9d\u6211\u4e00\u4ef6\u4e8b\u3002\u201d\u5357\u5bab\u5c1a \u9053\uff1a\u201c\u8bf7\u6bbf\u4e0b\u5429\u5490\u4fbf\u662f\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u5207\u4e0d\u53ef\u6cc4\u6f0f\u6211\u7684\u8eab\u4efd\uff01\u6211\u8981\u51ed\u6211\u81ea\u5df1\u7684\u672c\u9886\uff0c \u53d6\u5f97\u7a81\u53a5\u5927\u6c57\u7684\u91cd\u7528\uff0c\u8fd9\u6837\u5c06\u6765\u4e8b\u6210\u4e4b\u540e\uff0c\u4ed6\u624d\u4e0d\u6562\u770b\u8f7b\u4e8e\u6211\u3002\u201d\u5357\u5bab\u5c1a\u629a\u638c\u7b11\u9053\uff1a \u201c\u5927\u82f1\u96c4\u5927\u8c6a\u6770\uff0c\u5f53\u771f\u662f\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u5c42\uff0c\u6b66\u5219\u5929\u624b\u4e0b\u4e5f\u751a\u591a\u80fd\u4eba\uff0c\u82e5\u7136\u7ed9\u5979 \u77e5\u9053\u6211\u5728\u7a81\u53a5\u519b\u4e2d\uff0c\u8bf4\u4e0d\u5b9a\u4fbf\u8981\u9063\u523a\u5ba2\u6765\u6740\u6211\uff0c\u6240\u4ee5\u6211\u7684\u8eab\u4efd\uff0c\u4e0d\u4f46\u5bf9\u4f17\u541b\u81e3\u4e0d\u80fd\u6cc4\u6f0f\uff0c \u5bf9\u4efb\u4f55\u4eba\u4e5f\u4e0d\u80fd\u6cc4\u6f0f\uff01\u201d\u5357\u5bab\u5c1a\u5fc3\u60f3\u5982\u6b64\u4e00\u6765\uff0c\u81ea\u5df1\u5c31\u662f\u674e\u9038\u6700\u5fc3\u8179\u7684\u4eba\u4e86\uff01\u5c82\u4e0d\u5999\u6781\uff0c \u5f53\u4e0b\u53d1\u4e86\u91cd\u8a93\uff0c\u4e00\u53e3\u7b54\u5e94\u3002 \u3000\u3000\u5357\u5bab\u5c1a\u5c06\u90a3\u9a86\u9a7c\u80cc\u4e0a\u7684\u836f\u6750\u642c\u4e86\u4e0b\u6765\uff0c\u5c06\u6700\u8d35\u91cd\u7684\u548c\u6cbb\u54ee\u5598\u7684\u836f\u6750\u6361\u51fa\uff0c\u653e\u4e0a\u81ea\u5df1 \u7684\u5750\u9a91\uff0c\u4e0e\u674e\u9038\u7b56\u9a6c\u540c\u884c\u3002\u674e\u9038\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u6709\u4eba\u4e3e\u8350\uff0c\u90a3\u662f\u4f55\u4eba\uff1f\u201d\u5357\u5bab\u5c1a\u9053\uff1a \u201c\u90a3\u662f\u6211\u5230\u6ec7\u5317\u4e4b\u540e\uff0c\u6240\u7ed3\u8bc6\u7684\u4e00\u4f4d\u7eff\u6797\u8c6a\u5ba2\u3002\u201d\u6b63\u8bf4\u8bdd\u95f4\uff0c\u53ea\u542c\u5f97\u80cc\u540e\u9a6c\u94c3\u58f0\u54cd\uff0c\u5357 \u5bab\u5c1a\u56de\u5934\u4e00\u671b\uff0c\u7b11\u9053\uff1a\u201c\u6b63\u597d\u662f\u5927\u54e5\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u8bb0\u7740\uff0c\u6211\u7684\u540d\u5b57\u53eb\u4e0a\u5b98\u654f\u3002\u5207\u4e0d\u53ef\u518d\u4ee5\u6bbf\u4e0b\u76f8\u79f0\u3002\u201d\u5357\u5bab\u5c1a\u6014\u4e86\u4e00\u6014\uff0c \u968f\u5373\u9886\u609f\uff0c\u674e\u9038\u65e2\u8981\u4ed6\u906e\u7792\u8eab\u4efd\uff0c\u5f53\u7136\u4e5f\u6539\u59d3\u6362\u540d\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u90a3\u4e00\u9a91\u9a6c\u5df2\u7136\u8d76\u5230\uff0c\u53ea \u89c1\u9a6c\u4e0a\u7684\u9a91\u5bb9\u4e43\u662f\u4e00\u4e2a\u8c79\u5934\u72ee\u9f3b\u7684\u8001\u4eba\uff0c\u53cc\u76ee\u751a\u6709\u5a01\u4e25\uff0c\u624b\u4e2d\u6301\u7740\u4e00\u652f\u4e09\u5c3a\u591a\u957f\u7684\u65f1\u70df \u7a17\uff0c\u70df\u9505\u7279\u5927\uff0c\u8fd9\u65f6\u6b63\u5728\u5438\u5f97\u6ecb\u6ecb\u58f0\u54cd\uff0c\u70df\u9505\u91cc\u53d1\u51fa\u7ea2\u5149\u3002 \u3000\u3000\u5357\u5bab\u5c1a\u5bf9\u8fd9\u8001\u5934\u751a\u4e3a\u656c\u754f\uff0c\u7acb\u5373\u8df3\u4e0b\u9a6c\u6765\uff0c\u674e\u9038\u4e5f\u8ddf\u7740\u4e0b\u9a6c\u3002\u5357\u5bab\u5c1a\u521a\u9053\u5f97\u4e00\u58f0\uff1a \u201c\u5927\u54e5\uff0c\u90a3\u4e24\u4e2a\u82b1\u523a\u5b50\u6a21\u5546\u4eba\u2026\u2026\u201d\u6b63\u60f3\u62a5\u544a\u52ab\u9a86\u9a7c\u4e4b\u4e8b\uff0c\u90a3\u8001\u5934\u559d\u9053\uff1a\u201c\u4e14\u4f4f\uff0c\u4ed6\u662f \u4ec0\u4e48\u4eba\uff1f\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u4e49\u5144\uff0c\u540d\u53eb\u4e0a\u5b98\u654f\u3002\u201d\u90a3\u8001\u5934\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u7684\u4e49\u5144\uff01 \u505a\u4ec0\u4e48\u7684\uff1f\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u6211\u60f3\u4e0e\u4ed6\u540c\u5f80\u7a81\u53a5\u738b\u5ef7\uff0c\u56fe\u4e2a\u51fa\u8eab\uff0c\u672a\u66fe\u7980\u62a5\u5927\u54e5\uff0c\u8bf7\uff0c\u8bf7\uff01\u2026\u2026\u201d \u8001\u5934\u53cc\u76ee\u4e00\u7741\uff0c\u9053\uff1a\u201c\u5e2e\u4e2d\u89c4\u4f8b\uff0c\u51b3\u65e0\u66f4\u6539\uff0c\u4e0d\u5f97\u591a\u8a00\uff01\u201d\u5c06\u5357\u5bab\u5c1a\u7684\u8bf4\u8bdd\u6253\u65ad\uff0c\u5927\u8e0f \u6b65\u4e0a\u524d\u6765\uff0c\u674e\u9038\u751a\u4e3a\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u4ed6\u4eec\u7684\u4eba\uff0c\u4ed6\u8bb2\u4ec0\u4e48\u5e2e\u4e2d\u89c4\u4f8b\uff1f\u201d\u5ff5\u5934\u65b9 \u52a8\uff0c\u53ea\u89c1\u90a3\u8001\u5934\u5ffd\u7136\u6362\u4e86\u4e00\u4ed8\u7b11\u8138\uff0c\u4f38\u51fa\u4e00\u53ea\u624b\u6765\uff0c\u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u201d\u674e \u9038\u60f3\u4e0d\u900f\u4ed6\u4f55\u4ee5\u524d\u5014\u540e\u606d\uff0c\u89c1\u4ed6\u5982\u6b64\u5ba2\u6c14\uff0c\u53ea\u597d\u4ee5\u793c\u76f8\u89c1\uff0c\u4f38\u624b\u4e0e\u4ed6\u4e00\u63e1\uff0c\u9aa4\u7136\u95f4\u5ffd\u89c9 \u4e00\u80a1\u5927\u529b\uff0c\u90a3\u8001\u5934\u513f\u7684\u4e94\u6307\u7adf\u4f3c\u5316\u6210\u94a2\u74dc\u4e00\u822c\uff0c\u7d27\u7d27\u6293\u7740\u4ed6\u7684\u8109\u95e8\uff0c\u674e\u9038\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9 \u624d\u77e5\u9053\u8fd9\u8001\u5934\u513f\u662f\u4f38\u91cf\u4ed6\u7684\u529f\u592b\uff0c\u4e60\u6b66\u4e4b\u4eba\uff0c\u9aa4\u9047\u88ad\u51fb\uff0c\u53cd\u5e94\u81ea\u662f\u5feb\u901f\u5f02\u5e38\uff0c\u674e\u9009\u624b\u638c \u5f80\u5916\u4e00\u767b\uff0c\u4e00\u80a1\u5185\u529b\u4e5f\u987f\u65f6\u4f20\u4e86\u8fc7\u53bb\uff0c\u540c\u65f6\u624b\u81c2\u4e00\u8f6c\uff0c\u7528\u4e86\u4e00\u4e2a\u201c\u5378\u201d\u5b57\u8bc0\uff0c\u624b\u638c\u6ed1\u4f3c \u6e38\u9c7c\uff0c\u4ece\u5bf9\u7acb\u7684\u624b\u638c\u4e4b\u4e2d\u6ed1\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u8001\u5934\u513f\u8bf4\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u968f\u5373\u559d\u9053\uff1a\u201c\u7559\u5fc3\u63a5\u6211\u5341\u62db\uff01\u201d\u65f1\u70df\u7a17\u500f\u7136\u6296\u52a8\uff0c\u7adf \u662f\u4e00\u62db\u6781\u5389\u5bb3\u7684\u6253\u7a74\u62db\u6570\uff0c\u70df\u9505\u78b0\u5230\u4ed6\u80f8\u53e3\u7684\u201c\u63fd\u9965\u7a74\u201d\uff0c\u674e\u9038\u541e\u80f8\u5438\u8179\uff0c\u9669\u9669\u907f\u8fc7\uff0c \u80f8\u524d\u8863\u670d\u5df2\u7ed9\u6e85\u4e0a\u4e86\u4e00\u64ae\u70df\u7070\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u8001\u5934\u513f\u7684\u70df\u7a17\u6765\u5f97\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c \u62db\u62db\u90fd\u662f\u70b9\u6253\u674e\u9038\u7684\u547d\u95e8\u5927\u7a74\uff0c\u5357\u5bab\u5c1a\u53eb\u9053\uff1a\u201c\u5927\u54e5\u624b\u4e0b\u7559\u60c5\uff01\u201d\u90a3\u8001\u5934\u513f\u6839\u672c\u4e0d\u4e88\u7406 \u776c\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u4e00\u62db\u7d27\u8fc7\u4e00\u62db\u3002 \u3000\u3000\u674e\u9038\u5fc3\u4e2d\u6012\u9053\uff1a\u201c\u8fd9\u8001\u5934\u513f\u600e\u7684\uff1f\u5982\u6b64\u86ee\u4e0d\u8bb2\u7406\uff0c\u4e00\u89c1\u9762\u5c31\u8981\u53d6\u6211\u6027\u547d\uff1f\u201d\u4ed6\u65bd\u5c55 \u4e86\u5168\u8eab\u672c\u9886\uff0c\u597d\u5bb9\u6613\u907f\u8fc7\u4e09\u62db\uff0c\u9669\u8c61\u73af\u751f\uff0c\u81ea\u77e5\u7a7a\u624b\u96be\u4ee5\u62b5\u5fa1\uff0c\u8fd9\u65f6\u4ed6\u53c8\u5206\u4e0d\u51fa\u5fc3\u795e\u8bf4 \u8bdd\uff0c\u8feb\u5f97\u62d4\u51fa\u5251\u6765\uff0c\u65bd\u5c55\u5e08\u95e8\u7684\u7cbe\u5999\u5251\u6cd5\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u4e00\u62db\u201c\u9f99\u95e8\u9f13\u6d6a\u201d\u6a2a\u524a\u8fc7\u53bb\uff0c\u5251 \u5149\u95ea\u70c1\uff0c\u7aef\u7684\u6709\u5982\u957f\u6c5f\u6d6a\u6d8c\uff0c\u6eda\u6eda\u800c\u6765\uff0c\u4f46\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002\u90a3\u8001\u5934\u513f\u6307\u4e1c \u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u65f6\u800c\u7528\u94c1\u70df\u9505\u78d5\u5f00\u4ed6\u7684\u5b9d\u5251\uff0c\u65f6\u800c\u5012\u6301\u70df\u634d\uff0c\u5f53\u6210\u70b9\u7a74\u6485\u7528\uff0c\u523a\u4ed6\u7684 \u4e09\u5341\u516d\u5904\u5927\u7a74\uff0c\u624b\u6cd5\u5feb\u6377\u65e0\u4f26\u3002\u674e\u9038\u6709\u751f\u4ee5\u6765\uff0c\u8fd8\u4ece\u672a\u89c1\u8fc7\u8fd9\u6837\u5389\u5bb3\u7684\u6253\u7a74\u9ad8\u624b\uff0c\u9976\u662f \u4ed6\u7cbe\u901a\u4e24\u6d3e\u540d\u5bb6\u7684\u5251\u6cd5\uff0c\u4e5f\u4ec5\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5e76\u65e0\u8fd8\u51fb\u4e4b\u529b\u3002 \u3000\u3000\u8fd9\u8001\u5934\u513f\u6240\u6301\u7684\u94c1\u70df\u7a17\u70df\u9505\u7279\u5927\uff0c\u6240\u88c5\u7684\u70df\u53f6\u8981\u6bd4\u666e\u901a\u7684\u70df\u6597\u591a\u4e09\u500d\u6709\u591a\uff0c\u6fc0\u6218\u4e86 \u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u9505\u4e2d\u7684\u70df\u706b\u5c1a\u672a\u7184\u706d\uff0c\u9163\u6597\u4e4b\u4e2d\u8fd9\u8001\u5934\u4e00\u513f\u7a81\u7136\u5438\u4e86\u4e00\u53e3\uff0c\u731b\u5730\u4e00\u80a1\u6d53 \u70df\u55b7\u51fa\uff0c\u968f\u5373\u62a1\u5706\u70df\u7a17\uff0c\u4f3c\u70b9\u975e\u70b9\uff0c\u70df\u96fe\u8ff7\u79bb\u4e2d\uff0c\u7adf\u8fa8\u4e0d\u51fa\u4ed6\u7684\u653b\u52bf\u6307\u5411\u4f55\u5904\u3002\u674e\u9038\u5403 \u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u6a2a\u5251\u4e00\u5c01\uff0c\u8fd9\u4e00\u62db\u662f\u4ed6\u5e08\u7236\u5c09\u8fdf\u70af\u6bd5\u751f\u5fc3\u8840\u4e4b\u6240\u805a\uff0c\u7528\u4e8e\u9632\u5b88\uff0c\u7aef\u7684\u662f\u98ce \u96e8\u4e0d\u9014\uff0c\u4f46\u542c\u5f97\u4e00\u9635\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u6709\u5982\u7e41\u5f26\u6025\u594f\uff0c\u90a3\u8001\u5934\u513f\u5ffd\u9000\u51fa\u5708\u5b50\uff0c\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u5df2\u6ee1\u4e86\u5341\u62db\u4e86\uff01\u9601\u4e0b\u6b66\u529f\u9ad8\u5f3a\uff0c\u53ef\u7b97\u5f97\u662f\u5f53\u4eca\u8c6a\u6770\uff01\u201d \u3000\u3000\u674e\u9038\u63d2\u5251\u5f52\u9798\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u591a\u627f\u8001\u82f1\u96c4\u8fc7\u5956\uff0c\u5e78\u800c\u53ea\u8bd5\u5341\u62db\uff0c\u518d\u6218\u4e0b\u53bb\u3001\u53ef\u5b9e\u975e \u5bf9\u624b\u3002\u201d\u90a3\u8001\u5934\u513f\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u8bf7\u522b\u89c1\u602a\uff0c\u6b64\u6b21\u524d\u5f80\u7a81\u53a5\u738b\u5ef7\uff0c\u76f8\u4f1a\u5404\u65b9\u8c6a\u6770\uff0c\u9601\u4e0b\u65e2 \u4e0e\u6211\u4eec\u540c\u884c\uff0c\u867d\u7136\u5c1a\u672a\u5165\u672c\u5e2e\uff0c\u4e5f\u7b97\u5f97\u662f\u672c\u5e2e\u4e00\u8def\uff0c\u662f\u4ee5\u5c0f\u8001\u513f\u4e0d\u5f97\u4e0d\u5192\u6627\u4e00\u8bd5\u3002\u201d\u674e \u9038\u8fd9\u624d\u660e\u767d\uff0c\u60f3\u5fc5\u8fd9\u8001\u5934\u513f\u4e43\u662f\u4e00\u4e2a\u5f88\u6709\u58f0\u671b\u7684\u5e2e\u4e3b\uff0c\u4e0d\u5c51\u4e0e\u4e0d\u51e1\u4e4b\u8f88\u540c\u884c\uff0c\u6545\u6b64\u8981\u4f38 \u91cf\u4ed6\u7684\u672c\u9886\u3002\u5357\u5bab\u5c1a\u62b9\u4e86\u4e00\u989d\u51b7\u6c57\uff0c\u559c\u5b5c\u5b5c\u7684\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u5144\u5f1f\u6587\u6b66\u53cc\u5168\uff0c\u82e5\u975e\u76f8\u77e5 \u6709\u7d20\uff0c\u6211\u600e\u6562\u9080\u4ed6\u540c\u884c\uff1f\u5927\u54e5\u73b0\u5728\u53ef\u4ee5\u653e\u5fc3\u4e86\u5427\u3002\u201d \u3000\u3000\u674e\u9038\u4e0e\u90a3\u8001\u5934\u513f\u91cd\u65b0\u65bd\u793c\u89c1\u8fc7\uff0c\u95ee\u9053\uff1a\u201c\u672a\u8bf7\u6559\u8001\u82f1\u96c4\u9ad8\u59d3\u5927\u540d\uff0c\u8d35\u5e2e\u5728\u4f55\u5904\u5b89\u7a91 \u7acb\u5be8\uff1f\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u5927\u54e5\u5c31\u662f\u4ee5\u524d\u540d\u9707\u4e2d\u539f\u7684\u4f0f\u864e\u5e2e\u7a0b\u5e2e\u4e3b\uff01\u201d\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c \u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u7a0b\u8fbe\u82cf\uff0c\u5e78\u800c\u4ed6\u7684\u513f\u5b50\u6ca1\u6709\u540c\u6765\u3002\u201d\u7a0b\u8fbe\u82cf\u7684\u513f\u5b50\u5c31\u662f\u4ee5\u524d\u8981\u62a2\u674e\u9038\u5251 \u8c31\u7684\u90a3\u4e2a\u7a0b\u5efa\u7537\uff0c\u674e\u9038\u73b0\u4e0b\u867d\u7136\u5df2\u6539\u5bb9\u6613\u8c8c\uff0c\u4f46\u82e5\u662f\u7a0b\u5efa\u7537\u5728\u65c1\u89c2\u6218\uff0c\u770b\u4e86\u4ed6\u8fd9\u624b\u5251\u6cd5\uff0c \u5b9a\u7136\u53ef\u4ee5\u8bc6\u7834\u4ed6\u7684\u6765\u5386\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u4e0d\u6015\u9601\u4e0b\u89c1\u7b11\uff0c\u4f0f\u864e\u5e2e\u5b9e\u662f\u88ab\u4e00\u5987\u4eba\u6240\u8feb\uff0c\u903c\u5f97\u8fc1\u5230\u585e\u5916\u6765\u7684\u3002\u201d\u674e \u9038\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\u5987\u4eba\uff0c\u5982\u6b64\u5389\u5bb3\uff1f\u201d\u7a0b\u8fbe\u82cf\u54ac\u7259\u5207\u9f7f\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u662f\u5343\u53e4\u4ec5\u89c1\u7684\u5996\u5b7d\uff0c \u4f2a\u5468\u5973\u4e3b\u6b66\u5219\u5929\u5475\uff01\u201d\u539f\u6765\u6b66\u5219\u5929\u8981\u8083\u6e05\u4e3a\u5bb3\u767e\u59d3\u7684\u4e00\u4e9b\u6c5f\u6e56\u5e2e\u4f1a\uff0c\u4f0f\u864e\u5e2e\u4e5f\u5728\u88ab\u8083\u6e05 \u4e4b\u5217\uff0c\u5728\u4e2d\u539f\u7ad9\u4e0d\u4f4f\u811a\uff0c\u8fd9\u624d\u642c\u6765\u7684\u3002\u5357\u5bab\u5c1a\u4e3a\u4e86\u8981\u6295\u9760\u7a0b\u8fbe\u82cf\uff0c\u4e09\u5e74\u524d\u53bb\u585e\u5916\u5165\u5e2e\uff0c \u73b0\u5728\u662f\u4f0f\u864e\u5e2e\u7684\u526f\u5e2e\u4e3b\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u590d\u59d3\u4e0a\u5b98\uff0c\u4e0d\u77e5\u4e0e\u524d\u671d\u5927\u81e3\u4e0a\u5b98\u4eea\u662f\u5426\u4e00\u5bb6\uff1f\u201d\u674e\u9038\u8fd9\u4e2a\u5316\u540d\uff0c \u4e43\u662f\u56e0\u4e0a\u5b98\u5a49\u513f\u800c\u60f3\u8d77\u7684\uff0c\u81f3\u4e8e\u201c\u654f\u201d\u5b57\u5219\u662f\u4ed6\u513f\u5b50\u7684\u540d\u5b57\uff0c\u89c1\u7a0b\u8fbe\u82cf\u95ee\u53ca\uff0c\u968f\u53e3\u4fbf\u7b54 \u9053\uff1a\u201c\u4ed6\u662f\u6211\u758f\u5c1d\u53d4\u7956\u3002\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u9601\u4e0b\u4e5f\u8981\u4ea1\u547d\u8fb9\u8352\u3002\u201d\u7a0b\u8fbe \u82cf\u7591\u5fc3\u7a0d\u51cf\uff0c\u4f46\u60f3\u5230\u5357\u5bab\u5c1a\u4ece\u672a\u5bf9\u4ed6\u8bf4\u8fc7\u6709\u8fd9\u6837\u4e00\u4f4d\u6709\u672c\u9886\u7684\u7ed3\u62dc\u5144\u5f1f\uff0c\u5fc3\u4e0b\u4ecd\u662f\u4e0d\u80fd \u65e0\u7591\uff0c\u4e00\u8def\u4e0a\u8bd5\u63a2\u674e\u9038\u7684\u6765\u5386\uff0c\u674e\u9038\u5c0f\u5fc3\u5e94\u5bf9\uff0c\u5e78\u800c\u672a\u9732\u7834\u7efd\u3002 \u3000\u3000\u5f53\u665a\u5728\u8349\u539f\u5bbf\u8425\uff0c\u7a0b\u8fbe\u82cf\u7d6e\u7d6e\u4e0d\u4f11\u4e0e\u4ed6\u8c08\u8bba\u6b66\u529f\uff0c\u8c08\u5230\u6df1\u591c\uff01\u5c1a\u65e0\u5026\u60eb\uff0c\u8c08\u5174\u6b63\u6d53\uff0c \u7a0b\u8fbe\u82cf\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u7684\u90a3\u67c4\u5251\u771f\u662f\u795e\u7269\u5229\u5668\uff0c\u53ef\u5426\u501f\u6765\u4e00\u89c2\uff1f\u201d \u3000\u3000\u674e\u9038\u672c\u6765\u4e0d\u613f\uff0c\u4f46\u6015\u4ed6\u66f4\u8d77\u7591\u5fc3\uff0c\u53ea\u597d\u89e3\u4e0b\u4f69\u5251\uff0c\u7a0b\u8fbe\u82cf\u63a5\u4e86\u8fc7\u6765\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u4f46 \u89c1\u4e00\u78a7\u5bd2\u5149\uff0c\u6781\u9650\u751f\u65e2\uff0c\u7a0b\u8fbe\u82cf\u4f38\u51fa\u624b\u6307\uff0c\u5728\u5251\u810a\u4e0a\u8f7b\u8f7b\u4e00\u6263\uff0c\u950b\u9505\u58f0\u54cd\uff0c\u5b9b\u82e5\u9f99\u541f\uff0c \u7a0b\u8fbe\u82cf\u55b7\u5578\u79f0\u8d4f\uff0c\u8d5e\u9053\uff1a\u201c\u597d\u5251\uff0c\u597d\u5251\uff0c\u771f\u662f\u4e00\u628a\u5b9d\u5251\uff0c\u602a\u4e0d\u5f97\u8001\u592b\u7684\u94c1\u70df\u7a17\u4e5f\u7ed9\u5b83\u7559 \u4e0b\u4e86\u51e0\u9053\u5251\u75d5\uff01\u201d\u628a\u73a9\u7247\u523b\uff0c\u5ffd\u5730\u5931\u58f0\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u597d\u50cf\u662f\u5927\u5185\u4e4b\u521d\uff1f\u201d\u539f\u6765\u4ed6\u53d1\u73b0 \u4e86\u5251\u67c4\u4e0a\u76d6\u6709\u201c\u79e6\u738b\u5e9c\u201d\u7684\u62f4\u8bb0\uff0c\u674e\u4e16\u6c11\u672a\u505a\u7687\u5e1d\u4e4b\u524d\uff0c\u5c01\u4e3a\u201c\u79e6\u738b\u201d\uff0c\u8fd9\u628a\u5b9d\u5251\u65e2\u7136 \u76d6\u6709\u201c\u79e6\u738b\u5e9c\u201d\u7684\u6807\u8bb0\uff0c\u7eb5\u4f7f\u4e0d\u662f\u674e\u4e16\u6c11\u81ea\u7528\u7684\u4f69\u5251\uff0c\u4e5f\u5f53\u662f\u4ed6\u7684\u5927\u5185\u85cf\u73cd\u3002 \u3000\u3000\u674e\u9038\u65e9\u5df2\u60f3\u597d\uff0c\u4ece\u5bb9\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u628a\u5251\u6b63\u662f\u592a\u5b97\u7687\u5e1d\u8d50\u7ed9\u5bb6\u53d4\u7956\u7684\uff0c\u5f53\u5e74\u592a\u5b97 \u7687\u5e1d\u5728\u6625\u534e\u6bbf\u62db\u5bb4\u7fa4\u81e3\uff0c\u89c2\u8d4f\u5251\u821e\uff0c\u5bb6\u53d4\u7956\u5373\u5e2d\u8d4b\u8bd7\uff0c\u5e94\u5bf9\u79f0\u65e8\uff0c\u7687\u4e0a\u4e43\u5c06\u8fd9\u628a\u5b9d\u5251\u8d50 \u7ed9\u4e86\u4ed6\uff0c\u5bb6\u53d4\u7956\u89c1\u6211\u6027\u559c\u4e60\u6b66\uff0c\u53c8\u5c06\u8fd9\u628a\u5251\u8f6c\u8d50\u7ed9\u6211\u3002\u201d\u4e0a\u5b98\u4eea\u4e43\u662f\u5f53\u671d\u4e00\u54c1\uff0c\u7687\u5e1d\u8d60 \u4ed6\u73cd\u5b9d\uff0c\u539f\u4e5f\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u7a0b\u8fbe\u82cf\u60f3\u5230\u4e0a\u5b98\u4eea\u4e43\u662f\u6587\u81e3\uff0c\u867d\u8bf4\u662f\u56e0\u548f\u201c\u5251\u821e\u201d\u800c\u5f97\u8d50\u5251\uff0c \u4e8e\u7406\u4ea6\u901a\uff0c\u4f46\u7a76\u7adf\u4e0d\u5408\u4ed6\u7684\u8eab\u4efd\uff0c\u5fc3\u4e2d\u53c8\u591a\u4e86\u4e00\u79cd\u7591\u56e2\uff01 \u3000\u3000\u674e\u9038\u4ea6\u81ea\u5fc3\u4e2d\u60f4\u60f4\uff0c\u6b63\u5f85\u6536\u8d77\u5b9d\u5251\uff0c\u7a0b\u8fbe\u82cf\u5ffd\u5730\u53cc\u76ee\u4e00\u5f20\uff0c\u559d\u9053\uff1a\u201c\u5e10\u5916\u662f\u8c01\uff1f\u201d \u8bdd\u72b9\u672b\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u88c2\u5e1b\uff0c\u5e10\u5e55\u6495\u5f00\uff0c\u6709\u4eba\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e09\u4e2a\u6295\u6717\u53db\u56fd\u7684\u5978\u8d3c\uff0c \u5403\u6211\u4e00\u5200\uff01\u201d\u4e09\u67c4\u660e\u6643\u6643\u7684\u98de\u5200\uff0c\u4fbf\u4ece\u5e10\u5e55\u7684\u88c2\u7f1d\u98de\u4e86\u8fdb\u6765\uff0c\u5206\u53d6\u4e09\u4eba\uff0c\u674e\u9038\u6a2a\u5251\u4e00\u524a\uff0c \u5c06\u98de\u5200\u524a\u4e3a\u4e24\u7247\uff0c\u5357\u5bab\u5c1a\u95ea\u8eab\u8eb2\u5f00\uff0c\u7a0b\u8fbe\u82cf\u5219\u6709\u610f\u5356\u5f04\u6b66\u529f\uff0c\u4f38\u6307\u4e00\u5f39\uff0c\u950b\u7684\u4e00\u58f0\uff0c\u5c06 \u98de\u5200\u5f39\u51fa\u5e10\u5916\uff0c\u53cd\u88ad\u654c\u4eba\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u51b7\u7b11\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u6b66\u5219\u5929\u6d3e\u6765\u7684\u4eba\uff0c\u5357\u5bab\u5c1a\uff0c\u4f60\u66ff\u6211\u628a\u4ed6\u6740\u4e86\u3002\u201d\u7a0b\u8fbe\u82cf\u672b \u66fe\u51fa\u53bb\uff0c\u90a3\u4eba\u5df2\u62a2\u8fdb\u6765\uff0c\u4e00\u5200\u5411\u5357\u5bab\u5c1a\u5288\u4e0b\uff0c\u7a0b\u8fbe\u82cf\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201c\uff0c\u7acb\u523b\u4f7f\u4e86\u4e00 \u62db\u201c\u7a7f\u82b1\u624b\u201d\uff0c\u53cd\u6263\u4ed6\u7684\u8109\u95e8\uff0c\u90a3\u4eba\u5200\u6cd5\u7cbe\u5947\uff0c\u8eab\u6cd5\u7075\u654f\uff0c\u5357\u5bab\u5c1a\u64d2\u4e0d\u7740\u4ed6\uff0c\u53cd\u800c\u7ed9\u4ed6 \u8fde\u5288\u4e09\u5200\uff0c\u51e0\u4e4e\u65ab\u7740\uff0c\u7a0b\u8fbe\u82cf\u559d\u9053\uff1a\u201c\u51fa\u5e10\u5916\u6253\u53bb\uff0c\u4f11\u5f97\u6270\u6518\u8001\u592b\uff01\u201d\u8fde\u53d1\u4e24\u6b21\u5288\u7a7a\u638c\uff0c \u638c\u98ce\u6fc0\u8361\uff0c\u8feb\u5f97\u90a3\u4eba\u51e0\u4e4e\u7acb\u8db3\u4e0d\u7a33\uff0c\u5927\u5927\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u7eb5\u6a2a\u6c5f\u6e56\u7684\u4f0f\u864e\u5e2e\u5e2e\u4e3b\uff0c \u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u5728\u5e10\u4e2d\u7acb\u4e0d\u4f4f\u8db3\uff0c\u53ea\u597d\u8dd1\u51fa\u3002 \u3000\u3000\u8fd9\u65f6\u674e\u9038\u4e0e\u5357\u5b98\u5c1a\u90fd\u5df2\u8ba4\u51fa\u4e86\u6765\u4eba\u7684\u9762\u76ee\uff0c\u539f\u6765\u5c31\u662f\u90a3\u4e2a\u4ee5\u524d\u5047\u4f5c\u53cd\u5bf9\u6b66\u5219\u5929\uff0c\u9a97 \u8fc7\u674e\u9038\u7684\u90a3\u4e2a\u795e\u6b66\u8425\u536b\u58eb\u767d\u5143\u5316\uff0c\u4ed6\u7684\u98de\u5200\u7edd\u6280\uff0c\u5728\u6b66\u6797\u4e2d\u53ef\u7b97\u4e00\u7edd\uff0c\u6bd4\u4e4b\u6628\u65e5\u90a3\u4e24\u4e2a \u82b1\u523a\u5b50\u6a21\u5546\u4eba\uff0c\u90a3\u662f\u9ad8\u5f97\u591a\u4e86\u3002 \u3000\u3000\u5357\u5bab\u5c1a\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u767d\u5143\u5316\u4f60\u8fd9\u5c0f\u5b50\uff0c\u6211\u6b63\u60f3\u627e\u4f60\u7b97\u5e10\uff0c\u4f60\u5374\u81ea\u6295\u7f57\u7f51\u6765\u4e86\uff01\u201d \u8ffd\u51fa\u5e10\u5916\uff0c\u89e3\u4e0b\u4e86\u56f4\u8170\u7684\u8f6f\u97ad\uff0c\u4e00\u624b\u6301\u97ad\uff0c\u4e00\u624b\u6301\u5200\uff0c\u4e0e\u767d\u5143\u5316\u6076\u6597\uff0c\u4e24\u4eba\u6b66\u529f\u76f8\u82e5\uff0c \u767b\u65f6\u6253\u5f97\u4e2a\u96be\u5206\u96be\u89e3\u3002\u767d\u5143\u5316\u626c\u58f0\u53eb\u9053\uff1a\u201c\u6cf0\u5144\u5feb\u6765\uff0c\u5357\u5bab\u5c1a\u8fd9\u6b7c\u8d3c\u5728\u8fd9\u91cc\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u7a0b\u8fbe\u82cf\u548c\u674e\u9038\u90fd\u5df2\u8d70\u5230\u5e10\u5916\u89c2\u6218\uff0c\u7a0b\u8fbe\u82cf\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4f0f\u864e\u5e2e\u8fc1\u5230\u585e\u5916\uff0c\u5df2\u7b97 \u5f97\u662f\u6015\u4e86\u4f60\u8fd9\u4e2a\u5996\u5987\u4e86\uff0c\u4f60\u5374\u8fd8\u653e\u4e0d\u8fc7\u6211\uff0c\u4e07\u91cc\u8fe2\u8fe2\u7684\u6d3e\u4eba\u6765\u8ffd\u8e2a\u6211\u4e48\uff1f\u597d\uff0c\u6211\u5012\u8981\u770b \u770b\u4f60\u6d3e\u6765\u7684\u662f\u4e9b\u4ec0\u4e48\u4eba\uff0c\u6709\u591a\u5927\u7684\u672c\u9886\uff1f\u201d\u4ed6\u53e3\u4e2d\u6240\u9a82\u7684\u201c\u5996\u5987\u201d\uff0c\u6307\u7684\u5f53\u7136\u662f\u6b66\u5219\u5929\u3002 \u674e\u9038\u6697\u6697\u597d\u7b11\uff0c\u770b\u7a0b\u8fbe\u82cf\u8fd9\u6837\u88c1\u6307\u75db\u9a82\u7684\u795e\u60c5\uff0c\u5c31\u597d\u50cf\u6b66\u5219\u5929\u7ad9\u5728\u4ed6\u7684\u9762\u524d\u4e00\u822c\u3002\u674e\u9038 \u5fc3\u9053\uff1a\u201c\u6b66\u5219\u5929\u867d\u7136\u593a\u53bb\u4e86\u674e\u6c0f\u7684\u6c5f\u5c71\uff0c\u5979\u5374\u771f\u662f\u4e2a\u6709\u624d\u5e72\u7684\u5973\u4eba\uff0c\u7a0b\u8fbe\u82cf\u5492\u9a82\u5979\u4f5c\u5996 \u5987\uff0c\u672a\u514d\u592a\u65e0\u804a\u4e86\u3002\u201d \u3000\u3000\u767d\u5143\u5316\u9ad8\u58f0\u53eb\u5524\uff0c\u4ed6\u7684\u540c\u4f34\u5374\u8fd8\u672a\u9732\u8e2a\u5f71\uff0c\u5357\u5bab\u5c1a\u7528\u5de6\u624b\u5200\u821e\u5f00\u201c\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\u201d \u5c01\u4f4f\u5168\u8eab\u95e8\u6237\uff0c\u963b\u904f\u4e86\u767d\u5143\u5316\u7684\u653b\u52bf\uff0c\u53f3\u624b\u957f\u97ad\u6325\u821e\uff0c\u62e6\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\u3002\u53cc\u65b9\u53c8\u6fc0\u6218\u4e86 \u5341\u4f59\u62db\uff0c\u5357\u5bab\u5c1a\u7a0d\u7a0d\u5360\u5f97\u4e0a\u98ce\uff0c\u4f46\u767d\u5143\u5316\u7684\u5200\u6cd5\u4ecd\u7136\u4e1d\u6beb\u672a\u4e71\u3002\u7a0b\u8fbe\u82cf\u76b1\u7709\u9053\uff1a\u201c\u5357\u5bab \u5c1a\u600e\u4e48\u8fde\u8fd9\u4e2a\u5c0f\u5b50\u4e5f\u6536\u62fe\u4e0d\u6765\uff1f\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u8349\u539f\u4e0a\u9a6c\u8e44\u58f0\u54cd\uff0c\u4e00\u9a91\u9a6c\u8fdc\u8fdc\u5954\u6765\u3002\u767d\u5143\u5316\u5927\u559d\u4e00\u58f0\uff0c\u84e6\u7136\u95f4\u957f \u5200\u4e00\u5288\uff0c\u5c06\u5357\u5bab\u5c1a\u51b2\u5f97\u659c\u8eab\u95ea\u907f\uff0c\u7acb\u523b\u593a\u8def\u5954\u51fa\uff0c\u5357\u5bab\u5c1a\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u5982\u5f71\u968f\u5f62\uff0c \u8ddf\u8e2a\u6025\u4e0a\uff0c\u957f\u97ad\u6296\u52a8\uff0c\u97ad\u68a2\u5377\u5230\u4e86\u4ed6\u7684\u8863\u89d2\uff0c\u767d\u65e0\u5316\u84e6\u5730\u559d\u4e00\u58f0\u3002\u201c\u7740\uff01\u201d\u53cd\u624b\u4fbf\u662f\u4e09 \u67c4\u98de\u5200\uff0c\u5357\u5bab\u5c1a\u6599\u4e0d\u5230\u4ed6\u53d1\u5200\u7684\u624b\u6cd5\u7adf\u662f\u5982\u6b64\u8fc5\u6377\uff0c\u767e\u5fd9\u4e2d\u4f7f\u4e86\u4e00\u4e2a\u201c\u94c1\u677f\u6865\u201d\u7684\u8eab\u6cd5\uff0c \u8170\u5411\u540e\u5f2f\uff0c\u4f46\u542c\u5f97\u201c\u607b\u201d\u7684\u4e00\u58f0\u3002\u4e24\u67c4\u98de\u5200\u4ece\u4ed6\u9762\u95e8\u98de\u8fc7\uff0c\u7b2c\u4e09\u67c4\u98de\u5200\u65ab\u4e2d\u4e86\u4ed6\u7684\u989d\u89d2\u3002 \u674e\u9038\u65b9\u9053\u5357\u5bab\u5c1a\u8981\u7cdf\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5c16\u9510\u7684\u7b11\u58f0\uff0c\u7d27\u63a5\u7740\u201c\u54af\u54af\u201d\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u7684\u7adf\u7136 \u4e0d\u662f\u5357\u5bab\u5c1a\u800c\u662f\u767d\u5143\u5316\uff0c\u539f\u6765\u662f\u7a0b\u8fbe\u82cf\u6697\u4e2d\u53d1\u51fa\u4e86\u4e00\u7c92\u94c1\u83b2\u5b50\uff0c\u6253\u4e2d\u4e86\u767d\u5143\u5316\u7684\u7a74\u9053\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u90a3\u9a91\u9a6c\u5df2\u98de\u5954\u6765\u5230\uff0c\u9a6c\u4e0a\u7684\u9a91\u5ba2\u662f\u4e00\u4e2a\u8eab\u6750\u9b41\u4f1f\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u4f46\u89c1\u9a6c\u672a \u505c\u8e44\uff0c\u4ed6\u4fbf\u5728\u9a6c\u80cc\u4e0a\u4f7f\u4e86\u4e00\u4e2a\u201c\u4e00\u9e64\u51b2\u5929\u201d\u7684\u8eab\u6cd5\uff0c\u51cc\u7a7a\u98de\u8d77\uff0c\u5728\u534a\u5bb6\u4e2d\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c \u7acb\u5373\u4fbf\u662f\u4e00\u62db\u201c\u9e70\u51fb\u957f\u7a7a\u201d\uff0c\u5411\u5357\u5bab\u5c1a\u5f53\u5934\u523a\u4e0b\uff01 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u674e\u9038\u5982\u53d7\u96f7\u9707\uff0c\u60ca\u9a87\u4e07\u5206\uff01\u8fd9\u4e00\u62db\u201c\u9e70\u51fb\u957f\u7a7a\u201d\uff0c\u6b63\u662f\u4ed6\u5cb3\u7236\u957f\u5b59\u5747\u91cf \u6240\u521b\u7684\u5ce8\u5d4b\u5251\u6cd5\uff0c\u770b\u6e05\u695a\u4e86\uff0c\u8fd9\u4e2a\u6c49\u5b50\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u674e\u9038\u59bb\u5b50\u957f\u5b59\u58c1\u7684\u54e5\u54e5\uff0c\u957f\u5b59\u5747 \u91cf\u7684\u513f\u5b50\u957f\u5b59\u6cf0\uff01 \u3000\u3000\u674e\u9038\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u662f\u4ed6\uff0c\u957f\u5b59\u58c1\u66fe\u7ecf\u544a\u8bc9\u8fc7\u4ed6\uff0c\u90a3\u4e00\u665a\u5728\u8155\u5c71\u5c71\u811a\uff0c\u957f\u5b59\u5747\u91cf\u548c\u5979 \u5144\u59b9\u4e8c\u4eba\u78b0\u5230\u4e86\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u4e24\u4e2a\u5927\u9b54\u5934\uff0c\u957f\u5b59\u6cf0\u4e2d\u4e86\u6076\u884c\u8005\u7684\u6bd2\u638c\uff0c\u53c8\u88ab\u6bd2\u89c2\u97f3\u6253 \u4e86\u4e00\u7bf7\u900f\u7a74\u795e\u9488\uff0c\u6700\u540e\u4ed6\u820d\u547d\u62b1\u7740\u4e86\u6076\u884c\u8005\uff0c\u65e9\u5df2\u4e0e\u6076\u884c\u8005\u540c\u5f52\u4e8e\u5c3d\uff0c\u5728\u957f\u5b59\u58c1\u7684\u5fc3\u76ee \u4e2d\uff0c\u4e5f\u65e9\u5df2\u628a\u8fd9\u4e2a\u54e5\u54e5\u5f53\u4f5c\u6b7b\u4e86\uff0c\u600e\u7684\u8fd8\u5c45\u7136\u6d3b\u5728\u4e16\u95f4\uff1f\u8fd9\u8fd8\u4e0d\u7b97\u5947\u602a\uff0c\u957f\u5b59\u5747\u91cf\u4e00\u5bb6 \u90fd\u662f\u75db\u6068\u6b66\u5219\u5929\u505a\u7687\u5e1d\uff0c\u53d1\u8a93\u4e0e\u6b66\u5219\u5929\u4e0d\u5171\u6234\u5929\u7684\uff0c\u767d\u5143\u5316\u662f\u6b66\u5219\u5929\u6d3e\u6765\u7f09\u6355\u5357\u5bab\u5c1a\u7684 \u4eba\uff0c\u957f\u5b59\u6cf0\u5374\u600e\u4e48\u4f1a\u4e0e\u4ed6\u540c\u5728\u4e00\u8d77\uff0c\u53cd\u800c\u4e0e\u4ed6\u7684\u4e16\u5144\u5357\u5bab\u5c1a\u4e3a\u654c\uff1f \u3000\u3000\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u5357\u5bab\u5c1a\u7684\u94a2\u5200\u5df2\u88ab\u524a\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u60ca\u8be7\u4e4b\u6781\uff0c\u5931 \u58f0\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u4e0d\u662f\u957f\u5b59\u5144\u4e48\uff1f\u201d\u8981\u77e5\u957f\u5b59\u5747\u91cf\u505a\u592a\u5b97\u7687\u7684\u6bbf\u524d\u68c0\u70b9\u4e4b\u65f6\u3002\u5357\u5bab\u5c1a\u7684 \u7236\u4eb2\u6b63\u662f\u4ed6\u6700\u5f97\u529b\u7684\u90e8\u4e0b\uff0c\u5f53\u5e74\uff0c\u5e26\u5f15\u957f\u5b59\u6cf0\u5144\u59b9\u5230\u7f05\u5c71\u5c71\u811a\u63a5\u5e94\u674e\u9038\u7684\u4e5f\u6b63\u662f\u5357\u5bab\u5c1a\uff0c \u5982\u4eca\u7a81\u7136\u89c1\u957f\u5b59\u6cf0\u8e0a\u5230\uff0c\u5357\u5b98\u5c1a\u7109\u80fd\u4e0d\u5927\u4e3a\u60ca\u5947\uff1f \u3000\u3000\u957f\u5b59\u6cf0\u559d\u9053\uff1a\u201c\u5357\u5bab\u5c1a\uff0c\u5ff5\u5728\u4f60\u6211\u4e24\u5bb6\u7684\u4ea4\u60c5\uff0c\u4f60\u968f\u6211\u56de\u8f6c\u957f\u5b89\uff0c\u6211\u53ef\u4ee5\u66ff\u4f60\u5411\u5929 \u540e\u6c42\u6055\uff01\u201d\u5357\u5bab\u5c1a\u597d\u50cf\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u5411\u5929\u540e\u6c42\u6055\uff1f\u4f60\uff0c\u4f60\u662f\u6295\u9760 \u4e86\u6b66\u5219\u5929\u5566\uff01\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u4f60\u613f\u6295\u987a\u6b66\u5219\u5929\u90a3\u662f\u4f60\u7684\u4e8b\u60c5\uff0c\u6211\u7ba1\u4e0d\u7740\u3002\u4f46 \u4f60\u8981\u6295\u987a\u7a81\u53a5\u53ef\u6c57\uff0c\u8fd9\u6211\u5374\u975e\u7ba1\u4e0d\u53ef\uff0c\u5982\u4eca\u53ea\u6709\u4e24\u6761\u8def\u7ed9\u4f60\u9009\u62e9\uff0c\u4e00\u6761\u662f\u4f60\u5c06\u529f\u8d4e\u7f6a\uff0c \u4e0e\u6211\u628a\u8fd9\u8001\u8d3c\u64d2\u4e86\uff0c\u62bc\u56de\u957f\u5b89\uff0c\u53e6\u4e00\u6761\u662f\u4f60\u8ddf\u8fd9\u8001\u8d3c\u8d70\uff0c\u54b1\u4eec\u5144\u5f1f\u6069\u65ad\u4e49\u7edd\uff0c\u51ed\u7740\u624b\u4e2d \u5200\u5251\uff0c\u51b3\u4e2a\u6b7b\u751f\uff01\u201d\u957f\u5b59\u6cf0\u53e3\u4e2d\u7684\u201c\u8001\u8d3c\u201d\uff0c\u6307\u7684\u5f53\u7136\u662f\u7a0b\u8fbe\u82cf\uff0c\u7a0b\u8fbe\u82cf\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u65e0\u77e5\u5c0f\u8f88\uff0c\u5984\u51fa\u5927\u8a00\u3002\u597d\u5440\uff0c\u5357\u5bab\u5c1a\uff0c\u4f60\u9009\u62e9\u5427\uff0c\u4f60\u542c\u4ed6\u7684\u8bdd\uff0c\u5c31\u4e0e\u4ed6\u4e00\u9f50\u4e0a\u6765\uff0c\u4f60 \u542c\u6211\u7684\u8bdd\uff0c\u5c31\u4e0e\u6211\u4e00\u5200\u5c06\u4ed6\u6740\u4e86\u3002\u201d \u3000\u3000\u5357\u5bab\u5c1a\u4e00\u6765\u662f\u754f\u60e7\u7a0b\u8fbe\u82cf\uff0c\u5728\u4ed6\u79ef\u5a01\u4e4b\u4e0b\uff0c\u4e0d\u6562\u4e0d\u4ece\u3002\u4e8c\u6765\u4ed6\u4ee5\u524d\u884c\u523a\u8fc7\u6b66\u5219\u5929\uff0c \u7edd\u4e0d\u76f8\u4fe1\u6b66\u5219\u5929\u4f1a\u5bbd\u6055\u4ed6\uff0c\u4e09\u6765\u4ed6\u60f3\u6295\u9760\u7a81\u53a5\u53ef\u6c57\u4e4b\u5fc3\u5df2\u975e\u4e00\u65f6\uff0c\u957f\u5b59\u6cf0\u53ea\u51ed\u7740\u4e09\u8a00\u4e24 \u8bed\uff0c\u53c8\u7109\u80fd\u6253\u52a8\u4ed6\uff1f\u53ea\u89c1\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u7136\u4e00\u54ac\u7259\u6839\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7a0b\u5927\u54e5\uff0c\u6211\u5f53\u7136\u542c \u4f60\u7684\u3002\u201d\u731b\u5730\u4e00\u5200\u5288\u51fa\uff0c\u957f\u5b59\u6cf0\u5927\u6012\uff0c\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u4fa7\u8eab\u95ea\u5f00\uff0c\u957f\u5251\u4e00\u633a\uff0c\u5206\u5fc3\u4fbf\u523a\uff0c \u559d\u9053\uff1a\u201c\u597d\uff01\u4f60\u65e2\u7518\u5fc3\u4e3a\u864e\u4f5c\u6005\uff0c\u4f11\u602a\u6211\u624b\u4e0b\u65e0\u60c5\uff01\u201d\u5251\u5149\u970d\u970d\uff0c\u7acb\u5373\u5c55\u5f00\u4e86\u4e00\u6d3e\u8fdb\u624b \u62db\u6570\u3002 \u3000\u3000\u674e\u9038\u6b63\u81ea\u5fc3\u795e\u4e0d\u5b9a\uff0c\u5ffd\u542c\u5f97\u7a0b\u8fbe\u82cf\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u6211\u770b\u8fd9\u5c0f\u5b50\u7684\u5251\u672f\u9887\u662f\u4e0d\u51e1\uff0c \u5357\u5bab\u5c1a\u53ef\u80fd\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4f46\u6bd4\u8d77\u4f60\u6765\uff0c\u5374\u8fd8\u6709\u6240\u4e0d\u53ca\u3002\u201d\u8a00\u4e0b\u4e4b\u6025\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u4e43\u662f \u60f3\u8bf7\u674e\u9038\u51fa\u624b\u3002\u674e\u9038\u88c5\u4f5c\u4e0d\u61c2\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u7a0b\u5e2e\u4e3b\u8fc7\u5956\u4e86\u3002\u201d\u7a0b\u8fbe\u82cf\u89c1\u4ed6\u73e0\u65e0\u52a8\u624b\u4e4b \u610f\uff0c\u7591\u5fc3\u66f4\u5927\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u4f46\u89c1\u5357\u5bab\u5c1a\u7684\u5de6\u624b\u5df2\u88ab\u957f\u5b59\u6cf0\u524a \u65ad\uff0c\u53ea\u5269\u4e0b\u53f3\u624b\u7684\u4e00\u6761\u957f\u97ad\uff0c\u6321\u4e0d\u4f4f\u957f\u5b59\u6cf0\u7684\u653b\u52bf\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u5251\u52bf\u5982\u8679\uff0c\u6b65\u6b65\u8fdb\u903c\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u7978\u798f\u65e0\u95e8\uff0c\u7531\u4eba\u81ea\u62db\uff0c\u5357\u5bab\u5c1a\u4f60\u5c1a\u672a\u4fae \u609f\u4e48\uff1f\u201d\u4e00\u62db\u201c\u5c4b\u6c49\u6d6e\u6413\u201d\uff0c\u5251\u5c16\u76f4\u6307\u5230\u4e86\u5357\u5bab\u5c1a\u7684\u54bd\u5589\uff0c\u6b63\u8981\u559d\u5357\u5bab\u5c1a\u6295\u964d\uff0c\u5ffd\u5730\u4e00 \u822c\u6d53\u70df\u8fce\u9762\u55b7\u6765\uff0c\u5357\u5bab\u5c1a\u8d81\u6b64\u65f6\u673a\uff0c\u5012\u7eb5\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u70df\u96fe\u8ff7\u6f2b\uff0c\u957f\u5b59\u6cf0\u4e00\u5251\u5241\u7a7a\uff0c\u53ea \u542c\u7a0b\u8fbe\u82cf\u5df2\u5728\u4ed6\u8033\u8fb9\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u53eb\u4f60\u89c1\u8bc6\u8001\u592b\u7684\u672c\u9886\uff01\u201d\u597d\u4e2a\u957f\u5b59\u6cf0\uff0c\u5c45\u7136\u4e34\u5371\u4e0d\u4e71\uff0c \u8eab\u8eaf\u4e00\u77ee\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u6b63\u597d\u6321\u7740\u7a0b\u8fbe\u82cf\u7684\u94c1\u70df\u9505\uff0c\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c \u54cd\uff0c\u7a0b\u8fbe\u82cf\u7684\u529f\u529b\u6df1\u539a\u5f97\u591a\uff0c\u8fd9\u4e00\u62db\u957f\u5b59\u6cf0\u867d\u7136\u6321\u8fc7\uff0c\u864e\u53e3\u4ea6\u5df2\u88ab\u9707\u5f97\u9178\u9ebb\uff01 \u3000\u3000\u7a0b\u8fbe\u82cf\u7528\u94c1\u70df\u6597\u55b7\u70df\u6253\u7a74\u7684\u529f\u592b\uff0c\u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u4ed6\u4e00\u51fa\u624b\u4fbf\u7528\u4e0a\u8fd9\u95e8\u7edd\u6280\uff0c\u5b9e\u662f \u60f3\u5728\u7167\u9762\u4e4b\u95f4\uff0c\u4fbf\u5c06\u957f\u5b59\u6cf0\u51fb\u5012\uff0c\u5c82\u77e5\u4ecd\u7ed9\u957f\u5b59\u6cf0\u683c\u5f00\uff0c\u7a0b\u8fbe\u82cf\u4e5f\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u4e0d \u6562\u8fc7\u4efd\u8f7b\u654c\u3002\u5f53\u4e0b\u5c06\u70df\u634d\u4e00\u6296\u4e32\u6210\u5c0f\u82b1\u67aa\u7528\uff0c\u5411\u524d\u4e00\u6233\uff0c\u6296\u8d77\u4e86\u7897\u53e3\u5927\u7684\u67aa\u82b1\uff0c\u7247\u523b\u4e4b \u95f4\uff0c\u8fde\u88ad\u957f\u5b59\u6cf0\u5de6\u53f3\u4e24\u80c1\u7684\u516d\u5904\u7a74\u9053\u3002\u957f\u5b59\u6cf0\u79fb\u5f62\u6362\u6b65\uff0c\u7528\u4e86\u4e00\u62db\u201c\u767d\u9e64\u4eae\u7fc5\u201d\u4ee5\u5feb\u6253 \u5feb\uff0c\u77ac\u606f\u4e4b\u95f4\u548c\u4ed6\u7684\u70df\u634d\u63a5\u89e6\u4e86\u516d\u4e0b\uff0c\u867d\u7136\u7ed9\u7a0b\u8fbe\u82cf\u8feb\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u53ef\u662f\u7a0b\u8fbe\u82cf\u4e5f\u672a\u80fd \u523a\u4e2d\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u53c8\u5438\u4e86\u4e00\u53e3\u6d53\u70df\u55b7\u51fa\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u5c1a\u79f0\u4e0d\u4fd7\uff0c\u53ef\u662f\u8c05\u4e5f\u96be\u6321\u6ee1\u5341\u62db\u3002\u201d \u957f\u5b59\u6cf0\u6015\u4ed6\u6697\u7b97\uff0c\u62a2\u5230\u9006\u98ce\u4e4b\u5904\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u7a0b\u8fbe\u82cf\u5982\u5f71\u968f\u5f62\uff0c\u957f\u5b59\u6cf0\u524d\u811a\u843d\u5730\uff0c\u7a0b\u8fbe \u82cf\u540e\u811a\u4fbf\u5230\uff0c\u70df\u634d\u53c8\u6572\u5230\u4e86\u4ed6\u7684\u540e\u5fc3\u3002\u957f\u5b59\u6cf0\u6025\u4f7f\u201c\u5012\u8e29\u4e03\u661f\u6b65\u201d\uff0c\u5de6\u811a\u53f3\u6ed1\uff0c\u5251\u968f\u8eab \u8f6c\uff0c\u53cd\u624b\u4e00\u62db\u201c\u5012\u6d12\u91d1\u94b1\u201d\uff0c\u5251\u5149\u95ea\u70c1\uff0c\u65e2\u6551\u8d25\u62db\uff0c\u590d\u622a\u654c\u638c\u3002\u7a0b\u8fbe\u82cf\u6570\u9053\uff1a\u201c\u4e09\u62db\u201d\uff0c \u70df\u634d\u5411\u4e0a\u4e00\u6311\uff0c\u5bfb\u7455\u62b5\u9699\uff0c\u518d\u523a\u957f\u5b59\u6cf0\u808b\u4e0b\u7684\u201c\u9b42\u7a74\u95e8\u201d\uff0c\u7d27\u8ddf\u7740\u53c8\u662f\u4e00\u53e3\u6d53\u70df\u55b7\u53bb\u3002 \u3000\u3000\u674e\u9038\u51dd\u795e\u89c2\u6218\uff0c\u5fc3\u9053\uff1a\u201c\u4e00\u522b\u516b\u5e74\uff0c\u957f\u5b59\u6cf0\u7684\u5251\u672f\u4ea6\u5df2\u5927\u6709\u8fdb\u5883\uff0c\u53ef\u662f\u5374\u96be\u6321\u6ee1\u5341 \u62db\u3002\u201d\u7a0b\u8fbe\u82cf\u672c\u662f\u4e2d\u539f\u7b2c\u4e00\u70b9\u7a74\u529f\u592b\uff0c\u5728\u4e94\u5341\u5c81\u4ee5\u524d\uff0c\u7528\u7684\u5175\u5668\u662f\u70b9\u7a74\u6485\uff0c\u957f\u8fbe\u4e09\u5c3a\u516d \u5bf8\uff0c\u6bd4\u5176\u4ed6\u5404\u6d3e\u7684\u5175\u5668\u90fd\u957f\u5f97\u591a\uff0c\u6b66\u6797\u4e2d\u6709\u53e5\u8bdd\u8bf4\uff0c\u70b9\u7a74\u7684\u5175\u5668\u4e43\u662f\u201c\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\u201d\u3002 \u4ed6\u7684\u8bf4\u6cd5\u5219\u662f\u201c\u4e00\u5bf8\u957f\uff0c\u4e00\u5bf8\u5f3a\u201d\u6240\u4ee5\u4e0d\u8bba\u5175\u5668\u4e0e\u624b\u6cd5\uff0c\u90fd\u4e0e\u5404\u5bb6\u5404\u6d3e\u5927\u4e0d\u76f8\u540c\u3002\u5230\u4e86 \u4e94\u5341\u5c81\u4e4b\u540e\uff0c\u4ed6\u6539\u7528\u94c1\u70df\u634d\u70b9\u7a74\uff0c\u70df\u634d\u7684\u957f\u5ea6\u4e5f\u662f\u4e09\u5c3a\u516d\u5bf8\uff0c\u53ef\u4ee5\u5f53\u6210\u70b9\u7a74\u6485\u7528\uff0c\u4f46\u56e0 \u4e3a\u53ef\u4ee5\u55b7\u62d9\u6d53\u70df\u8ff7\u4eba\u773c\u76ee\uff0c\u6bd4\u8d77\u957f\u70b9\u7a74\u6485\u66f4\u4e3a\u5389\u5bb3\u3002\u957f\u5b59\u6cf0\u7684\u5251\u672f\u867d\u7136\u4e0d\u9519\uff0c\u53ef\u662f\u4e00\u6765 \u529f\u529b\u4e0d\u53ca\uff0c\u4e8c\u6765\u53c8\u4e0d\u61c2\u5e94\u4ed8\u4ed6\u8fd9\u79cd\u70b9\u7a74\u7684\u602a\u62db\u3002\u4e0d\u8fc7\u51e0\u62db\uff0c\u679c\u7136\u4fbf\u7ed9\u7a0b\u8fbe\u82cf\u6740\u5f97\u624b\u5fd9\u811a \u4e71\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u957f\u5b59\u6cf0\u4e00\u5251\u523a\u51fa\uff0c\u624e\u4e86\u4e2a\u7a7a\uff0c\u811a\u5c16\u70b9\u5730\uff0c\u8eab\u5f62\u7acb\u5373\u5411\u540e\u5012\u7eb5\uff0c\u4ed6\u8fd9\u4e00\u62db\u672c\u6765 \u662f\u201c\u4ee5\u8fdb\u4e3a\u9000\u201d\u7684\u3002\u5c82\u77e5\u8fde\u8fd9\u4e00\u62db\u4e5f\u65e9\u5728\u7a0b\u8fbe\u82cf\u610f\u6599\u4e4b\u4e2d\uff0c\u4f46\u542c\u4ed6\u4e00\u58f0\u559d\u9053\uff1a\u201c\u5f80\u54ea\u91cc \u8d70\uff1f\u201d\u7a0b\u8fbe\u82cf\u7a9c\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u4fe8\u5982\u98de\u9e70\u6251\u5154\uff0c\u94c1\u70df\u9505\u7167\u7740\u957f\u5b59\u6cf0\u7684\u9876\u95e8\u6253\u4e0b\u6765\uff0c\u82e5\u7136\u6253 \u4e2d\uff0c\u957f\u5b59\u6cf0\u7109\u6709\u547d\u5728\uff1f \u3000\u3000\u8fd9\u5728\u8fd9\u7edd\u9669\u7684\u5173\u5934\uff0c\u5ffd\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u674e\u9038\u5ffd\u7136\u4e00\u5251\u98de\u6765\uff0c\u67b6\u4f4f\u4e86\u7a0b \u8fbe\u82cf\u7684\u70df\u634d\uff0c\u7a0b\u8fbe\u82cf\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4f46\u89c1\u957f\u5b59\u6cf0\u53cc\u819d\u5f2f\u66f2\u3001 \u8eab\u5b50\u4e5f\u8f6f\u4e86\u4e0b\u53bb\u3002\u539f\u6765\u7a0b\u8fbe\u82cf\u7684\u70df\u6597\u867d\u7136\u6ca1\u6709\u7838\u4e2d\u4ed6\u7684\u9876\u95e8\uff0c\u978b\u5c16\u5374\u5df2\u8e22\u4e2d\u4e86\u4ed6\u817f\u5f2f\u7684 \u201c\u767d\u5e02\u7a74\u3002\u201d\u674e\u9038\u89c1\u4ed6\u70b9\u7a74\u7684\u529f\u592b\u5982\u6b64\u5389\u5bb3\uff0c\u6697\u6697\u5fc3\u60ca\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u7a0b\u8001\u5e2e\u4e3b\uff0c \u7559\u4e2a\u6d3b\u53e3\u4e0d\u80dc\u4e8e\u5c06\u4ed6\u6253\u6b7b\u5417\uff1f\u201d\u5357\u5bab\u5c1a\u5f53\u7136\u8981\u5e2e\u674e\u9038\u8bf4\u8bdd\uff0c\u4e5f\u8bf4\u9053\uff1a\u201c\u7980\u5927\u54e5\uff0c\u6b64\u4eba\u662f \u957f\u5b59\u5747\u91cf\u7684\u513f\u5b50\uff0c\u54b1\u4eec\u4e0d\u59a8\u6682\u65f6\u8ba9\u4ed6\u6d3b\u547d\uff0c\u95ee\u95ee\u4ed6\u53e3\u4f9b\u3002\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4f60\u4e0e\u6211 \u5c06\u4ed6\u7f1a\u4e86\uff0c\u62bc\u8fdb\u5e10\u6765\u3002\u201d \u3000\u3000\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u5462\uff1f\u201d\u4ed6\u6307\u7684\u662f\u767d\u5143\u5316\uff0c\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u4ed6\u7ed9\u6211\u6253\u4e2d\u4e86\u5173\u5143\u7a74\uff0c \u975e\u8fc7\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u4e0d\u80fd\u81ea\u89e3\uff0c\u6682\u65f6\u4e0d\u5fc5\u7406\u4ed6\u3002\u201d \u3000\u3000\u5357\u5bab\u5c1a\u5c06\u957f\u5b59\u6cf0\u53cc\u624b\u53cd\u7f1a\uff0c\u63a8\u8fdb\u5e10\u6765\uff0c\u7a0b\u8fbe\u82cf\u901a\u4e86\u53e3\u901a\u70df\u6597\uff0c\u91cd\u65b0\u88c5\u6ee1\u70df\u53f6\uff0c\u62bd\u4e86 \u51e0\u53e3\u70df\uff0c\u55b7\u51fa\u4e00\u5708\u5708\u7684\u70df\u96fe\uff0c\u51b7\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u771f\u662f\u957f\u5b59\u5747\u91cf\u7684\u513f\u5b50\u4e48\uff1f\u201d\u957f\u5b59\u6cf0\u672c\u6765\u6253 \u5b9a\u4e3b\u610f\uff0c\u4e0d\u7ba1\u4ed6\u95ee\u4e9b\u4ec0\u4e48\uff0c\u90fd\u95ed\u53e3\u4e0d\u7b54\uff0c\u4f46\u542c\u4ed6\u5982\u6b64\u4e00\u6765\uff0c\u5288\u5934\u5c31\u63d0\u53ca\u4ed6\u7684\u7236\u4eb2\uff0c\u4e0d\u7981 \u6012\u706b\u4e0a\u5347\uff0c\u7741\u773c\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u8d3c\u6562\u8fb1\u53ca\u6211\u7684\u7236\u4eb2\uff1f\u201d\u7a0b\u8fbe\u82cf\u51b7\u7b11\u9053\uff0c\u201c\u54c8\uff0c\u4f60\u8fd8\u77e5\u9053 \u6709\u7236\u4eb2\u5417\uff1f\u54fc\uff0c\u54fc\uff0c\u90a3\u662f\u4f60\u81ea\u8fb1\u53ca\u5148\u4eba\uff0c\u6211\u7a0b\u8fbe\u82cf\u5bf9\u957f\u5b59\u5927\u4eba\u5374\u662f\u94a6\u4f69\u5f97\u5f88\u3002\u201d\u957f\u5b59\u6cf0 \u9053\uff1a\u201c\u6211\u600e\u7684\u8fb1\u53ca\u5148\u4eba\uff1f\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u957f\u5b59\u5927\u4eba\u4e00\u751f\u5c3d\u5fe0\u5510\u5ba4\uff0c\u6599\u4e0d\u5230\u6709\u4f60\u8fd9\u6837\u7684\u4e0d \u8096\u513f\u5b59\uff1f\u201d\u957f\u5b59\u6cf0\u5927\u6012\u9053\uff1a\u201c\u6211\u600e\u6837\u4e0d\u8096\u4e86\uff1f\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u4f60\u7684\u7236\u4eb2\u4e0e\u4f2a\u5468\u6b66\u6c0f\u8a93\u4e0d \u4e24\u7acb\uff0c\u4f60\u5982\u4eca\u5374\u7518\u5fc3\u505a\u6b66\u5219\u5929\u7684\u5974\u624d\uff0c\u5c82\u975e\u4e0d\u8096\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u751f\u6027\u803f\u76f4\uff0c\u88ab\u7a0b\u8fbe\u82cf\u6fc0\u6012\uff0c\u7981\u4e0d\u4f4f\u628a\u672c\u6765\u4e0d\u60f3\u8bf4\u7684\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u8fd9\u8001\u8d3c\u5b9e\u662f \u6211\u7236\u4eb2\u4ec7\u4eba\u7684\u515a\u7fbd\uff0c\u4e8f\u4f60\u8fd8\u6562\u539a\u7740\u8138\u76ae\u8bf4\u94a6\u4f69\u4ed6\u3002\u6211\u7236\u4eb2\u4e0d\u4f46\u662f\u5510\u5ba4\u7684\u5fe0\u81e3\uff0c\u4ed6\u4e5f\u662f\u4e3a \u56fd\u4e3a\u6c11\u7684\u4e49\u58eb\uff0c\u4f60\u8fd9\u53ae\u8981\u53bb\u6295\u5954\u7a81\u53a5\uff0c\u6211\u7236\u4eb2\u82e5\u662f\u77e5\u9053\uff0c\u4e5f\u5b9a\u7136\u4e0d\u80fd\u9976\u4f60\u3002\u201d\u7a0b\u8fbe\u82cf\u51b7 \u7b11\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u82e5\u8fd8\u5728\u751f\uff0c\u4ed6\u5b9a\u7136\u4f1a\u91cd\u91cd\u6559\u8bad\u4f60\uff0c\u53ef\u60dc\u73b0\u5728\u4f60\u6211\u90fd\u4e0d\u80fd\u5c06\u4ed6\u8d77\u4e8e\u5730\u4e0b\uff0c \u95ee\u4ed6\u5fc3\u4e2d\u7684\u771f\u610f\u4e86\uff0c\u90a3\u4e5f\u7531\u5f97\u4f60\u80e1\u8bf4\u516b\u9053\u5427\u3002\u8fd9\u4e2a\u6682\u4e14\u4e0d\u63d0\uff0c\u4f46\u4f60\u8bf4\u6211\u662f\u4f60\u7236\u4eb2\u4ec7\u4eba\u7684 \u515a\u7fbd\uff0c\u8fd9\u5374\u53c8\u4ece\u4f55\u8bf4\u8d77\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u9762\u8272\u7a81\u53d8\uff0c\u8eab\u8eaf\u6218\u6296\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6211\u7684\u7239\u7239\uff0c\u4ed6\uff0c\u4ed6\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201d \u7a0b\u8fbe\u82cf\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u957f\u5b59\u5927\u4eba\u5728\u516b\u5e74\u4e4b\u524d\u65e9\u5df2\u6b7b\u4e86\uff0c\u4ed6\u662f\u88ab\u6b66\u5219\u5929\u7684\u5927\u5185\u536b\u58eb\u6740 \u6b7b\u7684\uff0c\u6b7b\u5728\u9760\u8fd1\u8fb9\u5173\u7684\u7518\u51c9\u65b9\u9053\u4e4b\u4e2d\uff0c\u8981\u662f\u4ed6\u4e0d\u6b7b\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u662f\u6295\u5954\u7a81\u53a5\u7684\uff01\u201d\u957f\u5b59\u6cf0 \u4e00\u54ac\u7259\u6839\uff0c\u5fcd\u7740\u773c\u6cea\uff0c\u4ef0\u5929\u558a\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u6b7b\u5f97\u597d\u82e6\u5440\uff01\u4f60\u4e00\u76f4\u88ab\u4eba\u8499\u5728\u9f13\u91cc\uff0c\u76f4\u5230 \u4e34\u6b7b\u4e4b\u524d\uff0c\u8fd8\u4e0d\u77e5\u9053\u4f60\u7684\u4ec7\u4eba\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u201d\u674e\u9038\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u7a0b\u5efa\u7537\u62e6\u52ab \u6211\u5cb3\u7236\u7684\u7075\u8f66\uff0c\u4e0e\u62a2\u593a\u6211\u5cb3\u7236\u5251\u8c31\u4e4b\u4e8b\uff0c\u4ed6\u65e9\u5df2\u544a\u8bc9\u4ed6\u7684\u7236\u4eb2\u4e86\u3002\u5e78\u800c\u6211\u73b0\u5728\u6539\u5bb9\u6613\u8c8c\uff0c \u7a0b\u8fbe\u82cf\u4ed6\u770b\u4e0d\u51fa\u6765\u3002\u957f\u5b59\u6cf0\u8bf4\u7684\u8fd9\u8bdd\u5374\u53c8\u662f\u4f55\u6240\u6307\u5462\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u957f\u5b59\u6cf0\u7ee7\u7eed\u558a\u9053\uff1a\u201c\u7239\u7239\u554a\uff0c\u4f60\u751f\u524d\u4e00\u76f4\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u77e5\u6076\u884c\u8005\u4e0e\u6bd2\u89c2\u97f3\u90a3 \u4e24\u4e2a\u9b54\u5934\u4f55\u4ee5\u8981\u4e0b\u6bd2\u624b\u5bb3\u4f60\uff1f\u4f60\u53ea\u5f53\u662f\u6b66\u5219\u5929\u6d3e\u4ed6\u4eec\u6765\u5bb3\u4f60\u7684\uff0c\u5c82\u77e5\u4ed6\u4eec\u6b63\u662f\u5929\u540e\u7684\u654c \u4eba\u6240\u5b9a\u4e0b\u7684\u8be1\u8ba1\uff0c\u8981\u4ed6\u4eec\u5047\u501f\u5929\u540e\u7684\u540d\u4e49\u524d\u6765\u7528\u6bd2\u624b\u4f24\u4f60\uff0c\u4e3a\u7684\u662f\u8981\u4f60\u4e00\u751f\u6000\u6068\u5929\u540e\u3002 \u6700\u540e\u8fd8\u8bf7\u51fa\u4ed6\u4eec\u7684\u5e08\u7236\u5929\u6076\u5996\u9053\u6765\u6697\u7b97\u4f60\uff0c\u8fd9\u624b\u6bb5\u4e0e\u4ed6\u4eec\u6697\u6740\u592a\u5b50\u8d24\u7684\u624b\u6bb5\u5982\u51fa\u4e00\u8f99\uff0c \u53ef\u53f9\u4f60\u5374\u4e00\u76f4\u88ab\u8499\u5728\u9f13\u4e2d\u3002\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u51b7\u7b11\u9053\uff1a\u201c\u4e00\u6d3e\u80e1\u8a00\uff01\u201d\u674e\u9038\u5374\u77e5\u9053\u957f\u5b59\u6cf0\u8bf4\u7684\u53e5\u53e5\u90fd\u662f\u5b9e\u8bdd\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u8fd9\u4e9b\u4e8b\u60c5\u60f3\u5fc5\u662f\u4ed6\u6295\u987a\u6b66\u5219\u5929\u4e4b\u540e\u624d\u77e5\u9053\u5e95\u8574\u7684\u3002\u53ef\u662f\u4ed6\u53c8\u4f55\u4ee5\u4f1a\u67d1\u4fe1\u6b66\u5219\u5929\u7684\u8bdd\u5462\uff1f\u201d \u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u957f\u5b59\u6cf0\u53c8\u9053\uff1a\u201c\u7a0b\u8001\u8d3c\uff0c\u4f60\u6562\u8bf4\u4f60\u4e0d\u662f\u5929\u6076\u9053\u4eba\u7684\u515a\u7fbd\u5417\uff1f\u5929\u6076\u9053\u4eba\u3001 \u706d\u5ea6\u795e\u541b\u548c\u4f60\u8fd9\u4e00\u4f19\u4eba\uff0c\u5e7f\u62db\u4e2d\u539f\u7684\u6c5f\u6e56\u8d25\u7c7b\uff0c\u8981\u53bb\u6295\u5954\u7a81\u53a5\uff0c\u5929\u540e\u65e9\u5df2\u77e5\u9053\u5f97\u6e05\u6e05\u695a \u695a\u4e86\uff0c\u5979\u8bf4\u4f60\u4eec\u53cd\u5bf9\u5979\u90a3\u8fd8\u60c5\u6709\u53ef\u539f\uff0c\u53db\u56fd\u6295\u654c\u5219\u662f\u7f6a\u65e0\u53ef\u6055\uff01\u5357\u5bab\u5c1a\uff0c\u60f3\u4e0d\u5230\u4f60\u4e5f\u53d7 \u4ed6\u4eec\u6240\u611a\u3002\u4f60\u4eec\u82e5\u4e0d\u53ca\u65f6\u56de\u5934\uff0c\u5c06\u6765\u6094\u4e4b\u665a\u77e3\uff01\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u6012\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u624d\u662f\u81f3\u6b7b\u4e0d\u609f\uff01\u4f60\u80cc\u7236\u6295\u654c\uff0c\u5356\u53cb\u6c42\u8363\uff0c\u7f6a\u4e0d\u5bb9\u8bdb\uff0c\u5403\u6211\u4e00 \u638c\uff01\u201d\u624b\u638c\u62ac\u8d77\uff0c\u7f13\u7f13\u5411\u957f\u5b59\u6cf0\u9876\u95e8\u62cd\u4e0b\uff0c\u957f\u5b59\u6cf0\u795e\u8272\u4e0d\u53d8\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8001\u8d3c\uff0c\u4f60\u8981 \u6740\u4fbf\u6740\uff0c\u4f55\u5fc5\u88c5\u6a21\u4f5c\u6837\uff01\u4f60\u4eca\u65e5\u6740\u6211\uff0c\u660e\u65e5\u7ba1\u6559\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d\u7a0b\u8fbe\u82cf\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u4ee5\u4e3a\u6211\u5f53\u771f\u4e0d\u6562\u6740\u4f60\u4e48\uff1f\u201d\u624b\u638c\u62cd\u4e0b\uff0c\u5ffd\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u674e\u9038\u7a81\u7136\u4f38\u624b\uff0c\u63a5\u4e86 \u4ed6\u7684\u4e00\u638c\u3002\u7a0b\u8fbe\u82cf\u53cc\u773c\u4e00\u7ffb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u8001\u5f1f\uff0c\u4f60\u600e\u4e48\u8001\u662f\u5e87\u62a4\u8fd9\u53ae\uff1f\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u7a0b\u8001\u5e2e\u4e3b\uff0c\u4f60\u95ee\u95ee\u4ed6\u8fd8\u6709\u51e0\u4e2a\u540c\u4f19\uff1f\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\u201d\uff0c\u9a88\u6307\u5982 \u621f\uff0c\u6307\u7740\u957f\u5b59\u6cf0\u95ee\u9053\uff1a\u201c\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u6b66\u5219\u5929\u9664\u4e86\u6d3e\u51fa\u4f60\u548c\u767d\u5143\u5316\u4e4b\u5916\uff0c\u8fd8\u6d3e\u4e86\u4e9b\u4ec0\u4e48\u4eba \u6765\uff1f\u4f60\u6562\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u6211\u7528\u5206\u7b4b\u65ad\u8109\u7684\u624b\u6cd5\uff0c\u53eb\u4f60\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff01\u201d\u5206\u7b4b\u65ad\u8109\u624b\u6cd5\uff0c \u4e43\u662f\u6c5f\u6e56\u4e0a\u4e00\u79cd\u5904\u7f6e\u4ec7\u654c\u7684\u6700\u5389\u5bb3\u7684\u6bd2\u5211\uff0c\u7a0b\u8fbe\u82cf\u662f\u70b9\u7a74\u540d\u5bb6\uff0c\u8fd9\u79cd\u6bd2\u5211\u6b63\u662f\u4ed6\u6240\u64c5\u957f \u7684\u624b\u6bb5\u3002\u674e\u9038\u4e5f\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u6211\u6b32\u4fdd\u6cf0\u54e5\uff0c\u53cd\u800c\u63d0\u9192\u4ed6\u4e86\u3002\u8fd9\u79cd\u6bd2\u5211\uff0c\u6bd4 \u6b7b\u66f4\u4e3a\u96be\u53d7\uff0c\u8bf4\u4e0d\u5f97\u53ea\u597d\u548c\u4ed6\u53cd\u9762\u4e86\u3002\u201d \u3000\u3000\u4f46\u542c\u5f97\u957f\u5b59\u6cf0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\u9ad8\u624b\u5982\u4e91\uff0c\u4f60\u4e00\u6740\u6211\uff0c\u6740\u4f60\u7684\u4eba\u4e5f\u5c31\u9a6c\u4e0a\u6765\u4e86\uff01\u201d \u7a0b\u8fbe\u82cf\u51b7\u7b11\u9053\uff1a\u201c\u5f53\u4eca\u4e4b\u4e16\uff0c\u80fd\u591f\u6740\u6211\u7684\u4eba\u4e5f\u5b9e\u5728\u6709\u9650\u5f97\u5f88\u3002\u4f60\u8bf4\u8bf4\u770b\uff0c\u662f\u4ec0\u4e48\u4eba\u3002\u201d \u957f\u5b59\u6cf0\u795e\u8272\u5014\u50b2\uff0c\u95ed\u53e3\u4e0d\u7b54\u3002\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u597d\uff0c\u5f85\u6211\u770b\u4f60\u7684\u9aa8\u5934\u662f\u4e0d\u662f\u94c1\u6253\u7684\uff1f\u201d\u6b63\u8981 \u65bd\u5211\uff0c\u674e\u9038\u8bf4\u9053\uff1a\u201c\u7a0b\u8001\u5e2e\u4e3b\uff0c\u4e0d\u5982\u5c06\u4ed6\u7559\u4e0b\uff0c\u4f5c\u4e3a\u4eba\u8d28\uff0c\u7eb5\u6709\u4ec0\u4e48\u9ad8\u624b\u5230\u6765\uff0c\u4ed6\u4eec\u4e5f \u5f97\u6295\u9f20\u5fcc\u5668\u3002\u201d\u7a0b\u8fbe\u82cf\u50b2\u7136\u51b7\u7b11\u9053\uff1a\u201c\u7a0b\u67d0\u7eb5\u6a2a\u6c5f\u6e56\u4e94\u5341\u591a\u5e74\uff0c\u5c82\u66fe\u6015\u8fc7\u4eba\u6765\uff1f\u4f55\u987b\u7528 \u8fd9\u79cd\u624b\u6bb5\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u5ffd\u7136\u9762\u8272\u5927\u53d8\uff0c\u51b2\u7740\u674e\u9038\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u539f\u6765\u4f60\u4e5f\u662f\u548c\u4ed6\u4eec\u4e00\u4f19\uff0c\u4f60\uff0c\u4f60\u2026\u2026\u201d \u539f\u6765\u4ed6\u8fd9\u65f6\u5df2\u542c\u51fa\u4e86\u674e\u9038\u7684\u53e3\u97f3\uff0c\u674e\u9038\u5fc3\u5934\u5927\u9707\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u7a0b\u8fbe\u82cf\u4e00\u58f0\u51b7\u7b11\uff0c\u53cc\u6307\u6233 \u5230\u4e86\u957f\u5b59\u6cf0\u7684\u592a\u9633\u7a74\u4e0a\uff0c\u674e\u9038\u65b9\u5728\u60ca\u6050\u4e4b\u4e2d\uff0c\u7a0b\u8fbe\u82cf\u7684\u70b9\u7a74\u624b\u6cd5\u8fc5\u5982\u95ea\u7535\uff0c\u674e\u9038\u8981\u6551\u5df2 \u6765\u4e0d\u53ca\uff0c\u6b63\u9053\u8981\u7cdf\uff0c\u5ffd\u542c\u5f97\u5495\u549a\u5495\u549a\u4e24\u58f0\uff0c\u5012\u4e0b\u53bb\u7684\u7adf\u7136\u4e0d\u662f\u957f\u5b59\u6cf0\uff0c\u800c\u662f\u5357\u5bab\u5c1a\u4e0e\u7a0b \u8fbe\u82cf\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5927\u6f20\u6df1\u5bb5\u9022\u65e7\u8bc6 \u3000\u3000\u8fd9\u4e00\u4e0b\u7a81\u5982\u5176\u6765\u7684\u53d8\u5316\uff0c\u5927\u51fa\u674e\u9038\u610f\u6599\u4e4b\u5916\uff0c\u6b63\u81ea\u60ca\u6115\uff0c\u5ffd\u542c\u5f97\u957f\u5b59\u6cf0\u53eb\u9053\uff1a\u201c\u674e \u516c\u5b50\uff0c\u521a\u624d\u6211\u9519\u602a\u4f60\u4e86\uff0c\u539f\u6765\u4f60\u5e76\u4e0d\u662f\u4ed6\u4eec\u4e00\u4f19\uff0c\u602a\u4e0d\u5f97\u51e0\u6b21\u4e09\u756a\u6551\u6211,\u73b0\u5728\u53c8\u6740\u4e86\u8fd9\u4e24 \u4e2a\u5978\u8d3c\u3002\u201d \u3000\u3000\u674e\u9038\u72b9\u5982\u5760\u5165\u4e86\u4e94\u91cc\u96fe\u4e2d\uff0c\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u8fd9\u4e24\u4e2a\u4eba\u4e0d\u662f\u4f60\u7684\u540c\u4f34\u6740\u7684\u5417\uff1f\u201d\u4fef\u8eab \u5bdf\u770b\uff0c\u5728\u7a0b\u8fbe\u82cf\u4e0e\u5357\u5bab\u5c1a\u7684\u8109\u95e8\u3001\u9876\u65e5\u62a5\u3001\u80cc\u5fc3\u767e\u4f1a\u4e09\u5904\u7a74\u9053\u4e00\u63a2\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u66f4 \u5947\u602a\u4e86\uff0c\u4ed6\u4eec\u8fd8\u6ca1\u6709\u6b7b\uff0c\u662f\u7ed9\u4eba\u7528\u6885\u82b1\u9488\u6253\u4e86\u7a74\u9053\u3002\u201d\u8bd5\u60f3\u7a0b\u8fbe\u82cf\u4e43\u662f\u6c5f\u6e56\u4e0a\u516c\u8ba4\u7684\u7b2c \u4e00\u70b9\u7a74\u9ad8\u624b\uff0c\u7adf\u88ab\u6765\u4eba\u65e0\u58f0\u65e0\u606f\u7684\u6253\u4e86\u7a74\u9053\uff0c\u8fd9\u4eba\u7684\u6b66\u529f\u4e4b\u5f3a\uff0c\u5c82\u975e\u662f\u4e0d\u53ef\u601d\u8bae\uff01 \u3000\u3000\u957f\u5b59\u6cf0\u66f4\u662f\u60ca\u5947\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u9053\u662f\u4f60\u5e72\u7684\uff0c\u600e\u4e48\u4e0d\u662f\u4f60\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u521a\u624d \u8bf4\u8fd8\u6709\u9ad8\u624b\uff0c\u968f\u540e\u5c31\u6765\uff0c\u90a3\uff0c\u90a3\u2026\u2026\u201d\u957f\u5b59\u6cf0\u7b11\u9053\uff1a\u201c\u90a3\u662f\u5047\u7684\u3002\u6211\u662f\u6545\u610f\u5413\u4e00\u5413\u8fd9\u4e2a \u8001\u8d3c\u7684\uff0c\u548c\u6211\u540c\u6765\u7684\u53ea\u6709\u767d\u5143\u5316\u4e00\u4eba\u3002\u201d \u3000\u3000\u674e\u9038\u6025\u5fd9\u8d70\u51fa\u5e10\u7bf7\uff0c\u8349\u539f\u4e0a\u6773\u65e0\u4eba\u5f71\uff0c\u8fde\u767d\u5143\u5316\u4e5f\u4e0d\u89c1\u4e86\u3002\u767d\u5143\u5316\u88ab\u7a0b\u8fbe\u82cf\u7528\u72ec\u95e8 \u624b\u6cd5\u70b9\u4e86\u7a74\u9053\uff0c\u65ad\u4e0d\u80fd\u8d70\u52a8\uff0c\u5206\u660e\u662f\u6709\u9ad8\u624b\u5c06\u4ed6\u6551\u53bb\u4e86\u3002\u674e\u9038\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u8fd9\u4e2a\u4eba\u7528\u6885\u82b1\u9488\u70b9\u4e86\u7a0b\u8fbe\u82cf\u7684\u7a74\u9053\uff0c\u53c8\u5c06\u767d\u5143\u5316\u6551\u8d70\uff0c\u5374\u4f55\u4ee5\u4e0d\u80af\u9732\u9762\uff0c\u4ed6\u4e0d\u662f\u957f\u5b59 \u6cf0\u4e00\u8def\u7684\u4eba\uff0c\u53c8\u4f55\u4ee5\u6697\u4e2d\u52a9\u4ed6\uff1f\u201d \u3000\u3000\u674e\u9038\u7591\u56e2\u585e\u80f8\uff0c\u8d70\u56de\u7bf7\u5e10\uff0c\u89e3\u5f00\u957f\u5b59\u6cf0\u7684\u675f\u7f1a\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u54b1\u4eec\u90fd\u662f\u9080\u5929\u4e4b\u5e78\uff0c \u5f97\u4ee5\u6b7b\u91cc\u9003\u751f\uff0c\u8fd9\u4f4d\u5f02\u4eba\u4e0d\u80af\u9732\u9762\uff0c\u53ea\u6709\u4ed6\u65e5\u518d\u56fe\u62a5\u7b54\u4e86\u3002\u6cf0\u5144\uff0c\u60f3\u4e0d\u5230\u4f60\u6211\u5728\u6b64\u76f8\u9022\uff0c \u6211\u6b63\u6709\u8bdd\u8981\u548c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u674e\u9038\u6b63\u5f85\u628a\u4ed6\u548c\u957f\u5b59\u58c1\u6210\u5a5a\u7684\u7ecf\u8fc7\u544a\u8bc9\u957f\u5b59\u6cf0\uff0c\u957f\u5b59\u6cf0\u6025\u4e0d\u53ca\u5f85\uff0c\u5df2\u637b\u5148\u8bf4\u9053\uff1a \u201c\u6211\u4e5f\u6b63\u6709\u8981\u548c\u4f60\u8bf4\uff0c\u6211\u662f\u53d7\u4e86\u4e00\u4e2a\u4eba\u90d1\u91cd\u5631\u5490\uff0c\u6765\u627e\u4f60\u7684\u3002\u201d \u3000\u3000\u674e\u9038\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u7684\u6765\u610f\uff0c\u4f60\u662f\u5949\u4e86\u6b66\u5219\u5929\u4e4b\u547d\uff0c\u8981 \u627e\u6211\u56de\u53bb\u7684\u5417\uff1f\u6211\u82e5\u80af\u6295\u987a\u5979\uff0c\u5f53\u5e74\u4e5f\u4e0d\u81f3\u4e8e\u4e07\u91cc\u8fe2\u8fe2\uff0c\u6295\u5230\u585e\u5916\u6765\u4e86\u3002\u4eba\u5404\u6709\u5fd7\uff0c\u6211 \u5b9e\u662f\u4e0d\u613f\u5728\u6b66\u5219\u5929\u7684\u624b\u4e0b\u505a\u5b98\uff0c\u8bf7\u4f60\u4e0d\u8981\u52c9\u5f3a\uff01\u201d \u3000\u3000\u54ea\u77e5\u957f\u5b59\u6cf0\u4e5f\u6447\u4e86\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u731c\u9519\u4e86\uff0c\u6211\u4e0d\u662f\u5949\u5929\u540e\u4e4b\u547d\u6765\u627e\u4f60\u7684\uff0c\u662f\u4f60\u7684 \u4e00\u4f4d\u9752\u6885\u7af9\u9a6c\u7684\u670b\u53cb\uff0c\u800c\u662f\u6700\u61c2\u5f97\u4f60\u5fc3\u4e8b\u7684\u4eba\uff0c\u6258\u6211\u6765\u627e\u4f60\u7684\u3002\u201d\u674e\u9038\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u8c01\uff1f\u201d \u957f\u5b59\u6cf0\u6781\u4e0d\u81ea\u7136\u7b54\u9053\uff1a\u201c\u662f\u4e0a\u5b98\u5a49\u513f\uff01\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u6c89\uff0c\u5583\u5583\u8bf4\u9053\uff0c\u201c\u662f\u4e0a\u5b98\u7897\u513f\uff1f\u662f\u4e0a\u5b98\u5a49\u513f\uff01\u201d\u5fc3\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u5e74\u4e86\uff0c \u539f\u6765\u5979\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u6211\u3002\u53ef\u662f\u5979\u600e\u4f1a\u6258\u957f\u5b59\u6cf0\u6765\u5462\uff1f\u201d\u53ea\u542c\u5f97\u957f\u5b59\u6cf0\u7ee7\u7eed\u60a6\u9053\uff1a\u201c\u5a49\u513f\u5979 \u77e5\u9053\u4f60\u662f\u4e0d\u4f1a\u56de\u53bb\u7684\uff0c\u53ef\u662f\u4e3a\u4e86\u5979\u7684\u539f\u6545\uff0c\u5979\u5e0c\u671b\u4f60\u80fd\u591f\u56de\u53bb\u4e00\u6b21\uff0c\u5979\u6709\u5f88\u91cd\u8981\u7684\u4e8b\u60c5 \u548c\u4f60\u5546\u91cf\u3002\u5979\u5411\u4f60\u4fdd\u8bc1\uff0c\u5929\u540e\u7edd\u4e0d\u4f1a\u52c9\u5f3a\u4f60\u505a\u5b98\u7684\uff0c\u4f60\u5230\u4e86\u957f\u5b89\uff0c\u613f\u7559\u4fbf\u7559\u4e0d\u613f\u7559\u4fbf\u8d70\u3002 \u5a49\u513f\uff0c\u5979\u6240\u76fc\u671b\u5730\u53ea\u662f\u8981\u89c1\u4f60\u4e00\u9762\u3002\u201d \u3000\u3000\u674e\u9038\u95ee\u9053\uff1a\u201c\u5a49\u513f\u662f\u6b66\u5219\u5929\u7684\u8bb0\u5ba4\uff08\u6309\uff1a\u76f8\u5f53\u4e8e\u4eca\u4e4b\u79d8\u4e66\uff09\u3002\u8eab\u5904\u6df1\u5bab\uff0c\u4f60\u80fd\u591f\u548c \u5979\u65f6\u5e38\u89c1\u9762\u5417\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u867d\u4e0d\u7ecf\u5e38\uff0c\u6bcf\u4e2a\u6708\u80fd\u89c1\u5979\u4e24\u4e09\u6b21\u3002\u6211\u73b0\u5728\u662f\u5929\u540e\u7684\u5927\u5185 \u5bbf\u536b\u3002\u201d\u674e\u9038\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u771f\u4ee4\u6211\u60f3\u50cf\u4e0d\u5230\uff0c\u6b66\u5219\u5929\u4f1a\u4fe1\u4efb\u4f60\u3002\u800c\u4f60\u4e5f\u5c45\u7136\u4f1a\u505a\u4e86\u62a4 \u536b\u6b66\u5219\u5929\u7684\u4eba\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u8fd9\u6837\u7684\u53d8\u5316\uff0c\u8fde\u6211\u4e5f\u662f\u59cb\u6599\u4e0d\u53ca\u3002\u4f60\u8fd8\u8bb0\u5f97\u516b\u5e74\u4e4b\u524d\u7684 \u4e00\u665a\uff0c\u5165\u5b98\u884c\u523a\u5929\u540e\u3002\u6211\u548c\u7239\u7239\u59b9\u59b9\u5728\u9a91\u5c71\u5c71\u811a\u63a5\u5e94\u4f60\u7684\u4e8b\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a \u4e0d\u8bb0\u5f97\uff1f\u542c\u58c1\u59b9\u8bf4\u4f60\u90a3\u665a\u53d7\u4e86\u91cd\u4f24\uff0c\u6211\u4eec\u771f\u4e3a\u4f60\u62c5\u5fc3\uff0c\u5e78\u800c\u54b1\u4eec\u90fd\u5e73\u5b89\u65e0\u4e8b\uff0c\u5982\u4eca\u7adf\u7136 \u8fd8\u80fd\u91cd\u89c1\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u542c\u674e\u9038\u63d0\u8d77\u4ed6\u59b9\u59b9\u7684\u65f6\u5019\uff0c\u53e3\u6c14\u751a\u662f\u4eb2\u70ed\uff0c\u6709\u70b9\u8be7\u5f02\uff0c\u5374\u4e0d\u4fbf\u95ee\u4ed6\uff0c\u7ee7\u7eed\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u90a3\u665a\u88ab\u6076\u884c\u8005\u6253\u4e86\u4e00\u638c\uff0c\u53c8\u4e2d\u4e86\u6bd2\u89c2\u97f3\u7684\u900f\u7a74\u795e\u9488\uff0c\u81ea\u5df1\u4e5f\u4ee5\u4e3a\u662f\u5fc5\u6b7b\u65e0\u7591\uff0c \u54ea\u77e5\u9192\u6765\u4e4b\u540e\uff0c\u5374\u53d1\u73b0\u81ea\u5df1\u8eba\u5728\u4e00\u5f20\u6781\u8212\u670d\u7684\u5e8a\u4e0a\uff0c\u5ba4\u4e2d\u7684\u5e03\u7f6e\u88c5\u9970\u90fd\u4e0d\u662f\u666e\u901a\u4eba\u5bb6\u6709 \u7684\uff0c\u66f4\u5947\u602a\u7684\u662f\u5a49\u513f\u4f8d\u6211\u5728\u7684\u8eab\u8fb9\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u90a3\u662f\u5a49\u513f\u5c06\u4f60\u6551\u5165\u5bab\u4e2d\u4e86\u3002\u201d\u957f\u5b59\u6cf0 \u9053\uff1a\u201c\u5979\u672c\u6765\u8981\u6551\u4f60\u7684\uff0c\u4e0d\u60f3\u5374\u6551\u4e86\u6211\uff0c\u5929\u540e\u6d3e\u5979\u6700\u9ad8\u660e\u7684\u5fa1\u533b\u7ed9\u6211\u533b\u6cbb\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a \u91d1\u9488\u56fd\u624b\u590f\u4faf\u575a\u7684\u5f1f\u5b50\uff0c\u5f97\u4e86\u4ed6\u5e08\u7236\u4e94\u5e74\u7684\u529f\u592b\uff0c\u7ed9\u6211\u533b\u4e86\u4e09\u5e74\uff0c\u6211\u624d\u5b8c\u5168\u6062\u590d\u3002\u201d\u674e \u9038\u9053\uff1a\u201c\u4f60\u611f\u6fc0\u6b66\u5219\u5929\u533b\u6cbb\u4f60\u7684\u6069\u5fb7\uff0c\u6240\u4ee5\u505a\u4e86\u5979\u7684\u62a4\u536b\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4e0d\u662f\u3002\u6211\u662f \u542c\u4e86\u5a49\u513f\u7684\u8bdd\uff0c\u77e5\u9053\u4e86\u4e00\u4e9b\u4e8b\u60c5\u7684\u771f\u76f8\uff1b\u540c\u65f6\u5728\u90a3\u4e09\u5e74\u4e4b\u4e2d\uff0c\u8033\u95fb\u76ee\u7779\uff0c\u591a\u5c11\u4e5f\u77e5\u9053\u4e86 \u4e00\u4e9b\u5929\u540e\u7684\u4e3a\u4eba\uff0c\u6240\u4ee5\u5728\u75c5\u597d\u4e4b\u540e\uff0c\u6211\u624d\u81ea\u613f\u505a\u5979\u7684\u536b\u58eb\u3002\u201d\u674e\u9038\u5fc3\u4e2d\u6697\u6697\u53f9\u606f\uff0c\u60f3\u9053\uff1a \u201c\u6b66\u5219\u5929\u7adf\u80fd\u4ee4\u5230\u5979\u7684\u4ec7\u654c\u4e3a\u5979\u6548\u5fe0\uff0c\u771f\u662f\u4e00\u4e2a\u53ef\u6015\u7684\u5973\u4eba\uff01\u6211\u60f3\u6062\u590d\u5510\u5ba4\uff0c\u770b\u6765\u90a3\u662f \u65e0\u671b\u7684\u4e86\uff0c\u53ea\u6015\u8981\u592a\u5b97\u7687\u5e1d\u91cd\u751f\uff0c\u624d\u662f\u5979\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u90a3\u4e9b\u6253\u7740\u65d7\u53f7\uff0c\u8bf4\u8981\u60ed\u590d\u5510\u5ba4\u7684\u4eba\uff0c\u5176\u5b9e\u5341\u4e4b\u516b\u4e5d\uff0c\u90fd\u662f\u5404\u6709\u5404\u7684\u91ce \u5fc3\uff0c\u50cf\u88f4\u708e\u4ed6\u5c31\u662f\u81ea\u5df1\u60f3\u505a\u7687\u5e1d\u7684\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u65e9\u5df2\u77e5\u9053\uff0c\u6240\u4ee5\u6211\u73b0\u5728 \u4ea6\u5df2\u5fc3\u7070\u610f\u51b7\u3002\u55ef\uff0c\u54b1\u4eec\u4e0d\u8c08\u8fd9\u4e9b\u4e89\u6743\u593a\u4f4d\u7684\u4e8b\u60c5\uff0c\u6211\u53ea\u60f3\u542c\u542c\u5a49\u513f\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u6781\u529b\u538b\u6291\u81ea\u5df1\uff0c\u4f46\u4ecd\u7136\u4e0d\u514d\u663e\u9732\u51fa\u4e00\u70b9\u75db\u82e6\u7684\u795e\u60c5\uff0c\u6b47\u4e86\u4e00\u4f1a\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a \u201c\u4f60\u662f\u77e5\u9053\u7684\uff0c\u5a49\u513f\u5979\u4e03\u5c81\u4e4b\u65f6\u6765\u5230\u6211\u5bb6\uff0c\u5341\u56db\u5c81\u79bb\u5f00\uff0c\u6211\u770b\u7740\u5979\u957f\u5927\uff0c\u6211\u4e00\u76f4\u662f\u5c06\u5979 \u5f53\u4f5c\u4eb2\u59b9\u59b9\u4e00\u6837\u770b\u5f85\u7684\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u542c\u5a49\u513f\u8bf4\u8fc7\uff0c\u5979\u5bf9\u4f60\u4e5f\u5f88\u5c0a\u656c\uff0c\u5f53\u5982\u5144\u957f\u4e00\u822c\u3002\u201d \u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211\u505a\u4e86\u5929\u540e\u7684\u536b\u571f\uff0c\u53c8\u4e0e\u5979\u76f8\u5904\u4e86\u516b\u5e74\u3002\u6211\u53d1\u73b0\u5979\u5fc3\u4e2d\u7231\u6155\u7684\u53e6\u6709\u5176\u4eba\uff0c \u90a3\u5c31\u662f\u4f60\u3002\u201d\u674e\u9038\u6c93\u7b11\u9053\uff1a\u201c\u662f\u6211\uff1f\u201d\u5176\u5b9e\u8fd9\u4e5f\u662f\u4ed6\u65e9\u5df2\u77e5\u9053\u7684\u4e86\u3002\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5979\u8bf4 \u4f60\u662f\u4e00\u4e2a\u6709\u672c\u9886\u7684\u4eba\uff0c\u5979\u5929\u5929\u5728\u76fc\u671b\u4f60\u56de\u53bb\u3002\u5979\u8fd8\u60f3\u542c\u4f60\u7684\u7434\u97f3\uff0c\u8bfb\u4f60\u7684\u8bd7\u53e5\u3002\u201d\u674e\u9038 \u53c8\u82e6\u7b11\u9053\uff1a\u201c\u5979\u77e5\u9053\u6211\u662f\u4e0d\u4f1a\u56de\u53bb\u7684\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u53ef\u662f\u4e3a\u4e86\u5979\u7684\u7ec8\u8eab\u7740\u60f3\uff0c\u6211\u529d\u4f60 \u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u8981\u56de\u53bb\u89c1\u5979\u4e00\u9762\u3002\u201d \u3000\u3000\u674e\u9038\u8138\u8272\u82cd\u767d\uff0c\u98a0\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6cf0\u5144\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u6211\u4e0d\uff0c\u4e0d\u2026\u2026\u201d\u4ed6\u9690\u5fcd\u4e0d \u4f4f\uff0c\u6b63\u8981\u5411\u957f\u5b59\u6cf0\u5410\u9732\uff0c\u4ed6\u5df2\u4e0e\u957f\u5b59\u58c1\u6210\u5a5a\uff0c\u4e0d\u53ef\u80fd\u4e0e\u4e0a\u5b98\u5a49\u513f\u7ed3\u5408\u4e86\u3002\u957f\u5b59\u6cf0\u5374\u62a2\u7740 \u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u522b\u5148\u62d2\u7edd\uff0c\u5148\u8ba9\u6211\u8bf4\uff01\u201d\u58f0\u97f3\u7a81\u7136\u63d0\u9ad8\uff0c\u663e\u89c1\u751a\u4e3a\u6fc0\u52a8\uff0c\u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c \u53ea\u542c\u5f97\u957f\u5b59\u6cf0\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5b9e\u5728\u662f\u559c\u6b22\u5a49\u513f\u7684\uff0c\u4f46\u56e0\u4e3a\u5979\u505a\u4e86\u6b66\u5219\u5929\u7684\u8bb0\u5ba4\uff0c \u4f60\u53c8\u6068\u6781\u4e86\u5979\u3002\u201d\u674e\u9038\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u662f\u3002\u201d\u4ed6\u6700\u521d\u786e\u662f\u6068\u5a49\u513f\u7684\uff0c\u4f46\u7ecf\u8fc7\u4e86\u8fd9\u4e48 \u591a\u5e74\uff0c\u8fd9\u6028\u6068\u4e5f\u7684\u786e\u6d88\u51cf\u4e86\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211\u4e0d\u662f\u529d\u4f60\u5a36\u5979\uff0c\u4f46\u4f60\u8981\u77e5\u9053\u5979\u662f\u5728\u7b49\u7740 \u4f60\uff0c\u4f60\u770b\u8fd9\u662f\u5979\u6258\u6211\u634e\u7ed9\u4f60\u7684\u4e00\u5c01\u4fe1\uff0c\u5979\u8bf4\u5979\u6709\u4e00\u9996\u8bd7\u662f\u4f60\u4ee5\u524d\u5f88\u559c\u6b22\u5ff5\u7684\uff0c\u5979\u73b0\u5728\u4eb2 \u7b14\u518d\u5199\u7ed9\u4f60\uff0c\u95ee\u4f60\u8fd8\u8bb0\u5f97\u5417\uff1f \u3000\u3000\u674e\u9038\u6253\u5f00\u4e86\u4fe1\uff0c\u8f7b\u8f7b\u5ff5\u9053\uff1a\u201c\u53f6\u4e0b\u6d1e\u5ead\u521d\uff0c\u601d\u541b\u4e07\u91cc\u4f59\u3002\u9732\u6d53\u9999\u88ab\u51b7\uff0c\u6708\u843d\u9526\u5c4f\u865a\u3002 \u6b32\u594f\u6c5f\u5357\u8c03\uff0c\u8d2b\u5c01\u84df\u5317\u8bd7\u3002\u4e66\u4e2d\u65e0\u522b\u610f\uff0c\u4f46\u6005\u4e45\u79bb\u76fe\u3002\u201d\u4ed6\u5f53\u7136\u8bb0\u5f97\uff0c\u8fd9\u662f\u4ed6\u548c\u5a49\u513f\u5728 \u6c5f\u6e56\u4e0a\u91cd\u9022\u4e4b\u540e\uff0c\u5a49\u513f\u66fe\u7ecf\u7ed9\u4ed6\u5ff5\u8fc7\u7684\u90a3\u4e00\u9996\u8bd7\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u4e00\u76f4\u538b\u5728\u5fc3\u5e95\uff0c\u5373\u5728\u65e0 \u4eba\u4e4b\u5904\uff0c\u4e5f\u4e0d\u6562\u62ff\u6765\u80cc\u8bf5\u3002\u5982\u4eca\u91cd\u8bfb\uff0c\u56de\u5fc6\u524d\u60c5\uff0c\u7981\u4e0d\u4f4f\u4e00\u7247\u6005\u60af\u3002\u8fd9\u4e00\u9996\u8bd7\u662f\u4e0a\u5b98\u5a49 \u513f\u4ee5\u524d\u5199\u6765\u6000\u5ff5\u4ed6\u7684\uff0c\u73b0\u5728\u8bfb\u6765\uff0c\u66f4\u89c9\u5207\u5408\uff0c\u201c\u4f46\u6005\u4e45\u79bb\u5c45\uff01\u201d\u662f\u7684\uff0c\u5206\u79bb\u4e4b\u540e\uff0c\u4e0d\u77e5 \u4e0d\u89c9\u4e4b\u95f4\uff0c\u4e00\u6643\u5c31\u516b\u5e74\u4e86\u5475\uff01 \u3000\u3000\u957f\u5b59\u6cf0\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u4f60\u660e\u767d\u4e86\u5427\uff1f\u5979\u4e00\u76f4\u5728\u7b49\u7740\u4f60\u5475\uff01\u5979\u82e5\u5f97\u4e0d\u5230\u4f60\u786e\u5b9e\u7684\u6d88 \u606f\uff0c\u5979\u662f\u4e0d\u4f1a\u518d\u5ac1\u4eba\u7684\u3002\u7eb5\u7b97\u4f60\u4e0d\u80fd\u4e0e\u5979\u7ed3\u5408\uff0c\u4e5f\u8be5\u8ba9\u5979\u77e5\u9053\uff0c\u597d\u6b7b\u4e86\u8fd9\u6761\u5fc3\u3002\u4f60\u6c38\u4e16 \u4e0d\u56de\u53bb\u89c1\u5979\uff0c\u90a3\u4e0d\u662f\u7d2f\u4e86\u5979\u7684\u7ec8\u8eab\u5417\uff1f\u201d\u957f\u5b59\u6cf0\u6027\u60c5\u5766\u7387\uff0c\u60f3\u5230\u4ec0\u4e48\u5c31\u6beb\u65e0\u987e\u5fcc\u7684\u8bf4\u4e86 \u51fa\u6765\uff0c\u674e\u9038\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u4ee5\u524d\u542c\u4e0a\u5b98\u5a49\u513f\u8bf4\u8fc7\uff0c\u9690\u7ea6\u77e5\u9053\u957f\u5b59\u6cf0\u5bf9\u5a49\u513f\u60c5\u6709\u6240\u949f\uff0c\u5fc3\u9053\uff1a \u201c\u539f\u6765\u4ed6\u81ea\u613f\u8bf7\u6c42\u6b66\u5219\u5929\u6d3e\u4ed6\u51fa\u585e\uff0c\u4e0d\u4f46\u662f\u4e3a\u4e86\u5a49\u513f\uff0c\u4e5f\u662f\u4e3a\u4ed6\u81ea\u5df1\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a \u201c\u6211\u662f\u4e0d\u4f1a\u56de\u957f\u5b89\u53bb\u4e86\uff0c\u4f60\u56de\u53bb\u544a\u8bc9\u5979\u5427\uff0c\u5979\u82e5\u662f\u6709\u4e86\u5408\u9002\u7684\u4eba\uff0c\u6211\u4e5f\u76fc\u671b\u5979\u65e9\u65e5\u7ec8\u8eab \u6709\u6258\u3002\u4f60\u8bf4\uff0c\u5979\u6709\u8981\u7d27\u7684\u4e8b\u60c5\u627e\u6211\uff0c\u5c31\u662f\u8981\u7b49\u6211\u4e3a\u5979\u51b3\u5b9a\u5417\uff1f\u597d\u5427\uff0c\u90a3\u4f60\u5c31\u544a\u8bc9\u5979\uff0c\u65e9 \u5728\u516b\u5e74\u4e4b\u524d\uff0c\u6211\u5c31\u7977\u544a\u82cd\u5929\uff0c\u4fdd\u4f51\u5979\u80fd\u591f\u627e\u5230\u53e6\u4e00\u4e2a\u79f0\u5fc3\u5982\u610f\u7684\u4eba\u4e86\uff01\u201d \u3000\u3000\u957f\u5b59\u6cf0\u4e00\u7247\u8ff7\u832b\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4f60\u7684\u610f\u601d\uff0c\u5979\u90a3\u6837\u6e34\u671b\u89c1\u4f60\uff0c\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u613f\u89c1\u5979\uff1f \u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u8bf4\u7684\u8981\u7d27\u4e8b\u60c5\u662f\u6307\u4ec0\u4e48\uff0c\u4f46\u6211\u77e5\u9053\u7684\u662f\u5979\u4e00\u5929\u6bd4\u4e00\u5929\u614c\u60bb\uff01\u201d \u3000\u3000\u674e\u9038\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u4e0d\u613f\u89c1\u5979\uff0c\u4e3a\u751a\u4e48\u6211\u4e0d\u613f\u89c1\u5979\uff1f\u201d\u5ffd\u5730\u8de8\u4e0a\u4e00\u6b65\uff0c\u7d27\u63e1 \u7740\u957f\u5b59\u6cf0\u7684\u53cc\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\u60c5\u4f60\u672a\u77e5\u9053\uff0c\u6211\u4eec\u4ee5\u524d\u53c8\u4e0d\u77e5\u9053\u4f60\u7684\u97f3\u8baf\uff0c\u6ca1\u6cd5\u7980 \u544a\uff0c\u6211\u548c\u58c1\u59b9\u6210\u4e3a\u592b\u5987\uff0c\u81f3\u4eca\u5df2\u6709\u516b\u5e74\u4e86\uff01\u201d\u957f\u5b59\u6cf0\u8eab\u8eaf\u4e00\u98a4\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u548c\u58c1\u59b9 \u65e9\u5df2\u6210\u4eb2\u4e86\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u662f\u7980\u627f\u4ee4\u5c0a\u5927\u4eba\u7684\u9057\u547d\uff0c\u4e0d\u5f85\u670d\u6ee1\uff0c\u4fbf\u6210\u4eb2\u7684\uff0c \u73b0\u5728\u6211\u4eec\u7684\u5b69\u5b50\u4e5f\u5df2\u6709\u4e03\u5c81\u4e86\uff01\u201d \u3000\u3000\u957f\u5b59\u6cf0\u60ca\u559c\u4ea4\u6742\uff0c\u5374\u53c8\u6709\u70b9\u96be\u4ee5\u4e3a\u60c5\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u53ea\u9053\u4ed6\u559c\u6b22\u5a49\u513f\uff0c\u539f\u6765\u65e9\u5df2\u662f\u6211 \u7684\u59b9\u592b\u3002\u201d\u5f53\u4e0b\u91cd\u65b0\u89c1\u8fc7\u5a18\u8205\u4e4b\u793c\uff0c\u5f7c\u6b64\u795d\u8d3a\u3002 \u3000\u3000\u674e\u9038\u7b11\u9053\uff1a\u201c\u4f60\u4e0e\u5a49\u513f\u76f8\u5904\u7684\u65e5\u5b50\u6bd4\u6211\u957f\u5f97\u591a\uff0c\u4f60\u5f53\u7136\u77e5\u9053\u5f97\u6bd4\u6211\u6e05\u695a\uff0c\u5979\u786e\u5b9e\u662f \u4e2a\u597d\u59d1\u5a18\u3002\u6211\u613f\u671b\u4f60\u4eec\u4e5f\u6210\u4e3a\u592b\u5987\u3002\u201d\u957f\u5b59\u6cf0\u6709\u70b9\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u662f\u559c\u6b22 \u5979\u7684\uff0c\u53ea\u6015\u914d\u4e0d\u4e0a\u5979\u3002\u5927\u7ea6\u5728\u534a\u5e74\u4e4b\u524d\uff0c\u6709\u4e00\u6b21\u6211\u89c1\u5979\u7684\u795e\u60c5\u5fe7\u90c1\uff0c\u66fe\u6084\u6084\u53bb\u95ee\u8fc7\u6b66\u90e1 \u4e3b\uff0c\u5c31\u662f\u4f60\u8ba4\u8bc6\u7684\u90a3\u4f4d\u6b66\u7384\u971c\u59d1\u5a18\uff0c\u95ee\u5a49\u513f\u5230\u5e95\u4e3a\u4e86\u4f55\u4e8b\u3002\u90c1\u90c1\u5be1\u6b22\uff1f\u5979\u7b11\u8bf4\u5973\u513f\u5927\u4e86\uff0c \u5f53\u7136\u4f1a\u60f3\u5230\u7ec8\u8eab\u7684\u95ee\u9898\uff0c\u5979\u5fc3\u4e2d\u59d4\u51b3\u4e0d\u4e0b\uff0c\u6b63\u81ea\u70e6\u607c\uff0c\u4f60\u4e0d\u8981\u60f9\u5979\u3002\u201d \u3000\u3000\u674e\u9038\u7a81\u7136\u4ece\u957f\u5b59\u6cf0\u7684\u53e3\u4e2d\u542c\u5230\u201c\u6b66\u7384\u971c\u201d\u7684\u540d\u5b57\uff0c\u4e0d\u89c9\u53c8\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u8981\u77e5\u6b66\u7384\u971c \u662f\u548c\u4ed6\u6709\u8fc7\u6069\u6028\u7ea0\u7f20\uff0c\u800c\u53c8\u662f\u4ed6\u6700\u4f69\u670d\u7684\u4e00\u4e2a\u5973\u5b50\uff0c\u5f53\u5e74\u4ed6\u66fe\u7ecf\u60f3\u8fc7\u5728\u5a49\u513f\u4e0e\u6b66\u7384\u971c\u4e4b \u4e2d\u9009\u62e9\u4e00\u4eba\uff0c\u90a3\u65f6\uff0c\u4ed6\u7684\u5fc3\u4e0a\u538b\u6839\u513f\u8fd8\u672a\u6709\u957f\u5b59\u58c1\u7684\u5f71\u5b50\u5462\uff0c\u957f\u5b59\u58c1\u540e\u6765\u7a81\u7136\u95ef\u5165\uff0c\u5b9e \u662f\u4ed6\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\u3002\u867d\u7136\u4ed6\u73b0\u5728\u5f88\u7231\u59bb\u5b50\uff0c\u4f46\u6709\u65f6\u4e5f\u4f1a\u6697\u4e2d\u60f3\u60f3\uff0c\u662f\u4e0d\u662f\u5f53\u5e74\u56e0\u4e3a\u81ea\u5df1 \u59d4\u51b3\u4e0d\u4e0b\uff0c\u800c\u8fd9\u4e24\u4e2a\u4eba\u53c8\u90fd\u6ca1\u6709\u548c\u81ea\u5df1\u7ed3\u5408\u7684\u53ef\u80fd\uff0c\u4e3a\u4e86\u6446\u8131\u70e6\u607c\uff0c\u8fd9\u624d\u5fc3\u7070\u610f\u51b7\uff0c\u9042 \u548c\u957f\u5b59\u58c1\u7ed3\u4e86\u5a5a\u5462\uff1f\u800c\u5e76\u4e0d\u662f\u5355\u5355\u4e3a\u4e86\u5979\u7236\u4eb2\u4e34\u6b7b\u5631\u6258\u7684\u539f\u6545\uff1f\u6bcf\u5f53\u60f3\u5230\u8fd9\u4e2a\u95ee\u9898\uff0c\u4ed6 \u5c31\u89c9\u5f97\u6709\u70b9\u6127\u5bf9\u59bb\u5b50\u3002 \u3000\u3000\u5e78\u800c\u4ed6\u4e0d\u77e5\u9053\u6b66\u7384\u971c\u4e5f\u5230\u4e86\u585e\u5916\uff0c\u8981\u4e0d\u4ed6\u6050\u6015\u66f4\u8981\u5fc3\u7eea\u4e0d\u5b81\u4e86\u3002\u5f53\u4e0b\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4 \u9053\uff1a\u201c\u90a3\u4e48\uff0c\u542c\u8fd9\u4f4d\u6b66\u59d1\u5a18\u7684\u8bdd\uff0c\u5a49\u513f\u5979\u5df2\u5728\u601d\u91cf\u5979\u7684\u5a5a\u5ac1\u95ee\u9898\u4e86\uff0c\u867d\u7136\u59d4\u51b3\u4e0d\u4e0b\u5012\u5e95 \u662f\u4ef6\u597d\u4e8b\uff0c\u4f60\u6b63\u5e94\u8be5\u6b22\u559c\u5462\uff01\u201d\u957f\u5b59\u6cf0\u7684\u5fc3\u601d\u4e0d\u5982\u674e\u9038\u7075\u654f\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u65b9\u59cb\u660e\u767d\u4ed6\u8bdd \u4e2d\u7684\u542b\u610f\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5a49\u513f\u65e2\u5728\u4e3a\u5a5a\u4e8b\u601d\u91cf\uff0c\u800c\u53c8\u59d4\u51b3\u4e0d\u4e0b\uff0c\u90a3\u4e48\uff0c\u7eb5\u4f7f\u5979\u4ecd\u7136\u6b22 \u559c\u674e\u9038\uff0c\u6700\u5c11\u5fc3\u4e2d\u4e5f\u6709\u6211\uff0c\u4ed6\u4e00\u53a2\u60c5\u613f\uff0c\u4ee5\u4e3a\u5a49\u513f\u662f\u8981\u5728\u674e\u9038\u4e0e\u4ed6\u4e4b\u4e2d\u9009\u62e9\u4e00\u4eba\uff0c\u73b0\u5728 \u674e\u9038\u65e2\u5df2\u6210\u4eb2\uff0c\u90a3\u5f53\u7136\u975e\u4ed6\u83ab\u5c5e\uff0c\u8fd9\u7965\u4e00\u60f3\uff0c\u5fc3\u4e0a\u6101\u4e91\u5c3d\u53bb\uff0c\u4e0d\u89c9\u559c\u4e0a\u7709\u68a2\u3002 \u3000\u3000\u674e\u9038\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u6b66\u59d1\u5a18\u600e\u4e48\u6837\uff0c\u7ed3\u4e86\u5a5a\u6ca1\u6709\uff1f\u201d\u4ed6\u672c\u6765\u662f\u6015\u63d0\u8d77\u6b66\u7384\u971c\u7684\uff0c\u5374\u53c8 \u7981\u4e0d\u4f4f\u4e0d\u95ee\uff0c\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u672a\u542c\u8bf4\u8fc7\uff0c\u5927\u7ea6\u672a\u66fe\u7ed3\u5a5a\u5427\u3002\u5979\u5728\u5916\u9762\u7684\u65f6\u5019\u591a\uff0c\u867d\u662f\u5929\u540e \u7684\u4f84\u5973\uff0c\u4e00\u5e74\u5374\u96be\u5f97\u6709\u51e0\u6b21\u8fdb\u5bab\u3002\u201d\u674e\u9038\u4e0d\u89c9\u53c8\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u7384\u971c\u7684\u5e74\u7eaa\u6bd4\u5a49 \u513f\u8fd8\u8981\u5927\u597d\u51e0\u5e74\uff0c\u5c1a\u672a\u7ed3\u5a5a\uff0c\u96be\u9053\uff0c\u96be\u9053\uff0c\u5979\u4e5f\u662f\u50cf\u5a49\u513f\u90a3\u6837\u5728\u7b49\u5f85\u6211\u5417\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211\u542c\u5a49\u513f\u8bf4\uff0c\u5929\u540e\u5df1\u6709\u610f\u601d\u5728\u767e\u5e74\u4e4b\u540e\uff0c\u5c06\u5e1d\u4f4d\u4f20\u7ed9\u5362\u9675\u738b\uff0c\u4ecd\u7136\u662f \u4f60\u4eec\u674e\u5bb6\u7684\u5929\u4e0b\uff0c\u4f60\u53ef\u4ee5\u56de\u53bb\u4e86\u5427\uff1f\u201d\u8fd9\u4e2a\u6d88\u606f\u867d\u7136\u9887\u51fa\u674e\u9038\u610f\u5916\uff0c\u4f46\u4ed6\u60f3\u4e00\u60f3\uff0c\u4ecd\u7136 \u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u56de\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4f60\u4e0d\u56de\u53bb\uff0c\u6211\u4e5f\u4e0d\u6562\u52c9\u5f3a\u4f60\u3002\u4f46\u4f60\u4e3a\u4ec0\u4e48\u4e0e\u8fd9\u4e2a\u7a0b\u8001\u8d3c\u4e00\u9053\uff0c\u96be\u9053\u4e5f \u662f\u60f3\u53bb\u6295\u5954\u7a81\u53a5\u4e48\uff1f\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u6211\u867d\u53cd\u5bf9\u4f2a\u5468\u6b66\u6c0f\uff0c\u5374\u8fd8\u4e0d\u81f3\u4e8e\u6295\u5954\u7a81\u53a5\u3002\u6211\u548c\u7a0b\u8fbe\u82cf\u4ed6\u4eec\u4e00\u9053\uff0c\u4e43\u662f \u60f3\u501f\u52a9\u4ed6\u4eec\u4e4b\u529b\uff0c\u6f5c\u5165\u7a81\u53a5\u738b\u5ef7\uff01\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u201d\u674e\u9038\uff1a\u201c\u8fd9\u662f\u4e3a\u4e86\u4f60\u5916 \u7525\u7684\u539f\u6545\u3002\u201d\u5f53\u4e0b\uff0c\u5c06\u6b66\u58eb\u63b3\u8d70\u4ed6\u7684\u513f\u5b50\uff0c\u5a01\u80c1\u4ed6\u6295\u987a\u7a81\u53a5\u5927\u6c57\u7b49\u4e8b\u60c5\u5bf9\u957f\u5b59\u6cf0\u8bf4\u4e86\u3002 \u957f\u5b59\u6cf0\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5a49\u513f\u4f1a\u6b22\u559c\u4ed6\uff0c\u539f\u6765\u4ed6\u4e0e\u5a49\u513f\uff0c\u9664\u4e86\u6027\u60c5\u76f8\u6295\u4e4b\u5916\uff0c\u5bf9\u4e8e\u5927 \u662f\u5927\u975e\uff0c\u4e5f\u8fd8\u5206\u5f97\u6e05\u695a\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u8fd9\u6b21\u7a81\u53a5\u51c6\u5907\u5174\u5175\u5165\u5bc7\uff0c\u5929\u540e\u65e9\u5df2\u5f97\u77e5\u98ce\u58f0\uff0c\u8fb9\u5173\u9632\u536b\u68ee\u4e25\uff0c\u53ef\u4ee5\u65e0 \u8651\u3002\u6240\u53ef\u8651\u8005\uff0c\u6709\u4e00\u73ed\u6b66\u6797\u8d25\u7c7b\uff0c\u548c\u4e00\u4e9b\u4e0d\u660e\u5927\u4e49\u7684\u7687\u5510\uff0c\u5927\u81e3\u4e5f\u7eb7\u7eb7\u6295\u5954\u7a81\u53a5\uff0c\u5374\u662f \u4e0d\u53ef\u4e0d\u9632\u3002\u6211\u8fd9\u6b21\u5c31\u662f\u5949\u4e86\u5929\u540e\u4e4b\u547d\uff0c\u4e13\u4e3a\u7f09\u6355\u7a0b\u8fbe\u82cf\u4e0e\u5357\u5bab\u5c1a\u6765\u7684\uff0c\u73b0\u5728\u4f60\u65e2\u7136\u8fd8\u6709 \u7528\u5f97\u7740\u4ed6\u4eec\u7684\u5730\u65b9\uff0c\u6211\u5c31\u8ba9\u4ed6\u4eec\u591a\u6d3b\u4e00\u65f6\u5427\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u542c\u4f60\u521a\u624d\u6240\u8bf4\uff0c\u5929\u6076\u9053\u4eba\u548c \u706d\u5ea6\u795e\u541b\u7b49\u4eba\uff0c\u4e5f\u90fd\u6295\u5230\u7a81\u53a5\u6765\u4e86\uff0c\u636e\u6211\u6240\u77e5\uff0c\u8fd9\u4e9b\u4eba\u7684\u6b66\u529f\u5b9e\u662f\u4e0d\u53ef\u5c0f\u89c6\uff0c\u53ea\u6015\u6b66\u5219 \u5929\u795e\u6b66\u8425\u4e2d\u90a3\u4e09\u5927\u9ad8\u624b\uff0c\u4e5f\u8fd8\u6bd4\u4e0d\u4e0a\u4ed6\u4eec\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5929\u540e\u662f\u5426\u53e6\u5916\u6d3e\u6709\u80fd\u4eba\uff0c\u6211 \u4e0d\u77e5\u9053\u3002\u548c\u6211\u540c\u6765\u7684\u5219\u53ea\u6709\u767d\u5143\u5316\u4e00\u4eba\u3002\u201d\u674e\u9038\u672c\u6765\u662f\u60f3\u4ece\u4fa7\u9762\u6253\u542c\u4e00\u4e0b\uff0c\u95ee\u95ee\u6b66\u7384\u971c \u662f\u5426\u4f1a\u6765\uff0c\u89c1\u957f\u5b59\u6cf0\u5e76\u4e0d\u77e5\u60c5\uff0c\u4e0d\u4fbf\u518d\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u58c1\u59b9\u5462\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u4e0d\u613f\u610f\u4ee4\u5979\u5192\u9669\uff0c\u6240\u4ee5\u8ba9\u5979\u56f4\u5728\u5929\u5c71\u3002\u201d\u957f \u5b59\u6cf0\u95ee\u4e86\u4e00\u4e9b\u4ed6\u516b\u5e74\u6765\u7684\u751f\u6d3b\u60c5\u5f62\uff0c\u53c8\u95ee\u4e86\u4ed6\u4e00\u4e9b\u5173\u4e8e\u59b9\u5b50\u7684\u60c5\u51b5\uff0c\u4ece\u674e\u9038\u7684\u53e3\u4e2d\u53ef\u4ee5 \u542c\u51fa\uff0c\u4ed6\u4eec\u592b\u5987\u4e4b\u95f4\u751a\u4e3a\u601d\u7231\uff0c\u957f\u5b59\u6cf0\u4e5f\u5c31\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u5929\u5c06\u7834\u6653\uff0c\u65e9\u8d77\u89c5\u98df\u7684\u5140\u9e70\uff0c\u5df2\u5728\u5e10\u5e55\u4e0a\u632f\u7ffc\u98de\u817e\uff0c\u98de\u8fc7\u4e4b\u65f6\uff0c\u5e26\u8d77\u4e86\u4e00\u80a1\u98ce\u58f0\uff0c \u8349\u539f\u4e0a\u7684\u4eba\u5bb6\uff0c\u542c\u5230\u8fd9\u79cd\u58f0\u97f3\uff0c\u5c31\u50cf\u4e2d\u539f\u7684\u4eba\u5bb6\u542c\u5230\u9e21\u9e23\u4e00\u6837\uff0c\u77e5\u9053\u9ed1\u591c\u5c06\u901d\u4e86\u3002\u957f\u5b59 \u6cf0\u9053\uff1a\u201c\u65f6\u5019\u4e0d\u65e9\uff0c\u6211\u8be5\u8d70\u5566\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u4eca\u540e\u884c\u6b62\u5982\u4f55\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211\u5148\u8981 \u627e\u5230\u767d\u5143\u5316\uff0c\u7136\u540e\u4e5f\u8bb8\u4f1a\u5230\u7a81\u53a5\u738b\u5ef7\u3002\u82e5\u662f\u4e8b\u60c5\u5df2\u4e86\uff0c\u6211\u4e5f\u613f\u5230\u5929\u5c71\u63a2\u671b\u4f60\u4eec\u3002\u201d \u3000\u3000\u4e24\u5a18\u8205\u5197\u624b\u9053\u522b\uff0c\u674e\u9038\u5c06\u4ed6\u9001\u51fa\u5e10\u5916\uff0c\u966a\u4ed6\u5728\u5468\u56f4\u5bdf\u770b\u4e00\u756a\uff0c\u5e76\u672b\u53d1\u89c1\u964c\u751f\u4eba\u7684\u8db3 \u5370\uff0c\u767d\u5143\u5316\u7684\u5f71\u5b50\u4e5f\u4f9d\u7136\u4e0d\u89c1\u3002\u674e\u9038\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4f4d\u5f02\u4eba\u53ea\u6551\u8d70\u4e86\u767d\u5143\u5316\uff0c\u5374\u4e0d\u7406\u4f1a \u957f\u5b59\u6cf0\uff0c\u96be\u9053\u4ed6\u5df2\u77e5\u9053\u957f\u5b59\u6cf0\u4e0e\u6211\u7684\u5173\u7cfb\uff0c\u4e5f\u77e5\u9053\u6211\u6709\u8bdd\u8981\u548c\u4e0a\u5b98\u5a49\u513f\u7ec6\u8bf4\u4e48\uff1f\u201d \u3000\u3000\u674e\u9038\u9001\u8d70\u4e86\u957f\u5b59\u6cf0\u4e4b\u540e\uff0c\u56de\u5230\u5e10\u4e2d\uff0c\u8be6\u7ec6\u5728\u7a0b\u8fbe\u82cf\u4e0e\u5357\u5bab\u5c1a\u7684\u8eab\u4e0a\u5bdf\u770b\uff0c\u7a0b\u8fbe\u82cf\u662f \u7ed9\u6885\u82b1\u9488\u6253\u5165\u4e86\u201c\u5173\u5143\u7a74\u201d\uff0c\u5357\u5bab\u5c1a\u5219\u662f\u88ab\u6253\u5165\u4e86\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u8fd9\u662f\u4e00\u79cd\u72ec\u7279\u7684\u6253\u7a74\u624b \u6cd5\uff0c\u8981\u66ff\u4ed6\u4eec\u89e3\u7a74\uff0c\u5fc5\u987b\u5148\u7528\u78c1\u77f3\u5c06\u6885\u82b1\u9488\u5438\u51fa\uff0c\u53ef\u662f\u674e\u9038\u5374\u5e76\u4e0d\u5907\u6709\u78c1\u77f3\uff0c\u60f3\u5230\u7a0b\u8fbe \u82cf\u4e43\u662f\u70b9\u7a74\u540d\u5bb6\uff0c\u4fbf\u53bb\u68c0\u67e5\u4ed6\u90a3\u76db\u6697\u5668\u7684\u76ae\u56ca\uff0c\u679c\u7136\u627e\u5230\u4e86\u4e00\u5757\u78c1\u77f3\u3002 \u3000\u3000\u674e\u9038\u62ff\u8d77\u78c1\u77f3\uff0c\u8d70\u5230\u7a0b\u8fbe\u82cf\u8eab\u8fb9\uff0c\u5fc3\u5ff5\u5ffd\u8f6c\uff0c\u6539\u4e86\u4e3b\u610f\uff0c\u5c06\u7a0b\u8fbe\u82cf\u6682\u65f6\u6401\u4e0b\uff0c\u5148\u66ff \u5357\u5bab\u5c1a\u6cbb\u7406\u3002 \u3000\u3000\u89e3\u5f00\u8863\u88f3\u7ec6\u770b\uff0c\u53ea\u89c1\u5357\u5bab\u5c1a\u7684\u201c\u98ce\u5e9c\u7a74\u201d\u4e0a\u6709\u4e24\u4e2a\u6781\u7ec6\u5c0f\u7684\u9488\u53e3\uff0c\u60f3\u662f\u90a3\u4e2a\u65bd\u653e\u6697 \u5668\u7684\u5f02\u4eba\uff0e\u6015\u4e00\u679d\u6885\u82b1\u9488\u7684\u529b\u91cf\u4e0d\u591f\uff0c\u6240\u4ee5\u7528\u4e0a\u4e86\u4e24\u53e3\u3002\u674e\u9038\u5c06\u78c1\u77f3\u5728\u9489\u53e3\u4e4b\u5904\u8f7b\u8f7b\u4e00 \u8f6c\uff0c\u628a\u4e24\u679a\u6885\u82b1\u9488\u5438\u4e86\u51fa\u6765\uff0c\u8d81\u7740\u5357\u5bab\u5c1a\u5c1a\u672a\u66fe\u6e05\u9192\uff0c\u7acb\u523b\u5c06\u4ed6\u7684\u4e24\u4e2a\u9488\u53e3\u5f04\u5927\uff0c\u8fde\u6210 \u4e00\u4e2a\uff0c\u968f\u5373\u62c8\u8d77\u4e86\u4e00\u6839\u6885\u82b1\u948e\uff0c\u5728\u81ea\u5df1\u80c1\u4e0b\u7684\u201c\u7389\u9f99\u7a74\u201d\u4e00\u523a\uff0c\u4f46\u5374\u6545\u610f\u4e0d\u523a\u6b63\u7a74\u9053\uff0c \u7a0d\u7a0d\u504f\u65c1\u4e86\u4e00\u4e24\u5206\u3002 \u3000\u3000\u674e\u9038\u5148\u66ff\u5357\u5bab\u5c1a\u89e3\u4e86\u7a74\u9053\uff0c\u5357\u5bab\u5c1a\u7741\u5f00\u773c\u775b\uff0c\u89c1\u674e\u9038\u5728\u4ed6\u8eab\u65c1\uff0c\u800c\u957f\u5b59\u6cf0\u5219\u5df2\u4e0d\u89c1\uff0c \u60ca\u8be7\u4e4b\u6781\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u54b1\u4eec\u90fd\u53d7\u4eba\u6697\u7b97\u4e86\uff0c\u4f60\u53ef\u77a7\u89c1\u6765\u4eba\u7684 \u9762\u8c8c\u5417\uff1f\u201d\u5357\u5bab\u5c1a\uff1a\u201c\u6ca1\u6709\u5440\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u5012\u5728\u5730\u4e0a\uff0c\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u597d\u4f3c\u89c9\u5f97\u6709\u4e00\u4e2a \u4eba\u8d70\u8fdb\u6765\uff0c\u4ee5\u540e\u5c31\u4e0d\u9192\u4eba\u4e8b\u4e86\u3002\u201d\u5357\u5bab\u5c1a\u6709\u70b9\u7591\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u5144\uff0c\u4f60\u7684\u529f\u529b\u6bd4\u6211\u9ad8\u5f97 \u591a\uff0c\u6211\u65b9\u81ea\u89c9\u5f97\u6709\u4eba\u6697\u88ad\uff0c\u4fbf\u7acb\u5373\u4e0d\u9192\u4eba\u4e8b\u4e86\u3002\u201d\u4ed6\u672c\u6765\u6709\u70b9\u7591\u5fc3\u662f\u674e\u9038\u6697\u7b97\uff0c\u4f46\u8f6c\u5ff5 \u4e00\u60f3\uff0c\u674e\u9038\u662f\u5510\u5ba4\u738b\u5b59\uff0c\u4ed6\u51b3\u6ca1\u6709\u53cd\u800c\u5e2e\u52a9\u654c\u4eba\u4e4b\u7406\u3002 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u7a0b\u8001\u5e2e\u4e3b\u4e00\u8def\u4e4b\u4e0a\u4f3c\u4e4e\u5bf9\u6211\u6709\u70b9\u7591\u5fc3\uff0c\u4f46\u6211\u7684\u8eab\u4efd\uff0c\u672a\u5230\u65f6\u5019\uff0c\u5374\u53c8\u4e0d \u4fbf\u5411\u4ed6\u5410\u9732\uff0c\u4f60\u66ff\u6211\u906e\u7792\u4e9b\u513f\u3002\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\u3002\u201d\u4ed6\u5fc3\u4e2d\u6697\u559c\u674e\u9038\u5bf9\u4ed6\u7684\u4fe1 \u4efb\uff0c\u4f46\u5374\u4e5f\u53e6\u5916\u8d77\u4e86\u4e00\u4e2a\u7591\u56e2\uff1a\u201c\u83ab\u6628\u662f\u4ed6\u5ff5\u5728\u957f\u5b59\u6cf0\u7236\u4eb2\u7684\u4efd\u4e0a\uff0c\u6015\u7a0b\u5927\u54e5\u52a0\u5bb3\u4e8e\u4ed6\uff0c \u6545\u6b64\u5c06\u6211\u4eec\u6697\u7b97\uff0c\u597d\u628a\u957f\u5b59\u6cf0\u653e\u8d70\uff1f\u5176\u5b9e\u4ed6\u82e5\u6709\u8fd9\u4e2a\u4e3b\u610f\uff0c\u5c3d\u53ef\u4ee5\u4e0e\u6211\u660e\u8a00\uff0c\u6211\u4e5f\u4e0d\u4e00 \u5b9a\u8981\u5bb3\u957f\u5b59\u6cf0\u7684\u3002\u201d \u3000\u3000\u674e\u9038\u63a5\u7740\u66ff\u7a0b\u8fbe\u82cf\u89e3\u7a74\uff0c\u7a0b\u8fbe\u82cf\u7684\u529f\u529b\u6df1\u539a\uff0c\u679c\u7136\u975e\u6bd4\u5bfb\u5e38\uff0c\u674e\u9038\u521a\u521a\u5c06\u63d2\u5728\u4ed6 \u201c\u5173\u5143\u7a74\u201d\u4e0a\u7684\u4e24\u679a\u6885\u82b1\u9488\u5438\u51fa\uff0c\u4ed6\u4fbf\u7acb\u5373\u9192\u8f6c\uff0c\u4e0d\u5f85\u674e\u9038\u66ff\u4ed6\u89e3\u7a74\uff0c\u4fbf\u5373\u8fd0\u6c14\u51b2\u5f00\uff0c \u500f\u5730\u4e00\u4e2a\u7ffb\u8eab\uff0c\u84e6\u7136\u8dc3\u8d77\uff0c\u53cd\u624b\u4e00\u6263\uff0c\u6263\u7740\u4e86\u674e\u9038\u7684\u8109\u95e8\u3002\u5357\u5bab\u5c1a\u5927\u60ca\u5931\u8272\uff0c\u53eb\u9053\uff1a \u201c\u5927\u54e5\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u8981\u77e5\u5357\u5bab\u5c1a\u867d\u7136\u5bf9\u674e\u9038\u4e16\u6697\u6697\u8d77\u7591\uff0c\u4f46\u4ed6\u4e3a\u4e86\u524d\u7a0b\uff0c\u7a76\u7adf\u662f\u5e2e\u7740 \u674e\u9038\u3002 \u3000\u3000\u4ee5\u674e\u9038\u7684\u6b66\u529f\uff0c\u672c\u6765\u53ef\u4ee5\u6323\u8131\uff0c\u4ed6\u5374\u4e1d\u6beb\u4e0d\u52a0\u6297\u62d2\uff0c\u6545\u610f\u4f5c\u51fa\u60ca\u6050\u975e\u5e38\u7684\u6837\u5b50\uff0c\u98a4 \u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5927\u54e5\uff0c\u6211\u662f\u6765\u66ff\u4f60\u89e3\u7a74\u7684\u5440\uff01\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u4e00\u58f0\u51b7\u7b11\uff0c\u6495\u4e0b\u4e86\u4ed6\u7684\u8863\u886b\uff0c\u4e00\u770b\u770b\u5230\u4e86\u4ed6\u201c\u7389\u9f99\u7a74\u201d\u65c1\u8fb9\u7684\u9488\u53e3\uff0c\u7591\u5fc3\u7a0d \u51cf\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u4e5f\u7ed9\u654c\u4eba\u6253\u4e86\u7a74\u9053\u4e86\u3002\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u7684\u786e\u662f\u6709\u5916\u4eba\u5077\u88ad\uff0c\u6211 \u5728\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u4e5f\u4f3c\u66fe\u542c\u5230\u4eba\u58f0\u3002\u201d\u7a0b\u8fbe\u82cf\u5fc3\u60f3\uff1a\u201c\u4ed6\u7684\u672c\u9886\u867d\u7136\u9ad8\u51fa\u5357\u5bab\u5c1a\u8bb8\u591a\uff0c\u4f46\u8981 \u6697\u7b97\u6211\uff0c\u8c05\u4ed6\u8fd8\u6ca1\u6709\u8fd9\u6837\u672c\u9886\u3002\u201d\u60f3\u4e86\u4e00\u60f3\uff0c\u5c06\u674e\u9038\u653e\u5f00\uff0c\u559d\u9053\uff1a\u201c\u5357\u5bab\u5c1a\uff0c\u4f60\u8fc7\u6765\uff01\u201d \u5357\u5bab\u5c1a\u60ca\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5927\u54e5\uff0c\u6211\u4e5f\u4e2d\u4e86\u654c\u4eba\u7684\u6885\u82b1\u9488\u5440\uff01\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u7ed9\u6211\u770b\u770b\u3002\u201d\u6495\u5f00\u4ed6\u7684\u8863\u895f\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u98ce\u5e9c\u7a74\u4e0a\u4e2d\u4e86\u4e00 \u679a\u6885\u82b1\u9488\uff0c\u6664\uff0c\u8fd9\u679a\u6885\u82b1\u9488\u6253\u5f97\u5f88\u5389\u5bb3\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u5e78\u597d\u7a0b\u5e2e\u4e3b\u968f\u8eab\u5e26\u6709\u78c1\u77f3\uff0c\u53ef\u662f \u6211\u7684\u624b\u6cd5\u4e0d\u5927\u719f\u7ec3\uff0c\u7ed3\u679c\u8fd8\u662f\u8981\u525c\u5f00\u5c11\u8bb8\u76ae\u8089\uff0c\u624d\u80fd\u591f\u628a\u8fd9\u53e3\u9488\u53d6\u51fa\u6765\u3002\u201d\u4ed6\u662f\u6015\u9488\u53e3 \u592a\u5927\uff0c\u7a0b\u8fbe\u82cf\u89c1\u4e86\u8d77\u7591\uff0c\u6545\u6b64\u52a0\u4ee5\u89e3\u91ca\u3002\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u7528\u78c1\u77f3\u5438\u9488\uff0c\u53c8\u61c2\u5f97\u89e3\u7a74\uff0c \u4e5f\u7b97\u5f97\u662f\u4e2a\u884c\u5bb6\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u5728\u5730\u4e0a\u6361\u8d77\u4e86\u56db\u679a\u6885\u82b1\u9488\uff0c\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u77a7\u89c1\u654c\u4eba\u7684\u9762\u8c8c\u4e48\uff1f\u201d \u674e\u9038\u4e0e\u5357\u5b98\u5c1a\u540c\u58f0\u7b54\u9053\uff1a\u201c\u53ea\u662f\u542c\u89c1\u58f0\u97f3\uff0c\u4fbf\u7acb\u5373\u660f\u8ff7\u4e86\u3002\u201d\u7a0b\u8fbe\u82cf\u6697\u6697\u53eb\u4e86\u4e00\u58f0\uff1a \u201c\u60ed\u6127\uff01\u201d\u539f\u6765\u4ed6\u8fde\u654c\u4eba\u7684\u58f0\u97f3\u90fd\u6beb\u65e0\u89c9\u5bdf\u3002\u674e\u9038\u4e0e\u5357\u5bab\u5c1a\u7684\u6b66\u529f\u90fd\u4e0e\u4ed6\u76f8\u5dee\u9887\u8fdc\uff0c\u4f55 \u4ee5\u53cd\u800c\u662f\u4ed6\u4eec\u542c\u51fa\u4e86\u654c\u4eba\u7684\u58f0\u606f\uff0c\u8fd9\u4e00\u70b9\u672c\u6765\u8db3\u4ee5\u4ee4\u7a0b\u8fbe\u82cf\u8d77\u7591\uff0c\u5e78\u800c\u674e\u9038\u5e03\u7f6e\u5f97\u975e\u5e38 \u5de7\u5999\uff0c\u7a0b\u8fbe\u82cf\u521a\u4e00\u8d77\u7591\uff0c\u4fbf\u7acb\u5373\u60f3\u5230\uff1a\u201c\u654c\u4eba\u8fdb\u6765\u5077\u88ad\uff0c\u5f53\u7136\u9996\u5148\u662f\u8981\u5bf9\u4ed8\u6211\uff0c\u5176\u6b21\u662f \u5357\u5bab\u5c1a\uff0c\u518d\u5176\u6b21\u624d\u662f\u8fd9\u4e2a\u4e0a\u5b98\u654f\u3002\u6211\u5148\u4e2d\u4e86\u4e24\u679a\u6885\u82b1\u9488\uff0c\u4ed6\u4eec\u7136\u540e\u5404\u4e2d\u4e00\u679a\uff0c\u4e0a\u5b98\u654f\u7684 \u8f7b\u529f\u5f88\u597d\uff0c\u6545\u6b64\u6253\u6b6a\u4e86\u5c11\u8bb8\u3002\u201d\u4ed6\u51ed\u7740\u6570\u5341\u5e74\u7684\u7ecf\u9a8c\uff0c\u81ea\u4ee5\u4e3a\u63a8\u65ad\u4e0d\u9519\uff0c\u4e8e\u662f\u5bf9\u674e\u9038\u7684 \u7591\u5fc3\u4e5f\u5c31\u56e0\u4e4b\u6d88\u9664\u3002 \u3000\u3000\u5f53\u4e0b\u7a0b\u8fbe\u82cf\u7b11\u9053\uff1a\u201c\u5e78\u4e8f\u8fd9\u4e2a\u5077\u88ad\u7684\u654c\u4eba\uff0c\u4ed6\u7528\u6885\u82b1\u9488\u6253\u7a74\u7684\u529f\u592b\uff0c\u8fd8\u672a\u5230\u6700\u4e0a\u4e58 \u7684\u5883\u754c\uff0c\u6253\u4e0a\u5b98\u654f\u5144\u7684\u90a3\u679a\u6885\u82b1\u9488\uff0c\u7adf\u5728\u4ed6\u7684\u7389\u9f99\u7a74\u65c1\u8fb9\u504f\u5f00\u4e24\u5206\uff0c\u8981\u4e0d\u7136\u54b1\u4eec\u73b0\u5728\u8fd8 \u6ca1\u6709\u4eba\u642d\u6551\u5462\u3002\u6211\u521a\u624d\u662f\u4e3a\u4e86\u67e5\u5bdf\u654c\u4eba\u7684\u624b\u6cd5\uff0c\u4e0a\u5b98\u5144\uff0c\u4f60\u4e0d\u8981\u591a\u5fc3\u3002\u201d\u674e\u9038\u677e\u4e86\u53e3\u6c14\uff0c \u8fde\u9053\uff1a\u201c\u4e0d\u6562\u201d\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u662f\u7a0b\u8fbe\u82cf\u7684\u81ea\u6211\u89e3\u5632\uff0c\u4ed6\u7aef\u8be6\u4e86\u90a3\u56db\u679a\u6885\u82b1\u9488\uff0c\u9488\u957f\u53ea\u6709\u4e03\u516b\u5206\uff0c\u6bd4\u666e\u901a\u7684 \u7f1d\u8863\u9488\u8fd8\u8981\u5e7c\u7ec6\u5f97\u591a\uff0c\u6709\u8fd9\u79cd\u4efd\u91cf\u6781\u8f7b\u7684\u6885\u82b1\u9488\u6253\u7a74\uff0c\u800c\u4e14\u6700\u5c11\u662f\u5728\u4e09\u4e08\u4e4b\u5916\u6253\u6765\uff08\u56e0 \u4e3a\u82e5\u5728\u4e09\u4e08\u4e4b\u5185\uff0c\u51ed\u4ed6\u7684\u672c\u9886\uff0c\u5b9a\u80fd\u89c9\u5bdf\uff09\u3002\u8fd9\u4efd\u529f\u592b\uff0c\u4ed6\u81ea\u95ee\u4e5f\u4e0d\u80fd\u591f\uff0c\u4ed6\u4e00\u5411\u4ee5\u4e3a \u81ea\u5df1\u70b9\u7a74\u3001\u6253\u7a74\u7684\u529f\u592b\u662f\u4e16\u4e0a\u65e0\u53cc\uff0c\u4eba\u95f4\u7b2c\u4e00\uff0c\u5c82\u77e5\u8fd8\u6709\u4eba\u9ad8\u51fa\u4ed6\u4e0a\uff0c\u7109\u80fd\u4e0d\u4ee4\u4ed6\u6697\u6697 \u60ca\u5fc3\uff01 \u3000\u3000\u7a0b\u8fbe\u82cf\u6068\u6068\u8bf4\u9053\u3002\u201c\u8fd9\u4e2a\u4eba\u4e5f\u7b97\u5f97\u662f\u4e2a\u6253\u7a74\u7684\u9ad8\u624b\u4e86\u3002\u53ea\u662f\u884c\u52a8\u5374\u672a\u514d\u4e0d\u591f\u5149\u660e\u78ca \u843d\uff0c\u53ef\u60dc\u4e0d\u77e5\u662f\u8c01\uff0c\u6211\u5012\u60f3\u548c\u4ed6\u597d\u597d\u7684\u8f83\u91cf\u4e00\u756a\u3002\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u5230\u4e86\u7a81\u53a5\u738b\u5ef7\uff0c\u95ee\u95ee \u5929\u6076\u9053\u4eba\u548c\u706d\u5ea6\u795e\u541b\uff0c\u6216\u8005\u4ed6\u4eec\u4f1a\u77e5\u9053\u3002\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u597d\uff0c\u54b1\u4eec\u73b0\u5728\u8d70\u5427\u3002\u201d \u3000\u3000\u4e09\u4eba\u6536\u62fe\u8d77\u5e10\u84ec\uff0c\u8d70\u4e86\u4e00\u7a0b\uff0c\u5ffd\u89c1\u8349\u539f\u4e0a\u6709\u4e09\u5339\u5feb\u9a6c\u9a70\u6765\uff0c\u5f53\u524d\u4e24\u9a91\u5df2\u770b\u6e05\u695a\u4e86\u4e43 \u662f\u6c49\u4eba\uff0c\u7a0b\u8fbe\u82cf\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u5c45\u7136\u6562\u4e00\u518d\u6b3a\u8d1f\u5230\u6211\u7684\u5934\u4e0a\u6765\u4e86\u3002\u201d\u626c\u624b\u4fbf\u662f\u4e24\u9897 \u94c1\u83b2\u5b50\u98de\u53bb\uff0c\u90a3\u4e24\u4e2a\u6c49\u4eba\u6b66\u58eb\u5728\u9a6c\u80cc\u4e0a\u817e\u98de\u8d77\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7a0b\u5927\u54e5\uff0c\u4e0d\u8ba4\u5f97\u5c0f\u5f1f\u4e86\u5417\uff1f\u201d \u968f\u540e\u90a3\u4e00\u9a91\u4ea6\u5df2\u6765\u5230\uff0c\u662f\u4e2a\u7a81\u53a5\u519b\u5b98\uff0c\u7a0b\u8fbe\u82cf\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4eec\u4e0d\u662f\u5c01\u7267\u91ce \u4e0e\u795d\u89c1\u7ae0\u5417\uff1f\u201d\u54ea\u4e24\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u5341\u591a\u5e74\u672a\u89c1\uff0c\u5927\u54e5\u539f\u6765\u8fd8\u8ba4\u5f97\u6211\u4eec\u3002\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u7741\u5927\u773c\u775b\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4eec\u5728\u6b66\u627f\u55e3\u95e8\u4e0b\u5f88\u662f\u5f97\u610f\uff0c\u600e\u7684\u5374\u4e5f\u5230\u8fd9\u91cc\u6765\u4e86\uff1f \u83ab\u975e\uff0c\u83ab\u975e\u662f\u4f60\u4eec\u4e5f\u6765\u66ff\u6b66\u5219\u5929\u9080\u8bf7\u8001\u592b\u4e48\uff1f\u201d\u5c01\u7267\u91ce\u7b11\u9053\uff1a\u201c\u6211\u662f\u6765\u4e3a\u7a81\u94c1\u5927\u6c57\u8fce\u63a5 \u4f60\u4eec\uff0c\u4e0e\u6b66\u5219\u5929\u6beb\u65e0\u5173\u7cfb\u3002\u55ef\uff0c\u8fd9\u4f4d\u662f\u5927\u6c57\u5fa1\u524d\u7684\u5df4\u56fe\u9c81\u54c8\u624e\u513f\u3002\u5927\u54e5\uff0c\u4f60\u8fd9\u4e24\u4f4d\u670b\u53cb\uff0c \u5c0f\u5f1f\u597d\u4f3c\u5728\u54ea\u91cc\u89c1\u8fc7\uff0c\u8bf7\u6055\u6211\u4e00\u65f6\u773c\u62d9\uff0c\u5374\u8bb0\u4e0d\u8d77\u6765\u3002\u201d\u539f\u6765\u5c01\u7267\u91ce\u4e0e\u795d\u89c1\u7ae0\u4e8c\u4eba\u4e43\u662f \u9752\u57ce\u6d3e\u4e0e\u4e07\u80dc\u95e8\u7684\u9ad8\u624b\uff0c\u5728\u6b66\u6797\u4e2d\u9887\u6709\u540d\u671b\uff0c\u5341\u591a\u5e74\u524d\uff0c\u4e5f\u66fe\u5728\u6c5f\u6e56\u4e0a\u505a\u8fc7\u72ec\u811a\u5927\u76d7\uff0c \u4f46\u56e0\u4ed6\u4eec\u63a9\u9970\u5f97\u597d\uff0c\u77e5\u9053\u7684\u4eba\u5f88\u5c11\uff0c\u7a0b\u8fbe\u82cf\u90a3\u65f6\u662f\u4e1c\u5317\u4e94\u7701\u7684\u7eff\u6797\u9886\u8896\uff0c\u5374\u548c\u4ed6\u4eec\u7d20\u6709 \u5f80\u6765\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u662f\u4e00\u4e2a\u6c5f\u6e56\u7ecf\u9a8c\u975e\u5e38\u4e30\u5bcc\u7684\u4eba\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6211\u65e9\u5df2\u542c\u8bf4\u4ed6\u4eec\u6295\u5230\u6b66 \u627f\u55e3\u95e8\u4e0b\uff0c\u4f46\u4ed6\u4eec\u82e5\u7136\u662f\u6b66\u5219\u5929\u6240\u6d3e\uff0c\u65ad\u4e0d\u4f1a\u4e0e\u7a81\u53a5\u519b\u5b98\u540c\u5728\u4e00\u8d77\uff0c\u8fd9\u5176\u4e2d\u60f3\u5fc5\u662f\u53e6\u6709 \u539f\u56e0\u3002\u4ed6\u4eec\u95ee\u8d77\u5357\u5bab\u5c1a\u548c\u4e0a\u5b98\u654f\u8fd9\u4e24\u4e2a\u4eba\uff0c\u5f53\u7136\u4e0d\u613f\u5728\u4ed6\u9762\u524d\u660e\u8bf4\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8fd9 \u4f4d\u662f\u6211\u7684\u526f\u5e2e\u4e3b\u5357\u5bab\u5c1a\uff0c\u8fd9\u4f4d\u662f\u6211\u65b0\u7ed3\u8bc6\u7684\u4e00\u4f4d\u670b\u53cb\u4e0a\u5b98\u654f\uff0c\u662f\u524d\u671d\u5927\u81e3\u897f\u53f0\u5f85\u90ce\u4e0a\u5b98 \u4eea\u7684\u4f84\u5b50\u3002\u201d \u3000\u3000\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u516b\u5e74\u524d\u6211\u5728\u957f\u5b89\u795e\u6b66\u8425\u4e2d\uff0c\u4f3c\u66fe\u89c1\u8fc7\u4e24\u4f4d\u4e00\u9762\u3002\u90a3\u65f6\u4e24\u4f4d\u662f\u968f \u6b66\u627f\u55e3\u524d\u6765\u62dc\u8bbf\u674e\u660e\u4e4b\uff0c\u674e\u5927\u603b\u7ba1\u7684\uff0c\u6211\u5c31\u662f\u90a3\u4e2a\u5b88\u95e8\u7684\u4eba\u3002\u201d\u5357\u5bab\u5c1a\u90a3\u65f6\u6df7\u5165\u795e\u6b66\u8425 \u4e2d\uff0c\u672c\u6765\u662f\u51c6\u5907\u884c\u523a\u6b66\u5219\u5929\u7684\uff0c\u800c\u5c01\u795d\u4e8c\u4eba\u5219\u662f\u6b66\u627f\u55e3\u7684\u4eb2\u4fe1\uff0c\u5f53\u65f6\u5404\u4e3a\u5176\u4e3b\uff0c\u5982\u4eca\u8bf4 \u8d77\uff0c\u4e0d\u7981\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u6211\u5374\u8bb0\u4e0d\u8d77\u5728\u54ea\u513f\u66fe\u89c1\u8fc7\u4e24\u4f4d\u4e86\u3002\u201d\u5176\u5b9e\u4ed6\u662f\u89c1\u8fc7\u7684\uff0c\u90a3\u662f\u5341\u591a\u5e74\u524d\u4ed6 \u8fd8\u672a\u79bb\u5f00\u957f\u5b89\uff0c\u800c\u6b66\u5219\u5929\u4e5f\u8fd8\u672b\u79f0\u5e1d\u7684\u65f6\u5019\uff0c\u6709\u4e00\u6b21\u4ed6\u4eec\u968f\u6b66\u627f\u55e3\u8fdb\u5bab\u8c12\u89c1\u6b66\u5219\u5929\uff0c\u6070 \u597d\u90a3\u65f6\u674e\u9038\u4e5f\u5728\u5bab\u4e2d\uff0c\u66fe\u548c\u4ed6\u4eec\u6253\u8fc7\u4e00\u4e2a\u7167\u9762\uff0c\u674e\u9038\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec \u7684\u773c\u5149\u771f\u7684\u5982\u6b64\u5389\u5bb3\uff1f\u90a3\u65f6\u6211\u8fd8\u672a\u6210\u5e74\uff0c\u5982\u4eca\u6211\u5df2\u6539\u5bb9\u6613\u8c8c\uff0c\u4ed6\u4eec\u5341\u4f59\u5e74\u524d\u89c1\u8fc7\u6211\u4e00\u6b21\uff0c \u53c8\u672a\u66fe\u4ea4\u8c08\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u8ba4\u51fa\u6211\u6765\uff1f\u5927\u7ea6\u8fd9\u4e0d\u8fc7\u662f\u4ed6\u4eec\u7684\u5740\u6e56\u4f0e\u4fe9\uff0c\u9760\u649e\u800c\u5df2\u3002\u201d \u3000\u3000\u5c01\u7267\u91ce\u7b11\u9053\uff1a\u201c\u4e0a\u5b98\u5144\u82f1\u98ce\u8c6a\u6c14\uff0c\u4ee4\u4eba\u4e00\u89c1\uff0c\u4fbf\u751f\u94a6\u4f69\uff0c\u7eb5\u4f7f\u4ee5\u524d\u672a\u6709\u89c1\u8fc7\uff0c\u5982\u4eca \u4e5f\u4e0d\u662f\u5916\u4eba\u4e86\u3002\u5c0f\u53ef\u4eca\u65e5\u65e2\u9047\u65e7\u8bc6\uff0c\u53c8\u7ed3\u65b0\u77e5\uff0c\u771f\u662f\u5feb\u4f55\u5982\u4e4b\uff01\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u4e24\u4f4d\u5982\u4f55\u77e5\u9053\u8001\u673d\u5230\u6765\uff1f\u201d\u795d\u89c1\u7ae0\u9053\uff1a\u201c\u6211\u4eec\u5728\u7a81\u53a5\u738b\u5ef7\u78b0\u89c1\u767e\u5fe7\u4e0a \u4eba\u7684\u5f1f\u5b50\u9633\u592a\u534e\u8bf4\u7a0b\u8001\u5e2e\u4e3b\u5df2\u6258\u4ed6\u4ee3\u9ed8\u55d3\u592a\u5e08\u5148\u5bb9\uff0c\u5927\u9a7e\u8fd9\u4e24\u65e5\u4fbf\u5230\u3002\u5c0f\u5f1f\u95fb\u8baf\uff0c\u6b23\u559c \u4f55\u4f3c\uff0c\u4f46\u671b\u65e9\u65e5\u62dc\u89c1\u543e\u5144\uff0c\u662f\u4ee5\u548c\u8fd9\u4f4d\u5927\u4eba\u8d76\u6765\u8fce\u63a5\u3002\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u5927\u5e05\u5982\u6b64\u4f18\u793c\uff0c \u771f\u662f\u592a\u4e0d\u6562\u5f53\u4e86\u3002\u767e\u5fe7\u4e0a\u4eba\u7684\u6cd5\u9a7e\u5230\u4e86\u6ca1\u6709\uff1f\u201d\u795d\u89c1\u7ae0\u9053\uff1a\u201c\u542c\u8bf4\u4e5f\u662f\u8fd9\u4e00\u4e24\u65e5\u5230\u6765\u3002\u201d \u674e\u9038\u5185\u5fc3\u6697\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u767e\u5fe7\u4e0a\u4eba\u4e0e\u5929\u6076\u9053\u4eba\u706d\u5ea6\u795e\u541b\u5408\u79f0\u57df\u5916\u4e09\u51f6\uff0c\u4ed6\u7684\u6b66\u529f\u66f4\u5728\u5929 \u6076\u3001\u706d\u5ea6\u4e4b\u4e0a\uff0e\u4ed6\u82e5\u4e5f\u6295\u7a81\u53a5\uff0c\u8c01\u4eba\u5236\u5f97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u95ee\u9053\uff1a\u201c\u738b\u57ce\u7684\u6b66\u58eb\u5927\u4f1a\u4ec0\u4e48\u65f6\u5019\u53ec\u5f00\uff1f\u201d\u795d\u89c1\u7ae0\u9053\uff1a\u201c\u5df2\u5b9a\u597d\u4e86\u65e5\u671f\uff0c\u5c31 \u5728\u4e09\u5929\u4e4b\u540e\u3002\u6211\u8fd8\u6015\u5927\u54e5\u8d76\u4e0d\u53ca\u5462\u3002\u201d\u7a0b\u8fbe\u82cf\u7b11\u9053\uff1a\u201c\u6211\u8001\u4e86\uff0c\u6b64\u53bb\u4e0d\u8fc7\u662f\u51d1\u51d1\u70ed\u95f9\u800c \u5df2\uff0c\u4ed6\u4eec\u5e74\u5c11\u82f1\u96c4\u5012\u53ef\u4ee5\u8d81\u6b64\u673a\u4f1a\uff0c\u5927\u663e\u8eab\u624b\uff0c\u95ef\u4e2a\u4e07\u513f\u3002\u201d\u201c\u95ef\u4e2a\u4e07\u513f\u201d\u4e43\u662f\u6c5f\u6e56\u672f \u8bed\uff0c\u5373\u662f\u6811\u7acb\u540d\u58f0\u7684\u610f\u601d\u3002 \u3000\u3000\u5c01\u7267\u91ce\u7b56\u9a6c\u4e0e\u674e\u9038\u5e76\u80a9\uff0c\u8bf4\u9053\uff1a\u201c\u4ee4\u53d4\u4ee5\u8bd7\u4e49\u9a70\u8a89\uff0c\u5144\u53f0\u5374\u559c\u4e0e\u6c5f\u6e56\u8c6a\u5ba2\u5f80\u6765\uff0c\u7aef \u7684\u96be\u5f97\u3002\u542c\u5357\u5bab\u5144\u8bf4\uff0c\u5144\u53f0\u7684\u5251\u672f\u5f53\u4e16\u5c11\u6709\uff0c\u4e0d\u77e5\u4ee4\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u5357\u5bab\u5144 \u662f\u6545\u610f\u7ed9\u5c0f\u5f1f\u9762\u4e0a\u8d34\u91d1\uff0c\u5176\u5b9e\u5c0f\u5f1f\u4e0d\u8fc7\u662f\u80e1\u4e71\u5b66\u4e86\u51e0\u624b\u5251\u6cd5\uff0c\u90a3\u6562\u5f53\u6b64\u865a\u8a89\u3002\u201d\u5ba2\u5957\u4e00 \u756a\uff0c\u5c01\u7267\u91ce\u53c8\u95ee\u9053\uff1a\u201c\u4e0a\u5b98\u5927\u4eba\u7684\u5343\u91d1\u4e0e\u9601\u4e0b\u4efd\u5c5e\u5144\u59b9\uff0c\u8fd9\u51e0\u5e74\u6765\u5979\u5f88\u5f97\u5929\u540e\u5ba0\u4fe1\uff0c\u4e0d \u77e5\u5144\u53f0\u53ef\u6709\u89c1\u8fc7\u5979\u4e48\uff1f\u201d\u674e\u9038\u542c\u4ed6\u63d0\u8d77\u4e0a\u5b98\u5a49\u513f\uff0c\u5fc3\u4e2d\u4e00\u9635\u7ede\u75db\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u5979 \u867d\u7136\u4efd\u5c5e\u5144\u59b9\uff0c\u5982\u4eca\u5374\u662f\u5404\u8d70\u5404\u8def\uff0c\u9053\u4e0d\u540c\uff0c\u4e0d\u76f8\u4e3a\u8c0b\uff0c\u81ea\u4ece\u5979\u5165\u5bab\u4e4b\u540e\uff0c\u6211\u4ece\u6765\u6ca1\u6709 \u89c1\u8fc7\u5979\u3002\u201d\u674e\u9038\u8fd9\u756a\u8bdd\u51fa\u81ea\u5fc3\u4e2d\uff0c\u8bf4\u6765\u611f\u60c5\u751a\u89c1\u6fc0\u52a8\uff0c\u5c01\u7267\u91ce\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98 \u59d1\u5a18\u4e43\u662f\u4e00\u4ee3\u624d\u5973\uff0c\u53ef\u60dc\u5979\u4e0d\u660e\u5927\u4e49\uff0c\u6539\u987a\u4ec7\u4eba\uff0c\u96be\u602a\u4f60\u505a\u5144\u957f\u7684\u4f24\u5fc3\u3002\u201d \u3000\u3000\u4e00\u8def\u4e0a\u5c01\u795d\u5c61\u6b21\u7528\u8bf4\u8bdd\u5411\u674e\u9038\u5241\u63c9\uff0c\u674e\u9038\u63a9\u9970\u5f97\u5f88\u597d\uff0c\u8fd9\u4e24\u4eba\u867d\u662f\u6709\u70b9\u6000\u7591\uff0c\u5374\u4e5f \u77a7\u4e0d\u51fa\u4ec0\u4e48\u7834\u7efd\u3002\u508d\u665a\u65f6\u5206\uff0c\u5230\u4e86\u5580\u7eb1\u62c9\u5c14\u6cb3\u4e0b\u6e38\uff0c\u5c01\u7267\u91ce\u9053\uff1a\u201c\u8fd8\u6709\u65e5\u534a\u8def\u7a0b\uff0c\u4fbf\u53ef \u4ee5\u5230\u7a81\u53a5\u738b\u5ef7\uff0c\u4e0d\u5fc5\u6025\u6025\u8d76\u8def\u4e86\u3002\u201d\u5728\u6cb3\u8fb9\u5b89\u4e0b\u5e10\u5e55\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u5929\u8272\u521a\u9ed1\u3002 \u3000\u3000\u665a\u4e0a\u6708\u8272\u5f88\u597d\uff0c\u8349\u539f\u666f\u8272\u8ff7\u4eba\uff0c\u5927\u5bb6\u4fbf\u5728\u8349\u539f\u4e0a\u6f2b\u6b65\u95f2\u8c08\uff0c\u7a0b\u8fbe\u82cf\u4e0e\u5c01\u795d\u4e8c\u4eba\u4e00\u9053\uff0c \u674e\u9038\u4e0e\u5357\u5bab\u5c1a\u4e00\u9053\uff0c\u6e10\u6e10\u8fd9\u4e24\u6279\u4eba\u5206\u5f00\uff0c\u5f7c\u6b64\u90fd\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u8fd9\u4f4d\u7a0b\u8001\u5e2e\u4e3b\u4f3c\u4e4e\u751a\u662f\u591a\u7591\uff0c\u6628\u591c\u4ed6\u51e0\u4e4e\u7591\u5fc3\u90a3\u6697\u5668\u662f\u6211\u6253\u7684\u5462\u3002\u201d\u5357 \u5bab\u5c1a\u9053\uff1a\u201c\u4ed6\u5341\u51e0\u5e74\u6765\u88ab\u6b66\u5219\u5929\u6d3e\u4eba\u7f09\u6355\uff0c\u5728\u6c5f\u6e56\u4e0a\u51e0\u4e4e\u65e0\u5730\u5bb9\u8eab\uff0c\u4e5f\u96be\u602a\u4ed6\u591a\u7591\u5584\u8651\u3002 \u6211\u60f3\uff1a\u8981\u4e0d\u662f\u6211\u77e5\u9053\u4f60\u662f\u5510\u5ba4\u738b\u5b59\uff0c\u662f\u4e2a\u4e0e\u6b66\u5219\u5929\u8a93\u4e0d\u4e24\u7acb\u7684\u4eba\uff0c\u8fde\u6211\u4e5f\u4f1a\u5bf9\u4f60\u6000\u7591\u5462\u3002\u201d \u3000\u3000\u8c08\u4e86\u4e00\u4f1a\uff0c\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u65f6\u5019\u4e0d\u65e9\uff0c\u54b1\u4eec\u8be5\u56de\u53bb\u6b47\u606f\u4e86\u5427\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u96be\u5f97\u5982\u6b64 \u6708\u8272\uff0c\u6211\u5012\u672a\u6709\u7761\u610f\uff0c\u4f60\u7d2f\u4e86\u4f60\u5148\u6b47\u5427\uff1f\u201d\u5357\u5bab\u5c1a\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\u4f60\u662f\u96c5\u4eba\uff0c\u6211\u5374\u4e0d\u61c2\u6b23 \u8d4f\u4ec0\u4e48\u6708\u8272\uff0c\u597d\u5427\uff0c\u90a3\u6211\u5c31\u5148\u56de\u5e10\u84ec\u66ff\u4f60\u4eec\u6599\u7406\u5367\u5177\u3002\u201d \u3000\u3000\u674e\u9038\u72ec\u81ea\u5728\u8349\u539f\u6563\u6b65\uff0c\u5fc3\u4e8b\u5982\u6f6e\uff0c\u8d8a\u884c\u8d8a\u8fdc\uff0c\u8d70\u5230\u6cb3\u5cb8\u6751\u8fb9\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u4f4e\u58f0\u8bf4\u8bdd\uff0c \u6709\u4e2a\u4eba\u9053\uff1a\u201c\u7a0b\u5927\u54e5\uff0c\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u91cc\u9762\u6709\u4e2a\u6781\u5927\u7684\u79d8\u5bc6\uff01\u201d\u6b63\u662f\u5c01\u7267\u91ce\u7684\u58f0\u97f3\u3002\u674e \u9038\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4e14\u542c\u542c\u4ed6\u8bf4\u7684\u662f\u4ec0\u4e48\u79d8\u5bc6\u3002\u201d\u4f0f\u5728\u4e00\u4e2a\u6c99\u4e18\u540e\u9762\u5077\u542c\uff0c\u53ea\u542c\u5f97 \u7a0b\u8fbe\u82cf\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d\u5c01\u7267\u91ce\u9053\uff1a\u201c\u4f60\u9053\u8fd9\u6c5f\u5c71\u662f\u59d3\u6b66\u7684\u8fd8\u662f\u59d3\u674e\u7684\uff1f\u201d\u7a0b\u8fbe\u82cf \u9053\uff1a\u201c\u600e\u4e48\uff0c\u6211\u79bb\u5f00\u4e86\u51e0\u5e74\uff0c\u96be\u9053\u56fd\u4e2d\u53c8\u6709\u4e86\u4ec0\u4e48\u53d8\u5316\u4e48\uff1f\u201d \u3000\u3000\u5c01\u7267\u91ce\u9053\uff1a\u201c\u6b66\u5219\u5929\u63a5\u53d7\u72c4\u4ec1\u6770\u7684\u529d\u8c0f\uff0c\u5df2\u5185\u5b9a\u5c06\u5e1d\u4f4d\u4f20\u7ed9\u4ed6\u7684\u513f\u5b50\u5362\u9675\u738b\u674e\u663e\u4e86\u3002 \u6240\u4ee5\u8fd9\u6c5f\u5c71\u73b0\u5728\u662f\u59d3\u6b66\u7684\uff0c\u5c06\u6765\u5374\u8fd8\u662f\u59d3\u674e\u7684\u3002\u201d \u3000\u3000\u8fd9\u6d88\u606f\u674e\u9038\u65e9\u542c\u5f97\u957f\u5b59\u6cf0\u8bf4\u8fc7\uff0c\u4e0d\u4ee5\u4e3a\u5947\uff0c\u7a0b\u8fbe\u82cf\u5374\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u51b7\u7b11\u8bf4\u9053\uff1a \u201c\u6b66\u5219\u5929\u5f53\u771f\u662f\u8001\u7cca\u6d82\u4e86\uff0c\u5979\u5927\u7ea6\u4ee5\u4e3a\u513f\u5b50\u6bd4\u4f84\u5b50\u597d\u5427\uff1f\u5979\u4e5f\u4e0d\u60f3\uff0c\u5979\u662f\u4ece\u674e\u6c0f\u624b\u4e2d\u593a \u6765\u7684\u6c5f\u5c71\uff01\u8fd9\u5bf9\u4e8e\u674e\u5510\u738b\u5ba4\u4e43\u662f\u4e00\u4e2a\u5927\u5927\u7684\u803b\u8fb1\uff0c\u800c\u4e14\u88ab\u5979\u6740\u5bb3\u7684\u738b\u5b59\u8d35\u65cf\uff0c\u5148\u671d\u5927\u81e3\uff0c \u4e0d\u8ba1\u5176\u6570\uff0c\u5979\u7684\u4ec7\u5bb6\uff0c\u5c06\u6765\u5c31\u4e0d\u4f1a\u62a5\u590d\u5417\uff1f\u7eb5\u4f7f\u5979\u5f97\u4ee5\u4fdd\u5168\u9996\u7ea7\uff0c\u6b66\u6c0f\u5b50\u5f1f\u53ea\u6015\u96be\u514d\u8981 \u88ab\u65a9\u8349\u9664\u6839\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u662f\u4e0d\u662f\u4e3a\u4e86\u6015\u9760\u5c71\u5c06\u5012\uff0c\u6240\u4ee5\u60f3\u53e6\u6295\u660e\u4e3b\uff1f\u201d \u795d\u89c1\u7ae0\u7b11\u9053\uff1a\u201c\u7a0b\u5927\u54e5\uff0c\u6211\u8bf4\u4e00\u53e5\u4e0d\u6015\u4f60\u89c1\u602a\u7684\u8bdd\uff0c\u4f60\u5927\u7ea6\u4e5f\u4e0d\u662f\u5b58\u5fc3\u8981\u505a\u674e\u59d3\u7684\u5fe0\u81e3 \u5427\uff1f\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u98df\u8fc7\u5510\u671d\u7684\u4ff8\u7984\uff0c\u5f53\u7136\u4e0d\u5fc5\u4e3a\u5b83\u6548\u6b7b\u5c3d\u5fe0\uff0c\u4e0d\u8fc7\u6b66\u5219\u5929\u8feb\u5f97 \u6211\u65e0\u8def\u53ef\u8d70\uff0c\u5982\u679c\u8ba9\u6211\u6311\u9009\u7684\u8bdd\uff0c\u90a3\u6211\u8fd8\u662f\u62e5\u62a4\u59d3\u674e\u7684\u505a\u7687\u5e1d\u3002\u201d\u795d\u89c1\u7ae0\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9 \u5566\uff01\u603b\u4e4b\u4e0d\u7ba1\u8c01\u505a\u7687\u5e1d\uff0c\u59d3\u674e\u4e5f\u597d\uff0c\u59d3\u6b66\u4e5f\u597d\uff0c\u53ea\u8981\u4ed6\u4e0d\u4e0e\u6211\u4eec\u4f5c\u5bf9\u4fbf\u884c\uff0c\u82e5\u80fd\u7ed9\u6211\u4eec \u529f\u540d\u5bcc\u8d35\uff0c\u90a3\u66f4\u662f\u6700\u5999\u4e0d\u8fc7\uff01\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8bf4\u5230\u6211\u7684\u5fc3\u574e\u4e0a\u5566\u3002\u201d \u3000\u3000\u5c01\u7267\u91ce\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u4eec\u4e0d\u6015\u5bf9\u4f60\u8bf4\u4e86\uff0c\u6211\u4eec\u8fd9\u6b21\uff0c\u6b63\u662f\u5949\u4e86\u9b4f\u738b\u4e4b\u547d\u6765\u7684\u3002\u5c06\u6765 \u82e5\u662f\u7a81\u53a5\u5927\u5175\u6253\u8fdb\u5173\u4e2d\uff0c\u9b4f\u738b\u613f\u610f\u91cc\u5e94\u5916\u5408\uff01\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d\u5c01\u7267\u91ce\u9053\uff1a \u201c\u600e\u4e48\u4e0d\u771f\uff1f\u9b4f\u738b\u4ed6\u867d\u662f\u6b66\u5219\u5929\u7684\u4f84\u513f\uff0c\u4f46\u4ed6\u4e5f\u5f97\u4e3a\u4ed6\u81ea\u5df1\u7684\u5229\u5bb3\u7740\u60f3\u5440\uff01\u6b66\u5219\u5929\u4f20\u4f4d \u7ed9\u5979\u7684\u513f\u5b50\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u6307\u671b\uff1f\u6240\u4ee5\u53ea\u8981\u7a81\u53a5\u5927\u6c57\u7b54\u5e94\u8ba9\u4ed6\u505a\u4e2d\u56fd\u7684\u7687\u5e1d\uff0c\u4ed6\u53c8\u4f55\u8f9e\u5927 \u4e49\u706d\u4eb2\uff1f\u201d \u3000\u3000\u674e\u9038\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u7b97\u662f\u4ec0\u4e48\u5927\u4e49\uff1f\u8fd9\u7b80\u76f4\u662f\u732a\u72d7\u4e0d\u5982\uff01\u201d\u7a0b\u8fbe\u82cf\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u6b66\u5219\u5929\u8fd9\u56de\u53ef\u771f\u662f\u4f17\u53db\u4eb2\u79bb\u4e86\uff01\u54c8\uff0c\u54c8\uff0c\u6211\u771f\u662f\u6599\u60f3\u4e0d\u5230\uff0c\u539f\u6765\u4f60\u7adf\u662f\u6b66\u627f\u55e3 \u7684\u5bc6\u4f7f\u5462\uff01\u201d \u3000\u3000\u5c01\u7267\u91ce\u9053\uff1a\u201c\u73b0\u5728\u7a81\u53a5\u5927\u6c57\u5df2\u7ecf\u4e00\u53e3\u7b54\u5e94\uff0c\u5c31\u5f85\u7a81\u53a5\u7684\u5927\u5175\u6253\u8fdb\u5173\u4e2d\u4e86\u3002\u4f60\u90a3\u4e2a\u526f \u5e2e\u4e3b\u5357\u5bab\u5c1a\u662f\u8981\u6062\u590d\u5510\u5ba4\u7684\uff0c\u54b1\u4eec\u7684\u79d8\u5bc6\u53ef\u4e0d\u80fd\u8ba9\u4ed6\u77e5\u9053\u3002\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u5357\u5bab\u5c1a\u5bf9\u6211 \u7684\u8bdd\u4ece\u6765\u4e0d\u6562\u4e0d\u4f9d\uff0c\u4e0d\u8fc7\u4e3a\u4e86\u7a33\u5f53\u8d77\u89c1\uff0c\u4e5f\u8fd8\u662f\u7792\u7740\u4ed6\u597d\u3002\u201d\u5c01\u7267\u91ce\u9053\uff1a\u201c\u8fd8\u6709\u90a3\u4e2a\u4e0a \u5b98\u654f\u4e5f\u5f88\u662f\u53ef\u7591\uff01\u201d \u3000\u3000\u674e\u9038\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u542c\u5f97\u7a0b\u8fbe\u82cf\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u770b\u51fa\u4e86\u4ec0\u4e48\u53ef\u7591\u4e4b\u5904\u4e86\u3002\u201d\u5c01\u7267\u91ce \u9053\uff1a\u201c\u6211\u770b\u4ed6\u7684\u795e\u8272\u6c14\u5ea6\uff0c\u4e00\u70b9\u4e5f\u4e0d\u50cf\u6c5f\u6e56\u4eba\u7269\uff0c\u751a\u81f3\u4e5f\u4e0d\u50cf\u666e\u901a\u7684\u4eba\u3002\u4e0a\u5b98\u4eea\u7684\u5b50\u4f84 \u6211\u5927\u534a\u77e5\u9053\uff0c\u5374\u4e0d\u66fe\u542c\u8bf4\u8fc7\u6709\u8fd9\u6837\u7684\u4eba\u7269\u3002\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u5357\u5bab\u5c1a\u8bf4\u8fd9\u4eba\u662f\u4ed6\u7684\u4e49\u5144\uff0c \u96be\u9053\u4ed6\u9a97\u6211\u4e0d\u6210\uff1f\u201d\u5c01\u7267\u91ce\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u662f\u7591\u5fc3\u7f62\u4e86\uff0c\u603b\u4e4b\uff0c\u662f\u8981\u63d0\u9632\u4e9b\u597d\u3002\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u548c\u4ed6\u4eec\u8c08\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u6b66\u5219\u5929\u624b\u4e0b\u6709\u4ec0\u4e48\u9ad8\u4eba\u4e48\uff1f\u201d \u3000\u3000\u5c01\u7267\u91ce\u9053\uff1a\u201c\u4ee5\u524d\u672c\u6765\u6709\u6240\u8c13\u795e\u6b66\u8425\u4e09\u5927\u9ad8\u624b\uff0c\u5373\u662f\u897f\u95e8\u9738\u3001\u79e6\u582a\u3001\u5f20\u633a\u4e09\u4eba\u3002\u201d \u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u4eba\u4ee5\u524d\u90fd\u66fe\u7ecf\u548c\u6211\u4ea4\u8fc7\u624b\uff0c\u4ee5\u897f\u95e8\u9738\u7684\u529f\u592b\u6700\u5f3a\uff0c\u6211\u7ed9\u4ed6\u626b\u4e86\u4e00\u97ad\uff0c \u4ed6\u4e5f\u7ed9\u6211\u6572\u4e86\u4e00\u8bb0\u70df\u6597\uff0c\u7b97\u662f\u4e24\u4e0d\u8f93\u4e8f\u3002\u5176\u4ed6\u4e8c\u4eba\u867d\u4e5f\u4e0d\u9519\uff0c\u563f\uff0c\u563f\uff0c\u90a3\u4e0d\u8fc7\u662f\u548c\u6211\u7684 \u526f\u624b\u5357\u5bab\u5c1a\u4e0d\u76f8\u4e0a\u4e0b\u7f62\u4e86\u3002\u201d \u3000\u3000\u5c01\u7267\u91ce\u9053\uff1a\u201c\u73b0\u5728\u66f4\u4e0d\u884c\u5566\u3002\u516b\u5e74\u524d\u5728\u7ef7\u5c71\u4e00\u6218\uff0c\u5f20\u633a\u7ed9\u5929\u6076\u9053\u4eba\u6253\u6b7b\uff0c\u897f\u95e8\u9738\u4e5f \u7ed9\u6253\u4f24\uff0c\u529f\u592b\u5df2\u5927\u4e0d\u5982\u524d\u4e86\u3002\u201d\u7a0b\u8fbe\u82cf\u6000\u7591\u9053\uff1a\u201c\u7167\u4f60\u8fd9\u6837\u8bf4\u6765\uff0c\u96be\u9053\u6b66\u5219\u5929\u624b\u4e0b\uff0c\u7adf \u662f\u6ca1\u6709\u4ec0\u4e48\u80fd\u4eba\uff1f\u201d\u5c01\u7267\u91ce\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4f4d\u795e\u6b66\u8425\u7684\u603b\u7ba1\u674e\u660e\u4e4b\uff0c\u5185\u5916\u529f\u592b\u90fd\u5f88\u4e0d\u9519\u3002 \u4f46\u4ed6\u662f\u7edf\u5175\u7684\u5c06\u9886\uff0c\u4e0d\u4f1a\u5728\u6c5f\u6e56\u884c\u8d70\u7684\u3002\u201d \u3000\u3000\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u542c\u8bf4\u6b66\u5219\u5929\u6709\u4e2a\u4f84\u5973\uff0c\u53eb\u505a\u6b66\u7384\u971c\u7684\uff0c\u4e43\u662f\u4f18\u4e91\u795e\u5c3c\u7684\u63e9\u610f\u5f1f\u5b50\uff0c\u4ee5 \u524d\u66fe\u5728\u5ce8\u5d4b\u91d1\u9876\u6363\u6bc1\u8fc7\u82f1\u96c4\u5927\u4f1a\uff0c\u8fde\u8c37\u795e\u7fc1\u4e5f\u66fe\u8d25\u5728\u5979\u7684\u624b\u4e0b\uff0c\u59d4\u5b9e\u4e0d\u53ef\u8f7b\u89c6\u3002\u600e\u7684\u4e0d \u89c1\u4f60\u63d0\u8d77\u5979\uff1f\u201d\u201c\u5979\uff0c\u5979\u2026\u2026\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u5979\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u674e\u9038\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u5934\u8df3\u52a8\uff0c\u7ad6\u8d77\u8033\u6735\u6765\u542c\uff0c\u5c01\u7267\u91ce\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u4e00\u4ef6\u79d8\u5bc6\uff0c\u6211\u6b63\u8981 \u4e0e\u5927\u54e5\u5546\u8bae\u3002\u201d\u521a\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5f97\u7a0b\u8fbe\u82cf\u4e00\u58f0\u559d\u9053\uff1a\u201c\u8c01\u5728\u5916\u8fb9\uff1f\u201d \u3000\u3000\u674e\u9038\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53ea\u9053\u7a0b\u8fbe\u82cf\u5df2\u53d1\u73b0\u4e86\u4ed6\uff0c\u5fc3\u60f3\u4e8b\u5df2\u5982\u65af\uff0c\u53ea\u597d\u633a\u8eab\u51fa\u53bb\uff0c\u5fc3 \u5ff5\u65b9\u52a8\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u662f\u6211\uff01\u201d\u662f\u5357\u5bab\u5c1a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u559d\u9053\uff1a\u201c\u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u5357\u5bab\u5c1a\u9053\uff1a\u201c\u6211\u5728\u5e10\u4e2d\u542c\u5230\u4e86\u591c\u884c\u4eba\u7684\u58f0\u606f\uff0c \u8ffd\u8e2a\u4e0b\u53bb\uff0c\u4e0d\u60f3\u5728\u8fd9\u91cc\u9047\u89c1\u5927\u54e5\uff01\u201d\u7a0b\u8fbe\u82cf\u8df3\u4e86\u8d77\u6765\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u5411\u54ea\u4e2a\u65b9\u5411\u8d70\u4e86\uff1f\u201d \u5357\u5bab\u5c1a\u6307\u4e86\u4e00\u6307\uff0c\u6240\u6307\u7684\u65b9\u5411\u521a\u597d\u4e0e\u674e\u9038\u85cf\u533f\u7684\u65b9\u5411\u76f8\u53cd\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u9a6c\u4e0a\u53bb\u8ffd\u3002\u201d\u4e00\u884c\u4eba\u5411\u6cb3\u7684\u4e0a\u6e38\u8ffd\u53bb\u3002\u674e\u9038\u677e\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a \u201c\u5357\u5bab\u5c1a\u8bf4\u6709\u591c\u884c\u4eba\u524d\u6765\uff0c\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f\u53ea\u6015\u662f\u4ed6\u6709\u610f\u5c06\u7a0b\u8fbe\u82cf\u4ed6\u4eec\u5f15\u5f00\u7684\u3002\u201d \u3000\u3000\u674e\u9038\u56de\u5230\u5e10\u5e55\uff0c\u54ea\u91cc\u7761\u5f97\u7740\u89c9\uff1f\u7ffb\u6765\u8986\u53bb\uff0c\u601d\u60f3\u5c01\u795d\u4e8c\u4eba\u521a\u624d\u6240\u8bf4\u7684\u8bdd\uff0c\u53ef\u60dc\u5c01\u7267 \u91ce\u7684\u8bdd\u88ab\u5357\u5bab\u5c1a\u6253\u65ad\uff0c\u542c\u4ed6\u7684\u53e3\u6c14\uff0c\u4ed6\u5206\u660e\u5c31\u8981\u8bf4\u51fa\u4e00\u4ef6\u6709\u5173\u6b66\u7384\u971c\u7684\u4e8b\u60c5\uff0c\u800c\u4e14\u8fd8\u662f \u4e00\u4ef6\u79d8\u5bc6\uff01\u53ea\u4e0d\u77e5\u662f\u4ec0\u4e48\u79d8\u5bc6\uff1f \u3000\u3000\u60f3\u8d77\u4e86\u6b66\u7384\u971c\uff0c\u674e\u9038\u7684\u5fc3\u5934\uff0c\u5c31\u50cf\u4e00\u6c60\u9759\u6c34\u7a81\u7136\u88ab\u6295\u4e0b\u4e00\u5757\u77f3\u5934\uff0c\u52a8\u8361\u4e0d\u4f11\u3002\u968f\u5373 \u53c8\u60f3\u8d77\u4e86\u6b66\u627f\u55e3\u6076\u6bd2\u7684\u9634\u8c0b\uff0c\u4ed6\u8981\u505a\u7a81\u53a5\u7684\u5185\u5e94\uff0c\u8fd9\u4ef6\u4e8b\u53ef\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u82e5\u7ed9\u4ed6\u6210\u529f\uff0c \u79f0\u5fc3\u5982\u610f\u7684\u505a\u4e86\u7687\u5e1d\uff0c\u4e2d\u56fd\u56fa\u7136\u8981\u53d8\u6210\u4e86\u7a81\u53a5\u7684\u85e9\u5c5e\uff0c\u674e\u5510\u7684\u738b\u5ba4\u5b50\u5b59\u4e5f\u8981\u88ab\u4ed6\u6740\u5f97\u5bf8 \u8349\u4e0d\u7559\u3002\u53ef\u4ee5\u6599\u60f3\u5f97\u5230\uff0c\u4ed6\u7684\u624b\u6bb5\uff0c\u5b9a\u7136\u8981\u6bd4\u6b66\u5219\u5929\u66f4\u52a0\u6b8b\u9177\u767e\u500d\uff01\u60f3\u81f3\u6b64\u5904\uff0c\u674e\u9038\u6026 \u7136\u5fc3\u8df3\uff0c\u60f3\u9053\uff1a\u201c\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u4f3c\u4e4e\u5e94\u8be5\u56de\u53bb\u4e00\u6b21\u3002\u201d \u3000\u3000\u4f46\u968f\u5373\u53c8\u60f3\u5230\u957f\u5b89\u4e43\u662f\u4ed6\u4f24\u5fc3\u4e4b\u5730\uff0c\u57ce\u4e2d\u6709\u4ed6\u6240\u4e0d\u613f\u89c1\u7684\u4eba\uff0c\u800c\u4ed6\u4e5f\u66fe\u7ecf\u5bf9\u957f\u5b59\u58c1 \u53d1\u8fc7\u8a93\u613f\uff0c\u613f\u4e0e\u5979\u7ec8\u8001\u5f02\u56fd\uff0c\u57cb\u9aa8\u5929\u5c71\uff0c\u6c38\u4e0d\u56de\u53bb\u7684\u4e86\u3002\u4f46\u662f\u6b66\u627f\u55e3\u7684\u8fd9\u4ef6\u9634\u8c0b\u53c8\u5b9e\u5728 \u5173\u7cfb\u592a\u5927\uff0c\u5230\u5e95\u662f\u56de\u53bb\u8fd8\u662f\u4e0d\u56de\u53bb\u5462\uff1f\u674e\u9038\u7ffb\u6765\u8986\u53bb\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u5fc3\u4e2d\u96be\u51b3\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u7a0b\u8fbe\u82cf\u4ed6\u4eec\u5df2\u7ecf\u56de\u6765\uff0c\u5c01\u7267\u91ce\u3001\u795d\u89c1\u7ae0\u4e0e\u90a3\u4e2a\u7a81\u53a5\u6b66\u58eb\u5df4\u624e\u513f\u540c \u4f4f\u4e00\u4e2a\u5e10\u5e55\uff0c\u7a0b\u8fbe\u82cf\u4e0e\u5357\u5bab\u5c1a\u5219\u4ecd\u7136\u4f4f\u539f\u6765\u7684\u5e10\u5e55\uff0c\u674e\u9038\u542c\u5230\u4ed6\u4eec\u7684\u811a\u6b65\u58f0\u8d70\u8fdb\u7bf7\u5e10\uff0c \u6025\u5fd9\u8499\u5934\u88c5\u7761\u3002 \u3000\u3000\u53ea\u542c\u5f97\u7a0b\u8fbe\u82cf\u53fd\u53fd\u5495\u5495\u7684\u8bf4\u9053\uff1a\u201c\u6765\u65e0\u8e2a\u53bb\u65e0\u8ff9\u7684\uff0c\u96be\u9053\u53c8\u662f\u6628\u665a\u7684\u90a3\u4e00\u4e2a\u4eba\uff1f\u201d \u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u5f53\u771f\u6ca1\u6709\u77a7\u9519\uff1f\u201d\u5357\u5b98\u5c1a\u9053\uff1a\u201c\u6211\u7684\u786e\u662f\u77a7\u89c1\u4e00\u6761\u9ed1\u5f71\u5411\u90a3\u4e2a\u65b9\u5411\u8dd1\u7684\uff01\u201d \u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u597d\uff0c\u660e\u5929\u518d\u67e5\u770b\u4ed6\u7684\u8db3\u8ff9\u3002\u54fc\uff0c\u4e0a\u5b98\u654f\u8fd9\u4e2a\u5c0f\u5b50\u5012\u7761\u5f97\u5f88\u9163\u3002\u201d \u3000\u3000\u674e\u9038\u88c5\u5f97\u547c\u547c\u719f\u7761\uff0c\u5fc3\u4e2d\u5374\u5728\u60f3\u9053\uff1a\u201c\u516b\u6210\u662f\u5357\u5bab\u5c1a\u4e3a\u4e86\u66ff\u6211\u906e\u7792\u3002\u6545\u610f\u5c06\u4ed6\u4eec\u5f15 \u5f00\u7684\u4e86\uff0c\u7a0b\u8fbe\u82cf\u662f\u4e2a\u8001\u72d0\u72f8\uff0c\u542c\u4ed6\u8bf4\u8bdd\uff0c\u4f3c\u5df2\u8d77\u4e86\u7591\u5fc3\uff0c\u660e\u5929\u82e5\u7ed9\u4ed6\u67e5\u51fa\u6ca1\u6709\u5916\u4eba\u7684\u8db3 \u8ff9\uff0c\u8fd9\u600e\u4e48\u529e\uff1f\u4f46\u7a0b\u8fbe\u82cf\u4e0e\u4ed6\u4eec\u540c\u4e00\u5e10\u7bf7\uff0c\u4ed6\u53c8\u4e0d\u80fd\u591f\u4e0e\u5357\u5bab\u5c1a\u79c1\u81ea\u5546\u8bae\u3002\u201d \u3000\u3000\u8349\u539f\u7684\u6c14\u5019\u53d8\u5316\u5f88\u5927\uff0c\u4e0a\u534a\u591c\u90a3\u4e48\u597d\u7684\u6708\u8272\uff0c\u4e0b\u534a\u591c\u5374\u522e\u8d77\u98ce\u4e0b\u8d77\u96e8\u6765\uff0c\u674e\u9038\u5fc3\u4e2d \u6697\u559c\uff0c\u60f3\u9053\uff1a\u201c\u5e78\u597d\u6709\u8fd9\u573a\u5927\u96e8\uff0c\u8db3\u8ff9\u662f\u518d\u4e5f\u67e5\u4e0d\u51fa\u6765\u7684\u4e86\u3002\u201d\u4ed6\u53ef\u6ca1\u60f3\u5230\uff0c\u8fd8\u6709\u4e00\u4ef6 \u66f4\u51fa\u4eba\u610f\u6599\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u4e00\u65e9\u8d77\u6765\uff0c\u98ce\u96e8\u65e9\u5df2\u505c\u4e86\uff0c\u7a0b\u8fbe\u82cf\u6700\u5148\u8d70\u51fa\u7bf7\u5e10\uff0c\u5ffd\u542c\u4ed6\u4e00\u58f0\u60ca\u547c\uff0c\u674e\u9038\u4e0e\u5357\u5bab\u5c1a \u6025\u5fd9\u968f\u7740\u5954\u51fa\uff0c\u53ea\u89c1\u5c01\u795d\u4e8c\u4eba\u4e0e\u7a81\u53a5\u6b66\u58eb\u90a3\u5ea7\u5e10\u5e55\u7adf\u7136\u79fb\u5230\u4e86\u534a\u91cc\u4e4b\u5916\uff0c\u53d8\u6210\u4e86\u4e00\u5806\u7834 \u5e03\uff0c\u59d4\u5f03\u5730\u4e0a\uff0c\u5c01\u795d\u4e0e\u90a3\u4e2a\u7a81\u53a5\u6b66\u58eb\u7761\u5728\u6ce5\u6cde\u4e4b\u4e2d\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u53eb\u58f0\uff1a\u201c\u4e0d\u5999\uff01\u201d\u8bd5\u60f3\u5c01\u795d\u4e8c\u4eba\u4f55\u7b49\u6b66\u529f\uff0c\u7109\u6709\u88ab\u98ce\u5439\u8d70\u5e10\u7bf7\u4ecd\u672b\u89c9\u9192\u4e4b\u7406\uff1f \u4f55\u51b5\u6628\u591c\u7684\u98ce\u58f0\u867d\u5927\uff0c\u5374\u4e5f\u4e0d\u81f3\u4e8e\u5377\u8d70\u5e10\u5e55\u3002\u7a0b\u8fbe\u82cf\u6025\u5fd9\u4e0a\u524d\u67e5\u770b\uff0c\u8fd9\u4e09\u4e2a\u4eba\u679c\u7136\u662f\u7ed9 \u4eba\u70b9\u4e86\u6655\u7761\u7a74\uff0c\u7a0b\u8fbe\u82cf\u662f\u4e2a\u70b9\u7a74\u7684\u5927\u884c\u5bb6\uff0c\u7acb\u5373\u66ff\u4ed6\u4eec\u89e3\u6551\uff0c\u4e09\u4eba\u9192\u6765\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u90a3 \u4e2a\u7a81\u53a5\u6b66\u571f\u60ca\u7591\u4e0d\u5b9a\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5c01\u7267\u91ce\u82e6\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u5927\u7ea6\u662f\u7ed9\u4eba\u6697 \u7b97\u4e86\uff01\u201d\u90a3\u4e2a\u6b66\u58eb\u77a7\u4e86\u7a0b\u8fbe\u82cf\u4e00\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6697\u7b97\uff1f\u54a6\uff0c\u4f60\u4eec\u5374\u5b8c\u5168\u6ca1\u4e8b\u5440\uff01\u201d\u7a0b \u8fbe\u82cf\u6ee1\u9762\u901a\u7ea2\uff0c\u4ed6\u672a\u80fd\u53d1\u73b0\u6697\u7b97\u7684\u4eba\uff0c\u5df2\u662f\u5728\u7a81\u53a5\u6b66\u58eb\u9762\u524d\u5927\u5931\u9762\u5b50\uff0c\u66f4\u7cdf\u7cd5\u7684\u662f\uff0c\u90a3 \u4e2a\u6697\u7b97\u7684\u4eba\u6545\u610f\u653e\u8fc7\u4ed6\u4eec\uff0c\u7a81\u53a5\u6b66\u58eb\u96be\u514d\u4e0d\u8d77\u7591\u5fc3\u3002\u7a0b\u8fbe\u82cf\u60f3\u8d77\u8fd9\u4e2a\u795e\u79d8\u7684\u654c\u4eba\uff0c\u672c\u9886 \u5982\u6b64\u4e4b\u5f3a\uff0c\u65e2\u662f\u7f9e\u6127\uff0c\u53c8\u662f\u60ca\u614c\uff0c\u5357\u5bab\u5c1a\u5219\u5fc3\u4e2d\u6697\u559c\uff0c\u60f3\u9053\uff1a\u201c\u6211\u6628\u591c\u80e1\u4e71\u626f\u4e86\u4e00\u4e2a\u8c0e\uff0c \u60f3\u4e0d\u5230\u679c\u7136\u6709\u591c\u884c\u4eba\u5230\u6765\u3002\u201d \u3000\u3000\u5e78\u5728\u7a0b\u8fbe\u82cf\u4ed6\u4eec\u5230\u5e95\u662f\u6295\u5954\u7a81\u53a5\u5927\u6c57\u7684\u5ba2\u4eba\uff0c\u90a3\u6b66\u58eb\u4e0d\u4fbf\u8ffd\u7a76\uff0c\u800c\u79bb\u738b\u5ef7\u4e0d\u8fdc\uff0c\u4e0d \u5fc5\u5728\u8def\u4e0a\u5148\u95f9\u8d77\u6765\u3002\u4e8e\u662f\u4e00\u884c\u4eba\u6362\u8fc7\u8863\u88f3\uff0c\u7ee7\u7eed\u8d76\u8def\uff0c\u9ec4\u660f\u65f6\u5206\uff0c\u5230\u4e86\u738b\u57ce\uff0c\u6295\u5230\u5bbe\u9986\uff0c \u81ea\u7136\u6709\u4eba\u8fce\u63a5\u3002 \u3000\u3000\u51fa\u6765\u8fce\u63a5\u7684\u662f\u4e00\u4e2a\u7626\u957f\u7684\u6c49\u5b50\uff0c\u6de1\u6de1\u7684\u7709\u6bdb\uff0c\u51f8\u51fa\u7684\u5929\u5ead\uff0c\u76f8\u8c8c\u751a\u4e3a\u7279\u522b\uff0c\u5728\u4ed6\u540e \u9762\u5219\u8ddf\u7740\u4e00\u4e2a\u7a81\u53a5\u519b\u5b98\uff0c\u7a0b\u8fbe\u82cf\u4e00\u89c1\u5927\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u9633\u8001\u5f1f\uff0c\u65e9\u77e5\u4f60\u5728\u8fd9\u91cc\uff0c\u6211\u4e5f\u4e0d\u7528 \u8fd9\u4e48\u8d39\u4e8b\u4e86\uff0c\u76f4\u63a5\u6295\u5954\u4f60\u5c31\u884c\u5566\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u662f\u53e8\u7740\u5e08\u7236\u7684\u5149\uff0c\u5e78\u8499\u5927\u6c57\u4fe1\u4efb\uff0c \u53eb\u6211\u7ed9\u4ed6\u529e\u70b9\u5dee\u4e8b\u7f62\u4e86\u3002\u6211\u542c\u8bf4\u4f60\u65e9\u5df2\u5411\u9ed8\u8e51\u592a\u5e08\u8f93\u8bda\uff0c\u592a\u987a\u4e5f\u66fe\u5411\u5927\u6c57\u8bf4\u4e86\u3002\u5927\u6c57\u77e5 \u9053\u4f60\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u751a\u4e3a\u6b22\u559c\uff0c\u52a0\u4e0a\u53c8\u6709\u5929\u6076\u3001\u706d\u5ea6\u4e24\u4f4d\u524d\u8f88\u7ed9\u4f60\u8bf4\u597d\u8bdd\uff0c\u5c06\u6765\u662f\u5fc5\u91cd\u7528 \u7684\u4e86\u3002\u201d\u7a0b\u8fbe\u82cf\u9053\uff1a\u201c\u6211\u5e26\u4e86\u4e00\u70b9\u8584\u793c\u8981\u732e\u7ed9\u592a\u5e08\uff0c\u8fd8\u8bf7\u8001\u5f1f\u4ee3\u4e3a\u5148\u5bb9\u3002\u201d\u90a3\u7626\u957f\u7684\u6c49 \u5b50\u9053\uff1a\u201c\u4e0d\u5fc5\u7740\u5fd9\uff0c\u660e\u5929\u6211\u4e0e\u4f60\u4e00\u540c\u53bb\u62dc\u89c1\u592a\u5e08\u4fbf\u662f\u3002\u201d\u7a0b\u8fbe\u82cf\u9053\u4e86\u58f0\u8c22\uff0c\u53c8\u95ee\u9053\uff1a \u201c\u540e\u5929\u662f\u7a81\u53a5\u7684\u62d4\u9752\u4f73\u8282\uff0c\u542c\u8bf4\u5927\u6c57\u7684\u6b66\u58eb\u5927\u4f1a\u4fbf\u8981\u5728\u8fd9\u8282\u65e5\u53ec\u5f00\uff0c\u5c0a\u5e08\u7684\u6cd5\u9a7e\u4e0d\u77e5\u5230 \u4e86\u6ca1\u6709\uff1f\u201d\u90a3\u7626\u957f\u7684\u6c49\u5b50\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u5927\u7ea6\u8981\u4e34\u5230\u4f1a\u671f\u65b9\u80fd\u8d76\u5230\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u7626\u957f\u6c49\u5b50\u540d\u53eb\u9633\u592a\u534e\uff0c\u6b63\u662f\u767e\u4f18\u4e0a\u4eba\u7684\u9996\u5f92\uff0c\u4ed6\u5949\u4e86\u7a81\u53a5\u5927\u6c57\u4e4b\u547d\uff0c\u4e13\u95e8\u63a5\u5f85 \u4e2d\u56fd\u7684\u6b66\u58eb\uff0c\u6697\u4e2d\u8d1f\u4e86\u5ba1\u67e5\u3001\u7504\u522b\u7684\u4efb\u52a1\u3002 \u3000\u3000\u5f53\u4e0b\u9633\u592a\u534e\u5c06\u4ed6\u4eec\u63a5\u5165\u5bbe\u9986\uff0c\u8fd9\u95f4\u5bbe\u9986\u4f4f\u7684\u90fd\u662f\u4ece\u4e2d\u56fd\u6295\u5954\u6765\u7684\u4eba\uff0c\u5341\u4e4b\u516b\u4e5d\u8ba4\u5f97 \u7a0b\u8fbe\u82cf\uff0c\u4f46\u5374\u65e0\u4e00\u4eba\u8ba4\u5f97\u674e\u9038\u3002\u90a3\u4e9b\u4eba\u7eb7\u7eb7\u4e0a\u6765\u62db\u547c\u7a0b\u8fbe\u82cf\uff0c\u5bbe\u9986\u7684\u5927\u5385\u95f9\u54c4\u54c4\u7684\u50cf\u4e2a \u5e02\u96c6\u3002 \u3000\u3000\u674e\u9038\u5bf9\u8fd9\u4e9b\u4eba\u751a\u4e3a\u8ba8\u538c\uff0c\u72ec\u81ea\u8eb2\u5230\u4e00\u89d2\uff0c\u5ffd\u89c1\u9633\u592a\u534e\u4e0e\u5c01\u7267\u91ce\u8bf4\u4e86\u51e0\u53e5\u8bdd\u540e\uff0c\u9762\u4e0a \u5ffd\u7136\u9732\u51fa\u8be1\u5f02\u7684\u7b11\u5bb9\uff0c\u5411\u4ed6\u8d70\u6765\uff0c\u674e\u9038\u5fc3\u4e2d\u4e00\u51db\uff0c\u53ea\u89c1\u9633\u592a\u534e\u5411\u4ed6\u4f38\u51fa\u624b\u6765\uff0c\u8bf4\u9053\uff1a \u201c\u4e0a\u5b98\u5144\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u201d\u674e\u9038\u53ea\u5f97\u4f38\u624b\u4e0e\u4ed6\u76f8\u63e1\uff0c\u9661\u7136\u95f4\u5ffd\u89c9\u4e00\u80a1\u70ed\u529b\u4f20\u4e86\u8fc7\u6765\uff0c\u72b9 \u5982\u63e1\u7740\u4e86\u4e00\u5757\u70bd\u70ed\u7684\u706b\u70ad\u4e00\u822c\uff0c\u5e78\u800c\u674e\u9038\u5728\u5929\u5c71\u82e6\u7ec3\u516b\u5e74\uff0c\u5185\u529f\u5df2\u751a\u6709\u6839\u5e95\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c \u5c06\u624b\u7f29\u56de\uff0c\u8bf4\u9053\uff1a\u201c\u9633\u5927\u4eba\uff0c\u4f60\u592a\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u9633\u592a\u534e\u89c1\u4ed6\u795e\u8272\u81ea\u5982\uff0c\u7591\u4e91\u5927\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u672a\u8bf7\u6559\u4e0a\u5b98\u5144\u5c5e\u4e8e\u4f55\u5b97\u4f55\u6d3e\uff0c\u5c0a\u5e08\u662f \u8c01\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u53ea\u662f\u80e1\u4e71\u5b66\u8fc7\u4e00\u4e9b\u529f\u592b\uff0c\u8ddf\u7684\u662f\u5bb6\u7236\u7684\u62a4\u9662\u6559\u5e08\uff0c\u8c08\u4e0d\u4e0a\u662f\u4f55\u5b97\u6d3e\u3002\u201d \u9633\u592a\u534e\u51b7\u7b11\u9053\uff1a\u201c\u543e\u5144\u4f55\u5fc5\u8fc7\u8c26\uff0c\u770b\u543e\u5144\u8fd9\u8eab\u7cbe\u7eaf\u7684\u5185\u529f\uff0c\u4f3c\u4e4e\u662f\u5ce8\u5d4b\u7684\u5fc3\u6cd5\uff0c\u4e0d\u77e5\u957f \u5b59\u8001\u5148\u751f\u4e0e\u5c09\u8fdf\u8001\u5148\u751f\u4e0e\u543e\u5144\u662f\u600e\u4e48\u4e2a\u79f0\u547c\uff1f\u201d \u3000\u3000\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u767e\u4f18\u4e0a\u4eba\u8fd9\u4e2a\u5f92\u5f1f\u679c\u7136\u5389\u5bb3\uff0c\u53ea\u662f\u4e0e\u4ed6\u63e1\u4e00\u63e1\u624b\uff0c\u4ed6\u5c45\u7136 \u5c31\u770b\u51fa\u4e86\u6211\u7684\u6b66\u529f\u5bb6\u6570\u6765\u3002\u518d\u7ed9\u4ed6\u76d8\u95ee\uff0c\u5b9a\u7136\u88ab\u4ed6\u8bc6\u7834\u6211\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5927\u5385\u91cc\u5ffd\u7136\u9e26\u96c0\u65e0\u58f0\uff0c\u4f46\u4ec5\u4ec5\u662f\u9759\u4e86\u7247\u523b\uff0c\u63a5\u7740\u5c31\u5f02\u53e3\u540c\u58f0\u7684\u53eb\u9053\uff1a \u201c\u8c37\u8001\u76df\u4e3b\uff0c\u600e\u4e48\u4f60\u4e5f\u6765\u4e86\u5440\uff01\u201d\u674e\u9038\u5b9a\u6674\u4e00\u770b\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u6765\u7684\u4e0d\u662f \u522b\u4eba\uff0c\u6b63\u662f\u4e0e\u81ea\u5df1\u9614\u522b\u4e86\u5c06\u8fd1\u5341\u5e74\u7684\u8c37\u795e\u7fc1\uff01 \u3000\u3000\u8c37\u795e\u7fc1\u4e43\u662f\u5341\u5e74\u4e4b\u524d\u4e2d\u539f\u4e00\u6b66\u6797\u76df\u4e3b\uff0c\u8bba\u4ed6\u7684\u8eab\u4efd\uff0c\u4e0e\u5929\u6076\u9053\u4eba\u3001\u706d\u5ea6\u795e\u541b\u540c\u662f\u4e00 \u8f88\uff0c\u8bba\u6b66\u6797\u7684\u5730\u4f4d\uff0c\u5219\u66f4\u5728\u4ed6\u4eec\u4e4b\u4e0a\u3002\u5982\u4eca\u7a81\u7136\u6765\u5230\u6b64\u95f4\uff0c\u4e8b\u524d\u53c8\u6ca1\u900f\u51fa\u534a\u70b9\u98ce\u58f0\uff0c\u7109 \u80fd\u4e0d\u4ee4\u4eba\u60ca\u5f02\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u738b\u5ef7\u76db\u4f1a\u901e\u5947\u80fd \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u738b\u5ef7\u76db\u4f1a\u901e\u5947\u80fd \u3000\u3000\u9633\u592a\u534e\u6025\u5fd9\u8d70\u4e0a\u524d\u53bb\u8fce\u63a5\uff0c\u8138\u4e0a\u5806\u6ee1\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u8001\u524d\u8f88\uff0c\u662f\u4ec0\u4e48\u98ce\u628a\u4f60\u5439\u6765 \u4e86\uff1f\u201d\u8981\u77e5\u8c37\u795e\u7fc1\u867d\u7136\u4e5f\u662f\u53cd\u5bf9\u6b66\u5219\u5929\u7684\u4eba\uff0c\u4f46\u4ed6\u4e00\u5411\u9119\u5c51\u57df\u5916\u4e09\u51f6\u7684\u4e3a\u4eba\uff0c\u867d\u672b\u516c\u5f00 \u4e0e\u4ed6\u4eec\u51b3\u88c2\uff0c\u5374\u662f\u751a\u5c11\u5f80\u6765\u3002\u5982\u4eca\u4e0d\u8bf7\u81ea\u5230\uff0c\u600e\u4e0d\u53eb\u9633\u592a\u534e\u60ca\u559c\uff1f\u5fc3\u9053\uff1a\u201c\u5230\u4e86\u8fd9\u6837\u5927 \u6709\u6765\u5934\u7684\u4eba\u7269\uff0c\u6211\u5e08\u7236\u8138\u4e0a\u4e5f\u6709\u5149\u5f69\uff01\u201d \u3000\u3000\u8c37\u795e\u7fc1\u7b11\u9053\uff1b\u201c\u6211\u542c\u8bf4\u767e\u5fe7\u4e0a\u4eba\u5c31\u8981\u8363\u4efb\u56fd\u5e08\uff0c\u6211\u662f\u7279\u5730\u6765\u7ed9\u4ed6\u8d3a\u559c\u7684\u5440\uff01\u201d\u9633\u592a \u534e\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u5934\u513f\u7684\u6d88\u606f\u5012\u771f\u7075\uff01\u201d\u6025\u5fd9\u606d\u606d\u656c\u656c\u7684\u8bf4\u9053\uff1a\u201c\u5bb6\u5e08\u5c1a\u672a\u6765 \u5230\uff0c\u5929\u6076\u3001\u706d\u5ea6\u4e24\u4f4d\u5e08\u53d4\u73b0\u5728\u5927\u6c57\u5bab\u4e2d\uff0c\u5f85\u5f1f\u5b50\u5373\u523b\u53bb\u7980\u62a5\u4ed6\u4eec\uff0c\u8bf7\u8c37\u8001\u524d\u8f88\u5230\u5bab\u4e2d\u5b89 \u6b47\u3002\u201d\u539f\u6765\u5bbe\u9986\u6240\u62db\u5f85\u7684\u662f\u6b21\u4e00\u7b49\u7684\u4eba\u7269\uff0c\u90a3\u4e9b\u9876\u5c16\u7684\u4eba\u7269\uff0c\u5219\u65e9\u5df2\u7531\u5927\u6c57\u63a5\u4eba\u5bab\u4e2d\uff0c \u5f85\u4ee5\u4e0a\u5bbe\u4e4b\u793c\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u6446\u6446\u624b\u9053\uff1a\u201c\u4e0d\u5fc5\uff0c\u4e0d\u5fc5\uff01\u8fd9\u91cc\u719f\u4eba\u591a\uff0c\u6211\u613f\u610f\u4f4f\u5728\u8fd9\u91cc\u3002\u201d\u773c\u5149\u73af\u626b\u5168\u573a\u3002 \u4e00\u773c\u77a5\u89c1\u674e\u9038\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5ffd\u7136\u5411\u4ed6\u8d70\u6765\u3002 \u3000\u3000\u674e\u9038\u6b63\u5728\u60ca\u7591\u4e0d\u5b9a\uff0c\u5fc3\u60f3\uff1a\u201c\u8c37\u795e\u7fc1\u786e\u662f\u4e00\u5fc3\u60f3\u6062\u590d\u5510\u5ba4\u7684\u4eba\uff0c\u4f46\u4ed6\u4e5f\u662f\u4e2a\u6709\u89c1\u8bc6 \u7684\u4eba\uff0c\u5374\u600e\u7684\u4e5f\u6548\u57df\u5916\u4e09\u51f6\u6240\u4e3a\uff0c\u6765\u6b64\u6295\u5954\u7a81\u53a5\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u8c37\u795e\u7fc1\u5df2\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff0c \u62f1\u624b\u8bf4\u9053\uff1a\u201c\u597d\u4e45\u4e0d\u89c1\u9762\u5440\uff01\u201d\u674e\u9038\u867d\u5df2\u6539\u5bb9\u6613\u8c8c\uff0c\u60f3\u4e0d\u5230\u8fd8\u662f\u7ed9\u4ed6\u770b\u4e86\u51fa\u6765\uff0c\u6025\u5fd9\u8bf4 \u9053\uff1a\u201c\u665a\u8f88\u4e0a\u5b98\u654f\u8c12\u89c1\u8c37\u8001\u76df\u4e3b\u3002\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u793c\u3002\u201d\u4f38\u624b\u4e0e\u4ed6\u62cd\u63e1\uff0c\u5374\u4ee5\u6781 \u8fc5\u6377\u7684\u624b\u6cd5\u5728\u4ed6\u638c\u5fc3\u5199\u9053\uff1a\u201c\u4e00\u5207\u6211\u5168\u77e5\u4e86\uff01\u201d \u3000\u3000\u9633\u592a\u534e\u9053\uff1a\u201c\u539f\u6765\u4e24\u4f4d\u662f\u8ba4\u8bc6\u7684\uff1f\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u4e0a\u5b98\u8001\u4e03\u5728\u8941\u8913\u4e4b\u4e2d\u6211\u5df2\u8ba4\u8bc6\u4ed6 \u4e86\uff0c\u4ed6\u6027\u559c\u4e60\u6b66\uff0c\u8001\u673d\u8fd8\u66fe\u548c\u4ed6\u5207\u78cb\u8fc7\u5251\u6cd5\u5462\uff01\u201d\u9633\u592a\u534e\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u8fd9 \u5c0f\u5b50\u61c2\u5f97\u6b63\u5b97\u7684\u5185\u529f\u3002\u201d\u8981\u77e5\u8c37\u795e\u7fc1\u672c\u6765\u662f\u5ce8\u5d4b\u6d3e\u51fa\u8eab\uff0c\u4e0e\u957f\u5b59\u5747\u91cf\u3001\u5c09\u8fdf\u70af\u90fd\u662f\u77e5\u4ea4\uff0c \u674e\u9038\u65e2\u7136\u81ea\u5c0f\u4fbf\u8ba4\u8bc6\u4ed6\uff0c\u90a3\u4e48\u4ece\u4ed6\u90a3\u513f\u83b7\u5f97\u7528\u5fc3\u6cd5\uff0c\u4e5f\u5c31\u4e0d\u8db3\u5947\u602a\u4e86\uff0c\u9633\u592a\u534e\u5982\u6b64\u4e00\u60f3\uff0c \u7591\u5fc3\u6e10\u606f\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u7f20\u4e03\u5939\u516b\u7684\u4fe1\u53e3\u80e1\u626f\uff0c\u4e0e\u674e\u9038\u95f2\u8c08\uff0c\u4e00\u9762\u4f63\u62c9\u7740\u4ed6\u7684\u624b\u4e0d\u653e\u3002\u5ffd\u53c8\u5728\u4ed6\u638c\u5fc3 \u5199\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u662f\u6765\u6295\u5954\u7a81\u53a5\u7684\u5417\uff1f\u201d\u674e\u9038\u5fc3\u4e2d\u6697\u559c\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u8fd9\u6837\u95ee\uff0c\u4ed6\u5f53\u7136\u4e5f \u4e0d\u662f\u6765\u6295\u5954\u7a81\u53a5\u7684\u4e86\u3002\u201d\u4fbf\u5728\u8c37\u795e\u7fc1\u638c\u5fc3\u5199\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\u3002\u201d\u8c37\u795e\u7fc1\u5c55\u7709\u4e00\u7b11\uff0c\u8fd9\u624d \u653e\u5f00\u4e86\u4ed6\uff0c\u8f6c\u4e0e\u5176\u4ed6\u719f\u4eba\u642d\u8bdd\u3002 \u3000\u3000\u674e\u9038\u53c8\u60ca\u53c8\u559c\uff0c\u53c8\u662f\u731c\u7591\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u90a3\u6697\u7b97\u7a0b\u8fbe\u82cf\u7684\u5c31\u662f\u4ed6\u4e86\uff1f\u53ef\u662f\u6211\u5374\u4e0d\u66fe \u542c\u8bf4\u4ed6\u7ec3\u8fc7\u6885\u82b1\u9488\u6253\u7a74\u7684\u7edd\u6280\u5440\u3002\u800c\u4e14\u90a3\u4e2a\u4eba\u7684\u6b66\u529f\u4f3c\u4e4e\u6bd4\u4ed6\u8fd8\u9ad8\u3002\u4f46\u4e0d\u662f\u4ed6\uff0c\u53c8\u662f\u8c01\uff1f \u83ab\u975e\u662f\u4ed6\u5728\u8fd9\u5341\u5e74\u4e2d\u53c8\u7ec3\u6210\u4e86\u4ec0\u4e48\u7edd\u6280\uff1f\u201d\u53ef\u60dc\u4eba\u591a\u53e3\u6742\uff0c\u674e\u9038\u6839\u672c\u5c31\u6ca1\u6709\u673a\u4f1a\u518d\u53bb\u95ee \u8c37\u795e\u7fc1\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u7a0b\u8fbe\u82cf\u672c\u6765\u8981\u5e26\u5357\u5bab\u5c1a\u4e0e\u674e\u9038\u6765\u62dc\u89c1\u9ed8\u8e51\u592a\u5e08\uff08\u76f8\u5f53\u4e8e\u4e2d\u56fd\u7684\u5bb0\u76f8\uff09\u7684\uff0c\u4f46 \u7a81\u53a5\u5927\u6c57\u4e34\u65f6\u6d3e\u4eba\u901a\u77e5\uff0c\u8bf4\u662f\u4eca\u65e5\u4e2d\u65f6\uff0c\u5927\u6c57\u5728\u5bab\u4e2d\u8d50\u5bb4\uff0c\u8d50\u5bb4\u4e4b\u540e\uff0c\u624d\u6b63\u5f0f\u5f00\u59cb\u6b66\u58eb \u5927\u4f1a\u3002\u6709\u6d88\u606f\u7075\u901a\u7684\u7a81\u53a5\u6b66\u571f\u544a\u8bc9\u4ed6\u4eec\uff0c\u539f\u6765\u662f\u7a81\u53a5\u5927\u6c57\u8fce\u5a36\u7684\u65b0\u738b\u5983\u5230\u4e86\uff0c\u542c\u8bf4\u8fd9\u4f4d \u65b0\u738b\u5983\u662f\u963f\u5c14\u6cf0\u5c71\u5357\u9762\u4e00\u4e2a\u5c0f\u56fd\u7684\u516c\u4e3b\uff0c\u751f\u5f97\u7f8e\u8c8c\u975e\u5e38\u3002\u65e9\u5df2\u8273\u540d\u8fdc\u64ad\u3002\u6240\u4ee5\u5927\u6c57\u4e0d\u60dc \u91d1\u94f6\u91cd\u5bbe\uff0c\u7279\u6d3e\u4e13\u4f7f\u5c06\u5979\u63a5\u6765\u3002\u5927\u6c57\u6700\u8fd1\u6709\u4e24\u4ef6\u5f97\u610f\u7684\u4e8b\u60c5\uff0c\u4e00\u662f\u53ec\u5f00\u6b66\u58eb\u5927\u4f1a\uff0c\u4e00\u662f \u8fce\u5a36\u65b0\u738b\u5983\u3002\u56e0\u6b64\u4eca\u65e5\u5728\u5bab\u4e2d\u76db\u8bbe\u559c\u7b75\uff0c\u62db\u5f85\u5404\u56fd\u6b66\u58eb\uff0c\u51c6\u5907\u5411\u5bbe\u5ba2\u5927\u5927\u5938\u8000\u4e00\u756a\u3002\u636e \u90a3\u4e2a\u7a81\u53a5\u6b66\u58eb\u8bf4\uff0c\u738b\u5983\u4e5f\u8bb8\u4f1a\u51fa\u6765\u5411\u5bbe\u5ba2\u656c\u9152\u559d\u3002 \u3000\u3000\u5927\u6c57\u5728\u738b\u5ef6\u8e22\u5bb4\uff0c\u88ab\u9080\u8bf7\u7684\uff0c\u90fd\u611f\u5230\u5149\u8363\uff0c\u5c24\u5176\u662f\u542c\u5f97\u7a81\u53a5\u6b66\u58eb\u5c06\u65b0\u738b\u5983\u8bf4\u5f97\u90a3\u6837 \u7f8e\u8c8c\uff0c\u66f4\u662f\u4f7f\u5f97\u5927\u5bb6\u90fd\u60f3\u53bb\u770b\u3002\u53ea\u6709\u674e\u9038\u542c\u8fc7\u4fbf\u7b97\uff0c\u5bf9\u4f17\u4eba\u8d76\u7740\u53bb\u4e0d\u4ec5\u4e0d\u9ad8\u5174\uff0c\u5fc3\u4e2d\u5e76 \u4e14\u611f\u5230\u618e\u6076\u3002 \u3000\u3000\u5230\u4e86\u5348\u95f4\uff0c\u5404\u56fd\u6b66\u58eb\u4e91\u96c6\u5bab\u4e2d\uff0c\u90a3\u5ea7\u5bab\u6bbf\u6b63\u5728\u5fa1\u82d1\u5f53\u4e2d\uff0c\u5fa1\u82d1\u4e2d\u5b88\u536b\u7684\u6b66\u58eb\u6797\u7acb\uff0c \u4e00\u6d3e\u68ee\u4e25\u7684\u6c14\u8c61\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u5230\u6765\u7684\u6d88\u606f\u65e9\u5df2\u6709\u4eba\u62a5\u544a\u4e86\u5929\u6076\u9053\u4eba\uff0c\u5927\u6c57\u4e5f\u5df2\u77e5\u9053\u4e86\u4ed6\u7684\u8eab\u4efd\uff0c\u4e00\u8fdb\u5bab\u4e2d\uff0c \u5929\u6076\u706d\u795e\u4e8c\u4eba\u4fbf\u5c06\u4ed6\u8bf7\u4e0a\u4e0a\u5ea7\uff0c\u5e76\u8c12\u89c1\u5927\u6c57\uff0c\u7a0b\u8fbe\u82cf\u8eab\u4efd\u8f83\u4f4e\uff0c\u5219\u9633\u592a\u534e\u966a\u540c\uff0c\u5e2d\u6b21\u4e5f \u6392\u5728\u540e\u9762\u3002\u674e\u9038\u4e0e\u5357\u5b98\u5c1a\u7b49\u4eba\u7684\u5e2d\u6b21\u5219\u6392\u5230\u4e09\u5341\u4ee5\u5916\uff0c\u9760\u8fd1\u5927\u95e8\uff0c\u8fd8\u6709\u51e0\u5341\u5e2d\u8bbe\u5728\u5bab\u95e8 \u5916\u7684\u8349\u5730\u4e0a\uff0c\u90a3\u4e9b\u4eba\u5219\u8fde\u5927\u6c57\u7684\u989c\u8272\u4e5f\u4e0d\u80fd\u201c\u77bb\u4ef0\u201d\u4e86\u3002 \u3000\u3000\u674e\u9038\u62ac\u5934\u770b\uff0c\u4f46\u89c1\u7a81\u53a5\u5927\u6c57\u9ad8\u9ad8\u5728\u4e0a\uff0c\u76f8\u8c8c\u751a\u4e3a\u5a01\u6b66\uff0c\u4f46\u770b\u6765\u6700\u5c11\u4e5f\u6709\u4e94\u5341\u591a\u5c81\u4e86\u3002 \u65b0\u738b\u5983\u8fd8\u6ca1\u6709\u51fa\u6765\uff0c\u674e\u9038\u60f3\u8d77\u90a3\u6b66\u58eb\u6240\u8bf4\uff0c\u65b0\u738b\u5983\u4e0d\u8fc7\u662f\u4e8c\u5341\u5de6\u53f3\u7684\u5c11\u5973\uff0c\u5fc3\u9053\uff1a\u201c\u4e24 \u4eba\u5e74\u7eaa\u76f8\u5dee\u4e00\u534a\u6709\u591a\uff0c\u65b0\u738b\u5983\u82e5\u7136\u771f\u50cf\u5979\u8bf4\u5f97\u90a3\u6837\u7f8e\u8c8c\u7684\u8bdd\uff0c\u5c82\u4e0d\u662f\u7cdf\u8e4b\u4e86\u5979\uff1f\u201d\u968f\u5373 \u53c8\u5728\u5fc3\u4e2d\u81ea\u7b11\uff0c\u505a\u4e86\u7687\u5e1d\u7684\u4eba\uff0c\u8c01\u4e0d\u662f\u4e09\u5bab\u516d\u9662\uff0c\u5983\u5ad4\u76c8\u5ead\uff0c\u90a3\u601c\u60dc\u5f97\u8fd9\u4e48\u591a\uff1f\u518d\u4e00\u770b\uff0c \u7a0b\u8fbe\u82cf\u6b63\u7531\u9633\u592a\u534e\u966a\u540c\u5411\u9ed8\u55d3\u592a\u5e08\u732e\u5a9a\uff0c\u867d\u7136\u542c\u4e0d\u5230\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4f46\u89c1\u4ed6\u6253\u8eac\u4f5c\u63d6\u7684 \u6837\u5b50\uff0c\u5374\u5b9e\u5728\u611f\u5230\u4e00\u9635\u6076\u5fc3\uff0c\u4fbf\u628a\u773c\u5149\u79fb\u5f00\uff0c\u4e0d\u613f\u518d\u770b\u3002 \u3000\u3000\u4f17\u4eba\u521a\u521a\u63a5\u7740\u6240\u6392\u7684\u5e2d\u6b21\u5750\u597d\uff0c\u5ffd\u542c\u5f97\u6709\u5406\u559d\u7684\u58f0\u97f3\uff0c\u674e\u9038\u62ac\u5934\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u5fa1 \u82d1\u4e2d\u95ef\u8fdb\u4e86\u4e00\u4e2a\u6c49\u5b50\uff0c\u7ea6\u83ab\u4e94\u5341\u5c81\u6765\u5c81\uff0c\u7a7f\u7740\u4e00\u4ef6\u892a\u8272\u7684\u957f\u886b\uff0c\u5934\u4e0a\u6234\u4e00\u9876\u6c61\u65e7\u7684\u65b9\u5dfe\uff0c \u6d3b\u50cf\u4e00\u4e2a\u79d1\u573a\u5c61\u8bd5\u4e0d\u7b2c\u7684\u843d\u626d\u4e66\u751f\uff0c\u75af\u75af\u766b\u766b\u7684\u6837\u5b50\uff0c\u6709\u4e94\u516d\u4e2a\u7a81\u53a5\u6b66\u58eb\u5927\u58f0\u5406\u559d\uff0c\u5411 \u4ed6\u8ffd\u6765\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u4ed6\u5f53\u7136\u4e0d\u662f\u5f97\u5230\u5927\u6c57\u9080\u8bf7\u7684\u5bbe\u5ba2\u4e86\u3002\u6240\u6709\u8d74\u5bb4\u7684\u6b66\u58eb\u90fd\u5927\u4e3a\u60ca\u8be7\uff0c \u8bd5\u60f3\u5927\u6c57\u7684\u7687\u5bab\uff0c\u9632\u536b\u4f55\u7b49\u68ee\u4e25\uff0c\u7adf\u6709\u602a\u5ba2\u95ef\u4e86\u8fdb\u6765\uff0c\u8fd9\u4e8b\u60c5\u5f53\u771f\u4e0d\u53ef\u601d\u8bae\uff0c\u800c\u8fd9\u4eba\u80c6 \u91cf\u4e4b\u5927\uff0c\u66f4\u662f\u60ca\u4e16\u9a87\u4fd7\uff01 \u3000\u3000\u6643\u773c\u4e4b\u95f4\uff0c\u4f46\u89c1\u90a3\u4e2a\u602a\u5ba2\u5df2\u95ef\u5230\u95e8\u5916\u7684\u90a3\u5757\u8349\u5730\uff0c\u8349\u5730\u4e0a\u6392\u6709\u51e0\u5f20\u684c\u7b75\u5e2d\uff0c\u5e2d\u4e0a\u7684 \u5bbe\u5ba2\u7eb7\u7eb7\u7ad9\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u6b66\u58eb\u4e3e\u6b65\u5982\u98de\uff0c\u8ffd\u5230\u4e86\u4ed6\u7684\u80cc\u540e\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u8fd8\u4e0d\u7ad9\u4f4f\uff01\u201d \u63d0\u8d77\u5927\u5200\uff0c\u4e00\u5200\u5c31\u5411\u4ed6\u8111\u540e\u5288\u53bb\uff01 \u3000\u3000\u90a3\u602a\u5ba2\u4f3c\u662f\u7ed9\u4ed6\u8ffd\u5f97\u53c8\u614c\u53c8\u6025\uff0c\u5ffd\u5730\u4e00\u8db3\u8e0f\u7a7a\uff0c\u80cc\u810a\u671d\u5929\u7684\u4ec6\u5012\u5730\u4e0a\u3002\u8fd9\u65f6\u90a3\u4e2a\u6b66 \u58eb\u7684\u5927\u5200\u521a\u521a\u65a9\u4e0b\uff0c\u674e\u9038\u5fc3\u4e2d\u6b63\u5728\u5403\u60ca\uff0c\u4f46\u542c\u5f97\u90a3\u602a\u5ba2\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u54ce\u54df\uff0c\u4e0d\u597d\uff01\u201d\u53ea \u89c1\u4ed6\u7684\u8eab\u5f62\u5728\u5373\u5c06\u5012\u5730\u4e4b\u9645\uff0c\u5ffd\u5730\u53f3\u8db3\u5411\u540e\u4e00\u8e22\uff0c\u201c\u556a\u5566\u201d\u4e00\u58f0\uff0c\u4e00\u53ea\u978b\u5b50\u98de\u4e86\u8d77\u6765\uff0c \u6070\u597d\u6253\u4e2d\u90a3\u4e2a\u6b66\u571f\u7684\u624b\u8155\uff0c\u6b66\u58eb\u7684\u5927\u5200\u8131\u624b\u98de\u51fa\uff0c\u90a3\u602a\u5ba2\u5728\u5730\u4e0a\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u500f\u7684\u8df3 \u8d77\uff0c\u63a5\u4e86\u90a3\u53ea\u4ece\u534a\u7a7a\u8dcc\u843d\u7684\u978b\u5b50\uff0c\u6765\u4e0d\u53ca\u518d\u884c\u7a7f\u4e0a\uff0c\u62fe\u7740\u978b\u5b50\uff0c\u53c8\u6025\u5fd9\u9003\u547d\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u6ee1\u5802\u5bbe\u5ba2\uff0c\u7686\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8bd5\u60f3\u90a3\u6b66\u571f\u5927\u5200\u65a9\u4e0b\u4e4b\u52bf\u662f\u4f55\u7b49\u521a\u731b\uff0c\u5374\u88ab\u4ed6 \u98de\u8d77\u4e86\u4e00\u53ea\u7834\u978b\uff0c\u5927\u5200\u4fbf\u8131\u624b\u98de\u4e0a\u4e86\u534a\u5929\uff0c\u8fd9\u7b49\u529f\u592b\uff0c\u5f53\u771f\u662f\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\u3002 \u3000\u3000\u6df7\u4e71\u4e2d\u4f46\u89c1\u5929\u6076\u9053\u4eba\u4e0e\u8c37\u795e\u7fc1\u53cc\u53cc\u5954\u51fa\uff0c\u8c37\u795e\u7fc1\u53eb\u9053\uff1a\u201c\u8001\u7b26\uff0c\u8001\u7b26\uff0c\u8fd9\u91cc\u662f\u5927\u6c57 \u5bab\u5ef7\uff0c\u4f60\u600e\u4e48\u6076\u4f5c\u5267\u6765\u4e86\uff1f\u201d\u5929\u6076\u9053\u4eba\u5219\u9053\uff1a\u201c\u662f\u5929\u5c71\u7b26\u8001\u5148\u751f\u5417\uff1f\u96be\u5f97\uff0c\u96be\u5f97\uff01\u4f60\u4e5f \u6765\u4e86\uff01\u201d\u542c\u90a3\u53e3\u6c14\uff0c\u8c37\u795e\u7fc1\u548c\u4ed6\u751a\u719f\uff0c\u800c\u5929\u6076\u9053\u4eba\u5219\u4f3c\u662f\u548c\u4ed6\u53ea\u5c5e\u95fb\u540d\uff0c\u5c1a\u672a\u66fe\u89c1\u8fc7\u9762\u3002 \u3000\u3000\u90a3\u602a\u5ba2\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u5f1f\uff0c\u4f60\u4eec\u6765\u5f97\uff0c\u6211\u4fbf\u6765\u4e0d\u5f97\u5417\uff1f\u201d\u8ffd\u8d76\u4ed6\u7684\u90a3\u73ed\u6b66\u571f\uff0c \u89c1\u6709\u5929\u6076\u9053\u4eba\u51fa\u6765\u62db\u547c\uff0c\u90fd\u6b62\u4e86\u811a\u6b65\u3002\u8fd9\u65f6\u602a\u5ba2\u4ece\u4ece\u5bb9\u5bb9\u7684\u7a7f\u4e0a\u4e86\u978b\u5b50\uff0c\u643a\u7740\u8c37\u795e\u7fc1\u7684 \u624b\uff0c\u563b\u563b\u54c8\u54c8\u7684\u6b65\u4e0a\u77f3\u9636\u3002 \u3000\u3000\u90a3\u4e9b\u4ece\u4e2d\u539f\u6765\u6295\u5954\u7a81\u53a5\u7684\u4eba\uff0c\u542c\u4e86\u4ed6\u4eec\u7684\u79f0\u547c\uff0c\u66f4\u662f\u5927\u611f\u60ca\u5947\uff0c\u90fd\u5728\u60f3\u9053\uff1a\u201c\u8fd9\u662f \u4ec0\u4e48\u4eba\uff1f\u8fde\u8c37\u795e\u7fc1\u5929\u6076\u9053\u4eba\u90fd\u5bf9\u4ed6\u8fd9\u4e48\u5c0a\u656c\uff1f\u201d\u770b\u8fd9\u602a\u5ba2\u7684\u76f8\u8c8c\uff0c\u816d\u4e0b\u53ea\u6709\u51e0\u6839\u957f\u9aef\uff0c \u770b\u6765\u6700\u591a\u4e0d\u8fc7\u4e94\u5341\u5e74\u7eaa\uff0c\u6bd4\u8c37\u795e\u7fc1\u4e0e\u5929\u6076\u9053\u4eba\u8981\u5e74\u8f7b\u5f97\u591a\uff0c\u4f46\u4ed6\u5374\u628a\u8c37\u795e\u7fc1\u4e0e\u5929\u6076\u9053\u4eba \u90fd\u79f0\u4f5c\u201c\u8001\u5f1f\uff01\u201d\u8fd8\u6709\uff0c\u542c\u4ed6\u4eec\u7684\u53e3\u6c14\uff0c\u4ed6\u53ea\u662f\u548c\u8c37\u795e\u7fc1\u76f8\u719f\uff0e\u548c\u5929\u6076\u9053\u4eba\u5219\u4f3c\u662f\u521a\u521a \u76f8\u8bc6\uff0c\u800c\u4e5f\u7528\u8fd9\u79cd\u4e0d\u5ba2\u6c14\u7684\u79f0\u547c\uff1b\u5929\u6076\u9053\u4eba\u4e00\u5411\u81ea\u9ad8\u81ea\u5927\uff0c\u88ab\u4ed6\u53eb\u4e86\u4e00\u58f0\u201c\u8001\u5f1f\u201d\uff0c\u9762 \u4e0a\u4e5f\u7adf\u65e0\u4e1d\u6beb\u60d5\u8272\u3002 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u8d77\u521d\u89c1\u5728\u76db\u7b75\u5c06\u5f00\u4e4b\u9645\uff0c\u7ade\u6709\u8fd9\u4e48\u4e00\u4e2a\u8863\u886b\u7834\u65e7\u7684\u602a\u5ba2\u524d\u6765\u95ef\u5e2d\uff0c\u672c\u6765\u6781 \u4e0d\u9ad8\u5174\uff0c\u540e\u6765\u5ffd\u7136\u89c1\u4ed6\u9732\u51fa\u90a3\u624b\u795e\u5947\u7684\u6b66\u529f\uff0c\u624d\u77e5\u4ed6\u662f\u4e2a\u98ce\u5c18\u5f02\u4eba\u3002\u7a81\u53a5\u5927\u6c57\u4e5f\u662f\u4e2a\u96c4 \u624d\u5927\u7565\u7684\u541b\u4e3b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6709\u5f02\u4eba\u6295\u5954\uff0c\u6b63\u4e14\u62db\u63fd\uff0c\u4e0d\u53ef\u6020\u6162\u4e86\u4ed6\u3002\u201d\u540c\u65f6\uff0c\u706d\u5ea6\u795e \u541b\u4e5f\u5bf9\u5927\u6c57\u8bf4\u51fa\u4e86\u8fd9\u4e2a\u602a\u5ba2\u4e43\u662f\u4e2a\u5927\u6709\u672c\u9886\u7684\u4eba\uff0c\u7a81\u989d\u5927\u6c57\u4fbf\u53eb\u592a\u5e08\u51fa\u6765\u8fce\u63a5\uff0c\u5c06\u4ed6\u8bf7 \u4e0a\u4e0a\u5ea7\uff0c\u4e0e\u5929\u6076\u9053\u4eba\u3001\u706d\u5ea6\u795e\u541b\u3001\u8c37\u795e\u7fc1\u7b49\u540c\u5e2d\u3002 \u3000\u3000\u674e\u9038\u542c\u5f97\u8c37\u795e\u7fc1\u5c06\u8fd9\u602a\u5ba2\u79f0\u4f5c\u201c\u8001\u7b26\u201d\uff0c\u731b\u7684\u7701\u8d77\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u539f\u6765\u662f\u5929\u5c71\u7684\u7b26 \u4e0d\u7591\u7b26\u8001\u524d\u8f88\uff01\u201d\u7b26\u4e0d\u7591\u662f\u6b66\u6797\u4e2d\u4e00\u4e2a\u9690\u58eb\uff0c\u884c\u4e8b\u9887\u4e3a\u602a\u8bde\uff0c\u90a3\u4e00\u6b21\u5ce8\u5d4b\u91d1\u9876\u7684\u82f1\u96c4\u5927 \u4f1a\uff0c\u8c37\u795e\u7fc1\u4e0e\u6b66\u7384\u971c\u6597\u5251\uff0c\u6b63\u5728\u96be\u89e3\u96be\u5206\u4e4b\u9645\uff0c\u4fbf\u662f\u7b26\u4e0d\u7591\u98d8\u7136\u800c\u6765\uff0c\u5c06\u8c37\u795e\u7fc1\u652f\u8d70\u7684\u3002 \u5176\u65f6\u674e\u9038\u867d\u5df2\u8d70\u5f00\uff0c\u4f46\u540e\u6765\u5374\u4e5f\u66fe\u542c\u5f97\u6b66\u7384\u971c\u8c08\u8fc7\uff0c\u60f3\u4e0d\u5230\u4ed6\u8fd9\u6b21\u53c8\u7a81\u5982\u5176\u6765\u4e86\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u548c\u674e\u9038\u7684\u5e08\u7236\u5c09\u8fdf\u70af\u672c\u6765\u4e5f\u662f\u5f88\u597d\u7684\u670b\u53cb\uff0c\u5c09\u8fdf\u70af\u5728\u5357\u5929\u5c71\u9690\u5c45\uff0c\u4ed6\u5728\u5317\u5929 \u5c71\u9690\u5c45\uff0c\u6709\u4e00\u6b21\u5c09\u8fdf\u70af\u53bb\u8bbf\u4ed6\uff0c\u4e0e\u4ed6\u5207\u78cb\u65b0\u521b\u7684\u51e0\u62db\u5251\u6cd5\uff0c\u7b26\u4e0d\u7591\u6b64\u4eba\u5f88\u559c\u6b22\u8bc4\u8bba\u522b\u4eba \u7684\u5251\u6cd5\uff0c\u6b22\u559c\u7528\u5632\u5f04\u7684\u53e3\u543b\uff0c\u90a3\u6b21\u4e24\u4eba\u6bd4\u8bd5\u4e86\u534a\u5929\uff0c\u7b26\u4e0d\u7591\u8d62\u4e86\u4e00\u62db\uff0c\u6316\u82e6\u4e86\u5c09\u8fdf\u70af\u4e00 \u987f\uff0c\u4f46\u5c09\u8fdf\u70af\u8ba4\u4e3a\u4ed6\u867d\u7136\u8d62\u4e86\uff0c\u5251\u6cd5\u4e2d\u4ea6\u662f\u4ecd\u6709\u7834\u7efd\uff0c\u4e0d\u8fc7\u4e00\u65f6\u95f4\u8fd8\u672a\u60f3\u51fa\u7834\u89e3\u4ed6\u7684\u6cd5 \u5b50\u7f62\u4e86\u3002\u4e24\u4eba\u9042\u76f8\u7ea6\u5728\u5341\u5e74\u4e4b\u540e\uff0c\u5404\u4ee5\u65b0\u521b\u7684\u5251\u6cd5\u518d\u6bd4\u8bd5\u4e00\u573a\uff0c\u8fd9\u662f\u674e\u9038\u672a\u5230\u5929\u5c71\u4ee5\u524d \u6240\u53d1\u751f\u7684\u4e8b\u60c5\u3002\u60f3\u4e0d\u5230\u672a\u6ee1\u5341\u5e74\u4e4b\u671f\uff0c\u5c09\u8fdf\u70af\u5148\u5df2\u6b7b\u4e86\u3002\u5929\u5c71\u5357\u5317\u8ddd\u79bb\u4e09\u5343\u4f59\u91cc\uff0c\u6240\u4ee5 \u674e\u9038\u548c\u7b26\u4e0d\u7591\u867d\u7136\u540c\u4f4f\u5929\u5c71\uff0c\u4e24\u4eba\u5374\u672a\u66fe\u89c1\u8fc7\u9762\u3002\u674e\u9038\u89c1\u662f\u4ed6\u6765\uff0c\u5fc3\u4e2d\u9887\u4e3a\u5947\u602a\uff1a\u201c\u7b26 \u4e0d\u7591\u7684\u884c\u4e3a\u867d\u7136\u602a\u8bde\uff0c\u5374\u662f\u4e2a\u4e0d\u80af\u968f\u4fd7\u6d0b\u6c89\u7684\u4e16\u5916\u9ad8\u4eba\uff0c\u600e\u4e48\u4ed6\u4e5f\u6765\u770b\u8fd9\u573a\u70ed\u95f9\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6ee1\u5802\u5bbe\u5ba2\u90fd\u5df2\u6309\u6240\u6392\u7684\u5e2d\u6b21\u5750\u597d\uff0c\u7a81\u53a5\u5927\u6c57\u65e9\u5df2\u53eb\u4eba\u53bb\u50ac\u65b0\u738b\u5983\u51fa\u6765\u656c\u9152\uff0c \u65b0\u738b\u5983\u5374\u8fdf\u8fdf\u672a\u6765\u3002\u536b\u58eb\u961f\u957f\u5df4\u56fe\u9c81\u6070\u514b\u56fe\u8bf4\u9053\uff1a\u201c\u738b\u5983\u5c1a\u672a\u51fa\u6765\uff0c\u54b1\u4eec\u53ef\u4ee5\u5148\u6765\u51e0\u573a \u73a9\u827a\uff0c\u4ee5\u5a31\u5bbe\u5ba2\uff0c\u4e5f\u514d\u5f97\u573a\u9762\u51b7\u9759\u3002\u201d\u5927\u6c57\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u73a9\u827a\u597d\u770b\u7684\uff1f\u201d\u6070\u514b\u56fe\u9053\uff1a \u201c\u6e24\u6d77\u738b\u56fd\u7684\u52d2\u52d2\u5927\u6c57\u8fdb\u8d21\u4e86\u51e0\u5934\u957f\u767d\u5c71\u7684\u5251\u9f7f\u864e\uff0c\u4eca\u65e5\u65e2\u662f\u6b66\u58eb\u5927\u4f1a\uff0c\u6b63\u597d\u8bf7\u54b1\u4eec\u7684 \u6b66\u58eb\u663e\u4e00\u663e\u8eab\u624b\uff0c\u8868\u6f14\u670d\u864e\u7684\u529f\u592b\u3002\u201d\u6e24\u6d77\u738b\u56fd\u662f\u4e1c\u5317\u7684\u4e00\u4e2a\u5927\u56fd\uff0c\u56fd\u4e2d\u6240\u4ea7\u7684\u957f\u767d\u5c71 \u5251\u9f7f\u864e\uff0c\u662f\u731b\u864e\u4e2d\u6700\u51f6\u6076\u7684\u4e00\u79cd\uff0c\u5176\u65f6\u6e24\u6d77\u738b\u56fd\u6b63\u4e0e\u7a81\u53a5\u8054\u76df\uff0c\u77e5\u9053\u7a81\u53a5\u5373\u5c06\u51fa\u5175\u653b\u6253 \u4e2d\u56fd\uff0c\u6545\u6b64\u9001\u4e86\u51e0\u5934\u731b\u864e\u6765\u4f5c\u4e3a\u8d3a\u793c\uff0c\u90a3\u662f\u795d\u4ed6\u519b\u5a01\u5927\u632f\u7684\u610f\u601d\u3002\u7a81\u53a5\u5927\u6c57\u4e00\u542c\uff0c\u8fde\u58f0 \u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff01\u5f88\u597d\uff01\u4e0d\u5fc5\u6311\u9009\u522b\u4eba\u4e86\uff0c\u5c31\u7531\u4f60\u53bb\u670d\u864e\u5427\u3002\u201d\u7a81\u53a5\u5927\u6c57\u77e5\u6070\u514b\u56fe\u795e\u52c7\u975e \u51e1\uff0c\u60f3\u8d81\u8fd9\u4e2a\u673a\u4f1a\uff0c\u8ba9\u5404\u56fd\u6765\u7684\u6b66\u58eb\u770b\u770b\u7a81\u53a5\u672c\u56fd\u6b66\u58eb\u7684\u529f\u592b\uff0c\u4ed6\u7684\u9762\u4e0a\u4e5f\u6709\u5149\u5f69\u3002 \u3000\u3000\u6070\u514b\u56fe\u9886\u4e86\u547d\u4ee4\uff0c\u4fbf\u53eb\u9972\u864e\u7684\u5c06\u731b\u864e\u653e\u51fa\u6765\uff0c\u8fd9\u65f6\u5bab\u95e8\u5916\u5fa1\u82d1\u7684\u4e00\u5757\u7a7a\u5730\uff0c\u65e9\u5df2\u5e03 \u7f6e\u59a5\u5f53\uff0c\u5468\u56f4\u7528\u94c1\u4e1d\u7f51\u62e6\u4f4f\uff0c\u4ee5\u514d\u731b\u864e\u95ef\u51fa\u4f24\u4eba\uff0c\u4f17\u4eba\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u662f\u4e00\u53ea\u96c4\u4f1f\u7855\u5927\u7684 \u540a\u775b\u767d\u989d\u5927\u864e\uff0c\u952f\u9f7f\u56da\u70e7\uff0c\u795e\u5a01\u51db\u51db\uff0c\u679c\u7136\u4ee4\u4eba\u5bb3\u6015\u3002 \u3000\u3000\u6070\u514b\u56fe\u4ece\u5bb9\u8d70\u5165\uff0c\u5411\u90a3\u731b\u864e\u53f1\u54a4\u4e00\u58f0\uff0c\u90a3\u5934\u731b\u864e\u731b\u89c1\u6709\u4eba\u62e6\u5728\u5b83\u7684\u9762\u524d\uff0c\u864e\u5a01\u9661\u53d1\uff0c \u8f9c\u7136\u95f4\u53d1\u51fa\u9739\u96f3\u4e00\u822c\u7684\u6012\u543c\uff0c\u5de8\u5c3e\u4e00\u6446\uff0c\u817e\u7a7a\u7a9f\u8d77\uff0c\u7acb\u5373\u4fbf\u5411\u6070\u514b\u56fe\u5f53\u5934\u6251\u4e0b\uff01 \u3000\u3000\u5ea7\u4e2d\u867d\u7136\u90fd\u662f\u6709\u672c\u9886\u7684\u6b66\u58eb\uff0c\u89c1\u731b\u864e\u8fd9\u7b49\u5a01\u52bf\uff0c\u4e5f\u4e0d\u7981\u6709\u70b9\u89e6\u76ee\u60ca\u5fc3\uff0c\u6070\u514b\u56fe\u5374\u672a \u7ed9\u5b83\u58f0\u52bf\u5413\u5230\uff0c\u4f46\u89c1\u4ed6\u4e00\u4e2a\u95ea\u8eab\uff0c\u201c\u4e2d\u201d\u7684\u4e00\u62f3\uff0c\u5148\u6253\u4e2d\u4e86\u8001\u864e\u7684\u80cc\u810a\u3002 \u3000\u3000\u90a3\u8001\u864e\u76ae\u7c97\u8089\u539a\uff0c\u4f46\u5403\u4e86\u4e00\u62f3\uff0c\u4e5f\u75db\u5f97\u8fde\u58f0\u5486\u54ee\uff0c\u66f4\u53d1\u6012\u4e86\uff0c\u53ea\u89c1\u5b83\u90a3\u5bf9\u78a7\u6c79\u6c79\u94dc \u94c3\u822c\u7684\u5927\u773c\u775b\uff0c\u597d\u50cf\u8981\u55b7\u51fa\u706b\u6765\uff0c\u731b\u5730\u628a\u8170\u80ef\u4e00\u6380\uff0c\u864e\u5c3e\u4e00\u626b\uff0c\u4e24\u53ea\u7684\u722a\u4e00\u6251\uff0c\u6070\u514b\u56fe \u53cc\u638c\u5411\u5b83\u8170\u80ef\u4e00\u6309\uff0c\u90a3\u8001\u864e\u5927\u543c\u4e00\u58f0\uff0c\u8170\u80ef\u4e00\u6380\uff0c\u7adf\u628a\u6070\u514b\u56fe\u629b\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5728\u65c1\u89c2\u770b\u4eba\u864e\u76f8\u6597\u7684\u7a81\u53a5\u6b66\u58eb\u90fd\u60ca\u4e86\u4e00\u60ca\uff0c\u5ffd\u89c1\u6070\u514b\u56fe\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e0b\uff0c\u4e00 \u4e2a\u8e6c\u811a\uff0c\u5728\u90a3\u8001\u864e\u5934\u4e0a\u91cd\u91cd\u7684\u8e0f\u4e86\u4e00\u4e0b\uff0c\u4eba\u4e0e\u864e\u500f\u7684\u5206\u5f00\uff0c\u8001\u864e\u5728\u5730\u4e0a\u4e00\u8fde\u6253\u4e86\u51e0\u4e2a\u6eda\uff0c \u75db\u5f97\u58f0\u58f0\u6012\u543c\uff0c\u8e72\u5728\u5730\u4e0a\uff0c\u5f20\u7259\u821e\u722a\uff0c\u4f46\u5df2\u4f3c\u6709\u70b9\u6c14\u9981\uff0c\u4e0d\u6562\u5373\u5411\u6070\u514b\u56fe\u6251\u6765\u3002 \u3000\u3000\u6070\u514b\u56fe\u54c8\u54c8\u5927\u7b11\uff0c\u6545\u610f\u8d70\u8fd1\u8001\u864e\uff0c\u62db\u624b\u5f15\u5b83\uff0c\u90a3\u8001\u864e\u773c\u5c04\u6124\u706b\uff0c\u53ea\u542c\u5f97\u53c8\u4f3c\u534a\u7a7a\u4e2d \u8d77\u4e86\u4e00\u58f0\u9739\u96f3\uff0c\u90a3\u8001\u864e\u50cf\u662f\u75af\u4e86\u4e00\u822c\uff0c\u817e\u7a7a\u7a9c\u8d77\uff0c\u5e26\u8d77\u4e86\u4e00\u80a1\u72c2\u98ce\uff0c\u84e6\u5730\u6251\u6765\uff0c\u864e\u722a\u4e00 \u6495\uff0c\u864e\u80cc\u4e00\u6380\uff0c\u864e\u5c3e\u4e00\u526a\uff0c\u4e00\u6251\u3001\u4e00\u6380\u3001\u4e00\u526a\u4e09\u822c\u4f7f\u8fc7\uff0c\u4ecd\u7136\u4f24\u4e0d\u4e86\u6070\u514b\u56fe\uff0c\u53cd\u800c\u7ed9\u4ed6 \u4e00\u8fde\u6253\u4e86\u51e0\u62f3\u3002\u8fd9\u4e00\u6251\u3001\u4e00\u6380\u3001\u4e00\u526a\u4e43\u662f\u8001\u864e\u6700\u5389\u5bb3\u7684\u4e09\u6837\u672c\u9886\uff0c\u4e09\u6837\u672c\u9886\u90fd\u4f24\u4e0d\u4e86\u654c \u4eba\uff0c\u6c14\u66f4\u9981\u4e86\u3002\u6070\u514b\u56fe\u8d81\u7740\u864e\u52bf\u4e00\u8870\u4e4b\u9645\uff0c\u77d7\u7136\u6251\u4e0a\uff0c\u53cc\u624b\u6293\u7740\u5b83\u7684\u5934\u76ae\uff0c\u5c06\u5b83\u6309\u4e0b\uff0c \u5411\u5730\u731b\u649e\uff0c\u559d\u9053\uff1a\u201c\u755c\u751f\uff0c\u4f60\u670d\u4e0d\u670d\uff1f\u201d\u90a3\u8001\u864e\u72c2\u6666\u6012\u543c\uff0c\u56db\u53ea\u811a\u722a\u5728\u5730\u4e0a\u6252\u5f00\u4e86\u4e00\u9053 \u5751\uff0c\u5374\u662f\u6446\u8131\u4e0d\u5f00\uff0c\u6e10\u6e10\u529b\u7aed\u58f0\u5636\uff0c\u5782\u5934\u4e27\u6c14\uff0c\u4e0d\u6562\u53d1\u6076\u3002\u6070\u514b\u56fe\u54c8\u54c8\u5927\u7b11\uff0c\u8de8\u4e0a\u864e\u80cc\uff0c \u4e00\u53ea\u624b\u6293\u7740\u5b83\u7684\u5934\u76ae\uff0c\u4e00\u53ea\u624b\u8f7b\u8f7b\u62cd\u5b83\u7684\u9888\u9877\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u505a\u4e2a\u5750\u9a91\u5427\uff01\u201d\u90a3\u8001\u864e \u7ed9\u4ed6\u6cbb\u5f97\u670d\u670d\u8d34\u8d34\uff0c\u6070\u514b\u56fe\u9a91\u7740\u8001\u864e\uff0c\u7ed5\u573a\u4e00\u5468\uff0c\u573a\u5916\u638c\u58f0\u96f7\u52a8\u3002\u6070\u514b\u56fe\u5f97\u610f\u6d0b\u6d0b\uff0c\u8fd9 \u624d\u653e\u4e86\u731b\u864e\uff0c\u56de\u6765\u8986\u547d\u3002 \u3000\u3000\u5927\u6c57\u89c1\u81ea\u5df1\u7684\u536b\u58eb\u961f\u957f\u5f97\u80dc\uff0c\u5f53\u7136\u975e\u5e38\u9ad8\u5174\uff0c\u7acb\u5373\u8d50\u9152\u4e09\u676f\uff0c\u5e76\u5c01\u4ed6\u505a\u201c\u4f0f\u864e\u5c06\u519b\u201d\u3002 \u53c8\u7b11\u7740\u95ee\u5929\u6076\u9053\u4eba\u9053\uff1a\u201c\u50cf\u6070\u514b\u56fe\u8fd9\u822c\u795e\u52c7\uff0c\u5728\u4e2d\u56fd\u6b66\u58eb\u4e4b\u4e2d\uff0c\u53ef\u7b97\u5f97\u7b2c\u51e0\u7b49\u4eba\u7269\uff1f\u201d \u5929\u6076\u9053\u4eba\u7b11\u4e86\u4e00\u7b11\uff0c\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6664\uff0c\u4e5f\u5dee\u4e0d\u591a\u53ef\u4ee5\u7b97\u5f97\u662f\u7b2c\u4e00\u7b49\u4e86\u3002\u201d\u542c\u8fd9\u53e3 \u6c14\uff0c\u5206\u660e\u53ea\u662f\u6577\u884d\u5927\u6c57\u7684\u9762\u5b50\u800c\u5df2\uff0c\u5176\u5b9e\u5bf9\u6070\u514b\u56fe\u7684\u672c\u9886\u5e76\u4e0d\u600e\u6837\u606d\u7ef4\u3002\u6070\u514b\u56fe\u6124\u7136\u8bf4 \u9053\uff1a\u201c\u8bf7\u9053\u957f\u4e5f\u53bb\u964d\u670d\u4e00\u5934\u731b\u864e\uff0c\u8ba9\u54b1\u4eec\u5f00\u5f00\u773c\u754c\uff1f\u201d\u5929\u6076\u9053\u4eba\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u53eb\u9633\u592a \u534e\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u53bb\u548c\u90a3\u51e0\u53ea\u755c\u751f\u73a9\u73a9\u5427\u3002\u201d\u770b\u5929\u6076\u9053\u4eba\u7684\u795e\u6c14\uff0c\u6839\u672c\u5c31\u4e0d\u5c51 \u548c\u8001\u864e\u4f5c\u5bf9\u624b\u3002 \u3000\u3000\u9633\u592a\u534e\u5782\u624b\u531d\u4e86\u4e00\u58f0\uff0c\u606d\u606d\u656c\u656c\u7684\u5411\u5927\u6c57\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u5927\u6c57\uff0c\u8fd8\u6709\u51e0\u5934\u8fd9\u6837\u7684\u731b\u864e\uff1f\u201d \u7a81\u53a5\u5927\u6c57\u9053\uff1a\u201c\u6e24\u6d77\u738b\u56fd\u8fdb\u8d21\u4e86\u516d\u5934\u3002\u201d\u9633\u592a\u534e\u9053\uff1a\u201c\u521a\u624d\u90a3\u5934\u5df2\u7ed9\u5927\u6c57\u7684\u6b66\u58eb\u6253\u6015\u4e86\uff0c \u5c31\u9664\u5f00\u5b83\u5427\uff0c\u8ba9\u6211\u72ec\u529b\u5236\u670d\u90a3\u4e94\u5934\u731b\u864e\u3002\u201d\u9633\u592a\u534e\u8eab\u6750\u7626\u957f\uff0c\u76f8\u8c8c\u6beb\u4e0d\u5a01\u6b66\u3002\u6070\u514b\u56fe\u5fc3 \u9053\uff1a\u201c\u51ed\u8fd9\u4e2a\u75c5\u9b3c\u7684\u6a21\u6837\uff0c\u76fe\u7136\u6562\u5938\u6b64\u6d77\u53e3\uff1f\u201d\u6070\u514b\u56fe\u4e0d\u4fe1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u80fd\u964d\u670d\u4e94\u5934 \u731b\u864e\uff0c\u6211\u613f\u610f\u7ed9\u4f60\u7275\u9a6c\u968f\u767b\uff01\u201d \u3000\u3000\u7a81\u53a5\u5927\u6c57\u4e5f\u60f3\u770b\u770b\u9633\u592a\u534e\u7684\u672c\u9886\uff0c\u4fbf\u53eb\u5c06\u90a3\u4e94\u5934\u731b\u864e\u90fd\u653e\u5165\u4e86\u90a3\u5757\u6709\u94c1\u4e1d\u7f51\u6240\u62e6\u7740 \u7684\u7a7a\u5730\uff0c\u9633\u592a\u534e\u8d70\u4e86\u8fdb\u53bb\uff0c\u5728\u5730\u4e0a\u76d8\u819d\u4e00\u5750\uff0c\u4e94\u5934\u731b\u864e\u90fd\u6012\u543c\u8d77\u6765\uff0c\u4ece\u56db\u9762\u6251\u4e0a\u3002\u9633\u592a \u534e\u5ffd\u5730\u4e00\u58f0\u5927\u543c\uff0c\u6709\u5982\u96f7\u9e23\uff0c\u6446\u5728\u5fa1\u82d1\u4e0a\u7684\u90a3\u51e0\u5341\u684c\u9152\u5e2d\uff0c\u5e2d\u4e0a\u7684\u676f\u76d8\u90fd\u8df3\u52a8\u8d77\u6765\uff0c\u90a3 \u543c\u58f0\u7adf\u7136\u628a\u4e94\u5934\u731b\u864e\u7684\u6012\u543c\u538b\u4e86\u4e0b\u53bb\uff01 \u3000\u3000\u6070\u514b\u56fe\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u770b\u6765\u4f3c\u75c5\u9b3c\u6a21\u6837\u7684\u6c49\u5b50\uff0c\u543c\u53eb\u5f97\u7adf\u662f\u5982\u6b64\u9a87 \u4eba\uff01\u201d\u4ed6\u5728\u5bab\u6bbf\u91cc\u5934\uff0c\u8033\u9f13\u5140\u81ea\u7ed9\u9707\u5f97\u55e1\u55e1\u4f5c\u54cd\uff0c\u5fa1\u82d1\u5916\u9762\u7684\u5bbe\u5ba2\uff0c\u529f\u529b\u7a0d\u5f31\u7684\u66f4\u7981\u53d7 \u4e0d\u8d77\uff0c\u7eb7\u7eb7\u6495\u4e0b\u8863\u895f\uff0c\u585e\u7740\u8033\u6735\u3002 \u3000\u3000\u731b\u864e\u78b0\u7740\u4e86\u6bd4\u5b83\u4eec\u66f4\u5389\u5bb3\u7684\u654c\u4eba\uff0c\u4e00\u6837\u5bb3\u6015\uff0c\u5b83\u4eec\u88ab\u9633\u592a\u534e\u7684\u543c\u58f0\u6240\u9707\u6151\uff0c\u5c3e\u5df4\u6e10 \u6e10\u5782\u4e86\u4e0b\u6765\uff0c\u7adf\u662f\u4e0d\u6562\u5f20\u7259\u821e\u722a\u4e86\u3002 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u7709\u5934\u4e00\u76b1\uff0c\u4ed6\u4e5f\u6709\u70b9\u7981\u53d7\u4e0d\u4f4f\u9633\u592a\u534e\u7684\u543c\u58f0\uff0c\u7136\u800c\u5730\u4ee5\u5927\u6c57\u4e4b\u5c0a\uff0c\u53c8\u4e0d\u4fbf \u585e\u7740\u8033\u6735\uff0c\u4fbf\u5bf9\u5929\u6076\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u8bf7\u9053\u957f\u4ee3\u6715\u5429\u5490\uff0c\u53eb\u4ee4\u5e08\u4f84\u4e0d\u5fc5\u518d\u5927\u58f0\u543c\u53eb\u4e86\u3002\u201d\u5929 \u6076\u9053\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u592a\u534e\uff0c\u4f60\u5236\u670d\u731b\u864e\u4fbf\u4e86\uff0c\u4e0d\u5e94\u60ca\u5413\u5927\u6c57\u7684\u5bbe\u5ba2\uff01\u201d \u4ed6\u7684\u8bdd\u58f0\u58f0\u8c03\u5982\u5e38\uff0c\u7136\u800c\u5728\u90a3\u6837\u5f3a\u70c8\u7684\u543c\u58f0\u4e4b\u4e0b\uff0c\u5374\u662f\u4eba\u4eba\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u7a81\u53a5\u5927\u6c57 \u4ee5\u4e3a\u4ed6\u8981\u51fa\u5230\u5fa1\u82d1\u5916\u8fb9\uff0c\u624d\u53ef\u4ee5\u5c06\u547d\u4ee4\u4f20\u8fbe\u7ed9\u9633\u592a\u534e\u7684\uff0c\u8c01\u77e5\u4ed6\u4eba\u4e0d\u79bb\u5e2d\uff0c\u5df2\u7ecf\u7528\u4e86 \u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u5c06\u58f0\u97f3\u9001\u8fdb\u4e86\u9633\u592a\u534e\u7684\u8033\u6735\u3002\u7b26\u4e0d\u7591\u5c06\u7b77\u5b50\u5728\u684c\u9762\u4e0a\u8f7b\u8f7b\u4e00 \u6572\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u672c\u9886\uff0c\u597d\u672c\u9886\uff01\u201d\u522b\u4eba\u4e0d\u89c9\u600e\u4e48\uff0c\u5929\u6076\u9053\u4eba\u5374\u662f\u5fc3\u5934\u4e00\u51db\u3002\u5c31\u5728\u8fd9\u65f6\uff0c \u9633\u592a\u534e\u7684\u543c\u58f0\u548c\u90a3\u4e94\u5934\u731b\u864e\u7684\u543c\u58f0\u90fd\u7a81\u7136\u505c\u6b62\u3002 \u3000\u3000\u90a3\u4e94\u5934\u731b\u864e\uff0c\u8e72\u5728\u9633\u592a\u534e\u7684\u5468\u56f4\uff0c\u4e0d\u6562\u6251\u4e0a\uff0c\u5374\u4e5f\u4e0d\u540e\u9000\uff0c\u4eba\u864e\u76f8\u6301\u4e86\u4e00\u4f1a\uff0c\u4e00\u5934 \u731b\u864e\u5927\u7740\u80c6\u5b50\uff0c\u5ffd\u5730\u53c8\u5927\u543c\u4e00\u58f0\uff0c\u817e\u7a7a\u7a9c\u8d77\uff0c\u5411\u9633\u592a\u534e\u6251\u4e0b\u6765\uff0c\u5b83\u6765\u5f97\u5feb\uff0c\u9633\u592a\u534e\u6bd4\u5b83 \u66f4\u5feb\uff0c\u4f46\u89c1\u90a3\u5934\u8001\u864e\u4e00\u6251\u6251\u7a7a\uff0c\u9633\u592a\u534e\u5df2\u5728\u5b83\u7684\u9888\u9879\u4e0a\u62cd\u4e86\u4e00\u638c\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u77e5\u6b7b \u6d3b\u7684\u755c\u751f\uff0c\u7ed9\u6211\u4e56\u4e56\u7684\u8eba\u4e0b\u6765\u5427\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u5934\u540a\u775b\u767d\u989d\u731b\u864e\u7adf\u4f3c\u8001\u9f20\u9047\u4e0a\u4e86\u732b\u513f \u4e00\u822c\uff0c\u679c\u7136\u670d\u670d\u8d34\u8d34\u7684\u8eba\u4e86\u4e0b\u6765\uff0c\u539f\u6765\u662f\u88ab\u4ed6\u7528\u5206\u7b4b\u9519\u9aa8\u7684\u624b\u6cd5\u5236\u4f0f\uff0c\u5168\u8eab\u9ebb\u8f6f\uff0c\u54ea\u91cc \u8fd8\u80fd\u53d1\u5a01\uff1f \u3000\u3000\u9633\u592a\u534e\u51e0\u4e2a\u8d77\u843d\uff0c\u7528\u540c\u6837\u7684\u624b\u6cd5\uff0c\u5c06\u4e94\u5934\u731b\u864e\u2014\u4e00\u5236\u670d\uff0c\u731b\u864e\u4f0f\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u90fd\u662f \u4e0d\u6562\u52a8\u5f39\u3002\u9633\u592a\u534e\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u7ed9\u6211\u505a\u4e2a\u5750\u9a91\u5427\uff01\u201d\u8de8\u4e0a\u4e86\u4e00\u53ea\u6700\u5927\u7684\u864e\u80cc\uff0c \u4e5f\u50cf\u521a\u624d\u6070\u514b\u56fe\u6240\u505a\u7684\u4e00\u6837\uff0c\u9a91\u7740\u731b\u864e\uff0c\u7ed5\u573a\u4e00\u5468\u3002\u53ef\u662f\u521a\u624d\u6070\u514b\u56fe\u53ea\u662f\u9a91\u7740\u8001\u864e\uff0c\u800c \u4ed6\u73b0\u5728\u5219\u4e0d\u4f46\u9a91\u7740\u4e00\u53ea\u8001\u864e\uff0c\u540e\u9762\u8fd8\u6709\u56db\u53ea\u8001\u864e\u968f\u4ece\uff0c\u6bd4\u8d77\u6070\u514b\u56fe\u90a3\u662f\u795e\u6c14\u5f97\u591a\u4e86\u3002\u573a \u5916\u6b22\u547c\u559d\u91c7\u7684\u58f0\u97f3\uff0c\u4e5f\u6bd4\u521a\u624d\u66f4\u4e3a\u70ed\u70c8\u3002 \u3000\u3000\u674e\u9038\u6697\u6697\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u767e\u5fe7\u4e0a\u4eba\u7684\u5f92\u5f1f\u8fd9\u6837\u5389\u5bb3\uff0c\u767e\u5fe7\u4e0a\u4eba\u66f4\u662f\u53ef\u60f3\u800c\u77e5\u3002\u6211\u867d \u7136\u6709\u8c37\u795e\u7fc1\u76f8\u52a9\uff0c\u53ea\u6084\u4e5f\u672a\u5fc5\u662f\u4ed6\u5bf9\u624b\u3002\u201d \u3000\u3000\u6070\u514b\u56fe\u5012\u662f\u4e2a\u786c\u6c49\u5b50\uff0c\u89c1\u9633\u592a\u534e\u5982\u6b64\u672c\u9886\uff0c\u597d\u751f\u4f69\u670d\uff0c\u5f85\u5230\u9633\u592a\u534e\u56de\u5e2d\uff0c\u4fbf\u5bf9\u4ed6\u9053\uff1a \u201c\u6211\u7684\u672c\u4e8b\u4e0d\u5982\u4f60\uff0c\u6211\u8fd9\u4e2a\u4f0f\u864e\u5c06\u519b\u8ba9\u7ed9\u4f60\u5427\u3002\u201d\u7a81\u53a5\u5927\u6c57\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u90fd\u662f\u96be\u5f97\u7684 \u52c7\u58eb\uff0c\u6070\u514b\u56fe\u4e0d\u5fc5\u63a8\u8ba9\u8fd9\u4e2a\u5c01\u53f7\uff0c\u6211\u53e6\u5916\u5c01\u9633\u58ee\u58eb\u505a\u795e\u5a01\u4f0f\u864e\u5c06\u519b\u3002\u201d\u9633\u592a\u534e\u5f97\u610f\u6d0b\u6d0b \u7684\u9886\u4e86\u5927\u6c57\u7684\u5c01\u8d4f\uff0c\u8fd9\u65f6\u5374\u5ffd\u7136\u542c\u5f97\u5728\u9996\u5e2d\u7684\u5e2d\u4f4d\u4e0a\u6709\u51b7\u7b11\u4e4b\u58f0\u3002 \u3000\u3000\u9633\u592a\u534e\u4e00\u770b\uff0c\u53d1\u7b11\u7684\u4e43\u662f\u7a81\u53a5\u516d\u5b9d\u5bfa\u7684\u83e9\u63d0\u4e0a\u4eba\u3002\u8fd9\u4e00\u5e2d\u662f\u6700\u9760\u8fd1\u7a81\u53a5\u5927\u6c57\u7684\u9996\u5e2d\uff0c \u5e2d\u4e0a\u5171\u662f\u516b\u4eba\uff0c\u9664\u4e86\u4e3b\u4eba\u65b9\u9762\u7684\u9ed8\u8e51\u592a\u5e08\u4e4b\u5916\uff0c\u5176\u4ed6\u4e03\u4eba\uff0c\u90fd\u662f\u5404\u56fd\u6765\u7684\u6700\u6709\u8eab\u4efd\u7684\u4eba \u7269\u3002\u5929\u6076\u9053\u4eba\u3001\u706d\u5ea6\u795e\u541b\u3001\u8c37\u795e\u7fc1\u3001\u7b26\u4e0d\u7591\uff0c\u4fbf\u662f\u5728\u8fd9\u4e00\u5e2d\u7684\u3002\u8fd8\u6709\u4e24\u4eba\uff0c\u4e00\u4e2a\u662f\u5410\u8f70 \u6765\u7684\u660c\u94a6\u5587\u561b\uff0c\u4e00\u4e2a\u662f\u5410\u8c37\u6d51\u6765\u7684\u6b66\u58eb\u9ebb\u7ffc\u8d5e\u3002 \u3000\u3000\u8fd9\u83e9\u63d0\u4e0a\u4eba\u4e43\u662f\u7a81\u53a5\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u672c\u6765\u7a81\u53a5\u5927\u6c57\u5df2\u5185\u5b9a\u4e86\u4ed6\u505a\u56fd\u5e08\u7684\uff0c\u540e\u6765\u767e\u4f18\u4e0a \u4eba\u6765\u5230\uff0c\u767e\u5fe7\u4e0a\u4eba\u7684\u540d\u6c14\u6bd4\u4ed6\u66f4\u5927\uff0c\u7a81\u53a5\u6539\u4e86\u4e3b\u610f\uff0c\u4e0e\u83e9\u63d0\u4e0a\u4eba\u5546\u8bae\uff0c\u8981\u4ed6\u5c06\u56fd\u5e08\u7684\u5c01 \u53f7\u8ba9\u7ed9\u767e\u4f18\u4e0a\u4eba\u3002\u5728\u7a81\u53a5\u5927\u6c57\u7684\u7528\u610f\uff0c\u4e43\u662f\u60f3\u62db\u63fd\u5916\u56fd\u7684\u5947\u4eba\u5f02\u58eb\uff0c\u672c\u56fd\u7684\u5230\u5e95\u662f\u201c\u81ea \u5df1\u4eba\u201d\uff0c\u4e0d\u59a8\u8c26\u8ba9\uff0c\u83e9\u63d0\u4e0a\u4eba\u8868\u9762\u4e0a\u5f53\u7136\u6beb\u65e0\u5f02\u8bae\uff0c\u5fc3\u5e95\u91cc\u5374\u662f\u4e0d\u670d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4ed6\u89c1\u767e\u4f18\u4e0a\u4eba\u7684\u5f92\u5f1f\u9633\u592a\u534e\u538b\u5012\u4e86\u6070\u514b\u56fe\uff0c\u6070\u514b\u56fe\u81ea\u5df1\u5e76\u4e0d\u600e\u6837\uff0c\u83e9\u63d0\u4e0a\u4eba \u5374\u611f\u5230\u8138\u4e0a\u65e0\u5149\uff0c\u60f3\u66ff\u7a81\u53a5\u7684\u6b66\u58eb\u4e89\u4e00\u53e3\u6c14\uff0c\u6545\u6b64\u53d1\u51fa\u51b7\u7b11\u3002 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u4e5f\u6709\u70b9\u8be7\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u4e0a\u4eba\u56e0\u4f55\u53d1\u7b11\uff1f\u201d\u83e9\u63d0\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u7b11\u6e24\u6d77\u738b\u56fd\u9001 \u6765\u7684\u8fd9\u51e0\u5934\u8001\u864e\uff0c\u5176\u5b9e\u662f\u4e2d\u770b\u4e0d\u4e2d\u7528\u7684\u91ce\u732b\u3002\u201d\u6070\u514b\u56fe\u4e0d\u670d\u6c14\u9053\uff1a\u201c\u8fd9\u51e0\u5934\u8001\u864e\u5176\u5b9e\u5f88 \u56f0\u731b\u554a\u3002\u201d\u83e9\u63d0\u4e0a\u4eba\u4e0d\u7406\u776c\u4ed6\uff0c\u9762\u5411\u5927\u6c57\u8bf4\u9053\uff1a\u201c\u5927\u6c57\uff0c\u4f60\u77a7\u662f\u731b\u864e\u5389\u5bb3\uff0c\u8fd8\u662f\u54b1\u4eec\u7684 \u91d1\u773c\u795e\u9e70\u5389\u5bb3\uff1f\u201d\u5927\u6c57\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u6050\u6015\u662f\u795e\u9e70\u6bd4\u731b\u864e\u66f4\u5389\u5bb3\u4e9b\uff0c\u54b1\u4eec\u4e0d\u59a8\u4e00\u8bd5\u3002\u201d \u4e8e\u662f\u4f20\u4e0b\u547d\u4ee4\uff0c\u53eb\u638c\u7ba1\u72e9\u730e\u7684\u201c\u6240\u7f57\u536b\u201c\uff08\u5b98\u540d\uff09\u5c06\u5730\u6e56\u5934\u91d1\u773c\u795e\u9e70\u653e\u51fa\u6765\uff0c\u4e0e\u8fd9\u4e94\u53ea \u8001\u864e\u640f\u6597\u3002 \u3000\u3000\u8fd9\u5934\u91d1\u773c\u795e\u9e70\u4e43\u662f\u5929\u5c71\u5140\u9e70\u7684\u4e00\u79cd\uff0c\u81ea\u5e7c\u517b\u719f\uff0c\u5927\u6c57\u6bcf\u6b21\u51fa\u730e\uff0c\u90fd\u5e26\u5b83\u968f\u53bb\uff0c\u5728\u5b83 \u722a\u4e0b\uff0c\u5df2\u4e0d\u77e5\u6293\u6b7b\u8fc7\u591a\u5c11\u72ee\u3001\u718a\u3001\u864e\u3002\u8c79\uff0c\u4f46\u540c\u65f6\u4e0e\u4e94\u53ea\u51f6\u731b\u7684\u957f\u767d\u864e\u640f\u6597\uff0c\u5374\u8fd8\u662f\u7b2c \u4e00\u6b21\u3002 \u3000\u3000\u9e70\u864e\u76f8\u6597\uff0c\u58f0\u52bf\u6781\u662f\u9a87\u4eba\uff0c\u4f46\u89c1\u90a3\u5934\u91d1\u773c\u795e\u9e70\u4e24\u53ea\u7fc5\u818a\u5c55\u5f00\u3002\u8db3\u6709\u78e8\u76d8\u5927\u5c0f\uff0c\u6247\u788d \u5730\u4e0a\u7802\u98de\u77f3\u8d70\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u6bd4\u674e\u9038\u4ee5\u524d\u5728\u8349\u539f\u4e0a\u6240\u89c1\u8fc7\u7684\u5140\u9e70\u8981\u5927\u5f97\u591a\uff01 \u3000\u3000\u731b\u864e\u77e5\u9053\u6765\u4e86\u52b2\u654c\uff0c\u4f0f\u8eab\u4f5c\u52bf\uff0c\u4e00\u89c1\u90a3\u53ea\u5140\u9e70\u6251\u4e0b\u6765\uff0c\u4e94\u53ea\u731b\u864e\u4e00\u9f50\u7a9c\u8d77\uff0c\u4f46\u542c\u5f97 \u864e\u5578\u9e70\u9e23\uff0c\u88c2\u4eba\u5fc3\u80ba\uff0c\u8f6c\u77ac\u95f4\uff0c\u53ea\u89c1\u90a3\u53ea\u5140\u9e70\u5c55\u7fc5\u98de\u8d77\uff0c\u843d\u4e0b\u4e86\u4e00\u5927\u7247\u6bdb\u7fbd\u3002\u7a81\u53a5\u5927\u6c57 \u53d8\u4e86\u9762\u8272\uff0c\u5374\u6709\u773c\u5149\u9510\u5229\u7684\u6b66\u58eb\u594f\u9053\uff1a\u201c\u91d1\u773c\u795e\u9e70\u5df2\u628a\u4e24\u5934\u731b\u864e\u7684\u773c\u775b\u6293\u778e\u201d\u7a81\u53a5\u5927\u6c57 \u8fd9\u624d\u77e5\u9053\u662f\u4ed6\u7684\u795e\u9e70\u5df2\u5360\u4e86\u4e0a\u98ce\u3002\u5927\u6c57\u547d\u4ee4\u9972\u9e70\u7684\u4eba\u5c06\u795e\u9e70\u653e\u51fa\uff0c\u9972\u9e70\u7684\u53d1\u4e86\u4e00\u58f0\u53e3\u54e8\uff0c \u968f\u5373\u54e9\u55c5\u54e9\u7684\u5c04\u51fa\u4e86\u4e09\u652f\u7fbd\u7bad\uff0c\u4e09\u652f\u7fbd\u7bad\u90fd\u843d\u5728\u83e9\u63d0\u4e0a\u4eba\u7684\u9762\u524d\uff0c\u6392\u5217\u6210\u4e00\u4e2a\u54c1\u5b57\u5f62\uff0c \u90fd\u4e0d\u5230\u4e00\u5c3a\u4e4b\u5730\u3002\u8fd9\u662f\u4e00\u4e2a\u8baf\u53f7\uff0c\u5f80\u5e38\u5927\u6c57\u5e26\u5b83\u53bb\u6253\u730e\u65f6\uff0c\u4fbf\u662f\u8fd9\u6837\u6559\u795e\u9e70\u968f\u7740\u4e86\u98de\u5931 \u53bb\u8ffd\u6355\u730e\u7269\u7684\u3002\u91d1\u773c\u795e\u9e70\u53ea\u77e5\u670d\u4ece\u4e3b\u4eba\u7684\u5429\u5490\uff0c\u7ba1\u4ed6\u662f\u4eba\u662f\u517d\uff0c\u7acb\u5373\u5c55\u5f00\u7fc5\u8180\uff0c\u597d\u50cf\u4e00 \u56e2\u9ed1\u4e91\u4f3c\u7684\uff0c\u5411\u83e9\u63d0\u4e0a\u4eba\u6251\u4e0b\u6765\uff01\u7a81\u53a5\u6b66\u58eb\u4eec\u89c1\u83e9\u63d0\u4e0a\u4eba\u4eb2\u81ea\u51fa\u573a\uff0c\u4eba\u4eba\u5174\u594b\uff0c\u53ef\u662f\u4ed6 \u4eec\u521a\u521a\u770b\u8fc7\u9e70\u864e\u76f8\u6597\u90a3\u6b8b\u5fcd\u7684\u4e00\u5e55\uff0c\u91d1\u773c\u795e\u9e70\u6293\u778e\u4e86\u8001\u864e\u4e4b\u540e\uff0c\u8fd8\u8981\u5c06\u5b83\u4eec\u6d3b\u6d3b\u6454\u6b7b\uff0c \u5374\u53c8\u4e0d\u514d\u4e3a\u83e9\u63d0\u4e0a\u4eba\u62c5\u60ca\u5bb3\u6015\u3002\u53ea\u89c1\u90a3\u5934\u5140\u9e70\u5df2\u6251\u5230\u4e86\u83e9\u63d0\u4e0a\u4eba\u7684\u5934\u9876\uff0c\u4ed6\u4ecd\u7136\u662f\u76d8\u819d \u800c\u5750\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff01\u773c\u770b\u5c31\u8981\u88ab\u795e\u9e70\u7684\u5229\u722a\u6293\u88c2\u8111\u76d6\uff0c\u6709\u4e9b\u80c6\u5c0f\u7684\u7adf\u95ed\u4e86\u773c\u775b\u3002\u5c31\u5728\u8fd9\u4e00 \u5239\u90a3\uff0c\u5ffd\u89c1\u90a3\u5934\u795e\u9e70\u655b\u4e86\u53cc\u7ffc\uff0c\u597d\u50cf\u5728\u6323\u624e\u7684\u6837\u5b50\uff0c\u6251\u4e86\u51e0\u6251\uff0c\u5374\u98de\u4e0d\u8d77\u6765\u3002\u4f17\u4eba\u597d\u751f \u5947\u602a\uff0c\u8d70\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u5934\u91d1\u773c\u795e\u9e70\u5df2\u843d\u5728\u83e9\u63d0\u4e0a\u4eba\u7684\u638c\u5fc3\uff0c\u795e\u9e70\u7684\u5229\u722a\u8d5b\u5982\u94a9\u523a\uff0c\u4f46 \u5728\u4ed6\u7684\u638c\u5fc3\u4e0a\u5374\u4e00\u70b9\u4e5f\u52a8\u5f39\u4e0d\u5f97\uff0c\u751a\u81f3\u8fde\u7fc5\u8180\u4e5f\u5f20\u4e0d\u5f00\u6765\uff0c\u4efb\u5b83\u5982\u4f55\u6323\u624e\uff0c\u7adf\u662f\u600e\u4e5f\u98de \u4e0d\u51fa\u83e9\u63d0\u4e0a\u4eba\u7684\u638c\u5fc3\uff01 \u3000\u3000\u539f\u6765\u83e9\u63d0\u4e0a\u4eba\u52a8\u8fd0\u7528\u7684\u662f\u5316\u52b2\u6d88\u52bf\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u7aef\u7684\u8fbe\u5230\u4e86\u62f3\u7ecf\u6240\u4e91\u201c\u4e0d\u504f\u4e0d\u501a\uff0c \u5ffd\u9690\u5ffd\u73b0\uff0c\u5de6\u91cd\u5219\u5de6\u865a\uff0c\u53f3\u91cd\u5219\u53f3\u865a\uff0c\u4ef0\u4e4b\u5219\u5f25\u9ad8\uff0c\u4fef\u4e4b\u5219\u5f25\u6df1\u3002\u8fdb\u4e4b\u5219\u6108\u957f\uff0c\u9000\u4e4b\u5219 \u6108\u4fc3\uff0c\u4e00\u7fbd\u4e0d\u80fd\u52a0\uff0c\u8747\u866b\u4e0d\u80fd\u843d\u201d\u7684\u5883\u754c\u3002\u8981\u77e5\u9e1f\u4e4b\u80fd\u591f\u8d77\u98de\u4e0e\u4eba\u4e4b\u80fd\u591f\u4e3e\u6b65\u51fa\u8981\u6709\u6240 \u51ed\u85c9\uff0c\u9760\u7740\u6240\u51ed\u85c9\u7684\u7269\u4f53\u7684\u201c\u53cd\u4f5c\u7528\u201d\uff0c\u624d\u80fd\u591f\u8fd0\u52a8\u3002\u8fd9\u662f\u8fd1\u4ee3\u7684\u201c\u529b\u5b66\u201d\u57fa\u672c\u5b9a\u7406\uff0c \u53e4\u4ee3\u7684\u4eba\u5f53\u7136\u4e0d\u77e5\u9053\u8fd9\u6761\u9053\u7406\uff0c\u53ef\u662f\u6b66\u5b66\u9ad8\u660e\u4e4b\u58eb\uff0c\u4ed6\u4eec\u6240\u609f\u51fa\u7684\u201c\u5316\u52b2\u6d88\u52bf\u201d\u7684\u529f\u592b\uff0c \u5b9e\u5df2\u4e0e\u201c\u529b\u5b66\u201d\u7684\u539f\u7406\u6697\u901a\u3002\u73b0\u5728\u83e9\u63d0\u4e0a\u4eba\u7684\u638c\u5fc3\u4e00\u70b9\u529b\u9002\u4e5f\u6ca1\u6709\uff0c\u5140\u9e70\u867d\u7136\u529b\u5927\u65e0\u7a77\u3002 \u5374\u5982\u7acb\u8db3\u5728\u201c\u4e00\u7fbd\u4e0d\u80fd\u52a0\u201d\u7684\u5f31\u6c34\u4e4b\u4e0a\uff0c\u5982\u4f55\u98de\u5f97\u8d77\u6765\uff1f \u3000\u3000\u674e\u9038\u770b\u5230\u4ed6\u8fd9\u7b49\u529f\u592b\uff0c\u4e5f\u81ea\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u7a81\u53a5\u56fd\u4e2d\uff0c\u4e5f\u5927\u6709\u80fd\u4eba\uff0c\u5b9e\u5728\u4e0d\u53ef \u5c0f\u89c6\u3002\u201d\u90a3\u5140\u9e70\u98de\u4e0d\u8d77\u6765\uff0c\u8fde\u58f0\u54c0\u9e23\uff0c\u83e9\u63d0\u4e0a\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u77a7\u4f60\u53ef\u601c\uff0c\u653e\u4f60\u8d70\u80a5\uff01\u201d \u638c\u5fc3\u653e\u5e73\uff0c\u5411\u4e0a\u4e00\u9001\uff0c\u91d1\u773c\u795e\u9e70\u5982\u91ca\u675f\u7f1a\uff0c\u500f\u7684\u4fbf\u662f\u4e00\u98de\u51b2\u5929\u3002 \u3000\u3000\u83e9\u63d0\u4e0a\u4eba\u56de\u5230\u5e2d\u4e0a\uff0c\u5bf9\u6070\u514b\u56fe\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d\u6070\u514b\u56fe\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u8bf4\u9053\uff1a \u201c\u5927\u5e08\uff0c\u4f60\u771f\u662f\u795e\u4eba\uff0c\u6211\u5c31\u4e0d\u660e\u767d\uff0c\u90a3\u5140\u9e70\u4e3a\u4f55\u98de\u4e0d\u8d77\u6765\uff1f\u201d\u8c37\u795e\u7fc1\u4e0e\u9633\u592a\u534e\u5f53\u7136\u61c2\u5f97 \u8fd9\u662f\u5316\u52b2\u6d88\u52bf\u7684\u529f\u592b\uff0c\u9633\u592a\u534e\u5fc3\u60f3\u9053\uff1a\u201c\u53ea\u6015\u8981\u6211\u7684\u5e08\u7236\u6765\u5230\uff0c\u624d\u80fd\u591f\u5c06\u4ed6\u6bd4\u4e0b\u53bb\u4e86\u3002\u201d \u8c37\u795e\u7fc1\u5219\u628a\u773c\u775b\u671b\u7740\u7b26\u4e0d\u7591\uff0c\u7b26\u4e0d\u7591\u5374\u61d2\u6d0b\u6d0b\u7684\u7b11\u9053\uff1a\u201c\u597d\uff0c\u770b\u5b8c\u4e00\u573a\u70ed\u95f9\u53c8\u662f\u4e00\u573a\uff0c \u771f\u662f\u8d8a\u770b\u8d8a\u6709\u610f\u601d\u4e86\u3002\u201d\u4ed6\u6446\u51fa\u4e86\u4e00\u4ed8\u8896\u624b\u65c1\u89c2\u7684\u795e\u6001\uff0c\u7adf\u4f3c\u6beb\u65e0\u4e0d\u4e0e\u4eba\u4e89\u80dc\u4e4b\u5ff5\u3002 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u5f53\u7136\u9ad8\u5174\u4e4b\u6781\uff0c\u9664\u4e86\u8d50\u9152\u676f\u4e4b\u5916\uff0c\u5e76\u53eb\u6070\u514b\u56fe\u5230\u4ed6\u7684\u5bab\u4e2d\u5b9d\u5e93\uff0c\u53d6\u4e86\u4e00\u4ef6 \u4e03\u5b9d\u8888\u88df\u6765\u8d4f\u7ed9\u83e9\u63d0\u4e0a\u4eba\u3002 \u3000\u3000\u83e9\u63d0\u4e0a\u4eba\u5750\u56de\u539f\u5ea7\uff0c\u6545\u4f5c\u8c26\u865a\u7684\u5bf9\u540c\u5e2d\u8bf4\u9053\uff1a\u201c\u8fd8\u8bf7\u5404\u4f4d\u9ad8\u660e\u6307\u6559\u3002\u201d\u8fd9\u4e00\u5e2d\u4e0a\u7684 \u5750\u7684\u90fd\u662f\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u83e9\u63d0\u4e0a\u4eba\u7684\u53e3\u543b\u5b9e\u662f\u5411\u540c\u5e2d\u7684\u6311\u6218\uff0c\u5176\u4e2d\u660c\u94c1\u5587\u561b\u4e0e\u9ebb\u7ffc\u8d5e\u4e43 \u662f\u83e9\u63d0\u4e0a\u4eba\u8fd9\u8fb9\u7684\u4eba\uff0c\u5f53\u7136\u4e0d\u4f1a\u5e94\u6218\u3002\u8c37\u795e\u7fc1\u4e0e\u706d\u5ea6\u795e\u541b\u81ea\u95ee\u6bd4\u4e0d\u4e0a\u4ed6\uff0c\u4e0d\u613f\u642d\u8154\uff0c\u7b26 \u4e0d\u7591\u53ea\u662f\u7b11\u563b\u563b\u7684\u770b\u70ed\u95f9\uff0c\u5929\u6076\u9053\u4eba\u7d20\u6765\u9a84\u50b2\uff0c\u4ed6\u5e73\u751f\u53ea\u670d\u4f18\u4e91\u8001\u5c3c\u4e0e\u767e\u4f18\u4e0a\u4eba\u4e24\u4e2a\uff0c \u4ed6\u770b\u4e86\u83e9\u63d0\u4e0a\u4eba\u5140\u9e70\u7684\u672c\u9886\uff0c\u867d\u7136\u4e5f\u611f\u5230\u6709\u70b9\u51fa\u4e4e\u610f\u5916\uff0c\u5982\u8fd8\u672a\u600e\u6837\u5fc3\u670d\uff0c\u5f53\u4e0b\u60f3\u4e86\u4e00 \u60f3\uff0c\u5ffd\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u6307\u7740\u5fa1\u82d1\u5916\u9762\u7684\u4e00\u68f5\u5927\u6811\u8bf4\u9053\uff1a\u201c\u91d1\u773c\u795e\u9e70\u786e\u662f\u795e\u529b\u60ca\u4eba\uff0c\u4f46\u5374\u4e5f \u672a\u5fc5\u6467\u6bc1\u5f97\u4e86\u8fd9\u68f5\u5927\u6811\u5427\uff1f\u201d\u8fd9\u68f5\u5927\u6811\u662f\u7a81\u53a5\u7279\u6709\u7684\u4e00\u79cd\u4e54\u6728\uff0c\u540d\u4e3a\u201c\u9f99\u722a\u6811\u201d\uff0c\u8981\u4e24 \u4e2a\u4eba\u624d\u80fd\u5408\u62b1\uff0c\u6811\u6839\u50cf\u9f99\u722a\u822c\u7262\u56fa\u5730\u76d8\u7ed3\u5730\u4e0a\uff0c\u6545\u540d\u201c\u9f99\u722a\u6811\u201d\u3002\u83e9\u63d0\u4e0a\u4eba\u5fc3\u9053\uff1a\u201c\u8981 \u6467\u6bc1\u8fd9\u68f5\u9f99\u722a\u6811\uff0c\u5c11\u8bf4\u4e5f\u8981\u4e07\u65a4\u795e\u529b\uff0c\u591a\u597d\u7684\u5185\u529f\u4e5f\u4e0d\u80fd\u591f\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u9053\u957f\u5982\u6b64 \u8bf4\u6cd5\uff0c\u5927\u7ea6\u662f\u81ea\u95ee\u53ef\u4ee5\u6467\u6bc1\u8fd9\u68f5\u5927\u6811\u4e86\uff0c\u4e0d\u77e5\u662f\u600e\u6837\u7684\u6467\u6bc1\u6cd5\uff1f\u6211\u5012\u5f88\u60f3\u5f00\u5f00\u773c\u754c\u3002\u201d \u7a81\u7136\u5927\u6c57\u7709\u5934\u4e00\u76b1\uff0c\u597d\u50cf\u672c\u6765\u60f3\u8bf4\u4ec0\u4e48\u4f3c\u7684\uff0c\u4f46\u542c\u5f97\u83e9\u63d0\u4e0a\u4eba\u8fd9\u6837\u8bf4\u4e86\uff0c\u4fbf\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u76ae\u7b11\u8089\u4e0d\u7b11\u7684\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u59d1\u4e14\u4e00\u8bd5\uff0c\u82e5\u662f\u4e0d\u80fd\uff0c\u8bf8\u4f4d\u8bf7\u522b\u89c1\u7b11\u3002\u201d \u4e8e\u662f\u8d70\u4e0b\u7389\u9636\uff0c\u5728\u5343\u767e\u6b66\u571f\u76ee\u5149\u6ce8\u89c6\u4e4b\u4e0b\u8d70\u5230\u4e86\u9f99\u722a\u6811\u4e0b\u9762\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u53cc\u638c\u6309\u5728\u6811\u4e0a\uff0c\u9762\u8272\u6c89\u91cd\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5934\u9876\u4e0a\u4fbf\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u9ec4\u8c46\u822c \u7684\u6c57\u73e0\u4e5f\u4e00\u9897\u9897\u7684\u6cbf\u7740\u9762\u988a\u6ef4\u4e0b\u6765\uff0c\u5728\u573a\u7684\u90fd\u662f\u6b66\u5b66\u9ad8\u660e\u4e4b\u58eb\uff0c\u77e5\u9053\u4ed6\u6b63\u4ee5\u4e0a\u4e58\u5185\u529f\u5efa \u6811\uff0c\u53ef\u662f\u90a3\u68f5\u5927\u6811\u5374\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u8fde\u6811\u53f6\u4e5f\u672a\u6389\u4e0b\u4e00\u7247\u3002 \u3000\u3000\u83e9\u63d0\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8d39\u529b\uff0c\u4f55\u82e6\u6765\u54c9\uff1f\u201d\u8c37\u795e\u7fc1\u7684\u5ea7\u4f4d\u4e0e\u4ed6\u76f8\u90bb\uff0c\u8fd9\u65f6\u6b63\u770b\u5f97\u51fa \u795e\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5929\u6076\u9053\u4eba\u5927\u7ea6\u8981\u65bd\u5c55\u4ed6\u7684\u770b\u5bb6\u672c\u9886\u4e86\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u83e9\u63d0\u4e0a\u4eba\u5bf9\u4ed6\u7b11 \u9053\uff1a\u201c\u8c37\u8001\u5148\u751f\uff0c\u6211\u542c\u8bf4\u4e2d\u56fd\u6709\u53e5\u6210\u8bed\u53eb\u505a\u8681\u6388\u64bc\u6811\uff0c\u770b\u6765\u4e0e\u4eca\u65e5\u7684\u60c5\u666f\u5927\u662f\u76f8\u4f3c\uff01\u201d \u8681\u6388\u64bc\u6811\uff0c\u4e43\u662f\u7b11\u4eba\u4e0d\u81ea\u91cf\u529b\u7684\u610f\u601d\uff0c\u83e9\u63d0\u4e0a\u4eba\u9887\u901a\u6c49\u5b66\uff0c\u5f15\u7528\u4e86\u8fd9\u53e5\u6210\u8bed\uff0c\u751a\u662f\u6cbe\u6cbe \u81ea\u559c\u3002\u8c37\u795e\u7fc1\u867d\u7136\u618e\u6068\u5929\u6076\u9053\u4eba\uff0c\u4f46\u542c\u5f97\u83e9\u63d0\u4e0a\u4eba\u8fd9\u6837\u8f7b\u8584\u7684\u53e3\u543b\uff0c\u5374\u7981\u4e0d\u4f4f\u51b7\u51b7\u7b11\u9053\uff1a \u201c\u53ea\u6015\u672a\u5fc5\u662f\u8681\u6388\u64bc\u6811\uff0c\u4e0a\u4eba\uff0c\u4f60\u518d\u4ed4\u7ec6\u770b\u5427\uff01\u201d \u3000\u3000\u8a00\u72b9\u672a\u4e86\uff0c\u7a81\u7136\u95f4\u5168\u573a\u9759\u5bc2\u65e0\u58f0\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u6562\u518d\u5077\u7b11\u4e86\u3002\u90a3\u68f5\u9f99\u722a\u6811\u672c\u6765\u662f\u60e0\u8327 \u8038\u7acb\uff0c\u6d53\u836b\u853d\u5730\u3002\u6811\u53f6\u6781\u4e3a\u8302\u76db\u7684\uff0c\u8fd9\u65f6\u4f46\u89c1\u9752\u7fe0\u7684\u6811\u53f6\u4e00\u7247\u7247\u53d8\u4e3a\u7126\u9ec4\uff0c\u679d\u6761\u4e0b\u5782\uff0c \u8fd9\u4e48\u5927\u7684\u4e00\u68f5\u6811\uff0c\u597d\u50cf\u7a81\u7136\u95f4\u53d8\u5f97\u67af\u840e\u4e86\uff0c\u5f53\u771f\u662f\u96be\u4ee5\u601d\u8bae\u7684\u602a\u4e8b\uff01 \u3000\u3000\u539f\u6765\u5929\u6076\u9053\u4eba\u662f\u8fd0\u7528\u4ed6\u7684\u6bd2\u638c\u795e\u529f\uff0c\u90a3\u68f5\u5927\u6811\u53d7\u4e86\u4ed6\u638c\u4e0a\u7684\u5267\u6bd2\uff0c\u518d\u88ab\u4ed6\u4ee5\u638c\u529b\u5c06 \u6bd2\u529b\u8feb\u5165\u6811\u5fc3\uff0c\u7ecf\u8fc7\u8f93\u6c34\u7684\u8109\u7edc\u6839\u9aef\u8f93\u9001\u5230\u679d\u53f6\u4e0a\u53bb\uff0c\u751f\u673a\u53d7\u4e86\u963b\u904f\uff0c\u6574\u68f5\u5927\u6811\u4fbf\u6e10\u6e10 \u53d8\u5f97\u67af\u9ec4\u4e86\u3002\u674e\u9038\u770b\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5929\u6076\u9053\u4eba\u7528\u5341\u5e74\u7684\u529f\u592b\u82e6\u7ec3\u6bd2\u638c\uff0c\u679c\u7136\u975e\u540c \u5c0f\u53ef\uff0c\u6bd4\u8d77\u4ece\u524d\uff0c\u90a3\u662f\u5389\u5bb3\u5f97\u591a\u4e86\u3002\u6b64\u4eba\u4e0d\u9664\uff0c\u7ec8\u662f\u5927\u60a3\uff01\u201d \u3000\u3000\u5929\u6076\u9053\u4eba\u6d0b\u6d0b\u81ea\u5f97\uff0c\u6b63\u62df\u6467\u6bc1\u5927\u6811\uff0c\u5ffd\u89c9\u6c14\u6c1b\u6709\u5f02\uff0c\u573a\u4e2d\u7adf\u6ca1\u4eba\u53d1\u51fa\u4e00\u53e5\u91c7\u58f0\uff0c\u84e6 \u7136\u60f3\u8d77\uff0c\u660e\u65e5\u4fbf\u662f\u7a81\u53a5\u7684\u201c\u62d4\u9752\u8282\u201d\uff0c\u7a81\u53a5\u662f\u4e00\u4e2a\u755c\u7267\u56fd\u5bb6\uff0c\u5bf9\u4e8e\u6811\u6728\u9752\u8349\u7684\u7e41\u6b96\u6ecb\u957f \u6700\u4e3a\u91cd\u89c6\uff0c\u81ea\u5df1\u5728\u4ed6\u4eec\u7684\u62d4\u9752\u4f73\u8282\u5c06\u4ed6\u4eec\u6240\u5c0a\u91cd\u7684\u9f99\u722a\u6811\u6467\u6bc1\uff0c\u5b9e\u5728\u662f\u72af\u4e86\u5927\u5fcc\u3002\u5929\u6076 \u9053\u4eba\u601d\u5ff5\u53ca\u6b64\uff0c\u4e0d\u89c9\u51b7\u6c57\u76f4\u6d41\u3002\u53ef\u662f\u90a3\u68f5\u5927\u6811\u201c\u4e2d\u6bd2\u201d\u5df2\u6df1\uff0c\u5929\u6076\u9053\u4eba\u81ea\u5df1\u4e5f\u65e0\u6cd5\u53ef\u4ee5 \u6551\u6cbb\u4e86\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u53ea\u597d\u653e\u5f03\u4e86\u6467\u6bc1\u5927\u6811\u7684\u4f01\u56fe\uff0c\u5728\u7a81\u53a5\u6b66\u58eb\u618e\u6068\u7684\u773c\u5149\u4e2d\u56de\u5230\u5e2d\u4e0a\uff0c\u7a81\u53a5\u5927 \u6c57\u6781\u4e0d\u9ad8\u5174\uff0c\u539f\u6765\u5728\u5929\u6076\u9053\u4eba\u8bf4\u51fa\u8981\u6467\u6bc1\u5927\u6811\u4e4b\u65f6\uff0c\u4ed6\u672c\u5c31\u60f3\u51fa\u58f0\u963b\u6b62\u7684\uff0c\u4f46\u90a3\u65f6\u83e9\u63d0 \u4e0a\u4eba\u6709\u610f\u8981\u4e0e\u5929\u6076\u4e3a\u96be\uff0c\u800c\u7a81\u989d\u5927\u6c57\u4e5f\u4e0d\u76f8\u4fe1\u4ed6\u80fd\u6467\u6bc1\u5927\u6811\uff0c\u6240\u4ee5\u8ba9\u4ed6\u53bb\u8bd5\u3002\u5982\u4eca\u5927\u6811 \u867d\u672a\u6467\u6bc1\uff0c\u5374\u5df2\u67af\u9ec4\uff0c\u7a81\u53a5\u5927\u6c57\u8ba4\u4e3a\u8fd9\u662f\u4e0d\u7965\u4e4b\u5146\uff0c\u6240\u4ee5\u5bf9\u4ed6\u51b7\u6de1\u4e4b\u6781\uff0c\u4e5f\u4e0d\u5411\u4ed6\u656c\u9152\u3002 \u3000\u3000\u83e9\u63d0\u4e0a\u4eba\u5374\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u9053\u957f\u679c\u7136\u597d\u672c\u9886\uff0c\u6211\u6765\u656c\u4f60\u4e00\u676f\uff01\u201d\u5929\u6076\u9053\u4eba\u8fde\u5fd9\u7ad9\u8d77\u6765 \u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u83e9\u63d0\u4e0a\u4eba\u5df2\u6258\u8d77\u4e00\u4e2a\u91d1\u76d8\uff0c\u76d8\u4e2d\u4e00\u4e2a\u767d\u7389\u676f\uff0c\u76db\u7740\u6ee1 \u6ee1\u7684\u4e00\u676f\u7f8e\u9152\uff0c\u91d1\u76d8\u5728\u4ed6\u638c\u4e0a\u6ef4\u6e9c\u6e9c\u7684\u65cb\u8f6c\uff0c\u500f\u7684\u5c31\u63a8\u5230\u4e86\u5929\u6076\u9053\u4eba\u7684\u80f8\u524d\u3002\u5929\u6076\u9053\u4eba \u4f55\u7b49\u672c\u9886\uff0c\u4e00\u89c1\u4ed6\u8fd9\u6837\u6765\u52bf\uff0c\u7acb\u5373\u77e5\u9053\u4ed6\u7684\u6765\u610f\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u8fd8\u8981\u4e0e\u6211 \u8f83\u91cf\u4e00\u756a\uff01\u201d\u4e0d\u6562\u6020\u6162\uff0c\u6025\u5fd9\u51dd\u795e\u8fd0\u6c14\uff0c\u4f38\u624b\u53bb\u63a5\uff0c\u4f46\u89c9\u4e00\u80a1\u6781\u5f3a\u52b2\u7684\u529b\u9053\u5411\u4ed6\u653b\u6765\u3002 \u5929\u6076\u9053\u4eba\u53cc\u8db3\u7262\u7262\u9489\u5728\u5730\u4e0a\uff0c\u4e0a\u8eab\u4ecd\u7136\u4e0d\u514d\u6643\u4e86\u4e00\u6643\u3002 \u3000\u3000\u539f\u6765\u83e9\u63d0\u4e0a\u4eba\u662f\u60f3\u4e86\u8bb8\u4e45\uff0c\u624d\u60f3\u51fa\u8fd9\u4e2a\u6cd5\u5b50\u6765\u4e0e\u5929\u6076\u9053\u4eba\u8f83\u91cf\u7684\u3002\u4ed6\u672c\u6765\u4e5f\u77e5\u9053\u5929 \u6076\u907f\u4eba\u64c5\u957f\u4f7f\u6bd2\uff0c\u4f46\u5374\u8fd8\u6599\u4e0d\u5230\u4ed6\u7684\u6bd2\u638c\u5982\u6b64\u5389\u5bb3\uff0c\u5c45\u7136\u80fd\u5728\u9877\u523b\u4e4b\u95f4\u4ee4\u5927\u6811\u67af\u840e\uff0c\u83e9 \u63d0\u4e0a\u4eba\u4e3a\u4e86\u907f\u514d\u4e0e\u5929\u6076\u9053\u4eba\u7684\u8eab\u4f53\u63a5\u89e6\uff0c\u56e0\u6b64\u624d\u60f3\u51fa\u4e86\u7528\u201c\u9694\u7269\u4f20\u529f\u201d\u7684\u672c\u9886\uff0c\u501f\u76db\u9152 \u7684\u91d1\u76c6\uff0c\u6765\u4e0e\u5929\u6076\u9053\u4eba\u6bd4\u62fc\u5185\u529f\u3002 \u3000\u3000\u82e5\u662f\u53cc\u65b9\u6b63\u5f0f\u4ea4\u624b\u8fc7\u62db\uff0c\u5929\u6076\u9053\u4eba\u51ed\u7740\u4ed6\u7684\u6bd2\u638c\u795e\u529f\uff0c\u81ea\u7136\u4e0d\u96be\u5236\u80dc\uff0c\u4f46\u8fd9\u6837\u6bd4\u62fc\uff0c \u4ed6\u7684\u5185\u529f\u5374\u8981\u7565\u900a\u83e9\u63d0\u4e0a\u4eba\u4e00\u7b79\uff0c\u5168\u529b\u6297\u62d2\uff0c\u5c1a\u6050\u6709\u5931\uff0c\u54ea\u6562\u817e\u9644\u624b\u53bb\u63a5\u76c6\u4e2d\u7684\u9152\u676f\uff1f \u83e9\u63d0\u4e0a\u4eba\u563b\u563b\u7b11\u9053\uff1a\u201c\u9053\u957f\uff0c\u8bf7\u559d\u9152\u5475\uff01\u55ef\uff0c\u6211\u9001\u5230\u9762\u524d\uff0c\u4f60\u53c8\u4e0d\u63a5\uff0c\u672a\u514d\u592a\u4e0d\u7ed9\u9762\u5b50 \u4e86\u5427\uff1f\u201d\u5929\u6076\u9053\u4eba\u6ee1\u809a\u76ae\u6012\u6c14\uff0c\u60c5\u77e5\u5bf9\u65b9\u662f\u6709\u610f\u8981\u81ea\u5df1\u4e0b\u4e0d\u4e86\u53f0\uff0c\u82e5\u4e0d\u662f\u5728\u5927\u6c57\u8ddf\u524d\uff0c \u4ed6\u771f\u60f3\u628a\u83e9\u63d0\u4e0a\u4eba\u6bd9\u4e8e\u638c\u4e0b\uff0c\u6b64\u9645\uff0c\u4ed6\u53ea\u597d\u88c5\u51fa\u7b11\u5bb9\uff0c\u7d22\u6027\u65bd\u5c55\u4e86\u5168\u8eab\u7684\u672c\u9886\uff0c\u53cc\u638c\u6258 \u7740\u91d1\u76d8\u3002\u5411\u5bf9\u65b9\u63a8\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5b9e\u5728\u4e0d\u6562\u5f53\uff0c\u8fd8\u662f\u6211\u501f\u82b1\u732e\u4f5b\uff0c\u5148\u656c\u4e0a\u4eba\u4e00\u676f\u5427\uff01\u201d \u3000\u3000\u91d1\u76d8\u65cb\u8f6c\u4e4b\u52bf\u5df2\u7136\u505c\u6b62\uff0c\u5929\u6076\u9053\u4eba\u5168\u529b\u8feb\u6765\uff0c\u83e9\u63d0\u4e0a\u4eba\u7684\u529f\u529b\u867d\u7136\u6bd4\u4ed6\u7a0d\u9ad8\uff0c\u5374\u4e5f \u4e0d\u6562\u817e\u51fa\u624b\u6765\u53d6\u76c6\u4e2d\u7684\u9152\u676f\uff0c\u53cc\u65b9\u5404\u81ea\u8fd0\u529f\uff0c\u6210\u4e86\u50f5\u6301\u4e4b\u5c40\uff0c\u4f46\u89c1\u4e24\u4eba\u7684\u5934\u9876\u4e0a\u90fd\u5192\u51fa \u4e86\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u53e3\u4e2d\u56b7\u9053\uff1a\u201c\u8bf7\u5440\uff0c\u8bf7\u5440\uff0c\u8bf7\u4f60\u5148\u5e72\u8fd9\u676f\uff01\u201d \u3000\u3000\u7a81\u53a5\u5927\u6c57\u77a7\u5f97\u7eb3\u7f55\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u600e\u4e48\u5982\u6b64\u5a46\u5a46\u5988\u5988\u7684\u4f60\u63a8\u6211\u8ba9\uff1f\u201d\u65c1\u8fb9\u7684\u4f8d\u4ece \u6b66\u58eb\u5f2f\u4e0b\u8eab\u8eaf\uff0c\u5728\u4ed6\u8033\u8fb9\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u4e8c\u4eba\u6b63\u5728\u4ee5\u751f\u6b7b\u76f8\u640f\uff0c\u8bf7\u5927\u6c57\u5b9a\u593a\u3002\u201d\u5927\u6c57 \u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u4fbf\u77a7\u51fa\u4e86\u5176\u4e2d\u51f6\u9669\u7684\u5f62\u52bf\uff0c\u61c2\u5f97\u4e86\u90a3\u6b66\u58eb\u7684\u610f\u601d\uff0c\u8981\u77e5\u6b64\u65f6\u53cc\u65b9\u5747\u4ee5\u6027 \u547d\u76f8\u640f\uff0c\u8c01\u90fd\u4e0d\u6562\u817e\u51fa\u624b\u6765\uff0c\u5927\u6c57\u60f3\u8981\u8c01\u6b7b\uff0c\u53ea\u987b\u5429\u5490\u4ed6\u5148\u559d\u8fd9\u676f\u4fbf\u884c\u3002\u6545\u6b64\u6b66\u58eb\u8bf4\u8bf7 \u4ed6\u201c\u5b9a\u593a\uff01\u201d\u5927\u6c57\u5fc3\u9053\uff1a\u201c\u8fd9\u9053\u58eb\u867d\u7136\u8ba8\u538c\uff0c\u72af\u6211\u5927\u5fcc\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u5ba2\u537f\u8eab\u4efd\uff0c\u6211\u6b63\u8981 \u62db\u63fd\u5404\u56fd\u6b66\u58eb\uff0c\u82e5\u7136\u4efb\u7531\u4ed6\u88ab\u83e9\u63d0\u4e0a\u4eba\u6240\u4f24\uff0c\u5c82\u4e0d\u4f7f\u5916\u4eba\u5bd2\u5fc3\uff1f\u201d\u5f53\u7136\u5927\u6c57\u4e5f\u7edd\u4e0d\u4f1a\u6697 \u52a9\u5929\u6076\u9053\u4eba\u3002\u4f46\u82e5\u4efb\u7531\u4ed6\u4eec\u50f5\u6301\u4e0b\u53bb\uff0c\u53c8\u52bf\u5c06\u4e24\u8d25\u4ff1\u4f24\u3002\u5927\u6c57\u60f3\u4e86\u7247\u523b\uff0c\u6b63\u60f3\u53eb\u4ed6\u4eec\u4e8c \u4eba\u7f62\u624b\uff0c\u5c1a\u672a\u51fa\u58f0\uff0c\u7b26\u4e0d\u7591\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u8ba9\u6765\u8ba9\u53bb\uff0c\u597d\uff0c\u8fd9\u4e00\u676f \u9152\uff0c\u5c31\u8ba9\u6211\u559d\u4e86\u5427\uff01\u201d\u62ff\u8d77\u4e00\u53cc\u7b77\u5b50\uff0c\u5728\u91d1\u76c6\u4e0a\u4e00\u6572\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u53ea\u767d\u7389 \u676f\u7ed9\u9707\u5f97\u98de\u4e86\u8d77\u6765\uff01 \u3000\u3000\u53ea\u89c1\u83e9\u63d0\u4e0a\u4eba\u4e0e\u5929\u6076\u9053\u4eba\u540c\u65f6\u677e\u5f00\u4e86\u624b\uff0c\u91d1\u76c6\u4e5f\u843d\u4e86\u4e0b\u6765\uff0c\u5410\u8c37\u6d51\u6765\u7684\u6b66\u571f\u9ebb\u7ffc\u8d5e \u6025\u5fd9\u5c06\u91d1\u76c6\u63a5\u4e0b\u3002\u8fd9\u65f6\u7b26\u4e0d\u7591\u5df2\u5f85\u676f\u5728\u624b\uff0c\u5c06\u676f\u4e2d\u7f8e\u9152\u4e00\u53e3\u559d\u5b8c\uff0c\u8fde\u58f0\u8d5e\u9053\uff1a\u201c\u597d\u9152\uff0c \u597d\u9152\uff01\u201d \u3000\u3000\u8fd9\u4e00\u5e2d\u5750\u7684\u90fd\u662f\u9876\u513f\u5c16\u513f\u4eba\u7269\uff0c\u4eba\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u8981\u77e5\u83e9\u63d0\u3001\u5929\u6076\u9053\u4eba\u4ee5\u7edd\u9876\u5185\u529f\u76f8 \u62fc\uff0c\u540c\u5e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u8fde\u8c37\u795e\u7fc1\u4e0e\u706d\u5ea6\u795e\u541b\u5728\u5185\uff0c\u81ea\u95ee\u90fd\u6ca1\u6709\u529b\u91cf\u5316\u89e3\uff0c\u5176\u5b9e\u4e0d\u53ea\u4ed6\u4eec \u6ca1\u6709\u529e\u6cd5\u5316\u89e3\uff0c\u5373\u7b97\u83e9\u63d0\u3001\u5929\u6076\u9053\u4eba\uff0c\u60f3\u81ea\u884c\u7f62\u624b\u4ea6\u662f\u4e0d\u80fd\u3002\u4e0d\u6599\u7b26\u4e0d\u7591\u53ea\u662f\u62c8\u8d77\u7b77\u5b50\uff0c \u8f7b\u8f7b\u4e00\u6572\u4fbf\u628a\u4e24\u80a1\u5185\u5bb6\u771f\u529b\uff0c\u90fd\u5316\u89e3\u4e86\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u5b9e\u5df2\u5230\u4e86\u9707\u4e16\u9a87\u4fd7\u7684\u5730\u6b65\uff01 \u3000\u3000\u4f46\u89c1\u83e9\u63d0\u3001\u5929\u6076\u4e8c\u4eba\u9893\u7136\u5750\u4e0b\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u5730\u4e0a\u7559\u4e0b\u4e86\u4ed6\u4eec\u6df1\u6df1\u7684\u8db3\u5370\uff0c\u5927\u6c57\u7684\u968f \u4ece\u6b66\u58eb\u867d\u7136\u4e0d\u61c2\u5f97\u7b26\u4e0d\u7591\u521a\u624d\u90a3\u4e00\u6572\u7684\u5965\u5999\uff0c\u770b\u4e86\u8fd9\u4e2a\u8db3\u5370\uff0c\u4ea6\u81ea\u9a87\u7136\u3002 \u3000\u3000\u4f46\u6700\u53d7\u60ca\u9a87\u7684\u8fd8\u662f\u83e9\u63d0\u3001\u5929\u6076\u9053\u4eba\uff0c\u83e9\u63d0\u4e0a\u4eba\u7684\u4e94\u810f\u516d\u8151\u90fd\u7ed9\u9707\u5f97\u597d\u50cf\u8981\u7ffb\u8f6c\u8fc7\u6765\uff0c \u6697\u81ea\u8fd0\u6c14\uff0c\u8fc7\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u65b9\u59cb\u590d\u539f\uff0c\u4ed6\u4e3e\u76ee\u770b\u5929\u6076\u9053\u4eba\uff0c\u4f46\u89c1\u5929\u6076\u9053\u4eba\u9762\u8272\u7070\u8d25\uff0c \u4e24\u773c\u65e0\u795e\uff0c\u83e9\u63d0\u4e0a\u4eba\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u59d3\u7b26\u7684\u5e76\u4e0d\u662f\u504f\u8892\u4ed6\uff0c\u770b\u6765\u8fd9\u725b\u9f3b\u5b50\u9053\u58eb\u6240\u53d7\u7684 \u4f24\uff0c\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\u3002\u201d\u539f\u6765\u4ed6\u4eec\u53d7\u4e86\u521a\u624d\u7684\u4e00\u9707\uff0c\u53cc\u65b9\u90fd\u8981\u635f\u4e86\u4e09\u5e74\u529f\u529b\u3002\u4e0d\u8fc7\uff0c\u82e5\u7136 \u6ca1\u6709\u7b26\u4e0d\u7591\u7684\u5316\u89e3\uff0c\u53ea\u6015\u4e24\u8d25\u4ff1\u4f24\uff0c\u90a3\u5c31\u8981\u6bd4\u635f\u5931\u4e09\u5e74\u529f\u529b\u66f4\u4e25\u91cd\u4e86\u3002\u5929\u6076\u9053\u4eba\u63e3\u606f\u7a0d \u5b9a\uff0c\u6697\u81ea\u601d\u91cf\uff0c\u4ee5\u7b26\u4e0d\u7591\u521a\u624d\u6240\u663e\u9732\u7684\u8fd9\u624b\u529f\u592b\u800c\u8bba\uff0c\u4ed6\u5b8c\u5168\u53ef\u4ee5\u6697\u52a9\u81ea\u5df1\u53d6\u80dc\uff0c\u4f46\u4ed6 \u5374\u4e0d\u504f\u4e0d\u501a\uff0c\u4ee4\u5230\u81ea\u5df1\u4e0e\u83e9\u63d0\u4e0a\u4eba\u90fd\u8981\u6298\u635f\u529f\u529b\uff0c\u771f\u731c\u4e0d\u900f\u4ed6\u5bf9\u81ea\u5df1\u7684\u771f\u610f\u5982\u4f55\uff1f\u6b8a\u4e0d \u77e5\u7b26\u4e0d\u7591\u6b63\u662f\u6709\u610f\u8981\u4ed6\u4eec\u90fd\u53d7\u4e00\u70b9\u5185\u4f24\u7684\u3002 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u89c1\u7b26\u4e0d\u7591\u5982\u6b64\u5316\u89e3\uff0c\u5929\u6076\u4e0e\u83e9\u63d0\u4e8c\u4eba\u65e2\u6ca1\u6709\u5206\u51fa\u8f93\u8d62\uff0c\u53cc\u65b9\u90fd\u4e0d\u81f3\u4e8e\u635f\u5931 \u9762\u5b50\uff0c\u4e5f\u5f88\u9ad8\u5174\uff0c\u5f53\u4e0b\u5bf9\u4ed6\u4eec\u4e09\u4eba\u90fd\u4eb2\u81ea\u8d50\u9152\u4e00\u676f\uff0c\u8c03\u505c\u4e86\u8fd9\u4e00\u573a\u6697\u5730\u91cc\u7684\u52fe\u5fc3\u6597\u89d2\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u6709\u4e00\u4e2a\u4eba\u8d70\u5230\u9ed8\u8e51\u592a\u5e08\u7684\u8ddf\u524d\uff0c\u4f4e\u58f0\u8bf4\u4e86\u51e0\u53e5\uff0c\u8fd9\u4e2a\u4eba\u662f\u9ed8\u8e51\u592a\u5e08\u7684\u7ba1 \u5bb6\u3002\u9ed8\u8e51\u542c\u4e86\u4e4b\u540e\uff0c\u5411\u7a81\u53a5\u5927\u6c57\u9053\uff1a\u201c\u6709\u4e00\u4f4d\u4e2d\u56fd\u6765\u7684\u5f02\u4eba\uff0c\u60f3\u5728\u5927\u6c57\u9762\u524d\u8868\u6f14\u4e00\u9879\u6280 \u80fd\u3002\u201d\u7a81\u9528\u5927\u6c57\u7709\u5934\u4e00\u76b1\uff0c\u751f\u6015\u53c8\u5f04\u51fa\u4ec0\u4e48\u4e8b\u6765\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4e2a\u4ec0\u4e48\u4eba\uff0c\u4f60\u77e5\u9053\u4ed6\u7684\u6765 \u5386\u4e48\uff1f\u4ed6\u8981\u8868\u6f14\u4ec0\u4e48\uff1f\u201d\u9ed8\u8e51\u592a\u5e08\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u4e2a\u533b\u751f\uff0c\u4ed6\u8bf4\u80fd\u533b\u597d\u90a3\u68f5\u9f99\u722a\u6811\u3002\u8fd9\u662f \u6211\u8ba4\u8bc6\u7684\u4e00\u4e2a\u533b\u751f\uff0c\u6211\u6562\u62c5\u4fdd\u4ed6\u4e0d\u662f\u574f\u4eba\u3002\u201d\u7a81\u53d6\u5927\u6c57\u542c\u4e86\u5927\u559c\uff0c\u4fbf\u4e0d\u518d\u76d8\u95ee\u9ed8\u8e51\u592a\u5e08 \u4f55\u4ee5\u4e0e\u4ed6\u76f8\u8bc6\uff0c\u7acb\u5373\u4f20\u4ee4\u9053\uff1a\u201c\u597d\uff0c\u53eb\u4ed6\u4e00\u8bd5\uff0c\u82e5\u80fd\u533b\u597d\uff0c\u91cd\u91cd\u6709\u8d4f\u3002\u201d \u3000\u3000\u547d\u4ee4\u4f20\u4e0b\uff0c\u53ea\u89c1\u573a\u4e2d\u8d70\u51fa\u4e00\u4e2a\u8001\u5934\uff0c\u4e09\u5c3a\u957f\u9aef\uff0c\u9887\u6709\u6f47\u6d12\u51fa\u5c18\u4e4b\u76f8\uff0c\u5728\u4f17\u4eba\u6ce8\u610f\u4e4b \u4e0b\uff0c\u8d70\u5230\u4e86\u90a3\u68f5\u5927\u6811\u65c1\u8fb9\u3002\u5929\u6076\u9053\u4eba\u89c1\uff0c\u4e0d\u7981\u53c8\u662f\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u8fd9\u4e2a\u8001\u5934\u513f\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5929\u6076\u9053\u4eba\u7684\u514b\u661f\u91d1\u9488\u56fd\u624b\u590f\u4faf\u575a\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u6539\u5bb9\u6613\u8c8c\uff0c \u8fd8\u662f\u539f\u6765\u7684\u90a3\u526f\u6253\u626e\u3002\u5929\u6076\u9053\u4eba\u89c1\u4e86\uff0c\u53c8\u60ca\u53c8\u7591\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u4e5f\u6765\u53c2\u52a0\u8fd9\u4e2a\u5927 \u4f1a\uff1f\u9633\u592a\u534e\u53c8\u4e0d\u662f\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u600e\u7684\u8ba9\u4ed6\u6df7\u8fdb\u6765\u4e86\uff1f\u201d\u8981\u77e5\u9633\u592a\u534e\u662f\u4e13\u804c\u8d1f\u8d23\u62db\u5f85\u5404\u56fd\u6765 \u7684\u6b66\u58eb\u7684\uff0c\u6309\u8bf4\u6709\u590f\u4faf\u575a\u8fd9\u6837\u8eab\u4efd\u7684\u4eba\u6765\u5230\uff0c\u4ed6\u5c31\u662f\u4e0d\u7980\u544a\u5927\u6c57\u4e5f\u8be5\u544a\u8bc9\u5929\u6076\u9053\u4eba\uff0c\u4e0d \u6599\u4ed6\u7adf\u8ba9\u590f\u4faf\u575a\u6df7\u5728\u4e00\u822c\u7684\u6b66\u58eb\u4e4b\u4e2d\uff0c\u76f4\u5230\u51fa\u4e86\u573a\uff0c\u5929\u6076\u9053\u4eba\u65b9\u624d\u53d1\u73b0\uff0c\u7109\u80fd\u4e0d\u53eb\u4ed6\u5927 \u5927\u60ca\u7591\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u6709\u6240\u4e0d\u77e5\uff0c\u590f\u4faf\u575a\u4e43\u662f\u9ed8\u8e51\u592a\u5e08\u8bf7\u8fdb\u6765\u7684\u3002\u539f\u6765\u9ed8\u8e51\u592a\u5e08\u6709\u4e2a\u72ec\u751f\u7231\u5b50\uff0c \u60a3\u4e86\u54ee\u5598\u75c5\uff0c\u8bf7\u4e86\u8bb8\u591a\u540d\u533b\u90fd\u533b\u4e0d\u597d\uff0c\u540e\u6765\u590f\u4faf\u575a\u626e\u505a\u4e00\u4e2a\u4e2d\u56fd\u6765\u7684\u8d70\u65b9\u90ce\u4e2d\uff0c\u53ea\u4e24\u4e09 \u5242\u836f\u5c31\u5c06\u4ed6\u533b\u597d\u4e86\uff0c\u6240\u4ee5\u9ed8\u8e51\u592a\u5e08\u5f88\u611f\u6fc0\u4ed6\u3002\u590f\u4faf\u575a\u77e5\u9053\u6b66\u58eb\u5927\u4f1a\u53ec\u5f00\uff0c\u8bf7\u6c42\u9ed8\u8e51\u592a\u5e08 \u51c6\u4ed6\u8fdb\u6765\u77a7\u77a7\u70ed\u95f9\uff0c\u9ed8\u8e51\u592a\u5e08\u7b54\u5141\u4e86\u4ed6\u3002\u4e0d\u8fc7\u9ed8\u8e51\u592a\u5e08\u5e76\u4e0d\u77e5\u9053\u4ed6\u8eab\u6000\u7edd\u4e16\u6b66\u529f\uff0c\u5c06\u4ed6 \u7684\u5ea7\u4f4d\u7f16\u5728\u666e\u901a\u7684\u5e2d\u6b21\u3002 \u3000\u3000\u573a\u4e2d\u53ea\u6709\u51e0\u4e2a\u4e00\u6d41\u9ad8\u624b\u77e5\u9053\u590f\u4faf\u575a\u7684\u6765\u5386\uff0c\u5176\u4ed6\u7684\u4eba\u5219\u6839\u672c\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\uff0c\u542c\u8bf4\u4ed6 \u80fd\u591f\u533b\u597d\u67af\u840e\u4e86\u7684\u9f99\u722a\u6811\u3002\u90fd\u611f\u5230\u65b0\u5947\uff0c\u5927\u5bb6\u51dd\u795e\u6ce8\u89c6\uff0c\u770b\u4ed6\u5982\u4f55\u533b\u6cbb\u3002 \u3000\u3000\u53ea\u89c1\u590f\u4faf\u575a\u8d70\u5230\u9f99\u722a\u6811\u4e0b\uff0c\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\uff0c\u4fbf\u4ece\u8863\u8896\u4e2d\u53d6\u51fa\u91d1\u9488\uff0c\u63d2\u5728\u6811\u5e72\u4e0a\uff0c\u4e00 \u8fde\u63d2\u4e86\u5341\u4e8c\u652f\u4e4b\u591a\uff0c\u968f\u7740\u53c8\u63d0\u4e86\u4e24\u6876\u6c34\uff0c\u6d47\u5728\u6811\u6839\uff0c\u5927\u7ea6\u8fc7\u4e86\u4e00\u652f\u9999\u7684\u65f6\u523b\uff0c\u53ea\u89c1\u67af\u9ec4 \u7684\u6811\u53f6\u7adf\u7136\u6062\u590d\u4e86\u9752\u7fe0\u7684\u989c\u8272\uff0c\u4e0b\u5782\u7684\u6811\u679d\u4e5f\u6062\u590d\u4e86\u5f39\u529b\uff0c\u968f\u98ce\u6296\u52a8\u8d77\u6765\uff0c\u67af\u840e\u50f5\u6b7b\u7684 \u5927\u6811\u679c\u7136\u201c\u590d\u6d3b\u201d\u4e86\uff01\u8981\u77e5\u5927\u6811\u4e4b\u6240\u4ee5\u67af\u840e\uff0c\u662f\u7531\u4e8e\u53d7\u4e86\u6bd2\u5bb3\uff0c\u800c\u5e76\u975e\u7531\u4e8e\u8870\u8001\uff0c\u5982\u4eca \u590f\u4faf\u575a\u89e3\u4e86\u6811\u4e2d\u7684\u6bd2\u8d28\uff0c\u6062\u590d\u4e86\u5b83\u7684\u81ea\u7136\u751f\u673a\uff0c\u56e0\u6b64\u80fd\u5728\u9877\u523b\u4e4b\u95f4\uff0c\u4fbf\u4ee4\u5b83\u201c\u590d\u6d3b\u201d\u3002 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u5927\u559c\uff0c\u7acb\u523b\u5ba3\u53ec\u547d\u4ed6\u4e0a\u6765\uff0c\u674e\u9038\u5750\u5728\u5bab\u6bbf\u91cc\u9760\u8fd1\u7389\u9636\u7684\u4e00\u5e2d\uff0c\u590f\u4faf\u575a\u8d70\u5165 \u6bbf\u5802\uff0c\u7ecf\u8fc7\u4ed6\u7684\u5e2d\u65c1\u65f6\uff0c\u5ffd\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u522b\u7684\u4eba\u90fd\u4e0d\u77e5\u9053\u4ed6\u662f\u5bf9\u674e\u9038\u6697\u6253\u547c\uff0c\u674e\u9038\u5219\u662f \u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6211\u7684\u6613\u5bb9\u4e39\u662f\u7531\u4ed6\u6240\u8d50\uff0c\u4ed6\u5f53\u7136\u770b\u5f97\u51fa\u6211\u7684\u672c\u6765\u9762\u76ee\u3002\u201d\u5c31\u5728\u6b64\u65f6\uff0c \u674e\u9038\u5ffd\u89c9\u8896\u7ba1\u4e4b\u4e2d\u4f3c\u4e4e\u94bb\u8fdb\u4e86\u4e00\u6761\u5c0f\u866b\uff0c\u6025\u6025\u4f38\u624b\u4e00\u6478\uff0c\u5374\u539f\u6765\u662f\u4e00\u6839\u6885\u82b1\u9488\u3002\u674e\u9038\u53c8 \u60ca\u53c8\u559c\uff0c\u8fd9\u65f6\u590f\u4faf\u575a\u8d70\u5230\u524d\u5934\uff0c\u674e\u9038\u8d81\u7740\u4f17\u4eba\u90fd\u5728\u6ce8\u610f\u590f\u4faf\u575a\u4e4b\u9645\uff0c\u6084\u6084\u7684\u5c06\u6885\u82b1\u9488\u62c8 \u51fa\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u9488\u5c16\u4e0a\u523a\u7740\u4e00\u5c0f\u8584\u7eb8\uff0c\u7eb8\u4e0a\u6709\u51e0\u4e2a\u6781\u7ec6\u7684\u5b57\uff0c\u674e\u9038\u88c5\u4f5c\u62ed\u6c57\uff0c\u628a\u7eb8\u7247\u51d1 \u8fd1\u773c\u5e18\uff0c\u8fd9\u624d\u770b\u51fa\u662f\u201c\u901f\u79bb\u6b64\u5730\uff0c\u8fdf\u5219\u6709\u53d8\u3002\u201d\u516b\u4e2a\u5c0f\u5b57\u3002 \u3000\u3000\u674e\u9038\u604d\u7136\u5927\u609f\uff0c\u201c\u54e6\uff0c\u539f\u6765\u90a3\u4e00\u665a\u7528\u6885\u82b1\u9488\u6697\u7b97\u7a0b\u8fbe\u82cf\u7684\u662f\u4ed6\uff01\u201d\u968f\u5373\u7591\u5fc3\u5927\u8d77\uff0c \u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u6211\u4ece\u901f\u79bb\u5f00\u8fd9\u4e2a\u5730\u65b9\uff1f\u96be\u9053\u6211\u5df2\u7ed9\u4ed6\u77a7\u7834\u4e86\uff1f\u201d\u53ef\u662f\u5728\u4f17\u76ee\u77aa\u77aa\u4e4b\u4e0b\uff0c\u53c8 \u600e\u53ef\u80fd\u8f7b\u6613\u79bb\u5f00\uff1f\u674e\u9038\u6b63\u5728\u5fc3\u614c\u610f\u4e71\uff0c\u4f46\u89c1\u590f\u4faf\u575a\u5df2\u5230\u4e86\u5927\u6c57\u5e2d\u524d\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5c82\u6709\u4f73\u4eba\u7518\u4f5c\u8d3c \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5c82\u6709\u4f73\u4eba\u7518\u4f5c\u8d3c \u3000\u3000\u7a81\u53a5\u5927\u6c57\u4e3a\u590f\u4faf\u575a\u7ed9\u4ed6\u533b\u597d\u4e86\u9f99\u722a\u6811\uff0c\u6781\u4e3a\u9ad8\u5174\uff0c\u95ee\u4e86\u59d3\u540d\uff0c\u5f53\u5373\u8d50\u9152\u4e09\u676f\uff0c\u5e76\u5429 \u5490\u9ed8\u8e51\u592a\u5e08\u9053\uff1a\u201c\u4f60\u66ff\u6715\u62db\u547c\u590f\u4faf\u5148\u751f\u3002\u201d\u9ed8\u8e51\u662f\u4ee3\u8868\u5927\u6c57\u505a\u9996\u5e2d\u7684\u4e3b\u4eba\u62db\u5f85\u8d35\u5bbe\u7684\uff0c \u5927\u6c57\u8fd9\u51e0\u53e5\u8bdd\u5429\u5490\u4e0b\u6765\uff0c\u5f53\u7136\u662f\u8981\u9ed8\u8e51\u592a\u5e08\u8bf7\u590f\u4faf\u575a\u5230\u9996\u5e2d\u4e0a\u5750\u4e86\u3002 \u3000\u3000\u9996\u5e2d\u4e0a\u5750\u7684\u90fd\u662f\u7b2c\u4e00\u6d41\u7684\u6b66\u5b66\u9ad8\u624b\uff0c\u800c\u4e14\u9664\u4e86\u4e3b\u4eba\u4e4b\u5916\uff0c\u4e03\u4e2a\u5ea7\u4f4d\u90fd\u5750\u6ee1\u4eba\u4e86\uff0c\u9ed8 \u8e51\u592a\u5e08\u9ed8\u60f3\u4e86\u4e00\u60f3\uff0c\u8d70\u53bb\u4e0e\u5410\u8c37\u6d51\u6b66\u58eb\u9ebb\u7ffc\u8d5e\u5546\u91cf\u9053\uff1a\u201c\u8fd9\u4f4d\u590f\u4faf\u5148\u751f\u662f\u8fdc\u65b9\u8d35\u5ba2\uff0c\u4f60 \u662f\u81ea\u5df1\u4eba\uff0c\u8ba9\u4e00\u8ba9\u5427\u3002\u201d\u9ebb\u7ffc\u8d5e\u4e0d\u6562\u4e0d\u4f9d\uff0c\u5fc3\u4e2d\u5374\u662f\u5f88\u4e0d\u8212\u670d\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u867d\u7136\u533b\u672f\u9ad8 \u660e\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e2a\u8d70\u65b9\u90ce\u4e2d\u800c\u5df2\uff0c\u600e\u914d\u5750\u4e0a\u6b64\u5e2d\uff1f\u201d\u8868\u9762\u4e0a\u88c5\u5f97\u6781\u4e3a\u5ba2\u6c14\uff0c\u62c9\u5f00\u6905\u5b50\uff0c\u8ba9 \u4ed6\u4e0a\u5750\uff0c\u6697\u5730\u91cc\u5c06\u811a\u8f7b\u8f7b\u4e00\u7eca\uff0c\u60f3\u628a\u590f\u4faf\u7eca\u5012\uff0c\u4ee4\u4ed6\u5f53\u573a\u51fa\u4e11\uff0c\u90a3\u77e5\u5fc3\u5ff5\u65b9\u52a8\uff0c\u7a81\u89c9\u817f \u5f2f\u4e00\u9ebb\uff0c\u8eac\u4e0b\u8170\u6765\uff0c\u7adf\u4f3c\u8981\u5bf9\u590f\u4faf\u575a\u884c\u5927\u793c\u4e00\u822c\uff0c\u590f\u4faf\u575a\u6545\u4f5c\u60f6\u6050\u7684\u795e\u60c5\uff0c\u6025\u5fd9\u5c06\u4ed6\u6276 \u8d77\uff0c\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff01\u4e0d\u6562\uff01\u201d\u9ebb\u7ffc\u8d5e\u4f46\u89c9\u4e00\u80a1\u5927\u529b\u5c06\u4ed6\u6258\u8d77\uff0c\u9976\u662f\u4ed6\u7528\u4e86\u6d51\u8eab\u672c\u9886\uff0c \u7adf\u7136\u6297\u62d2\u4e0d\u6765\uff0c\u8fd9\u624d\u5927\u5403\u4e00\u60ca\uff0c\u77e5\u9053\u5bf9\u65b9\u4e0d\u4f46\u533b\u672f\u9ad8\u660e\uff0c\u6b66\u529f\u4ea6\u662f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u8fde\u5fd9\u62f1\u624b \u8bf4\u9053\uff1a\u201c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d\u800c\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u817f\u5f2f\u7684\u9ebb\u75f9\u4e4b\u611f\u4e5f\u767b\u65f6\u723d\u7136\u82e5\u5931\u4e86\u3002 \u3000\u3000\u8fd9\u4e2d\u95f4\u7684\u53d8\u5316\uff0c\u8fde\u83e9\u63d0\u4e0a\u4eba\u4e5f\u672a\u66fe\u5bdf\u89c9\u4e5f\u6765\uff0c\u4f46\u89c9\u9ebb\u7ffc\u8d5e\u524d\u5028\u540e\u606d\uff0c\u6709\u70b9\u53e4\u602a\uff0c\u5750 \u5728\u90bb\u5e2d\u7684\u7a0b\u8fbe\u82cf\u5219\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u8001\u5934\u513f\u7684\u70b9\u7a74\u529f\u592b\uff0c\u5f53\u771f\u662f\u795e\u51fa\u9b3c\u6ca1\uff0c\u55ef\uff0c\u90a3 \u665a\u7528\u6885\u82b1\u9488\u6697\u7b97\u6211\u7684\uff0c\u83ab\u975e\u5c31\u662f\u6b64\u4eba\uff1f\u201d\u53ea\u89c1\u9ed8\u8e51\u592a\u5e08\u606d\u606d\u656c\u656c\u7684\u8bf7\u590f\u4faf\u575a\u5750\u4e0b\uff0c\u5e76\u5411 \u540c\u5e2d\u8bf8\u4eba\u4ecb\u7ecd\u9053\uff1a\u201c\u8fd9\u4f4d\u590f\u4faf\u5148\u751f\u533b\u672f\u901a\u795e\uff0c\u5c0f\u513f\u6240\u60a3\u7684\u987d\u75c7\u4fbf\u662f\u4ed6\u533b\u597d\u7684\uff0c\u54c8\uff0c\u54c8\uff0c \u590f\u4faf\u5148\u751f\uff0c\u60f3\u4e0d\u5230\u4f60\u4e0d\u4f46\u80fd\u591f\u533b\u4eba\uff0c\u8fd8\u80fd\u591f\u533b\u6811\uff0c\u6211\u4e5f\u7ed9\u4f60\u656c\u9152\u4e09\u676f\u3002\u201d\u7a0b\u8fbe\u82cf\u542c\u4e86\uff0c \u5bf9\u590f\u4faf\u575a\u654c\u610f\u66f4\u6d53\uff0c\u539f\u6765\u4ed6\u4ee5\u524d\u547d\u5357\u5bab\u5c1a\u52ab\u6740\u90a3\u82b1\u523a\u5b50\u6a21\u5546\u4eba\uff0c\u672c\u662f\u60f3\u62a2\u593a\u4ed6\u4eec\u7684\u836f\u6750\uff0c \u732e\u7ed9\u9ed8\u8e51\u592a\u5e08\u7684\uff0c\u5982\u4eca\u9ed8\u8e51\u592a\u5e08\u7684\u513f\u5b50\u5df2\u7ed9\u590f\u4faf\u575a\u533b\u597d\uff0c\u4ed6\u9001\u7ed9\u592a\u5e08\u7684\u90a3\u4efd\u793c\u7269\uff0c\u4ef7\u503c \u4fbf\u5f53\u7136\u5927\u51cf\u4e86\u3002 \u3000\u3000\u590f\u4faf\u575a\u5750\u4e0b\uff0c\u521a\u597d\u4e0e\u5929\u6076\u9053\u4eba\u5bf9\u9762\u3002\u5929\u6076\u9053\u4eba\u5c34\u5c2c\u4e4b\u6781\uff0c\u53ea\u89c1\u590f\u4faf\u575a\u5fae\u7b11\u8bf4\u9053\uff1a \u201c\u909b\u5d03\u5c71\u4e00\u522b\uff0c\u672a\u6ee1\u5341\u5e74\uff0c\u9053\u5144\u7684\u8150\u9aa8\u795e\u638c\u5df2\u7ecf\u7ec3\u6210\uff0c\u5c0f\u5f1f\u7518\u62dc\u4e0b\u98ce\u3002\u201d\u5929\u6076\u9053\u4eba\u53cc\u773c \u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u5f85\u76db\u4f1a\u8fc7\u540e\uff0c\u8fd8\u8981\u8bf7\u8001\u5144\u591a\u591a\u6307\u6559\uff01\u201d\u5176\u5b9e\u4ed6\u4eec\u6697\u4e2d\u5df2\u8f83\u91cf\u4e86\u4e00\u573a\uff0c\u5929\u6076 \u9053\u4eba\u672c\u6765\u4ee5\u4e3a\u81ea\u5df1\u7684\u6bd2\u638c\u7ec3\u6210\uff0c\u4fbf\u53ef\u4ee5\u5929\u4e0b\u65e0\u654c\uff0c\u90a3\u77e5\u5bf9\u9f99\u722a\u6811\u6240\u4e0b\u7684\u5267\u6bd2\u8fd8\u662f\u7ed9\u4ed6\u89e3 \u4e86\u3002\u5929\u6076\u9053\u4eba\u81ea\u5fd6\uff0c\u5c31\u662f\u76f4\u63a5\u4e0e\u4ed6\u52a8\u624b\uff0c\u4e5f\u672a\u5fc5\u80fd\u4f24\u5f97\u4e86\u4ed6\uff0c\u6545\u6b64\u4e0d\u6562\u5f53\u573a\u53d1\u4f5c\u3002\u5fc3\u4e2d \u76d8\u7b97\uff0c\u53ea\u5f85\u767e\u4f18\u4e0a\u4eba\u6765\u540e\uff0c\u518d\u60f3\u6cd5\u5b50\u8d62\u4ed6\u3002 \u3000\u3000\u5ea7\u4e2d\u8c37\u795e\u7fc1\u4e0e\u7b26\u4e0d\u7591\u90fd\u662f\u590f\u4faf\u575a\u7684\u8001\u76f8\u8bc6\uff0c\u51e0\u4e2a\u4eba\u5f00\u6000\u7545\u996e\uff0c\u8c08\u7b11\u751a\u6b22\uff0c\u5929\u6076\u3001\u706d \u5ea6\u4e8c\u4eba\u88ab\u51b7\u843d\u4e00\u65c1\uff0c\u66f4\u4e3a\u4e0d\u5feb\u3002 \u3000\u3000\u78b0\u676f\u8c08\u7b11\u58f0\u4e2d\uff0c\u5ffd\u542c\u5f97\u5bab\u4e2d\u5185\u4f8d\u4f20\u51fa\u6765\u7684\u62a5\u9053\uff1a\u201c\u65b0\u738b\u5983\u9a7e\u4e34\uff0c\u5411\u8d35\u5bbe\u656c\u9152\u6765\u4e86\uff01\u201d \u3000\u3000\u53c2\u52a0\u5bb4\u4f1a\u7684\u6b66\u571f\uff0c\u4eba\u4eba\u90fd\u77e5\u9053\u5927\u6c57\u65b0\u5a36\u7684\u738b\u5983\u4e43\u662f\u56fd\u4e2d\u7b2c\u4e00\u7f8e\u4eba\uff0c\u4e00\u542c\u8bf4\u738b\u5983\u5230\u6765\uff0c \u767b\u65f6\u5168\u573a\u8083\u9759\uff0c\u5c4f\u606f\u4ee5\u5f85\u3002 \u3000\u3000\u4f46\u89c1\u51e0\u540d\u5bab\u5973\uff0c\u72b9\u5982\u4f17\u661f\u62f1\u6708\u4f3c\u7684\uff0c\u7c07\u62e5\u7740\u738b\u5983\uff0c\u4ece\u5185\u6bbf\u7684\u6708\u7259\u95e8\u7f13\u7f13\u8d70\u51fa\uff0c\u679c\u7136 \u662f\u7edd\u8272\u7684\u7f8e\u4eba\u513f\uff01 \u3000\u3000\u674e\u9038\u5bf9\u65b0\u738b\u5983\u672c\u6765\u4e0d\u611f\u5174\u8da3\uff0c\u4f46\u738b\u5983\u65e2\u7136\u6765\u4e86\uff0c\u4ed6\u5f53\u7136\u4e5f\u968f\u7740\u4f17\u4eba\u770b\u5979\uff0c\u4e0d\u6599\u8fd9\u4e00 \u770b\uff0c\u767b\u65f6\u4ee4\u4ed6\u60ca\u5f97\u5446\u4e86\u3002\u65b0\u738b\u5983\u7684\u76f8\u8c8c\uff0c\u7adf\u7136\u4f3c\u66fe\u76f8\u8bc6\uff0c\u5c24\u5176\u662f\u770b\u5979\u90a3\u7709\u5b87\u95f4\u7684\u795e\u60c5\uff0c \u8d8a\u770b\u8d8a\u4f3c\u4e00\u4e2a\u4eba\u3002\u674e\u9038\u82e6\u82e6\u601d\u7d22\uff0c\u201c\u5979\uff0c\u5979\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u5927\u6c57\u559c\u5b5c\u5b5c\u7684\u8d77\u8eab\u8fce\u63a5\uff0c\u8bf4\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u4eca\u65e5\u662f\u4f60\u6211\u4f73\u671f\uff0c\u6211\u7279\u522b\u4e3a\u4f60\u5bb4\u8bf7\u5929\u4e0b \u82f1\u96c4\uff0c\u5404\u65b9\u6b66\u58eb\uff0c\u8fd9\u6837\u7684\u6765\u5e86\u795d\u4f60\u6211\u7ed3\u5a5a\u5927\u5178\uff0c\u5b9e\u662f\u7a81\u53a5\u5f00\u56fd\u4ee5\u6765\uff0c\u4ece\u6240\u672a\u6709\u7684\u76db\u5bb4\uff0c \u8bf7\u4f60\u5411\u5404\u4f4d\u8d35\u5bbe\u656c\u9152\u4e00\u676f\u3002\u201d \u3000\u3000\u65b0\u738b\u5983\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5927\u6c57\u4e3a\u6211\u5b89\u6392\u4e86\u8fd9\u6837\u9686\u91cd\u7684\u5bb4\u4f1a\u3002\u201d\u968f\u5373\u4f38\u51fa\u7ea4\u7ea4\u7389\u624b\uff0c \u4e3e\u8d77\u4e86\u4e00\u4e2a\u767d\u7389\u676f\uff0c\u7b11\u76c8\u76c8\u7684\u8bf4\u9053\uff1a\u201c\u8bf7\u5404\u4f4d\u8d35\u5bbe\u5e72\u4e86\u6b64\u676f\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u674e\u9038\u7684\u9152\u676f\u4ece\u624b\u4e2d\u8dcc\u4e0b\uff0c\u5e78\u800c\u5357\u5bab\u5c1a\u5728\u4ed6\u65c1\u8fb9\uff0c\u6025\u5fd9\u4e3e\u624b\u4e00\u6284\uff0c\u53e6\u4e00\u9152 \u676f\u672a\u66fe\u843d\u5730\uff0c\u4fbf\u7ed9\u5357\u5b98\u5c1a\u62a2\u81f3\u624b\u4e2d\uff0c\u5357\u5bab\u5c1a\u5c06\u9152\u676f\u9012\u8fd8\u674e\u9038\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u5983\u679c\u7136\u7f8e \u8273\u975e\u51e1\u3002\u6bbf\u4e0b\u4f60\u559d\u9152\u5427\u3002\u201d\u5357\u5bab\u5c1a\u8fd8\u4ee5\u4e3a\u674e\u9038\u662f\u56e0\u89c1\u738b\u5983\u7f8e\u8c8c\uff0c\u4ee5\u81f3\u52a8\u5fc3\uff0c\u6545\u6b64\u63d0\u9192\u4ed6 \u4e0d\u8981\u5931\u793c\u3002 \u3000\u3000\u674e\u9038\u63a5\u8fc7\u9152\u676f\uff0c\u5fc3\u795e\u7a0d\u5b9a\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u4f46\u8fd9\u795e\u6001\u4f3c\u66fe\u76f8\u8bc6\uff0c\u8fde\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u7b80\u76f4\u4e00 \u6837\uff01\u5979\uff0c\u5979\uff0c\u5979\u4e00\u5b9a\u662f\u6b66\u7384\u971c\u3002\u201d \u3000\u3000\u4e0d\u9519\uff0c\u8fd9\u738b\u5983\u786e\u662f\u6b66\u7384\u971c\u5047\u626e\u7684\uff0c\u771f\u7684\u5361\u6d74\u4e1d\u5df2\u6309\u7167\u5979\u4eec\u539f\u8d70\u7684\u8ba1\u5212\uff0c\u5192\u5145\u4e00\u4e2a\u4f8d \u5973\uff0c\u5e26\u4e86\u65b0\u5a18\u7684\u5ac1\u8863\uff0c\u4e58\u539f\u8f66\u56de\u5979\u7684\u5bb6\u4e61\u53bb\u4e86\uff0c\u8fd9\u662f\u5979\u5bb6\u4e61\u7684\u4e60\u4fd7\uff0c\u7a81\u53a5\u5927\u6c57\u867d\u7136\u7cbe\u660e\uff0c \u5374\u7edd\u5bf9\u60f3\u4e0d\u5230\u6709\u4eba\u6562\u5192\u5145\u738b\u5983\uff0c\u738b\u5983\u5374\u5192\u5145\u4f8d\u5973\uff0c\u4f55\u51b5\u6b66\u7384\u971c\u672c\u6765\u4e5f\u662f\u79c0\u4e3d\u975e\u51e1\uff0c\u5e76\u4e0d \u5728\u771f\u738b\u5983\u4e4b\u4e0b\u3002 \u3000\u3000\u6b66\u7384\u971c\u4e5f\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u674e\u9038\u5c31\u5728\u8fd9\u5ea7\u5bab\u6bbf\u4e4b\u4e2d\uff0c\u800c\u4e14\u5c45\u7136\u770b\u89c1\u5979\uff0c\u8981\u77e5\u6b66\u7384\u971c\u867d\u7136 \u7528\u4e86\u6613\u5bb9\u4e39\uff0c\u8bf4\u7684\u4e5f\u662f\u7ef4\u65cf\u7684\u8bed\u8a00\uff0c\u4f46\u5979\u7684\u795e\u60c5\u662f\u6539\u53d8\u4e0d\u4e86\u7684\uff0c\u9762\u8c8c\u7684\u8f6e\u5ed3\u4e5f\u8fd8\u662f\u4e0d\u80fd \u5b8c\u5168\u6539\u53d8\u7684\uff0c\u674e\u9038\u66fe\u7ecf\u548c\u5979\u5343\u91cc\u540c\u884c\uff0c\u5bf9\u5979\u7684\u4e00\u5207\u662f\u592a\u719f\u6089\u4e86\uff0c\u7eb5\u7136\u9694\u522b\u516b\u5e74\uff0c\u6b66\u7384\u971c \u7684\u6539\u5bb9\u6613\u8c8c\uff0c\u4ecd\u7136\u7792\u4e0d\u8fc7\u4ed6\u3002 \u3000\u3000\u674e\u9038\u5982\u5760\u5165\u4e94\u91cc\u96fe\u4e2d\uff0c\u4ed6\u770b\u4e86\u53c8\u770b\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u201c\u600e\u4e48\u53ef\u80fd\u662f\u6b66\u7384 \u971c\u5462\uff1f\u201d\u6b66\u7384\u971c\u7adf\u7136\u6210\u4e86\u5927\u6c57\u65b0\u5a36\u7684\u738b\u5983\uff1f\u8fd9\u4e8b\u60c5\u672a\u514d\u592a\u8fc7\u79bb\u5947\uff0c\u5c31\u5728\u674e\u9038\u505a\u68a6\u4e5f\u60f3\u4e0d \u5230\u3002\u53ef\u662f\u773c\u524d\u7684\u738b\u5983\uff0c\u5206\u660e\u662f\u4ed6\u68a6\u5bd0\u96be\u5fd8\u7684\u6b66\u7384\u971c\uff01\u674e\u9038\u4e00\u7247\u832b\u7136\uff0c\u72e0\u72e0\u54ac\u4e86\u4e00\u4e0b\u5634\u5507\uff0c \u5f88\u75db\uff0c\u8fd9\u53ef\u771f\u4e0d\u662f\u68a6\u554a\uff01 \u3000\u3000\u516b\u5e74\u524d\u5f80\u4e8b\uff0c\u970e\u65f6\u95f4\u91cd\u4e0a\u5fc3\u5934\uff0c\u5ce8\u5d4b\u6b64\u5251\uff0c\u9053\u7554\u8c08\u8bd7\uff0c\u5343\u91cc\u62a4\u6301\uff0c\u9a8a\u5c71\u8bc0\u522b\uff0c\u8fd9\u4e00 \u5207\u9519\u7efc\u590d\u6742\u7684\u6069\u6028\uff0c\u5230\u5982\u4eca\u90fd\u5df2\u4f3c\u68a6\u5982\u70df\uff0c\u7136\u800c\u56de\u60f3\u8d77\u6765\uff0c\u5374\u53c8\u4f3c\u6628\u5929\u4e00\u6837\u3002\u5ffd\u7136\uff0c\u674e \u9038\u4f46\u89c9\u773c\u524d\u4e00\u7247\u6a21\u7cca\uff0c\u773c\u524d\u6b66\u7384\u971c\u7684\u5f71\u5b50\u5ffd\u7136\u53d8\u6210\u4e86\u4ed6\u7684\u59bb\u5b50\uff0c\u4ed6\u597d\u4f3c\u611f\u89c9\u5230\u957f\u5b59\u58c1\u5e7d \u6028\u7684\u773c\u5149\u5728\u6697\u4e2d\u6ce8\u89c6\u4ed6\uff01\u674e\u9038\u795e\u667a\u6e05\u9192\u8fc7\u6765\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f46\u613f\u8fd9\u4e0d\u662f\u6b66\u7384\u971c\uff0c\u5509\uff0c\u7eb5 \u4f7f\u662f\u6b66\u7384\u971c\uff0c\u4f60\u4e5f\u4e0d\u5e94\u5b69\u6709\u8fd9\u6837\u7684\u5fc3\u60c5\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u674e\u9038\u5df2\u628a\u590f\u4faf\u575a\u5bf9\u4ed6\u7684\u529d\u544a\uff0c\u529d\u4ed6\u8d81\u65e9\u627e\u673a\u4f1a\u79bb\u5f00\u7684\u529d\u544a\u5fd8\u8bb0\u4e86\u3002\u4ed6\u867d\u7136\u81ea\u5df1 \u8d23\u5907\u81ea\u5df1\uff0c\u4e0d\u5e94\u8be5\u5bf9\u6b66\u7384\u971c\u518d\u6709\u8fd9\u6837\u7684\u5fc3\u60c5\uff0c\u4f46\u4ed6\u7684\u773c\u5149\u5374\u59cb\u7ec8\u6ca1\u6709\u79bb\u5f00\u6b66\u7384\u971c\uff0c\u53ef\u662f \u6b66\u7384\u971c\u5374\u4e0d\u66fe\u53d1\u73b0\u674e\u9038\uff0c\u5bbe\u5ba2\u592a\u591a\uff0c\u6bcf\u4e2a\u4eba\u7684\u773c\u5149\u90fd\u5728\u5bf9\u7740\u5979\uff0c\u56e0\u6b64\u674e\u9038\u7684\u5f20\u60f6\u5931\u6001\uff0c \u4e5f\u5c31\u6ca1\u6709\u5f15\u8d77\u522b\u4eba\u7684\u7279\u522b\u6ce8\u610f\u4e86\u3002 \u3000\u3000\u65b0\u738b\u5983\u5bf9\u4e00\u4f17\u5bbe\u5ba2\u656c\u9152\u4e4b\u540e\uff0c\u7a81\u53a5\u5927\u6c57\u5e26\u5979\u5230\u5e2d\u524d\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u4f4d\u662f\u6700\u5c0a\u8d35\u7684 \u5bbe\u5ba2\uff0c\u8bf7\u5983\u5b50\u6bcf\u4eba\u656c\u9152\u4e00\u676f\u3002\u201d\u7b26\u4e0d\u7591\u563b\u563b\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u201d\u8f6e\u5230\u5929\u6076\u9053\u4eba \u4e4b\u65f6\uff0c\u5929\u6076\u9053\u4eba\u4e3e\u8d77\u9152\u676f\uff0c\u773c\u775b\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u738b\u5983\uff0c\u5230\u738b\u5983\u5e72\u676f\u4e4b\u540e\uff0c\u4ed6\u624d\u8bb0\u5f97\u559d\u9152\uff0c \u5927\u6c57\u5fc3\u91cc\u5f88\u4e0d\u9ad8\u5174\uff0c\u60f3\uff1a\u201c\u8fd9\u9053\u58eb\u597d\u6ca1\u793c\u8c8c\uff01\u201d\u6b66\u7384\u971c\u9762\u8272\u7a0d\u53d8\uff0c\u4f46\u968f\u5373\u4fbf\u9547\u5b9a\u5982\u5e38\u3002 \u4e0d\u8fc7\u5927\u6c57\u548c\u5929\u6076\u9053\u4eba\u90fd\u5df2\u77a7\u5728\u773c\u4e2d\uff0c\u5927\u6c57\u4ee5\u4e3a\u662f\u5979\u8ba8\u538c\u5929\u6076\u9053\u4eba\u7684\u5931\u6001\uff0c\u5bf9\u5929\u6076\u9053\u4eba\u66f4 \u589e\u607c\u6012\u3002\u5929\u6076\u9053\u4eba\u5219\u5728\u6697\u6697\u5403\u60ca\uff0c\u539f\u6765\u4ed6\u5df2\u770b\u51fa\u65b0\u738b\u5983\u662f\u4e00\u4e2a\u6b66\u529f\u6781\u597d\u7684\u4eba\uff0c\u800c\u4e14\u597d\u4f3c \u662f\u5728\u4e0d\u4e45\u4e4b\u524d\uff0c\u8fd8\u5728\u90a3\u91cc\u89c1\u8fc7\u4e00\u6837\uff1f\u4f46\u4e0d\u8bba\u4ed6\u600e\u6837\u731c\u60f3\uff0c\u4e5f\u4e0d\u6562\u731c\u662f\u6b66\u7384\u971c\u3002 \u3000\u3000\u5927\u6c57\u4e0e\u738b\u5983\u56de\u5230\u5ea7\u4e0a\uff0c\u5927\u6c57\u5429\u5490\u4e00\u4e2a\u4f8d\u4ece\u9053\uff1a\u201c\u4f60\u53bb\u8bf7\u90a3\u4e24\u4f4d\u5927\u5510\u4f7f\u8005\u524d\u6765\u3002\u201d\u8fd9 \u65f6\u4e2d\u56fd\u867d\u5df2\u6539\u5510\u4e3a\u5468\uff0c\u4f46\u84c4\u90a6\u5916\u65cf\uff0c\u4e60\u60ef\u4e0a\u8fd8\u662f\u628a\u4e2d\u56fd\u79f0\u4e3a\u5927\u5510\u3002\u6b66\u7384\u971c\u542c\u5927\u6c57\u8fd9\u4e48\u5429 \u5490\uff0c\u751a\u662f\u8be7\u5f02\u3002\u5fc3\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u66fe\u542c\u59d1\u59d1\u8bf4\u8fc7\u8981\u6d3e\u4f7f\u8005\u6765\u554a\u3002\u201d\u6b63\u81ea\u601d\u7591\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a \u4f7f\u8005\u5df2\u8d70\u5230\u5e2d\u524d\uff0c\u5374\u539f\u6765\u5c31\u662f\u6b66\u7384\u971c\u5728\u5929\u5c71\u4e0a\u78b0\u89c1\u8fc7\u7684\u90a3\u4e24\u4e2a\u4eba\u2014\u2014\u5c01\u7267\u91ce\u4e0e\u795d\u89c1\u7ae0\u3002 \u3000\u3000\u5c01\u795d\u4e8c\u4eba\u78d5\u5934\u884c\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u606d\u795d\u5927\u6c57\u541b\u4e34\u4e07\u56fd\uff0c\u606d\u795d\u738b\u5983\u6c38\u4eab\u8363\u534e\u3002\u201d\u5927\u6c57\u54c8\u54c8 \u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u4f7f\u8005\u5e73\u8eab\u3002\u201d\u6b66\u7384\u971c\u5fc3\u4e2d\u607c\u6012\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\uff0c\u5047\u5192\u4f7f\u8005\uff0c \u4e22\u5c3d\u4e86\u4e2d\u56fd\u7684\u4f53\u9762\u3002\u201d\u8fd9\u65f6\u5979\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4e24\u4e2a\u4eba\u662f\u5979\u7684\u5802\u5144\u6b66\u627f\u55e3\u6d3e\u6765\u7684\u3002 \u3000\u3000\u5927\u6c57\u5fae\u7b11\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u5f85\u8fd9\u6b66\u58eb\u5927\u4f1a\u4e3e\u884c\u4e4b\u540e\uff0c\u6211\u4fbf\u8981\u6325\u5175\u6253\u5165\u4e2d\u56fd\u3002\u957f\u5b89\u5bcc\u4e3d \u4e4b\u6781\uff0c\u5361\u6d1b\u4e1d\u4f60\u53ef\u4ee5\u4f4f\u8fdb\u957f\u5b89\u7684\u5bab\u6bbf\uff0c\u8981\u4ec0\u4e48\u4fbf\u6709\u3002\u8fd9\u4e24\u4f4d\u4f7f\u8005\u662f\u4e2d\u56fd\u5973\u7687\u7684\u4f84\u513f\u6d3e\u6765 \u7684\uff0c\u8fd9\u4e2a\u5973\u7687\u5e1d\u4e0d\u5f97\u4eba\u5fc3\uff0c\u8fde\u5979\u7684\u4f84\u513f\u4e5f\u53cd\u53db\u5979\uff0c\u613f\u610f\u4e0e\u54b1\u4eec\u91cc\u5e94\u5916\u5408\uff0c\u54c8\uff0c\u54c8\uff0c\u8fd9\u4e0d \u662f\u4e0a\u5929\u52a9\u6715\u4e48\uff1f\u8fd9\u4e24\u4f4d\u4f7f\u81e3\u660e\u5929\u4fbf\u8981\u56de\u56fd\u4e86\uff0c\u6240\u4ee5\u6715\u7279\u53e6\u5ba3\u53ec\u4ed6\u4eec\u6765\uff0c\u53ef\u8d3a\u6566\uff08\u7a81\u53a5\u5bf9 \u738b\u5983\u7684\u5c0a\u79f0\uff09\uff0c\u8bf7\u4f60\u4ee3\u6715\u8d50\u4ed6\u4eec\u4e00\u676f\u7f8e\u9152\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u867d\u7136\u65e9\u5c31\u77e5\u9053\u6b66\u627f\u55e3\u7ed3\u638c\u63fd\u6743\uff0c\u56fe\u8c0b\u627f\u7ee7\u4ed6\u59d1\u59d1\u7684\u5e1d\u4f4d\uff0c\u5374\u8fd8\u672a\u6599\u5230\u4ed6\u574f\u5230 \u5982\u6b64\u5730\u6b65\uff0c\u7adf\u6562\u4e0d\u62e9\u624b\u6bb5\uff0c\u52fe\u7ed3\u5916\u5175\uff0c\u80cc\u53db\u672c\u56fd\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u9976\u662f\u5979\u5982\u4f55\u51b7\u9759\uff0c\u795e \u8272\u95f4\u4e5f\u4e0d\u514d\u7a0d\u7a0d\u9732\u51fa\u4e86\u4e0d\u6ee1\uff0c\u8fd9\u65f6\u5c01\u795d\u4e8c\u4eba\u521a\u521a\u62ac\u8d77\u5934\u6765\uff0c\u6b63\u597d\u4e0e\u6b66\u4eac\u971c\u7684\u76ee\u5149\u76f8\u63a5\uff0c \u4f3c\u89c9\u6b66\u7384\u971c\u7684\u76ee\u5149\u542b\u6709\u654c\u610f\uff0c\u4e0d\u89c9\u5fc3\u5934\u4e00\u51db\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u6211\u4eec\u5e76\u6ca1\u6709\u5730\u65b9\u62db\u607c\u738b\u5983\uff0c \u5979\u4e5f\u8bb8\u662f\u53e6\u6709\u751a\u4e48\u4e0d\u6109\u5feb\u7684\u4e8b\u60c5\u3002\u5fc3\u5ff5\u65b9\u52a8\uff0c\u6b66\u7384\u971c\u5df2\u628a\u4e24\u676f\u9152\u9012\u4e86\u8fc7\u6765\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a \u201c\u4e24\u4f4d\u4f7f\u81e3\u4e07\u91cc\u8fdc\u6765\uff0c\u591a\u591a\u8f9b\u82e6\uff0c\u8bf7\u996e\u6b64\u676f\u3002\u201d\u5c01\u795d\u4e8c\u4eba\u89c1\u5979\u548c\u989c\u60a6\u8272\uff0c\u6697\u7b11\u81ea\u5df1\u80e1\u4e71 \u731c\u7591\uff0c\u63a5\u8fc7\u4e86\u738b\u5983\u624b\u4e2d\u7684\u767d\u7389\u676f\uff0c\u4fbf\u5373\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u5c01\u795d\u4e8c\u4eba\u5411\u5927\u6c57\u4e0e\u738b\u5983\u53e9\u5934\u8c22\u6069\u4e4b\u540e\uff0c\u5c01\u7267\u91ce\u5ffd\u9053\uff1b\u201c\u6211\u6709\u4e00\u4ef6\u7d27\u6025\u7684\u4e8b\u60c5\u8981\u7980\u544a \u5927\u6c57\uff01\u201d\u7a81\u89c9\u5927\u6c57\u5fae\u6709\u8be7\u610f\uff0c\u51d1\u8fd1\u5c11\u8bb8\uff0c\u76ef\u7740\u4ed6\u9053\uff1a\u201c\u8bf7\u8bf4\uff01\u201d\u5c01\u7267\u91ce\u9053\uff1a\u201c\u5927\u6c57\u6240\u8981 \u5bfb\u627e\u7684\u90a3\u4f4d\u674e\u9038\uff0c\u4ed6\uff0c\u4ed6\u5df2\u7ecf\u6765\u5230\uff01\u201d\u5927\u6c57\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5230\u4e86\uff0c\u5230\u4e86\u54ea\u513f\uff1f\u201d\u8bdd \u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u5c01\u795d\u4e8c\u4eba\u8eab\u8eaf\u6447\u6643\uff0c\u5c01\u7267\u91ce\u52c3\u7136\u53d8\u8272\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6bd2\uff0c\u6bd2\uff01\u201d \u3000\u3000......\u4e0d\u6562\u8bf4\u51fa\u6765\u800c\u5df2\uff0c\u8fd9\u65f6\u83e9\u63d0\u4e0a\u4eba\u4e00\u8bf4\u51fa\u6765\uff0c\u7acb\u5373\u4fbf\u6709\u597d\u51e0\u4e2a\u6b66\u58eb\u8ddf\u968f\u7740\u4ed6\uff0c \u51b7\u8a00\u51b7\u8bed\u7684\u5411\u5929\u6076\u9053\u4eba\u76d8\u95ee\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u52c3\u7136\u5927\u6012\uff0c\u6307\u7740\u83e9\u63d0\u4e0a\u4eba\u65a5\u9053\uff1a\u201c\u4f60\u653e\u5c41\uff01\u201d\u83e9\u63d0\u4e0a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5929 \u4e0b\u7b2c\u4e00\u4f7f\u6bd2\u9ad8\u624b\uff0c\u9664\u4e86\u4f60\u8fd8\u6709\u8c01\u201d\uff1f\u5929\u6076\u9053\u4eba\u6709\u70b9\u60e7\u7b26\u4e0d\u7591\uff0c\u521a\u624d\u7b26\u4e0d\u7591\u7684\u51b7\u8a00\u51b7\u8bed\uff0c \u4ed6\u6562\u6012\u800c\u4e0d\u6562\u53d1\u4f5c\uff0c\u4f46\u5bf9\u4e8e\u83e9\u63d0\u4e0a\u4eba\uff0c\u5374\u8fd8\u4e0d\u600e\u6837\u653e\u5728\u5fc3\u4e0a\uff0c\u4f55\u51b5\u7b26\u4e0d\u7591\u53ea\u662f\u8f6c\u5f2f\u62b9\u89d2 \u7684\u5f71\u5c04\uff0c\u800c\u83e9\u63d0\u4e0a\u4eba\u76f4\u8a00\u6307\u65a5\u662f\u4ed6\uff0c\u5929\u6076\u9053\u4eba\u5982\u4f55\u6309\u637a\u5f97\u4f4f\uff1f\u4f46\u89c1\u4ed6\u9762\u8272\u94c1\u9752\uff0c\u83e9\u63d0\u4e0a \u4eba\u8bdd\u72b9\u672a\u4e86\uff0c\u4ed6\u4fbf\u4e00\u6293\u6293\u6765\uff0c\u66b4\u6012\u559d\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u51ed\u636e\uff1f\u4f60\u82e5\u8bf4\u4e0d\u51fa\u6765\uff0c\u5f97\u7ed9\u6211\u78d5\u5934 \u8d54\u7f6a\uff01\u201d \u3000\u3000\u83e9\u63d0\u4e0a\u4eba\u65e9\u5df2\u9632\u4ed6\u6bd2\u638c\u5389\u5bb3\uff0c\u89c1\u4ed6\u4e00\u6293\u6293\u5230\uff0c\u500f\u7684\u907f\u5f00\uff0c\u7acb\u5373\u8131\u4e0b\u8fde\u4e09\u88df\uff0c\u5411\u4ed6\u5f53 \u5934\u4e00\u7f69\uff0c\u4e5f\u5927\u58f0\u559d\u903c\uff1a\u201c\u8fd8\u8981\u4ec0\u4e48\u51ed\u636e\uff1f\u573a\u4e2d\u9664\u4f60\u4e4b\u4e2d\uff0c\u8fd8\u6709\u54ea\u4e2a\u6709\u8fd9\u79cd\u4e0b\u6bd2\u672c\u9886\uff1f\u4f60 \u82e5\u4e0d\u662f\u5fc3\u865a\u60c5\u602f\uff0c\u4f55\u987b\u66b4\u8df3\u5982\u96f7\uff1f\u201d \u3000\u3000\u4e24\u4eba\u4ece\u9a82\u6218\u6f14\u4e3a\u6fc0\u6218\uff0c\u4f46\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u83e9\u63d0\u4e0a\u4eba\u7684\u8888\u88df\u88ab\u6495\u6210\u4e24\u7247\uff0c\u4f46\u5929\u6076 \u9053\u4eba\u7ed9\u4ed6\u626b\u5f97\u8eab\u5f62\u4e0d\u7a33\uff0c\u7405\u7405\u8dc4\u8dc4\u7684\u5012\u9000\u51e0\u6b65\u3002 \u3000\u3000\u83e9\u63d0\u4e0a\u4eba\u8888\u88df\u88ab\u4ed6\u6495\u88c2\uff0c\u6012\u706b\u4e0a\u5347\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u4ed6\u662f\u5ba2\u537f\u8eab\u4efd\uff0c\u8888\u88df\u518d\u5c55\uff0c\u53c8\u662f\u547c \u7684\u4e00\u58f0\uff0c\u5411\u5929\u6076\u9053\u4eba\u7f69\u4e0b\uff0c\u8fd9\u4e00\u4e0b\u4ed6\u8fd0\u4e86\u7eaf\u771f\u7684\u5185\u529f\uff0c\u67d4\u8f6f\u7684\u8888\u88df\u53d8\u6210\u4e86\u94c1\u677f\u4e00\u822c\uff0c\u547c \u547c\u6289\u98ce\uff0c\u5c06\u5929\u6076\u9053\u4eba\u88f9\u5f97\u98ce\u96e8\u4e0d\u900f\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u5185\u529f\u7a0d\u900a\uff0c\u4ed6\u4e00\u8fde\u53d1\u4e86\u51e0\u8bb0\u5288\u7a7a\u638c\uff0c\u4ecd\u7136\u89e3\u4e0d\u4e86\u83e9\u63d0\u4e0a\u4eba\u7684\u653b\u52bf\uff01\u4ea6\u662f\u6012 \u706b\u653b\u5fc3\uff0c\u7259\u6839\u4e00\u54ac\uff0c\u77aa\u773c\u559d\u9053\uff1a\u201c\u4f60\u5f53\u6211\u6015\u4f60\u4e0d\u6210\uff1f\u4f60\u78d5\u4e0d\u78d5\u5934\uff1f\u518d\u4e0d\u78d5\u5934\uff0c\u4f11\u602a\u6211\u624b \u4e0b\u65e0\u60c5\uff01\u201d\u5c31\u5728\u8fd9\u8bf4\u8bdd\u7684\u5f53\uff0c\u4ed6\u8138\u4e0a\u7a81\u7136\u626d\u51fa\u4e86\u4e00\u5c42\u7167\u6c14\uff0c\u53cc\u638c\u62cd\u51fa\uff0c\u5e26\u8d77\u4e00\u80a1\u8165\u98ce\uff0c \u83e9\u63d0\u4e0a\u4eba\u4f46\u89c9\u5934\u6655\u76ee\u7729\uff0c\u80f8\u53e3\u80c0\u95f7\uff0c\u6599\u4e0d\u5230\u5929\u6076\u9053\u4eba\u7684\u6bd2\u638c\uff0c\u672a\u63a5\u89e6\u5230\u8eab\u4f53\uff0c\u4e5f\u8fd9\u6837\u5389 \u5bb3\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u5927\u6c57\u7684\u968f\u4ece\u89c1\u4ed6\u4eec\u95f9\u5f97\u4e0d\u6210\u4f53\u7edf\uff0c\u6025\u5fd9\u5411\u5927\u6c57\u8bf7\u793a\uff0c\u5927\u6c57\u8bf4\u9053\uff1a\u201c\u5148\u628a\u5bab\u4e2d\u5404\u5904\u95e8 \u6237\u5c01\u95ed\uff0c\u4e0d\u53ef\u653e\u4efb\u4f55\u4eba\u51fa\u53bb\uff01\u201d\u539f\u6765\u4ed6\u8bb0\u8d77\u4e86\u5c01\u7267\u91ce\u4e34\u6b7b\u4e4b\u524d\u7684\u544a\u5bc6\uff0c\u8bf4\u662f\u674e\u9038\u5230\u4e86\u8fd9 \u513f\uff0c\u6015\u674e\u9038\u8d81\u8fd9\u6df7\u4e71\u7684\u65f6\u673a\u9003\u8dd1\u3002\u5429\u5490\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u4e4b\u540e\uff0c\u8ddf\u7740\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6d3e\u4e24\u4e2a \u4eba\u5c06\u5929\u6076\u9053\u4eba\u8bf7\u5230\u540e\u9762\u6682\u884c\u5b89\u6b47\u5427\u3002\u201d\u8fd9\u53e5\u8bf4\u8bdd\u7684\u610f\u601d\uff0c\u5927\u6c57\u7684\u968f\u4ece\u6b66\u571f\u5f53\u7136\u660e\u767d\uff0c\u4e43 \u662f\u8981\u4ed6\u4eec\u52a9\u83e9\u63d0\u4e0a\u4eba\u5c06\u5929\u6076\u5236\u670d\uff0c\u62bc\u5230\u540e\u9762\u4ee5\u5f85\u5927\u6c57\u5ba1\u8baf\u3002\u4e0d\u8fc7\u5927\u6c57\u4e0d\u597d\u76f4\u8bf4\u51fa\u6765\uff0c\u6240 \u4ee5\u7528\u4e86\u4e00\u4e2a\u5ba2\u6c14\u7684\u201c\u8bf7\u201d\u5b57\u3002\u9ebb\u7ffc\u8d5e\u548c\u6070\u514b\u56fe\u542c\u4e86\u5927\u6c57\u7684\u5429\u5490\u4e4b\u540e\uff0c\u7acb\u5373\u6251\u4e0a\u524d\u53bb\uff0c\u5e2e \u52a9\u83e9\u63d0\u4e0a\u4eba\u3002 \u3000\u3000\u573a\u4e2d\u83e9\u63d0\u3001\u5929\u6076\u4e8c\u4eba\u6597\u5f97\u6b63\u9163\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u8eab\u6000\u7edd\u5b66\uff0c\u5404\u6709\u5947\u80fd\uff0c\u867d\u7136\u4ea4\u624b\u4e0d\u8fc7\u51e0 \u62db\uff0c\u4f46\u6bcf\u4e00\u62db\u90fd\u662f\u9669\u5230\u4e86\u6781\u70b9\u3002\u4ed6\u4eec\u90fd\u5728\u5168\u795e\u8d2f\u6ce8\uff0c\u5bf9\u4ed8\u5bf9\u65b9\uff0c\u6839\u672c\u5c31\u542c\u4e0d\u5230\u5927\u6c57\u8bf4\u4e9b \u4ec0\u4e48\uff0c\u4e5f\u4e0d\u77e5\u9053\u9ebb\u7ffc\u8d5e\u4e0e\u6070\u514b\u56fe\u662f\u5949\u547d\u6765\u7684\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u6076\u9053\u4eba\u6b63\u5728\u65bd\u7528\u8150\u9aa8\u6bd2\u638c\u7684\u795e\u529f\uff0c\u5c06\u83e9\u63d0\u4e0a\u4eba\u8feb\u5f00\u4e24\u6b65\uff0c\u9661\u7136\u95f4\u5ffd\u89c9\u8111\u540e\u98ce \u751f\uff0c\u77e5\u662f\u6709\u4eba\u88ad\u5230\uff0c\u5929\u6076\u9053\u4eba\u6012\u9053\uff1a\u201c\u5478\uff0c\u597d\u4e0d\u8981\u8138\uff01\u201d\u53cd\u624b\u4e00\u638c\uff0c\u4f46\u542c\u5230\u201c\u7bf7\u201d\u7684\u4e00 \u58f0\uff0c\u6070\u514b\u56fe\u90a3\u6c34\u725b\u822c\u96c4\u58ee\u7684\u8eab\u8eaf\uff0c\u7adf\u81ea\u5e94\u58f0\u98de\u8d77\uff0c\u8dcc\u51fa\u4e86\u4e00\u4e08\u5f00\u5916\uff01\u9ebb\u7ffc\u8d5e\u662f\u5410\u8c37\u6d51\u7684 \u7b2c\u4e00\u540d\u6b66\u58eb\uff0c\u6b66\u529f\u5341\u5206\u4e86\u5f97\uff0c\u4ed6\u4f7f\u51fa\u6454\u8de4\u7edd\u6280\uff0c\u811a\u5c16\u4e00\u8e2e\uff0c\u4e0a\u8eab\u4e00\u4ef0\uff0c\u5c06\u5929\u6076\u9053\u4eba\u7684\u624b \u8098\u67b6\u4f4f\uff0c\u4f46\u5929\u6076\u9053\u4eba\u4f7f\u4e86\u201c\u5343\u65a4\u5760\u201d\u7684\u91cd\u8eab\u6cd5\uff0c\u9ebb\u7ffc\u8d5e\u7eca\u4ed6\u4e0d\u5012\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46 \u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5929\u6076\u9053\u4eba\u7684\u9f99\u888d\u5403\u4ed6\u4e00\u722a\u6293\u88c2\uff0c\u9ebb\u7ffc\u8d5e\u6b63\u8981\u64d2\u4ed6\uff0c\u5ffd\u89c9\u4e00\u80a1\u8165\u6c14\uff0c \u51b2\u4e0a\u5fc3\u5934\uff0c\u9ebb\u7ffc\u8d5e\u5934\u6655\u76ee\u7729\uff0c\u80f8\u53e3\u80c0\u95f7\uff0c\u767b\u65f6\u5168\u8eab\u9ebb\u8f6f\uff0c\u5929\u6076\u9053\u4eba\u4e00\u4e2a\u201c\u8e6c\u811a\u201d\uff0c\u53cd\u8eab \u8e22\u51fa\uff0c\u201c\u5495\u54af\u201d\u4e00\u58f0\uff0c\u9ebb\u7ffc\u8d5e\u90a3\u7626\u957f\u7684\u8eab\u8eaf\uff0c\u50cf\u4e00\u6839\u6728\u5934\u4f3c\u7684\uff0c\u4e5f\u5012\u5728\u5730\u4e0b\u4e86\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u56de\u5934\u4e00\u770b\uff0c\u8ba4\u51fa\u9ebb\u7ffc\u8d5e\u4e0e\u6070\u514b\u56fe\u4e8c\u4eba\uff0c\u9ebb\u7ffc\u8d5e\u4e5f\u8fd8\u7f62\u4e86\uff0c\u6070\u514b\u56fe\u662f\u5927\u6c57\u7684 \u536b\u58eb\u961f\u957f\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u8fd9\u65f6\u83e9\u63d0\u4e0a\u4eba\u53c8\u5df2\u6251\u4e86\u4e0a\u6765\uff0c\u53ea\u542c\u5f97\u83e9\u63d0\u4e0a\u4eba\u559d\u9053\uff1a\u201c\u90fd \u4e0d\u8981\u4e0a\u6765\uff0c\u6211\u975e\u628a\u8fd9\u725b\u9f3b\u5b50\u62ff\u4e0b\u6765\u4e0d\u53ef\uff01\u201d\u539f\u6765\u83e9\u63d0\u4e0a\u4eba\u4e5f\u4e0d\u77e5\u9053\u9ebb\u3001\u6070\u4e8c\u4eba\u662f\u6765\u529d\u67b6 \u7684\u8fd8\u662f\u6765\u5e2e\u624b\u7684\uff0c\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u5b9e\u4e0d\u613f\u6709\u4eba\u5e2e\u624b\uff0c\u521a\u624d\u65e0\u6076\u9053\u4eba\u9a82\u4ed6\u4ee5\u591a\u4e3a\u80dc\uff0c\u5df2\u662f\u5927 \u5927\u7684\u4f24\u4e86\u4ed6\u7684\u9762\u5b50\uff0c\u540c\u65f6\u4ed6\u4e5f\u4e0d\u613f\u6709\u4eba\u529d\u67b6\uff0c\u6240\u4ee5\u4ed6\u559d\u4f17\u4eba\u4e0d\u8981\u4e0a\u6765\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u5fc3\u5934\u4e00\u51c9\uff0c\u60f3\u9053\uff1a\u201c\u6070\u514b\u56fe\u4ed6\u4e5f\u4e0a\u6765\uff0c\u96be\u9053\u662f\u5927\u6c57\u7591\u5fc3\u6211\u4e86\uff1f\u201d\u4f46\u83e9\u63d0\u4e0a \u4eba\u653b\u52bf\u6781\u731b\uff0c\u5df2\u4e0d\u5bb9\u4ed6\u4ed4\u7ec6\u601d\u91cf\uff0c\u53ea\u597d\u51dd\u795e\u5e94\u4ed8\u3002 \u3000\u3000\u5927\u6c57\u4e00\u770b\uff0c\u53ea\u89c1\u6070\u514b\u56fe\u5728\u5730\u4e0a\u54fc\u54fc\u537f\u537f\uff0c\u9762\u76ee\u9752\u80bf\uff0c\u5c1a\u672a\u722c\u5f97\u8d77\u6765\uff0c\u4f46\u4ed6\u8fd8\u54fc\u5f97\u51fa \u58f0\uff0c\u9ebb\u7ffc\u8d5e\u5374\u4f3c\u6b7b\u5c38\u822c\u633a\u5728\u5730\u4e0a\uff0c\u9762\u8272\u7600\u9ed1\uff0c\u773c\u8033\u9f3b\u53e3\u90fd\u6ef4\u51fa\u8840\u6c34\uff0c\u663e\u7136\u662f\u4e2d\u4e86\u5929\u6076\u9053 \u4eba\u7684\u6bd2\u638c\uff0c\u6d3b\u4e0d\u6210\u4e86\u3002 \u3000\u3000\u5927\u6c57\u56e0\u4e3a\u5929\u6076\u9053\u4eba\u9002\u624d\u6bd2\u6b7b\u9f99\u722a\u6811\uff0c\u672c\u6765\u5c31\u5df2\u8ba8\u538c\u4e86\u4ed6\uff0c\u8fd9\u65f6\u89c1\u4e24\u4e2a\u5fc3\u7231\u7684\u6b66\u58eb\u53c8 \u88ab\u4ed6\u6240\u4f24\uff0c\u66f4\u4e3a\u607c\u6012\uff0c\u6b63\u60f3\u7ffb\u9762\uff0c\u4e0b\u4ee4\u64d2\u4ed6\uff0c\u5ffd\u542c\u5f97\u5b88\u95e8\u7684\u6b66\u58eb\u5927\u58f0\u53eb\u9053\uff1a\u201c\u767e\u4f18\u4e0a\u4eba \u5230\u6765\u8c12\u89c1\u5927\u6c57\uff01\u201d \u3000\u3000\u4f46\u89c1\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u548c\u5c1a\uff0c\u62ab\u7740\u7d2b\u7ea2\u8272\u7684\u8888\u88df\uff0c\u5341\u5206\u60f9\u4eba\u6ce8\u76ee\uff0c\u6df7\u4e71\u4e2d\u5bab\u6bbf\u672c\u6765\u6324 \u6ee1\u4e86\u4eba\uff0c\u4f46\u767e\u4f18\u4e0a\u4eba\u8eab\u5f62\u6240\u5230\u4e4b\u5904\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u4f38\u624b\u63a8\u5f00\u963b\u8def\u7684\u4eba\uff0c\u53e6\u4e00\u4e9b\u4eba\u4fbf\u4f3c\u6f6e\u6c34\u4e00 \u822c\uff0c\u7eb7\u7eb7\u540e\u9000\uff01\u6709\u4e00\u4e9b\u672c\u9886\u7a0d\u5f31\u7684\u66f4\u8dcc\u5012\u5730\u4e0a\uff0c\u663e\u7136\u4ed6\u8fd8\u6765\u4e0d\u53ca\u8ba9\u8def\uff0c\u4fbf\u7ed9\u767e\u4f18\u4e0a\u4eba\u53d1 \u51fa\u7684\u4e00\u80a1\u65e0\u5f62\u6f5c\u529b\u6240\u63a8\u5012\u4e86\u3002\u8fd9\u79cd\u6700\u4e0a\u4e58\u7684\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u5185\u529f\uff0c\u8fde\u8c37\u795e\u7fc1\u590f\u4faf\u575a\u8fd9\u822c \u4eba\u770b\u4e86\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u5403\u60ca\uff01 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u5e76\u4e0d\u4e3e\u6b65\u98de\u9a70\uff0c\u4f46\u6643\u773c\u4e4b\u95f4\uff0c\u4fbf\u5df2\u5230\u4e86\u573a\u5fc3\uff0c\u8fd9\u65f6\u5929\u6076\u9053\u4eba\u6b63\u8981\u65bd\u5c55\u6740\u624b \u6bd2\u62db\uff0c\u638c\u631f\u8165\u98ce\uff0c\u5411\u83e9\u63d0\u4e0a\u4eba\u731b\u88ad\u3002\u8fd9\u4e24\u5927\u9ad8\u624b\uff0c\u5168\u529b\u4e89\u6301\uff0c\u9664\u4e86\u6ce8\u76ee\u5bf9\u65b9\u4e4b\u5916\uff0c\u5bf9\u65c1 \u8fb9\u7684\u4e00\u5207\uff0c\u5f53\u771f\u662f\u5230\u4e86\u89c6\u800c\u4e0d\u89c1\u3001\u542c\u800c\u4e0d\u95fb\u7684\u5730\u6b65\uff0c\u767e\u5fe7\u4e0a\u4eba\u5230\u6765\uff0c\u573a\u4e2d\u90a3\u4e48\u54c4\u52a8\uff0c\u4ed6 \u4eec\u7adf\u4f3c\u4e1d\u6beb\u672a\u89c9\u3002 \u3000\u3000\u8fd9\u65f6\u83e9\u63d0\u4e0a\u4eba\u4ea6\u5df2\u611f\u5230\u80f8\u53e3\u80c0\u95f7\uff0c\u8165\u6c14\u653b\u5fc3\uff0c\u77e5\u9053\u5982\u6b64\u4e0b\u53bb\uff0c\u7eb5\u7136\u4e0d\u88ab\u5929\u6076\u9053\u4eba\u7684 \u6bd2\u638c\u89e6\u53ca\uff0c\u4ea6\u5c06\u5fc5\u8d25\u65e0\u7591\u3002\u5c31\u5728\u5929\u6076\u9053\u4eba\u65bd\u5c55\u6bd2\u62db\u4e4b\u9645\uff0c\u4ed6\u4e00\u54ac\u7259\u6839\uff0c\u4e5f\u7528\u4e86\u5168\u529b\uff0c\u5927 \u6253\u51fa\u624b\uff0c\u4f46\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u638c\u98ce\u8d77\u5904\uff0c\u684c\u5012\u6905\u52d2\uff0c\u6bbf\u6444\u9707\u52a8\uff0c\u638c\u529b\u4e4b\u5f3a\uff0c\u6709\u5982\u6392\u5c71 \u5012\u6d77\uff0c\u773c\u770b\u56db\u638c\u76f8\u4ea4\uff0c\u4e24\u5927\u9ad8\u624b\u3002\u4fbf\u5c06\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4e24\u5927\u9ad8\u624b\u9aa4\u7136\u611f\u5230\u8eab\u5b50\u4e00\u8f7b\uff0c\u7acb\u8db3\u4e0d\u7a33\u3002\u4f46\u89c1\u767e\u5fe7\u4e0a\u4eba\u957f\u8896\u4e00\u6325\uff0c \u5f53\u4e2d\u4e00\u62c2\uff0c\u5929\u6076\u9053\u4eba\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u83e9\u63d0\u4e0a\u4eba\u4e5f\u8e09\u8e09\u8dc4\u8dc4\u7684\u9000\u4e86\u516d\u4e03\u6b65\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\u3002 \u8fd9\u4e00\u4e0b\uff0c\u5168\u573a\u9ad8\u624b\uff0c\u5c3d\u90fd\u9707\u52a8\uff0c\u8fde\u7b26\u4e0d\u7591\u4e5f\u8038\u7136\u52a8\u5bb9\uff0c\u7b77\u5b50\u5728\u684c\u4e0a\u4e00\u6572\uff0c\u8d5e\u9053\uff1a\u201c\u5999\u554a\uff0c \u5999\u554a\uff01\u8fd9\u8001\u548c\u5c1a\u5f53\u771f\u662f\u540d\u4e0d\u865a\u4f20\uff01\u201d\u8981\u77e5\u5929\u6076\u83e9\u63d0\u4e8c\u4eba\u7684\u638c\u529b\uff0c\u8db3\u53ef\u5f00\u7889\u88c2\u77f3\uff0c\u5982\u4eca\u7adf \u88ab\u767e\u5fe7\u4e0a\u4eba\u8f7b\u8f7b\u4e00\u62c2\uff0c\u4fbf\u5c06\u8fd9\u4e24\u5927\u9ad8\u624b\u7684\u638c\u529b\u5c3d\u90fd\u5316\u5f00\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u5b9e\u5728\u6bd4\u4ed6\u521a\u624d\u663e\u9732 \u7684\u90a3\u4e00\u624b\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u8fd8\u66f4\u60ca\u4eba\uff01 \u3000\u3000\u53ea\u542c\u5f97\u767e\u5fe7\u4e0a\u4eba\u65a5\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u8fd9\u6837\u62fc\u547d\u5e72\u4e48\uff1f\u201d\u5929\u6076\u9053\u4eba\u751f\u5e73\u53ea\u754f\u60e7\u4e24\u4e2a \u4eba\uff0c\u4e00\u4e2a\u662f\u4f18\u4e91\u795e\u5c3c\uff0c\u4e00\u4e2a\u4fbf\u662f\u767e\u5fe7\u4e0a\u4eba\uff0c\u5bf9\u767e\u5fe7\u4e0a\u4eba\u7684\u65a5\u8d23\uff0c\u5f53\u7136\u4e0d\u6562\u53d1\u6012\uff0c\u6151\u6151\u61e6 \u61e6\u7684\u5206\u8fa9\u9053\uff1a\u201c\u4ed6\uff0c\u4ed6\u80e1\u8d56\u6211\u6bd2\u6b7b\u9b4f\u738b\u4f7f\u8005\u3002\u201d\u201c\u9b4f\u738b\u201d\u662f\u6b66\u627f\u55e3\u7684\u5c01\u53f7\uff0c\u767e\u4f18\u4e0a\u4eba\u770b \u4e86\u6a2a\u5728\u5730\u4e0a\u7684\u5c01\u795d\u4e8c\u4eba\u7684\u5c38\u4f53\uff0c\u8bf4\u9053\uff1b\u201c\u4ed6\u4eec\u4fbf\u662f\u6b66\u627f\u55e3\u7684\u4f7f\u8005\u5417\uff1f\u54e6\uff0c\u679c\u7136\u662f\u4e2d\u6bd2\u6b7b \u7684\uff01\u8fd9\u4e2a\u6bd2\u53ef\u662f\u6709\u70b9\u5947\u602a\uff01\u201d \u3000\u3000\u65e9\u4e9b\u65f6\u5019\uff0c\u7a81\u53a5\u5927\u6c57\u8981\u83e9\u63d0\u4e0a\u4eba\u5c06\u201c\u56fd\u5e08\u201d\u7684\u5c01\u53f7\u8ba9\u7ed9\u767e\u5fe7\u4e0a\u4eba\u4e4b\u65f6\uff0c\u83e9\u63d0\u4e0a\u4eba\u672c \u6765\u53ea\u662f\u53e3\u670d\u800c\u5fc3\u4e0d\u670d\uff0c\u5982\u4eca\u89c1\u767e\u4f18\u4e0a\u4eba\u7684\u529f\u529b\u8fdc\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e5f\u670d\u4e86\u3002\u5bf9\u767e\u5fe7 \u4e0a\u4eba\u7684\u65a5\u8d23\uff0c\u4e5f\u4e0d\u6562\u53d1\u6012\uff0c\u4f4e\u58f0\u8fa9\u9053\uff1a\u201c\u662f\u5440\uff0c\u4e0a\u4eba\uff0c\u4f60\u4e5f\u770b\u51fa\u6765\u4e86\uff1f\u4f60\u770b\u8fd9\u4e24\u4e2a\u4f7f\u81e3 \u6b7b\u5f97\u8fd9\u6837\u5947\u602a\uff0c\u5929\u4e0b\u4f7f\u6bd2\u7684\u9ad8\u624b\u80fd\u6709\u51e0\u4eba\uff1f\u6559\u6211\u600e\u80fd\u4e0d\u731c\u7591\u662f\u4ed6\uff1f\u201d \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u4e14\u522b\u5435\u95f9\uff0c\u5f85\u6211\u518d\u53bb\u4ed4\u7ec6\u5ba1\u89c6\uff0c\u5929\u6076\uff0c\u4f60\u5148\u628a\u89e3\u836f\u53d6\u51fa\u6765\uff0c\u628a \u9ebb\u7ffc\u8d5e\u6551\u4e86\u3002\u201d \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u8c12\u89c1\u5927\u6c57\uff0c\u542c\u5927\u6c57\u8bf4\u4e86\u5c01\u795d\u4e8c\u4eba\u66b4\u6bd9\u7684\u60c5\u5f62\uff0c\u4ed6\u81ea\u5df1\u53c8\u518d\u53bb\u4ed4\u7ec6\u5ba1\u89c6\uff0c\u770b \u4e86\u53c8\u770b\uff0c\u8138\u4e0a\u9732\u51fa\u975e\u5e38\u5947\u602a\u7684\u795e\u8272\uff0c\u773c\u5149\u5411\u5750\u5728\u9996\u5e2d\u4e4b\u4eba\u63a0\u8fc7\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u662f\u8c01\u4e0b\u7684 \u6bd2\u624b\uff1f\u6709\u8fd9\u6837\u9ad8\u660e\u7684\u672c\u9886\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6562\u7ad9\u51fa\u6765\uff1f\u201d \u3000\u3000\u7b26\u4e0d\u7591\u563b\u563b\u4e00\u7b11\uff0c\u634f\u4e86\u590f\u4faf\u575a\u7684\u624b\u5fc3\u4e00\u4e0b\uff0c\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u5374\u53e6\u5916\u6709\u4e00\u4e2a\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c \u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u8fd9\u4e2a\u4eba\u5374\u662f\u767e\u5fe7\u4e0a\u4eba\u7684\u5927\u5f1f\u5b50\u9633\u592a\u534e\uff01 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u662f\u4f60\u5e72\u7684\u5417\uff1f\u201d\u9633\u592a\u534e\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4f46\u6211\u77e5\u9053\u5728\u8fd9\u5ea7\u5bab\u6bbf \u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u4eba\u4e00\u5b9a\u77e5\u9053\u662f\u8c01\u5e72\u7684\uff0c\u6211\u8981\u5bc6\u594f\u5927\u6c57\u3002\u201d\u767e\u4f18\u4e0a\u4eba\u548c\u4ed6\u540c\u81f3\u5927\u6c57\u8ddf\u524d\uff0c\u9633 \u592a\u534e\u9053\uff1a\u201c\u548c\u7a0b\u8fbe\u82cf\u540c\u6765\u7684\u90a3\u4e2a\u4e0a\u5b98\u654f\uff0c\u4e43\u662f\u674e\u9038\u7684\u5316\u540d\uff0c\u4ed6\u4e0d\u6388\u53d7\u5927\u6c57\u7684\u9080\u8bf7\uff0c\u5374\u5077 \u5077\u7684\u6539\u5bb9\u6613\u8c8c\u800c\u6765\uff0c\u663e\u7136\u662f\u5b58\u5fc3\u548c\u5927\u6c57\u654c\u5bf9\uff0c\u8fd9\u4e24\u4e2a\u4e2d\u56fd\u4f7f\u81e3\u5373\u7b97\u4e0d\u662f\u4ed6\u6bd2\u6b7b\u7684\uff0c\u4e5f\u5fc5 \u5b9a\u662f\u4ed6\u7684\u515a\u7fbd\u6240\u4e3a\u3002\u8bf7\u95ee\u5927\u6c57\u5982\u4f55\u5904\u7f6e\uff1f\u201d \u3000\u3000\u539f\u6765\u7a0b\u8fbe\u82cf\u4e0e\u9633\u592a\u534e\u4e24\u4eba\u65e9\u5df2\u5bf9\u674e\u9038\u8d77\u4e86\u7591\u5fc3\uff0c\u4f46\u8fd8\u672a\u786e\u77e5\u4ed6\u7684\u8eab\u4efd\uff0c\u5c01\u795d\u4e8c\u4eba\u5219 \u8ba4\u51fa\u4e86\u201c\u4e0a\u5b98\u654f\u201d\u5c31\u662f\u674e\u9038\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u8981\u4eb2\u81ea\u5411\u5927\u6c57\u63ed\u9732\uff0c\u4ee5\u4fbf\u9080\u529f\uff0c\u6545\u6b64\u4e8b\u5148\u4e5f\u7792\u7740 \u7a0b\u9633\u4e8c\u4eba\u3002\u60f3\u4e0d\u5230\u5c01\u7267\u91ce\u4e00\u8bf4\u201c\u674e\u9038\u201d\u4e8c\u5b57\uff0c\u4fbf\u4e2d\u6bd2\u6b7b\u4ea1\uff0c\u8fd9\u81ea\u4fbf\u5b9c\u4e86\u7a0b\u9633\u4e8c\u4eba\uff0c\u4ed6\u542c \u4e86\u5c01\u7267\u6240\u8bf4\u7684\u8bdd\uff0c\u5f53\u7136\u7acb\u5373\u4fbf\u731c\u5230\u4e86\u201c\u4e0a\u5b98\u654f\u201d\u4fbf\u662f\u674e\u9038\u4e86\u3002 \u3000\u3000\u5927\u6c57\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u674e\u9038\u80c6\u5b50\u771f\u5927\uff01\u201d\u5f53\u4e0b\u964d\u65e8\u8bf4\u9053\uff1a\u201c\u53ea\u8bb8\u751f\u64d2\uff0c\u4e0d\u8bb8\u4f24 \u4ed6\u6027\u547d\uff01\u201d\u9633\u592a\u534e\u5411\u7a0b\u8fbe\u82cf\u6253\u4e86\u4e00\u4e2a\u6697\u53f7\uff0c\u7a0b\u8fbe\u82cf\u4fbf\u63d0\u8d77\u70df\u6597\uff0c\u7ad9\u8d77\u8eab\u6765\u3002\u5bab\u4e2d\u7b75\u5f00\u767e \u5e2d\uff0c\u9633\u592a\u534e\u4e0e\u5927\u6c57\u7684\u8a00\u8bed\uff0c\u9664\u4e86\u4e0e\u5927\u6c57\u76f8\u4e61\u5f00\u7684\u9996\u5e2d\u8bf7\u4eba\u4e4b\u5915\u5176\u4ed6\u7684\u4eba\uff0c\u90fd\u542c\u4e0d\u89c1\uff0c\u53ea \u89c1\u5927\u6c57\u795e\u8272\u7d27\u5f20\uff0c\u4eba\u4eba\u5fc3\u4e2d\u7eb3\u7f55\u3002 \u3000\u3000\u674e\u9038\u6b63\u81ea\u5fc3\u4e71\u5982\u9ebb\uff0c\u5ffd\u89c1\u7a0b\u8fbe\u82cf\u5411\u4ed6\u8d70\u6765\uff0c\u5fc3\u4e2d\u5df2\u77e5\u4e0d\u5999\uff0c\u5f3a\u81ea\u9547\u5b9a\uff0c\u7ad9\u8d77\u6765\u8fce\u63a5\uff0c \u95ee\u9053\uff1a\u201c\u7a0b\u5e2e\u4e3b\u6709\u4f55\u5429\u5490\uff1f\u201d\u7a0b\u8fbe\u82cf\u7aef\u8d77\u4e86\u4e00\u9152\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u592b\u6709\u773c\u65e0\u73e0\uff0c\u4e0d\u8bc6\u6bbf\u4e0b\uff0c \u7279\u5730\u966a\u7f6a\u6765\uff01\u201d\u674e\u9038\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u7a0b\u8001\u5e2e\u4e3b\u559d\u9189\u4e86\u4e48\uff1f\u201d\u7a0b\u8fbe\u82cf\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u4eca\u65e5\u5e78\u8bc6\u6bbf\u4e0b\uff0c\u867d\u9189\u4f55\u8f9e\uff1f\u6765\uff0c\u6765\uff01\u6211\u5148\u5411\u6bbf\u4e0b\u656c\u9152\u4e00\u676f\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u4f46\u89c1\u4ed6\u4e3e\u676f\u996e\u5c3d\uff0c\u7a81\u7136\u5f20\u53e3\u4e00\u55b7\uff0c\u4e00\u80a1\u9152\u6d6a\u597d\u4f3c\u767d\u6d6a\u822c\u5c04\u51fa\uff0c\u5411\u674e\u9038\u7684\u201c\u592a\u9633\u7a74\u201d\u51b2\u6765\uff0c \u9152\u676f\u98de\u51fa\uff0c\u6253\u674e\u9038\u81c2\u5f2f\u7684\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u7a0b\u8fbe\u82cf\u6253\u7a74\u7684\u529f\u592b\u51fa\u795e\u5165\u5316\uff0c\u8fd9\u4e24\u4e0b\u6b63\u662f\u4ed6\u7684\u5e73 \u751f\u7edd\u6280\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u201d\u4e00\u58f0\uff0c\u53e6\u4e00\u9152\u676f\u6b63\u4e2d\u674e\u9038\u7684\u81c2\u5f2f\uff0c\u88c2\u6210\u51e0\u7247\uff0c\u53e6\u4e00\u80a1\u9152\u6d6a\u4e5f\u5c04\u4e2d \u674e\u9038\u7684\u989d\u5934\uff0c\u674e\u9038\u4e00\u4e2a\u7405\u8dc4\uff0c\u767b\u65f6\u8dcc\u5012\u3002 \u3000\u3000\u5357\u5bab\u5c1a\u7684\u5e2d\u4f4d\u4e0e\u674e\u9038\u76f8\u90bb\uff0c\u89c1\u674e\u9038\u7a81\u7136\u88ab\u7a0b\u8fbe\u82cf\u6253\u5012\uff0c\u5927\u60ca\u5931\u8272\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u4ed6 \u786e\u662f\u674e\u9038\uff0c\u4f46\u4ed6\u5316\u540d\u800c\u6765\uff0c\u5374\u6709\u56e0\u7531\uff0c\u4ed6\u662f\u6295\u5954\u5927\u6c57\u6765\u7684\uff0c\u5927\u54e5\uff0c\u4f60\u8bef\u4f1a\u4e86\uff01\u201d\u7a0b\u8fbe\u82cf \u559d\u9053\uff1a\u201c\u8822\u4e1c\u897f\uff0c\u4f60\u8bc6\u5f97\u751a\u4e48\uff0c\u4ed6\u662f\u6363\u4e71\u6765\u7684\uff0c\u8fde\u4f60\u4e5f\u8131\u4e0d\u4e86\u5173\u7cfb\uff0c\u4f60\u8fd8\u6562\u4e3a\u4ed6\u6c42\u60c5\uff01\u201d \u4e00\u638c\u63a8\u5f00\u4e86\u5357\u5bab\u5c1a\uff0c\u5f2f\u4e0b\u8170\u6765\uff0c\u4fbf\u8981\u64d2\u62ff\u674e\u9038\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u53ea\u9053\u674e\u9038\u5df2\u88ab\u4ed6\u6253\u4e2d\u7a74\u9053\uff0c\u8fd9\u4e00\u4e0b\u8fd8\u4e0d\u662f\u624b\u5230\u62ff\u6765\uff0c\u90a3\u77e5\u4ed6\u7684\u624b\u6307\u5c1a\u672a\u89e6\u53ca\uff0c \u674e\u9038\u7a81\u7136\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8dc3\u4e86\u8d77\u6765\uff0c\u4f46\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u7a0b\u8fbe\u82cf\u51b7\u4e0d\u53ca\u9632\uff0c \u9976\u662f\u4ed6\u672c\u9886\u9ad8\u5f3a\uff0c\u95ea\u907f\u5f97\u5feb\uff0c\u53f3\u624b\u7684\u98df\u6307\u5df2\u88ab\u674e\u9038\u7684\u5b9d\u5251\u524a\u4e86\uff01 \u3000\u3000\u539f\u6765\u674e\u9038\u7684\u672c\u9886\u867d\u7136\u5c1a\u4e0d\u53ca\u7a0b\u8fbe\u82cf\uff0c\u4f46\u4ed6\u6240\u5b66\u7684\u5374\u662f\u6b63\u5b97\u5185\u529f\uff0c\u7a0b\u8fbe\u82cf\u88ab\u5357\u5bab\u5c1a\u963b \u4e86\u4e00\u963b\uff0c\u867d\u7136\u4e0d\u8fc7\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u4f46\u674e\u9038\u5df2\u8d81\u6b64\u65f6\u673a\uff0c\u8fd0\u6c14\u51b2\u5173\uff0c\u81ea\u884c\u89e3\u5f00\u4e86\u7a74\u9053\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u88ab\u524a\u53bb\u4e86\u4e8c\u53ea\u624b\u6307\uff0c\u52c3\u7136\u5927\u6012\uff0c\u70df\u6597\u4e00\u78d5\uff0c\u7acb\u523b\u4fbf\u7167\u674e\u9038\u7684\u9876\u95e8\u6253\u4e0b\uff0c\u674e\u9038 \u6a2a\u5251\u4e00\u5c01\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u7a0b\u8fbe\u82cf\u8fd9\u652f\u70df\u634d\u4e43\u662f\u767e\u70bc\u91d1 \u94a2\uff0c\u52a0\u4e0a\u4ed6\u6df1\u539a\u7684\u5185\u529f\uff0c\u674e\u9038\u7684\u5b9d\u5251\u524a\u5b83\u4e0d\u65ad\u3002 \u3000\u3000\u5357\u5b98\u5c1a\u6025\u5f97\u6ee1\u5934\u5927\u6c57\uff0c\u5374\u662f\u4e0d\u6562\u963b\u62e6\uff0c\u90a3\u73ed\u8d74\u5bb4\u7684\u6b66\u58eb\u4eec\u4e0d\u77e5\u4ed6\u4eec\u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c \u5728\u8fd9\u68ee\u4e25\u7684\u5bab\u6bbf\u4e4b\u4e2d\uff0c\u81ea\u662f\u4e0d\u6562\u968f\u4fbf\u63d2\u624b\uff0c\u89c1\u4ed6\u4eec\u6253\u5f97\u6fc0\u70c8\uff0c\u7eb7\u7eb7\u907f\u5f00\uff0c\u62b1\u7740\u770b\u70ed\u95f9\u7684 \u5fc3\u60c5\uff0c\u770b\u4ed6\u4eec\u6076\u6597\u3002 \u3000\u3000\u6b66\u7384\u971c\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u674e\u9038\u7adf\u4e5f\u6562\u6765\u53c2\u52a0\u8fd9\u4e2a\u6b66\u58eb\u5927\u4f1a\uff0c\u521a\u624d\u542c\u5f97\u9633\u592a\u534e\u7684\u5bc6\u594f\uff0c\u5403 \u60ca\u4e0d\u5c0f\uff0c\u4f46\u8fd8\u4e0d\u6562\u76f8\u4fe1\uff0c\u8fd9\u65f6\u4e00\u89c1\u674e\u9038\u52a8\u624b\uff0c\u8ba4\u51fa\u4e86\u4ed6\u7684\u90a3\u628a\u5b9d\u5251\uff0c\u4e5f\u8ba4\u51fa\u4e86\u4ed6\u7684\u90a3\u624b \u5ce8\u7709\u5251\u6cd5\uff0c\u679c\u7136\u771f\u662f\u674e\u9038\uff01\u5979\u672c\u8981\u907f\u5f00\u674e\u9038\u7684\uff0c\u4f46\u7ec8\u4e8e\u8fd8\u662f\u5728\u8fd9\u91cc\u89c1\u9762\u4e86\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u7684\u6253\u7a74\u529f\u592b\u51fa\u795e\u5165\u5316\uff0c\u4f46\u89c1\u4ed6\u7684\u90a3\u53ea\u70df\u634d\u5929\u77eb\u5982\u9f99\uff0c\u4e58\u9699\u5373\u5165\uff0c\u4e0d\u987b\u591a\u4e45\uff0c \u674e\u9038\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6b66\u7384\u971c\u6697\u6697\u53eb\u82e6\uff0c\u4f46\u5979\u73b0\u5728\u4e43\u662f\u738b\u5983\u8eab\u4efd\uff0c\u5728\u5927\u6c57\u8ddf\u524d\uff0c\u6beb\u65e0\u529e\u6cd5\u3002 \u53ea\u6709\u6781\u529b\u6291\u5236\u81ea\u5df1\uff0c\u4e0d\u6562\u53eb\u5927\u6c57\u77a7\u51fa\u6765\u3002 \u3000\u3000\u5979\u867d\u7136\u6781\u529b\u538b\u5236\uff0c\u795e\u8272\u4e4b\u95f4\u4ecd\u662f\u63a9\u9970\u4e0d\u4f4f\u3002\u5927\u6c57\u597d\u751f\u8be7\u5f02\uff0c\u671b\u7740\u5979\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c \u4f60\u600e\u4e48\u5566\uff1f\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u4e0d\u5fc5\u5bb3\u6015\uff0c\u4ed6\u4e0d\u4f1a\u6253\u4e0a\u8fd9\u513f\u6765\uff0c\u6211\u662f\u60f3\u770b\u770b\u8fd9\u4e24\u4e2a\u4eba\u7684\u672c\u9886\uff0c \u4f60\u5bb3\u6015\uff0c\u6211\u5c31\u53eb\u83e9\u63d0\u4e0a\u4eba\u51fa\u53bb\uff0c\u5c06\u90a3\u4e2a\u674e\u9038\u8d76\u5feb\u64d2\u4e0b\uff0c\u7ed3\u675f\u4e86\u8fd9\u573a\u6bd4\u8bd5\u5427\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u7a0d\u5b9a\u5fc3\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u751a\u4e48\uff0c\u6211\u4e0d\u662f\u5bb3\u6015\uff0c\u6211\u662f\u6709\u70b9\u7591\u60d1\uff01\u201d\u5927\u6c57\u9053\uff1a\u201c\u600e \u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u521a\u624d\u90a3\u4e2a\u4e2d\u56fd\u4f7f\u81e3\uff0c\u4f3c\u4e4e\u8bf4\u4ed6\u662f\u6b66\u5219\u5929\u7684\u4ec0\u4e48\u4eba\uff0c\u6b66\u5219\u5929\u4e0d\u662f\u4e2d\u56fd \u7684\u5973\u7687\u5e1d\u5417\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u662f\u5440\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u521a\u624d\u53c8\u8bf4\u8fd9\u4e2a\u674e\u9038\u4e43\u662f\u5927\u5510\u7684\u738b\u5b59\uff0c \u90a3\u4e48\u4ed6\u5e94\u8be5\u4e0e\u6b66\u5219\u5929\u4f5c\u5bf9\u624d\u662f\uff0c\u600e\u4e48\u4e2d\u56fd\u4f7f\u81e3\u53c8\u8bf4\u4ed6\u662f\u6b66\u5219\u5929\u7684\u4eba\uff1f\u201d \u3000\u3000\u5176\u5b9e\u5c01\u7267\u91ce\u4e34\u6b7b\u4e4b\u524d\u6240\u8bf4\u7684\u90a3\u53e5\u8bdd\uff0c\u6307\u7684\u4e43\u662f\u6b66\u7384\u971c\uff0c\u4e5f\u6b63\u662f\u56e0\u4e3a\u4ed6\u7a81\u7136\u8ba4\u51fa\u6b66\u7384 \u971c\uff0c\u60ca\u6050\u8fc7\u5ea6\uff0c\u6240\u4ee5\u672b\u66fe\u628a\u8bdd\u8bf4\u5b8c\uff0c\u4fbf\u5fc3\u810f\u7206\u88c2\u800c\u6b7b\u3002\u6b66\u7384\u971c\u5f53\u7136\u4e5f\u77e5\u9053\u5c01\u7267\u91ce\u8bf4\u7684\u662f \u5979\uff0c\u5979\u6545\u610f\u626f\u5230\u674e\u9038\u8eab\u4e0a\uff0c\u7528\u610f\u6b63\u662f\u8981\u4e3a\u81ea\u5df1\u906e\u63a9\u3002 \u3000\u3000\u54ea\u77e5\u5927\u6c57\u5bf9\u5979\u6ca1\u6709\u7591\u5fc3\uff0c\u5929\u6076\u9053\u4eba\u5374\u65e9\u5df2\u5bf9\u5979\u731c\u7591\u4e86\u3002\u5927\u6c57\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u674e\u9038\u4ed6\u867d \u5728\u5927\u5510\u738b\u5b59\uff0c\u4f46\u4e0d\u80af\u5f52\u987a\u4e8e\u6211\uff0c\u6216\u8005\u771f\u662f\u6b66\u5219\u5929\u6d3e\u6765\u7684\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u6743\u529b\u4e4b\u4e89\uff0c\u96be\u8bf4\u5f97\u5f88\uff0c \u4f60\u770b\u90a3\u6b66\u627f\u55e3\u662f\u6b66\u5219\u5929\u7684\u4f84\u513f\uff0c\u4ed6\u4e0d\u662f\u4e5f\u6d3e\u4e86\u4f7f\u81e3\u6765\u8981\u6211\u5c06\u6765\u6276\u52a9\u4ed6\u505a\u7687\u5e1d\u5417\uff1f\u201d\u6b66\u7384 \u971c\u542c\u51fa\u5927\u6c57\u4e1d\u6beb\u6ca1\u6709\u7591\u5fc3\u5979\uff0c\u653e\u4e0b\u4e86\u5fc3\u5934\u4e00\u5757\u5927\u77f3\uff0c\u6697\u6697\u76d8\u7b97\u7528\u4ec0\u4e48\u65b9\u6cd5\u53ef\u4ee5\u53bb\u6551\u674e\u9038\uff0c \u4f46\u5979\u65e0\u610f\u4e4b\u95f4\uff0c\u773c\u5149\u4e00\u77a5\u3002\u5374\u89c1\u5929\u6076\u9053\u4eba\u4e5f\u6b63\u5728\u7528\u4e00\u79cd\u5947\u5f02\u7684\u76ee\u5149\u770b\u7740\u5979\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u8d8a\u770b\u8d8a\u662f\u8d77\u7591\uff0c\u4ed6\u65e9\u5df2\u770b\u51fa\u6b66\u7384\u971c\u662f\u4e2a\u6781\u6709\u672c\u9886\u7684\u5973\u5b50\uff01\u8fd9\u65f6\u53c8\u542c\u51fa\u5979\u7684 \u58f0\u97f3\u597d\u719f\uff0c\u7adf\u4f3c\u5728\u90a3\u513f\u89c1\u8fc7\u4f3c\u7684\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u5c01\u7267\u91ce\u5206\u660e\u662f\u9762\u5411\u5979\u8bf4\u51fa\u4f60\uff0c\u4f60\u662f \u6b66\u5219\u5929\u7684\uff0c\u7684\u2026\u2026\u201d\u90a3\u51e0\u4e2a\u65ad\u65ad\u7eed\u7eed\u7684\u5b57\u773c\uff0c\u53ef\u60dc\u8fd9\u4e00\u53e5\u8bdd\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4f46\u63e3\u5ea6\u610f\u601d\uff0c\u8fd9 \u4e2a\u4f60\u5b57\u6307\u7684\u4e0d\u5e94\u8be5\u662f\u674e\u9038\uff0c\u800c\u5e94\u8be5\u662f\u8fd9\u4e2a\u738b\u5983\uff0c\u5979\u662f\u6b66\u5219\u5929\u7684\u4ec0\u4e48\u4eba\u5462\uff1f\u662f\u6b66\u5219\u5929\u7684\u5978 \u7ec6\u8fd8\u662f\u4e0e\u6b66\u5219\u5929\u6709\u52fe\u7ed3\u7684\u4eba\uff1f\u8eab\u4efd\u5374\u662f\u7a81\u5458\u4ea4\u5c5e\u56fd\u7684\u4e00\u4e2a\u516c\u4e3b\uff0c\u800c\u4e14\u662f\u5927\u6c57\u65b0\u5a36\u7684\u738b\u5983\u3002 \u8981\u8bf4\u5979\u662f\u6b66\u5219\u5929\u7684\u4ec0\u4e48\u4eba\uff0c\u8fd9\u7b80\u76f4\u662f\u4e0d\u80fd\u60f3\u50cf\u7684\u4e8b\uff01\u201d\u5929\u6076\u9053\u4eba\u867d\u7136\u5927\u80c6\u731c\u7591\uff0c\u5374\u600e\u6837 \u4e5f\u8fd8\u4e0d\u6562\u60f3\u5230\u5979\u4fbf\u662f\u6b66\u7384\u971c\u3002 \u3000\u3000\u6b66\u7384\u971c\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u5411\u5927\u6c57\u8033\u8bed\u9053\uff1a\u201c\u8fd9\u4e2a\u9053\u58eb\u8d3c\u5fd2\u5fd2\u7684\u4e00\u5bf9\u773c\u775b\u76f4\u770b\u7740\u6211\uff0c\u6211\u6709\u70b9 \u5bb3\u6015\u3002\u201d\u5927\u6c57\u4e5f\u6ce8\u610f\u5230\u4e86\uff0c\u4ed6\u5bf9\u5929\u6076\u9053\u4eba\u6bd2\u6b7b\u9f99\u722a\u6811\u4e00\u4e8b\uff0c\u672c\u6765\u5c31\u4e0d\u9ad8\u5174\uff0c\u8fd9\u65f6\u89c1\u4ed6\u5bf9 \u738b\u5983\u8fd9\u6837\u65e0\u793c\uff0c\u66f4\u4e3a\u607c\u6012\uff0c\u4f46\u4ee5\u5927\u6c57\u7684\u8eab\u4efd\uff0c\u53c8\u6b63\u5728\u62db\u7eb3\u80fd\u4eba\u7684\u65f6\u5019\uff0c\u4e00\u65f6\u4e0d\u4fbf\u53d1\u4f5c\uff0c \u4e8e\u662f\u51b7\u51b7\u7684\u779f\u4e86\u5929\u6076\u9053\u4eba\u4e00\u773c\uff0c\u5374\u5bf9\u767e\u5fe7\u4e0a\u4eba\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u53ef\u77a7\u51fa\u4e86\u662f\u4ec0\u4e48\u4eba\u6bd2\u6bd9\u4f7f\u81e3 \u7684\u4e48\uff1f\u54fc\uff0c\u54fc\uff01\u8fd9\u6bd2\u836f\u4e0b\u5f97\u597d\u5389\u5bb3\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4eca\u65e5\u5fc5\u987b\u5148\u67e5\u51fa\u8fd9\u4e0b\u6bd2\u7684\u4eba\uff01\u201d\u8a00\u4e0b\u4e4b \u610f\uff0c\u81ea\u662f\u731c\u7591\u4e0b\u6bd2\u7684\u4fbf\u662f\u5929\u6076\u9053\u4eba\uff0c\u4ed6\u6015\u767e\u5fe7\u4e0a\u4eba\u4e0e\u5929\u6076\u9053\u4eba\u53cb\u597d\uff0c\u4e88\u4ee5\u5e87\u62a4\u3002 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u4e00\u76f4\u5c31\u5728\u5ba1\u67e5\u8fd9\u4e24\u4e2a\u4f7f\u81e3\u7684\u6b7b\u56e0\uff0c\u4ed6\u521a\u624d\u4f0f\u5728\u5730\u4e0b\uff0c\u95fb\u4e86\u5c01\u795d\u4e8c\u4eba\u6d41\u51fa\u6765 \u7684\u8840\u6db2\u7684\u6c14\u5473\uff0c\u8fd9\u65f6\u65b9\u7ad9\u8d77\u8eab\u6765\uff0c\u6b63\u76b1\u7740\u7709\u5934\u601d\u7d22\u3002 \u3000\u3000\u5927\u6c57\u5411\u4ed6\u4e00\u95ee\uff0c\u4ed6\u7a81\u7136\u53cc\u773c\u653e\u51fa\u5149\u8292\uff0c\u5e94\u58f0\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u77a7\u51fa\u6765\u4e86\u3002\u8fd9\u4e24\u4e2a\u4f7f\u81e3 \u4e0d\u662f\u6b7b\u4e8e\u6bd2\u836f\uff01\u201d\u5927\u6c57\u6025\u9053\uff1a\u201c\u4f60\u77a7\u51fa\u6765\u4e86\uff1f\u662f\u8c01\uff1f\u201d\u6070\u514b\u56fe\u4e5f\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4e0d\u662f\u6bd2 \u836f\uff1f\u90a3\u53c8\u600e\u4e48\u4f1a\u516d\u7a8d\u6d41\u8840\uff0c\u5f53\u573a\u66b4\u6bd9\uff1f\u201d \u3000\u3000\u4f46\u89c1\u767e\u5fe7\u4e0a\u4eba\u795e\u8272\u51dd\u91cd\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e14\u5f85\u6211\u5148\u627e\u51fa\u884c\u51f6\u7684\u5229\u5668\u3002\u201d\u7a81\u7136\u628a\u5c01\u7267\u91ce \u7684\u5c38\u4f53\u7ffb\u8f6c\u8fc7\u6765\uff0c\u638c\u5fc3\u5bf9\u7740\u4ed6\u80cc\u5fc3\u7684\u201c\u5927\u690e\u7a74\u201d\u4e00\u6309\uff0c\u53cc\u6307\u62c8\u8d77\u4e86\u4e00\u679a\u4e09\u5bf8\u957f\u77ed\u7684\u6885\u82b1 \u9488\uff0c\u63a5\u7740\u4f9d\u6cd5\u65bd\u4e3a\uff0c\u5728\u795d\u89c1\u7ae0\u7684\u201c\u5927\u690e\u7a74\u201d\u4e5f\u62c8\u8d77\u4e86\u4e00\u679a\u6885\u82b1\u9488\uff0c\u8fd9\u4e00\u4e0b\u81ea\u5927\u6c57\u4ee5\u81f3\u9996 \u5e2d\u8bf8\u4eba\u5747\u662f\u5927\u5403\u4e00\u60ca\uff0c\u5927\u6c57\u60ca\u7684\u662f\u54ea\u4e2a\u65bd\u653e\u6bd2\u9488\u7684\u4eba\u7adf\u662f\u5982\u6b64\u5389\u5bb3\uff0c\u6ee1\u5802\u9ad8\u624b\u4f17\u76ee\u777d\u777d\uff0c \u7adf\u7136\u88ab\u4ed6\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u8fde\u6740\u4e8c\u4eba\uff0c\u8981\u662f\u8fd9\u4e2a\u4eba\u5077\u5077\u7684\u5411\u81ea\u5df1\u5c04\u4e00\u53e3\u6bd2\u9488\uff0c\u54ea\u8fd8\u4e86\u5f97\uff1f \u9996\u5e2d\u51e0\u4e2a\u9876\u5c16\u513f\u7684\u6b66\u5b66\u5927\u5e08\u5403\u60ca\u7684\u5374\u662f\u767e\u5fe7\u4e0a\u4eba\u7684\u5185\u529f\u7adf\u7136\u7cbe\u5999\u5982\u65af\uff0c\u638c\u5fc3\u4e00\u6309\u4fbf\u80fd\u5438 \u51fa\u6bd2\u9488\uff0c\u8fd9\u624b\u529f\u592b\u3001\u4ed6\u4eec\u81ea\u95ee\u90fd\u529e\u4e0d\u5230\u3002 \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u6885\u82b1\u9488\u4e0d\u662f\u5929\u6076\u9053\u4eba\u7684\u4e1c\u897f\uff0c\u4ed6\u6709\u7684\u900f\u7a74\u795e\u9488\uff0c\u6211\u80fd\u591f\u5206\u522b\uff01\u201d \u8bf4\u4e86\u8fd9\u4e24\u53e5\u8bdd\uff0c\u4fbf\u5c06\u90a3\u679a\u6885\u82b1\u9488\u6446\u5728\u638c\u5fc3\uff0c\u8d70\u5230\u590f\u4faf\u575a\u7684\u8eab\u8fb9\uff0c\u7a81\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4fbf\u662f\u91d1 \u9488\u56fd\u624b\u590f\u4faf\u575a\u4e48\uff1f\u8001\u7eb3\u4e45\u4ef0\u4e86\uff01\u201d \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u662f\u771f\u8c6a\u6770\u50b2\u738b\u4faf \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u662f\u771f\u8c6a\u6770\u50b2\u738b\u4faf \u3000\u3000\u590f\u4faf\u575a\u9053\uff1a\u201c\u4e0a\u4eba\u5927\u540d\uff0c\u5982\u96f7\u8d2f\u8033\uff0c\u8001\u673d\u4e5f\u4e45\u4ef0\u4e86\uff01\u201d\u767e\u4f18\u4e0a\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4eca\u65e5 \u5e78\u4f1a\uff0c\u54b1\u4eec\u4eb2\u8fd1\u3001\u4eb2\u8fd1\uff01\u201d\u84e6\u7136\u4f38\u51fa\u624b\u6765\uff0c\u4f3c\u662f\u8981\u4e0e\u590f\u4faf\u575a\u63e1\u624b\u4e3a\u793c\uff0c\u5b9e\u662f\u4e00\u62db\u6781\u5386\u5bb3 \u7684\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u800c\u4e14\u6697\u85cf\u7740\u6781\u9634\u67d4\u800c\u53c8\u6781\u5f3a\u52b2\u7684\u5c0f\u5929\u661f\u638c\u529b\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u5ffd\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u8001\u7b26\u4e5f\u4e0d\u662f\u65e0\u540d\u4e4b\u8f88\uff0c\u4e0a\u4eba\u4f60\u5c31\u4e0d\u4e45\u4ef0\u6211\u4e48\uff1f \u6765\uff0c\u6765\uff01\u54b1\u4eec\u4e5f\u4eb2\u8fd1\u4eb2\u8fd1\uff01\u201d\u4ed6\u6447\u7740\u4e00\u628a\u6298\u6247\uff0c\u63d2\u8fdb\u4e24\u4eba\u4e2d\u95f4\uff0c\u521a\u597d\u767e\u4f18\u4e0a\u4eba\u5411\u590f\u4faf\u575a \u4e00\u6293\u6293\u4e0b\uff0c\u88ab\u7b26\u4e0d\u7591\u4e00\u6321\uff0c\u4f46\u542c\u5f97\u201c\u5618\u6696\u201d\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u7b26\u4e0d\u7591\u90a3\u628a\u6298\u6247\u4e43\u662f\u767e\u70bc \u7cbe\u94a2\u6240\u6253\u6210\u7684\uff0c\u88ab\u767e\u5fe7\u4e0a\u4eba\u4e00\u6293\uff0c\u7adf\u7136\u6298\u65ad\u4e86\u4e24\u6839\u6247\u9aa8\uff0c\u800c\u4e14\u94c1\u6247\u548c\u4ed6\u7684\u624b\u6307\u63a5\u89e6\uff0c\u7adf \u7136\u53d1\u51fa\u91d1\u5c5e\u7684\u7f42\u9e23\u4e4b\u58f0\uff0c\u8ff8\u51fa\u706b\u82b1\uff0c\u767e\u4f18\u4e0a\u4eba\u7684\u94c1\u6307\u529f\u592b\uff0c\u5f53\u771f\u662f\u5230\u4e86\u9707\u4e16\u9a87\u4fd7\u7684\u5730\u6b65\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u4e0e\u4f60\u4eb2\u8fd1\uff0c\u4f60\u7adf\u635f\u574f\u4e86\u6211\u7684\u6247\u5b50\uff0c\u65e0\u793c\u5982\u65af\uff0c\u6211\u8001\u7b26\u8fd8\u672a \u89c1\u8fc7\uff01\u201d\u94c1\u6247\u4e00\u5408\uff0c\u5411\u767e\u5fe7\u4e0a\u4eba\u4e00\u6233\u3002\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u597d\u50cf\u751f\u6c14\u4e4b\u6781\uff0c\u8eab\u8eaf\u5267\u70c8\u98a4\u6296\uff0c\u90a3 \u4e00\u67c4\u94c1\u6247\uff0c\u968f\u7740\u4ed6\u624b\u5a49\u7684\u98a4\u6296\uff0c\u767b\u65f6\u5316\u6210\u4e86\u5341\u51e0\u67c4\u6247\u5b50\uff0c\u5c31\u5728\u8fd9\u7728\u773c\u4e4b\u95f4\uff0c\u8fde\u88ad\u767e\u5fe7\u4e0a \u4eba\u7684\u5341\u4e09\u5904\u5927\u7a74\u3002\u767e\u5fe7\u4e0a\u4eba\u4e5f\u4e0d\u7981\u5fc3\u4e2d\u4e00\u51db\uff0c\u4ed6\u4e00\u6293\u6293\u53bb\uff0c\u8fd9\u4e00\u6b21\u7adf\u7136\u6ca1\u6709\u6293\u7740\uff0c\u4f46\u542c \u5f97\u201c\u535c\u201c\u535c\u201d\u4e24\u58f0\uff0c\u767e\u5fe7\u4e0a\u4eba\u5de6\u8170\u7684\u201c\u5c55\u8c2c\u7a74\u201d\u548c\u5c0f\u817f\u7684\u201c\u9633\u9675\u7a74\u201d\uff0c\u5df2\u5403\u4ed6\u6233\u4e86\u4e00\u4e0b\uff0c \u767e\u5fe7\u4e0a\u4eba\u6012\u543c\u4e00\u58f0\uff0c\u5de6\u638c\u8fc5\u5373\u8fde\u73af\u62cd\u51fa\uff0c\u7b26\u4e0d\u7591\u7528\u7684\u662f\u91cd\u624b\u6cd5\u6253\u7a74\uff0c\u60f3\u4e0d\u5230\u767e\u4f18\u4e0a\u4eba\u7684 \u5185\u529f\u5df2\u7ec3\u5230\u5dee\u4e0d\u591a\u8fd1\u4f3c\u201c\u91d1\u94a2\u4e0d\u574f\u201d\u4e4b\u4f53\uff0c\u867d\u7136\u88ab\u4ed6\u6233\u4e2d\u4e24\u5904\u5927\u7a74\uff0c\u4e5f\u4e0d\u8fc7\u4ec5\u4ec5\u4e00\u9635\u9178 \u9ebb\u800c\u5df2\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u7684\u94c1\u6247\u6025\u5207\u4e4b\u95f4\u6536\u4e0d\u56de\u6765\uff0c\u773c\u89c1\u4ed6\u8fd9\u4e00\u638c\u6709\u5982\u8fc5\u58f0\u51fb\u5230\uff0c\u65e0\u6cd5\u8eb2\u907f\uff0c\u4e0d\u5047\u601d \u7d22\uff0c\u53ea\u6709\u786c\u63a5\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u53ea\u542c\u5f97\u201c\u7bf7\u201d\u7684\u4e00\u58f0\uff0c\u7b26\u4e0d\u7591\u7ed9\u4ed6\u9707\u5f97\u5012\u9000\u4e86\u4e94\u516d\u6b65\uff0c\u800c\u767e \u5fe7\u4e0a\u4eba\u7684\u8eab\u8eaf\u4e5f\u6643\u4e86\u4e24\u6643\uff0c\u6240\u62ab\u7684\u5927\u7ea2\u8888\u88df\uff0c\u597d\u50cf\u9047\u81f3\u5f3a\u98ce\uff0c\u7ffb\u5377\u8d77\u6765\uff01 \u3000\u3000\u5927\u6c57\u5fd9\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u6162\u52a8\u624b\uff01\u4e0a\u4eba\uff0c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u7528\u6885\u82b1\u9488\u6740\u6b7b\u4e24\u4e2a\u4f7f\u81e3\u7684\u7a76 \u7adf\u662f\u8c01\u4eba\uff1f\u201d\u767e\u5fe7\u4e0a\u4eba\u6307\u7740\u590f\u4faf\u575a\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e2a\u8001\u513f\uff01\u201d\u53c8\u6307\u7740\u7b26\u4e0d\u7591\u9053\uff1a\u201c\u8fd9\u662f\u4ed6 \u7684\u515a\u7fbd\uff0c\u8bf7\u5927\u6c57\u4f20\u65e8\uff0c\u5c06\u8fd9\u4e24\u4eba\u62ff\u4e86\u3002\u201d \u3000\u3000\u590f\u5019\u575a\u9053\uff1a\u201c\u5927\u6c57\u5723\u660e\uff0c\u8001\u592b\u53ea\u4f1a\u533b\u4eba\uff0c\u4e0d\u4f1a\u6bd2\u4eba\u3002\u201d\u5927\u6c57\u56e0\u590f\u4faf\u575a\u533b\u597d\u9f99\u722a\u6811\uff0c \u53c8\u66fe\u542c\u8bf4\u9ed8\u8e51\u592a\u5e08\u7684\u513f\u5b50\u4e5f\u662f\u4ed6\u533b\u597d\u7684\uff0c\u5bf9\u4ed6\u9887\u6709\u597d\u611f\uff0c\u5f53\u4e0b\u534a\u4fe1\u534a\u7591\uff0c\u95ee\u767e\u5fe7\u9053\uff1a \u201c\u4e0a\u4eba\u600e\u4e48\u77e5\u9053\u662f\u4ed6\uff1f\u201d\u767e\u5fe7\u4e0a\u4eba\u9053\uff1a\u201c\u4ed6\u53f7\u79f0\u91d1\u9488\u56fd\u624b\uff0c\u80fd\u7528\u91d1\u9488\u6551\u4eba\uff0c\u4e5f\u80fd\u7528\u91d1\u9488 \u6740\u4eba\uff0c\u6211\u770b\u4e00\u5b9a\u662f\u4ed6\uff0c\u51c6\u9519\u4e0d\u4e86\uff01\u201d\u6b66\u7384\u971c\u6084\u6084\u5728\u5927\u6c57\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u4f7f\u81e3\u6b7b\u65f6\uff0c \u767e\u5fe7\u4e0a\u4eba\u8fd8\u672a\u6765\u5462\uff01\u201d\u5927\u6c57\u4e00\u542c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5e76\u672a\u773c\u89c1\uff0c\u83ab\u8981\u51a4\u6789\u4e86\u597d\u4eba\u3002 \u4f46\u53c8\u4e0d\u597d\u9a73\u65a5\u767e\u5fe7\u4e0a\u4eba\uff0c\u6b63\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u6bbf\u4e0b\u4e00\u58f0\u5c16\u53eb\u3002 \u3000\u3000\u6b66\u7384\u971c\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u674e\u9038\u53d7\u4e86\u4f24\u3002\u7531\u4e8e\u767e\u5fe7\u4e0a\u4eba\u5728\u6307\u8bc1\u590f\u4faf\u575a\u6697\u6740\u4f7f\u81e3\uff0c\u4f17\u4eba\u5bf9 \u674e\u9038\u7684\u6076\u6218\u4e0d\u5927\u6ce8\u610f\uff0c\u5982\u4eca\u542c\u5f97\u674e\u9038\u7684\u60e8\u53eb\u58f0\uff0c\u53c8\u628a\u76ee\u5149\u96c6\u4e2d\u8fd9\u4e24\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u674e\u9038\u662f\u7ed9\u7a0b\u8fbe\u82cf\u7684\u94c1\u70df\u676f\u6233\u4f24\u7684\uff0c\u4ed6\u6076\u6218\u4e86\u4e94\u5341\u6765\u62db\uff0c\u674e\u9038\u9677\u5165\u56f0\u5883\uff0c\u5468\u56f4\u90fd\u662f\u654c \u4eba\uff0c\u9976\u662f\u4ed6\u5982\u4f55\u80c6\u5927\uff0c\u4e5f\u4e0d\u514d\u6709\u70b9\u5fc3\u614c\uff0c\u4e00\u4e2a\u758f\u795e\uff0c\u907f\u5f00\u4e86\u7a0b\u8fbe\u82cf\u4e00\u8bb0\u6253\u7a74\uff0c\u5374\u4e0d\u6599\u4ed6 \u7a81\u7136\u5012\u8f6c\u70df\u6746\uff0c\u62ff\u6765\u5f53\u4f5c\u5c0f\u82b1\u67aa\u7528\uff0c\u4e00\u6233\u6233\u4e2d\u4e86\u674e\u9038\u7684\u8170\u90e8\uff0c\u767b\u65f6\u8840\u6d41\u5982\u6ce8\uff0c\u67d3\u7ea2\u4e86\u534a \u8fb9\u8863\u88f3\u3002 \u3000\u3000\u6b66\u7384\u971c\u76ee\u7779\u674e\u9038\u53d7\u4f24\uff0c\u7981\u4e0d\u4f4f\u5fc3\u5934\u5927\u9707\uff0c\u82b1\u5bb9\u5931\u8272\uff0c\u5927\u6c57\u4ee5\u4e3a\u5979\u4e0d\u6562\u770b\u6d41\u8840\u60e8\u8c61\uff0c \u89c1\u674e\u9038\u8fd8\u5728\u62fc\u6b7b\u6076\u6597\uff0c\u7a0b\u8fbe\u82cf\u5728\u4e00\u65f6\u4e4b\u95f4\uff0c\u4f3c\u4e4e\u5c1a\u672a\u80fd\u5c06\u4ed6\u62ff\u4e0b\uff0c\u4fbf\u5bf9\u767e\u5fe7\u4e0a\u4eba\u8bf4\u9053\uff1a \u201c\u8bf7\u56fd\u5e08\u628a\u8fd9\u59d3\u674e\u7684\u62ff\u4e0b\u5427\uff0c\u5983\u5b50\u5fc3\u6148\uff0c\u4e0d\u5fcd\u89c1\u90a3\u4eba\u518d\u6d41\u8840\u4e86\u3002\u201d\u6b66\u7384\u971c\u542c\u5f97\u5927\u6c57\u8fd9\u6837 \u5429\u5490\uff0c\u66f4\u662f\u5403\u60ca\u3002\u767e\u5fe7\u4e0a\u4eba\u751a\u4e3a\u4e0d\u60a6\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6740\u9e21\u7109\u7528\u725b\u5200\uff1f\u6697\u6740\u4f7f\u81e3\u8fd9\u6869\u4e8b\u60c5 \u8fd8\u672a\u5904\u7f6e\u5462\uff0c\u8bf7\u793a\u5927\u6c57\uff0c\u8fd9\u4e24\u4e2a\u4eba\u7a76\u7adf\u8981\u4e0d\u8981\u62ff\u6765\u5ba1\u95ee\uff1f\u201d\u5927\u6c57\u672c\u6765\u4e0d\u5927\u76f8\u4fe1\u662f\u590f\u4faf\u575a \u6740\u5bb3\u7684\uff0c\u4ed6\u521a\u624d\u5429\u5490\u767e\u5fe7\u4e0a\u4eba\u53bb\u62ff\u674e\u9038\uff0c\u7528\u610f\u5c31\u5728\u6682\u65f6\u7f13\u548c\u4ed6\u4eec\u7684\u4e89\u6267\u3002\u4f46\u767e\u5fe7\u4e0a\u4eba\u8feb \u5f97\u751a\u7d27\uff0c\u5927\u6c57\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u5c31\u8bf7\u590f\u5019\u575a\u5148\u751f\u4e0e\u5929\u6076\u9053\u957f\u5bf9\u8d28\u3002\u201d\u8bdd\u4e2d\u4e4b\u6025\uff0c\u8ba4 \u4e3a\u5929\u6076\u9053\u4eba\u4e5f\u6709\u5acc\u7591\uff0c\u6545\u6b64\u8981\u4ed6\u4eec\u4e8c\u4eba\u201c\u5bf9\u8d28\u201d\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u5fc3\u5934\u706b\u8d77\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u8bda\u5fc3\u6765\u52a9\u5927\u6c57\uff0c\u4e0d\u60f3\u53cd\u4ee4\u5927\u6c57\u89c1\u7591\uff0c\u65e2\u7136\u5982 \u6b64\uff0c\u8d2b\u9053\u544a\u9000\uff01\u201d\u590f\u4faf\u575a\u4e5f\u8d81\u52bf\u53d1\u6012\u9053\uff1a\u201c\u7b26\u8001\u5144\uff0c\u54b1\u4eec\u8fdc\u8fc8\u6295\u5954\uff0c\u5374\u88ab\u4eba\u5f53\u4f5c\u72af\u4eba\uff0c \u4f60\u8bf4\u5982\u4f55\uff1f\u201d\u7b26\u4e0d\u7591\u563b\u563b\u7b11\u9053\uff1a\u201c\u6b64\u5904\u4e0d\u7559\u4eba\uff0c\u81ea\u6709\u7559\u4eba\u5904\uff01\u54b1\u4eec\u90fd\u8d70\u4e86\u5427\uff01\u90a3\u4f4d\u5927\u548c \u5c1a\u8981\u6765\u6349\u62ff\uff0c\u5c3d\u7ba1\u6765\u5427\uff01\u201d \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u4e00\u628a\u62c9\u7740\u5929\u6076\u9053\u4eba\uff0c\u6012\u6c14\u51b2\u51b2\u7684\u8bf4\u9053\uff1a\u201c\u5927\u6c57\u8bf7\u65e9\u5b9a\u593a\uff0c\u7a76\u7adf\u662f\u8981\u4ed6\u4eec\u8fd8 \u662f\u8981\u6211\u4eec\uff1f\u82e5\u4e0d\u5c06\u8fd9\u4e24\u4e2a\u51f6\u624b\u62ff\u4e0b\uff0c\u54b1\u4eec\u4e09\u4eba\u90fd\u8d70\uff01\u201d \u3000\u3000\u6df7\u4e71\u4e2d\uff0c\u5ffd\u542c\u5f97\u201c\u5495\u547c\u201d\u4e00\u58f0\uff0c\u7a0b\u8fbe\u82cf\u5ffd\u7136\u88ab\u674e\u9038\u523a\u4e2d\uff0c\u5012\u4e8e\u5730\u4e0b\u3002\u8fd9\u4e00\u4e0b\u6765\u5f97\u592a \u8fc7\u7a81\u7136\uff0c\u7a0b\u8fbe\u82cf\u672c\u5df2\u5360\u5c3d\u4e0a\u98ce\uff0c\u5374\u5ffd\u7136\u4e2d\u5251\u91cd\u4f24\uff0c\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c\u83e9\u63d0\u4e0a\u4eba\u66f4\u52a0\u7559\u795e\uff0c \u770b\u5f97\u6e05\u695a\uff0c\u5931\u58f0\u56b7\u9053\uff1a\u201c\u5509\uff0c\u5f53\u771f\u662f\u90a3\u8001\u5934\u53d1\u7684\u6885\u82b1\u9488\uff01\u201d \u3000\u3000\u5927\u6c57\u5446\u4e86\u4e00\u5446\uff0c\u4ed6\u867d\u7136\u4e0d\u6ee1\u610f\u767e\u4f18\u3001\u5929\u6076\u4e8c\u4eba\u7684\u65e0\u793c\u6001\u5ea6\uff0c\u4f46\u4e00\u60f3\u5230\u5e95\u662f\u4ed6\u4eec\u53ef\u9760 \u5f97\u591a\uff0c\u7b26\u4e0d\u7591\u4e0e\u590f\u4faf\u575a\u4e0e\u4ed6\u4eec\u76f8\u6bd4\uff0c\u603b\u662f\u201c\u5916\u4eba\u2019\uff0c\u5373\u7b97\u4e0d\u662f\u51f6\u624b\uff0c\u4e5f\u4e0d\u80fd\u4e3a\u4e86\u4e24\u4e2a\u5916 \u4eba\u800c\u5c06\u767e\u4f18\u4e0a\u4eba\u5f97\u7f6a\u3002\u4e8e\u662f\u5f53\u673a\u7acb\u65ad\uff0c\u545b\u561f\u4e00\u58f0\uff0c\u63b7\u676f\u4e8e\u5730\uff0c\u559d\u9053\uff1a\u201c\u5c06\u8fd9\u4e24\u4eba\u62ff\u4e0b\uff01\u201d \u3000\u3000\u4f17\u6b66\u58eb\u7eb7\u7eb7\u62e5\u4e0a\uff0c\u7b26\u4e0d\u7591\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5b50\u8981\u6765\u4fbf\u6765\uff0c\u8981\u53bb\u4fbf\u53bb\uff0c\u4f60\u4eec\u7559\u5f97 \u4f4f\u4e48\uff1f\u201d\u5927\u8896\u8fde\u6325\uff0c\u556a\u556a\u4e24\u58f0\uff0c\u5c06\u4e24\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u6b66\u58eb\u7529\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u590f\u4faf\u575a\u8d81\u8fd9\u6df7\u4e71 \u7684\u5f62\u52bf\uff0c\u628a\u624b\u4e00\u626c\uff0c\u98de\u8d77\u4e86\u4e00\u56e2\u70df\u96fe\u3002 \u3000\u3000\u70df\u96fe\u8ff7\u6f2b\u4e4b\u4e2d\uff0c\u53ea\u89c1\u9ed1\u5f71\u77b3\u5e62\uff0c\u56db\u5904\u4e71\u7a9c\uff0c\u9762\u76ee\u771f\u76f8\uff0c\u4e0d\u80fd\u5206\u8fa9\uff0c\u4f17\u6b66\u58eb\u53c8\u6015\u8fd9\u662f \u6bd2\u70df\uff0c\u7eb7\u7eb7\u8d70\u907f\uff0c\u590f\u4faf\u575a\u4fbf\u8d81\u8fd9\u65f6\u673a\u64d2\u674e\u9038\u3002\u662f\u6bbf\u5185\u4eba\u6570\u592a\u591a\uff0c\u62e5\u6324\u63a8\u585e\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c \u8fd8\u4e0d\u80fd\u62a2\u5230\u674e\u9038\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u4e00\u58f0\u543c\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff1f\u201d\u4e00\u8fde\u53d1\u51fa\u51e0\u4e0b\u7a7a\u638c\uff0c\u638c\u529b\u5c06\u70df\u96fe\u8361\u5f00\uff0c\u5929\u6076\u9053\u4eba \u558a\u9053\uff1a\u201c\u4e0d\u662f\u6bd2\u70df\uff0c\u4e0d\u7528\u6536\uff01\u201d\u590f\u4faf\u575a\u7684\u8231\u8944\u4e2d\uff0c\u672c\u6765\u4e5f\u6709\u6709\u6bd2\u7684\u836f\u6563\uff0c\u4f46\u4ed6\u4e0d\u613f\u591a\u4f24 \u65e0\u8f9c\uff0c\u6240\u4ee5\u4e0d\u7528\u3002 \u3000\u3000\u8bf4\u65f6\u90a3\u65f6\u5feb\uff0c\u767e\u5fe7\u4e0a\u4eba\u8eab\u5f62\u4e00\u8d77\uff0c\u500f\u7136\u95f4\u5c31\u5230\u4e86\uff0c\u7b26\u4e0d\u7591\u94c1\u6247\u4e00\u6325\uff0c\u75be\u70b9\u4ed6\u7684\u864e\u53e3 \u5bf8\u8109\uff0c\u767e\u5fe7\u4e0a\u4eba\u624b\u8155\u4e00\u7ffb\uff0c\u98de\u811a\u8e22\u53bb\uff0c\u7b26\u4e0d\u7591\u8d81\u4ed6\u6362\u62db\u4e4b\u9645\uff0c\u811a\u6b65\u4e00\u6ed1\uff0c\u7acb\u523b\u5411\u540e\u6ed1\u51fa \u4e08\u4f59\uff0c\u4ed6\u5934\u4e5f\u4e0d\u56de\uff0c\u5728\u4ed6\u8eab\u540e\u7684\u4e24\u4e2a\u7a81\u53a5\u6b66\u58eb\u4fbf\u7ed9\u4ed6\u7684\u8098\u9524\u649e\u6655\uff0c\u8eab\u6cd5\u4e4b\u602a\uff0c\u62db\u6570\u4e4b\u5947\uff0c \u4ee4\u5f97\u767e\u4f18\u4e0a\u4eba\u4ea6\u662f\u4e0d\u7981\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u4e00\u51fb\u4e0d\u4e2d\uff0c\u4fa7\u8eab\u7ed5\u6b65\uff0c\u53c8\u62a2\u5230\u590f\u4faf\u575a\u8eab\u65c1\uff0c\u590f\u4faf\u575a\u9a88\u6307\u4e00\u6233\uff0c\u4f46\u542c\u51fa\u201c\u535c\u201d \u7684\u4e00\u58f0\uff0c\u590f\u4faf\u575a\u51cc\u7a7a\u98de\u8d77\uff0c\u5728\u534a\u7a7a\u4e2d\u63a5\u8fde\u7ffb\u4e86\u4e24\u4e2a\u7b4b\u6597\uff0c\u843d\u5230\u4e00\u5f20\u684c\u4e0a\uff0c\u767b\u65f6\u628a\u90a3\u5f20\u684c \u5b50\u8e0f\u788e\uff0c\u684c\u4e0a\u7684\u676f\u76d8\u7897\u789f\uff0c\u5982\u51b0\u96f9\u4e00\u822c\u98de\u843d\uff0c\u5468\u56f4\u4e03\u516b\u4e2a\u6b66\u58eb\u90fd\u7ed9\u788e\u7247\u5272\u4f24\uff0c\u7b26\u4e0d\u7591\u54c8 \u54c8\u5927\u7b11\uff0c\u4e0e\u590f\u4faf\u575a\u5e76\u6237\u5916\u95ef\uff0c\u95ef\u51fa\u4e86\u5927\u6bbf\u3002 \u3000\u3000\u539f\u6765\u767e\u5fe7\u4e0a\u4eba\u7528\u7684\u662f\u91d1\u638c\u529b\uff0c\u590f\u4faf\u575a\u7528\u7684\u662f\u4e00\u6307\u63b8\u529f\uff0c\u767e\u4f18\u4e0a\u4eba\u95ed\u5173\u5341\u5e74\u7ec3\u5c31\u201c\u91d1 \u521a\u4e0d\u574f\u4e4b\u8eaf\u201d\uff0c\u54ea\u77e5\u590f\u4faf\u575a\u7684\u201c\u63b8\u529f\u201d\u6709\u5982\u5f00\u91d1\u88c2\u77f3\uff0c\u6307\u638c\u76f8\u4ea4\uff0c\u767e\u4f18\u4e0a\u4eba\u5fc3\u5934\u5927\u9707\uff0c \u5168\u8eab\u9165\u9ebb\uff0c\u590f\u4faf\u575a\u4e5f\u7ed9\u4ed6\u7684\u638c\u9707\u4e86\u8d77\u6765\uff0c\u53cc\u65b9\u6362\u4e86\u8fd9\u62db\uff0c\u53ef\u4ee5\u8bf4\u6070\u597d\u662f\u68cb\u9022\u654c\u624b\uff0c\u4e0d\u5206 \u4e0a\u4e0b\u3002 \u3000\u3000\u767e\u4f18\u4eba\u4e0a\u4eba\u771f\u6c14\u4e00\u8fd0\uff0c\u89e3\u4e86\u590f\u4faf\u575a\u7684\u6307\u529b\uff0c\u559d\u9053\uff1a\u201c\u592a\u534e\uff0c\u4f60\u53bb\u6349\u90a3\u5c0f\u5b50\uff0c\u5929\u6076\u3001 \u706d\u5ea6\uff0c\u54b1\u4eec\u4e09\u9762\u5408\u56f4\uff0c\u7edd\u4e0d\u80fd\u8ba9\u8fd9\u4e24\u4e2a\u8001\u5339\u592b\u8d70\u6389\u3002\u201d \u3000\u3000\u9633\u592a\u534e\u662f\u767e\u5fe7\u4e0a\u4eba\u7684\u9996\u5f92\uff0c\u542c\u5f97\u5e08\u7236\u7684\u5429\u5490\uff0c\u521a\u521a\u8fc8\u52a8\u811a\u6b65\uff0c\u5728\u4ed6\u8eab\u8fb9\u7684\u8c37\u795e\u7fc1\u5ffd \u7136\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u6211\u66ff\u4f60\u6548\u52b3\u5427\uff01\u201d\u624b\u638c\u4e00\u6309\uff0c\u9633\u592a\u534e\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u8c37\u8001\u76df\u4e3b\uff0c\u4f60\uff0c \u4f60\u4e5f\u662f\u4ed6\u4eec\u4e00\u8def\u7684\u5417\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u7ed9\u8c37\u795e\u7fc1\u4e00\u638c\u6253\u7ffb\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u62d4\u51fa\u53cc\u5251\uff0c\u541e\u5410\u62bd\u6492\uff0c\u5de6\u53f3\u76d8\u65cb\uff0c\u4fe8\u5982\u7389\u9f99\u5929\u77eb\uff0c\u7075\u86c7\u98de\u821e\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u53ee \u53ee\u5f53\u5f53\u7684\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u8fd1\u7740\u4ed6\u7684\uff0c\u7ed9\u4ed6\u7684\u53cc\u5251\u4e00\u78d5\uff0c\u5175\u5203\u767b\u65f6\u8131\u624b\u98de\u53bb\uff0c\u8c37\u795e\u7fc1\u662f\u540d \u9707\u5b87\u5185\u7684\u4e09\u5927\u5251\u5ba2\u4e4b\u4e00\uff0c\u5c55\u5f00\u4e86\u7cbe\u4fee\u6570\u5341\u5e74\u7684\u8e51\u4e91\u5251\u6cd5\uff0c\u771f\u662f\u5982\u81c2\u4f7f\u6307\uff0c\u4e0d\u8bba\u5bbd\u655e\u4e4b\u5730\u3001 \u72ed\u7a84\u4e4b\u5904\u90fd\u53ef\u8fd0\u7528\u81ea\u5982\uff0c\u5927\u6bbf\u5185\u867d\u7136\u6324\u6ee1\u4e86\u4eba\uff0c\u4f46\u4ed6\u4e13\u62e3\u654c\u4eba\u7684\u95f4\u9699\u8fdb\u653b\uff0c\u7ffb\u8eab\u8fdb\u5251\uff0c \u98d8\u5ffd\u5982\u98ce\uff0c\u5251\u5230\u4eba\u5230\uff0c\u89c1\u5f71\u800c\u4e0d\u89c1\u4eba\uff0c\u5de6\u9762\u4e00\u515c\uff0c\u53f3\u9762\u4e00\u7ed5\uff0c\u4f3c\u4e1c\u5b9e\u897f\uff0c\u4f3c\u5357\u5b9e\u5317\uff0c\u79fb \u6b65\u6362\u5f62\uff0c\u53d1\u62db\u6613\u4f4d\uff0c\u6bbf\u4e2d\u6b66\u58eb\u867d\u591a\uff0c\u7adf\u7136\u62e6\u4ed6\u4e0d\u4f4f\uff01 \u3000\u3000\u706d\u5ea6\u795e\u541b\u89c1\u52bf\u4e0d\u597d\uff0c\u4ed6\u672c\u6765\u662f\u5949\u4e86\u767e\u5fe7\u4e0a\u4eba\u4e4b\u547d\uff0c\u8981\u4ed6\u53bb\u53c2\u52a0\u56f4\u6355\u7b26\u4e0d\u7591\u548c\u590f\u4faf\u575a \u7684\uff0c\u8fd9\u65f6\u89c1\u8c37\u795e\u7fc1\u7a81\u7136\u53d1\u96be\uff0c\u6bbf\u4e2d\u5e76\u65e0\u9ad8\u624b\u963b\u62e6\uff0c\u751f\u6015\u4ed6\u4e58\u673a\u4f24\u4e86\u5927\u6c57\uff0c\u53ea\u597d\u6682\u65f6\u5c06\u767e \u5fe7\u4e0a\u4eba\u7684\u547d\u4ee4\u6401\u4e0b\uff0c\u8d76\u4e0a\u524d\u53bb\u5bf9\u4ed8\u8c37\u795e\u7fc1\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u559d\u58f0\u201c\u6765\u5f97\u597d\uff01\u201d\u62a2\u5148\u8e0f\u4e0a\u4e00\u6b65\uff0c\u4e00\u811a\u8e22\u7ffb\u4e00\u4e2a\u6b66\u58eb\uff0c\u963b\u4e86\u4ed6\u4e00\u963b\uff0c\u8fc5\u5373\u53cd \u624b\u4e00\u5251\uff0c\u523a\u706d\u5ea6\u795e\u541b\u7684\u80f8\u53e3\u201c\u9886\u9965\u7a74\u201d\uff0c\u4ed6\u5728\u4ee5\u5be1\u654c\u4f17\uff0c\u5f62\u52bf\u975e\u5e38\u7d27\u5f20\u7684\u60c5\u51b5\u4e0b\uff0c\u62d9\u5251 \u523a\u7a74\uff0c\u7adf\u662f\u4e0d\u5dee\u6beb\u9ecd\uff0c\u706d\u5ea6\u795e\u541b\u8d5e\u9053\uff1a\u201c\u8c37\u8001\u513f\u7684\u8e51\u4e91\u5251\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u836f\u9504\u970d\u5730\u4e00 \u5288\uff0c\u201c\u54e8\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u65b9\u90a3\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\u3002\u8c37\u795e\u7fc1\u8eab\u5f62\u4e00\u95ea\uff0c\u8fc5\u5373\u53d8\u62db\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u8fde\u653b \u4e86\u706d\u5ea6\u795e\u541b\u4e09\u5251\uff0c\u706d\u5ea6\u795e\u541b\u5c06\u8f9f\u4e91\u9504\u7684\u9504\u6cd5\u5c55\u5f00\uff0c\u4e0a\u4f7f\u201c\u96ea\u82b1\u76d6\u9876\u201d\uff0c\u4e0b\u4f7f\u201c\u67af\u6811\u76d8\u6839\u201d\uff0c \u628a\u5168\u8eab\u9632\u62a4\u5f97\u98ce\u96e8\u4e0d\u900f\u3002\u8c37\u795e\u7fc1\u7684\u5251\u6cd5\u867d\u7136\u51cc\u5389\u4e4b\u6781\uff0c\u5374\u4e5f\u65e0\u9699\u53ef\u5165\u3002\u8c37\u795e\u7fc1\u5fc3\u60f3\uff1a \u201c\u706d\u5ea6\u795e\u541b\u662f\u57df\u5916\u4e09\u51f6\u4e2d\u6700\u5f31\u7684\u4e00\u4e2a\uff0c\u5c45\u7136\u4e5f\u8fd9\u4e48\u4e86\u5f97\uff0c\u770b\u6765\u4eca\u65e5\u975e\u820d\u4e86\u6027\u547d\uff0c\u4e0d\u80fd\u51b2 \u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u4ee5\u8c37\u795e\u7fc1\u7684\u672c\u9886\uff0c\u672c\u6765\u7a0d\u80dc\u706d\u5ea6\u795e\u541b\u4e00\u7b79\uff0c\u4f46\u975e\u5230\u4e09\u4e94\u62db\u4e4b\u540e\uff0c\u4e5f\u4e0d\u6613\u5206\u51fa\u80dc\u8d1f\uff0c \u5728\u8fd9\u6837\u60c5\u5f62\u4e4b\u4e0b\uff0c\u8c37\u795e\u7fc1\u54ea\u6562\u604b\u6218\u4e86\u4ed6\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u674e\u9038\u4e5f\u5df2\u9003\u51fa\u4e86\u95e8\u5916\uff0c\u5fc3\u5934\u4e00\u5bbd\uff0c \u7acb\u5373\u65bd\u5c55\u79fb\u6b65\u6362\u5f62\uff0c\u907f\u5f3a\u51fb\u5f31\uff0c\u8fde\u4f24\u4e86\u65c1\u8fb9\u7684\u51e0\u540d\u6b66\u58eb\uff0c\u6bbf\u4e2d\u4eba\u6570\u592a\u591a\uff0c\u81ea\u76f8\u62e5\u6324\uff0c\u706d \u5ea6\u795e\u541b\u6709\u6240\u98a4\u5fcc\uff0c\u53cd\u800c\u53d7\u4e86\u7275\u5236\uff0c\u62e6\u4e0d\u4f4f\u8c37\u795e\u7fc1\uff0c\u4e0d\u4e45\uff0c\u4fbf\u7ed9\u4ed6\u51b2\u51fa\u6bbf\u5916\uff0c\u706d\u5ea6\u795e\u541b\u7d27 \u8ddf\u7740\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u590f\u4faf\u575a\u4e0e\u7b26\u4e0d\u7591\u65e9\u5df2\u5230\u4e86\u5916\u9762\uff0c\u5916\u9762\u4e43\u662f\u5927\u6c57\u7684\u5fa1\u82d1\uff0c\u4f17\u6b66\u571f\u5835\u585e\u5404\u5904\u901a\u9053\uff0c \u8ba9\u51fa\u4e86\u4e00\u5927\u7247\u7a7a\u5730\uff0c\u767e\u4f18\u4e0a\u4eba\u4e0e\u5929\u6076\u9053\u4eba\u62a2\u8fc7\u524d\u5934\uff0c\u62e6\u4f4f\u4e86\u4ed6\u4eec\u7684\u53bb\u8def\u3002 \u3000\u3000\u4f46\u542c\u5f97\u767e\u5fe7\u4e0a\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u8888\u88df\u4e00\u5c55\uff0c\u4fe8\u5982\u4e00\u7247\u7ea2\u4e91\uff0c\u9996\u5148\u5411\u590f\u4faf\u575a\u5f53\u5934\u7f69\u4e0b\uff0c\u590f \u4faf\u575a\u521a\u624d\u4ee5\u6307\u6362\u638c\uff0c\u867d\u7136\u5e76\u672a\u5403\u4e8f\uff0c\u4f46\u4ed6\u81ea\u77e5\u8fd9\u662f\u767e\u4f18\u4e0a\u4eba\u8f7b\u654c\u6240\u81f4\uff0c\u8bba\u5230\u529f\u529b\u7684\u6df1\u539a\uff0c \u81ea\u5df1\u5c1a\u662f\u4e0d\u5982\u767e\u4f18\u4e0a\u4eba\uff0c\u89c1\u767e\u4f18\u4e0a\u4eba\u62fc\u4e86\u5168\u529b\uff0c\u731b\u6251\u800c\u6765\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u5f53\u4e0b\u65bd\u5c55\u4e86\u4e00\u62db \u6700\u4e0a\u4e58\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u4e00\u4e2a\u201c\u7ec6\u80de\u5de7\u7ffb\u4e91\u201d\u5411\u540e\u5012\uff0c\u54ea\u77e5\u767e\u5fe7\u4e0a\u4eba\u7adf\u5982\u5f71\u968f\u5f62\uff0c\u53f1\u54a4\u4e00\u58f0\uff0c \u8ddf\u7740\u4ed6\u4e5f\u7eb5\u8d77\u6765\uff0c\u638c\u52bf\u51cc\u7a7a\u6253\u4e0b\uff0c\u7b26\u4e0d\u7591\u53d1\u58f0\u602a\u7b11\uff0c\u8eab\u5f62\u5982\u7bad\uff0c\u5ffd\u5730\u5e73\u7a7a\u7a9c\u8d77\uff0c\u6247\u5934\u70b9 \u4ed6\u7684\u864e\u53e3\u5927\u7a74\uff0c\u8fd9\u4e00\u6765\uff0c\u767e\u5fe7\u4e0a\u4eba\u7684\u638c\u52bf\u82e5\u7136\u6309\u5b9e\uff0c\u590f\u4fdf\u575a\u975e\u5f97\u91cd\u4f24\u4e0d\u53ef\uff0c\u53ef\u662f\u767e\u4f18\u4e0a \u4eba\u4e5f\u5fc5\u7136\u8981\u88ab\u7b26\u4e0d\u7591\u70b9\u4e2d\u7a74\u9053\uff0c\u4ed6\u521a\u624d\u9886\u6559\u8fc7\u7b26\u4e0d\u7591\u91cd\u624b\u6cd5\u70b9\u7a74\u7684\u529f\u592b\uff0c\u81ea\u5df1\u867d\u7136\u7ec3\u6709 \u201c\u91d1\u521a\u4e0d\u574f\u201d\u7684\u8eab\u6cd5\uff0c\u5728\u8fd9\u6837\u51cc\u7a7a\u786c\u63a5\uff0c\u65e0\u53ef\u5378\u529b\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u4e5f\u662f\u4e0d\u6613\u62b5\u6321\uff0c\u8fd9\u51e0\u4eba \u90fd\u662f\u5f53\u4e16\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u5927\u5bb6\u7684\u672c\u9886\u90fd\u5df2\u5230\u4e86\u80fd\u53d1\u80fd\u6536\uff0c\u968f\u5fc3\u6240\u6b32\u7684\u5883\u754c\u3002\u5fc3\u5ff5\u4e00\u52a8\uff0c \u500f\u7136\u95f4\u4fbf\u5373\u5206\u5f00\uff0c\u4e09\u4eba\u5206\u5411\u4e09\u4e2a\u65b9\u5411\u843d\u4e0b\uff0c\u5176\u4e2d\u590f\u4faf\u575a\u6070\u597d\u843d\u5728\u5929\u6076\u9053\u4eba\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u4ec7\u4eba\u76f8\u89c1\uff0c\u4efd\u5916\u773c\u7ea2\uff0c\u5929\u6076\u9053\u4eba\u4e58\u4ed6\u7acb\u8db3\u672a\u7a33\uff0c\u62c2\u5c18\u4e00\u5c55\uff0c\u7acb\u5373\u5411\u4ed6\u8fce\u9762\u62c2\u53bb\u3002\u8fd9 \u4e00\u62db\u6b63\u662f\u5929\u6076\u9053\u4eba\u7684\u6740\u624b\u7edd\u62db\uff0c\u4fbf\u89c1\u62c2\u5c18\u8fce\u9762\u6563\u5f00\uff0c\u5343\u4e1d\u4e07\u7f15\uff0c\u4e00\u9f50\u7f69\u4e0b\uff0c\u5c18\u5c3e\u867d\u7136\u662f \u6781\u8f7b\u67d4\u4e4b\u7269\uff0c\u4f46\u7531\u4e8e\u4ed6\u5185\u529b\u6240\u6ce8\uff0c\u7adf\u4f3c\u5316\u6210\u4e86\u65e0\u6570\u5229\u9488\uff0c\u523a\u590f\u4faf\u575a\u7684\u9762\u3001\u775b\u3001\u8033\u3001\u9f3b\u7a8d\uff0c \u8fd9\u4e00\u4e0b\u7a81\u5982\u5176\u6765\uff0c\u72e0\u4e4b\u6781\uff0c\u5929\u6076\u9053\u4eba\u6599\u60f3\u590f\u4faf\u575a\u6b66\u529f\u867d\u9ad8\uff0c\u4e00\u65e0\u9632\u5907\u3002\u54ea\u77e5\u590f\u4faf\u575a\u5728\u534a \u7a7a\u4e2d\u7ffb\u8eab\u843d\u4e0b\u4e4b\u65f6\uff0c\u65e9\u5df2\u89d1\u96be\u4e86\u5929\u6076\u9053\u4eba\uff0c\u6599\u5230\u4ed6\u6709\u6b64\u4e00\u62db\uff0c\u6709\u5fc3\u8981\u7ed9\u4ed6\u4e00\u70b9\u5389\u5bb3\uff0c\u5c31 \u5728\u62c2\u5c18\u7f69\u4e0b\uff0c\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u4ed6\u5ffd\u5730\u5f20\u53e3\u4e00\u5439\uff0c\u767b\u65f6\u5c18\u5c3e\u98d8\u98d8\uff0c\u6709\u5982\u67f3\u7d6e\u968f\u98ce\uff0c\u90fd\u6311\u4e86 \u5f00\u53bb\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u590f\u4faf\u575a\u53cd\u624b\u4e00\u638c\uff0c\u201c\u7bf7\u201d\u7684\u4e00\u58f0\uff0c\u6253\u4e2d\u4e86\u5929\u6076\u9053\u4eba\u7684\u8eab\u4f53\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u6643\u4e86\u4e24\u6643\uff0c\u9762\u8272\u7070\u767d\uff0c\u5374\u602a\u58f0\u7b11\u9053\uff1a\u201c\u590f\u4faf\u8001\u5144\uff0c\u771f\u6709\u4f60\u7684\uff0c\u6211\u518d\u8bd5\u8bd5\u4f60 \u7684\u89e3\u6bd2\u672c\u9886\u3002\u201d\u539f\u6765\u4ee5\u5929\u6076\u9053\u4eba\u7684\u672c\u9886\uff0c\u590f\u4faf\u575a\u4e00\u638c\u867d\u7136\u5389\u5bb3\uff0c\u4ed6\u4e5f\u8fd8\u53ef\u4ee5\u907f\u5f00\uff0c\u4ed6\u662f \u6709\u610f\u8ba9\u4ed6\u6253\u4e2d\uff0c\u4ee4\u4ed6\u4e2d\u6bd2\u7684\u3002 \u3000\u3000\u590f\u4faf\u575a\u4e00\u638c\u6253\u4e0b\uff0c\u4f46\u89c9\u638c\u5fc3\u9ebb\u75d2\uff0c\u767b\u65f6\u624b\u81c2\u80bf\u4e86\u8d77\u6765\uff0c\u590f\u4faf\u575a\u53d6\u51fa\u4e09\u679a\u91d1\u9488\uff0c\u4e00\u63d2 \u8109\u95e8\u864e\u53e3\uff0c\u4e00\u63d2\u81c2\u5f2f\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u4e00\u63d2\u814b\u7a9d\u7389\u864e\u7a74\uff0c\u624b\u6cd5\u5e72\u51c0\u5229\u843d\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u7684 \u8150\u9aa8\u795e\u529f\uff0c\u5c82\u80fd\u5948\u6211\u4f55\u54c9\uff1f\u201d\u628a\u624b\u4e00\u626c\uff0c\u638c\u4e2d\u6263\u7740\u7684\u4e00\u7bf7\u91d1\u9488\uff0c\u500f\u7684\u98de\u51fa\uff0c\u5316\u6210\u4e86\u5341\u6570 \u9053\u5149\u8292\uff0c\u5411\u5929\u6076\u9053\u4eba\u5c04\u53bb\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u6599\u4e0d\u5230\u4ed6\u4e2d\u6bd2\u4e4b\u540e\uff0c\u51fa\u624b\u8fd8\u8fd9\u6837\u5feb\u6377\uff0c\u767e\u5fd9\u4e2d\u4e5f\u6253\u51fa\u4e86\u4e00\u7bf7\u900f\u7a74\u795e\u9488\uff0c\u4f46 \u542c\u5f97\u55e4\u55e4\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u91d1\u9488\u94f6\u9488\u4e92\u76f8\u78b0\u51fb\uff0c\u7eb7\u7eb7\u843d\u5730\uff0c\u4ed6\u4eec\u4e8c\u4eba\u7684\u529f\u529b\u672c\u6765\u65d7\u9f13\u76f8 \u5f53\uff0c\u53ef\u662f\u5929\u6076\u9053\u4eba\u56e0\u4e3a\u9002\u624d\u66fe\u4e0e\u83e9\u63d0\u4e0a\u4eba\u6bd4\u62fc\u5185\u529b\uff0c\u6709\u6240\u635f\u8017\uff0c\u8f83\u91cf\u8d77\u6765\uff0c\u7a0d\u7a0d\u5403\u4e8f\uff0c \u4ed6\u7684\u900f\u7a74\u795e\u9488\u6ca1\u6709\u4e00\u679a\u80fd\u8fd1\u5f97\u4e86\u590f\u4faf\u575a\uff0c\u800c\u590f\u4faf\u575a\u7684\u91d1\u9488\u5374\u6709\u51e0\u679a\u5c04\u5230\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u6b63\u5728\u4e0e\u7b26\u4e0d\u7591\u6076\u6218\uff0c\u4e00\u89c1\u5929\u6076\u9053\u4eba\u5f62\u52bf\u4e0d\u5999\uff0c\u7acb\u5373\u98de\u8eab\u63a0\u8d77\uff0c\u4eba\u672a\u843d\u5730\uff0c \u534a\u7a7a\u4e2d\u4e00\u4e2a\u5288\u7a7a\u638c\u4fbf\u628a\u590f\u4faf\u575a\u7684\u91d1\u9488\u90fd\u9707\u843d\u4e86\uff0c\u5929\u6076\u9053\u4eba\u8fd9\u624d\u4e0d\u81f3\u4e8e\u88ab\u91d1\u9488\u5c04\u5165\u7a74\u9053\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u529f\u529b\u7a0d\u900a\u4e8e\u767e\u5fe7\u4e0a\u4eba\uff0c\u4f46\u5929\u6076\u9053\u4eba\u53d7\u4f24\u4e4b\u540e\uff0c\u5374\u7a0d\u900a\u4e8e\u590f\u4faf\u575a\uff0c\u800c\u706d\u5ea6\u795e\u541b \u53c8\u56e0\u8981\u5bf9\u4ed8\u8c37\u795e\u7fc1\uff0c\u4ee5\u81f3\u57df\u5916\u4e09\u51f6\u5408\u56f4\u7684\u8ba1\u5212\u4e0d\u80fd\u5b9e\u73b0\u3002\u767e\u4f18\u3001\u5929\u6076\u5408\u6218\u7b26\u4e0d\u7591\u4e0e\u590f\u4faf \u575a\uff0c\u521a\u597d\u65d7\u9f13\u76f8\u5f53\uff0c\u6253\u6210\u5e73\u624b\u3002\u8fd9\u56db\u4eba\u90fd\u662f\u9886\u513f\u5c16\u7684\u89d2\u8272\uff0e\u638c\u98ce\u8d77\u5904\uff0c\u6253\u5f97\u7802\u98de\u77f3\u8d70\uff0c \u5176\u4ed6\u7684\u6b66\u58eb\uff0c\u53ea\u6709\u65c1\u89c2\u7684\u4efd\u513f\uff0c\u54ea\u6562\u63d2\u8fdb\u624b\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u674e\u9038\u4e5f\u5df2\u6253\u51fa\u4e86\u5fa1\u82d1\uff0c\u4f46\u4ed6\u5728\u6570\u5341\u7a81\u53a5\u6b66\u58eb\u56f4\u653b\u4e4b\u4e0b\uff0c\u4e5f\u672a\u80fd\u4e0e\u7b26\u4e0d\u7591\u4ed6\u4eec\u4f1a \u5408\u4e00\u9f50\uff0c\u674e\u9038\u62fc\u6b7b\u6076\u6597\uff0c\u52a0\u4e0a\u4ed6\u6240\u4f7f\u7684\u53c8\u662f\u4e00\u628a\u524a\u91d1\u5207\u7389\u7684\u5b9d\u5251\uff0c\u5f53\u8005\u62ab\u9761\uff0c\u6076\u6597\u591a\u65f6\uff0c \u4ed6\u867d\u7136\u53c8\u53d7\u4e86\u597d\u51e0\u5904\u4f24\uff0c\u53ef\u662f\u7a81\u53a5\u6b66\u58eb\u4e2d\u5251\u5012\u5730\u7684\u7adf\u6709\u5341\u6570\u4eba\u4e4b\u591a\uff0c\u4eba\u4eba\u80c6\u5bd2\uff0c\u90fd\u4e0d\u6562 \u8fc7\u4efd\u8feb\u8fd1\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u89c1\u9633\u592a\u534e\u8ffd\u4e86\u51fa\u6765\uff0c\u8c37\u795e\u7fc1\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u5403\u4e86\u6211\u7684\u4e00\u638c\uff0c\u5c45\u7136\u6ca1 \u6709\u53d7\u4f24\uff0c\u8fd9\u56de\u674e\u9038\u53ef\u8981\u7cdf\u4e86\uff01\u201d\u4ed6\u548c\u674e\u9038\u7684\u5e08\u7236\u5c09\u8fdf\u70af\u4e43\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u8fd9\u56de\u662f\u7279\u4e3a\u6551\u674e \u9038\u6765\u7684\uff0c\u53ef\u662f\u4ed6\u88ab\u706d\u5ea6\u795e\u541b\u7f20\u5f97\u751a\u7d27\uff0c\u4ed6\u7684\u529f\u529b\u867d\u7136\u7a0d\u80dc\u706d\u5ea6\u795e\u541b\u4e00\u7b79\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u5374 \u662f\u6446\u8131\u4e0d\u4e86\u3002 \u3000\u3000\u9633\u592a\u534e\u4e00\u5230\uff0c\u56f4\u653b\u674e\u9038\u7684\u6b66\u58eb\u4e24\u8fb9\u8ba9\u5f00\uff0c\u9633\u592a\u534e\u51b2\u5230\u4e86\u674e\u9038\u7684\u9762\u524d\uff0c\u5de6\u638c\u5212\u4e86\u4e00\u4e2a \u5706\u5f27\uff0c\u53f3\u638c\u500f\u7684\u7a7f\u51fa\uff0c\u7528\u7684\u6b63\u662f\u4e00\u62db\u6781\u5389\u5bb3\u7684\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u8981\u786c\u62a2\u674e\u9038\u7684\u5b9d\u5251\uff0c\u674e\u9038\u53cd \u624b\u4e00\u5251\uff0c\u4f46\u542c\u5f97\u201c\u54e8\u201d\u7684\u4e00\u58f0\uff0c\u5b9d\u5251\u7adf\u7ed9\u4ed6\u7684\u624b\u6307\u5f39\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46 \u89c1\u4ed6\u7684\u624b\u638c\u5df2\u62cd\u5230\u80f8\u524d\uff0c\u674e\u9038\u62fc\u4e86\u5168\u529b\uff0c\u5de6\u638c\u731b\u51fb\uff0c\u53f3\u624b\u7684\u5b9d\u5251\u4e00\u63d0\u4e00\u7ffb\uff0c\u540c\u65f6\u75be\u523a\u4ed6 \u7684\u819d\u76d6\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u674e\u9038\u5927\u53eb\u4e00\u58f0\uff0c\u864e\u53e3\u7adf\u7136\u9707\u88c2\u6d41\u8840\uff0c\u65b9\u9053\u4e0d\u5999\uff0c\u5374\u542c\u5f97\u201c\u5495\u51ac\u201d\u4e00 \u58f0\uff0c\u9633\u592a\u534e\u5148\u5df2\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u9633\u592a\u534e\u662f\u767e\u5fe7\u4e0a\u4eba\u7684\u9996\u5f92\uff0c\u82e5\u8bba\u529f\u529b\uff0c\u6bd4\u674e\u9038\u8981\u80dc\u4e00\u7b79\uff0c\u4f55\u4ee5\u4ed6\u773c\u770b\u4fbf\u80fd\u53d6\u80dc\uff0c\u5374 \u53cd\u800c\u8d25\u4e86\uff1f\u539f\u6765\u4ed6\u5403\u4e86\u8c37\u795e\u7fc1\u4e00\u638c\uff0c\u5143\u6c14\u5927\u4f24\uff0c\u4e0d\u8fc7\u4ed7\u7740\u767e\u4f18\u4e0a\u4eba\u6240\u6388\u7684\u72ec\u95e8\u5185\u529f\uff0c\u63d0 \u8d77\u7cbe\u795e\uff0c\u51dd\u805a\u771f\u529b\uff0c\u8868\u9762\u4e0a\u770b\u4e0d\u51fa\u53d7\u4f24\u7684\u8ff9\u8c61\u3002\u8fd9\u4e00\u4e0b\u548c\u674e\u9038\u786c\u78b0\u786c\u63a5\uff0c\u674e\u9038\u8eab\u4e0a\u867d\u7136 \u4e5f\u53d7\u4e86\u51e0\u5904\u4f24\uff0c\u4f24\u7684\u4e0d\u8fc7\u76ae\u8089\uff0c\u771f\u529b\u6ca1\u6709\u600e\u6837\u8017\u635f\uff0c\u6240\u4ee5\u786c\u78b0\u4e4b\u4e0b\uff0c\u9633\u592a\u534e\u5403\u4e8f\u66f4\u5927\uff0c \u4e0d\u4f46\u53e3\u5410\u9c9c\u8840\uff0c\u819d\u76d6\u4e5f\u88ab\u674e\u9038\u4e00\u5251\u523a\u7a7f\u3002 \u3000\u3000\u53ef\u662f\u674e\u9038\u4e5f\u4f24\u5f97\u4e0d\u8f7b\uff0c\u4ed6\u5de6\u624b\u864e\u53e3\u7834\u88c2\uff0c\u53ea\u5269\u4e0b\u4e00\u6761\u624b\u81c2\u597d\u7528\uff0c\u7a81\u53a5\u6b66\u58eb\u8d81\u52bf\u731b\u653b\uff0c \u674e\u9038\u54ac\u7d27\u7259\u6839\uff0c\u62fc\u6b7b\u8840\u6218\uff0c\u4ed7\u7740\u4ed6\u90a3\u67c4\u65e0\u575a\u4e0d\u6467\u7684\u5b9d\u5251\uff0c\u53c8\u6740\u4f24\u4e86\u51e0\u4eba\uff1b\u90a3\u4e9b\u7a81\u53a5\u6b66\u58eb \u89c1\u4ed6\u5982\u6b64\u51f6\u731b\uff0c\u5012\u662f\u4e0d\u6562\u8fc7\u4efd\u903c\u8fd1\u3002\u4f46\u674e\u9038\u81ea\u5df1\u77e5\u9053\uff0c\u4ed6\u5df2\u662f\u5230\u4e86\u6cb9\u5c3d\u706f\u67af\u7684\u5730\u6b65\uff0c\u6c14 \u529b\u8870\u7aed\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u7a81\u51fa\u91cd\u56f4\u4e86\u3002 \u3000\u3000\u6b63\u5728\u5371\u6025\u4e4b\u9645\u5ffd\u542c\u5f97\u5357\u5bab\u5c1a\u53eb\u9053\uff1a\u201c\u6bbf\u4e0b\u4f11\u614c\uff0c\u5357\u5bab\u5c1a\u62a4\u9a7e\u6765\u4e86\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u54e9\u3001 \u54e9\u3001\u54e9\u51e0\u53e3\u98de\u5200\uff0c\u63b7\u5165\u4eba\u4e1b\uff0c\u5c06\u56f4\u653b\u7684\u6b66\u58eb\u903c\u5f00\uff0c\u674e\u9038\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5e76\u80a9\u51b2 \u51fa\uff0c\u4e0e\u7b26\u8001\u524d\u8f88\u4f1a\u9f50\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5357\u5bab\u5c1a\u5df2\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff0c\u5ffd\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u4e0e\u5927\u6c57\u4f1a\u9762\u5427\uff01\u201d \u84e6\u5730\u628a\u624b\u4e00\u626c\uff0c\u4e00\u67c4\u98de\u5200\uff0c\u7535\u5c04\u800c\u51fa\uff0c\u674e\u9038\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4ed6\u7a81\u7136\u53db\u53d8\uff0c\u65bd\u7528\u8be1\u8ba1\u4f24\u4eba\uff0c \u8ddd\u79bb\u53c8\u8fd1\uff0c\u5982\u4f55\u8eb2\u95ea\u5f97\u5f00\uff1f\u767e\u5fd9\u4e2d\uff0c\u4ed6\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u8eab\u5f62\u521a\u521a\u8f6c\u4e86\u534a\u4e2a\u5706\u5708\uff0c\u53ea \u542c\u5f97\u201c\u5618\u201d\u7684\u4e00\u58f0\uff0c\u98de\u5200\u5df2\u63d2\u8fdb\u4e86\u4ed6\u7684\u80cc\u810a\u3002\u674e\u9038\u53eb\u9053\uff1a\u201c\u5357\u5bab\u5c1a\uff0c\u4f60\u597d\uff01\u201d\u767b\u65f6\u50cf\u4e00 \u6839\u6728\u5934\u822c\u8dcc\u5012\u4e86\uff01 \u3000\u3000\u5357\u5bab\u5c1a\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\u8bf7\u6055\u6211\u65e0\u793c\uff01\u201d\u4fef\u4e0b\u8eab\u8eaf\uff0c\u628a\u674e\u9038\u6276\u8d77\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u5ffd\u542c \u5f97\u674e\u9038\u4e00\u58f0\u5927\u559d\uff1a\u201c\u53db\u8d3c\u62ff\u8fc7\u547d\u6765\uff01\u201d\u7a81\u7136\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7ffb\u8eab\u8df3\u8d77\uff0c\u5251\u5149\u4e00\u95ea\uff0c \u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u5b9d\u5251\u7adf\u81ea\u5357\u5bab\u5c1a\u7684\u524d\u5fc3\u63d2\u5165\uff0c\u7a7f\u8fc7\u540e\u5fc3\uff01 \u3000\u3000\u674e\u9038\u62d4\u51fa\u5b9d\u5251\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u4f17\u6b66\u58eb\u89c1\u4ed6\u8eab\u53d7\u91cd\u4f24\uff0c\u4ecd\u7136\u4e00\u5251\u628a\u5357\u5bab\u5c1a\u6740\u4e86\uff0c\u76f8\u987e\u9a87 \u7136\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u88ab\u4ed6\u5413\u4f4f\uff0c\u7adf\u4e0d\u6562\u4e0a\u524d\u3002\u9633\u592a\u534e\u5374\u542c\u51fa\u4ed6\u7684\u7b11\u58f0\u4e2d\u6c14\u4e0d\u8db3\uff0c\u89c1\u4f17\u6b66\u58eb\u4e0d \u6562\u4e0a\u524d\uff0c\u9a82\u58f0\u201c\u8113\u5305\u201d\uff0c\u4ed6\u529f\u529b\u6df1\u6e5b\uff0c\u819d\u76d6\u867d\u88ab\u674e\u9038\u5b9d\u5251\u523a\u7a7f\uff0c\u5355\u8db3\u652f\u5730\uff0c\u4ecd\u80fd\u4e00\u8dc3\u800c \u8d77\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u76d8\u65cb\uff0c\u7528\u4e86\u4e00\u62db\u4e03\u79bd\u638c\u6cd5\uff0c\u5411\u674e\u9038\u540e\u5fc3\u72e0\u72e0\u51fb\u4e0b\uff0c\u674e\u9038\u500f\u7684\u8f6c\u8eab\uff0c\u98de \u5251\u51fa\u624b\uff0c\u5316\u6210\u4e00\u9053\u957f\u8679\uff0c \u3000\u3000\u9633\u592a\u534e\u6599\u4e0d\u5230\u4ed6\u7adf\u4f1a\u6254\u51fa\u624b\u4e2d\u7684\u5175\u5668\uff0c\u5728\u534a\u7a7a\u4e2d\u95ea\u8eab\u4e0d\u6613\uff0c\u5e78\u4ed6\u5e94\u53d8\u5f97\u5feb\uff0c\u672c\u9886\u4e5f \u786e\u5c5e\u9ad8\u5f3a\uff0c\u767e\u5fd9\u4e2d\u5de6\u811a\u671d\u53f3\u811a\u811a\u80cc\u4e00\u8e0f\uff0c\u786c\u751f\u751f\u7684\u5728\u534a\u7a7a\u4e2d\u5012\u9000\u6570\u5c3a\uff0c\u9976\u662f\u5982\u6b64\uff0c\u5de6\u638c \u638c\u5fc3\u4e5f\u88ab\u674e\u9038\u7684\u98de\u5251\u7a7f\u8fc7\u4e86\uff01 \u3000\u3000\u674e\u9038\u54c8\u54c8\u5927\u7b11\uff0c\u7b11\u58f0\u5374\u8d8a\u6765\u8d8a\u5f31\uff0c\u5c31\u5728\u9633\u592a\u534e\u5012\u5730\u4e4b\u540e\uff0c\u4ed6\u8eab\u8eaf\u6447\u6643\uff0c\u4e5f\u5728\u7b11\u58f0\u4e2d \u5012\u4e0b\u5730\u4e86\u3002\u6b66\u58eb\u4eec\u8d77\u521d\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u8bf1\u654c\u4e4b\u8ba1\uff0c\u540e\u6765\u89c1\u4ed6\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53c8\u89c1\u4ed6\u5b9d\u5251\u5df2\u7ecf\u51fa\u624b\uff0c \u51cf\u4e86\u987e\u5fcc\uff0c\u8fd9\u624d\u6562\u4e00\u62e5\u800c\u524d\uff0c\u674e\u9038\u6beb\u65e0\u62b5\u6297\uff0c\u539f\u6765\u4ed6\u5df2\u529b\u7aed\u7b4b\u75b2\uff0c\u5728\u6740\u4e86\u5357\u5bab\u5c1a\u3001\u91cd\u4f24 \u4e86\u9633\u592a\u534e\u4e4b\u540e\uff0c\u518d\u4e5f\u65e0\u80fd\u4e3a\u529b\u4e86\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u8ddd\u79bb\u8f83\u8fd1\uff0c\u89c1\u674e\u9038\u88ab\u64d2\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u5251\u75be\u8d77\uff0c\u5de6\u4e00\u5251\u201c\u5ba2\u661f\u72af \u6708\u201d\uff0c\u53f3\u4e00\u5251\u201c\u5212\u7834\u5929\u6cb3\u201d\uff0c\u8fd9\u53cc\u5251\u8fde\u73af\u75be\u523a\uff0c\u6b63\u662f\u4ed6\u8e51\u4e91\u5251\u6cd5\u7684\u6740\u624b\u795e\u62db\uff0c\u706d\u5ea6\u795e\u541b \u62b5\u6321\u4e0d\u4f4f\uff0c\u4f46\u542c\u5f97\u54e8\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u4ed6\u624b\u4e2d\u7684\u8f9f\u4e91\u9504\u51e0\u4e4e\u7ed9\u9707\u6389\u8dcc\u843d\uff0c\u8c37\u795e\u7fc1\u5251\u8eab \u968f\u8fdb\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u8ba9\u4e0d\u8ba9\u8def\uff01\u201d\u706d\u5ea6\u795e\u541b\u89c1\u4ed6\u795e\u5a01\u51db\u51db\uff0c\u4e0d\u7981\u5fc3\u602f\uff0c\u8fde\u5fd9\u9000\u6b65\uff0c\u9000\u5f97 \u7a0d\u6162\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u81c2\u818a\u7adf\u7ed9\u4ed6\u7684\u5251\u950b\u5212\u8fc7\uff0c\u5212\u5f00\u4e86\u4e00\u9053\u957f\u957f\u7684\u4f24\u53e3\u3002 \u3000\u3000\u53ef\u662f\u8c37\u795e\u7fc1\u4e5f\u8fdf\u4e86\u4e00\u6b65\uff0c\u674e\u9038\u5df2\u7ed9\u6b66\u58eb\u4eec\u67b6\u8d70\u4e86\uff0c\u4ed6\u6b63\u5f85\u8ffd\u53bb\uff0c\u767e\u5fe7\u4e0a\u4eba\u5df2\u8d76\u4e86\u5230 \u6765\uff0c\u8888\u88df\u4e00\u5c55\uff0c\u6402\u5934\u7f69\u4e0b\uff0c\u8c37\u795e\u7fc1\u529b\u900f\u5251\u5c16\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u53cc\u5251\u9f50\u51fa\uff0c\u5ffd\u89c9\u5251\u950b \u6240\u89e6\u4e4b\u5904\uff0c\u8f6f\u7ef5\u7ef5\u5168\u4e0d\u53d7\u529b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u500f\u7136\u95f4\u4e00\u80a1\u6781\u5927\u7684\u6f5c\u529b\u538b\u6765\uff0c\u8c37\u795e\u7fc1\u7528\u5c3d\u5168\u529b\uff0c \u53cc\u5251\u7adf\u7136\u4e0d\u80fd\u79fb\u52a8\u3002 \u3000\u3000\u706d\u5ea6\u795e\u541b\u89c1\u767e\u5fe7\u4e0a\u4eba\u6765\u63f4\uff0c\u80c6\u6c14\u53c8\u58ee\uff0c\u4e3e\u8d77\u836f\u9504\uff0c\u4fbf\u5411\u8c37\u795e\u7fc1\u7684\u80cc\u540e\u88ad\u6765\uff0c\u53ef\u662f\u5c31 \u5728\u8fd9\u65f6\uff0c\u7b26\u4e0d\u7591\u4ea6\u5df2\u8d76\u5230\uff0c\u706d\u5ea6\u795e\u541b\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u6025\u5fd9\u62b5\u6321\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u542c \u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u7684\u624b\u8155\u5df2\u7ed9\u7b26\u4e0d\u7591\u7684\u94c1\u6247\u6572\u4e86\u4e00\u4e0b\uff0c\u8f9f\u4e91\u9504\u767b\u65f6\u5760\u5730\uff0c\u7b26\u4e0d\u7591\u563b\u563b \u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u62b5\u4e00\u4e2a\uff0c\u4f60\u4e5f\u7ed9\u6211\u62ff\u8fc7\u547d\u6765\uff01\u201d\u94c1\u6247\u4e00\u5408\uff0c\u80a9\u5934\u6233\u5411\u4ed6\u80f8\u53e3\u7684\u201c\u5de8\u9614\u7a74\u201d\uff0c \u8fd9\u201c\u5de8\u9614\u7a74\u201d\u4e43\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u82e5\u7ed9\u6233\u4e2d\uff0c\u7109\u6709\u547d\u5728\uff1f \u3000\u3000\u8fd9\u65f6\u767e\u4f18\u4e0a\u4eba\u5bf9\u8c37\u795e\u7fc1\uff0c\u5df2\u662f\u5b8c\u5168\u5360\u4e86\u4e0a\u98ce\uff0c\u53ea\u8981\u518d\u52a0\u91cd\u529f\u529b\uff0c\u4e0d\u96be\u5c06\u8c37\u795e\u7fc1\u5236\u670d\uff0c \u53ef\u662f\u706d\u5ea6\u795e\u541b\u906d\u5371\uff0c\u4ed6\u5c82\u80fd\u5750\u89c6\u4e0d\u6551\uff0c\u8fd9\u51e0\u4e2a\u4eba\u7684\u6b66\u529f\u90fd\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u80c4\u4e4b\u5883\uff0c\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u5404\u81ea\u4f7f\u51fa\u7edd\u9669\u7684\u5947\u62db\uff01 \u3000\u3000\u4f46\u89c1\u767e\u5fe7\u4e0a\u4eba\u547c\u7684\u4e00\u58f0\uff0c\u8f6c\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u5c06\u8888\u88df\u629b\u51fa\uff01\u88df\u631f\u7740\u52b2\u98ce\uff0c\u5b9b\u5982\u4e00\u7247\u60ca\u6d9b \u6025\u6d6a\uff0c\u5411\u7b26\u4e0d\u7591\u75be\u5377\u800c\u6765\uff0c\u7b26\u4e0d\u7591\u786c\u751f\u751f\u7684\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u907f\u5f00\u4e86\u9a76\u88df\u7684\u7a81\u88ad\uff0c\u6539 \u4e86\u65b9\u5411\uff0c\u7fe9\u5982\u98de\u9e1f\u822c\u7684\u5411\u767e\u5fe7\u4e0a\u4eba\u51b2\u53bb\uff0c\u767e\u5fe7\u4e0a\u4eba\u7528\u4e86\u201c\u5343\u65a4\u5760\u201d\u7684\u91cd\u8eab\u6cd5\u51fa\u6307\u642d\u7740\u7b26 \u4e0d\u7591\u6253\u6765\u7684\u94c1\u6247\uff0c\u767b\u65f6\u5c06\u7b26\u4e0d\u7591\u731b\u51b2\u4e4b\u52bf\u963b\u4f4f\uff0c\u4f46\u4ed6\u7684\u4e0a\u8eab\u4e5f\u4e0d\u7981\u6643\u4e86\u4e24\u6643\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u8eab\u4e0a\u7684\u538b\u529b\u4e00\u677e\uff0c\u767b\u65f6\u4f7f\u51fa\u4e86\u79fb\u6b65\u6362\u5f62\uff0c\u53d8\u62db\u6613\u4f4d\u7684\u529f\u592b\uff0c\u4e00\u5251\u5411\u706d\u5ea6\u795e\u541b \u6eaf\u53bb\uff0c\u706d\u5ea6\u795e\u541b\u4e5f\u597d\u751f\u4e86\u5f97\uff0c\u5c31\u5728\u7b26\u4e0d\u7591\u88ab\u8888\u88df\u903c\u5f00\u7684\u90a3\u4e00\u77ac\u4e4b\u95f4\uff0c\u4ed6\u5df2\u62fe\u8d77\u4e86\u5175\u5668\uff0c \u8f9f\u4e91\u9504\u6a2a\u80f8\u4e00\u6321\uff0c\u67b6\u5f00\u4e86\u8c37\u795e\u7fc1\u7684\u957f\u5251\u3002\u5929\u6076\u9053\u4eba\u4e0e\u590f\u4faf\u575a\u76f8\u7ee7\u8d76\u6765\uff0c\u4e09\u5bf9\u9ad8\u624b\uff0c\u4f1a\u5408 \u4e00\u9f50\uff0c\u76f4\u6740\u5f97\u5929\u660f\u5730\u6697\uff0c\u65e5\u6708\u65e0\u5149\u3002 \u3000\u3000\u57df\u5916\u4e09\u51f6\u8fd9\u8fb9\uff0c\u706d\u5ea6\u795e\u541b\u548c\u5929\u6076\u9053\u4eba\u90fd\u53d7\u4e86\u4f24\uff0c\u867d\u7136\u4e0d\u5f88\u4e25\u91cd\uff0c\u5185\u529b\u5374\u5df2\u4e0d\u7ee7\uff0c\u8fd9 \u4e00\u8fb9\uff0c\u590f\u4faf\u575a\u4e2d\u4e86\u5929\u6076\u9053\u4eba\u7684\u201c\u8150\u9aa8\u795e\u529f\u201d\uff0c\u867d\u7136\u4ed6\u7acb\u5373\u7528\u91d1\u9488\u89e3\u6bd2\uff0c\u4f46\u5728\u6fc0\u6597\u4e4b\u4e0b\uff0c \u771f\u6c14\u96be\u4ee5\u51dd\u805a\uff0c\u6bd2\u52bf\u6e10\u6e10\u7684\u5728\u4f53\u5185\u8513\u5ef6\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u4ea6\u81ea\u89c9\u5f97\u5934\u6655\u76ee\u7729\uff0c\u6697\u53eb\u4e0d\u5999\uff0c\u800c \u8c37\u795e\u7fc1\u56e0\u4e3a\u9002\u624d\u4e0e\u767e\u4f18\u4e0a\u4eba\u786c\u62fc\u5185\u529b\uff0c\u4e8f\u8017\u751a\u5927\uff0c\u62db\u6570\u53d1\u51fa\uff0c\u4e5f\u6e10\u6e10\u89c9\u5f97\u529b\u4e0d\u4ece\u5fc3\u3002\u4e0d \u8fc7\uff0c\u53cc\u65b9\u90fd\u6709\u4e86\u4e24\u4e2a\u4eba\u53d7\u4f24\uff0c\u4ecd\u7136\u662f\u4e2a\u76f8\u5cd9\u4e4b\u5c40\uff0c\u96be\u5206\u9ad8\u4e0b\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u7b26\u4e0d\u7591\u7a81\u7136\u4f7f\u51fa\u4e24\u8bb0\u72e0\u62db\uff0c\u731b\u88ad\u706d\u5ea6\u795e\u541b\uff0c\u706d\u5ea6\u795e\u541b\u662f\u57df\u5916\u4e09\u51f6\u4e2d\u6700\u5f31\u7684\u4e00 \u73af\uff0c\u62db\u67b6\u4e0d\u4f4f\u4ed6\u90a3\u795e\u5999\u65e0\u65b9\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u88ab\u8feb\u8fde\u8fde\u540e\u9000\uff0c\u7b26\u4e0d\u7591\u563b\u563b\u7b11\u9053\uff1a\u201c\u9152\u9189\u996d\u9971\uff0c \u67b6\u4e5f\u6253\u5f97\u591f\u4e86\uff0c\u591a\u8c22\u4e3b\u4eba\u76db\u60c5\u62db\u5f85\uff0c\u54b1\u4eec\u544a\u8f9e\u3002\u201d\u590f\u4faf\u575a\u4e0e\u8c37\u795e\u7fc1\u5fc3\u60f3\uff0c\u674e\u9038\u5728\u4eca\u65e5\u662f \u65e0\u8bba\u5982\u4f55\u4e5f\u6551\u4e0d\u51fa\u6765\u4e86\u3002\u4ed6\u4eec\u90fd\u53d7\u4e86\u4f24\uff0c\u5be1\u4e0d\u654c\u4f17\uff0c\u518d\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u81ea\u5df1\u4e5f\u8131\u8eab\u4e0d\u4e86\uff0c \u4e8e\u662f\u590f\u4faf\u575a\u65bd\u5c55\u91d1\u9488\u523a\u7a74\u7684\u7edd\u6280\uff0c\u8c37\u795e\u7fc1\u65bd\u5c55\u79fb\u5f62\u6613\u4f4d\u7684\u529f\u592b\uff0c\u7531\u7b26\u4e0d\u7591\u6bbf\u540e\uff0c\u62b5\u6321\u767e \u5fe7\u4e0a\u4eba\u7684\u8ffd\u51fb\uff0c\u4e09\u4eba\u5408\u529b\uff0c\u9f50\u5411\u5916\u95ef\u3002 \u3000\u3000\u6b66\u7384\u971c\u4f34\u7740\u5927\u6c57\u5750\u5728\u6bbf\u4e0a\uff0c\u5979\u770b\u4e0d\u6e05\u695a\u5916\u9762\u7684\u60c5\u5f62\uff0c\u4f46\u542c\u5f97\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\uff0c\u9707\u8033\u6b32 \u804b\uff0c\u5fc3\u4e2d\u7740\u6025\u4e4b\u6781\uff0c\u597d\u5728\u5927\u6c57\u8fd9\u65f6\u4e5f\u5728\u5168\u795e\u6ce8\u89c6\u5916\u9762\u7684\u6fc0\u6218\uff0c\u6ca1\u6709\u7559\u610f\u5979\u7684\u9762\u8272\uff0c\u8fc7\u4e86 \u4e00\u4f1a\uff0c\u6709\u4eba\u4e0a\u6765\u62a5\u9053\uff0c\u674e\u9038\u5df2\u91cd\u4f24\u88ab\u64d2\uff0c\u6b66\u7384\u971c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u7a81\u53a5\u5927\u6c57\u5219\u559c\u6c14\u6d0b\u6d0b\uff0c \u6025\u5fd9\u5429\u5490\u9053\uff1a\u201c\u4e0d\u8981\u4f24\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u8fd9\u4e2a\u4eba\u6211\u8fd8\u6709\u7528\uff0c\u8d76\u5feb\u5c06\u4ed6\u62ac\u8fdb\u5bab\u91cc\u53bb\uff0c\u5429\u5490\u5fa1\u533b \u7ed9\u4ed6\u6025\u6551\u3002\u201d\u5429\u5490\u5b8c\u6bd5\uff0c\u659f\u4e86\u4e00\u676f\u9152\u7ed9\u6b66\u7384\u971c\u9053\uff1a\u201c\u5983\u5b50\u4f60\u559d\u4e00\u676f\u9152\u538b\u60ca\uff01\u201d\u5ffd\u89c1\u6b66\u7384 \u971c\u9762\u8272\u82cd\u767d\uff0c\u5927\u6c57\u9053\uff1a\u201c\u522b\u6015\uff0c\u522b\u6015\uff0c\u8fd9\u573a\u4e71\u4e8b\u5c31\u8fc7\u53bb\u5566\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u5916\u9762\u53ae\u6740\u4e4b \u58f0\u592a\u8fc7\u9a87\u4eba\uff0c\u9996\u6076\u5df2\u64d2\uff0c\u5176\u4ed6\u7684\u4eba\u5c31\u8ba9\u4ed6\u4eec\u8d70\u5427\u3002\u201d\u5927\u6c57\u9053\uff1a\u201c\u5983\u5b50\u8bf4\u5f97\u662f\uff0c\u662f\u4e0d\u5fc5\u8feb \u4ed6\u4eec\u4f5c\u56f0\u517d\u4e4b\u6597\u4e86\u3002\u201d\u4fbf\u4f20\u4ee4\u4e0b\u53bb\uff0c\u53eb\u767e\u5fe7\u4e0a\u4eba\u4e0d\u5fc5\u8ffd\u8d76\u3002 \u3000\u3000\u767e\u5fe7\u3001\u5929\u6076\u3001\u706d\u5ea6\u4e09\u4eba\u4e4b\u4e2d\uff0c\u53ea\u6709\u767e\u5fe7\u4e0a\u4eba\u5c1a\u672a\u53d7\u4f24\uff0c\u5176\u5b9e\u4ed6\u4eec\u4ea6\u5df2\u6709\u70b9\u5fc3\u602f\uff0c\u4e0d \u8fc7\u4e3a\u4e86\u8eab\u4efd\u5a01\u540d\uff0c\u4e0d\u5f97\u4e0d\u4f5c\u52bf\u8ffd\u8d76\u800c\u5df2\uff0c\u5927\u6c57\u4f20\u4e0b\u4ee4\u6765\uff0c\u6b63\u5408\u4ed6\u4eec\u7684\u5fc3\u610f\uff0c\u7acb\u5373\u56de\u8f6c\u5927 \u6bbf\uff0c\u5411\u5927\u6c57\u590d\u547d\u3002\u5176\u4ed6\u7684\u4eba\uff0c\u8c01\u6562\u53bb\u62e6\u963b\u7b26\u4e0d\u7591\u4ed6\u4eec\uff1f\u8651\u5f20\u58f0\u52bf\uff0c\u95f9\u4e86\u4e00\u4f1a\uff0c\u7b26\u4e0d\u7591\u7b49 \u4e00\u884c\u4e09\u4eba\uff0c\u65e9\u5df2\u6253\u7834\u4e86\u5fa1\u82d1\u7684\u89d2\u95e8\uff0c\u95ef\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u76db\u5bb4\u88ab\u4ed6\u4eec\u4e00\u95f9\uff0c\u5f53\u7136\u662f\u5174\u5473\u7d22\u7136\uff0c\u4e0d\u8fc7\uff0c\u5e78\u800c\u64d2\u4e86\u4e00\u4e2a\u674e\u9038\uff0c\u633d\u56de\u4e86\u4e9b\u5c11 \u9762\u5b50\uff0c\u5927\u6c57\u5f53\u5373\u4f20\u65e8\u7f62\u5bb4\u56de\u5bab\uff0c\u6b66\u58eb\u5927\u4f1a\uff0c\u8981\u7559\u5230\u660e\u65e5\u518d\u6b63\u5f0f\u4e3e\u884c\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u966a\u4f34\u5927\u6c57\u56de\u5230\u5185\u5bab\uff0c\u5927\u6c57\u5bf9\u5979\u751a\u662f\u62b1\u6b49\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u662f\u4f60\u6211\u4f73\u671f\uff0c\u60f3\u4e0d\u5230 \u5728\u534e\u5802\u4e4b\u4e0a\uff0c\u76db\u7b75\u4e4b\u4e2d\uff0c\u88ab\u90a3\u51e0\u4e2a\u5357\u671d\u86ee\u5b50\u80e1\u95f9\u4e00\u573a\uff0c\u771f\u662f\u5927\u715e\u98ce\u666f\uff0c\u73b0\u5728\u6211\u53c8\u8981\u5ba1\u95ee \u90a3\u4e2a\u674e\u9038\uff0c\u4e0d\u80fd\u966a\u4f34\u4e8e\u4f60\uff0c\u5983\u5b50\u4f60\u7eb5\u7136\u4e0d\u57cb\u6028\u6211\uff0c\u6211\u5fc3\u4e2d\u4ea6\u89c9\u4e0d\u5b89\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u5927\u6c57\u4f60\u6709\u6b63\u4e8b\u8981\u529e\uff0c\u4e0d\u5fc5\u987e\u6211\u3002\u90a3\u4e2a\u5357\u671d\u86ee\u5b50\u662f\u4e2a\u5f88\u91cd\u8981\u7684\u4eba\u4e48\uff1f\u5927 \u6c57\u4f60\u8981\u72ec\u81ea\u5ba1\u8baf\u4ed6\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u4ed6\u662f\u5510\u5ba4\u7684\u738b\u5b59\uff0c\u6211\u662f\u6015\u4f60\u4e0d\u8010\u70e6\u542c\u6211\u5ba1\u95ee\uff0c\u770b\u4f60\u4e5f \u6709\u70b9\u75b2\u5026\u4e86\uff0c\u6240\u4ee5\u60f3\u8ba9\u4f60\u6b47\u606f\u6b47\u606f\uff0c\u5f85\u6211\u5ba1\u95ee\u5b8c\u6bd5\uff0c\u7acb\u523b\u56de\u6765\u966a\u4f60\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u5927\u6c57\u5bf9\u6211\u8fd9\u6837\u4f53\u8d34\u5165\u5fae\uff0c\u6211\u975e\u5e38\u611f\u6fc0\u3002\u4f46\u4eca\u65e5\u662f\u4f60\u6211\u4f73\u671f\uff0c\u82e5\u5927\u6c57\u4e0d \u5acc\u6211\u5728\u65c1\u963b\u788d\u7684\u8bdd\uff0c\u6211\u613f\u610f\u966a\u4f60\u5ba1\u95ee\u3002\u201d\u5927\u6c57\u5fc3\u4e2d\u751c\u4e1d\u4e1d\u7684\uff0c\u7b11\u9053\uff1a\u201c\u6211\u53ea\u662f\u6015\u4f60\u4e0d\u611f \u5174\u8da3\u800c\u5df2\uff0c\u96be\u9053\u8fd8\u6015\u4f60\u6cc4\u9732\u673a\u5bc6\u4e48\uff1f\u4f60\u613f\u610f\u966a\u6211\u5ba1\u95ee\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u6211\u5176\u5b9e\u4e5f\u662f\u4e0d\u613f \u7247\u523b\u79bb\u5f00\u4f60\u554a\uff01\u201d\u8bf4\u7740\u8bf4\u7740\u5c31\u6328\u8fd1\u8fc7\u6765\uff0c\u5c06\u6b66\u7384\u971c\u7684\u7389\u624b\u8f7b\u8f7b\u63c9\u6413\uff0c\u6b66\u7384\u971c\u4f46\u89c9\u5927\u6c57\u8eab \u4e0a\u90a3\u4e00\u80a1\u81bb\u8165\u6c14\u5473\uff0c\u76f4\u51b2\u9f3b\u89c2\uff0c\u6697\u6697\u76b1\u7709\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u73b0\u5728\u8ba9\u4f60\u5360\u70b9\u4fbf\u5b9c\uff0c\u7b49\u4e0b\u53ef\u8981 \u4f60\u5927\u5403\u82e6\u5934\u3002\u201d \u3000\u3000\u5927\u6c57\u53eb\u4e00\u4e2a\u4f8d\u536b\u53bb\u5c06\u674e\u9038\u63d0\u6765\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u4f8d\u536b\u56de\u6765\u62a5\u9053\uff1a\u201c\u90a3\u4e2a\u5357\u86ee\u5b50\u7684\u8840\u5df2 \u6b62\u4e86\uff0c\u73b0\u5728\u6b63\u66ff\u4ed6\u88f9\u4f24\uff0c\u7b49\u4e0b\u5c31\u6765\u3002\u8fd9\u662f\u7f34\u83b7\u7684\u5b9d\u5251\uff0c\u732e\u7ed9\u5927\u6c57\u3002\u201d \u3000\u3000\u5927\u6c57\u63a5\u8fc7\u674e\u9038\u90a3\u628a\u5b9d\u5251\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u968f\u624b\u4e00\u6325\uff0c\u5c06\u4e00\u4e2a\u4e09\u8db3\u94dc\u9f0e\u65a9\u65ad\u4e86\u4e00\u8db3\uff0c\u6697\u6697 \u79f0\u8d5e\u9053\uff1a\u201c\u771f\u662f\u5b9d\u5251\uff01\u201d\u6b66\u7384\u971c\u5fc3\u60f3\uff1a\u201c\u674e\u9038\u7684\u5b9d\u5251\u53ef\u4e0d\u80fd\u843d\u5728\u4ed6\u7684\u624b\u4e2d\u3002\u201d\u4fbf\u4e5f\u7b11\u9053\uff1a \u201c\u5927\u6c57\u76d6\u4e16\u82f1\u96c4\uff0c\u6709\u4e86\u8fd9\u628a\u5b9d\u5251\uff0c\u771f\u662f\u76f8\u5f97\u76ca\u5f70\u3002\u6211\u867d\u7136\u4e0d\u61c2\u5b9d\u5251\uff0c\u4f46\u770b\u8fd9\u628a\u5251\u9798\uff0c\u4e5f \u77e5\u662f\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u7269\u3002\u201d\u90a3\u5251\u9798\u7f15\u91d1\u523b\u7389\uff0c\u7f00\u4ee5\u660e\u73e0\uff0c\u5b9d\u6c14\u73e0\u5149\uff0c\u8000\u4eba\u773c\u76ee\uff0c\u6b66\u7384\u971c\u62ff \u8d77\u6765\u770b\u4e86\u53c8\u770b\uff0c\u4f5c\u51fa\u4e00\u526f\u7231\u6781\u4e0d\u5fcd\u91ca\u624b\u7684\u795e\u6c14\u3002 \u3000\u3000\u5927\u6c57\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53ef\u8d3a\u6566\u7231\u5b83\uff0c\u6211\u5c31\u5c06\u8fd9\u628a\u5251\u8d50\u4f60\u4f69\u6234\u5427\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u55ef\uff0c\u8fd9 \u600e\u4e48\u6210\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u53cd\u6b63\u4f69\u5728\u4f60\u7684\u8eab\u4e0a\uff0c\u4e5f\u5c31\u7b49\u5982\u5728\u6211\u7684\u8eab\u4e0a\u4e00\u822c\u3002\u6c49\u4eba\u8bf4\u5b9d\u5251\u8d60\u82f1 \u96c4\uff0c\u6211\u800c\u4eca\u4ee5\u5b9d\u5251\u8d60\u7f8e\u4eba\uff0c\u54c8\u54c8\uff0c\u5c82\u4e0d\u66f4\u662f\u5343\u79cb\u4f73\u8bdd\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u5ae3\u7136\u4e00\u7b11\uff0c\u63a5\u8fc7\u5b9d\u5251\uff0c\u9053\u4e86\u58f0\uff1a\u201c\u591a\u8c22\u5927\u6c57\u3002\u201d\u7a81\u53a5\u5927\u6c57\u7709\u5f00\u773c\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u6c49\u4eba\u6709\u53e5\u6210\u8bed\uff0c\u5927\u610f\u662f\uff1a\u7f8e\u4eba\u4e00\u7b11\uff0c\u8db3\u4ee5\u503e\u56fd\u503e\u57ce\uff0c\u6211\u53ea\u7528\u4e00\u628a\u5b9d\u5251\uff0c\u5c31\u8d62\u5f97\u4e86\u5983\u5b50 \u7684\u6b22\u5fc3\uff0c\u90a3\u662f\u592a\u503c\u5f97\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u6545\u610f\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u674e\u9038\u9002\u624d\u5927\u95f9\u5bab\u5ef7\uff0c\u5927\u6c57\u53ef\u8981\u5904\u6b7b\u4ed6\u4e48\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u4e0d\uff0c \u6211\u7559\u7740\u4ed6\u8fd8\u6709\u7528\u5904\u5462\u3002\u4ed6\u662f\u5510\u5ba4\u7684\u738b\u5b59\uff0c\u82e5\u80fd\u5f52\u987a\u4e8e\u6211\uff0c\u5c06\u6765\u6211\u6253\u8fdb\u4e2d\u539f\uff0c\u90a3\u4e9b\u6548\u5fe0\u5510 \u5b9d\u7684\u81e3\u6c11\uff0c\u4e00\u5b9a\u4f1a\u5e2e\u6211\u6253\u73b0\u5728\u5728\u4f4d\u7684\u4e2d\u56fd\u7684\u5973\u7687\u5e1d\u3002\u4f60\u5927\u7ea6\u4e5f\u542c\u8bf4\u8fc7\u5427\uff0c\u73b0\u5728\u4e2d\u56fd\u7684\u5973 \u7687\u5e1d\u540d\u53eb\u6b66\u5219\u5929\uff0c\u5510\u671d\u7684\u7687\u5e1d\u5b9d\u5ea7\u5c31\u662f\u7ed9\u5979\u7be1\u593a\u4e86\u7684\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u542c\u8bf4\u8fc7\u4e86\uff0c\u6b66\u5219 \u5929\u4ee5\u4e00\u4e2a\u5973\u4eba\u800c\u80fd\u505a\u5230\u7687\u5e1d\uff0c\u4e5f\u7b97\u5f97\u5973\u4e2d\u82f1\u6770\u4e86\u554a\uff01\u201d\u5927\u6c57\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u6240\u4ee5\u6211\u624d \u60f3\u5230\u8981\u5229\u7528\u674e\u9038\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u8fd9\u4e2a\u674e\u9038\uff0c\u4e0d\u77e5\u4ed6\u53ef\u80af\u4f9d\u4ece\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u6211\u6b63\u4e3a\u6b64 \u62c5\u5fe7\uff0c\u770b\u6765\u8fd9\u4e2a\u674e\u9038\u5014\u5f3a\u5f97\u5f88\u3002\u6211\u66fe\u6d3e\u4eba\u53bb\u8bf7\u4ed6\u51fa\u5c71\uff0c\u4ed6\u4e0d\u63a5\u7eb3\uff0c\u4eca\u65e5\u53cd\u800c\u6765\u7ed9\u6211\u5927\u95f9 \u4e00\u573a\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4ed6\u6562\u5728\u6b66\u58eb\u4f1a\u4e0a\u5927\u95f9\uff0c\u5f53\u771f\u662f\u4e00\u4e2a\u4e0d\u6015\u6b7b\u7684\u4eba\uff01\u65e2\u7136\u4ed6\u6b7b\u90fd\u4e0d\u6015\uff0c \u90a3\u4e48\u8fd8\u6709\u4f55\u4e8b\u53ef\u4ee5\u4ee4\u4ed6\u5c48\u670d\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u4ed6\u4e0d\u6015\u6b7b\uff0c\u4f46\u662f\u6211\u4e5f\u8fd8\u6709\u6cd5\u5b50\u6cbb\u4ed6\u3002\u201d\u6b66\u7384 \u971c\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u4ed6\u7684\u513f\u5b50\uff0c\u5728\u6211\u638c\u63e1\u4e4b\u4e2d\u3002\u201d\u5f53\u4e0b\uff0c\u4fbf\u5c06\u4ed6\u600e\u6837\u8bbe\u8ba1\uff0c \u600e\u6837\u6d3e\u9063\u6b66\u58eb\u52ab\u8d70\u4e86\u674e\u9038\u7684\u513f\u5b50\u7b49\u7b49\u4e8b\u60c5\uff0c\u90fd\u5bf9\u6b66\u7384\u971c\u8bf4\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u773c\u73e0\u4e00\u8f6c\uff0c\u88c5\u4f5c\u66ff\u5927\u6c57\u60f3\u8ba1\u7b56\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u6cd5\u5b50\u5f88\u597d\uff0c\u90a3\u4e48\uff0c\u7b49\u4e0b \u5927\u6c57\u5ba1\u95ee\u674e\u9038\u4e4b\u65f6\u3002\u4e0d\u5982\u5c31\u628a\u4ed6\u7684\u513f\u5b50\u4e5f\u62ff\u6765\uff0c\u8ba9\u4ed6\u77a7\u89c1\u3002\u7236\u6bcd\u7231\u5b50\u4e4b\u5fc3\uff0c\u4eba\u7686\u6709\u4e4b\uff0c \u4ed6\u77a7\u89c1\u81ea\u5df1\u7684\u5b69\u5b50\uff0c\u5fc3\u80a0\u8fd8\u4e0d\u8f6f\u5417\uff1f\u201d\u5927\u6c57\u62cd\u638c\u7b11\u9053\uff1a\u201c\u5983\u5b50\uff0c\u4f60\u8bbe\u60f3\u5f97\u771f\u5468\u5230\u3002\u5bf9\uff0c \u5c31\u662f\u8fd9\u4e2a\u529e\u6cd5\uff0c\u4e0d\u6015\u4ed6\u4e0d\u5c31\u8303\u4e86\u3002\u201d\u5f53\u4e0b\uff0c\u7acb\u523b\u6d3e\u4eba\u53bb\u63d0\u674e\u9038\u7684\u5b69\u5b50\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e00\u4e2a\u5bab\u5973\u5c06\u674e\u9038\u7684\u5b69\u5b50\u9001\u6765\uff0c\u6b66\u7384\u971c\u4e00\u770b\uff0c\u8fd9\u4e2a\u5b69\u5b50\u6e05\u7626\u4e86\u8bb8\u591a\uff0c\u4f46\u4e00 \u5bf9\u773c\u73e0\u8fd8\u662f\u9aa8\u788c\u788c\u7684\u7075\u6d3b\u5f97\u5f88\u3002\u6b66\u7384\u971c\u597d\u751f\u601c\u60dc\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5b69\u5b50\u5012\u5f88\u53ef\u7231\u5462\u3002\u201d \u6b63\u60f3\u62c9\u4ed6\u7684\u624b\uff0c\u90a3\u5b69\u5b50\u5ffd\u7136\u81ea\u52a8\u5411\u5979\u8d70\u6765\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u770b\u7740\u5979\u3002\u5927\u6c57\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u5b69 \u5b50\u4e5f\u7ed9\u4f60\u7684\u7f8e\u8c8c\u8ff7\u7740\u4e86\uff01\u201d \u3000\u3000\u90a3\u5b69\u5b50\u770b\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u5bf9\u6b66\u7384\u971c\u8bf4\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u6211\u8ba4\u5f97\u4f60\uff01\u201d\u6b66\u7384\u971c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3 \u60f3\uff1a\u201c\u8fd9\u5b69\u5b50\u7684\u8bb0\u6027\u771f\u597d\uff0c\u6211\u5728\u5929\u5c71\u5c71\u811a\u89c1\u8fc7\u4ed6\u4e00\u9762\uff0c\u5982\u4eca\u9694\u4e86\u4e2a\u591a\u6708\u4e86\uff0c\u6211\u53c8\u5df2\u6539\u5bb9 \u6613\u8c8c\uff0c\u4ed6\u5c45\u7136\u8fd8\u8ba4\u5f97\u6211\u3002\u201d\u8981\u77e5\u5c0f\u5b69\u5b50\u5fc3\u795e\u4e13\u4e00\uff0c\u90a3\u4e00\u665a\u6b66\u7384\u971c\u7ed9\u4ed6\u7684\u5370\u8c61\u592a\u6df1\uff0c\u800c\u4ed6 \u53c8\u662f\u90a3\u4e00\u665a\u88ab\u6b66\u58eb\u63b3\u8d70\u7684\uff0c\u6240\u4ee5\u4ed6\u770b\u591a\u4e86\u4e00\u4f1a\uff0c\u5c31\u8ba4\u51fa\u6b66\u7384\u971c\u6765\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u8bb0\u8d77\u6b66\u7384\u971c\u66fe\u7ed9\u4ed6\u679c\u812f\u5403\uff0c\u53c8\u8bb0\u8d77\u4e86\u4ed6\u88ab\u6b66\u58eb\u7ed1\u67b6\u4e4b\u65f6\uff0c\u6b66\u7384\u971c\u60ca\u60f6\u5927\u53eb\uff0c \u8ffd\u53bb\u6551\u4ed6\u3002\u867d\u7136\u6ca1\u6709\u8ffd\u4e0a\uff0c\u4f46\u8fd9\u5c0f\u5b69\u5b50\u7684\u5fc3\u7075\u5df2\u611f\u5230\u6b66\u7384\u971c\u662f\u7231\u62a4\u4ed6\u7684\u4eba\uff0c\u8fd9\u65f6\u4ed6\u4e00\u77a7 \u89c1\u4e86\u6b66\u7384\u971c\uff0c\u5c31\u50cf\u77a7\u89c1\u4e86\u4eb2\u4eba\u4e00\u822c\u3002 \u3000\u3000\u5927\u6c57\u542c\u5b69\u5b50\u8bf4\u4ed6\u8ba4\u8bc6\u6b66\u7384\u971c\uff0c\u7b11\u9053\uff1a\u201c\u771f\u662f\u5b69\u5b50\u8bdd\uff0c\u4f60\u51e0\u65f6\u89c1\u8fc7\u6211\u7684\u53ef\u8d3a\u6566\u7684\uff1f\u201d \u90a3\u5b69\u5b50\u89c1\u6b66\u7384\u971c\u7a7f\u7684\u662f\u7ef4\u65cf\u738b\u5983\u670d\u9970\uff0c\u4ed6\u8bf4\u7684\u4e5f\u662f\u7ef4\u8bed\u3002\u6b66\u7384\u971c\u5a49\u7136\u4e00\u7b11\uff0c\u5c06\u4ed6\u697c\u5165\u6000 \u4e2d\uff0c\u4eb2\u4e86\u4ed6\u7684\u9762\u988a\u4e00\u4e0b\uff0c\u5374\u8d81\u6b64\u65f6\u673a\uff0c\u4f4e\u58f0\u5728\u4ed6\u8033\u8fb9\u7528\u6c49\u8bed\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u8bf4\u8ba4\u8bc6\u6211\uff0c\u7b49 \u4f1a\u513f\u4f60\u7684\u7238\u7238\u4f1a\u6765\uff0c\u6211\u4f1a\u8bbe\u6cd5\u6551\u4f60\u4eec\u51fa\u53bb\uff0c\u660e\u767d\u4e86\u5417\uff1f\u201d\u6b66\u7384\u971c\u5185\u529f\u6df1\u6e5b\uff0c\u5979\u8d34\u7740\u5c0f\u5b69 \u5b50\u7684\u8033\u8fb9\u8bf4\u8bdd\uff0c\u58f0\u97f3\u7ec6\u82e5\u6e38\u4e1d\uff0c\u90a3\u5b69\u5b50\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u65c1\u8fb9\u7684\u4eba\uff0c\u5374\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u5979\u5728 \u8bf4\u8bdd\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u70b9\u4e86\u70b9\u5934\uff0c\u9762\u5411\u7740\u5927\u6c57\u8bf4\u9053\uff1a\u201c\u5979\u957f\u5f97\u771f\u50cf\u6211\u7684\u5988\u5988\uff0c\u548c\u6211\u5988\u5988\u4e00\u6837\u597d\u770b\uff0c \u55ef\uff0c\u6211\u6b22\u559c\u5979\u3002\u201d\u5927\u6c57\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u4f60\u628a\u5979\u5f53\u6210\u4f60\u7684\u5988\u5988\u4e86\u3002\u201d\u6b66\u7384\u971c\u5fc3\u4e2d\u6697\u8d5e \u8fd9\u5b69\u5b50\u673a\u7075\uff0c\u62c9\u7740\u4ed6\u7684\u624b\u9053\uff1a\u201c\u6211\u4e5f\u6b22\u559c\u4f60\u3002\u201d\u5927\u6c57\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u771f\u662f\u6709\u7f18\uff0c \u82e5\u662f\u674e\u9038\u964d\u987a\u6211\uff0c\u6211\u5c31\u8ba9\u4f60\u6536\u4ed6\u505a\u5e72\u513f\u5b50\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u5916\u9762\u9563\u94d0\u66f3\u5730\u7684\u53ee\u54e8\u58f0\u54cd\uff0c\u6b66\u7384\u971c\u5fc3\u5934\u4e00\u9707\uff0c\u4f46\u89c1\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927 \u7684\u6b66\u58eb\uff0c\u5df2\u628a\u674e\u9038\u62bc\u4e86\u8fdb\u6765\uff01\u5206\u522b\u4e86\u516b\u5e74\uff0c\u4ed6\u4eec\u7ec8\u4e8e\u5728\u7a81\u53a5\u7684\u7687\u5bab\u4e2d\u4f1a\u9762\u4e86\uff0c\u8fd9\u756a\u4f1a\u9762\uff0c \u5982\u6b64\u79bb\u5947\uff0c\u4e24\u4eba\u90fd\u662f\u68a6\u60f3\u4e0d\u5230\uff01 \u3000\u3000\u674e\u9038\u7728\u7728\u773c\u775b\uff0c\u8fd9\u65f6\u4ed6\u4e0e\u6b66\u7384\u971c\u76f8\u8ddd\u4e0d\u8fc7\u4e09\u4e08\u5730\uff0c\u6bd4\u521a\u624d\u770b\u5f97\u66f4\u771f\u5207\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u53eb \u9053\uff1a\u201c\u5440\uff0c\u6ca1\u6709\u770b\u9519\uff0c\u5343\u771f\u4e07\u786e\uff0c\u7edd\u5bf9\u662f\u6b66\u7384\u971c\uff01\u201d\u66f4\u5947\u602a\u7684\u662f\u4ed6\u7684\u513f\u5b50\u501a\u504e\u7740\u6b66\u7384\u971c\uff0c \u7adf\u4f3c\u6bcd\u5b50\u4e00\u822c\u7684\u4eb2\u70ed\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u7239\u7239\u201d\u6251\u4e86\u8fc7\u53bb\uff0c\u674e\u9038\u89c1\u5b69\u5b50\u6e05\u7626\u8bb8\u591a\uff0c\u5fc3\u4e2d\u9178\u695a\uff0c\u8bf4\u9053\uff1a \u201c\u654f\u513f\uff0c\u7239\u7239\u6765\u5f97\u8fdf\u4e86\uff0c\u4ee4\u4f60\u53d7\u82e6\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u51dd\u795e\u7ec6\u770b\uff0c\u674e\u9038\u9762\u8272\u82cd\u767d\uff0c\u4f46\u5374\u4e0d\u4f3c\u53d7\u4e86\u5185\u4f24\uff0c\u5fc3\u4e2d\u7a0d\u7a0d\u653e\u5bbd\u3002\u4f46\u662f\u4ed6\u8eab\u4e0a \u53d7\u4e86\u4e94\u516d\u5904\u4f24\uff0c\u80cc\u4e0a\u7684\u5200\u4f24\u5c24\u5176\u5389\u5bb3\uff0c\u867d\u7136\u88f9\u597d\u7eb1\u5e03\uff0c\u8840\u6c34\u8fd8\u6d78\u900f\u51fa\u6765\u3002\u6b66\u5b98\u971c\u5fc3\u4e2d\u9690 \u9690\u4f5c\u75db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u73ed\u5974\u624d\u4eec\u4e5f\u592a\u53ef\u6076\u4e86\uff0c\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u8fd8\u6015\u4ed6\u9003\u8d70\u5417\uff1f\u7adf\u7136\u7ed9\u4ed6\u5e26\u4e0a \u8fd9\u6837\u6c89\u91cd\u7684\u9563\u94d0\u3002\u201d \u3000\u3000\u62bc\u89e3\u674e\u9038\u7684\u90a3\u4e2a\u6b66\u58eb\u662f\u9ebb\u7ffc\u8d5e\uff0c\u4ed6\u89c1\u5b69\u5b50\u6251\u4e86\u4e0a\u6765\uff0c\u4fbf\u60f3\u62e6\u963b\uff0c\u5927\u6c57\u8bf4\u9053\uff1a\u201c\u5c31\u8ba9 \u4ed6\u4eec\u7236\u5b50\u53d9\u4e00\u4f1a\u5427\u3002\u201d\u9ebb\u7ffc\u8d5e\u9053\uff1a\u201c\u83e9\u63d0\u4e0a\u4eba\u6050\u9632\u6709\u5931\uff0c\u4ea6\u5df1\u6765\u4e86\uff0c\u8981\u4e0d\u8981\u53ec\u3002\u201d\u5927\u6c57 \u9053\uff1b\u201c\u5c31\u59d4\u5c48\u4ed6\u5728\u5916\u9762\u6682\u4f5c\u5b88\u536b\u5427\uff0c\u63d0\u9632\u523a\u5ba2\u5165\u5bab\u3002\u201d\u83e9\u63d0\u4e0a\u4eba\u662f\u7a81\u53a5\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u5927 \u6c57\u66fe\u7ecf\u60f3\u5c01\u4ed6\u505a\u56fd\u5e08\uff0c\u6240\u4ee5\u5bf9\u4ed6\u751a\u4e3a\u5ba2\u6c14\u3002 \u3000\u3000\u6b66\u7384\u971c\u77e5\u9053\u9ebb\u7ffc\u8d5e\u6b66\u529f\u751a\u597d\uff0c\u5fc3\u4e2d\u4e00\u51db\u3002\u60f3\u9053\uff1a\u201c\u6709\u9ebb\u7ffc\u8d5e\u5728\u6b64\u3002\u53c8\u6709\u83e9\u63d0\u4e0a\u4eba\u5728 \u5916\u9762\u76d1\u89c6\uff0c\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u90a3\u5b69\u5b50\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u7ed1\u4f60\uff1f\u6211\u60f3\u4f60\u62b1\u62b1\u6211\u3002\u554a\uff01\u201d\u5927\u6c57\u7b11\u9053\uff1b\u201c\u597d\u5b69 \u5b50\uff0c\u4f60\u529d\u4f60\u7684\u7239\u7239\u542c\u6211\u7684\u8bdd\uff0c\u6211\u9a6c\u4e0a\u5c31\u653e\u4e86\u4f60\u7684\u7239\u7239\u3002\u201d\u674e\u9038\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u654f\u513f\uff0c\u4e0d\u8981 \u542c\u574f\u4eba\u7684\u8bdd\uff01\u201d \u3000\u3000\u90a3\u5b69\u5b50\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u542c\u4ed6\u4eec\u7684\u8bdd\u3002\u201d\u4ed6\u633a\u4e86\u633a\u80f8\u3002\u9762\u5411\u7740\u5927\u6c57\u8bf4\u9053\uff1a\u201c\u7239\u7239\u6559 \u8bad\u8fc7\u6211\uff0c\u5bf9\u574f\u4eba\u4e0d\u53ef\u5c48\u670d\u3002\u4f60\u5bf9\u6211\u7684\u7239\u7239\u8fd9\u6837\u51f6\u3002\u4f60\u662f\u574f\u4eba\uff01\u201d \u3000\u3000\u5927\u6c57\u9762\u8272\u4e00\u6c89\u3001\u4f46\u968f\u5373\u4fbf\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u4f36\u4fd0\u7684\u5b69\u5b50\uff0c\u53ef\u60dc\u4f60\u5e74\u7eaa\u592a\u5c0f\uff0c\u4f60\u8fd8\u672a\u660e\u767d \u6211\u5bf9\u4f60\u7684\u7239\u7239\u5b9e\u662f\u4e00\u756a\u597d\u610f\u3002\u597d\uff0c\u9ebb\u7ffc\u8d5e\uff0c\u4f60\u628a\u8fd9\u5b69\u5b50\u62c9\u5f00\uff0c\u8ba9\u6211\u548c\u4ed6\u7684\u7239\u7239\u8bf4\u8bdd\u3002\u201d \u90a3\u5b69\u5b50\u4e0d\u60f3\u8d70\u5f00\uff0c\u4f46\u4ed6\u600e\u62b5\u6297\u5f97\u4e86\u9ebb\u7ffc\u8d5e\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u8981\u96be\u4e3a\u8fd9\u4e2a\u5b69\u5b50\uff01\u201d\u4eb2\u81ea\u5c06 \u5b69\u5b50\u63a5\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4e0d\u8981\u5435\u95f9\u3002\u201d\u90a3\u5b69\u5b50\u679c\u7136\u5f88\u542c\u5979\u7684\u8bf4\u8bdd\uff0c\u670d\u670d\u8d34 \u8d34\u7684\u4f9d\u504e\u5728\u5979\u7684\u8eab\u65c1\u3002 \u3000\u3000\u674e\u9038\u5982\u5728\u68a6\u4e2d\uff0c\u89c9\u5f97\u5947\u602a\u6781\u4e86\uff0c\u6b66\u7384\u971c\u600e\u4e48\u4f1a\u53d8\u6210\u4e86\u738b\u5983\uff1f\u654f\u513f\u4e3a\u4ec0\u4e48\u80af\u542c\u5979\u7684\u8bdd\uff1f \u4ed6\u54ac\u4e86\u54ac\u820c\u5934\uff0c\u5f88\u75db\uff0c\u8fd9\u7684\u786e\u4e0d\u662f\u68a6\u5440\uff0c\u4f46\u8fd9\u79cd\u79cd\u5947\u602a\u7684\u4e8b\u60c5\u53c8\u8be5\u5982\u4f55\u89e3\u91ca\uff1f\u4f46\u4e8b\u6001\u5c3d \u7ba1\u79bb\u5947\u96be\u89e3\uff0c\u4ed6\u5fc3\u4e2d\u5374\u6709\u4e00\u4e2a\u4fe1\u5ff5\uff0c\u6b66\u7384\u971c\u7edd\u5bf9\u4e0d\u4f1a\u53db\u56fd\u6295\u654c\uff0c\u5979\u4e5f\u7edd\u4e0d\u4f1a\u5bf9\u81ea\u5df1\u5b58\u6709 \u574f\u5fc3\u3002 \u3000\u3000\u5927\u6c57\u659f\u4e86\u4e00\u676f\u9152\uff0c\u5bf9\u8eab\u65c1\u7684\u5bab\u5973\u8bf4\u9053\uff1a\u201c\u4f60\u66ff\u4ed6\u62b9\u51c0\u8138\u4e0a\u7684\u8840\u6c61\uff0c\u518d\u8bf7\u4ed6\u559d\u4e00\u676f\u9152\u3002\u201d \u674e\u9038\u5e26\u7740\u624b\u94d0\u811a\u9563\uff0c\u53ea\u597d\u7531\u5979\u6446\u5e03\uff0c\u90a3\u5bab\u5973\u5c06\u4e00\u6761\u6e7f\u900f\u4e86\u7684\u4e1d\u5dfe\uff0c\u8f7b\u8f7b\u66ff\u4ed6\u63e9\u8138\uff0c\u63e9\u62ed \u4e4b\u540e\uff0c\u7a81\u7136\u53d1\u89c9\u674e\u9038\u5bb9\u5149\u7115\u53d1\uff0c\u5982\u540c\u6362\u4e86\u4e2a\u4eba\uff01\u5bab\u5973\u5403\u4e86\u4e00\u60ca\uff0c\u5927\u6c57\u9053\uff1a\u201c\u628a\u4ed6\u7684\u987b\u5b50 \u62d4\u4e0b\u6765\uff01\u201d\u5bab\u5973\u5927\u7740\u80c6\u5b50\u4e00\u626f\uff0c\u674e\u9038\u7684\u80e1\u987b\u5e94\u624b\u800c\u843d\uff0c\u7a81\u53a5\u5927\u6c57\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c \u679c\u7136\u662f\u5927\u5510\u7684\u674e\u6bbf\u4e0b\uff0c\u4f60\u6539\u88c5\u5f97\u771f\u5de7\u5999\u554a\uff01\u201d\u539f\u6765\u5927\u6c57\u65e9\u85cf\u6709\u674e\u9038\u7684\u753b\u50cf\uff0c\u90a3\u662f\u6b66\u627f\u55e3 \u4f7f\u8005\u5c01\u7267\u91ce\u9001\u7ed9\u4ed6\u7684\uff0c\u6240\u4ee5\u4ed6\u8981\u5bab\u5973\u62ed\u53bb\u674e\u9038\u8138\u4e0a\u7684\u5316\u88c5\uff0c\u4e0e\u753b\u56fe\u5bf9\u7167\u3002\u662f\u5426\u76f8\u7b26\u3002 \u3000\u3000\u674e\u9038\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u884c\u4e0d\u66f4\u540d\uff0c\u5750\u4e0d\u6539\u59d3\uff0c\u6211\u4fbf\u662f\u674e\u9038\uff0c\u4ee5\u672c\u6765\u9762\u89c1\u4f60\uff0c\u53c8\u6709 \u4f55\u59a8\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u6211\u4f69\u670d\u4f60\u7684\u80c6\u91cf\uff0c\u5148\u8bf7\u4f60\u559d\u4e00\u676f\u9152\uff0c\u63d0\u63d0\u7cbe\u795e\u3002\u201d\u674e\u9038\u6599\u4ed6\u8fd8\u8981\u5229 \u7528\u81ea\u5df1\uff0c\u4e0d\u81f3\u4e8e\u5728\u9152\u4e2d\u4e0b\u6bd2\uff0c\u5f20\u5f00\u4e86\u53e3\uff0c\u5c06\u5b98\u5973\u9001\u6765\u7684\u7f8e\u9152\u4e00\u559d\u800c\u5c3d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5927 \u4e08\u592b\u4e0d\u6015\u5c71\u5251\u6811\uff0c\u4e5f\u4e0d\u6015\u7f8e\u9152\u7518\u8a00\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u82b1\u62db\uff1f\u201d\u5927\u6c57\u4f38\u51fa\u62c7\u6307\u9053\uff1a\u201c\u597d\uff0c\u786e\u662f \u4e00\u6761\u6c49\u5b50\uff0c\u6211\u6b63\u8981\u7528\u4f60\u8fd9\u6837\u7684\u4eba\uff01\u201d \u3000\u3000\u674e\u9038\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b66\u627f\u55e3\u4e4b\u6d41\u53ef\u4ee5\u4e3a\u4f60\u6240\u7528\uff0c\u6211\u674e\u9038\u5374\u4e0d\u662f\u90a3\u6837\u7684\u4eba\u3002\u201d \u5927\u6c57\u9053\uff1a\u201c\u54b1\u4eec\u6162\u6162\u8c08\u5427\u3002\u4f60\u8bf4\u8fc7\uff0c\u5bf9\u574f\u4eba\u4e0d\u53ef\u5c48\u670d\uff0c\u8fd9\u8bdd\u8bf4\u5f97\u5f88\u597d\u3002\u90a3\u6211\u95ee\u4f60\uff0c\u6b66\u5219 \u5929\u5979\u662f\u4e0d\u662f\u574f\u4eba\uff1f\u201d\u674e\u9038\u770b\u4e86\u6b66\u7384\u971c\u4e00\u773c\uff0c\u60f3\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u5979\u662f\u4e0d\u662f\u574f\u4eba\uff0c\u6211\u4e0d\u80fd\u65ad \u5b9a\u3002\u201d\u5927\u6c57\u9053\uff1a\u201c\u6700\u5c11\u5979\u603b\u662f\u4f60\u7684\u4ec7\u4eba\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u7be1\u593a\u4e86\u6211\u5bb6\u7684\u7687\u4f4d\uff0c\u5f53 \u7136\u662f\u6211\u7684\u654c\u4eba\uff01\u201d\u7a81\u53a5\u5927\u6c57\u542c\u4e86\uff0c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u7b11\u4f60\u4e0d\u8bc6\u597d\u574f\uff01\u201d\u674e\u9038\u53cc\u7709\u4e00\u7ad6\uff0c\u9053\uff1a\u201c\u6211\u600e \u4e48\u4e0d\u8bc6\u597d\u574f\u4e86\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u6b66\u5219\u5929\u62a2\u4e86\u5510\u5ba4\u7684\u6c5f\u5c71\uff0c\u4f60\u4e5f\u627f\u8ba4\u5979\u662f\u4f60\u7684\u654c\u4eba\uff0c\u5982\u4eca\u6211 \u8981\u8fdb\u5175\u8ba8\u4f10\u5979\uff0c\u4e5f\u5c31\u662f\u5e2e\u4f60\u6253\u5012\u4f60\u7684\u654c\u4eba\uff0c\u54b1\u4eec\u6b63\u8be5\u540c\u4ec7\u5171\u654c\uff0c\u4f60\u5374\u4e3a\u4f55\u4e0e\u6211\u4f5c\u5bf9\uff1f\u8fd9 \u5c82\u4e0d\u662f\u4e0d\u8bc6\u597d\u574f\u4e48\uff1f\u201d \u3000\u3000\u674e\u9038\u559d\u9053\uff1a\u201c\u4f4f\u53e3\uff01\u201d\u5927\u6c57\u9053\uff1a\u201c\u600e\u4e48\uff0c\u6211\u8bf4\u9519\u4e86\u4e48\uff1f\u201d\u674e\u9038\u4ece\u5bb9\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u662f \u8bf4\u9519\u4e86\uff01\u7eb5\u8bb8\u6211\u4eec\u59d3\u674e\u7684\u4e0e\u59d3\u6b66\u7684\u4e89\u593a\u6c5f\u5c71\uff0c\u90a3\u4e5f\u662f\u6211\u4eec\u4e2d\u56fd\u4eba\u4e89\u593a\u4e2d\u56fd\u7684\u6c5f\u5c71\uff0c\u4e0e\u4f60 \u4f55\u5e72\uff1f\u4f60\u501f\u8ba8\u4f10\u6b66\u5219\u5929\u4e3a\u540d\uff0c\u5206\u660e\u662f\u60f3\u5360\u593a\u6211\u5927\u5510\u7684\u82b1\u82b1\u4e16\u754c\uff0c\u9526\u7ee3\u4e7e\u5764\u3002\u51e1\u662f\u5927\u5510\u5b50 \u6c11\uff0c\u90fd\u8be5\u6267\u5343\u6208\u4ee5\u5fa1\u793e\u7a37\uff0c\u4f55\u51b5\u6211\u662f\u5510\u5ba4\u7684\u738b\u5b59\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u542c\u4ed6\u8bf4\u5f97\u5927\u4e49\u51db\u7136\uff0c\u82b3\u5fc3\u5927\u6170\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u867d\u7136\u5c1a\u6709\u4e00\u5bb6\u4e00\u63fd\u7684\u89c2\u5ff5\uff0c\u4f46\u5bf9 \u5927\u662f\u5927\u975e\u4e4b\u5904\uff0c\u5374\u770b\u5f97\u751a\u662f\u5206\u660e\u3002\u602a\u4e0d\u5f97\u59d1\u59d1\u4e5f\u60f3\u8bf7\u4ed6\u56de\u53bb\u3002\u201d \u3000\u3000\u7a81\u53a5\u5927\u6c57\u6014\u4e86\u4e00\u6014\uff0c\u7b11\u5bb9\u987f\u655b\uff0c\u6362\u4e86\u4e00\u4ed8\u9762\u5b54\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e3a\u4e86\u8fd9\u6837\uff0c \u624d\u4e0e\u6211\u4f5c\u5bf9\u4e48\uff1f\u201d\u674e\u9038\u6012\u9053\uff1b\u201c\u4f60\u8981\u5360\u593a\u5927\u5510\u7684\u6c5f\u5c71\uff0c\u6211\u8fd8\u4e0d\u8be5\u4e0e\u4f60\u4f5c\u5bf9\u4e48\uff1f\u201d\u5927\u6c57\u5ffd \u5730\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u9519\u4e86\uff01\u4f60\u4e0d\u8981\u5fd8\u8bb0\uff0c\u6b66\u5219\u5929\u65e9\u5df2\u6539\u4e86\u4f60\u5927\u5510\u7684\u56fd\u53f7\u4e86\u3002\u4f60\u77e5\u9053 \u6211\u8bf7\u4f60\u524d\u6765\uff0c\u662f\u4e3a\u4e86\u4ec0\u4e48\u5417\uff1f\u201d\u674e\u9038\u51b7\u7b11\u9053\uff1a\u201c\u603b\u4e0d\u4f1a\u662f\u4ec0\u4e48\u597d\u4e8b\u5427\uff1f\u201d\u5927\u6c57\u5927\u7b11\u9053\uff1a \u201c\u6240\u4ee5\u6211\u8bf4\u4f60\u9519\u4e86\uff01\u4f60\u603b\u662f\u5bf9\u6211\u731c\u7591\uff0c\u53ef\u77e5\u6211\u662f\u60f3\u628a\u4e2d\u56fd\u7687\u5e1d\u7684\u5b9d\u5ea7\u5949\u9001\u7ed9\u4f60\u4e48\uff1f\u5174\u7684 \u662f\u4ec1\u4e49\u4e4b\u9890\uff0c\u7ed9\u4f60\u4eec\u4e2d\u56fd\u9664\u6389\u80c6\u6562\u4ee5\u5973\u5b50\u505a\u7687\u5e1d\u7684\u5996\u5b7d\uff0c\u6253\u5012\u6b66\u5219\u5929\u4e4b\u540e\uff1b\u6211\u5c31\u6276\u52a9\u4f60 \u505a\u7687\u5e1d\uff0c\u5927\u5510\u4e00\u7edf\u6c5f\u5c71\uff0c\u5168\u5f52\u4f60\u7ba1\u3002\u4f60\u8fd8\u8981\u600e\u6837\uff1f\u4f60\u8fd8\u8bf4\u8fd9\u4e0d\u662f\u4e00\u4ef6\u5929\u5927\u7684\u597d\u4e8b\u4e48\uff1f \u3000\u3000\u674e\u9038\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u756a\u8bf4\u8bdd\uff0c\u53ea\u597d\u54c4\u9a97\u4e09\u5c81\u7684\u5c0f\u5b69\uff01\u54fc\uff0c\u54fc\uff0c\u4e5f\u8bb8\u9a97\u4e09\u5c81\u7684\u5c0f\u5b69\u4e5f \u9a97\u4e0d\u5230\uff01\u4f60\u5984\u52a8\u5e72\u6208\uff0c\u5374\u539f\u6765\u662f\u4e3a\u4e86\u8bf7\u6211\u505a\u7687\u5e1d\uff1f\u54c8\uff0c\u54c8\uff01\u4f60\u81ea\u5df1\u5c31\u4e0d\u60f3\u5f97\u70b9\u597d\u5904\uff1f\u4f60 \u4f55\u5fc5\u4e3a\u4e86\u6211\u7684\u539f\u6545\uff0c\u8017\u635f\u4f60\u7684\u7a81\u53a5\u7684\u56fd\u529b\uff0c\u727a\u7272\u4f60\u7a81\u53a5\u7684\u58eb\u5175\uff1f\u201d \u3000\u3000\u5927\u6c57\u4fa7\u76ee\u659c\u77a7\uff0c\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u95ee\u5f97\u597d\uff01\u82e5\u8bf4\u6211\u4e0d\u60f3\u5f97\u5230\u4e00\u70b9\u597d\u5904\uff0c\u96be\u602a\u4f60 \u4e0d\u76f8\u4fe1\u3002\u597d\uff0c\u6211\u5c31\u544a\u8bc9\u4f60\u5427\uff0c\u6211\u4e0d\u8fc7\u662f\u8981\u4e2d\u56fd\u6210\u4e3a\u6211\u7684\u5c5e\u56fd\u800c\u5df2\uff0c\u4e2d\u56fd\u7684\u58eb\u5730\u767e\u59d3\uff0c\u4ecd \u7136\u5f52\u4f60\u6cbb\u7406\u3002\u4f60\u6240\u5f97\u7684\u597d\u5904\uff0c\u4e0d\u662f\u6bd4\u6211\u66f4\u5927\u4e48\uff1f\u201d \u3000\u3000\u674e\u9038\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u5927\u6c57\uff0c\u4f60\u770b\u9519\u4eba\u4e86\uff0c\u6211\u674e\u9038\u4e0d\u662f\u505a\u7687\u5e1d\u7684\u4eba\uff01\u201d\u5927\u6c57\u9053\uff1a\u201c\u5413\uff0c \u7687\u5e1d\u7684\u5b9d\u5ea7\u4f60\u90fd\u4e0d\u8981\uff1f\u4f60\u8981\u4ec0\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u662f\u4e2d\u56fd\u4eba\uff0c\u4f4f\u5728\u8d35\u56fd\uff0c\u4f46\u613f\u89c1\u5230\u8d35\u6211 \u4e24\u56fd\u548c\u7766\u4ea4\u597d\uff0c\u6211\u6240\u8981\u7684\uff0c\u4fbf\u662f\u60f3\u8bf7\u4f60\u606f\u4e86\u5e72\u6208\u3002\u201d \u3000\u3000\u5927\u6c57\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u4f60\u60f3\u6e05\u695a\u4e86\uff0c\u53ef\u522b\u540e\u6094\uff01\u201d\u674e\u9038\u5927\u58f0\u8bf4\u9053\uff1a \u201c\u6211\u672c\u6765\u5c31\u4e0d\u60f3\u505a\u7687\u5e1d\uff0c\u4f55\u540e\u6094\u4e4b\u6709\uff1f\u4f60\u8981\u52a8\u5e72\u6208\uff0c\u4ee5\u5375\u51fb\u77f3\uff0c\u53c8\u4e0d\u662f\u6211\u800c\u662f\u4f60\uff01\u4f60\u60f3 \u6e05\u695a\u4e86\u6ca1\u6709\u3002\u201d \u3000\u3000\u7a81\u53a5\u5927\u6c57\u9762\u8272\u94c1\u9752\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u5a01\u4e34\u4e07\u56fd\uff0c\u6218\u65e0\u4e0d\u80dc\uff0c\u653b\u65e0\u4e0d\u514b\uff0c\u4e0d\u5fc5\u4f60\u4e3a\u6211 \u62c5\u5fc3\uff0c\u8bf7\u4f60\u4e0d\u8981\u5fd8\u8bb0\uff0c\u4f60\u73b0\u5728\u4e43\u662f\u5728\u6211\u7684\u638c\u63e1\u4e4b\u4e2d\uff0c\u4f60\u4e0d\u80af\u4f9d\u4ece\u6211\uff0c\u90a3\u5c31\u662f\u6211\u7684\u654c\u4eba\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5927\u4e0d\u4e86\u4f60\u628a\u6211\u6740\u6389\uff0c\u6211\u4f55\u987b\u6015\u4f60\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u597d\u6c49\uff0c \u4f60\u4e0d\u6015\u6b7b\uff01\u4f60\u7684\u513f\u5b50\u5462\uff1f\u4f60\u4e0d\u987e\u81ea\u5df1\uff0c\u8fde\u513f\u5b50\u4e5f\u4e0d\u7231\u60dc\u4e86\u4e48\uff1f\u201d\u674e\u9038\u7684\u513f\u5b50\u5ffd\u5730\u5927\u58f0\u53eb \u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u4e5f\u4e0d\u6015\u6b7b\uff01\u201d\u674e\u9038\u7b11\u9053\uff1a\u201c\u597d\uff0c\u654f\u513f\uff0c\u4f60\u662f\u6211\u7684\u597d\u5b69\u5b50\uff01\u201d \u3000\u3000\u7a81\u53a5\u5927\u6c57\u4e00\u76b1\u7709\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u5929\u4e0b\u7adf\u7136\u6709\u8fd9\u7965\u5014\u5f3a\u7684\u4eba\uff01\u201d\u8138\u4e0a\u7684\u6740\u6c14\u5ffd\u9690\u5ffd\u73b0\uff0c \u7247\u523b\u4e4b\u95f4\u4e00\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u5140\u662f\u6253\u4e0d\u5b9a\u4e3b\u610f\uff1a\u662f\u7acb\u5373\u6740\u4e86\u674e\u9038\u5462\uff1f\u8fd8\u662f\u628a\u4ed6\u56da\u7981\u8d77\u6765\uff0c \u518d\u60f3\u6cd5\u8f6f\u5316\u4ed6\uff1f \u3000\u3000\u6b63\u5728\u5927\u6c57\u8e0c\u8e87\u672a\u51b3\u4e4b\u9645\uff0c\u5b88\u95e8\u7684\u6b66\u58eb\u5ffd\u5728\u5916\u9762\u62c9\u52a8\u94c1\u73af\uff0c\u6572\u4e86\u51e0\u4e0b\uff0c\u5927\u6c57\u559d\u9053\uff1a \u201c\u6709\u4f55\u4e8b\u7980\u62a5\uff1f\u201d\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u767e\u4f18\u4e0a\u4eba\u4e0e\u5929\u6076\u9053\u4eba\u5728\u5bab\u95e8\u5916\u5019\u89c1\uff01\u201d\u539f\u6765\u7a81\u53a5\u5927\u6c57\u5bab \u7981\u68ee\u4e25\uff0c\u4ed6\u79d8\u5bc6\u5728\u5bdd\u5bab\u91cc\u5ba1\u8baf\u674e\u9038\uff0c\u9ebb\u7ffc\u8d5e\u662f\u4ed6\u6700\u4eb2\u4fe1\u7684\u6b66\u58eb\u4e4b\u4e00\uff0c\u4ed6\u8ba9\u4ed6\u5728\u5bdd\u5bab\u91cc\u9632 \u8303\u674e\u9038\u3002\u5bdd\u5bab\u7d27\u95ed\u5927\u95e8\uff0c\u53e6\u4e00\u4f4d\u4eb2\u4fe1\u5fc3\u8179\u5728\u95e8\u5916\u8b66\u6212\u3002\u5373\u4ee5\u767e\u5fe7\u4e0a\u4eba\u4e4b\u5c0a\uff0c\u4e5f\u53ea\u80fd\u5728\u4e09 \u91cd\u95e8\u5916\uff0c\u901a\u540d\u5019\u89c1\u3002 \u3000\u3000\u5927\u6c57\u626c\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u73b0\u5728\u6b63\u6709\u4e8b\u60c5\uff0c\u8bf7\u56fd\u5e08\u8fc7\u4e00\u4e2a\u65f6\u8fb0\u518d\u8bf4\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u767e\u5fe7\u3001\u5929\u6076\u4e3a\u4ec0\u4e48\u5728\u8fd9\u4e2a\u65f6\u5019\u6c42\u89c1\uff1f\u201d\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u542c \u5f97\u95e8\u5916\u7684\u6b66\u58eb\u53c8\u62c9\u52a8\u94c1\u73af\uff0c\u7980\u9053\uff1a\u201c\u83e9\u63d0\u4e0a\u4eba\u5df2\u5411\u56fd\u5e08\u4f20\u4e86\u5927\u6c57\u7684\u8c15\u65e8\uff0c\u4f46\u56fd\u5e08\u8bf4\uff0c\u4ed6 \u6709\u975e\u5e38\u3001\u975e\u5e38\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u975e\u5f97\u7acb\u5373\u8c12\u89c1\u5927\u6c57\u4e0d\u53ef\uff01\u201d \u3000\u3000\u539f\u6765\u5929\u6076\u9053\u4eba\u5728\u5bb4\u4f1a\u6563\u540e\uff0c\u60f3\u8d77\u4e86\u65b0\u738b\u5983\u7684\u79cd\u79cd\u53ef\u7591\u4e4b\u5904\uff0c\u4ed6\u662f\u548c\u6b66\u7384\u971c\u4ea4\u8fc7\u51e0\u6b21 \u624b\u7684\uff0c\u5f53\u65f6\u4e0d\u6562\u60f3\u5230\u662f\u5979\uff0c\u8fc7\u540e\u8d8a\u60f3\u8d8a\u7591\uff0c\u53c8\u60f3\u5230\u5c01\u7267\u91ce\u4e34\u6b7b\u4e4b\u65f6\uff01\u9762\u5bf9\u7740\u65b0\u738b\u5983\u8bf4\u51fa \u7684\u90a3\u53e5\u672a\u8bf4\u5b8c\u7684\u8bdd\uff1a\u201c\u4f60\u3001\u4f60\u662f\u6b66\u5219\u5929\u7684\u2026\u2026\u201d\u8fd9\u4e2a\u201c\u4f60\u201d\u6599\u60f3\u4e0d\u662f\u6307\u674e\u9038\u800c\u662f\u6307\u65b0\u738b \u5983\uff0c\u4ed6\u5927\u80c6\u63a8\u60f3\uff0c\u5ffd\u7136\u60f3\u5230\u4e86\u8fd9\u5fc5\u5b9a\u662f\u6b66\u7384\u971c\u65e0\u7591\u3002\u4f46\u5179\u4e8b\u4f53\u5927\uff0c\u4ed6\u4e0d\u6562\u72ec\u62c5\u5e72\u7cfb\u6240\u4ee5 \u9080\u4e86\u767e\u5fe7\u4e0a\u4eba\u540c\u6765\u3002\u83e9\u63d0\u4e0a\u4eba\u867d\u4e0e\u5929\u6076\u4e0d\u7766\uff0c\u4f46\u4e00\u542c\u5230\u8fd9\u662f\u4e0e\u5927\u6c57\u6027\u547d\u500f\u5173\u7684\u4e8b\u60c5\uff0c\u4e5f \u5c31\u4e0d\u6562\u62e6\u963b\u4ed6\u4eec\u4e86\u3002 \u3000\u3000\u4f46\u542c\u5f97\u767e\u5fe7\u3001\u5929\u6076\u4e8c\u4eba\u7684\u811a\u6b65\u58f0\u5df2\u5728\u5916\u9762\u4f20\u6765\uff0c\u5927\u6c57\u751a\u4e3a\u60ca\u8be7\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5947\u602a\uff0c \u4ed6\u4eec\u6709\u975e\u5e38\u7d27\u8981\u7684\u4e8b\u60c5\uff1f\u201d\u6b63\u60f3\u9ebb\u7ffc\u8d5e\u5f00\u95e8\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u6b66\u7384\u971c\u7a81\u7136\u8dc3\u8d77\uff0c\u51fa\u6307\u5982\u7535\uff0c \u500f\u7684\u5c31\u70b9\u4e86\u9ebb\u7ffc\u8d5e\u7684\u7a74\u9053\uff01\u9ebb\u7ffc\u8d5e\u7684\u6b66\u529f\u672c\u4e8b\u4e0d\u5728\u6b66\u7384\u971c\u4e4b\u4e0b\uff0c\u4f46\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u65b0\u738b \u5983\u7adf\u4f1a\u88ad\u51fb\u4ed6\u3001\u51b7\u4e0d\u53ca\u9632\uff0c\u4f46\u89c9\u80c1\u4e0b\u4e00\u9ebb\uff0c\u672a\u66fe\u53eb\u5f97\u51fa\u58f0\uff0c\u7acb\u523b\u4fbf\u5168\u8eab\u50f5\u786c\uff0c\u6709\u5982\u4e00\u5c0a \u77f3\u50cf\uff0c\u524d\u811a\u5c1a\u672a\u8e0f\u4e0b\uff0c\u4fbf\u50f5\u5728\u90a3\u513f\u4e86\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u67d4\u80a0\u4fa0\u9aa8\u60c5\u65e0\u9650 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u67d4\u80a0\u4fa0\u9aa8\u60c5\u65e0\u9650 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u53ef\uff0c\u53ef\u8d3a\u6566\uff0c\u4f60\uff0c\u4f60\u662f\u2026\u2026\u201d\u201c\u4f60\u662f\u8c01\u4eba\uff1f\u201d \u8fd9\u53e5\u8bdd\u5c1a\u672a\u66fe\u95ee\u5f97\u51fa\u6765\uff0c\u5ffd\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u904d\u4f53\u751f\u5bd2\uff0c\u6b66\u7384\u971c\u65e9\u5df2\u62d4\u51fa\u5b9d\u5251\u6401\u5728\u4ed6\u7684\u9888\u9879\uff0c \u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u82e5\u6562\u4e71\u56b7\uff0c\u4e00\u5251\u8981\u4f60\u7684\u547d\uff01\u201d\u7a81\u53a5\u5927\u6c57\u672c\u6765\u751a\u4e3a\u52c7\u6b66\uff0c\u4f46\u88ab\u6b66\u7384 \u971c\u7528\u5927\u64d2\u62ff\u624b\u6cd5\u4e00\u628a\u6263\u7740\u4ed6\u7684\u624b\u8155\uff0c\u7adf\u662f\u534a\u70b9\u4e0d\u80fd\u52a8\u5f39\uff0c\u8fd9\u65f6\u4ed6\u624d\u77e5\u9053\u201c\u65b0\u738b\u5983\u201d\u4e43\u662f \u975e\u5e38\u4e4b\u4eba\uff0c\u5b9d\u5251\u6401\u5728\u7684\u9888\u4e0a\uff0c\u4e0d\u7531\u5f97\u4ed6\u4e0d\u670d\u670d\u8d34\u8d34\u3002 \u3000\u3000\u65c1\u8fb9\u4f8d\u7acb\u7684\u90a3\u4e2a\u5bab\u5973\u5df2\u5413\u5f97\u5446\u4e86\uff0c\u6b66\u7384\u971c\u987a\u624b\u53c8\u70b9\u4e86\u5979\u7684\u7a74\u9053\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u767e\u5fe7\u3001 \u5929\u6076\u4e8c\u4eba\uff0c\u5df2\u5230\u95e8\u5916\uff0c\u53ea\u542c\u5f97\u767e\u5fe7\u4e0a\u4eba\u7684\u58f0\u97f3\u7980\u9053\uff1a\u201c\u8d2b\u50e7\u6709\u4e8b\uff0c\u7980\u62a5\u5927\u6c57\uff01\u201d\u6b66\u7384\u971c \u5728\u4ed6\u8033\u8fb9\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5927\u58f0\u8bf4\uff0c\u8bf7\u56fd\u5e08\u7a0d\u5f85\u7247\u523b\u3002\u201d\u7a81\u53a5\u5927\u6c57\u4e0d\u6562\u4e0d\u4f9d\uff0c\u5927\u58f0\u8bf4\u4e86\uff0c \u58f0\u97f3\u98a4\u6218\uff0c\u81ea\u662f\u4e0d\u514d\u663e\u51fa\u60ca\u4ff1\u4e4b\u60c5\u3002\u6b66\u7384\u971c\u6599\u5230\u767e\u5fe7\u4e0a\u4eba\u3001\u5929\u6076\u9053\u4eba\u5b9a\u4f1a\u8d77\u7591\uff0c\u4f46\u4e5f\u6599 \u5230\u4ed6\u4eec\u6682\u65f6\u8fd8\u4e0d\u6562\u7834\u95e8\u800c\u5165\uff0c\u5f53\u4e0b\u8fc5\u5373\u70b9\u4e86\u5927\u6c57\u7684\u9ebb\u7a74\uff0c\u5728\u4ed6\u8eab\u4e0a\u641c\u51fa\u4e00\u9762\u91d1\u724c\uff0c\u8fd9\u624d \u5bf9\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5361\u6d1b\u4e1d\uff0c\u6211\u662f\u4e2d\u56fd\u5973\u7687\u5e1d\u6d3e\u6765\u7684\u4eba\uff0c\u4f60\u660e\u767d\u4e86\u5427\uff1f\u4f60\u602a\u4e0d\u5f97\u8c01\uff0c\u53ea \u80fd\u602a\u4f60\u4e0d\u957f\u773c\u775b\uff0c\u4eca\u65e5\u6211\u6682\u4e14\u70e7\u4f60\uff0c\u4f60\u82e5\u6562\u682a\u8fde\u65e0\u8f9c\uff0c\u6211\u968f\u65f6\u53ef\u53d6\u4f60\u6027\u547d\u3002\u201d \u3000\u3000\u674e\u9038\u53c8\u60ca\u53c8\u559c\uff0c\u6b66\u7384\u971c\u624b\u8d77\u5251\u843d\uff0c\u524a\u65ad\u4e86\u4ed6\u8eab\u4e0a\u7684\u9563\u94d0\uff0c\u968f\u5373\u5265\u4e0b\u4e86\u9ebb\u7ffc\u8d5e\u7684\u6b66\u58eb \u670d\u9970\uff0c\u53eb\u674e\u9038\u7a7f\u4e0a\uff0c\u5916\u9762\u7f69\u4e0a\u4e00\u4ef6\u6597\u7bf7\uff0c\u9ebb\u7ffc\u8d5e\u7684\u8eab\u6750\u6bd4\u674e\u9038\u9b41\u4f1f\u5f97\u591a\u8863\u670d\u751a\u4e0d\u79f0\u8eab\uff0c \u674e\u9038\u5c06\u513f\u5b50\u5305\u5728\u5bbd\u5927\u7684\u6597\u7bf7\u91cc\u9762\u3002 \u3000\u3000\u6b66\u7384\u971c\u53d6\u51fa\u4e86\u4e00\u9897\u6613\u5bb9\u4e39\uff0c\u674e\u9038\u4e0d\u5f85\u5979\u8bf4\uff0c\u4fbf\u77e5\u5979\u7684\u7528\u610f\uff0c\u5f53\u4e0b\u5306\u5306\u5fd9\u5fd9\uff0c\u501f\u52a9\u8fd9 \u9897\u6613\u5bb9\u4e39\uff0c\u626e\u6210\u4e00\u4e2a\u7ef4\u65cf\u6b66\u58eb\u7684\u6a21\u6837\u3002 \u3000\u3000\u6b66\u7384\u971c\u9664\u4e0b\u51e4\u51a0\uff0c\u8d70\u81f3\u5e10\u540e\uff0c\u7247\u5230\u4e4b\u95f4\uff0c\u4e5f\u6539\u626e\u6210\u4e86\u4e00\u4e2a\u5bab\u5973\u7684\u6a21\u6837\u51fa\u6765\uff0c\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u54b1\u8d70\u5427\u3002\u201d\u674e\u904d\u6b63\u5728\u5fc3\u60f3\uff1a\u201c\u767e\u5fe7\u3001\u5929\u6076\u4e8c\u4eba\u5b88\u5728\u5916\u9762\uff0c\u5982\u4f55\u8d70\u5f97\u51fa\u53bb\uff1f\u201d\u4f46\u89c1 \u6b66\u7384\u971c\u79fb\u5f00\u80e1\u5e8a\uff0c\u5728\u5899\u58c1\u4e00\u6309\uff0c\u5f00\u4e86\u4e00\u9053\u89d2\u95e8\uff0c\u539f\u6765\u5927\u6c57\u5e26\u6b66\u7384\u971c\u8fdb\u8fd9\u5bdd\u5bab\u4e4b\u65f6\uff0c\u4fbf\u662f \u4ece\u8fd9\u89d2\u95e8\u6765\u7684\u3002\u540e\u9762\u662f\u4e00\u95f4\u5927\u6c57\u7ed9\u5979\u5e03\u7f6e\u7684\u68b3\u5986\u5ba4\uff0c\u5927\u6c57\u4e3a\u5979\u6b22\u5fc3\uff0c\u6240\u4ee5\u65e9\u5c31\u628a\u4ed6\u7ed9\u5979 \u7cbe\u5fc3\u5e03\u7f6e\u7684\u5bdd\u5bab\u6784\u9020\uff0c\u544a\u4e4b\u4e8e\u5979\uff0c\u68b3\u5986\u5ba4\u7a97\u5916\u4fbf\u662f\u5fa1\u82b1\u56ed\uff0c\u53ef\u4ee5\u6b23\u8d4f\u56ed\u4e2d\u7684\u82b1\u6728\u3002 \u3000\u3000\u6b66\u7384\u971c\u4e0e\u674e\u9038\u8d70\u5165\u4e86\u68b3\u5986\u5ba4\uff0c\u63a8\u5f00\u7a97\u95e8\uff0c\u8fd9\u65f6\u6b63\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u4e00\u77a7\u4e0b\u9762\u65e0\u4eba\uff0c\u6b66\u7384 \u971c\u4e00\u62c9\u674e\u9038\uff0c\u4f7f\u5373\u8dc3\u4e0b\u3002\u54ea\u77e5\u521a\u4e00\u8d70\u51e0\u6b65\uff0c\u4fbf\u6709\u4eba\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u8fd9\u4e2a\u4eba\u6b63\u662f\u5927\u6c57\u7684 \u4f8d\u536b\u957f\u6070\u514b\u56fe\u3002\u674e\u9038\u5c06\u4e24\u91d1\u724c\u5411\u6070\u514b\u56fe\u4e00\u6643\uff0c\u538b\u4f4e\u58f0\u97f3\u8bf4\u9053\u3002\u201c\u5949\u5927\u6c57\u547d\uff0c\u62a4\u9001\u65b0\u738b\u5983 \u7684\u9ebb\u5973\u51fa\u5bab\u3002\u201d\u6070\u514b\u56fe\u5f53\u7136\u8ba4\u5f97\u8fd9\u9762\u91d1\u724c\uff0c\u4ed6\u53c8\u77e5\u9053\u65b0\u738b\u5983\u5361\u6d1b\u4e1d\u662f\u963f\u5c14\u6cf0\u5c71\u5c71\u4e0b\u7684\u4e00 \u4e2a\u56fd\u7684\u516c\u4e3b\uff0c\u7167\u7a81\u601d\u7684\u4e60\u4fd7\uff0c\u51fa\u5ac1\u7684\u5973\uff0c\u5230\u4e86\u592b\u5bb6\u4e4b\u540e\uff0c\u5c31\u8981\u5c06\u5979\u6240\u7740\u7684\u4e11\u5957\u65b0\u954c\u5df2\u8863 \u9001\u56de\u53bb\u7ed9\u6bcd\u4eb2\uff0c\u8868\u793a\u5728\u6b64\u4e4b\u524d\u662f\u9760\u7236\u6bcd\uff0c\u5728\u6b64\u4e4b\u540e\u4fbf\u662f\u9760\u4e08\u592b\u4e86\u3002\u4e00\u89c1\u674e\u9038\u8bf4\u662f\u62a4\u9001\u65b0 \u738b\u5983\u7684\u4f8d\u5973\u51fa\u5bab\uff0c\u53ea\u5f53\u8fd9\u540d\u5f85\u5973\u662f\u5c06\u65b0\u738b\u5983\u7684\u5ac1\u8863\u9001\u56de\u6bcd\u5bb6\u7684\uff0c\u81ea\u662f\u4e0d\u7591\u6709\u4ed6\uff0c\u5f53\u4e0b\u6325 \u4e86\u6325\u624b\uff0c\u653e\u674e\u9038\u7acb\u53bb\u3002\u4ed6\u54ea\u91cc\u77e5\u9053\u771f\u738b\u5983\u5361\u6d1b\u4e1d\u65e9\u5df2\u5192\u5145\u4f8d\u5973\uff0c\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u524d\u4fbf\u5df2\u4e58 \u539f\u8f66\u51fa\u57ce\u53bb\u4e86\u3002 \u3000\u3000\u674e\u9038\u4e0e\u6b66\u7384\u971c\u52a0\u5feb\u811a\u6b65\uff0c\u8def\u4e0a\u867d\u7136\u78b0\u4e0a\u51e0\u4e2a\u5de1\u903b\u7684\u6b66\u58eb\uff0c\u91d1\u724c\u4e00\u6643\uff0c\u95ee\u4e5f\u6ca1\u95ee\u4fbf\u901a\uff0c \u4e0d\u4e00\u4f1a\u4fbf\u5230\u4e86\u5fa1\u82b1\u56ed\u7684\u540e\u95e8\uff0c\u4fa7\u8fb9\u662f\u9a6c\u53a9\uff0c\u674e\u9038\u4eae\u51fa\u91d1\u724c\uff0c\u7d22\u6027\u5411\u7ba1\u9a6c\u7684\u4eba\u8ba8\u4e86\u4e24\u5339\u9a8f \u9a6c\uff0c\u7136\u540e\u5429\u5490\u5f00\u95e8\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u6070\u514b\u56fe\u5927\u58f0\u53eb\u8d70\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d\u5b88\u95e8\u7684\u6b66\u58eb\u89c1\u6070\u514b\u56fe\u98de\u5954\u800c\u6765\uff0c \u4e0d\u6562\u5f00\u95e8\uff0c\u674e\u9038\u559d\u9053\uff1a\u201c\u4f60\u6562\u963b\u8bef\u6211\u5417\uff1f\u5feb\u5f00\uff01\u201d\u6070\u514b\u56fe\u53eb\u9053\uff1a\u201c\u522b\u5fd9\u51fa\u53bb\uff0c\u5f85\u6211\u770b\u4e00 \u4e0b\u3002\u201d\u674e\u9038\u500f\u7684\u51fa\u624b\u3002\u4e00\u70b9\u70b9\u4e86\u8fd9\u540d\u6b66\u58eb\u7684\u7a74\u9053\uff0c\u7acb\u5373\u4fbf\u62a2\u4e86\u9501\u5319\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u6070\u514b\u56fe\u5df2\u662f\u8d76\u4e86\u4e0a\u6765\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u5411\u674e\u9038\u4e00\u638c\u5288\u4e0b\u3002 \u3000\u3000\u539f\u6765\u6070\u514b\u56fe\u653e\u8d70\u4e86\u674e\u9038\u4e4b\u540e\uff0c\u5ffd\u8d77\u7591\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u6b66\u58eb\u662f\u8c01\uff1f\u6211\u600e\u4e48\u4e0d\u8ba4\u8bc6\u4ed6\uff1f\u201d \u8981\u77e5\u6070\u514b\u56fe\u662f\u4f8d\u536b\u961f\u957f\uff0c\u5bab\u4e2d\u7684\u6570\u5341\u540d\u4f8d\u536b\uff0c\u90fd\u662f\u7ecf\u8fc7\u4ed6\u9009\u62d4\u7684\u3002\u521a\u624d\u56e0\u4e3a\u591c\u8272\u8198\u9647\uff0c \u674e\u9038\u7528\u6597\u84ec\u906e\u7740\u4e86\u534a\u8fb9\u9762\u5b54\uff0c\u517c\u4ee5\u624b\u4e0a\u53c8\u6709\u5927\u6c57\u7684\u91d1\u724c\uff0c\u4ed6\u4e00\u65f6\u5927\u610f\uff0c\u6ca1\u6709\u76d8\u95ee\uff0c\u8fc7\u540e \u4e00\u60f3\u4e0d\u5bf9\uff0c\u53c8\u60f3\u8d77\u4e86\u8fd9\u4ef6\u6597\u7bf7\u4f3c\u4e4e\u662f\u9ebb\u7ffc\u8d5e\u7684\uff0c\u8d8a\u60f3\u8d8a\u7591\uff0c\u662f\u4ee5\u8ffd\u4e0a\u524d\u53bb\uff0c\u60f3\u8981\u4ed4\u7ec6\u77a7 \u77a7\u3002 \u3000\u3000\u53ca\u81f3\u674e\u9038\u70b9\u5012\u4e86\u90a3\u4e2a\u770b\u95e8\u7684\u6b66\u571f\uff0c\u6070\u514b\u56fe\u5927\u5403\u4e00\u60ca\uff0c\u65ad\u5b9a\u674e\u9038\u5fc5\u662f\u5192\u5145\uff0c\u5f53\u4e0b\u4e00\u8dc3 \u6570\u4e08\uff0c\u7528\u5c3d\u5168\u8eab\u6c14\u529b\uff0c\u638c\u5288\u674e\u9038\u3002 \u3000\u3000\u6070\u514b\u56fe\u662f\u7a81\u53a5\u51fa\u540d\u7684\u5927\u529b\u571f\uff0c\u8fd9\u4e00\u638c\u7aef\u7684\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u674e\u9038\u53d7\u4f24\u4e4b\u540e\uff0c\u4e0d\u6562\u786c \u63a5\uff0c\u65bd\u5c55\u817e\u3001\u632a\u3001\u95ea\u3001\u5c55\u7684\u5c0f\u5de7\u529f\u592b\uff0c\u63a5\u8fde\u907f\u5f00\u4ed6\u51e0\u8bb0\u5f3a\u52b2\u7684\u653b\u52bf\uff0c\u6070\u514b\u56fe\u4e00\u638c\u7d27\u4f3c\u4e00 \u638c\uff0c\u8feb\u5f97\u674e\u9038\u65e0\u6687\u62d4\u5251\uff0c\u540c\u65f6\u8fde\u58f0\u5927\u53eb\u9053\uff1a\u201c\u6765\u4eba\u5440\uff0c\u5feb\u6765\u4eba\u5440\uff01\u201d\u6b66\u7384\u971c\u89c1\u65f6\u673a\u6025\u8feb\uff0c \u5ffd\u5730\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u53e6\u5916\u6709\u523a\u5ba2\u5df2\u5230\u5927\u6c57\u7684\u5bdd\u5bab\uff0c\u4f60\u4e0d\u53bb\u6551\u9a7e\uff0c\u8ffd\u6211\u4eec\u505a\u4ec0\u4e48\uff1f\u201d\u6070\u514b\u56fe \u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u8bdd\u5c24\u672b\u4e86\uff0c\u6b66\u7384\u971c\u7a81\u7136\u6b3a\u8eab\u76f4\u8fdb\uff0c\u201c\u556a\u201d\u7684\u4e00 \u638c\uff0c\u51fb\u4e2d\u4ed6\u80f8\u524d\u7684\u201c\u8303\u6c5b\u7a74\u201d\uff0c\u672c\u6765\u4ee5\u6070\u514b\u56fe\u7684\u672c\u9886\uff0c\u867d\u7136\u4e0d\u654c\u6b66\u7384\u971c\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u7ed9\u5979 \u4e00\u62db\u51fb\u5012\uff0c\u4f46\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e2a\u5bab\u5973\u7adf\u7136\u6000\u6709\u7edd\u4e16\u6b66\u529f\uff0c\u51b7\u4e0d\u9632\u7684\u7ed9\u5979\u4e0a\u638c\u51fb\u4e2d\uff0c\u54fc \u4e5f\u672a\u54fc\u4e00\u58f0\uff0c\u4fbf\u5373\u5012\u5730\u3002 \u3000\u3000\u674e\u9038\u6025\u5fd9\u5f00\u4e86\u56ed\u95e8\uff0c\u4e0e\u6b66\u7384\u971c\u8de8\u4e0a\u9a6c\u80cc\uff0c\u75be\u9a70\u800c\u53bb\u3002\u4f46\u542c\u5f97\u80cc\u540e\u5f13\u5982\u9739\u96f3\u3002\u7bad\u4f3c\u98de \u8757\uff0c\u674e\u9038\u5c06\u5b9d\u5251\u821e\u8d77\uff0c\u5316\u6210\u4e00\u9053\u62a4\u8eab\u7684\u94f6\u8679\uff0c\u6325\u4e86\u7247\u523b\uff0c\u4fbf\u8131\u51fa\u5f13\u7bad\u6240\u80fd\u5c04\u53ca\u7684\u8303\u56f4\uff0c \u90a3\u4e48\u591a\u7684\u6b66\u58eb\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u8ffd\u6765\u3002\u539f\u6765\u662f\u6070\u514b\u56fe\u88ab\u6b66\u7384\u971c\u90a3\u756a\u8bdd\u5413\u4f4f\u4e86\uff01\u6b66\u7384\u971c\u8bf4\u53e6\u5916 \u6709\u523a\u5ba2\u5df2\u8fdb\u5165\u4e86\u5927\u6c57\u7684\u5bdd\u5b98\uff0c\u6070\u514b\u56fe\u4e0d\u77e5\u771f\u5047\uff01\u4f46\u4e00\u60f3\u8fd9\u4e2a\u5bab\u5973\u4e0e\u674e\u9038\u6b63\u662f\u4ece\u5927\u6c57\u5bdd\u5bab \u90a3\u4e2a\u65b9\u5411\u8dd1\u51fa\u6765\uff0c\u9ebb\u7ffc\u8d5e\u7684\u6597\u84ec\u53c8\u62ab\u5728\u674e\u9038\u7684\u8eab\u4e0a\uff0c\u4fbf\u62b1\u7740\u201c\u5b81\u53ef\u4fe1\u5176\u6709\uff0c\u4e0d\u53ef\u4fe1\u5176\u65e0\u201d \u7684\u5b97\u65e8\uff0c\u6b62\u4f4f\u4e86\u90a3\u73ed\u6b66\u58eb\uff0c\u53eb\u4ed6\u4eec\u8d76\u56de\u53bb\u6551\u9a7e\uff0c\u5e76\u641c\u7d22\u674e\u9038\u7684\u515a\u7fbd\uff0c\u90a3\u73ed\u6b66\u571f\u89c1\u674e\u9038\u80fd \u591f\u4ece\u6df1\u5bab\u4e4b\u4e2d\u9003\u51fa\uff0c\u53ea\u6015\u4ed6\u515a\u7fbd\u751a\u591a\uff0c\u8bf4\u4e0d\u5b9a\u5728\u5bab\u5185\u5404\u5904\u9690\u85cf\uff0c\u4fbf\u4e0d\u6562\u5206\u8584\u529b\u91cf\u53bb\u8ffd\u4ed6 \u4e86\u3002 \u3000\u3000\u674e\u6b66\u4e8c\u4eba\u8dd1\u4e86\u4e00\u7a0b\uff0c\u89c1\u540e\u9762\u5e76\u65e0\u654c\u9a91\u8ffd\u6765\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u4e24\u4eba\u5e76\u8f94\u9a70\u9a71\uff0c\u674e\u9038\u773c\u5149\u4e00 \u77a5\uff0c\u53ea\u89c1\u6b66\u7384\u971c\u5728\u9a6c\u80cc\u4e0a\u4e5f\u626d\u8f6c\u5934\u6765\u671b\u4ed6\uff0c\u56db\u76ee\u76f8\u6295\uff0c\u53c8\u5404\u81ea\u5c06\u773c\u5149\u79fb\u5f00\uff0c\u8fd9\u4e00\u5239\u90a3\uff0c \u4e24\u4eba\u7684\u5fc3\u91cc\u90fd\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u5f7c\u6b64\u4f46\u611f\u5230\u4e00\u9635\u832b\u7136\uff01 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\uff0c\u5f80\u4e8b\u524d\u5c18\uff0c\u95ea\u7535\u4e00\u822c\u7684\u4ece\u674e\u9038\u7684\u5fc3\u5934\u63a0\u8fc7\uff0c\u5ce8\u5d4b\u91d1\u9876\u7684\u6bd4\u5251\u4e00\u5207\u5ce1\u5c71\u9053 \u4e0a\u7684\u7434\u9ad3\u6d9b\u58f0\uff0c\u5979\u4e3a\u81ea\u5df1\u5411\u590f\u4faf\u575a\u6c42\u533b\uff0c\u90a3\u5341\u591a\u5929\u7684\u5343\u91cc\u9a70\u9a71\uff0c\u7ec6\u5fc3\u5475\u62a4\uff0c\u6700\u540e\u662f\u7ef7\u5c71 \u7684\u6b7b\u522b\u751f\u79bb\u3002\u8fd9\u4e00\u5e55\u5e55\u7684\u60c5\u666f\u4ece\u674e\u9038\u5fc3\u5934\u7ffb\u8fc7\uff0c\u800c\u73b0\u5728\u60f3\u4e0d\u5230\u53c8\u5728\u8fd9\u6837\u7684\u60c5\u666f\u4e4b\u4e0b\u76f8\u805a\uff0c \u5979\uff0c\u5979\u53c8\u4e00\u6b21\u7684\u6551\u4e86\u81ea\u5df1\uff01\u674e\u9038\u5fc3\u5934\u6fc0\u52a8\u975e\u5e38\uff0c\u7981\u4e0d\u4f4f\u53c8\u8f6c\u8fc7\u5934\u53bb\uff0c\u627e\u7740\u4e86\u6b66\u7384\u971c\u7684\u76ee \u5149\uff0c\u8f7b\u8f7b\u7684\u8bf4\u4e86\u4e00\u58f0\u201c\u8c22\u8c22\u201d\u3002 \u3000\u3000\u6b66\u7384\u971c\u5fc3\u4e2d\u7684\u6fc0\u52a8\u6b8a\u4e0d\u5728\u674e\u9038\u4e4b\u4e0b\uff0c\u5979\u6240\u8981\u8eb2\u907f\u7684\u4eba\u7ec8\u662f\u4e0d\u80fd\u907f\u5f00\uff0c\u73b0\u5728\u53c8\u4e0e\u4ed6\u5e76 \u8f94\u9a70\u9a71\u4e86\u3002\u53ef\u662f\u8fd9\u5df2\u4e0e\u516b\u5e74\u4e4b\u524d\u7684\u60c5\u666f\u5b8c\u5168\u4e0d\u540c\u4e86\uff0c\u5728\u5979\u6000\u4e2d\u62b1\u7740\u662f\u4ed6\u7684\u513f\u5b50\uff01 \u3000\u3000\u6b66\u7384\u971c\u4f4e\u4e0b\u4e86\u5934\uff0c\u4fbf\u9053\uff1a\u201c\u8c22\u4ec0\u4e48\uff1f\u4f60\u80fd\u591f\u8131\u9669\uff0c\u6211\u4e5f\u5fc3\u5b89\u4e86\u3002\u201d\u8bed\u8c03\u5e73\u6de1\uff0c\u4f46\u674e \u9038\u5374\u542c\u5f97\u51fa\u6765\uff0c\u5979\u5bf9\u81ea\u5df1\u8fd8\u662f\u8574\u85cf\u7740\u65e0\u9650\u7684\u70ed\u60c5\uff0c\u674e\u9038\u4e0d\u81ea\u89c9\u7684\u8eab\u8eaf\u98a4\u6296\uff0c\u5728\u9a6c\u80cc\u4e0a\u6643 \u4e86\u4e24\u6643\uff0c\u6b66\u7384\u971c\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u662f\u4f24\u53e3\u590d\u88c2\u4e86\u5417\uff1f\u201d\u674e\u9038\u81ea\u9053\uff1a\u201c\u6ca1\u4ec0 \u4e48\uff0c\u6211\u53d7\u7684\u53ea\u662f\u4e00\u70b9\u76ae\u5916\u4f24\uff0c\u53ea\u6015\u662f\u8dd1\u5f97\u6709\u70b9\u7d2f\u4e86\uff0c\u6b47\u4e00\u6b47\u5427\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u4f30\u91cf\u5df2\u8dd1\u51fa\u5341\u591a\u91cc\u4e86\uff0c\u4fbf\u9053\uff1a\u201c\u597d\u5427\uff0c\u54b1\u4eec\u5230\u524d\u9762\u7684\u5c71\u4e18\u6b47\u4e00\u4f1a\u3002\u201d\u674e\u9038\u8dc3 \u4e0b\u9a6c\u80cc\uff0c\u62b1\u8d77\u513f\u5b50\uff0c\u6b66\u7384\u971c\u9ed8\u9ed8\u65e0\u8a00\u7684\u968f\u7740\u4ed6\u3002\u8d70\u5230\u5c71\u4e0a\uff0c\u672a\u66fe\u5750\u5b9a\uff0c\u674e\u9038\u7684\u513f\u5b50\u4fbf\u6251 \u5230\u6b66\u7384\u971c\u7684\u6000\u4e2d\uff0c\u6402\u7740\u5979\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u771f\u6709\u529e\u6cd5\uff0c\u8bf4\u6551\u6211\u4eec\uff0c\u679c\u7136\u4fbf\u6551\u4e86\u6211\u4eec\u3002\u201d\u6b66 \u7384\u971c\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u7684\u8bdd\uff0c\u5f53\u7136\u8981\u505a\u3002\u201d\u5b69\u5b50\u9762\u5411\u7740\u4ed6\u7236\u4eb2\u8bf4\u9053\uff1a\u201c\u8fd9\u59d1\u59d1\u771f\u597d\uff0c\u5988\u5988\u597d \u50cf\u4e0d\u5927\u559c\u6b22\u5979\uff0c\u90a3\u5929\u5979\u7ed9\u6211\u679c\u94fa\u5403\uff0c\u5988\u8fd8\u4e0d\u51c6\u6211\u63a5\u5462\u3002\u5509\uff0c\u5988\u54ea\u77e5\u9053\u5979\u662f\u8fd9\u6837\u7684\u597d\u4eba\uff0c \u6211\u53ef\u771f\u559c\u6b22\u5979\uff01\u201d \u3000\u3000\u674e\u9038\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u89c1\u957f\u5b59\u58c1\u4e86\uff1f\u201d\u6b66\u7384\u971c\u5fae\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u8d3a\u559c\u5462\uff01\u201d\u5979 \u8138\u4e0a\u73b0\u51fa\u7b11\u5bb9\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7531\u81ea\u5df1\u7684\u611f\u5230\u4e00\u9635\u5fc3\u9178\u3002\u9661\u7136\u95f4\uff0c\u957f\u5b59\u58c1\u90a3\u51c4\u6028\u7684\u773c\u5149\uff0c\u5992\u5fcc \u7684\u795e\u8272\uff0c\u53c8\u597d\u50cf\u6d6e\u73b0\u5728\u5979\u7684\u773c\u524d\uff0c\u5979\u6781\u529b\u538b\u6291\u81ea\u5df1\u6fc0\u52a8\u7684\u5fc3\u60c5\uff0c\u5fc3\u4e2d\u60f3\u5230\uff1a\u201c\u6211\u7edd\u4e0d\u80fd \u81ea\u5bfb\u70e6\u607c\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u4f60\u662f\u5728\u54ea\u513f\u89c1\u5230\u5979\u7684\uff1f\u201d\u4ed6\u7684\u513f\u5b50\u63d2\u5634\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u6837\u7684\u6253\u626e\u3002\u7239\uff0c\u4f60 \u77e5\u4e0d\u77e5\u9053\u3002\u6211\u5c31\u662f\u5728\u90a3\u665a\u4e0a\u88ab\u4e24\u4e2a\u6b66\u58eb\u6349\u53bb\u7684\u3002\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u6211\u542c\u4f60\u5988\u5988\u8bf4\u4e86\u3002\u8fd9\u6b21\u5168\u9760\u4f60\u7684\u59d1\u59d1\u5c06\u4f60\u6551\u51fa\u6765\uff0c\u4f60\u8fd8\u672a\u66fe\u5411\u5979\u9053\u8c22\u5462\u3002\u201d \u90a3\u5b69\u5b50\u5411\u5979\u78d5\u4e86\u4e00\u4e2a\u54cd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u6211\u957f\u5927\u4e86\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u6389\u4f60\uff01\u6700\u597d\uff0c\u4f60\u80fd\u591f \u548c\u6211\u4eec\u5728\u4e00\u8d77\uff0c\u5988\u82e5\u77e5\u9053\u662f\u4f60\u6551\u6211\uff0c\u5979\u4e5f\u4e00\u5b9a\u4f1a\u559c\u6b22\u4f60\u4e86\u3002\u201d\u6b66\u7384\u971c\u5c06\u4ed6\u62c9\u4e86\u8d77\u6765\uff0c\u7b11 \u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u771f\u4e56\uff0c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\u201d\uff1f\u674e\u9038\u9053\uff1a\u201c\u53eb\u674e\u5e0c\u654f\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u654f\u513f\uff0c\u6211 \u4e5f\u559c\u6b22\u4f60\uff0c\u5f85\u4f60\u957f\u5927\u4e86\u6211\u518d\u6765\u770b\u4f60\u5427\u3002\u201d\u90a3\u5b69\u5b50\u9732\u51fa\u6005\u60d8\u7684\u795e\u60c5\uff0c\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u8981\u8d70 \u4e86\u4e48\uff1f\u201d\u6b66\u7384\u971c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\u554a\uff0c\u5c31\u8981\u8d70\u4e86\uff01\u201d\u90a3\u5b69\u5b50\u9053\uff1a\u201c\u4f60\u4e0d\u7b49\u548c\u6211\u7684\u5988\u5988\u89c1\u9762 \u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u6211\u5c31\u6258\u4f60\u5411\u5979\u95ee\u597d\u5427\uff0c\u4f60\u8bb0\u5f97\u5417\uff1f\u201d \u3000\u3000\u90a3\u5b69\u5b50\u9053\uff1a\u201c\u6211\u600e\u6837\u5411\u5988\u5988\u8bf4\u5462\uff1f\u5988\u77e5\u9053\u662f\u4f60\u6551\u6211\uff0c\u5979\u4f1a\u602a\u6211\u4e0d\u5c06\u4f60\u7559\u4e0b\u6765\u7684\u3002\u4f60 \u522b\u77a7\u90a3\u665a\u5988\u597d\u50cf\u5f88\u51f6\uff0c\u5176\u5b9e\u5979\u662f\u975e\u5e38\u75bc\u6211\u7684\u3002\u4f60\u5bf9\u6211\u8fd9\u4e48\u597d\uff0c\u5979\u4e00\u5b9a\u975e\u5e38\u611f\u6fc0\u3002\u201d\u6b66\u7384 \u971c\u5fae\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u4f60\u5c31\u5bf9\u5979\u8bf4\uff0c\u59d1\u59d1\u76fc\u671b\u5979\u8fc7\u5f97\u5feb\u5feb\u6d3b\u6d3b\uff0c\u4e00\u5207\u4e8b\u60c5\u90fd\u79f0\u5fc3\u5982\u610f\u3002\u201d \u90a3\u5b69\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u8bb0\u7740\u4e86\u3002\u54a6\uff0c\u59d1\u59d1\uff0c\u4f60\u7b11\u5f97\u4e0d\u5927\u81ea\u7136\uff0c\u662f\u4e0d\u662f\u6709\u70b9\u4e0d\u5feb\u6d3b\uff1f\u201d\u6b66 \u7384\u971c\u7b11\u9053\uff1a\u201c\u4f60\u731c\u9519\u4e86\uff0c\u6211\u5f88\u5feb\u6d3b\u3002\u201d\u5176\u5b9e\u5b69\u5b50\u4e00\u70b9\u4e5f\u6ca1\u6709\u731c\u9519\uff0c\u674e\u9038\u5446\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c \u5fc3\u4e2d\u7684\u6fc0\u52a8\u8fd9\u624d\u7a0d\u7a0d\u5e73\u9759\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u8bf4\u8bdd\uff0c\u5c0f\u5b69\u5b50\u4e0d\u8981\u80e1\u4e71\u6253\u5c94\u3002\u201d\u518d\u95ee\u6b66\u7384 \u971c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u5230\u8fc7\u5929\u5c71\u7684\u4e86\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u540e\u6765\uff0c\u6211\u53c8\u5728\u5929\u5c71\u78b0\u5230\u58c1 \u59b9\uff0c\u90a3\u65f6\u654f\u513f\u5df2\u7ecf\u88ab\u63b3\u8d70\u4e86\uff0c\u5979\u90a3\u665a\u7528\u4e86\u6613\u5bb9\u4e39\uff0c\u6211\u7b2c\u4e8c\u6b21\u89c1\u9762\u624d\u8ba4\u51fa\u662f\u5979\u3002\u201d\u674e\u9038 \u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u8fd9\u624d\u660e\u767d\uff0c\u81ea\u5df1\u51b3\u5b9a\u5355\u72ec\u4e0b\u5c71\u53bb\u6551\u654f\u513f\u7684\u65f6\u5019\uff0c\u4e3a\u4ec0\u4e48\u957f\u5b59\u58c1\u4f1a\u6d41\u9732\u51fa \u4e00\u79cd\u6050\u60e7\u7684\u5fc3\u60c5\uff0c\u6562\u60c5\u5979\u5c31\u662f\u6015\u81ea\u5df1\u4e0e\u6b66\u7384\u971c\u76f8\u9047\uff01 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u4e3a\u4e86\u6551\u654f\u513f\uff0c\u624d\u5192\u5145\u65b0\u738b\u5983\u7684\uff1f\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d\u6b66\u7384 \u971c\u5c06\u7ecf\u8fc7\u8bf4\u4e86\uff0c\u674e\u9038\u79f0\u5947\u4e0d\u5df2\uff0c\u654f\u513f\u542c\u5f97\u6b66\u7384\u971c\u5c06\u5927\u6c57\u620f\u5f04\uff0c\u66f4\u662f\u5f00\u5fc3\u4e4b\u6781\uff0c\u4e0d\u4f4f\u54a7\u5f00 \u5634\u7b11\u3002\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u8fd9\u6b21\u5230\u7a81\u53a5\u6765\uff0c\u5374\u662f\u5949\u4e86\u6211\u59d1\u59d1\u7684\u5dee\u9063\u6765\u627e\u4f60\u56de\u53bb\u7684\uff01\u201d \u674e\u9038\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u4e86\uff0c\u6211\u4e0d\u4f1a\u56de\u53bb\u7684\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u6b21\u4e0d\u540c\u3002\u6211\u59d1\u59d1\u5df2\u51b3\u5b9a\u4f20\u4f4d\u7ed9\u5362\u9675\u738b\uff0c\u8981\u8bf7\u4f60\u56de\u53bb\u8f85\u52a9\u4ed6\u3002 \u4f60\u4e0d\u662f\u4e00\u76f4\u8981\u6062\u590d\u674e\u5510\u7684\u2018\u6b63\u7edf\u2019\u5417\uff1f\u5982\u4eca\u6c5f\u5c71\u4f9d\u7136\u59d3\u674e\uff0c\u4f60\u4e3a\u4f55\u8fd8\u8981\u8001\u6b7b\u4ed6\u4e61\uff1f\u201d\u674e \u9038\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u6765\u6211\u7684\u8c6a\u6c14\u65e9\u5df2\u6d88\u78e8\u8d3b\u5c3d\u4e86\uff0c\u4e0d\u7ba1\u662f\u59d3\u6b66\u4e5f\u597d\uff0c\u59d3\u674e\u4e5f\u597d\uff0c\u6211\u73b0 \u4e5f\u4e0d\u60f3\u5377\u5165\u662f\u975e\u6f29\u6da1\u3002\u55ef\uff0c\u4f60\u4e0d\u61c2\u5f97\u6211\u7684\u5fc3\u60c5\uff0c\u6211\u4f46\u613f\u80fd\u591f\u5fd8\u60bc\u5f80\u4e8b\uff0c\u4e5f\u4e0d\u60f3\u6709\u4eba\u77e5\u9053 \u6211\uff0c\u6211\u662f\u4e0d\u613f\u56de\u53bb\u7684\u4e86\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u4e00\u9635\u6c89\u9ed8\uff0c\u5979\u662f\u61c2\u5f97\u674e\u9038\u5fc3\u60c5\uff0c\u73b0\u5728\u4e4b\u6240\u4ee5\u4e0d\u613f\u610f\u56de\u53bb\uff0c\u5df2\u4e0d\u53ea\u662f\u4e3a\u4e86\u8c01\u505a \u7687\u5e1d\u7684\u95ee\u9898\u4e86\uff0c\u6562\u60c5\u4ed6\u4e5f\u51b3\u610f\u8981\u907f\u5f00\u81ea\u5df1\uff0c\u907f\u5f00\u4e0a\u5b98\u5a49\u513f\uff0c\u514d\u5f97\u89e6\u53ca\u5fc3\u5e95\u7684\u521b\u4f24\uff01 \u3000\u3000\u674e\u9038\u53c8\u9053\uff1a\u201c\u672c\u6765\u5728\u524d\u4e24\u5929\uff0c\u6211\u66fe\u7ecf\u60f3\u8fc7\u8981\u56de\u53bb\u7684\uff0c\u5982\u4eca\u4f60\u65e2\u7136\u5c31\u8981\u56de\u53bb\uff0c\u4e5f\u7701\u5f97 \u6211\u591a\u8dd1\u4e00\u8d9f\u4e86\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\u4f60\u7684\u5802\u5144\u6b66\u627f\u55e3\u52fe \u7ed3\u7a81\u53a5\u7684\u4e8b\u60c5\uff0c\u4f60\u56de\u53bb\u544a\u8bc9\u4f60\u7684\u59d1\u59d1\u5427\uff0c\u5979\u66f4\u4f1a\u76f8\u4fe1\u4f60\u7684\u8bdd\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u9ed8\u7136\u534a\u664c\uff0c\u7f13\u7f13\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u6837\u5206\u624b\u4e5f\u597d\uff0c\u54b1\u4eec\u603b\u7b97\u89c1\u8fc7\u4e00\u6b21\u9762\u4e86\uff0c\u53ef\u60dc \u5a49\u513f\u5f88\u60f3\u89c1\u4f60\uff0c\u5374\u89c1\u4e0d\u7740\u3002\u201d\u674e\u9038\u542c\u5979\u63d0\u8d77\u4e86\u4e0a\u5b98\u5a49\u513f\uff0c\u4e0d\u7981\u53c8\u662f\u5fc3\u5f26\u98a4\u6296\uff0c\u95ee\u9053\uff1a \u201c\u5a49\u513f\u5979\u600e\u4e48\u6837\uff1f\u542c\u8bf4\u5979\u6700\u8fd1\u7ea2\u557b\u661f\u52a8\uff0c\u5ac1\u674f\u6709\u671f\uff0c\u6709\u8fd9\u4e8b\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u662f\u8c01\u6765 \u4e86\u3002\u4ed6\u8bf4\u7684\u4e5f\u5e76\u4e0d\u5168\u662f\u6355\u98ce\u6349\u5f71\u4e4b\u8c08\uff0c\u4e0d\u8fc7\uff0c\u5a49\u513f\uff0c\u5979\uff0c\u5979\u53ef\u6b63\u4e3a\u7740\u8fd9\u4ef6\u4e8b\u60c5\u70e6\u607c\u5462\uff01 \u5979\u672c\u6765\u6709\u4e00\u4e9b\u8bdd\u6258\u6211\u544a\u8bc9\u4f60\u7684\uff0c\u5509\uff0c\u73b0\u5728\u4e0d\u8bf4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u674e\u9038\u4e00\u7247\u6005\u60af\uff0c\u9053\uff1a\u201c\u957f\u5b59\u6cf0\u4e5f\u5f88\u4e0d\u9519\u5440\uff0c\u65e9\u5728\u516b\u5e74\u4e4b\u524d\uff0c\u6211\u5c31\u7977\u544a\u82cd\u5929\uff0c\u4fdd\u4f51\u5979 \u80fd\u591f\u627e\u5230\u4e00\u4e2a\u79f0\u5fc3\u5982\u610f\u7684\u4eba\u4e86\uff01\u4e0d\u7ba1\u600e\u6837\uff0c\u6211\u603b\u662f\u5c06\u5979\u5f53\u4f5c\u59b9\u5b50\u770b\u5f85\uff0c\u613f\u5979\u8fc7\u5f97\u5feb\u6d3b\uff01\u201d \u6b66\u7384\u971c\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u731c\u9519\u4e86\uff0c\u5a49\u513f\u5ac1\u7684\u4eba\u4e0d\u662f\u957f\u5b59\u6cf0\uff0c\u6240\u4ee5\u5979\u5f88\u60f3\u89c1\u4f60\uff0c\u518d\u51b3\u5b9a\u4e3b \u610f\uff0c\u53ef\u662f\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u73b0\u5728\u90fd\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d\u674e\u9038\u8be7\u5f02\u4e4b\u6781\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u662f\u957f\u5b59\u6cf0\u53c8\u662f\u8c01\u5462\uff1f \u5979\u82e5\u7136\u4e0d\u6b22\u559c\u90a3\u4eba\uff0c\u53c8\u4f55\u987b\u5982\u6b64\u70e6\u607c\uff1f\u4ee5\u5979\u90a3\u6837\u7684\u5014\u5f3a\uff0c\u53c8\u662f\u90a3\u6837\u6df1\u5177\u806a\u660e\u624d\u667a\u7684\u5973\u5b50\uff0c \u5979\u4e0d\u613f\u5ac1\uff0c\u8fd8\u6709\u8c01\u5f3a\u5f97\u4e86\u5979\uff1f\u201d\u674e\u9038\u7591\u56e2\u585e\u80f8\uff0c\u672c\u6765\u8fd8\u60f3\u95ee\u6b66\u7384\u971c\u7684\uff0c\u53ef\u4e00\u60f3\u5230\u81ea\u5df1\u5df2 \u7ecf\u6709\u59bb\u6709\u5b50\uff0c\u800c\u6b66\u7384\u971c\u53c8\u4e00\u518d\u8bf4\u4e0d\u60f3\u518d\u63d0\uff0c\u4ed6\u4e5f\u53ea\u597d\u6000\u7740\u8fd9\u4e2a\u95f7\u80e1\u82a6\uff0c\u4e0d\u4fbf\u95ee\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8d76\u5feb\u56de\u53bb\u627e\u58c1\u59b9\uff0c\u6211\u4e5f\u5f97\u8d76\u56de\u957f\u5b89\u53bb\u4e86\uff01\u201d\u5979\u6b63\u60f3\u628a\u957f\u5b59\u58c1 \u79bb\u5f00\u5929\u5c71\u7684\u4e8b\u60c5\u544a\u8bc9\u674e\u9038\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u89c1\u5927\u8def\u4e0a\u6709\u4e24\u6761\u4eba\u5f71\uff0c\u75be\u5982\u5954\u9a6c\uff0c\u6700\u521d\u4e0d\u8fc7\u4f3c \u5f39\u4e38\u5927\u5c0f\u7684\u9ed1\u70b9\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u8f6e\u5ed3\u5c31\u663e\u9732\u51fa\u6765\uff0c\u6b66\u7384\u971c\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u662f\u767e\u4f18\u4e0a\u4eba \u548c\u5929\u6076\u9053\u4eba\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u8fd9\u4e24\u4e2a\u4eba\u5df2\u6765\u5230\u5c71\u4e0b\uff0c\u767e\u5fe7\u4e0a\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u770b\u4f60\u4eec\u8fd8\u9003\u5f97 \u4e0a\u5929\uff01\u201d \u3000\u3000\u539f\u6765\u767e\u4f18\u3001\u5929\u6076\u4e8c\u4eba\u88ab\u6b66\u7384\u971c\u4f7f\u7528\u7f13\u5175\u4e4b\u8ba1\uff0c\u963b\u5728\u5bdd\u5bab\u5916\u9762\uff0c\u8fc7\u4e86\u7ea6\u4e00\u4f1a\u513f\u7684\u65f6\u523b\uff0c \u5c1a\u672a\u89c1\u5927\u6c57\u5ba3\u53ec\u4ed6\u4eec\uff0c\u518d\u9ad8\u58f0\u7980\u62a5\uff0c\u4e5f\u542c\u4e0d\u89c1\u5927\u6c57\u7684\u56de\u7b54\uff0c\u767e\u5fe7\u4e0a\u4eba\u60c5\u77e5\u6709\u5f02\uff0c\u6043\u7740\u4ed6 \u56fd\u5e08\u7684\u8eab\u4efd\uff0c\u5927\u80c6\u6253\u7834\u5bab\u95e8\uff0c\u89c1\u5927\u6c57\u548c\u9ebb\u7ffc\u8d5e\u90fd\u7ed9\u4eba\u70b9\u4e86\u7a74\u9053\uff0c\u800c\u674e\u9038\u4e0e\u65b0\u738b\u5983\u5219\u65e0\u5f71 \u65e0\u8e2a\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u89e3\u5f00\u5927\u6c57\u7684\u7a74\u9053\uff0c\u4e0d\u4e45\uff0c\u6070\u514b\u56fe\u4e5f\u7387\u9886\u6b66\u58eb\u4eec\u56de\u6765\u201c\u6551\u9a7e\u201d\uff0c \u5927\u6c57\u542c\u8bf4\u674e\u9038\u4e0e\u6b66\u7384\u971c\u5df2\u9003\u51fa\u738b\u5b98\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u4fbf\u8d23\u6210\u767e\u4f18\u3001\u5929\u6076\u4e8c\u4eba\uff0c\u52a1\u5fc5\u8981\u5c06\u4ed6\u4eec \u6d3b\u6349\u56de\u6765\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u7b11\u58f0\u672a\u7edd\uff0c\u5ffd\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u4e00\u5757\u78e8\u76d8\u80a1\u5927\u5c0f\u7684\u5de8\u77f3\uff0c\u4ece\u5c71\u9876\u58eb\u76f4\u6eda \u4e0b\u6765\uff0c\u88ab\u5b83\u78b0\u7740\uff0c\u6015\u4e0d\u538b\u6210\u8089\u997c\uff0c\u9976\u662f\u5929\u6076\u9053\u4eba\u6b66\u529f\u5353\u7edd\uff0c\u4e16\u4e0d\u6562\u9996\u5f53\u5176\u51b2\uff0c\u521a\u521a\u907f\u5f00\uff0c \u7b2c\u4e8c\u5757\u3001\u7b2c\u4e09\u5757\u77f3\u5934\uff0c\u76f8\u7ee7\u6eda\u4e0b\u3002 \u3000\u3000\u674e\u9038\u4e0e\u6b66\u7384\u971c\u4e0d\u65ad\u5728\u5c71\u4e0a\u629b\u6eda\u5de8\u77f3\uff0c\u9707\u5f97\u5c71\u8c37\u8f70\u9e23\uff0c\u5c18\u96fe\u5f25\u5929\uff0c\u5c71\u4e0a\u7684\u5c42\u51b0\u79ef\u96ea\u4e5f \u88ab\u9707\u5f97\u6eda\u584c\u4e0b\u6765\uff0c\u98de\u843d\u5982\u96e8\uff0c\u58f0\u52bf\u66f4\u662f\u60ca\u4eba\u3002\u5929\u6076\u9053\u4eba\u5c55\u5f00\u817e\u3001\u632a\u3001\u95ea\u3001\u5c55\u7684\u529f\u592b\uff0c\u867d \u7136\u6ca1\u6709\u7ed9\u5de8\u77f3\u6240\u538b\uff0c\u8eab\u4e0a\u4e5f\u4e2d\u4e86\u5341\u51e0\u5757\u96ea\u5757\uff0c\u8fd9\u4e9b\u96ea\u5757\u4ece\u6570\u5341\u4e08\u7684\u5c71\u9876\u4e0a\u98de\u4e0b\u6765\uff0c\u6253\u4e2d \u4e86\u4ed6\uff0c\u65e0\u5f02\u94c5\u5f39\uff0c\u75db\u5f97\u5929\u6076\u9053\u4eba\u5457\u5457\u5927\u53eb\u3002 \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u5374\u731b\u5730\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e9b\u77f3\u5934\u96ea\u5757\uff0c\u5c31\u963b\u788d\u4e86\u6211\u5417\uff1f\u201d\u4ed6\u6325\u821e\u8888\u88df\uff0c\u8361\u8d77\u4e86 \u4e00\u80a1\u52b2\u98ce\uff0c\u51b0\u96f9\u96ea\u5757\uff0c\u5728\u79bb\u4ed6\u6570\u4e08\u4e4b\u5730\u4f7f\u5411\u56db\u9762\u98de\u5f00\u3002\u4ed6\u51cf\u5c11\u4e86\u4e0a\u7a7a\u7684\u5a01\u80c1\uff0c\u4e13\u5fc3\u5e94\u4ed8 \u90a3\u4e9b\u6eda\u4e0b\u6765\u7684\u5927\u77f3\u5934\uff0c\u4e00\u9762\u65bd\u5c55\u4e0a\u4e58\u7684\u8f7b\u529f\u95ea\u907f\uff0c\u4e00\u9762\u89d1\u51c6\u6765\u52bf\uff0c\u4ee5\u638c\u51fb\u77f3\uff0c\u4f7f\u51fa\u201c\u56db \u4e24\u62e8\u5343\u65a4\u201d\u7684\u5de7\u52b2\uff0c\u6eda\u5230\u4ed6\u8eab\u8fb9\u7684\u5de8\u77f3\uff0c\u88ab\u4ed6\u624b\u638c\u4e00\u5e26\uff0c\u7acb\u5373\u5411\u65c1\u8fb9\u98de\u5f00\uff0c\u6839\u672c\u963b\u4ed6\u4e0d \u4f4f\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5728\u77f3\u5934\u96ea\u5757\u8f70\u51fb\u4e4b\u4e0b\uff0c\u4ecd\u7136\u4e3e\u6b65\u5982\u98de\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u4fbf\u5df2\u5230\u4e86\u5c71\u4e0a\u3002\u674e\u9038\u4e0e\u6b66 \u7384\u971c\u5408\u529b\u63a8\u4e0b\u4e00\u5757\u5927\u77f3\uff0c\u8fd9\u65f6\u53cc\u65b9\u76f8\u8ddd\u5df2\u4e0d\u5230\u5341\u4e08\u4e4b\u9065\uff0c\u767e\u4f18\u4e0a\u4eba\u5927\u559d\u4e00\u58f0\u201c\u8fd8\u4e0d\u4f4f\u624b\uff01\u201d \u5355\u638c\u4e00\u6258\uff0c\u7528\u4e86\u4e00\u62db\u201c\u72ec\u81c2\u63a3\u5929\u201d\uff0c\u7adf\u7136\u4ee5\u91d1\u521a\u638c\u5c06\u90a3\u5757\u5927\u77f3\u53cd\u63b7\u56de\u53bb\u3002\u6b66\u7384\u971c\u5927\u5403\u4e00 \u60ca\uff0c\u6025\u5fd9\u95ea\u907f\uff0c\u4f46\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5927\u77f3\u521a\u597d\u843d\u5728\u6b66\u7384\u971c\u4e0e\u674e\u9038\u4e4b\u95f4\uff0c\u767e\u4f18\u4e0a\u4eba\u54c8\u54c8 \u5927\u7b11\uff1a\u201c\u770b\u4f60\u4eec\u8fd8\u9003\u5f97\u4e86\u3002\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u4fe8\u5982\u5de8\u9e1f\u6469\u4e91\uff0c\u51cc\u7a7a\u6251\u4e0b\uff0c\u8888\u88df\u75be\u5c55\uff0c\u50cf\u4e00\u7247 \u7ea2\u4e91\uff0c\u5c06\u6b66\u7384\u971c\u4e0e\u674e\u9038\u7684\u8eab\u5f71\u90fd\u7f69\u7740\u4e86\uff01 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u5f97\u610f\u6d0b\u6d0b\uff0c\u7eb5\u58f0\u5927\u7b11\uff0c\u6ee1\u4ee5\u4e3a\u8fd9\u8888\u88df\u4e00\u7f69\uff0c\u5b9a\u7136\u624b\u5230\u64d2\u6765\uff0c\u54ea\u77e5\u5c31\u5728\u8fd9\u5239 \u90a3\u4e4b\u95f4\uff0c\u4e00\u9053\u7d2b\u8272\u7684\u5251\u5149\u7a81\u7136\u51b2\u8d77\uff0c\u63a5\u7740\u6b66\u7384\u971c\u4e00\u58f0\u53f1\u54a4\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5ffd\u5730\u8131\u4e86\u51fa\u6765\uff0c \u767e\u4f18\u4e0a\u4eba\u7684\u8888\u88df\u50cf\u9f9f\u7f51\u4e00\u822c\u5730\u5411\u5979\u6492\u53bb\uff0c\u5979\u7adf\u5e73\u7a7a\u62d4\u8d77\uff0c\u8db3\u5c16\u5728\u8888\u88df\u4e0a\u70b9\uff0c\u8fde\u4eba\u5e26\u5251\uff0c \u5316\u6210\u4e86\u4e00\u9053\u94f6\u8679\uff0c\u4e00\u62db\u201c\u91d1\u9e21\u593a\u7c9f\u201d\uff0c\u5251\u5c16\u4fbf\u5411\u7740\u767e\u5fe7\u4e0a\u4eba\u7684\u5149\u5934\u76f4\u523a\u4e0b\u6765\u3002 \u3000\u3000\u672c\u6765\u4ee5\u767e\u5fe7\u4e0a\u4eba\u7684\u6b66\u529f\uff0c\u521a\u624d\u90a3\u88ad\u88df\u4e00\u7f69\uff0c\u82e5\u7136\u4ed6\u662f\u65bd\u5168\u529b\uff0c\u6b66\u674e\u4e8c\u4eba\u867d\u7136\u8054\u5251\u62b5 \u5fa1\uff0c\u4e5f\u8d70\u7136\u62b5\u5fa1\u4e0d\u4e86\uff0c\u4f46\u56e0\u7a81\u53a5\u5927\u6c57\u5429\u5490\u5728\u5148\uff0c\u52a1\u5fc5\u8981\u5c06\u4ed6\u4eec\u4e8c\u4eba\u6d3b\u6349\uff0c\u767e\u5fe7\u4e0a\u4eba\u53c8\u4f4e \u4f30\u4e86\u4ed6\u4eec\u7684\u529f\u529b\uff0c\u6050\u6015\u8888\u88df\u7f69\u4e0b\uff0c\u4f1a\u4ee4\u4ed6\u4eec\u7a92\u606f\u800c\u6b7b\uff0c\u6240\u4ee5\u53ea\u7528\u4e86\u4e94\u6210\u771f\u529b\u3002\u54ea\u77e5\u674e\u9038 \u624b\u4e2d\u6240\u6301\u7684\u4e43\u662f\u4e00\u628a\u5927\u5185\u5b9d\u5251\uff0c\u62fc\u547d\u523a\u51fa\uff0c\u7adf\u628a\u4ed6\u7684\u8888\u88df\u523a\u7a7f\uff0c\u6b66\u7384\u971c\u8eab\u6cd5\u8f7b\u7075\uff0c\u8d81\u7740 \u8fd9\u4e2a\u65f6\u673a\uff0c\u7acb\u65bd\u53cd\u51fb\u3002 \u3000\u3000\u6b66\u7384\u971c\u7684\u5251\u6cd5\u5df2\u5f97\u4f18\u4e91\u795e\u5c3c\u7684\u771f\u4f20\uff0c\u8fd9\u4e00\u5251\u51cc\u7a7a\u523a\u4e0b\uff0c\u52bf\u4f3c\u5954\u96f7\uff0c\u75be\u5982\u9a87\u7535\uff0c\u767e\u5fe7 \u4e0a\u4eba\u4e5f\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6b66\u7384\u971c\u7684\u5251\u5c16\u79bb\u5f00\u4ed6\u7684\u5149\u5934\u5df2\u4e0d\u5230\u4e94\u5bf8\uff0c\u767e \u4f18\u4e0a\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u84e6\u5730\u4f38\u6307\u4e00\u5f39\uff0c\u4f46\u542c\u5f97\u201c\u950b\u201d\u7684\u4e00\u58f0\uff0c\u6b66\u7384\u971c\u5fc3\u5934\u4e00\u9707\uff0c\u957f\u5251\u51e0\u4e4e\u638c \u63e1\u4e0d\u7a33\uff0c\u5e78\u800c\u5979\u4e00\u89c9\u4e0d\u5999\uff0c\u7acb\u5373\u65bd\u5c55\u201c\u4e91\u91cc\u5012\u7ffb\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u4e00\u4e2a\u7b4b\u6597\uff0c\u7ffb\u51fa\u6570\u4e08\u4e4b \u5916\uff0c\u8fd9\u624d\u582a\u582a\u907f\u5f00\u4e86\u767e\u5fe7\u4e0a\u4eba\u63a5\u7740\u800c\u6765\u7684\u53cd\u51fb\u3002 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u90a3\u4e00\u5f39\u5df2\u7528\u4e86\u4e03\u6210\u771f\u529b\uff0c\u89c1\u6b66\u7384\u971c\u5c45\u7136\u8fd8\u80fd\u591f\u907f\u5f00\uff0c\u4e5f\u81ea\u6709\u70b9\u8be7\u5f02\uff0c\u70b9\u70b9 \u5934\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u513f\u7684\u5251\u6cd5\u8fd8\u7b97\u4e0d\u9519\uff0c\u53ef\u60dc\u4f60\u7684\u5e08\u7236\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8981\u4e0d\u7136\u6211\u771f\u60f3\u627e\u5979\u8f83\u91cf \u4e00\u756a\u3002\u201d\u6b66\u7384\u971c\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u662f\u4f60\u7684\u9020\u5316\uff0c\u82e5\u662f\u6211\u5e08\u7236\u5c1a\u8fd8\u5728\u751f\uff0c\u54ea\u8fd8\u5bb9\u5f97\u4f60\u884c\u51f6\u4f5c\u6076\uff01\u201d \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u559d\u9053\uff1a\u201c\u5c0f\u8f88\u65e0\u793c\uff01\u4f60\u628a\u4f60\u5e08\u7236\u7684\u5251\u6cd5\u65bd\u5c55\uff0c\u518d\u63a5\u6211\u4e24\u62db\u7740\u770b\uff01\u201d\u8888\u88df\u4e00 \u6296\uff0c\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u500f\u7136\u95f4\u5c31\u62e6\u4f4f\u4e86\u6b66\u7384\u971c\u7684\u9000\u8def\uff0c\u4fbf\u4f3c\u5728\u5979\u7684\u5468\u56f4\u7b51\u8d77\u4e86\u4e00\u9053\u94c1\u58c1\u94dc \u5899\u3002 \u3000\u3000\u6b66\u674e\u4e8c\u4eba\u80cc\u5fc3\u76f8\u8d34\uff0c\u53cc\u5251\u8054\u9632\uff0c\u9976\u662f\u674e\u9038\u624b\u6709\u5b9d\u5251\uff0c\u6b66\u7384\u971c\u4e5f\u65bd\u5c55\u4e86\u6d51\u8eab\u89e3\u6570\uff0c\u5374 \u600e\u6837\u4e5f\u51b2\u4e0d\u51fa\u53bb\uff0c\u800c\u4e14\u6e10\u6e10\u89c9\u5f97\u5708\u5b50\u8d8a\u6536\u8d8a\u7d27\uff0c\u547c\u5438\u4e5f\u6e10\u6e10\u7d27\u5f20\u8d77\u6765\u3002\u518d\u8fc7\u4e00\u4f1a\uff0c\u6b66\u7384 \u971c\u8fd8\u52c9\u5f3a\u53ef\u4ee5\u652f\u6301\uff0c\u674e\u9038\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u6c14\u529b\u4e0d\u7ee7\uff0c\u4f46\u611f\u5934\u6655\u76ee\u7729\uff0c\u773c\u524d\u6e10\u6e10\u6a21\u7cca\uff01\u4e0d\u7531 \u5f97\u5fc3\u5185\u4e00\u51c9\uff1a\u60f3\u4e0d\u5230\u6211\u4eca\u65e5\u4e0e\u6b66\u7384\u971c\u6b7b\u5728\u4e00\u5904\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u5929\u6076\u9053\u4eba\u4e5f\u5df2\u4e0a\u5230\u5c71\u4e0a\uff0c\u4ee5\u767e\u4f18\u4e0a\u4eba\u7684\u8eab\u4efd\u4e0e\u6b66\u529f\uff0c\u5f53\u7136\u7528\u4e0d\u5230\u4ed6\u53bb\u76f8\u52a9\uff0c \u4ed6\u6e38\u76ee\u56db\u987e\uff0c\u53d1\u73b0\u674e\u9038\u7684\u5b69\u5b50\u6b63\u8eb2\u5728\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u602a\u58f0\u7b11\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u5c0f\u5b69\u5b50\uff0c\u4e3a\u4ec0\u4e48\u5230\u5904\u4e71\u8dd1\uff1f\u5927\u6c57\u5bab\u4e2d\u6709\u98df\u6709\u4f4f\uff0c\u53c8 \u6709\u90a3\u4e48\u5927\u7684\u82b1\u56ed\u597d\u73a9\uff0c\u4f60\u8fd8\u8981\u8dd1\u51fa\u6765\uff1f\u8d76\u5feb\u968f\u6211\u56de\u5bab\u53bb\u5427\uff01\u201d\u90a3\u5b69\u5b50\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u56de\u53bb\uff0c \u6211\u4e0d\u56de\u53bb\uff01\u6211\u8ddf\u6211\u7684\u7239\u7239\uff01\u201d\u5929\u6076\u9053\u4eba\u9053\uff1a\u201c\u4f60\u7684\u7239\u7239\u4e5f\u8981\u5230\u5927\u6c57\u90a3\u513f\u53bb\u7684\u3002\u54c8\uff0c\u4f60\u8fd8 \u8eb2\uff0c\u4f60\u4e0d\u4e56\u4e56\u7684\u542c\u6211\u7684\u8bdd\uff0c\u6211\u53ef\u8981\u6349\u4f60\u56de\u53bb\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u542c\u5f97\u5929\u6076\u9053\u4eba\u5728\u6b3a\u4fae\u4ed6\u7684\u513f\u5b50\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u53ef\u662f\u4ed6\u88ab\u767e\u5fe7\u4e0a\u4eba\u8888\u88df\u62e6\u7740\uff0c\u54ea\u91cc \u62bd\u5f97\u51fa\u8eab\uff1f\u6025\u6012\u653b\u5fc3\uff0c\u4e00\u4e2a\u758f\u795e\uff0c\u88ab\u8888\u88df\u62c2\u4e86\u4e00\u4e0b\uff0c\u7a81\u7136\u80f8\u53e3\u5982\u4e2d\u94c1\u9ad3\uff0c\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\uff0c \u767e\u4f18\u4e0a\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u817e\u51fa\u624b\u6765\uff0c\u4fbf\u60f3\u628a\u4ed6\u6d3b\u6349\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u5b9b\u4f3c\u9f99\u541f\uff0c\u9707\u5f97\u6797\u4e2d\u6811\u53f6\uff0c\u7c41\u7c41\u843d\u4e0b\uff0c\u6b66\u7384\u971c\u7cbe\u795e\u4e00 \u632f\uff0c\u5237\u5237\u51e0\u5251\uff0c\u62fc\u6b7b\u9632\u536b\uff0c\u767e\u4f18\u4e0a\u4eba\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u8fd8\u6709\u9ad8\u624b\u63a5\u5e94\uff0c\u57cb\u4f0f\u6797\u4e2d\u3002\u201d \u4ed6\u542c\u90a3\u5578\u58f0\uff0c\u77e5\u9053\u6765\u4eba\u529f\u529b\u4e0d\u5f31\uff0c\u4f46\u5929\u6076\u9053\u4eba\u5c3d\u53ef\u62b5\u6321\u5f97\u4f4f\uff0c\u5374\u4e5f\u5e76\u65e0\u6076\u610f\u3002 \u3000\u3000\u5929\u6076\u9053\u4eba\u6b63\u5728\u94bb\u5165\u8305\u8349\u4e1b\u4e2d\uff0c\u6349\u6355\u674e\u9038\u7684\u5b69\u5b50\uff0c\u90a3\u5b69\u5b50\u6e9c\u6ed1\u5f97\u5f88\uff0c\u5c4f\u606f\u4e86\u547c\u5438\uff0c\u4f0f \u5728\u5730\u4e0a\uff0c\u5929\u6076\u9053\u4eba\u4e00\u65f6\u5c1a\u672a\u53d1\u73b0\u4ed6\uff0c\u5ffd\u542c\u5f97\u90a3\u58f0\u957f\u5578\uff0c\u5929\u6076\u9053\u4eba\u4e5f\u6014\u4e86\u4e00\u6014\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c \u53ea\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\uff0c\u6b3a\u4fae\u5b69\u5b50\uff01\u201d\u9661\u7136\u95f4\u4e00\u9053\u91d1\u5149\uff0c\u5411\u5929\u6076\u900d\u4eba\u5c04\u6765\uff01 \u3000\u3000\u5929\u6076\u9053\u4eba\u8ba4\u51fa\u662f\u590f\u4faf\u575a\u7684\u91d1\u9488\uff0c\u8bc6\u5f97\u5389\u5bb3\uff0c\u6025\u5fd9\u5c06\u62c2\u5c18\u4e00\u5c55\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9488 \u7a7f\u8fc7\u62c2\u5c18\uff0c\u4e00\u64ae\u5c18\u5c3e\u8ddf\u7740\u91d1\u9488\u98de\u8d77\uff0c\u4e71\u8349\u4e00\u822c\u7684\u98d8\u821e\u7a7a\u4e2d\u3002\u5929\u6076\u9053\u4eba\u7684\u62c2\u5c18\u4e43\u662f\u4e4c\u91d1\u4e1d \u6240\u70bc\uff0c\u7b97\u5f97\u662f\u6b66\u6797\u4e00\u5b9d\uff0c\u7adf\u963b\u6321\u4e0d\u4e86\u590f\u4faf\u575a\u7684\u4e00\u652f\u91d1\u9488\uff0c\u800c\u4e14\u8fd8\u7ed9\u5b83\u5f04\u65ad\u4e86\u5341\u51e0\u6839\u5c18\u5c3e\uff0c \u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u54ea\u8fd8\u6562\u53bb\u6349\u674e\u9038\u7684\u5b69\u5b50\uff0c\u6025\u5fd9\u8dc3\u51fa\u8349\u4e1b\uff0c\u6a2a\u638c\u5f53\u80f8\uff0c\u5e94\u4ed8\u5f3a\u654c\u3002 \u3000\u3000\u62ac\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u6765\u7684\u5171\u662f\u4e24\u4eba\uff01\u9664\u4e86\u590f\u4faf\u575a\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u5929\u6076\u9053\u4eba\u8ba4 \u51fa\u662f\u6b66\u7384\u971c\u7684\u5e08\u5144\u88f4\u53d4\u5ea6\u3002 \u3000\u3000\u590f\u4faf\u575a\u7b11\u9053\uff1a\u201c\u5bab\u4e2d\u4e00\u6218\uff0c\u4f60\u6211\u90fd\u8fd8\u672a\u5f97\u5c3d\u5c55\u6240\u957f\uff0c\u6765\uff0c\u6765\uff0c\u6765\uff01\u7b49\u8001\u592b\u518d\u9886\u6559\u4f60 \u7684\u8150\u9aa8\u795e\u638c\u3002\u201d \u3000\u3000\u88f4\u53d4\u5ea6\u89c1\u5e08\u59b9\u5371\u673a\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u98de\u8eab\u8dc3\u8d77\uff0c\u4e00\u62db\u201c\u5212\u7834\u5929\u6cb3\u201d\uff0c\u9022\u523a\u767e\u5fe7\u4e0a\u4eba\u7684 \u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u767e\u5fe7\u4e0a\u4eba\u53cd\u624b\u4e00\u626b\uff0c\u88f4\u53d4\u5ea6\u7684\u5251\u5c16\u867d\u7136\u7ed9\u4ed6\u7684\u638c\u529b\u9707\u6b6a\uff0c\u4f46\u4ecd\u7136\u539f\u5f0f\u4e0d\u53d8\uff0c \u6325\u5251\u75be\u523a\uff0c\u767e\u5fe7\u4e0a\u4eba\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\u3002\u4e00\u63a5\u89e6\u4fbf\u77e5\u88f4\u53d4\u5ea6\u7684\u672c\u9886\u9ad8\u51fa\u6b66\u7384\u971c\u8bb8\u591a\uff0c\u8fd9\u4e00 \u638c\u65ad\u4e0d\u80fd\u5c06\u4ed6\u9707\u5012\uff0c\u53ea\u5f97\u653e\u677e\u4e86\u674e\u9038\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4e0e\u88f4\u53d4\u5ea6\u6b63\u9762\u63a5\u6218\u3002\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c \u88f4\u53d4\u5ea6\u5df2\u7ecf\u75be\u523a\u4e09\u5251\uff0c\u4f46\u542c\u5f97\u535c\u535c\u535c\u4e09\u58f0\u8fde\u73e0\u5bc6\u54cd\uff0c\u4ed6\u6301\u7684\u662f\u4e00\u67c4\u666e\u901a\u7684\u9752\u94dc\u5251\uff0c\u4e09\u5251 \u90fd\u5241\u4e2d\u4e86\u8888\u88df\uff0c\u5374\u90fd\u7ed9\u8888\u88df\u53cd\u5f39\u56de\u6765\u3002\u88f4\u53d4\u5ea6\u8fd9\u4e00\u60ca\u4e5f\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5e08 \u7236\u751f\u524d\u8ba4\u4e3a\u767e\u5fe7\u4e0a\u4eba\u662f\u4e2a\u5f3a\u654c\uff0c\u4ee5\u672a\u66fe\u5c06\u4ed6\u526a\u9664\u4e3a\u61be\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u4e0e\u674e\u9038\u7f00\u8fc7\u53e3\u6c14\uff0c\u53cc\u5251\u8fde\u73af\u523a\u51fa\uff0c\u4e0e\u88f4\u53d4\u5ea6\u5408\u6218\u767e\u5fe7\u4e0a\u4eba\u3002\u4f18\u4e91\u8001\u5c3c\u5728\u5929\u5c71 \u9762\u58c1\u6570\u5e74\uff0c\u878d\u4f1a\u5404\u5bb6\u4e4b\u957f\uff0c\u521b\u51fa\u4e86\u4e00\u5957\u7cbe\u5999\u65e0\u6bd4\u7684\u5251\u6cd5\uff0c\u88f4\u53d4\u5ea6\u5df2\u5c3d\u5f97\u5979\u7684\u771f\u4f20\uff0c\u6b66\u7384 \u971c\u83b7\u5f97\u4e86\u5e08\u7236\u6240\u7559\u7684\u5251\u8c31\u4e4b\u540e\uff0c\u5251\u672f\u4e5f\u7a81\u98de\u731b\u8fdb\uff0c\u867d\u6bd4\u4e0d\u4e0a\u5e08\u5144\uff0c\u4ea6\u4e0d\u8fc7\u7a0d\u5dee\u4e00\u7b79\u800c\u5df2\u3002 \u8fd8\u6709\u4e00\u4e2a\u674e\u9038\uff0c\u867d\u7136\u53d7\u4f24\uff0c\u4f46\u4ed6\u6301\u6709\u5b9d\u5251\uff0c\u4ea6\u662f\u4e0d\u65e0\u5a01\u529b\uff0c\u767e\u4f18\u4e0a\u4eba\u867d\u7136\u8eab\u6000\u7edd\u4e16\u6b66\u529f\uff0c \u4ee5\u4e00\u654c\u4e09\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u5374\u4e5f\u4e0d\u80fd\u53d6\u80dc\u3002 \u3000\u3000\u6b66\u7384\u971c\u5ffd\u7136\u5f97\u5230\u5e08\u5144\u7684\u76f8\u52a9\uff0c\u5fc3\u4e2d\u751a\u662f\u60ca\u5f02\uff0c\u60f3\u9053\uff1a\u201c\u5e08\u5144\u4e0a\u6b21\u4e2d\u4e86\u5929\u6076\u9053\u4eba\u7684\u6bd2 \u638c\uff0c\u4f30\u8ba1\u5728\u6211\u79bb\u5f00\u4ed6\u4e4b\u540e\uff0c\u4ed6\u6700\u5c11\u8fd8\u5f97\u8c03\u517b\u4e00\u4e2a\u6708\uff0c\u65b9\u53ef\u5b8c\u5168\u590d\u539f\uff0c\u7b97\u8fd9\u65f6\u95f4\uff0c\u73b0\u5728\u4e0d \u8fc7\u8fc7\u4e86\u4e8c\u5341\u591a\u5929\uff0c\u4ed6\u8fd8\u5e94\u5728\u5c71\u4e0a\u4f11\u517b\u624d\u662f\uff0c\u600e\u4e48\u5374\u4e5f\u5230\u4e86\u5927\u6c57\u7684\u738b\u57ce\uff1f\u800c\u4e14\u529f\u529b\u4f3c\u4e4e\u8fd8 \u66f4\u80dc\u4ece\u524d\uff1f\u4ed6\u53c8\u600e\u4e48\u4f1a\u4e0e\u590f\u4faf\u575a\u540c\u5728\u4e00\u8d77\uff1f\u201d\u5fc3\u4e2d\u7591\u95ee\u751a\u591a\uff0c\u4f46\u5728\u8fd9\u65f6\u6b63\u5728\u4e0e\u5f3a\u654c\u6027\u547d \u76f8\u640f\uff0c\u54ea\u6709\u4f59\u6687\u53bb\u95ee\u3002 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u751f\u6015\u4e00\u4e16\u76db\u540d\uff0c\u6298\u5728\u5c0f\u8f88\u4e4b\u624b\uff0c\u54ea\u8fd8\u6562\u6709\u4e1d\u6beb\u8f7b\u654c\uff1f\u4f46\u89c1\u4ed6\u5c06\u8888\u88df\u6325\u821e\uff0c \u52b2\u98ce\u547c\u547c\uff0c\u5377\u5f97\u7802\u98de\u77f3\u8d70\uff0c\u821e\u5230\u75be\u5904\uff0c\u5b9b\u5982\u4e00\u5927\u7247\u7ea2\u4e91\uff0c\u5c06\u56db\u4e2a\u4eba\u7684\u8eab\u5f71\u90fd\u7f69\u5f97\u98ce\u96e8\u4e0d \u900f\uff01\u88f4\u6b66\u4e8c\u4eba\u5c55\u5f00\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u5c31\u50cf\u4e24\u9053\u7535\u5149\uff0c\u5728\u5bc6\u4e91\u4e4b\u4e2d\u75be\u95ea\uff0c\u65f6\u4e0d\u65f6\u900f\u8fc7\u201c\u4e91\u5c42\u201d\uff0c \u5411\u767e\u5fe7\u4e0a\u4eba\u53cd\u51fb\uff0c\u674e\u9038\u5219\u4f8d\u7740\u5b9d\u5251\u81ea\u4fdd\u3002\u53cc\u65b9\u6253\u4e86\u4e2a\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u767e\u4f18\u4e0a\u4eba\u7684\u529f\u529b\uff0c\u5230\u5e95\u662f\u6bd4\u4ed6\u4eec\u9ad8\u51fa\u8bb8\u591a\uff0c\u5230\u4e86\u5c06\u8fd1\u767e\u62db\uff0c\u80dc\u8d25\u5c31\u6e10\u6e10\u5206\u51fa \u6765\u4e86\uff0c\u4ed6\u7684\u4e00\u88ad\u8888\u88df\u5c06\u4e09\u67c4\u957f\u5251\u7d27\u7d27\u88f9\u4f4f\uff0c\u4e09\u4eba\u4e4b\u4e2d\uff0c\u529f\u529b\u6700\u9ad8\u7684\u88f4\u53d4\u5ea6\u4ea6\u5df2\u89c9\u5f97\u5fc3\u810f \u7684\u8df3\u52a8\u52a0\u5feb\uff0c\u4e00\u5251\u523a\u51fa\uff0c\u6bcf\u6bcf\u529b\u4e0d\u4ece\u5fc3\uff0c\u674e\u9038\u5939\u5728\u88f4\u6b66\u4e8c\u4eba\u5f53\u4e2d\uff0c\u6240\u53d7\u7684\u538b\u529b\u8f83\u5c0f\uff0c\u4f46 \u4ed6\u4ec5\u80fd\u81ea\u4fdd\uff0c\u5b9d\u5251\u7684\u5a01\u529b\u5df2\u662f\u4e0d\u80fd\u53d1\u6325\u4e86\u3002 \u3000\u3000\u4f46\u53e6\u5916\u4e00\u8fb9\uff0c\u590f\u4faf\u575a\u4e5f\u5360\u4e86\u4e0a\u98ce\uff0c\u5929\u6076\u9053\u4eba\u4f7f\u51fa\u62c2\u5c18\u5939\u638c\u7684\u6b79\u6bd2\u529f\u592b\uff0c\u5c18\u5c3e\u6563\u5f00\uff0c \u5343\u4e1d\u4e07\u7f15\uff0c\u5185\u52b2\u8fd0\u8db3\uff0c\u6839\u6839\u90fd\u4f3c\u5229\u9488\u4e00\u822c\uff0c\u53ef\u4ee5\u5241\u4eba\u7a74\u9053\uff0c\u800c\u4ed6\u7684\u8150\u9aa8\u795e\u638c\u662f\u5929\u4e0b\u7b2c\u4e00 \u7b49\u7684\u6bd2\u638c\uff0c\u4e0d\u8981\u8bf4\u88ab\u4ed6\u6253\u4e2d\uff0c\u5c31\u662f\u6bd2\u638c\u6240\u53d1\u51fa\u7684\u90a3\u80a1\u8165\u98ce\uff0c\u6b66\u529f\u7a0d\u5f31\u7684\u4eba\u4e5f\u7981\u53d7\u4e0d\u8d77\uff0c \u4f46\u590f\u4faf\u575a\u53ea\u51ed\u7740\u4e00\u53cc\u8089\u638c\uff0c\u5374\u5c45\u7136\u5e94\u4ed8\u81ea\u5982\uff0c\u4f46\u89c1\u4ed6\u638c\u5288\u6307\u622a\uff0c\u7528\u638c\u529b\u9707\u6563\u62c2\u5c18\uff0c\u7528\u4e00 \u6307\u63b8\u529f\u5e94\u4ed8\u6bd2\u638c\uff0c\u4e0d\u8bba\u5929\u6076\u9053\u4eba\u7684\u6bd2\u638c\u4ece\u54ea\u4e2a\u65b9\u5411\u6253\u6765\uff0c\u4ed6\u90fd\u80fd\u62ff\u634f\u65f6\u5019\uff0c\u4e0d\u5dee\u6beb\u5398\uff0c \u4e2d\u6307\u4e00\u63b8\uff0c\u5c31\u70b9\u5411\u4ed6\u7684\u8109\u95e8\u3002\u5929\u6076\u9053\u4eba\u8bc6\u5f97\u5389\u5bb3\uff0c\u4e0d\u6562\u8ba9\u4ed6\u70b9\u5b9e\uff0c\u6bcf\u6b21\u90fd\u88ab\u8feb\u64a4\u638c\u56de\u9632\u3002 \u4e24\u4eba\u6253\u5f97\u975e\u5e38\u6fc0\u70c8\uff0c\u867d\u7136\u5f7c\u6b64\u90fd\u6ca1\u6709\u78b0\u7740\u5bf9\u65b9\u4e00\u4e0b\uff0c\u4f46\u6bcf\u4e00\u62db\u90fd\u8574\u85cf\u7740\u6781\u51f6\u9669\u7684\u6740\u673a\uff01 \u3000\u3000\u5929\u6076\u9053\u4eba\u4ee5\u516b\u5e74\u82e6\u529f\uff0c\u7ec3\u6210\u4e86\u8150\u9aa8\u795e\u638c\uff0c\u6597\u4e86\u5c06\u8fd1\u767e\u62db\uff0c\u89c1\u590f\u4faf\u575a\u4e0d\u4f46\u4e1d\u6beb\u672a\u6709\u4e2d \u6bd2\u7684\u8ff9\u8c61\uff0c\u800c\u4e14\u8d8a\u6218\u8d8a\u53d1\u7cbe\u795e\uff0c\u4ed6\u8d56\u4ee5\u5236\u80dc\u7684\u8150\u9aa8\u795e\u638c\u4f24\u4e0d\u4e86\u5bf9\u65b9\uff0c\u5fc3\u4e2d\u5148\u81ea\u60e7\uff0c\u4f55\u51b5 \u4ed6\u7684\u529f\u529b\u7a0d\u900a\u590f\u4faf\u575a\u4e00\u7b79\uff0c\u56e0\u6b64\u8fc7\u4e86\u767e\u62db\uff0c\u5c31\u5b8c\u5168\u88ab\u590f\u4faf\u575a\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u4f46\u5c31\u5728\u5929\u6076\u9053\u4eba\u88ab\u590f\u4faf\u575a\u903c\u5f97\u6b65\u6b65\u540e\u9000\u4e4b\u9645\uff0c\u88f4\u3001\u6b66\u3001\u674e\u4e09\u4eba\uff0c\u4e5f\u7ed9\u767e\u4f18\u4e0a\u4eba\u903c\u5f97 \u6b65\u6b65\u540e\u9000\u3002\u767e\u5fe7\u4e0a\u4eba\u8888\u88df\u75be\u5c55\uff0c\u72c2\u6d9b\u9a87\u6d6a\u822c\u76f4\u5377\u8fc7\u6765\uff0c\u88f4\u3001\u6b66\u4e8c\u4eba\u7684\u957f\u5251\u4e00\u89e6\u53ca\u8888\u88df\uff0c \u5c31\u7ed9\u53cd\u5f39\u56de\u53bb\uff0c\u7adf\u662f\u5403\u4e0d\u6d88\u4ed6\u90a3\u80a1\u5f3a\u52b2\u7684\u538b\u529b\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u6709\u4eba\u6717\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\u56fe\u4eca\u65e5\u5f97\u89c1\u4f18\u4e91\u8001\u5c3c\u5251\u6cd5\uff0c\u771f\u662f \u597d\u554a\u3001\u597d\u554a\uff01\u201d\u8f6c\u773c\u95f4\u5c71\u5934\u4e0a\u591a\u4e86\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u4e43\u662f\u7b26\u4e0d\u7591\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u6307\u7740\u767e\u5fe7\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u4e8f\u4f60\u8fd8\u6709\u8fd9\u6837\u539a\u9762\u76ae\uff0c\u4f60\u8888\u88df\u7a7f\u4e86\u4e03\u5904\uff0c\u8fd8\u597d\u610f\u601d\u518d \u6253\u4e0b\u53bb\u5417\uff1f\u201d \u3000\u3000\u4ee5\u767e\u5fe7\u4e0a\u4eba\u7684\u6b66\u529f\u8eab\u4efd\uff0c\u5982\u4f55\u542c\u4e0d\u51fa\u7b26\u4e0d\u7591\u7684\u610f\u601d\uff1f\u90a3\u5c31\u662f\u8bf4\uff1a\u201c\u4f60\u4e0d\u8fc7\u51ed\u7740\u529f\u529b \u7a0d\u9ad8\uff0c\u6b3a\u4fae\u5c0f\u8f88\u7f62\u4e86\uff0c\u82e5\u662f\u53ea\u8bba\u5251\u6cd5\uff0c\u4f60\u65e9\u5df2\u8d25\u5728\u4ed6\u4eec\u7684\u5251\u4e0b\u4e86\uff01\u201d\u81ea\u4f18\u4e91\u8001\u5c3c\u6b7b\u540e\uff0c \u767e\u5fe7\u4e0a\u4eba\u81ea\u8d1f\u5929\u4e0b\u65e0\u654c\uff0c\u600e\u5fcd\u53d7\u5f97\u4e86\u7b26\u4e0d\u7591\u7684\u8ba5\u6d88\uff1f\u4fbf\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5c31\u6362\u4f60\u8fd9\u4e2a\u9178\u4e01\u6765 \u5427\uff01\u201d\u7b26\u4e0d\u7591\u7b11\u563b\u563b\u8131\u4e0b\u4e86\u4e00\u5bf9\u8349\u978b\uff0c\u8bf4\u9053\uff1a\u201c\u65e5\u95f4\u672a\u66fe\u5c3d\u5174\uff0c\u6b63\u597d\u548c\u4f60\u518d\u6218\u4e00\u573a\uff01\u201d \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u89c1\u4ed6\u62ff\u4e00\u5bf9\u8349\u978b\u5f53\u4f5c\u6b66\u5668\uff0c\u5927\u6012\u8bf4\u9053\uff1a\u201c\u6211\u9080\u4f60\u5355\u6253\u72ec\u6597\uff0c\u6b63\u662f\u770b\u5f97\u8d77\u4f60\uff0c \u4f60\u7adf\u6562\u8511\u89c6\u4e8e\u6211\uff1f\u201d\u7b26\u4e0d\u7591\u7b11\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff01\u6211\u8001\u7b26\u4e00\u65f6\u627e\u4e0d\u7740\u5408\u9002\u7684\u5175\u5668\uff0c\u53ea\u597d \u50cf\u4f60\u4e00\u6837\uff0c\u968f\u4fbf\u62ff\u4e00\u4ef6\u8eab\u4e0a\u7684\u4e1c\u897f\u4e86\uff0c\u6211\u4e0d\u770b\u8f7b\u4f60\u7684\u8888\u88df\uff0c\u4f60\u7109\u6562\u5c0f\u89d1\u6211\u7684\u8349\u978b\uff1f\u201d\u9ad8 \u624b\u6bd4\u62fc\uff0c\u5f7c\u6b64\u90fd\u4e0d\u80af\u5360\u534a\u70b9\u4fbf\u5b9c\uff0c\u7b26\u4e0d\u7591\u8981\u7528\u8349\u978b\u6765\u5bf9\u4ed8\u4ed6\u7684\u8888\u88df\uff0c\u6b63\u662f\u4e3a\u4e86\u7ef4\u6301\u8eab\u4efd\uff0c \u4f46\u767e\u5fe7\u4e0a\u4eba\u81ea\u5927\u60ef\u4e86\uff0c\u603b\u89c9\u5f97\u8fd9\u662f\u4e00\u79cd\u4fae\u8fb1\uff0c\u5f53\u4e0b\u8888\u88df\u4e00\u5c55\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u6cb9\u5634\u6ed1\u820c\uff0c \u4f11\u602a\u6211\u624b\u4e0b\u65e0\u60c5\uff01\u201d \u3000\u3000\u7b26\u4e0d\u7591\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u771f\u6562\u8f7b\u89c6\u6211\u8fd9\u5bf9\u8349\u978b\uff1f\u597d\u597d\u6253\uff01\u201d\u767e\u5fe7\u4e0a\u4eba\u7684\u8888\u88df\u7f69\u4e0b\uff0c\u7b26\u4e0d \u7591\u7684\u8349\u978b\u4e00\u626c\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u201c\u535c\u201d\u4e24\u58f0\uff0c\u597d\u50cf\u94c1\u68d2\u6253\u5728\u6781\u539a\u7684\u725b\u76ae\u4e0a\u4e00\u6837\uff0c\u53d1\u51fa\u95f7\u96f7 \u822c\u7684\u58f0\u54cd\uff0c\u4e24\u5927\u9ad8\u624b\u90fd\u662f\u8fd0\u8db3\u4e86\u5185\u5bb6\u771f\u529b\uff0c\u8fd9\u4e00\u8f83\u52b2\uff0c\u767b\u65f6\u53cc\u65b9\u90fd\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u864e\u53e3\u9178 \u9ebb\uff0c\u5404\u81ea\u5012\u9000\u4e00\u6b65\uff0c\u7b26\u4e0d\u7591\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u6211\u7684\u8349\u978b\u4e5f\u4e0d\u5f31\u4e8e\u4f60\u7684\u8888\u88df\u5427\uff1f\u201d\u500f\u7136\u95f4\u6b3a \u8eab\u76f4\u8fdb\uff0c\u626c\u8d77\u8349\u978b\u53c8\u6253\uff01 \u3000\u3000\u90a3\u4e00\u5bf9\u8349\u978b\u6253\u4e0b\u6765\uff0c\u7adf\u7136\u542b\u7740\u53cc\u5251\u7684\u62db\u6570\uff0c\u7cbe\u5999\u4e4b\u5904\uff0c\u5b9e\u4e0d\u5728\u88f4\u6b66\u4e8c\u4eba\u8054\u5251\u4e4b\u4e0b\uff0c \u4f46\u4ee5\u7b26\u4e0d\u7591\u7684\u529f\u529b\uff0c\u90a3\u5374\u662f\u8981\u6bd4\u88f4\u6b66\u4e8c\u4eba\u8054\u624b\u5408\u6597\u66f4\u96be\u5e94\u4ed8\u4e86\u3002 \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u89c1\u7b26\u4e0d\u7591\u5c06\u90a3\u5bf9\u8349\u978b\u4f7f\u5f97\u51fa\u795e\u5165\u5316\uff0c\u4e5f\u81ea\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97 \u8fd9\u9178\u4e01\u5938\u53e3\uff0c\u679c\u7136\u662f\u6709\u51e0\u5206\u771f\u624d\u5b9e\u5b66\uff01\u201d\u4e0d\u6562\u8f7b\u654c\uff0c\u5c31\u5728\u90a3\u5bf9\u8349\u978b\u582a\u582a\u88ad\u5230\u4e4b\u65f6\uff0c\u4ed6\u4e5f \u7a81\u7136\u6539\u4e86\u6218\u6cd5\uff0c\u5c06\u8888\u88df\u5377\u6210\u4e00\u675f\uff0c\u53d8\u6210\u4e86\u4e00\u652f\u6746\u68d2\uff0c\u84e6\u5730\u4e00\u6311\uff0c\u628a\u7b26\u4e0d\u7591\u7684\u53cc\u5251\u62db\u6570\u7834 \u89e3\u4e86\u3002 \u3000\u3000\u8888\u88df\u4e0e\u8349\u978b\u672c\u90fd\u662f\u6781\u5bfb\u5e38\u7684\u4e1c\u897f\uff0c\u4f46\u7ecf\u8fd9\u4e24\u5927\u9ad8\u624b\u8fd0\u4e0a\u5185\u529b\uff0c\u5374\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\uff01\u4f46 \u542c\u5f97\u52b2\u98ce\u547c\u547c\uff0c\u4e24\u4eba\u8d8a\u6253\u8d8a\u5feb\uff0c\u9976\u662f\u6b66\u7384\u971c\u90a3\u6837\u7684\u76ee\u529b\uff0c\u4e5f\u5206\u522b\u4e0d\u51fa\u8c01\u662f\u767e\u5fe7\u4e0a\u4eba\uff0c\u8c01 \u662f\u7b26\u4e0d\u7591\uff0c\u6240\u89c1\u7684\u53ea\u662f\u4e00\u7247\u7ea2\u4e91\u88f9\u7740\u4e24\u53ea\u5929\u77eb\u98de\u821e\u7684\u8349\u978b\u3002 \u3000\u3000\u672c\u6765\u82e5\u8bba\u529f\u529b\uff0c\u662f\u767e\u5fe7\u4e0a\u4eba\u8f83\u4e3a\u6df1\u539a\uff0c\u4f46\u4ed6\u521a\u521a\u7ecf\u8fc7\u4e00\u573a\u6076\u6218\uff0c\u6c14\u529b\u65b9\u9762\uff0c\u4e0d\u514d\u6709 \u6240\u635f\u8017\uff0c\u8fd9\u6837\u6b64\u6d88\u5f7c\u957f\uff0c\u6070\u597d\u62c9\u5e73\uff0c\u8c01\u90fd\u80dc\u4e0d\u4e86\u8c01\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u770b\u4e86\u4e00\u4f1a\uff0c\u5bf9\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4e8c\u4eba\u53ea\u6015\u975e\u5230\u5343\u62db\u4e4b\u540e\uff0c\u96be\u4ee5\u5206\u51fa\u80dc\u8d1f\uff0c \u767e\u4f18\u8001\u79c3\u9a74\uff0c\u4eca\u756a\u662f\u78b0\u5230\u4e86\u52b2\u654c\u4e86\u3002\u201d\u6b66\u7384\u971c\u653e\u4e0b\u4e86\u5fc3\uff0c\u8fd9\u65f6\u624d\u6709\u4f59\u6687\u95ee\u5979\u7684\u5e08\u5144\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u4f1a\u590d\u539f\u5f97\u8fd9\u4e48\u5feb\u7684\uff0c\u591a\u4e8f\u590f\u4faf\u524d\u8f88\u7ed9\u6211\u7528\u91d1\u9488\u62d4\u6bd2\uff0c\u53c8\u7ed9\u6211 \u5e26\u6765\u4e86\u4e00\u652f\u5343\u5e74\u4f55\u9996\u4e4c\uff0c\u53cd\u800c\u4ee4\u6211\u56e0\u7978\u5f97\u798f\u4e86\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u590f\u4faf\u524d\u8f88\u4e5f\u66fe\u5230\u4e86\u5929\u5c71 \u4e48\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u5c31\u5728\u4f60\u8d70\u540e\u7684\u7b2c\u4e09\u5929\uff0c\u4ed6\u548c\u8c37\u795e\u7fc1\u7b26\u4e0d\u7591\u8054\u8882\u4e0a\u5c71\u3002\u201d \u3000\u3000\u539f\u6765\u590f\u4faf\u575a\u5c1a\u672a\u77e5\u9053\u4f18\u4e91\u8001\u5c3c\u5df2\u6b7b\uff0c\u7ecf\u8fc7\u591a\u5e74\u7684\u63a2\u8bbf\uff0c\u53ea\u77e5\u9053\u5979\u9690\u5c45\u5929\u5c71\uff0c\u4fbf\u9080\u4e86 \u8c37\u7b26\u4e8c\u4eba\uff0c\u540c\u4e0a\u5929\u5c71\u5bfb\u8bbf\uff0c\u7ec8\u4e8e\u5bfb\u5230\u4e86\u88f4\u53d4\u5ea6\uff0c\u4ece\u88f4\u53d4\u5ea6\u7684\u53e3\u4e2d\uff0c\u8fd9\u624d\u77e5\u9053\u674e\u9038\u4e0e\u6b66\u7384 \u971c\u7684\u79cd\u79cd\u906d\u9047\uff0c\u77e5\u9053\u674e\u9038\u4e3a\u6551\u513f\u5b50\uff0c\u5df2\u5f80\u7a81\u53a5\u738b\u5ef7\u3002\u8c37\u795e\u7fc1\u5bf9\u674e\u9038\u6709\u5982\u5b50\u4f84\uff0c\u5f53\u7136\u4e0d\u80fd \u5750\u89c6\uff0c\u590f\u4faf\u575a\u542c\u8bf4\u4f18\u4e91\u8001\u5c3c\u7559\u6709\u9057\u7269\u7ed9\u4ed6\uff0c\u73b0\u5728\u6b66\u7384\u971c\u4e4b\u624b\uff0c\u4e5f\u6025\u7740\u8981\u89c1\u6b66\u7384\u971c\uff0c\u7b26\u4e0d \u7591\u5219\u60f3\u53bb\u770b\u6b66\u58eb\u5927\u4f1a\u70ed\u95f9\uff0c\u4e8e\u662f\u88f4\u53d4\u5ea6\u5c31\u9080\u4e86\u4ed6\u4eec\u4e09\u4eba\uff0c\u4e00\u540c\u8d76\u5f80\u7a81\u53a5\u738b\u5ef7\u3002\u6b66\u58eb\u5927\u4f1a \u90a3\u5929\uff0c\u88f4\u53d4\u5ea6\u5728\u5bd3\u6240\u7559\u5b88\uff0c\u6ca1\u6709\u53c2\u52a0\u3002 \u3000\u3000\u6b66\u7384\u971c\u53c8\u4f55\u9053\uff1a\u201c\u4f60\u4eec\u600e\u77e5\u9053\u6211\u6709\u8fd9\u573a\u707e\u7978\uff1f\u201d\u88f4\u53d4\u5ea6\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u5bb9\u6613\u77e5\u9053\uff1f \u590f\u4faf\u524d\u8f88\u4e00\u89c1\u4f60\uff0c\u5c31\u770b\u51fa\u4f60\u662f\u7528\u4e86\u4ed6\u7684\u6613\u5bb9\u4e39\u6539\u626e\u738b\u5983\u7684\uff0c\u5979\u6599\u4f60\u53ea\u80fd\u8499\u9a97\u4e00\u65f6\uff0c\u7ec8\u5c06 \u88ab\u4eba\u770b\u51fa\uff0c\u56e0\u6b64\u6211\u4eec\u56db\u4e2a\u4eba\u624d\u5206\u6279\u51fa\u6765\u63a5\u5e94\uff0c\u7ea6\u5b9a\u4ee5\u5578\u58f0\u4e3a\u53f7\uff0c\u8c01\u5148\u53d1\u73b0\u4f60\u4eec\uff0c\u5c31\u62db\u5524 \u5176\u4ed6\u7684\u4eba\u968f\u6765\u3002\u8c37\u795e\u7fc1\u6050\u6015\u4e5f\u5c31\u8981\u6765\u4e86\u3002\u201d\u6b66\u7384\u971c\u8fd9\u624d\u660e\u767d\u4e86\u5176\u4e2d\u7684\u66f2\u6298\uff0c\u60f3\u8d77\u590f\u4faf\u575a\u3001 \u5c09\u8fdf\u70af\u4e0e\u5979\u5e08\u7236\u7684\u4e00\u6bb5\u60c5\u5b7d\u7ea0\u7f20\uff0c\u5fc3\u4e2d\u751a\u4e3a\u611f\u53f9\u3002 \u3000\u3000\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u590f\u4faf\u575a\u4e0e\u5929\u6076\u9053\u4eba\u9ad8\u547c\u9163\u6597\uff0c\u5929\u6076\u9053\u4eba\u6240\u53d1\u51fa\u7684\u6bd2\u6c14\u8165\u98ce\uff0c\u7adf\u51dd\u7ed3 \u4e00\u5c42\u7565\u5e26\u6de1\u7d2b\u8272\u7684\u8584\u96fe\uff0c\u7b3c\u7f69\u5728\u4e24\u4eba\u4e0a\u7a7a\uff0c\u6b66\u7384\u971c\u88f4\u53d4\u5ea6\u4e0e\u4ed6\u4eec\u76f8\u8ddd\u751a\u8fdc\uff0c\u4e5f\u95fb\u5230\u4e00\u80a1 \u523a\u9f3b\u7684\u6076\u5473\uff0c\u4e0d\u7981\u76f8\u987e\u9a87\u7136\uff0c\u88f4\u53d4\u5ea6\u60f3\u8d77\u90a3\u65e5\u4e2d\u4ed6\u6bd2\u638c\uff0c\u66f4\u89c9\u5fc3\u60b8\uff0c\u5fc3\u9053\uff1a\u201c\u5e78\u597d\u90a3\u65e5 \u5e08\u59b9\u7528\u5e08\u7236\u7684\u6cd5\u8eab\u5413\u9000\u4ed6\uff0c\u82e5\u518d\u6371\u4ed6\u4e00\u638c\uff0c\u90a3\u771f\u662f\u4e0d\u582a\u60f3\u50cf\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u89c1\u5929\u6076\u9053\u4eba\u9ad8\u547c\u9163\u6597\uff0c\u4e00\u638c\u731b\u4f3c\u4e00\u638c\uff0c\u800c\u590f\u4faf\u575a\u5219\u6b65\u6b65\u540e\u9000\uff0c\u5934\u9876\u4e0a\u5192\u51fa\u4e86 \u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u4e0d\u7981\u6697\u6697\u4e3a\u4ed6\u62c5\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4e0e\u8fd9\u7b49\u9b54\u5934\u4e0d\u5fc5\u8bb2\u4ec0\u4e48\u4fe1\u4e49\uff0c\u54b1\u4eec \u8054\u624b\u5c06\u4ed6\u9664\u6389\u4e86\u5427\u3002\u201d\u6590\u53d4\u5ea6\u7b11\u9053\uff1a\u201c\u6709\u590f\u4faf\u524d\u8f88\u5df2\u8db3\u53ef\u9664\u5730\uff0c\u4f55\u987b\u54b1\u4eec\u8d39\u529b\uff1f\u201d\u6b66\u7384 \u971c\u9053\uff1a\u201c\u590f\u4faf\u524d\u8f88\u521a\u624d\u8fd8\u5360\u4e0a\u98ce\uff0c\u4f46\u4f60\u770b\u73b0\u5728\u5374\u5ffd\u7136\u53d8\u6210\u90a3\u6076\u9053\u5360\u4e86\u4e0a\u98ce\uff0c\u53ea\u6015\u662f\u590f\u4faf \u524d\u8f88\u5e74\u8001\u4f53\u8870\uff0c\u96be\u4ee5\u6301\u4e45\uff01\u201d\u88f4\u53d4\u5ea6\u7b11\u9053\uff1a\u201c\u4f60\u770b\u9519\u4e86\uff0c\u590f\u4faf\u524d\u8f88\u5927\u7ea6\u7528\u4e0d\u4e86\u5341\u62db\uff0c\u4fbf \u53ef\u8d62\u4ed6\u4e86\u3002\u201d\u6b66\u7384\u971c\u77e5\u9053\u5e08\u5144\u4e0d\u4f1a\u4e71\u8bf4\uff0c\u518d\u4ed4\u7ec6\u770b\u65f6\uff0c\u4f46\u89c1\u590f\u4faf\u575a\u53cc\u773c\u795e\u5149\u6e5b\u7136\uff0c\u867d\u7136 \u4e0d\u4f4f\u540e\u9000\uff0c\u8eab\u6cd5\u6b65\u6cd5\uff0c\u5374\u662f\u4e1d\u6beb\u4e0d\u4e71\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u539f\u6765\u5929\u6076\u9053\u4eba\u4e45\u6218\u4e0d\u4e0b\uff0c\u77e5\u9053\u590f\u4faf\u575a\u7684\u5185\u529f\u7684\u7cbe\u7eaf\u5728\u5df2\u4e4b\u4e0a\uff0c\u5fc5\u8d25\u65e0\u7591\uff0c\u4ed6\u4e00\u53d1\u4e86 \u6068\uff0c\u628a\u5168\u8eab\u529f\u529b\u90fd\u8fd0\u5230\u638c\u4e0a\uff0c\u4f5c\u56f0\u72b9\u4e4b\u6597\uff0c\u8150\u9aa8\u795e\u638c\u672c\u5c31\u5389\u5bb3\u65e0\u6bd4\uff0c\u52a0\u4ee5\u4ed6\u62fc\u4e86\u6027\u547d\uff0c \u53d1\u52a8\u653b\u52bf\uff0c\u6240\u4ee5\u590f\u4faf\u575a\u4e5f\u4e0d\u5fc5\u548c\u4ed6\u786c\u63a5\u786c\u67b6\u3002 \u3000\u3000\u53ef\u662f\u590f\u4faf\u575a\u4e5f\u6b63\u597d\u5c06\u8ba1\u5c31\u8ba1\uff0c\u6d88\u8017\u4ed6\u7684\u771f\u529b\uff0c\u6218\u5230\u5206\u9645\uff0c\u590f\u4faf\u575a\u7a81\u7136\u559d\u9053\uff1a\u201c\u5929\u6076 \u9053\u4eba\uff0c\u4f60\u5df2\u662f\u9ed4\u9a74\u6280\u5c3d\uff0c\u8fd8\u4e0d\u670d\u4e48\uff1f\u201d\u53cc\u638c\u8fde\u73af\u75be\u5288\uff0c\u7740\u7740\u53cd\u653b\uff0c\u521a\u731b\u65e0\u8bba\u7684\u638c\u52bf\u4e4b\u4e2d \u8fd8\u5939\u7740\u4e00\u6307\u63b8\u529f\uff0c\u767b\u65f6\u628a\u5929\u6076\u9053\u4eba\u6253\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u3002\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u72fc\u72c8\u4e4b\u8c61\u6bd5\u9732\uff01 \u3000\u3000\u5929\u6076\u9053\u4eba\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u8d62\u6211\uff0c\u6211\u8f93\u4e86\u4e5f\u8fd8\u4e0d\u670d\uff01\u201d\u590f\u4faf\u575a\u7b11\u9053\uff1a\u201c\u8981\u600e\u6837\u4f60\u624d\u5fc3 \u670d\uff1f\u201d\u5929\u6076\u9053\u4eba\u9053\uff1a\u201c\u4f60\u6562\u786c\u63a5\u6211\u4e00\u638c\u4e48\uff1f\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u6562\uff1f\u6211\u4e0d\u8fd8\u624b\uff0c\u4fbf\u4efb \u4f60\u6253\u4e00\u638c\u5982\u4f55\u3002\u53ea\u662f\u8fd9\u4e00\u638c\u3002\u8981\u4e0d\u4e86\u6211\u7684\u6027\u547d\uff0c\u4f60\u81ea\u5df1\u5c31\u8981\u9001\u547d\u4e86\uff0c\u4f60\u6562\u6253\u4fbf\u6253\uff01\u201d\u5929 \u6076\u9053\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u5929\u4e0b\u6709\u8c01\u80fd\u591f\u786c\u63a5\u6211\u7684\u8150\u9aa8\u795e\u638c\uff0c\u7eb5\u7136\u6211\u56e0\u6b64\u9001\u547d\uff0c\u4ea6\u662f\u6b7b \u800c\u65e0\u6028\uff0c\u53ef\u662f\u590f\u4faf\u8001\u513f\uff0c\u4f60\u6b7b\u4e86\u53ef\u4e0d\u80fd\u57cb\u6028\u6211\u554a\uff01\u201d\u590f\u4faf\u575a\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u4f55\u987b\u591a \u8bf4\uff0c\u6253\u5427\uff01\u201d\u8bf4\u7f62\u679c\u7136\u8d1f\u624b\u633a\u80f8\uff0c\u7b49\u4faf\u5929\u6076\u9053\u4eba\u53d1\u638c\uff01 \u3000\u3000\u674e\u9038\u8fd9\u65f6\u6b63\u95ed\u76ee\u517b\u795e\u3002\u8c03\u505c\u547c\u5438\uff0c\u542c\u5f97\u590f\u4faf\u575a\u548c\u5929\u6076\u9053\u4eba\u8fd9\u756a\u5bf9\u7b54\uff0c\u4e0d\u7981\u5fc3\u5934\u5927\u9707\uff0c \u7741\u5f00\u773c\u6765\uff0c\u60f3\u9053\uff1a\u201c\u516b\u5e74\u4e4b\u524d\uff0c\u5929\u6076\u9053\u4eba\u7684\u8150\u9aa8\u795e\u638c\u5c1a\u672a\u7ec3\u6210\uff0c\u590f\u4faf\u524d\u8f88\u63a5\u4ed6\u4e00\u638c\uff0c\u8eab \u4e0a\u62ab\u4e86\u91d1\u4e1d\u8f6f\u7532\uff0c\u4e8b\u540e\u8fd8\u8981\u51cb\u517b\u591a\u5929\uff0c\u73b0\u5728\u5929\u6076\u7684\u6bd2\u638c\u5df2\u7ecf\u7ec3\u6210\uff0c\u4ed6\u600e\u7684\u8fd8\u8fd9\u6837\u6258\u5927\uff1f\u201d \u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u5929\u6076\u9053\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u5df2\u7136\u4e00\u638c\u5288\u4e0b\uff01 \u3000\u3000\u674e\u9038\u4e0e\u6b66\u7384\u971c\u90fd\u5413\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5929\u6076\u9053\u4eba\u7684\u8eab\u5b50\u7a81\u7136\u629b\u4e86\u8d77 \u6765\uff0c\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u88f4\u53d4\u5ea6\u662f\u4e2a\u6b66\u5b66\u7684\u884c\u5bb6\uff0c\u65b9\u81ea\u60ca\u8be7\uff0c\u5fc3\u60f3\uff1a\u201c\u5929\u6076\u9053\u4eba\u5df2 \u88ab\u590f\u4faf\u575a\u9707\u5f97\u91cd\u4f24\uff0c\u5c45\u7136\u8fd8\u80fd\u65bd\u5c55\u8fd9\u7b49\u4e0a\u4e58\u7684\u8f7b\u529f\u672c\u9886\uff01\u201d\u5fc3\u5ff5\u65b9\u52a8\uff0c\u5ffd\u89c1\u5929\u6076\u9053\u4eba\u54ea \u4e2a\u7b4b\u6597\u8fd8\u6ca1\u6709\u7ffb\u8fc7\u6765\uff0c\u4fbf\u7a81\u7136\u50cf\u65ad\u4e86\u7ebf\u7684\u98ce\u7b5d\u4f3c\u7684\uff0c\u4ece\u534a\u7a7a\u4e2d\u7b14\u76f4\u7684\u8dcc\u843d\uff0c\u63a5\u7740\u662f\u4e00\u58f0 \u51c4\u5389\u7684\u53eb\u58f0\uff0c\u5212\u8fc7\u957f\u7a7a\uff0c\u542c\u5f97\u4eba\u6bdb\u9aa8\u8038\u7136\uff0c\u518d\u770b\u90a3\u590f\u4faf\u575a\u65f6\uff0c\u53ea\u89c1\u590f\u4faf\u575a\u6728\u7136\u7ad9\u7acb\uff0c\u9762 \u8272\u7070\u767d\u3002\u6709\u5982\u4e00\u5c0a\u77f3\u50cf\uff0c\u90a3\u795e\u60c5\u4e5f\u662f\u53ef\u6015\u6781\u4e86\u3002\u539f\u6765\u5728\u8fd9\u516b\u5e74\u5f53\u4e2d\uff0c\u5929\u6076\u9053\u4eba\u82e6\u7ec3\u8150\u9aa8 \u795e\u638c\uff0c\u590f\u4faf\u575a\u4e5f\u82e6\u7ec3\u89e3\u6bd2\u7684\u672c\u9886\uff0c\u4ed6\u4e8b\u524d\u5df2\u541e\u4e0b\u4e86\u81ea\u5236\u7684\u201c\u56fa\u9b44\u57f9\u5143\u4e39\u201d\uff0c\u90a3\u662f\u4e13\u95e8\u9632 \u5fa1\u8150\u9aa8\u795e\u638c\u7684\uff0c\u800c\u4e14\u5728\u524d\u80f8\u3001\u540e\u80f8\uff0c\u90fd\u5b89\u7f6e\u4e86\u5b9d\u94a2\u62a4\u5fc3\u955c\uff0c\u6bd4\u4e0a\u4e00\u6b21\u6240\u62ab\u7684\u91d1\u4e1d\u8f6f\u7532\u66f4 \u4e3a\u575a\u539a\uff0c\u8fd9\u624d\u6562\u5728\u5929\u6076\u9053\u4eba\u7684\u771f\u529b\u5927\u5927\u635f\u8017\u4e4b\u540e\uff0c\u786c\u63a5\u4ed6\u7684\u4e00\u638c\u3002 \u3000\u3000\u590f\u4faf\u575a\u7684\u5185\u529f\u672c\u6765\u6bd4\u5929\u6076\u9053\u4eba\u7cbe\u7eaf\u5f97\u591a\uff0c\u4f55\u51b5\u5929\u6076\u9053\u4eba\u662f\u5728\u4e0e\u4ed6\u82e6\u6218\u6570\u767e\u62db\u4e4b\u540e\uff0c \u4e1a\u5df2\u5230\u4e86\u5f3a\u9a7d\u4e4b\u672b\uff0c\u600e\u80fd\u62b5\u6321\u5f97\u4e86\u590f\u4faf\u575a\u7684\u53cd\u9707\u4e4b\u529b\uff1f\u504f\u504f\u4ed6\u8fd8\u60f3\u987e\u5168\u9762\u5b50\uff0c\u88ab\u5f39\u5230\u534a \u7a7a\uff0c\u8fd8\u60f3\u65bd\u5c55\u8f7b\u529f\u9003\u8d70\uff0c\u767b\u65f6\u5fc3\u810f\u7206\u88c2\uff0c\u8dcc\u4e0b\u5c71\u5761\u6b7b\u4e86\u3002 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u4ed6\u89c1\u5929\u6076\u9053\u4eba\u4e0e\u590f\u4faf\u575a\u786c\u62fc\uff0c\u5df2\u77e5\u4e0d\u5999\uff0c\u521a\u60f3\u8d76\u6765 \u62e6\u963b\uff0c\u5929\u6076\u9053\u4eba\u5df2\u88ab\u9707\u4e0a\u534a\u7a7a\uff0c\u767e\u4f18\u4e0a\u4eba\u5927\u543c\u4e00\u58f0\uff0c\u820d\u4e86\u7b26\u4e0d\u7591\uff0c\u7acb\u5373\u5411\u590f\u4faf\u575a\u6251\u53bb\u3002 \u7b26\u4e0d\u7591\u60c5\u77e5\u590f\u4faf\u575a\u4e5f\u5fc5\u5b9a\u53d7\u4e86\u91cd\u4f24\uff0c\u54ea\u6562\u8ba9\u5f3a\u654c\u8131\u8eab\uff0c\u7acb\u5373\u4e5f\u662f\u51cc\u7a7a\u8dc3\u8d77\uff0c\u4e00\u53cc\u8349\u978b\uff0c \u7167\u7740\u767e\u5fe7\u4e0a\u4eba\u7684\u5149\u5934\u4fbf\u6253\uff0c\u767e\u5fe7\u4e0a\u4eba\u4e00\u58f0\u5927\u559d\uff0c\u8888\u88df\u5377\u6210\u4e00\u675f\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u9563\u5929\u201d\uff0c\u90a3 \u8888\u88df\u5377\u6210\u4e00\u675f\u4e4b\u540e\uff0c\u7ecf\u4ed6\u5185\u529b\u8fd0\u7528\uff0c\u8d5b\u5982\u94c1\u68d2\uff0c\u53ea\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u7b26\u4e0d\u7591\u7684\u4e00\u53ea\u8349\u978b\u88ab \u4ed6\u6253\u843d\u3002\u7b26\u4e0d\u7591\u4f7f\u4e86\u4e00\u4e2a\u201c\u51cc\u7a7a\u6b65\u865a\u201d\u7684\u8eab\u6cd5\uff0c\u786c\u751f\u751f\u7684\u518d\u62d4\u9ad8\u4e09\u5c3a\uff0c\u907f\u5f00\u4e86\u767e\u5fe7\u4e0a\u4eba \u90a3\u80a1\u5f3a\u52b2\u7684\u529b\u9053\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u518d\u63a5\u6211\u8fd9\u53ea\u8349\u978b\uff01\u201d\u8349\u978b\u5728\u534a\u7a7a\u63b7\u4e0b\uff0c\u767e\u5fe7\u4e0a\u4eba\u811a\u4e0d \u505c\uff0c\u53cd\u624b\u4e00\u6325\uff0c\u4f46\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u53ea\u8349\u978b\u6b63\u6b63\u6253\u4e2d\u4ed6\u7684\u624b\u8155\uff0c\u767e\u4f18\u4e0a\u4eba\u7684\u624b\u8155\u767b \u65f6\u7ea2\u80bf\u8d77\u6765\uff0c\u4f46\u90a3\u53ea\u8349\u978b\u4e5f\u5df2\u88ab\u4ed6\u9707\u6210\u7c89\u788e\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u767e\u5fe7\u4e0a\u4eba\u5df2\u51b2\u5230\u590f\u4faf\u575a\u8eab\u8fb9\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u590f\u4faf \u8001\u513f\uff0c\u6211\u8981\u4f60\u4e00\u547d\u8fd8\u4e00\u547d\uff01\u201d\u4ed6\u53ea\u662f\u987e\u5fcc\u8eab\u540e\u7684\u7b26\u4e0d\u7591\uff0c\u51b7\u4e0d\u9632\u4e24\u9053\u5251\u5149\uff0c\u7a81\u7136\u4ece\u590f\u4faf \u575a\u4e24\u4fa7\u98de\u51fa\uff0c\u88f4\u53d4\u5ea6\u4e0e\u6b66\u7384\u971c\u4e24\u4eba\u5df2\u7ecf\u8d76\u5230\u3002\u8fd9\u4e00\u4e0b\uff0c\u4e09\u65b9\u9762\u90fd\u4f3c\u79bb\u5f26\u4e4b\u7bad\uff0c\u78b0\u4e2a\u6b63\u7740\uff0c \u767e\u5fe7\u4e0a\u4eba\u5927\u543c\u4e00\u58f0\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u8888\u88df\u4e00\u5c55\uff0c\u628a\u88f4\u6b66\u4e8c\u4eba\u6454\u5012\u5730\u4e0a\u3002\u767e\u5fe7\u4e0a\u4eba\u5012\u7eb5\u51fa\u4e94\u516d \u4e08\u5f00\u5916\uff0c\u559d\u9053\uff1a\u201c\u6d12\u5bb6\u8fdf\u65e9\u90fd\u8981\u53d6\u4f60\u4eec\u7684\u6027\u547d\uff01\u201d\u54fc\u4e86\u4e00\u58f0\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u5c31\u4e0d\u89c1\u4e86\u4ed6\u7684 \u80cc\u5f71\u3002 \u3000\u3000\u539f\u6765\u5728\u90a3\u4e00\u5239\u90a3\u95f4\uff0c\u53cc\u65b9\u867d\u662f\u7535\u95ea\u822c\u7684\u4e00\u89e6\u5373\u6536\uff0c\u4f46\u5df2\u4ea4\u6362\u4e86\u51e0\u4e2a\u9669\u62db\u3002\u88f4\u6b66\u4e8c\u4eba \u65bd\u5c55\u4e86\u4e00\u62db\u6700\u7cbe\u5999\u7684\u5251\u672f\uff0c\u53ef\u4ee5\u540c\u65f6\u5241\u654c\u4eba\u7684\u4e03\u5904\u7a74\u9053\uff0c\u767e\u5fe7\u4e0a\u4eba\u4ed7\u7740\u6df1\u6e5b\u7684\u5185\u529f\uff0c\u5c55 \u5f00\u8888\u88df\u62b5\u6321\uff0c\u867d\u7136\u6ca1\u6709\u7ed9\u4ed6\u4eec\u523a\u4e2d\uff0c\u8888\u88df\u5374\u88ab\u523a\u7a7f\u4e86\u5341\u56db\u4e2a\u5c0f\u5b54\uff0c\u8fde\u4e0a\u4ee5\u524d\u7684\u5251\u75d5\uff0c\u90a3 \u4ef6\u8888\u88df\u5df2\u662f\u767e\u5b54\u5343\u75ae\uff0c\u4e0d\u582a\u4f55\u7528\u4e86\u3002\u767e\u4f18\u4e0a\u4eba\u4e00\u60f3\uff0c\u6709\u7b26\u4e0d\u7591\u52a0\u4e0a\u88f4\u6b66\u4e24\u4eba\uff0c\u81ea\u5df1\u7edd\u5bf9 \u8ba8\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u56e0\u6b64\u53ea\u597d\u54bd\u4e0b\u90a3\u80a1\u6012\u6c14\uff0c\u72ec\u81ea\u9003\u8d70\uff0c\u8fde\u5929\u6076\u9053\u4eba\u7684\u751f\u6b7b\u4e5f\u4e0d\u53ca\u5bdf\u770b\u4e86\u3002 \u3000\u3000\u88f4\u6b66\u4e24\u4eba\u88ab\u767e\u4f18\u4e0a\u4eba\u7684\u5185\u529b\u9707\u5012\uff0c\u5e78\u800c\u4f24\u5f97\u4e0d\u91cd\uff0c\u722c\u4e86\u8d77\u6765\uff0c\u6025\u5fd9\u5148\u53bb\u770b\u590f\u4faf\u575a\uff0c \u53ea\u89c1\u590f\u4faf\u575a\u6447\u5934\u82e6\u7b11\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u4e00\u53e3\u7600\u8840\u3002\u6b66\u7384\u971c\u9053\uff1a\u201c\u590f\u4faf\u524d\u8f88\uff0c\u4f60\u600e \u4e48\u5566\uff1f\u201d\u590f\u4faf\u7b11\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u8fd8\u597d\uff0c\u5c1a\u4e0d\u81f3\u88ab\u5929\u6076\u9053\u4eba\u8981\u4e86\u6027\u547d\u3002\u201d\u53d6\u51fa\u4e03\u53e3\u91d1\u9488\uff0c\u63d2 \u5728\u81ea\u5df1\u7684\u9633\u9675\u3001\u7ef4\u9053\u3001\u5f52\u85cf\u3001\u7389\u6cc9\u3001\u5929\u95e8\u3001\u5173\u5143\u3001\u547d\u95e8\u4e03\u5904\u5927\u7a74\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u5c06\u91d1\u9488\u62d4 \u51fa\uff0c\u4e03\u53e3\u91d1\u9488\u90fd\u53d8\u6210\u4e86\u9ed1\u58a8\u58a8\u7684\uff0c\u4f17\u4eba\u4e0d\u7981\u9a87\u7136\u3002\u590f\u4faf\u575a\u9053\uff1a\u201c\u6211\u4ee5\u516b\u5e74\u529f\u592b\uff0c\u82e6\u7ec3\u62b5 \u5fa1\u4ed6\u8150\u9aa8\u795e\u638c\u7684\u529f\u592b\uff0c\u60f3\u4e0d\u5230\u4ed6\u7684\u5389\u5bb3\u4e4b\u5904\uff0c\u7adf\u51fa\u4e4e\u6211\u7684\u610f\u6599\u4e4b\u5916\uff0c\u5e78\u800c\u4fdd\u5168\u4e86\u8fd9\u51e0\u6839 \u8001\u9aa8\u5934\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4e5f\u4e0d\u60f3\u5728\u6c5f\u6e56\u4e0a\u901e\u5f3a\u4e89\u80dc\u4e86\u3002\u201d\u7b26\u4e0d\u7591\u542c\u8fd9\u8bf4\u8bdd\uff0c\u77e5\u9053\u4ed6\u5728\u53bb\u6bd2\u7597 \u4f24\u4e4b\u540e\uff0c\u6700\u5c11\u4e5f\u5f97\u635f\u8017\u5341\u5e74\u529f\u529b\uff0c\u5fc3\u4e2d\u751a\u66ff\u8001\u53cb\u96be\u8fc7\uff0c\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u4f60\u526a\u9664\u4e86\u8fd9\u4e2a\u6076\u9053\uff0c \u4e5f\u603b\u7b97\u503c\u5f97\u4e86\u3002\u6211\u8001\u7b26\u88ab\u8001\u79c3\u9a74\u6bc1\u4e86\u6211\u7684\u4e00\u53cc\u8349\u978b\uff0c\u81ea\u5df1\u65e0\u529b\u62a5\u590d\uff0c\u624d\u771f\u662f\u60ed\u6127\u5f97\u5f88\u554a\uff01 \u5e78\u800c\u4e24\u4f4d\u8d24\u4f84\u6bc1\u4e86\u4ed6\u7684\u90a3\u4ef6\u8888\u88df\uff0c\u7ed9\u6211\u51fa\u4e86\u4e00\u53e3\u95f7\u6c14\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u9053\uff1a\u201c\u590f\u4faf\u524d\u8f88\uff0c\u6211\u5e08\u7236\u7559\u6709\u4e00\u4e2a\u7389\u5323\u7ed9\u4f60\u3002\u201d\u590f\u4faf\u575a\u6253\u5f00\u5323\u5b50\u4e00\u770b\uff0c\u53ea\u89c1 \u91cc\u9762\u6709\u51e0\u6735\u5929\u5c71\u96ea\u83b2\u3002\u8fd8\u6709\u51e0\u6837\u4e16\u95f4\u7f55\u89c1\u7684\u7075\u836f\uff0c\u90fd\u662f\u4ed6\u4ee5\u524d\u548c\u4f18\u4e91\u8001\u5c3c\u8bf4\u8fc7\uff0c\u800c\u81ea\u5df1 \u5c1a\u672a\u91c7\u96c6\u5230\u7684\u3002\u4ed6\u6367\u7740\u7389\u5323\uff0c\u5ff5\u53ca\u4f18\u4e91\u8001\u5c3c\u7684\u8fd9\u756a\u5fc3\u610f\uff0c\u4e0d\u7981\u6f78\u7136\u6cea\u4e0b\uff0c\u60f3\u9053\uff1a\u201c\u743c\u9999 \uff08\u4f18\u4e91\u795e\u5c3c\u7684\u4fd7\u5bb6\u540d\u5b57\uff09\u751f\u524d\u867d\u7136\u6ca1\u6709\u7b54\u5e94\u6211\u7684\u6c42\u5a5a\uff0c\u4e34\u6b7b\u5374\u4e5f\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u6211\u8fd9\u4e2a\u670b\u53cb\uff0c \u4eba\u751f\u5f97\u6b64\u77e5\u5df2\uff0c\u5c1a\u6709\u4f55\u61be\uff01\u201d \u3000\u3000\u590f\u4faf\u575a\u6f78\u7136\u6cea\u4e0b\uff0c\u7b26\u4e0d\u7591\u5374\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u513f\u8fd8\u54ed\u4ec0\u4e48\uff1f\u5929\u5c71\u96ea\u83b2\u80fd\u89e3\u767e\u6bd2\uff0c \u6bd4\u4f60\u81ea\u7ec3\u7684\u2018\u4ed9\u4e39\u2019\u8fd8\u8981\u7075\u6548\u5f97\u591a\uff0c\u6709\u4e00\u6735\u96ea\u83b2\uff0c\u4f60\u4fbf\u65e0\u987b\u635f\u5931\u5341\u5e74\u529f\u529b\u4e86\u3002\u201d\u590f\u4faf\u575a \u5bf9\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5728\u4f60\u5e08\u7236\u6b7b\u540e\uff0c\u6211\u8fd8\u8499\u53d7\u5979\u7684\u6069\u5fb7\u3002\u5979\u751f\u524d\u5e0c\u671b\u6211\u628a\u533b\u672f\u6d41\u4f20\u4e0b \u6765\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u6211\u7eb5\u7136\u6062\u590d\u529f\u529b\uff0c\u4e5f\u4e0d\u613f\u518d\u5728\u6c5f\u6e56\u4e89\u80dc\u4e86\u3002\u53d4\u5ea6\uff0c\u4f60\u4e0d\u5acc\u5f03\u6211\u7684\u8bdd\uff0c\u6211 \u613f\u4e0e\u4f60\u7ed3\u5e90\u6bd4\u90bb\uff0c\u66ff\u4f60\u7684\u5e08\u7236\u5b88\u575f\uff0c\u4e5f\u597d\u8d81\u6b64\u66ae\u5e74\u5c81\u6708\uff0c\u5199\u6210\u6211\u90a3\u51e0\u7bc7\u533b\u4e66\u3002\u201d\u88f4\u53d4\u5ea6 \u9053\uff1a\u201c\u5f97\u4e0e\u8001\u524d\u8f88\u4e3a\u90bb\uff0c\u6211\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u201d\u5ffd\u5730\u6709\u70b9\u611f\u6168\u8bf4\u9053\uff1a\u201c\u5341\u5e74\u6765\u6211\u672a\u4e0b\u8fc7\u5929\u5c71\uff0c \u8fd9\u6b21\u4e8b\u60c5\u7ed3\u675f\uff0c\u6211\u4e5f\u8be5\u56de\u53bb\u4e86\u3002\u5e08\u59b9\uff0c\u4f60\u5462\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u8981\u89c1\u7684\u4eba\u90fd\u89c1\u5230\u4e86\uff0c\u8981\u529e \u7684\u4e8b\u4e5f\u90fd\u529e\u4e86\uff0c\u660e\u5929\u4e00\u65e9\u6211\u4e5f\u8981\u56de\u56fd\u4e86\u3002\u201d\u7b26\u4e0d\u7591\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4f4d\u4f60\u5c1a\u672a\u89c1\u5230\uff0c\u4f60\u4e0d\u7b49 \u7b49\u4ed6\u5417\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u7b26\u4e0d\u7591\u7b11\u9053\uff1a\u201c\u66fe\u5728\u5ce8\u5d4b\u91d1\u9876\u548c\u4f60\u6bd4\u8fc7\u5251\u7684\u8c37\u795e\u7fc1\u554a\uff0c \u4f60\u5fd8\u8bb0\u4e86\u5417\uff1f\u90a3\u4e00\u6b21\u4f60\u5f04\u5f97\u4ed6\u51e0\u4e4e\u4e0b\u4e0d\u4e86\u53f0\u3002\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e00\u6b21\u7684\u4e8b\u60c5\uff0c \u6211\u8fd8\u672a\u6709\u673a\u4f1a\u5411\u4ed6\u8d54\u7f6a\u5462\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u662f\u5440\uff0c\u8001\u8c37\u600e\u4e48\u8fd8\u4e0d\u6765\uff1f\u4ed6\u96be\u9053\u5c1a\u672a\u542c\u89c1\u6211 \u4eec\u7684\u5578\u58f0\uff1f\u6216\u662f\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d\u7b26\u4e0d\u7591\u518d\u6b21\u64ae\u6248\u957f\u5578\uff0c\u5578\u58f0\u5b9b\u82e5\u9f99\u541f\uff0c\u5728\u8fd9\u9ad8\u5c71\u9876 \u4e0a\u53d1\u51fa\uff0c\u4f30\u91cf\u5341\u91cc\u5de6\u53f3\u90fd\u80fd\u542c\u5f97\u89c1\u3002 \u3000\u3000\u674e\u9038\u7684\u5b69\u5b50\u65e9\u5df2\u4ece\u8349\u4e1b\u91cc\u94bb\u51fa\u6765\uff0c\u8fd9\u65f6\u5ffd\u5730\u6251\u5230\u6b66\u7384\u971c\u8eab\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u8fd8\u6709 \u4e00\u4f4d\u4f60\u5c1a\u672a\u89c1\u5230\uff0c\u4f60\u4e0d\u7b49\u7b49\u4ed6\u5417\uff1f\u201d\u4ed6\u5b66\u7740\u7b26\u4e0d\u7591\u7684\u53e3\u6c14\uff0c\u7528\u540c\u6837\u7684\u8bf4\u8bdd\uff0c\u4ef0\u7740\u8138\u513f\u95ee \u6b66\u7384\u971c\uff0c\u5f15\u5f97\u6b66\u7384\u971c\u4e0d\u7981\u7b11\u4e86\u8d77\u6765\uff0c\u6402\u7740\u4ed6\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u90a3\u5b69\u5b50\u9053\uff1a\u201c\u6211\u7684\u5988\u5988\u3002\u90a3 \u5929\u665a\u4e0a\u5979\u4e0d\u51c6\u6211\u5403\u4f60\u7684\u4e1c\u897f\uff0c\u4f60\u751f\u6c14\u4e86\u4e48\uff1f\u201d\u6b66\u7384\u971c\u7b11\u5bb9\u987f\u53d9\uff0c\u5fc3\u5934\u6c89\u4e86\u4e0b\u6765\uff0c\u90a3\u5b69\u5b50 \u53c8\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u5988\u672a\u77e5\u9053\u662f\u4f60\u6551\u6211\uff0c\u77e5\u9053\u4e86\u4e00\u5b9a\u5f88\u611f\u6fc0\u4f60\u7684\uff0c\u4f60\u4e0d\u8981\u751f\u5979\u7684\u6c14\u3002\u201d\u8fd9\u8bdd \u4ed6\u5df2\u7ecf\u662f\u7b2c\u4e8c\u904d\u5411\u6b66\u7384\u971c\u8bf4\u4e86\uff0c\u6562\u60c5\u4ed6\u5f53\u771f\u662f\u5f88\u5bb3\u6015\u6b66\u7384\u971c\u751f\u6c14\u5462\u3002 \u3000\u3000\u6b66\u7384\u971c\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60\u7684\u5988\u5988\u662f\u5f88\u597d\u7684\u670b\u53cb\uff0c\u600e\u4f1a\u751f\u5979\u7684\u6c14\uff1f\u4e0d\u8fc7\uff0c\u6211\u6015\u6765\u4e0d\u53ca \u7b49\u5979\u4e86\uff0c\u597d\u5b69\u5b50\uff0c\u4f60\u7ed9\u6211\u5411\u5988\u5988\u95ee\u597d\uff0c\u8bf7\u5979\u4e0d\u8981\u602a\u6211\u3002\u201d\u90a3\u5b69\u5b50\u561f\u7740\u5c0f\u5634\u513f\u9053\uff1a\u201c\u4f60\u4e0d \u7b49\u5979\u89c1\u9762\uff0c\u5979\u5f53\u771f\u4f1a\u602a\u4f60\u7684\u3002\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u77e5\u9053\u5988\u4e0d\u4f1a\u602a\u7684\uff0c\u4f46\u4f60\u65e2\u7136\u6015\u5979\u602a\u4f60\uff0c \u6211\u5c31\u5413\u4e00\u5413\u4f60\u3002\u201d\u6b66\u7384\u971c\u5fae\u7b11\u9053\uff1a\u201c\u6709\u4f60\u66ff\u6211\u8bf4\u597d\u8bdd\uff0c\u6211\u77e5\u9053\u4f60\u5988\u4e0d\u4f1a\u602a\u6211\u7684\u3002\u201d \u3000\u3000\u674e\u9038\u542c\u4e86\u4ed6\u4eec\u7684\u5bf9\u7b54\uff0c\u4e0d\u7981\u611f\u89e6\u4e07\u7aef\uff0c\u5446\u5446\u7684\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u90a3\u5b69\u5b50\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60 \u771f\u7684\u660e\u5929\u4e00\u65e9\u5c31\u8981\u8d70\u4e86\uff1f\u6211\u820d\u4e0d\u5f97\u4f60\u554a\uff01\u201d\u6b66\u7384\u971c\u7d27\u7d27\u6402\u7740\u4ed6\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u6211\u4f1a\u8bb0\u7740 \u4f60\u7684\uff0c\u5230\u4f60\u957f\u5927\u4e86\u6211\u518d\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u590f\u4faf\u575a\u9053\uff1a\u201c\u8001\u8c37\u6765\u4e86\u3002\u201d\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\uff0c \u75be\u5982\u5954\u9a6c\uff0c\u76f4\u4e0a\u5c71\u5934\uff0c\u6b66\u7384\u971c\u5fc3\u9053\uff1a\u201c\u8c37\u795e\u7fc1\u4ee5\u8f7b\u7075\u98d8\u5ffd\u8e51\u4e91\u5251\u3001\u8e51\u4e91\u6b65\u9a70\u540d\uff0c\u8fd9\u8f7b\u529f \u8eab\u6cd5\uff0c\u679c\u7136\u662f\u975e\u540c\u5c0f\u53ef\u3002\u201d \u3000\u3000\u8f6c\u77ac\u95f4\u8c37\u795e\u7fc1\u4fbf\u6765\u5230\u5c71\u5934\uff0c\u7b26\u4e0d\u7591\u6b63\u60f3\u548c\u4ed6\u6253\u8da3\uff0c\u89c1\u4ed6\u7684\u5f62\u72b6\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u6025 \u5fd9\u95ee\u9053\uff1a\u201c\u8001\u8c37\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u662f\u8c01\u4f24\u4e86\u4f60\u4e86\uff1f\u201d \u3000\u3000\u4f46\u89c1\u8c37\u795e\u7fc1\u8863\u886b\u67d3\u8840\uff0c\u795e\u8272\u5f20\u60f6\uff0c\u6b66\u7384\u971c\u5413\u4e86\u4e00\u8df3\uff0c\u987e\u4e0d\u5f97\u548c\u4ed6\u53d9\u65e7\uff0c\u5148\u4e0a\u6765\u66ff\u4ed6 \u88f9\u4f24\u3002\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\uff0c\u6211\u4e0d\u8fc7\u4e2d\u4e86\u4e00\u7bad\uff0c\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u8fd8\u71ac\u5f97\u4f4f\u3002\u201d\u8c37\u795e\u7fc1\u662f\u4ee5 \u524d\u7684\u6b66\u6797\u76df\u4e3b\uff0c\u8f7b\u529f\u672c\u9886\u66f4\u662f\u5929\u4e0b\u65e0\u53cc\uff0c\u5c45\u7136\u6709\u4eba\u80fd\u591f\u5c04\u4f24\u8c37\u795e\u7fc1\uff0c\u5927\u5bb6\u90fd\u4e0d\u7981\u5341\u5206\u9a87 \u5f02\uff0c\u7eb7\u7eb7\u95ee\u4ed6\u7684\u906d\u9047\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u9038\uff0c\u4f60\u7684\u59bb\u5b50\u6765\u4e86\uff0c\u5440\uff0c\u5979\uff0c\u5979\u2014\u2014\u5979\u88ab\u6355\u4e86\uff01\u201d \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u5251\u80c6\u7434\u5fc3\u610f\u81ea\u4f24 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u5251\u80c6\u7434\u5fc3\u610f\u81ea\u4f24 \u3000\u3000\u8c37\u795e\u7fc1\u5e26\u6765\u7684\u8fd9\u4e2a\u6d88\u606f\uff0c\u72b9\u5982\u6674\u5929\u9739\u96f7\uff0c\u9707\u5f97\u4eba\u9b42\u98de\u9b44\u6563\uff0c\u674e\u9038\u5446\u82e5\u6728\u9e21\uff0c\u597d\u534a\u664c \u624d\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u4f1a\u5427\uff0c\u58c1\u59b9\uff0c\u5979\uff0c\u5979\u7b54\u5e94\u8fc7\u6211\u7559\u5728\u5929\u5c71\uff0c\u7b49\uff0c\u7b49\u6211\u56de\u6765\u7684\u3002\u201d\u4ed6 \u53d7\u60ca\u8fc7\u5ea6\uff0c\u4e00\u53e5\u8bdd\u5206\u6210\u4e86\u597d\u51e0\u622a\u624d\u8bf4\u5f97\u51fa\u6765\u3002 \u3000\u3000\u6b66\u7384\u971c\u54bd\u4e0b\u773c\u6cea\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u58c1\u59b9\u5bf9\u4f60\u60c5\u6df1\u4e49\u91cd\uff0c\u5979\u53d1\u8a93\u4e0e\u4f60\u540c\u751f\u5171\u6b7b\uff0c\u4f60\u4e0d\u77e5 \u9053\uff0c\u4f60\u79bb\u5f00\u5929\u5c71\u7684\u7b2c\u4e8c\u5929\uff0c\u5979\u4e5f\u8ddf\u7740\u6765\u4e86\u3002\u201d\u6b66\u7384\u971c\u60f3\u8d77\u957f\u5b59\u58c1\u90a3\u4e24\u9996\u9898\u58c1\u7684\u7edd\u547d\u8bd7\uff1a \u201c\u5341\u5e74\u68a6\u9192\u76f8\u601d\u6cea\uff0c\u4e07\u91cc\u897f\u98ce\u701a\u6d77\u6c99\u3002\u540c\u547d\u9e33\u9e2f\u60b2\u547d\u8584\uff0c\u5929\u6daf\u4f55\u5904\u662f\u543e\u5bb6\uff1f\u201d\u201c\u613f\u5c06\u70ed \u8840\u6d12\u80e1\u5c18\uff0c\u4e14\u628a\u9057\u8a00\u6258\u65e7\u4eba\uff0c\u5e94\u5ff5\u674e\u9038\u5bb6\u56fd\u6068\uff0c\u7559\u4ed6\u540c\u8d4f\u96ea\u5c71\u6625\u3002\u201d\u5ffd\u5730\u611f\u5230\u5185\u6127\u4e8e\u5fc3\uff0c \u60f3\u9053\uff1a\u201c\u8981\u4e0d\u662f\u4e3a\u4e86\u6211\uff0c\u5979\u4e5f\u8bb8\u4f1a\u542c\u674e\u9038\u7684\u8bdd\u7559\u5728\u5929\u5c71\u3002\u6211\u4e0d\u80fd\u602a\u5979\u5fc3\u80f8\u72ed\u7a84\uff0c\u6362\u4e86\u662f \u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u653e\u5fc3\u7684\u3002\u201d\u674e\u9038\u5219\u5728\u6697\u6697\u5947\u602a\uff0c\u6b66\u7384\u971c\u600e\u7684\u77e5\u9053\u5f97\u8fd9\u6837\u6e05\u695a\uff1f\u53ef\u662f\u5728\u90a3\u4e2a \u65f6\u5019\uff0c\u4ed6\u5df2\u65e0\u6687\u53bb\u95ee\u6b66\u7384\u971c\u4e86\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u6211\u5728\u7a81\u53a5\u7684\u90fd\u57ce\u5916\u8fb9\uff0c\u6b63\u78b0\u89c1\u957f\u5b59\u58c1\u88ab\u4ed6\u4eec\u8ffd\u6355\uff0c\u6211\u8fdf\u4e86\u4e00\u6b65\uff0c\u770b\u6e05 \u695a\u662f\u5979\u7684\u65f6\u5019\uff0c\u5979\u5df2\u88ab\u83e9\u63d0\u4e0a\u4eba\u6349\u83b7\uff0c\u62bc\u4e0a\u56da\u8f66\u3002\u201d\u674e\u9038\u8fd8\u6709\u70b9\u4e0d\u5927\u76f8\u4fe1\uff0c\u95ee\u9053\uff1a\u201c\u8c37 \u8001\u524d\u8f88\uff0c\u4f60\u5f53\u771f\u770b\u6e05\u695a\u4e86\u662f\u5979\uff1f\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u5979\u6253\u626e\u6210\u4e00\u4e2a\u7ef4\u65cf\u5987\u4eba\u7684\u6a21\u6837\uff0c\u4e5f\u7528\u4e86 \u590f\u4faf\u8001\u5144\u7684\u6613\u5bb9\u4e39\uff0c\u4f46\u5374\u7792\u4e0d\u8fc7\u6211\u7684\u773c\u775b\u3002\u5979\u770b\u89c1\u6211\u7684\u65f6\u5019\uff0c\u60ca\u53eb\u4e00\u58f0\uff0c\u770b\u6837\u5b50\u662f\u60f3\u5411 \u6211\u6c42\u6551\uff0c\u540e\u6765\u6015\u662f\u4e0d\u613f\u8fde\u7d2f\u6211\uff0c\u6ca1\u6709\u53eb\u51fa\u6211\u7684\u540d\u5b57\u3002\u674e\u9038\u542c\u4ed6\u8bf4\u5f97\u5343\u771f\u4e07\u786e\uff0c\u5fc3\u5934\u597d\u50cf \u5760\u4e86\u4e00\u5757\u94c5\u5757\uff0c\u6c89\u91cd\u4e4b\u6781\uff0c\u4f46\u5374\u4e5f\u6709\u70b9\u5947\u602a\uff1a\u957f\u5b59\u58c1\u65e2\u7136\u7528\u4e86\u6613\u5bb9\u4e39\uff0c\u90a3\u4e9b\u7a81\u53a5\u6b66\u58eb\u53c8 \u662f\u600e\u6837\u8ba4\u51fa\u5979\u7684\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\u6211\u6df7\u6742\u5728\u884c\u4eba\u5806\u4e2d\uff0c\u5979\u90a3\u4e00\u58f0\u60ca\u53eb\uff0c\u5f15\u8d77\u4e86\u90a3\u73ed\u7a81\u53a5\u6b66\u58eb \u7684\u6ce8\u610f\uff0c\u5176\u4e2d\u6709\u83e9\u63d0\u4e0a\u4eba\u548c\u6070\u514b\u56fe\uff0c\u7acb\u523b\u8ba4\u51fa\u6211\u6765\u3002\u5176\u5b9e\u6211\u649e\u89c1\u4e86\u957f\u5b59\u58c1\u88ab\u4ed6\u4eec\u64d2\u83b7\uff0c \u5373\u7b97\u4ed6\u4eec\u8ba4\u4e0d\u51fa\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u7f6e\u4e4b\u4e0d\u7406\u7684\u3002\u5f53\u4e0b\u6df7\u6218\u4e00\u573a\uff0c\u4ed6\u4eec\u9664\u4e86\u83e9\u63d0\u4e0a\u4eba\u4e4b\u5916\uff0c\u8fd8 \u6709\u597d\u51e0\u4e2a\u786c\u624b\uff0c\u6211\u5be1\u4e0d\u654c\u4f17\uff0c\u53ea\u597d\u9003\u51fa\u6765\u62a5\u8baf\uff0c\u6070\u514b\u56fe\u8fd9\u53ae\u7684\u6c14\u529b\u786e\u662f\u60ca\u4eba\uff0c\u6211\u9003\u51fa\u5341 \u6570\u4e08\u8fdc\uff0c\u8fd8\u7ed9\u4ed6\u5c04\u4e2d\u4e86\u4e00\u7bad\u3002\u201d \u3000\u3000\u539f\u6765\u957f\u5b59\u58c1\u65e9\u5df2\u5230\u4e86\u7a81\u53a5\u4eac\u90fd\uff0c\u8fd9\u65e5\u7a81\u53a5\u5927\u6c57\u5728\u5bab\u5ef7\u62db\u5bb4\u5404\u5904\u6295\u5954\u6765\u7684\u6b66\u58eb\uff0c\u53d1\u751f \u4e86\u7b26\u4e0d\u7591\u3001\u590f\u4faf\u575a\u3001\u8c37\u795e\u7fc1\u4e09\u8001\u5927\u95f9\u7687\u5bab\uff0c\u4ee5\u53ca\u674e\u9038\u88ab\u6355\u7b49\u7b49\u4e8b\u60c5\uff0c\u6b66\u58eb\u5927\u4f1a\u6563\u540e\uff0c\u8fd9 \u4e9b\u4e8b\u60c5\u9a6c\u4e0a\u5c31\u4f20\u626c\u5f00\u53bb\uff0c\u957f\u5b59\u58c1\u542c\u8bf4\u5728\u4f1a\u4e2d\u63d0\u83b7\u4e86\u4e00\u4e2a\u5927\u5510\u7684\u738b\u5b50\uff08\u674e\u9038\u672c\u662f\u738b\u5b59\u8eab\u4efd\uff0c \u4f46\u90a3\u4e9b\u6b66\u58eb\u4e0d\u77e5\u5e95\u7ec6\uff0c\u628a\u4ed6\u8bf4\u6210\u4e86\u662f\u5927\u5510\u738b\u5b50\u3002\uff09\u5927\u5927\u5403\u60ca\uff0c\u6025\u5fd9\u8dd1\u51fa\u53bb\u6253\u542c\uff0c\u5979\u626e\u6210 \u4e00\u4e2a\u666e\u901a\u7684\u7ef4\u65cf\u5987\u4eba\uff0c\u672c\u6765\u662f\u4e0d\u5bb9\u6613\u7ed9\u4eba\u77a7\u7834\u7684\uff0c\u5374\u4e0d\u6599\u65e0\u5de7\u4e0d\u5de7\uff0c\u5979\u78b0\u5230\u4e86\u4e24\u4e2a\u8ba4\u8bc6 \u5979\u7684\u4eba\uff0c\u8fd9\u4e24\u4e2a\u4eba\u4e00\u4e2a\u662f\u7a0b\u8fbe\u82cf\u7684\u513f\u5b50\u7a0b\u5efa\u7537\uff0c\u53e6\u4e00\u4e2a\u5219\u662f\u4f0f\u864e\u5e2e\u4e2d\u7684\u5c0f\u5934\u76ee\u6768\u521b\u3002 \u3000\u3000\u516b\u5e74\u4e4b\u524d\uff0c\u674e\u9038\u62a4\u9001\u957f\u5b59\u5747\u91cf\u7684\u7075\u8f66\u51fa\u5173\uff0c\u9014\u4e2d\u957f\u5b59\u58c1\u5fe7\u4f24\u6210\u75c5\uff0c\u66fe\u5728\u4e00\u5ea7\u77f3\u5e99\u517b \u75c5\uff0c\u5e99\u4e2d\u6709\u4e00\u4e2a\u70e7\u706b\u7684\u5c0f\u548c\u5c1a\u540d\u53eb\u201c\u53bb\u5b7d\u201d\uff0c\u539f\u662f\u4f0f\u864e\u5e2e\u7684\u5506\u5580\uff0c\u5f97\u5e99\u4e2d\u7684\u8001\u4e3b\u6301\u6536\u517b \u7684\uff0c\u4ed6\u89ca\u89ce\u674e\u9038\u7684\u94b1\u7269\u548c\u5b9d\u5251\uff0c\u6697\u5730\u91cc\u5411\u4f0f\u864e\u5e2e\u901a\u98ce\u62a5\u8baf\uff0c\u5c11\u5e2e\u4e3b\u7a0b\u5efa\u7537\u540e\u6765\u5e26\u4e86\u8d3c\u515a \u524d\u6765\u62a2\u52ab\uff0c\u88ab\u674e\u9038\u6740\u9000\uff0c\u4e8b\u867d\u4e0d\u6210\uff0c\u4f46\u7a0b\u5efa\u7537\u3001\u957f\u5b59\u58c1\u5df2\u7ecf\u89c1\u8fc7\u9762\uff0c\u800c\u90a3\u4e2a\u5c0f\u548c\u5c1a\u91cd\u5f52 \u4f0f\u864e\u5e2e\u4e4b\u540e\uff0c\u4e5f\u88ab\u63d0\u5347\u4e3a\u5934\u76ee\u3002\u8fd9\u4e2a\u5f53\u5e74\u7684\u5c0f\u548c\u5c1a\u201c\u53bb\u5b7d\u201d\u4fbf\u662f\u73b0\u5728\u7684\u4f0f\u864e\u5e2e\u5934\u76ee\u6768\u521b\u3002 \u3000\u3000\u4f0f\u864e\u5e2e\u7684\u8001\u5e2e\u4e3b\u7a0b\u8fbe\u82cf\u5148\u5230\u7a81\u53a5\u738b\u5ef7\uff0c\u53c2\u52a0\u6b66\u58eb\u5927\u4f1a\uff0c\u7a0b\u5efa\u7537\u5b89\u6392\u597d\u5e2e\u4e2d\u4e8b\u52a1\u4e4b\u540e\uff0c \u5e26\u4e86\u6768\u521b\u4e5f\u8ddf\u7740\u5230\u6765\uff0c\u6070\u597d\u78b0\u89c1\u4e86\u957f\u5b59\u58c1\u5411\u4eba\u6253\u63a2\u674e\u9038\u7684\u6d88\u606f\u3002 \u3000\u3000\u8fd9\u4e2a\u4f0f\u864e\u5e2e\u7684\u5c0f\u5934\u76ee\u6768\u521b\uff0c\u6b66\u529f\u867d\u7136\u4e0d\u9ad8\uff0c\u4eba\u5374\u673a\u7075\u5f97\u5f88\uff0c\u4ed6\u89c1\u8fc7\u4e00\u9762\u7684\u4eba\uff0c\u5f88\u4e45 \u90fd\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u957f\u5b59\u58c1\u867d\u7136\u6539\u5bb9\u6613\u8c8c\uff0c\u626e\u6210\u4e86\u4e00\u4e2a\u7ef4\u65cf\u5f52\u4eba\u6a21\u6837\uff0c\u4ecd\u7136\u5f15\u8d77\u4e86\u4ed6\u7684\u7591\u5fc3\uff0c \u518d\u542c\u957f\u5b59\u58c1\u6253\u63a2\u7684\u4e43\u662f\u4ec0\u4e48\u201c\u5927\u5510\u738b\u5b50\u201d\u88ab\u64d2\u7684\u4e8b\u60c5\uff0c\u5fc3\u91cc\u66f4\u89c9\u7591\u60d1\u4e86\uff0c\u4ed6\u770b\u4e86\u597d\u4e00\u4f1a\uff0c \u7a81\u7136\u5728\u5979\u80cc\u540e\u7528\u6c49\u8bed\u53eb\u4e86\u4e00\u58f0\u201c\u957f\u5b59\u58c1\u201d\uff01\u957f\u5b59\u58c1\u84e6\u5403\u4e00\u60ca\uff0c\u4e0d\u81ea\u89c9\u7684\u7528\u6c49\u8bed\u56de\u4e86\u4e00\u53e5 \u201c\u662f\u8c01\u53eb\u6211\uff1f\u201d\u5c31\u662f\u8fd9\u6837\uff0c\u957f\u5b59\u58c1\u7684\u884c\u85cf\u7ed9\u4eba\u8bc6\u7834\uff0c\u7a0b\u5efa\u7537\u7f20\u7740\u5979\uff0c\u6768\u521b\u8dd1\u56de\u53bb\u62a5\u8baf\uff0c \u7ec8\u4e8e\u5f15\u6765\u83e9\u63d0\u4e0a\u4eba\u3001\u6070\u514b\u56fe\u7b49\u4e00\u73ed\u7a81\u53a5\u6b66\u58eb\uff0c\u5c06\u957f\u5b59\u58c1\u6349\u4f4f\u3002 \u3000\u3000\u957f\u5b59\u58c1\u88ab\u64d2\u7684\u8be6\u7ec6\u7ecf\u8fc7\uff0c\u674e\u9038\u5f53\u7136\u4e0d\u77e5\uff0c\u4f46\u8c37\u795e\u7fc1\u4eb2\u773c\u89c1\u5979\u88ab\u62bc\u4e0a\u56da\u8f66\uff0c\u4e8b\u60c5\u5f53\u7136 \u662f\u65e0\u53ef\u7f6e\u7591\u7684\u4e86\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u8d24\u4f84\u653e\u5fc3\uff0c\u6709\u6211\u4eec\u5728\u6b64\uff0c\u600e\u4e48\u6837\u4e5f\u8981\u5c06\u957f\u5b59\u58c1\u6551\u51fa\u6765\u3002\u201d\u7b26\u4e0d\u7591\u3001\u590f \u4faf\u575a\u90fd\u662f\u957f\u5b59\u5747\u91cf\u7684\u751f\u524d\u597d\u53cb\uff0c\u6545\u53cb\u7684\u5973\u513f\u8eab\u906d\u5371\u96be\uff0c\u62ef\u6551\u4e4b\u8d23\uff0c\u5f53\u7136\u4e5f\u662f\u4e49\u4e0d\u5bb9\u8f9e\u3002 \u3000\u3000\u4f46\u8981\u6551\u957f\u5b59\u58c1\u5374\u662f\u8c08\u4f55\u5bb9\u6613\uff0c\u7b2c\u4e00\uff0c\u4e0d\u77e5\u5979\u56da\u5728\u4ec0\u4e48\u5730\u65b9\u3002\u7b2c\u4e8c\u3001\u5927\u6c57\u7684\u738b\u5bab\u7ecf\u8fc7 \u4e86\u8fd9\u4e00\u573a\u5927\u95f9\u4e4b\u540e\uff0c\u5fc5\u7136\u9632\u8303\u68ee\u4e25\uff0c\u5bf9\u65b9\u867d\u7136\u6298\u4e86\u5929\u6076\u9053\u4eba\u3001\u4f46\u8fd8\u6709\u83e9\u63d0\u4e0a\u4eba\u3001\u9ebb\u7ffc\u8d5e \u7b49\u9ad8\u624b\uff0c\u8fd8\u6709\u5404\u5730\u6295\u5954\u6765\u7684\u6b66\u58eb\uff0c\u5176\u4e2d\u4e16\u5927\u6709\u80fd\u4eba\u3002\u800c\u4e14\u767e\u4f18\u4e0a\u4eba\u795e\u529f\u65e0\u654c\uff0c\u81ea\u5df1\u8fd9\u65b9\uff0c \u590f\u4faf\u575a\u8eab\u53d7\u91cd\u4f24\uff0c\u867d\u5f97\u5929\u5c71\u96ea\u83b2\u89e3\u6bd2\uff0c\u4e00\u65f6\u4e4b\u95f4\u529f\u529b\u4e5f\u672a\u80fd\u6062\u590d\uff0c\u8bba\u8d77\u5f3a\u5f31\u4e4b\u52bf\uff0c\u90a3\u662f \u5bf9\u65b9\u5f3a\u5f97\u591a\u4e86\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u7b11\u9053\uff1a\u201c\u7eb5\u662f\u864e\u7a74\u9f99\u6f6d\uff0c\u6211\u8001\u7b26\u4e5f\u518d\u95ef\u5b83\u4e00\u95ef\u3002\u4f9d\u6211\u4e4b\u89c1\uff0c\u7d22\u6027\u8fdb\u5bab\u53bb\u518d\u95f9 \u4e00\u573a\uff0c\u82e5\u80fd\u628a\u5927\u6c57\u64d2\u4e86\uff0c\u4e0d\u6101\u4ed6\u4e0d\u653e\u4eba\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u4e8b\u60c5\u672a\u5fc5\u8fd9\u6837\u987a\u624b\uff0c\u4e0d\u8fc7\uff0c\u65e2 \u7136\u6ca1\u6709\u5176\u4ed6\u529e\u6cd5\uff0c\u4e5f\u53ea\u597d\u8bd5\u5b83\u4e00\u8bd5\u3002\u201d \u3000\u3000\u6b63\u5728\u5546\u8bae\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u5c71\u4e0b\u91d1\u9f13\u96f7\u9e23\uff0c\u539f\u6765\u662f\u6070\u514b\u56fe\u9886\u4e00\u5343\u94c1\u7532\u519b\u8d76\u5230\u3002\u7b26\u4e0d\u7591\u9053\uff1a \u201c\u54b1\u4eec\u53ea\u597d\u51b2\u51fa\u53bb\u518d\u8bf4\u4e86\u3002\u201d\u674e\u9038\u7684\u5b69\u5b50\u501a\u504e\u5728\u6b66\u7384\u971c\u8eab\u8fb9\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u654f\u513f\uff0c\u4f60\u5bb3 \u6015\u5417\uff1f\u201d\u674e\u5e0c\u654f\u4ef0\u7740\u8138\u513f\u8bf4\u9053\uff1a\u201c\u6709\u59d1\u59d1\u5728\u6b64\uff0c\u654f\u513f\u4e00\u70b9\u4e5f\u4e0d\u5bb3\u6015\uff01\u201d \u3000\u3000\u590f\u4faf\u575a\u7b11\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5bf9\u4f60\u5012\u662f\u5341\u5206\u4fe1\u8d56\uff0c\u4f60\u5e26\u5979\u4e0b\u53bb\u5427\u3002\u8001\u7b26\uff0c\u4f60\u505a\u6211\u7684\u4fdd\u9556\u3002\u201d \u7b26\u4e0d\u7591\u542c\u4ed6\u4e00\u8bf4\uff0c\u5df2\u77e5\u5176\u610f\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7a81\u53a5\u519b\u961f\u4eba\u6570\u4f17\u591a\uff0c\u800c\u4e14\u90fd\u62ab\u7740\u94c1\u7532\uff0c \u54b1\u4eec\u82e5\u662f\u805a\u5728\u4e00\u8d77\uff0c\u53ea\u6015\u96be\u4ee5\u7a81\u56f4\uff0c\u4e0d\u5982\u5206\u6210\u51e0\u8def\uff0c\u6559\u4ed6\u4eec\u987e\u6b64\u5931\u5f7c\u3002\u201d\u5f53\u4e0b\u5206\u6210\u4e09\u8def\uff0c \u6b66\u7384\u971c\u5e26\u4e86\u5b69\u5b50\u4e0e\u88f4\u53d4\u5ea6\u505a\u4e00\u8def\uff0c\u4ece\u6765\u9762\u4e0b\u5c71\uff0c\u7b26\u4e0d\u7591\u4e0e\u590f\u4faf\u575a\u4e00\u8def\uff0c\u4ece\u5357\u9762\u4e0b\u5c71\uff0c\u8c37 \u795e\u7fc1\u4e0e\u674e\u9038\u4ece\u897f\u9762\u4e0b\u5c71\u3002\u590f\u4faf\u575a\u4e0e\u674e\u9038\u90fd\u8d1f\u4e86\u4f24\uff0c\u6240\u4ee5\u8981\u4eba\u63a9\u62a4\u3002 \u3000\u3000\u8fd9\u65f6\u94c1\u7532\u519b\u6b63\u5728\u5411\u5c71\u4e0a\u63a8\u8fdb\uff0c\u6070\u514b\u56fe\u76ee\u529b\u751a\u597d\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u5df2\u77a7\u89c1\u4e86\u5c71\u5934\u4e0a\u7684\u7b26\u4e0d \u7591\u3001\u8c37\u795e\u7fc1\u3001\u590f\u4faf\u575a\u7b49\u4eba\uff0c\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u65e9\u77e5\u8fd9\u51e0\u4e2a\u8001\u5bb6\u4f19\u5728\u6b64\uff0c\u6211\u5e94\u8be5 \u591a\u8bf7\u51e0\u4e2a\u9ad8\u624b\u524d\u6765\uff0c\u54a6\uff0c\u767e\u5fe7\u4e0a\u4eba\u548c\u5929\u6076\u9053\u4eba\u90a3\u91cc\u53bb\u4e86\uff1f\u96be\u9053\u4ed6\u4eec\u8fd8\u6ca1\u6709\u53d1\u73b0\u654c\u8e2a\uff1f\u201d \u4ed6\u4e0d\u77e5\u9053\uff0c\u767e\u4f18\u4e0a\u4eba\u65e9\u5df2\u94f1\u7fbd\u800c\u5f52\uff0c\u5929\u6076\u9053\u4eba\u4e5f\u5df2\u5f52\u897f\u3002\u767e\u4f18\u4e0a\u4eba\u867d\u7136\u6599\u5230\u94c1\u7532\u519b\u968f\u540e \u4f1a\u6765\uff0c\u4f46\u4ed6\u662f\u6b66\u5b66\u5927\u5e08\u7684\u8eab\u4efd\uff0c\u8bb2\u7a76\u5355\u6253\u72ec\u6597\uff0c\u88ab\u4eba\u6253\u8d25\u4e4b\u540e\uff0c\u82e5\u518d\u631f\u4f17\u91cd\u6765\uff0c\u5c31\u662f\u6709 \u5931\u8eab\u4efd\u4e86\u3002 \u3000\u3000\u6070\u514b\u56fe\u77e5\u9053\u7b26\u4e0d\u7591\u7b49\u4eba\u7684\u5389\u5bb3\uff0c\u4e0d\u6562\u5192\u8fdb\uff0c\u4e0b\u4ee4\u5c06\u94c1\u7532\u519b\u6446\u6210\u6247\u5f62\u9635\u52bf\uff0c\u7f13\u7f13\u5411\u5c71 \u4e0a\u63a8\u8fdb\u3002\u4e0a\u9762\u4e00\u58f0\u957f\u7b11\uff0c\u7b26\u4e0d\u7591\u4e0e\u590f\u4faf\u575a\u5148\u884c\u51b2\u4e0b\u6765\uff0c\u8fd9\u73ed\u7a81\u53a5\u519b\u867d\u6709\u94c1\u7532\u5934\u76d4\uff0c\u4e5f\u88ab \u7b11\u58f0\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002\u6070\u514b\u56fe\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u6325\u519b\u62b5\u5fa1\uff0c\u5ffd\u542c\u5f97\u8c37\u795e\u7fc1\u4e00\u58f0\u5927\u559d\uff0c\u624b \u821e\u53cc\u5251\uff0c\u4e5f\u51b2\u4e86\u4e0b\u6765\u3002\u6070\u514b\u56fe\u773c\u5149\u4e00\u77a5\uff0c\u77a7\u89c1\u8ddf\u5728\u8c37\u795e\u7fc1\u8eab\u540e\u7684\u6b63\u662f\u674e\u9038\uff0c\u6025\u5fd9\u53eb\u9053\uff1a \u201c\u6b63\u70b9\u513f\u5728\u8fd9\u4e00\u8fb9\uff0c\u8fd9\u5c0f\u4f19\u5b50\u662f\u54b1\u4eec\u5927\u6c57\u6240\u8981\u7684\u4eba\uff0c\u5b81\u53ef\u653e\u8fc7\u4e86\u90a3\u51e0\u4e2a\u8001\u5934\uff0c\u4e0d\u53ef\u653e\u8fc7 \u4e86\u4ed6\uff01\u201d\u4e00\u9a6c\u5f53\u5148\uff0c\u8f6c\u52a8\u9635\u5f62\uff0c\u4eb2\u53bb\u6349\u674e\u9038\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u6b66\u7384\u971c\u5e26\u4e86\u5b69\u5b50\uff0c\u4e0e\u88f4\u53d4\u5ea6\u4e00 \u8d77\uff0c\u4ece\u9632\u5fa1\u6700\u5f31\u7684\u897f\u65b9\uff0c\u6084\u6ca1\u58f0\u7684\u75be\u9a70\u800c\u4e0b\u3002 \u3000\u3000\u6070\u514b\u56fe\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u7a81\u7136\u77a7\u89c1\u6b66\u7384\u971c\u51b2\u5165\u9635\u4e2d\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u51e0\u4e4e \u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u6211\u662f\u5728\u505a\u767d\u65e5\u68a6\uff0c\u8fd9\uff0c\u8fd9\u4e0d\u662f\u65b0\u738b\u5983\u5417\uff1f\u201d\u8981\u77e5\u6b66 \u7384\u971c\u867d\u7136\u5728\u5bab\u4e2d\u5927\u95f9\uff0c\u4f46\u5f53\u65f6\u6070\u514b\u56f9\u5728\u5fa1\u82b1\u56ed\u5de1\u903b\uff0c\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u540e\u6765\u674e\u9038\u4e0e\u6b66 \u7384\u971c\u4ece\u82b1\u56ed\u9003\u51fa\uff0c\u5f53\u65f6\u662f\u5728\u660f\u591c\u4e4b\u4e2d\uff0c\u6811\u6728\u6d53\u836b\uff0c\u800c\u6070\u514b\u56fe\u53c8\u53ea\u6ce8\u610f\u674e\u9038\uff0c\u53ea\u5f53\u5979\u662f\u4e2a \u666e\u901a\u5bab\u5973\uff0c\u4ee5\u81f4\u88ab\u5979\u51b7\u4e0d\u9632\u7684\u4e00\u638c\u51fb\u5012\uff0c\u5374\u8fd8\u672a\u770b\u6e05\u695a\u5979\u7684\u9762\u8c8c\u3002 \u3000\u3000\u6070\u514b\u56fe\u5e26\u9886\u7684\u4e00\u5343\u540d\u94c1\u7532\u519b\uff0c\u6bcf\u4e09\u4e2a\u4eba\u4e2d\u5c31\u6709\u4e00\u4eba\u62ff\u7740\u677e\u679d\u706b\u628a\uff0c\u7167\u8000\u5f97\u5982\u540c\u767d\u663c\uff0c \u6b66\u7384\u971c\u4ece\u5c71\u4e0a\u75be\u9a70\u800c\u4e0b\uff0c\u6070\u514b\u56fe\u81ea\u662f\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5927\u60ca\u4e4b\u540e\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u65b0\u738b\u5983\u600e \u4e48\u4f1a\u4e0e\u4ed6\u4eec\u4e00\u8def\uff1f\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u90fd\u5f97\u95ee\u4e2a\u660e\u767d\u3002\u201d\u8981\u77e5\u674e\u9038\u867d\u7136\u91cd\u8981\uff0c\u4f46\u6070\u514b\u56fe\u77e5\u9053\u5927 \u6c57\u6781\u4e3a\u5ba0\u7231\u201c\u65b0\u738b\u5983\u201d\uff0c\u6bd4\u8f83\u8d77\u6765\uff0c\u90a3\u53c8\u662f\u201c\u65b0\u738b\u5983\u201d\u66f4\u91cd\u8981\u4e86\u3002 \u3000\u3000\u970e\u773c\u4e4b\u95f4\uff0c\u6b66\u7384\u971c\u5df2\u6765\u5230\u9762\u524d\uff0c\u6070\u514b\u56fe\u53eb\u9053\uff1a\u201c\u53ef\u8d3a\u6566\uff0c\u5351\u804c\u606d\u8fce\u51e4\u9a7e\u3002\u201d\u6b66\u7384\u971c \u633a\u5251\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d\u6070\u514b\u56fe\u5446\u4e86\u4e00\u5446\uff0c\u4e0d\u77e5\u662f\u62e6\u963b\u597d\u8fd8\u662f\u4e0d\u62e6\u963b\u597d\uff1f\u4e3b\u610f\u5c1a\u672a\u6253\u5b9a\uff0c \u6b66\u7384\u971c\u500f\u7684\u4fbf\u4ece\u4ed6\u8eab\u8fb9\u63a0\u8fc7\u3002 \u3000\u3000\u6070\u514b\u56fe\u8ba4\u51fa\u5979\u80cc\u7684\u662f\u674e\u9038\u7684\u5b69\u5b50\uff0c\u5927\u53eb\u9053\uff1a\u201c\u8bf7\u53ef\u8d3a\u6566\u7559\u6b65\uff01\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u88f4\u53d4\u5ea6 \u5df2\u5230\uff0c\u559d\u9053\uff1a\u201c\u7ed9\u6211\u95ed\u5634\uff01\u201d\u5251\u631f\u52b2\u98ce\uff0c\u500f\u5730\u5288\u4e0b\uff0c\u6070\u514b\u56fe\u6325\u5200\u6025\u6321\uff0c\u4ed6\u6709\u964d\u72ee\u4f0f\u864e\u4e4b \u80fd\uff0c\u8fd9\u4e00\u5200\u5288\u51fa\uff0c\u8db3\u6709\u5343\u65a4\u4e4b\u529b\uff0c\u6ee1\u4ee5\u4e3a\u53ef\u4ee5\u5c06\u5bf9\u65b9\u7684\u957f\u5251\u9707\u98de\uff0c\u54ea\u6599\u5200\u5251\u76f8\u4ea4\uff0c\u5f53\u7684 \u4e00\u58f0\uff0c\u6070\u514b\u56fe\u7adf\u7136\u6536\u52bf\u4e0d\u4f4f\uff0c\u4f46\u89c9\u5bf9\u65b9\u7684\u957f\u5251\u4f3c\u6709\u4e00\u80a1\u5438\u529b\uff0c\u6070\u514b\u56fe\u6b63\u60f3\u65bd\u5c55\u5343\u65a4\u5760\u7684 \u91cd\u8eab\u6cd5\u7a33\u4f4f\u8eab\u5f62\uff0c\u5ffd\u89c9\u624b\u4e2d\u4e00\u8f7b\uff0c\u90a3\u53e3\u6708\u7259\u5f2f\u5200\u5df2\u98de\u4e0a\u4e86\u534a\u7a7a\uff0c\u539f\u6765\u88f4\u53d4\u5ea6\u77e5\u9053\u4ed6\u795e\u52c7 \u65e0\u6bd4\uff0c\u5728\u5200\u5251\u76f8\u4ea4\u4e4b\u9645\uff0c\u7528\u4e86\u501f\u529b\u6253\u529b\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u4e00\u7c98\u4e00\u5e26\uff0c\u501f\u4e86\u4ed6\u90a3\u80a1\u5f3a\u52b2\u7684\u529b\u9053\uff0c \u5c06\u4ed6\u7684\u5175\u5203\u5f04\u5f97\u98de\u51fa\u624b\u53bb\uff0c\u6070\u514b\u56fe\u5931\u4e86\u5175\u5203\uff0c\u5c1a\u81ea\u83ab\u540d\u5176\u5999\uff0c\u8f6c\u773c\u95f4\u88f4\u53d4\u5ea6\u4e5f\u8fc7\u53bb\u4e86\u3002 \u6070\u514b\u56fe\u5927\u6012\uff0c\u4ece\u5175\u58eb\u624b\u4e2d\u62a2\u8fc7\u4e00\u628a\u786c\u5f13\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53ef\u8d3a\u6566\u53db\u4e86\u5927\u6c57\uff0c\u6211\u8fd8\u987e\u5fcc\u4ec0\u4e48\uff1f\u201d \u4f46\u4ed6\u4ecd\u7136\u4e0d\u6562\u5c04\u6b66\u7384\u971c\uff0c\u8fd9\u4e00\u7bad\u5bf9\u51c6\u4e86\u5979\u6240\u80cc\u7684\u5b69\u5b50\u3002 \u3000\u3000\u6b66\u7384\u971c\u542c\u5f97\u80cc\u540e\u5f13\u5f26\u58f0\u54cd\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u628a\u90a3\u652f\u7fbd\u7bad\u524a\u4e3a\u4e24\u6bb5\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u51e0\u652f\u957f\u77db \u540c\u65f6\u6eaf\u4e86\u8fc7\u6765\uff0c\u6b66\u7384\u971c\u7528\u4e86\u4e00\u62db\u201c\u72c2\u98ce\u626b\u53f6\u201d\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u597d\u51e0\u6839\u77db\u5934 \u7ed9\u5979\u4e00\u5251\u524a\u65ad\uff0c\u4f46\u5176\u4e2d\u6709\u4e2a\u957f\u77db\u624b\u72e1\u733e\u5f97\u5f88\uff0c\u4ed6\u628a\u957f\u77db\u6325\u4e86\u534a\u4e2a\u5f27\u5f62\uff0c\u4e2d\u9014\u53d8\u62db\uff0c\u521a\u597d \u907f\u5f00\u6b66\u7384\u971c\u7684\u4e00\u51fb\uff0c\u8d81\u7740\u6b66\u7384\u971c\u521a\u521a\u524a\u65ad\u90a3\u51e0\u6839\u957f\u77db\uff0c\u672b\u53ca\u56de\u5251\u9632\u62a4\u4e4b\u9645\uff0c\u77db\u5934\u5bf9\u51c6\u4e86 \u90a3\u5b69\u5b50\u7684\u9888\u9879\u4fbf\u523a\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u5728\u540e\u9762\u89c1\u6b64\u60c5\u5f62\uff0c\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u6025\u5fd9\u98de\u8eab\u63a0\u8d77\uff0c\u811a\u5c16\u4e00\u70b9\u4e00\u4e2a\u94c1\u7532\u519b\u7684\u5934 \u76d4\uff0c\u501f\u52bf\u518d\u8dc3\uff0c\u4ed6\u4eba\u5728\u534a\u7a7a\uff0c\u5c1a\u672a\u6251\u4e0b\uff0c\u53ea\u89c1\u90a3\u6839\u957f\u77db\u5df2\u582a\u582a\u523a\u5230\u4e86\u5b69\u5b50\u7684\u9888\u9879\uff0c\u90a3\u5b69 \u5b50\u7a81\u7136\u4f38\u51fa\u53cc\u624b\uff0c\u63e1\u7d27\u957f\u77db\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u88f4\u53d4\u5ea6\u6709\u5982\u98de\u5c06\u4ece\u5929\u800c\u964d\uff0c\u4e00\u5251\u5c06\u90a3\u4e2a\u6b66 \u58eb\u5288\u7ffb\uff01 \u3000\u3000\u674e\u5e0c\u654f\u54a7\u5f00\u53e3\u7b11\u9053\uff1a\u201c\u771f\u597d\u73a9\uff0c\u53d4\u53d4\uff0c\u4f60\u4ece\u534a\u7a7a\u4e2d\u98de\u4e0b\uff0c\u6765\u6253\u4eba\u7684\u6cd5\u5b50\u53ef\u5f97\u6559\u6211\uff01\u201d \u88f4\u53d4\u5ea6\u548c\u6b66\u7384\u971c\u62b9\u4e86\u4e00\u989d\u6c57\uff0c\u8d5e\u9053\uff1a\u201c\u654f\u513f\uff0c\u4f60\u7684\u80c6\u5b50\u771f\u5927\uff01\u201d\u674e\u5e0c\u654f\u7b11\u9053\uff1a\u201c\u6211\u65e9\u8bf4 \u8fc7\uff0c\u6211\u8ddf\u7740\u59d1\u59d1\uff0c\u6211\u5c31\u4e00\u70b9\u4e5f\u4e0d\u5bb3\u6015\uff01\u201d \u3000\u3000\u7a81\u53a5\u6b66\u58eb\u89c1\u4ed6\u4eec\u8fd9\u6837\u5389\u5bb3\uff0c\u800c\u4e14\u53c8\u542c\u5f97\u6070\u514b\u56fe\u79f0\u6b66\u7384\u971c\u4e3a\u201c\u53ef\u8d3a\u6566\u201d\uff0c\u4e0d\u6562\u518d\u8ffd\uff0c \u88f4\u3001\u6b66\u4e8c\u4eba\u4fbf\u5148\u51b2\u51fa\u4e86\u654c\u9635\u3002 \u3000\u3000\u6070\u514b\u56f9\u8f6c\u8fc7\u5934\u6765\u8981\u5305\u56f4\u674e\u9038\uff0c\u8fd9\u65f6\u8c37\u795e\u7fc1\u5c55\u5f00\u4e86\u8e51\u4e91\u5251\u6cd5\uff0c\u4f46\u89c1\u4ed6\u7ffb\u8eab\u8fdb\u5251\uff0c\u98d8\u5ffd \u5982\u98ce\uff0c\u5251\u5230\u8eab\u5230\uff0c\u604d\u998f\u89c1\u5f71\u800c\u4e0d\u89c1\u4eba\uff0c\u5f15\u5f97\u654c\u519b\u8ddf\u7740\u4ed6\u4e71\u7a9c\uff0c\u5374\u6349\u4ed6\u4e0d\u7740\uff0c\u674e\u9038\u7d27\u7d27\u8ddf \u5728\u4ed6\u80cc\u540e\uff0c\u5251\u4e0d\u6cbe\u8840\uff0c\u654c\u519b\u5305\u56f4\u4e4b\u52bf\u672a\u6210\uff0c\u4ed6\u4eec\u4e24\u4eba\u4e5f\u5df2\u51b2\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u548c\u590f\u4faf\u575a\u4e8c\u4eba\u4e00\u8def\uff0c\u95ef\u51fa\u6765\u66f4\u4e3a\u5bb9\u6613\uff0c\u6070\u514b\u56fe\u4ee5\u5927\u90e8\u4efd\u7684\u94c1\u7532\u519b\u53bb\u8ffd\u8f85\u674e\u9038 \u548c\u62e6\u622a\u6b66\u7384\u971c\uff0c\u7b26\u4e0d\u7591\u6b66\u529f\u6700\u9ad8\uff0c\u5374\u4ece\u8870\u5f31\u7684\u4e00\u73af\u51b2\u51fa\uff0c\u81ea\u662f\u4e0d\u5e9f\u5439\u7070\u4e4b\u529b\uff0c\u4ed6\u65bd\u5c55\u5927 \u6454\u7891\u624b\u7684\u529f\u592b\uff0c\u4e00\u6709\u7a81\u53a5\u6b66\u58eb\u8fd1\u8eab\uff0c\u4fbf\u7ed9\u6293\u4e86\u8d77\u6765\uff0c\u629b\u4e0a\u6765\u7a7a\uff0c\u8dcc\u4e2a\u534a\u6b7b\uff0c\u4e00\u8fde\u8dcc\u4e86\u597d \u51e0\u4e2a\uff0c\u5176\u4ed6\u7684\u4eba\u53d1\u4e00\u58f0\u558a\uff0c\u90fd\u56db\u6563\u907f\u5f00\uff0c\u4e0d\u6562\u518d\u8ffd\u4e86\u3002 \u3000\u3000\u534a\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4e09\u8def\u7a81\u56f4\u7684\u4eba\u5df2\u8d70\u51fa\u5341\u6570\u91cc\u5916\uff0c\u5c06\u94c1\u7532\u519b\u8fdc\u8fdc\u7684\u629b\u5728\u540e\u9762\u3002\u4ed6\u4eec\u4f1a \u5408\u4e00\u9f50\uff0c\u91cd\u5546\u6551\u957f\u5b59\u58c1\u4e4b\u7b56\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u9053\uff1a\u201c\u4f9d\u6211\u4e4b\u89c1\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u4eca\u665a\u5c31\u5165\u5bab\u53bb\u63a2\u4e00\u63a2\u6d88\u606f\u201d\u3002\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u54b1 \u4eec\u5b89\u6392\u4e00\u4e0b\uff0c\u54ea\u4e9b\u4eba\u5165\u5bab\uff0c\u54ea\u4e9b\u4eba\u7559\u4e0b\u6765\u63a5\u5e94\u3002\u201d\u7b26\u4e0d\u7591\u9053\uff1a\u201c\u590f\u4faf\u5144\u9700\u8981\u62d4\u6bd2\u7597\u4f24\uff0c \u4ed6\u7559\u4e0b\u6765\u5427\u3002\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u674e\u8d24\u4f84\uff0c\u4f60\u7684\u4f24\u52bf\u5982\u4f55\uff1f\u4e0d\u5982\u4f60\u4e5f\u7559\u4e0b\u6765\u5427\uff0c\u53cd\u6b63\u5f97\u6709\u4eba \u7167\u987e\u4f60\u7684\u5b69\u5b50\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u4f24\u5f97\u4e0d\u91cd\uff0c\u58c1\u59b9\u4e3a\u6211\u800c\u6765\uff0c\u6211\u5c82\u80fd\u8896\u624b\u65c1\u89c2\u3002\u201d\u4f17\u4eba\u89c1 \u4ed6\u4e00\u5f80\u60c5\u6df1\uff0c\u4fbf\u4e0d\u518d\u52c9\u5f3a\u4ed6\u7559\u4e0b\u3002 \u3000\u3000\u674e\u9038\u60f3\u4e86\u4e00\u60f3\uff0c\u5230\u6b66\u7384\u971c\u9762\u524d\u8bf4\u9053\uff1a\u201c\u7384\u971c\u59d0\u59d0\uff0c\u6211\u6c42\u4f60\u2014\u4ef6\u4e8b\u60c5\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a \u201c\u4f60\u8bf4\u5427\uff0c\u4e3a\u4e86\u58c1\u59b9\uff0c\u6211\u8d74\u6c64\u8e48\u706b\uff0c\u5728\u6240\u4e0d\u8f9e\u3002\u201d\u674e\u9038\u9053\uff1b\u201c\u6211\u77e5\u9053\u4f60\u660e\u5929\u4e00\u65e9\u4fbf\u8981\u56de \u56fd\uff0c\u73b0\u5728\u8bf7\u4f60\u803d\u6401\u4e9b\u65f6\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u8fd9\u4f55\u987b\u8bf4\uff1f\u58c1\u59b9\u672a\u6551\u51fa\u6765\uff0c\u6211\u4e0d\u56de\u53bb\u4fbf\u662f\u3002\u201d \u674e\u9038\u9053\uff1a\u201c\u90a3\u4e48\u654f\u513f\u5c31\u6258\u4f60\u7167\u987e\u4e86\u3002\u654f\u513f\uff0c\u4f60\u548c\u59d1\u59d1\u4e00\u8d77\uff0c\u6211\u53bb\u5c06\u4f60\u7684\u5988\u5988\u63a5\u6765\uff0c\u597d\u5417\uff1f\u201d \u674e\u5e0c\u654f\u9053\uff1a\u201c\u5988\u5988\u4e5f\u7ed9\u90a3\u4e2a\u4ec0\u4e48\u5927\u6c57\u6349\u53bb\u4e86\uff0c\u662f\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u662f\u7684\uff0c\u4f46\u6709\u8fd9\u8bb8\u591a\u4f4d \u516c\u516c\u548c\u6211\u540c\u53bb\uff0c\u4e00\u5b9a\u80fd\u591f\u5c06\u5979\u6551\u51fa\u6765\u7684\u3002\u201d\u674e\u5e0c\u654f\u9053\uff1a\u201c\u597d\u5440\uff0c\u59d1\u59d1\uff0c\u90a3\u4e48\u4f60\u4e0d\u8d70\u4e86\uff1f \u5f85\u63a5\u4e86\u6211\u7684\u5988\u5988\u51fa\u6765\uff0c\u54b1\u4eec\u4f4f\u5728\u4e00\u5904\u597d\u4e0d\u597d\uff1f\u201d\u8fd9\u5b69\u5b50\u4f69\u670d\u4ed6\u7684\u7236\u4eb2\uff0c\u5fc3\u60f3\u7239\u7239\u8bf4\u8fc7\u53ef \u4ee5\u5c06\u5988\u5988\u63a5\u6765\uff0c\u90a3\u5c31\u4e00\u5b9a\u80fd\u591f\u6765\u4e86\uff0c\u6240\u4ee5\u4ed6\u73b0\u5728\u5012\u4e0d\u662f\u62c5\u5fc3\u5988\u5988\u4e0d\u80fd\u6551\u51fa\u6765\uff0c\u800c\u662f\u62c5\u5fc3 \u6b66\u7384\u971c\u8981\u8d70\u3002 \u3000\u3000\u6b66\u7384\u971c\u5fc3\u60c5\u6c89\u91cd\u4e4b\u6781\uff0c\u5979\u672c\u6765\u662f\u60f3\u548c\u674e\u9038\u540c\u53bb\u6551\u4eba\u7684\uff0c\u4f46\u60f3\u5230\u957f\u5b59\u58c1\u90a3\u5992\u6068\u7684\u773c\u5149\uff0c \u82e5\u662f\u7ed9\u5979\u89c1\u5230\u81ea\u5df1\u548c\u674e\u9038\u5728\u4e00\u8d77\uff0c\u4e0d\u77e5\u5979\u53c8\u4f1a\u600e\u6837\u60f3\u5462\uff01\u73b0\u5728\u674e\u9038\u8981\u5979\u7559\u4e0b\u6765\u7167\u987e\u5b69\u5b50\uff0c \u5979\u6700\u521d\u4e0d\u5927\u613f\u610f\uff0c\u7ec8\u4e8e\u4e5f\u540c\u610f\u4e86\u3002\u5fc3\u4e2d\u5219\u5728\u6697\u6697\u51b3\u5b9a\uff0c\u7b49\u5230\u957f\u5b59\u58c1\u6551\u51fa\u6765\uff0c\u5979\u4fbf\u7acb\u5373\u56de \u56fd\uff0c\u6700\u597d\u907f\u514d\u548c\u5979\u89c1\u9762\u3002 \u3000\u3000\u5927\u5bb6\u5546\u91cf\u59a5\u5f53\uff0c\u7559\u4e0b\u590f\u4faf\u575a\u548c\u6b66\u7384\u971c\u4e8c\u4eba\uff0c\u7ea6\u5b9a\u4ee5\u5c71\u4e0b\u7684\u4e00\u5ea7\u72ee\u5f62\u77f3\u7a9f\u4f5c\u4e3a\u805a\u96c6\u4e4b \u5904\u3002\u6b66\u7384\u971c\u51ed\u8bb0\u5fc6\u6240\u5f97\uff0c\u753b\u4e86\u4e00\u5f20\u7a81\u53a5\u738b\u5bab\u7684\u8349\u56fe\uff0c\u590f\u4faf\u575a\u4e5f\u5c06\u6613\u5bb9\u4e39\u5206\u7ed9\u6bcf\u4eba\u4e24\u9897\uff0c \u51c6\u5907\u5e94\u6025\u7528\u3002\u5e76\u7ed9\u674e\u9038\u541e\u670d\u4e86\u4e00\u7c92\u57f9\u5143\u56fa\u672c\u7684\u5927\u8fd8\u4e39\uff0c\u5404\u81ea\u5206\u5934\u529e\u4e8b\u3002 \u3000\u3000\u674e\u9038\u4e0e\u7b26\u4e0d\u7591\u3001\u8c37\u795e\u7fc1\u4ed6\u4eec\u4e00\u9053\u4ece\u5c0f\u8def\u518d\u5f80\u7a81\u53a5\u7684\u738b\u5ef7\uff0c\u4ed6\u9065\u671b\u6b66\u7384\u971c\u80cc\u7740\u4ed6\u7684\u5b69 \u5b50\u4e0e\u590f\u4faf\u575a\u4e00\u9053\u4e0a\u5c71\uff0c\u5fc3\u4e2d\u611f\u6168\u4e07\u7aef\uff0c\u60f3\u4e0d\u5230\u6614\u65e5\u7684\u201c\u4ec7\u4eba\u201d\uff0c\u800c\u4eca\u7adf\u6210\u4e3a\u81ea\u5df1\u6258\u59bb\u5bc4 \u5b50\u7684\u77e5\u5df1\u3002 \u3000\u3000\u5f85\u5230\u5165\u9ed1\u4e4b\u540e\uff0c\u7b26\u4e0d\u7591\u3001\u8c37\u795e\u7fc1\u3001\u88f4\u53d4\u5ea6\u3001\u674e\u9038\u7b49\u4e00\u884c\u56db\u4eba\uff0c\u4fbf\u6f5c\u5165\u7a81\u53a5\u7684\u4eac\u57ce\u3002 \u4eac\u57ce\u867d\u7136\u5230\u5904\u6709\u4eba\u628a\u5b88\uff0c\u4f46\u4ed6\u4eec\u8f7b\u529f\u5353\u7edd\uff0c\u52a0\u4ee5\u53c8\u662f\u5728\u660f\u591c\u4e4b\u4e2d\uff0c\u5b88\u57ce\u7684\u5175\u58eb\u7adf\u65e0\u4e00\u4eba \u53d1\u89c9\u3002\u4e0d\u4f46\u5982\u6b64\uff0c\u7b26\u4e0d\u7591\u548c\u8c37\u795e\u7fc1\u8fd8\u7528\u6885\u82b1\u9488\u6253\u7a74\u7684\u529f\u592b\uff0c\u5404\u81ea\u6349\u4e86\u4e24\u4e2a\u5175\u58eb\uff0c\u5265\u4e86\u4ed6 \u7684\u8863\u670d\uff0c\u4ed7\u7740\u590f\u4faf\u575a\u7684\u6613\u5bb9\u4e39\u3002\u8fd9\u56db\u4e2a\u4eba\u90fd\u626e\u6210\u4e86\u7a81\u53a5\u6b66\u58eb\u7684\u6a21\u6837\uff0c\u4e00\u76f4\u6df1\u5165\u5230\u738b\u5bab\u7684 \u7981\u533a\u3002 \u3000\u3000\u738b\u5bab\u7684\u5fa1\u82d1\u501a\u5c71\u4fee\u5efa\uff0c\u8c37\u795e\u7fc1\u8f7b\u529f\u6700\u597d\uff0c\u6545\u610f\u53d1\u51fa\u4e00\u652f\u54cd\u7bad\uff0c\u5f15\u5f97\u597d\u4e9b\u536b\u58eb\u5954\u4e0a\u5c71 \u6765\u641c\u67e5\u3002\u674e\u9038\u548c\u7b26\u4e0d\u7591\u4fbf\u8d81\u4ed6\u4eec\u614c\u4e71\u4e4b\u65f6\uff0c\u5077\u5077\u7684\u8fdb\u4e86\u5fa1\u82b1\u56ed\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4eba\u5f71\u5e62\u5e62\uff0c \u4ed6\u4eec\u7a7f\u7684\u53c8\u662f\u7a81\u53a5\u6b66\u58eb\u7684\u670d\u9970\uff0c\u5b88\u536b\u7684\u53ea\u5f53\u4ed6\u4eec\u662f\u81ea\u5df1\u4eba\uff0c\u4e00\u4e0b\u5b50\u4fbf\u7ed9\u4ed6\u4eec\u6df7\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5f53\u7136\u4e5f\u77e5\u9053\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\u90fd\u5728\u5bab\u4e2d\uff0c\u8d8a\u6df1\u5165\u5371\u9669\u8d8a\u5927\uff0c\u81ea\u662f\u4e0d\u6562\u6709\u4e1d\u6beb\u7684\u5927\u610f\uff0c \u4e00\u8fdb\u4e86\u5fa1\u82b1\u56ed\u4fbf\u7acb\u5373\u5206\u5f00\uff0c\u85c9\u7740\u6811\u6728\u5c71\u77f3\u7684\u63a9\u853d\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u63a2\u7d22\u524d\u884c\u3002 \u3000\u3000\u674e\u9038\u6b63\u5728\u884c\u8d70\u4e4b\u9645\uff0c\u5ffd\u89c1\u6709\u4e24\u76cf\u7ea2\u7eb1\u706f\u7b3c\u8fce\u9762\u800c\u6765\u3002\u674e\u9038\u8eb2\u5728\u5047\u5c71\u77f3\u540e\uff0c\u5b9a\u775b\u4e00\u77a7\uff0c \u5374\u539f\u6765\u662f\u4e24\u4e2a\u5bab\u5973\uff0c\u4e00\u624b\u63d0\u7740\u706f\u7b3c\uff0c\u4e00\u624b\u63d0\u7740\u7bee\u5b50\uff0c\u7bee\u5b50\u5185\u4f3c\u4e4e\u653e\u7740\u8336\u76c5\u4e4b\u7c7b\u7684\u5668\u76bf\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u5bab\u5973\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u90a3\u4e2a\u65b0\u738b\u5983\u7adf\u662f\u4e2d\u56fd\u7684\u5973\u7687\u5e1d\u6d3e\u6765\u7684\uff0c\u771f\u662f\u5947\u602a\uff01\u201d\u53e6 \u4e00\u4e2a\u5bab\u5973\u9053\uff1a\u201c\u542c\u8bf4\u8fd8\u5927\u95f9\u4e86\u4e00\u573a\u5462\uff0c\u5927\u6c57\u53d7\u4e86\u60ca\u5413\uff0c\u6240\u4ee5\u624d\u8981\u559d\u8fd9\u53c2\u6c64\u3002\u54ce\u5440\uff0c\u4f60\u5c0f \u5fc3\u4e9b\u8d70\uff0c\u4e0d\u8981\u78b0\u8dcc\u4e86\uff0c\u8fd9\u4eba\u53c2\u662f\u6e24\u6d77\u738b\u56fd\u8fdb\u8d21\u6765\u7684\uff0c\u73cd\u8d35\u65e0\u6bd4\uff0c\u4f60\u6cfc\u6cfb\u4e86\u53c2\u6c64\uff0c\u6027\u547d\u4e5f \u8d54\u4e0d\u8d77\uff01\u201d\u6562\u60c5\u662f\u8d70\u5728\u524d\u9762\u7684\u90a3\u4e2a\u5bab\u5973\u8e22\u7740\u4e86\u77f3\u5b50\uff0c\u8e0f\u5dee\u4e86\u4e00\u6b65\uff0c\u6240\u4ee5\u540e\u9762\u90a3\u4e2a\u5bab\u5973\u51fa \u8a00\u8b66\u544a\u3002 \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u90a3\u4e24\u4e2a\u5bab\u5973\u6b63\u597d\u6765\u5230\u5047\u5c71\u77f3\u540e\uff0c\u674e\u9038\u85cf\u597d\u8eab\u8eaf\uff0c\u53ea\u542c\u5f97\u524d\u5934\u90a3\u4e2a\u5b98\u5973\u8bf4 \u9053\uff1a\u201c\u8d70\u5f97\u7d2f\u4e86\uff0c\u6b47\u4e00\u4f1a\u5427\uff0c\u53cd\u6b63\u5927\u6c57\u53ea\u6015\u4e5f\u8fd8\u6ca1\u6709\u7761\u9192\u3002\u201d\u540e\u9762\u90a3\u4e2a\u5bab\u5973\u9053\uff1a\u201c\u4e0d\u6210\uff0c \u5927\u6c57\u5429\u5490\u4e09\u66f4\u65f6\u5206\u9001\u5230\u7684\uff0c\u73b0\u5728\u5feb\u5230\u65f6\u523b\u4e86\uff0c\u5b81\u53ef\u65e9\u4e9b\u9001\u5230\uff0c\u4e0d\u53ef\u8bef\u4e86\u65f6\u523b\u3002\u5582\uff0c\u4f60\u53ef \u77e5\u9053\u8fd9\u4e24\u7897\u53c2\u6c64\u662f\u7ed9\u8c01\u559d\u7684\u4e48\uff1f\u201d\u524d\u5934\u90a3\u4e2a\u5bab\u5973\u9053\uff1a\u201c\u4e0d\u662f\u5927\u6c57\u81ea\u5df1\u8981\u559d\u7684\u4e48\uff1f\u201d\u5979\u540c \u4f34\u9053\uff1a\u201c\u5927\u6c57\u559d\u4e00\u4efd\uff0c\u53e6\u4e00\u4efd\u5374\u662f\u7ed9\u4e00\u4e2a\u5973\u72af\u4eba\u559d\u7684\u3002\u201d\u524d\u5934\u90a3\u4e2a\u5bab\u5973\u4f3c\u4e4e\u5927\u4e3a\u5947\u602a\uff0c \u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8ba9\u5973\u72af\u4eba\u559d\u53c2\u6c64\uff1f\u201d\u540e\u5934\u90a3\u4e2a\u5bab\u5973\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4ef6\u6781\u79d8\u5bc6\u7684 \u4e8b\u60c5\uff0c\u6709\u4e00\u4e2a\u548c\u6211\u8981\u597d\u7684\u4f8d\u536b\u8bf4\u7ed9\u6211\u542c\u7684\u3002\u201d\u8d70\u5728\u524d\u9762\u7684\u90a3\u4e2a\u5bab\u5973\u56de\u5934\u8bf4\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c \u8bf4\u7ed9\u6211\u542c\u542c\u3002\u201d \u3000\u3000\u540e\u9762\u90a3\u4e2a\u5bab\u5973\u9053\uff1a\u201c\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u53ef\u4e0d\u8bb8\u518d\u5bf9\u7b2c\u4e8c\u4e2a\u4eba\u8bb2\uff0c\u4eca\u5929\u4ed6\u4eec\u6349\u5230\u4e86\u4e00\u4e2a\u5973 \u5b50\uff0c\u542c\u8bf4\u5c31\u662f\u90a3\u4e2a\u5927\u5510\u738b\u5b50\u7684\u5983\u5b50\u3002\u201d\u674e\u9038\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u5934\u535c\u535c\u4e71\u8df3\uff0c\u8fd9\u4e2a\u5bab\u5973\u8bf4\u7684\u5927 \u5510\u738b\u5b50\u7684\u5983\u5b50\uff0c\u5f53\u7136\u6307\u7684\u662f\u4ed6\u7684\u59bb\u5b50\u957f\u5b59\u58c1\uff01\u957f\u5b59\u58c1\u679c\u7136\u662f\u88ab\u56da\u5728\u5927\u6c57\u5bab\u4e2d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u524d\u5934\u90a3\u4e2a\u5bab\u5973\u53c8\u95ee\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u5927\u5510\u7684\u738b\u5983\u957f\u5f97\u4ec0\u4e48\u6a21\u6837\uff1f\u5979\u6bd4\u5f97\u4e0a \u54b1\u4eec\u7684\u65b0\u738b\u5983\u5417\uff1f\u201d\u5979\u7684\u540c\u4f34\u5657\u55e4\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u7684\u90a3\u4f4d\u65b0\u738b\u5983\uff0c\u4e5f\u662f\u4e2d\u56fd\u5973\u7687\u5e1d\u6d3e\u6765\u7684 \u4eba\u5440\uff0c\u4f60\u8fd8\u5f53\u5979\u662f\u771f\u7684\u53ef\u8d3a\u6566\u5417\uff1f\u4e2d\u56fd\u7684\u5973\u5b50\u4e00\u4e2a\u4e2a\u90fd\u8fd9\u4e48\u6f02\u4eae\uff0c\u542c\u4ed6\u4eec\u8bf4\uff0c\u6349\u83b7\u7684\u90a3 \u4e2a\u5927\u5510\u738b\u5983\uff0c\u4e5f\u662f\u5929\u4ed9\u4f3c\u7684\uff0c\u5e76\u4e0d\u8f93\u4e8e\u90a3\u4f4d\u5192\u724c\u738b\u5983\u5462\uff01\u201d\u524d\u5934\u90a3\u4e2a\u5bab\u5973\u7b11\u9053\uff1a\u201c\u6562\u60c5 \u5927\u6c57\u89c1\u5979\u7f8e\u8c8c\uff0c\u6240\u4ee5\u7279\u522b\u4f18\u5f85\u5979\uff0c\u8fd9\u7897\u53c2\u6c64\u60f3\u5fc5\u662f\u7ed9\u5979\u559d\u7684\u4e86\uff1f\u201d \u3000\u3000\u540e\u9762\u90a3\u4e2a\u5bab\u5973\u7b11\u9053\uff1a\u201c\u4f60\u522b\u90aa\u5fc3\uff0c\u6211\u542c\u4ed6\u4eec\u8bf4\uff0c\u5927\u6c57\u662f\u60f3\u628a\u8fd9\u5973\u5b50\u5f53\u4f5c\u9999\u9975\uff0c\u5f15\u90a3 \u4e2a\u59d3\u674e\u7684\u738b\u5b50\u4e0a\u94a9\u5462\u3002\u5927\u6c57\u8bf4\u597d\u4e86\u4eca\u665a\u4e09\u66f4\u65f6\u5206\u63a5\u89c1\u5979\uff0c\u60f3\u662f\u89c1\u5979\u53d7\u4e86\u4f24\uff0c\u53c8\u4e0d\u80af\u559d\u4e1c \u897f\uff0c\u6240\u4ee5\u8981\u704c\u5979\u53c2\u6c64\u3002\u201d\u524d\u5934\u90a3\u4e2a\u5bab\u5973\u53c8\u95ee\u9053\uff1a\u201c\u5927\u6c57\u4e3a\u4ec0\u4e48\u5728\u65b0\u623f\u91cc\u63a5\u89c1\u5979\uff1f\u201d\u540e\u9762 \u90a3\u4e2a\u5bab\u5973\u7b11\u9053\uff1a\u201c\u4f60\u8d8a\u95ee\u8d8a\u51fa\u5947\uff0c\u6211\u600e\u4e48\u77e5\u9053\u5927\u6c57\u7684\u5fc3\u601d\uff1f\u201d\u524d\u9762\u90a3\u4e2a\u5bab\u5973\u9053\uff1a\u201c\u6211\u4e0d \u662f\u8bf4\u5927\u6c57\u5bf9\u8fd9\u4e2a\u5973\u5b50\u6709\u4ec0\u4e48\u574f\u5fc3\u601d\uff0c\u6211\u662f\u5728\u5947\u602a\uff0c\u4ed6\u88ab\u90a3\u4f4d\u5192\u724c\u738b\u5983\u4f5c\u5f04\u4e00\u573a\uff0c\u65b0\u90ce\u90fd \u505a\u4e0d\u6210\u4e86\uff0c\u5374\u8fd8\u6709\u5fc3\u60c5\u7559\u5728\u65b0\u623f\u91cc\u9762\uff1f\u7167\u4ed6\u5f80\u5e38\u7684\u813e\u6c14\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u4e0d\u77e5\u8981\u6740\u591a\u5c11\u4eba\u5462\uff01\u201d \u3000\u3000\u4e24\u4e2a\u5bab\u5973\u5431\u5431\u55b3\u55b3\u7684\u8c08\u8bba\uff0c\u8d70\u8fc7\u4e86\u90a3\u5757\u5047\u5c71\u6e56\u77f3\uff0c\u6ca1\u5165\u4e86\u82b1\u6811\u4e1b\u4e2d\uff0c\u58f0\u54cd\u4e5f\u6e10\u6e10\u542c \u4e0d\u6e05\u695a\u4e86\u3002\u674e\u9038\u54ac\u4e86\u54ac\u7259\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5927\u6c57\u8981\u5f15\u6211\u4e0a\u94a9\uff0c\u6211\u504f\u504f\u8981\u53bb\u548c\u4ed6\u4f5c\u5bf9\uff0c\u770b\u662f \u9c7c\u513f\u4e0a\u94a9\u8fd8\u662f\u9c7c\u94a9\u88ab\u6bc1\uff1f\u597d\u5728\u6211\u5df2\u77e5\u9053\u4ed6\u6240\u5728\u7684\u5730\u65b9\uff0c\u5f85\u5230\u4e09\u66f4\u65f6\u5206\uff0c\u76f4\u95ef\u8fdb\u53bb\u4fbf\u662f\u3002 \u3000\u3000\u674e\u9038\u4ece\u5047\u5c71\u77f3\u540e\u51fa\u6765\uff0c\u6b63\u60f3\u627e\u5bfb\u7b26\u4e0d\u7591\u7684\u8e2a\u5f71\uff0c\u9ed1\u9edd\u7684\u89d2\u843d\u91cc\u5ffd\u7136\u8df3\u51fa\u4e00\u4eba\uff0c\u559d\u9053\uff1a \u201c\u53e3\u4ee4\uff01\u201d\u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c\u7acb\u5373\u9a88\u6307\u5982\u621f\uff0c\u70b9\u4ed6\u7684\u7a74\u9053\uff0c\u53ea\u542c\u5f97\u201c\u5475\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4eba\u5f71 \u6643\u4e86\u4e00\u6643\uff0c\u5e76\u672a\u8dcc\u5012\uff0c\u53cd\u800c\u9a7e\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u5c0f\u5b50\u539f\u6765\u8fd8\u4f1a\u70b9\u7a74\uff0c\u54c8\uff0c\u539f\u6765\u4f60\u662f\u674e\u9038\uff01\u201d \u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u5374\u662f\u4e1d\u6beb\u4e0d\u653e\u677e\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u4fbf\u5411\u674e\u9038\u5288\u4e86\u4e24\u638c\u3002 \u3000\u3000\u674e\u9038\u63a5\u4e86\u4e24\u62db\uff0c\u4f46\u89c9\u5bf9\u65b9\u7684\u52b2\u9053\u5927\u5f97\u51fa\u5947\uff0c\u77a7\u6e05\u695a\u4e86\uff0c\u539f\u6765\u662f\u767e\u5fe7\u4e0a\u4eba\u7684\u5927\u5f1f\u5b50\u9633 \u592a\u534e\uff0c\u602a\u4e0d\u5f97\u7528\u91cd\u624b\u6cd5\u70b9\u7a74\u4e5f\u70b9\u4ed6\u4e0d\u5012\u3002 \u3000\u3000\u674e\u9038\u77e5\u9053\u4ed6\u7684\u5389\u5bb3\uff0c\u6025\u5fd9\u7ed5\u6811\u4e00\u8f6c\uff0c\u5f85\u9633\u592a\u534e\u8ffd\u5230\uff0c\u4ed6\u5df2\u62d4\u51fa\u4e86\u5b9d\u5251\uff0c\u4e00\u62db\u201c\u6a2a\u6307 \u5929\u5357\u201d\uff0c\u75be\u5241\u8fc7\u53bb\uff0c\u9633\u592a\u534e\u519c\u8896\u4e00\u62c2\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u5de6\u638c\u4e00\u987f\u4e00\u642d\uff0c\u8f7b\u62e8\u674e\u9038\u5251\u628a\uff0c\u53f3\u638c \u4e00\u62db\u201c\u4e58\u9f99\u5f15\u51e4\u201d\uff0c\u8098\u5e95\u7a7f\u51fa\uff0c\u53cd\u6765\u622a\u51fb\u674e\u9038\u7684\u5de6\u81c2\u3002\u674e\u9038\u89c1\u4ed6\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u62db\u6570 \u4f7f\u5f97\u53d8\u5316\u83ab\u6d4b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5c16\u53eb\u4e0d\u5999\u3002\u8981\u77e5\u674e\u9038\u5229\u4e8e\u901f\u6218\u901f\u51b3\uff0c\u6570\u62db\u4e4b\u5185\uff0c\u82e5\u4e0d\u80fd\u51fb\u5012 \u5bf9\u65b9\uff0c\u8e2a\u8ff9\u4fbf\u5f97\u8d25\u9732\u3002 \u3000\u3000\u9633\u592a\u534e\u62a2\u4e0d\u8d70\u674e\u9038\u7684\u5b9d\u5251\uff0c\u674e\u9038\u5728\u6025\u5207\u4e4b\u95f4\u4e5f\u4f24\u4e0d\u4e86\u4ed6\u3002\u679c\u7136\u8fc7\u4e86\u51e0\u62db\uff0c\u9633\u592a\u534e\u7f13 \u8fc7\u53e3\u6c14\u7acb\u5373\u5927\u58f0\u56b7\u9053\uff1a\u201c\u6709\u523a\u5ba2\uff0c\u5feb\u6765\u4eba\u5440\uff01\u201d \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u4f46\u542c\u5f97\u4eba\u58f0\u3001\u811a\u6b65\u58f0\u7eb7\u7136\u800c\u6765\uff0c\u674e\u9038\u5927\u4e3a\u7740\u6025\uff0c\u820d\u547d\u62a2\u653b\uff0c\u4e00\u62db\u201c\u94c1\u9a91 \u7a81\u51fa\u201d\uff0c\u63a5\u7740\u4e00\u62db\u201c\u98de\u6e21\u9634\u5c71\u201d\uff0c\u4e0a\u5241\u54bd\u5589\uff0c\u4e0b\u5241\u80f8\u80c1\uff0c\u8fd9\u4e24\u62db\u5168\u662f\u8fdb\u624b\u7684\u62db\u6570\uff0c\u786e\u662f \u51cc\u5389\u975e\u5e38\uff0c\u4f46\u4ed6\u4fa7\u91cd\u8fdb\u653b\uff0c\u672c\u8eab\u7684\u9632\u536b\u5374\u4e5f\u662f\u7a7a\u95e8\u6bd5\u9732\u3002 \u3000\u3000\u9ad8\u624b\u640f\u6597\u54ea\u5bb9\u5f97\u4e1d\u6beb\u66b4\u8e81\uff0c\u674e\u9038\u610f\u56fe\u884c\u9669\u4fa5\u5e78\uff0c\u53cd\u800c\u7ed9\u4e86\u9633\u592a\u534e\u4ee5\u53ef\u4e58\u4e4b\u673a\uff0c\u4f46\u89c1 \u4ed6\u6ef4\u6e9c\u6e9c\u7684\u4e00\u4e2a\u8f6c\u8eab\uff0c\u8eab\u5f62\u524d\u4fef\uff0c\u53cd\u800c\u62a2\u4e86\u8fdb\u6765\uff0c\u9a88\u6307\u5982\u655b\uff0c\u500f\u7684\u70b9\u5230\u4e86\u674e\u9038\u4e73\u4e0b\u7684 \u201c\u671f\u95e8\u7a74\u201d\uff0c\u201c\u671f\u95e8\u7a74\u201d\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u8fd9\u4e00\u6765\u4e24\u65b9\u7684\u62db\u6570\u5982\u2014\u7528\u5b9e\uff0c\u9633\u592a\u534e\u5b9a\u80fd\u88ab \u6311\u65ad\u7435\u7436\u9aa8\uff0c\u674e\u9038\u7eb5\u7136\u4e0d\u6b7b\uff0c\u4e5f\u8981\u53d7\u5230\u91cd\u4f24\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u53d1\u4e4b\u9645\uff0c\u9633\u592a\u534e\u5ffd\u5730\u95f7\u54fc\u4e00\u58f0\uff0c\u7bad\u822c\u7684\u5411\u540e\u5230\u53bb\uff0c\u5934\u649e\u5728\u77f3\u4e0a\uff0c\u201c\u5495\u549a\u201d \u4e00\u58f0\uff0c\u76f4\u633a\u633a\u7684\u8dcc\u5012\u3002\u674e\u9038\u5f81\u4e86\u4e00\u6014\uff0c\u4ed6\u7684\u5251\u950b\u6839\u672c\u5c31\u672a\u66fe\u89e6\u53ca\u9633\u592a\u534e\u7684\u8eab\u4f53\uff0c\u9633\u592a\u534e \u600e\u4e48\u5c31\u5012\u4e86\uff1f \u3000\u3000\u5fc3\u5ff5\u4e0d\u5df2\uff0c\u9661\u89c1\u4e24\u6761\u9ed1\u5f71\u51cc\u7a7a\u98de\u63a0\u800c\u6765\uff0c\u5ffd\u5730\u5728\u7a7a\u4e2d\u4e00\u649e\uff0c\u53cc\u53cc\u8dcc\u4e0b\uff0c\u968f\u5373\u542c\u5f97\u4e00 \u4e2a\u6d2a\u4eae\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u9178\u4e01\u771f\u662f\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u6562\u95ef\u8fdb\u5bab\u6765\uff0c\u4f5b\u7237\u53ef\u8981\u7ed9\u4f60\u5ff5\u751f\u5492\u4e86\uff01\u201d \u53e6\u4e00\u4e2a\u58f0\u97f3\u7a81\u563b\u563b\u7684\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5148\u8d54\u6211\u4e00\u53cc\u8349\u978b\uff0c\u7136\u540e\u518d\u5ff5\u4f60\u7684\u5012\u5934\u7ecf\u5427\uff01\u201d\u8fd9\u4e24 \u4e2a\u4eba\u4e00\u4e2a\u662f\u7b26\u4e0d\u7591\uff0c\u53e6\u4e00\u4e2a\u6b63\u662f\u767e\u5fe7\u548c\u5c1a\u3002\u539f\u6765\u4ed6\u4e24\u4eba\u90fd\u53d1\u73b0\u4e86\u674e\u9038\u548c\u9633\u592a\u534e\u5728\u751f\u6b7b\u640f \u6597\uff0c\u7b26\u4e0d\u7591\u8ddd\u79bb\u8f83\u8fd1\uff0c\u51fa\u624b\u5728\u5148\uff0c\u4ee5\u98de\u82b1\u6458\u53f6\u3001\u4f24\u4eba\u7acb\u6b7b\u7684\u529f\u592b\uff0c\u6697\u52a9\u674e\u9038\u4e00\u81c2\u4e4b\u529b\u3002 \u4f46\u767e\u4f18\u4e0a\u4eba\u5982\u5f71\u968f\u5f62\uff0c\u7acb\u5373\u8ddf\u8e2a\u8d76\u81f3\uff0c\u4e24\u4eba\u672a\u5f85\u8eab\u5f62\u843d\u5730\uff0c\u5728\u534a\u7a7a\u4e2d\u4fbf\u4ea4\u6362\u4e86\u4e00\u62db\uff0c\u767e \u5fe7\u4e0a\u4eba\u4ee5\u5185\u529b\u9707\u7ffb\u4e86\u7b26\u4e0d\u7591\uff0c\u7b26\u4e0d\u7591\u5219\u4ee5\u4e00\u6307\u63b8\u529f\u6233\u4e2d\u4e86\u767e\u5fe7\u4e0a\u4eba\u7684\u8109\u95e8\uff0c\u53cc\u65b9\u5404\u5403\u4e00 \u70b9\u5c0f\u4e8f\uff0c\u7f13\u4e86\u53e3\u6c14\uff0c\u7acb\u5373\u53c8\u8df3\u8d77\u6765\u518d\u5ea6\u4ea4\u624b\u3002 \u3000\u3000\u674e\u9038\u53c8\u60ca\u53c8\u559c\uff0c\u60ca\u8005\u662f\u5df2\u7ed9\u767e\u5fe7\u4e0a\u4eba\u53d1\u73b0\uff0c\u559c\u662f\u6709\u7b26\u4e0d\u7591\u5c06\u4ed6\u62cc\u5230\uff0c\u8fd9\u4e24\u4eba\u4ea4\u4e0a\u624b\uff0c \u975e\u5230\u5343\u62db\u4e4b\u5916\uff0c\u96be\u5206\u80dc\u8d1f\uff0c\u53e6\u5916\u90a3\u4e9b\u98de\u5954\u800c\u6765\u7684\u6b66\u58eb\uff0c\u8fd9\u65f6\u90fd\u7ed9\u7b26\u4e0d\u7591\u5438\u5f15\u4ed6\u4eec\u7684\u6ce8\u610f \u529b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5c1a\u672a\u5bdf\u89c9\u5728\u5c71\u77f3\u63a9\u853d\u4e0b\u7684\u674e\u9038\u3002 \u3000\u3000\u674e\u9038\u6025\u5fd9\u94bb\u8fc7\u4e00\u4e2a\u5c71\u77f3\u6d1e\uff0c\u86c7\u884c\u9f9f\u4f19\uff0c\u5230\u4e86\u82b1\u6728\u4e1b\u4e2d\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u542c\u5f97\u6709\u597d\u51e0\u4e2a \u7a81\u53a5\u6b66\u58eb\u7684\u58f0\u97f3\u7eb7\u7eb7\u56b7\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4e0d\u597d\uff0c\u9633\u592a\u534e\u7ed9\u4eba\u5bb3\u4e86\uff0c\u8fd8\u597d\uff01\u8fd8\u597d\uff01\u4ed6\u8fd8\u6709\u6c14\u606f\u3002\u201d \u201c\u5feb\u8bf7\u83e9\u63d0\u4e0a\u4eba\u524d\u6765\uff0c\u5c06\u4ed6\u6551\u6d3b\u3002\u201d\u8fd9\u4e00\u7fa4\u6b66\u58eb\u5fd9\u7740\u6551\u9633\u592a\u534e\uff0c\u6682\u65f6\u987e\u4e0d\u5f97\u641c\u7d22\u654c\u4eba\uff0c \u674e\u9038\u8d81\u8fd9\u4e2a\u673a\u4f1a\uff0c\u53c8\u7a7f\u8fc7\u4e00\u7247\u6811\u6728\u3002\u6084\u6084\u7684\u6e9c\u5165\u4e86\u540e\u5bab\u3002 \u3000\u3000\u80cc\u540e\u91d1\u5200\u5431\u5431\u4e4b\u58f0\uff0c\u542c\u90a3\u58f0\u97f3\uff0c\u88f4\u53d4\u5ea6\u548c\u8c37\u795e\u7fc1\u4f3c\u4e4e\u90fd\u5df2\u6765\uff01\u800c\u4e14\u5df2\u9677\u5165\u91cd\u56f4\u4e4b\u4e2d\u3002 \u3000\u3000\u674e\u9038\u5fc3\u60f3\u4ee5\u7b26\u88f4\u8c37\u4e09\u4eba\u7684\u6b66\u529f\uff0c\u7eb5\u7136\u9677\u5165\u91cd\u56f4\uff0c\u8981\u8131\u9669\u8c05\u975e\u96be\u4e8b\uff0c\u73b0\u4e0b\u5df2\u662f\u4e09\u66f4\uff0c \u65f6\u673a\u7a0d\u7eb5\u5373\u901d\uff0c\u82e5\u7136\u60ca\u52a8\u5927\u6c57\uff0c\u6551\u4eba\u90a3\u5c31\u96be\u4e86\u3002\u56e0\u6b64\u53ea\u597d\u6487\u4e0b\u4ed6\u4eec\uff0c\u72ec\u81ea\u8fdb\u5bab\u523a\u63a2\u3002 \u3000\u3000\u4ed6\u5df2\u77e5\u9053\u5927\u6c57\u7684\u6240\u5728\uff0c\u53c2\u7167\u6b66\u7384\u971c\u6240\u753b\u7684\u5bab\u4e2d\u8349\u56fe\uff0c\u4e00\u8def\u501f\u7269\u969c\u5f62\uff0c\u86c7\u884c\u514d\u4f0f\uff0c\u7ed5 \u8fc7\u66f2\u6298\u56de\u5eca\uff0c\u7a7f\u8fc7\u91cd\u91cd\u95e8\u6237\uff0c\u867d\u7136\u65f6\u4e0d\u65f6\u78b0\u5230\u5de1\u67e5\u7684\u6b66\u58eb\uff0c\u53ef\u8d8b\u907f\u5f97\u5b9c\uff0c\u6ca1\u6709\u7ed9\u4ed6\u4eec\u53d1 \u89c9\u3002 \u3000\u3000\u6765\u5230\u4e86\u90a3\u5ea7\u738b\u5983\u7684\u201c\u65b0\u623f\u201d\uff0c\u5947\u602a\u5f97\u5f88\uff0c\u5916\u9762\u7adf\u7136\u6ca1\u6709\u9632\u5b88\u7684\u6b66\u58eb\uff0c\u674e\u9038\u4e5f\u8d77\u4e86\u7591 \u5fc3\uff0c\u53ef\u662f\u60c5\u52bf\u7d27\u8feb\uff0c\u54ea\u5bb9\u5f97\u674e\u9038\u4ed4\u7ec6\u63a8\u6572\uff0c\u5fc3\u60f3\u53cd\u6b63\u6765\u4e86\uff0c\u5373\u7b97\u662f\u864e\u7a74\u9f99\u6f6d\uff0c\u4e5f\u5f97\u95ef\u4ed6 \u4e00\u95ef\u4e86\u3002 \u3000\u3000\u674e\u9038\u98de\u8eab\u8df3\u4e0a\u74e6\u9762\uff0c\u6500\u7740\u6a90\u89d2\uff0c\u7528\u4e00\u4e2a\u201c\u73cd\u73e0\u4f8b\u5377\u5e18\u201d\u7684\u59ff\u52bf\uff0c\u659c\u6302\u534a\u8eab\uff0c\u63a2\u5934\u7aa5 \u89c6\uff0c\u4f46\u89c1\u90a3\u7a81\u53a5\u5927\u6c57\u6b63\u5728\u5c4b\u4e2d\uff0c\u4ed6\u65c1\u8fb9\u6709\u4e00\u4e2a\u6301\u957f\u97ad\u7684\u6b66\u58eb\uff0c\u65e2\u4e0d\u662f\u9ebb\u7ffc\u8d5e\uff0c\u4e5f\u4e0d\u662f\u6070 \u514b\u56fe\uff0c\u674e\u9038\u66f4\u89c9\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u5927\u6c57\u600e\u7684\u5982\u6b64\u5927\u610f\uff0c\u4e0d\u8981\u7b2c\u4e00\u6d41\u9ad8\u624b\u9632\u62a4\uff0c\u96be\u9053\u662f\u4ed6\u53e6 \u5916\u5b89\u6392\u4e86\u9677\u4e95\uff1f\u8fd8\u662f\u5929\u8d50\u826f\u673a\uff0c\u4ee4\u6211\u6210\u529f\uff1f\u201d \u3000\u3000\u674e\u9038\u672c\u6765\u4e0d\u662f\u4e2a\u7c97\u5fc3\u5927\u610f\u7684\u4eba\uff0c\u4f46\u6b64\u65f6\u4ed6\u6551\u59bb\u5fc3\u5207\uff0c\u65e9\u5df2\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u4f55\u51b5\u8fd9 \u65f6\u4ed6\u53c8\u770b\u5230\u4e86\u5c4b\u5b50\u91cc\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e00\u770b\u767b\u65f6\u4ee4\u4ed6\u547c\u5438\u7d27\u4fc3\uff0c\u8840\u8109\u6124\u5f20\uff0c\u66f4\u987e\u4e0d\u5f97\u5927\u6c57 \u662f\u5426\u5b89\u6392\u6709\u9677\u4e95\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u7a7f\u7740\u7ef4\u65cf\u670d\u9970\u7684\u5973\u5b50\uff0c\u4f46\u89c1\u5979\u5e26\u7740\u9563\u94d0\uff0c\u5750\u5728\u5927\u6c57\u7684\u4fa7\u8fb9\u3002\u4e2d\u95f4\u9694\u7740\u4e00\u4e2a \u957f\u65b9\u5f62\u7684\u8336\u51e0\uff0c\u674e\u9038\u53ea\u89c1\u5230\u5979\u7684\u4fa7\u9762\uff0c\u867d\u7136\u770b\u5f97\u4e0d\u5f88\u6e05\u711a\uff0c\u4f46\u9664\u4e86\u957f\u5b59\u58c1\u8fd8\u6709\u8c01\u4eba\uff1f\u674e \u9038\u8fd8\u8ba4\u5f97\u5979\u90a3\u4ef6\u8863\u670d\u3002\u6bcf\u6b21\u957f\u5b59\u58c1\u6539\u626e\u7ef4\u65cf\u5973\u5b50\u4e0b\u5c71\u3002\u603b\u662f\u6b22\u559c\u7a7f\u8fd9\u4ef6\u8863\u670d\u7684\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5927\u6c57\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u6574\u5929\u6ca1\u6709\u5403\u4e1c\u897f\uff0c\u8fd9\u600e\u4e48\u884c\uff1f\u6211\u5bf9\u4f60\u4eec\u592b\u5987\u5b9e\u662f\u4e00\u7247\u597d \u5fc3\uff0c\u4f60\u559d\u4e86\u8fd9\u7897\u53c2\u6c64\uff0c\u6211\u518d\u548c\u4f60\u8bf4\u5427\u3002\u201d\u957f\u5b59\u58c1\u54fc\u4e86\u4e00\u58f0\uff0c\u4e0d\u8a00\u4e0d\u8bed\u3002\u5927\u6c57\u9053\uff1a\u201c\u597d\uff0c \u5979\u4e0d\u559d\uff0c\u4f60\u704c\u5979\u559d\uff01\u201d\u90a3\u6b66\u58eb\u5e94\u4e86\u58f0\u201c\u8bfa\u201d\uff0c\u62ff\u8d77\u53c2\u6c64\uff0c\u6309\u7740\u957f\u5b59\u58c1\u4fbf\u704c\uff0c\u5ffd\u542c\u5f97\u201c\u545b \u5577\u201d\u4e00\u58f0\uff0c\u957f\u5b59\u58c1\u4fa7\u8f6c\u8eab\u5b50\uff0c\u628a\u624b\u4e00\u62e8\uff0c\u76db\u7740\u53c2\u6c64\u7684\u78c1\u7897\u8dcc\u6210\u7c89\u788e\u3002\u674e\u9038\u5fc3\u9053\uff1a\u201c\u597d\uff0c \u4e0d\u6127\u662f\u6211\u7684\u59bb\u5b50\uff01\u201d \u3000\u3000\u5927\u6c57\u6012\u9053\uff1a\u201c\u5b64\u738b\u597d\u610f\u5bf9\u4f60\uff0c\u4f60\u5374\u8fd9\u6837\u65e0\u793c\uff01\u597d\u5440\uff0c\u656c\u9152\u4e0d\u5403\u4f60\u8981\u5403\u7f5a\u9152\uff0c\u5580\u5c14\u5df4\uff0c \u7ed9\u6211\u91cd\u91cd\u7684\u97ad\u5979\uff0c\u6211\u5012\u8981\u770b\u770b\u5979\u7684\u9aa8\u5934\u6709\u591a\u786c\uff01\u201d\u90a3\u4e2a\u6b66\u58eb\u6325\u52a8\u957f\u97ad\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c \u91cd\u91cd\u7684\u5728\u957f\u5b59\u58c1\u7684\u80cc\u810a\u4e0a\u62bd\u4e86\u4e00\u4e0b\uff0c\u957f\u5b59\u58c1\u88ab\u4ed6\u62bd\u5f97\u80f8\u812f\u8d77\u4f0f\uff0c\u4ecd\u7136\u54ac\u7259\u786c\u633a\uff0c\u4e0d\u80af\u51fa \u58f0\u547b\u541f\u3002 \u3000\u3000\u662f\u53ef\u5fcd\u5b70\u4e0d\u53ef\u5fcd\uff1f\u674e\u9038\u820c\u7efd\u6625\u96f7\uff0c\u9661\u7684\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f4f\u624b\uff01\u201d\u98de\u8eab\u7a9c\u5165\uff0c\u5288\u624b\u593a\u4e86 \u90a3\u6b66\u58eb\u7684\u957f\u97ad\uff0c\u53e6\u4e00\u624b\u4e00\u628a\u6293\u7740\u4e86\u957f\u5b59\u58c1\uff0c\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u5ffd\u89c9\u811a\u5e95\u8f7b\u8f7b\u98d8\u98d8\uff0c \u8e0f\u4e86\u4e2a\u7a7a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5730\u677f\u5ffd\u7136\u88c2\u5f00\uff0c\u674e\u9038\u6402\u7740\u957f\u5b59\u58c1\u53cc \u53cc\u8dcc\u4e0b\u53bb\u4e86\uff0c\u4e0a\u9762\u5140\u81ea\u4f20\u6765\u7a81\u53a5\u5927\u6c57\u5f97\u610f\u7684\u7b11\u58f0\u3002 \u3000\u3000\u4e0b\u9762\u662f\u4e2a\u9ed1\u9edd\u9edd\u7684\u5730\u7262\uff0c\u674e\u9038\u4e2d\u8ba1\uff0c\u4fae\u4e4b\u5df2\u665a\uff0c\u4f46\u4ed6\u7ec8\u4e8e\u5f97\u4e0e\u59bb\u5b50\u91cd\u9022\uff0c\u96be\u8fc7\u4e4b\u4e2d\uff0c \u5374\u4e5f\u611f\u5230\u6b23\u6170\uff0c\u5fc3\u60f3\uff1a\u8fd9\u603b\u7b97\u662f\u4e0d\u5e78\u4e2d\u4e4b\u5e78\u4e86\u3002 \u3000\u3000\u674e\u9038\u6402\u7d27\u59bb\u5b50\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7ffb\u8eab\uff0c\u7f13\u548c\u4e86\u4e0b\u5760\u4e4b\u52bf\uff0c\u8f7b\u98d8\u98d8\u7684\u843d\u5230\u5730\u4e0a\uff0c\u5e78\u800c\u6ca1 \u6709\u635f\u4f24\u3002\u9ed1\u6697\u4e2d\u770b\u4e0d\u5230\u59bb\u5b50\u7684\u9762\u76ee\uff0c\u674e\u9038\u7d27\u63e1\u7740\u5979\u7684\u624b\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u6211\u5728\u8fd9\u513f\uff0c\u54b1\u4eec\u5230 \u5e95\u53c8\u805a\u5728\u4e00\u5904\u4e86\u3002\u201d\u957f\u5b59\u58c1\u8f7b\u8f7b\u54fd\u54bd\uff0c\u674e\u9038\u6478\u7d22\u7740\u89e3\u5979\u7684\u624b\u94d0\uff0c\u4e00\u8fb9\u8bf4\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u4f60 \u4e0d\u8981\u96be\u8fc7\uff0c\u54b1\u4eec\u5f97\u4ee5\u540c\u751f\u5171\u6b7b\uff0c\u6b7b\u4ea6\u65e0\u61be\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c9\u53cc\u624b\u7d27\u675f\uff0c\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u5c31\u5728\u8fd9\u65f6\uff0c \u957f\u5b59\u58c1\u5ffd\u5730\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u59bb\u5b50\uff1f\u4f60\u628a\u773c\u775b\u7741\u5f00\uff0c\u770b\u6e05\u695a\u4e86\uff01\u201d \u3000\u3000\u5730\u7262\u91cc\u73b0\u51fa\u706b\u5149\uff0c\u90a3\u7ef4\u65cf\u5973\u5b50\u9000\u540e\u4e86\u6570\u4e08\u4e4b\u5730\uff0c\u5979\u7684\u624b\u94d0\u5df2\u67ec\u5230\u4e86\u674e\u9038\u7684\u624b\u4e0a\u3002\u674e \u9038\u5b9a\u775b\u7ec6\u770b\uff0c\u90a3\u7ef4\u65cf\u5973\u5b50\uff0c\u8eab\u6750\u8f6e\u5ed3\u90fd\u4e0e\u957f\u5b59\u58c1\u76f8\u4f3c\uff0c\u4f46\u786e\u5b9e\u4e0d\u662f\u957f\u5b59\u58c1\u3002 \u3000\u3000\u539f\u6765\u7a81\u53a5\u5927\u6c57\u9884\u6599\u5230\u674e\u9038\u5fc5\u5b9a\u4f1a\u8fdb\u5bab\u6551\u4eba\uff0c\u56e0\u6b64\u5b89\u6392\u4e0b\u8fd9\u4e2a\u9677\u4e95\uff0c\u9009\u4e00\u4e2a\u4e0e\u957f\u5b59\u58c1 \u76f8\u4f3c\u7684\u5bab\u5973\uff0c\u8bf1\u674e\u9038\u4e0a\u5f53\u7684\u3002\u65b0\u623f\u91cc\u7684\u673a\u5173\u4e5f\u662f\u4e34\u65f6\u5e03\u7f6e\u7684\uff0c\u5728\u90a3\u4e2a\u5bab\u5973\u7684\u811a\u4e0b\uff0c\u5c31\u6b63 \u662f\u673a\u5173\u6240\u5728\uff0c\u674e\u9038\u6025\u6551\u4eba\uff0c\u7109\u6709\u4e0d\u4e0a\u5f53\u4e4b\u7406\uff1f \u3000\u3000\u674e\u9038\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u5e26\u7740\u624b\u94d0\uff0c\u5c31\u60f3\u8fc7\u53bb\u5c06\u5bab\u5973\u6251\u6740\uff0c\u4f46\u4e00\u60f3\u8fd9\u4e2a\u5bab\u5973\u4e0d\u8fc7\u662f \u5927\u6c57\u6240\u5229\u7528\u7684\u5de5\u5177\uff0c\u53ea\u597d\u5fcd\u7740\u4e86\u6c14\uff0c\u957f\u53f9\u4e00\u58f0\u3002 \u3000\u3000\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6b66\u58eb\u8d70\u4e86\u51fa\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u5bb3\u6015\uff0c\u5927\u6c57\u4e0d\u4f1a\u4e8f\u5f85\u4f60\u7684\u3002\u201d\u674e\u9038 \u5927\u6012\uff0c\u68d2\u7740\u624b\u94d0\uff0c\u6a2a\u626b\u8fc7\u53bb\uff0c\u8fd9\u6b66\u58eb\u540d\u53eb\u5580\u5c14\u5df4\uff0c\u662f\u897f\u85cf\u8d5e\u666e\u6cd5\u5e08\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u6b66\u529f\u4e0e \u9633\u592a\u534e\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5728\u6070\u514b\u56fe\u4e4b\u4e0a\uff0c\u674e\u9038\u6234\u7740\u624b\u94d0\uff0c\u5982\u4f55\u4f24\u5f97\u4e86\u4ed6\uff0c\u88ab\u4ed6\u4e00\u628a\u6293\u7740\u4e86\u81c2\u5f2f \u7684\u66f2\u6c60\u7a74\uff0c\u767b\u65f6\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u5580\u5c14\u5df4\u7b11\u9053\uff1a\u201c\u4f60\u7684\u813e\u6c14\u597d\u5927\uff0c\u5927\u6c57\u8981\u628a\u4e2d\u56fd\u7687\u5e1d\u7684\u5b9d\u5ea7\u9001\u7ed9\u4f60\uff0c\u4f60\u8fd8\u53d1\u8fd9\u4e48\u5927\u7684 \u813e\u6c14\uff0c\u4e5f\u7b97\u5f97\u662f\u5947\u602a\u6781\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u559d\u9053\uff1a\u201c\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u6211\u843d\u5728\u4f60\u4eec\u7684\u624b\u4e2d\uff0c\u5b81\u6b7b\u4e0d\u8fb1\uff01\u201d\u5580\u5c14\u5df4\u7b11\u9053\uff1a\u201c\u5927\u6c57\u662f \u62ac\u4e3e\u4f60\uff0c\u9664\u975e\u662f\u4f60\u81ea\u53d6\u5176\u8fb1\u3002\u4f60\u6709\u4ec0\u4e48\u8bdd\uff0c\u5411\u5927\u6c57\u8bf4\u5427\u3002\u201d\u5728\u5899\u58c1\u4e00\u6309\uff0c\u5f00\u4e86\u4e00\u9053\u89d2\u95e8\uff0c \u62bc\u7740\u674e\u9038\u8d70\u4e0a\u53bb\u3002\u8d70\u4e86\u8bb8\u591a\u77f3\u7ea7\uff0c\u53c8\u56de\u5230\u524d\u9762\u90a3\u5ea7\u5bab\u6bbf\uff0c\u7a81\u53a5\u5927\u6c57\u4e0e\u9ebb\u7ffc\u8d5e\u5df2\u5728\u90a3\u91cc\u7b49 \u5019\u3002 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u5f97\u610f\u7b11\u9053\uff1a\u201c\u4f60\u6628\u65e5\u8d70\u5f97\u592a\u5306\u5fd9\u4e86\uff0c\u6211\u7684\u8bdd\u4e5f\u8bb8\u4f60\u8fd8\u672a\u66fe\u5e73\u5fc3\u9759\u6c14\u53bb\u60f3\uff0c \u96be\u5f97\u4f60\u4eca\u65e5\u56de\u6765\uff0c\u54b1\u4eec\u518d\u8c08\u8c08\u5427\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u65bd\u7528\u8be1\u8ba1\u6349\u4e86\u6211\uff0c\u54b1\u4eec\u8fd8\u6709\u4ec0\u4e48\u53ef\u8c08 \u7684\uff1f\u201d\u5927\u6c57\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u602a\u4f60\u805a\u4f17\u8fdb\u5bab\u80e1\u95f9\uff0c\u4f60\u5374\u602a\u6211\u65bd\u7528\u8be1\u8ba1\u5417\uff1f\u5175\u4e0d\u538c\u8bc8\uff0c\u8fd9\u8bdd\u539f \u662f\u4f60\u4eec\u4e2d\u56fd\u7684\u5175\u5bb6\u8bf4\u7684\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u8f6c\u8eab\u95ee\u9ebb\u7ffc\u8d5e\u9053\uff1a\u201c\u5916\u9762\u95f9\u5f97\u600e\u4e48 \u6837\uff1f\u90a3\u51e0\u4e2a\u8001\u5bb6\u4f19\u6349\u5230\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ebb\u7ffc\u8d5e\u9053\uff1a\u201c\u65e9\u5df2\u88ab\u9a71\u9010\u51fa\u5bab\u53bb\u4e86\u3002\u73b0\u5728\u56fd\u5e08\u6b63\u7387\u9886\u6b66\u58eb\u53bb\u8ffd\u3002\u4ed6\u4eec\u53d7\u4e86\u91cd\u4f24\uff0c\u8c05 \u4e5f\u9003\u8d70\u4e0d\u8fdc\u3002\u201d\u674e\u9038\u5fc3\u60f3\uff0c\u4ee5\u7b26\u4e0d\u7591\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u7eb5\u7136\u5be1\u4e0d\u4f17\u654c\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u53d7\u5230\u91cd\u4f24\uff0c \u542c\u5f97\u4ed6\u4eec\u5df2\u7ecf\u9003\u51fa\uff0c\u53cd\u800c\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u5927\u6c57\u53c8\u5f97\u610f\u7b11\u9053\uff1a\u201c\u6211\u56fd\u4e2d\u5175\u7cbe\u7cae\u8db3\uff0c\u6b66\u58eb\u82f1\u52c7\uff0c\u4f60\u7ecf\u8fc7\u8fd9\u4e24\u6b21\u4ea4\u624b\uff0c\u77e5\u9053\u5386\u5bb3\u4e86 \u5427\uff1f\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u4e2d\u56fd\u6709\u53e5\u5723\u4eba\u7684\u8bdd\u8bf4\uff0c\u552f\u4ec1\u8005\u65b9\u53ef\u4ee5\u65e0\u654c\u4e8e\u5929\u4e0b\u3002\u5f92\u6043\u7532\u5175\u4e4b\u5229\uff0c\u5c82\u80fd \u670d\u5f97\u4e86\u4eba\uff1f\u201d\u5927\u6c57\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u90a3\u662f\u4f60\u4eec\u8150\u5112\u7684\u8bf4\u8bdd\u3002\u201d\u674e\u9038\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u5927\u6c57 \u7684\u5389\u5bb3\uff0c\u6211\u786e\u662f\u89c1\u8bc6\u8fc7\u4e86\uff0c\u54c8\uff0c\u54c8\uff0c\u90a3\u5f53\u771f\u662f\u53ef\u7b11\u800c\u53c8\u53ef\u9119\uff01\u201d\u5927\u6c57\u9762\u8272\u4e00\u53d8\uff0c\u6012\u9053\uff1a \u201c\u4f60\u6562\u975e\u8bae\u5b64\u738b\uff1f\u6211\u6709\u54ea\u70b9\u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u4f60\u62e5\u6709\u7532\u5175\u5341\u6253\uff0c\u6b66\u58eb\u5343\u4eba\uff0c\u62ff\u7740\u6211\u6ca1\u6709\u529e\u6cd5\uff0c\u5374\u6765\u6b3a\u4fae\u6211\u7684\u59bb\u513f\uff0c\u6b64 \u7b49\u624b\u6bb5\uff0c\u5c82\u975e\u53ef\u7b11\u53ef\u9119\uff1f\u201d \u3000\u3000\u5927\u6c57\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u4ece\u4f60\u4eec\u4e2d\u56fd\u5b66\u6765\u7684\u529e\u6cd5\u5440\u3002\u4f60\u4eec\u4e2d\u56fd\u7684\u541b\u4e3b\u4e0d\u662f\u6700\u559c\u6b22\u62d8\u7559\u4ed6 \u4eec\u4e0d\u4fe1\u4efb\u7684\u4eba\u7684\u513f\u5973\uff0c\u4f5c\u4e3a\u4eba\u8d28\u7684\u4e48\uff1f\u4e2d\u56fd\u541b\u4e3b\u62d8\u7559\u4eba\u8d28\u7684\u6545\u4e8b\uff0c\u786e\u662f\u53f2\u4e0d\u7edd\u4e66\uff0c\u6700\u8457 \u540d\u7684\u4f8b\u5b50\u5982\u5468\u5e73\u738b\u4ee5\u5929\u5b50\u4e4b\u5c0a\uff0c\u7528\u90d1\u5e84\u738b\u505a\u2018\u537f\u58eb\u2019\uff0c\u541b\u81e3\u4e8c\u4eba\u95f9\u8e69\u626d\uff0c\u7adf\u7136\u4e92\u76f8\u4ea4\u6362 \u513f\u5b50\u4f5c\u4e3a\u62b5\u62bc\uff0c\u5468\u5929\u5b50\u2018\u72d0\u2019\u4e3a\u8d28\u4e8e\u90d1\uff0c\u90d1\u516c\u5b50\u2018\u5ffd\u2019\u4e3a\u8d28\u4e8e\u5468\uff0c\u6210\u4e3a\u5386\u53f2\u4e0a\u7684\u5927\u7b11\u8bdd\u3002\u201d \u7a81\u53a5\u5927\u6c57\u8bf7\u6709\u6c49\u5112\u7ed9\u4ed6\u8bb2\u8ff0\u4e2d\u56fd\u7684\u53f2\u4e8b\uff0c\u73b0\u5728\u4fbf\u62ff\u6765\u53cd\u9a73\u4ed6\u3002 \u3000\u3000\u674e\u9038\u51b7\u7b11\u9053\uff1a\u201c\u4e2d\u56fd\u6709\u591a\u5c11\u597d\u7684\u4e1c\u9149\u503c\u5f97\u4f60\u5b66\uff0c\u4f60\u4e0d\u5b66\u597d\u7684\uff0c\u4e13\u5b66\u574f\u7684\uff0c\u8fd9\u4e5f\u662f\u53ef \u7b11\u5f97\u5f88\u5440\uff01\u4e2d\u56fd\u8fd8\u6709\u4e00\u53e5\u5723\u4eba\u7684\u8bf4\u8bdd\u2018\u4e09\u519b\u53ef\u4ee5\u593a\u5e05\uff0c\u5339\u592b\u4e0d\u53ef\u593a\u5fd7\u2019\u4f60\u61c2\u5f97\u5417\uff1f\u4e0d\u8bba \u4f60\u7528\u4ec0\u4e48\u5a01\u80c1\u5229\u8bf1\u7684\u624b\u6bb5\uff0c\u603b\u4e4b\u6211\u4e0d\u4f1a\u5bf9\u4f60\u4f9d\u4ece\u3002\u201d \u3000\u3000\u5927\u6c57\u6709\u70b9\u6c14\u6cae\uff0c\u7785\u7740\u674e\u9038\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u7b97\u4f60\u662f\u6761\u786c\u6c49\uff0c\u4f60\u8fde\u59bb\u5b50\u4e5f\u4e0d\u8981\u4e86\u4e48\uff1f\u201d\u674e \u9038\u9053\uff1a\u201c\u6211\u4eec\u592b\u59bb\u4e8c\u4eba\u5982\u540c\u4e00\u4f53\uff0c\u6211\u6b63\u662f\u4e3a\u4e86\u5979\uff0c\u624d\u820d\u4e86\u6027\u547d\u5230\u8fd9\u91cc\u6765\uff0c\u613f\u4e0e\u5979\u540c\u751f\u5171 \u6b7b\uff01\u6211\u77e5\u9053\u5979\u7684\u60f3\u6cd5\u4e0e\u6211\u4e00\u6837\uff0c\u4f60\u60f3\u62ff\u5979\u6765\u5a01\u80c1\u6211\uff0c\u6216\u8005\u62ff\u6211\u6765\u5a01\u80c1\u5979\uff0c\u60f3\u8981\u6211\u4eec\u6295\u964d\uff0c \u90a3\u53ea\u662f\u4f60\u7684\u75f4\u5fc3\u5984\u60f3\uff01\u201d \u3000\u3000\u5927\u6c57\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u6f20\u8bf4\u9053\uff1a\u201c\u5c06\u4ed6\u7684\u59bb\u5b50\u62ff\u6765\uff0c\u6211\u5012\u8981\u770b\u770b\uff0c\u5979\u662f\u4e0d\u662f\u4e5f\u50cf\u4f60 \u8fd9\u6837\u7684\u94c1\u77f3\u5fc3\u80a0\uff0c\u4e0d\u7231\u60dc\u81ea\u5df1\u7684\u6027\u547d\uff0c\u4e5f\u4e0d\u7231\u60dc\u81ea\u5df1\u7684\u4eb2\u4eba\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u6070\u514b\u56fe\u679c\u7136\u628a\u957f\u5b59\u58c1\u62bc\u8fdb\u6765\uff0c\u5927\u6c57\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\u6e05\u695a\u4e86\uff0c\u4f60\u7684\u4e08\u592b\u5c31\u7ad9 \u5728\u8fd9\u513f\uff01\u4f60\u7684\u6027\u547d\u5c31\u634f\u5728\u4ed6\u7684\u624b\u4e2d\uff0c\u4ed6\u4f9d\u4ece\u6211\uff0c\u6211\u7ed9\u4f60\u505a\u4e2d\u56fd\u7684\u7687\u540e\uff0c\u4ed6\u4e0d\u4f9d\u4ece\uff0c\u4f60\u4eec \u4e24\u4eba\u90fd\u4e0d\u5f97\u597d\u6b7b\uff01\u4f60\u597d\u597d\u7684\u548c\u4f60\u7684\u4e08\u592b\u8bf4\u53bb\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u5446\u5446\u7684\u671b\u7740\u674e\u9038\uff0c\u5927\u6c57\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5979\u6839\u672c\u5c31\u6ca1\u6709\u542c\u89c1\uff0c\u5979\u5fc3\u4e2d\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\uff1a \u201c\u4ed6\u679c\u7136\u6765\u4e86\uff0c\u4e3a\u4e86\u6211\u6765\u4e86\uff01\u4ed6\u5bf9\u6211\u5982\u6b64\u60c5\u6df1\u4e49\u539a\uff0c\u5440\uff0c\u6211\u5374\u8fd8\u5bf9\u4ed6\u731c\u7591\uff01\u201d \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u540c\u547d\u9e33\u9e2f\u60b2\u547d\u8584 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u540c\u547d\u9e33\u9e2f\u60b2\u547d\u8584 \u3000\u3000\u5979\u8fd9\u6837\u4e00\u60f3\uff0c\u4f46\u89c9\u5185\u759a\u4e8e\u5fc3\uff0c\u5446\u5446\u7684\u671b\u7740\u5979\u7684\u4e08\u592b\uff0c\u4e00\u9897\u9897\u7684\u6cea\u73e0\u6ef4\u4e86\u4e0b\u6765\uff0c\u6b22\u559c\u3001 \u60b2\u4f24\u3001\u60ed\u6127\u3001\u7126\u8651\uff0c\u79cd\u79cd\u9519\u7efc\u590d\u6742\u7684\u60c5\u7eea\uff0c\u6709\u5982\u4e71\u4e1d\u585e\u80f8\uff0c\u5979\u54fd\u54bd\u7740\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u674e\u9038\u4e5f\u77a7\u6e05\u695a\u4e86\uff0c\u8fd9\u7684\u786e\u662f\u957f\u5b59\u58c1\uff0c\u4e0d\u4f1a\u518d\u662f\u5047\u5192\u7684\u4e86\uff01\u4ed6\u6234\u7740\u9563\u94d0\uff0c\u7f13\u7f13\u7684\u8d70\u5230 \u59bb\u5b50\u9762\u524d\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u6211\u6765\u4e86\uff0c\u6211\u62d6\u7d2f\u4e86\u4f60\uff0c\u5f88\u662f\u5bf9\u4f60\u4e0d\u8d77\u3002\u201d\u957f\u5b59\u58c1\u53cc\u7709\u5f00 \u5c55\uff0c\u6cea\u75d5\u672a\u5e72\uff0c\u4fbf\u4f3c\u5e7d\u8c37\u4e2d\u96e8\u540e\u7efd\u5f00\u7684\u767e\u5408\uff0c\u653e\u51fa\u7b11\u5bb9\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u4e86\uff0c\u5f88\u597d\uff0c \u5f88\u597d\uff01\u6211\u80fd\u591f\u548c\u4f60\u6b7b\u5728\u4e00\u8d77\uff0c\u6b7b\u4ea6\u65e0\u61be\uff01\u55ef\uff0c\u8fd8\u6709\u654f\u513f\u5462\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u654f\u513f\u5df2\u7ecf\u6551\u51fa \u53bb\u4e86\u3002\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u90a3\u6211\u5c31\u66f4\u653e\u5fc3\u4e86\uff01\u9038\u54e5\u54e5\uff0c\u4f60\u522b\u8bf4\u4ec0\u4e48\u62d6\u7d2f\u7684\u8bdd\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u8fc7 \u4eca\u5929\u7684\u5feb\u4e50\uff01\u201d\u8fd9\u662f\u771f\u7684\uff0c\u516b\u5e74\u6765\u5979\u4e00\u76f4\u62c5\u7740\u5fc3\u4e8b\uff0c\u5e38\u5e38\u8fd9\u6837\u7684\u60f3\uff1a\u201c\u4ed6\u662f\u65e0\u53ef\u5948\u4f55\u624d \u4e0e\u6211\u7ed3\u5a5a\u7684\uff0c\u8981\u662f\u6b66\u7384\u971c\u6216\u4e0a\u5b98\u5a49\u513f\u6765\u4e86\uff0c\u4ed6\u4f1a\u600e\u4e48\u6837\u5462\uff1f\u201d\u73b0\u5728\u6b66\u7384\u971c\u5df2\u7ecf\u6765\u4e86\uff0c\u4ed6 \u53ef\u5e76\u6ca1\u6709\u5fd8\u8bb0\u81ea\u5df1\uff0c\u4e0d\u4f46\u6ca1\u6709\u5fd8\u8bb0\uff0c\u8fd8\u820d\u4e86\u6027\u547d\u524d\u6765\u76f8\u6551\uff0c\u6b66\u7384\u971c\u662f\u518d\u4e5f\u4e0d\u80fd\u5c06\u4ed6\u62a2\u8d70 \u4e86\uff01 \u3000\u3000\u5927\u6c57\u63a5\u8fde\u7684\u5411\u957f\u5b59\u58c1\u8bf4\u4e86\u51e0\u6b21\uff0c\u5e0c\u671b\u5979\u529d\u544a\u4e08\u592b\uff0c\u957f\u5b59\u58c1\u4e00\u5fc3\u653e\u5728\u5979\u4e08\u592b\u7684\u8eab\u4e0a\uff0c \u5bf9\u65c1\u8fb9\u4e00\u5207\uff0c\u7adf\u4f3c\u662f\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u53c8\u95fb\u5927\u6c57\u6012\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8bf7\u4f60\u4eec\u6765\u8c08\u60c5\u7684\u554a\uff01\u597d\uff0c \u4f60\u4eec\u96be\u820d\u96be\u79bb\uff0c\u6211\u504f\u504f\u8981\u4f60\u4eec\u5206\u5f00\uff0c\u4f60\u4eec\u90fd\u53ef\u4ee5\u9759\u4e0b\u5fc3\u6765\uff0c\u4ed4\u7ec6\u60f3\u60f3\u3002\u201d\u4e00\u58f0\u4ee4\u4e0b\uff0c\u9ebb \u7ffc\u8d5e\u4e0e\u6070\u514b\u56fe\u5c06\u4ed6\u4eec\u592b\u5987\u5206\u522b\u5173\u4e86\u8d77\u6765\uff0c\u5927\u6c57\u72e0\u72e0\u7684\u8bf4\u9053\uff1a\u201c\u4e3a\u7978\u4e3a\u798f\uff0c\u5168\u770b\u4f60\u4eec\u81ea\u5df1 \u4e86\u3002\u4f60\u4eec\u4e00\u65e5\u4e0d\u80af\u5f52\u987a\uff0c\u6211\u5c31\u4e00\u65e5\u4e0d\u653e\u4f60\u4eec\u3002\u8ba9\u4f60\u4eec\u592b\u5987\u53ef\u4ee5\u58f0\u606f\u76f8\u95fb\uff0c\u5982\u4e00\u4e16\u4e5f\u4e0d\u80fd \u89c1\u9762\uff01\u201d \u3000\u3000\u4ed6\u4eec\u88ab\u5173\u5728\u76f8\u90bb\u7684\u5bc6\u5ba4\u91cc\uff0c\u4e2d\u95f4\u9694\u7740\u4e00\u5835\u539a\u539a\u7684\u5899\uff0c\u6069\u7231\u592b\u5987\uff0c\u95fb\u58f0\u800c\u4e0d\u80fd\u89c1\u5f71\uff0c \u8fd9\u5f53\u771f\u662f\u6700\u6b8b\u5fcd\u7684\u6298\u78e8\uff0c\u4f46\u957f\u5b59\u58c1\u6ca1\u6709\u54ed\u6ce3\uff0c\u5979\u53cd\u800c\u5728\u5fc3\u91cc\u7b11\u4e86\u51fa\u6765\uff0c\u5979\u81ea\u89c9\u8fd9\u4e2a\u65f6\u5019\uff0c \u674e\u9038\u624d\u662f\u5b8c\u5168\u5c5e\u4e8e\u5979\u4e86\uff0c\u5979\u5ffd\u5730\u4e3a\u6b66\u7384\u971c\u611f\u5230\u53ef\u601c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5979\u4e07\u91cc\u8fdc\u6765\uff0c\u8fd9\u4e00\u8d9f \u53ef\u662f\u767d\u8d70\u7684\u4e86\uff01\u5979\u73b0\u5728\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u53ef\u60dc\u5979\u6ca1\u6709\u773c\u89c1\u521a\u624d\u7684\u573a\u9762\u5440\uff0c\u6211\u771f\u60f3\u8ba9\u5979\u77e5\u9053\uff0c \u6211\u7684\u674e\u9038\u54e5\u54e5\u5bf9\u6211\u662f\u4f55\u7b49\u60c5\u6df1\u4e49\u91cd\uff01\u201d\u957f\u5b59\u58c1\u53ef\u662f\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c\u6b66\u7384\u971c\u66fe\u7ecf\u60f3\u8fc7\u8981\u8fdb \u5bab\u770b\u5979\uff0c\u800c\u73b0\u5728\u53c8\u66ff\u5979\u7167\u987e\u5b69\u5b50\uff01 \u3000\u3000\u6b66\u7384\u971c\u4e0e\u590f\u4faf\u575a\u5728\u77f3\u7a9f\u4e4b\u4e2d\u7b49\u5019\uff0c\u590f\u4faf\u575a\u5229\u7528\u8fd9\u4e2a\u7a7a\u95f2\u7684\u65f6\u5019\uff0c\u81ea\u5df1\u9ed8\u8fd0\u7384\u529f\uff0c\u62d4 \u6bd2\u7597\u4f24\uff0c\u6b66\u7384\u971c\u5219\u7167\u6599\u5b69\u5b50\uff0c\u5b69\u5b50\u8001\u662f\u95ee\u4e1c\u95ee\u897f\uff0c\u6b66\u7384\u971c\u5fc3\u795e\u4e0d\u5b9a\uff0c\u5e38\u5e38\u95ee\u975e\u6240\u7b54\uff0c\u5b69 \u5b50\u89c9\u5f97\u6ca1\u8da3\uff0c\u4e0d\u4e45\u5c31\u7761\u7740\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u660e\u660e\u77e5\u9053\u4ed6\u4eec\u4e0d\u4f1a\u8fd9\u6837\u5feb\u56de\u6765\uff0c\u4f46\u4ecd\u7136\u5fcd\u4e0d\u4f4f\u6bcf\u9694\u7247\u523b\u5c31\u5230\u6d1e\u53e3\u5f20\u671b\u4e00\u6b21\uff0c \u5979\u8877\u5fc3\u76fc\u671b\u957f\u5b59\u58c1\u80fd\u591f\u8131\u9669\u5f52\u6765\uff0c\u4f46\u53c8\u5bb3\u6015\u548c\u957f\u5b59\u58c1\u89c1\u9762\u65f6\u7684\u5c34\u5c2c\u573a\u9762\u3002\u5979\u8f7b\u8f7b\u629a\u6478\u5b69 \u5b50\u7684\u9762\u5e9e\uff0c\u4e0d\u81ea\u89c9\u7684\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5ffd\u95fb\u5f97\u590f\u4faf\u575a\u4e5f\u662f\u4e00\u58f0\u53f9\u606f\uff0c\u6b66\u7384\u971c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u590f\u4faf\u8001\u4f2f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u4f53\u4e2d\u7684\u6bd2 \u53ef\u90fd\u903c\u51fa\u6765\u4e86\u3002\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u8eab\u4f53\u4e0a\u6240\u53d7\u7684\u4f24\uff0c\u4e0d\u8bba\u600e\u4e48\u5389\u5bb3\uff0c\u90fd\u662f\u533b\u5f97\u597d\u7684\u3002\u201d\u6b66 \u7384\u971c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u4f2f\u4f60\u662f\u5929\u4e0b\u7b2c\u4e00\u795e\u533b\uff0c\u4e16\u4e0a\u6ca1\u6709\u4f60\u533b\u4e0d\u597d\u7684\u75c5\u3002\u201d\u590f\u4faf\u575a\u597d\u4f3c\u81ea\u8a00 \u81ea\u8bed\u7684\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u5fc3\u4e0a\u7684\u4f24\u5c31\u96be\u533b\u4e86\uff0c\u6211\u5c31\u533b\u4e0d\u597d\u81ea\u5df1\u5fc3\u4e0a\u7684\u4f24\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u590f\u4faf\u575a\u53c8\u53f9\u4e86\u4e00\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6b66\u7384\u971c\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4f60\u7b2c\u4e00\u6b21 \u89c1\u6211\u7684\u4e8b\u4e48\uff1f\u201d\u6b66\u7384\u971c\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u975e\u5e38\u6e05\u695a\u3002\u201d\u90a3\u65f6\uff0c\u590f\u4faf\u575a\u95ee\u5979\u7684\u5e08\u7236\uff0c \u6b66\u7384\u971c\u7528\u82b1\u73af\u6392\u51fa\u201c\u4e0d\u53ef\u8bf4\uff1f\u4e0d\u53ef\u8bf4\uff1f\u201d\u516d\u4e2a\u5b57\u5f62\uff0c\u800c\u590f\u4faf\u575a\u7528\u82b1\u73af\u6392\u51fa\u201c\u5982\u4e4b\u4f55\uff1f\u5982 \u4e4b\u4f55\uff1f\u201d\u516d\u5b57\u3002\u590f\u4faf\u575a\u9053\uff1a\u201c\u90a3\u65f6\uff0c\u6211\u662f\u65e0\u53ef\u5948\u4f55\u7684\u5fc3\u60c5\uff0c\u5b69\u5b50\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u6b66\u7384\u971c \u9053\uff1a\u201c\u6211\u660e\u767d\u3002\u201d\u590f\u4faf\u575a\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6b21\u4f60\u9001\u674e\u9038\u6765\u6c42\u533b\uff0c\u6211\u5f53\u65f6\u5c31\u60f3\uff0c\u674e \u9038\u7684\u75c5\u6211\u6709\u628a\u63e1\u533b\u597d\uff0c\u4f60\u7684\u75c5\u5374\u96be\u533b\u5f97\u5f88\uff0c\u6240\u4ee5\u6211\u5f53\u65f6\u53ea\u7559\u4e0b\u674e\u9038\uff0c\u5bf9\u4f60\u7684\u75c5\uff0c\u5374\u662f\u8fde \u95ee\u4e5f\u4e0d\u6562\u591a\u95ee\u3002\u201d\u6b66\u7384\u971c\u5fc3\u5934\u4e00\u9707\uff0c\u5979\u61c2\u5f97\u590f\u4faf\u575a\u7684\u542b\u610f\u4e86\uff0c\u9762\u4e0a\u4e0d\u7981\u98de\u8d77\u4e00\u7247\u7ea2\u971e\uff0c \u590f\u4faf\u575a\u8f7b\u58f0\u95ee\u9053\uff1a\u201c\u7384\u971c\uff0c\u4f60\u73b0\u5728\u5fc3\u91cc\u5f88\u96be\u8fc7\uff0c\u662f\u4e48\uff1f\u201d\u6b66\u7384\u971c\u751a\u89c9\u5c34\u5c2c\uff0c\u52c9\u5f3a\u6291\u5236\u4e0b \u6fc0\u52a8\u7684\u5fc3\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u5c06\u957f\u5b59\u58c1\u6551\u51fa\u6765\uff0c\u6211\u5fc3\u91cc\u7684\u786e\u5f88\u662f\u96be\u8fc7\u3002\u201d\u590f\u4faf\u575a\u82e5\u6709\u6df1 \u610f\u7684\u671b\u5979\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5fc3\u4e0a\u7684\u4f24\u672a\u66fe\u533b\u597d\uff0c\u6551\u51fa\u6765\u4e86\uff0c\u4f60\u4ecd\u7136\u4f1a\u96be\u8fc7\u7684\u3002\u55ef\uff0c\u6211\u662f \u8fc7\u6765\u4eba\u4e86\u3002\u201d\u6b66\u7384\u971c\u7ed9\u4ed6\u8bf4\u5230\u5fc3\u574e\u91cc\u53bb\uff0c\u6014\u6014\u7684\u65e0\u8bdd\u53ef\u4ee5\u8fa9\u89e3\uff0c\u5509\uff0c\u5979\u8fd9\u65f6\u5019\u7684\u5fc3\u60c5\u786e \u662f\u50cf\u590f\u4faf\u575a\u5f53\u5e74\u4e00\u6837\uff0c\u90a3\u662f\u4e00\u7247\u65e0\u53ef\u5948\u4f55\u7684\u5fc3\u60c5\uff01 \u3000\u3000\u590f\u4faf\u575a\u62ac\u8d77\u773c\u775b\uff0c\u8138\u4e0a\u5ffd\u7136\u6cdb\u51fa\u4e00\u5c42\u5947\u5f02\u7684\u5149\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5fc3\u5934\u521b\u4f24\uff0c\u5509\uff0c\u51e0\u5341 \u5e74\u6765\uff01\u73b0\u5728\u624d\u5b8c\u5168\u5e73\u590d\u3002\u4f60\u77e5\u9053\u662f\u8c01\u533b\u597d\u6211\u7684\u5417\uff1f\u201d\u4e0d\u5f85\u6b66\u7384\u971c\u8bf4\u8bdd\uff0c\u81ea\u5df1\u53c8\u81ea\u95ee\u81ea\u7b54 \u9053\uff1a\u201c\u662f\u4f60\u7684\u5e08\u7236\uff0c\u5979\u5728\u6b7b\u540e\u533b\u597d\u4e86\u6211\u5fc3\u4e0a\u7684\u521b\u4f24\u3002\u6211\u7ffb\u9605\u4e86\u4f60\u5e08\u7236\u9057\u7559\u4e0b\u7684\u90a3\u672c\u8bd7\u96c6\uff0c \u6211\u63a5\u53d7\u4e86\u4f60\u5e08\u7236\u9001\u7ed9\u6211\u7684\u73cd\u8d35\u836f\u7269\uff0c\u8fd9\u4e9b\u836f\u7269\u4e0d\u4f46\u672c\u8eab\u662f\u65e0\u4ef7\u4e4b\u5b9d\uff0c\u4e5f\u533b\u597d\u4e86\u6211\u5fc3\u4e0a\u7684 \u521b\u4f24\u3002\u56e0\u4e3a\u6211\u660e\u767d\u4e86\u4e00\u4ef6\u4e8b\u60c5\uff1a\u2018\u77e5\u5df1\u670b\u53cb\u7684\u60c5\u8c0a\uff0c\u5e76\u4e0d\u89c1\u5f97\u5c31\u900a\u4e8e\u592b\u5987\u7684\u60c5\u7231\uff01\u2019\u201d \u3000\u3000\u6b66\u7384\u971c\u542c\u4e86\u4ed6\u8fd9\u4e24\u53e5\u8bdd\uff0c\u597d\u50cf\u7ed9\u4ed6\u7528\u91d1\u9488\u523a\u4e86\u4e00\u4e0b\u4f3c\u7684\uff0c\u53ef\u662f\u90a3\u662f\u6cbb\u75c5\u7684\u91d1\u9488\uff0c\u91d1 \u9488\u624e\u5728\u5979\u7684\u5fc3\u5934\uff0c\u75db\u82e6\u4e4b\u4e2d\u5374\u53c8\u611f\u5230\u8212\u670d\uff0c\u5979\u660e\u767d\u590f\u4faf\u575a\u662f\u7528\u81ea\u5df1\u7684\u4e8b\u60c5\uff0c\u73b0\u8eab\u8bf4\u6cd5\u6765 \u70b9\u5316\u5979\u3002\u5979\u60f3\u8d77\u590f\u4faf\u575a\u548c\u5979\u5e08\u7236\u7684\u60c5\u5b7d\uff0c\u53c8\u60f3\u8d77\u81ea\u5df1\u548c\u674e\u9038\u4e4b\u95f4\u7684\u7231\u6068\u7ea0\u7f20\uff0c\u60c5\u5f62\u6709\u8bb8 \u591a\u76f8\u4f3c\uff0c\u4f46\u6709\u4e00\u6837\u4e0d\u540c\u7684\u662f\uff0c\u5979\u5e08\u7236\u5728\u8ba4\u8bc6\u590f\u4faf\u575a\u4e4b\u524d\uff0c\u5fc3\u4e0a\u65e9\u5df2\u6709\u4e86\u4e00\u4e2a\u5c09\u8fdf\u70af\u4e86\u3002 \u674e\u9038\u5728\u8ba4\u8bc6\u81ea\u5df1\u7684\u65f6\u5019\uff0c\u53ea\u6015\u6839\u672c\u8fd8\u672b\u66fe\u60f3\u5230\u4f1a\u4e0e\u957f\u5b59\u58c1\u7ed3\u6210\u592b\u59bb\u3002 \u3000\u3000\u4f46\u4e8b\u60c5\u90fd\u5df2\u6210\u5b9a\u5c40\u4e86\uff0c\u518d\u60f3\u8fd9\u4e9b\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u8fc7\u4e86\u534a\u664c\uff0c\u6b66\u7384\u971c\u4e5f\u62ac\u8d77\u5934\u6765\uff0c\u7f13 \u7f13\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\u653e\u5fc3\uff0c\u6211\u7684\u521b\u4f24\u4e5f\u4f1a\u6162\u6162\u6062\u590d\u7684\uff01\u201d \u3000\u3000\u590f\u4faf\u575a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5c81\u6708\u65e0\u60c5\uff0c\u4e00\u4e2a\u4eba\u8981\u505a\u7684\u4e8b\u60c5\u5f88\u591a\uff0c\u662f\u5e94\u8be5\u65e9\u70b9\u628a\u81ea\u5df1\u533b\u597d\u3002\u201d \u4ed6\u4e3a\u4e86\u5e73\u590d\u6b66\u7384\u971c\u6fc0\u52a8\u7684\u5fc3\u60c5\uff0c\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\u540e\uff0c\u968f\u5373\u8f6c\u6362\u8bdd\u9898\uff0c\u548c\u5979\u8c08\u4e00\u4e9b\u6b66\u6797\u4e2d\u7684 \u5947\u95fb\u5f02\u4e8b\uff0c\u4ee5\u53ca\u81ea\u5df1\u6240\u8bca\u6cbb\u8fc7\u7684\u4e00\u4e9b\u602a\u75c5\uff0c\u6b66\u7384\u971c\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u968f\u540e\u590f\u4faf\u575a\u53c8\u8c08\u5230\u81ea \u5df1\u6240\u5236\u70bc\u7684\u4e00\u4e9b\u7075\u4e39\u5999\u836f\uff0c\u4ee5\u53ca\u5728\u533b\u672f\u4e0a\u7684\u65b0\u53d1\u73b0\u7b49\u7b49\uff0c\u6b66\u7384\u971c\u66f4\u662f\u5927\u611f\u5174\u8da3\uff0c\u4e0d\u65f6\u7684 \u63d0\u51fa\u4e00\u4e9b\u7591\u96be\u95ee\u9898\u95ee\u4ed6\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u591c\u5e55\u964d\u4e0b\uff0c\u590f\u4faf\u575a\u5c06\u6d1e\u4e2d\u7684\u67af\u679d\u8d25\u53f6\u626b\u5728\u4e00\u8d77\uff0c\u71c3\u8d77\u4e86\u4e00\u5806\u706b\u6765\uff0c\u6b66 \u7384\u971c\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u6628\u591c\u5165\u5bab\u7aa5\u63a2\uff0c\u81f3\u8fdf\u4eca\u65e9\u5c31\u5e94\u8be5\u79bb\u5f00\u7a81\u53a5\u7684\u738b\u5ef7\uff0c\u7167\u8ba1\u8def \u7a0b\uff0c\u73b0\u5728\u4e5f\u5e94\u8be5\u56de\u5230\u8fd9\u513f\u4e86\uff0c\u600e\u7684\u8fd8\u4e0d\u89c1\u4ed6\u4eec\u56de\u6765\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u590f\u4faf\u575a\u7b11\u9053\uff1a\u201c\u6211\u542c\u5230\u4ed6\u4eec\u7684\u811a\u6b65\u58f0\u4e86\u3002\u201d\u968f\u5373\u53d1\u58f0\u957f\u5578\uff0c\u58f0\u97f3\u67d4\u548c\uff0c \u4f46\u77f3\u58c1\u7684\u56de\u58f0\u5374\u60a0\u957f\u4e0d\u7edd\uff0c\u6b66\u7384\u971c\u4e00\u542c\uff0c\u4fbf\u77e5\u4ed6\u7684\u529f\u529b\u5df2\u6062\u590d\u4e86\u4e03\u516b\u6210\uff0c\u5fc3\u60f3\u6b64\u8001\u533b\u672f \u5f53\u771f\u662f\u795e\u5999\u65e0\u6bd4\uff0c\u53d7\u4e86\u5929\u6076\u9053\u4eba\u7684\u5267\u6bd2\uff0c\u5c45\u7136\u4e00\u65e5\u4e00\u591c\u4e4b\u95f4\uff0c\u4fbf\u90fd\u62d4\u9664\u51c0\u5c3d\uff0c\u540c\u65f6\u5bf9\u4ed6 \u201c\u4f0f\u5730\u542c\u58f0\u201d\u7684\u672c\u9886\uff0c\u4e5f\u6781\u4e3a\u4f69\u670d\u3002 \u3000\u3000\u6b66\u7384\u971c\u6025\u4e0d\u53ca\u5f85\uff0c\u51fa\u6d1e\u76f8\u8fce\uff0c\u4f46\u89c1\u8fdc\u8fdc\u7684\u5954\u6765\u4e09\u6761\u9ed1\u5f71\uff0c\u6b66\u7384\u971c\u5fc3\u5934\u4e00\u6c89\uff0c\u5c1a\u672a\u8fa8 \u5f97\u51fa\u662f\u54ea\u4e00\u4e2a\u6ca1\u6709\u56de\u6765\u3002\u8fdc\u8fdc\u7684\u4fbf\u542c\u5f97\u8c37\u795e\u7fc1\u7684\u58f0\u97f3\u558a\u9053\uff1a\u201c\u674e\u9038\u56de\u6765\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u6d51\u8eab\u53d1\u51b7\uff0c\u8f6c\u77ac\u95f4\u4ed6\u4eec\u5df2\u6765\u5230\u6d1e\u524d\uff0c\u56de\u6765\u7684\u53ea\u662f\u7b26\u4e0d\u7591\u3001\u8c37\u795e\u7fc1\u3001\u88f4\u53d4\u5ea6\u4e09 \u4eba\uff0c\u5e76\u6ca1\u6709\u674e\u9038\uff01\u6b66\u7384\u971c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u674e\u9038\uff0c\u4ed6\u3001\u4ed6\u4e0d\u662f\u548c\u4f60\u4eec\u5728\u4e00\u8d77\u5417\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a \u201c\u6211\u4eec\u5c31\u662f\u56e0\u4e3a\u4e00\u8def\u7b49\u4ed6\uff0c\u6240\u4ee5\u56de\u6765\u665a\u4e86\u3002\u6211\u4eec\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u5148\u56de\u6765\u4e86\u5462\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a \u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u7b26\u4e0d\u7591\u9053\uff1a\u201c\u8fdb\u53bb\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u8fdb\u4e86\u77f3\u7a9f\u5750\u5b9a\uff0c\u7b26\u4e0d\u7591\u9053\uff1a\u201c\u4ed6\u5728\u5fa1\u82d1\u91cc\u88ab\u9633\u592a\u534e\u53d1\u73b0\uff0c\u6253\u8d77\u6765\uff0c\u6211\u501f\u52a9\u4e86\u4ed6\u4e00\u81c2 \u4e4b\u529b\uff0c\u5c06\u9633\u592a\u534e\u51fb\u5012\uff0c\u968f\u540e\u767e\u5fe7\u4e0a\u4eba\u4fbf\u8d76\u4e86\u5230\u6765\uff0c\u5c55\u5f00\u4e86\u4e00\u573a\u6df7\u6218\uff0c\u5728\u5927\u6c57\u7684\u5fa1\u82d1\u91cc\u51e0 \u4e4e\u95f9\u4e86\u4e00\u4e2a\u66f4\u6b21\uff0c\u5374\u4e00\u76f4\u4e0d\u89c1\u674e\u9038\u9732\u9762\u3002\u201d\u4ed6\u4eec\u8ddf\u7740\u8be6\u8bf4\u5728\u5bab\u4e2d\u6fc0\u6218\u7684\u60c5\u5f62\uff0c\u4ed6\u4eec\u90a3\u8fb9 \u867d\u7136\u6298\u4e86\u4e00\u4e2a\u5929\u6076\u9053\u4eba\uff0c\u4f46\u5374\u8865\u4e0a\u4e86\u83e9\u63d0\u4e0a\u4eba\u548c\u706d\u5ea6\u795e\u541b\uff0c\u83e9\u63d0\u4e0a\u4eba\u7684\u529f\u529b\u6bd4\u5929\u6076\u9053\u4eba \u5c1a\u8981\u7a0d\u80dc\u4e00\u7b79\uff0c\u706d\u5ea6\u795e\u541b\u8f83\u5f31\uff0c\u4f46\u4e5f\u53ef\u4ee5\u548c\u88f4\u53d4\u5ea6\u6253\u6210\u5e73\u624b\uff0c\u56e0\u6b64\u6df7\u6218\u8d77\u6765\uff0c\u7adf\u662f\u4ed6\u4eec \u90a3\u8fb9\u5360\u5c3d\u4e0a\u98ce\uff0c\u52a0\u4ee5\u4e00\u5f00\u9996\u7b26\u4e0d\u7591\u7ed9\u767e\u5fe7\u4e0a\u4eba\u7684\u5185\u529b\u9707\u5f97\u7565\u53d7\u635f\u4f24\uff0c\u82e5\u7136\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea \u6015\u8981\u88ab\u4e00\u7f51\u6210\u64d2\uff0c\u6240\u4ee5\u4ed6\u4eec\u624d\u987e\u4e0d\u5f97\u518d\u627e\u674e\u9038\uff0c\u53ea\u597d\u5148\u6c42\u8131\u9669\u518d\u8bf4\u3002 \u3000\u3000\u4e8b\u6001\u5982\u65af\uff0c\u5927\u5bb6\u90fd\u60f3\u5230\u674e\u9038\u4e8e\u662f\u51f6\u591a\u5409\u5c11\uff0c\u6b66\u7384\u971c\u5c24\u5176\u96be\u8fc7\uff0c\u7adf\u4f3c\u662f\u5931\u9b42\u843d\u9b44\u4e00\u822c\uff01 \u3000\u3000\u88f4\u53d4\u5ea6\u6700\u7559\u610f\u6b66\u7384\u971c\uff0c\u89c1\u5979\u5982\u6b64\uff0c\u6697\u6697\u53f9\u606f\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u7b26\u8001\u524d\u8f88\uff0c \u4f60\u7684\u592a\u6e05\u5251\u6cd5\u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u4e3a\u4f55\u8fd9\u6b21\u820d\u5251\u4e0d\u7528\uff1f\u201d\u7b26\u4e0d\u7591\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236\u8fd8\u672a\u66fe \u548c\u4f60\u8bf4\u8fc7\u4e48\uff1f\u6709\u5979\u8001\u4eba\u5bb6\u5728\uff0c\u6211\u8fd8\u7109\u6562\u7528\u5251\uff1f\u201d\u539f\u6765\u8fdc\u5728\u4e09\u516b\u5e74\u524d\uff0c\u7b26\u4e0d\u7591\u3001\u5c09\u8fdf\u70af\u3001 \u8c37\u795e\u7fc1\u3001\u957f\u5b59\u5747\u91cf\u56db\u4eba\u90fd\u4ee5\u5251\u672f\u9a70\u540d\uff0c\u88ab\u6b66\u6797\u4eba\u58eb\u516c\u8ba4\u4e3a\u5f53\u4e16\u56db\u5927\u540d\u5bb6\uff0c\u7b26\u4e0d\u7591\u66f4\u662f\u56db \u5927\u540d\u5bb6\u4e4b\u9996\u3002\u540e\u6765\u5c09\u8fdf\u70af\u5728\u5317\u5929\u5c71\u9690\u5c45\uff0c\u7b26\u4e0d\u7591\u5728\u5357\u5929\u5c71\u9690\u5c45\uff0c\u6709\u4e00\u6b21\u5c09\u8fdf\u70af\u53bb\u8bbf\u4ed6\uff0c \u4e0e\u4ed6\u5207\u78cb\u65b0\u521b\u7684\u51e0\u62db\u5229\u6cd5\uff0c\u6bd4\u8bd5\u4e86\u534a\u5929\uff0c\u7b26\u4e0d\u7591\u8d62\u4e86\u4e00\u62db\uff0c\u4f46\u5c09\u8fdf\u70af\u8ba4\u4e3a\u4ed6\u867d\u7136\u8d62\u4e86\uff0c \u5251\u62db\u4e2d\u4ecd\u6709\u7834\u7efd\uff0c\u4e24\u4eba\u76f8\u7ea6\uff0c\u5728\u5341\u5e74\u540e\uff0c\u5404\u4ee5\u65b0\u521b\u7684\u5251\u6cd5\u518d\u6bd4\u4e00\u573a\uff0c\u60f3\u4e0d\u5230\u672a\u6ee1\u5341\u5e74\u4e4b \u671f\uff0c\u5c09\u8fdf\u70af\u5148\u5df2\u6b7b\u4e86\u3002\u7b26\u4e0d\u7591\u53bb\u4e0a\u575f\uff0c\u9047\u5230\u4f18\u4e91\u8001\u5c3c\uff0c\u8bf4\u8d77\u5f53\u5e74\u4e4b\u4e8b\uff0c\u4f18\u4e91\u8001\u5c3c\u77e5\u9053\u5c09 \u8fdf\u70af\u66fe\u4e3a\u6b64\u4e8b\u803f\u803f\u4e8e\u5fc3\uff0c\u4fbf\u4ee3\u5c09\u8fdf\u70af\u4e86\u7ed3\u751f\u524d\u7684\u5fc3\u613f\uff0c\u4e0e\u4ed6\u6bd4\u8bd5\u4e00\u573a\uff0c\u7ed3\u679c\u4f18\u4e91\u8001\u5c3c\u4ee5 \u65b0\u521b\u7684\u4f5b\u95e8\u65e0\u76f8\u5251\u6cd5\u8d62\u4e86\u7b26\u4e0d\u7591\u4e00\u62db\uff0c\u7b26\u4e0d\u7591\u63b7\u5251\u53f9\u9053\uff1a\u201c\u5b50\u671f\u6b7b\u540e\uff0c\u4f2f\u7259\u7ec8\u751f\u4e0d\u518d\u9f13 \u7434\uff0c\u4f55\u51b5\u8fd8\u6709\u9ad8\u4eba\u80dc\u6211\uff01\u201d\u4ece\u6b64\u4ed6\u4e5f\u4e0d\u518d\u7528\u5251\u3002 \u3000\u3000\u8c37\u795e\u7fc1\u542c\u4ed6\u63d0\u8d77\u6b64\u4e8b\uff0c\u6447\u4e86\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u8001\u7b26\uff0c\u4f60\u4e5f\u592a\u8fc2\u8150\u4e86\uff0c\u4f60\u867d\u7136\u4e3a\u4e86\u60bc\u5ff5 \u77e5\u5df1\uff0c\u4f24\u5fc3\u4e4b\u4f59\uff0c\u4e0d\u80af\u7528\u5251\uff0c\u4f46\u5982\u4eca\u662f\u4e3a\u4e86\u6551\u5c09\u8fdf\u70af\u7684\u5f92\u5f1f\uff0c\u7834\u4e00\u7834\u4f8b\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u800c \u4e14\u4f18\u4e91\u8001\u5c3c\u4e5f\u5df2\u6b7b\u4e86\u3002\u201d\u7b26\u4e0d\u7591\u9053\uff1a\u201c\u6211\u81ea\u8ba4\u5251\u672f\u5c1a\u672a\u5230\u5bb6\uff0c\u65e0\u989c\u518d\u7528\u3002\u4f55\u51b5\u6211\u7eb5\u7136\u4f7f \u5251\uff0c\u4e5f\u672a\u5fc5\u8d62\u5f97\u4e86\u767e\u4f18\u4e0a\u4eba\u3002\u201d \u3000\u3000\u6590\u53d4\u5ea6\u8bf4\u9053\uff1a\u201c\u6211\u5e08\u7236\u751f\u524d\u8bc4\u8bba\u5f53\u4ee3\u5251\u672f\u540d\u5bb6\uff0c\u5979\u6700\u4f69\u670d\u7684\u5c31\u662f\u4f60\uff0c\u5979\u8bf4\u90a3\u6b21\u8d62\u4e86 \u4f60\u7684\u4e00\u62db\uff0c\u5b9e\u5728\u662f\u975e\u5e38\u4fa5\u5e78\u3002\u201d\u7b26\u4e0d\u7591\u53cc\u7709\u4e00\u5c55\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5f53\u771f\u662f\u8fd9\u4e48\u8bf4\uff1f\u55ef\uff0c \u4f60\u5e08\u7236\u662f\u6545\u610f\u7ed9\u6211\u4fdd\u5b58\u9762\u5b50\uff0c\u5979\u786e\u786e\u5b9e\u5b9e\u662f\u7528\u771f\u672c\u9886\u8d62\u4e86\u6211\uff0c\u54ea\u91cc\u662f\u4ec0\u4e48\u4fa5\u5e78\uff1f\u201d\u7b26\u4e0d \u7591\u867d\u7136\u8fd9\u6837\u731c\u60f3\u4f18\u4e91\u8001\u5c3c\u7684\u7528\u610f\uff0c\u5374\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u82b1\u6012\u653e\u3002 \u3000\u3000\u6590\u53d4\u5ea6\u8bf4\u9053\uff1a\u201c\u6211\u5e08\u7236\u5728\u4e34\u7ec8\u4e4b\u524d\u5429\u5490\u8fc7\u6211\uff0c\u5979\u4f20\u4e86\u6211\u7684\u65e0\u76f8\u5251\u6cd5\uff0c\u53eb\u6211\u82e5\u6709\u4e0d\u660e \u4e4b\u5904\uff0c\u4fbf\u8bf7\u4f60\u6307\u6559\u3002\u4f60\u77e5\u9053\u5979\u8001\u4eba\u5bb6\u521b\u4e86\u65e0\u76f8\u5251\u6cd5\u4e4b\u540e\uff0c\u6ca1\u591a\u4e45\u4fbf\u6b7b\u4e86\u3002\u8fd9\u5957\u5251\u6cd5\u592a\u8fc7 \u535a\u5927\u7cbe\u6df1\uff0c\u6211\u94bb\u7814\u4e86\u591a\u5e74\uff0c\u5c1a\u6709\u4e00\u5904\u672a\u660e\uff0c\u7b26\u8001\u524d\u8f88\uff0c\u4f60\u867d\u7136\u4e0d\u613f\u7528\u5251\uff0c\u6307\u70b9\u665a\u8f88\u4e00\u6b21\uff0c \u603b\u53ef\u4ee5\u5427\uff1f\u201d \u3000\u3000\u7b26\u4e0d\u7591\u672c\u662f\u4e2a\u6781\u7231\u597d\u5251\u672f\u7684\u4eba\uff0c\u542c\u4e86\u6b64\u8bdd\uff0c\u5fc3\u75d2\u96be\u71ac\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u7684\u5251\u672f\u795e\u5947 \u83ab\u6d4b\uff0c\u6211\u4e5f\u4e0d\u77e5\u662f\u5426\u80fd\u61c2\uff0c\u4f60\u8bf4\u8bf4\u770b\uff0c\u662f\u54ea\u4e00\u5904\u4f60\u4e0d\u660e\u767d\uff0c\u54b1\u4eec\u5207\u78cb\u5207\u78cb\u3002\u201d\u88f4\u53d4\u5ea6\u53eb \u6b66\u7384\u971c\u53d6\u51fa\u5e08\u7236\u90a3\u672c\u65e0\u76f8\u5251\u8c31\uff0c\u63ed\u5f00\u4e00\u9875\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u5957\u4e24\u4eba\u5408\u4f7f\u7684\u5251\u672f\uff0c\u6211\u600e\u4e48\u6837 \u4e5f\u4e0d\u660e\u767d\u3002\u201d\u7b26\u4e0d\u7591\u53d6\u6765\u4e00\u770b\uff0c\u4e0d\u89c9\u5fc3\u9189\u795e\u9a70\uff0c\u8fde\u58f0\u8d5e\u5999\uff01 \u3000\u3000\u8fd9\u5957\u4e24\u4eba\u5408\u4f7f\u7684\u5251\u672f\uff0c\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u865a\u5b9e\u83ab\u6d4b\uff0c\u7b26\u4e0d\u7591\u770b\u4e86\u4e00\u904d\uff0c\u4e0d\u7981\u53eb\u8d77\u6765 \u9053\uff1a\u201c\u8001\u8c37\uff0c\u4f60\u4e5f\u6765\u770b\uff0c\u8fd9\u5957\u5251\u672f\u65bd\u5c55\u5f00\u6765\uff0c\u53ea\u6015\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u4e5f\u96be\u4ee5\u51b2\u7834\u53cc\u5251\u5408\u58c1 \u7684\u5305\u56f4\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u8d77\u521d\u9887\u4e3a\u7591\u8651\uff0c\u539f\u6765\u8fd9\u5957\u5251\u672f\u4e43\u662f\u4f18\u4e91\u8001\u5c3c\u4e3a\u4e86\u51c6\u5907\u4e0e\u5c09\u8fdf\u70af\u8054\u5251\u5bf9\u654c\u800c\u521b \u7684\uff0c\u64b7\u53d6\u4e86\u5c09\u8fdf\u70af\u5ce8\u5d4b\u5251\u6cd5\u4e0e\u5979\u81ea\u5df1\u6240\u521b\u7684\u4f5b\u95e8\u65e0\u76f8\u5251\u6cd5\u7684\u7cbe\u534e\uff0c\u7aef\u7684\u662f\u5965\u5999\u65e0\u6bd4\uff0c\u53ef \u60dc\u4ed6\u4eec\u4e8c\u4eba\u81f3\u6b7b\u672a\u66fe\u590d\u5408\uff0c\u8fd9\u5957\u5251\u6cd5\u672a\u6709\u673a\u4f1a\u7528\u8fc7\uff0c\u6b66\u7384\u971c\u5728\u5f97\u5230\u5e08\u7236\u6240\u9057\u7559\u7684\u5251\u8c31\u4e4b \u540e\uff0c\u66fe\u7528\u5fc3\u94bb\u7814\uff0c\u5bf9\u8fd9\u5957\u6df1\u5965\u7684\u5251\u6cd5\uff0c\u770b\u4e0d\u660e\u767d\uff0c\u5f53\u65f6\u88f4\u53d4\u5ea6\u4ea6\u66fe\u7ed9\u5979\u8be6\u7ec6\u8bb2\u89e3\u8fc7\u7684\uff0c \u662f\u4ee5\u5979\u521d\u65f6\u5fc3\u4e2d\u7591\u60d1\uff1a\u201c\u5e08\u5144\u660e\u660e\u61c2\u5f97\uff0c\u5374\u4e3a\u4f55\u8981\u8bf7\u7b26\u4e0d\u7591\u6307\u70b9\uff1f\u201d\u8fd9\u65f6\u542c\u4e86\u7b26\u4e0d\u7591\u7684 \u8bdd\uff0c\u65b9\u59cb\u731c\u5230\u4e86\u5e08\u5144\u7684\u7528\u610f\u3002\u5fc3\u60f3\uff1a\u201c\u662f\u4e86\uff0c\u5b9a\u662f\u5e08\u5144\u60f3\u7b26\u4e0d\u7591\u5b66\u4e86\u8fd9\u5957\u5251\u6cd5\uff0c\u597d\u5236\u670d \u767e\u4f18\u4e0a\u4eba\uff01\u201d\u8981\u77e5\u7b26\u4e0d\u7591\u662f\u4e00\u4ee3\u5251\u672f\u5927\u5bb6\uff0c\u82e5\u975e\u85c9\u53e3\u8bf7\u4ed6\u6307\u70b9\uff0c\u4ed6\u53c8\u600e\u80af\u79c1\u9605\u4f18\u4e91\u8001\u5c3c \u7684\u5251\u8c31\uff1f\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u7b26\u4e0d\u7591\u8bf4\u9053\uff1a\u201c\u53d4\u5ea6\uff0c\u8fd9\u5957\u5251\u672f\u592a\u8fc7\u5965\u5999\u590d\u6742\uff0c\u96be\u602a\u4f60\u4e0d\u660e \u767d\uff0c\u6211\u770b\u662f\u770b\u5f97\u61c2\uff01\u4f46\u8981\u7ec3\u4f1a\u4e86\u62db\u5f0f\uff0c\u624d\u80fd\u7ed9\u4f60\u8bb2\u89e3\uff0c\u6700\u5c11\u4e5f\u6015\u5f97\u5728\u4e09\u65e5\u4e4b\u540e\uff0c\u8001\u8c37\uff0c \u8fd9\u662f\u4e24\u4eba\u5408\u7ec3\u7684\u5251\u6cd5\uff0c\u4f60\u53ef\u5f97\u7ed9\u6211\u5582\u62db\u3002\u201d\u6b66\u7384\u971c\u5fc3\u5934\u4e00\u6c89\u3002\u60f3\u5230\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u7b26\u4e0d \u7591\u6700\u5c11\u5fc5\u8981\u5728\u4e09\u65e5\u4e4b\u540e\uff0c\u624d\u80fd\u5e94\u7528\u8fd9\u5957\u5251\u6cd5\uff0c\u7f13\u4e0d\u6d4e\u6025\uff0c\u5982\u4f55\u662f\u597d\uff1f\u201d\u8981\u77e5\u8fd9\u5957\u5251\u6cd5\u4e43 \u662f\u4f18\u4e91\u8001\u5c3c\u51c6\u5907\u4e0e\u5c09\u8fdf\u70af\u5408\u7528\u7684\uff0c\u4ed6\u4eba\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u4f7f\u7528\u8fd9\u5957\u5251\u6cd5\uff0c\u5a01\u529b\u624d\u80fd\u5c3d \u91cf\u53d1\u6325\uff0c\u81f3\u4e8e\u88f4\u53d4\u5ea6\u4e0e\u6b66\u7384\u971c\u4e8c\u4eba\uff0c\u5219\u56e0\u529f\u529b\u672a\u5230\uff0c\u7eb5\u7136\u52c9\u5f3a\u80fd\u591f\u4f7f\u7528\uff0c\u4e5f\u65ad\u65ad\u4e0d\u80fd\u514b \u5236\u767e\u5fe7\u4e0a\u4eba\u7684\u3002\u8fd9\u4e5f\u6b63\u662f\u88f4\u53d4\u5ea6\u6697\u4f7f\u5fc3\u8ba1\uff0c\u8981\u7b26\u4e0d\u7591\u5b66\u8fd9\u5957\u5251\u672f\u7684\u7406\u7531\u3002 \u3000\u3000\u5f53\u591c\uff0c\u6b66\u7384\u971c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e00\u591c\u65e0\u7720\uff0c\u6574\u665a\u76d8\u7b97\u7528\u4ec0\u4e48\u529e\u6cd5\u6765\u6551\u674e\u9038\uff0c\u7b26\u8c37\u4e8c\u8001\u5219\u5c06 \u8fd9\u5957\u5251\u6cd5\u53cd\u8986\u62c6\u89e3\uff0c\u4e5f\u662f\u5230\u4e86\u6df1\u591c\u624d\u7761\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u8d77\u6765\uff0c\u4f17\u4eba\u5ffd\u7136\u53d1\u89c9\u6b66\u7384\u971c\u4e0d\u89c1\u4e86\uff0c\u590f\u4faf\u575a\u5fc3\u5934\u4e00\u9707\uff0c\u6478\u4e00\u6478\u4ed6\u7684\u836f\u56ca\uff0c\u82e6 \u7b11\u9053\uff1a\u201c\u7384\u971c\u771f\u662f\u5927\u80c6\uff0c\u5979\u7adf\u7136\u5077\u8d70\u4e86\u6211\u7684\u65ad\u9b42\u6563\u4e86\uff01\u201d\u8c37\u795e\u7fc1\u9053\uff1a\u201c\u65ad\u9b42\u6563\u662f\u4f5c\u4ec0\u4e48 \u7528\u7684\uff1f\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u65ad\u9b42\u6563\u7ed9\u4eba\u670d\u4e86\uff0c\u7acb\u5373\u6c14\u7edd\u8eab\u4ea1\uff0c\u4f46\u5374\u4e0d\u662f\u771f\u6b7b\uff0c\u53ea\u8981\u5728\u4e03\u65e5\u4e4b \u5185\uff0c\u7528\u6211\u7684\u8fd8\u9b42\u4e39\u89e3\u6551\uff0c\u4fbf\u53ef\u4ee5\u590d\u6d3b\u3002\u6211\u6628\u65e5\u65e0\u610f\u4e4b\u4e2d\u7ed9\u5979\u8bb2\u4e86\u8fd9\u4e24\u6837\u5947\u836f\u7684\u6548\u7528\uff0c\u60f3 \u662f\u5979\u5df2\u7262\u8bb0\u5728\u5fc3\uff0c\u60f3\u5229\u7528\u8fd9\u79cd\u5947\u836f\u5192\u9669\u5165\u5bab\u53bb\u6551\u674e\u9038\u592b\u59bb\uff01\u201d\u641c\u67e5\u836f\u56ca\uff0c\u679c\u7136\u53d1\u89c9\u6b66\u7384 \u971c\u7559\u4e0b\u7684\u5b57\u6761\uff0c\u4e00\u5207\u6b63\u5982\u590f\u4faf\u575a\u6240\u6599\u3002\u539f\u6765\u8fd9\u77f3\u7a9f\u5e7d\u6df1\uff0c\u6709\u4e2a\u540e\u6d1e\u901a\u5411\u5c71\u5916\uff0c\u6b66\u7384\u971c\u662f \u81ea\u5df1\u4eba\uff0c\u590f\u4faf\u575a\u6599\u60f3\u4e0d\u5230\u5979\u4f1a\u5077\u4e86\u5947\u836f\u4ece\u540e\u6d1e\u51fa\u8d70\u3002 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u64d2\u4e86\u674e\u9038\u592b\u59bb\u4e4b\u540e\uff0c\u5927\u4e3a\u5f97\u610f\uff0c\u8fd9\u65e5\u4ed6\u6b63\u8981\u518d\u53bb\u529d\u8bf1\u957f\u5b59\u58c1\uff0c\u5ffd\u6709\u4e00\u4e2a\u5bab \u5973\u5306\u5306\u8fdb\u6765\u3002\u9762\u4e0a\u663e\u51fa\u5f20\u60f6\u7684\u795e\u8272\u3002 \u3000\u3000\u5927\u6c57\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u614c\u614c\u5f20\u5f20\u7684\uff1f\u201d\u90a3\u5bab\u5973\u8dea\u4e0b\u7980\u9053\uff1a\u201c\u53ef\u3001\u53ef\u8d3a\u6566\u6765\u4e86\uff0c\u8bf4 \u662f\u8981\u89c1\u5927\u6c57\uff0c\u6070\u514b\u56fe\u4e0d\u6562\u8ba9\u5979\u8fdb\u6765\uff0c\u7279\u6211\u6765\u8bf7\u793a\u5927\u6c57\u3002\u201d\u90a3\u5bab\u5973\u77e5\u9053\u65b0\u738b\u5983\u9003\u8d70\u7684\u4e8b\u60c5\uff0c \u5374\u4e0d\u77e5\u9053\u8fd9\u4e2a\u738b\u5983\u539f\u662f\u5047\u7684\uff0c\u6240\u4ee5\u6b66\u7384\u971c\u6765\u4e86\uff0c\u5979\u4ecd\u7136\u5c06\u5979\u5c0a\u79f0\u4e3a\u201c\u53ef\u8d3a\u6566\u201d\u3002 \u3000\u3000\u7a81\u53a5\u5927\u6c57\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u5979\uff0c\u5979\u5c45\u7136\u6562\u56de\u6765\u89c1\u6211\uff1f\u201d\u5bab\u5973\u9053\uff1a\u201c\u55ef\uff0c\u53ef \u8d3a\u6566\u5c31\u5728\u5bab\u95e8\u5916\u7b49\u5019\u5927\u6c57\u53ec\u89c1\u3002\u201d\u5927\u6c57\u5b9a\u4e86\u5b9a\u795e\uff0c\u5429\u5490\u4e00\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u5feb\u53bb\u8bf7\u56fd\u5e08\u6765\u3002\u201d \u3000\u3000\u539f\u6765\u6b66\u7384\u971c\u6709\u5927\u6c57\u7684\u4e00\u9762\u91d1\u724c\uff0c\u5979\u4ecd\u7136\u7a7f\u4e86\u738b\u5983\u7684\u670d\u9970\uff0c\u6602\u7136\u7684\u8fdb\u5165\u5bab\u95e8\uff0c\u5b88\u95e8\u7684 \u6b66\u58eb\u4e0d\u6562\u62e6\u963b\uff0c\u60ca\u5f02\u4e4b\u6781\uff0c\u6025\u5fd9\u8fdb\u53bb\u7980\u62a5\u4f8d\u4e91\u536b\u957f\u6070\u514b\u56fe\uff0c\u6070\u514b\u56fe\u4e5f\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u53ea\u597d \u5c06\u5979\u6b62\u5728\u5bab\u95e8\u4e4b\u5916\uff0c\u7b49\u5019\u5927\u6c57\u7684\u65e8\u610f\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u767e\u5fe7\u4e0a\u4eba\u5949\u53ec\u800c\u6765\uff0c\u5927\u6c57\u95ee\u6e05\u695a\u4e86\u6765\u7684\u53ea\u662f\u6b66\u7384\u971c\u4e00\u4eba\uff0c\u4fbf\u5429\u5490\u90a3\u5bab\u5973 \u9053\uff1a\u201c\u53eb\u6070\u514b\u56fe\u5c06\u53ef\u8d3a\u6566\u653e\u8fdb\u6765\u3002\u201d \u3000\u3000\u5927\u6c57\u9053\uff1a\u201c\u8fd9\u59d3\u6b66\u7684\u5973\u5b50\uff0c\u4e0a\u6b21\u5047\u5192\u738b\u5983\uff0c\u653e\u8d70\u674e\u9038\uff0c\u6715\u6b63\u8981\u5c06\u5979\u6349\u56de\u6765\uff0c\u6599\u4e0d\u5230 \u5979\u7adf\u7136\u5982\u6b64\u5927\u80c6\uff0c\u81ea\u5df1\u6295\u6765\u4e86\u3002\u53ea\u4e0d\u77e5\u5979\u4e0e\u674e\u9038\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u5979\u65e2\u662f\u6b66\u5219\u5929\u6d3e\u6765\u7684\u4eba\uff0c \u5374\u4e3a\u4f55\u8981\u4e00\u518d\u7684\u820d\u6027\u547d\u6765\u6551\u674e\u9038\uff1f\u201d\u767e\u5fe7\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u542c\u5929\u6076\u9053\u4eba\u8bf4\u8fc7\uff0c\u8fd9\u59d3\u6b66\u7684\u5973\u5b50 \u548c\u674e\u9038\u7684\u4ea4\u60c5\u4f3c\u4e4e\u751a\u4e0d\u5bfb\u5e38\uff0c\u5728\u5979\u5047\u5192\u738b\u5983\u5165\u5bab\u4e4b\u524d\uff0c\u5929\u6076\u9053\u4eba\u7684\u5973\u5f1f\u5b50\u5728\u5929\u5c71\u4e0a\u674e\u9038 \u7684\u5bb6\u4e2d\u66fe\u78b0\u89c1\u8fc7\u5979\u3002\u6211\u867d\u7136\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f46\u6599\u60f3\u603b\u662f\u6765\u6551\u674e\u9038\u7684\u4e86\u3002\u201d\u5927\u6c57 \u51b7\u7b11\u9053\uff1a\u201c\u5979\u5355\u8eab\u524d\u6765\uff0c\u8981\u6551\u674e\u9038\uff0c\u54c8\u54c8\uff0c\u8fd9\u5c82\u975e\u662f\u81ea\u6295\u843d\u7f51\uff01\u201d\u767e\u4f18\u4e0a\u4eba\u95ee\u9053\uff1a\u201c\u8bf7 \u95ee\u5927\u6c57\u4e3b\u610f\u5982\u4f55\uff1f\u8981\u6b7b\u7684\u8fd8\u662f\u8981\u6d3b\u7684\uff1f\u201d\u5927\u6c57\u7b11\u9053\uff1a\u201c\u5979\u867d\u662f\u5bf9\u6715\u5927\u5927\u5192\u72af\uff0c\u6715\u5374\u8fd8\u6709 \u601c\u9999\u60dc\u7389\u4e4b\u5fc3\uff0c\u8fd9\u6837\u4e00\u4e2a\u7edd\u8272\u7684\u7f8e\u4eba\u513f\uff0c\u82e5\u80fd\u5c06\u5979\u6536\u670d\uff0c\u90a3\u662f\u6700\u5999\u4e0d\u8fc7\u3002\u4e14\u770b\u5979\u6765\u610f\u5982 \u4f55\uff1f\u82e5\u662f\u5979\u6562\u884c\u523a\uff0c\u56fd\u5e08\u518d\u51fa\u624b\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d\u7a81\u53a5\u5927\u6c57\u6df1\u77e5\u767e\u5fe7\u4e0a\u4eba\u7684\u6b66\u529f\u8fdc\u5728\u6b66\u7384\u971c \u4e4b\u4e0a\uff0c\u8bf7\u5f97\u4ed6\u6765\u4fdd\u9a7e\uff0c\u81ea\u662f\u6709\u6043\u65e0\u6050\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6b66\u7384\u971c\u5728\u6070\u514b\u56fe\u76d1\u89c6\u4e4b\u4e0b\u8d70\u8fdb\u5bab\u95e8\uff0c\u5927\u6c57\u7b11\u9053\uff1a\u201c\u4f60\u7684\u80c6\u5b50\u53ef\u4e0d\u5c0f\u5440\uff0c \u600e\u4e48\u524d\u65e5\u9003\u51fa\u53bb\u73b0\u5728\u53c8\u56de\u6765\u4e86\uff1f\u53ef\u662f\u820d\u4e0d\u5f97\u5bab\u4e2d\u7684\u5bcc\u8d35\u7e41\u534e\u4e48\u3002\u201d\u6b66\u7384\u971c\u67f3\u7709\u4e00\u575a\uff0c\u51b7 \u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u6211\u662f\u4e3a\u4ec0\u4e48\u6765\u7684\uff0c\u8fd8\u95ee\u4ec0\u4e48\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u4f60\u662f\u60f3\u6551\u674e\u9038\u51fa\u53bb\u5417\uff1f \u8fd9\u529e\u4e0d\u5230\uff0c\u9664\u975e\u4f60\u7559\u4e0b\u6765\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u8bdd\u53ef\u771f\uff1f\u201d\u5927\u6c57\u9053\uff1a\u201c\u6715\u738b\u5c82\u6709\u620f\u8a00\uff1f\u201d \u6b66\u7384\u971c\u9053\uff1a\u201c\u597d\uff0c\u6211\u7559\u4e0b\u6765\uff0c\u4f60\u653e\u4ed6\u4eec\u4e8c\u4eba\u51fa\u53bb\uff01\u201d\u5927\u6c57\u6599\u4e0d\u5230\u6b66\u7384\u971c\u7b54\u5e94\u5f97\u8fd9\u6837\u723d\u5feb\uff0c \u5012\u662f\u5403\u4e86\u4e00\u60ca\u3002\u76b1\u76b1\u7709\u5934\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u968f\u5373\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u8fd9\u7a81\u53a5\u5927\u6c57\u9887\u6709\u624d\u667a\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u731c\u6d4b\u6b66\u7384\u971c\u7684\u7528\u5fc3\uff0c\u5fc3\u91cc\u6697\u9053\uff1a\u201c\u4f60\u806a\u660e\u6211\u4e5f\u4e0d \u50bb\uff0c\u4f60\u5c82\u662f\u7518\u5fc3\u60c5\u613f\u505a\u6211\u7684\u738b\u5983\uff0c\u4e0d\u8fc7\u662f\u60f3\u9a97\u6211\u5c06\u674e\u9038\u653e\u8d70\u7f62\u4e86\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u4f60\u8fd8\u501f\u6b64\u673a \u4f1a\u6765\u884c\u523a\u6211\u3002\u201d\u60f3\u5230\u6b66\u7384\u971c\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u82e5\u7136\u771f\u7684\u505a\u4e86\u81ea\u5df1\u7684\u738b\u5983\uff0c\u671d\u5915\u76f8\u5904\uff0c\u51ed\u5979\u7684 \u6b66\u529f\uff0c\u8981\u53d6\u81ea\u5df1\u7684\u6027\u547d\uff0c\u90a3\u771f\u662f\u6613\u5982\u53cd\u638c\uff0c\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u5bd2\u800c\u6817\uff0c\u4f46\u4ed6\u7709\u5934\u4e00\u76b1\uff0c\u7acb\u5373 \u8ba1\u4e0a\u5fc3\u6765\uff0c\u795e\u8272\u4e0d\u9732\uff0c\u53cd\u800c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u738b\u5983\u7edd\u8272\u7f8e\u4eba\uff0c\u4e16\u95f4\u5c11\u6709\uff0c\u82e5\u80af\u966a\u4f34\u5b64\u738b\uff0c\u83ab \u8bf4\u653e\u8fd9\u4e24\u4e2a\u4eba\uff0c\u5c31\u662f\u8981\u6211\u8ba9\u51fa\u738b\u4f4d\uff0c\u6211\u4e5f\u7b54\u5e94\u3002\u6765\uff0c\u6765\uff0c\u6765\uff0c\u8fd9\u65c1\u5750\u4e0b\uff0c\u54b1\u4eec\u4eca\u65e5\u91cd\u5706\uff0c \u7406\u8be5\u996e\u9152\u5e86\u8d3a\u3002\u4f8d\u513f\uff0c\u7ed9\u738b\u5983\u659f\u9152\uff01\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ea\u8981\u4f60\u9152\u4e00\u6cbe\u5507\uff0c\u90a3\u53ef\u5c31\u5f97\u7531\u6211\u6446 \u5e03\u4e86\u3002\u201d\u539f\u6765\u4ed6\u9152\u4e2d\u4e0b\u4e86\u8ff7\u836f\uff0c\u65e0\u8272\u65e0\u5473\uff0c\u559d\u5230\u53e3\u91cc\uff0c\u4e5f\u5c1d\u4e0d\u51fa\u6765\uff0c\u4ed6\u6253\u7b97\u628a\u6b66\u7384\u971c\u8ff7 \u5012\u4e4b\u540e\uff0c\u4fbf\u8bf7\u767e\u5fe7\u4e0a\u4eba\u5e9f\u53bb\u5979\u7684\u6b66\u529f\uff0c\u90a3\u65f6\u5979\u7eb5\u6709\u884c\u523a\u4e4b\u5fc3\uff0c\u4ea6\u5df2\u65e0\u80fd\u4e3a\u529b\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u9762\u8272\u4e00\u53d8\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5f85\u653e\u4e86\u8fd9\u4e24\u4e2a\u4eba\u51fa\u53bb\uff0c\u6211\u518d\u4f34\u4f60\u559d\u9152\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u6211 \u8981\u5148\u89c1\u89c1\u4ed6\u4eec\uff0c\u7136\u540e\u9001\u4ed6\u4eec\u51fa\u5bab\u3002\u201d\u7a81\u53a5\u5927\u6c57\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u662f\u4e0d\u76f8\u4fe1\u6211\u5440\uff01\u201d\u6b66\u7384 \u971c\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u76f8\u4fe1\u5927\u738b\uff0c\u6211\u603b\u5f97\u89c1\u7740\u4ed6\u4eec\u6d3b\u7740\u51fa\u5bab\uff0c\u6211\u624d\u653e\u5fc3\u3002\u201d\u5927\u6c57\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c \u4f60\u4eec\u4e2d\u56fd\u6709\u53e5\u4fd7\u8bdd\uff0c\u5b81\u4e86\u5931\u4fe1\u4e8e\u5929\u4e0b\uff0c\u83ab\u5931\u4fe1\u4e8e\u5987\u4eba\u3002\u8054\u65e2\u7136\u7b54\u5e94\u4e86\u4f60\uff0c\u5f53\u7136\u4e0d\u4f1a\u5931\u4fe1\u3002 \u4f60\u6240\u8bf4\u7684\uff0c\u6211\u4e00\u6982\u4f9d\u4f60\u4fbf\u662f\u3002\u4f60\u8981\u5148\u89c1\u54ea\u4e2a\uff1f\u662f\u4e08\u592b\uff1f\u8fd8\u662f\u59bb\u5b50\uff1f\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u5c31 \u653e\u4ed6\u4eec\u51fa\u5bab\uff0c\u4ed6\u4eec\u53c8\u8d70\u5f97\u591a\u8fdc\uff1f\u4e00\u4e2a\u83e9\u63d0\u4e0a\u4eba\u5df2\u8db3\u4ee5\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u53cc\u7709\u4e00\u5c55\uff0c\u76c8\u76c8\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5927\u738b\u3002\u201d\u968f\u5373\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4eba\u4e0d\u662f\u5173 \u5728\u4e00\u8d77\u7684\u5417\uff1f\u201d\u5927\u6c57\u7b11\u9053\uff1a\u201c\u6211\u8ba9\u4ed6\u4eec\u6bd4\u90bb\u800c\u5c45\uff0c\u95fb\u58f0\u800c\u4e0d\u80fd\u89c1\u9762\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f55 \u82e6\u8fd9\u6837\u6298\u78e8\u4ed6\u4eec\uff0c\u8bf7\u5927\u738b\u5148\u8ba9\u4ed6\u4eec\u592b\u59bb\u76f8\u805a\uff0c\u6211\u518d\u53bb\u89c1\u4ed6\u4eec\u3002\u201d\u539f\u6765\u6b66\u7384\u971c\u77e5\u9053\u957f\u5b59\u58c1 \u751a\u4e3a\u731c\u7591\u81ea\u5df1\uff0c\u5148\u89c1\u957f\u5b59\u58c1\uff0c\u957f\u5b59\u58c1\u672a\u5fc5\u80af\u4f9d\u8ba1\u884c\u4e8b\uff0c\u53ea\u6015\u53cd\u628a\u4e8b\u60c5\u5f04\u7cdf\uff0c\u4f46\u82e5\u5148\u89c1\u674e \u9038\uff0c\u957f\u5b59\u58c1\u7684\u731c\u7591\uff0c\u90a3\u5c31\u53ef\u80fd\u66f4\u6df1\u4e86\u3002\u5927\u6c57\u60f3\u4e86\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u6211\u90fd\u4f9d\u4f60\u4fbf\u662f\u3002\u201d \u4ed6\u5df2\u6210\u7af9\u5728\u80f8\uff0c\u5e03\u7f6e\u4e0b\u5929\u7f57\u5730\u7f51\uff0c\u5c31\u8ba9\u674e\u9038\u592b\u59bb\u6682\u805a\u7247\u65f6\uff0c\u90a3\u4e5f\u662f\u65e0\u5173\u91cd\u8981\u7684\u4e86\u3002 \u3000\u3000\u957f\u5b59\u58c1\u88ab\u56da\u7981\u5728\u8fd9\u95f4\u5bc6\u5ba4\u91cc\u5df2\u7ecf\u6709\u4e09\u5929\u4e86\uff0c\u8fd9\u4e09\u5929\u6765\uff0c\u5979\u611f\u5230\u5feb\u6170\uff0c\u4e5f\u611f\u5230\u75db\u82e6\u3002 \u5feb\u6170\u7684\u662f\uff0c\u5979\u7684\u4e08\u592b\u5c31\u5728\u5979\u7684\u9694\u90bb\uff0c\u7518\u613f\u4e0e\u5979\u540c\u751f\u5171\u6b7b\uff0c\u8fd9\u4e16\u754c\u4e0a\u662f\u6ca1\u6709\u4ec0\u4e48\u4eba\u53ef\u4ee5\u628a \u4ed6\u4eec\u5206\u5f00\u7684\u4e86\uff0c\u4e0d\u7ba1\u662f\u4e0a\u5b98\u5a49\u513f\u6216\u662f\u6b66\u7384\u971c\uff0c\u751a\u81f3\u662f\u90a3\u4e2a\u5177\u6709\u65e0\u4e0a\u5a01\u6743\u7684\u7a81\u53a5\u5927\u6c57\uff0c\u90fd \u4e0d\u80fd\u591f\u5c06\u5979\u7684\u4e08\u592b\u62a2\u8d70\u4e86\uff01\u5979\u771f\u771f\u6b63\u6b63\u611f\u5230\u4e08\u592b\u662f\u5c5e\u4e8e\u5979\u7684\u4e86\uff01\u4f46\u611f\u5230\u75db\u82e6\u7684\u4e5f\u662f\uff0c\u5979 \u7684\u4e08\u592b\u5c31\u5728\u5979\u7684\u9694\u90bb\uff0c\u5979\u5c06\u8033\u6735\u8d34\u7740\u5899\u58c1\uff0c\u53ef\u4ee5\u542c\u5f97\u89c1\u5979\u4e08\u592b\u884c\u52a8\u7684\u58f0\u606f\uff0c\u53f9\u6c14\u7684\u58f0\u97f3\uff0c \u4f46\u5374\u4e0d\u80fd\u548c\u4ed6\u89c1\u9762\uff0c\u5979\u662f\u591a\u4e48\u6e34\u671b\u80fd\u591f\u89c1\u4e08\u592b\u4e00\u9762\u554a\uff01\u5979\u6839\u672c\u5c31\u6ca1\u6709\u6253\u7b97\u80fd\u591f\u6d3b\u7740\u51fa\u53bb\uff0c \u5979\u5fc3\u4e2d\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\uff0c\u8981\u662f\u80fd\u591f\u6b7b\u5728\u4e08\u592b\u7684\u6000\u4e2d\uff0c\u90a3\u5c31\u662f\u6700\u5927\u7684\u5e78\u798f\uff01 \u3000\u3000\u6b64\u523b\uff0c\u5979\u6b63\u5728\u82e6\u82e6\u601d\u5ff5\u5979\u7684\u4e08\u592b\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u5f00\u52a8\u94c1\u9501\u7684\u58f0\u97f3\uff0c\u90a3\u4e24\u6247\u50f5\u539a\u7684\u94c1\u95e8 \u5ffd\u5730\u6253\u5f00\uff0c\u4e00\u4e2a\u4eba\u88ab\u63a8\u4e86\u8fdb\u6765\uff0c\u8dcc\u8dcc\u649e\u649e\u7684\u51e0\u4e4e\u78b0\u5230\u5979\u7684\u8eab\u4e0a\u3002\u5440\uff0c\u8fd9\u662f\u5728\u4f5c\u68a6\u5417\uff1f\u5979 \u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u8fd9\u4e2a\u88ab\u63a8\u8fdb\u6765\u7684\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5979\u7684\u4e08\u592b\uff01 \u3000\u3000\u5979\u5012\u5728\u674e\u9038\u7684\u6000\u4e2d\uff0c\u53ea\u542c\u5f97\u8033\u8fb9\u4e00\u4e2a\u67d4\u67d4\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u662f\u6211\u5440\uff01\u4f60\u5403\u60ca\u4e86 \u5427\uff1f\u201d\u518d\u6ca1\u6709\u6000\u7591\u4e86\uff0c\u786e\u786e\u5b9e\u5b9e\u662f\u5979\u7684\u4e08\u592b\uff0c\u957f\u5b59\u58c1\u53c8\u559c\u53c8\u60b2\uff0c\u542b\u7740\u773c\u6cea\u8bf4\u9053\uff1a\u201c\u674e\u9038 \u54e5\u54e5\uff0c\u5f53\u771f\u662f\u4f60\u5440\uff01\u548c\u4f60\u5728\u4e00\u8d77\uff0c\u6211\u662f\u4e00\u70b9\u4e5f\u4e0d\u5bb3\u6015\u4e86\uff01\u55ef\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u80af\u653e\u4f60\u8fdb\u6765\u4e86\u3002\u201d \u674e\u9038\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5927\u6c57\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u6216\u8005\u662f\u4ed6\u8981\u5c06\u6211\u4eec\u5904\u6b7b\uff0c\u6240\u4ee5\u5728\u6211\u4eec\u4e34\u6b7b\u4e4b\u524d \u4e00\u53d1\u6148\u60b2\uff0c\u8ba9\u6211\u4eec\u592b\u59bb\u6700\u540e\u89c1\u4e00\u6b21\u9762\u3002\u201d\u957f\u5b59\u58c1\u7b11\u9053\uff1a\u201c\u82e5\u7136\u5982\u6b64\uff0c\u6211\u867d\u7136\u6068\u6781\u4e86\u5927\u6c57\uff0c \u8fd9\u4e00\u6b21\u5374\u4e0d\u80fd\u4e0d\u611f\u8c22\u4ed6\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u90fd\u662f\u6211\u8fde\u7d2f\u4e86\u4f60\uff0c\u5cb3\u7236\u5c06\u4f60\u6258\u7ed9\u6211\uff0c\u6211\u4e0d \u4f46\u4e0d\u80fd\u4fdd\u62a4\u4f60\uff0c\u53cd\u800c\u8981\u8fde\u7d2f\u4f60\u966a\u6211\u9001\u547d\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u4e3e\u624b\u5c01\u7740\u4e86\u4ed6\u7684\u5634\u5df4\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u80fd\u591f\u548c\u4f60\u540c\u6b7b\uff0c\u5728\u6211\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u4f60\u8fd8 \u591a\u8bf4\u505a\u751a\uff1f\u6211\u53ea\u6709\u4e00\u6837\u96be\u8fc7\u7684\u4e8b\u2026\u2026\u201d\u674e\u9038\u79fb\u5f00\u4e86\u5979\u7684\u624b\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u957f\u5b59 \u58c1\u9053\uff1a\u201c\u6211\uff0c\u6211\u597d\u50cf\u53c8\u6709\u4e86\uff01\u201d\u8138\u4e0a\u6cdb\u8d77\u4e00\u7247\u5a07\u7ea2\uff0c\u674e\u9038\u7acb\u5373\u9192\u609f\uff0c\u7b11\u9053\uff1a\u201c\u53c8\u6709\u4e86\u5b69 \u5b50\u4e86\uff1f\u201d\u957f\u5b59\u58c1\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5927\u7ea6\u6709\u4e09\u4e2a\u6708\u4e86\u3002\u55ef\uff0c\u6211\u5e0c\u671b\u662f\u4e2a\u5973\u7684\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c \u4e24\u592b\u59bb\u90fd\u60f3\u8d70\u4e86\uff0c\u751f\u547d\u5371\u5728\u65e6\u5915\uff0c\u957f\u5b59\u58c1\u8179\u4e2d\u7684\u5b69\u5b50\u53ea\u6015\u6839\u672c\u5c31\u6ca1\u6709\u89c1\u5929\u65e5\u7684\u673a\u4f1a\uff0c\u8fd8 \u8bba\u4ec0\u4e48\u662f\u7537\u662f\u5973\uff1f\u957f\u5b59\u58c1\u81ea\u89c9\u8bf4\u9519\u4e86\u8bdd\uff0c\u4f4e\u4e0b\u4e86\u5934\uff0c\u9eef\u7136\u795e\u4f24\u3002\u674e\u9038\u5b89\u6170\u5979\u9053\uff1a\u201c\u597d\u5728 \u654f\u513f\u5df2\u7ecf\u6551\u51fa\uff0c\u4f60\u4e5f\u4e0d\u7528\u592a\u4f24\u5fc3\u4e86\u3002\u54b1\u4eec\u76f8\u805a\u7684\u65f6\u5019\u53ea\u6015\u6ca1\u591a\u4e45\u4e86\uff0c\u8bf4\u4e00\u4e9b\u6b22\u559c\u7684\u8bdd\u5427\u3002\u201d \u957f\u5b59\u58c1\u5f3a\u6291\u8f9b\u9178\uff0c\u95ee\u9053\uff1a\u201c\u654f\u513f\u662f\u600e\u6837\u6551\u51fa\u53bb\u7684\uff1f\u8be6\u7ec6\u7684\u8bf4\u7ed9\u6211\u542c\u542c\uff0c\u597d\u8ba9\u6211\u4e5f\u559c\u6b22\u559c \u6b22\u3002\u201d\u674e\u9038\u8e0c\u8e87\u534a\u664c\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u4f60\u6240\u610f\u6599\u4e0d\u5230\u7684\u4eba\u5c06\u4ed6\u6551\u51fa\u53bb\u7684\u3002\u201d \u3000\u3000\u957f\u5b59\u58c1\u5fc3\u5934\u4e00\u98a4\uff0c\u5979\u5df2\u731c\u5230\u662f\u4ec0\u4e48\u4eba\u4e86\uff0c\u679c\u7136\u542c\u5f97\u674e\u9038\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5c31\u662f\u6b66\u7384\u971c\uff0c \u5979\u5047\u5192\u738b\u5983\uff0c\u5192\u4e86\u5f88\u5927\u7684\u5371\u9669\uff0c\u4e3a\u7684\u5c31\u662f\u54b1\u4eec\u7684\u5b69\u5b50\uff01\u201d\u957f\u5b59\u58c1\u9ed8\u9ed8\u65e0\u8a00\uff0c\u542c\u674e\u9038\u8be6\u7ec6 \u8bf4\u4e86\u4e8b\u60c5\u7684\u7ecf\u8fc7\uff0c\u597d\u4e45\uff0c\u624d\u5e7d\u5e7d\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u662f\u6211\u9519\u602a\u4e86\u5979\u4e86\u3002\u9038\u54e5\uff0c\u602a\u4e0d\u5f97\u5979 \u867d\u7136\u662f\u4f60\u7684\u654c\u4eba\uff0c\u4f60\u5374\u4e00\u5411\u628a\u5979\u5f53\u4f5c\u77e5\u5df1\uff01\u55ef\uff0c\u4f60\u4e0d\u8981\u8fa9\uff0c\u8fd9\u8bdd\u4e0d\u5fc5\u4f60\u8bf4\u51fa\u6765\uff0c\u6211\u662f\u65e9 \u5df2\u77e5\u9053\u4e86\u7684\u3002\u8fd9\u6b21\u4f60\u5e94\u8be5\u66f4\u611f\u6fc0\u5979\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u654f\u513f\u662f\u54b1\u4eec\u7684\u547d\u6839\u5b50\uff0c\u5979\u6551\u4e86\u654f\u513f\uff0c \u6211\u5f53\u7136\u662f\u611f\u6fc0\u5979\uff0c\u4f60\u4e0d\u611f\u6fc0\u5979\u4e48\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u611f\u6fc0\u5979\uff0c\u55ef\uff0c\u6211\u66f4\u611f\u6fc0\u4f60\uff0c\u4f60 \u6ca1\u6709\u629b\u5f03\u6211\uff0c\u591a\u8c22\u4e0a\u5929\uff0c\u8fd9\u95f4\u623f\u5b50\u91cc\u53ea\u6709\u54b1\u4eec\u4e24\u4e2a\u4eba\uff0c\u6b66\u7384\u971c\u5374\u5728\u5916\u9762\uff0c\u5509\uff0c\u6211\u73b0\u5728\u53cd \u800c\u89c9\u5f97\u5979\u53ef\u601c\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u5ffd\u5730\u51c4\u7136\u4e00\u7b11\uff0c\u8fd9\u4e00\u7b11\u5305\u542b\u4e86\u6781\u5176\u590d\u6742\u7684\u60c5\u7eea\uff0c\u597d\u50cf\u662f\u53ef\u601c \u6b66\u7384\u971c\uff0c\u4e5f\u50cf\u662f\u53ef\u601c\u674e\u9038\uff0c\u66f4\u50cf\u662f\u53ef\u601c\u81ea\u5df1\uff0c\u4f46\u5728\u51c4\u51c9\u4e4b\u4e2d\uff0c\u53c8\u4f3c\u542b\u6709\u4e00\u4efd\u6ee1\u8db3\u7684\u5fc3\u60c5\u3002 \u674e\u9038\u671b\u7740\u5979\u7684\u773c\u775b\uff0c\u5fc3\u4e2d\u4e5f\u4f3c\u5927\u6d77\u6ce2\u7ffb\uff0c\u52a8\u8361\u96be\u6b62\u3002\u957f\u5b59\u58c1\u7684\u8fd9\u4e2a\u7b11\u5bb9\uff0c\u4ee5\u540e\u5728\u4ed6\u4e00\u751f \u4e4b\u4e2d\uff0c\u6c38\u8fdc\u90fd\u6ca1\u6709\u5fd8\u8bb0\uff01 \u3000\u3000\u957f\u5b59\u58c1\u7684\u5fc3\u60c5\u7684\u786e\u590d\u6742\u5f97\u5f88\uff0c\u4e0d\u9519\uff0c\u5979\u786e\u662f\u5f88\u611f\u6fc0\u6b66\u7384\u971c\uff0c\u4f46\u5374\u4e5f\u611f\u5230\u6050\u60e7\uff0c\u6015\u6b66 \u7384\u971c\u56e0\u6b64\u66f4\u83b7\u5f97\u4e86\u674e\u9038\u7684\u5fc3\uff01\u4e0d\u8fc7\uff0c\u8fd9\u6050\u60e7\u4e4b\u611f\u5e76\u6ca1\u6709\u505c\u7559\u591a\u4e45\uff0c\u56e0\u4e3a\u5979\u7684\u4e08\u592b\u5c31\u5728\u5979 \u7684\u8eab\u8fb9\uff0c\u8fd9\u56da\u623f\u91cc\u53ea\u6709\u4ed6\u4eec\u4e24\u4eba\uff0c\u4e0d\u7ba1\u6b66\u7384\u971c\u7528\u4ec0\u4e48\u624b\u6bb5\uff0c\u603b\u4e0d\u80fd\u628a\u5979\u7684\u4e08\u592b\u62a2\u8d70\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u5728\u767e\u5fe7\u4e0a\u4eba\u201c\u62a4\u9001\u201d\u4e4b\u4e0b\uff0c\u8d70\u5411\u957f\u5b59\u58c1\u7684\u56da\u623f\uff0c\u5979\u7684\u5fc3\u60c5\u4e5f\u662f\u52a8\u8361\u4e0d\u4f11\uff0c\u5b9e \u4e0d\u5728\u957f\u5b59\u58c1\u4e0e\u674e\u9038\u4e4b\u4e0b\uff0c\u4f46\u5979\u6781\u529b\u6291\u5236\u4e0d\u8ba9\u767e\u4f18\u4e0a\u4eba\u770b\u5f97\u51fa\u6765\u3002\u5c06\u8fd1\u56da\u623f\uff0c\u767e\u4f18\u4e0a\u4eba\u5ffd \u7136\u95ee\u9053\uff1a\u201c\u6211\u771f\u4e0d\u660e\u767d\uff0c\u4f60\u4e3a\u4ec0\u4e48\u7518\u613f\u820d\u8eab\u6765\u6551\u674e\u9038\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u674e \u9038\u662f\u5c09\u8fdf\u70af\u7684\u5f1f\u5b50\u3002\u201d\u767e\u4f18\u4e0a\u4eba\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u5982\u6b64\uff0c\u6211\uff01\u660e\u767d\u4e86\uff01\u201d\u767e\u5fe7\u4e0a\u4eba\u548c\u5c09\u8fdf \u70af\u3001\u4f18\u4e91\u8001\u5c3c\u662f\u540c\u4e00\u8f88\u7684\u4eba\uff0c\u4f18\u4e91\u66fe\u662f\u5c09\u8fdf\u70af\u7684\u672b\u5a5a\u59bb\u5b50\uff0c\u4ee5\u53ca\u4ed6\u4eec\u4e4b\u95f4\u7684\u60c5\u5b7d\u7ea0\u7eb7\uff0c \u767e\u5fe7\u4e0a\u4eba\u7ea6\u7565\u77e5\u9053\u4e00\u4e9b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u539f\u6765\u6b66\u7384\u971c\u662f\u4e3a\u4e86\u5979\u5e08\u5085\u4f18\u4e91\u8001\u5c3c\u7684\u539f\u6545\uff0c\u56e0\u800c \u8981\u4fdd\u5168\u5c09\u8fdf\u70af\u7684\u5f1f\u5b50\u3002\u4f18\u4e91\u8001\u5c3c\u5bf9\u5c09\u8fdf\u70af\u7684\u7231\u751f\u6b7b\u4e0d\u6e1d\uff0c\u800c\u6b66\u7384\u971c\u5bf9\u5e08\u7236\u7684\u5fe0\u5fc3\u4e5f\u771f\u662f \u4e16\u95f4\u5c11\u6709\u3002\u5509\uff0c\u53ef\u60dc\u6211\u6ca1\u6709\u4e00\u4e2a\u8fd9\u6837\u7684\u597d\u5f1f\u5b50\u3002\u201d\u767e\u5fe7\u4e0a\u4eba\u54ea\u91cc\u77e5\u9053\uff0c\u6b66\u7384\u971c\u5e76\u4e0d\u53ea\u662f \u4e3a\u4e86\u5979\u7684\u5e08\u7236\uff0c\u8981\u662f\u4ed6\u77e5\u9053\u6b66\u7384\u971c\u771f\u5b9e\u7684\u611f\u60c5\uff0c\u4ed6\u6050\u6015\u66f4\u8981\u8be7\u5f02\u4e86\u3002 \u3000\u3000\u5230\u4e86\u56da\u623f\u524d\u9762\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u8981\u5355\u72ec\u548c\u4ed6\u4eec\u4f1a\u9762\u3002\u201d\u767e\u5fe7\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u5927\u6c57\u5df2\u5141 \u8bb8\u4e86\u4f60\uff0c\u6211\u5f53\u7136\u4e0d\u4f1a\u6253\u6405\u4f60\u4eec\u3002\u8fd9\u662f\u5f00\u4ed6\u4eec\u9563\u94d0\u7684\u9501\u5319\uff0c\u7531\u4f60\u4eb2\u81ea\u653e\u4ed6\u4eec\u51fa\u6765\uff0c\u518d\u4eb2\u81ea \u9001\u4ed6\u4eec\u51fa\u5bab\uff0c\u4f60\u603b\u53ef\u4ee5\u653e\u5fc3\u4e86\u5427\u3002\u201d\u6b66\u7384\u971c\u63a5\u8fc7\u9501\u5319\uff0c\u8f7b\u8f7b\u628a\u95e8\u63a8\u5f00\uff0c\u8d70\u4e86\u8fdb\u53bb\uff0c\u968f\u624b \u628a\u94c1\u95e8\u63a9\u4e0a\uff0c\u5c06\u767e\u5fe7\u4e0a\u4eba\u5173\u5728\u5916\u9762\u3002 \u3000\u3000\u674e\u9038\u8df3\u4e86\u8d77\u6765\uff0c\u5f20\u5927\u773c\u775b\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u957f\u5b59\u58c1\u795e\u60c5\u6cae\u4e27\uff0c\u597d\u50cf\u7ed9\u5f3a\u654c\u6253\u8d25\u4e86\u4e00\u822c\uff0c \u5931\u58f0\u53eb\u9053\uff1a\u201c\u7384\u971c\uff0c\u662f\u4f60\uff01\u201d\u4f46\u542c\u5f97\u5979\u624b\u8db3\u4e0a\u7684\u9563\u94d0\uff0c\u53ee\u5f53\u4f5c\u54cd\uff0c\u4ee4\u4eba\u611f\u89c9\u5f97\u5230\uff0c\u5979\u7684 \u8eab\u4f53\u548c\u5fc3\u7075\u90fd\u5728\u53d1\u6296\uff01\u5979\u505a\u68a6\u4e5f\u6599\u60f3\u4e0d\u5230\u6b66\u7384\u971c\u4e5f\u4f1a\u820d\u4e86\u6027\u547d\u8fdb\u6765\uff0c\u5979\u672c\u4ee5\u4e3a\u53ef\u4ee5\u907f\u5f00 \u5979\u4e86\uff0c\u7136\u800c\u8fd8\u662f\u907f\u4e0d\u5f00\uff0c\u5728\u5979\u548c\u674e\u9038\u7684\u5c0f\u5929\u5730\u4e4b\u95f4\uff0c\u6b66\u7384\u971c\u7adf\u7136\u53c8\u63d2\u8fdb\u6765\u4e86\uff01 \u3000\u3000\u6b66\u7384\u971c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u522b\u614c\uff0c\u6211\u662f\u6765\u6551\u4f60\u4eec\u51fa\u53bb\u7684\u3002\u201d\u957f\u5b59\u58c1\u4e00\u7247\u832b\u7136\uff0c\u8fce\u7740\u6b66\u7384\u971c \u7684\u76ee\u5149\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u613f\u610f\u6b7b\u5728\u8fd9\u91cc\uff01\u201d\u6b66\u7384\u971c\u6253\u5f00\u4e86\u4ed6\u4eec\u4e24\u4eba\u7684\u9563\u94d0\uff0c\u8f7b\u629a\u957f \u5b59\u58c1\u7684\u79c0\u53d1\uff0c\u67d4\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u58c1\u59b9\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u4f60\u7684\u654f\u513f\u5728\u7b49\u7740\u4f60\u5462\uff01\u201d\u957f\u5b59\u58c1\u60f3\u8d77 \u4e86\u5979\u6d3b\u6cfc\u53ef\u7231\u7684\u654f\u513f\uff0c\u4f4e\u4e0b\u5934\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u674e\u9038\u5b9a\u4e86\u5b9a\u795e\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u884c\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u6562\u8fdb\u6765\uff0c\u81ea\u6709\u5999\u6cd5\uff01\u4f60\u600e\u77e5 \u9053\u4e0d\u884c\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u731c\u5f97\u5230\u4f60\u7684\u529e\u6cd5\u3002\u201d\u671b\u4e86\u4e00\u773c\u5979\u6240\u7a7f\u7684\u738b\u5983\u670d\u9970\uff0c\u8bf4\u9053\uff1a\u201c\u4f60 \u662f\u4e0d\u662f\u60f3\u54c4\u9a97\u5927\u6c57\uff0c\u8bf4\u662f\u613f\u610f\u505a\u4ed6\u7684\u738b\u5983\uff0c\u597d\u653e\u6211\u4eec\u51fa\u53bb\uff0c\u7136\u540e\u518d\u60f3\u529e\u6cd5\u884c\u523a\u4ed6\uff1f\u4e0d\u884c \u5440\uff0c\u7384\u971c\uff01\u5927\u6c57\u5e76\u4e0d\u662f\u7b28\u4eba\uff0c\u4ed6\u82e5\u7136\u7b54\u5e94\u4e86\u4f60\uff0c\u5b9a\u662f\u5c06\u8ba1\u5c31\u8ba1\u53e6\u6709\u5b89\u6392\uff0c\u4f60\u4e0d\u8be5\u628a\u4e8b\u60c5 \u60f3\u5f97\u592a\u5bb9\u6613\u4e86\u3002\u201d\u674e\u9038\u731c\u6d4b\u6b66\u7384\u971c\u7684\u7528\u610f\u6070\u597d\u548c\u5927\u6c57\u6240\u731c\u6d4b\u7684\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u6b66\u7384\u971c\u5fae\u5fae\u4e00\u7b11\uff0c\u8fd9\u987e\u4e0d\u5f97\u518d\u907f\u5acc\u7591\uff0c\u5728\u4ed6\u8033\u8fb9\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u624d\u4e0d\u4f1a\u8fd9\u6837\u50bb\uff0c\u8fd9 \u6837\u5f53\u7136\u9a97\u4e0d\u4e86\u5927\u6c57\uff0c\u6211\u662f\u53e6\u6709\u5999\u6cd5\uff0c\u51ed\u4ed6\u600e\u6837\u806a\u660e\uff0c\u4e5f\u51b3\u5bf9\u6599\u60f3\u4e0d\u5230\u201d\u674e\u9038\u534a\u4fe1\u534a\u7591\uff0c \u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u4f60\u8bf4\u8bf4\u770b\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u6015\u6b7b\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u60f3 \u6d3b\u7740\u51fa\u53bb\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u91cc\u6709\u4e00\u5305\u836f\u6563\uff0c\u4f60\u670d\u4e86\u4e0b\u53bb\uff0c\u7acb\u5373\u6c14\u7edd\u8eab\u4ea1\uff01\u201d\u957f\u5b59 \u58c1\u5403\u4e86\u4e00\u60ca\uff0c\u6012\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u60f3\u7684\u662f\u8fd9\u4e2a\u529e\u6cd5\u5417\uff1f\u201d\u6b66\u7384\u971c\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u5728\u5979\u8033\u8fb9 \u8bf4\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u4f60\u76f8\u4fe1\u6211\uff0c\u96be\u9053\u6211\u4f1a\u5bb3\u6b7b\u4f60\u7684\u4e08\u592b\u5417\uff1f\u8fd9\u662f\u590f\u4faf\u575a\u7684\u79d8\u79d8\u5947\u836f\uff0c\u6b7b\u4e86\u4e4b \u540e\uff0c\u5728\u4e03\u5929\u4e4b\u5185\u8fd8\u53ef\u4ee5\u590d\u6d3b\u3002\u9038\u54e5\u6b7b\u4e86\uff0c\u5927\u6c57\u8981\u4ed6\u7684\u5c38\u4f53\u6709\u4ec0\u4e48\u7528\uff1f\u4f60\u53ef\u4ee5\u9886\u4ed6\u7684\u5c38\u4f53 \u51fa\u53bb\uff01\u201d\u957f\u5b59\u58c1\u5b9a\u4e0b\u5fc3\u795e\uff0c\u6df1\u4fe1\u6b66\u7384\u971c\u4e0d\u4f1a\u6bd2\u5bb3\u674e\u9038\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u9664\u4e86\u8fd9\u6837\uff0c\u786e\u662f\u65e0 \u6cd5\u6d3b\u7740\u51fa\u53bb\u3002\u201d\u95ee\u9053\uff1a\u201c\u90a3\u4f60\u5462\uff1f\u4f60\u53c8\u600e\u6837\u51fa\u53bb\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u53e6\u5916\u6709\u529e\u6cd5\uff0c\u4f60\u4e0d \u4e45\u5c31\u4f1a\u77e5\u9053\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u82e5\u56e0\u6b64\u62d6\u7d2f\u4e86\u4f60\uff0c\u6211\u8fd8\u662f\u4e0d\u51fa\u53bb\u7684\u597d\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4e0d \u51fa\u53bb\uff0c\u90a3\u5c31\u8981\u62d6\u7d2f\u66f4\u591a\u7684\u4eba\u4e86\u3002\u8c37\u795e\u7fc1\u4ed6\u4eec\u4e00\u5b9a\u8981\u6551\u4f60\u7684\uff0c\u5bab\u4e2d\u597d\u624b\u5982\u4e91\uff0c\u4f60\u5c31\u4e0d\u6015\u4ed6 \u4eec\u9001\u547d\u5417\uff1f\u4f60\u653e\u5fc3\uff0c\u6211\u8bf4\u8fc7\u6709\u529e\u6cd5\u51fa\u53bb\u5c31\u662f\u6709\u529e\u6cd5\u51fa\u53bb\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u597d\uff0c\u6211\u76f8\u4fe1\u4f60\uff01\u201d \u53d6\u4e86\u90a3\u5305\u836f\u6563\uff0c\u7acb\u5373\u670d\u4e0b\u3002 \u3000\u3000\u957f\u5b59\u58c1\u6276\u7740\u4e08\u592b\u7684\u8eab\u8eaf\uff0c\u8ba9\u4ed6\u6162\u6162\u8eba\u4e0b\uff0c\u674e\u9038\u670d\u836f\u4e4b\u524d\u5bf9\u6b66\u7384\u971c\u7684\u5173\u6000\u4ee4\u5979\u611f\u5230\u4e00 \u80a1\u9178\u5473\uff0c\u5979\u5ffd\u5730\u6293\u7740\u6b66\u7384\u971c\u7684\u624b\u9053\uff1a\u201c\u4f60\u8fd9\u836f\u6563\u4e5f\u7ed9\u6211\u4e00\u5305\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u8fd8\u8981\u4f60\u6599\u7406\u2018\u540e\u4e8b\u2019\u5462\uff0c\u4f60\u8981\u8fd9\u836f\u7c89\u505a\u4ec0\u4e48\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a \u201c\u5269\u6211\u4e00\u4eba\u5728\u8fd9\u7a81\u53a5\u5bab\u4e2d\uff0c\u6211\u5fc3\u91cc\u6709\u70b9\u5bb3\u6015\u3002\u597d\u59d0\u59d0\uff0c\u4f60\u5c31\u7ed9\u4e86\u6211\u5427\uff0c\u6211\u5907\u800c\u4e0d\u7528\u4e5f\u597d\u3002\u201d \u6b66\u7384\u971c\u4e00\u60f3\u4e5f\u6709\u9053\u7406\uff0c\u7ec8\u4e8e\u7ed9\u4e86\u5979\u4e00\u5305\u836f\u7c89\u3002 \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u5728\u5916\u9762\u7b49\u4e86\u8bb8\u4e45\uff0c\u4e0d\u89c1\u4ed6\u4eec\u51fa\u6765\uff0c\u7740\u6025\u53eb\u9053\uff1a\u201c\u53ef\u8d3a\u6566\uff0c\u5927\u6c57\u8fd8\u7b49\u7740\u4f60\u8986 \u547d\u5462\uff0c\u4f60\u8981\u9001\u4ed6\u4eec\u51fa\u53bb\u5c31\u5feb\u70b9\u9001\u5427\uff0c\u53c8\u4e0d\u662f\u751f\u79bb\u6b7b\u522b\uff0c\u600e\u7684\u6709\u90a3\u4e48\u591a\u8bdd\u8981\u8bf4\uff01\u3002\u201d\u6b66\u7384 \u971c\u5927\u58f0\u5e94\u9053\uff1a\u201c\u4f60\u8bf7\u5927\u6c57\u6765\u5427\uff0c\u6211\u6709\u8bdd\u8981\u548c\u4ed6\u8bf4\uff0c\u201d\u767e\u4f18\u4e0a\u4eba\u5947\u9053\uff1a\u201c\u4f60\u8981\u5927\u6c57\u5230\u8fd9\u56da \u623f\u91cc\u505a\u4ec0\u4e48\uff1f\u6709\u8bdd\u4f60\u4e0d\u80fd\u5165\u5bab\u53bb\u8bf4\u5417\uff1f\u201d\u6b66\u7384\u971c\u65a5\u9053\uff1a\u201c\u8981\u4f60\u591a\u7ba1\uff1f\u4f60\u7ed9\u6211\u8bf7\u4ed6\u4fbf\u4e86\uff0c \u95ee\u4e9b\u4ec0\u4e48\uff1f\u201d\u767e\u4f18\u4e0a\u4eba\u662f\u56fd\u5e08\u8eab\u4efd\uff0c\u5927\u6c57\u5bf9\u4ed6\u4e5f\u8981\u5c0a\u656c\u51e0\u5206\uff0c\u88ab\u6b66\u7384\u971c\u65a5\u8d23\uff0c\u4e0d\u89c9\u706b\u8d77\uff0c \u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u65f6\u5019\u4e0d\u65e9\uff0c\u4f60\u504f\u8981\u7f20\u5939\u4e0d\u6e05\uff0c\u597d\u5427\uff0c\u4f60\u65e2\u7136\u6709\u8bdd\u8981\u548c\u5927\u6c57\u8bf4\u53bb\uff0c\u8fd9\u4e24\u4e2a\u4eba \u6211\u5c31\u66ff\u4f60\u9001\u4ed6\u4eec\u51fa\u5bab\u5427\u3002\u201d\u4ed6\u5fc3\u4e2d\u53ea\u60f3\u8d76\u5feb\u529e\u59a5\u8fd9\u4ef6\u5dee\u4e8b\uff0c\u5c06\u674e\u9038\u592b\u5987\u65e9\u70b9\u548c\u5979\u9694\u5f00\u3002 \u3000\u3000\u5fc3\u5ff5\u65b9\u52a8\uff0c\u5ffd\u542c\u5f97\u6b66\u7384\u971c\u51b7\u7b11\u9053\uff1a\u201c\u674e\u9038\u8fd8\u80fd\u591f\u6d3b\u7740\u51fa\u53bb\u5417\uff1f\u4f60\u771f\u662f\u505a\u68a6\u5566\uff01\u201d\u767e \u5fe7\u4e0a\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5979\u65e9\u5df2\u8bc6\u7834\u4e86\u5927\u6c57\u7684\u8ba1\u8c0b\uff1f\u201d\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u80fd\uff1f \u5927\u6c57\u7b54\u5e94\u8fc7\u7684\uff0c\u4f60\u8fd8\u4e0d\u76f8\u4fe1\u5417\uff1f\u201d\u6b66\u7384\u971c\u51b7\u7b11\u9053\uff1a\u201c\u5927\u6c57\u7b54\u5e94\uff0c\u6211\u53ef\u6ca1\u6709\u7b54\u5e94\u5440\u3002\u201d\u5728 \u6b66\u7384\u971c\u7684\u51b7\u7b11\u58f0\u4e2d\uff0c\u957f\u5b59\u58c1\u54ed\u6ce3\u7684\u58f0\u97f3\u4e5f\u4f20\u51fa\u6765\u4e86\u3002 \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u77e5\u9053\u5176\u4e2d\u5fc5\u6709\u8e4a\u8df7\uff0c\u6025\u5fd9\u53eb\u4eba\u53bb\u8bf7\u5927\u6c57\uff0c\u4ed6\u81ea\u5df1\u4e5f\u7acb\u5373\u63a8\u5f00\u94c1 \u95e8\uff0c\u8d70\u8fdb\u56da\u623f\u3002\u5b9a\u775b\u4e00\u770b\uff0c\u4f46\u89c1\u674e\u9038\u8eba\u5728\u5730\u4e0a\uff0c\u9762\u4e0a\u6beb\u65e0\u8840\u8272\uff0c\u7adf\u7136\u4e0d\u8c61\u662f\u4e2a\u751f\u4eba\uff0c\u957f \u5b59\u58c1\u6276\u7740\u5899\u58c1\uff0c\u54c0\u54c0\u54ed\u6ce3\uff0c\u6b66\u7384\u971c\u5374\u662f\u795e\u91c7\u98de\u626c\uff0c\u6602\u5934\u51b7\u7b11\u3002 \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u6363\u4ec0\u4e48\u9b3c\u5440\uff1f\u201d\u6025\u5fd9\u4fef\u4e0b\u8170\u8eaf\uff0c\u5c06\u674e\u9038\u62b1\u8d77\uff0c\u4e00\u63a2\u4ed6\u7684\u8109 \u606f\uff0c\u4e0d\u89c9\u53eb\u58f0\u201c\u82e6\u4e5f\uff01\u201d\u539f\u6765\u674e\u9038\u65e9\u5df2\u6c14\u7edd\u4e86\u3002\u4ed6\u77e5\u674e\u9038\u662f\u5927\u6c57\u6240\u8981\u5229\u7528\u7684\u4eba\uff0c\u867d\u7136\u4ed6 \u5014\u5f3a\u4e0d\u670d\uff0c\u4f46\u975e\u5230\u5b8c\u5168\u7edd\u671b\uff0c\u5927\u6c57\u8fd8\u662f\u4e0d\u80af\u5c06\u4ed6\u5904\u6b7b\u7684\u3002 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u5f20\u76ee\u7ed3\u820c\uff0c\u534a\u664c\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u662f\u4f60\u5c06\u4ed6\u6bd2\u6b7b\u7684\u5417\uff1f\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u662f \u6211\u53c8\u600e\u6837\uff1f\u4f60\u7ba1\u5f97\u4e86\u6211\u4e48\uff1f\u201d\u767e\u4f18\u4e0a\u4eba\u5706\u7741\u53cc\u773c\uff0c\u4f46\u6b66\u7384\u971c\u5230\u5e95\u662f\u5927\u6c57\u6240\u8981\u7684\u4eba\uff0c\u5728\u672a \u77e5\u9053\u5927\u6c57\u7684\u4e3b\u610f\u4e4b\u524d\uff0c\u767e\u5fe7\u4e0a\u4eba\u5374\u662f\u4e0d\u6562\u5bf9\u5979\u53d1\u4f5c\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u5927\u6c57\u5e26\u4e86\u6070\u514b\u56fe\u548c\u9ebb\u7ffc\u8d5e\u5306\u5306\u8d76\u6765\uff0c\u4e00\u8fdb\u56da\u623f\uff0c\u89c1\u6b64\u666f\u8c61\uff0c\u4e5f\u4e0d\u7981\u5413\u7740 \u4e86\uff0c\u8fde\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u6b66\u7384\u971c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5927\u6c57\uff0c\u4f60\u5fd8\u8bb0 \u4e86\u4e00\u4ef6\u4e8b\u5566\uff0c\u6211\u662f\u4e2d\u56fd\u5973\u7687\u5e1d\u7684\u4f84\u5973\u513f\uff01\u201d\u53c8\u542c\u957f\u5b59\u58c1\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5973\u597d\u72e0\u5fc3\u5440\uff0c\u5bb3 \u6b7b\u4e86\u6211\u7684\u4e08\u592b\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u7684\u4e08\u592b\u53cd\u6b63\u662f\u8981\u6b7b\u7684\uff0c\u4e0e\u5176\u6b7b\u5728\u654c\u4eba\u624b\u4e2d\uff0c\u4e0d\u5982\u6b7b\u5728 \u6211\u7684\u624b\u4e0a\uff0c\u4f60\u9a82\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a81\u53a5\u5927\u6c57\u5927\u60ca\u5931\u8272\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u5feb\u628a\u5927\u592b\u8bf7\u6765\uff0c\u4ece\u901f\u65bd\u6551\uff01\u201d\u6b66\u7384\u971c\u7b11\u9053\uff1a\u201c\u4e0d \u5fc5\u591a\u8d39\u5fc3\u4e86\uff0c\u4ed6\u65e9\u5df2\u6c14\u7edd\u591a\u65f6\uff0c\u5929\u4e0b\u6700\u6709\u672c\u9886\u7684\u5927\u592b\u4e5f\u4e0d\u80fd\u8d77\u6b7b\u56de\u751f\u4e86\uff01\u201d\u767e\u4f18\u4e0a\u4eba\u8fd8 \u5728\u62b1\u7740\u674e\u9038\u7684\u201c\u5c38\u4f53\u201d\uff0c\u5927\u6c57\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u8fd8\u6709\u6c14\u606f\u6ca1\u6709\uff1f\u201d\u767e\u5fe7\u4e0a\u4eba\u6447\u4e86\u6447\u5934\uff0c\u5c06 \u674e\u9038\u7684\u5c38\u4f53\u653e\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u8d3a\u6566\u4e0d\u77e5\u7528\u4e86\u4ec0\u4e48\u5389\u5bb3\u7684\u6bd2\u836f\uff0c\u53d1\u4f5c\u5f97\u771f\u5feb\uff0c\u8fd9\u4e2a\u4eba\u7684\u751f \u673a\u65e9\u5df2\u65ad\u7edd\uff01\u201d\u5927\u6c57\u987f\u8db3\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2014\u2014\u201d\u767e\u4f18\u4e0a\u4eba\u6025\u5fd9\u8fa9\u9053\uff1a\u201c\u53ef\u8d3a\u6566\u8981\u548c\u4ed6\u4eec\u5355 \u72ec\u4f1a\u9762\uff0c\u4f60\u7b54\u5e94\u8fc7\u5979\u7684\uff0c\u6211\u6ca1\u6562\u8fdb\u53bb\uff0c\u600e\u77e5\u9053\u5979\u4f1a\u7a81\u7136\u4e0b\u6bd2\uff1f\u201d\u5927\u6c57\u53cc\u773c\u5706\u7741\u2014\u2014\u77aa\u7740 \u6b66\u7384\u971c\u9053\uff1a\u201c\u6211\u662f\u8bf4\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0b\u6b64\u6bd2\u624b\uff1f\u201d \u3000\u3000\u6b66\u7384\u971c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u542c\u6e05\u695a\u5417\uff1f\u6211\u662f\u4e2d\u56fd\u5973\u7687\u5e1d\u7684\u4f84\u5973\u513f\u5440\uff01\u8fd9\u674e\u9038\u662f\u6211 \u59d1\u59d1\u7684\u654c\u4eba\uff0c\u4ed6\u843d\u5728\u4f60\u4eec\u7684\u624b\u4e2d\uff0c\u5c31\u662f\u6211\u59d1\u59d1\u7684\u5fc3\u8179\u5927\u60a3\uff0c\u6211\u600e\u80fd\u653e\u5fc3\u5f97\u4e0b\uff1f\u54c8\uff0c\u6709\u6b64 \u826f\u673a\uff0c\u6211\u5f53\u7136\u8981\u628a\u4ed6\u9664\u53bb\u4e86\uff01\u201d\u7a81\u53a5\u5927\u6c57\u81ea\u8d1f\u96c4\u624d\u5927\u7565\uff0c\u4e0d\u6599\u4eca\u65e5\u88ab\u4e00\u4e2a\u5973\u5b50\u6240\u9a97\uff0c\u767b \u65f6\u6012\u706b\u52c3\u8d77\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4e5f\u4e0d\u60f3\u6d3b\u5566\uff01\u201d\u5411\u767e\u4f18\u4e0a\u4eba\u629b\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u793a\u610f\u53eb \u4ed6\u5e9f\u53bb\u6b66\u7384\u971c\u7684\u6b66\u529f\uff0c\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u542c\u5f97\u6b66\u7384\u971c\u5df2\u5728\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6211\u5e72\u4e86\u8fd9\u6869\u5927\u4e8b\uff0c\u65e9 \u5c31\u4e0d\u6253\u7b97\u6d3b\u4e86\uff01\u201d\u767e\u4f18\u4e0a\u4eba\u811a\u6b65\u521a\u521a\u8e0f\u51fa\uff0c\u4f46\u89c1\u5979\u6643\u4e86\u4e24\u6643\uff0c\u535f\u7684\u4e00\u58f0\uff0c\u4fbf\u5012\u4e0b\u5730\u6765\uff01 \u539f\u6765\u5979\u65e9\u5df2\u628a\u90a3\u5305\u836f\u7c89\u653e\u5728\u53e3\u4e2d\uff0c\u4e00\u8bf4\u5b8c\u90a3\u51e0\u53e5\u8bdd\uff0c\u4fbf\u5373\u54ac\u7834\u5c01\u7eb8\uff0c\u5f85\u5230\u767e\u5fe7\u4e0a\u4eba\u8d76\u6765\uff0c \u5df2\u662f\u65e0\u6cd5\u89e3\u6551\u3002 \u3000\u3000\u53c8\u662f\u4e00\u4e2a\u610f\u6599\u4e0d\u5230\u7684\u7a81\u53d8\uff0c\u5927\u6c57\u5413\u5f97\u5446\u82e5\u6728\u9e21\uff0c\u597d\u534a\u664c\u624d\u987f\u8db3\u53eb\u9053\uff1b\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01 \u8fd9\u59d3\u6b66\u7684\u5973\u5b50\u771f\u5389\u5bb3\uff01\u201d\u773c\u770b\u6b66\u7384\u971c\u7389\u6b92\u9999\u9500\uff0c\u5fc3\u4e2d\u597d\u751f\u540e\u6094\u3002 \u3000\u3000\u6b66\u7384\u971c\u7684\u201c\u5c38\u4f53\u201d\u521a\u597d\u5012\u5728\u674e\u9038\u7684\u65c1\u8fb9\uff0c\u957f\u5b59\u58c1\u5fc3\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u5979\u4e5f\u662f\u60f3\u7740\u8fd9\u4e2a \u529e\u6cd5\u51fa\u53bb\u3002\u201d\u84e6\u5730\u53c8\u60f3\u8d77\u4e86\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u4e0d\u77e5\u5979\u8bf4\u7684\u662f\u771f\u662f\u5047\uff1f\u54ce\uff0c\u8981\u662f\u4e0d\u80fd\u6551\u6d3b\uff0c\u5979 \u548c\u6211\u7684\u674e\u9038\u54e5\u54e5\u5012\u662f\u6b7b\u540e\u505a\u4e86\u592b\u5987\u4e86\u3002\u5979\u751f\u524d\u4e0d\u80fd\u62a2\u8d70\u6211\u7684\u674e\u9038\u54e5\u54e5\uff0c\u83ab\u975e\u56e0\u6b64\u5c31\u60f3\u51fa \u8fd9\u4e2a\u6cd5\u513f\uff0c\u6c42\u5f97\u6b7b\u540e\u4e0e\u4ed6\u540c\u7a74\uff1f\u201d \u3000\u3000\u5927\u6c57\u5931\u610f\u4e4b\u6781\uff0c\u8fde\u58f0\u51b7\u7b11\uff0c\u76ee\u5149\u6e10\u6e10\u79fb\u5230\u4e86\u957f\u5b59\u58c1\u7684\u8eab\u4e0a\uff0c\u957f\u5b59\u58c1\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fc3\u9053\uff1a \u201c\u4e0d\u7ba1\u5979\u662f\u771f\u662f\u5047\uff0c\u6211\u603b\u5f97\u8bd5\u5b83\u4e00\u8bd5\u3002\u201d\u4fbf\u5728\u5927\u6c57\u9762\u524d\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u6211\u4e08\u592b\u88ab\u6bd2\u6b7b\u4e86\uff0c \u6740\u6211\u4e08\u592b\u7684\u51f6\u624b\u4e5f\u81ea\u5c3d\u4e86\uff0c\u6211\u4e0d\u5fc5\u6c42\u5927\u6c57\u66ff\u6211\u590f\u4ec7\u4e86\u3002\u4f46\u6c42\u5927\u6c57\u51c6\u8bb8\u6211\u5c06\u4ed6\u4eec\u7684\u5c38\u4f53\u9886 \u51fa\u53bb\u3002\u201d\u5927\u6c57\u6ca1\u7cbe\u6253\u91c7\u7684\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u628a\u4f60\u4e08\u592b\u7684\u5c38\u4f53\u9886\u51fa\u53bb\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u6211 \u4e08\u592b\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5bf9\u4f60\u662f\u4e00\u70b9\u7528\u5904\u4e5f\u6ca1\u6709\u5566\u3002\u4ed6\u5230\u5e95\u662f\u5927\u5510\u738b\u5b59\u7684\u8eab\u4efd\uff0c\u6211\u8981\u5c06\u4ed6\u7684\u9057\u4f53 \u9001\u56de\u4e2d\u56fd\u5b89\u846c\uff0c\u4f46\u6c42\u5927\u6c57\u5141\u8bb8\uff0c\u6211\u4e00\u751f\u90fd\u4f1a\u611f\u6fc0\u4f60\u7684\u5927\u6069\uff01\u201d\u5927\u6c57\u201c\u6664\u201d\u4e86\u4e00\u58f0\uff0c\u4e0d\u7f6e \u53ef\u5426\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u5979\u7684\u5c38\u4f53\u4f60\u4e5f\u8981\u9886\u51fa\u53bb\u4e48\uff1f\u201d\u957f\u5b59\u58c1\u5fc3\u5934\u4e00\u8df3\uff0c\u4f46\u89c1\u5927\u6c57\u6b63\u5728\u7528\u7740 \u6000\u7591\u7684\u76ee\u5149\uff0c\u624b\u6307\u7740\u5730\u4e0a\u7684\u6b66\u7384\u971c\u3002 \u3000\u3000\u957f\u5b59\u58c1\u7075\u673a\u4e00\u52a8\uff0c\u9547\u9759\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8bf7\u5927\u6c57\u6069\u51c6\uff0c\u6211\u5c06\u5979\u4e5f\u5e26\u56de\u56fd\u53bb\u3002\u201d\u5927\u6c57\u9053\uff1a \u201c\u4e3a\u4ec0\u4e48\uff1f\u5979\u4e0d\u662f\u4f60\u7684\u4ec7\u4eba\u5417\uff1f\u201d\u957f\u5b59\u58c1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u662f\u6211\u7684\u4ec7\u4eba\uff0c\u4f46\u5979\u4e5f\u662f\u6b66\u5219\u5929 \u7684\u4f84\u5973\u513f\u5440\u3002\u82e5\u662f\u6211\u53ea\u62a4\u9001\u6211\u4e08\u592b\u7684\u7075\u67a2\u56de\u56fd\uff0c\u6b66\u5219\u5929\u8033\u76ee\u4f17\u591a\uff0c\u5b9a\u7136\u77e5\u9053\uff0c\u5979\u5c82\u80af\u5bb9 \u6211\u5b89\u846c\u4e08\u592b\uff1f\u6b66\u5219\u5929\u624b\u6bb5\u72e0\u8fa3\uff0c\u6709\u4ec0\u4e48\u4e0d\u6562\u505a\u7684\uff1f\u6211\u6b7b\u4e0d\u8db3\u60dc\uff0c\u53ea\u6015\u5979\u5c06\u6211\u7684\u4e08\u592b\u6bc1\u68fa \u622e\u5c38\uff0c\u90a3\u5c31\u60e8\u4e86\u3002\u5982\u4eca\u6211\u5c06\u5979\u4f84\u5973\u513f\u7684\u68fa\u6750\u4e5f\u8fd0\u56de\u53bb\uff0c\u4e24\u5177\u68fa\u6750\uff0c\u5979\u4e0d\u77e5\u9053\u54ea\u4e00\u5177\u88c5\u7684 \u662f\u674e\u9038\uff0c\u54ea\u4e00\u5177\u88c5\u7684\u662f\u5979\u7684\u4f84\u5973\uff0c\u4e2d\u56fd\u7684\u98ce\u4fd7\uff0c\u4eba\u6b7b\u4e4b\u540e\uff0c\u9489\u4e0a\u4e86\u68fa\u76d6\u3002\u5c31\u518d\u4e5f\u4e0d\u80fd\u7ffb \u52a8\u4ed6\u7684\u5c38\u8eab\uff0c\u60ca\u6270\u9b3c\u9b42\uff0c\u8fd9\u6837\u6211\u5c06\u4e24\u5177\u68fa\u6750\u540c\u8fd0\u56de\u53bb\uff0c\u540c\u65f6\u4e0b\u846c\uff0c\u5979\u7eb5\u7136\u6d3e\u4eba\u6765\u6bc1\u68fa\u622e \u5c38\uff0c\u4e5f\u5f97\u6709\u6240\u987e\u5fcc\u4e86\u3002\u201d\u5927\u6c57\u55ef\u4e86\u4e00\u58f0\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u7684\u5fc3\u601d\u8fd9\u6837\u5468\u5bc6\uff0c\u54c8\uff0c \u786e\u662f\u4e2a\u806a\u660e\u4f36\u4fd0\u7684\u7f8e\u4eba\u513f\u3002\u201d \u3000\u3000\u957f\u5b59\u58c1\u634f\u7740\u4e00\u628a\u51b7\u6c57\uff0c\u5979\u7684\u4e08\u592b\u4e0e\u6b66\u7384\u971c\u80fd\u5426\u6709\u4e00\u7ebf\u751f\u673a\uff0c\u5c31\u5168\u770b\u5927\u6c57\u662f\u5426\u70b9\u5934\u4e86\uff0c \u5979\u62ac\u8d77\u5934\u6765\uff0c\u4f46\u89c1\u5927\u6c57\u4e5f\u6b63\u671b\u7740\u5979\uff0c\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f55\u987b\u8d39\u8fd9\u6837\u5927\u7684\u529b\u6c14\uff0c\u4eba\u6b7b\u4e86\uff0c \u5728\u90a3\u91cc\u57cb\u846c\u90fd\u662f\u4e00\u6837\u3002\u6211\u5c06\u4f60\u7684\u4e08\u592b\u7528\u7687\u5ba4\u4e4b\u793c\u57cb\u846c\uff0c\u7ed9\u4ed6\u4fee\u7b51\u5b8f\u4e3d\u7684\u9675\u5893\uff0c\u8ba9\u4f60\u5b89\u5fc3\u3002 \u4f60\u4e5f\u53ef\u4ee5\u7559\u5728\u6211\u7684\u5bab\u4e2d\uff0c\u4e0d\u5fc5\u518d\u56de\u53bb\u4e86\u3002\u201d\u957f\u5b59\u58c1\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2014\u2014\u201d\u5927 \u6c57\u628a\u624b\u4e00\u6325\uff0c\u7acb\u5373\u622a\u65ad\u5979\u7684\u8bdd\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u4f60\u7559\u4e0b\u6765\u966a\u4f34\u5b64\u738b\uff0c\u6c38\u4eab\u8363\u534e\u5bcc\u8d35\uff0c \u8fd9\u4e0d\u80dc\u4e8e\u4f60\u5192\u9669\u56de\u53bb\uff0c\u8981\u987e\u5fcc\u6b66\u5219\u5929\u7684\u8feb\u5bb3\u4e48\uff1f\u4e0d\u5fc5\u591a\u8a00\uff0c\u6715\u5df2\u4e3a\u4f60\u6253\u7b97\u5f97\u5341\u5206\u5468\u5230\u3002 \u5bab\u5973\uff0c\u5feb\u670d\u4f8d\u8fd9\u4f4d\u65b0\u738b\u5983\u5230\u540e\u5bab\u53bb\u6c90\u6d74\u66f4\u8863\uff01\u201d \u3000\u3000\u957f\u5b59\u58c1\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u60f3\u4e0d\u5230\u8d39\u4e86\u8bb8\u591a\u5507\u820c\uff0c\u7adf\u843d\u5f97\u5982\u6b64\u7ed3\u679c\uff0c\u4f46\u89c1\u4e24\u540d\u5bab\u5973\u5df2\u8d70 \u8fd1\u8eab\u6765\uff0c\u767e\u5fe7\u4e0a\u4eba\u864e\u89c6\u7708\u803d\uff0c\u53ea\u5f85\u5979\u7a0d\u6709\u53cd\u6297\uff0c\u4fbf\u8981\u52a8\u624b\uff0c\u957f\u5b59\u58c1\u54ac\u4e86\u54ac\u7259\uff0c\u8bf4\u9053\uff1a \u201c\u4e14\u6162\uff0c\u6211\u8fd8\u8981\u89c1\u6211\u4e08\u592b\u4e00\u9762\uff01\u201d\u5927\u6c57\u54c8\u54c8\u9053\uff1a\u201c\u77a7\u4f60\u4e0d\u51fa\uff0c\u5012\u662f\u4e00\u4e2a\u4e49\u91cd\u60c5\u957f\u7684\u5973\u5b50 \u5440\uff01\u597d\uff0c\u6715\u4fbf\u8ba9\u4f60\u4e86\u6b64\u5fc3\u613f\uff0c\u5411\u4e08\u592b\u544a\u8f9e\u5427\uff01\u201d\u957f\u5b59\u58c1\u5728\u4ed6\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u5c31\u4fef\u4e0b\u67f3\u8170\uff0c \u51dd\u671b\u7740\u674e\u9038\u7684\u9762\u5b54\uff0c\u7a81\u7136\u5c06\u90a3\u5305\u836f\u7c89\u541e\u4e0b\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u9038\u54e5\u54e5\uff0c\u4f60\u6162\u8d70\u4e00\u6b65\uff0c\u7b49\u7b49\u6211\u5427\uff01\u201d \u5fc3\u9053\uff1a\u201c\u4e0d\u7ba1\u771f\u6b7b\u5047\u6b7b\uff0c\u6211\u603b\u662f\u6b7b\u5728\u4ed6\u7684\u6000\u4e2d\u4e86\uff01\u201d\u8ff7\u7cca\u4e2d\u4f46\u89c9\u674e\u9038\u7d27\u7d27\u7684\u6402\u7740\u5979\uff0c\u5979 \u5fc3\u6ee1\u610f\u8db3\uff0c\u53cc\u773c\u95ed\u4e0a\uff0c\u518d\u4e5f\u6ca1\u6709\u77e5\u89c9\u4e86\u3002 \u3000\u3000\u5bab\u5973\u5927\u5403\u4e00\u60ca\uff0c\u4e0a\u524d\u53bb\u62c9\uff0c\u957f\u5b59\u58c1\u8eba\u5728\u674e\u9038\u6000\u4e2d\uff0c\u7d27\u6402\u7740\u4ed6\u7684\u4e08\u592b\uff0c\u5bab\u5973\u7adf\u7136\u5206\u4e0d \u5f00\u4ed6\u4eec\u3002\u5927\u6c57\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e2d\u56fd\u7684\u5973\u5b50\u5982\u6b64\u8d1e\u70c8\uff0c\u6715\u7adf\u662f\u4e00\u4e2a\u4e5f\u4fdd\u4e0d\u4f4f\uff01\u771f \u662f\u4ee4\u6715\u53c8\u662f\u5931\u671b\uff0c\u53c8\u662f\u656c\u4f69\uff01\u8fd9\u674e\u9038\u4e5f\u7b97\u5f97\u662f\u4e2a\u597d\u6c49\u5b50\uff0c\u5927\u4e08\u592b\uff0c\u6715\u4e00\u8a00\u65e2\u51fa\uff0c\u4e0d\u518d\u66f4 \u6539\uff0c\u5c06\u4ed6\u4eec\u4f9d\u793c\u5b89\u4e27\u4e86\u5427\uff01\u201d\u90c1\u90c1\u4e0d\u4e50\uff0c\u62c2\u8896\u9000\u5165\u540e\u5bab\u3002 \u3000\u3000\u6570\u65e5\u4e4b\u540e\uff0c\u7a81\u53a5\u738b\u57ce\u7684\u897f\u90ca\u6dfb\u4e86\u4e00\u5ea7\u65b0\u575f\uff0c\u4ed6\u751f\u524d\u6069\u6028\u7ea0\u7f20\uff0c\u6b7b\u540e\u5374\u90fd\u57cb\u5728\u4e00\u5904\u4e86\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u5fcf\u60c5\u6167\u5251\u65ad\u60c5\u6839 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u5fcf\u60c5\u6167\u5251\u65ad\u60c5\u6839 \u3000\u3000\u5728\u7a81\u53a5\u738b\u57ce\u897f\u9762\u7684\u5929\u683c\u5c14\u5c71\u5c71\u4e0b\uff0c\u662f\u4e00\u5927\u7247\u8352\u91ce\uff0c\u8352\u91ce\u4e0a\u6709\u4e00\u5ea7\u65b0\u575f\uff0c\u8fd9\u4e00\u665a\uff0c\u5927 \u7ea6\u4e09\u66f4\u65f6\u5206\uff0c\u957f\u6797\u8302\u8349\u4e4b\u95f4\uff0c\u5ffd\u7136\u51fa\u73b0\u56db\u6761\u4eba\u5f71\uff0c\u8fd9\u56db\u4e2a\u4eba\u6b63\u662f\u590f\u4faf\u575a\u3001\u88f4\u53d4\u5ea6\u3001\u7b26\u4e0d \u7591\u548c\u8c37\u795e\u7fc1\u3002\u4ed6\u4eec\u662f\u6765\u6398\u674e\u9038\u7684\u575f\u5893\u7684\u3002\u8fd9\u4e00\u5929\u6070\u597d\u662f\u674e\u9038\u7b49\u4eba\u670d\u836f\u81ea\u5c3d\u4e4b\u540e\u7684\u7b2c\u516d\u5929\u3002 \u6765\u5230\u575f\u524d\uff0c\u88f4\u53d4\u5ea6\u63e3\u63e3\u4e0d\u5b89\uff0c\u6084\u6084\u95ee\u9053\uff1a\u201c\u5f53\u771f\u8fd8\u6551\u5f97\u6d3b\u4e48\uff1f\u201d\u590f\u4faf\u575a\u7b11\u9053\uff1a\u201c\u82e5\u662f\u54b1 \u4eec\u8fdf\u4e00\u5929\uff0c\u90a3\u5c31\u96be\u4fdd\u4e86\u3002\u73b0\u5728\u6765\u7684\u6070\u662f\u65f6\u4faf\u3002\u94f2\u5427\uff01\u201d\u56db\u67c4\u5927\u94c1\u94f2\u540c\u65f6\u94f2\u4e0b\uff0c\u4e0d\u6d88\u7247\u523b\uff0c \u5df2\u94f2\u53bb\u4e86\u575f\u5934\uff0c\u9732\u51fa\u6d1e\u7a74\uff0c\u88f4\u53d4\u5ea6\u4fef\u8eab\u4e00\u77a7\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u53ea\u6709\u4e24\u5177\u68fa\u6750\u3002\u201d \u3000\u3000\u590f\u4faf\u575a\u6b63\u5f85\u5bdf\u770b\uff0c\u5ffd\u542c\u5f97\u4e71\u4e1b\u4e2d\u54b3\u568e\u7684\u51e0\u58f0\u8f7b\u54cd\uff0c\u590f\u4faf\u575a\u7b11\u9053\uff1a\u201c\u539f\u6765\u8fd9\u91cc\u8fd8\u57cb\u4f0f \u6709\u770b\u575f\u7684\u5462\uff0c\u54b1\u4eec\u4e5f\u4e0d\u80fd\u592a\u5927\u610f\u4e86\u3002\u201d\u7b26\u4e0d\u7591\u6293\u8d77\u4e00\u628a\u788e\u77f3\uff0c\u626c\u624b\u6253\u53bb\uff0c\u767b\u65f6\u542c\u5f97\u6709\u51e0 \u4e2a\u4eba\u6454\u5012\u5730\u4e0b\u7684\u58f0\u97f3\uff0c\u63a5\u7740\u6709\u4eba\u5927\u9a82\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u8fd9\u6837\u5927\u80c6\u3002\u6562\u6765\u5077\u6398\u738b\u5983\u7684\u575f\u5893\uff1f\u201d \u767b\u65f6\u5728\u65b0\u575f\u7684\u5357\u5317\u4e24\u9762\uff0c\u7a9c\u51fa\u4e86\u5341\u51e0\u6761\u9ed1\u5f71\uff0c\u6697\u5668\u5982\u8757\uff0c\u7eb7\u7eb7\u5c04\u81f3\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u548c\u590f\u4faf\u575a\u5404\u81ea\u53d1\u51fa\u4e00\u8bb0\u5288\u7a7a\u638c\uff0c\u5c06\u6697\u5668\u5728\u79bb\u8eab\u4e09\u4e08\u4e4b\u5916\u4fbf\u6253\u4e86\u4e0b\u6765\uff0c\u7b26\u4e0d\u7591 \u9053\uff1a\u201c\u5171\u662f\u4e00\u5341\u4e09\u4eba\uff0c\u5176\u4e2d\u6709\u4e2a\u529f\u529b\u8f83\u9ad8\uff0c\u8001\u8c37\uff0c\u53d4\u5ea6\uff0c\u4f60\u4eec\u4e24\u4eba\u5df2\u5c3d\u53ef\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d \u8c37\u795e\u7fc1\u88f4\u53d4\u5ea6\u62e8\u5251\u5954\u51fa\uff0c\u4e00\u4e2a\u8fce\u654c\u5357\u9762\u6765\u88ad\u7684\u654c\u4eba\uff0c\u4e00\u4e2a\u8fce\u654c\u5317\u9762\u6765\u88ad\u7684\u654c\u4eba\uff0c\u8352\u91ce\u91cc \u54cd\u8d77\u4e86\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u58f0\uff0c\u9707\u8033\u6b32\u88ad\uff0c\u7b26\u4e0d\u7591\u548c\u590f\u4faf\u575a\u4ecd\u5728\u4e13\u5fc3\u94f2\u571f\u3002\u5927\u7ea6\u8fc7\u4e86\u4e00\u652f\u9999\u7684 \u65f6\u523b\uff0c\u62fc\u6740\u7684\u58f0\u97f3\u9759\u6b62\u4e0b\u6765\u3002\u8c37\u88f4\u4e8c\u4eba\u56de\u6765\u62a5\u9053\uff1a\u201c\u60ed\u6127\u5f97\u5f88\uff0c\u8fd8\u662f\u8ba9\u4e09\u4e2a\u9e70\u722a\u5b59\u9003\u8dd1 \u4e86\u3002\u201d\u7b26\u4e0d\u7591\u9053\uff1a\u201c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u5f85\u4ed6\u4eec\u5c06\u6551\u5175\u8bf7\u6765\uff0c\u54b1\u4eec\u65e9\u5df2\u5b8c\u4e8b\u5566\u3002\u201d\u8c37\u795e\u7fc1\u7b11 \u9053\uff1a\u201c\u9057\u61be\u7684\u662f\u54b1\u4eec\u65b0\u7ec3\u6210\u7684\u8fd9\u5957\u5251\u672f\uff0c\u5374\u5c1a\u672a\u6709\u673a\u4f1a\u627e\u767e\u5fe7\u8001\u79c3\u4e00\u8bd5\u3002\u201d \u3000\u3000\u8fd9\u65f6\u575f\u5934\u5df2\u7ecf\u94f2\u5e73\uff0c\u9732\u51fa\u718f\u9edd\u9edd\u7684\u6d1e\u7a74\uff0c\u7ea6\u6709\u4e09\u4e08\u591a\u6df1\uff0c\u7b26\u4e0d\u7591\u53d6\u51fa\u4e24\u6761\u957f\u957f\u7684\u94c1 \u7d22\uff0c\u7d22\u7aef\u6709\u4e2a\u5c16\u94a7\uff0c\u4ed6\u4e0e\u8c37\u795e\u7fc1\u5404\u6267\u4e00\u6761\uff0c\u5782\u4e0b\u6d1e\u7a74\uff0c\u5404\u52fe\u7740\u68fa\u6750\u7684\u4e00\u5934\uff0c\u7528\u529b\u6536\u7d27\u94c1 \u7d22\uff0c\u5c06\u68fa\u6750\u626f\u4e86\u4e0a\u6765\uff0c\u8c37\u795e\u7fc1\u7b11\u9053\uff1a\u201c\u53d4\u5ea6\uff0c\u4f60\u53ef\u4ee5\u5b89\u5fc3\u5566\uff0c\u7b2c\u4e00\u5177\u68fa\u6750\u6bd4\u7b2c\u4e8c\u5177\u68fa\u6750 \u8981\u6c89\u91cd\u5f97\u591a\uff0c\u91cc\u9762\u5b9a\u7136\u662f\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u96c7\u7684\u9a6c\u8f66\u8fd8\u6ca1\u6709\u6765\u5462\uff0c\u4f1a\u4e0d\u4f1a\u4ed6\u80c6\u602f\u4e0d\u6562\u6765\u4e86\u3002\u201d\u8c37\u795e\u7fc1\u62ac\u5934\u4e00\u671b\uff0c \u6708\u4eae\u5c06\u8fd1\u5929\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u8fd8\u672a\u5230\u7ea6\u5b9a\u7684\u65f6\u5019\u5462\uff0c\u4f60\u82e5\u5fc3\u6025\uff0c\u53ef\u4ee5\u5148\u63ed\u5f00\u68fa\u76ca\u770b\u770b\uff0c\u770b\u4f60 \u7684\u5e08\u59b9\u662f\u5426\u5728\u91cc\u9762\uff1f\u201d\u7b26\u4e0d\u7591\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\uff0c\u54a6\uff0c\u4e0d\u5bf9\uff0c\u4e0d\u662f\u9a6c\u8f66\uff0c\u662f\u51e0\u5339\u5feb \u9a6c\uff0c\u662f\u767e\u5fe7\u8001\u79c3\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u5f53\u524d\u4e00\u9a91\u3002\u65cb\u98ce\u4f3c\u7684\u75be\u5954\u800c\u81f3\uff0c\u8ddd\u79bb\u8fd8\u6709\u5341\u591a\u4e08\u8fdc\uff0c \u9a6c\u4e0a\u7684\u9a91\u5ba2\u4fbf\u5373\u98de\u8eab\u8dc3\u8d77\uff0c\u843d\u5728\u575f\u524d\uff0c\u9762\u5bf9\u4f17\u4eba\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u6b63\u662f\u767e\u4f18\u4e0a\u4eba\u3002 \u3000\u3000\u539f\u6765\u767e\u5fe7\u4e0a\u4eba\u65e9\u5c31\u6599\u5230\u4ed6\u4eec\u4f1a\u6765\u4e0a\u575f\uff0c\u4f46\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u5f53\u7136\u4e0d\u80fd\u6bcf\u65f6\u5728\u575f\u524d\u5b88\u5019\uff0c \u56e0\u6b64\u4ed6\u4e00\u9762\u8bf7\u5927\u6c57\u6d3e\u51fa\u5341\u4e09\u4e2a\u4e00\u7b49\u6b66\u58eb\u5b88\u575f\uff0c\u4ed6\u81ea\u5df1\u5219\u548c\u706d\u5ea6\u795e\u541b\u7b49\u4eba\u5728\u79bb\u575f\u4e03\u3001\u516b\u91cc \u5916\u7684\u4e00\u4e2a\u536b\u6240\u4f4f\u5bbf\uff0c\u51c6\u5907\u968f\u65f6\u63a5\u5e94\uff0c\u662f\u4ee5\u95fb\u62a5\u5373\u6765\uff0c\u4f46\u51fa\u4e4e\u7b26\u4e0d\u7591\u7b49\u4eba\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u4f46\u89c1\u767e\u4f18\u4e0a\u4eba\u8fc5\u7740\u98d8\u98ce\uff0c\u8eab\u5f62\u672a\u5b9a\uff0c\u7acb\u5373\u4fbf\u5411\u8c37\u795e\u7fc1\u6293\u53bb\uff0c\u8c37\u795e\u524d\u4ee5\u8e51\u4e91\u6b65\u6cd5\u95ea\u5f00\uff0c \u7b26\u4e0d\u7591\u633a\u5251\u4fbf\u523a\uff0c\u767e\u4f18\u4e0a\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e24\u6b21\u4ea4\u624b\uff0c\u90fd\u672a\u66fe\u5206\u51fa\u80dc\u8d1f\uff0c\u4eca\u665a\u518d\u75db\u75db \u5feb\u5feb\u7684\u6253\u4e00\u573a\u5427\uff01\u201d\u7b26\u4e0d\u7591\u662f\u56db\u5927\u5251\u5ba2\u4e4b\u9996\uff0c\u8fd9\u4e00\u5251\u6765\u5f97\u51cc\u5389\u975e\u5e38\uff0c\u767e\u5fe7\u4e0a\u4eba\u4e00\u5ff5\u8f7b\u654c\uff0c \u4e3e\u8896\u4e00\u62c2\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8896\u7ba1\u5df2\u88ab\u524a\u53bb\u4e00\u622a\u3002 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u521a\u62c2\u5f00\u7b26\u4e0d\u7591\u7684\u957f\u5251\uff0c\u53ea\u542c\u5f97\u80cc\u540e\u201c\u5220\u201d\u7684\u4e00\u58f0\uff0c\u8c37\u795e\u7fc1\u4ea6\u5df2\u62d4\u5251\u523a\u6765\uff0c \u767e\u5fe7\u4e0a\u4eba\u659c\u8dc3\u6570\u4e08\uff0c\u63d0\u8d77\u7985\u6756\u7b11\u9053\uff1a\u201c\u7a77\u9178\uff0c\u4f60\u7684\u5251\u6cd5\u4e0d\u574f\u5440\uff0c\u4eca\u665a\u4e5f\u53eb\u4f60\u89c1\u8bc6\u89c1\u8bc6\u8001 \u7eb3\u7684\u4f0f\u9b54\u6756\u6cd5\u5427\uff01\u201d\u4ed6\u77e5\u9053\u5728\u56db\u4e2a\u654c\u4eba\u4e4b\u4e2d\uff0c\u4ee5\u7b26\u4e0d\u7591\u7684\u672c\u9886\u6700\u5f3a\uff0c\u6545\u6b64\u5148\u5411\u4ed6\u53eb\u9635\uff0c \u4f46\u4ed6\u63b8\u6756\u4e00\u6325\uff0c\u5374\u5148\u78b0\u4e0a\u4e86\u8c37\u795e\u7fc1\u7684\u957f\u5251\uff0c\u8c37\u795e\u7fc1\u5185\u529b\u900a\u4ed6\u4e00\u7b79\uff0c\u8fd9\u4e00\u4e0b\u786c\u78b0\u786c\u63a5\uff0c\u7adf \u7ed9\u4ed6\u9707\u5f97\u864e\u53e3\u9178\u9ebb\uff0c\u957f\u5251\u51e0\u4e4e\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u559d\u9053\uff1a\u201c\u63a5\u62db\uff01\u201d\u4ed6\u4e0d\u80af\u5077\u88ad\uff0c\u5148\u559d\u4e00\u58f0\uff0c\u767e\u5fe7\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u6765\u5427\uff0c\u5175\u5668\u4e0a \u54b1\u4eec\u8fd8\u672a\u8f83\u91cf\u8fc7\u5462\uff1f\u201d\u63b8\u6756\u6325\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u5c06\u7b26\u4e0d\u7591\u7684\u8eab\u5f62\u7f69\u4f4f\uff0c\u7b26\u4e0d\u7591\u7528\u4e86\u62db\u201c\u6a2a\u6307 \u5929\u5357\u201d\uff0c\u5251\u5149\u5982\u77e2\uff0c\u900f\u8fc7\u4e86\u4ed6\u7684\u5305\u56f4\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u9707\u5f97\u8033\u9f13 \u55e1\u55e1\u4f5c\u54cd\uff0c\u7b26\u4e0d\u7591\u6536\u5251\u4e00\u770b\uff0c\u53ea\u89c1\u5230\u5251\u5203\u4e0a\u5df2\u635f\u4e86\u4e09\u5904\u7f3a\u53e3\uff0c\u8981\u77e5\u767e\u5fe7\u4e0a\u4eba\u7684\u5185\u529b\u672c\u6765 \u7565\u80dc\u5c11\u8bb8\uff0c\u4ee5\u4ed6\u7684\u7985\u6756\u6c89\u91cd\uff0c\u6240\u4ee5\u7b26\u4e0d\u7591\u4e00\u4ea4\u624b\u4fbf\u5403\u4e86\u70b9\u5c0f\u4e8f\u3002 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u670d\u4e0d\u670d\uff0c\u4e0d\u670d\u518d\u6765\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u540e\u9762\u51e0\u9a91\u5feb\u9a6c\u4ea6\u5df2\u8d76\u5230\uff0c \u4e43\u662f\u706d\u5ea6\u795e\u541b\u3001\u9ebb\u7ffc\u8d5e\u548c\u83e9\u63d0\u4e0a\u4eba\u3002 \u3000\u3000\u7b26\u4e0d\u7591\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e5f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u8fb9\u56db\u4eba\uff0c\u4f60\u4eec\u6765\u7684\u4e5f\u662f\u56db\u4eba\uff0c\u6b63\u597d\u51b3\u4e2a\u96cc \u96c4\uff0c\u8c01\u90fd\u4e0d\u5fc5\u4ee5\u591a\u4e3a\u80dc\u3002\u201d\u767e\u5fe7\u4e0a\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5212\u51fa\u9053\u513f\u6765\u5427\uff0c\u662f\u53cc\u65b9\u4e00\u9f50 \u4e0a\u5462\uff0c\u8fd8\u662f\u5355\u6253\u72ec\u95e8\uff1f\u4eca\u665a\u65e2\u662f\u4eba\u6570\u76f8\u7b49\uff0c\u8981\u6253\u5c31\u5f97\u5224\u4e2a\u96cc\u96c4\uff0c\u4f60\u4eec\u53ef\u4e0d\u8981\u518d\u50cf\u4e0a\u4e24\u6b21 \u4e00\u6837\uff0c\u672b\u5f85\u5b8c\u573a\uff0c\u5c31\u6e9c\u8d70\u4e86\u3002\u201d \u3000\u3000\u7b26\u4e0d\u7591\u7b11\u9053\uff1a\u201c\u4e0a\u4eba\u6b64\u8a00\uff0c\u6df1\u5408\u543e\u5fc3\u3002\u4e8b\u4e0d\u8fc7\u4e09\uff0c\u4eca\u665a\u4e00\u5b9a\u51b3\u4e2a\u80dc\u8d1f\u4fbf\u662f\u3002\u4f60\u4eec\u57df \u5916\u4e09\u51f6\uff0c\u5982\u540c\u4e00\u4f53\uff0c\u6211\u548c\u8001\u8c37\u4e5f\u662f\u516b\u62dc\u4e4b\u4ea4\u7684\u670b\u53cb\uff0c\u597d\uff0c\u6211\u548c\u8001\u8c37\u613f\u4e0e\u4f60\u4eec\u57df\u5916\u4e09\u51f6\u5148 \u51b3\u4e2a\u96cc\u96c4\uff0c\u5440\uff0c\u53ea\u53ef\u60dc\u4f60\u4eec\u7684\u5929\u6076\u9053\u4eba\u6b7b\u4e86\uff0c\u4e09\u51f6\u53ea\u80fd\u6539\u79f0\u4e24\u51f6\u5566\uff01\u201d\u767e\u5fe7\u4e0a\u4eba\u6012\u6c14\u52c3 \u53d1\uff0c\u63b8\u6756\u4e00\u6446\uff0c\u53eb\u9053\uff1a\u201c\u706d\u5ea6\u8001\u5f1f\uff0c\u54b1\u4eec\u4eca\u665a\u4e0e\u5929\u6076\u62a5\u4ec7\uff01\u7a77\u9178\uff0c\u4f9d\u4f60\u6240\u8a00\uff0c\u4f60\u4eec\u4e24\u4e2a \u6765\u5427\uff01\u201d \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u83e9\u63d0\u4e0a\u4eba\u4e5f\u5411\u590f\u4faf\u575a\u53eb\u9635\uff0c\u4ed6\u6709\u70b9\u5fcc\u60ee\u590f\u4faf\u575a\u201c\u91d1\u9488\u523a\u7a74\u201d\u7684\u672c\u9886\uff0c\u63d0\u51fa \u8981\u548c\u590f\u4faf\u575a\u8f83\u91cf\u5185\u529f\uff0c\u590f\u4faf\u575a\u9053\uff1a\u201c\u4e45\u4ef0\u4e0a\u4eba\u662f\u7a81\u53a5\u7b2c\u4e00\u9ad8\u624b\uff0c\u8001\u673d\u4f53\u5f31\u6c14\u8870\uff0c\u87b3\u8782\u6321 \u8f66\uff0c\u81ea\u662f\u4e0d\u582a\u4e00\u51fb\uff0c\u4f46\u4e0a\u4eba\u6709\u547d\uff0c\u8001\u673d\u6562\u4e0d\u820d\u547d\u5949\u966a\u4e86\uff0c\u4fbf\u8bf7\u4e0a\u4eba\u5212\u51fa\u9053\u513f\u6765\u5427\u3002\u201d\u83e9 \u63d0\u4e0a\u4eba\u89c1\u4ed6\u7b54\u5e94\uff0c\u6ee1\u5fc3\u6b22\u559c\uff0c\u4fbf\u6307\u7740\u4e00\u5757\u5706\u5982\u955c\u53f0\u7684\u5927\u77f3\u8bf4\u9053\uff1a\u201c\u590f\u4faf\u5148\u751f\u4e0d\u5fc5\u8fc7\u8c26\uff0c \u6211\u4e45\u95fb\u4e2d\u58eb\u7684\u6b66\u5b66\u7cae\u6df1\uff0c\u5185\u529f\u5c24\u5176\u5965\u5999\uff0c\u4eca\u65e5\u6b63\u597d\u4e92\u76f8\u5370\u8bc1\u5370\u8bc1\u3002\u5c31\u5728\u8fd9\u5757\u5927\u77f3\u4e0a\u6bd4\u8bd5 \u5982\u4f55\uff0c\u8c01\u8981\u662f\u8dcc\u4e86\u4e0b\u6765\uff0c\u90a3\u5c31\u7b97\u8f93\u4e86\u3002\u201d\u590f\u4faf\u575a\u9053\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4e24\u4eba\u4fbf\u5728\u77f3\u4e0a\u76d8\u819d \u800c\u5750\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u5f00\u59cb\u6bd4\u62da\u3002 \u3000\u3000\u8fd8\u5269\u4e0b\u4e00\u4e2a\u9ebb\u7ffc\u8d5e\uff0c\u88f4\u53d4\u5ea6\u4e00\u770b\uff0c\u9ebb\u7ffc\u8d5e\u624b\u4e2d\u62ff\u7684\u6b63\u662f\u674e\u9038\u90a3\u628a\u5b9d\u5251\uff0c\u539f\u6765\u9ebb\u7ffc\u8d5e \u4e43\u662f\u5410\u8c37\u6d51\u7684\u5251\u672f\u540d\u5bb6\uff0c\u674e\u9038\u201c\u6b7b\u201d\u540e\uff0c\u4ed6\u4fbf\u8bf7\u6c42\u5927\u6c57\u5c06\u8fd9\u628a\u5b9d\u5251\u8d50\u7ed9\u4ed6\u3002\u88f4\u53d4\u5ea6\u5b58\u5fc3 \u8981\u7ed9\u674e\u9038\u8981\u56de\u5b9d\u5251\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u4f60\u6301\u6709\u5b9d\u5251\uff0c\u60f3\u5fc5\u662f\u7cbe\u901a\u5251\u672f\u7684\u4e86\uff0c \u6765\uff0c\u6765\uff0c\u6765\uff01\u6211\u4fbf\u5411\u4f60\u8bf7\u6559\u5251\u672f\uff01\u201d\u9ebb\u7ffc\u8d5e\u6b63\u60f3\u8bd5\u8bd5\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u542c\u88f4\u53d4\u5ea6\u8bf4\u8981\u548c\u4ed6\u6bd4 \u5251\uff0c\u81ea\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002 \u3000\u3000\u4e8e\u662f\u4e24\u65b9\u516b\u5927\u9ad8\u624b\u6210\u4e09\u5904\u640f\u6597\uff0c\u767e\u5fe7\u4e0a\u4eba\u9887\u4e3a\u8f7b\u654c\uff0c\u7985\u6756\u4e00\u8d77\u3002\u4e00\u62db\u201c\u795e\u9f99\u51fa\u6d77\u201d\uff0c \u5148\u5411\u7b26\u4e0d\u7591\u6253\u6765\uff0c\u7b26\u4e0d\u7591\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u8c37\uff0c\u4eca\u5929\u6709\u673a\u4f1a\u4e00\u8bd5\u5566\uff01\u201d\u9661\u7136\u95f4\u4f46\u89c1\u4e24\u9053\u5339 \u7ec3\u822c\u7684\u5251\u5149\uff0c\u53d8\u6210\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u5c06\u767e\u5fe7\u4e0a\u4eba\u7ede\u4f4f\uff0c\u767e\u5fe7\u4e0a\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u6025\u6025\u53d8\u62db\uff0c\u624b\u6267 \u7985\u6756\u4e2d\u95f4\uff0c\u65cb\u98ce\u75be\u821e\uff0c\u767b\u65f6\u6756\u5f71\u5982\u5c71\uff0c\u9976\u662f\u5982\u6b64\uff0c\u53cc\u5251\u4ece\u4ed6\u5934\u9876\u524a\u8fc7\uff0c\u767e\u4f18\u4e0a\u4eba\u4e5f\u89c9\u5f97 \u5934\u76ae\u4e00\u7247\u6c81\u51c9\uff0c\u82e5\u975e\u4ed6\u5e94\u53d8\u5f97\u5b9c\uff1b\u5929\u7075\u76d6\u65e9\u7ed9\u524a\u53bb\uff01\u706d\u5ea6\u795e\u541b\u6325\u52a8\u8f9f\u4e91\u9504\u53c2\u6218\uff0c\u867d\u7136\u7a0d \u7a0d\u51cf\u8f7b\u4e86\u767e\u5fe7\u4e0a\u4eba\u6240\u53d7\u7684\u5a01\u534f\uff0c\u4f46\u5374\u4ecd\u7136\u4e0d\u80fd\u51b2\u7834\u53cc\u5251\u6240\u6784\u6210\u7684\u5251\u5e55\uff01 \u3000\u3000\u767e\u5fe7\u4e0a\u4eba\u521d\u65f6\u4ee5\u4e3a\u81ea\u5df1\u7684\u6b66\u529f\u8981\u80dc\u8fc7\u7b26\u4e0d\u7591\u4e00\u7b79\uff0c\u706d\u5ea6\u795e\u541b\u867d\u7136\u8f83\u5f31\uff0c\u4f46\u6700\u5c11\u4e5f\u53ef \u4ee5\u548c\u8c37\u795e\u7fc1\u6253\u6210\u5e73\u624b\uff0c\u4ee5\u4e8c\u654c\u4e8c\uff0c\u90a3\u662f\u5fc5\u64cd\u80dc\u7b97\uff0c\u5c82\u77e5\u53cc\u5251\u5408\u4e00\u7684\u5a01\u529b\u5927\u5f97\u51fa\u5947\uff0c\u6597\u4e86 \u51e0\u5341\u62db\u5140\u662f\u672a\u80fd\u6273\u6210\u5e73\u624b\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u80c6\u5bd2\u3002 \u3000\u3000\u7b26\u8c37\u4e8c\u4eba\u4e43\u662f\u5251\u672f\u540d\u5bb6\uff0c\u7b2c\u4e00\u6b21\u65bd\u5c55\u8fd9\u5957\u53cc\u5251\u5408\u9a9b\u7684\u795e\u62db\u6570\uff0c\u521d\u65f6\u8fd8\u89c9\u7a0d\u6b20\u7eaf\u719f\uff0c \u6e10\u6e10\u4fbf\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\u3002\u767e\u4f18\u4e0a\u4eba\u5f00\u59cb\u8fd8\u53ef\u4ee5\u5360\u4e09\u56db\u6210\u653b\u52bf\uff0c\u5230\u4e86\u540e\u6765\uff0c\u4f7f\u5c3d\u6d51\u8eab\u672c\u9886\uff0c \u7adf\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u7b26\u4e0d\u7591\u5360\u4e86\u4e0a\u98ce\uff0c\u8d8a\u6218\u8d8a\u662f\u7cbe\u795e\uff0c\u5feb\u610f\u4e4b\u6781\uff0c\u4f46\u5fc3 \u4e2d\u5374\u4e5f\u6697\u6697\u53eb\u58f0\u201c\u4fa5\u5e78\u201d\uff0c\u60f3\u9053\uff1a\u201c\u8981\u662f\u767e\u5fe7\u8001\u79c3\u575a\u6301\u5355\u6253\u72ec\u6597\uff0c\u4eca\u5929\u53ef\u8981\u7cdf\u4e86\u3002\u201d\u8981 \u77e5\u4ee5\u7b26\u4e0d\u7591\u548c\u8c37\u795e\u7fc1\u7684\u8eab\u4efd\uff0c\u5f53\u7136\u4e0d\u80fd\u5939\u653b\u767e\u5fe7\u4e0a\u4eba\uff0c\u6240\u4ee5\u767e\u4f18\u4e0a\u4eba\u521d\u5230\u4e4b\u65f6\uff0c\u4ed6\u4eec\u4e8c \u4eba\u867d\u7136\u63a5\u8fde\u5403\u4e8f\uff0c\u5374\u8fd8\u662f\u4e0d\u613f\u65bd\u5c55\u51fa\u53cc\u5251\u5408\u58c1\u7684\u5251\u672f\uff0c\u5982\u4eca\u5bf9\u65b9\u867d\u7136\u591a\u4e86\u4e2a\u706d\u5ea6\u795e\u541b\uff0c \u4f46\u53cc\u5251\u5408\u58c1\uff0c\u5a01\u529b\u5927\u4e86\u4e00\u500d\u6709\u591a\uff0c\u7b49\u4e8e\u662f\u56db\u4e2a\u7b26\u4e0d\u7591\u548c\u4ed6\u4eec\u4f5c\u6218\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\u590f\u4faf\u575a\u4e0e\u83e9\u63d0\u4e0a\u4eba\u5728\u77f3\u4e0a\u8bd5\u5185\u529f\uff0c\u4e24\u4eba\u76d8\u819d\u800c\u5750\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u590f \u4faf\u575a\u4f46\u89c9\u6d51\u8eab\u53d1\u70ed\uff0c\u5bf9\u65b9\u7684\u624b\u638c\uff0c\u7adf\u4f3c\u70bd\u70ed\u7684\u706b\u70ad\u4e00\u822c\uff0c\u638c\u529b\u4e5f\u8d8a\u6765\u5f3a\u52b2\u4e86\u3002\u83e9\u63d0\u4e0a\u4eba \u5219\u89c9\u5f97\u5bf9\u65b9\u7684\u638c\u529b\u67d4\u548c\u4e4b\u6781\uff0c\u4f46\u4e0d\u8bba\u4ed6\u600e\u6837\u8fd0\u52b2\u5f3a\u653b\uff0c\u5374\u4f3c\u6309\u5728\u68c9\u82b1\u4e0a\u4e00\u822c\uff0c\u8f6f\u7ef5\u7ef5\u7684 \u5168\u4e0d\u53d7\u529b\uff0c\u4e5f\u770b\u4e0d\u51fa\u5bf9\u65b9\u6709\u4ec0\u4e48\u53cd\u5e94\u3002\u8fd9\u6837\u4e00\u67d4\u4e00\u521a\uff0c\u5f7c\u6b64\u76f8\u6301\uff0c\u8fc7\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c \u590f\u4faf\u575a\u7684\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u83e9\u63d0\u4e0a\u4eba\u4e5f\u6d41\u4e86\u4e00\u8eab\u51b7\u6c57\u3002\u539f\u6765\u83e9\u63d0\u4e0a\u4eba\u6240\u7ec3\u7684\u5185\u529f \u751a\u4e3a\u602a\u5f02\uff0c\u80fd\u4ee5\u672c\u8eab\u7684\u771f\u6c14\uff0c\u53d1\u4e3a\u9ad8\u70ed\uff0c\u4ee4\u5bf9\u65b9\u53d7\u5230\u714e\u71ac\u4e4b\u82e6\u3002\u82e5\u7136\u7981\u53d7\u4e0d\u8d77\uff0c\u88ab\u4ed6\u628a \u4f53\u5185\u7684\u6c34\u4efd\u201c\u6324\u201d\u5e72\uff0c\u90a3\u4e48\u7eb5\u7b97\u662f\u7b2c\u4e00\u6d41\u7684\u6b66\u529f\uff0c\u4e5f\u8981\u53d8\u6210\u5e9f\u4eba\u3002 \u3000\u3000\u590f\u4faf\u575a\u4ee5\u51e0\u5341\u5e74\u7cbe\u7eaf\u7684\u5185\u529f\uff0c\u7528\u7eaf\u67d4\u6765\u5bf9\u4ed8\u7eaf\u521a\uff0c\u6070\u597d\u662f\u529f\u529b\u6089\u654c\uff0c\u4e24\u96be\u53d6\u80dc\uff0c\u4f46 \u590f\u4faf\u575a\u60ac\u6302\u8001\u53cb\u7684\u5b89\u5371\uff0c\u4ed6\u6df1\u77e5\u767e\u4f18\u4e0a\u4eba\u4e43\u662f\u5f53\u4e16\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u7b26\u4e0d\u7591\u548c\u8c37\u795e\u7fc1\u867d\u7136\u7ec3 \u6210\u4e86\u6700\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u5374\u4e0d\u77e5\u80fd\u4e0d\u80fd\u514b\u5236\u4ed6\uff1f\u4ed6\u5fc3\u6709\u987e\u8651\uff0c\u53c8\u4e0d\u80fd\u5206\u795e\u53bb\u770b\uff0c\u800c\u4e14\u867d\u7136\u8bf4 \u53cc\u65b9\u4eba\u6570\u76f8\u7b49\uff0c\u7a76\u7adf\u662f\u5728\u654c\u4eba\u7684\u5305\u56f4\u4e4b\u4e2d\uff0c\u65f6\u95f4\u4e45\u4e86\uff0c\u96be\u4fdd\u6ca1\u6709\u5176\u4ed6\u53d8\u5316\uff0c\u9ad8\u624b\u6bd4\u6597\uff0c \u54ea\u5bb9\u5f97\u5fc3\u7eea\u7a0d\u6709\u4e0d\u5b81\uff1f\u590f\u4faf\u575a\u6e10\u89c9\u5947\u70ed\u96be\u5f53\uff0c\u4e0d\u7531\u5fc3\u5934\u4e00\u51db\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u706d\u5ea6\u795e\u541b\u4e00\u58f0\u5389\u53eb\uff0c\u767e\u4f18\u4e0a\u4eba\u6012\u543c\u5982\u96f7\uff0c\u542c\u90a3\u58f0\u97f3\uff0c\u4f3c\u4e4e\u662f\u706d\u5ea6 \u795e\u541b\u5df2\u53d7\u4e86\u4f24\uff0c\u767e\u4f18\u4e0a\u4eba\u5927\u7ea6\u4e5f\u5403\u4e86\u70b9\u4e8f\uff0c\u6240\u4ee5\u624d\u5fcd\u4e0d\u4f4f\u6012\u9a82\u3002 \u3000\u3000\u590f\u4faf\u575a\u731c\u5f97\u4e0d\u9519\uff0c\u7b26\u8c37\u4e8c\u4eba\u53cc\u5251\u5408\u58c1\uff0c\u8fd9\u65f6\u5df2\u4e0e\u767e\u4f18\u3001\u706d\u5ea6\u6597\u4e86\u4e09\u767e\u6765\u62db\uff0c\u4f18\u4e91\u8001 \u5c3c\u6240\u521b\u7684\u8fd9\u5957\u5251\u6cd5\u867d\u7136\u53ea\u6709\u4e09\u5341\u516d\u4e2a\u5f0f\u5b50\uff0c\u4f46\u4e24\u4eba\u5408\u7528\uff0c\u5404\u4f7f\u4e00\u62db\u4e0d\u540c\u7684\u62db\u6570\uff0c\u914d\u5408\u8d77 \u6765\uff0c\u53d8\u5316\u4fbf\u662f\u7a77\u5f97\u65e0\u5c3d\uff0c\u5947\u8be1\u5c3d\u4f26\uff01\u706d\u5ea6\u795e\u541b\u672c\u9886\u7a0d\u5dee\uff0c\u9996\u5148\u4e2d\u4e86\u8c37\u795e\u7fc1\u7684\u4e00\u5251\uff0c\u5e78\u5728 \u6ca1\u6709\u4f24\u7740\u9aa8\u5934\uff0c\u8fd8\u53ef\u4ee5\u652f\u6301\u5f97\u4f4f\u3002 \u3000\u3000\u83e9\u63d0\u4e0a\u4eba\u4e5f\u662f\u4e00\u4f4d\u6b66\u5b66\u5927\u5e08\uff0c\u5f53\u7136\u542c\u5f97\u51fa\u706d\u5ea6\u795e\u541b\u662f\u53d7\u4e86\u4f24\uff0c\u6700\u7cdf\u7684\u662f\u4ed6\u53c8\u4e0d\u80fd\u79fb \u5f00\u773c\u775b\u5bdf\u770b\uff0c\u4e0d\u77e5\u706d\u5ea6\u795e\u541b\u53d7\u4f24\u7684\u6df1\u6d45\u5982\u4f55\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u5fc3\u795e\u5f53\u7136\u5927\u53d7\u5f71\u54cd\uff0c\u4e0e\u4ed6\u76f8\u53cd\uff0c \u590f\u4faf\u575a\u5219\u662f\u7cbe\u795e\u4e00\u632f\uff0c\u4e0d\u6b62\u6273\u6210\u5e73\u624b\uff0c\u800c\u4e14\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u590f\u4faf\u575a\u4e0e\u83e9\u63d0\u4e0a\u4eba\u5c1a\u5728\u76f8\u6301\u4e0d\u4e0b\uff0c\u53e6\u4e00\u5bf9\u88f4\u53d4\u5ea6\u4e0e\u9ebb\u7ffc\u8d5e\u5219\u5230\u4e86\u751f\u6b7b\u7acb\u5224\u7684\u65f6\u523b\u3002 \u3000\u3000\u9ebb\u7ffc\u8d5e\u662f\u5410\u8c37\u6d51\u7684\u5251\u5b66\u5927\u5e08\uff0c\u4ed6\u7684\u5251\u96c6\u5404\u57df\u5404\u6d3e\u4e4b\u957f\uff0c\u51f6\u608d\u4e4b\u6781\uff0c\u4ed6\u89c1\u88f4\u53d4\u5ea6\u4e0d\u8fc7 \u662f\u4e2a\u4e09\u5341\u51e0\u5c81\u5de6\u53f3\u7684\u4e2d\u5e74\u4eba\uff0c\u6700\u521d\u9887\u4e3a\u8f7b\u654c\uff0c\u4e00\u51fa\u624b\u4fbf\u5c55\u5f00\u4e86\u4f24\u6b8b\u7684\u5251\u6cd5\uff0c\u7740\u7740\u8fdb\u653b\u3002 \u54ea\u77e5\u88f4\u53d4\u5ea6\u5e74\u7eaa\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u4ed6\u5728\u4f18\u4e91\u8001\u5c3c\u95e8\u4e0b\u6700\u4e45\uff0c\u5df2\u5c3d\u5f97\u4f18\u4e91\u8001\u5c3c\u5251\u5b66\u7684\u771f\u4f20\uff0c\u8bba \u4ed6\u73b0\u5728\u7684\u672c\u9886\uff0c\u9664\u4e86\u529f\u529b\u7a0d\u6b20\uff0c\u706b\u5019\u672a\u5230\u4e4b\u5916\uff0c\u5251\u672f\u4e0a\u7684\u9020\u8be3\u5df2\u4e0d\u5728\u7b26\u4e0d\u7591\u3001\u8c37\u795e\u7fc1\u4e4b \u4e0b\u3002\u9ebb\u7ffc\u8d5e\u7684\u653b\u52bf\u6709\u5982\u72c2\u98ce\u66b4\u96e8\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u62c6\u5f0f\uff0c\u6beb\u4e0d\u754f\u60e7\u3002 \u3000\u3000\u6597\u4e86\u4e00\u9635\uff0c\u9ebb\u7ffc\u8d5e\u5f3a\u653b\u4e0d\u5df2\uff0c\u4ed6\u6301\u7740\u6709\u4e00\u628a\u5b9d\u5251\uff0c\u6beb\u65e0\u987e\u5fcc\uff0c\u6a2a\u6311\u76f4\u523a\u3001\u5e73\u65ab\u659c\u524a\uff0c \u968f\u610f\u65bd\u4e3a\uff0c\u60f3\u4ed7\u7740\u5b9d\u5251\u4e4b\u529b\uff0c\u5148\u628a\u5bf9\u65b9\u7684\u5175\u5668\u524a\u65ad\uff0c\u88f4\u53d4\u5ea6\u5728\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u65bd\u5c55\u5f00\u4f5b \u95e8\u65e0\u76f8\u5251\u6cd5\uff0c\u5251\u62db\u8f7b\u98d8\u98d8\u7684\uff0c\u4e00\u53d1\u5373\u6536\uff0c\u4e4d\u6cbe\u5373\u9000\uff0c\u5982\u6709\u5982\u65e0\uff0c\u82e5\u865a\u82e5\u5b9e\uff0c\u4fe8\u5982\u5f69\u8776\u7a7f \u82b1\uff0c\u873b\u8713\u70b9\u6c34\u3002\u9ebb\u7ffc\u8d5e\u7684\u5251\u52bf\u867d\u7136\u52b2\u9053\u5341\u8db3\uff0c\u65e0\u5948\u5bf9\u65b9\u7684\u957f\u5251\u7adf\u4f3c\u4e00\u7247\u8f7b\u98d8\u98d8\u7684\u6811\u53f6\u4e00 \u822c\uff0c\u987a\u7740\u7684\u5251\u98ce\u98d8\u6765\u6641\u53bb\uff0c\u4efb\u4ed6\u7684\u5251\u52bf\u5982\u4f55\u5f3a\u52b2\uff0c\u5374\u603b\u662f\u65e0\u6cd5\u4f7f\u529b\u524a\u65ad\u5bf9\u65b9\u7684\u5175\u5203\u3002 \u3000\u3000\u9ebb\u7ffc\u8d5e\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u8fd9\u65f6\u54ea\u8fd8\u6709\u4e1d\u6beb\u8f7b\u654c\uff1f\u6597\u4e86\u4e00\u767e\u591a\u62db\uff0c\u88f4\u53d4\u5ea6\u4e58\u7740\u4ed6\u9510\u6c14 \u5df2\u6d88\uff0c\u529f\u52bf\u987f\u632b\u4e4b\u9645\uff0c\u7a81\u7136\u4e00\u58f0\u957f\u5578\uff0c\u53d1\u5251\u8fd8\u653b\uff0c\u5f53\u771f\u662f\u9759\u5982\u5904\u5b50\uff0c\u52a8\u82e5\u8131\u5154\uff0c\u5251\u62db\u5feb \u5f97\u51fa\u5947\uff0c\u9ebb\u7ffc\u8d5e\u867d\u7136\u6709\u628a\u5b9d\u5251\uff0c\u4f46\u5bf9\u65b9\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u4ed6\u6839\u672c\u5c31\u78b0\u4e0d\u7740\u5bf9\u65b9\u7684\u5175 \u5203\uff0c\u8fd9\u65f6\u4ed6\u4f46\u6c42\u80fd\u591f\u4ed7\u7740\u5b9d\u5251\u81ea\u4fdd\uff0c\u4e8e\u613f\u5df2\u8db3\uff0c\u54ea\u671b\u8fd8\u6562\u5f3a\u653b\uff1f\u6fc0\u6218\u4e2d\uff0c\u9ebb\u7ffc\u8d5e\u4f46\u89c1\u56db \u9762\u516b\u65b9\u90fd\u662f\u88f4\u53d4\u5ea6\u7684\u5f71\u5b50\uff0c\u7adf\u4f3c\u6709\u51e0\u5341\u628a\u5251\u540c\u65f6\u5411\u81ea\u5df1\u653b\u6765\uff0c\u4e0d\u7531\u5f97\u8d8a\u6218\u8d8a\u614c\uff0c\u88f4\u53d4\u5ea6 \u89c1\u65f6\u673a\u5df2\u5230\uff0c\u4e3e\u5251\u75be\u523a\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u9ebb\u7ffc\u8d5e\u7684\u624b\u8155\u88ab\u88f4\u53d4\u5ea6\u7684\u5251\u5c16\u70b9\u4e2d\uff0c \u88f4\u53d4\u5ea6\u7684\u957f\u5251\u4e5f\u7ed9\u9ebb\u7ffc\u8d5e\u7684\u957f\u5251\u524a\u65ad\uff0c\u9ebb\u7ffc\u8d5e\u8155\u8109\u88ab\u6311\u65ad\uff0c\u5b9d\u5251\u628a\u5f85\u4e0d\u4f4f\uff0c\u88f4\u53d4\u5ea6\u6254\u5251 \u593a\u5251\uff0c\u51e0\u4e2a\u52a8\u4f5c\u4e00\u6c14\u5475\u6210\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u9ebb\u7ffc\u8d5e\u6240\u5f97\u7684\u674e\u9038\u90a3\u628a\u5b9d\u5251\u5df2\u5230\u4e86\u4ed6\u7684\u624b\u4e2d\u3002 \u3000\u3000\u9ebb\u7ffc\u8d5e\u5931\u4e86\u5b9d\u5251\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u88f4\u53d4\u5ea6\u559d\u9053\uff1a\u201c\u9976\u4f60\u6027\u547d\uff0c\u8fd8\u4e0d\u8d70\u5417\uff1f\u201d\u9ebb\u7ffc\u8d5e\u8fd8\u60f3 \u53d1\u638c\u6b7b\u62da\uff0c\u4f46\u89c9\u624b\u81c2\u8f6f\u7ef5\u7ef5\u7684\uff0c\u4e3e\u4e0d\u8d77\u6765\uff0c\u9ebb\u7ffc\u8d5e\u60f3\u5230\u81ea\u5df1\u53f3\u624b\u7684\u8155\u8109\u88ab\u6311\uff0c\u6210\u4e86\u5e9f\u4eba\uff0c \u5df2\u662f\u7ec8\u751f\u4e0d\u80fd\u4f7f\u5251\u4e86\uff01\u7981\u4e0d\u4f4f\u4e00\u58f0\u60b2\u53f7\uff0c\u7528\u5de6\u624b\u62fe\u8d77\u5730\u4e0a\u7684\u4e00\u622a\u65ad\u5251\uff0c\u5ffd\u7136\u63d2\u8fdb\u4e86\u81ea\u5df1 \u7684\u80f8\u819b\uff0c\u539f\u6765\u4ed6\u4e00\u751f\u4ee5\u5251\u672f\u81ea\u8d1f\uff0c\u60f3\u5230\u81ea\u5df1\u4ece\u6b64\u4e0d\u80fd\u4f7f\u5251\uff0c\u4e00\u53e3\u6c14\u54bd\u4e0d\u8fc7\u6765\uff0c\u4fbf\u7518\u613f\u81ea \u5c3d\u4e86\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u89c1\u4ed6\u5982\u6b64\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u4e3a\u4ed6\u53f9\u606f\uff0c\u60f3\u9053\uff1a\u201c\u9ebb\u7ffc\u8d5e\u5012\u4e0d\u5931\u4e3a\u4e00\u6761\u6c49\u5b50\uff0c\u65e9\u77e5\u5982 \u6b64\uff0c\u6211\u5b9e\u8be5\u624b\u4e0b\u7559\u60c5\u3002\u201d\u5f53\u4e0b\u5c06\u9ebb\u7ffc\u8d5e\u8eab\u4e0a\u90a3\u628a\u5251\u9798\u4e5f\u53d6\u4e86\u8fc7\u6765\uff0c\u8fd8\u5251\u5f52\u9798\uff0c\u518d\u53bb\u89c2\u6218\u3002 \u3000\u3000\u8fd9\u65f6\u7b26\u8c37\u4e8c\u4eba\u4e0e\u767e\u5fe7\u3001\u706d\u5ea6\uff0c\u5df2\u6597\u4e86\u5c06\u8fd1\u4e94\u767e\u6765\u62db\uff0c\u767e\u4f18\u4e0a\u4eba\u81ea\u8d1f\u7edd\u4e16\u6b66\u529f\uff0c\u6599\u4e0d \u5230\u5728\u7b26\u8c37\u4e8c\u4eba\u53cc\u5251\u5408\u58c1\u4e4b\u4e0b\uff0c\u7adf\u662f\u4e00\u7b79\u83ab\u5c55\uff0c\u597d\u51e0\u5ea6\u5f3a\u653b\u731b\u6253\uff0c\u90fd\u51b2\u4e0d\u7834\u5bf9\u65b9\u53cc\u5251\u4ea4\u7ec7 \u7684\u5251\u5e55\uff0c\u672c\u6765\u5c31\u5df2\u6709\u70b9\u80c6\u602f\uff0c\u8fd9\u65f6\u89c1\u9ebb\u7ffc\u8d5e\u4e00\u6b7b\uff0c\u66f4\u4e3a\u5fc3\u5bd2\uff0c\u4f0f\u9b54\u6756\u6cd5\u7684\u5a01\u529b\u4e5f\u4e3a\u4e4b\u5927 \u51cf\uff0c\u6fc0\u6218\u4e2d\u7b26\u4e0d\u7591\u5ffd\u5730\u4e00\u58f0\u5927\u559d\uff0c\u957f\u5251\u4e00\u8d77\uff0c\u94f6\u8679\u75be\u5410\uff0c\u4f3c\u662f\u653b\u5411\u767e\u4f18\u4e0a\u4eba\uff0c\u5b9e\u662f\u6697\u88ad \u706d\u5ea6\u795e\u541b\uff0c\u767e\u5fe7\u4e0a\u4eba\u56de\u6756\u81ea\u4fdd\uff0c\u706d\u5ea6\u795e\u541b\u5982\u4f55\u6321\u5f97\u4f4f\u8fd9\u7b49\u795e\u5999\u7684\u5251\u62db\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c \u7b26\u8c37\u4e8c\u4eba\uff0c\u53cc\u5251\u75be\u53d1\uff0c\u4ea4\u53c9\u4e00\u526a\uff0c\u767b\u65f6\u628a\u706d\u5ea6\u795e\u541b\u65a9\u4e3a\u4e09\u6bb5\uff01 \u3000\u3000\u767e\u4f18\u4e0a\u4eba\u597d\u50cf\u53d7\u4f24\u7684\u91ce\u517d\u4f3c\u7684\uff0c\u84e6\u7136\u5927\u543c\u4e00\u58f0\uff0c\u4e00\u6756\u626b\u51fa\uff0c\u4ed6\u6025\u6012\u653b\u5fc3\uff0c\u62da\u6b7b\u51b3\u6218\uff0c \u8fd9\u4e00\u6756\u5b9e\u662f\u4ed6\u6bd5\u751f\u529f\u529b\u6240\u805a\uff0c\u4f46\u89c1\u52b2\u98ce\u8d77\u5904\uff0c\u7802\u77f3\u7eb7\u98de\uff0c\u771f\u6709\u6392\u5c71\u5012\u6d77\u4e4b\u52bf\uff0c\u98ce\u96f7\u5939\u51fb \u4e4b\u5a01\uff01\u5251\u5149\u6756\u5f71\u4e4b\u4e2d\uff0c\u53ea\u89c1\u7b26\u8c37\u4e8c\u4eba\u51cc\u7a7a\u98de\u8d77\uff0c\u534a\u7a7a\u4e2d\u500f\u7684\u5212\u8fc7\u4e24\u9053\u94f6\u8679\uff0c\u8eab\u6cd5\u4e4b\u5feb\uff0c \u62db\u6570\u4e4b\u5947\uff0c\u8fde\u88f4\u53d4\u5ea6\u8fd9\u6837\u6df1\u901a\u8fd9\u5957\u5251\u6cd5\u7684\u4eba\uff0c\u4e5f\u81ea\u76ee\u7729\u795e\u6447\uff0c\u672a\u66fe\u770b\u5f97\u6e05\u695a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\u4e4b\u95f4\uff0c\u53ea\u89c1\u4e24\u9053\u94f6\u8679\u4ea4\u53c9\u63a0\u8fc7\uff0c\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u5140\u81ea\u9707\u8033\u6b32\u804b\uff0c \u4f46\u8fd9\u4e09\u5927\u9ad8\u624b\u5374\u5df2\u5404\u81ea\u5206\u5f00\uff0c\u5404\u5728\u4e00\u65b9\u7ad9\u5b9a\uff0c\u88f4\u53d4\u5ea6\u773c\u5149\u77a5\u53bb\uff0c\u4f46\u89c1\u5730\u4e0a\u6709\u4e24\u622a\u65ad\u5251\uff0c \u800c\u767e\u4f18\u4e0a\u4eba\u7684\u8888\u88df\u5219\u5df2\u6210\u4e86\u8840\u888d\u3002\u539f\u6765\u521a\u624d\u5728\u8fd9\u4e00\u62db\u4e4b\u5185\uff0c\u767e\u4f18\u4e0a\u4eba\u8eab\u4e0a\u5df2\u662f\u53d7\u4e86\u4e03\u5904 \u5251\u4f24\uff0c\u800c\u8c37\u795e\u7fc1\u7684\u957f\u5251\u4e5f\u7ed9\u4ed6\u9707\u65ad\u4e86\u3002\u88f4\u53d4\u5ea6\u89c1\u767e\u5fe7\u4e0a\u4eba\u5728\u53cc\u5251\u5408\u58c1\u4e4b\u4e0b\uff0c\u53d7\u4f24\u4e4b\u540e\uff0c \u7a81\u7136\u8fd8\u80fd\u591f\u9707\u65ad\u8c37\u795e\u7fc1\u7684\u957f\u5251\uff0c\u4e0d\u7981\u5927\u60ca\uff0c\u4ed6\u4e0d\u77e5\u9053\u767e\u5fe7\u4e0a\u4eba\u4f24\u5f97\u6df1\u6d45\u5982\u4f55\uff0c\u751f\u6015\u4ed6\u72c2 \u6012\u53cd\u6251\uff0c\u6025\u5fd9\u518d\u62d4\u51fa\u674e\u9038\u90a3\u628a\u5b9d\u5251\uff0c\u653e\u5728\u638c\u5fc3\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u5c06\u90a3\u67c4\u5251\u5bf9\u7740\u8c37\u795e\u7fc1\u5e73\u5c04\u98de \u51fa\uff0c\u540c\u65f6\u53eb\u9053\uff1a\u201c\u8c37\u8001\u524d\u8f88\uff0c\u8bf7\u4f60\u6362\u5251\uff01\u201d\u8c37\u795e\u7fc1\u63a5\u4e86\u5b9d\u5251\uff0c\u795e\u8272\u9ee0\u7136\uff0c\u4ed6\u4e0e\u7b26\u4e0d\u7591\u8054 \u6210\u7284\u89d2\u4e4b\u52bf\uff0c\u5404\u81ea\u633a\u5251\u5140\u7acb\uff0c\u76ee\u4e0d\u8f6c\u77ac\u7684\u76ef\u7740\u767e\u5fe7\u4e0a\u4eba\uff0c\u767e\u5fe7\u4e0a\u4eba\u6a2a\u6756\u5f53\u80f8\uff0c\u4ea6\u4f3c\u73e0\u65e0 \u53cd\u6251\u4e4b\u610f\uff0c\u6c14\u6c1b\u9759\u5bc2\u5f97\u4ee4\u4eba\u611f\u5230\u7279\u522b\u53ef\u6016\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u767e\u4f18\u4e0a\u4eba\u5389\u58f0\u53eb\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u6211\u5e73\u751f\u65e0\u654c\u5929\u4e0b\uff0c\u4e0d\u5e94\u4e3a\u522b\u4eba\u6240\u6740\uff01 \u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u7a81\u7136\u628a\u7985\u6756\u63b7\u51fa\uff01\u7b26\u4e0d\u7591\u53eb\u9053\uff1a\u201c\u6211\u4eec\u7528\u7684\u662f\u4f18\u4e91\u8001\u5c3c\u6240\u7559\u4e0b\u7684\u5251\u6cd5\uff0c \u4f60\u662f\u8d25\u7ed9\u4f18\u4e91\u8001\u5c3c\uff0c\u4e0d\u662f\u8d25\u7ed9\u6211\u4eec\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u767e\u5fe7\u4e0a\u4eba\u5df2\u662f\u4e00\u638c\u5411\u81ea\u5df1\u7684\u8111\u95e8\u62cd\u4e0b\uff0c \u786c\u751f\u751f\u7684\u9707\u88c2\u4e86\u81ea\u5df1\u5929\u7075\u76d6\uff01\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u201c\u8f70\u201d\u7136\u5de8\u54cd\uff0c\u539f\u6765\u4ed6\u7684\u90a3\u6839\u7985\u6756\uff0c\u63d2 \u5165\u4e86\u5c71\u58c1\uff01\u53ea\u9732\u51fa\u5c11\u5c11\u4e00\u622a\uff0c\u6756\u5c3e\u5140\u81ea\u98a4\u52a8\u4e0d\u4f11\uff01\u7b26\u5408\u4e8c\u4eba\u89c1\u4ed6\u5982\u6b64\u4e0b\u573a\uff0c\u4e5f\u4e0d\u7981\u6697\u6697 \u53f9\u606f\u3002 \u3000\u3000\u590f\u4faf\u575a\u4e0e\u83e9\u63d0\u4e0a\u4eba\u6bd4\u62fc\u5185\u529f\uff0c\u8fd9\u65f6\u4e5f\u5c06\u5230\u4e86\u80dc\u8d1f\u7acb\u51b3\u7684\u65f6\u5019\uff0c\u83e9\u63d0\u4e0a\u4eba\u672c\u6765\u5c31\u5df2\u5904 \u5728\u4e0b\u98ce\uff0c\u542c\u5f97\u767e\u5fe7\u4e0a\u4eba\u4e34\u6b7b\u4e4b\u524d\u90a3\u4e00\u58f0\u5389\u53eb\uff0c\u5fc3\u7075\u5927\u53d7\u9707\u64bc\uff0c\u4f46\u89c9\u5bf9\u65b9\u7684\u5185\u529b\uff0c\u7ef5\u7ef5\u4e0d \u65ad\u7684\u653b\u6765\uff0c\u4e0d\u7981\u5fc3\u5934\u51b0\u51b7\uff0c\u7791\u76ee\u5f85\u6b7b\u3002\u8981\u77e5\u6bd4\u62da\u5185\u529f\uff0c\u6bd4\u7528\u5175\u5668\u640f\u6597\u8fd8\u66f4\u51f6\u9669\u5f97\u591a\uff0c\u7528 \u5175\u5668\u8fd8\u53ef\u4ee5\u8d8b\u907f\uff0c\u6bd4\u62da\u5185\u529f\uff0c\u90a3\u5219\u662f\u5f3a\u5b58\u5f31\u4ea1\uff0c\u7edd\u65e0\u4fa5\u5e78\u4e4b\u7406\u3002 \u3000\u3000\u83e9\u63d0\u4e0a\u4eba\u6b63\u5728\u7791\u76ee\u5f85\u6b7b\uff0c\u5ffd\u89c9\u8eab\u4e0a\u7684\u538b\u529b\u4e00\u8f7b\uff0c\u7741\u773c\u770b\u65f6\uff0c\u4f46\u89c1\u590f\u4faf\u575a\u5df2\u7ecf\u6536\u638c\u8d77 \u7acb\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u518d\u6bd4\u4e86\u5427\uff01\u201d\u83e9\u63d0\u4e0a\u4eba\u8fd9\u624d\u77e5\u9053\u5bf9\u65b9\u662f\u6709\u610f\u9976\u4e86\u81ea\u5df1\u7684\u6027\u547d\uff0c\u5fc3 \u91cc\u597d\u751f\u60ed\u6127\uff0c\u4f4e\u4f4e\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u591a\u8c22\u5c45\u58eb\u3002\u201d\u4fbf\u5373\u8df3\u4e0b\u77f3\u53f0\uff0c\u98d8\u8eab\u81ea\u53bb\u3002 \u3000\u3000\u6fc0\u6218\u4e4b\u540e\uff0c\u65f7\u91ce\u4e00\u7247\u9759\u5bc2\u3002\u6708\u5149\u5df2\u8fc7\u5929\u5fc3\uff0c\u662f\u5c06\u8fd1\u56db\u66f4\u7684\u65f6\u5206\u4e86\u3002 \u3000\u3000\u590f\u4faf\u575a\u64ae\u5507\u957f\u5578\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u4e00\u8f86\u9a6c\u8f66\u4ece\u5c71\u8c37\u91cc\u51fa\u6765\uff0c\u9a7e\u8f66\u7684\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f \u957f\u5b59\u58c1\u7684\u54e5\u54e5\u957f\u5b59\u6cf0\uff0c\u4ed6\u7684\u5ea7\u4f4d\u65c1\u8fb9\u8fd8\u6709\u4e00\u4e2a\u730e\u6237\u6a21\u6837\u7684\u4eba\uff0c\u957f\u5b59\u6cf0\u4e00\u4e0b\u8f66\u4fbf\u9053\uff1a\u201c\u590f \u4faf\u524d\u8f88\uff0c\u8bf7\u4f60\u770b\u4e00\u770b\u8fd9\u4f4d\u5927\u54e5\uff0c\u4ed6\u5413\u574f\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u957f\u5b59\u6cf0\u4e0e\u767d\u5143\u5316\u90a3\u4e00\u665a\u5728\u8349\u539f\u4e0a\u591c\u6218\u7a0b\u8fbe\u82cf\uff0c\u767d\u65e0\u5316\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u8ddf\u7740\u957f\u5b59\u6cf0 \u4e5f\u88ab\u4ed6\u6240\u64d2\uff01\u540e\u6765\u5f97\u590f\u4faf\u575a\u6697\u52a9\uff0c\u5c06\u4ed6\u4eec\u6551\u8d70\u3002\u4ed6\u4eec\u5728\u674e\u9038\u4e4b\u524d\uff0c\u5148\u5230\u4e86\u7a81\u53a5\u7684\u738b\u5ef7\uff0c \u4fbf\u533f\u5c45\u5728\u8fd9\u5929\u683c\u5c14\u5c71\u4e00\u5bb6\u730e\u6237\u7684\u5bb6\u4e2d\uff0c\u5927\u524d\u5929\u624d\u548c\u590f\u4faf\u575a\u4ed6\u4eec\u53d6\u5f97\u8054\u7edc\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u590f\u4faf\u575a\u4e0e\u4ed6\u4eec\u4e8b\u5148\u7ea6\u5b9a\uff0c\u53eb\u957f\u5b59\u6cf0\u96c7\u4e86\u4e00\u8f86\u9a6c\u8f66\uff0c\u4e09\u66f4\u65f6\u5206\u6765\u63a5\u3002\u767d\u5143\u5316\u5219 \u7559\u5728\u5bb6\u4e2d\u7167\u987e\u674e\u9038\u7684\u5b69\u5b50\uff0c\u9a6c\u8f66\u4e0a\u7684\u90a3\u4eba\u4fbf\u662f\u7ed9\u957f\u5b59\u6cf0\u5e26\u8def\u7684\u90a3\u4e2a\u730e\u6237\uff0c\u4ed6\u4eec\u5230\u6765\u7684\u65f6 \u5019\uff0c\u6b63\u503c\u767e\u4f18\u4e0a\u4eba\u4e0e\u7b26\u8c37\u4e8c\u4eba\u6076\u6218\u65b9\u9163\u4e4b\u9645\uff0c\u4ed6\u4eec\u4fbf\u5c06\u9a6c\u8f66\u5728\u957f\u6797\u8302\u8349\u91cc\u9690\u853d\u8d77\u6765\uff0c\u90a3 \u4e2a\u730e\u6237\u5e73\u65e5\u6562\u4e8e\u8ffd\u6355\u864e\u8c79\uff0c\u4f46\u5374\u88ab\u8fd9\u573a\u60ca\u5929\u52a8\u5730\u7684\u6076\u6218\u5413\u574f\u4e86\u3002 \u3000\u3000\u590f\u4faf\u575a\u4e0a\u524d\u4e00\u770b\uff0c\u7b11\u9053\uff1a\u201c\u65e0\u59a8\u3002\u201d\u5f53\u4e0b\u7528\u96ea\u6c34\u8c03\u4e86\u4e00\u4e9b\u836f\u7c89\u7ed9\u4ed6\u670d\u4e0b\uff0c\u8fc7\u4e86\u597d\u4e00 \u4f1a\u513f\uff0c\u90a3\u4eba\u795e\u667a\u65b9\u59cb\u6e05\u9192\uff0c\u5140\u81ea\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u4e0d\u6015\u4eba\uff0c\u597d\u4e0d\u6015\u4eba\uff01\u201d \u3000\u3000\u5728\u8fd9\u65f6\u95f4\uff0c\u7b26\u4e0d\u7591\u548c\u88f4\u53d4\u5ea6\u5df2\u628a\u4e24\u5177\u68fa\u6750\u642c\u4e0a\u9a6c\u8f66\uff0c\u7acb\u5373\u9a71\u8f66\u75be\u8d70\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u957f\u5b59\u6cf0\u4e5f\u50cf\u88f4\u53d4\u5ea6\u4e00\u6837\uff0c\u5fc3\u4e2d\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u53ea\u6015\u4ed6\u7684\u59b9\u5b50\u4e0d\u80fd\u6551\u6d3b\uff0c\u8981\u77e5\u4eba\u6b7b \u590d\u6d3b\uff0c\u7a76\u7adf\u662f\u975e\u5e38\u7a00\u5947\u7684\u4e8b\u60c5\uff0c\u4ed6\u867d\u7136\u6df1\u4fe1\u590f\u4faf\u575a\u7684\u533b\u672f\u901a\u795e\uff0c\u5fc3\u4e2d\u603b\u662f\u96be\u514d\u6050\u60e7\u3002 \u3000\u3000\u5c06\u8fd1\u9ece\u660e\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u56de\u5230\u4e86\u90a3\u5bb6\u730e\u5bb6\uff0c\u767d\u5143\u5316\u548c\u674e\u9038\u7684\u5b69\u5b50\u65e9\u5df2\u5728\u95e8\u524d\u76f8\u5019\uff0c\u767d \u5143\u5316\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u6628\u665a\u4e00\u665a\u4e0d\u80af\u7761\u89c9\uff0c\u8bf4\u662f\u8981\u7b49\u4ed6\u7684\u5988\u5988\u56de\u6765\u3002\u201d\u674e\u5e0c\u654f\u53eb\u9053\uff1a\u201c\u6211\u5988 \u5988\u5462\uff1f\u8fd8\u6709\u6211\u7684\u7239\u7239\u548c\u59d1\u59d1\u5462\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u89c1\u4ed6\uff1f\u201d\u590f\u4faf\u575a\u6015\u4ed6\u89c1\u4e86\u68fa\u6750\u5bb3\u6015\uff0c\u4fbf\u7b11\u9053\uff1a \u201c\u4f60\u5988\u5988\u7239\u7239\u548c\u59d1\u59d1\u6b63\u7761\u5f97\u5f88\u597d\uff0c\u4f60\u4e0d\u8981\u6253\u6405\u4ed6\u4eec\uff0c\u4f60\u5988\u4e00\u5b9a\u5bf9\u4f60\u8bf4\u8fc7\uff0c\u597d\u5b69\u5b50\u665a\u4e0a\u5e94 \u8be5\u7761\u89c9\uff0c\u4e0d\u8981\u5435\u9192\u5927\u4eba\u3002\u4f60\u73b0\u5728\u5feb\u53bb\u7761\u89c9\u5427\uff0c\u7761\u9192\u4e86\u5988\u5c31\u4f1a\u5728\u4f60\u8eab\u8fb9\u4e86\u3002\u201d\u674e\u5e0c\u654f\u9053\uff1a \u201c\u597d\uff0c\u6211\u542c\u516c\u516c\u7684\u8bdd\uff0c\u4ed6\u4eec\u662f\u4e0d\u662f\u53c8\u548c\u5927\u6c57\u7684\u6b66\u58eb\u6253\u67b6\u4e86\uff0c\u6664\uff0c\u4ed6\u4eec\u4e00\u5b9a\u7d2f\u5f97\u5f88\u4e86\uff0c\u4f60 \u4e0d\u5fc5\u7740\u5fd9\u5524\u9192\u4ed6\u4eec\u3002\u201d\u8fd9\u5b69\u5b50\u6ee1\u6000\u559c\u60a6\uff0c\u767d\u5143\u5316\u5c06\u4ed6\u62b1\u56de\u5367\u5ba4\uff0c\u4ed6\u5012\u5728\u5e8a\u4e0a\u4e0d\u4e45\u5c31\u719f\u7761 \u4e86\u3002 \u3000\u3000\u8fd9\u5bb6\u4eba\u5bb6\u65e9\u5df2\u817e\u51fa\u4e00\u5927\u95f4\u7a7a\u623f\uff0c\u623f\u4e2d\u6709\u4e00\u4e2a\u5927\u7095\uff0c\u7095\u5e95\u70e7\u7740\u5a92\u7403\uff0c\u6696\u6d0b\u6d0b\u7684\u4e00\u5ba4\u5982 \u6625\uff0c\u623f\u4e2d\u8fd8\u70e7\u7740\u4ee4\u4eba\u7cbe\u795e\u5b81\u9759\u7684\u6a80\u9999\uff0c\u8fd9\u90fd\u662f\u767d\u5143\u5316\u9884\u5148\u5e03\u7f6e\u597d\u7684\u3002\u539f\u6765\u590f\u4faf\u575a\u7684\u7075\u836f \u867d\u7136\u80fd\u591f\u5728\u5047\u6b7b\u4e4b\u540e\u7684\u4e03\u5929\u4e4b\u5185\u5c06\u4eba\u590d\u6d3b\uff0c\u4f46\u4ed6\u4eec\u201c\u6b7b\u201d\u4e86\u8fd9\u51e0\u5929\uff0c\u751f\u673a\u5df2\u662f\u5b8c\u5168\u505c\u987f\uff0c \u5728\u521d\u9192\u65f6\uff0c\u62b5\u6297\u7684\u80fd\u529b\u8981\u6bd4\u5e38\u4eba\u8fd8\u5f31\u5f97\u591a\uff0c\u6240\u4ee5\u4e0d\u80fd\u5728\u51b0\u5929\u96ea\u5730\u7684\u77ff\u91ce\u4e4b\u4e2d\u5f00\u68fa\u6551\u6cbb\u3002 \u3000\u3000\u590f\u4faf\u575a\u4ece\u8c37\u795e\u7fc1\u624b\u4e2d\u63a5\u8fc7\u674e\u9038\u90a3\u628a\u5b9d\u5251\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u628a\u5b9d\u5251\u6b63\u597d\u5408\u7528\u3002\u201d\u5c06\u5b9d\u5251\u8f7b \u8f7b\u4e00\u5212\uff0c\u68fa\u76d6\u7acb\u523b\u88c2\u5f00\uff0c\u91cc\u9762\u4e1d\u6beb\u4e0d\u53d7\u9707\u52a8\uff0c\u5f53\u7136\u8981\u80dc\u8fc7\u7528\u94c1\u65a7\u5288\u5f00\u4e86\u3002 \u3000\u3000\u6253\u5f00\u4e86\u7b2c\u4e00\u5177\u68fa\u6750\uff0c\u88f4\u53d4\u5ea6\u8212\u4e86\u53e3\u6c14\uff0c\u90a3\u91cc\u9762\u8eba\u7740\u7684\u662f\u6b66\u7384\u971c\u3002\u53ea\u89c1\u5979\u9762\u8272\u5982\u751f\uff0c \u4e1d\u6beb\u672a\u53d8\uff0c\u5f53\u771f\u5c31\u50cf\u5728\u719f\u7761\u4e2d\u4e00\u822c\u3002 \u3000\u3000\u590f\u4faf\u575a\u5c06\u6b66\u7384\u971c\u62b1\u8d77\uff0c\u653e\u5230\u7095\u4e0a\uff0c\u63a5\u7740\u53c8\u53bb\u6253\u5f00\u7b2c\u4e8c\u5177\u68fa\u6750\uff0c\u957f\u5b59\u6cf0\u4e5f\u8212\u4e86\u53e3\u6c14\uff0c \u8fd9\u5177\u68fa\u6750\u91cc\u9762\u6709\u4e24\u4e2a\u4eba\uff0c\u6b63\u662f\u674e\u9038\u548c\u4ed6\u7684\u59b9\u5b50\u3002 \u3000\u3000\u4f46\u89c1\u957f\u5b59\u58c1\u53cc\u624b\u62b1\u7740\u674e\u9038\uff0c\u957f\u5b59\u6cf0\u7ade\u662f\u4e0d\u80fd\u5c06\u4ed6\u5206\u5f00\uff0c\u4f17\u4eba\u65e0\u4e0d\u6696\u53f9\uff0c\u957f\u5b59\u6cf0\u4e0d\u6562 \u7528\u529b\u5f3a\u5206\uff0c\u53ea\u597d\u5c06\u4ed6\u4eec\u4e24\u4e2a\u4eba\u90fd\u62b1\u8d77\u6765\uff0c\u653e\u5230\u7095\u4e0a\u3002 \u3000\u3000\u590f\u4faf\u575a\u4e0a\u524d\u4e00\u770b\uff0c\u53ea\u89c1\u674e\u9038\u8138\u5982\u767d\u7389\uff0c\u989c\u8272\u672a\u53d8\uff0c\u4f46\u957f\u5b59\u58c1\u7684\u7709\u5fc3\u5374\u73b0\u51fa\u51e0\u70b9\u9edb\u8272 \u7684\u6591\u70b9\uff0c\u590f\u4faf\u575a\u9762\u8272\u5fae\u53d8\uff0c\u8f7b\u8f7b\u7684\u201c\u5475\u201d\u4e86\u4e00\u58f0\u3002\u957f\u5b59\u6cf0\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4ed6\u4eec\u80fd\u591f\u6551\u6d3b \u5417\uff1f\u201d\u590f\u4faf\u575a\u9053\uff1a\u201c\u8001\u592b\u7684\u8fd8\u9b42\u4e39\u5728\u4e03\u65e5\u4e4b\u5185\u603b\u80fd\u6551\u6d3b\uff0c\u9664\u975e\u662f\u6709\u610f\u60f3\u4e0d\u5230\u7684\u53d8\u5316\uff0c\u90a3 \u5c31\u53ea\u597d\u542c\u5929\u7531\u547d\u4e86\u3002\u201d\u4f17\u4eba\u672c\u6765\u90fd\u662f\u6df1\u4fe1\u590f\u4faf\u575a\u7684\u533b\u672f\u901a\u795e\uff0c\u542c\u4e86\u4ed6\u8fd9\u8bdd\uff0c\u5fc3\u5934\u5374\u4f3c\u60ac \u4e0a\u4e00\u5757\u94c5\u5757\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7095\u5e95\u7684\u70ed\u6c14\u900f\u4e0a\u6765\uff0c\u4ed6\u4eec\u7684\u624b\u8db3\u6e10\u6e10\u6709\u70b9\u6696\u548c\uff0c\u590f\u4faf\u575a\u5012\u4e86\u4e09\u676f\u836f\u9152\uff0c \u53d6\u51fa\u4e09\u9897\u7ea2\u8272\u7684\u4e39\u4e5d\uff0c\u64ac\u5f00\u4ed6\u4eec\u7684\u7259\u5173\uff0c\u4f9d\u6b21\u5c06\u836f\u9152\u548c\u4e39\u836f\uff0c\u704c\u5165\u4ed6\u4eec\u7684\u53e3\u4e2d\uff0c\u5ba4\u5185\u8bf8 \u4eba\u5747\u662f\u5c4f\u606f\u4ee5\u5f85\uff0c\u8fd9\u4e09\u4e2a\u4eba\u662f\u6b7b\u662f\u751f\uff0c\u5c31\u8981\u63ed\u6653\u4e86\u3002\u88f4\u53d4\u5ea6\u548c\u957f\u5b59\u6cf0\u66f4\u662f\u611f\u5230\u98a4\u6817\u4e0d\u5b89\u3002 \u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u4e00\u652f\u9999\u7684\u65f6\u523b\uff0c\u6b66\u7384\u971c\u8eab\u5b50\u52a8\u4e86\u4e00\u52a8\uff0c\u5589\u5934\u54af\u54af\u4f5c\u54cd\uff0c\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u9996 \u5148\u53eb\u4e86\u51fa\u6765\u3002\u590f\u4faf\u575a\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u6b66\u59d1\u5a18\u9192\u6765\u4e86\u3002\u53d4\u5ea6\uff0c\u4f60\u7ed9\u5979\u63a8\u8840\u8fc7\u5bab\uff0c\u8ba9\u5979 \u65e9\u4e9b\u6062\u590d\u3002\u201d \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u674e\u9038\u4e5f\u50cf\u6b66\u7384\u971c\u4e00\u6837\uff0c\u8eab\u5b50\u4e00\u4fa7\uff0c\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\uff0c\u674e\u9038\u7684\u5173 \u8282\uff0c\u5df2\u5f00\u59cb\u80fd\u591f\u6d3b\u52a8\uff0c\u590f\u4faf\u575a\u65bd\u5c55\u5de7\u5999\u7684\u624b\u6cd5\uff0c\u5c06\u4ed6\u7684\u624b\u8f7b\u8f7b\u4e00\u62c9\uff0c\u5c06\u4ed6\u548c\u957f\u5b59\u58c1\u5206\u4e86 \u5f00\u6765\u3002\u8c37\u795e\u7fc1\u4e0a\u524d\u7ed9\u674e\u9038\u63a8\u8840\u8fc7\u5bab\uff0c\u957f\u5b59\u6cf0\u4e0a\u524d\u5bdf\u770b\u59b9\u59b9\uff0c\u957f\u5b59\u58c1\u4ecd\u7136\u662f\u50f5\u786c\u5982\u6b7b\uff0c\u52a8 \u4e5f\u4e0d\u52a8\uff0c\u8fd9\u65f6\u8fde\u590f\u4faf\u575a\u4e5f\u6709\u70b9\u614c\u4e86\u3002 \u3000\u3000\u6b66\u7384\u971c\u5f20\u5f00\u773c\u775b\uff0c\u7b2c\u4e00\u53e5\u8bdd\u5c31\u95ee\uff1a\u201c\u58c1\u59b9\u5462\uff1f\u201d\u590f\u4faf\u575a\u4f38\u624b\u53bb\u6478\uff0c\u89e6\u7740\u674e\u9038\u7684\u624b\uff0c \u674e\u9038\u521a\u60ed\u590d\u77e5\u89c9\uff0c\u50cf\u662f\u5728\u4e00\u573a\u6076\u68a6\u4e4b\u4e2d\u9192\u6765\uff0c\u5f20\u5f00\u773c\u775b\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u7384\u971c\uff0c\u662f\u4f60\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u51c4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u590f\u4faf\u524d\u8f88\uff0c\u54b1\u4eec\u53c8\u9003\u8fc7\u4e00\u6b21\u96be\u5173\u4e86\u3002\u5509\uff0c\u58c1\u59b9\uff0c\u4f60 \u600e\u4e48\u4e0d\u548c\u6211\u8bf4\u8bdd\u5440\uff1f\u201d\u5979\u5750\u4e86\u8d77\u6765\uff0c\u8fd9\u65f6\u624d\u770b\u6e05\u695a\u4e86\uff0c\u957f\u5b59\u58c1\u8fd8\u662f\u53cc\u76ee\u7d27\u95ed\uff0c\u50f5\u5367\u7095\u4e0a\u3002 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u539f\u6765\u5979\u4e5f\u670d\u4e0b\u4e86\u90a3\u5305\u836f\u6563\uff0c\u54b1\u4eec\u65e2\u7136\u9192\u4e86\uff0c\u5979\u5f53\u7136\u4e5f\u4f1a\u9192\u7684\u3002\u7384\u971c\u59d0\u59d0\uff0c \u4f60\u653e\u5fc3\u3002\u201d\u4ed6\u529d\u6b66\u7384\u971c\u653e\u5fc3\uff0c\u4f46\u4ed6\u6478\u4e00\u6478\u957f\u5b59\u58c1\u7684\u624b\u8db3\uff0c\u53ea\u89c9\u4e00\u7247\u51b0\u51b7\uff0c\u4ed6\u81ea\u5df1\u5374\u9996\u5148 \u614c\u4e86\u3002 \u3000\u3000\u590f\u4faf\u575a\u5c06\u674e\u9038\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6084\u6084\u95ee\u9053\u3002\u201c\u4f60\u59bb\u5b50\u662f\u4e0d\u662f\u6000\u6709\u8eab\u5b55\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u662f\u6709 \u4e09\u4e2a\u6708\u7684\u8eab\u5b55\u4e86\u3002\u6211\u4e5f\u8fd8\u662f\u90a3\u5929\u624d\u77e5\u9053\u7684\uff0c\u90a3\u5929\u5927\u6c57\u8ba9\u6211\u4eec\u592b\u59bb\u76f8\u4f1a\uff0c\u58c1\u59b9\u544a\u8bc9\u6211\u5979\u6000 \u5b55\u7684\u4e8b\u60c5\u3002\u4e0d\u4e45\uff0c\u7384\u971c\u5c31\u6765\u4e86\u3002\u590f\u4faf\u524d\u8f88\uff0c\u5979\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u672a\u9192\u6765\uff1f\u662f\u4e0d\u662f\u56e0\u4e3a\u6000\u6709\u8eab\u5b55\uff0c \u8981\u8fdf\u4e00\u4e9b\u65f6\u5019\uff1f\u201d\u4f46\u89c1\u590f\u4faf\u575a\u9762\u8272\u7070\u767d\uff0c\u674e\u9038\u5fc3\u77e5\u4e0d\u5999\uff0c\u767b\u65f6\u5446\u4e86\uff01 \u3000\u3000\u539f\u6765\u590f\u4faf\u575a\u8fd9\u8d77\u6b7b\u56de\u751f\u7684\u7075\u836f\uff0c\u7537\u5973\u8001\u5e7c\uff0c\u5747\u6709\u7075\u6548\uff0c\u5c31\u53ea\u662f\u5b55\u5987\u5fcc\u670d\uff0c\u90a3\u65e5\u7384\u971c \u548c\u4ed6\u8c08\u8d77\u8fd9\u79cd\u7075\u836f\u7684\u5947\u6548\uff0c\u4ed6\u60f3\u4e0d\u5230\u5979\u4f1a\u76d7\u53bb\u6551\u674e\u9038\u592b\u5987\u7684\uff0c\u5f53\u65f6\u6ca1\u6709\u5c06\u8fd9\u4e00\u5c42\u907f\u5fcc\u544a \u8bc9\u5979\u3002 \u3000\u3000\u674e\u9038\u5446\u5446\u7684\u671b\u7740\u590f\u4faf\u58c1\uff0c\u50cf\u4e00\u4e2a\u6b7b\u56da\u7b49\u5f85\u7740\u5224\u51b3\uff0c\u5c4b\u5185\u7684\u7a7a\u6c14\u4e5f\u597d\u50cf\u8981\u51dd\u7ed3\u8d77\u6765\uff0c \u957f\u5b59\u6cf0\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u6211\u59b9\u5b50\u80fd\u4e0d\u80fd\u6551\u6d3b\uff0c\u590f\u4faf\u4f2f\u4f2f\uff0c\u8bf7\u4f60\u5b9e\u8bf4\uff01\u201d\u590f\u4faf\u575a\u867d\u7136\u6781\u4e0d\u613f\u610f \u8bf4\u51fa\uff0c\u4f46\u771f\u76f8\u603b\u662f\u96be\u4ee5\u4e45\u7792\uff0c\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u6000\u6709\u4e09\u4e2a\u6708\u7684\u8eab\u5b55\uff0c\u751f\u673a\u4e00 \u505c\uff0c\u4fbf\u96be\u590d\u82cf\uff0c\u8001\u592b\u4e5f\u65e0\u80fd\u4e3a\u529b\u4e86\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5c4b\u4e2d\u9759\u5bc2\u5982\u6b7b\uff0c\u5ffd\u542c\u5f97\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u6b66\u7384\u971c\u9996\u5148\u54ed\u4e86\u51fa\u6765\uff0c\u5979\u8d39\u5c3d\u5fc3 \u529b\u53bb\u6551\u957f\u5b59\u58c1\uff0c\u60f3\u4e0d\u5230\u957f\u5b59\u58c1\u53cd\u800c\u56e0\u6b64\u6b7b\u4e86\uff01\u5509\uff0c\u957f\u5b59\u58c1\u6b7b\u4e86\uff0c\u5979\u771f\u7684\u6b7b\u4e86\uff1f\u957f\u5b59\u58c1\u597d \u50cf\u6b63\u505a\u7740\u4e00\u4e2a\u7f8e\u68a6\uff0c\u7761\u5f97\u90a3\u6837\u5b81\u9759\u5b89\u8be6\uff0c\u5979\u662f\u6b7b\u5728\u5979\u4e08\u592b\u7684\u6000\u4e2d\u7684\uff0c\u5979\u662f\u6000\u7740\u5e78\u798f\u7684\u611f \u89c9\u957f\u7720\u7684\u3002\u53ef\u662f\u6b66\u7384\u971c\u5374\u8fd8\u4f3c\u5bf9\u7740\u5979\u90a3\u5e7d\u6028\u7684\u76ee\u5149\uff01\u6b66\u7384\u971c\u611f\u5230\u6709\u751f\u4ee5\u6765\u6700\u5267\u70c8\u7684\u5fc3\u7075 \u9707\u6296\uff01\u5979\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u90fd\u662f\u6211\uff0c\u6211\u5bb3\u4e86\u5979\uff01\u201d\u590f\u4faf\u575a\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u8fd9\u4e8b\u4e0d\u80fd\u602a\u8c01\uff0c\u8981\u602a\u53ea\u80fd\u602a\u7a81\u53a5\u5927\u6c57\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u6ee1\u8138\u6cea\u6c34\uff0c\u58f0\u97f3\u5636\u54d1\uff0c\u62b1\u7740\u674e\u9038\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u54ed\u51fa\u6765\u5440\uff01\u201d\u4f46\u89c1\u674e\u9038 \u7684\u773c\u73e0\u597d\u4f3c\u5b9a\u7740\u4e00\u822c\uff0c\u6b66\u7384\u971c\u7684\u54ed\u6ce3\uff0c\u957f\u5b59\u6cf0\u7684\u98a4\u53eb\uff0c\u4ed6\u90fd\u597d\u50cf\u662f\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb \u4e86\uff01\u4ed6\u773c\u4e2d\u53ea\u6709\u4e00\u4e2a\u957f\u5b59\u58c1\uff0c\u957f\u5b59\u58c1\u9759\u9759\u7684\u8eba\u7740\uff0c\u5c31\u597d\u50cf\u5e73\u5e38\u90a3\u6837\uff0c\u7761\u5728\u4ed6\u7684\u8eab\u8fb9\u3002\u516b \u5e74\u6765\u6069\u7231\u5239\u90a3\u95f4\u90fd\u5728\u5fc3\u5934\u6cdb\u8d77\uff0c\u5440\uff0c\u957f\u5b59\u58c1\u5728\u516b\u5e74\u957f\u7684\u65e5\u5b50\u91cc\uff0c\u70ed\u7231\u7740\u4ed6\uff0c\u800c\u53c8\u6000\u7740\u6050 \u60e7\uff0c\u6050\u60e7\u4f1a\u5931\u53bb\u4ed6\u3002\u5979\u8fd9\u590d\u6742\u7684\u5fc3\u60c5\uff0c\u53ea\u6709\u4ed6\u4e00\u4eba\u77e5\u9053\u3002\u5509\uff0c\u6ca1\u60f3\u5230\u53cd\u800c\u662f\u4ed6\u5931\u53bb\u4e86\u5979\u3002 \u3000\u3000\u674e\u9038\u611f\u5230\u4e86\u523b\u9aa8\u7684\u4f24\u5fc3\uff0c\u6781\u7aef\u7684\u96be\u8fc7\uff0c\u4e0d\u53ea\u662f\u56e0\u4e3a\u5931\u53bb\u4e86\u59bb\u5b50\uff0c\u800c\u4e14\u662f\u56e0\u4e3a\u611f\u5230\u5185 \u759a\uff0c\u611f\u5230\u81ea\u5df1\u5728\u5979\u7684\u751f\u524d\u6ca1\u6709\u4ee4\u5979\u5f97\u5230\u5e78\u798f\u3002\u4ed6\u548c\u957f\u5b59\u58c1\u7684\u6210\u5a5a\u672c\u6765\u751a\u4e3a\u52c9\u5f3a\uff0c\u4f46\u662f\u5728 \u8fd9\u4e2a\u65f6\u5019\uff0c\u4ed6\u7b2c\u4e00\u6b21\u611f\u5230\u81ea\u5df1\u771f\u6b63\u7231\u5979\u4e86\uff01\u4f46\u662f\uff0c\u8fd9\u5df2\u7ecf\u8fdf\u4e86\uff0c\u5979\u5df2\u7ecf\u4e00\u7791\u4e0d\u89c6\u4e86\uff01 \u3000\u3000\u674e\u9038\u7f13\u7f13\u8dea\u5728\u7095\u8fb9\uff0c\u53cc\u624b\u6309\u5728\u5979\u7684\u8eab\u4e0a\uff0c\u558a\u4e86\u4e00\u58f0\u201c\u58c1\u59b9\uff01\u201d\u5ffd\u5730\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c \u5012\u4e86\u4e0b\u53bb\uff0c\u53cc\u624b\u4ecd\u7136\u7d27\u62c9\u7740\u957f\u5b59\u58c1\uff0c\u4ed6\u521a\u521a\u590d\u6d3b\uff0c\u7981\u53d7\u4e0d\u8d77\u8fd9\u6837\u75db\u82e6\u7684\u714e\u71ac\uff0c\u53c8\u662f\u5012\u4e86\u3002 \u4f17\u4eba\u8d76\u5fd9\u56f4\u7740\u4ed6\u65bd\u6551\uff0c\u6b66\u7384\u971c\u5374\u6084\u6084\u7684\u8d70\u51fa\u53bb\u4e86\uff01 \u3000\u3000\u96ea\u5730\u4e0a\u51b7\u51b7\u6e05\u6e05\uff0c\u6b66\u7384\u971c\u5b64\u8eab\u5355\u5f71\uff0c\u5979\u611f\u5230\u4ece\u6240\u672a\u6709\u7684\u5bc2\u5bde\u4e0e\u51c4\u51c9\uff0c\u6e10\u6e10\u5979\u7684\u5fc3\u7075 \u4e5f\u597d\u50cf\u51bb\u5f97\u9ebb\u6728\u4e86\uff0c\u8111\u5b50\u91cc\u7a7a\u7a7a\u6d1e\u6d1e\u7684\u4f3c\u662f\u5931\u53bb\u4e86\u601d\u60f3\uff0c\u5979\u8981\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u5462\uff1f\u8fde\u81ea\u5df1 \u4e5f\u4e0d\u77e5\u9053\uff0c\u5979\u53ea\u662f\u6709\u4e00\u4e2a\u5ff5\u5934\uff0c\u4e0d\u60f3\u548c\u674e\u9038\u518d\u89c1\u9762\u4e86\u3002 \u3000\u3000\u5ffd\u7136\u96ea\u5730\u4e0a\u53c8\u73b0\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u8e0f\u7740\u5979\u8d70\u8fc7\u7684\u8db3\u8ff9\uff0c\u9760\u8fd1\u5979\u7684\u8eab\u65c1\uff0c\u4ed6\u662f\u88f4\u53d4\u5ea6\u3002\u53ef \u662f\u6b66\u7384\u971c\u597d\u50cf\u5e76\u6ca1\u6709\u5bdf\u89c9\u5979\u7684\u5e08\u5144\uff0c\u88f4\u53d4\u5ea6\u4e5f\u6ca1\u6709\u5f00\u53e3\u53eb\u5979\uff0c\u53ea\u662f\u8ddf\u7740\u5979\u9ed8\u9ed8\u7684\u8d70\u3002\u5509\uff0c \u4ed6\u77e5\u9053\u5e08\u59b9\u6b64\u9645\u7684\u5fc3\u60c5\uff0c\u800c\u4ed6\u7684\u60b2\u4f24\u4e5f\u5b9e\u4e0d\u5728\u4ed6\u5e08\u59b9\u4e4b\u4e0b\u3002\u81ea\u4ece\u6b66\u7384\u971c\u5230\u8fc7\u5929\u5c71\u4e4b\u540e\uff0c \u4ed6\u6e10\u6e10\u53d1\u89c9\u4e86\u5e08\u59b9\u5bf9\u674e\u9038\u7684\u611f\u60c5\uff0c\u4ed6\u662f\u591a\u4e48\u5bb3\u6015\u4ed6\u5e08\u59b9\u91cd\u8e48\u4ed6\u59d1\u59d1\u7684\u8986\u8f99\u554a\uff01\u800c\u4e14\u9664\u4e86 \u8fd9\u4e2a\u5bb3\u6015\u4e4b\u5916\uff0c\u4ed6\u4e5f\u6e10\u6e10\u53d1\u89c9\u4e86\u5728\u81ea\u5df1\u7684\u5fc3\u5e95\u4e5f\u9690\u53d9\u7740\u4e00\u4efd\u5bf9\u5e08\u59b9\u7684\u611f\u60c5\u3002 \u3000\u3000\u4e24\u4eba\u9ed8\u9ed8\u7684\u8d70\u4e86\u597d\u4e9b\u65f6\u5019\uff0c\u5929\u53c8\u4e0b\u96ea\u4e86\uff0c\u9e45\u6bdb\u822c\u7684\u96ea\u7247\u6492\u5728\u4ed6\u4eec\u7684\u8eab\u4e0a\uff0c\u6b66\u7384\u971c\u505c \u4e86\u4e0b\u6765\uff0c\u4f4e\u4f4e\u7684\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u597d\u51b7\uff01\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u56de\u53bb\u5427\uff01\u201d\u6b66\u7384\u971c\u6447\u4e86\u6447\u5934\uff0c \u53f9\u4e86\u53e3\u6c14\uff0c\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u4e0d\u8981\u96be\u8fc7\uff0c\u8fd9\u4e0d\u662f\u4f60\u7684\u8fc7\u9519\u3002\u201d\u6b66\u7384\u971c\u9ed8\u9ed8\u65e0\u8a00\u7684\u53c8 \u8d70\u4e86\u51e0\u6b65\uff0c\u96ea\u4e0b\u5f97\u66f4\u5927\u4e86\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u9f13\u8d77\u52c7\u6c14\uff0c\u8bd5\u63a2\u95ee\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4e0d\u5982\u54b1\u4eec\u4e00\u540c\u56de\u5230\u5929\u5c71\u53bb\u5427\u3002\u8fd9\u91cc\u7684\u6d88\u606f\uff0c \u4f60\u53ef\u4ee5\u6258\u957f\u5b59\u6cf0\u5e26\u56de\u53bb\u7ed9\u5929\u540e\u3002\u5e08\u7236\u5bf9\u4f60\u7684\u671f\u671b\u5f88\u5927\uff0c\u5e0c\u671b\u4f60\u6210\u4e3a\u5979\u7684\u4f20\u4eba\u3002\u5728\u5929\u5c71\u54b1 \u4eec\u53ef\u4ee5\u5207\u78cb\u5251\u6cd5\uff0c\u4f60\u4e5f\u53ef\u4ee5\u65f6\u65f6\u770b\u5230\u674e\u9038\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u542c\u5230\u201c\u674e\u9038\u201d\u7684\u540d\u5b57\uff0c\u8eab\u8eaf\u7a81\u7136\u98a4\u6296\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u5e08\u5144\uff0c\u6211\u4e0d\u613f\u610f\u518d \u89c1\u4ed6\u4e86\u3002\u6211\uff0c\u6211\u51b3\u5b9a\u56de\u8f6c\u957f\u5b89\uff01\u201d\u88f4\u53d4\u5ea6\u6014\u4e86\u4e00\u6014\uff0c\u5411\u9053\uff1a\u201c\u73b0\u5728\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u6211\u4e0d\u60f3\u7b49\u81f3\u660e\u671d\u4e86\u3002\u4f60\u7ed9\u6211\u5411\u51e0\u4f4d\u8001\u524d\u8f88\u544a\u7f6a\u5427\uff01\u201d\u7a81\u7136\u52a0\u5feb\u811a\u6b65\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u76f4 \u5411\u524d\u884c\u3002 \u3000\u3000\u88f4\u53d4\u5ea6\u5446\u6ede\u7684\u770b\u7740\u5979\u7684\u80cc\u5f71\uff0c\u5728\u96ea\u5730\u4e0a\u5189\u5189\u800c\u6ca1\uff0c\u4ed6\u6ca1\u6709\u8ffd\u5979\uff0c\u4ed6\u77e5\u9053\u8ffd\u4e5f\u662f\u8ffd\u4e0d \u56de\u6765\u4e86\u3002\u4ed6\u66f4\u77e5\u9053\uff0c\u5e08\u59b9\u5bf9\u674e\u9038\u5b9e\u662f\u6709\u96be\u4ee5\u5fd8\u6000\u7684\u611f\u60c5\uff0c\u5979\u8fd9\u6837\u5306\u5306\u7684\u8d70\uff0c\u6b63\u662f\u7531\u4e8e\u5979 \u5bf9\u81ea\u5df1\u8fd9\u4efd\u611f\u60c5\u5730\u5bb3\u6015\u3002\u8fd9\u4e00\u77ac\u95f4\u88f4\u53d4\u5ea6\u611f\u5230\u51b7\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u4ed6\u5728\u98ce\u96ea\u4e2d\u6084\u7136\u51dd\u671b\uff0c\u5728 \u8352\u91ce\u4e2d\u72ec\u81ea\u7ad9\u4e86\u8bb8\u4e45\u8bb8\u4e45\u3002 \u3000\u3000\u5230\u4ed6\u56de\u8f6c\u90a3\u730e\u6237\u7684\u5bb6\u4e2d\uff0c\u5df2\u5dee\u4e0d\u591a\u662f\u4e2d\u5348\u7684\u65f6\u5206\uff0c\u674e\u9038\u65e9\u5df2\u9192\u6765\uff0c\u4e00\u770b\u4ed6\u7684\u795e\u60c5\uff0c \u5c31\u77e5\u9053\u6b66\u7384\u971c\u5df2\u7ecf\u8d70\u4e86\uff0c\u4ed6\u7684\u5fc3\u7075\u4e5f\u597d\u50cf\u9ebb\u6728\u4e86\uff0c\u88f4\u53d4\u5ea6\u6ca1\u6709\u8bf4\uff0c\u4ed6\u4e5f\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff0c\u201c\u654f\u513f\u521a\u624d\u5728\u68a6\u4e2d\u8fd8\u53eb\u7740\u4ed6\u7684\u5988\u5462\uff01\u201d\u674e\u9038\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\u54c4\u4ed6\uff0c \u8bf4\u662f\u4ed6\u5988\u5988\u548c\u4ed6\u7684\u59d1\u59d1\u4e00\u540c\u8d70\u4e86\u3002\u4f60\u5c06\u58c1\u59b9\u5df2\u63a9\u57cb\u4e86\u5427\uff01\u201d\u957f\u5b59\u6cf0\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u5b69\u5b50 \u771f\u53ef\u601c\u3002\u8fd9\u6837\u4e5f\u597d\uff0c\u8fc7\u4e00\u5e74\u518d\u544a\u8bc9\u4ed6\u3002\u4e5f\u597d\u5728\u6709\u73b0\u6210\u7684\u68fa\u6750\uff01\u201d\u957f\u5b59\u6cf0\u62b1\u8d77\u59b9\u59b9\u7684\u5c38\u4f53 \u653e\u5165\u68fa\u4e2d\uff0c\u60f3\u8d77\u81ea\u5df1\u8fdc\u9053\u800c\u6765\uff0c\u89c1\u7740\u4e86\u59b9\u59b9\u7684\u9762\uff0c\u5374\u4e0d\u80fd\u548c\u5979\u8bf4\u4e00\u53e5\u8bdd\uff0c\u7981\u4e0d\u4f4f\u53c8\u6d12\u4e0b \u6cea\u73e0\u3002\u4ed6\u6015\u60ca\u9192\u7525\u513f\uff0c\u5f3a\u81ea\u6291\u5236\uff0c\u4e0d\u5fcd\u54ed\u51fa\u58f0\u6765\u3002 \u3000\u3000\u4e09\u65e5\u540e\uff0c\u5c71\u8c37\u91cc\u8d77\u4e86\u4e00\u5ea7\u65b0\u575f\uff0c\u8fd9\u5ea7\u65b0\u575f\u5f53\u7136\u6ca1\u6709\u7a81\u53a5\u5927\u6c57\u6240\u5efa\u7684\u90a3\u5ea7\u5b8f\u4e3d\uff0c\u4f46\u5374 \u662f\u674e\u9038\u4eb2\u81ea\u4e3a\u4ed6\u7684\u59bb\u5b50\u8425\u9020\u7684,\u5893\u7891\u4e0a\u6709\u4ed6\u624b\u523b\u7684\u201c\u7231\u59bb\u957f\u5b59\u58c1\u4e4b\u5893\u201d\u51e0\u4e2a\u5927\u5b57\u3002\u957f\u5b59\u58c1 \u6cc9\u4e0b\u6709\u77e5\uff0c\u4e5f\u5e94\u5f53\u7791\u76ee\u4e86\u5427\uff1f \u3000\u3000\u674e\u9038\u7684\u8eab\u4f53\u5df2\u7ecf\u590d\u539f\uff0c\u4ed6\u5fc3\u7075\u4e0a\u7684\u521b\u4f24\u5374\u662f\u6c38\u8fdc\u4e0d\u80fd\u590d\u539f\u4e86\uff0c\u957f\u5b59\u6cf0\u4f34\u4e86\u674e\u9038\u4e09\u5929\uff0c \u5e2e\u4ed6\u6599\u7406\u4e86\u59b9\u59b9\u7684\u540e\u4e8b\uff0c\u4ed6\u6df1\u6df1\u611f\u5230\u674e\u9038\u5fc3\u4e2d\u7684\u54c0\u75db\uff0c\u4ed6\u672c\u6765\u8fd8\u60f3\u591a\u4f34\u674e\u9038\u51e0\u5929\u7684\uff0c\u4f46 \u4e3a\u4e86\u8981\u56de\u957f\u5b89\u8986\u547d\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u4e0d\u8d70\u4e86\uff0c\u4e24\u90ce\u8205\u5c31\u5728\u957f\u5b59\u58c1\u7684\u575f\u524d\u8bdd\u522b\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u6211\u8d70\u4e86\u4ee5\u540e\uff0c\u8fd8\u671b\u4f60\u5584\u81ea\u4fdd\u91cd\uff0c\u7a0d\u8282\u54c0\u601d\u3002\u201d\u674e\u9038\u9ed8 \u7136\u65e0\u8bed\uff0c\u957f\u5b59\u6cf0\u53c8\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u867d\u7136\u6ca1\u6709\u5f97\u548c\u58c1\u59b9\u76f8\u53d9\uff0c\u4f46\u4ece\u654f\u513f\u7684\u53e3\u4e2d\uff0c\u6211\u77e5\u9053\u58c1\u59b9 \u5f88\u6000\u5ff5\u6545\u56fd\u3002\u5979\u5e38\u7ed9\u654f\u513f\u8bb2\u4e2d\u56fd\u7684\u4e8b\u60c5\uff0c\u7b54\u5e94\u8fc7\u4ed6\u5c06\u6765\u8981\u5e26\u4ed6\u5230\u957f\u5b89\u53bb\u73a9\u3002\u201d\u674e\u9038\u9053\uff1a \u201c\u6211\u77e5\u9053\uff0c\u654f\u513f\u5c0f\u65f6\u5019\u4e00\u54ed\uff0c\u5979\u5c31\u5e38\u5e38\u8fd9\u6837\u54c4\u4ed6\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u613f\u654f\u513f\u957f\u4f5c\u57df \u5916\u4e4b\u6c11\u5427\uff1f\u201d\u674e\u9038\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u662f\u4e0d\u613f\u56de\u53bb\u7684\u4e86\uff0c\u5509\uff0c\u8fd9\u516b\u5e74\u6765\u5979\u4f34\u6211\u4f4f\u5728\u8352\u5c71\uff0c\u53d7 \u4e86\u8bb8\u591a\u82e6\uff0c\u6211\u4e00\u60f3\u8d77\u6765\u5c31\u89c9\u5f97\u5bf9\u5979\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u95ee\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5bf9\u4e8e\u5929\u540e\u7684\u770b\u6cd5\u600e\u6837\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u6709\u9b44\u529b\u7684\u5973\u4eba\u3002 \u4f46\u662f\u5979\u7528\u4e86\u8bb8\u591a\u6211\u4f69\u670d\u7684\u4eba\uff0c\u4e5f\u6740\u4e86\u8bb8\u591a\u6211\u4f69\u670d\u7684\u4eba\u3002\u6211\u4e5f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u8bf4\u624d\u597d\uff0c\u5979\u7684 \u5343\u79cb\u529f\u7f6a\uff0c\u8fd8\u662f\u7559\u5f85\u540e\u4e16\u7684\u53f2\u5bb6\u53bb\u8bc4\u8bba\u5427\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211\u662f\u4f69\u670d\u5979\u7684\uff0c\u5979\u786e\u5b9e\u628a\u56fd \u5bb6\u6cbb\u7406\u5f97\u4e0d\u9519\uff0c\u6700\u5c11\u662f\u6bd4\u4ee5\u524d\u7684\u7687\u5e1d\u8981\u597d\u5f97\u591a\u3002\u4f46\u5979\u4e5f\u4e0d\u662f\u6ca1\u6709\u7f3a\u70b9\uff0c\u5979\u6240\u91cd\u7528\u7684\u4e24\u4e2a \u4f84\u513f\u2014\u2014\u6b66\u627f\u55e3\u548c\u6b66\u4e09\u601d\u5c31\u4e0d\u662f\u597d\u4e1c\u897f\u3002\u5509\uff0c\u4f60\u4e0d\u60f3\u56de\u53bb\uff0c\u6211\u4e0d\u80fd\u52c9\u5f3a\u4f60\uff0c\u4f46\u662f\u8fd8\u6709\u51e0 \u4e2a\u4f60\u6240\u4f69\u670d\u7684\u4eba\u5e0c\u671b\u4f60\u56de\u53bb\u7684\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u8c01\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u9664\u4e86\u4e0a\u5b98\u5a49\u513f\u8fd8\u6709\u8c01\u671b\u6211 \u56de\u53bb\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5f20\u67ec\u4e4b\u505a\u5bb0\u76f8\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u662f\u72c4\u4ec1\u6770\u4fdd\u8350\u7684\u3002\u201d \u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e78\u800c\u6709\u4ed6\u548c\u72c4\u4ec1\u6770\u3001\u6052\u5f66\u8303\u7b49\u4e00\u6d3e\u6b63\u76f4\u7684\u5927\u81e3\uff0c\u4e8c\u6b66\u8fd8\u4e0d\u6562\u516c\u7136\u4f5c \u6076\uff0c\u4f46\u7a76\u7adf\u662f\u671d\u5ef7\u7684\u9690\u60a3\u3002\u5c31\u662f\u72c4\u4ec1\u6770\u548c\u5f20\u67ec\u4e4b\u4ed6\u4eec\u5e0c\u671b\u4f60\u56de\u53bb\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u662f\u5e0c\u671b \u6211\u52a9\u4ed6\u4eec\u4e00\u81c2\u4e4b\u529b\uff0c\u706d\u9664\u4e8c\u6b66\u4e48\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6b63\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u73b0\u5728\u5929\u540e\u4f20\u4f4d\u5979\u7684\u513f \u5b50\u5362\u9675\u738b\u5df2\u6210\u5b9a\u5c40\uff0c\u53ea\u6015\u5c06\u6765\u96be\u514d\u4e00\u573a\u5175\u53d8\u3002\u82e5\u662f\u4e8c\u6b66\u5f97\u52bf\uff0c\u4f60\u4eec\u674e\u5bb6\u7684\u5b50\u5b59\u66f4\u65e0\u55df\u7c7b\uff0c \u76f8\u53cd\uff0c\u82e5\u662f\u5362\u9675\u738b\u5373\u4f4d\uff0c\u4ed6\u7684\u624b\u4e0b\u62a5\u590d\u8d77\u6765\uff0c\u6b66\u5bb6\u7684\u4eba\u6050\u6015\u4e5f\u8981\u7389\u77f3\u4ff1\u711a\u3002\u5728\u8fd9\u6837\u5371\u673a \u91cd\u91cd\u4e4b\u4e0b\uff0c\u591a\u51e0\u4e2a\u6709\u89c1\u8bc6\u7684\u4eba\u4e3b\u6301\u5927\u5c40\uff0c\u603b\u8981\u597d\u4e9b\u3002\u4f60\u96be\u9053\u5fcd\u5fc3\u7f6e\u8eab\u4e8b\u5916\uff0c\u4e0d\u7406\u4f60\u7684\u5144 \u5f1f\u4eb2\u4eba\uff0c\u4e0d\u7406\u7384\u971c\uff0c\u4e5f\u4e0d\u7ba1\u4f60\u7684\u6545\u56fd\u906d\u53d7\u52ab\u96be\u5417\uff1f\u201d\u674e\u9038\u542c\u4e86\u4ed6\u8fd9\u4e00\u756a\u8bdd\uff0c\u4e0d\u89c9\u5fc3\u4e71\u5982 \u9ebb\u3002\u8fc7\u4e86\u8bb8\u4e45\uff0c\u4f46\u542c\u5f97\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u5374\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u8fd8\u4e61\u6e38\u5b50\u4f24\u707e\u52ab \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u8fd8\u4e61\u6e38\u5b50\u4f24\u707e\u52ab \u3000\u3000\u957f\u5b59\u6cf0\u53c8\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u5f88\u6b22\u559c\u5a49\u513f\uff0c\u4e3a\u4e86\u5a49\u513f\u7684\u539f\u6545\uff0c\u6211\u4e5f\u76fc\u4f60\u56de\u53bb\u4e00\u8d9f\u3002\u201d\u674e \u9038\u5583\u5583\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u5a49\u513f\uff0c\u5a49\u513f\u2026\u2026\u201d\u8fd9\u4e2a\u4ed6\u5c0f\u65f6\u5019\u6700\u4eb2\u5bc6\u6295\u5408\u7684\u670b\u53cb\uff0c\u6b64\u523b\u5728\u4ed6\u7684\u5fc3 \u4e0a\u4e5f\u6e10\u6e10\u6de1\u4e86\uff0c\u4f46\u957f\u5b59\u6cf0\u518d\u4e00\u6b21\u63d0\u8d77\u4e86\u5979\uff0c\u674e\u9038\u4ecd\u662f\u7981\u4e0d\u4f4f\u5fae\u5fae\u98a4\u6296\u3002\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211 \u4e0a\u4e00\u6b21\u5df2\u7ecf\u548c\u4f60\u8bf4\u8fc7\uff0c\u5979\u8fd9\u4e00\u5e74\u8d8a\u6765\u8d8a\u6194\u60b4\u4e86\uff0c\u5979\u4f3c\u6709\u4e00\u4ef6\u5f88\u91cd\u5927\u7684\u4e8b\u60c5\u8981\u7b49\u5f85\u4f60\u4e3a\u5979 \u51b3\u5b9a\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u7384\u971c\u4e5f\u8fd9\u6837\u8bf4\u8fc7\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5a49\u513f\u7b49\u4e8e\u6211\u7684\u59b9\u59b9\uff0c\u6211\u77e5\u9053\u4f60\u4e5f \u5f88\u7231\u62a4\u5979\uff0c\u6211\u4e0d\u5fcd\u89c1\u5979\u90c1\u90c1\u800c\u7ec8\uff0c\u5979\u5fc3\u4e2d\u6709\u4e86\u7591\u96be\u7684\u4e8b\u60c5\uff0c\u8981\u7b49\u5f85\u4f60\u4e3a\u5979\u89e3\u51b3\uff0c\u96be\u9053\u4f60 \u7adf\u8fd9\u6837\u5fcd\u5fc3\uff0c\u4e0d\u80af\u89c1\u5979\u4e00\u9762\u3002\u201d \u3000\u3000\u674e\u9038\u957f\u53f9\u4e00\u58f0\uff0c\u4ecd\u7136\u4e0d\u8bed\u3002\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u6267\u610f\u4e0d\u80af\u56de\u53bb\uff0c\u6211\u4e5f\u4e0d\u6562\u52c9\u5f3a\u4f60\u3002 \u4f46\u6211\u5e0c\u671b\u4f60\u5728\u54c0\u4f24\u8fc7\u540e\uff0c\u518d\u4ed4\u7ec6\u7684\u60f3\u60f3\u3002\u201d\u539f\u6765\u957f\u5b59\u6cf0\u6b63\u662f\u56e0\u4e3a\u6015\u674e\u9038\u54c0\u4f24\u592a\u8fc7\uff0c\u7eb5\u4e0d \u4f24\u8eab\uff0c\u4ea6\u5c06\u53d8\u6210\u9893\u5e9f\uff0c\u6240\u4ee5\u60f3\u529d\u4ed6\u56de\u56fd\uff0c\u5e72\u4e00\u756a\u4e8b\u4e1a\uff0c\u597d\u8ba9\u4ed6\u7cbe\u795e\u6709\u6240\u5bc4\u6258\u3002\u540c\u65f6\u4e5f\u6b63 \u56e0\u4e3a\u4ed6\u7231\u4e0a\u5b98\u5a49\u513f\u7231\u5f97\u975e\u5e38\u4e4b\u6df1\uff0c\u660e\u77e5\u82e5\u662f\u674e\u9038\u56de\u53bb\uff0c\u53ef\u80fd\u5bf9\u4ed6\u4e0d\u5229\uff0c\u4f46\u4ed6\u4e3a\u4e86\u4ee4\u5a49\u513f \u5f97\u5230\u5feb\u4e50\uff0c\u4ecd\u7136\u4e00\u518d\u7684\u529d\u674e\u9038\u56de\u53bb\u3002 \u3000\u3000\u674e\u9038\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u6211\u4f1a\u4ed4\u7ec6\u60f3\u7684\u3002\u201d\u957f\u5b59\u6cf0\u548c\u4ed6\u7d27\u7d27\u63e1\u624b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48 \u6211\u73b0\u5728\u8d70\u4e86\uff0c\u6211\u5e0c\u671b\u5c06\u6765\u80fd\u591f\u5728\u957f\u5b89\u548c\u4f60\u518d\u89c1\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u8d70\u540e\uff0c\u674e\u9038\u795e\u601d\u604d\u60da\uff0c\u5fc3\u7eea\u4e0d\u5b81\uff0c\u56de\u5230\u4e86\u4f4f\u6240\uff0c\u7adf\u7136\u75c5\u4e86\u8d77\u6765\u3002\u957f\u5b59\u6cf0\u7684\u8bdd \u5728\u4ed6\u5fc3\u4e2d\u6fc0\u8d77\u4e86\u6781\u5927\u7684\u6ce2\u52a8\uff0c\u56fd\u6068\u3001\u5bb6\u4ec7\u3001\u53cb\u8c0a\u3001\u7231\u60c5\u3001\u5bf9\u4ea1\u59bb\u7684\u4f24\u60bc\uff0c\u5bf9\u77e5\u5df2\u53cb\u4eba\u7684 \u671f\u671b\u2026\u2026\u8fd9\u79cd\u79cd\u7231\u6068\u6101\u70e6\uff0c\u597d\u50cf\u5728\u4ed6\u7684\u5fc3\u4e0a\u6253\u4e86\u65e0\u6570\u96be\u4ee5\u89e3\u5f00\u7684\u7ed3\uff01\u5f53\u771f\u662f\u526a\u4e0d\u65ad\uff0c\u7406 \u8fd8\u4e71\uff01\u5728\u75c5\u4e2d\uff0c\u957f\u5b59\u58c1\u3001\u6b66\u7384\u971c\u3001\u4e0a\u5b98\u5a49\u513f\u7684\u5f71\u5b50\uff0c\u4e00\u4e2a\u4e00\u4e2a\u5728\u4ed6\u5fc3\u5934\u63a0\u8fc7\u3002\u5bf9\u6545\u56fd\u7684 \u6000\u5ff5\uff0c\u8fd9\u662f\u4ed6\u516b\u5e74\u6765\u4e00\u76f4\u538b\u6291\u7740\u7684\uff0c\u8fd9\u65f6\u5019\u4e5f\u611f\u5230\u4e0d\u80fd\u518d\u6291\u5236\u4e86\u3002\u56de\u56fd\u6000\u4e61\u7684\u6101\u601d\uff0c\u5728 \u75c5\u4e2d\u603b\u662f\u4f1a\u7279\u522b\u52a0\u6d53\u7684\uff01 \u3000\u3000\u5728\u75c5\u4e2d\u4ed6\u7684\u513f\u5b50\u5f88\u61c2\u4e8b\u7684\u4f8d\u5019\u4ed6\uff0c\u4f46\u4e5f\u5c61\u6b21\u5411\u4ed6\u95ee\u8d77\u5988\u5988\uff0c\u76fc\u671b\u7236\u4eb2\u7684\u75c5\u5feb\u4e9b\u597d\uff0c \u597d\u5e26\u4ed6\u5230\u957f\u5b89\u53bb\u627e\u5988\u5988\u548c\u59d1\u59d1\u3002\u4ed6\u6127\u5bf9\u5b69\u5b50\u65e0\u90aa\u7684\u773c\u5149\uff0c\u4e5f\u56e0\u6b64\u800c\u5fc3\u60c5\u66f4\u4e71\uff01 \u3000\u3000\u8c37\u795e\u7fc1\u3001\u7b26\u4e0d\u7591\u3001\u590f\u4faf\u575a\u548c\u88f4\u53d4\u5ea6\u56db\u4eba\u672c\u6765\u8981\u56de\u8f6c\u5929\u5c71\u7684\uff0c\u4e5f\u56e0\u4ed6\u803d\u6401\u4e86\u4e0b\u6765\u4e86\u3002 \u5728\u8fd9\u671f\u95f4\uff0c\u5927\u6c57\u4e5f\u66fe\u6d3e\u8fc7\u6b66\u58eb\u5230\u5c71\u4e2d\u641c\u7d22\uff0c\u5e78\u800c\u4ed6\u4eec\u63a9\u853d\u5f97\u597d\uff0c\u53c8\u9760\u6613\u5bb9\u4e39\u4e4b\u52a9\uff0c\u51e0\u6b21 \u9003\u8fc7\u4e86\u641c\u67e5\uff0c\u540e\u6765\u6b66\u58eb\u4e5f\u6ca1\u6709\u518d\u6765\u4e86\u3002 \u3000\u3000\u674e\u9038\u6574\u6574\u75c5\u4e86\u534a\u4e2a\u6708\uff0c\u8fd9\u534a\u4e2a\u6708\u4ed6\u628a\u957f\u5b59\u6cf0\u7684\u8bdd\u60f3\u4e86\u53c8\u60f3\uff0c\u5230\u4e86\u7b2c\u516b\u5929\u5ffd\u7136\u6709\u4e86\u8d77 \u8272\uff0c\u5927\u5bb6\u90fd\u89c9\u5f97\u5947\u602a\uff0c\u53ea\u6709\u590f\u4faf\u575a\u660e\u767d\uff0c\u674e\u9038\u7684\u75c5\u591a\u534a\u662f\u5fc3\u75c5\uff0c\u5fc3\u75c5\u53ea\u6709\u75c5\u4eba\u81ea\u5df1\u80fd\u533b\u3002 \u3000\u3000\u590f\u4faf\u575a\u7ed9\u5403\u4e86\u51e0\u5242\u57f9\u5143\u56fa\u672c\u7684\u836f\uff0c\u674e\u9038\u5f88\u5feb\u7684\u6062\u590d\u4e86\u5065\u5eb7\u3002\u8fd9\u4e00\u65e5\u4ed6\u5ffd\u7136\u5bf9\u513f\u5b50\u8bf4 \u9053\uff1a\u201c\u654f\u513f\uff0c\u4f60\u4e0d\u662f\u60f3\u6211\u5230\u957f\u5b89\u53bb\u5417\uff1f\u6211\u73b0\u5728\u5c31\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u654f\u62cd\u624b\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u5988\u5988\u548c\u59d1\u59d1\u90fd\u5728\u957f\u5b89\uff0c\u957f\u5b89\u6709\u8bb8\u591a\u597d\u770b\u597d\u5403\u7684\u4e1c\u897f\uff0c\u7239 \u7239\uff0c\u6211\u4e5f\u8981\u53bb\u3002\u201d\u674e\u9038\u63e1\u7740\u4ed6\u7684\u5c0f\u624b\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u654f\u513f\uff0c\u4f60\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u8fc7\u4e24\u5e74\u6211\u518d\u5e26 \u4f60\u53bb\uff0c\u4f60\u8ddf\u7740\u590f\u4faf\u516c\u516c\u548c\u88f4\u4f2f\u4f2f\uff0c\u8981\u542c\u516c\u516c\u548c\u4f2f\u4f2f\u7684\u8bdd\u3002\u201d\u674e\u5e0c\u654f\u6709\u70b9\u5931\u671b\uff0c\u4f46\u4ed6\u4fa7\u7740 \u8111\u888b\u60f3\u4e86\u4e00\u60f3\uff0c\u5f88\u5feb\u53c8\u9ad8\u5174\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\u4f60\u7ed9\u6211\u5411\u5988\u5988\u95ee\u597d\uff0c\u5411\u59d1\u59d1\u95ee\u597d\u3002\u8bf4\u654f \u513f\u8bb0\u6302\u5979\u4eec\uff0c\u8bf7\u4ed6\u4eec\u5feb\u4e9b\u56de\u6765\u770b\u6211\u3002\u55ef\uff0c\u5988\u5988\u548c\u59d1\u59d1\u73b0\u5728\u662f\u597d\u670b\u53cb\u4e86\uff0c\u59d1\u59d1\u7ed9\u679c\u5b50\u6211\u5403\uff0c \u5988\u5988\u4e0d\u4f1a\u518d\u751f\u6c14\u4e86\uff0c\u662f\u5417\uff1f\u201d\u674e\u9038\u4e00\u9635\u5fc3\u9178\uff0c\u51e0\u4e4e\u6ef4\u4e0b\u6cea\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u5979\u4eec\u90fd\u5f88 \u75bc\u4f60\uff0c\u6211\u4f1a\u66ff\u4f60\u5411\u5979\u4eec\u95ee\u597d\u7684\u3002\u201d \u3000\u3000\u7b26\u4e0d\u7591\u9080\u8c37\u795e\u7fc1\u5230\u5929\u5c71\u540c\u4f4f\uff0c\u590f\u4faf\u575a\u5219\u5230\u5357\u5929\u5c71\u4e0e\u88f4\u53d4\u5ea6\u9690\u5c45\uff0c\u5c09\u8fdf\u70af\u548c\u4f18\u4e91\u8001\u5c3c \u7684\u575f\u5893\u90fd\u5728\u5357\u5929\u5c71\u3002\u590f\u4faf\u575a\u613f\u610f\u966a\u4f34\u4ed6\uff0c\u674e\u9038\u5c06\u513f\u5b50\u4ea4\u6258\u7ed9\u590f\u4faf\u575a\uff0c\u590f\u4faf\u575a\u9053\uff1a\u201c\u8fd9\u5b69 \u5b50\u5f88\u806a\u660e\uff0c\u53d4\u5ea6\u4f60\u6559\u4ed6\u5251\u672f\uff0c\u6211\u6559\u4ed6\u8bfb\u4e66\uff0c\u5b69\u5b50\u4f60\u957f\u5927\u4e86\u6b22\u559c\u505a\u4ec0\u4e48\uff1f\u201d\u674e\u5e0c\u654f\u9053\uff1a \u201c\u6211\u60f3\u50cf\u7239\u7239\u4e00\u6837\u505a\u4e00\u4e2a\u5251\u5ba2\uff0c\u4e5f\u60f3\u50cf\u516c\u516c\u4e00\u6837\uff0c\u505a\u4e00\u4e2a\u533b\u751f\uff0c\u6211\u5b66\u4e86\u5251\u672f\u6740\u574f\u4eba\uff0c\u5b66 \u4e86\u533b\u672f\u6551\u597d\u4eba\uff0c\u516c\u516c\uff0c\u4f60\u8bf4\u597d\u4e0d\u597d\uff1f\u201d\u590f\u4faf\u575a\u7fd8\u8d77\u62c7\u6307\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8bf4\u5f97\u771f\u597d\uff01\u5c06\u6765\u516c \u516c\u628a\u672c\u9886\u90fd\u4f20\u6388\u7ed9\u4f60\u3002\u201d\u674e\u9038\u5fd9\u9053\uff1a\u201c\u8fd8\u4e0d\u78d5\u5934\uff01\u201d\u674e\u5e0c\u654f\u4e56\u5de7\u5f97\u5f88\uff0c\u7acb\u5373\u78d5\u5934\u8bf4\u9053\uff1a \u201c\u90a3\u4e48\uff0c\u516c\u516c\uff0c\u6211\u8981\u6539\u53e3\u53eb\u4f60\u505a\u5e08\u7236\u4e86\u3002\u201d\u590f\u4faf\u575a\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u674e\u9038\uff0c\u6211\u548c\u4f60\u7684\u5e08\u7236 \u662f\u597d\u670b\u53cb\uff0c\u4f60\u5e08\u7236\u6709\u4f60\u8fd9\u4e2a\u597d\u5f92\u5f1f\uff0c\u6211\u4e00\u76f4\u975e\u5e38\u7fa1\u6155\uff0c\u5982\u4eca\u6211\u4e5f\u6709\u4e00\u4e2a\u597d\u5f92\u5f1f\u4e86\u3002\u6211\u6562 \u5938\u53e3\uff0c\u6211\u5c06\u6765\u6559\u51fa\u7684\u5f92\u5f1f\u8981\u6bd4\u4f60\u5e08\u7236\u7684\u5f92\u5f1f\u597d\uff01\u201d\u7b26\u4e0d\u7591\u4e5f\u7b11\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u5df2\u7ecf\u6b7b\u4e86\uff0c \u4f60\u8fd8\u8981\u8d4c\u4e00\u53e3\u6c14\u5417\uff1f\u201d\u672c\u6765\u6309\u6b66\u6797\u4e2d\u7684\u8f88\u4efd\u89c4\u77e9\uff0c\u590f\u4faf\u575a\u6bd4\u674e\u5e0c\u654f\u9ad8\u4e86\u4e24\u8f88\uff0c\u82e5\u975e\u590f\u4faf \u575a\u5148\u900f\u9732\u51fa\u613f\u610f\u6536\u5f92\u4e4b\u610f\uff0c\u674e\u9038\u662f\u4e0d\u6562\u8ba9\u513f\u5b50\u62dc\u5e08\u7684\uff0c\u96be\u5f97\u8fd9\u51e0\u4f4d\u524d\u8f88\u90fd\u662f\u975e\u5e38\u6d12\u8131\u8c41 \u8fbe\u7684\u4eba\uff0c\u4e1d\u6beb\u4e5f\u4e0d\u62d8\u4e8e\u6b66\u6797\u4e2d\u7684\u9648\u89c4\u65e7\u77e9\u3002 \u3000\u3000\u590f\u4faf\u575a\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8981\u5b66\u5251\u672f\uff0c\u90a3\u8fd8\u5f97\u62dc\u4e00\u4e2a\u5e08\u7236\u5440\uff01\u201d\u674e\u5e0c\u654f\u53c8\u53bb\u5411\u88f4\u53d4\u5ea6\u53e9\u5934\uff0c \u88f4\u53d4\u5ea6\u8fde\u5fd9\u6447\u624b\u9053\uff1a\u201c\u8fd9\u4f7f\u4e0d\u5f97\uff0c\u8fd9\u4f7f\u4e0d\u5f97\uff01\u201d\u4f46\u590f\u4faf\u575a\u5df2\u628a\u4ed6\u53cc\u624b\u6309\u4f4f\uff0c\u8ba9\u674e\u5e0c\u654f\u7aef \u7aef\u6b63\u6b63\u7684\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4f7f\u4e0d\u5f97\uff1f\u54b1\u5404\u6559\u5404\u7684\uff0c\u7406\u4ed6\u4ec0\u4e48\u8f88\u4efd\u89c4\u77e9\uff1f \u4f60\u6015\u964d\u4f4e\u4e86\u4f60\u7684\u8f88\u4efd\u5417\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1a\u201c\u4e0d\uff0c\u90a3\u662f\u62ac\u9ad8\u4e86\u6211\u7684\u8f88\u4efd\u4e86\u3002\u201d\u4e24\u4e2a\u76f8\u5dee\u4e00\u8f88 \u7684\u4eba\u540c\u6536\u4e00\u4e2a\u5f92\u5f1f\uff0c\u5bf9\u8f88\u4efd\u4f4e\u7684\u90a3\u4e2a\u5e08\u7236\u800c\u8a00\uff0c\u4ed6\u65e2\u662f\u548c\u5f92\u5f1f\u540c\u4e00\u8f88\u4efd\uff0c\u53c8\u548c\u4ed6\u7684\u5c0a\u957f \u540c\u4e00\u8f88\u4efd\uff0c\u6240\u4ee5\u590f\u4faf\u575a\u548c\u6590\u53d4\u5ea6\u5404\u8bf4\u4e00\u8f9e\uff0c\u4f17\u4eba\u542c\u4e86\u90fd\u54c8\u54c8\u5927\u7b11\u3002\u674e\u9038\u5fc3\u4e2d\u7684\u9ad8\u5174\u66f4\u4e0d \u7528\u63d0\u4e86\uff0c\u5c06\u6765\u4ed6\u7684\u513f\u5b50\u6210\u4e3a\u4e00\u4ee3\u5927\u4fa0\uff0c\u4e00\u4ee3\u56fd\u624b\uff0c\u540d\u6ee1\u5929\u4e0b\uff0c\u5e72\u4e86\u4e00\u756a\u8f70\u8f70\u70c8\u70c8\u7684\u4e8b\u4e1a\uff0c \u8fdc\u65fa\u4e8e\u4ed6\uff0c\u8fd9\u662f\u540e\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u674e\u9038\u548c\u51e0\u4f4d\u524d\u8f88\u522b\u8fc7\uff0c\u53ea\u8eab\u56de\u56fd\uff0c\u56de\u9996\u524d\u5c18\uff0c\u604d\u5982\u4e00\u68a6\u3002\u4ed6\u4ecd\u7136\u626e\u4f5c\u4e00\u4e2a\u7ef4\u65cf \u730e\u4eba\uff0c\u907f\u4eba\u8033\u76ee\u3002\u8d70\u4e86\u51e0\u5929\uff0c\u8fd9\u5929\u5728\u8349\u539f\u4e0a\u5355\u9a91\u72ec\u884c\uff0c\u5ffd\u89c1\u524d\u9762\u9ec4\u6c99\u6eda\u6eda\uff0c\u6709\u4e00\u961f\u519b\u9a6c \u9a70\u6765\u3002 \u3000\u3000\u674e\u9038\u767b\u4e0a\u9ad8\u5730\u4e00\u770b\uff0c\u4f46\u89c1\u65cf\u65d7\u59d4\u5730\uff0c\u9a6c\u592b\u79bb\u978d\uff0c\u8863\u7532\u4e0d\u5168\uff0c\u519b\u5bb9\u51cc\u4e71\uff0c\u770b\u6765\u7adf\u662f\u4e00 \u961f\u6e83\u5175\u3002\u674e\u9038\u4e00\u7b97\u884c\u7a0b\uff0c\u79bb\u8fb9\u5173\u5df2\u662f\u4e0d\u8fdc\uff0c\u6562\u60c5\u8fd9\u961f\u6e83\u5175\u4e43\u662f\u4ece\u524d\u7ebf\u8d25\u4e0b\u6765\u7684\u3002\u674e\u9038\u65e2 \u559c\u4e14\u5fe7\uff0c\u559c\u8005\u662f\u4e2d\u56fd\u6253\u4e86\u80dc\u4ed7\uff0c\u5fe7\u8005\u662f\u8d25\u519b\u65e0\u4eba\u7ba1\u8f96\uff0c\u6cbf\u9014\u62a2\u52ab\u767e\u59d3\uff0c\u90a3\u662f\u96be\u514d\u7684\u4e86\u3002 \u3000\u3000\u674e\u9038\u907f\u5f00\u9014\u519b\u7684\u65b9\u5411\uff0c\u8d70\u4e86\u4e00\u7a0b\uff0c\u5ffd\u89c1\u6709\u51e0\u4e2a\u7a81\u53a5\u5175\u7eb5\u9a6c\u8ffd\u6765\uff0c\u5927\u58f0\u559d\u4ed6\u505c\u4e0b\uff0c\u674e \u9038\u4eba\u5f3a\u9a6c\u58ee\uff0c\u672c\u6765\u53ef\u4ee5\u9003\u8dd1\u5f97\u4e86\uff0c\u4f46\u4ed6\u60f3\u4ece\u8fd9\u51e0\u4e2a\u7a81\u53a5\u5175\u58eb\u7684\u53e3\u4e2d\u6253\u542c\u519b\u60c5\uff0c\u6545\u610f\u7f13\u7f13 \u800c\u884c\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u56db\u5339\u9a6c\u9f50\u5411\u674e\u9038\u51b2\u6765\uff0c\u55d6\u55d6\u8fde\u58f0\uff0c\u51b7\u7bad\u5c04\u5230\uff0c\u674e\u9038\u4f7f\u51fa\u63a5\u6697\u5668\u7684\u624b\u6cd5\uff0c \u6765\u4e00\u652f\u63a5\u4e00\u652f\uff0c\u90a3\u51e0\u4e2a\u7a81\u53a5\u5175\u89c1\u4ed6\u6b66\u827a\u5982\u6b64\u9ad8\u5f3a\uff0c\u4e0d\u8c61\u662f\u4e2a\u597d\u60f9\u7684\u4eba\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u4fbf\u60f3 \u62d4\u9a6c\u9000\u53bb\uff0c\u8fd9\u65f6\u53cc\u65b9\u8ddd\u79bb\u5df2\u8fd1\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u674e\u9038\u5c06\u63a5\u6765\u7684\u5229\u7bad\u7529\u624b\u5c04\u51fa\uff0c\u4e0d\u5c04\u4eba \u800c\u5c04\u9a6c\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u90a3\u56db\u4e2a\u7a81\u53a5\u5175\u90fd\u8dcc\u4e0b\u9a6c\u80cc\uff0c\u5728\u96ea\u5730\u4e0a\u6323\u624e\u4e86\u4e00\u9635\uff0c\u7adf\u7136\u722c\u4e0d\u8d77\u6765\uff0c \u89c1\u4ed6\u4eec\u9762\u5982\u83dc\u8272\uff0c\u53cc\u76ee\u6df1\u9677\uff0c\u539f\u6765\u90fd\u662f\u997f\u574f\u4e86\u7684\uff0c\u88ab\u8d1f\u75c5\u72c2\u5954\u7684\u5750\u9a91\u629b\u4e86\u4e0b\u6765\uff0c\u767b\u65f6\u5934 \u6655\u773c\u82b1\uff0c\u54ea\u91cc\u8fd8\u6709\u529b\u6c14\u6323\u624e\uff1f \u3000\u3000\u674e\u9038\u5fc3\u4e2d\u607b\u7136\uff0c\u770b\u5176\u4e2d\u4e00\u4e2a\u7684\u670d\u9970\u4f3c\u662f\u519b\u5b98\uff0c\u989c\u5bb9\u4e5f\u6ca1\u6709\u90a3\u4e48\u6194\u7cb9\uff0c\u674e\u9038\u8df3\u4e0b\u9a6c\u6765\uff0c \u5c06\u4ed6\u6380\u8d77\uff0c\u90a3\u519b\u5b98\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u58ee\u58eb\u9976\u547d\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981 \u5c04\u6740\u6211\uff1f\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u6211\u53ea\u662f\u60f3\u8ba8\u53d6\u4e00\u70b9\u5e72\u7cae\u3002\u201d\u539f\u6765\u8349\u539f\u4e0a\u4eba\u70df\u7a00\u5c11\uff0c\u5f80\u5f80\u6570\u5341\u91cc \u4e0d\u89c1\u4eba\u5bb6\uff0c\u4ed6\u4eec\u997f\u5f97\u614c\u4e86\uff0c\u89c1\u4eba\u5c31\u62a2\uff0c\u5728\u8349\u539f\u4e0a\u5f80\u6765\u7684\u4eba\uff0c\u4e0d\u662f\u730e\u6237\u5c31\u662f\u7267\u6c11\uff0c\u6700\u5c11\u4e5f \u8d2e\u5907\u6709\u51e0\u5929\u5e72\u7cae\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5\u5c06\u4ed6\u4eec\u7684\u5750\u9a91\u6740\u4e86\u6765\u5403\u3002\u81f3\u4e8e\u4ed6\u4eec\u81ea\u5df1\u7684\u5750\u9a91\uff0c\u90a3\u662f\u9003\u751f \u7528\u7684\uff0c\u975e\u5230\u5fc5\u8981\uff0c\u51b3\u4e0d\u80af\u6740\u3002\u201d \u3000\u3000\u674e\u9038\u770b\u4ed6\u4eec\u997f\u5f97\u53ef\u601c\uff0c\u4ed6\u8fd8\u6709\u5341\u51e0\u65a4\u5e72\u7cae\u548c\u51e0\u65a4\u8089\uff0c\u4fbf\u5206\u4e86\u4e00\u534a\u7ed9\u4ed6\u4eec\uff0c\u90a3\u51e0\u4e2a\u7a81 \u53a5\u5175\u5927\u56bc\u4e00\u987f\uff0c\u53c8\u5411\u674e\u9038\u8ba8\u6c34\u6765\u559d\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7cbe\u795e\u624d\u6e10\u6e10\u6062\u590d\uff0c\u5bf9\u674e\u9038\u5343\u6069\u4e07\u8c22\u3002\u4ed6 \u4eec\u89c1\u674e\u9038\u7a7f\u7684\u662f\u7ef4\u65cf\u670d\u9970\uff0c\u8bf4\u7684\u662f\u7a81\u53a5\u8bed\u8a00\uff0c\u53ea\u9053\u90fd\u662f\u65cf\u4eba\uff0c\u90a3\u519b\u5b98\u7f9e\u60ed\u6ee1\u9762\u7684\u8bf4\u9053\uff1a \u201c\u6211\u4eec\u4e5f\u90fd\u662f\u7a77\u82e6\u7684\u767e\u59d3\u51fa\u8eab\uff0c\u82e5\u4e0d\u662f\u997f\u5f97\u614c\u4e86\uff0c\u7edd\u4e0d\u4f1a\u62a2\u81ea\u5df1\u4eba\u7684\u3002\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u600e\u4e48\u8d25\u5f97\u8fd9\u4e48\u60e8\uff1f\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u90fd\u662f\u6211\u4eec\u7684\u957f\u5b98\u4e0d\u597d\uff0c\u4ed6\u9a97\u6211\u4eec\u4e2d\u56fd \u5175\u4e0d\u80fd\u6253\u4ed7\uff0c\u53eb\u6211\u4eec\u653e\u5fc3\u6253\u8fdb\u53bb\u62a2\u4e2d\u56fd\u7684\u5973\u5b50\u7389\u5e1b\uff0c\u4e0a\u4e2a\u6708\u6211\u4eec\u6253\u8fdb\u4e2d\u56fd\u7684\u5b9a\u5dde\uff0c\u53c8\u653b \u4e0b\u851a\u5dde\u7684\u98de\u72d0\u53bf\uff0c\u957f\u5b98\u53eb\u6211\u4eec\u653e\u706b\u70e7\u5c3d\u4ed6\u4eec\u7684\u623f\u5c4b\uff0c\u628a\u4e2d\u56fd\u4eba\u90fd\u63b3\u63a0\u6765\u5f53\u5974\u5f79\uff0c\u6211\u4eec\u53ea \u9053\u53ef\u4ee5\u957f\u9a71\u76f4\u5165\uff0c\u4e00\u76f4\u6253\u5230\u957f\u5b89\uff0c\u968f\u5730\u90fd\u53ef\u4ee5\u8865\u5145\u7cae\u98df\uff0c\u4fbf\u653e\u5fc3\u5927\u70e7\u5927\u6740\uff0c\u54ea\u77e5\u6211\u4eec\u7acb \u8db3\u672a\u7a33\uff0c\u4e2d\u56fd\u7684\u5927\u519b\u4fbf\u6765\u53cd\u653b\uff0c\u542c\u8bf4\u662f\u4e2d\u56fd\u7684\u7687\u592a\u5b50\u505a\u5143\u5e05\uff0c\u8fd8\u6709\u5410\u8543\u7684\u519b\u961f\u5e2e\u4ed6\u4eec\uff0c \u6211\u4eec\u63a5\u8fde\u6253\u4e86\u51e0\u4e2a\u8d25\u4ed7\uff0c\u5b9a\u5dde\u851a\u5dde\u7684\u4e2d\u56fd\u767e\u59d3\u7eb7\u7eb7\u805a\u96c6\u8d77\u6765\uff0c\u6cbf\u9014\u4f0f\u51fb\uff0c\u65ad\u4e86\u6211\u4eec\u540e\u65b9 \u7684\u7cae\u9053\uff0c\u6211\u4eec\u524d\u519b\u6df1\u5165\uff0c\u540e\u63f4\u4e0d\u65ad\uff0c\u51e0\u4e4e\u5168\u519b\u8986\u6ca1\uff01\u201d \u3000\u3000\u674e\u9038\u542c\u95fb\u5f97\u4ed6\u4eec\u5728\u4e2d\u56fd\u5883\u5185\u5927\u8086\u70e7\u6740\uff0c\u987b\u7709\u7686\u7ad6\uff0c\u63d0\u8d77\u9a6c\u97ad\uff0c\u9a82\u9053\uff1a\u201c\u5982\u6b64\u6b8b\u66b4\uff0c \u7406\u5e94\u5168\u519b\u8986\u5f79\uff0c\u54fc\uff0c\u4f60\u4eec\u5c31\u997f\u6b7b\u4e86\u4e5f\u662f\u6d3b\u8be5\uff01\u201d\u9a6c\u97ad\u6325\u52a8\uff0c\u7167\u7740\u90a3\u4e2a\u7a81\u53a5\u519b\u5b98\u5934\u9876\u6253\u4e0b\uff0c \u90a3\u519b\u5b98\u89c1\u4ed6\u7a81\u7136\u7ffb\u9762\uff0c\u5413\u5f97\u5728\u96ea\u5730\u4e0a\u7f29\u4f5c\u4e00\u56e2\uff0c\u53ea\u542c\u5f97\u50fb\u556a\u4e00\u58f0\uff0c\u9a6c\u97ad\u5728\u4ed6\u9762\u4e0a\u63a0\u8fc7\uff0c \u5374\u672a\u66fe\u6253\u7740\u4ed6\uff0c\u674e\u9038\u5ffd\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u4f60\u4eec\u5927\u6c57\u7684\u7f6a\u8fc7\u3002\u201d\u6536\u56de\u9a6c\u97ad\uff0c \u8df3\u4e0a\u9a6c\u80cc\uff0c\u6487\u4e0b\u4ed6\u4eec\u8d70\u4e86\u3002 \u3000\u3000\u6cbf\u9014\u6240\u89c1\u7684\u6e83\u519b\uff0c\u7edc\u7ece\u4e0d\u7edd\uff0c\u4f46\u90fd\u662f\u96f6\u661f\u5c0f\u80a1\uff0c\u674e\u9038\u4e0d\u60f3\u518d\u62db\u60f9\u4ed6\u4eec\uff0c\u4e00\u9047\u4e0a\u4e86\u5c31 \u8fdc\u8fdc\u907f\u5f00\u3002\u8d70\u4e86\u4e24\u5929\uff0c\u6e83\u519b\u6e10\u6e10\u7a00\u5c11\uff0c\u78b0\u89c1\u51e0\u4e2a\u96be\u6c11\uff0c\u542c\u4ed6\u4eec\u8bf4\u7a81\u53a5\u5927\u6c57\u5df2\u5411\u4e2d\u56fd\u5973\u7687 \u5e1d\u6c42\u548c\uff0c\u672a\u77e5\u771f\u5047\uff0c\u4ed6\u4eec\u7684\u5bb6\u5728\u8fb9\u5883\u9644\u8fd1\uff0c\u9003\u4ea1\u4e86\u5341\u591a\u5929\u73b0\u5728\u60c5\u52bf\u7a0d\u5b9a\uff0c\u5192\u9669\u56de\u5bb6\u63a2\u671b\uff0c \u674e\u9038\u8fde\u65e5\u89c1\u5230\u6218\u4e89\u60e8\u8c61\uff0c\u5fc3\u4e2d\u4e5f\u662f\u548c\u7a81\u53a5\u7684\u767e\u59d3\u540c\u4e00\u613f\u671b\uff0c\u4f46\u613f\u65e9\u65e9\u606f\u4e86\u5e72\u6208\u3002 \u3000\u3000\u518d\u8fc7\u4e24\u5929\uff0c\u5df2\u5230\u4e86\u8fb9\u5883\u7684\u519b\u4e8b\u533a\u57df\uff0c\u674e\u9038\u4e0d\u6562\u518d\u884c\u5927\u8def\u53d6\u9053\u5c71\u533a\uff0c\u60f3\u7a7f\u8fc7\u661f\u661f\u5ce1\u8fdb \u5165\u5b89\u897f\u5185\u5730\uff0c\u4ed6\u7684\u5e72\u7cae\u4e5f\u5403\u5b8c\u4e86\uff0c\u5e78\u800c\u8fd0\u6c14\u5f88\u597d\uff0c\u730e\u5f97\u4e24\u53ea\u91ce\u5154\uff0c\u53ef\u4ee5\u6743\u5145\u4e24\u5929\u7cae\u98df\uff0c \u8fd9\u4e00\u65e5\u6b63\u7a7f\u8fc7\u4e00\u4e2a\u72ed\u957f\u7684\u5c71\u8c37\uff0c\u5ffd\u542c\u524d\u6709\u6781\u60e8\u7684\u53eb\u58f0\uff0c\u542c\u51fa\u662f\u4e00\u4e2a\u7a81\u53a5\u5973\u4eba\u5728\u53eb\u6551\u547d\uff0c \u674e\u9038\u77e5\u9053\u53c8\u662f\u6e83\u519b\u5728\u52ab\u63a0\u767e\u59d3\uff0c\u6025\u5fd9\u98de\u9a6c\u8fc7\u53bb\uff0c\u770b\u89c1\u524d\u9762\u6709\u4e00\u4e2a\u5012\u584c\u4e86\u7684\u8305\u68da\uff0c\u5730\u4e0a\u6709 \u4e2a\u5973\u4eba\u548c\u4e24\u4e2a\u5b69\u5b50\u7684\u5c38\u9996\uff0c\u6740\u5bb3\u8fd9\u4e9b\u5987\u5b7a\u7684\u7adf\u662f\u4e24\u4e2a\u6c49\u4eba\u3002 \u3000\u3000\u674e\u9038\u5927\u6012\uff0c\u76f4\u51b2\u8fc7\u53bb\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u4e24\u5757\u77f3\u5b50\u8fce\u9762\u98de\u6765\uff0c\u52bf\u5927\u529b\u6c89\uff0c\u7adf\u662f\u9ad8\u624b\u6240\u53d1\uff0c \u674e\u9038\u4e0d\u6562\u786c\u63a5\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u5c06\u7b2c\u4e00\u5757\u77f3\u5934\u5288\u843d\uff0c\u968f\u5373\u4fbf\u4e00\u4e2a\u201c\u956b\u91cc\u85cf\u8eab\u201d\u95ea\u5f00\u4e86\u7b2c\u4e8c\u5757 \u77f3\u5934\uff0c\u4ed6\u7684\u5750\u9a91\u5ffd\u5730\u957f\u5636\u4e00\u58f0\uff0c\u56db\u8e44\u5c48\u5730\uff0c\u539f\u6765\u5df2\u7ed9\u5bf9\u65b9\u7684\u77f3\u5b50\u6253\u4e2d\u4e86\u8111\u888b\u3002\u674e\u9038\u53cc\u811a \u4e00\u6491\uff0c\u5982\u7bad\u79bb\u5f26\uff0c\u5728\u9a6c\u80cc\u4e0a\u5c04\u51fa\uff0c\u95ea\u7535\u822c\u7684\u5230\u4e86\u90a3\u4e24\u4e2a\u4eba\u7684\u8ddf\u524d\uff0c\u53cc\u65b9\u6253\u4e86\u4e00\u4e2a\u7167\u9762\uff0c \u4e0d\u89c9\u90fd\u53eb\u51fa\u4e86\u58f0\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u6b63\u662f\u7a0b\u8fbe\u82cf\u7236\u5b50\uff0c\u4f46\u89c1\u4ed6\u4eec\u8863\u6749\u8934\u7f15\uff0c\u6ee1\u9762\u98ce\u5c18\uff0c\u5149\u666f\u751a\u4e3a\u72fc\u72c8\u3002\u674e\u9038\u597d \u751f\u8be7\u5f02\uff0c\u60f3\u4ed6\u4eec\u5df2\u4f9d\u9644\u4e86\u7a81\u6485\u5927\u6c57\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5\u5f97\u4e00\u5b98\u534a\u804c\uff0c\u5374\u4f55\u6545\u72fc\u72c8\u5982\u65af\u3002 \u3000\u3000\u539f\u6765\u7a81\u53a5\u5927\u6c57\u6c42\u548c\u7684\u6d88\u606f\u4e43\u662f\u771f\u7684\uff0c\u6218\u4e89\u7206\u53d1\u4e4b\u540e\uff0c\u6b66\u5219\u5929\u4e58\u673a\u7acb\u5362\u9675\u738b\u4e3a\u7687\u592a\u5b50\uff0c \u547d\u4ed6\u505a\u6cb3\u5317\u9053\u884c\u519b\u5927\u5143\u5e05\uff0c\u72c4\u4ec1\u6770\u5728\u519b\u4e2d\u8f85\u4f50\u4ed6\uff0c\u7528\u610f\u662f\u5728\u8ba9\u4ed6\u638c\u63e1\u5175\u6743\uff0c\u597d\u4f5c\u4ed6\u65e5\u767b \u4f4d\u7684\u51c6\u5907\u3002\u592a\u5b50\u867d\u7136\u5e73\u5eb8\uff0c\u4f46\u72c4\u4ec1\u6770\u66ff\u4ed6\u8c03\u5ea6\uff0c\u751a\u662f\u5f97\u5b9c\uff0c\u5206\u5175\u4e09\u8def\uff0c\u4ee5\u5e7d\u5dde\u90fd\u7763\u5f20\u4ec1 \u53e0\u7edf\u5175\u4e09\u5341\u4e07\u4e3a\u4e1c\u8def\uff0c\u53f3\u7fbd\u6797\u536b\u5927\u5c06\u519b\u960e\u656c\u5bb9\u7edf\u5175\u5341\u4e94\u4e07\u4e3a\u897f\u8def\uff0c\u4ee5\u5410\u9ec4\u5c06\u9886\u6c99\u5412\u5fe0\u4e49 \u7edf\u9888\u85e9\u53cc\u6df7\u5408\u519b\u5341\u4e07\u4e3a\u4e2d\u8def\uff0c\u4e09\u8def\u53cd\u653b\uff0c\u4e0d\u4f46\u5c3d\u590d\u5931\u5730\uff0c\u800c\u4e14\u6253\u5165\u4e86\u7a81\u53a5\u5883\u5185\uff0c\u7a81\u53a5\u5927 \u6c57\u65e0\u6cd5\u62b5\u6321\uff0c\u6d3e\u9053\u4f7f\u8005\u83ab\u8d3a\u8fbe\u4e8e\u5230\u957f\u5b89\u8981\u6c42\u548c\u4eb2\uff0c\u613f\u4ee5\u81ea\u5df1\u7684\u5973\u513f\u5ac1\u7ed9\u4e2d\u56fd\u7687\u592a\u5b50\u7684\u513f \u5b50\uff0c\u5386\u53f2\u4e0a\u7684\u548c\u4eb2\u591a\u662f\u4e2d\u56fd\u5c06\u516c\u4e3b\u6216\u5192\u5145\u7684\u516c\u4e3b\u5ac1\u4e0e\u5916\u56fd\uff0c\u8fd9\u6b21\u7a81\u53a5\u8981\u4ee5\u516c\u4e3b\u548c\u4eb2\uff0c\u90a3 \u662f\u6781\u7f55\u89c1\u7684\u4e8b\u3002\u540e\u6765\u4e8b\u867d\u4e0d\u6210\uff0c\u5374\u5df2\u5728\u5386\u53f2\u4e0a\u7559\u4e0b\u4e00\u6bb5\u201c\u4f73\u8bdd\u201d\u3002\uff08\u4e8b\u89c1\u65e7\u5510\u4e66\u4e00\u4e5d\u56db \u7a81\u53a5\u5217\u4f20\uff09 \u3000\u3000\u5927\u6c57\u6c42\u548c\u7684\u6d88\u606f\u4f20\u51fa\u53bb\uff0c\u7a81\u53a5\u7684\u8001\u767e\u59d3\u77e5\u9053\u4e86\u90fd\u975e\u5e38\u9ad8\u5174\uff0c\u4f46\u4e5f\u6709\u4e00\u5c0f\u64ae\u4eba\u975e\u5e38\u6050 \u614c\uff0c\u4ed6\u4eec\u662f\u4ece\u4e2d\u56fd\u6765\u6295\u5954\u7a81\u53a5\u7684\u53db\u8d3c\uff0c\u5305\u62ec\u4e86\u7a0b\u8fbe\u82cf\u7236\u5b50\u5728\u5185\uff0c\u4ed6\u751f\u6015\u548c\u8bae\u6210\u529f\uff0c\u6b66\u5219 \u5929\u8981\u5411\u5927\u6c57\u7d22\u53d6\u4ed6\u4eec\uff0c\u5c24\u5176\u662f\u7a0b\u8fbe\u82cf\uff0c\u4ed6\u5728\u56fd\u5185\u7684\u65f6\u5019\uff0c\u6b66\u5219\u5929\u5c31\u8981\u7f09\u6355\u4ed6\u7684\uff0c\u56e0\u6b64\u66f4 \u4e3a\u5bb3\u6015\uff0c\u5c31\u5728\u7a81\u53a5\u6c42\u548c\u4f7f\u8005\u51fa\u53d1\u7684\u90a3\u4e00\u5929\uff0c\u4ed6\u4fbf\u6084\u6084\u7684\u9003\u8d70\u4e86\u3002\u7a0b\u8fbe\u82cf\u662f\u4f0f\u864e\u5e2e\u7684\u5e2e\u4e3b\uff0c \u4ed6\u7684\u5e2e\u4f17\u5728\u8fb9\u5883\u4e00\u5e26\uff0c\u4ed6\u60f3\u5077\u8fc7\u8fb9\u5883\u4f1a\u5408\u4ed6\u7684\u5e2e\u4f17\u3002\u60f3\u4e0d\u5230\u4ed6\u5728\u5c71\u91cc\u62a2\u52ab\u907f\u96be\u7ef4\u5987\u7684\u7cae \u98df\uff0c\u5374\u610f\u5916\u7684\u9047\u89c1\u4e86\u674e\u9038\u3002 \u3000\u3000\u674e\u9038\u867d\u7136\u6539\u5bb9\u6613\u8c8c\uff0c\u4f46\u7a0b\u8fbe\u82cf\u5374\u8ba4\u51fa\u4e86\u4ed6\u7684\u90a3\u628a\u5b9d\u5251\uff0c\u77e5\u9053\u65e0\u6cd5\u8eb2\u907f\u3002\u5927\u559d\u4e00\u58f0\uff1a \u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u94c1\u70df\u6746\u4e00\u6296\uff0c\u4e00\u62db\u201c\u4e91\u9f99\u4e09\u73b0\u201d\uff0c\u4fbf\u5411\u674e\u9038\u78d5\u4e0b\u6765\uff0c\u674e\u9038\u4e3e \u5251\u4e00\u8fce\uff0c\u4f46\u542c\u5f97\u2018\u5f53\u2019\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u7a0b\u8fbe\u82cf\u5012\u9000\u4e09\u6b65\uff0c\u674e\u9038\u5374\u53ea\u662f\u6643\u4e86\u6643\u3002 \u3000\u3000\u674e\u9038\u5927\u4e3a\u8be7\u5f02\uff0c\u672c\u6765\u7a0b\u8fbe\u82cf\u7684\u529f\u529b\u6bd4\u4ed6\u6df1\u539a\u5f97\u591a\uff0c\u4f46\u8fd9\u6b21\u5175\u5203\u4e00\u4ea4\uff0c\u674e\u9038\u5374\u53d1\u89c9\u4e86 \u4ed6\u7684\u529f\u529b\u597d\u50cf\u5927\u4e0d\u5982\u524d\uff0c\u539f\u6765\u7a0b\u8fbe\u82cf\u5728\u7a81\u53a5\u5927\u6c57\u53ec\u5f00\u6b66\u58eb\u5927\u4f1a\u7684\u90a3\u5929\uff0c\u88ab\u590f\u4faf\u575a\u7684\u91d1\u9488 \u4f24\u4e86\u4ed6\u7684\u7b4b\u8110\uff0c\u81f3\u4eca\u672a\u6108\uff0c\u52a0\u4ee5\u997f\u4e86\u4e24\u5929\uff0c\u8fd9\u6837\u6b64\u6d88\u5f7c\u957f\uff0c\u4e0e\u674e\u9038\u4e00\u6bd4\u8d77\u6765\uff0c\u5f53\u7136\u76f8\u5f62 \u89c1\u7ecc\u4e86\u3002 \u3000\u3000\u6570\u62db\u4e00\u8fc7\uff0c\u674e\u9038\u7740\u7740\u62a2\u653b\uff0c\u7a0b\u89c1\u7537\u89c1\u52bf\u4e0d\u5999\uff0c\u53d6\u51fa\u5224\u5b98\u53cc\u7b14\uff0c\u4e0a\u524d\u52a9\u653b\uff0c\u7236\u5b50\u4e8c\u4eba\uff0c \u8054\u624b\u5408\u6597\u674e\u9038\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u7684\u529f\u529b\u867d\u5df2\u5927\u51cf\uff0c\u4f46\u70df\u676f\u6253\u7a74\u7684\u7edd\u6280\u8fd8\u5728\uff0c\u4ecd\u7136\u662f\u4e2a\u52b2\u654c\uff0c\u597d\u5728\u4ed6\u7684\u65f1\u70df\u5df2 \u7ecf\u5438\u5b8c\uff0c\u65e0\u6cd5\u7528\u70df\u96fe\u6765\u8ff7\u60d1\u674e\u9038\u7684\u89c6\u7ebf\uff0c\u674e\u9038\u4f4d\u7740\u5b9d\u5251\u7684\u5a01\u529b\u5c55\u5f00\u4e86\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u548c\u4ed6 \u4eec\u7236\u5b50\u4e8c\u4eba\uff0c\u6070\u6070\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u6fc0\u6218\u4e86\u4e00\u767e\u591a\u62db\uff0c\u7a0b\u8fbe\u82cf\u5ffd\u89c9\u5de6\u808b\u540e\u7684\u201c\u9b42\u95e8\u7a74\u201d\u9690\u9690\u4f5c\u75db\uff0c\u539f\u6765\u8fd9\u6b63\u662f\u4ed6\u88ab\u590f\u4faf \u575a\u7684\u91d1\u9488\u6240\u4f24\u4e4b\u5904\uff0c\u5e73\u65f6\u8fd8\u4e0d\u89c9\u5f97\u600e\u4e48\uff0c\u4e00\u5230\u7528\u4e86\u771f\u529b\uff0c\u5185\u4f24\u53c8\u53d1\u4f5c\u4e86\u3002\u7a0b\u8fbe\u82cf\u5fc3\u91cc\u4e00 \u51c9\uff0c\u5192\u9669\u8fdb\u653b\uff0c\u7528\u4e86\u4e00\u62db\u2018\u6a2a\u9a7e\u91d1\u6881\u2019\uff0c\u94c1\u70df\u6746\u9a7e\u7740\u674e\u9038\u7684\u5251\u950b\uff0c\u500f\u7684\u4e00\u4e2a\u8f6c\u8eab\uff0c\u70df\u676f \u6296\u8d77\u4e86\u4e00\u4e2a\u67aa\u82b1\uff0c\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u622a\u674e\u9038\u7684\u4e09\u5904\u5927\u7a74\u3002\u54ea\u77e5\u4ed6\u5feb\u674e\u9038\u4e5f\u5feb\uff0c\u4f46\u89c1\u53cc\u65b9\u8eab \u5f62\u98de\u8d77\uff0c\u674e\u9038\u5927\u559d\u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u659c\u5288\u4e0b\u6765\uff0c\u500f\u7136\u95f4\u53c8\u6539\u5288\u4e3a\u626b\uff0c\u4e00\u62db\u201c\u94c1\u9501\u6a2a\u821f\u201d \u5411\u7a0b\u8fbe\u82cf\u53f3\u80a9\u731b\u524a\uff0c\u8fd9\u4e24\u62db\u8fc5\u5982\u7535\u63a3\uff0c\u53d8\u5316\u5947\u5e7b\uff0c\u7a0b\u8fbe\u82cf\u70df\u6746\u622a\u7a7a\uff0c\u53eb\u58f0\u201c\u4e0d\u597d\uff01\u201d\u6025 \u5fd9\u85cf\u5934\u7f29\u52b2\uff0c\u5411\u4e0b\u4e00\u77ee\u8eab\u8eaf\uff0c\u9976\u662f\u4ed6\u5e94\u53d8\u5f97\u5b9c\uff0c\u4f46\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u5251\u950b\u63a0\u8fc7\uff0c\u5df2\u5728 \u4ed6\u7684\u5de6\u80a9\u524a\u4e0b\u4e86\u4e00\u7247\u8840\u6dcb\u6dcb\u7684\u76ae\u8089\uff0c\u968f\u7740\u53c8\u662f\u201c\u5f53\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u7a0b\u5efa\u7537\u7684\u5224\u5b98\u7b14\u4e5f\u88ab \u524a\u65ad\u4e86\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u53eb\u9053\uff1a\u201c\u5efa\u7537\uff0c\u4f60\u5feb\u8d70\uff01\u201d\u53cc\u76ee\u706b\u7ea2\uff0c\u5c24\u5982\u53d7\u4f24\u7684\u91ce\u517d\u4e00\u822c\uff0c\u5c06\u94c1\u70df\u6746\u821e\u5f97 \u65cb\u98ce\u4f3c\u7684\uff0c\u7d27\u7d27\u7f20\u7740\u674e\u9038\u3002\u7a0b\u5efa\u7537\u65b9\u81ea\u8e0c\u8e0c\uff0c\u672a\u80af\u5373\u8d70\uff0c\u7a0b\u8fbe\u82cf\u5927\u559d\u9053\uff1a\u201c\u4e0d\u8096\u513f\uff0c\u4f60 \u60f3\u7a0b\u5bb6\u65ad\u5b50\u7edd\u5b59\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5efa\u7537\u653e\u58f0\u5927\u54ed\uff0c\u75be\u5954\u800c\u53bb\u3002\u674e\u9038\u867d\u7136\u75db\u6068\u4ed6\u4eec\u4f5c\u6076\u591a\u7aef\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u6115\u7136\u3002 \u7a0b\u8fbe\u82cf\u75af\u72c2\u5730\u6251\u4e0a\u6765\uff0c\u94c1\u70df\u6746\u6a2a\u6572\u76f4\u622a\uff0c\u6709\u5982\u72c2\u98ce\u66b4\u96e8\uff0c\u770b\u6837\u5b50\u662f\u60f3\u62d6\u5ef6\u65f6\u5019\uff0c\u8ba9\u4ed6\u7684 \u513f\u5b50\u9003\u751f\uff0c\u8fc7\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u7a0b\u5efa\u7537\u7684\u54ed\u58f0\u5df2\u542c\u4e0d\u89c1\u4e86\uff0c\u7a0b\u8fbe\u82cf\u4e5f\u50cf\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\u4e00 \u6837\uff0c\u90a3\u80a1\u731b\u52b2\u677e\u61c8\u4e0b\u6765\uff0c\u674e\u9038\u6536\u52bf\u4e0d\u4f4f\uff0c\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u7a0b\u8fbe\u82cf\u7684\u819d\u76d6\u88ab\u524a\u53bb\u4e86 \u4e00\u7247\u3002 \u3000\u3000\u7a0b\u8fbe\u82cf\u8e09\u8e09\u8dc4\u8dc4\u5012\u9000\u6570\u6b65\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6211\u7a0b\u67d0\u7eb5\u6a2a\u4e86\u6570\u5341\u5e74\uff0c\u6b7b\u4e5f\u503c\u5f97\u4e86\uff01\u201d\u674e \u9038\u8d77\u4e86\u4fa7\u9690\u4e4b\u5fc3\uff0c\u6309\u5251\u8bf4\u9053\uff1a\u201c\u7a0b\u8001\u5e2e\u4e3b\uff0c\u4f60\u628a\u4f0f\u864e\u5e2e\u7684\u7b26\u4ee4\u540d\u518c\u4ea4\u4e86\u51fa\u6765\uff0c\u81ea\u5df1\u6bc1\u6389 \u6b66\u529f\uff0c\u6211\u8ba9\u4f60\u56de\u5bb6\u4e0e\u513f\u5b50\u56e2\u805a\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u7a0b\u8fbe\u82cf\u5df2\u662f\u516d\u5341\u7684\u8001\u4eba\u4e86\uff0c\u4fbf\u7559\u4ed6\u4e00\u6761\u6027\u547d \u5427\u3002\u7f34\u4e86\u4ed6\u7684\u7b26\u4ee4\u540d\u518c\uff0c\u6211\u53ef\u4ee5\u9001\u7ed9\u957f\u5b59\u6cf0\u8ba9\u4ed6\u4ea4\u5dee\uff0c\u4e5f\u514d\u5f97\u4f0f\u864e\u5e2e\u518d\u4e3a\u60a3\u5730\u65b9\u3002\u201d \u3000\u3000\u90a3\u6599\u7a0b\u8fbe\u82cf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8981\u6211\u81ea\u6bc1\u6b66\u529f\uff0c\u4ea4\u51fa\u7b26\u4ee4\uff0c\u54c8\uff0c\u4f60\u4e5f\u5c0f\u89d0\u4e86\u6211\u7a0b\u67d0\u4e86\uff01\u5927 \u4e08\u592b\u6b7b\u5219\u6b7b\u8033\uff0c\u5c82\u80fd\u5411\u4eba\u6447\u5c3e\u4e5e\u601c\uff1f\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u6251\u901a\u4e00\u58f0\uff0c\u4ed6\u5df2\u76f4\u633a\u633a\u7684\u5012\u5728 \u5730\u4e0a\u4e86\uff0c\u6562\u60c5\u662f\u81ea\u65ad\u7ecf\u8109\u800c\u4ea1\uff01 \u3000\u3000\u674e\u9038\u5fc3\u4e2d\u6168\u53f9\uff0c\u60f3\u9053\uff1a\u201c\u53c8\u662f\u4e00\u4e2a\u767e\u4f18\u4e0a\u4eba\u3002\u201d\u5ff5\u4ed6\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u60f3\u641c\u51fa\u4e86\u4ed6\u7684\u7b26 \u4ee4\u540d\u518c\u4e4b\u540e\uff0c\u4fbf\u7ed9\u4ed6\u63a9\u57cb\u3002\u674e\u9038\u521a\u4fef\u4e0b\u8170\uff0c\u5ffd\u89c9\u80f8\u524d\u4e00\u9ebb\uff0c\u7a0b\u8fbe\u82cf\u500f\u5730\u8df3\u8d77\uff0c\u94c1\u70df\u6746 \u201c\u535c\u201d\u7684\u4e00\u58f0\u3002\u91cd\u91cd\u7684\u5411\u4ed6\u80f8\u53e3\u6253\u4e0b\uff0c\u8fd9\u4e00\u4e0b\u6253\u4e2a\u6b63\u7740\uff0c\u75db\u5f97\u674e\u9038\u8111\u888b\u6b32\u88c2\uff0c\u672c\u80fd\u7684\u98de \u8d77\u4e00\u811a\uff0c\u8fd9\u4e00\u811a\u8e22\u51fa\uff0c\u7acb\u5373\u4fbf\u611f\u5230\u7a81\u7136\u88ad\u6765\u7684\u6655\u7729\uff0c\u8ff7\u7cca\u4e2d\u4f3c\u542c\u5230\u51c4\u5389\u7684\u53eb\u58f0\uff0c\u63a5\u7740\u4ed6 \u5c31\u4e0d\u7701\u4eba\u4e8b\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u674e\u9038\u624d\u60a0\u60a0\u9192\u8f6c\uff0c\u4f46\u89c1\u5df2\u662f\u9ec4\u660f\u65f6\u5019\uff0c\u843d\u65e5\u4f59\u971e\uff0c\u67d3\u5f97\u5c71\u91ce\u4e00\u62b9 \u91d1\u9ec4\uff0c\u5728\u4ed6\u65c1\u8fb9\u4e0d\u8fdc\uff0c\u5c31\u662f\u90a3\u4e2a\u7ef4\u65cf\u5973\u4eba\u548c\u5979\u4e24\u4e2a\u5b69\u5b50\u7684\u5c38\u4f53\uff0c\u6c14\u6c1b\u76ca\u589e\u6050\u6016\u3002\u674e\u9038\u60f3 \u722c\u8d77\u8eab\uff0c\u5374\u8fd8\u672a\u80fd\u8f6c\u52a8\uff0c\u77e5\u662f\u7a74\u9053\u5c1a\u672a\u89e3\u5f00\uff0c\u5e78\u800c\u4ed6\u5b66\u5f97\u662f\u6b63\u5b97\u5185\u529f\uff0c\u517b\u606f\u4e86\u4e00\u4f1a\uff0c\u4f53 \u529b\u6e10\u6e10\u6062\u590d\uff0c\u8fd0\u771f\u6c14\u51b2\u5173\u89e3\u7a74\uff0c\u53c8\u8fc7\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u5019\uff0c\u8fd9\u624d\u80fd\u591f\u56db\u80a2\u6d3b\u52a8\uff0c\u7ad9\u5f97\u8d77\u6765\u3002 \u674e\u9038\u8d70\u5230\u521a\u624d\u7684\u5730\u65b9\u5bdf\u770b\uff0c\u90a3\u5730\u65b9\u521a\u597d\u662f\u4e00\u5904\u60ac\u5d16\u7684\u65c1\u8fb9\uff0c\u674e\u9038\u5b9a\u775b\u4e00\u770b\uff0c\u5e7d\u8c37\u5e95\u4e0b\u6709 \u4e00\u5177\u5c38\u4f53\uff0c\u501f\u7740\u843d\u65e5\u4f59\u8f89\uff0c\u4ed4\u7ec6\u8fa8\u8ba4\uff0c\u9690\u7ea6\u8fd8\u53ef\u4ee5\u8ba4\u5f97\u51fa\u662f\u7a0b\u8fbe\u82cf\u7684\u5c38\u4f53\u3002 \u3000\u3000\u674e\u9038\u722c\u4e0b\u5c71\u8c37\uff0c\u5728\u7a0b\u8fbe\u82cf\u7684\u8eab\u4e0a\u641c\u51fa\u4e86\u7b26\u5408\u540d\u518c\uff0c\u518d\u722c\u4e0a\u6765\uff0c\u4f46\u89c9\u6d51\u8eab\u9178\u8f6f\uff0c\u6709\u6c14 \u65e0\u529f\uff0c\u539f\u6765\u4ed6\u4e5f\u997f\u5f97\u8f6f\u4e86\u3002 \u3000\u3000\u7ef4\u65cf\u96be\u5987\u7684\u90a3\u5ea7\u8305\u68da\u65e9\u5df2\u6253\u5f97\u7a00\u70c2\u3001\u8305\u68da\u65c1\u8fb9\u7684\u90a3\u4e00\u9505\u7a00\u7ca5\u5012\u8fd8\u4fdd\u5b58\uff0c\u6ce5\u571f\u4e0b\u7684\u6b8b \u706b\u4e5f\u8fd8\u672a\u7184\u51cf\uff0c\u53ea\u662f\u90a3\u4f3c\u6e05\u6c34\u4e00\u822c\u7684\u7a00\u7ca5\u4e0a\u9762\u5374\u6709\u51e0\u70b9\u8840\u82b1\uff0c\u674e\u9038\u53ef\u4ee5\u60f3\u50cf\u5f97\u5230\u5f53\u65f6\u7684 \u60c5\u666f\uff0c\u90a3\u7ef4\u65cf\u5987\u4eba\u716e\u597d\u4e86\u7a00\u7ca5\uff0c\u6b63\u8981\u7ed9\u5979\u7684\u4e24\u4e2a\u5b69\u5b50\u5145\u9965\uff0c\u7a81\u7136\u7a0b\u8fbe\u82cf\u4e24\u7236\u5b50\u6765\u4e86\uff0c\u8fd9 \u4f4d\u66fe\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u4e0d\u53ef\u4e00\u4e16\u7684\u7a0b\u8fbe\u82cf\uff0c\u66fe\u7ecf\u505a\u8fc7\u7a81\u53a5\u5927\u6c57\u4e0a\u5bbe\uff0c\u53c2\u52a0\u8fc7\u5bab\u5ead\u76db\u5bb4\u7684\u7a0b\u8fbe\u82cf\uff0c \u5982\u4eca\u9965\u706b\u4e2d\u70e7\uff0c\u7adf\u7136\u6765\u62a2\u7ef4\u65cf\u5987\u4eba\u8fd9\u4e00\u9505\u7a00\u7ca5\uff01\u4e8e\u662f\u7ef4\u65cf\u5987\u4eba\u6b7b\u547d\u4e89\u593a\uff0c\u7a0b\u8fbe\u82cf\u6740\u4e86\u5979\uff0c \u4e8e\u662f\u5979\u7684\u9c9c\u8840\u6e85\u5165\u9505\u4e2d\uff0c\u7ed9\u90a3\u6e05\u6c34\u4e00\u822c\u7684\u7a00\u7ca5\u52a0\u4e0a\u51e0\u7f15\u6de1\u7ea2\u7684\u989c\u8272\uff01 \u3000\u3000\u674e\u9038\u8111\u6d77\u4e2d\u5e7b\u51fa\u8fd9\u4e00\u5e55\u5e55\u51c4\u60e8\u7684\u60c5\u666f\uff0c\u867d\u7136\u4ec5\u662f\u51e0\u70b9\u8840\u82b1\uff0c\u4ed6\u5982\u95fb\u5230\u6d53\u539a\u7684\u8840\u8165\u5473 \u9053\uff01\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6218\u4e89\u60e8\u9177\uff0c\u4e00\u81f3\u5982\u65af\uff01\u201d\u4ed6\u867d\u7136\u8179\u4f3c\u96f7\u9e23\uff0c\u96be \u582a\u9965\u6e34\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u5fcd\u559d\u8fd9\u9505\u7a00\u7ca5\u4e86\uff0c\u4ed6\u7684\u5750\u9a91\u521a\u624d\u88ab\u7a0b\u8fbe\u82cf\u6253\u788e\u8111\u58f3\uff0c\u8fd9\u65f6\u5df2\u5012\u6bd9\u5728\u8def \u65c1\uff0c\u674e\u9038\u4fbf\u5272\u4e0b\u4e00\u7247\u9a6c\u8089\uff0c\u751f\u8d77\u706b\u6765\u70e4\u719f\uff0c\u585e\u9971\u4e86\u809a\uff0c\u518d\u53bb\u5c71\u6da7\u91cc\u89c5\u6c34\u89e3\u6e34\uff0c\u8fde\u671b\u4e5f\u4e0d \u6562\u518d\u671b\u90a3\u4e00\u9505\u7a00\u7ca5\u3002 \u3000\u3000\u5403\u9971\u4e4b\u540e\uff0c\u674e\u9038\u63a9\u57cb\u4e86\u7a0b\u8fbe\u82cf\u548c\u7ef4\u65cf\u5987\u4eba\u53ca\u5979\u7684\u5b69\u5b50\uff0c\u53c8\u518d\u524d\u884c\u3002\u4ed6\u9760\u9a6c\u8089\u5145\u9965\uff0c \u8d70\u4e86\u4e94\u516d\u5929\uff0c\u7ec8\u4e8e\u8d70\u51fa\u8352\u5c71\uff0c\u7a7f\u8fc7\u4e86\u661f\u661f\u5ce1\uff0c\u6765\u5230\u4e86\u4e2d\u56fd\u7684\u5b89\u897f\u5730\u754c\u3002 \u3000\u3000\u4e00\u522b\u516b\u5e74\uff0c\u5982\u4eca\u4ed6\u53c8\u8e0f\u4e0a\u4e86\u7956\u56fd\u7684\u571f\u5730\u4e86\uff0c\u60f3\u8d77\u5f53\u521d\u4e0e\u957f\u5b59\u58c1\u9a71\u8f66\u51fa\u5173\uff0c\u516b\u5e74\u6765\u7684 \u7ecf\u5386\u4e00\u5e55\u5e55\u7684\u5728\u4ed6\u8111\u4e2d\u95ea\u8fc7\uff0c\u4eff\u5982\u505a\u4e86\u4e00\u573a\u6076\u68a6\uff01\u5982\u4eca\u4e00\u68a6\u9192\u6765\uff0c\u4ed6\u4f9d\u7136\u662f\u5b64\u8eab\u53ea\u5f71\uff0c \u5fc3\u5883\u7684\u51c4\u51c9\u6bd4\u51fa\u56fd\u4e4b\u524d\u66f4\u751a\u3002 \u3000\u3000\u4ed6\u6362\u4e86\u6c49\u4eba\u7684\u670d\u9970\uff0c\u6df7\u8fdb\u96be\u6c11\u7fa4\u4e2d\uff0c\u9003\u5165\u5185\u5730\u3002\u8fd9\u4e00\u7fa4\u96be\u6c11\u662f\u851a\u5dde\u5b9a\u4fa7\u4e00\u5e26\u7684\u8001\u5f31 \u5987\u5b7a\uff0c\u4ed6\u4eec\u7684\u5bb6\u56ed\u88ab\u7a81\u53a5\u5175\u711a\u70e7\u52ab\u63a0\uff0c\u65e9\u5df2\u7a7a\u65e0\u6240\u6709\uff0c\u56e0\u6b64\u9003\u8fdb\u5185\u5730\u89c5\u98df\uff0c\u4e00\u8def\u7684\u51c4\u60e8 \u5883\u51b5\uff0c\u81ea\u662f\u8bf4\u4e4b\u4e0d\u5c3d\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u7684\u795e\u60c5\uff0c\u5374\u6ca1\u6709\u7a81\u53a5\u96be\u6c11\u90a3\u822c\u6cae\u4e27\uff0c\u56e0\u4e3a\u80dc\u5229\u7684\u6d88\u606f \u9891\u4f20\uff0c\u800c\u4e14\u542c\u8bf4\u6b66\u5219\u5929\u4e5f\u5df2\u63a5\u7eb3\u4e86\u7a81\u53a5\u7684\u6c42\u548c\u4f7f\u8005\u4e86\uff0c\u4ed6\u4eec\u8fd8\u6709\u5e0c\u671b\u56de\u53bb\u518d\u91cd\u6574\u5bb6\u56ed\u3002 \u3000\u3000\u8d70\u4e86\u597d\u591a\u5929\uff0c\u6709\u4e9b\u96be\u6c11\u627e\u5230\u4e86\u4eb2\u53cb\u6295\u9760\uff0c\u6709\u4e9b\u96be\u6c11\u88ab\u5b98\u5e9c\u6536\u5bb9\u3002\u96be\u6c11\u7684\u884c\u5217\u66f4\u4e00\u5929 \u5929\u7f29\u77ed\uff0c\u674e\u9038\u5f53\u7136\u4e0d\u613f\u6c42\u5b98\u5e9c\u7684\u6551\u6d4e\u6240\u6536\u5bb9\uff0c\u4ecd\u7136\u968f\u7740\u4e00\u4e9b\u53bb\u6295\u4eb2\u7684\u96be\u6c11\u8d76\u8def\u3002\u8fd9\u65f6\u79bb \u5f00\u6218\u533a\u5df2\u8fdc\uff0c\u540e\u65b9\u5df2\u53ef\u4ee5\u4e70\u5230\u5403\u7684\u4e1c\u897f\u4e86\uff0c\u4e0d\u8fc7\u674e\u9038\u4e3a\u4e86\u907f\u4eba\u6ce8\u76ee\uff0c\u4ecd\u7136\u6df7\u5728\u96be\u6c11\u7fa4\u4e2d\u3002 \u3000\u3000\u518d\u8d70\u4e24\u65e5\uff0c\u8fc7\u4e86\u9152\u6cc9\uff0c\u6b63\u662f\u6625\u8015\u65f6\u5206\uff0c\u7530\u5934\u9647\u7554\uff0c\u519c\u592b\u8377\u9504\uff0c\u7267\u7ae5\u5439\u7b1b\uff0c\u6218\u4e89\u7684\u75d5 \u8ff9\u5df2\u5b8c\u5168\u4e0d\u89c1\uff0c\u6362\u4e0a\u4e86\u4e00\u7247\u5b81\u9759\u548c\u5e73\u7684\u6c14\u6c1b\u3002\u674e\u9038\u7684\u5fc3\u60c5\u4e5f\u597d\u4e86\u8bb8\u591a\u3002\u8fd9\u4e00\u65e5\u6b63\u5728\u8def\u4e0a \u884c\u8d70\uff0c\u5ffd\u89c1\u51e0\u9a91\u5feb\u9a6c\u8d85\u8fc7\u96be\u6c11\u7684\u884c\u5217\uff0c\u5728\u9ec4\u571f\u8def\u4e0a\u626c\u8d77\u4e00\u7247\u5c18\u6c99\uff0c\u674e\u9038\u5ffd\u7136\u53d1\u73b0\u5176\u4e2d\u4e00 \u4e2a\u9a91\u58eb\u76f8\u8c8c\u597d\u719f\u3002 \u3000\u3000\u5fc3\u4e2d\u4e00\u52a8\uff0c\u731b\u5730\u60f3\u8d77\uff1a\u8fd9\u53ef\u4e0d\u662f\u9633\u592a\u534e\uff1f\u8f6c\u773c\u4e4b\u95f4\uff0c\u90a3\u5339\u5feb\u9a6c\u5df2\u8d85\u8fc7\u96be\u6c11\u7684\u884c\u5217\uff0c \u7bad\u4e00\u822c\u7684\u5c04\u5411\u524d\u65b9\uff0c\u5728\u9ec4\u6c99\u6eda\u6eda\u4e4b\u4e2d\u770b\u4e0d\u89c1\u4e86\u3002\u4f46\u674e\u9038\u8fd9\u4e00\u77a5\u5df2\u7ecf\u8ba4\u4e86\u51fa\u6765\uff0c\u4e0d\u9519\uff0c\u662f \u9633\u592a\u534e\uff0c\u662f\u767e\u4f18\u4e0a\u4eba\u90a3\u4e2a\u6700\u5f97\u610f\u7684\u5f1f\u5b50\u2014\u2014\u9633\u592a\u534e\uff01\u770b\u4ed6\u534e\u670d\u9a8f\u9a6c\uff0c\u795e\u6c14\u5341\u8db3\uff0c\u5168\u4e0d\u662f \u96be\u6c11\u6a21\u6837\u3002\u674e\u9038\u4e0d\u7981\u6ee1\u8179\u72d0\u7591\uff1a\u201c\u8fd9\u9633\u592a\u534e\u600e\u7684\u5982\u6b64\u5927\u80c6\uff0c\u7adf\u6562\u5927\u6447\u5927\u6446\u7684\u8fdb\u6765\uff1f\u4ed6\u6df7 \u8fdb\u6765\u505a\u4ec0\u4e48\uff1f\u662f\u9003\u96be\u6216\u662f\u53e6\u6709\u6240\u56fe\uff1f\u548c\u4ed6\u540c\u884c\u7684\u53c8\u662f\u4e9b\u4ec0\u4e48\u4eba\u5462\uff1f\u201d\u79cd\u79cd\u7591\u96be\uff0c\u767e\u601d\u83ab \u89e3\u3002\u7531\u4e8e\u9633\u592a\u534e\u7684\u51fa\u73b0\uff0c\u674e\u9038\u5fc3\u4e2d\uff0c\u591a\u4e86\u51e0\u5206\u6212\u4ff1\u3002 \u3000\u3000\u5230\u4e86\u9152\u6cc9\uff0c\u96be\u6c11\u5341\u6709\u516b\u4e5d\u90fd\u5df2\u5f97\u5230\u5b89\u7f6e\uff0c\u53ea\u6709\u5be5\u5be5\u7684\u5341\u4e2a\u516b\u4e2a\uff0c\u8981\u5230\u5404\u5730\u6295\u4eb2\u7684\uff0c \u4e5f\u90e8\u5206\u6563\u5f00\u4e86\u3002\u674e\u9038\u53d6\u51fa\u4e00\u7247\u91d1\u53f6\uff0c\u5728\u9152\u6cc9\u6362\u4e86\u788e\u94f6\uff0c\u5f53\u65f6\u6709\u4e9b\u6bd4\u8f83\u5bcc\u6709\u7684\u96be\u6c11\uff0c\u5c06\u5168 \u90e8\u5bb6\u8d22\u5e26\u4e86\u9003\u96be\u7684\uff0c\u6240\u4ee5\u91d1\u8086\u4e2d\u4eba\u4e5f\u5e76\u4e0d\u89c9\u5f97\u5947\u602a\uff0c\u674e\u9038\u6362\u4e86\u788e\u94f6\uff0c\u5230\u9aa1\u9a6c\u5e02\u573a\u60f3\u4e70\u4e00 \u5339\u5750\u9a91\uff0c\u5728\u6218\u4e89\u65f6\u95f4\u7684\u9a6c\u5339\u90fd\u88ab\u519b\u961f\u5f81\u53d1\u53bb\u4e86\uff0c\u4ed6\u53ea\u4e70\u5230\u4e00\u5339\u9752\u9aa1\uff0c\u968f\u7740\u53c8\u5230\u8863\u7269\u5e02\u573a \u4e70\u4e86\u4e24\u5957\u5149\u9c9c\u7684\u8863\u670d\u3002\u56e0\u4e3a\u5230\u4e86\u8fdc\u79bb\u6218\u533a\u7684\u540e\u65b9\u57ce\u9547\uff0c\u82e5\u7136\u8fd8\u4ee5\u96be\u6c11\u7684\u8eab\u5206\u51fa\u73b0\uff0c\u90a3\u5c31 \u53cd\u800c\u60f9\u4eba\u6ce8\u76ee\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\uff0c\u674e\u9038\u7115\u7136\u4e00\u65b0\uff0c\u79bb\u5f00\u9152\u6cc9\uff0c\u8de8\u4e86\u9752\u9aa1\u8d76\u8def\u3002\u8d70\u4e86\u516d\u4e03\u5929\uff0c\u8fc7\u4e86\u5170\u5dde\uff0c\u6df1\u5165 \u540e\u65b9\uff0c\u66f4\u662f\u4e00\u7247\u592a\u5e73\u666f\u8c61\uff0c\u4e0e\u7a81\u53a5\u4e3e\u56fd\u9a9a\u4e71\u7684\u60c5\u666f\uff0c\u771f\u6709\u5929\u6e0a\u4e4b\u522b\u3002\u674e\u9038\u5fc3\u60f3\uff1a\u201c\u4e2d\u56fd \u5230\u5e95\u662f\u5730\u5927\u7269\u535a\uff0c\u5e94\u4ed8\u8fd9\u573a\u6218\u4e89\uff0c\u7ef0\u6709\u4f59\u88d5\u3002\u201d\u4f46\u968f\u5373\u53c8\u60f3\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u5355\u9760\u5730\u5927\u7269\u535a\uff0c \u8fd8\u662f\u4e0d\u80fd\u591f\u5728\u6218\u4e89\u4e4b\u4e2d\u4ee4\u5230\u540e\u65b9\u767e\u59d3\u5b89\u5c45\u4e50\u4e1a\u7684\uff0c\u90a3\u8fd8\u8981\u9760\u79c9\u653f\u8005\u8c03\u5ea6\u5f97\u5b9c\uff0c\u624d\u80fd\u591f\u5c3d \u91cf\u51cf\u5c11\u6218\u4e89\u7684\u5f71\u54cd\u3002\u201d\u674e\u9038\u7ecf\u5386\u4e86\u8fd9\u573a\u6218\u4e89\uff0c\u8d70\u4e86\u51e0\u5343\u91cc\u8def\uff0c\u6240\u89c1\u6240\u95fb\uff0c\u611f\u6168\u6781\u591a\u3002\u4ed6 \u4ece\u654c\u4eba\u53e3\u4e2d\u77e5\u9053\u4e86\u6b66\u5219\u5929\u7528\u5175\u7684\u795e\u5999\uff0c\u4ed6\u53c8\u4eb2\u773c\u89c1\u4e86\u4e2d\u56fd\u5b98\u5e9c\u5bf9\u96be\u6c11\u7684\u5b89\u629a\uff0c\u540e\u65b9\u7684\u5e73 \u9759\uff0c\u867d\u7136\u8fd8\u672a\u5fc5\u662f\u5341\u5168\u5341\u7f8e\uff0c\u4f46\u5374\u5904\u5904\u90fd\u8868\u73b0\u4e86\u6b66\u5219\u5929\u662f\u4e2a\u96c4\u624d\u5927\u7565\uff0c\u80af\u4e3a\u767e\u59d3\u529e\u4e8b\u7684 \u541b\u7687\uff01\u4ed6\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u7eb5\u4fbf\u662f\u592a\u5b97\u7687\u5e1d\u590d\u751f\uff0c\u4ed6\u5e94\u4ed8\u8fd9\u573a\u6218\u4e89\uff0c\u60f3\u4ea6\u4e0d\u8fc7\u5982\u6b64\u3002\u90a3\u4e48\uff0c \u5bf9\u767e\u59d3\u6765\u8bf4\uff0c\u53c8\u4f55\u5fc5\u4e00\u5b9a\u8981\u6211\u59d3\u674e\u7684\u505a\u7687\u5e1d\uff1f\u53c8\u4f55\u5fc5\u4e00\u5b9a\u8981\u7537\u4eba\u6765\u505a\u7687\u5e1d\uff1f\u6b66\u5219\u5929\u62a2\u4e86 \u674e\u5bb6\u7684\u5929\u4e0b\uff0c\u6211\u4e00\u76f4\u75db\u6068\u5979\uff0c\u8fd9\u7a76\u7adf\u662f\u5bf9\u5462\uff0c\u8fd8\u662f\u4e0d\u5bf9\uff1f\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u4e00\u7247\u832b\u7136\uff01 \u3000\u3000\u534a\u6708\u4e4b\u540e\uff0c\u674e\u9038\u5230\u4e86\u957f\u5b89\uff0c\u957f\u5b89\u7684\u666f\u8c61\u6bd4\u516b\u5e74\u4e4b\u524d\u66f4\u5174\u65fa\u4e86\uff0c\u5bbd\u5e7f\u7684\u5927\u8857\u4e0a\uff0c\u884c\u4eba \u7199\u6765\u6518\u5f80\uff0c\u7b80\u76f4\u55c5\u4e0d\u5230\u6218\u4e89\u7684\u6c14\u5473\u4e86\u3002\u674e\u9038\u53c8\u4e0d\u7981\u60f3\u5230\u4ed6\u521d\u89c1\u6b66\u7384\u971c\u4e4b\u65f6\uff0c\u4ed6\u5f39\u594f\u201c\u9ecd \u79bb\u201d\u7684\u8bd7\u7bc7\uff0c\u5f53\u65f6\u5728\u4ed6\u60f3\u50cf\u4e4b\u4e2d\uff0c\u957f\u5b89\u662f\u4e00\u7247\u8352\u51c9\uff0c\u6240\u4ee5\u501f\u4e3b\u4eba\u54c0\u4f24\u6545\u56fd\u7684\u8bd7\u7bc7\uff0c\u53d1\u6cc4 \u81ea\u5df1\u80f8\u4e2d\u7684\u90c1\u95f7\uff0c\u5f53\u65f6\u6b66\u7384\u971c\u66fe\u5927\u5927\u7684\u8ba5\u8bbd\u4e86\u4ed6\u3002\u540e\u6765\u4ed6\u5230\u4e86\u957f\u5b89\uff0c\u624d\u53d1\u73b0\u957f\u5b89\u5b8c\u5168\u4e0d \u662f\u4ed6\u60f3\u50cf\u7684\u90a3\u6837\u3002\u5982\u4eca\u4ed6\u53c8\u5230\u957f\u5b89\u6765\u4e86\uff0c\u6b66\u7384\u971c\u8be5\u4e0d\u4f1a\u518d\u8ba5\u8bbd\u4ed6\u4e86\u5427\uff1f \u3000\u3000\u674e\u9038\u627e\u4e00\u95f4\u5ba2\u5e97\u4f4f\u4e0b\uff0c\u6253\u7b97\u8fc7\u4e24\u5929\u53bb\u627e\u957f\u5b59\u6cf0\uff0c\u8bbe\u6cd5\u89c1\u4e0a\u5b98\u5a49\u513f\u4e00\u9762\u3002\u8fd9\u4e00\u665a\u4ed6\u5fc3 \u4e8b\u5982\u6f6e\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e0d\u80fd\u5165\u5bd0\uff0c\u5fc3\u60f3\uff1a\u4e0a\u5b98\u5a49\u513f\u4e0d\u77e5\u6709\u4ec0\u4e48\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u51e0\u6b21\u4e09\u756a\u6258\u4eba \u5e26\u8bdd\uff0c\u8981\u6211\u56de\u6765\u5546\u91cf\uff1f\u53c8\u60f3\u4e0d\u77e5\u6b66\u7384\u971c\u662f\u5426\u4e5f\u5728\u5bab\u4e2d\uff0c\u82e5\u7136\u78b0\u89c1\u4e86\u5979\uff0c\u60c5\u4f55\u4ee5\u582a\uff1f\u7ffb\u6765 \u8986\u53bb\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u4e09\u66f4\u65f6\u5206\u3002 \u3000\u3000\u6b63\u81ea\u5fc3\u7eea\u4e0d\u5b81\uff0c\u5ffd\u542c\u5f97\u5e97\u5c0f\u4e8c\u62cd\u95e8\u53eb\u9053\uff1a\u201c\u5ba2\u5b98\u4eec\u8bf7\u8d77\u6765\uff0c\u5b98\u4eba\u6765\u67e5\u591c\u5566\uff01\u201d\u968f\u5373 \u53c8\u542c\u5f97\u4f3c\u5b98\u5dee\u7684\u53e3\u543b\uff0c\u5927\u58f0\u5406\u559d\u9053\uff1a\u201c\u90fd\u5230\u5916\u9762\u6765\u7ad9\u597d\u961f\uff0c\u542c\u5019\u6821\u5c09\u5927\u4eba\u95ee\u8bdd\uff01\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u83ab\u975e\u4ed6\u4eec\u662f\u51b2\u7740\u6211\u6765\u7684\uff1f\u6562\u60c5\u662f\u6b66\u5219\u5929\u5df2\u77e5\u9053\u6211\u5230\u4e86\u957f\u5b89\uff0c\u6d3e\u4eba \u6765\u641c\u67e5\u6211\u7684\u4e0b\u843d\uff1f\u201d\u4ed6\u867d\u7136\u76f8\u4fe1\u6b66\u5219\u5929\u4e0d\u4f1a\u52a0\u5bb3\u4e8e\u4ed6\uff0c\u4f46\u4ed6\u7a76\u7adf\u8fd8\u662f\u4e0d\u613f\u66b4\u9732\u81ea\u5df1\u7684\u8eab \u4efd\u3002\u53ea\u542c\u5f97\u4eba\u58f0\u811a\u6b65\u58f0\u5608\u5608\u6742\u6742\uff0c\u4f4f\u5ba2\u4eec\u9646\u7eed\u8d70\u51fa\u623f\u95f4\u3002\u674e\u9038\u5fc3\u60f3\uff1a\u201c\u82e5\u7136\u771f\u662f\u6b66\u5219\u5929 \u6d3e\u4eba\u6765\u641c\u67e5\u6211\uff0c\u8fd9\u4e2a\u65f6\u5019\u8981\u8eb2\u907f\u4e5f\u907f\u4e0d\u5f00\u4e86\u3002\u6216\u8005\u662f\u4f8b\u884c\u7684\u67e5\u591c\u5427\uff1f\u6211\u4e14\u4e0d\u5fc5\u5148\u81ea\u591a\u7591\u3002\u201d \u5b9a\u4e86\u5b9a\u795e\uff0c\u8d70\u5230\u5916\u9762\u5927\u5385\uff0c\u5f20\u76ee\u4e00\u671b\uff0c\u8fd9\u4e00\u671b\u4e0d\u7531\u5f97\u4ed6\u4e0d\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u6b66\u5b98\u5e26\u7740\u4e24\u4e2a\u516c\u5dee\uff0c\u6b63\u5728\u90a3\u91cc\u67e5\u95ee\u4f4f\u5ba2\uff0c\u8fd9\u6b66\u5b98\u4e0d\u662f\u522b\u4eba\uff0c\u7adf\u7136\u662f\u9633\u592a\u534e\uff01 \u4e24\u4eba\u76ee\u5149\u4e00\u63a5\uff0c\u9633\u592a\u534e\u5927\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u7a81\u53a5\u6765\u7684\u5978\u7ec6\uff0c\u5feb\u628a\u4ed6\u62ff\u4e0b\uff01\u201d\u674e\u9038\u5927\u6012\u559d \u9053\uff1a\u201c\u4f60\u624d\u662f\u7a81\u53a5\u7684\u5978\u7ec6\uff01\u201d\u9633\u592a\u534e\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u8eab\u4e3a\u4e1c\u95e8\u6821\u5c09\uff0c\u4f60\u8bec\u9677\u5b98\u957f\uff0c\u7f6a\u4e0a \u52a0\u7f6a\uff01\u201d\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\uff0c\u9633\u592a\u534e\u65e9\u5df2\u62ff\u51fa\u4f69\u5200\uff0c\u51b2\u4e86\u8fc7\u6765\uff0c\u54e9\u7684\u4e00\u5200\u5411\u4ed6\u5288\u4e0b\u3002 \u3000\u3000\u674e\u9038\u7684\u5b9d\u5251\u8fd8\u7559\u5728\u623f\u4e2d\uff0c\u4ed6\u4e00\u6765\u6050\u6015\u5b9d\u5251\u6709\u5931\uff0c\u4e8c\u6765\u4ed6\u77e5\u9053\u9633\u592a\u534e\u6b66\u827a\u9ad8\u5f3a\uff0c\u7a7a\u624b \u5bf9\u654c\uff0c\u53ea\u6015\u5403\u4e8f\u3002\u89c1\u9633\u592a\u534e\u51b2\u6765\uff0c\u4ed6\u7acb\u5373\u8f6c\u8eab\u4fbf\u8dd1\uff0c\u9633\u592a\u534e\u4e00\u5200\u780d\u7a7a\uff0c\u5927\u559d\u9053\uff1a\u201c\u5978\u7ec6 \u8fd8\u60f3\u9003\u4e48\uff1f\u201d\u674e\u9038\u4e09\u6b65\u5e76\u4f5c\u4e24\u6b65\uff0c\u5954\u56de\u623f\u95f4\uff0c\u811a\u6b65\u672a\u9690\uff0c\u5ffd\u89c1\u5e10\u540e\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u9053\u5251\u5149 \u7a81\u7136\u523a\u7834\u5e8a\u5e10\uff0c\u8fce\u9762\u622a\u6765\uff0c\u674e\u9038\u8eab\u8eaf\u4e00\u77ee\uff0c\u7528\u4e86\u4e00\u62db\u201c\u63a2\u5b75\u53d6\u73e0\u201d\u7684\u7a7a\u624b\u5165\u767d\u5203\u624b\u6cd5\uff0c \u4f38\u6307\u4e00\u5f39\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u5f39\u4e2d\u90a3\u4eba\u624b\u8155\uff0c\u5de6\u624b\u4e00\u52fe\uff0c\u6293\u7740\u5251\u67c4\uff0c\u7acb\u5373\u5c06\u5b9d\u5251\u593a\u4e86\u8fc7 \u6765\uff0c\u548c\u90a3\u4eba\u6253\u4e86\u4e00\u4e2a\u7167\u9762\uff0c\u674e\u9038\u4e0d\u7531\u5f97\u53c8\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u4e2a\u4eba\u6b63\u662f\u7a0b\u5efa\u7537\uff01 \u3000\u3000\u53ea\u89c1\u7a0b\u5efa\u7537\u53cc\u76ee\u5706\u7741\uff0c\u6076\u6068\u6068\u7684\u9a82\u9053\uff1a\u201c\u674e\u9038\u4f60\u4e5f\u6709\u4eca\u65e5\u4e48\uff1f\u62ff\u8fc7\u547d\u6765\uff01\u201d\u500f\u7684\u62d4 \u51fa\u5224\u5b98\u53cc\u7b14\uff0c\u4e00\u62db\u201c\u53cc\u9f99\u51fa\u6d77\u201d\uff0c\u53cc\u7b14\u4e00\u5206\uff0c\u5206\u70b9\u674e\u9038\u7684\u201c\u671f\u95e8\u7a74\u201d\u548c\u201c\u80a9\u4e95\u7a74\u201d\uff0c\u7a0b \u5efa\u7537\u7684\u5251\u672f\u4e0d\u884c\uff0c\u7528\u5224\u5b98\u7b14\u70b9\u7a74\u5374\u662f\u4ed6\u7684\u5bb6\u4f20\u7edd\u6280\uff0c\u53cc\u7b14\u70b9\u6765\uff0c\u53c8\u72e0\u53c8\u51c6\uff0c\u674e\u9038\u7684\u6b66\u529f \u867d\u7136\u8fdc\u80dc\u4e8e\u4ed6\uff0c\u4f46\u5728\u8fd9\u6597\u5ba4\u4e4b\u4e2d\uff0c\u7cbe\u5999\u7684\u5251\u672f\u96be\u4e8e\u65bd\u5c55\uff0c\u60f3\u5728\u4e09\u62db\u4e24\u5f0f\u4e4b\u5185\uff0c\u5c06\u4ed6\u51fb\u5012\uff0c \u5374\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u7a0b\u5efa\u7537\u4e00\u526f\u62fc\u547d\u7684\u795e\u6c14\uff0c\u72e0\u72e0\u6251\u6765\uff0c\u674e\u9038\u7528\u4e86\u4e00\u62db\u201c\u9000\u6b65\u8de8\u864e\u201d\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u201c\u5f53\u201d \u7684\u4e00\u58f0\u5c06\u4ed6\u53cc\u7b14\u8361\u5f00\uff0c\u538b\u4e0b\u4e86\u4ed6\u7684\u51f6\u7130\uff0c\u6b63\u60f3\u5c55\u5251\u523a\u4ed6\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u5ffd\u89c9\u80cc\u540e\u91d1 \u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u9633\u592a\u534e\u4e5f\u5df2\u95ef\u8fdb\u4e86\u4ed6\u7684\u623f\u95f4\u4e86\uff01 \u3000\u3000\u674e\u9038\u559d\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u4e00\u62db\u201c\u82cf\u79e6\u80cc\u5251\u201d\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u5251\u950b\u4e00\u8f6c\u3002\u53cd\u624b\u4ece\u8098\u5e95\u7a7f \u51fa\uff0c\u8fd9\u4e00\u62db\u5947\u8be1\u65e0\u6bd4\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u9633\u592a\u534e\u7684\u539a\u80cc\u9b3c\u5934\u5200\u7adf\u88ab\u524a\u53bb\u4e86\u5200\u5934\u3002 \u9633\u592a\u534e\u89c1\u4ed6\u5b9d\u5251\u5728\u624b\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u9053\u540e\u4e00\u6b65\uff0c\u6697\u5730\u91cc\u9a82\u58f0\u201c\u8113\u5305\u201d\uff0c\u539f\u6765\u4ed6\u4e0e\u7a0b\u5efa\u7537\u7ea6 \u597d\uff0c\u4ed6\u5c06\u4f4f\u5ba2\u5524\u51fa\u6765\u95ee\u8bdd\uff0c\u7531\u7a0b\u5efa\u7537\u6f5c\u5165\u623f\u95f4\u76d7\u5251\uff0c\u54ea\u77e5\u7a0b\u5efa\u7537\u65b9\u81ea\u5f97\u624b\uff0c\u5374\u53c8\u7ed9\u674e\u9038 \u593a\u4e86\u56de\u53bb\u3002 \u3000\u3000\u674e\u9038\u5b9d\u5251\u5728\u624b\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u5978\u8d3c\uff0c\u597d\u5927\u7684\u80c6\u5b50\uff01\u201d\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c \u53c8\u662f\u4e00\u62db\u201c\u795e\u9f99\u9732\u722a\u201d\uff0c\u5411\u9633\u592a\u534e\u5fc3\u7a9d\u523a\u53bb\uff0c\u9633\u592a\u534e\u4e0d\u6562\u786c\u63a5\uff0c\u817e\u5f97\u4e00\u811a\uff0c\u5c06\u4e00\u5f20\u8336\u51e0 \u8e22\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u674e\u9038\u7684\u5b9d\u5251\u9677\u5165\u8336\u51e0\u4e4b\u4e2d\uff0c\u4e00\u65f6\u95f4\u62d4\u4e0d\u51fa\u6765\uff0c\u53ea\u542c\u5f97\u8111 \u540e\u98ce\u751f\uff0c\u7a0b\u5efa\u7537\u7684\u53cc\u7b14\u53c8\u5230\uff0c\u674e\u9038\u4e00\u4e2a\u76d8\u65cb\uff0c\u90a3\u5f20\u8336\u51e0\u6070\u4f3c\u505a\u4e86\u4ed6\u7684\u76fe\u724c\uff0c\u6321\u4f4f\u4e86\u7a0b\u5efa \u7537\u7684\u53cc\u7b14\uff0c\u674e\u9038\u529b\u900f\u5251\u5c16\uff0c\u5c06\u90a3\u8336\u51e0\u6325\u8d77\uff0c\u5f80\u524d\u4e00\u9001\uff0c\u8336\u51e0\u8131\u624b\u98de\u51fa\uff0c\u9633\u592a\u534e\u4e00\u638c\u5c06\u90a3 \u8336\u51e0\u9707\u88c2\uff0c\u7830\u7830\u4e24\u58f0\uff0c\u8336\u51e0\u78b0\u5230\u7a97\u4e0a\uff0c\u7a97\u95e8\u4e5f\u7ed9\u9707\u5f00\uff0c\u674e\u8fd4\u8eab\u5f62\u4e00\u6643\uff0c\u7acb\u5373\u7a7f\u7a97\u8df3\u51fa\u3002 \u3000\u3000\u9633\u592a\u534e\u548c\u7a0b\u5efa\u7537\u4e5f\u8ddf\u7740\u8df3\u51fa\u6765\uff0c\u674e\u9038\u8df3\u4e0a\u5c4b\u9876\uff0c\u63ed\u8d77\u4e86\u4e00\u53e0\u74e6\u5f80\u4e0b\u4fbf\u6253\uff0c\u9633\u592a\u534e\u4e00 \u638c\u62cd\u51fa\uff0c\u74e6\u7247\u7c89\u788e\uff0c\u7a0b\u5efa\u7537\u6b63\u5728\u4ed6\u7684\u540e\u9762\uff0c\u88ab\u74e6\u783e\u7c89\u5c51\u6e17\u5165\u773c\u4e2d\uff0c\u674e\u9038\u65e9\u5728\u638c\u5fc3\u6263\u4e86\u51e0 \u679a\u94dc\u94b1\uff0c\u90a3\u94dc\u94b1\u4e00\u6253\u4e0b\u6765\uff0c\u8ddf\u7740\u4fbf\u4ee5\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u624b\u6cd5\uff0c\u5c06\u624b\u4e2d\u7684\u94dc\u94b1\u5f53\u4f5c\u91d1\u94b1\u9556\u53d1 \u51fa\uff0c\u7a0b\u5efa\u7537\u773c\u775b\u4e00\u65f6\u8f9f\u4e0d\u5f00\uff0c\u817f\u5f2f\u7684\u7a74\u9053\u88ab\u4e00\u679a\u94dc\u94b1\u6253\u4e2d\uff0c\u767b\u65f6\u683d\u5012\u3002\u9633\u592a\u534e\u5374\u5df2\u8df3\u4e0a \u4e86\u74e6\u9762\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6765\u62ff\u5978\u7ec6\u5440\uff01\u201d \u3000\u3000\u674e\u9038\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u62ff\u4ed6\u89c1\u5b98\uff0c\u4e8e\u6211\u4e0d\u4fbf\u3002\u4e0d\u5982\u5148\u89c1\u4e86\u6cf0\u5144\u518d\u8bf4\uff01\u201d\u65e0\u5fc3\u604b\u6218\uff0c\u5f53\u4e0b \u65bd\u5c55\u8f7b\u529f\uff0c\u8df3\u8fc7\u4e24\u95f4\u94fa\u95f4\uff0c\u9633\u592a\u534e\u5927\u53eb\u5927\u56b7\uff0c\u4ecd\u7136\u7d27\u9053\u4e0d\u820d\u3002 \u3000\u3000\u674e\u9038\u5927\u6012\uff0c\u8df3\u4e0b\u8857\u9053\uff0c\u5927\u559d\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u6709\u738b\u6cd5\u4e48\uff1f\u4eac\u57ce\u4e4b\u4e2d\uff01\u7adf\u5bb9\u5978\u8d3c\u65e0\u6cd5\u65e0\u5929\uff01\u201d \u8857\u5934\u6b63\u6709\u4e00\u5c0f\u961f\u5de1\u903b\u7684\u5175\u58eb\uff0c\u542c\u5f97\u55a7\u95f9\uff01\u6025\u5fd9\u5954\u6765\uff0c\u9633\u592a\u534e\u8df3\u4e0b\u8857\u5fc3\uff0c\u4e5f\u5927\u58f0\u559d\u9053\uff1a \u201c\u4f60\u7b49\u5feb\u62ff\u5978\u7ec6\uff0c\u4e0d\u5f97\u6709\u8bef\uff01\u201d\u90a3\u4e9b\u5175\u58eb\u8f70\u7136\u5e94\u547d\uff0c\u5f20\u5f13\u642d\u7bad\uff0c\u7eb7\u7eb7\u5411\u674e\u9038\u5c04\u6765\u3002\u674e\u9038 \u5403\u4e86\u4e00\u60ca\uff0c\u521d\u65f6\u4ed6\u8fd8\u5f53\u9633\u592a\u534e\u662f\u5192\u5145\u7684\u519b\u5b98\uff0c\u5982\u4eca\u89c1\u8fd9\u4e9b\u5175\u58eb\u90fd\u542c\u9633\u592a\u534e\u7684\u5429\u5490\uff0c\u770b\u6765 \u7adf\u662f\u771f\u7684\u4e86\uff01\u674e\u9038\u771f\u662f\u60f3\u4e0d\u660e\u767d\uff0c\u4ed6\u600e\u7684\u7adf\u6709\u5982\u6b64\u624b\u6bb5\uff0c\u4e00\u5230\u957f\u5b89\uff0c\u5c31\u6df7\u5f97\u4e2a\u4ec0\u4e48\u4e1c\u95e8 \u6821\u5c09\u7684\u5b98\u513f\uff1f \u3000\u3000\u90a3\u4e9b\u7eb7\u7eb7\u5c04\u6765\u7684\u5229\u7bad\u5f53\u7136\u4f24\u4e0d\u4e86\u674e\u9038\uff0c\u53ef\u662f\u4e5f\u5c06\u4ed6\u963b\u4e86\u4e00\u963b\uff0c\u9633\u592a\u534e\u53c8\u8ffd\u5230\u8eab\u540e\uff0c \u674e\u9038\u4e14\u6218\u4e14\u8d70\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u8d8a\u8fc7\u4e86\u4e24\u6761\u957f\u8857\u3002\u674e\u9038\u7684\u672c\u9886\u867d\u7136\u7a0d\u80dc\u9633\u592a\u534e\u4e00\u7b79\uff0c\u4f46\u4ed6 \u5f97\u5b98\u5175\u4e4b\u52a9\uff0c\u674e\u9038\u65f6\u4e0d\u65f6\u8981\u9632\u5907\u6697\u5904\u5c04\u6765\u7684\u51b7\u7bad\uff0c\u7adf\u88ab\u4ed6\u7f20\u5f97\u4e0d\u80fd\u8131\u8eab\u3002 \u3000\u3000\u674e\u9038\u5f85\u4ed6\u8ffd\u8fd1\uff0c\u7a81\u7136\u6b62\u6b65\uff0c\u5237\u7684\u4e00\u5251\uff0c\u53cd\u624b\u523a\u51fa\uff0c\u9633\u592a\u534e\u4e0d\u6562\u786c\u63a5\uff0c\u7528\u4e86\u4e00\u62db\u201c\u987a \u624b\u63a8\u821f\u201d\uff0c\u987a\u7740\u5251\u52bf\uff0c\u60f3\u628a\u674e\u9038\u7684\u5b9d\u5251\u5f15\u51fa\u5916\u95e8\uff0c\u8fd9\u65f6\uff0c\u80cc\u540e\u6709\u5341\u51e0\u652f\u51b7\u7bad\u5c04\u6765\uff0c\u9633\u592a \u534e\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u957f\u773c\u775b\u5417\uff1f\u505c\u6b62\u653e\u7bad\uff0c\u8d76\u5feb\u5305\u56f4\uff01\u201d\u8bdd\u5c24\u672a\u4e86\uff0c\u4f46\u89c1\u5251\u5149\u95ea\u70c1\uff0c\u9c9c\u8840 \u76f4\u5192\uff0c\u9633\u592a\u534e\u7684\u80a9\u5934\u5df2\u662f\u4e2d\u4e86\u4e00\u5251\u3002 \u3000\u3000\u539f\u6765\u5728\u521a\u624d\u8ffd\u9010\u7684\u65f6\u5019\uff0c\u674e\u9038\u4e0e\u9633\u592a\u534e\u4e00\u524d\u4e00\u540e\uff0c\u5f13\u7bad\u624b\u81ea\u662f\u5bb9\u6613\u8ba4\u6e05\u76ee\u6807\uff0c\u5982\u4eca \u674e\u9038\u7a81\u7136\u6b62\u6b65\uff0c\u4e0e\u4ed6\u8fd1\u8eab\u7f20\u6597\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u5f13\u7bad\u624b\u4e00\u65f6\u672a\u66fe\u5bdf\u89c9\uff0c\u4ecd\u7136\u4e0d\u505c\u7684\u653e\u7bad\uff0c\u8fd9 \u6837\u4e00\u6765\uff0c\u5c04\u6765\u7684\u5229\u7bad\u4fbf\u5bf9\u53cc\u65b9\u90fd\u6709\u5a01\u80c1\u4e86\u3002\u4f46\u674e\u9038\u4f7f\u7684\u4e43\u662f\u5b9d\u5251\uff0c\u5f13\u7bad\u787c\u4e0a\u4fbf\u5373\u6298\u65ad\uff0c \u81ea\u5360\u4fbf\u5b9c\uff0c\u9633\u592a\u534e\u5374\u8981\u4e00\u9762\u62b5\u5fa1\u654c\u4eba\uff0c\u4e00\u9762\u8eb2\u907f\u5f13\u7bad\uff0c\u4ed6\u7684\u6b66\u529f\u672c\u6765\u5c31\u6bd4\u674e\u9038\u7a0d\u900a\u4e00\u7b79\uff0c \u5f53\u7136\u66f4\u5403\u4e8f\u4e86\u3002\u5e78\u800c\u8fd9\u4e00\u5251\u4ec5\u5728\u4ed6\u7684\u80a9\u4e0a\u5212\u4e86\u4e00\u9053\u4e09\u5bf8\u6765\u957f\u7684\u53e3\u5b50\uff0c\u672a\u66fe\u4f24\u7740\u4ed6\u7684\u9aa8\u5934\u3002 \u3000\u3000\u7ecf\u8fc7\u9633\u592a\u534e\u8fd9\u4e48\u4e00\u559d\uff0c\u7bad\u662f\u505c\u6b62\u4e86\uff0c\u53ef\u662f\u674e\u9038\u4e5f\u7acb\u5373\u9003\u4e86\u3002\u9633\u592a\u534e\u53c8\u6c14\u53c8\u6012\uff0c\u559d\u9053\uff1a \u201c\u77a7\u7740\u524d\u9762\u5e26\u65b9\u5dfe\u8fd9\u4eba\uff0c\u653e\u7bad\uff01\u201d\u957f\u5b89\u5404\u6761\u8857\u9053\uff0c\u90fd\u6709\u5de1\u591c\u7684\u5175\u3002\u9633\u592a\u534e\u5306\u5306\u88f9\u597d\u4f24\u53e3\uff0c \u4ecd\u7136\u5fa1\u5c3e\u6025\u8ffd\uff0c\u4e00\u9762\u5927\u58f0\u5730\u559d\uff0c\u6307\u70b9\u76ee\u6807\u3002\u4ed6\u6253\u597d\u4e3b\u610f\uff0c\u4e0e\u674e\u9038\u81f3\u5c11\u4fdd\u6301\u4e09\u4e94\u4e08\u7684\u8ddd\u79bb\uff0c \u514d\u5f97\u51b7\u7bad\u8bef\u4f24\u3002 \u3000\u3000\u674e\u9038\u4e00\u628a\u626f\u4e0b\u5934\u4e0a\u7684\u65b9\u4e2d\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u9633\u592a\u534e\uff0c\u6211\u5c31\u4e0e\u4f60\u6bd4\u6bd4\u8f7b\u529f\u3002\u201d\u4e13\u62e3\u50fb\u9759\u7684 \u8857\u9053\u9003\u53bb\uff0c\u9633\u592a\u534e\u6012\u9053\uff1a\u201c\u4f60\u9003\u5230\u5929\u8fb9\uff0c\u6211\u4e5f\u8981\u8ffd\u3002\u201d\u98ce\u9a70\u7535\u9010\u7684\u8ffd\u4e86\u4e00\u4f1a\uff0c\u674e\u9038\u94bb\u5165 \u4e00\u6761\u72ed\u957f\u7684\u8857\u5df7\uff0c\u9633\u592a\u534e\u7d27\u8ddf\u7740\u4e5f\u5230\u4e86\u5df7\u53e3\uff0c\u7a81\u7136\u5728\u5df7\u53e3\u7684\u90a3\u8fb9\u53c8\u662f\u4e00\u6392\u5f13\u7bad\u5c04\u6765\uff0c\u9633 \u592a\u534e\u6325\u821e\u957f\u5200\uff0c\u62d4\u6253\u5f13\u7bad\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6211\u662f\u4e1c\u95e8\u6821\u5c09\uff0c\u524d\u9762\u90a3\u4e2a\u5c0f\u5b50\u662f\u7a81\u53a5\u5978\u7ec6\uff0c\u4f60 \u4eec\u5feb\u5835\u622a\u4ed6\uff01\u201d\u54e9\u7684\u4e00\u58f0\uff0c\u4e00\u652f\u52b2\u7bad\u75be\u5c04\u800c\u6765\uff0c\u9633\u592a\u534e\u7528\u5200\u4e00\u62d4\uff0c\u90a3\u652f\u7bad\u529b\u9053\u5927\u5f97\u51fa\u5947\uff0c \u4f59\u52bf\u672a\u8870\uff0c\u7bad\u5934\u4e00\u6b6a\uff0c\u7adf\u7136\u63d2\u5165\u4e86\u4ed6\u7684\u5c0f\u817f\u3002 \u3000\u3000\u9633\u592a\u534e\u6012\u53eb\u9053\uff1a\u201c\u505c\u6b62\u653e\u7bad\uff0c\u8d76\u5feb\u6349\u8d3c\uff01\u201d\u4e00\u54ac\u7259\u628a\u90a3\u652f\u5229\u7bad\u62d4\u4e86\u51fa\u6765\uff0c\u53ea\u89c1\u674e\u9038 \u5df2\u8df3\u4e0a\u4e86\u5c4b\u9876\uff0c\u5c4b\u9876\u4e0a\u6709\u51e0\u4e2a\u6b66\u58eb\u6b63\u622a\u7740\u4ed6\u6076\u6597\u3002\u9633\u592a\u534e\u63d0\u4e00\u53e3\u6c14\uff0c\u5f85\u8981\u7eb5\u4e0a\uff0c\u53cc\u811a\u5df2 \u662f\u4e0d\u542c\u4f7f\u5524\uff0c\u539f\u6765\u90a3\u652f\u5229\u7bad\u5df2\u4f24\u4e86\u4ed6\u7684\u7b4b\u9aa8\u3002 \u3000\u3000\u6697\u89d2\u91cc\u4e00\u4e2a\u519b\u5b98\u5954\u51fa\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u662f\u9633\u5927\u4eba\uff01\u53d7\u4e86\u4f24\u4e48\uff1f\u201d\u9633\u592a\u534e\u4e00\u770b\uff0c \u662f\u4e2a\u7a7f\u7740\u7fbd\u6797\u519b\uff08\u5373\u5fa1\u6797\u519b\uff0c\u5510\u79f0\u7fbd\u6797\u519b\u3002\uff09\u670d\u9970\u7684\u519b\u5b98\uff0c\u6025\u5fd9\u6325\u624b\u53eb\u9053\uff1a\u201c\u5feb\u53bb\u62ff\u8d3c\uff0c \u4e0d\u5fc5\u987e\u6211\uff0c\u6211\u4f24\u5f97\u4e0d\u91cd\uff01\u201d \u3000\u3000\u8fd9\u662f\u897f\u95e8\u6821\u5c09\u7ba1\u8f96\u7684\u5730\u533a\uff0c\u7fbd\u6797\u519b\u6bcf\u665a\u4e5f\u8981\u6d3e\u51fa\u51e0\u4e2a\u519b\u5b98\uff0c\u5230\u57ce\u5de1\u903b\uff0c\u8fd9\u65f6\u6070\u597d\u6709 \u4e00\u4e2a\u519b\u5b98\u5de1\u5230\u8fd9\uff0c\u9633\u592a\u534e\u77e5\u9053\u7fbd\u6797\u519b\u7684\u519b\u5b98\u4e2a\u4e2a\u90fd\u6709\u4e00\u8eab\u672c\u9886\uff0c\u897f\u95e8\u6821\u5c09\u5b87\u6587\u6e05\u4e5f\u662f\u4e00 \u628a\u597d\u624b\uff0c\u5fc3\u60f3\u8fd9\u56de\u674e\u9038\u603b\u9003\u4e0d\u4e86\u3002 \u3000\u3000\u90a3\u7fbd\u6797\u519b\u519b\u5b98\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8ba9\u5f00\uff0c\u5f85\u6211\u7528\u98de\u5200\u53d6\u4ed6\uff01\u201d\u4e00\u626c\u624b\uff0c\u4f46\u89c1\u4e24\u9053\u767d\u5149\u7535\u5c04 \u98de\u51fa\u3002 \u3000\u3000\u674e\u9038\u4e00\u542c\uff0c\u8fd9\u519b\u5b98\u7684\u58f0\u97f3\u597d\u719f\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u8fd9\u4e0d\u662f\u767d\u5143\u5316\u5417\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u54e9\u54e9 \u8fde\u58f0\uff0c\u90a3\u4e24\u53e3\u98de\u5200\u5df2\u662f\u8fde\u7fe9\u98de\u81f3\uff0c\u6070\u6070\u4ece\u674e\u9038\u7684\u989d\u89d2\u64e6\u8fc7\uff0c\u4ec5\u4ec5\u5dee\u4e86\u534a\u5206\u6ca1\u4f24\u7740\u4ed6\uff01 \u3000\u3000\u767d\u5143\u5316\u7684\u98de\u5200\u7edd\u6280\u9a70\u540d\u4eac\u90fd\uff0c\u56f4\u653b\u674e\u9038\u7684\u90a3\u51e0\u4e2a\u6b66\u571f\u542c\u5f97\u4ed6\u7684\u559d\u58f0\u65e9\u5df2\u95ea\u5f00\u3002\u674e\u9038 \u8d81\u8fd9\u4e2a\u7a7a\u6321\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u5411\u524d\u98de\u63a0\u6570\u4e08\uff0c\u767d\u5143\u5316\u559d\u9053\uff1a\u201c\u5978\u8d3c\u5f80\u54ea\u91cc\u9003\uff1f\u201d\u8d8a\u8fc7\u4e86\u897f\u95e8 \u6821\u5c09\u5b87\u6587\u6e05\uff0c\u98de\u6b65\u6025\u8ffd\uff0c\u674e\u9038\u548c\u767d\u5143\u5316\u7684\u8f7b\u529f\u90fd\u5728\u5b87\u6587\u6e05\u4e4b\u4e0a\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u4fbf\u628a\u5b87\u6587\u6e05 \u8fd9\u4e00\u4f19\u4eba\u629b\u5728\u80cc\u540e\uff0c\u9633\u592a\u534e\u7684\u811a\u53d7\u4e86\u4f24\uff0c\u5f53\u7136\u66f4\u8ffd\u4e0d\u4e0a\u4e86\u3002 \u3000\u3000\u4e00\u8ffd\u4e00\u9003\uff0c\u7247\u523b\u95f4\u53c8\u8fc7\u4e86\u4e24\u6761\u957f\u8857\uff0c\u767d\u5143\u5316\u559d\u9053\uff1a\u201c\u8d3c\u5b50\u770b\u5200\uff01\u201d\u54e9\u7684\u4e00\u58f0\uff0c\u53c8\u662f \u4e00\u53e3\u98de\u5200\u63b7\u51fa\uff0c\u8fd9\u4e00\u6b21\u504f\u5dee\u66f4\u5927\uff0c\u4ece\u674e\u9038\u5934\u9876\u63a0\u8fc7\uff0c\u674e\u9038\u4e3e\u5251\u4e00\u64a9\uff0c\u6ca1\u6709\u78b0\u7740\uff0c\u597d\u751f\u8be7 \u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u767d\u5143\u5316\u7684\u98de\u5200\u767e\u4e0d\u5931\u4e00\uff0c\u600e\u7684\u4eca\u665a\u5982\u6b64\u5931\u5e38\uff1f\u201d\u674e\u9038\u672c\u6765\u806a\u660e\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c \u968f\u5373\u9192\u609f\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u8fd9\u98de\u5200\u5b9a\u662f\u6307\u793a\u6211\u7684\u65b9\u5411\u7684\uff01\u201d\u767d\u5143\u5316\u6bcf\u9694\u4e00\u4e9b\u65f6\u5019\uff0c\u4fbf\u53d1\u51fa\u4e00 \u67c4\u98de\u5200\uff0c\u674e\u9038\u8ddf\u7740\u4ed6\u98de\u5200\u6240\u5c04\u7684\u65b9\u5411\u5954\u9003\uff0c\u679c\u7136\u9003\u51fa\u4e86\u5b98\u519b\u7684\u7f57\u7f51\uff0c\u767d\u5143\u5316\u7528\u98de\u5200\u6307\u5f15\uff0c \u4e0d\u4e45\u4fbf\u5c06\u674e\u9038\u201c\u8d76\u201d\u5230\u4e00\u4e2a\u50fb\u9759\u7684\u5730\u65b9\uff0c\u56db\u5468\u4e00\u671b\uff0c\u518d\u65e0\u522b\u4eba\uff0c\u767d\u5143\u5316\u505c\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a \u201c\u6bbf\u4e0b\uff0c\u4f60\u56de\u6765\u4e86\uff01\u6cf0\u5144\u6b63\u5728\u76fc\u671b\u4f60\u5462\u3002\u201d\u674e\u9038\u8c22\u4e86\u4ed6\u89e3\u6551\u4e4b\u6069\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u7a76\u7adf\u662f\u600e \u4e48\u4e00\u56de\u4e8b\uff1f\u9633\u592a\u534e\u7adf\u505a\u4e86\u4f60\u7684\u540c\u4f19\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u4ed6\u662f\u7a81\u53a5\u56fd\u5e08\u767e\u5fe7\u4e0a\u4eba\u7684\u5f1f\u5b50\u554a\uff01\u201d \u767d\u5143\u5316\u9053\uff1a\u201c\u6211\u4eec\u524d\u4e24\u65e5\u5df2\u67e5\u51fa\u4ed6\u7684\u6765\u5386\u4e86\uff0c\u4e0d\u8fc7\u8fd9\u8bdd\u8bf4\u6765\u8bdd\u957f\uff0c\u4f60\u73b0\u5728\u5e94\u8be5\u5148\u627e\u4e2a\u5730 \u65b9\u8eb2\u8d77\u6765\uff0c\u6211\u8fd8\u8981\u56de\u53bb\u6577\u884d\u4ed6\u4eec\u4e00\u4e0b\u3002\u201d \u3000\u3000\u674e\u9038\u542c\u4ed6\u63d0\u8d77\u4e86\u957f\u5b59\u6cf0\uff0c\u4f7f\u5373\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u5185\u5144\u7684\u4f4f\u5740\u5417\uff1f\u201d\u767d\u5143\u5316\u9053\uff1a\u201c\u5bf9 \u5566\uff0c\u4f60\u8eb2\u5230\u957f\u5b59\u6cf0\u90a3\u513f\uff0c\u6700\u597d\u4e0d\u8fc7\uff01\u4ed6\u7684\u5bb6\u5728\u897f\u798f\u9686\u8857\u90a3\u4e2a\u767d\u5854\u53f3\u8fb9\uff0c\u95e8\u524d\u6709\u4e00\u68f5\u5927\u6811 \u7684\u4fbf\u662f\u3002\u4eca\u665a\u6070\u597d\u4e0d\u662f\u4ed6\u5f53\u9053\uff0c\u4f60\u4eec\u4e24\u5a18\u8205\u53ef\u4ee5\u4f1a\u9762\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u719f\u6089\u957f\u5b89\u9053\u8def\uff0c\u4e0e\u767d\u5143\u5316\u522b\u8fc7\uff0c\u4fbf\u5373\u5c55\u5f00\u8f7b\u529f\u8eab\u6cd5\uff0c\u76f4\u5954\u897f\u798f\u9686\u8857\uff0c\u8dd1\u4e86\u4e00\u4f1a\uff0c \u8fdc\u8fdc\u542c\u5f97\u767d\u5143\u5316\u5728\u76f8\u53cd\u7684\u65b9\u5411\u5927\u53eb\u5978\u8d3c\uff0c\u9644\u8fd1\u51e0\u6761\u5927\u8857\u5de1\u903b\u7684\u5175\u58eb\uff0c\u90fd\u7ed9\u4ed6\u7684\u53eb\u58f0\u5438\u5f15 \u53bb\u4e86\u3002 \u3000\u3000\u674e\u9038\u4ece\u4ece\u5bb9\u5bb9\u7684\u7ed5\u8fc7\u51e0\u6761\u964b\u8857\u5c0f\u5df7\uff0c\u6765\u5230\u4e86\u897f\u798f\u9686\u8857\uff0c\u8fd9\u662f\u4e00\u6761\u9760\u5728\u5c71\u8fb9\u7684\u8857\u9053\u4e0a\uff0c \u5341\u5206\u5e7d\u9759\uff0c\u627e\u4e86\u4e00\u4f1a\uff0c\u679c\u7136\u53d1\u73b0\u6709\u5904\u4eba\u5bb6\uff0c\u95e8\u524d\u6709\u68f5\u5927\u6811\uff0c\u674e\u9038\u63c9\u8eab\u4e0a\u6811\uff0c\u5f80\u4e0b\u4e00\u770b\uff0c \u53ea\u89c1\u6709\u95f4\u623f\u5b50\uff0c\u706f\u706b\u672b\u7184\uff0c\u957f\u5b59\u6cf0\u7684\u5f71\u5b50\u5728\u7a97\u7eb1\u4e0a\u8d70\u6765\u8d70\u53bb\uff0c\u674e\u9038\u5fc3\u9053\uff1a\u201c\u8fd9\u4e48\u591c\u4e86\uff0c \u4ed6\u8fd8\u672a\u7761\uff0c\u770b\u8fd9\u6837\u5b50\uff0c\u4f3c\u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\u3002\u201d\u4ece\u6811\u4e0a\u8df3\u4e0b\u5899\u5934\uff0c\u4e00\u4e2a\u7ffb\u8eab\uff0c\u98de\u5165\u5185\u9662\uff0c\u8eab \u5f62\u521a\u521a\u843d\u5730\uff0c\u957f\u5b59\u6cf0\u4e5f\u5df2\u4ece\u7a97\u53e3\u8df3\u51fa\uff0c\u674e\u9038\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u6cf0\u5144\uff0c\u662f\u6211\uff01\u201d\u957f\u5b59\u6cf0\u63d2\u5200\u5f52 \u9798\uff0c\u7d27\u7d27\u63e1\u7740\u4ed6\u7684\u53cc\u624b\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4f60\u7ec8\u4e8e\u56de\u6765\u4e86\uff01\u6211\u77e5\u9053\u4f60\u4f1a\u56de\u6765\u7684\uff01\u201d\u4e24\u5a18\u8205\u52ab \u540e\u76f8\u9022\uff0c\u4e0d\u89c9\u90fd\u6ef4\u4e0b\u6cea\u6765\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56de\u3000\u7a83\u56fd\u795e\u5978\u4f0f\u7978\u6839 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e09\u5341\u56de\u3000\u7a83\u56fd\u795e\u5978\u4f0f\u7978\u6839 \u3000\u3000\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u524d\u65e5\u6211\u5728\u5bab\u4e2d\u5f53\u9053\uff0c\u89c1\u5230\u5a49\u513f\uff0c\u5a49\u513f\u8fd8\u95ee\u8d77\u4f60\u6765\u5462\u3002\u5509\uff0c\u5979\u8fd1\u6765\u989c\u5bb9 \u6194\u60b4\uff0c\u4e0d\u77e5\u662f\u6709\u4ec0\u4e48\u4e8b\u60c5\u95f7\u5728\u5fc3\u91cc\uff0c\u6211\u771f\u6015\u5979\u95f7\u51fa\u75c5\u6765\u3002\u201d\u674e\u9038\u5fc3\u60c5\u6005\u60d8\uff0c\u6697\u6697\u53f9\u4e86\u53e3 \u6c14\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u80fd\u591f\u7ed9\u6211\u8bbe\u6cd5\uff0c\u89c1\u4e00\u89c1\u5a49\u513f\u5417\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4e0b\u4e00\u6b21\u6211\u5165\u5bab\u5f53\u9053\uff0c\u4fbf \u548c\u5a49\u513f\u5546\u91cf\u3002\u201d\u674e\u9038\u9053\u201c\u6211\u4e0d\u60f3\u6b66\u5219\u5929\u77e5\u9053\uff0c\u53ea\u6015\u5b98\u4e2d\u8033\u76ee\u4f17\u591a\uff0c\u6cc4\u9732\u4e86\u98ce\u58f0\uff0c\u4f60\u80fd\u591f \u7ed9\u6211\u7792\u8fc7\u53bb\u5417\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u54b1\u4eec\u5230\u91cc\u9762\u53bb\u4ece\u957f\u8ba1\u8bae\u3002\u201d \u3000\u3000\u5165\u623f\u5750\u5b9a\uff0c\u706f\u5149\u4e0b\u7167\u89c1\u674e\u9038\u8863\u895f\u4e0a\u8840\u8ff9\u6591\u6591\uff0c\u957f\u5b59\u6cf0\u60ca\u9053\uff1a\u201c\u4f60\u521a\u521a\u548c\u4eba\u52a8\u8fc7\u624b\u6765\uff1f\u201d \u674e\u9038\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u9633\u592a\u534e\u90a3\u4e2a\u5978\u8d3c\u3002\u6211\u6b63\u8981\u95ee\u4f60\uff0c\u4ed6\u600e\u4e48\u505a\u8d77\u4ec0\u4e48\u4e1c\u95e8\u6821\u5c09\u7684\u5b98\u513f \u6765\u4e86\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u78b0\u4ed6\u7684\uff1f\u4ed6\u4eec\u77e5\u4e0d\u77e5\u9053\u4f60\u9003\u6765\u8fd9\u513f\uff1f\u201d\u674e\u9038\u5c06\u521a\u624d\u7684\u7ecf \u8fc7\u5411\u957f\u5b59\u6cf0\u8bf4\u4e86\u4e00\u904d\uff0c\u957f\u5b59\u6cf0\u77e5\u9053\u4e86\u662f\u767d\u5143\u5316\u6307\u5f15\u4ed6\u6765\u7684\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u597d\u50cf\u6709\u70b9\u6015\u4ed6\uff1f\u201d\u957f\u5b59\u6cf0\u7b11\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u662f\u9b4f\u738b\u6b66\u627f\u55e3\u7684\u4eba \u4e86\uff0c\u4ed6\u8fd9\u4e2a\u4e1c\u95e8\u6821\u5c09\u7684\u5b98\u804c\uff0c\u5c31\u662f\u6b66\u627f\u55e3\u4fdd\u4e3e\u4ed6\u7684\uff0c\u6295\u9f20\u5fcc\u5668\uff0c\u6211\u600e\u80fd\u4e0d\u6015\u4ed6\u4e09\u5206\uff1f\u201d \u674e\u9038\u6c14\u9053\uff1a\u201c\u6b66\u627f\u55e3\u771f\u662f\u80c6\u5927\u5984\u4e3a\uff0c\u5c45\u5fc3\u53f5\u6d4b\uff0c\u7a81\u53a5\u5927\u8d25\u4e4b\u540e\uff0c\u4ed6\u5c45\u7136\u8fd8\u6562\u6536\u5bb9\u53db\u8d3c\u3002 \u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u7a0b\u5efa\u7537\u60f3\u5fc5\u4e5f\u5df2\u6295\u9760\u4e86\u6b66\u627f\u55e3\u4e86\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211\u8fd8\u672a\u77e5\u7a0b\u5efa\u7537\u7684\u4e8b\uff0c \u54fc\uff0c\u9633\u592a\u534e\u62db\u6765\u4ed6\u7684\u72d0\u670b\u72d7\u515a\uff0c\u6295\u9760\u9b4f\u738b\uff0c\u83ab\u975e\u5f53\u771f\u662f\u60f3\u9020\u53cd\uff1f\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u95ee\u9053\uff1a \u201c\u542c\u8bf4\u6b66\u627f\u55e3\u6b66\u4e09\u601d\u79c1\u901a\u7a81\u53a5\uff0c\u8fd9\u4e8b\u60c5\u4f60\u77e5\u9053\u5f97\u5f88\u6e05\u695a\uff0c\u90a3\u6b21\u7a81\u53a5\u738b\u5ef7\u7684\u6b66\u58eb\u5927\u4f1a\uff0c\u6211 \u6ca1\u6709\u53c2\u52a0\uff0c\u662f\u4e8b\u540e\u542c\u5f97\u590f\u4faf\u575a\u8001\u524d\u8f88\u8bf4\u7684\uff0c\u542c\u8bf4\u6b66\u627f\u55e3\u4e5f\u6d3e\u4e86\u4e24\u4e2a\u4f7f\u8005\u6765\uff0c\u5f53\u573a\u7ed9\u590f\u4faf \u575a\u7684\u91d1\u9488\u5c04\u6b7b\u4e86\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6709\u8fd9\u56de\u4e8b\u513f\u3002\u6b66\u627f\u55e3\u901a\u654c\u7684\u4e8b\u60c5\uff0c\u7384\u971c\u77e5\u9053\u5f97 \u6700\u6e05\u695a\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u611f\u89e6\u9887\u591a\uff0c\u671b\u4e86\u674e\u9038\u4e00\u773c\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u7384\u971c\u73b0\u5728\u4e0d\u5728\u957f\u5b89\u3002\u201d\u674e\u9038\u95ee\u9053\uff1a \u201c\u5979\u53bb\u4e86\u54ea\u513f\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5979\u6bd4\u6211\u5148\u56de\u5230\u957f\u5b89\uff0c\u542c\u8bf4\u53ea\u5728\u5bab\u4e2d\u4f4f\u4e86\u4e24\u5929\uff0c\u53c8\u8d76\u5230\u524d \u65b9\u519b\u4e2d\u53bb\u770b\u79cb\u5927\u4eba\u4e86\u3002\u6b66\u627f\u55e3\u901a\u654c\u7684\u4e8b\u60c5\uff0c\u4f60\u613f\u4e0d\u613f\u610f\u5c06\u4f60\u6240\u77e5\u9053\u7684\u5199\u4e00\u4efd\u51fa\u6765\uff0c\u8ba9\u6211 \u4ea4\u7ed9\u5f20\u76f8\u56fd\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u5f20\u67ec\u4e4b\u6562\u52a8\u6b66\u627f\u55e3\u5417\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5f20\u76f8\u56fd\u79c9\u516c\u6267\u653f\uff0c\u5f88 \u5f97\u5929\u540e\u4fe1\u8d56\u3002\u6628\u5929\u5f20\u76f8\u56fd\u8fd8\u53eb\u6211\u548c\u767d\u5143\u5316\u53bb\uff0c\u8be6\u7ec6\u67e5\u95ee\u6b66\u627f\u55e3\u6d3e\u5bc6\u4f7f\u5230\u7a81\u53a5\u53bb\u7684\u4e8b\u60c5\uff0c \u53ef\u60dc\u6211\u77e5\u9053\u5f97\u4e0d\u6e05\u695a\u3002\u201d\u674e\u9038\u5947\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u600e\u4e48\u5012\u5148\u77e5\u9053\u4e86\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u8fd8\u6709\u4e00 \u6837\u5947\u602a\u7684\u5462\uff0c\u9633\u592a\u534e\u6295\u5165\u9b4f\u738b\u5e9c\u4e2d\uff0c\u88ab\u6d3e\u5145\u4e1c\u95e8\u6821\u5c09\u7684\u4e8b\uff0c\u4e5f\u662f\u4ed6\u544a\u8bc9\u6211\u7684\u3002\u4f60\u77e5\u9053\u6211 \u672a\u66fe\u53c2\u52a0\u7a81\u53a5\u7684\u6b66\u571f\u5927\u4f1a\uff0c\u6839\u672c\u5c31\u4e0d\u8ba4\u8bc6\u9633\u592a\u534e\u3002\u5e78\u4e8f\u5f20\u76f8\u56fd\u544a\u8bc9\u6211\uff0c\u6211\u624d\u77e5\u9053\u4ed6\u7684\u5e95 \u7ec6\uff0c\u73b0\u5728\u603b\u7b97\u548c\u4ed6\u7ed3\u8bc6\u4e86\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4f60\u4e3a\u6b64\u7279\u522b\u53bb\u7ed3\u8bc6\u9633\u592a\u534e\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211 \u8fd9\u662f\u5949\u4e86\u5f20\u76f8\u56fd\u4e4b\u547d\uff0c\u5f20\u76f8\u56fd\u4e0d\u4f46\u8981\u6211\u7ed3\u8bc6\u9633\u592a\u534e\uff0c\u8fd8\u8981\u6211\u548c\u6b66\u627f\u55e3\u7ed3\u7eb3\u5462\uff01\u201d \u3000\u3000\u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u5f20\u67ec\u4e4b\u7528\u5fc3\u826f\u82e6\uff0c\u5982\u6b64\u770b\u6765\uff0c\u4ed6\u65e9\u5df2\u6709\u4e86\u5e03\u7f6e\u4e86\uff01\u201d \u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4f60\u771f\u806a\u660e\uff0c\u4e00\u4e0b\u5b50\u5c31\u731c\u5230\u4e86\u76f8\u56fd\u7684\u7528\u610f\uff0c\u6b66\u627f\u55e3\u6b66\u4e09\u601d\u8fd1\u6765\u5e7f\u62db\u95e8\u5ba2\uff0c\u5bf9 \u7fbd\u6797\u519b\u7684\u519b\u5b98\u548c\u7981\u536b\u519b\u7684\u7edf\u9886\u5c24\u5176\u62c9\u62e2\uff0c\u5f20\u76f8\u56fd\u4fbf\u53eb\u6211\u5c06\u8ba1\u5c31\u8ba1\uff0c\u4f9d\u9644\u4e8e\u4ed6\uff0c\u63a2\u542c\u4ed6\u7684 \u52a8\u9759\uff0c\u8bf4\u5f97\u76f4\u767d\u4e00\u4e9b\uff0c\u90a3\u5c31\u662f\u53eb\u6211\u53bb\u5367\u5e95\u4e86\u3002\u201d\u674e\u9038\u7b11\u9053\uff1a\u201c\u4e8c\u6b66\u867d\u7136\u6743\u52bf\u6ed4\u5929\uff0c\u8bba\u5230 \u8001\u8c0b\u6df1\u7b97\uff0c\u7edd\u5bf9\u4e0d\u662f\u5f20\u67ec\u4e4b\u7684\u5bf9\u624b\uff0c\u4f55\u51b5\u8fd8\u6709\u4e00\u4f4d\u6781\u5f97\u4eba\u5fc3\u7684\u72c4\u4ec1\u6770\u5e2e\u5f20\u67ec\u4e4b\u7b56\u5212\uff0c\u770b \u6765\u4e8c\u6b66\u88ab\u6d88\u706d\uff0c\u53ea\u662f\u8fdf\u65e9\u7684\u4e8b\uff0c\u6211\u53ef\u4ee5\u65e0\u5fe7\u4e86\u3002\u201d\u5f53\u4e0b\u5c31\u5c06\u4ed6\u6240\u77e5\u9053\u7684\uff0c\u5173\u4e8e\u6b66\u627f\u55e3\u79c1 \u901a\u7a81\u53a5\u7684\u4e8b\u60c5\uff0c\u5199\u4e86\u4e00\u4efd\u4ea4\u7ed9\u957f\u5b59\u6cf0\uff0c\u8ba9\u4ed6\u62ff\u53bb\u7ed9\u5f20\u67ec\u4e4b\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u53c8\u662f\u957f\u5b59\u6cf0\u5165\u5bab\u8f6e\u9053\u7684\u65e5\u5b50\uff0c\u674e\u9038\u5c06\u4ed6\u4ece\u7a0b\u8fbe\u82cf\u8eab\u4e0a\u641c\u51fa\u6765\u7684\u9ed1\u864e\u5e2e\u7684 \u540d\u518c\u548c\u7b26\u4ee4\u4e5f\u7ed9\u4e86\u957f\u5b59\u6cf0\uff0c\u8ba9\u4ed6\u5411\u7981\u536b\u519b\u90fd\u5c09\u674e\u660e\u4e4b\u4ea4\u5dee\uff0c\u4f46\u5374\u53ee\u5631\u4ed6\u4e0d\u8981\u8bf4\u51fa\u662f\u81ea\u5df1 \u7f34\u83b7\u7684\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u53bb\u540e\uff0c\u674e\u9038\u5fc3\u4e8b\u5982\u6f6e\uff0c\u5750\u5367\u4e0d\u5b81\uff0c\u5230\u4e86\u7b2c\u4e8c\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u957f\u5b59\u6cf0\u5174\u5306\u5306\u7684\u8d76 \u4e86\u56de\u6765\uff0c\u89c1\u5230\u674e\u9038\uff0c\u7b2c\u4e00\u53e5\u8bdd\u4fbf\u8bf4\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u7ed9\u4f60\u5b89\u6392\u59a5\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u6837\u5b89\u6392\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u89c1\u4e86\u4e0a\u5b98\u5a49\u513f\uff0c\u4e0b\u6b21\u6211\u8fdb\u5bab\u8f6e \u9053\u7684\u65f6\u5019\uff0c\u4f60\u6362\u4e0a\u7981\u536b\u519b\u7684\u670d\u9970\uff0c\u6211\u5e26\u4f60\u5165\u5bab\uff0c\u4f60\u53ef\u4ee5\u5728\u534e\u6e05\u9601\u91cc\u548c\u5a49\u513f\u5355\u72ec\u89c1\u9762\uff0c\u5230 \u65f6\u5979\u81ea\u4f1a\u628a\u5bab\u5973\u9063\u5f00\u3002\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u5979\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5979\u6ca1\u6709\u5176\u4ed6\u8bf4\u8bdd\u4e86\uff0c\u53ea\u662f\u53eb\u4f60\u4f9d\u671f\u8d74 \u7ea6\u3002\u54e6\uff0c\u5bf9\u4e86\uff0c\u5979\u6709\u4e00\u9996\u65b0\u8bd7\uff0c\u58a8\u6c88\u672a\u5e72\uff0c\u4fbf\u7ed9\u6211\u62ff\u6765\u4e86\u3002\u5979\u8bf4\uff0c\u4f60\u62ff\u53bb\u4e5f\u597d\uff0c\u5c31\u7ed9\u9038 \u54e5\u770b\u770b\u5427\u3002\u4ed6\u4f1a\u61c2\u5f97\u6211\u7684\u610f\u601d\u7684\u3002\u201d \u3000\u3000\u674e\u9038\u5c55\u5f00\u4e8c\u8bd7\u7b3a\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7684\u662f\u9996\u4e94\u8a00\u7edd\u53e5\uff0c\u8bd7\u9053\uff1a\u201c\u767d\u9a79\u6b4c\u5df2\u901d\uff01\u4f0a\u4eba\u6c34 \u4e00\u65b9\uff1b\u6742\u63c9\u82b3\u4e0e\u6cfd\uff0c\u76f8\u89c1\u5fcd\u76f8\u5fd8\uff1f\u201d\u7b2c\u4e00\u53e5\u7528\u7684\u662f\u8bd7\u7ecf\u300a\u767d\u9a79\u300b\u7bc7\u7684\u5178\u6545\uff0c\u8bf4\u662f\u5979\u60f3\u628a \u8fdc\u65b9\u7684\u5ba2\u4eba\u7559\u4f4f\uff0c\u6240\u4ee5\u628a\u4ed6\u7684\u767d\u9a6c\u62f4\u8d77\u6765\uff0c\u53ef\u662f\u7ec8\u4e8e\u8fd8\u662f\u7559\u4e0d\u4f4f\u7684\uff0c\u56e0\u6b64\u8bf4\u662f\u201c\u767d\u9a79\u6b4c \u5df2\u901d\u201d\u3002\u7b2c\u4e8c\u53e5\u7528\u7684\u662f\u8bd7\u7ecf\u300a\u517c\u8511\u300b\u7bc7\u7684\u5178\u6545\uff0c\u201c\u8363\u5357\u82cd\u82cd\uff0c\u767d\u9732\u4e3a\u971c\uff0c\u6240\u8c13\u4f0a\u4eba\uff0c\u5728 \u6c34\u4e00\u65b9\u3002\u6eaf\u6d1e\u4ece\u4e4b\uff0c\u9053\u963b\u4e14\u957f\u3002\u6eaf\u6e38\u4ece\u4e4b\uff0c\u5b9b\u5728\u6c34\u4e2d\u592e\u3002\u201d\u90a3\u610f\u601d\u662f\u8bf4\u5979\u6240\u4ef0\u6155\u3001\u5979\u6240 \u8981\u8ffd\u6c42\u7684\u4eba\u513f\uff0c\u53ef\u671b\u800c\u4e0d\u53ef\u5373\u3002\u7b2c\u4e09\u53e5\u662f\u7528\u695a\u8f9e\u300a\u601d\u7f8e\u4eba\u300b\u7bc7\u7684\u5178\u6545\uff0c\u610f\u601d\u662f\u8bf4\u7f8e\u4eba\u53d7 \u4e86\u59d4\u5c48\uff0c\u9999\u82b1\u6df7\u5728\u6d4a\u8349\u4e2d\u95f4\u3002\u7b2c\u56db\u53e5\u662f\u8bf4\uff0c\u5728\u8fd9\u6837\u60c5\u52bf\u4e4b\u4e0b\uff0c\u76f8\u89c1\u4e4b\u540e\u4e5f\u8fd8\u662f\u4e92\u76f8\u5fd8\u6389 \u7684\u597d\uff0c\u4f46\u53c8\u600e\u5fcd\u76f8\u5fd8\u5462\uff1f\u867d\u7136\u8fd9\u9996\u8bd7\u4ec5\u4ec5\u662f\u5be5\u5be5\u4e8c\u5341\u4e2a\u5b57\uff0c\u5374\u5305\u542b\u4e86\u6781\u590d\u6742\u7684\u610f\u601d\uff0c\u54c0 \u6028\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002\u674e\u9038\u5fc3\u5f26\u98a4\u6296\uff0c\u201c\u5a49\u513f\u5979\u679c\u7136\u8fd8\u5728\u82e6\u82e6\u7684\u601d\u5ff5\u6211\uff01\u201d\u4f46\u4ed6\u8d77\u4e86\u6781\u5927 \u7684\u6000\u7591\uff0c\u4ee5\u4e09\u56db\u4e24\u53e5\u7684\u8bd7\u610f\u6765\u770b\uff0c\u5a49\u513f\u4f3c\u4e4e\u662f\u53d7\u7740\u5f88\u5927\u7684\u59d4\u5c48\uff0c\u4f3c\u4e4e\u662f\u8981\u5ac1\u7ed9\u4e00\u4e2a\u5979\u6240 \u4e0d\u613f\u610f\u5ac1\u7684\u4eba\uff0c\u8fd9\u4ef6\u4e8b\u53ef\u5c31\u5947\u602a\u4e86\uff01 \u3000\u3000\u674e\u9038\u6df1\u77e5\u4e0a\u5b98\u5a49\u513f\u7684\u6027\u683c\u5b9e\u662f\u5916\u67d4\u5185\u521a\uff0c\u53ea\u6709\u5979\u8ba4\u4e3a\u662f\u5bf9\u7684\uff0c\u5979\u624d\u80af\u53bb\u505a\uff0c\u6240\u4ee5\u5979 \u5f53\u5e74\u6562\u5b64\u8eab\u53bb\u884c\u523a\u6b66\u5219\u5929\uff0c\u4f46\u4e00\u5230\u670d\u4e86\u6b66\u5219\u5929\u4e4b\u540e\uff0c\u5373\u4f7f\u662f\u5979\u5fc3\u7231\u7684\u4eba\uff0c\u4e5f\u4e0d\u80fd\u6539\u53d8\u5979 \u7684\u4e3b\u610f\u4e86\u3002\u4ee5\u5979\u8fd9\u7965\u7684\u6027\u683c\uff0c\u82e5\u8bf4\u5979\u7518\u613f\u5c06\u7ec8\u8eab\u5927\u4e8b\u4efb\u4eba\u6446\u5e03\uff0c\u90a3\u662f\u4e0d\u53ef\u60f3\u50cf\u4e4b\u4e8b\uff01 \u3000\u3000\u957f\u5b59\u6cf0\u95ee\u9053\uff1a\u201c\u5a49\u513f\u8fd9\u9996\u8bd7\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4ecd\u662f\u4ee5\u524d\u4f60\u5bf9\u6211\u8bf4 \u8fc7\u7684\u90a3\u4e9b\u8bdd\uff0c\u5979\u4f3c\u662f\u6709\u4e00\u4ef6\u4e8b\u60c5\u8981\u548c\u6211\u5546\u91cf\u3002\u201d\u674e\u9038\u6015\u957f\u5b59\u6cf0\u96be\u8fc7\uff0c\u56e0\u6b64\u4e0d\u80af\u628a\u8bd7\u4e2d\u771f \u610f\u5411\u4ed6\u89e3\u91ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u957f\u5b59\u6cf0\u75f4\u5fc3\u6697\u604b\uff0c\u53ef\u60dc\u5a49\u513f\u559c\u6b22\u7684\u4e0d\u662f\u4ed6\u3002\u5509\uff0c\u90a3\u4e2a\u5979\u6240\u4e0d \u613f\u610f\u5ac1\u7684\u4eba\u662f\u8c01\u5462\uff1f\u53c8\u662f\u8c01\u903c\u5979\u7684\u5462\uff1f\u96be\u9053\u662f\u6b66\u5219\u5929\uff1f\u4ee5\u5979\u7684\u6027\u683c\uff0c\u5979\u6240\u4e0d\u613f\u610f\u505a\u7684\u4e8b\uff0c \u5373\u7b97\u662f\u6b66\u5219\u5929\u903c\u5979\uff0c\u5979\u4e5f\u4e0d\u4f1a\u4f9d\u4ece\u7684\uff01\u4f55\u51b5\u6b66\u5219\u5929\u6b63\u5ba0\u7231\u5979\uff0c\u8981\u5229\u7528\u5979\u7684\u624d\u80fd\u5e2e\u5979\u529e\u4e8b\uff0c \u60f3\u6765\u4e5f\u4e0d\u4f1a\u903c\u5979\u3002\u201d\u60f3\u5230\u5a49\u513f\u7edd\u9876\u806a\u660e\uff0c\u53e4\u4eca\u5c11\u6709\uff0c\u82e5\u7136\u5ac1\u4e86\u4e00\u4e2a\u5979\u6240\u4e0d\u559c\u6b22\u7684\u5e73\u5eb8\u4e4b \u8f88\uff0c\u90a3\u5c82\u4e0d\u662f\u4e00\u6735\u9c9c\u82b1\u63d2\u5728\u725b\u7caa\u4e0a\u4e86\u3002\u674e\u9038\u867d\u7136\u65e9\u5df2\u65ad\u4e86\u548c\u5a49\u513f\u7ed3\u5408\u4e4b\u5ff5\uff0c\u4f46\u60f3\u5230\u6b64\u5904\uff0c \u4ecd\u662f\u5341\u5206\u96be\u8fc7\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u89c1\u4ed6\u4f4e\u5934\u9ed8\u60f3\uff0c\u4ee5\u4e3a\u4ed6\u662f\u5728\u731c\u6d4b\u5a49\u513f\u7684\u5fc3\u4e8b\uff0c\u4fbf\u9053\uff1a\u201c\u597d\u5728\u5979\u613f\u628a\u5fc3\u4e8b\u5411\u4f60 \u503e\u8bc9\uff0c\u8fd9\u4e2a\u95f7\u846b\u82a6\u8fc7\u51e0\u5929\u5c31\u53ef\u4ee5\u6253\u7834\u4e86\uff0c\u6211\u5374\u95f7\u4e86\u4e00\u5e74\u591a\u5462\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u6cf0\u5144\uff0c\u6211\u770b \u4f60\u4e5f\u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u662f\u4e3a\u4e86\u5a49\u513f\u5417\uff1f\u201d\u957f\u5b59\u6cf0\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u76fc\u4e86\u8fd9\u8bb8\u4e45\uff0c\u76fc\u5230\u4f60\u6765\u4e86\uff0c \u6015\u53ea\u6015\u6211\u6ca1\u6709\u673a\u4f1a\u77e5\u9053\u5a49\u513f\u7684\u5fc3\u4e8b\u4e86\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u5979\u544a\u8bc9\u6211\uff0c\u6211\u4e00\u5b9a\u544a\u8bc9\u4f60\u3002\u201d\u957f\u5b59 \u6cf0\u9053\uff1a\u201c\u4f46\u53ea\u6015\u6211\u4e0b\u6b21\u4e0d\u80fd\u966a\u4f60\u8fdb\u5bab\u4e86\uff0c\u4e0d\u8fc7\uff0c\u6211\u7eb5\u4f7f\u4e0d\u80fd\u966a\u4f60\uff0c\u6211\u4e5f\u4f1a\u53eb\u767d\u5143\u5316\u66ff\u4ee3 \u6211\u7684\u3002\u201d\u674e\u9038\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4e0d\u662f\u8bf4\u65e9\u5df2\u548c\u5a49\u513f\u7ea6\u5b9a\u4e86\u5417\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u82e6\u7b11\u9053\uff1a\u201c\u662f\u7ea6\u5b9a\u4e86\u3002\u4e0d\u8fc7\uff0c\u540e\u6765\u53c8\u6709\u53d8\u5316\uff0c\u6211\u6b63\u8981\u548c\u4f60\u5546\u91cf\u3002\u201d\u674e\u9038\u9053\uff1a \u201c\u4ec0\u4e48\u53d8\u5316\uff1f\u201d\u957f\u5b59\u6cf0\u82e6\u7b11\u9053\uff1a\u201c\u6211\u89c1\u4e86\u5a49\u513f\u4e4b\u540e\uff0c\u4e0d\u4e45\u674e\u90fd\u5c09\u53c8\u53ec\u89c1\u6211\uff0c\u4ea4\u7ed9\u6211\u4e00\u4ef6 \u5dee\u4e8b\u3002\u201d\u674e\u9038\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5dee\u4e8b\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u660e\u5929\u6b66\u627f\u55e3\u6709\u4e2a\u5bb4\u4f1a\uff0c\u5bb4\u8bf7\u548c\u4ed6 \u6709\u4ea4\u60c5\u7684\u519b\u5b98\uff0c\u6211\u4e5f\u62e8\u5230\u4e86\u8bf7\u5e16\u3002\u674e\u90fd\u5c09\u8981\u6211\u5728\u660e\u5929\u7684\u5e2d\u4e0a\uff0c\u5c06\u9633\u592a\u534e\u548c\u7a0b\u5efa\u7537\u8fd9\u4e24\u4e2a \u5978\u8d3c\u62ff\u4e0b\u3002\u8fd9\u4e8b\u662f\u5f20\u76f8\u56fd\u548c\u4ed6\u51b3\u5b9a\u7684\uff0c\u5f20\u76f8\u56fd\u8bf4\u662f\u65f6\u673a\u5df2\u5230\uff0c\u5728\u5e2d\u4e0a\u64d2\u597d\uff0c\u4e5f\u597d\u53eb\u90a3\u4e9b \u519b\u5b98\u8bc6\u7834\u6b66\u627f\u55e3\u7684\u9634\u8c0b\uff0c\u7eb5\u4f7f\u8fd9\u4e0d\u80fd\u6273\u5012\u6b66\u627f\u55e3\u6b66\u4e09\u601d\uff0c\u4e5f\u603b\u662f\u5bf9\u54b1\u4eec\u5927\u5927\u6709\u5229\u3002\u201d\u674e \u9038\u9053\uff1a\u201c\u8fd9\u4e3b\u610f\u4e0d\u9519\uff01\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6b66\u627f\u55e3\u5e9c\u4e2d\u6b66\u58eb\u5982\u4e91\uff0c\u82e5\u662f\u4ed6\u5f53\u573a\u53d8\u8138\uff0c\u5e87\u8ba9\u90a3 \u4e24\u4e2a\u5978\u8d3c\uff0c\u867d\u7136\u6211\u6301\u6709\u674e\u90fd\u5c09\u7684\u547d\u4ee4\uff0c\u53e6\u5916\u4e5f\u6709\u51e0\u4f4d\u7fbd\u6797\u519b\u548c\u7981\u536b\u519b\u7684\u519b\u5b98\u542c\u6211\u8c03\u9063\uff0c \u534f\u540c\u6355\u8d3c\uff0c\u4f46\u7ec8\u662f\u654c\u5f3a\u6211\u5f31\uff0c\u6b66\u627f\u55e3\u4e00\u53d8\u9762\uff0c\u52a8\u8d77\u6b66\u6765\uff0c\u4e8b\u60c5\u5c31\u96be\u529e\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u60f3\u4e86\u4e00\u60f3\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u5e2e\u4e86\u6211\u7684\u5927\u5fd9\uff0c\u6211\u4e5f\u5e94\u8be5\u5e2e\u4f60\u4e00\u4e2a\u5fd9\uff0c\u660e\u5929\u6211\u548c\u4f60 \u4e00\u9053\u53bb\u4fbf\u662f\uff01\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6015\u9732\u51fa\u8eab\u4efd\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u8fd8\u85cf\u6709\u590f\u4faf\u575a\u7684\u6613 \u5bb9\u4e39\uff0c\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff01\u5373\u7b97\u5192\u4e00\u6b21\u9669\uff0c\u4e5f\u662f\u8981\u7684\u3002\u4f60\u62ff\u4e00\u5957\u6b66\u58eb\u7684\u670d\u9970\u6765\uff0c\u8ba9\u6211\u6539\u88c5\u6613 \u5bb9\uff0c\u8bd5\u4e00\u8bd5\u770b\uff01\u201d \u3000\u3000\u674e\u9038\u6253\u626e\u505c\u5f53\uff0c\u518d\u52c3\u4e0a\u4e86\u4e24\u64ae\u5c0f\u80e1\u5b50\uff0c\u63fd\u955c\u4e00\u7167\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6cf0\u5144\uff0c\u4f60\u53ef\u8fd8\u8ba4\u5f97 \u5c0f\u5f1f\u4e48\uff1f\u201d\u957f\u5b59\u6cf0\u4e00\u770b\uff0c\u53ea\u89c1\u674e\u9038\u989d\u89d2\u5fae\u6709\u76b1\u7eb9\uff0c\u5bb9\u8c8c\u8d28\u6734\u82cd\u8001\uff0c\u4e0e\u4ed6\u5e73\u7d2f\u98ce\u6d41\u4fca\u96c5\u7684 \u6a21\u6837\u5927\u4e0d\u76f8\u540c\uff0c\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u590f\u4faf\u575a\u7684\u6613\u5bb9\u4e39\u679c\u7136\u795e\u5999\uff0c\u82e5\u662f\u5728\u522b\u5904\u76f8\u9022\uff0c\u6211\u4e5f\u4e0d\u6562\u76f8 \u8ba4\u3002\u53ea\u662f\u773c\u795e\u8fd8\u672a\u80fd\u6536\u655b\uff0c\u900f\u9732\u51fa\u4e00\u80a1\u82f1\u6c14\uff0c\u773c\u795e\u662f\u65e0\u6cd5\u53d8\u6613\u7684\uff0c\u597d\u5728\u4f60\u88c5\u626e\u7684\u662f\u7981\u536b \u519b\u519b\u5b98\u8eab\u4efd\uff0c\u4e5f\u5e94\u8be5\u6709\u70b9\u5a01\u4eea\u3002\u201d\u674e\u9038\u7b11\u9053\uff1a\u201c\u6211\u4e0a\u6b21\u5728\u7a81\u53a5\u53c2\u52a0\u8fc7\u4ed6\u4eec\u7684\u6b66\u58eb\u5927\u4f1a\uff0c \u66fe\u7792\u8fc7\u4e86\u9633\u592a\u534e\u4e00\u6b21\uff0c\u4f46\u613f\u8fd9\u4e00\u6b21\u4e5f\u7792\u5f97\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u957f\u5b59\u6cf0\u518d\u4ed4\u7ec6\u7684\u770b\u4ed6\u4e00\u904d\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u8fd8\u6709\u4e00\u4e2a\u6781\u5927\u7684\u7834\u7efd\uff0c\u9700\u8981\u8bbe\u6cd5\u5f25 \u7f1d\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u5b9d\u5251\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u5927\u5185\u4e4b\u7269\uff01\u5728\u7a81\u53a5\u53ef \u4ee5\u7792\u8fc7\uff0c\u6b66\u627f\u55e3\u5e9c\u4e2d\u7684\u6b66\u58eb\u5c82\u6709\u4e0d\u77e5\uff1f\u201d\u674e\u9038\u8e0c\u8e87\u9053\uff1a\u201c\u82e5\u662f\u4e0d\u5e26\u8fd9\u628a\u5b9d\u5251\uff0c\u53ea\u6015\u6ca1\u90a3 \u4e48\u5bb9\u6613\u5236\u670d\u9633\u592a\u534e\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6362\u8fc7\u4e00\u628a\u5251\u9798\u5982\u4f55\uff1f\u201d\u674e\u9038\u539f\u6765\u90a3\u628a\u5251\u9798\u9576\u91d1\u523b\u7389\uff0c \u73cd\u8d35\u5f02\u5e38\uff0c\u957f\u5b59\u6cf0\u7ed9\u4ed6\u6311\u9009\u4e86\u4e00\u628a\u6837\u5f0f\u53e4\u8001\u8d28\u6734\u7684\u5957\u4e0a\uff0c\u5251\u67c4\u518d\u6f06\u4e0a\u4e86\u4e00\u5c42\uff0c\u8bf4\u9053\uff1a \u201c\u884c\u5566\uff0c\u82e5\u662f\u4f60\u4e0d\u62d4\u51fa\u6765\uff0c\u522b\u4eba\u5c31\u770b\u4e0d\u51fa\u662f\u628a\u5b9d\u5251\u4e86\u3002\u201d \u3000\u3000\u674e\u9038\u7b11\u9053\uff1a\u201c\u6cf0\u5144\uff0c\u4f60\u6bd4\u4ee5\u524d\u7cbe\u7ec6\u591a\u4e86\uff01\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u6211\u5728\u5bab\u4e2d\u6267\u5f79\uff0c\u5df2\u6709\u4e86\u4e5d \u4e2a\u5e74\u5934\uff0c\u591a\u5c11\u53d7\u4e86\u4e00\u70b9\u5929\u540e\u7684\u85b0\u9676\u3002\u201d\u674e\u9038\u9ed8\u7136\u65e0\u8bed\uff0c\u5fc3\u60f3\u63a5\u8fd1\u6b66\u5219\u5929\u7684\u4eba\uff0c\u7adf\u662f\u6beb\u65e0 \u4f8b\u5916\u7684\uff0c\u6bcf\u4e00\u4e2a\u90fd\u53d7\u5230\u5979\u7684\u5f71\u54cd\uff0c\u5c31\u53ea\u662f\u4ece\u8fd9\u4e9b\u5c0f\u4e8b\u6765\u770b\uff0c\u6b66\u5219\u5929\u4e5f\u771f\u662f\u4e00\u4e2a\u4e0d\u5e73\u51e1\u7684 \u5973\u4eba\uff01 \u3000\u3000\u5f85\u5230\u4e86\u5bb4\u4f1a\u4e4b\u671f\uff0c\u957f\u5b59\u6cf0\u643a\u4e86\u674e\u9038\u4f9d\u65f6\u524d\u5f80\uff0c\u8d74\u4f1a\u7684\u519b\u5b98\uff0c\u6709\u56db\u4e94\u4e2a\u90fd\u662f\u957f\u5b59\u6cf0\u9884 \u5148\u7ea6\u597d\u7684\u4eba\uff0c\u767d\u5143\u5316\u4e5f\u5728\u5176\u4e2d\uff0c\u8fd9\u4e00\u73ed\u4eba\u7b97\u51c6\u65f6\u95f4\uff0c\u540c\u65f6\u5230\u8fbe\uff0c\u597d\u8ba9\u674e\u9038\u6df7\u5728\u4e2d\u95f4\uff0c\u4e0d \u8fc7\uff0c\u9664\u4e86\u767d\u65e0\u5316\u77e5\u9053\u4ed6\u7684\u5e95\u7ec6\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\u5374\u672a\u77e5\u9053\uff0c\u53ea\u77e5\u9053\u662f\u957f\u5b59\u6cf0\u9080\u6765\u7684\u4e00\u4e2a\u9ad8 \u624b\uff0c\u5192\u5145\u662f\u7981\u536b\u519b\u519b\u5b98\uff0c\u8bf7\u4ed6\u4eec\u5e2e\u540c\u906e\u63a9\u7684\u3002 \u3000\u3000\u6b66\u627f\u55e3\u7684\u738b\u5e9c\u5802\u7687\u5bcc\u4e3d\uff0c\u8c6a\u5962\u6c14\u8c61\uff0c\u80dc\u4f3c\u7687\u5bab\uff0c\u674e\u9038\u6697\u6697\u55df\u53f9\u3002\u8fdb\u4e86\u51e0\u91cd\u95e8\u6237\uff0c\u5230 \u4e86\u5bb4\u4f1a\u7684\u5927\u5385\uff0c\u5ffd\u89c1\u9633\u592a\u6bd5\u7ad9\u5728\u9636\u4e0a\u8fce\u5bbe\uff0c\u674e\u9038\u5fc3\u9053\uff1a\u201c\u5728\u7a81\u53a5\u7684\u6b66\u58eb\u5927\u4f1a\u4e2d\u662f\u4ed6\u62db\u5f85 \u6211\u7684\uff0c\u73b0\u5728\u53c8\u662f\u4ed6\u6765\u4e86\u3002\u201d\u6697\u6697\u76d8\u7b97\u5e94\u4ed8\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u5728\u7981\u536b\u519b\u4e2d\u5df2\u505a\u5230\u4e86\u4e09\u54c1\u9a81\u9a91\u90fd\u5c09\u7684\u5b98\u804c\uff0c\u5728\u5f53\u65e5\u8d74\u4f1a\u7684\u519b\u5b98\u4e4b\u4e2d\uff0c\u9664\u4e86\u4e09 \u56db\u4e2a\u4eba\u4e4b\u5916\uff0c\u5c31\u4ee5\u4ed6\u7684\u519b\u9636\u6700\u9ad8\u4e86\uff0c\u9633\u592a\u534e\u9996\u5148\u548c\u4ed6\u89c1\u793c\uff0c\u674e\u9038\u6df7\u5728\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u5411\u4ed6\u70b9 \u4e86\u70b9\u5934\uff0c\u4fbf\u60f3\u6df7\u8fc7\u3002\u9633\u592a\u534e\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u674e\u9038\u4f3c\u4e4e\u6709\u70b9\u76f8\u8bc6\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u4eba\u8fd8 \u672a\u89c1\u8fc7\uff1f\u201d\u957f\u5b59\u6cf0\u6ca1\u6cd5\uff0c\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u65b0\u6765\u7684\u7981\u536b\u519b\u5f20\u961f\u957f\u3002\u8fd9\u4f4d\u662f\u4e1c\u95e8\u6821\u5c09\u9633 \u5927\u4eba\uff0c\u9b4f\u738b\u7237\u8ddf\u524d\u7684\u7ea2\u4eba\u3002\u4f60\u4eec\u4e24\u4f4d\u591a\u591a\u4eb2\u8fd1\u4eb2\u8fd1\uff01\u201d\u9633\u592a\u534e\u4f38\u51fa\u624b\u6765\uff0c\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c \u5e78\u4f1a\uff01\u5e78\u4f1a\uff01\u201d \u3000\u3000\u674e\u9038\u77e5\u9053\u4ed6\u662f\u60f3\u8bd5\u8bd5\u81ea\u5df1\u7684\u529f\u592b\uff0c\u4e0a\u6b21\u5728\u7a81\u53a5\u6b66\u571f\u5927\u4f1a\u4e2d\u9633\u592a\u534e\u4e5f\u66fe\u8fd9\u6837\u8bd5\u8fc7\u4ed6\u7684\uff0c \u5f53\u65f6\u4ed6\u8fd0\u7528\u6b63\u5b97\u7684\u5185\u529f\u62b5\u5fa1\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u770b\u51fa\u6765\u5386\uff0c\u8fd9\u6b21\u674e\u9038\u80f8\u6709\u6210\u7af9\uff0c\u795e\u8272\u4e0d\u53d8\uff0c\u6beb\u4e0d \u8fdf\u7591\u7684\u5c31\u4f38\u624b\u4e0e\u4ed6\u76f8\u63e1\u3002 \u3000\u3000\u9633\u592a\u534e\u7ec3\u7684\u662f\u4e00\u79cd\u90aa\u6d3e\u5185\u529f\uff0c\u53cc\u638c\u4e00\u63e1\uff0c\u53ea\u542c\u5f97\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u674e\u9038\u6025\u5fd9\u62bd\u51fa\u624b\u6765\uff0c \u8dc4\u8dc4\u8e09\u8e09\u7684\u5012\u9000\u51e0\u6b65\uff0c\u53cc\u638c\u8fde\u6413\uff0c\u51d1\u8fd1\u53e3\u8fb9\u5475\u6c14\u3002\u9633\u592a\u534e\u4e5f\u6643\u4e86\u4e24\u6643\uff0c\u4ed6\u4eec\u811a\u4e0b\u6240\u8e0f\u7684 \u9752\u7816\uff0c\u5df2\u788e\u4e86\u4e24\u5757\u3002 \u3000\u3000\u539f\u6765\u5728\u53cc\u638c\u76f8\u4ea4\u7684\u65f6\u5019\uff0c\u9633\u592a\u534e\u7384\u529f\u4e00\u8fd0\uff0c\u624b\u638c\u767b\u65f6\u53d8\u5f97\u4f3c\u70bd\u70ed\u7684\u706b\u70ad\u4e00\u822c\uff0c\u674e\u9038 \u82e5\u4ee5\u7cbe\u7eaf\u7684\u5185\u529f\u62b5\u5fa1\uff0c\u81ea\u53ef\u65e0\u59a8\uff0c\u4f46\u4ed6\u6709\u8fc7\u4e0a\u4e00\u6b21\u7684\u7ecf\u9a8c\uff0c\u4e0d\u613f\u88ab\u9633\u592a\u534e\u8bc6\u7834\uff0c\u8fd9\u4e00\u6b21 \u7eaf\u4ee5\u521a\u731b\u7684\u638c\u529b\u53cd\u51fb\uff0c\u4e1d\u6beb\u4e0d\u9732\u51fa\u81ea\u5df1\u66fe\u7ec3\u8fc7\u5185\u529f\uff0c\u8fd9\u6837\u4e00\u6765\u3002\u674e\u9038\u7684\u624b\u638c\u767b\u65f6\u4f3c\u88ab\u70d9 \u8fc7\u4e00\u822c\uff0c\u8d77\u4e86\u4e24\u9053\u7ea2\u5370\u3002\u800c\u9633\u592a\u534e\u5403\u4ed6\u7684\u638c\u529b\u4e00\u9707\uff0c\u4e5f\u81ea\u62ff\u6869\u4e0d\u7a33\u3002 \u3000\u3000\u674e\u9038\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u9633\u5927\u4eba\u597d\u6b66\u529f\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d\u4ed6\u54d1\u7740\u55d3\u5b50\u8bf4\u8bdd\uff0c\u88c5\u51fa\u5589\u5499\u7126\u6e34 \u7684\u6a21\u6837\uff0c\u9633\u592a\u534e\u679c\u7136\u542c\u4e0d\u51fa\u6765\u3002\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u539f\u6765\u7ec3\u7684\u662f\u5916\u5bb6\u529f\u592b\uff0c\u529f\u592b\u867d\u7136\u4e0d\u5f31\uff0c\u5230 \u5e95\u662f\u4e8c\u6d41\u89d2\u8272\uff0c\u505a\u4e00\u4e2a\u7981\u536b\u519b\u7684\u5c0f\u961f\u957f\uff0c\u4e5f\u7b97\u5f97\u662f\u9002\u5f53\u7684\u4e86\u3002\u201d\u5f53\u4e0b\u4e5f\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u9601 \u4e0b\u7684\u91d1\u521a\u638c\u529b\uff0c\u7ec3\u5230\u8fd9\u6837\u7684\u5730\u6b65\uff0c\u4e5f\u5f88\u4e0d\u9519\u4e86\u3002\u8bf7\u8fdb\u91cc\u9762\u53bb\u5750\uff01\u201d \u3000\u3000\u5802\u4e2d\u5bbe\u5ba2\u5982\u4e91\uff0c\u5341\u4e4b\u4e03\u516b\u90fd\u662f\u519b\u5b98\uff0c\u957f\u5b59\u6cf0\u4e00\u770b\uff0c\u7fbd\u6797\u519b\u4e2d\u597d\u51e0\u4e2a\u9ad8\u7ea7\u7684\u519b\u5b98\u4e5f\u90fd \u5728\u5ea7\uff0c\u5fc3\u60f3\uff1a\u201c\u88ab\u6b66\u627f\u55e3\u62c9\u62e2\u7684\u4eba\uff0c\u5012\u8fd8\u771f\u4e0d\u5c11\u5462\uff01\u201d\u957f\u5b59\u6cf0\u4e0e\u51e0\u4e2a\u9ad8\u7ea7\u7684\u519b\u5b98\u540c\u5e2d\uff0c \u674e\u9038\u4e0e\u767d\u5143\u5316\u53e6\u5750\u4e00\u5e2d\uff0c\u540c\u5e2d\u7684\u6709\u4e00\u534a\u662f\u957f\u5b59\u6cf0\u6240\u7ea6\u6765\u7684\u4eba\uff0c\u5176\u4ed6\u7684\u4e00\u534a\u867d\u7136\u90fd\u4e0d\u8ba4\u8bc6 \u674e\u9038\uff0c\u4f46\u6709\u767d\u5143\u5316\u4ed6\u4eec\u66ff\u674e\u9038\u63a9\u9970\uff0c\u90a3\u4e9b\u4eba\u679c\u7136\u628a\u674e\u9038\u5f53\u4f5c\u65b0\u5230\u4efb\u7684\u7981\u536b\u519b\u519b\u5b98\uff0c\u6ca1\u6709 \u8c01\u8d77\u7591\u5fc3\u3002 \u3000\u3000\u5750\u5b9a\u4e4b\u540e\uff0c\u6b66\u627f\u55e3\u6b65\u51fa\u4e2d\u5802\uff0c\u8eab\u8fb9\u6709\u4e00\u4e2a\u9053\u58eb\uff0c\u6234\u7740\u707f\u70c2\u7684\u91d1\u51a0\uff0c\u8fd8\u6709\u4e00\u4e2a\u8001\u5112\u751f \u88c5\u675f\u7684\u4eba\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u628a\u6298\u6247\uff0c\u6709\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u51a0\u9053\u4eba\u548c\u725b\u5148\u751f\u4e5f\u6765\u5566\uff01\u201d\u674e\u9038\u867d \u7136\u4e0d\u77e5\u9053\u4e8c\u4eba\u7684\u6765\u5386\uff0c\u89c1\u4f17\u4eba\u8fd9\u6837\u6ce8\u610f\u4ed6\u4eec\uff0c\u6599\u60f3\u662f\u4e2a\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\u3002\u6b66\u627f\u55e3\u4e00\u5230\uff0c \u4f17\u519b\u5b98\u7eb7\u7eb7\u8d77\u7acb\uff0c\u6b66\u627f\u55e3\u6ee1\u8138\u5806\u6b22\uff0c\u4e3e\u676f\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u5404\u4f4d\u5149\u4e34\uff0c\u8bf7\u4e0d\u5fc5\u62d8\u675f\uff0c\u5c3d\u60c5\u6b22 \u996e\u3002\u6211\u5148\u5411\u5404\u4f4d\u656c\u9152\u4e09\u676f\u3002\u201d\u4f17\u4eba\u7eb7\u7eb7\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u201d\u6b66\u627f\u55e3\u7b11\u9053\uff1a\u201c\u671d \u5ef7\u6700\u8fd1\u6253\u4e86\u4e2a\u5927\u80dc\u4ed7\uff0c\u7a81\u53a5\u5927\u6c57\u5df2\u9063\u4f7f\u6c42\u548c\uff0c\u8fd9\u7b2c\u4e00\u676f\u9152\u662f\u795d\u798f\u4e4b\u9152\uff0c\u5404\u4f4d\u5c82\u53ef\u63a8\u8f9e\uff1f\u201d \u5e72\u4e86\u7b2c\u4e00\u676f\uff0c\u6b66\u627f\u55e3\u53c8\u9053\uff1a\u201c\u7b2c\u4e8c\u676f\u9152\u795d\u5929\u540e\u965b\u4e0b\u4e07\u5bff\u65e0\u7586\uff01\u201d\u4f17\u519b\u5b98\u6b22\u547c\u4e07\u5c81\uff0c\u628a\u7b2c \u4e8c\u676f\u5e72\u4e86\uff0c\u674e\u9038\u5fc3\u60f3\uff1a\u201c\u6b66\u627f\u55e3\u79c1\u901a\u7a81\u53a5\uff0c\u9634\u8c0b\u7be1\u4f4d\uff0c\u96be\u4e3a\u4ed6\u8fd8\u6562\u8bf4\u51fa\u8fd9\u4e24\u53e5\u8bdd\u6765\uff0c\u8138 \u4e0a\u534a\u70b9\u4e0d\u7ea2\uff0c\u5f53\u771f\u662f\u8001\u5978\u5de8\u733e\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u770b\u8fd9\u60c5\u5f62\uff0c\u519b\u5b98\u4eec\u5bf9\u6b66\u5219\u5929\u786e\u662f\u4e00\u81f4\u6548\u5fe0\uff0c \u602a\u4e0d\u5f97\u6b66\u627f\u55e3\uff0c\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002\u201d \u3000\u3000\u6b66\u627f\u55e3\u4e3e\u8d77\u7b2c\u4e09\u9152\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u676f\u4e48\u2026\u2026\u201d\u7565\u4f5c\u6c89\u541f\uff0c\u4f3c\u662f\u6b63\u5728\u60f3\u4e00\u4e2a\u529d\u9152\u7684\u85c9\u53e3\uff0c \u9633\u592a\u534e\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u9b4f\u738b\u8f85\u4f50\u5929\u540e\u965b\u4e0b\uff0c\u529f\u5728\u56fd\u5bb6\uff0c\u8fd9\u4e00\u676f\u4e48\uff0c\u5c31\u795d\u9b4f\u738b\u5343\u79cb\u4e07\u5c81\uff0c\u4e8b \u4e8b\u5982\u610f\uff0c\u90fd\u5e72\u4e86\uff01\u201d\u4f17\u4eba\u8f70\u7136\u79f0\u662f\uff0c\u7eb7\u7eb7\u5e72\u9152\uff0c\u674e\u9038\u6697\u6697\u9a82\u58f0\uff0c\u201c\u65e0\u803b\uff01\u201d\u4ee5\u8896\u63a9\u676f\uff0c \u6084\u6084\u628a\u9152\u6cfc\u4e86\u3002\u6b66\u627f\u55e3\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u738b\u4f55\u5fb7\u4f55\u80fd\uff0c\u5168\u9760\u5404\u4f4d\u6276\u6301\uff0c\u4eca\u540e\u8981\u4ef0\u4ed7 \u5404\u4f4d\u7684\u8fd8\u591a\u5462\uff01\u201d\u9b4f\u738b\u5e9c\u7684\u603b\u7ba1\u5d14\u4e5d\u9704\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4f1a\uff0c\u9ad8\u4eba\u4e91\u96c6\uff0c\u5c24\u5176\u5f97\u5230\u91d1 \u51a0\u9053\u957f\u4e0e\u725b\u5148\u751f\u524d\u6765\uff0c\u66f4\u662f\u589e\u5149\u4e0d\u5c11\u3002\u673a\u4f1a\u96be\u9022\uff0c\u6211\u60f3\u8bf7\u4ed6\u4eec\u4e8c\u4f4d\u663e\u9732\u51e0\u624b\u529f\u592b\uff0c\u8ba9\u6211 \u4eec\u77bb\u4ef0\u77bb\u4ef0\uff01\u201d \u3000\u3000\u91d1\u51a0\u9053\u4eba\u77e5\u9053\u6b66\u627f\u55e3\u7684\u5fc3\u610f\uff0c\u662f\u60f3\u8981\u4ed6\u4eec\u663e\u9732\u7edd\u6280\uff0c\u6151\u670d\u7fa4\u96c4\uff0c\u6559\u8fd9\u4e9b\u519b\u5b98\u5c06\u6765\u4e0d \u6562\u80cc\u53db\uff0c\u4fbf\u9996\u5148\u7ad9\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u606d\u9022\u76db\u4f1a\uff0c\u7406\u8be5\u51d1\u51d1\u70ed\u95f9\uff0c\u8d2b\u9053\u6709\u4e00\u624b\u4e0d\u6210\u6c14 \u5019\u7684\u529f\u592b\uff0c\u804a\u535a\u738b\u7237\u548c\u5404\u4f4d\u4e00\u7b11\u3002\u201d \u3000\u3000\u8bf4\u7f62\u4fbf\u8bf7\u738b\u5e9c\u4e2d\u7684\u6267\u5f79\u5c06\u5404\u5904\u7a97\u6237\u90fd\u5173\u8d77\u6765\uff0c\u53ea\u89c1\u4ed6\u7ad9\u5728\u5f53\u4e2d\uff0c\u5ffd\u5730\u957f\u5578\u4e00\u58f0\uff0c\u5728 \u5ea7\u8bf8\u4eba\u90fd\u89c9\u5f97\u5fbd\u98ce\u98d2\u7136\uff0c\u63a0\u9762\u800c\u8fc7\uff0c\u968f\u5373\u542c\u5f97\u7a97\u6237\u683c\u683c\u4f5c\u54cd\uff0c\u5468\u56f4\u4e00\u770b\uff0c\u6240\u6709\u7684\u7a97\u6237\u90fd \u5df2\u6253\u5f00\u4e86\u3002\u4f17\u4eba\u5927\u60ca\u5931\u8272\uff0c\u8bd5\u60f3\u5728\u8fd9\u4e2a\u53ef\u4ee5\u5bb9\u7eb3\u5343\u4eba\u7684\u6bbf\u5802\u4e2d\uff0c\u8db3\u6709\u51e0\u5341\u4e2a\u7a97\u6237\uff0c\u4ed6\u53ea \u662f\u4e00\u58f0\u957f\u5578\uff0c\u4fbf\u4ee4\u7a97\u6237\u5168\u90e8\u6253\u5f00\uff0c\u8fd9\u6c14\u529f\u7684\u5389\u5bb3\uff0c\u5f53\u771f\u662f\u532a\u5937\u6240\u601d\uff01 \u3000\u3000\u674e\u9038\u4e5f\u81ea\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u8d3c\u9053\u7684\u6c14\u529f\uff0c\u867d\u7136\u672a\u5230\u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\uff0c\u4f46\u5df2\u5728\u6211 \u4e4b\u4e0a\uff0c\u770b\u6765\u4eca\u65e5\u64d2\u5978\u4e4b\u4e8b\uff0c\u9669\u963b\u5b9a\u591a\uff01\u201d \u3000\u3000\u91d1\u51a0\u9053\u4eba\u7b11\u9053\uff1a\u201c\u725b\u5144\uff0c\u8f6e\u5230\u4f60\u4e86\uff01\u201d\u725b\u5148\u751f\u8f7b\u6447\u6298\u6247\uff0c\u8d70\u51fa\u573a\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u53ef \u6ca1\u6709\u4f60\u8fd9\u6837\u9ad8\u660e\u7684\u529f\u592b\uff0c\u53ea\u597d\u6765\u4e00\u4e2a\u72d7\u5c3e\u7eed\u8c82\uff0c\u5c06\u4f60\u5f04\u7184\u706d\u7684\u70db\u706b\u91cd\u71c3\u4e0a\u5427\u3002\u201d\u539f\u6765\u5728 \u5404\u5904\u7a97\u6237\u7684\u65c1\u8fb9\uff0c\u90fd\u71c3\u70b9\u6709\u5de8\u70db\uff0c\u5149\u8000\u534e\u5802\uff0c\u91d1\u51a0\u9053\u4eba\u5728\u7528\u6c14\u529f\u63a8\u5f00\u7a97\u6237\u7684\u540c\u65f6\uff0c\u4e5f\u540c \u65f6\u5f04\u7184\u4e86\u70db\u706b\u3002\u674e\u9038\u4e4b\u6240\u4ee5\u8ba4\u4e3a\u91d1\u51a0\u9053\u4eba\u7684\u6c14\u529f\u8fd8\u672a\u5230\u7089\u706b\u7eaf\u9752\uff0c\u5c31\u662f\u4e3a\u6b64\u3002 \u3000\u3000\u53ea\u89c1\u725b\u5e03\u8863\u957f\u8896\u4e00\u6325\uff0c\u6298\u6247\u4e00\u6447\uff0c\u4ed6\u8896\u4e2d\u98de\u51fa\u6570\u5341\u70b9\u6d41\u661f\uff0c\u90a3\u662f\u4ed6\u72ec\u6709\u7684\u7779\u5668\u6d41\u661f \u706b\u7130\u5f39\uff0c\u4f53\u79ef\u6781\u5c0f\uff0c\u88ab\u4ed6\u6298\u6247\u62e8\u4e86\u51e0\u62e8\uff0c\u56db\u6563\u98de\u5f00\uff0c\u6bcf\u4e00\u9897\u706b\u7130\u5f39\u521a\u597d\u843d\u5728\u4e00\u652f\u5de8\u70db\u4e0a\uff0c \u970e\u65f6\u95f4\u5c31\u628a\u51e0\u5341\u652f\u5de8\u70db\u90fd\u70b9\u71c3\u4e86\uff01\u8fd9\u79cd\u6697\u5668\u7684\u529f\u592b\u4f17\u4eba\u54ea\u91cc\u89c1\u8fc7\uff1f\u767b\u65f6\u53c8\u662f\u66b4\u96f7\u822c\u7684\u4e00 \u7247\u5f69\u58f0\u3002 \u3000\u3000\u5f85\u5230\u559d\u5f69\u58f0\u9759\u4e0b\uff0c\u6b66\u627f\u55e3\u53c8\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u5148\u751f\u7684\u6b66\u529f\u771f\u662f\u51fa\u795e\u5165\u5316\uff0c\u4f69\u670d\uff0c\u4f69 \u670d\uff01\u9633\u6821\u5c09\uff0c\u4f60\u4e5f\u662f\u65b0\u6765\u7684\u4eba\uff0c\u4e0a\u4efb\u8fd8\u672a\u6709\u51e0\u5929\uff0c\u548c\u8bb8\u591a\u670b\u53cb\u90fd\u672a\u89c1\u8fc7\uff0c\u54b1\u4eec\u4eca\u65e5\u662f\u4ee5 \u6b66\u4f1a\u53cb\uff0c\u4f60\u4e5f\u9732\u4e00\u624b\u5427\uff01\u201d \u3000\u3000\u9633\u592a\u534e\u77e5\u9053\u6b66\u627f\u55e3\u5b58\u5fc3\u5c06\u4ed6\u6367\u8d77\u6765\uff0c\u5fc3\u4e2d\u5f97\u610f\u4e4b\u6781\u3002\u5374\u6545\u4f5c\u8c26\u865a\u7684\u8bf4\u9053\uff1a\u201c\u73e0\u7389\u5728 \u524d\uff0c\u5351\u804c\u7109\u6562\u732e\u62d9\uff1f\u4e0d\u8fc7\u738b\u7237\u6709\u547d\uff0c\u6211\u4e5f\u4e0d\u6562\u63a8\u8f9e\u3002\u5f85\u6211\u60f3\u60f3\uff0c\u7528\u4ec0\u4e48\u6765\u5411\u5404\u4f4d\u8bf7\u6559\u5462\uff1f\u201d \u60f3\u4e86\u4e00\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u559d\u4e86\u51e0\u676f\u9152\u6709\u70b9\u70ed\u4e86\uff01\u8bf7\u6055\u6211\u65e0\u793c\uff0c\u8131\u8fd9\u4ef6\u8863\u670d\u5427\u3002\u201d\u5ffd\u5730\u5c06\u8131\u4e0b \u7684\u8863\u670d\u63c9\u6210\u4e00\u56e2\uff0c\u5408\u5728\u638c\u4e2d\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u53cc\u638c\u6025\u6413\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4fbf\u6709\u706b\u82b1\u4ece\u4ed6\u7684\u6307\u7f1d\u4e2d\u98de\u51fa\uff0c\u91d1\u51a0\u9053\u4eba\u70b9\u5934\u5fae\u7b11\u9053\uff1a \u201c\u597d\u529f\u592b\uff0c\u597d\u529f\u592b\uff01\u201d\u9633\u592a\u534e\u53cc\u638c\u4e00\u5f20\uff0c\u4f46\u89c1\u9ed1\u70df\u6eda\u6eda\uff0c\u706b\u5149\u8000\u773c\uff0c\u90a3\u56e2\u8863\u670d\u5df2\u53d8\u6210\u4e86 \u4e00\u4e2a\u706b\u7403\uff0c\u8fc5\u5373\u70e7\u6389\uff0c\u9633\u592a\u534e\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u732e\u62d9\u4e86\uff0c\u8bf7\u5404\u4f4d\u4e0d\u8981\u89c1\u7b11\uff01\u201d \u3000\u3000\u9633\u592a\u534e\u6240\u70ab\u9732\u7684\u8fd9\u624b\u529f\u592b\uff0c\u867d\u7136\u8fd8\u53ca\u4e0d\u4e0a\u91d1\u51a0\u9053\u4eba\u548c\u725b\u5148\u751f\u90a3\u6837\u7684\u795e\u5947\uff0c\u5374\u4e5f\u975e\u540c \u5c0f\u53ef\uff0c\u987b\u77e5\u94bb\u6728\u53d6\u706b\u4e5f\u5f97\u8d39\u597d\u5927\u4e00\u4f1a\u529f\u592b\uff0c\u800c\u4ed6\u4ee5\u53cc\u638c\u6469\u64e6\u6240\u53d1\u751f\u7684\u70ed\u529b\uff0c\u7247\u523b\u4e4b\u95f4\u4fbf \u80fd\u71c3\u70e7\u8863\u7269\uff01\u8fd9\u79cd\u90aa\u95e8\u7684\u638c\u529b\u4e5f\u7b97\u5f97\u662f\u76f8\u5f53\u602a\u5f02\u4e86\u3002 \u3000\u3000\u4f17\u519b\u5b98\u77e5\u9053\u4ed6\u662f\u6b66\u627f\u55e3\u7684\u4eba\uff0c\u517c\u4e4b\u4ed6\u8fd9\u624b\u529f\u592b\u786e\u5b9e\u4e5f\u8fd8\u4e0d\u9519\uff0c\u4fbf\u7eb7\u7eb7\u7ed9\u4ed6\u9f13\u638c\u559d\u5f69\u3002 \u725b\u5e03\u8863\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9633\u5927\u4eba\uff0c\u4f60\u7684\u529f\u592b\u6f02\u4eae\u5f97\u5f88\uff0c\u5c31\u53ef\u60dc\u6bc1\u4e86\u8fd9\u4ef6\u8863\u88f3\u4e86\u3002\u201d \u3000\u3000\u6b66\u627f\u55e3\u5fae\u7b11\u9053\uff1a\u201c\u50ac\u603b\u7ba1\u53d6\u4e00\u4ef6\u9526\u888d\u6765\u8d50\u6821\u5c09\u3002\u201d\u9633\u592a\u534e\u62ab\u4e0a\u9526\u888d\uff0c\u5f97\u610f\u6d0b\u6d0b\u7684\u8d70 \u8fc7\u53bb\u5411\u6b66\u627f\u55e3\u9053\u8c22\u3002 \u3000\u3000\u6b66\u627f\u55e3\u53c8\u9053\uff1a\u201c\u4eca\u65e5\u8fd8\u6709\u51e0\u4f4d\u65b0\u6765\u7684\u670b\u53cb\uff0c\u8bf7\u5927\u5bb6\u4e0d\u8981\u5ba2\u6c14\uff0c\u5c06\u5404\u81ea\u62ff\u624b\u7684\u672c\u4e8b\u6296 \u51fa\u6765\u77a7\u77a7\u3002\u201d \u3000\u3000\u9633\u592a\u534e\u7684\u76ee\u5149\u6ce8\u89c6\u5230\u674e\u9038\u8eab\u4e0a\uff0c\u738b\u5e9c\u603b\u7ba1\u5d14\u4e5d\u9704\u4fbf\u8d70\u5230\u674e\u9038\u5e2d\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f \u5f20\u5927\u4eba\u5417\uff1f\u4ee5\u524d\u8fd8\u6ca1\u6709\u89c1\u8fc7\u3002\u201d\u767d\u5143\u5316\u4ee3\u4ed6\u7b54\u9053\uff1a\u201c\u8fd9\u4f4d\u5f20\u5144\u662f\u6700\u8fd1\u624d\u5230\u7981\u536b\u519b\u7684\uff0c\u4ed6 \u662f\u957f\u5b59\u90fd\u5c09\u591a\u5e74\u7684\u597d\u53cb\uff0c\u76ee\u524d\u867d\u5c48\u5c45\u7981\u536b\u519b\u961f\u957f\u4e4b\u804c\uff0c\u672c\u9886\u59d4\u5b9e\u4e0d\u9519\u3002\u201d\u5d14\u4e5d\u9704\u9053\uff1a \u201c\u662f\u957f\u5b59\u90fd\u5c09\u4fdd\u8350\u7684\u4eba\uff0c\u5f53\u7136\u4e0d\u4f1a\u9519\u4e86\uff0c\u4fbf\u8bf7\u5f20\u5927\u4eba\u7565\u663e\u529f\u592b\uff0c\u8ba9\u6211\u4eec\u5f00\u5f00\u773c\u754c\uff01\u201d \u3000\u3000\u674e\u9038\u7ad9\u4e86\u8d77\u6765\uff0c\u54d1\u7740\u55d3\u5b50\u8bf4\u9053\uff1a\u201c\u767d\u5927\u4eba\u7ed9\u6211\u8138\u4e0a\u8d34\u91d1\uff0c\u5176\u5b9e\u6211\u53ea\u4f1a\u51e0\u624b\u7c97\u7b28\u7684\u62f3 \u811a\u3002\u201d\u5d14\u4e5d\u9704\u9053\uff1a\u201c\u5f20\u5927\u4eba\u4e0d\u5fc5\u5ba2\u6c14\u4e86\uff0c\u738b\u7237\u4e5f\u7b49\u7740\u77a7\u4f60\u7684\u529f\u592b\u5462\u3002\u201d\u674e\u9038\u82e6\u7b11\u9053\uff1a \u201c\u90a3\u4e48\uff0c\u6211\u8fd9\u4e2a\u4e11\u5ab3\u5987\u53ef\u8981\u6015\u770b\u89c1\u5bb6\u59d1\u4e86\u3002\u201d \u3000\u3000\u5ea7\u4e2d\u8fd8\u6709\u597d\u51e0\u4f4d\u7981\u536b\u519b\u7684\u519b\u5b98\uff0c\u90fd\u4e0d\u8ba4\u8bc6\u674e\u9038\uff0c\u53ea\u5f53\u4ed6\u5f53\u771f\u662f\u957f\u5b59\u6cf0\u6700\u8fd1\u5f15\u6765\u7684\u65b0 \u4eba\uff0c\u8fd8\u672a\u66fe\u6b63\u5f0f\u4e0e\u540c\u50da\u4f1a\u9762\u7684\u3002\u5927\u5bb6\u90fd\u6709\u70b9\u597d\u5947\uff0c\u7eb7\u7eb7\u5c06\u773c\u5149\u6ce8\u89c6\u4ed6\uff0c\u770b\u4ed6\u6709\u4ec0\u4e48\u672c\u9886\u3002 \u7a0b\u5efa\u7537\u6295\u8fdb\u9b4f\u738b\u5e9c\uff0c\u8fd8\u672b\u5c01\u6709\u5b98\u804c\uff0c\u6df7\u5728\u6267\u5f79\u7684\u738b\u5e9c\u6b66\u58eb\u7fa4\u4e2d\uff0c\u8fd9\u4eba\u5fc3\u601d\u7ec6\u5bc6\uff0c\u89c1\u8fd9\u4e2a \u519b\u5b98\u7684\u795e\u8272\u6709\u5f02\uff0c\u4fbf\u4e5f\u76ee\u4e0d\u8f6c\u775b\u7684\u76ef\u7740\u674e\u9038\u3002 \u3000\u3000\u674e\u9038\u7ad9\u5728\u573a\u5fc3\uff0c\u4f5c\u51fa\u4e00\u4ed8\u82e6\u53e3\u82e6\u8138\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u54ce\u5440\uff0c\u6211\u8fd9\u51e0\u624b\u4e09\u811a\u732b\u7684\u529f\u592b \u600e\u4e48\u62ff\u5f97\u51fa\u6765\u5462\uff1f\u6211\u5f53\u771f\u662f\u53ea\u4f1a\u51e0\u624b\u62f3\u811a\uff0c\u50cf\u4ed6\u4eec\u51e0\u4f4d\u5355\u72ec\u5c31\u53ef\u4ee5\u8868\u73b0\u7684\u795e\u901a\uff01\u6211\u53ef\u62ff \u4e0d\u51fa\u6765\uff01\u201d\u5d14\u4e5d\u9704\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5c31\u8bf7\u4e00\u4e2a\u4eba\u51fa\u6765\u548c\u4f60\u5408\u6f14\u4e00\u5957\u62f3\u811a\u5427\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211 \u521a\u624d\u770b\u5230\u9633\u5927\u4eba\u90a3\u624b\u529f\u592b\uff0c\u4ef0\u6155\u5f97\u5f88\uff0c\u4f46\u662f\u8fd8\u6709\u70b9\u6000\u7591\uff0c\u4e0d\u77e5\u5728\u5bf9\u638c\u4e4b\u9645\uff0c\u5b83\u80fd\u4e0d\u80fd\u70e7 \u7126\u522b\u4eba\u7684\u76ae\u8089\uff0c\u6211\u60f3\u5411\u9633\u5927\u4eba\u9886\u6559\u63a8\u638c\u7684\u529f\u592b\uff0c\u4e0d\u77e5\u9053\u9633\u5927\u4eba\u80af\u4e0d\u80af\u8d50\u6559\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c \u4f17\u519b\u5b98\u5927\u4e3a\u60ca\u6115\uff0c\u542c\u674e\u9038\u8d77\u521d\u7684\u8bf4\u8bdd\uff0c\u5f88\u662f\u5ba2\u6c14\uff0c\u60f3\u4e0d\u5230\u4ed6\u4f1a\u7a81\u5982\u5176\u6765\u7684\u5411\u9633\u592a\u534e\u6307\u540d \u6311\u6218\uff01 \u3000\u3000\u9633\u592a\u534e\u5148\u662f\u4e00\u6014\uff0c\u7ee7\u800c\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4f1a\uff0c\u672c\u6765\u5c31\u662f\u4ee5\u6b66\u4f1a\u53cb\uff0c\u5f7c\u6b64\u5207\u78cb\uff0c\u6709\u4f55\u4e0d \u53ef\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u6211\u521a\u624d\u4ee4\u4ed6\u5403\u4e86\u4e00\u70b9\u5c0f\u4e8f\uff0c\u4ed6\u7684\u540c\u50da\u4e5f\u90fd\u770b\u4e86\u51fa\u6765\uff0c\u4ed6\u65b0\u4efb\u519b\u5b98\uff0c\u9762\u5b50 \u4e0a\u5f53\u7136\u8fc7\u4e0d\u53bb\u4e86\u3002\u4ed6\u7684\u91d1\u521a\u638c\u529b\u672a\u5f97\u65bd\u53d1\uff0c\u60f3\u5fc5\u5fc3\u4e2d\u8fd8\u4e0d\u670d\u6c14\uff0c\u8981\u6765\u627e\u56de\u9762\u5b50\u3002\u597d\uff0c\u4ed6 \u65e2\u7136\u4e0d\u77e5\u8fdb\u9000\uff0c\u6211\u5c31\u6b63\u597d\u62ff\u4ed6\u626c\u5a01\u7acb\u4e07\uff01\u201d\u9633\u592a\u534e\u5728\u674e\u9038\u5165\u95e8\u4e4b\u65f6\uff0c\u5c31\u8bd5\u8fc7\u4ed6\u7684\u672c\u9886\uff0c \u81ea\u5fd6\u6709\u7edd\u5bf9\u7684\u628a\u63e1\u80dc\u4ed6\uff0c\u5f53\u4e0b\u5ba2\u6c14\u8bdd\u4e5f\u4e0d\u591a\u8bf4\u4e00\u53e5\uff0c\u4fbf\u5373\u6b23\u7136\u79bb\u5ea7\u3002 \u3000\u3000\u674e\u9038\u58f0\u660e\u662f\u8981\u6bd4\u8bd5\u63a8\u638c\u7684\u529f\u592b\uff0c\u8fd9\u6b63\u5408\u9633\u592a\u534e\u7684\u5fc3\u610f\uff0c\u53cc\u638c\u4e00\u7c98\uff0c\u7acb\u5373\u9ed8\u8fd0\u7384\u529f\uff0c \u65bd\u5c55\u4ed6\u7684\u90aa\u95e8\u638c\u529b\uff0c\u638c\u5fc3\u53d1\u51fa\u817e\u817e\u70ed\u6c14\uff0c\u674e\u9038\u4f3c\u4e4e\u662f\u7981\u53d7\u4e0d\u4e86\uff0c\u989d\u89d2\u6c81\u51fa\u9ec4\u8c46\u822c\u7684\u6c57\u73e0\uff0c \u9633\u592a\u534e\u5fc3\u9053\uff1a\u201c\u6211\u975e\u4ee4\u4f60\u6c42\u9976\u4e0d\u53ef\uff01\u201d\u5f53\u4e0b\u66f4\u50ac\u7d27\u638c\u529b\uff0c\u638c\u5fc3\u7684\u70ed\u5ea6\u4e5f\u8d8a\u6765\u8d8a\u9ad8\uff01 \u3000\u3000\u4f46\u89c9\u5bf9\u65b9\u7684\u638c\u529b\u7adf\u662f\u6beb\u65e0\u53cd\u5e94\u7684\u6715\u5146\uff0c\u4e5f\u672a\u55c5\u5230\u76ae\u8089\u88ab\u70e7\u7126\u7684\u81ed\u5473\uff0c\u81ea\u5df1\u90a3\u6837\u5f3a\u52b2 \u7684\u638c\u529b\uff0c\u5374\u4f3c\u6253\u5230\u68c9\u82b1\u4e0a\u4e00\u822c\uff0c\u65e2\u65e0\u53cd\u6297\u4e5f\u672a\u9707\u52a8\u5bf9\u65b9\u5206\u6beb\uff0c\u9633\u592a\u534e\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a \u201c\u83ab\u975e\u8fd9\u4eba\u8eab\u6000\u7edd\u6280\uff0c\u6545\u610f\u6765\u8bf1\u6211\u4e0a\u5f53\u7684\u4e48\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c9\u5bf9\u65b9\u7684\u638c\u5fc3\u751f\u51fa\u4e00\u80a1\u7c98 \u529b\uff0c\u5c06\u4ed6\u7684\u53cc\u638c\u7262\u7262\u7c98\u4f4f\uff0c\u8fdb\u662f\u4e0d\u80fd\uff0c\u9000\u4ea6\u4e0d\u5f97\uff0c\u9633\u592a\u534e\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u53ea\u9053\u4ed6 \u7ec3\u7684\u662f\u5916\u5bb6\u529f\u592b\uff0c\u600e\u7684\u5185\u529f\u4e5f\u6df1\u539a\u5982\u65af\uff01\u800c\u4e14\u7adf\u4f3c\u4e4e\u662f\u5ce8\u5d4b\u5fc3\u6cd5\uff01\u201d\u5b9a\u775b\u4e00\u770b\uff0c\u8d8a\u770b\u8d8a \u89c9\u5f97\u8fd9\u4eba\u4f3c\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u4e00\u822c\uff0c\u84e6\u7136\u95f4\u5fc3\u4e2d\u7701\u609f\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u674e\u9038\uff1f\u201d\u53ef\u662f\u9633\u592a\u534e\u8fd9\u65f6 \u770b\u51fa\uff0c\u5df2\u7ecf\u8fdf\u4e86\uff0c\u674e\u9038\u7684\u5185\u529b\u5df2\u4ece\u638c\u5fc3\u5410\u51fa\uff0c\u7ef5\u7ef5\u5bc6\u5bc6\uff0c\u4e0d\u4f46\u5438\u4f4f\u4e86\u4ed6\u7684\u53cc\u638c\u800c\u4e14\u53cd\u51b2 \u8fc7\u6765\uff0c\u8fd9\u7b49\u9ad8\u624b\u6bd4\u62da\u5185\u529f\uff0c\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u54ea\u5bb9\u5f97\u4ed6\u5206\u51fa\u5fc3\u795e\u8bf4\u8bdd\uff01\u9633\u592a\u534e\u771f\u662f\u505a\u68a6\u4e5f \u60f3\u4e0d\u5230\u674e\u9038\u8fd9\u6837\u5927\u80c6\uff0c\u7adf\u6562\u4e54\u88c5\u519b\u5b98\uff0c\u95ef\u8fdb\u6b66\u627f\u55e3\u7684\u738b\u5e9c\uff0c\u5fc3\u4e2d\u53eb\u82e6\u4e0d\u5df2\uff01 \u3000\u3000\u674e\u9038\u7684\u5185\u529f\u6bd4\u9633\u592a\u6bd5\u7cbe\u7eaf\u5f97\u591a\uff0c\u6e10\u6e10\u65c1\u8fb9\u7684\u9ad8\u624b\u770b\u4e86\u51fa\u6765\u3002\u7a0b\u5efa\u7537\u66f4\u662f\u7559\u5fc3\u6ce8\u89c6\uff0c \u4ed6\u8d77\u521d\u89c1\u674e\u9038\u6c57\u4e0b\u5982\u96e8\uff0c\u4f3c\u4e4e\u9633\u592a\u534e\u7684\u53d6\u80dc\u53ea\u5728\u6307\u987e\u4e4b\u95f4\uff0c\u54ea\u77e5\u8fd8\u672a\u5230\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c \u5f62\u52bf\u4fbf\u5b8c\u5168\u6389\u8f6c\uff0c\u674e\u9038\u6c14\u5b9a\u795e\u95f2\uff0c\u9633\u592a\u534e\u5374\u662f\u795e\u8272\u5927\u53d8\uff0c\u6c57\u6e7f\u91cd\u886b\uff01\u7a0b\u5efa\u7537\u8fd9\u4eba\u6b66\u529f\u867d \u4e0d\u5f88\u9ad8\uff0c\u4f46\u5374\u673a\u7075\u5f97\u5f88\uff0c\u4ed6\u89c1\u8fc7\u4e00\u9762\u7684\u4eba\uff0c\u5f88\u4e45\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u8fd9\u65f6\u4e5f\u6000\u7591\u5230\u8fd9\u4e2a\u519b\u5b98\u662f \u674e\u9038\u4e54\u88c5\u7684\u4e86\uff0c\u4f46\u674e\u9038\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u5374\u662f\u6709\u597d\u51e0\u4e2a\u7981\u536b\u519b\u519b\u5b98\u966a\u540c\u4ed6\u7684\uff0c\u7a0b\u5efa\u7537\u867d\u7136\u8d8a \u60f3\u8d8a\u7591\uff0c\u4e00\u65f6\u95f4\u5374\u8fd8\u672a\u6562\u63ed\u7834\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u53c8\u89c1\u9633\u592a\u534e\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u795e\u60c5\u8d8a\u6765\u8d8a\u72fc\u72c8\u4e86\u3002\u6b66\u627f\u55e3\u4e5f\u53d1\u89c9 \u6709\u70b9\u4e0d\u597d\u4e86\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5bf9\u7a0b\u5efa\u7537\u9053\uff1a\u201c\u4f60\u53bb\u529d\u4ed6\u4eec\u7f62\u624b\u5427\uff01\u201d\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u9633\u592a\u534e \u5df2\u6447\u6447\u6b32\u5760\uff0c\u7a0b\u5efa\u7537\u9886\u4e86\u547d\u4ee4\uff0c\u518d\u65e0\u987e\u5fcc\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u5316\u89e3\u4e0d\u53ca\uff0c\u500f\u7684\u4fbf\u98de\u51fa\u4e00\u9897\u94c1\u83e9 \u63d0\uff0c\u6697\u88ad\u674e\u9038\u7684\u7a74\u9053\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5728\u53e6\u4e00\u5e2d\u4e0a\u98de\u51fa\u4e00\u4e2a\u9152\u676f\uff0c\u548c\u90a3\u9897\u94c1\u83e9\u63d0 \u649e\u4e2a\u6b63\u7740\uff0c\u767b\u65f6\u7c89\u788e\uff01 \u3000\u3000\u98de\u51fa\u9152\u676f\u7684\u8fd9\u4e2a\u4eba\u6b63\u662f\u767d\u5143\u5316\uff0c\u4ed6\u7684\u6697\u5668\u529f\u592b\u8fdc\u5728\u7a0b\u5efa\u7537\u4e4b\u4e0a\uff0c\u7b2c\u4e00\u4e2a\u9152\u676f\u78b0\u8dcc\u4e86 \u7a0b\u5efa\u7537\u7684\u94c1\u83e9\u63d0\uff0c\u7b2c\u4e8c\u4e2a\u9152\u676f\u63a5\u7740\u98de\u6765\uff0c\u6253\u4e2d\u4e86\u7a0b\u5efa\u7537\u7684\u66f2\u6c60\u7a74\uff0c\u7a0b\u5efa\u7537\u53cc\u819d\u4e00\u8f6f\uff0c\u8dea \u5012\u5730\u4e0a\uff0c\u6500\u7740\u6b66\u627f\u55e3\u7684\u8fd9\u5f20\u684c\u5b50\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u59d3\u5f20\u7684\u519b\u5b98\u662f\u674e\u9038\u5192\u5145\u7684\uff01\u201d\u8fd9\u65f6\u5c4b \u5b50\u91cc\u95f9\u6210\u4e00\u7247\uff0c\u7a0b\u5efa\u7537\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u53ea\u6709\u6b66\u627f\u55e3\u548c\u4ed6\u65c1\u8fb9\u7684\u51e0\u4e2a\u4eb2\u4fe1\u7684\u6b66\u58eb\u542c\u5230\u3002 \u3000\u3000\u6b66\u627f\u55e3\u53eb\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u662f\u8c01\u6363\u4e71\uff0c\u5feb\u67e5\u51fa\u6765\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u573a\u4e2d\u674e\u9633\u4e1a\u5df2\u5206 \u51fa\u80dc\u8d1f\uff0c\u738b\u5e9c\u603b\u7ba1\u5d14\u4e5d\u9704\u6b63\u60f3\u51fa\u53bb\u529d\u89e3\uff0c\u8fd8\u672a\u66fe\u8d70\u8fd1\u4ed6\u4eec\uff0c\u5ffd\u89c1\u674e\u9038\u5df2\u628a\u9633\u592a\u534e\u4e3e\u4e86\u8d77 \u6765\uff0c\u65cb\u98ce\u4e00\u821e\uff0c\u632f\u81c2\u629b\u51fa\uff0c\u767d\u5143\u5316\u4e00\u628a\u63a5\u7740\uff0c\u7acb\u5373\u628a\u4ed6\u53cd\u7f1a\u8d77\u6765\uff1f \u3000\u3000\u8fd9\u4e00\u6765\u66f4\u662f\u5168\u573a\u54c4\u52a8\uff0c\u6b66\u627f\u55e3\u5ffd\u5730\u559d\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u4e43\u662f\u7a81\u53a5\u5978\u7ec6\uff0c\u5feb\u7ed9\u6211\u5c06\u4ed6\u4eec \u62ff\u4e0b\uff01\u201d\u4ed6\u6307\u7740\u7684\u662f\u674e\u9038\u548c\u767d\u5143\u5316\u4e8c\u4eba\u3002\u6b66\u627f\u55e3\u8fd9\u65f6\u5df2\u77e5\u9053\u674e\u9038\u7684\u8eab\u4efd\uff0c\u4ed6\u60f3\u7ee7\u627f\u59d1\u6bcd \u7684\u5730\u4f4d\uff0c\u9664\u4e86\u592a\u5b50\u662f\u4ed6\u7684\u5927\u654c\u4e4b\u5916\uff0c\u674e\u9038\u4e5f\u662f\u6709\u6240\u9890\u5fcc\u7684\u4eba\uff0c\u6240\u4ee5\u6b66\u627f\u55e3\u4e0d\u80fd\u518d\u85c9\u53e3\u674e \u9038\u662f\u738b\u5b59\u800c\u902e\u6355\u4ed6\uff0c\u4ed6\u6599\u5230\u674e\u9038\u4e0d\u6562\u8868\u660e\u8eab\u4efd\uff0c\u56e0\u6b64\u63a5\u7eb3\u4e86\u7a0b\u5efa\u7537\u4e4b\u8ba1\uff0c\u5c06\u674e\u9038\u8bec\u4e3a\u5978 \u7ec6\uff0c\u8fde\u5e26\u626f\u4e0a\u4e86\u767d\u5143\u5316\u3002 \u3000\u3000\u6b66\u627f\u55e3\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u519b\u5b98\u5927\u5403\u4e00\u60ca\uff0c\u6709\u4e03\u516b\u4e2a\u738b\u5e9c\u7684\u6b66\u58eb\u5954\u51fa\u573a\u6765\uff0c\u957f\u5b59\u6cf0\u559d\u9053\uff1a \u201c\u4e14\u6162\uff01\u201d\u638f\u51fa\u4e86\u674e\u660e\u4e4b\u7ed9\u4ed6\u7684\u90a3\u5f20\u201c\u6d77\u6355\u6587\u4e66\u201d\uff0c\uff08\u4e0d\u9650\u5730\u70b9\uff0c\u4e0d\u9650\u65f6\u95f4\u7684\u7f09\u6355\u7f6a\u72af \u7684\u516c\u6587\uff0c\u5404\u5904\u5b98\u5e9c\uff0c\u90fd\u8981\u534f\u52a9\u3002\uff09\u626c\u4e86\u4e00\u626c\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u7237\u4f60\u5f04\u9519\u4e86\uff0c\u8fd9\u91cc\u786e\u6709\u4e24 \u4e2a\u7a81\u53a5\u5978\u7ec6\uff0c\u4f46\u5374\u4e0d\u662f\u4ed6\u4eec\u3002\u201d\u6b66\u627f\u55e3\u9762\u8272\u5927\u53d8\uff0c\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4e00\u4e2a \u5c31\u662f\u8fd9\u4f4d\u4e1c\u95e8\u6821\u5c09\u9633\u592a\u534e\uff0c\u53e6\u4e00\u4e2a\u662f\u4f60\u65c1\u8fb9\u7684\u90a3\u4e2a\u7a0b\u5efa\u7537\uff0c\u4ed6\u53c8\u662f\u6c5f\u6e56\u4e0a\u8457\u540d\u7684\u532a\u5e2e\u2014 \u2014\u4f0f\u864e\u5e2e\u7684\u5c11\u5e2e\u4e3b\uff01\u8fd9\u91cc\u662f\u674e\u90fd\u5c09\u9881\u53d1\u7684\uff0c\u6349\u62ff\u8fd9\u4e24\u4e2a\u5978\u8d3c\u7684\u6d77\u6355\u6587\u4e66\uff0c\u8bf7\u738b\u7237\u770b\uff0c\u4fbf \u77e5\u7aef\u8be6\uff01\u201d\u8bf4\u7f62\u4fbf\u5c06\u90a3\u5f20\u201c\u6d77\u6355\u6587\u4e66\u201d\u4ea4\u7ed9\u4ed6\u8eab\u8fb9\u7684\u4e00\u4e2a\u6b66\u58eb\uff0c\u4e00\u624b\u4f20\u4e00\u624b\u9012\u4e0a\u53bb\u7ed9\u6b66 \u627f\u55e3\uff0c\u4f20\u5230\u54ea\u4e00\u4e2a\u6b66\u58eb\u7684\u624b\u4e2d\uff0c\u90fd\u4e0d\u514d\u77a5\u4e86\u4e00\u773c\uff0c\u65c1\u8fb9\u7684\u519b\u5b98\u4e5f\u90fd\u4f38\u957f\u9888\u5b50\u6765\u77a7\uff0c\u6587\u4e66 \u4e0a\u7684\u5927\u7ea2\u5bab\u5370\uff0c\u674e\u660e\u4e4b\u4eb2\u7b14\u5b57\u8ff9\uff0c\u90a3\u4e9b\u519b\u5b98\u5927\u90fd\u89c1\u8fc7\uff0c\u77e5\u9053\u8fd9\u5f20\u6587\u4e66\u7edd\u4e0d\u4f1a\u662f\u5047\u7684\u4e86\uff0c \u767b\u65f6\u54c4\u95f9\u7684\u6c14\u6c1b\u53c8\u9759\u6b62\u4e0b\u6765\uff0c\u519b\u5b98\u4eec\u90fd\u7ed9\u8fd9\u7a81\u5982\u5176\u6765\u7684\u53d8\u5316\u5413\u4f4f\u4e86\uff01 \u3000\u3000\u6b66\u627f\u55e3\u63a5\u5230\u6587\u4e66\uff0c\u77a7\u4e86\u4e00\u773c\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5c06\u5b83\u6495\u5f97\u7a00\u70c2\uff0c\u62cd\u6848\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b \u9053\uff0c\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f\u6211\u63d0\u62d4\u7684\uff0c\u6211\u7d20\u6765\u77e5\u9053\u4ed6\u4eec\uff0c\u600e\u4f1a\u662f\u5978\u7ec6\uff1f\u4f60\u5feb\u628a\u9633\u6821\u5c09\u653e\u4e86\uff01\u201d\u957f \u5b59\u6cf0\u5fcd\u7740\u6c14\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u674e\u90fd\u5c09\u7684\u547d\u4ee4\u5351\u804c\u4e0d\u6562\u6709\u8fdd\uff01\u201d\u6b66\u627f\u55e3\u559d\u9053\uff1a\u201c\u674e\u660e\u4e4b\u7684\u547d\u4ee4 \u4f60\u4e0d\u6562\u8fdd\u6297\u4f60\u5c31\u6562\u8fdd\u6297\u6211\u7684\u5417\uff1f\u597d\uff0c\u5929\u5927\u7684\u4e8b\u60c5\u6709\u6211\u62c5\u5f53\uff0c\u4f60\u4eec\u7ed9\u6211\u5c06\u9633\u6821\u5c09\u62a2\u56de\u6765\uff0c \u518d\u628a\u90a3\u4e24\u4e2a\u5978\u7ec6\u7f1a\u4e86\uff01\u201d \u3000\u3000\u6b66\u627f\u55e3\u8fd9\u6b21\u6240\u5bb4\u8bf7\u7684\u519b\u5b98\uff0c\u5927\u591a\u6570\u662f\u5c5e\u4e8e\u7981\u536b\u519b\u548c\u7fbd\u6797\u519b\u7684\uff0c\u674e\u660e\u4e4b\u662f\u7981\u536b\u519b\u7edf\u9886 \u7684\u7384\u5c5e\u957f\u5b98\uff0c\u7fbd\u6797\u519b\u867d\u7136\u4e0d\u5f52\u4ed6\u7ba1\u8f96\uff0c\u4f46\u4e5f\u662f\u6709\u5173\u7cfb\u7684\u4e0a\u53f8\uff0c\u957f\u5b59\u6cf0\u6301\u6709\u674e\u660e\u4e4b\u7684\u547d\u4ee4\uff0c \u90a3\u4e9b\u519b\u5b98\u65e2\u4e0d\u6562\u5f97\u7f6a\u6b66\u627f\u55e3\uff0c\u66f4\u4e0d\u6562\u8fdd\u6297\u81ea\u5df1\u7684\u4e0a\u53f8\uff0c\u56e0\u6b64\u5341\u4e4b\u516b\u4e5d\u90fd\u5728\u8896\u624b\u65c1\u89c2\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u662f\u7981\u536b\u519b\u7684\u9ad8\u7ea7\u519b\u5b98\uff0c\u738b\u5e9c\u7684\u6b66\u58eb\u4e5f\u6709\u70b9\u987e\u5fcc\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u90fd\u5954\u5411\u674e\u9038\uff0c\u674e \u9038\u559d\u9053\uff1a\u201c\u653e\u7740\u5978\u7ec6\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u4e0d\u53bb\u6349\uff0c\u6765\u505a\u4ec0\u4e48\uff1f\u53ef\u4f11\u602a\u6211\u65e0\u793c\uff01\u201d\u4e00\u4e2a\u6b66\u58eb\u98de\u8fc7 \u6765\u4e00\u67c4\u6d41\u661f\u5251\uff0c\u88ab\u674e\u9038\u4f7f\u51fa\u91d1\u521a\u6307\u529b\uff0c\u4e00\u6293\u6293\u7740\u9ad3\u5934\uff0c\u53cd\u8361\u56de\u53bb\uff0c\u54e8\u54e8\u4e24\u58f0\uff0c\u767b\u65f6\u628a\u53e6 \u5916\u4e24\u4e2a\u6b66\u58eb\u7684\u5200\u5251\u78d5\u98de\uff0c\u8fc5\u5373\u53c8\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u4e2d\u4e86\u8fd1\u8eab\u7684\u4e00\u4e2a\u6b66\u58eb\u819d\u76d6\u3002\u6709\u4e24\u4e09\u4e2a\u7fbd\u6797 \u519b\u519b\u5b98\u60f3\u8ba8\u597d\u6b66\u627f\u55e3\uff0c\u4e5f\u51fa\u6765\u53c2\u52a0\u56f4\u653b\u674e\u9038\uff0c\u767d\u5143\u5316\u559d\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u6253\u8d77\u81ea\u5df1\u4eba\u6765\u4e86\uff1f \u4f60\u4eec\u96be\u9053\u5f53\u771f\u628a\u6211\u5f53\u6210\u5978\u7ec6\u4e48\uff1f\u201d\u767d\u5143\u5316\u662f\u4ece\u7981\u536b\u519b\u51fa\u8eab\uff0c\u5f53\u4e0a\u4e86\u7fbd\u6797\u519b\u4e00\u4e2a\u76f8\u5f53\u9ad8\u7ea7 \u7684\u519b\u5b98\u7684\uff0c\u540c\u50da\u4eec\u6df1\u77e5\u4ed6\u7684\u5e95\u7ec6\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u76f8\u4fe1\u4ed6\u662f\u7a81\u53a5\u7684\u5978\u7ec6\uff0c\u542c\u4ed6\u4e00\u559d\uff0c\u90fd\u505c\u4e86\u4e0b \u6765\u3002\u957f\u5b59\u6cf0\u7ea6\u6765\u7684\u4eba\u8fd9\u65f6\u4e5f\u7eb7\u7eb7\u633a\u8eab\u800c\u51fa\uff0c\u4e00\u9762\u62e6\u963b\u4e0d\u660e\u4e8b\u7406\u7684\u519b\u5b98\uff0c\u4e00\u9762\u5e2e\u52a9\u674e\u9038\u62b5 \u5fa1\u738b\u5e9c\u7684\u6b66\u58eb\u3002 \u3000\u3000\u7a0b\u5efa\u7537\u4e0e\u9633\u592a\u534e\u4f11\u621a\u76f8\u5173\uff0c\u89c1\u738b\u5e9c\u7684\u6b66\u58eb\u4e5f\u4e0d\u6562\u53bb\u6551\u4eba\uff0c\u4fbf\u51b2\u4e86\u51fa\u6765\uff0c\u5411\u957f\u5b59\u6cf0\u653b \u51fb\uff0c\u957f\u5b59\u6cf0\u559d\u9053\uff1a\u201c\u4f60\u6765\u5f97\u597d\uff01\u201d\u62d4\u51fa\u957f\u5251\uff0c\u4e00\u62db\u201c\u795e\u9f99\u51fa\u6d77\u201d\uff0c\u5206\u5fc3\u4fbf\u523a\uff0c\u957f\u5b59\u6cf0\u7684 \u5251\u672f\u662f\u5bb6\u4f20\u7edd\u6280\uff0c\u5373\u5728\u7981\u536b\u519b\u4e2d\u4e5f\u662f\u6570\u4e00\u6570\u4e8c\u7684\u597d\u624b\uff0c\u8fd9\u4e00\u62db\u201c\u795e\u9f99\u51fa\u6d77\u201d\u521a\u731b\u65e0\u4f26\uff0c \u7a0b\u5efa\u7537\u53cc\u7b14\u4e00\u67b6\uff0c\u9707\u5f97\u80f3\u818a\u9178\u9ebb\uff0c\u4e0d\u6562\u518d\u884c\u786c\u63a5\u3002\u4ed6\u6240\u957f\u7684\u662f\u70b9\u7a74\u529f\u592b\uff0c\u4f46\u957f\u5b59\u6cf0\u7684\u957f \u5251\u5c55\u5f00\uff0c\u5468\u8eab\u98ce\u96e8\u4e0d\u900f\uff0c\u6c14\u529b\u53c8\u5927\uff0c\u7a0b\u5efa\u7537\u6839\u672c\u6b3a\u4e0d\u8fdb\u8eab\u53bb\uff0c\u70b9\u7a74\u7684\u529f\u592b\u4e5f\u5c31\u6beb\u65e0\u4f5c\u7528\uff0c \u6570\u62db\u4e00\u8fc7\uff0c\u7a98\u6001\u6bd5\u9732\u3002 \u3000\u3000\u6b66\u627f\u55e3\u5927\u6012\u559d\u9053\uff1a\u201c\u6211\u517b\u4f60\u4eec\u5e72\u4ec0\u4e48\u7684\uff1f\u8fd8\u4e0d\u8d76\u5feb\u53bb\u6349\u8d3c\u6551\u4eba\uff0c\u674e\u660e\u4e4b\u7b97\u5f97\u4ec0\u4e48\u4e1c \u897f\uff0c\u4f60\u4eec\u5c31\u6015\u4e86\u4ed6\u4e48\uff1f\u5929\u5927\u7684\u4e8b\u60c5\u6709\u6211\u62c5\u5f53\uff01\u6709\u54ea\u4e2a\u6562\u62e6\u963b\u7684\uff0c\u7ba1\u4ed6\u662f\u8c01\uff0c\u4e00\u5e76\u62ff\u4e0b\uff01\u201d \u738b\u5e9c\u7684\u6b66\u58eb\u88ab\u4ed6\u8fd9\u4e48\u4e00\u559d\uff0c\u8fd9\u624d\u6025\u6025\u800c\u51fa\uff0c\u4f46\u4ecd\u7136\u662f\u5927\u591a\u6570\u53bb\u53c2\u52a0\u56f4\u653b\u674e\u9038\uff0c\u53ea\u6709\u4e00\u5c0f \u534a\u5954\u53bb\u6551\u9633\u592a\u534e\u3002\u957f\u5b59\u6cf0\u559d\u9053\uff1a\u201c\u6211\u5949\u547d\u6349\u62ff\u5978\u7ec6\uff0c\u8c01\u6562\u963b\u6320\uff0c\u4f11\u602a\u6211\u5251\u4e0b\u65e0\u60c5\uff01\u201d\u5237 \u5237\u4e24\u5251\uff0c\u5c06\u6700\u5148\u5954\u5230\u7684\u4e24\u4e2a\u6b66\u58eb\u523a\u4f24\uff0c\u767d\u5143\u5316\u7684\u98de\u5200\u4e5f\u4f24\u4e86\u51e0\u4eba\uff0c\u4f17\u6b66\u58eb\u5230\u5e95\u5bf9\u957f\u5b59\u6cf0 \u6709\u70b9\u987e\u5fcc\uff0c\u53ea\u662f\u56e2\u56e2\u7684\u5c06\u4ed6\u4eec\u56f4\u7740\uff0c\u8fd8\u4e0d\u6562\u771f\u4e2a\u52a8\u624b\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u957f\u5b59\u6cf0\u5df2\u662f\u4e00 \u811a\u628a\u7a0b\u5efa\u7537\u8e22\u7ffb\uff0c\u767d\u5143\u5316\u5c06\u4ed6\u6309\u7740\uff0c\u8fc5\u5373\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff01\u638c\u4e2d\u6263\u7740\u4e09\u67c4\u98de\u5200\uff0c\u4e00\u811a\u8e0f\u7740 \u9633\u592a\u534e\uff0c\u4e00\u811a\u8e0f\u7740\u7a0b\u5efa\u7537\uff0c\u706b\u773c\u91d1\u775b\u7684\u76ef\u7740\u738b\u5e9c\u6b66\u58eb\uff01 \u3000\u3000\u6b66\u627f\u55e3\u6012\u9053\uff0c\u201c\u5d14\u603b\u7ba1\uff0c\u4f60\u51fa\u53bb\u7763\u6218\uff01\u201d\u5c31\u5728\u6b64\u65f6\uff0c\u674e\u9038\u53c8\u7528\u5927\u64d2\u62ff\u624b\u6cd5\u6454\u5012\u4e86\u4e24 \u4eba\uff0c\u4e0e\u4e24\u4e2a\u7981\u536b\u519b\u519b\u5b98\u5e76\u80a9\u51b2\u51fa\uff0c\u5ffd\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u7a81\u7136\u73b0\u51fa\u4e86\u4e00\u56e2\u91d1\u5149\uff0c\u539f\u6765\u662f\u91d1\u51a0 \u9053\u4eba\u5c06\u4ed6\u7684\u91d1\u51a0\u98de\u51fa\uff01 \u3000\u3000\u91d1\u51a0\u9053\u4eba\u672c\u6765\u662f\u4e2a\u72ec\u884c\u5927\u76d7\uff0c\u4e8c\u5341\u5e74\u524d\uff0c\u7eb5\u6a2a\u9655\u7518\u9053\u4e0a\uff0c\u6240\u5411\u65e0\u654c\uff0c\u6b66\u5219\u5929\u6267\u653f\u4e4b \u540e\uff0c\u4e25\u5211\u5cfb\u6cd5\uff0c\u8bdb\u706d\u5f3a\u6881\uff0c\u91d1\u51a0\u9053\u4eba\u4e3a\u4e86\u9003\u907f\u7f09\u6355\uff0c\u9690\u6027\u57cb\u540d\uff0c\u6295\u5165\u51c9\u5dde\u767d\u9a6c\u89c2\u4e2d\u505a\u4e2a \u9053\u58eb\uff0c\u524d\u4efb\u89c2\u4e3b\u6b7b\u540e\uff0c\u4ed6\u9738\u5360\u767d\u9a6c\u89c2\u81ea\u4e3a\u89c2\u4e3b\uff0c\u6b66\u627f\u55e3\u8bbf\u77e5\u4ed6\u7684\u6765\u5386\uff0c\u4ee5\u5351\u8f9e\u539a\u793c\uff0c\u8bf7 \u4ed6\u5165\u4eb2\u3002\u4ed6\u8eb2\u907f\u4e86\u4e8c\u5341\u5e74\uff0c\u6599\u60f3\u7f09\u76d7\u7684\u8859\u53f8\u4e0d\u4f1a\u518d\u6ce8\u610f\u4ed6\u4e86\uff0c\u517c\u4ee5\u6709\u6b66\u627f\u55e3\u7684\u5e87\u62a4\uff0c\u9042 \u653e\u80c6\u51fa\u5c71\uff0c\u51c6\u5907\u6276\u52a9\u4e86\u6b66\u627f\u55e3\u767b\u57fa\u4e4b\u540e\uff0c\u4ed6\u4fbf\u8981\u8fd8\u4fd7\u518d\u4eab\u8363\u534e\u3002 \u3000\u3000\u91d1\u51a0\u9053\u4eba\u5728\u8fd9\u4e8c\u5341\u5e74\u4e2d\u7ec3\u6210\u4e86\u9053\u5bb6\u7684\u5929\u4e00\u7f61\u6c14\uff0c\u53c8\u7ec3\u4e86\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u6697\u5668\uff0c\u80fd\u4ee5\u91d1 \u51a0\u6740\u654c\uff0c\u6240\u4ee5\u81ea\u79f0\u91d1\u51a0\u9053\u4eba\uff0c\u8fd9\u65f6\u4ed6\u89c1\u738b\u5e9c\u7684\u6b66\u58eb\u5904\u5728\u4e0b\u98ce\uff0c\u5373\u5c06\u6e83\u8d25\uff0c\u6709\u610f\u5728\u6b66\u627f\u55e3 \u9762\u524d\uff0c\u5356\u5f04\u795e\u901a\uff0c\u4e00\u51fa\u624b\u4fbf\u98de\u51fa\u4e86\u4ed6\u7684\u72ec\u95e8\u6697\u5668\u3002 \u3000\u3000\u91d1\u51a0\u98de\u51fa\uff0c\u58f0\u52bf\u751a\u662f\u60ca\u4eba\uff0c\u4f46\u89c1\u4e00\u56e2\u91d1\u5149\uff0c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u5728\u4f17\u4eba\u5934\u9876\u547c\u547c\u65cb \u8f6c\uff0c\u738b\u5e9c\u7684\u6b66\u58eb\u77e5\u9053\u5389\u5bb3\uff0c\u56db\u6563\u907f\u5f00\uff0c\u5e2e\u674e\u9038\u62b5\u5fa1\u6b66\u58eb\u7684\u4e00\u4e2a\u7981\u536b\u519b\u519b\u5b98\u62ac\u5934\u4e00\u770b\uff0c\u6070 \u6070\u78b0\u7740\u90a3\u91d1\u51a0\u659c\u98de\u88ad\u6765\uff0c\u4f46\u542c\u5f97\u60e8\u53eb\u4e00\u58f0\uff0c\u8fd9\u519b\u5b98\u7684\u4e00\u53ea\u624b\u81c2\u5df2\u88ab\u91d1\u51a0\u524a\u53bb\u3002\u539f\u6765\u8fd9\u91d1 \u51a0\u4e0d\u4f46\u5e3d\u6cbf\u950b\u5229\uff0c\u5185\u91cc\u8fd8\u85cf\u6709\u5341\u4e8c\u67c4\u5315\u9996\uff0c\u6709\u5982\u952f\u9f7f\uff0c\u53ef\u4ee5\u7ede\u4eba\u9996\u7ea7\u3002\u8fd9\u519b\u5b98\u4ec5\u88ab\u524a\u53bb \u4e00\u6761\u624b\u81c2\uff0c\u5df2\u7b97\u662f\u4e0d\u5e78\u4e2d\u4e4b\u5927\u5e78\u3002\u91d1\u51a0\u524a\u65ad\u4e86\u90a3\u519b\u5b98\u7684\u624b\u81c2\u4e4b\u540e\uff0c\u4ecd\u7136\u76d8\u65cb\u98de\u884c\uff0c\u500f\u7684 \u5c31\u98de\u5230\u674e\u9038\u9762\u524d\uff0c\u674e\u9038\u5927\u6012\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u559d\u9053\uff1a\u201c\u5927\u80c6\u5996\u9053\uff0c\u52a9\u7ea3\u4e3a\u8650\uff0c\u5403\u6211\u4e00\u5251\uff01\u201d \u674e\u9038\u7684\u5b9d\u5251\u53ef\u4ee5\u65ad\u91d1\u5207\u7389\uff0c\u5b9b\u5982\u6d12\u4e0b\u6ee1\u5929\u5200\u96e8\uff01\u738b\u5e9c\u7684\u6b66\u58eb\u548c\u519b\u5b98\u4eec\u90fd\u6709\u51e0\u4eba\u53d7\u4e86\u4f24\u3002 \u91d1\u51a0\u9053\u4eba\u635f\u4e86\u91d1\u51a0\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u7acb\u5373\u8df3\u51fa\u573a\u6765\uff0c\u6251\u5411\u674e\u9038\u3002 \u3000\u3000\u8d74\u5bb4\u7684\u519b\u5b98\u4e2d\u4e5f\u6709\u8bb8\u591a\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u4eec\u8ba4\u5f97\u8fd9\u628a\u5b9d\u5251\u4e43\u662f\u4ee5\u524d\u592a\u5b97\u7687\u5e1d\u7684\u4f69\u5251\uff0c \u540e\u6765\u8d50\u7ed9\u674e\u9038\u7684\uff0c\u674e\u9038\u5341\u56db\u5c81\u79bb\u5f00\u5bab\u5ef7\uff0c\u8fd9\u65f6\u6b63\u662f\u4e09\u5341\u51fa\u5934\u7684\u4e2d\u5e74\uff0c\u90a3\u4e9b\u8001\u5e74\u7684\u519b\u5b98\u4f9d \u7a00\u8fd8\u8bb0\u5f97\u4ed6\u5f53\u5e74\u7684\u5bb9\u8c8c\uff0c\u8fd9\u65f6\u4ed4\u7ec6\u4e00\u770b\uff0c\u674e\u9038\u7684\u9762\u8c8c\u867d\u7136\u5927\u5927\u6539\u53d8\uff0c\u4f46\u4ecd\u6709\u4e00\u4e24\u70b9\u7279\u5f81\uff0c \u4ed6\u4eec\u8fd8\u53ef\u4ee5\u8ba4\u51fa\u6765\u3002\u8fd9\u4e9b\u519b\u5b98\u867d\u7136\u4e0d\u6562\u5f53\u573a\u8ba4\u4ed6\uff0c\u4f46\u5374\u4e0d\u518d\u76f8\u4fe1\u4ed6\u662f\u7a81\u53a5\u7684\u5978\u7ec6\u4e86\u3002 \u3000\u3000\u91d1\u51a0\u9053\u4eba\u5954\u51fa\u573a\u5fc3\uff0c\u51b2\u7740\u674e\u9038\u4e00\u58f0\u957f\u5578\uff0c\u674e\u9038\u4f46\u89c9\u5fc3\u7075\u4e00\u9707\uff0c\u5e78\u800c\u4ed6\u5728\u5929\u5c71\u6f5c\u4fee\u516b \u8f7d\uff0c\u5185\u529f\u7684\u6839\u57fa\u4ea6\u5df2\u76f8\u5f53\u6df1\u539a\uff0c\u91d1\u51a0\u9053\u4eba\u7684\u5929\u4e00\u7f61\u6c14\u4f24\u4e0d\u4e86\u4ed6\u3002\u674e\u9038\u7b11\u9053\uff1a\u201c\u4f60\u9b3c\u53eb\u4ec0 \u4e48\uff1f\u201d\u5b9d\u5251\u4e00\u6325\uff0c\u5bd2\u5149\u7535\u95ea\uff0c\u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u767b\u65f6\u5c06\u91d1\u51a0\u9053\u4eba\u7684\u8eab\u5f62\u7b3c\u7f69\u5728\u5251\u5149\u4e4b \u5185\u3002\u91d1\u51a0\u9053\u4eba\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u6043\u7740\u6709\u4e00\u628a\u5b9d\u5251\uff0c\u5c31\u4ee5\u4e3a\u53ef\u4ee5\u5728\u8001\u9053\u9762\u524d\u901e\u80fd\u4e86 \u4e48\uff1f\u4e14\u53eb\u4f60\u77e5\u9053\u6211\u7684\u5389\u5bb3\u3002\u201d\u53d6\u51fa\u4e00\u5bf9\u94dc\u94b1\uff0c\u53cc\u94b1\u4e00\u5408\uff0c\u8f70\u8e51\u4e4b\u58f0\uff0c\u6709\u5982\u96f7\u9e23\uff0c\u5385\u5185\u6570 \u767e\u519b\u5b98\uff0c\u8033\u9f13\u90fd\u7ed9\u9707\u5f97\u55e1\u55e1\u4f5c\u54cd\uff01\u674e\u9038\u6325\u52a8\u5b9d\u5251\uff0c\u201c\u54e8\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5149\u6d41\u6563\uff0c\u674e\u9038\u4f46\u89c9 \u4e00\u80a1\u5927\u529b\u538b\u6765\uff0c\u4e0d\u7531\u5f97\u5012\u9000\u4e09\u6b65\u3002\u91d1\u51a0\u9053\u4eba\u7684\u529f\u529b\u5728\u674e\u9038\u4e4b\u4e0a\uff0c\u674e\u9038\u7684\u5b9d\u5251\u88ab\u4ed6\u4e00\u9707\uff0c \u51e0\u4e4e\u8131\u624b\u98de\u53bb\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u91d1\u51a0\u9053\u4eba\u7684\u4e00\u9762\u94dc\u94b1\u4e5f\u7ed9\u4ed6\u5212\u4e86\u4e00\u9053\u88c2\u75d5\u3002 \u3000\u3000\u91d1\u51a0\u9053\u4eba\u4e00\u4e3e\u624b\u4fbf\u7834\u89e3\u4e86\u674e\u9038\u7684\u5251\u62db\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u53cc\u8dcb\u4e00\u5408\uff0c\u53c8\u4ee5\u6cf0\u5c71\u538b\u9876\u4e4b\u52bf\u653b \u6765\uff0c\u674e\u9038\u8bd5\u51fa\u4e86\u4ed6\u7684\u529f\u529b\uff0c\u4e0d\u518d\u786c\u63a5\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u817e\u8eab\u98de\u8d77\uff0c\u5b9d\u5251\u5728\u4ed6\u94dc\u94b1\u504f\u65c1\u63a0\u8fc7\uff0c \u5251\u950b\u4e00\u8f6c\uff0c\u500f\u7136\u95f4\u4fbf\u662f\u4e00\u62db\u201c\u5212\u7834\u5929\u6cb3\u201d\uff0c\u5251\u950b\u4e0e\u94dc\u8dcb\u4e00\u64e6\uff0c\u767b\u65f6\u53c8\u662f\u4e00\u7247\u65ad\u91d1\u5207\u7389\u4e4b \u58f0\uff0c\u5b9d\u5251\u6240\u6296\u8d77\u7684\u5bd2\u5149\uff0c\u5c31\u5982\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u7384\u6d12\u4e0b\u6765\uff01\u8fd9\u4e00\u62db\u674e\u9038\u867d\u7136\u4ecd\u662f\u7ed9 \u4ed6\u51fb\u9000\uff0c\u4f46\u53cc\u65b9\u7684\u52b2\u529b\u662f\u6b63\u9762\u63a5\u89e6\uff0c\u674e\u9038\u6240\u53d7\u7684\u538b\u529b\u4fbf\u5927\u5927\u51cf\u8f7b\uff0c\u800c\u91d1\u51a0\u9053\u4eba\u4e5f\u611f\u5230\u4e86 \u5b9d\u5251\u7684\u5a01\u80c1\u3002 \u3000\u3000\u674e\u9038\u4ed7\u7740\u8f7b\u7075\u7cbe\u5999\u7684\u5251\u6cd5\u4e0e\u91d1\u51a0\u9053\u4eba\u5468\u65cb\uff0c\u867d\u7136\u4ecd\u5904\u4e0b\u98ce\uff0c\u4f46\u5df2\u53ef\u4ee5\u52c9\u529b\u652f\u6301\uff0c\u8fd9 \u4e24\u4eba\u5c55\u5f00\u6076\u6218\uff0c\u5468\u56f4\u4e09\u4e08\u4e4b\u5916\u4e00\u7247\u5251\u5149\uff0c\u5343\u91cd\u94b1\u5f71\uff0c\u5176\u4ed6\u7684\u4eba\u54ea\u91cc\u63d2\u5f97\u8fdb\u53bb\uff1f \u3000\u3000\u738b\u5e9c\u603b\u7ba1\u5d14\u4e5d\u9704\u51fa\u6765\u7763\u9635\uff0c\u7387\u9886\u6b66\u58eb\uff0c\u5c06\u957f\u5b59\u6cf0\u8fd9\u4e00\u73ed\u4eba\u56f4\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u5d14\u4e5d\u9704\u4fbf \u5f85\u53bb\u62a2\u6551\u9633\u592a\u534e\uff0c\u767d\u5143\u5316\u4e00\u811a\u8e0f\u7740\u9633\u592a\u534e\uff0c\u4e00\u811a\u8e0f\u7740\u7a0b\u5efa\u7537\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u98de\u51fa\u4e86\u4e09\u67c4\u98de \u5200\uff0c\u5d14\u4e5d\u9704\u662f\u738b\u5e9c\u6709\u6570\u7684\u9ad8\u624b\uff0c\u6240\u4f7f\u7684\u94c1\u62c2\u5c18\u662f\u6c5f\u6e56\u4e0a\u7f55\u89c1\u7684\u5916\u95e8\u5175\u5668\uff0c\u767d\u5143\u5316\u98de\u5200\u63b7 \u5230\uff0c\u7ed9\u4ed6\u4e00\u62c2\uff0c\u4e24\u67c4\u843d\u5730\uff0c\u4e00\u67c4\u98de\u5f00\uff0c\u65c1\u8fb9\u7684\u4e00\u4e2a\u6b66\u58eb\u95ea\u907f\u4e0d\u53ca\uff0c\u7ed9\u98de\u5200\u523a\u4f24\uff0c\u5d14\u4e5d\u9704 \u867d\u7136\u4e0d\u4ff1\u98de\u5200\uff0c\u4e5f\u7ed9\u963b\u4e86\u4e00\u963b\u3002\u81ea\u5143\u5316\u5927\u559d\u9053\uff1a\u201c\u4f60\u8981\u62a2\u4eba\uff0c\u6211\u5c31\u628a\u4e24\u5177\u5c38\u4f53\u7ed9\u4f60\u3002\u201d \u5d14\u4e5d\u9704\u6015\u4ed6\u8e0f\u6b7b\u4e86\u9633\u592a\u534e\uff0c\u679c\u7136\u4e0d\u6562\u786c\u6765\u3002\u53ea\u597d\u6307\u6325\u6b66\u58eb\uff0c\u5411\u957f\u5b59\u6cf0\u72e0\u72e0\u653b\u51fb\uff0c\u5fc3\u60f3\u628a \u957f\u5b59\u6cf0\u6349\u4e86\uff0c\u903c\u4ed6\u4e0b\u4ee4\uff0c\u4e0d\u6015\u767d\u5143\u5316\u4e0d\u4f9d\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u5927\u559d\u9053\uff1a\u201c\u5404\u4f4d\u540c\u50da\uff0c\u6211\u5949\u4e86\u674e\u90fd\u5c09\u7684\u6d77\u6355\u6587\u4e66\u3002\u8bf7\u5404\u4f4d\u534f\u540c\u6355\u8d3c\uff01\u201d\u674e\u660e \u4e4b\u662f\u7981\u536b\u519b\u7684\u9876\u5934\u4e0a\u53f8\uff0c\u8fd9\u65f6\u519b\u5b98\u4eec\u53c8\u77e5\uff0c\u674e\u9038\u7edd\u975e\u5978\u7ec6\uff0c\u6709\u4e00\u90e8\u5206\u4eba\u4fbf\u51fa\u6765\u5e2e\u957f\u5b59\u6cf0 \u4f5c\u6218\uff0c\u4f46\u5927\u90e8\u5206\u4eba\u8fd8\u662f\u6015\u627f\u55e3\u7684\u5a01\u52bf\uff0c\u4ecd\u7136\u8896\u624b\u65c1\u89c2\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u5f62\u6210\u4e86\u738b\u5e9c\u6b66\u58eb\u4e0e\u7981 \u536b\u519b\u5b98\u7684\u6df7\u6218\u5c40\u9762\u3002\u6b66\u627f\u55e3\u5927\u6012\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u201d\u7acb\u5728\u4ed6\u65c1\u8fb9\u7684\u725b\u5148\u751f\u7b11\u9053\uff1a\u201c\u738b \u7237\u4e0d\u5fc5\u751f\u6c14\uff0c\u5f85\u6211\u5c06\u8fd9\u4e9b\u72af\u4e0a\u4f5c\u4e71\u7684\u53db\u5f92\u64d2\u6765\u4fbf\u662f\uff01\u201d\u4ed6\u662f\u6697\u5668\u7684\u5927\u540d\u5bb6\uff0c\u5728\u516d\u4e03\u4e08\u5916\uff0c \u98de\u51fa\u4e86\u4e00\u628a\u6885\u82b1\u9488\uff0c\u5728\u90a3\u6837\u6df7\u4e71\u7684\u573a\u9762\u4e2d\uff0c\u6885\u82b1\u9488\u7adf\u4f3c\u662f\u8ba4\u5f97\u4eba\u4f3c\u7684\uff0c\u4e13\u6253\u7981\u536b\u519b\u5b98\uff0c \u7247\u523b\u4e4b\u95f4\uff0c\u7adf\u6709\u56db\u4e94\u4e2a\u6b66\u529f\u7a0d\u5f31\u7684\u519b\u5b98\u7ed9\u6885\u82b1\u9488\u5c04\u4e2d\u4e86\u7a74\u9053\uff0c\u767b\u65f6\u5012\u4e0b\uff0c\u88ab\u738b\u5e9c\u7684\u6b66\u58eb \u6349\u4e86\u53bb\u3002\u6b63\u5728\u95f9\u5f97\u4e0d\u53ef\u5f00\u652f\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u65a5\u559d\uff1a\u201c\u90fd\u7ed9\u6211\u4f4f\u624b\uff01\u201d\u7adf\u7136\u662f\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u56de\u3000\u5386\u52ab\u4e86\u65e0\u751f\u6b7b\u5ff5 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u5386\u52ab\u4e86\u65e0\u751f\u6b7b\u5ff5 \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u4e0d\u5230\u6765\u7684\u7adf\u662f\u6b66\u7384\u971c\uff01\u9ad8\u624b\u6bd4\u62fc\uff0c\u54ea\u5bb9\u5f97\u7a0d\u7a0d\u5206\u795e\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c \u91d1\u51a0\u9053\u4eba\u53cc\u8dcb\u4e00\u5408\uff0c\u7adf\u628a\u674e\u9038\u7684\u5b9d\u5251\u5939\u5728\u5f53\u4e2d\uff0c\u52b2\u529b\u4e00\u53d1\uff0c\u674e\u9038\u864e\u53e3\u6d41\u8840\uff0c\u628a\u6043\u4e0d\u4f4f\uff0c \u545b\u561f\u4e00\u58f0\uff0c\u5b9d\u5251\u5760\u5730\uff01\u91d1\u51a0\u9053\u4eba\u6b63\u8981\u518d\u4f24\u674e\u9038\uff0c\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u91d1\u51a0\u9053\u4eba\u53cc\u8dcb\u4e00\u65cb\uff0c \u53cd\u624b\u5288\u53bb\uff0c\u6b66\u7384\u971c\u65a5\u9053\uff1a\u201c\u4f60\u6562\u8fdd\u6297\u6211\u7684\u547d\u4ee4\uff01\u201d\u91d1\u51a0\u9053\u4eba\u7a81\u89c9\u6c14\u6c1b\u6709\u5f02\uff0c\u90a3\u95f9\u54c4\u54c4\u7684 \u573a\u9762\uff0c\u5ffd\u7136\u95f4\u53d8\u5f97\u5bc2\u9759\u5982\u6b7b\uff0c\u7b80\u76f4\u8fde\u4e00\u6839\u9488\u8dcc\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u8bf4 \u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u4e24\u9762\u94dc\u8dcb\u5df2\u7ed9\u6b66\u7384\u971c\u5288\u624b\u593a\u53bb\uff01\u672c\u6765\u4ee5\u91d1\u51a0\u9053\u4eba\u7684\u6b66\u529f\uff0c \u867d\u7136\u654c\u4e0d\u8fc7\u6b66\u7384\u971c\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5\u62b5\u654c\u56db\u4e94\u5341\u62db\uff0c\u4f46\u4ed6\u5728\u6076\u6218\u4e4b\u540e\uff0c\u52a0\u4ee5\u5fc3\u795e\u4e00\u4e71\uff0c\u7acb\u5373 \u88ab\u6b66\u7384\u971c\u4e58\u865a\u800c\u5165\uff0c\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u6b66\u7384\u971c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8fd9\u4e2a\u5996\u9053\uff01\u201d\u4e00\u638c\u51fb\u51fa\uff0c\u5c06\u91d1\u51a0\u9053\u4eba \u9707\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u7acb\u5373\u559d\u4ee4\u7981\u536b\u519b\u7684\u519b\u5b98\u628a\u4ed6\u7f1a\u4e86\u3002 \u3000\u3000\u674e\u9038\u5446\u82e5\u6728\u9e21\uff0c\u76ee\u5149\u76f8\u63a5\uff0c\u53ea\u89c1\u6b66\u7384\u971c\u6cdb\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u56de\u6765\u4e86\u3002\u201d \u674e\u9038\u70b9\u4e86\u70b9\u5934\uff0c\u5f2f\u8170\u62fe\u5251\uff0c\u518d\u62ac\u8d77\u5934\u65f6\uff0c\u6b66\u7384\u971c\u5df2\u8d70\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u519b\u5b98\u4eec\u548c\u738b\u5e9c\u6b66\u58eb\u90fd\u8ba4\u8bc6\u6b66\u7384\u971c\uff0c\u89c1\u5979\u7a81\u5982\u5176\u6765\uff0c\u6599\u60f3\u5fc5\u662f\u5949\u4e86\u6b66\u5219\u5929\u7687\u5e1d\u7684\u547d\u4ee4\uff0c \u8c01\u4eba\u8fd8\u6562\u52a8\u624b\uff1f\u53ea\u6709\u725b\u5148\u751f\u4e0d\u8ba4\u5f97\u5979\uff0c\u4f46\u4e5f\u89c9\u60c5\u5f62\u6709\u5f02\uff0c\u6df7\u4e71\u4e2d\u5ffd\u542c\u5f97\u6b66\u627f\u55e3\u6084\u6084\u5429\u5490 \u4ed6\u9053\uff1a\u201c\u4f60\u628a\u9633\u7a0b\u4e24\u4eba\u6740\u6b7b\uff0c\u8d76\u5feb\u9003\u8d70\uff01\u201d\u519b\u5b98\u548c\u6b66\u58eb\u4eec\u4e24\u8fb9\u5206\u5f00\uff0c\u8ba9\u51fa\u4e00\u6761\u8def\u53e3\u6765\uff0c \u725b\u5148\u751f\u542c\u4e86\u6b66\u627f\u55e3\u7684\u5429\u5490\uff0c\u6084\u6084\u9000\u4e0b\uff0c\u9053\u4eba\u6b66\u58eb\u4e1b\u4e2d\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u6b66\u7384\u971c\u4e5f\u5df2\u8d70\u5230\u6b66\u627f \u55e3\u7684\u9762\u524d\u3002 \u3000\u3000\u6b66\u627f\u55e3\u88c5\u4f5c\u4e0d\u77e5\uff0c\u8d77\u7acb\u8bf4\u9053\uff1b\u201c\u59b9\u59b9\uff0c\u4f60\u6765\u5f97\u6b63\u597d\u3002\u8fd9\u91cc\u6709\u4e24\u4e2a\u7a81\u53a5\u5978\u7ec6\uff0c\u6211\u6b63\u8981 \u6349\u4ed6\u4eec\u3002\u8bf7\u4f60\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u662f\u771f\u4e0d\u77e5\u8fd8\u662f\u5047\u4e0d\u77e5\uff0c\u90a3\u4e24\u4e2a\u5978\u7ec6\u4e0d\u662f \u5df2\u7ecf\u6349\u4f4f\u4e86\u5417\uff1f\u201d\u6b66\u627f\u7528\u9053\uff1a\u201c\u8c01\uff1f\u54ce\u5440\uff0c\u90a3\u4e24\u4e2a\u4e0d\u662f\u5978\u7ec6\uff0c\u8fd9\u4e24\u4e2a\u624d\u662f\u5462\uff01\u201d\u6b66\u7384\u971c \u9053\uff1a\u201c\u957f\u5b59\u6cf0\uff0c\u4f60\u7684\u6d77\u6355\u6587\u4e66\u5e26\u6765\u4e86\u6ca1\u6709\uff1f\u674e\u90fd\u5c09\u547d\u4ee4\u4f60\u7f09\u6355\u7684\u5978\u7ec6\u662f\u8c01\uff1f\u201d\u957f\u5b59\u6cf0\u5927 \u58f0\u8bf4\u9053\uff1a\u201c\u8981\u7f09\u6355\u7684\u662f\u9633\u592a\u534e\u548c\u7a0b\u5efa\u7537\uff0c\u5e78\u4e0d\u8fb1\u547d\uff0c\u5df2\u7ecf\u62ff\u4e0b\u6765\u4e86\u3002\u4f46\u9b4f\u738b\u4e0d\u80af\u653e\u8d70\uff0c \u6d77\u6355\u6587\u4e66\u4e5f\u7ed9\u4ed6\u6495\u70c2\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u67f3\u7709\u4e00\u7ad6\uff0c\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d\u6b66\u627f\u55e3\u867d\u7136\u662f\u6b66\u5219\u5929\u7684\u4eb2\u4f84\u513f\uff0c\u4f46\u4ed6 \u6df1\u77e5\u59d1\u6bcd\u5ba0\u7231\u8fd9\u4e2a\u5802\u59b9\u8fdc\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u4e00\u5411\u90fd\u5bf9\u5979\u6709\u70b9\u60e7\u6015\uff0c\u88ab\u5979\u8ffd\u95ee\uff0c\u614c\u5fd9\u8fa9\u9053\uff1a \u201c\u674e\u660e\u4e4b\u60f3\u5fc5\u662f\u6709\u70b9\u8bef\u4f1a\u4e86\uff0c\u8fd9\u9633\u592a\u534e\u662f\u4e1c\u95e8\u6821\u5c09\uff0c\u600e\u4f1a\u662f\u5978\u7ec6\u5462\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60 \u8bf4\u4ed6\u4e0d\u662f\u5978\u7ec6\uff0c\u4f60\u548c\u5929\u540e\u965b\u4e0b\u8bf4\u53bb\uff0c\u6211\u5949\u4e86\u965b\u4e0b\u7684\u8bcf\u4e66\uff0c\u8bf4\u4ed6\u4e8c\u4eba\u7f6a\u60c5\u4e25\u91cd\uff0c\u5929\u540e\u965b\u4e0b \u8981\u63d0\u4ed6\u4eec\u5165\u5bab\u4eb2\u81ea\u5ba1\u95ee\u3002\u4f60\u8981\u66ff\u4ed6\u4eec\u8fa9\u62a4\uff0c\u90a3\u4e48\u5c31\u4e00\u540c\u53bb\u5427\uff01\u201d \u3000\u3000\u6b66\u7384\u971c\u53d6\u51fa\u8bcf\u4e66\uff0c\u6b66\u627f\u55e3\u5413\u5f97\u9762\u5982\u571f\u8272\uff0c\u60f3\u4e0d\u5230\u4e8b\u60c5\u5df2\u95f9\u5230\u5219\u5929\u7687\u5e1d\u7684\u9762\u524d\uff0c\u674e\u660e \u4e4b\u7684\u547d\u4ee4\u4ed6\u53ef\u4ee5\u4e0d\u7406\u4f1a\uff0c\u7687\u5e1d\u7684\u8bcf\u4e66\u4ed6\u7109\u6562\u4e0d\u9075\uff1f\u8fd9\u5239\u90a3\u95f4\u4ed6\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u5ffd\u5730\u62cd \u6848\u9a82\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u4e24\u4e2a\u5978\u7ec6\u7adf\u7136\u6562\u6df7\u5230\u738b\u5e9c\u6765\uff0c\u9a97\u5f97\u6211\u597d\u82e6\uff01\u81ea\u5df1\u662f\u5978\u7ec6\uff0c\u5374\u8fd8\u8bec\u8d56 \u522b\u4eba\uff0c\u771f\u771f\u53ef\u6068\uff01\u5de6\u53f3\uff0c\u5c06\u4ed6\u4eec\u62bc\u4e0a\u6765\uff0c\u66ff\u6211\u91cd\u91cd\u7684\u6253\u4ed6\u4eec\u4e00\u987f\uff01\u201d \u3000\u3000\u767d\u5143\u5316\u548c\u957f\u5b59\u6cf0\u4e5f\u60f3\u5230\u6b66\u627f\u55e3\u662f\u4e3a\u4e86\u89e3\u56f4\uff0c\u4f46\u4e00\u60f3\u6b66\u627f\u55e3\u5df2\u80af\u4f4e\u5934\uff0c\u627f\u8ba4\u4e86\u9633\u7a0b\u4e8c \u4eba\u4e43\u662f\u5978\u7ec6\uff0c\u4ed6\u5230\u5e95\u662f\u4e2a\u738b\u7237\uff0c\u591a\u5c11\u4e5f\u5f97\u7ed9\u4ed6\u4e00\u70b9\u9762\u5b50\uff0c\u767d\u5143\u5316\u4fbf\u79fb\u5f00\u811a\u6b65\uff0c\u628a\u9633\u7a0b\u4e8c \u4eba\u6293\u4e86\u8d77\u6765\uff0c\u4ea4\u7ed9\u4e86\u4e00\u4e2a\u738b\u5e9c\u6b66\u58eb\u3002 \u3000\u3000\u6709\u6b66\u7384\u971c\u5728\u6b64\uff0c\u957f\u5b59\u6cf0\u6599\u60f3\u4e0d\u81f3\u4e8e\u6709\u4ec0\u4e48\u53d8\u5366\uff0c\u54ea\u77e5\u6b66\u627f\u55e3\u7684\u8bdd\uff0c\u5b9e\u5728\u662f\u6697\u793a\u725b\u5148 \u751f\u624b\u4e0b\u7684\u3002\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u5fc5\u5728\u8fd9\u91cc\u8d23\u6253\u4e86\uff0c\u89e3\u8fdb\u5bab\u91cc\u518d\u5ba1\u95ee\u5427\u3002\u201d\u8bdd\u72b9\u672b\u4e86\uff0c\u5ffd\u542c\u5f97 \u4e24\u58f0\u60e8\u53eb\uff0c\u539f\u6765\u662f\u725b\u5148\u751f\u6df7\u5728\u6b66\u58eb\u4e1b\u4e2d\uff0c\u6697\u4e0b\u6bd2\u624b\uff0c\u4e24\u679a\u5582\u6709\u5267\u6bd2\u7684\u900f\u9aa8\u9489\uff0c\u5c04\u5165\u4e86\u9633 \u592a\u534e\u548c\u7a0b\u5efa\u7537\u7684\u5fc3\u623f\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u53d1\u751f\u610f\u5916\uff0c\u957f\u5b59\u6cf0\u521a\u7701\u609f\u662f\u6b66\u627f\u55e3\u6740\u4eba\u706d\u53e3\uff0c\u5ffd\u89c1\u91d1\u51a0\u9053\u4eba\u4e5f\u722c\u4e86\u8d77\u6765\uff0c\u5f80 \u5916\u4fbf\u8dd1\uff0c\u539f\u6765\u4ed6\u7684\u6c14\u529f\u9020\u8be3\u975e\u51e1\uff0c\u8fd0\u6c14\u51b2\u5173\uff0c\u6070\u6070\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u81ea\u5df1\u89e3\u5f00\u4e86\u7a74\u9053\u3002\u957f\u5b59 \u6cf0\u4e00\u638c\u51fb\u53bb\uff0c\u201c\u7bf7\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u4e2d\u4ed6\u7684\u80cc\u5fc3\uff0c\u91d1\u51a0\u9053\u4eba\u7a74\u9053\u65b9\u901a\uff0c\u5c1a\u672a\u80fd\u8fd0\u529f\u53cd\u51fb\uff0c\u4f46 \u957f\u5b59\u6cf0\u8fd9\u4e00\u638c\u5374\u4e5f\u6253\u4ed6\u4e0d\u5012\uff0c\u4ed6\u987a\u7740\u638c\u52bf\uff0c\u5411\u524d\u51b2\u5f97\u66f4\u6025\uff0c\u8f6c\u773c\u95f4\u5df2\u51fa\u4e86\u5927\u95e8\u3002 \u3000\u3000\u767d\u5143\u5316\u8ba4\u51fa\u6697\u4e0b\u6bd2\u624b\u7684\u662f\u725b\u5148\u751f\uff0c\u4e00\u6296\u624b\u98de\u51fa\u4e24\u67c4\u98de\u5200\uff0c\u91d1\u51a0\u9053\u4eba\u4e2d\u4e86\u4e00\u5200\uff0c\u6447\u6447 \u6b32\u5760\uff0c\u53e6\u4e00\u53e3\u98de\u5200\u89e6\u53ca\u725b\u5148\u751f\u7684\u8eab\u4f53\uff0c\u5374\u201c\u54e8\u537f\u201d\u4e00\u58f0\uff0c\u8dcc\u4e0b\u5730\u6765\u3002\u539f\u6765\u725b\u5148\u751f\u7ec3\u6709 \u2018\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u4ed6\u548c\u91d1\u51a0\u9053\u4eba\u4e0d\u540c\uff0c\u4ed6\u672a\u66fe\u53d7\u4f24\u3002\u529f\u529b\u65e0\u635f\uff0c\u98de\u5200\u867d\u7136\u63b7 \u4e2d\u4e86\u4ed6\uff0c\u5374\u8fde\u4ed6\u7684\u5e03\u8863\u4e5f\u6ca1\u6709\u523a\u7a7f\u3002 \u3000\u3000\u674e\u9038\u98de\u8eab\u4fbf\u8ffd\uff0c\u53ea\u542c\u5f97\u6b66\u627f\u55e3\u5927\u559d\u9053\uff1a\u201c\u53ef\u607c\uff0c\u53ef\u607c\uff01\u662f\u8c01\u6697\u6740\u4e86\u94a6\u72af\uff1f\u5c06\u4ed6\u6bd9\u4e86\uff01\u201d \u767b\u65f6\u4e00\u9635\u4e71\u5200\uff0c\u5c06\u90a3\u4e2a\u6b66\u58eb\u780d\u6b7b\u3002\u771f\u51f6\u725b\u5148\u751f\u5219\u5df2\u9003\u51fa\u5927\u95e8\u3002\u674e\u9038\u811a\u6b65\u4e0d\u505c\u8ddf\u7740\u8ffd\u51fa\uff0c \u80cc\u540e\u9690\u9690\u4f20\u6765\u4e86\u957f\u5b59\u6cf0\u7684\u53eb\u58f0\uff1a\u201c\u674e\u5144\uff0c\u56de\u6765\uff01\u201d \u3000\u3000\u674e\u9038\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f6f\u4f5c\u4e0d\u95fb\uff0c\u4ecd\u7136\u7d27\u8ffd\u4e0d\u820d\uff0c\u539f\u6765\u4ed6\u4e00\u65b9\u9762\u56fa\u7136\u662f\u4e3a\u4e86\u8981\u8ffd\u725b\u5148\u751f\uff0c \u53e6\u4e00\u65b9\u9762\uff0c\u5374\u4e5f\u662f\u85c9\u6b64\u673a\u4f1a\uff0c\u907f\u5f00\u4e86\u5728\u8fd9\u79cd\u5c34\u5c2c\u7684\u573a\u9762\u4e4b\u4e0b\u4e0e\u6b66\u7384\u971c\u76f8\u53d9\u3002 \u3000\u3000\u7981\u536b\u519b\u4e5f\u6709\u51e0\u4e2a\u9ad8\u624b\u8ffd\u51fa\u6765\uff0c\u4f46\u8ffd\u4e86\u4e00\u4f1a\uff0c\u4fbf\u9010\u6e10\u843d\u540e\uff0c\u53ea\u6709\u674e\u9038\u8fd8\u5728\u725b\u5148\u751f\u8eab\u540e\uff0c \u7247\u523b\u4e4b\u95f4\uff0c\u8ffd\u5230\u4e86\u50fb\u9759\u7684\u6240\u5728\uff0c\u725b\u5148\u751f\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u674e\u9038\uff0c\u6b66\u5219\u5929\u4e5f\u662f\u4f60\u7684\u4ec7\u4eba\uff0c\u4f60 \u4f55\u82e6\u4e3a\u5979\u5356\u547d\uff1f\u201d\u53cd\u624b\u4e00\u626c\uff0c\u4e00\u5927\u7247\u7ec6\u5982\u725b\u6bdb\u7684\u6885\u82b1\u9488\u5316\u6210\u4e86\u4e00\u7bf7\u94f6\u96e8\uff0c\u5411\u674e\u9038\u7f69\u4e0b\u6765\uff0c \u674e\u9038\u6325\u52a8\u5b9d\u5251\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u6885\u82b1\u9488\u6295\u5165\u5251\u5149\u5708\u4e2d\uff0c\u90fd\u7ed9\u7ede\u6210\u7c89\u5c51\uff01 \u3000\u3000\u725b\u5148\u751f\u53cc\u6307\u4e00\u5f39\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u53d1\u51fa\u4e00\u679a\u900f\u9aa8\u9489\uff0c\u900f\u9aa8\u9489\u7684\u4efd\u91cf\u6bd4\u6885\u82b1\u9488\u6c89\u91cd\uff0c \u52b2\u529b\u4e5f\u5f53\u7136\u5927\u5f97\u591a\uff0c\u7adf\u7136\u7a7f\u8fc7\u5251\u5149\u7ec7\u6210\u7684\u5149\u5e55\uff0c\u674e\u9038\u6325\u5251\u5c06\u5b83\u6253\u843d\uff0c\u4e5f\u81ea\u89c9\u5f97\u864e\u53e3\u4e00\u9ebb\uff0c \u5251\u5c16\u9707\u52a8\u5f97\u55e1\u55e1\u4f5c\u54cd\u3002\u900f\u9aa8\u9488\u4ece\u4ed6\u9f3b\u5c16\u98de\u8fc7\uff0c\u9690\u9690\u95fb\u5f97\u4e00\u80a1\u8165\u98ce\u3002 \u3000\u3000\u674e\u9038\u5927\u6012\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u4e00\u62db\u201c\u9e4f\u640f\u4e5d\u9704\u201d\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u51cc\u7a7a\u523a\u4e0b\uff0c\u725b\u5148\u751f\u65e9\u5df2\u8131\u4e0b \u8eab\u4e0a\u7684\u957f\u886b\uff0c\u5f53\u6210\u5175\u5668\u4f7f\u7528\uff0c\u626c\u7a7a\u4e00\u5377\uff0c\u674e\u9038\u7684\u5251\u5c16\u5728\u4ed6\u7684\u5e03\u8863\u4e0a\u4e00\u6ed1\u800c\u8fc7\uff0c\u52b2\u529b\u7adf\u7136 \u4f7f\u4e0d\u51fa\u6765\uff0c\u725b\u5148\u751f\u5377\u4e0d\u7740\u4ed6\u7684\u5b9d\u5251\uff0c\u4ea6\u662f\u5fc3\u4e2d\u4e00\u51db\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u674e\u9038\u500f\u7684\u53c8\u53d8\u62db\u523a\u51fa\uff0c\u8fd9\u4e00\u62db\u7528\u7684\u662f\u5185\u5bb6\u9634\u67d4\u4e4b\u529b\uff0c\u725b\u5e03\u8863\u7684\u957f \u886b\u4e00\u9707\uff0c\u4e24\u80a1\u9634\u67d4\u4e4b\u529b\u4e00\u7c98\uff0c\u674e\u9038\u7684\u5b9d\u5251\u950b\u5229\u975e\u5e38\uff0c\u4f46\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u725b\u5e03\u8863\u7684\u957f \u886b\u5f00\u4e86\u4e00\u9053\u88c2\u7f1d\uff0c\u6025\u5fd9\u540e\u9000\uff0c\u674e\u9038\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff1f\u201d\u4e00\u62db\u201c\u6a2a\u6307\u5929\u5357\u201d\uff0c\u8ddf\u8e2a\u8ffd\u51fb\uff0c \u725b\u5e03\u8863\u559d\u9053\uff1a\u201c\u4f60\u771f\u4e2a\u8981\u7ed9\u6b66\u5219\u5929\u5356\u547d\uff1f\u201d\u5ffd\u5730\u53c8\u53d1\u51fa\u4e00\u5b97\u6697\u5668\uff0c\u5f62\u4f3c\u5706\u7403\uff0c\u545c\u545c\u4f5c\u54cd\uff0c \u8ddd\u79bb\u6781\u8fd1\uff0c\u95ea\u907f\u4e0d\u5f00\uff0c\u674e\u9038\u4e00\u5251\u5c06\u5b83\u5288\u7834\uff0c\u7a81\u7136\u95f4\u98de\u51fa\u8bb8\u591a\u9ec4\u8c46\u822c\u5927\u5c0f\u7684\u8dcc\u83b2\u5b50\uff0c\u6709\u5982 \u51b0\u96f9\u4e71\u843d\uff0c\u674e\u9038\u6325\u8d77\u4e00\u5708\u94f6\u8679\uff0c\u817e\u8eab\u62d4\u8d77\uff0c\u4f46\u89c9\u80a9\u5934\u5fae\u5fae\u4e00\u9ebb\uff0c\u5df2\u7ed9\u4e00\u9897\u94c1\u83b2\u5b50\u6253\u4e2d\u3002 \u3000\u3000\u725b\u5148\u751f\u54c8\u54c8\u5927\u7b11\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u8ffd\u4e48\uff1f\u201d\u4e00\u626c\u624b\u4e00\u4e2a\u5706\u7403\u53c8\u98de\u8fc7\u6765\uff0c\u674e\u9038\u659c\u523a\u95ea \u5f00\uff0c\u725b\u5e03\u8863\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u867d\u7136\u5b66\u4e86\u4e56\uff0c\u53ef\u60dc\u4ecd\u672b\u5b66\u5168\uff01\u201d\u626c\u624b\u4e00\u67c4\u98de\u5200\uff0c\u5c06\u90a3\u5706\u7403\u51fb\u7834\uff0c \u94c1\u83b2\u5b50\u53c8\u7eb7\u7eb7\u5411\u674e\u9038\u5c04\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u7a81\u7136\u95f4\u4ece\u5c4b\u9876\u4e0a\u8df3\u4e0b\u4e00\u4e2a\u4eba\uff0c\u624b\u6267\u4e00\u9762\u5927\u65d7\uff0c\u5927\u65d7\u4e00\u5c55\uff0c\u5c06 \u94c1\u83b2\u5b50\u5168\u90fd\u5377\u53bb\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u674e\u9038\u5df2\u7ed9\u4e86\u725b\u5148\u751f\u5b9d\u5251\u4e00\u62db\u201c\u60ca\u6d9b\u62cd\u5cb8\u201d\uff0c\u5411\u4ed6\u4e0b\u4e09\u8def\u5377 \u5230\uff0c\u725b\u5148\u751f\u7684\u957f\u6749\u75be\u5fd9\u5f80\u4e0b\u4e00\u88f9\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u624b\u6267\u5927\u65d7\u7684\u90a3\u4e2a\u6b66\u571f\u5df2\u5982\u98de\u8d76\u81f3\uff0c\u5927 \u65d7\u6325\u821e\uff0c\u53cd\u800c\u628a\u725b\u5148\u751f\u7684\u957f\u886b\u88f9\u4f4f\uff0c\u725b\u5148\u751f\u7684\u811a\u8ddf\u4e2d\u4e86\u674e\u9038\u4e00\u5251\uff0c\u767b\u65f6\u88ab\u90a3\u4e2a\u6b66\u58eb\u7684\u5927 \u65d7\u5377\u4e86\u8d77\u6765\uff0c\u53ea\u542c\u5f97\u4ed6\u4e00\u58f0\u60e8\u547c\uff0c\u6b66\u58eb\u5c06\u65d7\u6296\u5f00\uff0c\u628a\u725b\u5e03\u8863\u63b7\u843d\u5730\u4e0b\u3002\u4ed6\u88ab\u90a3\u6b66\u58eb\u7684\u5927 \u65d7\u7d27\u7d27\u4e00\u675f\uff0c\u808b\u9aa8\u65ad\u4e86\u4e24\u6761\uff0c\u4eba\u4e5f\u65e9\u5df2\u6655\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u6b66\u58eb\u539f\u6765\u662f\u795e\u6b66\u8425\u4e09\u5927\u9ad8\u624b\u4e4b\u4e00\u7684\u79e6\u6e5b\uff0c\u674e\u9038\u4ee5\u524d\u5192\u540d\u6295\u519b\uff0c\u66fe\u548c\u4ed6\u540c\u8fc7\u4e8b\uff0c \u79e6\u6e5b\u751f\u64d2\u4e86\u725b\u5148\u751f\uff0c\u671b\u4e86\u674e\u9038\u4e00\u773c\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u662f\u8c01\uff0c\u54b1\u4eec\u597d\u4f3c\u5728\u54ea\u91cc\u89c1\u8fc7\u4f3c\u7684\uff1f\u201d \u674e\u9038\u6539\u4e86\u9762\u5bb9\uff0c\u4ed6\u4e00\u65f6\u8ba4\u4e0d\u51fa\u6765\u3002\u674e\u9038\u9053\uff1a\u201c\u6211\u59d3\u5f20\uff0c\u524d\u624d\u6765\u7684\u3002\u4f60\u628a\u8fd9\u53ae\u9001\u53bb\u7ed9\u674e\u90fd \u5c09\u5427\uff0c\u6211\u4e5f\u8be5\u56de\u53bb\u8986\u547d\u4e86\u3002\u201d\u79e6\u6e5b\u6b63\u60f3\u95ee\u4ed6\u8986\u4ec0\u4e48\u547d\uff1f\u674e\u9038\u5df2\u5c55\u5f00\u9646\u5730\u98de\u817e\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c \u5982\u98de\u8dd1\u4e86\u3002\u79e6\u6e5b\u9887\u611f\u5947\u602a\uff0c\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u662f\u674e\u9038\uff0c\u4e3a\u4e86\u6015\u4ed6\u8ba4\u51fa\uff0c\u907f\u5f00\u4ed6\u7684\u3002 \u3000\u3000\u674e\u9038\u56de\u5230\u957f\u5b59\u6cf0\u7684\u5e9c\u90b8\uff0c\u957f\u5b59\u6cf0\u5c1a\u672a\u56de\u6765\u3002\u674e\u9038\u72ec\u5750\u4e66\u623f\uff0c\u5fc3\u4e71\u5982\u9ebb\uff0c\u60f3\u8d77\u4e86\u4e0a\u5b98 \u5a49\u513f\u53c8\u60f3\u8d77\u4e86\u6b66\u7384\u971c\uff0c\u6b63\u81ea\u795e\u601d\u8ff7\u60f3\uff0c\u5ffd\u5730\u6709\u4eba\u63ed\u5f00\u5e18\u5b50\uff0c\u8d70\u5165\u4ed6\u7684\u623f\u4e2d\u3002 \u3000\u3000\u6302\u5728\u5899\u4e0a\u7684\u5706\u955c\u73b0\u51fa\u4e00\u4e2a\u5c11\u5973\u7684\u5f71\u5b50\uff0c\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u7384\u971c\uff01\u201d\u6b66\u7384 \u971c\u7b11\u9053\u4e86\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u662f\u6211\u5427\uff1f\u6211\u4e5f\u60f3\u4e0d\u5230\u4f60\u4f1a\u56de\u6765\uff0c\u654f\u513f\u597d\u5417\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u597d\uff0c\u590f \u4faf\u524d\u8f88\u5df2\u7b54\u5141\u6536\u4ed6\u4e3a\u5f92\u4e86\uff0c\u8fd9\u5b69\u5b50\u4ed6\u4e5f\u5f88\u6302\u5ff5\u4f60\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u5750\u4e86\u4e0b\u6765\uff0c\u5411\u674e\u9038\u671b\u4e86\u4e00\u773c\u3002\u67f3\u7709\u5fae\u8d5e\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u70b9\u4f24\uff1f\u201d\u674e\u9038\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u662f\u4e2d\u4e86\u725b\u5148\u751f\u7684\u4e00\u7c92\u94c1\u83b2\u5b50\uff0c\u4e0d\u7b97\u5389\u5bb3\uff0c\u94c1\u83b2\u5b50\u867d\u7136\u662f\u6709\u6bd2\u7684\uff0c\u4f46\u5df2\u7ed9\u6211\u8fd0\u5185 \u529b\u5c06\u6bd2\u903c\u51fa\u6765\u4e86\u3002\u201d\u6b66\u7384\u971c\u53d6\u51fa\u4e00\u7c92\u78a7\u7eff\u8272\u7684\u4e39\u4e38\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5e08\u5144\u6240\u70bc\u7684\u78a7\u7075\u4e39\uff0c \u80fd\u89e3\u767e\u6bd2\uff0c\u6211\u6015\u4f60\u4f59\u6bd2\u672a\u6e05\uff0c\u5c0f\u5fc3\u4e3a\u4e0a\uff0c\u4f60\u5c31\u518d\u670d\u4e00\u7c92\u5427\u3002\u201d \u3000\u3000\u674e\u9038\u6df1\u611f\u5979\u5173\u5207\u4e4b\u60c5\uff0c\u867d\u7136\u89c9\u5f97\u51ed\u7740\u81ea\u5df1\u7684\u5185\u529f\uff0c\u53ef\u4ee5\u4e0d\u5fc5\u518d\u8981\u89e3\u836f\uff0c\u8fd8\u662f\u4f9d\u8a00\u670d \u4e86\u3002\u4e24\u4eba\u76ee\u5149\u76f8\u63a5\uff0c\u4e07\u8bed\u5343\u8a00\uff0c\u4e0d\u77e5\u4ece\u54ea\u91cc\u8bf4\u8d77\uff1f\u8fc7\u4e86\u534a\u664c\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u56de\u6765\u5df2\u6709 \u591a\u5929\uff0c\u957f\u5b89\u662f\u6bd4\u4ee5\u524d\u597d\u4e86\u8fd8\u662f\u574f\u4e86\uff0c\u4f60\u603b\u53ef\u4ee5\u77a7\u51fa\u4e00\u4e9b\u6765\u5427\uff1f\u201d\u674e\u9038\u9ed8\u7136\u4e0d\u8bed\u3002\u6b66\u7384\u971c \u9053\uff1a\u201c\u5176\u5b9e\u4e0d\u7ba1\u662f\u597d\u662f\u574f\uff0c\u603b\u80dc\u4e8e\u6258\u5e87\u5f02\u56fd\uff0c\u8001\u6b7b\u5f02\u4e61\u3002\u201d\u674e\u9038\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f \u8bb8\u5c06\u6765\u6211\u4f1a\u5e26\u654f\u513f\u56de\u6765\uff0c\u4f46\u957f\u5b89\u5374\u4e0d\u662f\u6211\u4e45\u5c45\u4e4b\u5730\u3002\u6211\u60f3\u89c1\u4e86\u5a49\u513f\u4e00\u9762\u4e4b\u540e\uff0c\u6211\u5c31\u8981\u79bb \u5f00\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u6b66\u7384\u971c\u5ffd\u7136\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u6709\u4e00\u53e5\u8bdd\u6211\u4e0d\u77e5\u8be5\u4e0d\u8be5\u95ee\u4f60\uff1f\u201d\u773c\u5149\u4e2d\u663e\u9732\u7740\u5f02\u6837\u7684\u8868\u60c5\uff0c \u674e\u9038\u5fc3\u5934\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u8fd8\u6709\u4ec0\u4e48\u8bdd\u4e0d\u53ef\u8bf4\u7684\uff1f\u8bf4\u5427\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u521a \u5165\u4e2d\u5e74\uff0c\u654f\u513f\u4e5f\u8fd8\u8981\u4eba\u7167\u987e\uff0c\u6b7b\u8005\u5df2\u77e3\uff0c\u4f60\u53ef\u66fe\u60f3\u8fc7\u7eed\u5f26\u4e48\uff1f\u201d\u674e\u9038\u5fc3\u5f26\u98a4\u52a8\uff0c\u8f7b\u8f7b\u6447 \u4e86\u6447\u5934\uff0c\u6b66\u7384\u971c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5a49\u513f\u7edd\u4ee3\u624d\u534e\uff0c\u53c8\u662f\u81ea\u5e7c\u548c\u4f60\u4e00\u8d77\u957f\u5927\u7684\uff0c\u4f60\u4eec\u672c\u6765\u53ef\u4ee5 \u662f\u4e00\u5bf9\u5929\u751f\u4f73\u5076\u3002\u201d\u674e\u9038\u8fd9\u624d\u77e5\u9053\u5979\u539f\u6765\u662f\u64ae\u5408\u5a49\u513f\uff0c\u66f4\u89c9\u5fc3\u4e71\u5982\u9ebb\uff0c\u8fc7\u4e86\u534a\u664c\u8bf4\u9053\uff1a \u201c\u6709\u4e00\u4e2a\u4eba\u5f88\u7231\u6155\u5a49\u513f\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u77e5\u9053\uff0c\u662f\u957f\u5b59\u6cf0\u3002\u4f46\u5a5a\u59fb\u4e4b\u4e8b\uff0c\u5c82 \u80fd\u52c9\u5f3a\uff0c\u5a49\u513f\u5c0a\u656c\u4ed6\uff0c\u4f46\u5374\u4e0d\u613f\u610f\u5ac1\u4ed6\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u524d\u51e0\u5929\u6211\u5f97\u5230\u5a49\u513f\u4e00\u9996\u8bd7\uff0c\u770b\u8bd7 \u4e2d\u7684\u610f\u601d\uff0c\u5979\u4f3c\u4e4e\u8981\u5ac1\u7ed9\u4e00\u4e2a\u5979\u6240\u4e0d\u613f\u610f\u5ac1\u7684\u4eba\uff0c\u6709\u8fd9\u56de\u4e8b\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u82e5\u662f \u548c\u5a49\u513f\u7ed3\u5408\uff0c\u4f60\u4eec\u4e24\u4eba\u90fd\u53ef\u4ee5\u7ec8\u751f\u5feb\u4e50\u3002\u82e5\u662f\u4f60\u4e0d\u5a36\u5979\uff0c\u4e5f\u8bb8\u5979\u4f1a\u5ac1\u7ed9\u4e00\u4e2a\u5979\u6240\u4e0d\u6b22\u559c \u7684\u4eba\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u5a49\u513f\u600e\u4f1a\u505a\u5979\u4e0d\u613f\u610f\u505a\u7684\u4e8b\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u5979\u4e0d\u559c\u6b22\u8fd9\u4e2a\u4eba\uff0c\u4f46 \u5374\u662f\u613f\u610f\u5ac1\u7ed9\u4ed6\u7684\u3002\u9664\u975e\u4f60\u5a36\u5979\uff0c\u5426\u5219\u5979\u4e00\u5b9a\u4f1a\u5ac1\u7ed9\u8fd9\u4e2a\u4eba\uff0c\u800c\u4e14\u4e5f\u4e00\u5b9a\u662f\u7ec8\u751f\u90c1\u90c1\u4e0d \u6b22\uff0c\u4f60\u518d\u4ed4\u7ec6\u7684\u60f3\u60f3\u5427\uff01\u201d \u3000\u3000\u674e\u9038\u7684\u8111\u6d77\u4e2d\u6d6e\u8d77\u4e86\u957f\u5b59\u58c1\u7684\u5f71\u5b50\uff0c\u5fc3\u60f3\uff1a\u201c\u58c1\u59b9\u5c38\u9aa8\u672a\u5bd2\uff0c\u6211\u600e\u5fcd\u53e6\u8c08\u5a5a\u5ac1\uff1f\u201d \u6b66\u7384\u971c\u9053\uff1a\u201c\u4e5f\u7f62\uff0c\u4f60\u4e00\u65f6\u59d4\u51b3\u4e0d\u4e0b\uff0c\u8ba9\u4f60\u5148\u89c1\u4e86\u5a49\u513f\u4e5f\u597d\u3002\u4e0d\u8fc7\uff0c\u6211\u5e0c\u671b\u4f60\u5728\u89c1\u5230\u5a49 \u513f\u7684\u65f6\u5019\uff0c\u5148\u8981\u6253\u597d\u4e86\u4e3b\u610f\uff0c\u5a49\u513f\u4e00\u751f\u7684\u547d\u8fd0\uff0c\u5c31\u8981\u770b\u4f60\u5982\u4f55\u5904\u7406\u4e86\u3002\u597d\uff0c\u4f60\u4eca\u665a\u5c31\u53bb \u89c1\u5979\u5427\uff01\u201d \u3000\u3000\u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u53ef\u5e76\u4e0d\u662f\u957f\u5b59\u6cf0\u5165\u5bab\u8f6e\u9053\u4e4b\u671f\u554a\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u6211 \u5e26\u4f60\u8fdb\u53bb\u3002\u201d\u674e\u9038\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u5e26\u6211\u53bb\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u85cf\u5728\u6211\u7684\u8f66\u5b50 \u91cc\uff0c\u8c01\u4e5f\u4e0d\u6562\u641c\u67e5\uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u5c31\u8fdb\u53bb\u4e86\u3002\u201d\u674e\u9038\u9053\uff1b\u201c\u4f60\u59d1\u59d1\u77e5\u9053\u4e86\u6ca1\u6709\uff1f\u201d\u6b66 \u7384\u971c\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u544a\u8bc9\u5979\u3002\u201d\u674e\u9038\u795e\u8272\u8e0c\u8e87\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u82e5\u4eca\u665a\u4e0d\u53bb\uff0c\u4ee5\u540e\u518d \u627e\u673a\u4f1a\u5c31\u96be\u5f97\u591a\u4e86\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u5728\u9b4f\u738b\u5e9c\u5927\u95f9\u4e00\u573a\uff0c \u5929\u540e\u965b\u4e0b\u73b0\u5728\u8fd8\u65e0\u6687\u67e5\u95ee\u8be6\u7ec6\u60c5\u5f62\uff0c\u5c06\u6765\u4e00\u5b9a\u6709\u4eba\u544a\u8bc9\u5979\u7684\u3002\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u9e7f\u649e\uff0c\u53ea\u542c\u5f97\u6b66\u7384\u971c\u53c8\u9053\uff1a\u201c\u6211\u5df2\u548c\u5a49\u513f\u7ea6\u597d\uff0c\u5165\u5bab\u4e4b\u540e\uff0c\u4f60\u8eb2\u5728\u6211\u7684\u623f \u4e2d\uff0c\u4e8c\u66f4\u65f6\u5206\uff0c\u5979\u6765\u4f1a\u4f60\u3002\u6211\u53bb\u7eca\u4f4f\u59d1\u59d1\uff0c\u6709\u4ec0\u4e48\u4e8b\u6211\u53ef\u4ee5\u66ff\u4f60\u4eec\u906e\u63a9\u3002\u4f60\u8d76\u5feb\u6362\u8fc7\u4e00 \u8eab\u8863\u670d\u5427\uff0c\u540e\u5bab\u53ef\u662f\u4e0d\u8bb8\u7537\u5b50\u8fdb\u53bb\u7684\u554a\uff0c\u4f60\u6700\u597d\u626e\u6210\u4e00\u4e2a\u5bab\u5973\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u7537\u5b50\u6c49\u5927 \u4e08\u592b\u5c82\u80fd\u6613\u5f01\u800c\u94a6\uff0c\u6211\u4e0d\u626e\u5bab\u5973\u3002\u201d\u6b66\u7384\u9732\u7b11\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u6253\u7d27\uff0c\u5f53\u4eca\u7684\u7687\u5e1d\u5c1a\u4e14\u662f \u5973\u4eba\uff0c\u4f60\u5374\u8fd8\u662f\u91cd\u7537\u8f7b\u5973\u3002\u597d\u5427\uff0c\u6211\u4e5f\u4e0d\u52c9\u5f3a\u4f60\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u8eab\u6b66\u58eb\u7684\u670d\u9970\u662f\u8981\u6362\u7684\uff0c\u6211 \u603b\u4e0d\u80fd\u5e26\u4e00\u4e2a\u6b66\u58eb\u8fdb\u6211\u7684\u95fa\u623f\u5440\u3002\u8fd9\u6837\u5427\uff0c\u4f60\u6253\u626e\u6210\u5c0f\u592a\u76d1\u7684\u6a21\u6837\uff0c\u548c\u6211\u540c\u8f66\u5165\u5bab\u3002\u201d \u5979\u65e9\u5c31\u51c6\u5907\u4e86\u4e00\u5957\u5b98\u5973\u7684\u5bab\u88c5\u548c\u4e00\u5957\u592a\u76d1\u7684\u9752\u8863\u5c0f\u5e3d\uff0c\u73b0\u5728\u674e\u9038\u4e0d\u613f\u6539\u626e\u5bab\u5973\uff0c\u5979\u5c31\u628a \u90a3\u5957\u592a\u76d1\u7684\u670d\u9970\u7ed9\u4ed6\uff0c\u7b11\u9053\uff1a\u201c\u59d4\u5c48\u4f60\u4e00\u4e0b\u5b50\uff0c\u6362\u597d\u4e86\u8863\u670d\uff0c\u5c31\u8bf7\u51fa\u6765\u3002\u201d\u8bf4\u7f62\u4fbf\u8d70\u51fa \u623f\u95f4\u53bb\u4e86\u3002 \u3000\u3000\u674e\u9038\u4e00\u7247\u832b\u7136\uff0c\u5fc3\u4e2d\u53ea\u662f\u76d8\u65cb\u7740\u4e00\u4e2a\u5ff5\u5934\u3002\u201c\u6211\u4eca\u665a\u8981\u89c1\u5a49\u513f\uff0c\u8981\u89c1\u5a49\u513f\uff01\u201d\u957f\u5b59 \u6cf0\u5ffd\u5730\u8d70\u4e86\u8fdb\u6765\uff0c\u5c06\u95e8\u8f7b\u8f7b\u63a9\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u7384\u971c\u5df2\u7ecf\u548c\u4f60\u8bf4\u597d\u4e86\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u8bf4\u597d\u4e86\uff0c \u6211\u4eca\u665a\u5c31\u89c1\u5a49\u513f\uff01\u54a6\uff0c\u4f60\u662f\u51e0\u65f6\u56de\u6765\u7684\uff1f\u6b66\u627f\u55e3\u7684\u4e8b\u60c5\uff0c\u6b66\u5219\u5929\u600e\u6837\u53d1\u843d\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a \u201c\u6211\u662f\u548c\u6b66\u7384\u971c\u4e00\u9053\u56de\u6765\u7684\uff0c\u6211\u77e5\u9053\u5979\u5df2\u7ecf\u66ff\u4f60\u5b89\u6392\u597d\u4e86\u3002\u6b66\u627f\u55e3\u7684\u4e8b\u60c5\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c \u5f20\u76f8\u56fd\u5df2\u7ecf\u9012\u4e0a\u594f\u6298\uff0c\u53c8\u6709\u6b66\u7384\u971c\u4f5c\u6307\u8bc1\uff0c\u6599\u60f3\u6b66\u627f\u55e3\u8fd9\u756a\u96be\u9003\u516c\u9053\u3002\u201d \u3000\u3000\u674e\u9038\u5306\u5306\u7684\u6362\u8fc7\u8863\u88f3\uff0c\u56de\u8fc7\u5934\u6765\u3002\u957f\u5b59\u6cf0\u4e00\u7247\u6005\u60af\u7684\u795e\u60c5\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u674e\u5144\uff0c\u4f60 \u5bf9\u58c1\u59b9\u751f\u6b7b\u4e0d\u5fd8\uff0c\u6211\u611f\u6fc0\u5f97\u5f88\u3002\u4f46\u6b7b\u8005\u4e0d\u80fd\u590d\u751f\uff0c\u654f\u7525\u4e5f\u8981\u4eba\u7167\u6599\u3002\u773c\u524d\u6709\u5408\u9053\u7684\u4eba\uff0c \u6211\u529d\u4f60\u8fd8\u662f\u7eed\u5f26\u7684\u597d\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5a49\u513f\u4e00\u76f4\u5c06\u6211\u5f53\u4f5c\u5144\u957f\u770b\u5f85\uff0c\u58c1\u59b9\u6b7b\u4e86\uff0c \u6211\u5c31\u53ea\u6709\u5979\u4e00\u4e2a\u59b9\u59b9\u4e86\uff0c\u6211\u4e0d\u613f\u610f\u5a49\u513f\u6291\u90c1\u800c\u7ec8\uff0c\u6211\u5931\u6389\u4e86\u4e00\u4e2a\u59b9\u59b9\uff0c\u4e0d\u80fd\u518d\u5931\u6389\u5979\u4e86\u3002 \u552f\u6709\u4f60\u53ef\u4ee5\u4ee4\u5979\u5feb\u4e50\uff0c\u6211\u4e5f\u4f46\u6c42\u5979\u5f97\u5230\u5feb\u4e50\u3002\u674e\u5144\uff0c\u4f60\u5e94\u8be5\u660e\u767d\u6211\u7684\u610f\u601d\u5427\u3002\u201d\u674e\u9038\u70b9 \u70b9\u5934\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u8fc7\u2014\u2014\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4e0d\u8981\u518d\u8bf4\u4ec0\u4e48\u4e0d\u8fc7\u4e86\uff0c\u4f60\u4eec\u5feb\u4e50\uff0c \u6211\u5c31\u5feb\u4e50\u3002\u4e00\u5207\u90fd\u8981\u4e3a\u5a49\u513f\u7740\u60f3\u3002\u597d\u5427\uff0c\u4f60\u6362\u597d\u8863\u88f3\u4e86\uff0c\u5feb\u4e9b\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u957f\u5b59\u6cf0\u773c\u6709\u4e24\u9897\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u6025\u5fd9\u4e3e\u8896\u62ed\u53bb\uff0c\u4f46\u674e\u9038\u5df2\u5728\u955c\u4e2d\u77a7\u89c1\u4e86\u3002\u674e\u9038\u56de\u8fc7\u5934 \u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6cf0\u5144\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u4ee4\u4f60\u5931\u671b\u7684\u3002\u201d\u8fd9\u8bf4\u8bdd\u6a21\u68f1\u4e24\u53ef\uff0c\u4f46\u957f\u5b59\u6cf0\u5df2 \u65e0\u6687\u63a8\u6572\u4e86\uff0c\u7d27\u7d27\u63e1\u7740\u4ed6\u7684\u624b\u9053\uff1a\u201c\u4f60\u660e\u767d\u6211\u7684\u5fc3\u610f\u5c31\u597d\uff0c\u597d\u4e86\uff0c\u4f60\u53bb\u89c1\u5a49\u513f\u5427\uff01\u201d \u3000\u3000\u674e\u9038\u85cf\u5728\u6b66\u7384\u971c\u7684\u8f66\u5b50\u91cc\uff0c\u4e00\u8def\u9a70\u5165\u5bab\u95e8\uff0c\u8f66\u8f6e\u75be\u8f6c\u5982\u98de\uff0c\u674e\u9038\u7684\u5fc3\u4e5f\u4f3c\u968f\u7740\u8f66\u8f6e \u8f6c\u52a8\u3002\u957f\u5b59\u6cf0\u7684\u610f\u601d\u660e\u767d\u5f97\u5f88\uff0c\u4e43\u662f\u5e0c\u671b\u4ed6\u4e0e\u5a49\u513f\u7ed3\u5408\uff0c\u5b81\u613f\u81ea\u5df1\u9ed8\u9ed8\u5fcd\u53d7\u54c0\u4f24\u3002\u674e\u9038 \u5fc3\u4e71\u5982\u9ebb\uff0c\u4ed6\u4e0d\u5fcd\u957f\u5b59\u6cf0\u4f24\u5fc3\uff0c\u4f46\u4e5f\u4e0d\u613f\u5a49\u513f\u90c1\u90c1\u7ec8\u8001\u3002\u201c\u5a5a\u59fb\u4e4b\u4e8b\uff0c\u5c82\u80fd\u52c9\u5f3a\u4e86\uff0c\u5979 \u771f\u6b63\u6b22\u559c\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u90a3\u5c31\u662f\u4f60\u554a\uff01\u201d\u6b66\u7384\u971c\u521a\u624d\u7684\u8bf4\u8bdd\u53c8\u5728\u4ed6\u7684\u8033\u8fb9\u54cd\u8d77\u6765\uff0c\u4ed6\u77e5 \u9053\u5a49\u513f\u7684\u6027\u683c\uff0c\u5979\u4e0d\u613f\u5ac1\u957f\u5b59\u6cf0\uff0c\u529d\u4e5f\u6ca1\u6709\u7528\u7684\u3002\u53ef\u662f\u521a\u624d\u542c\u6b66\u7384\u971c\u7684\u8bf4\u8bdd\uff0c\u82e5\u662f\u4ed6\u4e0d \u5a36\u5a49\u513f\u7684\u8bdd\uff0c\u5a49\u513f\u4e00\u5b9a\u4f1a\u5ac1\u7ed9\u53e6\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u662f\u957f\u5b59\u6cf0\uff0c\u5979\u867d\u7136\u4e0d\u6b22\u559c\u8fd9\u4e2a\u4eba\uff0c\u4f46 \u5374\u613f\u610f\u5ac1\u7ed9\u4ed6\uff01\u201d\u8fd9\u662f\u4ec0\u4e48\u539f\u6545\u5462\uff1f\u9976\u662f\u674e\u9038\u7edd\u9876\u806a\u660e\uff0c\u5374\u4e5f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u674e\u9038\u85cf\u5728\u8f66\u53a2\u540e\u9762\uff0c\u4e0d\u80fd\u548c\u6b66\u7384\u971c\u4ea4\u8c08\uff0c\u4ed6\u671b\u7740\u6b66\u7384\u971c\u7684\u80cc\u5f71\uff0c\u4e0d\u7981\u5fc3\u4e71\u5982\u9ebb\uff0c\u4ed6 \u7231\u60dc\u4e0a\u5b98\u5a49\u513f\u7684\u7edd\u4e16\u624d\u534e\uff0c\u8981\u662f\u5a49\u513f\u6ca1\u6709\u5f52\u987a\u6b66\u5219\u5929\uff0c\u800c\u81ea\u5df1\u5f53\u5e74\u53c8\u8feb\u4e8e\u5f62\u52bf\uff0c\u4e0d\u5f97\u4e0d \u4e0e\u957f\u5b59\u58c1\u7ed3\u5a5a\u7684\u8bdd\uff0c\u4e5f\u8bb8\u4ed6\u65e9\u5df2\u4e0e\u5a49\u513f\u7ed3\u5408\u4e86\u3002\u7136\u800c\u73b0\u5728\u2014\u2014\u5509\uff0c\u8f6c\u773c\u5c06\u8fd1\u5341\u5e74\uff0c\u5341\u5e74 \u6765\u7684\u53d8\u5316\u53c8\u662f\u5982\u6b64\u4e4b\u5927\uff01\u4ed6\u5728\u5a49\u513f\u4e4b\u540e\u78b0\u5230\u4e86\u6b66\u7384\u971c\uff0c\u5728\u6b66\u7384\u971c\u4e4b\u540e\u53c8\u78b0\u5230\u4e86\u957f\u5b59\u58c1\uff0c \u66f4\u60f3\u4e0d\u5230\u7684\u662f\u4e0e\u957f\u5b59\u58c1\u6210\u4e3a\u592b\u5987\uff0c\u800c\u957f\u5b59\u58c1\u53c8\u662f\u4e3a\u4ed6\u6b7b\u7684\uff01 \u3000\u3000\u5f80\u4e8b\u5386\u5386\uff0c\u91cd\u4e0a\u5fc3\u5934\uff0c\u674e\u9038\u671b\u7740\u6b66\u7384\u971c\u7684\u80cc\u5f71\uff0c\u4e0d\u7981\u5e7d\u5e7d\u7684\u53f9\u4e86\u53e3\u6c14\u3002\u73b0\u5728\u957f\u5b59\u58c1 \u5df2\u7ecf\u6b7b\u4e86\uff0c\u4ed6\u672c\u6765\u51b3\u610f\u72ec\u8eab\u7ec8\u8001\u4e86\uff0c\u8c01\u77e5\u53c8\u78b0\u5230\u4e86\u8fd9\u6869\u4e8b\u60c5\u3002\u4e0a\u5b98\u5a49\u513f\u624d\u534e\u7edd\u4e16\uff0c\u4ed6\u53c8 \u600e\u5fcd\u89c1\u5979\u5f69\u51e4\u968f\u9e26\uff1f\u5a49\u513f\u548c\u6b66\u7384\u971c\u7684\u5f71\u5b50\u540c\u65f6\u5728\u4ed6\u8111\u6d77\u4e2d\u6cdb\u8d77\uff0c\u5a49\u513f\u548c\u4ed6\u7684\u6027\u683c\u8f83\u4e3a\u76f8 \u6295\uff0c\u800c\u7384\u971c\u5bf9\u4ed6\u7684\u611f\u60c5\u5219\u66f4\u4e3a\u6df1\u539a\uff0c\u674e\u9038\u53c8\u662f\u4e00\u9635\u8ff7\u832b\uff0c\u4f46\u5c3d\u7ba1\u4ecd\u5728\u8e0c\u8e87\uff0c\u90a3\u72ec\u8eab\u7ec8\u8001 \u7684\u51b3\u5fc3\u5df2\u662f\u6709\u70b9\u52a8\u6447\u4e86\uff01 \u3000\u3000\u6b66\u7384\u971c\u7684\u5ea7\u8f66\u6beb\u65e0\u963b\u788d\u7684\u72ec\u5165\u540e\u5bab\uff0c\u6b66\u7384\u971c\u5728\u5bab\u4e2d\u4f4f\u7684\u65f6\u95f4\u5f88\u5c11\uff0c\u5979\u559c\u6b22\u6e05\u9759\uff0c\u6b66 \u5219\u5929\u5728\u592a\u6db2\u6c60\u8fb9\u7684\u7af9\u6797\u91cc\u62e8\u4e86\u4e00\u6240\u4f4f\u5b85\u7ed9\u5979\uff0c\u56e0\u4e3a\u4e0d\u5e38\u4f4f\u7684\u539f\u6545\uff0c\u670d\u6043\u7684\u5b98\u5973\u53ea\u6709\u51e0\u4e2a \u4eba\uff0c\u5176\u4e2d\u4e24\u4e2a\u8fd8\u662f\u5979\u5e26\u6765\u7684\u5fc3\u8179\u5a62\u5973\uff0c\u674e\u9038\u626e\u6210\u4e00\u4e2a\u592a\u76d1\u7684\u6a21\u6837\u968f\u5979\u8fdb\u53bb\uff0c\u5e76\u6ca1\u6709\u5f15\u8d77 \u600e\u4e48\u6ce8\u610f\uff0c\u5176\u65f6\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u6b66\u7384\u971c\u5c06\u674e\u9038\u5b89\u987f\u623f\u4e2d\uff0c\u5429\u5490\u4e86\u5fc3\u8179\u5a62\u5973\u51e0\u53e5\uff0c\u4fbf\u51fa\u53bb \u4e86\u3002 \u3000\u3000\u674e\u9038\u72ec\u5904\u623f\u4e2d\uff0c\u542c\u5bab\u4e2d\u7389\u6f0f\u5df2\u8fd1\u4e8c\u66f4\uff0c\u5fc3\u5934\u6026\u6026\u8df3\u52a8\uff0c\u8fc7\u4e86\u4e00\u523b\uff0c\u5ffd\u542c\u5f97\u6709\u811a\u6b65\u58f0 \u4f20\u6765\uff0c\u597d\u4f3c\u4e0d\u6b62\u4e00\u4e2a\u4eba\uff0c\u674e\u9038\u6014\u4e86\u4e00\u6014\uff0c\u614c\u5fd9\u9003\u5230\u5e10\u540e\uff0c\u63a5\u7740\u542c\u5f97\u4e00\u4e2a\u5a07\u67d4\u7684\u58f0\u97f3\u7b11\u9053\uff1a \u201c\u7384\u971c\u8868\u59d0\u7684\u4f4f\u5904\u771f\u4e0d\u9519\u5440\uff01\u201d \u3000\u3000\u6765\u7684\u662f\u6b66\u5219\u5929\u548c\u5979\u7684\u5973\u513f\u592a\u5e73\u516c\u4e3b\uff0c\u674e\u9038\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5979\u4eec\u5df2\u7ecf\u77e5\u9053\u6211 \u5728\u8fd9\u513f\uff1f\u662f\u8c01\u628a\u98ce\u58f0\u6cc4\u6f0f\u51fa\u53bb\u4e86\u3002\u201d\u4f46\u542c\u5f97\u6b66\u5219\u5929\u7b11\u9053\uff1a\u201c\u4f60\u770b\u5979\u7684\u623f\u4e2d\u56fe\u4e66\u6ee1\u67b6\uff0c\u88c5 \u9970\u867d\u7136\u7b80\u5355\uff0c\u5374\u6bd4\u4f60\u9ad8\u96c5\u591a\u4e86\u3002\u201d\u592a\u5e73\u516c\u4e3b\u9053\uff1a\u201c\u7384\u971c\u8868\u59d0\u6587\u6b66\u53cc\u5168\uff0c\u6211\u600e\u80fd\u4e0e\u5979\u76f8\u6bd4\uff1f\u201d \u6b66\u5219\u5929\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u771f\u8be5\u5411\u4f60\u7684\u8868\u59d0\u548c\u5a49\u513f\u591a\u5b66\u4e00\u4e9b\u4e1c\u897f\u3002\u201d\u592a\u5e73\u516c\u4e3b\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d \u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u5176\u5b9e\u6211\u66f4\u60f3\u8ddf\u4f60\u591a\u5b66\u4e00\u4e9b\u6cbb\u56fd\u5e73\u5929\u4e0b\u7684\u672c\u9886\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4f60\u6709\u8fd9 \u4e2a\u5fd7\u613f\u4e5f\u672a\u5c1d\u4e0d\u597d\uff0c\u6cbb\u7406\u56fd\u5bb6\u6700\u7d27\u8981\u7684\u662f\u5927\u516c\u65e0\u79c1\uff0c\u7528\u4eba\u552f\u8d24\uff0c\u8fd8\u5e94\u8be5\u4f53\u6064\u767e\u59d3\u3002\u505a\u7687 \u5e1d\u5e76\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\uff01\u6211\u770b\u4f60\u8fd1\u51e0\u5e74\u6765\u8d8a\u6765\u8d8a\u8d2a\u56fe\u4eab\u53d7\u4e86\uff0c\u542c\u8bf4\u4f60\u6700\u8fd1\u8981\u4eac\u5146\u5c39\u7ed9 \u4f60\u5f81\u96c6\uff0c\u5927\u4fee\u9a78\u9a6c\u5e9c\uff0c\u6709\u8fd9\u56de\u4e8b\u5417\uff1f\u201d \u3000\u3000\u592a\u5e73\u516c\u4e3b\u4f4e\u4e0b\u4e86\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5e1d\u738b\u4e4b\u5bb6\uff0c\u5973\u513f\u5c31\u662f\u591a\u9020\u4e00\u5ea7\u5e9c\u90b8\uff0c\u4e5f\u5c0f\u662f\u4ec0\u4e48 \u5927\u4e8b\u3002\u53ea\u662f\u4e8b\u524d\u6ca1\u6709\u660e\u7980\u6bcd\u540e\uff0c\u8fd9\u662f\u5b69\u513f\u7684\u8fc7\u9519\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u4f60\u662f\u6211\u7684\u5973\u513f\uff0c \u66f4\u5e94\u8be5\u81ea\u77e5\u68c0\u70b9\uff0c\u5e1d\u738b\u4e4b\u5bb6\uff0c\u5c31\u53ef\u4ee5\u80e1\u4f5c\u975e\u4e3a\u5417\uff1f\u4f60\u5efa\u9020\u79c1\u4eba\u5e9c\u90b8\uff0c\u600e\u53ef\u4ee5\u52a8\u7528\u4eac\u5146\u5c39 \u6765\u5f81\u96c6\u6c11\u5feb\uff0c\u4f60\u8fd9\u662f\u4e71\u4e86\u671d\u5ef7\u7684\u6cd5\u5ea6\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u5c24\u5176\u73b0\u5728\u662f\u6218\u4e89\u8fc7\u540e\uff0c\u66f4\u4e0d\u53ef\u52b3\u6c11\u4f24 \u8d22\u3002\u201d\u592a\u5e73\u516c\u4e3b\u9762\u8272\u5927\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u6bcd\u4eb2\u8d23\u5907\u7684\u662f\uff0c\u5b69\u513f\u505c\u6b62\u4fee\u9020\u4fbf\u662f\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u5598\u4e86\u53e3\u6c14\uff0c\u7eed\u9053\uff1a\u201c\u8fd8\u6709\u4eba\u5bc6\u544a\u4f60\u7763\u5b98\u5356\u7235\uff0c\u4efb\u7528\u79c1\u4eba\uff0c\u7aa6\u6000\u8d1e\u3001\u8427\u81f3\u5fe0\u7b49 \u4eba\u90fd\u662f\u51ed\u85c9\u4f60\u7684\u52bf\u529b\u5f97\u5b98\u7684\u3002\u201d\u592a\u5e73\u516c\u4e3b\u5927\u5403\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u5988\u4f60\u4e0d\u8981\u542c\u522b\u4eba\u7684\u95f2\u8bdd\uff0c \u5b69\u513f\u7edd\u6ca1\u6709\u7763\u5b98\u5356\u7235\u7684\u4e8b\u60c5\uff0c\u5b69\u513f\u5f15\u8fdb\u4e00\u4e9b\u4eba\u4e5f\u4e0d\u8fc7\u662f\u4e3a\u6bcd\u4eb2\u5206\u52b3\u7f62\u4e86\u3002\u5a49\u513f\u4e0d\u662f\u4e5f\u63a8 \u8350\u8fc7\u59da\u5d07\u3001\u5b8b\u666f\u90fd\u7b49\u4eba\u4e48\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u59da\u5d07\u3001\u5b8b\u666f\u90fd\u662f\u6709\u624d\u80fd\u7684\u8d24\u4eba\uff0c\u7aa6\u6000\u8d1e\u7b49\u5c82 \u53ef\u4e0e\u4e4b\u76f8\u6bd4\uff1f\u201d\u592a\u5e73\u516c\u4e3b\u9053\uff1a\u201c\u7aa6\u6000\u8d1e\u4e0d\u662f\u4e5f\u6709\u76f8\u5f53\u624d\u5e72\u5417\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\u662f\u6211\u89c1 \u4ed6\u4eec\u6709\u70b9\u624d\u80fd\uff0c\u6211\u65e9\u5c31\u628a\u4ed6\u4eec\u8d2c\u6ef4\u4e86\u3002\u4ed6\u4eec\u5bf9\u6211\u8c04\u5a9a\u5949\u627f\uff0c\u6211\u4e00\u770b\u5c31\u77e5\u9053\u4ed6\u4eec\u662f\u5fc3\u672f\u4e0d \u6b63\u7684\u5c0f\u4eba\u3002\u201d \u3000\u3000\u592a\u5e73\u516c\u4e3b\u4e0d\u6562\u542d\u58f0\uff0c\u6b66\u5219\u5929\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ee5\u524d\u5bf9\u5f85\u4f60\u7684\u51e0\u4e2a\u54e5\u54e5\u5f88\u4e25\u5389\uff0c \u6709\u4eba\u8bf4\u6211\u6ca1\u6709\u9aa8\u8089\u4e4b\u60c5\u3002\u5176\u5b9e\u6211\u65e0\u975e\u662f\u60f3\u4e3a\u4f60\u4eec\u597d\u554a\uff01\u201d\u592a\u5e73\u516c\u4e3b\u4f4e\u58f0\u9053\uff1a\u201c\u5988\uff0c\u6211\u77e5 \u9053\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6211\u5fd9\u4e8e\u56fd\u4e8b\uff0c\u5bf9\u4f60\u4eec\u7684\u7ba1\u6559\u5176\u5b9e\u5df2\u662f\u5931\u4e4b\u8fc7\u5bbd\u4e86\u3002\u6211\u73b0\u5728\u5df2\u662f\u516b\u65ec \u8001\u5987\uff0c\u7cbe\u795e\u4e0d\u6bd4\u4ece\u524d\uff0c\u5bf9\u4f60\u4eec\u66f4\u7eb5\u5bb9\u4e86\u4e9b\uff0c\u8fd9\u662f\u6211\u7684\u8fc7\u9519\u3002\u5509\uff0c\u8981\u4e0d\u662f\u6211\u7ba1\u6559\u4e0d\u4e25\uff0c\u600e \u4f1a\u95f9\u51fa\u6b66\u627f\u55e3\u8fd9\u6869\u4e8b\u60c5\u3002\u201d \u3000\u3000\u592a\u5e73\u516c\u4e3b\u9053\uff1a\u201c\u627f\u55e3\u8868\u54e5\u4e00\u65f6\u4e0d\u5bdf\uff0c\u88ab\u5978\u7ec6\u6df7\u5165\u4ed6\u7684\u738b\u5e9c\uff0c\u8fd8\u671b\u6bcd\u4eb2\u4ece\u5bbd\u53d1\u843d\u3002\u201d \u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u7ba1\uff0c\u6211\u81ea\u6709\u5206\u6570\uff0c\u54a6\uff0c\u5a49\u513f\u600e\u4e48\u8fd8\u4e0d\u6765\u5462\uff1f\u201d\u542c\u4e86\u8fd9\u8bdd\uff0c\u674e\u9038\u53c8\u662f \u5fc3\u5934\u4e00\u9707\uff0c\u201c\u83ab\u975e\u5979\u5df2\u7ecf\u77e5\u9053\u4e86\u6211\u4e0e\u5a49\u513f\u5728\u8fd9\u91cc\u7ea6\u4f1a\uff1f\u201d \u3000\u3000\u592a\u5e73\u516c\u4e3b\u9053\uff1a\u201c\u5a49\u513f\u4e0d\u5728\u5979\u81ea\u5df1\u7684\u5c4b\u5b50\uff0c\u4e00\u5b9a\u662f\u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u6bcd\u4eb2\u4f60\u5c31\u518d\u5750\u4e00\u4f1a\uff1b \u770b\u770b\u5b69\u513f\u6599\u5f97\u51c6\u4e48\uff1f\u201d\u6b66\u4fa7\u5929\u7b11\u9053\uff1a\u201c\u8fd9\u70b9\u9b3c\u806a\u660e\u6211\u76f8\u4fe1\u4f60\u8fd8\u6709\uff0c\u4e0d\u8fc7\u5a49\u513f\u4e00\u5b9a\u6599\u4e0d\u5230 \u7384\u971c\u4e0d\u5728\u8fd9\u513f\uff0c\u5979\u89c1\u4e86\u6211\uff0c\u4ea6\u5f53\u610f\u60f3\u4e0d\u5230\u5427\uff01\u201d\u542c\u5979\u4eec\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u6b66\u5219\u5929\u5df2\u627e\u8fc7\u5a49\u513f \u4e00\u6b21\uff0c\u627e\u4e0d\u5230\u624d\u5230\u8fd9\u91cc\u6765\u7684\u3002\u674e\u9038\u7a0d\u7a0d\u5b89\u5fc3\uff0c\u4f46\u6b66\u7384\u971c\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u6b66\u7384\u971c\u672c\u6765\u5bf9\u674e\u9038 \u8bf4\u8fc7\uff0c\u5979\u662f\u8981\u53bb\u7eca\u4f4f\u6b66\u5219\u5929\uff0c\u8ba9\u4ed6\u4eec\u7684\u5e7d\u4f1a\u4e0d\u53d7\u60ca\u6270\u7684\uff0c\u4f55\u4ee5\u73b0\u5728\u6b66\u5219\u5929\u6765\u4e86\uff0c\u6b66\u7384\u971c \u5374\u4e0d\u968f\u6765\uff1f\u60f3\u81f3\u6b64\u5904\uff0c\u674e\u9038\u53c8\u4e0d\u7981\u5fc3\u5934\u60f4\u60f4\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u95e8\u5916\u6709\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\uff0c\u592a\u5e73\u516c\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u201c\u5988\uff0c\u4f60\u770b\u6211\u6599 \u5f97\u591a\u8c01\uff01\u201d\u679c\u7136\u63ed\u5e18\u800c\u5165\u7684\u6b63\u662f\u4e0a\u5b98\u5a49\u513f\u3002\u6b66\u5219\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5a49\u513f\uff0c\u6211\u7b49\u4f60\u8bb8\u4e45\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u5927\u4e3a\u60ca\u6115\uff0c\u6781\u529b\u5fcd\u4f4f\uff0c\u4e0d\u8ba9\u795e\u8272\u4e0a\u8868\u9732\u51fa\u6765\uff0c\u5979\u5411\u6b66\u5219\u5929\u884c\u8fc7\u4e86\u793c\uff0c\u95ee\u9053\uff1a \u201c\u5929\u540e\u965b\u4e0b\u53ef\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u6211\u529e\u4e48\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6b63\u662f\u6709\u4ef6\u7d27\u8981\u7684\u4e8b\u60c5\u8981\u548c\u4f60\u5546\u91cf\uff0c \u5b69\u513f\uff0c\u4f60\u4e14\u9000\u4e0b\uff0c\u8fc7\u4e00\u4f1a\u518d\u6765\u3002\u201d\u592a\u5e73\u516c\u4e3b\u5185\u5fc3\u5992\u5fcc\uff0c\u5374\u4e0d\u6562\u542d\u58f0\u3002 \u3000\u3000\u592a\u5e73\u516c\u4e3b\u8d70\u540e\uff0c\u6b66\u5219\u5929\u7b11\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u4eca\u665a\u9762\u8272\u6709\u70b9\u4e0d\u5bf9\uff0c\u4e3a\u7684\u4ec0\u4e48\uff1f\u201d\u5a49\u513f\u5fc3 \u5934\u4e00\u51db\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u5440\uff0c\u4e5f\u8bb8\u662f\u6628\u665a\u7761\u5f97\u4e0d\u597d\u5427\u3002\u201d\u674e\u9038\u4ece\u5e10\u540e\u5077\u7aa5\uff0c\u89c1\u5a49\u513f\u989c\u5bb9 \u614c\u6062\uff0c\u6697\u6697\u75db\u5fc3\u3002 \u3000\u3000\u6b66\u5219\u5929\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u4e8b\u60c5\u8f83\u591a\uff0c\u8f9b\u82e6\u4f60\u4e86\uff0c\u627f\u55e3\u7684\u6848\u4ef6\uff0c\u5377\u5b97\u4f60\u5f04\u597d\u6ca1\u6709\uff1f\u201d\u4e0a\u5b98 \u5a49\u513f\u9053\uff1a\u201c\u5df2\u6574\u7406\u597d\u4e86\uff0c\u5c31\u7b49\u965b\u4e0b\u8fc7\u76ee\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u627f\u55e3\u9001\u6765\u7684\u8bf7\u7f6a\u594f\u6298\u600e\u4e48\u8bf4\u3002\u201d \u5a49\u513f\u9053\uff1a\u201c\u4ed6\u8bf4\u4e0d\u77e5\u9053\u90a3\u4e24\u4eba\u662f\u5978\u7ec6\uff0c\u81ea\u8ba4\u5931\u5bdf\u4e4b\u7f6a\u3002\u90a3\u4e24\u4eba\u5df2\u7ecf\u6b7b\u4e86\uff0c\u65e0\u53ef\u5bf9\u8d28\u3002\u201d \u6b66\u5219\u5929\u53c8\u95ee\u9053\uff1a\u201c\u7384\u971c\u6307\u8bc1\u4ed6\u66fe\u6d3e\u9063\u5bc6\u4f7f\uff0c\u79c1\u901a\u7a81\u53a5\uff0c\u4ed6\u7684\u6298\u5b50\u91cc\u600e\u6837\u81ea\u8fa9\uff1f\u201d\u5a49\u513f\u9053\uff1a \u201c\u4ed6\u8bf4\u7a81\u53a5\u5927\u6c57\u8981\u6c42\u548c\u4eb2\uff0c\u60f3\u628a\u5973\u513f\u5ac1\u7ed9\u4ed6\u7684\u513f\u5b50\u6dee\u9633\u738b\u5ef6\u79c0\uff0c\u4ed6\u6d3e\u53bb\u7684\u4eba\u4e0d\u8fc7\u662f\u8c08\u548c \u4eb2\u7684\u4e8b\u60c5\uff0c\u90a3\u65f6\u6218\u4e89\u5c1a\u672a\u53d1\u751f\uff0c\u540e\u6765\u6218\u4e8b\u4e00\u8d77\uff0c\u4ed6\u7684\u50fb\u8005\u56de\u4e0d\u6765\uff0c\u662f\u5426\u8feb\u4e8e\u6deb\u5a01\uff0c\u5f52\u987a \u4e86\u7a81\u53a5\uff0c\u90a3\u4ed6\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u4ed6\u6ca1\u6709\u7980\u965b\u4e0b\uff0c\u81ea\u8ba4\u4e13\u9053\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u5012\u5584\u4e8e\u907f\u91cd\u5c31\u8f7b\uff01\u201d\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u7ed9\u6211\u8d77\u8349\u4e00 \u5c01\u8bcf\u4e66\uff0c\u5c06\u6b66\u627f\u55e3\u7684\u5404\u79cd\u804c\u6743\u5c3d\u7686\u89e3\u9664\uff0c\u5e76\u7f5a\u50a3\u5e74\uff0c\u53ea\u4fdd\u7559\u4ed6\u9b4f\u738b\u7684\u5c01\u53f7\u3002\u201d \u3000\u3000\u5a49\u513f\u4ee5\u4e3a\u6b66\u5219\u5929\u5b9a\u8981\u5927\u53d1\u813e\u6c14\uff0c\u91cd\u8d23\u6b66\u627f\u55e3\u7684\uff0c\u54ea\u77e5\u7adf\u5927\u51fa\u5979\u610f\u6599\u4e4b\u5916\u3002\u6b66\u5219\u5929\u77a7 \u4e86\u5979\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u5fc3\u91cc\u4e00\u5b9a\u4e0d\u670d\uff0c\u8bf4\u6211\u8892\u62a4\u81ea\u5df1\u7684\u4f84\u513f\u4e86\uff1f\u201d\u5a49\u513f\u9ed8\u4e0d\u4f5c\u58f0\uff0c \u7d22\u6027\u7ed9\u5979\u6765\u4e2a\u9ed8\u8ba4\u3002\u6b66\u5219\u5929\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u5f88\u5fe0\u76f4\uff0c\u6211\u5c31\u662f\u6b22\u559c\u4f60\u8fd9\u4e2a\u813e\u6c14\u3002\u9b4f\u738b\u7f6a\u5927 \u7f5a\u8f7b\uff0c\u96be\u602a\u4f60\u4e0d\u670d\u6c14\uff0c\u53ef\u662f\u4e8b\u81f3\u5982\u4eca\uff0c\u6211\u4e5f\u53ea\u80fd\u8fd9\u6837\uff01\u201d \u3000\u3000\u6b66\u5219\u5929\u53f9\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u51e0\u5e74\u7cbe\u795e\u4e0d\u591f\uff0c\u4e00\u4e2a\u4eba\u5e74\u7eaa\u8001\u4e86\uff0c\u5bf9\u513f\u5973\u3001\u5bf9 \u4f84\u513f\u53c8\u4e0d\u514d\u6eba\u7231\u4e00\u4e9b\uff0c\u663e\u513f\u548c\u627f\u55e3\u90fd\u60f3\u5728\u6211\u6b7b\u540e\uff0c\u7ee7\u627f\u6211\u7684\u5e1d\u4f4d\uff0c\u4ed6\u4eec\u5404\u81ea\u7ed3\u515a\u8425\u79c1\uff0c \u6211\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u9519\u5728\u6211\u81ea\u8d1f\u8fc7\u751a\uff0c\u6599\u60f3\u4ed6\u4eec\u7ffb\u4e0d\u51fa\u6211\u7684\u638c\u5fc3\uff0c\u6ca1\u6709\u53ca\u65f6\u5236\u6b62\u4ed6\u4eec\u3002\u73b0\u5728 \u4ed6\u4eec\u7684\u7fbd\u7ffc\u90e8\u5df2\u957f\u6210\u4e86\u3002\u53bb\u5e74\uff0c\u6211\u6743\u8861\u8f7b\u91cd\uff0c\u7acb\u4e86\u663e\u513f\u505a\u592a\u5b50\uff0c\u627f\u55e3\u751f\u4e86\u6028\u5992\u4e4b\u5fc3\uff0c\u8fd9 \u6211\u4e5f\u77e5\u9053\u7684\u3002\u4ed6\u6d3e\u9063\u4f7f\u8005\u52fe\u901a\u7a81\u53a5\u7684\u4e8b\u60c5\uff0c\u867d\u7136\u6ca1\u6709\u771f\u51ed\u5b9e\u952f\uff0c\u53ea\u6709\u7384\u971c\u7684\u4e00\u9762\u4e4b\u8f9e\uff0c \u4f46\u4f9d\u6211\u770b\u6765\uff0c\u591a\u534a\u4e5f\u662f\u771f\u7684\uff0c\u6211\u5e94\u8be5\u6740\u4e86\u4ed6\uff01\u4f46\u8fd9\u4e8b\u60c5\u4e00\u63ed\u5f00\u6765\u7c98\u8fde\u6781\u5e7f\uff0c\u7edd\u4e0d\u662f\u53ea\u6740 \u4e86\u627f\u55e3\u4e00\u4e2a\u4eba\u5c31\u53ef\u4ee5\u4e86\u4e8b\u7684\uff0c\u6211\u8001\u4e86\u3002\u9b44\u529b\u8fdc\u4e0d\u53ca\u5e74\u5c11\u4e4b\u65f6\uff0c\u800c\u4e14\u53c8\u5728\u548c\u7a81\u53a5\u5927\u6218\u8fc7\u540e\uff0c \u56fd\u529b\u6d88\u8017\u8fc7\u751a\uff0c\u6211\u4e0d\u60f3\u518d\u60f9\u8d77\u4efb\u4f55\u4e71\u4e8b\u4e86\uff0c\u4e0d\u8bba\u89c4\u6a21\u5927\u5c0f\uff0c\u6211\u90fd\u8981\u907f\u514d\u3002\u6240\u4ee5\u6211\u53ea\u6518\u593a \u627f\u55e3\u7684\u5404\u79cd\u804c\u6743\uff0c\u8ba9\u4ed6\u5f79\u6709\u529b\u91cf\u9020\u53cd\uff0c\u6211\u7684\u82e6\u5fc3\uff0c\u4f60\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u5a49\u513f\u5446\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u6211\u6ca1\u6709\u529f\u592b\u770b \u7fa4\u81e3\u7684\u594f\u6298\uff0c\u6709\u4ec0\u4e48\u7d27\u8981\u7684\u4f60\u62e3\u51e0\u4ef6\u8bf4\u7ed9\u6211\u542c\u542c\u3002\u201d\u5a49\u513f\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7d27\u8981\u7684\uff0c\u53ea \u662f\u5d14\u544a\u5473\u3001\u8881\u6055\u5df1\u4e8c\u4eba\u5408\u4e0a\u4e00\u4e2a\u594f\u6298\uff0c\u662f\u5bf9\u965b\u4e0b\u6709\u6240\u529d\u8c0f\u7684\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4ed6\u4eec\u4e8c\u4eba \u662f\u6b63\u76f4\u7684\u4eba\uff0c\u65e2\u6709\u529d\u8c0f\uff0c\u90a3\u4e00\u5b9a\u662f\u6211\u505a\u9519\u4e86\u4ec0\u4e48\u4e8b\u4e86\uff0c\u8fd9\u8fd8\u4e0d\u7d27\u8981\u5417\uff1f\u8d76\u5feb\u8bf4\u5427\uff01\u201d\u5a49 \u513f\u9053\uff1a\u201c\u4ed6\u4eec\u529d\u8c0f\u4e24\u4ef6\u4e8b\u60c5\uff0c\u4e00\u4ef6\u662f\u8bf7\u965b\u4e0b\u505c\u6b62\u4fee\u9020\u4f5b\u5bfa\uff0c\u4ed6\u4eec\u8bf4\u53bb\u5e74\u4fee\u9020\u540c\u798f\u5bfa\uff0c\u53c8 \u5efa\u4f5b\u6559\u7684\u201c\u5929\u62a0\u201d\uff0c\u7528\u4e86\u94dc\u94c1\u4e24\u767e\u4e07\u65a4\uff0c\u8017\u8d39\u94b1\u8d22\u5de5\u529b\u592a\u591a\uff0c\u8bf7\u965b\u4e0b\u4f53\u6064\u6c11\u8270\u3002\u201d\u6b66\u5219 \u5929\u60ca\u7136\u800c\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u7528\u4e86\u8fd9\u4e48\u591a\u94a2\u94c1\uff0c\u529e\u4e8b\u7684\u4eba\u7adf\u7136\u6ca1\u6709\u544a\u8bc9\u6211\uff01\u8fd9\u662f\u53bb\u5e74\u6211\u5728\u75c5\u4e2d\uff0c \u4ed6\u4eec\u66ff\u6211\u2018\u7948\u798f\u2019\uff0c\u800c\u5efa\u9020\u7684\uff0c\u5f53\u65f6\u6211\u60f3\u8fd9\u4e5f\u65e0\u53ef\u65e0\u4e0d\u53ef\uff0c\u4e00\u65f6\u8003\u8651\u672a\u5468\uff0c\u4fbf\u7b54\u5e94\u4e86\uff0c \u60f3\u4e0d\u5230\u4ed6\u4eec\u5f97\u4e86\u6211\u7684\u65e8\u610f\uff0c\u4fbf\u5927\u5174\u571f\u6728\uff0c\u8017\u8d39\u6c11\u529b\uff0c\u5509\uff0c\u521a\u624d\u6211\u8fd8\u8d23\u5907\u6211\u7684\u5973\u513f\u4fee\u9a78\u9a6c \u5e9c\u5462\uff0c\u5c82\u77e5\u6211\u7684\u9519\u6bd4\u5979\u8fd8\u5927\uff01\u771f\u662f\u4ee4\u6211\u75db\u5fc3\uff01\u8fd8\u6709\u4e00\u4ef6\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5a49\u513f\u8e0c\u8e87\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u8bf7\u965b\u4e0b\u8fdc\u5c0f\u4eba\u800c\u8fd1\u541b\u5b50\u3002\u201d\u6b66\u5219\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a \u201c\u4ed6\u4eec\u6307\u7684\u5c0f\u4eba\u662f\u8c01\uff1f\u201d\u5a49\u513f\u9053\uff1a\u201c\u6307\u7684\u662f\u5f20\u6613\u4e4b\u548c\u5f20\u660c\u5b97\uff0c\u4ed6\u4eec\u8bf4\u4e8c\u5f20\u662f\u965b\u4e0b\u7684\u58c1\u81e3\uff0c \u7559\u5728\u5b98\u4e2d\uff0c\u6613\u6ecb\u7269\u8bae\uff0c\u8bf7\u965b\u4e0b\u9a71\u9010\u4ed6\u4eec\u51fa\u5b98\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6211\u89c1\u4ed6\u4eec\u4e8c\u4eba\u61c2\u5f97\u97f3\u4e50\uff0c \u7684\u786e\u662f\u628a\u4ed6\u5f53\u4f5c\u58c1\u81e3\u770b\u5f85\uff0c\u7559\u5728\u5b98\u4e2d\u89e3\u95f7\u7684\u3002\u6211\u662f\u4e00\u4e2a\u8001\u592a\u5a46\u4e86\uff0c\u672c\u6765\u4ee5\u4e3a\u4e0d\u4f1a\u6709\u4ec0\u4e48 \u95f2\u8bdd\u7684\u3002\u6664\uff0c\u4f46\u4ed6\u4eec\u8bf4\u5f97\u4e5f\u5bf9\uff0c\u4e8c\u5f20\u5e76\u4e0d\u662f\u6b63\u6d3e\u7684\u4eba\uff0c\u8981\u9632\u5907\u4ed6\u4eec\u6301\u7740\u6211\u7684\u5ba0\u7231\u800c\u5356\u5f04 \u6743\u52bf\uff0c\u597d\u5427\uff0c\u6211\u660e\u5929\u9063\u6563\u4ed6\u4eec\u4fbf\u662f\u3002\u5509\uff0c\u4e0d\u662f\u6709\u4eba\u529d\u8c0f\uff0c\u6211\u8fd9\u4e00\u751f\u771f\u4e0d\u77e5\u8fd8\u8981\u505a\u9519\u591a\u5c11 \u4e8b\u60c5\uff01\u201d \u3000\u3000\u5a49\u513f\u9053\uff1a\u201c\u965b\u4e0b\u4e00\u751f\u4e2d\u505a\u7684\u597d\u4e8b\u4e5f\u96be\u4e8e\u80dc\u8ba1\uff01\u201d\u6b66\u5219\u5929\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e8b\u662f \u5e94\u8be5\u505a\u7684\uff0c\u4e0d\u503c\u5f97\u63d0\u3002\u55ef\uff0c\u5a49\u513f\uff0c\u73b0\u5728\u8f6e\u5230\u6211\u548c\u4f60\u8bf4\u4e86\uff01\u201d \u3000\u3000\u6b66\u5219\u5929\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u795e\u8272\u663e\u5f97\u975e\u5e38\u6c89\u91cd\uff0c\u5a49\u513f\u5fc3\u5934\u4e00\u51db\uff0c\u8bf4\u9053\uff1a\u201c\u965b\u4e0b\u6709\u4ec0 \u4e48\u5429\u5490\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8fd9\u6b21\u662f\u6765\u6c42\u4f60\u7684\uff01\u201d\u5a49\u513f\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u965b\u4e0b \u8a00\u91cd\u4e86\uff0c\u5a49\u513f\u5f85\u7f6a\u4e4b\u8eab\uff0c\u5f97\u965b\u4e0b\u6258\u4ee5\u8179\u5fc3\uff0c\u82e5\u6709\u5dee\u9063\uff0c\u4e07\u6b7b\u4e0d\u8f9e\uff01\u201d \u3000\u3000\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u66f4\u611f\u8c22\u4f60\u3002\u8fd9\u5341\u5e74\u6765\u4f60\u5e2e\u5fd9\u6211\u505a\u4e86\u8bb8\u591a\u4e8b\u60c5\uff0c\u6700\u61c2\u5f97\u6211\u5fc3\u4e8b\u7684 \u4eba\u6050\u6015\u4e5f\u53ea\u6709\u4f60\u4e86\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4eba\u751f\u4e03\u5341\u53e4\u6765\u7a00\uff0c\u6211\u4eca\u5e74\u5df2\u7ecf\u662f\u516b\u5341\u51e0 \u5c81\u4e86\uff0c\u81ea\u77e5\u6765\u65e5\u65e0\u591a\uff0c\u8bf4\u53e5\u7b11\u8bdd\uff0c\u6211\u5df2\u662f\u4e00\u53ea\u811a\u8de8\u8fdb\u575f\u5893\u91cc\u7684\u4eba\u4e86\uff01\u201d \u3000\u3000\u5a49\u513f\u671b\u7740\u6b66\u5219\u5929\u8870\u8001\u7684\u989c\u5bb9\uff0c\u542c\u7740\u5979\u51c4\u51c9\u7684\u58f0\u8c03\uff0c\u60f3\u5979\u4e00\u4ee3\u96c4\u624d\uff0c\u5f00\u5929\u8f9f\u5730\u4ee5\u6765\u7684 \u7b2c\u4e00\u4f4d\u5973\u7687\u5e1d\uff0c\u6700\u7ec8\u4e5f\u4e0d\u514d\u5f52\u4e8e\u9ec4\u571f\uff0c\u5fc3\u5934\u4e0d\u7981\u4e00\u9635\u4f24\u611f\uff0c\u5fcd\u6cea\u8bf4\u9053\uff1a\u201c\u965b\u4e0b\u7cbe\u795e\u5065\u70c1\uff0c \u4f55\u4e3a\u51fa\u6b64\u4e0d\u7965\u4e4b\u8a00\uff01\u201d \u3000\u3000\u6b66\u5219\u5929\u51c4\u7136\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4eba\u603b\u662f\u8981\u6b7b\u7684\uff0c\u6211\u6d3b\u4e86\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u591a\u5c11\u4e5f\u505a\u4e86 \u4e00\u4e9b\u524d\u4eba\u6240\u672a\u505a\u8fc7\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u6b7b\u4e86\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u9057\u61be\u3002\u4f46\u662f\u56fd\u5bb6\u5927\u4e8b\uff0c\u6211\u4ecd\u672a\u80fd\u653e\u5fc3\uff0c \u6240\u4ee5\u8981\u6c42\u4f60\u6765\u5206\u6311\u6211\u7684\u62c5\u5b50\u3002\u201d \u3000\u3000\u5a49\u513f\u60f6\u6050\u8bf4\u9053\uff1a\u201c\u965b\u4e0b\u8fd9\u8bdd\u6298\u6740\u6211\u4e86\u3002\u201d\u6b66\u5219\u5929\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u6211 \u4eca\u665a\u548c\u4f60\u8bf4\u7684\u6bcf\u4e00\u53e5\u8bdd\u90fd\u662f\u5fc3\u8179\u4e4b\u8a00\u3002\u201d\u5598\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u8ddf\u4e86\u6211\u8fd9\u4e48\u591a\u5e74\uff0c \u5e94\u8be5\u77e5\u9053\u6211\u6700\u653e\u5fc3\u4e0d\u4e0b\u7684\u5c31\u662f\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u6211\u5378\u4e0b\u7684\u62c5\u5b50\u4e0d\u77e5\u8c01\u80fd\u6311\u8d77\uff1f\u6211\u672c\u6765\u60f3\u8fc7\u8981 \u628a\u5e1d\u4f4d\u8ba9\u7ed9\u72c4\u4ec1\u6770\u7684\uff0c\u53ef\u60dc\u63a8\u4f4d\u8ba9\u8d24\u7684\u4e8b\u60c5\u53ea\u80fd\u89c1\u4e4b\u5343\u53e4\u4ee3\uff0c\u73b0\u5728\u662f\u4e00\u5bb6\u4e00\u59d3\u7684\u89c2\u5ff5\u5df2 \u7ecf\u6df1\u5165\u4eba\u5fc3\uff0c\u6211\u62d7\u4e0d\u8fc7\u8fd9\u51e0\u5343\u5e74\u6765\u7684\u89c2\u5ff5\uff0c\u6240\u4ee5\u6211\u8fd9\u4e2a\u5fc3\u613f\u4e5f\u53ea\u597d\u6c38\u8fdc\u6401\u5728\u5fc3\u5934\uff0c\u9664\u4f60 \u4e4b\u5916\uff0c\u5bf9\u4efb\u4f55\u4eba\u90fd\u672a\u66fe\u900f\u9732\u3002\u201d \u3000\u3000\u5a49\u513f\u9053\uff1a\u201c\u592a\u5b50\u4e3a\u4eba\u5fe0\u539a\uff0c\u965b\u4e0b\u5f53\u653f\u4ee5\u6765\uff0c\u53c8\u63d0\u62d4\u4e86\u8bb8\u591a\u8d24\u4eba\u53ef\u4ee5\u8f85\u4f50\u4ed6\uff0c\u4e5f\u4e0d\u5fc5 \u8fc7\u4e8e\u591a\u8651\u3002\u201d\u6b66\u5219\u5929\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7684\u51e0\u4e2a\u513f\u5b50\u90fd\u662f\u5eb8\u624d\u3002\u592a\u5b50\u8f83\u4e3a\u5fe0\u539a\uff0c\u5374\u4e0d\u662f\u80fd\u591f\u62c5 \u5f53\u5927\u4e8b\u7684\u4eba\u3002\u6211\u7684\u51e0\u4e2a\u4f84\u513f\u66f4\u4e0d\u662f\u597d\u4e1c\u897f\u3002\u6211\u7684\u5973\u513f\uff0c\u5509\uff0c\u5979\u60f3\u5b66\u6211\u53ea\u5b66\u5230\u6211\u7684\u76ae\u6bdb\uff0c \u5979\u8d2a\u6743\u52df\u52bf\uff0c\u5982\u679c\u6211\u6b7b\u4e4b\u540e\uff0c\u6ca1\u4eba\u7ba1\u5979\uff0c\u66f4\u6050\u6015\u5c06\u6765\u4f1a\u5f04\u51fa\u7978\u60a3\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u559d\u4e86\u53e3\u8336\uff0c\u8fde\u8fde\u5598\u6c14\uff0c\u5a49\u513f\u9053\uff1a\u201c\u965b\u4e0b\u4f60\u6b47\u6b47\u518d\u8bf4\u5427\u3002\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e0d\uff0c \u6211\u73b0\u5728\u4e0d\u8bf4\uff0c\u5c06\u6765\u5c31\u6ca1\u673a\u4f1a\u8bf4\u4e86\u3002\u4eca\u5929\u95f9\u51fa\u7684\u6b66\u627f\u55e3\u6848\u4ef6\uff0c\u66f4\u4ee4\u6211\u5bf9\u513f\u5973\u3001\u4f84\u513f\u5931\u671b\u4f24 \u5fc3\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u662f\u968f\u65f6\u4f1a\u53d1\u751f\u53d8\u7978\u7684\uff01\u6240\u4ee5\u6211\u6c42\u6c42\u4f60\uff0c\u6c42\u4f60\u505a\u6211\u7684\u5ab3\u5987\uff01\u592a\u5b50\u53ef\u597d\u53ef\u574f\uff0c \u6709\u4f60\u505a\u4ed6\u7684\u5185\u52a9\uff0c\u6211\u6b7b\u4e86\u624d\u80fd\u5b89\u5fc3\uff01\u201d \u3000\u3000\u674e\u9038\u85cf\u5728\u5e10\u540e\uff0c\u542c\u5230\u8fd9\u8bdd\uff0c\u6709\u5982\u7126\u96f7\u8f70\u9876\uff0c\u4ed6\u8fd9\u65f6\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u5a49\u513f\u78b0\u5230\u7684\u662f \u8fd9\u4e2a\u96be\u9898\uff0c\u5979\u6240\u4e0d\u559c\u6b22\u7684\u4f46\u53c8\u53ef\u80fd\u5ac1\u7ed9\u4ed6\u7684\u4eba\u4e43\u662f\u592a\u5b50\uff01 \u3000\u3000\u5a49\u513f\u9762\u8272\u5530\u7684\u4e00\u4e0b\u53d8\u5f97\u82cd\u767d\u5982\u7eb8\uff0c\u597d\u4e45\uff0c\u597d\u4e45\uff0c\u90fd\u672a\u66fe\u7b54\u6b66\u5219\u5929\u7684\u8bdd\u3002\u672c\u6765\uff0c\u8fd9\u4ef6 \u5a5a\u4e8b\uff0c\u5728\u4e00\u5e74\u4e4b\u524d\uff0c\u6b66\u5219\u5929\u5c31\u66fe\u7ecf\u53eb\u7384\u971c\u548c\u592a\u5e73\u516c\u4e3b\u5411\u5979\u793a\u610f\u8fc7\u4e86\uff0c\u4f46\u6b66\u5219\u5929\u4eb2\u53e3\u5411\u5979 \u63d0\u4eb2\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002 \u3000\u3000\u6b66\u5219\u5929\u8f7b\u8f7b\u629a\u6478\u5a49\u513f\u7684\u79c0\u53d1\uff0c\u773c\u5149\u4e2d\u5145\u6ee1\u7231\u601c\u548c\u671f\u5f85\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u7684\u513f\u5b50\u662f\u4e2a \u5eb8\u624d\uff0c\u8fd9\u5934\u5a5a\u4e8b\u5b9e\u5728\u662f\u59d4\u5c48\u4f60\u7684\uff0c\u82e5\u7136\u53e6\u6709\u826f\u7b56\uff0c\u6211\u4e5f\u4e0d\u60f3\u4f60\u5ac1\u7ed9\u4ed6\u7684\u3002\u4f46\u4e3a\u4e86\u5927\u5c40\u7740 \u60f3\uff0c\u6211\u8fd8\u662f\u5e0c\u671b\u4f60\u505a\u6211\u7684\u5ab3\u5987\u3002\u4f60\u8ddf\u6211\u591a\u5e74\uff0c\u719f\u6089\u653f\u4e8b\uff0c\u6211\u7684\u62c5\u5b50\uff0c\u4e5f\u53ea\u6709\u4f60\u80fd\u6311\u8d77\u6765\u3002 \u4f60\u4e0d\u4f46\u53ef\u4ee5\u8f85\u4f50\u6211\u7684\u513f\u5b50\uff0c\u5c06\u6765\u674e\u6b66\u4e24\u59d3\u7684\u7ea0\u7eb7\uff0c\u4f60\u4e5f\u662f\u6700\u597d\u7684\u8c03\u505c\u4eba\u3002\u201d \u3000\u3000\u5a49\u513f\u773c\u89d2\u6c81\u51fa\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u965b\u4e0b\uff0c\u591a\u627f\u4f60\u770b\u5f97\u8d77\u6211\uff0c\u6211\u4e5f\u611f\u6fc0\u4f60\u5bf9\u6211 \u7684\u671f\u671b\uff0c\u5bf9\u6211\u7684\u4fe1\u6258\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u8fd8\u5f97\u60f3\u60f3\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u62bd\u51fa\u624b\u7ee2\uff0c\u4eb2\u81ea\u66ff\u5979\u62ed\u53bb\u4e86\u6cea\u73e0\uff0c\u51dd\u7738\u5bf9\u7740\u5979\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u662f\u4e0d\u662f\u53e6\u6709 \u5fc3\u4e0a\u7684\u4eba\uff1f\u201d \u3000\u3000\u5a49\u513f\u907f\u5f00\u5979\u6ce8\u89c6\u7684\u773c\u5149\uff0c\u6447\u4e86\u6447\u5934\uff0c\u5fc3\u5934\u5374\u6cdb\u8d77\u4e86\u674e\u9038\u7684\u5f71\u5b50\uff0c\u201c\u4ed6\u6765\u4e86\u6ca1\u6709\u5462\uff1f \u6211\u5fc3\u4e0a\u6709\u4ed6\uff0c\u4ed6\u5fc3\u4e0a\u6709\u6ca1\u6709\u6211\u5462\uff1f\u201d\u5929\u540e\u4e00\u751f\u7684\u5a5a\u70df\u4e5f\u6781\u4e0d\u5982\u610f\uff0c\u5979\u5b9e\u5728\u662f\u4e3a\u4e86\u56fd\u5bb6\u727a \u7272\u4e86\u81ea\u5df1\u7684\u5a5a\u70df\u5e78\u798f\u7684\uff0c\u6211\u662f\u4e0d\u662f\u4e5f\u5e94\u8be5\u8d70\u5979\u6240\u8d70\u8fc7\u7684\u8def\u5462\uff1f\u201d\u5a49\u513f\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e00\u65f6\u95f4 \u5b9e\u662f\u59d4\u51b3\u4e0d\u4e0b\u3002 \u3000\u3000\u6b66\u5219\u5929\u7ffb\u4e86\u7ffb\u684c\u4e0a\u7684\u4e00\u5b97\u6587\u4ef6\uff0c\u5ffd\u9053\uff1a\u201c\u674e\u9038\u5df2\u7ecf\u56de\u5230\u957f\u5b89\uff0c\u4f60\u77e5\u9053\u4e86\u4e48\uff1f\u201d\u5e10\u540e \u7684\u674e\u9038\u4e0e\u5f85\u7acb\u5728\u5979\u65c1\u8fb9\u7684\u5a49\u513f\uff0c\u540c\u65f6\u5fc3\u4e2d\u4e00\u9707\uff0c\u53ea\u542c\u5f97\u6b66\u5219\u5929\u7ee7\u7eed\u8bf4\u9053\uff0c\u201c\u4eca\u5929\u5728\u627f\u55e3 \u738b\u5e9c\u6349\u62ff\u5978\u7ec6\uff0c\u4e5f\u6709\u4ed6\u7684\u4e00\u4efd\uff0c\u7981\u536b\u519b\u7684\u51e0\u4e2a\u7edf\u9886\u90fd\u6765\u5411\u6211\u62a5\u544a\u4e86\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u6b47\u4e86\u4e00\u6b47\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u7eed\u9053\uff1a\u201c\u674e\u9038\u662f\u4e2a\u4eba\u624d\uff0c\u53ef\u60dc\u4ed6\u4ee5\u524d\u4e00\u76f4\u628a\u6c5f\u5c71\u770b\u4f5c \u4ed6\u674e\u5bb6\u4e4b\u7269\uff0c\u5bf9\u6211\u6210\u89c1\u592a\u6df1\uff0c\u4e0d\u77e5\u4ed6\u73b0\u5728\u6709\u4e9b\u6539\u53d8\u6ca1\u6709\u4e86\u3002\u542c\u8bf4\u4f60\u5c0f\u65f6\u5019\u548c\u4ed6\u5f88\u597d\u3002\u82e5 \u662f\u4ed6\u613f\u610f\u8f85\u4f50\u592a\u5b50\uff0c\u90a3\u4e48\u6211\u53ef\u4ee5\u5c01\u4ed6\u505a\u4e2a\u4eb2\u738b\uff0c\u8ba9\u4f60\u505a\u4ed6\u7684\u738b\u5983\u3002\u201d \u3000\u3000\u5a49\u513f\u5fc3\u60c5\u6fc0\u52a8\u4e4b\u6781\uff0c\u4f4e\u4e0b\u5934\u6765\uff0c\u597d\u50cf\u662f\u81ea\u8a00\u81ea\u8bed\u7684\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c \u4ed6\u5fc3\u4e0a\u65e9\u5df2\u53e6\u6709\u4eba\u4e86\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u9053\uff1a\u201c\u662f\u4e48\uff1f\u6211\u8fd8\u672a\u77e5\u9053\u5462\u3002\u5176\u5b9e\uff0c\u5bf9\u6211\u6765\u8bf4\uff0c\u6211\u5f53\u7136\u662f\u6700\u5e0c\u671b\u4f60\u505a\u6211\u7684\u5ab3 \u5987\uff0c\u73b0\u5728\u7684\u60c5\u52bf\u5df2\u7ecf\u8feb\u5f97\u5f88\u7d27\u4e86\uff0c\u6211\u5927\u7ea6\u4e5f\u6d3b\u4e0d\u4e86\u591a\u4e45\u4e86\uff0c\u5a49\u513f\uff0c\u6211\u5728\u7b49\u5f85\u4f60\u7684\u56de\u590d\uff01\u201d \u3000\u3000\u5a49\u513f\u60f3\u4e86\u4e00\u60f3\uff0c\u7b54\u9053\uff1a\u201c\u965b\u4e0b\uff0c\u8bf7\u4f60\u7ed9\u6211\u4e09\u5929\u7684\u671f\u9650\u3002\u201d\u6b66\u5219\u5929\u5fae\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e09 \u5929\u6211\u5927\u7ea6\u8fd8\u53ef\u4ee5\u5f85\u5f97\u5230\u5427\u3002\u201d\u8bf4\u4e86\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5979\u63a5\u7740\u5c31\u6309\u4e86\u6309\u684c\u4e0a\u7684\u91d1\u94c3\uff0c\u5524\u4e00\u4e2a\u5bab\u5973 \u8fdb\u6765\uff0c\u95ee\u9053\uff1a\u201c\u7384\u971c\u56de\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90a3\u5bab\u5973\u56de\u9053\uff1a\u201c\u5979\u4eec\u5df2\u5230\u51cc\u6ce2\u5bab\u95ee\u8fc7\u4e86\uff0c\u90e1\u4e3b\u8fd8\u6ca1\u6709\u56de\u6765\u3002\u201d\u6b66\u5219\u5929\u76b1\u8d77\u7709\u5934\uff0c\u81ea \u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5947\u602a\uff0c\u96be\u9053\u5f53\u771f\u4f1a\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u6325\u624b\u53eb\u90a3\u5bab\u5973\u9000\u4e0b\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u4f3c\u4e4e \u663e\u5f97\u751a\u662f\u4e0d\u5b89\u3002 \u3000\u3000\u5a49\u513f\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u51db\uff0c\u5979\u8ddf\u4e86\u6b66\u5219\u5929\u5c06\u8fd1\u5341\u5e74\uff0c\u5f88\u5c11\u89c1\u6b66\u5219\u5929\u6709\u8fc7\u8fd9\u6837\u7684\u795e\u8272\uff0c\u5979 \u8fd8\u8bb0\u5f97\u5c31\u662f\u5728\u5f90\u656c\u4e1a\u9020\u53cd\u7684\u65f6\u5019\uff0c\u6b66\u5219\u5929\u4e5f\u8fd8\u662f\u8c08\u7b11\u81ea\u5982\uff0c\u96be\u9053\u5728\u8fd9\u592a\u5e73\u7684\u65e5\u5b50\uff0c\u4f1a\u7a81 \u7136\u53d1\u751f\u6bd4\u5f90\u656c\u4e1a\u9020\u53cd\u540f\u5927\u7684\u4e71\u5b50\u4e0d\u6210\uff1f \u3000\u3000\u5a49\u513f\u95ee\u9053\uff1a\u201c\u7384\u971c\u59d0\u59d0\u771f\u7684\u4e0d\u5728\u5bab\u4e2d\u5417\uff1f\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u6211\u542c\u8bf4\u6709\u90e8\u4efd\u7981\u536b\u519b\u4e0d\u7a33\uff0c \u662f\u6211\u5dee\u9063\u5979\u5411\u674e\u660e\u4e4b\u6253\u542c\u53bb\u4e86\u3002\u201d\u5a49\u513f\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u4e86\u6b66\u7384\u971c\u4f55\u4ee5\u4e0d\u5728\u6b66\u5219\u5929\u8eab \u8fb9\u7684\u539f\u56e0\uff0c\u4e5f\u660e\u767d\u6b66\u5219\u5929\u4f55\u4ee5\u8981\u90a3\u4e48\u7740\u6025\u5411\u5979\u63d0\u4eb2\u4e86\u3002 \u3000\u3000\u5a49\u513f\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u660e\u4e4b\u662f\u965b\u4e0b\u7684\u4eb2\u4fe1\uff0c\u7981\u536b\u519b\u548c\u7fbd\u6797\u519b\u7684\u519b\u5b98\u4e5f\u90fd\u662f\u62e5\u6234 \u965b\u4e0b\u7684\u53ef\u9760\u4e4b\u4eba\uff0c\u6216\u8005\u90a3\u6d88\u606f\u662f\u8c23\u4f20\u7684\u5427\uff01\u201d\u6b66\u5219\u5929\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u60c5\u5f80\u5f80 \u662f\u96be\u4e8e\u9884\u6599\uff0c\u4f60\u8d8a\u4ee5\u4e3a\u4e0d\u4f1a\u53d1\u751f\u7684\uff0c\u53ef\u80fd\u5c31\u4f1a\u53d1\u751f\uff0c\u6211\u8fd8\u662f\u56de\u53bb\u770b\u770b\u7684\u597d\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u98a4\u5dcd\u5dcd\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u5a49\u513f\u6276\u7740\u5979\uff0c\u521a\u521a\u8d70\u5230\u95e8\u53e3\uff0c\u5ffd\u89c1\u592a\u5e73\u516c\u4e3b\u5306\u5306\u8dd1\u6765\uff0c \u53eb\u9053\uff1a\u201c\u5988\uff0c\u4e0d\u597d\u5566\uff0c\u6709\u519b\u58eb\u54d7\u53d8\uff0c\u5df2\u7ecf\u6253\u8fdb\u5bab\u6765\u4e86\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u662f\u8c01\u7387\u9886\u7684\uff1f\u201d \u592a\u5e73\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u5916\u9762\u5f88\u4e71\uff0c\u5988\uff0c\u4f60\u6700\u597d\u6682\u65f6\u4e0d\u8981\u51fa\u53bb\uff0c\u6211\u5df2\u7ecf\u53eb\u5f20\u6613\u4e4b\u5144\u5f1f\u8c03 \u52a8\u5bab\u4e2d\u7684\u5bbf\u536b\u519b\u58eb\u4e86\u3002\u201d \u3000\u3000\u6b66\u5219\u5929\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u8fd9\u65f6\u5019\u6211\u4e0d\u51fa\u53bb\uff0c\u8fd8\u6709\u8c01\u80fd\u591f\u5236\u6b62\u4ed6\u4eec\uff1f\u5f20\u6613\u4e4b\u5144\u5f1f\u61c2\u5f97\u4ec0 \u4e48\uff0c\u4f60\u80e1\u4e71\u53eb\u4ed6\u8c03\u52a8\u5bbf\u536b\uff0c\u8fdd\u53cd\u6211\u7684\u6cd5\u5ea6\uff0c\u4f60\u8fd8\u672a\u77e5\u7f6a\u4e48\uff1f\u201d\u592a\u5e73\u516c\u4e3b\u614c\u5fd9\u8dea\u4e0b\u6765\u53e9\u5934\uff0c \u9053\uff1a\u201c\u6bcd\u540e\u965b\u4e0b\uff0c\u6211\u662f\u4e3a\u4e86\u4f60\u554a\uff01\u201d \u3000\u3000\u6b66\u5219\u5929\u6012\u9053\uff1a\u201c\u4f60\u5c31\u662f\u77e5\u9053\u7ed9\u6211\u591a\u6dfb\u9ebb\u70e6\uff01\u5a49\u513f\uff0c\u4f60\u966a\u6211\u53bb\uff0c\u6211\u4eb2\u81ea\u5904\u57cb\u8fd9\u4ef6\u4e8b\u60c5\uff01\u201d \u4e8b\u5230\u4e34\u5934\uff0c\u6b66\u5219\u5929\u767b\u65f6\u597d\u50cf\u6362\u4e86\u4e2a\u4eba\uff0c\u53cd\u800c\u7cbe\u795e\u594b\u53d1\uff0c\u9547\u5b9a\u8d77\u6765\uff0c\u674e\u9038\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\uff0c \u4ed6\u5bf9\u519b\u58eb\u54d7\u53d8\u7684\u4e8b\u60c5\uff0c\u4e5f\u89c9\u5f97\u5927\u51fa\u610f\u5916\u3002 \u3000\u3000\u5a49\u513f\u6276\u7740\u5979\u521a\u8fc8\u51fa\u4e00\u6b65\uff0c\u5ffd\u5730\u53c8\u6709\u4e24\u4e2a\u6b66\u58eb\u8dd1\u6765\uff0c\u5927\u58f0\u7980\u9053\uff1a\u201c\u53db\u519b\u5df2\u7ecf\u6253\u7834\u4e86\u6731 \u96c0\u95e8\uff0c\u51b2\u5230\u4e86\u51cc\u6ce2\u6bbf\u5566\uff01\u4e24\u4f4d\u5f20\u5927\u4eba\u4e5f\u90fd\u7ed9\u53db\u519b\u6740\u4e86\uff01\u201d\u4ed6\u4eec\u6240\u8bf4\u7684\u201c\u4e24\u4f4d\u5f20\u5927\u4eba\u201d\u6307 \u7684\u4e43\u662f\u5f20\u6613\u4e4b\u548c\u5f20\u660c\u5b97\uff0c\u6b66\u5219\u5929\u559d\u9053\uff1a\u201c\u4e8c\u5f20\u6709\u7f6a\uff0c\u4e5f\u5e94\u8be5\u8ba9\u5927\u7406\u4e1e\u53bb\u4f9d\u6cd5\u5ba1\u95ee\u624d\u662f\uff0c \u600e\u4e48\u53ef\u4ee5\u64c5\u6740\u5462\uff1f\u53e6\u5916\u8fd8\u6740\u4e86\u4ec0\u4e48\u4eba\u6ca1\u6709\uff1f\u201d\u90a3\u4e24\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u5bab\u4e2d\u6df7\u6218\uff0c\u6709\u8bb8\u591a\u5bbf\u536b \u5df2\u5728\u4e71\u519b\u4e2d\u88ab\u6740\u6b7b\u4e86\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u662f\u8c01\u5e26\u9886\u53db\u519b\uff1f\u201d\u90a3\u4e24\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u6709\u5f20\u76f8\u56fd\u3001 \u6853\u5f66\u8303\u3001\u656c\u6656\u3001\u5d14\u7384\u5473\u3001\u8881\u6055\u5df1\u7b49\u4eba\u5728\u5185\uff01\u201d \u3000\u3000\u6b66\u5219\u5929\u9762\u8272\u5927\u53d8\uff0c\u9893\u7136\u8bf4\u9053\uff1a\u201c\u8fde\u4ed6\u4eec\u4e5f\u80cc\u53db\u6211\u4e48\uff1f\u201d\u8fd9\u4e9b\u4eba\u90fd\u662f\u5979\u76f8\u4fe1\u7684\u6b63\u7384\u5927 \u81e3\uff0c\u542c\u8bf4\u4ed6\u4eec\u7387\u9886\u53db\u519b\u6253\u5165\u5bab\u5ef7\uff0c\u5979\u5fc3\u4e2d\u81ea\u662f\u96be\u8fc7\u4e4b\u6781\u3002\u5176\u4e2d\u5d14\u4ea5\u5473\u3001\u8881\u6055\u5df1\u4e8c\u4eba\u662f\u5a49 \u513f\u5411\u6b66\u5219\u5929\u63a8\u8350\u8fc7\u7684\uff0c\u5a49\u513f\u542c\u4e86\uff0c\u4e5f\u662f\u60f6\u6050\u4e0d\u5b89\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u4e9b\u4eba\u5012\u4e0d\u662f\u8981\u53cd\u5bf9\u6b66\u5219\u5929\uff0c\u800c\u662f\u8981\u8fce\u7acb\u592a\u5b50\u3002\u4ed6\u4eec\u89c1\u6b66\u5219\u5929\u5e74\u8001\uff0c\u4e8c\u6b66\u638c\u6743\uff0c \u5fc3\u4e2d\u65e9\u5df2\u6709\u4e86\u9690\u5fe7\uff0c\u8fd9\u6b21\u95f9\u51fa\u4e86\u6b66\u627f\u55e3\u7684\u6848\u4ef6\uff0c\u4ed6\u4eec\u90fd\u662f\u51fa\u5934\u5f39\u523b\u6b66\u627f\u55e3\u7684\u4eba\uff0c\u89c1\u6b66\u627f \u55e3\u867d\u88ab\u524a\u53bb\u804c\u6743\uff0c\u5374\u4ecd\u7136\u7a33\u5750\u738b\u5e9c\uff0c\u4ed6\u4eec\u81ea\u662f\u66f4\u4e3a\u5fe7\u8651\uff0c\u751f\u6015\u6b66\u5219\u5929\u4e00\u65e6\u6b7b\u53bb\uff0c\u4e8c\u6b66\u4e89 \u6743\uff0c\u56fd\u4e8b\u4fbf\u8981\u5f04\u5230\u4e0d\u53ef\u6536\u62fe\uff0c\u56e0\u6b64\u53ea\u6709\u8d81\u8fd9\u65f6\u673a\u53d1\u52a8\u5175\u53d8\uff0c\u7528\u5feb\u5200\u65a9\u4e71\u9ebb\u7684\u529e\u6cd5\uff0c\u8bf7\u6b66 \u5219\u5929\u9000\u4f4d\uff0c\u8fce\u592a\u5b50\u56de\u671d\uff0c\u6062\u590d\u674e\u5510\u6b63\u7edf\u3002 \u3000\u3000\u6b66\u5219\u5929\u5c1a\u672a\u77e5\u9053\u4ed6\u4eec\u7684\u6765\u610f\uff0c\u4f46\u60f3\u4e86\u4e00\u60f3\uff0c\u7acb\u5373\u53c8\u6062\u590d\u4e86\u81ea\u4fe1\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e0d \u4fe1\u8fd9\u4e9b\u4eba\u4f1a\u4f24\u5bb3\u6211\uff0c\u5a49\u513f\uff0c\u4f60\u6276\u6211\u51fa\u53bb\uff0c\u8ba9\u4ed6\u4eec\u89c1\u6211\uff01\u201d\u592a\u5e73\u516c\u4e3b\u53eb\u9053\uff1a\u201c\u6bcd\u540e\u965b\u4e0b\uff0c \u4fd7\u8bed\u8bf4\u5f97\u597d\uff0c\u753b\u864e\u753b\u76ae\u96be\u753b\u9aa8\uff0c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\uff0c\u4e5f\u8bb8\u4f60\u6240\u4eb2\u4fe1\u7684\u4eba\uff0c\u4e5f\u6b63\u5c31\u662f\u8981\u5bb3\u4f60 \u7684\u4eba\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u4f60\u8fd8\u4e00\u5473\u76f8\u4fe1\u5916\u4eba\u4e48\uff1f\u5bab\u4e2d\u5371\u673a\u56db\u4f0f\u2014\u2014\u201d\u987f\u4e86\u4e00\u987f\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u7a81 \u7136\u559d\u9053\uff1a\u201c\u8c01\uff0c\u8fd9\u5c4b\u5b50\u5c31\u4f0f\u6709\u523a\u5ba2\uff01\u201d\u524d\u9762\u7684\u6b66\u58eb\u628a\u624b\u4e00\u626c\uff0c\u500f\u5730\u4e00\u67c4\u5315\u9996\u5411\u5e10\u540e\u63b7\u53bb\uff01 \u3000\u3000\u4ee5\u674e\u9038\u7684\u6b66\u529f\u672c\u6765\u4e0d\u81f3\u4e8e\u88ab\u4ed6\u6697\u7b97\uff0c\u4f46\u4ed6\u542c\u5f97\u5bab\u4e2d\u53d1\u751f\u53d8\u4e71\uff0c\u5a49\u513f\u5c31\u8981\u8ddf\u7740\u6b66\u5219\u5929 \u51fa\u53bb\uff0c\u4ed6\u4e5f\u662f\u5927\u53d7\u9707\u61be\uff0e\u4ed6\u5168\u4ed8\u5fc3\u795e\u90fd\u653e\u5728\u542c\u6b66\u5219\u5929\u548c\u5a49\u513f\u7684\u8c08\u8bdd\u4e0a\uff0c\u731b\u542c\u5f97\u91d1\u5203\u7834\u7a7a \u4e4b\u58f0\uff0c\u8eb2\u95ea\u5df2\u6765\u4e0d\u53ca\uff0c\u90a3\u67c4\u5315\u9996\u7a7f\u8fc7\u4e86\u5e37\u5e10\uff0c\u5728\u4ed6\u5de6\u81c2\u4e0a\u5212\u7834\u4e86\u4e00\u9053\u4f24\u53e3\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u6b66\u58eb\u62d4\u51fa\u8170\u5200\uff0c\u7acb\u523b\u6251\u5411\u674e\u9038\uff0c\u5ffd\u542c\u5f97\u201c\u54e8\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u67c4\u8170\u5200\u65ad\u4e3a\u4e24\u622a\uff0c \u592a\u5e73\u516c\u4e3b\u53eb\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u2014\u2014\u201d\u539f\u6765\u662f\u4e0a\u5b98\u5a49\u513f\u7528\u6b66\u5219\u5929\u9001\u5979\u7684\u90a3\u67c4\u5315\u9996\u524a\u65ad\u4e86\u8fd9\u6b66 \u58eb\u7684\u5175\u5203\u3002 \u3000\u3000\u6b66\u5219\u5929\u84e6\u5730\u559d\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u4f4f\u624b\uff01\u201d\u592a\u5e73\u516c\u4e3b\u53eb\u9053\uff1a\u201c\u6bcd\u540e\u965b\u4e0b\uff0c\u4f60\u8981\u95ee\u95ee\u5a49\u513f\uff0c \u4e3a\u3001\u4e3a\u4ec0\u4e48\u2014\u2014\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u6b66\u5219\u5929\u5df2\u6325\u624b\u6b62\u4f4f\u4e86\u5979\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u591a\u5634\uff0c\u55ef\uff0c\u662f\u4f60\u56de \u6765\u4e86\uff1f\u4f60\u662f\u8981\u89c1\u5a49\u513f\u7684\u4e48\uff1f\u201d\u540e\u9762\u8fd9\u4e24\u53e5\u8bdd\u662f\u5411\u674e\u9038\u8bf4\u7684\u3002 \u3000\u3000\u674e\u9038\u8de8\u4e0a\u4e00\u6b65\uff0c\u800c\u5bf9\u7740\u6b66\u5219\u5929\u548c\u5a49\u513f\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\uff1f\u6b66\u5219\u5929\u63a5\u7740 \u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u73b0\u5728\u5df2\u7ecf\u6ca1\u6709\u65f6\u95f4\u8ba9\u4f60\u4eec\u8bf4\u8bdd\u4e86\uff0c\u4f60\u56de\u6765\u4e86\u5f88\u597d\uff0c\u5f85\u6211\u5904\u7406\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c \u518d\u8ba9\u5a49\u513f\u548c\u4f60\u77ed\u53d9\u201d\u3002 \u3000\u3000\u5a49\u513f\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4f60\u53d7\u4f24\u4e86\uff01\u8fd9\u662f\u6709\u6bd2\u7684\u6697\u5668\uff0c\u8fd9\uff0c\u8fd9\u600e\u4e48\u597d\uff1f\u201d\u674e\u9038\u7684\u4f24 \u53e3\u6d41\u51fa\u5e26\u7740\u8165\u81ed\u6c14\u5473\u7684\u9ed1\u8840\uff0c\u8fd9\u65f6\u542c\u5f97\u5a49\u513f\u8fd9\u4e48\u4e00\u558a\uff0c\u767b\u65f6\u89c9\u5f97\u6574\u6761\u624b\u81c2\u9ebb\u75d2\u75d2\u7684\uff0c\u5fc3 \u5934\u4e5f\u6b32\u4f5c\u5455\uff01 \u3000\u3000\u592a\u5e73\u516c\u4e3b\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u5730\u638f\u51fa\u4e00\u4e2a\u5c0f\u5de7\u96c5\u81f4\u7684\u7389\u74f6\uff0c\u5012\u51fa\u4e24\u9897\u4e38\u836f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f \u5927\u5185\u7684\u4e03\u5b9d\u7075\u4e39\uff0c\u80fd\u6d88\u767e\u6bd2\uff0c\u4f60\u8d76\u5feb\u7ed9\u4ed6\u670d\u4e0b\u3002\u201d\u4e00\u8fb9\u8bf4\u8bdd\uff0c\u4e00\u8fb9\u6349\u7740\u5a49\u513f\u7684\u624b\uff0c\u628a\u90a3 \u4e24\u9897\u4e39\u836f\u5012\u5728\u5979\u7684\u624b\u4e2d\u3002 \u3000\u3000\u4e0a\u5b98\u5a49\u513f\u54ea\u91cc\u77e5\u9053\u516c\u4e3b\u6b63\u6253\u7740\u6b79\u6bd2\u7684\u4e3b\u610f\uff0c\u539f\u6765\u592a\u5e73\u516c\u4e3b\u81ea\u5c0f\u770b\u60ef\u5979\u6bcd\u4eb2\u505a\u7687\u5e1d\u7684 \u795e\u6c14\uff0c\u5fc3\u4e2d\u975e\u5e38\u7fa1\u5e55\uff0c\u4e5f\u60f3\u5b66\u5979\u6bcd\u4eb2\u7684\u699c\u6837\uff0c\u53ef\u60dc\u5730\u6bcd\u4eb2\u5374\u628a\u5e1d\u4f4d\u4f20\u7ed9\u4e86\u5979\u7684\u54e5\u54e5\u3002\u6b66 \u627f\u55e3\u77e5\u5979\u5fc3\u610f\uff0c\u6781\u529b\u5df4\u7ed3\u5979\uff0c\u7b54\u5e94\u5c06\u6765\u6276\u52a9\u5979\uff0c\u5c31\u7b97\u505a\u4e0d\u5230\u5973\u7687\u5e1d\uff0c\u4e5f\u53ef\u4ee5\u5e2e\u5979\u638c\u63e1\u5927 \u6743\uff0c\u64cd\u7eb5\u671d\u653f\uff0c\u56e0\u6b64\u592a\u5e73\u516c\u4e3b\u53cd\u800c\u4e0e\u6b66\u627f\u55e3\u7ed3\u6210\u4e86\u4e00\u515a\u3002\u5979\u751a\u6709\u5fc3\u8ba1\uff0c\u5e73\u65e5\u548c\u5a49\u513f\u975e\u5e38 \u63a5\u8fd1\uff0c\u535a\u53d6\u6bcd\u4eb2\u7684\u6b22\u5fc3\uff0c\u56e0\u6b64\u6b66\u5219\u5929\u548c\u5a49\u513f\u90fd\u6ca1\u6709\u7591\u5fc3\u5979\u3002\u6b66\u5219\u5929\u867d\u7136\u53d1\u89c9\u5979\u884c\u4e8b\u4efb\u6027\uff0c \u4e5f\u53ea\u5f53\u5979\u662f\u88ab\u7eb5\u5bb9\u60ef\u4e86\uff0c\u6043\u5ba0\u751f\u5a07\u800c\u5df2\uff0c\u60f3\u4e0d\u5230\u5979\u6000\u6709\u90a3\u6837\u5927\u7684\u91ce\u5fc3\u3002 \u3000\u3000\u592a\u5e73\u516c\u4e3b\u65e2\u7136\u548c\u5a49\u513f\u65e5\u5915\u76f8\u5904\uff0c\u5a49\u513f\u7684\u5fc3\u4e8b\u4e5f\u5c31\u7792\u4e0d\u8fc7\u5979\uff0c\u8fd9\u6b21\u5979\u77e5\u9053\u674e\u9038\u5df2\u56de\u5230 \u957f\u5b89\uff0c\u6599\u60f3\u674e\u9038\u5fc5\u5b9a\u4f1a\u5192\u9669\u5165\u5bab\u63a2\u671b\u5a49\u513f\uff0c\u5c31\u53eb\u5fc3\u8179\u7684\u592a\u76d1\u5bab\u5973\uff0c\u6697\u5730\u91cc\u6ce8\u610f\u4e0a\u5b98\u5a49\u513f \u548c\u6b66\u7384\u971c\uff0c\u679c\u7136\u7ed9\u5979\u63a2\u542c\u5230\u4e86\u6b66\u7384\u971c\u5e26\u4e00\u4e2a\u5c0f\u592a\u76d1\u8fdb\u6765\u7684\u6d88\u606f\uff0c\u6b66\u7384\u971c\u548c\u5a49\u513f\u7684\u4ea4\u60c5\uff0c \u592a\u5e73\u516c\u4e3b\u6df1\u77e5\uff0c\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u7acb\u5373\u4fbf\u8d77\u4e86\u731c\u7591\uff0c\u6240\u4ee5\u5979\u624d\u7eb5\u607f\u6bcd\u4eb2\u5230\u6b66\u7384\u971c\u7684\u623f\u4e2d\u7b49 \u5019\u5a49\u513f\u3002 \u3000\u3000\u5c31\u5728\u6b66\u5219\u5929\u548c\u5a49\u513f\u5bc6\u8c08\u7684\u65f6\u5019\uff0c\u5979\u5374\u51fa\u53bb\u62f7\u95ee\u6b66\u7384\u971c\u7684\u5bab\u5973\uff0c\u90a3\u5bab\u5973\u89c1\u662f\u516c\u4e3b\uff0c\u53c8 \u77e5\u9053\u5979\u4e0e\u5a49\u513f\u7d20\u6709\u4ea4\u60c5\uff0c\u4fbf\u628a\u79d8\u5bc6\u8bf4\u4e86\u3002\u592a\u5e73\u516c\u4e3b\u53eb\u624b\u4e0b\u5c06\u90a3\u5bab\u5973\u770b\u7ba1\u8d77\u6765\uff0c\u4e0d\u8bb8\u6cc4\u6f0f \u6d88\u606f\uff0c\u4e00\u9762\u53ec\u96c6\u4e86\u81ea\u5df1\u7684\u5fc3\u8179\u6b66\u58eb\u6765\uff0c\u6070\u5de7\u53db\u519b\u6253\u8fdb\u5bab\u4e2d\uff0c\u5979\u4fbf\u8d81\u6b64\u673a\u4f1a\uff0c\u4ee5\u641c\u7d22\u5978\u7ec6 \u4e3a\u540d\uff0c\u641c\u51fa\u4e86\u674e\u9038\u3002\u5979\u672c\u4ee5\u4e3a\u8fde\u4e0a\u5b98\u5a49\u513f\u90fd\u53ef\u4ee5\u4e00\u5e76\u6273\u5012\u7684\uff0c\u54ea\u77e5\u5979\u6bcd\u4eb2\u4e0d\u4f46\u4e1d\u6beb\u6ca1\u6709 \u8d23\u5907\u5a49\u513f\uff0c\u5bf9\u674e\u9038\u4e5f\u4f3c\u4e4e\u751a\u6709\u597d\u611f\uff0c\u542c\u5979\u7684\u53e3\u6c14\uff0c\u751a\u81f3\u8fd8\u8981\u5c06\u674e\u9038\u7559\u4e0b\u6765\u3002\u592a\u5e73\u516c\u4e3b\u4e00 \u60f3\uff0c\u5a49\u513f\u7684\u806a\u660e\u624d\u667a\u5728\u5979\u4e4b\u4e0a\uff0c\u82e5\u518d\u52a0\u4e0a\u4e00\u4e2a\u674e\u9038\uff0c\u81ea\u5df1\u5c06\u6765\u4e00\u5b9a\u8981\u53d7\u4ed6\u4eec\u538b\u5236\uff0c\u4e8e\u662f \u4e00\u8ba1\u4e0d\u6210\uff0c\u53c8\u751f\u4e00\u8ba1\uff0c\u7adf\u628a\u968f\u8eab\u643a\u5e26\u7684\u6bd2\u836f\u5192\u5145\u4e03\u5b9d\u7075\u4e39\uff0c\u4ea4\u7ed9\u4e86\u5a49\u513f\u3002 \u3000\u3000\u8fd9\u65f6\u6b66\u5219\u5929\u6b63\u5728\u50ac\u5a49\u513f\u966a\u5979\u53bb\u5e94\u4ed8\u5175\u53d8\uff0c\u65f6\u673a\u7d27\u8feb\uff0c\u4e0d\u5bb9\u5a49\u513f\u4ed4\u7ec6\u601d\u91cf\uff0c\u800c\u4e14\u5979\u505a \u68a6\u4e5f\u60f3\u4e0d\u5230\u592a\u5e73\u516c\u4e3b\u5b58\u5fc3\u8981\u5bb3\u674e\u9038\uff0c\u5f97\u5230\u201c\u7075\u4e39\u201d\uff0c\u5982\u83b7\u81f3\u5b9d\uff0c\u7acb\u5373\u7ed9\u674e\u9038\u670d\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5916\u9762\u5954\u8dd1\u547c\u53f7\u4e4b\u58f0\uff0c\u6e10\u6765\u6e10\u8fd1\uff0c\u53c8\u6709\u4e24\u4e2a\u592a\u76d1\u8fdb\u6765\u7980\u544a\uff0c\u8bf4\u662f\u53db\u519b\u5df2\u6253\u8fdb\u4e86 \u4e7e\u5143\u5bab\uff0c\u6b66\u5219\u5929\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u5bb3\u6015\u5417\uff1f\u201d\u5a49\u513f\u9053\uff1a\u201c\u5728\u965b\u4e0b\u8eab\u8fb9\uff0c\u6211\u4ec0\u4e48\u90fd\u4e0d\u5bb3\u6015\u3002\u201d \u6b66\u5219\u5929\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u5bb3\u6015\uff0c\u5c31\u8d76\u5feb\u5427\uff0c\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5728\u8fd9\u6837\u7d27\u6025\u7684\u5173\u5934\uff0c\u5a49\u513f\u5f53\u7136\u8981\u966a\u7740\u6b66\u5219\u5929\uff0c\u5979\u542b\u7740\u773c\u6cea\uff0c\u8d70\u4e86\u4e24\u6b65\uff0c\u53c8\u56de\u8fc7\u5934\u6765 \u671b\u7740\u674e\u9038\uff0c\u60f3\u4e0d\u5230\u8d39\u5c3d\u5fc3\u529b\uff0c\u624d\u628a\u674e\u9038\u5f04\u8fdb\u5bab\u6765\uff0c\u7adf\u4e0d\u80fd\u542c\u4ed6\u8bf4\u4e00\u53e5\u8bdd\uff01 \u3000\u3000------------- \u3000\u3000\u6d77\u5929\u690d\u5b57 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u7ecf\u971c\u65b9\u663e\u50b2\u5bd2\u5fc3 \u6881\u7fbd\u751f\u300a\u5973\u5e1d\u5947\u82f1\u4f20\u300b \u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u7ecf\u971c\u65b9\u663e\u50b2\u5bd2\u5fc3 \u3000\u3000\u674e\u9038\u5ffd\u5730\u611f\u5230\u773c\u775b\u53d1\u9ed1\uff0c\u4e00\u80a1\u51b7\u610f\u76f4\u88ad\u5fc3\u5934\uff0c\u6655\u7729\u4e2d\u9690\u7ea6\u4f3c\u89c1\u5230\u592a\u5e73\u516c\u4e3b\u4e0e\u90a3\u4e24\u4e2a \u6b66\u58eb\u76f8\u89c6\u800c\u7b11\uff0c\u674e\u9038\u5fc3\u5934\u4e00\u52a8\uff0c\u6025\u5fd9\u8fd0\u4e86\u4e00\u53e3\u771f\u6c14\uff0c\u5954\u4e0a\u4e24\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u5a49\u513f\uff01\u201d\u5a49\u513f \u56de\u5934\u4e00\u770b\uff0c\u89c1\u4ed6\u9762\u8272\u6709\u5f02\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4e00\u540c\u51fa \u53bb\uff01\u201d\u6b66\u5219\u5929\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u884c\uff01\u6211\u4e0d\u8981\u65c1\u4eba\u5377\u5165\u8fd9\u4e2a\u6f29\u6da1\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u5377 \u5165\u6f29\u6da1\uff0c\u4f46\u6211\u4e0d\u80fd\u7559\u5728\u4f60\u7684\u5bab\u4e2d\u3002\u201d\u4e0a\u5b98\u5a49\u513f\u8fd8\u672a\u60f3\u5230\u662f\u6bd2\u836f\u53d1\u4f5c\uff0c\u53ea\u9053\u662f\u4ed6\u53d7\u4f24\u4e4b\u540e\uff0c \u8840\u8fd8\u672a\u6b62\uff0c\u867d\u6709\u201c\u89e3\u836f\u201d\uff0c\u5374\u4ecd\u7136\u652f\u6301\u4e0d\u4f4f\uff0c\u5fc3\u60f3\uff1a\u5728\u4e71\u519b\u4e4b\u4e2d\uff0c\u53db\u519b\u548c\u5bab\u4e2d\u7684\u5bbf\u536b\u90fd \u8ba4\u4e0d\u5f97\u4ed6\uff0c\u51fa\u53bb\u56fa\u7136\u5371\u9669\uff0c\u7559\u5728\u8fd9\u513f\uff0c\u7ed9\u4e71\u519b\u649e\u5230\uff0c\u4e5f\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u4fbf\u5411\u6b66\u5219\u5929\u8bf4\u9053\uff1a \u201c\u5929\u540e\u965b\u4e0b\uff0c\u4ed6\u65e2\u4e0d\u613f\u7559\u5728\u5bab\u4e2d\uff0c\u5c31\u8ba9\u4ed6\u4ece\u5730\u9053\u51fa\u53bb\u5427\uff01\u201d\u6b66\u5219\u5929\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u5c31\u8ba9\u5982 \u610f\u6765\u7167\u6599\u4ed6\u5e76\u62a4\u9001\u4ed6\u51fa\u53bb\uff01\u674e\u9038\uff0c\u8fd9\u662f\u4e3a\u4f60\u800c\u7279\u522b\u7834\u4f8b\uff0c\u4f60\u53ef\u4e0d\u8981\u6cc4\u6f0f\u4e86\u5bab\u4e2d\u7684\u79d8\u5bc6\uff01\u201d \u5979\u6276\u7740\u5a49\u513f\u7684\u80a9\u5934\uff0c\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u811a\u6b65\u5374\u4e00\u523b\u4e0d\u505c\uff0c\u8bf4\u5b8c\u4e86\u8fd9\u6bb5\u8bdd\uff0c\u5979\u4eec\u5df2\u8d70\u5230\u4e24\u9053\u7684\u8f6c \u89d2\u5904\u4e86\u3002\u4e0a\u5b98\u5a49\u513f\u6700\u540e\u8fd8\u56de\u5934\u4e00\u671b\uff0c\u773c\u89d2\u6302\u7740\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u674e\u9038\u76ee\u9001\u5a49\u513f\u7684\u80cc\u5f71\uff0c\u8d70\u8fc7\u8f6c\u89d2\u5c31\u4e0d\u89c1\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u9635\u9635\u7ede\u75db\uff0c\u4e00\u4e2a\u5bab\u5973\u5982\u98de\u5954\u6765\uff0c \u8f6c\u773c\u95f4\u5c31\u5230\u4e86\u4ed6\u7684\u8ddf\u524d\uff0c\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u201d\u8fd9\u5bab\u5973\u6b63\u662f\u6b66\u7384\u971c\u7684\u5fc3\u8179\u5a62 \u5973\uff0c\u66fe\u968f\u8fc7\u6b66\u7384\u971c\u5927\u95f9\u5ce8\u5d4b\u5c71\u82f1\u96c4\u4f1a\u7684\u90a3\u4e2a\u4e2b\u73af\u5982\u610f\u3002 \u3000\u3000\u592a\u5e73\u516c\u4e3b\u548c\u90a3\u4e24\u4e2a\u6b66\u58eb\u672c\u60f3\u5f85\u6b66\u5219\u5929\u8d70\u540e\uff0c\u5c31\u628a\u674e\u9038\u6740\u4e86\u7684\uff0c\u5374\u4e0d\u6599\u6b66\u5219\u5929\u628a\u5982\u610f \u53eb\u6765\u7167\u6599\u4ed6\uff0c\u4ed6\u4eec\u90fd\u77e5\u9053\u8fd9\u4e2a\u4e2b\u73af\u7684\u672c\u4e8b\uff0c\u5f53\u7136\u4e0d\u6562\u52a8\u624b\u3002\u592a\u5e73\u516c\u4e3b\u4f6f\u4f5c\u5173\u6000\uff0c\u8bc8\u7b11\u8bf4 \u9053\uff1a\u201c\u674e\u9038\uff0c\u4f60\u597d\u597d\u517b\u4f24\uff0c\u4e71\u4e8b\u8fc7\u540e\uff0c\u65e9\u4e9b\u8fdb\u5bab\uff0c\u5a49\u513f\u8fd8\u5728\u7b49\u7740\u4f60\u5462\uff01\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u591a\u8c22\u516c\u4e3b\u597d\u5fc3\uff0c\u6211\u4e0d\u4f1a\u518d\u8fdb\u5b98\u6765\u4e86\uff01\u5982\u610f\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u5982\u610f\u628a\u5927\u5e8a\u79fb \u5f00\uff0c\u63ed\u5f00\u4e86\u4e00\u5757\u77f3\u677f\uff0c\u73b0\u51fa\u6d1e\u53e3\uff0c\u539f\u6765\u5730\u9053\u5c31\u5728\u4e0b\u9762\u3002\u5bab\u4e2d\u4e3a\u4e86\u9632\u5907\u5371\u6025\u65f6\u9003\u96be\u4e4b\u7528\uff0c \u4fee\u4e86\u8bb8\u591a\u6761\u53ef\u4ee5\u901a\u5230\u5916\u9762\u53bb\u7684\u5730\u9053\uff0c\u8fd9\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u6b66\u5219\u5929\u4e0d\u60dc\u8ba9\u4ed6\u4f7f\u7528\u8fd9\u6761\u5730\u9053\uff0c\u786e \u5b9e\u662f\u5bf9\u4ed6\u7279\u522b\u770b\u5f85\u4e86\u3002 \u3000\u3000\u5982\u610f\u5411\u592a\u5e73\u516c\u4e3b\u884c\u4e86\u4e2a\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u82e5\u89c1\u6211\u5bb6\u5c0f\u59d0\uff0c\u8bf7\u544a\u8bc9\u5979\u662f\u6211\u62a4\u9001\u6bbf\u4e0b\u51fa \u5bab\u3002\u201d\u592a\u5e73\u516c\u4e3b\u9053\uff1a\u201c\u597d\u7684\uff0c\u4f60\u653e\u5fc3\u8d70\u5427\uff01\u201d\u5979\u597d\u50cf\u6709\u4ec0\u4e48\u6025\u4e8b\u4f3c\u7684\uff0c\u4e00\u8bf4\u5b8c\u5c31\u548c\u90a3\u4e24 \u4e2a\u6b66\u58eb\u6025\u6025\u5fd9\u5fd9\u8d70\u4e86\u3002 \u3000\u3000\u5982\u610f\u548c\u674e\u9038\u8d70\u4e0b\u5730\u9053\u3002\u674e\u9038\u62d4\u51fa\u5b9d\u5251\uff0c\u501f\u7740\u5b9d\u5251\u7684\u5149\u534e\u8ba4\u8def\uff0c\u8d70\u4e86\u516d\u4e03\u6b65\u77f3\u7ea7\uff0c\u5ffd \u5730\u53c8\u89c9\u5934\u6655\u76ee\u7729\uff0c\u4e94\u810f\u516d\u8151\u597d\u4f3c\u8981\u7ffb\u8f6c\u6765\u4f3c\u7684\uff0c\u4e00\u4e2a\u5931\u8db3\uff0c\u7adf\u4ece\u77f3\u7ea7\u4e0a\u6eda\u4e0b\uff0c\u5982\u610f\u5927\u5403 \u4e00\u60ca\uff0c\u6025\u5fd9\u5c06\u4ed6\u6276\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u4f60\u53d7\u4e86\u91cd\u4f24\u5417\uff1f\u201d \u3000\u3000\u674e\u9038\u6df1\u6df1\u7684\u5438\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\uff0c\u54b1\u4eec\u5feb\u70b9\u8d70\u5427\uff01\u201d\u5176\u5b9e\u8fd9\u65f6\u4ed6\u4f53\u5185\u7684\u6bd2\u836f \u5df2\u7ecf\u53d1\u4f5c\uff0c\u6bd2\u6c14\u6b63\u5faa\u7740\u4ed6\u7684\u624b\u5c11\u9633\u7ecf\u8109\u653b\u4e0a\u5fc3\u623f\uff0c\u5e78\u4e8f\u4ed6\u5728\u5165\u5bab\u4e4b\u524d\uff0c\u66fe\u670d\u4e86\u4e00\u9897\u6b66\u7384 \u971c\u7ed9\u4ed6\u7684\u78a7\u7075\u4e39\uff0c\u867d\u7136\u4e0d\u662f\u5bf9\u75c7\u7684\u89e3\u836f\uff0c\u65f6\u95f4\u4e5f\u9694\u5f97\u8fc7\u957f\uff0c\u4f46\u603b\u662f\u589e\u52a0\u4e86\u4ed6\u8eab\u4f53\u6297\u6bd2\u7684 \u80fd\u529b\uff0c\u4ed6\u4ed7\u7740\u7cbe\u7eaf\u7684\u5185\u529f\uff0c\u5c06\u771f\u6c14\u8fd0\u4e86\u4e00\u8f6c\uff0c\u5c06\u8981\u653b\u5230\u4ed6\u5fc3\u623f\u7684\u4e00\u6761\u9ed1\u7ebf\uff0c\u53c8\u6e10\u6e10\u903c\u5230 \u624b\u8155\u4ee5\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u674e\u9038\u4e5f\u8d77\u4e86\u7591\u5fc3\uff1a\u201c\u96be\u9053\u592a\u5e73\u516c\u4e3b\u7ed9\u6211\u7684\u4e0d\u662f\u80fd\u89e3\u767e\u6bd2\u7684\u4e03\u5b9d\u4e39\uff0c\u53cd\u800c\u662f\u53e6\u5916 \u4e00\u79cd\u5389\u5bb3\u7684\u6bd2\u836f\u4e48\uff1f\u201d \u3000\u3000\u5982\u610f\u8d34\u5728\u4ed6\u7684\u8eab\u8fb9\u7167\u6599\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\u672c\u6765\u8981\u5e26\u6211\u5230\u7981\u536b\u519b\u53bb\u7684\uff0c\u8d70\u51fa\u4e86\u6e05\u534e\u95e8\uff0c \u5c0f\u59d0\u4e0d\u653e\u5fc3\uff0c\u53c8\u53eb\u6211\u56de\u6765\u3002\u60f3\u4e0d\u5230\u4f60\u679c\u7136\u7ed9\u4ed6\u4eec\u53d1\u73b0\u4e86\uff0c\u771f\u662f\u597d\u9669\uff01\u4f60\u53ef\u77e5\u9053\u4f60\u662f\u600e\u4e48 \u7ed9\u53d1\u73b0\u7684\u5417\uff1f\u201d \u3000\u3000\u674e\u9038\u5fc3\u5934\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\u3002\u201d\u5982\u610f\u9053\uff1a\u201c\u6211\u4e00\u56de\u6765\uff0c\u5c31\u542c\u5230\u516c\u4e3b\u5728\u62f7\u95ee\u5bab \u5973\uff0c\u4f60\u8eb2\u5728\u5c0f\u59d0\u623f\u4e2d\u7684\u79d8\u5bc6\uff0c\u662f\u90a3\u5bab\u5973\u6cc4\u6f0f\u7684\uff0c\u540e\u6765\u516c\u4e3b\u5c31\u5e26\u4e86\u90a3\u4e24\u4e2a\u6b66\u58eb\u8fdb\u53bb\uff0c\u6211\u4ee5 \u4e3a\u516c\u4e3b\u4e00\u5b9a\u5bf9\u4f60\u4e0d\u6000\u597d\u610f\u3002\u73b0\u5728\u770b\u6765\uff0c\u5979\u5bf9\u4f60\u8fd8\u50cf\u4e0d\u9519\uff0c\u6216\u8005\u662f\u6211\u778e\u7591\u5fc3\u4e86\u3002\u55ef\uff0c\u4f60\u7684 \u4f24\u662f\u600e\u4e48\u53d7\u7684\uff1f\u201d \u3000\u3000\u674e\u9038\u542c\u4e86\u8fd9\u8bdd\uff0c\u767b\u65f6\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u592a\u5e73\u516c\u4e3b\u679c\u7136\u662f\u60f3\u628a\u6211\u7f6e\u4e4b\u6b7b\u5730\uff0c\u8981 \u4e0d\u662f\u6b66\u5219\u5929\u5dee\u9063\u5982\u610f\u9001\u6211\u51fa\u5bab\uff0c\u53ea\u6015\u6211\u65e9\u5df2\u505a\u4e86\u7cca\u91cc\u7cca\u6d82\u7684\u51a4\u9b3c\u4e86\u3002\u201d \u3000\u3000\u5982\u610f\u542c\u8bf4\u4ed6\u662f\u4e2d\u4e86\u90a3\u6b66\u58eb\u7684\u6bd2\u836f\u98de\u5200\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6b66\u58eb\u662f\u516c\u4e3b\u7684\u4eb2\u4fe1\uff0c \u5979\u660e\u660e\u77e5\u9053\u8eb2\u5728\u623f\u4e2d\u7684\u662f\u4f60\uff0c\u8fd8\u8ba9\u5979\u7684\u6b66\u58eb\u4f24\u4f60\uff0c\u54ce\u5440\uff0c\u8fd9\u4e8b\u60c5\u4e0d\u5999\uff0c\u54b1\u4eec\u5feb\u8d70\uff0c\u5feb\u8d70\uff01 \u63d0\u9632\u6709\u4eba\u8ffd\u6765\uff01\u201d \u3000\u3000\u4e24\u4eba\u6025\u6b65\u5982\u98de\uff0c\u8dd1\u4e86\u4e00\u4f1a\u3002\u90a3\u5730\u9053\u9ed1\u9edd\u9edd\u7684\uff0c\u9664\u4e86\u4ed6\u4eec\u7684\u811a\u6b65\u58f0\u4e4b\u5916\uff0c\u518d\u4e5f\u6ca1\u6709\u5176 \u4ed6\u58f0\u606f\uff0c\u674e\u9038\u7a0d\u7a0d\u653e\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u610f\uff0c\u8c22\u8c22\u4f60\uff01\u201d \u3000\u3000\u5982\u610f\u7b11\u9053\uff1a\u201c\u8c22\u6211\u505a\u4ec0\u4e48\uff0c\u4f60\u5e94\u8be5\u591a\u8c22\u6211\u4eec\u7684\u5c0f\u59d0\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u662f\u554a\uff0c\u4f60\u4eec\u7684\u5c0f \u59d0\u5df2\u7ecf\u6551\u8fc7\u6211\u51e0\u6b21\u4e86\uff0c\u6211\u8fd8\u5f97\u597d\u597d\u8c22\u5979\u3002\u201d\u5982\u610f\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\uff01\u6211\u53ea\u5f53\u4f60\u5fc3\u4e0a\u6ca1\u6709 \u5c0f\u59d0\u5462\u3002\u4f60\u53ef\u77e5\u9053\uff0c\u8fd9\u4e5d\u5e74\u6765\u5979\u4e00\u76f4\u662f\u5728\u7b49\u5f85\u4f60\u554a\uff01\u201d \u3000\u3000\u674e\u9038\u5fc3\u5f26\u98a4\u6296\uff0c\u60f3\u8d77\u6b66\u7384\u971c\u5bf9\u81ea\u5df1\u786e\u662f\u6d77\u6837\u60c5\u6df1\uff0c\u5728\u5979\u51b3\u610f\u8981\u64ae\u5408\u81ea\u5df1\u548c\u5a49\u513f\u5a5a\u4e8b \u7684\u65f6\u5019\uff0c\u5fc3\u4e2d\u4e0d\u77e5\u8574\u85cf\u4e86\u591a\u5c11\u75db\u82e6\uff01\u4f46\u5979\u4e3a\u4e86\u5a49\u513f\u7684\u5e78\u798f\uff0c\u7adf\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\uff0c\u7518\u613f\u4f5c\u4e2a \u7ea2\u5a18\uff0c\u8fd9\u53c8\u662f\u4f55\u5176\u53ef\u4f69\uff01 \u3000\u3000\u674e\u9038\u5fc3\u60c5\u52a8\u8361\uff0c\u767b\u65f6\u6bd2\u6c14\u53c8\u5347\u4e0a\u6765\u3002\u4ed6\u6025\u5fd9\u5f3a\u6444\u5fc3\u795e\uff0c\u7ee7\u7eed\u524d\u884c\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u5230\u4e86 \u5730\u9053\u7684\u5c3d\u5934\uff0c\u5ffd\u542c\u5f97\u6709\u8f67\u8f67\u7684\u58f0\u54cd\uff0c\u5982\u610f\u53eb\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u4e00\u6296\u624b\uff0c\u98de\u51fa\u4e86\u4e24\u70b9\u5bd2\u661f\uff0c \u62c9\u4e86\u674e\u9038\uff0c\u6025\u6025\u5fd9\u5fd9\u7684\u5411\u5730\u9053\u53e3\u6251\u53bb\uff01 \u3000\u3000\u53ea\u542c\u5f97\u5916\u9762\u201c\u54ce\u54df\u201d\u4e00\u58f0\u53eb\u558a\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u674e\u9038\u548c\u5982\u610f\u5df2\u5230\u4e86\u5730\u9053\u53e3\uff0c\u5982\u610f\u4f38 \u624b\u4e00\u6309\u67a2\u7ebd\uff0c\u5f00\u4e86\u77f3\u95e8\uff0c\u4f46\u89c1\u4e00\u9762\u5343\u65a4\u95f8\u6b63\u5728\u6025\u901f\u964d\u4e0b\u3002 \u3000\u3000\u539f\u6765\u5bab\u4e2d\u4fee\u9020\u8fd9\u4e9b\u79d8\u5bc6\u5730\u9053\u7684\u65f6\u5019\uff0c\u4e3a\u4e86\u9884\u9632\u51fa\u53e3\u5904\u7ed9\u654c\u4eba\u53d1\u73b0\uff0c\u90fd\u88c5\u6709\u4e00\u9762\u5343\u65a4 \u95f8\uff0c\u5371\u6025\u4e4b\u65f6\uff0c\u53ef\u4ee5\u628a\u5343\u65a4\u95f8\u653e\u4e0b\uff0c\u5835\u6b7b\u6d1e\u53e3\uff0c\u9694\u65ad\u9053\u5175\uff0c\u597d\u8ba9\u91cc\u9762\u7684\u4eba\uff0c\u8f6c\u56de\u5bab\u4e2d\u3002 \u518d\u4ece\u7b2c\u4e8c\u6761\u5730\u9053\u9003\u8d70\uff0c\u5343\u65a4\u95f8\u975e\u4eba\u529b\u53ef\u80fd\u79fb\u52a8\uff0c\u987b\u7528\u8f98\u62a4\u5347\u964d\uff0c\u8fd9\u65f6\u5916\u9762\u6b63\u6709\u4e24\u4e2a\u6b66\u571f \u626f\u52a8\u8f98\u8f73\u7684\u94a2\u7d22\uff0c\u5c06\u5343\u65a4\u95f8\u653e\u4e0b\u6765\u3002\u5176\u4e2d\u4e00\u4e2a\u6b66\u58eb\u88ab\u5982\u610f\u7684\u6697\u5668\u6253\u4e2d\u624b\u8155\uff0c\u8feb\u5f97\u677e\u624b\uff0c \u8981\u4e0d\u7136\u8fd9\u5343\u65a4\u95f8\u65e9\u5df2\u843d\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u5982\u610f\u4e00\u4fef\u8eab\u4ece\u4e0b\u9762\u6eda\u4e86\u51fa\u53bb\uff0c\u674e\u9038\u8fdf\u4e86\u4e00\u6b65\uff0c\u90a3\u5343\u65a4\u95f8\u79bb\u5f00\u5730\u9762\u5df2\u662f\u4e0d\u5230\u4e09\u5c3a\uff0c\u674e \u9038\u5e73\u8eba\u5730\u4e0a\uff0c\u8fd0\u4e86\u5168\u8eab\u529f\u529b\uff0c\u5411\u4e0a\u4e00\u6258\uff0c\u7acb\u5373\u4f3c\u7bad\u4e00\u822c\u7684\u5c04\u51fa\uff0c\u4ed6\u53cc\u624b\u521a\u4e00\u677e\u52b2\uff0c\u4f46\u542c \u5f97\u8f70\u9686\u4e00\u58f0\uff0c\u90a3\u4e2a\u5343\u65a4\u95f8\u5df2\u7ecf\u843d\u4e86\u4e0b\u6765\uff0c\u771f\u662f\u9669\u5230\u6781\u70b9\uff01 \u3000\u3000\u674e\u9038\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u4ea6\u5df2\u4ece\u57ce\u5899\u8df3\u4e0b\uff0c\u8fd9\u5730\u9053\u901a\u5411\u7687\u57ce\u5916\u7684 \u4e00\u5904\u50fb\u9759\u6240\u5728\uff0c\u674e\u9038\u89c1\u53ea\u6709\u4e24\u4e2a\u6b66\u58eb\uff0c\u7a0d\u7a0d\u653e\u5fc3\uff0c\u4f46\u62ac\u5934\u4e00\u770b\uff0c\u5374\u53c8\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\u3002\u8fd9 \u4e24\u4e2a\u4eba\u6b63\u662f\u674e\u9038\u4ee5\u524d\u5728\u795e\u6b66\u8425\u65f6\u5019\u7684\u540c\u50da\uff0c\u4e00\u4e2a\u53eb\u5d14\u4ef2\u5143\uff0c\u662f\u5251\u672f\u540d\u5bb6\u8c22\u8865\u4e4b\u5f1f\u5b50\uff0c\u672a \u5165\u795e\u6b66\u8425\u4ee5\u524d\uff0c\u5728\u5317\u4e94\u7701\u5c31\u5927\u5927\u6709\u540d\uff0c\u53e6\u4e00\u4e2a\u540d\u53eb\u5468\u5927\u5e74\uff0c\u4e5f\u662f\u4e2a\u5185\u5bb6\u9ad8\u624b\u3002\u674e\u9038\u5f53\u5e74 \u5192\u5d4b\u5c71\u6b66\u58eb\u5f20\u4e4b\u5947\u4e4b\u540d\uff0c\u53c2\u52a0\u795e\u6b66\u8425\u7684\u9009\u62d4\u8bd5\uff0c\u5c31\u662f\u548c\u4ed6\u4eec\u540c\u4e00\u573a\u8003\u53d6\u7684\u3002\u5f53\u65f6\u5468\u5927\u5e74 \u66fe\u663e\u9732\u8fc7\u8e29\u8c46\u6210\u7c89\u7684\u6b66\u529f\uff0c\u800c\u5d14\u4ef2\u5143\u5219\u4ee5\u4e00\u5957\u201c\u7075\u733f\u5251\u6cd5\u201d\u60e7\u670d\u7fa4\u96c4\uff0c\u540e\u6765\u795e\u6b66\u8425\u7684\u90fd \u5c09\u674e\u660e\u4e4b\u8981\u674e\u9038\u548c\u4ed6\u6bd4\u6b66\uff0c\u674e\u9038\u5251\u4e0b\u7559\u60c5\uff0c\u6545\u610f\u8ba9\u4ed6\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u7684\u6b66\u529f\u4ec5\u5728\u795e\u6b66\u8425\u4e09\u5927\u9ad8\u624b\u4e4b\u4e0b\uff0c\u674e\u9038\u82e5\u7136\u672a\u66fe\u53d7\u4f24\uff0c\u81ea\u662f\u5e94\u4ed8\u5f97\u4e86\u3002\u4f46 \u73b0\u5728\u4e2d\u4e86\u5267\u6bd2\uff0c\u90a3\u5c31\u6b8a\u65e0\u628a\u63e1\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5d14\u4ef2\u5143\u54c8\u54c8\u7b11\u9053\uff1b\u201c\u674e\u9038\uff0c\u4f60\u8fd8\u60f3\u9003\u5f97\u4e86\u5417\uff1f\u6765\uff0c\u6765\uff0c\u6765\uff0c\u6765\uff0c\u54b1\u4eec\u518d\u6765\u6bd4 \u5212\u6bd4\u5212\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u5d14\u5144\uff0c\u4f60\u6211\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f55\u4ee5\u82e6\u82e6\u76f8\u903c\uff1f\u201d\u5d14\u4ef2\u65e0\u9053\uff1a\u201c\u4f60\u4e0e\u6211\u65e0 \u51a4\u65e0\u4ec7\uff0c\u4e0e\u592a\u5e73\u516c\u4e3b\u6709\u4ec7\uff0c\u516c\u4e3b\u4e0d\u80af\u9976\u4f60\uff0c\u4f60\u505a\u4e86\u51a4\u9b3c\uff0c\u5230\u960e\u738b\u8001\u5b50\u90a3\u91cc\u63a7\u8bc9\u5979\u5427\uff0c\u6211 \u662f\u5949\u4e86\u4e3b\u4eba\u4e4b\u547d\uff0c\u4f60\u987b\u6028\u6211\u4e0d\u5f97\u3002\u95f2\u8bdd\u5c11\u8bf4\uff0c\u4eae\u5251\u5427\uff0c\u5484\uff0c\u4f60\u5728\u795e\u6b66\u8425\u65f6\u5019\u7684\u5a01\u98ce\u54ea\u91cc \u53bb\u4e86\uff1f\u201d\u539f\u6765\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4ece\u795e\u6b66\u8425\u8f6c\u5230\u5bab\u4e2d\u5f53\u4e86\u5bbf\u536b\u4e4b\u540e\uff0c\u592a\u5e73\u516c\u4e3b\u77e5\u9053\u4ed6\u4eec\u672c\u9886\u9ad8\u5f3a\uff0c \u5c31\u628a\u4ed6\u4eec\u6536\u4e3a\u5fc3\u8179\u7684\u6b66\u58eb\u3002\u4ed6\u4eec\u73b0\u5728\u6b63\u662f\u5949\u4e86\u516c\u4e3b\u4e4b\u547d\uff0c\u6765\u53d6\u674e\u9038\u548c\u5982\u610f\u7684\u9996\u7ea7\u7684\u3002 \u3000\u3000\u674e\u9038\u88ab\u4ed6\u903c\u5f97\u65e0\u8def\u53ef\u8d70\uff0c\u52c3\u7136\u706b\u8d77\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u5d14\u6797\u5143\uff0c\u54b1\u4eec\u4fbf\u518d\u8f83\u91cf\u4e00 \u6b21\u5251\u6cd5\uff0c\u8fd9\u6b21\u53ef\u4e0d\u6bd4\u5728\u795e\u6b66\u8425\u7684\u65f6\u5019\u4e86\uff0c\u5484\uff0c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u5d14\u4ef2\u5143\u5927\u7b11\u9053\uff1a \u201c\u8fd9\u4e2a\u4f55\u987b\u518d\u8bf4\uff01\u201d\u5530\u4e00\u5251\uff0c\u4fbf\u523a\u8fc7\u6765\uff01 \u3000\u3000\u674e\u9038\u5438\u4e86\u53e3\u6c14\uff0c\u4e00\u4e2a\u201c\u56de\u8eab\u62d7\u6b65\u201d\uff0c\u5251\u5982\u98de\u51e4\uff0c\u659c\u659c\u524a\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c \u5d14\u4ef2\u5143\u7684\u5251\u950b\u5df2\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u5d14\u4ef2\u5143\u53c8\u60ca\u53c8\u559c\uff0c\u60ca\u7684\u662f\u674e\u9038\u5b9d\u5251\u950b\u5229\uff0c\u559c\u7684\u662f\u4ed6\u5df2\u8bd5 \u51fa\u4e86\u674e\u9038\u7684\u5185\u529b\u5927\u4e0d\u5982\u524d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u592a\u5e73\u516c\u4e3b\u679c\u7136\u6ca1\u6709\u9a97\u6211\uff0c\u4ed6\u7684\u786e\u662f\u5df2\u7ecf\u4e2d\u6bd2\u53d7 \u4f24\uff01\u201d\u8981\u77e5\u5d14\u4ef2\u5143\u672c\u662f\u674e\u9038\u7684\u624b\u4e0b\u8d25\u5c06\uff0c\u8981\u4e0d\u662f\u4ed6\u77e5\u9053\u674e\u9038\u4e2d\u6bd2\u53d7\u4f24\uff0c\u4ed6\u662f\u600e\u4e48\u6837\u4e5f\u4e0d \u6562\u6765\u7684\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u5982\u610f\u548c\u5468\u5927\u5e74\u4e5f\u4ea4\u4e0a\u4e86\u624b\uff0c\u5468\u5927\u5e74\u521a\u624d\u4e2d\u4e86\u5979\u7684\u6697\u5668\uff0c\u867d\u7136\u4ec5\u4ec5\u662f\u5212\u7834\u4e86 \u76ae\u8089\uff0c\u4f46\u4e5f\u662f\u4e2a\u6210\u540d\u7684\u4eba\u7269\uff0c\u5403\u4e86\u4e00\u4e2a\u5c0f\u4e2b\u73af\u7684\u4e8f\uff0c\u8fd9\u53e3\u6c14\u4ee5\u662f\u5fcd\u4e0d\u4e0b\u6765\uff0c\u4ed6\u7528\u7684\u662f\u4e00 \u6761\u8f6f\u97ad\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u201c\u56de\u98ce\u626b\u67f3\u201d\u8fde\u73af\u4e09\u97ad\u7684\u7edd\u6280\uff0c\u5530\uff0c\u5530\uff0c\u5530\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u5377\u8d77\u4e86\u4e00 \u56e2\u97ad\u5f71\uff0c\u5982\u610f\u7528\u4e86\u4e00\u62db\u201c\u4e00\u9e64\u51b2\u5929\u201d\u7684\u8eab\u6cd5\uff0c\u5530\u7684\u4e00\u58f0\uff0c\u5468\u5927\u5e74\u7684\u7b2c\u4e00\u97ad\u8d34\u7740\u5979\u7684\u978b\u5e95 \u626b\u8fc7\uff0c\u5982\u610f\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7ffb\u8eab\uff0c\u4fef\u51b2\u4e0b\u6765\uff0c\u624b\u4e0a\u5df2\u591a\u4e86\u4e00\u628a\u9752\u94dc\u5251\uff0c\u97ad\u5251\u76f8\u4ea4\uff0c\u5468\u5927\u5e74 \u7684\u957f\u97ad\u7ed9\u5979\u62e8\u5f00\uff0c\u5982\u610f\u4e5f\u8d81\u52bf\u5012\u7eb5\u5f00\u53bb\uff0c\u5468\u5927\u5e74\u7684\u7b2c\u4e8c\u97ad\u53c8\u7ed9\u5979\u5316\u89e3\u4e86\uff0c\u5f85\u5230\u5468\u5927\u5e74\u671d \u7b2c\u4e09\u97ad\u626b\u6765\uff0c\u5982\u610f\u5df2\u89e3\u4e0b\u4e86\u675f\u8170\u7684\u7ea2\u7ef8\uff0c\u7ea2\u7ef8\u6325\u821e\uff0c\u4fe8\u5982\u4e00\u7247\u7ea2\u971e\uff0c\u75be\u5377\u800c\u6765\uff0c\u5c06\u5468\u5927 \u5e74\u7684\u957f\u97ad\u88f9\u4f4f\uff0c\u53f3\u624b\u957f\u5251\u4e00\u4f38\uff0c\u4fbf\u6765\u523a\u4ed6\u624b\u8155\uff0c\u5468\u5927\u5e74\u5185\u529b\u900f\u8fc7\u97ad\u68a2\uff0c\u8fd0\u52b2\u4e00\u6325\uff0c\u547c\u7684 \u4e00\u58f0\uff0c\u8f6f\u97ad\u6709\u5982\u868a\u9f99\u51fa\u6d77\uff0c\u500f\u7136\u95f4\u8131\u51fa\u91cd\u56f4\uff0c\u521a\u597d\u628a\u5982\u610f\u90a3\u4e00\u5251\u62e6\u4f4f\u3002 \u3000\u3000\u5982\u610f\u7684\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u6bd4\u82f1\u96c4\u4f1a\u4e0a\u7684\u90a3\u4e9b\u4ec0\u4e48\u5be8\u4e3b\u3001\u638c\u95e8\u8fd8\u8981\u96be\u6597\u5f97\u591a\uff01\u201d \u5468\u5927\u5e74\u66f4\u5403\u60ca\u4e0d\u5c0f\uff0c\u4ed6\u6709\u4e09\u5341\u5e74\u4ee5\u4e0a\u7684\u5185\u5bb6\u529f\u529b\uff0c\u51ed\u7740\u8fd9\u6761\u866c\u9f99\u97ad\u4e5f\u66fe\u6253\u904d\u5927\u6c5f\u5357\u5317\uff0c \u60f3\u4e0d\u5230\u4eca\u65e5\u78b0\u5230\u4e86\u52b2\u654c\uff0c\u8fd9\u4e2a\u52b2\u654c\u5374\u4e0d\u8fc7\u662f\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u4e2b\u73af\uff01 \u3000\u3000\u8fd9\u4e00\u6765\u4e24\u4eba\u90fd\u4e0d\u6562\u6709\u4e9b\u8f7b\u654c\uff0c\u4f46\u5982\u610f\u4e3a\u4e86\u8981\u7167\u987e\u674e\u9038\uff0c\u5374\u4e0d\u514d\u5206\u4e86\u5fc3\u795e\uff0c\u6fc0\u6218\u4e2d\u5ffd \u542c\u5f97\u5d14\u4ef2\u5143\u4e00\u9635\u72c2\u7b11\u4e4b\u58f0\uff0c\u5982\u610f\u626d\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u674e\u9038\u81c2\u818a\u4e0a\u4e00\u7247\u8840\u7ea2\uff0c\u4f3c\u4e4e\u662f\u5df2\u4e2d\u4e86\u654c \u4eba\u7684\u4e00\u5251\u3002\u5982\u610f\u53eb\u9053\uff1a\u201c\u6bbf\u4e0b\u522b\u614c\uff0c\u6211\u6765\u5566\uff01\u201d\u98de\u8eab\u4e00\u7eb5\uff0c\u5468\u5927\u5e74\u5982\u4f55\u80af\u653e\u8fc7\u5979\uff0c\u957f\u97ad \u4e00\u6325\uff0c\u97ad\u68a2\u626b\u4e2d\u4e86\u5982\u610f\u7684\u811a\u8e1d\uff0c\u5982\u610f\u4e00\u8de4\u6454\u5012\uff0c\u6025\u5fd9\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u7ffb\u4e86\u8d77\u6765\uff0c\u5468\u5927\u5e74 \u7684\u957f\u97ad\uff0c\u5df2\u4f3c\u66b4\u98ce\u9aa4\u96e8\u822c\u7684\u88ad\u5230\uff0c\u5982\u610f\u88ab\u4ed6\u56f0\u4f4f\uff0c\u7adf\u7136\u8131\u4e0d\u4e86\u8eab\u3002 \u3000\u3000\u674e\u9038\u53eb\u9053\uff1a\u201c\u4f60\u5c0f\u5fc3\u5e94\u4ed8\u654c\u4eba\uff0c\u6211\u4e0d\u788d\u4e8b\uff01\u201d\u5176\u5b9e\u4ed6\u4e2d\u7684\u90a3\u4e00\u5251\u6b63\u5728\u5de6\u81c2\u7684\u201c\u66f2\u6c60 \u7a74\u201d\u4e4b\u5904\uff0c\u4e00\u6761\u624b\u81c2\u5df2\u662f\u4e0d\u80fd\u52a8\u5f39\u3002\u5d14\u4ef2\u5143\u4e00\u5251\u5f97\u624b\uff0c\u653b\u5f97\u66f4\u731b\uff0c\u674e\u9038\u8fd0\u4e86\u4e00\u53e3\u771f\u6c14\uff0c \u6545\u610f\u5356\u4e2a\u7834\u7efd\uff0c\u8ba9\u4ed6\u6b3a\u8fd1\u8eab\u6765\uff0c\u731b\u5730\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u5251\u5149\u8d77\u5904\uff0c\u5982\u7bad\u79bb\u5f26\uff0c\u8fd9\u4e00\u62db \u8d25\u4e2d\u6c42\u80dc\uff0c\u7cbe\u5999\u4e4b\u6781\uff0c\u53ea\u542c\u5f97\u5530\u7684\u4e00\u58f0\uff0c\u5d14\u4ef2\u5143\u7684\u80a9\u5934\uff0c\u4e5f\u4e2d\u4e86\u4e00\u5251\uff0c\u674e\u9038\u6697\u53eb\u53ef\u60dc\uff0c \u82e5\u662f\u4ed6\u5185\u529b\u5145\u8db3\uff0c\u518d\u6df1\u4e09\u5bf8\uff0c\u8fd9\u4e00\u5251\u5c31\u53ef\u4ee5\u628a\u5bf9\u65b9\u7684\u7435\u7436\u9aa8\u523a\u7a7f\uff01 \u3000\u3000\u674e\u9038\u4e2d\u4e86\u5267\u6bd2\uff0c\u5168\u4ed7\u7740\u4e00\u53e3\u771f\u6c14\uff0c\u62a4\u7740\u5fc3\u5934\uff0c\u8fd9\u65f6\u4e5f\u5f3a\u8fd0\u7384\u529f\uff0c\u62da\u5c3d\u5168\u529b\u3002\u4e00\u5251\u4f24 \u4e86\u654c\u4eba\uff0c\u672c\u8eab\u4ea6\u5df2\u652f\u6301\u4e0d\u4f4f\uff0c\u5ffd\u5730\u611f\u5230\u773c\u524d\u4e00\u7247\u6a21\u7cca\uff0c\u4e00\u79cd\u9ebb\u75f9\u7684\u611f\u89c9\uff0c\u6e10\u6e10\u4ece\u5de6\u81c2\u5ef6 \u53ca\u5168\u8eab\uff0c\u4e0d\u7531\u5f97\u8dc4\u8dc4\u8e09\u8e09\u7684\u5012\u9000\u51e0\u6b65\u3002 \u3000\u3000\u5d14\u4ef2\u5143\u89c1\u6b64\u60c5\u5f62\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u75bc\u75db\u4e5f\u90fd\u5fd8\u4e86\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u53c8\u6251\u4e0a\u6765\uff0c\u4ea4\u624b\u6570\u62db\uff0c\u674e \u9038\u7684\u5c0f\u8179\u53c8\u4e2d\u4e86\u4e00\u5251\uff0c\u88ab\u5251\u950b\u5212\u7834\u4e86\u4e09\u5bf8\u6765\u957f\u7684\u4f24\u53e3\uff0c\u9c9c\u8840\u6cae\u6cae\u6d41\u51fa\uff0c\u4ed6\u867d\u7136\u6781\u529b\u54ac\u7259 \u5fcd\u7740\uff0c\u4e5f\u4e0d\u7981\u54fc\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5982\u610f\u8fd9\u65f6\u4e5f\u6b63\u5230\u4e86\u5403\u7d27\u7684\u5173\u5934\uff0c\u5979\u7684\u672c\u9886\u672c\u6765\u4e0d\u5f31\u4e8e\u5468\u5927\u5e74\uff0c\u4f46\u5fc3\u795e\u4e00\u4e71\uff0c\u5374\u8fde\u8fde \u906d\u53d7\u9669\u62db\uff0c\u8fd9\u65f6\u5ffd\u5730\u542c\u5230\u674e\u9038\u547b\u541f\u7684\u58f0\u97f3\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u5468\u5927\u5e74\u5927\u559d\u4e00\u58f0:\u201c\u7740\uff01\u201d\u957f\u97ad\u4e00 \u6325\uff0c\u500f\u5730\u5c06\u5979\u5377\u4e86\u8d77\u6765\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u5468\u5927\u5e74\u5f97\u610f\u7684\u7b11\u58f0\u521a\u521a\u53d1\u51fa\uff0c\u5ffd\u542c\u5f97\u5982\u610f\u4e5f\u559d\u4e00\u58f0\u201c\u7740\uff01\u201d \u5c06\u624b\u4e2d\u7684\u957f\u5251\u5316\u6210\u4e86\u4e00\u9053\u94f6\u8679\uff0c\u500f\u7136\u95f4\u4fbf\u8131\u624b\u63b7\u51fa\uff01\u8fd9\u4e00\u62db\u662f\u4e0e\u654c\u5055\u4ea1\u7684\u6740\u624b\u7edd\u62db\uff0c\u975e \u5230\u6700\u5371\u9669\u65f6\u5019\uff0c\u51b3\u4e0d\u8f7b\u6613\u4f7f\u7528\uff0c\u5468\u5927\u5e74\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u654c\u4eba\u5df2\u88ab\u4ed6\u7684\u957f\u97ad\u5377\u7740\uff0c\u5c4a\u7136\u8fd8\u6709 \u8fd9\u4e00\u62db\u6740\u624b\uff01\u4ed6\u5377\u7740\u654c\u4eba\uff0c\u987a\u7740\u97ad\u52bf\uff0c\u5f80\u540e\u4e00\u6298\uff0c\u63a5\u7740\u518d\u5411\u524d\u6454\u51fa\uff0c\u5c31\u5728\u4ed6\u521a\u521a\u8981\u6454\u51fa \u7684\u65f6\u5019\uff0c\u731b\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u5c31\u88ab\u5251\u950b\u7a7f\u8fc7\u4e86\u4ed6\u7684\u54bd\u5589\uff01 \u3000\u3000\u5468\u5927\u5e74\u5927\u53eb\u4e00\u58f0\uff0c\u957f\u97ad\u4e00\u7529\uff0c\u5f80\u540e\u4fbf\u5012\uff0c\u4f46\u8fd9\u4e00\u7529\u4e43\u662f\u4ed6\u6bd5\u751f\u529f\u529b\u6240\u805a\uff0c\u5982\u610f\u88ab\u4ed6 \u4e00\u7529\uff0c\u767b\u65f6\u4e5f\u6655\u5012\u5730\u4e0a\uff0c\u5931\u4e86\u77e5\u89c9\uff01 \u3000\u3000\u6fc0\u6218\u4e2d\u7684\u674e\u9038\u548c\u5d14\u4ef2\u5143\u542c\u5f97\u4ed6\u4eec\u51c4\u5389\u7684\u53eb\u58f0\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u505c\u4e86\u4e0b\u6765\uff0c\u773c \u5149\u4e00\u77a5\uff0c\u5d14\u4ef2\u5143\u89c1\u540c\u4f34\u4e27\u547d\uff0c\u56fa\u7136\u662f\u5927\u5927\u5403\u60ca\uff0c\u674e\u9038\u89c1\u5982\u610f\u76f4\u633a\u633a\u7684\u8eba\u5728\u5730\u4e0a\uff0c\u53ea\u9053\u5979 \u4e5f\u5df2\u6d3b\u4e0d\u6210\u4e86\uff0c\u66f4\u662f\u611f\u5230\u5b8c\u5168\u7edd\u671b\uff01\uff3f \u3000\u3000\u5d14\u4ef2\u5143\u53eb\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u5f03\u5251\u6295\u964d\uff0c\u5c31\u8981\u8ddf\u4ed6\u4eec\u4e00\u540c\u8d70\u4e86\uff01\u201d\u674e\u9038\u5f85\u4ed6\u6251\u4e0a\u524d\u6765\uff0c\u84e6 \u5730\u4e00\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u5b9d\u5251\u4e00\u6325\uff0c\u767b\u65f6\u6296\u8d77\u4e86\u6570\u5341\u6735\u5251\u82b1\uff0c\u4fe8\u5982\u9ed1\u591c \u7e41\u661f\uff0c\u6b92\u843d\u5982\u96e8\uff0c\u5d14\u4ef2\u5143\u4e00\u58f0\u60e8\u53eb\u3002\u6eda\u51fa\u4e86\u6570\u4e08\u4e4b\u5916\uff01\u539f\u6765\u674e\u9038\u8d81\u8fd9\u65f6\u673a\uff0c\u65e9\u5df2\u8fd0\u4e86\u4e00 \u53e3\u771f\u6c14\uff0c\u5c06\u5185\u529b\u900f\u8fc7\u5251\u5c16\uff0c\u84c4\u52b2\u5f85\u53d1\uff0c\u5f85\u5d14\u4ef2\u5143\u6251\u5230\uff0c\u4ed6\u7a81\u7136\u95f4\u4fbf\u5c55\u51fa\u6740\u624b\uff0c\u8fd9\u4e00\u628a\u540d \u4e3a\u201c\u94f6\u6cb3\u661f\u843d\u201d\uff0c\u6b63\u662f\u5ce8\u5d4b\u5251\u6cd5\u4e2d\u6700\u7cbe\u5999\u7684\u4e00\u62db\uff0c\u5d14\u4ef2\u5143\u4e5f\u662f\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u5982\u4f55\u62db\u67b6\u5f97 \u4e86\uff1f\u4e00\u62db\u4e4b\u5185\uff0c\u8eab\u4e0a\u53d7\u4e86\u4e03\u5904\u5251\u4f24\u3002 \u3000\u3000\u674e\u9038\u8fd9\u4e00\u5251\u523a\u51fa\uff0c\u8033\u4e2d\u542c\u5f97\u654c\u4eba\u51c4\u5389\u7684\u558a\u58f0\uff0c\u7cbe\u795e\u4e00\u677e\uff0c\u767b\u65f6\u611f\u5230\u5730\u8f6c\u5929\u65cb\uff0c\u773c\u775b \u53d1\u9ed1\uff0c\u5168\u8eab\u9ebb\u75f9\uff0c\u762b\u5728\u5730\u4e0a\uff0c\u4e00\u70b9\u6c14\u529b\u4e5f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u5d14\u4ef2\u5143\u5ffd\u5730\u8815\u8815\u800c\u52a8\uff0c\u5411\u7740\u4ed6\u6162\u6162\u7684\u722c\u8fc7\u6765\uff0c\u539f\u6765\u4ed6\u8eab\u4e2d\u4e03\u5251\uff0c\u867d \u7136\u4f24\u5f97\u6781\u91cd\uff0c\u5374\u8fd8\u672a\u66fe\u6bd9\u547d\uff01 \u3000\u3000\u5d14\u4ef2\u5143\u5728\u5730\u4e0a\u6162\u6162\u79fb\u52a8\uff0c\u4e00\u5bf8\u4e00\u5bf8\u7684\u5411\u7740\u674e\u9038\u7684\u65b9\u5411\u722c\u6765\uff0c\u4e24\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\u8d8a\u6765\u8d8a \u8fd1\uff0c\u6e10\u6e10\u674e\u9038\u53ef\u4ee5\u542c\u5230\u4ed6\u6c89\u91cd\u7684\u5598\u6c14\u7684\u58f0\u606f\uff0c\u611f\u5230\u4ed6\u5251\u950b\u7684\u5bd2\u610f\u4e86\uff01\u674e\u9038\u611f\u5230\u4e86\u6b7b\u4ea1\u7684 \u6050\u6016\uff0c\u5fc3\u5934\u4e00\u7247\u82cd\u51c9\uff0c\u4e0a\u5b98\u5a49\u513f\u3001\u6b66\u7384\u971c\u3001\u4ed6\u7684\u513f\u5b50\uff0c\u4e00\u4e2a\u4e00\u4e2a\u5f71\u5b50\u4ece\u4ed6\u5fc3\u5934\u63a0\u8fc7\uff0c\u4ed6 \u4e0d\u662f\u6015\u6b7b\uff0c\u800c\u662f\u8fd8\u4e0d\u613f\u610f\u6b7b\u554a\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u674e\u9038\uff0c\u674e\u9038\uff01\u201d\u674e\u9038\u5fc3\u5934\u4e00\u9707\uff0c \u201c\u8fd9\u662f\u6211\u7684\u5e7b\u89c9\u5462\uff0c\u8fd8\u662f\u5979\u771f\u7684\u6765\u4e86\uff1f\u201d\u4ed6\u6b63\u8981\u6323\u624e\u7740\u62ac\u8d77\u5934\u6765\uff0c\u5d14\u4ef2\u5143\u5ffd\u5730\u5927\u53eb\u4e00\u58f0\uff0c \u6eda\u5230\u4ed6\u7684\u8ddf\u524d\uff0c\u4e00\u5251\u5c31\u5411\u4ed6\u5fc3\u623f\u624e\u53bb\uff01 \u3000\u3000\u674e\u9038\u773c\u775b\u53d1\u9ed1\uff0c\u5fc3\u4e2d\u53eb\u9053\uff1a\u201c\u5b8c\u4e86\uff01\u5b8c\u4e86\uff01\u201d\u7136\u800c\u5947\u602a\u5f97\u5f88\uff0c\u4ed6\u5e76\u6ca1\u6709\u611f\u5230\u7279\u522b\u75bc \u75db\uff0c\u4e5f\u597d\u50cf\u8fd8\u6709\u77e5\u89c9\uff0c\u8ff7\u7cca\u4e2d\u9690\u9690\u611f\u5230\u4e00\u53ea\u6e29\u6696\u67d4\u8f6f\u7684\u624b\u638c\u8f7b\u8f7b\u7684\u629a\u6170\u7740\u4ed6\uff0c\u9762\u988a\u4e0a\u611f \u5230\u9732\u73e0\u7684\u6e05\u51c9\uff0c\u8fd9\u7edd\u5bf9\u4e0d\u4f1a\u662f\u90a3\u4e2a\u51f6\u6076\u7684\u654c\u4eba\uff0c\u5440\u3002\u8fd9\u4e0d\u662f\u4f5c\u68a6\u5427\uff1f\u4ed6\u7528\u529b\u773c\u5f00\u4e86\u773c\u775b\uff0c \u9661\u7136\u95f4\u53d1\u73b0\u4e00\u4e2a\u767d\u8863\u5c11\u5973\u7ad9\u5728\u4ed6\u7684\u9762\u524d\uff0c\u4ed6\u60ca\u559c\u4ea4\u96c6\uff0c\u53eb\u4e86\u4e00\u58f0\uff0c\u7531\u4e8e\u5fc3\u60c5\u8fc7\u4efd\u7684\u6fc0\u52a8\uff0c \u767b\u65f6\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e2a\u5c11\u5973\u6b63\u662f\u6b66\u7384\u971c\uff0c\u5979\u662f\u56de\u5bab\u4e4b\u540e\uff0c\u542c\u5230\u5bab\u5973\u7684\u62a5\u544a\uff0c\u77e5\u9053\u674e\u9038\u5df2\u4ece\u5730\u9053\u51fa\u53bb\uff0c \u5306\u5306\u5fd9\u5fd9\u7684\u8d76\u6765\u7684\uff0c\u5c31\u5728\u90a3\u5343\u94a7\u4e00\u53d1\u7684\u65f6\u5019\uff0c\u5979\u5c06\u5d14\u4ef2\u5143\u4e00\u811a\u8e22\u5f00\uff0c\u6551\u4e86\u674e\u9038\u3002\u674e\u9038\u9762 \u988a\u4e0a\u611f\u5230\u7684\u6e05\u51c9\uff0c\u6b63\u662f\u5979\u6ef4\u4e0b\u6765\u7684\u6cea\u6c34\u3002 \u3000\u3000\u5f85\u5230\u674e\u9038\u60ed\u590d\u77e5\u89c9\u7684\u65f6\u5019\uff0c\u5df2\u662f\u56de\u5230\u4e86\u957f\u5b59\u6cf0\u7684\u5bb6\u4e2d\uff0c\u4ed6\u53d1\u73b0\u81ea\u5df1\u8eba\u5728\u5e8a\u4e0a\uff0c\u6b66\u7384 \u971c\u5750\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u6b63\u5728\u7528\u4e00\u65b9\u624b\u7ee2\u62ed\u6cea\u3002 \u3000\u3000\u674e\u9038\u5438\u4e86\u53e3\u6c14\uff0c\u4f46\u89c9\u80f8\u53e3\u9690\u9690\u4f5c\u95f7\uff0c\u771f\u6c14\u5df2\u662f\u4e0d\u80fd\u8fd0\u8f6c\u81ea\u5982\uff0c\u4ed6\u5fc3\u5934\u98a4\u6817\uff0c\u7136\u800c\u4ed6 \u6240\u76fc\u671b\u7684\u4eba\u513f\u6bd5\u7adf\u662f\u5728\u4ed6\u7684\u8eab\u8fb9\u4e86\uff0c\u56e0\u6b64\u5728\u6b7b\u4ea1\u7684\u6050\u6016\u4e2d\u4e5f\u611f\u5230\u4e86\u6b22\u6b23\uff0c\u4ed6\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u7384\u971c\uff0c\u591a\u8c22\u4f60\u53c8\u4e00\u6b21\u7684\u6551\u4e86\u6211\uff0c\u6211\uff0c\u6211\uff0c\u54b3\u2014\u2014\u201d\u6b66\u7384\u971c\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u591a\u8bf4\u8bdd\uff0c\u5b89 \u5fc3\u7684\u9759\u517b\u5427\u3002\u6211\u8fd9\u91cc\u6709\u4e24\u9897\u78a7\u7075\u4e39\uff0c\u4f60\u8fc7\u4e24\u4e2a\u65f6\u8fb0\u670d\u98df\u4e00\u7c92\u3002\u201d\u5979\u638f\u51fa\u94f6\u74f6\u653e\u5728\u5e8a\u5934\u4e0a\uff0c \u674e\u9038\u611f\u5230\u4e00\u80a1\u6696\u610f\uff0c\u597d\u4f3c\u7535\u6d41\u822c\u7684\u901a\u8fc7\u4ed6\u7684\u5168\u8eab\uff0c\u4f46\u4ed6\u4e5f\u611f\u5230\u4e86\u6b66\u7384\u971c\u7684\u5fae\u7b11\uff0c\u7adf\u662f\u5f02 \u6837\u7684\u51c4\u51c9\uff01 \u3000\u3000\u674e\u9038\u4ecd\u7136\u7981\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5982\u610f\u5462\uff1f\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u5979\u6ca1\u6709\u6b7b\uff0c\u6211\u4e5f\u5c06\u5979\u6551\u4e86\u3002\u201d\u674e \u9038\u9053\uff1a\u201c\u8bf7\u4f60\u4ee3\u6211\u5411\u5979\u9053\u8c22\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u518d\u60f3\u65c1\u7684\u4e8b\u60c5\uff0c\u542c\u6211\u7684\u8bdd\uff0c\u5b89\u5fc3\u9759 \u517b\u5427\u3002\u201d\u674e\u9038\u51dd\u671b\u7740\u5979\uff0c\u597d\u50cf\u5fc3\u4e2d\u60ac\u6302\u770b\u4ec0\u4e48\u4e8b\u60c5\u60f3\u95ee\u5979\u7684\u795e\u8272\u3002\u6b66\u7384\u971c\u77e5\u4ed6\u5fc3\u610f\uff0c\u67d4 \u58f0\u8bf4\u9053\uff1a\u201c\u6211\u90fd\u544a\u8bc9\u4f60\u5427\uff0c\u8ba9\u4f60\u653e\u5fc3\u3002\u4e71\u4e8b\u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c\u5a49\u513f\u548c\u5929\u540e\u965b\u4e0b\u90fd\u8fd8\u6d3b\u7740\u3002\u592a \u5b50\u8fd9\u4e24\u5929\u5c31\u4f1a\u56de\u6765\uff0c\u5929\u540e\u965b\u4e0b\u5df2\u7ecf\u4e0b\u8bcf\u9000\u4f4d\uff0c\u8ba9\u592a\u5b50\u505a\u7687\u5e1d\u4e86\u3002\u6c5f\u5c71\u5df2\u662f\u4ea4\u8fd8\u7ed9\u4e86\u4f60\u4eec \u674e\u5bb6\uff0c\u4f60\u5e94\u8be5\u53ef\u4ee5\u6ee1\u610f\u4e86\u5427\uff1f\u201d \u3000\u3000\u6b66\u5219\u5929\u9000\u4f4d\u7684\u6d88\u606f\uff0c\u674e\u9038\u82e5\u662f\u5728\u524d\u51e0\u5e74\u542c\u5230\uff0c\u4e00\u5b9a\u4f1a\u6b22\u559c\u5f97\u8df3\u8dc3\u8d77\u6765\uff0c\u73b0\u5728\u542c\u5230\uff0c \u5fc3\u60c5\u5374\u53cd\u800c\u66f4\u7070\u6697\u4e86\u3002\u5ffd\u542c\u5f97\u623f\u95e8\u5916\u6709\u811a\u6b65\u58f0\u8d70\u6765\u8d70\u53bb\uff0c\u6b66\u7384\u971c\u9053\uff1a\u201c\u957f\u5b59\u6cf0\u56de\u6765\u4e86\uff0c \u6211\u8fd8\u6709\u70b9\u4e8b\u60c5\u8981\u5230\u5bab\u4e2d\u4e00\u8d9f\uff0c\u4f60\u5b89\u5fc3\u9759\u517b\uff0c\u660e\u5929\u6211\u518d\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u674e\u9038\u632a\u52a8\u8eab\u8eaf\uff0c\u60f3\u501a\u7740\u5e8a\u67f1\uff0c\u76ee\u9001\u5979\u7684\u80cc\u5f71\uff0c\u5ffd\u5730\u611f\u5230\u8179\u4e2d\u9635\u9635\u5267\u75db\uff0c\u56db\u80a2\u4ea6\u5df2\u5b8c \u5168\u9ebb\u75f9\uff0c\u529b\u4e0d\u4ece\u5fc3\uff0c\u674e\u9038\u5fc3\u5934\u4e00\u7247\u5bd2\u51b7\uff0c\u6b66\u7384\u971c\u7684\u5f71\u5b50\u5df2\u7ecf\u4ece\u4ed6\u7684\u773c\u5e18\u6d88\u5931\u4e86\u3002\u5509\uff0c\u4ee5 \u540e\u8fd8\u53ef\u80fd\u518d\u89c1\u5230\u5979\u5417\uff1f \u3000\u3000\u674e\u9038\u6323\u624e\u7740\u6293\u5230\u653e\u5728\u5e8a\u5934\u4e0a\u7684\u90a3\u4e2a\u94f6\u74f6\uff0c\u541e\u4e86\u4e00\u7c92\u6b66\u7384\u971c\u9001\u7ed9\u4ed6\u7684\u78a7\u7075\u4e39\uff0c\u75db\u82e6\u662f \u51cf\u8f7b\u4e86\uff0c\u4f46\u547c\u5438\u4ecd\u662f\u672a\u80fd\u8212\u7545\uff0c\u60f3\u8fd0\u8f6c\u771f\u6c14\uff0c\u90a3\u66f4\u662f\u4e0d\u80fd\u4e86\u3002\u539f\u6765\u592a\u5e73\u516c\u4e3b\u9a97\u4ed6\u670d\u4e0b\u7684 \u90a3\u9897\u201c\u89e3\u836f\u201d\uff0c\u662f\u5b54\u96c0\u80c6\u548c\u9e64\u9876\u7ea2\u4e24\u6837\u6700\u5389\u5bb3\u7684\u6bd2\u836f\u5408\u6210\u7684\uff0c\u53c8\u7ecf\u8fc7\u4e00\u573a\u6076\u6597\uff0c\u7cbe\u529b\u6d88 \u8017\u6b86\u5c3d\uff0c\u867d\u7136\u6709\u78a7\u7075\u4e39\uff0c\u4e5f\u4e0d\u8fc7\u4ec5\u80fd\u82df\u5ef6\u6b8b\u5598\u800c\u5df2\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u8d70\u4e86\u8fdb\u6765\uff0c\u4ed6\u8fd8\u672a\u77e5\u9053\u5371\u673a\u8fd9\u6837\u4e25\u91cd\uff0c\u674e\u9038\u521a\u670d\u4e0b\u4e86\u78a7\u7075\u4e39\uff0c\u6c14\u8272\u751a\u597d\uff0c\u957f \u5b59\u6cf0\u8d70\u5230\u5e8a\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u53d7\u4e86\u4f24\uff0c\u6211\u5306\u5306\u5fd9\u5fd9\u7684\u8d76\u56de\u6765\uff0c\u4e0d\u592a\u7d27\u8981\u5427\uff1f\u201d\u674e\u9038\u9053\uff1a \u201c\u8fd8\u597d\u3002\u6628\u665a\u5f20\u67ec\u4e4b\u3001\u6853\u5f66\u8303\u4ed6\u4eec\u5e26\u5175\u5165\u5bab\uff0c\u4f60\u4e5f\u6709\u53bb\u5427\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u662f\u4e34\u65f6\u88ab\u674e\u90fd\u5c09\u62db\u4e86\u53bb\u7684\u3002\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u4e5f\u4e0d\u4f1a\u53bb\u7684\u3002\u201d\u674e \u9038\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4eec\u90fd\u4e0d\u662f\u60f3\u53cd\u5bf9\u5929\u540e\u965b\u4e0b\uff0c\u53ea\u662f\u60f3\u592a\u5b50\u65e9\u65e5\u767b\u57fa\uff0c \u53ef\u4ee5\u6d88\u9664\u6b66\u627f\u55e3\u4f5c\u4e71\u7684\u91ce\u5fc3\u3002\u201d\u674e\u9038\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u4eec\u7684\u7528\u5fc3\uff0c\u6b66\u5219\u5929\u7684\u5e74\u7eaa\u786e\u5b9e\u662f\u592a \u8001\u4e86\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5c31\u662f\u4e3a\u6b64\uff0c\u6211\u4eec\u4e0d\u60f3\u5929\u540e\u965b\u4e0b\u592a\u8fc7\u64cd\u52b3\u56fd\u4e8b\uff0c\u5e0c\u671b\u5979\u5378\u4e0b\u62c5\u5b50\uff0c \u5b89\u4eab\u665a\u5e74\u3002\u8fd9\u756a\u7528\u5fc3\uff0c\u5176\u5b9e\u8fd8\u662f\u4e3a\u4e86\u656c\u7231\u5979\u7684\u3002\u54ea\u77e5\u5979\u770b\u5230\u6211\u4eec\uff0c\u4f24\u5fc3\u5230\u6781\uff0c\u6211\u5f53\u65f6\u5728 \u5f20\u76f8\u56fd\u7684\u8eab\u8fb9\uff0c\u770b\u89c1\u5979\u5c06\u9000\u4f4d\u7684\u8bcf\u4e66\u4ea4\u7ed9\u4e86\u5f20\u76f8\u56fd\uff0c\u53cc\u624b\u98a4\u6296\uff0c\u53ea\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff1a\u2018\u4f60\u4eec \u597d\u81ea\u4e3a\u4e4b\uff0c\u4f46\u613f\u4f60\u4eec\u8f85\u4f50\u592a\u5b50\uff0c\u6cbb\u7406\u56fd\u4e8b\uff0c\u6bd4\u6211\u66f4\u597d\uff01\u2019\u5f20\u76f8\u56fd\u773c\u4e2d\u6ee1\u662f\u6cea\u6c34\uff0c\u5929\u540e\u965b \u4e0b\u4e0d\u7b49\u4ed6\u8bf4\u8bdd\uff0c\u5c31\u6276\u7740\u5a49\u513f\u56de\u53bb\u4e86\uff0c\u542c\u8bf4\u5979\u4e00\u56de\u53bb\u7acb\u523b\u5c31\u75c5\u5012\u4e86\uff01\u201d \u3000\u3000\u674e\u9038\u9053\uff1a\u201c\u6b66\u5219\u5929\u4e00\u751f\u638c\u60ef\u5927\u6743\uff0c\u5979\u662f\u8fd9\u6837\u5014\u5f3a\u7684\u5973\u4eba\uff0c\u5f53\u7136\u4e0d\u7518\u5fc3\u88ab\u522b\u4eba\u903c\u5979\u653e \u5f03\u6743\u529b\u3002\u201d\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u5f53\u65f6\u6211\u4eec\u4e5f\u96be\u8fc7\u5f97\u5f88\uff0c\u4f46\u60f3\u5230\u4e71\u4e8b\u6216\u8005\u53ef\u4ee5\u56e0\u6b64\u9632\u6b62\uff0c\u4e5f\u8fd8\u503c \u5f97\u3002\u201d \u3000\u3000\u674e\u9038\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u7684\u4e71\u4e8b\u662f\u8fc7\u53bb\u4e86\uff0c\u4ee5\u540e\u7684\u4e71\u5b50\u6050\u6015\u4f1a\u95f9\u5f97\u66f4\u51f6\u5462\u3002\u201d \u957f\u5b59\u6cf0\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u6b66\u5219\u5929\u6b7b\u540e\uff0c\u592a\u5e73\u516c\u4e3b\u66f4\u6ca1\u6709\u4eba\u7ba1\u5f97\u4f4f\u5979 \u4e86\u3002\u5979\u6ca1\u6709\u5979\u6bcd\u4eb2\u90a3\u4efd\u624d\u5e72\uff0c\u5374\u6709\u5979\u6bcd\u4eb2\u90a3\u4efd\u91ce\u5fc3\uff0c\u624b\u6bb5\u7684\u6bd2\u8fa3\uff0c\u5219\u8fd8\u5728\u5979\u6bcd\u4eb2\u4e4b\u4e0a\uff0c \u592a\u5b50\u4e0d\u4f1a\u662f\u5979\u7684\u5bf9\u624b\u7684\uff01\u201d\u957f\u5b59\u6cf0\u4e5f\u7ea6\u7565\u77e5\u9053\u592a\u5e73\u516c\u4e3b\u7684\u5389\u5bb3\uff0c\u4e0d\u7981\u5927\u4e3a\u7126\u6025\uff0c\u6413\u624b\u8bf4 \u9053\uff1a\u201c\u8fd9\u600e\u4e48\u597d\uff1f\u8fd9\u600e\u4e48\u597d\uff1f\u201d \u3000\u3000\u662f\u5440\uff0c\u8fd9\u600e\u4e48\u597d\uff1f\u957f\u5b59\u6cf0\u7126\u6025\u7684\u58f0\u97f3\u4e5f\u5f15\u8d77\u4e86\u674e\u9038\u5fc3\u5f26\u7684\u98a4\u52a8\uff0c\u8fd9\u65f6\u4ed6\u624d\u6df1\u523b\u7684\u4f53 \u4f1a\u5230\u6b66\u5219\u5929\u7684\u5fc3\u60c5\uff0c\u660e\u767d\u5979\u4e3a\u4ec0\u4e48\u90a3\u6837\u7740\u6025\u8981\u6c42\u5a49\u513f\u505a\u5979\u7684\u5ab3\u5987\u4e86\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u7528\u60f6\u60d1\u7684\u773c\u5149\u671b\u7740\u4ed6\uff0c\u95ee\u9053\uff1a\u201c\u674e\u5144\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d\u674e\u9038\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u89c1 \u5230\u5a49\u513f\u4e86\u3002\u201d\u957f\u5b59\u6cf0\u5fc3\u5934\u4e00\u9707\uff0c\u4ed6\u672c\u6765\u65e9\u5c31\u60f3\u95ee\u5173\u4e8e\u5a49\u513f\u7684\u4e8b\u4e86\uff0c\u7531\u4e8e\u4e00\u8fde\u4e32\u7684\u610f\u5916\u53d1 \u751f\uff0c\u76f4\u5230\u73b0\u5728\u624d\u8c08\u5230\u5979\u3002 \u3000\u3000\u674e\u9038\u53f9\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u89c1\u5230\u4e86\u6b66\u5219\u5929\u4e86\uff0c\u5979\u4eec\u4e24\u4e2a\u4eba\u5728\u4e00\u8d77\u3002\u201d\u957f\u5b59\u6cf0 \u6025\u5fd9\u95ee\u9053\uff1a\u201c\u5a49\u513f\u600e\u4e48\u6837\uff1f\u201d\u674e\u9038\u9053\uff1a\u201c\u5979\u5f88\u53ef\u601c\uff0c\u55ef\uff0c\u4e5f\u8bb8\u4e0d\u662f\u53ef\u601c\uff0c\u800c\u662f\u4e00\u4ed8\u6c89\u91cd \u7684\u62c5\u5b50\uff0c\u4ee4\u5979\u611f\u5230\u60f6\u6050\u3002\u201d\u957f\u5b59\u6cf0\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u6c89\u91cd\u7684\u62c5\u5b50\uff0c\u55ef\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u674e\u9038\u9053\uff1a\u201c\u4e0d\u4e45\u4f60\u5c31\u4f1a\u660e\u767d\u7684\u3002\u5509\uff0c\u6211\u73b0\u5728\u60f3\u900f\u4e86\uff0c\u4e00\u4e2a\u4eba\u603b\u5f97\u820d\u5f03\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff0c\u8bf4\u5fc3 \u91cc\u8bdd\uff0c\u5bf9\u5a49\u513f\u7684\u4e8b\u60c5\uff0c\u6211\u662f\u4e0d\u6ee1\u610f\u6b66\u5219\u5929\u7684\u3002\u4f46\u4e5f\u8bb8\u5979\u770b\u5f97\u6bd4\u6211\u4eec\u8fdc\u4e9b\uff0c\u5979\u8981\u5a49\u513f\u8ddf\u7740 \u5979\u7684\u8def\u8d70\uff0c\u5bf9\u4e0e\u4e0d\u5bf9\uff0c\u6211\u53ef\u5c31\u4e0d\u6562\u8bf4\u4e86\u3002\u4f46\u6700\u5c11\u6b66\u5219\u5929\u4e5f\u5e76\u4e0d\u662f\u5b8c\u5168\u4e3a\u81ea\u5df1\u7740\u60f3\u7684\u3002\u4e0d \u8bba\u600e\u6837\u5014\u5f3a\u7684\u4eba\uff0c\u6709\u65f6\u4e5f\u96be\u514d\u8981\u8ba9\u81ea\u5df1\u53d7\u5230\u4e00\u4e9b\u59d4\u5c48\uff0c\u820d\u5f03\u4e00\u4e9b\u4e1c\u897f\u3002\u6cf0\u5144\uff0c\u4f60\u660e\u767d\u4e86 \u5427\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u597d\u50cf\u6709\u70b9\u660e\u767d\uff0c\u518d\u60f3\u4e00\u60f3\uff0c\u7981\u4e0d\u4f4f\u98a4\u6817\u8d77\u6765\uff0c\u4ed6\u4e0d\u6562\u518d\u95ee\u4e0b\u53bb\u4e86\u3002\u674e\u9038\u7ecf\u8fc7 \u4e86\u4e00\u756a\u6fc0\u52a8\uff0c\u8138\u8272\u53c8\u82cd\u767d\u8d77\u6765\u3002\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u4f60\u6b47\u4e00\u4f1a\u5427\uff0c\u6211\u7ed9\u4f60\u7aef\u8336\u6765\uff0c\u8fd9\u662f\u7384\u971c\u6c42 \u5fa1\u533b\u5f00\u7684\u65b9\u5b50\u3002\u201d \u3000\u3000\u674e\u9038\u5fc3\u4e8b\u5982\u6f6e\uff0c\u6697\u6697\u53f9\u606f\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u674e\u9038\u6b63\u5728\u60f3\u9053\uff1a\u201c\u6cf0\u5144\u600e \u7684\u8fd9\u6837\u5feb\u53c8\u6765\u4e86\uff1f\u201d\u62ac\u5934\u4e00\u671b\uff0c\u5ffd\u7136\u53d1\u73b0\u8fdb\u6765\u7684\u662f\u4e2a\u5973\u5b50\uff0c\u5979\u662f\u4e0a\u5b98\u5a49\u513f\uff01 \u3000\u3000\u674e\u9038\u5931\u58f0\u53eb\u9053\uff1a\u201c\u662f\u4f60\u6765\u4e86\uff1f\u201d\u5a49\u513f\u5c06\u4e00\u7897\u836f\u8336\u653e\u5728\u5e8a\u5934\u4e0a\uff0c\u5750\u5728\u4ed6\u7684\u5e8a\u524d\uff0c\u4f4e\u58f0 \u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u56de\u6765\u4e86\uff0c\u6211\u600e\u80fd\u4e0d\u6765\u770b\u4f60\u5462\uff0c\u4f60\u7684\u4f24\u597d\u4e86\u70b9\u5417\uff1f\u201d\u5979\u773c\u5149\u4e00\u77a5\uff0c\u5ffd\u7136\u53d1 \u73b0\u674e\u9038\u6795\u8fb9\u6709\u4e00\u65b9\u624b\u5e15\uff0c\u6ee1\u662f\u6cea\u75d5\uff0c\u5979\u8ba4\u5f97\u8fd9\u662f\u6b66\u7384\u971c\u7684\u4e1c\u897f\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u5979\u7684\u5fc3\u5934\u5ffd \u7136\u611f\u5230\u975e\u5e38\u6c89\u91cd\uff01 \u3000\u3000\u674e\u9038\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5f97\u591a\u4e86\u3002\u201d\u4ed6\u4e0d\u613f\u610f\u8bf4\u51fa\u771f\u76f8\uff0c\u514d\u5f97\u5a49\u513f\u4e3a\u4ed6\u4f24\u5fc3\u3002\u4ed6 \u77e5\u9053\u82e5\u662f\u8bf4\u51fa\u4e86\u592a\u5e73\u516c\u4e3b\u4e0b\u6bd2\u7684\u4e8b\u60c5\uff0c\u5a49\u513f\u4e00\u5b9a\u4f1a\u4e0e\u5979\u51b3\u88c2\u7684\uff0c\u5f53\u7136\u4e5f\u5c31\u4e0d\u4f1a\u5ac1\u7ed9\u592a\u5b50 \u4e86\u3002\u592a\u5e73\u516c\u4e3b\u5728\u5bab\u5ef7\u4e2d\u6709\u6781\u5927\u7684\u52bf\u529b\uff0c\u73b0\u5728\u6b66\u5219\u5929\u53c8\u5df2\u75c5\u5012\uff0c\u5a49\u513f\u6ca1\u6709\u652f\u6301\uff0c\u7eb5\u4f7f\u6709\u6b66 \u7384\u971c\u5e2e\u4ed6\uff0c\u4e5f\u662f\u6597\u4e0d\u8fc7\u516c\u4e3b\u7684\u3002\u800c\u4e14\u4ed6\u4e0d\u613f\u4e3a\u4e86\u81ea\u5df1\uff0c\u518d\u5f15\u8d77\u4ec0\u4e48\u53d8\u4e71\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u4e5f\u6ce8\u610f\u5230\u4e86\u5a49\u513f\u7684\u795e\u60c5\uff0c\u5fc3\u5934\u4e00\u52a8\uff0c\u62ff\u8d77\u4e86\u90a3\u65b9\u624b\u5e15\u9053\uff1a\u201c\u7384\u971c\u59d0\u59d0\u4e5f\u6765\u8fc7 \u4e86\u3002\u8fd9\u65b9\u624b\u5e15\u60f3\u5fc5\u662f\u5979\u7559\u4e0b\u7684\uff0c\u5c31\u6258\u4f60\u5e26\u53bb\u4ea4\u8fd8\u5979\u5427\u3002\u201d\u5a49\u513f\u5fc3\u4e71\u5982\u9ebb\uff0c\u51c4\u7136\u7b11\u9053\uff1a \u201c\u4e0d\u5fc5\u4e86\uff0c\u8fd8\u662f\u4f60\u7559\u7740\u5427\uff0c\u6211\u60f3\u5979\u603b\u4f1a\u518d\u6765\u770b\u4f60\u7684\u3002\u201d \u3000\u3000\u8981\u77e5\u6b66\u7384\u971c\u867d\u7136\u662f\u5bf9\u674e\u9038\u4e00\u5f80\u60c5\u6df1\uff0c\u4f46\u56e0\u4e3a\u5979\u548c\u5a49\u513f\u60c5\u540c\u59d0\u59b9\uff0c\u5979\u81ea\u4ece\u548c\u5a49\u513f\u7ed3\u8bc6 \u4e4b\u540e\uff0c\u4fbf\u77e5\u9053\u5a49\u513f\u7231\u7684\u4e5f\u662f\u674e\u9038\uff0c\u56e0\u6b64\u5979\u4ece\u4e0d\u66fe\u5c06\u81ea\u5df1\u7684\u5fc3\u4e8b\u5728\u5a49\u513f\u8ddf\u524d\u8868\u9732\u3002\u4e0d\u8fc7\uff0c \u5a49\u513f\u662f\u4e2a\u7edd\u9876\u806a\u660e\u7684\u4eba\uff0c\u65e5\u5b50\u4e45\u4e86\uff0c\u5979\u4e5f\u9690\u7ea6\u731c\u5230\u4e00\u4e9b\uff0c\u5982\u4eca\u89c1\u4e86\u8fd9\u65b9\u6ee1\u662f\u6cea\u75d5\u7684\u624b\u5e15\uff0c \u5979\u66f4\u662f\u5b8c\u5168\u660e\u767d\u4e86\uff1a\u201c\u539f\u6765\u7384\u971c\u59d0\u59d0\u5bf9\u674e\u9038\u7684\u523b\u9aa8\u76f8\u601d\uff0c\u4e5f\u662f\u548c\u6211\u5b8c\u5168\u4e00\u6837\uff01\u201d\u970e\u65f6\u95f4 \u5fc3\u4e71\u5982\u9ebb\uff0c\u60f3\u8d77\u7384\u971c\u5bf9\u5979\u7684\u60c5\u610f\uff0c\u4e0d\u7981\u6f78\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u674e\u9038\u62c9\u7740\u5979\u7684\u624b\u9053\uff1a\u201c\u4eba\u751f\u5f97\u4e00\u77e5\u5df1\uff0c\u6b7b\u800c\u65e0\u61be\uff0c\u6211\u8fd9\u6b21\u5f97\u548c\u4f60\u89c1\u9762\uff0c\u5df2\u7ecf\u662f\u5fc3\u6ee1 \u610f\u8db3\u4e86\uff0c\u5a49\u513f\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u4f24\u5fc3\u3002\u6708\u6709\u5706\u7f3a\uff0c\u4eba\u6709\u79bb\u5408\uff0c\u4e16\u4e0a\u7684\u4e8b\u60c5\uff0c\u672c\u6765\u4e0d\u662f\u6837\u6837\u90fd\u5c3d \u5982\u4eba\u610f\u7684\u3002\u201d \u3000\u3000\u5a49\u513f\u7d27\u7d27\u63e1\u7740\u4ed6\u7684\u624b\u9053\uff1a\u201c\u53ea\u8981\u4f60\u6ee1\u610f\u6211\u4e5f\u5c31\u6ee1\u610f\u4e86\u3002\u201d\u674e\u9038\u4f55\u7b49\u806a\u660e\uff0c\u5f53\u7136\u542c\u5f97 \u51fa\u5979\u7684\u8bdd\u610f\uff0c\u5a49\u513f\u662f\u4e3a\u4ed6\u548c\u7384\u971c\u800c\u795d\u798f\uff0c\u60f3\u662f\u5979\u8ba4\u4e3a\u81ea\u5df1\u5df2\u7ecf\u51b3\u5b9a\u548c\u7384\u971c\u7ed3\u5408\u4e86\u3002\u674e\u9038 \u5fc3\u4e2d\u4e00\u9635\u9178\u75db\uff0c\u5374\u4e0d\u8fa9\u89e3\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5728\u5341\u5e74\u524d\uff0c\u6211\u542c\u5230\u4f60\u505a\u4e86\u6b66\u5219\u5929\u8bb0\u5ba4\u7684\u6d88\u606f\uff0c \u5f53\u65f6\u66fe\u7ecf\u5f88\u662f\u60b2\u4f24\uff0c\u751a\u81f3\u8fd8\u6068\u8fc7\u4f60\uff01\u73b0\u5728\u6211\u5374\u662f\u4f69\u670d\u4f60\u4e86\u3002\u4f60\u6709\u5fd7\u6c14\uff0c\u6709\u624d\u534e\uff0c\u672c\u6765\u5e94 \u8be5\u505a\u4e00\u756a\u4e8b\u4e1a\uff0c\u6b66\u5219\u5929\u4e5f\u662f\u503c\u5f97\u4f60\u66ff\u5979\u6548\u529b\u7684\u4eba\u3002\u201d\u5a49\u513f\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u7684\u770b\u6cd5\u4e5f\u7ec8\u4e8e\u6539 \u53d8\u4e86\u3002\u55ef\uff0c\u90a3\u4f60\u4eca\u540e\u6253\u7b97\u600e\u6837\uff1f\u8be5\u7559\u4e0b\u6765\u4e86\u5427\uff1f\u201d\u674e\u9038\u5fc3\u4e2d\u4e00\u9635\u5267\u75db\uff1a\u201c\u6211\u5df2\u5c06\u4e0d\u4e45\u4e8e \u4eba\u4e16\u4e86\uff0c\u54ea\u91cc\u8fd8\u8c08\u5f97\u5230\u5c06\u6765\uff1f\u201d\u4f46\u4ed6\u6781\u529b\u538b\u5236\u7740\u5fc3\u5e95\u7684\u60b2\u4f24\uff0c\u4e0d\u8ba9\u5a49\u513f\u770b\u51fa\u4ed6\u75c5\u60c5\u7684\u4e25 \u91cd\uff0c\u63d0\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u73b0\u5728\u592a\u5b50\u5373\u5c06\u590d\u4f4d\uff0c\u6211\u7684\u5fc3\u613f\u5df2\u4e86\u3002\u4eca\u540e\u6211\u5c06 \u4ee5\u95f2\u4e91\u91ce\u9e64\u4e4b\u8eab\uff0c\u5728\u6c5f\u6e56\u4e0a\u5ea6\u8fc7\u4e00\u751f\uff01\u201d\u5a49\u513f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u7384\u971c\u59d0\u59d0\u66fe\u5bf9\u6211\u8bf4\u8fc7\uff0c \u5728\u4e71\u4e8b\u8fc7\u540e\uff0c\u7b49\u5230\u5929\u540e\u965b\u4e0b\u5f52\u5929\uff0c\u5979\u4e5f\u5c06\u4ece\u6b64\u6d41\u6d6a\u6c5f\u6e56\uff0c\u4e0d\u518d\u987e\u95ee\u671d\u5ef7\u4e4b\u4e8b\u4e86\u3002\u55ef\uff0c\u4f60 \u4eec\u4e8c\u4eba\u5fd7\u540c\u9053\u5408\uff0c\u80fd\u7ed3\u4e3a\u7ec8\u8eab\u4f34\u4fa3\uff0c\u5728\u6c5f\u6e56\u4e0a\u884c\u4fa0\u4ed7\u4e49\uff0c\u4f60\u4eec\u7684\u6b22\u4e50\u4e5f\u5c31\u662f\u6211\u7684\u6b22\u4e50\u4e86\u3002\u201d \u5a49\u513f\u6b64\u65f6\u5fc3\u610f\u5df2\u51b3\uff0c\u7384\u971c\u66fe\u7ecf\u4e3a\u4e86\u5979\u800c\u60f3\u727a\u7272\u81ea\u5df1\u7684\u5e78\u798f\uff0c\u5982\u4eca\u5979\u4e5f\u613f\u4e3a\u7384\u971c\u800c\u727a\u7272\u81ea \u5df1\u7684\u5e78\u798f\u4e86\u3002 \u3000\u3000\u5a49\u513f\u7f13\u7f13\u8d77\u7acb\uff0c\u51c4\u7136\u7b11\u9053\uff1a\u201c\u5929\u540e\u965b\u4e0b\u5982\u4eca\u4e5f\u662f\u5367\u75c5\u5728\u5e8a\uff0c\u6211\u8981\u56de\u53bb\u770b\u5979\u4e86\u3002\u54b1\u4eec \u4eca\u540e\u6050\u6015\u672a\u5fc5\u53ef\u4ee5\u518d\u76f8\u89c1\u4e86\uff0c\u4f60\u3001\u4f60\u597d\u81ea\u4fdd\u91cd\u5427\uff01\u201d\u5979\u5c06\u674e\u9038\u90a3\u5f20\u53e4\u7434\u79fb\u5230\u5e8a\u5934\u4e0a\uff0c\u8c03 \u597d\u7434\u5f26\uff0c\u9eef\u7136\u60b2\u6b4c\uff1a\u201c\u53ef\u601c\u7476\u53f0\u6811\uff0c\u707c\u707c\u4f73\u4eba\u59ff\uff0c\u78a7\u534e\u6620\u6731\u5b9e\uff0c\u6500\u6298\u9752\u6625\u65f6\u3002\u5c82\u4e0d\u76db\u5149 \u5ba0\uff0c\u8363\u541b\u767d\u7389\u8f89\u3002\u4f46\u6068\u7ea2\u82b3\u6b47\uff0c\u8c03\u4f24\u611f\u6240\u601d\u3002\u201d\u6b4c\u65e2\u7ec8\uff0c\u6cea\u76c8\u4e8e\u776b\uff0c\u6b47\u4e86\u4e00\u6b47\uff0c\u7434\u58f0\u518d \u8d77\uff0c\u7ee7\u7eed\u6b4c\u9053\uff1a\u201c\u7384\u8749\u53f7\u767d\u9732\uff0c\u5179\u5c81\u5df2\u78cb\u8dce\uff0c\u7fa4\u7269\u4ece\u5927\u5316\uff0c\u5b64\u82f1\u5c06\u5948\u4f55\uff1f\u7476\u53f0\u6709\u9752\u4e4c\uff0c \u8fdc\u98df\u7389\u5c71\u79be\u3002\u6606\u4ed1\u89c1\u7384\u51e4\uff0c\u5c82\u590d\u865e\u4e91\u7f57\u3002\u201d\u94ee\u7136\u58f0\u54cd\uff0c\u7434\u5f26\u65ad\u4e86\u4e24\u6839\uff0c\u5a49\u513f\u63a8\u7434\u800c\u8d77\uff0c \u80cc\u5f71\u5189\u5189\u800c\u6ca1\u3002 \u3000\u3000\u5a49\u513f\u5f39\u7684\u8fd9\u4e24\u9996\u6b4c\u8bd7\uff0c\u7b2c\u4e00\u9996\u662f\u60b2\u53f9\u81ea\u5df1\u547d\u8fd0\u7684\u4e0d\u5e78\uff0c\u672c\u6765\u4ee5\u4e3a\u53ef\u4ee5\u5728\u81ea\u5df1\u9752\u6625\u672a \u6d88\u901d\u7684\u65f6\u5019\uff0c\u627e\u5f97\u5982\u610f\u7684\u914d\u5076\uff0c\u540c\u4eab\u78a7\u534e\u7684\uff08\u78a7\u534e\u6620\u6731\u5b9e\uff0c\u6500\u6298\u9752\u6625\u65f6\u3002\u5c82\u4e0d\u76db\u5149\u5ba0\uff0c \u8363\u541b\u767d\u7389\u5880\uff09\uff0c\u54ea\u77e5\u4e00\u9635\u65e0\u60c5\u7684\u98ce\u96e8\uff0c\u6467\u6b8b\u4e86\u6b63\u5728\u76db\u5f00\u7684\u82b1\u6735\uff0c\u5269\u4e0b\u7684\u4fbf\u53ea\u6709\u65e0\u53ef\u5948\u4f55 \u7684\u60c6\u6005\u4e0e\u60b2\u54c0\uff08\u4f46\u6068\u7ea2\u82b3\u6b47\uff0c\u51cb\u4f24\u611f\u6240\u601d\u3002\uff09\uff01\u7b2c\u4e8c\u9996\u662f\u7fa1\u6155\u674e\u9038\u4e0e\u6b66\u7384\u971c\u7684\u8fdc\u8d70\u9ad8\u98de\uff0c \u56db\u6d77\u900d\u9065\uff0c\u540d\u5c71\u5055\u9690\uff0c\u4ece\u6b64\u4e0d\u7528\u5fe7\u8651\u4eba\u95f4\u7684\u7f57\u7f51\uff0c\u505a\u4e00\u5bf9\u79f0\u5fc3\u5982\u610f\u7684\u592b\u59bb\uff08\u6447\u53f0\u6709\u9752\u9e1f\uff0c \u8fdc\u98df\u7389\u5c71\u79be\u3002\u6606\u4ed1\u89c1\u7384\u51e4\uff0c\u5c82\u590d\u865e\u4e91\u7f57\u3002\uff09 \u3000\u3000\u4f59\u97f3\u8885\u8885\uff0c\u674e\u9038\u5374\u6697\u81ea\u6cea\u54bd\u5fc3\u9178\uff0c\u60f3\u9053\uff1a\u201c\u5a49\u513f\uff0c\u5a49\u513f\uff0c\u4f60\u54ea\u91cc\u77e5\u9053\u6211\u7684\u5fc3\u610f\u554a\uff01\u201d \u8f6c\u53c8\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u5979\u53ef\u4ee5\u653e\u5f00\u6211\u800c\u5ac1\u592a\u5b50\u4e86\u3002\u201d\u674e\u9038\u4e4b\u6240\u4ee5\u8981\u7792\u7740\u75c5\u60c5\uff0c\u5e76\u7531\u5979 \u8bef\u4f1a\uff0c\u4e3a\u7684\u5c31\u662f\u8fd9\u4e2a\u539f\u56e0\u3002\u4ed6\u62ed\u5e72\u773c\u6cea\uff0c\u5fc3\u5934\u6e10\u6e10\u5e73\u9759\u4e0b\u6765\uff0c\u9732\u51fa\u4e86\u4e00\u4e1d\u5fae\u7b11\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u5b88\u5019\u75c5\u623f\u5916\u9762\uff0c\u5fc3\u4e2d\u6b63\u81ea\u5fd0\u5fd1\u4e0d\u5b89\u51fa\uff0c\u5ffd\u89c1\u5a49\u513f\u6ee1\u8138\u90fd\u662f\u6cea\u75d5\uff0c\u957f\u5b59\u6cf0\u5403\u4e86 \u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5a49\u513f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5a49\u513f\u6325\u8896\u8bf4\u9053\uff1a\u201c\u6211\u8981\u8d70\u5566\uff0c\u4f60\u8fdb\u53bb\u770b\u62a4\u4ed6\u5427\uff0c\u55ef\uff0c \u4f60\u4eca\u540e\u4e5f\u4e0d\u5fc5\u5165\u5bab\u63a2\u671b\u6211\u4e86\uff0c\u4f60\u5bf9\u6211\u7684\u597d\u5904\uff0c\u6211\u4f1a\u6c38\u8fdc\u8bb0\u5f97\u7684\uff01\u201d \u3000\u3000\u5a49\u513f\u8d70\u4e86\uff0c\u957f\u5b59\u6cf0\u5341\u51e0\u5e74\u7684\u75f4\u60c5\u7737\u604b\uff0c\u7b49\u5230\u7684\u5c31\u662f\u8fd9\u51e0\u53e5\u8bdd\uff0c\u957f\u5b59\u6cf0\u5fc3\u5934\u7ede\u75db\uff0c\u4e00 \u7247\u832b\u7136\uff0c\u201c\u554a\uff0c\u539f\u6765\u5a49\u513f\u5bf9\u674e\u9038\u662f\u8fd9\u6837\u60c5\u6df1\uff01\u4f46\u5979\u4e3a\u4ec0\u4e48\u5982\u6b64\u4f24\u5fc3\uff1f\u662f\u674e\u9038\u8bf4\u4e86\u4e9b\u4ec0\u4e48 \u8bdd\u4ee4\u5979\u5fc3\u788e\uff1f\u201d \u3000\u3000\u957f\u5b59\u6cf0\u8fdb\u5165\u75c5\u623f\uff0c\u89c1\u674e\u9038\u795e\u8272\u5b89\u9759\uff0c\u4e0d\u4f3c\u95f9\u8fc7\u4ec0\u4e48\u98ce\u6ce2\uff0c\u674e\u9038\u8bf4\u9053\uff1a\u201c\u6cf0\u5144\uff0c\u4f60\u7cbe \u795e\u597d\u50cf\u4e0d\u5927\u597d\uff0c\u8fde\u65e5\u52b3\u7d2f\uff0c\u4f60\u4e5f\u8be5\u65e9\u70b9\u5b89\u6b47\u4e86\u3002\u6211\u521a\u624d\u5403\u4e86\u836f\uff0c\u597d\u4e86\u8bb8\u591a\uff0c\u4f60\u4e0d\u5fc5\u6302\u5fc3\u3002\u201d \u957f\u5b59\u6cf0\u5fc3\u60f3\uff1a\u201c\u4e14\u5f85\u4ed6\u75c5\u597d\u4e4b\u540e\uff0c\u518d\u95ee\u4ed6\u5427\u3002\u201d \u3000\u3000\u54ea\u77e5\u8fc7\u4e86\u4e00\u665a\uff0c\u674e\u9038\u975e\u4f46\u4e0d\u89c1\u597d\u8f6c\uff0c\u53cd\u4f3c\u8d8a\u6765\u8d8a\u6c89\u91cd\u4e86\uff0c\u957f\u5b59\u6cf0\u4e00\u65e9\u8d77\u5e8a\uff0c\u4fbf\u53bb\u63a2 \u671b\u4ed6\uff0c\u53ea\u89c1\u4ed6\u5df2\u5728\u660f\u8ff7\u7684\u72b6\u6001\u4e4b\u4e2d\uff0c\u65f6\u4e0d\u65f6\u53d1\u51fa\u6a21\u7cca\u7684\u8c35\u8bed\uff0c\u597d\u50cf\u662f\u5728\u547c\u5524\u6b66\u7384\u971c\u7684\u540d \u5b57\uff0c\u53c8\u597d\u50cf\u662f\u5728\u547c\u5524\u5a49\u513f\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u5927\u4e3a\u9707\u60ca\uff0c\u60f3\u4e0d\u5230\u4ed6\u7684\u75c5\u60c5\u4f1a\u7a81\u7136\u95f4\u6c89\u91cd\u5982\u65af\uff0c\u4ed6\u4e0d\u80fd\u79bb\u5f00\u674e\u9038\uff0c\u53ea\u597d\u5c06\u4e2a \u5bb6\u4e01\u5524\u6765\uff0c\u5dee\u9063\u4ed6\u53bb\u901a\u77e5\u767d\u5143\u5316\uff0c\u53eb\u767d\u5143\u5316\u8d76\u5feb\u53bb\u627e\u6b66\u7384\u971c\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u6709\u9f13\u4e50\u4e4b\u58f0\uff0c\u4ece\u8857\u5916\u4f20\u6765\uff0c\u90a3\u5bb6\u4e01\u8bf4\u9053\uff1a\u201c\u5bab\u4e2d\u4eca\u5929\u529e\u559c\u4e8b\u3002\u5927 \u6e05\u65e9\u5c31\u6709\u5c0f\u9ec4\u95e8\u6765\u901a\u77e5\u4e86\uff0c\u8bf4\u662f\u8981\u6240\u6709\u7684\u5927\u5185\u4f8d\u536b\uff0c\u5728\u5348\u65f6\u4e4b\u524d\uff0c\u90fd\u5230\u5b98\u4e2d\u62a5\u5230\u3002\u542c\u5019 \u8c03\u9063\uff0c\u8001\u7237\uff0c\u4f60\u81ea\u5df1\u4e0d\u53bb\u4e48\uff1f\u201d\u957f\u5b59\u6cf0\u6014\u4e86\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u5a36\u897f\u5bab\u5a18\u5a18\uff1f\u662f\u54ea\u4e00\u5bb6\u7684\uff0c \u4f60\u53ef\u77e5\u9053\uff1f\u201d\u90a3\u5bb6\u4e01\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u5c31\u662f\u6628\u5929\u6765\u8fc7\u8fd9\u91cc\u7684\u90a3\u4f4d\u4e0a\u5b98\u5c0f\u59d0\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u662f\u6b66\u5219\u5929\u7684\u4e3b\u610f\uff0c\u5979\u8981\u5728\u672a\u6b7b\u4e4b\u524d\uff0c\u770b\u89c1\u5a49\u513f\u6210\u4e3a\u5979\u7684\u5ab3\u5987\u3002\u5a49\u513f\u7684\u6b63\u5f0f\u5c01 \u53f7\u662f\u201c\u662d\u5bb9\u201d\uff0c\u5e76\u975e\u897f\u5bab\uff0c\u4f46\u56e0\u4e3a\u6b66\u5219\u5929\u5bf9\u5979\u7279\u522b\u770b\u91cd\uff0c\u8fce\u4eb2\u65f6\u7684\u4eea\u4ed7\u793c\u8282\uff0c\u90fd\u4e0d\u8fc7\u4ec5 \u6b21\u4e8e\u738b\u540e\u4e00\u7b49\uff0c\u6240\u4ee5\u5c0f\u9ec4\u95e8\u5f80\u5404\u5904\u901a\u62a5\uff0c\u5c31\u628a\u5979\u79f0\u4f5c\u4e86\u201c\u897f\u5bab\u5a18\u5a18\u201d\u3002\u5a49\u513f\u6628\u5929\u6765\u89c1\u674e \u9038\uff0c\u5c1a\u5728\u8e0c\u8e87\uff0c\u5f85\u5230\u89c1\u4e86\u6b66\u7384\u971c\u7684\u624b\u5e15\uff0c\u5fc3\u610f\u59cb\u51b3\uff0c\u56de\u5bab\u4e4b\u540e\uff0c\u4fbf\u63a5\u53d7\u4e86\u6b66\u5219\u5929\u7684\u5c01\u65e8\uff0c \u7b2c\u4e8c\u5929\u5c31\u529e\u559c\u4e8b\uff0c\u4e5d\u57ce\u594f\u4e50\uff0c\u5185\u5916\u540c\u6b22\u3002 \u3000\u3000\u957f\u5b59\u6cf0\u542c\u4e86\u5bb6\u4e01\u7684\u62a5\u544a\uff0c\u60f3\u8d77\u5a49\u513f\u6628\u65e5\u7684\u795e\u60c5\uff0c\u65b9\u59cb\u604d\u7136\u5927\u609f\uff0c\u6697\u81ea\u4f24\u5fc3\uff0c\u4ed6\u5429\u5490 \u5bb6\u4e01\u9053\uff1a\u201c\u6211\u8981\u7167\u6599\u75c5\u4eba\uff0c\u4eca\u5929\u4e0d\u80fd\u5165\u5bab\u4e86\uff0c\u4f60\u4ecd\u7136\u7167\u6211\u7684\u5429\u5490\uff0c\u62ff\u8fd9\u5c01\u4fe1\u53bb\u89c1\u767d\u5927\u4eba\uff0c \u5e76\u8bf7\u4ed6\u4ee3\u6211\u5411\u603b\u7ba1\u5927\u4eba\u544a\u5047\u3002\u201d \u3000\u3000\u9063\u8d70\u4e86\u5bb6\u4e01\uff0c\u957f\u5b59\u6cf0\u518d\u53bb\u770b\u671b\u674e\u9038\uff0c\u674e\u9038\u4e5f\u597d\u4f3c\u4e3a\u5916\u9762\u7684\u4e50\u58f0\u6240\u60ca\u9192\uff0c\u53cc\u7738\u534a\u542f\uff0c \u95ee\u957f\u5b59\u6cf0\u9053\uff1a\u201c\u662f\u8c01\u5bb6\u5a36\u4eb2\uff1f\u9f13\u4e50\u55a7\u5929\uff0c\u60f3\u5fc5\u4e0d\u662f\u5bfb\u5e38\u767e\u59d3\uff1f\u201d\u957f\u5b59\u6cf0\u5fcd\u7740\u773c\u6cea\uff0c\u6447\u4e86 \u6447\u5934\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff01\u201d\u674e\u9038\u56de\u5149\u53cd\u7167\uff0c\u795e\u667a\u5ffd\u7136\u7279\u522b\u6e05\u9192\u8d77\u6765\uff0c\u957f\u5b59\u6cf0\u60b2\u75db \u7684\u795e\u60c5\u843d\u5728\u4ed6\u7684\u773c\u4e2d\uff0c\u4ed6\u51c4\u7136\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff1f\u6211\u53ef\u77e5\u9053\u4e86\uff01\u8fd9\u6837\u7684\u6536\u573a\u4e0d\u5f88\u597d\u5417\uff1f \u5a49\u513f\u7684\u5fc3\u4e2d\u6709\u4f60\u3001\u6709\u6211\uff0c\u5979\u4e5f\u6709\u5979\u81ea\u5df1\u7684\u8def\u8981\u8d70\uff0c\u4f60\u53c8\u4f55\u5fc5\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u674e\u9038\u7684\u58f0\u97f3\u6e10\u6e10\u4f4e\u5f31\u4e0b\u6765\uff0c\u8bf4\u5b8c\u4e86\u8fd9\u4e00\u6bb5\u8bdd\uff0c\u5df2\u662f\u6c14\u82e5\u6e38\u4e1d\uff0c\u957f\u5b59\u6cf0\u5413\u5f97\u624b\u8db3\u65e0\u63aa\uff0c \u6025\u5fd9\u62b1\u7740\u4ed6\u7684\u8eab\u5b50\uff0c\u5728\u4ed6\u7684\u8033\u8fb9\u5524\u9053\uff1a\u201c\u6bbf\u4e0b\uff01\u6211\u5df2\u6d3e\u4eba\u8bf7\u7384\u971c\u6765\u4e86\uff0c\u4f60\u7b49\u7b49\u5979\u5427\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6b66\u7384\u971c\u5c31\u5728\u65c1\u8fb9\u8bf4\u9053\uff1a\u201c\u8ba9\u6211\u6765\u770b\u770b\u4ed6\u3002\u201d\u539f\u6765\u6b66\u7384\u971c\u5df2\u7ecf\u6765\u4e86\uff0c\u957f\u5b59\u6cf0\u5c1a \u672a\u77e5\u9053\u3002 \u3000\u3000\u674e\u9038\u7cbe\u795e\u4e00\u632f\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u6b66\u7384\u971c\u6ee1\u9762\u6cea\u75d5\uff0c\u67f3\u7709\u6df1\u9501\uff0c\u674e\u9038\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u54ed \u4ec0\u4e48\uff0c\u4e16\u4e0a\u54ea\u6709\u767e\u5e74\u4e0d\u6563\u7684\u7b75\u5e2d\uff1f\u5a49\u513f\u6709\u4e86\u5f52\u5bbf\uff0c\u6211\u5df2\u5fc3\u5b89\u2026\u2026\u201d\u6362\u4e86\u53e3\u6c14\uff0c\u518d\u7ee7\u7eed\u8bf4 \u9053\uff1a\u201c\u53ea\u6709\u4f60\u7684\u6069\u60c5\uff0c\u6211\u5c1a\u672a\u80fd\u62a5\u7b54\uff0c\u800c\u4e14\u8fd8\u8981\u5c06\u8eab\u540e\u7684\u4e8b\u60c5\u6765\u9ebb\u70e6\u4f60\u2026\u2026\u201d\u6b66\u7384\u971c\u54bd \u4e0b\u773c\u6cea\uff0c\u7d27\u63e1\u7740\u4ed6\u7684\u624b\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff01\u201d\u674e\u9038\u7684\u8109\u8c61\u5df2\u7ecf\u6563\u4e71\uff0c\u8fd9\u65f6\u6b66\u7384\u971c\u4e5f\u7edd\u671b\u4e86\u3002 \u3000\u3000\u674e\u9038\u65ad\u65ad\u7eed\u7eed\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u628a\u5251\u8bf7\u4f60\u5e26\u7ed9\u6211\u7684\u654f\u513f\uff0c\u4ed6\u957f\u5927\u4e86\uff0c\u4f60\u5e26\u4ed6\u56de\u4e2d\u56fd \u6765\uff01\u201d\u6b66\u7384\u971c\u5782\u6cea\u9053\uff1a\u201c\u6211\u771f\u4e0d\u8be5\u53eb\u4f60\u56de\u6765\uff01\u201d\u674e\u9038\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u6211\u4e00\u70b9\u4e5f\u4e0d\u540e\u6094\uff0c \u6211\u56de\u6765\u540e\uff0c\u770b\u5230\u4e86\u4e00\u4e9b\u4ee4\u4eba\u62c5\u5fe7\u7684\u4e8b\u60c5\uff0c\u4f46\u4e5f\u770b\u5230\u4e86\u66f4\u591a\u4ee4\u4eba\u5174\u594b\u7684\u4e8b\u60c5\uff0c\u6211\u73b0\u5728\u660e\u767d \u4e86\uff0c\u4e2a\u4eba\u5b9e\u5728\u7b97\u4e0d\u5f97\u4ec0\u4e48\uff0c\u54b1\u4eec\u7684\u56fd\u5bb6\u662f\u6709\u5e0c\u671b\u7684\uff01\u201d\u58f0\u97f3\u7a81\u7136\u53c8\u5fae\u5f31\u4e0b\u53bb\uff0c\u6b66\u7384\u971c\u51dd \u795e\u8046\u542c\uff0c\u674e\u9038\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u653e\u5fc3\u7684\u53ea\u6709\u4f60\uff0c\u55ef\uff0c\u4f60\u7684\u5e08\u5144\u3002\u4ed6\u3001\u4ed6\uff0c\u4e3a\u4eba\u5f88\u597d\u2026\u2026\u201d\u8bdd \u672a\u8bf4\u5b8c\uff0c\u4fbf\u54bd\u4e86\u6c14\uff01 \u3000\u3000\u6b66\u7384\u971c\u5fc3\u75db\u5982\u5272\uff0c\u53cd\u800c\u54ed\u4e0d\u51fa\u6765\uff0c\u5979\u62ff\u4e86\u674e\u9038\u90a3\u628a\u5b9d\u5251\uff0c\u5fc3\u4e2d\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u4e0d\u4f1a \u8f9c\u8d1f\u4f60\u7684\uff0c\u4f60\u662f\u751f\u662f\u6b7b\uff0c\u6211\u90fd\u5bf9\u4f60\u4e00\u6837\uff01\u201d\u5979\u8d70\u51fa\u5927\u95e8\uff0c\u540e\u9762\u65b9\u59cb\u4f20\u6765\u957f\u5b59\u6cf0\u7684\u54ed\u58f0\u3002 \u3000\u3000\u7269\u6362\u661f\u79fb\u4eba\u4e8b\u6539\uff0c\u674e\u9038\u6b7b\u540e\uff0c\u5306\u5306\u53c8\u662f\u4e00\u5e74\uff0c\u5728\u8fd9\u4e00\u5e74\u5f53\u4e2d\uff0c\u6b66\u5219\u5929\u4f20\u4f4d\u7ed9\u4e86\u513f\u5b50 \u4e4b\u540e\uff0c\u4e0d\u4e45\u5c31\u75c5\u6b7b\u4e86\uff0c\u4e0a\u5b98\u5a49\u513f\u505a\u4e86\u7687\u5e1d\u7684\u201c\u662d\u5bb9\u201d\uff0c\u592a\u5e73\u516c\u4e3b\u7684\u52bf\u529b\u8d8a\u6765\u8d8a\u5927\uff0c\u957f\u5b59 \u6cf0\u5347\u4e86\u4e00\u7ea7\uff0c\u505a\u5230\u7981\u536b\u519b\u7684\u526f\u90fd\u5c09\uff0c\u53ea\u6709\u6b66\u7384\u971c\u65e9\u5df2\u79bb\u5f00\u957f\u5b89\uff0c\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u5929\u5c71\u7684\u5357\u9ad8\u5cf0\u4e0a\uff0c\u674e\u9038\u7684\u513f\u5b50\u5728\u7b49\u770b\u4ed6\u7684\u7236\u4eb2\u56de\u6765\uff0c\u4ed6\u5df2\u7ecf\u662f\u5341\u51e0\u5c81\u5927\u7684\u5b69\u5b50\u4e86\uff0c \u6bd4\u8d77\u4ee5\u524d\u66f4\u61c2\u4e8b\u5f97\u591a\uff0c\u8fd9\u4e00\u5929\u4ed6\u8ddf\u88f4\u53d4\u5ea6\u5728\u5c71\u524d\u7ec3\u5251\uff0c\u5c45\u7136\u5c06\u4e00\u5957\u5f88\u590d\u6742\u7684\u5251\u6cd5\u4f7f\u5f97\u4e2d \u89c4\u4e2d\u77e9\uff0c\u88f4\u53d4\u5ea6\u6ee1\u6000\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u4f60\u7239\u7239\u89c1\u4e86\uff0c\u4e0d\u77e5\u9053\u8be5\u591a\u9ad8\u5174\u5462\uff01\u201d \u3000\u3000\u674e\u5e0c\u654f\u628a\u5251\u4e00\u6536\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u6211\u7684\u7239\u7239\u600e\u4e48\u8fd8\u672a\u56de\u6765\uff1f\u4ed6\u8bf4\u8fc7\u6700\u591a\u4e00\u5e74\u4fbf \u56de\u6765\u7684\uff0c\u73b0\u5728\u5df2\u7ecf\u8fc7\u4e86\u4e00\u5e74\u53c8\u4e09\u4e2a\u6708\u4e86\u3002\u201d\u6590\u53d4\u5ea6\u9053\uff1a\u201c\u4ece\u957f\u5b89\u5230\u8fd9\u91cc\u6709\u51e0\u4e07\u91cc\u8def\uff0c\u7a0d \u6709\u963b\u8bef\uff0c\u4fbf\u4e0d\u80fd\u4f9d\u671f\u56de\u6765\u4e86\u3002\u800c\u4e14\u4e5f\u8bb8\u4ed6\u8fd8\u6709\u65c1\u7684\u4e8b\u60c5\u5462\uff1f\u201d\u674e\u5e0c\u654f\u8fc7\uff1a\u201c\u4e0d\uff0c\u6211\u7239\u7239 \u4ece\u6765\u4e0d\u4f1a\u9a97\u6211\u7684\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u88f4\u53d4\u5ea6\u5ffd\u5730\u5931\u58f0\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u90a3\u8fb9\u6709\u4eba\u6765\u4e86\uff01\u201d\u4ed6\u5b9a \u775b\u4e00\u77a7\uff0c\u9ed8\u7136\u4f3c\u89e6\u7535\u4e00\u822c\uff0c\u6d51\u8eab\u53d1\u6296\u3002 \u3000\u3000\u674e\u5e0c\u654f\u7bad\u4e00\u822c\u7684\u5c04\u51fa\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u7239\u7239\uff01\u5440\uff0c\u59d1\u59d1\uff01\u201d\u6b66\u7384\u971c\u767d\u8863\u5982\u96ea\uff0c\u8170 \u95f4\u60ac\u7740\u674e\u9038\u90a3\u628a\u5b9d\u5251\uff0c\u773c\u89d2\u6709\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u674e\u5e0c\u654f\u6251\u8fdb\u5979\u7684\u6000\u4e2d\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u679c\u7136\u6ca1\u6709\u5fd8\u8bb0\u6211\uff01\u4f60\u5728\u957f\u5b89\u53ef\u89c1\u5230\u6211\u7684\u7239 \u7239\u4e48\uff1f\u54a6\uff0c\u8fd9\u628a\u5251\u662f\u6211\u7239\u7239\u7684\uff01\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u7239\u7239\u4e48\uff1f\u4ed6\uff0c\u4ed6\u4e0d\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u674e\u5e0c\u654f\u7741\u5927\u4e86\u773c\u775b\uff0c\u5728\u4ed6\u7a1a\u5f31\u7684\u5fc3\u7075\u4e2d\uff0c\u9690\u9690\u611f\u5230\u4e86\u4e0d\u5e78\u3002\u6b66\u7384\u971c\u9053\uff1a\u201c\u4f60\u7239\u7239\u8981 \u4f60\u542c\u6211\u7684\u8bdd\uff0c\u6211\u5e26\u4f60\u56de\u4e2d\u56fd\u53bb\uff0c\u4f60\u613f\u610f\u8ddf\u6211\u4e48\u3002\u201d\u674e\u5e0c\u654f\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u54fd\u54bd \u8bf4\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u6211\u542c\u4f60\u7684\u8bdd\uff01\u201d \u3000\u3000\u88f4\u53d4\u5ea6\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u771f\u662f\u6599\u60f3\u4e0d\u5230\uff0c\u6599\u60f3\u4e0d\u5230\uff0c\u5e08\u59b9\uff0c\u4f60\u4e0d\u7559\u4e0b\u6765\u4e48\uff1f\u6211\uff0c\u6211\u4e5f \u53ef\u4ee5\u5e2e\u52a9\u4f60\u7167\u6599\u5b69\u5b50\uff01\u201d\u4ed6\u5728\u4f24\u5fc3\u4e4b\u4e2d\u7a81\u7136\u9f13\u8d77\u4e86\u52c7\u6c14\uff0c\u8bf4\u51fa\u4e86\u4e45\u5df2\u60f3\u8bf4\u7684\u8bdd\uff0c\u5fc3\u60c5\u4f3c \u7ef7\u7d27\u7684\u5f13\u5f26\uff0c\u7b49\u5f85\u5e08\u59b9\u7684\u56de\u7b54\u3002\u53ea\u542c\u5f97\u6b66\u7384\u971c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u591a\u8c22\u4f60\u7684\u597d\u610f\uff0c\u6211\u7684 \u5fc3\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4eca\u540e\u6211\u53ea\u6709\u548c\u8fd9\u5b69\u5b50\u76f8\u4f9d\u4e3a\u547d\u4e86\u3002\u6211\u7b54\u5e94\u8fc7\u4ed6\u7684\u7236\u4eb2\u5e26\u56de\u53bb\u7684\uff0c\u4e0d\u60f3\u518d\u9ebb \u70e6\u4f60\u4e86\u3002\u590f\u4faf\u524d\u8f88\u5462\uff1f\u201d\u88f4\u53d4\u5ea6\u9053\uff1b\u201c\u590f\u4faf\u524d\u8f88\u5f80\u5317\u5929\u5c71\u627e\u7b26\u4e0d\u7591\u53bb\u4e86\uff0c\u4ed6\u5df2\u4f20\u6388\u4e86\u8fd9 \u5b69\u5b50\u7684\u5185\u529f\u5fc3\u6cd5\u3002\u201d\u6b66\u7384\u971c\u9053\uff1a\u201c\u90a3\u4e48\u6211\u53ea\u597d\u7b49\u5f85\u5c06\u6765\u89c1\u9762\u7684\u65f6\u5019\u518d\u5411\u4ed6\u9053\u8c22\u4e86\u3002\u5e08\u5144\uff0c \u672c\u95e8\u7684\u5251\u6cd5\u5f85\u4f60\u53d1\u626c\u5149\u5927\uff0c\u4f60\uff0c\u4f60\u5584\u81ea\u73cd\u91cd\uff01\u201d\u88f4\u53d4\u5ea6\u5931\u671b\u4f24\u5fc3\uff0c\u5fc3\u5934\u51b0\u51b7\uff0c\u6cea\u5f71\u6a21\u7cca \u4e2d\uff0c\u9065\u671b\u6b66\u7384\u971c\u643a\u7740\u5b69\u5b50\uff0c\u5df2\u53bb\u5f97\u8fdc\u4e86\uff0c\u8fdc\u4e86\uff01\u6b63\u662f\uff1a \u3000\u3000\u4eba\u95f4\u65e0\u9650\u4f24\u5fc3\u4e8b\u3002\u6b7b\u522b\u751f\u79bb\u4e24\u4e0d\u582a\uff01"}),showOnHomepage:!0}]}}]);