"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[55025],{13837:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u82f1\u96c4\u65e0\u6cea",content:(0,t.jsx)(t.Fragment,{children:"\u4e00\u5ea7\u9ad8\u5c71\uff0c\u4e00\u5904\u4f4e\u5ca9\uff0c\u4e00\u9053\u65b0\u6cc9\uff0c\u4e00\u682a\u53e4\u677e\uff0c\u4e00\u7089\u7ea2\u706b\uff0c\u4e00\u58f6\u7eff\u8336\uff0c\u4e00\u4f4d\u8001\u4eba\uff0c\u4e00\u4e2a\u5c11 \u5e74\u3002 \u3000\u3000\u201c\u5929\u4e0b\u6700\u53ef\u6015\u7684\u6b66\u5668\u662f\u4ec0\u4e48\uff1f\u201d\u5c11\u5e74\u95ee\u8001\u4eba\uff1a\u201c\u662f\u4e0d\u662f\u4f8b\u4e0d\u865a\u53d1\u7684\u5c0f\u674e\u98de\u5200\uff1f\u201d \u3000\u3000\u201c\u4ee5\u524d\u4e5f\u8bb8\u662f\uff0c\u73b0\u5728\u5374\u4e0d\u662f\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u81ea\u4ece\u5c0f\u674e\u63a2\u82b1\u4ed9\u53bb\u540e\uff0c\u8fd9\u79cd\u6b66\u5668\u5df2\u6210\u7eb6\u664c\u3002\u201d\u8001\u4eba\u9eef\u7136\u53f9\u606f\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u4e16\u4e0a \u518d\u4e5f\u4e0d\u4f1a\u6709\u5c0f\u674e\u63a2\u82b1\u8fd9\u79cd\u4eba\uff1b\u4e5f\u4e0d\u4f1a\u518d\u6709\u5c0f\u674e\u98de\u5200\u8fd9\u79cd\u6b66\u5668\u4e86\u3002\u201d \u3000\u3000\u5c11\u5e74\u4ef0\u671b\u9ad8\u5c71\uff0c\u5c71\u5dc5\u767d\u4e91\u60a0\u60a0\u3002 \u3000\u3000\u201c\u73b0\u5728\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u6b66\u5668\u662f\u4ec0\u4e48\uff1f\u201d\u5c11\u5e74\u53c8\u95ee\u8001\u4eba\uff1a\u201c\u662f\u4e0d\u662f\u84dd\u5927\u5148\u751f\u7684\u84dd\u5c71\u53e4\u5251\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u5357\u6d77\u795e\u529b\u738b\u7684\u5927\u94c1\u690e\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u5173\u4e1c\u843d\u65e5\u9a6c\u573a\u51af\u5927\u603b\u7ba1\u7684\u767d\u94f6\u67aa\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u4e09\u5e74\u524d\u5728\u90af\u90f8\u53e4\u9053\u4e0a\uff0c\u8f7b\u9a91\u8bdb\u516b\u5bc7\u7684\u98de\u661f\u5f15\u6708\u5200\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u8d77\u6765\u4e86\u3002\u201d\u5c11\u5e74\u8bf4\u5f97\u6781\u6709\u628a\u63e1\uff1a\u201c\u662f\u6768\u94ee\u7684\u79bb\u522b\u94a9\uff1a\u4e00\u5b9a\u662f\u6768\u94ee\u7684\u79bb\u522b\u94a9\u3002\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u662f\uff0c\u201d\u8001\u4eba\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u4e9b\u6b66\u5668\u867d\u7136\u90fd\u5f88\u53ef\u6015\uff0c\u5374\u4e0d\u662f\u6700\u53ef\u6015\u7684\u4e00\u79cd\u3002\u201d \u3000\u3000\u201c\u6700\u53ef\u6015\u7684\u4e00\u79cd\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u53e3\u7bb1\u5b50\u3002\u201d \u3000\u3000\u201c\u4e00\u53e3\u7bb1\u5b50\uff1f\u201d\u5c11\u5e74\u60ca\u5947\u6781\u4e86\uff1a\u201c\u5f53\u4eca\u5929\u4e0b\u6700\u53ef\u6015\u7684\u6b66\u5668\u662f\u4e00\u53e3\u7bb1\u5b50\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u7b2c\u4e00\u7ae0\u3000\u4e00\u53e3\u7bb1\u5b50>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u4e00\u7ae0\u3000\u4e00\u53e3\u7bb1\u5b50 \u3000\u3000\u4e00\u4e2a\u4eba\uff0c\u4e00\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u4e00\u4e2a\u6c89\u9ed8\u5e73\u51e1\u7684\u4eba\uff0c\u63d0\u7740\u4e00\u53e3\u9648\u65e7\u5e73\u51e1\u7684\u7bb1\u5b50\uff0c\u5728\u6ee1\u5929\u5915\u9633\u4e0b\uff0c\u9ed8\u7136\u7684\u8d70\u5165\u4e86\u957f\u5b89\u53e4 \u57ce\u3002 \u4e00 \u3000\u3000\u6b63\u6708\u5341\u4e94\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5173\u4e0a\u4e86\u4e86\u95e8\uff0c\u628a\u8fd9\u957f\u5b89\u53e4\u57ce\u4e2d\u5343\u5e74\u4e0d\u53d8\u7684\u98ce\u96ea\u5173\u5728\u95e8\u5916\uff0e\u8131\u4e0b\u4ed6\u90a3\u4ef6\u4ee5\u7d2b\u7ed2\u4e3a\u9762 \u4f5c\u6210\u7684\u7d2b\u8c82\u6597\u7bf7\uff0c\u6302\u5728\u4ed6\u5de6\u624b\u4e00\u4e2a\u7528\u7d2b\u6a80\u6728\u679d\u505a\u6210\u7684\u8863\u67b6\u4e0a\uff0c\u8f6c\u8fc7\u8eab\u65f6\uff0c\u53f3\u624b\u5df2\u62ff\u8d77\u4e00\u4e2a\u7d2b \u94dc\u706b\u94b3\uff0c\u628a\u524d\u9762\u4e00\u4e2a\u7d2b\u94dc\u706b\u76c6\u91cc\u7ec8\u65e5\u4e0d\u706d\u7684\u7089\u706b\u62e8\u5f97\u66f4\u65fa\u4e9b\u3002 \u3000\u3000\u706b\u76c6\u65c1\u5c31\u662f\u4e00\u4e2a\u4e0a\u9762\u94fa\u7740\u7d2b\u8c82\u76ae\u6bdb\u7684\u7d2b\u6a80\u6728\u6905\uff0c\u6728\u6905\u65c1\u7d2b\u6a80\u6728\u684c\u4e0a\u7684\u7d2b\u6c34\u6676\u74f6\u4e2d\uff0c\u7ecf\u5e38 \u90fd\u6ee1\u76db\u7740\u7d2b\u8272\u7684\u6ce2\u65af\u8461\u8404\u9152\u3002 \u3000\u3000\u4ed6\u53ea\u8981\u8d70\u4e24\u6b65\u5c31\u53ef\u4ee5\u5750\u4e0b\u6765\uff0c\u968f\u624b\u5c31\u53ef\u4ee5\u5012\u51fa\u4e00\u676f\u9152\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u7d2b\u8272\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u540d\u9a6c\u4f73\u4eba\u534e\u8863\u7f8e\u9152\uff0c\u559c\u6b22\u4eab\u53d7\u3002 \u3000\u3000\u5bf9\u6bcf\u4e00\u4ef6\u4e8b\u4ed6\u90fd\u975e\u5e38\u8bb2\u7a76\u6311\u5254\uff0c\u505a\u7684\u6bcf\u4e00\u4ef6\u4e8b\u90fd\u7ecf\u8fc7\u7cbe\u5bc6\u8ba1\u5212\uff0c\u7edd\u4e0d\u80af\u591a\u6d6a\u8d39\u4e00\u5206\u529b \u6c14\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4e00\u70b9\u758f\u5ffd\uff0c\u5c31\u8fde\u8fd9\u4e9b\u751f\u6d3b\u4e0a\u7684\u7ec6\u8282\u90fd\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u5353\u4e1c\u6765\u3002 \u3000\u3000\u4ed6\u80fd\u591f\u6d3b\u5230\u73b0\u5728\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4ed6\u662f\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5750\u4e0b\u6765\uff0c\u6d45\u6d45\u7684\u555c\u4e86\u4e00\u53e3\u9152\u3002 \u3000\u3000\u7cbe\u81f4\u534e\u7f8e\u800c\u6e29\u6696\u7684\u5c4b\u5b50\u3001\u7518\u9999\u751c\u7f8e\u7684\u9152\uff0c\u5df2\u7ecf\u628a\u4ed6\u8eab\u4f53\u7684\u5bd2\u6c14\u5b8c\u5168\u9a71\u9664\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u5f88\u75b2\u5026\u3002 \u3000\u3000\u4e3a\u4e86\u7b79\u5907\u4eca\u591c\u7684\u5927\u5178\uff0c\u8fd9\u4e24\u592d\u4ed6\u5df2\u7ecf\u628a\u81ea\u5df1\u751f\u6d3b\u7684\u89c4\u5f8b\u5b8c\u5168\u641e\u4e71\u4e86\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u80fd\u8ba9\u8fd9\u4ef6\u4e8b\u53d1\u751f\u4efb\u4f55\u4e00\u70b9\u9519\u8bef\uff0c\u4efb\u4f55\u4e00\u70b9\u5fae\u5c0f\u7684\u9519\u8bef\uff0c\u90fd\u53ef\u80fd\u4f1a\u9020\u6210\u6c38\u8fdc\u65e0\u6cd5\u5f25 \u8865\u7684\u5927\u9519\uff0c\u90a3\u65f6\u4e0d\u4f46\u4ed6\u81ea\u5df1\u5fc5\u5c06\u6094\u6068\u7ec8\u751f\uff0c\u4ed6\u7684\u4e3b\u4eba\u4e5f\u8981\u53d7\u5230\u8fde\u7d2f\uff0c\u751a\u81f3\u8fde\u6c5f\u6e56\u4e2d\u7684\u5927\u5c40\u90fd \u4f1a\u56e0\u6b64\u800c\u6539\u53d8\u3002 \u3000\u3000\u66f4\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u7edd\u4e0d\u80fd\u8ba9\u53f8\u9a6c\u8d85\u7fa4\u5982\u65e5\u4e2d\u5929\u7684\u4e8b\u4e1a\u548c\u58f0\u540d\uff0c\u53d7\u5230\u4e00\u70b9\u6253\u51fb\u548c\u635f\u5bb3\u3002 \u3000\u3000\u4e00\u4e2a\u5df2\u6e10\u6e10\u6210\u4e3a\u6c5f\u6e56\u8c6a\u6770\u5fc3\u76ee\u4e2d\u5076\u50cf\u7684\u4eba\uff0c\u65e0\u8bba\u505a\u4efb\u4f55\u4e8b\u90fd\u53ea\u8bb8\u6210\u529f\uff0c\u4e0d\u8bb8\u5931\u8d25\u3002 \u3000\u3000\u5353\u4e1c\u6765\u8fd9\u4e00\u751f\u4e2d\u6700\u4e0d\u80fd\u5fcd\u53d7\u7684\u4e24\u4ef6\u4e8b\uff0c\u5c31\u662f\u201c\u9519\u8bef\u201d\u548c\u201c\u5931\u8d25\u201d\u3002 \u3000\u3000\u53f8\u9a6c\u8d77\u7fa4\u7684\u786e\u5df2\u7ecf\u4e0d\u80fd\u8d25\u4e86\u3002 \u3000\u3000\u4ed6\u4ece\u5341\u516b\u5c81\u5d1b\u8d77\u6c5f\u6e56\uff0c\u8eab\u7ecf\u5927\u5c0f\u4e09\u5341\u4e09\u6218\uff0c\u81f3\u4eca\u4ece\u672a\u8d25\u8fc7\u4e00\u6b21\u3002 \u3000\u3000\u4ed6\u9ad8\u5927\u5f3a\u58ee\u82f1\u4fca\uff0c\u5a01\u6b66\u8c6a\u723d\uff0c\u4e00\u5f20\u8f6e\u5ed3\u5206\u660e\u7684\u8138\u4e0a\uff0c\u603b\u662f\u5e26\u7740\u723d\u6717\u7684\u7b11\u5bb9\uff0c\u5c31\u8fde\u4ed6\u7684\u4ec7 \u654c\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\u4ed6\u662f\u6761\u5c11\u89c1\u7684\u7537\u4e8e\u6c49\uff0c\u7edd\u4e0d\u4f1a\u7f3a\u5c11\u7f8e\u5973\u966a\u4f34\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5bf9\u4ed6\u7684\u59bb\u5b50\u513f\u5973\u548c\u5bf9\u4ed6\u7684\u670b\u53cb\uff0c\u90fd\u540c\u6837\u5fe0\u5b9e\uff0c\u4ece\u672a\u6ca1\u6709\u4e00\u70b9\u4e11\u95fb\u7275\u8fde\u5230\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u4e9b\u8fd8\u4e0d\u662f\u4ed6\u6700\u503c\u5f97\u9a84\u505a\u4e4b\u5904\u3002 \u3000\u3000\u5728\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u6700\u503c\u5f97\u9a84\u505a\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u4ed6\u5728\u4e24\u5e74\u4e4b\u5185\uff0c\u4ee5\u4ed6\u7684\u6b66\u529f\u667a\u6167\u548c\u505a\u4eba\u505a\u4e8b\u7684 \u660e\u5feb\u4f5c\u98ce\uff0c\u8bf4\u670d\u4e86\u81ea\u6cb3\u6714\u4e2d\u539f\u5230\u5173\u4e1c\u8fd9\u6761\u7ebf\u4e0a\u6700\u91cd\u8981\u7684\u4e09\u5341\u4e5d\u8def\u7eff\u6797\u8c6a\u6770\uff0c\u4ece\u9ed1\u9053\u8d70\u4e0a\u767d \u9053\uff0c\u7ec4\u7ec7\u6210\u4e00\u4e2a\u6c5f\u6e56\u4e2d\u7a7a\u524d\u672a\u6709\u7684\u8d85\u7ea7\u5927\u9556\u5c40\uff0c\u6536\u5408\u7406\u7684\u8d39\u7528\uff0c\u4fdd\u62a4\u8fd9\u6761\u8def\u7ebf\u4e0a\u6240\u6709\u884c\u5546\u5ba2 \u65c5\u7684\u5b89\u5168\u3002 \u3000\u3000\u5728\u4ed6\u4eec\u90a3\u6746\u4ee5\u7d2b\u7f0e\u9576\u8fb9\u7684\u201c\u5927\u201d\u5b57\u7f25\u65d7\u4fdd\u62a4\u4e0b\uff0c\u4ece\u672a\u6709\u4efb\u4f55\u4e00\u8d9f\u9556\u51fa\u8fc7\u4e00\u70b9\u5dee\u9519\u3002 \u3000\u3000\u8fd9\u662f\u6c5f\u6e56\u4e2d\u7a7a\u524d\u672a\u6709\u7684\u4e00\u6b21\u8f89\u714c\u6210\u5c31\uff0c\u8fd9\u79cd\u6210\u5c31\u7edd\u4e0d\u662f\u53ea\u51ed\u201c\u94c1\u201d\u4e0e\u201c\u8840\u201d\u5c31\u53ef\u4ee5\u505a\u5f97 \u5230\u7684\u3002 \u3000\u3000\u73b0\u5728\u53f8\u9a6c\u8d85\u7fa4\u624d\u4e09\u5341\u516d\u5c81\uff0c\u5c31\u5df2\u7ecf\u6e10\u6e10\u6210\u4e3a\u6c5f\u6e56\u8c6a\u6770\u5fc3\u76ee\u4e2d\u7684\u5076\u50cf\u2014\u2014\u6c38\u8fdc\u4e0d\u8d25\u7684\u82f1\u96c4 \u5076\u50cf\u3002 \u3000\u3000\u53ea\u6709\u4ed6\u81ea\u5df1\u548c\u5353\u4e1c\u6765\u5fc3\u91cc\u77e5\u9053\u8fd9\u79cd\u5730\u4f4d\u662f\u600e\u4e48\u9020\u6210\u7684\u3002 \u4e8c \u3000\u3000\u559d\u5b8c\u4e86\u7b2c\u4e00\u676f\u9152\u65f6\uff0c\u5353\u4e1c\u6765\u5df2\u7ecf\u628a\u7b56\u5212\u4eca\u591c\u8fd9\u6b21\u5927\u5178\u7684\u524d\u540e\u7ecf\u8fc7\u4ece\u5934\u53c8\u60f3\u4e86\u4e00\u904d\u3002 \u3000\u3000\u4ed6\u7684\u9152\u4e00\u5411\u559d\u5f97\u5f88\u6162\uff0c\u601d\u60f3\u5374\u6781\u5feb\u3002 \u3000\u3000\u4eca\u5929\u662f\u53f8\u9a6c\u8d85\u7fa4\u7b2c\u4e00\u6b21\u5f00\u5c71\u95e8\u6536\u5f92\u5f1f\uff0c\u65e0\u8bba\u4ece\u54ea\u65b9\u9762\u6765\u8bf4\uff0c\u90fd\u53ef\u4ee5\u7b97\u662f\u4ef6\u8f70\u52a8\u6c5f\u6c8f\u7684\u5927 \u4e8b\u3002 \u3000\u3000\u6700\u4f7f\u4eba\u9707\u60ca\u7684\u4e00\u70b9\u662f\uff0c\u53f8\u9a6c\u8d85\u7fa4\u6536\u7684\u8fd9\u4f4d\u5f1f\u5b50\uff0c\u8d6b\u7136\u7adf\u662f\u4e00\u4e2a\u6708\u524d\u624d\u53db\u51fa\u201c\u4e2d\u5dde\u96c4\u72ee \u5802\u201d\u7684\u6768\u575a\u3002 \u3000\u3000\u96c4\u72ee\u5802\u662f\u5317\u9762\u9053\u4e0a\u56db\u5341\u8def\u7eff\u6797\u597d\u6c49\u4e2d\uff0c\u552f\u4e00\u6ca1\u6709\u53c2\u52a0\u53f8\u9a6c\u8d85\u7fa4\u76df\u7ea6\u7684\u4e00\u4e2a\u7ec4\u7ec7\uff0c\u4e5f\u662f\u5176 \u4e2d\u89c4\u6a21\u6700\u5e9e\u5927\u3001\u6700\u6709\u52bf\u529b\u7684\u4e00\u4e2a\u7ec4\u7ec7\u3002 \u3000\u3000\u6768\u575a\u672c\u6765\u662f\u96c4\u72ee\u5802\u6731\u5802\u4e3b\u9ebe\u4e0b\u7684\u56db\u5927\u7231\u5c06\u4e4b\u4e00\u3002 \u3000\u3000\u6c5f\u7322\u4e2d\u4eba\u4ece\u6765\u4e5f\u6ca1\u60f3\u5230\u6768\u575a\u4e5f\u4f1a\u53db\u51fa\u96c4\u72ee\u5802\uff0c\u53ef\u662f\u6bcf\u4e2a\u4eba\u90fd\u77e5\u9053\u3002\u6768\u575a\u51fa\u8d70\u540e\u7684\u7b2c\u4e8c \u5929\uff0c\u201c\u96c4\u72ee\u201d\u6731\u731b\u5c31\u5df2\u904d\u6d12\u6b66\u6797\u5e16\uff0c\u8868\u660e\u4ed6\u7684\u6001\u5ea6\u3002 \u3000\u3000\u2014\u2014\u65e0\u8bba\u662f\u54ea\u4e00\u95e8\u54ea\u4e00\u5e2e\u54ea\u4e00\u6d3e\uff0c\u53ea\u8981\u6709\u4eba\u6536\u5bb9\u6768\u575a\uff0c\u5c31\u662f\u96c4\u72ee\u5802\u7684\u6b7b\u654c\uff0c\u5fc5\u5c06\u53d7\u5230\u96c4 \u72ee\u5802\u4e0d\u62e9\u4e00\u5207\u624b\u6bb5\u7684\u6b8b\u9177\u62a5\u590d\u3002 \u3000\u3000\u73b0\u5728\u53f8\u9a6c\u8d85\u7fa4\u4e0d\u4f46\u6536\u5bb9\u4e86\u6768\u575a\uff0c\u800c\u4e14\u5927\u5f00\u9999\u5802\uff0c\u6536\u4ed6\u4e3a\u5f00\u5c71\u95e8\u7684\u5f92\u5f1f\u3002 \u3000\u3000\u96c4\u72ee\u5802\u867d\u7136\u6ca1\u6709\u6295\u653e\u53f8\u9a6c\u7684\u201c\u5927\u9556\u5c40\u201d\uff0c\u53ef\u662f\u4e5f\u6ca1\u6709\u6b63\u9762\u548c\u4ed6\u4eec\u4f5c\u5bf9\u8fc7\uff0c\u66f4\u6ca1\u6709\u52a8\u8fc7\u4ed6 \u4eec\u7684\u9556\u65d7\u3002 \u3000\u3000\u201c\u96c4\u72ee\u201d\u6731\u731b\u9634\u9e37\u6c89\u731b\uff0c\u51b7\u9177\u65e0\u60c5\uff0c\u662f\u4e2a\u6781\u4e0d\u597d\u60f9\u7684\u4eba\uff0c\u800c\u4e14\u8a00\u51fa\u5fc5\u884c\uff0c\u5982\u679c\u4ed6\u8bf4\u4ed6\u8981 \u4e0d\u62e9\u624b\u6bb5\u53bb\u5bf9\u4ed8\u4e00\u4e2a\u4eba\uff0c\u90a3\u4e48\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u624b\u6bb5\u4ed6\u90fd\u4f1a\u7528\u5f97\u51fa\u6765\u3002 \u3000\u3000\u4e3a\u4e86\u8fbe\u5230\u76ee\u7684\uff0c\u5c31\u7b97\u8981\u4ed6\u62ff\u96c4\u72ee\u5802\u5c5e\u4e0b\u5b50\u5f1f\u7684\u4e09\u5343\u516b\u767e\u9897\u5934\u9885\u53bb\u6362\uff0c\u4ed6\u4e5f\u5728\u6240\u4e0d\u60dc\u3002 \u3000\u3000\u4ed6\u5e73\u751f\u6700\u949f\u7231\u7684\u4e00\u4e2a\u5973\u4eba\u53eb\u8776\u821e\u3002 \u3000\u3000\u8776\u821e\u4e0d\u4f46\u4eba\u7f8e\uff0c\u821e\u59ff\u66f4\u7f8e\u3002 \u3000\u3000\u5929\u4e0b\u6700\u61c2\u5f97\u6b23\u8d4f\u5973\u4eba\u7684\u4e16\u88ad\u4e00\u7b49\u4faf\u72c4\u9752\u9e9f\uff0c\u8fd8\u6ca1\u6709\u6b7b\u4e8e\u79bb\u522b\u94a9\u4e4b\u4e0b\u7684\u65f6\u5019\uff0c\u5728\u770b\u5230\u8776\u821e \u4e00\u821e\u65f6\uff0c\u5c45\u7136\u53d8\u5f97\u4ec0\u4e48\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\uff0c\u522b\u4eba\u95ee\u4ed6\u7684\u611f\u89c9\u5982\u4f55\uff0c\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\u4e4b\u540e\u4ed6\u624d\u53f9\u606f\u7740 \u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u8bdd\u8bf4\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u60f3\u5230\u51e1\u4eba\u8eab\u4e0a\u4f1a\u6709\u8fd9\u4e48\u6837\u4e00\u53cc\u817f\uff0c\u6211\u4e5f\u4ece\u6765\u6ca1\u6709\u770b\u5230 \u8fc7\u3002\u201d \u3000\u3000\u6c5f\u6e56\u4e2d\u6bcf\u4e2a\u4eba\u90fd\u7edd\u5bf9\u76f8\u501f\uff0c\u8fd9\u4e00\u6b21\u6731\u731b\u4e0d\u7ba1\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u90fd\u7edd\u5bf9\u4e0d\u4f1a\u653e\u8fc7\u6768\u575a\u7684\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u6682\u65f6\u8fd8\u52a8\u4e0d\u4e86\u53f8\u9a6c\u8d85\u7fa4\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u5148\u6740\u4e86\u6768\u575a\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7684\u60f3\u6cd5\u5374\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u8fd9\u4e00\u6b21\u4e0d\u7ba1\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u6731\u731b\u90fd\u4f11\u60f3\u52a8\u6768\u575a\u4e00\u6839\u6beb\u53d1\u3002 \u3000\u3000\u4ed6\u6709\u628a\u63e1\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5927\u5178\u662f\u5b8c\u5168\u516c\u5f00\u7684\uff0c\u6536\u5230\u8bf7\u67ec\u7684\u4eba\u56fa\u7136\u53ef\u4ee5\u767b\u5802\u4eba\u5ba4\uff0c\u505a\u53f8\u9a6c\u8d85\u7fa4\u7684\u4f73\u5bbe\uff0c\u6ca1\u6709 \u6536\u5230\u8bf7\u67ec\u7684\u4eba\uff0c\u4e5f\u53ef\u5230\u5927\u5385\u5916\u7684\u9662\u5b50\u91cc\u6765\u770b\u770b\u70ed\u95f9\u3002 \u3000\u3000\u96c4\u72ee\u5802\u95e8\u4e0b\u7684\u5f1f\u5b50\u4e2d\uff0c\u6709\u5f88\u591a\u90fd\u662f\u8eab\u7ecf\u767e\u6218\u6740\u4eba\u65e0\u7b97\u7684\u597d\u624b\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u5f85\u4ef7\u800c\u6cbd\u7684\u523a\u5ba2\u6740\u624b\u4e2d\uff0c\u80fd\u5728\u91cd\u91cd\u8b66\u536b\u4e2d\u6740\u4eba\u4e8e\u77ac\u606f\u95f4\u7684\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u3002\u8fd9\u4e9b\u4eba \u4eca\u5929\u665a\u4e0a\u90fd\u53ef\u80fd\u4f1a\u8d76\u5230\u8fd9\u91cc\u672a\uff0c\u6df7\u5165\u4eba\u7fa4\u91cc\uff0c\u7b49\u5f85\u523a\u6740\u6768\u575a\u7684\u673a\u4f1a\u3002 \u3000\u3000\u5728\u5927\u5178\u8fdb\u884c\u7684\u8fc7\u7a0b\u4e2d\uff0c\u8fd9\u79cd\u673a\u4f1a\u5f53\u7136\u4e0d\u5c11\u3002 \u3000\u3000\u4f46\u662f\u5353\u4e1c\u7c73\u76f8\u4fe1\u5927\u5178\u8fd8\u662f\u4f1a\u987a\u5229\u5b8c\u6210\uff0c\u6768\u575a\u8fd8\u662f\u4e0d\u53bb\u53d7\u5230\u6beb\u53d1\u4e4b\u4f24\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u628a\u6bcf\u4e00\u79cd\u53ef\u80fd\u4f1a\u53d1\u751f\u7684\u60c5\u51b5\u90fd\u8ba1\u7b97\u8fc7\uff0c\u6bcf\u4e00\u4e2a\u6709\u53ef\u80fd\u4f1a\u523a\u6740\u6768\u575a\u7684\u4eba\uff0c\u90fd\u5df1 \u5728\u4ed6\u7684\u4e25\u5bc6\u76d1\u89c6\u4e0b\u3002 \u3000\u3000\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u5df2\u7ecf\u51fa\u52a8\u4e86\u201c\u5317\u9053\u4e09\u5341\u4e5d\u8def\u5927\u9556\u5c40\u201d\u65d7\u4e0b\u7684\u4e00\u767e\u516b\u5341\u516d\u4f4d\u4e00\u7ea7\u597d\u624b\uff0c\u6bcf \u4e00\u4f4d\u90fd\u53ef\u4ee5\u5bf9\u4ed8\u4e8c\u5341\u4e03\u516b\u6761\u5927\u6c49\u7684\u597d\u624b\u3002 \u3000\u3000\u5353\u4e1c\u6765\u628a\u4ed6\u4eec\u5206\u6210\u4e86\u516b\u7ec4\uff0c\u6bcf\u4e00\u7ec4\u90e8\u7edd\u5bf9\u53ef\u4ee5\u72ec\u5f53\u4e00\u9762\u3002 \u3000\u3000\u53ef\u662f\u5176\u4e2d\u7ecf\u8fc7\u7279\u522b\u6311\u9009\u7684\u4e00\u7ec4\uff0c\u5374\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u8981\u53bb\u5bf9\u4ed8\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u662f\u54ea\u4e09\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u4eca\u5929\u65e9\u4e0a\u53f8\u9a6c\u8d85\u7fa4\u66fe\u7ecf\u95ee\u8fc7\u5353\u4e1c\u6765\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u7528\u4e00\u7ec4\u4eba\u5bf9\u4ed8\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u53ea\u8bf4\u51fa\u4e24\u4e2a\u4eba\u7684\u540d\u5b57\u5c31\u5df2\u89e3\u7b54\u4e86\u8fd9\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u4e09\u4e2a\u4eba\u4e2d\u6709\u4e00\u4e2a\u662f\u97e9\u7ae0\uff0c\u8fd8\u6709\u4e00\u4e2a\u662f\u6728\u9e21\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5019\u53f8\u9a6c\u8d85\u7fa4\u6b63\u5728\u5403\u65e9\u996d\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u975e\u5e38\u5f3a\u58ee\u7684\u4eba\uff0c\u9700\u8981\u6781\u4e30\u5bcc\u7684\u98df\u7269\u624d\u80fd\u7ef4\u6301\u4ed6\u5145\u6c9b\u7684\u4f53\u529b\u3002 \u3000\u3000\u4eca\u5929\u4ed6\u7684\u65f1\u996d\u662f\u4e00\u5927\u5757\u81f3\u5c11\u6709\u4e09\u65a4\u91cd\u7684\u5c0f\u725b\u8170\u8089\uff0c\u518d\u914d\u4e0a\u5341\u4e2a\u86cb\uff0c\u548c\u5927\u91cf\u6c34\u679c\u852c\u83dc\u3002 \u3000\u3000\u725b\u8089\u662f\u7528\u6728\u70ad\u6587\u706b\u70e4\u6210\u7684\uff0c\u4e0a\u9762\u6d82\u6ee1\u4e86\u53e3\u5473\u6781\u91cd\u7684\u9171\u6c41\u548c\u9999\u6599\uff0c\u70e4\u5f97\u6781\u5ae9\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u6700\u559c\u7231\u7684\u98df\u7269\u4e4b\u4e00\uff0c\u53ef\u662f\u542c\u5230\u5353\u4e1c\u6765\u8bf4\u51fa\u7684\u4e24\u4e2a\u540d\u5b57\u540e\uff0c\u4ed6\u5c31\u653e\u4e0b\u4e86\u4ed6\u5272\u8089\u7528\u7684 \u6ce2\u65af\u5f2f\u5200\uff0c\u7528\u4e00\u53cc\u5200\u950b\u6bb7\u7684\u9510\u773c\u76ef\u7740\u5353\u4e1c\u6765\u3002 \u3000\u3000\u201c\u97e9\u7ae0\u548c\u6728\u9e21\u90fd\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4ee5\u524d\u89c1\u8fc7\u8fd9\u4e24\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u3002\u201d\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u6211\u76f8\u4fe1\u8fd9\u91cc\u6ca1\u6709\u4eba\u89c1\u8fc7\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4ed6\u4eec\u7684\u540d\u5b57\u6c5f\u6e56\u4e2d\u5927\u591a\u6570\u7684\u4eba\u90fd\u77e5\u9053\uff0c\u5374\u5f88\u5c11\u6709\u4eba\u89c1\u8fc7\u4ed6\u4eec\u3002 \u3000\u3000\u97e9\u7ae0\u548c\u6768\u575a\u4e00\u6837\uff0c\u90fd\u662f\u201c\u96c4\u72ee\u201d\u7684\u7231\u5c06\uff0c\u662f\u4ed6\u8eab\u8fb9\u6700\u4eb2\u4fe1\u7684\u4eba\uff0c\u4e5f\u662f\u4ed6\u624b\u4e0b\u6700\u5371\u9669\u7684 \u4eba\u3002 \u3000\u3000\u6731\u731b\u4e00\u5411\u5f88\u5c11\u8ba9\u4ed6\u4eec\u79bb\u5f00\u81ea\u5df1\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u6728\u9e21\u8fdc\u6bd4\u97e9\u7ae0\u66f4\u5371\u9669\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u5bb6\uff0c\u6ca1\u6709\u56fa\u5b9a\u7684\u4f4f\u5904\uff0c\u4e5f\u6ca1\u6709\u56fa\u5b9a\u7684\u751f\u6d3b\u65b9\u5f0f\uff0c\u6240\u4ee5\u8c01\u4e5f\u627e\u4e0d\u5230\u4ed6\u3002 \u3000\u3000\u53ef\u662f\u5982\u679c\u6709\u4eba\u9700\u8981\u4ed6\uff0c\u4ed6\u4e5f\u8ba4\u4e3a\u81ea\u5df1\u9700\u8981\u8fd9\u4e2a\u4eba\uff0c\u90a3\u4e48\u4ed6\u5c31\u4f1a\u5ffd\u7136\u5728\u8fd9\u4e2a\u4eba\u9762\u524d\u51fa\u73b0 \u4e86\u3002 \u3000\u3000\u4ed6\u9700\u8981\u7684\u901a\u5e38\u90fd\u662f\u522b\u4eba\u7684\u73e0\u5b9d\u9ec4\u91d1\u548c\u6570\u76ee\u6781\u5927\u7684\u5de8\u989d\u94f6\u7968\u3002 \u3000\u3000\u522b\u4eba\u9700\u8981\u4ed6\u7684\uff0c\u901a\u5e38\u90fd\u662f\u4ed6\u7684\u7ede\u7d22\u98de\u9556\u548c\u4ed6\u6c38\u8fdc\u4e0d\u79bb\u624b\u8fb9\u7684\u4e24\u628a\u5200\u3002 \u3000\u3000\u4e00\u628a\u957f\u5200\uff0c\u4e00\u628a\u77ed\u5200\u3002 \u3000\u3000\u4ed6\u7528\u5200\u5272\u65ad\u4e00\u4e2a\u4eba\u7684\u54bd\u5589\u65f6\uff0c\u5c31\u597d\u50cf\u519c\u592b\u7528\u9570\u5200\u5272\u8349\u822c\u8f7b\u677e\u7eaf\u719f\u3002 \u3000\u3000\u4ed6\u7528\u7ede\u7d22\u6740\u4eba\u65f6\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u6e29\u67d4\u591a\u60c5\u7684\u82b1\u82b1\u516c\u5b50\uff0c\u628a\u4e00\u6761\u73e0\u94fe\u6302\u4e0a\u60c5\u4eba\u7684\u8116\u5b50\u3002 \u3000\u3000\u4ed6\u505a\u8fd9\u79cd\u4e8b\u5f53\u7136\u662f\u9700\u8981\u4ee3\u4ef7\u7684\uff0c\u5982\u679c\u4f60\u4ed8\u51fa\u7684\u4ee3\u4ef7\u4e0d\u80fd\u8ba9\u4ed6\u6ee1\u610f\uff0c\u5c31\u7b97\u8dea\u4e0b\u6765\u6c42\u4ed6\uff0c\u4ed6 \u4e5f\u4e0d\u4f1a\u4e3a\u4f60\u53bb\u8e0f\u6b7b\u4e00\u53ea\u8682\u8681\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u8981\u4ed6\u53bb\u505a\u8fd9\u79cd\u4e8b\uff0c\u90fd\u4e00\u5b9a\u8981\u5148\u4ed8\u51fa\u4e00\u7b14\u80fd\u591f\u8ba9\u4ed6\u6ee1\u610f\u7684\u4ee3\u4ef7\uff0c\u53ea\u6709\u4e00\u4e2a\u4eba\u662f\u4f8b \u5916\uff0c\u56e0\u4e3a\u4ed6\u4e00\u751f\u4e2d\u53ea\u6b20\u8fd9\u4e00\u4e2a\u4eba\u7684\u60c5\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5c31\u662f\u672c\u9e21\u3002 \u3000\u3000\u5200\u73af\u4e0a\u9576\u6ee1\u78a7\u7389\u7684\u5f2f\u5200\uff0c\u5df2\u7ecf\u6446\u5728\u76db\u4f2a\u7684\u6728\u76d8\u91cc\uff0c\u5200\u950b\u4e0a\u8fd8\u7559\u9996\u6d53\u6d53\u7684\u8089\u6c41\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7528\u4e00\u5757\u67d4\u8f6f\u7684\u4e1d\u4e2d\u628a\u5200\u950b\u64e6\u5f97\u96ea\u4eae\uff0c\u7136\u540e\u624d\u95ee\u5353\u4e1c\u6765\uff1a \u3000\u3000\u201c\u4f60\u6ca1\u6709\u89c1\u8fc7\u4ed6\u4eec\uff0c\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5353\u4e1c\u6765\u8c08\u6de1\u7684\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\uff0c\u6240\u4ee5\u6211\u5c31\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u7b97\u662f\u4ec0\u4e48\u56de\u7b54\uff1f\u8fd9\u79cd\u56de\u7b54\u6839\u672c\u5c31\u4e0d\u80fd\u7b97\u662f\u56de\u7b54\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u6ee1\u610f\u7684\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5374\u5df2\u7ecf\u5f88\u6ee1\u610f\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u662f\u5353\u4e1c\u6765\u8bf4\u51fa\u6765\u7684\uff0c\u4ed6\u76f8\u4fe1\u5353\u4e1c\u6765\u7684\u5224\u65ad\u529b\uff0c\u6b63\u5982\u4ed6\u76f8\u4fe1\u6728\u76d8\u91cc\u8fd9\u628a\u5200\u662f\u53ef\u4ee5\u5272 \u8089\u7684\u4e00\u6837\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u773c\u775b\u91cc\u5374\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u5ffd\u7136\u8bf4\u51fa\u53e5\u5f88\u5947\u602a\u7684\u8bdd\u3002 \u3000\u3000\u201c\u9519\u4e86\uff01\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u6b21\u6731\u731b\u9519\u4e86\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u81ea\u95ee\uff1a\u201c\u73b0\u5728\u97e9\u7ae0\u548c\u6728\u9e21\u662f\u4e0d\u662f\u5df2\u7ecf\u6765\u5230\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u8fd8\u80fd\u4e0d\u80fd\u6d3b\u7740\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5bf9\u6731\u731b\u662f\u4e0d\u662f\u5f88\u6709\u7528\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u8ba9\u4e24\u4e2a\u5bf9\u81ea\u5df1\u8fd9\u4e48\u6709\u7528\u7684\u4eba\u53bb\u9001\u6b7b\uff0c\u8fd9\u79cd\u4e8b\u6211\u4f1a\u4e0b\u4f1a\u505a\uff1f\u201d\u53f8\u9a6c\u95ee\u5353\u4e1c\u6765\uff1a\u201c\u4f60\u4f1a\u4e0d \u4f1a\u505a\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\uff01\u201d \u3000\u3000\u53f8\u9a6c\u5927\u7b11\uff1a\u201c\u6240\u4ee5\u6731\u731b\u9519\u4e86\uff0c\u4ed6\u5f88\u5c11\u9519\uff0c\u53ef\u662f\u8fd9\u6b21\u9519\u4e86\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6ca1\u6709\u7b11\uff0c\u7b49\u53f8\u9a6c\u7b11\u5b8c\u4e86\uff0c\u624d\u6162\u6162\u7684\u8bf4\uff1a\u201c\u6731\u731b\u6ca1\u6709\u9519\uff01\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8981\u4ed6\u4eec\u5230\u8fd9\u91cc\u6765\uff0c\u5e76\u4e0d\u662f\u8981\u4ed6\u4eec\u6765\u9001\u6b7b\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\u3002 \u3000\u3000\u201c\u4ed6\u8981\u4ed6\u4eec\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6765\u505a\u5e4c\u5b50\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u97e9\u7ae0\u548c\u6728\u9e21\u90fd\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5e4c\u5b50\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u771f\u6b63\u8981\u51fa\u624b\u523a\u6740\u6768\u575a\u7684\u5e76\u4e0d\u662f\u4ed6\u4eec\uff0c\u800c\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5982\u679c\u6211\u4eec \u5355\u53ea\u9632\u5907\u4ed6\u4eec\uff0c\u7b2c\u4e09\u4e2a\u4eba\u51fa\u624b\u65f6\u5c31\u5bb9\u6613\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u5e74\u8f7b\u4eba\uff0c\u7a7f\u4e00\u8eab\u7c97\u5e03\u886b\uff0c\u5e26\u7740\u4e00\u53e3\u5251\uff0c\u4f4f\u5728\u4e00\u5bb6\u6700\u4fbf\u5b9c\u7684\u5c0f\u5ba2\u6808\u91cc\uff0c\u6bcf\u987f\u53ea\u5403\u4e00 \u7897\u7528\u767d\u83dc\u716e\u7684\u6e05\u6c64\u9762\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4ed6\u5df2\u7ecf\u6765\u4e86\u4e09\u5929\uff0c\u53ef\u662f\u9664\u4e86\u51fa\u6765\u5403\u9762\u7684\u65f6\u5019\u5916\uff0c\u4ece\u6765 \u6ca1\u6709\u51fa\u8fc7\u623f\u95e8\u3002\u201d \u3000\u3000\u201c\u4ed6\u628a\u81ea\u5df1\u5173\u5728\u90a3\u5e62\u9664\u4e86\u81ed\u866b\u5916\uff0c\u4ec0\u4e48\u90e8\u6ca1\u6709\u7684\u5c0f\u5c4b\u5b50\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u5b66\u7684\u662f\u4ec0\u4e48\u5251\u6cd5\uff1f\u5251\u6cd5\u9ad8\u4e0d\u9ad8\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7684\u77b3\u5b54\u5ffd\u7136\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u548c\u5353\u4e1c\u6765\u76f8\u4ea4\u5df2\u6709\u4e8c\u5341\u5e74\uff0c\u4ece\u8d2b\u7a77\u56f0\u82e6\u7684\u6ce5\u6dd6\u4e2d\u722c\u5230\u4eca\u5929\u7684\u5730\u4f4d\uff0c\u6ca1\u6709\u4eba\u6bd4\u5353\u4e1c\u6765\u66f4 \u4e86\u89e3\u4ed6\uff0c\u4e5f\u6ca1\u6709\u4eba\u6bd4\u4ed6\u66f4\u4e86\u89e3\u5353\u4e1c\u6765\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u60f3\u5230\u201c\u4e0d\u77e5\u9053\u201d\u8fd9\u4e09\u4e2a\u5b57\u4e5f\u4f1a\u4ece\u5353\u4e1c\u6765\u5634\u91cc\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5982\u679c\u8981\u8c03\u67e5\u4e00\u4e2a\u4eba\uff0c\u6700\u591a\u53ea\u8981\u7528\u4e09\u3001\u4e94\u4e2a\u65f6\u8fb0\uff0c\u5c31\u53ef\u4ee5\u628a\u8fd9\u4e2a\u4eba\u7684\u51fa\u8eab\u5bb6\u4e16\u80cc\u666f \u4e60\u60ef\u55dc\u597d\u6b66\u529f\u95e8\u6d3e\uff0c\u81ea\u4f55\u5904\u6765\uff0c\u5f80\u4f55\u5904\u53bb\uff0c\u5168\u90e8\u8c03\u67e5\u51fa\u6765\u3002 \u3000\u3000\u505a\u8fd9\u4e00\u7c7b\u7684\u4e8b\uff0c\u4ed6\u4e0d\u4f46\u6781\u6709\u7ecf\u9a8c\uff0c\u800c\u4e14\u6709\u65b9\u6cd5\uff0c\u5f88\u591a\u79cd\u7279\u522b\u7684\u65b9\u6cd5\u3002\u6bcf\u4e00\u79cd\u90fd\u7edd\u5bf9\u6709 \u6548\u3002 \u3000\u3000\u8fd9\u4e9b\u65b9\u6cd5\u53f8\u9a6c\u8d85\u7fa4\u4e5f\u77e5\u9053\u3002 \u3000\u3000\u201c\u4ed6\u4f4f\u7684\u662f\u4fbf\u5b9c\u5ba2\u6808\uff0c\u7a7f\u7684\u662f\u7c97\u5e03\u8863\u88f3\uff0c\u5403\u7684\u662f\u767d\u83dc\u716e\u9762\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u4ece\u8fd9\u51e0\u4ef6 \u4e8b\u4e0a\uff0c\u4f60\u81f3\u5c11\u5df2\u7ecf\u5e94\u8be5\u770b\u51fa\u6765\u4ed6\u7edd\u4e0d\u4f1a\u662f\u4e2a\u5f88\u6210\u529f\u7684\u4eba\uff0c\u51fa\u8eab\u4e00\u5b9a\u4e5f\u4e0d\u592a\u597d\u3002\u201d \u3000\u3000\u201c\u672c\u6765\u5e94\u8be5\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u8fd9\u4e2a\u5c11\u5e74\u5374\u662f\u4f8b\u5916\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u7684\u6c14\u5ea6\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u770b\u89c1\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u867d\u7136\u662f\u5728\u4e00\u5bb6\u6324\u6ee1\u4e86\u82e6\u529b\u8f66\u592b\u7684 \u5c0f\u996d\u6355\u91cc\u5403\u767d\u83dc\u716e\u9762\uff0c\u53ef\u662f\u4ed6\u7684\u6837\u5b50\u770b\u8d77\u6765\u5374\u597d\u50cf\u662f\u4f4d\u65b0\u79d1\u72b6\u5143\u5750\u5728\u592a\u534e\u6bbf\u91cc\u5403\u743c\u6797\u5bb4\uff0c\u867d \u7136\u53ea\u7a7f\u7740\u90a3\u4ef6\u7c97\u5e03\u8863\u88f3\uff0c\u5374\u597d\u50cf\u662f\u4ef6\u4ef7\u503c\u5343\u91d1\u7684\u8c82\u88d8\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4ed6\u662f\u5728\u6545\u610f\u88c5\u8154\u4f5c\u6001\u3002\u201d \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u662f\u88c5\u4e0d\u51fa\u6765\u7684\uff0c\u53ea\u6709\u4e00\u4e2a\u5bf9\u81ea\u5df1\u7edd\u5bf9\u6709\u4fe1\u5fc3\u7684\u4eba\u624d\u4f1a\u6709\u8fd9\u79cd\u6c14\u5ea6\u3002\u201d\u5353\u4e1c\u6765 \u8bf4\uff1a\u201c\u6211\u4ece\u672a\u89c1\u8fc7\u50cf\u4ed6\u90a3\u4e48\u6709\u81ea\u4fe1\u7684\u4eba\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u773c\u775b\u91cc\u53d1\u51fa\u4e86\u5149\uff0c\u5bf9\u8fd9\u4e2a\u5c11\u5e74\u4e5f\u6e10\u6e10\u6709\u5174\u8da3\u4e86\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u89c1\u8fc7\u5353\u4e1c\u6765\u8fd9\u4e48\u6837\u770b\u91cd\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4ed6\u5728\u90a3\u5bb6\u5ba2\u6808\u91cc\u7528\u7684\u540d\u5b57\u53eb\u674e\u8f89\u6210\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e2a\u540d\u5b57\u4e00\u5b9a\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4e00\u5b9a\u662f\u5047\u7684\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u770b\u89c1\u8fc7\u4ed6\u5728\u67dc\u53f0\u4e0a\u7559\u7684\u540d\u5b57\uff0c\u662f\u4ed6\u81ea\u5df1\u5199\u7684\uff0c\u5b57\u5199\u5f97\u4e0d\u9519\u3002\u5374\u5199\u5f97\u5f88\u751f\u786c\u3002\u201d \u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e00\u4e2a\u4f1a\u5199\u5b57\u7684\u4eba\u7edd\u4e0d\u4f1a\u628a\u81ea\u5df1\u7684\u540d\u5b57\u5199\u5f97\u90a3\u4e48\u5446\u677f\u751f\u786c\u3002\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u8bdd\u662f\u4ec0\u4e48\u53e3\u97f3\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u542c\u8fc7\u4ed6\u8bf4\u8bdd\uff0c\u53ef\u662f\u6211\u95ee\u8fc7\u90a3\u5bb6\u5ba2\u6808\u7684\u638c\u67dc\u3002\u201d \u3000\u3000\u201c\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4ee5\u524d\u662f\u5bb6\u9556\u5c40\u91cc\u7684\u8d9f\u5b50\u624b\uff0c\u8d70\u8fc7\u5f88\u591a\u5730\u65b9\uff0c\u4f1a\u8bf4\u4e03\u516b\u4e2a\u7701\u4efd\u7684\u8bdd\u3002\u201d\u5353\u4e1c\u6765\u9053\uff1a \u201c\u53ef\u662f\u4ed6\u4e5f\u542c\u4e0d\u51fa\u8fd9\u4f4d\u59d3\u674e\u7684\u5ba2\u4eba\u662f\u54ea\u91cc\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u4f4d\u674e\u5148\u751f\u4e5f\u4f1a\u8bf4\u4e03\u516b\u4e2a\u7701\u4efd\u7684\u8bdd\uff0c\u6bcf\u4e00\u79cd\u90fd\u8bf4\u5f97\u6bd4\u4ed6\u597d\u3002\u201d \u3000\u3000\u201c\u4ed6\u7a7f\u7684\u8863\u88f3\u5462\uff1f\u201d \u3000\u3000\u4ece\u4e00\u4e2a\u4eba\u7a7f\u7684\u8863\u670d\u4e0a\uff0c\u4e5f\u53ef\u4ee5\u770b\u51fa\u5f88\u591a\u4e8b\u3002 \u3000\u3000\u8863\u670d\u6599\u5b50\u4e0d\u540c\uff0c\u540c\u6837\u662f\u7c97\u5e03\uff0c\u4e5f\u6709\u5f88\u591a\u79cd\uff0c\u6bcf\u4e2a\u5730\u65b9\u67d3\u7ec7\u7684\u65b9\u6cd5\u90fd\u4e0d\u4e00\u6837\uff0c\u68c9\u7eb1\u7684\u4ea7\u5730 \u4e5f\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u9274\u522b\u8fd9\u4e00\u7c7b\u7684\u4e8b\uff0c\u5353\u4e1c\u6765\u4e5f\u662f\u4e13\u5bb6\u3002 \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u770b\u8fc7\u4ed6\u7684\u8863\u670d\uff0c\u201d\u53f8\u9a6c\u8d85\u7fa4\u95ee\uff1a\u201c\u4f60\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4ec0\u4e48\u90fd\u770b\u4e0d\u51fa\u3002\u201d\u5353\u4e1c\u6765\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u770b\u8fc7\u90a3\u79cd\u7c97\u5e03\uff0c\u751a\u81f3\u8fde\u4ed6\u7f1d\u8863\u670d\u7528\u7684\u90a3 \u79cd\u7ebf\u6211\u90fd\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u76f8\u4fe1\u4e00\u5b9a\u662f\u4ed6\u81ea\u5df1\u7eba\u7684\u7eb1\uff0c\u81ea\u5df1\u7ec7\u7684\u5e03\uff0c\u81ea\u5df1\u7f1d\u7684\u8863\u670d\uff0c\u8fde\u68c9\u82b1\u90fd\u662f\u4ed6 \u81ea\u5df1\u5728\u4e00\u4e2a\u5f88\u7279\u522b\u7684\u5730\u65b9\u79cd\u51fa\u6765\u7684\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u90a3\u4e2a\u5730\u65b9\u4f60\u6211\u5927\u6982\u90fd\u6ca1\u6709\u53bb\u8fc7\u3002\u201d \u3000\u3000\u4ed6\u4eec\u540c\u65f6\u51fa\u9053\uff0c\u95ef\u904d\u5929\u4e0b\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u6614\u7b11\uff1a\u201c\u8fde\u6211\u4eec\u90fd\u6ca1\u6709\u53bb\u8fc7\u7684\u5730\u65b9\uff0c\u53bb\u8fc7\u7684\u4eba\u5927\u6982\u4e5f\u4e0d\u4f1a\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u6ca1\u6709\u770b\u5230\u4ed6\u7684\u5251\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u9053\uff1a\u201c\u4ed6\u7684\u5251\u59cb\u7ec8\u7528\u5e03\u5305\u7740\uff0c\u59cb\u7ec8\u5e26\u5728\u8eab\u8fb9\u3002\u201d \u3000\u3000\u201c\u4ed6\u7528\u6765\u5305\u5251\u7684\u5e03\u662f\u4e0d\u662f\u4e5f\u8ddf\u4ed6\u505a\u8863\u670d\u7684\u5e03\u4e00\u6837\uff1f\u201d \u3000\u3000\u201c\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5ffd\u7136\u53c8\u7b11\u4e86\uff1a\u201c\u770b\u8d77\u6765\u8fd9\u4f4d\u674e\u5148\u751f\u5012\u771f\u7684\u662f\u4e2a\u602a\u4eba\uff0c\u5982\u679c\u4ed6\u771f\u662f\u6765\u6740\u6211\u7684\uff0c\u90a3 \u4e48\u4eca\u5929\u665a\u4e0a\u5c31\u5f88\u597d\u73a9\u4e86\u3002\u201d \u4e09 \u3000\u3000\u9ec4\u660f\u3002 \u3000\u3000\u5c0f\u996d\u94fa\u91cc\u5145\u6ee1\u4e86\u732a\u6cb9\u7092\u83dc\u7684\u9999\u6c14\u3001\u82e6\u529b\u8f66\u592b\u8eab\u4e0a\u7684\u6c57\u81ed\uff0c\u548c\u70c8\u9152\u8fa3\u6912\u5927\u8471\u5927\u849c\u6df7\u5408\u6210\u7684 \u4e00\u79cd\u96be\u4ee5\u5f62\u5bb9\u7684\u5947\u602a\u5473\u9053\u3002 \u3000\u3000\u5c0f\u9ad8\u559c\u6b22\u8fd9\u79cd\u5473\u9053\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u9ad8\u5c71\u4e0a\u90a3\u79cd\u98d8\u6d6e\u5728\u81ea\u4e91\u548c\u51b7\u98ce\u4e2d\u7684\u6728\u53f6\u6e05\u9999\uff0c\u53ef\u662f\u4ed6\u4e5f\u559c\u6b22\u8fd9\u79cd\u5473\u9053\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u9ad8\u8d35\u4f18\u96c5\u7684\u9ad8\u4eba\u540d\u58eb\uff0c\u53ef\u662f\u4ed6\u4e5f\u559c\u6b22\u8fd9\u4e9b\u6d41\u7740\u6c57\u7528\u5927\u997c\u5377\u5927\u8471\u5c31\u7740\u849c\u5934\u5403\u80a5\u8089\u559d \u52a3\u9152\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u5b64\u72ec\u4e86\u592a\u4e45\uff0c\u9664\u4e86\u9752\u5c71\u767d\u4e91\u6d41\u6c34\u53e4\u677e\u5916\uff0c\u4ed6\u4e00\u76f4\u90fd\u5f88\u5c11\u89c1\u5230\u4eba\u3002 \u3000\u3000\u76f4\u5230\u4e09\u4e2a\u6708\u524d\uff0c\u4ed6\u624d\u56de\u5230\u4eba\u7684\u4e16\u754c\u91cc\u6765\uff0c\u4e09\u4e2a\u6708\u4ed6\u5df2\u7ecf\u6740\u4e86\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u56db\u4e2a\u58f0\u540d\u663e\u8d6b\u96c4\u9738\u4e00\u65b9\u7684\u4eba\uff0c\u56db\u4e2a\u672c\u6765\u867d\u7136\u8be5\u6b7b\u5374\u4e0d\u4f1a\u6b7b\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u4eba\uff0c\u53ef\u662f\u4ed6\u8981\u6740\u4eba\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u559c\u6b22\u6740\u4eba\uff0c\u53ef\u662f\u4ed6\u8981\u6740\u4eba\u3002 \u3000\u3000\u4e16\u754c\u4e0a\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u4f7f\u4f60\u6839\u672c\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\u3002 \u3000\u3000\u957f\u5b89\uff0c\u53e4\u8001\u7684\u957f\u5b89\uff0c\u96c4\u4f1f\u7684\u57ce\u581e\uff0c\u5145\u6ee1\u4e86\u60a0\u4e45\u5386\u53f2\u548c\u65e0\u6570\u4f20\u5947\u6545\u4e8b\u7684\u52a8\u4eba\u98ce\u60c5\u3002 \u3000\u3000\u5c0f\u9ad8\u5374\u4e0d\u662f\u4e3a\u4e86\u8fd9\u4e9b\u4e8b\u6765\u7684\u3002 \u3000\u3000\u5c0f\u9ad8\u662f\u4e3a\u4e86\u4e00\u4e2a\u4eba\u6765\u7684\u2014\u2014\u6c38\u8fdc\u4e0d\u8d25\u7684\u82f1\u96c4\u53f8\u9a6c\u8d85\u7fa4\u3002 \u3000\u3000\u4ed6\u5e26\u7740\u4ed6\u7684\u5251\u6765\uff0c\u4ed6\u7684\u5251\u5c31\u5728\u4ed6\u7684\u624b\u8fb9\uff0c\u6c38\u8fdc\u90fd\u5728\u4ed6\u7684\u624b\u8fb9\u3002 \u3000\u3000\u4e00\u67c4\u7528\u7c97\u5e03\u7d27\u7d27\u5305\u4f4f\u7684\u5251\u3002 \u3000\u3000\u5f88\u5c11\u6709\u4eba\u80fd\u770b\u5230\u8fd9\u67c4\u5251\uff0c\u4ece\u8fd9\u67c4\u5251\u51fa\u7089\u4ee5\u6765\uff0c\u5c31\u5f88\u5c11\u6709\u4eba\u80fd\u770b\u5230\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u4e0d\u662f\u7ed9\u4eba\u770b\u7684\u3002 \u3000\u3000\u5c0f\u9ad8\u77e5\u9053\u5df2\u7ecf\u6709\u4eba\u5728\u6ce8\u610f\u4ed6\u4e86\u3002 \u3000\u3000\u5230\u8fd9\u91cc\u6765\u7684\u7b2c\u4e8c\u5929\uff0c\u4ed6\u5c31\u53d1\u73b0\u6709\u4e2a\u4eba\u5728\u6ce8\u610f\u4ed6\uff0c\u4e00\u4e2a\u8eab\u6750\u5f88\u7626\u5c0f\uff0c\u8863\u7740\u5f88\u534e\u8d35\uff0c\u4e00\u53cc\u51b7 \u51b7\u6de1\u6de1\u597d\u50cf\u6c38\u8fdc\u4e0d\u4f1a\u6709\u4ec0\u4e48\u8868\u60c5\u7684\u773c\u775b\uff0c\u770b\u8d77\u6765\u4eff\u4f5b\u662f\u7070\u8272\u7684\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u8fc7\u8fd9\u79cd\u773c\u775b\u3002 \u3000\u3000\u5341\u4e00\u5c81\u7684\u65f6\u5019\uff0c\u4ed6\u51e0\u4e4e\u6b7b\u5728\u4e00\u5934\u8c79\u5b50\u7684\u5229\u722a\u4e0b\uff0c\u8fd9\u4e2a\u4eba\u7684\u773c\u775b\u5c31\u8ddf\u90a3\u5934\u8c79\u5b50\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e00\u51fa\u73b0\uff0c\u5c0f\u996d\u94fa\u91cc\u5f88\u591a\u4eba\u597d\u50cf\u8fde\u547c\u5438\u90fd\u505c\u987f\u4e86\u3002 \u3000\u3000\u540e\u6765\u4ed6\u624d\u77e5\u9053\u8fd9\u4e2a\u4eba\u5c31\u662f\u201c\u603b\u7ba1\u5317\u9053\u4e09\u5341\u4e5d\u8def\u5927\u9556\u5c40\u201d\u7684\u5927\u9f99\u5934\u53f8\u9a6c\u8d85\u7fa4\u8eab\u8fb9\u6700\u5f97\u529b\u7684 \u5e2e\u624b\u2014\u2014\u5353\u4e1c\u6765\u3002 \u3000\u3000\u5c0f\u9ad8\u6162\u6162\u7684\u5403\u7740\u4e00\u7897\u7528\u767d\u83dc\u716e\u7684\u6e05\u6c64\u9762\uff0c\u5fc3\u91cc\u89c9\u5f97\u5f88\u6109\u5feb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u5353\u4e1c\u6765\u548c\u53f8\u9a6c\u8d85\u7fa4\u4e00\u5b9a\u4f1a\u6000\u7591\u4ed6\u3001\u8c08\u8bba\u4ed6\uff0c\u731c\u6d4b\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u4ed6\u4eec\u4e00\u5b9a\u4e0d\u4f1a\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\u7684\u3002 \u3000\u3000\u4ed6\u8fd9\u4e2a\u4eba\u5c31\u548c\u4ed6\u7684\u5251\u4e00\u6837\uff0c\u81f3\u4eca\u8fd8\u5f88\u5c11\u6709\u4eba\u770b\u89c1\u8fc7\u3002 \u56db \u3000\u3000\u5929\u8272\u5df2\u7ecf\u6e10\u6e10\u6697\u4e86\uff0c\u5c4b\u5b50\u91cc\u867d\u7136\u6ca1\u6709\u70b9\u706f\uff0c\u5916\u9762\u7684\u706f\u706b\u5374\u8d8a\u6765\u8d8a\u8f89\u714c\u660e\u4eae\u3002 \u3000\u3000\u5bd2\u98ce\u4ece\u7a97\u7f1d\u91cc\u5439\u8fdb\u6765\uff0c\u5df2\u7ecf\u9690\u7ea6\u53ef\u4ee5\u542c\u89c1\u524d\u9762\u5927\u9662\u91cc\u4f20\u6765\u7684\u4eba\u58f0\u548c\u7b11\u58f0\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u77e5\u9053\u4ed6\u8bf7\u6765\u89c2\u793c\u7684\u4f73\u5bbe\u548c\u4ed6\u6ca1\u6709\u8bf7\u7684\u4eba\u90fd\u5df2\u7ecf\u6765\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u6bcf\u4e2a\u4eba\u90fd\u5728\u7b49\u7740\u4ed6\u9732\u9762\uff0c\u7b49\u7740\u770b\u4ed6\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u751a\u81f3\u8fde\u4ed6\u7684\u59bb\u5b50\u8fdb\u6765\u65f6\u4ed6\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u70e6\u900f\u4e86\u3002 \u3000\u3000\u5f00\u9999\u5802\uff0c\u6536\u5f1f\u5b50\uff0c\u5927\u5f20\u7b75\u5e2d\uff0c\u63a5\u89c1\u5bbe\u5ba2\uff0c\u5bf9\u6240\u6709\u7684\u8fd9\u4e9b\u4e8b\u4ed6\u90fd\u89c9\u5f97\u70e6\u900f\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u60f3\u5b89\u5b89\u9759\u9759\u7684\u5750\u5728\u8fd9\u91cc\u559d\u676f\u9152\u3002 \u3000\u3000\u5434\u5a49\u4e86\u89e3\u4ed6\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6bd4\u5434\u5a49\u66f4\u4e86\u89e3\u53f8\u9a6c\u8d85\u7fa4\uff0c\u4ed6\u4eec\u7ed3\u5408\u5df2\u7ecf\u6709\u5341\u4e00\u5e74\uff0c\u5df2\u7ecf\u6709\u4e86\u4e00\u4e2a\u4e5d\u5c81\u7684\u5b69\u5b50\u3002 \u3000\u3000\u5979\u662f\u6765\u50ac\u4ed6\u5feb\u70b9\u51fa\u53bb\u7684\u3002 \u3000\u3000\u53ef\u662f\u5979\u6084\u6084\u7684\u63a8\u95e8\u8fdb\u6765\uff0c\u53c8\u6084\u6084\u7684\u63a9\u95e8\u51fa\u53bb\uff0c\u5e76\u6ca1\u6709\u60ca\u52a8\u4ed6\u3002 \u3000\u3000\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u5979\u7684\u773c\u6cea\u5ffd\u7136\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53f8\u9a6c\u53c8\u5012\u4e86\u4e00\u676f\u9152\u3002 \u3000\u3000\u8fd9\u5df2\u7ecf\u4e0d\u662f\u7b2c\u4e00\u676f\u4e86\uff0c\u662f\u7b2c\u4e8c\u5341\u4e03\u676f\u3002 \u3000\u3000\u4ed6\u559d\u7684\u4e0d\u662f\u5353\u4e1c\u6765\u559d\u7684\u90a3\u79cd\u6ce2\u65af\u8461\u8404\u9152\uff0c\u4ed6\u559d\u7684\u662f\u70e7\u5200\u5b50\uff0c\u867d\u7136\u65e0\u8272\u65e0\u5473\uff0c\u559d\u4e0b\u53bb\u65f6\u809a \u5b50\u91cc\u5374\u597d\u50cf\u6709\u706b\u7130\u5728\u71c3\u70e7\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u628a\u8fd9\u6000\u9152\u559d\u4e0b\u3002 \u3000\u3000\u95e8\u53c8\u6084\u4fcf\u7684\u63a8\u5f00\u4e86\uff0c\u8fd9\u6b21\u8fdb\u6765\u7684\u4e0d\u662f\u5434\u5a49\uff0c\u662f\u5353\u4e1c\u6765\u3002 \u3000\u3000\u53f8\u9a6c\u5782\u4e0b\u624b\uff0c\u628a\u8fd9\u676f\u8fd8\u6ca1\u6709\u559d\u7684\u9152\u653e\u5230\u6905\u4e0b\uff0c\u770b\u7740\u7ad9\u5728\u95e8\u53e3\u9634\u5f71\u4e2d\u7684\u5353\u4e1c\u6765\u3002 \u3000\u3000\u201c\u6211\u662f\u4e0d\u662f\u5df2\u7ecf\u5e94\u8be5\u51fa\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u4e94 \u3000\u3000\u5927\u9662\u91cc\u706f\u706b\u8f89\u714c\uff0c\u4eba\u58f0\u55a7\u54d7\u3002 \u3000\u3000\u5c0f\u9ad8\u6324\u5728\u4eba\u4e1b\u91cc\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u662f\u53f8\u9a6c\u8d85\u7fa4\u8bf7\u6765\u7684\u8d35\u5bbe\uff0c\u4e0d\u80fd\u8fdb\u5165\u90a3\u4e2a\u706f\u706b\u66f4\u8f89\u714c\u660e\u4eae\u7684\u5927 \u5385\u3002 \u3000\u3000\u5927\u5385\u91cc\u7684\u4eba\u4e5f\u6709\u4e0d\u5c11\uff0c\u5f53\u7136\u90fd\u662f\u4e9b\u540d\u4eba\uff0c\u6709\u8eab\u4efd\u3001\u6709\u5730\u4f4d\u3001\u6709\u6743\u52bf\u7684\u540d\u4eba\u3002 \u3000\u3000\u9664\u4e86\u8fd9\u4e9b\u540d\u4eba\u5916\uff0c\u8fd8\u6709\u4e00\u4e9b\u7a7f\u4e00\u8272\u9752\u7f0e\u9762\u7f8a\u76ae\u8902\u7684\u58ee\u6c49\u5728\u63a5\u5f85\u5bbe\u5ba2\uff0c\u6bcf\u4e2a\u4eba\u7684\u52a8\u4f5c\u90fd\u5f88 \u77eb\u5065\u654f\u6377\uff0c\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u5f88\u4eae\uff0c\u7edd\u4e0d\u4f1a\u9519\u8fc7\u4efb\u4f55\u4e00\u4ef6\u4e0d\u8be5\u53d1\u751f\u7684\u5c0f\u4e8b\u3002 \u3000\u3000\u4eba\u58f0\u5ffd\u7136\u5b89\u9759\u4e0b\u6765\u3002 \u3000\u3000\u603b\u7ba1\u5317\u9053\u4e09\u5341\u4e5d\u8def\u5927\u9556\u5c40\u7684\u5927\u9f99\u5934\u3001\u5f53\u4eca\u6b66\u6797\u4e2d\u7684\u7b2c\u4e00\u5f3a\u4eba\u3001\u6c38\u8fdc\u4e0d\u8d25\u7684\u53f8\u9a6c\u8d85\u7fa4\u7ec8\u4e8e \u51fa\u73b0\u4e86\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u51fa\u73b0\u7684\u65f6\u5019\uff0c\u7a7f\u4e00\u8eab\u4ee5\u9ed1\u767d\u4e24\u8272\u4e3a\u4e3b\u3001\u7ecf\u8fc7\u7279\u522b\u8bbe\u8ba1\u548c\u7cbe\u5fc3\u526a\u88c1\u7684\u8863\u88f3\uff0c\u4f7f\u5f97 \u4ed6\u7684\u8eab\u6750\u770b\u6765\u66f4\u5a01\u6b66\u9ad8\u5927\uff0c\u4e5f\u4f7f\u5f97\u4ed6\u5e74\u7eaa\u770b\u6765\u6bd4\u4ed6\u7684\u5b9e\u9645\u5e74\u9f84\u8fd8\u8981\u8f7b\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u7528\u660e\u6717\u8bda\u6073\u7684\u6001\u5ea6\u62db\u547c\u5bbe\u5ba2\uff0c\u8fd8\u7279\u5730\u8d70\u5230\u5385\u524d\u7684\u77f3\u9636\u4e0a\uff0c\u5411\u9662\u5b50\u91cc\u7684\u4eba\u7fa4\u6325\u624b\u3002 \u3000\u3000\u5728\u9707\u8033\u7684\u6b22\u547c\u58f0\u4e2d\uff0c\u5c0f\u9ad8\u6ce8\u610f\u7684\u5e76\u4e0d\u662f\u53f8\u9a6c\u8d85\u7fa4\uff0c\u800c\u662f\u53e6\u5916\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u7684\u88c5\u675f\u5bb9\u8c8c\u90fd\u5f88\u5e73\u51e1\uff0c\u4f46\u662f\u773c\u775b\u91cc\u5374\u5145\u6ee1\u4e00\u79cd\u51b7\u9177\u800c\u53ef\u6015\u7684\u6740\u673a\u3002 \u3000\u3000\u4ed6\u4eec\u5e76\u6ca1\u6709\u7ad9\u5728\u4e00\u8d77\uff0c\u4e5f\u6ca1\u6709\u4e92\u76f8\u770b\u8fc7\u4e00\u773c\uff0c\u4f46\u662f\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u7684\u9644\u8fd1\u5404\u6709\u516b\u4e5d\u4e2a\u4eba\u5728\u5077 \u5077\u7684\u76ef\u7740\u4ed6\u4eec\uff0c\u4e00\u76f4\u90fd\u8ddf\u4ed6\u4eec\u4fdd\u6301\u7740\u4e00\u6bb5\u9002\u5f53\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u5c0f\u9ad8\u5fae\u7b11\u3002 \u3000\u3000\u4ed6\u770b\u5f97\u51fa\u8fd9\u4e24\u4e2a\u4eba\u662f\u4e3a\u4e86\u6768\u575a\u6765\u7684\uff0c\u90fd\u662f\u6731\u731b\u6d3e\u51fa\u6765\u7684\u4e00\u7ea7\u6740\u624b\u3002 \u3000\u3000\u4ed6\u4e5f\u770b\u5f97\u51fa\u53f8\u9a6c\u548c\u5353\u4e1c\u6765\u4e00\u5b9a\u4e5f\u628a\u4ed6\u5f53\u4f5c\u4ed6\u4eec\u4e00\u8def\u7684\u4eba\uff0c\u56e0\u4e3a\u4ed6\u65e9\u5df2\u53d1\u73b0\u4ed6\u8eab\u8fb9\u9644\u8fd1\u4e5f \u5b58\u4eba\u5728\u76ef\u7740\u4ed6\u3002\u751a\u81f3\u6bd4\u4ed6\u4eec\u76ef\u5728\u8eab\u8fb9\u7684\u4eba\u52a0\u8d77\u6765\u8fd8\u591a\u3002 \u3000\u3000\u5353\u4e1c\u6765\u65e0\u7591\u5df2\u7ecf\u628a\u4ed6\u5f53\u4f5c\u6700\u5371\u9669\u7684\u4eba\u7269\u3002 \u3000\u3000\u201c\u53ef\u662f\u5353\u4e1c\u6765\u8fd9\u6b21\u9519\u4e86\uff01\u201d\u5c0f\u9ad8\u5728\u5fc3\u91cc\u5fae\u7b11\uff1a\u201c\u4ed6\u6d3e\u4eba\u672a\u9489\u7740\u6211\uff0c\u5b9e\u5728\u662f\u6d6a\u8d39\u4e86\u4eba \u529b\u3002\u201d \u3000\u3000\u5927\u5385\u4e2d\u592e\u7684\u5927\u6848\u4e0a\uff0c\u4e24\u6839\u5de8\u5927\u7684\u7ea2\u70db\u5df2\u71c3\u8d77\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5df2\u7ecf\u5750\u5230\u6848\u524d\u4e00\u5f20\u94fa\u7740\u864e\u76ae\u7684\u7d2b\u6a80\u6728\u6905\u4e0a\u3002 \u3000\u3000\u6905\u524d\u5df2\u7ecf\u94fa\u8d77\u7ea2\u6be1\uff0c\u6446\u597d\u4e86\u7d2b\u7f0e\u62dc\u57ab\u3002 \u3000\u3000\u5927\u5178\u5df2\u5c06\u5f00\u59cb\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u773c\u4e2d\u5e26\u7740\u6740\u673a\u7684\u4eba\uff0c\u5df2\u7ecf\u5728\u6e10\u6e10\u5411\u524d\u79fb\u52a8\uff0c\u76ef\u7740\u4ed6\u4eec\u7684\u4eba\u5f53\u7136\u4e5f\u8ddf\u7740\u4ed6\u4eec\u79fb\u52a8\uff0c \u6bcf\u4e2a\u4eba\u7684\u624b\u90fd\u5df2\u4f38\u4eba\u6000\u91cc\u3002 \u3000\u3000\u6000\u91cc\u85cf\u7740\u7684\uff0c\u5f53\u7136\u662f\u81f4\u547d\u7684\u6b66\u5668\u3002 \u3000\u3000\u53ea\u8981\u8fd9\u4e24\u4e2a\u4eba\u4e00\u6709\u52a8\u4f5c\uff0c\u8fd9\u4e9b\u4eba\u7684\u624b\u90fd\u5fc5\u5c06\u5728\u5239\u90a3\u95f4\u628a\u4e00\u4ef6\u6b66\u5668\u4ece\u6000\u91cc\u4f38\u51fa\u6765\uff0c\u5728\u5239\u90a3 \u95f4\u628a\u4ed6\u4eec\u683c\u6740\u4e8e\u5927\u5385\u524d\u3002 \u3000\u3000\u5c0f\u9ad8\u786e\u4fe1\u8fd9\u4e24\u4e2a\u4eba\u7edd\u4e0d\u4f1a\u5f97\u624b\u7684\u3002 \u3000\u3000\u2014\u2014\u4e00\u5b9a\u8fd8\u6709\u7b2c\u4e09\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u624d\u662f\u6731\u731b\u6d3e\u6765\u523a\u6740\u6768\u575a\u7684\u4e3b\u529b\u3002 \u3000\u3000\u5c0f\u9ad8\u7684\u60f3\u6cd5\u5c45\u7136\u4e5f\u8ddf\u5353\u4e1c\u6765\u4e00\u6837\uff0c\u552f\u4e00\u4e0d\u540c\u7684\u662f\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e2a\u4eba\u5e76\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u4eba\u662f\u8c01\u5462\uff1f \u3000\u3000\u5c0f\u9ad8\u7684\u77b3\u5b54\u5ffd\u7136\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u770b\u89c1\u6709\u4e00\u4e2a\u7edd\u4e0d\u4f1a\u5f15\u8d77\u4efb\u4f55\u4eba\u6ce8\u610f\u7684\u4eba\uff0c\u5728\u4eba\u4e1b\u4e2d\u95ea\u8eab\u800c\u8fc7\u3002 \u3000\u3000\u5c0f\u9ad8\u6ce8\u610f\u5230\u8fd9\u4e2a\u4eba\uff0c\u53ea\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u63d0\u7740\u4e00\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u4e00\u53e3\u9648\u65e7\u5e73\u51e1\u3001\u7edd\u4e0b\u4f1a\u5f15\u8d77\u4efb\u4f55\u4eba\u6ce8\u610f\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u4ed6\u60f3\u770b\u8fd9\u4e2a\u4eba\u7684\u8138\uff0c\u53ef\u662f\u8fd9\u4e2a\u4eba\u4e00\u76f4\u6ca1\u6709\u6b63\u9762\u5bf9\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u60f3\u6324\u8fc7\u53bb\uff0c\u53ef\u662f\u4eba\u7fa4\u4e5f\u5728\u5f80\u524d\u6324\uff0c\u56e0\u4e3a\u8fd9\u6b21\u5927\u5178\u7684\u4e2d\u5fc3\u4eba\u7269\u5df2\u7ecf\u8d70\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u6768\u575a\u7684\u8138\u8272\u663e\u5f97\u6709\u70b9\u82cd\u81ea\u865a\u5f31\uff0c\u4f46\u662f\u8138\u4e0a\u4ecd\u7136\u5e26\u7740\u5fae\u7b11\u3002 \u3000\u3000\u4ed6\u662f\u88ab\u516d\u4e2a\u4eba\u56f4\u62e5\u7740\u8d70\u8fdb\u6765\u7684\u3002 \u3000\u3000\u5c0f\u9ad8\u4e0d\u8ba4\u5f97\u8fd9\u516d\u4e2a\u4eba\uff0c\u53ef\u662f\u53ea\u8981\u5728\u6c5f\u6e56\u4e2d\u7ecf\u5e38\u8d70\u52a8\u7684\u4eba\uff0c\u4e0d\u8ba4\u5f97\u4ed6\u4eec\u7684\u5c31\u5f88\u5c11\u4e86\uff0c\u5176\u4e2d \u975e\u4f46\u6709\u9556\u5c40\u4e1a\u4e2d\u6210\u540d\u5df2\u4e45\u7684\u9ad8\u624b\uff0c\u751a\u81f3\u8fde\u6614\u5e74\u6a2a\u884c\u5173\u6d1b\u9053\u4e0a\u7684\u5927\u76d7\u4e91\u6ee1\u5929\u8d6b\u7136\u4e5f\u5728\u5176\u4e2d\u3002 \u3000\u3000\u5728\u8fd9\u4e48\u6837\u516d\u4f4d\u9ad8\u624b\u7684\u4fdd\u62a4\u4e0b\uff0c\u8fd8\u6709\u8c01\u80fd\u4f24\u6768\u575a\u7684\u6beb\u53d1\uff1f \u3000\u3000\u6768\u575a\u5df2\u7ecf\u8d70\u4e0a\u4e86\u7ea2\u6be1\uff0c\u8d70\u5230\u90a3\u4e2a\u7279\u5730\u9009\u6765\u4e3a\u4ed6\u62dc\u5e08\u7528\u7684\u7f0e\u57ab\u524d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u9662\u5b50\u91cc\u5df2\u7ecf\u6709\u4e86\u884c\u52a8\uff01\u5df2\u7ecf\u6709\u4e8c\u5341\u591a\u4e2a\u4eba\u5012\u4e86\u4e0b\u53bb\uff0c\u6d41\u7740\u8840\uff0c\u60e8\u547c\u7740 \u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u4eba\u4e1b\u4e2d\u6323\u624e\u547c\u558a\u3002 \u3000\u3000\u5012\u4e0b\u53bb\u7684\u4eba\uff0c\u5e76\u4e0d\u5b8c\u5168\u662f\u5353\u4e1c\u6765\u7684\u5c5e\u4e0b\uff0c\u5927\u591a\u6570\u90fd\u662f\u65e0\u8f9c\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u662f\u97e9\u7ae0\u548c\u6728\u9e21\u5546\u8bae\u597d\u4e86\u7684\u8ba1\u5212\u3002 \u3000\u3000\u4ed6\u4eec\u5f53\u7136\u4e5f\u77e5\u9053\u6709\u4eba\u5728\u76ef\u7740\u4ed6\u4eec\uff0c\u6240\u4ee5\u4ed6\u4eec\u5728\u51fa\u624b\u524d\uff0c\u4e00\u5b9a\u8981\u5148\u9020\u6210\u6df7\u4e71\uff0c\u7528\u65e0\u8f9c\u8005\u7684 \u9c9c\u8840\u6765\u9020\u6210\u6df7\u4e71\u3002 \u3000\u3000\u6df7\u4e71\u4e2d\uff0c\u4ed6\u4eec\u7684\u8eab\u5b50\u5df2\u98de\u6251\u800c\u8d77\uff0c\u6251\u5411\u6768\u575a\u3002 \u3000\u3000\u5c0f\u9ad8\u8fde\u770b\u90fd\u6ca1\u6709\u53bb\u770b\u4ed6\u4eec\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u4ed6\u4eec\u4e0d\u7ba1\u7528\u4ec0\u4e48\u65b9\u6cd5\u90fd\u4e0d\u4f1a\u5f97\u624b\u7684\uff0c\u4ed6\u6ce8\u610f\u7684\u662f\u4e2a\u63d0\u7740\u7bb1\u5b50\u7684\u4eba\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8fd8\u662f\u7aef\u5750\u5728\u7d2b\u64c5\u672c\u6905\u4e0a\uff0c\u58f0\u8272\u4e0d\u52a8\uff0c\u795e\u60c5\u4e5f\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u884c\u523a\u7684\u6740\u624b\u5df2\u7ecf\u88ab\u9694\u79bb\u5728\u5927\u5385\u524d\u3002 \u3000\u3000\u6768\u575a\u5df2\u7ecf\u5728\u516d\u4f4d\u9ad8\u5b50\u7684\u4fdd\u62a4\u4e0b\uff0c\u8d70\u51fa\u4e86\u5927\u5385\u540e\u9762\u7684\u4e00\u6247\u95e8\u3002 \u3000\u3000\u5c0f\u9ad8\u65e9\u5df2\u770b\u51c6\u8fd9\u6247\u95e8\u7684\u65b9\u5411\u3002 \u3000\u3000\u4e00\u76f4\u5728\u76ef\u7740\u4ed6\u7684\u90a3\u4e9b\u4eba\uff0c\u6ce8\u610f\u529b\u5df2\u7136\u5206\u6563\uff0c\u5c0f\u9ad8\u5ffd\u7136\u95ea\u8eab\u7a9c\u5165\u5927\u5385\uff0c\u7528\u4e00\u79cd\u6ca1\u6709\u4eba\u80fd\u5f62 \u5bb9\u7684\u5947\u7279\u8eab\u6cd5\uff0c\u6cbf\u7740\u5899\u58c1\u6ed1\u8fc7\u53bb\uff0c\u6ed1\u51fa\u4e86\u4e00\u6247\u7a97\u6237\u3002 \u3000\u3000\u8fd9\u6247\u7a97\u6237\u548c\u90a3\u9053\u95e8\u5f53\u7136\u662f\u540c\u4e00\u65b9\u5411\u7684\u3002 \u516d \u3000\u3000\u7a97\u5916\u7684\u540e\u9662\u91cc\u5145\u6ee1\u4e86\u6885\u9999\u548c\u677e\u9999\uff0c\u6df7\u5408\u6210\u4e00\u79cd\u975e\u5e38\u4ee4\u4eba\u6109\u5feb\u7684\u9999\u6c14\uff0c\u9634\u68ee\u7684\u957f\u5eca\u4e2d\uff0c\u5bc6 \u5e03\u7740\u8170\u60ac\u957f\u5200\u7684\u9752\u8863\u8b66\u536b\u3002 \u3000\u3000\u957f\u5eca\u7684\u5c3d\u5934\uff0c\u4e5f\u6709\u4e00\u6247\u95e8\u3002 \u3000\u3000\u5c0f\u9ad8\u63a0\u51fa\u7a97\u5916\u7684\u65f6\u5019\uff0c\u6b63\u597d\u770b\u5230\u4e91\u6ee1\u5929\u4ed6\u4eec\u62e5\u7740\u6768\u575a\u95ea\u5165\u4e86\u8fd9\u6247\u95e8\u3002 \u3000\u3000\u95e8\u7acb\u523b\u88ab\u5173\u4e0a\u3002 \u3000\u3000\u9752\u8863\u8b66\u536b\u4eec\u8170\u4e0a\u7684\u957f\u5200\u5df2\u51fa\u9798\uff0c\u5200\u5149\u95ea\u52a8\u95f4\uff0c\u5df2\u6709\u5341\u4e8c\u4e2a\u4eba\u5411\u5c0f\u9ad8\u6251\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u6ca1\u6709\u95ee\u5c0f\u9ad8\u662f\u8c01\uff0c\u4e5f\u6ca1\u6709\u95ee\u4ed6\u6765\u5e72\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u4eec\u63a5\u5230\u7684\u547d\u4ee4\u662f\uff1a\u53ea\u8981\u6709\u964c\u751f\u4eba\u8fdb\u5165\u8fd9\u4e2a\u9662\u5b50\uff0c\u7acb\u523b\u683c\u6740\u52ff\u8bba\uff01 \u3000\u3000\u5c0f\u9ad8\u4e5f\u6ca1\u6709\u89e3\u91ca\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5230\u8fd9\u91cc\u6765\uff0c\u73b0\u5728\u7684\u60c5\u51b5\uff0c\u5df2\u7ecf\u5230\u4e86\u6ca1\u6709\u4efb\u4f55\u8a00\u8bed\u80fd\u591f\u89e3\u91ca\u7684 \u65f6\u5019\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u552f\u4e00\u80fd\u505a\u7684\u4e8b\uff0c\u5c31\u662f\u5148\u51fb\u5012\u8fd9\u4e9b\u4eba\u3002\u7528\u6700\u5feb\u7684\u65b9\u6cd5\u51fb\u5012\u8fd9\u4e9b\u4eba\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u5c3d\u5feb\u51b2\u5165\u957f\u5eca\u5c3d\u5934\u90a3\u95f4\u5c4b\u5b50\u3002 \u3000\u3000\u5200\u5149\u5df2\u5339\u7ec3\u822c\u98de\u6765\uff0c\u5c0f\u9ad8\u7684\u5251\u4ecd\u5728\u7c97\u5e03\u5305\u88b1\u91cc\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u62d4\u51fa\u4ed6\u7684\u5251\uff0c\u5c31\u7528\u8fd9\u4e2a\u7c97\u5e03\u5305\u88b1\uff0c\u4ed6\u5df2\u51fb\u98de\u4e86\u4e09\u628a\u5200\uff0c\u51fb\u5012\u4e86\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u5728\u4ed6\u51b2\u4eba\u957f\u5eca\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u53c8\u6709\u4e03\u516b\u4e2a\u4eba\u88ab\u51fb\u5012\uff0c\u8fd9\u4e9b\u4eba\u5012\u4e0b\u65f6\uff0c\u4ed6\u5df2\u51b2\u5230\u90a3\u6247\u95e8\u5916 \u9762\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5df2\u7ecf\u5728\u95e8\u5916\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u662f\u4e2a\u9690\u85cf\u5728\u5e55\u540e\u7684\u4eba\uff0c\u53ef\u662f\u53ea\u8981\u4e00\u65e6\u6709\u975e\u5e38\u7684\u53d8\u5316\u53d1\u751f\uff0c\u4ed6\u7acb\u523b\u5c31\u4f1a\u53ca\u65f6\u51fa\u73b0\u3002 \u3000\u3000\u5c0f\u9ad8\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u957f\u53cd\u53f9\u606f\uff1a\u201c\u672c\u672a\u4e5f\u8bb8\u8fd8\u6765\u5f97\u53ca\u7684\uff0c\u53ef\u60dc\u73b0\u5728\u4e00\u5b9a\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u540e\u9762\u7684\u5200\u5149\u53c8\u5288\u6765\uff0c\u5c0f\u9ad8\u6ca1\u6709\u56de\u5934\uff0c\u5353\u4e1c\u6765\u5374\u6325\u4e86\u6325\u624b\uff0c\u51cc\u7a7a\u5288\u4e0b\u7684\u5200\u5149\u7acb\u523b\u505c\u987f\u3002 \u3000\u3000\u201c\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u201d\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u95ee\uff1a\u201c\u4f60\u8981\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u6765\u770b\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u770b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6740\u4eba\u7684\u4eba\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u7b11\uff1a\u201c\u6ca1\u6709\u4eba\u80fd\u5728\u8fd9\u91cc\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u6709\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6709\u4e00\u4e2a\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u8138\u8272\u5ffd\u7136\u6539\u53d8\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u55c5\u5230\u4e00\u822c\u6de1\u6de1\u7684\u8840\u8165\u6c14\u3002 \u3000\u3000\u8840\u8165\u6c14\u7adf\u8d6b\u7136\u771f\u7684\u662f\u4ece\u95e8\u540e\u4f20\u6765\u7684\u3002 \u3000\u3000\u5353\u4e1c\u6765\u56de\u8eab\u649e\u5f00\u4e86\u8fd9\u6247\u95e8\uff0e\u5c31\u5728\u4ed6\u56de\u8eab\u649e\u5f00\u95e8\u7684\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u7684\u4eba\u4eff\u4f5b\u5df2\u843d\u5165\u4e86\u5730\u72f1\u3002 \u3000\u3000\u95e8\u540e\u672c\u6765\u662f\u4e00\u95f4\u6781\u4e3a\u7cbe\u81f4\u534e\u7f8e\u7684\u5c4b\u5b50\uff0c\u53ef\u662f\u73b0\u5728\u5df2\u53d8\u6210\u4e86\u5730\u72f1\u3002 \u4e03 \u3000\u3000\u5730\u72f1\u91cc\u6c38\u8fdc\u6ca1\u6709\u6d3b\u4eba\u7684\uff0c\u8fd9\u5c4b\u5b50\u91cc\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u521a\u624d\u8fd8\u6d3b\u751f\u751f\u8d70\u8fdb\u6765\u7684\u4e03\u4e2a\u4eba\uff0c\u73b0\u5728\u90fd\u5df2\u7ecf\u6c38\u8fdc\u4e0d\u80fd\u6d3b\u7740\u8d70\u51fa\u53bb\u3002\u6709\u7684\u4eba\u54bd\u5589\u5df2\u88ab\u5272 \u65ad\uff0c\u6709\u7684\u4eba\u5fc3\u810f\u5df2\u88ab\u523a\u7a7f\uff0c\u4ece\u524d\u80f8\u523a\u5165\uff0c\u540e\u80cc\u7a7f\u51fa\u3002 \u3000\u3000\u6700\u60e8\u7684\u662f\u6768\u575a\u3002 \u3000\u3000\u6768\u575a\u7684\u5934\u9885\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff0c\u8eab\u8fb9\u591a\u4e86\u5f20\u62dc\u5e16\uff0c\u4e0a\u9762\u6709\u516b\u4e2a\u5b57\uff1a\u201c\u8fd9\u5c31\u662f\u53db\u5f92\u7684\u4e0b\u573a\uff01\u201d \u3000\u3000\u5c4b\u5b50\u91cc\u6709\u56db\u6247\u7a97\u6237\uff0c\u7a97\u6237\u90fd\u662f\u5173\u7740\u7684\u3002 \u3000\u3000\u6740\u4eba\u7684\u4eba\u5462\uff1f \u3000\u3000\u63a8\u5f00\u7a97\u6237\uff0c\u7a97\u5916\u661f\u6708\u5728\u5929\uff0c\u8fdc\u5904\u9523\u9f13\u58f0\u6684\uff0c\u4eca\u591c\u672c\u6765\u5c31\u662f\u91d1\u543e\u4e0d\u7981\u7684\u4e0a\u5143\u591c\u3002 \u3000\u3000\u5353\u4e1c\u6765\u8fce\u7740\u6251\u9762\u7684\u5bd2\u98ce\uff0c\u9ed8\u7acb\u4e86\u5f88\u4e45\uff0c\u5c45\u7136\u6ca1\u6709\u6d3e\u4eba\u53bb\u8ffd\u7d22\u51f6\u5b50\uff0c\u5374\u8f6c\u8fc7\u8eab\uff0c\u76ef\u7740\u5c0f \u9ad8\u3002 \u3000\u3000\u201c\u4f60\u77e5\u9053\u6709\u4eba\u8981\u5230\u8fd9\u91cc\u6765\u6740\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f46\u6211\u77e5\u9053\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u3002\u201d\u5c0f\u9ad8\u53f9\u606f\uff1a\u201c\u6211\u65e9\u5c31\u60f3\u89c1\u8fd9\u4e2a\u4eba\u4e00\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u6740\u4eba\u7684\u7edd\u4e0d\u6b62\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5272\u65ad\u54bd\u5589\u7528\u7684\u662f\u4e00\u628a\u950b\u5203\u6781\u8584\u7684\u7094\u5200\uff0c\u523a\u7a7f\u5fc3\u810f\u7528\u7684\u662f\u4e00\u67c4\u950b\u5c16\u6781\u5229\u7684\u67aa\u4e88\u3002 \u3000\u3000\u6768\u575a\u7684\u5934\u9885\u5374\u50cf\u662f\u88ab\u4e00\u628a\u65a7\u5934\u780d\u4e0b\u6765\u7684\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7684\u6001\u5ea6\u5df2\u7ecf\u51b7\u9759\u4e86\u4e0b\u6765\uff0c\u9547\u5b9a\u800c\u51b7\u9759\u3002 \u3000\u3000\u201c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u6765\u7684\u81f3\u5c11\u6709\u4e09\u4e2a\u4eba\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6ca1\u6709\u4eba\u80fd\u540c\u65f6\u4f7f\u7528\u8fd9\u4e09\u79cd\u5f62\u72b6\u4efd\u91cf\u62db\u5f0f \u90fd\u5b8c\u5168\u4e0d\u540c\u7684\u6b66\u5668\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u6709\u3002\u201d\u5c0f\u9ad8\u7684\u56de\u7b54\u5145\u6ee1\u81ea\u4fe1\uff1a\u201c\u6709\u4e00\u4e2a\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u4e16\u4e0a\u771f\u6709\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u80fd\u540c\u65f6\u4f7f\u7528\u8fd9\u4e09\u79cd\u6b66\u5668\u5728\u4e00\u77ac\u95f4\u523a\u6740\u4e03\u4f4d\u9ad8\u624b\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u5c0f\u9ad8\u8bf4\u5f97\u6781\u6709\u628a\u63e1\uff1a\u201c\u4e5f\u8bb8\u4e16\u4e0a\u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u8fd9\u4e48\u6837\u7684\u4eba\uff0c\u53ef\u662f\u7edd\u5bf9\u6709\u4e00 \u4e2a\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53c8\u5728\u53f9\u606f\uff1a\u201c\u5982\u679c\u4f60\u521a\u624d\u6ca1\u6709\u6321\u4f4f\u6211\uff0c\u4e5f\u8bb8\u6211\u5c31\u80fd\u770b\u89c1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u76ef\u7740\u4ed6\uff0c\u5df2\u7ecf\u53ef\u4ee5\u611f\u89c9\u5230\u81ea\u5df1\u638c\u5fc3\u5206\u6ccc\u51fa\u7684\u51b7\u6c57\u3002 \u3000\u3000\u201c\u4f46\u662f\u6211\u672c\u6765\u5e76\u4e0d\u77e5\u9053\u4ed6\u5df2\u7ecf\u5230\u4e86\u957f\u5b89\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u4e3a\u6731\u731b\u6740\u4eba\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u770b\u4ed6\u7684\u773c\u795e\uff0c\u770b\u4ed6\u7684\u6001\u5ea6\uff0c\u770b\u4ed6\u7ad9\u7acb\u7684\u65b9\u5f0f\uff0c\u770b\u4ed6\u624b\u91cc\u90a3\u67c4 \u7528\u7c97\u5e03\u5305\u7740\u7684\u5251\uff0c\u5ffd\u7136\u8bf4\uff0c\u201c\u6211\u76f8\u4fe1\u4f60\uff0c\u5982\u679c\u4f60\u8981\u8d70\u3002\u73b0\u5728\u5c31\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u53e5\u8bdd\u7684\u4eba\u90fd\u5f88\u60ca\u8bb6\uff0c\u56e0\u4e3a\u8fd9\u7edd\u5bf9\u4e0d\u662f\u5353\u4e1c\u6765\u5e73\u65e5\u7684\u4f5c\u98ce\uff0c\u4ed6\u4ece\u672a\u5982\u6b64\u8f7b\u6613\u653e\u8fc7\u4e00 \u4e2a\u4eba\u3002 \u3000\u3000\u53ea\u6709\u5353\u4e1c\u6765\u81ea\u5df1\u77e5\u9053\u4e3a\u4ec0\u4e48\u8fd9\u6837\u505a\u3002\u4ed6\u5df2\u770b\u51fa\u5c0f\u9ad8\u4e5f\u662f\u4e2a\u975e\u5e38\u5371\u9669\u7684\u4eba\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5 \u4e0b\uff0c\u4ed6\u4e0d\u60f3\u518d\u60f9\u9ebb\u70e6\u3002 \u3000\u3000\u5c0f\u9ad8\u5374\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u6211\u4e5f\u77e5\u9053\u6211\u8981\u8d70\u7684\u65f6\u5019\u968f\u65f6\u90fd\u53ef\u4ee5\u8d70\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u53ef\u60dc\u6211\u8fd8\u4e0d\u60f3\u8d70\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8fd8\u6709\u4ef6\u4e8b\u6ca1\u6709\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u59d3\u5b63\uff0c\u4e5f\u4e0d\u53eb\u674e\u8f89\u6210\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u4e3a\u6768\u575a\u800c\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u77e5\u9053\uff0c\u6240\u4ee5\u624d\u8ba9\u4f60\u8d70\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\u8fd8\u6709\u5f88\u591a\u4e8b\u4f60\u90fd\u4e0d\u77e5\u9053\u3002\u201d\u5c0f\u9ad8\u5fae\u7b11\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u8fd8\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\u6211\u8fd8\u4e0d\u80fd \u8d70\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u624b\u638c\u63e1\u7d27\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u8fd9\u4e2a\u5c11\u5e74\u6709\u4e00\u79cd\u522b\u4eba\u5f88\u96be\u5bdf\u89c9\u5230\u7684\u91ce\u6027\uff0c\u5c31\u50cf\u662f\u4e00\u53ea\u521a\u4ece\u6df1\u5c71\u4e2d\u5ba1\u51fa\u6765\u7684\u91ce \u517d\uff0c\u5bf9\u4efb\u4f55\u4eba\u4efb\u4f55\u4e8b\u90fd\u6beb\u65e0\u6240\u60e7\u3002 \u3000\u3000\u201c\u6211\u59d3\u9ad8\uff0c\u6211\u662f\u4e3a\u4e00\u4e2a\u4eba\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4e86\u8c01\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u53f8\u9a6c\u8d85\u7fa4\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6c38\u8fdc\u4e0d\u8d25\u7684\u53f8\u9a6c\u8d85\u7fa4\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u63e1\u7d27\u7684\u624b\u638c\u4e2d\uff0c\u5ffd\u7136\u53c8\u6709\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u201c\u4f60\u5c31\u662f\u9ad8\u6e10\u98de\uff1f\u201d\u4ed6\u95ee\u5c0f\u9ad8\uff1a\u201c\u5c31\u662f\u90a3\u4f4d\u5728\u4e09\u4e2a\u6708\u91cc\u523a\u6740\u4e86\u6606\u4ed1\u534e\u5c71\u5d06\u5cd2\u4e09\u5927\u5251\u6d3e\u95e8 \u4e0b\u56db\u5927\u9ad8\u624b\u7684\u5c11\u5e74\u5251\u5ba2\u9ad8\u6e10\u98de\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u5c31\u662f\u3002\u201d \u3000\u3000\u591c\u66f4\u6697\uff0c\u98ce\u66f4\u7d27\u3002 \u3000\u3000\u201c\u6211\u4ece\u4e0d\u5728\u6697\u4e2d\u6740\u4eba\uff01\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u8981\u4f60\u4eec\u9009\u4e00\u4e2a\u65f6\u5019\uff0c\u9009\u4e00\u4e2a\u5730\u65b9\uff0c\u8ba9\u6211\u770b\u770b \u53f8\u9a6c\u8d85\u7fa4\u662f\u4e0d\u662f\u771f\u7684\u6c38\u8fdc\u4e0d\u8d25\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u7b11\u4e86\uff1a\u201c\u6211\u4fdd\u8bc1\u4ed6\u4e00\u5b9a\u4f1a\u8ba9\u4f60\u77e5\u9053\u7684\uff0c\u53ea\u4e0d\u8fc7\u6211\u5e0c\u671b\u4f60\u8fd8\u662f\u6c38\u8fdc\u4e0d\u8981\u77e5\u9053\u7684 \u597d\u3002\u201d \u516b \u3000\u3000\u957f\u8857\u4e0a\u91d1\u543e\u4e0d\u7981\uff0c\u82b1\u5e02\u82b1\u706f\u706f\u5982\u753b\u3002 \u3000\u3000\u5404\u5f0f\u5404\u6837\u7684\u82b1\u706f\uff0c\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u5c0f\u9ad8\u90e8\u597d\u5065\u5168\u90fd\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5df2\u7ecf\u7b54\u5e94\u4ed6\uff0c\u5728\u4e00\u4e2a\u6708\u5185\u5c31\u4f1a\u7ed9\u4ed6\u7b54\u8986\uff0c\u5e76\u4e14\u4fdd\u8bc1\u8ba9\u4ed6\u548c\u53f8\u9a6c\u8d85\u7fa4\u4f5c\u4e00\u6b21\u516c\u5e73\u7684 \u51b3\u6597\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5c31\u662f\u4e3a\u6b64\u800c\u6765\u7684\uff0c\u53ef\u662f\u73b0\u5728\u597d\u50cf\u4e5f\u4e0d\u592a\u5173\u5fc3\u8fd9\u4ef6\u4e8b\u4e86\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5fc3\u91cc\u60f3\u5230\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u4e00\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u8fd9\u53e3\u7bb1\u5b50\u7a76\u7adf\u662f\u79cd\u591a\u4e48\u53ef\u6015\u7684\u6b66\u5668\uff1f \u4e5d \u3000\u3000\u8fd9\u65f6\u5019\u6b63\u6709\u4e00\u4e2a\u4eba\uff0c\u63d0\u7740\u4e00\u53e3\u7bb1\u5b50\uff0c\u5728\u6697\u591c\u51b7\u98ce\u4e2d\uff0c\u9ed8\u9ed8\u7684\u8d70\u51fa\u4e86\u957f\u5b89\u53e4\u57ce\u3002 \u7b2c\u4e8c\u7ae0\u3000\u5927\u597d\u5934\u9885>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u4e8c\u7ae0\u3000\u5927\u597d\u5934\u9885 \u4e00 \u3000\u3000\u6b63\u6708\u5341\u516d\u3002 \u3000\u3000\u7ea2\u82b1\u96c6\u3002 \u3000\u3000\u98ce\u96ea\u6ee1\u5929\u3002 \u3000\u3000\u4e00\u9a91\u5feb\u9a6c\u5192\u7740\u98ce\u96ea\u51b2\u4eba\u4e86\u957f\u5b89\u57ce\u897f\u5357\u4e00\u767e\u516d\u5341\u91cc\u5916\u7684\u7ea2\u82b1\u96c6\u3002 \u3000\u3000\u5143\u5bb5\u591c\u5df2\u7ecf\u8fc7\u4e86\uff0c\u6b22\u4e50\u7684\u65e5\u5b50\u5df2\u7ed3\u675f\u3002 \u3000\u3000\u4e00\u76cf\u6b8b\u7834\u7684\u82b1\u706f\uff0c\u5728\u5bd2\u98ce\u4e2d\u6eda\u5728\u79ef\u96ea\u7684\u8857\u9053\uff0c\u6eda\u5165\u65e0\u8fb9\u65e0\u9645\u7684\u98ce\u96ea\u91cc\uff0c\u867d\u7136\u8fd8\u5e26\u7740\u548b\u591c \u7684\u6b8b\u5986\uff0c\u5374\u5df2\u518d\u4e5f\u6ca1\u6709\u4eba\u4f1a\u53bb\u770b\u5b83\u4e00\u773c\u4e86\uff0c\u5c31\u50cf\u662f\u4e2a\u53ea\u5f97\u5ba0\u4e86\u4e00\u591c\u5c31\u5f7c\u629b\u5f03\u7684\u5973\u4eba\u4e00\u6837\u3002 \u3000\u3000\u9a6c\u4e0a\u9a91\u58eb\u5728\u5e02\u96c6\u5916\u5c31\u505c\u4e0b\uff0c\u628a\u9a6c\u5339\u7cfb\u5728\u4e00\u68f5\u67af\u6811\u4e0a\uff0c\u8131\u4e0b\u4e86\u8eab\u4e0a\u4e00\u4ef6\u8d28\u6599\u5f88\u597d\u3001\u4ef7\u503c\u6602 \u8d35\u7684\u9632\u98ce\u6597\u7bf7\uff0c\u9732\u51fa\u4e86\u91cc\u9762\u4e00\u8eab\u84dd\u5e03\u68c9\u8884\uff0c\u4ece\u9a6c\u978d\u65c1\u7684\u4e00\u4e2a\u9ebb\u5e03\u888b\u4e8e\u91cc\uff0c\u62ff\u51fa\u4e86\u4e00\u67c4\u6cb9\u7eb8 \u4f1e\uff0c\u4e00\u53cc\u9489\u978b\u3002 \u3000\u3000\u4ed6\u7a7f\u4e0a\u9489\u978b\uff0c\u6491\u8d77\u6cb9\u7eb8\u4f1e\uff0c\u89e3\u4e0b\u90a3\u4e2a\u9ebb\u5e03\u888b\u63d0\u5728\u624b\u91cc\uff0c\u770b\u8d77\u6765\u5c31\u548c\u522b\u7684\u4e61\u4e0b\u4eba\u5b8c\u5168\u6ca1\u4ec0 \u4e48\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u7136\u540e\u4ed6\u624d\u6df1\u4e00\u811a\u3001\u6d45\u4e00\u811a\u7684\u8e0f\u7740\u96ea\u8d70\u5165\u7ea2\u82b1\u96c6\u3002 \u3000\u3000\u4ed6\u7684\u9ebb\u888b\u91cc\u88c5\u7740\u7684\u662f\u4e00\u4e2a\u8db3\u4ee5\u9707\u52a8\u5929\u4e0b\u7684\u5927\u79d8\u5bc6\uff0c\u4ed6\u7684\u5fc3\u91cc\u4e5f\u85cf\u7740\u4e00\u4e2a\u8db3\u4ee5\u9707\u52a8\u5929\u4e0b\u7684 \u5927\u79d8\u5bc6\uff0c\u5929\u4e0b\u53ea\u6709\u4ed6\u4e00\u4e2a\u4eba\u77e5\u9053\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u4ed6\u5230\u8fd9\u91cc\u6765\uff0c\u53ea\u56e0\u4e3a\u4ed6\u8981\u5373\u65f6\u5c06\u9ebb\u888b\u91cc\u7684\u4e1c\u897f\u9001\u5230\u7ea2\u82b1\u96c6\u4e0a\u7684\u4e00\u5bb6\u5993\u9662\u53bb\uff0c\u4ea4\u7ed9\u4e00\u4e2a \u4eba\u3002 \u3000\u3000\u2014\u2014\u4ed6\u8fd9\u9ebb\u888b\u91cc\u88c5\u7740\u7684\u662f\u4ec0\u4e48\uff1f\u8981\u53bb\u4ea4\u7ed9\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u5982\u679c\u6709\u4eba\u77e5\u9053\u8fd9\u79d8\u5bc6\uff0c\u4e0d\u51fa\u7247\u523b\u4ed6\u8fd9\u4e2a\u4eba\u5c31\u4f1a\u88ab\u56de\u88ab\u4e71\u5200\u5206\u5c38\uff0c\u4ed6\u7684\u7236\u6bcd\u59bb\u5b50\u513f\u5973\u4eb2 \u621a\uff0c\u4e5f\u5fc5\u5c06\u5728\u4e09\u65e5\u5185\u60e8\u6b7b\u4e8e\u4e71\u5200\u4e0b\uff0c\u6b7b\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u79d8\u5bc6\u662f\u6c38\u8fdc\u4e0d\u4f1a\u6cc4\u9732\u7684\u3002\u4ed6\u81ea\u5df1\u7edd\u4e0d\u4f1a\u6cc4\u9732\uff0c\u522b\u4eba\u4e5f\u7edd\u5bf9\u67e5\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u8c01\u4e5f\u60f3\u4e0d\u5230\u201c\u96c4\u72ee\u201d\u6731\u731b\u7adf\u4f1a\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u8f7b\u9a91\u8fdc\u79bb\u4ed6\u8b66\u536b\u68ee\u4e25\u7684\u6d1b\u9633\u603b\u8235\uff0c\u5355\u4eba \u5339\u9a6c\u95ef\u5165\u53f8\u9a6c\u8d85\u7fa4\u7684\u5730\u76d8\u3002            \u4e8c \u3000\u3000\u5c31\u8fde\u7b97\u65e0\u9057\u7b56\u7684\u5353\u4e1c\u6765\u4e5f\u60f3\u4e0d\u5230\u4ed6\u6562\u5192\u8fd9\u79cd\u9669\u3002 \u3000\u3000\u6df3\u6734\u7684\u5c0f\u9547\uff0c\u7b80\u964b\u7684\u5993\u9662\u3002 \u3000\u3000\u6731\u731b\u8d64\u7740\u818a\uff0c\u7a7f\u7740\u4e00\u6761\u728a\u9f3b\u88e4\uff0c\u7b95\u8e1e\u5728\u4e00\u5f20\u5927\u7095\u4e0a\uff0c\u7528\u4e00\u53ea\u5927\u6d77\u7897\u548c\u8fd9\u91cc\u9152\u91cf\u6700\u597d\u7684\u4e03 \u516b\u4e2a\u59d1\u5a18\u62fc\u9152\uff0c\u53ea\u8981\u6709\u4eba\u559d\u4e00\u7897\uff0c\u4ed6\u5c31\u559d\u4e00\u7897\u3002 \u3000\u3000\u4ed6\u559d\u7684\u662f\u6c7e\u9152\uff0c\u5df2\u7ecf\u8fde\u559d\u4e86\u56db\u5341\u4e09\u5927\u7897\uff0c\u8fd8\u662f\u9762\u4e0d\u6539\u8272\u3002 \u3000\u3000\u770b\u7684\u4eba\u90fd\u5413\u5446\u4e86\u3002 \u3000\u3000\u8fd9\u6761\u6ee1\u8138\u80e1\u5b50\u7684\u5927\u6c49\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u94c1\u6253\u7684\uff0c\u8fde\u80a0\u80c3\u90fd\u50cf\u662f\u94c1\u6253\u7684\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u7897\u8f6e\u5230\u8c01\u4e86\uff1f\u201d\u6731\u731b\u53c8\u6ee1\u6ee1\u5012\u4e86\u4e00\u7897\u9152\uff1a\u201c\u8c01\u6765\u8ddf\u6211\u62fc\uff1f\u201d \u3000\u3000\u8c01\u4e5f\u4e0d\u6562\u518d\u8ddf\u4ed6\u62fc\uff0c\u8fde\u4e00\u4e2a\u5916\u53f7\u53eb\u505a\u5927\u6d77\u7f38\u7684\u5c71\u4e1c\u5927\u599e\u90fd\u4e0d\u6562\u518d\u5f00\u53e3\u3002 \u3000\u3000\u559d\u9189\u7684\u5ba2\u4eba\u51fa\u624b\u603b\u662f\u6bd4\u8f83\u5927\u65b9\u4e9b\uff0c\u704c\u5ba2\u4eba\u7684\u9152\uff0c\u672c\u6765\u662f\u8fd9\u4e9b\u59d1\u5a18\u4eec\u7684\u62ff\u624b\u672c\u4e8b \u3000\u3000\u201c\u53ef\u662f\u8fd9\u4e2a\u4eba\u2026\u2026\u201d\u5927\u9152\u7f38\u540e\u6765\u5bf9\u522b\u4eba\u8bf4\uff1a\u201c\u4ed6\u7b80\u76f4\u4e0d\u662f\u4e2a\u4eba\uff0c\u662f\u4e2a\u9152\u6876\uff0c\u6ca1\u6709\u5e95\u7684\u9152 \u6876\u3002\u201d \u3000\u3000\u6731\u731b\u4ef0\u9762\u5927\u7b11\uff0c\u81ea\u5df1\u4e00\u53e3\u6c14\u53c8\u559d\u4e86\u4e09\u5927\u7897\uff0c\u5ffd\u7136\u7528\u529b\u5c06\u8fd9\u4e2a\u7c97\u74f7\u5927\u6d77\u7897\u5f80\u5730\u4e0a\u4e00\u6454\uff0c\u6454 \u5f97\u7c89\u788e\uff0c\u4e00\u53cc\u94dc\u94c3\u822c\u7684\u5927\u773c\u91cc\uff0c\u5ffd\u7136\u66b4\u5c04\u51fa\u5200\u950b\u822c\u7684\u5149\uff0c\u53ee\u7740\u521a\u8d70\u8fdb\u95e8\u5c31\u5df2\u7ecf\u88ab\u5413\u5f97\u4e24\u817f\u53d1 \u8f6f\u7684\u9f9f\u5974\u3002 \u3000\u3000\u201c\u5916\u9762\u662f\u4e0d\u662f\u6709\u4eba\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u6765\u627e\u6211\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u9f9f\u5974\u8bf4\u8bdd\u7684\u58f0\u97f3\u5df2\u7ecf\u5728\u53d1\u6296\uff1a\u201c\u662f\u4e2a\u540d\u5b57\u5f88\u602a\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u53eb\u505a\u9489\u978b\u3002\u201d \u3000\u3000\u6731\u731b\u7528\u529b\u4e00\u62cd\u5df4\u638c\uff0c\u201c\u597d\u5c0f\u5b50\uff0c\u603b\u7b97\u8d76\u6765\u4e86\uff0c\u5feb\u53eb\u4ed6\u7ed9\u6211\u6eda\u8fdb\u6765\u3002\u201d \u3000\u3000\u201c\u9489\u978b\u201d\u8131\u4e0b\u4e86\u811a\u4e0a\u7684\u9489\u978b\uff0c\u624d\u63d0\u7740\u9ebb\u5e03\u888b\u8d70\u8fdb\u8fd9\u4e2a\u5927\u7095\u5df2\u88ab\u9a6c\u7caa\u70e7\u5f97\u6e29\u6696\u5982\u6625\u7684\u4e0a \u623f\u3002 \u3000\u3000\u4ed6\u521a\u8d70\u8fdb\u95e8\uff0c\u624b\u91cc\u7684\u9ebb\u888b\u5c31\u88ab\u4eba\u4e00\u628a\u593a\u4e86\u8fc7\u53bb\uff0c\u9ebb\u888b\u4e00\u6296\uff0c\u5c31\u6709\u6837\u4e1c\u897f\u4ece\u91cc\u9762\u6eda\u51fa\u6765\uff0c \u9aa8\u788c\u788c\u7684\u6eda\u5728\u5927\u7095\u4e0a\uff0c\u8d6b\u7136\u7adf\u662f\u9897\u4eba\u5934\u3002 \u3000\u3000\u59d1\u5a18\u4eec\u5413\u60e8\u4e86\uff0c\u9f9f\u5974\u7684\u88e4\u6863\u5df2\u6e7f\u900f\u3002 \u3000\u3000\u6731\u731b\u5374\u53c8\u5927\u7b11\u3002 \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u603b\u7b97\u6ca1\u6709\u770b\u9519\u4f60\uff0c\u4f60\u8fd8\u771f\u80fd\u66ff\u4f60\u8001\u5b50\u529e\u70b9\u4e8b\uff0c\u56de\u53bb\u8d4f\u4f60\u4e24\u4e2a\u5c0f\u8001\u5a46\u3002\u201d \u3000\u3000\u4ed6\u7684\u7b11\u58f0\u5ffd\u53c8\u505c\u987f\uff0c\u76ef\u7740\u9489\u978b\u6c89\u58f0\u95ee\uff1a\u201c\u4ed6\u6709\u6ca1\u6709\u4ea4\u4ee3\u4f60\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u9489\u978b\u9053\uff1a\u201c\u6211\u53ea\u770b\u89c1\u4ed6\u624b\u91cc\u597d\u50cf\u63d0\u7740\u53e3\u7bb1\u5b50\uff0c\u8fde\u4ed6\u7684\u8138\u90fd\u6ca1\u6709\u770b\u6e05\u695a\u3002\u201d \u3000\u3000\u6731\u731b\u9510\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u5ffd\u7136\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u5634\u91cc\u5583\u5583\u7684\u8bf4\uff1a\u201c\u73b0\u5728\u4f60\u5df2 \u7ecf\u4e0d\u6b20\u6211\u4ec0\u4e48\u4e86\uff0c\u6211\u53ea\u5e0c\u671b\u4f60\u4ee5\u540e\u8fd8\u4f1a\u60f3\u5230\u6765\u770b\u770b\u6211\uff0c\u966a\u6211\u559d\u51e0\u676f\u9152\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u8bdd\u4ed6\u5f53\u7136\u4e0d\u662f\u5bf9\u9489\u978b\u8bf4\u7684\uff0c\u53f9\u6c14\u4e5f\u4e0d\u662f\u4ed6\u5e38\u6709\u7684\u4e60\u60ef\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u7acb\u523b\u53c8\u5927\u7b11\uff1a\u201c\u5353\u4e1c\u6765\uff0c\u5353\u4e1c\u6765\uff0c\u522b\u4eba\u90fd\u8bf4\u4f60\u4ed6\u5a18\u7684\u662f\u4e2a\u8bf8\u845b\u4eae\uff0c\u4f60\u6709\u6ca1\u6709\u60f3\u5230 \u8001\u5b50\u5df2\u7ecf\u5728\u4f60\u4eec\u7684\u72d7\u7a9d\u8fb9\u4e0a\u559d\u4e86\u4e00\u591c\u9152\uff1f\u201d \u3000\u3000\u201c\u5802\u4e3b\u505a\u4e8b\u4e00\u5411\u795e\u51fa\u9b3c\u6ca1\uff0c\u59d3\u5353\u7684\u600e\u4e48\u80fd\u6599\u5f97\u5230\uff1f\u201d\u9489\u978b\u5782\u7740\u624b\u8bf4\uff1a\u201c\u53ef\u662f\u4ed6\u4e00\u5b9a\u7b97\u51c6 \u4e86\u6211\u4eec\u8981\u628a\u6768\u575a\u7684\u4eba\u5934\u9001\u56de\u6d1b\u9633\u7684\u5fc5\u7ecf\u4e4b\u8def\uff0c\u6240\u4ee5\u4ed6\u4e00\u5b9a\u65e9\u5c31\u5728\u8fd9\u91cc\u4e0b\u4e86\u6869\u5e03\u4e86\u5361\u3002\u201d \u3000\u3000\u201c\u90a3\u6709\u4e2a\u5c41\u7528\uff1f\u201d\u6731\u731b\u77aa\u773c\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u60f3\u4e0d\u5230\u8001\u5b50\u5728\u8fd9\u91cc\uff0c\u4f1a\u4e0d\u4f1a\u628a\u4e3b\u529b\u90fd\u8c03\u5230\u8fd9\u91cc \u6765\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u201c\u4ed6\u8ddf\u53f8\u9a6c\u4f1a\u4e0d\u4f1a\u6765\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u6d3e\u6765\u7684\u4eba\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u662f\u4ed6\u8eab\u8fb9\u90a3\u4e24\u4e2a\u8fde\u80e1\u5b50\u90fd\u957f\u4e0d\u51fa\u7684\u5c0f\u5154\u5d3d\u5b50\u800c\u5df2\u3002\u201d\u6731 \u731b\u65ad\u7136\u9053\uff1a\u201c\u6211\u6599\u5b9a\u4ed6\u6d3e\u6765\u7684\u4e0d\u662f\u90ed\u5e84\uff0c\u5c31\u662f\u5b59\u901a\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u9489\u978b\u5782\u9996\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u7684\u3002\u201d \u3000\u3000\u4ed6\u5782\u4e0b\u5934\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u8ba9\u6731\u731b\u770b\u5230\u4ed6\u773c\u4e2d\u9732\u51fa\u7684\u754f\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u4e2a\u6ee1\u8138\u80e1\u5b50\u6ee1\u5634\u7c97\u8bdd\u770b\u8d77\u6765\u50cf\u662f\u4e2a\u5927\u8001\u7c97\u7684\u4eba\uff0c\u4e0d\u4f46\u8fdc\u6bd4\u522b\u4eba\u60f3\u8c61\u4e2d\u806a\u660e \u5f97\u591a\uff0c\u4e5f\u8fdc\u6bd4\u4efb\u4f55\u4eba\u60f3\u8c61\u4e2d\u53ef\u6015\u5f97\u591a\u3002 \u3000\u3000\u6731\u731b\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u91d1\u521a\u822c\u7ad9\u5728\u5927\u7095\u4e0a\uff0c\u5927\u58f0\u95ee\u90a3\u4e9b\u5df2\u88ab\u5413\u5f97\u8fde\u8def\u90fd\u8d70\u4e0d\u52a8\u7684\u59d1\u5a18\u548c\u9f9f \u5974\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u662f\u4e0d\u662f\u5df2\u7ecf\u77e5\u9053\u6211\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u6ca1\u6709\u4eba\u6562\u56de\u7b54\uff0c\u6ca1\u6709\u4eba\u6562\u5f00\u53e3\u3002 \u3000\u3000\u201c\u6211\u5c31\u6700\u6731\u5927\u592a\u7237\u3002\u201d\u6731\u731b\u7528\u5927\u62c7\u6307\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\u8bf4\uff1a\u201c\u5c31\u662f\u53f8\u9a6c\u8d85\u7fa4\u7684\u6b7b\u5bf9\u5934\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u51b2\u51fa\u53bb\uff0c\u4ece\u5916\u9762\u7684\u67dc\u53f0\u4e0a\u62ff\u4e86\u4e00\u5927\u7897\u58a8\u6c41\u4e00\u652f\u79c3\u7b14\u8fdb\u6765\uff0c\u7528\u79c3\u7b14\u8638\u9971\u6d53\u58a8\uff0c\u5728\u6700 \u8fd1\u521a\u7c89\u5237\u8fc7\u7684\u767d\u57a9\u5899\u4e0a\uff0c\u4e00\u53e3\u6c14\u5199\u4e0b\u4e86\u5341\u4e2a\u6bd4\u5934\u9885\u8fd8\u5927\u7684\u5b57\u3002 \u3000\u3000\u201c\u6d1b\u9633\u5927\u4fa0\u6731\u731b\u5230\u6b64\u4e00\u6e38\u3002\u201d \u3000\u3000\u767d\u7c89\u5899\u4e0a\u58a8\u6c41\u6dcb\u6f13\uff0c\u6731\u731b\u63b7\u7b14\u5927\u7b11\u3002 \u3000\u3000\u201c\u8001\u5b50\u5df2\u7ecf\u6765\u8fc7\uff0c\u73b0\u5728\u8981\u56de\u53bb\u4e86\u3002\u201d\u4ed6\u7528\u529b\u4e00\u62cd\u9489\u978b\u7684\u80a9\uff1a\u201c\u54b1\u4eec\u4e00\u8def\u6740\u56de\u53bb\uff0c\u770b\u8c01\u80fd \u6321\u5f97\u4f4f\u3002\u201d \u4e09 \u3000\u3000\u5b59\u901a\u5176\u5b9e\u4e0d\u5e94\u8be5\u53eb\u5b59\u901a\u7684\u3002 \u3000\u3000\u4ed6\u5e94\u8be5\u53eb\u5b59\u6321\u3002 \u3000\u3000\u56e0\u4e3a\u5353\u4e1c\u6765\u66fe\u7ecf\u5728\u5f88\u591a\u4eba\u9762\u524d\u79f0\u8d5e\u8fc7\u4ed6\uff1a\u201c\u5b59\u901a\u7684\u5e74\u7eaa\u867d\u7136\u4e0d\u5927\uff0c\u53ef\u662f\u65e0\u8bba\u4ec0\u4e48\u4eba\u6765 \u4e86\uff0c\u4ed6\u90fd\u53ef\u4ee5\u6321\u4e00\u6321\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\u53d1\u751f\uff0c\u4ed6\u4e5f\u53ef\u4ee5\u6321\u4e00\u6321\uff0c\u800c\u4e14\u4e00\u5b9a\u53ef\u4ee5\u6321\u5f97\u4f4f\u3002\u201d \u3000\u3000\u7ea2\u82b1\u96c6\u5916\u7684\u5b98\u9053\u65c1\uff0c\u6709\u5bb6\u8336\u9986\uff0c\u5982\u679c\u5750\u5728\u8336\u9986\u95e8\u53e3\u7684\u4f4d\u4e8e\u4e0a\uff0c\u5c31\u53ef\u4ee5\u628a\u5b98\u9053\u4e0a\u6765\u5f80\u7684\u6bcf \u4e00\u4e2a\u4eba\u90fd\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u5b59\u901a\u5c31\u5750\u5728\u8fd9\u4e2a\u4f4d\u5b50\u4e0a\u3002 \u3000\u3000\u9053\u8def\u4e24\u65c1\u7684\u5c4b\u6a90\u4e0b\uff0c\u53ea\u8981\u662f\u53ef\u4ee5\u6321\u5f97\u4f4f\u98ce\u96ea\u7684\u5730\u65b9\uff0c\u90fd\u7ad9\u7740\u4e00\u4e24\u4e2a\u9752\u8863\u4eba\uff0c\u8fd9\u4e9b\u4eba\u7684\u5e74 \u7eaa\u90fd\u6bd4\u4ed6\u5927\u5f97\u591a\uff0c\u5728\u9556\u5c40\u91cc\u7684\u5e74\u8d44\u4e5f\u6bd4\u4ed6\u8001\u5f97\u591a\u3002\u5374\u90fd\u662f\u4ed6\u7684\u5c5e\u4e0b\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u867d\u7136\u4e5f\u90fd\u662f\u7ecf\u8fc7\u7279\u522b\u6311\u9009\u3001\u773c\u5149\u6781\u9510\u5229\u3001\u7ecf\u9a8c\u6781\u4e30\u5bcc\u7684\u597d\u624b\uff0c\u53ef\u662f\u5b59\u901a\u65e0\u8bba\u5728\u54ea \u65b9\u9762\u90fd\u6bd4\u4ed6\u4eec\u4f18\u79c0\u5f97\u591a\uff0c\u8fde\u4ed6\u4eec\u81ea\u5df1\u90fd\u53e3\u670d\u5fc3\u670d\u3002 \u3000\u3000\u4ed6\u4eec\u88ab\u6d3e\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u56e0\u4e3a\u5b59\u901a\u8981\u5229\u7528\u4ed6\u4eec\u7684\u773c\u5149\u548c\u7ecf\u9a8c\uff0c\u68c0\u67e5\u6bcf\u4e00\u4e2a\u4ece\u7ea2\u82b1\u96c6\u8d70\u51fa\u6765 \u7684\u4eba\u3002 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u4eba\uff0c\u53ea\u8981\u6709\u4e00\u70b9\u53ef\u7591\u4e4b\u5904\uff0c\u624b\u91cc\u53ea\u8981\u63d0\u7740\u4e2a\u53ef\u4ee5\u88c5\u5f97\u4e0b\u5934\u9885\u7684\u5305\u88b1\uff0c\u8f66\u8f7f\u4e0a\u53ea \u8981\u6709\u4e2a\u53ef\u4ee5\u85cf\u5f97\u4f4f\u5934\u9885\u7684\u5730\u65b9\uff0c\u90fd\u8981\u53d7\u5230\u4ed6\u4eec\u5f7b\u5e95\u641c\u67e5\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u641c\u67e5\u6709\u65f6\u867d\u7136\u4f1a\u4ee4\u4eba\u96be\u582a\uff0c\u4e5f\u6ca1\u6709\u4eba\u6562\u62d2\u7edd\u3002\u56e0\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u77e5\u9053\uff0c\u4ece\u201c\u5927\u9556\u5c40\u201d \u51fa\u6765\u7684\u4eba\uff0c\u662f\u7edd\u5bf9\u4e0d\u80fd\u5f97\u7f6a\u7684\u3002 \u3000\u3000\u5b59\u901a\u4e5f\u4e0d\u6015\u5f97\u7f6a\u4efb\u4f55\u4eba\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u63a5\u5230\u5353\u4e1c\u6765\u7684\u547d\u4ee4\uff0c\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u90fd\u7edd\u4e0d\u80fd\u8ba9\u6768\u575a\u7684\u5934\u9885\u88ab\u5e26\u51fa\u957f\u5b89\u5e9c \u5883\u3002 \u3000\u3000\u4ed6\u6267\u884c\u5353\u4e1c\u6765\u7684\u547d\u4ee4\u65f6\uff0c\u4e00\u5411\u5f7b\u5e95\u800c\u6709\u6548\u3002 \u3000\u3000\u5c0f\u9ad8\u4ece\u7ea2\u82b1\u96c6\u8d70\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5b59\u901a\u5e76\u6ca1\u6709\u7279\u522b\u6ce8\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u5c0f\u9ad8\u5168\u8eab\u4e0a\u4e0b\u7edd\u5bf9\u6ca1\u6709\u4efb\u4f55\u5730\u65b9\u53ef\u4ee5\u85cf\u5f97\u4f4f\u4e00\u4e2a\u5934\u9885\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u9ad8\u5374\u8d70\u5230\u4ed6\u9762\u524d\u6765\u4e86\uff0c\u800c\u4e14\u5728\u4ed6\u5bf9\u9762\u7684\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u751a\u81f3\u8fd8\u5bf9\u4ed6\u7b11\u4e86\u7b11\uff0c\u5c45\u7136\u8fd8 \u95ee\u4ed6\uff1a\u201c\u8d35\u59d3\uff1f\u5927\u540d\uff1f\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u7b11\uff0c\u53ef\u662f\u4e5f\u6ca1\u6709\u62d2\u7edd\u53e3\u7b54\uff0c\u201c\u59d3\u5b59\uff0c\u5b59\u901a\u3002\u201d \u3000\u3000\u201c\u4f60\u597d\u3002\u201d \u3000\u3000\u201c\u867d\u7136\u4e0d\u592a\u597d\uff0c\u4e5f\u4e0d\u80fd\u7b97\u592a\u574f\u3002\u201d\u5b59\u901a\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u6700\u5c11\u6211\u7684\u4eba\u5934\u8fd8\u5728\u8116\u4e8e\u4e0a\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5927\u7b11\u3002 \u3000\u3000\u201c\u77e5\u9053\u81ea\u5df1\u7684\u4eba\u5934\u8fd8\u5728\u81ea\u5df1\u7684\u8116\u5b50\u4e0a\uff0c\u7684\u786e\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u5982\u679c\u8fd8\u80fd\u591f\u77e5 \u9053\u6768\u575a\u7684\u4eba\u5934\u5728\u54ea\u91cc\uff0c\u90a3\u5c31\u66f4\u6109\u5feb\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u77e5\u9053\u5353\u5148\u751f\u4e00\u5b9a\u5f88\u4e0d\u613f\u610f\u770b\u5230\u6768\u575a\u7684\u5934\u9885\u843d\u4eba\u6731\u731b\u624b\u91cc\uff0c\u8ba9\u4ed6\u63d0\u7740\u5b83\u5230\u6c5f\u6e56\u670b\u53cb \u9762\u524d\u53bb\u8000\u6b66\u626c\u5a01\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u624d\u4f1a\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u7684\u597d\u50cf\u5f88\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u53ea\u53ef\u60dc\u6211\u8fd8\u662f\u4e0d\u5927\u660e\u767d\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u8981\u5230\u6d1b\u9633\u53bb\u7684\u4eba\uff0c\u5e76\u4e0d\u4e00\u5b9a\u8981\u8d70\u5b98\u9053\u7684\uff0c\u8fde\u6211 \u8fd9\u4e2a\u5916\u4e61\u4eba\u90fd\u77e5\u9053\u53e6\u5916\u6700\u5c11\u8fd8\u6709\u4e24\u4e09\u6761\u5c0f\u8def\u3002\u201d \u3000\u3000\u201c\u6211\u53ea\u7ba1\u5927\u8def\uff0c\u4e0d\u7ba1\u5c0f\u8def\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8d70\u5c0f\u8def\u7684\u4eba\uff0c\u80c6\u5b50\u4e5f\u4e0d\u4f1a\u592a\u5927\uff0c\u8fd8\u7528\u4e0d\u7740\u8981\u6211\u53bb\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u201c\u8bf4\u5f97\u597d\uff01\u597d\u6781\u4e86\uff01\u201d \u3000\u3000\u5c0f\u9ad8\u4ece\u5b59\u901a\u7684\u8336\u58f6\u91cc\u5012\u4e86\u676f\u8336\uff0c\u5ffd\u7136\u53c8\u538b\u4f4e\u4e86\u58f0\u97f3\u95ee\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u53d1\u73b0\u4ec0\u4e48\u53ef\u7591\u7684\u4eba\uff1f \u3000\u3000\u201c\u53ea\u53d1\u73b0\u4e86\u4e00\u4e2a\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u4f60\uff01\u201d \u3000\u3000\u5c0f\u9ad8\u53c8\u5927\u7b11\uff1a\u201c\u5982\u679c\u771f\u7684\u662f\u6211\uff0c\u90a3\u5c31\u5f88\u4e0d\u6109\u5feb\u4e86\u3002\u201d \u3000\u3000\u201c\u8c01\u4e0d\u6109\u5feb\uff1f\u201d \u3000\u3000\u201c\u4f60\uff01\u201d \u3000\u3000\u5c0f\u9ad8\u770b\u7740\u5b59\u901a\uff1a\u201c\u5982\u679c\u6211\u8981\u5e26\u7740\u6768\u575a\u7684\u5934\u9885\u95ef\u8fd9\u4e00\u5173\uff0c\u90a3\u4e48\u9601\u4e0b\u4e5f\u8bb8\u5c31\u4f1a\u5ffd\u7136\u53d1\u73b0\u9601\u4e0b \u7684\u5927\u597d\u5934\u9885\u5df2\u7ecf\u4e0d\u5728\u9601\u4e0b\u7684\u8116\u5b50\u4e0a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5c45\u7136\u8fd8\u8981\u89e3\u91ca\uff0c\u201c\u9601\u4e0b\u7684\u610f\u601d\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u5b59\u901a\u6ca1\u6709\u53d1\u6012\uff0c\u8138\u8272\u4e5f\u6ca1\u6709\u53d8\uff0c\u8fde\u773c\u775b\u4e5f\u90fd\u6ca1\u6709\u7728\u4e00\u4e0b\u3002 \u3000\u3000\u201c\u6211\u4e5f\u770b\u5f97\u51fa\u4f60\u6ca1\u6709\u5e26\u6768\u575a\u7684\u4eba\u5934\uff01\u201d\u5b59\u901a\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u770b\u5f97\u51fa\u4f60\u5e26\u4e86\u4e00\u53e3\u5251\u3002\u201d \u3000\u3000\u201c\u4f60\u6ca1\u6709\u770b\u9519\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u62e8\u51fa\u4f60\u7684\u5251\u6765\u8bd5\u4e00\u8bd5\uff1f\u201d \u3000\u3000\u201c\u8bd5\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8bd5\u8bd5\u770b\u7a76\u7adf\u662f\u8c01\u7684\u5934\u9885\u4f1a\u4ece\u8116\u5b50\u4e0a\u843d\u4e0b\u3002\u201d\u5b59\u901a\u8bf4\u3002 \u3000\u3000\u5c0f\u9ad8\u8f7b\u629a\u7740\u4ed6\u90a3\u4e2a\u6c38\u8fdc\u4e0d\u79bb\u624b\u8fb9\u7684\u7c97\u5e03\u5305\u88b1\uff0c\u5fae\u7b11\u6447\u5934\u3002\u201c\u6211\u4e0d\u80fd\u8bd5\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u7edd\u5bf9 \u4e0d\u80fd\u8bd5\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u6562\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4e0b\u6562\uff0c\u662f\u4e0d\u80fd\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8fd9\u628a\u5251\u4e0d\u662f\u7528\u6765\u5bf9\u4ed8\u4f60\u7684\uff0c\u201d\u5c0f\u9ad8\u7528\u4e00\u79cd\u975e\u5e38\u5ba2\u6c14\u7684\u6001\u5ea6\u8bf4\uff1a\u201c\u56e0\u4e3a\u4f60\u8fd8\u4e0d \u914d\u3002\u201d \u3000\u3000\u5b59\u901a\u7684\u8138\u8272\u8fd8\u662f\u6ca1\u6709\u53d8\uff0c\u53ef\u662f\u773c\u775b\u91cc\u5374\u5ffd\u7136\u5e03\u6ee1\u4e86\u8840\u4e1d\u3002 \u3000\u3000\u6709\u5f88\u591a\u4eba\u5728\u6740\u4eba\u4e4b\u524d\u90fd\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5df2\u7ecf\u5782\u4e0b\uff0c\u63e1\u4f4f\u4e86\u653e\u5728\u51f3\u5b50\u4e0a\u7684\u5251\u67c4\u3002 \u3000\u3000\u5c0f\u9ad8\u5374\u5df2\u7ecf\u7ad9\u8d77\u6765\uff0c\u8f6c\u8fc7\u8eab\uff0c\u51c6\u5907\u8d70\u4e86\uff0c\u5982\u679c\u4ed6\u60f3\u8981\u51fa\u624b\u65f6\uff0c\u6ca1\u6709\u4eba\u80fd\u963b\u6b62\u4ed6\uff0c\u5982\u679c\u4ed6 \u4e0d\u60f3\u51fa\u624b\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u52c9\u5f3a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u8fd8\u6ca1\u6709\u8d70\u51fa\u53bb\uff0c\u5c31\u5df2\u542c\u89c1\u4e00\u9635\u5954\u96f7\u822c\u7684\u9a6c\u8e44\u58f0\u3002\u201d \u3000\u3000\u8e44\u58f0\u4e2d\u8fd8\u5939\u6742\u7740\u4e00\u79cd\u5f88\u5947\u602a\u7684\u4ef0\u6b65\u58f0\uff0c\u53ea\u6709\u7a7f\u7740\u9489\u96c5\u5728\u51b0\u96ea\u4e0a\u5954\u9c8d\u65f6\u624d\u4f1a\u53d1\u51fa\u8fd9\u79cd\u811a\u6b65 \u58f0\u3002 \u3000\u3000\u4ed6\u521a\u5206\u8fa8\u51fa\u8fd9\u4e24\u79cd\u4e0d\u540c\u7684\u58f0\u97f3\uff0c\u5c31\u5df2\u7ecf\u770b\u5230\u4e00\u9a91\u7094\u9a6c\u98de\u5954\u800c\u6765\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u9a91\u58eb\u6ee1\u9762\u866c\u9aef\uff0c\u53cd\u7a7f\u4e00\u4ef6\u7f8a\u76ae\u5927\u8884\uff0c\u8863\u895f\u5374\u662f\u6563\u5f00\u7684\uff0c\u8ba9\u98ce\u96ea\u5200\u950b\u822c\u522e\u5728\u4ed6\u8d64\u88f8 \u8d2d\u80f8\u819b\u4e0a\uff0c\u4ed6\u4e00\u70b9\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u540e\u9762\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u811a\u4e0a\u7a7f\u7740\u53cc\u6cb9\u5e03\u9489\u978b\uff0c\u4e00\u53ea\u4e01\u62c9\u4f4f\u9a6c\u5c3e\uff0c\u53e6\u5916\u4e00\u53ea\u624b\u91cc\u5374\u6311\u7740\u6839\u7af9 \u7aff\uff0c\u628a\u4e00\u4e2a\u9ebb\u5e03\u888b\u9ad8\u9ad8\u6311\u5728\u7af9\u7aff\u4e0a\uff0c\u8ddf\u7740\u5065\u9a6c\u98de\u5954\uff0c\u5634\u91cc\u8fd8\u5728\u5927\u58f0\u547c\u558a\u7740\uff0c\u201c\u6768\u575a\u7684\u4eba\u5934\u5c31 \u5728\u8fd9\u91cc\uff0c\u8fd9\u5c31\u662f\u53db\u5f92\u7684\u4e0b\u573a\u3002\u201d \u3000\u3000\u9a6c\u4e0a\u4eba\u7eb5\u58f0\u5927\u7b11\uff0c\u7b11\u58f0\u5982\u72eehou\uff0c\u9707\u5f97\u5c4b\u6a90\u4e0a\u7684\u79ef\u96ea\u4e00\u5927\u7247\u4e00\u5927\u7247\u7684\u843d\u4e0b\u6765\u3002 \u3000\u3000\u5c0f\u9ad8\u5f53\u7136\u4e0d\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u89c1\u8fc7\u6731\u731b\uff0c\u53ef\u662f\u4ed6\u4e00\u773c\u5c31\u770b\u51fa\u8fd9\u4e2a\u4eba\u5fc5\u5b9a\u5c31\u662f\u6731\u731b\u3002 \u3000\u3000\u9664\u4e86\u201c\u96c4\u72ee\u201d\u6731\u731b\u5916\uff0c\u8c01\u6709\u8fd9\u6837\u7684\u5a01\u98ce\uff1f \u3000\u3000\u4ed6\u4e5f\u60f3\u4e0d\u5230\u6731\u731b\u600e\u4e48\u4f1a\u5ffd\u7136\u5728\u8fd9\u91cc\u51fa\u73b0\uff0c\u4f46\u662f\u4ed6\u5e0c\u671b\u5b59\u901a\u8ba9\u4ed6\u4eec\u8fc7\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u770b\u89c1\u4e86\u6731\u731b\u624b\u91cc\u5012\u63d0\u7740\u7684\u4e00\u67c4\u91d1\u80cc\u5927\u780d\u5200\u3002 \u3000\u3000\u56db\u5c3a\u4e5d\u5bf8\u957f\u7684\u91d1\u80cc\u5927\u780d\u5200\uff0c\u5200\u80cc\u6bd4\u5c60\u592b\u7684\u7827\u677f\u8fd8\u539a\uff0c\u5200\u950b\u5374\u8584\u5982\u7eb8\u3002 \u3000\u3000\u5b59\u901a\u8fd8\u5e74\u8f7b\u3002 \u3000\u3000\u5c0f\u9ad8\u5b9e\u5728\u4e0d\u60f3\u770b\u89c1\u8fd9\u4e48\u6837\u4e00\u4e2a\u5e74\u8f7b\u4eba\uff0c\u88ab\u8fd9\u4e48\u6837\u4e00\u628a\u5200\u65a9\u6740\u5728\u9a6c\u8e44\u524d\u3002 \u3000\u3000\u53ef\u60dc\u5b59\u901a\u5df2\u7ecf\u51fa\u53bb\u4e86\uff0c\u5e26\u7740\u4e00\u7247\u96ea\u4eae\u7684\u5251\u5149\uff0c\u4ece\u684c\u5b50\u540e\u9762\u98de\u8dc3\u800c\u8d77\uff0c\u98de\u9e1f\u8239\u63a0\u51fa\u53bb\uff0c\u5251 \u5149\u5982\u98de\u8679\uff0c\u76f4\u53d6\u9a6c\u4e0a\u6731\u731b\u7684\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\u5c31\u50cf\u662f\u8d4c\u5f92\u7684\u6700\u540e\u4e00\u9053\u5b64\u6ce8\uff0c\u5df2\u7ecf\u628a\u81ea\u5df1\u7684\u8eab\u5bb6\u6027\u547d\u5168\u90e8\u62bc\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\u662f\u5fc5\u7136\u81f4\u547d\u7684\uff0c\u4e0d\u662f\u5bf9\u65b9\u7684\u547d\uff0c\u5c31\u662f\u81ea\u5df1\u7684\u547d\u3002 \u3000\u3000\u6731\u731b\u72c2\u7b11\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u771f\u6709\u79cd\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u56db\u5c3a\u4e5d\u5bf8\u957f\u7684\u5927\u780d\u5200\u5df2\u9ad8\u9ad8\u626c\u8d77\uff0c\u5200\u80cc\u4e0a\u7684\u91d1\u5149\u4e0e\u5200\u950b\u4e0a\u7684\u5bd2\u5149\uff0c\u5728\u96ea\u5149\u53cd\u6620 \u4e2d\u4eae\u5f97\u50cf\u5c16\u9488\u4e00\u6837\u523a\u773c\u3002 \u3000\u3000\u5c0f\u9ad8\u53ea\u770b\u89c1\u5200\u5149\u4e00\u95ea\uff0c\u5ffd\u7136\u95f4\u5c31\u53d8\u6210\u4e86\u4e00\u7247\u8165\u7ea2\u3002 \u3000\u3000\u65e0\u6570\u70b9\u9c9c\u7ea2\u7684\u8840\u82b1\uff0c\u5c31\u50cf\u662f\u7130\u706b\u822c\u5ffd\u7136\u4ece\u5200\u5149\u4e2d\u98de\u6e85\u800c\u51fa\uff0c\u548c\u4e00\u7247\u94f6\u767d\u7684\u96ea\u8272\u4ea4\u7ec7\u51fa\u4e00 \u5e45\u4ee4\u4eba\u6c38\u8fdc\u5fd8\u4e0d\u4e86\u7684\u56fe\u753b\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u8fd9\u79cd\u7f8e\uff0c\u7f8e\u5f97\u5982\u6b64\u51c4\u8273\uff0c\u5982\u6b64\u6b8b\u9177\uff0c\u5982\u6b64\u60e8\u70c8\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4eba\u4e16\u95f4\u6240\u6709\u7684\u4e07\u4e8b\u4e07\u7269\u4e07\u79cd\u751f\u673a\u90fd\u4f3c\u5df2\u88ab\u8fd9\u79cd\u7f8e\u6240\u9707\u6151\u800c\u505c\u6b62\u3002 \u3000\u3000\u5c0f\u9ad8\u53ea\u89c9\u5f97\u81ea\u5df1\u8fde\u5fc3\u8df3\u547c\u5438\u90fd\u4f3c\u5df2\u505c\u6b62\u3002 \u3000\u3000\u8fd9\u867d\u7136\u53ea\u4e0d\u8fc7\u662f\u4e00\u77ac\u95f4\u7684\u4e8b\uff0c\u53ef\u662f\u8fd9\u4e00\u77ac\u95f4\u5374\u4eff\u4f5b\u5c31\u662f\u6c38\u6052\u3002 \u3000\u3000\u5929\u5730\u95f4\u672c\u6765\u5c31\u53ea\u6709\u201c\u6b7b\u201d\u624d\u662f\u6c38\u6052\u7684\u3002 \u3000\u3000\u5954\u9a6c\u98de\u9a70\u672a\u505c\uff0c\u9489\u978b\u4ecd\u5728\u5954\u8dd1\uff0c\u8dd1\u51fa\u53bb\u4e8c\u5341\u4f59\u4e08\u540e\uff0c\u5b59\u901a\u7684\u5c38\u4f53\u624d\u843d\u4e86\u4e0b\u6765\uff0c\u843d\u5728\u4ed6\u4eec \u7684\u4eba\u548c\u9a6c\u540e\u9762\uff0c\u843d\u5728\u50cf\u90a3\u67c4\u5927\u780d\u5200\u7684\u5200\u950b\u4e00\u6837\u51b7\u9177\u65e0\u60c5\u7684\u51b0\u96ea\u4e0a\u3002 \u3000\u3000\u7136\u540e\u90a3\u5343\u767e\u70b9\u8840\u82b1\u624d\u968f\u7740\u4e00\u70b9\u70b9\u96ea\u82b1\u843d\u4e0b\u6765\u3002 \u3000\u3000\u8840\u82b1\u9c9c\u7ea2\uff0c\u96ea\u82b1\u83b9\u767d\u3002 \u3000\u3000\u5954\u9a6c\u957f\u5636\uff0c\u4eba\u7acb\u9762\u8d77\uff0c\u7a7f\u9489\u978b\u7684\u4eba\u4e5f\u8f7b\u98d8\u98d8\u98de\u8d77\u3002 \u3000\u3000\u6731\u731b\u52d2\u9a6c\uff0c\u6389\u8f6c\u9a6c\u5934\u5c0f\u6b65\u5954\u56de\uff0c\u9489\u978b\u5c31\u50cf\u662f\u4e00\u53ea\u7eb8\u9e22\u822c\u6302\u5728\u9a6c\u5c3e\u4e0a\u3002 \u3000\u3000\u9053\u8def\u4e24\u65c1\u7684\u9752\u8863\u4eba\uff0c\u867d\u7136\u5df2\u7ecf\u62d4\u51fa\u4e86\u8170\u5200\uff0c\u4ed6\u4eec\u7684\u5200\u950b\u867d\u7136\u4e5f\u548c\u6731\u731b\u7684\u5200\u950b\u4e00\u6837\u4eae\uff0c\u53ef \u662f\u4ed6\u4eec\u7684\u8138\u8272\u548c\u773c\u8272\u5374\u5df2\u53d8\u6210\u6b7b\u7070\u8272\u3002 \u3000\u3000\u6731\u731b\u53c8\u5927\u7b11\u3002 \u3000\u3000\u201c\u4f60\u4eec\u770b\u6e05\u695a\uff0c\u8001\u5b50\u5c31\u662f\u6731\u731b\u3002\u201d\u4ed6\u5927\u7b11\u9053\uff1a\u201c\u8001\u5b50\u7559\u4e0b\u4f60\u4eec\u7684\u8111\u888b\uff0c\u53ea\u56e0\u4e3a\u8001\u5b50\u8981\u4f60 \u4eec\u7528\u773c\u775b\u628a\u8001\u592b\u770b\u6e05\u695a\uff0c\u7528\u5634\u5df4\u56de\u53bb\u544a\u8bc9\u53f8\u9a6c\u548c\u5353\u4e1c\u6765\uff0c\u8001\u5b50\u5df2\u7ecf\u6765\u8fc7\u4e86\uff0c\u73b0\u5728\u53c8\u8981\u8d70\u4e86\uff0c \u5c31\u7b97\u8fd9\u91cc\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u8001\u5b50\u4e5f\u4e00\u6837\u8981\u6765\u5c31\u6765\uff0c\u8981\u8d70\u5c31\u8d70\u3002\u201d \u3000\u3000\u4ed6\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u6eda\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u672c\u6765\u5df2\u7ecf\u5728\u5f80\u540e\u9000\uff0c\u542c\u89c1\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u7acb\u523b\u5168\u90e8\u8dd1\u4e86\uff0c\u8dd1\u5f97\u6bd4\u9a6c\u8fd8\u5feb\u3002 \u3000\u3000\u6731\u731b\u672c\u6765\u53c8\u60f3\u7b11\u7684\uff0c\u5374\u8fd8\u6ca1\u6709\u7b11\u51fa\u6765\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u542c\u89c1\u4e00\u4e2a\u4eba\u53f9\u7740\u6c14\u8bf4\uff0c\u201c\u73b0\u5728\u6211\u624d\u77e5 \u9053\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u50cf\u5b59\u901a\u90a3\u4e48\u4e0d\u6015\u6b7b\u7684\u4eba\u5b9e\u5728\u4e0d\u591a\u3002\u201d \u56db \u3000\u3000\u5c0f\u9ad8\u5df2\u7ecf\u5750\u4e0b\uff0c\u5c31\u5750\u5728\u5b59\u901a\u521a\u624d\u5750\u7684\u4f4d\u5b50\u4e0a\uff0c\u800c\u4e14\u8fd8\u628a\u5b59\u901a\u521a\u624d\u62d4\u5251\u65f6\u8dcc\u843d\u7684\u5251\u9798\u6361\u8d77 \u6765\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u548c\u4ed6\u81ea\u5df1\u90a3\u67c4\u7528\u7c97\u5e03\u5305\u4f4f\u7684\u5251\u653e\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u7528\u6b63\u773c\u53bb\u770b\u6731\u731b\uff0c\u53ef\u662f\u4ed6\u77e5\u9053\u6731\u731b\u7684\u8138\u8272\u5df2\u7ecf\u53d8\u4e86\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u53d1\u73b0\u6731\u731b\u5df2\u7ecf\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u9ad8\u9ad8\u7684\u9a91\u5728\u9a6c\u4e0a\uff0c\u7528\u4e00\u53cc\u94dc\u94c3\u822c\u7684\u9510\u773c\u77aa\u7740\u4ed6\u3002 \u3000\u3000\u5c0f\u9ad8\u597d\u50cf\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u4ed6\u5728\u559d\u8336\u3002 \u3000\u3000\u676f\u5b50\u91cc\u7684\u8336\u5df2\u51c9\u4e86\uff0c\u4ed6\u6cfc\u6389\uff0c\u518d\u4ece\u58f6\u91cc\u5012\u4e86\u4e00\u676f\uff0c\u53c8\u6cfc\u60bc\uff0c\u56e0\u4e3a\u58f6\u91cc\u7684\u8336\u4e5f\u662f\u51b7\u7684\uff0c\u53ef \u662f\u4ed6\u5c45\u7136\u8fd8\u8981\u518d\u5012\u4e00\u676f\u3002 \u3000\u3000\u6731\u731b\u4e00\u76f4\u77aa\u7740\u4ed6\uff0c\u5ffd\u7136\u5927\u58f0\u95ee\uff1a\u201c\u4f60\u5728\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u5728\u559d\u8336\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u53e3\u6e34\uff0c\u60f3\u559d\u8336\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u6ca1\u6709\u559d\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8336\u5df2\u7ecf\u51b7\u4e86\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u4e00\u5411\u4e0d\u559c\u6b22\u559d\u51b7\u8336\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u559d\u9152\u6211\u4e0d\u5728\u4e4e\uff0c\u4ec0\u4e48\u6837\u7684\u9152\u6211\u90fd\u559d\uff0c\u53ef\u662f\uff0c\u559d\u8336\u6211\u4e00\u5411\u5f88\u8bb2\u7a76\uff0c\u51b7\u8336\u662f \u4e07\u4e07\u559d\u4e0d\u5f97\u7684\uff0c\u8981\u6211\u559d\u51b7\u8336\uff0c\u6211\u5b81\u53ef\u559d\u6bd2\u9152\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u8fd8\u60f3\u4ece\u8fd9\u4e2a\u8336\u58f6\u91cc\u5012\u676f\u70ed\u8336\u51fa\u6765\uff1f\u201d\u6731\u731b\u95ee\u5c0f\u9ad8\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u5c31\u5728\u8fd9\u4e48\u60f3\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u58f6\u8336\u5df2\u7ecf\u5b8c\u5168\u51b7\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u6731\u731b\u770b\u7740\u4ed6\uff0c\u5c31\u597d\u50cf\u770b\u7740\u4e2a\u602a\u7269\u4e00\u6837\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u58f6\u8336\u5df2\u7ecf\u51b7\u4e86\uff0c\u53ef\u662f\u4f60\u8fd8\u60f3\u4ece\u8fd9\u58f6\u8336 \u91cc\u5012\u676f\u70ed\u7684\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f46\u8981\u70ed\u7684\uff0c\u800c\u4e14\u8fd8\u8981\u70eb\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u53c8\u6eda\u53c8\u70ed\u7684\u8336\u624d\u597d\u559d\u3002\u201d \u3000\u3000\u6731\u731b\u5ffd\u7136\u53c8\u7b11\u4e86\uff0c\u56de\u5934\u544a\u8bc9\u9489\u978b\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u60f3\u628a\u8fd9\u5c0f\u5b50\u7684\u8111\u888b\u780d\u4e0b\u6765\u7684\uff0c\u53ef\u662f\u6211\u73b0\u5728\u4e0d\u80fd\u780d\u4e86\u3002\u201d\u6731\u731b\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50 \u662f\u4e2a\u75af\u5b50\uff0c\u8001\u5b50\u4ece\u6765\u4e0d\u780d\u75af\u5b50\u7684\u8111\u888b\u3002\u201d \u3000\u3000\u9489\u978b\u6ca1\u6709\u7b11\uff0c\u56e0\u4e3a\u4ed6\u770b\u89c1\u4e86\u4e00\u4ef6\u602a\u4e8b\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u5c0f\u9ad8\u5c45\u7136\u771f\u7684\u4ece\u90a3\u58f6\u51b7\u8336\u91cc\uff0c\u5012\u4e86\u4e00\u676f\u70ed\u7684\u51fa\u6765\uff0c\u6eda\u70eb\u7684\u70ed\u8336\uff0c\u70eb\u5f97\u5192\u70df\u3002 \u3000\u3000\u6731\u731b\u7684\u7b11\u58f0\u4e5f\u5f88\u5feb\u5c31\u505c\u987f\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u770b\u89c1\u4e86\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u770b\u89c1\u8fd9\u79cd\u4e8b\u4e4b\u540e\u8fd8\u80fd\u591f\u7b11\u5f97\u51fa\u6765\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u80fd\u591f\u7528\u638c\u5fc3\u7684\u5185\u529b\u548c\u70ed\u529b\uff0c\u628a\u4e00\u58f6\u51b7\u8336\u53d8 \u6210\u70ed\u8336\u7684\u4eba\u4e5f\u4e0d\u591a\u3002 \u3000\u3000\u6731\u731b\u5ffd\u7136\u53c8\u56de\u5934\u95ee\u9489\u978b\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u4e0d\u662f\u75af\u5b50\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u662f\u4e0d\u662f\u597d\u50cf\u8fd8\u6709\u4ed6\u5a18\u7684\u4e00\u70b9\u771f\u529f\u592b\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u8fd9\u5c0f\u5b50\u8fd8\u771f\u662f\u597d\u5c0f\u5b50\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u8001\u5b50\u5c45\u7136\u5dee\u4e00\u70b9\u770b\u8d70\u773c\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u606c\uff0c\u4ed6\u5c31\u505a\u51fa\u4ef6\u4efb\u4f55\u4eba\u90fd\u60f3\u4e0d\u5230\u4ed6\u4f1a\u505a\u51fa\u6765\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u4e0b\u4e86\u9a6c\uff0c\u628a\u624b\u91cc\u7684\u5927\u780d\u5200\u5f80\u5730\u4e0a\u4e00\u63d2\uff0c\u8d70\u5230\u5c0f\u9ad8\u9762\u524d\uff0c\u4e00\u672c\u6b63\u7ecf\u7684\u62b1\u62f3\u884c\u4e86\u793c\uff0c \u4e00\u672c\u6b63\u7ecf\u7684\u8bf4\uff1a\u201c\u4f60\u4e0d\u662f\u75af\u5b50\uff0c\u4f60\u662f\u6761\u597d\u6c49\uff0c\u53ea\u8981\u4f60\u80af\u8ba4\u6211\u505a\u5144\u5f1f\uff0c\u80af\u966a\u6211\u56de\u53bb\u75db\u75db\u5feb\u5feb\u7684 \u559d\u51e0\u5929\u9152\uff0c\u6211\u9a6c\u4e0a\u5c31\u8dea\u4e0b\u6765\u8ddf\u4f60\u78d5\u4e09\u4e2a\u54cd\u5934\u3002\u201d \u3000\u3000\u201c\u96c4\u72ee\u5802\u201d\u597d\u624b\u5982\u4e91\uff0c\u96c4\u72ee\u6731\u731b\u5a01\u9707\u6cb3\u6d1b\uff0c\u4ee5\u4ed6\u7684\u8eab\u5206\uff0c\u600e\u4e48\u4f1a\u5982\u6b64\u5df4\u7ed3\u4e00\u4e2a\u65e0\u540d\u7684\u843d \u62d3\u7684\u5c11\u5e74\uff1f\u53ef\u662f\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u5374\u4e00\u70b9\u4e0d\u50cf\u662f\u5047\u7684\u3002 \u3000\u3000\u5c0f\u9ad8\u597d\u50cf\u5df2\u7ecf\u6014\u4f4f\u4e86\uff0c\u6014\u4e86\u534a\u5929\uff0c\u624d\u53f9\u53e3\u6c14\uff0c\u6614\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u76f8\u4fe1\u6c5f\u6e56\u4e2d\u4eba\u8bf4\u7684\u4e0d \u5047\uff0c\u96c4\u72ee\u6731\u731b\u679c\u7136\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u89d2\u8272\uff0c\u96be\u602a\u6709\u90a3\u4e48\u591a\u4eba\u670d\u4f60\uff0c\u80af\u4e3a\u4f60\u53bb\u5356\u547d\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u6731\u731b\u7acb\u523b\u95ee\uff1a\u201c\u4f60\u80af\u4e0d\u80af\u4ea4\u6211\u6731\u731b\u8fd9\u4e2a\u670b\u53cb\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u5ffd\u7136\u7528\u529b\u4e00\u62cd\u684c\u5b50\uff0c\u5927\u58f0\u8bf4\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u4ea4\u670b\u53cb\u5c31\u4ea4\u670b\u53cb\uff0c\u4ea4\u4e2a\u670b\u53cb\u6709\u4ec0\u4e48\u4e86\u4e0d \u8d77\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u6bd4\u6731\u731b\u8fd8\u5927\uff1a\u201c\u6211\u9ad8\u6e10\u98de\u5728\u6c5f\u6e56\u4e2d\u6df7\u4e86\u51e0\u4e2a\u6708\uff0c\u8fd8\u6ca1\u6709\u9047\u5230\u8fc7\u4e00\u4e2a\u50cf\u4f60\u8fd9\u4e48 \u6837\u770b\u5f97\u8d77\u6211\u7684\u4eba\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u4ea4\u4f60\u8fd9\u4e2a\u670b\u53cb\uff1f\u201d \u3000\u3000\u6731\u731b\u4ef0\u9762\u5927\u7b11\uff0c\u201c\u597d\uff01\u8bf4\u5f97\u597d\uff01\u201d \u3000\u3000\u201c\u53ea\u4e0d\u8fc7\u78d5\u5934\u8fd9\u4ef6\u4e8b\u5343\u4e07\u8981\u514d\u6389\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4f60\u8ddf\u6211\u8dea\u4e0b\u6765\uff0c\u6211\u4e5f\u4e0d\u80fd\u7ad9\u7740\uff0c\u82e5\u662f\u4e24 \u4e2a\u4eba\u5168\u8dea\u5728\u5730\u4e0a\u78d5\u5934\uff0c\u4f60\u78d5\u8fc7\u6765\uff0c\u6211\u78d5\u8fc7\u53bb\uff0c\u5c82\u975e\u53d8\u6210\u4e00\u5bf9\u78d5\u5934\u866b\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u5927\u58f0\u8bf4\uff1a\u201c\u8fd9\u79cd\u4e8b\u6211\u662f\u7edd\u4e0d\u505a\u7684\u3002\u201d \u3000\u3000\u6731\u731b\u7acb\u523b\u540c\u610f\uff01 \u3000\u3000\u201c\u4f60\u8bf4\u4e0d\u505a\uff0c\u54b1\u4eec\u5c31\u4e0d\u505a\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u80fd\u966a\u4f60\u56de\u53bb\u559d\u9152\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u5728\u957f\u5b89\u8fd8\u6709\u4e2a\u6b7b\u7ea6\u4f1a\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u559d\uff0c\u559d\u4ed6\u4e2a\u75db\u5feb\u3002\u201d \u3000\u3000\u201c\u5c31\u5728\u8fd9\u91cc\u559d\uff1f\u201d\u5c0f\u9ad8\u76b1\u7709\uff1a\u201c\u4f60\u4e0d\u6015\u53f8\u9a6c\u8d76\u6765\uff1f\u201d \u3000\u3000\u6731\u731b\u5ffd\u7136\u4e5f\u7528\u529b\u4e00\u62cd\u684c\u5b50\u3002 \u3000\u3000\u201c\u4ed6\u5976\u5976\u7684\uff0c\u5c31\u7b97\u4ed6\u6765\u4e86\u53c8\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff1f\u8001\u5b50\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u628a\u8fd9\u6761\u547d\u53bb\u8ddf\u4ed6\u62fc\u6389\u800c \u5df2\uff0c\u4ed6\u8fd8\u80fd\u628a\u8001\u5b50\u600e\u4e48\u6837\uff1f\u201d\u6731\u731b\u5927\u58f0\u9053\uff1a\u201c\u53ef\u662f\u54b1\u4eec\u8fd9\u987f\u9152\u5374\u662f\u975e\u559d\u4e0d\u53ef\u7684\uff0c\u4e0d\u559d\u6bd4\u6b7b\u8fd8 \u96be\u53d7\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u559d\u5c31\u559d\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u8981\u662f\u4f60\u4e0d\u6015\uff0c\u6211\u6015\u4e2a\u9e1f\u3002\u201d \u3000\u3000\u8336\u9986\u91cc\u975e\u4f46\u6ca1\u6709\u5ba2\u4eba\uff0c\u8fde\u4f19\u6c41\u90fd\u6e9c\u4e86\u3002 \u3000\u3000\u5e78\u597d\u9152\u575b\u5b50\u4e0d\u4f1a\u6e9c\u3002 \u3000\u3000\u672a\u731b\u5c0f\u9ad8\u559d\u9152\uff0c\u9489\u978b\u5012\u9152\uff0c\u5012\u7684\u8fd8\u6ca1\u6709\u559d\u7684\u5feb\uff0c\u4e00\u575b\u9152\u8fd8\u6ca1\u6709\u559d\u5b8c\u3002\u8fdc\u5904\u5df2\u6709\u9a6c\u8e44\u58f0\u4f20 \u6765\u3002 \u3000\u3000\u8e44\u58f0\u5bc6\u5982\u7d27\u9f13\uff0c\u6765\u7684\u9a6c\u81f3\u5c11\u4e5f\u6709\u516d\u4e03\u5341\u5339\u3002 \u3000\u3000\u7ea2\u82b1\u96c6\u672c\u6765\u5c31\u5728\u53f8\u9a6c\u8d85\u7fa4\u7684\u52bf\u529b\u8303\u56f4\u4e4b\u5185\uff0c\u5982\u679c\u6709\u4eba\u8bf4\u53ea\u8981\u53f8\u9a6c\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7247\u523b\u95f4\u5c31\u53ef \u4ee5\u628a\u8fd9\u5730\u65b9\u8e29\u4e3a\u5e73\u5730\uff0c\u90a3\u4e5f\u4e0d\u80fd\u7b97\u592a\u5938\u5f20\u3002 \u3000\u3000\u4f46\u662f\u6731\u731b\u5374\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\uff0c\u5343\u91cc\u62ff\u7740\u6ee1\u6ee1\u7684\u4e00\u5927\u7897\u9152\uff0c\u4e5f\u6ca1\u6709\u4e00\u6ef4\u6cfc\u51fa\u6765\u3002 \u3000\u3000\u201c\u6211\u518d\u656c\u4f60\u4e09\u5927\u7897\u3002\u201d\u4ed6\u5bf9\u5c0f\u9ad8\u8bf4\uff1a\u201c\u795d\u4f60\u591a\u798f\u591a\u5bff\uff0c\u8eab\u5b50\u5065\u5eb7\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u559d\u3002\u201d \u3000\u3000\u4ed6\u559d\u5f97\u867d\u5feb\uff0c\u9a6c\u8e44\u58f0\u7684\u6765\u52bf\u66f4\u5feb\uff0c\u8fd9\u4e09\u7897\u9152\u559d\u5b8c\uff0c\u8e44\u58f0\u542c\u6765\u5df2\u5982\u96f7\u9e23\u3002 \u3000\u3000\u9489\u978b\u6367\u7740\u9152\u575b\u5b50\u7684\u624b\u5df2\u7ecf\u6709\u70b9\u53d1\u8f6f\u4e86\uff0c\u6731\u731b\u5374\u8fd8\u662f\u9762\u4e0d\u6539\u8272\u3002 \u3000\u3000\u201c\u8fd9\u6b21\u8f6e\u5230\u4f60\u656c\u6211\u4e86\u3002\u201d\u4ed6\u5bf9\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4f60\u6700\u5c11\u4e5f\u5f97\u656c\u6211\u4e09\u5927\u7897\u3002\u201d \u3000\u3000\u9489\u978b\u5ffd\u7136\u63d2\u5634\uff1a\u201c\u62a5\u544a\u5802\u4e3b\uff0c\u8fd9\u4e09\u7897\u6050\u6015\u662f\u4e0d\u80fd\u518d\u559d\u4e86\u3002\u201d \u3000\u3000\u6731\u731b\u66b4\u6012\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u559d\uff1f\u201d \u3000\u3000\u201c\u62a5\u544a\u5802\u4e3b\uff0c\u518d\u559d\u4e0b\u53bb\uff0c\u8fd9\u4f4d\u9ad8\u5c11\u7237\u7684\u6027\u547d\u6050\u6015\u4e5f\u8981\u966a\u5802\u4e3b\u4e00\u8d77\u62fc\u6389\u3002\u201d \u3000\u3000\u6731\u731b\u6012\u6c14\u5ffd\u7136\u6d88\u5931\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff0c\u201c\u4ed6\u8bf4\u7684\u4e5f\u6709\u7406\uff0c\u6211\u7684\u6027\u547d\u62fc\u6389\u65e0\u59a8\uff0c\u4e3a\u4ec0\u4e48\u8981\u8fde \u7d2f\u4f60\uff1f\u201d \u3000\u3000\u4ed6\u6b63\u60f3\u4e00\u8dc3\u800c\u8d77\uff0c\u5c0f\u9ad8\u5374\u6309\u4f4f\u4e86\u4ed6\u7684\u80a9\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\uff1a\u201c\u6211\u7684\u547d\u53c8\u4e0d\u6bd4\u4f60\u503c\u94b1\uff0c\u4f60\u80fd \u62fc\u547d\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u4f55\u51b5\u6211\u4eec\u4e5f\u672a\u5fc5\u5c31\u62fc\u4e0d\u8fc7\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6731\u731b\u53c8\u5927\u7b11\uff1a\u201c\u6709\u7406\uff0c\u4f60\u8bf4\u5f97\u66f4\u6709\u7406\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u4e5f\u8981\u656c\u4f60\u4e09\u5927\u7897\uff0c\u4e5f\u795d\u4f60\u591a\u798f\u591a\u5bff\uff0c\u8eab\u5b50\u5eb7\u5065\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u540c\u65f6\u5927\u7b11\uff0c\u7b11\u58f0\u8fd8\u672a\u505c\uff0c\u5954\u96f7\u822c\u7684\u9a6c\u8e44\u58f0\u5df2\u7ed5\u8fc7\u8fd9\u5bb6\u8336\u9986\uff0c\u5728\u7247\u523b\u95f4\u5c31\u628a\u8336\u9986\u5305 \u56f4\u3002 \u3000\u3000\u8e44\u58f0\u9aa4\u7136\u505c\u987f\uff0c\u51e0\u58f0\u65ad\u7eed\u7684\u9a6c\u5636\u58f0\u8fc7\u540e\uff0c\u6240\u6709\u7684\u58f0\u97f3\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u5929\u5730\u95ee\u5ffd\u7136\u53d8\u5f97\u50bb\u6b7b\u4e00\u822c\u9759\u5bc2\uff0c\u8fd9\u95ee\u8336\u9986\u5c31\u662f\u4e2a\u575f\u5893\u3002 \u3000\u3000\u9489\u978b\u5ffd\u7136\u4e5f\u5750\u4e0b\u6765\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u62a5\u544a\u5802\u4e3b\uff0c\u73b0\u5728\u6211\u4e5f\u60f3\u559d\u70b9\u9152\u4e86\u3002\u201d \u4e94 \u3000\u3000\u5200\u65e0\u58f0\uff0c\u5251\u65e0\u58f0\uff0c\u4eba\u65e0\u58f0\uff0c\u9a6c\u4e5f\u65e0\u58f0\u3002 \u3000\u3000\u56e0\u4e3a\u6bcf\u4e00\u4e2a\u4eba\u3001\u6bcf\u4e00\u5339\u9a6c\u90fd\u5df2\u7ecf\u8fc7\u591a\u5e74\u4e25\u683c\u7684\u8bad\u7ec3\uff0c\u5728\u5fc5\u8981\u65f6\u7edd\u4e0d\u53d1\u51fa\u4e00\u70b9\u4e0d\u5fc5\u8981\u7684\u58f0 \u97f3\u6765\uff0c\u5c31\u7b97\u5934\u9885\u88ab\u780d\u4e0b\uff0c\u4e5f\u4e0d\u4f1a\u53d1\u51fa\u4e00\u70b9\u58f0\u97f3\u6765\u3002 \u3000\u3000\u6b7b\u4e00\u822c\u7684\u9759\u5bc2\u4e2d\uff0c\u4e00\u4e2a\u4eba\u6234\u7d2b\u7389\u51a0\uff0c\u7740\u7d2b\u8c82\u88d8\uff0c\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u8d70\u5165\u4e86\u8fd9\u5bb6\u8336\u9986\u3002 \u3000\u3000\u201c\u7d2b\u6c14\u4e1c\u6765\u201d\u5353\u4e1c\u6765\u5df2\u7ecf\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u6001\u5ea6\u6781\u6c89\u9759\uff0c\u4e00\u79cd\u53ea\u6709\u5728\u4e00\u4e2a\u4eba\u5df2\u7ecf\u77e5\u9053\u81ea\u5df1\u7edd\u5bf9\u638c\u63e1\u4f4f\u4f18\u52bf\u7684\u65f6\u5019\uff0c\u624d\u80fd\u8868\u73b0\u51fa \u7684\u6c89\u9759\u3002 \u3000\u3000\u8336\u9986\u91cc\u8fd9\u4e09\u4e2a\u4eba\u4e09\u6761\u547d\u65e0\u7591\u5df2\u88ab\u4ed6\u638c\u63e1\u5728\u624b\u91cc\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u9ad8\u548c\u6731\u731b\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u201c\u6211\u8fd8\u8981\u518d\u656c\u4f60\u4e09\u5927\u7897\u3002\u201d\u5c0f\u9ad8\u8bf4\uff0c\u201c\u8fd9\u4e09\u7897\u795d\u4f60\u957f\u547d\u5bcc\u8d35\uff0c\u591a\u5b50\u591a\u5b59\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u5012\u9152\uff0c\u5353\u4e1c\u6765\u5df2\u7ecf\u5230\u4e86\u4ed6\u4eec\u9762\u524d\uff0c\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u8fd9\u4e09\u7897\u5e94\u8be5\u7531\u6211\u6765\u656c\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6731\u5802\u4e3b\u8fdc\u6765\uff0c\u6211\u4eec\u5c45\u7136\u5b8c\u5168\u6ca1\u6709\u5c3d\u5230\u4e00\u70b9\u5730\u4e3b\u4e4b\u8c0a\uff0c\u8fd9\u4e09\u7897\u5f53\u7136\u5e94\u8be5\u7531\u6211\u6765\u656c\u3002\u201d \u3000\u3000\u6731\u731b\u5c45\u7136\u8fde\u8bdd\u90fd\u4e0d\u8bf4\u5c31\u559d\u4e86\u4e09\u5927\u7897\uff0c\u5353\u4e1c\u6765\u559d\u5f97\u5c45\u7136\u4e5f\u4e0d\u6bd4\u4ed6\u6162\u3002 \u3000\u3000\u201c\u6211\u4e5f\u8fd8\u8981\u518d\u656c\u6731\u5802\u4e3b\u4e09\u5927\u7897\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u8fd9\u4e09\u7897\u9152\u6211\u4e5f\u662f\u975e\u559d\u4e0d\u53ef\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u559d\u8fc7\u8fd9\u4e09\u7897\u9152\u4e4b\u540e\uff0c\u6211\u5c31\u6709\u4ef6\u4e8b\u60f3\u8bf7\u6559\u6731\u5802\u751f\u4e86\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u5148\u559d\u4e86\u4e09\u7897\u9152\uff1a\u201c\u6731\u5802\u4e3b\u884c\u8e2a\u98d8\u5ffd\uff0c\u795e\u51fa\u9b3c\u6ca1\uff0c\u628a\u8fd9\u91cc\u89c6\u82e5\u65e0\u4eba\u4e4b\u5730\u3002\u201d\u4ed6\u53f9\u4e86 \u53e3\u6c14\uff1a\u201c\u5982\u679c\u6731\u5802\u4e3b\u521a\u624d\u5c31\u8d70\u4e86\uff0c\u6211\u4eec\u4e5f\u5b9e\u5728\u65e0\u80fd\u4e3a\u529b\u3002\u201d \u3000\u3000\u4ed6\u62ac\u8d77\u5934\uff0c\u51b7\u51b7\u7684\u770b\u7740\u6731\u731b\uff1a\u201c\u53ef\u662f\u6731\u5802\u4e3b\u521a\u624d\u4e3a\u4ec0\u4e48\u4e0d\u8d70\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u4e0d\u5230\uff1f\u201d \u3000\u3000\u201c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\uff01\u201d \u3000\u3000\u201c\u5176\u5b9e\u6211\u672c\u6765\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u56e0\u4e3a\u90a3\u65f6\u6211\u8fd8\u6ca1\u6709\u4ea4\u5230\u8fd9\u4e2a\u670b\u53cb\u3002\u201d\u6731\u731b\u62cd\u7740\u5c0f\u9ad8\u7684\u80a9\uff1a \u201c\u73b0\u5728\u6211\u65e2\u7136\u5df2\u7ecf\u4ea4\u4e86\u8fd9\u4e2a\u670b\u53cb\uff0c\u6211\u5f53\u7136\u8981\u966a\u4ed6\u559d\u51e0\u676f\uff0c\u4ed6\u65e2\u7136\u4e0d\u80fd\u8ddf\u6211\u56de\u53bb\uff0c\u6211\u4e5f\u53ea\u597d\u7559 \u5728\u8fd9\u91cc\u966a\u4ed6\u3002\u201d \u3000\u3000\u6731\u731b\u53c8\u5927\u7b11\uff1a\u201c\u8fd9\u9053\u7406\u5176\u5b9e\u7b80\u5355\u5f97\u5f88\uff0c\u53ea\u53ef\u60dc\u4f60\u4eec\u8fd9\u6837\u7684\u4eba\u7edd\u5bf9\u4e0d\u4f1a\u660e\u767d\u800c\u5df2\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u4e0d\u54cd\u4e0d\u52a8\u4e0d\u53f9\u6c14\u4e0d\u559d\u9152\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u5728\u8fd9\u6bb5\u65f6\u95f4\uff0c\u4ed6\u8fd9\u4e2a\u4eba\u5c31\u597d\u50cf\u5ffd\u7136\u53d8\u6210\u4e86\u4e2a\u6728\u5934\u4eba\uff0c\u751a\u81f3\u8fde\u773c\u775b\u91cc\u90fd\u6ca1\u6709\u4e00\u70b9\u8868\u60c5\u3002 \u3000\u3000\u5916\u9762\u4e5f\u6ca1\u6709\u4e3e\u52a8\uff0c\u6ca1\u6709\u5f97\u5230\u5353\u4e1c\u6765\u7684\u547d\u4ee4\uff0c\u8c01\u4e5f\u4e0d\u6562\u6709\u4efb\u4f55\u4e3e\u52a8\u3002 \u3000\u3000\u8fd9\u65f6\u95f4\u5e76\u4e0d\u77ed\u3002 \u3000\u3000\u5728\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u5c0f\u9ad8\u548c\u6731\u731b\u5728\u4e8e\u4ec0\u4e48\uff1f\u5353\u4e1c\u6765\u65e2\u4e0d\u77e5\u9053\uff0c\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u5728\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u53ea\u6709\u5c0f\u9ad8\u4e00\u4e2a\u4eba\u7684\u8868\u60c5\u6700\u5947\u602a\u3002 \u3000\u3000\u4ece\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u770b\u8d77\u6765\uff0c\u5c31\u597d\u50cf\u4ed6\u660e\u660e\u770b\u5230\u6709\u4e03\u516b\u53ea\u874e\u5b50\u3001\u5341\u51e0\u4e2a\u81ed\u866b\u94bb\u5230\u4ed6\u8863\u88f3\u91cc\u53bb \u4e86\uff0c\u5374\u504f\u504f\u8fd8\u8981\u5fcd\u4f4f\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u786e\u5b9e\u770b\u5230\u4e86\u4e00\u4ef6\u522b\u4eba\u90fd\u6ca1\u6709\u770b\u5230\u7684\u4e8b\uff0c\u56e0\u4e3a\u4ed6\u5750\u7684\u65b9\u5411\uff0c\u6b63\u597d\u5bf9\u7740\u5de6\u540e\u65b9\u7684\u4e00\u4e2a\u7a97 \u6237\uff0c\u8fd9\u4e2a\u7a97\u6237\u6070\u5de7\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u7a97\u5b50\u5916\u9762\uff0c\u5f53\u7136\u4e5f\u6709\u5353\u4e1c\u6765\u5e26\u6765\u7684\u4eba\u9a6c\uff0c\u53ef\u662f\u4ece\u5c0f\u9ad8\u5750\u7684\u8fd9\u4e2a\u89d2\u5ea6\u770b\u8fc7\u53bb\uff0c\u521a\u597d\u80fd \u4ece\u4eba\u9a6c\u5200\u7bad\u7684\u7a7a\u9699\u4e2d\u770b\u5230\u4e00\u68f5\u6811\u3002 \u3000\u3000\u4e00\u68f5\u5df2\u7ecf\u67af\u6b7b\u4e86\u7684\u5927\u767d\u6768\u6811\uff0c\u6811\u4e0b\u7ad9\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ece\u5c0f\u9ad8\u5750\u7684\u8fd9\u4e2a\u4f4d\u5b50\u4e0a\u770b\u8fc7\u53bb\uff0c\u521a\u597d\u53ef\u4ee5\u770b\u89c1\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u6c89\u9ed8\u5e73\u51e1\u7684\u4eba\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u53e3\u9648\u65e7\u5e73\u51e1\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u5c0f\u9ad8\u60f3\u51b2\u51fa\u53bb\uff0c\u6709\u597d\u51e0\u6b21\u90fd\u60f3\u51b2\u51fa\u53bb\uff0c\u53ef\u662f\u4ed6\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u51b3\u5b9a\u6027\u7684\u65f6\u5019\uff0c\u6240\u6709\u4eba\u7684\u751f\u6b7b\u547d\u8fd0\uff0c\u90fd\u5c06\u8981\u5728\u8fd9\u4e00\u77ac\u95f4\u51b3\u5b9a\uff0c \u4ed6\u505a\u7684\u4efb\u4f55\u4e00\u4ef6\u4e8b\uff0c\u90fd\u53ef\u80fd\u4f1a\u4f24\u5bb3\u5230\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u90a3\u4e2a\u63d0\u7740\u53e3\u7bb1\u5b50\u7ad9\u5728\u6811\u4e0b\u7684\u4eba\u4e5f\u4e0d\u8981\u8d70\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u5ffd\u7136\u53c8\u770b\u89c1\u4e00\u4ef6\u975e\u5e38\u5947\u602a\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u770b\u5230\u5353\u4e1c\u6765\u7b11\u4e86\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u4e00\u77ac\u95f4\u4ed6\u624d\u53d1\u73b0\uff0c\u5353\u4e1c\u6765\u7b11\u8d77\u6765\u7684\u65f6\u5019\u4e5f\u662f\u5f88\u8ff7\u4eba\u7684\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u5353\u4e1c\u6765\u5fae\u7b11\u7740\u7ad9\u8d77\u6765\uff0c\u7528\u4e00\u79cd\u65e0\u6bd4\u4f18\u96c5\u7684\u59ff\u6001\u5411\u6731\u731b\u5fae\u7b11\u97a0\u8eac\u3002 \u3000\u3000\u201c\u6731\u5802\u4e3b\uff0c\u6211\u4e0d\u518d\u656c\u4f60\u9152\u4e86\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6b64\u53bb\u683c\u9633\uff0c\u8def\u9014\u4ecd\u8fdc\uff0c\u559d\u5f97\u5927\u591a\u603b\u662f\u4e0d\u592a \u597d\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u6014\u4f4f\uff0c\u6731\u731b\u4e5f\u6014\u4f4f\u3002 \u3000\u3000\u201c\u4f60\u8ba9\u4ed6\u8d70\uff1f\u201d\u5c0f\u9ad8\u95ee\uff0c\u201c\u4f60\u771f\u7684\u80af\u8ba9\u4ed6\u8d70\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u4ed6\u80fd\u4ea4\u4f60\u8fd9\u4e2a\u670b\u53cb\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u4ed6\u80fd\u5192\u9669\u966a\u4f60\u5728\u8fd9\u91cc\u559d \u9152\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u4e3a\u4f60\u8ba9\u4ed6\u8d70\uff1f\u201d \u3000\u3000\u4ed6\u5c45\u7136\u8fd8\u4eb2\u81ea\u628a\u6731\u731b\u7684\u9a6c\u7275\u8fc7\u6765\uff1a\u201c\u6731\u5802\u4e3b\uff0c\u4ece\u6b64\u4e00\u522b\uff0c\u540e\u4f1a\u6709\u671f\uff0c\u6055\u6211\u4e0d\u80fd\u8fdc\u9001 \u4e86\u3002\u201d \u3000\u3000\u70df\u5c18\u6eda\u6eda\uff0c\u4e00\u5339\u9a6c\uff0c\u4e00\u6761\u9a6c\u5c3e\uff0c\u4e00\u53cc\u9489\u978b\u548c\u4e24\u4e2a\u4eba\u90fd\u5df2\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u5c0f\u9ad8\u76ee\u9001\u4ed6\u4eec\u8fdc\u53bb\uff0c\u624d\u56de\u8fc7\u5934\u9762\u5bf9\u5353\u4e1c\u6765\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u53f9\u606f\uff1a\u201c\u73b0\u5728\u6211\u624d\u76f8\u4fe1\u6c5f\u6e56\u4e2d\u4eba\u8bf4 \u7684\u4e0d\u5047\uff0c\u2018\u7d2b\u6c14\u4e1c\u6765\u2019\u5353\u4e1c\u6765\u679c\u7136\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u4e5f\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u53ef\u60dc\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u4ea4\u6211\u8fd9\u4e2a\u670b\u53cb\u7684\uff0c\u56e0\u4e3a\u4f60\u4e00\u5fc3\u53ea\u60f3\u6210\u540d\uff0c\u4e00\u5fc3 \u53ea\u60f3\u8981\u53f8\u9a6c\u8d85\u7fa4\u6b7b\u5728\u4f60\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u6c89\u9ed8\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u8bf4\uff1a\u201c\u6b7b\u7684\u4e5f\u8bb8\u4e0d\u662f\u4ed6\uff0c\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u6b7b\u7684\u5f88\u53ef\u80fd\u662f\u4f60\u3002\u201d\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff0c\u201c\u5982\u679c\u6709\u4eba\u8981\u8ddf\u6211\u6253\u8d4c\uff0c\u6211\u613f\u610f\u7528\u5341\u53bb \u535a\u4e00\uff0c\u8d4c\u4f60\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u770b\u7740\u5c0f\u9ad8\uff1a\u201c\u5982\u679c\u4f60\u8981\u8ddf\u6211\u8d4c\uff0c\u6211\u4e5f\u613f\u610f\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u613f\u610f\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8f93\u4e0b\u8d77\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5c0f\u9ad8\u5c31\u51b2\u4e86\u51fa\u53bb\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u53d1\u73b0\u521a\u624d\u8fd8\u7ad9\u5728\u6811\u4e0b\u7684\u90a3\u4e2a\u4eba\uff0c\u5ffd\u7136\u95f4\u53c8 \u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5c0f\u9ad8\u51b3\u5fc3\u8981\u8ffd\u4e0a\u4ed6\u3002 \u7b2c\u4e09\u7ae0\u3000\u5947\u88ad>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u4e09\u7ae0\u3000\u5947\u88ad \u4e00 \u3000\u3000\u6b63\u6708\u5341\u516d\u3002\u201d \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u6e05\u6668\uff0c\u9177\u5bd2\u3002 \u3000\u3000\u5353\u4e1c\u6765\u8d77\u5e8a\u65f6\uff0c\u53f8\u9a6c\u8d85\u7fa4\u5df2\u5728\u5c0f\u5385\u7b49\u7740\uff0c\u5c31\u5750\u5728\u90a3\u94fa\u7740\u7d2b\u8c82\u76ae\u7684\u6905\u5b50\u4e0a\uff0c\u7528\u6c34\u6676\u676f\u559d\u4ed6 \u7684\u8461\u8404\u9152\u3002 \u3000\u3000\u53ea\u6709\u53f8\u9a6c\u8d85\u7fa4\u4e00\u4e2a\u4eba\u53ef\u4ee5\u8fd9\u4e48\u505a\uff0c\u6709\u4e00\u5929\u6709\u4e00\u4e2a\u81ea\u5df1\u8ba4\u4e3a\u5353\u4e1c\u6765\u5df2\u7ecf\u79bb\u4e0d\u5f00\u5979\u7684\u5c11\u5973\uff0c \u521a\u5750\u4e0a\u8fd9\u5f20\u6905\u4e8e\uff0c\u5c31\u88ab\u8d64\u88f8\u88f8\u7684\u629b\u5728\u95e8\u5916\u7684\u79ef\u96ea\u91cc\u3002 \u3000\u3000\u5353\u4e1c\u6765\u6240\u6709\u7684\u4e00\u5207\uff0c\u90fd\u7edd\u4e0d\u5bb9\u4eba\u4fb5\u72af\uff0c\u53ea\u6709\u53f8\u9a6c\u8d85\u7fa4\u662f\u4f8b\u5916\u3002 \u3000\u3000\u4f46\u662f\u5353\u4e1c\u6765\u8fd8\u662f\u8ba9\u4ed6\u5728\u5916\u9762\u7b49\u4e86\u5f88\u4e45\uff0c\u624d\u62ab\u4e0a\u4ef6\u5bbd\u888d\u8d64\u7740\u811a\u8d70\u51fa\u5367\u623f\uff0c\u7b2c\u4e00\u53e5\u6d3b\u5c31\u95ee\u53f8 \u9a6c\uff1a\u201c\u8fd9\u4e48\u65e9\u4f60\u5c31\u6765\u4e86\uff0c\u662f\u4e0d\u662f\u6025\u7740\u8981\u95ee\u6211\u6628\u5929\u4e3a\u4ec0\u4e48\u653e\u8d70\u6731\u731b\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u53f8\u9a6c\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u6709\u5f88\u591a\u7406\u7531\uff0c\u53ef\u60dc\u6211\u8fde\u4e00\u70b9\u90fd\u60f3\u4e0d\u51fa\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u4e5f\u5750\u4e86\u4e0b\u53bb\uff0c\u5750\u5728\u4e00\u53e0\u67d4\u8f6f\u7684\u7d2b\u8c82\u4e4b\u4e0a\uff0c\u5e73\u65f6\uff0c\u4ed6\u5728\u53f8\u9a6c\u9762\u524d\uff0c\u6c38\u8fdc\u90fd\u662f\u8863\u51a0\u6574 \u8083\uff0c\u6001\u5ea6\u606d\u8c28\uff0c\u4ece\u672a\u4e0e\u53f8\u9a6c\u5e73\u8d77\u5e73\u5750\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u8981\u8ba9\u522b\u4eba\u611f\u89c9\u5230\u53f8\u9a6c\u8d85\u7fa4\u6c38\u8fdc\u90fd\u662f\u9ad8\u9ad8\u5728\u4e0a\u7684\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u5c4b\u5b50\u91cc\u53ea\u6709\u4ed6\u4eec\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u6211\u4e0d\u80fd\u6740\u6731\u731b\uff0c\u201d\u5353\u4e1c\u672a\u8bf4\uff1a\u201c\u7b2c\u4e00\uff0c\u56e0\u4e3a\u6211\u4e0d\u60f3\u6740\u4ed6\uff0c\u7b2c\u4e8c\uff0c\u56e0\u4e3a\u6211\u6ca1\u6709\u628a\u63e1\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u60f3\u6740\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5355\u4eba\u5339\u9a6c\uff0c\u95ef\u5165\u4e86\u6211\u4eec\u7684\u8179\u5730\uff0c\u4ece\u5bb9\u6325\u5200\u628a\u6211\u4eec\u7684\u5927\u5c06\u65a9\u6740\u4e8e\u9a6c\u524d\uff0c\u672c\u6765\u8fd8\u53ef\u4ee5\u626c \u957f\u800c\u53bb\u7684\uff0c\u53ea\u56e0\u4e3a\u8981\u966a\u4e00\u4e2a\u670b\u53cb\u559d\u9152\uff0c\u6240\u4ee5\u624d\u7559\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u90a3\u65f6\u6211\u82e5\u662f\u6740\u4e86\u4ed6\uff0c\u65e5\u540e\u6c5f\u6e56\u4e2d\u4eba\u4e00\u5b9a\u4f1a\u8bf4\u2018\u96c4\u72ee\u2019\u6731\u731b\u7684\u786e\u4e0d\u6127\u662f\u6761 \u597d\u6c49\uff0c\u591f\u670b\u53cb\uff0c\u8bb2\u4e49\u6c14\uff0c\u6709\u80c6\u91cf\u3002\u201d\u5353\u4e1c\u6765\u51b7\u7b11\uff1a\u201c\u6211\u6740\u4e86\u4ed6\u5c82\u975e\u53cd\u800c\u6210\u5168\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u51dd\u89c6\u7740\u6c34\u6676\u676f\u91cc\u7684\u9152\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u6709\u7406\u7531\u7684\uff0c\u4f46\u6211 \u5374\u60f3\u4e0d\u901a\u4f60\u600e\u4e48\u4f1a\u6ca1\u6709\u628a\u63e1\uff1f\u201d\u4ed6\u95ee\u5353\u4e1c\u6765\uff1a\u201c\u4f60\u5e26\u53bb\u7684\u597d\u624b\u4e0d\u5c11\uff0c\u8fd8\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u4eec\u4e09\u4e2a \u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4e09\u4e2a\u4eba\uff0c\u662f\u56db\u4e2a\u3002\u201d \u3000\u3000\u201c\u7b2c\u56db\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u770b\u89c1\uff0c\u4f46\u662f\u6211\u80fd\u611f\u89c9\u51fa\u4ed6\u5c31\u7ad9\u5728\u6211\u540e\u9762\u7684\u4e00\u6247\u7a97\u6237\u5916\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4ed6\u867d\u7136 \u8fdc\u8fdc\u7ad9\u5728\u7a97\u5916\uff0c\u4f46\u662f\u5728\u6211\u7684\u611f\u89c9\u4e2d\u5374\u597d\u50cf\u7d27\u8d34\u5728\u6211\u80cc\u540e\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u7684\u6740\u6c14\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u5e73\u751f\u4ece\u672a\u9047\u5230\u8fc7\u90a3\u4e48\u53ef\u6015\u7684\u6740\u6c14\u3002\u201d \u3000\u3000\u201c\u4f60\u6ca1\u6709\u56de\u5934\u53bb\u770b\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u3002\u56e0\u4e3a\u6211\u77e5\u9053\u4ed6\u4e00\u76f4\u5728\u76ef\u7740\u6211\uff0c\u597d\u50cf\u7279\u610f\u5728\u8b66\u544a\u6211\uff0c\u53ea\u8981\u6211\u6709\u4e00\u70b9\u52a8\u4f5c\uff0c\u65e0\u8bba \u4ec0\u4e48\u52a8\u4f5c\uff0c\u4ed6\u90fd\u53ef\u80fd\u4f1a\u51fa\u624b\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u8bf4\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u770b\u5230\u4ed6\uff0c\u53ef\u662f\u9ad8\u6e10\u98de\u4e00\u5b9a\u770b\u5230\u4ed6\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u90a3\u65f6\u9ad8\u6e10\u98de\u5c31\u5750\u5728\u6211\u5bf9\u9762\uff0c\u6b63\u597d\u5bf9\u7740\u90a3\u4e2a\u7a97\u53e3\uff0c\u6211\u611f\u89c9\u5230\u90a3\u80a1\u6740\u6c14\u65f6\uff0c\u9ad8\u6e10\u98de\u7684\u8138\u8272 \u4e5f\u53d8\u4e86\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u770b\u89c1\u4e86\u9b3c\u9b42\u4e00\u6837\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u9ad8\u6e10\u98de\u7edd\u5bf9\u53ef\u4ee5\u7b97\u662f\u8fd1\u5e74\u6765\u540e\u8d77\u5251\u5ba2\u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u5982\u679c\u6ca1\u6709\u7279\u522b\u7f18\u6545\uff0c \u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u4e00\u4e2a\u964c\u751f\u4eba\u5982\u6b64\u754f\u60e7\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5ffd\u7136\u7b11\u4e86\uff0c\u5927\u7b11\u3002 \u3000\u3000\u201c\u6240\u4ee5\u4f60\u4e5f\u6709\u70b9\u5bb3\u6015\u4e86\uff01\u201d\u4ed6\u7684\u7b11\u58f0\u4e2d\u7adf\u4f3c\u5145\u6ee1\u8ba5\u8bee\uff1a\u201c\u60f3\u4e0d\u5230\u7d2b\u6c14\u4e1c\u6765\u5353\u4e1c\u6765\u4e5f\u6709\u5bb3 \u6015\u7684\u65f6\u5019\uff0c\u6015\u7684\u7adf\u662f\u4e00\u4e2a\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u5230\u8fc7\u7684\u4eba\u3002\u201d \u3000\u3000\u5353\u4e1c\u672a\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u7b49\u4ed6\u7b11\u5b8c\u4e86\uff0c\u624d\u5e73\u5e73\u9759\u9759\u7684\u8bf4\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u770b\u89c1\u4ed6\uff0c\u53ef\u662f\u5374\u5df2 \u7ecf\u77e5\u9053\u4ed6\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u63a8\u5e7f\u53f8\u9a6c\u7684\u7b11\u58f0\u505c\u987f\uff1a\u201c\u96be\u9053\u4f60\u8ba4\u4e3a\u4ed6\u5c31\u662f\u90a3\u4e2a\u523a\u6740\u4e86\u6768\u575a\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e00\u5b9a\u662f\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u6781\u5c11\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\uff0c\u4e00\u5b9a\u548c\u6731\u731b\u6709\u79cd\u7279\u522b\u7684\u5173\u7cfb\uff0c\u4f46\u5374\u7edd\u4e0d\u662f\u6731\u731b \u7684\u624b\u4e0b\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u8fd9\u4e2a\u4eba\u7528\u7684\u4e00\u5b9a\u662f\u79cd\u4ece\u672a\u6709\u4eba\u89c1\u5230\u8fc7\u7684\u6781\u53ef\u6015\u7684\u6b66\u5668\uff0c\u53ef\u4ee5\u540c\u65f6\u53d1 \u51fa\u5f88\u591a\u79cd\u4e0d\u540c\u6b66\u5668\u7684\u5a01\u529b\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u5462\uff1f\u201d\u53f8\u9a6c\u95ee\u3002 \u3000\u3000\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u7684\u5c31\u53ea\u6709\u8fd9\u4e48\u591a\uff1f\u201d \u3000\u3000\u201c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u6211\u77e5\u9053\u7684\u5c31\u53ea\u6709\u8fd9\u4e48\u591a\u3002\u751a\u81f3\u8fde\u90a3\u79cd\u6b66\u5668\u662f\u4ec0\u4e48\u5f62\u72b6\u6211\u90fd\u60f3\u8c61\u4e0d\u51fa\u3002\u201d \u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u76f8\u4fe1\uff0c\u6211\u77e5\u9053\u7684\u8fd9\u4e9b\u5df2\u7ecf\u6bd4\u4efb\u4f55\u4eba\u90fd\u591a\u4e86\u3002\u201d \u3000\u3000\u53f8\u9a6c\u60f3\u7b11\uff0c\u5374\u6ca1\u6709\u7b11\u51fa\u6765\u3002 \u3000\u3000\u5353\u4e1c\u6765\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u66fe\u7ecf\u5171\u8fc7\u751f\u6b7b\u60a3\u96be\u7684\u597d\u670b\u53cb\uff0c\u5353\u4e1c\u6765\u4e5f\u662f\u4ed6\u6700\u5f97\u529b\u7684\u597d\u5e2e\u624b\u3002 \u3000\u3000\u53ef\u662f\u8c01\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5f53\u4ed6\u4eec\u4e24\u4eba\u5355\u72ec\u76f8\u5904\u65f6\uff0c\u4ed6\u603b\u662f\u8981\u548c\u5353\u4e1c\u6765\u9488\u950b\u76f8\u5bf9\uff0c\u603b\u597d \u50cf\u8981\u60f3\u5c3d\u65b9\u6cd5\u53bb\u523a\u4f24\u4ed6\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5374\u603b\u662f\u5b8c\u5168\u4e0d\u62b5\u6297\uff0c\u751a\u81f3\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u53c8\u559d\u4e86\u4e00\u676f\u8461\u8404\u9152\u4e4b\u540e\uff0c\u53f8\u9a6c\u5ffd\u7136\u53c8\u95ee\u5353\u4e1c\u6765\uff1a \u3000\u3000\u201c\u73b0\u5728\u5b59\u901a\u5df2\u7ecf\u6b7b\u4e86\uff0c\u90ed\u5e84\u5462\uff1f\u201d \u3000\u3000\u201c\u90ed\u5e84\u4e5f\u4e0d\u5728\u3002\u201d \u3000\u3000\u201c\u6628\u5929\u65e9\u4e0a\u6211\u8fd8\u770b\u89c1\u4ed6\u7684\uff0c\u4e3a\u4ec0\u4e48\u4eca\u5929\u65e9\u4e0a\u5c31\u4e0d\u5728\u4e86\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6628\u5929\u65e9\u4e0a\u6211\u5df2\u7ecf\u53eb\u4ed6\u8d76\u5230\u6d1b\u9633\u53bb\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e00\u542c\u5230\u6731\u731b\u5df2\u7ecf\u5230\u4e86\u7ea2\u82b1\u96c6\u7684 \u6d88\u606f\uff0c\u6211\u5c31\u53eb\u4ed6\u53bb\u4e86\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u8981\u4ed6\u6bcf\u8fc7\u4e94\u767e\u91cc\u5c31\u6362\u9a6c\u4e00\u6b21\uff0c\u663c\u591c\u517c\u7a0b\u7684\u8d76\u53bb\uff0c\u4e00\u5b9a\u8981\u5728\u6731\u731b\u56de\u5bb6\u7684\u524d \u4e00\u5929\u8d76\u5230\u6d1b\u9633\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7684\u773c\u775b\u91cc\u5ffd\u7136\u53d1\u51fa\u5149\uff0c\u5ffd\u7136\u95ee\uff0c\u201c\u4ed6\u4e00\u5b9a\u80fd\u53ca\u65f6\u8d76\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e00\u5b9a\u80fd\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u8d76\u4e0d\u5230\u5462\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff0c\u201c\u90a3\u4e48\u6211\u5c31\u53eb\u4ed6\u6b7b\u5728\u6d1b\u9633\uff0c\u4e0d\u5fc5\u518d\u56de\u6765\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5e76\u6ca1\u6709\u95ee\u5353\u4e1c\u6765\uff0c\u4e3a\u4ec0\u4e48\u8981\u4ee4\u90ed\u5e84\u8d76\u5230\u6d1b\u9633\u53bb\uff0c\u53bb\u5e72\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u4e0d\u5fc5\u95ee\u3002 \u3000\u3000\u961c\u4e1c\u6765\u7684\u8ba1\u5212\u548c\u884c\u52a8\u4ed6\u5df2\u5b8c\u5168\u4e86\u89e3\u3002 \u3000\u3000\u2014\u2014\u6731\u731b\u8f7b\u9a91\u8fdc\u51fa\uff0c\u624b\u4e0b\u7684\u5927\u5c06\u65e2\u7136\u6ca1\u6709\u8ddf\u6765\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u5728\u8def\u4e0a\u63a5\u5e94\uff0c\u5728\u6731\u731b\u8d76\u53e3\u53bb\u4e4b \u524d\uff0c\u201c\u96c4\u72ee\u5802\u201d\u5185\u90e8\u7684\u9632\u5b88\u5fc5\u5b9a\u8981\u6bd4\u5e73\u65f6\u5f31\u5f97\u591a\uff0c\u6b63\u662f\u4ed6\u4eec\u8d76\u53bb\u7a81\u88ad\u7684\u597d\u673a\u4f1a\u3002 \u3000\u3000\u2014\u2014\u53ea\u8981\u80fd\u628a\u63e1\u4f4f\u6700\u597d\u7684\u673a\u4f1a\uff0c\u4e00\u6b21\u5947\u88ad\u8fdc\u6bd4\u5341\u6b21\u82e6\u6218\u66f4\u6709\u6548\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u5353\u4e1c\u6765\u6700\u5e38\u7528\u7684\u6218\u7565\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u8ba1\u5212\u7684\u786e\u7cbe\u786e\u72e0\u8fa3\u4e0e\u5927\u80c6\uff0c\u4e5f\u6b63\u662f\u5353\u4e1c\u6765\u7684\u4e00\u8d2f\u4f5c\u98ce\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u53ea\u95ee\u5353\u4e1c\u6765\uff1a\u201c\u4f60\u53ea\u6d3e\u4e86\u90ed\u5e84\u4e00\u4e2a\u4eba\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u5728\u6d1b\u9633\u4e5f\u6709\u4eba\u624b\u3002\u201d\u5353\u4e1c\u672a\u8bf4\uff0c\u201c\u90ed\u5e84\u4e5f\u4e0d\u662f\u4e00\u4e2a\u4eba\u53bb\u7684\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u8c01\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u6728\u9e21\u3002\u201d \u3000\u3000\u201c\u6728\u9e21\uff1f\u201d\u53f8\u9a6c\u52a8\u5bb9\uff0c\u201c\u4f60\u6ca1\u6709\u6740\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e00\u5411\u662f\u975e\u5e38\u6709\u7528\u7684\u4eba\uff0c\u5bf9\u6211\u4eec\u4e5f\u4e00\u6837\u6709\u7528\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u6731\u731b\u6d3e\u6765\u6740\u6768\u575a\u7684\uff0c\u4e0d\u6015\u4ed6\u51fa\u5356\u6211\u4eec\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u4ed6\u8981\u6740\u7684\u5df2\u7ecf\u4e0d\u662f\u6768\u575a\uff0c\u800c\u662f\u6731\u731b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u77e5\u9053\u6731\u731b\u53ea\u4e0d\u8fc7\u60f3\u5229\u7528\u4ed6\u6765\u505a\u5e4c\u5b50\u800c\u5df2\uff0c\u800c\u4e14\u662f\u5b58\u5fc3\u8981\u4ed6\u672a\u9001\u6b7b\u7684\uff0c\u56e0\u4e3a \u6731\u731b\u65e9\u5c31\u7b97\u51c6\u4ed6\u7edd\u4e0d\u80fd\u5f97\u624b\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4ed6\u4e0d\u6015\u88ab\u4eba\u5229\u7528\uff0c\u53ef\u662f\u4ed6\u53d7\u4e0d\u4e86\u8fd9\u79cd\u4fae\u8fb1\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u8bf4\uff1a\u201c\u4f55\u51b5\u6211\u4ed8\u7ed9\u4ed6\u7684\u8fdc\u6bd4\u6731\u731b\u8fd8\u591a\u5f97\u591a\u3002\u201d \u3000\u3000\u53f8\u9a6c\u770b\u7740\u4ed6\uff0c\u773c\u91cc\u53c8\u9732\u51fa\u79cd\u5145\u6ee1\u8ba5\u8bee\u7684\u7b11\u610f\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6740\u6731\u731b\u4e86\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u4f60\u8981\u4ed6\u6d3b\u7740\u56de\u53bb\uff0c\u4f60\u8981\u4ed6\u4eb2\u773c\u770b\u5230 \u4f60\u7ed9\u4e86\u4ed6\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u60e8\u75db\u6559\u8bad\uff0c\u8981\u4ed6\u77e5\u9053\u4f60\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u4ed6\u770b\u7740\u5353\u4e1c\u6765\u5fae\u7b11\uff1a\u201c\u4f60\u4e00\u5411\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u603b\u662f\u8981\u8ba9\u522b\u4eba\u53c8\u6068\u4f60\u53c8\u6015\u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u662f\u8981\u6731\u731b\u5bb3\u6015\uff0c\u8981\u4ed6\u5bb3\u6015\u800c\u505a\u51fa\u4e0d\u53ef\u539f\u8c05\u7684\u9519\u4e8b\u548c\u7b28\u4e8b\u6765\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a \u201c\u53ea\u4e0d\u8fc7\u6211\u5e76\u4e0d\u662f\u8981\u4ed6\u6015\u6211\uff0c\u800c\u662f\u8981\u4ed6\u6015\u4f60\u3002\uff0c \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5f88\u67d4\u548c\uff1a\u201c\u9664\u4e86\u6211\u4eec\u81ea\u5df1\u4e4b\u5916\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u8fd9\u6b21\u884c\u52a8\u662f\u8c01\u4e3b\u6301\u7684\u3002\u201d \u3000\u3000\u53f8\u9a6c\u5374\u8df3\u4e86\u8d77\u6765\uff0c\u989d\u4e0a\u5df2\u6709\u4e00\u6839\u6839\u9752\u7b4b\u51f8\u8d77\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u77e5\u9053\u3002\u201d\u4ed6\u5927\u58f0\u8bf4\uff1a\u201c\u8981\u53d1\u8fd9\u79cd\u5927\u4e8b\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fde\u95ee\u90fd\u4e0d\u6765\u95ee\u6211\u4e00\u58f0\uff1f\u4e3a\u4ec0\u4e48 \u8981\u7b49\u5230\u4f60\u505a\u8fc7\u4e86\u4e4b\u540e\u624d\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u6001\u5ea6\u8fd8\u662f\u5f88\u5e73\u9759\uff0c\u7528\u4e00\u79cd\u5e73\u9759\u800c\u6e29\u67d4\u7684\u773c\u5149\u51dd\u89c6\u7740\u53f8\u9a6c\u8d85\u7fa4\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u505a\u7684\u4e0d\u662f\u8fd9\u79cd\u4e8b\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u8981\u4f60\u505a\u7684\u662f\u5927\u4e8b\uff0c\u8981\u4f60\u6210\u4e3a\u6c5f\u6e56\u4e2d\u7a7a\u524d\u672a \u6709\u7684\u82f1\u96c4\uff0c\u5b8c\u6210\u6b66\u6797\u4e2d\u7a7a\u524d\u672a\u6709\u7684\u9738\u4e1a\u3002\u201d \u3000\u3000\u53f8\u9a6c\u7d27\u63e1\u53cc\u62f3\uff0c\u77aa\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u63e1\u7d27\u7684\u53cc\u62f3\u4e5f\u653e\u677e\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u4eba\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u6162\u6162\u7684\u5411\u5916\u8d70\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u53c8\u95ee\u4ed6\uff1a\u201c\u9ad8\u6e10\u98de\u8fd8\u5728\u957f\u5b89\u9644\u8fd1\uff0c\u7b49\u7740\u4f60\u7ed9\u4ed6\u56de\u97f3\uff0c\u4f60\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u8ddf\u4ed6\u4ea4 \u624b\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8fde\u5934\u90fd\u6ca1\u6709\u56de\u3002 \u3000\u3000\u201c\u968f\u4fbf\u4f60\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u5ffd\u7136\u53d8\u5f97\u5f88\u51b7\u6de1\uff1a\u201c\u8fd9\u4e00\u7c7b\u7684\u4e8b\uff0c\u4f60\u4e00\u5b9a\u65e9\u5df2\u8ba1\u5212\u597d\u4e86\uff0c\u53cd\u6b63\u4e0d \u7ba1\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u4ea4\u624b\uff0c\u4ed6\u90fd\u8fde\u4e00\u70b9\u673a\u4f1a\u90fd\u6ca1\u6709\uff0c\u56e0\u4e3a\u4f60\u7edd\u4e0b\u4f1a\u7ed9\u4ed6\u4e00\u70b9\u673a\u4f1a\u7684\u3002\u201d \u3000\u3000\u53f8\u9a6c\u6de1\u6de1\u7684\u8bf4\uff0c\u201c\u6240\u4ee5\u8fd9\u4e00\u7c7b\u7684\u4e8b\u4f60\u4ee5\u540e\u4e5f\u4e0d\u5fc5\u56de\u6765\u95ee\u6211\u3002\u201d \u4e8c \u3000\u3000\u9ad8\u6e10\u98de\u9192\u6765\u65f6\uff0c\u624b\u3001\u811a\u6930\u5df2\u7ecf\u5feb\u8981\u88ab\u51bb\u50f5\u4e86\u3002 \u3000\u3000\u8fd9\u95f4\u5ec9\u4ef7\u5ba2\u6808\u7684\u6597\u5ba4\u91cc\uff0c\u672c\u6765\u8fd8\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u706b\u76c6\uff0c\u53ef\u662f\u73b0\u5728\u706b\u76c6\u91cc\u7684\u4e00\u70b9\u6728\u70ad\u65e9\u5df2\u70e7 \u5149\u4e86\u3002 \u3000\u3000\u4ed6\u8df3\u8d77\u672a\uff0c\u5728\u5e8a\u4e0a\u505a\u4e86\u516d\u4e03\u5341\u79cd\u5947\u602a\u7684\u59ff\u5f0f\uff0c\u4ed6\u7684\u8eab\u4f53\u5c31\u597d\u50cf\u4e00\u6839\u9762\u6761\u822c\u53ef\u4ee5\u968f\u7740\u4ed6\u7684 \u601d\u60f3\u4efb\u610f\u5f2f\u52a8\u626d\u66f2\uff0c\u505a\u5230\u7b2c\u5341\u4e00\u4e2a\u59ff\u5f0f\u65f6\uff0c\u4ed6\u5168\u8eab\u4e0a\u4e0b\u90fd\u5df2\u5f00\u59cb\u6e29\u6696\uff0c\u7b49\u4ed6\u505c\u4e0b\u7c73\u7684\u65f6\u5019\uff0c \u53ea\u89c9\u5f97\u81ea\u5df1\u7cbe\u795e\u632f\u594b\uff0c\u5bb9\u5149\u7115\u53d1\uff0c\u5fc3\u60c5\u4e5f\u6109\u5feb\u6781\u4e86\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u81ea\u5df1\u4eca\u5929\u4e00\u5b9a\u53ef\u4ee5\u89c1\u5230\u90a3\u4e2a\u63d0\u7740\u4e00\u53e3\u7bb1\u5b50\u7684\u4eba\u3002 \u3000\u3000\u6628\u5929\u79bb\u5f00\u90a3\u5bb6\u8336\u9986\u540e\uff0c\u4ed6\u53c8\u89c1\u5230\u8fc7\u8fd9\u4e2a\u4eba\u4e09\u6b21\uff0c\u4e00\u6b21\u662f\u5728\u4e00\u6761\u7ed3\u4e86\u51b0\u7684\u5c0f\u6cb3\u8fb9\uff0c\u4e00\u6b21\u662f \u5728\u5c71\u811a\u4e0b\uff0c\u4e00\u6b21\u662f\u5728\u957f\u5b89\u57ce\u91cc\u7684\u4e00\u6761\u964b\u5df7\u91cc\u3002 \u3000\u3000\u4ed6\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u867d\u7136\u4ed6\u76f4\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u770b\u6e05\u8fd9\u4e2a\u4eba\u7684\u8138\uff0c\u4f46\u662f\u90a3\u8eab\u7070\u6734\u7684\u68c9\u888d\u548c\u90a3\u53e3\u6697\u8910\u8272\u7684\u725b\u76ae\u7bb1 \u5b50\uff0c\u90fd\u662f\u7edd\u5bf9\u4e0d\u4f1a\u770b\u9519\u7684\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u6bcf\u6b21\u8d76\u8fc7\u53bb\u65f6\uff0c\u90a3\u4e2a\u4eba\u90fd\u5df2\u7ecf\u50cf\u7a7a\u6c14\u822c\u5ffd\u7136\u6d88\u5931\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u4e0d\u518d\u7ee7\u7eed\u8ffd\u4e0b\u53bb\u4e86\uff0c\u51b3\u5b9a\u5148\u56de\u6765\u597d\u597d\u7684\u7761\u4e00\u89c9\u518d\u8bf4\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u53d1\u73b0\u90a3\u4e2a\u4eba\u5e76\u4e0d\u662f\u4e0d\u60f3\u89c1\u4ed6\uff0c\u5426\u5219\u4e5f\u5c31\u4e0d\u4f1a\u6545\u610f\u5728\u4ed6\u9762\u524d\u51fa\u73b0\u4e09\u6b21\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u662f\u5728\u8bd5\u63a2\u4ed6\uff0c\u8bd5\u63a2\u4ed6\u7684\u6b66\u529f\uff0c\u8bd5\u63a2\u4ed6\u5bf9\u4ed6\u662f\u5426\u6709\u6076\u610f\u3002 \u3000\u3000\u5c0f\u9ad8\u76f8\u4fe1\u5982\u679c\u81ea\u5df1\u4e0d\u518d\u53bb\u627e\u4ed6\uff0c\u4ed6\u8fdf\u65e9\u8fd8\u662f\u4f1a\u9732\u9762\u7684\u3002 \u3000\u3000\u96ea\u867d\u7136\u5df2\u7ecf\u505c\u4e86\uff0c\u5929\u6c14\u5374\u66f4\u51b7\uff0c\u5c0f\u9ad8\u51b3\u5b9a\u5931\u53bb\u5403\u4e00\u7897\u70ed\u547c\u547c\u7684\u70ed\u6c64\u9762\u3002 \u3000\u3000\u4e00\u5230\u4e86\u4ed6\u5e38\u53bb\u7684\u90a3\u5bb6\u5c0f\u9762\u9986\uff0c\u5c0f\u9ad8\u679c\u7136\u5c31\u770b\u89c1\u4e86\u90a3\u4e2a\u4eba\u548c\u4ed6\u7684\u90a3\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u73b0\u5728\u8fd8\u6ca1\u6709\u5230\u5403\u5348\u996d\u7684\u65f6\u5019\uff0c\u5c0f\u9762\u9986\u91cc\u7684\u5ba2\u4eba\u8fd8\u4e0d\u591a\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5c31\u5750\u5728\u5c0f\u9ad8\u5e38\u5750\u7684\u4e00\u4e2a\u89d2\u843d\u91cc\uff0c\u9ed8\u9ed8\u7684\u5403\u7740\u4e00\u7897\u9762\uff0c\u5403\u7684\u4e5f\u662f\u5c0f\u9ad8\u5e38\u5403\u7684\u90a3\u79cd\u767d \u83dc\u6c64\u9762\u3002 \u3000\u3000\u4ed6\u7684\u7bb1\u4e88\u5c31\u6446\u5728\u4ed6\u7684\u624b\u8fb9\u3002\u6241\u6241\u7684\u4e00\u53e3\u7bb1\u5b50\uff0c\u6709\u4e00\u5c3a\u591a\u5bbd\uff0c\u4e24\u5c3a\u591a\u957f\u3002 \u3000\u3000\u2014\u2014\u8fd9\u53e3\u7bb1\u5b50\u91cc\u88c5\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff0c\u8fd9\u4e48\u5e73\u51e1\u7684\u4e00\u53e3\u7bb1\u5b50\uff0c\u600e\u4e48\u4f1a\u662f\u5929\u4e0b\u6700\u53ef\u6015\u7684\u6b66\u5668\uff1f \u3000\u3000\u5c0f\u9ad8\u5b9e\u5728\u5f88\u60f3\u51b2\u8fc7\u53bb\uff0c\u628a\u8fd9\u53e3\u7bb1\u4e8e\u62a2\u8fc7\u6765\uff0c\u6253\u5f00\u770b\u770b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5fcd\u4f4f\u4e86\u8fd9\u79cd\u51b2\u52a8\u3002 \u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u8fd9\u6b21\u4ed6\u603b\u7b97\u770b\u6e05\u695a\u8fd9\u4e2a\u4eba\u7684\u8138\u4e86\u3002 \u3000\u3000\u4e00\u5f20\u8721\u9ec4\u8272\u7684\u8138\uff0c\u4e00\u53cc\u9eef\u6de1\u65e0\u795e\u7684\u773c\u775b\uff0c\u4e00\u526f\u6709\u6c14\u65e0\u529b\u7684\u6837\u5b50\uff0c\u5c31\u597d\u50cf\u662f\u4e2a\u751f\u4e86\u5341\u516d\u516b \u5e74\u91cd\u75c5\u3001\u5df2\u7ecf\u75c5\u5f97\u5feb\u6b7b\u4e86\u7684\u4eba\u3002 \u3000\u3000\u9762\u9986\u867d\u7136\u8fd8\u6709\u5f88\u591a\u7a7a\u4f4d\uff0c\u5c0f\u9ad8\u5374\u8fd8\u662f\u786c\u7740\u5934\u76ae\u8d70\u8fc7\u53bb\uff0c\u5728\u8fd9\u4e2a\u4eba\u5bf9\u9762\u5750\u4e0b\u6765\uff0c\u5148\u53eb\u4e86\u4e00 \u7897\u9762\uff0c\u7136\u540e\u5c31\u7acb\u523b\u5bf9\u8fd9\u4e2a\u4eba\u8bf4\uff1a\u201c\u6211\u59d3\u9ad8\uff0c\u9ad8\u5c71\u6d41\u6c34\u7684\u9ad8\uff0c\u201d\u4ed6\u544a\u8bc9\u8fd9\u4e2a\u4eba\uff1a\u201c\u6211\u53eb\u9ad8\u6e10 \u98de\uff0c\u5c31\u662f\u6e10\u6e10\u5feb\u8981\u98de\u8d77\u6765\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\uff0c\u5c31\u597d\u50cf\u6839\u672c\u6ca1\u770b\u89c1\u5bf9\u9762\u5df2\u7ecf\u6709\u4e2a\u4eba\u5750\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u53e3\u6697\u8910\u8272\u7684\u725b\u76ae\u7bb1\u5b50\u5c31\u6446\u5728\u684c\u65c1\uff0c\u5c0f\u9ad8\u4e00\u4f38\u624b\u5c31\u53ef\u4ee5\u62ff\u5230\u3002 \u3000\u3000\u5982\u679c\u4ed6\u4f38\u624b\u62ff\u8d77\u8fd9\u53e3\u7bb1\u5b50\u8f6c\u8eab\u5c31\u8dd1\uff0c\u4f1a\u53d1\u751f\u4ec0\u4e48\u6837\u7684\u540e\u679c\uff1f \u3000\u3000\u5c0f\u9ad8\u4e0d\u6562\u8bd5\u3002 \u3000\u3000\u4ed6\u7684\u80c6\u5b50\u4e00\u5411\u4e0d\u5c0f\uff0c\u5929\u4e0b\u597d\u50cf\u6ca1\u6709\u51e0\u4ef6\u4ed6\u4e0d\u6562\u53bb\u505a\u7684\u4e8b\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e2a\u770b\u8d77\u6765\u597d\u50cf\u5df2\u7ecf\u75c5\u5f97\u5feb\u8981\u6b7b\u4e86\u7684\u4eba\uff0c\u5374\u597d\u50cf\u6709\u7740\u67d0\u79cd\u4eca\u4eba\u65e0\u6cd5\u89e3\u91ca\u800c\u4e14\u4e0d\u53ef\u601d \u8bae\u7684\u795e\u79d8\u529b\u91cf\uff0c\u8db3\u4ee5\u4f7f\u5f97\u4efb\u4f55\u4eba\u90fd\u4e0d\u6562\u5bf9\u4ed6\u751f\u51fa\u4e1d\u6beb\u5192\u6e0e\u4fb5\u72af\u4e4b\u610f\u3002 \u3000\u3000\u5c0f\u9ad8\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u538b\u4f4e\u58f0\u97f3\uff0c\u7528\u53ea\u80fd\u8ba9\u4ed6\u4e00\u4e2a\u4eba\u542c\u5230\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u662f \u4f60\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u6740\u6b7b\u6768\u575a\u7684\u4eba\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u7ec8\u4e8e\u62ac\u8d77\u5934\uff0c\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u90a3\u53cc\u9eef\u6de1\u65e0\u795e\u7684\u773c\u775b\u91cc\u5ffd\u7136\u6709\u5bd2\u5149\u4e00\u95ea\uff0c\u5c31\u597d\u50cf\u7070\u6697 \u5929\u7a7a\u4e2d\u5ffd\u7136\u6253\u4e0b\u6765\u7684\u4e00\u9053\u95ea\u7535\u4e00\u6837\u3002 \u3000\u3000\u53ef\u662f\u95ea\u7535\u4e4b\u540e\u5e76\u6ca1\u6709\u96f7\u58f0\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7acb\u523b\u53c8\u6062\u590d\u4e86\u4ed6\u90a3\u79cd\u6709\u6c14\u65e0\u529b\u7684\u6837\u5b50\uff0c\u9ed8\u9ed8\u6478\u51fa\u51e0\u6587\u94b1\u653e\u5728\u684c\u4e0a\uff0c\u9ed8\u9ed8\u7684\u63d0\u8d77\u4e86 \u7bb1\u5b50\uff0c\u9ed8\u9ed8\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5c0f\u9ad8\u7acb\u523b\u5c31\u8ddf\u7740\u8ffd\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u8fd9\u4e2a\u4eba\u5c45\u7136\u6ca1\u6709\u50cf\u4ee5\u524d\u90a3\u4e09\u6b21\u4e00\u6837\uff0c\u5ffd\u7136\u81ea\u7a7a\u6c14\u4e2d\u6d88\u5931\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u90fd\u5728\u524d\u9762\u8d70\uff0c\u800c\u4e14\u8d70\u5f97\u5f88\u6162\uff0c\u597d\u50cf\u751f\u6015\u5c0f\u9ad8\u8ffd\u4e0d\u4e0a\u4ed6\u3002 \u3000\u3000\u8d70\u4e86\u534a\u5929\u540e\uff0c\u5c0f\u9ad8\u5ffd\u7136\u53d1\u73b0\u4ed6\u53c8\u8d70\u5230\u6628\u5929\u66fe\u7ecf\u89c1\u8fc7\u4ed6\u7684\u90a3\u6761\u964b\u5df7\u91cc\u3002 \u3000\u3000\u964b\u5df7\u65e0\u4eba\uff0c\u662f\u6761\u8d70\u4e0d\u51fa\u53bb\u7684\u6b7b\u5df7\u5b50\u3002 \u3000\u3000\u5c0f\u9ad8\u7684\u5fc3\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u2014\u2014\u4ed6\u662f\u4e0d\u662f\u56e0\u4e3a\u6211\u5df2\u77e5\u9053\u4e86\u4ed6\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u624d\u628a\u6211\u5e26\u5230\u8fd9\u91cc\uff0c\u8981\u7528\u4ed6\u90a3\u53e3\u795e\u79d8\u7684\u7bb1\u5b50 \u628a\u6211\u6740\u4e86\u706d\u53e3\uff1f \u3000\u3000\u5c0f\u9ad8\u6839\u672c\u4e0d\u77e5\u9053\u8fd9\u53e3\u7bb1\u5b50\u7a76\u7adf\u662f\u79cd\u4ec0\u4e48\u6837\u7684\u6b66\u5668\uff0c\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u4e0d\u662f\u80fd\u7528\u638c\u4e2d\u7684\u5251\u62db \u67b6\u62b5\u6297\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\u4ed6\u5fc3\u91cc\u7adf\u5ffd\u7136\u89c9\u5f97\u6709\u79cd\u4ece\u6765\u672a\u66fe\u6709\u8fc7\u7684\u6050\u60e7\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4e2a\u4eba\u770b\u8d77\u6765\u5374\u4e0d\u50cf\u8981\u7cfb\u4eba\u7684\u6837\u5b50\uff0c\u4e5f\u4e0d\u50cf\u80fd\u591f\u7cfb\u4eba\u7684\u6837\u5b50\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u5c0f\u9ad8\uff0c\u8fc7\u4e86\u5f88\u4e45\u4e4b\u540e\uff0c\u624d\u7528\u4e00\u79cd\u5e73\u548c\u800c\u5636\u54d1\u7684\u58f0\u97f3\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60 \u77e5\u4e0d\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6b63\u6708\u5341\u4e94\u4e4b\u524d\u4f60\u6709\u6ca1\u6709\u89c1\u8fc7\u6211\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u6765\u50cf\u4e0d\u50cf\u662f\u4e2a\u4f1a\u6740\u4eba\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u50cf\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u770b\u8fc7\u6211\u6740\u4eba\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8981\u8bf4\u6211\u6740\u4e86\u6768\u575a\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u8fd9\u53e3\u7bb1\u5b50\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u53e3\u7bb1\u5b50\u662f\u79cd\u975e\u5e38\u795e\u79d8\u7684\u6b66\u5668\uff0c\u800c\u4e14\u975e\u5e38\u53ef \u6015\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u51dd\u89c6\u7740\u5c0f\u9ad8\u3002 \u3000\u3000\u5c0f\u9ad8\u7684\u773c\u8272\u3001\u795e\u6001\u3001\u7ad9\u7740\u7684\u59ff\u52bf\u3001\u547c\u5438\u7684\u9891\u7387\u3001\u8863\u670d\u7684\u8d28\u6599\u548c\u624b\u91cc\u7684\u7c97\u5e03\u5305\u88b1\uff0c\u5168\u8eab\u4e0a \u4e0b\u6bcf\u4e00\u4e2a\u5730\u65b9\u4ed6\u90fd\u6ca1\u6709\u653e\u8fc7\u3002 \u3000\u3000\u4ed6\u770b\u5f97\u597d\u50cf\u8fdc\u6bd4\u5353\u4e1c\u6765\u8fd8\u4ed4\u7ec6\uff0c\u4ed6\u90a3\u53cc\u7070\u9eef\u65e0\u795e\u7684\u773c\u775b\u91cc\u7adf\u597d\u50cf\u9690\u85cf\u7740\u67d0\u79cd\u7279\u5730\u5236\u9020\u51fa \u7529\u6765\u89c2\u5bdf\u522b\u4eba\u7684\u7cbe\u5bc6\u6697\u5668\u3002 \u3000\u3000\u7136\u540e\u53c8\u7528\u540c\u6837\u5e73\u548c\u7684\u58f0\u97f3\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60\u8bf4\u4f60\u7684\u540d\u5b57\u53eb\u9ad8\u6e10\u98de\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4ece\u5c71\u4e0a\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u4e00\u5ea7\u5f88\u9ad8\u7684\u5c71\uff1f\u201d\u4ed6\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60\u4f4f\u7684\u5730\u65b9\u662f\u4e0d\u662f\u6709\u4e00\u9053\u8bf7\u6cc9\uff0c\u4e00\u682a\u53e4\u677e\uff1f\u201d \u3000\u3000\u201c\u4f60\u8eab\u4e0a\u7a7f\u7684\u8fd9\u8eab\u8863\u670d\uff0c\u662f\u4e0d\u662f\u7528\u5c71\u540e\u6240\u4ea7\u7684\u68c9\u9ebb\u81ea\u5df1\u7eba\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u5df2\u7ecf\u5f00\u59cb\u89c9\u5f97\u5f88\u60ca\u5947\uff0c\u8fd9\u4e2a\u4eba\u5bf9\u4ed6\u7684\u4e8b\u77e5\u9053\u5f97\u7adf\u6bd4\u4efb\u4f55\u4eba\u90fd\u591a\u5f97\u591a\u3002 \u3000\u3000\u201c\u90a3\u5ea7\u5c71\u662f\u4e0d\u662f\u6709\u4e2a\u5f88\u559c\u6b22\u559d\u8336\u7684\u8001\u4eba\uff1f\u201d\u4ed6\u53c8\u95ee\u5c0f\u9ad8\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u7ecf\u5e38\u5750\u5728\u90a3\u68f5\u53e4\u677e \u4e0b\u7528\u90a3\u91cc\u7684\u6cc9\u6c34\u70f9\u8336\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u5c0f\u5546\u8bf4\uff1a\u201c\u6709\u5173\u4f60\u8fd9\u53e3\u7bb1\u5b50\u7684\u4e8b\uff0c\u5c31\u662f\u4ed6\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u6709\u6ca1\u6709\u544a\u8bc9\u4f60\u6709\u5173\u6211\u8fd9\u4e2a\u4eba\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u76ef\u7740\u5c0f\u9ad8\uff0c\u7070\u9eef\u7684\u773c\u91cc\u53c8\u6709\u5bd2\u5149\u4e00\u95ee\uff1a\u201c\u4ed6\u4ece\u6765\u4e5f\u6ca1\u6709\u63d0\u8d77\u8fc7\u6211\uff1f\u8fde\u4e00\u70b9\u6709\u5173\u6211 \u7684\u4e8b\u90fd\u6ca1\u6709\u63d0\u8d77\u8fc7\uff1f\u201d \u3000\u3000\u201c\u7edd\u5bf9\u6ca1\u6709\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u53ea\u4e0d\u8fc7\u544a\u8bc9\u6211\uff0c\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u6b66\u5668\u662f\u4e00\u53e3\u7bb1\u5b50\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u544a\u8bc9\u8fc7\u522b\u4eba\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u6709\u6ca1\u6709\u4eba\u77e5\u9053\u4f60\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u8bf4\uff1a\u201c\u5353\u4e1c\u6765\u66fe\u7ecf\u68c0\u67e5\u8fc7\u6211\u7684\u8863\u7269\uff0c\u60f3\u4ece\u6211\u8863\u670d\u7684\u8d28\u6599\u4e0a\u770b\u51fa\u6211\u662f\u4ece\u4ec0\u4e48\u5730\u65b9\u6765 \u7684\uff0c\u53ef\u60dc\u4ed6\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u51fa\u6765\u3002\u201d \u3000\u3000\u68c9\u9ebb\u662f\u4ed6\u81ea\u5df1\u79cd\u7684\uff0c\u5e03\u662f\u4ed6\u81ea\u5df1\u7ec7\u7684\uff0c\u8863\u88f3\u662f\u4ed6\u81ea\u5df1\u7f1d\u7684\uff0c\u90a3\u5ea7\u5c71\u662f\u5ea7\u4e0d\u77e5\u540d\u7684\u9ad8\u5c71\uff0c \u9664\u4e86\u4ed6\u4eec\u4e4b\u5916\uff0c\u8fd8\u6ca1\u6709\u51e1\u4eba\u7684\u8db3\u8ff9\u8e0f\u4e0a\u53bb\u8fc7\u3002 \u3000\u3000\u5c0f\u9ad8\u5fae\u7b11\uff1a\u201c\u5353\u4e1c\u6765\u5c31\u7b97\u6709\u5929\u5927\u7684\u672c\u4e8b\uff0c\u4e5f\u4f11\u60f3\u67e5\u51fa\u6211\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u5251\u5462\uff1f\u201d\u8fd9\u4e2a\u4eba\u95ee\uff1a\u201c\u6709\u6ca1\u6709\u4eba\u770b\u8fc7\u4f60\u7684\u5251\uff1f\u201d \u3000\u3000\u201c\u6709\u513f\u4e2a\u3002\u201d \u3000\u3000\u201c\u51e0\u4e2a\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u51e0\u4e2a\u6b7b\u4eba\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u770b\u8fc7\u6211\u8fd9\u67c4\u5251\u7684\u4eba\uff0c\u90fd\u5df2\u6b7b\u5728\u6211\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u67c4\u5251\u6709\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u6709\u7684\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u7279\u522b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u67c4\u5251\u7684\u5251\u810a\u4e0a\u6709\u4e00\u9053\u5f88\u5947\u602a\u7684\u75d5\u8ff9\uff0c\u770b\u8d77\u6765\u5c31\u597d\u50cf\u662f\u6cea\u75d5\u4e00\u6837\u3002\u201d \u3000\u3000\u63d0\u7740\u7bb1\u5b50\u7684\u8fd9\u4e2a\u4eba\uff0c\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u89e3\u91ca\u7684\u8868\u60c5\uff0c\u4eff\u4f5b\u5f88\u60b2\u4f24\uff0c\u53c8\u4eff\u4f5b\u5f88 \u6b22\u6109\u3002 \u3000\u3000\u201c\u6cea\u75d5\uff0c\u6cea\u75d5\uff0c\u539f\u6765\u4e16\u4e0a\u771f\u7684\u6709\u8fd9\u4e48\u4e00\u67c4\u5251\u3002\u201d\u4ed6\u5583\u5583\u7684\u8bf4\uff0c\u201c\u6740\u4eba\u7684\u5251\u4e0a\u4e3a\u4ec0\u4e48\u4f1a\u6709 \u6cea\u75d5\uff1f\u4e16\u4e0a\u4e3a\u4ec0\u4e48\u8981\u6709\u8fd9\u4e48\u6837\u4e00\u67c4\u5251\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u65e0\u6cd5\u56de\u7b54\u3002 \u3000\u3000\u8fd9\u672c\u6765\u5c31\u662f\u4e2a\u5f88\u5947\u5999\u7684\u95ee\u9898\uff0c\u4e5f\u8bb8\u6839\u672c\u5c31\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u3002 \u3000\u3000\u5c0f\u9ad8\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\u4ed6\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u53ef\u4ee5\u544a\u8bc9\u6211\uff0c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u6211\u7684\u4e8b\u4f60\u600e \u4e48\u4f1a\u77e5\u9053\u5f97\u8fd9\u4e48\u591a\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u95ed\u7740\u5634\uff0c\u4ec0\u4e48\u8bdd\u90fd\u4e0d\u8bf4\uff0c\u5374\u5ffd\u7136\u4ee5\u62c7\u6307\u5f39\u4e2d\u6307\uff0c\u53d1\u51fa\u6e05\u8106\u7684\u4e00\u58f0\u54cd\u3002 \u3000\u3000\u5c0f\u9ad8\u7acb\u523b\u5c31\u542c\u5230\u4e86\u4e00\u9635\u8f66\u8f6e\u6eda\u52a8\u548c\u9a6c\u8e44\u8e0f\u5730\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u8f6c\u8fc7\u5934\u53bb\u770b\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u6709\u4e00\u4fe9\u9ed1\u6f06\u9a6c\u8f66\u505c\u5728\u8fd9\u6761\u964b\u5df7\u5916\u3002 \u3000\u3000\u63d0\u7740\u7bb1\u5b50\u7684\u4eba\u5df2\u7ecf\u63d0\u7740\u4ed6\u7684\u7bb1\u5b50\u8d70\u8fc7\u53bb\uff0c\u6253\u5f00\u8f66\u95e8\uff0e\u5750\u5165\u8f66\u53a2\uff0c\u7136\u540e\u624d\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60\u4e0a \u4e0d\u4e0a\u672a\uff1f\u201d \u3000\u3000\u2014\u2014\u8fd9\u8f86\u9a6c\u8f66\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f \u3000\u3000\u5c0f\u9ad8\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u2014\u2014\u8fd9\u8f86\u9a6c\u8f66\u8981\u5f80\u54ea\u91cc\u53bb\uff1f \u3000\u3000\u5c0f\u9ad8\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0a\u53bb\u4e86\uff0c\u5c31\u7b97\u4ed6\u660e\u77e5\u8fd9\u4fe9\u9a6c\u8f66\u662f\u4ece\u5730\u72f1\u91cc\u6765\u7684\uff0c\u8981\u8f7d\u4ed6\u56de\u5730\u72f1\uff0c\u4ed6\u4e5f\u4e00\u6837\u4f1a\u4e0a \u53bb\u3002 \u4e09 \u3000\u3000\u8f66\u53a2\u91cc\u5bbd\u655e\u8212\u670d\u800c\u534e\u4e3d\uff0c\u8f66\u5b50\u8d70\u5f97\u6781\u5feb\u6781\u7a33\uff0c\u62c9\u8f66\u7684\u56db\u5339\u9a6c\u548c\u8d76\u8f66\u7684\u8f66\u592b\u5929\u7591\u90fd\u53d7\u8fc7\u826f \u597d\u7684\u8bad\u7ec3\uff0c\u8f66\u8f6d\u8f66\u8f6e\u548c\u8f66\u53a2\u4e5f\u65e0\u7591\u662f\u7279\u522b\u8bbe\u8ba1\u51fa\u6765\u7684\uff0c\u5c31\u7b97\u5728\u738b\u516c\u5de8\u5bcc\u7684\u8f66\u623f\u548c\u9a6c\u53a9\u91cc\uff0c\u4e5f \u672a\u5fc5\u6709\u8fd9\u4e48\u597d\u7684\u8f66\u9a6c\u3002 \u3000\u3000\u8fd9\u4e2a\u5e03\u8863\u7c97\u98df\u5bb9\u8c8c\u5e73\u51e1\u7684\u4eba\uff0c\u600e\u4e48\u4f1a\u62e5\u6709\u8fd9\u4e48\u6837\u4e00\u8f86\u534e\u8d35\u7684\u9a6c\u8f66\uff1f \u3000\u3000\u5c0f\u9ad8\u6709\u5f88\u591a\u95ee\u9898\u60f3\u95ee\u4ed6\uff0c\u4f46\u662f\u4ed6\u4e00\u4e0a\u4e86\u8f66\u5c31\u95ed\u8d77\u773c\u775b\uff0c\u4e00\u95ed\u4e0a\u9650\u5c31\u7761\u7740\u4e86\u3002 \u3000\u3000\u90a3\u53e3\u795e\u79d8\u7684\u7bb1\u5b50\uff0c\u5c31\u6446\u5728\u4ed6\u8eab\u8fb9\u7684\u5ea7\u4f4d\u4e0a\u3002 \u3000\u3000\u5c0f\u9ad8\u7684\u5fc3\u53c8\u52a8\u4e86\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u6211\u5077\u5077\u7684\u6253\u5f00\u6765\u770b\u770b\uff0c\u4e0d\u77e5\u9053\u4ed6\u4f1a\u600e\u4e48\u6837\uff1f\u6211\u53ea\u4e0d\u8fc7\u770b\u770b\u800c\u5df2\uff0c\u5c31\u7b97\u88ab\u4ed6\u53d1 \u73b0\uff0c\u5927\u6982\u4e5f\u6ca1\u4ec0\u4e48\u5173\u7cfb\u3002 \u3000\u3000\u8fd9\u4e2a\u8bf1\u60d1\u5b9e\u5728\u592a\u5927\uff0c\u5927\u5f97\u4ee4\u4eba\u96be\u4ee5\u6297\u62d2\u3002 \u3000\u3000\u5c0f\u9ad8\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u4f38\u51fa\u4e86\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6781\u4e3a\u7075\u5de7\uff0c\u800c\u4e14\u53d7\u5230\u8fc7\u6781\u4e25\u683c\u7684\u8bad\u7ec3\uff0c\u66fe\u7ecf\u5728\u4e00\u6b21\u8bd5\u9a8c\u4e2d\uff0c\u8fde\u7eed\u4e0d\u505c\u7684\u6253\u5f00\u4e86\u5206 \u522b\u7531\u5341\u4e00\u4f4d\u540d\u5320\u6253\u9020\u7684\u4e09\u5341\u628a\u597d\u9501\u3002 \u3000\u3000\u90a3\u4e9b\u9501\u522b\u4eba\u5c31\u7b97\u6709\u94a5\u5319\u4e5f\u5f88\u96be\u6253\u5f00\uff0c\u4ed6\u7528\u7684\u5374\u53ea\u4e0d\u8fc7\u662f\u4e00\u6839\u94c1\u4e1d\u3002 \u3000\u3000\u7bb1\u5b50\u4e0a\u7684\u673a\u7c27\uff0c\u5f88\u5feb\u5c31\u88ab\u4ed6\u627e\u5230\uff0c\u53ea\u542c\u201c\u683c\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u673a\u7c27\u5df2\u88ab\u62e8\u5f00\u3002 \u3000\u3000\u7bb1\u5b50\u7684\u4e3b\u4eba\u4ecd\u5728\u6c89\u7761\u3002 \u3000\u3000\u2014\u2014\u7bb1\u5b50\u91cc\u7a76\u7adf\u6709\u4e9b\u4ec0\u4e48\u4e1c\u897fj\u4e3a\u4ec0\u4e48\u4f1a\u662f\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u6b66\u5668\uff1f \u3000\u3000\u8fd9\u4e2a\u79d8\u5bc6\u7ec8\u4e8e\u8981\u63ed\u9732\u4e86\uff0c\u5c0f\u9ad8\u7684\u5fc3\u8df3\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u7684\u6162\u4f46\u7684\u6380\u8d77\u76d6\u5b50\uff0c\u7bb1\u5b50\u91cc\u88c5\u7740\u7684\u597d\u50cf\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e9b\u5f62\u72b6\u5947\u7279\u7684\u94c1\u7ba1\u548c\u94c1\u4ef6\u800c \u5df2\u3002\u5927\u6982\u6709\u5341\u4e09\u56db\u4ef6\uff0c\u6bcf\u4e00\u4ef6\u7684\u5f62\u5f0f\u548c\u5927\u5c0f\u90fd\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u53ef\u60dc\u5c0f\u9ad8\u5e76\u6ca1\u6709\u770b\u6e05\u695a\u3002 \u3000\u3000\u7bb1\u5b50\u4e00\u6253\u5f00\uff0c\u4ed6\u5c31\u5ffd\u7136\u55c5\u5230\u4e00\u79cd\u6de1\u6de1\u7684\u597d\u50cf\u6845\u5b50\u82b1\u4e00\u6837\u7684\u9999\u6c14\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u6655\u4e86\u8fc7\u53bb\u3002 \u7b2c\u56db\u7ae0\u3000\u5947\u4eba\u5947\u5730\u5947\u4e8b>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u56db\u7ae0\u3000\u5947\u4eba\u5947\u5730\u5947\u4e8b \u4e00 \u3000\u3000\u6b63\u6708\u5341\u516b\u65e5\u3002 \u3000\u3000\u4e00\u4e2a\u4efb\u4f55\u4eba\u90fd\u4e0d\u5982\u9053\u662f\u4ec0\u4e48\u5730\u65b9\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e00\u4ef6\u5f62\u72b6\u65e2\u4e0d\u89c4\u5219\u4e5f\u4e0d\u5b8c\u6574\u7684\u94c1\u4ef6\uff0c\u600e\u4e48\u4f1a\u662f\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6700\u53ef\u6015\u7684\u6b66\u5668\uff1f \u3000\u3000\u5c0f\u9ad8\u8fd8\u6ca1\u5b8c\u5168\u6e05\u9192\uff0c\u53ef\u662f\u8fd9\u4e2a\u95ee\u9898\u5374\u4e00\u76f4\u50cf\u662f\u6761\u6bd2\u86c7\u822c\u76d8\u636e\u5728\u4ed6\u5fc3\u91cc\u3002 \u3000\u3000\u7b49\u4ed6\u5b8c\u5168\u6e05\u9192\u65f6\uff0c\u4ed6\u5c31\u7acb\u523b\u88ab\u773c\u524d\u770b\u5230\u7684\u666f\u8c61\u5413\u5446\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u6765\u5230\u4e86\u4e00\u4e2a\u53ea\u6709\u5728\u6700\u8352\u5510\u79bb\u5947\u7684\u68a6\u5883\u4e2d\u624d\u4f1a\u51fa\u73b0\u7684\u5730\u65b9\u3002 \u3000\u3000\u8fd9\u5730\u65b9\u4eff\u4f5b\u662f\u5c71\u8179\u91cc\u7684\u4e00\u4e2a\u6d1e\u7a9f\uff0c\u5c0f\u9ad8\u7edd\u5bf9\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u65e0\u8bba\u8c01\u5230\u4e86\u8fd9\u91cc\uff0c\u90fd\u4f1a\u50cf\u4ed6\u4e00 \u6837\uff0c\u88ab\u8fd9\u4e2a\u6d1e\u7a9f\u8ff7\u4f4f\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u770b\u5230\u8fc7\u4efb\u4f55\u4e00\u4e2a\u5730\u65b9\u6709\u8fd9\u4e48\u4ee4\u4eba\u60ca\u5947\u8ff7\u60d1\u7684\u4e1c\u897f\u3002 \u3000\u3000\u4ece\u6ce2\u65af\u6765\u7684\u6c34\u6676\u706f\uff0c\u9ad8\u9ad8\u540a\u5728\u4e00\u4e9b\u5149\u602a\u9646\u79bb\u8272\u5f69\u6591\u70c2\u7684\u5de8\u5927\u949f\u4e73\u95f4\uff0c\u5730\u4e0a\u94fa\u6ee1\u4e86\u624b\u5de5\u7cbe \u7ec6\u56fe\u6848\u5947\u7f8e\u7684\u5730\u6bef\uff0c\u56db\u58c1\u7684\u6728\u67b6\u4e0a\u9648\u5217\u7740\u5404\u5f0f\u5404\u6837\u7684\u5947\u95e8\u6b66\u5668\uff0c\u6709\u51e0\u79cd\u5c0f\u9ad8\u975e\u4f46\u6ca1\u6709\u89c1\u8fc7\uff0c \u8fde\u542c\u90fd\u6ca1\u6709\u542c\u8fc7\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4e08\u4f59\u9ad8\u7684\u73ca\u745a\uff0c\u51e0\u5c3a\u957f\u7684\u8c61\u7259\uff0c\u7528\u65e0\u6687\u7f8e\u7389\u96d5\u6210\u7684\u767d\u9a6c\uff0c\u7528\u78a7\u7eff\u7fe1\u7fe0\u548c \u8d64\u7ea2\u739b\u7459\u5851\u6210\u7684\u82b1\u6728\u548c\u679c\u83dc\uff0c\u7528\u66b9\u7f57\u9ec4\u91d1\u94f8\u6210\u7684\u5de8\u5927\u4f5b\u50cf\uff0c\u4f5b\u50cf\u4e0a\u8fd8\u6302\u6ee1\u4e86\u4e00\u7533\u4e32\u6676\u83b9\u5706\u6da6 \u5927\u5982\u9f99\u773c\u822c\u7684\u73cd\u73e0\u3002 \u3000\u3000\u53e6\u5916\u4e00\u5f20\u5927\u6848\u4e0a\u6446\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u91d1\u6a3d\u7389\u7235\u548c\u6c34\u6676\u74f6\uff0c\u6ee1\u76db\u7740\u4ea7\u81ea\u5929\u4e0b\u5404\u5730\u7684\u7f8e\u9152\u3002 \u3000\u3000\u56db\u4e94\u4e2a\u8eab\u7a7f\u8749\u7ffc\u822c\u8584\u7eb1\u7684\u7edd\u8272\u7f8e\u5973\uff0c\u6b63\u7ad9\u5728\u5c0f\u9ad8\u8eba\u7740\u7684\u8f6f\u69bb\u8fb9\uff0c\u770b\u7740\u5c0f\u9ad8\u5403\u5403\u7684\u7b11\uff0c\u5176 \u4e2d\u6709\u4e00\u4e2a\u91d1\u53d1\u78a7\u773c\u3001\u76ae\u80a4\u6bd4\u96ea\u8fd8\u767d\u7684\u5973\u5b69\u5b50\uff0c\u7b11\u5f97\u6700\u5929\u771f\uff0c\u53e6\u5916\u4e00\u4e2a\u76ae\u80a4\u5374\u60ac\u6df1\u8910\u8272\u7684\uff0c\u5c31 \u50cf\u662f\u8910\u8272\u7684\u7f0e\u5b50\u4e00\u6837\uff0c\u67d4\u8f6f\u5149\u6ed1\uff0c\u83b9\u83b9\u751f\u5149\u3002 \u3000\u3000\u5c0f\u9ad8\u5df2\u7ecf\u5b8c\u5168\u88ab\u8ff7\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u6b66\u5668\uff0c\u8fd9\u4e9b\u73cd\u5b9d\uff0c\u8fd9\u4e9b\u7f8e\u4eba\uff0c\u90fd\u4e0d\u662f\u51e1\u4eba\u6240\u80fd\u89c1\u5230\u7684\u3002 \u3000\u3000\u96be\u9053\u8fd9\u4e2a\u5730\u65b9\u5df2\u4e0d\u5728\u4eba\u95f4\uff1f \u3000\u3000\u5982\u679c\u8fd9\u91cc\u5c31\u662f\u5730\u72f1\uff0c\u90a3\u4e48\u8fd9\u4e2a\u4e16\u754c\u4e5f\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u4eba\u613f\u610f\u4e0b\u5730\u72f1\u4e86\u3002 \u4e8c \u3000\u3000\u2014\u2014\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f \u3000\u3000\u5973\u5b69\u5b50\u4eec\u53ea\u7b11\uff0c\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u5c0f\u9ad8\u60f3\u7ad9\u8d77\u6765\uff0c\u5374\u5df2\u7ecf\u88ab\u4e00\u4e2a\u5c0f\u5de7\u5982\u9999\u6247\u5760\u7684\u5973\u5b69\u5b50\u6309\u4f4f\u4e86\u4ed6\u7684\u80a9\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u78b0\u8fd9\u4e2a\u5973\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u5e76\u4e0d\u662f\u4e00\u4e2a\u7ecf\u5e38\u90fd\u80fd\u591f\u6297\u62d2\u8bf1\u611f\u7684\u4eba\u3002 \u3000\u3000\u6700\u8ba9\u4eba\u53d7\u4e0d\u4e86\u7684\u662f\uff0c\u90a3\u4e2a\u91d1\u53d1\u78a7\u773c\u7684\u5973\u5b69\u5b50\uff0c\u5c45\u7136\u6367\u4f4f\u4e86\u4ed6\u7684\u8138\uff0c\u5bf9\u7740\u4ed6\u8033\u6735\u8f7b\u8f7b\u5439 \u6c14\u3002 \u3000\u3000\u5c0f\u9ad8\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4f53\u5df2\u7ecf\u5feb\u8981\u6709\u53d8\u5316\u4e86\uff0c\u5f88\u4e0d\u96c5\u89c2\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u5ffd\u7136\u5f2f\u66f2\uff0c\u4ece\u4e00\u4e2a\u4efb\u4f55\u4eba\u90fd\u60f3\u4e0d\u5230\u7684\u90e8\u4f4d\u5f80\u4e00\u4e2a\u4efb\u4f55\u4eba\u90fd\u60f3\u4e0d\u5230\u7684\u65b9\u5411\u5f2f\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u6309\u4f4f\u4ed6\u80a9\u3001\u6367\u4f4f\u4ed6\u8138\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\uff0c\u53ea\u89c9\u5f97\u624b\u4e00\u6ed1\uff0c\u88ab\u5979\u4eec\u6309\u4f4f\u6367\u4f4f\u7684\u4eba\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff0c \u518d\u56de\u5934\u53bb\u627e\u65f6\uff0c\u624d\u53d1\u73b0\u4ed6\u5df2\u7ecf\u8eb2\u5230\u5f88\u8fdc\u7684\u4e00\u4e2a\u9ec4\u91d1\u4f5b\u50cf\u540e\u9762\u3002 \u3000\u3000\u201c\u4f60\u4eec\u5343\u4e07\u4e0d\u8981\u8fc7\u6765\u3002\u201d\u5c0f\u9ad8\u5927\u58f0\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u4eba\u5e76\u4e0d\u662f\u4e2a\u597d\u4eba\uff0c\u4f60\u4eec\u5982\u679c\u771f\u6562\u8fc7\u6765\uff0c \u6211\u5c31\u771f\u7684\u8981\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u4ed6\u771f\u7684\u6709\u70b9\u6015\u8fd9\u4e9b\u5973\u5b69\u5b50\uff0c\u4f46\u662f\u5979\u4eec\u5982\u679c\u771f\u7684\u8fc7\u53bb\u4e86\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u592a\u96be\u8fc7\u7684\uff0c\u4e5f\u4e0d\u4f1a \u88ab\u5413\u6b7b\u3002 \u3000\u3000\u53ef\u60dc\u5979\u4eec\u90fd\u6ca1\u6709\u8fc7\u53bb\uff0c\u8fde\u4e00\u4e2a\u90fd\u6ca1\u6709\u8fc7\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u8fd9\u4e2a\u5730\u65b9\u7684\u4e3b\u4eba\u5df2\u7ecf\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u82f1\u633a\u7626\u524a\u3001\u8eab\u6750\u5f88\u9ad8\u7684\u4eba\uff0c\u968f\u968f\u4fbf\u4fbf\u7684\u7a7f\u7740\u4ef6\u9ed1\u5f97\u53d1\u4eae\u7684\u9ed1\u4e1d\u957f\u888d\uff0c\u8ba9\u4e00\u5934\u6f06\u9ed1\u7684 \u957f\u53d1\u968f\u968f\u4fbf\u4fbf\u7684\u62ab\u6563\u5728\u80a9\u8180\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u7a7f\u7740\u867d\u7136\u968f\u4fbf\uff0c\u53ef\u662f\u4ed6\u8fd9\u4e2a\u4eba\u770b\u8d77\u6765\u5374\u5982\u540c\u5e1d\u738b\u3002 \u3000\u3000\u5c24\u5176\u662f\u4ed6\u7684\u8138\u3002 \u3000\u3000\u4ed6\u7684\u8138\u8f6e\u5ed3\u6781\u5206\u660e\uff0c\u7ebf\u6761\u6781\u660e\u663e\u3002 \u3000\u3000\u4ed6\u7684\u8138\u8272\u82cd\u767d\uff0c\u5b8c\u5168\u6ca1\u6709\u4e00\u70b9\u8840\u8272\uff0c\u5c31\u50cf\u662f\u7528\u4e00\u5757\u96ea\u767d\u7684\u5927\u7406\u77f3\u96d5\u51fa\u6765\u7684\uff0c\u5e26\u7740\u79cd\u65e0\u6cd5 \u5f62\u5bb9\u7684\u51b7\u6f20\u548c\u9ad8\u8d35\u3002 \u3000\u3000\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u5973\u5b69\u5b50\u4eec\u7acb\u523b\u5168\u90fd\u76c8\u76c8\u62dc\u5012\uff0c\u5c0f\u9ad8\u7acb\u523b\u5927\u58f0\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u5c31\u662f\u8fd9\u91cc \u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u6211\u65e2\u4e0d\u8ba4\u5f97\u4f60\uff0c\u4f60\u4e5f\u4e0d\u8ba4\u5f97\u6211\uff0c\u4f60\u628a\u6211\u5f04\u5230\u8fd9\u91cc\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d\u5c0f\u9ad8\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u6839\u672c\u5c31\u6ca1\u6709\u8981\u4f60\u6765\uff0c\u662f\u4f60\u81ea\u5df1\u8981\u8ddf\u7740\u6211\u6765\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u6014\u4f4f\uff0c\u6014\u4e86\u534a\u5929\u624d\u5f00\u53e3\u3002 \u3000\u3000\u201c\u662f\u6211\u81ea\u5df1\u8981\u8ddf\u7740\u4f60\u6765\u7684\uff0c\u96be\u9053\u4f60\u5c31\u662f\u90a3\u4e2a\u63d0\u7740\u53e3\u7bb1\u5b50\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7528\u624b\u62b1\u4f4f\u5934\uff0c\u597d\u50cf\u9a6c\u4e0a\u5c31\u8981\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u5e03\u8863\u673a\u98df\u5bb9\u8c8c\u5e73\u51e1\u7684\u4eba\uff0c\u7adf\u5ffd\u7136\u5947\u8ff9\u822c\u53d8\u6210\u4e86\u4e00\u4f4d\u5e1d\u738b\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u672c\u6765\u53ea\u6709\u5728\u795e\u8bdd\u4e2d\u624d\u4f1a\u53d1\u751f\u7684\uff0c\u5374\u504f\u504f\u88ab\u5c0f\u9ad8\u5728\u65e0\u610f\u95f4\u9047\u5230\u3002 \u3000\u3000\u201c\u4f60\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d\u5c0f\u9ad8\u4ece\u4f5b\u50cf\u540e\u8d70\u51fa\u6765\uff1a\u201c\u662f\u4e2a\u950b\u8292\u4e0d\u9732\u3001\u63d0\u7740\u53e3\u7bb1\u5b50\u6d41\u6d6a \u5929\u6daf\u7684\u523a\u5ba2\uff1f\u8fd8\u662f\u4e2a\u8fdc\u907f\u7ea2\u5c18\u5bcc\u903e\u738b\u4faf\u7684\u9690\u58eb\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u95ee\u4ed6\uff1a\u201c\u8fd9\u4e24\u79cd\u4eba\u662f\u5b8c\u5168\u4e0d\u540c\u7684\uff0c\u7a76\u7adf\u54ea\u4e00\u79cd\u624d\u662f\u4f60\u7684\u771f\u9762\u76ee\uff1f\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u4f60\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d\u4ed6\u53cd\u95ee\u5c0f\u9ad8\uff1a\u201c\u662f\u4e2a\u5bf9\u4eba\u4e16\u95f4\u6bcf\u4ef6\u4e8b\u90fd\u89c9\u5f97\u597d\u5947\u7684 \u70ed\u8840\u5c11\u5e74\uff1f\u8fd8\u662f\u4e2a\u89c6\u4eba\u547d\u5982\u8349\u82a5\u7684\u65e0\u60c5\u5251\u5ba2\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u4e2a\u5b66\u5251\u7684\u4eba\uff0c\u4e00\u4e2a\u4eba\u5982\u679c\u8981\u5b66\u5251\uff0c\u5c31\u5e94\u8be5\u732e\u8eab\u4e8e\u5251\uff0c\u867d\u6b7b\u65e0\u61be\u3002\u201d\u5c0f\u9ad8\u53c8\u95ee\u4ed6\uff1a \u201c\u4f60\u5462\uff1f\u4f60\u6740\u4eba\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u662f\u4e3a\u4e86\u94b1\u8d22\uff1f\u8fd8\u662f\u56e0\u4e3a\u4f60\u6740\u4eba\u65f6\u89c9\u5f97\u5f88\u6109\u5feb\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u51dd\u89c6\u7740\u4ed6\uff1a\u201c\u4e00\u4e2a\u4eba\u77e5\u9053\u81ea\u5df1\u80fd\u4e3b\u5bb0\u522b\u4eba\u7684\u751f\u6b7b\u65f6\uff0c\u662f\u4e0d\u662f\u4f1a\u89c9\u5f97\u5f88\u6109\u5feb\uff1f\u201d \u3000\u3000\u9ed1\u888d\u4eba\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u8d70\u5230\u5927\u6848\u524d\uff0c\u4ece\u4e00\u4e2a\u6c34\u6676\u6a3d\u91cc\u5012\u4e86\u676f\u9152\uff0c\u6162\u6162\u7684\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7136\u540e\u4ed6\u624d\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u5bf9\u6211\u6765\u8bf4\uff0c\u8fd9\u5df2\u7ecf\u4e0d\u662f\u6109\u5feb\u7684\u4e8b\u4e86\uff0c\u53ea\u53ef\u60dc\u6211\u4e5f\u50cf\u8fd9\u4e16\u4e0a\u5927\u591a\u6570 \u4eba\u4e00\u6837\uff0c\u4e5f\u4f1a\u53bb\u505a\u4e00\u4e9b\u81ea\u5df1\u672c\u672a\u5e76\u4e0d\u60f3\u505a\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u6b21\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u6768\u575a\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u6731\u731b\uff0c\u56e0\u4e3a\u6211\u6b20\u4ed6\u4e00\u6761\u547d\u3002\u201d \u3000\u3000\u201c\u8c01\u7684\u547d\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u6731\u731b\u6551\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u201c\u6bcf\u4e2a\u4eba\u90fd\u96be\u514d\u4f1a\u6709\u5371\u9669\u56f0\u96be\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u4e0d\u4f8b\u5916\u3002\u201d\u9ed1\u8863\u4eba\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u5c06\u6765\u4f60\u4e5f\u4f1a \u6709\u8fd9\u79cd\u65f6\u5019\u7684\uff0c\u53ef\u662f\u4f60\u6c38\u8fdc\u90fd\u65e0\u6cd5\u9884\u6599\u90a3\u65f6\u662f\u8c01\u4f1a\u53bb\u6551\u4f60\uff0c\u5c31\u6b63\u5982\u73b0\u5728\u4f60\u4e5f\u4e0d\u77e5\u9053\u5c06\u6765\u4f1a\u6709 \u4e9b\u4ec0\u4e48\u4eba\u8981\u6b7b\u5728\u4f60\u624b\u91cc\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u6b7b\u5728\u6211\u7684\u624b\u91cc\uff0c\u662f\u6b7b\u5728\u6211\u7684\u5251\u4e0b\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6b7b\u5728\u6211\u5251\u4e0b\u7684\u4eba\uff0c\u90fd\u65e9\u5df2\u628a\u6027\u547d \u732e\u8eab\u4e8e\u5251\uff0c\u5c31\u50cf\u6211\u4e00\u6837\uff0c\u5982\u679c\u6211\u6b7b\u5728\u4ed6\u4eec\u7684\u5251\u4e0b\uff0c\u6211\u6b7b\u800c\u65e0\u6028\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5ffd\u7136\u4ece\u58c1\u67b6\u4e0a\u53d6\u4e0b\u4e00\u67c4\u5f62\u5f0f\u5947\u53e4\u7684\u957f\u5251\uff0c\u51b7\u51b7\u7684\u770b\u7740\u5c0f\u9ad8\uff1a\u201c\u5982\u679c\u73b0\u5728\u6211\u5c31\u7528\u8fd9 \u67c4\u5251\u6740\u4e86\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u5c31\u4f1a\u89c9\u5f97\u5f88\u9057\u61be\u4e86\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u56e0\u4e3a\u73b0\u5728\u6211\u8fde\u4f60\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u7684\u5df2\u7ecf\u591f\u591a\u4e86\uff0c\u5df2\u7ecf\u591a\u5f97\u8db3\u591f\u8ba9\u6211\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u5df2\u7ecf\u77e5\u9053\u6211\u6740\u4e86\u6768\u575a\uff0c\u5df2\u7ecf\u5077\u5077\u7684\u770b\u8fc7\u4e86\u6211\u90a3\u53e3\u7bb1\u5b50\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u51fa\u6765\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u8fd8\u662f\u60f3\u4e0d\u901a\u90a3\u600e\u4e48\u4f1a\u662f\u5929\u4e0b\u6700\u53ef\u6015\u7684\u6b66 \u5668\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u975e\u5e38\u60f3\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5ffd\u7136\u62d4\u5251\uff0c\u51b7\u68ee\u68ee\u7684\u5251\u6c14\u7acb\u523b\u903c\u4eba\u7709\u776b\u800c\u6765\uff0c\u95ea\u52a8\u7684\u5251\u5149\u7adf\u662f\u78a7\u7eff\u8272\u7684\u3002 \u3000\u3000\u201c\u8fd9\u67c4\u5251\u53eb\u7eff\u67f3\uff0c\u662f\u5df4\u5c71\u987e\u9053\u4eba\u7684\u9057\u7269\u3002\u201d\u9ed1\u8863\u4eba\u8f7b\u629a\u5251\u950b\uff1a\u201c\u6614\u5e74\u987e\u9053\u4eba\u4ee5\u4e03\u4e03\u56db\u5341 \u4e5d\u624b\u56de\u98ce\u821e\u67f3\u5251\u7eb5\u6a2a\u5929\u4e0b\uff0c\u6b7b\u5728\u8fd9\u67c4\u5251\u4e0b\u7684\u6210\u540d\u5251\u5ba2\u3002\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4e86\u3002\u201d \u3000\u3000\u4ed6\u653e\u4e0b\u957f\u5251\uff0c\u4e49\u4ece\u67b6\u4e0a\u62ff\u8d77\u4e00\u67c4\u5ba3\u82b1\u5927\u65a7\u3002 \u3000\u3000\u201c\u8fd9\u662f\u6614\u5e74\u9ec4\u5c71\u9690\u4fa0\u6b66\u9675\u6a35\u7528\u7684\u65a7\u5934\uff0c\u51c0\u91cd\u4e03\u5341\u4e09\u65a4\uff0c\u201d\u4ed6\u8bf4\uff1a\u201c\u4ed6\u7528\u7684\u62db\u5f0f\u867d\u7136\u53ea\u6709 \u5341\u4e00\u62db\uff0c\u53ef\u662f\u6bcf\u4e00\u62db\u90fd\u662f\u6781\u9738\u9053\u7684\u6740\u624b\uff0c\u636e\u8bf4\u5f53\u65f6\u6c5f\u6e56\u4e2d\u4ece\u6765\u90fd\u6ca1\u6709\u4eba\u80fd\u5728\u4ed6\u624b\u4e0b\u8d70\u8fc7\u4e03 \u62db\u3002\u201d \u3000\u3000\u5ba3\u82b1\u65a7\u65c1\u6446\u7684\u662f\u67c4\u53c8\u50cf\u662f\u67aa\u53c8\u4e0d\u662f\u67aa\u7684\u6b66\u5668\uff0c\u56e0\u4e3a\u67aa\u5934\u4e0a\u88c5\u7684\u4e0d\u662f\u67aa\u5c16\uff0c\u662f\u67c4\u9570\u5200\uff0c\u8fd8 \u7528\u6761\u94c1\u94fe\u5b50\u6302\u4f4f\u3002 \u3000\u3000\u201c\u94c1\u94fe\u98de\u9570\u6740\u4eba\u5982\u5272\u8349\u3002\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u4ef6\u6b66\u5668\u636e\u8bf4\u662f\u6765\u81ea\u4e1c\u701b\u7684\u3002\u62db\u5f0f\u8be1\u79d8\uff0c\u4e2d\u4e0a \u672a\u89c1\u3002\u201d \u3000\u3000\u4ed6\u53c8\u6307\u7740\u67b6\u4e0a\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u4e00\u53cc\u5a25\u7709\u523a\uff0c\u4e00\u67c4\u8de8\u864e\u84dd\uff0c\u4e00\u628a\u5434\u94a9\u5251\uff0c\u4e00\u53ea\u94a9\u9570\u67aa\uff0c\u4e00\u7b52 \u4e03\u661f\u4ec0\uff0c\u4e00\u628a\u6ce2\u65af\u5f2f\u5200\uff0c\u548c\u4e00\u6839\u767d\u8721\u5927\u7aff\u5b50\u8bf4\uff1a\u201c\u8fd9\u4e9b\u6b66\u5668\u6614\u5e74\u4e5f\u90fd\u662f\u5c5e\u4e8e\u5f53\u4ee3\u7edd\u9876\u9ad8\u624b\u6240 \u6709\uff0c\u6bcf\u4ef6\u6b66\u5668\u90fd\u6709\u5b83\u72ec\u7279\u7684\u62db\u5f0f\u3002\u6bcf\u4ef6\u6b66\u5668\u90fd\u4e0d\u77e5\u9644\u7740\u6709\u591a\u5c11\u6b66\u6797\u9ad8\u624b\u7684\u82f1\u9b42\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5fcd\u4e0d\u4f4f\u8bf4\uff1a\u201c\u6211\u95ee\u7684\u662f\u4f60\u90a3\u53e3\u7bb1\u5b50\uff0c\u4e0d\u662f\u8fd9\u4e9b\u6b66\u5668\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u4f46\u662f\u6211\u90a3\u53e3\u7bb1\u5b50\uff0c\u5c31\u662f\u8fd9\u4e9b\u6b66\u5668\u7684\u7cbe\u534e\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u95ee\u4ed6\uff1a\u201c\u4e00\u53e3\u7bb1\u4e8e\u600e\u4e48\u4f1a\u662f\u5341\u4e09\u79cd\u6b66\u5668\u7684\u7cbe\u534e\uff1f\u6211\u770b\u90a3\u53e3\u7bb1\u5b50\u91cc\u53ea\u4e0d\u8fc7\u662f\u4e9b\u652f\u79bb\u7834 \u788e\u7684\u94c1\u5757\u94c1\u7ba1\u548c\u94c1\u7247\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u90a3\u5176\u4e2d\u7684\u5965\u79d8\uff0c\u4f60\u5f53\u7136\u4e0d\u4f1a\u770b\u5f97\u51fa\u6765\u3002\u201d\u9ed1\u8863\u4eba\u8bf4\uff1a\u201c\u4f46\u662f\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\uff0c\u4e16\u4e0a\u6240\u6709 \u7684\u6b66\u5668\u672c\u6765\u90fd\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e9b\u96f6\u788e\u7684\u94c1\u4ef6\uff0c\u4e00\u5b9a\u8981\u62fc\u51d1\u5728\u4e00\u8d77\u4e4b\u540e\uff0c\u624d\u4f1a\u6210\u4e3a\u4e00\u79cd\u6b66\u5668\u3002\u201d \u3000\u3000\u4ed6\u53c8\u89e3\u91ca\uff1a\u201c\u5c31\u7b97\u662f\u4e00\u628a\u5200\uff0c\u4e5f\u8981\u6709\u5200\u8eab\u3001\u5200\u9537\u3001\u5200\u67c4\u3001\u5200\u73af\u3001\u5200\u8863\uff0c\u4e5f\u8981\u7528\u4e94\u79cd\u4e0d\u540c \u7684\u4e1c\u897f\u62fc\u51d1\u5728\u4e00\u8d77\uff0c\u624d\u80fd\u6210\u4e3a\u4e00\u628a\u5200\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u597d\u50cf\u5df2\u7ecf\u6709\u70b9\u61c2\u4e86\u3002 \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u662f\u4e0d\u662f\u8bf4\uff0c\u4f60\u53ef\u4ee5\u7528\u4f60\u90a3\u53e3\u7bb1\u5b50\u91cc\u7684\u90a3\u4e9b\u94c1\u4ef6\u3002\u62fc\u51d1\u51fa\u4e00\u79cd\u6b66\u5668\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4e00\u79cd\u6b66\u5668\uff0c\u662f\u5341\u4e09\u79cd\u6b66\u5668\uff0c\u5341\u4e09\u79cd\u4e0d\u540c\u7684\u6b66\u5668\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u6014\u4f4f\u3002 \u3000\u3000\u201c\u7528\u5341\u4e09\u79cd\u4e0d\u540c\u7684\u65b9\u6cd5\uff0c\u62fc\u51d1\u51fa\u5341\u4e09\u79cd\u4e0d\u540c\u5f62\u5f0f\u7684\u6b66\u5668\u6765\uff0c\u53ef\u662f\u6bcf\u4e00\u79cd\u5f62\u5f0f\u90fd\u548c\u5e38\u89c1\u7684 \u6b66\u5668\u4e0d\u540c\uff0c\u56e0\u4e3a\u6bcf\u4e00\u79cd\u5f62\u5f0f\u81f3\u5c11\u90fd\u6709\u4e24\u4e09\u79cd\u6b66\u5668\u7684\u529f\u7528\u3002\u201d\u9ed1\u8863\u4eba\u8bf4\uff1a\u201c\u8fd9\u4e9b\u6b66\u5668\u6240\u6709\u7684\u62db \u5f0f\u53d8\u5316\u7cbe\u534e\u6240\u5728\uff0c\u5168\u90fd\u5728\u6211\u90a3\u53e3\u7bb1\u5b50\u91cc\u3002\u201d \u3000\u3000\u4ed6\u95ee\u5c0f\u9ad8\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u660e\u767d\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u5df2\u7ecf\u542c\u5f97\u5b8c\u5168\u6014\u4f4f\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u867d\u7136\u5df2\u7ecf\u660e\u767d\uff0c\u6768\u575a\u548c\u4e91\u6ee1\u5929\u4ed6\u4eec\u4e03\u4e2a\u4eba\uff0c\u4e3a\u4ec0\u4e48\u770b\u8d77\u6765\u4f1a\u50cf\u662f\u540c\u65f6\u6b7b\u5728\u4e09\u56db\u79cd \u4e0d\u540c\u7684\u6b66\u5668\u4e4b\u4e0b\uff0c\u51fa\u624b\u7684\u90e8\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e00\u70b9\u5c0f\u9ad8\u867d\u7136\u60f3\u901a\u4e86\uff0c\u5374\u8fd8\u662f\u4e0d\u80fd\u5b8c\u5168\u76f8\u4fe1\u3002 \u3000\u3000\u5982\u679c\u6ca1\u6709\u4eb2\u773c\u770b\u89c1\uff0c\u6709\u8c01\u4f1a\u76f8\u4fe1\u4e16\u4e0a\u771f\u7684\u6709\u8fd9\u4e48\u6837\u4e00\u4ef6\u6784\u9020\u5982\u6b64\u7cbe\u5de7\u7cbe\u786e\u7cbe\u5bc6\u590d\u6742\u7684\u6b66 \u5668\u5b58\u5728\uff1f \u3000\u3000\u4f46\u8db3\u5c0f\u9ad8\u4e0d\u80fd\u4e0d\u4fe1\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5fcd\u4e0d\u4f4f\u957f\u957f\u53f9\u606f\uff1a\u201c\u80fd\u94f8\u9020\u51fa\u8fd9\u4e48\u6837\u4e00\u4ef6\u6b66\u5668\u6765\u7684\u4eba\uff0c\u4e00\u5b9a\u662f\u4f4d\u4e86\u4e0d\u8d77\u7684\u5929 \u624d\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u82cd\u81ea\u5c0a\u8d35\u51b7\u6f20\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u6700\u8654\u8bda\u7684\u4fe1\u5f92\uff0c\u5ffd \u7136\u63d0\u5230\u4e86\u4ed6\u6700\u5d07\u4fe1\u7684\u795e\u7957\u3002 \u3000\u3000\u201c\u6ca1\u6709\u4eba\u80fd\u6bd4\u5f97\u4e0a\u4ed6\u3002\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u7684\u5251\u672f\u3001\u4ed6\u7684\u667a\u6167\u3001\u4ed6\u7684\u601d\u60f3\u3001\u4ed6\u7684\u4ec1\u5fc3\u548c\u4ed6 \u70bc\u94c1\u70bc\u5251\u7684\u65b9\u6cd5\uff0c\u90fd\u6ca1\u6709\u4eba\u6bd4\u5f97\u4e0a\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5c31\u662f\u94f8\u9020\u4f60\u90a3\u67c4\u2018\u6cea\u75d5\u2019\u7684\u4eba\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u6709\u4e86\u79cd\u5f88\u5947\u5999\u7684\u611f\u89c9\uff0c\u89c9\u5f97\u4ed6\u81ea\u5df1\u548c\u8fd9\u4e2a\u795e\u79d8\u7684\u9ed1\u519c\u4eba\u4e4b\u95f4\u4eff\u4f5b\u6709\u67d0\u79cd\u6781\u5fae\u5999\u7684 \u5173\u7cfb\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u4f7f\u5f97\u4ed6\u53c8\u60ca\u5947\u3001\u53c8\u5174\u594b\u3001\u53c8\u6050\u60e7\u3002 \u3000\u3000\u4ed6\u8fd8\u60f3\u6d31\u591a\u77e5\u9053\u4e00\u70b9\uff0c\u6709\u5173\u8fd9\u53e3\u7bb1\u5b50\u3001\u8fd9\u67c4\u5251\u548c\u8fd9\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\u4e0e\u4e8b\uff0c\u4ed6\u90fd\u60f3\u591a\u77e5\u9053\u4e00 \u70b9\uff0c\u4f46\u662f\u9ed1\u8863\u4eba\u5374\u597d\u50cf\u4e0d\u613f\u4ed6\u77e5\u9053\u5f97\u592a\u591a\uff0c\u5df2\u7ecf\u6539\u53d8\u4e86\u8bdd\u9898\uff1a\u201c\u8fd9\u53e3\u7bb1\u5b50\u56fa\u7136\u662f\u7a7a\u524d\u672a\u6709\u7684 \u6770\u51fa\u6b66\u5668\uff0c\u8981\u4f7f\u7528\u5b83\u4e5f\u4e0d\u5bb9\u6613\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u5982\u679c\u6ca1\u6709\u4e00\u4e2a\u6770\u51fa\u7684\u4eba\u6765\u4f7f\u7528\u5b83\uff0c\u4e5f\u4e0d\u80fd\u53d1\u6325\u51fa \u5b83\u7684\u5a01\u529b\u3002\u201d \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u5728\u5938\u8000\u81ea\u5df1\uff0c\u4e5f\u6ca1\u6709\u81ea\u8d1f\u4e4b\u610f\uff0c\u53ea\u4e0d\u8fc7\u662f\u53d9\u8ff0\u4e00\u4ef6\u4e8b\u5b9e\u800c\u5df2\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u8981 \u7cbe\u901a\u8fd9\u5341\u4e09\u79cd\u6b66\u5668\u7684\u62db\u5f0f\u53d8\u5316\uff0c\u5bf9\u6bcf\u4ef6\u6b66\u5668\u7684\u6784\u9020\u90fd\u8981\u4e86\u89e3\u5f97\u6781\u6e05\u695a\uff0c\u800c\u4e14\u8fd8\u8981\u6709\u4e00\u53cc\u6781\u7075 \u5de7\u7684\u624b\uff0c\u624d\u80fd\u5728\u6700\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u628a\u7bb1\u4e8e\u91cc\u7684\u94c1\u4ef6\u62fc\u51d1\u8d77\u6765\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u53c8\u8bf4\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u8fd8\u8981\u6709\u6781\u4e30\u5bcc\u7684\u7ecf\u9a8c\u3001\u6781\u7075\u654f\u7684\u53cd\u5e94\u548c\u6781\u6b63\u786e\u7684\u5224\u65ad \u529b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5bf9\u624b\u4e0d\u540c\uff0c\u6240\u7528\u7684\u6b66\u5668\u548c\u62db\u5f0f\u4e5f\u4e0d\u540c\uff0c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u5728\u6700\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u5224\u65ad\u51fa\u8981 \u7528\u4ec0\u4e48\u5f62\u5f0f\u7684\u6b66\u5668\u624d\u80fd\u514b\u5236\u4f4f\u4f60\u7684\u5bf9\u624b\u3002\u201d\u9ed1\u8863\u4eba\u8bf4\uff1a\u201c\u5728\u5bf9\u65b9\u8fd8\u6ca1\u6709\u51fa\u624b\u524d\uff0c\u4f60\u5c31\u8981\u7b97 \u51c6\uff0c\u5e94\u8be5\u7528\u54ea\u51e0\u4ef6\u4e1c\u897f\u62fc\u6210\u4e00\u79cd\u4ec0\u4e48\u6837\u7684\u6b66\u5668\uff0c\u800c\u4e14\u8fd8\u8981\u5728\u5bf9\u65b9\u51fa\u4e8e\u524d\u5c06\u5b83\u5b8c\u6210\uff0c\u53ea\u8981\u6162\u4e86 \u4e00\u6b65\uff0c\u5c31\u53ef\u80fd\u6b7b\u5728\u5bf9\u65b9\u624b\u4e0b\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u82e6\u7b11\u3002 \u3000\u3000\u201c\u770b\u6765\u8fd9\u5b9e\u5728\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\uff0c\u50cf\u8fd9\u6837\u7684\u4eba\u627e\u8fc4\u5929\u4e0b\u6050\u6015\u4e5f\u627e\u4e0d\u51fa\u51e0\u4e2a\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9759\u9759\u7684\u770b\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4eba\u624d\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u8981\u6253\u5f00\u6211\u90a3\u53e3\u7bb1\u5b50\uff0c\u4e5f\u4e0d\u662f\u5bb9\u6613\u4e8b\uff0c\u53ef \u662f\u4f60\u5f88\u7094\u5c31\u6253\u5f00\u4e86\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u4f60\u7684\u624b\u5df2\u7ecf\u8db3\u591f\u7075\u5de7\u3002\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u6b66\u529f\u5df2\u7ecf\u5f88\u6709\u6839\u57fa\uff0c\u800c\u4e14\u597d\u50cf\u8fd8\u7ec3\u8fc7\u4f20\u81ea\u5929\u7afa\u79d8\u5b97\u3001\u5723\u6bcd\u4e4b\u6c34\u9ad8\u5cf0\u4e0a\u7684\u745c\u5496 \u672f\u3002\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f20\u7ed9\u4f60\u8fd9\u67c4\u2018\u6cea\u75d5\u2019\u7684\u8001\u4eba\uff0c\u548c\u6211\u8fd9\u53e3\u7bb1\u5b50\u672c\u6765\u5c31\u6709\u70b9\u5173\u7cfb\u3002\u201d\u9ed1\u8863\u4eba\u6de1\u6de1\u7684\u8bf4\uff1a \u201c\u6240\u4ee5\u76f4\u5230\u73b0\u5728\u4f60\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u672c\u6765\u60f3\u6740\u4e86\u6211\u7684\uff1f\u201d\u5c0f\u9ad8\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u6ca1\u6709\u6740\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u7559\u5728\u8fd9\u91cc\u3002\u201d\u9ed1\u8863\u4eba\u8bf4\uff1a\u201c\u6211\u8981\u4f60\u7ee7\u627f\u6211\u7684\u6b66\u529f\uff0c\u7ee7\u627f\u6211\u7684\u7bb1\u5b50\uff0c\u7ee7\u627f\u8fd9 \u91cc\u6240\u6709\u7684\u4e00\u5207\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u4ef6\u522b\u4eba\u8fde\u505a\u68a6\u5374\u68a6\u60f3\u4e0d\u5230\u7684\u5e78\u8fd0\u3002 \u3000\u3000\u2014\u2014\u53ef\u654c\u56fd\u7684\u8d22\u5bcc\uff0c\u7384\u79d8\u4e4b\u6781\u7684\u6b66\u5668\uff0c\u5929\u4e0b\u6700\u53ef\u6015\u7684\u6b66\u5668\u3002 \u3000\u3000\u4e00\u4e2a\u4e00\u65e0\u6240\u6709\u7684\u5e74\u8f7b\u4eba\uff0c\u5ffd\u7136\u95f4\u5c31\u8981\u62e5\u6709\u8fd9\u6240\u6709\u7684\u4e00\u5207\uff0c\u4ed6\u4e00\u751f\u4e2d\u7684\u547d\u8fd0\u5ffd\u7136\u95ee\u5c31\u5df2\u5728 \u8fd9\u4e00\u77ac\u95f4\u6539\u53d8\u3002 \u3000\u3000\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u5fc3\u91cc\u4f1a\u6709\u4ec0\u4e48\u6837\u7684\u611f\u89c9\uff1f \u3000\u3000\u5c0f\u9ad8\u5c45\u7136\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\uff0c\u5c31\u597d\u50cf\u5728\u542c\u522b\u4eba\u8bf4\u4e00\u4ef6\u548c\u4ed6\u5b8c\u5168\u65e0\u5173\u7684\u4e8b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u53c8\u8bf4\uff1a\u201c\u6211\u552f\u4e00\u7684\u6761\u4ef6\u5c31\u662f\u5728\u4f60\u8fd8\u6ca1\u6709\u628a\u6211\u7684\u6b66\u529f\u7ec3\u6210\u4e4b\u524d\uff0c\u7edd\u4e0d\u80fd\u79bb\u5f00\u6b64\u5730\u4e00 \u6b65\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u6761\u4ef6\u5e76\u4e0d\u82db\u523b\uff0c\u800c\u4e14\u975e\u5e38\u5408\u7406\u3002 \u3000\u3000\u201c\u53ea\u53ef\u60dc\u4f60\u5fd8\u4e86\u95ee\u620f\u4e00\u4ef6\u4e8b\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff0c\u201c\u4f60\u5fd8\u4e86\u95ee\u6211\u662f\u4e0d\u662f\u80af\u7559\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u95ee\u9898\u5176\u5b9e\u4e0d\u5fc5\u5411\u7684\uff0c\u8fd9\u6837\u7684\u6761\u4ef6\u53ea\u6709\u75af\u5b50\u548c\u767d\u75f4\u624d\u4f1a\u62d2\u7edd\u3002 \u3000\u3000\u5c0f\u9ad8\u4e0d\u662f\u75af\u5b50\uff0c\u4e5f\u4e0d\u662f\u767d\u75f4\uff0c\u9ed1\u8863\u4eba\u5374\u8fd8\u662f\u95ee\u4e86\u4ed6\u4e00\u53e5\uff1a\u201c\u4f60\u80af\u4e0d\u80af\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u80af\u3002\u201d\u5c0f\u9ad8\u8fde\u60f3\u90fd\u4e0d\u60f3\u5c31\u56de\u7b54\uff1a\u201c\u6211\u4e5f\u4e0d\u613f\u610f\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7684\u77b3\u5b54\u5ffd\u7136\u53d8\u4e86\uff0c\u7531\u4e00\u4e2a\u51e1\u4eba\u7684\u77b3\u5b54\u53d8\u6210\u4e86\u4e00\u6839\u9488\u7684\u5c16\uff0c\u4e00\u67c4\u5251\u7684\u950b\uff0c\u4e00\u53ea\u871c\u8702 \u7684\u523a\uff0c\u76f4\u523a\u7740\u5c0f\u9ad8\u7684\u773c\u775b\u3002 \u3000\u3000\u5c0f\u9ad8\u7684\u773c\u775b\u8fde\u7728\u90fd\u6ca1\u6709\u7728\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u9ed1\u8863\u4eba\u624d\u95ee\u4ed6\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80af\uff1f\u201d \u3000\u3000\u201c\u5176\u5b9e\u4e5f\u4e0d\u4e3a\u4ec0\u4e48\uff01\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u8fd9\u91cc\u592a\u95f7\u4e86\uff0c\u800c\u6211\u5374\u4e00\u5411\u8fc7\u60ef\u4e86\u81ea \u7531\u81ea\u5728\u7684\u65e5\u5b50\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u8fd9\u4e2a\u795e\u79d8\u800c\u53ef\u6015\u7684\u4eba\uff0c\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211\u4e0d\u60f3\u505a\u50cf\u4f60\u8fd9\u6837\u7684 \u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u6211\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u603b\u89c9\u5f97\u4f60\u8fd9\u4e2a\u4eba\u597d\u50cf\u4e00\u76f4\u90fd\u662f\u6d3b\u5728\u9634\u5f71\u91cc\u7684\uff0c\u4e0d\u7ba1\u4f60 \u7528\u54ea\u79cd\u9762\u76ee\u51fa\u73b0\uff0c\u597d\u50cf\u90fd\u53ea\u6709\u5728\u9634\u5f71\u4e2d\u51fa\u73b0\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u867d\u7136\u6709\u5bcc\u53ef\u654c\u56fd\u7684\u8d22\u5bcc\uff0c\u5929\u4e0b\u65e0\u53cc\u7684\u6b66\u529f\uff0c\u53ef\u662f\u6709\u65f6\u5019\u6211\u5374\u89c9\u5f97\u4f60\u7684 \u65e5\u5b50\u8fc7\u5f97\u8fd8\u6ca1\u6709\u6211\u6109\u5feb\uff0c\u6709\u65f6\u5019\u6211\u751a\u81f3\u4f1a\u89c9\u5f97\u76f8\u540c\u60c5\u4f60\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u770b\u7740\u4ed6\uff0c\u77b3\u5b54\u91cc\u7684\u5bd2\u5149\u5ffd\u7136\u6563\u5f00\uff0c\u6563\u6210\u4e86\u4e00\u56fd\u6726\u6726\u80e7\u80e7\u7684\u5149\u5f71\uff0c\u6563\u6210\u4e86\u4e00\u7247\u865a \u65e0\u3002 \u3000\u3000\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u6743\u9009\u62e9\u81ea\u5df1\u751f\u6d3b\u7684\u65b9\u5f0f\uff0c\u6211\u4e5f\u6709\u6743\u9009\u62e9\u6211\u7684\u3002\u201d\u5c0f\u79bd\u8bf4\uff1a\u201c\u6211\u8981\u6d3b\u5728\u592a\u9633 \u4e0b\uff0c\u5c31\u7b97\u6211\u8981\u6740\u4eba\uff0c\u6211\u4e5f\u4f1a\u5802\u5802\u6b63\u6b63\u7684\u53bb\u5411\u4ed6\u6311\u6218\u3002\u8ddf\u4ed6\u516c\u516c\u5e73\u5e73\u7684\u4e89\u4e00\u4e2a\u80dc\u8d1f\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5ffd\u7136\u51b7\u7b11\u3002 \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u53f8\u9a6c\u8d85\u7fa4\u771f\u7684\u4f1a\u8ddf\u4f60\u516c\u5e73\u51b3\u6597\uff1f\u201d \u3000\u3000\u201c\u6211\u5149\u660e\u6b63\u5927\u7684\u5411\u4ed6\u6311\u6218\uff0c\u5927\u5bb6\u4ee5\u4e00\u5bf9\u4e00\uff0c\u600e\u4e48\u4f1a\u4e0d\u516c\u5e73\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u4f60\u5f53\u7136\u4e0d\u4f1a\u61c2\u7684\uff0c\u201d\u9ed1\u8863\u4eba\u53c8\u53f9\u4e86\u4e00\u53e3\u6c14\uff1a\u201c\u7b49\u5230\u4f60\u61c2\u7684\u65f6\u5019\uff0c\u53ea\u6015\u5df2\u7ecf\u592a\u8fdf \u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u8fd8\u662f\u8981\u53bb\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u73b0\u5728\u6211\u7684\u809a\u5b50\u997f\u5f97\u8981\u547d\uff0c\u6211\u53ea\u5e0c\u671b\u4f60\u7559\u6211\u597d \u597d\u7684\u5403\u4e00\u987f\u996d\uff0c\u7136\u540e\u5c31\u8ba9\u6211\u8d70\u3002\u201d \u3000\u3000\u4ed6\u53c8\u663e\u5f97\u9ad8\u5174\u8d77\u6765\uff0c\u201c\u6211\u770b\u5f97\u51fa\u4f60\u4e0d\u662f\u4e2a\u5c0f\u6c14\u7684\u4eba\uff0c\u6211\u8fd9\u4e2a\u8981\u6c42\u5927\u6982\u4e5f\u4e0d\u7b97\u592a\u8fc7\u4efd\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u4e0d\u7b97\u4eba\u8fc7\u4efd\u3002\u201d\u9ed1\u8863\u4eba\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u4e5f\u5fd8\u4e86\u95ee\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5230\u4e86\u8fd9\u5730\u65b9\u6765\u7684\u4eba\uff0c\u4ece\u6765\u6ca1\u6709\u4e00\u4e2a\u80fd\u6d3b\u7740\u51fa\u53bb\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5c45\u7136\u8fd8\u5728\u7b11\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u7684\u8bdd\uff0c\u5e78\u597d\u6bcf\u4ef6\u4e8b\u90fd\u6709\u4f8b\u5916\u7684\u3002\u201d\u4ed6\u7b11\u5f97\u5c45\u7136\u8fd8\u5f88\u6109\u5feb\uff1a \u201c\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u4f1a\u4e3a\u6211\u7834\u4f8b\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u4e3a\u4f60\u7834\u4f8b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4eec\u662f\u670b\u53cb\uff0c\u4e0d\u662f\u4ec7\u654c\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u5f97\u7f6a\u8fc7\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\u3002\u201d\u9ed1\u8863\u4eba\u8bf4\uff1a\u201c\u4f60\u4e0d\u662f\u6211\u7684\u670b\u53cb\uff0c\u4e5f\u4e0d\u914d\u505a\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u773c\u4e2d\u5ffd\u7136\u53c8\u9732\u51fa\u79cd\u5947\u7279\u7684\u5149\u5f71\uff1a\u201c\u5982\u679c\u6211\u80af\u4e3a\u4f60\u7834\u4f8b\u4e00\u6b21\uff0c\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u4e00\u70b9\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u540c\u60c5\u6211\u3002\u201d\u9ed1\u8863\u4eba\u8bf4\u3002 \u3000\u3000\u4ed6\u773c\u4e2d\u7684\u5149\u5f71\u5ffd\u7136\u95f4\u4eff\u4f5b\u53c8\u53d8\u6210\u4e86\u4e00\u79cd\u53c8\u8f9b\u9178\u53c8\u82e6\u6da9\u7684\u8ba5\u8bee\u4e4b\u610f\uff1a\u201c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u53ea\u6709\u4eba \u6068\u6211\u3001\u6015\u6211\uff0c\u5374\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u95f4\u60c5\u8fc7\u6211\uff0c\u53ea\u56e0\u4e3a\u8fd9\u4e00\u70b9\uff0c\u6211\u5c31\u4e0d\u59a8\u7ed9\u4f60\u4e00\u6b21\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6837\u7684\u673a\u4f1a\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7ad9\u8d77\u6765\uff0c\u4ece\u5927\u6848\u4e0a\u968f\u4fbf\u62ff\u8d77\u4e86\u4e24\u4e2a\u6c34\u6676\u6a3d\uff0c\u8981\u5c0f\u9ad8\u9009\u4e00\u74f6\u559d\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u6211\u9009\uff1f\u201d\u5c0f\u9ad8\u95ee\uff01\u201c\u8fd9\u4e24\u74f6\u9152\u597d\u50cf\u662f\u5b8c\u5168\u4e00\u6837\u7684\uff0c\u8fde\u74f6\u5b50\u90fd\u662f\u4e00\u6837\u7684\u3002\u201d \u3000\u3000\u201c\u53ea\u6709\u4e00\u70b9\u4e0d\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e24\u74f6\u9152\u6709\u4e00\u74f6\u662f\u6bd2\u9152\uff0c\u201d\u9ed1\u53cb\u4eba\u8bf4\uff1a\u201c\u7a7f\u80a0\u593a\u547d\u7684\u6bd2\u9152\u3002\u201d \u3000\u3000\u5176\u5b9e\u8fd9\u4e24\u74f6\u9152\u8fd8\u6709\u4e00\u70b9\u662f\u4e0d\u4e00\u6837\u7684\uff0c\u5176\u4e2d\u6709\u4e00\u74f6\u9152\u6bd4\u53e6\u5916\u74f6\u5c11\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u74f6\u9152\u5df2\u7ecf\u88ab\u9ed1\u8863\u4eba\u5012\u51fa\u6765\u4e00\u70b9\uff0c\u800c\u4e14\u5df2\u7ecf\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u8fd8\u6d3b\u7740\u3002 \u3000\u3000\u8fd9\u4e00\u70b9\u5c0f\u9ad8\u5e94\u8be5\u770b\u5f97\u51fa\u6765\uff0c\u4f46\u662f\u4ed6\u9009\u7684\u5374\u662f\u53e6\u5916\u4e00\u74f6\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u51b7\u51b7\u7684\u95ee\uff1a\u201c\u4f60\u9009\u5b9a\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u9009\u5b9a\u4e86\uff0c\u800c\u4e14\u7edd\u4e0d\u4f1a\u6539\u53d8\u4e3b\u610f\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u770b\u5230\u6211\u521a\u624d\u559d\u8fc7\u4e00\u676f\u9152\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u89c1\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u559d\u7684\u662f\u54ea\u4e00\u74f6\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u9009\u6211\u559d\u8fc7\u7684\u4e00\u74f6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8fd8\u4e0d\u60f3\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5fae\u7b11\uff0c\u7b11\u5f97\u66f4\u6109\u5feb\uff1a\u201c\u4f60\u77e5\u9053\u6211\u4e0d\u662f\u778e\u5b50\uff0c\u4e5f\u4e0d\u7b97\u592a\u7b28\uff0c\u4e00\u5b9a\u80fd\u770b\u5f97\u51fa\u8fd9\u4e24\u74f6\u9152\u91cc \u6709\u4e00\u74f6\u662f\u4f60\u559d\u8fc7\u7684\uff0c\u53ef\u662f\u4f60\u8fd8\u8981\u8ba9\u6211\u9009\uff0c\u56e0\u4e3a\u5927\u591a\u6570\u4eba\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u90fd\u4f1a\u9009\u4f60\u559d\u8fc7\u7684\u90a3\u4e00 \u74f6\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u201c\u5e78\u597d\u6211\u4e0d\u662f\u5927\u591a\u6570\u4eba\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u628a\u6211\u5f53\u4f5c\u90a3\u4e9b\u4eba\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4f60\u559d\u8fc7\u7684\u90a3\u74f6\u9152\u91cc\u5982 \u679c\u771f\u7684\u6ca1\u6709\u6bd2\uff0c\u4f60\u5c31\u4e0d\u4f1a\u7528\u8fd9\u79cd\u65b9\u6cd5\u6765\u8bd5\u6211\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u4f60\u8981\u5bf9\u4ed8\u6211\uff0c\u5f53\u7136\u8981\u7528\u6bd4\u8f83\u56f0\u96be\u4e00\u70b9\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u8fd9\u79cd\u9009\u62e9\u5b9e\u5728\u5f88\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u5c31\u7b97\u6709\u667a\u6167\uff0c\u80fd\u60f3\u5230\u6bd2\u9152\u5f88\u53ef\u80fd\u5c31\u662f\u9ed1\u8863\u4eba\u81ea\u5df1\u559d\u8fc7\u7684\u90a3\u4e00\u74f6\uff0c\u4e5f\u672a\u5fc5\u6709\u80c6\u91cf\u628a \u53e6\u5916\u4e00\u74f6\u559d\u6389\u3002 \u3000\u3000\u201c\u6bd2\u9152\u662f\u4f60\u7684\uff0c\u4f60\u5f53\u7136\u6709\u89e3\u836f\uff0c\u5c31\u7b97\u559d\u4e2a\u5341\u74f6\u516b\u74f6\u4e5f\u6ca1\u95ee\u9898\uff0c\u53ef\u662f\u6211\u5c31\u559d\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u5c0f\u9ad8\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u53ea\u6709\u9009\u8fd9\u4e00\u74f6\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u773c\u8272\u770b\u7740\u5c0f\u9ad8\uff0c\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u58f0\u97f3\u95ee\u4ed6\uff1a\u201c\u5982\u679c\u4f60\u9009\u9519\u4e86 \u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u4e5f\u53ea\u6709\u6b7b\u4e86\u7b97\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5c0f\u9ad8\u5c31\u628a\u4ed6\u81ea\u5df1\u9009\u7684\u4e00\u74f6\u9152\u4e00\u53e3\u6c14\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7684\u4eba\u4e5f\u5012\u4e86\u4e0b\u53bb\u3002 \u7b2c\u4e94\u7ae0\u3000\u5947\u9022\u5947\u9047>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u4e94\u7ae0\u3000\u5947\u9022\u5947\u9047 \u4e00 \u3000\u3000\u6b63\u6708\u4e8c\u5341\u4e94\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u5e76\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u4ed6\u7684\u5224\u65ad\u5b8c\u5168\u6b63\u786e\uff0c\u4ed6\u7684\u80c6\u5b50\u4e5f\u591f\u5927\uff0c\u6240\u4ee5\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u552f\u4e00\u9057\u61be\u7684\u662f\uff0c\u4ed6\u5b8c\u5168\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u600e\u4e48\u79bb\u5f00\u90a3\u4e2a\u5730\u65b9\u7684\uff0c\u4e5f\u4e0d\u77e5\u9053\u90a3\u4e2a\u5947\u79d8\u7684\u6d1e\u7a9f\u7a76 \u7adf\u5728\u54ea\u91cc\u3002 \u3000\u3000\u559d\u4e0b\u90a3\u74f6\u9152\u4e4b\u540e\uff0c\u4ed6\u7acb\u523b\u5c31\u6655\u8ff7\u5012\u5730\uff0c\u4e0d\u7701\u4eba\u4e8b\uff0c\u7136\u540e\u4ed6\u5c31\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u56de\u5230\u90a3\u5bb6\u5ec9\u4ef7 \u7684\u5c0f\u5ba2\u6808\uff0c\u7761\u5728\u90a3\u95f4\u5c0f\u5c4b\u91cc\u7684\u6728\u677f\u5e8a\u4e0a\u3002 \u3000\u3000\u4ed6\u662f\u600e\u4e48\u56de\u53bb\u7684\uff1f\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u56de\u53bb\u7684\uff1f\u4ed6\u81ea\u5df1\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u522b\u4eba\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u8fd9\u4e24\u5929\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u4e5f\u6ca1\u6709\u4eba\u5173\u5fc3\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u5e78\u597d\u8fd8\u6709\u6837\u4e1c\u897f\u80fd\u8bc1\u660e\u8fd9\u4e24\u5929\u4ed6\u7ecf\u5386\u8fc7\u7684\u4e8b\u5e76\u4e0d\u662f\u5728\u505a\u68a6\u3002 \u3000\u3000\u2014\u2014\u4e00\u53e3\u7bb1\u5b50\uff0c\u4e00\u53e3\u6697\u8910\u8272\u7684\u725b\u76ae\u7bb1\u5b50\u3002 \u3000\u3000\u5c0f\u9ad8\u9192\u6765\u65f6\uff0c\u5c31\u53d1\u73b0\u4e86\u8fd9\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u7bb1\u5b50\u5c31\u6446\u5728\u4ed6\u5e8a\u8fb9\u7684\u5c0f\u684c\u4e0a\uff0c\u989c\u8272\u5f62\u72b6\u90fd\u548c\u4ed6\u66fe\u7ecf\u6253\u5f00\u8fc7\u7684\u90a3\u4e00\u53e3\u5b8c\u5168\u4e00\u6837\u3002\u751a\u81f3\u8fde\u7bb1 \u5b50\u4e0a\u88c5\u7684\u673a\u7c27\u9501\u94ae\u90fd\u4e00\u6837\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u8fd9\u53e3\u7bb1\u5b50\u771f\u7684\u5c31\u662f\u90a3\u4ef6\u7a7a\u524d\u672a\u6709\u72ec\u4e00\u65e0\u4e8c\u7684\u6b66\u5668\uff0c\u4ed6\u600e\u4e48\u4f1a\u81ea\u4e0b\u6765\u7ed9\u6211\uff1f \u3000\u3000\u5c0f\u9ad8\u867d\u7136\u4e0d\u4fe1\uff0c\u5374\u8fd8\u662f\u672a\u514d\u6709\u70b9\u52a8\u5fc3\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u60f3\u8981\u6253\u5f00\u672a\u770b\u770b\u3002 \u3000\u3000\u5e78\u597d\u4ed6\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u4e0a\u4e00\u6b21\u7684\u6559\u8bad\u3002 \u3000\u3000\u5982\u679c\u4e00\u4e2a\u4eba\u6bcf\u6b21\u6253\u5f00\u4e00\u53e3\u7bb1\u5b50\u6765\u7684\u65f6\u5019\uff0c\u90fd\u8981\u88ab\u8ff7\u5012\u4e00\u6b21\uff0c\u90a3\u5c31\u5f88\u4e0d\u597d\u73a9\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u7bb1\u5b50\u4e00\u6253\u5f00\uff0c\u5c0f\u9ad8\u7684\u4eba\u5c31\u5df2\u7ecf\u5230\u4e86\u7a97\u5916\uff0c\u51b7\u98ce\u5200\u522e\u80a1\u7684\u5439\u8fdb\u7a97\u6237\uff0c\u522e\u8fdb\u5c4b\u5b50\u91cc\uff0c\u4e0d \u7ba1\u4ec0\u4e48\u6837\u7684\u8ff7\u9999\uff0c\u90fd\u5df2\u7ecf\u5e94\u8be5\u88ab\u522e\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u5c0f\u9ad8\u624d\u6162\u541e\u541e\u7684\u4ece\u5916\u9762\u515c\u4e86\u4e2a\u5708\u5b50\uff0c\u4ece\u623f\u95e8\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u770b\u5230\u4e86\u7bb1\u5b50\u91cc\u7684\u4e1c\u897f\u540e\uff0c\u4ed6\u5c45\u7136\u89c9\u5f97\u5931\u671b\u3002 \u3000\u3000\u56e0\u4e3a\u7bb1\u5b50\u91cc\u88c5\u7740\u7684\u53ea\u4e0d\u8fc7\u662f\u4e9b\u73e0\u5b9d\u7fe1\u7fe0\u548c\u4e00\u5927\u53e0\u91d1\u53f6\u5b50\u800c\u5df2\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u662f\u8db3\u8db3\u53ef\u4ee5\u628a\u4e00\u6574\u6761\u8857\u90fd\u4e70\u4e0b\u6765\uff0c\u53ef\u4ee5\u8ba9\u4e00\u57ce\u4eba\u90fd\u4e3a\u5b83\u53bb\u62fc\u547d\u7684\u73e0\u5b9d\u7fe1\u7fe0\u548c\u9ec4\u91d1 \u800c\u5df2\u3002 \u3000\u3000\u8fd9\u5df2\u7ecf\u662f\u4e09\u5929\u524d\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u8fd9\u4e09\u5929\u4ed6\u51fa\u95e8\u7684\u65f6\u5019\uff0c\u867d\u7136\u603b\u662f\u5e26\u7740\u8fd9\u53e3\u7bb1\u5b50\u51fa\u53bb\uff0c\u4f46\u662f\u4ed6\u7684\u751f\u6d3b\u4e00\u70b9\u90fd\u6ca1\u6709\u6539\u53d8\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u4f4f\u5728\u90a3\u5bb6\u6700\u4fbf\u5b9c\u7684\u5c0f\u5ba2\u6808\u91cc\uff0c\u5403\u6700\u4fbf\u5b9c\u7684\u767d\u83dc\u716e\u9762\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u5b8c\u5168\u4e0d\u77e5\u9053\u8fd9\u7bb1\u4e1c\u897f\u662f\u53ef\u4ee5\u7528\u6765\u505a\u5f88\u591a\u4e8b\u7684\uff0c\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u53d8\u6210\u4e86\u4e2a\u5927\u5bcc \u7fc1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6839\u672c\u6ca1\u6709\u53bb\u60f3\u8fc7\uff0c\u6839\u672c\u4e0d\u60f3\u77e5\u9053\u3002 \u3000\u3000\u5bf9\u4e8e\u91d1\u94b1\u7684\u4ef7\u503c\uff0c\u4ed6\u6839\u672c\u5b8c\u5168\u6ca1\u6709\u89c2\u5ff5\u3002\u4ed6\u7edd\u4e0d\u8ba9\u81ea\u5df1\u7684\u751f\u6d3b\u56e0\u4e3a\u4efb\u4f55\u4e8b\u800c\u6539\u53d8\u3002 \u3000\u3000\u53ef\u662f\u5728\u6b63\u6708\u4e8c\u5341\u4e94\u8fd9\u4e00\u65e0\uff0c\u4ed6\u7684\u751f\u6d3b\u8fd8\u662f\u6539\u53d8\u4e86\u3002\u6539\u53d8\u5f97\u5f88\u5947\u602a\u3002 \u4e8c \u3000\u3000\u8fd9\u4e00\u5929\u662f\u6674\u5929\uff0c\u5728\u90a3\u5bb6\u5c0f\u9762\u9986\u91cc\u5403\u8fc7\u9762\u4e4b\u540e\uff0c\u4ed6\u53c8\u51c6\u5907\u56de\u53bb\u8499\u5934\u5927\u7761\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u548c\u5353\u4e1c\u6765\u6765\u90a3\u8fb9\u81f3\u4eca\u8fd8\u662f\u6ca1\u6709\u6d88\u606f\uff0c\u4e5f\u4e0d\u77e5\u9053\u7a76\u7adf\u51c6\u5907\u5728\u54ea\u4e00\u5929\u8ddf\u4ed6\u4ea4\u624b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e00\u70b9\u90fd\u4e0d\u7740\u6025\u3002 \u3000\u3000\u90a3\u4e2a\u795e\u79d8\u7684\u9ed1\u8863\u4eba\uff0c\u65e0\u7f18\u65e0\u6545\u7684\u9001\u4e86\u4ed6\u8fd9\u4e48\u5927\u4e00\u7b14\u8d22\u5bcc\u4e4b\u540e\uff0c\u4e5f\u97f3\u8baf\u5168\u65e0\u3002 \u3000\u3000\u4ed6\u968f\u65f6\u90fd\u51c6\u5907\u628a\u8fd9\u7bb1\u4e1c\u897f\u8fd8\u7ed9\u4ed6\uff0c\u6240\u4ee5\u624d\u968f\u8eab\u5e26\u7740\uff0c\u4f46\u662f\u4ed6\u4eec\u4eca\u540e\u5374\u6050\u6015\u6c38\u8fdc\u65e0\u6cd5\u518d\u89c1 \u4e86\uff0c\u8fd9\u7bb1\u4e1c\u897f\u53cd\u800c\u53d8\u6210\u4e86\u4ed6\u7684\u4e00\u4e2a\u7d2f\u8d58\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u9ad8\u4e5f\u6ca1\u6709\u56e0\u6b64\u800c\u70e6\u607c\u3002 \u3000\u3000\u8fd9\u4e2a\u4e16\u754c\u4e0a\u597d\u50cf\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u5f71\u54cd\u5230\u4ed6\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u522b\u4eba\u8981\u4ed6\u7b49\u4e24\u5929\uff0c\u4ed6\u5c31\u7b49\u4e24\u5929\uff0c\u8981\u4ed6\u7b49\u4e24\u4e2a\u6708\uff0c\u4ed6\u5c31\u7b49\u4e24\u4e2a\u6708\uff0c\u53cd\u6b63\u8fdf\u65e9\u603b\u6709\u4e00\u5929\u4f1a\u7b49 \u5230\u6d88\u606f\u7684\uff0c\u53c8\u4f55\u5fc5\u70e6\u8e81\u7740\u6025\uff1f \u3000\u3000\u4ed6\u5df2\u7ecf\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u5728\u8fd9\u6b21\u51b3\u6218\u4e4b\u524d\uff0c\u4ec0\u4e48\u4e8b\u4ed6\u90fd\u4e0d\u505a\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u4f7f\u81ea\u5df1\u7684\u4f53\u529b\u59cb\u7ec8\u4fdd\u6301\u5728\u5dc5\u5cf0\u72b6\u51b5\u4e2d\uff0c\u800c\u4e14\u4e00\u5b9a\u8981\u8ba9\u81ea\u5df1\u7684\u5fc3\u60c5\u4fdd\u6301\u5e73\u8861\u3002 \u3000\u3000\u8fd9\u5929\u4e2d\u5348\u4ed6\u6cbf\u7740\u79ef\u96ea\u7684\u957f\u8857\u8d70\u56de\u53bb\u65f6\uff0c\u5c31\u53d1\u73b0\u540e\u9762\u6709\u4e2a\u4eba\u5728\u76ef\u4ed6\u7684\u68a2\uff0c\u5c0f\u9ad8\u7528\u4e0d\u7740\u56de\u5934 \u53bb\u770b\uff0c\u5c31\u5df2\u731c\u51fa\u8fd9\u4e2a\u4eba\u662f\u8c01\u3002 \u3000\u3000\u6628\u5929\u665a\u4e0a\u5403\u996d\u65f6\uff0c\u4ed6\u5c31\u53d1\u73b0\u8fd9\u4e2a\u4eba\u5728\u76ef\u7740\u4ed6\u4e86\uff0c\u5c31\u597d\u50cf\u4e00\u5934\u732b\u76ef\u7740\u53ea\u8001\u9f20\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7a7f\u5f97\u5f88\u7834\u70c2\uff0c\u6234\u7740\u9876\u7834\u6be1\u5e3d\uff1b\u8eab\u6750\u867d\u7136\u4e0d\u9ad8\u5927\uff0c\u5374\u957f\u7740\u4e00\u8138\u5927\u80e1\u5b50\uff0c\u8d70\u8def\u7684\u811a\u6b65 \u58f0\u5f88\u8f7b\uff0c\u663e\u7136\u662f\u7ec3\u8fc7\u529f\u592b\u7684\u3002 \u3000\u3000\u5c0f\u9ad8\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\u4e3a\u4ec0\u4e48\u8981\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u81ea\u5df1\u5e76\u6ca1\u6709\u4ec0\u4e48\u53ef\u4ee5\u8ba9\u4eba\u53d1\u751f\u5174\u8da3\u7684\u5730\u65b9\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u6bb5\u8def\u4e4b\u540e\uff0c\u540e\u9762\u7684\u811a\u6b65\u58f0\u5ffd\u7136\u542c\u4e0d\u89c1\u4e86\uff0c\u5c0f\u9ad8\u521a\u677e\u4e86\u53e3\u6c14\uff0c\u65c1\u8fb9\u7684\u4e00\u6761\u6a2a\u5df7\u91cc\u5ffd \u7136\u6709\u6761\u7ef3\u5b50\u98de\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e00\u6761\u5f88\u7c97\u7684\u7ef3\u5b50\uff0c\u7528\u6d3b\u7ed3\u6253\u4e86\u4e2a\u7ef3\u5708\uff0c\u4e00\u4e0b\u5b50\u5c31\u5957\u4f4f\u4e86\u9ad8\u6e10\u98de\u7684\u8116\u5b50\uff0c\u5957\u5f97\u5947\u51c6\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u8116\u5b50\u5982\u679c\u88ab\u8fd9\u79cd\u7ef3\u5708\u5957\u4f4f\uff0c\u773c\u73e0\u968f\u65f6\u90fd\u4f1a\u51f8\u51fa\u6765\uff0c\u820c\u5934\u968f\u65f6\u90fd\u4f1a\u5410\u51fa\u6765\uff0c\u968f\u65f6 \u90fd\u53ef\u80fd\u4f1a\u65ad\u6c14\u3002 \u3000\u3000\u5c0f\u9ad8\u5f88\u660e\u767d\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u6240\u4ee5\u7ef3\u5b50\u4e00\u4f4d\u52a8\uff0c\u4ed6\u5c31\u98de\u4e86\u8d77\u6765\uff0c\u5c31\u50cf\u662f\u4e2a\u98ce\u7b5d\u4e00\u6837\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5728\u6a2a\u5df7\u4e2d\u62c9\u7ef3\u5b50\u7684\u4eba\uff0c\u679c\u7136\u5c31\u662f\u90a3\u4e2a\u5927\u80e1\u5b50\u3002 \u3000\u3000\u4ed6\u8fd8\u5728\u7528\u529b\u7684\u62c9\uff0c\u53ef\u60dc\u7ef3\u5b50\u5df2\u7ecf\u65ad\u4e86\uff0c\u88ab\u4ed6\u7ef3\u5b50\u5957\u4f4f\u5934\u7684\u4eba\u5df2\u7ecf\u5411\u4ed6\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5927\u80e1\u5b50\u6389\u5934\u5c31\u8dd1\uff0c\u8dd1\u51fa\u4e86\u4e00\u6bb5\u8def\uff0c\u5c31\u89c9\u5f97\u6709\u70b9\u5947\u602a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u5c0f\u9ad8\u5c45\u7136\u6ca1\u6709\u53bb\u8ffd\u4ed6\u3002 \u3000\u3000\u5927\u80e1\u5b50\u53c8\u8dd1\u4e86\u4e24\u6b65\uff0c\u5ffd\u7136\u505c\u4e0b\uff0c\u540e\u9762\u8fd8\u662f\u6ca1\u6709\u4eba\u8ffd\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u8f6c\u8fc7\u8eab\uff0c\u5403\u60ca\u7684\u770b\u7740\u5c0f\u9ad8\uff0c\u5c45\u7136\u8fd8\u8981\u95ee\u5c0f\u9ad8\uff0c\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6765\u8ffd\u6211\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u771f\u662f\u95ee\u5f97\u7edd\u900f\u4e86\uff0c\u53ef\u662f\u5c0f\u9ad8\u66f4\u7edd\uff0c\u5c45\u7136\u8fd8\u53cd\u95ee\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u8ffd\u4f60\uff1f\u201d \u3000\u3000\u5927\u80e1\u5b50\u6014\u4e86\u6014\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u6211\u521a\u624d\u60f3\u7528\u90a3\u6761\u7ef3\u5b50\u52d2\u6b7b\u4f60\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u653e\u8fc7\u4e86\u6211\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u6ca1\u6709\u88ab\u4f60\u52d2\u6b7b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u6700\u5c11\u4e5f\u8be5\u95ee\u95ee\u6211\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u52d2\u6b7b\u4f60\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u60f3\u95ee\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u6839\u672c\u4e0d\u60f3\u77e5\u9053\u3002\u201d\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u5c0f\u9ad8\u5c45\u7136\u5c31\u8f6c\u8eab\u8d70\u4e86\uff0c\u8fde\u5934\u90fd\u4e0d\u56de\u3002 \u3000\u3000\u5927\u80e1\u5b50\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u50cf\u5c0f\u9ad8\u8fd9\u6837\u7684\u4eba\uff0c\u4ed6\u8fd9\u4e00\u8f88\u5b50\u90e8\u6ca1\u6709\u770b\u5230\u8fc7\u4e00\u4e2a\u3002 \u3000\u3000\u53ef\u662f\u50cf\u4ed6\u8fd9\u6837\u7684\u4eba\uff0c\u5c0f\u9ad8\u4e5f\u6ca1\u6709\u770b\u5230\u8fc7\uff0c\u5c0f\u9ad8\u4e0d\u53bb\u8ffd\u4ed6\uff0c\u4ed6\u53cd\u800c\u6765\u8ffd\u5c0f\u9ad8\u4e86\uff0c\u800c\u4e14\u5c45\u7136 \u53c8\u4ece\u8eab\u4e0a\u62ff\u51fa\u60e7\u7ef3\u4e8e\uff0c\u5f88\u5feb\u7684\u7ed3\u4e86\u4e2a\u7ef3\u5708\uff0c\u5f80\u5c0f\u9ad8\u7684\u8116\u5b50\u4e0a\u5957\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u5957\u5f97\u771f\u51c6\uff0c\u5c0f\u9ad8\u53c8\u88ab\u4ed6\u5957\u4f4f\u4e86\u3002 \u3000\u3000\u552f\u4e00\u9057\u61be\u7684\u662f\uff0c\u4ed6\u867d\u7136\u5957\u4f4f\u4e86\uff0c\u8fd8\u662f\u8fde\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4e0d\u7ba1\u4ed6\u600e\u4e48\u7528\u529b\u5f80\u540e\u4f4d\uff0c\u5c0f\u9ad8\u90fd\u8fd8\u662f\u597d\u597d\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u975e\u4f46\u8116\u5b50\u6ca1\u6709\u88ab\u4ed6\u52d2\u65ad\uff0c\u8fde\u52a8\u90fd \u6ca1\u6709\u52a8\u3002 \u3000\u3000\u5927\u80e1\u5b50\u5c45\u7136\u53c8\u95ee\u4ed6\uff1a\u201c\u4f60\u8fd9\u4e2a\u4eba\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4e3a\u4ec0\u4e48\u6211\u603b\u662f\u52d2\u4e0d\u6b7b\u4f60\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8fd9\u4e2a\u4eba\u9664\u4e86\u8116\u5b50\u5916\u8fd8\u6709\u624b\u6307\u5934\u3002\u201d \u3000\u3000\u7ef3\u5708\u5957\u4e0a\u5c0f\u9ad8\u8116\u4e8e\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u7528\u4e00\u6839\u624b\u6307\u628a\u7ef3\u5b50\u52fe\u4f4f\u4e86\uff0c\u5728\u54bd\u5589\u524d\u9762\u52fe\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6307\u4e00\u7528\u529b\uff0c\u5927\u6eaf\u5b50\u5c31\u88ab\u4ed6\u4e00\u4e0b\u5b50\u62c9\u4e86\u8fc7\u6765\uff0c\u4ed6\u521a\u8f6c\u8fc7\u8eab\uff0c\u5927\u80e1\u5b50\u8155\u4e00\u5934\u649e\u5728\u4ed6\u6000 \u91cc\u3002 \u3000\u3000\u201c\u4f60\u7684\u7ef3\u5b50\u73a9\u5f97\u4e0d\u597d\u3002\u201d\u5c0f\u5546\u8bf4\uff1a\u201c\u9664\u4e86\u73a9\u7ef3\u5b50\u5916\uff0c\u4f60\u8fd8\u4f1a\u73a9\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8fd8\u4f1a\u73a9\u5200\u3002\u201d\u5927\u80e1\u5b50\u8bf4\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u8fd8\u6ca1\u6709\u7ad9\u7a33\uff0c\u624b\u91cc\u5df2\u7ecf\u6cb9\u51fa\u4e00\u628a\u77ed\u5200\uff0c\u4e00\u5200\u5f80\u5c0f\u9ad8\u7684\u8f6f\u80c1\u4e0a\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u7684\u5200\u4e5f\u4e0d\u591f\u5feb\uff0c\u5c0f\u9ad8\u7528\u4e00\u6839\u624b\u6307\u5728\u4ed6\u624b\u8155\u4e00\u6572\uff0c\u4ed6\u7684\u5200\u5c31\u88ab\u6572\u98de\u4e86\u3002 \u3000\u3000\u201c\u6211\u770b\u4f60\u8fd8\u662f\u653e\u8fc7\u6211\u5427\u3002\u201d\u5c0f\u9ad8\u53f9\u7740\u6c14\u6447\u5934\uff1a\u201c\u4e0d\u7ba1\u4f60\u73a9\u4ec0\u4e48\uff0c\u5bf9\u6211\u90fd\u6ca1\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u5927\u80e1\u5b50\u672c\u6765\u5df2\u7ecf\u5feb\u5012\u5728\u5730\u4e0a\uff0c\u5ffd\u7136\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u8eab\u5b50\u5ffd\u7136\u5012\u7ffb\u8d77\u6765\uff0c\u4e24\u6761\u817f\u5ffd\u7136 \u50cf\u626d\u9ebb\u82b1\u4f3c\u7684\u51cc\u7a7a\u4e00\u7ede\uff0c\u7ede\u4f4f\u4e86\u5c0f\u9ad8\u7684\u5934\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u8fde\u5c0f\u9ad8\u90fd\u6ca1\u6709\u60f3\u5230\u3002 \u3000\u3000\u8fd9\u4e2a\u5927\u80e1\u5b50\u7684\u4e24\u6761\u817f\u975e\u4f46\u8f7b\u6377\u7075\u6d3b\uff0c\u800c\u4e14\u7ed3\u5b9e\u6709\u529b\uff0c\u5c0f\u9ad8\u5dee\u4e00\u70b9\u8fde\u6c14\u90fd\u900f\u4e0d\u8fc7\u6765\uff0c\u8fd9\u53cc \u817f\u4e0a\u7a7f\u7684\u4e00\u6761\u7834\u88e4\u4e8e\u5473\u9053\u4e5f\u5f88\u4e0d\u597d\u55c5\u3002 \u3000\u3000\u5c0f\u9ad8\u5b9e\u5728\u53d7\u4e0d\u4e86\uff0c\u8eab\u5b50\u5ffd\u7136\u7528\u4e00\u79cd\u5f88\u5947\u7279\u7684\u65b9\u6cd5\u4e00\u67e0\u4e00\u626d\u4e00\u8f6c\u4e00\u7528\uff0c\u5927\u80e1\u5b50\u7684\u4eba\u5c31\u88ab\u7529 \u4e86\u51fa\u53bb\uff0c\u4eba\u8dcc\u5728\u5730\u4e0a\uff0c\u88e4\u5b50\u4e5f\u88c2\u5f00\uff0c\u9732\u51fa\u4e86\u4e00\u53cc\u817f\u3002 \u3000\u3000\u4ed6\u7684\u88e4\u5b50\u672c\u6765\u5c31\u5df2\u7ecf\u5feb\u7834\u4e86\uff0c\u4e00\u7834\u5c31\u7834\u5230\u4e86\u5e95\uff0c\u51e0\u4e4e\u628a\u4e24\u6761\u817f\u5168\u90e8\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u662f\u5c0f\u9ad8\u6014\u4f4f\u4e86\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u770b\u5230\u4e00\u5806\u70c2\u6ce5\u4e2d\u957f\u51fa\u4e86\u4e00\u6735\u9c9c\u82b1\u4e00\u6837\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u817f\u7684\uff0c\u53ef\u662f\u5c0f\u9ad8\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u89c1\u8fc7\u8fd9\u4e48\u597d\u770b\u7684\u4e00\u53cc\u817f\u3002 \u3000\u3000\u4e0d\u4f46\u5c0f\u9ad8\u6ca1\u6709\u770b\u89c1\u8fc7\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5927\u591a\u6570\u4eba\u6050\u6015\u90fd\u6ca1\u6709\u770b\u89c1\u8fc7\u3002 \u3000\u3000\u8fd9\u4e2a\u4e16\u754c\u4e0a\u80fd\u770b\u89c1\u8fd9\u4e48\u4e00\u53cc\u817f\u7684\u4eba\u6050\u6015\u8fd8\u6ca1\u6709\u51e0\u4e2a\u3002 \u3000\u3000\u8fd9\u53cc\u817f\u4fee\u957f\u800c\u7ed3\u5b9e\uff0c\u7ebf\u6761\u5300\u5747\u67d4\u7f8e\uff0c\u808c\u8089\u5145\u6ee1\u4e86\u5f39\u6027\uff0c\u76ae\u80a4\u662f\u4e73\u767d\u8272\u7684\uff0c\u5c31\u50cf\u662f\u521a\u4ece\u4e00 \u6761\u6bcd\u725b\u8eab\u4e0a\u6324\u51fa\u6765\u7684\u65b0\u9c9c\u725b\u5976\u7684\u989c\u8272\u4e00\u6837\u3002 \u3000\u3000\u5c0f\u9ad8\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e2a\u53c8\u8d43\u53c8\u81ed\u7684\u5927\u80e1\u5b50\uff0c\u5c45\u7136\u4f1a\u6709\u8fd9\u4e48\u4e00\u53cc\u817f\u3002 \u3000\u3000\u8ba9\u4ed6\u66f4\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u8fd9\u4e2a\u53c8\u60f3\u7528\u7ef3\u5b50\u52d2\u6b7b\u4ed6\u53c8\u60f3\u7528\u5200\u6740\u6b7b\u4ed6\u7684\u5927\u80e1\u5b50\u5c45\u7136\u54ed\u4e86\uff0c\u5c45\u7136\u5750 \u5728\u5730\u4e0a\uff0c\u7528\u624b\u6342\u7740\u8138\uff0c\u50cf\u5c0f\u5b69\u4e00\u6837\u54ed\u4e86\u8d77\u6765\uff0c\u54ed\u5f97\u597d\u4f24\u5fc3\u597d\u4f24\u5fc3\u3002 \u3000\u3000\u5c0f\u9ad8\u672c\u6765\u5e94\u8be5\u8d70\u7684\uff0c\u5c31\u50cf\u521a\u624d\u90a3\u6837\u5b50\u5934\u4e5f\u4e0d\u56de\u7684\u8d70\u6389\uff0c\u53ef\u60dc\u4ed6\u504f\u504f\u53c8\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a\u201c\u4f60 \u54ed\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u559c\u6b22\u54ed\uff0c\u6211\u9ad8\u5174\u54ed\uff0c\u6211\u613f\u610f\u54ed\uff0c\u4f60\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u957f\u7740\u4e00\u8138\u5927\u80e1\u5b50\u7684\u5927\u7537\u4eba\uff0c\u8bf4\u8d77\u8bdd\u6765\u5c45\u7136\u50cf\u662f\u4e2a\u5c0f\u5973\u5b69\u4e00\u6837\u4e0d\u8bb2\u7406\uff0c\u8fde\u8bf4\u8bdd\u7684\u58f0\u97f3 \u90fd\u53d8\u5f97\u597d\u50cf\u662f\u4e2a\u5c0f\u5973\u5b69\u7684\u58f0\u97f3\uff0c\u50cf\u8fd9\u4e48\u6837\u4e00\u4e2a\u602a\u7269\uff0c\u600e\u4e48\u80fd\u518d\u8ddf\u4ed6\u7ea0\u7f20\u4e0b\u53bb\uff1f \u3000\u3000\u5c0f\u9ad8\u51b3\u5fc3\u4e0d\u518d\u7406\u4ed6\uff0c\u51b3\u5fc3\u8981\u8d70\u4e86\uff0c\u5927\u80e1\u5b50\u5374\u53c8\u53eb\u4f4f\u4e86\u4ed6\uff1a\u201c\u4f60\u7ad9\u4f4f\u3002\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u7ad9\u4f4f\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e48\u6837\u4f60\u5c31\u60f3\u8d70\uff1f\u5929\u4e0b\u6709\u8fd9\u4e48\u4fbf\u5b9c\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8d70\uff1f\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4f60\u53c8\u8981\u52d2\u6b7b\u6211\uff0c\u53c8\u8981\u7528\u5200\u6740\u6211\uff0c\u6211\u8fd9\u4e48\u6837\u8d70\u6389\uff0c\u5df2\u7ecf \u5f88\u5bf9\u5f97\u8d77\u4f60\u4e86\uff0c\u4f60\u8fd8\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u60f3\u8981\u4f60\u628a\u4f60\u7684\u773c\u73e0\u5b50\u6316\u51fa\u6765\uff0c\u201d\u8fd9\u4e2a\u5927\u80e1\u5b50\u8bf4\uff1a\u201c\u628a\u4f60\u4e24\u4e2a\u773c\u775b\u91cc\u7684\u773c\u73e0\u5e72\u90e8\u6316 \u51fa\u6765\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53c8\u60f3\u7b11\uff0c\u53c8\u7b11\u4e0d\u51fa\uff1a\u201c\u6211\u53c8\u6ca1\u6709\u75af\uff0c\u4e3a\u4ec0\u4e48\u8981\u628a\u81ea\u5df1\u7684\u773c\u73e0\u5b50\u6316\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u770b\u89c1\u4e86\u6211\u7684\u817f\uff0c\u201d\u5927\u80e1\u5b50\u8bf4\uff1a\u201c\u6211\u8fd9\u53cc\u817f\u53c8\u4e0d\u662f\u968f\u4fbf\u5c31\u53ef\u4ee5\u7ed9\u522b\u4eba\u770b\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u4ed6\u7684\u8fd9\u53cc\u817f\u957f\u5f97\u5b9e\u5728\u5f88\u7279\u522b\uff0c\u7279\u522b\u7684\u597d\u770b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53c8\u4e0d\u662f\u6545\u610f\u8981\u770b\u7684\uff0c\u4e24\u6761\u817f\u88ab\u522b\u4eba\u770b\u89c1\uff0c\u4e5f\u4e0d\u80fd\u7b97\u662f\u4ec0\u4e48\u4e0d\u5f97\u4e86\u7684\u4e8b\u3002 \u3000\u3000\u201c\u8981\u662f\u4f60\u89c9\u5f97\u4e0d\u670d\u6c14\u7684\u6d3b\uff0c\u6211\u4e5f\u53ef\u4ee5\u628a\u6211\u7684\u4e24\u6761\u817f\u8ba9\u4f60\u770b\u770b\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u968f\u4fbf\u4f60\u8981\u770b \u591a\u4e45\u90fd\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u653e\u4f60\u7684\u72d7\u5c41\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u72d7\uff0c\u6211\u4e5f\u6ca1\u6709\u653e\u5c41\u3002\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u4e0d\u662f\u72d7\uff0c\u56e0\u4e3a\u4f60\u6bd4\u72d7\u8fd8\u7b28\u3002\u201d\u5927\u80e1\u5b50\u8bf4\uff1a\u201c\u5929\u4e0b\u6240\u6709\u7684\u72d7\u90fd\u6bd4\u4f60\u806a\u660e\u5f97\u591a\uff0c\u4e0d \u7ba1\u662f\u5927\u72d7\u5c0f\u72d7\u516c\u72d7\u6bcd\u72d7\u90fd\u6bd4\u4f60\u806a\u660e\u4e00\u767e\u500d\u3002\u56e0\u4e3a\u4f60\u662f\u5934\u732a\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u5927\u80e1\u5b50\u8d8a\u8bf4\u8d8a\u751f\u6c14\uff0c\u5ffd\u7136\u8df3\u8d77\u6765\uff1a\u201c\u4f60\u8fd9\u5934\u732a\uff0c\u96be\u9053\u4f60\u8fd8\u770b\u4e0d\u51fa\u6211\u662f\u4e2a\u5973\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u4f1a\u662f\u4e2a\u5973\u4eba\uff1f\u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5446\u5446\u7684\u8bf4\uff1a\u201c\u5973\u4eba\u600e\u4f1a\u6709\u80e1\u5b50\uff1f\u201d \u3000\u3000\u5927\u80e1\u5b50\u597d\u50cf\u5df2\u7ecf\u6c14\u5f97\u5feb\u75af\u4e86\uff0c\u5ffd\u7136\u7528\u529b\u5c06\u81ea\u5df1\u8138\u4e0a\u7684\u90a3\u4e00\u5927\u628a\u5927\u80e1\u5b50\u5168\u90e8\u6495\u4e86\u4e0b\u6765\uff0c\u5f80 \u5c0f\u9ad8\u8138\u4e0a\u63b7\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u7684\u8eab\u5b50\u4e5f\u8ddf\u7740\u98de\u4e86\u8fc7\u53bb\uff0c\u8170\u80a2\u4e00\u62e7\u4e00\u626d\uff0c\u4e24\u6761\u817f\u53c8\u628a\u5c0f\u9ad8\u7ede\u4f4f\u4e86\u3002\u201d \u3000\u3000\u4e24\u6761\u5149\u6e9c\u6e9c\u7684\u817f\uff0c\u4e0a\u9762\u8fdd\u4e00\u6839\u7ef5\u7eb1\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u6b21\u5c0f\u9ad8\u771f\u7684\u8fde\u52a8\u90fd\u4e0d\u6562\u52a8\u4e86\uff0c\u53ea\u6709\u770b\u7740\u5979\u82e6\u7b11\uff0c\u201c\u6211\u8ddf\u4f60\u65e2\u6ca1\u6709\u51a4\uff0c\u53c8\u6ca1\u6709\u4ec7\uff0c\u4f60\u4e3a \u4ec0\u4e48\u8981\u8fd9\u6837\u5b50\u5bf9\u6211\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u770b\u4e2d\u4e86\u4f60\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53c8\u5413\u5446\u4e86\uff0c\u5e78\u597d\u8fd9\u4e2a\u5df2\u7ecf\u6ca1\u6709\u5927\u80e1\u5b50\u7684\u5927\u80e1\u5b50\u5f88\u5feb\u5c31\u63a5\u7740\u8bf4\uff1a\u201c\u4f60\u4e0d\u5fc5\u81ea\u6211\u9676\u9189\uff0c \u6211\u770b\u4e2d\u7684\u5e76\u4e0d\u662f\u4f60\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u4e2d\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u4f60\u624b\u91cc\u7684\u8fd9\u53e3\u7bb1\u5b50\u3002\u201d\u8fd9\u4e2a\u6ca1\u6709\u5927\u80e1\u5b50\u7684\u5927\u59d1\u5a18\u8bf4\uff1a\u201c\u53ea\u8981\u4f60\u628a\u8fd9\u53e3\u7bb1\u5b50\u7ed9\u6211\uff0c\u6211 \u4ee5\u540e\u7edd\u4e0d\u518d\u6765\u627e\u4f60\u9ebb\u70e6\uff0c\u4f60\u4e5f\u6c38\u8fdc\u518d\u4e5f\u770b\u4e0d\u5230\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u6211\u8fd9\u53e3\u7bb1\u5b50\u91cc\u6709\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u201d\u8fd9\u4f4d\u5927\u59d1\u5a18\u8bf4\uff1a\u201c\u4f60\u8fd9\u53e3\u7bb1\u5b50\u91cc\u6700\u5c11\u6709\u4ef7\u503c\u516b\u5341\u4e07\u4e24\u4ee5\u4e0a\u7684\u9ec4\u91d1\u73e0 \u5b9d\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u5f53\u7136\u89c9\u5f97\u5f88\u8be7\u5f02\uff0c\u56e0\u4e3a\u4ed6\u4ece\u6765\u4e5f\u6ca1\u6709\u5728\u522b\u4eba\u9762\u524d\u6253\u5f00\u8fc7\u8fd9\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u5979\u975e\u4f46\u4e0d\u56de\u7b54\uff0c\u53cd\u800c\u95ee\u5c0f\u5546\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u7684\u7236\u4eb2\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u4e2a\u795e\u5077\uff0c\u5999\u624b\u795e\u5077\uff0c\u5077\u904d\u5929\u4e0b\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u5931\u624b\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u597d\u672c\u9886\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u6bd4\u8d77\u6211\u7684\u7956\u7236\u6765\u53c8\u5dee\u5f97\u591a\u4e86\uff0c\u201d\u5979\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u7684\u7956\u7236\u662f\u4ec0\u4e48 \u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u8001\u4eba\u5bb6\u662f\u4f4d\u5927\u76d7\uff0c\u89c1\u4eba\u76d7\u4eba\uff0c\u89c1\u9b3c\u76d7\u9b3c\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u539f\u6765\u4f60\u4eec\u5bb6\u4e0a\u4e0b\u4e09\u4ee3\u90fd\u662f\u5e72\u8fd9\u4e00\u884c\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u603b\u7b97\u660e\u767d\u4e86\u3002\u201d\u5927\u80e1\u5b50\u59d1\u5a18\u8bf4\uff1a\u201c\u4e00\u4e2a\u4e0a\u4e0b\u4e09\u4ee3\u90fd\u5e72\u8fd9\u884c\u7684\u4eba\uff0c\u600e\u4e48\u4f1a\u770b\u4e0d\u51fa\u8fd9\u53e3 \u7bb1\u5b50\u91cc\u6709\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\uff0c\u8fd9\u4e00\u884c\u7684\u597d\u624b\u90fd\u6709\u8fd9\u79cd\u672c\u4e8b\uff0c\u4ece\u4e00\u4e2a\u4eba\u8d70\u8def\u7684\u6837\u5b50\u4e0a\uff0c\u90fd\u80fd\u770b\u5f97\u51fa\u8fd9\u4e2a \u4eba\u8eab\u4e0a\u662f\u4e0d\u662f\u5e26\u7740\u503c\u94b1\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d\u5927\u59d1\u5a18\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u5374\u770b\u4e0d\u51fa\u4f60\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u624b\u91cc\u63d0\u7740\u4e00\u7bb1\u5b50\u9ec4\u91d1\u73e0\u5b9d\uff0c\u6bcf\u5929\u5403\u7684\u90fd\u662f\u4e09\u4e94\u6587\u94b1\u4e00\u7897\u7684\u83b1\u716e\u9762\uff0c\u201d\u5927\u59d1\u5a18\u95ee\u5c0f \u9ad8\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u4e2a\u5c0f\u6c14\u9b3c\uff1f\u8fd8\u662f\u5927\u602a\u7269\uff1f\u201d \u3000\u3000\u201c\u6211\u624b\u91cc\u867d\u7136\u63d0\u7740\u4e00\u7bb1\u5b50\u9ec4\u91d1\u73e0\u5b9d\uff0c\u53ea\u53ef\u60dc\u5168\u90fd\u4e0d\u662f\u6211\u7684\uff0c\u6240\u4ee5\u5c31\u7b97\u60f3\u9001\u7ed9\u4f60\uff0c\u4e5f\u4e0d\u80fd \u9001\u7ed9\u4f60\u3002\u201d\u5c0f\u9ad8\u8bf4\uff0c\u201c\u6211\u4e5f\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u5c31\u7b97\u4f60\u7684\u672c\u4e8b\u518d\u5927\u5341\u500d\uff0c\u4e5f\u4f11\u60f3\u628a\u8fd9\u53e3\u7bb1\u5b50\u4ece\u6211\u624b\u91cc \u62a2\u8d70\u3002\u201d \u3000\u3000\u5927\u59d1\u5a18\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u6211\u4e5f\u77e5\u9053\u6211\u662f\u62a2\u4e0d\u8d70\u7684\u3002\u201d\u5979\u8bf4\uff1a\u201c\u53ef\u662f\u4e0d\u7ba1\u600e\u6837\u6211\u8981\u8bd5\u8bd5\uff0c\u5c31\u7b97\u62fc\u4e86\u8fd9\u6761\u547d\uff0c\u6211\u8981 \u8ddf\u4f60\u6b7b\u7f20\u5230\u5e95\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5982\u679c\u4e0d\u80fd\u5728\u4e09\u5929\u5185\u7b79\u8db3\u4e94\u4e07\u4e24\u94f6\u5b50\uff0c\u4e5f\u4e00\u6837\u662f\u6b7b\u5b9a\u4e86\u3002\u201d\u5979\u7684\u773c\u73e0\u5b50\u8f6c\u4e86\u8f6c\uff0c \u773c\u6cea\u53c8\u6d41\u4e86\u4e0b\u6765\uff1a\u201c\u4f60\u60f3\u60f3\uff0c\u9664\u4e86\u4ece\u4f60\u8eab\u4e0a\u60f3\u529e\u6cd5\u4e4b\u5916\uff0c\u6211\u5230\u54ea\u91cc\u53bb\u627e\u4e94\u4e07\u4e24\u94f6\u5b50\uff1f\u201d \u3000\u3000\u5979\u7684\u773c\u6cea\u5c31\u50cf\u96e8\u70b9\u822c\u4e0d\u505c\u7684\u5f80\u4e0b\u6389\uff1a\u201c\u6211\u770b\u5f97\u51fa\u4f60\u662f\u597d\u5fc3\u4eba\uff0c\u4f60\u4e00\u5b9a\u8981\u6551\u6551\u6211\uff0c\u6211\u8fd9\u4e00 \u8f88\u5b50\u90fd\u611f\u6fc0\u4f60\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u5fc3\u5df2\u7ecf\u6709\u70b9\u8f6f\u4e86\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u5728\u4e09\u5929\u91cc\u7b79\u8db3\u4e94\u4e07\u4e24\u94f6\u5b50\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u53f8\u9a6c\u8d85\u7fa4\u7684\u5927\u9556\u5c40\uff0c\u4e00\u5b9a\u8981\u6211\u4ed8\u51fa\u4e94\u4e07\u4e24\u94f6\u5b50\uff0c\u624d\u80af\u628a\u6211\u62a4\u9001\u56de\u5bb6\u53bb\u3002\u201d\u5979\u8bf4\uff1a \u201c\u6211\u7684\u5bb6\u5728\u5173\u4e1c\uff0c\u5982\u679c\u6ca1\u6709\u4ed6\u4eec\u62a4\u9001\uff0c\u8fd9\u4e00\u8def\u4e0a\u6211\u968f\u65f6\u90fd\u53ef\u80fd\u6b7b\u5728\u9053\u8def\u65c1\uff0c\u8fde\u6536\u5c38\u7684\u4eba\u90fd\u6ca1 \u6709\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u51b7\u7b11\uff1a\u201c\u9001\u4e00\u4e2a\u4eba\u51fa\u5173\u5c31\u8981\u4e94\u4e07\u4e24\uff0c\u4ed6\u4eec\u7684\u5fc3\u672a\u514d\u592a\u9ed1\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4e0d\u602a\u4ed6\u4eec\uff0c\u8981\u628a\u6211\u9001\u56de\u53bb\u5b9e\u5728\u5f88\u4e0d\u5bb9\u6613\uff0c\u201d\u5927\u59d1\u5a18\u8bf4\uff1a\u201c\u5982\u679c\u6211\u662f\u53f8\u9a6c\u8d85\u7fa4\uff0c \u6211\u5f00\u51fa\u6765\u7684\u4ef7\u94b1\u4e5f\u8bb8\u66f4\u9ad8\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8981\u6740\u6211\u7684\u90a3\u4e9b\u4eba\u5b9e\u5728\u592a\u51f6\u6076\u592a\u53ef\u6015\u4e86\uff0c\u8c01\u90fd\u4e0d\u613f\u610f\u8ddf\u4ed6\u4eec\u4f5c\u5bf9\u7684\u3002\u201d\u5927\u59d1\u5a18\u8bf4\uff1a \u201c\u6211\u76f8\u4fe1\u4f60\u6c38\u8fdc\u90fd\u60f3\u4e0d\u5230\u5929\u4e0b\u4f1a\u6709\u4ed6\u4eec\u90a3\u4e48\u51f6\u66b4\u6b8b\u5fcd\u7684\u4eba\u3002\u201d \u3000\u3000\u5979\u7684\u8eab\u5b50\u5df2\u7ecf\u5f00\u59cb\u53d1\u6296\uff0c\u5979\u7684\u8138\u4e0a\u663e\u7136\u62b9\u7740\u70df\u7070\uff0c\u53ef\u662f\u73b0\u5728\u4e5f\u4e00\u6837\u80fd\u770b\u5f97\u51fa\u5979\u7684\u8138\u5df2\u56e0 \u60ca\u9a87\u6050\u60e7\u800c\u626d\u66f2\u3002 \u3000\u3000\u5979\u771f\u7684\u6015\u5f97\u8981\u547d\u3002 \u3000\u3000\u5c0f\u9ad8\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u5927\u59d1\u5a18\u597d\u50cf\u5df2\u7ecf\u542c\u4e0d\u89c1\u4ed6\u5728\u95ee\u4ec0\u4e48\u4e86\uff0c\u4e0d\u505c\u7684\u6d41\u7740\u6cea\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u653e\u8fc7\u6211 \u7684\uff0c\u6211\u77e5\u9053\u4ed6\u4eec\u968f\u65f6\u968f\u5730\u90fd\u4f1a\u8d76\u6765\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u5979\u597d\u50cf\u5df2\u7ecf\u6709\u4e86\u67d0\u79cd\u51f6\u6076\u4e0d\u7965\u7684\u9884\u611f\uff0c\u4e00\u79cd\u5c31\u597d\u50cf\u4e00\u53ea\u91ce\u517d\u5df2\u7ecf\u611f\u89c9\u5230\u6709\u9677\u9631\u5728\u524d\uff0c\u6709 \u730e\u4eba\u5c06\u8981\u6355\u6740\u5b83\u65f6\u7684\u9884\u611f\u3002 \u3000\u3000\u8fd9\u79cd\u9884\u611f\u867d\u7136\u65e0\u6cd5\u89e3\u91ca\uff0c\u53ef\u662f\u901a\u5e38\u90fd\u5f88\u7075\u9a8c\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u7a84\u5df7\u4e24\u8fb9\u8d2d\u77ed\u5899\u4e0a\u5df2\u7ecf\u5206\u522b\u6709\u6697\u5668\u66b4\u5c04\u800c\u51fa\uff0c\u5de6\u9762\u662f\u4e00\u84ec\u94f6\u96e8\uff0c\u53f3\u9762\u662f\u513f \u70b9\u5bd2\u661f\u3002 \u3000\u3000\u9ad8\u6d59\u98de\u7684\u53cd\u5e94\u4e00\u5411\u6781\u7094\u3002 \u3000\u3000\u4ed6\u4ee5\u53f3\u624b\u63d0\u7740\u7684\u7bb1\u5b50\u548c\u5305\u88b1\u6321\u4f4f\u4e86\u5de6\u9762\u5c04\u6765\u7684\u4e00\u84ec\u94f6\u96e8\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5df2\u5e26\u7740\u7528\u4e24\u6761\u817f\u7ede\u4f4f\u4ed6\u7684\u5927\u59d1\u5a18\uff0c\u5f80\u53f3\u9762\u659c\u659c\u98de\u8d77\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u8fd8\u662f\u542c\u5230\u5979\u53d1\u51fa\u4e86\u4e00\u58f0\u53f9\u606f\u822c\u7684\u547b\u541f\uff0c\u8fd8\u662f\u611f\u89c9\u5230\u5979\u7ed3\u5b9e\u6709\u529b\u7684\u4e24\u6761\u817f\uff0c\u5ffd\u7136\u8f6f \u4e86\u4e0b\u53bb\u3002\u4ece\u534a\u7a7a\u4e2d\u60bc\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5c0f\u9ad8\u6ca1\u6709\u88ab\u5979\u62d6\u4e0b\u53bb\uff0c\u53cd\u800c\u53c8\u5411\u4e0a\u62d4\u8d77\uff0c\u4ee5\u53f3\u811a\u57ab\u5de6\u811a\uff0c\u501f\u529b\u4f7f\u529b\uff0c\u53c8\u5411\u4e0a\u62e8\u8d77\u4e08\u4f59\uff0c \u5c31\u770b\u89c1\u7a84\u5df7\u4e24\u8fb9\u7684\u77ed\u5899\u540e\uff0c\u90fd\u6709\u4e00\u4e2a\u4eba\u5206\u522b\u5411\u5de6\u53f3\u4e24\u65b9\u7a9c\u51fa\uff0c\u8eab\u624b\u90fd\u6781\u77eb\u5065\uff0c\u8f7b\u529f\u90fd\u4e0d\u5f31\u3002 \u3000\u3000\u4ed6\u4eec\u7a9c\u4e0a\u6570\u4e08\u5916\u7684\u5c4b\u810a\u65f6\uff0c\u5c0f\u9ad8\u4e5f\u843d\u5728\u5899\u5934\uff0c\u4e24\u4e2a\u4eba\u5ffd\u7136\u5168\u90e8\u8f6c\u8fc7\u8eab\u6765\u76ef\u7740\u4ed6\uff0c\u8138\u4e0a\u90fd \u5e26\u7740\u72f0\u72de\u7684\u9762\u5177\uff0c\u773c\u91cc\u90fd\u5145\u6ee1\u4e86\u51f6\u66b4\u6b8b\u9177\u6076\u6bd2\u7684\u8868\u60c5\uff0c\u5176\u4e2d\u4e00\u4e2a\u4eba\u7528\u5636\u54d1\u7684\u58f0\u97f3\u51b7\u51b7\u7684\u8bf4\uff1a \u201c\u670b\u53cb\uff0c\u4f60\u7684\u529f\u592b\u5f88\u4e0d\u9519\uff0c\u8981\u7ec3\u6210\u2018\u68af\u4e91\u7eb5\u2019\u8fd9\u4e00\u7c7b\u7684\u8f7b\u529f\u4e5f\u5f88\u4e0d\u5bb9\u6613\uff0c\u5982\u679c\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c31 \u6b7b\u4e86\uff0c\u5b9e\u5728\u5f88\u53ef\u60dc\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5fae\u7b11\uff1a\u201c\u5e78\u597d\u6211\u6682\u65f6\u8fd8\u4e0d\u60f3\u6b7b\uff0c\u4e5f\u6b7b\u4e0d\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u6700\u597d\u5c31\u542c\u6211\u826f\u8a00\u76f8\u529d\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u662f\u7ba1\u4e0d\u5f97\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u7ba1\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u201c\u60f9\u4e0a\u4e86\u6211\u4eec\uff0c\u5c31\u597d\u50cf\u88ab\u9b54\u9b3c\u7f20\u4e0a\u4e86\u8eab\u3002\u201d\u8fd9\u4e2a\u4eba\u8bf4\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u5728\u5403\u996d\u4e5f\u597d\uff0c\u7761\u89c9\u4e5f \u597d\uff0c\u4e0d\u7ba1\u4f60\u5728\u5e72\u4ec0\u4e48\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u4f1a\u53d1\u73b0\u6709\u4ef6\u4f60\u4ece\u672a\u89c1\u8fc7\u7684\u5175\u5203\u6697\u5668\u5df2\u7ecf\u5230\u4e86\u4f60\u7684\u54bd\u5589\u7709\u776b \u95f4\uff0c\u4f60\u4e00\u89c9\u7761\u9192\uff0c\u4e5f\u53ef\u80fd\u4f1a\u53d1\u73b0\u6709\u4e2a\u4eba\u6b63\u5728\u7528\u4e00\u628a\u5272\u8089\u5200\u6162\u6162\u7684\u5272\u4f60\u7684\u8116\u5b50\u3002\u201d \u3000\u3000\u4ed6\u9634\u607b\u607b\u7684\u8bf4\uff1a\u201c\u4e0d\u7ba1\u8c01\u9047\u5230\u4e86\u8fd9\u79cd\u4e8b\uff0c\u5fc3\u60c5\u90fd\u4e0b\u4f1a\u6109\u5feb\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u4e5f\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u7684\u786e\u5f88\u4e0d\u597d\u73a9\uff0c\u53ea\u53ef\u60dc\u6211\u8fd9\u4e2a\u4eba\u5929\u751f\u6709\u79cd\u602a\u813e\u6c14\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u522b\u4eba\u8d8a\u4e0d\u8981\u6211\u7ba1\u7684\uff0c\u6211\u8d8a\u60f3\u53bb\u7ba1\u4e00\u7ba1\u3002\u201d \u3000\u3000\u53e6\u5916\u4e00\u4e2a\u4eba\u5ffd\u7136\u51b7\u7b11\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u56de\u53bb\u7b49\u6b7b\u5427\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u53c8\u540c\u65f6\u7ffb\u8eab\u8dc3\u8d77\uff0c\u5411\u540e\u7a9c\u51fa\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u8eab\u6cd5\u867d\u5feb\uff0c\u5c0f\u9ad8\u6700\u5c11\u8fd8\u662f\u53ef\u4ee5\u8ffd\u4e0a\u4e00\u4e2a\uff0c\u53ea\u53ef\u60dc\u5730\u4e0a\u8fd8\u8eba\u7740\u4e00\u4e2a\u4eba\uff0c\u4e00\u8dcc\u5230\u5730\u4e0a \u53bb\u4e4b\u540e\uff0c\u5c31\u8fde\u52a8\u4e5f\u6ca1\u6709\u52a8\u8fc7\uff0c\u4e00\u53cc\u5149\u6ed1\u7ed3\u5b9e\u4fee\u957f\u7684\u817f\uff0c\u5df2\u7ecf\u5feb\u8981\u88ab\u51bb\u6210\u7d2b\u8272\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u4e2a\u4eba\u548c\u5c0f\u9ad8\u8fde\u4e00\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\uff0c\u53ef\u662f\u8981\u5c0f\u9ad8\u5c31\u8fd9\u6837\u770b\u7740\u5979\u5149\u7740\u4e24\u6761\u817f\u6b7b\u5728\u79ef\u96ea\u7684 \u7a84\u5df7\uff0c\u8fd9\u79cd\u4e8b\u5c0f\u9ad8\u4e5f\u7edd\u5bf9\u505a\u4e0d\u51fa\u7684\u3002 \u3000\u3000\u5979\u7684\u4f24\u5728\u80a9\u540e\uff0c\u5f88\u5c0f\u5f88\u5c0f\u7684\u4e00\u4e2a\u4f24\u53e3\uff0c\u5374\u5df2\u7ecf\u80bf\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u8fd8\u5728\u53d1\u70eb\u3002 \u3000\u3000\u2014\u2014\u6697\u5668\u6709\u6bd2\uff0c\u56de\u4e00\u5b9a\u6709\u6bd2\u3002 \u3000\u3000\u5e78\u597d\u5979\u9047\u89c1\u4e86\u9ad8\u60ed\u98de\uff0c\u4e00\u4e2a\u4ece\u5c0f\u5c31\u4f4f\u5728\u5230\u5904\u90fd\u6709\u6bd2\u866b\u6bd2\u8681\u6bd2\u86c7\u7684\u8352\u5c71\u4e2d\u7684\u4eba\uff0c\u8eab\u4e0a\u5f53\u7136 \u4e0d\u4f1a\u6ca1\u6709\u89e3\u6bd2\u7684\u836f\u3002 \u3000\u3000\u6240\u4ee5\u5979\u6ca1\u6709\u6b7b\uff0c\u800c\u4e14\u5f88\u5feb\u5c31\u9192\u4e86\u8fc7\u6765\u3002 \u4e09 \u3000\u3000\u5979\u9192\u6765\u65f6\u5df2\u7ecf\u8eba\u5728\u5c0f\u9ad8\u5ba2\u6808\u91cc\u90a3\u5f20\u6728\u677f\u5e8a\u4e0a\uff0c\u4f24\u53e3\u5df2\u7ecf\u6577\u4e0a\u836f\uff0c\u7528\u4e00\u6761\u79df\u5e03\u7f20\u4f4f\u3002 \u3000\u3000\u5979\u9752\u89c1\u4e86\u5c0f\u9ad8\uff0c\u770b\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u8f7b\u8f7b\u7684\u95ee\uff1a\u201c\u4f60\u6b7b\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u662f\u4e0d\u662f\u4e5f\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u600e\u4e48\u4f1a\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d\u5979\u597d\u50cf\u89c9\u5f97\u5f88\u610f\u5916\uff1a\u201c\u4ed6\u4eec\u5df2\u7ecf\u8ffd\u6765\u4e86\uff0c\u6211\u600e\u4e48\u4f1a\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u7684\u8fd0\u6c14\u4e0d\u9519\uff0c\u9047\u5230\u4e86\u6211\u3002\u201d \u3000\u3000\u8fd9\u4f4d\u8138\u4e0a\u5df2\u7ecf\u6ca1\u6709\u80e1\u5b50\u7684\u5927\u59d1\u5a18\u5ffd\u7136\u751f\u6c14\u4e86\uff1a\u201c\u6211\u5df2\u7ecf\u88ab\u4eba\u903c\u5f97\u65e0\u8def\u53ef\u8d70\uff0c\u6bcf\u5929\u50cf\u91ce\u72d7 \u4e00\u822c\u4e1c\u5954\u897f\u7a9c\uff0c\u4e1c\u85cf\u897f\u8eb2\uff0c\u53c8\u4e2d\u4e86\u522b\u4eba\u7684\u6bd2\u836f\u6697\u5668\uff0c\u4f60\u5c45\u7136\u8fd8\u8bf4\u6211\u8fd0\u6c14\u4e0d\u9519\uff1f\u201d \u3000\u3000\u5979\u77aa\u7740\u5c0f\u9ad8\uff0c\u201c\u6211\u5012\u8981\u542c\u4f60\u8bf4\u8bf4\u770b\uff0c\u8981\u600e\u4e48\u6837\u624d\u7b97\u8fd0\u6c14\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u82e6\u7b11\uff0c\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u8fd9\u4f4d\u5927\u59d1\u5a18\u53c8\u77aa\u4e86\u4ed6\u534a\u5929\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u662f\u7edd\u4e0d\u80af\u628a\u7bb1\u5b50\u7ed9\u6211\u7684\uff0c\u6240\u4ee5\u4f60\u6700\u597d\u4e5f\u4e0d\u8981\u518d\u7ba1\u6211\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u4f60\u662f\u7ba1\u4e0d\u5f97\u7684\uff0c\u6211\u7684\u6b7b\u6d3b\u4e5f\u8ddf\u4f60\u6ca1\u5173\u7cfb\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u8ddf\u4f60\u672c\u6765\u5c31\u8fde\u4e00\u70b9\u5173\u7cfb \u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u672c\u6765\u8fde\u4e00\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\uff0c\u53ef\u662f\u73b0\u5728\u5374\u597d\u50cf\u6709\u70b9\u5173\u7cfb\u4e86\u3002\u201d \u3000\u3000\u201c\u653e\u4f60\u7684\u72d7\u5c41\u3002\u201d\u5927\u59d1\u5a18\u5ffd\u7136\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u8bf4\uff0c\u6211\u8ddf\u4f60\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u4f60\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u9047\u5230\u8fc7\u8fd9\u6837\u7684\u4eba\uff0c\u4ee5\u524d\u6ca1\u6709\uff0c\u4ee5\u540e\u4e5f\u4e0d\u4f1a\u6709\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u73b0\u5728\u5374\u504f\u504f\u9047\u5230\u4e86\u4e00\u4e2a\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u5927\u59d1\u5a18\u53c8\u95ee\u4ed6\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u628a\u6211\u5e26\u5230\u8fd9\u4e48\u6837\u4e00\u4e2a\u72d7\u7a9d\u91cc\u6765\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u91cc\u4e0d\u662f\u72d7\u7a9d\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u8fd9\u91cc\u662f\u6211\u4f4f\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u8fd9\u4f4d\u5927\u59d1\u5a18\u5ffd\u7136\u53c8\u7741\u5927\u4e86\u773c\u775b\u77aa\u4f4f\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u662f\u5934\u732a\uff0c\u4f60\u771f\u7684\u662f\u5934\u732a\uff0c\u201d\u5979\u5927\u58f0\u8bf4\uff1a\u201c\u6ee1\u8857\u7684\u4eba\u90fd\u77e5\u9053\u4f60\u4f4f\u5728\u8fd9\u91cc\uff0c\u4f60\u5c45\u7136\u8fd8\u8981 \u628a\u6211\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u4f60\u662f\u4e0d\u662f\u4e00\u5b9a\u8981\u770b\u5230\u6211\u6b7b\u5728\u4ed6\u4eec\u624b\u91cc\u624d\u9ad8\u5174\uff0c\u662f\u4e0d\u662f\u4e00\u5b9a\u8981\u7b49\u5230\u4ed6\u4eec\u627e\u6765 \u628a\u6211\u4e00\u5757\u5757\u5207\u788e\u4e86\u624d\u5f00\u5fc3\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u7b11\u4e86\u3002 \u3000\u3000\u8fd9\u4e48\u4e0d\u8bb2\u7406\u7684\u4eba\u5e76\u4e0d\u662f\u65f6\u5e38\u90fd\u80fd\u9047\u5f97\u5230\u7684\u3002 \u3000\u3000\u5927\u59d1\u5a18\u66f4\u751f\u6c14\u3002 \u3000\u3000\u201c\u4f60\u8fd8\u7b11\uff0c\u6709\u4ec0\u4e48\u597d\u7b11\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u600e\u4e48\u6837\uff1f\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u8981\u6211\u54ed\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd9\u5934\u732a\uff0c\u732a\u600e\u4e48\u4f1a\u54ed\uff1f\u4f60\u51e0\u65f6\u770b\u89c1\u8fc7\u4e00\u5934\u732a\u4f1a\u54ed\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5012\u662f\u771f\u7684\u3002\u201d\u5c0f\u9ad8\u50cf\u5ffd\u7136\u53d1\u73b0\u4e86\u4e00\u4e2a\u5927\u9053\u7406\uff1a\u201c\u732a\u597d\u50cf\u771f\u662f\u4e0d\u4f1a\u54ed\uff0c\u53ef\u662f\u732a\u597d\u50cf\u4e5f \u4e0d\u4f1a\u7b11\u3002\u201d \u3000\u3000\u5927\u59d1\u5a18\u5374\u597d\u50cf\u5df2\u7ecf\u5feb\u8981\u88ab\u6c14\u75af\u4e86\uff0c\u53f9\u7740\u6c14\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u4f60\u4e0d\u662f\u732a\uff0c\u4f60\u662f\u4eba\uff0c\u662f\u4e2a\u597d \u4eba\uff0c\u6211\u53ea\u6c42\u4f60\u628a\u6211\u9001\u56de\u53bb\uff0c\u8d76\u5feb\u9001\u56de\u53bb\uff0c\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u628a\u4f60\u9001\u5230\u54ea\u91cc\u5927\uff0c\u201d \u3000\u3000\u201c\u9001\u56de\u6211\u4f4f\u7684\u5730\u65b9\uff0c\u201d\u5927\u59d1\u5a18\u8bf4\uff1a\u201c\u90a3\u4e2a\u5730\u65b9\u4ed6\u4eec\u662f\u7edd\u5bf9\u627e\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u627e\u4e0d\u5230\uff0c\u6211\u4e5f\u627e\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\u8fd9\u91cc\u4e00\u5b9a\u6709\u4e2a\u4eba\u662f\u80fd\u627e\u5f97\u5230\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5927\u59d1\u5a18\u53c8\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u8fd9\u4e2a\u4eba\u5c31\u662f\u6211\u3002\u201d \u56db \u3000\u3000\u4e00\u4e2a\u5e76\u4e0d\u7b97\u592a\u5927\u7684\u56db\u5408\u9662\uff0c\u5374\u4f4f\u7740\u5341\u516d\u5bb6\u4eba\u3002 \u3000\u3000\u8fd9\u5341\u516d\u5bb6\u4eba\u5f53\u7136\u90fd\u4e0d\u662f\u5f88\u6709\u529e\u6cd5\u7684\u4eba\uff0c\u53ea\u8981\u6709\u4e00\u70b9\u529e\u6cd5\u7684\u4eba\u5c31\u4e0d\u4f1a\u5f80\u5728\u8fd9\u91cc\u4e86\u3002 \u3000\u3000\u5982\u679c\u4f60\u60f3\u4e0d\u901a\u4e00\u5bb6\u4eba\u53e3\u600e\u4e48\u80fd\u6324\u5728\u4e00\u95f4\u9e3d\u5b50\u7b3c\u4e00\u6837\u7684\u5c0f\u5c4b\u91cc\u8fc7\u65e5\u5b50\uff0c\u90a3\u4e48\u4f60\u5c31\u5e94\u8be5\u5230\u8fd9 \u4e2a\u5927\u6742\u9662\u91cc\u6765\u770b\u770b\uff0c\u770b\u770b\u8fd9\u4e2a\u4e16\u754c\u4e0a\u67d0\u4e00\u4e9b\u4eba\u8fc7\u7684\u662f\u79cd\u4ec0\u4e48\u6837\u7684\u65e5\u5b50\u3002 \u3000\u3000\u6700\u8fd1\u8fd9\u4e2a\u5927\u6742\u9662\u91cc\u4f4f\u7684\u4eba\u5bb6\u53c8\u7531\u5341\u516d\u6237\u53d8\u6210\u4e86\u5341\u4e03\u6237\uff0c\u56e0\u4e3a\u8fd9\u91cc\u7684\u4e8c\u623f\u4e1c\u53c8\u628a\u540e\u9662\u91cc\u4e00 \u95f4\u7528\u6728\u677f\u642d\u6210\u7684\u67f4\u623f\u9694\u6210\u4e86\u4e24\u95f4\uff0c\u79df\u7ed9\u4e86\u4e00\u4e2a\u5916\u5730\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u603b\u662f\u6234\u7740\u9876\u7834\u6be1\u5e3d\uff0c\u957f\u7740\u4e00\u8138\u5927\u80e1\u5b50\u7684\u4eba\u3002 \u3000\u3000\u770b\u5230\u8fd9\u4e2a\u73b0\u5728\u5df2\u7ecf\u6ca1\u6709\u5927\u80e1\u5b50\u7684\u5927\u59d1\u5a18\u6240\u4f4f\u7684\u8fd9\u4e2a\u5730\u65b9\uff0c\u5c0f\u9ad8\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u201c\u9601\u4e0b\u4f4f\u7684\u8fd9\u4e2a\u516c\u9986\uff0c\u597d\u50cf\u4e5f\u4e0d\u6bd4\u6211\u90a3\u4e2a\u72d7\u7a9d\u597d\u591a\u5c11\u3002\u201d \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u7ecf\u628a\u5979\u9001\u4e86\u56de\u6765\u3002 \u3000\u3000\u5982\u679c\u662f\u5728\u767d\u5929\uff0c\u8fd9\u4e2a\u5927\u6742\u9662\u91cc\u9e21\u98de\u72d7\u8df3\u732b\u53eb\u4eba\u5435\u592b\u59bb\u76f8\u9a82\u59af\u5a0c\u6597\u5634\u8001\u5934\u5410\u75f0\u5b69\u5b50\u64a4\u5c3f\uff0c \u5c31\u7b97\u6709\u53ea\u82cd\u8747\u98de\u8fdb\u6765\uff0c\u4e5f\u4f1a\u88ab\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u5e78\u597d\u73b0\u5728\u5929\u5df2\u9ed1\u4e86\uff0c\u800c\u4e14\u4ed6\u4eec\u662f\u4ece\u540e\u9762\u8df3\u5899\u8fdb\u6765\u7684\u3002 \u3000\u3000\u5982\u679c\u4e00\u4e2a\u4eba\u8981\u8eb2\u8d77\u6765\uff0c\u518d\u60f3\u627e\u4e00\u4e2a\u6bd4\u8fd9\u91cc\u66f4\u96be\u627e\u7684\u5730\u65b9\u5c31\u5f88\u96be\u4e86\u3002 \u3000\u3000\u8fd9\u4f4d\u5927\u59d1\u5a18\u600e\u4e48\u80fd\u627e\u5230\u8fd9\u4e48\u6837\u4e00\u4e2a\u5730\u65b9\uff1f\u8fde\u5c0f\u9ad8\u90fd\u4e0d\u80fd\u4e0d\u4f69\u670d\u3002 \u3000\u3000\u8ba9\u4ed6\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u5979\u521a\u624d\u795e\u667a\u660e\u660e\u5df2\u7ecf\u5f88\u6e05\u9192\uff0c\u8eab\u5b50\u91cc\u7684\u6bd2\u597d\u50cf\u5df2\u7ecf\u88ab\u4ed6\u7684\u836f\u5b8c\u5168\u62e8\u4e86 \u51fa\u6765\uff0c\u53ef\u662f\u73b0\u5728\u5374\u53c8\u6655\u8ff7\u4e86\u8fc7\u53bb\uff0c\u800c\u4e14\u6bd4\u4e0a\u4e00\u6b21\u6655\u8ff7\u5f97\u66f4\u4e45\u3002 \u3000\u3000\u5c0f\u9ad8\u672c\u6765\u4e00\u76f4\u8ba4\u4e3a\u81ea\u5df1\u7684\u89e3\u836f\u7edd\u5bf9\u6709\u6548\uff0c\u73b0\u5728\u5374\u6709\u70b9\u6000\u7591\u4e86\u3002 \u3000\u3000\u662f\u5979\u4e2d\u7684\u6bd2\u592a\u6df1\uff0c\u5df2\u7ecf\u4fb5\u5165\u4e86\u5979\u7684\u9aa8\u9ad3\u8840\u8109\uff1f\u8fd8\u662f\u4ed6\u7684\u89e3\u836f\u529b\u91cf\u4e0d\u591f\uff1f \u3000\u3000\u4e0d\u7ba1\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5c0f\u9ad8\u5374\u5df2\u7ecf\u6ca1\u6cd5\u5b50\u5c31\u8fd9\u4e48\u6837\u4e00\u8d70\u4e86\u4e4b\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u60c5\u51b5\u4e00\u76f4\u90fd\u5f88\u4e0d\u7a33\u5b9a\uff0c\u6709\u65f6\u5019\u6655\u8ff7\uff0c\u6709\u65f6\u6e05\u9192\uff0c\u6655\u8ff7\u7684\u65f6\u5019\u5c31\u4f1a\u6d41\u7740\u51b7\u6c57\u8bf4\u4e00 \u4e9b\u53ef\u6015\u7684\u68a6\u5453\uff0c\u6e05\u9192\u7684\u65f6\u5019\u603b\u662f\u7528\u4e00\u53cc\u865a\u5f31\u65e0\u795e\u7684\u773c\u775b\u770b\u7740\u5c0f\u9ad8\uff0c\u597d\u50cf\u751f\u6015\u5c0f\u9ad8\u4f1a\u5f03\u5979\u800c \u53bb\u3002 \u3000\u3000\u5c0f\u9ad8\u53ea\u6709\u966a\u7740\u5979\uff0c\u8fde\u6bcf\u5929\u90fd\u8981\u53bb\u5403\u7684\u767d\u83dc\u716e\u9762\u90fd\u653e\u5f03\u4e86\u3002\u997f\u7684\u65f6\u5019\u5c31\u5230\u540e\u95e8\u5916\u53bb\u4e70\u51e0\u4e2a \u9992\u5934\u70d9\u8bb2\u5145\u9965\uff0c\u7d2f\u7684\u65f6\u5019\u5c31\u9760\u5728\u6905\u5b50\u4e0a\u7761\u4e00\u9635\u5b50\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u505a\uff0c\u5c45\u7136\u4f1a\u4e3a\u4e86\u4e00\u4e2a\u964c\u751f\u7684\u5973\u4eba\uff0c\u5b8c\u5168\u6539\u53d8\u4e86\u81ea\u5df1\u4ece\u672a\u6539 \u53d8\u8fc7\u7684\u751f\u6d3b\u89c4\u5f8b\u3002 \u3000\u3000\u5979\u65e0\u602a\u662f\u4e2a\u6781\u7f8e\u7684\u5973\u4eba\u3002 \u3000\u3000\u5c0f\u9ad8\u7b2c\u4e00\u6b21\u7528\u6e7f\u5e03\u628a\u5979\u8138\u4e0a\u7684\u7164\u7070\u548c\u51b7\u6c57\u90fd\u64e6\u5e72\u51c0\u4e86\u7684\u65f6\u5019\uff0c\u5c31\u53d1\u73b0\u5979\u4e0d\u4f46\u6709\u4e00\u53cc\u6781\u7f8e \u7684\u817f\uff0c\u5bb9\u8c8c\u4e5f\u6781\u7f8e\u3002 \u3000\u3000\u53ef\u662f\u5982\u679c\u6709\u4eba\u8bf4\u5c0f\u9ad8\u5df2\u7ecf\u5728\u559c\u6b22\u5979\u4e86\uff0c\u6240\u4ee5\u624d\u4f1a\u7559\u4e0b\u6765\uff0c\u5c0f\u9ad8\u662f\u6b7b\u4e5f\u4e0d\u4f1a\u627f\u8ba4\u7684\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u76ee\u4e2d\u4ece\u6765\u4e5f\u6ca1\u6709\u60f3\u5230\u8fc7\u5973\u4eba\uff0c\u4ed6\u4e00\u76f4\u8ba4\u4e3a\u5973\u4eba\u5728\u4ed6\u5fc3\u91cc\u7684\u5730\u4f4d\uff0c\u53ea\u4e0d\u8fc7\u597d\u50cf\u662f\u4e00 \u7c92\u7a17\u5b50\u5728\u4e00\u5927\u9505\u767d\u996d\u91cc\u7684\u5730\u4f4d\u4e00\u6837\u3002 \u3000\u3000\u90a3\u4e48\u4ed6\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u662f\u4e3a\u4e86\u5979\u5904\u5883\u7684\u60b2\u60e8\uff1f\u8fd8\u662f\u4e3a\u4e86\u90a3\u4e00\u53cc\u867d\u7136\u9ed8\u9ed8\u65e0\u8a00\u5374\u5145\u6ee1\u4e86\u611f\u6fc0\u548c\u6073\u6c42\u7684\u773c\u775b\uff1f \u3000\u3000\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u60c5\u611f\uff0c\u5c82\u975e\u672c\u672a\u5c31\u662f\u7b2c\u4e09\u8005\u6c38\u8fdc\u65e0\u6cd5\u4e86\u89e3\u4e5f\u65e0\u6cd5\u89e3\u91ca\u7684\u3002 \u3000\u3000\u65e5\u5b50\u597d\u50cf\u5df2\u7ecf\u8fc7\u4e86\u4e24\u4e09\u5929\uff0c\u5c0f\u9ad8\u867d\u7136\u89c9\u5f97\u81ea\u5df1\u53c8\u8d43\u53c8\u7d2f\uff0c\u53ef\u662f\u4e00\u70b9\u90fd\u4e0d\u540e\u6094\u3002 \u3000\u3000\u5982\u679c\u540c\u6837\u7684\u4e8b\u518d\u53d1\u751f\u4e00\u6b21\uff0c\u4ed6\u8fd8\u662f\u4f1a\u8fd9\u4e48\u505a\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u5929\u6765\uff0c\u5979\u867d\u7136\u8fde\u4e00\u53e5\u8bdd\u90fd\u6ca1\u6709\u5bf9\u4ed6\u8bf4\u8fc7\uff0c\u53ef\u662f\u770b\u4ed6\u7684\u773c\u795e\u5c31\u53ef\u4ee5\u770b\u51fa\u5979\u5df2\u7ecf\u628a\u4ed6\u5f53 \u505a\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u5f53\u4f5c\u4e86\u8fd9\u4e2a\u4e16\u754c\u4e0a\u552f\u4e00\u7684\u4f9d\u9760\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u662f\u79cd\u4ec0\u4e48\u6837\u7684\u611f\u89c9\uff1f \u3000\u3000\u5c0f\u9ad8\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u5fc3\u91cc\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u4ed6\u8fd9\u4e00\u751f\u4e2d\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u8fd9\u4e48\u6837\u4f9d\u8d56\u8fc7\u4ed6\u3002 \u3000\u3000\u6709\u4e00\u5929\u4ed6\u9192\u6765\u65f6\uff0c\u5c31\u53d1\u73b0\u5979\u53c8\u5728\u9ed8\u9ed8\u7684\u770b\u7740\u4ed6\uff0c\u9ed8\u9ed8\u7684\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u8bf4\uff1a\u201c\u4f60\u7d2f\u4e86\uff0c \u4f60\u4e5f\u5e94\u8be5\u8eba\u4e0b\u6765\u7761\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u8f7b\u67d4\u5e73\u6de1\uff0c\u5c0f\u9ad8\u4e5f\u6beb\u4e0d\u8003\u8651\u5c31\u8eba\u4e86\u4e0b\u53bb\uff0c\u8eba\u5728\u5979\u8ba9\u51fa\u6765\u7684\u534a\u8fb9\u7a7a\u5e8a\u4e0a\u3002\u4e24\u4e2a\u4eba \u597d\u50cf\u90fd\u89c9\u5f97\u8fd9\u662f\u4ef6\u5f88\u81ea\u7136\u7684\u4e8b\uff0c\u5c31\u597d\u50cf\u6625\u98ce\u5439\u904d\u5927\u5730\u65f6\u82b1\u6735\u4e00\u5b9a\u4f1a\u5f00\u653e\u90a3\u4e48\u81ea\u7136\u3002 \u3000\u3000\u5c0f\u9ad8\u4e00\u8eba\u4e0b\u53bb\u5c31\u7761\u7740\u4e86\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u5927\u7d2f\uff0c\u6240\u4ee5\u4e00\u7761\u5c31\u7761\u5f97\u5f88\u719f\uff0c\u4e5f\u4e0d\u77e5\u9053\u7761\u4e86\u591a\u4e45\uff0c\u9192\u6765\u65f6\u5df2\u7ecf\u5feb\u5230\u9ec4\u660f\u4e86\u3002 \u3000\u3000\u7761\u5728\u4ed6\u8eab\u65c1\u7684\u4eba\u5df2\u7ecf\u8d77\u6765\u68b3\u6d17\u8fc7\uff0c\u6362\u4e86\u8eab\u8863\u88f3\uff0c\u7528\u4e00\u6839\u4e1d\u5e26\u675f\u4f4f\u4e86\u6ee1\u5934\u6d41\u6c34\u822c\u67d4\u6ed1\u7684\u957f \u53d1\uff0c\u5750\u5728\u4ed6\u5e8a\u5934\u9ed8\u9ed8\u7684\u770b\u7740\u4ed6\u3002 \u3000\u3000\u7a97\u5916\u7684\u5929\u8272\u5df2\u7ecf\u6e10\u6e10\u6697\u4e86\uff0c\u547c\u5578\u7684\u5bd2\u98ce\u5df2\u7ecf\u6e10\u6e10\u505c\u4e86\u3002 \u3000\u3000\u5929\u5730\u95f4\u4e00\u7247\u5e73\u9759\u6e29\u67d4\uff0c\u5979\u5ffd\u7136\u8f7b\u8f7b\u664c\u95ee\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u8fde\u6211\u7684\u540d\u5b57\u90fd\u4e0d\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u8981\u5bf9\u6211\u8fd9\u4e48\u597d\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u5c0f\u9ad8\u8bf4\u3002 \u3000\u3000\u4ed6\u771f\u7684\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u4ed6\u53ea\u77e5\u9053\u4ed6\u5df2\u7ecf\u9047\u5230\u4e86\u8fd9\u4e48\u6837\u4e00\u4e2a\u5973\u4eba\uff0c\u5df2\u7ecf\u505a\u51fa\u4e86\u8fd9\u4e48\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u522b\u7684\u4ed6\u5168\u90fd\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u5979\u5ffd\u7136\u8f7b\u8f7b\u7684\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u4e0d\u77e5\u9053\u4f60\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u4e5f\u4e0d\u77e5\u9053\u4f60\u7684\u540d\u5b57\uff0c\u201d \u5979\u8f7b\u629a\u7740\u4ed6\u7684\u8138\uff1a\u201c\u53ef\u662f\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4e5f\u4f1a\u8ba9\u51fa\u4e2a\u5730\u65b9\u6765\u8ba9\u6211\u8eba\u4e00\u8eba\u3002\u201d \u3000\u3000\u4ed6\u8ba9\u51fa\u4e2a\u5730\u65b9\uff0c\u5979\u5c31\u8eba\u4e86\u4e0b\u53bb\uff0c\u8eba\u5728\u4ed6\u8eab\u8fb9\uff0c\u8eba\u5728\u4ed6\u7684\u6000\u62b1\u91cc\u3002 \u3000\u3000\u6240\u6709\u4e00\u5207\u4e8b\u7684\u53d1\u751f\u90fd\u90a3\u4e48\u81ea\u7136\uff0c\u5c31\u597d\u50cf\u6625\u96e8\u6ecb\u6da6\u5927\u5730\u65f6\uff0c\u4e07\u7269\u90fd\u4e00\u5b9a\u4f1a\u751f\u957f\u90a3\u4e48\u81ea\u7136\u3002 \u3000\u3000\u90a3\u4e48\u81ea\u7136\uff0c\u90a3\u4e48\u7f8e\uff0c\u7f8e\u5f97\u8ba9\u4eba\u5fc3\u9189\u3002 \u4e94 \u3000\u3000\u9759\u9759\u7684\u5bd2\u591c\uff0c\u9759\u9759\u7684\u957f\u8857\u3002 \u3000\u3000\u4ed6\u4eec\u624b\u633d\u7740\u624b\uff0c\u8e0f\u7740\u6ee1\u8857\u7684\u79ef\u96ea\uff0c\u627e\u5230\u4e86\u4e00\u4e2a\u6446\u5728\u5c4b\u6a90\u4e0b\u7684\u5c0f\u644a\u5b50\uff0c\u5403\u4e86\u7897\u53c8\u9999\u53c8\u8fa3\u53c8 \u70eb\u7684\u7f8a\u8089\u6ce1\u998d\u3002 \u3000\u3000\u4ed6\u4eec\u6ca1\u6709\u559d\u9152\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u7ecf\u4e0d\u9700\u8981\u7528\u9152\u6765\u6fc0\u53d1\u4ed6\u4eec\u7684\u70ed\u60c5\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4eec\u53c8\u624b\u633d\u7740\u624b\uff0c\u8d70\u56de\u5c0f\u9ad8\u4f4f\u7684\u90a3\u5bb6\u5c0f\u5ba2\u6808\uff0c\u56e0\u4e3a\u5c0f\u9ad8\u8fd8\u6709\u4e9b\u4e1c\u897f\u7559\u5728\u90a3\u91cc\u3002 \u3000\u3000\u521a\u8f6c\u8fc7\u90a3\u6761\u8857\u7684\u8857\u53e3\uff0c\u4ed6\u4eec\u5c31\u53d1\u73b0\u4e00\u4ef6\u5f88\u5947\u602a\u7684\u4e8b\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u88ab\u4ed6\u638c\u5fc3\u6e29\u70ed\u4e86\u7684\u624b\uff0c\u5ffd\u7136\u53d8\u5f97\u51b0\u51b7\u3002 \u3000\u3000\u5ba2\u6808\u7684\u95e8\u5df2\u7ecf\u5173\u4e86\uff0c\u53ef\u662f\u5728\u5ba2\u6808\u95e8\u5916\u90a3\u76cf\u660f\u9ec4\u7684\u706f\u7b3c\u4e0b\u5374\u7ad9\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u50cf\u6728\u5934\u4eba\u4e00\u6837\u7684\u4eba\uff0c\u52a8\u4e5f\u4e0d\u52a8\u7684\u7ad9\u5728\u51ac\u591c\u7684\u5bd2\u98ce\u91cc\uff0c\u4e00\u5f20\u8138\u5df2\u88ab\u51bb\u5f97\u53d1\u7d2b\uff0c\u4f46\u6001\u5ea6 \u5374\u8fd8\u662f\u5f88\u6c89\u9759\u3002 \u3000\u3000\u5c0f\u9ad8\u63e1\u7d27\u5979\u51b0\u51b7\u7684\u4e01\uff0c\u8f7b\u8f7b\u7684\u8bf4\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u662f\u6765\u627e\u4f60\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u5927\u9556\u5c40\u91cc\u7684\u4eba\uff0c\u6b63\u6708\u5341\u4e94\u90a3\u5929\u6211\u89c1\u8fc7\u4ed6\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u89c1\u8fc7\u4e00\u9762\u7684\u4eba\u4f60\u5c31\u4e0d\u4f1a\u5fd8\u8bb0\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8fd8\u6ca1\u6709\u8d70\u8fc7\u53bb\uff0c\u8fd9\u4e2a\u4eba\u679c\u7136\u5df2\u7ecf\u606d\u606d\u656c\u656c\u7684\u5bf9\u5c0f\u9ad8\u8eac\u8eab\u884c\u793c\xb7 \u3000\u3000\u201c\u5c0f\u4eba\u5b59\u8fbe\uff0c\u62dc\u89c1\u9ad8\u5927\u4fa0\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6b63\u6708\u5341\u4e94\u90a3\u4e00\u5929\uff0c\u5c0f\u4eba\u66fe\u7ecf\u89c1\u8fc7\u9ad8\u5927\u4fa0\u4e00\u9762\uff0c\u201d\u5b59\u8fbe\u6c89\u7a33\u7684\u8bf4\uff1a\u201c\u5c31\u5728\u6768\u575a\u88ab\u523a\u7684\u90a3 \u95f4\u5bc6\u5ba4\u5916\u89c1\u5230\u7684\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u89c1\u8fc7\u4e00\u9762\u7684\u4eba\u4f60\u5c31\u4e0d\u4f1a\u5fd8\u8bb0\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7b11\u4e86\uff1a\u201c\u6211\u4e5f\u8bb0\u5f97\u4f60\uff0c\u4f60\u662f\u90a3\u5929\u552f\u4e00\u6ca1\u6709\u88ab\u6211\u51fb\u5012\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u9ad8\u5927\u4fa0\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u201c\u4f60\u7ad9\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u662f\u4e0d\u662f\u5728\u7b49\u6211\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5b59\u8fbe\u8bf4\uff1a\u201c\u5c0f\u4eba\u5df2\u7ecf\u5728\u8fd9\u91cc\u7b49\u4e86\u4e24\u5929\u4e00\u591c\u3002\u201d \u3000\u3000\u201c\u4e00\u76f4\u90fd\u8fd9\u4e48\u6837\u7ad9\u5728\u8fd9\u91cc\u7b49\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e24\u5929\u9ad8\u5927\u4fa0\u884c\u8e2a\u4e0d\u5b9a\uff0c\u5c0f\u4eba\u751f\u6015\u9519\u8fc7\uff0c\u6240\u4ee5\u5bf8\u6b65\u90fd\u4e0d\u6562\u79bb\u5f00\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u8fd8\u4e0d\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u5c0f\u4eba\u5c31\u53ea\u6709\u5728\u8fd9\u91cc\u7b49\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u8fd8\u8981\u518d\u8fc7\u4e09\u5929\u4e09\u591c\u5bf8\u56de\u6765\uff0c\u4f60\u5c31\u8fd9\u4e48\u6837\u7ad9\u5728\u8fd9\u91cc\u518d\u7b49\u6211\u4e09\u5929\u4e09\u591c\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u9ad8\u5927\u4fa0\u8fd8\u8981\u518d\u8fc7\u4e09\u4e2a\u6708\u624d\u4f1a\u56de\u6765\uff0c\u5c0f\u4eba\u4e5f\u4e00\u6837\u4f1a\u7ad9\u5728\u8fd9\u91cc\u7b49\u7684\u3002\u201d\u5b59\u8fbe\u5e73\u5e73\u9759\u9759 \u7684\u8bf4\u3002 \u3000\u3000\u201c\u662f\u8c01\u8981\u4f60\u8fd9\u4e48\u505a\u7684\uff1f\u201d\u5c0f\u9ad8\u95ee\u4ed6\uff0c\u201c\u662f\u4e0d\u662f\u5353\u4e1c\u6765\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u8981\u4f60\u53bb\u505a\u4ec0\u4e48\uff0c\u4f60\u90fd\u4f1a\u53bb\u505a\uff1f\u201d \u3000\u3000\u201c\u5353\u5148\u751f\u4e00\u5411\u4ee4\u51fa\u5982\u5c71\uff0c\u81f3\u4eca\u8fd8\u6ca1\u6709\u4eba\u6562\u8fdd\u6297\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u6837\u542c\u4ed6\u7684\u8bdd\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4eba\u4e0d\u77e5\u9053\u3002\u201d\u5b59\u8fbe\u8bf4\uff1a\u201c\u5c0f\u4eba\u53ea\u77e5\u9053\u670d\u4ece\u547d\u4ee4\uff0c\u4ece\u672a\u60f3\u5230\u8fc7\u662f\u4e3a\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u9ad8\u6e10\u98de\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u8fd9\u4e2a\u4eba\u5b9e\u5728\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\uff0c\u4e0d\u4f46\u6709\u80c6\u8bc6\u6709\u8c0b\u7565\u6709\u773c\u5149\uff0c\u800c\u4e14\u6709\u5927 \u5c06\u4e4b\u5bf8\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u4e00\u76f4\u4e0d\u660e\u767d\uff0c\u4f60\u4eec\u8fd9\u4e2a\u5927\u9556\u5c40\u7684\u5927\u9f99\u5934\u4e3a\u4ec0\u4e48\u4e0d\u662f\u4ed6\uff1f\u201d \u3000\u3000\u5b59\u8fbe\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\uff0c\u597d\u50cf\u6839\u672c\u6ca1\u6709\u542c\u5230\u8fd9\u4e9b\u8bdd\uff0c\u5374\u4ece\u8863\u895f\u91cc\u62ff\u51fa\u4e00\u5f20\u5927\u7ea2\u62dc\u5e16\uff0c\u606d\u606d\u656c \u656c\u7684\u7528\u53cc\u624b\u5949\u4e0a\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u5353\u5148\u751f\u7279\u5730\u8981\u5c0f\u4eba\u6765\u4ea4\u7ed9\u9ad8\u5927\u4fa0\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u5728\u8fd9\u91cc\u7ad9\u4e86\u4e24\u5929\u4e00\u591c\uff0c\u5c31\u4e3a\u4e86\u8981\u628a\u8fd9\u5f20\u5e16\u5b50\u4ea4\u7eb7\u6211\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u5982\u679c\u4f60\u628a\u5b83\u7559\u5728\u67dc\u53f0\uff0c\u6211\u4e5f\u4e00\u6837\u80fd\u770b\u5f97\u5230\u3002\u201d \u3000\u3000\u201c\u5c0f\u4eba\u6ca1\u6709\u53bb\u60f3\uff0c\u201d\u5b59\u8fbe\u8bf4\uff1a\u201c\u6709\u5f88\u591a\u4e8b\u5c0f\u4eba\u90fd\u4ece\u6765\u6ca1\u6709\u53bb\u60f3\u8fc7\uff0c\u60f3\u5f97\u592a\u591a\u5e76\u4e0d\u662f\u4ef6\u597d \u4e8b\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u201c\u5bf9\uff0c\u4f60\u8bf4\u5f97\u5bf9\u3002\u201d\u4ed6\u63a5\u8fc7\u62dc\u5e16\uff1a\u201c\u4ee5\u540e\u6211\u4e00\u5b9a\u4e5f\u8981\u5b66\u5b66\u4f60\u3002\u201d \u3000\u3000\u9ad8\u6e10\u98de\u7528\u4e0d\u7740\u6253\u5f00\u8fd9\u5f20\u62dc\u5e16\uff0c\u5c31\u5df2\u7ecf\u77e5\u9053\u5b83\u5e76\u4e0d\u662f\u4e00\u5f20\u62dc\u5e16\uff0c\u800c\u662f\u4e00\u5c01\u6218\u4e66\u3002 \u3000\u3000\u4e00\u5c01\u7b80\u5355\u800c\u660e\u4e86\u7684\u6218\u4e66\u3002 \u3000\u3000\u201c\u4e8c\u6708\u521d\u4e00\uff0c\u51cc\u6668\u3002 \u3000\u3000\u674e\u5e84\uff0c\u6148\u6069\u5bfa\uff0c\u5927\u96c1\u5854\u3002 \u3000\u3000\u53f8\u9a6c\u8d77\u7fa4\u3002\u201d \u3000\u3000\u201c\u4e8c\u6708\u521d\u4e00\uff0c\u201d\u5c0f\u9ad8\u95ee\u5b59\u8fbe\uff1a\u201c\u4eca\u5929\u662f\u4ec0\u4e48\u65e5\u5b50\uff1f\u201d \u3000\u3000\u201c\u4eca\u5929\u662f\u6b63\u6708\u5345\u65e5\u3002\u201d \u3000\u3000\u201c\u4ed6\u8ba2\u7684\u65e5\u5b50\u5c31\u662f\u660e\u5929\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5b59\u8fbe\u53c8\u606d\u606d\u656c\u656c\u7684\u884c\u793c\uff1a\u201c\u5c0f\u4eba\u544a\u8f9e\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8eab\u8d70\u51fa\u4e86\u4e00\u6bb5\u8def\uff0c\u5c0f\u9ad8\u5ffd\u7136\u53c8\u628a\u4ed6\u53eb\u4f4f\u3002 \u3000\u3000\u201c\u4f60\u53eb\u5b59\u8fbe\uff1f\u201d\u4ed6\u95ee\u8fd9\u4e2a\u575a\u6bc5\u6c89\u7a33\u7684\u5e74\u8f7b\u4eba\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5b59\u901a\u7684\u5144\u5f1f\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5b59\u8fbe\u7684\u811a\u6b65\u505c\u4e86\u4e00\u4e0b\uff0c\u5374\u6ca1\u6709\u56de\u5934\uff0c\u201c\u5c0f\u4eba\u662f\u5b59\u901a\u7684\u5144\u5f1f\u3002\u201d \u3000\u3000\u5bd2\u591c\uff0c\u5bd2\u5982\u5200\u950b\u3002 \u3000\u3000\u770b\u7740\u5b59\u8fbe\u5728\u96ea\u5149\u53cd\u6620\u7684\u9053\u8def\u4e0a\u6e10\u6e10\u53bb\u8fdc\uff0c\u5c0f\u9ad8\u5ffd\u7136\u95ee\u4e00\u76f4\u9ed8\u9ed8\u7684\u4f9d\u504e\u5728\u4ed6\u8eab\u65c1\u7684\u5973\u4eba\uff1a \u201c\u4f60\u6709\u6ca1\u6709\u6ce8\u610f\u5230\u4e00\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u4e2a\u975e\u5e38\u597d\u770b\u7684\u5973\u4eba\uff0c\u7537\u4eba\u7684\u773c\u775b\u751f\u6765\u5c31\u662f\u4e3a\u4e86\u8981\u770b\u4f60\u8fd9\u79cd\u5973\u4eba\u7684\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a \u201c\u53ef\u662f\u5b59\u8fbe\u59cb\u7ec8\u90fd\u6ca1\u6709\u770b\u8fc7\u4f60\u4e00\u773c\u3002\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u4ed6\u770b\uff1f\u4f60\u4e3a\u4ec0\u4e48\u8981\u4ed6\u770b\u6211\uff1f\u201d\u5979\u597d\u50cf\u6709\u70b9\u751f\u6c14\u4e86\uff1a\u201c\u96be\u9053\u4f60\u4e00\u5b9a\u8981\u522b\u7684\u7537 \u4eba\u6b7b\u76ef\u7740\u6211\u770b\u4f60\u624d\u9ad8\u5174\uff1f\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u4e0d\u8ba9\u5979\u751f\u6c14\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\u88ab\u5979\u7684\u60c5\u4eba\u7d27\u7d27\u62b1\u4f4f\u7684\u65f6\u5019\uff0c\u662f\u4ec0\u4e48\u6c14\u90fd\u751f\u4e0d\u51fa\u6765\u7684\u3002 \u3000\u3000\u201c\u5176\u5b9e\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u662f\u4ec0\u4e48\u610f\u601d\u4e86\u3002\u201d\u5979\u67d4\u58f0\u8bf4\uff1a\u201c\u4f60\u53ea\u4e0d\u8fc7\u60f3\u544a\u8bc9\u6211\uff0c\u5b59\u8fbe\u8fd9\u4e2a\u4eba\u4e5f \u4e0d\u662f\u4e2a\u7b80\u5355\u7684\u4eba\uff0c\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u66f4\u6e29\u67d4\uff1a\u201c\u53ef\u662f\u6211\u5e76\u4e0d\u60f3\u8981\u4f60\u544a\u8bc9\u6211\u8fd9\u4e9b\u4e8b\u3002\u6211\u4e5f\u4e0d\u60f3\u77e5\u9053\u8fd9\u4e9b\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u60f3\u77e5\u9053\uff0c\u53f8\u9a6c\u8d85\u7fa4\u4e3a\u4ec0\u4e48\u8981\u7ea6\u4f60\u660e\u5929\u5230\u5927\u96c1\u5854\u53bb\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\u4e5f\u4e0d\u662f\u4ed6\u7ea6\u6211\u7684\uff0c\u662f\u6211\u7ea6\u4e86\u4ed6\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6b63\u6708\u5341\u4e94\u90a3\u4e00\u5929\uff0c\u6211\u5df2\u7ecf\u7ea6\u4e86 \u4ed6\uff0c\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u7ea6\u4ed6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e5f\u60f3\u77e5\u9053\u4e00\u4ef6\u4e8b\u3002\u201d\u5c0f\u9ad8\u8bf4\uff0c\u201c\u6211\u4e00\u76f4\u90fd\u60f3\u77e5\u9053\uff0c\u6c38\u8fdc\u4e0d\u8d25\u7684\u53f8\u9a6c\u8d85\u7fa4\uff0c\u662f\u4e0d \u662f\u771f\u7684\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u88ab\u4eba\u51fb\u8d25\uff1f\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5c31\u5df2\u7ecf\u53d1\u89c9\u5979\u7684\u624b\u5ffd\u7136\u53c8\u53d8\u5f97\u51b0\u51b7\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4ee5\u4e3a\u5979\u4f1a\u8981\u6c42\u4ed6\uff0c\u6c42\u4ed6\u660e\u5929\u4e0d\u8981\u53bb\uff0c\u514d\u5f97\u5979\u5bb3\u6015\u62c5\u5fc3\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u5979\u5374\u544a\u8bc9\u4ed6\uff1a\u201c\u660e\u5929\u4f60\u5f53\u7136\u4e00\u5b9a\u8981\u53bb\uff0c\u800c\u4e14\u4e00\u5b9a\u4f1a\u51fb\u8d25\u4ed6\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u53ef\u662f\u4f60\u4e5f \u8981\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4eca\u5929\u665a\u4e0a\u4e0d\u8bb8\u4f60\u78b0\u6211\uff0c\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u5c31\u4e0d\u8bb8\u78b0\u6211\u3002\u201d\u5979\u5df2\u7ecf\u628a\u5c0f\u9ad8\u63a8\u5f00\u4e86\uff1a\u201c\u6211\u8981\u4f60 \u73b0\u5728\u5c31\u8ddf\u6211\u56de\u53bb\uff0c\u597d\u597d\u7684\u7761\u4e00\u89c9\u3002\u201d \u516d \u3000\u3000\u5c0f\u9ad8\u6ca1\u7761\u597d\u3002\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u8eab\u65c1\u6709\u53cc\u4fee\u957f\u7ed3\u5b9e\u7f8e\u4e3d\u7684\u817f\uff0c\u4e5f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u5bf9\u660e\u6668\u90a3\u4e00\u6218\u7684 \u7d27\u5f20\u7126\u8651\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5df2\u7ecf\u7761\u7740\u3002 \u3000\u3000\u4ed6\u5bf9\u81ea\u5df1\u6709\u4fe1\u5fc3\uff0c\u5bf9\u4ed6\u8eab\u8fb9\u7684\u4eba\u4e5f\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u7b49\u6211\u56de\u6765\u7684\u3002\u201d\u5c0f\u9ad8\u5bf9\u5979\u8bf4\uff1a\u201c\u4e5f\u8bb8\u4f60\u8fd8\u6ca1\u6709\u7761\u9192\u6211\u5c31\u5df2\u7ecf\u56de\u6765 \u4e86\u3002\u201d \u3000\u3000\u4f46\u662f\u5979\u5374\u95ee\u4ed6\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u7b49\u4f60\u56de\u6765\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8ddf\u4f60\u53bb\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u662f\u4e2a\u5973\u4eba\uff0c\u5973\u4eba\u901a\u5e38\u90fd\u6bd4\u8f83\u5bb9\u6613\u7d27\u5f20\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u548c\u53f8\u9a6c\u8d85\u7fa4\u4ea4\u624b\uff0c\u751f\u6b7b \u80dc\u8d1f\u53ea\u4e0d\u8fc7\u662f\u4e00\u77ac\u95f4\u7684\u4e8b\uff0c\u4f60\u770b\u5230\u4e00\u5b9a\u4f1a\u7d27\u5f20\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff0c\u201c\u4f60\u7d27\u5f20\uff0c\u6211\u5c31\u4f1a\u7d27\u5f20\uff0c\u6211\u7d27\u5f20\uff0c\u6211\u5c31\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u201c\u4f60\u80fd\u4e0d\u80fd\u627e\u4e00\u4e2a\u4e0d\u4f1a\u7d27\u5f20\u7684\u4eba\u966a\u4f60\u53bb\uff0c\u4e5f\u597d\u5728\u65c1\u8fb9\u7167\u987e\u4f60\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u627e\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u6ca1\u6709\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\u8fde\u4e00\u4e2a\u90fd\u6ca1\u6709\u7684\uff0c\u73b0\u5728\u603b\u7b97\u6709\u4e86\u4e00\u4e2a\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u7684\u4eba\u5728\u6d1b\u9633\u3002\u201d \u3000\u3000\u201c\u6d1b\u9633\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u4e5f\u5230\u6d1b\u9633\u53bb\u8fc7\uff0c\u5c31\u4e00\u5b9a\u542c\u5230\u8fc7\u4ed6\u7684\u540d\u5b57\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4ed6\u59d3\u6731\uff0c\u53eb\u6731\u731b\u3002\u201d \u3000\u3000\u5979\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u518d\u8bf4\uff0c\u5c0f\u9ad8\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u5979\u7684\u795e\u8272\u6709\u4ec0\u4e48\u6539\u53d8\u3002 \u3000\u3000\u4ed6\u53c8\u5f00\u59cb\u5728\u7ec3\u4e60\u90a3\u4e9b\u5947\u79d8\u800c\u602a\u5f02\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u8fd9\u79cd\u7ec3\u4e60\u4e0d\u4f46\u80fd\u4f7f\u4ed6\u7684\u808c\u8089\u7075\u6d3b\uff0c\u7cbe\u529b\u5145\u6c9b\uff0c\u8fd8\u80fd\u6f84\u6e05\u4ed6\u7684\u601d\u60f3\u3002\u5b89\u5b9a\u4ed6\u7684\u60c5\u7eea\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5f88\u5feb\u5c31\u7761\u7740\u4e86\uff0c\u7761\u5f97\u5f88\u6c89\u3002\u901a\u5e38\u90fd\u53ef\u4ee5\u4e00\u89c9\u7761\u5230\u5929\u4eae\u3002 \u3000\u3000\u4f46\u662f\u4eca\u5929\u665a\u4e0a\u4ed6\u7761\u5230\u534a\u591c\u5c31\u5ffd\u7136\u60ca\u9192\uff0c\u88ab\u4e00\u79cd\u5f88\u5947\u602a\u7684\u611f\u89c9\u6240\u60ca\u9192\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u662f\u5929\u5730\u95f4\u6700\u5b89\u9759\u7684\u65f6\u5019\uff0c\u751a\u81f3\u8fde\u96ea\u82b1\u8f7b\u8f7b\u98d8\u843d\u5728\u5c4b\u810a\u4e0a\u7684\u58f0\u97f3\u90fd\u80fd\u542c\u5f97\u5230\u3002 \u3000\u3000\u8fd9\u79cd\u58f0\u97f3\u662f\u7edd\u4e0d\u4f1a\u5435\u9192\u4efb\u4f55\u4eba\u7684\u3002 \u3000\u3000\u672c\u6765\u5c0f\u9ad8\u8fd8\u5728\u5947\u602a\uff0c\u4e0d\u660e\u767d\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4f1a\u5ffd\u7136\u9192\u8fc7\u6765\u6765\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5f88\u5feb\u5c31\u660e\u767d\u4e86\u3002 \u3000\u3000\u4e00\u5c4b\u5b50\u91cc\u5df2\u7ecf\u53ea\u5269\u4e0b\u4ed6\u4e00\u4e2a\u4eba\uff0c\u7761\u5728\u4ed6\u8eab\u8fb9\u7684\u4eba\u5df2\u7ecf\u4e0d\u5728\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5ffd\u7136\u4ece\u4e07\u4e08\u9ad8\u697c\u4e0a\u843d\u4e0b\u53bb\u65f6\u662f\u4ec0\u4e48\u611f\u89c9\uff1f \u3000\u3000\u73b0\u5728\u5c0f\u9ad8\u5fc3\u91cc\u5c31\u662f\u8fd9\u79cd\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u5934\u8111\u5ffd\u7136\u4e00\u9635\u6655\u7729\uff0c\u5168\u8eab\u90e8\u5df2\u865a\u8131\uff0c\u7136\u540e\u5c31\u5fcd\u4e0d\u4f4f\u5f2f\u4e0b\u8170\u53bb\u5f00\u59cb\u5455\u5410\u3002 \u3000\u3000\u56e0\u4e3a\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5df2\u7ecf\u611f\u89c9\u5230\u5979\u8fd9\u4e00\u53bb\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u518d\u56de\u5230\u4ed6\u8eab\u8fb9\u6765\u3002 \u3000\u3000\u5979\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f \u3000\u3000\u4e3a\u4ec0\u4e48\u8fde\u4e00\u4e2a\u5b57\u4e00\u53e5\u8bdd\u90fd\u6ca1\u6709\u7559\u4e0b\uff0c\u5c31\u8fd9\u4e48\u6837\u6084\u6084\u7684\u8d70\u4e86\uff1f \u3000\u3000\u5c0f\u9ad8\u60f3\u4e0d\u901a\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u5c31\u65e0\u6cd5\u601d\u60f3\u3002 \u3000\u3000\u5728\u8fd9\u4e2a\u9759\u5bc2\u7684\u5bd2\u591c\u4e2d\uff0c\u6700\u5bd2\u51b7\u5bc2\u9759\u7684\u4e00\u6bb5\u65f6\u95f4\u91cc\uff0c\u4ed6\u53ea\u60f3\u5230\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u2014\u2014\u4ed6\u751a\u81f3\u8fde\u5979\u53eb\u505a\u4ec0\u4e48\u540d\u5b57\u90fd\u4e0d\u77e5\u9053\u3002 \u7b2c\u516d\u7ae0\u3000\u4e03\u7ea7\u6d6e\u5c60>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u516d\u7ae0\u3000\u4e03\u7ea7\u6d6e\u5c60 \u4e00 \u3000\u3000\u4e8c\u6708\u521d\u4e00\u3002 \u3000\u3000\u674e\u5e84\uff0c\u6148\u6069\u5bfa\u3002 \u3000\u3000\u51cc\u6668\u3002 \u3000\u3000\u4ece\u6628\u591c\u5f00\u59cb\u4e0b\u7684\u96ea\uff0c\u76f4\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u505c\uff0c\u628a\u8fd9\u4e2a\u79ef\u96ea\u521a\u88ab\u6253\u626b\u5e72\u51c0\u7684\u7985\u9662\uff0c\u53c8\u94fa\u4e0a\u4e00\u5c42 \u94f6\u767d\u3002 \u3000\u3000\u6668\u949f\u5df2\u54cd\u8fc7\uff0c\u5bd2\u98ce\u4e2d\u9690\u9690\u4f20\u6765\u4e00\u9635\u9635\u68b5\u5531\uff0c\u4f20\u5165\u4e86\u53f3\u9762\u7684\u4e00\u95f4\u7985\u623f\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u9759\u9759\u5750\u5728\u4e00\u5f20\u7985\u5e8a\u4e0a\u542c\u7740\uff0c\u9759\u9759\u7684\u5728\u559d\u4e00\u74f6\u6628\u591c\u4ed6\u81ea\u5df1\u5e26\u6765\u7684\u51b7\u9152\u3002 \u3000\u3000\u51b7\u5f97\u50cf\u51b0\uff0c\u559d\u4e0b\u53bb\u5374\u597d\u50cf\u6709\u706b\u7130\u5728\u71c3\u70e7\u4e00\u6837\u7684\u767d\u9152\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5df2\u7ecf\u8fdb\u6765\u4e86\uff0c\u4e00\u76f4\u5728\u51b7\u51b7\u7684\u770b\u7740\u4ed6\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5374\u88c5\u4f5c\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u5f00\u53e3\uff1a\u201c\u73b0\u5728\u5c31\u5f00\u59cb\u559d\u9152\u662f\u4e0d\u662f\u5acc\u5927\u65e9\u4e86\u4e00\u70b9\uff1f\u201d\u4ed6\u51b7\u51b7\u7684\u95ee\u53f8\u9a6c\uff1a \u201c\u4eca\u5929\u4f60\u5c31\u7b97\u8981\u559d\u9152\uff0c\u662f\u4e0d\u662f\u4e5f\u5e94\u8be5\u7b49\u5230\u665a\u4e00\u70b9\u7684\u65f6\u5019\u518d\u559d\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u9a6c\u4e0a\u5c31\u8981\u9047\u5230\u4e00\u4e2a\u5f88\u5f3a\u7684\u5bf9\u624b\uff0c\u5f88\u53ef\u80fd\u6bd4\u6211\u4eec\u60f3\u8c61\u4e2d\u8fd8\u8981\u5f3a\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\u5c31\u7b97\u4e00\u5b9a\u8981\u559d\u9152\uff0c\u6700\u5c11\u4e5f\u5e94\u8be5\u7b49\u5230\u548c\u4ed6\u4ea4\u8fc7\u624b\u4e4b\u540e\u518d\u559d\u3002\u201d \u3000\u3000\u53f8\u9a6c\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u7b49\u5230\u90a3\u65f6\u5019\uff0c\u4f60\u96be\u9053\u5fd8\u4e86\u6211\u662f\u6c38\u8fdc\u4e0d\u8d25\u7684\u53f8\u9a6c\u8d85\u7fa4\uff1f\u201d \u3000\u3000\u4ed6\u7684\u7b11\u5bb9\u4e2d\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u8ba5\u6d88\u3002 \u3000\u3000\u201c\u6211\u53cd\u6b63\u4e0d\u4f1a\u8d25\u7684\uff0c\u5c31\u7b97\u559d\u5f97\u70c2\u9189\u5982\u6ce5\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u8d25\uff0c\u56e0\u4e3a\u4f60\u4e00\u5b9a\u65e9\u5c31\u5b89\u6392\u597d\u4e86\uff0c\u628a\u4ec0 \u4e48\u4e8b\u90fd\u5b89\u6392\u597d\u4e86\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u5927\u7b11\uff1a\u201c\u90a3\u4e2a\u53eb\u9ad8\u6e10\u98de\u7684\u5c0f\u5b50\uff0c\u53cd\u6b63\u5df2\u975e\u8d25\u4e0d\u53ef\uff0c\u975e\u6b7b\u4e0d \u53ef\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6ca1\u6709\u7b11\uff0c\u6ca1\u6709\u627f\u8ba4\uff0c\u4e5f\u6ca1\u6709\u5426\u8ba4\uff0c\u8138\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u8868\u60c5\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u770b\u7740\u4ed6\uff1a\u201c\u8fd9\u4e00\u6b21\u4f60\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u4f60\u7a76\u7adf\u662f\u600e\u4e48\u5b89\u6392\u7684\u5e7f \u3000\u3000\u5353\u4e1c\u6765\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u6709\u4e9b\u4e8b\u672c\u6765\u5c31\u968f\u65f6\u4f1a\u53d1\u751f\u7684\uff0c\u7528\u4e0d\u7740\u6211\u5b89\u6392\u4e5f\u4e00 \u6837\u3002\u201d \u3000\u3000\u201c\u4f60\u53ea\u4e0d\u8fc7\u8ba9\u9ad8\u6e10\u98de\u5f88\u5076\u7136\u7684\u9047\u5230\u4e86\u4e00\u4e24\u4ef6\u8fd9\u6837\u7684\u4e8b\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u6bcf\u4e2a\u4eba\u90fd\u96be\u514d\u4f1a\u5076\u7136\u9047\u5230\u4e00\u4e9b\u8fd9\u6837\u7684\u4e8b\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e0d\u7ba1\u8c01\u9047\u5230\uff0c\u90fd\u540c\u6837\u65e0\u53ef\u5948 \u4f55\uff0c\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8d70\u8fc7\u53bb\uff0c\u62ff\u8d77\u7985\u5e8a\u77ee\u51e0\u4e0a\u7684\u90a3\u74f6\u767d\u9152\uff0c\u5012\u4e86\u4e00\u70b9\u5728\u4e00\u676f\u6e05\u6c34\u91cc\u3002 \u3000\u3000\u9152\u4e0e\u6c34\u7acb\u523b\u6eb6\u5316\u5728\u4e00\u8d77\uff0c\u6eb6\u4e3a\u4e00\u4f53\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4e0d\u662f\u5f88\u81ea\u7136\u7684\u4e8b\uff1f\u201d\u5353\u4e1c\u6765\u95ee\u53f8\u9a6c\u3002 \u3000\u3000\u201c\u6709\u4e9b\u4eba\u4e5f\u4e00\u6837\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6709\u4e9b\u4eba\u76f8\u9047\u4e4b\u540e\uff0c\u4e5f\u4f1a\u50cf\u9152\u548c\u6c34\u822c\u76f8\u6eb6\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u9152\u6c34\u76f8\u6eb6\u4e4b\u540e\uff0c\u9152\u5c31\u4f1a\u53d8\u5f97\u6de1\u4e86\uff0c\u6c34\u4e5f\u4f1a\u53d8\u4e86\u8d28\u3002\u201d \u3000\u3000\u201c\u4eba\u4e5f\u4e00\u6837\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff0c\u201c\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6709\u4e9b\u4eba\u76f8\u9047\u4e4b\u540e\u4e5f\u4f1a\u53d8\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6709\u4e9b\u4eba\u9047\u5230\u67d0\u4e00\u4e2a\u4eba\u4e4b\u540e\uff0c\u5c31\u4f1a\u53d8\u5f97\u8f6f\u5f31 \u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u5c31\u50cf\u662f\u53c2\u4e86\u6c34\u7684\u9152\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u5c31\u8ba9\u9ad8\u6e10\u98de\u5076\u7136\u9047\u5230\u4e86\u8fd9\u4e48\u6837\u4e00\u4e2a\u50cf\u6c34\u4e00\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5076\u7136\u95f4\u76f8\u9047\uff0c\u5076\u7136\u95f4\u522b\u79bb\uff0c\u8c01\u4e5f\u65e0\u53ef\u5948\u4f55\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u8fd8\u662f\u90a3\u4e48\u51b7\u6de1\uff1a \u201c\u5929\u5730\u95f4\u672c\u6765\u5c31\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u53f8\u9a6c\u53c8\u5927\u7b11\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5bf9\u6211\u8fd9\u4e48\u597d\uff1f\u201d\u4ed6\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u628a\u6211\u7684\u6bcf\u4ef6\u4e8b\u90fd\u5b89\u6392\u5f97\u8fd9\u4e48\u597d\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u662f\u53f8\u9a6c\u8d85\u7fa4\u3002\u201d\u5353\u4e1c\u6765\u7684\u56de\u7b54\u5f88\u7b80\u5355\uff1a\u201c\u56e0\u4e3a\u53f8\u9a6c\u8d85\u7fa4\u662f\u6c38\u8fdc\u4e0d\u80fd\u8d25\u7684\u3002\u201d \u4e8c \u3000\u3000\u5510\u671d\u65f6\uff0c\u9ad8\u5b97\u4e3a\u5176\u6bcd\u6587\u5fb7\u7687\u540e\u7b51\u5927\u96c1\u5854\uff0c\u540d\u50e7\u7384\u5958\u66fe\u5728\u6b64\u8bd1\u7ecf\uff0c\u521d\u5efa\u4e94\u5c42\uff0c\u4eff\u897f\u57df\u6d6e\u5c60 \u7960\uff0c\u540e\u52a0\u5efa\u4e3a\u4e03\u7ea7\uff0c\u662f\u4e3a\u4e03\u7ea7\u6d6e\u5c60\u3002 \u3000\u3000\u73b0\u5728\u9ad8\u6e10\u98de\u5c31\u7ad9\u5728\u5927\u96c1\u5854\u4e0b\u3002 \u3000\u3000\u5854\u4e0b\u6ca1\u6709\u9634\u5f71\uff0c\u56e0\u4e3a\u4eca\u5929\u6ca1\u6709\u592a\u9633\uff0c\u6ca1\u6709\u9633\u5149\u5c31\u6ca1\u6709\u9634\u5f71\u3002 \u3000\u3000\u5c0f\u9ad8\u5fc3\u91cc\u4e5f\u6ca1\u6709\u9634\u5f71\u3002\u4ed6\u5fc3\u91cc\u5df2\u7ecf\u662f\u4e00\u7247\u7a7a\u767d\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u624b\u91cc\u8fd8\u6709\u5251\uff0c\u4e00\u67c4\u7528\u7c97\u5e03\u5305\u7740\u7684\u5251\uff0c\u4e00\u67c4\u5f88\u5c11\u88ab\u4eba\u770b\u5230\u8fc7\u7684\u5251\u3002 \u3000\u3000\u53ea\u6709\u5251\uff0c\u6ca1\u6709\u7bb1\u5b50\u3002 \u3000\u3000\u7bb1\u5b50\u5e76\u6ca1\u6709\u88ab\u5979\u5e26\u8d70\uff0c\u5979\u4e0d\u8be5\u8d70\u7684\uff0c\u53ef\u662f\u5979\u8d70\u4e86\uff0c\u5979\u672c\u6765\u5e94\u8be5\u628a\u7bb1\u5b50\u5e26\u8d70\u7684\uff0c\u53ef\u662f\u5979\u6ca1 \u6709\u5e26\u8d70\u3002 \u3000\u3000\u7bb1\u5b50\u88ab\u5c0f\u9ad8\u7559\u5728\u90a3\u95f4\u5c0f\u5c4b\u91cc\u4e86\u3002 \u3000\u3000\u5e94\u8be5\u7559\u4e0b\u7684\u65e2\u7136\u4e0d\u80fd\u7559\u4e0b\u6765\uff0c\u4e0d\u5e94\u8be5\u7559\u4e0b\u7684\u4e3a\u4ec0\u4e48\u7559\u4e0b\uff1f \u3000\u3000\u4ed6\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u6765\u4e86\u591a\u4e45\uff0c\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\u3002 \u3000\u3000\u4ed6\u53ea\u77e5\u9053\u4ed6\u5df2\u7ecf\u6765\u4e86\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u770b\u89c1\u4e86\u5353\u4e1c\u6765\u548c\u53f8\u9a6c\u8d85\u7fa4\u3002 \u3000\u3000\u7a7f\u4e00\u8eab\u9ed1\u767d\u5206\u660e\u7684\u8863\u88f3\uff0c\u6709\u4e00\u53cc\u9ed1\u767d\u5206\u660e\u7684\u773c\u775b\uff0c\u767d\u7684\u96ea\u767d\uff0c\u9ed1\u7684\u6f06\u9ed1\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u65e0\u8bba\u5728\u4ec0\u4e48\u65f6\u5019\u51fa\u73b0\uff0c\u7ed9\u4eba\u7684\u611f\u89c9\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u2014\u2014\u660e\u663e\u3001\u5f3a\u70c8\u3001\u9ed1\u767d\u5206\u660e\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5728\u8fd9\u4e00\u7247\u94f6\u767d\u7684\u4e16\u754c\u4e2d\uff0c\u6240\u6709\u7684\u8363\u8000\u5149\u8292\u90fd\u662f\u5c5e\u4e8e\u4ed6\u4e00\u4e2a\u4eba\u7684\uff0c\u5353\u4e1c\u6765\u53ea \u4e0d\u8fc7\u662f\u4ed6\u5149\u8292\u7167\u8000\u4e0b\u7684\u4e00\u4e2a\u9634\u5f71\u800c\u5df2\u3002 \u3000\u3000\u5353\u4e1c\u6765\u81ea\u5df1\u597d\u50cf\u4e5f\u5f88\u660e\u767d\u8fd9\u4e00\u70b9\uff0c\u6240\u4ee5\u6c38\u8fdc\u90fd\u9ed8\u9ed8\u7684\u7ad9\u5728\u4e00\u8fb9\u3002\u6c38\u8fdc\u4e0d\u4f1a\u6321\u4f4f\u4ed6\u7684\u5149 \u4eae\u3002 \u3000\u3000\u5c0f\u9ad8\u7b2c\u4e00\u773c\u5c31\u770b\u89c1\u4e86\u53f8\u9a6c\u8d85\u7fa4\u90a3\u53cc\u7075\u4eae\u7684\u773c\u775b\u548c\u6f06\u9ed1\u7684\u7738\u5b50\u3002 \u3000\u3000\u5982\u679c\u4ed6\u80fd\u8d70\u8fd1\u4e00\u70b9\uff0c\u770b\u5f97\u4ed4\u7ec6\u4e00\u70b9\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u770b\u89c1\u8fd9\u53cc\u773c\u775b\u91cc\u5df2\u7ecf\u6709\u4e86\u7ea2\u4e1d\uff0c\u5c31\u597d\u50cf\u4e00 \u4e1d\u4e1d\u88ab\u706b\u7130\u4ece\u5fc3\u91cc\u71c3\u70e7\u8d77\u6765\u7684\u9c9c\u8840\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u9664\u4e86\u5353\u4e1c\u6765\u4e4b\u5916\uff0c\u6ca1\u6709\u4eba\u80fd\u63a5\u8fd1\u53f8\u9a6c\u8d85\u7fa4\u3002 \u3000\u3000\u201c\u4f60\u5c31\u662f\u9ad8\u6e10\u98de\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u662f\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u4e5f\u5728\u770b\u7740\u5c0f\u9ad8\uff0c\u770b\u7740\u4ed6\u7684\u773c\u795e\uff0c\u770b\u7740\u4ed6\u7684\u8138\u8272\uff0c\u770b\u7740\u4ed6\u7684\u6837\u5b50\u3002 \u3000\u3000\u5927\u96c1\u5854\u4e0b\u867d\u7136\u6ca1\u6709\u9634\u5f71\uff0c\u53ef\u662f\u4ed6\u6574\u4e2a\u4eba\u90fd\u867e\u50cf\u88ab\u7b3c\u7f69\u5728\u9634\u5f71\u91cc\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u9759\u9759\u7684\u770b\u4e86\u4ed6\u534a\u5929\uff0c\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u8d70\u4e86\u3002 \u3000\u3000\u5353\u4e1c\u6765\u6ca1\u6709\u963b\u62e6\u4ed6\uff0c\u5353\u4e1c\u6765\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u5374\u6251\u8fc7\u53bb\u62e6\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8d70\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e0d\u60f3\u6740\u4f60\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u5728\u6211\u7684\u5251\u4e0b\uff0c\u8d25\u5c31\u662f\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u7684\u51b7\u9759\u5b8c\u5168\u4e0d\u50cf\u559d\u8fc7\u9152\u7684\u6837\u5b50\uff1a\u201c\u5176\u5b9e\u73b0\u5728\u4f60\u81ea\u5df1\u4e5f\u5e94\u8be5\u77e5\u9053\u4f60\u5df2\u7ecf\u8d25\u4e86\uff0c\u56e0\u4e3a\u4f60\u8fd9 \u4e2a\u4eba\u5df2\u7ecf\u662f\u4e2a\u7a7a\u7684\u4eba\uff0c\u5c31\u597d\u50cf\u4e00\u53e3\u88c5\u7c73\u7684\u9ebb\u888b\uff0c\u5df2\u7ecf\u88ab\u4eba\u628a\u888b\u5b50\u91cc\u7684\u7c73\u5012\u7a7a\u4e86\u4e00\u6837\u3002\u201d \u3000\u3000\u4e00\u4e2a\u7a7a\u7684\u4eba\u548c\u4e00\u53e3\u7a7a\u9ebb\u888b\u90fd\u662f\u7ad9\u4e0d\u8d77\u6765\u7684\uff0c\u5982\u679c\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u6765\u3002\u600e\u4e48\u80fd\u80dc\uff1f \u3000\u3000\u8fd9\u9053\u7406\u65e0\u8bba\u8c01\u90fd\u5e94\u8be5\u660e\u767d\u7684\u3002 \u3000\u3000\u53ea\u6709\u5c0f\u9ad8\u4e0d\u660e\u767d\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u662f\u7a7a\u7684\uff0c\u4e00\u4e2a\u7a7a\u7684\u4eba\u8fd8\u4f1a\u660e\u767d\u4ec0\u4e48\u9053\u7406\uff1f \u3000\u3000\u6240\u4ee5\u4ed6\u5df2\u7ecf\u5f00\u59cb\u5728\u89e3\u4ed6\u7684\u5305\u88b1\uff0c\u8fd9\u4e2a\u5305\u88b1\u4e0d\u662f\u7a7a\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u5305\u88b1\u91cc\u6709\u5251\uff0c\u53ef\u4ee5\u5728\u77ac\u606f\u95f4\u53d6\u4eba\u6027\u547d\u7684\u5251\uff0c\u4e5f\u540c\u6837\u53ef\u4ee5\u8ba9\u522b\u4eba\u6709\u8db3\u591f\u7684\u7406\u7531\u5728\u77ac\u606f \u95f4\u53d6\u4ed6\u7684\u6027\u547d\u3002 \u3000\u3000\u53f8\u9a6c\u8d77\u7fa4\u7684\u811a\u6b65\u867d\u7136\u5df2\u505c\u4e0b\uff0c\u76ee\u5149\u5374\u5230\u4e86\u8fdc\u65b9\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u770b\u9ad8\u6e10\u98de\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u8981\u62d4\u5251\u65f6\uff0c\u662f\u8c01\u4e5f\u65e0\u6cd5\u963b\u6b62\u7684\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u53bb\u770b\u5353\u4e1c\u6765\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u5353\u4e1c\u6765\u5bf9\u8fd9\u79cd\u4e8b\u7edd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u81ea\u5df1\u773c\u91cc\u5374\u5df2\u9732\u51fa\u79cd\u6de1\u6de1\u7684\u54c0\u4f24\u3002 \u3000\u3000\u2014\u2014\u5982\u6b64\u503c\u5f97\u73cd\u60dc\u7684\u751f\u547d\uff0c\u4e00\u5230\u4e86\u67d0\u79cd\u60c5\u51b5\u4e0b\uff0c\u4e3a\u4ec0\u4e48\u5c31\u4f1a\u53d8\u5f97\u5982\u6b64\u88ab\u4eba\u8f7b\u8d31\uff1f \u3000\u3000\u4ed6\u7684\u624b\u4e5f\u5df2\u63e1\u4f4f\u4e86\u4ed6\u7684\u5251\uff0c\u56e0\u4e3a\u4ed6\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4e5f\u5df2\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\u3002 \u3000\u3000\u201c\u6ce2\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u957f\u5251\u541e\u53e3\u4e0a\u7684\u5d29\u7c27\u5df2\u5f39\u5f00\uff0c\u53ef\u662f\u53f8\u9a6c\u8d85\u7fa4\u7684\u5251\u5e76\u6ca1\u6709\u62d4\u51fa\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5927\u96c1\u5854\u4e0a\u5ffd\u7136\u6d41\u661f\u822c\u5760\u4e0b\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u4ece\u5854\u4e0a\u5760\u4e0b\u7684\uff0c\u5f53\u7136\u5e76\u4e0d\u662f\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\uff0c\u800c\u662f\u4e00\u4e2a\u4eba\uff0c\u53ef\u662f\u8fd9\u4e2a\u4eba\u7684\u901f\u5ea6\u5b9e\u5728\u592a\u5feb\uff0c \u8fde\u53f8\u9a6c\u8d85\u7fa4\u90fd\u770b\u4e0d\u6e05\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u53ea\u770b\u89c1\u4e00\u6761\u6de1\u7070\u8272\u7684\u5f71\u5b50\u843d\u4e0b\uff0c\u5e26\u8d77\u4e86\u9ad8\u6e10\u98de\u3002 \u3000\u3000\u4e8e\u662f\u9ad8\u6e10\u98de\u4e5f\u98de\u4e86\u8d77\u6765\uff0c\u4e0d\u662f\u6e10\u6e10\u98de\u8d77\u6765\u7684\uff0c\u800c\u662f\u5ffd\u7136\u95f4\u5c31\u5df2\u98de\u9e1f\u822c\u8dc3\u8d77\uff0c\u8f6c\u77ac\u95f4\u5c31\u5df2 \u5230\u4e86\u5927\u96c1\u5854\u7684\u7b2c\u4e09\u5c42\u4e0a\u3002 \u3000\u3000\u518d\u4e00\u8f6c\u773c\uff0c\u4e24\u6761\u4eba\u5f71\u90fd\u5df2\u98de\u4e0a\u4e86\u8fd9\u5ea7\u6d6e\u5c60\u9ad8\u5854\u7684\u7b2c\u4e03\u7ea7\u3002 \u3000\u3000\u7136\u540e\u4e24\u4e2a\u4eba\u5c31\u5168\u90fd\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u672c\u6765\u60f3\u8ffd\u4e0a\u53bb\uff0c\u5374\u542c\u89c1\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u4f60\u65e2\u7136\u672c\u6765\u5c31\u4e0d\u60f3\u6740\u4ed6\uff0c\u53c8\u4f55\u5fc5\u518d \u53bb\u8ffd\uff1f\u201d \u4e09 \u3000\u3000\u96ea\u5df2\u7ecf\u505c\u4e86\uff0c\u8001\u50e7\u6765\u5949\u8336\u540e\u53c8\u9000\u4e0b\u3002 \u3000\u3000\u6709\u65f6\u6765\uff0c\u6709\u65f6\u53bb\uff0c\u6709\u65f6\u843d\uff0c\u6709\u65f6\u505c\uff0c\u65e0\u60c5\u7684\u96ea\u82b1\u548c\u5fd8\u60c5\u7684\u8001\u50e7\u90fd\u5982\u662f\u3002 \u3000\u3000\u4eba\u5462\uff1f \u3000\u3000\u4eba\u53c8\u4f55\u5c1d\u4e0d\u662f\u8fd9\u6837\uff1f \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5374\u8fd8\u662f\u9759\u9759\u7684\u5750\u5728\u90a3\u5f20\u7985\u5e8a\u4e0a\uff0c\u559d\u4ed6\u90a3\u74f6\u8fd8\u6ca1\u6709\u559d\u5b8c\u7684\u51b7\u9152\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u5ffd\u7136 \u95f4\u5353\u4e1c\u6765\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u51b7\u7b11\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u8bf4\u7684\u662f\u8c01\uff0c\u4f60\u4e0d\u8ba9\u6211\u53bb\u8ffd\uff0c\u5c31\u56e0\u4e3a\u4f60\u6015\u4ed6\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7ad9\u8d77\u6765\uff0c\u8d70\u5230\u7a97\u53e3\uff0c\u6253\u5f00\u7a97\u5b50\uff0c\u53c8\u5173\u4e0a\uff0c\u7136\u540e\u624d\u8f6c\u8eab\u9762\u5bf9\u53f8\u9a6c\u3002 \u3000\u3000\u201c\u6b66\u6797\u4e2d\u9ad8\u624b\u8f88\u51fa\uff0c\u5404\u6709\u7edd\u6280\uff0c\u9ad8\u624b\u5bf9\u51b3\u65f6\uff0c\u80dc\u8d1f\u4e4b\u5206\u901a\u5e38\u90fd\u8981\u9760\u4ed6\u4eec\u5f53\u65f6\u7684\u60c5\u51b5\u548c\u673a \u9047\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u81ea\u4ece\u5c0f\u674e\u98de\u5200\u9000\u9690\u540e\uff0c\u771f\u6b63\u80fd\u591f\u65e0\u654c\u4e8e\u5929\u4e0b\u7684\u9ad8\u624b\uff0c\u51e0\u4e4e\u5df2\u7ecf\u6ca1\u6709 \u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u51e0\u4e4e\u6ca1\u6709\uff1f\u8fd8\u662f\u7edd\u5bf9\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u80fd\u786e\u5b9a\u3002\u201d\u5353\u4e1c\u6765\u7684\u58f0\u97f3\u4eff\u4f5b\u6709\u4e9b\u5636\u54d1\uff1a\u201c\u53ea\u4e0d\u8fc7\u6709\u4eba\u544a\u8bc9\u8fc7\u6211\uff0c\u5728\u8fd9\u4e2a\u4e16\u754c \u4e0a\u67d0\u4e00\u4e2a\u4e0d\u77e5\u540d\u7684\u5730\u65b9\uff0c\u6709\u4e00\u4e2a\u8fd9\u4e48\u6837\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d\u53f8\u9a6c\u8d85\u7fa4\u8038\u7136\u52a8\u5bb9\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4ed6\u59d3\u8427\uff0c\u6613\u6c34\u8427\u8427\u7684\u8427\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4ed6\u7684\u540d\u5b57\u53eb\u8427\u6cea\u8840\u3002\u201d \u56db \u3000\u3000\u201c\u68ee\u68ee\u5251\u6c14\uff0c\u8427\u8427\u6613\u6c34\uff1b \u3000\u3000\u82f1\u96c4\u65e0\u6cea\uff0c\u5316\u4f5c\u78a7\u8840\u3002\u201d \u3000\u3000\u9ad8\u6e10\u98de\u597d\u50cf\u53c8\u7761\u7740\u4e86\uff0c\u5c31\u5728\u4ed6\u8981\u89e3\u8863\u62d4\u5251\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u5c31\u7761\u7740\u4e86\uff0c\u800c\u4e14\u5ffd\u7136\u5728\u7761\u68a6\u4e2d\u8f7b \u98d8\u98d8\u7684\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u6839\u672c\u5206\u4e0d\u6e05\u8fd9\u7a76\u7adf\u662f\u68a6\u662f\u771f\uff1f\u4e00\u4e2a\u4eba\u88ab\u522b\u4eba\u7528\u5f88\u8f7b\u800c\u4e14\u5f88\u5999\u7684\u624b\u6cd5\uff0c\u62c2\u8fc7\u7761\u7a74 \u65f6\uff0c\u901a\u5e38\u90fd\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u4ed6\u6e05\u9192\u7684\u65f6\u5019\uff0c\u5c31\u542c\u5230\u6709\u4eba\u5728\u4f4e\u6b4c\uff0c\u4f4e\u4f4e\u7684\u6b4c\u58f0\u4e2d\u4eff\u4f5b\u4e5f\u5e26\u7740\u79cd\u68ee\u68ee\u7684\u5251\u6c14\u548c\u4e00\u79cd\u8bf4\u4e0d \u51fa\u7684\u82cd\u51c9\u8427\u7d22\u3002 \u3000\u3000\u201c\u6d6a\u5b50\u4e09\u5531\uff0c\u53ea\u5531\u82f1\u96c4\uff1b \u3000\u3000\u6d6a\u5b50\u65e0\u6839\uff0c\u82f1\u96c4\u65e0\u6cea\u3002\u201d \u3000\u3000\u6b4c\u58f0\u621b\u7136\u65ad\u7edd\uff0c\u6b4c\u8005\u6162\u6162\u7684\u8f6c\u8eab\uff0c\u4e00\u5f20\u9ec4\u8721\u822c\u7684\u8138\uff0c\u4e00\u53cc\u75b2\u5026\u65e0\u795e\u7684\u773c\u795e\uff0c\u4e00\u8eab\u7070\u6734\u6734 \u7684\u8863\u670d\u3002 \u3000\u3000\u4e00\u4e2a\u6c89\u9ed8\u5e73\u51e1\u7684\u4eba\uff0c\u624b\u91cc\u63d0\u7740\u53e3\u9648\u65e7\u5e73\u51e1\u7684\u7bb1\u5b50\u3002 \u4e94 \u3000\u3000\u201c\u8427\u6cea\u8840\uff01\u201d \u3000\u3000\u51b7\u9152\u706b\u7130\u822c\u6eda\u8fc7\u53f8\u9a6c\u8d85\u7fa4\u7684\u8840\u8109\u5fc3\u810f\uff0c\u4ed6\u7684\u5fc3\u5374\u8fd8\u662f\u6ca1\u6709\u56e0\u6b64\u70ed\u8d77\u6765\uff0c\u201c\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837 \u7684\u4eba\uff1f\u4f60\u6709\u6ca1\u6709\u770b\u5230\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u3002\u8c01\u4e5f\u6ca1\u6709\u770b\u89c1\u8fc7\u4ed6\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5c31\u7b97\u770b\u89c1\u8fc7\u4ed6\u7684\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u77e5\u9053\u4ed6\u662f \u8c01\u3002\u201d \u516d \u3000\u3000\u98ce\u6025\u800c\u51b7\uff0c\u5f88\u6025\uff0c\u6781\u51b7\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u662f\u5728\u9ad8\u5904\uff0c\u5728\u4e03\u7ea7\u6d6e\u5c60\u9ad8\u5854\u7684\u6700\u4e0a\u5c42\u3002 \u3000\u3000\u201c\u662f\u4f60\uff0c\u53c8\u662f\u4f60\uff0c\u201d\u5c0f\u9ad8\u832b\u7136\u56db\u987e\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u4e3a\u4ec0\u4e48\u5ffd\u7136\u53c8\u628a\u6211\u5f04\u5230\u8fd9\u4e48\u6837 \u4e00\u4e2a\u89c1\u9b3c\u7684\u5730\u65b9\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5730\u65b9\u89c1\u4e0d\u5230\u9b3c\u7684\uff0c\u53ef\u662f\u4e0d\u628a\u4f60\u5f04\u5230\u8fd9\u5730\u65b9\u6765\uff0c\u6211\u5c31\u8981\u89c1\u5230\u4e00\u4e2a\u9b3c\u4e86\u3002\u201d\u4ed6\u6de1\u6de1\u7684 \u8bf4\uff1a\u201c\u4e00\u4e2a\u65b0\u6b7b\u7684\u9b3c\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u65b0\u6b7b\u7684\u9b3c\u5c31\u662f\u6211\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u4e00\u5b9a\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u7684\u5251\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u75b2\u5026\u65e0\u795e\u7684\u773c\u775b\u91cc\uff0c\u4eff\u4f5b\u5ffd\u7136\u6709\u4e86\u4e00\u70b9\u661f\u5149\uff0c\u5c31\u50cf\u662f\u6781\u5317\u7684\u5929\u8fb9\u90a3\u9897\u6c38\u6052\u7684\u4eba\u661f \u4e00\u6837\uff0c\u90a3\u4e48\u9065\u8fdc\uff0c\u90a3\u4e48\u795e\u79d8\uff0c\u90a3\u4e48\u660e\u4eae\u3002 \u3000\u3000\u201c\u5f80\u4e8b\u84bf\u83b1\uff0c\u6614\u65e5\u7684\u540d\u5251\u5df2\u6c89\u57cb\uff0c\u4f60\u7684\u8fd9\u67c4\u5251\u5df2\u7ecf\u662f\u5f53\u4eca\u5929\u4e0b\u65e0\u53cc\u7684\u5229\u5668\uff0c\u8fd1\u4e94\u767e\u5e74\u6765 \u6ca1\u6709\u4efb\u4f55\u4e00\u67c4\u5251\u53ef\u4ee5\u6bd4\u5f97\u4e0a\u5b83\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u94f8\u9020\u5b83\u7684\u4eba\uff0c\u662f\u6b27\u51b6\u5b50\u4e4b\u540e\u7b2c\u4e00\u4f4d\u5927\u5e08\uff0c\u4e5f\u662f\u5f53\u65f6\u7684\u7b2c\u4e00\u4f4d\u5251\u5ba2\uff0c\u53ef\u662f\u7ec8\u4ed6\u7684\u4e00\u751f\uff0c \u4ece\u6765\u4e5f\u6ca1\u6709\u7528\u8fc7\u8fd9\u67c4\u5251\uff0c\u751a\u81f3\u6ca1\u6709\u62d4\u51fa\u9798\u6765\u7ed9\u4eba\u770b\u8fc7\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u67c4\u5251\u592a\u51f6\uff0c\u53ea\u8981\u4e00\u51fa\u9798\uff0c\u5fc5\u996e\u4eba\u8840\u3002\u201d \u3000\u3000\u4ed6\u7684\u8138\u4e0a\u6ca1\u6709\u8868\u60c5\uff0c\u56e0\u4e3a\u4ed6\u8138\u4e0a\u6709\u4e00\u5c42\u7c7b\u4f3c\u9ec4\u8721\u7684\u6613\u5bb9\u836f\u7269\uff0c\u53ef\u662f\u4ed6\u773c\u91cc\u5374\u5ffd\u7136\u53c8\u9732\u51fa \u79cd\u8bf4\u4e0d\u51fa\u7684\u60b2\u4f24\u3002 \u3000\u3000\u201c\u6b64\u5251\u51fa\u7089\u65f6\uff0c\u90a3\u4f4d\u5927\u5e08\u5c31\u5df2\u770b\u51fa\u5251\u4e0a\u7684\u51f6\u5146\uff0c\u4e00\u79cd\u65e0\u6cd5\u53ef\u89e3\u7684\u51f6\u5146\uff0c\u6240\u4ee5\u4ed6\u5fcd\u4e0d\u4f4f\u6d41 \u4e0b\u6cea\u6765\uff0c\u6ef4\u843d\u5728\u8fd9\u67c4\u5251\u4e0a\uff0c\u5316\u505a\u4e86\u6cea\u75d5\u3002\u201d \u3000\u3000\u201c\u5251\u950b\u4e0a\u7684\u6cea\u75d5\u5c31\u662f\u8fd9\u4e48\u6837\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u90a3\u4f4d\u5927\u5e08\u65e2\u7136\u5df2\u770b\u51fa\u5b83\u7684\u51f6\u715e\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7d22\u6027\u6bc1\u4e86\u5b83\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u67c4\u5251\u94f8\u9020\u5f97\u5b9e\u5728\u592a\u5b8c\u7f8e\uff0c\u201d\u4ed6\u95ee\u5c0f\u9ad8\uff1a\u201c\u6709\u8c01\u80fd\u5fcd\u5fc3\u4e0b\u5f97\u4e86\u624b\uff0c\u628a\u81ea\u5df1\u4e00\u751f\u5fc3 \u8840\u5316\u6210\u7684\u7cbe\u8403\u6bc1\u4e8e\u4e00\u65e6\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u8bf4\uff1a\u201c\u4f55\u51b5\u5251\u5df2\u51fa\u7089\uff0c\u5df2\u6210\u795e\u5668\uff0c\u5c31\u7b97\u80fd\u6bc1\u4e86\u5b83\u7684\u5f62\uff0c\u4e5f\u6bc1\u4e0d\u4e86\u5b83\u7684\u795e\u4e86\uff0c\u8fdf\u65e9\u603b \u6709\u4e00\u5929\uff0c\u5b83\u7684\u9884\u5146\uff0c\u8fd8\u662f\u4f1a\u7075\u9a8c\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5c45\u7136\u660e\u767d\u4ed6\u7684\u610f\u601d\uff1a\u201c\u5929\u5730\u95f4\u672c\u6765\u5c31\u6709\u4e9b\u4e8b\u7269\u662f\u6c38\u8fdc\u65e0\u6cd5\u6d88\u706d\u7684\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4eca\u5929\u4f60\u53ea\u8981\u62d4\u51fa\u4e86\u8fd9\u67c4\u5251\uff0c\u5c31\u5fc5\u5c06\u6b7b\u5728\u8fd9\u67c4\u5251\u4e0b\u3002\u201d\u8fd9\u4e2a\u4eba\u8bf4\uff1a\u201c\u56e0\u4e3a\u4f60\u4eca\u5929\u7edd \u5bf9\u4e0d\u662f\u53f8\u9a6c\u8d85\u7fa4\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u5c0f\u9ad8\u8bf4\uff1a\u201c\u73b0\u5728\u4f60\u603b\u8be5\u5df2\u7ecf\u660e\u767d\uff0c\u5c31\u7b97\u662f\u516c\u5e73\u7684\u51b3\u6597\uff0c\u4e5f\u4e0d\u662f\u5b8c\u5168\u516c\u5e73\u7684\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u5230\u8fbe\u4e86\u67d0\u79cd\u5730\u6b65\uff0c\u6709\u4e86\u67d0\u79cd\u52bf\u529b\u540e\uff0c\u5c31\u80fd\u591f\u5236\u9020\u51fa\u4e00\u4e9b\u4e8b\u60c5\u6765\uff0c\u524a\u5f31\u5bf9\u624b\u7684\u529b \u91cf\uff0c\u4f7f\u81ea\u5df1\u83b7\u80dc\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u8fd9\u79cd\u4e8b\u901a\u5e38\u90fd\u662f\u975e\u5e38\u4e13\u4eba\u75db\u82e6\u7684\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e8b\u5b9e\uff0c\u6781\u6b8b\u9177\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u73b0\u5728\u5c0f\u9ad8\u5df2\u65e0\u6cd5\u5426\u8ba4\u3002\u56e0\u4e3a\u73b0\u5728\u4ed6\u5df1\u8ba4\u6e05\u4e86\u8fd9\u4e00\u70b9\uff0c\u5df2\u7ecf\u5f97\u5230\u4e86\u60e8\u75db\u7684\u6559\u8bad\u3002 \u3000\u3000\u201c\u6240\u4ee5\u5982\u679c\u4f60\u771f\u7684\u60f3\u5bf9\u4ed8\u53f8\u9a6c\u8d85\u7fa4\uff0c\u552f\u4e00\u7684\u65b9\u6cd5\u5c31\u662f\u51fa\u5176\u4e0d\u610f\uff0c\u5c06\u4ed6\u523a\u6740\u4e8e\u5251\u4e0b\u3002\u201d\u8fd9 \u4e2a\u4eba\u8bf4\uff1a\u201c\u56e0\u4e3a\u4f60\u6839\u672c\u6ca1\u6709\u8ddf\u4ed6\u516c\u5e73\u51b3\u6597\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u53cc\u62f3\u7d27\u63e1\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u6211\u8fd9\u4e9b\u4e8b\uff1f\u201d\u4ed6\u95ee\u8fd9\u4e2a\u4eba\uff0c\u201c\u4e3a\u4ec0\u4e48\u8981\u6551\u6211\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u6ca1\u6709\u6740\u4f60\uff0c\u6240\u4ee5\u4e5f\u4e0d\u60f3\u8ba9\u4f60\u6b7b\u5728\u522b\u4eba\u624b\u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u4e5f\u4e0d\u60f3\u8ba9\u6211\u8fd9\u67c4\u5251\u843d\u5728\u522b\u4eba\u624b\u91cc\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u8fd9\u4e2a\u4eba\u7684\u56de\u7b54\u5f88\u5e72\u8106\u3002 \u3000\u3000\u5c0f\u9ad8\u53c8\u95ee\u4ed6\uff1a\u201c\u4f60\u65e2\u7136\u5df2\u7ecf\u6709\u4e86\u4e00\u4ef6\u5929\u4e0b\u65e0\u53cc\u7684\u6b66\u5668\uff0c\u96be\u9053\u8fd8\u60f3\u8981\u8fd9\u67c4\u5251\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u60f3\u8981\u3002\u201d\u8fd9\u4e2a\u4eba\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u5982\u679c\u6211\u60f3\u8981\uff0c\u5b83\u65e9\u5df2\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\u5c0f\u9ad8\u4e5f\u65e0\u6cd5\u5426\u8ba4\u3002 \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8981\u5173\u5fc3\u5b83\uff1f\u96be\u9053\u8fd9\u67c4\u5251\u548c\u4f60\u8fd9\u4e2a\u4eba\u4e4b\u95f4\u4e5f\u6709\u67d0\u79cd\u7279\u522b\u7684\u5173\u7cfb\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u5ffd\u7136\u51fa\u624b\uff0c\u63e1\u4f4f\u4e86\u5c0f\u9ad8\u7684\u624b\u8155\u3002 \u3000\u3000\u5c0f\u9ad8\u7acb\u523b\u6d41\u51fa\u4e86\u51b7\u6c57\uff0c\u5168\u8eab\u4e0a\u4e0b\u90fd\u75db\u5f97\u6d41\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u77e5\u9053\u4ed6\u81ea\u5df1\u4e00\u5b9a\u4e5f\u89e6\u75db\u4e86\u8fd9\u4e2a\u4eba\uff0c\u89e6\u75db\u4e86\u4ed6\u5fc3\u91cc\u67d0\u4e00\u5904\u6700\u4e0d\u613f\u88ab\u4eba\u89e6\u53ca\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e00\u4e2a\u5982\u6b64\u575a\u5f3a\u51b7\u9177\u7684\u4eba\uff0c\u5fc3\u91cc\u600e\u4e48\u4f1a\u4e5f\u6709\u5982\u6b64\u8106\u5f31\u4e4b\u5904\uff1f \u3000\u3000\u201c\u4f60\u7684\u7bb1\u5b50\u548c\u6211\u7684\u5251\uff0c\u90fd\u662f\u51fa\u81ea\u540c\u4e00\u4eba\u4e4b\u624b\uff0c\u4f60\u548c\u6211\u4e4b\u95f4\u662f\u4e0d\u662f\u4e5f\u4f1a\u6709\u67d0\u79cd\u7279\u522b\u7684\u5173 \u7cfb\uff1f\u201d\u5c0f\u9ad8\u53c8\u95ee\uff1a\u201c\u8fd9\u4e9b\u4e8b\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80af\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\u4e8b\u90fd\u662f\u5c0f\u9ad8\u975e\u95ee\u4e0d\u53ef\u7684\uff0c\u5c31\u7b97\u624b\u8155\u88ab\u634f\u788e\uff0c\u4e5f\u975e\u95ee\u4e0d\u53ef\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u6ca1\u6709\u5f97\u5230\u56de\u7b54\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u653e\u4e0b\u4e86\u4ed6\u7684\u624b\uff0c\u63a0\u51fa\u4e86\u9ad8\u5854\u3002 \u3000\u3000\u9ad8\u5854\u5916\u4e00\u7247\u94f6\u767d\uff0c\u8fd9\u4e2a\u4eba\u548c\u4ed6\u7684\u7bb1\u5b50\u5df2\u7ecf\u50cf\u96ea\u82b1\u822c\u6d88\u5931\u5728\u4e00\u7247\u94f6\u767d\u4e2d\u3002 \u3000\u3000\u5929\u8272\u6e10\u6e10\u6697\u4e86\uff0c\u5c0f\u9ad8\u5df2\u7ecf\u5728\u8fd9\u91cc\u60f3\u4e86\u5f88\u4e45\uff0c\u6709\u5f88\u591a\u4e8b\u4ed6\u90fd\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6839\u672c\u65e0\u6cd5\u96c6\u4e2d\u601d\u60f3\u3002 \u3000\u3000\u4ed6\u60f3\u6765\u60f3\u53bb\uff0c\u8fd8\u662f\u514d\u4e0d\u4e86\u8981\u53bb\u60f3\u5230\u5979\u3002 \u3000\u3000\u2014\u2014\u7a76\u7adf\u662f\u8c01\uff1f\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u2014\u2014\u8981\u8ffd\u6740\u5979\u7684\u4eba\uff0c\u662f\u4e9b\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u5979\u627e\u5230\u4ed6\uff0c\u662f\u4e0d\u662f\u53f8\u9a6c\u8d85\u7fa4\u8981\u5979\u8fd9\u4e48\u6837\u505a\u7684\uff1f\u8981 \u4ed6\u4e3a\u5979\u795e\u9b42\u98a0\u5012\uff1f \u3000\u3000\u2014\u2014\u4ed6\u5ffd\u7136\u79bb\u4ed6\u800c\u53bb\uff0c\u662f\u5426\u4e5f\u662f\u53f8\u9a6c\u8d85\u7fa4\u8981\u5979\u8d70\u7684\uff1f\u8981\u8ba9\u4ed6\u75db\u82e6\u4f24\u5fc3\u7edd\u671b\uff1f \u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5c0f\u9ad8\u90fd\u51b3\u5fc3\u8981\u627e\u5230\u5979\uff0c\u95ee\u4e2a\u6e05\u695a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u627e\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u5e94\u8be5\u4ece\u4ec0\u4e48\u5730\u65b9\u5f00\u59cb\u53bb\u627e\u3002 \u3000\u3000\u4e00\u4e2a\u521d\u4eba\u6c5f\u6e56\u7684\u5c11\u5e74\uff0c\u6ca1\u6709\u7ecf\u5386\uff0c\u6ca1\u6709\u670b\u53cb\uff0c\u4e5f\u6ca1\u6709\u4eba\u5e2e\u52a9\u4ed6\uff0c\u4ed6\u80fd\u505a\u4ec0\u4e48\uff1f \u3000\u3000\u9664\u4e86\u7528\u4ed6\u7684\u5251\u53bb\u6740\u4eba\u5916\uff0c\u4ed6\u8fd8\u80fd\u505a\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u80fd\u53bb\u6740\u8c01\u5462\uff1f\u5e94\u8be5\u53bb\u6740\u8c01\u5462\uff1f \u3000\u3000\u8c01\u80fd\u544a\u8bc9\u4ed6\uff1f \u3000\u3000\u5929\u8272\u66f4\u6697\u4e86\uff0c\u665a\u949f\u5df2\u54cd\u8d77\uff0c\u540e\u9662\u7684\u9999\u79ef\u53a8\u91cc\u98d8\u51fa\u4e86\u7ca5\u7c73\u996d\u7684\u82b3\u9999\uff0c\u51e0\u4e2a\u665a\u5f52\u7684\u50e7\u4eba\u7a7f\u7740 \u9489\u978b\u8d76\u56de\u6765\u5403\u4ed6\u4eec\u7684\u665a\u81b3\u3002 \u3000\u3000\u9489\u978b\u8e0f\u788e\u4e86\u51b0\u96ea\uff0c\u5c0f\u9ad8\u5ffd\u7136\u60f3\u8d77\u4e86\u6731\u731b\u3002 \u3000\u3000\u6731\u731b\u5728\u6d1b\u9633\u3002 \u7b2c\u4e03\u7ae0\u3000\u94dc\u9a7c\u5df7\u91cc\u96c4\u72ee\u5802>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u4e03\u7ae0\u3000\u94dc\u9a7c\u5df7\u91cc\u96c4\u72ee\u5802 \u4e00 \u3000\u3000\u4e8c\u6708\u521d\u516d\u3002 \u3000\u3000\u6d1b\u9633\u3002 \u3000\u3000\u6d1b\u9633\u662f\u4e1c\u5468\u3001\u5317\u9b4f\u3001\u897f\u664b\u3001\u9b4f\u3001\u968b\u3001\u540e\u5510\u7b49\u4e03\u671d\u5efa\u90fd\u4e4b\u5730\uff0c\u53f3\u638c\u864e\u7262\u3002\u5de6\u63a7\u5173\u4e2d\uff0c\u5317\u671b \u71d5\u4e91\uff0c\u5357\u51ed\u6c5f\u5357\uff0c\u5bab\u5ba4\u57ce\u9600\u6781\u5c3d\u58ee\u7f8e\u3002 \u3000\u3000\u5b8b\u592a\u7956\u51fa\u4e16\u7684\u5939\u9a6c\u8425\u3001\u540e\u5510\u65f6\u521b\u5efa\u7684\u4e1c\u5927\u5bfa\u3001\u66f9\u690d\u6d1b\u795e\u8d4b\u4e2d\u7684\u5b93\u5983\u7960\uff0c\u94dc\u9a7c\u5df7\u91cc\u7684\u8001\u5b50 \u6545\u5c45\u3001\u767d\u9a6c\u81ea\u897f\u5929\u9a7c\u7ecf\u800c\u6765\u7684\u767d\u9a6c\u5bfa\u3001\u201c\u5929\u6d25\u6865\u4e0b\u9633\u6625\u6c34\u201d\u7684\u53e4\u6865\uff0c\u81f3\u4eca\u72b9\u5728\u6b64\u3002 \u3000\u3000\u53ef\u662f\u9ad8\u6e10\u98de\u7684\u5fd7\u5374\u4e0d\u5728\u6b64\u3002 \u3000\u3000\u5c0f\u9ad8\u5e76\u4e0d\u662f\u4e3a\u4e86\u8fd9\u4e9b\u540d\u80dc\u53e4\u8ff9\u800c\u6765\u7684\uff0c\u4ed6\u8981\u627e\u7684\u53ea\u6709\u4e00\u4e2a\u5730\u65b9\uff0c\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u8981\u627e\u7684\u662f\u96c4\u72ee\u5802\uff0c\u6731\u731b\u7684\u96c4\u72ee\u5802\u3002 \u3000\u3000\u4ed6\u627e\u5230\u4e86\u3002 \u3000\u3000\u96c4\u72ee\u5802\u7684\u603b\u8235\u5c31\u5728\u94dc\u9a7c\u5df7\u91cc\uff0c\u5c31\u5728\u4f20\u8bf4\u4e2d\u8001\u5b50\u6545\u5c45\u7684\u9644\u8fd1\uff0c\u51e0\u4e4e\u5360\u636e\u4e86\u4e00\u6574\u6761\u5df7\u5b50\u3002 \u3000\u3000\u5c0f\u9ad8\u5f88\u5feb\u5c31\u627e\u5230\u4e86\u3002 \u3000\u3000\u5728\u4ed6\u60f3\u8c61\u4e2d\uff0c\u96c4\u72ee\u5802\u4e00\u5b9a\u662f\u680b\u53e4\u8001\u575a\u56fa\u7684\u5de8\u5927\u5efa\u7b51\uff0c\u867d\u7136\u4e0d\u4f1a\u5f88\u96c4\u4f1f\u534e\u4e3d\uff0c\u4f46\u5374\u4e00\u5b9a\u5f88 \u5bbd\u655e\u5f00\u9614\uff0c\u5f88\u6709\u6c14\u52bf\uff0c\u5c31\u50cf\u662f\u6731\u731b\u7684\u4eba\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u7684\u60f3\u6cd5\u6ca1\u6709\u9519\uff0c\u96c4\u72ee\u5802\u672c\u6765\u786e\u5b9e\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u53ea\u4e0d\u8fc7\u6709\u4e00\u70b9\u4ed6\u6ca1\u6709\u60f3\u5230\uff0c\u8fd9\u680b\u53e4\u8001\u575a \u56fa\u5bbd\u655e\u5f00\u9614\u7684\u5e84\u9662\u73b0\u5728\u51e0\u4e4e\u5df2\u5b8c\u5168\u88ab\u70e7\u6210\u4e86\u74e6\u783e\u3002 \u3000\u3000\u9664\u4e86\u540e\u9762\u51e0\u95f4\u5c4b\u5b50\u5916\uff0c\u96c4\u8e1e\u6d1b\u9633\u591a\u5e74\u7684\u96c4\u72ee\u5802\uff0c\u7adf\u5df2\u5b8c\u5168\u88ab\u6bc1\u4e8e\u70c8\u706b\u4e2d\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u51b7\u98ce\u5982\u5200\uff0c\u74e6\u783e\u5806\u95f4\u5076\u7136\u8fd8\u4f1a\u6709\u4e9b\u6b8b\u5c51\u88ab\u5bd2\u98ce\u5439\u5f97\u98de\u5377\u800c\u8d77\uff0c\u4e5f\u4e0d\u77e5\u662f\u70e7\u7126\u4e86\u7684\u6881\u6728\uff0c \u8fd8\u662f\u70e7\u7126\u4e86\u7684\u4eba\u9aa8\u3002 \u3000\u3000\u6614\u65e5\u5bbe\u5ba2\u76c8\u95e8\u5f1f\u4e8e\u5982\u96ea\u7684\u96c4\u72ee\u5802\uff0c\u73b0\u5728\u7adf\u5df2\u770b\u4e0d\u5230\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u8fd9\u6761\u5145\u6ee1\u4e86\u5f80\u65e5\u53e4\u8001\u4f20\u8bf4\u548c\u5f53\u4eca\u8c6a\u6770\u96c4\u98ce\u7684\u94dc\u9a7c\u5df7\uff0c\u73b0\u5728\u5df2\u7ecf\u53ea\u5269\u4e0b\u4e00\u7247\u51c4\u82e6\u8083\u6740\u8427 \u7d22\u3002 \u3000\u3000\u6ca7\u6d77\u6851\u7530\uff0c\u4eba\u4e8b\u7684\u53d8\u5316\u867d\u65e0\u5e38\uff0c\u53ef\u662f\u8fd9\u79cd\u53d8\u5316\u4e5f\u672a\u514d\u53d8\u5f97\u5927\u5feb\u5927\u53ef\u6015\u4e86\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u4ec0\u4e48\u65f6\u5019\u53d1\u751f\u7684\uff1f\u600e\u4e48\u53d1\u751f\u7684\uff1f \u3000\u3000\u2014\u2014\u610f\u6c14\u98ce\u53d1\uff0c\u4e0d\u53ef\u4e00\u4e16\u7684\u6731\u731b\uff0c\u548c\u4ed6\u95e8\u4e0b\u90a3\u4e9b\u8eab\u7ecf\u767e\u6218\u7684\u597d\u624b\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u5c0f\u9ad8\u5ffd\u7136\u60f3\u8d77\u4e86\u5353\u4e1c\u6765\uff0c\u60f3\u5230\u4ed6\u505a\u4e8b\u7684\u65b9\u6cd5\uff0c\u60f3\u5230\u4ed6\u7684\u9634\u9e37\u4e0e\u6c89\u7740\u3002 \u3000\u3000\u90a3\u5929\u5728\u98ce\u96ea\u4ea4\u52a0\u7684\u7ea2\u82b1\u96c6\u91cc\u53d1\u751f\u7684\u6bcf\u4e00\u4ef6\u4e8b\uff0c\u73b0\u5728\u53c8\u4e00\u5e55\u5e55\u5728\u5c0f\u9ad8\u8111\u4e2d\u663e\u73b0\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u660e\u767d\u5353\u4e1c\u6765\u4e3a\u4ec0\u4e48\u8981\u653e\u8d70\u6731\u731b\u4e86\u3002 \u3000\u3000\u6731\u731b\u8dc3\u7136\u5728\u957f\u5b89\uff0c\u6d1b\u9633\u603b\u8235\u7684\u9632\u5b88\u529b\u91cf\u5fc5\u5b9a\u4f1a\u524a\u5f31\uff0c\u5982\u679c\u6d3e\u4eba\u517c\u7a0b\u8d74\u6765\u7a81\u88ad\uff0c\u65e0\u7591\u662f\u6700 \u597d\u7684\u673a\u4f1a\u3002 \u3000\u3000\u8fd9\u6837\u7684\u673a\u4f1a\u5353\u4e1c\u6765\u4e00\u5b9a\u5df2\u7ecf\u7b49\u5f85\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4e3e\u676f\u5411\u6731\u731b\u795d\u798f\u656c\u9152\u65f6\uff0c\u7a81\u88ad\u7684\u4eba\u9a6c\u4e00\u5b9a\u5df2\u5728\u9053\u9014\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u5b9a\u5c31\u662f\u90a3\u6b21\u7a81\u88ad\u7684\u7ed3\u679c\u3002 \u3000\u3000\u5c31\u5728\u6731\u731b\u81ea\u5df1\u89c9\u5f97\u81ea\u5df1\u5b8c\u5168\u5f97\u80dc\u65f6\uff0c\u4ed6\u5df2\u7ecf\u88ab\u51fb\u8d25\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u4ed6\u5b9e\u5728\u8d25\u5f97\u592a\u60e8\u3002 \u3000\u3000\u5c0f\u9ad8\u7684\u624b\u8db3\u51b0\u51b7\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u60f3\u8c61\u6731\u731b\u600e\u4e48\u80fd\u627f\u53d7\u8fd9\u4e48\u5927\u7684\u6253\u51fb\uff0c\u53ef\u662f\u4ed6\u76f8\u4fe1\u6731\u731b\u4e00\u5b9a\u4e0d\u4f1a\u88ab\u51fb\u5012\u3002 \u3000\u3000\u53ea\u8981\u6731\u731b\u8fd8\u6d3b\u7740\uff0c\u5c31\u4e00\u5b9a\u4e0d\u4f1a\u88ab\u4efb\u4f55\u4eba\u51fb\u5012\u3002 \u3000\u3000\u73b0\u5728\u5c0f\u9ad8\u552f\u4e00\u60f3\u5230\u7684\u662f\uff0c\u6731\u731b\u6025\u7740\u8981\u53bb\u62a5\u590d\uff0c\u56e0\u4e3a\u73b0\u5728\u5353\u4e1c\u6765\u4e00\u5b9a\u5df2\u7ecf\u5728\u957f\u5b89\u5f20\u5f00\u4e86\u7f57 \u7f51\uff0c\u7b49\u7740\u4ed6\u53bb\u3002 \u3000\u3000\u5982\u679c\u73b0\u5728\u6731\u731b\u5df2\u7ecf\u5230\u4e86\u957f\u5b89\uff0c\u90a3\u4e48\u4ed6\u6d3b\u7740\u56de\u6765\u7684\u673a\u4f1a\u5c31\u5f88\u5c11\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u7ecf\u8fc7\u8fd9\u4e48\u5927\u7684\u4e00\u6b21\u6253\u51fb\u540e\uff0c\u4ed6\u7684\u601d\u60f3\u548c\u884c\u52a8\u90fd\u96be\u514d\u56e0\u6025\u8e81\u6124\u6012\u800c\u758f\u5ffd\u3002 \u3000\u3000\u53ea\u8981\u6709\u4e00\u70b9\u758f\u5ffd\uff0c\u5c31\u53ef\u80fd\u9020\u6210\u81f4\u547d\u7684\u9519\u8bef\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7684\u8ba1\u5212\u90fd\u662f\u6c38\u8fdc\u4e0d\u4f1a\u6709\u758f\u5ffd\u7684\uff0c\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u5c0f\u9ad8\u8fde\u5fc3\u90fd\u51b7\u900f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5df2\u4e0b\u5b9a\u51b3\u5fc3\u3002 \u3000\u3000\u4ed6\u4e5f\u8981\u8d76\u56de\u957f\u5b89\u53bb\uff0c\u4e0d\u7ba1\u6731\u731b\u73b0\u5728\u662f\u6b7b\u662f\u6d3b\uff0c\u4ed6\u90fd\u8981\u8d76\u56de\u53bb\u3002 \u3000\u3000\u5982\u679c\u6731\u731b\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4ed6\u4e5f\u8bb8\u8fd8\u80fd\u4e3a\u4ed6\u7684\u670b\u53cb\u5c3d\u4e00\u5206\u529b\u3002 \u3000\u3000\u4ed6\u8fd8\u6709\u4e00\u53cc\u624b\u4e00\u628a\u5251\u4e00\u6761\u547d\u3002 \u3000\u3000\u5982\u679c\u6731\u731b\u5df2\u7ecf\u6b7b\u5728\u5353\u4e1c\u6765\u624b\u91cc\uff0c\u4ed6\u4e5f\u8981\u8d76\u56de\u53bb\u4e3a\u4ed6\u7684\u670b\u53cb\u53bb\u6536\u5c38\u3001\u53bb\u62fc\u547d\u3001\u53bb\u590d\u4ec7\u3002 \u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u76f4\u5230\u73b0\u5728\u4e3a\u6b62\u8fd8\u53ea\u6709\u6731\u731b\u4e00\u4e2a\u4eba\u628a\u4ed6\u5f53\u4f5c\u670b\u53cb\u3002 \u3000\u3000\u4ed6\u4e5f\u53ea\u6709\u6731\u731b\u8fd9\u4e48\u6837\u4e00\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u201c\u670b\u53cb\u201d\u8fd9\u4e24\u4e2a\u5b57\u7684\u610f\u4e49\u4ed6\u867d\u7136\u8fd8\u4e0d\u80fd\u5b8c\u5168\u4e86\u89e3\uff0c\u56e0\u4e3a\u4ed6\u4ee5\u524d\u4ece\u6765\u6ca1\u6709\u4ea4\u8fc7\u670b\u53cb\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6709\u4e00\u80a1\u6c14\u3002 \u3000\u3000\u4e00\u80a1\u4fa0\u6c14\uff0c\u4e00\u80a1\u8840\u6c14\uff0c\u4e00\u80a1\u4e49\u6c14\u3002 \u3000\u3000\u2014\u2014\u5c31\u56e0\u4e3a\u8fd9\u4e2a\u4e16\u754c\u4e0a\u8fd8\u6709\u4e9b\u4eba\u6709\u8fd9\u4e48\u6837\u4e00\u80a1\u6c14\uff0c\u6240\u4ee5\u6b63\u4e49\u624d\u80fd\u51fb\u8d25\u90aa\u6076\uff0c\u4eba\u7c7b\u624d\u80fd\u6c38 \u8fdc\u5b58\u5728\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u73b0\u5728\u9ad8\u6e10\u98de\u65e0\u8bba\u60f3\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u5374\u5f88\u56e0\u96be\u4e86\u3002 \u4e8c \u3000\u3000\u672c\u6765\u5bc2\u9759\u65e0\u4eba\u7684\u957f\u5df7\u91cc\uff0c\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u9ad8\u6700\u591a\u53ea\u6709\u56db\u5c3a\u7684\u8910\u8863\u4eba\uff0c\u5374\u6709\u4e00\u5f20\u4e00\u5c3a\u957f\u7684\u9a6c\u8138\uff0c\u4e24\u6761\u6d53\u7709\u5c31\u597d\u50cf\u4e24\u628a\u626b\u5e1a\u822c \u8fde\u5728\u4e00\u8d77\uff0c\u800c\u4e14\u8fd8\u7528\u6761\u7c97\u7ef3\u5b50\u5728\u7709\u5fc3\u6253\u4e86\u4e2a\u7ed3\u3002 \u3000\u3000\u4ed6\u7684\u5e74\u7eaa\u7edd\u4e0b\u4f1a\u592a\u5927\uff0c\u53ef\u662f\u770b\u8d77\u672a\u5374\u663e\u5f97\u5f88\u8001\u6c14\uff0c\u6d53\u7709\u4e0b\u4e00\u53cc\u72ed\u773c\u95ea\u95ea\u53d1\u5149\uff0c\u4e00\u770b\u89c1\u5c0f \u9ad8\uff0c\u4ed6\u7684\u773c\u775b\u5c31\u50cf\u9489\u5b50\u4e00\u6837\u9489\u5728\u5c0f\u9ad8\u8eab\u4e0a\u3002 \u3000\u3000\u5c0f\u9ad8\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u50cf\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u65e0\u8bba\u8c01\u53ea\u8981\u770b\u8fc7\u4e00\u773c\u90fd\u4e0d\u592a\u5bb9\u6613\u5fd8\u8bb0\u3002 \u3000\u3000\u5c0f\u9ad8\u8bb0\u5f97\u4ed6\u672c\u6765\u597d\u50cf\u662f\u5728\u5df7\u5b50\u5916\u9762\u90a3\u6761\u5927\u8857\u4e0a\u5356\u5207\u7cd5\u7684\uff0c\u7528\u4e00\u628a\u53c8\u957f\u53c8\u72ed\u7684\u8584\u5200\uff0c\u5207\u4e00 \u5757\u5757\u7528\u67a3\u5b50\u505a\u7684\u751c\u7cd5\u3002 \u3000\u3000\u8fd9\u628a\u5200\u73b0\u5728\u5c31\u63d2\u5728\u8170\u5e26\u4e0a\u3002 \u3000\u3000\u5982\u679c\u8981\u7528\u8fd9\u628a\u5200\u5c06\u4e00\u4e2a\u4eba\u4e00\u5757\u5757\u5207\u5f00\u6765\uff0c\u5927\u6982\u4e5f\u4e0d\u662f\u4ef6\u5927\u56f0\u96be\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e00\u51fa\u9662\uff0c\u5df7\u5b50\u91cc\u5ffd\u7136\u5c31\u70ed\u95f9\u4e86\u8d77\u6765\u3002\u672c\u6765\u5728\u5927\u8857\u4e0a\u7684\u4eba\u5ffd\u7136\u95f4\u5168\u90fd\u6d8c\u5165\u4e86\u8fd9\u6761\u5df7 \u5b50\uff0c\u8857\u4e0a\u6240\u6709\u7684\u4eba\u597d\u50cf\u5168\u90e8\u6765\u4e86\uff0c\u5c31\u597d\u50cf\u6f6e\u6c34\u4e00\u6837\uff0c\u4e00\u4e0b\u5b50\u5c31\u628a\u5c0f\u9ad8\u6df9\u6ca1\u3002 \u3000\u3000\u5c0f\u9ad8\u53ea\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u5ffd\u7136\u95ef\u5165\u4e86\u4e00\u4e2a\u6781\u70ed\u95f9\u7684\u5e99\u4f1a\u91cc\uff0c\u56db\u9762\u516b\u65b9\u90fd\u6324\u6ee1\u4e86\u4eba\uff0c\u5404\u5f0f\u5404\u6837 \u7684\u4eba\uff0c\u6324\u5f97\u6c34\u6cc4\u4e0d\u901a\uff0c\u6324\u5f97\u4ed6\u8fde\u52a8\u90fd\u52a8\u4e0d\u4e86\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u77e5\u9053\u5e94\u8be5\u600e\u4e48\u6837\u5e94\u4ed8\u8fd9\u79cd\u5c40\u9762\uff0c\u56e0\u4e3a\u4ed6\u4ece\u672a\u4e5f\u6ca1\u6709\u9047\u5230\u8fc7\u8fd9\u79cd\u4e8b\u3002 \u3000\u3000\u5356\u5207\u7cd5\u7684\u4eba\u521a\u624d\u597d\u50cf\u5df2\u7ecf\u88ab\u6324\u5230\u4ed6\u9762\u524d\uff0c\u73b0\u5728\u5374\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5b9e\u5728\u592a\u77ee\uff0c\u8981\u60f3\u5728\u4eba\u4e1b\u91cc\u53bb\u627e\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u5b9e\u5728\u5f88\u96be\u627e\u5f97\u5230\uff0c\u53ef\u662f\u5982\u679c\u4ed6\u60f3\u7528\u4ed6 \u90a3\u628a\u5207\u7cd5\u7684\u5200\u5728\u4eba\u4e1b\u91cc\u5f80\u522b\u4eba\u7684\u8170\u773c\u4e0a\u523a\u4e00\u5200\uff0c\u90a3\u5c31\u6050\u6015\u6bd4\u5207\u7cd5\u8fd8\u5bb9\u6613\u3002 \u3000\u3000\u5c0f\u9ad8\u4e0d\u60f3\u6328\u8fd9\u4e48\u6837\u4e00\u5200\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u5148\u627e\u5230\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u5df2\u7ecf\u770b\u51fa\u8fd9\u4e2a\u4eba\u5c31\u662f\u8fd9\u4e00\u7fa4\u4eba\u7684\u9996\u8111\u3002 \u3000\u3000\u201c\u6211\u8981\u4e70\u5207\u7cd5\u3002\u201d\u5c0f\u9ad8\u5ffd\u7136\u5927\u58f0\u8bf4\uff1a\u201c\u5356\u5207\u7cd5\u7684\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u4ec0\u4e48\u5730\u65b9\u90fd\u6ca1\u6709\u53bb\u3002\u201d\u4e00\u4e2a\u4eba\u7528\u4e00\u79cd\u4f4e\u6c89\u800c\u6c99\u54d1\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u6211\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u58f0\u97f3\u662f\u4ece\u5c0f\u9ad8\u80cc\u540e\u4f20\u6765\u7684\uff0c\u5c0f\u9ad8\u8f6c\u8fc7\u5934\uff0c\u5374\u770b\u4e0d\u89c1\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53c8\u542c\u89c1\u4e86\u8fd9\u4e2a\u4eba\u7684\u58f0\u97f3\uff0c\u6240\u4ee5\u4ed6\u5f88\u5feb\u5c31\u660e\u767d\u4e86\uff0c\u4ed6\u4e00\u76f4\u6ca1\u6709\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u53ea\u4e0d\u8fc7 \u56e0\u4e3a\u4ed6\u4e00\u76f4\u90fd\u6ca1\u6709\u4f4e\u4e0b\u5934\u53bb\u770b\u3002 \u3000\u3000\u8fd9\u4e48\u77ee\u7684\u4e00\u4e2a\u4eba\uff0c\u88ab\u6324\u5728\u4eba\u4e1b\u91cc\uff0c\u5982\u679c\u4f60\u4e0d\u4f4e\u4e0b\u5934\u53bb\u770b\uff0c\u662f\u4e00\u5b9a\u770b\u4e0d\u5230\u7684\u3002 \u3000\u3000\u201c\u4f60\u770b\u4e0d\u89c1\u6211\uff0c\u6211\u4e5f\u770b\u4e0d\u89c1\u4f60\uff0c\u6211\u4eec\u600e\u4e48\u6837\u505a\u4e70\u5356\uff1f\u201d\u4ed6\u95ee\u5c0f\u9ad8\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u95ee\u9898\u597d\u89e3\u51b3\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5ffd\u7136\u5728\u4eba\u4e1b\u4e2d\u8e72\u4e0b\u53bb\uff0c\u522b\u4eba\u7684\u8138\u867d\u7136\u770b\u4e0d\u89c1\u4e86\uff0c\u53ef\u662f\u4e00\u5f20\u53c8\u957f\u53c8\u5927\u7684\u9a6c\u8138\u5374\u5df2\u7ecf\u5230 \u4e86\u4ed6\u773c\u524d\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u4eec\u662f\u4e0d\u662f\u53ef\u4ee5\u505a\u4e70\u5356\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u54a7\u5f00\u5927\u5634\u4e00\u7b11\uff0c\u5634\u89d2\u51e0\u4e4e\u54a7\u5230\u8033\u6839\uff0c\u201c\u4f60\u771f\u7684\u8981\u4e70\u5207\u7cd5\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u4e70\u5207\u7cd5\u5916\uff0c\u6211\u4eec\u8fd8\u6709\u6ca1\u6709\u522b\u7684\u4ea4\u6613\u53ef\u8c08\uff1f\u8fd8\u6709\u6ca1\u6709\u522b\u7684\u4e70\u5356\u53ef\u505a\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u5c31\u4e70\u5207\u7cd5\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u4e70\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u5356\u7ed9\u6211\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u51fa\u5f97\u8d77\u4ef7\u94b1\uff0c\u591a\u5c11\u6211\u90fd\u5356\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u5207\u7cd5\u662f\u4ec0\u4e48\u4ef7\u94b1\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u5f97\u770b\u4e86\u3002\u201d \u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u770b\u4eba\u3002\u201d \u3000\u3000\u201c\u770b\u4eba\uff1f\u201d\u5c0f\u9ad8\u4e0d\u61c2\uff1a\u201c\u5356\u5207\u7cd5\u4e5f\u8981\u770b\u4eba\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u770b\u4eba\uff0c\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u6765\u4e70\u5207\u7cd5\uff0c\u6211\u5c31\u8981\u4ec0\u4e48\u6837\u7684\u4ef7\u94b1\u3002\u201d \u3000\u3000\u770b\u4eba\u51fa\u4ef7\uff0c\u672c\u6765\u5c31\u662f\u505a\u751f\u610f\u7684\u79d8\u8bc0\u4e4b\u4e00\u3002 \u3000\u3000\u201c\u6709\u4e9b\u4eba\u6765\u4e70\u6211\u7684\u5207\u7cd5\uff0c\u6211\u53ea\u8981\u4e24\u6587\u94b1\u4e00\u65a4\uff0c\u6709\u4e9b\u4eba\u6765\u4e70\uff0c\u5c31\u662f\u51fa\u6211\u4e94\u767e\u6839\u91d1\u6761\u6211\u4e5f\u4e0d \u5356\u3002\u201d\u8fd9\u4e2a\u4eba\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u770b\u4ed6\u4e0d\u987a\u773c\u3002\u201d \u3000\u3000\u201c\u6211\u5462\uff1f\u201d\u5c0f\u9ad8\u95ee\uff1a\u201c\u4f60\u770b\u6211\u987a\u4e0d\u987a\u773c\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u76ef\u7740\u4ed6\u4e0a\u4e0a\u4e0b\u4e0b\u770b\u4e86\u534a\u5929\uff0c\u6d53\u7709\u4e0b\u72ed\u773c\u4e2d\u5bd2\u5149\u66b4\u5c04\u5982\u5229\u5203\uff0c\u5ffd\u7136\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60\u662f \u4e0d\u662f\u4ece\u957f\u5b89\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u624b\u91cc\u8fd9\u4e2a\u5305\u88b1\u91cc\u5305\u7740\u7684\u662f\u4ec0\u4e48\uff0c\u662f\u4e0d\u662f\u4e00\u53e3\u5251\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u4ece\u957f\u5b89\u8d76\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u4e0d\u662f\u4e3a\u4e86\u2018\u96c4\u72ee\u5802\u2019\u7684\u6731\u5927\u8001\u7237\u800c\u6765\u7684\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u5ffd\u7136\u53c8\u54a7\u5634\u4e00\u7b11\uff0c\u9732\u51fa\u4e00\u53e3\u767d\u9716\u68ee\u7684\u7259\u9f7f\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u7684\u4e70\u5356\u5c31\u8c08\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6b7b\u4eba\u662f\u4e0d\u4f1a\u5403\u5207\u7cd5\u7684\uff0c\u6211\u7684\u5207\u7cd5\u4e5f\u4e0d\u5356\u7ed9\u6b7b\u4eba\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u624b\u5fc3\u91cc\u5df2\u7ecf\u5f00\u59cb\u5728\u6dcc\u6c57\uff0c\u51b7\u6c57\u3002 \u3000\u3000\u56db\u9762\u7684\u4eba\u6f6e\u5982\u679c\u4e00\u4e0b\u5b50\u5168\u90e8\u6d8c\u8fc7\u6765\uff0c\u6324\u4e5f\u8981\u628a\u4ed6\u6324\u6b7b\uff0c\u4ed6\u600e\u4e48\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u4ed6\u542c\u5f97\u51fa\u8fd9\u4e9b\u4eba\u7684\u547c\u5438\u58f0\u5df2\u7ecf\u56e0\u4e3a\u5174\u594b\u800c\u53d8\u7c97\u4e86\uff0c\u65e0\u8bba\u8c01\u5728\u6740\u4eba\u524d\u90fd\u4f1a\u53d8\u5f97\u5174\u594b\u8d77\u6765 \u7684\u3002 \u3000\u3000\u4eba\u4e1b\u5df2\u7ecf\u5f00\u59cb\u5728\u5f80\u524d\u6324\uff0c\u5356\u5207\u7cd5\u7684\u4eba\u53f3\u624b\u5df2\u63e1\u4f4f\u4e86\u4ed6\u8170\u4e0a\u7684\u5207\u5200\u3002 \u3000\u3000\u5c0f\u9ad8\u5ffd\u7136\u53d1\u73b0\u4e86\u4e00\u4ef6\u4e8b\u2014\u2014 \u3000\u3000\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6700\u53ef\u6015\u7684\u5c31\u662f\u4eba\uff0c\u4eba\u529b\u5982\u679c\u80fd\u96c6\u4e2d\u56e2\u7ed3\uff0c\u8fdc\u6bd4\u4e16\u4e0a\u4efb\u4f55\u529b\u91cf\u90fd\u53ef\u6015\u3002 \u3000\u3000\u4f46\u662f\u9ad8\u6d59\u98de\u8fd8\u662f\u80fd\u6c89\u5f97\u4f4f\u6c14\u3002\u56e0\u4e3a\u4ed6\u5df2\u770b\u51fa\u8fd9\u4e9b\u4eba\u90fd\u662f\u96c4\u72ee\u5802\u7684\u4eba\uff0c\u90fd\u548c\u4ed6\u4e00\u6837\uff0c\u662f\u7ad9 \u5728\u6731\u731b\u8fd9\u4e00\u8fb9\u7684\uff0c\u6240\u4ee5\u4ed6\u8bf4\uff1a\u201c\u6211\u662f\u4ece\u957f\u5b89\u6765\u7684\uff0c\u6211\u8fd9\u5305\u88b1\u91cc\u7684\u786e\u6709\u4e00\u67c4\u6740\u4eba\u7684\u5229\u5251\uff0c\u53ea\u4e0d \u8fc7\u6211\u8981\u6740\u7684\u4eba\u5e76\u4e0d\u662f\u6731\u731b\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u6740\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u6740\u7684\u4eba\uff0c\u4e5f\u5c31\u662f\u4f60\u4eec\u8981\u6740\u7684\u4eba\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u4e5f\u8ddf\u4f60\u4eec\u4e00\u6837\uff0c\u6211\u4e5f\u662f\u6731\u731b \u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6211\u59d3\u9ad8\uff0c\u53eb\u9ad8\u6e10\u98de\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u6e10\u6e10\u8981\u9ad8\u98de\u8d77\u6765\u7684\u90a3\u4e2a\u9ad8\u6e10\u98de\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4f60\u4e0d\u59a8\u56de\u53bb\u95ee\u95ee\u6731\u731b\uff0c\u662f\u4e0d\u662f\u6709\u6211\u8fd9\u4e48\u6837\u4e00\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u5fc5\u95ee\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5356\u5207\u7cd5\u7684\u72ed\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u8be1\u8c32\u7684\u7b11\u610f\uff0c\u5ffd\u7136\u5bf9\u5c0f\u9ad8\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u77e5\u9053\u4f60\u662f\u6731\u731b\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u6211\u77e5\u9053\uff0c\u6240\u4ee5\u624d\u8981\u6740\u4f60\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u80cc\u5ffd\u7136\u6e7f\u900f\uff0c\u88ab\u51b7\u6c57\u6e7f\u900f\u3002 \u3000\u3000\u4eba\u4e1b\u867d\u7136\u53c8\u5728\u5f80\u524d\u6324\uff0c\u5207\u7cd5\u7684\u5200\u867d\u7136\u950b\u5229\uff0c\u53ef\u662f\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u8fd9\u662f\u6709\u673a\u4f1a\u53ef\u4ee5\u634f\u788e \u8fd9\u53ea\u63e1\u5200\u7684\u624b\uff0c\u6253\u65ad\u8fd9\u5f20\u9a6c\u8138\u4e0a\u7684\u9f3b\u6881\uff0c\u6316\u51fa\u8fd9\u53cc\u72ed\u773c\u4e2d\u7684\u8be1\u8c32\u6076\u6bd2\u4e4b\u610f\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e0d\u80fd\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u6740\u4e86\u8fd9\u4e2a\u4eba\uff0c\u4f46\u662f\u56db\u9762\u6f6e\u6c34\u822c\u7684\u4eba\u7fa4\u5374\u662f\u4ed6\u4e0d\u80fd\u6740\u4e5f\u6740\u4e0d\u5c3d\u7684\u3002 \u3000\u3000\u5982\u679c\u4ed6\u5229\u7528\u8fd9\u7a0d\u7eb5\u5373\u901d\u7684\u4e00\u77ac\u826f\u673a\u6740\u4e86\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u81ea\u5df1\u5c31\u5f88\u53ef\u80fd\u88ab\u522b\u4eba\u7684\u4e71\u5200\u65a9\u4e3a\u8089 \u9171\u3002 \u3000\u3000\u5356\u5207\u7cd5\u7684\u4eba\u53c8\u7b11\u4e86\uff0c\u9634\u607b\u607b\u7684\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u624b\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u672c\u6765\u8e72\u5728\u4ed6\u9762\u524d\u7684\u5c0f\u9ad8\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u4e00\u7ad9\u8d77\u6765\uff0c\u4ed6\u7684\u8eab\u5b50\u5c31\u5df2\u633a \u633a\u7684\u76f4\u62d4\u800c\u8d77\uff0c\u5c31\u597d\u50cf\u4e0a\u9762\u6709\u4e00\u53ea\u770b\u4e0d\u89c1\u7684\u5927\u624b\uff0c\u63d0\u8d77\u4e86\u4ed6\u7684\u8863\u9886\uff0c\u628a\u4ed6\u50cf\u62d4\u8471\u4e00\u6837\u62e8\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u8fd9\u662f\u6c5f\u6e56\u7f55\u89c1\u7684\u8f7b\u529f\uff0c\u4e5f\u662f\u6b7b\u4e2d\u6c42\u6d3b\u7684\u7edd\u6280\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u65e2\u4e0d\u662f\u98de\u4e4c\uff0c\u4e5f\u6ca1\u6709\u7fc5\u8180\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u53ea\u4e0d\u8fc7\u662f\u51ed\u4e00\u53e3\u771f\u6c14\u786c\u62e8\u8d77\u6765\u7684\uff0c\u8fd9\u80a1\u6c14\u968f\u65f6\u90fd\u4f1a\u7528\u7aed\u3002\u4ed6\u7684\u8eab\u5b50\u8fd8\u662f\u4f1a\u843d\u4e0b \u6765\uff0c\u843d\u4e0b\u6765\u65f6\u8fd8\u662f\u4f1a\u843d\u5165\u4eba\u4e1b\u4e2d\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u660e\u767d\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4e0b\u9762\u7684\u4eba\u4e00\u5b9a\u90fd\u5df2\u7ecf\u62d4\u51fa\u4e86\u5175\u5203\uff0c\u51c6\u5907\u597d\u6740\u624b\uff0c\u7b49\u7740\u4ed6\u529b\u7aed\u843d\u4e0b\u3002 \u3000\u3000\u90a3\u65f6\u4ed6\u5c31\u7b97\u8fd8\u80fd\u62d4\u5251\u6740\u4eba\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5fc5\u5c06\u6b7b\u5728\u522b\u4eba\u7684\u8840\u6cca\u548c\u5c38\u4f53\u95f4\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u505a\u8fd9\u79cd\u4e8b\uff0c\u4e5f\u4e0d\u60f3\u770b\u5230\u90a3\u79cd\u8840\u8089\u6a2a\u98de\u7684\u60e8\u8c61\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e5f\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5ffd\u7136\u770b\u89c1\u4e00\u6761\u957f\u7ef3\u8fdc\u8fdc\u7684\u98de\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u770b\u89c1\u8fd9\u6761\u957f\u7ef3\u662f\u4ece\u54ea\u91cc\u98de\u6765\u7684\uff0c\u4e5f\u6ca1\u6709\u770b\u89c1\u8fd9\u6761\u7ef3\u7d22\u5728\u8c01\u7684\u624b\u91cc\u3002 \u3000\u3000\u5e78\u8fd0\u7684\u662f\uff0c\u4ed6\u770b\u89c1\u4e86\u8fd9\u6761\u957f\u7ef3\uff0c\u800c\u4e14\u80fd\u53ca\u65f6\u6293\u4f4f\u3002 \u3000\u3000\u957f\u7ef3\u5728\u7528\u529b\u793e\u524d\u62c9\uff0c\u4ed6\u7684\u8eab\u5b50\u4e5f\u501f\u7740\u7ef3\u5b50\u4e0a\u7684\u8fd9\u80a1\u529b\u91cf\u88ab\u62c9\u8d77\u3002 \u3000\u3000\u5c31\u50cf\u662f\u98ce\u7b5d\u4e00\u6837\u88ab\u62c9\u8d77\uff0c\u8d8a\u62c9\u8d8a\u9ad8\u3002 \u3000\u3000\u62c9\u7740\u7ef3\u5b50\u7684\u4eba\u4e5f\u50cf\u62c9\u98ce\u7b5d\u4e00\u6837\u5728\u5f80\u524d\u62c9\uff0c\u5c0f\u9ad8\u8fd8\u662f\u6ca1\u6709\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u5374\u542c\u89c1\u4e86\u4e00\u9635\u5f88\u719f \u6089\u7684\u58f0\u97f3\u3002 \u3000\u3000\u9489\u978b\u5728\u96ea\u5730\u4e0a\u5954\u8dd1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5c0f\u9ad8\u5fc3\u91cc\u7acb\u523b\u6709\u4e86\u4e00\u80a1\u6e29\u6696\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u53c8\u770b\u89c1\u4e86\u4e00\u4e2a\u4eba\uff0c\u7a7f\u7740\u53cc\u9489\u978b\uff0c\u62c9\u7740\u4e00\u5339\u9a6c\u7684\u5c3e\u5df4\uff0c\u4e5f\u50cf\u662f\u98ce\u7b5d\u4e00\u6837\u88ab\u6302\u5728\u9a6c\u5c3e \u4e0a\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u53c8\u770b\u89c1\u4e86\u9a6c\u4e0a\u7684\u90a3\u4e2a\u4eba\uff0c\u53c8\u770b\u89c1\u4e86\u90a3\u4e2a\u4eba\u7684\u96c4\u98ce\u548c\u8c6a\u6c14\u3002 \u3000\u3000\u4ed6\u65e9\u5c31\u77e5\u9053\u6731\u731b\u662f\u7edd\u4e0d\u4f1a\u88ab\u4efb\u4f55\u4eba\u51fb\u5012\u7684\u3002 \u4e09 \u3000\u3000\u201c\u9ad8\u5927\u5c11\uff0c\u60f3\u4e0d\u5230\u4f60\u771f\u7684\u6765\u4e86\u3002\u201d\u9489\u978b\u7684\u5954\u8dd1\u4e00\u505c\u4e0b\uff0c\u5c31\u4f0f\u5012\u5728\u96ea\u5730\uff1a\u201c\u5802\u4e3b\u65e9\u5c31\u8bf4\u9ad8 \u5927\u5c11\u4e00\u5b9a\u4f1a\u6765\u770b\u4ed6\u7684\uff0c\u60f3\u4e0d\u5230\u9ad8\u5927\u5c11\u771f\u7684\u6765\u4e86\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7528\u4e86\u5f88\u5927\u7684\u529b\uff0c\u624d\u80fd\u628a\u8fd9\u4e2a\u5fe0\u5fc3\u7684\u670b\u53cb\u4ece\u96ea\u5730\u4e0a\u62c9\u8d77\u6765\u3002 \u3000\u3000\u201c\u5e94\u8be5\u8dea\u4e0b\u7c73\u7684\u662f\u6211\uff0c\u201d\u4ed6\u5bf9\u9489\u978b\u8bf4\uff1a\u201c\u4f60\u6551\u4e86\u6211\u7684\u547d\u3002\u201d \u3000\u3000\u9489\u978b\u64e6\u5e72\u4e86\u51e0\u4e4e\u5df2\u5c06\u593a\u7736\u800c\u51fa\u7684\u70ed\u6cea\uff0c\u795e\u8272\u53c8\u53d8\u5f97\u6124\u6168\u8d77\u6765\u3002 \u3000\u3000\u201c\u5c0f\u4eba\u65e9\u5c31\u7b97\u51c6\u8521\u5d07\u7edd\u4e0d\u4f1a\u653e\u8fc7\u5802\u751f\u7684\u4efb\u4f55\u4e00\u4f4d\u670b\u53cb\uff0c\u201d\u9489\u978b\u8bf4\uff1a\u201c\u5802\u4e3b\u7684\u670b\u53cb\u4eec\u51e0\u4e4e \u5df2\u5168\u90fd\u906d\u4e86\u4ed6\u7684\u6bd2\u624b\uff0c\u5c31\u8fde\u4ece\u8fdc\u5730\u6765\u7684\u90fd\u6ca1\u6709\u653e\u8fc7\u4e00\u4e2a\u3002\u201d \u3000\u3000\u201c\u8521\u795f\u5c31\u662f\u90a3\u4e2a\u5356\u5207\u78c1\u7684\u602a\u7269\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u672c\u6765\u5f53\u7136\u4e0d\u662f\u5356\u5207\u7cd5\u7684\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4ed6\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u548c\u59d3\u6768\u7684\u90a3\u5c0f\u5b50\u4e00\u6837\uff0c\u672c\u6765\u90fd\u662f\u5802\u4e3b\u7684\u5fc3\u8179\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e5f\u8ddf\u6768\u575a\u4e00\u6837\uff0c\u80cc\u53db\u4e86\u4f60\u4eec\u7684\u5802\u4e3b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6bd4\u6768\u575a\u66f4\u53ef\u6076\uff0c\u201d\u9489\u978b\u6068\u6068\u7684\u8bf4\uff1a\u201c\u4ed6\u80cc\u53db\u5802\u4e3b\u7684\u65f6\u5019\uff0c\u6b63\u662f\u5802\u4e3b\u5fc3\u91cc\u6700\u96be\u53d7\u3001\u6700 \u9700\u8981\u4ed6\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u201c\u4f60\u4eec\u4ece\u957f\u5b89\u56de\u6765\u65f6\uff0c\u4e0d\u4f46\u96c4\u72ee\u5802\u5df2\u7ecf\u88ab\u6bc1\u4e86\uff0c\u8521\u5d07\u4e5f\u53cd\u4e86\uff0c\u201d\u5c0f\u9ad8\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u90a3\u4e24 \u5929\u4f60\u4eec\u7684\u65e5\u5b50\u4e00\u5b9a\u5f88\u4e0d\u597d\u8fc7\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u201d\u9489\u978b\u8bf4\uff1a\u201c\u662f\u5f88\u4e0d\u597d\u8fc7\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u65e0\u8bba\u591a\u96be\u8fc7\u7684\u65e5\u5b50\u90fd\u4f1a\u8fc7\u53bb\u7684\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u201d\u9489\u978b\u50cf\u6728\u5076\u822c\u91cd\u590d\u5c0f\u9ad8\u7684\u8bdd\uff1a\u201c\u662f\u4f1a\u8fc7\u53bb\u7684\u3002\u201d \u3000\u3000\u4ed6\u7684\u773c\u775b\u91cc\u5ffd\u7136\u6d41\u9732\u51fa\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u6c89\u75db\u548c\u54c0\u4f24\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u4eba\u773c\u770b\u7740\u81ea\u5df1\u5728\u5f80\u4e0b\u6c89\uff0c \u6c89\u4eba\u4e86\u4e07\u52ab\u4e0d\u590d\u7684\u6d41\u6c99\u3002 \u3000\u3000\u5c0f\u9ad8\u7684\u5fc3\u5ffd\u7136\u95f4\u4e5f\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u2014\u2014\u8521\u5d07\u5728\u6731\u731b\u6700\u56f0\u96be\u65f6\u80cc\u53db\u4e86\u4ed6\uff0c\u6731\u731b\u5374\u76f4\u5230\u73b0\u5728\u8fd8\u8ba9\u4ed6\u9ad8\u9ad8\u5174\u5174\u7684\u5927\u6447\u5927\u6446\u6d3b\u5728\u8fd9 \u4e2a\u4e16\u754c\u4e0a\u3002 \u3000\u3000\u8fd9\u7edd\u4e0d\u662f\u6731\u731b\u5e73\u65f6\u7684\u4f5c\u98ce\u3002 \u3000\u3000\u5c0f\u9ad8\u76ef\u7740\u9489\u7566\u7684\u773c\u775b\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e0d\u6562\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u9489\u978b\u4e5f\u7d27\u5f20\u8d77\u6765\uff1a\u201c\u4ec0\u4e48\u4e0d\u6562\u544a\u8bc9\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u5ffd\u7136\u7528\u529b\u63e1\u4f4f\u4ed6\u7684\u80a9\uff1a\u201c\u4f60\u4eec\u7684\u5802\u4e3b\u662f\u4e0d\u662f\u5df2\u7ecf\u906d\u4e86\u6bd2\u624b\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u771f\u7684\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u771f\u7684\u6ca1\u6709\u3002\u201d\u9489\u978b\u597d\u50cf\u5728\u5c3d\u529b\u60f3\u505a\u51fa\u4e00\u70b9\u6109\u5feb\u7684\u8868\u60c5\u6765\uff1a\u201c\u5c0f\u4eba\u73b0\u5728\u5c31\u53ef\u4ee5\u5e26\u9ad8\u5927\u5c11 \u53bb\u770b\u4ed6\u3002\u201d \u56db \u3000\u3000\u79ef\u96ea\u7684\u67af\u6797\uff0c\u72f0\u72de\u7684\u5ca9\u77f3\u3002 \u3000\u3000\u5ca9\u77f3\u524d\u751f\u7740\u4e00\u5806\u706b\uff0c\u5ca9\u77f3\u4e0a\u9ad8\u8e1e\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5df2\u7ecf\u7626\u5f97\u8131\u4e86\u5f62\u7684\u4eba\uff0c\u5c31\u50cf\u662f\u4e00\u53ea\u5df2\u6709\u5f88\u4e45\u672a\u66fe\u89c1\u5230\u6b7b\u4eba\u5c38\u4f53\u7684\u5140\u9e70\u3002 \u3000\u3000\u706b\u7130\u5728\u95ea\u52a8\uff0c\u95ea\u52a8\u7684\u706b\u5149\u7167\u5728\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u4e00\u5f20\u5145\u6ee1\u4e86\u5b64\u72ec\u7edd\u671b\u548c\u60b2\u4f24\u7684\u5927\u8138\uff0c\u6d53\u7709\u95f4\u9501\u6ee1\u4e86\u6101\u5bb9\uff0c\u4e00\u53cc\u75b2\u5026\u65e0\u795e\u7684\u5927\u773c\u5df2\u6df1\u9677\u5728 \u98a7\u9aa8\u91cc\uff0c\u52a8\u4e5f\u4e0d\u52a8\u7684\u51dd\u89c6\u7740\u9762\u524d\u95ea\u52a8\u7684\u706b\u5149\uff0c\u5c31\u597d\u50cf\u6b63\u5728\u671f\u5f85\u7740\u706b\u7130\u4e2d\u4f1a\u6709\u5947\u8ff9\u51fa\u73b0\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u6731\u731b\u3002 \u3000\u3000\u201c\u96c4\u72ee\u201d\u6731\u731b\u7edd\u4e0b\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u201c\u96c4\u72ee\u201d\u6731\u731b\u4e00\u5411\u662f\u6761\u597d\u6c49\uff0c\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u51fb\u5012\u7684\u597d\u6c49\u3002 \u3000\u3000\u53ef\u662f\u9489\u978b\u5df2\u62dc\u5012\u5728\u5ca9\u77f3\u524d\uff1a\u201c\u62a5\u544a\u5802\u4e3b\uff0c\u5802\u4e3b\u6700\u60f3\u89c1\u7684\u4eba\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u4ed6\u7684\u773c\u6cea\u867d\u7136\u5df2\u7ecf\u5c06\u8981\u593a\u7736\u800c\u51fa\uff0c\u4f46\u5374\u6ca1\u6709\u6d41\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u5df2\u591a\u5e74\u672a\u66fe\u6d41\u6cea\u3002 \u3000\u3000\u6731\u731b\u5df2\u7ecf\u62ac\u5934\uff0c\u832b\u7136\u770b\u7740\u4ed6\uff0c\u4eff\u4f5b\u5df2\u7ecf\u8ba4\u4e0d\u51fa\u7ad9\u5728\u4ed6\u9762\u524d\u7684\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u5c0f\u9ad8\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u624d\u660e\u767d\u9489\u978b\u773c\u4e2d\u4e3a\u4ec0\u4e48\u4f1a\u6709\u90a3\u79cd\u7edd\u671b\u7684\u8868\u60c5\u4e86\uff0c\u4f46\u4ed6\u5374\u8fd8\u662f\u4e0d\u660e\u767d\u90a3\u5929\u5728\u7ea2\u82b1\u96c6 \u5916\u7eb5\u9a6c\u6325\u5200\u6740\u4eba\u4e8e\u7728\u773c\u95f4\u7684\u597d\u6c49\uff0c\u600e\u4e48\u4f1a\u5982\u6b64\u8f7b\u6613\u5c31\u88ab\u51fb\u5012\u3002 \u3000\u3000\u201c\u5c0f\u9ad8\uff0c\u9ad8\u6e10\u98de\u3002\u201d \u3000\u3000\u6731\u731b\u5ffd\u7136\u72c2\u543c\u4e00\u58f0\uff0c\u4ece\u5ca9\u77f3\u4e0a\u8dc3\u4e0b\uff0c\u6251\u8fc7\u6765\u62b1\u4f4f\u4e86\u5c0f\u9ad8\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u4eff\u4f5b\u53c8\u6709\u4e86\u751f\u6c14\uff0c\u201c\u6211\u5c31\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6765\uff0c\u4f60\u679c\u7136\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7528\u529b\u62b1\u7d27\u5c0f\u9ad8\uff0c\u7528\u81ea\u5df1\u7684\u8138\u8d34\u4f4f\u5c0f\u9ad8\u7684\u8138\u3002 \u3000\u3000\u4ed6\u5728\u7b11\uff0c\u7eb5\u58f0\u5927\u7b11\uff0c\u5c31\u597d\u50cf\u90a3\u5929\u5728\u7ea2\u82b1\u96c6\u5916\u6325\u5200\u65a9\u4eba\u5934\u9885\u65f6\u4e00\u6837\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u9ad8\u5374\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u7684\u8138\u5df2\u7ecf\u6e7f\u4e86\u3002 \u3000\u3000\u2014\u2014\u662f\u4e0d\u662f\u6709\u4eba\u5728\u6d41\u6cea\uff1f\u662f\u8c01\u5728\u6d41\u6cea\uff1f \u3000\u3000\u201c\u6d6a\u5b50\u4e09\u5531\uff0c\u4e0d\u5531\u60b2\u6b4c\u3002 \u3000\u3000\u7ea2\u5c18\u95f4\uff0c\u60b2\u4f24\u4e8b\uff0c\u5df2\u5927\u591a\u3002 \u3000\u3000\u6d6a\u5b50\u4e3a\u541b\u6b4c\u4e00\u66f2\uff0c\u529d\u541b\u5207\u83ab\u628a\u6cea\u6d41\uff0c\u4eba\u95f4\u82e5\u6709\u4e0d\u5e73\u4e8b\uff0c\u7eb5\u9152\u6325\u5200\u65a9\u4eba\u5934\u3002\u201d \u4e94 \u3000\u3000\u4e00\u628a\u94c1\u67aa\uff0c\u4e00\u53ea\u94dc\u58f6\uff0c\u4e00\u58f6\u6d4a\u9152\u3002 \u3000\u3000\u4e00\u5806\u706b\u3002 \u3000\u3000\u9489\u978b\u4ee5\u94c1\u67aa\u540a\u94dc\u58f6\u5728\u706b\u4e0a\u716e\u9152\uff0c\u677e\u679d\u4e2d\u6709\u5bd2\u98ce\u547c\u5578\u800c\u8fc7\uff0c\u9152\u4ecd\u672a\u70ed\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u9ad8\u7684\u8840\u5df2\u70ed\u4e86\u3002 \u3000\u3000\u201c\u5353\u4e1c\u6765\uff0c\u8fd9\u4e2a\u738b\u516b\u86cb\u5012\u771f\u4ed6\u5a18\u7684\u662f\u4e2a\u89d2\u8272\u3002\u201d\u6731\u731b\u5df2\u7ecf\u559d\u4e86\u4e09\u58f6\u9152\uff0c\u201c\u4ed6\u867d\u7136\u6363\u4e86\u6211 \u7684\u8001\u7a9d\uff0c\u6211\u8fd8\u662f\u4e0d\u80fd\u4e0d\u670d\u4ed6\u3002\u201d \u3000\u3000\u6d4a\u800c\u4e0b\u809a\uff0c\u8c6a\u6c14\u6e10\u751f\uff1a\u201c\u670d\u5f52\u670d\uff0c\u53ef\u662f\u8fdf\u65e9\u603b\u6709\u4e00\u5929\uff0c\u8001\u5b50\u8fd8\u662f\u4f1a\u5272\u4e0b\u4ed6\u7684\u8111\u888b\u672a\u5f53\u591c \u58f6\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u770b\u7740\u4ed6\uff0c\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u6ca1\u6709\u53bb\uff1f\u201d \u3000\u3000\u6731\u731b\u970d\u7136\u7ad9\u8d77\uff0c\u53c8\u6162\u6162\u7684\u5750\u4e0b\uff0c\u8138\u4e0a\u5ffd\u7136\u53c8\u9732\u51fa\u90a3\u79cd\u7edd\u671b\u7684\u60b2\u4f24\u4e4b\u8272\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u8fd8\u4e0d\u80fd\u53bb\u3002\u201d\u6731\u731b\u9ed8\u7136\u9053\uff0c\u201c\u6211\u53bb\u4e86\uff0c\u5979\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u201c\u5979\u662f\u8c01\uff1f\u662f\u4e0d\u662f\u4e2a\u5973\u4eba\uff1f\u201d \u3000\u3000\u6731\u731b\u6447\u5934\uff0c\u95ed\u5634\uff0c\u559d\u9152\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u53bb\u6740\u8521\u5d07\uff0c\u4e5f\u662f\u4e3a\u4e86\u5979\uff1f\u201d\u5c0f\u9ad8\u53c8\u95ee\u3002 \u3000\u3000\u6731\u731b\u53c8\u6447\u5934\uff0c\u8fc7\u4e86\u5f88\u4e45\u7528\u4e00\u79cd\u5636\u54d1\u800c\u7834\u788e\u7684\u58f0\u97f3\u53cd\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u4e2a\u5c0f\u5a4a\u5b50\u517b \u7684\u5e26\u8d70\u4e86\u6211\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5e26\u8d70\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u5168\u90e8\u3002\u201d \u3000\u3000\u201c\u5168\u90e8\uff1f\u201d\u5c0f\u9ad8\u5f88\u60ca\u8bb6\uff1a\u201c\u96be\u9053\u96c4\u72ee\u5802\u6240\u6709\u7684\u5f1f\u5b50\u90e8\u8ddf\u7740\u4ed6\u8d70\u4e86\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u9489\u978b\u5916\uff0c\u6bcf\u4e2a\u4eba\u90fd\u88ab\u4ed6\u6536\u4e70\u4e86\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u4e00\u76f4\u5728\u66ff\u6211\u7ba1\u94b1\u3002\u96c4 \u72ee\u5802\u6240\u6709\u94b1\u8d22\u7684\u8fdb\u51fa\uff0c\u90fd\u8981\u7ecf\u8fc7\u4ed6\u7684\u624b\u3002\u6211\u4ece\u6765\u90fd\u6ca1\u6709\u7ba1\u8fc7\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u8ba4\u4e3a\u4f60\u5c31\u7b97\u53bb\u627e\u4ed6\u4e5f\u6ca1\u6709\u7528\u7684\uff0c\u56e0\u4e3a\u4ed6\u7684\u4eba\u6bd4\u4f60\u591a\u5f97\u591a\u3002\u201d \u3000\u3000\u6731\u731b\u5c45\u7136\u627f\u8ba4\u4e86\uff0c\u521a\u624d\u88ab\u70c8\u9152\u6fc0\u8d77\u7684\u8c6a\u6c14\u5ffd\u7136\u95f4\u53c8\u5df2\u6d88\u5931\u3002 \u3000\u3000\u4ed6\u7528\u4e00\u53cc\u9aa8\u8282\u51f8\u51fa\u7684\u5927\u624b\u68d2\u7740\u4ed6\u7684\u9152\u7897\uff0c\u4e00\u5927\u53e3\u4e00\u5927\u53e3\u7684\u559d\u7740\u6eda\u70eb\u7684\u70ed\u9152\uff0c\u9664\u4e86\u8fd9\u7897\u9152 \u4e4b\u5916\uff0c\u8fd9\u4e2a\u4e16\u754c\u597d\u50cf\u5df2\u6ca1\u6709\u522b\u7684\u4e8b\u503c\u5f97\u4ed6\u5173\u5fc3\u3002 \u3000\u3000\u5c0f\u9ad8\u7684\u5fc3\u5728\u523a\u75db\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u6731\u731b\u4e0d\u4f46\u5916\u8868\u53d8\u4e86\uff0c\u8fde\u5185\u90e8\u90fd\u5df2\u5f00\u59cb\u5728\u8150\u70c2\u3002 \u3000\u3000\u4ee5\u524d\u7684\u6731\u731b\u7edd\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u4ee5\u524d\u4ed6\u5982\u679c\u77e5\u9053\u80cc\u53db\u4ed6\u7684\u4eba\u8fd8\u5728\u5927\u8857\u4e0a\u7b49\u7740\u523a\u6740\u4ed6\u7684\u670b\u53cb\uff0c\u5c31\u7b97\u6709\u5343\u519b\u4e07\u9a6c\u5728\u4fdd\u62a4\u90a3\u4e2a \u4eba\uff0c\u4ed6\u4e5f\u4f1a\u7eb5\u9a6c\u6325\u5200\u51b2\u8fdb\u53bb\u5c06\u90a3\u4e2a\u4eba\u65a9\u6740\u4e8e\u9a6c\u8e44\u524d\u3002 \u3000\u3000\u2014\u2014\u4e5f\u8bb8\u8fd9\u624d\u662f\u4ed6\u95e8\u4e0b\u5f1f\u5b50\u80cc\u53db\u4ed6\u7684\u4e3b\u8981\u539f\u56e0\u3002 \u3000\u3000\u5728\u6c5f\u6e56\u4e2d\u6df7\u7684\u4eba\uff0c\u8c01\u613f\u610f\u8ddf\u968f\u4e00\u4e2a\u52c7\u6c14\u5df2\u4e27\u5931\u7684\u9996\u9886\uff1f \u3000\u3000\u5c0f\u9ad8\u5b9e\u5728\u4e0d\u660e\u767d\u4e00\u6761\u94c1\u94ee\u94ee\u7684\u597d\u6c49\u4e3a\u4ec0\u4e48\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\uff1f\u4e3a\u4ec0\u4e48\u4f1a\u53d8\u5f97\u8fd9\u4e48\u5feb\uff1f \u3000\u3000\u4ed6\u6ca1\u6709\u95ee\u6731\u731b\u3002 \u3000\u3000\u6731\u731b\u5df2\u7ecf\u9189\u4e86\uff0c\u9189\u5f97\u6bd4\u6614\u65e5\u5feb\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u5de8\u5927\u7684\u9aa8\u9abc\u5916\u672c\u6765\u5df2\u7ecf\u53ea\u5269\u4e0b\u4e00\u5c42\u8584\u8584\u7684\u76ae\u8089\uff0c\u9189\u5012\u540e\u770b\u6765\u5c31\u50cf\u662f\u4e00\u5934\u96c4\u72ee\u7684\u67af\u9aa8\u3002 \u3000\u3000\u5c0f\u9ad8\u4e0d\u5fcd\u518d\u770b\u4ed6\u3002 \u3000\u3000\u706b\u5149\u4ecd\u5728\u95ea\u52a8\uff0c\u9489\u978b\u4ecd\u5728\u716e\u9152\uff0c\u4e5f\u6ca1\u6709\u53bb\u770b\u4ed6\u3002\u773c\u4e2d\u5374\u53c8\u9732\u51fa\u4e86\u90a3\u79cd\u7edd\u671b\u7684\u6c89\u75db\u548c\u60b2 \u4f24\u3002 \u3000\u3000\u5c0f\u9ad8\u7ad9\u8d77\u6765\uff0c\u8d70\u8fc7\u53bb\uff0c\u9ed8\u9ed8\u7684\u628a\u624b\u91cc\u4e00\u7897\u9152\u9012\u7ed9\u4e86\u4ed6\u3002 \u3000\u3000\u9489\u978b\u8fdf\u7591\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u4e00\u53e3\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c0f\u9ad8\u63a5\u8fc7\u4ed6\u7684\u94c1\u67aa\uff0c\u4e5f\u4ece\u94dc\u58f6\u91cc\u5012\u51fa\u4e00\u7897\u9152\u3002\u4e00\u53e3\u559d\u4e0b\u53bb\uff0c\u7136\u540e\u624d\u53f9\u606f\u7b54\u9053\uff1a\u201c\u6211\u679c\u7136 \u6ca1\u6709\u770b\u9519\u4f60\uff0c\u4f60\u679c\u7136\u662f\u4ed6\u7684\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u5c0f\u4eba\u4e0d\u662f\u5802\u4e3b\u7684\u670b\u53cb\uff0c\u201d\u9489\u978b\u7684\u8868\u60c5\u6781\u4e25\u8083\uff1a\u201c\u5c0f\u4eba\u4e0d\u914d\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u4e5f\u8bb8\u53ea\u6709\u4f60\u624d\u662f\u4ed6\u771f\u6b63\u7684\u670b\u53cb\uff0c\u4e5f\u53ea\u6709\u4f60\u624d\u914d\u505a\u4ed6\u7684\u670b\u53cb\uff01\u201d \u3000\u3000\u201c\u5c0f\u4eba\u4e0d\u914d\uff0c\u201d\u9489\u978b\u8fd8\u662f\u8bf4\uff1a\u201c\u5c0f\u4eba\u4e5f\u4e0d\u6562\u8fd9\u4e48\u6837\u60f3\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u73b0\u5728\u53ea\u6709\u4f60\u5728\u966a\u7740\u4ed6\u3002\u201d \u3000\u3000\u201c\u90a3\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u5c0f\u4eba\u8fd9\u6761\u547d\u672c\u6765\u5c31\u662f\u5802\u4e3b\u7684\u3002\u201d\u9489\u978b\u8bf4\uff1a\u201c\u5c0f\u4eba\u8fd9\u4e00\u8f88\u5b50\u90fd\u8ddf\u5b9a\u4ed6 \u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u5df2\u7ecf\u53d8\u6210\u4e86\u8fd9\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5802\u4e3b\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\u90fd\u4e00\u6837\u662f\u6211\u7684\u5802\u4e3b\u3002\u201d\u9489\u978b\u65ad\u7136\u8bf4\uff1a\u201c\u8fd9\u4e00\u70b9\u662f\u7edd\u4e0d\u4f1a\u53d8 \u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u89c1\u4ed6\u53d8\u5316\u8fd9\u4e48\u5927\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u96be\u53d7\uff1f\u201d \u3000\u3000\u9489\u978b\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5c0f\u9ad8\u53c8\u5012\u4e86\u7897\u9152\uff0c\u770b\u7740\u4ed6\u559d\u4e0b\u53bb\uff0c\u7136\u540e\u624d\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u4e00\u5b9a\u4e5f\u8ddf\u6211\u4e00\u6837\u96be \u53d7\u7684\uff0c\u4e00\u5b9a\u4e5f\u5e0c\u671b\u4ed6\u80fd\u591f\u632f\u4f5c\u8d77\u6765\u3002\u201d \u3000\u3000\u9489\u978b\u6c89\u9ed8\u3002 \u3000\u3000\u5c0f\u9ad8\u51dd\u89c6\u7740\u4ed6\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u60f3\u4e0d\u51fa\u4ec0\u4e48\u6cd5\u5b50\u80fd\u8ba9\u4ed6\u632f\u4f5c\u3002\u201d \u3000\u3000\u9489\u978b\u53c8\u559d\u4e86\u4e00\u7897\u9152\uff0c\u8fd9\u6b21\u662f\u4ed6\u81ea\u5df1\u5012\u7684\u9152\u3002 \u3000\u3000\u5c0f\u9ad8\u4e5f\u559d\u4e86\u4e00\u7897\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u51fa\uff0c\u6211\u60f3\u5f97\u51fa\u3002\u201d \u3000\u3000\u9489\u978b\u7acb\u523b\u62ac\u8d77\u5934\uff0c\u76ef\u7740\u5c0f\u9ad8\u3002 \u3000\u3000\u201c\u53ef\u662f\u4f60\uff0c\u5b9a\u8981\u5148\u544a\u8bc9\u6211\uff0c\u4ed6\u662f\u600e\u4e48\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\uff1f\u201d\u5c0f\u9ad8\u4e5f\u5728\u76ef\u7740\u9489\u978b\uff0c\u201c\u662f\u4e0d\u662f \u4e3a\u4e86\u4e00\u4e2a\u5973\u4eba\uff1f\u201d \u3000\u3000\u201c\u9ad8\u5927\u5c11\uff0c\u201d\u9489\u978b\u7684\u58f0\u97f3\u597d\u50cf\u5728\u54ed\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u95ee\u8fd9\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u8981\u95ee\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u8981\u6cbb\u75c5\uff0c\u5c31\u5f97\u5148\u67e5\u51fa\u4ed6\u7684\u75c5\u6839\u3002\u201d \u3000\u3000\u9489\u978b\u672c\u6765\u597d\u50cf\u5df2\u7ecf\u51c6\u5907\u8bf4\u4e86\uff0c\u5ffd\u7136\u53c8\u7528\u529b\u6447\u5934\uff0c\u201c\u5c0f\u4eba\u4e0d\u80fd\u8bf4\uff0c\u4e5f\u4e0d\u6562\u8bf4\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9489\u978b\u7d22\u6027\u5750\u4e0b\u53bb\uff0c\u7528\u53cc\u624b\u62b1\u4f4f\u4e86\u81ea\u5df1\u7684\u5934\uff0c\u4e0d\u7406\u5c0f\u9ad8\u4e86\u3002 \u3000\u3000\u2014\u2014\u6731\u731b\u7a76\u7adf\u662f\u600e\u4e48\u53d8\u7684\uff1f\u771f\u7684\u662f\u4e3a\u4e86\u4e00\u4e2a\u5973\u4eba\uff1f \u3000\u3000\u2014\u2014\u90a3\u4e2a\u5973\u4eba\u662f\u8c01\uff1f\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u9489\u978b\u4e3a\u4ec0\u4e48\u4e0d\u6562\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u591c\u66f4\u6df1\uff0c\u66f4\u51b7\u3002\u706b\u52bf\u5df2\u5f31\u3002 \u3000\u3000\u9489\u978b\u6323\u624e\u7740\u7ad9\u8d77\u6765\uff0c\u5583\u5583\u7684\u8bf4\uff1a\u201c\u5c0f\u4eba\u53bb\u627e\u4e9b\u67f4\u6765\u6dfb\u706b\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u8d70\u5f00\uff0c\u6731\u731b\u5ffd\u7136\u5728\u9189\u68a6\u4e2d\u53d1\u51fa\u4e00\u58f0\u5927\u543c\u3002 \u3000\u3000\u201c\u8776\u821e\uff0c\u4f60\u4e0d\u80fd\u8d70\u3002\u201d\u4ed6\u5636\u58f0\u4f4e\u543c\uff1a\u201c\u4f60\u662f\u6211\u7684\uff0c\u8c01\u4e5f\u4e0d\u80fd\u628a\u4f60\u5e26\u8d70\u3002\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5927\u543c\uff0c\u5c31\u50cf\u662f\u4e00\u6839\u97ad\u5b50\uff0c\u91cd\u91cd\u7684\u62bd\u5728\u9489\u978b\u8eab\u4e0a\u3002 \u3000\u3000\u9489\u978b\u7684\u8eab\u5b50\u5ffd\u7136\u5f00\u59cb\u53d1\u6296\u3002 \u3000\u3000\u6731\u731b\u7ffb\u4e86\u4e2a\u8eab\u53c8\u7761\u7740\u4e86\uff0c\u5c0f\u9ad8\u5df2\u62e6\u4f4f\u9489\u978b\u4e3a\u53bb\u8def\uff0c\u7528\u529b\u63e1\u4f4f\u4ed6\u7684\u53cc\u80a9\u3002 \u3000\u3000\u201c\u662f\u8776\u821e\uff0c\u4e00\u5b9a\u662f\u8776\u821e\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6731\u731b\u4e00\u5b9a\u662f\u4e3a\u4e86\u5979\u624d\u53d8\u7684\u3002\u201d \u3000\u3000\u9489\u978b\u5782\u4e0b\u4e86\u5934\uff0c\u7ec8\u4e8e\u9ed8\u7136\u4e86\u3002 \u3000\u3000\u201c\u73b0\u5728\u5979\u8fd8\u5728\u4e0d\u5728\u6d1b\u9633\uff1f\u201d\u5c0f\u9ad8\u95ee\u3002 \u3000\u3000\u201c\u4e0d\u5728\u3002\u201d\u9489\u978b\u9053\uff1a\u201c\u5c0f\u4eba\u548c\u5802\u4e3b\u8fdc\u8d74\u957f\u5b89\u56de\u6765\u65f6\u7684\u5934\u4e00\u5929\u665a\u4e0a\uff0c\u6709\u4eba\u591c\u88ad\u96c4\u72ee\u5802\uff0c\u90a3 \u5929\u665a\u4e0a\u6b63\u597d\u662f\u8521\u5d07\u5f53\u503c\uff0c\u5c45\u7136\u5728\u6beb\u65e0\u6212\u5907\u7684\u60c5\u51b5\u4e0b\uff0c\u8ba9\u4eba\u8f7b\u6613\u5f97\u624b\uff0c\u4e0d\u4f46\u70e7\u4e86\u6211\u4eec\u7684\u96c4\u72ee \u5802\uff0c\u8fd8\u6740\u4e86\u6211\u4eec\u56db\u5341\u591a\u4f4d\u5144\u5f1f\uff0c\u624d\u626c\u957f\u800c\u53bb\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u90a3\u4e9b\u4eba\u4e00\u5b9a\u662f\u5353\u4e1c\u6765\u6d3e\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4e00\u5b9a\u662f\u3002\u201d\u9489\u978b\u8bf4\uff1a\u201c\u4ed6\u4eec\u6765\u7684\u4e0d\u4f46\u90fd\u662f\u597d\u624b\uff0c\u800c\u4e14\u5bf9\u6211\u4eec\u5185\u90e8\u7684\u60c5\u51b5\u5f88\u719f\u6089\u3002\u201d \u3000\u3000\u201c\u96c4\u72ee\u5802\u91cc\u4e00\u5b9a\u4e5f\u6709\u5353\u4e1c\u6765\u6d3e\u6765\u5367\u5e95\u7684\u4eba\u3002\u201d\u5c0f\u9ad8\u8bf4\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6709\u4eba\u6000\u7591\u8521\u5d07\u65e9\u5c31\u6709\u4e86\u80cc\u53db\u5802\u4e3b\u7684\u610f\u601d\uff0c\u4e5f\u6709\u4eba\u8ba4\u4e3a\u4ed6\u662f\u56e0\u4e3a\u77e5\u9053\u81ea\u5df1\u758f\u4e8e\u804c \u5b88\uff0c\u751f\u6015\u5802\u4e3b\u7528\u5bb6\u6cd5\u6cbb\u4ed6\uff0c\u6240\u4ee5\u5c31\u7d22\u6027\u53cd\u4e86\u3002\u201d \u3000\u3000\u201c\u8776\u821e\u662f\u4e0d\u662f\u4e5f\u8ddf\u4ed6\u4e00\u8d77\u53cd\u4e86\uff1f\u201d \u3000\u3000\u9489\u978b\u6447\u5934\uff1a\u201c\u8776\u59d1\u5a18\u4e00\u5411\u770b\u4e0d\u8d77\u90a3\u4e2a\u81ed\u5c0f\u5b50\uff0c\u600e\u4e48\u4f1a\u8ddf\u7740\u5979\u8d70\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u5979\u662f\u88ab\u5353\u4e1c\u6765\u7684\u4eba\u67b6\u8d70\u7684\uff1f\u60f3\u7528\u5979\u6765\u505a\u4eba\u8d28\uff0c\u8981\u80c1\u6731\u731b\uff1f\u201d \u3000\u3000\u9489\u978b\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5c31\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u6240\u4ee5\u5802\u4e3b\u624d\u6ca1\u6709\u5230\u957f\u5b89\u53bb\u627e\u53f8\u9a6c\u7b97\u5e10\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u8521\u5d07\u4e0d\u53cd\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u53bb\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u4e0d\u4f1a\u3002\u201d\u9489\u978b\u9eef\u7136\u9053\uff1a\u201c\u5982\u679c\u5802\u4e3b\u5230\u4e86\u957f\u5b89\uff0c\u5927\u9556\u5c40\u7684\u90a3\u4e9b\u738b\u516b\u86cb\u5f88\u53ef\u80fd\u5c31\u4f1a\u7acb \u523b\u628a\u8776\u59d1\u5a18\u62ff\u6765\u5f00\u5200\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u542c\u8d77\u6765\u53c8\u597d\u50cf\u8981\u54ed\u7684\u6837\u5b50\uff1a\u201c\u5802\u4e3b\u66fe\u7ecf\u544a\u8bc9\u5c0f\u4eba\uff0c\u53ea\u8981\u8776\u59d1\u5a18\u80fd\u597d\u597d\u7684\u6d3b\u7740\uff0c \u5802\u4e3b\u5c31\u7b97\u53d7\u70b9\u7f6a\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u8fd9\u4f4d\u8776\u59d1\u5a18\uff0c\u6240\u4ee5\u4f60\u4eec\u7684\u5802\u4e3b\u624d\u4f1a\u53d8\u5f97\u610f\u6c14\u6d88\u6c89\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u60f3\u505a\uff1f\u6240\u4ee5\u8521\u5d07 \u76f4\u5230\u73b0\u5728\u8fd8\u80fd\u5927\u6447\u5927\u6446\u7684\u6a2a\u884c\u95f9\u5e02\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4eba\u4e5f\u60f3\u4e0d\u5230\u5802\u4e3b\u4f1a\u4e3a\u4e86\u4e00\u4e2a\u5973\u4eba\u8fd9\u4e48\u75f4\u5fc3\u3002\u201d\u9489\u978b\u8bf4\uff1a\u201c\u5c0f\u4eba\u5b9e\u5728\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d \u5230\u3002\u201d \u3000\u3000\u4ed6\u672c\u6765\u4ee5\u4e3a\u5c0f\u9ad8\u4e00\u5b9a\u4f1a\u89c9\u5f97\u8fd9\u662f\u4ef6\u5f88\u53ef\u7b11\u7684\u4e8b\uff0c\u53ef\u601c\u800c\u53c8\u53ef\u7b11\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u53d1\u73b0\u5c0f\u9ad8\u7684\u773c\u4e2d\u5ffd\u7136\u4e5f\u53d8\u5f97\u5145\u6ee1\u4e86\u60b2\u4f24\uff0c\u6b63\u5728\u75f4\u75f4\u7684\u671b\u7740\u8fdc\u65b9\u7684\u9ed1\u6697\u51fa\u795e\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u8fde\u540d\u5b57\u90fd\u4e0d\u77e5\u9053\u7684\u5973\u4eba\uff0c\u4e00\u6bb5\u6c38\u751f\u90e8\u96be\u4ee5\u5fd8\u6000\u7684\u604b\u60c5\u3002 \u3000\u3000\u9489\u978b\u5f53\u7136\u4e0d\u77e5\u9053\u8fd9\u4e9b\u4e8b\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u542c\u89c1\u5c0f\u9ad8\u7528\u4e00\u79cd\u6e29\u67d4\u800c\u4f24\u611f\u7684\u58f0\u97f3\u8bf4\uff0c \u3000\u3000\u201c\u4f60\u4eec\u7684\u5802\u4e3b\u5e76\u6ca1\u6709\u53d8\uff0c\u4ed6\u8fd8\u662f\u6761\u7537\u5b50\u6c49\u3002\u201d\u5c0f\u9ad8\u9053\uff1a\u201c\u6709\u771f\u6b63\u7684\u7537\u5b50\u6c49\u624d\u4f1a\u5173\u5fc3\u522b \u4eba\uff0c\u5982\u679c\u4ed6\u5b8c\u5168\u4e0d\u5173\u5fc3\u522b\u4eba\u7684\u6b7b\u6d3b\uff0c\u4f60\u5927\u6982\u4e5f\u4e0d\u4f1a\u8ddf\u7740\u4ed6\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u9489\u978b\u989e\u98a5\u7740\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\u624d\u9f13\u8d77\u52c7\u6c14\u9053\uff1a\u201c\u9ad8\u5927\u5c11\uff0c\u6709\u53e5\u8bdd\u5c0f\u4eba\u4e0d\u77e5\u9053\u8be5\u4e0d\u8be5\u8bf4\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u6bcf\u4e2a\u4eba\u90fd\u5e94\u8be5\u5173\u5fc3\u522b\u4eba\u7684\uff0c\u53ef\u662f\u4e3a\u4e86\u522b\u4eba\u6298\u78e8\u81ea\u5df1\u5c31\u4e0d\u5bf9\u4e86\u3002\u201d\u9489\u978b\u8bf4\uff1a\u201c\u90a3\u6837\u5b50\u53cd \u800c\u4f1a\u8ba9\u4ed6\u5173\u5fc3\u7684\u4eba\u4f24\u5fc3\u5931\u671b\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u52c9\u5f3a\u7684\u7b11\u4e86\u7b11\uff0c\u6539\u53d8\u4e86\u8bdd\u9898\u3002 \u3000\u3000\u201c\u6211\u770b\u5230\u90a3\u8fb9\u6709\u4e2a\u907f\u98ce\u7684\u5730\u65b9\uff0c\u6211\u8981\u53bb\u7761\u4e00\u4e0b\u3002\u201d\u4ed6\u5bf9\u9489\u978b\u8bf4\uff1a\u201c\u4f60\u4e5f\u8be5\u7761\u4e86\u3002\u201d \u3000\u3000\u5929\u5730\u95f4\u53c8\u5b8c\u5168\u6c89\u5bc2\u4e0b\u6765\uff0c\u53ea\u5269\u4e0b\u67af\u679d\u5728\u706b\u7130\u4e2d\u88ab\u71c3\u70e7\u65f6\u53d1\u51fa\u7684\u201c\u5288\u556a\u201d\u58f0\u3002 \u3000\u3000\u9489\u978b\u5c06\u4e00\u6761\u539a\u6be1\u94fa\u5728\u5ca9\u77f3\u4e0a\uff0c\u62b1\u7740\u6731\u731b\u7761\u4e0a\u53bb\uff0c\u53c8\u7528\u4e24\u6761\u6bdb\u6be1\u76d6\u4f4f\uff0c\u7136\u540e\u4ed6\u81ea\u5df1\u624d\u5728\u65c1 \u8fb9\u7761\u4e0b\u6765\uff0c\u7761\u5728\u51b0\u51b7\u7684\u5ca9\u77f3\u4e0a\uff0c\u5c31\u50cf\u662f\u4e2a\u867e\u7c73\u822c\u7f16\u6210\u4e86\u4e00\u56e2\u3002 \u3000\u3000\u5929\u4eae\u524d\u4ed6\u88ab\u51bb\u9192\u65f6\uff0c\u5c31\u53d1\u89c9\u5c0f\u9ad8\u4e5f\u5df2\u9192\u4e86\u3002 \u3000\u3000\u5728\u71b9\u5fae\u7684\u6668\u5149\u4e2d\uff0c\u4ed6\u770b\u89c1\u5c0f\u9ad8\u6b63\u5728\u7528\u51b0\u96ea\u6d17\u8138\uff0c\u800c\u4e14\u8fd8\u597d\u50cf\u628a\u624b\u91cc\u7684\u90a3\u4e2a\u5305\u88b1\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u9489\u978b\u6ca1\u6709\u770b\u6e05\u5305\u88b1\u91cc\u7a76\u7adf\u6709\u6ca1\u6709\u4e00\u628a\u5251\uff0c\u66f4\u6ca1\u6709\u770b\u89c1\u5251\u7684\u5f62\u72b6\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u4ed4\u7ec6\u53bb\u770b\u3002 \u3000\u3000\u4ed6\u88c5\u4f5c\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u5fc3\u4e00\u76f4\u5728\u8df3\uff0c\u8df3\u5f97\u597d\u5feb\u597d\u5feb\u3002 \u516d \u3000\u3000\u6731\u731b\u9192\u6765\u65f6\u5929\u5df2\u5927\u4eae\uff0c\u9489\u978b\u65e9\u5df2\u8d77\u6765\uff0c\u6b63\u5728\u751f\u706b\u70e7\u6c34\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u9ad8\u5374\u4e0d\u5728\u4e86\u3002 \u3000\u3000\u6731\u731b\u8dc3\u8d77\u6765\uff0c\u7528\u4e00\u53cc\u5e03\u6ee1\u8840\u4e1d\u7684\u5927\u773c\u5230\u5904\u53bb\u627e\u4e5f\u627e\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u5589\u4e2d\u53d1\u51fa\u91ce\u517d\u822c\u7684\u4f4e\u543c\u3002 \u3000\u3000\u201c\u4ed6\u4e5f\u8d70\u4e86\uff1f\u201d\u6731\u731b\u95ee\u9489\u978b\uff1a\u201c\u4ed6\u662f\u4ec0\u4e48\u65f6\u5019\u8d70\u7684\uff0c\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u8fd8\u4f1a\u4e0d\u4f1a\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u62a5\u544a\u5802\u4e3b\uff0c\u9ad8\u5927\u5c11\u8d70\u7684\u65f6\u5019\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u8bf4\uff0c\u5c0f\u4eba\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u201d\u9489\u978b \u8bf4\uff1a\u201c\u53ef\u662f\u5802\u4e3b\u5e94\u8be5\u60f3\u5f97\u5230\u7684\uff0c\u56e0\u4e3a\u9ad8\u5927\u5c11\u662f\u5802\u4e3b\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u6731\u731b\u7684\u4eba\u672c\u6765\u5df2\u56e0\u60b2\u4f24\u5931\u671b\u800c\u53d8\u5f97\u66f4\u840e\u7f29\uff0c\u542c\u5230\u9489\u978b\u8fd9\u53e5\u6d3b\uff0c\u5374\u5ffd\u7136\u632f\u594b\u8d77\u6765\uff0c\u5145\u6ee1\u8840 \u5927\u7684\u773c\u4e2d\u4e5f\u6709\u4e86\u5149\uff0c\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u5e94\u8be5\u77e5\u9053\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u201d\u6731\u731b\u5927\u58f0\u9053\uff1a\u201c\u9489\u978b\uff0c\u6211\u4eec\u4e5f\u8d70\u5427\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u9489\u978b\u7684\u7cbe\u795e\u597d\u50cf\u4e5f\u632f\u594b\u8d77\u6765\uff0c\u773c\u4e2d\u5374\u6709\u4e86\u70ed\u6cea\uff0c\u201c\u5c0f\u4eba\u65e9\u5c31\u51c6\u5907\u597d\u4e86\uff0c\u5c0f\u4eba\u968f \u65f6\u90fd\u5728\u51c6\u5907\u7740\uff0c\u5c0f\u4eba\u4e00\u76f4\u90fd\u5728\u7b49\u7740\u8fd9\u4e00\u5929\u3002\u201d \u7b2c\u516b\u7ae0\u3000\u4e49\u65e0\u53cd\u987e>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u516b\u7ae0\u3000\u4e49\u65e0\u53cd\u987e \u4e00 \u3000\u3000\u4e8c\u6708\u521d\u4e03\u3002 \u3000\u3000\u6d1b\u9633\u3002 \u3000\u3000\u8521\u795f\u5750\u5728\u7528\u56db\u6839\u6728\u68cd\u548c\u4e00\u5757\u5e06\u5e03\u9489\u6210\u7684\u51f3\u5b50\u4e0a\uff0c\u770b\u7740\u8857\u4e0a\u7199\u6765\u6518\u5728\u7684\u4eba\u62dc\uff0c\u8138\u8272\u9634\u6c89\u6c89 \u7684\uff0c\u65e0\u8bba\u8c01\u90fd\u770b\u5f97\u51fa\u6765\u4eca\u5929\u4ed6\u7684\u5fc3\u60c5\u4e0d\u5927\u597d\u3002 \u3000\u3000\u5c0f\u9ad8\u672c\u6765\u5df2\u7ecf\u662f\u4ed6\u74ee\u4e2d\u7684\u9cd6\uff0c\u7f51\u4e2d\u7684\u9c7c\uff0c\u60f3\u4e0b\u5230\u7adf\u5728\u6700\u540e\u4e00\u77ac\u95f4\u4ece\u4ed6\u638c\u63e1\u4e2d\u6e9c\u8d70\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4ed6\u7684\u6bcf\u6b21\u884c\u52a8\u90fd\u5f88\u987a\u5229\uff0c\u6210\u529f\u5f97\u5927\u5feb\u4e86\u4e9b\u3002\u6240\u4ee5\u624d\u4f1a\u9020\u6210\u8fd9\u79cd\u758f\u5ffd\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u5728\u8fd9\u4e9b\u65e5\u5b50\u91cc\uff0c\u5e76\u6ca1\u6709\u7247\u523b\u5fd8\u8bb0\u8fc7\u6731\u731b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6731\u731b\u73b0\u5728\u4e00\u5b9a\u8fd8\u6ca1\u6709\u79bb\u5f00\u6d1b\u9633\uff0c\u5982\u679c\u4ed6\u51b3\u5fc3\u53bb\u627e\uff0c\u4e00\u5b9a\u80fd\u627e\u5f97\u5230\u7684\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u53bb\u627e\uff0c\u4ed6\u5e76\u4e0d\u56e0\u4e3a\u662f\u6127\u5bf9\u6545\u4eba\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u4e0d\u6562\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u867d\u7136\u5df2\u53d6\u4ee3\u4e86\u6731\u731b\u7684\u5730\u4f4d\uff0c\u53ef\u662f\u5728\u4ed6\u5fc3\u5e95\u6df1\u5904\uff0c\u4ed6\u8fd8\u662f\u5bf9\u6731\u731b\u5b58\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684 \u754f\u60e7\u3002 \u3000\u3000\u5728\u6731\u731b\u591a\u5e74\u7684\u79ef\u5a01\u4e4b\u4e0b\uff0c\u8fd9\u79cd\u754f\u60e7\u5df2\u7ecf\u5728\u4ed6\u5fc3\u91cc\u751f\u4e86\u6839\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u53ea\u8981\u4e00\u60f3\u8d77\u6731\u731b\uff0c\u8fd8\u662f\u4f1a\u89c9\u5f97\u624b\u8db3\u51b0\u51b7\uff0c\u5168\u8eab\u5192\u6c57\uff0c\u6709\u65f6\u751a\u81f3\u4f1a\u5728\u534a\u591c\u4ece\u5669\u68a6\u4e2d \u60ca\u9192\uff0c\u4e00\u4e2a\u4eba\u8eba\u5728\u88ab\u81ea\u5df1\u51b7\u6c57\u6e7f\u900f\u4e86\u7684\u88ab\u8925\u4e2d\u53d1\u6296\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u6731\u731b\u6765\u627e\u4ed6\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u4f0d\u8fd9\u6761\u8857\u4e0b\u5e03\u6ee1\u4e86\u81f4\u547d\u7684\u9677\u997c\u548c\u57cb\u4f0f\uff0c\u53ea\u8981\u4ed6\u4e00\u58f0\u4ee4\u4e0b\uff0c\u6240\u6709\u7684\u57cb\u4f0f\u7acb\u523b\u5c31\u53ef\u53d1 \u52a8\u3002\u5c31\u7b97\u6731\u731b\u7684\u4f53\u80fd\u8fd8\u5728\u5dc5\u5cf0\u65f6\uff0c\u4e5f\u4e00\u6837\u9003\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u624d\u4f1a\u6bcf\u5929\u4e00\u5927\u65e9\u5c31\u5750\u5728\u8fd9\u91cc\u5356\u5207\u7cd5\uff0c\u56e0\u4e3a\u4ed6\u8981\u7528\u81ea\u5df1\u505a\u9975\uff0c\u9493\u6731\u731b\u90a3\u6761\u5927\u9c7c\u3002 \u3000\u3000\u8fd9\u6837\u505a\u867d\u7136\u5192\u9669\uff0c\u53ef\u662f\u53ea\u8981\u6731\u731b\u8fd8\u6d3b\u7740\uff0c\u4ed6\u8fd9\u4e00\u8f88\u5b50\u5c31\u4f11\u60f3\u6709\u4e00\u5929\u597d\u65e5\u5b50\u8fc7\u3002 \u3000\u3000\u8fd9\u662f\u6761\u70ed\u95f9\u7684\u957f\u8857\uff0c\u6709\u83dc\u9986\uff0c\u6709\u82b1\u5e02\uff0c\u8fd8\u6709\u83dc\u573a\uff0c\u6240\u4ee5\u5728\u6e05\u6668\u65f6\u5c31\u6709\u4e86\u65e9\u5e02\uff0c\u4e00\u5927\u65e9\u8857 \u4e0a\u5c31\u6324\u6ee1\u4e86\u4eba\uff0c\u8fd9\u4e24\u5929\u7684\u60c5\u51b5\u548c\u5e73\u65f6\u4e0d\u540c\u7684\u5730\u65b9\u662f\uff1a\u8857\u4e0a\u7684\u4eba\u81f3\u5c11\u6709\u4e00\u534a\u662f\u4ed6\u5e03\u4e0b\u7684\u57cb\u4f0f\uff0c \u5176\u4e2d\u4e0d\u4f46\u6709\u96c4\u72ee\u5802\u7684\u65e7\u90e8\uff0c\u4e5f\u6709\u4ed6\u6700\u8fd1\u624d\u4ece\u8fdc\u5730\u627e\u6765\u7684\u4ea1\u547d\u4e4b\u5f92\u3002 \u3000\u3000\u4e00\u4e9b\u53ea\u8981\u6709\u94b1\u4ec0\u4e48\u4e8b\u90fd\u505a\u5f97\u51fa\u7684\u4ea1\u547d\u4e4b\u5f92\u3002 \u3000\u3000\u6731\u731b\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e9b\u4eba\uff0c\u4ed6\u4eec\u5bf9\u6731\u731b\u4e5f\u6ca1\u6709\u4efb\u4f55\u611f\u60c5\u3002 \u3000\u3000\u5c31\u7b97\u96c4\u72ee\u5802\u7684\u65e7\u90e8\u4e2d\u4e5f\u6709\u4eba\u548c\u4ed6\u4e00\u6837\uff0c\u5bf9\u6731\u731b\u72b9\u6709\u4f59\u60b8\uff0c\u5728\u51fa\u624b\u65f6\u96be\u514d\u72b9\u7591\u754f\u60e7\uff0c\u53ef\u662f \u8fd9\u4e9b\u4ea1\u547d\u4e4b\u5f92\u5374\u662f\u516d\u4eb2\u4e0d\u8ba4\u7684\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u8521\u5d07\u7684\u5fc3\u91cc\u624d\u6bd4\u8f83\u8212\u670d\u4e86\u4e9b\u3002\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4ed6\u770b\u89c1\u4e00\u4e2a\u4eba\u8d70\u4eba\u4e86\u8fd9\u6761\u957f \u8857\u3002 \u3000\u3000\u201c\u5c0f\u9ad8\uff0c\u9ad8\u6e10\u98de\uff01\u201d \u3000\u3000\u8521\u5d07\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u4e2a\u6628\u5929\u624d\u4ece\u6b7b\u91cc\u9003\u751f\u7684\u4eba\uff0c\u73b0\u5728\u5c45\u7136\u53c8\u7279\u5730\u6765\u9001\u6b7b\u4e86\u3002 \u4e8c \u3000\u3000\u5c0f\u9ad8\u8eab\u4e0a\u53ea\u82bd\u7740\u4ef6\u5355\u8584\u7684\u77ed\u886b\u88e4\uff0c\u5374\u5c06\u4e00\u4ef6\u957f\u886b\u642d\u5728\u80a9\u8180\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u8138\u5df2\u7ecf\u88ab\u51bb\u5f97\u53d1\u7ea2\uff0c\u773c\u91cc\u4e5f\u5e26\u7740\u8840\u4e1d\uff0c\u663e\u89c1\u5f97\u5f88\u4e45\u90fd\u6ca1\u6709\u7761\u597d\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u7cbe\u795e\u770b\u8d77\u6765\u5374\u4e0d\u574f\uff0c\u795e\u60c5\u4e5f\u5f88\u9547\u5b9a\uff0c\u770b\u6765\u548c\u5176\u4ed6\u90a3\u4e9b\u6765\u5403\u65e9\u8336\u7684\u4eba\u5e76\u6ca1\u6709\u4ec0\u4e48 \u4e24\u6837\u3002 \u3000\u3000\u5df2\u7ecf\u8ba4\u51fa\u4ed6\u7684\u4eba\uff0c\u90fd\u77aa\u5927\u4e86\u773c\u775b\uff0c\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u773c\u4e2d\u90fd\u6709\u4e86\u6740\u673a\u3002 \u3000\u3000\u5c0f\u9ad8\u5374\u4e00\u70b9\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u6709\u4eba\u5df2\u7ecf\u51c6\u5907\u5bf9\u4ed6\u51fa\u624b\u4e86\uff0c\u5947\u602a\u7684\u662f\uff0c\u8521\u5d07\u5c45\u7136\u4e00\u76f4\u90fd\u6ca1\u6709\u53d1\u51fa\u884c\u52a8\u7684\u53f7\u4ee4\uff0c\u5c45\u7136\u5c31\u8fd9 \u6837\u770b\u7740\u5c0f\u9ad8\u8d70\u5230\u4ed6\u7684\u9762\u524d\u3002 \u3000\u3000\u5c0f\u9ad8\u5728\u8521\u5d07\u9762\u524d\u4e00\u5f20\u6446\u6ee1\u5207\u7cd5\u7684\u5c0f\u6728\u684c\u524d\u7ad9\u4f4f\uff0c\u684c\u4e0a\u7684\u5207\u7cd5\u662f\u7528\u597d\u51e0\u5c42\u68c9\u8925\u76ca\u7740\u7684\uff0c\u5c0f \u9ad8\u629b\u4e86\u4e24\u6587\u94b1\u5728\u6728\u684c\u4e0a\uff0c\u770b\u7740\u8521\u5d07\u3002 \u3000\u3000\u201c\u6211\u8981\u4e70\u4e24\u6587\u94b1\u5207\u7cd5\uff0c\u8981\u5e26\u7740\u67a3\u5b50\u7684\u90a3\u4e00\u8fb9\u3002\u201d \u3000\u3000\u8521\u5d07\u4e5f\u5728\u770b\u7740\u4ed6\uff0c\u770b\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u7b11\u4e86\uff1a\u201c\u4f60\u771f\u7684\u662f\u6765\u4e70\u5207\u7cd5\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u5356\u7684\u662f\u5207\u7cd5\uff0c\u6211\u5f53\u7136\u53ea\u6709\u6765\u4e70\u5207\u7cd5\uff0c\u8fd9\u79cd\u4e8b\u6709\u4ec0\u4e48\u5947\u602a\uff1f\u6709\u4ec0\u4e48\u597d\u7b11\uff1f\u201d \u3000\u3000\u201c\u7684\u786e\u4e0d\u597d\u7b11\uff0c\u4e00\u70b9\u90fd\u4e0d\u597d\u7b11\u3002\u201d\u8521\u5d07\u8bf4\uff1a\u201c\u8fd9\u79cd\u4e8b\u5b9e\u5728\u503c\u5f97\u5927\u54ed\u4e00\u573a\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u54ed\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5e94\u8be5\u54ed\u7684\u4e0d\u662f\u6211\uff0c\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u53ea\u8981\u6211\u4e00\u58f0\u4ee4\u4e0b\uff0c\u73b0\u5728\u4f60\u5f88\u53ef\u80fd\u5df2\u7ecf\u53d8\u6210\u4e2a\u523a\u732c\u4e86\uff0c\u8eab\u4e0a\u6700\u5c11\u4e5f\u6709\u5341\u4e03\u516b \u4e2a\u5730\u65b9\u4f1a\u50cf\u6c34\u888b\u7834\u4e86\u6d1e\u4e00\u6837\u5f80\u5916\u9762\u6d41\u8840\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u73b0\u5728\u8fd8\u6d3b\u7740\uff0c\u201d\u8521\u5d07\u51b7\u51b7\u7684\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u80fd\u8bdd\u5230\u73b0\u5728\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5b9e\u5728\u5f88\u60f3\u95ee\u95ee\u4f60\uff0c\u4f60\u7a76\u7adf\u662f\u6765\u5e72\u4ec0\u4e48\u7684\uff1f\u201d\u8521\u5d07\u9053\uff1a\u201c\u662f\u6765\u66ff\u6731\u731b\u505a\u8bf4\u5ba2\uff1f\u66ff \u4ed6\u6765\u8ddf\u6211\u8c08\u6761\u4ef6\uff1f\u8fd8\u662f\u66ff\u4ed6\u6765\u6c42\u60c5\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u770b\u7740\u4ed6\uff0c\u4e5f\u770b\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u522b\u4eba\u7684\u5fc3\u4e8b\u662f\u4e0d\u662f\u4ece\u6765\u90fd\u7792\u4e0d\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u8521\u5d07\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u201c\u5176\u5b9e\u6731\u731b\u53ef\u4ee5\u81ea\u5df1\u6765\u7684\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u4eec\u5230\u5e95\u662f\u8001\u54e5\u513f\u4eec\u4e86\uff0c\u201d\u8521\u5d07\u8bf4\u5f97\u5f88\u8bda\u6073\uff1a \u201c\u53ea\u8981\u6761\u4ef6\u4e0d\u592a\u8fc7\u4efd\uff0c\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u6211\u90fd\u53ef\u4ee5\u7167\u529e\u3002\u201d \u3000\u3000\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u771f\u7684\uff0c\u201d\u8521\u5d07\u9053\uff1a\u201c\u6211\u6839\u672c\u5c31\u4e0d\u60f3\u8ddf\u4ed6\u8fd9\u4e48\u6837\u8017\u4e0b\u53bb\uff0c\u81ea\u5df1\u7684\u5144\u5f1f\u7a9d\u91cc\u7ffb\uff0c\u5f04 \u5f97\u5927\u5bb6\u7cbe\u75b2\u529b\u7aed\uff0c\u4e24\u8d25\u4ff1\u4f24\uff0c\u8ba9\u5916\u4eba\u6765\u6361\u4fbf\u5b9c\uff0c\u8fd9\u6837\u53c8\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u201c\u786e\u5b9e\u8fde\u4e00\u70b9\u597d\u5904\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u4e0d\u59a8\u56de\u53bb\u628a\u6211\u7684\u610f\u601d\u544a\u8bc9\u4ed6\u3002\u201d\u8521\u5d07\u9053\uff1a\u201d\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u4e5f\u80fd\u770b\u5f97\u51fa\u6211\u662f\u4e00\u756a \u8bda\u610f\u3002\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u770b\u5f97\u51fa\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u89c9\u5f97\u6709\u70b9\u5947\u602a\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u5c31\u4ece\u6765\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u6211\u662f\u66ff\u6731\u731b\u6765\u6740\u4f60\u7684\uff1f\u201d \u3000\u3000\u8521\u5d07\u5fae\u7b11\uff0c\u8fde\u90a3\u53cc\u5229\u5203\u4f3c\u7684\u72ed\u773c\u4e2d\u90fd\u5145\u6ee1\u7b11\u610f\u3002 \u3000\u3000\u201c\u4f60\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u600e\u4e48\u4f1a\u505a\u8fd9\u79cd\u4e8b\uff1f\u201d\u4ed6\u8bf4\uff1a\u201c\u8fd9\u6761\u8857\u4e0a\u90fd\u662f\u6211\u7684\u4eba\uff0c\u53ea\u8981\u4f60\u4e00\u51fa\u624b\uff0c \u5c31\u662f\u80fd\u6740\u4e86\u6211\uff0c\u4f60\u81ea\u5df1\u4e5f\u5fc5\u6b7b\u65e0\u7591\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u8fd9\u4e00\u70b9\u6211\u4e5f\u770b\u5f97\u51fa\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd8\u5e74\u8f7b\uff0c\u524d\u7a0b\u5982\u9526\uff0c\u4f60\u8ddf\u6731\u731b\u53c8\u6ca1\u6709\u4ec0\u4e48\u592a\u6df1\u539a\u7684\u4ea4\u60c5\uff0c\u4e3a\u4ec0\u4e48\u8981\u66ff\u4ed6\u6765\u5356\u547d\uff1f\u201d \u8521\u5d07\u5fae\u7b11\u6447\u5934\uff1a\u201c\u4f60\u5f53\u7136\u4e0d\u4f1a\u505a\u8fd9\u79cd\u4e8b\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u4e5f\u7b11\u4e86\uff1a\u201c\u4f60\u8bf4\u5f97\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u8fd9\u79cd\u4e8b\u8fde\u5929\u4e0b\u6700\u7b28\u7684\u5927\u7b28\u86cb\u90fd\u4e0d\u4f1a\u505a\u7684\u3002\u201d \u3000\u3000\u8521\u5d07\u5927\u7b11\uff0c\u7b11\u5f97\u6109\u5feb\u6781\u4e86\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u7b11\u5f97\u6700\u6109\u5feb\u65f6\uff0c\u5ffd\u7136\u770b\u89c1\u6de1\u8c08\u7684\u9752\u5149\u4e00\u95ea\uff0c\u5df2\u7ecf\u6709\u4e00\u628a\u5229\u5251\u523a\u4eba\u4e86\u4ed6\u7684\u5fc3\u810f\u3002 \u3000\u3000\u7b11\u5bb9\u5ffd\u7136\u51bb\u7ed3\uff0c\u5c31\u50cf\u662f\u4e00\u5f20\u624b\u5de5\u6781\u62d9\u52a3\u7684\u9762\u5177\u822c\u51bb\u7ed3\u5728\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u6240\u6709\u7684\u58f0\u97f3\u548c\u884c\u52a8\u4eff\u4f5b\u4e5f\u5168\u90e8\u88ab\u51bb\u7ed3\u3002\u53ef\u662f\u5728\u4e00\u77ac\u95f4\u4e4b\u540e\uff0c\u5c31\u5ffd\u7136\u9a9a\u52a8\u6cb8 \u817e\u4e86\u8d77\u6765\uff0c\u4f7f\u5f97\u8fd9\u6761\u957f\u8857\u53d8\u5f97\u5c31\u50cf\u662f\u706b\u7089\u4e0a\u4e00\u9505\u521a\u716e\u6eda\u7684\u70ed\u7ca5\u3002 \u3000\u3000\u552f\u4e00\u80fd\u591f\u4fdd\u6301\u51b7\u9759\u7684\u4e00\u4e2a\u4eba\u8fd8\u662f\u5c0f\u9ad8\u3002 \u3000\u3000\u4ed6\u6765\u505a\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u8fd9\u4ef6\u4e8b\u662f\u4ed6\u5e94\u8be5\u505a\u7684\uff0c\u6210\u8d25\u5229\u5bb3\uff0c\u751f\u6b7b\u5b58\u4ea1\uff0c\u4ed6\u6839\u672c\u6ca1\u6709 \u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u4f7f\u547d\u5df2\u5b8c\u6210\uff0c\u5df2\u7ecf\u4eb2\u773c\u770b\u5230\u4e86\u53db\u5f92\u5f97\u5230\u5e94\u6709\u7684\u4e0b\u573a\uff0c\u522b\u7684\u4e8b\u4ed6\u5df2\u7ecf\u5b8c\u5168\u4e0d\u5728 \u4e4e\u3002 \u3000\u3000\u867d\u7136\u4ed6\u4e0d\u5728\u4e4e\uff0c\u53ef\u662f\u6709\u4eba\u5728\u4e4e\u3002 \u3000\u3000\u52a8\u4e71\u7684\u4eba\u7fa4\u8fd8\u6ca1\u6709\u6251\u8fc7\u6765\uff0c\u534a\u7a7a\u4e2d\u5ffd\u7136\u6709\u4e00\u6761\u9ad8\u5927\u7684\u4eba\u5f71\u98de\u9e1f\u822c\u5760\u4e0b\uff0c\u843d\u5728\u5c0f\u9ad8\u8eab\u8fb9\u3002 \u62c9\u4f4f\u4e86\u5c0f\u9ad8\u7684\u624b\u3002 \u3000\u3000\u201c\u4ed6\u662f\u6211\u7684\u670b\u53cb\u3002\u201d\u6731\u731b\u53c8\u53d1\u51fa\u96c4\u72ee\u822c\u7684\u6012\u543c\uff1a\u201c\u4f60\u4eec\u8981\u52a8\u4ed6\uff0c\u5c31\u5f97\u5148\u6740\u4e86\u6211\uff01\u201d \u7b2c\u4e5d\u7ae0\u3000\u8776\u821e>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u4e5d\u7ae0\u3000\u8776\u821e \u4e00 \u3000\u3000\u4e8c\u6708\u521d\u516d\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u56db\u53ea\u4fe1\u9e3d\u81ea\u6d1b\u9633\u98de\u51fa\uff0c\u6709\u4e00\u53ea\u5728\u7070\u51b7\u7684\u6697\u7a7a\u4e2d\u8ff7\u5931\u4e86\u65b9\u5411\uff0c\u6709\u4e00\u53ea\u7684\u7fc5\u8180\u88ab\u5bd2\u98ce\u7684\u51b0\u96ea \u51bb\u7ed3\uff0c\u5760\u6b7b\u5728\u5173\u6d1b\u8fb9\u5883\u7684\u7a77\u5c71\u4e2d\uff0c\u5374\u8fd8\u662f\u6709\u4e24\u53ea\u98de\u5230\u4e86\u957f\u5b89\u3002\u5728\u4e8c\u6708\u521d\u516b\u7684\u9ece\u660e\u524d\u5c31\u98de\u5230\u4e86 \u957f\u5b89\u3002 \u3000\u3000\u201c\u8521\u5d07\u5df2\u7ecf\u6b7b\u4e86\uff0c\u201d\u5353\u4e1c\u6765\u5f88\u5e72\u9759\u7684\u544a\u8bc9\u53f8\u9a6c\u8d85\u7fa4\uff0c\u201c\u6768\u575a\u6b7b\u5728\u8fd9\u91cc\u3002\u53e6\u5916\u4e24\u4e2a\u6b7b\u5728\u6211 \u4eec\u7684\u90a3\u6b21\u7a81\u88ad\u4e2d\uff0c\u6731\u731b\u624b\u4e0b\u7684\u56db\u5927\u91d1\u521a\u73b0\u5728\u5df2\u7ecf\u8fde\u4e00\u4e2a\u5269\u4e0b\u7684\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f8\u9a6c\u6b63\u5728\u4eab\u53d7\u4ed6\u7684\u70ad\u70e7\u725b\u8089\uff0c\u8fd9\u4e00\u987f\u597d\u50cf\u5df2\u6210\u4e3a\u4ed6\u4e00\u5929\u6d3b\u529b\u7684\u6765\u6e90\uff0c\u8fd9\u65f6\u5019\u4e5f\u6b63\u662f\u4ed6\u4e00 \u5929\u4e2d\u7cbe\u795e\u6700\u597d\u3001\u5934\u8111\u6700\u6e05\u9192\u7684\u65f6\u5019\u3002 \u3000\u3000\u201c\u8521\u795f\u662f\u4ec0\u4e48\u65f6\u5019\u6b7b\u7684\uff1f\u201d\u4ed6\u95ee\u5353\u4e1c\u6765\u3002 \u3000\u3000\u201c\u6628\u5929\u65e9\u4e0a\u3002\u201d\u5353\u4e1c\u6765\u56de\u8a00\uff1a\u201c\u4e00\u4e2a\u65f6\u8fb0\u524d\u6211\u624d\u63a5\u5230\u4ed6\u7684\u6b7b\u8baf\u3002\u201d \u3000\u3000\u4ed6\u5c5e\u4e0b\u6709\u4e00\u4f4d\u8bad\u7ec3\u4fe1\u9e3d\u7684\u4e13\u5bb6\uff0c\u4ed6\u6d3e\u5230\u6d1b\u9633\u53bb\u63a2\u542c\u6d88\u606f\u7684\u4eba\u901a\u5e38\u90fd\u4f1a\u5e26\u4e00\u4e24\u53ea\u4fe1\u9e3d\u53bb\u3002 \u5728\u90a3\u65f6\u4f20\u9012\u6d88\u606f\u7edd\u5bf9\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u65b9\u6cd5\u6bd4\u8fd9\u79cd\u65b9\u6cd5\u66f4\u5feb\u3002 \u3000\u3000\u201c\u6211\u597d\u50cf\u542c\u8bf4\u8521\u5d07\u5df2\u7ecf\u5b8c\u5168\u63a7\u5236\u4e86\u96c4\u72ee\u5802\uff0c\u600e\u4e48\u4f1a\u5ffd\u7136\u5c31\u6b7b\u4e86\uff1f\u201d\u53f8\u9a6c\u8c08\u8c08\u7684\u8bf4\uff0c\u201c\u4e00 \u4e2a\u50cf\u4ed6\u90a3\u6837\u7684\u4eba\uff0c\u597d\u6a61\u4e0d\u8be5\u6b7b\u5f97\u8fd9\u4e48\u5feb\u7684\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u88ab\u4e00\u67c4\u5251\u523a\u4eba\u5fc3\u53e3\uff0c\u4e0d\u7ba1\u4ec0\u4e48\u4eba\u90fd\u4f1a\u6b7b\u5f97\u5f88\u5feb\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8981\u628a\u4e00\u67c4\u5251\u523a\u4eba\u4ed6\u7684\u5fc3\u53e3\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d\u53f8\u9a6c\u95ee\uff1aw\u67c4\u5251\u662f\u8c01\u7684\u5251\u3002\u201d \u3000\u3000\u201c\u662f\u5c0f\u9ad8\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u9ad8\u6e10\u98de\u3002\u201d \u3000\u3000\u201c\u53c8\u662f\u4ed6\uff01\u201d\u53f8\u9a6c\u7528\u4ed6\u7684\u5f2f\u5200\u5272\u4e0b\u4e00\u592a\u5757\u725b\u8089\uff0c\u201c\u4ed6\u5df2\u7ecf\u5230\u4e86\u6d1b\u9633\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u524d\u5929\u624d\u5230\u3002\u201d \u3000\u3000\u53f8\u9a6c\u6162\u6162\u7684\u5480\u56bc\uff0c\u76f4\u5230\u725b\u8089\u7684\u9c9c\u9999\u5b8c\u5168\u6eb6\u5165\u4ed6\u7684\u611f\u89c9\u65f6\u624d\u5f00\u53e3\uff1a\u201c\u4ee5\u9ad8\u60ed\u98de\u7684\u5251\u672f\u8521\u5d07 \u5f53\u7136\u4e0d\u662f\u5bf9\u624b\uff0c\u53ef\u662f\u8521\u5d07\u65e2\u7136\u5df2\u63a7\u5236\u4e86\u96c4\u72ee\u5802\u3002\u8eab\u8fb9\u4e94\u5341\u6b65\u4e4b\u5185\u90fd\u5e94\u8be5\u6709\u597d\u624b\u5728\u4fdd\u62a4\u624d \u5bf9\u3002\u201d \u3000\u3000\u201c\u636e\u8bf4\u5f53\u65f6\u662f\u5728\u4e00\u6761\u8857\u4e0a\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u90a3\u65f6\u8857\u4e0a\u4e0d\u4f46\u5e03\u6ee1\u4e86\u96c4\u72ee\u5802\u7684\u5b50\u5f1f\uff0c\u800c\u4e14\u8fd8 \u6709\u5341\u6765\u4e2a\u88ab\u4ed6\u4ee5\u91cd\u4ef7\u6536\u4e70\u7684\u6740\u624b\u3002\u4ed6\u7684\u5bf9\u5934\u5982\u679c\u8981\u8d70\u4e0a\u90a3\u6761\u8857\uff0c\u7b80\u76f4\u6bd4\u4e00\u6761\u7f8a\u8d70\u5165\u72fc\u7fa4\u8fd8\u5371 \u9669\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5c0f\u9ad8\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5c0f\u9ad8\u53bb\u4e86\uff0c\u4e00\u4e2a\u4eba\u53bb\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e00\u4e2a\u4eba\uff0c\u4e00\u67c4\u5251\uff0c\u5c31\u597d\u50cf\u8001\u592a\u5a46\u63d0\u7740 \u83dc\u7bee\u5b50\u4e70\u83dc\u4e00\u6837\uff0c\u8d70\u4e0a\u4e86\u90a3\u6761\u8857\u3002\u201d \u3000\u3000\u201c\u7136\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u7136\u540e\u4ed6\u5c31\u7528\u90a3\u67c4\u5251\u523a\u5165\u4e86\u8521\u5d07\u7684\u5fc3\u53e3\uff0c\u5f80\u524d\u80f8\u523a\u8fdb\u53bb\uff0c\u540e\u80cc\u7a7f\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u8521\u5d07\u600e\u4e48\u4f1a\u8ba9\u4ed6\u8fd1\u8eab\u7684\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u5148\u4e0b\u4ee4\u51fa\u624b\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\u6211\u4e5f\u60f3\u5230\u8fc7\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u60f3\u6700\u91cd\u8981\u7684\u539f\u56e0\u662f\uff0c\u8521\u5d07\u4e0d\u4f46\u60f3\u5229\u7528\u5c0f\u9ad8\u53bb\u8bf1 \u6740\u6731\u731b\uff0c\u800c\u4e14\u4e95\u6ca1\u6709\u5341\u5206\u91cd\u89c6\u4ed6\uff0c\u4e00\u5b9a\u8ba4\u4e3a\u4ed6\u7edd\u4e0d\u6562\u5728\u90a3\u79cd\u60c5\u51b5\u4e0b\u51fa\u624b\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u8521\u5d07\u5c31\u6b7b\u5f97\u4e00\u70b9\u4e5f\u4e0d\u51a4\u6789\u4e86\uff0c\u201d\u53f8\u9a6c\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u65e0\u8bba\u8c01\u4f4e\u4f30\u4e86\u81ea\u5df1\u7684\u5bf9\u624b\u90fd\u8be5 \u6b7b\u3002\u201d \u3000\u3000\u8521\u5d07\u4e0d\u4f46\u4f4e\u4f30\u4e86\u5c0f\u9ad8\u51fa\u624b\u7684\u901f\u5ea6\u548c\u6b66\u529f\uff0c\u4e5f\u4f4e\u4f30\u4e86\u4ed6\u7684\u4eba\u683c\u548c\u52c7\u6c14\u3002 \u3000\u3000\u53f8\u9a6c\u5ffd\u7136\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u53ef\u662f\u5c0f\u9ad8\u4e00\u5b9a\u4e5f\u6b7b\u5b9a\u4e86\u3002\u4ed6\u53bb\u7684\u65f6\u5019\u4e00\u5b9a\u5c31\u5df2\u7ecf\u62b1\u7740\u5fc5\u6b7b\u4e4b \u5fc3\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u9053\uff1a\u201c\u6731\u731b\u80fd\u4ea4\u5230\u4ed6\u8fd9\u4e2a\u670b\u53cb\u771f\u662f\u8fd0\u6c14\u3002\u201d \u3000\u3000\u201c\u50cf\u8fd9\u6837\u7684\u4eba\u73b0\u5728\u7684\u786e\u5df2\u4e0d\u591a\uff0c\u6b7b\u6389\u4e00\u4e2a\u5c31\u5c11\u6389\u4e00\u4e2a\u3002\u201d\u5353\u4e1c\u6765\u8bf4\u3002\u201c\u53ef\u662f\u73b0\u5728\u8fd8\u6ca1\u6709 \u5c11\u3002\u201d \u3000\u3000\u201c\u5c0f\u9ad8\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6de1\u8c08\u7684\u8bf4\uff1a\u201c\u73b0\u5728\u4ed6\u6d3b\u5f97\u4e5f\u8bb8\u6bd4\u4e16\u4e0a\u5927\u591a\u6570\u4eba\u90fd\u6109\u5feb\u5f97\u591a\u3002\u201d \u3000\u3000\u53f8\u9a6c\u663e\u5f97\u5f88\u60ca\u8bb6\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e5f\u6ca1\u6709\u4ea4\u9519\u670b\u53cb\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6731\u731b\u5e76\u6ca1\u6709\u8ba9\u4ed6\u4e00\u4e2a\u4eba\u53bb\u62fc\u547d\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u6731\u731b\u4e5f\u8d76\u53bb\u4e86\uff1f\u201d\u53f8\u9a6c\u66f4\u60ca\u8bb6\uff1a\u201c\u4ed6\u773c\u770b\u7740\u8521\u5d07\u628a\u4ed6\u7684\u4eba\u5168\u90e8\u5e26\u8d70\uff0c\u81ea\u5df1\u5374\u50cf\u662f \u6761\u91ce\u72d7\u822c\u8eb2\u4e86\u8d77\u6765\u3002\u5728\u90a3\u79cd\u65f6\u5019\uff0c\u4ed6\u600e\u4e48\u6709\u79cd\u95ef\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\u6211\u4e5f\u4ee5\u4e3a\u4ed6\u5b8c\u4e86\uff0c\u5df2\u7ecf\u50cf\u662f\u4e2a\u9489\u9524\u4e0b\u7684\u6838\u6843\u822c\u88ab\u6211\u4eec\u628a\u4ed6\u5916\u8868\u7684\u786c\u58f3\u6572\u788e\uff0c\u5269\u4e0b \u7684\u6838\u6843\u4ec1\u8fde\u6ca1\u6709\u7259\u7684\u5b69\u5b50\u90fd\u54ac\u5f97\u52a8\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4ed6\u7684\u786c\u58f3\u662f\u4e0d\u662f\u53c8\u957f\u4e86\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u957f\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u773c\u4e2d\u5e26\u7740\u6df1\u601d\u4e4b\u9c7c\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\u4e4b\u540e\u624d\u6162\u6162\u7684\u8bf4\uff1a\u201c\u6709\u4e9b\u6811\u6728\u5728\u51ac\u5929\u770b\u6765\u597d\u50cf\u5df2 \u5b8c\u5168\u67af\u6b7b\uff0c\u53ef\u662f\u4e00\u5230\u4e86\u6625\u5929\u3002\u63a5\u53d7\u4e86\u6625\u98ce\u96e8\u6c34\u6696\u6c14\u548c\u9633\u5149\u7684\u6ecb\u6da6\u540e\uff0c\u5ffd\u7136\u53c8\u53d8\u5f97\u6709\u4e86\u751f\u673a\uff0c \u53c8\u62bd\u51fa\u4e86\u7eff\u82bd\uff0c\u957f\u51fa\u4e86\u65b0\u53f6\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4eff\u4f5b\u5f88\u9065\u8fdc\uff1a\u201c\u6709\u4e9b\u670b\u53cb\u5bf9\u4eba\u7684\u5f71\u54cd\uff0c\u5c31\u597d\u50cf\u6625\u98ce\u96e8\u6c34\u6696\u6c14\u548c\u9633\u5149\u4e00\u6837\u3002\u201d\u5353 \u4e1c\u6765\u8bf4\uff1a\u201c\u5bf9\u6731\u731b\u6765\u8bf4\uff0c\u9ad8\u6e10\u98de\u597d\u50cf\u5c31\u662f\u8fd9\u4e00\u7c7b\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8f7b\u8f7b\u7684\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4ed6\u786e\u5b9e\u662f\u7684\uff0c\u4e0d\u7ba1\u5bf9\u4ec0\u4e48\u4eba\u6765\u8bf4\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u6c89\u9ed8\uff0c\u4e00\u53cc\u72fc\u4e00\u822c\u7684\u7070\u773c\u4e2d\uff0c\u5ffd\u7136\u9732\u51fa\u79cd\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u4e86\u89e3\u4e5f\u65e0\u6cd5\u89e3\u91ca\u7684\u8868 \u60c5\uff0c\u773c\u4e2d\u7684\u950b\u8292\u4e5f\u6e10\u6e10\u9eef\u6de1\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5374\u597d\u50cf\u6ca1\u6709\u6ce8\u610f\u5230\uff0c\u53c8\u63a5\u7740\u8bf4\uff0c\u201c\u8521\u5d07\u57cb\u4f0f\u5728\u90a3\u6761\u8857\u4e0a\u7684\u4eba\uff0c\u5927\u591a\u662f\u6731\u731b\u7684\u65e7 \u90e8\uff0c\u770b\u89c1\u6731\u731b\u5ffd\u7136\u53c8\u91cd\u632f\u8d77\u6614\u65e5\u7684\u96c4\u98ce\uff0c\u4e00\u5b9a\u4f1a\u88ab\u4ed6\u7684\u6c14\u52bf\u9707\u6151\uff0c\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u4f55\u51b5\u8521\u5d07\u53c8 \u5df2\u6b7b\u5728\u5c0f\u9ad8\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u6240\u4ee5\u4ed6\u7684\u7ed3\u8bba\u662f\uff1a\u201c\u53ea\u8981\u6731\u731b\u4e00\u73b0\u8eab\uff0c\u8fd9\u4e9b\u4eba\u591a\u534a\u90fd\u4e0d\u6562\u51fa\u624b\u7684\uff0c\u56e0\u4e3a\u6731\u731b\u8fd8\u6709\u4e00\u80a1 \u6c14\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u4fdd\u6301\u6c89\u9ed8\u3002 \u3000\u3000\u53f8\u9a6c\u53c8\u8bf4\uff1a\u201c\u88ab\u8521\u5d07\u4ee5\u9ad8\u4ef7\u8058\u672a\u7684\u90a3\u4e9b\u4eba\uff0c\u5f53\u7136\u66f4\u4e0b\u4f1a\u51fa\u624b\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u662f\u6709\u4ef7\u94b1\u7684\u4eba\uff0c\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u8521\u5d07\u80fd\u6536\u4e70\u4ed6\u4eec\uff0c\u6731\u731b\u4e5f\u4e00\u6837\u80fd\u6536\u4e70\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u91cc\u5145\u6ee1\u4e0d\u5c51\uff1a\u201c\u4e00\u4e2a\u4eba\u5982\u679c\u6709\u4ef7\u94b1\uff0c\u5c31\u4e0d\u503c\u94b1\u4e86\uff0c\u8fde\u4e00\u6587\u90e8\u4e0d\u503c\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u8521\u5d07\u5fd8\u8bb0\u4e86\u8fd9\u4e24\u70b9\uff0c\u6240\u4ee5\u6731\u731b\u548c\u5c0f\u9ad8\u624d\u80fd\u6d3b\u5230\u73b0\u5728\u3002\u201d\u53f8\u9a6c\u5410\u51fa\u53e3\u6c14\uff0c\u5bf9\u81ea\u5df1 \u7684\u63a8\u8bba\u663e\u7136\u89c9\u5f97\u5f88\u6ee1\u610f\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5374\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\uff0c\u53f8\u9a6c\u5fcd\u4e0d\u4f4f\u53c8\u8981\u95ee\u4ed6\uff1a\u201c\u96be\u9053\u4f60\u8fde\u4e00\u70b9\u610f\u89c1\u90fd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u6447\u5934\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u76b1\u8d77\u7709\uff1a\u201c\u6731\u731b\u8d76\u53bb\u4e4b\u540e\uff0c\u90a3\u91cc\u96be\u9053\u8fd8\u53d1\u751f\u8fc7\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff1f\u201d\u53f8\u9a6c\u8d85\u7fa4\u51e0\u4e4e\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u53c8\u6c89\u9ed8\u5f88\u4e45\u4e4b\u540e\uff0c\u5353\u4e1c\u6765\u624d\u51b7\u51b7\u7684\u56de\u7b54\uff1a\u201c\u56e0\u4e3a\u8fd9\u4e9b\u6d88\u606f\u5e76\u4e0d\u662f\u4eba\u5e26\u6765\u7684\uff0c\u662f\u9e3d\u5b50\u5e26\u6765 \u7684\uff0c\u9e3d\u5b50\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u53ea\u80fd\u5e26\u4fe1\u6765\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u9e3d\u5b50\u4e5f\u4e0d\u662f\u8001\u9e70\u3002\u6d1b\u9633\u5230\u957f\u5b89\u7684\u8def\u9014\u4e5f\u4e0d\u8fd1\uff0c \u8981\u9e3d\u5e26\u4fe1\uff0c\u5c31\u4e0d\u80fd\u5e26\u592a\u957f\u7684\u4fe1\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u58f0\u97f3\u91cc\u5168\u65e0\u611f\u60c5\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5374\u4e00\u5b9a\u8981\u4e00\u5c01\u5f88\u957f\u7684\u4fe1\u624d\u80fd\u8bf4\u5f97\u6e05\u695a\uff0c\u6240\u4ee5\u4ed6\u4eec\u53ea \u6709\u628a\u8fd9\u5c01\u4fe1\u5206\u6210\u56db\u6bb5\uff0c\u5206\u7ed9\u56db\u53ea\u9e3d\u5b50\u5e26\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u63a5\u5230\u513f\u53ea\u9e3d\u4e8e\uff1f\u201d \u3000\u3000\u201c\u4e24\u53ea\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e24\u53ea\u9e3d\u5b50\uff0c\u4e24\u6bb5\u4fe1\u3002\u201d \u3000\u3000\u201c\u54ea\u4e24\u6bb5\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\u6bb5\u548c\u6700\u540e\u4e00\u6bb5\u3002\u201d \u3000\u3000\u201c\u521a\u624d\u4f60\u8bf4\u7684\u5f53\u7136\u662f\u7b2c\u4e00\u6bb5\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u95ee\uff1a\u201c\u6700\u540e\u4e00\u6bb5\u5462\uff1f\u201d \u3000\u3000\u201c\u6700\u540e\u4e00\u6bb5\u5df2\u7ecf\u662f\u7ed3\u5c40\u4e86\uff0c\u53ea\u5199\u4e86\u51e0\u884c\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u53ef\u4ee5\u5ff5\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u4ed6\u679c\u7136\u7acb\u523b\u5c31\u4e00\u5b57\u4e0d\u6f0f\u7684\u5ff5\u4e86\u51fa\u6765\uff1a\u201c\u8fd9\u4e00\u6218\u5171\u8ba1\u6b7b\u4e8c\u5341\u4e09\u4eba\uff0c\u91cd\u4f24\u5341\u4e5d\uff0c\u8f7b\u4f24\u5341\u4e00\uff0c \u6b7b\u4f24\u4e0d\u53ef\u8c13\u4e0d\u60e8\uff0c\u6218\u540e\u8840\u8165\u4e4b\u6c14\u4e45\u4e45\u4e0d\u6563\uff0c\u8857\u9053\u5982\u88ab\u8840\u6d17\uff0c\u552f\u6731\u731b\u4e0e\u9ad8\u6e10\u98de\u90fd\u80fd\u5e78\u5b58\u65e0 \u6059\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5ff5\u5b8c\u4e86\u5f88\u4e45\uff0c\u53f8\u9a6c\u624d\u957f\u957f\u53f9\u606f\u3002 \u3000\u3000\u201c\u6b7b\u7684\u4eba\u6bd4\u91cd\u4f24\u7684\u591a\uff0c\u91cd\u4f24\u7684\u4eba\u6bd4\u8f7b\u4f24\u7684\u591a\uff0c\u8fd9\u4e00\u6218\u7684\u60e8\u70c8\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u6ee5\uff1a\u201c\u7531\u6b64\u53ef\u89c1\uff0c\u5f53\u65f6\u5e76\u4e0d\u662f\u6ca1\u6709\u4eba\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u5f53\u65f6\u90a3\u6761\u8857\u5c31\u597d\u50cf\u4e00\u5927\u5305\u8fd8\u6ca1\u6709\u88ab\u5f15\u53d1\u7684\u706b\u836f\uff0c\u53ea\u8981\u6709\u4e00\u4e2a\u4eba\u6562\u51fa\u624b\uff0c\u8fd9\u4e2a\u4eba\u5c31\u4f1a\u53d8 \u6210\u706b\u836f\u7684\u5f15\u5b50\uff0c\u800c\u4e14\u5df2\u7ecf\u88ab\u70b9\u7740\uff0c\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u6240\u4ee5\u5f53\u65f6\u53ea\u8981\u6709\u4eba\u6562\u51fa\u624b\uff0c\u90a3\u4e00\u5927\u5305\u706b\u836f\u7acb \u523b\u5c31\u4f1a\u70b8\u8d77\u6765\uff0c\u628a\u6731\u731b\u548c\u5c0f\u9ad8\u70b8\u5f97\u7c89\u8eab\u788e\u9aa8\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5f53\u65f6\u7684\u60c5\u51b5\u786e\u5b9e\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u6731\u731b\u548c\u5c0f\u9ad8\u73b0\u5728\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4ed6\u4eec\u4e24\u4e2a\u4eba\u786e\u5b9e\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u201c\u4ee5\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e4b\u529b\uff0c\u600e\u4e48\u80fd\u62fc\u5f97\u8fc7\u90a3\u4e9b\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u662f\u4e24\u4e2a\u4eba\uff0c\u662f\u4e09\u4e2a\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4e2a\u662f\u8c01\u3002\u201d \u3000\u3000\u201c\u9489\u978b\u3002\u201d \u3000\u3000\u201c\u9489\u978b\uff1f\u201d \u3000\u3000\u201c\u9489\u978b\u5e76\u4e0d\u662f\u4e00\u53cc\u9489\u978b\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u9489\u978b\u662f\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u201c\u4ed6\u7684\u6b66\u529f\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4e0d\u600e\u4e48\u6837\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u4f60\u5374\u597d\u50cf\u5f88\u5c0a\u91cd\u4ed6\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u5353\u4e1c\u6765\u7acb\u523b\u627f\u8ba4\uff0c\u201c\u5bf9\u6709\u7528\u7684\u4eba\u6211\u4e00\u5411\u90fd\u5f88\u5c0a\u91cd\u3002\u201d \u3000\u3000\u201c\u4ed6\u6709\u7528\uff1f\u201d \u3000\u3000\u201c\u975e\u5e38\u6709\u7528\u3002\u4e5f\u8bb8\u6bd4\u6731\u731b\u95e8\u4e0b\u5176\u4ed6\u7684\u5f1f\u5b50\u52a0\u8d77\u6765\u90fd\u6709\u7528\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u968f\u65f6\u90fd\u53ef\u4ee5\u4e3a\u6731\u731b\u53bb\u6b7b\uff1f\u201d \u3000\u3000\u201c\u6b7b\u5e76\u4e0d\u662f\u4ef6\u56f0\u96be\u7684\u4e8b\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u968f\u65f6\u4e3a\u6731\u731b\u53bb\u6b7b\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u53ea\u8981\u6731\u731b\u6d3b\u7740\uff0c\u4ed6 \u4e00\u5b9a\u4e5f\u4f1a\u60f3\u6cd5\u5b50\u6d3b\u4e0b\u53bb\uff0c\u56e0\u4e3a\u4ed6\u8981\u7167\u987e\u6731\u731b\uff0c\u4ed6\u5bf9\u6731\u731b\u5c31\u597d\u50cf\u4e00\u6761\u8001\u72d7\u5bf9\u5b83\u7684\u4e3b\u4eba\u4e00\u6837\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u63a5\u7740\u9053\uff1a\u201c\u5982\u679c\u4ed6\u968f\u65f6\u90fd\u60f3\u4e3a\u6731\u731b\u53bb\u62fc\u547d\uff0c\u8fd9\u79cd\u4eba\u4e5f\u5c31\u4e0d\u503c\u5f97\u770b\u91cd\u4e86\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5ffd\u7136\u7b11\u4e86\uff0c\u5927\u7b11\uff0c\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u975e\u5e38\u660e\u767d\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u770b\u97f3\u4ed6\uff0c\u51b7\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u6bd4\u5200\u950b\u66f4\u53ef\u6015\u7684\u6124\u6012\u4e4b\u8272\uff0c\u5ffd\u800c\u8f6c\u8fc7\u8eab\uff0c\u5927\u6b65 \u8d70\u4e86\u51fa\u53bb\u3002 \u4e8c \u3000\u3000\u5929\u8272\u9634\u6697\uff0c\u7a97\u5916\u53c8\u4f20\u5165\u96ea\u82b1\u98d8\u843d\u7684\u58f0\u97f3\uff0c\u4e00\u79cd\u53ea\u6709\u5728\u4eba\u4eec\u5341\u5206\u5bc2\u5bde\u65f6\u624d\u80fd\u542c\u5f97\u5230\u7684\u58f0 \u97f3\u3002 \u3000\u3000\u53f8\u9a6c\u7684\u7b11\u58f0\u65e9\u5df2\u505c\u987f\uff0c\u773c\u4e2d\u975e\u4f46\u5168\u65e0\u7b11\u610f\uff0c\u53cd\u800c\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684\u60b2\u4f24\u3002 \u3000\u3000\u4ed6\u542c\u89c1\u4e86\u96ea\u82b1\u98d8\u843d\u7684\u58f0\u97f3\u3002\u5374\u6ca1\u6709\u542c\u89c1\u4ed6\u59bb\u5b50\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u56e0\u4e3a\u5434\u5a49\u8d70\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u7ecf\u5f00\u59cb\u5728\u559d\u9152\u3002 \u3000\u3000\u5434\u5a49\u6084\u6084\u7684\u8d70\u8fc7\u6765\uff0c\u5728\u4ed6\u8eab\u8fb9\u5750\u4e0b\u3002 \u3000\u3000\u5979\u4ece\u672a\u529d\u963b\u4ed6\u559d\u9152\uff0c\u56e0\u4e3a\u5979\u662f\u4e2a\u806a\u660e\u7684\u5973\u4eba\uff0c\u4e5f\u662f\u4e2a\u8d24\u6167\u7684\u59bb\u5b50\u3002\u5979\u77e5\u9053\u6709\u4e9b\u4e8b\u60c5\u662f\u8c01 \u90fd\u65e0\u6cd5\u529d\u963b\u7684\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4eca\u5929\u548c\u5e73\u65f6\u6709\u4e00\u70b9\u4e0d\u540c\uff0c\u4eca\u5929\u5979\u5c45\u7136\u4e5f\u5f00\u59cb\u559d\u9152\u4e86\uff0c\u800c\u4e14\u559d\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u76f4\u5230\u5979\u5f00\u59cb\u8981\u559d\u7b2c\u4e09\u676f\u7684\u65f6\u5019\uff0c\u53f8\u9a6c\u624d\u56de\u8fc7\u5934\u53bb\u770b\u770b\u5979\u3002 \u3000\u3000\u201c\u73b0\u5728\u597d\u50cf\u8fd8\u662f\u65e9\u4e0a\u3002\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u597d\u50cf\u5df2\u7ecf\u5f00\u59cb\u5728\u559d\u9152\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d\u5434\u5a49\u8f7b\u8f7b\u7684\u56de\u7b54\u3002 \u3000\u3000\u5979\u662f\u4e2a\u6e29\u67d4\u7684\u59bb\u5b50\uff0c\u975e\u5e38\u975e\u5e38\u6e29\u67d4\uff0c\u5bf9\u5979\u7684\u4e08\u592b\u4e00\u5411\u5343\u4f9d\u767e\u987a\uff0c\u5c31\u7b97\u5728\u5fc3\u91cc\u6700\u96be\u53d7\u6700\u751f \u6c14\u7684\u65f6\u5019\uff0c\u8bf4\u8bdd\u4e5f\u662f\u8f7b\u58f0\u7ec6\u8bed\uff0c\u4ece\u6765\u6ca1\u6709\u53d1\u8fc7\u813e\u6c14\u3002 \u3000\u3000\u53ef\u662f\u53f8\u9a6c\u8d85\u7fa4\u77e5\u9053\uff1a\u201c\u4f60\u53ea\u6709\u5728\u751f\u6c14\u7684\u65f6\u5019\u624d\u4f1a\u4e00\u5927\u65e9\u5c31\u5f00\u59cb\u559d\u9152\u3002\u201d\u4ed6\u95ee\u4ed6\u7684\u59bb\u5b50\uff1a \u201c\u4eca\u5929\u4f60\u4e3a\u4ec0\u4e48\u751f\u6c14\uff1f\u201d \u3000\u3000\u5434\u5a49\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u5979\u5728\u9ed8\u9ed8\u7684\u659f\u9152\uff0c\u4e3a\u5979\u7684\u4e08\u592b\u548c\u5979\u81ea\u5df1\u90fd\u6ee1\u6ee1\u7684\u659f\u4e86\u4e00\u676f\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e3a\u4e86\u4ec0\u4e48\u751f\u6c14\uff0c\u4f60\u662f\u4e3a\u4e86\u5353\u4e1c\u6765\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u4f60\u770b\u4e0d\u60ef\u4ed6\u5bf9\u6211\u8bf4\u8bdd\u7684\u90a3 \u79cd\u6837\u5b50\uff1f\u201d \u3000\u3000\u5434\u5a49\u6c89\u9ed8\uff0c\u9ed8\u8ba4\u3002 \u3000\u3000\u201c\u53ef\u662f\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u4ed6\u5e73\u65f6\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u4eca\u5929\u4ed6\u4e5f\u5728\u751f\u6c14\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u56e0\u4e3a\u4eca\u5929\u6211 \u4e00\u76f4\u5728\u4ed6\u9762\u524d\u5938\u8d5e\u5c0f\u9ad8\u3002\u201d \u3000\u3000\u4ed6\u773c\u4e2d\u5ffd\u7136\u53c8\u9732\u51fa\u5145\u6ee1\u8ba5\u6d88\u7684\u7b11\u610f\uff1a\u201c\u4ed6\u4e00\u5411\u4e0d\u559c\u6b22\u6211\u5728\u4ed6\u9762\u524d\u5938\u8d5e\u522b\u4eba\u662f\u4e2a\u597d\u670b \u53cb\u3002\u201d \u3000\u3000\u5434\u5a49\u5c45\u7136\u5f00\u53e3\u4e86\u3002 \u3000\u3000\u201c\u96be\u9053\u4ed6\u662f\u5728\u5403\u918b\uff1f\u201d\u5979\u7684\u58f0\u97f3\u5ffd\u7136\u63d0\u9ad8\u4e86\u4e9b\uff0c\u800c\u4e14\u4e5f\u5145\u6ee1\u4e86\u8ba5\u8bee\uff1a\u201c\u8fde\u6211\u90fd\u6ca1\u6709\u5403 \u918b\uff0c\u4ed6\u51ed\u4ec0\u4e48\u5403\u918b\uff1f\u201d \u3000\u3000\u5434\u5a49\u4e00\u5411\u6e29\u67d4\uff0c\u975e\u5e38\u6e29\u67d4\uff0c\u53ef\u662f\u73b0\u5728\u5979\u5df2\u7ecf\u559d\u4e86\u4e94\u676f\u9152\u3002 \u3000\u3000\u5979\u559d\u7684\u662f\u53f8\u9a6c\u5e73\u65f6\u6700\u5e38\u559d\u7684\u9152\uff0c\u53f8\u9a6c\u5e73\u65f6\u559d\u7684\u90fd\u662f\u70c8\u9152\uff0c\u6700\u70c8\u7684\u9152\u3002 \u3000\u3000\u4e00\u4e2a\u5e73\u65f6\u5f88\u5c11\u559d\u9152\u7684\u5973\u4eba\uff0c\u5ffd\u7136\u4e00\u4e0b\u5b50\u559d\u4e0b\u4e86\u4e94\u676f\u70c8\u9152\u4e4b\u540e\uff0c\u4e0d\u7ba1\u8bf4\u51fa\u4ec0\u4e48\u6837\u7684\u8bdd\u6765\uff0c \u90fd\u662f\u503c\u5f97\u539f\u8c05\u7684\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u5e73\u65f6\u5f88\u5c11\u559d\u9152\u7684\u7537\u4eba\u5ffd\u7136\u559d\u4e0b\u4e94\u676f\u70c8\u9152\uff0c\u8bf4\u51fa\u6765\u7684\u8bdd\u4e5f\u540c\u6837\u503c\u5f97\u539f\u8c05\u3002 \u3000\u3000\u6240\u4ee5\u53f8\u9a6c\u7b11\u4e86\u3002 \u3000\u3000\u201c\u4f60\u672c\u6765\u5c31\u662f\u5728\u5403\u918b\uff0c\u4f60\u4e00\u76f4\u90fd\u5728\u5403\u5353\u4e1c\u6765\u7684\u918b\uff0c\u5c31\u597d\u50cf\u6211\u4f1a\u628a\u4ed6\u5f53\u4f5c\u5973\u4eba\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u628a\u4ed6\u5f53\u4f5c\u5973\u4eba\u7684\uff0c\u4ed6\u4e5f\u6ca1\u6709\u628a\u4f60\u5f53\u4f5c\u5973\u4eba\u3002\u201d\u5434\u5a49\u53c8\u559d\u4e86\u4e00\u676f\uff1a\u201c\u4ed6\u4e00 \u76f4\u90fd\u628a\u4f60\u5f53\u4f5c\u4ed6\u7684\u513f\u5b50\uff0c\u5982\u679c\u6ca1\u6709\u4ed6\uff0c\u4f60\u6839\u672c\u5c31\u6ca1\u6709\u4eca\u5929\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5df2\u5636\u54d1\uff0c\u5979\u5636\u58f0\u95ee\u5979\u7684\u5927\u592b\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u81ea\u5df1\u53bb\u505a\u4e00\u70b9\u4e8b\uff0c\u8ba9\u4ed6\u77e5\u9053\u6ca1\u6709 \u4ed6\u4f60\u4e5f\u4e00\u6837\u6d3b\u5f97\u4e0b\u53bb\uff1f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8bc1\u660e\u7ed9\u4ed6\u770b\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u4e5f\u548c\u4ed6\u7684\u59bb\u5b50\u4e00\u6837\uff0c\u5728\u9ed8\u9ed8\u7684\u659f\u9152\uff0c\u4e3a\u4ed6\u81ea\u5df1\u548c\u4ed6\u7684\u59bb\u90fd\u659f\u4e86\u4e00\u676f\u3002 \u3000\u3000\u53ef\u662f\u5434\u5a49\u6ca1\u6709\u518d\u559d\u8fd9\u4e00\u676f\u3002\u5979\u5df2\u7ecf\u5012\u5728\u4ed6\u7684\u6000\u91cc\uff0c\u5931\u58f0\u5730\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u53f8\u9a6c\u6ca1\u6709\u54ed\uff0c\u773c\u775b\u91cc\u751a\u81f3\u8fde\u4e00\u70b9\u6cea\u5149\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u5df2\u7ecf\u6ca1\u6709\u773c\u6cea\u3002 \u4e09 \u3000\u3000\u5728\u8fd9\u4e2a\u5efa\u7b51\u5b8f\u4f1f\u7684\u5e84\u9662\u91cc\uff0c\u5bbd\u9614\u534e\u7f8e\u7684\u5ead\u56ed\u4e2d\uff0c\u6709\u4e00\u4e2a\u5e7d\u50fb\u7684\u89d2\u843d\uff0c\u89d2\u843d\u91cc\u6709\u4e00\u6247\u5f88\u7a84 \u7684\u95e8\u3002\u95e8\u540e\u5076\u800c\u4f1a\u4f20\u51fa\u4e00\u4e24\u6bb5\u60a0\u626c\u7684\u7434\u58f0\u3002\u53ef\u662f\u8c01\u4e5f\u4e0d\u77e5\u9053\u95e8\u5916\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u8c01\u4e5f\u6ca1\u6709\u89c1\u5230 \u8fc7\u90a3\u4f4d\u5f39\u7434\u7684\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u91cc\u662f\u5353\u4e1c\u6765\u5212\u4e0b\u7684\u7981\u533a\uff0c\u5982\u679c\u6709\u4eba\u6562\u8e0f\u4eba\u7981\u533a\u4e00\u6b65\uff0c\u4ed6\u7684\u5de6\u811a\u5148\u8e0f\u8fdb\u6765\uff0c\u5c31\u780d\u65ad \u4ed6\u7684\u5de6\u811a\uff0c\u53f3\u811a\u5148\u8e0f\u5165\u5c31\u780d\u65ad\u53f3\u811a\u3002 \u3000\u3000\u8fd9\u662f\u6761\u975e\u5e38\u7b80\u5355\u7684\u6cd5\u4ee4\uff0c\u7b80\u5355\u800c\u6709\u6548\u3002 \u3000\u3000\u4e0d\u7ba1\u662f\u4ece\u53f8\u9a6c\u7684\u5c45\u5904\u8fd8\u662f\u4ece\u5353\u4e1c\u6765\u7684\u5c0f\u5c4b\u8d70\u5230\u8fd9\u91cc\u6765\uff0c\u90fd\u8981\u8d70\u5f88\u957f\u7684\u4e00\u6bb5\u8def\u3002 \u3000\u3000\u5353\u4e1c\u6765\u6491\u7740\u628a\u6cb9\u7eb8\u4f1e\uff0c\u5192\u7740\u96ea\u7a7f\u8fc7\u5ead\u56ed\uff0c\u4ed6\u8d70\u5728\u79ef\u96ea\u7684\u5c0f\u5f84\u4e0a\u65f6\uff0c\u867d\u7136\u6ca1\u6709\u65bd\u5c55\u8f7b\u529f\uff0c \u96ea\u5730\u4e0a\u4e5f\u53ea\u4e0d\u8fc7\u7559\u4e0b\u4e00\u70b9\u6d45\u6d45\u7684\u811a\u5370\u3002 \u3000\u3000\u89d2\u843d\u91cc\u7684\u7a84\u95e8\u7ec8\u5e74\u5e38\u95ed\u3002 \u3000\u3000\u5353\u4e1c\u6765\u8f7b\u8f7b\u6572\u95e8\uff0c\u5149\u6572\u4e09\u58f0\uff0c\u518d\u6572\u4e00\u54cd\uff0c\u53c8\u7b49\u4e86\u5f88\u4e45\u4e4b\u5507\uff0c\u7a84\u95e8\u624d\u5f00\u4e86\u4e00\u7ebf\u3002 \u3000\u3000\u5f00\u95e8\u7684\u662f\u4e2a\u6781\u7f8e\u7684\u5973\u4eba\uff0c\u7a7f\u7740\u4ef6\u96ea\u767d\u7684\u94f6\u72d0\u6597\u7bf7\uff0c\u8138\u8272\u4e5f\u597d\u50cf\u5979\u7684\u6597\u7bf7\u4e00\u6837\u3002 \u3000\u3000\u5353\u4e1c\u6765\u538b\u4f4e\u58f0\u97f3\uff0c\u5f88\u606d\u656c\u7684\u95ee\uff1a\u201c\u8001\u5148\u751f\u8d77\u6765\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u65e9\u5c31\u8d77\u6765\u4e86\u3002\u201d\u8fd9\u4e2a\u5973\u4eba\u8bf4\uff1a\u201c\u8001\u5e74\u4eba\u603b\u662f\u8d77\u5f97\u7279\u522b\u65e9\u7684\uff0c\u201d\u5979\u5e7d\u5e7d\u7684\u8bf4\uff1a\u201c\u4e5f\u8bb8\u4ed6 \u4eec\u77e5\u9053\u6765\u65e5\u5df2\u65e0\u591a\uff0c\u6240\u4ee5\u5bf9\u6bcf\u4e00\u5929\u90fd\u7279\u522b\u73cd\u60dc\u3002\u201d \u3000\u3000\u95e8\u540e\u662f\u4e2a\u5e7d\u9759\u7684\u5c0f\u9662\uff0c\u5bd2\u98ce\u4e2d\u5145\u6ee1\u4e86\u6c81\u4eba\u5fc3\u813e\u7684\u6885\u9999\uff0c\u4e00\u682a\u5f62\u72b6\u53e4\u62d9\u7684\u8001\u677e\u4e0b\uff0c\u6709\u4e00\u4e2a \u5c0f\u5c0f\u7684\u516d\u89d2\u4ead\uff0c\u4e00\u4e2a\u8001\u4eba\u5750\u5728\u4ead\u5b50\u91cc\uff0c\u770b\u7740\u5916\u9762\u7684\u96ea\u82b1\u4e00\u7247\u7247\u98d8\u843d\uff0c\u4eff\u4f5b\u5df2\u7ecf\u770b\u5f97\u51fa\u795e\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u5e74\u7eaa\u548c\u59d3\u540d\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u5df2\u7ecf\u5fd8\u8bb0\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u67af\u7626\u800c\u77ee\u5c0f\uff0c\u8fdc\u8fdc\u770b\u8fc7\u53bb\u5c31\u50cf\u662f\u4e2a\u516b\u4e5d\u5c81\u7684\u5b69\u5b50\uff0c\u4ed6\u7684\u5934\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u98ce\u5e72\u4e86 \u7684\u786c\u58f3\u679c\uff0c\u8138\u4e0a\u523b\u6ee1\u4e86\u98ce\u971c\u96e8\u9732\u548c\u65e0\u6570\u6b21\u75db\u82e6\u7ecf\u9a8c\u7559\u4e0b\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u65e0\u60c5\u7684\u5c81\u6708\u867d\u7136\u5df2\u4f7f\u4ed6\u7684\u8eab\u4f53\u5b8c\u5168\u840e\u7f29\uff0c\u53ef\u662f\u4ed6\u7684\u4e00\u53cc\u773c\u775b\u91cc\u5374\u8fd8\u662f\u65f6\u5e38\u4f1a\u95ea\u52a8\u8d77\u4e00\u79cd \u5145\u6ee1\u4e86\u8001\u4eba\u7684\u667a\u6167\u548c\u5b69\u5b50\u822c\u8c03\u76ae\u7684\u5149\u8292\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u7684\u773c\u775b\u770b\u6765\u5c31\u597d\u50cf\u662f\u9633\u5149\u7167\u8000\u4e0b\u7684\u6d77\u6d0b\u3002 \u3000\u3000\u5353\u4e1c\u6765\u606d\u606d\u656c\u656c\u7684\u7ad9\u5728\u5c0f\u4ead\u5916\uff0c\u606d\u606d\u656c\u656c\u7684\u884c\u793c\u95ee\u597d\uff1a\u201c\u8001\u5148\u751f\u7684\u6c14\u8272\u770b\u6765\u6bd4\u6211\u4e0a\u6b21\u6765 \u7684\u65f6\u5019\u597d\u5f97\u591a\u4e86\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u5e74\u8f7b\u4e86\u4e8c\u5341\u5c81\u3002\u201d \u3000\u3000\u8001\u4eba\u672c\u6765\u597d\u50cf\u6839\u672c\u6ca1\u6709\u770b\u89c1\u4ed6\uff0c\u4e5f\u4e0d\u51c6\u5907\u7406\u4ed6\uff0c\u5374\u53c8\u5ffd\u7136\u8f6c\u8fc7\u5934\u3002\u5bf9\u4ed6\u970e\u4e86\u970e\u773c\u3002 \u3000\u3000\u201c\u4f60\u770b\u6211\u771f\u7684\u597d\u50cf\u5e74\u8f7b\u4e86\u4e8c\u5341\u5c81\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5c31\u662f\u4e2a\u778e\u5b50\uff0c\u53c8\u8822\u53c8\u7b28\u7684\u778e\u5b50\u3002\u201d\u8001\u4eba\u867d\u7136\u5728\u9a82\u4eba\uff0c\u58f0\u97f3\u5374\u663e\u5f97\u5f88\u6109\u5feb\uff1a\u201c\u4f60 \u96be\u9053\u770b\u4e0d\u51fa\u6211\u5df2\u7ecf\u5e74\u8f7b\u4e86\u56db\u5341\u5c81\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u7b11\u4e86\u3002 \u3000\u3000\u4e00\u8eab\u96ea\u81ea\u7684\u5973\u4eba\u5df2\u7ecf\u7ad9\u5728\u8001\u4eba\u8eab\u8fb9\uff0c\u8001\u4eba\u62c9\u8d77\u5979\u7684\u624b\uff0c\u7528\u4e24\u53ea\u624b\u6367\u7740\u3002 \u3000\u3000\u201c\u8fd9\u662f\u5979\u7684\u529f\u52b3\u3002\u201d\u8001\u4eba\u772f\u8d77\u773c\u7b11\u9053\uff1a\u201c\u53ea\u6709\u50cf\u5979\u8fd9\u4e48\u5e74\u8f7b\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u624d\u80fd\u4f7f\u4e00\u4e2a \u8001\u5934\u5b50\u53d8\u5f97\u5e74\u8f7b\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e5f\u662f\u6211\u7684\u529f\u52b3\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u662f\u6211\u628a\u5979\u9001\u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4e00\u70b9\u90fd\u4e0d\u611f\u6fc0\u4f60\uff0c\u201d\u8001\u4eba\u53c8\u5728\u970e\u7740\u773c\uff0c\u773c\u4e2d\u95ea\u52a8\u7740\u8c03\u76ae\u800c\u72e1\u8c32\u7684\u5149\u8292\uff1a\u201c\u6211\u77e5 \u9053\u4f60\u53c8\u5728\u62cd\u6211\u7684\u9a6c\u5c41\uff0c\u53c8\u60f3\u628a\u6211\u5b58\u5728\u8111\u5b50\u91cc\u7684\u4e1c\u897f\u6316\u51fa\u6765\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5e76\u4e0d\u5426\u8ba4\uff0c\u8001\u4eba\u95ee\u4ed6\uff1a\u201c\u8fd9\u6b21\u4f60\u60f3\u6316\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u8427\u6cea\u8840\u3002\u201d \u3000\u3000\u8001\u4eba\u8138\u4e0a\u7684\u7b11\u5bb9\u5ffd\u7136\u6d88\u5931\u4e86\uff0c\u8fde\u4e00\u53cc\u53d1\u4eae\u7684\u773c\u775b\u90fd\u53d8\u6210\u4e86\u6b7b\u7070\u8272\u3002 \u3000\u3000\u201c\u8427\u6cea\u8840\uff0c\u8427\u6cea\u8840\uff0c\u201d\u8001\u4eba\u5634\u91cc\u4e0d\u505c\u7684\u5ff5\u7740\u8fd9\u4e2a\u540d\u5b57\uff1a\u201c\u4ed6\u8fd8\u6d3b\u7740\uff1f\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u6709\uff01\u201d \u3000\u3000\u8001\u4eba\u957f\u957f\u53f9\u606f\uff0c\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u4f60\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u4e86\u3002\u201d\u4ed6\u4f38\u51fa\u4e00\u6839\u4e8e\u762a\u7684\u624b\u6307\uff0c\u6307\u7740 \u5353\u4e1c\u6765\u7684\u9f3b\u5b50\uff1a\u201c\u4f60\u662f\u4e2a\u8d85\u7ea7\u5927\u6df7\u86cb\uff0c\u53c8\u6df7\u53c8\u8822\u53c8\u7b28\uff0c\u6240\u4ee5\u4f60\u624d\u4f1a\u53bb\u60f9\u4ed6\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6ca1\u6709\u751f\u6c14\u3002 \u3000\u3000\u4e0d\u7ba1\u8fd9\u4e2a\u8001\u4eba\u600e\u4e48\u6837\u5bf9\u4ed6\uff0c\u4ed6\u597d\u50cf\u90fd\u4e0b\u4f1a\u751f\u6c14\uff0c\u56e0\u4e3a\u53ea\u6709\u8fd9\u4e2a\u8001\u4eba\u624d\u80fd\u544a\u8bc9\u4ed6\u4e00\u4e9b\u4ed6\u5f88 \u60f3\u77e5\u9053\u5374\u504f\u504f\u4e0d\u77e5\u9053\u7684\u4e8b\u3002 \u3000\u3000\u201c\u6211\u5e76\u4e0d\u60f3\u60f9\u4ed6\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u53ea\u60f3\u77e5\u9053\u6709\u5173\u4ed6\u7684\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u54ea\u4e24\u4ef6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7684\u6b66\u529f\uff0c\u4ed6\u7684\u6b66\u5668\u3002\u201d \u3000\u3000\u8001\u4eba\u597d\u50cf\u5ffd\u7136\u7d27\u5f20\u8d77\u6765\uff0c\u4e00\u4e2a\u50cf\u4ed6\u8fd9\u79cd\u5e74\u7eaa\u7684\u8001\u4eba\u672c\u6765\u4e0d\u8be5\u8fd9\u4e48\u7d27\u5f20\u7684\u3002 \u3000\u3000\u201c\u4f60\u770b\u89c1\u8fc7\u4ed6\u7528\u7684\u6b66\u5668\uff1f\u201d\u4ed6\u95ee\u5353\u4e1c\u6765\u3002 \u3000\u3000\u201c\u6211\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u6ca1\u6709\u770b\u89c1\u8fc7\uff0c\u201d\u8001\u4eba\u53c8\u653e\u677e\u4e86\uff1a\u201c\u53ea\u6709\u6b7b\u5728\u5730\u72f1\u91cc\u7684\u9b3c\u9b42\u624d\u770b\u89c1\u8fc7\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u4eba\u89c1\u8fc7\u4ed6\u7684\u6b66\u5668\uff1f\u201d \u3000\u3000\u201c\u7edd\u5bf9\u6ca1\u6709\uff0c\u201d\u8001\u4eba\u8bf4\uff1a\u201c\u5c31\u597d\u50cf\u4ed6\u4e5f\u6c38\u8fdc\u4e0d\u80fd\u770b\u89c1\u6cea\u75d5\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u6cea\u75d5\uff1f\u201d\u5353\u4e1c\u6765\u95ee\uff0c\u201c\u8c01\u662f\u6cea\u75d5\uff1f\u201d \u3000\u3000\u201c\u8427\u5927\u5e08\u7684\u6cea\u75d5\u3002\u201d \u3000\u3000\u201c\u8427\u5927\u5e08\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8427\u5927\u5e08\u5c31\u662f\u8427\u6cea\u75d5\u7684\u7236\u4eb2\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u4e00\u5411\u8ba4\u4e3a\u81ea\u5df1\u662f\u4e2a\u975e\u5e38\u660e\u667a\u7684\u4eba\uff0c\u73b0\u5728\u5374\u5b8c\u5168\u6df7\u4e71\u4e86\u3002 \u3000\u3000\u8001\u4eba\u8bf4\u7684\u8bdd\u4ed6\u5c45\u7136\u5b8c\u5168\u4e0d\u61c2\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u770b\u89c1\u4ed6\u7236\u4eb2\u7684\u6cea\u75d5\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u770b\u5230\u6cea\u75d5\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u8981\u6b7b\u5728\u6cea\u75d5\u4e0b\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u66f4\u4e0d\u61c2\uff1a\u201c\u6cea\u75d5\u4e5f\u80fd\u6740\u4eba\uff1f\u201d \u3000\u3000\u8001\u4eba\u9065\u671b\u7740\u8fdc\u65b9\uff0c\u773c\u4e2d\u4eff\u4f5b\u5145\u6ee1\u4e86\u60b2\u4f24\u548c\u6050\u60e7\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u4eba\u5ffd\u7136\u770b\u5230\u4e86\u4e00\u4ef6\u4ed6\u6240\u65e0\u6cd5 \u7406\u89e3\u4e5f\u65e0\u6cd5\u63a7\u5236\u7684\u4e8b\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u624d\u6162\u6162\u7684\u4f38\u51fa\u4e86\u4ed6\u90a3\u53cc\u5df2\u5e72\u762a\u840e\u7f29\u7684\u624b\uff0c\u8f7b\u8f7b\u7684\u62e8\u529f\u4e86\u4ed6\u9762\u524d\u7ea6\u4e00 \u5f20\u7434\u3002 \u3000\u3000\u201c\u94ee\u742e\u201d\u4e00\u58f0\uff0c\u7434\u5f26\u54cd\u52a8\u3002 \u3000\u3000\u8001\u4eba\u5ffd\u7136\u8bf4\uff1a\u201c\u8776\u821e\uff0c\u8bf7\u4f60\u4e3a\u6211\u4e00\u821e\u3002\u201d \u3000\u3000\u94f6\u72d0\u6597\u7bf7\u4ece\u80a9\u4e0a\u6ed1\u843d\uff0c\u7a7f\u4e00\u8eab\u94f6\u767d\u7684\u5973\u4eba\u4ecd\u7136\u4e00\u8eab\u94f6\u767d\u3002 \u3000\u3000\u94a5\u767d\u7684\u77ed\u8902\uff0c\u94f6\u767d\u7684\u957f\u88d9\u3002 \u3000\u3000\u957f\u88d9\u6d41\u6c34\u822c\u98d8\u52a8\uff0c\u8776\u821e\u7fe9\u7136\u800c\u821e\uff0c\u957f\u88d9\u98de\u4e91\u822c\u5377\u8d77\uff0c\u9732\u51fa\u4e86\u4e00\u53cc\u4fee\u957f\u7ed3\u5b9e\u7f8e\u4e3d\u5145\u6ee1\u4e86\u5f39 \u6027\u7684\u817f\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u5979\u7684\u821e\u59ff\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u5979\u7684\u8fd9\u53cc\u817f\u3002 \u3000\u3000\u5c31\u8fde\u6700\u61c2\u5f97\u6b23\u8d4f\u5973\u4eba\u7684\u72c4\u5c0f\u4faf\u72c4\u9752\u9e9f\u4e5f\u53ea\u80fd\u8bf4\uff1a\u201c\u6211\u7b80\u76f4\u4e0d\u80fd\u76f8\u4fe1\u4e00\u4e2a\u4eba\u8eab\u4e0a\u4f1a\u957f\u51fa\u8fd9 \u4e48\u6837\u4e00\u53cc\u817f\u6765\u3002\u201d \u3000\u3000\u60a0\u626c\u7684\u7434\u58f0\u5ffd\u7136\u53d8\u5f97\u82cd\u90c1\u800c\u8427\u7d22\uff0c\u821e\u8005\u7684\u821e\u59ff\u4e5f\u53d8\u5f97\u4eff\u4f5b\u6b8b\u79cb\u65f6\u72b9\u5728\u79cb\u98ce\u4e2d\u5377\u821e\u7684\u6700\u540e \u4e00\u7247\u843d\u65f6\uff0c\u7f8e\u5f97\u90a3\u4e48\u51c4\u51c9\uff0c\u7f8e\u5f97\u4ee4\u4eba\u5fc3\u788e\u3002 \u3000\u3000\u8001\u4eba\u773c\u4e2d\u5ffd\u7136\u6709\u4e86\u6cea\u5149\u3002 \u3000\u3000\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u7434\u5f26\u65ad\u4e86\uff0c\u7434\u58f0\u505c\u4e86\uff0c\u821e\u8005\u7684\u957f\u88d9\u6d41\u4e91\u822c\u98d8\u843d\u3002 \u3000\u3000\u821e\u8005\u7684\u4eba\u4e5f\u8737\u4f0f\u5728\u5730\u4e0a\uff0c\u5c31\u597d\u50cf\u4e00\u53ea\u5927\u9e45\u5728\u5782\u6b7b\u4e2d\u6162\u6162\u6d88\u6c89\u4e8e\u84dd\u5929\u78a7\u6d77\u95f4\u3002 \u3000\u3000\u7136\u540e\u5c31\u662f\u4e00\u7247\u5b89\u8be6\u800c\u548c\u8c10\u7684\u9759\u5bc2\u3002\u90a3\u4e48\u9759\uff0c\u90a3\u4e48\u7f8e\u3002 \u3000\u3000\u8001\u4eba\u773c\u4e2d\u5df2\u6709\u4e00\u6ef4\u6cea\u73e0\u73cd\u73e0\u822c\u6d41\u4e86\u4e0b\u6765\uff0c\u5728\u4ed6\u82cd\u8001\u67af\u7626\u5e72\u762a\u7684\u8138\u4e0a\u7559\u4e0b\u4e00\u9053\u6e05\u4eae\u7684\u6cea \u75d5\u3002 \u3000\u3000\u4e00\u6ef4\uff0c\u4e24\u6ef4\u2026\u2026 \u3000\u3000\u201c\u6cea\u75d5\u5c31\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d\u8001\u4eba\u5583\u5583\u9053\uff0c\u201c\u6cea\u75d5\u5c31\u662f\u8fd9\u6837\u5b50\u7684\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u201c\u72ec\u4e00\u65e0\u4e8c\uff0c\u5b8c\u7f8e\u65e0\u7f3a\u3002\u201d\u8001\u4eba\u8bf4\uff1a\u201c\u5f53\u4e16\u72b9\u5728\u4eba\u95f4\u7684\u5229\u5668\uff0c\u7edd\u5bf9\u6ca1\u6709\u4e00\u67c4\u5251\u6bd4\u5b83\u66f4 \u5229\uff01\u201d \u3000\u3000\u201c\u5251\uff0c\u201d\u5353\u4e1c\u6765\u95ee\uff0c\u201c\u6cea\u75d5\u662f\u4e00\u67c4\u5251\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u67c4\u5251\u3002\u201d\u8001\u4eba\u8bf4\uff1a\u201c\u4e00\u67c4\u5b8c\u7f8e\u65e0\u7f3a\u7684\u5251\uff0c\u5c31\u50cf\u662f\u8776\u821e\u7684\u821e\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u8fd9\u67c4\u5251\u4e3a\u4ec0\u4e48\u8981\u53eb\u505a\u6cea\u75d5\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5251\u4e0a\u6709\u6cea\u75d5\u3002\u201d\u8001\u4eba\u8bf4\uff1a\u201c\u5b9d\u5251\u51fa\u7089\u65f6\uff0e\u82e5\u662f\u6709\u773c\u6cea\u6ef4\u5728\u5251\u4e0a\u3002\u5c31\u4f1a\u7559\u4e0b\u6c38\u8fdc\u65e0 \u6cd5\u78e8\u706d\u7684\u6cea\u75d5\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u7684\u6cea\u75d5\uff1f\u201d \u3000\u3000\u201c\u662f\u8427\u5927\u5e08\u7684\uff0c\u201d\u8001\u4eba\u8bf4\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u72ec\u4e00\u65e0\u4e8c\u7684\u8427\u5927\u5e08\u3002\u201d \u3000\u3000\u201c\u5b9d\u5251\u521d\u51fa\uff0c\u795e\u9b3c\u7686\u5fcc\uff0c\u8fd9\u4e00\u70b9\u6211\u4e5f\u660e\u81ea\u3002\u201d\u5353\u4e1c\u6765\u9053\uff1a\u201c\u53ef\u662f\u6211\u4e0d\u61c2\u8427\u5927\u5e08\u81ea\u5df1\u4e3a\u4ec0 \u4e48\u4e5f\u8981\u4e3a\u5b83\u6d41\u6cea\u5462\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u5584\u4e8e\u94f8\u5251\uff0c\u76f8\u5251\u4e4b\u672f\u4e5f\u65e0\u4eba\u6240\u53ca\uff0c\u201d\u8001\u4eba\u58f0\u97f3\u4e2d\u5145\u6ee1\u54c0\u4f24\uff1a\u201c\u5251\u4e00\u51fa\u7089\uff0c \u4ed6\u5df2\u4ece\u5251\u4e0a\u770b\u51fa\u4e00\u79cd\u65e0\u6cd5\u5316\u89e3\u7684\u51f6\u5146\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u51f6\u5146\uff1f\u201d \u3000\u3000\u8001\u4eba\u957f\u957f\u53f9\u606f\uff1a\u201c\u4f60\u81ea\u5df1\u521a\u624d\u4e5f\u8bf4\u8fc7\uff0c\u5b9d\u5251\u51fa\u4e16\uff0c\u795e\u9b3c\u5171\u5fcc\uff0c\u8fd9\u67c4\u5251\u4e00\u51fa\u7089\uff0c\u5c31\u5e26\u7740\u9b3c \u795e\u7684\u8bc5\u5492\u548c\u5929\u5730\u7684\u623e\u6c14\uff0c\u4e0d\u4f46\u51fa\u9798\u5fc5\u5b9a\u4f24\u4eba\uff1a\u800c\u4e14\u8fd8\u8981\u628a\u8427\u5927\u5e08\u8eab\u8fb9\u4e00\u4e2a\u6700\u4eb2\u8fd1\u7684\u4eba\u4f5c\u4e3a\u796d \u793c\u3002\u201d \u3000\u3000\u201c\u8427\u5927\u5e08\u6700\u4eb2\u8fd1\u7684\u4eba\u5c31\u662f\u8427\u6cea\u8840\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d\u8001\u4eba\u9eef\u7136\u9053\uff1a\u201c\u8fd9\u67c4\u5251\u51fa\u7089\u65f6\uff0c\u8427\u5927\u5e08\u5c31\u5df2\u770b\u51fa\u4ed6\u7684\u72ec\u751f\u5b50\u8981\u6b7b\u5728\u8fd9\u67c4\u5251 \u4e0b\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u6bc1\u4e86\u8fd9\u67c4\u5251\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u5fcd\uff0c\u4e5f\u4e0d\u6562\u3002\u201d \u3000\u3000\u201c\u8fd9\u67c4\u5251\u662f\u4ed6\u81ea\u5df1\u7684\u5fc3\u8840\u7ed3\u6676\uff0c\u4ed6\u5f53\u7136\u4e0d\u5fcd\u4e0b\u624b\u53bb\u6bc1\u4e86\u5b83\u3002\u201d\u8fd9\u4e00\u70b9\u5353\u4e1c\u6765\u4e5f\u80fd\u4e86\u89e3\uff1a \u201c\u53ef\u662f\u6211\u4e0d\u61c2\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u6562\u6bc1\u4e86\u5b83\u3002\u201d \u3000\u3000\u201c\u5929\u610f\u65e0\u5e38\uff0c\u5929\u5a01\u96be\u6d4b\uff0c\u51a5\u51a5\u4e2d\u6709\u5f88\u591a\u5b89\u6392\u90fd\u662f\u4eba\u529b\u65e0\u6cd5\u6297\u4e89\u7684\uff0c\u201d\u8001\u4eba\u76ee\u4e2d\u53c8\u9732\u51fa\u90a3 \u79cd\u8bf4\u4e0d\u51fa\u7684\u6050\u60e7\uff1a\u201c\u5982\u679c\u8427\u5927\u5e08\u6bc1\u4e86\u8fd9\u67c4\u5251\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u6709\u66f4\u53ef\u6015\u7684\u7978\u4e8b\u964d\u4e34\u5230\u4ed6\u7684\u72ec\u5b50\u8eab \u4e0a\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u773c\u91cc\u5728\u95ea\u7740\u5149\uff1a\u201c\u540e\u6765\u8427\u5927\u5e08\u662f\u600e\u4e48\u5904\u7f6e\u8fd9\u67c4\u5251\u7684\uff1f\u201d \u3000\u3000\u201c\u8427\u5927\u5e08\u6709\u4e09\u4f4d\u5f1f\u5b50\uff0c\u5927\u5f1f\u5b50\u5f97\u4e86\u4ed6\u7684\u76f8\u5251\u672f\uff0c\u8d70\u904d\u5929\u6daf\uff0c\u76f8\u5c3d\u5229\u5668\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\uff0c\u6c5f\u6e56\u4e2d\u6709\u4f4d\u78e8\u5200\u7684\u8001\u4eba\uff0c\u76f8\u5251\u51f6\u5409\uff0c\u7075\u9a8c\u5982\u795e\uff0c\u201d\u5353\u4e1c\u6765\u9053\uff1a\u201c\u8427\u5927\u5e08 \u7684\u5927\u5f1f\u5b50\u60f3\u5fc5\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u8001\u4eba\u70b9\u5934\uff1a\u201c\u8427\u5927\u5e08\u7684\u4e8c\u5f1f\u5b50\u90b5\u7a7a\u5b50\u5f97\u4e86\u4ed6\u7684\u94f8\u5251\u4e4b\u672f\uff0c\u540e\u6765\u4e5f\u6210\u4e3a\u4e00\u4ee3\u5251\u5e08\u3002\u201d \u3000\u3000\u201c\u90b5\u7a7a\u5b50\uff1f\u201d\u5353\u4e1c\u6765\u8038\u7136\u52a8\u5bb9\uff1a\u201c\u5c31\u662f\u94f8\u9020\u79bb\u522b\u94a9\u7684\u90a3\u4f4d\u90b5\u5927\u5e08\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u8001\u4eba\u8bf4\uff1a\u201c\u8fd9\u4e24\u4eba\u90fd\u662f\u4e0d\u51fa\u4e16\u7684\u5947\u624d\uff0c\u4f46\u662f\u8427\u5927\u5e08\u5374\u5c06\u81ea\u5df1\u6700\u5f97\u610f\u7684\u523a\u51fb\u4e4b\u672f\u4f20\u7ed9\u4e86\u7b2c \u4e09\u4e2a\u5f1f\u5b50\uff0c\u800c\u4e14\u5c06\u6cea\u75d5\u4e5f\u4f20\u7ed9\u4e86\u4ed6\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u4f20\u7ed9\u4ed6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u5fc3\u80f8\u535a\u5927\u4ec1\u6148\uff0c\u5929\u6027\u4e5f\u6781\u6de1\u6cca\uff0c\u5b8c\u5168\u6ca1\u6709\u4e00\u70b9\u540d\u5fc3\u5229\u6b32\uff0c\u800c\u4e14\u4ece\u4e0d\u6740 \u751f\u3002\u201d \u3000\u3000\u201c\u4ed6\u5df2\u5c3d\u5f97\u8427\u5927\u5e08\u7684\u5251\u672f\uff0c\u5f53\u7136\u6ca1\u6709\u4eba\u80fd\u4ece\u4ed6\u624b\u4e2d\u5c06\u6cea\u75d5\u593a\u8d70\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u8fd9\u4e48\u6837 \u4e00\u4f4d\u6709\u4ec1\u5fc3\u7684\u957f\u8005\uff0c\u5f53\u7136\u66f4\u4e0d\u4f1a\u4f24\u5bb3\u6069\u5e08\u7684\u72ec\u5b50\u3002\u201d \u3000\u3000\u201c\u800c\u4e14\u4ed6\u4e09\u5341\u5c81\u65f6\u5c31\u5df2\u9690\u4e8e\u6df1\u5c71\uff0c\u53d1\u8a93\u6709\u751f\u4e4b\u65e5\u7edd\u4e0d\u518d\u8e0f\u5165\u7ea2\u5c18\u4e00\u6b65\uff0c\u6b7b\u540e\u4e5f\u8981\u5c06\u6cea\u75d5 \u966a\u4ed6\u846c\u4e8e\u6df1\u5c71\u3002\u201d \u3000\u3000\u201c\u662f\u54ea\u5ea7\u5c71\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u8001\u4eba\u8bf4\uff1a\u201c\u6ca1\u6709\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53f9\u606f\uff1a\u201c\u5c31\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u6240\u4ee5\u6c5f\u6e56\u4e2d\u624d\u5c11\u4e86\u4e00\u4f4d\u5251\u672f\u5927\u5e08\uff0c\u4e5f\u5c11\u4e86\u4e00\u67c4\u5229\u5668\u795e \u5175\uff0c\u8fd9\u662f\u6c5f\u6e56\u4eba\u7684\u5e78\u8fd0\uff1f\u8fd8\u662f\u4e0d\u5e78\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u8427\u6cea\u8840\u5374\u603b\u7b97\u6d3b\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u5353\u4e1c\u6765\u60a0\u60a0\u7684\u8bf4\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u8427\u6cea\u8840\u603b\u7b97\u6ca1\u6709\u6b7b\u5728\u6cea\u75d5\u4e0b\uff0c\u81f3\u5c11\u4ed6\u73b0\u5728 \u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u91cc\u867d\u7136\u4e5f\u5145\u6ee1\u4f24\u611f\uff0c\u53ef\u662f\u4ed6\u7684\u773c\u775b\u5374\u5df2\u56e0\u5174\u594b\u800c\u53d1\u5149\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u767b\u5f92\u5b50\u770b\u89c1 \u4e00\u4e2a\u8d64\u88f8\u7684\u5c11\u5973\u5df2\u7ecf\u7ad9\u5728\u4ed6\u5e8a\u5934\u4e00\u6837\u3002 \u3000\u3000\u7b49\u4ed6\u518d\u62ac\u8d77\u5934\u53bb\u770b\u5c0f\u4ead\u4e2d\u7684\u8001\u4eba\u65f6\uff0c\u8001\u4eba\u4eff\u4f5b\u5df2\u7761\u7740\u4e86\u3002 \u3000\u3000\u7ec6\u96ea\u970f\u970f\uff0c\u5c0f\u95e8\u534a\u5f00\uff0c\u5353\u4e1c\u6765\u5df2\u7ecf\u8d70\u51fa\u53bb\uff0c\u8776\u821e\u5df2\u7ecf\u51c6\u5907\u5173\u95e8\u4e86\u3002 \u3000\u3000\u53ea\u8981\u628a\u8fd9\u9053\u95e8\u5173\u4e0a\uff0c\u8fd9\u5730\u65b9\u5c31\u597d\u50cf\u548c\u5916\u9762\u7684\u4e16\u754c\u5b8c\u5168\u9694\u7edd\u4e86\u3002 \u3000\u3000\u5979\u53ea\u5e0c\u671b\u6c38\u8fdc\u4e0d\u8981\u6709\u4eba\u518d\u6765\u6572\u95e8\uff0c\u8ba9\u5979\u548c\u90a3\u4e2a\u8001\u4eba\u5728\u8fd9\u91cc\u81ea\u751f\u81ea\u706d\uff0c\u56e0\u4e3a\u5979\u5bf9\u5916\u9762\u7684\u90a3 \u4e2a\u4e16\u754c\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u4f01\u671b\uff0c\u5b8c\u5168\u6ca1\u6709\u7559\u604b\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u5fc3\u5df2\u6b7b\uff0c\u5269\u4e0b\u7684\u53ea\u4e0d\u8fc7\u662f\u4e00\u526f\u9ebb\u6728\u7684\u8eaf\u58f3\u548c\u4e00\u53cc\u817f\u3002 \u3000\u3000\u5979\u7684\u8fd9\u53cc\u817f\u5c31\u597d\u50cf\u662f\u8c61\u7684\u7259\u3001\u9e9d\u7684\u9999\u3001\u7fce\u7f8a\u7684\u89d2\uff0c\u662f\u5979\u751f\u547d\u4e2d\u6700\u503c\u5f97\u5b9d\u8d35\u73cd\u60dc\u7684\u4e00\u90e8 \u4efd\uff0c\u4e5f\u662f\u5979\u6240\u6709\u4e00\u5207\u4e0d\u5e78\u7684\u6839\u6e90\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u6ca1\u6709\u8fd9\u4e48\u6837\u4e00\u53cc\u817f\uff0c\u5979\u4f1a\u53d8\u6210\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u662f\u4e0d\u662f\u4f1a\u6d3b\u5f97\u66f4\u5e78\u798f\u4e9b\uff1f \u3000\u3000\u8776\u821e\u5782\u7740\u5934\uff0c\u7ad9\u5728\u5c0f\u95e8\u540e\uff0c\u53ea\u5e0c\u671b\u5353\u4e1c\u6765\u5feb\u70b9\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5374\u5df2\u8f6c\u8fc7\u8eab\uff0c\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u773c\u795e\u770b\u7740\u5979\uff0c\u76ef\u7740\u5979\u770b\u4e86\u5f88\u4eba\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u5929\u6765\uff0c\u4f60\u65e5\u5b50\u8fc7\u5f97\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u8776\u821e\u7684\u58f0\u97f3\u91cc\u5168\u65e0\u611f\u60c5\uff0c\u51e0\u4e4e\u6bd4\u5353\u4e1c\u6765\u7684\u58f0\u97f3\u66f4\u51b7\u6de1\u3002 \u3000\u3000\u201c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u4f60\u53ef\u4ee5\u4e00\u76f4\u7559\u5728\u8fd9\u91cc\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff0c\u201c\u6211\u53ef\u4ee5\u4fdd\u8bc1\u7edd\u4e0d\u4f1a\u6709\u4eba\u6765\u6253 \u6270\u3002\u201d \u3000\u3000\u201c\u8c22\u8c22\u4f60\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4e5f\u53ef\u4ee5\u628a\u4f60\u9001\u5230\u522b\u7684\u5730\u65b9\u53bb\uff0c\u201d\u5353\u4e1c\u6765\u6de1\u8c08\u7684\u8bf4\uff1a\u201c\u8981\u6211\u613f\u610f\uff0c\u6211\u968f\u65f6\u90fd\u53ef\u4ee5 \u628a\u4f60\u9001\u5230\u522b\u7684\u5730\u65b9\u53bb\uff0c\u6211\u77e5\u9053\u6709\u4e9b\u4eba\u4e00\u5b9a\u5f88\u5e0c\u671b\u6211\u8fd9\u4e48\u6837\u505a\u7684\u3002\u201d \u3000\u3000\u8776\u821e\u5ffd\u7136\u53d8\u5f97\u50cf\u662f\u6761\u53d7\u60ca\u7684\u7f9a\u7f8a\u822c\u5f80\u540e\u9000\u7f29\uff0c\u9000\u5230\u95e8\u540e\u7684\u89d2\u843d\u91cc\uff0c\u7f29\u6210\u4e86\u4e00\u56e2\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7b11\u4e86\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u5f53\u7136\u4e0d\u4f1a\u8fd9\u4e48\u6837\u505a\u7684\uff0c\u201d\u4ed6\u7684\u7b11\u773c\u4e2d\u5145\u6ee1\u6b8b\u9177\u4e3b\u610f\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u8981\u8ba9\u4f60\u77e5\u9053\uff0c \u4f60\u5e94\u8be5\u5bf9\u6211\u597d\u4e00\u70b9\uff0c\u56e0\u4e3a\u4f60\u6b20\u6211\u7684\u60c5\u3002\u201d \u3000\u3000\u8776\u821e\u62ac\u8d77\u5934\uff0c\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u8981\u6211\u600e\u4e48\u6837\u5bf9\u4f60\u597d\uff1f\u201d\u8776\u821e\u5ffd\u7136\u95ee\u4ed6\uff1a\u201c\u662f\u4e0d\u662f\u8981\u6211\u966a\u4f60\u4e0a\u5e8a\u7761\u89c9\uff1f\u201d \u3000\u3000\u5979\u7684\u98ce\u59ff\u4ecd\u7136\u4f18\u96c5\u5982\u8d35\u5987\uff0c\u53ef\u662f\u8bf4\u51fa\u6765\u7684\u8bdd\u5374\u50cf\u662f\u4e2a\u5a4a\u5b50\u3002 \u3000\u3000\u201c\u4f60\u5e94\u8be5\u542c\u8bf4\u8fc7\u6211\u7684\u529f\u592b\u662f\u6ca1\u6709\u4eba\u80fd\u6bd4\u5f97\u4e0a\u7684\uff0c\u53ea\u8981\u8ddf\u6211\u7761\u8fc7\u4e00\u6b21\u89c9\u7684\u7537\u4eba\uff0c\u5c31\u4f1a\u4e00\u8f88 \u5b50\u90fd\u5fd8\u4e0d\u4e86\u6211\u3002\u201d\u8776\u821e\u8bf4\uff1a\u201c\u6211\u7684\u817f\u52a8\u8d77\u6765\u7684\u65f6\u5019\u7537\u4eba\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u4f60\u6050\u6015\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d \u5230\u3002\u201d \u3000\u3000\u5979\u5df2\u7ecf\u5f00\u59cb\u5728\u7b11\u4e86\uff0c\u7b11\u58f0\u8d8a\u6765\u8d8a\u75af\u72c2\uff1a\u201c\u53ef\u662f\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u8981\u6211\u7684\uff0c\u56e0\u4e3a\u4f60\u559c\u6b22\u7684\u4e0d\u662f \u6211\uff0c\u4f60\u559c\u6b22\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u4f60\u8fd9\u4e00\u8f88\u5b50\u6d3b\u7740\u90fd\u662f\u4e3a\u4e86\u4ed6\u2026\u2026\u201d \u3000\u3000\u5979\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u62e7\u4f4f\u5979\u7684\u624b\uff0c\u53cd\u624b\u4e00\u8033\u5149\u91cd\u91cd\u7684\u63b4\u5728\u5979\u8138\u4e0a\u3002 \u3000\u3000\u5979\u82cd\u767d\u7f8e\u4e3d\u7684\u8138\u4e0a\u7acb\u523b\u56fe\u4e0b\u4e94\u6761\u8840\u7ea2\u7684\u6307\u75d5\uff0c\u53ef\u662f\u773c\u4e2d\u7684\u754f\u60e7\u4e4b\u8272\u53cd\u800c\u6d88\u5931\u4e86\uff0c\u53d8\u6210\u4e86 \u6ee1\u8154\u8f7b\u8511\u548c\u8ba5\u8bee\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7528\u529b\u62e7\u8f6c\u5979\u7684\u624b\uff0c\u62e7\u5230\u5979\u7684\u540e\u80cc\u4e0a\uff0c\u8ba9\u5979\u75db\u5f97\u6d41\u51fa\u4e86\u773c\u6cea\u4e4b\u540e\uff0c\u624d\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57 \u7684\u8bf4\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u201d\u4ed6\u773c\u4e2d\u4eff\u4f5b\u5df2\u56e0\u522b\u4eba\u7684\u75db\u82e6\u800c\u5145\u6ee1\u6fc0\u60c5\uff1a\u201c\u73b0\u5728\u6211\u5c31\u8981\u8ba9\u4f60\u77e5\u9053\uff0c\u4f60\u9519 \u5f97\u591a\u4e48\u5389\u5bb3\u3002\u201d \u56db \u3000\u3000\u591c\u6df1\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6ca1\u6709\u71c3\u706f\uff0c\u53ea\u6709\u7089\u4e2d\u7684\u706b\u7130\u5728\u95ea\u52a8\u3002\u8776\u821e\u8d64\u88f8\u88f8\u7684\u8737\u66f2\u5728\u94fa\u6ee1\u7d2b\u8c82\u7684\u8f6f\u69bb\u4e0a\uff0c\u5728 \u95ea\u52a8\u7684\u706b\u5149\u4e2d\u770b\u6765\uff0c\u5979\u7684\u817f\u66f4\u7f8e\uff0c\u7f8e\u5f97\u8ba9\u4eba\u5b81\u613f\u4e3a\u5979\u4e0b\u5730\u72f1\u3002 \u3000\u3000\u5979\u7684\u773c\u6cea\u5df2\u4e0d\u518d\u6d41\u3002 \u3000\u3000\u6bd4\u8d77\u521a\u624d\u6240\u53d7\u5230\u7684\u4fae\u8fb1\u548c\u75db\u82e6\u6765\uff0c\u4ee5\u524d\u5979\u6240\u53d7\u5230\u7684\u82e6\u96be\u7b80\u76f4\u5c31\u50cf\u662f\u513f\u620f\u3002 \u3000\u3000\u5979\u7b80\u76f4\u65e0\u6cd5\u60f3\u8c61\u4eba\u7c7b\u4e2d\u7adf\u6709\u8fd9\u79cd\u53d8\u6001\u7684\u91ce\u517d\u3002 \u3000\u3000\u901a\u5f80\u5916\u5ba4\u7684\u95e8\u662f\u865a\u63a9\u7740\u7684\uff0c\u5353\u4e1c\u6765\u5df2\u7ecf\u51fa\u53bb\uff0c\u8776\u821e\u542c\u89c1\u5916\u9762\u6709\u4e2a\u5e74\u8f7b\u4eba\u7684\u58f0\u97f3\u5728\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u8776\u821e\u9690\u7ea6\u542c\u51fa\u4ed6\u662f\u5728\u544a\u8bc9\u5353\u4e1c\u6765\uff0c\u53f8\u9a6c\u8d85\u7fa4\u5ffd\u7136\u75c5\u4e86\uff0c\u800c\u4e14\u75c5\u5f97\u5f88\u91cd\uff0c \u5df2\u7ecf\u8bf7\u4e86\u597d\u51e0\u4f4d\u540d\u533b\u6765\u770b\u8fc7\uff0c\u90fd\u8bf4\u4ed6\u662f\u56e0\u4e3a\u79ef\u52b3\u6210\u75be\uff0c\u5fc5\u9700\u9759\u517b\u624d\u80fd\u6062\u590d\uff0c\u6240\u4ee5\u6682\u65f6\u4e0d\u80fd\u89c1 \u5ba2\u3002 \u3000\u3000\u5353\u4e1c\u6765\u6c89\u9ed8\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u95ee\u8fd9\u5e74\u8f7b\u4eba\uff1a \u3000\u3000\u201c\u662f\u4e0d\u80fd\u89c1\u5ba2\uff1f\u8fd8\u662f\u4ec0\u4e48\u4eba\u90fd\u4e0d\u80fd\u89c1\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u4ec0\u4e48\u4eba\u90fd\u4e0d\u80fd\u89c1\u3002\u201d \u3000\u3000\u201c\u8fde\u6211\u4e5f\u4e0d\u80fd\u89c1\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u592b\u4eba\u624d\u7279\u5730\u8981\u4f60\u6765\u544a\u8bc9\u6211\uff0c\u53eb\u6211\u4e5f\u4e0d\u8981\u53bb\u6253\u6270\u4ed6\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u53ea\u8bf4\uff0c\u8bf7\u5353\u5148\u751f\u628a\u6240\u6709\u7684\u4e8b\u90fd\u6682\u65f6\u6401\u4e00\u4e0b\uff0c\u7b49\u8001\u603b\u75c5\u597d\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u4f60\u89c1\u8fc7\u592b\u4eba\u8bf7\u6765\u7684\u5927\u592b\uff1f\u201d \u3000\u3000\u201c\u4e09\u4f4d\u6211\u90fd\u89c1\u5230\u4e86\u3002\u201d\u5e74\u8f7b\u4eba\u8bf4\u51fa\u4e86\u8fd9\u4e09\u4f4d\u5927\u5927\u7684\u540d\u5b57\uff0c\u65e0\u7591\u90fd\u662f\u957f\u5b89\u7684\u540d\u533b\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u600e\u4e48\u8bf4\uff1f\u201d\u5353\u4e1c\u6765\u53c8\u95ee\uff1a\u201c\u4ed6\u4eec\u90fd\u8bf4\u8001\u603b\u8fd9\u6b21\u75c5\u5f97\u4e0d\u8f7b\uff0c\u5982\u679c\u518d\u62d6\u4e0b\u53bb\uff0c\u5c31\u5371\u9669 \u5f97\u5f88\u4e86\uff1f\u201d \u3000\u3000\u5353\u4e1c\u672a\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u8fd9\u51e0\u5929\u4ed6\u5b9e\u5728\u4e0d\u8be5\u751f\u75c5\u7684\uff0c\u4ed6\u75c5\u5f97\u771f\u4e0d\u5de7\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u663e\u7136\u662f\u5353\u4e1c\u6765\u8eab\u8fb9\u7684\u4eb2\u4fe1\uff0c\u6240\u4ee5\u624d\u6562\u95ee\u4ed6\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u5185\u5ba4\u4e2d\u7684\u8776\u821e\u5168\u8eab\u808c\u8089\u7a81\u7136\u7ef7\u7d27\uff0c\u56e0\u4e3a\u5979\u542c\u89c1\u5353\u4e1c\u6765\u53c8\u5728\u7528\u4ed6\u90a3\u79cd\u7279\u522b\u6b8b\u9177\u7f13\u6162\u7684\u65b9 \u5f0f\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u5bf9\u90a3\u5e74\u8f7b\u4eba\u8bf4\uff1a\u201c\u56e0\u4e3a\u8fd9\u4e24\u5929\u6731\u731b\u4e00\u5b9a\u4f1a\u6765\u7684\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u7ae0\u3000\u4e8c\u6708\u6d1b\u9633\u6625\u4ecd\u65e9",content:(0,t.jsx)(t.Fragment,{children:"\u4e00 \u3000\u3000\u4e8c\u6708\u4e8c\u5341\u4e8c\u65e5\u3002 \u3000\u3000\u6d1b\u9633\u3002 \u3000\u3000\u6668\u3002 \u3000\u3000\u4e00\u9a91\u5feb\u9a6c\u5192\u7740\u98ce\u96ea\u51b2\u5165\u4e86\u6d1b\u9633\uff0c\u9a6c\u4e0a\u4eba\u7a7f\u4e00\u4ef6\u85cf\u9752\u6597\u7bf7\uff0c\u6234\u4e00\u9876\u8303\u9633\u6be1\u7b20\uff0c\u628a\u7b20\u5e3d\u4f4e\u4f4e \u7684\u538b\u5728\u7709\u6bdb\u4e0a\uff0c\u6321\u4f4f\u4e86\u534a\u8fb9\u8138\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u9a91\u6c42\u7cbe\u7edd\uff0c\u53ef\u662f\u4e00\u5165\u6d1b\u9633\u5883\u5185\u5c31\u4e0b\u4e86\u9a6c\uff0c\u597d\u50cf\u975e\u4f46\u4e0d\u613f\u8ba9\u4eba\u770b\u89c1\u4ed6\u7684\u771f\u9762\u76ee\uff0c \u4e5f\u4e0d\u613f\u88ab\u4eba\u770b\u5230\u4ed6\u77eb\u5065\u7684\u8eab\u624b\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u6b21\u8fd8\u662f\u4ed6\u7b2c\u4e00\u6b21\u5230\u6d1b\u9633\u6765\uff0c\u6d1b\u9633\u57ce\u91cc\u8fd8\u6ca1\u6709\u4eba\u89c1\u8fc7\u4ed6\u3002 \u3000\u3000\u540c\u5e74\u540c\u6708\u540c\u65e5\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u4e8c\u6708\u957f\u5b89\u7684\u6e05\u6668\u4e5f\u548c\u6d1b\u9633\u540c\u6837\u5bd2\u51b7\uff0c\u5927\u591a\u6570\u4eba\u8fd8\u7559\u604b\u5728\u88ab\u7a9d\u91cc\u7684\u65f6\u5019\uff0c\u5353\u4e1c\u6765\u5df2\u7ecf\u8d77\u6765 \u4e86\u3002 \u3000\u3000\u4ed6\u7684\u7cbe\u795e\u867d\u7136\u5f88\u597d\uff0c\u8138\u8272\u5374\u5f88\u6c89\u91cd\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5df2\u7ecf\u75c5\u4e86\u597d\u51e0\u5929\uff0c\u75c5\u60c5\u6beb\u65e0\u8d77\u8272\uff0c\u4ed6\u7684\u5fc3\u60c5\u81ea\u7136\u4e0d\u4f1a\u597d\u7684\u3002 \u3000\u3000\u8fd9\u51e0\u5929\u4ed6\u4e00\u76f4\u6ca1\u6709\u89c1\u5230\u8fc7\u53f8\u9a6c\uff0c\u6bcf\u6b21\u4ed6\u8981\u53bb\u63a2\u75c5\u65f6\uff0c\u90fd\u88ab\u5434\u5a49\u6321\u4f4f\u4e86\u9a7e\u3002 \u3000\u3000\u75c5\u623f\u5185\u5916\u90e8\u5145\u6ee1\u4e86\u836f\u9999\uff0c\u5434\u5a49\u7684\u795e\u60c5\u4e5f\u663e\u5f97\u5f88\u6194\u60b4\uff0c\u53ef\u662f\u6001\u5ea6\u5374\u5f88\u575a\u51b3\uff0c\u9664\u4e86\u5979\u81ea\u5df1\u548c \u770b\u75c5\u7684\u5927\u592b\u5916\uff0c\u8c01\u4e5f\u4e0d\u80fd\u8fdb\u53bb\uff0c\u8fde\u5353\u4e1c\u6765\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u6ce8\u662f\u5979\u751f\u5e73\u7b2c\u4e00\u6b21\u65f6\u5353\u4e1c\u6765\u5982\u6b64\u65e0\u793c\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5374\u4e00\u70b9\u90fd\u4e0d\u5728\u4e4e\uff0c\u53cd\u800c\u544a\u8bc9\u522b\u4eba\uff1a\u201c\u4e00\u4e2a\u5973\u4eba\u4e3a\u81ea\u5df1\u4e08\u592b\u7684\u5b89\u5371\uff0c\u4e0d\u7ba1\u505a\u51fa\u4ec0\u4e48 \u6837\u7684\u4e8b\u6765\u90fd\u503c\u5f97\u539f\u8c05\u3002\u201d \u3000\u3000\u867d\u7136\u8fd9\u662f\u6e05\u6668\uff0c\u82b1\u56ed\u91cc\u5df2\u7ecf\u6709\u4e24\u4f4d\u5ba2\u4eba\u5728\u7b49\u7740\u5353\u5148\u751f\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e00\u4f4d\u59d3\u7b80\uff0c\u4e00\u4f4d\u59d3\u65bd\uff0c\u90fd\u662f\u957f\u5b89\u7684\u4e16\u4ee3\u540d\u533b\uff0c\u5e73\u65f6\u517b\u5c0a\u5904\u4f18\u3002\u5728\u8fd9\u4e48\u51b7\u7684\u5929\u6c14 \u91cc\uff0c\u51e0\u4e4e\u4ece\u6765\u6ca1\u6709\u79bb\u5f00\u8fc7\u88ab\u7a9d\u548c\u706b\u76c6\u3002 \u3000\u3000\u53ef\u662f\u4eca\u5929\u4ed6\u4eec\u4e00\u5927\u65e9\u5c31\u88ab\u5353\u4e1c\u6765\u6d3e\u4eba\u53bb\u8bf7\u6765\u4e86\uff0c\u800c\u4e14\u4e0d\u628a\u4ed6\u4eec\u8fce\u5165\u6696\u5385\uff0c\u5374\u8981\u4ed6\u4eec\u5728\u4e00 \u4e2a\u56db\u9762\u901a\u98ce\u7684\u5c0f\u4ead\u91cc\u82e6\u7b49\u3002 \u3000\u3000\u5982\u679c\u73b0\u5728\u662f\u516d\u6708\uff0c\u4ead\u5916\u8377\u7ea2\u67f3\u7eff\uff0c\u56db\u9762\u6e05\u98ce\u5f90\u6765\uff0c\u90a3\u79cd\u60c5\u51b5\u5c31\u5341\u5206\u4ee4\u4eba\u6109\u5feb\u4e86\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u51b7\u98ce\u522e\u5728\u8eab\u4e0a\u5c31\u597d\u50cf\u5200\u5b50\u4e00\u6837\uff0c\u4e24\u4f4d\u5148\u751f\u8eab\u4e0a\u867d\u7136\u7a7f\u7740\u91cd\u88d8\uff0c\u624b\u91cc\u867d\u7136\u6342\u7740\u6696 \u7089\uff0c\u8fd8\u662f\u88ab\u51bb\u5f97\u8138\u8272\u53d1\u9752\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u5c31\u5f00\u4e24\u5e16\u6cfb\u836f\u7ed9\u5353\u4e1c\u6765\u5403\u5403\u3002 \u3000\u3000\u8fd9\u79cd\u60f3\u6cd5\u5f53\u7136\u662f\u8fde\u4e00\u70b9\u5f71\u5b50\u90fd\u4e0d\u80fd\u8868\u9732\u51fa\u6765\u7684\uff0c\u5f97\u7f6a\u4e86\u5353\u5148\u751f\u7684\u4eba\u4f1a\u6709\u4ec0\u4e48\u6837\u7684\u4e0b\u573a\uff0c \u957f\u5b89\u57ce\u91cc\u6bcf\u4e2a\u4eba\u90fd\u77e5\u9053\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u6240\u4ee5\u5353\u4e1c\u6765\u7a7f\u7740\u7d2b\u7ecd\u88d8\uff0c\u5e26\u7740\u968f\u4ece\u4ece\u77f3\u5f84\u4e0a\u65bd\u65bd\u7136\u8d70\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u4e24\u4e2a\u4eba\u90fd\u663e\u5f97\u5f88\u6109\u5feb \u7684\u6837\u5b50\uff0c\u957f\u63d6\u5230\u5730\uff0c\u8d54\u7b11\u95ee\u597d\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5bf9\u4ed6\u4eec\u4e5f\u5f88\u5ba2\u6c14\u3002 \u3000\u3000\u201c\u5982\u6b64\u4e25\u5bd2\uff0c\u6211\u6ca1\u6709\u8bf7\u4e24\u4f4d\u5230\u6696\u9601\u76f8\u5750\uff0c\u5374\u628a\u4e24\u4f4d\u62db\u547c\u5230\u8fd9\u91cc\u6765\uff0c\u4e24\u4f4d\u5fc3\u91cc\u662f\u4e0d\u662f\u89c9\u5f97 \u5f88\u5947\u602a\uff1f\u201d \u3000\u3000\u5fc3\u91cc\u5f53\u7136\u662f\u5947\u602a\u7684\uff0c\u5634\u91cc\u7684\u8bf4\u6cd5\u5374\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u201c\u5feb\u96ea\u521d\u6674\uff0c\u6885\u82b1\u4e5f\u5f00\u5f97\u6b63\u597d\u3002\u201d\u6bd4\u8f83\u4f1a\u8bf4\u8bdd\u7684\u65bd\u5927\u592b\u62a2\u7740\u9053\uff1a\u201c\u4e1c\u7fc1\u4e00\u5411\u662f\u4f4d\u96c5\u4eba\uff0c \u83ab\u975e\u8981\u6211\u4eec\u5230\u8fd9\u91cc\u6765\u770b\u82b1\u8d4f\u96ea\uff1f\u201d \u3000\u3000\u201c\u6211\u5012\u662f\u786e\u5b9e\u8981\u8bf7\u4e24\u4f4d\u5230\u8fd9\u91cc\u6765\u770b\u6837\u4e1c\u897f\uff0c\u53ea\u4e0d\u8fc7\u770b\u7684\u5e76\u4e0d\u662f\u82b1\uff0c\u4e5f\u4e0d\u662f\u96ea\u3002\u201d \u3000\u3000\u770b\u7684\u4e0d\u662f\u82b1\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u201c\u65bd\u5927\u592b\u57ce\u5916\u522b\u9986\u91cc\u7684\u96ea\u592b\u4eba\u808c\u80a4\u5982\u96ea\uff0c\u7b80\u5148\u751f\u6628\u591c\u4f9b\u517b\u7684\u82b1\u854a\u59d1\u5a18\u4e5f\u6bd4\u8fd9\u91cc\u7684\u6885\u82b1\u597d \u770b\u5f97\u591a\u3002\u201d\u5353\u4e1c\u6765\u5fae\u7b11\uff1a\u201c\u8981\u770b\u82b1\u8d4f\u96ea\uff0c\u53c8\u4f55\u5fc5\u8bf7\u4e24\u4f4d\u5230\u8fd9\u91cc\u672a\uff1f\u201d \u3000\u3000\u4e24\u4f4d\u540d\u533b\u624b\u5fc3\u91cc\u597d\u50cf\u90fd\u5728\u5192\u6c57\u4e86\uff0c\u8fd9\u4e9b\u4e8b\u8fde\u4ed6\u4eec\u7684\u59bb\u5b50\u90fd\u4e0d\u77e5\u9053\u3002\u5353\u4e1c\u6765\u5374\u8f7b\u63cf\u6de1\u5199\u7684 \u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5728\u4e00\u4e2a\u968f\u968f\u4fbf\u4fbf\u5c31\u80fd\u628a\u4f60\u7684\u79d8\u5bc6\u9690\u79c1\u8bf4\u51fa\u6765\u7684\u4eba\u9762\u524d\uff0c\u4ed6\u4eec\u8fd8\u6562\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u201c\u4e24\u4f4d\u8bf7\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7b11\u5f97\u867d\u7136\u6709\u70b9\u4e0d\u6000\u597d\u610f\uff0c\u65bd\u5927\u592b\u548c\u7b80\u5927\u592b\u4e5f\u53ea\u6709\u4e56\u4e56\u7684\u8ddf\u7740\u4ed6\u8d70\u3002 \u3000\u3000\u8d70\u5230\u82b1\u5f84\u65c1\u4e00\u6761\u7528\u767d\u77f3\u780c\u6210\u7684\u6c34\u6c9f\u524d\u9762\uff0c\u5353\u4e1c\u6765\u5148\u53eb\u4eba\u6380\u8d77\u4e0a\u9762\u76d6\u7740\u7684\u77f3\u677f\uff0c\u56de\u8fc7\u5934\u6765 \u95f4\u4ed6\u4eec\uff1a\u201c\u4e24\u4f4d\u8bf7\u770b\uff0c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u662f\u6761\u6c34\u6c9f\uff0c\u65e0\u8bba\u8c01\u90fd\u770b\u5f97\u51fa\u8fd9\u662f\u6761\u6c34\u6c9f\uff0c\u5353\u4e1c\u6765\u4e00\u5927\u65e9\u628a\u4ed6\u4eec\u627e\u6765\uff0c\u96be\u9053\u5c31\u662f\u4e3a\u4e86\u8981 \u4ed6\u4eec\u6765\u770b\u6c34\u6c9f\u7684\uff1f \u3000\u3000\u4e00\u6761\u6c34\u6c9f\u6709\u4ec0\u4e48\u597d\u770b\uff1f \u3000\u3000\u65bd\u5927\u592b\u548c\u7b80\u5927\u592b\u90e8\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4e2a\u76f4\u7ad9\u5728\u90a3\u91cc\uff0c\u770b\u7740\u8fd9\u6761\u6c34\u6c9f\uff0c\u770b\u5f97\u51fa\u4e86\u795e\u3002\u5c31\u597d\u50cf\u4e16\u754c\u4e0a\u518d\u4e5f\u6ca1\u6709\u6bd4\u8fd9\u6761\u6c34\u6c9f \u66f4\u503c\u5f97\u4ed6\u4eec\u6765\u770b\u7684\u4e1c\u897f\u3002 \u3000\u3000\u7b80\u5927\u592b\u7684\u813e\u6c14\u6bd4\u8f83\u6025\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u770b\u8d77\u6765\u8fd9\u597d\u50cf\u53ea\u4e0d\u8fc7\u662f\u6761\u6c34\u6c9f\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u770b\u8d77\u6765\u8fd9\u597d\u50cf\u53ea\u4e0d\u8fc7\u662f\u6761\u6c34\u6c9f\u800c\u5df2\u3002\u201d\u5353\u4e1c\u6765\u6de1\u8c08\u7684\u8bf4\uff0c\u201c\u56e0\u4e3a\u8fd9\u672c\u6765 \u5c31\u53ea\u4e0d\u8fc7\u662f\u6761\u6c34\u6c9f\uff0c\u770b\u8d77\u6765\u600e\u4e48\u4f1a\u50cf\u522b\u7684\uff1f\u201d \u3000\u3000\u65bd\u5927\u592b\u548c\u7b80\u5927\u592b\u53c8\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u5353\u4e1c\u6765\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u662f\u6761\u780c\u5f97\u975e\u5e38\u597d\u7684\u6c34\u6c9f\uff0c\u5149\u6ed1\u5e72\u6574\uff0c\u4ece\u4e0d\u6de4\u585e\u3002\u4ece\u53f8\u9a6c\u592b\u5987\u7684\u5c45\u5904 \u4e00\u76f4\u901a\u5230\u82b1\u56ed\u5916\uff0c\u4e00\u76f4\u7545\u901a\u65e0\u963b\u3002\u201d \u3000\u3000\u4e24\u4f4d\u5927\u592b\u867d\u7136\u719f\u8bfb\u533b\u4e66\uff0c\u8fd9\u6b21\u5374\u4e5f\u4e0d\u77e5\u9053\u4ed6\u846b\u82a6\u91cc\u5356\u7684\u4ec0\u4e48\u836f\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u98ce\u4e2d\u5c45\u7136\u597d\u50cf\u771f\u7684\u6709\u4e00\u9635\u836f\u9999\u4f20\u6765\u4e86\u3002 \u3000\u3000\u77f3\u5f84\u4e0a\u4e00\u5927\u65e9\u5c31\u88ab\u6253\u626b\u5e72\u51c0\uff0c\u8fde\u6c34\u6c9f\u91cc\u7684\u79ef\u96ea\u90fd\u5df2\u88ab\u6d88\u9664\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u55c5\u5230\u836f\u9999\u7684\u65f6\u5019\uff0c\u6c34\u6c9f\u91cc\u5df2\u7ecf\u6709\u4e00\u80a1\u6697\u8910\u8272\u7684\u6c61\u6c34\uff0c\u4ece\u4e0a\u9762\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5353\u4e1c\u6765\u6325\u4e86\u6325\u624b\uff0c\u4ed6\u7684\u968f\u4ece\u4e2d\u5c31\u6709\u4eba\u628a\u8fd9\u9053\u6c61\u624d\u6d45\u6d45\u7684\u6309\u4f4f\u4e86\u5c0f\u534a\u7897\uff0c\u53cc\u624b\u6367\u5230\u4e24\u4f4d\u5927 \u592b\u9762\u524d\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u8bf7\u770b\u770b\uff0c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e24\u4f4d\u5927\u592b\u8fde\u770b\u90fd\u4e0d\u7528\u770b\uff0c\u5c31\u5df2\u7ecf\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u4e86\u3002\u8fd9\u5f53\u7136\u4e0d\u662f\u6c61\u6c34\uff0c\u6c61\u6c34\u91cc\u7edd\u4e0d\u4f1a\u6709 \u836f\u3002 \u3000\u3000\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u76ef\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u201c\u6211\u60f3\u4e24\u4f4d\u5927\u6982\u90fd\u4e0d\u4f1a\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u5427\uff1f\u201d \u3000\u3000\u7b80\u5927\u592b\u60f3\u8bf4\u8bdd\uff0c\u53ef\u662f\u5634\u5507\u52a8\u4e86\u4e24\u4e0b\u540e\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u65bd\u5927\u5927\u7684\u5634\u66f4\u597d\u50cf\u88ab\u4eba\u7528\u9488\u7ebf\u7f1d\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4e24\u4f4d\u6628\u5929\u66ff\u6211\u4eec\u8001\u603b\u5f00\u7684\u836f\uff0c\u81ea\u4ece\u6628\u5929\u534a\u591c\u5f00\u59cb\uff0c\u7528\u6587\u706b\u714e\u4e86\u4e24\u4e2a\u591a\u65f6\u8fb0\uff0c\u4e00 \u76f4\u5230\u73b0\u5728\u624d\u714e\u597d\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u636e\u6211\u6240\u77e5\u9053\uff0c\u8fd9\u4e00\u5e16\u836f\u6700\u5c11\u4e5f\u8981\u503c\u4e94\u5341\u4e24\u3002\u201d \u3000\u3000\u4e24\u4f4d\u5927\u592b\u7684\u8138\u8272\u90fd\u53d8\u4e86\u3002 \u3000\u3000\u5353\u4e1c\u6765\u9053\uff1a\u201c\u8fd9\u7897\u836f\u73b0\u5728\u672c\u6765\u5e94\u8be5\u5df2\u7ecf\u6d41\u5165\u53f8\u9a6c\u7684\u80a0\u80c3\u91cc\uff0c\u600e\u4e48\u4f1a\u6d41\u5230\u6c34\u6c9f\u91cc\u6765\u4e86\uff0c\u6211 \u5b9e\u5728\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u4ed6\u773c\u4e2d\u5ffd\u7136\u5c04\u51fa\u4eae\u5149\uff1a\u201c\u5e78\u597d\u6211\u77e5\u9053\u6709\u4eba\u4e00\u5b9a\u660e\u81ea\u7684\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d\u65bd\u5927\u592b\u989e\u98a5\u7740\u95ee\uff1a\u201c\u8c01\u660e\u81ea\uff1f\u201d \u3000\u3000\u201c\u4f60\u3002\u201d \u3000\u3000\u65bd\u5927\u592b\u5c31\u50cf\u662f\u5ffd\u7136\u88ab\u4eba\u7528\u529b\u62bd\u4e86\u4e00\u97ad\u5b50\uff0c\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u7a33\u4e86\u3002 \u3000\u3000\u201c\u5982\u679c\u4f60\u4e5f\u4e0d\u660e\u767d\uff0c\u90a3\u4e00\u5b9a\u662f\u56e0\u4e3a\u8fd9\u91cc\u592a\u70ed\u4e86\u3002\u201d\u5353\u4e1c\u6765\u7684\u53e3\u6c14\u53c8\u53d8\u5f97\u5f88\u6e29\u548c\uff1a\u201c\u4e00\u4e2a \u4eba\u592a\u70ed\u7684\u65f6\u5019\uff0c\u603b\u662f\u4f1a\u6709\u5f88\u591a\u4e8b\u60f3\u4e0d\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u7acb\u523b\u5429\u5490\u4ed6\u7684\u968f\u4ece\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u4e3a\u65bd\u5927\u592b\u5bbd\u8863\uff1f\u201d \u3000\u3000\u65bd\u5927\u592b\u7528\u529b\u62c9\u7d27\u4e86\u8eab\u4e0a\u7684\u76ae\u88d8\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7684\u8bf4\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u5343\u4e07\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u8fd9\u8863\u670d\u662f \u4e07\u4e07\u5bbd\u4e0d\u5f97\u7684\u3002\u201d \u3000\u3000\u7a7f\u7740\u76ae\u88d8\u5df2\u7ecf\u5feb\u8981\u51bb\u6b7b\uff0c\u5982\u8981\u8131\u4e0b\u6765\uff0c\u53ea\u6709\u51bb\u6b7b\u4e3a\u6b62\u3002 \u3000\u3000\u968f\u4ece\u4e2d\u6709\u4e24\u6761\u5927\u6c49\u7ad9\u5728\u65bd\u5927\u5927\u5de6\u53f3\uff0c\u5353\u4e1c\u6765\u53c8\u7528\u5f88\u6e29\u548c\u7684\u53e3\u6c14\u95f4\u4ed6\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u70ed\uff1f\u201d \u3000\u3000\u65bd\u5927\u592b\u62fc\u547d\u6447\u5934\u3002 \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e00\u5b9a\u5df2\u7ecf\u60f3\u8d77\u6765\u4e86\uff0c\u672c\u6765\u5e94\u8be5\u559d\u4e0b\u53bb\u7684\u836f\uff0c\u600e\u4e48\u4f1a\u88ab\u5012\u5728\u6c34\u6c9f\u91cc\uff1f\u201d\u5353\u4e1c\u6765 \u95f4\uff1a\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u90a3\u4f4d\u75c5\u4eba\u6839\u672c\u6ca1\u6709\u75c5\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u7b11\uff0c\u4e24\u6761\u5927\u6c49\u7684\u5de8\u638c\u5df2\u7ecf\u642d\u4e0a\u65bd\u5927\u592b\u7684\u80a9\uff0c\u65bd\u5927\u5927\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u6211\u771f\u7684\u4e0d\u77e5\u9053\uff0c\u6211\u6839\u672c\u5c31\u6ca1\u6709\u89c1\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u77b3\u5b54\u9aa4\u7136\u6536\u7f29\u3002 \u3000\u3000\u201c\u4f60\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff1f\u4f60\u6ca1\u6709\u89c1\u8fc7\u53f8\u9a6c\u8d85\u7fa4\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\uff0c\u771f\u7684\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4ed6\u7684\u592b\u4eba\u8bf7\u4f60\u6765\u4e3a\u4ed6\u770b\u75c5\uff0c\u53ef\u662f\u4f60\u5c45\u7136\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u8fde\u4ed6\u7684\u5f71\u5b50\u90e8\u6ca1\u6709\u89c1\u5230\u8fc7\u3002\u201d\u65bd\u5927\u592b\u5df2\u7ecf\u6025\u4e86\uff1a\u201c\u90a3\u95f4\u5c4b\u5b50\u91cc\u8ddf\u672c\u8fde\u4ed6\u7684\u4eba\u5f71\u5b50\u90fd \u6ca1\u6709\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u9762\u5bf9\u7740\u7070\u6697\u51b7\u6f20\u7684\u5929\u7a7a\uff0c\u9759\u9759\u7684\u7ad9\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u7684\u56de\u8fc7\u5934\uff0c \u51dd\u89c6\u7740\u7b80\u5927\u592b\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u95ee\uff1a \u3000\u3000\u201c\u4f60\u5462\uff1f\u4f60\u4e5f\u6ca1\u6709\u770b\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u6ca1\u6709\uff0c\u201d\u7b80\u5927\u592b\u5df2\u7ecf\u6bd4\u8f83\u9547\u9759\u4e86\u4e00\u70b9\uff1a\u201c\u53f8\u9a6c\u5927\u4fa0\u6839\u672c\u4e0d\u5728\u90a3\u5c4b\u5b50\u91cc\uff0c\u53f8\u9a6c\u592b\u4eba \u8bf7\u6211\u4eec\u6765\uff0c\u53ea\u4e0d\u8fc7\u8981\u6211\u4eec\u66ff\u4e00\u95f4\u7a7a\u5c4b\u5b50\u770b\u75c5\u800c\u5df2\u3002\u201d \u3000\u3000\u7136\u540e\u4ed6\u4eec\u5c31\u542c\u89c1\u4e86\u5434\u5a49\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u5982\u679c\u6709\u4eba\u80af\u51fa\u4e94\u767e\u4e24\u9ec4\u91d1\uff0c\u6709\u5f88\u591a\u5927\u592b\u90fd\u80af\u66ff\u7a7a\u5c4b\u5b50\u770b\u75c5\u7684\u3002\u201d\u5979\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u4e0b\u6b21 \u6211\u5982\u679c\u8fd8\u8981\u53bb\u627e\uff0c\u4e00\u5b9a\u4f1a\u53bb\u627e\u6bd4\u8f83\u4e0d\u6015\u51b7\u7684\u3002\u201d \u3000\u3000\u5982\u679c\u8bf4\u8fd9\u5730\u65b9\u6709\u4eba\u771f\u7684\u751f\u75c5\u4e86\uff0c\u90a3\u4e48\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u662f\u5434\u5a49\u3002 \u3000\u3000\u5979\u7684\u8138\u8272\u67af\u9ec4\u800c\u6194\u60b4\uff0c\u672c\u6765\u5f88\u660e\u6717\u7684\u773c\u775b\u91cc\u73b0\u5728\u5df2\u5145\u6ee1\u8840\u4e1d\u3002 \u3000\u3000\u5979\u76ef\u7740\u8fd9\u4e24\u4f4d\u6015\u51b7\u7684\u5927\u592b\u3002 \u3000\u3000\u201c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5973\u4eba\uff0c\u5f53\u7136\u6ca1\u6709\u5353\u5148\u751f\u8fd9\u4e48\u5927\u7684\u672c\u4e8b\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8981\u4e24\u4f4d\u8131\u8863\u670d\uff0c\u201d\u5979\u7684 \u58f0\u97f3\u51b7\u5f97\u50cf\u51b0\uff1a\u201c\u53ef\u662f\u6211\u529d\u4e24\u4f4d\u4ee5\u540e\u7761\u89c9\u524d\u8981\u591a\u5c0f\u5fc3\u95e8\u6237\uff0c\u83ab\u8981\u7b49\u5230\u534a\u591c\u9192\u6765\uff0c\u5ffd\u7136\u53d1\u73b0\u81ea \u5df1\u5df2\u7ecf\u7761\u5728\u96ea\u5730\u4e0a\u3002\u201d \u3000\u3000\u4e24\u4f4d\u5927\u5927\u7684\u8138\u90fd\u7eff\u4e86\u3002 \u3000\u3000\u5982\u679c\u4e00\u4e2a\u4eba\u7684\u773c\u5149\u53ef\u4ee5\u6740\u4eba\uff0c\u73b0\u5728\u4ed6\u4eec\u6050\u6015\u5c31\u5df2\u7ecf\u6b7b\u5728\u96ea\u5730\u4e0a\u3002 \u3000\u3000\u201c\u73b0\u5728\u4e24\u4f4d\u662f\u4e0d\u662f\u5df2\u7ecf\u53ef\u4ee5\u8bf7\u6eda\u4e86\uff1f\u201d\u5434\u5a49\u8bf4\uff1a\u201c\u8bf7\u3001\u6eda\u3002\u201d \u3000\u3000\u5979\u4e00\u5411\u662f\u4e2a\u5f88\u6e29\u67d4\u7684\u5973\u4eba\uff0c\u6e29\u67d4\u800c\u4f18\u96c5\uff0c\u8bf4\u8bdd\u7684\u65f6\u5019\u901a\u5e38\u4f1a\u5148\u8bf4\u4e00\u4e2a\u201c\u8bf7\u201d\u5b57\u3002 \u3000\u3000\u201c\u5353\u5148\u751f\uff0c\u201d\u7b49\u5230\u4e24\u4f4d\u5927\u592b\u8d70\u4e86\u540e\uff0c\u5979\u53c8\u8bf4\uff1a\u201c\u6211\u5b9e\u5728\u5f88\u60f3\u8bf7\u4f60\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8bf7\u4f60\u4e5f\u8ddf\u4ed6\u4eec\u4e00\u8d77\u6eda\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6ca1\u6709\u53cd\u5e94\uff0c\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\uff0c\u751a\u81f3\u8fde\u8138\u4e0a\u90fd\u6ca1\u6709\u4e00\u70b9\u8868\u60c5\u3002 \u3000\u3000\u201c\u53ef\u60dc\u6211\u4e5f\u77e5\u9053\u4f60\u662f\u4e00\u5b9a\u4e0d\u4f1a\u6eda\u7684\u3002\u201d\u5434\u5a49\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u662f\u53f8\u9a6c\u8d85\u7fa4\u7684\u597d\u670b\u53cb\u3001\u597d\u5144 \u5f1f\uff0c\u6211\u904d\u5929\u4e0b\u90fd\u518d\u4e5f\u627e\u4e0d\u5230\u4f60\u4eec\u8fd9\u4e48\u597d\u7684\u5144\u5f1f\u670b\u53cb\u4e86\uff01\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u91cc\u4e5f\u5145\u6ee1\u4e86\u8ba5\u6d88\uff0c\u5c31\u50cf\u662f\u8776\u821e\u8ddf\u5353\u4e1c\u6765\u8bf4\u8bdd\u65f6\u4e00\u6837\u3002 \u3000\u3000\u201c\u800c\u4e14\u53f8\u9a6c\u8d85\u7fa4\u5168\u90fd\u662f\u9760\u4f60\u8d77\u5bb6\u7684\uff0c\u4ed6\u53ea\u4e0d\u8fc7\u662f\u4e2a\u56db\u80a2\u53d1\u8fbe\u5934\u8111\u7b80\u5355\u7684\u5080\u5121\u800c\u5df2\uff0c\u6ca1\u6709 \u4f60\uff0c\u4ed6\u600e\u4e48\u4f1a\u6709\u4eca\u5929\u3002\u201d\u5434\u5a49\u51b7\u7b11\uff1a\u201c\u6700\u5c11\u4f60\u5fc3\u91cc\u662f\u8fd9\u4e48\u60f3\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5353\u4e1c\u672a\u8fd8\u662f\u5168\u65e0\u53cd\u5e94\uff0c\u5c31\u597d\u50cf\u542c\u4e00\u4e2a\u620f\u5b50\u5728\u53f0\u4e0a\u5531\u620f\u3002 \u3000\u3000\u201c\u4f60\u5f53\u7136\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\uff0c\u4e86\u4e0d\u8d77\u7684\u597d\u670b\u53cb\uff0c\u56e0\u4e3a\u4f60\u66ff\u4ed6\u727a\u7272\u4e86\u4e00\u5207\uff0c\u4f60\u8fd9\u4e00\u8f88\u5b50\u6d3b\u7740 \u4e5f\u90fd\u662f\u4e3a\u4e86\u4ed6\uff0c\u8ba9\u4ed6\u6210\u540d\u9732\u8138\uff0c\u8ba9\u4ed6\u505a\u5927\u9aa0\u5c40\u7684\u603b\u74e2\u628a\u5b50\uff0c\u8ba9\u4ed6\u6210\u4e3a\u5929\u4e0b\u4eba\u5fc3\u76ee\u4e2d\u7684\u5927\u82f1 \u96c4\u3002\u201d \u3000\u3000\u5434\u5a49\u7684\u51b7\u7b11\u58f0\u5ffd\u7136\u53d8\u5f97\u5f88\u75af\u72c2\u3002 \u3000\u3000\u201c\u53ef\u662f\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u8fd9\u4f4d\u5927\u82f1\u96c4\u7684\u65e5\u5b50\u600e\u4e48\u8fc7\u7684\uff1f\u201d\u5979\u7684\u7b11\u58f0\u4e2d\u5145\u6ee1\u6028\u6bd2\uff1a\u201c\u4ed6\u6709\u59bb\u5b50 \u513f\u5973\uff0c\u6709\u81ea\u5df1\u7684\u5bb6\uff0c\u53ef\u662f\u4ed6\u6839\u672c\u5c31\u597d\u50cf\u4e0d\u662f\u8fd9\u4e2a\u5bb6\u91cc\u7684\u4eba\uff0c\u6839\u672c\u6ca1\u6709\u8fc7\u4e00\u5929\u4ed6\u81ea\u5df1\u613f\u610f\u8fc7\u7684 \u65e5\u5b50\uff0c\u56e0\u4e3a\u6bcf\u4ef6\u4e8b\u4f60\u90fd\u66ff\u4ed6\u5b89\u6392\u597d\u4e86\uff0c\u4f60\u8981\u4ed6\u600e\u4e48\u505a\uff0c\u4ed6\u5c31\u5f97\u600e\u4e48\u505a\uff0c\u751a\u81f3\u8fde\u559d\u70b9\u9152\u90fd\u8981\u5077 \u5077\u7684\u559d\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7a81\u7136\u6253\u65ad\u5979\u7684\u8bdd\u3002 \u3000\u3000\u201c\u591f\u4e86\u3002\u201d\u4ed6\u544a\u8bc9\u5434\u5a49\uff1a\u201c\u4f60\u5df2\u7ecf\u8bf4\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u6211\u5df2\u7ecf\u8bf4\u591f\u4e86\u3002\u201d\u5434\u5a49\u5782\u4e0b\u5934\uff0c\u773c\u6cea\u5df2\u6d41\u6ee1\u9762\u988a\uff0c\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u6709\u4ec0\u4e48\u8bdd\u8981 \u8bf4\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u6709\u51e0\u53e5\u8bdd\u95ee\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u4f1a\u8bf4\u7684\uff0c\u201d\u5434\u5a49\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u8ba9\u4f60\u6709\u673a\u4f1a\u50cf\u5bf9\u522b\u4eba\u90a3\u4e48\u6837\u65f6\u6211\u3002\u201d \u3000\u3000\u5979\u7684\u53e3\u97f3\u867d\u7136\u8fd8\u662f\u5f88\u786c\uff0c\u5176\u5b9e\u5df2\u7ecf\u8f6f\u4e86\uff1a\u201c\u6c5f\u7322\u4e2d\u8c01\u4e0d\u77e5\u9053\u2018\u7d2b\u6c14\u4e1c\u6765\uff0c\u5353\u4e1c\u6765\u6700\u5c11\u6709 \u4e00\u767e\u79cd\u6cd5\u5b50\u80fd\u591f\u903c\u4eba\u8bf4\u5b9e\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4f60\u80fd\u591f\u4e86\u89e3\u8fd9\u4e00\u70b9\u90a3\u5c31\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u53f8\u9a6c\u662f\u4e0d\u662f\u5df2\u7ecf\u79bb\u5f00\u4e86 \u957f\u5b89\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u66ff\u4ed6\u7792\u4f4f\u6211\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8981\u4ed6\u53bb\u505a\u4e00\u4e9b\u4ed6\u81ea\u5df1\u60f3\u505a\u7684\u4e8b\u3002\u201d\u5434\u5a49\u8bf4\uff1a\u201c\u6211\u662f\u4ed6\u7684\u59bb\u5b50\u3002\u6211\u76f8\u4fe1\u6bcf\u4e2a\u505a\u59bb \u5b50\u7684\u4eba\u90fd\u5e0c\u671b\u5979\u7684\u4e08\u592b\u662f\u6761\u72ec\u7acb\u81ea\u4e3b\u7684\u7537\u5b50\u6c49\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u4ec0\u4e48\u65f6\u5019\u8d70\u7684\uff1f\u201d \u3000\u3000\u201c\u5341\u4e03\u7684\u665a\u4e0a\u3002\u201d\u5434\u5a49\u8bf4\uff1a\u201c\u7b97\u8d77\u6765\u73b0\u5728\u4ed6\u5df2\u7ecf\u5e94\u8be5\u5230\u4e86\u6d1b\u9633\u3002\u201d \u3000\u3000\u201c\u6d1b\u9633\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u72fc\u4e00\u822c\u7684\u7070\u773c\u4e2d\u5ffd\u7136\u8ff8\u51fa\u8840\u4e1d\uff1a\u201c\u4f60\u8ba9\u4ed6\u4e00\u4e2a\u4eba\u5230\u6d1b\u9633\u53bb\uff1f\u4f60\u662f\u4e0d\u662f\u60f3\u800d\u4ed6\u53bb\u9001 \u6b7b\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u592b\u59bb\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u8ba9\u4ed6\u53bb\u9001\u6b7b\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u76ef\u7740\u5979\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7528\u4ed6\u90a3\u79cd\u6bd4\u5200\u950b\u8fd8\u5c16\u9510\u3001\u6bd4\u86c7\u874e\u8fd8\u6076\u6bd2\u7684\u72ec\u7279\u53e3\u6c14\u4e00\u4e2a\u5b57 \u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a \u3000\u3000\u201c\u56e0\u4e3a\u90ed\u5e84\u3002\u201d \u3000\u3000\u6bcf\u5f53\u5353\u4e1c\u6765\u7528\u8fd9\u79cd\u53e3\u6c14\u8bf4\u8bdd\u65f6\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5c31\u6700\u5c11\u6709\u4e00\u4e2a\u4eba\u8981\u53d7\u5230\u4ed6\u81f4\u547d\u7684\u4f24\u5bb3\u548c\u6253 \u51fb\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u90ed\u5e84\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5728\u522b\u4eba\u542c\u6765\u867d\u7136\u6beb\u65e0\u610f\u4e49\uff0c\u53ef\u662f\u5434\u5a49\u542c\u4e86\u5374\u597d\u50cf\u5ffd\u7136\u88ab\u6bd2\u874e\u6240\u87ab\u5229\u5203\u6240\u4f24\uff0c\u5c31\u597d \u50cf\u5ffd\u7136\u4ece\u4e07\u4e08\u9ad8\u697c\u4e0a\u5931\u8db3\u843d\u4e0b\uff0c\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u4f4f\u4e86\uff0c\u67af\u9ec4\u7901\u7cb9\u7684\u8138\u4e0a\uff0c\u4e5f\u8d77\u4e86\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u53ef \u6015\u53d8\u5316\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5f53\u7136\u4e0d\u4f1a\u9519\u8fc7\u5979\u8fd9\u4e9b\u53d8\u5316\u7684\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u5e74\u6765\u53f8\u9a6c\u4e00\u76f4\u90fd\u8ddf\u4f60\u5206\u623f\u800c\u7761\uff0c\u8fde\u78b0\u90fd\u6709\u6ca1\u78b0\u8fc7\u4f60\u3002\u201d\u5353\u4e1c\u6765\u7684\u58f0\u97f3\u51b7\u6f20\u800c\u6b8b \u9177\uff1a\u201c\u4f60\u6b63\u5728\u72fc\u864e\u4e4b\u5e74\uff0c\u8eab\u8fb9\u521a\u597d\u6709\u90ed\u5e84\u90a3\u4e48\u6837\u4e00\u4e2a\u5e74\u8f7b\u529b\u58ee\u7684\u6f02\u4eae\u5c0f\u4f19\u5b50\uff0c\u800c\u4e14\u5f88\u61c2\u5f97\u5bf9 \u5973\u4eba\u732e\u6bb7\u52e4\u3002\u53ea\u53ef\u60dc\u73b0\u5728\u4ed6\u5df2\u7ecf\u6b7b\u5728\u7ea2\u82b1\u96c6\uff0c\u6b7b\u5728\u6731\u731b\u7684\u5200\u4e0b\uff0c\u8fde\u5934\u9885\u2026\u2026\u201d \u3000\u3000\u5434\u5a49\u5ffd\u7136\u5636\u58f0\u5927\u558a\uff1a\u201c\u591f\u4e86\uff0c\u4f60\u5df2\u7ecf\u8bf4\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u4e8b\u6211\u672c\u6765\u4e0d\u60f3\u8bf4\u7684\uff0c\u56e0\u4e3a\u6211\u4e0d\u60f3\u8ba9\u53f8\u9a6c\u4f24\u5fc3\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u73b0\u5728\u6211\u8bf4\u51fa\u6765\uff0c\u53ea \u4e0d\u8fc7\u8981\u8ba9\u4f60\u77e5\u9053\uff0c\u4f60\u505a\u7684\u4e8b\u6ca1\u6709\u4e00\u4ef6\u80fd\u7792\u5f97\u8fc7\u6211\uff0c\u6240\u4ee5\u4f60\u4ee5\u540e\u4e0d\u7ba1\u8981\u505a\u4ec0\u4e48\u4e8b\uff0c\u90fd\u8981\u7279\u522b\u5c0f \u5fc3\u8c28\u614e\u3002\u201d \u3000\u3000\u5434\u5a49\u7684\u8eab\u5b50\u5df2\u7ecf\u5f00\u59cb\u5728\u53d1\u6296\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u624d\u660e\u767d\u4e86\uff0c\u201d\u5979\u773c\u4e2d\u5145\u6ee1\u4ec7\u6068\u6028\u6bd2\uff1a\u201c\u4f60\u6d3e\u90ed\u5e84\u5230\u7ea2\u82b1\u96c6\u53bb\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u4ed6\u53bb \u9001\u6b7b\uff0c\u56e0\u4e3a\u4f60\u65e9\u5c31\u77e5\u9053\u4e86\u6211\u8ddf\u4ed6\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u6251\u8fc7\u53bb\uff0c\u6293\u4f4f\u5353\u4e1c\u6765\u7684\u8863\u895f\uff0c\u5636\u58f0\u95f4\uff1a\u201c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u662f\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u770b\u7740\u5979\uff0c\u7528\u4e24\u6839\u624b\u6307\u8f7b\u8f7b\u4e00\u5212\u5979\u53cc\u624b\u7684\u8109\u95e8\u3002 \u3000\u3000\u5434\u5a49\u7684\u624b\u677e\u5f00\uff0c\u4eba\u4e5f\u5012\u4e0b\uff0c\u5374\u8fd8\u5728\u95ee\uff1a\u201c\u662f\u4e0d\u662f\uff1f\u662f\u4e0d\u662f\uff1f\u662f\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\uff1f\u201d \u3000\u3000\u5979\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\u771f\u76f8\uff0c\u56e0\u4e3a\u5353\u4e1c\u6765\u5df2\u7ecf\u8d70\u4e86\uff0c\u518d\u4e5f\u6ca1\u6709\u56de\u5934\uff0c\u4e5f\u6ca1\u6709\u770b\u5979\u4e00 \u773c\uff0c\u5c31\u597d\u50cf\u628a\u5979\u5f53\u4f5c\u4e86\u4e00\u53ea\u521a\u88ab\u4ed6\u4ece\u8863\u895f\u4e0a\u6296\u843d\u7684\u866b\u8681\uff0c\u5bf9\u5979\u518d\u4e5f\u4e0d\u5c51\u4e00\u987e\u3002 \u3000\u3000\u4e00\u6761\u957f\u7ef3\u3002 \u3000\u3000\u957f\u7ef3\u5728\u5434\u5a49\u624b\u91cc\uff0c\u5434\u5a49\u5728\u623f\u91cc\u7684\u6a2a\u7cb1\u4e0b\uff0c\u6709\u98ce\u4ece\u7a97\u5916\u5439\u8fdb\u6765\uff0c\u597d\u51b7\u597d\u51b7\u7684\u98ce\u3002 \u3000\u3000\u201c\u4eca\u5929\u662f\u4ec0\u4e48\u65e5\u5b50\uff1f\u6211\u60f3\u4e00\u5b9a\u662f\u4e2a\u597d\u65e5\u5b50\u3002\u201d\u5979\u75f4\u75f4\u7684\u81ea\u8bed\uff0c\u6162\u6162\u7684\u5c06\u957f\u7ef3\u6253\u4e86\u7ed3\u3002 \u3000\u3000\u4e00\u4e2a\u6b7b\u7ed3\u3002 \u4e8c \u3000\u3000\u540c\u65e5\u3002\u6d1b\u9633\u3002 \u3000\u3000\u8fd9\u6761\u8857\u672c\u6765\u662f\u6761\u5f88\u70ed\u95f9\u7684\u8857\uff0c\u6709\u83dc\u573a\uff0c\u6709\u8336\u9986\uff0c\u6709\u65e9\u96c6\uff0c\u8fd8\u6709\u82b1\u5e02\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u5ffd\u7136\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u5c31\u50cf\u662f\u4e00\u4e2a\u4e00\u5411\u5341\u5206\u5065\u5eb7\u5f3a\u58ee\u7684\u4eba\u5ffd\u7136\u66b4\u6bd9\u4e86\u4e00\u6837\uff0c\u8fd9\u6740\u8857\u4e5f\u6b7b\u4e86\uff0c\u53d8\u6210\u4e86\u4e00\u6761\u6b7b\u8857\u3002 \u3000\u3000\u8336\u9986\u7684\u95e8\u677f\u5df2\u7ecf\u6709\u597d\u51e0\u5929\u6ca1\u6709\u62ff\u4e0b\u6765\uff0c\u83dc\u573a\u91cc\u5c60\u592b\u7684\u8089\u6848\u4e0a\u53ea\u5269\u4e0b\u4e00\u4e9b\u6591\u9a73\u4ea4\u9519\u7684\u4e71\u5200 \u75d5\u8ff9\uff0c\u8857\u4e0a\u51e0\u4e4e\u770b\u4e0d\u89c1\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u613f\u610f\u518d\u5230\u8fd9\u6761\u8857\u4e0a\u6765\u3002\u8fd9\u6761\u8857\u4e0a\u53d1\u751f\u7684\u60b2\u60e8\u7978\u4e8b\u5b9e\u5728\u5927\u591a\u4e86\u3002 \u3000\u3000\u53ea\u6709\u4e00\u6761\u5939\u7740\u5c3e\u5df4\u7684\u91ce\u72d7\uff0c\u4f38\u957f\u4e86\u820c\u5934\u5728\u62b5\u7740\u77f3\u677f\u7f1d\u91cc\u8fd8\u6ca1\u6709\u88ab\u6d17\u5e72\u51c0\u7684\u8840\u8ff9\u3002 \u3000\u3000\u91ce\u72d7\u6c38\u8fdc\u4e5f\u4e0b\u4f1a\u77e5\u9053\u8fd9\u91cc\u7684\u8840\u662f\u4e9b\u4ec0\u4e48\u4eba\u7684\u8840\u3002 \u3000\u3000\u91ce\u72d7\u4e0d\u77e5\u9053\uff0c\u725b\u76ae\u77e5\u9053\u3002 \u4e09 \u3000\u3000\u5728\u53e6\u5916\u4e00\u6761\u5c0f\u8857\u4e0a\uff0c\u4e00\u5bb6\u53eb\u201c\u8001\u5f20\u9992\u5934\u5e97\u201d\u7684\u5c0f\u9986\u91cc\uff0c\u725b\u76ae\u6b63\u5728\u5439\u725b\u3002 \u3000\u3000\u201c\u725b\u76ae\u201d\u662f\u4e00\u4e2a\u4eba\u7684\u5916\u53f7\uff0c\u56e0\u4e3a\u8fd9\u4e2a\u597d\u9152\u8d2a\u676f\u7684\u5c0f\u4f19\u5b50\u4e0d\u4f46\u4f1a\u5439\u725b\uff0c\u800c\u4e14\u8138\u76ae\u771f\u539a\uff0c\u6bd4 \u725b\u76ae\u8fd8\u539a\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u5411\u4e00\u4e2a\u4ece\u8fdc\u5730\u6765\u7684\u964c\u751f\u4eba\u5439\u725b\uff0c\u56e0\u4e3a\u8fd9\u4e2a\u964c\u751f\u4eba\u5df2\u7ecf\u8bf7\u4e86\u4ed6\u559d\u4e0b\u4e0d\u5c11\u9152\u3002 \u3000\u3000\u4ed6\u5439\u7684\u5c31\u662f\u90a3\u5929\u5728\u94dc\u9a7c\u5df7\u5916\u90a3\u6761\u8857\u4e0a\u53d1\u751f\u7684\u90a3\u4e2a\u60b2\u58ee\u60e8\u70c8\u7684\u6545\u4e8b\u3002 \u3000\u3000\u201c\u90a3\u4e2a\u5c0f\u5b50\u771f\u4ed6\u5a18\u7684\u662f\u4e2a\u597d\u5c0f\u5b50\uff0c\u4ffa\u725b\u76ae\u771f\u7684\u6253\u5fc3\u773c\u513f\u91cc\u4f69\u670d\u4ed6\u3002\u201d\u725b\u76ae\u8bf4\uff1a\u201c\u90a3\u5c0f\u5b50 \u771f\u4ed6\u5a18\u7684\u591f\u79cd\uff0c\u771f\u4ed6\u5a18\u7684\u4e0d\u6015\u6b7b\u3002\u201d \u3000\u3000\u964c\u751f\u4eba\u9ed8\u9ed8\u7684\u542c\u7740\uff0c\u9ed8\u9ed8\u7684\u4e3a\u4ed6\u5012\u9152\u3002 \u3000\u3000\u201c\u540e\u6765\u4ffa\u624d\u542c\u8bf4\u90a3\u5c0f\u5b50\u59d3\u9ad8\uff0c\u662f\u8001\u72ee\u5b50\u7684\u670b\u53cb\u3002\u201d\u725b\u76ae\u8bf4\uff1a\u201c\u9f99\u4ea4\u9f99\uff0c\u51e4\u4ea4\u98ce\uff0c\u8001\u9f20\u4ea4 \u7684\u670b\u53cb\u4f1a\u6253\u6d1e\uff0c\u8fd9\u53e5\u8bdd\u771f\u4ed6\u5a18\u7684\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u4e5f\u53ea\u6709\u8001\u72ee\u5b50\u90a3\u6837\u7684\u597d\u6c49\uff0c\u624d\u80fd\u4ea4\u5f97\u5230\u4ed6\u90a3\u79cd \u670b\u53cb\u3002\u201d \u3000\u3000\u964c\u751f\u4eba\u773c\u4e2d\u4eff\u4f5b\u6709\u7cbe\u5149\u4e00\u95ea\uff0c\u53ef\u662f\u5f88\u5feb\u7684\u5c31\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u201c\u90a3\u5929\u4f60\u4e5f\u5728\u90a3\u6761\u8857\u4e0a\uff1f\u201d \u3000\u3000\u201c\u4ffa\u600e\u4e48\u4f1a\u4e0d\u5728\uff0c\u8fd9\u79cd\u4e8b\u4ffa\u600e\u4e48\u4f1a\u9519\u8fc7\uff1f\u201d\u725b\u76ae\u5174\u9ad8\u91c7\u70c8\uff1a\u201c\u90a3\u5929\u4ffa\u6b63\u60f3\u5230\u8001\u80e1\u7684\u8336\u9986 \u91cc\u53bb\u559d\u76c5\u65e9\u9152\uff0c\u5c31\u770b\u89c1\u90a3\u5c0f\u5b50\u4e00\u4e2a\u4eba\u5927\u6447\u5927\u6446\u7684\u53bb\u4e86\uff0c\u4e8c\u6708\u5929\u4ed6\u8eab\u4e0a\u5c45\u7136\u53ea\u7a7f\u7740\u8eab\u77ed\u5e03\u8902\uff0c \u5374\u628a\u5927\u8902\u5b50\u642d\u5728\u4e8e\u91cc\uff0c\u540e\u6765\u4ffa\u624d\u77e5\u9053\uff0c\u90a3\u4ef6\u5927\u8902\u5b50\u4e0b\u9762\u539f\u6765\u85cf\u7740\u628a\u5b9d\u5251\u3002\u201d \u3000\u3000\u725b\u76ae\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u7528\u7b77\u5b50\u4e00\u6bd4\u5212\uff1a\u201c\u5c31\u8fd9\u4e48\u4e00\u4e0b\u5b50\uff0c\u90a3\u628a\u5251\u5c31\u523a\u8fdb\u4e86\u8521\u8001\u5927\u7684\u5fc3\u53e3\uff0c\u5feb \u5f97\u8ba9\u4eba\u8fde\u77a7\u90fd\u77a7\u4e0d\u6e05\u695a\u3002\u201d\u4ed6\u6447\u7740\u5934\u53f9\u6c14\uff1a\u201c\u8c01\u90fd\u6ca1\u60f3\u5230\u90a3\u5c0f\u5b50\u771f\u7684\u90a3\u4e48\u6709\u79cd\uff0c\u8fde\u4ffa\u725b\u76ae\u90fd \u88ab\u5413\u50bb\u4e86\u3002\u201d \u3000\u3000\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u5bb6\u90fd\u8ba4\u5b9a\u90a3\u5c0f\u5b50\u51c6\u8981\u88ab\u4eba\u5927\u5378\u516b\u5757\u4e86\uff0c\u60f3\u4e0d\u5230\u5c31\u5728\u90a3\u8282\u9aa8\u773c\u4e0a\uff0c\u534a\u7a7a\u91cc\u5ffd\u7136\u6389\u4e0b\u4e2a \u4eba\u6765\uff0c\u5c31\u597d\u50cf\u2026\u2026\u5c31\u597d\u50cf\u98de\u5c06\u519b\u81ea\u5929\u800c\u964d\u3002\u201d \u3000\u3000\u8fd9\u4e48\u597d\u7684\u4e00\u53e5\u201c\u8bcd\u513f\u201d\u5c45\u7136\u662f\u4ece\u81ea\u5df1\u5634\u91cc\u8bf4\u51fa\u672a\u7684\uff0c\u725b\u76ae\u5b9e\u5728\u5f97\u610f\u6781\u4e86\uff0c\u6240\u4ee5\u8d76\u7d27\u559d\u4e86 \u4e00\u5927\u7897\u9152\uff0c\u6545\u610f\u95ee\u90a3\u964c\u751f\u4eba\uff1a \u3000\u3000\u201c\u4f60\u731c\u731c\u770b\uff0c\u4ece\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u8001\u72ee\u5b50\uff1f\u201d \u3000\u3000\u725b\u76ae\u7528\u529b\u4e00\u62cd\u5927\u817f\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u5c31\u662f\u4ed6\u3002\u201d\u725b\u76ae\u8d8a\u8bf4\u8d8a\u8d77\u52b2\u3002 \u3000\u3000\u201c\u8001\u72ee\u4e8e\u5230\u5e95\u662f\u8001\u72ee\u5b50\uff0c\u6700\u8fd1\u8fd0\u6c14\u867d\u7136\u4e0d\u600e\u4e48\u597d\uff0c\u4eba\u4e5f\u7626\u5f97\u591a\u4e86\uff0c\u53ef\u662f\u4e00\u7ad9\u51fa\u6765\uff0c\u8fd8\u662f \u6761\u96c4\u72ee\u7684\u6a21\u6837\u3002\u201d \u3000\u3000\u725b\u76ae\u633a\u8d77\u80f8\uff0c\u62cd\u7740\u80f8\u812f\uff0c\u5b66\u7740\u6731\u731b\u7684\u53e3\u6c14\u8bf4\uff1a\u201c\u4ed6\u662f\u6211\u7684\u670b\u53cb\uff0c\u4f60\u4eec\u8c01\u6562\u52a8\u4ed6\uff0c\u5c31\u5f97\u5148 \u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u540e\u6765\u5462\uff1f\u201d\u964c\u751f\u4eba\u51b7\u51b7\u6de1\u6de1\u7684\u95ee\uff1a\u201c\u8521\u8001\u5927\u7684\u5144\u5f1f\u4eec\u96be\u9053\u5c31\u6ca1\u6709\u4eba\u6562\u53bb\u52a8\u4ed6\uff1f\u201d \u3000\u3000\u201c\u8c01\u6562\u52a8\uff0c\u8001\u72ee\u5b50\u7684\u72ee\u5a01\u4e00\u53d1\uff0c\u8fd8\u6709\u8c01\u6562\u52a8\uff1f\u201d \u3000\u3000\u725b\u76ae\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u672c\u6765\u771f\u7684\u662f\u6ca1\u4eba\u6562\u52a8\u7684\uff0c\u60f3\u4e0d\u5230\u5c45\u7136\u6709\u3002\u4e00\u6279\u4ece\u5916\u5730\u6765\u7684\u738b\u516b\u86cb \u5c45\u7136\u4e0d\u77e5\u9053\u6b7b\u6d3b\u597d\u6b79\uff0c\u5c45\u7136\u786c\u8981\u5728\u72ee\u5b50\u5934\u4e0a\u52a8\u571f\u3002\u201d \u3000\u3000\u201c\u4ece\u5916\u5730\u6765\u7684\u4eba\uff1f\u201d \u3000\u3000\u725b\u76ae\u70b9\u5934\uff1a\u201c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u90a3\u7fa4\u738b\u516b\u86cb\u90fd\u662f\u8521\u8001\u5927\u82b1\u94b1\u8bf7\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8521\u8001\u5927\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4ed6\u4eec\u5c31\u7b97\u5bb0\u4e86\u8001\u72ee\u5b50\uff0c\u4e5f\u6ca1\u4eba\u4ed8\u94b1\u8bf7\u4ed6\u4eec\u4e86\u3002\u201d\u964c\u751f\u4eba\u95ee\uff1a \u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u8981\u66ff\u6b7b\u4eba\u62fc\u547d\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5f53\u7136\u6709\u4ed6\u4eec\u7684\u6253\u7b97\u3002\u201d\u725b\u76ae\u5f97\u610f\u6d0b\u6d0b\uff1a\u201c\u4f60\u8001\u54e5\u867d\u7136\u60f3\u4e0d\u901a\uff0c\u4ffa\u5fc3\u91cc\u5374\u6709\u6570\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u8001\u54e5\u867d\u7136\u4e0d\u77e5\u9053\u8001\u72ee\u5b50\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u53ef\u662f\u4ffa\u77e5\u9053\uff0c\u90a3\u7fa4\u738b\u516b\u86cb\u4e00\u5b9a\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u77e5\u9053\u8001\u72ee\u5b50\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u7fa4\u738b\u516b\u86cb\u89c1\u94b1\u5c31\u6740\u4eba\uff0c\u4e24\u53ea\u624b\u4e0a\u90fd\u662f\u8840\u8165\uff0c\u53c8\u4e0d\u662f\u96c4\u72ee\u5802\u7684\u5144\u5f1f\uff0c\u8981\u662f\u8001\u72ee\u5b50\u91cd\u65b0 \u767b\u4e0a\u5802\u4e3b\u7684\u5b9d\u5ea7\uff0c\u8fd8\u80fd\u8ba9\u4ed6\u4eec\u7684\u8111\u888b\u957f\u5728\u8116\u5b50\u4e0a\u5417\uff1f\u201d \u3000\u3000\u201c\u6709\u7406\u3002\u201d\u964c\u751f\u4eba\u627f\u8ba4\uff1a\u201c\u4f60\u8bf4\u5f97\u6709\u7406\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4eec\u5982\u679c\u771f\u7684\u628a\u8001\u72ee\u5b50\u5bb0\u4e86\uff0c\u591a\u5c11\u603b\u80fd\u4ece\u8521\u8001\u5927\u7684\u624b\u4e0b\u90a3\u91cc\u69a8\u51fa\u70b9\u6cb9\u672c\u6765\u7684\u3002\u201d \u725b\u76ae\u8bf4\uff1a\u201c\u6240\u4ee5\u4ed6\u95e8\u5c31\u5e72\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5bf9\u4e8e\u8fd9\u4e48\u590d\u6742\u7684\u4e8b\u4ed6\u5c45\u7136\u4e5f\u80fd\u5206\u6790\u5f97\u8fd9\u4e48\u8fd9\u4e48\u6e05\u695a\uff0c\u725b\u76ae\u5b9e\u5728\u4e0d\u80fd\u4e0d\u4f69\u670d\u81ea\u5df1\uff0c\u6240\u4ee5\u7acb \u523b\u53c8\u559d\u4e86\u4e00\u5927\u7897\uff1a\u201c\u8fd9\u5c31\u53eb\u5148\u4e0b\u624b\u7684\u4e3a\u5f3a\uff0c\u540e\u4e0b\u624b\u7684\u906d\u6b83\u3002\u201d \u3000\u3000\u201c\u906d\u6b83\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\u4ffa\u4e5f\u770b\u4e0d\u51fa\u6765\u7684\u3002\u201d\u725b\u76ae\u8bf4\uff1a\u201c\u90a3\u4e00\u6218\u6253\u5f97\u662f\u60ca\u5929\u52a8\u5730\uff0c\u9b3c\u54ed\u795e\u53f7\uff0c\u8857\u4e0a\u7684\u4eba\u5341 \u4e2a\u91cc\u9762\u6700\u5c11\u6709\u516b\u4e2a\u88ab\u5413\u5f97\u8fde\u5c3f\u90fd\u5c3f\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u725b\u76ae\u81ea\u5df1\u773c\u4e2d\u4e5f\u9732\u51fa\u4e86\u6050\u60e7\u4e4b\u8272\uff0c\u4eff\u4f5b\u53c8\u770b\u89c1\u4e86\u4e00\u5927\u5757\u4e00\u5927\u5757\u7684\u8840\u8089\u6a2a\u98de\u800c\u8d77\uff0c\u53c8\u542c\u89c1 \u4e86\u5200\u70fd\u780d\u5728\u9aa8\u5934\u4e0a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u4ffa\u725b\u76ae\u4e5f\u4e0d\u662f\u8113\u5305\uff0c\u53ef\u662f\u81ea\u4ece\u770b\u8fc7\u90a3\u4e00\u6218\u4e4b\u540e\uff0c\u4ffa\u6700\u5c11\u4e5f\u6709\u4e24\u4e09\u5929\u5403\u4e0d\u4e0b\u996d\u7761\u4e0d\u7740 \u89c9\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5df2\u7ecf\u53d1\u54d1\uff0c\u597d\u50cf\u5df2\u7ecf\u4e0d\u8d77\u518d\u8bf4\u4e0b\u53bb\u4e86\uff0c\u53ef\u662f\u964c\u751f\u4eba\u53c8\u53ca\u65f6\u66ff\u4ed6\u6dfb\u4e86\u4e00\u5927\u7897\u9152\u3002 \u3000\u3000\u8fd9\u7897\u9152\u7acb\u523b\u628a\u4ed6\u7684\u5174\u81f4\u63d0\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u4e00\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u672c\u6765\u662f\u8001\u72ee\u5b50\u548c\u90a3\u59d3\u9ad8\u7684\u5c0f\u5b50\u5360\u4e0a\u98ce\u7684\uff0c\u53ef\u662f\u540e\u6765\u5c31\u4e0d\u5bf9\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5e38\u8a00\u8bf4\u5f97\u597d\uff0c\u53cc\u62f3\u62b5\u4e0d\u8fc7\u56db\u624b\uff0c\u597d\u6c49\u67b6\u4e0d\u4f4f\u4eba\u591a\uff0c\u8001\u72ee\u5b50\u867d\u7136\u96c4\u98ce\u4e0d\u51cf\uff0c\u53ef\u662f\u5230\u5e95\u53ea \u6709\u4e24\u4e2a\u4eba\uff0c\u5c31\u7b97\u522b\u4eba\u4f38\u51fa\u8116\u5b50\u6765\u8ba9\u4ed6\u4eec\u780d\uff0c\u4ed6\u4eec\u7684\u624b\u8fdf\u65e9\u4e5f\u4f1a\u780d\u9178\u7684\u3002\u201d \u3000\u3000\u725b\u76ae\u53c8\u8bf4\uff1a\u201c\u770b\u5230\u8fd9\u79cd\u60c5\u51b5\uff0c\u672c\u6765\u5df2\u7ecf\u88ab\u8001\u72ee\u5b50\u5a01\u98ce\u9707\u4f4f\u7684\u90a3\u4e9b\u96c4\u72ee\u5802\u7684\u5f1f\u5144\uff0c\u597d\u50cf\u4e5f \u60f3\u52a8\u4e86\uff0c\u60f3\u4e58\u673a\u672a\u6253\u4e00\u6253\u8fd9\u5934\u843d\u6c34\u72ee\u5b50\u3002\u201d \u3000\u3000\u964c\u751f\u4eba\u5728\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u7684\u60f3\u6cd5\u4e5f\u5982\u6b64\uff0c\u5f53\u65f6\u7684\u60c5\u51b5\u4e00\u5b9a\u4f1a\u6f14\u53d8\u6210\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u201c\u53ea\u8981\u90a3\u4e9b\u4eba\u4e00\u52a8\uff0c\u8001\u72ee\u5b50\u548c\u90a3\u59d3\u9ad8\u7684\u6050\u6015\u5c31\u8981\u88ab\u5241\u6210\u8089\u9171\u3002\u201d \u3000\u3000\u725b\u76ae\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u90a3\u65f6\u5019\u4ffa\u5df2\u5e0c\u671b\u4ed6\u4eec\u80fd\u8d76\u5feb\u8dd1\u6389\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u662f\u6ca1\u6709\u673a\u4f1a\u8dd1\uff0c\u8981\u662f\u6362 \u4e86\u4ffa\u725b\u76ae\uff0c\u65e9\u5c31\u4e0d\u77e5\u9053\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u72ee\u5b50\u6ca1\u6709\u8dd1\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6ca1\u6709\u8dd1\u3002\u201d\u725b\u76ae\u53c8\u633a\u8d77\u80f8\uff1a\u201c\u8001\u72ee\u5b50\u662f\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u4ed6\u53c8\u4e0d\u662f\u4ffa\u725b\u76ae\u8fd9\u6837\u7684\u65e0\u540d \u5c0f\u5352\uff0c\u4ee5\u4ed6\u7684\u8eab\u4efd\u548c\u813e\u6c14\uff0c\u6740\u4e86\u4ed6\u4ed6\u4e5f\u4e0b\u4f1a\u8dd1\u7684\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u6ca1\u6709\u8dd1\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u77e5\u9053\u4ed6\u4e5f\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u201c\u4ed6\u5f53\u7136\u6ca1\u6709\u6b7b\uff0c\u8001\u72ee\u5b50\u600e\u4e48\u4f1a\u6b7b\u5f97\u4e86\u3002\u201d\u725b\u76ae\u53f9\u606f\uff1a\u201c\u53ef\u662f\u9489\u978b\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u9489\u978b\uff1f\u201d\u964c\u751f\u4eba\u95ee\uff1a\u201c\u9489\u978b\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u6761\u597d\u6c49\uff0c\u4e86\u4e0d\u8d77\u7684\u597d\u6c49\uff0c\u201d\u725b\u76ae\u7684\u8138\u56e0\u5174\u594b\u800c\u53d1\u7ea2\uff1a\u201c\u4ffa\u725b\u76ae\u8fd9\u4e00\u8f88\u5b50\u90fd\u6ca1\u6709\u89c1\u8fc7 \u4ed6\u90a3\u6837\u7684\u597d\u6c49\uff0c\u8981\u662f\u4ed6\u4e0d\u6b7b\uff0c\u4ffa\u725b\u76ae\u60c5\u613f\u6bcf\u5929\u66ff\u4ed6\u6d17\u811a\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f46\u4ffa\u4f69\u670d\u4ed6\uff0c\u53ea\u8981\u662f\u4e2a\u4eba\uff0c\u5c31\u4e0d\u80fd\u4e0d\u4f69\u670d\u4ed6\u3002\u201d\u725b\u76ae\u8bf4\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u964c\u751f\u4eba\u53c8\u95ee\u3002 \u3000\u3000\u201c\u4ed6\u672c\u6765\u53ea\u4e0d\u8fc7\u662f\u8001\u72ee\u5b50\u7684\u4e00\u4e2a\u8ddf\u73ed\u800c\u5df2\uff0c\u5e73\u5e38\u770b\u8d77\u6765\u5c31\u50cf\u662f\u4e2a\u5b59\u5b50\u4e00\u6837\uff0c\u8001\u662f\u88ab\u4eba\u6b3a \u8d1f\u3002\u201d\u725b\u76ae\u6da8\u7ea2\u4e86\u8138\uff1a\u201c\u53ef\u662f\u5230\u73b0\u5728\u4ffa\u624d\u77e5\u9053\uff0c\u5e73\u65f6\u5728\u4ed6\u9762\u524d\u5145\u82f1\u96c4\u7684\u90a3\u4e9b\u4e2a\u4eba\u624d\u662f\u9f9f\u5b59 \u5b50\uff0c\u4ed6\u624d\u662f\u771f\u6b63\u7684\u82f1\u96c4\u597d\u6c49\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u4e2a\u4eba\uff0c\u725b\u76ae\u5168\u8eab\u7684\u8840\u597d\u50cf\u5168\u90fd\u70ed\u4e86\u8d77\u6765\uff0c\u4e00\u628a\u626f\u5f00\u4e86\u8eab\u4e0a\u90a3\u4ef6\u7834\u68c9\u8884\u7684\u8863\u895f\uff0c\u5927 \u58f0\u8bf4\uff1a\u201c\u90a3\u5929\u4ffa\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u5168\u8eab\u4e0a\u4e0b\u4e00\u5171\u88ab\u4eba\u780d\u4e86\u5341\u4e5d\u5200\uff0c\u8fde\u9f3b\u5b50\u90e8\u88ab\u780d\u6389\u4e00\u5927\u534a\uff0c \u53ea\u5269\u4e0b\u4e00\u5c42\u76ae\u642d\u62c9\u7740\u6302\u5728\u8138\u4e0a\uff0c\u53ea\u8981\u4ed6\u4e00\u52a8\uff0c\u6302\u5728\u8138\u4e0a\u7684\u90a3\u5927\u534a\u4e2a\u9f3b\u5b50\u5c31\u8ddf\u7740\u4ed6\u76f4\u6643\u3002\u201d \u3000\u3000\u201c\u4ed6\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5c31\u7d22\u6027\u628a\u9f3b\u5b50\u8fde\u76ae\u5e26\u8089\u626f\u4e86\u4e0b\u6765\uff0c\u4e00\u53e3\u541e\u4e0b\u4e86\u809a\u5b50\u3002\u53cd\u624b\u4e00\u5200\u3002\u53c8\u62fc\u6389\u4e00\u4e2a\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u4e00\u76f4\u8868\u73b0\u5f97\u5f88\u51b7\u6de1\u7684\u964c\u751f\u4eba\u4e5f\u4e0d\u7981\u559d\u4e86\u4e00\u7897\u9152\uff0c\u5927\u58f0\u8d5e\u9053\uff1a\u201c\u597d\u6c49\uff0c\u679c\u7136\u662f \u597d\u6c49\u3002\u201d \u3000\u3000\u725b\u76ae\u7528\u529b\u4e00\u62cd\u684c\u5b50\uff1a\u201c\u53ef\u60dc\u8fd9\u4e48\u6837\u4e00\u6761\u597d\u6c49\u540e\u6765\u8fd8\u662f\u529b\u7aed\u6218\u6b7b\u4e86\uff0c\u76f4\u5230\u4e24\u6761\u624b\u81c2\u4e00\u6761\u817f \u90fd\u5df2\u88ab\u780d\u65ad\u7684\u65f6\u5019\u624d\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u4e0b\u53bb\u7684\u65f6\u5019\u5634\u91cc\u8fd8\u542b\u7740\u4ece\u522b\u4eba\u8eab\u4e0a\u54ac\u4e0b\u672a\u7684\u4e00\u5757\u8089\u3002\u201d \u3000\u3000\u201c\u540e\u6765\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u770b\u5230\u4ed6\u8fd9\u4e48\u82f1\u52c7\u60e8\u70c8\u82e6\u6218\u6b7b\u6218\uff0c\u4ffa\u4eec\u8fd9\u4e9b\u4eba\u90fd\u770b\u5f97\u5fcd\u4e0d\u4f4f\u8981\u54ed\u51fa\u6765\uff0c\u5c31\u8fde\u90a3\u4e9b\u672c\u6765\u8fd8 \u60f3\u4f5c\u4e71\u7684\u96c4\u72ee\u5802\u5144\u5f1f\uff0c\u4e5f\u88ab\u4ed6\u611f\u52a8\u5f97\u6389\u4e0b\u773c\u6cea\u3002\u201d \u3000\u3000\u725b\u76ae\u53c8\u8bf4\u3002\u201c\u8001\u72ee\u5b50\u6ca1\u6709\u6d41\u6cea\uff0c\u8001\u72ee\u5b50\u6d41\u7684\u662f\u8840\uff0c\u4ed6\u7684\u773c\u89d2\u90fd\u8ff8\u88c2\u4e86\uff0c\u9c9c\u8840\u50cf\u773c\u6cea\u4e00\u6837 \u4e0d\u505c\u7684\u4f4f\u4e0b\u6389\uff0c\u867d\u7136\u4e5f\u5df2\u7ecf\u5feb\u8981\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u4f46\u662f\u594b\u8d77\u6700\u540e\u7684\u795e\u529b\uff0c\u6740\u51fa\u4e00\u6761\u8840\u8def\u51b2\u5230\u9489\u978b \u8eab\u8fb9\uff0c\u62b1\u8d77\u4e86\u4ed6\u8fd9\u4e2a\u4e00\u76f4\u50cf\u72d7\u4e00\u6837\u8ddf\u7740\u4ed6\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u7528\u529b\u64e4\u4e86\u4e00\u5927\u628a\u9f3b\u6d95\uff0c\u64e6\u5e72\u4e86\u8138\u4e0a\u7684\u6cea\u75d5\uff0c\u773c\u6cea\u6c6a\u6c6a\u7684\u63a5\u7740\u9053\uff1a\u201c\u90a3\u65f6\u5019\u9489\u978b\u8fd8\u6ca1\u6709 \u6b7b\uff0c\u8fd8\u5269\u4e0b\u6700\u540e\u4e00\u53e3\u6c14\u3002\u201d \u3000\u3000\u8840\u6d17\u957f\u8857\uff0c\u5c0f\u9ad8\u4ecd\u5728\u82e6\u6218\u3002 \u3000\u3000\u6731\u731b\u62b1\u8d77\u4e86\u9489\u978b\uff0c\u60f3\u8bf4\u8bdd\uff0c\u5374\u8fde\u4e00\u4e2a\u5b57\u90fd\u8bf4\u4e0d\u51fa\uff0c\u4ece\u773c\u89d2\u8fdb\u51fa\u7684\u9c9c\u8840\u4e00\u6ef4\u6ef4\u6389\u5728\u9489\u978b\u8138 \u4e0a\u3002 \u3000\u3000\u9489\u978b\u5ffd\u7136\u7741\u5f00\u4e86\u5df2\u7ecf\u88ab\u9c9c\u8840\u6a21\u7cca\u4e86\u7684\u4e00\u53ea\u773c\u775b\uff0c\u8bf4\u51fa\u4e86\u4e34\u6b7b\u524d\u6700\u540e\u4e00\u53e5\u6d3b\uff0c \u3000\u3000\u201c\u62a5\u544a\u5802\u4e3b\uff0c\u5c0f\u4eba\u4e0d\u80fd\u518d\u4f8d\u4faf\u5802\u4e3b\u4e86\u3002\u201d\u9489\u978b\u8bf4\uff1a\u201c\u5c0f\u4eba\u8981\u6b7b\u4e86\u3002\u201d \u3000\u3000\u51b7\u98ce\u4e00\u76f4\u5439\u4e2a\u4e0d\u505c\uff0c\u628a\u9992\u5934\u5e97\u5916\u5c4b\u6a90\u4e0a\u7684\u79ef\u96ea\u4e00\u5927\u7247\u4e00\u5927\u7247\u7684\u5439\u4e0b\u6765\uff0c\u725b\u76ae\u8138\u4e0a\u7684\u773c\u6cea \u4e5f\u4e00\u76f4\u4e00\u5927\u6ef4\u4e00\u5927\u6ef4\u7684\u5f80\u4e0b\u6389\u3002 \u3000\u3000\u964c\u751f\u4eba\u6ca1\u6709\u6d41\u6cea\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ef\u662f\u53cc\u62f3\u4e5f\u5df2\u63e1\u7d27\uff0c\u4eff\u4f5b\u5728\u5c3d\u529b\u63a7\u5236\u4ed6\u81ea\u5df1\uff0c\u751f\u6015\u81ea\u5df1 \u6709\u6cea\u6d41\u4e0b\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u725b\u76ae\u624d\u80fd\u5f00\u53e3\u3002 \u3000\u3000\u201c\u9489\u978b\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\u5c31\u65ad\u6c14\u4e86\uff0c\u53ef\u662f\u90a3\u6765\u8857\u5ffd\u7136\u54cd\u8d77\u4e86\u4e00\u9635\u96f7\u4e00\u6837\u7684\u5927\u543c\u58f0\uff0c\u975e\u4f46\u96c4\u72ee \u5802\u7684\u5144\u5f1f\u4eec\u518d\u4e5f\u618b\u4e0d\u4f4f\uff0c\u8fde\u4ffa\u4e5f\u618b\u4e0d\u4f4f\u4e86\u3002\u201d\u725b\u76ae\u5927\u58f0\u8bf4\uff1a\u201c\u5ffd\u7136\u95f4\u5927\u5bb6\u5168\u90fd\u4e00\u4e0b\u5b50\u51b2\u4e86\u4e0a \u53bb\uff0c\u628a\u90a3\u7fa4\u6ee1\u624b\u8840\u8165\u7684\u738b\u516b\u86cb\u5bb0\u4e86\u4e2a\u5e72\u51c0\uff0c\u8fde\u4ffa\u725b\u76ae\u90fd\u5bb0\u4e86\u4ed6\u4eec\u51e0\u5200\u3002\u201d \u3000\u3000\u8fd9\u65f6\u964c\u751f\u4eba\u5ffd\u7136\u4e5f\u7528\u529b\u4e00\u62cd\u684c\u5b50\uff1a\u201c\u597d\uff0c\u5bb0\u5f97\u597d\u3002\u201d\u4ed6\u6ee1\u6ee1\u5012\u4e86\u4e00\u5927\u7897\u9152\uff1a\u201c\u6211\u53f8\u9a6c\u8d85 \u7fa4\u59a5\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u201c\u5f53\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u725b\u76ae\u624b\u91cc\u7684\u4e00\u7897\u9152\u6dd6\u5728\u5730\u4e0a\uff0c\u7838\u5f97\u7c89\u788e\u3002 \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u201d\u4ed6\u5403\u60ca\u7684\u770b\u770b\u8fd9\u4e2a\u964c\u4e3b\u4eba\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8bf4\u6211\u8981\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u521a\u624d\u8bf4\u662f\u8c01\u8981\u656c\u6211\u4e00\u676f\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u53eb\u53f8\u9a6c\u8d85\u7fa4\u7684\u5c0f\u5b50\u3002\u201d \u3000\u3000\u201c\u4f60\u5c31\u662f\u53f8\u9a6c\u8d85\u7fa4\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u662f\u3002\u201d \u3000\u3000\u725b\u76ae\u6574\u4e2a\u4eba\u5ffd\u7136\u53d8\u8f6f\u4e86\uff0c\u597d\u50cf\u5df2\u7ecf\u5feb\u8981\u8f6f\u5728\u5730\u4e0a\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7684\u8bf4\uff1a\u201c\u4eba\u4e0d\u77e5\u9053\u5927\u7237\u5c31\u662f \u5929\u4e0b\u7b2c\u4e00\u6761\u597d\u6c49\u53f8\u9a6c\u5927\u7237\uff0c\u5c0f\u4eba\u4e0d\u6562\u8981\u5927\u7237\u656c\u9152\u3002\u201d \u3000\u3000\u201c\u6211\u8981\u656c\u4f60\uff0c\u4e00\u5b9a\u8981\u656c\u4f60\uff0c\u56e0\u4e3a\u4f60\u4e5f\u662f\u6761\u6709\u8840\u6027\u7684\u597d\u6c49\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u5176\u5b9e\u6211\u656c\u4f60\u4e00\u676f \u8fd8\u4e0d\u591f\uff0c\u6211\u8981\u656c\u4f60\u4e00\u575b\u3002\u201d \u3000\u3000\u4ed6\u771f\u7684\u7528\u53cc\u624b\u6367\u8d77\u4e00\u575b\uff0c\u575b\u53e3\u5bf9\u7740\u5634\uff0c\u4ef0\u8d77\u8116\u5b50\u559d\u4e86\u4e0b\u53bb\uff0c\u4ef0\u5929\u957f\u957f\u53f9\u606f\uff1a\u201c\u5929\u4e0b\u6c5f\u6e56 \u670b\u53cb\u90fd\u8bf4\u6211\u662f\u5f53\u4e16\u65e0\u53cc\u7684\u82f1\u96c4\uff0c\u5176\u5b9e\u6211\u600e\u4e48\u6bd4\u5f97\u4e0a\u9489\u978b\uff0c\u600e\u4e48\u6bd4\u5f97\u4e0a\u672a\u731b\uff1f\u201d \u3000\u3000\u5916\u9762\u7684\u98ce\u5439\u5f97\u66f4\u6025\u3001\u66f4\u51b7\u3002 \u3000\u3000\u73b0\u5728\u867d\u7136\u5df2\u7ecf\u662f\u4e8c\u6708\uff0c\u53ef\u662f\u6625\u5929\u8ddd\u79bb\u6d1b\u9633\u4eff\u4f5b\u4ecd\u7136\u5f88\u8fdc\u3002 \u7b2c\u5341\u4e00\u7ae0\u3000\u516b\u5341\u516b\u6b7b\u58eb>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u5341\u4e00\u7ae0\u3000\u516b\u5341\u516b\u6b7b\u58eb \u4e00 \u3000\u3000\u4e8c\u6708\u4e8c\u5341\u4e8c\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u51cc\u6668\u3002 \u3000\u3000\u5929\u7a7a\u662f\u6b7b\u7070\u8272\u7684\uff0c\u5927\u5730\u4e5f\u662f\u6b7b\u7070\u8272\u7684\uff0c\u5efa\u7b51\u5b8f\u4f1f\u7684\u957f\u5b89\u53e4\u57ce\u57ce\u95e8\u8fd8\u6ca1\u6709\u5f00\u3002 \u3000\u3000\u6bcf\u5929\u8d1f\u8d23\u5f00\u57ce\u95e8\u7684\u5175\u5352\u8001\u9ec4\u548c\u963f\u91d1\uff0c\u6628\u5929\u6740\u4e86\u6761\u91ce\u72d7\uff0c\u51d1\u94b1\u4e70\u4e86\u4e24\u65a4\u70e7\u5200\u5b50\uff0c\u4e24\u5385\u5927 \u997c\uff0c\u5403\u4e86\u4e2a\u9152\u8db3\u996d\u9971\uff0c\u65e9\u4e0a\u5c31\u722c\u4e0d\u8d77\u5e8a\u4e86\u3002 \u3000\u3000\u6020\u5ffd\u804c\u5b88\uff0c\u803d\u8bef\u4e86\u5f00\u57ce\u7684\u65f6\u523b\uff0c\u90a3\u662f\u8981\u5904\u201c\u65a9\u7acb\u51b3\u201d\u7684\u6b7b\u7f6a\u3002 \u3000\u3000\u519b\u6cd5\u5982\u5c71\uff0c\u8001\u9ec4\u8d77\u5e8a\u65f6\u53d1\u73b0\u65f6\u5019\u5df2\u7ecf\u665a\u4e86\u5927\u534a\u523b\uff0c\u5f53\u65f6\u5c31\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u8fde\u68c9\u8884\u7684\u94ae \u6263\u90fd\u6765\u4e0d\u53ca\u6263\u4e0a\uff0c\u5c31\u8d76\u53bb\u5f00\u57ce\u3002 \u3000\u3000\u201c\u5929\u6c14\u8fd9\u4e48\u51b7\uff0c\u5927\u6982\u4e0d\u4f1a\u6709\u4eba\u8fd9\u4e48\u65e9\u8fdb\u57ce\u7684\u3002\u201d \u3000\u3000\u8001\u9ec4\u5728\u5fc3\u91cc\u5b89\u6170\u81ea\u5df1\uff0c\u6253\u5f00\u4e86\u95e8\u4e0a\u7684\u5927\u94c1\u9501\uff0c\u521a\u628a\u57ce\u95e8\u63a8\u5f00\u4e86\u4e00\u7ebf\uff0c\u5c31\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u5916\u9762\u4e0d\u4f46\u5df2\u7ecf\u6709\u4eba\u5728\u7b49\u7740\u8fdb\u57ce\uff0c\u800c\u4e14\u770b\u8d77\u6765\u6700\u5c11\u4e5f\u6709\u4e03\u516b\u5341\u4f4d\u3002 \u3000\u3000\u4e03\u516b\u5341\u4e2a\u4eba\u90fd\u7a7f\u7740\u4e00\u8eab\u52b2\u88c5\uff0c\u6253\u7740\u5012\u8d76\u5343\u5c42\u6d6a\u7684\u7ed1\u817f\uff0c\u80cc\u540e\u90fd\u80cc\u7740\u9b3c\u5934\u5200\uff0c\u5934\u4e0a\u90fd\u624e\u7740 \u767d\u5e03\u4e2d\uff0c\u4e0a\u9762\u8fd8\u7f1d\u7740\u4e00\u5757\u6697\u8d64\u8272\u7684\u788e\u5e03\u3002\u6bcf\u4e2a\u4eba\u7684\u8138\u8272\u90fd\u50cf\u662f\u4eca\u5929\u7684\u5929\u6c14\u4e00\u6837\uff0c\u5e26\u7740\u79cd\u53eb\u4eba \u5fc3\u91cc\u53d1\u6bdb\u7684\u6740\u6c14\u3002 \u3000\u3000\u57ce\u95e8\u4e00\u5f00\uff0c\u8fd9\u4e9b\u4eba\u5c31\u5206\u6210\u4e86\u4e24\u884c\uff0c\u9ed8\u9ed8\u7684\u8d70\u8fdb\u4e86\u57ce\uff0c\u5200\u4e0a\u7684\u8840\u7ea2\u5200\u8863\u8fce\u98ce\u98d8\u52a8\uff0c\u886c\u7740\u5934 \u4e0a\u624e\u7740\u7684\u767d\u5dfe\uff0c\u96ea\u4eae\u7684\u5200\u950b\u95ea\u7740\u5bd2\u5149\u3002 \u3000\u3000\u6bcf\u628a\u5200\u90fd\u5df2\u51fa\u9798\uff0c\u56e0\u4e3a\u5200\u4e0a\u6839\u672c\u6ca1\u6709\u9798\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e9b\u6740\u6c14\u817e\u817e\u7684\u5927\u5974\u7a76\u7adf\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u5230\u957f\u5b89\u6765\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u5b88\u57ce\u7684\u8001\u9ec4\u804c\u8d23\u6240\u5728\uff0c\u672c\u6765\u60f3\u62e6\u4f4f\u4ed6\u4eec\u76d8\u95ee\uff0c\u53ef\u662f\u820c\u5934\u5374\u50cf\u662f\u5ffd\u7136\u53d1\u786c\u4e86\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd \u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4e00\u6761\u53cd\u7a7f\u7740\u718a\u76ae\u8884\u7684\u5927\u6c49\u5df2\u51fa\u73b0\u5728\u4ed6\u773c\u524d\uff0c\u7528\u4e00\u53cc\u6ee1\u5e03\u8840\u4e1d\u7684\u5927\u773c\u77aa \u7740\u4ed6\uff0c\u4eba\u867d\u7136\u5df2\u7ecf\u7626\u5f97\u8131\u4e86\u5f62\uff0c\u53ef\u662f\u98a7\u9aa8\u9ad8\u8038\uff0c\u773c\u9510\u5982\u5200\uff0c\u770b\u6765\u8fd8\u662f\u5a01\u98ce\u51db\u51db\uff0c\u5c31\u50cf\u662f\u6761\u521a \u4ece\u6df1\u5c71\u4e2d\u7a9c\u51fa\u7684\u731b\u517d\u3002 \u3000\u3000\u4ed6\u7684\u6ee1\u5934\u4e71\u53d1\u4e5f\u7528\u4e00\u6761\u767d\u5e03\u4e2d\u7d27\u7d27\u624e\u4f4f\uff0c\u4e0a\u9762\u6709\u5757\u6697\u8d64\u8272\u7684\u788e\u5e03\u3002 \u3000\u3000\u552f\u4e00\u88c5\u675f\u6253\u626e\u548c\u4ed6\u4eec\u4e0d\u540c\u7684\u4eba\uff0c\u662f\u4e2a\u6e05\u4fca\u7626\u524a\u7684\u5e74\u8f7b\u4eba\uff0c\u624b\u63d0\u770b\u72ed\u957f\u7684\u9752\u65b9\u5305\u88b1\uff0c\u7d27\u968f \u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u8001\u9ec4\u7684\u817f\u5df2\u7ecf\u53d1\u8f83\u4e86\uff0c\u65e0\u8bba\u8c01\u90fd\u770b\u5f97\u51fa\u8fd9\u4e2a\u4eba\u8981\u6740\u4eba\u65f6\u7edd\u4e0d\u4f1a\u76b1\u4e00\u76b1\u7709\u5934\u3002 \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u76d8\u95ee\u76d8\u95ee\u6211\u4eec\uff0c\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u58f0\u97f3\u867d\u7136\u5636\u54d1\uff0c\u53ef\u662f\u53e3\u6c14\u4e2d\u4e5f\u5e26\u7740\u79cd\u6151\u5165\u7684\u5a01\u4e25\u6c14\u6982\u3002 \u3000\u3000\u201c\u4f60\u542c\u7740\uff0c\u597d\u597d\u7684\u542c\u7740\uff0c\u6211\u5c31\u662f\u6731\u731b\uff0c\u6d1b\u9633\u6731\u731b\u3002\u201d\u4ed6\u5389\u58f0\u9053\uff1a\u201c\u6211\u4eec\u662f\u5230\u957f\u5b89\u6765\u6b7b \u7684\u3002\u201d \u4e8c \u3000\u3000\u5353\u4e1c\u6765\u7684\u8138\u571f\u672c\u6765\u5c31\u6ca1\u6709\u4ec0\u4e48\u8868\u60c5\uff0c\u73b0\u5728\u66f4\u597d\u50cf\u5df2\u7ecf\u88ab\u51bb\u7ed3\u4e86\uff0c\u8138\u4e0a\u6bcf\u4e00\u6839\u808c\u8089\u90fd\u88ab\u51bb \u7ed3\u4e86\u3002\u5982\u679c\u4f60\u66fe\u7ecf\u770b\u5230\u8fc7\u51bb\u6b7b\u5728\u51b0\u4e2d\u7684\u6b7b\u4eba\u7684\u8138\uff0c\u4f60\u624d\u80fd\u60f3\u8c61\u5230\u4ed6\u73b0\u5728\u7684\u8138\u8272\u548c\u795e\u60c5\u3002 \u3000\u3000\u4e00\u4e2a\u5e74\u7eaa\u8fd8\u4e0d\u6ee1\u4e8c\u5341\u7684\u5c11\u5e74\u4eba\u6807\u67aa\u8231\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u770b\u6765\u5c45\u7136\u8ddf\u4ed6\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u8fd9\u4f4d\u5c11\u5e74\u4eba\u53eb\u5353\u9752\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5e76\u4e0d\u59d3\u5353\uff0c\u4ed6\u59d3\u90ed\uff0c\u662f\u6b7b\u5728\u7ea2\u82b1\u6848\u7684\u90ed\u5e84\u7684\u5e7c\u5f1f\u3002 \u3000\u3000\u53ef\u662f\u81ea\u4ece\u5353\u4e1c\u6765\u5c06\u4ed6\u6536\u4e3a\u4e49\u5b50\u540e\uff0c\u4ed6\u7acb\u523b\u5c31\u628a\u672c\u6765\u7684\u59d3\u540d\u5fd8\u8bb0\u4e86\u3002 \u3000\u3000\u201c\u6731\u731b\u5df2\u5165\u57ce\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u6d88\u606f\u5c31\u662f\u4ed6\u62a5\u4e0a\u6765\u7684\uff0c\u67e5\u51fa\u6c34\u6c9f\u6bcf\u5929\u90fd\u6709\u836f\u6c41\u6d41\u51fa\u7684\u4eba\u4e5f\u662f\u4ed6\u3002 \u3000\u3000\u6700\u8fd1\u4ed6\u4e3a\u5353\u4e1c\u6765\u505a\u7684\u4e8b\uff0c\u8fdc\u6bd4\u5353\u4e1c\u6765\u5c5e\u4e0b\u6240\u6709\u7684\u4eb2\u4fe1\u52a0\u8d77\u6765\u90fd\u591a\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u6765\u4e86\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u201c\u8fde\u9ad8\u6e10\u98de\u5728\u5185\uff0c\u4e00\u5171\u6709\u516b\u5341\u516b\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eb2\u53e3\u544a\u8bc9\u5b88\u57ce\u7684\u8001\u9ec4\uff0c\u4ed6\u5c31\u662f\u6731\u731b\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4ed6\u8fd8\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u4ed6\u4eec\u662f\u5230\u957f\u5b89\u6765\u6b7b\u7684\uff01\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u77b3\u5b54\u9aa4\u7136\u6536\u7f29\uff0c\u770b\u8d77\u6765\u4eff\u4f5b\u5df2\u53d8\u6210\u4e86\u4e24\u628a\u9525\u5b50\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u4e0d\u662f\u5230\u957f\u5b89\u6765\u6740\u4eba\u7684\uff1f\u4ed6\u4eec\u662f\u5230\u957f\u5b89\u6765\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u5f88\u597d\u3002\u201d\u5353\u4e1c\u6765\u7684\u773c\u89d2\u5ffd\u7136\u5f00\u59cb\u8df3\u52a8\uff1a\u201c\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u8ba4\u5f97\u5353\u4e1c\u6765\u7684\u4eba\u90fd\u77e5\u9053\u53ea\u6709\u5728\u4e8b\u6001\u6700\u4e25\u91cd\u65f6\u4ed6\u7684\u773c\u89d2\u624d\u4f1a\u8df3\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u773c\u89d2\u5f00\u59cb\u8df3\u52a8\uff0c\u56e0\u4e3a\u4ed6\u5df2\u770b\u51fa\u4e86\u5bf9\u65b9\u6765\u7684\u5e76\u4e0d\u662f\u516b\u5341\u516b\u4e2a\u4eba\uff0c\u800c\u662f\u516b\u767e\u516b\u5341 \u4e2a\u3002 \u3000\u3000\u2014\u2014\u6765\u6740\u4eba\u7684\u4eba\u4e0d\u53ef\u6015\uff0c\u6765\u6b7b\u7684\u4eba\u624d\u53ef\u6015\uff0c\u8fd9\u79cd\u4eba\u4e00\u4e2a\u5c31\u53ef\u4ee5\u6bd4\u5f97\u4e0a\u5341\u4e2a\u3002 \u3000\u3000\u201c\u4f60\u628a\u4ed6\u4eec\u7684\u6253\u626e\u518d\u8bf4\u4e00\u904d\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u90fd\u7a7f\u52b2\u88c5\uff0c\u6253\u88f9\u817f\uff0c\u624e\u767d\u5dfe\uff0c\u767d\u5dfe\u4e0a\u8fd8\u7f1d\u7740\u6761\u6697\u8d64\u9c7c\u7684\u788e\u5e02\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u7b11\u3002 \u3000\u3000\u201c\u597d\uff0c\u597d\u6781\u4e86\u3002\u201d\u4ed6\u95ee\u5353\u9752\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u4e9b\u788e\u5e03\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u5b9a\u662f\u9489\u978b\u7684\u8840\u8863\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff0c\u201c\u9489\u978b\u6b7b\u65f6\uff0c\u8863\u886b\u5df2\u5c3d\u88ab\u9c9c\u8840\u67d3\u7ea2\u3002\u201d \u3000\u3000\u6d1b\u9633\u5df1\u6709\u4eba\u6765\uff0c\u5411\u5353\u4e1c\u6765\u62a5\u544a\u4e86\u90a3\u4e00\u6b21\u8840\u6218\u7684\u5168\u90e8\u7ecf\u8fc7\u3002 \u3000\u3000\u201c\u96c4\u72ee\u5802\u672c\u6765\u5df2\u7ecf\u53d8\u6210\u4e86\u4e00\u76d8\u6563\u6c99\uff0c\u53ef\u662f\u9489\u978b\u7684\u8840\u53c8\u628a\u8fd9\u76d8\u6563\u6c99\u7ed3\u5728\u4e00\u8d77\u4e86\u3002\u201d\u5353\u4e1c\u6765 \u7684\u58f0\u97f3\u91cc\u5c45\u7136\u4e5f\u6709\u4e86\u611f\u60c5\uff0c\u201c\u9489\u978b\uff0c\u597d\uff0c\u597d\u9489\u978b\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u5353\u9752\u8bf4\uff1a\u201c\u9489\u978b\u4e0d\u597d\u770b\uff0c\u9489\u978b\u4e5f\u5f88\u4fbf\u5b9c\uff0c\u5e73\u65f6\u867d\u7136\u6bd4\u4e0d\u4e0a\u522b\u7684\u978b\u5b50\uff0c\u53ef\u662f\u5230 \u4e86\u4e0b\u96e8\u4e0b\u96ea\u6ce5\u6cde\u6ee1\u8def\u65f6\uff0c\u5c31\u53ea\u6709\u9489\u978b\u624d\u662f\u6700\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5f88\u5e73\u6de1\uff0c\u56e0\u4e3a\u4ed6\u53ea\u4e0d\u8fc7\u662f\u5728\u53d9\u8bf4\u4e00\u4ef6\u4e8b\u5b9e\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u4e0d\u662f\u5bb9\u6613\u52a8\u611f\u60c5\u7684\u4eba\u3002 \u3000\u3000\u5353\u4e1c\u672a\u51dd\u89c6\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u5ffd\u7136\u505a\u51fa\u4ef6\u4efb\u4f55\u4eba\u90fd\u60f3\u4e0d\u5230\u4ed6\u4f1a\u505a\u51fa\u6765\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u8d70\u8fc7\u53bb\u62b1\u4f4f\u4e86\u5353\u9752\uff0c\u867d\u7136\u53ea\u4e0d\u8fc7\u8f7b\u8f7b\u7684\u62b1\u4e86\u4e00\u4e0b\u3002\u5374\u5df2\u7ecf\u662f\u4ed6\u5e73\u751f\u7b2c\u4e00 \u6b21\u3002 \u3000\u3000\u2014\u2014\u9664\u4e86\u53f8\u9a6c\u8d85\u7fa4\u5916\uff0c\u7b2c\u4e00\u6b21\u5bf9\u4e00\u4e2a\u7537\u4eba\u5982\u6b64\u4eb2\u8fd1\u3002 \u3000\u3000\u5353\u9752\u867d\u7136\u8fd8\u662f\u6807\u67aa\u822c\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u773c\u4e2d\u5374\u4f3c\u5df2\u6709\u70ed\u6cea\u6ee1\u7736\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5374\u597d\u50cf\u6ca1\u6709\u6ce8\u610f\u5230\u4ed6\u7684\u53cd\u5e94\uff0c\u5ffd\u7136\u6539\u53d8\u4e86\u8bdd\u9898\uff1a\u201c\u6731\u731b\u77e5\u9053\u6211\u5728\u90a3\u91cc\uff0c\u53ef\u662f\u4ed6\u6682 \u65f6\u7edd\u4e0d\u4f1a\u6765\u627e\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u65e2\u7136\u662f\u6765\u6b7b\u7684\uff0c\u6211\u4eec\u5f53\u7136\u8981\u6210\u572d\u4ed6\uff0c\u5f53\u7136\u4f1a\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u8fd9\u516b\u5341\u516b\u4e2a\u4eba\u90fd\u62b1\u7740\u5fc5\u6b7b\u4e4b\u5fc3\u800c\u6765\uff0c\u516b\u5341\u516b\u4e2a\u4eba\u53ea\u6709\u4e00\u6761\u5fc3\uff0c\u516b\u5341\u516b\u4e2a\u4eba\u90fd\u6709\u4e00\u80a1 \u6c14\u3002\u201d\u5353\u4e1c\u7c73\u8bf4\uff1a\u201c\u8fd9\u80a1\u6c14\u73b0\u5728\u5df2\u7ecf\u618b\u8db3\u4e86\uff0c\u4e00\u89e6\u5373\u53d1\u3002\u9510\u4e0d\u53ef\u5f53\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u73b0\u5728\u4e0d\u4f1a\u53bb\u627e\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5c16\u9525\u822c\u7684\u77b3\u5b54\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u6b8b\u9177\u800c\u96be\u6d4b\u7684\u7b11\u610f\uff0c\u95ee\u5353\u9752\uff1a\u201c\u4f60\u77e5\u9053\u6211\u8981\u600e\u4e48\u5bf9\u4ed8 \u4ed6\u4eec\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u7528\u4ed6\u90a3\u79cd\u72ec\u7279\u7684\u53e3\u6c14\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u544a\u8bc9\u5353\u9752\u3002 \u3000\u3000\u201c\u6211\u8981\u8bf7\u4ed6\u4eec\u5403\u996d\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u6211\u8981\u5728\u2018\u957f\u5b89\u5c45\u2019\u7684\u7b2c\u4e00\u697c\u66ff\u4ed6\u4eec\u63a5\u98ce\uff0c\u8bf7\u4ed6 \u4eec\u5403\u996d\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u66ff\u6211\u53bb\u8bf7\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u6731\u731b\u4e5f\u8bb8\u4e0d\u4f1a\u7b54\u5e94\uff0c\u4e5f\u8bb8\u4f1a\u8ba4\u4e3a\u8fd9\u662f\u4e2a\u9677\u8bb2\uff0c\u201d\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u76f8\u4fe1\u4f60\u4e00 \u5b9a\u6709\u6cd5\u5b50\u8ba9\u4ed6\u4eec\u53bb\u7684\u3002\u4e0d\u4f46\u6731\u731b\u8981\u53bb\uff0c\u9ad8\u6e10\u98de\u4e5f\u591a\u53bb\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u4ed6\u4eec\u4f1a\u53bb\u7684\uff0c\u4e00\u5b9a\u4f1a\u53bb\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u5e0c\u671b\u4f60\u80fd\u6d3b\u7740\u56de\u672a\u3002\u201d \u3000\u3000\u5353\u9752\u7684\u56de\u7b54\u7b80\u77ed\u80af\u5b9a\uff1a\u201d\u6211\u4f1a\u3002\u201d \u4e09 \u3000\u3000\u5353\u4e1c\u6765\u56de\u5230\u4ed6\u90a3\u95f4\u6e29\u6696\u5982\u6625\u7684\u5bdd\u5ba4\u65f6\uff0c\u8776\u821e\u6b63\u5728\u68b3\u5934\u3002 \u3000\u3000\u5979\u628a\u6f06\u9ed1\u7684\u957f\u53d1\u68b3\u4e86\u4e00\u904d\u53c8\u4e00\u904d\uff0c\u9664\u4e86\u68b3\u5934\u5916\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u597d\u50cf\u5df2\u7ecf\u6ca1\u6709\u5979\u60f3\u8981\u505a\u7684 \u4e8b\u3002 \u3000\u3000\u5353\u4e1c\u6765\u9759\u9759\u7684\u770b\u7740\u5979\u68b3\u5934\uff0c\u770b\u7740\u5979\u68b3\u4e86\u4e00\u904d\u53c8\u4e00\u904d\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e00\u4e2a\u68b3\u5934\uff0c\u4e00\u4e2a\u770b\u7740\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5ffd\u7136\u95f4\u201c\u5d29\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u6728\u68b3\u65ad\u4e86\uff0c\u65ad \u6210\u4e09\u622a\u3002 \u3000\u3000\u8fd9\u628a\u68b3\u5b50\u662f\u67f3\u5dde\u201c\u7389\u4eba\u574a\u201d\u7684\u7cbe\u54c1\uff0c\u5c31\u7b97\u7528\u4e24\u53ea\u624b\u7528\u529b\u53bb\u62d7\uff0c\u4e5f\u5f88\u96be\u62d7\u5f97\u65ad\u3002 \u3000\u3000\u5973\u4eba\u4eec\u65f6\u81ea\u5df1\u7684\u5934\u53d1\u901a\u5e38\u90fd\u5f88\u73cd\u60dc\uff0c\u68b3\u5934\u65f6\u901a\u5e38\u90fd\u4e0d\u4f1a\u592a\u7528\u529b\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u68b3\u5b50\u5df2\u7ecf\u65ad\u4e86\u3002 \u3000\u3000\u8776\u821e\u7684\u624b\u5728\u53d1\u6296\uff0c\u6296\u5f97\u8fde\u624b\u91cc\u4ec5\u5269\u7684\u4e00\u622a\u68b3\u5b50\u90fd\u62ff\u4e0d\u4f4f\u4e86\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u843d\u5728\u5986\u53f0 \u4e0a\u3002 \u3000\u3000\u5353\u4e1c\u6765\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u4ed6\u597d\u50cf\u5168\u90e8\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u201c\u4eca\u5929\u665a\u4e0a\u6211\u8981\u8bf7\u4eba\u5403\u996d\u3002\u201d\u4ed6\u5f88\u6e29\u548c\u7684\u544a\u8bc9\u8776\u821e\uff1a\u201c\u8bf7\u4e24\u4f4d\u8d35\u5ba2\u5403\u996d\u3002\u201d \u3000\u3000\u8776\u821e\u770b\u7740\u5986\u5408\u4e0a\u6298\u65ad\u7684\u6728\u68b3\uff0c\u4eff\u4f5b\u5df2\u7ecf\u770b\u75f4\u4e86\u3002 \u3000\u3000\u201c\u4eca\u5929\u665a\u4e0a\u6211\u4e5f\u8981\u8bf7\u4eba\u5403\u996d\u3002\u201d\u5979\u75f4\u75f4\u7684\u8bf4\uff1a\u201c\u8bf7\u6211\u81ea\u5df1\u5403\u996d\u3002\u201d \u3000\u3000\u5979\u53c8\u75f4\u75f4\u7684\u5728\u7b11\uff1a\u201c\u6bcf\u5929\u6211\u90fd\u8981\u8bf7\u6211\u81ea\u5df1\u5403\u996d\uff0c\u56e0\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u8981\u5403\u996d\u7684\uff0c\u8fde\u6211\u8fd9\u79cd\u4eba\u90fd \u8981\u5403\u996d\uff0c\u5403\u4e86\u4e00\u7897\u53c8\u4e00\u7897\uff0c\u5403\u5f97\u597d\u5f00\u5fc3\u597d\u5f00\u5fc3\u3002\u201d \u3000\u3000\u201c\u4eca\u5929\u6211\u4e5f\u60f3\u8ba9\u6211\u7684\u8d35\u5ba2\u5403\u5f97\u5f00\u5fc3\uff01\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u60f3\u8bf7\u4f60\u4e3a\u6211\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u968f\u4fbf\u4f60\u8981\u6211\u505a\u4ec0\u4e48\u90fd\u884c\u3002\u201d\u8776\u821e\u4e00\u76f4\u7b11\u4e2a\u4e0d\u505c\uff1a\u201c\u5c31\u7b97\u662f\u4f60\u8981\u6211\u4e0d\u5403\u996d\u53bb\u5403\u5c4e\uff0c\u6211\u4e5f \u4f1a\u9075\u547d\u53bb\u5403\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\u6781\u4e86\uff01\u201d \u3000\u3000\u5353\u4e1c\u6765\u5c45\u7136\u4e5f\u5728\u7b11\uff0c\u800c\u4e14\u4e5f\u597d\u50cf\u7b11\u5f97\u5f88\u6109\u5feb\u7684\u6837\u5b50\u3002 \u3000\u3000\u201c\u5176\u5b9e\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u60f3\u6e05\u4f60\u53bb\u4e16\u4ec0\u4e48\u4e8b\u7684\uff0c\u201d\u4ed6\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u6211\u60f3\u8bf7\u4f60\u53bb\u4e3a\u6211 \u4e00\u821e\u3002\u201d \u3000\u3000\u201c\u5b9d\u5251\u65e0\u60c5\uff0c\u5e84\u751f\u65e0\u68a6\uff1a \u3000\u3000\u4e3a\u541b\u4e00\u821e\uff0c\u5316\u4f5c\u8774\u8776\u3002\u201d \u56db \u3000\u3000\u957f\u5b89\u57ce\u6700\u6709\u540d\u7684\u9152\u697c\u662f\u201c\u957f\u5b89\u5c45\u201d\u3002\u957f\u5b89\u6700\u6709\u540d\u7684\u8336\u9986\u4e5f\u662f\u201c\u957f\u5b89\u5c45\u201d\uff0c\u53ea\u4e0d\u8fc7\u957f\u5b89\u5c45 \u9152\u697c\u548c\u957f\u5b89\u5c45\u8336\u9986\u662f\u5b8c\u5168\u4e0d\u540c\u7684\u3002 \u3000\u3000\u201c\u957f\u5b89\u5c45\uff0c\u5927\u4e0d\u6613\u3002\u201d \u3000\u3000\u8981\u5f00\u8fd9\u4e48\u6837\u4e00\u5bb6\u9152\u697c\u8336\u9986\u4e5f\u540c\u6837\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u957f\u5b89\u5c45\u9152\u697c\u5728\u57ce\u897f\uff0c\u56ed\u6797\u5f00\u9614\uff0c\u7528\u5668\u7cbe\u96c5\uff0c\u82b1\u6728\u6276\u758f\u95ee\u6709\u5341\u6570\u697c\u9601\uff0c\u6bcf\u4e00\u697c\u6bcf\u4e00\u9601\u7684\u9648 \u8bbe\u5e03\u7f6e\u90fd\u534e\u7f8e\u7edd\u4f26\uff0c\u996e\u98df\u4e4b\u4f73\uff0c\u66f4\u4ee4\u4eba\u8d5e\u4e0d\u7edd\u53e3\u3002 \u3000\u3000\u957f\u5b89\u5c45\u8336\u9986\u5728\u57ce\u4e2d\uff0c\u5728\u57ce\u4e2d\u6700\u7e41\u8363\u70ed\u95f9\u7684\u4e00\u6761\u8857\u4e0a\uff0c\u4ef7\u683c\u516c\u9053\uff0c\u7ecf\u6d4e\u5b9e\u60e0\u3002\u800c\u4e14\u65e0\u8bba\u8336 \u6c34\u996e\u98df\u9762\u70b9\u9152\u83dc\uff0c\u6bcf\u6837\u4e1c\u897f\u7684\u4efd\u91cf\u90fd\u5f88\u8db3\uff0c\u7edd\u4e0d\u4f1a\u8ba9\u4eba\u6709\u5403\u4e8f\u4e0a\u5f53\u7684\u611f\u89c9\u3002 \u3000\u3000\u6240\u4ee5\u6bcf\u5929\u4e00\u5927\u65f1\u8fd9\u91cc\u5c31\u5df2\u9ad8\u670b\u6ee1\u5ea7\uff0c\u4e09\u6559\u4e5d\u6d41\u4ec0\u4e48\u6837\u7684\u4eba\u90fd\u6709\u3002 \u3000\u3000\u56e0\u4e3a\u5230\u8fd9\u91cc\u6765\u9664\u4e86\u5403\u559d\u5916\uff0c\u8fd8\u53ef\u4ee5\u4eab\u53d7\u5230\u5176\u4ed6\u5f88\u591a\u79cd\u4e50\u8da3\uff0c\u53ef\u4ee5\u770b\u89c1\u5f88\u591a\u7a00\u5947\u53e4\u602a\u7684 \u4eba\uff0c\u53ef\u4ee5\u9047\u89c1\u4e00\u4e9b\u591a\u5e74\u6765\u89c1\u7684\u670b\u53cb\uff0c\u5728\u4f60\u65c1\u8fb9\u4e00\u5f20\u684c\u4e0a\u966a\u7740\u4e08\u592b\u5b69\u5b50\u559d\u8336\u7684\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u4f60 \u6614\u5e74\u7684\u60c5\u4eba\uff0c\u8eb2\u5728\u4e00\u4e2a\u89d2\u843d\u91cc\u4e0d\u6562\u62ac\u5934\u770b\u4f60\u7684\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u4f60\u627e\u4e86\u5f88\u4e45\u90fd\u627e\u4e0d\u5230\u7684\u503a\u6237\u3002 \u3000\u3000\u6240\u4ee5\u4e00\u4e2a\u4eba\u5982\u679c\u4e0d\u60f3\u88ab\u522b\u4eba\u627e\u5230\uff0c\u5c31\u7edd\u4e0d\u8be5\u5230\u8fd9\u5730\u65b9\u3002 \u3000\u3000\u6240\u4ee5\u6731\u731b\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u6015\u88ab\u4eba\u627e\u5230\uff0c\u4ed6\u6b63\u5728\u7b49\u7740\u5927\u9aa0\u5c40\u91cc\u7684\u4eba\u6765\u627e\u4ed6\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6562\u95ee\u6731\u731b\uff0c\u201c\u4e3a\u4ec0\u4e48\u8981\u5728\u8fd9\u91cc\u7b49\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u4e00\u53e3\u6c14\u6740\u8fdb\u5927\u9556\u5c40\u53bb\uff1f\u201d \u3000\u3000\u6731\u731b\u5f53\u7136\u6709\u4ed6\u7684\u7406\u7531\u3002 \u3000\u3000\u2014\u2014\u957f\u5b89\u662f\u5927\u9556\u5c40\u7684\u6839\u636e\u5730\uff0c\u957f\u5b89\u7684\u603b\u5c40\u91cc\u597d\u624b\u5982\u4e91\uff0c\u53f8\u9a6c\u8d85\u7fa4\u548c\u5353\u4e1c\u6765\u7684\u6b66\u529f\u66f4\u53ef \u6015\u3002\u73b0\u5728\u4ed6\u4eec\u4ee5\u9038\u5f85\u52b3\uff0c\u5df2\u7ecf\u5360\u5c3d\u4e86\u5929\u65f6\u5730\u5229\u3002 \u3000\u3000\u201c\u6211\u4eec\u662f\u6765\u62fc\u547d\u7684\uff0c\u4e0d\u662f\u6765\u9001\u6b7b\u7684\uff0c\u5c31\u7b97\u8981\u6b7b\uff0c\u4e5f\u8981\u6b7b\u5f97\u6709\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u2014\u2014\u8981\u6218\u5f3a\u654c\uff0c\u5e76\u4e0d\u662f\u5355\u51ed\u4e00\u80a1\u8840\u6c14\u5c31\u591f\u7684\u3002 \u3000\u3000\u201c\u6211\u4eec\u4e00\u5b9a\u8981\u5fcd\u8010\uff0c\u4e00\u5b9a\u8981\u81ea\u7acb\u81ea\u5f3a\uff0c\u4e00\u5b9a\u8981\u5fcd\u8fb1\u8d1f\u91cd\u3002\u201d \u3000\u3000\u2014\u2014\u8776\u821e\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u53bb\u4e3a\u522b\u4eba\u800c\u821e\uff1f \u3000\u3000\u6731\u731b\u5c3d\u91cf\u4e0d\u53bb\u60f3\u5979\u3002 \u3000\u3000\u8776\u821e\u7684\u821e\u59ff\u867d\u7136\u4ee4\u4eba\u523b\u9aa8\u94ed\u5fc3\uff0c\u6c38\u751f\u96be\u5fd8\uff0c\u53ef\u662f\u73b0\u5728\u5374\u5df2\u88ab\u9489\u978b\u7684\u9c9c\u8840\u51b2\u6de1\u3002 \u3000\u3000\u4ed6\u53d1\u8a93\uff0c\u7edd\u4e0d\u8ba9\u9489\u978b\u7684\u8840\u767d\u6d41\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u559d\u9152\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u60c5\u7eea\u90fd\u5f88\u6fc0\u52a8\uff0c\u6597\u5fd7\u90fd\u5f88\u6fc0\u6602\uff0c\u7528\u4e0d\u7740\u518d\u7528\u9152\u6765\u523a\u6fc0\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u8fd9\u5bb6\u6709\u4e00\u767e\u591a\u5f20\u684c\u5b50\u7684\u8336\u9986\u91cc\uff0c\u5360\u636e\u4e86\u5341\u4e09\u4e2a\u5ea7\u5934\uff0c\u672c\u6765\u8fd9\u5730\u65b9\u65e9\u5df2\u5ba2\u6ee1\u4e86\uff0c\u53ef \u662f\u4ed6\u4eec\u51fa\u73b0\u4e86\u7247\u523b\u4e4b\u540e\uff0c\u8336\u9986\u91cc\u7684\u4eba\u5c31\u8d70\u4e86\u4e00\u5927\u534a\u3002 \u3000\u3000\u770b\u5230\u4ed6\u4eec\u80cc\u540e\u7684\u8840\u7ea2\u5200\u8863\uff0c\u770b\u5230\u4ed6\u4eec\u5934\u7f20\u7684\u767d\u5dfe\uff0c\u770b\u5230\u4ed6\u4eec\u8138\u4e0a\u7684\u6740\u6c14\uff0c\u6bcf\u4e2a\u4eba\u90fd\u770b\u5f97 \u51fa\u8fd9\u4e9b\u964c\u751f\u7684\u5916\u5730\u5ba2\u7edd\u4e0d\u662f\u6765\u559d\u8336\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u8981\u559d\u7684\u662f\u8840\u3002 \u3000\u3000\u4ec7\u4eba\u7684\u8840\u3002 \u3000\u3000\u5353\u9752\u662f\u4e00\u4e2a\u4eba\u6765\u7684\u3002 \u3000\u3000\u4ed6\u8d70\u8fdb\u8fd9\u5bb6\u8336\u9986\u65f6\uff0c\u4ed6\u4eec\u5e76\u6ca1\u6709\u6ce8\u610f\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u3002 \u3000\u3000\u53ea\u6709\u5c0f\u9ad8\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u4e2a\u5c11\u5e74\u4eba\u66fe\u7ecf\u8ba9\u4ed6\u7559\u4e0b\u4e86\u6839\u6df1\u523b\u7684\u5370\u8c61\uff0c\u5353\u9752\u5374\u597d\u50cf\u5df2\u7ecf\u4e0d\u8ba4\u5f97\u4ed6\u4e86\uff0c\u4e00\u5b9a\u5165\u8336\u9986\uff0c \u5c31\u76f4\u63a5\u8d70\u5230\u6731\u731b\u7684\u9762\u524d\u3002 \u3000\u3000\u201c\u662f\u4e0d\u662f\u6d1b\u9633\u96c4\u72ee\u5802\u7684\u6731\u5802\u4e3b\uff1f\u3002 \u3000\u3000\u6731\u731b\u970d\u7136\u62ac\u5934\uff0c\u7528\u4e00\u53cc\u5e03\u6ee1\u8840\u4e1d\u7684\u5927\u773c\u77aa\u7740\u4ed6\u3002\u201c\u6211\u5c31\u662f\u6731\u731b\uff0c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u59d3\u5353\u3002\u201d \u3000\u3000\u201c\u4f60\u59d3\u5353\uff1f\u201d\u5c0f\u9ad8\u5f88\u60ca\u8bb6\uff1a\u201c\u6211\u8bb0\u5f97\u4f60\u672c\u6765\u597d\u50cf\u4e0d\u662f\u59d3\u5353\u7684\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u672c\u6765\u59d3\u90ed\uff0c\u6211\u8bb0\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u5df2\u7ecf\u4e0d\u8bb0\u5f97\u4e86\u3002\u201d\u5353\u9752\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u5df2\u7ecf\u8fc7\u53bb\u7684\u4e8b\uff0c\u6211\u4e00\u5411\u90fd\u5fd8\u5f97\u5f88\u5feb\uff0c\u5e94\u8be5 \u5fd8\u8bb0\u7684\u4e8b\uff0c\u6211\u66f4\u8fde\u60f3\u90fd\u4e0d\u4f1a\u53bb\u60f3\u5b83\u3002\u201d \u3000\u3000\u4ed6\u9759\u9759\u7684\u770b\u7740\u5c0f\u9ad8\uff0c\u8138\u4e0a\u5168\u65e0\u8868\u60c5\uff1a\u201c\u6709\u65f6\u5019\u4f60\u4e5f\u4e0d\u59a8\u5b66\u5b66\u6211\uff0c\u90a3\u4e48\u4f60\u6d3b\u5f97\u4e5f\u8bb8\u5c31\u4f1a\u6bd4 \u8f83\u6109\u5feb\u4e00\u4e9b\u4e86\u3002\u201d \u3000\u3000\u2014\u2014\u4eba\u4eec\u603b\u662f\u4f1a\u5728\u4e00\u4e9b\u4e0d\u9002\u5f53\u7684\u65f6\u5019\u60f3\u8d77\u4e00\u4e9b\u4e0d\u8be5\u60f3\u7684\u4e8b\uff0c\u8fd9\u672c\u6765\u5c31\u662f\u4eba\u7c7b\u6700\u5927\u7684\u75db\u82e6 \u4e4b\u4e00\u3002 \u3000\u3000\u2014\u2014\u73b0\u5728\u5c0f\u9ad8\u662f\u4e0d\u662f\u53c8\u60f3\u8d77\u4e86\u90a3\u4e2a\u4e0d\u8be5\u60f3\u7684\u5973\u4eba\uff1f \u3000\u3000\u5c0f\u9ad8\u5ffd\u7136\u60f3\u559d\u9152\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u5f00\u59cb\u60f3\u7684\u65f6\u5019\uff0c\u6731\u731b\u5ffd\u7136\u7b11\u4e86\uff0c\u4ef0\u9762\u72c2\u7b11\u3002 \u3000\u3000\u201c\u597d\uff0c\u8bf4\u5f97\u597d\u3002\u201d\u4ed6\u5927\u58f0\u5429\u5490\uff1a\u201c\u62ff\u9152\u6765\uff0c\u6211\u8981\u8ddf\u8fd9\u4e2a\u4f1a\u8bf4\u8bdd\u7684\u5c0f\u5b50\u6d6e\u4e09\u5927\u767d\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u665a\u8f88\u4e0d\u60f3\u559d\u9152\uff0c\u201d\u5353\u9752\u8bf4\uff1a\u201c\u6240\u4ee5\u665a\u8f88\u4e0d\u80fd\u5949\u966a\u3002\u201d \u3000\u3000\u6731\u731b\u7684\u7b11\u58f0\u9aa4\u7136\u505c\u987f\uff0c\u731b\u517d\u822c\u77aa\u7740\u4ed6\uff1a\u201c\u4f60\u4e0d\u60f3\u559d\u9152\uff0c\u4f60\u4e5f\u4e0d\u60f3\u966a\u6211\u559d\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u665a\u8f88\u4e0d\u60f3\u559d\uff0c\u8fde\u4e00\u6ef4\u90fd\u4e0d\u60f3\u559d\u3002\u201d\u5353\u9752\u7684\u773c\u775b\u7728\u4e5f\u4e0d\u7728\uff1a\u201c\u665a\u8f88\u8981\u5fd8\u8bb0\u4e00\u4ef6\u4e8b \u7684\u65f6\u5019\uff0c\u4e5f\u7528\u4e0d\u7740\u559d\u9152\u3002\u201d \u3000\u3000\u6731\u731b\u970d\u7136\u8d77\u8eab\u800c\u7acb\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u54cd\uff0c\u4e00\u53ea\u8336\u58f6\u5df2\u88ab\u4ed6\u634f\u5f97\u7c89\u788e\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u559d\uff1f\u201d \u3000\u3000\u5353\u9752\u8fd8\u662f\u795e\u8272\u4e0d\u53d8\u3002 \u3000\u3000\u201c\u6731\u5802\u751f\u73b0\u5728\u82e5\u662f\u8981\u6740\u6211\uff0c\u5f53\u7136\u6613\u5982\u53cd\u638c\uff0c\u8981\u6211\u559d\u9152\u5374\u96be\u5982\u767b\u5929\u3002\u201d \u3000\u3000\u6731\u731b\u5ffd\u7136\u53c8\u5927\u7b11\u3002 \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u771f\u6709\u79cd\u3002\u201d\u4ed6\u95ee\u5353\u9752\uff1a\u201c\u4f60\u59d3\u5353\uff0c\u662f\u4e0d\u662f\u5353\u4e1c\u6765\u7684\u5353\uff1f\u201d \u3000\u3000\u201c\u662f\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u5353\u4e1c\u6765\u8981\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u5949\u547d\u6765\u8bf7\u6731\u5802\u4e3b\u548c\u9ad8\u5927\u4fa0\u3002\u201d\u5353\u80b2\u8bf4\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u5353\u5148\u751f\u5b9a\u5728\u57ce\u897f\u957f\u5b89\u5c45\u7684\u7b2c\u4e00 \u697c\u4e3a\u4e24\u4f4d\u6446\u9152\u63a5\u51e4\u3002\u201d \u3000\u3000\u201c\u4ed6\u77e5\u4e0d\u77e5\u9053\u6211\u4eec\u6765\u4e86\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6b21\u6731\u5802\u4e3b\u5e26\u6765\u7684\u4eba\uff0c\u9664\u4e86\u9ad8\u5927\u4fa0\u5916\uff0c\u8fd8\u6709\u516b\u5341\u516d\u4f4d\u3002\u201d \u3000\u3000\u201c\u4ed6\u53ea\u8bf7\u6211\u4eec\u4e24\u4e2a\u4eba\uff1f\u201d\u6731\u731b\u51b7\u7b11\uff1a\u201c\u5353\u4e1c\u6765\u4e5f\u672a\u514d\u5927\u5c0f\u6c14\u4e86\u3002\u201d \u3000\u3000\u201c\u53ea\u6015\u4e0d\u662f\u5c0f\u6c14\uff0c\u800c\u662f\u5468\u5230\u3002\u201d \u3000\u3000\u201c\u5468\u5230\uff1f\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u5353\u5148\u751f\u60f3\u5f97\u5468\u5230\uff0c\u6240\u4ee5\u624d\u53ea\u6562\u8bf7\u6731\u5802\u4e3b\u548c\u9ad8\u5927\u4fa0\u4e24\u4f4d\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e24\u4f4d\u82f1\u96c4\u76d6\u4e16\uff0c\u7eb5\u7136\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u4e5f\u4e00\u6837\u6765\u53bb\u81ea\u5982\u3002\u201d\u5353\u9752\u6de1\u6de1\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u522b\u7684\u4eba \u6050\u6015\u5c31\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u6731\u731b\u53c8\u5927\u7b11\uff1a\u201c\u597d\uff0c\u8bf4\u5f97\u597d\uff0c\u5c31\u7b97\u957f\u5b89\u5c45\u7684\u7b2c\u4e00\u697c\u771f\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u6731\u731b\u548c\u5c0f\u9ad8\u4e5f\u4f1a\u53bb\u95ef \u4e00\u95ef\u3002\u53ef\u662f\u4f60\u5374\u4e0d\u8be5\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\u624d\uff0c\u65e2\u7136\u6765\u4e86\uff0c\u6211\u600e\u4e48\u820d\u5f97\u653e\u4f60\u8d70\uff1f\u201d\u6731\u731b\u7684\u7b11\u58f0\u5982\u96f7\uff1a\u201c\u6211\u82e5\u653e\u4f60\u8d70 \u4e86\uff0c\u5c82\u975e\u8ba9\u5929\u4e0b\u670b\u53cb\u7b11\u6211\u6731\u731b\u6709\u773c\u65e0\u73e0\u4e0d\u8bc6\u82f1\u96c4\uff1f\u201d \u3000\u3000\u5353\u9752\u5c45\u7136\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u6768\u575a\u53ef\u4ee5\u6295\u9760\u5927\u9556\u5c40\uff0c\u6211\u5f53\u7136\u4e5f\u53ef\u4ee5\u6295\u9760\u96c4\u72ee\u5802\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u53ef\u662f\u73b0\u5728\u8fd8\u4e0d\u884c\u3002\u201d \u3000\u3000\u201c\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\u624d\u884c\uff1f\u201d \u3000\u3000\u201c\u7b49\u5230\u96c4\u72ee\u5802\u7684\u529b\u91cf\u8db3\u4ee5\u51fb\u8d25\u5927\u9556\u5c40\u7684\u65f6\u5019\u3002\u201d\u5353\u9752\u5b8c\u5168\u4e0d\u52a8\u611f\u60c5\uff1a\u201c\u665a\u8f88\u5e76\u4e0d\u662f\u4e2a\u5fe0 \u5fc3\u7684\u4eba\uff0c\u4f46\u5374\u4e00\u5411\u5f88\u8bc6\u65f6\u52a1\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u5b9e\u5728\u60f3\u4e0d\u5230\u8fd9\u4e48\u5e74\u8f7b\u7684\u4e00\u4e2a\u4eba\u5c45\u7136\u80fd\u8bf4\u5f97\u51fa\u8fd9\u79cd\u8bdd\u6765\u3002 \u3000\u3000\u5353\u9752\u7acb\u523b\u5c31\u53d1\u73b0\u4e86\u4ed6\u8868\u60c5\u7684\u53d8\u5316\u3002 \u3000\u3000\u201c\u6211\u8bf4\u7684\u662f\u5b9e\u8bdd\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u5b9e\u8bdd\u901a\u5e38\u90fd\u4e0d\u4f1a\u592a\u597d\u542c\u3002\u201d \u3000\u3000\u6731\u731b\u4e0d\u7b11\u4e86\uff0c\u5389\u58f0\u95ee\uff1a\u201c\u90a3\u4e48\u6211\u662f\u4e0d\u662f\u5e94\u8be5\u653e\u4f60\u56de\u53bb\u5e2e\u5353\u4e1c\u6765\u6765\u5bf9\u4ed8\u6211\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u8bf4\u8fc7\uff0c\u6731\u5802\u4e3b\u8981\u6740\u6211\u6613\u5982\u53cd\u638c\u3002\u201d\u5353\u9752\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u6731\u5802\u4e3b\u82e5\u662f\u771f\u7684\u6740\u4e86\u6211\uff0c\u8981 \u60f3\u518d\u89c1\u90a3\u4e2a\u4eba\u5c31\u96be\u5982\u767b\u5929\u4e86\u3002\u201d \u3000\u3000\u6731\u731b\u53d8\u8272\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u660e\u767d\u5353\u9752\u8bf4\u7684\u201c\u90a3\u4e2a\u4eba\u201d\u662f\u8c01\u3002\u8fd9\u53e5\u6d3b\u5c31\u50cf\u662f\u6761\u97ad\u5b50\u822c\u62bd\u8fc7\u6765\uff0c\u4e00\u65f6\u95f4\u4ed6\u5b8c\u5168\u4e0d \u77e5\u9053\u5e94\u8be5\u5982\u4f55\u62db\u67b6\u3002 \u3000\u3000\u5353\u9752\u5df2\u7ecf\u5728\u8eac\u8eab\u884c\u672d\uff1a\u201c\u665a\u8f88\u544a\u8f9e\u3002\u201d \u3000\u3000\u4ed6\u5c45\u7136\u771f\u7684\u8f6c\u8eab\u8d70\u4e86\uff0c\u800c\u4e14\u4e00\u70b9\u4e5f\u4e0d\u6015\u522b\u4eba\u4f1a\u4ece\u4ed6\u80cc\u540e\u4e00\u5200\u780d\u4e0b\u4ed6\u7684\u5934\u9885\uff0c\u4e5f\u6ca1\u6709\u518d\u770b \u6731\u731b\u4e00\u773c\u3002 \u3000\u3000\u6731\u731b\u989d\u4e0a\u5df2\u6709\u9752\u7b4b\u66b4\u8d77\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4e0d\u80fd\u8ba9\u5353\u9752\u8d70\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u7684\u5c5e\u4e0b\u770b\u7740\u4ed6\u4e3a\u4e86\u4e00\u4e2a\u5973\u4eba\u800c\u653e\u8d70\u4ed6\u4eec\u7684\u4ec7\u654c\u3002 \u3000\u3000\u2014\u2014\u53ef\u662f\u4ed6\u53c8\u600e\u4e48\u80fd\u8ba9\u8776\u821e\u56e0\u6b64\u800c\u6b7b\uff1f \u3000\u3000\u5c0f\u9ad8\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u60f3\u4e0d\u5230\u4ed6\u771f\u7684\u770b\u51c6\u4e86\uff0c\u770b\u51c6\u4e86\u96c4\u72ee\u6731\u731b\u7edd\u4e0d\u4f1a\u6740\u4e00\u4e2a\u624b\u65e0\u5bf8\u94c1\u3001 \u5949\u547d\u5230\u8fd9\u91cc\u6765\u4f20\u8baf\u7684\u4eba\u3002\u201d\u4ed6\u7684\u76ee\u5149\u56db\u626b\uff1a\u201c\u8fd9\u79cd\u4e8b\u53ea\u8981\u662f\u4eb2\u7537\u5b50\u6c49\u5c31\u7edd\u4e0d\u4f1a\u505a\u7684\uff0c\u4f55\u51b5\u6731 \u731b\u3002\u201d \u3000\u3000\u4e00\u6761\u5934\u7f20\u767d\u5dfe\u7684\u5927\u6c49\u970d\u7136\u7ad9\u8d77\uff0c\u5927\u58f0\u9053\uff1a\u201c\u9ad8\u5927\u54e5\u8bf4\u7684\u662f\uff0c\u6211\u4eec\u5144\u5f1f\u5927\u4f19\u513f\u90fd\u8981\u656c\u9ad8\u5927 \u54e5\u4e00\u676f\u3002\u201d \u3000\u3000\u516b\u5341\u516b\u6761\u597d\u6c49\u7acb\u523b\u8f70\u96f7\u822c\u54cd\u5e94\u3002\u5c0f\u9ad8\u4e00\u628a\u626f\u5f00\u4e86\u8863\u895f\uff1a\u201c\u597d\uff0c\u62ff\u9152\u6765\u3002\u201d \u4e94 \u3000\u3000\u201c\u6211\u77e5\u9053\u6731\u731b\u8fd8\u662f\u653e\u4e0d\u4e0b\u8776\u821e\u7684\uff0c\u201d\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u90a3\u4e48\u8f7b\u6613 \u8ba9\u4f60\u8d70\u3002\u201d \u3000\u3000\u4ed6\u773c\u4e2d\u5e26\u7740\u6df1\u601d\u4e4b\u8272\uff1a\u201c\u4e3a\u4e86\u4e00\u4e2a\u5973\u4eba\uff0c\u5c31\u8f7b\u6613\u653e\u8d70\u4ec7\u654c\uff0c\u6731\u731b\u96be\u9053\u5c31\u4e0d\u6015\u4ed6\u7684\u5144\u5f1f\u4eec \u56e0\u6b64\u800c\u770b\u8f7b\u4ed6\uff1b\u96be\u9053\u5c31\u4e0d\u6015\u635f\u4e86\u4ed6\u4eec\u7684\u58eb\u6c14\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u7b11\uff1a\u201c\u8776\u821e\u8fd9\u4e2a\u5973\u4eba\u96be\u9053\u771f\u7684\u6709\u8fd9\u4e48\u5927\u7684\u9b54\u529b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u58eb\u6c14\u5e76\u6ca1\u6709\u56e0\u6b64\u6d88\u6c89\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u9ad8\u6e10\u98de\u5f88\u4e86\u89e3\u6731\u731b\u5f53\u65f6\u7684\u5fc3\u60c5\uff0c\u53ca\u65f6\u5e2e\u4ed6\u8131\u51fa\u4e86\u56f0\u5883\uff0c\u8ba9\u4ed6\u7684\u5144\u5f1f\u4eec\u8ba4\u4e3a\u4ed6\u4e0d\u6740 \u4f60\u5e76\u975e\u4e3a\u4e86\u5973\u8272\uff0c\u800c\u662f\u4e3a\u4e86\u4e49\u6c14\u3002\u201d \u3000\u3000\u201c\u4e24\u56fd\u4ea4\u950b\uff0c\u4e0d\u65a9\u6765\u4f7f\uff0c\u5149\u660e\u78ca\u843d\u7684\u6731\u731b\uff0c\u600e\u4e48\u4f1a\u6740\u4e00\u4e2a\u624b\u65e0\u5bf8\u94c1\u7684\u4eba\uff1f\u201d \u3000\u3000\u5353\u9752\u773c\u4e2d\u9732\u51fa\u8d5e\u4f69\u4e4b\u8272\uff1a\u201c\u9ad8\u6e10\u98de\u6b63\u662f\u8fd9\u4e48\u8bf4\u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u4e0d\u505c\u7684\u51b7\u7b11\uff1a\u201c\u8fd9\u4e2a\u4eba\u5012\u771f\u662f\u6731\u731b\u7684\u597d\u670b\u53cb\uff0c\u6731\u731b\u7684\u90a3\u4e9b\u5144\u5f1f\u5374\u90fd\u662f\u732a\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\u90a3\u4e9b\u4eba\u4e5f\u4e0d\u662f\u4e0d\u660e\u767d\u9ad8\u6e10\u98de\u7684\u610f\u601d\u3002\u201d\u5353\u9752\u9053\uff1a\u201c\u4f46\u662f\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u56e0\u6b64\u770b\u8f7b\u6731 \u731b\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4eec\u5e76\u4e0d\u5e0c\u671b\u6731\u731b\u771f\u7684\u90a3\u4e48\u51b7\u9177\u65e0\u60c5\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u56e0\u4e3a\u771f\u6b63\u7684\u82f1\u96c4\u5e76\u4e0d\u662f\u65e0\u60c5 \u7684\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6837\u7684\u4eba\u624d\u771f\u6b63\u65e0\u60c5\uff1f\u201d \u3000\u3000\u201c\u67ad\u96c4\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u82f1\u96c4\u65e0\u6cea\uff0c\u67ad\u96c4\u65e0\u60c5\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u773c\u4e2d\u5ffd\u7136\u6709\u5bd2\u5149\u66b4\u5c04\u800c\u51fa\uff0c\u76ef\u7740\u5353\u9752\u770b\u4e86\u5f88\u4e45\uff0c\u624d\u51b7\u51b7\u7684\u95f4\uff1a\u201c\u9ad8\u6e10\u98de\u5982\u679c\u6ca1 \u6709\u90a3\u4e48\u8bf4\uff0c\u6731\u731b\u662f\u4e0d\u662f\u5c31\u4f1a\u6740\u4e86\u4f60\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e5f\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5353\u9752\u7684\u58f0\u97f3\u51b7\u6de1\u800c\u5e73\u9759\uff1a\u201d\u56e0\u4e3a\u5728\u4ed6\u7684\u5fc3\u65e5\u4e2d\uff0c\u8776\u821e\u7684\u547d\u6bd4\u6211\u73cd\u8d35\u5f97\u591a\u3002\u201d \u516d \u3000\u3000\u9ec4\u660f\u3002\u9ec4\u660f\u540e\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u5df2\u7ecf\u5f88\u6697\u4e86\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u70b9\u706f\uff0c\u8776\u821e\u4e00\u5411\u4e0d\u559c\u6b22\u70b9\u706f\u3002 \u3000\u3000\u2014\u2014\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u5979\u751f\u6015\u81ea\u5df1\u4f1a\u53d8\u5f97\u50cf\u98de\u86fe\u4e00\u6837\u6251\u5411\u706b\u7130\uff1f \u3000\u3000\u7089\u4e2d\u6709\u706b\u5149\u95ea\u52a8\uff0c\u8776\u821e\u7ad9\u5728\u7089\u706b\u65c1\uff0c\u6162\u9992\u7684\u8131\u4e0b\u4e86\u5979\u8eab\u4e0a\u7684\u8863\u670d\u3002 \u3000\u3000\u5979\u7684\u916e\u4f53\u6676\u83b9\u67d4\u6da6\u6d01\u767d\u65e0\u6687\u3002 \u3000\u3000\u95e8\u88ab\u63a8\u5f00\uff0c\u5979\u77e5\u9053\u6709\u4eba\u8fdb\u6765\u4e86\uff0c\u53ef\u662f\u5979\u6ca1\u6709\u56de\u5934\uff0c\u56e0\u4e3a\u9664\u4e86\u5353\u4e1c\u6765\u4e4b\u5916\uff0c\u6ca1\u6709\u4eba\u6562\u8d70\u5165 \u8fd9\u95f4\u5c4b\u5b50\u3002 \u3000\u3000\u5979\u5f2f\u4e0b\u8170\uff0c\u8f7b\u63c9\u81ea\u5df1\u7684\u817f\u3002 \u3000\u3000\u751a\u81f3\u8fde\u5979\u81ea\u5df1\u90fd\u53ef\u4ee5\u611f\u89c9\u5230\u5979\u817f\u4e0a\u808c\u8089\u7684\u5f39\u6027\u662f\u591a\u4e48\u5bb9\u6613\u6311\u9017\u8d77\u4eba\u4eec\u7684\u60c5\u6b32\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u6297\u62d2\u8fd9\u79cd\u6311\u9017\uff0c\u4ece\u672a\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6240\u4ee5\u5979\u5947\u602a\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4e00\u76f4\u90fd\u5728\u770b\u5979\uff0c\u53ef\u662f\u4e00\u76f4\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u4efb\u4f55\u52a8\u4f5c\u3002 \u3000\u3000\u8f7b\u76c8\u7684\u821e\u8863\uff0c\u8f7b\u5982\u8749\u7ffc\uff0c\u7a7f\u4e0a\u5b83\u5c31\u50cf\u662f\u7a7f\u4e0a\u4e00\u5c42\u6708\u5149\uff0c\u7f8e\u5f97\u6726\u80e7\uff0c\u6726\u80e7\u4e2d\u770b\u6765\u66f4\u7f8e\uff0c\u66f4 \u4ee4\u4eba\u96be\u4ee5\u6297\u62d2\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5c45\u7136\u8fd8\u662f\u7ad9\u5728\u5979\u8eab\u540e\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u8776\u821e\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u56de\u8fc7\u5934\uff0c\u624b\u91cc\u521a\u62c8\u8d77\u7684\u4e00\u6735\u73e0\u82b1\u5ffd\u7136\u6389\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u521a\u624d\u8fdb\u6765\u7684\u4eba\u5c45\u7136\u4e0d\u662f\u5353\u4e1c\u6765\u3002 \u3000\u3000\u5979\u56de\u8fc7\u5934\uff0c\u5c31\u770b\u89c1\u4e00\u4e2a\u8138\u8272\u82cd\u767d\u7684\u5c11\u5e74\u7ad9\u5728\u5730\u9762\u524d\u770b\u7740\u5979\u3002 \u3000\u3000\u8776\u821e\u5f88\u5feb\u5c31\u6062\u590d\u4e86\u9547\u5b9a\u3002 \u3000\u3000\u5979\u60f3\u4e0d\u5230\u9664\u4e86\u5353\u4e1c\u6765\u4e4b\u5916\u8fd8\u6709\u4eba\u6562\u95ef\u5165\u8fd9\u95f4\u5c4b\u5b50\uff0c\u53ef\u662f\u5979\u5df2\u7ecf\u88ab\u4eba\u770b\u60ef\u4e86\u3002 \u3000\u3000\u552f\u4e00\u8ba9\u5979\u89c9\u5f97\u4e0d\u4e60\u60ef\u7684\u662f\uff0c\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u770b\u7740\u5979\u65f6\u7684\u773c\u5149\u548c\u4efb\u4f55\u4eba\u90fd\u4e0d\u540c\u3002 \u3000\u3000\u522b\u4eba\u770b\u5230\u5979\u8d64\u88f8\u7684\u80f4\u4f53\u548c\u5979\u7684\u4e00\u53cc\u817f\u65f6\uff0c\u773c\u4e2d\u90fd\u597d\u50cf\u6709\u706b\u7130\u5728\u71c3\u70e7\u3002 \u3000\u3000\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u7684\u773c\u775b\u5374\u51b7\u5982\u51b0\u96ea\u5ca9\u77f3\u5200\u950b\u3002 \u3000\u3000\u5353\u9752\u770b\u7740\u8776\u821e\uff0c\u5c31\u597d\u50cf\u5728\u770b\u7740\u4e00\u56e2\u51b0\u96ea\u4e00\u5757\u5ca9\u77f3\u4e00\u67c4\u5200\u950b\u3002 \u3000\u3000\u8776\u821e\u4e5f\u5728\u770b\u7740\u4ed6\uff0c\u770b\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u8fd8\u770b\u4e0d\u51fa\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u7684\u8868\u60c5\u6709\u4e00\u70b9\u53d8\u5316\u3002 \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u201d\u8776\u821e\u5fcd\u4e0d\u4f4f\u95ee\u4ed6\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5353\u9752\uff0c\u6211\u53eb\u5353\u9752\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u4eba\uff1f\u662f\u4e0d\u662f\u4e2a\u6709\u8840\u6709\u8089\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u778e\u5b50\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u770b\u89c1\u6211\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u89c1\u4e86\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u4f60\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e2a\u5730\u65b9\u6211\u90fd\u770b\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u51b7\u6f20\u800c\u6709\u672d\uff0c\u5b8c\u5168\u4e0d\u52a8\u611f\u60c5\uff0c\u5b8c\u5168\u6ca1\u6709\u4e00\u70b9\u8ba5\u8bee\u7325\u4eb5\u7684\u610f\u601d\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u53ea\u4e0d\u8fc7\u5728\u53d9\u8bf4\u4e00\u4ef6\u4e8b\u5b9e\u800c\u5df2\u3002 \u3000\u3000\u8776\u821e\u7b11\u4e86\uff0c\u5e26\u7740\u7b11\u53f9\u4e86\u53e3\u6c14\uff0c\u53f9\u7740\u6c14\u95ee\u5353\u9752\uff1a \u3000\u3000\u201c\u4f60\u96be\u9053\u4ece\u6765\u4e0d\u4f1a\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u201c\u6709\u65f6\u4f1a\uff0c\u6709\u65f6\u4e0d\u4f1a\u3002\u201d\u5353\u9752\u9053\uff1a\u201c\u6ca1\u6709\u5fc5\u8981\u8bf4\u8c0e\u7684\u65f6\u5019\uff0c\u6211\u4e00\u5411\u8bf4\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4f60\u6ca1\u6709\u5fc5\u8981\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u201c\u5b8c\u5168\u6ca1\u6709\u3002\u201d \u3000\u3000\u8776\u821e\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4f60\u8bf4\u4f60\u628a\u6211\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e2a\u5730\u65b9\u90fd\u770b\u6e05\u695a\u4e86\uff0c\u4f60\u4e0d\u6015\u8001\u5353\u6316\u51fa\u4f60\u7684\u773c \u73e0\u5b50\u6765\uff1f\u201d \u3000\u3000\u5353\u9752\u9759\u9759\u7684\u770b\u7740\u5979\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u73b0\u5728\u4ed6\u5df2\u7ecf\u4e0d\u4f1a\u8fd9\u4e48\u6837\u505a\u4e86\u3002\u201d \u3000\u3000\u8776\u821e\u770b\u8d77\u6765\u4eff\u4f5b\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\uff0c\u5176\u5b9e\u5df2\u7ecf\u5b8c\u5168\u660e\u767d\u4e86\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u201c\u73b0\u5728\u4ed6\u5df2\u7ecf\u4e0d\u4f1a\u8fd9\u4e48\u6837\u505a\u4e86\u3002\u201d\u5979\u95ee\u5353\u9752\uff1a\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u628a\u6211\u8ba9\u7ed9\u4e86\u4f60\uff1f\u201d \u3000\u3000\u5353\u9752\u6447\u5934\u3002 \u3000\u3000\u8776\u821e\u53c8\u95ee\uff1a\u201c\u4e0d\u662f\u4f60\uff1f\u662f\u522b\u4eba\uff1f\u201d \u3000\u3000\u5353\u9752\u6c89\u9ed8\u3002 \u3000\u3000\u201c\u4ed6\u5b9e\u5728\u5927\u65b9\u5f97\u5f88\u3002\u201d\u8776\u821e\u7684\u58f0\u97f3\u5145\u6ee1\u8ba5\u6d88\uff1a\u201c\u78b0\u8fc7\u6211\u7684\u7537\u4eba\u4ece\u6765\u6ca1\u6709\u4e00\u4e2a\u820d\u5f97\u628a\u6211\u8ba9 \u51fa\u53bb\u3002\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u53f9\u606f\uff1a\u201c\u8fd9\u5b9e\u5728\u5f88\u53ef\u60dc\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u5728\u66ff\u4f60\u53ef\u60dc\uff0c\u4ed6\u5b9e\u5728\u5e94\u8be5\u628a\u6211\u8ba9\u7ed9\u4f60\u7684\u3002\u201d\u8776\u821e\u8bf4\uff1a\u201c\u4f60\u8fd9\u4e00\u8f88\u5b50\u518d\u4e5f\u4e0d\u4f1a\u9047\u5230 \u7b2c\u4e8c\u4e2a\u50cf\u6211\u8fd9\u6837\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u5728\u66ff\u6211\u81ea\u5df1\u53ef\u60dc\uff0c\u201d\u8776\u821e\u770b\u7740\u5353\u9752\uff1a\u201c\u4f60\u5e74\u8f7b\uff0c\u4f60\u662f\u4e2a\u5f88\u597d\u770b\u7684\u7537\u5b69\u5b50\uff0c\u6211\u4e00\u5411 \u6700\u559c\u6b22\u4f60\u8fd9\u4e48\u5927\u7684\u7537\u5b69\u5b50\u3002\u4f60\u4eec\u597d\u50cf\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u7d2f\u7684\u3002\u201d \u3000\u3000\u5979\u7684\u773c\u6ce2\u6e10\u6e10\u6726\u80e7\uff0c\u5634\u5507\u6e10\u6e10\u6f6e\u6e7f\uff0c\u5ffd\u7136\u6162\u6162\u7684\u8d70\u8fc7\u6765\uff0c\u89e3\u5f00\u4e86\u5979\u7684\u821e\u8863\uff0c\u628a\u5979\u67d4\u8f6f\u5149 \u6ed1\u6e29\u6696\u7684\u80f4\u4f53\u8d64\u88f8\u88f8\u7684\u7d27\u8d34\u5728\u5353\u9752\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u7684\u8170\u80a2\u5728\u626d\u52a8\uff0c\u5589\u95f4\u5728\u4f4e\u4f4e\u5598\u606f\u547b\u541f\u3002 \u3000\u3000\u5353\u9752\u5c45\u7136\u6ca1\u6709\u53cd\u5e94\u3002 \u3000\u3000\u8776\u821e\u5598\u606f\u7740\uff0c\u4f38\u624b\u53bb\u627e\u4ed6\u7684\uff0c\u53ef\u662f\u5979\u7684\u624b\u7acb\u523b\u88ab\u63e1\u4f4f\uff0c\u5979\u7684\u4eba\u4e5f\u88ab\u629b\u8d77\u3002 \u3000\u3000\u5353\u9752\u629b\u7403\u822c\u5c06\u5979\u629b\u5728\u5e8a\u4e0a\uff0c\u51b7\u51b7\u7684\u770b\u7740\u5979\uff1a\u201c\u4f60\u53ef\u4ee5\u7528\u5404\u79cd\u6cd5\u5b50\u6765\u6298\u78e8\u81ea\u5df1\uff0c\u4fae\u8fb1\u81ea \u5df1\uff0c\u968f\u4fbf\u4f60\u7528\u4ec0\u4e48\u6cd5\u5b50\u90fd\u884c\u3002\u201d\u5353\u9752\u51b7\u51b7\u7684\u8bf4\uff0c\u201c\u53ef\u662f\u6211\u4e0d\u884c\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u884c\uff1f\u201d\u8776\u821e\u53c8\u7b11\u4e86\uff0c\u75af\u72c2\u822c\u5927\u7b11\uff1a\u201c\u4f60\u4e0d\u662f\u7537\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u6fc0\u6012\u6211\u4e5f\u6ca1\u6709\u7528\u7684\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u6211\u7edd\u4e0d\u4f1a\u78b0\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e5f\u662f\u7537\u4eba\uff0c\u6211\u4e0d\u60f3\u4ee5\u540e\u6bcf\u5929\u665a\u4e0a\u90fd\u8981\u60f3\u7740\u4f60\u5728\u4e0b\u9762\u7684\u6837\u5b50\u6765\u6298\u78e8\u81ea\u5df1\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u4ee5\u540e\u6bcf\u5929\u665a\u4e0a\u4f60\u90fd\u53ef\u4ee5\u62b1\u7740\u6211\u7761\u89c9\u7684\u3002\u201d \u3000\u3000\u5353\u9752\u5fae\u7b11\uff0c\u7b11\u5bb9\u5374\u50cf\u662f\u7528\u82b1\u5c97\u77f3\u523b\u51fa\u6765\u7684\uff1a\u201c\u6211\u4e5f\u66fe\u8fd9\u4e48\u6837\u60f3\u8fc7\u3002\u201d\u4ed6\u5e26\u7740\u5fae\u7b11\u8bf4\uff1a \u201c\u53ea\u53ef\u60dc\u6211\u4e5f\u77e5\u9053\u90a3\u4e9b\u60f3\u6bcf\u5929\u62b1\u7740\u4f60\u7684\u7537\u4eba\u662f\u4ec0\u4e48\u4e0b\u573a\u3002\u201d \u3000\u3000\u8776\u821e\u4e0d\u7b11\u4e86\uff0c\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u65e0\u6cd5\u63cf\u53d9\u7684\u60b2\u4f24\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u5f97\u5bf9\u3002\u201d\u5979\u5e7d\u5e7d\u7684\u8bf4\uff1a\u201c\u90a3\u4e9b\u60f3\u6bcf\u5929\u62b1\u7740\u6211\u7684\u7537\u4eba\u5c31\u7b97\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4e5f\u5728\u53d7\u6d3b \u7f6a\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5df2\u56e0\u75db\u82e6\u800c\u5636\u54d1\uff1a\u201c\u5e78\u597d\u90a3\u4e9b\u4eba\u4e0d\u662f\u6df7\u86cb\u5c31\u662f\u767d\u75f4\uff0c\u4e0d\u7ba1\u4ed6\u4eec\u53d7\u4ec0\u4e48\u6837\u7684\u7f6a\u90fd \u6d3b\u8be5\u3002\u201d \u3000\u3000\u201c\u6731\u731b\u5462\uff1f\u201d\u5353\u9752\u5ffd\u7136\u95ee\u5979\uff1a\u201c\u6731\u731b\u662f\u6df7\u86cb\u8fd8\u662f\u767d\u75f4\uff1f\u201d \u3000\u3000\u8776\u821e\u7ad9\u8d77\u6765\uff0c\u51dd\u89c6\u7740\u7089\u4e2d\u95ea\u52a8\u7684\u706b\u7130\uff0c\u8fc7\u4e86\u5f88\u4e45\u5ffd\u7136\u51b7\u7b11\u3002 \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u6731\u731b\u4f1a\u60f3\u6211\uff1f\u4f60\u4ee5\u4e3a\u6731\u731b\u4f1a\u4e3a\u6211\u96be\u53d7\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u4f1a\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6839\u672c\u5c31\u4e0d\u662f\u4eba\u3002\u201d\u8776\u821e\u58f0\u97f3\u4e2d\u5145\u6ee1\u6068\u610f\uff1a\u201c\u5c31\u50cf\u5353\u4e1c\u6765\u4e00\u6837\u4e0d\u662f\u4eba\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u5bf9\u4f60\u6839\u672c\u4e0d\u5728\u4e4e\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5728\u4e4e\u4ec0\u4e48\uff1f\u201d\u8776\u821e\u8bf4\uff1a\u201c\u4ed6\u53ea\u5728\u4e4e\u4ed6\u7684\u58f0\u540d\uff0c\u4ed6\u7684\u5730\u4f4d\uff0c\u4ed6\u7684\u6743\u529b\uff0c\u5c31\u7b97\u6211\u6b7b\u5728\u4ed6 \u9762\u524d\uff0c\u4ed6\u4e5f\u4e0b\u4f1a\u6389\u4e00\u6ef4\u773c\u6cea\u3002\u201d \u3000\u3000\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u5728\u4ed6\u7684\u773c\u91cc\uff0c\u6211\u4e5f\u4e0d\u662f\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u73a9\u7269\u800c\u5df2\u3002\u5c31\u50cf\u662f\u5b69\u5b50\u73a9\u7684\u6ce5\u5a03\u5a03\uff0c\u4ed6\u9ad8\u5174\u7684\u65f6 \u5019\uff0c\u5c31\u62ff\u8d77\u6765\u73a9\u73a9\uff0c\u73a9\u538c\u4e86\u5c31\u4e22\u5728\u4e00\u8fb9\uff0c\u6709\u65f6\u5019\u751a\u81f3\u4f1a\u4e00\u8fde\u597d\u51e0\u5929\u90fd\u4e0d\u8ddf\u6211\u8bf4\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u201c\u5c31\u56e0\u4e3a\u4ed6\u8fd9\u4e48\u6837\u5bf9\u4f60\uff0c\u6240\u4ee5\u4f60\u624d\u4f1a\u4e58\u6211\u4eec\u7a81\u88ad\u96c4\u72ee\u5802\u7684\u65f6\u5019\u6e9c\u8d70\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u662f\u4eba\u3002\u201d\u8776\u821e\u95ee\u5353\u9752\uff1a\u201c\u6709\u6ca1\u6709\u4eba\u613f\u610f\u88ab\u522b\u4eba\u5f53\u4f5c\u73a9\u7269\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5353\u9752\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u4f60\u4e5f\u8bb8\u770b\u9519\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u770b\u9519\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u201c\u50cf\u4ed6\u90a3\u6837\u7684\u7537\u4eba\uff0c\u5c31\u7b97\u5fc3\u91cc\u5bf9\u4eba\u5f88\u597d\uff0c\u4e5f\u672a\u5fc5\u4f1a\u8868\u9732\u51fa\u6765\u7684\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u6709 \u5f88\u591a\u4eba\u90fd\u5f88\u4e0d\u4f1a\u8868\u9732\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u5c24\u5176\u662f\u5bf9\u81ea\u5df1\u559c\u6b22\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u4eec\u89c9\u5f97\u5728\u5973\u4eba\u9762\u524d\u4f5c\u51fa\u6df1\u60c5\u6b3e\u6b3e\u7684\u6837\u5b50\u5c31\u6ca1\u6709\u7537\u5b50\u6c49\u5927\u4e08\u5927\u7684\u6c14\u6982 \u4e86\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u5c31\u4e0b\u61c2\u5f97\u8981\u600e\u4e48\u6837\u505a\u3002\u201d \u3000\u3000\u201c\u6731\u731b\u4e0d\u662f\u8fd9\u79cd\u4eba\u3002\u201d\u8776\u821e\u8bf4\u5f97\u622a\u9489\u65ad\u94c1\uff1a\u201c\u8fd9\u79cd\u4e8b\u4ed6\u6bd4\u8c01\u90fd\u61c2\uff0c\u6bd4\u8c01\u90fd\u4f1a\u505a\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5bf9\u522b\u4eba\u597d\u7684\u65f6\u5019\uff0c\u505a\u51fa\u6765\u7684\u4e8b\u6bd4\u8c01\u90fd\u6f02\u4eae\u3002\u201d\u8776\u821e\u8bf4\uff1a\u201c\u4ed6\u4e3a\u522b\u4eba\u505a\u7684\u90a3\u4e9b\u4e8b\u6709\u65f6 \u5019\u8fde\u6211\u90fd\u4f1a\u89c9\u5f97\u8089\u9ebb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u4e0d\u662f\u522b\u4eba\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u4f60\u662f\u8ddf\u522b\u4eba\u4e0d\u540c\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u662f\u4ed6\u7684\u5973\u4eba\uff0c\u4e5f\u8bb8\u4ed6\u8ba4\u4e3a\u4f60\u5e94\u8be5\u77e5\u9053\u4ed6\u5bf9\u4f60\u662f\u8ddf\u522b\u4eba\u4e0d\u540c\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u8776\u821e\u8bf4\uff1a\u201c\u4e00\u4e2a\u7537\u4eba\u5982\u679c\u771f\u7684\u559c\u6b22\u4e00\u4e2a\u5973\u4eba\uff0c\u5c31\u5e94\u8be5\u8ba9\u5979\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4f60\u8fd8\u4e0d\u4e86\u89e3\u4ed6\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4e86\u89e3\u4ed6\uff01\u201d\u8776\u821e\u53c8\u5728\u51b7\u7b11\uff1a\u201c\u6211\u8ddf\u4ed6\u5728\u4e00\u8d77\u62b1\u7740\u7761\u89c9\u7761\u4e86\u4e09\u56db\u5e74\uff0c\u6211\u8fd8\u4e0d\u4e86\u89e3 \u4ed6\uff1f\u201d \u3000\u3000\u5353\u9752\u8138\u4e0a\u53c8\u9732\u51fa\u90a3\u79cd\u5ca9\u77f3\u822c\u50f5\u51b7\u7684\u5fae\u7b11\u3002 \u3000\u3000\u201c\u4f60\u5f53\u7136\u5f88\u4e86\u89e3\u4ed6\uff0c\u800c\u4e14\u4e00\u5b9a\u6bd4\u6211\u4eec\u8fd9\u4e9b\u4eba\u90fd\u4e86\u89e3\u5f97\u591a\u3002\u201d \u3000\u3000\u591c\u8272\u5df2\u4e34\uff0c\u5c4b\u5b50\u91cc\u5df2\u7ecf\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u8776\u821e\u624d\u8f7b\u8f7b\u7684\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u4eca\u5929\u6211\u8bf4\u7684\u8bdd\u662f\u4e0d\u662f\u5df2\u7ecf\u592a\u591a\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u6240\u4ee5\u73b0\u5728\u6211\u4eec\u5df2\u7ecf\u5e94\u8be5\u8d70\u4e86\uff0c\u6211\u672c\u6765\u5c31\u662f\u8981\u6765\u5e26\u4f60\u8d70\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u5e26\u6211\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5353\u9752\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u96be\u9053\u4f60\u5fd8\u4e86\uff1f\u4f60\u5df2\u7ecf\u7b54\u5e94\u5353\u5148\u751f\u4eca\u591c\u8981\u53bb\u4e3a\u4ed6\u4e00\u821e\u3002\u201d \u7b2c\u5341\u4e8c\u7ae0\u3000\u7eb5\u7136\u4e00\u821e\u4e5f\u9500\u9b42>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u7eb5\u7136\u4e00\u821e\u4e5f\u9500\u9b42 \u4e00 \u3000\u3000\u4e8c\u6708\u4e8c\u5341\u4e8c\u3002 \u3000\u3000\u6d1b\u9633\u3002 \u3000\u3000\u98ce\u96ea\u6ee1\u5929\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u6234\u6597\u7b20\uff0c\u62ab\u98ce\u6be1\uff0c\u97ad\u5feb\u9a6c\uff0c\u5192\u7740\u8fd9\u4e2a\u51ac\u5b63\u7684\u6700\u540e\u4e00\u6b21\u98ce\u96ea\u51b2\u51fa\u6d1b\u9633\uff0c\u5954\u5411\u957f \u5b89\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6731\u731b\u73b0\u5728\u5f88\u53ef\u80fd\u5df2\u7ecf\u5230\u4e86\u957f\u5b89\u3002 \u3000\u3000\u5927\u9556\u5c40\u7684\u5b9e\u529b\u867d\u7136\u96c4\u539a\uff0c\u53ef\u662f\u529b\u91cf\u5927\u5206\u6563\uff0c\u5927\u9556\u5c40\u65d7\u4e0b\u7684\u4e00\u6d41\u597d\u624b\uff0c\u4eba\u591a\u662f\u96c4\u636e\u4e00\u65b9\u7684 \u6c5f\u6e56\u5927\u8c6a\uff0c\u5374\u4e0d\u4f1a\u8f7b\u6613\u79bb\u5f00\u81ea\u5df1\u7684\u6839\u636e\u5730\u5230\u957f\u5b89 \u3000\u3000\u6731\u731b\u8fd9\u6b21\u5e26\u5230\u957f\u5b89\u53bb\u7684\u4eba\uff0c\u5374\u90fd\u662f\u4ee5\u4e00\u5f53\u5341\u7684\u6b7b\u58eb\uff0c\u90fd\u6ca1\u6709\u6253\u7b97\u6d3b\u7740\u56de\u6d1b\u9633\u6765\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4e5f\u4e00\u5b9a\u4f1a\u770b\u51fa\u8fd9\u4e00\u70b9\uff0c\u7edd\u4e0d\u4f1a\u548c\u6731\u731b\u6b63\u9762\u786c\u6218\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e00\u5b9a\u6709\u65b9\u6cd5\u5bf9\u4ed8\u6731\u731b\uff0c\u4ed6\u7528\u7684\u65b9\u6cd5\u4e00\u5b9a\u6781\u6709\u6548\u3002 \u3000\u3000\u673a\u8bc8\u3001\u6b8b\u9177\u3001\u5351\u9119\uff0c\u53ef\u662f\u7edd\u5bf9\u6709\u6548\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6bd4\u53f8\u9a6c\u8d85\u7fa4\u66f4\u4e86\u89e3\u5353\u4e1c\u6765\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u80fd\u53ca\u65f6\u8d76\u56de\u53bb\uff0c\u80fd\u591f\u53ca\u65f6\u963b\u6b62\u5353\u4e1c\u6765\u505a\u51fa\u90a3\u79cd\u4e00\u5b9a\u4f1a\u8ba9\u4ed6\u89c9\u5f97\u9057\u61be\u7ec8\u751f\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u722c\u5f97\u591f\u9ad8\u4e86\uff0c\u5df2\u7ecf\u89c9\u5f97\u975e\u5e38\u75b2\u5026\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u60f3\u518d\u8e29\u7740\u6731\u731b\u7684\u8eaf\u4f53\u722c\u5230\u66f4\u9ad8\u4e00\u5c42\u697c\u4e0a\u53bb\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4f1a\u7528\u4ec0\u4e48\u65b9\u6cd5\u5bf9\u4ed8\u6731\u731b\u548c\u5c0f\u9ad8\uff1f\u53f8\u9a6c\u8d85\u7fa4\u8fd8\u6ca1\u6709\u60f3\u5230\uff0c\u4e5f\u6ca1\u6709\u8ba4\u771f\u53bb\u60f3\u8fc7\u3002\u6ee1\u5929 \u96ea\u82b1\u98de\u821e\uff0c\u5c31\u50cf\u662f\u4e00\u53ea\u53ea\u98de\u821e\u7740\u7684\u8774\u8776\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u5ffd\u7136\u6c89\u4e86\u4e0b\u53bb\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u77e5\u9053\u5353\u4e1c\u672a\u7528\u7684\u662f\u4ec0\u4e48\u6cd5\u5b50\u4e86\u3002 \u4e8c \u3000\u3000\u540c\u65e5\uff0c\u957f\u5b89\u3002 \u3000\u3000\u957f\u5b89\u5c45\u3002 \u3000\u3000\u957f\u5b89\u5c45\u7684\u7b2c\u4e00\u697c\u5728\u4e00\u7247\u51b7\u9999\u4e07\u6735\u6885\u82b1\u95f4\u3002 \u3000\u3000\u697c\u4e0a\u6ca1\u6709\u751f\u706b\uff0c\u751f\u706b\u5c31\u4fd7\u4e86\uff0c\u8d4f\u6885\u8981\u51b7\uff0c\u8d8a\u51b7\u8d8a\u9999\uff0c\u8d8a\u51b7\u8d8a\u96c5\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u5f53\u7136\u53ea\u6709\u90a3\u4e9b\u62e5\u8c82\u88d8\u996e\u9187\u9152\u4ece\u6765\u4e0d\u77e5\u9965\u5bd2\u4e3a\u4f55\u7269\u7684\u4eba\u624d\u4f1a\u660e\u767d\uff0c\u7ec8\u5e74\u90fd\u5403\u4e0d\u70ae\u7a7f \u4e0d\u6696\u7684\u4eba\u5f53\u7136\u662f\u4e0d\u4f1a\u61c2\u7684\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4e24\u4f4d\u5c45\u7136\u6bd4\u6211\u6765\u5f97\u8fd8\u65e9\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u4e0a\u697c\u65f6\uff0c\u6731\u731b\u548c\u5c0f\u9ad8\u5df2\u7ecf\u9ad8\u5750\u5728\u697c\u5934\uff0c\u4e00\u575b\u9152\u5df2\u7ecf\u53ea\u5269\u4e0b\u534a\u575b\u3002 \u3000\u3000\u201c\u4f38\u5934\u4e5f\u662f\u4e00\u5200\uff0c\u7f29\u5934\u4e5f\u662f\u4e00\u5200\uff0c\u65e2\u7136\u662f\u6765\u5b9a\u7684\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u70b9\u6765\uff0c\u5148\u628a\u8fd9\u91cc\u4e0d\u8981\u94b1 \u7684\u597d\u9152\u559d\u4ed6\u5a18\u7684\u4e00\u4e2a\u75db\u5feb\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u6731\u5802\u4e3b\u8bf4\u7684\u662f\uff0c\u662f\u65e9\u70b9\u6765\u7684\u597d\u3002\u201d\u5353\u4e1c\u6765\u5fae\u7b11\uff1a\u201c\u6765\u5f97\u8d8a\u65e9\uff0c\u770b\u5230\u7684\u8d8a\u591a\u3002\u201d \u3000\u3000\u4ed6\u5c06\u697c\u4e0a\u7a97\u6237\u4e00\u6247\u6247\u5168\u90fd\u63a8\u5f00\uff1a\u201c\u9664\u4e86\u8fd9\u6ee1\u56ed\u6885\u82b1\u5916\uff0c\u6731\u5802\u4e3b\u8fd8\u770b\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd8\u770b\u5230\u4e86\u4e00\u5927\u5806\u72d7\u5c4e\u3002\u201d\u6731\u731b\u54a7\u5f00\u5927\u5634\uff1a\u201c\u4e5f\u4e0d\u77e5\u662f\u4ece\u54ea\u91cc\u7a9c\u51fa\u6765\u7684\u91ce\u72d7\u62c9\u51fa\u6765 \u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u795e\u8272\u4e0d\u53d8\uff0c\u4e5f\u4e0d\u751f\u6c14\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u70b9\u6211\u4e5f\u4e0d\u5927\u6e05\u695a\u4e86\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u5012\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u90a3\u6761\u91ce\u72d7\u7edd\u4e0d\u662f\u6211\u5e03\u4e0b\u7684 \u57cb\u4f0f\uff0c\u4e5f\u4e0d\u662f\u4ece\u5927\u9556\u5c40\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5b83\u4e0d\u662f\u4ece\u5927\u9556\u5c40\u6765\u7684\uff1f\u201d\u6731\u731b\u51b7\u7b11\uff1a\u201c\u4f60\u95ee\u8fc7\u5b83\uff1f\u4f60\u4eec\u8c08\u8fc7\u8bdd\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u4ecd\u7136\u9762\u5e26\u5fae\u7b11\u3002 \u3000\u3000\u201c\u6709\u4e9b\u4e8b\u662f\u4e0d\u5fc5\u95ee\u7684\u3002\u201d\u5353\u4e1c\u6765\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\u6731\u5802\u4e3b\u770b\u5230\u4e86\u4e00\u5806\u72d7\u5c4e\uff0c\u5c31\u77e5\u9053\u90a3\u662f\u72d7\u62c9 \u7684\u5c4e\uff0c\u4e5f\u4e0d\u5fc5\u518d\u53bb\u95ee\u90a3\u5806\u5c4e\u662f\u4e0d\u662f\u72d7\u62c9\u51fa\u6765\u7684\uff0c\u72d7\u548c\u72d7\u5c4e\u90fd\u4e00\u6837\u4e0d\u4f1a\u8bf4\u8bdd\uff0f \u3000\u3000\u6731\u731b\u5927\u7b11\u3002 \u3000\u3000\u201c\u597d\uff0c\u8bf4\u5f97\u597d\uff0c\u8001\u5b50\u8bf4\u4e0d\u8fc7\u4f60\u3002\u201d\u4ed6\u5927\u7b11\u4e3e\u676f\uff0c\u201d\u8001\u5b50\u53ea\u6709\u8ddf\u4f60\u559d\u9152\u3002\u201d \u3000\u3000\u201c\u559d\u9152\u6211\u4e5f\u5949\u966a\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u4e5f\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff1a\u201c\u53ea\u4e0d\u8fc7\u6709\u4ef6\u4e8b\u4f60\u6211\u5fc3\u91cc\u4e00\u5b9a\u5f88\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6731\u5802\u4e3b\u80af\u8d4f\u5149\u5230\u8fd9\u91cc\u6765\uff0c\u5f53\u7136\u5e76\u4e0d\u662f\u53ea\u4e3a\u4e86\u8981\u6765\u559d\u51e0\u676f\u6c34\u9152\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6731\u5802\u4e3b\u5230\u8fd9\u91cc\u6765\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u8981\u770b\u770b\u6211\u5353\u4e1c\u6765\u7a76\u7adf\u60f3\u73a9\u4ec0\u4e48\u628a\u620f\uff1f\u201d \u3000\u3000\u6731\u731b\u53c8\u5927\u7b11\uff1a\u201c\u8fd9\u4e00\u6b21\u4f60\u53c8\u8bf4\u5bf9\u4e86\uff0c\u8bf4\u5f97\u771f\u4ed6\u5a18\u7684\u4e00\u70b9\u90fd\u4e0d\u9519\uff0f \u3000\u3000\u4ed6\u7684\u7b11\u58f0\u5ffd\u7136\u505c\u987f\uff0c\u4e00\u53cc\u5e03\u6ee1\u8840\u4e1d\u7684\u5927\u773c\u4e2d\u51fb\u51fa\u4e86\u95ee\u7535\u822c\u7684\u5389\u5149\uff0c\u5389\u58f0\u95ee\u5353\u4e1c\u6765\uff1a\u201c\u4f60 \u7a76\u7adf\u60f3\u73a9\u4ec0\u4e48\u628a\u620f\uff1f\u201d \u3000\u3000\u201c\u5176\u5b9e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u628a\u620f\uff0c\u5c31\u7b97\u6709\uff0c\u73a9\u628a\u620f\u7684\u4eba\u4e5f\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u5012\u4e86\u676f\u9152\uff0c\u6d45\u6d45\u7684\u555c\u4e86\u4e00\u53e3\uff0c\u7136\u540e\u624d\u7528\u4ed6\u90a3\u79cd\u72ec\u7279\u7684\u53e3\u6c14\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a \u3000\u3000\u201c\u4eca\u5929\u665a\u4e0a\u6211\u8bf7\u6731\u5802\u4e3b\u5230\u8fd9\u91cc\u6765\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6709\u4e2a\u4eba\u4eca\u591c\u8981\u4e3a\u541b\u4e00\u821e\uff0f \u3000\u3000\u6731\u731b\u7684\u8138\u8272\u9aa4\u7136\u53d8\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5fc3\u91cc\u662f\u4ec0\u4e48\u611f\u89c9\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u4e86\u89e3\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\uff0c\u5200\u522e\u3001\u9488\u523a\u3001\u706b\u7099\uff0c\u90fd\u4e0d\u8db3\u4ee5\u5f62\u5bb9\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5374\u5df2\u5411\u5c0f\u9ad8\u4e3e\u676f\u3002 \u3000\u3000\u201c\u8776\u821e\u4e4b\u821e\uff0c\u51a0\u7edd\u5929\u4e0b\uff0c\u7edd\u4e0d\u662f\u8f7b\u6613\u80fd\u770b\u5f97\u5230\u7684\uff0c\u4f60\u6211\u4eca\u65e5\u7684\u773c\u798f\u90fd\u4e0d\u6d45\uff0f \u3000\u3000\u5c0f\u9ad8\u6c89\u9ed8\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7b11\u4e86\u7b11\uff1a\u201c\u53ea\u4e0d\u8fc7\u4eca\u591c\u6211\u8bf7\u9ad8\u5144\u6765\u770b\u7684\uff0c\u5e76\u4e0d\u8d77\u8fd9\u4e00\u821e\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u672a\u770b\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u4e2a\u4eba\u3002\u201d\u5353\u4e1c\u6765\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u4e00\u4f4d\u9ad8\u5144\u4e00\u5b9a\u5f88\u60f3\u770b\u5230\u7684\u4eba\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u8138\u8272\u4e5f\u53d8\u4e86\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u8fde\u59d3\u540d\u90fd\u4e0d\u77e5\u9053\u7684\u5973\u4eba\uff0c\u4e00\u6bb5\u6c38\u751f\u90e8\u4e0d\u80fd\u5fd8\u6000\u7684\u611f\u60c5\u3002 \u3000\u3000\u5353\u4e1c\u6765\u60a0\u7136\u800c\u7b11\uff1a\u201c\u9ad8\u5144\u73b0\u5728\u60f3\u5fc5\u5df2\u7ecf\u731c\u51fa\u6211\u8bf4\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u201c\u6ce2\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5c0f\u9ad8\u624b\u91cc\u7684\u9152\u676f\u7c89\u788e\uff0c\u788e\u7247\u4e00\u7247\u7247\u523a\u4eba\u638c\u5fc3\u3002 \u3000\u3000\u6731\u731b\u5ffd\u7136\u864e\u543c\u4e00\u58f0\uff0c\u4f38\u51fa\u9752\u7b4b\u51f8\u8d77\u7684\u5927\u624b\uff0c\u4e00\u628a\u63ea\u4f4f\u4e86\u5353\u4e1c\u6765\u7684\u8863\u895f\uff0c\u201c\u5979\u5728\u54ea\u91cc\uff1f\u4f60 \u8bf4\u7684\u90a3\u4e2a\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u52a8\u4e5f\u4e0d\u52a8\uff0c\u51b7\u51b7\u7684\u770b\u7740\u4ed6\u7684\u624b\uff0c\u76f4\u7b49\u8fd9\u53ea\u624b\u653e\u677e\u4e86\u4ed6\u7684\u8863\u895f\uff0c\u4ed6\u624d\u6162\u6162\u7684\u8bf4\u9053\uff1a \u201c\u6211\u8bf4\u7684\u4eba\u5f88\u5feb\u5c31\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u6d3b\u4ed6\u597d\u50cf\u662f\u5bf9\u6731\u731b\u8bf4\u7684\uff0c\u53ef\u662f\u4ed6\u7684\u773c\u775b\u5374\u5728\u770b\u7740\u5c0f\u9ad8\u3002 \u4e09 \u3000\u3000\u8fd9\u65f6\u5019\u5df2\u7ecf\u6709\u4e00\u8f86\u53d1\u4eae\u7684\u9ed1\u6f06\u9a6c\u8f66\u5728\u957f\u5b89\u5c45\u7684\u5927\u95e8\u5916\u505c\u4e0b\u3002 \u3000\u3000\u5706\u6797\u4e2d\u9690\u9690\u6709\u4e1d\u7af9\u7ba1\u5f26\u4e4b\u58f0\u4f20\u51fa\u6765\uff0c\u4e50\u58f0\u51c4\u7f8e\uff0c\u4f34\u7740\u6b47\u58f0\u4f4e\u5531\uff0c\u5531\u7684\u662f\u4eba\u751f\u7684\u60b2\u6b22\u79bb \u5408\uff0c\u6b4c\u58f0\u4e2d\u5145\u6ee1\u4e86\u4e00\u79cd\u65e0\u53ef\u5948\u4f55\u7684\u60b2\u4f24\u3002 \u3000\u3000\u201c\u6625\u53bb\u53c8\u6625\u6765\uff0c\u82b1\u5f00\u53c8\u82b1\u843d\uff1b \u3000\u3000\u5230\u4e86\u79bb\u522b\u65f6\uff0c\u6709\u8c01\u80fd\u7559\u4e0b\uff1f\u201d \u3000\u3000\u8776\u821e\u75f4\u75f4\u7684\u5750\u5728\u8f66\u53a2\u91cc\uff0c\u75f4\u75f4\u7684\u542c\u7740\uff0c\u98ce\u4e2d\u4e5f\u4e0d\u77e5\u4ece\u54ea\u91cc\u5439\u6765\u4e00\u7247\u67af\u6b7b\u5df2\u4e45\u7684\u843d\u53f6\uff0c\u8774 \u8776\u822c\u8f7b\u8f7b\u7684\u98d8\u843d\u5728\u96ea\u5730\u4e0a\u3002 \u3000\u3000\u5979\u63a8\u5f00\u8f66\u95e8\u8d70\u4e0b\u6765\uff0c\u62fe\u8d77\u8fd9\u7247\u843d\u53f6\uff0c\u75f4\u75f4\u7684\u770b\u7740\uff0c\u4e5f\u4e0d\u77e5\u770b\u4e86\u591a\u4e45\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u4ece\u54ea\u91cc\u6ef4\u843d\u4e0b\u4e00\u6ef4\u6c34\u73e0\uff0c\u6ef4\u843d\u5728\u8fd9\u7247\u843d\u53f6\u4e0a\uff0c\u4e5f\u4e0d\u77e5\u662f\u6cea\u8fd8\u662f\u96e8\uff1f\u770b\u8d77\u6765\u5374\u50cf\u662f \u6625\u65e5\u767e\u82b1\u76db\u653e\u65f6\u7eff\u53f6\u4e0a\u6676\u83b9\u7684\u9732\u73e0\u4e00\u6837\u3002 \u56db \u3000\u3000\u51b7\u9999\u6ee1\u6402\uff0c\u51b7\u98ce\u6ee1\u697c\uff0c\u6731\u731b\u5374\u5c06\u8863\u895f\u62c9\u5f97\u66f4\u5f00\uff0c\u4eff\u4f5b\u60f3\u8981\u8ba9\u8fd9\u5200\u950b\u822c\u7684\u51b7\u98ce\u523a\u5165\u4ed6\u5fc3 \u91cc\u3002 \u3000\u3000\u4ed6\u548c\u5c0f\u9ad8\u90fd\u6ca1\u6709\u5f00\u53e3\u3002\u90a3\u79cd\u53c8\u751c\u53c8\u6d53\u53c8\u9178\u53c8\u82e6\u7684\u601d\u5ff5\u5df2\u7ecf\u5835\u585e\u4f4f\u4ed6\u4eec\u7684\u54bd\u5589\u3002 \u3000\u3000\u4e00\u4e2a\u81ea\u53d1\u82cd\u82cd\u7684\u66fe\u76ee\u8001\u4eba\uff0c\u4ee5\u7af9\u6756\u70b9\u5730\uff0c\u6162\u6162\u7684\u8d70\u4e0a\u697c\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u68b3\u7740\u6761\u5927\u8fab\u5b50\u7684\u5c0f\u59d1\u5a18\uff0c\u7275\u7740\u8001\u4eba\u7684\u8863\u89d2\uff0c\u8ddf\u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u8001\u4eba\u6301\u6d1e\u8427\uff0c\u5c11\u5973\u62b1\u7435\u7436\uff0c\u663e\u7136\u662f\u51c6\u5907\u6765\u4e3a\u8776\u821e\u4f34\u594f\u7684\u4e50\u8005\uff0c\u8001\u4eba\u6ee1\u5e03\u76b1\u7eb9\u7684\u8138\u4e0a\u867d\u7136 \u5168\u65e0\u8868\u60c5\uff0c\u53ef\u662f\u6bcf\u6761\u76b1\u7eb9\u91cc\u90fd\u50cf\u662f\u4e00\u5ea7\u575f\u5893\uff0c\u57cb\u846c\u7740\u6570\u4e0d\u6e05\u7684\u82e6\u96be\u548c\u60b2\u4f24\u3002 \u3000\u3000\u4eba\u4e16\u95f4\u7684\u60b2\u4f24\u4e8b\u4ed6\u5df2\u770b\u5f97\u5927\u591a\u3002 \u3000\u3000\u5c11\u5973\u5374\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u89c1\u8fc7\uff0c\u56e0\u4e3a\u5979\u4e5f\u662f\u4e2a\u778e\u5b50\uff0c\u4e00\u751f\u4e0b\u6765\u5c31\u662f\u4e2a\u778e\u5b50\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u770b\u89c1 \u8fc7\u5149\u660e\uff0c\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u9752\u6625\u7684\u6b22\u4e50\u662f\u4ec0\u4e48\u6837\u5b50\u7684\u3002 \u3000\u3000\u8fd9\u4e48\u6837\u7684\u4e24\u4e2a\u4eba\uff0c\u600e\u4e48\u80fd\u594f\u5f97\u51fa\u5e78\u798f\u548c\u6b22\u4e50\uff1f \u3000\u3000\u8001\u4eba\u9ed8\u9ed8\u7684\u8d70\u4e0a\u6765\uff0c\u9ed8\u9ed8\u7684\u8d70\u5230\u4e00\u4e2a\u4ed6\u719f\u6089\u7684\u89d2\u843d\u91cc\u5750\u4e0b\u3002 \u3000\u3000\u4ed6\u5230\u8fd9\u91cc\u5df2\u7ecf\u4e0d\u662f\u7b2c\u4e00\u6b21\u4e86\uff0c\u6bcf\u4e00\u6b21\u6765\u594f\u7684\u90fd\u662f\u60b2\u6b4c\u3002 \u3000\u3000\u4e3a\u4e00\u4e9b\u5e73\u65f6\u7b11\u5f97\u5927\u591a\u7684\u4eba\u6765\u594f\u60b2\u6b4c\uff0c\u7528\u6b4c\u58f0\u6765\u6311\u8d77\u4ed6\u4eec\u5fc3\u91cc\u4e00\u4e9b\u79d8\u5bc6\u7684\u75db\u82e6\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u4e5f\u613f\u610f\u8ba9\u4ed6\u8fd9\u4e48\u6837\u505a\u3002 \u3000\u3000\u2014\u2014\u4eba\u7c7b\u5b9e\u5728\u662f\u79cd\u5947\u602a\u7684\u52a8\u7269\uff0c\u6709\u65f6\u7adf\u4f1a\u5c06\u60b2\u4f24\u548c\u75db\u82e6\u5f53\u4f5c\u79cd\u4eab\u53d7\u3002 \u3000\u3000\u697c\u4e0b\u53c8\u6709\u811a\u6b65\u58f0\u4f20\u6765\u4e86\u3002 \u3000\u3000\u5f88\u8f7b\u7684\u811a\u6b65\u58f0\uff0c\u8f7b\u800c\u9707\u52a8\u3002 \u3000\u3000\u542c\u89c1\u8fd9\u811a\u6b65\u58f0\uff0c\u5c0f\u9ad8\u7684\u4eba\u5df2\u63a0\u8fc7\u684c\u5b50\uff0c\u7a9c\u5411\u697c\u68af\u53e3\uff0c\u51b2\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6731\u731b\u5374\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u7684\u5168\u8eab\u4eff\u4f5b\u90fd\u5df2\u50f5\u786c\uff0c\u53d8\u6210\u4e86\u4e00\u5177\u5df2\u7ecf\u5316\u6210\u4e86\u5ca9\u77f3\u7684\u5c38\u4f53\u3002\u4e0a\u53e4\u65f6\u6b7b\u4eba\u7684\u5c38\u4f53\u3002 \u3000\u3000\u4e00\u4e2a\u8fde\u59d3\u540d\u90fd\u4e0d\u77e5\u9053\u7684\u5973\u4eba\uff0c\u4e00\u6bb5\u6c38\u751f\u90fd\u4e0d\u80fd\u5fd8\u6000\u7684\u611f\u60c5\u3002 \u3000\u3000\u5c0f\u9ad8\u672c\u6765\u4ee5\u4e3a\u81ea\u5df1\u6c38\u8fdc\u89c1\u4e0d\u5230\u5979\u4e86\uff0c\u53ef\u662f\u73b0\u5728\u5979\u5df2\u7ecf\u5728\u4ed6\u773c\u524d\u3002 \u3000\u3000\u4e00\u8fd9\u662f\u4e0d\u662f\u68a6\uff1f \u3000\u3000\u5979\u4e5f\u770b\u5230\u4e86\u4ed6\u3002 \u3000\u3000\u5979\u75f4\u75f4\u7684\u770b\u7740\u4ed6\uff0c\u4e5f\u4e0d\u77e5\u662f\u60ca\u5947\uff1f\u662f\u6b22\u559c\uff1f\u662f\u60f3\u8fce\u4e0a\u53bb\uff1f\u8fd8\u662f\u60f3\u9003\u907f\uff1f \u3000\u3000\u5c0f\u9ad8\u6ca1\u6709\u8ba9\u5979\u9009\u62e9\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u51b2\u4e0a\u53bb\uff0c\u62c9\u4f4f\u4e86\u5979\uff0c\u7528\u4e24\u53ea\u624b\u62c9\u4f4f\u4e86\u5979\u7684\u4e24\u53ea\u624b\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u68a6\uff0c\u4e5f\u4e0d\u662f\u5e7b\u89c9\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u7684\u611f\u89c9\u662f\u90a3\u4e48\u6e29\u6696\u5145\u5b9e\uff0c\u4ed6\u5fc3\u91cc\u7684\u611f\u89c9\u4e5f\u662f\u90a3\u4e48\u6e29\u6696\u5145\u5b9e\u3002 \u3000\u3000\u201c\u90a3\u5929\u4f60\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f\u5230\u54ea\u91cc\u4f1a\u4e86\uff1f\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\u8bdd\u5c0f\u9ad8\u90fd\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u4eec\u80fd\u591f\u76f8\u89c1\uff0c\u522b\u7684\u4e8b\u90fd\u4e0d\u91cd\u8981\u3002 \u3000\u3000\u201c\u4f60\u6765\u4e86\uff0c\u4f60\u771f\u7684\u6765\u4e86\uff0c\u8fd9\u6b21\u6211\u518d\u4e5f\u4e0d\u4f1a\u8ba9\u4f60\u8d70\u4e86\u3002\u201d \u3000\u3000\u4ed6\u62c9\u4f4f\u5979\uff0c\u5012\u9000\u7740\u4e00\u7ea7\u7ea7\u8d70\u4e0a\u697c\u68af\uff0c\u4ed6\u7684\u773c\u775b\u518d\u4e5f\u820d\u4e0d\u5f97\u79bb\u5f00\u5979\u7684\u8138\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5979\u7684\u8138\u4e0a\u8d77\u4e86\u79cd\u8c01\u90fd\u65e0\u6cd5\u9884\u6599\u7684\u53d8\u5316\u3002 \u3000\u3000\u5979\u7684\u77b3\u5b54\u7a81\u7136\u56e0\u6050\u60e7\u800c\u6536\u7f29\uff0c\u53c8\u7a81\u7136\u6269\u6563\uff0c\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u5d29\u6e83\u865a\u8131\u3002 \u3000\u3000\u2014\u2014\u5979\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u5c0f\u9ad8\u5403\u60ca\u7684\u770b\u7740\u5979\uff0c\u672c\u6765\u60f3\u7acb\u523b\u56de\u5934\u53bb\u627e\u5979\u770b\u89c1\u7684\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u81ea\u5df1\u8138\u4e0a\u5ffd\u7136\u4e5f\u8d77\u4e86\u79cd\u53ef\u6015\u7684\u53d8\u5316\uff0c\u4eff\u4f5b\u5ffd\u7136\u60f3\u5230\u4e86\u4e00\u4ef6\u6781\u53ef\u6015\u7684\u4e8b\uff0c\u8fc7\u4e86\u5f88\u4e45 \u5f88\u4e45\u5f88\u4e45\u4e4b\u540e\uff0c\u624d\u6562\u56de\u5934\u3002 \u3000\u3000\u4ed6\u56de\u8fc7\u5934\uff0c\u5c31\u770b\u89c1\u4e86\u6731\u731b\u3002 \u3000\u3000\u6731\u731b\u8138\u4e0a\u7684\u8868\u60c5\u770b\u6765\u5c31\u50cf\u662f\u53ea\u91ce\u517d\uff0c\u4e00\u53ea\u5df2\u843d\u5165\u730e\u4eba\u9677\u8bb2\u7684\u91ce\u517d\uff0c\u60b2\u4f24\u6124\u6012\u800c\u7edd\u671b\u3002 \u3000\u3000\u4ed6\u5728\u770b\u7740\u7684\u4eba\u5c31\u662f\u5c0f\u9ad8\u62c9\u4e0a\u697c\u6765\u7684\u4eba\u3002 \u3000\u3000\u8776\u821e\u3002 \u3000\u3000\u5ffd\u7136\u95f4\u5c0f\u9ad8\u5df2\u7ecf\u5b8c\u5168\u660e\u767d\u4e86\u3002 \u3000\u3000\u8776\u821e\u3002 \u3000\u3000\u8fd9\u4e2a\u4ed6\u9b42\u7275\u68a6\u8426\u6c38\u96be\u5fd8\u6000\u7684\u5973\u4eba\uff0c\u5c31\u662f\u6731\u731b\u9b42\u7275\u68a6\u8426\u6c38\u96be\u5fd8\u6000\u7684\u8776\u821e\u3002 \u3000\u3000\u2014\u2014\u547d\u8fd0\u4e3a\u4ec0\u4e48\u5982\u6b64\u6b8b\u9177\uff01 \u3000\u3000\u8fd9\u4e0d\u662f\u547d\u8fd0\uff0c\u4e5f\u4e0d\u662f\u5de7\u5408\uff0c\u7edd\u5bf9\u4e0d\u662f\u3002 \u3000\u3000\u5353\u4e1c\u6765\u770b\u7740\u4ed6\u4eec\uff0c\u773c\u4e2d\u7684\u7b11\u610f\u5c31\u50cf\u662f\u4e00\u4e2a\u90aa\u795e\u5728\u770b\u7740\u611a\u4eba\u4eec\u4e3a\u4ed6\u5949\u732e\u7684\u796d\u672d\u3002 \u3000\u3000\u624b\u51b0\u51b7\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u624b\u90fd\u662f\u51b0\u51b7\u7684\u3002 \u3000\u3000\u5c0f\u9ad8\u653e\u5f00\u4e86\u8776\u821e\u51b0\u51b7\u7684\u624b\uff0c\u53c8\u5f00\u59cb\u5f80\u540e\u9000\uff0c\u9000\u5165\u4e86\u4e00\u4e2a\u89d2\u843d\u3002 \u3000\u3000\u6731\u731b\u7684\u773c\u775b\u73b0\u5728\u5df2\u7ecf\u76ef\u5728\u4ed6\u8138\u4e0a\uff0c\u4e00\u53cc\u6ee1\u5e03\u8840\u4e1d\u7684\u5927\u773c\u5c31\u50cf\u662f\u5df2\u7ecf\u53d8\u6210\u4e86\u4e00\u67c4\u957f\u67aa\u3002 \u3000\u3000\u4e00\u67c4\u8840\u6dcb\u6dcb\u7684\u957f\u67aa\u3002 \u3000\u3000\u5c0f\u9ad8\u6b7b\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u867d\u7136\u8fd8\u6ca1\u6709\u6b7b\uff0c\u53ef\u662f\u4ed6\u7684\u5fc3\u5df2\u7ecf\u88ab\u523a\u6b7b\u5728\u8fd9\u67c4\u8840\u6dcb\u6dcb\u7684\u957f\u67aa\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u6b7b\u4e5f\u4e0d\u80fd\u89e3\u8131\u3002 \u3000\u3000\u2014\u2014\u6731\u731b\u4f1a\u600e\u4e48\u6837\u5bf9\u4ed6\uff1f\u4ed6\u5e94\u8be5\u600e\u4e48\u6837\u5bf9\u6731\u731b\uff1f \u3000\u3000\u5c0f\u9ad8\u4e0d\u6562\u53bb\u60f3\uff0c\u4e5f\u60f3\u4e0d\u51fa\u3002\u4ed6\u6839\u672c\u5c31\u65e0\u6cd5\u601d\u60f3\u3002 \u3000\u3000\u4ed6\u552f\u4e00\u80fd\u505a\u7684\u4e00\u4ef6\u4e8b\u5c31\u662f\u201c\u8d70\u201d\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u5c31\u5728\u4ed6\u51c6\u5907\u8981\u8d70\u7684\u65f6\u5019\u5ffd\u7136\u6709\u4eba\u53eb\u4f4f\u4e86\u4ed6\uff1a\u201c\u7b49\u4e00\u7b49\uff0f \u3000\u3000\u5c0f\u9ad8\u5403\u60ca\u7684\u53d1\u73b0\u8776\u821e\u5c45\u7136\u5df2\u5b8c\u5168\u6062\u590d\u4e86\u51b7\u9759\uff0c\u5c45\u7136\u5df2\u4e0d\u4f2f\u9762\u5bf9\u4ed6\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u8981\u8d70\u4e86\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u975e\u8d70\u4e0d\u53ef\u3002\u201d\u8776\u821e\u8bf4\uff1a\u201c\u53ef\u662f\u4f60\u4e00\u5b9a\u8981\u7b49\u4e00\u7b49\u518d\u8d70\u3002\u201d \u3000\u3000\u5979\u7684\u6001\u5ea6\u51b7\u9759\u800c\u575a\u51b3\uff0c\u5979\u7684\u773c\u775b\u91cc\u4eff\u4f5b\u6709\u4e00\u79cd\u53ef\u4ee5\u4f7f\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u62d2\u7edd\u5979\u7684\u529b\u91cf\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u53ea\u6709\u5728\u5bf9\u6240\u6709\u7684\u4e00\u5207\u4e8b\u90fd\u5168\u65e0\u6240\u60e7\u65f6\uff0c\u624d\u4f1a\u4ea7\u751f\u8fd9\u79cd\u529b\u91cf\u3002 \u3000\u3000\u8776\u821e\u53c8\u8f6c\u8eab\u9762\u5bf9\u6731\u731b\uff1a\u201c\u6211\u8bb0\u5f97\u4f60\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u5728\u6211\u8981\u8d77\u821e\u65f6\uff0c\u8c01\u4e5f\u4e0d\u80fd\u8d70\uff0f \u3000\u3000\u6731\u731b\u7684\u53cc\u62f3\u7d2b\u63e1\uff0c\u5c31\u597d\u50cf\u8981\u628a\u8fd9\u4e2a\u4e16\u754c\u653e\u5728\u4ed6\u7684\u624b\u638c\u91cc\u634f\u788e\uff0c\u628a\u6240\u6709\u7684\u4e00\u5207\u5168\u90fd\u6bc1\u706d\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5374\u7b11\u4e86\uff0c\u9634\u607b\u607b\u7684\u5fae\u7b11\u7740\u95ee\u8776\u821e\uff1a\u201c\u4f60\u8fd8\u80fd\u821e\uff1f\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u770b\u89c1\u8fc7\u5410\u4e1d\u7684\u6625\u8695\uff1f\u201d\u8776\u821e\u8bf4\uff1a\u201c\u53ea\u8981\u5b83\u8fd8\u6ca1\u6709\u6b7b\uff0c\u5b83\u7684\u4e1d\u5c31\u4e0d\u4f1a\u5c3d\u3002\u201d \u3000\u3000\u5979\u8bf4\uff1a\u201c\u6211\u4e5f\u4e00\u6837\uff0c\u53ea\u8981\u6211\u8fd8\u6d3b\u7740\uff0c\u6211\u5c31\u80fd\u821e\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u62ca\u638c\uff1a\u201c\u90a3\u5c31\u5b9e\u5728\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u72d0\u6c05\u843d\u4e0b\uff0c\u821e\u8863\u98d8\u8d77\u3002 \u3000\u3000\u4e00\u76f4\u9ed8\u9ed8\u5750\u5728\u4e00\u9685\u7684\u767d\u5934\u4e50\u5e08\u5ffd\u7136\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u6194\u60b4\u75b2\u5026\u7684\u8001\u8138\u770b\u6765\u5c31\u50cf\u4e1b\u4e00\u56e2\u63c9\u76b1\u4e86 \u7684\u9ec4\u7eb8\u3002 \u3000\u3000\u201c\u6211\u662f\u4e2a\u778e\u5b50\uff0c\u53c8\u8001\u53c8\u778e\uff0c\u5fc3\u91cc\u5df2\u7ecf\u6709\u5f88\u4e45\u6ca1\u6709\u60f3\u8d77\u8fc7\u4e00\u70b9\u80fd\u591f\u8ba9\u6211\u89c9\u5f97\u5f00\u5fc3\u7684\u4e8b\uff0c\u6240 \u4ee5\u6211\u4e3a\u5927\u7237\u4eec\u594f\u7684\u603b\u662f\u4e9b\u4f24\u5fc3\u7684\u4e50\u66f2\u3002\u201d\u4ed6\u6162\u6162\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u4eca\u5929\u6211\u5374\u8981\u7834\u4f8b\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u7834\u4f8b\u4e3a\u6211\u4eec\u594f\u4e00\u66f2\u5f00\u5fc3\u7684\u8c03\u5b50\uff1f\u201d\u5353\u672a\u6765\u95ee\u3002 \u3000\u3000\u201c\u662f\u7684\uff0f \u3000\u3000\u201c\u4eca\u5929\u4f60\u6709\u6ca1\u6709\u60f3\u8d77\u4ec0\u4e48\u5f00\u5fc3\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0f \u3000\u3000\u201c\u65e2\u7136\u6ca1\u6709\uff0c\u4e3a\u4ec0\u4e48\u8981\u7834\u4f8b\uff1f\u201d \u3000\u3000\u767d\u5934\u4e50\u5e08\u7528\u4e00\u53cc\u6839\u672c\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u7684\u778e\u773c\uff0c\u51dd\u89c6\u7740\u8fdc\u65b9\u7684\u9ed1\u6697\uff0c\u4ed6\u7684\u58f0\u97f3\u6c99\u54d1\u800c\u54c0\u4f24\uff1a \u201c\u6211\u867d\u7136\u662f\u4e2a\u778e\u5b50\uff0c\u53c8\u8001\u53c8\u778e\uff0c\u53ef\u662f\u6211\u8fd8\u662f\u80fd\u611f\u89c9\u5230\u4eca\u5929\u8fd9\u91cc\u7684\u60b2\u4f24\u4e8b\u5df2\u7ecf\u592a\u591a\u4e86\uff0f \u3000\u3000\u201c\u94ee\u742e\u201d\u4e00\u58f0\uff0c\u7435\u7436\u54cd\u8d77\uff0c\u8001\u8005\u7684\u7b2c\u4e00\u58f0\u5c31\u50cf\u662f\u4e00\u6839\u4e1d\u4e00\u6837\u5f15\u52a8\u4e86\u7435\u7436\u3002 \u3000\u3000\u4e00\u6839\u4e1d\u53d8\u6210\u4e86\u65e0\u6570\u6839\uff0c\u7435\u7436\u7684\u5f26\u58f0\u5982\u73e0\u843d\u7389\u76d8\u3002 \u3000\u3000\u6bcf\u4e00\u6839\u4e1d\uff0c\u6bcf\u4e00\u7c92\u73e0\uff0c\u90fd\u662f\u8f7b\u76c8\u800c\u6b22\u5077\u7684\uff0c\u4eca\u5929\u4ed6\u6240\u594f\u7684\u4e0d\u518d\u662f\u4eba\u751f\u4e2d\u90a3\u4e9b\u65e0\u53ef\u5948\u4f55\u7684 \u60b2\u4f24\u3002 \u3000\u3000\u4ed6\u6240\u594f\u7684\u662f\u751f\u547d\u7684\u6b22\u4e50\u3002 \u3000\u3000\u8776\u821e\u5728\u821e\u3002 \u3000\u3000\u5979\u7684\u821e\u59ff\u4e5f\u540c\u6837\u8f7b\u76c8\u6b22\u6109\uff0c\u4eff\u4f5b\u5df2\u628a\u5979\u751f\u547d\u4e2d\u6240\u6709\u7684\u82e6\u96be\u5168\u90e8\u5fd8\u8bb0\u3002 \u3000\u3000\u5979\u7684\u751f\u547d\u5df2\u7ecf\u548c\u5979\u7684\u821e\u878d\u4e3a\u4e00\u4f53\uff0c\u5979\u5df2\u7ecf\u628a\u5979\u7684\u751f\u547d\u878d\u5165\u5979\u7684\u821e\u91cc\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u751f\u547d\u4e2d\u5269\u4e0b\u6765\u7684\u5df2\u7ecf\u53ea\u6709\u821e\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u662f\u821e\u8005\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u523b\u95f4\uff0c\u5979\u5df2\u4e0d\u518d\u662f\u90a3\u4e2a\u9971\u7ecf\u6ca7\u6851\u3001\u9971\u53d7\u82e6\u96be\u7684\u5973\u4eba\uff0c\u800c\u662f\u821e\u8005\uff0c\u90a3\u4e48\u9ad8\u8d35\uff0c\u90a3\u4e48 \u7eaf\u6d01\uff0c\u90a3\u4e48\u7f8e\u4e3d\u3002 \u3000\u3000\u5979\u821e\u51fa\u4e86\u5979\u7684\u6b22\u4e50\u4e0e\u9752\u6625\uff0c\u5979\u7684\u9752\u6625\u4e0e\u6b22\u4e50\u4e5f\u5728\u821e\u4e2d\u6d88\u901d\u3002 \u3000\u3000\u201c\u5b9d\u5251\u65e0\u60c5\uff0c\u5e84\u751f\u65e0\u68a6\uff1b \u3000\u3000\u4e3a\u541b\u4e00\u821e\uff0c\u5316\u4f5c\u8774\u8776\uff0f \u3000\u3000\u5f39\u7435\u7436\u7684\u8001\u4eba\u5ffd\u7136\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4ed6\u594f\u7684\u662f\u6b22\u6109\u7684\u4e50\u66f2\uff0c\u53ef\u662f\u4ed6\u7a7a\u865a\u7684\u778e\u773c\u91cc\u5374\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4ed6\u770b\u4e0d\u89c1\u5c4b\u5b50\u91cc\u7684\u4eba\uff0c\u53ef\u662f\u4ed6\u611f\u89c9\u5f97\u5230\u3002 \u3000\u3000\u2014\u2014\u591a\u4e48\u60b2\u4f24\u7684\u4eba\uff0c\u591a\u4e48\u9ed1\u6697\u3002 \u3000\u3000\u4ed6\u594f\u51fa\u7684\u6b22\u6109\u4e50\u58f0\u53ea\u6709\u4f7f\u60b2\u4f24\u663e\u5f97\u66f4\u60b2\u4f24\uff0c\u4ed6\u594f\u51fa\u7684\u6b22\u6109\u4e50\u66f2\u5c31\u597d\u50cf\u5df2\u7ecf\u53d8\u5f97\u4e0d\u662f\u4e50 \u66f2\uff0c\u800c\u662f\u4e00\u79cd\u8bbd\u523a\u3002 \u3000\u3000\u53c8\u662f\u201c\u94ee\u201d\u7684\u4e00\u54cd\uff0c\u7435\u7436\u5f26\u65ad\u3002 \u3000\u3000\u821e\u4e5f\u65ad\u4e86\u3002 \u3000\u3000\u8776\u821e\u5c31\u50cf\u662f\u4e00\u7247\u843d\u53f6\u822c\u98d8\u843d\u5728\u5353\u4e1c\u6765\u8db3\u4e0b\uff0c\u5ffd\u7136\u4ece\u5353\u4e1c\u6765\u7684\u9774\u7b52\u91cc\u62bd\u51fa\u4e00\u628a\u5200\u3002 \u3000\u3000\u4e00\u628a\u5b9d\u77f3\u822c\u8000\u773c\u7684\u77ed\u5200\u3002 \u3000\u3000\u5979\u62ac\u8d77\u5934\uff0c\u770b\u4e86\u6731\u731b\u4e00\u773c\uff0c\u53c8\u8f6c\u8fc7\u5934\uff0c\u770b\u4e86\u5c0f\u9ad8\u4e00\u773c\u3002 \u3000\u3000\u5979\u624b\u91cc\u7684\u77ed\u5200\u5df2\u843d\u4e0b\uff0c\u843d\u5728\u5979\u7684\u819d\u76d6\u4e0a\u3002 \u3000\u3000\u8840\u82b1\u6e85\u8d77\u3002 \u3000\u3000\u5200\u950b\u4e00\u843d\u4e0b\uff0c\u8840\u82b1\u5c31\u6e85\u8d77\u3002 \u3000\u3000\u5979\u7684\u4e00\u53cc\u817f\u5728\u8fd9\u628a\u5200\u7684\u5200\u950b\u4e0b\u53d8\u5f97\u5c31\u597d\u50cf\u662f\u4e24\u6bb5\u8150\u70c2\u4e86\u7684\u6728\u5934\u3002 \u3000\u3000\u5200\u950b\u4e00\u843d\u4e0b\uff0c\u5979\u5c31\u5df2\u4e0d\u518d\u662f\u821e\u8005\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6c38\u8fdc\u90fd\u6ca1\u6709\u65ad\u817f\u7684\u821e\u8005\u3002 \u3000\u3000\u90a3\u4e48\u7f8e\u7684\u817f\uff0c\u90a3\u4e48\u8f7b\u76c8\u3001\u90a3\u4e48\u7075\u5de7\u3001\u90a3\u4e48\u7f8e\u3002 \u7b2c\u5341\u4e09\u7ae0\u3000\u5c60\u573a>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u5341\u4e09\u7ae0\u3000\u5c60\u573a \u4e00 \u3000\u3000\u4e8c\u6708\u4e8c\u5341\u56db\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u9ece\u660e\u4e4b\u524d\u3002 \u3000\u3000\u5929\u7a7a\u4e00\u7247\u9ed1\u6697\uff0c\u6bd4\u4e00\u5929\u4e2d\u4efb\u4f55\u65f6\u5019\u90fd\u9ed1\u6697\u3002\u9ad8\u6e10\u98de\u4e00\u4e2a\u4eba\u5750\u5728\u9ed1\u6697\u4e2d\uff0c\u51b7\u5f97\u8fde\u8840\u90fd\u4eff\u4f5b \u5df2\u7ed3\u51b0\u3002 \u3000\u3000\u201c\u6211\u6ca1\u6709\u9519\u3002\u201d\u4ed6\u4e00\u76f4\u4e0d\u65ad\u7684\u544a\u8bc9\u81ea\u5df1\uff1a\u201c\u6211\u6ca1\u6709\u5bf9\u4e0d\u8d77\u6731\u731b\uff0c\u4e5f\u6ca1\u6709\u5bf9\u4e0d\u8d77\u5979\uff0c\u6211\u6ca1 \u6709\u9519\u3002\u201d \u3000\u3000\u7231\u7684\u672c\u8eab\u5e76\u6ca1\u6709\u9519\u3002\u65e0\u8bba\u4efb\u4f55\u4e00\u4e2a\u4eba\u7231\u4e0a\u53e6\u5916\u4e00\u4e2a\u4eba\u90fd\u4e0d\u662f\u9519\u3002 \u3000\u3000\u4ed6\u7231\u4e0a\u8776\u821e\u65f6\u6839\u672c\u4e0d\u77e5\u9053\u8776\u821e\u662f\u6731\u731b\u7684\u5973\u4eba\uff0c\u4ed6\u8fde\u60f3\u90fd\u6ca1\u6709\u60f3\u5230\u8fc7\u3002 \u3000\u3000\u53ef\u662f\u6bcf\u5f53\u4ed6\u60f3\u8d77\u6731\u731b\u770b\u5230\u8776\u821e\u65f6\u800c\u4e0a\u7684\u8868\u60c5\uff0c\u4ed6\u5fc3\u91cc\u5c31\u4f1a\u6709\u79cd\u5200\u5272\u822c\u7684\u6b49\u759a\u6094\u6068\u4e4b\u610f\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4e5f\u60f3\u6251\u8fc7\u53bb\uff0c\u888d\u4f4f\u8840\u6cca\u4e2d\u7684\u8776\u821e\uff0c\u628a\u6240\u6709\u7684\u4e00\u5207\u5168\u90fd\u629b\u5f00\u3002\u62b1\u4f4f\u8fd9\u4e2a\u4ed6\u4e00\u751f\u4e2d\u552f \u4e00\u7684\u5973\u4eba\uff0c\u7167\u987e\u5979\u4e00\u8f88\u4e8e\uff0c\u7231\u5979\u4e00\u8f88\u5b50\uff0c\u4e0d\u7ba1\u5979\u7684\u817f\u662f\u4e0d\u662f\u65ad\u4e86\u90fd\u4e00\u6837\u7231\u5979\u3002 \u3000\u3000\u53ef\u662f\u6731\u731b\u5df2\u7ecf\u5148\u6251\u8fc7\u53bb\u62b1\u4f4f\u4e86\u5979\uff0c\u6240\u4ee5\u4ed6\u5c31\u9ed8\u9ed8\u7684\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u8d70\u3002 \u3000\u3000\u2014\u2014\u4ed6\u80fd\u8d70\u591a\u8fdc\uff1f\u8be5\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff1f\u8981\u8d70\u591a\u8fdc\u624d\u80fd\u5fd8\u8bb0\u8fd9\u4e9b\u4e8b\uff1f \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u6709\u8c01\u80fd\u66ff\u4ed6\u56de\u7b54\uff1f \u3000\u3000\u8ddd\u79bb\u5929\u4eae\u7684\u65f6\u5019\u8d8a\u8fd1\uff0c\u5927\u5730\u4eff\u4f5b\u8d8a\u9ed1\u6697\u3002\u5c0f\u9ad8\u8eba\u4e0b\u6765\uff0c\u8eba\u5728\u51b0\u51b7\u7684\u96ea\u5730\u4e0a\uff0c\u4ef0\u89c6\u7740\u9ed1\u6697 \u7684\u7a79\u82cd\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u2014\u2014\u65e2\u7136\u7741\u5f00\u9650\u775b\u4e5f\u53ea\u80fd\u770b\u5230\u4e00\u7247\u9ed1\u6697\uff0c\u95ed\u4e0a\u773c\u775b\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u201c\u8fd9\u6837\u5b50\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u4ed6\u624d\u521a\u95ed\u4e0a\u773c\u775b\uff0c\u5c31\u542c\u89c1\u4e00\u4e2a\u4eba\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u4eca\u5e74\u51ac\u5929\u957f\u5b89\u57ce\u91cc\u6700\u5c11\u4e5f\u6709\u56db\u3001\u4e94\u4e2a\u4eba\u662f \u8fd9\u6837\u5b50\u51bb\u6b7b\u7684\uff0c\u51bb\u5f97\u6bd4\u77f3\u5934\u8fd8\u786c\uff0c\u8fde\u91ce\u72d7\u90fd\u5543\u4e0d\u52a8\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u2014\u2014\u65e2\u7136\u6d3b\u5f97\u5982\u6b64\u8270\u82e6\uff0c\u6b7b\u4e86\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u53ef\u662f\u8fd9\u4e2a\u4eba\u504f\u504f\u4e0d\u8ba9\u4ed6\u6b7b\u3002 \u3000\u3000\u4ed6\u7684\u4e0b\u989a\u5ffd\u7136\u88ab\u626d\u5f00\uff0c\u5ffd\u7136\u611f\u89c9\u5230\u6709\u4e00\u80a1\u70ed\u8fa3\u8fa3\u7684\u4e1c\u897f\u51b2\u5165\u4e86\u4ed6\u7684\u54bd\u5589\uff0c\u6d41\u8fdb\u4e86\u4ed6\u7684 \u80c3\u3002 \u3000\u3000\u4ed6\u7684\u80c3\u91cc\u7acb\u523b\u5c31\u597d\u50cf\u6709\u4e00\u56e2\u706b\u7130\u5728\u71c3\u70e7\uff0c\u4f7f\u5f97\u4ed6\u5168\u8eab\u90fd\u6e29\u6696\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7741\u5f00\u773c\uff0c\u5c31\u770b\u89c1\u4e00\u4e2a\u4eba\u77f3\u50cf\u822c\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u624b\u91cc\u63d0\u7740\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u4e00\u4e2a\u4e0d\u5e73\u51e1\u7684\u4eba\uff0c\u4e00\u53e3\u4e0d\u5e73\u51e1\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5982\u679c\u60f3\u8981\u4e00\u4e2a\u4eba\u6d3b\u4e0b\u53bb\uff0c\u65e0\u8bba\u8c01\u90fd\u5f88\u96be\u6b7b\u5f97\u4e86\uff0c\u5c31\u6b63\u5982\u4ed6\u60f3\u8981\u4e00\u4e2a\u4eba\u6b7b\u7684\u65f6\u5019\uff0c \u65e0\u8bba\u8c01\u90fd\u5f88\u96be\u6d3b\u5f97\u4e0b\u53bb\u3002 \u3000\u3000\u5c0f\u9ad8\u660e\u767d\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u201c\u597d\u9152\u3002\u201d\u4ed6\u4e00\u8dc3\u800c\u8d77\uff0c\u5c3d\u529b\u4f5c\u51fa\u5f88\u4e0d\u5728\u4e4e\u7684\u6837\u5b50\uff1a\u201c\u4f60\u521a\u624d\u7ed9\u6211\u559d\u7684\u662f\u4e0d\u662f\u6caa\u5dde\u5927 \u66f2\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u3002\u201d \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u4f60\u662f\u7792\u4e0d\u8fc7\u6211\u7684\uff0c\u522b\u4eba\u5728\u5403\u5976\u7684\u65f6\u5019\u6211\u5c31\u5df2\u7ecf\u5f00\u59cb\u559d\u9152\u4e86\u3002\u201d\u5c0f\u9ad8\u5927\u7b11\uff0c\u597d\u50cf \u771f\u7684\u7b11\u5f97\u5f88\u6109\u5feb\uff1a\u201c\u6709\u4eba\u5929\u751f\u662f\u82f1\u96c4\uff0c\u6709\u4e9b\u4eba\u5929\u751f\u662f\u5251\u5ba2\uff0c\u53e6\u5916\u8fd8\u6709\u4e9b\u4eba\u5929\u751f\u5c31\u662f\u9152\u9b3c\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u9152\u9b3c\uff0c\u201d\u8fd9\u4e2a\u4eba\u51b7\u51b7\u7684\u770b\u7740\u5c0f\u9ad8\uff1a\u201c\u4f60\u662f\u4e2a\u6df7\u86cb\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53c8\u5927\u7b11\uff1a\u201c\u6df7\u86cb\u5c31\u6df7\u86cb\uff0c\u6df7\u86cb\u548c\u9152\u9b3c\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d \u3000\u3000\u201c\u6709\u4e00\u70b9\u5206\u522b\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u201c\u4f60\u770b\u8fc7\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d\u5c0f\u9ad8\u95ee\uff1a\u201c\u5230\u54ea\u91cc\u53bb\u770b\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u5ffd\u7136\u6258\u4f4f\u4ed6\u7684\u80c1\uff0c\u5e26\u7740\u4ed6\u98de\u63a0\u800c\u8d77\uff0c\u63a0\u8fc7\u65e0\u6570\u91cd\u5c4b\u810a\u540e\u624d\u505c\u4e0b\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u5c31\u662f\u5230\u8fd9\u91cc\u6765\u770b\uff01\u201d \u3000\u3000\u8fd9\u91cc\u662f\u4e00\u5ea7\u9ad8\u697c\u7684\u5c4b\u810a\uff0c\u9ad8\u697c\u5728\u4e00\u7247\u5e7f\u9614\u7684\u56ed\u6797\u4e2d\u3002 \u3000\u3000\u8fd9\u5ea7\u9ad8\u697c\u5c31\u662f\u957f\u5b89\u5c45\u7684\u7b2c\u4e00\u697c\u3002 \u4e8c \u3000\u3000\u5929\u5df2\u7ecf\u5feb\u4eae\u4e86\uff0c\u5728\u7070\u8499\u8499\u7684\u66d9\u8272\u4e2d\u770b\u8fc7\u53bb\uff0c\u82b1\u4f9d\u65e7\u7ea2\u5f97\u90a3\u4e48\u9ad8\u50b2\uff0c\u90a3\u4e48\u8273\u4e3d\uff0c\u5947\u602a\u7684 \u662f\uff0c\u96ea\u5730\u4e0a\u4eff\u4f5b\u4e5f\u98d8\u843d\u4e86\u4e00\u5730\u7684\u82b1\u3002 \u3000\u3000\u201c\u5982\u679c\u4f60\u8ba4\u4e3a\u90a3\u662f\u82b1\u4f60\u5c31\u9519\u4e86\u3002\u201d\u63d0\u7740\u7bb1\u5b50\u7684\u4eba\u8bf4\uff1a\u201c\u90a3\u4e0d\u662f\u82b1\uff0c\u90a3\u662f\u8840\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u90a3\u662f\u8840\uff0c\u4e5f\u77e5\u9053\u90a3\u662f\u4ec0\u4e48\u4eba\u7684\u8840\u3002 \u3000\u3000\u6731\u731b\u6765\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u5c06\u4ed6\u5c5e\u4e0b\u7684\u6b7b\u58eb\u57cb\u4f0f\u5728\u8fd9\u91cc\uff0c\u5df2\u7ecf\u51c6\u5907\u548c\u5353\u4e1c\u6765\u51b3\u4e00\u6b7b\u6218\u3002 \u3000\u3000\u201c\u53ef\u662f\u4f60\u4eec\u4e5f\u5e94\u8be5\u60f3\u5230\uff0c\u5353\u4e1c\u6765\u4e5f\u4e0d\u4f1a\u6ca1\u6709\u51c6\u5907\u3002\u201d\u63d0\u7740\u7bb1\u5b50\u7684\u4eba\u8bf4\uff1a\u201c\u8fd9\u91cc\u6ca1\u6709\u4ed6\u7684 \u4eba\uff0c\u53ea\u56e0\u4e3a\u4ed6\u7684\u4eba\u90fd\u5728\u5916\u9762\uff0c\u4ed6\u77e5\u9053\u4f60\u4eec\u8981\u628a\u4eba\u624b\u57cb\u4f0f\u5728\u8fd9\u91cc\uff0c\u6240\u4ee5\u5c31\u5728\u5916\u9762\u628a\u4f60\u4eec\u5305 \u56f4\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6b21\u5353\u4e1c\u6765\u5c5e\u4e0b\u4e00\u5171\u51fa\u52a8\u4e86\u4e09\u767e\u4e8c\u5341\u4eba\uff0c\u90fd\u662f\u4ed6\u8fd9\u4e24\u5929\u91cc\u6240\u80fd\u8c03\u96c6\u6765\u7684\u6700\u4f73\u4eba\u624b\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u7684\u4eba\u867d\u7136\u51e0\u4e4e\u6bd4\u4f60\u4eec\u591a\u51e0\u500d\uff0c\u5353\u4e1c\u6765\u5374\u8fd8\u662f\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u77e5\u9053\u96c4\u72ee\u5802\u8fd9\u6b21\u6765\u7684\u4eba\u90fd\u662f\u4e0d\u6015\u6b7b\u7684\u597d\u6c49\uff0c\u90fd\u662f\u6765\u62fc\u547d\u7684\u3002\u201d \u3000\u3000\u201c\u62fc\u547d\uff1f\u201d\u63d0\u7bb1\u5b50\u7684\u4eba\u51b7\u7b11\uff1a\u201c\u4f60\u4ee5\u4e3a\u62fc\u547d\u5c31\u4e00\u5b9a\u6709\u7528\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u5c0f\u9ad8\uff1a\u201c\u5982\u679c\u4f60\u8981\u8ddf\u6211\u62fc\u547d\u4f1a\u4e0d\u4f1a\u6709\u7528\uff1f\u6211\u4f1a\u4e0d\u4f1a\u5413\u5f97\u4e0d\u6562\u52a8\u624b\uff1f\u201d \u3000\u3000\u4ed6\u7684\u95ee\u9898\u5c16\u9510\u800c\u65e0\u60c5\uff0c\u4ee4\u4eba\u6839\u672c\u65e0\u6cd5\u56de\u7b54\uff0c\u4ed6\u4e5f\u4e0d\u51c6\u5907\u8981\u5c0f\u9ad8\u56de\u7b54\u3002 \u3000\u3000\u201c\u6709\u65f6\u62fc\u547d\u53ea\u4e0d\u8fc7\u662f\u9001\u6b7b\u800c\u5df2\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u5353\u4e1c\u6765\u6015\u7684\u7edd\u4e0d\u662f\u90a3\u4e9b\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u6015\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u4f60\uff01\u201d \u3000\u3000\u5c0f\u9ad8\u7b11\u4e86\uff0c\u82e6\u7b11\uff1a\u201c\u4f60\u96be\u9053\u5fd8\u4e86\u6211\u548c\u53f8\u9a6c\u5728\u5927\u96c1\u57f9\u4e0b\u7684\u90a3\u4e00\u6218\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u53f8\u9a6c\u4e0d\u5728\u957f\u5b89\u3002\u201d \u3000\u3000\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u6d1b\u9633\u3002\u201d\u63d0\u7bb1\u5b50\u7684\u4eba\u8bf4\uff1a\u201c\u4ed6\u4e0d\u662f\u5353\u4e1c\u6765\u90a3\u6837\u7684\u4eba\uff0c\u4ed6\u4e5f\u6709\u6731\u731b\u7684\u8c6a\u6c14\uff0c\u53ea\u4e0d\u8fc7\u4ed6 \u53d7\u5230\u7684\u7275\u5236\u5927\u591a\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u8981\u505a\u4e00\u4e2a\u4e0d\u8d25\u7684\u82f1\u96c4\u7edd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u53f8\u9a6c\u8d85\u7fa4\u7684\u65e5\u5b50\u5e76\u4e0d\u597d\u8fc7\u3002\u201d \u3000\u3000\u63d0\u7bb1\u5b50\u7684\u4eba\u5728\u4e3a\u53f8\u9a6c\u53f9\u606f\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u5fc3\u91cc\u4e5f\u6709\u540c\u6837\u7684\u611f\u89e6\u3002 \u3000\u3000\u201c\u53f8\u9a6c\u4e0d\u5728\u957f\u5b89\uff0c\u4ee5\u5353\u4e1c\u6765\u4e00\u4eba\u4e4b\u529b\uff0c\u600e\u4e48\u80fd\u5bf9\u4ed8\u4f60\u548c\u6731\u731b\uff1f\u5982\u679c\u4ed6\u7684\u624b\u4e0b\u5148\u52a8\u624b\uff0c\u4f60 \u4eec\u4f1a\u4e0d\u4f1a\u653e\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u770b\u7740\u96ea\u5730\u4e0a\u843d\u82b1\u822c\u7684\u8840\u8ff9\uff0c\u80cc\u810a\u4e0a\u5ffd\u7136\u5192\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u8776\u821e\uff0c\u5f53\u65f6\u4ed6\u548c\u6731\u731b\u7684\u786e\u6709\u5f88\u597d\u7684\u673a\u4f1a\u628a\u5353\u4e1c\u6765\u65a9\u6740\u5e72\u9152\u7b75\u524d\u3002 \u3000\u3000\u201c\u90a3\u662f\u4f60\u4eec\u552f\u4e00\u7684\u4e00\u6b21\u673a\u4f1a\uff0c\u5374\u88ab\u4f60\u4eec\u8f7b\u8f7b\u653e\u8fc7\u4e86\uff0c\u56e0\u4e3a\u4f60\u8d70\u4e86\u3002\u201d\u63d0\u7bb1\u5b50\u7684\u4eba\u8bf4\uff1a \u201c\u4f60\u5f53\u7136\u5e94\u8be5\u8d70\u7684\uff0c\u56e0\u4e3a\u4f60\u662f\u6761\u7537\u5b50\u5974\uff0c\u5f53\u7136\u4e0d\u4f1a\u4e3a\u4e86\u4e00\u4e2a\u5973\u4eba\u548c\u6731\u731b\u7ffb\u8138\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u51b7\u9510\u5982\u5c16\u523a\uff1a\u201c\u53ef\u662f\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u4f60\u8d70\u7684\u65f6\u5019\uff0c\u6b63\u597d\u662f\u6731\u731b\u6700\u9700\u8981\u4f60\u7684\u65f6 \u5019\uff0c\u4f60\u628a\u4e00\u4e2a\u65ad\u4e86\u817f\u7684\u5973\u4eba\u7559\u7ed9\u6731\u731b\uff0c\u5c31\u8ba4\u4e3a\u81ea\u5df1\u5df2\u7ecf\u662f\u4e2a\u5f88\u591f\u4e49\u6c14\u7684\u670b\u53cb\uff0c\u53ef\u662f\u6211\u5374\u8ba4\u4e3a \u4f60\u5bf9\u5353\u4e1c\u6765\u66f4\u591f\u670b\u53cb\uff0c\u56e0\u4e3a\u4f60\u628a\u6731\u731b\u548c\u96c4\u72ee\u5802\u7684\u516b\u5341\u516d\u4e2a\u5144\u5f1f\u90fd\u7559\u7ed9\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u8bf4\u4e0d\u51fa\uff0c\u5168\u8eab\u8863\u670d\u90fd\u5df2\u88ab\u51b7\u6c57\u6e7f\u900f\u3002 \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u4eec\u53ea\u6709\u8ddf\u5353\u4e1c\u6765\u7684\u4eba\u62fc\u547d\u4e86\uff0c\u53ea\u53ef\u60dc\u62fc\u547d\u5e76\u4e0d\u662f\u4e00\u5b9a\u6709\u7528\u7684\u3002\u201d\u6349\u7bb1\u5b50\u7684\u4eba \u8bf4\uff1a\u201c\u4f60\u8d70\u4e86\u4e4b\u540e\uff0c\u8fd9\u91cc\u5c31\u53d8\u6210\u4e86\u4e2a\u5c60\u573a\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u7684\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5c60\u573a\u662f\u4ec0\u4e48\u6837\u5b50\u7684\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u6162\u6162\u7684\u62ac\u8d77\u5934\uff0c\u53ee\u7740\u4ed6\uff0c\u58f0\u97f3\u5df2\u56e0\u60b2\u75db\u800c\u5636\u54d1\u3002 \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u56e0\u4e3a\u90a3\u65f6\u5019\u6211\u4e5f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u5c31\u5750\u5728\u8fd9\u91cc\uff0c\u770b\u7740\u90a3\u4e9b\u4eba\u50cf\u725b\u7f8a\u822c\u88ab\u5bb0\u6740\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f46\u5728\u770b\uff0c\u800c\u4e14\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u6bcf\u4e00\u5200\u780d\u4e0b\u53bb\u7684\u65f6\u5019\u6211\u90fd\u770b\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u770b\u5f97\u5f88\u6109\u5feb\uff1f\u201d \u3000\u3000\u201c\u5e76\u4e0d\u592a\u6109\u5feb\uff0c\u4e5f\u4e0d\u5927\u96be\u53d7\u3002\u201d\u63d0\u7bb1\u5b50\u7684\u4eba\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u56e0\u4e3a\u8fd9\u672c\u6765\u5c31\u662f\u4f60\u7684\u4e8b\uff0c\u8ddf\u6211 \u4e00\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u4e00\u76f4\u5728\u6291\u5236\u7740\u7684\u6124\u6012\uff0c\u7ec8\u4e8e\u50cf\u6d2a\u7089\u70b8\u5f00\u65f6\u7684\u706b\u7130\u822c\u8ff8\u51fa\u3002 \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u4f60\u662f\u4eba\uff0c\u600e\u4e48\u80fd\u5750\u5728\u8fd9\u91cc\u770b\u7740\u522b\u4eba\u50cf\u725b\u7f8a\u822c\u88ab\u4eba\u5bb0\u6740\uff1f\u201d\u5c0f\u9ad8\u5389\u58f0\u5411\u8fd9\u4e2a\u597d\u50cf\u6c38 \u8fdc\u90fd\u4e0d\u4f1a\u52a8\u4e00\u70b9\u60c5\u611f\u7684\u4eba\u8bf4\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6551\u6551\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u7b11\u4e86\uff0c\u5e26\u7740\u79cd\u53ef\u4ee5\u8ba9\u4eba\u8fde\u9aa8\u9ad3\u90fd\u51b7\u900f\u7684\u7b11\u610f\u53cd\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7559\u4e0b\u6765\u6551\u6551 \u4ed6\u4eec\uff0c\u4e3a\u4ec0\u4e48\u8981\u4e00\u4e2a\u4eba\u53bb\u8eba\u5728\u96ea\u5730\u4e0a\u7b49\u6b7b\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u5634\u95ed\u4f4f\u3002 \u3000\u3000\u201c\u5982\u679c\u4f60\u771f\u7684\u8981\u6b7b\uff0c\u4e5f\u7528\u4e0d\u7740\u81ea\u5df1\u53bb\u627e\u6b7b\uff0c\u56e0\u4e3a\u5353\u4e1c\u6765\u5df2\u7ecf\u66ff\u4f60\u5b89\u6392\u597d\u4e86\u3002\u201d\u8fd9\u4e2a\u4eba\u6de1 \u6de1\u7684\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u5df2\u7ecf\u66ff\u4f60\u627e\u5230\u4e86\u4e00\u4e2a\u968f\u65f6\u90fd\u53ef\u4ee5\u9001\u4f60\u53bb\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u8981\u9001\u6211\u53bb\u6b7b\u4e5f\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d\u5c0f\u9ad8\u51b7\u7b11\uff1a\u201c\u4ed6\u6211\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u80fd\u9001\u4f60\u53bb\u6b7b\u7684\u4eba\u786e\u5b9e\u4e0d\u591a\uff0c\u53ef\u662f\u4ed6\u627e\u7684\u8fd9\u4e2a\u4eba\u6740\u4eba\u4ece\u672a\u5931\u624b\u8fc7\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u4e5f\u77e5\u9053\uff0c\u6c5f\u6e56\u4e2d\u6709\u4e9b\u4eba\u662f\u4ee5\u6740\u4eba\u4e3a\u751f\u7684\uff0c\u4ef7\u94b1\u8981\u5f97\u8d8a\u9ad8\u7684\u3002\u5931\u624b\u7684\u53ef\u80fd\u8d8a \u5c11\u3002\u201d \u3000\u3000\u201c\u4ed6\u627e\u7684\u8fd9\u4e2a\u4eba\u662f\u4e0d\u662f\u4ef7\u94b1\u6700\u9ad8\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u63d0\u7bb1\u5b50\u7684\u4eba\u8bf4\uff1a\u201c\u4ed6\u59d3\u8427\uff0c\u5251\u6c14\u8427\u8427\u7684\u8427\uff0c\u4ed6\u7684\u540d\u5b57\u53eb\u8427\u6cea\u8840\u3002\u201d \u3000\u3000\u201c\u4f60\u5c31\u662f\u8427\u6cea\u8840\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5df2\u7ecf\u5b8c\u5168\u51b7\u9759\u4e86\u4e0b\u6765\uff0c\u53ea\u6709\u8fd9\u79cd\u5c16\u9488\u822c\u7684\u523a\u6fc0\u624d\u80fd\u4f7f\u4ed6\u81ea\u60b2\u75db\u6b49\u759a\u8ff7\u4e71\u4e2d\u9aa4\u7136\u51b7 \u9759\u3002 \u3000\u3000\u6668\u96fe\u521a\u5347\u8d77\uff0c\u4ed6\u9759\u9759\u7684\u770b\u7740\u8fd9\u4e2a\u6bd4\u96fe\u8fd8\u795e\u79d8\u7684\u4eba\uff0c\u8f7b\u8f7b\u7684\u53f9\u606f\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u201c\u8fd9\u5b9e\u5728\u662f\u4ef6\u5f88\u9057\u61be\u7684\u4e8b\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u4f60\u8fd8\u8981\u4e3a\u94b1\u800c\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u60f3\u4e0d\u5230\uff0c\u6211\u5df2\u7ecf\u5f88\u4e45\u6ca1\u6709\u4e3a\u94b1\u6740\u8fc7\u4eba\u4e86\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u8fd9\u79cd\u4e8b\u5e76\u4e0d\u6709\u8da3\u3002\u201d \u3000\u3000\u201c\u8fd9\u6b21\u4f60\u4e3a\u4ec0\u4e48\u8981\u7834\u4f8b\uff1f\u201d \u3000\u3000\u8427\u6cea\u8840\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u7070\u9eef\u7684\u51b7\u773c\u91cc\u5374\u9732\u51fa\u79cd\u96fe\u4e00\u822c\u7684\u8868\u60c5\u3002 \u3000\u3000\u201c\u6bcf\u4e2a\u4eba\u8eab\u4e0a\u90fd\u6709\u6761\u770b\u4e0d\u89c1\u7684\u7ef3\u5b50\uff0c\u4ed6\u4e00\u751f\u4e2d\u5927\u90e8\u4efd\u65f6\u5019\u4e5f\u90fd\u662f\u88ab\u8fd9\u6761\u7ef3\u5b50\u7d27\u7d27\u7ed1\u4f4f \u7684\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u6709\u4e9b\u4eba\u7684\u7ef3\u5b50\u662f\u5bb6\u5ead\u59bb\u5b50\u513f\u5973\uff0c\u6709\u4e9b\u4eba\u7684\u7ef3\u5b50\u662f\u94b1\u8d22\u4e8b\u4e1a\u8d23\u4efb\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u51dd\u89c6\u7740\u5c0f\u9ad8\uff1a\u201c\u4f60\u548c\u6731\u731b\u8fd9\u4e00\u7c7b\u7684\u4eba\u867d\u7136\u4e0d\u4f1a\u88ab\u8fd9\u4e00\u7c7b\u7684\u7ef3\u5b50\u7ed1\u4f4f\uff0c\u53ef\u662f\u4f60\u4eec\u4e5f\u6709 \u4f60\u4eec\u81ea\u5df1\u4e3a\u81ea\u5df1\u505a\u51fa\u6765\u7684\u7ef3\u5b50\u3002\u201d \u3000\u3000\u201c\u611f\u60c5\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u4f60\u4eec\u90fd\u592a\u91cd\u611f\u60c5\uff0c\u8fd9\u5c31\u662f\u4f60\u4eec\u7684\u7ef3\u5b50\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u5c0f\u9ad8\u95ee\uff1a\u201c\u4f60\u7684\u7ef3\u5b50\u662f\u4ec0\u4e48\uff1f\u4ec0\u4e48\u6837\u7684\u7ef3\u5b50\u624d\u80fd\u7ed1\u5f97\u4f4f\u4f60\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u5f20\u5951\u7ea6\u3002\u201d \u3000\u3000\u201c\u5951\u7ea6\uff1f\u201d\u5c0f\u9ad8\u4e0d\u61c2\uff1a\u201c\u4ec0\u4e48\u5951\u7ea6\uff1f\u201d \u3000\u3000\u201c\u6740\u4eba\u7684\u5951\u7ea6\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u7684\u58f0\u97f3\u4eff\u4f5b\u5df2\u5230\u4e86\u8fdc\u65b9\uff1a\u201c\u73b0\u5728\u6211\u867d\u7136\u662f\u4e2a\u5bcc\u53ef\u654c\u56fd\u7684\u9690\u58eb\uff0c\u4e8c\u5341\u5e74\u524d\u6211\u5374\u53ea\u4e0d \u8fc7\u662f\u4e2a\u4e00\u6587\u4e0d\u540d\u7684\u6d6a\u5b50\uff0c\u5c31\u50cf\u4f60\u73b0\u5728\u4e00\u6837\uff0c\u6ca1\u6709\u670b\u53cb\uff0c\u6ca1\u6709\u4eb2\u4eba\uff0c\u6ca1\u6709\u6839\uff0c\u9664\u4e86\u8fd9\u53e3\u7bb1\u5b50 \u5916\uff0e\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u8fd9\u53e3\u7bb1\u5b50\u662f\u4ef6\u6740\u4eba\u7684\u6b66\u5668\uff0c\u6240\u4ee5\u4f60\u5c31\u5f00\u59cb\u4ee5\u6740\u4eba\u4e3a\u751f\uff1f\u201d \u3000\u3000\u201c\u6211\u6740\u7684\u4eba\u90fd\u662f\u8be5\u6740\u7684\uff0c\u6211\u4e0d\u6740\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u6b7b\u5728\u522b\u4eba\u624b\u91cc\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u6211\u8981\u7684 \u4ef7\u683c\u867d\u9ad8\uff0c\u4fe1\u7528\u5374\u5f88\u597d\uff0c\u53ea\u8981\u8ba2\u4e0b\u4e86\u5951\u7ea6\uff0c\u5c31\u4e00\u5b9a\u4f1a\u5b8c\u6210\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4e2d\u5145\u6ee1\u8bbd\u523a\uff0c\u5bf9\u81ea\u5df1\u7684\u8bbd\u523a\uff1a\u201c\u5c31\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u6240\u4ee5\u6211\u665a\u4e0a\u4ece\u6765\u4e0d\u4f1a\u7761\u4e0d\u7740 \u89c9\u3002\u201d \u3000\u3000\u201c\u53ea\u4e0d\u8fc7\u540e\u6765\u4f60\u8fd8\u662f\u6d17\u624b\u4e86\u3002\u201d\u5c0f\u9ad8\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u56e0\u4e3a\u4f60\u8d5a\u7684\u94b1\u5df2\u591f\u591a\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u540e\u6765\u6211\u6d17\u624b\u4e86\uff0c\u5374\u4e0d\u662f\u56e0\u4e3a\u6211\u8d5a\u7684\u94b1\u5df2\u7ecf\u591f\u591a\uff0c\u800c\u4e14\u56e0\u4e3a\u6709\u4e00\u5929\u665a\u4e0a\u6211\u6740\u4e86\u4e00 \u4e2a\u4eba\u4e4b\u540e\uff0c\u5ffd\u7136\u53d8\u5f97\u7761\u4e0d\u7740\u4e86\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u63e1\u7d27\u4ed6\u7684\u7bb1\u5b50\uff1a\u201c\u5bf9\u4e8e\u5e72\u6211\u4eec\u8fd9\u4e00\u884c\u7684\u4eba\u6765\u8bf4\uff0c\u8fd9\u624d\u662f\u6700\u53ef\u6015\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u90a3\u6761\u7ef3\u4e8e\u662f\u600e\u4e48\u7559\u4e0b\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u90a3\u5f20\u5951\u7ea6\u662f\u6211\u6700\u65e9\u8ba2\u4e0b\u6765\u7684\uff0c\u5951\u7ea6\u4e0a\u6ce8\u660e\uff0c\u4ed6\u968f\u65f6\u968f\u5730\u90fd\u53ef\u4ee5\u8981\u6211\u53bb\u4e3a\u4ed6\u6740\u4e00\u4e2a\u4eba\uff0c \u65e0\u8bba\u5728\u4ec0\u4e48\u65f6\u5019\u8981\u6211\u53bb\u6740\u4ec0\u4e48\u4eba\uff0c\u6211\u90fd\u4e0d\u80fd\u62d2\u7edd\u3002\u201d \u3000\u3000\u201c\u8fd9\u5f20\u5951\u7ea6\u4e00\u76f4\u90e8\u6ca1\u6709\u5b8c\u6210\uff1f\u201d \u3000\u3000\u201c\u4e00\u76f4\u90fd\u6ca1\u6709\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u5e76\u4e0d\u662f\u56e0\u4e3a\u6211\u4e0d\u60f3\u53bb\u5b8c\u6210\u5b83\uff0c\u800c\u662f\u56e0\u4e3a\u90a3\u4e2a\u4eba\u4e00\u76f4\u90fd\u6ca1 \u6709\u8981\u6211\u53bb\u505a\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u8fd9\u5f20\u5951\u7ea6\u4e00\u76f4\u5230\u73b0\u5728\u8fd8\u6709\u6548\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8ba2\u8fd9\u4e48\u6837\u4e00\u5f20\u8981\u547d\u7684\u5951\u7ea6\uff1f\u201d\u5c0f\u9ad8\u53f9\u606f\uff1a\u201c\u4ed6\u51fa\u7684\u4ef7\u94b1\u662f\u4e0d\u662f\u7279\u522b\u9ad8\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u7ed9\u4e86\u4f60\u591a\u5c11\uff1f\u201d\u5c0f\u9ad8\u95ee\u3002 \u3000\u3000\u201c\u4ed6\u7ed9\u4e86\u6211\u4e00\u6761\u547d\u3002\u201d \u3000\u3000\u201c\u8c01\u7684\u547d\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u5728\u6211\u8ba2\u90a3\u5f20\u5951\u7ea6\u7684\u65f6\u5019\uff0c\u4ed6\u968f\u65f6\u968f\u5730\u90fd\u53ef\u4ee5\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u8981\u6740\u4f60\u4e5f\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d\u5c0f\u9ad8\u53c8\u95ee\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8427\u6cea\u8840\u62d2\u7edd\u56de\u7b54\u8fd9\u95ee\u9898\u3002 \u3000\u3000\u201c\u6211\u53ea\u80fd\u544a\u8bc9\u4f60\uff0c\u73b0\u5728\u8fd9\u5f20\u5951\u7ea6\u5df2\u7ecf\u9001\u56de\u6765\u7ed9\u6211\u4e86\uff0c\u4e0a\u9762\u5df2\u7ecf\u6709\u4e86\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u201c\u4e00\u4e2a\u8981\u4f60\u53bb\u6740\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\u5c31\u662f\u9ad8\u6e10\u98de\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u9759\u9759\u7684\u770b\u7740\u9ad8\u6e10\u98de\uff0c\u9ad8\u6e10\u98de\u4e5f\u5728\u9759\u9759\u7684\u770b\u7740\u4ed6\uff0c\u4e24\u4e2a\u4eba\u90fd\u5e73\u9759\u5f97\u51fa\u5947\uff0c\u5c31\u597d\u50cf\u6740 \u4eba\u548c\u88ab\u6740\u90fd\u53ea\u4e0d\u8fc7\u662f\u4ef6\u5f88\u5e73\u5e38\u7684\u4e8b\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\u4e4b\u540e\u5c0f\u9ad8\u624d\u95ee\u8427\u6cea\u8840\uff1a \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6731\u731b\u7684\u5c38\u4f53\u5728\u54ea\u91cc\uff1f\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u60f3\u53bb\u796d\u4e00\u796d\u4ed6\u3002\u201d \u3000\u3000\u201c\u6731\u731b\u8fd8\u6ca1\u6709\u5c38\u4f53\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u4ed6\u6682\u65f6\u8fd8\u4e0d\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u547c\u5438\u4eff\u4f5b\u505c\u987f\u4e86\u4e00\u4e0b\u4e88\uff1a\u201c\u8fd9\u4e00\u6b21\u4ed6\u53c8\u6740\u51fa\u4e86\u91cd\u56f4\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4ed6\u81ea\u5df1\u6740\u51fa\u53bb\u7684\uff0c\u662f\u5353\u4e1c\u6765\u653e\u4ed6\u8d70\u7684\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u4ed6\u672c\u6765\u5df2\u7ecf\u7edd\u65e0\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u5353\u4e1c\u6765\u4e3a\u4ec0\u4e48\u8981\u653e\u4ed6\u8d70\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5353\u4e1c\u6765\u8981\u628a\u4ed6\u7559\u7ed9\u53f8\u9a6c\u8d85\u7fa4\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff0c\u201c\u6731\u731b\u7684\u6b7b\uff0c\u5fc5\u5c06\u662f\u4ef6\u8f70\u52a8\u6c5f\u6e56\u7684\u5927 \u4e8b\uff0c\u8fd9\u4e00\u7c7b\u7684\u4e8b\u5353\u4e1c\u6765\u901a\u5e38\u90fd\u4f1a\u7559\u7ed9\u53f8\u9a6c\u8d85\u7fa4\u505a\u7684\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u8981\u9020\u5c31\u4e00\u4f4d\u82f1\u96c4\u4e5f\u5f88\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u786e\u5b9e\u5f88\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u65ec\u8bdd\uff0c\u4e24\u4e2a\u4eba\u53c8\u95ed\u4e0a\u4e86\u5634\uff0c\u8fdc\u65b9\u5374\u5ffd\u7136\u6709\u4e00\u80a1\u6de1\u6de1\u7684\u7ea2\u8272\u8f7b\u70df\u5347\u8d77\uff0c\u5728\u8fd9\u4e00\u7247 \u7070\u8499\u8499\u7684\u66d9\u8272\u4e2d\u770b\u6765\uff0c\u5c31\u50cf\u662f\u521a\u6e17\u4eba\u51b0\u96ea\u4e2d\u7684\u4e00\u7f15\u9c9c\u8840\u3002 \u3000\u3000\u8f7b\u70df\u5f88\u5feb\u5c31\u88ab\u5439\u6563\u4e86\uff0c\u8427\u6cea\u8840\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u58f0\u97f3\u5bf9\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u8981\u5230\u4e00\u4e2a\u5f88\u7279\u522b\u7684\u5730 \u65b9\u53bb\uff0c\u4f60\u4e5f\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u90a3\u822c\u7ea2\u8272\u7684\u8f7b\u70df\u662f\u4ece\u54ea\u91cc\u5347\u8d77\u7684\uff1f\u662f\u4e0d\u662f\u8c61\u5f81\u7740\u67d0\u79cd\u7279\u522b\u7684\u610f\u601d\uff1f \u3000\u3000\u2014\u2014\u662f\u4e00\u79cd\u8baf\u53f7\uff1f\u8fd8\u662f\u4e00\u79cd\u8b66\u544a\uff1f \u3000\u3000\u90a3\u4e2a\u7279\u522b\u7684\u5730\u65b9\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u8427\u6cea\u8840\u4e3a\u4ec0\u4e48\u8981\u5e26\u5c0f\u9ad8\u5230\u90a3\u91cc\u53bb\uff1f \u3000\u3000\u6709\u5f88\u591a\u4eba\u7cfb\u4eba\u65f6\u90fd\u559c\u6b22\u9009\u4e00\u4e2a\u7279\u522b\u7684\u5730\u65b9\uff0c\u96be\u9053\u90a3\u91cc\u4e5f\u662f\u4e2a\u5c60\u573a\uff1f \u3000\u3000\u8fd9\u91cc\u4e0d\u662f\u5c60\u573a\uff0c\u770b\u6765\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u3002\u8fd9\u91cc\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5c0f\u5c0f\u7684\u571f\u5730\u5e99\u800c\u5df2\uff0c\u5efa\u7b51\u5728\u4e00\u6761 \u504f\u50fb\u51b7\u5df7\u4e2d\u7684\u4e00\u4e2a\u5c0f\u5c0f\u571f\u5730\u5e99\u3002 \u3000\u3000\u5e99\u91cc\u7684\u571f\u5730\u516c\u5a46\u4e5f\u5df2\u88ab\u51b7\u843d\u4e86\u5f88\u4e45\u4e86\uff0c\u5728\u8fd9\u9177\u5bd2\u7684\u4e8c\u6708\u51cc\u6668\uff0c\u5f53\u7136\u66f4\u4e0d\u4f1a\u6709\u9999\u706b\u3002 \u3000\u3000\u5c0f\u9ad8\u9ed8\u9ed8\u7684\u7ad9\u5728\u8427\u6cea\u8840\u8eab\u540e\uff0c\u9ed8\u9ed8\u7684\u770b\u7740\u8fd9\u4e00\u5bf9\u770b\u5c3d\u4e86\u4e16\u6001\u708e\u51c9\u3001\u5386\u5c3d\u4e86\u6ca7\u6d77\u6851\u7530\u5374\u59cb \u7ec8\u4e92\u76f8\u53ae\u5b88\u5728\u4e00\u8d77\u7684\u516c\u5a46\uff0c\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u5bc2\u5bde\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u8fd9\u4e00\u5bf9\u81ea\u53e4\u4ee5\u6765\u5c31\u4e0d\u88ab\u91cd\u89c6\u7684\u5351\u5fae\u5c0f\u795e\uff0c\u8fdc\u6bd4\u90a3\u4e9b\u9ad8\u636e\u5728\u4e5d\u5929\u4e4b\u4e0a\u3001\u5e26\u7740\u4e07 \u4e08\u91d1\u5149\u7684\u4ed9\u4f5b\u795e\u7957\u90fd\u8981\u5e78\u798f\u5f97\u591a\u3002 \u3000\u3000\u4e00\u4e00\u8776\u821e\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u662f\u8776\u821e\u4e86\u4e3a\u4ec0\u4e48\u4e0d\u662f\u53e6\u5916\u4e00\u4e2a\u5973\u4eba\uff1f \u3000\u3000\u4ed6\u4e00\u76f4\u90fd\u6ca1\u6709\u95ee\u8d77\u8fc7\u5979\u7684\u751f\u6b7b\u4e0b\u843d\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u95ee\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u672c\u6765\u5c31\u4e0d\u5c5e\u4e8e\u4ed6\uff0c\u4ed6\u53ea\u5e0c\u671b\u81ea\u5df1\u80fd\u628a\u4ed6\u4eec\u53ae\u5b88\u5728\u4e00\u8d77\u7684\u90a3\u51e0\u5929\u5f53\u4f5c\u4e00\u4e2a\u68a6\u5883\u3002 \u4e09 \u3000\u3000\u8fd9\u5730\u65b9\u6709\u4ec0\u4e48\u7279\u522b\uff0c\u8427\u6cea\u8840\u4e3a\u4ec0\u4e48\u8981\u5e26\u4ed6\u5230\u8fd9\u91cc\u6765\uff1f\u6765\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u5c0f\u9ad8\u6ca1\u95ee\uff0c\u8427\u6cea\u8840\u5374\u8bf4\uff1a\u201c\u4ed6\u4eec\u5168\u90fd\u77e5\u9053\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u90a3\u6bb5\u65e5\u5b50\u91cc\u6211\u505a\u7684\u6bcf\u4ef6\u4e8b\u4ed6\u4eec\u5168 \u90fd\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\uff1f\u201d\u5c0f\u9ad8\u95ee\uff1a\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5c31\u662f\u4ed6\u4eec\uff0c\u201d\u8427\u6cea\u8840\u770b\u7740\u9f9b\u4e2d\u7684\u795e\u50cf\uff1a\u201c\u5c31\u662f\u8fd9\u4e00\u5bf9\u571f\u5730\u516c\u516c\u548c\u571f\u5730\u5a46\u5a46\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u4e0d\u61c2\uff0c\u8427\u6cea\u8840\u4e5f\u77e5\u9053\u4ed6\u4e0d\u61c2\u3002 \u3000\u3000\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u591f\u8d44\u683c\u8981\u6211\u53bb\u6740\u4eba\u7684\u4eba\uff0c\u90fd\u77e5\u9053\u8fd9\u4e2a\u5730\u65b9\uff0c\u4e5f\u90fd\u4f1a\u5230\u8fd9\u91cc\u6765\uff0c\u7559\u4e0b\u4e00\u4e2a\u5730 \u540d\uff0c\u4e00\u4e2a\u4eba\u540d\u3002\u201d\u8427\u6cea\u8840\u89e3\u91ca\uff1a\u201c\u5730\u540d\u662f\u8981\u6211\u53bb\u62ff\u94b1\u7684\u5730\u65b9\uff0c\u4eba\u540d\u662f\u6211\u8981\u53bb\u6740\u7684\u4eba\u3002\u201d \u3000\u3000\u2014\u2014\u4e00\u4e2a\u51b7\u50fb\u7684\u571f\u5730\u5e99\uff0c\u4e00\u4e2a\u9690\u5bc6\u7684\u89d2\u843d\uff0c\u4e00\u5757\u53ef\u4ee5\u6d3b\u52a8\u7684\u7ea2\u7816\uff0c\u4e00\u5377\u88ab\u5c0f\u5fc3\u5377\u8d77\u7684\u7eb8 \u6761\uff0c\u4e00\u7b14\u975e\u5e38\u53ef\u89c2\u7684\u4ee3\u4ef7\uff0c\u4e00\u6761\u547d\uff01 \u3000\u3000\u591a\u4e48\u7b80\u5355\uff0c\u53c8\u591a\u4e48\u590d\u6742\u3002 \u3000\u3000\u201c\u5982\u679c\u6211\u8ba4\u4e3a\u90a3\u4e2a\u4eba\u662f\u5e94\u8be5\u6740\u7684\u4eba\uff0c\u6211\u5c31\u4f1a\u5230\u4ed6\u4eec\u7559\u4e0b\u540d\u5b57\u7684\u90a3\u4e2a\u5730\u65b9\u53bb\uff0c\u90a3\u91cc\u5c31\u4f1a\u6709 \u4e00\u7b14\u94b1\u7b49\u7740\u6211\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201d\u53ea\u6709\u94b1\uff0c\u6ca1\u6709\u4eba\uff0c\u6211\u7684\u4e3b\u987e\u4eec\u4ece\u6765\u90fd\u6ca1\u6709\u89c1\u8fc7\u6211\u7684\u771f\u9762 \u76ee\u3002\u201d \u3000\u3000\u201c\u6b7b\u5728\u4f60\u624b\u91cc\u7684\u90a3\u4e9b\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u80fd\u591f\u8ba9\u4eba\u4e0d\u60dc\u82b1\u8d39\u8fd9\u4e48\u9ad8\u7684\u4ee3\u4ef7\u53bb\u6740\u4ed6\u7684\u4eba\uff0c\u901a\u5e38\u90fd\u80b2\u4ed6\u8be5\u6b7b\u7684\u7406\u7531\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a \u201c\u6240\u4ee5\u8fd9\u4e2a\u5c0f\u5c0f\u7684\u571f\u5730\u5e99\u5f88\u53ef\u80fd\u5c31\u662f\u957f\u5b89\u57ce\u91cc\u4ea4\u6613\u505a\u5f97\u6700\u5927\u7684\u4e00\u4e2a\u5730\u65b9\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u91cc\u53c8\u5145\u6ee1\u8ba5\u8bee\uff1a\u201c\u6211\u4eec\u8fd9\u4e00\u884c\u672c\u6765\u5c31\u662f\u4eba\u7c7b\u6700\u53e4\u8001\u7684\u884c\u4e1a\u4e4b\u4e00\uff0c\u751a\u81f3\u53ef\u4ee5\u7b97\u662f \u7537\u4eba\u6240\u80fd\u505a\u7684\u884c\u4e1a\u4e2d\u6700\u53e4\u8001\u7684\u4e00\u79cd\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u5973\u4eba\u6240\u80fd\u505a\u7684\u884c\u4e1a\u4e2d\u6709\u4e00\u884c\u8fdc\u6bd4\u8fd9\u4e00\u884c\u66f4\u53e4\u8001\uff0c\u56e0\u4e3a\u5979\u4eec\u6709\u6700\u539f\u59cb\u7684\u8d44\u672c\u3002 \u3000\u3000\u201c\u5341\u516d\u5e74\uff0c\u5341\u516d\u5e74\u96f6\u4e09\u4e2a\u6708\uff0c\u591a\u4e48\u957f\u7684\u4e00\u6bb5\u65e5\u5b50\u3002\u201d\u8427\u6cea\u8840\u8f7b\u8f7b\u53f9\u606f\uff1a\u201c\u5728\u8fd9\u6bb5\u65e5\u5b50 \u91cc\uff0c\u6709\u4eba\u751f\u3001\u6709\u4eba\u8001\u3001\u6709\u4eba\u6b7b\uff0c\u53ef\u662f\u8fd9\u5730\u65b9\u5374\u597d\u50cf\u8fde\u4e00\u70b9\u53d8\u5316\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u8fd9\u5341\u516d\u5e74\u6765\u4f60\u90fd\u6ca1\u6709\u5230\u8fc7\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u76f4\u5230\u524d\u5929\u6211\u624d\u6765\u3002\u201d \u3000\u3000\u201c\u8fc7\u4e86\u5341\u516d\u5e74\u4e4b\u540e\uff0c\u4f60\u600e\u4e48\u4f1a\u5ffd\u7136\u53c8\u6765\u4e86\uff1f\u201d\u5c0f\u9ad8\u95ee\u8427\u6cea\u8840\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u53c8\u770b\u5230\u4e86\u5341\u516d\u5e74\u524d\u88ab\u6c5f\u6e56\u4e2d\u4eba\u79f0\u4e3a\u2018\u8840\u706b\u2019\u7684\u70df\u8baf\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u6211\u4eec\u521a\u624d\u770b\u5230\u7684\u90a3\u80a1\u7ea2\u70df\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u8427\u6d1e\u8840\u63a5\u7740\u8bf4\uff1a\u201c\u8840\u706b\u4e00\u73b0\uff0c\u6c5f\u6e56\u4e2d\u5c31\u5fc5\u5b9a\u6709\u4e00\u4f4d\u6781\u91cd\u8981\u7684\u4eba\u7a81\u7136\u66b4\u6bd9\uff0c\u6240\u4ee5\uff0c\u53c8\u6709\u4eba \u79f0\u5b83\u4e3a\u2018\u6b7b\u4ee4\u2019\uff0c\u52fe\u9b42\u7684\u6b7b\u4ee4\uff0c\u201d\u4ed6\u53c8\u89e3\u91ca\uff1a\u201c\u627e\u6211\u7684\u4eba\u5230\u8fd9\u91cc\u6765\u8fc7\u4e4b\u540e\uff0c\u5c31\u8981\u5230\u57ce\u5916\u5927\u53d1 \u653e\u8fd9\u79cd\u7ea2\u8272\u7684\u70df\u706b\uff0c\u6bcf\u5929\u51cc\u6668\u4e00\u6b21\uff0c\u8fde\u53d1\u4e09\u6b21\u3002\u4f60\u521a\u624d\u770b\u89c1\u7684\u5df2\u7ecf\u662f\u7b2c\u4e09\u6b21\u4e86\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u524d\u5929\u5df2\u7ecf\u6765\u8fc7\uff0c\u5df2\u7ecf\u63a5\u5230\u4e86\u90a3\u5f20\u4e0d\u80fd\u4e0d\u5b8c\u6210\u7684\u5951\u7ea6\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u7528\u4f60\u7684\u4e00\u6761\u547d\u6765\u6362\u8fd9\u5f20\u5951\u7ea6\u7684\u4eba\u5c31\u662f\u5353\u4e1c\u6765\uff1f\u201d\u5c0f\u9ad8\u95ee\u3002 \u3000\u3000\u201c\u4e0d\u662f\u4ed6\u3002\u201d\u8427\u6cea\u8840\u51b7\u7b11\uff1a\u201c\u4ed6\u8fd8\u4e0d\u914d\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u4f60\u5374\u77e5\u9053\u8fd9\u662f\u5353\u4e1c\u6765\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d\u8427\u6cea\u8840\u8bf4\u7684\u6d3b\u5f88\u5947\u602a\uff1a\u201c\u81ea\u4ece\u90a3\u4e2a\u4eba\u5ffd\u7136\u81ea\u4eba\u95f4\u6d88\u5931\u4e4b\u540e\uff0c \u6211\u4e00\u76f4\u60f3\u4e0d\u901a\u4ed6\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u76f4\u5230\u73b0\u5728\u6211\u624d\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u201c\u90a3\u4e2a\u4eba\u201d\uff0c\u65e0\u7591\u5c31\u662f\u548c\u4ed6\u8ba2\u7acb\u8fd9\u5f20\u5951\u7ea6\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u662f\u4e0d\u662f\u548c\u5353\u4e1c\u6765\u6709\u67d0\u79cd\u795e\u79d8\u7684\u5173\u7cfb\uff1f \u3000\u3000\u8fd9\u4e9b\u4e8b\u5c0f\u9ad8\u90fd\u4e0d\u60f3\u95ee\u4e86\u3002\u4ed6\u672c\u6765\u5df2\u7ecf\u5f88\u75b2\u5026\uff0c\u75b2\u5026\u5f97\u6574\u4e2a\u4eba\u90fd\u4f3c\u4e4e\u5df2\u5c06\u865a\u8131\uff0c\u53ef\u662f\u73b0\u5728 \u7cbe\u795e\u5374\u5ffd\u7136\u632f\u594b\u8d77\u6765\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u73b0\u5728\u6211\u8fd8\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u80fd\u6b7b\u5728\u4f60\u7684\u624b\u91cc\uff0c\u6211\u4e5f\u6b7b\u800c\u65e0\u61be\uff0c\u56e0\u4e3a\u90a3\u81f3\u5c11\u603b\u6bd4\u6b7b \u5728\u522b\u4eba\u624b\u91cc\u597d\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u53ef\u662f\u4f60\u8981\u6740\u6211\u4e5f\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u4ed6\u76ef\u7740\u8427\u6cea\u8840\u624b\u91cc\u7684\u7bb1\u5b50\uff1a\u201c\u4f60\u8981\u6740\u6211\uff0c\u81f3\u5c11\u4e5f\u5f97\u5148\u6253\u5f00\u4f60\u8fd9\u53e3\u7bb1\u5b50\uff0c\u5728\u6211\u62d4\u51fa\u6211\u7684\u8fd9 \u67c4\u5251\u4e4b\u524d\uff0c\u5c31\u6253\u5f00\u8fd9\u53e3\u7bb1\u5b50\u3002\u201d \u3000\u3000\u4ed6\u7684\u5251\u4e5f\u5728\u4ed6\u7684\u624b\u91cc\uff0c\u5df2\u7ecf\u4e0d\u518d\u7528\u9752\u5e03\u5305\u7740\uff0c\u4e00\u5165\u957f\u5b89\uff0c\u4ed6\u5c31\u5df2\u968f\u65f6\u51c6\u5907\u62d4\u5251\u3002 \u3000\u3000\u8427\u6cea\u8840\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u76ef\u7740\u5c0f\u9ad8\u8fd9\u53ea\u63e1\u5251\u7684\u624b\uff0c\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u975e\u5e38\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u63d0\u7740\u7bb1\u5b50\u7684\u90a3\u53ea\u624b\u6307\u8282\u5ffd\u7136\u53d1\u767d\uff0c\u624b\u80cc\u4e0a\u5ffd\u7136\u6709\u9752\u7b4b\u66b4\u8d77\u3002 \u3000\u3000\u2014\u2014\u5b9d\u5251\u521d\u51fa\uff0c\u795e\u9b3c\u7686\u5fcc\u3002 \u3000\u3000\u2014\u2014\u5251\u4e0a\u7684\u6cea\u75d5\u662f\u8c01\u7684\u6cea\u75d5\uff1f \u3000\u3000\u2014\u2014\u8427\u5927\u5e08\u7684\u3002 \u3000\u3000\u2014\u2014\u5b9d\u5251\u5df2\u94f8\u6210\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u6d41\u6cea\uff1f \u3000\u3000\u2014\u2014\u56e0\u4e3a\u4ed6\u5df2\u9884\u89c1\u5230\u4e00\u4ef6\u707e\u7978\uff0c\u4ed6\u5df2\u7ecf\u5728\u5251\u6c14\u4e2d\u9884\u89c1\u5230\u4ed6\u7684\u72ec\u751f\u5b50\u8981\u6b7b\u5728\u8fd9\u67c4\u5251\u4e0b\u3002 \u3000\u3000\u2014\u2014\u4ed6\u7684\u72ec\u751f\u4e8e\u5c31\u662f\u8427\u6cea\u8840\uff1f \u3000\u3000\u2014\u2014\u662f\u7684\u3002 \u56db \u3000\u3000\u6d74\u5ba4\u4e2d\u70ed\u6c14\u817e\u817e\uff0c\u5353\u4e1c\u6765\u6b63\u5728\u6d17\u6fa1\uff0c\u4eff\u4f5b\u60f3\u53ca\u65f6\u6d17\u53bb\u6628\u591c\u65b0\u67d3\u4e0a\u7684\u90a3\u4e00\u8eab\u8840\u6c61\u3002 \u3000\u3000\u8fd9\u95f4\u6d74\u5ba4\u5728\u4ed6\u7684\u5bdd\u5ba4\u540e\uff0c\u5c31\u50cf\u662f\u85cf\u5b9d\u7684\u5bc6\u5ba4\u4e00\u6837\uff0c\u5efa\u7b51\u5f97\u575a\u56fa\u800c\u4e25\u5bc6\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6d17\u6fa1\u7684\u65f6\u5019\u7edd\u4e0d\u5bb9\u4efb\u4f55\u4eba\u95ef\u8fdb\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u65e0\u8bba\u4efb\u4f55\u4eba\u6d17\u6fa1\u65f6\u90fd\u662f\u8d64\u88f8\u7684\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u4f8b\u5916\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u5a74\u513f\u65f6\u5728\u4ed6\u6bcd\u4eb2\u9762\u524d\u4e4b\u5916\uff0c\u5353\u4e1c\u6765\u8fd9\u4e00\u751f\u4e2d\u4ece\u672a\u8ba9\u5176\u4ed6\u4efb\u4f55\u4eba\u770b\u5230\u4ed6\u5b8c\u5168\u8d64\u88f8 \u8fc7\u3002 \u3000\u3000\u5353\u4e1c\u6765\u662f\u4e2a\u6b8b\u5e9f\uff0c\u53d1\u80b2\u4e0d\u5168\u7684\u7578\u5f62\u6b8b\u5e9f\u8005\u3002 \u3000\u3000\u4ed6\u7684\u5de6\u817f\u6bd4\u53f3\u817f\u77ed\u4e00\u70b9\uff0c\u4ed6\u53d1\u80b2\u4e0d\u5168\uff0c\u53ea\u56e0\u4e3a\u4ed6\u5728\u5a18\u80ce\u4e2d\u5df2\u7ecf\u53d7\u5230\u53e6\u5916\u4e00\u4e2a\u4eba\u7684\u538b\u6324\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u4ed6\u7684\u5f1f\u5f1f\u3002 \u3000\u3000\u5353\u4e1c\u6765\u662f\u5b6a\u751f\u5b50\uff0c\u672c\u6765\u5e94\u8be5\u6709\u4e2a\u5f1f\u5f1f\uff0c\u5728\u6bcd\u4f53\u4e2d\u548c\u4ed6\u5206\u4eab\u53d7\u548c\u8425\u517b\u7684\u5f1f\u5f1f\u3002 \u3000\u3000\u4ed6\u5148\u751f\u51fa\u6765\u4e86\uff0c\u4ed6\u7684\u5f1f\u5f1f\u5374\u6b7b\u5728\u5979\u6bcd\u4eb2\u7684\u5b50\u5bab\u91cc\uff0c\u548c\u4ed6\u7684\u6bcd\u4eb2\u540c\u65f6\u6b7b\u7684\u3002 \u3000\u3000\u201c\u6211\u662f\u4e2a\u51f6\u624b\uff0c\u5929\u751f\u5c31\u662f\u51f6\u624b\uff0c\u201d\u5353\u4e1c\u6765\u5728\u6076\u68a6\u4e2d\u5e38\u5e38\u4f1a\u547c\u558a\uff1a\u201c\u6211\u4e00\u51fa\u751f\u5c31\u6740\u6b7b\u4e86\u6211 \u7684\u6bcd\u4eb2\u548c\u5f1f\u5f1f\u3002\u201d \u3000\u3000\u4ed6\u4e00\u76f4\u8ba4\u4e3a\u4ed6\u7684\u6b8b\u5e9f\u662f\u4e0a\u5929\u5bf9\u4ed6\u7684\u60e9\u7f5a\uff0c\u53ef\u662f\u4ed6\u53c8\u4e0d\u670d\u6c14\u3002 \u3000\u3000\u4ed6\u4ee5\u65e0\u6bd4\u7684\u51b3\u5fc3\u548c\u6bc5\u529b\u514b\u670d\u4e86\u4ed6\u624b\u8db3\u7684\u5148\u5929\u969c\u788d\uff0c\u81ea\u4ece\u4ed6\u6210\u5e74\u540e\u3002\u5c31\u6ca1\u6709\u4eba\u80fd\u770b\u5f97\u51fa\u4ed6 \u662f\u4e2a\u8ddb\u5b50\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4ee5\u524d\u5e38\u5e38\u4f1a\u56e0\u4e3a\u7ec3\u4e60\u50cf\u5e73\u5e38\u4eba\u4e00\u6837\u8d70\u8def\u800c\u75db\u5f97\u6d41\u6c57\u3002 \u3000\u3000\u53ef\u60dc\u53e6\u5916\u8fd8\u6709\u4e00\u4ef6\u4e8b\u5374\u662f\u4ed6\u6c38\u8fdc\u505a\u4e0d\u5230\u7684\uff0c\u65e0\u8bba\u4ed8\u51fa\u591a\u5927\u7684\u4ee3\u4ef7\u90fd\u505a\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u6c38\u8fdc\u90fd\u65e0\u6cd5\u6210\u4e3a\u4e00\u4e2a\u771f\u6b63\u7684\u7537\u4eba\uff0c\u4ed6\u8eab\u4f53\u4e0a\u7684\u67d0\u4e00\u90e8\u4efd\u6c38\u8fdc\u90fd\u50cf\u662f\u4e2a\u5a74\u513f\u3002 \u3000\u3000\u5353\u4e1c\u6765\u624b\u80cc\u4e0a\u4e5f\u6709\u9752\u7b4b\u51f8\u8d77\uff0c\u662f\u88ab\u70ed\u6c34\u6ce1\u51fa\u6765\u7684\uff0c\u4ed6\u559c\u6b22\u6ce1\u5728\u6eda\u70eb\u7684\u70ed\u6c34\u91cc\u3002 \u3000\u3000\u4ed6\u6c90\u6d74\u7684\u8bbe\u5907\u662f\u7279\u5730\u6d3e\u4eba\u4ece\u201c\u6276\u6851\u56fd\u201d\u4eff\u5236\u7684\u201c\u98ce\u5415\u201d\u3002 \u3000\u3000\u6bcf\u5f53\u4ed6\u6ce1\u5728\u6eda\u6eda\u7684\u70ed\u6c34\u4e2d\u65f6\uff0c\u4ed6\u5c31\u4f1a\u89c9\u5f97\u4ed6\u597d\u50cf\u53c8\u56de\u5230\u4ed6\u5f1f\u5f1f\u7684\u8eab\u8fb9\uff0c\u53c8\u53d7\u5230\u4e86\u90a3\u79cd\u70ed \u529b\u548c\u538b\u6324\u3002 \u3000\u3000\u2014\u2014\u4ed6\u662f\u5728\u8650\u5f85\u81ea\u5df1\uff1f\u8fd8\u662f\u5728\u60e9\u7f5a\u81ea\u5df1\uff1f \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u4e5f\u540c\u6837\u5c06\u8650\u4f8d\u60e9\u7f5a\u522b\u4eba\u5f53\u4f5c\u4e00\u79cd\u4e50\u8da3\uff1f \u3000\u3000\u73b0\u5728\u5353\u4e1c\u6765\u5fc3\u91cc\u6240\u60f3\u7684\u5374\u4e0d\u662f\u8fd9\u4e9b\u4e8b\uff0c\u4ed6\u60f3\u7684\u662f\u4ef6\u66f4\u6709\u8da3\u7684\u4e8b\uff0c\u4ed6\u60f3\u5c0f\u9ad8\u548c\u8427\u6cea\u8840\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u9ad8\u624b\uff0c\u800c\u4e14\u8fd8\u6709\u4e00\u4ef6\u5929\u4e0b\u6700\u53ef\u6015\u7684\u6b66\u5668\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u547d\u8fd0\u5374\u5df2\u88ab\u6ce8\u5b9a\u4e86\uff0c\u6ce8\u5b9a\u8981\u6b7b\u5728\u4ed6\u7236\u4eb2\u94f8\u51fa\u7684\u5b9d\u5251\u4e0b\u3002 \u3000\u3000\u53e6\u5916\u4e00\u4e2a\u4eba\u672c\u6765\u662f\u5fc5\u5c06\u6b7b\u5728\u4ed6\u624b\u91cc\u7684\uff0c\u6839\u672c\u5c31\u5b8c\u5168\u6ca1\u6709\u62b5\u6321\u9003\u907f\u7684\u4f59\u5730\u3002 \u3000\u3000\u53ef\u662f\u5b9d\u5251\u5374\u5728\u8fd9\u4e2a\u4eba\u624b\u91cc\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4e24\u4e2a\u4eba\u4e4b\u4e2d\u6b7b\u7684\u662f\u8c01\uff1f \u3000\u3000\u5353\u4e1c\u6765\u89c9\u5f97\u8fd9\u4e2a\u95ee\u9898\u5b9e\u5728\u5f88\u6709\u8da3\uff0c\u5b9e\u5728\u6709\u8da3\u6781\u4e86\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u8981\u7b11\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fd8\u6ca1\u6709\u7b11\u51fa\u6765\uff0c\u4ed6\u7684\u7b11\u5bb9\u5c31\u5df2\u7ecf\u88ab\u51bb\u6b7b\u5728\u4ed6\u7684\u76ae\u80a4\u808c\u8089\u91cc\u3002 \u3000\u3000\u4ed6\u7684\u77b3\u5b54\u5df2\u6536\u7f29\u3002 \u3000\u3000\u53ea\u6709\u5728\u771f\u6b63\u6050\u60e7\u7d27\u5f20\u65f6\uff0c\u4ed6\u7684\u77b3\u5b54\u624d\u4f1a\u6536\u7f29\u3002\u73b0\u5728\u4ed6\u5df2\u7ecf\u611f\u89c9\u5230\u8fd9\u4e00\u7c7b\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u611f\u89c9\u5230\u6709\u4e00\u4e2a\u4eba\u7528\u4e00\u79cd\u4ed6\u76f4\u5230\u73b0\u5728\u8fd8\u4e0d\u80fd\u4e86\u89e3\u7684\u65b9\u6cd5\uff0c\u6253\u5f00\u4e86\u4ed6\u8fd9\u95f4\u5bc6\u5ba4\u7684\u95e8\uff0c \u5df2\u7ecf\u9b3c\u9b42\u822c\u7ad9\u5728\u4ed6\u7684\u8eab\u540e\u3002 \u3000\u3000\u8fd9\u5b9e\u5728\u662f\u4ef6\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\uff0c\u5353\u4e1c\u6765\u4ece\u4e0d\u76f8\u4fe1\u8fd9\u4e2a\u4e16\u754c\u4e0a\u771f\u7684\u6709\u4eba\u5177\u6709\u8fd9\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u80fd \u529b\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u5df2\u7ecf\u4e0d\u80fd\u4e0d\u4fe1\u3002 \u3000\u3000\u4ed6\u5f88\u5feb\u5c31\u60f3\u5230\u4e00\u4e2a\u4eba\uff0c\u552f\u4e00\u7684\u4e00\u4e2a\u4eba\uff0c\u201c\u8427\u6cea\u8840\uff0c\u6211\u77e5\u9053\u4e00\u5b9a\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u4e00\u4e2a\u6c99\u54d1\u4f4e\u6c89\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u662f\u6211\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u957f\u957f\u53f9\u606f\u3002 \u3000\u3000\u201c\u795e\u9b3c\u65e0\u51ed\uff0c\u9b3c\u795e\u4e4b\u8bf4\u6bd5\u7adf\u662f\u9760\u4e0d\u4f4f\u7684\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u5426\u5219\u4f60\u5c31\u4e0d\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u73b0\u5728\u4f60\u5e94\u8be5\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\uff0c\u6b7b\u5728\u9ad8\u6e10\u98de\u7684\u2018\u6cea\u75d5\u2019\u4e0b\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u51a5\u51a5\u4e2d\u672c \u6765\u5df2\u5f80\u5b9a\u4e86\u4f60\u7684\u547d\u8fd0\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u606f\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u8fd9\u79cd\u8bf4\u6cd5\u591a\u4e48\u8352\u8c2c\u53ef\u7b11\u3002\u201d \u3000\u3000\u201c\u4ee5\u524d\u5462\uff1f\u201d\u8427\u6cea\u8840\u95ee\uff1a\u201c\u4ee5\u524d\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u201c\u672a\u5fc5\u5c3d\u4fe1\uff0c\u4e5f\u672a\u5fc5\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u5c31\u60f3\u5c3d\u65b9\u6cd5\u8981\u6211\u53bb\u6740\u9ad8\u6e10\u98de\uff1f\u201d\u8427\u6cea\u8840\u53c8\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u770b\u770b\u6211\u4eec\u4e24\u4e2a\u4eba\u4e4b \u4e2d\u7a76\u7adf\u662f\u8c01\u4f1a\u6b7b\u5728\u60df\u624b\u91cc\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u6b7b\u7684\u662f\u8c01\uff0c\u4f60\u5927\u6982\u90fd\u4e0d\u4f1a\u4f24\u5fc3\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u786e\u4e0d\u4f1a\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e0d\u7ba1\u6b7b\u7684\u662f\u8c01\uff0c\u5bf9\u6211\u90fd\u6709\u597d\u5904\uff0c\u5982\u679c\u4f60\u4eec\u4e24\u4f4d\u4e00\u8d77\u6b7b \u4e86\uff0c\u66f4\u662f\u5999\u4e0d\u53ef\u8a00\uff0c\u6211\u4e00\u5b9a\u4f1a\u597d\u597d\u5b89\u6392\u4f60\u4eec\u7684\u540e\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u5353\u4e1c\u6765\u4e00\u5411\u8bf4\u5b9e\u8bdd\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u5fc5\u8bf4\u5047\u8bdd\u3002 \u3000\u3000\u5728\u5927\u591a\u6570\u4eba\u9762\u524d\uff0c\u4ed6\u6839\u672c\u5b8c\u5168\u6ca1\u6709\u8bf4\u8c0e\u7684\u5fc5\u8981\uff0c\u5bf9\u53e6\u5916\u4e00\u4e9b\u4eba\u8bf4\u8c0e\u6839\u672c\u6ca1\u6709\u7528\u3002 \u3000\u3000\u8427\u6cea\u8840\u5df2\u7ecf\u770b\u51fa\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u548c\u8fd9\u4e00\u7c7b\u7684\u4eba\u4ea4\u624b\uff0c\u90a3\u53ef\u4ee5\u7701\u6389\u5f88\u591a\u4e0d\u5fc5\u8981\u7684\u9ebb\u70e6\u3002 \u3000\u3000\u80fd\u548c\u8fd9\u4e00\u7c7b\u7684\u4eba\u4ea4\u624b\u4e5f\u8fdc\u6bd4\u505a\u4ed6\u4eec\u7684\u670b\u53cb\u6109\u5feb\u5f97\u591a\u3002 \u3000\u3000\u201c\u6211\u4e00\u5411\u4e5f\u53ea\u8bf4\u5b9e\u8bdd\uff0c\u201d\u8427\u6cea\u8840\u9053\uff1a\u201c\u6211\u8bf4\u51fa\u7684\u6bcf\u53e5\u6d3b\u4f60\u6700\u597d\u90fd\u8981\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u6211\u4e00\u5b9a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u8fd8\u6ca1\u6709\u89c1\u8fc7\u6211\uff0c\u4f60\u4e00\u5b9a\u5f88\u60f3\u770b\u770b\u6211\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u5b9e\u5728\u60f3\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u53ea\u8981\u56de\u5934\u770b\u6211\u4e00\u773c\uff0c\u4f60\u5c31\u6c38\u8fdc\u770b\u4e0d\u5230\u522b\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u56de\u5934\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6682\u65f6\u6211\u8fd8\u4e0d\u60f3\u6b7b\u3002\u201d \u3000\u3000\u201c\u8bf4\u5b9e\u8bdd\u662f\u79cd\u5f88\u597d\u7684\u4e60\u60ef\uff0c\u6211\u5e0c\u671b\u4f60\u80fd\u4e00\u76f4\u4fdd\u6301\u4e0b\u53bb\u3002\u201d\u8386\u6cea\u8840\u7684\u58f0\u97f3\u5f88\u5e73\u6de1\uff1a\u201c\u53ea\u8981 \u4f60\u8bf4\u4e86\u4e00\u53e5\u8c0e\u8bdd\uff0c\u6211\u5c31\u8981\u4f60\u6b7b\u5728\u8fd9\u4e2a\u6728\u6876\u91cc\u3002\u201d \u3000\u3000\u201c\u6211\u8bf4\u8fc7\uff0c\u6682\u65f6\u6211\u8fd8\u4e0d\u60f3\u6b7b\u3002\u201d\u5353\u4e1c\u6765\u7684\u58f0\u97f3\u4e5f\u5f88\u5e72\u9759\uff1a\u201c\u6211\u5f53\u7136\u66f4\u4e0d\u60f3\u8d64\u88f8\u88f8\u7684\u6b7b\u5728 \u8fd9\u4e48\u6837\u4e00\u4e2a\u6728\u67c4\u91cc\uff0c\u4f60\u5e94\u8be5\u76f8\u4fe1\u8fd9\u4e8b\u6211\u662f\u7edd\u4e0d\u4f1a\u505a\u7684\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u5bf9\u8fd9\u79cd\u60c5\u51b5\u4f3c\u4e4e\u5df2\u7ecf\u89c9\u5f97\u5f88\u6ee1\u610f\uff0c\u6240\u4ee5\u7acb\u523b\u5c31\u95ee\u5230\u4ed6\u6700\u60f3\u77e5\u9053\u7684\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u6211\u8ddf\u4e00\u4e2a\u4eba\u8ba2\u4e86\u4e00\u5f20\u6740\u4eba\u7684\u5951\u7ea6\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u5951\u7ea6\u4e0a\u6700\u91cd\u8981\u7684\u4e00\u9879\u4e00\u76f4\u662f\u7a7a\u767d\u7684\uff0c\u4e00\u76f4\u5c11\u4e86\u4e00\u4e2a\u540d\u5b57\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\u6211\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5df2\u7ecf\u6709\u4eba\u628a\u8fd9\u5f20\u5951\u7ea6\u9001\u6765\u7ed9\u6211\u4e86\uff0c\u800c\u4e14\u5df2\u7ecf\u5728\u4e0a\u9762\u586b\u597d\u4e86\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\u3002\u201d\u8427\u6cea \u8840\u53c8\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u662f\u8c01\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5353\u4e1c\u6765\u5c45\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u90a3\u4e2a\u540d\u5b57\u662f\u6211\u586b\u4e0a\u53bb\u7684\uff0c\u6211\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u5951\u7ea6\u662f\u4e0d\u662f\u4f60\u8ddf\u6211\u8ba2\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff0c\u201d\u6211\u8fd8\u4e0d\u914d\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u4f60\u9001\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\uff0c\u201d\u5353\u4e1c\u6765\u9053\uff0c\u201c\u662f\u4e00\u4e2a\u4eba\u8981\u6211\u9001\u53bb\u7684\uff0c\u5148\u628a\u5951\u7ea6\u9001\u5230\u90a3\u4e2a\u571f\u5730\u5e99\uff0c\u518d\u5230\u57ce\u5916\u53bb\u70b9 \u71c3\u8840\u706b\uff0c\u4e3a\u4e86\u786e\u5b9a\u8981\u8ba9\u4f60\u770b\u89c1\uff0c\u6240\u4ee5\u8981\u6bcf\u5929\u70b9\u4e00\u6b21\uff0c\u8fde\u70b9\u4e09\u5929\u3002\u201d \u3000\u3000\u201c\u662f\u4e00\u4e2a\u4eba\u8981\u4f60\u9001\u53bb\u7684\uff0c\u201d\u8427\u6cea\u8840\u7684\u58f0\u97f3\u5ffd\u7136\u53d8\u5f97\u66f4\u5636\u54d1\uff1a\u201c\u4f60\u77e5\u9053\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u77e5\u9053\u4ed6\u7684\u4eba\u90fd\u4ee5\u4e3a\u4ed6\u65e9\u5c31\u6b7b\u4e86\uff0c\u8fd8\u6709\u5f88\u591a\u4eba\u6839\u672c\u4e0d\u77e5\u9053\u4ed6\u7684 \u540d\u5b57\uff0c\u53ef\u662f\u6211\u77e5\u9053\uff0c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u6ca1\u6709\u4eba\u6bd4\u6211\u77e5\u9053\u5f97\u66f4\u591a\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u201d \u3000\u3000\u201c\u4f60\u4e5f\u77e5\u9053\u4ed6\u7684\u4eba\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\uff0c\u201d\u8427\u6cea\u8840\u7684\u58f0\u97f3\u4eff\u4f5b\u5df2\u88ab\u6495\u88c2\uff1a\u201c\u73b0\u5728\u4f60\u53ef\u4ee5\u7ad9\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u7ad9\u8d77\u6765\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u8981\u5e26\u6211\u53bb\u89c1\u4ed6\u3002\u201d \u3000\u3000\u201c\u6211\u80fd\u4e0d\u80fd\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7acb\u523b\u5c31\u7ad9\u8d77\u6765\uff0c\u5bf9\u4e8e\u65e0\u6cd5\u4e89\u8fa9\u7684\u4e8b\uff0c\u4ed6\u4ece\u6765\u90fd\u4e0d\u4f1a\u4e89\u8fa9\u7684\u3002 \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u62ab\u4e0a\u4f60\u7684\u7d2b\u8c82\uff0c\u7a7f\u4e0a\u4f60\u7684\u978b\u5b50\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u53ef\u662f\u4f60\u6700\u597d\u4e0d\u8981\u518d\u505a\u522b\u7684 \u4e8b\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8de8\u51fa\u6d74\u6d8c\uff0c\u62ab\u4e0a\u8c82\u88d8\uff0c\u4ed6\u7684\u52a8\u4f5c\u5f88\u6162\uff0c\u6bcf\u4e2a\u52a8\u4f5c\u90fd\u5f88\u8c28\u614e\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u542c\u51fa\u4e86\u8427\u6cea\u8840\u58f0\u97f3\u91cc\u7684\u4ec7\u6068\u548c\u6740\u673a\u3002 \u3000\u3000\u8427\u6cea\u8840\u4e0d\u4f1a\u6740\u4ed6\u7684\uff0c\u4e5f\u4e0b\u4f1a\u780d\u65ad\u4ed6\u7684\u817f\uff0c\u53ef\u662f\u53ea\u8981\u4ed6\u7684\u52a8\u4f5c\u8ba9\u8427\u6cea\u8840\u89c9\u5f97\u6709\u4e00\u70b9\u4e0d\u5bf9\uff0c \u4ed6\u8eab\u4e0a\u5c31\u4e00\u5b9a\u4f1a\u6709\u67d0\u4e00\u90e8\u4efd\u8981\u8131\u79bb\u4ed6\u4e86\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u7ed9\u4efb\u4f55\u4eba\u8fd9\u79cd\u673a\u4f1a\u3002 \u3000\u3000\u8427\u6cea\u8840\u65e0\u7591\u6b63\u5728\u89c2\u5bdf\u7740\u4ed6\uff0c\u5bf9\u4ed6\u6bcf\u4e00\u4e2a\u52a8\u4f5c\u90fd\u89c2\u5bdf\u5f97\u5f88\u4ed4\u7ec6\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5411\u662f\u4e2a\u975e\u5e38\u9a84\u50b2\u7684\u4eba\uff0c\u4f60\u7684\u53cd\u5e94\u548c\u901f\u5ea6\u90fd\u591f\u5feb\uff0c\u5185\u5bb6\u6c14\u529f\u4e5f\u7ec3\u5f97\u5f88\u597d\uff0c\u5f53 \u4eca\u5929\u4e0b\u5df2\u7ecf\u5f88\u5c11\u6709\u4eba\u80fd\u51fb\u8d25\u4f60\u3002\u201d\u8427\u8bc5\u8840\u8bf4\uff1a\u201c\u6211\u76f8\u4fe1\u53f8\u9a6c\u8d85\u7fa4\u4e5f\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u56e0\u4e3a\u4ed6\u8fdc \u8fdc\u4e0d\u53ca\u4f60\u51b7\u9759\u3002\u6211\u4ece\u672a\u89c1\u8fc7\u6bd4\u4f60\u66f4\u51b7\u9759\u7684\u4eba\uff0c\u201d \u3000\u3000\u201c\u6709\u65f6\u5019\u6211\u4e5f\u4f1a\u8fd9\u4e48\u60f3\u7684\u3002\u201d\u5353\u4e1c\u6765\u53c8\u5728\u7b11\uff0c\u201c\u6bcf\u4e2a\u4eba\u90fd\u96be\u514d\u4f1a\u6709\u81ea\u6211\u9676\u9189\u7684\u65f6\u5019\uff0c\u5c24 \u5176\u662f\u5728\u591c\u534a\u65e0\u4eba\u65f6\uff0c\u8584\u9189\u5fae\u91ba\u540e\u3002\u201d \u3000\u3000\u201c\u4f60\u6ca1\u6709\u89c1\u8fc7\u6211\uff0c\u4e5f\u6ca1\u6709\u89c1\u8fc7\u6211\u51fa\u624b\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u771f\u7684\u6bd4\u4f60\u5f3a\uff1f\u201d\u8427\u6cea\u8840\u6de1\u6de1\u7684\u95ee\uff1a \u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u4e5f\u8bb8\u4f60\u4e00\u51fa\u624b\u5c31\u53ef\u4ee5\u6740\u4e86\u6211\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u60f3\u5230\u8fc7\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u8fd9\u4e00\u7c7b\u7684\u4e8b\u6211\u6839\u672c\u8fde\u60f3\u90fd\u4e0d\u53bb\u60f3\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u7edd\u5bf9\u7981\u6b62\u81ea\u5df1\u53bb\u60f3\uff0c\u201d\u5353\u4e1c\u6765\u7b11\u5f97\u4eff\u4f5b\u6709\u70b9\u611f\u4f24\uff1a\u201c\u4e00\u4e2a\u4eba\u5982\u679c\u8fd8\u80fd\u6d3b\u4e0b\u53bb\uff0c \u50cf\u8fd9\u4e00\u7c7b\u7684\u4e8b\u5c31\u8fde\u60f3\u90fd\u4e0d\u80fd\u53bb\u60f3\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u51b7\u7b11\uff1a\u201c\u6240\u4ee5\u4f60\u5b81\u613f\u53d8\u5f97\u50cf\u4e00\u6761\u72d7\u4e00\u6837\u542c\u8bdd\uff0c\u4e5f\u4e0d\u6562\u51fa\u624b\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u4e94 \u3000\u3000\u5c0f\u9662\u5916\u7684\u7a84\u95e8\u7d27\u95ed\u3002 \u3000\u3000\u5353\u4e1c\u6765\u6572\u95e8\uff0c\u5148\u6572\u4e09\u58f0\uff0c\u518d\u6572\u4e00\u54cd\u3002 \u3000\u3000\u8fd9\u79cd\u6572\u95e8\u7684\u65b9\u6cd5\u65e0\u7591\u662f\u4ed6\u548c\u9662\u4e2d\u8001\u4eba\u79d8\u5bc6\u7ea6\u5b9a\u7684\uff0c\u5c0f\u9662\u91cc\u5374\u6ca1\u6709\u56de\u5e94\u3002 \u3000\u3000\u201c\u4ed6\u4e0d\u5728\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5728\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e00\u5b9a\u5728\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u901a\u77e5\u4ed6\uff0c\u6709\u4e2a\u4ed6\u4e0d\u80fd\u89c1\u7684\u4eba\u6765\u4e86\uff0c\u8981\u4ed6\u5feb\u70b9\u8d70\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u4ed6\u4e0d\u4f1a\u8d70\u7684\uff0c\u4ed6\u8fd9\u4e00\u751f\u4ece\u6765\u4e5f\u6ca1\u6709\u9003\u8d70\u8fc7\u3002\u201d\u5353\u4e1c\u6765\u544a\u8bc9\u8427\u6cea\u8840\uff1a\u201c\u4f55\u51b5 \u4ed6\u65e9\u5c31\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6765\u627e\u4ed6\u3002\u201d \u3000\u3000\u53ef\u662f\u5c0f\u9662\u91cc\u4ecd\u7136\u6ca1\u6709\u5e94\u58f0\uff0c\u5353\u4e1c\u6765\u53c8\u6572\u95e8\uff0c\u6572\u5f97\u6bd4\u8f83\u7528\u529b\u4e00\u70b9\u3002 \u3000\u3000\u95e8\u5ffd\u7136\u5f00\u4e86\uff0c\u5f00\u4e86\u4e00\u7ebf\u3002 \u3000\u3000\u8fd9\u6247\u95e8\u867d\u7136\u662f\u5f00\u7740\u7684\uff0c\u53ef\u662f\u91cc\u9762\u5e76\u6ca1\u6709\u9501\u4f4f\uff0c\u4e5f\u6ca1\u6709\u4e0a\u6813\u3002 \u3000\u3000\u8001\u4eba\u4e5f\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u5e7d\u9759\u7684\u5c0f\u9662\u91cc\uff0c\u82b1\u9999\u4f9d\u65e7\uff0c\u53e4\u677e\u4f9d\u65e7\uff0c\u5c0f\u4ead\u4f9d\u65e7\uff0c\u8001\u4eba\u4e5f\u4f9d\u65e7\u5750\u5728\u5c0f\u4ead\u91cc\uff0c\u9762\u5bf9\u7740\u4ead\u524d \u7684\u96ea\u5730\uff0c\u4ead\u524d\u4eff\u4f5b\u4f9d\u65e7\u6709\u8776\u821e\u5728\u821e\u3002 \u3000\u3000\u8776\u821e\u4e0d\u518d\u821e\u3002 \u3000\u3000\u8001\u4eba\u4e5f\u4e0d\u4f1a\u518d\u8001\u4e86\u3002 \u3000\u3000\u53ea\u6709\u601d\u60f3\u548c\u611f\u60c5\u624d\u4f1a\u4f7f\u4eba\u8001\uff0c\u5982\u679c\u4e00\u4e2a\u4eba\u5df2\u7ecf\u4e0d\u80fd\u518d\u601d\u60f3\uff0c\u4e0d\u518d\u6709\u611f\u60c5\uff0c\u5c31\u4e0d\u4f1a\u518d\u8001 \u4e86\u3002 \u3000\u3000\u8001\u4eba\u5df2\u7ecf\u4e0d\u80fd\u518d\u601d\u60f3\uff0c\u4e0d\u80fd\u518d\u8003\u8651\u5224\u65ad\u8ba1\u5212\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u8001\u4eba\u4e5f\u5df2\u4e0d\u518d\u6709\u611f\u60c5\uff0c\u4e0d\u518d\u6709\u5fe7\u90c1\u75db\u82e6\u6b22\u4e50\u70e6\u607c\u76f8\u601d\u56de\u5fc6\u3002 \u3000\u3000\u53ea\u6709\u6b7b\u4eba\u624d\u4f1a\u4e0d\u518d\u6709\u601d\u60f3\u548c\u611f\u60c5\uff0c\u53ea\u6709\u6b7b\u4eba\u6c38\u4e0d\u518d\u8001\u3002 \u3000\u3000\u8001\u4eba\u5df2\u6b7b\u3002 \u3000\u3000\u4ed6\u8fd8\u50cf\u6d3b\u7740\u65f6\u4e00\u6837\uff0c\u5e26\u7740\u79cd\u65e0\u6bd4\u98ce\u96c5\u548c\u60a0\u95f2\u7684\u59ff\u6001\u5750\u5728\u5c0f\u4ead\u91cc\u3002\u53ef\u662f\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u4ed6\u90a3\u53cc\u6df7\u5408\u7740\u8001\u4eba\u7684\u667a\u6167\u548c\u5b69\u5b50\u822c\u8c03\u76ae\u7684\u773c\u6674\uff0c\u770b\u6765\u5df2\u4e0d\u518d\u50cf\u9633\u5149\u7167\u8000\u4e0b\u7684\u6d77\u6d0b\uff0c\u5df2\u7ecf \u4e0d\u518d\u6709\u9633\u5149\u7684\u707f\u70c2\u548c\u6d77\u6c34\u7684\u6e5b\u84dd\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u5df2\u7ecf\u53d8\u6216\u6b7b\u7070\u8272\u7684\uff0c\u5c31\u597d\u50cf\u5c06\u665a\u672a\u665a\u5c06\u96ea\u672a\u96ea\u65f6\u7684\u5929\u8272\u4e00\u6837\u3002 \u3000\u3000\u770b\u89c1\u4e86\u8fd9\u53cc\u773c\u775b\uff0c\u5353\u4e1c\u6765\u5c31\u65e0\u6cd5\u518d\u5f80\u524d\u8d70\u4e86\uff0c\u8fde\u4e00\u6b65\u90fd\u4e0d\u60f3\u518d\u5f80\u524d\u8d70\u3002 \u3000\u3000\u4ed6\u7684\u5168\u8eab\u90fd\u4f3c\u5df2\u50f5\u786c\uff0c\u50f5\u786c\u5982\u8fd9\u4e2a\u5df2\u7ecf\u50f5\u6b7b\u4e86\u7684\u8001\u4eba\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u770b\u89c1\u4e86\u8427\u6cea\u8840\u3002 \u3000\u3000\u8427\u6cea\u8840\u770b\u8d77\u6765\u5e76\u4e0d\u9ad8\uff0c\u5b9e\u9645\u4e0a\u5374\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u8981\u9ad8\u4e00\u70b9\uff0c\u800c\u4e14\u5f88\u7626\u3002 \u3000\u3000\u4ed6\u7684\u5934\u53d1\u6f06\u9ed1\uff0c\u8fde\u4e00\u70b9\u82b1\u767d\u7684\u90fd\u6ca1\u6709\uff0c\u7528\u4e00\u6839\u989c\u8272\u5f88\u6de1\u7684\u7070\u5e03\u5728\u5934\u4e0a\u624e\u4e86\u4e2a\u53d1\u9afb\u3002 \u3000\u3000\u4ed6\u8eab\u5df2\u7a7f\u7684\u8863\u886b\u4e5f\u662f\u7528\u8fd9\u79cd\u7070\u5e03\u505a\u6210\u7684\uff0c\u526a\u88c1\u65e2\u4e0d\u5408\u8eab\uff0c\u624b\u5de5\u4e5f\u4e0d\u597d\u3002\u4ed6\u7684\u624b\u91cc\u63d0\u7740\u53e3 \u7bb1\u5b50\uff0c\u9648\u65e7\u800c\u53c8\u5e73\u51e1\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u5353\u4e1c\u6765\u770b\u5230\u7684\u5c31\u53ea\u6709\u8fd9\u4e48\u591a\uff0c\u56e0\u4e3a\u4ed6\u770b\u89c1\u7684\u53ea\u4e0d\u8fc7\u662f\u8427\u6cea\u8840\u7684\u80cc\u3002 \u3000\u3000\u5c31\u597d\u50cf\u4e00\u9635\u51e4\u4ece\u8eab\u8fb9\u5439\u8fc7\u53bb\u4e00\u6837\uff0c\u8fd9\u4e2a\u4e00\u76f4\u50cf\u5f71\u5b50\u4e00\u6837\u8d34\u5728\u4ed6\u540e\u9762\u7684\u4eba\uff0c\u5ffd\u7136\u5c31\u5230\u4e86\u4ed6 \u524d\u9762\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u6c5f\u6e56\u4e2d\u6700\u795e\u79d8\u6700\u53ef\u6015\u7684\u4eba\uff0c\u957f\u5f97\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u5353\u4e1c\u6765\u8fd8\u662f\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u53ef\u662f\u4e00\u4e2a\u8138\u4e0a\u5f88\u5c11\u8868\u9732\u51fa\u60c5\u611f\u7684\u4eba\uff0c\u5374\u5f80\u5f80\u4f1a\u5728\u65e0\u610f\u4e2d\u628a\u60c5\u611f\u4ece\u80cc\u4e0a\u6d41\u9732\u51fa\u6765\u3002 \u3000\u3000\u8427\u6cea\u8840\u7684\u80cc\u5df2\u7ef7\u7d27\uff0c\u6bcf\u4e00\u6839\u808c\u8089\u90fd\u5df2\u7ef7\u7d27\uff0c\u7136\u540e\u5c31\u5f00\u59cb\u4e0d\u505c\u7684\u98a4\u52a8\uff0c\u5c31\u597d\u50cf\u6b63\u5728\u88ab\u4e00\u6761 \u770b\u4e0d\u89c1\u7684\u97ad\u5b50\u7528\u529b\u97ad\u631e\u3002 \u3000\u3000\u8001\u4eba\u7684\u6b7b\uff0c\u5c31\u662f\u8fd9\u6761\u97ad\u5b50\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u4ece\u4ed6\u7684\u58f0\u97f3\u91cc\u542c\u51fa\u4ed6\u7edd\u4e0d\u662f\u8fd9\u4e2a\u8001\u4eba\u7684\u670b\u53cb\u3002 \u3000\u3000\u4ed6\u4eec\u4e4b\u95f4\u65e0\u7591\u6709\u67d0\u79cd\u65e0\u6cd5\u5316\u89e3\u7684\u4ec7\u6068\u3002 \u3000\u3000\u4ed6\u903c\u5353\u4e1c\u6765\u5230\u4ed6\u8fd9\u91cc\u6765\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u8981\u5229\u7528\u8fd9\u4e2a\u8001\u4eba\u7684\u8840\u6765\u6d17\u53bb\u4ed6\u5fc3\u91cc\u7684\u6028\u6bd2\u548c\u4ec7\u6068\u3002 \u3000\u3000\u73b0\u5728\u8001\u4eba\u6b7b\u4e86\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u53cd\u800c\u5982\u6b64\u75db\u82e6\u6fc0\u52a8\u548c\u60b2\u4f24\uff1f \u3000\u3000\u66f4\u4ee4\u4eba\u60f3\u4e0d\u5230\u7684\u662f\u5353\u4e1c\u6765\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u662f\u5fc3\u80f8\u5f00\u9614\u7684\u4eba\uff0c\u7edd\u4e0d\u5bb9\u4efb\u4f55\u4eba\u4fb5\u72af\u5230\u4ed6\u7684\u81ea\u5c0a\u3002 \u3000\u3000\u8fd9\u4e2a\u4e16\u754c\u4e0a\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u50cf\u8427\u6cea\u8840\u8fd9\u4e48\u6837\u4fae\u8fb1\u8fc7\u4ed6\uff0c\u8fd9\u79cd\u4fae\u8fb1\u4e5f\u53ea\u6709\u7528\u8840\u624d\u80fd\u6d17\u6e05\u3002 \u3000\u3000\u5982\u679c\u4ed6\u6740\u4e86\u8427\u6cea\u8840\uff0c\u6ca1\u6709\u4eba\u4f1a\u89c9\u5f97\u5947\u602a\uff0c\u4e5f\u6ca1\u6709\u4eba\u4f1a\u89c9\u5f97\u9057\u61be\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u5982\u996e\u9152\u822c\u628a\u8427\u6cea\u8840\u7684\u8840\u559d\u5e72\uff0c\u4e5f\u6ca1\u6709\u4eba\u4f1a\u96be\u53d7\u3002 \u3000\u3000\u8427\u6cea\u8840\u5e76\u4e0d\u662f\u4e2a\u503c\u5f97\u540c\u60c5\u7684\u4eba\uff0c\u5353\u4e1c\u6765\u672c\u6765\u5c31\u5e94\u8be5\u6740\u4e86\u4ed6\u7684\u3002\u53ea\u8981\u4e00\u6709\u673a\u4f1a\uff0c\u5c31\u4e0d\u8be5\u653e \u8fc7\u4ed6\u3002 \u3000\u3000\u73b0\u5728\u6b63\u662f\u5353\u4e1c\u6765\u4e0b\u624b\u7684\u6700\u597d\u673a\u4f1a\u3002 \u3000\u3000\u73b0\u5728\u8427\u6cea\u8840\u7684\u80cc\u5c31\u50cf\u662f\u4e00\u5927\u5757\u5e73\u5766\u80a5\u7f8e\u800c\u4e14\u5b8c\u5168\u4e0d\u8bbe\u9632\u7684\u571f\u5730\u4e00\u6837\uff0c\u7b49\u7740\u4eba\u672a\u4fb5\u72af\u8df5 \u8e0f\u3002 \u3000\u3000\u73b0\u5728\u6b63\u662f\u4ed6\u60c5\u7eea\u6700\u6fc0\u52a8\u3001\u6700\u5bb9\u6613\u9020\u6210\u758f\u5ffd\u548c\u9519\u8bef\u7684\u65f6\u5019\u3002 \u3000\u3000\u53ef\u662f\u5353\u4e1c\u6765\u5c45\u7136\u8fde\u4e00\u70b9\u4e3e\u52a8\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u79cd\u673a\u4f1a\u5c31\u50cf\u662f\u4e00\u7247\u6b63\u597d\u4ece\u4f60\u9762\u524d\u98de\u8fc7\u53bb\u7684\u6d6e\u4e91\uff0c\u7a0d\u7eb5\u5373\u901d\uff0c\u6c38\u4e0d\u518d\u6765\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7684\u547c\u5438\u5ffd\u7136\u505c\u987f\uff0c\u77b3\u5b54\u518d\u6b21\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u770b\u89c1\u8fd9\u4e2a\u4eba\u4e86\uff0c\u8fd9\u4e2a\u5929\u4e0b\u6700\u795e\u79d8\u6700\u53ef\u6015\u7684\u4eba\u3002 \u3000\u3000\u8427\u6cea\u8840\u5c45\u7136\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u5353\u4e1c\u6765\u3002 \u3000\u3000\u4ed6\u7684\u8138\u662f\u4e00\u5f20\u5f88\u5e73\u51e1\u7684\u8138\uff0c\u53ef\u662f\u4ed6\u7684\u773c\u775b\u5374\u50cf\u662f\u4e00\u628a\u521a\u51fa\u9798\u7684\u5b9d\u5200\u3002 \u3000\u3000\u201c\u5982\u679c\u6709\u4eba\u8981\u6740\u6211\uff0c\u5219\u624d\u5c31\u662f\u6700\u597d\u7684\u673a\u4f1a\u4e86\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u50cf\u90a3\u6837\u7684\u673a\u4f1a\u6c38\u8fdc\u4e0d\u4f1a\u518d \u6709\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u5f97\u51fa\u3002\u201d \u3000\u3000\u201c\u521a\u624d\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u624b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5e76\u4e0d\u60f3\u6740\u4f60\u3002\u201d\u5353\u4e1c\u6765\u8bf4\u5f97\u5f88\u8bda\u6073\uff1a\u201c\u8fd9\u4e00\u7c7b\u7684\u4e8b\u6211\u4ece\u6765\u6ca1\u6709\u53bb\u60f3\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u60f3\u4e00\u60f3\u7684\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff0c\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u4e00\u5b9a\u4f1a\u6740\u4f60\u3002\u201d \u3000\u3000\u201c\u4e00\u5b9a\u4f1a\u6740\u6211\uff1f\u201d\u5353\u4e1c\u6765\u7684\u773c\u5149\u59cb\u7ec8\u6ca1\u6709\u79bb\u5f00\u8fc7\u8fd9\u4e2a\u4eba\u7684\u8138\uff1a\u201d\u4f60\u597d\u50cf\u4e00\u5411\u90fd\u4e0d\u80af\u514d\u8d39 \u6740\u4eba\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u6b21\u5374\u662f\u4f8b\u5916\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u76ee\u5149\u7ec8\u4e8e\u79fb\u5411\u4ead\u4e2d\u7684\u8001\u4eba\uff1a\u201c\u4f60\u8bf4\u6211\u6740\u4e86\u4ed6\uff1f\u4f60\u8ba4\u4e3a\u4ed6\u4f1a\u6b7b\u4efb\u6211\u624b\u91cc\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\u4f60\u5f53\u7136\u52a8\u4e0d\u4e86\u4ed6\uff0c\u8fde\u4ed6\u7684\u4e00\u6839\u6beb\u53d1\u90fd\u52a8\u4e0d\u4e86\uff0c\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u4f60\u7684\u6b66\u529f\u867d\u4e0d\u5dee\uff0c \u53ef\u662f\u4ed6\u4e3e\u624b\u95f4\u5c31\u53ef\u4ee5\u5c06\u4f60\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4ed6\u53ea\u8981\u7528\u4e00\u6839\u624b\u6307\u5c31\u8db3\u591f\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u73b0\u5728\u7684\u60c5\u51b5\u5df2\u4e0d\u540c\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u4e4b\u524d\uff0c\u5c31\u5df2\u7ecf\u662f\u4e2a\u5e9f\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u5f97\u51fa\u4ed6\u7684\u771f\u6c14\u5185\u529b\u90fd\u65e9\u5c31\u88ab\u4eba\u5e9f\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u5f97\u51fa\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u521a\u624d\u770b\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7eb5\u6a2a\u5929\u4e0b\uff0c\u884c\u8ff9\u4e00\u5411\u98d8\u5ffd\uff0c\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u529f\u529b\u5df2\u5931\uff0c\u600e\u4e48\u80af\u8eb2\u5230\u8fd9\u91cc\u6765\uff0c\u5bc4\u5c45\u5728\u4e00 \u4e2a\u4ed6\u7edd\u5bf9\u4e0d\u4f1a\u770b\u5f97\u8d77\u7684\u4eba\u7684\u5c4b\u6a90\u4e0b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u770b\u5f97\u8d77\u6211\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u4f46\u4ed6\u5374\u8fd8\u662f\u5230\u6211\u8fd9\u91cc\u6765\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u56e0\u4e3a\u4ed6\u77e5 \u9053\u6211\u8fd9\u4e2a\u4eba\u81f3\u5c11\u6709\u4e00\u70b9\u597d\u5904\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u201c\u6211\u5f88\u53ef\u9760\uff0c\u975e\u5e38\u53ef\u9760\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e0d\u4f46\u4eba\u53ef\u9760\uff0c\u5634\u4e5f\u53ef\u9760\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6c5f\u6e56\u4e2d\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u529f\u529b\u5df2\u5931\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u9690\u5c45\u5728\u8fd9\u91cc\uff0c\u56e0\u4e3a\u6211\u4e00\u76f4\u5b88\u53e3 \u5982\u74f6\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\u8427\u6cea\u8840\u4e5f\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u201c\u6c5f\u6e56\u4e2d\u60f3\u8981\u4ed6\u8fd9\u6761\u547d\u7684\u4eba\u5f88\u4e0d\u5c11\uff0c\u5982\u679c\u6211\u8981\u51fa\u5356\u4ed6\uff0c\u4ed6\u65e9\u5df2\u6b7b\u5728\u522b\u4eba\u624b\u91cc\u3002\u201d\u5353\u4e1c\u6765 \u8bf4\uff1a\u201c\u5c31\u7b97\u6211\u8981\u4eb2\u624b\u6740\u4ed6\uff0c\u4e5f\u4e0d\u5fc5\u7b49\u5230\u73b0\u5728\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\u65e0\u7591\u4e5f\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u201c\u800c\u4e14\u4ed6\u8fd8\u6551\u8fc7\u6211\u4e00\u547d\uff0c\u6240\u4ee5\u624d\u4f1a\u5728\u6700\u5371\u9669\u7684\u65f6\u5019\u6765\u627e\u6211\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4f60\u60f3\u6211\u4f1a\u4e0d \u4f1a\u5bb3\u6b7b\u6211\u552f\u4e00\u7684\u6069\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f60\u4f1a\uff01\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u6211\u65e9\u5df2\u77e5\u9053\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u591a\u5e74\u524d\u6211\u5c31\u5df2\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6765\u7684\u65f6\u5019\uff0c\u529f\u529b\u5c31\u5df2\u88ab\u4eba\u5e9f\u4e86\u3002\u6240\u4ee5\u624d\u4f1a\u9690\u5c45\u5728\u8fd9\u91cc\uff0c\u8fd9\u4e00\u70b9\u4f60\u4e5f\u5e94\u8be5\u60f3\u8c61\u5f97 \u5230\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u627f\u8ba4\u3002 \u3000\u3000\u4e8c\u5341\u5e74\u524d\uff0c\u8001\u4eba\u8fd8\u672a\u8001\uff0c\u90a3\u65f6\u5019\u6c5f\u6e56\u5df2\u7ecf\u6ca1\u6709\u51e0\u4e2a\u4eba\u662f\u4ed6\u7684\u5bf9\u624b\u3002 \u3000\u3000\u8427\u6cea\u8840\u58f0\u97f3\u51b0\u51b7\uff1a\u201c\u522b\u4eba\u4e0d\u4f1a\uff1b\u53ef\u662f\u4f60\u4f1a\u3002\u201d \u3000\u3000\u201c\u4ed6\u7684\u52a8\u529b\u867d\u5931\uff0c\u5934\u8111\u4ecd\u5728\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u4ed6\u7684\u5934\u8111\u5c31\u50cf\u662f\u4e2a\u6c38\u8fdc\u6316\u4e0d\u5c3d\u7684\u5b9d\u85cf\uff0c\u91cc \u9762\u57cb\u85cf\u7740\u7684\u601d\u60f3\u667a\u6167\u548c\u79d8\u5bc6\uff0c\u8fdc\u6bd4\u4e16\u4e0a\u4efb\u4f55\u73e0\u5b9d\u90fd\u73cd\u8d35\u3002\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u7684\u770b\u7740\u5353\u4e1c\u6765\uff1a\u201c\u4f60\u4e00\u76f4\u4e0d\u6740\u6c60\uff0c\u53ea\u56e0\u4e3a\u4ed6\u5bf9\u4f60\u8fd8\u6709\u7528\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6c89\u9ed8\u7740\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u4e45\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u5353\u4e1c\u6765\u5c45\u7136\u627f\u8ba4\u4e86\uff1a\u201c\u662f\u6211\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u7684\u624b\u63e1\u7d27\uff0c\u63d0\u7740\u7bb1\u5b50\u7684\u624b\uff0c\u77ac\u606f\u95f4\u5c31\u53ef\u4ee5\u6740\u4eba\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u201c\u5176\u4e70\u4ed6\u4e00\u76f4\u5230\u73b0\u5728\u5bf9\u6211\u90fd\u8fd8\u662f\u6709\u7528\u7684\u3002\u201d\u5353\u4e1c\u6765\u53f9\u606f\uff1a\u201c\u53ea\u53ef\u60dc\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u975e\u6740\u4ed6 \u4e0d\u53ef\u7684\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u4ed6\u770b\u7740\u8427\u6cea\u8840\u624b\u91cc\u7684\u7bb1\u5b50\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u51c6\u5907\u51fa\u624b\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u5728\u4f60\u51fa\u624b\u4e4b\u524d\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6b8b\u4e00\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f60\u8981\u6740\u6211\u771f\u7684\u662f\u56e0\u4e3a\u4f60\u8981\u4e3a\u4ed6\u590d\u4ec7\uff0c\u201d \u3000\u3000\u5353\u4e1c\u6765\u4e0d\u7b49\u8427\u6cea\u8840\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u5c31\u5df2\u7ecf\u5148\u5426\u5b9a\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u201c\u4e0d\u662f\u7684\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u4f60\u7edd\u4e0d\u4f1a\u4e3a\u4ed6\u590d\u4ec7\uff0c\u56e0\u4e3a\u6211\u770b\u5f97\u51fa\u4f60\u6068\u4ed6\uff0c\u8fdc\u6bd4\u4e16\u4e0a\u6240\u6709\u7684\u4eba\u90fd \u6068\u4ed6\uff0c\u5982\u679c\u4ed6\u8fd8\u6d3b\u7740\uff0c\u4f60\u4e5f\u4f1a\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u8427\u6cea\u8840\u5c45\u7136\u4e5f\u7acb\u523b\u627f\u8ba4\uff1a\u201c\u5982\u679c\u4ed6\u4e0d\u6b7b\uff0c\u6211\u4e5f\u4f1a\u6740\u4e86\u4ed6\u7684\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u53c8\u56e0\u75db\u82e6\u800c\u5636\u54d1\uff1a\u201c\u53ef\u662f\u5728\u6211\u51fa\u624b\u4e4b\u524d\uff0c\u6211\u4e5f\u4f1a\u95ee\u4ed6\u4e00\u4ef6\u4e8b\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a \u201c\u4e00\u4ef6\u53ea\u6709\u4ed6\u624d\u80fd\u544a\u8bc9\u6211\u7684\u4e8b\uff0c\u4e00\u4ef6\u53ea\u6709\u4ed6\u624d\u80fd\u89e3\u7b54\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u77e5\u9053\u6211\u8981\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u53cd\u95ee\uff1a\u201c\u5982\u679c\u6211\u77e5\u9053\u53c8\u600e\u4e48\u6837\uff1f\u4f60\u4f1a\u4e0d\u4f1a\u653e\u8fc7\u6211\uff0c\u201d \u3000\u3000\u8427\u6cea\u8840\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u6ca1\u6709\u518d\u8bf4\u4e00\u4e2a\u5b57\uff0c\u8427\u6cea\u8840\u53c8\u957f\u957f\u53f9\u606f\u3002 \u3000\u3000\u201c\u53ef\u60dc\u6211\u4e0d\u77e5\u9053\uff0c\u771f\u7684\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u90a3\u5b9e\u5728\u5f88\u53ef\u60dc\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u8981\u95ee\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f \u3000\u3000\u65e0\u8bba\u90a3\u662f\u4ec0\u4e48\u4e8b\uff0c\u73b0\u5728\u90fd\u5df2\u4e0d\u91cd\u8981\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u73b0\u5728\u8001\u4eba\u5df2\u6b7b\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5df2\u7ecf\u6ca1\u6709\u4eba\u80fd\u89e3\u7b54\u8fd9\u4e2a\u79d8\u5bc6\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5df2\u7ecf\u6b7b\u4e86\uff0c\u65e0\u8bba\u8c01\u90fd\u5e94\u8be5\u53ef\u4ee5\u770b\u51fa\u4ed6\u5df2\u7ecf\u6b7b\u5b9a\u4e86\xb7 \u3000\u3000\u8427\u6cea\u8840\u5df2\u7ecf\u6253\u5f00\u4e86\u4ed6\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u2014\u2014\u5929\u4e0b\u6700\u53ef\u6015\u7684\u6b66\u5668\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u2014\u2014\u662f\u4e00\u53e3\u7bb1\u5b50\u3002 \u3000\u3000\u7bb1\u5b50\u53ef\u6015\uff0c\u63d0\u7740\u7bb1\u5b50\u7684\u8fd9\u4e2a\u4eba\u66f4\u53ef\u6015\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7684\u77b3\u5b54\u53c8\u5f00\u59cb\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u5728\u770b\u7740\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u7684\u8138\u4e0a\u5728\u6d41\u7740\u51b7\u6c57\uff0c\u4ed6\u5168\u8eab\u808c\u8089\u90e8\u5728\u98a4\u6296\u8df3\u52a8\u3002 \u3000\u3000\u201c\u5d29\u201d\u7684\u4e00\u54cd\uff0c\u7bb1\u5b50\u5f00\u4e86\uff0c\u5f00\u4e86\u4e00\u7ebf\u3002 \u3000\u3000\u5c31\u50cf\u662f\u5a9a\u773c\u5982\u4e1d\u7684\u60c5\u4eba\u4e4b\u773c\uff0c\u90a3\u4e48\u6837\u7684\u4e00\u6761\u7ebf\u3002 \u516d \u3000\u3000\u65e0\u8bba\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u4ec0\u4e48\u5730\u65b9\uff0c\u53ea\u8981\u8fd9\u53e3\u7bb1\u5b50\u6253\u5f00\u8fd9\u4e48\u6837\u4e00\u6761\u7ebf\uff0c\u8fd9\u4e2a\u5730\u65b9\u5c31\u4f1a\u6709\u4e00\u4e2a\u4eba \u4f1a\u88ab\u63d0\u7740\u7bb1\u5b50\u7684\u8fd9\u4e2a\u4eba\u50cf\u725b\u7f8a\u822c\u5ba1\u5224\u3002 \u3000\u3000\u8fd9\u4e2a\u5730\u65b9\u4e5f\u5c31\u4f1a\u50cf\u662f\u4e2a\u5c60\u573a\u3002 \u7b2c\u5341\u56db\u7ae0\u3000\u8c01\u662f\u725b\u7f8a>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u5341\u56db\u7ae0\u3000\u8c01\u662f\u725b\u7f8a \u4e00 \u3000\u3000\u4e8c\u6708\u5eff\u56db\uff0c\u5348\u65f6\u3002 \u3000\u3000\u5173\u6d1b\u9053\u4e0a\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u97ad\u9a6c\u3001\u653e\u7f30\u3001\u98de\u9a70\u3002 \u3000\u3000\u9a70\u5411\u957f\u5b89\u3002 \u3000\u3000\u4ed6\u7684\u9a6c\u4ecd\u5728\u98de\u5954\uff0c\u4ecd\u7136\u51b2\u52b2\u5341\u8db3\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u5728\u9014\u4e2d\u6362\u8fc7\u4e86\u56db\u6b21\u9a6c\u3002 \u3000\u3000\u4ed6\u6362\u7684\u90fd\u662f\u597d\u9a6c\u3001\u5feb\u9a6c\uff0c\u56e0\u4e3a\u4ed6\u8bc6\u9a6c\uff0c\u4e5f\u80af\u51fa\u9ad8\u4ef7\u4e70\u9a6c\u3002 \u3000\u3000\u4ed6\u6025\u7740\u8981\u8d76\u56de\u957f\u5b89\u3002 \u3000\u3000\u6362\u56db\u6b21\u9a6c\uff0c\u88ab\u6362\u4e0b\u7684\u9a6c\u90fd\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7684\u4eba\u4e5f\u4e00\u6837\uff0c\u4e00\u6837\u7cbe\u75b2\u529b\u7aed\uff0c\u4e00\u6837\u5c06\u8981\u5012\u4e0b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e00\u5b9a\u8981\u6025\u7740\u8d76\u56de\u957f\u5973\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5ffd\u7136\u6709\u4e86\u79cd\u51f6\u6076\u4e0d\u7965\u7684\u9884\u5146\uff0c\u597d\u50cf\u5df2\u611f\u89c9\u5230\u6709\u4e00\u4e2a\u548c\u4ed6\u6781\u4eb2\u8fd1\u7684\u4eba\u5c06\u8981\u50cf\u725b\u7f8a\u822c \u88ab\u6740\u3002 \u4e8c \u3000\u3000\u540c\u65e5\uff0c\u540c\u65f6\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u4f9d\u65e7\u662f\u957f\u5b89\uff0c\u957f\u5b89\u4f9d\u65e7\uff0c\u4eba\u4e5f\u4f9d\u65e7\u3002 \u3000\u3000\u63d0\u7740\u7bb1\u5b50\u7b49\u7740\u6740\u4eba\u7684\u4eba\uff0c\u6ca1\u6709\u63d0\u7bb1\u5b50\u7b49\u7740\u88ab\u6740\u7684\u4eba\u90fd\u4f9d\u65e7\u3002 \u3000\u3000\u65e0\u96ea\uff0c\u4e5f\u65e0\u9633\u5149\u3002 \u3000\u3000\u60e8\u60e8\u6de1\u6de1\u7684\u5929\u8272\u5c31\u50cf\u662f\u4e00\u53cc\u5df2\u7ecf\u54ed\u5f97\u5927\u4e45\u7684\u5c11\u5973\u773c\u775b\u4e00\u6837\uff0c\u5df2\u7ecf\u5931\u53bb\u4e86\u5b83\u7684\u5996\u5a9a\u660e\u8273\u548c \u5149\u4eae\u3002 \u3000\u3000\u5728\u8fd9\u4e48\u6837\u4e00\u53cc\u773c\u775b\u4e0b\u770b\u6765\uff0c\u8fd9\u53e3\u7bb1\u5b50\u4e5f\u4f9d\u65e7\u662f\u90a3\u4e48\u5e73\u51e1\uff0c\u90a3\u4e48\u9648\u65e7\uff0c\u90a3\u4e48\u7b28\u62d9\uff0c\u90a3\u4e48\u4e11 \u964b\u3002 \u3000\u3000\u53ef\u662f\u7cdf\u5b50\u5df2\u7ecf\u5f00\u4e86\u3002 \u3000\u3000\u7bb1\u5b50\u91cc\u90a3\u4e9b\u5e73\u51e1\u9648\u65e7\u7b28\u62d9\u4e11\u964b\u7684\u94c1\u4ef6\uff0c\u5df2\u5c06\u5728\u77ac\u606f\u95f4\u53d8\u4e3a\u4e00\u79cd\u4e0d\u53ef\u62db\u67b6\u95ea\u907f\u6297\u62d2\u62b5\u5fa1\u7684 \u6b66\u5668\uff0c\u5c06\u5353\u4e1c\u6765\u683c\u6740\u4e8e\u540c\u4e00\u5239\u90a3\u95f4\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5c11\u5e74\u65f6\u662f\u7528\u5200\u7684\uff0c\u76f4\u5230\u58ee\u5e74\u65f6\u4ecd\u7528\u5200\u3002 \u3000\u3000\u4ed6\u7528\u8fc7\u5f88\u591a\u79cd\u5200\uff0c\u4ece\u4ed6\u5341\u4e09\u5c81\u65f6\u7528\u4e00\u67c4\u4ece\u5c60\u592b\u8089\u6848\u4e0a\u7a83\u6765\u7684\u5c60\u5200\uff0c\u628a\u5f53\u5730\u9c7c\u8089\u5e02\u4e95\u7684\u6076 \u9738\u201c\u6740\u732a\u8001\u5927\u201d\u523a\u6740\u4e8e\u8089\u6848\u4e0a\u4e4b\u540e\uff0c\u4ed6\u5df2\u4e0d\u77e5\u6362\u8fc7\u591a\u5c11\u67c4\u5200\u3002 \u3000\u3000\u5341\u56db\u5c81\u65f6\u4ed6\u7528\u62c6\u94c1\u5355\u5200\uff0c\u5341\u4e94\u5c81\u65f6\u4ed6\u7528\u7eaf\u94a2\u6734\u5200\uff0c\u5341\u516d\u5c81\u65f6\u4ed6\u7528\u9b3c\u5934\u5200\uff0c\u5341\u516b\u5c81\u65f6\u4ed6\u5219 \u6362\u5355\u5200\u4e3a\u53cc\u5200\uff0c\u7528\u4e00\u5bf9\u6781\u7075\u4fbf\u8f7b\u5de7\u7684\u9e33\u9e2f\u8774\u8776\u5200\uff0c\u4e8c\u5341\u5c81\u65f6\u4ed6\u53c8\u6362\u53cc\u5200\u4e3a\u5355\u5200\uff0c\u6362\u4e86\u67c4\u4efd\u91cf \u6781\u91cd\u3001\u6781\u6709\u6c14\u6d3e\u7684\u91d1\u80cc\u780d\u5c71\u5200\u3002 \u3000\u3000\u5eff\u4e09\u5c81\u65f6\uff0c\u4ed6\u7528\u7684\u5c31\u662f\u6b66\u6797\u4e2d\u6700\u6709\u6c14\u6d3e\u7684\u9c7c\u9cde\u7d2b\u91d1\u5200\u4e86\u3002 \u3000\u3000\u53ef\u662f\u5eff\u516d\u5c81\u4ee5\u540e\uff0c\u4ed6\u7528\u7684\u5200\u53c8\u4ece\u534e\u4e3d\u53d8\u4e3a\u5e73\u51e1\u4e86\u3002 \u3000\u3000\u4ed6\u53c8\u7528\u8fc7\u62c6\u94c1\u5200\u3001\u96c1\u7fce\u5200\uff0c\u751a\u81f3\u8fd8\u7528\u8fc7\u65b9\u5916\u4eba\u7528\u7684\u6212\u5200\u3002 \u3000\u3000\u4ece\u4e00\u4e2a\u4eba\u7528\u5200\u7684\u8f6c\u53d8\u548c\u8fc7\u7a0b\u95f4\uff0c\u662f\u4e0d\u662f\u4e5f\u53ef\u4ee5\u770b\u51fa\u4ed6\u5200\u6cd5\u548c\u5fc3\u60c5\u7684\u8f6c\u53d8\uff1f \u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5bf9\u4e8e\u201c\u5200\u201d\u4e0e\u201c\u5200\u6cd5\u201d\u7684\u4e86\u89e3\u548c\u8ba4\u8bc6\uff0c\u6b66\u6797\u4e2d\u5927\u6982\u5df2\u7ecf\u6ca1\u6709\u51e0\u4e2a\u4eba\u80fd\u6bd4\u5f97 \u4e0a\u4ed6\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u58ee\u5e74\u540e\u5c31\u5df2\u4e0d\u518d\u7528\u5200\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u80fd\u628a\u6709\u5f62\u7684\u5200\u6362\u4e3a\u65e0\u5f62\u7684\u5200\uff0c\u5df2\u7ecf\u80fd\u4ee5\u201c\u65e0\u5200\u201d\u80dc\u201c\u6709\u5200\u201d\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4ecd\u6709\u5200\u3002 \u3000\u3000\u4ed6\u7684\u9774\u7b52\u91cc\u8fd8\u662f\u85cf\u7740\u628a\u950b\u5229\u6c89\u91cd\u524a\u94c1\u5982\u6ce5\u7684\u77ed\u5200\uff0c\u4e00\u628a\u80fd\u8f7b\u6613\u5c06\u4eba\u53cc\u817f\u523a\u65ad\u5982\u5207\u8c46\u8150\u4e00 \u6837\u7684\u77ed\u5200\u3002 \u3000\u3000\u2014\u2014\u8776\u821e\u7684\u817f\uff0c\u591a\u4e48\u8f7b\u76c8\uff0c\u591a\u4e48\u7075\u5de7\uff0c\u591a\u4e48\u7f8e\u3002 \u3000\u3000\u9c9c\u8840\u9c9c\u82b1\u822c\u6e85\u51fa\uff0c\u8776\u821e\u4e0d\u821e\uff0c\u4e5f\u4e0d\u80fd\u518d\u821e\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u6731\u731b\u5954\uff0c\u5c0f\u9ad8\u8d70\u3002 \u3000\u3000\u4e8e\u662f\u77ed\u5200\u53c8\u88ab\u5353\u4e1c\u6765\u62fe\u8d77\uff0c\u5e26\u7740\u8840\u6dcb\u6dcb\u7684\u821e\u8005\u4e4b\u9b42\uff0c\u88ab\u85cf\u4e8e\u51b7\u51b0\u51b0\u7684\u4eba\u4e4b\u9774\u7b52\u3002 \u3000\u3000\u8fd9\u67c4\u5200\u65e0\u7591\u662f\u5200\u4e2d\u4e4b\u5200\uff0c\u662f\u5353\u4e1c\u6765\u7ecf\u8fc7\u65e0\u6570\u6b21\u60e8\u75db\u6559\u8bad\u3001\u7ecf\u8fc7\u65e0\u6570\u6b21\u632b\u8d25\u548c\u65e0\u6570\u6b21\u80dc\u5229 \u4e4b\u540e\uff0c\u624d\u8715\u53d8\u51fa\u7684\u4e00\u628a\u5200\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u5982\u679c\u51fa\u5200\uff0c\u65e0\u7591\u4e5f\u662f\u4ed6\u65e0\u6570\u6b21\u8715\u53d8\u4e2d\u7684\u7cbe\u8403\u3002 \u3000\u3000\u8427\u6cea\u8840\u8981\u7528\u4ec0\u4e48\u6cd5\u5b50\u624d\u80fd\u62fc\u6210\u4e00\u79cd\u6b66\u5668\u6765\u514b\u5236\u4f4f\u8fd9\u628a\u5200\uff1f \u3000\u3000\u4ed6\u5f53\u7136\u6709\u6cd5\u5b50\u7684\u3002 \u3000\u3000\u4ed6\u6740\u4eba\u4ece\u672a\u5931\u624b\u8fc7\u3002 \u4e09 \u3000\u3000\u540c\u65e5\uff0c\u5348\u540e\u3002 \u3000\u3000\u957f\u5b89\u57ce\u5916\u7684\u5b98\u9053\u3002 \u3000\u3000\u957f\u5b89\u5df2\u8fd1\u4e86\uff0c\u53f8\u9a6c\u8d85\u7fa4\u7684\u5fc3\u60c5\u5374\u66f4\u70e6\u8e81\uff0c\u90a3\u79cd\u4e0d\u7965\u7684\u9884\u611f\u4e5f\u66f4\u5f3a\u70c8\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u5df2\u7ecf\u53ef\u4ee5\u770b\u5230\u4ed6\u6709\u4e00\u4e2a\u6700\u4eb2\u8fd1\u7684\u4eba\u6b63\u5012\u5728\u8840\u6cca\u4e2d\u6323\u624e\u547c\u558a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u770b\u4e0d\u51fa\u8fd9\u4e2a\u4eba\u662f\u8c01\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5fc5\u5c06\u6b7b\u5728\u957f\u5b89\u7684\u4eba\uff0c\u662f\u9ad8\u6e10\u98de\u548c\u6731\u731b\uff0c\u4ed6\u7b97\u51c6\u4ed6\u4eec\u5fc5\u6b7b\u65e0\u7591\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5bf9\u8fd9\u4e24\u4e2a\u4eba\u7684\u6b7b\u6d3b\u5e76\u4e0d\u5173\u5fc3\u3002\u4ed6\u4eec\u65e2\u4e0d\u662f\u4ed6\u7684\u4eb2\u4eba\uff0c\u4e5f\u4e0d\u662f\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u5434\u5a49\u5462\uff1f\u4f1a\u4e0d\u4f1a\u662f\u5434\u5a49\uff1f \u3000\u3000\u7edd\u4e0d\u4f1a\u3002 \u3000\u3000\u5979\u662f\u4e2a\u5973\u4eba\uff0c\u4ece\u672a\u4f24\u5bb3\u8fc7\u522b\u4eba\uff0c\u800c\u4e14\u4e00\u5411\u6df1\u5c45\u7b52\u51fa\uff0c\u600e\u4e48\u4f1a\u9047\u5230\u8fd9\u79cd\u53ef\u6015\u7684\u707e\u7978\uff1f \u3000\u3000\u96be\u9053\u662f\u5353\u4e1c\u6765\uff1f \u3000\u3000\u90a3\u66f4\u662f\u7edd\u65e0\u53ef\u80fd\u7684\u4e8b\uff0c\u4ee5\u5353\u4e1c\u6765\u7684\u8c28\u614e\u667a\u8c0b\u548c\u6b66\u529f\uff0c\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\u90fd\u80fd\u4fdd\u62a4\u81ea\u5df1 \u7684\u3002 \u3000\u3000\u5c31\u7b97\u5927\u4f7f\u5c40\u8fd9\u4e00\u6b21\u4e0d\u5e78\u60e8\u8d25\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u4f1a\u5b89\u7136\u8131\u8d70\uff0c\u5168\u8eab\u800c\u9000\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u51e0\u4e4e\u5df2\u7ecf\u6ca1\u6709\u4eb2\u4eba\u4e86\uff0c\u4ed6\u5fc3\u91cc\u8fd9\u79cd\u51f6\u6076\u4e0d\u7965\u7684\u9884\u611f\uff0c\u7a76\u7adf\u8981 \u5e94\u5728\u8c01\u7684\u8eab\u4e0a\uff1f \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u66f4\u60f3\u4e0d\u5230\u5353\u4e1c\u672a\u6b64\u523b\u7684\u5904\u5883\u5c31\u50cf\u662f\u864e\u722a\u4e0b\u7684\u725b\u7f8a\uff0c\u5200\u7827\u4e0a\u7684\u9c7c\u8089\u3002 \u56db \u3000\u3000\u540c\u65e5\uff0c\u540c\u65f6\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u5353\u4e1c\u6765\u786e\u5b9a\u5e94\u8be5\u5df2\u7ecf\u6b7b\u5b9a\u4e86\uff0c\u4ed6\u4e5f\u77e5\u9053\u8427\u6cea\u8840\u6740\u4eba\u4ece\u672a\u5931\u624b\u8fc7\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u201c\u5d29\u201d\u7684\u4e00\u54cd\uff0c\u7bb1\u5b50\u5f00\u4e86\uff0c\u8427\u6cea\u8840\u7ea4\u957f\u7075\u5de7\u800c\u6709\u529b\u7684\u624b\u6307\u5df2\u5f00\u59cb\u52a8\u4f5c\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u7684\u52a8\u4f5c\u4e00\u5f00\u59cb\uff0c\u7bb1\u5b50\u91cc\u5c31\u4f1a\u6709\u67d0\u51e0\u79cd\u94c1\u5668\u5728\u4e00\u77ac\u95f4\u62fc\u6210\u4e00\u4ef6\u81f4\u547d\u7684\u6b66\u5668\uff0c\u4e00\u4ef6\u7edd \u5bf9\u80fd\u514b\u5236\u5353\u4e1c\u6765\u7684\u6b66\u5668\u3002 \u3000\u3000\u53ef\u662f\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u7684\u624b\u6307\u5374\u7a81\u7136\u50f5\u786c\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u4eff\u4f5b\u90fd\u5df2\u50f5\u786c\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\u4e4b\u540e\uff0c\u4ed6\u624d\u62ac\u8d77\u5934\uff0c\u9762\u5bf9\u5353\u4e1c\u6765\uff0c\u4ed6\u7684\u8138\u4e0a\u867d\u7136\u8fd8\u662f\u5168\u65e0\u8868\u60c5\uff0c\u773c\u775b\u91cc\u5374 \u5145\u6ee1\u4e00\u79cd\u5782\u6b7b\u91ce\u517d\u9762\u5bf9\u730e\u4eba\u7684\u6124\u6012\u548c\u60b2\u4f24\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4e5f\u5728\u770b\u7740\u4ed6\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u9762\u5bf9\u9762\u7684\u7ad9\u7740\uff0c\u90fd\u6ca1\u6709\u5f00\u53e3\uff0c\u4e5f\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u56fd\u5916\u7684\u5c0f\u5f84\u4e0a\u5ffd\u7136\u4f20\u4e00\u9635\u811a\u6b65\u58f0\uff0c\u5353\u9752\u5c45\u7136\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u540e\u9762\u8fd8\u8ddf\u7740\u56db\u4e2a\u4eba\uff0c\u4e00\u4e2a\u4eba\u6367\u9152\u5668\uff0c\u4e00\u4e2a\u4eba\u6367\u8863\u5e3d\uff0c\u4e24\u4e2a\u4eba\u62ac\u9996\u5f20\u4e0a\u9762\u94fa\u7740\u7d2b\u8c82\u76ae\u7684 \u7d2b\u6a80\u6728\u6905\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5728\u8c82\u88d8\u91cc\u52a0\u4e0a\u4e00\u5957\u8863\u88e4\uff0c\u7a7f\u4e0a\u889c\u5b50\uff0c\u6234\u4e0a\u76ae\u5e3d\uff0c\u8212\u8212\u670d\u670d\u7684\u5728\u7d2b\u6a80\u6728\u6905\u4e0a\u5750\u4e0b\uff0c \u7528\u7d2b\u6676\u676f\u5012\u4e86\u676f\u8461\u8404\u9152\u559d\u4e0b\u53bb\uff0c\u624d\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u8fd9\u6837\u5b50\u5c31\u6bd4\u8f83\u8212\u670d\u591a\u4e86\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u6ca1\u6709\u542c\u89c1\uff0c\u4e5f\u6ca1\u6709\u770b\u89c1\uff0c\u6240\u6709\u7684\u8fd9\u4e00\u5207\u4e8b\uff0c\u4ed6\u597d\u50cf\u5168\u90fd\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u5982\u679c\u6709\u522b\u7684\u4eba\u770b\u89c1\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u4ee5\u4e3a\u81ea\u5df1\u770b\u5230\u7684\u53ea\u4e0d\u8fc7\u662f\u79cd\u5e7b\u89c9\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u4f1a\u53d1\u751f\u7684\u3002 \u3000\u3000\u9762\u5bf9\u7740\u5929\u4e0b\u6700\u53ef\u6015\u7684\u654c\u4eba\u548c\u6700\u53ef\u6015\u7684\u6b66\u5668\uff0c\u751f\u6b7b\u53ea\u4e0d\u8fc7\u662f\u547c\u5438\u95f4\u7684\u4e8b\uff0c\u4ed6\u5c45\u7136\u8fd8\u8fd9\u4e48\u4ece \u5bb9\u60a0\u95f2\uff0c\u5c45\u7136\u8fd8\u53eb\u4eba\u66ff\u4ed6\u642c\u6905\u5b50\u6362\u8863\u670d\uff0c\u5c45\u7136\u8fd8\u8981\u559d\u9152\u3002 \u3000\u3000\u53ea\u8981\u662f\u4e00\u4e2a\u795e\u667a\u6e05\u9192\u7684\u4eba\uff0c\u5c31\u7edd\u4e0d\u4f1a\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\u3002 \u3000\u3000\u53ef\u662f\u5353\u4e1c\u6765\u5374\u505a\u51fa\u6765\u4e86\u3002 \u3000\u3000\u7bb1\u5b50\u5df2\u7ecf\u5f00\u4e86\uff0c\u8427\u6cea\u8840\u4e5f\u4e0d\u518d\u6709\u4efb\u4f55\u52a8\u4f5c\u3002 \u3000\u3000\u8fd9\u4e2a\u795e\u79d8\u800c\u53ef\u6015\u7684\u4eba\u672c\u6765\u5c31\u50cf\u662f\u6765\u81ea\u5730\u72f1\u7684\u4e0a\u7a7a\u5e7d\u7075\uff0c\u73b0\u5728\u5ffd\u7136\u53c8\u88ab\u51a5\u51a5\u4e2d\u7684\u751f\u5bb0\u5c06\u4ed6 \u7684\u7cbe\u9b42\u53ec\u56de\u53bb\uff0c\u5c06\u4ed6\u53d8\u4f5c\u4e86\u4e00\u4e2a\u4e0a\u53e4\u65f6\u5c31\u5df2\u5316\u77f3\u7684\u5c38\u4f53\u3002 \u3000\u3000\u5353\u4e1c\u6765\u53c8\u5012\u4e86\u676f\u9152\u6d45\u6d45\u555c\u4e86\u4e00\u53e3\uff0c\u624d\u56de\u8fc7\u5934\u53bb\u95ee\u5353\u9752\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff0c\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u4f4d\u8427\u5148\u751f\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u81ea\u5df1\u56de\u7b54\u4e86\u8fd9\u4e2a\u95ee\u9898\uff1a\u201c\u4ed6\u662f\u4e2a\u975e\u5e38\u4e86\u4e0d\u8d77\u7684\u4eba\uff0c\u8fd9\u4e8c\u4e09\u5341\u5e74\u6765\uff0c\u6b7b\u5728\u4ed6\u624b\u4e0b\u7684 \u6c5f\u6e56\u5927\u8c6a\u6b66\u6797\u9ad8\u624b\u6700\u5c11\u4e5f\u6709\u56db\u4e94\u5341\u4f4d\u3002\u201d \u3000\u3000\u5353\u9752\u542c\u7740\u3002 \u3000\u3000\u201c\u4ed6\u624b\u91cc\u6349\u7740\u7684\u8fd9\u53e3\u7bb1\u5b50\uff0c\u636e\u8bf4\u5c31\u662f\u5929\u4e0b\u6700\u53ef\u6015\u7684\u6b66\u5668\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u4e00\u5411\u4e0d\u592a\u8c26 \u865a\uff0c\u53ef\u662f\u6211\u76f8\u4fe1\u53ea\u8981\u4ed6\u4e00\u51fa\u624b\uff0c\u6211\u5c31\u662f\u4e2a\u6b7b\u4eba\u3002\u201d \u3000\u3000\u4ed6\u770b\u7740\u8427\u6cea\u8840\u624b\u91cc\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u201c\u73b0\u5728\u4ed6\u5df2\u7ecf\u628a\u7bb1\u5b50\u6253\u5f00\u4e86\uff0c\u56e0\u4e3a\u4ed6\u672c\u6765\u662f\u60f3\u6740\u4e86\u6211\u7684\uff0c\u5374\u4e00\u76f4\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u51fa\u624b\u3002\u201d \u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u4ed6\u5c45\u7136\u5b81\u53ef\u53d8\u5f97\u50cf\u662f\u4e2a\u5446\u5b50\u4e00\u6837\u7ad9\u5728\u90a3\u91cc\u770b\u6211\u559d\u9152\uff0c\u4e5f\u4e0d\u51fa\u624b\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u6ca1\u6709\u542c\u89c1\u3002 \u3000\u3000\u65e0\u8bba\u5353\u4e1c\u6765\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u90fd\u597d\u50cf\u5b8c\u5168\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u201c\u4ed6\u5f53\u7136\u4e0d\u662f\u4e0d\u6562\u6740\u6211\uff0c\u50cf\u6211\u8fd9\u6837\u7684\u4eba\uff0c\u5728\u8427\u5148\u751f\u773c\u91cc\u4e5f\u8bb8\u8fde\u4e00\u6761\u72d7\u90fd\u6bd4\u4e0d\u4e0a\u3002\u201d\u4ed6\u53c8 \u95ee\u5353\u9752\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u6740\u6211\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u6740\u6211\uff0c\u53ea\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u6ca1\u6cd5\u5b50\u6740\u6211\u4e86\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u73b0\u5728\u4ed6\u552f\u4e00\u80fd\u505a\u7684\u4e8b\uff0c\u5c31\u662f \u7ad9\u5728\u90a3\u91cc\u7b49\u7740\u6211\u53bb\u6740\u4ed6\uff0c\u50cf\u6740\u72d7\u4e00\u6837\u7684\u6740\u3002\u4e5f\u8bb8\u6bd4\u6740\u72d7\u8fd8\u5bb9\u6613\u3002\u201d \u3000\u3000\u8fd9\u79cd\u4e8b\u672c\u6765\u4e5f\u662f\u7edd\u4e0d\u53ef\u80fd\u53d1\u751f\u7684\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6562\u5728\u8427\u6cea\u8840\u9762\u524d\u8fd9\u4e48\u6837\u4fae\u8fb1\u4ed6\uff0c\u5c31\u6b63\u5982\u4ee5\u524d\u4e5f\u6ca1\u6709\u4eba\u6562\u4fae\u8fb1\u5353\u4e1c\u6765\u4e00\u6837\u3002 \u3000\u3000\u201c\u5353\u9752\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u5929\u4e0b\u65e0\u53cc\u7684\u8427\u5148\u751f\u600e\u4e48\u4f1a\u5ffd\u7136\u53d8\u6210\u4e86\u4e00\u6761\u72d7\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u6765\u7684\uff0c\u591a\u5c11\u603b\u8be5\u770b\u51fa\u6765\u4e00\u70b9\u3002\u201d\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u5982\u679c\u4f60\u8fde\u8fd9\u79cd\u4e8b\u90fd \u770b\u4e0d\u51fa\u6765\uff0c\u8981\u6d3b\u5230\u4e8c\u5341\u5c81\u6050\u6015\u90fd\u4e0d\u592a\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u8fd9\u79cd\u4e8b\u6211\u591a\u5c11\u90fd\u5e94\u8be5\u80fd\u770b\u5f97\u51fa\u4e00\u70b9\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8427\u5148\u751f\u6050\u6015\u662f\u88ab\u4eba\u7528\u4e00\u79cd\u5f88\u7279\u522b\u7684\u65b9\u6cd5\u5236\u4f4f\u4e86\uff0c\u5168\u8eab\u7684\u529f\u529b\u6050\u6015\u8fde\u4e00\u5206\u90fd\u4f7f\u4e0d\u51fa \u6765\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d \u3000\u3000\u201c\u8427\u5148\u751f\u672c\u6765\u662f\u4eba\u4e2d\u4e4b\u9f99\uff0c\u5e76\u4e0d\u662f\u72d7\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u53ea\u4e0d\u8fc7\u8427\u5148\u751f\u4e5f\u77e5\u9053\uff0c\u5982\u679c\u9f99\u6b7b \u4e86\uff0c\u5c31\u7b97\u662f\u4e00\u6761\u795e\u9f99\u4e5f\u6bd4\u4e0d\u4e0a\u4e00\u6761\u72d7\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u8fd8\u662f\u90a3\u4e48\u5e73\u9759\uff0c\u56e0\u4e3a\u4ed6\u8bf4\u7684\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u201c\u53ef\u662f\u72d7\u4e5f\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u4f1a\u6b7b\uff0c\u8fdf\u65e9\u603b\u4f1a\u6b7b\uff0c\u53ef\u662f\u81f3\u5c11\u73b0\u5728\u8fd8\u6d3b\u7740\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u4e0d\u7ba1\u662f\u9f99\u662f\u4eba\u662f\u72d7\uff0c\u80fd \u591a\u6d3b\u7247\u523b\u4e5f\u6bd4\u9a6c\u4e0a\u5c31\u6b7b\u4e86\u7684\u597d\u3002\u201d \u3000\u3000\u53ea\u8981\u6d3b\u7740\uff0c\u5c31\u6709\u5e0c\u671b\uff0c\u53ea\u8981\u8fd8\u6709\u4e00\u7ebf\u5e0c\u671b\uff0c\u5c31\u4e0d\u8be5\u653e\u5f03\u3002 \u3000\u3000\u201c\u53ef\u60dc\u73b0\u5728\u6211\u5df2\u7ecf\u770b\u4e0d\u51fa\u4ed6\u8fd8\u6709\u4ec0\u4e48\u5e0c\u671b\u4e86\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u65e0\u8bba\u8c01\u4e2d\u4e86\u2018\u541b\u5b50\u9999\u2019\u7684 \u6bd2\uff0c\u6050\u6015\u90fd\u4e0b\u4f1a\u518d\u6709\u5229\u4e48\u5e0c\u671b\u4e86\u3002\u201d \u3000\u3000\u201c\u541b\u5b50\u9999\uff1f\u201d \u3000\u3000\u201c\u541b\u5b50\u4e4b\u4ea4\u8c08\u5982\u6c34\uff0c\u8c06\u8c06\u541b\u5b50\uff0c\u6e29\u826f\u5982\u7389\uff0c\u541b\u5b50\u9999\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u4e00\u6837\uff1f\u201d \u3000\u3000\u201c\u6c34\u4e00\u6837\u6e05\u6f88\u6d41\u52a8\uff0c\u65e0\u8272\u65e0\u5473\uff0c\u7389\u4e00\u6837\u6e29\u6da6\u67d4\u7f8e\u3002\u201d\u5353\u4e1c\u6765\u7684\u58f0\u97f3\u4e5f\u4e00\u6837\u6e29\u67d4\uff1a\u201c\u552f\u4e00 \u4e0d\u540c\u7684\u662f\uff0c\u541b\u5b50\u9999\u8fd9\u4f4d\u541b\u5b50\uff0c\u5176\u5b9e\u662f\u4e2a\u4f2a\u541b\u5b50\uff0c\u662f\u6709\u6bd2\u7684\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\uff1a\u201c\u5982\u541b\u5b50\u4ea4\uff0c\u5982\u6c90\u6625\u98ce\uff0c\u8fd9\u4f4d\u4f2a\u541b\u5b50\u7684\u6bd2\u4e5f\u597d\u50cf\u6625\u98ce\u4e00\u6837\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95ee\u5c31\u8ba9\u4eba \u9189\u4e86\uff0c\u4e00\u9189\u5c31\u9500\u9b42\u8680\u9aa8\uff0c\u4e07\u52ab\u4e0d\u590d\u3002\u201d \u3000\u3000\u201c\u8427\u5148\u751f\u600e\u4e48\u4f1a\u4e2d\u8fd9\u79cd\u6bd2\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5728\u8427\u5148\u751f\u773c\u4e2d\u53ea\u4e0d\u8fc7\u662f\u6761\u72d7\u800c\u5df2\uff0c\u6bd4\u72d7\u8fd8\u542c\u8bdd\uff0c\u5728\u8427\u5148\u751f\u9762\u524d\uff0c\u6709\u4e9b\u4e8b\u6211\u8fde\u60f3 \u90fd\u4e0d\u6562\u60f3\uff0c\u56e0\u4e3a\u5fc3\u91cc\u4e00\u60f3\uff0c\u795e\u8272\u5c31\u96be\u514d\u4f1a\u6709\u4e9b\u4e0d\u5bf9\u4e86\uff0c\u5c31\u96be\u514d\u4f1a\u88ab\u8427\u5148\u751f\u770b\u51fa\u6765\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u659f\u4e86\u4e00\u676f\u9152\u3002 \u3000\u3000\u201c\u8427\u5148\u751f\u5f53\u7136\u4e5f\u60f3\u4e0d\u5230\u6211\u65e9\u5df2\u628a\u541b\u5b50\u9999\u6446\u5728\u4e00\u4e2a\u6b7b\u4eba\u7684\u8863\u895f\u91cc\uff0c\u53ea\u8981\u8427\u5148\u751f\u8d70\u8fd1\u8fd9\u4f4d\u6b7b \u4eba\uff0c\u52a8\u4e86\u52a8\u8fd9\u4f4d\u6b7b\u4eba\u7684\u8863\u7740\uff0c\u541b\u5b50\u9999\u5c31\u4f1a\u50cf\u6625\u98ce\u822c\u62c2\u8fc7\u4ed6\u7684\u8138\u3002\u201d\u5353\u4e1c\u6765\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u8427\u5148 \u751f\u5f53\u7136\u60f3\u4e0d\u5230\u4e00\u6761\u72d7\u4f1a\u505a\u51fa\u8fd9\u79cd\u4e8b\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u4ee5\u540e\u6211\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u628a\u4e00\u4e2a\u4eba\u5f53\u4f5c\u4e00\u6761\u72d7\u7684\u3002\u201d \u3000\u3000\u8001\u4eba\u5df2\u6b7b\uff0c\u8427\u6cea\u8840\u6700\u60f3\u77e5\u9053\u7684\u4e00\u4ef6\u79d8\u5bc6\u4e5f\u968f\u6b7b\u8005\u800c\u53bb\u3002 \u3000\u3000\u5728\u4ed6\u770b\u5230\u6b7b\u53bb\u7684\u8001\u4eba\u65f6\uff0c\u5f53\u7136\u8981\u53bb\u770b\u4e00\u770b\u8001\u4eba\u662f\u4e0d\u662f\u771f\u7684\u6b7b\u4e86\uff1f\u662f\u600e\u4e48\u6b7b\u7684\uff1f \u3000\u3000\u8981\u67e5\u770b\u4e00\u4e2a\u4eba\u7684\u6b7b\u56e0\uff0c\u5f53\u7136\u96be\u514d\u8981\u4e3b\u52a8\u4ed6\u7684\u8863\u88f3\u3002 \u3000\u3000\u5353\u4e1c\u6765\u65e9\u5df2\u7b97\u51c6\u8427\u6cea\u8840\u53ea\u8981\u6d3b\u7740\u5c31\u4e00\u5b9a\u4f1a\u6765\uff0c\u6240\u4ee5\u65e9\u5c31\u51c6\u5907\u676f\u541b\u5b50\u9999\u3002 \u3000\u3000\u8fd9\u5b9e\u5728\u662f\u4ef6\u5f88\u7b80\u5355\u7684\u4e8b\uff0c\u975e\u5e38\u7b80\u5355\u3002 \u3000\u3000\u7b80\u5355\u5f97\u53ef\u6015\u3002 \u3000\u3000\u5353\u4e1c\u6765\u53c8\u5728\u53f9\u606f\uff1a\u201c\u8fd9\u4f4d\u8001\u4eba\u6d3b\u7740\u65f6\u5e76\u4e0d\u662f\u541b\u5b50\uff0c\u53c8\u6709\u8c01\u80fd\u60f3\u5230\u4ed6\u6b7b\u540e\u53cd\u800c\u6709\u4e86\u541b\u5b50\u4e4b \u9999\uff1f\u201d\u4ed6\u53f9\u606f\u7740\u9053\uff1a\u201c\u6709\u65f6\u5019\u541b\u5b50\u4e5f\u662f\u5f88\u53ef\u6015\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u5e76\u4e0d\u662f\u4ec0\u4e48\u91d1\u7389\u826f\u8a00\uff0c\u66f4\u4e0d\u662f\u4ec0\u4e48\u80fd\u591f\u53d1\u4eba\u6df1\u7701\u7684\u54f2\u7406\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u53ea\u4e0d\u8fc7\u662f\u53e5\u5b9e\u8bdd\u800c\u5df2\u3002 \u4e94 \u3000\u3000\u9ec4\u660f\u65f6\u53f8\u9a6c\u8d85\u7fa4\u5df2\u7ecf\u56de\u5230\u957f\u5b89\u57ce\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u4ed6\u5c45\u4f4f\u5f97\u6700\u4e45\u7684\u5730\u65b9\uff0c\u57ce\u91cc\u5927\u591a\u6570\u8857\u9053\u4ed6\u90fd\u5f88\u719f\u6089\uff0c\u53ef\u662f\u73b0\u5728\u770b\u6765\u5374\u597d\u50cf\u53d8\u4e86\u6837 \u5b50\u3002 \u3000\u3000\u53e4\u8001\u7684\u957f\u5b89\u662f\u4e0d\u4f1a\u53d8\u7684\uff0c\u53d8\u7684\u662f\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u81ea\u5df1\u4e5f\u8bf4\u4e0d\u51fa\u81ea\u5df1\u6709\u4e9b\u4ec0\u4e48\u5730\u65b9\u6539\u53d8\u4e86\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u65f6\u5019\u6539\u53d8\u7684\u3002 \u3000\u3000\u2014\u2014\u662f\u5728\u4ed6\u8e0f\u4e0a\u90a3\u6761\u77f3\u677f\u7f1d\u91cc\u4ecd\u6709\u8840\u8ff9\u7684\u957f\u8857\u65f6\uff1f\u8fd8\u662f\u5728\u4ed6\u542c\u725b\u76ae\u8bf4\u5230\u9489\u978b\u7684\u6d74\u8840\u6218 \u65f6\uff1f \u3000\u3000\u4e00\u4e2a\u4eba\u5982\u679c\u4e00\u5b9a\u8981\u8e29\u7740\u522b\u4eba\u7684\u5c38\u4f53\u624d\u80fd\u5f80\u4e0a\u722c\uff0c\u5c31\u7b97\u722c\u5230\u5dc5\u5cf0\uff0c\u4e5f\u4e0d\u662f\u4ef6\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u4eba\u548c\u9a6c\u90fd\u5df2\u540c\u6837\u75b2\u5026\u3002 \u3000\u3000\u4ed6\u6253\u9a6c\u7ecf\u8fc7\u57ce\u5899\u8fb9\u4e00\u6761\u8352\u50fb\u7684\u8857\u9053\uff0c\u5ffd\u7136\u770b\u5230\u4e86\u4e00\u4e2a\u5f88\u719f\u6089\u7684\u4eba\u7684\u80cc\u5f71\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u8f6c\u5165\u57ce\u5899\u4e0b\u7684\u9634\u5f71\u4e2d\uff0c\u5f88\u5feb\u5c31\u6d88\u5931\u5728\u9ed1\u6697\u91cc\uff0c\u4e00\u76f4\u90fd\u6ca1\u6709\u56de\u8fc7\u5934\u6765\u3002 \u3000\u3000\u53ef\u662f\u53f8\u9a6c\u8d85\u7fa4\u5374\u6709\u628a\u63e1\u53ef\u4ee5\u786e\u5b9a\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u9ad8\u6e10\u98de\u3002 \u3000\u3000\u5728\u4ed6\u8fd8\u6ca1\u6709\u559d\u9189\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u8bb0\u5fc6\u529b\u548c\u773c\u529b\u90e8\u8fdc\u6bd4\u522b\u4eba\u597d\u5f97\u591a\u3002 \u3000\u3000\u2014\u2014\u9ad8\u6e10\u98de\u600e\u4e48\u8fd8\u6ca1\u6709\u6b7b\uff1f\u5353\u4e1c\u6765\u600e\u4e48\u4f1a\u653e\u8fc7\u4ed6\uff1f \u3000\u3000\u2014\u2014\u5927\u9556\u5c40\u548c\u96c4\u72ee\u5802\u7684\u4eba\u662f\u4e0d\u662f\u5df2\u7ecf\u6709\u8fc7\u6b63\u9762\u51b2\u7a81\uff1f \u3000\u3000\u53f8\u9a6c\u8d77\u7fa4\u5f88\u60f3\u8ffd\u8fc7\u53bb\u95ee\u95ee\u9ad8\u6e10\u98de\uff0c\u53ef\u662f\u4ed6\u66f4\u6025\u7740\u8981\u8d76\u56de\u5bb6\u53bb\uff0c\u770b\u770b\u4ed6\u90a3\u79cd\u51f6\u6076\u4e0d\u7965\u7684\u9884 \u611f\u662f\u5426\u5df2\u7075\u9a8c\uff1f \u3000\u3000\u8fd9\u65f6\u5019\u5929\u8272\u5df2\u7ecf\u5f88\u6697\u4e86\u3002\u4ed6\u7684\u5fc3\u60c5\u53c8\u5f88\u6025\u8e81\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u65e0\u8bba\u8c01\u90fd\u96be\u514d\u4f1a\u770b\u9519\u4eba \u7684\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u7684\u4e5f\u8bb8\u5e76\u4e0d\u662f\u9ad8\u6e10\u98de\u3002 \u3000\u3000\u8427\u6cea\u8840\u65e2\u7136\u8fd8\u6ca1\u6709\u6b7b\u5728\u201c\u6cea\u75d5\u201d\u4e0b\uff0c\u9ad8\u6e10\u98de\u5c31\u5df2\u5fc5\u6b7b\u65e0\u7591\u3002 \u3000\u3000\u53ea\u8981\u63a5\u5230\u6740\u4eba\u7684\u5951\u7ea6\uff0c\u8427\u6cea\u8840\u4ece\u672a\u56e0\u4efb\u4f55\u7f18\u6545\u653e\u8fc7\u4efb\u4f55\u4eba\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e5f\u4e0d\u4f1a\u4e3a\u5c0f\u9ad8\u7834\u4f8b\u3002 \u3000\u3000\u5c0f\u9ad8\u53ea\u4e0d\u8fc7\u662f\u4e2a\u4e0d\u8db3\u8f7b\u91cd\u7684\u6c5f\u6e56\u6d6a\u5b50\u800c\u5df2\uff0c\u548c\u4ed6\u6839\u672c\u6ca1\u6709\u4efb\u4f55\u5173\u7cfb\u3002 \u3000\u3000\u5c0f\u76f2\u81ea\u5df1\u4e5f\u60f3\u4e0d\u901a\u8427\u6cea\u8840\u4e3a\u4ec0\u4e48\u6ca1\u6709\u6740\u4ed6\uff0c\u4ed6\u751a\u81f3\u66ff\u8427\u6cea\u8840\u627e\u4e86\u5f88\u591a\u79cd\u7406\u7531\uff0c\u53ef\u662f\u8fde\u4ed6 \u81ea\u5df1\u90fd\u4e0d\u6ee1\u610f\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u627e\u4e0d\u51fa\u4efb\u4f55\u4e00\u79cd\u7406\u7531\u80fd\u89e3\u91ca\u8427\u6cea\u8840\u4e3a\u4ec0\u4e48\u4f1a\u653e\u8fc7\u4ed6\u7684\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\u4ed6\u8fd8\u6d3b\u7740\uff0c\u5b9e\u5728\u662f\u5947\u8ff9\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5e76\u6ca1\u6709\u770b\u9519\uff0c\u521a\u624d\u4ed6\u770b\u89c1\u7684\u90a3\u4e2a\u4eba\u786e\u5b9e\u662f\u9ad8\u6e10\u98de\u3002 \u3000\u3000\u5c0f\u9ad8\u4e5f\u770b\u89c1\u4e86\u5feb\u9a6c\u98de\u9a70\u800c\u8fc7\u7684\u53f8\u9a6c\u8d85\u7fa4\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6545\u610f\u907f\u5f00\u4e86\uff0c\u56e0\u4e3a\u9664\u4e86\u6731\u731b\u5916\uff0c\u6682\u65f6\u4ed6\u4e0d\u60f3\u89c1\u5230\u4efb\u4f55\u4eba\u3002 \u3000\u3000\u4ed6\u5728\u627e\u6731\u731b\uff0c\u627e\u904d\u4e86\u957f\u5b89\u57ce\u91cc\u6bcf\u4e00\u4e2a\u9634\u6697\u7684\u89d2\u843d\u3002 \u3000\u3000\u73b0\u5728\u6b63\u662f\u6731\u731b\u6700\u9700\u8981\u670b\u53cb\u7684\u65f6\u5019\uff0c\u4e0d\u7ba1\u6731\u731b\u662f\u4e0d\u662f\u8fd8\u628a\u4ed6\u5f53\u4f5c\u670b\u53cb\uff0c\u65e0\u8bba\u5982\u4f55\u4ed6\u90fd\u4e0d\u80fd \u5c31\u8fd9\u4e48\u6837\u5f03\u6731\u731b\u800c\u53bb\u3002 \u3000\u3000\u2014\u2014\u5982\u679c\u73b0\u5728\u6731\u731b\u8fd8\u5728\u966a\u7740\u8776\u821e\uff0c\u770b\u5230\u4ed6\u7684\u65f6\u5019\u4f1a\u5bf9\u4ed6\u600e\u4e48\u6837\uff1f \u3000\u3000\u5c0f\u9ad8\u4e5f\u5df2\u60f3\u8c61\u5230\u8fd9\u79cd\u96be\u582a\u7684\u60c5\u51b5\uff0c\u4f46\u662f\u4ed6\u5df2\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u6709\u8db3\u591f\u7684\u52c7\u6c14\u53bb\u9762\u5bf9\u4e00\u5207\u3002 \u3000\u3000\u5929\u8272\u66f4\u6697\u4e86\u3002 \u3000\u3000\u957f\u5b89\u53e4\u57ce\u7684\u9634\u5f71\u6c89\u91cd\u7684\u538b\u5728\u5c0f\u9ad8\u8eab\u4e0a\uff0c\u4ed6\u7684\u5fc3\u60c5\u4e5f\u540c\u6837\u6c89\u91cd\u3002 \u3000\u3000\u2014\u2014\u6731\u731b\u662f\u6761\u597d\u6c49\uff0c\u80f8\u895f\u5f00\u9614\u3001\u91cd\u60c5\u91cd\u4e49\u7684\u597d\u6c49\u3002 \u3000\u3000\u2014\u2014\u6731\u731b\u5e94\u8be5\u4e86\u89e3\u4ed6\u7684\u82e6\u8877\uff0c\u5e94\u8be5\u80fd\u539f\u8c05\u4ed6\u7684\u3002 \u3000\u3000\u53ef\u662f\u8776\u821e\u5462\uff1f \u3000\u3000\u5c0f\u9ad8\u63e1\u7d27\u53cc\u62f3\uff0c\u5927\u6b65\u5f80\u524d\u8d70\uff0c\u5ffd\u7136\u95f4\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u4e00\u67c4\u96ea\u4eae\u7684\u5927\u5200\u4ece\u9ed1\u6697\u4e2d\u8fce\u9762\u5288\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u5288\u4e0b\u6765\u65f6\uff0c\u65e0\u7591\u5df2\u4e0b\u4e86\u51b3\u5fc3\u8981\u628a\u4ed6\u7684\u5934\u9885\u5288\u6210\u4e24\u534a\u3002 \u3000\u3000\u4f46\u662f\u65e0\u8bba\u8c01\u8981\u4e00\u5200\u628a\u9ad8\u6e10\u98de\u5288\u6210\u4e24\u534a\u90fd\u7edd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u91cc\u8fd8\u6709\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u5e76\u4e0d\u592a\u5feb\uff0c\u7528\u7684\u4e5f\u4e0d\u662f\u4ec0\u4e48\u60ca\u4eba\u7684\u5200\u6cd5\u3002\u4ed6\u672c\u6765\u5f88\u8f7b\u6613\u7684\u5c31\u53ef\u4ee5\u62d4\u5251\u53cd\u51fb\uff0c\u628a\u8fd9 \u4e2a\u8eb2\u5728\u9634\u5f71\u4e2d\u6697\u7b97\u4ed6\u7684\u4eba\u523a\u6740\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u62d4\u5251\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u77ac\u95f4\uff0c\u770b\u5230\u4e86\u8fd9\u4e2a\u4eba\u5934\u7f20\u7684\u767d\u5dfe\uff0c\u4e5f\u770b\u5230\u4e86\u8fd9\u4e2a\u4eba\u7684\u8138\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u53eb\u86ee\u725b\uff0c\u662f\u96c4\u72ee\u5802\u5c5e\u4e0b\u6700\u6709\u79cd\u7684\u597d\u6c49\u4e4b\u4e00\uff0c\u4e5f\u662f\u6731\u731b\u8fd9\u6b21\u5e26\u5230\u957f\u5b89\u6765\u7684\u516b\u5341\u516d\u4f4d \u6b7b\u58eb\u4e4b\u4e00\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u672c\u6765\u8ddf\u4ed6\u7d20\u4e0d\u76f8\u8bc6\uff0c\u73b0\u5728\u5374\u5df2\u5168\u90fd\u662f\u4ed6\u7684\u597d\u5144\u5f1f\uff0c\u8ddf\u4ed6\u540c\u751f\u6b7b\u5171\u60a3\u96be\u7684\u597d\u5144\u5f1f\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u4e00\u5b9a\u662f\u780d\u9519\u4e86\u4eba\u3002 \u3000\u3000\u201c\u6211\u662f\u5c0f\u9ad8\uff0c\u9ad8\u6e10\u98de\u3002\u201d \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u4e00\u95ea\uff0c\u5200\u5c31\u5288\u7a7a\u4e86\uff0c\u5200\u950b\u780d\u5728\u5730\u4e0a\uff0c\u706b\u661f\u56db\u6e85\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u6709\u53cc\u8840\u7ea2\u7684\u773c\u775b\u5728\u77aa\u7740\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u662f\u5c0f\u9ad8\uff0c\u4ffa\u77e5\u9053\u4f60\u662f\u5c0f\u9ad8\u3002\u201d\u86ee\u725b\u5ffd\u7136\u5927\u543c\uff1a\u201c\u4ffa\u64cd\u4f60\u4e2a\u5a18\u3002\u201d \u3000\u3000\u543c\u58f0\u4e2d\uff0c\u53c8\u6709\u5200\u780d\u5df2\u9664\u4e86\u86ee\u725b\u7684\u5200\uff0c\u8fd8\u6709\u53e6\u5916\u51e0\u628a\u5200\u3002 \u3000\u3000\u51e0\u628a\u5200\u90fd\u4e0d\u662f\u597d\u5200\uff0c\u7528\u5200\u7684\u4eba\u4e5f\u4e0d\u662f\u597d\u624b\uff0c\u53ef\u662f\u6bcf\u4e00\u5200\u90fd\u5145\u6ee1\u4e86\u4ec7\u6068\u548c\u6124\u6012\uff0c\u6bcf\u4e2a\u4eba\u90fd \u662f\u62fc\u4e86\u547d\u6765\u7684\u3002 \u3000\u3000\u5c0f\u9ad8\u4e0d\u6015\u6b7b\u3002 \u3000\u3000\u5c0f\u9ad8\u4e0d\u80fd\u7528\u4ed6\u90a3\u79cd\u6bcf\u4e00\u5251\u90fd\u80fd\u5728\u77ac\u95f4\u53d6\u4eba\u54bd\u5589\u7684\u5251\u6cd5\uff0c\u6765\u5bf9\u4ed8\u8fd9\u73ed\u5144\u5f1f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u6837\u6b7b\u5728\u4e71\u5200\u4e0b\u3002 \u3000\u3000\u5b9d\u5251\u867d\u7136\u672a\u51fa\u9798\uff0c\u5251\u9798\u6325\u6253\u70b9\u51fb\u95f4\uff0c\u5200\u5df2\u843d\u5730\uff0c\u63e1\u5200\u7684\u624b\u5df2\u62ac\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u63e1\u5200\u7684\u4eba\u5374\u6ca1\u6709\u8fdf\u4e0b\u53bb\uff0c\u6bcf\u4e00\u53cc\u773c\u775b\u91cc\u90fd\u5145\u6ee1\u6028\u6bd2\u6124\u6012\u548c\u4ec7\u6068\u3002 \u3000\u3000\u201c\u597d\uff0c\u59d3\u9ad8\u7684\uff0c\u7b97\u4f60\u6709\u672c\u4e8b\uff0c\u201d\u86ee\u725b\u5636\u58f0\u9053\uff1a\u201c\u4f60\u6709\u79cd\u5c31\u628a\u8001\u5b50\u4eec\u5168\u5bb0\u4e86\uff0c\u82e5\u5269\u4e0b\u4e00\u4e2a \u4f60\u5c31\u662f\u72d7\u517b\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u61c2\u4f60\u4eec\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u5c0f\u9ad8\u4e5f\u751f\u6c14\u4e86\uff0c\u6c14\u5f97\u53d1\u6296\uff1a\u201c\u6211\u771f\u7684\u4e0d\u61c2\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u61c2\uff1f\u4ffa\u64cd\u4f60\u7956\u5b97\uff0c\u4f60\u4e0d\u61c2\u8c01\u61c2\uff1f\u201d\u86ee\u725b\u6012\u543c\uff1a\u201c\u8001\u5b50\u4eec\u628a\u4f60\u5f53\u4eba\uff0c\u8c01\u77e5\u9053\u4f60\u662f\u4e2a \u755c\u7272\uff0c\u8001\u5b50\u4eec\u5728\u62fc\u547d\u7684\u65f6\u5019\uff0c\u4f60\u8fd9\u4e2a\u755c\u7272\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u662f\u4e0d\u662f\u53c8\u53bb\u5077\u522b\u4eba\u7684\u8001\u5a46\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u660e\u767d\u4f60\u4eec\u7684\u610f\u601d\u4e86\uff0c\u53ef\u662f\u4f60\u4eec\u4e0d\u4f1a\u660e\u767d\u7684\u3002\u201d\u4ed6\u9eef\u7136\u7684\u8bf4\uff1a\u201c\u6709\u4e9b\u4e8b\u4f60\u4eec\u6c38\u8fdc \u90fd\u4e0b\u4f1a\u660e\u767d\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u60f3\u8981\u4f60\u4eec\u5e26\u6211\u53bb\u89c1\u6731\u731b\u3002\u201d \u3000\u3000\u201c\u4f60\u771f\u4ed6\u5a18\u7684\u4e0d\u8981\u8138\uff0c\u201d\u86ee\u725b\u8df3\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u8fd8\u6709\u8138\u4f1a\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u4e00\u5b9a\u8981\u53bb\u89c1\u4ed6\u3002\u201d\u5c0f\u9ad8\u6c89\u4f4f\u6c14\uff1a\u201c\u4f60\u4eec\u975e\u5e26\u6211\u53bb\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u8001\u5b50\u5e26\u4f60\u53bb\uff01\u201d \u3000\u3000\u53e6\u5916\u4e00\u6761\u5927\u6c49\u4e5f\u8df3\u8d77\u6765\uff0c\u4e00\u5934\u5f80\u57ce\u5899\u4e0a\u649e\u4e86\u8fc7\u53bb\uff0c\u4ed6\u7684\u4e00\u9897\u5927\u597d\u5927\u9885\u7acb\u523b\u5c31\u53d8\u5f97\u597d\u50cf\u662f \u4e2a\u7efd\u7834\u4e86\u7684\u77f3\u69b4\u3002 \u3000\u3000\u70ed\u8840\u98de\u6e85\uff0c\u5c0f\u9ad8\u7684\u5fc3\u5374\u51b7\u4e86\u3002\u86ee\u725b\u53c8\u5927\u543c\uff1a \u3000\u3000\u201c\u4f60\u8fd8\u8981\u89c1\u4ed6\uff0c\u662f\u4e0d\u662f\u8981\u6c14\u6b7b\u4ed6\uff0c\u597d\uff0c\u4ffa\u4e5f\u53ef\u4ee5\u5e26\u4f60\u53bb\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u4e00\u5934\u4f4f\u57ce\u5899\u4e0a\u649e\u8fc7\u53bb\uff0c\u53ef\u662f\u8fd9\u6b21\u5c0f\u9ad8\u5df2\u7ecf\u6709\u4e86\u75db\u82e6\u7684\u7ecf\u9a8c\uff0c\u4e00\u628a\u62c9\u4f4f\u4e86\u4ed6\uff0c\u628a\u4ed6\u63bc \u5728\u5730\u4e0a\uff0c\u7136\u540e\u5c31\u5934\u4e5f\u4e0d\u56de\u7684\u8d70\u4e86\u3002\u970e\u773c\u95f4\u4eba\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u4ed6\u7684\u6cea\u5df2\u7ecf\u6eb6\u5165\u4ed6\u7684\u8840\u3002 \u3000\u3000\u82f1\u96c4\u65e0\u6cea\uff0c\u5316\u4e3a\u78a7\u8840\u3002 \u3000\u3000\u9752\u950b\u8fc7\u5904\uff0c\u662f\u6cea\u662f\u8840\uff1f \u7b2c\u5341\u4e94\u7ae0\u3000\u5dc5\u5cf0>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u5341\u4e94\u7ae0\u3000\u5dc5\u5cf0 \u4e00 \u3000\u3000\u4e8c\u6708\u4e8c\u5341\u4e94\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u6709\u706f\u3002 \u3000\u3000\u6de1\u7d2b\u8272\u7684\u6c34\u6676\u706f\u7f69\uff0c\u9ec4\u91d1\u706f\uff0c\u706f\u4e0b\u6709\u4e00\u53e3\u7bb1\u5b50\uff0c\u4e00\u53e3\u9648\u65e7\u5e73\u51e1\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u706f\u4e0b\u4e5f\u6709\u4eba\uff0c\u5374\u4e0d\u662f\u90a3\u4e2a\u6c89\u9ed8\u5e73\u51e1\u63d0\u7740\u8fd9\u53e3\u7bb1\u5b50\u7684\u4eba\u3002 \u3000\u3000\u706f\u4e0b\u7684\u4eba\u662f\u5353\u4e1c\u6765\u3002 \u3000\u3000\u5929\u8fd8\u6ca1\u6709\u4eae\uff0c\u6240\u4ee5\u706f\u662f\u71c3\u7740\u7684\uff0c\u706f\u5149\u6b63\u597d\u7167\u5728\u4ed6\u770b\u8d77\u6765\u6bd4\u8f83\u67d4\u548c\u7684\u5de6\u9762\u534a\u8fb9\u8138\u4e0a\u3002 \u3000\u3000\u4eca\u5929\u4ed6\u8fd9\u534a\u8fb9\u8138\u770b\u6765\u7b80\u76f4\u5c31\u50cf\u662f\u4ec1\u6148\u7684\u7236\u4eb2\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5728\u5bf9\u81ea\u5df1\u5fc3\u6ee1\u610f\u8db3\u7684\u65f6\u5019\uff0c\u5bf9\u522b\u4eba\u4e5f\u4f1a\u6bd4\u8f83\u4ec1\u6148\u4e9b\u7684\u3002 \u3000\u3000\u73b0\u5728\u6731\u731b\u5df2\u7ecf\u5728\u4ed6\u638c\u63e1\u4e2d\uff0c\u96c4\u72ee\u5802\u5df2\u5b8c\u5168\u74e6\u89e3\u5d29\u6e83\uff0c\u9ad8\u6e10\u98de\u4e5f\u5df2\u6b7b\u4e86\u3002\u81f3\u5c11\uff0c\u4ed6\u8ba4\u4e3a\u9ad8 \u6e10\u98de\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6bcf\u4e00\u4ef6\u4e8b\u90fd\u5df2\u5b8c\u5168\u5728\u4ed6\u7684\u63a7\u5236\u4e0b\u3002 \u3000\u3000\u5f3a\u654c\u5df2\u9664\uff0c\u5927\u6743\u5728\u63e1\uff0c\u6c5f\u6e56\u4e2d\u518d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4eba\u80fd\u548c\u4ed6\u4e00\u4e89\u957f\u77ed\uff0c\u8fd9\u79cd\u60c5\u51b5\u5c31\u7b97\u6700\u4e0d\u77e5\u8db3 \u7684\u4eba\u4e5f\u4e0d\u80fd\u4e0d\u6ee1\u610f\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u4e00\u751f\u4e8b\u4e1a\uff0c\u65e0\u7591\u5df2\u5230\u8fbe\u5dc5\u5cf0\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u6ca1\u6709\u6740\u8427\u6cea\u8840\u3002 \u3000\u3000\u73b0\u5728\u8427\u6cea\u8840\u7684\u60c5\u51b5\u51e0\u4e4e\u5df2\u7ecf\u548c\u90a3\u8001\u4eba\u5b8c\u5168\u4e00\u6837\uff0c\u529f\u529b\u5df2\u5b8c\u5168\u6d88\u5931\uff0c\u4e5f\u88ab\u5353\u4e1c\u6765\u5b89\u6392\u5728\u90a3 \u4e2a\u5e7d\u9759\u7684\u5c0f\u9662\u91cc\uff0c\u7b49\u7740\u5353\u4e1c\u6765\u53bb\u69a8\u53d6\u4ed6\u8111\u4e2d\u7684\u667a\u6167\u548c\u4ed6\u90a3\u4e00\u7b14\u79d8\u5bc6\u7684\u8d22\u5bcc\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u90fd\u53ef\u4ee5\u7b49\u5230\u4ee5\u540e\u6162\u6162\u53bb\u505a\uff0c\u5353\u4e1c\u6765\u4e00\u70b9\u4e5f\u4e0d\u7740\u6025\u3002 \u3000\u3000\u4e00\u4e2a\u529f\u529b\u5df2\u5b8c\u5168\u6d88\u5931\u4e86\u7684\u6740\u4eba\u8005\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u65e0\u4eba\u7406\u776c\u7684\u5782\u66ae\u5993\u5973\uff0c\u662f\u6ca1\u6709\u4ec0\u4e48\u8def\u53ef\u4ee5 \u8d70\u7684\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5730\u65b9\u53ef\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u505a\u7684\u884c\u4e1a\u90fd\u662f\u4eba\u7c7b\u6700\u53e4\u8001\u7684\u884c\u4e1a\uff0c\u4ed6\u4eec\u7684\u60b2\u54c0\u4e5f\u662f\u4eba\u7c7b\u6700\u53e4\u8001\u7684\u60b2\u5267\u3002 \u3000\u3000\u8427\u6cea\u8840\u7684\u7bb1\u5b50\u73b0\u5728\u4e5f\u5df2\u843d\u5165\u5353\u4e1c\u6765\u624b\u91cc\u4e86\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u8fd9\u53e3\u7bb1\u5b50\u662f\u4e16\u4e0a\u6700\u795e\u79d8\u6700\u53ef\u6015\u7684\u6b66\u5668\uff0c\u5728\u96c4\u72ee\u5802\u7684\u53db\u5f92\u6768\u575a\u88ab\u523a\u6740\u7684\u90a3\u4e00\u5929\uff0c \u4ed6\u5df2\u7ecf\u77e5\u9053\u8fd9\u4ef6\u6b66\u5668\u7684\u53ef\u6015\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u6c5f\u6e56\u4e2d\u4e00\u5b9a\u6709\u5f88\u591a\u4eba\u613f\u610f\u51fa\u5356\u81ea\u5df1\u7684\u7075\u9b42\u6765\u6362\u53d6\u8fd9\u4ef6\u6b66\u5668\u3002 \u3000\u3000\u5e78\u597d\u4ed6\u4e0d\u662f\u90a3\u4e9b\u4eba\uff0c\u4ed6\u548c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5176\u4ed6\u90a3\u4e9b\u4eba\u90fd\u662f\u5b8c\u5168\u4e0d\u540c\u7684\u3002 \u3000\u3000\u73b0\u5728\u7bb1\u5b50\u5c31\u6446\u5728\u4ed6\u9762\u524d\uff0c\u4ed6\u8fde\u52a8\u90fd\u61d2\u5f97\u53bb\u52a8\u5b83\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6709\u53e6\u4e00\u79cd\u66f4\u53ef\u6015\u7684\u6b66\u5668\uff0c\u4ed6\u7684\u667a\u6167\u5c31\u662f\u4ed6\u7684\u6b66\u5668\u3002 \u3000\u3000\u4ed6\u8fd0\u7528\u4ed6\u7684\u667a\u6167\u65f6\uff0c\u8fdc\u6bd4\u4e16\u4e0a\u4efb\u4f55\u4eba\u4f7f\u7528\u4efb\u4f55\u6b66\u5668\u90fd\u53ef\u6015\u3002 \u3000\u3000\u2014\u2014\u8427\u6cea\u8840\u867d\u7136\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u9ad8\u624b\uff0c\u53ef\u662f\u5728\u4ed6\u9762\u524d\u8fde\u51fa\u624b\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u2014\u2014\u6731\u731b\u867d\u7136\u52c7\u731b\u9aa0\u608d\uff0c\u96c4\u72ee\u5802\u867d\u7136\u52bf\u529b\u5f3a\u5927\uff0c\u53ef\u4ed6\u8fd8\u662f\u5728\u4e3e\u624b\u95f4\u5c31\u628a\u4ed6\u4eec\u51fb\u6e83\u4e86\u3002 \u3000\u3000\u4ed6\u80fd\u505a\u5230\u8fd9\u4e9b\u4e8b\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u80fd\u628a\u63e1\u7740\u6bcf\u4e00\u4e2a\u673a\u4f1a\uff0c\u8fd8\u80fd\u5236\u9020\u673a\u4f1a\u3002 \u3000\u3000\u5728\u522b\u4eba\u8ba4\u4e3a\u4ed6\u5df2\u5931\u8d25\u4e86\u7684\u65f6\u5019\uff0c\u5728\u6700\u5371\u6025\u7684\u60c5\u51b5\u4e0b\uff0c\u4ed6\u975e\u4f46\u4e0d\u4f1a\u5fc3\u614c\u610f\u4e71\uff0c\u53cd\u800c\u9002\u65f6\u5236 \u9020\u826f\u673a\u51fb\u6e83\u5f3a\u654c\uff0c\u53cd\u8d25\u4e3a\u80dc\u3002 \u3000\u3000\u53ea\u6709\u8fd9\u79cd\u4eba\uff0c\u624d\u662f\u771f\u6b63\u7684\u5f3a\u8005\u3002 \u3000\u3000\u957f\u67aa\u5927\u65a7\u94a2\u5200\u5b9d\u5251\u90fd\u53ea\u4e0d\u8fc7\u662f\u5339\u592b\u7684\u5229\u5668\u800c\u5df2\uff0c\u751a\u81f3\u8fde\u8fd9\u53e3\u7bb1\u5b50\u90fd\u4e00\u6837\u3002 \u3000\u3000\u5353\u9752\u5df2\u7ecf\u7ad9\u5728\u4ed6\u9762\u524d\u7b49\u4e86\u8bb8\u4e45\uff0c\u80dc\u5229\u7684\u6ecb\u5473\u5c31\u50cf\u662f\u6a44\u6984\u4e00\u6837\uff0c\u8981\u7ec6\u7ec6\u963b\u56bc\u624d\u80fd\u4eab\u53d7\u5230\u5b83 \u7684\u7518\u7f8e\uff0c\u6240\u4ee5\u5353\u9752\u5df2\u7ecf\u51c6\u5907\u6084\u6084\u7684\u9000\u51fa\u53bb\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5374\u5ffd\u7136\u53eb\u4f4f\u4e86\u4ed6\uff0c\u7528\u4e00\u79cd\u5f88\u6e29\u548c\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u4f60\u4e5f\u8f9b\u82e6\u4e86\u4e00\u4e2a\u665a\u4e0a\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d \u5750\u4e0b\u672a\u559d\u676f\u9152\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u559d\u9152\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u5b66\u3002\u201d\u5353\u4e1c\u6765\u5fae\u7b11\uff1a\u201c\u8981\u5b66\u559d\u9152\u5e76\u4e0d\u662f\u4ef6\u5f88\u56f0\u96be\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u73b0\u5728\u8fd8\u4e0d\u5230\u6211\u8981\u5b66\u559d\u9152\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u201c\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\u4f60\u624d\u5f00\u59cb\u5b66\uff1f\u201d\u5353\u4e1c\u6765\u7684\u7b11\u5bb9\u5df2\u9690\u6ca1\u5728\u9634\u5f71\u91cc\uff0c\u201c\u662f\u4e0d\u662f\u8981\u7b49\u5230\u4f60\u80fd \u591f\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5ffd\u7136\u6539\u53d8\u4e86\u8bdd\u9898\u95ee\u5353\u9752\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u628a\u8427\u5148\u751f\u5b89\u987f\u597d\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u8d70\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u60c5\u51b5\u5982\u4f55\uff1f\u6709\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u5353\u9752\u9053\uff1a\u201c\u4ed6\u8fd8\u662f\u548c\u521a\u624d\u4e00\u6837\uff0c\u597d\u50cf\u5bf9\u4efb\u4f55\u4e8b\u90fd\u5df2\u7ecf\u5b8c\u5168\u4e0d\u5728\u4e4e\u4e86\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\u3002\u201d\u5353\u4e1c\u6765\u53c8\u9732\u51fa\u5fae\u7b11\uff1a\u201c\u80fd\u591f\u542c\u5929\u7531\u547d\uff0c\u5c3d\u91cf\u4f7f\u81ea\u5df1\u5b89\u4e8e\u73b0\u51b5\u7684\u4eba\uff0c\u624d\u662f\u771f\u6b63 \u7684\u806a\u660e\u4eba\uff0c\u8fd9\u79cd\u4eba\u624d\u80fd\u6d3b\u5f97\u957f\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u5fae\u7b11\u4e2d\u4eff\u4f5b\u4e5f\u6709\u79cd\u5c16\u9510\u5982\u9525\u7684\u601d\u60f3\uff1a\u201c\u6709\u65f6\u5019\u6211\u89c9\u5f97\u4ed6\u6709\u5f88\u591a\u5730\u65b9\u90fd\u8ddf\u6211\u4e00 \u6837\uff0c\u81ea\u5df1\u505a\u4e0d\u5230\u7684\u4e8b\uff0c\u4ed6\u975e\u4f46\u4e0d\u4f1a\u53bb\u505a\uff0c\u8fde\u60f3\u90fd\u4e0d\u4f1a\u53bb\u60f3\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u7684\u63a5\u7740\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5982\u679c\u603b\u559c\u6b22\u53bb\u505a\u4e00\u4e9b\u81ea\u5df1\u505a\u4e0d\u5230\u7684\u4e8b\u3002\u5c31\u96be\u514d\u4f1a\u6b7b\u4e8e\u975e\u547d\uff0c \u9ad8\u6e10\u98de\u5c31\u662f\u4e2a\u5f88\u597d\u7684\u4f8b\u5b50\u3002\u201d \u3000\u3000\u5353\u9752\u5ffd\u7136\u8bf4\uff1a\u201c\u9ad8\u6e10\u98de\u4e0d\u662f\u4e2a\u5f88\u597d\u7684\u4f8b\u5b50\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u662f\uff1f\u201d\u5353\u4e1c\u6765\u95f4\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u90d1\u8bda\u5728\u6628\u5929\u9ec4\u660f\u65f6\u8fd8\u4eb2\u773c\u770b\u89c1\u4ed6\u63d0\u770b\u5230\u51fa\u57ce\u53bb\u3002\u201d \u3000\u3000\u201c\u90d1\u8bda\uff1f\u201d\u5353\u4e1c\u6765\u4eff\u4f5b\u5728\u8bb0\u5fc6\u4e2d\u641c\u7d22\u8fd9\u4e2a\u540d\u5b57\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u771f\u7684\u770b\u89c1\u4e86\u9ad8\u6e10\u98de\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e00\u53d1\u73b0\u9ad8\u6e10\u98de\u7684\u884c\u8e2a\uff0c\u5c31\u7acb\u523b\u8d76\u56de\u6765\u544a\u8bc9\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u76f8\u4fe1\u4ed6\u7684\u8bdd\uff1f\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u7b11\u5bb9\u53c8\u9690\u6ca1\uff0c\u58f0\u97f3\u5374\u66f4\u6e29\u548c\uff0c\u201c\u5bf9\uff01\u4f60\u5e94\u8be5\u76f8\u4fe1\u4ed6\u3002\u5982\u679c\u4f60\u60f3\u8981\u522b\u4eba\u4fe1\u4efb\u4f60\uff0c \u5c31\u4e00\u5b9a\u8981\u5148\u8ba9\u4ed6\u77e5\u9053\u4f60\u5f88\u4fe1\u4efb\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u597d\u50cf\u5ffd\u7136\u53d1\u89c9\u8fd9\u53e5\u8bdd\u662f\u4e0d\u8be5\u8bf4\u7684\uff0c\u7acb\u523b\u53c8\u6539\u53d8\u8bdd\u9898\u95ee\u5353\u9752\u3002 \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u9ad8\u6e10\u98de\u4f1a\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u4ed6\u4e00\u5b9a\u662f\u5230\u7ea2\u82b1\u96c6\u90a3\u5993\u9662\u53bb\u627e\u6731\u731b\u4e86\u3002\u201d\u5353\u9752\u8bf4\uff1a\u201c\u6731\u731b\u65e2\u7136\u4e0d\u5728\u90a3\u91cc\uff0c\u9ad8\u6e10\u98de \u4e00\u5b9a\u8fd8\u4f1a\u56de\u53bb\u627e\u7684\uff0c\u6240\u4ee5\u6211\u5e76\u6ca1\u6709\u53eb\u90d1\u8bda\u53bb\u76ef\u4ed6\uff0c\u53ea\u8981\u4ed6\u5728\u957f\u5b89\uff0c\u5c31\u5728\u6211\u4eec\u7684\u638c\u63e1\u4e2d\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u7b11\u4e86\uff0c\u7b11\u5f97\u66f4\u6109\u5feb\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u5df2\u7ecf\u53ef\u4ee5\u5f00\u59cb\u5b66\u559d\u9152\u4e86\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4f60\u5df2\u7ecf\u6709\u8d44\u683c\u559d\u9152\u3002\u800c\u4e14\u6bd4\u5927\u591a\u6570\u4eba \u90fd\u6709\u8d44\u683c\u559d\u9152\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u5c06\u4ed6\u4e00\u76f4\u62ff\u7740\u7684\u4e00\u676f\u9152\u9001\u5230\u5353\u9752\u9762\u524d\u3002 \u3000\u3000\u5353\u9752\u7acb\u523b\u63a5\u8fc7\u53bb\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u9152\u7518\u751c\uff0c\u53ef\u662f\u4ed6\u5634\u91cc\u5374\u53c8\u9178\u53c8\u82e6\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u53d1\u73b0\u81ea\u5df1\u8bdd\u8bf4\u5f97\u5927\u591a\uff0c\u5982\u679c\u80fd\u628a\u4ed6\u521a\u624d\u8bf4\u7684\u6d3b\u5168\u90e8\u6536\u56de\u53bb\u3002\u4ed6\u60c5\u613f\u780d\u65ad\u81ea\u5df1\u4e00\u53ea \u624b\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5374\u597d\u50cf\u5b8c\u5168\u6ca1\u6709\u89c9\u5bdf\u5230\u4ed6\u7684\u53cd\u5e94\uff0c\u63a5\u8fc7\u4ed6\u7684\u7a7a\u676f\uff0c\u53c8\u5012\u4e86\u676f\u9152\uff0c\u5750\u4e0b\u53bb\u6d45\u555c\u4e00 \u53e3\u3002 \u3000\u3000\u201c\u8427\u6cea\u8840\u660e\u660e\u77e5\u9053\u9ad8\u6e10\u98de\u662f\u4ed6\u5bbf\u547d\u4e2d\u7684\u707e\u7978\uff0c\u8427\u6cea\u8840\u8fd9\u4e00\u751f\u4ece\u672a\u6094\u7ea6\u8fc7\u4e00\u6b21\uff0c\u73b0\u5728\u4ed6\u5df2 \u63a5\u5230\u4e86\u5951\u7ea6\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u6740\u9ad8\u6e10\u98de\uff1f\u5353\u4e1c\u6765\u9677\u5165\u6c89\u601d\uff1a\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4eec\u4e4b\u95f4\u6709\u4ec0\u4e48\u7279\u522b\u7684 \u5173\u7cfb\uff1f\u90a3\u7a76\u7adf\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e5f\u5c06\u676f\u4e2d\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u773c\u775b\u91cc\u5ffd\u7136\u53d1\u51fa\u4e86\u5149\uff1a\u201c\u4ed6\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u4e00\u5b9a\u53ea\u6709\u90a3\u4e2a \u8001\u4eba\u624d\u80fd\u786e\u5b9a\u3002\u8427\u6cea\u8840\u8981\u95ee\u8001\u4eba\u7684\uff0c\u4e00\u5b9a\u5c31\u662f\u8fd9\u4ef6\u4e8b\uff0c\u8fd9\u4ef6\u4e8b\u5bf9\u4ed6\u4e00\u5b9a\u5f88\u91cd\u8981\uff0c\u6240\u4ee5\u8001\u4eba\u4e00 \u6b7b\uff0c\u4ed6\u5c31\u52a8\u4e86\u6740\u673a\uff0c\u56e0\u4e3a\u8001\u4eba\u6b7b\u540e\u4e16\u4e0a\u5c31\u518d\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u9ad8\u6e10\u98de\u7a76\u7adf\u662f\u4e0d\u662f\u4ed6\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u201c\u4ed6\u7684\u513f\u5b50\uff1f\u201d \u3000\u3000\u5353\u9752\u672c\u6765\u5df2\u51b3\u5fc3\u4e0d\u5f00\u53e3\u7684\uff0c\u6b64\u523b\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u5927\u58f0\u95ee\uff1a\u201c\u9ad8\u6e10\u98de\u600e\u4e48\u4f1a\u662f\u8427\u6cea\u8840\u7684\u513f \u5b50\uff1f\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u4e0d\u53ef\u80fd\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u7b11\uff1a\u201c\u9ad8\u6e10\u98de\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5fae\u4e0d\u8db3\u9053\u7684\u5e74\u8f7b\u4eba\u800c\u5df2\uff0c\u4e00\u5411\u51b7\u9177\u65e0\u60c5\u7684\u8386\u6cea\u8840\u4e3a\u4ec0 \u4e48\u8981\u6551\u4ed6\uff1f\u5982\u679c\u4ed6\u4eec\u4e4b\u95f4\u6839\u672c\u4e0d\u53ef\u80fd\u6709\u8fd9\u79cd\u5173\u7cfb\u5b58\u5728\uff0c\u5c31\u7b97\u6709\u5341\u4e07\u4e2a\u9ad8\u6e10\u98de\u6b7b\u5728\u8427\u6cea\u8840\u9762 \u524d\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u52a8\u4e00\u6839\u624b\u6307\u7684\u3002\u201d \u3000\u3000\u4ed6\u770b\u9752\u5353\u9752\uff0c\u58f0\u97f3\u53c8\u53d8\u5f97\u5f88\u6e29\u548c\u3002 \u3000\u3000\u201c\u4f60\u4e00\u5b9a\u8981\u76f8\u4fe1\u6211\uff0c\u4ec0\u4e48\u4e8b\u90fd\u53ef\u80fd\u53d1\u751f\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u50cf\u6731\u731b\u8fd9\u6837\u4e00\u6761\u94c1\u94ee\u94ee\u7684\u597d \u6c49\uff0c\u600e\u4e48\u53ef\u80fd\u8d25\u5728\u4e00\u4e2a\u5973\u4eba\u624b\u91cc\uff1f\u53ef\u662f\u4ed6\u8d25\u4e86\uff0c\u8d25\u5f97\u5f88\u60e8\uff0c\u8427\u6cea\u8840\u4e5f\u4e00\u6837\uff0c\u8c01\u80fd\u60f3\u5f97\u5230\u4ed6\u6709 \u4eca\u65e5\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u4e00\u6837\uff0c\u6211\u53c8\u4f55\u5c1d\u80fd\u60f3\u5230\u5c06\u6765\u6211\u4f1a\u8d25\u5728\u8c01\u7684\u624b\u91cc\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u8bb8\u5e76\u4e0d\u662f\u5b9e\u6d3b\uff0c\u53ef\u662f\u5176\u4e2d\u5374\u6709\u4e9b\u503c\u5f97\u6df1\u601d\u7684\u54f2\u7406\u3002 \u3000\u3000\u5353\u9752\u5ffd\u7136\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u4ed6\u5e94\u8be5\u9000\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u53f8\u9a6c\u8d85\u7fa4\u5df2\u7ecf\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u542c\u89c1\u53f8\u9a6c\u8d85\u7fa4\u5728\u8bf4\uff1a\u201c\u662f\u7684\uff0c\u8fd9\u79cd\u4e8b\u672c\u6765\u5c31\u662f\u8c01\u90fd\u60f3\u4e0d\u5230\u7684\u3002\u201d \u4e8c \u3000\u3000\u95e8\u662f\u5f00\u7740\u7684\uff0c\u53f8\u9a6c\u8d85\u7fa4\u7ad9\u5728\u95e8\u53e3\uff0c\u5916\u9762\u662f\u4e00\u7247\u63a5\u8fd1\u4e73\u767d\u8272\u7684\u6d53\u96fe\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u662f\u4e2a\u4e2d\u5e74\u4eba\uff0c\u8863\u670d\u548c\u5934\u53d1\u90fd\u5f88\u51cc\u4e71\uff0c\u7ecf\u8fc7\u957f\u9014\u5954\u6ce2\u540e\uff0c\u4e5f\u663e\u5f97\u5f88\u75b2\u5026\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7ad9\u5728\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u770b\u8d77\u6765\u8fd8\u662f\u90a3\u4e48\u9ad8\u5927\u82f1\u4fca\u5f3a\u58ee\uff0c\u800c\u4e14\u8fdc\u6bd4\u4ed6\u5b9e\u9645\u5e74\u9f84\u5e74\u8f7b\u5f97 \u591a\uff0c\u5728\u95e8\u5916\u7684\u6d53\u96fe\u548c\u5c4b\u91cc\u7684\u706f\u5149\u886c\u6258\u4e0b\uff0c\u4ed6\u770b\u6765\u7b80\u76f4\u5c31\u50cf\u662f\u56fe\u753b\u4e2d\u7684\u5929\u795e\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u4e00\u70b9\u65e0\u7591\u662f\u6c5f\u6e56\u4e2d\u4efb\u4f55\u4eba\u90fd\u6bd4\u4e0d\u4e0a\u7684\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u7684\u6b66\u529f\u53ea\u6709\u73b0\u5728\u4e00\u534a\u597d\uff0c\u4ed6\u4e5f\u5fc5\u5c06\u6210\u4e3a\u4e00\u4f4d\u53d7\u4eba\u8d5e\u4f69\u5c0a\u656c\u7684\u82f1\u96c4\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5929\u751f\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u5353\u4e1c\u6765\u770b\u7740\u4ed6\u7684\u65f6\u5019\uff0c\u773c\u4e2d\u4e5f\u4e0d\u7981\u9732\u51fa\u8d5e\u8d4f\u4e4b\u9c7c\u3002\u5f88\u5feb\u7684\u7ad9\u8d77\u6765\uff0c\u4e3a\u4ed6\u5012\u4e86\u676f\u9152\u3002 \u3000\u3000\u2014\u2014\u4f60\u4e3a\u4ec0\u4e48\u8981\u5230\u6d1b\u9633\u53bb\uff1f\u4e3a\u4ec0\u4e48\u8981\u88c5\u75c5\u9a97\u6211\uff1f \u3000\u3000\u8fd9\u4e9b\u4e8b\u5353\u4e1c\u6765\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u63d0\u3002 \u3000\u3000\u5728\u4ed6\u80fd\u611f\u89c9\u5230\u53f8\u9a6c\u8d85\u7fa4\u5fc3\u60c5\u4e0d\u597d\u7684\u65f6\u5019\uff0c\u4ed6\u603b\u662f\u4f1a\u5c0f\u5fc3\u907f\u514d\u63d0\u8d77\u8fd9\u4e00\u7c7b\u4e0d\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u201c\u4f60\u4e00\u5b9a\u5f88\u7d2f\u4e86\uff0c\u4e00\u5b9a\u6025\u7740\u5728\u8d76\u8def\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u672c\u6765\u9884\u8ba1\u4f60\u8981\u5230\u660e\u540e\u5929\u624d\u4f1a\u56de\u6765 \u7684\u3002\u201d \u3000\u3000\u4ed6\u5e26\u7740\u5fae\u7b11\u95ee\uff1a\u201c\u6d1b\u9633\u90a3\u8fb9\u7684\u5929\u6c14\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u6c89\u9ed8\u7740\uff0c\u795e\u8272\u597d\u50cf\u6709\u70b9\u5947\u602a\uff0c\u8fc7\u4e86\u534a\u5929\u624d\u5f00\u53e3\uff1a\u201c\u90a3\u8fb9\u7684\u5929\u6c14\u5f88\u597d\uff0c\u6bd4\u8fd9\u91cc \u597d\uff0c\u6d41\u5728\u8857\u4e0a\u7684\u8840\u4e5f\u5e72\u5f97\u5f88\u5feb\uff0c\u6bd4\u8fd9\u91cc\u5feb\u5f97\u591a\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u597d\u50cf\u4e5f\u6709\u70b9\u602a\u602a\u7684\uff0c\u5353\u4e1c\u6765\u5374\u597d\u50cf\u6ca1\u6709\u611f\u89c9\u5230\u3002 \u3000\u3000\u201c\u53ea\u8981\u8840\u6d41\u4e86\u51fa\u6765\uff0c\u8fdf\u65e9\u603b\u4f1a\u5e72\u7684\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u65e9\u4e00\u70b9\u5e72\uff0c\u665a\u4e00\u70b9\u5e72\u3002\u5176\u5b9e\u90fd\u6ca1\u6709\u4ec0 \u4e48\u5173\u7cfb\u3002\u201c \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e16\u4e0a\u6709\u5f88\u591a\u4e8b\u60c5\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u4e16\u4e0a\u4e5f\u6709\u5f88\u591a\u4e8b\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4eba\u6d3b\u7740\uff0c\u8fdf\u65e9\u603b\u8981\u6b7b\u3002\u53ef\u662f\u65e9\u6b7b\u548c\u665a\u6b7b\u7684\u5206\u522b\u5c31\u5f88\u5927\u4e86\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u5982\u679c\u4f60\u8981 \u6740\u4e00\u4e2a\u4eba\uff0c\u80fd\u4e0d\u80fd\u7b49\u5230\u4ed6\u6b7b\u4e86\u4e4b\u540e\u624d\u52a8\u624b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6740\u4eba\u8981\u53ca\u65f6\uff0c\u65f6\u673a\u4e00\u8fc7\uff0c\u7269\u79fb\u4eba\u6362\uff0c\u60c5\u51b5\u5c31\u4e0d\u5bf9\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u4e3e\u676f\uff1a\u201c\u5c31\u50cf\u559d\u9152\u4e00\u6837\uff0c\u559d\u9152\u4e5f\u8981\u53ca\u65f6\uff0c\u5982\u679c\u4f60\u628a\u8fd9\u676f\u9152\u7559\u5230\u4ee5\u540e\u518d\u559d\uff0c\u5b83\u5c31\u4f1a \u53d8\u9178\u7684\u3002\u201d \u3000\u3000\u201c\u5bf9\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u540c\u610f\uff1a\u201c\u4f60\u8bf4\u5f97\u5bf9\u6781\u4e86\u3002\u4f60\u8bf4\u7684\u6d3b\u597d\u50cf\u6c38\u8fdc\u4e0d\u4f1a\u9519\u3002\u201d \u3000\u3000\u4ed6\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff1a\u201c\u8fd9\u4e00\u676f\u6211\u8981\u656c\u4f60\uff0c\u56e0\u4e3a\u4f60\u53c8\u66ff\u6211\u4eec\u7684\u5927\u9556\u5c40\u6253\u4e86\u6b21\u6f02\u6f02\u4eae\u4eae\u7684\u80dc \u4ed7\u3002\u201d \u3000\u3000\u201c\u4f60\u5df2\u7ecf\u77e5\u9053\u8fd9\u91cc\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u6211\u5df2\u7ecf\u56de\u6765\u5f88\u4e45\uff0c\u4e5f\u60f3\u4e86\u5f88\u4e45\u3002\u201d \u3000\u3000\u201c\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u60f3\u4f60\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7684\u8868\u60c5\u66f4\u5947\u602a\uff1a\u201c\u6211\u628a\u8fd9\u4e09\u5341\u5e74\u6765\u4f60\u66ff\u6211\u505a\u7684\u6bcf\u4ef6\u4e8b\u90fd\u4ed4\u7ec6\u60f3\u8fc7\u4e00\u904d\u3002\u6211\u8d8a\u60f3 \u8d8a\u89c9\u5f97\u4f60\u771f\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\uff0c\u6211\u5b9e\u5728\u6bd4\u4e0d\u4e0a\u4f60\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u7b11\u5bb9\u4ecd\u5728\u8138\u4e0a\uff0c\u5374\u5df2\u53d8\u5f97\u5f88\u751f\u786c\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u60f3\u8fd9\u4e9b\u4e8b\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u8f6c\u8fc7\u8eab\u3002 \u3000\u3000\u201c\u4f60\u8ddf\u6211\u6765\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u5e26\u4f60\u53bb\u770b\u51e0\u4e2a\u4eba\uff0c\u4f60\u770b\u8fc7\u4e4b\u540e\u5c31\u4f1a\u660e\u767d\u7684\u3002\u201d \u4e09 \u3000\u3000\u6668\u66e6\u521d\u9732\uff0c\u96fe\u8272\u66f4\u6d53\u3002 \u3000\u3000\u8fd9\u4e2a\u5c0f\u56ed\u4e2d\u6ca1\u6709\u79cd\u82b1\uff0c\u5374\u79cd\u7740\u4e9b\u9ec4\u82bd\u767d\u3001\u8c4c\u8c46\u9752\u3001\u841d\u535c\u3001\u83b4\u82e3\u3001\u80e1\u74dc\u548c\u97ed\u83dc\u3002 \u3000\u3000\u8fd9\u4e9b\u852c\u83dc\u90fd\u662f\u5434\u5a49\u79cd\u7684\uff0c\u53f8\u9a6c\u8d85\u7fa4\u4e00\u5411\u559c\u6b22\u5403\u521a\u6458\u4e0b\u7684\u65b0\u9c9c\u852c\u83dc\u3002 \u3000\u3000\u6240\u4ee5\u56ed\u91cc\u4e0d\u79cd\u82b1\uff0c\u53ea\u79cd\u83dc\u3002 \u3000\u3000\u5434\u5a49\u505a\u7684\u6bcf\u4ef6\u4e8b\u90fd\u662f\u4e3a\u5979\u7684\u4e08\u592b\u800c\u505a\u7684\uff0c\u5979\u7684\u4e08\u592b\u548c\u4ed6\u4eec\u7684\u4e24\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5b69\u5b50\u4e00\u5411\u5f88\u4e56\u5de7\uff0c\u5f88\u542c\u8bdd\uff0c\u56e0\u4e3a\u5434\u5a49\u4ece\u5c0f\u5c31\u628a\u4ed6\u4eec\u6559\u517b\u5f97\u5f88\u597d\uff0c\u4ece\u6765\u4e0d\u8ba9\u4ed6\u4eec\u63a5 \u89e6\u5230\u5927\u4eba\u7684\u4e8b\uff0c\u4e5f\u4e0d\u8ba9\u4ed6\u4eec\u968f\u4fbf\u6e9c\u5230\u5916\u9762\u53bb\u3002 \u3000\u3000\u5916\u9762\u5c31\u662f\u5927\u9556\u5c40\u7684\u8303\u56f4\u4e86\uff0c\u90a3\u4e9b\u4eba\u548c\u90a3\u4e9b\u4e8b\u90fd\u4e0d\u662f\u5b69\u5b50\u5e94\u8be5\u770b\u5230\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u5c0f\u56ed\u548c\u540e\u9762\u7684\u4e00\u5ea7\u5c0f\u697c\uff0c\u5c31\u662f\u5434\u5a49\u548c\u5b69\u5b50\u751f\u6d3b\u7684\u5929\u5730\u3002 \u3000\u3000\u8d70\u5230\u8fd9\u91cc\uff0c\u5353\u4e1c\u6765\u624d\u60f3\u8d77\u5df2\u7ecf\u6709\u597d\u51e0\u5929\u6ca1\u6709\u89c1\u5230\u8fc7\u4ed6\u4eec\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u758f\u5ffd\u3002 \u3000\u3000\u4e3a\u4e86\u4ed6\u548c\u53f8\u9a6c\u4e4b\u95f4\u7684\u4ea4\u60c5\uff0c\u4e3a\u4e86\u5927\u9556\u5c40\u7684\u524d\u9014\uff0c\u4ed6\u51b3\u5fc3\u4ee5\u540e\u4e0d\u518d\u63d0\u8d77\u90ed\u5e84\u90a3\u4ef6\u4e8b\uff0c\u800c\u4e14 \u5bf9\u5434\u5a49\u548c\u5b69\u5b50\u4eec\u597d\u4e00\u70b9\u3002 \u56db \u3000\u3000\u5c0f\u697c\u4e0b\u9762\u662f\u5385\uff0c\u4e00\u95f4\u6b63\u5385\u548c\u4e00\u95f4\u559d\u9152\u7684\u82b1\u5385\uff0c\u8fd9\u91cc\u867d\u7136\u5f88\u5c11\u6709\u5ba2\u4eba\u6765\uff0c\u5434\u5a49\u8fd8\u662f\u628a\u8fd9\u4e24 \u4e2a\u5385\u5e03\u7f6e\u5f97\u5f88\u5e7d\u9759\u8212\u670d\u3002 \u3000\u3000\u697c\u4e0a\u624d\u662f\u5979\u548c\u5b69\u5b50\u7684\u5367\u623f\uff0c\u4ece\u5979\u5a18\u5bb6\u966a\u5ac1\u6765\u7684\u4e00\u4e2a\u5976\u5976\u548c\u4e24\u4e2a\u4e2b\u5934\u4e5f\u8ddf\u5979\u4f4f\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5979\u7684\u4e08\u592b\u5374\u4e0d\u4f4f\u5728\u5bb6\u91cc\u3002 \u3000\u3000\u53f8\u9a6c\u5bf9\u5979\u5f88\u597d\uff0c\u5bf9\u5b69\u5b50\u4eec\u4e5f\u597d\uff0c\u53ef\u662f\u665a\u4e0a\u5374\u4ece\u6765\u4e0d\u4f4f\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u5929\u8272\u8fd8\u6ca1\u6709\u4eae\u3002\u697c\u4e0a\u5e76\u6ca1\u6709\u71c3\u706f\uff0c\u5434\u5a49\u548c\u5b69\u5b50\u4eec\u60f3\u5fc5\u8fd8\u5728\u6c89\u7761\u3002 \u3000\u3000\u2014\u2014\u53f8\u9a6c\u8d85\u7fa4\u4e3a\u4ec0\u4e48\u8981\u5e26\u4ed6\u5230\u8fd9\u91cc\u6765\u770b\u4ed6\u4eec\uff1f \u3000\u3000\u5353\u4e1c\u6765\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u5367\u623f\u7684\u7a97\u5b50\u5c45\u7136\u662f\u5f00\u7740\u7684\uff0c\u4e73\u767d\u8272\u7684\u6d53\u96fe\u88ab\u98ce\u5439\u8fdb\u6765\u4e4b\u540e\uff0c\u5c31\u53d8\u6210\u4e00\u79cd\u6de1\u6de1\u7684\u6b7b\u7070\u8272\uff0c \u4f7f\u5f97\u8fd9\u95f4\u672c\u6765\u5f88\u5e7d\u96c5\u7684\u5c4b\u5b50\u53d8\u5f97\u597d\u50cf\u5145\u6ee1\u4e86\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u9634\u68ee\u4e4b\u610f\uff0c\u800c\u4e14\u975e\u5e38\u51b7\uff0c\u5947\u51b7\u5f7b \u9aa8\u3002 \u3000\u3000\u56e0\u4e3a\u706b\u76c6\u65e9\u5df2\u706d\u4e86\u3002 \u3000\u3000\u4e00\u5411\u7ec6\u5fc3\u7684\u5973\u4e3b\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4e3a\u5979\u7684\u5b69\u5b50\u5728\u706b\u76c6\u91cc\u6dfb\u4e00\u70b9\u706b\uff1f \u3000\u3000\u6ca1\u6709\u706f\uff0c\u6ca1\u6709\u706b\u3002\u53ef\u662f\u6709\u98ce\u3002 \u3000\u3000\u4ece\u9634\u68ee\u68ee\u7070\u8499\u8499\u7684\u96fe\u4e2d\u770b\u8fc7\u53bb\uff0c\u5c4b\u5b50\u91cc\u4eff\u4f5b\u6709\u4e2a\u4eba\u5728\u968f\u98ce\u6447\u52a8\u3002 \u3000\u3000\u540a\u5728\u534a\u7a7a\u4e2d\u968f\u98ce\u6447\u52a8\u3002 \u3000\u3000\u4e00\u600e\u4e48\u4f1a\u540a\u5728\u534a\u7a7a\u4e2d\uff0c\u8fd9\u4e2a\u4eba\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u5353\u4e1c\u6765\u7684\u5fc3\u5ffd\u7136\u6c89\u4e86\u4e0b\u53bb\uff0c\u77b3\u5b54\u5ffd\u7136\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u6709\u53cc\u7ecf\u8fc7\u591a\u5e74\u523b\u82e6\u8bad\u7ec3\u540e\u9762\u53d8\u5f97\u5140\u9e70\u822c\u9510\u5229\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u770b\u51fa\u4e86\u8fd9\u4e2a\u60ac\u5728\u534a\u7a7a\u4e2d\u7684\u4eba\uff0c\u800c\u4e14\u770b\u51fa\u8fd9\u4e2a\u4eba\u662f\u7528\u4e00\u6839\u7ef3\u4e8e\u60ac\u5728\u534a\u7a7a\u4e2d\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u5434\u5a49\u3002 \u3000\u3000\u5979\u628a\u4e00\u6839\u7ef3\u5b50\u6253\u4e86\u4e00\u4e2a\u6b7b\u7ed3\uff0c\u628a\u8fd9\u6839\u7ef3\u5b50\u60ac\u5728\u6881\u4e0a\uff0c\u518d\u628a\u81ea\u5df1\u7684\u8116\u5b50\u5957\u8fdb\u53bb\uff0c\u628a\u5979\u81ea\u5df1 \u6253\u7684\u90a3\u4e2a\u6b7b\u7ed3\u5957\u5728\u81ea\u5df1\u7684\u54bd\u5589\u3002 \u3000\u3000\u7b49\u5979\u7684\u4e24\u6761\u817f\u79bb\u5730\u65f6\uff0c\u8fd9\u4e2a\u6b7b\u7ed3\u5c31\u5d4c\u5165\u4e86\u5979\u7684\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u6b7b\u3002 \u3000\u3000\u5343\u53e4\u8270\u96be\u552f\u4e00\u6b7b\uff0c\u8fd9\u672c\u6765\u662f\u4ef6\u591a\u4e48\u56f0\u96be\u7684\u4e8b\uff0c\u53ef\u662f\u6709\u65f6\u5019\u5374\u53c8\u504f\u504f\u8fd9\u4e48\u5bb9\u6613\u3002 \u3000\u3000\u9664\u4e86\u5434\u5a49\u5916\uff0c\u5c4b\u5b50\u91cc\u8fd8\u6709\u4e2a\u4eba\uff0c\u4e00\u4e2a\u767d\u53d1\u5982\u971c\u7684\u8001\u5976\u5988\uff0c\u4e24\u4e2a\u5e74\u534e\u5df2\u5982\u82b1\u4e00\u822c\u51cb\u843d\u7684\u4e2b \u5934\uff0c\u4e00\u5bf9\u53ef\u7231\u7684\u5b69\u5b50\uff0c\u6709\u7740\u65e0\u9650\u8fdc\u5927\u524d\u7a0b\u7684\u53ef\u7231\u5b69\u5b50\uff0c\u8ba9\u4eba\u770b\u89c1\u5c31\u4f1a\u4ece\u5fc3\u91cc\u6b22\u559c\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\uff0c\u5976\u5988\u7684\u5934\u53d1\u5df2\u7ecf\u4e0d\u518d\u53d1\u767d\u4e86\uff0c\u4e2b\u5934\u4eec\u4e5f\u4e0d\u4f1a\u518d\u81ea\u4f24\u5e74\u534e\u8001\u53bb\u3002 \u3000\u3000\u5b69\u5b50\u4e5f\u4e0d\u4f1a\u518d\u8ba9\u4eba\u4e00\u770b\u89c1\u5c31\u4ece\u5fc3\u91cc\u6b22\u559c\uff0c\u53ea\u4f1a\u8ba9\u4eba\u4e00\u770b\u89c1\u5c31\u4f1a\u89c9\u5f97\u5fc3\u91cc\u6709\u79cd\u5200\u5272\u822c\u7684\u60b2 \u4f24\u548c\u75db\u82e6\u3002 \u3000\u3000\u2014\u2014\u591a\u4e48\u53ef\u7231\u7684\u5b69\u5b50\uff0c\u591a\u4e48\u53ef\u601c\u3002 \u3000\u3000\u201c\u6211\u5bf9\u4e0d\u8d77\u4f60\uff0c\u6240\u4ee5\u6211\u6b7b\u4e86\uff0c\u6211\u8be5\u6b7b\uff0c\u6211\u53ea\u6709\u6b7b\u3002\u5b69\u5b50\u4eec\u5374\u4e0d\u8be5\u6b7b\u7684\u3002 \u3000\u3000\u53ef\u662f\u6211\u4e5f\u53ea\u6709\u8ba9\u4ed6\u4eec\u966a\u6211\u6b7b\u3002 \u3000\u3000\u6211\u4e0d\u8981\u8ba9\u4ed6\u4eec\u505a\u4e00\u4e2a\u6ca1\u6709\u5a18\u7684\u5b69\u5b50\uff0c\u6211\u4e5f\u4e0d\u8981\u8ba9\u4ed6\u4eec\u957f\u5927\u540e\u53d8\u6210\u4e86\u4e00\u4e2a\u50cf\u4f60\u7684\u597d\u670b\u53cb\u5353 \u4e1c\u6765\u90a3\u6837\u7684\u4eba\u3002 \u3000\u3000\u5d14\u5988\u662f\u6211\u7684\u5976\u5988\uff0c\u6211\u4ece\u5c0f\u5c31\u662f\u5403\u5979\u7684\u5976\u957f\u5927\u7684\uff0c\u5979\u4e00\u76f4\u628a\u6211\u5f53\u505a\u5979\u7684\u5973\u513f\u4e00\u6837\u3002 \u3000\u3000\u5c0f\u82ac\u548c\u5c0f\u82b3\u5c31\u50cf\u662f\u6211\u7684\u59d0\u59b9\u3002 \u3000\u3000\u6211\u6b7b\u4e86\uff0c\u5979\u4eec\u4e5f\u4e0d\u60f3\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u6240\u4ee5\u6211\u4eec\u90fd\u6b7b\u4e86\u3002 \u3000\u3000\u6211\u4e0d\u8981\u4f60\u539f\u8c05\u6211\uff0c\u53ea\u8981\u4f60\u597d\u597d\u7684\u6d3b\u4e0b\u53bb\uff0c\u6211\u4e5f\u77e5\u9053\u6ca1\u6709\u6211\u4eec\u4f60\u4e00\u5b9a\u4e5f\u4f1a\u4e00\u6837\u6d3b\u5f97\u5f88\u597d \u7684\u3002\u201d \u3000\u3000\u597d\u51b7\u3001\u597d\u51b7\u3001\u597d\u51b7\uff0c\u5353\u4e1c\u6765\u4ece\u672a\u89c9\u5f97\u8fd9\u4e48\u51b7\u8fc7\u3002 \u3000\u3000\u8fd9\u95f4\u7cbe\u96c5\u7684\u5367\u623f\u7adf\u662f\u4e2a\u575f\u5893\uff0c\u800c\u4ed6\u81ea\u5df1\u4e5f\u5728\u8fd9\u4e2a\u575f\u5893\u91cc\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u4f53\u808c\u8089\u8840\u8109\u9aa8\u9ad3\u90fd\u4eff\u4f5b\u5df2\u51b7\u5f97\u7ed3\u51b0\u3002 \u3000\u3000\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u8fd9\u662f\u4ec0\u4e48\u65f6\u5019\u53d1\u751f\u7684\uff1f\u5434\u8155\u4e3a\u4ec0\u4e48\u8981\u6b7b\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u771f\u7684\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6b7b\u4e86\u81f3\u5c11\u5df2\u7ecf\u6709\u4e09\u56db\u5929\uff0c\u4f60\u5c45\u7136\u8fd8\u4e0d\u77e5\u9053\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u7684\u58f0\u97f3\u51b0\u51b7\uff1a\u201c\u4f60\u5b9e\u5728\u628a \u4ed6\u4eec\u7167\u987e\u5f97\u5f88\u597d\uff0c\u6211\u5b9e\u5728\u5e94\u8be5\u611f\u6fc0\u4f60\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u8bdd\u5c31\u597d\u50cf\u4e00\u6839\u51b0\u51b7\u7684\u957f\u9488\u4ece\u5353\u4e1c\u6765\u7684\u5934\u9876\u4e00\u76f4\u63d2\u5230\u4ed6\u811a\u5e95\u3002 \u3000\u3000\u4ed6\u6709\u5f88\u591a\u7406\u7531\u53ef\u4ee5\u89e3\u91ca\u3002 \u3000\u3000\u2014\u2014\u8fd9\u51e0\u5929\u4ed6\u4e00\u76f4\u5168\u529b\u5728\u5bf9\u4ed8\u96c4\u72ee\u5802\uff0c\u8fd9\u5730\u65b9\u662f\u5c5e\u4e8e\u5434\u5a49\u548c\u5b69\u5b50\u4eec\u7684\uff0c\u4ed6\u548c\u5927\u9556\u5c40\u7684\u4eba \u90fd\u5f88\u5c11\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u89e3\u91ca\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u6839\u672c\u5c31\u65e0\u6cd5\u89e3\u91ca\uff0c\u65e0\u8bba\u600e\u4e48\u6837\u89e3\u91ca\u90fd\u662f\u591a\u4f59\u7684\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u59cb\u7ec8\u6ca1\u6709\u770b\u8fc7\u4ed6\u4e00\u773c\uff0c\u4ed6\u4e5f\u770b\u4e0d\u89c1\u53f8\u9a6c\u8138\u4e0a\u7684\u8868\u60c5\u3002 \u3000\u3000\u201c\u4f60\u95ee\u6211\uff0c\u5434\u5a49\u4e3a\u4ec0\u4e48\u8981\u6b7b\uff1f\u6211\u672c\u6765\u4e5f\u60f3\u4e0d\u901a\u7684\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u5979\u7684\u5e74\u7eaa\u5e76\u4e0d\u5927\uff0c \u8eab\u4f53\u4e00\u5411\u5f88\u597d\uff0c\u4e00\u5411\u5f88\u559c\u6b22\u5b69\u5b50\uff0c\u5979\u5bf9\u6211\u867d\u7136\u5e76\u4e0d\u5341\u5206\u5fe0\u5b9e\uff0c\u5374\u4e00\u76f4\u90fd\u80fd\u5c3d\u5230\u505a\u59bb\u5b50\u7684\u8d23 \u4efb\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u51fa\u5947\u5e73\u9759\uff1a\u201c\u53ef\u662f\u6211\u5374\u6ca1\u6709\u5c3d\u5230\u505a\u4e08\u5927\u7684\u8d23\u4efb\uff0c\u6240\u4ee5\u9519\u7684\u662f\u6211\uff0c\u4e0d\u662f\u5979\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u77e5\u9053\u90a3\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u65e9\u5df2\u77e5\u9053\uff0c\u505a\u4e08\u592b\u7684\u5e76\u4e0d\u4e00\u5b9a\u662f\u6700\u540e\u77e5\u9053\u7684\u4e00\u4e2a\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u6211\u4e5f\u77e5 \u9053\u90a3\u4ef6\u4e8b\u5f88\u5feb\u5c31\u4f1a\u8fc7\u53bb\u7684\u3002\u5979\u8fd8\u662f\u4f1a\u505a\u6211\u7684\u597d\u59bb\u5b50\uff0c\u8fd8\u662f\u4f1a\u597d\u597d\u7167\u987e\u6211\u7684\u5b69\u5b50\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u7684\u63a5\u7740\u8bf4\uff1a\u201c\u6211\u65e2\u7136\u51b3\u5fc3\u8981\u4f9d\u7167\u4f60\u7684\u610f\u601d\u505a\u4e00\u4e2a\u4e86\u4e0d\u8d77\u7684\u5927\u82f1\u96c4\uff0c\u5c31\u5fc5\u9700\u4ed8\u51fa\u4ee3 \u4ef7\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u5c31\u6545\u610f\u88c5\u505a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u82e5\u77e5\u9053\uff0c\u5c31\u4e00\u5b9a\u8981\u6740\u4e86\u5979\uff0c\u4e00\u4e2a\u82f1\u96c4\u7684\u5bb6\u91cc\u662f\u7edd\u5bf9\u4e0d \u5141\u8bb8\u8fd9\u79cd\u4e8b\u53d1\u751f\u7684\uff0c\u6211\u5f53\u7136\u975e\u6740\u5979\u4e0d\u53ef\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u53ea\u6709\u88c5\u505a\u4e0d\u77e5\u9053\u3002\u56e0\u4e3a\u8fd9\u662f\u6211\u7684\u5bb6\uff0c\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\u6211\u90fd\u4e0d\u80fd\u628a\u8fd9 \u4e2a\u5bb6\u6bc1\u6389\u3002\u6211\u4e0d\u4f46\u8981\u88c5\u4f5c\u4e0d\u77e5\u9053\uff0c\u800c\u4e14\u8fd8\u8981\u5979\u8ba4\u4e3a\u6211\u5b8c\u5168\u4e0d\u77e5\u9053\uff0c\u8fd9\u4e2a\u5bb6\u624d\u80fd\u4fdd\u5b58\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u663e\u5f97\u5f88\u60ca\u8bb6\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\u4ed6\u624d\u53d1\u73b0\u81ea\u5df1\u4ee5\u524d\u6839\u672c\u6ca1\u6709\u5b8c\u5168\u4e86\u89e3\u53f8\u9a6c\u8d85\u7fa4\u3002\u4ed6\u4ece\u4e0d\u77e5\u9053\u53f8\u9a6c\u8d85\u7fa4\u7684\u6027\u683c\u4e2d \u8fd8\u6709\u8fd9\u6837\u7684\u4e00\u9762\u3002\u5c45\u7136\u662f\u4e2a\u8fd9\u4e48\u91cd\u611f\u60c5\u7684\u4eba\uff0c\u9047\u5230\u8fd9\u79cd\u4e8b\uff0c\u5c45\u7136\u8fd8\u80fd\u7279\u522b\u4eba\u7740\u60f3\u3002 \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u672c\u6765\u662f\u4efb\u4f55\u7537\u4eba\u90fd\u4e0d\u80fd\u5fcd\u53d7\u7684\uff0c\u53ef\u662f\u6211\u5df2\u7ecf\u60f3\u901a\u4e86\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u7b49\u5230\u8fd9\u4ef6\u4e8b \u8fc7\u53bb\uff0c\u7b49\u5230\u5b69\u5b50\u4eec\u957f\u5927\uff0c\u6211\u4eec\u8fd8\u662f\u50cf\u522b\u7684\u6069\u7231\u592b\u5987\u4e00\u6837\uff0c\u4e92\u76f8\u53ae\u5b88\uff0c\u5171\u5ea6\u4f59\u5e74\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\uff0c\u9762\u5bf9\u5353\u4e1c\u6765\uff1a\u201c\u5982\u679c\u4e0d\u662f\u4f60\u903c\u6b7b\u4e86\u5979\uff0c\u6211\u4eec\u4e00\u5b9a\u4f1a\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u903c\u6b7b\u4e86\u5979\uff1f\u201d\u5353\u4e1c\u6765\u58f0\u97f3\u5df2\u5636\u54d1\uff1a\u201c\u4f60\u8ba4\u4e3a\u662f\u6211\u903c\u6b7b\u4e86\u5979\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u4f46\u903c\u6b7b\u4e86\u5979\uff0c\u903c\u6b7b\u4e86\u90ed\u5e84\uff0c\u800c\u4e14\u8fdf\u65e9\u4f1a\u628a\u6211\u4e5f\u903c\u6b7b\u7684\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u56e0\u4e3a\u4f60\u6c38\u8fdc \u90fd\u8981\u522b\u4eba\u4f9d\u7167\u4f60\u5b89\u6392\u7684\u65b9\u5f0f\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u5353\u4e1c\u6765\uff1a\u201c\u56e0\u4e3a\u4f60\u7684\u5fc3\u91cc\u6709\u75c5\uff0c\u4f60\u5916\u8868\u867d\u7136\u81ea\u9ad8\u81ea\u5927\uff0c\u5176\u5b9e\u5fc3\u91cc\u5374\u770b\u4e0d\u8d77\u81ea \u5df1\uff0c\u6240\u4ee5\u4f60\u8981\u6211\u4ee3\u8868\u4f60\u53bb\u505a\u90a3\u4e9b\u672c\u6765\u5e94\u8be5\u662f\u4f60\u81ea\u5df1\u53bb\u505a\u7684\u4e8b\u60c5\uff0c\u4f60\u8981\u628a\u6211\u9020\u6210\u4e00\u4e2a\u82f1\u96c4\u5076 \u50cf\uff0c\u56e0\u4e3a\u4f60\u5fc3\u91cc\u5df2\u7ecf\u628a\u6211\u5f53\u4f5c\u4f60\u7684\u5316\u8eab\uff0c\u6240\u4ee5\u4f60\u82e5\u8ba4\u4e3a\u6709\u4eba\u4f1a\u963b\u788d\u4f60\u7684\u8ba1\u5212\uff0c\u5c31\u4f1a\u4e0d\u62e9\u624b\u6bb5 \u628a\u4ed6\u903c\u6b7b\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u5434\u5a49\u5c31\u662f\u8fd9\u4e48\u6837\u6b7b\u7684\u3002\u56e0\u4e3a\u4f60\u89c9\u5f97\u5979\u5df2\u7ecf\u963b\u788d\u4e86\u4f60\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6c89\u9ed8\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\u5f88\u4e45\u3002 \u3000\u3000\u201c\u4f60\u521a\u624d\u544a\u8bc9\u6211\uff0c\u4f60\u5df2\u7ecf\u60f3\u4e86\u5f88\u4e45\uff0c\u60f3\u4e86\u5f88\u591a\u4e8b\u3002\u201d\u4ed6\u95ee\u53f8\u9a6c\uff1a\u201d\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4f60\u89c9\u5f97 \u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u8981\u4e0b\u51b3\u5fc3\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u6709\u4e86\u51b3\u5b9a\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u51b3\u5b9a\u4ee5\u540e\u8981\u600e\u4e48\u6837\u505a\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4ee5\u540e\u8981\u600e\u4e48\u6837\u505a\uff0c\u662f\u73b0\u5728\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u73b0\u5728\u6211\u5c31\u8981\u4f60\u8d70\uff0c\u6c38\u8fdc\u4e0d\u8981\u8ba9\u6211\u518d \u89c1\u5230\u4f60\uff0c\u6c38\u8fdc\u4e0d\u8981\u518d\u7ba1\u6211\u7684\u4e8b\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u53d8\u5f97\u597d\u50cf\u7ad9\u90fd\u7ad9\u4e0d\u7a33\u4e86\uff0c\u597d\u50cf\u5ffd\u7136\u88ab\u4eba\u4e00\u68cd\u5b50\u6253\u5728\u5934\u9876\u4e0a\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u4f60\u8981\u628a\u4ec0\u4e48\u5e26\u8d70\u90fd\u53ef\u4ee5\uff0c\u4f46\u662f\u4f60\u4e00\u5b9a\u8981\u8d70\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\u5f97\u622a\u9489\u65ad\u94c1\uff1a\u201c\u4eca\u5929\u65e5 \u843d\u4e4b\u524d\uff0c\u4f60\u4e00\u5b9a\u8981\u8fdc\u79bb\u957f\u5b89\u57ce\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u8fd9\u4e9b\u6d3b\u5e76\u4e0d\u662f\u4f60\u771f\u5fc3\u8981\u8bf4\u51fa\u6765\u7684\u3002\uff0c\u4ed6\u67d4\u58f0\u8bf4\uff1a\u201c\u4f60\u53d7\u4e86\u6253\u51fb\uff0c\u53c8\u592a\u7d2f\uff0c\u53ea\u8981 \u597d\u597d\u4f11\u606f\u4e00\u9635\u5b50\uff0c\u5c31\u4f1a\u628a\u8fd9\u4e9b\u8bdd\u5fd8\u8bb0\u7684\u3002\uff0c \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u51b7\u51b7\u7684\u770b\u7740\u4ed6\u3002 \u3000\u3000\u201c\u8fd9\u6b21\u4f60\u9519\u4e86\uff0c\u73b0\u5728\u4f60\u5c31\u8981\u8d70\uff0c\u975e\u8d70\u4e0d\u53ef\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\u6211\u4eec\u521a\u624d\u8bf4\u8fc7\u7684\u8bdd \u4e86\u6740\u4eba\u8981\u53ca\u65f6\uff0c\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u65f6\u673a\u9519\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u77b3\u5b54\u53c8\u5f00\u59cb\u6536\u7f29\u3002 \u3000\u3000\u201c\u5982\u679c\u6211\u4e0d\u8d70\u5462\u5e7f\u4ed6\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u95ee\u53f8\u9a6c\uff1a\u201c\u5982\u679c\u6211\u4e0d\u8d70\uff0c\u4f60\u662f\u4e0d\u662f\u4f1a\u6740\u4e86\u6211\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u4e5f\u7528\u4ed6\u540c\u6837\u7684\u53e3\u6c14\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u4e0d\u8d70\uff0c\u6211\u5c31\u8981\u6740\u4e86\u4f60\u3002\u201d \u4e94 \u3000\u3000\u5929\u8272\u5df2\u6e10\u6e10\u4eae\u4e86\uff0c\u5c4b\u5b50\u91cc\u5374\u53cd\u800c\u66f4\u663e\u5f97\u9634\u68ee\u8be1\u79d8\u53ef\u6016\u3002 \u3000\u3000\u56e0\u4e3a\u5c4b\u91cc\u7684\u5149\u7ebf\u5df2\u7ecf\u8ba9\u4eba\u53ef\u4ee5\u770b\u6e05\u695a\u90a3\u4e9b\u60e8\u6b7b\u7684\u4eba\u3002 \u3000\u3000\u6d3b\u7740\u65f6\u8d8a\u53ef\u7231\u7684\u4eba\uff0c\u6b7b\u540e\u770b\u6765\u8d8a\u60b2\u60e8\u53ef\u6015\u3002 \u3000\u3000\u5353\u4e1c\u6765\u548c\u53f8\u9a6c\u8d85\u7fa4\u9762\u5bf9\u9762\u7684\u7ad9\u7740\uff0c\u51b7\u98ce\u4ece\u7a97\u5916\u5439\u8fdb\u6765\uff0c\u5200\u950b\u822c\u780d\u5728\u4ed6\u4eec\u4e4b\u95f4\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u53ef\u4ee5\u8d70\u7684\uff0c\u50cf\u6211\u8fd9\u6837\u7684\u4eba\uff0c\u65e0\u8bba\u54ea\u91cc\u90fd\u53ef\u4ee5\u53bb\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4f46\u662f\u6211\u4e0d\u80fd \u8d70\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4e5f\u53d8\u5f97\u51fa\u5947\u51b7\u9759\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u82b1\u4e86\u4e00\u751f\u5fc3\u8840\u624d\u9020\u6210\u4f60\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u6211\u4e0d\u80fd\u8ba9\u4f60\u6bc1\u5728\u522b\u4eba\u624b\u91cc\uff0c\u201d\u5353\u4e1c\u6765\u53c8 \u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u4f60\u77e5\u9053\u6211\u7684\u4e3a\u4eba\uff0c\u6709\u5f88\u591a\u4e8b\u6211\u90fd\u5b81\u613f\u81ea\u5df1\u505a\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u4e0d\u662f\u4e00\u5411\u90fd\u80fd\u5f7c\u6b64\u4e86\u89e3\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u65e9\u5df2\u51c6\u5907\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u51c6\u5907\u5728\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u201c\u51c6\u5907\u5c31\u5728\u6b64\u65f6\u6b64\u523b\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u6740\u4eba\u8981\u53ca\u65f6\uff0c\u8fd9\u53e5\u6d3b\u6211\u4e00\u5b9a\u4f1a\u6c38\u8fdc\u7262\u8bb0\u5728\u5fc3\u3002\u201d \u3000\u3000\u201c\u4f60\u51c6\u5907\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u6b64\u5730\u3002\u201d \u3000\u3000\u53f8\u9a6c\u73af\u89c6\u5c4b\u91cc\u7684\u5c38\u4f53\uff0c\u6bcf\u4e00\u4e2a\u5c38\u4f53\u6d3b\u7740\u65f6\u90fd\u662f\u4ed6\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u90fd\u6709\u4e00\u6bb5\u4ee4\u4ed6\u6c38\u96be\u5fd8\u6000\u7684 \u611f\u60c5\uff0c\u6bcf\u4e00\u4e2a\u4eba\u7684\u6b7b\u90fd\u5fc5\u5c06\u4ee4\u4ed6\u60b2\u75db\u6094\u6068\u7ec8\u751f\u3002 \u3000\u3000\u751a\u81f3\u8fde\u5353\u4e1c\u6765\u90fd\u4e00\u6837\u3002 \u3000\u3000\u5982\u679c\u5353\u4e1c\u6765\u4e5f\u6b7b\u5728\u8fd9\u91cc\uff0c\u90a3\u4e48\u4ed6\u751f\u547d\u4e2d\u6700\u91cd\u8981\u7684\u4e00\u90e8\u5206\u4e5f\u5c31\u5168\u90fd\u6b7b\u5728\u8fd9\u91cc\u4e86\u3002 \u3000\u3000\u201c\u5c31\u5728\u6b64\u5730\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u5929\u4e0b\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u6bd4\u8fd9\u91cc\u66f4\u597d\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4e86\u3002\u201d\u5353\u4e1c\u6765\u957f\u957f\u53f9\u606f\uff1a\u201c\u786e\u5b9e\u6ca1\u6709\u4e86\u3002\u201d \u516d \u3000\u3000\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6709\u79cd\u5f88\u7279\u522b\u7684\u4eba\uff0c\u5e73\u65f6\u4f60\u4e5f\u8bb8\u5230\u5904\u90fd\u627e\u4e0d\u5230\u4ed6\uff0c\u53ef\u662f\u4f60\u9700\u8981\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u4e00 \u5b9a\u4f1a\u5728\u4f60\u9644\u8fd1\uff0c\u7edd\u4e0b\u4f1a\u8ba9\u4f60\u5931\u671b\u3002 \u3000\u3000\u5353\u9752\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u201c\u5353\u9752\uff0c\u4f60\u8fdb\u6765\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u597d\u50cf\u77e5\u9053\u5353\u9752\u4e00\u5b9a\u4f1a\u5728\u4ed6\u9644\u8fd1\u7684\uff0c\u53ea\u8981\u8f7b\u8f7b\u4e00\u5524\uff0c\u5c31\u4f1a\u51fa\u73b0\u3002 \u3000\u3000\u5353\u9752\u679c\u7136\u6ca1\u6709\u8ba9\u4ed6\u5931\u671b\uff0c\u5353\u9752\u4ece\u6765\u90fd\u6ca1\u6709\u8ba9\u4efb\u4f55\u4eba\u5931\u671b\u8fc7\u3002 \u3000\u3000\u4ece\u4ed6\u5f88\u5c0f\u7684\u65f6\u5019\u5c31\u6ca1\u6709\u8ba9\u4eba\u5931\u671b\u8fc7\u3002\u53ef\u662f\u4eca\u5929\u4ed6\u770b\u6765\u5374\u663e\u5f97\u6709\u4e9b\u75b2\u5026\uff0c\u8eab\u4e0a\u8fd8\u7a7f\u7740\u6628\u5929 \u7684\u8863\u670d\uff0c\u8fde\u9774\u5b50\u4e0a\u7684\u6ce5\u6c61\u90fd\u6ca1\u6709\u64e6\u5e72\u51c0\u3002 \u3000\u3000\u5e73\u65f6\u4ed6\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u5e73\u65f6\u4ed6\u4e0d\u7ba1\u591a\u4e48\u5fd9\uff0c\u90fd\u4f1a\u62bd\u51fa\u65f6\u95f4\u53bb\u6574\u7406\u4fee\u9970\u4ed6\u7684\u4eea\u8868\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u5353\u4e1c\u6765\u548c\u53f8\u9a6c\u8d85\u7fa4 \u90fd\u662f\u975e\u5e38\u8bb2\u7a76\u8fd9\u4e9b\u4e8b\u7684\u4eba\u3002 \u3000\u3000\u5e78\u597d\u4eca\u5929\u5353\u4e1c\u6765\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u8fd9\u4e9b\uff0c\u53ea\u662f\u7b80\u5355\u7684\u5429\u5490\uff1a \u3000\u3000\u201c\u8dea\u4e0b\u53bb\uff0c\u5411\u53f8\u9a6c\u5927\u7237\u53e9\u5934\u3002\u201d \u3000\u3000\u5353\u9752\u8dea\u4e0b\u53bb\uff0c\u53f8\u9a6c\u8d85\u7fa4\u5e76\u6ca1\u6709\u963b\u6b62\u4ed6\uff0c\u773c\u775b\u5374\u5728\u76f4\u89c6\u7740\u5353\u4e1c\u6765\u3002 \u3000\u3000\u201c\u4f60\u7528\u4e0d\u7740\u8981\u4ed6\u53e9\u5934\u7684\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u662f\u4f60\u7684\u4e49\u5b50\uff0c\u4f60\u6ca1\u6709\u513f\u5b50\uff0c\u6211\u4f1a\u8ba9\u4ed6\u627f \u7ee7\u5353\u5bb6\u7684\u9999\u706b\uff0c\u5982\u679c\u4f60\u6b7b\u4e86\uff0c\u6211\u4e00\u5b9a\u4f1a\u597d\u597d\u7684\u7167\u987e\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u53bb\u770b\u8089\u5df1\u7684\u513f\u5b50\uff0c\u773c\u4e2d\u7acb\u523b\u5145\u6ee1\u60b2\u4f24\u548c\u6124\u6012\u2026\u2026\u201c\u6211\u81f3\u5c11\u4e0d\u4f1a\u50cf\u4f60\u7167\u987e\u6211\u7684\u513f \u5b50\u8fd9\u6837\u7167\u987e\u4ed6\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u7edd\u5bf9\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4ed6\u770b\u7740\u5353\u9752\u53e9\u5b8c\u5934\u7ad9\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u542c\u5230\u53f8\u9a6c\u5927\u7237\u8bf4\u7684\u6d3b\uff0c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u53f8\u9a6c\u5927\u7237 \u5bf9\u4efb\u4f55\u4eba\u90fd\u6ca1\u6709\u5931\u4fe1\u8fc7\uff0c\u4ed6\u7167\u987e\u4f60\u4e00\u5b9a\u6bd4\u6211\u7167\u987e\u5f97\u66f4\u597d\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5353\u9752\u7684\u58f0\u97f3\u4e5f\u5df2\u56e0\u611f\u6fc0\u800c\u987a\u54d1\uff1a\u201c\u53ef\u662f\u6211\u8fd9\u4e00\u751f\u90fd\u4e0d\u4f1a\u518d\u59d3\u522b\u4eba\u7684\u59d3\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u4e00\u5b9a\u8981\u8bb0\u4f4f\uff0c\u5982\u679c\u6211\u6b7b\u4e86\uff0c\u4f60\u5bf9\u53f8\u9a6c\u5927\u7237\u4e5f\u8981\u50cf\u5bf9\u6211\u4e00\u6837\u3002\u201d\u5353\u4e1c\u6765\u65e0\u7591\u4e5f\u52a8\u4e86 \u611f\u60c5\uff1a\u201c\u6211\u548c\u53f8\u9a6c\u5927\u7237\u4e4b\u95f4\u65e0\u8bba\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u90fd\u662f\u6211\u548c\u4ed6\u4e4b\u95f4\u7684\u79c1\u4e8b\uff0c\u4f60\u975e\u4f46\u4e0d\u80fd\u6709\u4e00\u70b9 \u6000\u6068\u7684\u5fc3\uff0c\u800c\u4e14\u7edd\u4e0d\u80fd\u628a\u4eca\u5929\u4f60\u770b\u5230\u7684\u4e8b\u544a\u8bc9\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5353\u9752\u9eef\u7136\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u4f1a\u7167\u4f60\u7684\u610f\u601d\u53bb\u505a\uff0c\u5c31\u7b97\u8981\u6211\u53bb\u6b7b\uff0c\u6211\u4e5f\u4f1a\u53bb\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u957f\u957f\u53f9\u606f\uff01 \u3000\u3000\u201c\u4f60\u4e00\u5411\u662f\u4e2a\u597d\u5b69\u5b50\uff0c\u5c06\u6765\u4e00\u5b9a\u4f1a\u6709\u51fa\u606f\u7684\u3002\u201d\u4ed6\u770b\u7740\u5353\u9752\uff1a\u201c\u4f60\u8fc7\u6765\uff0c\u6709\u6837\u4e1c\u897f\u6211\u8981 \u7559\u7ed9\u4f60\uff0c\u4e0d\u7ba1\u6211\u6b7b\u6d3b\uff0c\u4f60\u90fd\u8981\u597d\u597d\u4fdd\u5b58\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u5353\u9752\u8d70\u8fc7\u53bb\uff0c\u6162\u6162\u7684\u8d70\u8fc7\u53bb\uff0c\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u8bf4\u4e0d\u51fa\u7684\u60b2\u4f24\uff0c\u597d\u50cf\u5df2\u7ecf\u9884\u89c1\u5230\u6709\u4e00\u4ef6\u6781 \u60b2\u60e8\u53ef\u6015\u7684\u4e8b\u8981\u53d1\u751f\u4e86\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u9003\u907f\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u662f\u65e0\u6cd5\u9003\u907f\u7684\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8f6c\u8fc7\u5934\u4e0d\u518d\u53bb\u770b\u4ed6\u4eec\u3002 \u3000\u3000\u4ed6\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u7edd\u4e0d\u80fd\u88ab\u4efb\u4f55\u4eba\u611f\u52a8\uff0c\u7edd\u4e0d\u80fd\u56e0\u4e3a\u4efb\u4f55\u4e8b\u6539\u53d8\u4e3b\u610f\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u542c\u89c1\u4e86\u4e00\u58f0\u975e\u5e38\u5947\u602a\u7684\u58f0\u97f3\uff0c\u5c31\u597d\u50cf\u76ae\u9769\u523a\u7834\u65f6\u53d1\u51fa\u7684\u90a3\u79cd\u58f0\u97f3\u3002 \u3000\u3000\u7b49\u4ed6\u518d\u8f6c\u5934\u53bb\u770b\u65f6\uff0c\u5c31\u53d1\u73b0\u5353\u4e1c\u6765\u5df2\u7ecf\u5728\u8fd9\u4e00\u77ac\u95f4\u628a\u4e00\u628a\u5200\u523a\u5165\u5353\u9752\u7684\u5fc3\u810f\u3002 \u3000\u3000\u5353\u9752\u540e\u9000\u4e86\u534a\u6b65\u5c31\u6162\u6162\u7684\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u558a\u53eb\u3002 \u3000\u3000\u4ed6\u82cd\u767d\u7684\u8138\u4e0a\u4e5f\u6ca1\u6709\u4e00\u70b9\u60ca\u8bb6\u75db\u82e6\u7684\u8868\u60c5\uff0c\u5c31\u597d\u50cf\u65e9\u5df2\u9884\u6599\u5230\u8fd9\u4ef6\u4e8b\u4f1a\u53d1\u751f\u3002 \u3000\u3000\u2014\u2014\u5e76\u4e0d\u662f\u56e0\u4e3a\u5353\u4e1c\u6765\u8fd9\u4e00\u5200\u51fa\u624b\u592a\u6289\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u65e9\u6709\u51c6\u5907\uff0c\u5728\u4ed6\u8d70\u8fc7\u53bb\u7684\u65f6\u5019\uff0c\u5c31 \u597d\u50cf\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7684\u8138\u8272\u5374\u5df2\u56e0\u60ca\u8bb6\u800c\u6539\u53d8\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff0c\u201d\u53f8\u9a6c\u5386\u58f0\u95ee\u5353\u4e1c\u6765\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6015\u6211\u5728\u4f60\u6b7b\u540e\u6298\u78e8\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4f60\u7684\u5fc3\u80f8\u4e00\u5411\u6bd4\u6211\u5bbd\u5927\u4ec1\u6148\uff0c\u7edd\u4e0d\u4f1a\u505a\u8fd9\u79cd\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5f88\u5e73\u9759\uff1a\u201c\u6211\u6740\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211\u4e0d\u80fd\u628a\u4ed6\u7559\u7ed9\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u662f\u4e2a\u975e\u5e38\u5371\u9669\u7684\u4eba\uff0c\u9634\u6c89\u3001\u51b7\u9177\u800c\u5371\u9669\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff0c\u201c\u73b0\u5728\u4ed6\u7684\u5e74\u7eaa\u8fd8\u8f7b\uff0c \u6211\u8fd8\u53ef\u4ee5\u6740\u4ed6\uff0c\u518d\u8fc7\u51e0\u5e74\uff0c\u6050\u6015\u8fde\u6211\u90fd\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u89e3\u4e0b\u8eab\u4e0a\u7684\u7d2b\u8c82\u88d8\uff0c\u8f7b\u8f7b\u7684\u76d6\u4f4f\u4e86\u5353\u9752\u7684\u5c38\u4f53\uff0c\u4ed6\u7684\u52a8\u4f5c\u5c31\u597d\u50cf\u6148\u7236\u5728\u4e3a\u7231\u5b50\u76d6\u88ab\u4e00 \u6837\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u58f0\u97f3\u91cc\u5374\u5168\u65e0\u611f\u60c5\u3002 \u3000\u3000\u201c\u73b0\u5728\u4ed6\u5df2\u7ecf\u5728\u57f9\u690d\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u6211\u6d3b\u7740\uff0c\u8fd8\u53ef\u4ee5\u63a7\u5236\u4ed6\uff0c\u5982\u679c\u6211\u6b7b\u4e86\uff0c\u4e24\u4e09\u5e74\u4e4b\u95f4\u4ed6 \u5c31\u4f1a\u53d6\u4ee3\u6211\u73b0\u5728\u7684\u5730\u4f4d\uff0c\u7136\u540e\u4ed6\u5c31\u4f1a\u6740\u4e86\u4f60\u3002\u201d\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u5982\u679c\u6211\u628a\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba \u7559\u5728\u4f60\u8eab\u8fb9\uff0c\u6211\u6b7b\u4e5f\u4e0d\u80fd\u5b89\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5f88\u5e73\u6de1\uff0c\u5e73\u6de1\u5f97\u5c31\u597d\u50cf\u4ed6\u53ea\u4e0d\u8fc7\u4e3a\u53f8\u9a6c\u8d85\u7fa4\u62cd\u6b7b\u4e86\u4e00\u53ea\u868a\u5b50\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u5e76\u4e0d\u60f3\u8ba9\u53f8\u9a6c\u8d85\u7fa4\u77e5\u9053\uff0c\u4e0d\u7ba1\u4ed6\u5bf9\u522b\u4eba\u591a\u4e48\u9634\u9669\u3001\u72e0\u6bd2\u3001\u51b7\u9177\uff0c\u4ed6\u5bf9\u53f8\u9a6c\u8d85\u7fa4\u7684 \u60c5\u611f\u8fd8\u662f\u771f\u5b9e\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u70b9\u786e\u5b9e\u4e0d\u5bb9\u4efb\u4f55\u4eba\u5426\u8ba4\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7684\u53cc\u62f3\u7d27\u63e1\uff0c\u8eab\u4f53\u91cc\u6bcf\u4e00\u6839\u8840\u7ba1\u4e2d\u7684\u8840\u6db2\u90fd\u4f3c\u5df2\u6cb8\u817e\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e00\u5b9a\u8981\u63a7\u5236\u4f4f\u81ea\u5df1\uff0c\u4ed6\u7edd\u4e0d\u80fd\u518d\u50cf\u4ee5\u524d\u90a3\u4e48\u6837\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u6709\u8840\u6709\u8089\u7684\u4eba\uff0c\u4e0d\u662f\u4e2a\u5080\u5121\u3002 \u3000\u3000\u4ed6\u59bb\u5b50\u7684\u5c38\u4f53\u8fd8\u60ac\u5728\u6881\u4e0a\uff0c\u4ed6\u7684\u4e24\u4e2a\u6d3b\u6cfc\u53ef\u7231\u806a\u660e\u542c\u8bdd\u7684\u5b69\u5b50\uff0c\u5df2\u7ecf\u518d\u4e5f\u4e0d\u4f1a\u53eb\u4ed6\u7238\u7238 \u4e86\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7684\u8eab\u5b50\u5ffd\u7136\u98de\u8dc3\u800c\u8d77\uff0c\u71d5\u5b50\u822c\u63a0\u8fc7\u5c4b\u9876\u4e0b\u7684\u6a2a\u6881\u3002 \u3000\u3000\u4ed6\u7684\u5251\u5728\u6881\u4e0a\u3002 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u5b9d\u5251\u95ea\u7535\u822c\u51fb\u4e0b\u3002 \u4e03 \u3000\u3000\u6c5f\u6e56\u4e2d\u4eba\u90fd\u77e5\u9053\u53f8\u9a6c\u8d85\u7fa4\u7528\u7684\u5251\u662f\u4e00\u67c4\u201c\u5343\u9524\u5927\u94c1\u5251\u201d\u3002 \u3000\u3000\u5343\u9524\u767e\u70bc\uff0c\u70bc\u6210\u6b64\u5251\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u4e0b\u51fb\u65f6\u7684\u529b\u91cf\uff0c\u4e5f\u50cf\u662f\u6709\u4e00\u5343\u67c4\u5927\u94c1\u9524\u540c\u65f6\u51fb\u4e0b\u4e00\u7965\uff0c\u51cc\u5389\u5a01\u731b\uff0c\u4e07\u592b\u4e0d\u6321\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u957f\u56db\u5c3a\u4e09\u5bf8\uff0c\u91cd\u4e09\u5341\u4e5d\u65a4\uff0c\u94f8\u5251\u65f6\u7528\u7684\u94c1\u6765\u81ea\u4e5d\u5e9c\u5341\u4e09\u5dde\uff0c\u96c6\u4e5d\u5e9c\u5341\u4e09\u5dde\u7684\u94c1\u4e2d \u7cbe\u82f1\uff0c\u5343\u9524\u767e\u70bc\u624d\u94f8\u6210\u4e86\u8fd9\u67c4\u5927\u94c1\u5251\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u67c4\u5251\u5b9e\u5728\u592a\u91cd\u4e86\u3002 \u3000\u3000\u5251\u6cd5\u4ee5\u8f7b\u7075\u6d41\u52a8\u53d8\u5e7b\u83ab\u6d4b\u4e3a\u80dc\uff0c\u7528\u8fd9\u4e48\u4e00\u67c4\u5251\uff0c\u5728\u62db\u5f0f\u53d8\u5316\u95f4\u65e0\u7591\u4f1a\u635f\u5931\u5f88\u591a\u53ef\u4ee5\u5728\u4e00 \u77ac\u95f4\u5236\u654c\u4f24\u4eba\u7684\u673a\u4f1a\u3002 \u3000\u3000\u9ad8\u624b\u76f8\u640f\uff0c\u8fd9\u79cd\u673a\u4f1a\u65e0\u7591\u662f\u7a0d\u7eb5\u5373\u901d\u3001\u6c38\u4e0d\u518d\u6765\u7684\u3002 \u3000\u3000\u53ef\u662f\u53f8\u9a6c\u8d85\u7fa4\u4e00\u5b9a\u8981\u7528\u8fd9\u4e48\u6837\u4e00\u67c4\u5251\uff0c\u56e0\u4e3a\u4ed6\u662f\u53f8\u9a6c\u8d85\u7fa4\u3002 \u3000\u3000\u53ea\u6709\u4ed6\u624d\u914d\u7528\u8fd9\u4e48\u6837\u4e00\u67c4\u5251\uff0c\u4e5f\u53ea\u6709\u4ed6\u624d\u80fd\u7528\u8fd9\u4e48\u6837\u4e00\u67c4\u5251\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u90fd\u77e5\u9053\uff0c\u53f8\u9a6c\u8d85\u7fa4\u5929\u751f\u795e\u529b\uff0c\u4e3e\u5343\u94a9\u5982\u4e3e\u8349\u82a5\u3002 \u3000\u3000\u5982\u679c\u4ed6\u7528\u7684\u4e0d\u662f\u8fd9\u4e48\u6837\u4e00\u67c4\u5251\uff0c\u5927\u5bb6\u90fd\u4f1a\u89c9\u5f97\u5f88\u5931\u671b\u7684\u3002 \u3000\u3000\u82f1\u96c4\u65e0\u654c\u7684\u53f8\u9a6c\u8d85\u7fa4\uff0c\u600e\u4e48\u80fd\u8ba9\u6c5f\u6e56\u8c6a\u6770\u5931\u671b\uff1f \u3000\u3000\u73b0\u5728\u4ed6\u4ece\u67d3\u4e0a\u53d6\u4e0b\u7684\u5251\u5374\u4e0d\u662f\u8fd9\u67c4\u53ef\u4ee5\u529b\u654c\u4e07\u5927\u7684\u5343\u9524\u5927\u94c1\u5251\u3002 \u3000\u3000\u4e07\u592b\u53ef\u654c\uff0c\u5353\u4e1c\u6765\u4e0d\u53ef\u3002 \u3000\u3000\u591a\u5e74\u6765\u4ed6\u4eec\u4e00\u76f4\u5e76\u80a9\u4f5c\u6218\uff0c\u4e00\u76f4\u662f\u751f\u6b7b\u4e0e\u5171\u7684\u670b\u53cb\uff0c\u4e0d\u662f\u4ec7\u654c\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u6bcf\u4e00\u6b21\u8f89\u714c\u7684\u80dc\u5229\uff0c\u5353\u4e1c\u6765\u90fd\u662f\u5728\u5e55\u540e\u7b56\u5212\u7684\u529f\u81e3\u3002 \u3000\u3000\u73b0\u5728\u7684\u60c5\u51b5\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u867d\u7136\u4ece\u672a\u4e0e\u5353\u4e1c\u6765\u4ea4\u624b\uff0c\u53ef\u662f\u4ed6\u77e5\u9053\u5353\u4e1c\u6765\u6bd4\u4ed6\u8fd9\u4e00\u751f\u4e2d\u6240\u9047\u5230\u7684\u4efb\u4f55\u4e00\u4e2a\u5bf9 \u624b\u90fd\u8981\u5f3a\u5f97\u591a\uff0c\u751a\u81f3\u6bd4\u4ed6\u8fd8\u8981\u5f3a\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u6709\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\u5353\u4e1c\u6765\u6bd4\u4ed6\u5f3a\uff0c\u4ed6\u51c6\u5907\u548c\u5353\u4e1c\u6765\u51b3\u4e00\u6b7b\u6218\u65f6\uff0c\u5df2\u7ecf\u51c6\u5907\u6b7b\u5728\u5353 \u4e1c\u6765\u7684\u5200\u4e0b\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u4e00\u6b21\u4ed6\u7528\u7684\u5e76\u4e0d\u662f\u90a3\u67c4\u5343\u9524\u5927\u94c1\u5251\uff0c\u56e0\u4e3a\u4ed6\u7edd\u4e0d\u80fd\u635f\u5931\u4efb\u4f55\u4e00\u4e2a\u53ef\u4ee5\u5728\u4e00\u77ac\u95f4\u5236 \u654c\u4f24\u4eba\u7684\u673a\u4f1a\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u4e00\u6b21\u4ed6\u7528\u7684\u4e5f\u662f\u4e00\u628a\u77ed\u5251\uff0c\u548c\u5353\u4e1c\u6765\u7684\u5200\u4e00\u6837\u77ed\u3001\u4e00\u6837\u950b\u5229\u3002 \u3000\u3000\u4ed6\u4eec\u7528\u7684\u5200\u5251\u4e5f\u50cf\u662f\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e00\u6837\uff0c\u4e5f\u662f\u4ece\u540c\u4e00\u4e2a\u7089\u4e2d\u953b\u70bc\u51fa\u6765\u7684\u3002 \u3000\u3000\u7089\u4e2d\u71c3\u70e7\u7740\u7684\u4e5f\u662f\u540c\u4e00\u79cd\u706b\uff1a\u80fd\u628a\u94c1\u70bc\u6210\u94a2\uff0c\u4e5f\u80fd\u4f7f\u4eba\u7531\u8f6f\u5f31\u53d8\u4e3a\u575a\u5f3a\u3002 \u3000\u3000\u540c\u4e00\u4e2a\u7089\uff0c\u540c\u4e00\u4e2a\u91dc\uff0c\u540c\u4e00\u79cd\u706b\u3002 \u3000\u3000\u8c01\u662f\u8c46\uff1f\u8c01\u662f\u7b95\uff1f \u516b \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u5982\u95ea\u7535\u822c\u51fb\u4e0b\u3002 \u3000\u3000\u8fd9\u662f\u53f8\u9a6c\u8d85\u7fa4\u5a01\u9707\u5929\u4e0b\u7684\u201c\u9739\u96f3\u4e5d\u5f0f\u201d\u4e2d\u6700\u5a01\u731b\u9738\u9053\u7684\u4e00\u7740\u201c\u5927\u9739\u96f3\u201d\uff0c\u6c5f\u6e56\u4e2d\u5df2\u4e0d\u77e5 \u6709\u591a\u5c11\u9ad8\u624b\u8d25\u5728\u4ed6\u8fd9\u4e00\u5251\u4e0b\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7528\u7684\u867d\u7136\u4e0d\u662f\u4ed6\u7684\u5927\u94c1\u5251\uff0c\u8fd9\u4e00\u5251\u51fb\u4e0b\u65f6\u7684\u5a01\u529b\u867d\u7136\u8981\u5dee\u4e00\u4e9b\uff0c\u53ef\u662f\u8fd9\u67c4\u77ed\u5251\u7684 \u950b\u5229\uff0c\u5df2\u53ef\u5f25\u8865\u5b83\u529b\u91cf\u7684\u4e0d\u8db3\uff0c\u5728\u8fd0\u7528\u65f6\u7684\u53d8\u5316\u4e5f\u66f4\u7075\u6d3b\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u53f8\u9a6c\u8d85\u7fa4\u8fd8\u662f\u4e0d\u8be5\u4f7f\u51fa\u8fd9\u4e00\u5251\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u662f\u4ee5\u5f3a\u51fb\u5f31\u7684\u5251\u6cd5\uff0c\u662f\u5728\u7b97\u51c6\u5bf9\u65b9\u5fc3\u5df2\u602f\u3001\u529b\u5df2\u7aed\uff0c\u7edd\u975e\u81ea\u5df1\u5bf9\u624b\u65f6\u624d\u80fd\u4f7f\u51fa\u7684 \u5251\u6cd5\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e00\u5251\u51fb\u51fa\uff0c\u529b\u5df2\u653e\u5c3d\uff0c\u5982\u679c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5c31\u5fc5\u5b9a\u4f1a\u88ab\u5bf9\u65b9\u6240\u4f24\u3002\u5176\u95f4\u51e0\u4e4e\u5b8c\u5168\u6ca1\u6709 \u4e00\u70b9\u9009\u62e9\u7684\u4f59\u5730\u3002 \u3000\u3000\u5bf9\u5353\u4e1c\u6765\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u4ed6\u600e\u4e48\u80fd\u4f7f\u51fa\u8fd9\u4e00\u5251\u6765\uff1f\u662f\u56e0\u4e3a\u4ed6\u4f4e\u4f30\u4e86\u5353\u4e1c\u6765\uff1f\u8fd8\u662f\u56e0\u4e3a\u4ed6 \u5bf9\u81ea\u5df1\u5927\u6709\u628a\u63e11 \u3000\u3000\u9ad8\u624b\u76f8\u4e89\uff0c\u65e0\u8bba\u662f\u4f4e\u4f30\u4e86\u5bf9\u65b9\uff0c\u8fd8\u662f\u9ad8\u4f30\u4e86\u81ea\u5df1\uff0c\u90fd\u540c\u6837\u662f\u4e0d\u53ef\u539f\u8c05\u7684\u9519\u8bef\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5e94\u8be5\u660e\u767d\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4ed6\u65e2\u4e0d\u4f1a\u4f4e\u4f30\u5353\u4e1c\u6765\uff0c\u4e5f\u4e0d\u4f1a\u9ad8\u4f30\u81ea\u5df1\uff0c\u4ed6\u4e00\u5411\u662f\u4e2a\u5f88\u4e0d\u5bb9\u6613\u72af\u9519\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4f7f\u51fa\u8fd9\u4e00\u5251\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u592a\u4e86\u89e3\u5353\u4e1c\u6765\u4e86\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4eba\u8c28\u614e\uff0c\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u5982\u679c\u6ca1\u6709\u5fc5\u80dc\u7684\u628a\u63e1\uff0c\u90fd\u4e0b\u4f1a\u51fa\u624b\uff0c\u51fa\u624b\u65f6\u6240\u7528\u7684 \u62db\u5f0f\uff0c\u4e5f\u4e00\u5b9a\u662f\u4e07\u65e0\u4e00\u5931\u7684\u62db\u5f0f\u3002 \u3000\u3000\u53ea\u8981\u5bf9\u65b9\u6709\u4e07\u5206\u4e4b\u4e00\u7684\u673a\u4f1a\u80fd\u4f24\u5bb3\u4ed6\uff0c\u4ed6\u5c31\u4e0d\u4f1a\u4f7f\u51fa\u90a3\u4e00\u62db\u6765\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u662f\u4ed6\u81ea\u5df1\u9020\u6210\u7684\u4e0d\u8d25\u7684\u82f1\u96c4\uff0c\u4ed6\u66fe\u7ecf\u773c\u770b\u8fc7\u65e0\u6570\u9ad8\u624b\u88ab\u65a9\u6740\u5728\u8fd9\u4e00\u5251\u4e0b\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7965\u8fd9\u4e2a\u4eba\u548c\u201c\u5927\u9739\u96f3\u201d\u8fd9\u4e00\u5251\uff0c\u5728\u4ed6\u5fc3\u91cc\u90fd\u65e0\u7591\u4f1a\u6709\u79cd\u5de8\u5927\u7684\u538b\u529b\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4ed6\u7684\u5f31\u70b9\u3002 \u3000\u3000\u4ed6\u7684\u5f31\u70b9\uff0c\u5c31\u662f\u53f8\u9a6c\u8d85\u7fa4\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u4e00\u5b9a\u8981\u628a\u63e1\u4f4f\u8fd9\u4e2a\u673a\u4f1a\uff0c\u53ea\u8981\u5353\u4e1c\u6765\u5728\u4ed6\u7684\u538b\u529b\u4e0b\u6709\u4e00\u70b9\u8fdf\u7591\u754f\u7f29\uff0c\u4ed6\u8fd9\u4e00\u5251 \u6795\u5fc5\u5c06\u6d1e\u7a7f\u5353\u4e1c\u6765\u7684\u5fc3\u810f\u3002 \u3000\u3000\u9ad8\u624b\u76f8\u4e89\uff0c\u751f\u6b7b\u80dc\u8d1f\u5f80\u5f80\u53ea\u4e0d\u8fc7\u662f\u4e00\u62db\u95f4\u7684\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5728\u4e00\u62db\u51fb\u51fa\u65f6\uff0c\u5c31\u5df2\u5c06\u6bcf\u4e00\u79cd\u60c5\u51b5\u90fd\u7b97\u597d\u4e86\u3002 \u3000\u3000\u2014\u2014\u5929\u65f6\uff0c\u5730\u5229\uff0c\u5bf9\u624b\u7684\u60c5\u7eea\u548c\u4f53\u529b\uff0c\u90fd\u5df2\u5728\u4ed6\u4eec\u7684\u8ba1\u7b97\u4e2d\u3002 \u3000\u3000\u53ef\u662f\u6bcf\u4e2a\u4eba\u90fd\u96be\u514d\u6709\u70b9\u9519\u7684\u65f6\u5019\uff0c\u53ea\u8981\u4ed6\u7684\u8ba1\u7b97\u6709\u5206\u6beb\u4e4b\u5dee\uff0c\u4ed6\u72af\u4e0b\u7684\u9519\u8bef\u5c31\u5fc5\u5c06\u4ee4\u4ed6 \u9057\u6068\u7ec8\u751f\u3002 \u4e5d \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u95ea\u7535\u822c\u51fb\u4e0b\u3002 \u3000\u3000\u5353\u4e1c\u6765\u6ca1\u6709\u72b9\u7591\uff0c\u6ca1\u6709\u754f\u7f29\uff0c\u4e5f\u6ca1\u6709\u88ab\u95ea\u7535\u822c\u7684\u7729\u76ee\u7684\u5251\u5149\u6240\u8ff7\u60d1\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u5728\u5149\u8292\u95ea\u52a8\u4e2d\u627e\u51fa\u4e86\u8fd9\u4e00\u5251\u7684\u5c16\u950b\u3002 \u3000\u3000\u5251\u7684\u5c16\uff0c\u5c31\u662f\u5251\u7684\u5fc3\u3002 \u3000\u3000\u5251\u52bf\u968f\u7740\u5c16\u950b\u800c\u53d8\u5316\uff0c\u8fd9\u79cd\u53d8\u5316\u5c31\u662f\u8fd9\u4e00\u5251\u7684\u547d\u8109\u3002 \u3000\u3000\u4ed6\u4e00\u5200\u65ad\u4e86\u8fd9\u4e00\u5251\u7684\u547d\u8109\u3002 \u3000\u3000\u6ee1\u5929\u95ea\u52a8\u7684\u5251\u5149\u9aa4\u7136\u6d88\u5931\uff0c\u5353\u4e1c\u6765\u7684\u5200\u950b\u5df2\u7ecf\u5728\u53f8\u9a6c\u5de6\u9888\u540e\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u95ea\u907f\u62db\u67b6\u53cd\u51fb\u7684\u4f59\u529b\uff0c\u524a\u94c1\u5982\u6ce5\u7684\u5200\u950b\u5728\u4e00\u77ac\u95f4\u5c31\u53ef\u4ee5\u5272\u4e0b\u4ed6\u7684\u5934\u9885\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u95ed\u4e0a\u773c\u775b\u7b49\u7740\u6328\u8fd9\u4e00\u5200\u3002\u4ed6\u7684\u773c\u775b\u91cc\u4e5f\u6ca1\u6709\u4e1d\u6beb\u60b2\u75db\u6028\u4ec7\u6050\u60e7\u4e4b\u610f\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u53f8\u9a6c\u8d85\u7fa4\u5c45\u7136\u663e\u5f97\u8fdc\u6bd4\u521a\u624d\u5e73\u9759\u5f97\u591a\u3002 \u3000\u3000\u5982\u679c\u4ed6\u521a\u624d\u4e00\u5251\u523a\u6740\u4e86\u5353\u4e1c\u6765\uff0c\u4e5f\u8bb8\u53cd\u800c\u6ca1\u6709\u6b64\u65f6\u8fd9\u4e48\u5e73\u9759\u3002 \u3000\u3000\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u773c\u4e2d\u4e5f\u6ca1\u6709\u4e1d\u6beb\u611f\u60c5\u3002 \u3000\u3000\u201c\u4f60\u9519\u4e86\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6240\u4ee5\u4f60\u8d25\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u6211\u8d25\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u4e00\u76f4\u90fd\u5f88\u60f3\u77e5\u9053\uff0c\u5982\u679c\u6211\u4eec\u4e24\u4e2a\u4eba\u4ea4\u624b\u4f1a\u6709\u4ec0\u4e48\u6837\u7684\u7ed3\u679c\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u5374\u4e0d\u60f3\u77e5\u9053\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u4e00\u76f4\u90fd\u4e0d\u76f8\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u8bf4\u4e0d\u51fa\u7684\u54c0\u4f24\uff0c\u53ef\u662f\u4ed6\u624b\u91cc\u7684\u5200\u5df2\u7ecf\u780d\u5728\u53f8\u9a6c\u8d85\u7fa4\u7684\u8116\u5b50\u4e0a\u3002 \u3000\u3000\u53ea\u6709\u5200\u5149\u4e00\u95ea\uff0c\u6ca1\u6709\u9c9c\u8840\u6e85\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u662f\u7528\u5200\u80cc\u780d\u4e0b\u53bb\u7684\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u8d70\uff0c\u65e2\u6ca1\u6709\u56de\u5934\uff0c\u4e5f\u6ca1\u6709\u518d\u770b\u53f8\u9a6c\u8d85\u7fa4\u4e00\u773c\u3002 \u3000\u3000\u53f8\u9a6c\u5fcd\u4e0d\u4f4f\u5636\u58f0\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6740\u6211\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u8fd8\u662f\u6ca1\u6709\u56de\u5934\uff0c\u53ea\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u56e0\u4e3a\u73b0\u5728\u4f60\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u3002\u201d \u7b2c\u5341\u516d\u7ae0\u3000\u9ad8\u5904\u4e0d\u80dc\u5bd2>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u5341\u516d\u7ae0\u3000\u9ad8\u5904\u4e0d\u80dc\u5bd2 \u4e00 \u3000\u3000\u4e8c\u6708\u5eff\u4e94\u4e09\u66f4\u524d\u540e\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u8fdc\u5904\u6709\u4eba\u5728\u6572\u66f4\uff0c\u4e09\u66f4\u3002 \u3000\u3000\u6bcf\u4e00\u591c\u90fd\u6709\u4e09\u66f4\uff0c\u6bcf\u4e00\u591c\u7684\u4e09\u66f4\u4eff\u4f5b\u90fd\u5e26\u7740\u79cd\u51c4\u51c9\u800c\u795e\u79d8\u7684\u7f8e\u3002 \u3000\u3000\u6bcf\u4e00\u591c\u7684\u4e09\u66f4\u4eff\u4f5b\u90fd\u662f\u8fd9\u4e00\u5929\u4e4b\u4e2d\u6700\u4ee4\u4eba\u9500\u9b42\u7684\u65f6\u5019\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5750\u62e5\u8c82\u88d8\uff0c\u6d45\u659f\u7f8e\u9152\uff0c\u5e94\u7740\u8fdc\u8fdc\u4f20\u6765\u7684\u66f4\u9f13\uff0c\u5728\u8fd9\u4e2a\u4ee4\u4eba\u9500\u9b42\u7684\u4e09\u66f4\u591c\u91cc\uff0c\u4ed6\u5e94 \u8be5\u53ef\u4ee5\u7b97\u662f\u957f\u5b89\u57ce\u91cc\u6700\u6109\u5feb\u7684\u4eba\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u5bf9\u624b\u90fd\u5df2\u88ab\u51fb\u8d25\uff0c\u4ed6\u8981\u505a\u7684\u4e8b\u90fd\u5df2\u5b8c\u6210\uff0c\u5f53\u4eca\u5929\u4e0b\uff0c\u8fd8\u6709\u8c01\u80fd\u4e0e\u4ed6\u4e89\u950b\uff1f \u3000\u3000\u53c8\u6709\u8c01\u77e5\u9053\u4ed6\u5fc3\u91cc\u662f\u4e0d\u662f\u771f\u7684\u6709\u522b\u4eba\u60f3\u8c61\u4e2d\u90a3\u4e48\u6109\u5feb\uff1f \u3000\u3000\u4ed6\u4e5f\u5728\u95ee\u81ea\u5df1\u3002 \u3000\u3000\u2014\u2014\u4ed6\u65e2\u7136\u4e0d\u6740\u53f8\u9a6c\uff0c\u4e3a\u4ec0\u4e48\u8981\u5c06\u53f8\u9a6c\u51fb\u8d25\uff1f\u4e3a\u4ec0\u4e48\u8981\u51fb\u8d25\u4ed6\u81ea\u5df1\u9020\u6210\u7684\u82f1\u96c4\u5076\u50cf\uff1f\u4ed6 \u81ea\u5df1\u662f\u4e0d\u662f\u4e5f\u548c\u5929\u4e0b\u82f1\u96c4\u540c\u6837\u5931\u671b\uff1f \u3000\u3000\u4ed6\u65e0\u6cd5\u56de\u7b54\u3002 \u3000\u3000\u2014\u2014\u4ed6\u65e2\u7136\u4e0d\u6740\u53f8\u9a6c\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7d22\u6027\u6210\u5168\u4ed6\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u6084\u7136\u800c\u53bb\uff1f \u3000\u3000\u5353\u4e1c\u6765\u4e5f\u65e0\u6cd5\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u53ea\u77e5\u9053\u90a3\u4e00\u5200\u7edd\u4e0d\u80fd\u7528\u5200\u950b\u780d\u4e0b\u53bb\uff0c\u7edd\u4e0d\u80fd\u8ba9\u53f8\u9a6c\u8d85\u7fa4\u6b7b\u5728\u4ed6\u624b\u91cc\uff1a\u6b63\u5982\u4ed6\u4e0d\u80fd\u4eb2\u624b \u6740\u6b7b\u81ea\u5df1\u4e00\u6837\u3002 \u3000\u3000\u5728\u67d0\u4e00\u65b9\u9762\u6765\u8bf4\uff0c\u4ed6\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u6709\u4e00\u90e8\u5206\u6eb6\u5165\u53f8\u9a6c\u8d85\u7fa4\u7684\u8eab\u4f53\u91cc\uff0c\u4ed6\u81ea\u5df1\u8eab\u4f53\u91cc\u4e5f\u4e00\u90e8 \u5206\u5df2\u7ecf\u88ab\u53f8\u9a6c\u8d85\u7fa4\u53d6\u4ee3\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u76f8\u4fe1\uff0c\u5c31\u7b97\u6ca1\u6709\u53f8\u9a6c\u8d85\u7fa4\uff0c\u4ed6\u4e5f\u4e00\u6837\u4f1a\u6d3b\u4e0b\u53bb\uff0c\u5927\u9556\u5c40\u4e5f\u4e00\u6837\u4f1a\u7ee7\u7eed\u5b58\u5728\u3002 \u3000\u3000\u559d\u5230\u7b2c\u56db\u676f\u65f6\uff0c\u5353\u4e1c\u6765\u7684\u5fc3\u60c5\u5df2\u7ecf\u771f\u7684\u6109\u5feb\u8d77\u6765\u4e86\uff0c\u4ed6\u51c6\u5907\u518d\u559d\u4e00\u676f\u5c31\u4e0a\u5e8a\u53bb\u7761\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4f38\u624b\u53bb\u5012\u8fd9\u676f\u9152\u65f6\uff0c\u4ed6\u7684\u5fc3\u5ffd\u7136\u6c89\u4e86\u4e0b\u53bb\uff0c\u77b3\u5b54\u5ffd\u7136\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u6446\u5728\u706f\u4e0b\u7684\u90a3\u53e3\u7bb1\u5b50\u5df2\u7ecf\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9644\u8fd1\u65e5\u591c\u90fd\u6709\u4eba\u5728\u8f6e\u73ed\u5b88\u536b\uff0c\u6ca1\u6709\u4eba\u80fd\u8f7b\u6613\u8d70\u8fdb\u4ed6\u8fd9\u680b\u5c0f\u5c4b\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u8fd9\u53e3\u5e73\u51e1\u9648 \u65e7\u7684\u7bb1\u5b50\u662f\u4ef6\u53ef\u6015\u7684\u79d8\u5bc6\u6b66\u5668\u3002 \u3000\u3000\u6709\u4ec0\u4e48\u4eba\u4f1a\u5192\u7740\u751f\u547d\u5371\u9669\u5230\u8fd9\u91cc\u62ff\u8d70\u4e00\u53e3\u7bb1\u5b50\uff1f \u3000\u3000\u201c\u6ce2\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5353\u4e1c\u6765\u624b\u91cc\u7684\u6c34\u6676\u676f\u5df2\u7c89\u788e\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u5f88\u53ef\u80fd\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\uff0c \u5ffd\u7136\u60f3\u5230\u4e86\u5353\u9752\u4e34\u6b7b\u524d\u7684\u8868\u60c5\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u542c\u89c1\u5916\u9762\u6709\u4eba\u5728\u6572\u95e8\u3002 \u3000\u3000\u201c\u8fdb\u6765\u3002\u201d \u3000\u3000\u4e00\u4e2a\u9ad8\u989d\u65b9\u8138\u5bbd\u80a9\u592a\u5b50\u7684\u5065\u58ee\u5c11\u5e74\uff0c\u7acb\u523b\u63a8\u95e8\u800c\u5165\uff0c\u8863\u7740\u6574\u6d01\u6734\u7d20\uff0c\u6001\u5ea6\u4e25\u8083\u8bda\u6073\u3002 \u3000\u3000\u5927\u9556\u5c40\u7684\u89c4\u6a21\u5e9e\u5927\uff0c\u7ec4\u7ec7\u4e25\u5bc6\uff0c\u6bcf\u4e00\u9879\u5de5\u4f5c\uff0c\u6bcf\u4e00\u6b21\u884c\u52a8\u90fd\u6709\u4eba\u5206\u5c42\u8d1f\u8d23\uff0c\u76f4\u63a5\u53d7\u4ee4\u4e8e \u5353\u4e1c\u6765\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u6240\u4ee5\u9556\u5c40\u91cc\u7684\u4f4e\u5c42\u5c5e\u4e0b\u80fd\u5f53\u9762\u89c1\u5230\u4ed6\u7684\u4eba\u4e5f\u4e0d\u591a\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4ee5\u524d\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u8fd9\u4e2a\u5e74\u8f7b\u4eba\uff0c\u53ef\u662f\u73b0\u5728\u7acb\u523b\u5c31\u731c\u51fa\u4ed6\u662f\u8c01\u4e86\u3002 \u3000\u3000\u201c\u90d1\u8bda\u3002\u201d\u5353\u4e1c\u6765\u6c89\u7740\u8138\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6700\u8fd1\u4e3a\u5353\u9752\u7acb\u8fc7\u529f\uff0c\u53ef\u662f\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u8fd9\u5730\u65b9\u4e0d \u662f\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u968f\u4fbf\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u5f1f\u5b50\u77e5\u9053\u3002\u201d\u90d1\u8bda\u606d\u8c28\u800c\u8bda\u6073\uff1a\u201c\u53ef\u662f\u5f1f\u5b50\u4e0d\u80fd\u4e0d\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e94\u4e2a\u6708\u524d\uff0c\u5353\u9752\u5df2\u5c06\u5f1f\u5b50\u62e8\u5728\u4ed6\u7684\u5c5e\u4e0b\uff0c\u7531\u4ed6\u76f4\u63a5\u6307\u6325\u4e86\u3002\u201d\u90d1\u8bda\u8bf4\uff1a\u201c\u6240\u4ee5\u4e0d\u7ba1\u4ed6 \u8981\u5f1f\u5b50\u505a\u4ec0\u4e48\uff0c\u5f1f\u5b50\u90fd\u4e0d\u6562\u6297\u547d\u3002\u201d \u3000\u3000\u201c\u662f\u5353\u9752\u8981\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u90d1\u8bda\u8bf4\uff1a\u201c\u6765\u66ff\u4ed6\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u201c\u66ff\u4ed6\u8bf4\u8bdd\uff1f\u201d\u5353\u4e1c\u672a\u5389\u58f0\u95ee\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u4f60\u6765\u66ff\u4ed6\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u6ca1\u6709\u6b7b\uff0c\u4f60\u5c31\u4e0b\u4f1a\u6765\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u90d1\u8bda\u5e73\u5e73\u9759\u9759\u7684\u8bf4\uff1a\u201c\u5982\u679c\u4ed6\u8fd8\u6d3b\u7740\uff0c\u5c31\u7b97\u628a\u5f1f\u5b50\u629b\u4e0b\u6cb9\u9505\uff0c\u4e5f\u4e0b\u4f1a\u628a\u4ed6\u8bf4 \u7684\u90a3\u4e9b\u8bdd\u6cc4\u9732\u4e00\u5b57\u3002\u201d \u3000\u3000\u201c\u4ed6\u8981\u4f60\u7b49\u4ed6\u6b7b\u4e86\u4e4b\u540e\u518d\u6765\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u90d1\u8bda\u9053\uff1a\u201c\u4ed6\u5429\u5490\u5f1f\u5b50\uff0c\u5982\u679c\u4ed6\u6b7b\u4e86\uff0c\u5c31\u8981\u5f1f\u5b50\u5728\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u5185\u6765\u89c1\u5353\u5148 \u751f\uff0c\u628a\u4ed6\u7684\u6d3b\u4e00\u5b57\u4e0d\u6f0f\u7684\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u53d1\u73b0\u8fd9\u4e2a\u4eba\u8bf4\u8bdd\u7684\u6001\u5ea6\u548c\u53e3\u6c14\uff0c\u51e0\u4e4e\u5c31\u50cf\u662f\u5353\u9752\u81ea\u5df1\u5728\u8bf4\u8bdd \u4e00\u6837\u3002 \u3000\u3000\u201c\u73b0\u5728\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d\u90d1\u8bda\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u5f1f\u5b50\u4e0d\u80fd\u4e0d\u6765\uff0c\u4e5f\u4e0d\u6562\u4e0d\u6765\u3002\u201d \u3000\u3000\u6c34\u6676\u676f\u7684\u788e\u7247\u72b9\u5728\u706f\u4e0b\u95ea\u7740\u5149\uff0c\u6bcf\u4e00\u7247\u788e\u7247\u770b\u6765\u90fd\u50cf\u662f\u5353\u9752\u4e34\u6b7b\u7684\u773c\u795e\u4e00\u6837\u3002 \u3000\u3000\u5353\u4e1c\u6765\u65e0\u7591\u53c8\u60f3\u8d77\u4e86\u4ed6\u4e34\u6b7b\u7684\u6001\u5ea6\uff0c\u8fc7\u4e86\u5f88\u4eba\u624d\u95ee\u90d1\u8bda\uff1a\u201c\u4ed6\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u5429\u5490\u4f60 \u7684\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u5728\u620d\u65f6\u524d\u540e\u3002\u201d \u3000\u3000\u201c\u620a\u65f6\u524d\u540e\uff1f\u201d\u5353\u4e1c\u6765\u7684\u77b3\u5b54\u518d\u6b21\u6536\u7f29\uff0c\u201c\u5f53\u7136\u662f\u5728\u620d\u65f6\u524d\u540e\u3002\u201d \u3000\u3000\u90a3\u65f6\u5019\u53f8\u9a6c\u8d85\u7fa4\u548c\u5353\u4e1c\u6765\u90fd\u5df2\u7ecf\u5230\u4e86\u90a3\u95f4\u575f\u5893\u822c\u7684\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u90a3\u65f6\u5019\u6b63\u662f\u5353\u9752\u53ef\u4ee5\u62bd\u7a7a\u53bb\u68b3\u6d17\u66f4\u8863\u7684\u65f6\u5019\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u5e76\u6ca1\u6709\u50cf\u5e73\u5e38\u4e00\u6837\u53bb\u505a\u8fd9\u4e9b\u4e8b\uff0c\u90a3\u65f6\u5019\u4ed6\u53bb\u505a\u7684\u4e8b\uff0c\u662f\u53ea\u80fd\u5728\u4ed6\u6b7b\u540e\u624d\u80fd\u8ba9\u5353 \u4e1c\u672a\u77e5\u9053\u7684\u4e8b\u3002 \u3000\u3000\u5353\u4e1c\u6765\u76ef\u7740\u90d1\u8bda\u3002 \u3000\u3000\u201c\u90a3\u65f6\u5019\u4ed6\u5c31\u5df2\u77e5\u9053\u4ed6\u5feb\u8981\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5927\u6982\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u201d\u90d1\u8bda\u8bf4\uff1a\u201c\u4ed6\u81ea\u5df1\u544a\u8bc9\u6211\uff0c\u4ed6\u5927\u6982\u5df2\u7ecf\u6d3b\u4e0d\u5230\u660e\u6668\u65e5\u51fa\u65f6\u3002\u201d \u3000\u3000\u201c\u4ed6\u6d3b\u5f97\u597d\u597d\u7684\uff0c\u600e\u4e48\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u77e5\u9053\u6709\u4e2a\u4eba\u51c6\u5907\u8981\u4ed6\u6b7b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u4f60\u3002\u201d\u90d1\u8bda\u76f4\u89c6\u5353\u4e1c\u6765\uff1a\u201c\u4ed6\u8bf4\u7684\u8fd9\u4e2a\u4eba\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u4f1a\u8981\u4ed6\u6b7b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e3a\u4f60\u505a\u7684\u4e8b\u5927\u591a\u4e86\uff0c\u77e5\u9053\u7684\u4e8b\u4e5f\u5927\u591a\u4e86\uff0c\u4f60\u7edd\u4e0d\u4f1a\u628a\u4ed6\u7559\u7ed9\u53f8\u9a6c\u8d85\u7fa4\u7684\u3002\u201d\u90d1 \u8bda\u8bf4\uff1a\u201c\u4ed6\u770b\u5f97\u51fa\u4f60\u548c\u53f8\u9a6c\u5df2\u7ecf\u5230\u4e86\u51b3\u88c2\u7684\u65f6\u5019\uff0c\u4e0d\u7ba1\u662f\u4e3a\u4e86\u53f8\u9a6c\u8fd8\u662f\u4e3a\u4e86\u4f60\u81ea\u5df1\uff0c\u4f60\u90fd\u4f1a \u5148\u5c06\u4ed6\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u201c\u4ed6\u65e2\u7136\u7b97\u5f97\u8fd9\u4e48\u51c6\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u9003\u8d70\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u6ca1\u6709\u65f6\u95f4\u4e86\uff0c\u4ed6\u60f3\u4e0d\u5230\u4e8b\u60c5\u4f1a\u53d1\u751f\u5f97\u8fd9\u4e48\u5feb\uff0c\u4ed6\u6839\u672c\u6765\u4e0d\u53ca\u51c6\u5907\u3002\u201d\u90d1\u8bda \u9053\uff1a\u201c\u53ef\u662f\u4f60\u548c\u53f8\u9a6c\u4ea4\u624b\u4e4b\u524d\uff0c\u4e00\u5b9a\u8981\u5148\u627e\u5230\u4ed6\uff0c\u5982\u679c\u53d1\u73b0\u4ed6\u5df2\u9003\u79bb\uff0c\u4e00\u5b9a\u4f1a\u5c06\u522b\u7684\u4e8b\u5168\u90fd \u653e\u4e0b\uff0c\u5168\u529b\u53bb\u8ffd\u6355\u4ed6\uff0c\u4ee5\u4ed6\u73b0\u5728\u7684\u529b\u91cf\uff0c\u8fd8\u9003\u4e0d\u8131\u4f60\u7684\u638c\u63e1\u3002\u201d \u3000\u3000\u201c\u5230\u90a3\u65f6\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e00\u6b7b\u800c\u5df2\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8bd5\u4e00\u8bd5\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5230\u4e86\u90a3\u65f6\u5019\uff0c\u53f8\u9a6c\u7684\u60b2\u6124\u53ef\u80fd\u5df2\u5e73\u606f\uff0c\u51b3\u5fc3\u4e5f\u53ef\u80fd\u5df2\u52a8\u6447\uff0c\u4ed6\u81ea\u5df1\u8fd8\u662f\u96be\u9003\u4e00 \u6b7b\uff0c\u4f60\u548c\u53f8\u9a6c\u53cd\u800c\u53ef\u80fd\u56e0\u6b64\u800c\u590d\u5408\u3002\u201d \u3000\u3000\u90d1\u8bda\u8bf4\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u8fd9\u79cd\u4e8b\u4ed6\u662f\u7edd\u4e0d\u4f1a\u505a\u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u63e1\u7d27\u53cc\u62f3\u3002 \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u5b81\u6b7b\u4e5f\u4e0d\u613f\u7ed9\u6211\u8fd9\u4e2a\u673a\u4f1a\uff0c\u5b81\u6b7b\u4e5f\u4e0d\u613f\u8ba9\u6211\u4e0e\u53f8\u9a6c\u590d\u5408\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u90d1\u8bda\u8bf4\uff1a\u201c\u56e0\u4e3a\u4f60\u4eec\u4e24\u4e2a\u4eba\u5408\u5219\u4e24\u5229\uff0c\u5206\u5219\u4e24\u8d25\uff0c\u4ed6\u8981\u66ff\u81ea\u5df1\u590d\u4ec7\uff0c\u8fd9\u6b21\u673a \u4f1a\u5c31\u662f\u4ed6\u552f\u4e00\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u7b11\uff1a\u201c\u4ed6\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8fd8\u80fd\u4e3a\u81ea\u5df1\u590d\u4ec7\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u90d1\u8bda\u8bf4\uff1a\u201c\u4ed6\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u6740\u4e86\u4ed6\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u8981\u4f60\u540e\u6094\u7684\uff0c\u56e0\u4e3a\u4ed6\u5728\u4e34\u6b7b \u4e4b\u524d\uff0c\u5df2\u7ecf\u66ff\u4f60\u6316\u597d\u4e86\u575f\u5893\uff0c\u4f60\u8fdf\u65e9\u603b\u6709\u4e00\u5929\u4f1a\u8eba\u8fdb\u53bb\u3002\u201d \u3000\u3000\u90d1\u8bda\u8bf4\uff1a\u201c\u4ed6\u8fd8\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u8fd9\u4e00\u5929\u4e00\u5b9a\u5f88\u5feb\u5c31\u4f1a\u6765\u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u76ef\u7740\u4ed6\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u73b0\u5728\u6211\u8fd8\u6ca1\u6709\u6b7b\uff0c\u8fd8\u662f\u5728\u4e3e\u624b\u95f4\u5c31\u53ef\u4ee5\u6b7b\u4e86 \u4f60\uff0c\u800c\u4e14\u8ba9\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5728\u6211\u9762\u524d\u8bf4\u8bdd\u600e\u6562\u5982\u6b64\u65e0\u793c\uff0c\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u4e9b\u8bdd\u4e0d\u662f\u6211\u8bf4\u7684\uff0c\u662f\u5353\u9752\u8bf4\u7684\u3002\u201d\u90d1\u8bda\u795e\u8272\u4e0d\u53d8\uff1a\u201c\u4ed6\u8981\u6211\u628a\u8fd9\u4e9b\u8bdd\u4e00\u5b57\u4e0d\u6f0f \u7684\u544a\u8bc9\u4f60\uff0c\u6211\u82e5\u5c11\u8bf4\u4e86\u4e00\u53e5\uff0c\u975e\u4f46\u65f6\u4f60\u4e0d\u5fe0\uff0c\u5bf9\u4ed6\u4e5f\u65e0\u4e49\u3002\u201d \u3000\u3000\u4ed6\u7684\u6001\u5ea6\u4e25\u8083\u800c\u8bda\u6073\uff1a\u201c\u73b0\u5728\u6211\u8fd8\u4e0d\u591f\u8d44\u683c\u505a\u4e00\u4e2a\u4e0d\u5fe0\u4e0d\u4e49\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4e0d\u591f\u8d44\u683c\uff1f\u201d\u5353\u4e1c\u6765\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u8981\u505a\u4e00\u4e2a\u4e0d\u5fe0\u4e0d\u4e49\u7684\u4eba\uff0c\u4e5f\u8981\u6709\u8d44\u683c\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u8981\u6709\u4ec0\u4e48\u6837\u7684\u8d44\u683c\u624d\u80fd\u505a\u4e00\u4e2a\u4e0d\u5fe0\u4e0d\u4e49\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u8981\u8ba9\u4eba\u867d\u7136\u660e\u77e5\u4ed6\u4e0d\u5fe0\u4e0d\u4e49\uff0c\u4e5f\u53ea\u80fd\u6068\u5728\u5fc3\u91cc\uff0c\u770b\u5230\u4ed6\u65f6\uff0c\u8fd8\u662f\u53ea\u80fd\u5bf9\u4ed6\u606d\u606d\u656c\u656c\uff0c \u4e0d\u6562\u6709\u4e1d\u6beb\u65e0\u672d\u3002\u201d\u90d1\u8bda\u8bf4\uff1a\u201c\u82e5\u662f\u6ca1\u6709\u8fd9\u6837\u7684\u8d44\u683c\u4e5f\u60f3\u505a\u4e00\u4e2a\u4e0d\u5fe0\u4e0d\u4e49\u7684\u4eba\uff0c\u90a3\u5c31\u771f\u7684\u8981 \u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u4e86\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u53c8\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u95ee\uff1a\u201c\u6211\u662f\u4e0d\u662f\u5df2\u7ecf\u6709\u8fd9\u6837\u7684\u8d44\u683c\uff1f\u201d \u3000\u3000\u90ed\u8bda\u6beb\u4e0d\u8003\u8651\u5c31\u56de\u7b54\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u8be5\u7b11\u7684\uff0c\u90ed\u8bda\u8bf4\u7684\u8bdd\u5e76\u4e0d\u597d\u7b11\uff0c\u6bcf\u53e5\u6d3b\u90fd\u4e0d\u597d\u7b11\uff0c\u4efb\u4f55\u4eba\u542c\u5230\u8fd9\u4e9b\u8bdd\u90fd\u4e0d\u4f1a\u7b11\u5f97\u51fa \u6765\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7b11\u4e86\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u5f97\u597d\uff0c\u8bf4\u5f97\u597d\u6781\uff0c\u201d\u5353\u4e1c\u6765\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5982\u679c\u5df2\u7ecf\u6709\u8d44\u683c\u505a\u4e00\u4e2a\u4e0d\u5fe0\u4e0d\u4e49\u7684 \u4eba\uff0c\u5929\u4e0b\u8fd8\u6709\u4ec0\u4e48\u4e8b\u80fd\u8ba9\u4ed6\u70e6\u607c\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u6ca1\u6709\u4e86\uff0c\u201d\u90d1\u8bda\u8bf4\u5f97\u5f88\u8bda\u6073\uff1a\u201c\u5982\u679c\u6709\u4e00\u5929\u6211\u4e5f\u80fd\u505a\u5230\u8fd9\u4e00\u6b65\uff0c\u6211\u4e5f\u4e0d\u4f1a\u518d\u6709\u4ec0 \u4e48\u70e6\u607c\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5c31\u597d\u597d\u7684\u53bb\u505a\u5427\u3002\u201d\u5353\u4e1c\u6765\u5c45\u7136\u8bf4\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u80fd\u505a\u5f97\u5230\u3002\u201d \u3000\u3000\u4ed6\u53c8\u7b11\u4e86\u7b11\uff1a\u201c\u6211\u76f8\u4fe1\u5353\u9752\u4e00\u5b9a\u4e5f\u7b97\u51c6\u4e86\u6211\u4e0d\u4f1a\u6740\u4f60\uff0c\u73b0\u5728\u6211\u6b63\u597d\u7528\u5f97\u7740\u4f60\u8fd9\u6837\u7684 \u4eba\u3002\u201d \u3000\u3000\u90d1\u8bda\u770b\u7740\u4ed6\uff0c\u773c\u4e2d\u5145\u6ee1\u5c0a\u656c\uff0c\u5c31\u597d\u50cf\u4ee5\u524d\u5353\u9752\u7684\u773c\u8272\u4e00\u6837\u3002 \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u201d\u90d1\u8bda\u8bf4\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u4eba\u5f88\u53ef\u80fd\u6bd4\u6211\u66f4\u6709\u7528\u3002\u201d \u3000\u3000\u201c\u63a8\uff1f\u201d \u3000\u3000\u201c\u9ad8\u6e10\u98de\u3002\u201d \u3000\u3000\u90d1\u8bda\u8bf4\uff1a\u201c\u4ed6\u4e00\u76f4\u5728\u7b49\u7740\u89c1\u4f60\uff0c\u6211\u8981\u4ed6\u8d70\uff0c\u4ed6\u5374\u4e00\u5b9a\u8981\u7b49\uff0c\u800c\u4e14\u8bf4\u4e0d\u7ba1\u7b49\u591a\u4e45\u90fd\u6ca1\u5173 \u7cfb\uff0c\u56e0\u4e3a\u4ed6\u53cd\u6b63\u4e5f\u6ca1\u6709\u4ec0\u4e48\u522b\u7684\u5730\u65b9\u53ef\u53bb\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u4eec\u5c31\u8ba9\u4ed6\u7b49\u5427\u3002\u201d\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u4e00\u4e2a\u4eba\u5728\u7b49\u4eba\u7684\u65f6\u5019\u603b\u662f\u6bd4\u8f83\u96be\u8fc7 \u4e9b\u7684\u3002\u6240\u4ee5\u6211\u4eec\u5bf9\u4ed6\u4e0d\u59a8\u597d\u4e00\u70b9\uff0c\u4ed6\u8981\u4ec0\u4e48\uff0c\u4f60\u5c31\u7ed9\u4ed6\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u90d1\u8bda\u6162\u6162\u7684\u9000\u4e0b\u53bb\uff0c\u597d\u50cf\u8fd8\u5728\u7b49\u7740\u5353\u4e1c\u6765\u95ee\u4ed6\u4ec0\u4e48\u8bdd\u3002 \u3000\u3000\u53ef\u662f\u5353\u4e1c\u6765\u4ec0\u4e48\u90fd\u6ca1\u6709\u518d\u95ee\uff0c\u800c\u4e14\u5df2\u7ecf\u95ed\u4e0a\u773c\u775b\uff0c\u4eff\u4f5b\u5df2\u7ecf\u7761\u7740\u4e86\u3002 \u3000\u3000\u5728\u706f\u4e0b\u770b\u6765\uff0c\u4ed6\u7684\u8138\u8272\u786e\u5b9e\u5f88\u75b2\u5026\uff0c\u82cd\u767d\u865a\u5f31\u800c\u75b2\u5026\u3002 \u3000\u3000\u4f46\u662f\u90d1\u8bda\u770b\u7740\u4ed6\u7684\u65f6\u5019\uff0c\u773c\u4e2d\u5374\u5145\u6ee1\u4e86\u656c\u754f\u4e4b\u610f\uff0c\u771f\u6b63\u4ece\u5fc3\u5e95\u53d1\u51fa\u7684\u5c0a\u656c\u548c\u754f\u60e7\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u7684\u786e\u662f\u8ddf\u522b\u4eba\u4e0d\u4e00\u6837\u7684\uff0c\u5bf9\u6bcf\u4ef6\u4e8b\u7684\u770b\u6cd5\u548c\u53cd\u5e94\u90fd\u548c\u522b\u4eba\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u90d1\u8bda\u9000\u51fa\u53bb\uff0c\u63a9\u4e0a\u95e8\uff0c\u51b7\u98ce\u5439\u5230\u4ed6\u8eab\u4e0a\u65f6\uff0c\u4ed6\u624d\u53d1\u73b0\u81ea\u5df1\u8fde\u88e4\u88c6\u90fd\u5df2\u88ab\u51b7\u6c57\u6e7f\u900f\u3002 \u4e8c \u3000\u3000\u5353\u4e1c\u6765\u7684\u786e\u548c\u4efb\u4f55\u4eba\u90fd\u4e0d\u4e00\u6837\u7684\u3002 \u3000\u3000\u522b\u4eba\u4e00\u5b9a\u4f1a\u4e3a\u67d0\u4e00\u4ef6\u4e8b\u60b2\u4f24\u6124\u6012\u65f6\uff0c\u4ed6\u5374\u7b11\u4e86\uff0c\u522b\u4eba\u4e00\u5b9a\u4f1a\u4e3a\u67d0\u4e00\u4ef6\u4e8b\u60ca\u5947\u5174\u594b\u65f6\uff0c\u4ed6 \u7684\u53cd\u5e94\u5374\u51b7\u6de1\u5f97\u51fa\u5947\uff0c\u751a\u81f3\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u9ad8\u6e10\u98de\u6765\u4e86\uff0c\u800c\u4e14\u6b63\u50cf\u4e00\u4e2a\u75f4\u60c5\u7684\u5c11\u5e74\u5728\u7b49\u5019\u60c5\u4eba\u4e00\u6837\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u9ad8\u6e10\u98de\u5251\u4e0a\u7684\u6cea\u75d5\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u53d8\u4e3a\u8840\u75d5\uff0c\u53ef\u80fd\u662f\u4ed6\u7684\u8840\uff0c\u4e5f\u53ef\u80fd\u662f\u4ed6\u4ec7\u654c\u7684 \u8840\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5374\u597d\u50cf\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u684c\u4e0a\u7684\u7bb1\u5b50\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff0c\u88ab\u5353\u9752\u5b89\u987f\u5728\u90a3\u5c0f\u9662\u4e2d\u7684\u7bb1\u5b50\u4e3b\u4eba\u5f88\u53ef\u80fd\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5353\u9752\u5df2\u7ecf\u51b3\u5fc3\u8981\u62a5\u590d\u3002 \u3000\u3000\u5982\u679c\u4ed6\u8981\u66ff\u5353\u4e1c\u6765\u627e\u4e00\u4e2a\u6700\u53ef\u6015\u7684\u4ec7\u654c\uff0c\u8427\u6cea\u8840\u65e0\u7591\u662f\u6700\u7406\u60f3\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u541b\u5b50\u9999\u5e76\u4e0d\u662f\u4e00\u79cd\u6c38\u8fdc\u89e3\u4e0d\u5f00\u7684\u8ff7\u836f\uff0c\u5982\u679c\u4e0d\u7ee7\u7eed\u4f7f\u7528\uff0c\u8427\u6cea\u8840\u7684\u529f\u529b\u5728\u4e09\u4e24\u5929\u4e4b\u5185\u5c31 \u53ef\u4ee5\u5b8c\u5168\u6062\u590d\u3002 \u3000\u3000\u90a3\u65f6\u5019\u5f88\u53ef\u80fd\u5c31\u662f\u5353\u4e1c\u6765\u7684\u6b7b\u671f\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u5353\u9752\u8fd8\u53ef\u4ee5\u4e3a\u4ed6\u505a\u5f88\u591a\u4e8b\uff0c\u5f88\u591a\u8981\u4ed6\u540e\u6094\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u7684\u5e10\u76ee\uff0c\u4ed6\u7684\u94b1\u8d22\uff0c\u4ed6\u7684\u4fe1\u672d\uff0c\u4ed6\u7684\u79d8\u5bc6\uff0c\u6bcf\u4e00\u6837\u90fd\u53ef\u80fd\u88ab\u5353\u9752\u51fa\u5356\uff0c\u4e0e\u4ed6\u4e0d\u5bf9\u7684\u90e8 \u5c5e\uff0c\u6bcf\u4e00\u4e2a\u4eba\u90fd\u53ef\u80fd\u88ab\u5353\u9752\u6240\u5229\u7528\u3002 \u3000\u3000\u2014\u2014\u5353\u9752\u4e34\u6b7b\u524d\uff0c\u4e3a\u4ed6\u6316\u597d\u7684\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u575f\u5893\uff1f \u3000\u3000\u5982\u679c\u8fd9\u79cd\u4e8b\u53d1\u751f\u5728\u522b\u4eba\u8eab\u4e0a\uff0c\u4e00\u5b9a\u4f1a\u7528\u5c3d\u4e00\u5207\u65b9\u6cd5\uff0c\u5728\u6700\u77ed\u7684\u65f6\u95f4\u91cc\u53bb\u67e5\u51fa\u6765\u3002 \u3000\u3000\u53ef\u662f\u5353\u4e1c\u6765\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u505a\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7761\u7740\u4e86\uff0c\u771f\u7684\u7761\u7740\u4e86\u3002 \u3000\u3000\u4ed6\u5148\u8d70\u8fdb\u4ed6\u7684\u5bdd\u5ba4\uff0c\u5173\u4e0a\u95e8\u7a97\uff0c\u5728\u5e8a\u5934\u67d0\u4e00\u4e2a\u79d8\u5bc6\u7684\u89d2\u843d\u91cc\u6309\u52a8\u4e86\u4e00\u4e2a\u79d8\u5bc6\u7684\u67a2\u7ebd\u3002 \u3000\u3000\u7136\u540e\u4ed6\u53c8\u5230\u90a3\u4e2a\u89d2\u843d\u91cc\u4e00\u4e2a\u6697\u67dc\u4e2d\uff0c\u62ff\u51fa\u4e86\u4e00\u4e2a\u9576\u7740\u73e0\u5b9d\u7684\u5c0f\u5323\u5b50\uff0c\u4ece\u5323\u5b50\u91cc\u62ff\u51fa\u4e00\u7c92 \u6de1\u7eff\u8272\u4e38\u836f\u541e\u4e0b\u53bb\uff0c\u4e00\u79cd\u53ef\u4ee5\u8ba9\u4ed6\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\u90fd\u5b89\u7136\u5165\u7761\u7684\u836f\u4e38\u3002 \u3000\u3000\u4ed6\u592a\u75b2\u5026\u3002 \u3000\u3000\u5728\u4e00\u6b21\u7279\u522b\u8f89\u714c\u7684\u80dc\u5229\u540e\uff0c\u603b\u662f\u4f1a\u8ba9\u4eba\u89c9\u5f97\u7279\u522b\u75b2\u5026\u7684\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u552f\u4e00\u80fd\u4f7f\u4eba\u771f\u6b63\u6062\u590d\u6e05\u9192\u7684\u4e8b\u5c31\u662f\u7761\u7720\u3002 \u3000\u3000\u751f\u6b7b\u80dc\u8d1f\u7684\u5173\u952e\u5f80\u5f80\u5c31\u51b3\u5b9a\u5728\u4e00\u77ac\u95f4\uff0c\u5728\u51b3\u5b9a\u8fd9\u79cd\u4e8b\u7684\u65f6\u5019\uff0c\u4e00\u5b9a\u8981\u7edd\u5bf9\u6e05\u9192\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u9700\u8981\u7761\u7720\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u6ca1\u6709\u4efb\u4f55\u4e8b\u6bd4\u8fd9\u4ef6\u4e8b\u66f4\u91cd\u8981\u3002 \u3000\u3000\u4e5f\u6ca1\u6709\u4efb\u4f55\u4eba\u6bd4\u5353\u4e1c\u6765\u66f4\u80fd\u5224\u65ad\u4e00\u4ef6\u4e8b\u7684\u5229\u5bb3\u8f7b\u91cd\u3002 \u3000\u3000\u5728\u4ed6\u4eba\u7761\u524d\uff0c\u4ed6\u53ea\u60f3\u5230\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u7684\u65e2\u4e0d\u662f\u6e17\u6b7b\u5728\u4ed6\u5200\u4e0b\u7684\u5353\u9752\uff0c\u4e5f\u4e0d\u662f\u968f\u65f6\u90fd\u53ef\u80fd\u6765\u53d6\u4ed6\u6027\u547d\u7684\u8427\u6cea\u8840\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u7684\u662f\u4ed6\u7684\u5144\u5f1f\uff0c\u90a3\u4e2a\u4e00\u751f\u4e0b\u6765\u5c31\u6b7b\u4e86\u7684\u5144\u5f1f\uff0c\u66fe\u7ecf\u548c\u4ed6\u5728\u6bcd\u80ce\u4e2d\u5171\u540c\u751f\u5b58\u4e86\u5341\u4e2a \u6708\uff0c\u66fe\u7ecf\u548c\u4ed6\u5171\u540c\u63a5\u53d7\u548c\u4e89\u593a\u8fc7\u6bcd\u80ce\u4e2d\u7cbe\u8840\u7684\u5144\u5f1f\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u89c1\u8fc7\u4ed6\u7684\u5144\u5f1f\uff0c\u4ed6\u7684\u5144\u5f1f\u5728\u4ed6\u7684\u5fc3\u91cc\u6c38\u8fdc\u90fd\u53ea\u4e0d\u8fc7\u662f\u4e2a\u6a21\u7cca\u6726\u80e7\u7684\u5f71\u5b50\u800c\u5df2\u3002 \u3000\u3000\u53ef\u662f\u5728\u4ed6\u5165\u7761\u65f6\u90a3\u4e00\u77ac\u6726\u80e7\u865a\u5e7b\u95f4\uff0c\u8fd9\u4e2a\u6a21\u7cca\u7684\u5f71\u5b50\u5ffd\u7136\u53d8\u6210\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u53ef\u4ee5\u770b\u5f97\u5f88 \u6e05\u695a\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4eff\u4f5b\u5c31\u662f\u53f8\u9a6c\u8d85\u7fa4\u3002 \u4e09 \u3000\u3000\u8fdc\u5904\u6709\u4eba\u5728\u6253\u66f4\uff0c\u5df2\u8fc7\u4e09\u66f4\u3002 \u3000\u3000\u90a3\u4e48\u5355\u8c03\u7684\u66f4\u9f13\u58f0\uff0c\u5374\u53c8\u90a3\u4e48\u51c4\u51c9\u90a3\u4e48\u65e0\u60c5\uff0c\u5230\u4e86\u4e09\u66f4\u65f6\uff0c\u8c01\u4e5f\u4f11\u60f3\u5c06\u5b83\u7559\u5728\u4e8c\u66f4\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8bb0\u5f97\u4ed6\u5219\u624d\u8fd8\u542c\u89c1\u6709\u4eba\u5728\u6572\u66f4\u7684\uff0c\u4ed6\u8bb0\u5f97\u521a\u624d\u542c\u5230\u6572\u7684\u660e\u660e\u662f\u4e8c\u66f4\u3002 \u3000\u3000\u4ed6\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u90a3\u65f6\u5019\u4ed6\u867d\u7136\u5df2\u7ecf\u559d\u4e86\u9152\uff0c\u53ef\u662f\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u559d\u4e86\u4e03\u516b\u65a4\u800c\u5df2\uff0c\u867d\u7136\u5df2\u7ecf\u6709\u4e86\u70b9\u8f7b\u98d8\u98d8 \u7684\u611f\u89c9\uff0c\u53ef\u662f\u5934\u8111\u8fd8\u662f\u6e05\u695a\u5f97\u5f88\u3002 \u3000\u3000\u4ed6\u6e05\u6e05\u695a\u695a\u7684\u8bb0\u5f97\uff0c\u90a3\u65f6\u5019\u4ed6\u6b63\u5728\u4e00\u5bb6\u6d3b\u89c1\u9b3c\u7684\u5c0f\u9152\u94fa\u91cc\u559d\u9152\uff0c\u9664\u4e86\u4ed6\u5916\uff0c\u65c1\u8fb9\u8fd8\u6709\u4e00 \u5927\u684c\u5ba2\u4eba\uff0c\u90fd\u662f\u4e9b\u5341\u516b\u4e5d\u5c81\u7684\u5c0f\u4f19\u5b50\uff0c\u6402\u7740\u4e94\u516d\u4e2a\u81f3\u5c11\u6bd4\u4ed6\u4eec\u5927\u4e00\u500d\u7684\u5973\u4eba\u5728\u5927\u58f0\u5439\u725b\u3002 \u3000\u3000\u4ed6\u4eec\u5439\u7684\u662f\u53f8\u9a6c\u8d85\u7fa4\u3002\u6bcf\u4e2a\u4eba\u90fd\u628a\u53f8\u9a6c\u8d85\u7fa4\u6367\u6210\u662f\u4e2a\u5929\u4e0a\u5c11\u6709\u5730\u4e0b\u65e0\u53cc\u7684\u5927\u82f1\u96c4\uff0c\u800c\u4e14 \u591a\u591a\u5c11\u5c11\u8ddf\u4ed6\u4eec\u6709\u70b9\u4ea4\u60c5\u3002 \u3000\u3000\u5439\u7684\u4eba\u5439\u5f97\u5f88\u9ad8\u5174\uff0c\u542c\u7684\u4eba\u4e5f\u542c\u5f97\u5f88\u5f00\u5fc3\u3002 \u3000\u3000\u552f\u4e00\u53ea\u6709\u4e00\u4e2a\u4eba\u65e2\u4e0d\u9ad8\u5174\u4e5f\u4e0d\u5f00\u5fc3\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u53f8\u9a6c\u8d85\u7fa4\u81ea\u5df1\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5c31\u62fc\u547d\u559d\u9152\u3002 \u3000\u3000\u4ed6\u4e5f\u6e05\u6e05\u695a\u695a\u7684\u8bb0\u5f97\uff0c\u5c31\u5728\u522b\u4eba\u5439\u5f97\u6700\u9ad8\u5174\u7684\u65f6\u5019\uff0c\u4ed6\u5ffd\u7136\u7ad9\u8d77\u62cd\u7740\u684c\u5b50\u5927\u9a82\uff1a\u201c\u53f8\u9a6c \u8d85\u7fa4\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u4ed6\u6839\u672c\u5c31\u4e0d\u662f\u4e2a\u4e1c\u897f\uff0c\u6839\u672c\u5c31\u4e0d\u662f\u4eba\uff0c\u8fde\u4e00\u6587\u90e8\u4e0d\u503c\uff0c\u8fde\u4e2a\u5c41\u90fd\u6bd4\u4e0d \u4e0a\u3002\u201d \u3000\u3000\u4ed6\u8d8a\u9a7e\u8d8a\u9ad8\u5174\uff0c\u522b\u4eba\u5374\u542c\u5f97\u4e0d\u9ad8\u5174\u4e86\uff0c\u6709\u4e2a\u4eba\u5ffd\u7136\u628a\u684c\u4e8e\u4e00\u7ffb\uff0c\u5341\u6765\u4e2a\u5c0f\u4f19\u5b50\u5c31\u4e00\u8d77\u51b2 \u4e86\u8fc7\u6765\uff0c\u4ed6\u597d\u50cf\u628a\u5176\u4e2d\u4e00\u4e2a\u4eba\u7684\u4e00\u4e2a\u9f3b\u5b50\u6253\u6210\u4e86\u4e24\u4e2a\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u53f8\u9a6c\u8d85\u7fa4\u90fd\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u6bd4\u6700\u7528\u529f\u7684\u5c0f\u5b66\u7ae5\u8bb0\u5343\u5b57\u6587\u8bb0\u5f97\u8fd8\u6e05\u695a\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fd8\u8bb0\u5f97\u5176\u4e2d\u6709\u4e2a\u8138\u4e0a\u80ed\u8102\u6d82\u5f97\u5c31\u597d\u50cf\u67d0\u79cd\u4f1a\u722c\u6811\u7684\u755c\u7272\u7684\u67d0\u4e00\u90e8\u4efd\u4e00\u6837\u7684\u5973\u4eba\uff0c \u5c31\u8131\u4e0b\u811a\u4e0a\u7a7f\u7684\u6728\u5c50\u6765\u6572\u4ed6\u7684\u5934\u3002 \u3000\u3000\u53ef\u662f\u4ee5\u540e\u7684\u4e8b\u60c5\uff0c\u4ed6\u5c31\u5168\u4e0d\u8bb0\u5f97\u4e86\u3002 \u3000\u3000\u90a3\u65f6\u5019\u4ed6\u6e05\u6e05\u695a\u695a\u7684\u542c\u89c1\u6572\u7684\u662f\u4e8c\u66f4\uff0c\u73b0\u5728\u5374\u5df2\u7ecf\u8fc7\u4e09\u66f4\u3002 \u3000\u3000\u90a3\u65f6\u5019\u4ed6\u8fd8\u5750\u5728\u4e00\u5bb6\u6d3b\u89c1\u9b3c\u7684\u5c0f\u9152\u94fa\u91cc\u559d\u9152\uff0c\u73b0\u5728\u5374\u5df2\u7ecf\u8eba\u4e86\u4e0b\u53bb\uff0c\u8eba\u5728\u4e00\u4e2a\u65e2\u6ca1\u6709\u6768 \u67f3\u5cb8\u4e5f\u6ca1\u6709\u6653\u98ce\u6b8b\u6708\u7684\u6697\u5df7\u4e2d\uff0c\u4e00\u4e2a\u5934\u53d8\u5f97\u6709\u5e73\u65f6\u516b\u4e2a\u90a3\u4e48\u91cd\uff0c\u5589\u5499\u4e5f\u53d8\u5f97\u597d\u50cf\u662f\u4e2a\u5927\u53a8\u623f \u91cc\u7684\u70df\u56f1\uff0c\u800c\u4e14\u5168\u8eab\u53c8\u9178\u53c8\u75db\uff0c\u5c31\u597d\u50cf\u521a\u88ab\u4eba\u5f53\u4f5c\u4e86\u4e00\u6761\u7834\u88e4\u5b50\u4e00\u6837\u5728\u6413\u677f\u4e0a\u6413\u6d17\u8fc7\u3002 \u3000\u3000\u2014\u2014\u90a3\u4e2a\u80d6\u5973\u4eba\u7684\u7ea2\u6f06\u6728\u5c50\u7a76\u7adf\u6709\u6ca1\u6709\u6572\u5728\u4ed6\u7684\u5934\u4e0a\uff1f \u3000\u3000\u2014\u2014\u4ed6\u662f\u600e\u4e48\u5230\u8fd9\u91cc\u6765\u7684\uff1f \u3000\u3000\u2014\u2014\u5728\u8fd9\u6bb5\u65f6\u5019\u91cc\uff0c\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5b8c\u5168\u4e0d\u8bb0\u5f97\u4e86\u3002 \u3000\u3000\u8fd9\u6bb5\u65f6\u5019\u7adf\u4f3c\u5b8c\u5168\u53d8\u6210\u4e86\u4e00\u65e6\u7a7a\u767d\uff0c\u5c31\u597d\u50cf\u4e00\u672c\u4e66\u91cc\u6709\u4e00\u9875\u88ab\u4eba\u6495\u6389\u4e86\u4e00\u6837\u3002 \u56db \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u60f3\u6323\u624e\u7740\u7ad9\u8d77\u6765\u7684\u65f6\u5019\uff0c\u624d\u53d1\u73b0\u8fd9\u6761\u6697\u5df7\u91cc\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u6b63\u5728\u7528\u4e00\u79cd\u5f88\u5947 \u602a\u7684\u773c\u795e\u770b\u7740\u4ed6\uff0c\u597d\u50cf\u6b63\u5728\u95ee\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u771f\u7684\u5c31\u662f\u90a3\u4e2a\u5929\u4e0b\u65e0\u53cc\u7684\u82f1\u96c4\u53f8\u9a6c\u8d85\u7fa4\uff1f\u4f60\u600e\u4e48\u4f1a\u53d8\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u51b3\u5fc3\u4e0d\u7406\u4ed6\uff0c\u51b3\u5fc3\u88c5\u4f5c\u6ca1\u6709\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u53ef\u662f\u8fd9\u4e2a\u4eba\u5374\u51b3\u5fc3\u4e00\u5b9a\u8981\u8ba9\u4ed6\u770b\u89c1\uff0c \u4e0d\u4f46\u7acb\u523b\u8d70\u4e86\u8fc7\u6765\uff0c\u8fd8\u6400\u8d77\u4e86\u4ed6\u7684\u81c2\u3002 \u3000\u3000\u4ed6\u672c\u6765\u8d39\u4e86\u5927\u529b\u6c14\u8fd8\u65e0\u6cd5\u7ad9\u8d77\uff0c\u53ef\u662f\u73b0\u5728\u4e00\u4e0b\u5c31\u7ad9\u8d77\u6765\u4e86\uff0c\u800c\u4e14\u7ad9\u5f97\u7b14\u633a\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5374\u8fd8\u662f\u4e0d\u80af\u653e\u5f00\u4ed6\uff0c\u773c\u795e\u91cc\u5145\u6ee1\u540c\u60c5\u548c\u54c0\u4f24\uff1a\u201c\u8001\u603b\uff0c\u4f60\u9189\u4e86\uff0c\u8ba9\u6211\u6276\u7740\u4f60\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u8bf4\uff1a\u201c\u6211\u662f\u963f\u6839\uff0c\u8001\u603b\uff0c\u4f60\u96be\u9053\u8fde\u963f\u6839\u90fd\u4e0d\u8ba4\u5f97\u4e86\uff1f\u201d \u3000\u3000\u201c\u963f\u6839\u201d\uff1f\u8fd9\u4e2a\u540d\u5b57\u597d\u719f\u3002 \u3000\u3000\u53ea\u6709\u5728\u4ed6\u521d\u51fa\u9053\u65f6\u5c31\u8ddf\u7740\u4ed6\u7684\u4eba\u624d\u4f1a\u79f0\u4ed6\u4e3a\u201c\u8001\u603b\u201d\u3002 \u3000\u3000\u53f8\u9a6c\u5ffd\u7136\u7528\u529b\u4e00\u62cd\u8fd9\u4e2a\u4eba\u7684\u80a9\uff0c\u7528\u529b\u63e1\u7740\u4ed6\u7684\u81c2\uff0c\u5f00\u6000\u5927\u7b11\u3002 \u3000\u3000\u201c\u597d\u5c0f\u5b50\uff0c\u8fd9\u51e0\u5e74\u4f60\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u5a36\u4e86\u8001\u5a46\u6ca1\u6709\uff1f\u6709\u6ca1\u6709\u628a\u8001\u5a46\u8f93\u6389\uff1f\u201d \u3000\u3000\u963f\u6839\u4e5f\u7b11\u4e86\uff0c\u773c\u4e2d\u5374\u4f3c\u6709\u70ed\u6cea\u5c06\u8981\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u8001\u603b\u5c45\u7136\u8fd8\u8bb0\u5f97\u6211\u8fd9\u4e2a\u8d4c\u9b3c\u3002\u5c45\u7136\u8fd8\u8ba4\u5f97\u6211\u8fd9\u4e2a\u6ca1\u51fa\u606f\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8d4c\u9b3c\uff0c\u6211\u4eec\u4e24\u4e2a\u4e00\u6837\u6ca1\u51fa\u606f\u3002\u201d\u4ed6\u62c9\u4f4f\u963f\u6839\uff1a\u201c\u8d70\uff0c\u6211\u4eec\u518d\u627e\u4e2a\u5730\u65b9\u559d\u9152\u53bb\u3002\u201d \u3000\u3000\u201c\u8001\u603b\uff0c\u4f60\u4e0d\u80fd\u518d\u559d\u4e86\uff0c\u201d\u963f\u6839\u8bf4\uff1a\u201c\u8981\u662f\u4f60\u521a\u624d\u6ca1\u6709\u628a\u6700\u540e\u90a3\u534a\u7f38\u9152\u4e00\u4e0b\u5b50\u559d\u4e0b\u53bb\uff0c \u90a3\u4e9b\u5c0f\u738b\u516b\u86cb\u600e\u4e48\u78b0\u5f97\u5230\u8001\u603b\u4f60\u4e00\u6839\u6c57\u6bdb\uff1f\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u7529\u4e5f\u5145\u6ee1\u60b2\u4f24\uff0c\u201c\u8001\u603b\uff0c\u8981\u4e0d\u662f\u56e0\u4e3a\u4f60\u559d\u5f97\u5168\u8eab\u90fd\u8f6f\u4e86\uff0c\u600e\u4e48\u4f1a\u88ab\u90a3\u4e9b\u5c0f\u738b\u516b \u86cb\u63cd\u6210\u8fd9\u6837\u5b50\uff1f\u8fde\u5934\u4e0a\u90fd\u88ab\u90a3\u6761\u80d6\u6bcd\u72d7\u7528\u6728\u5c50\u6253\u4e86\u4e2a\u6d1e\u3002\u201d \u3000\u3000\u963f\u6839\u8bf4\uff1a\u201c\u90a3\u4e9b\u5154\u5d3d\u5b50\u5e73\u65f6\u53ea\u8981\u542c\u5230\u8001\u603b\u7684\u540d\u5b57\uff0c\u8fde\u5c3f\u90fd\u4f1a\u88ab\u5413\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u6211\u521a\u624d\u771f\u7684\u6328\u4e86\u63cd\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u5b9e\u5728\u6709\u70b9\u4e0d\u4fe1\uff0c\u53ef\u662f\u6478\u4e86\u6478\u81ea\u5df1\u7684\u5934\u548c\u808b\u9aa8\u4e4b\u540e\uff0c\u5c31\u4e0d\u80fd\u4e0d\u4fe1\u4e86\u3002 \u3000\u3000\u201c\u770b\u6837\u5b50\u6211\u662f\u771f\u7684\u6328\u4e86\u63cd\u3002\u201d\u4ed6\u5ffd\u7136\u5927\u7b11\uff1a\u201c\u597d\uff0c\u63cd\u5f97\u597d\uff0c\u63cd\u5f97\u75db\u5feb\uff0c\u60f3\u4e0d\u5230\u6328\u63cd\u5c45\u7136 \u662f\u4ef6\u8fd9\u4e48\u75db\u5feb\u7684\u4e8b\uff0c\u597d\u51e0\u5341\u5e74\u6211\u90fd\u6ca1\u6709\u8fd9\u4e48\u75db\u5feb\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8001\u603b\u4e5f\u6ca1\u6709\u8ba9\u4ed6\u4eec\u5360\u5230\u4ec0\u4e48\u4fbf\u5b9c\uff0c\u4e5f\u628a\u90a3\u4e9b\u5c0f\u738b\u516b\u86cb\u75db\u6253\u4e86\u4e00\u987f\uff0c\u6253\u5f97\u5c31\u50cf\u91ce\u72d7 \u4e00\u6837\u6ee1\u5730\u4e71\u722c\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u597d\u73a9\u4e86\u3002\u201d\u53f8\u9a6c\u5c45\u7136\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6211\u5b9e\u5728\u4e0d\u8be5\u63cd\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u63cd\u6211\uff1f\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u628a\u4ed6\u4eec\u5fc3\u76ee\u4e2d\u7684\u5927\u82f1\u96c4\u53f8\u9a6c\u8d85\u7fa4 \u9a82\u5f97\u72d7\u8840\u6dcb\u5934\uff0c\u4e00\u6587\u4e0d\u503c\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5927\u7b11\uff1a\u201c\u53f8\u9a6c\u8d85\u7fa4\u4e3a\u4e86\u5927\u9a82\u81ea\u5df1\u800c\u88ab\u75db\u6253\uff0c\u8fd9\u4ef6\u4e8b\u82e5\u662f\u8ba9\u5929\u4e0b\u82f1\u96c4\u77e5\u9053\uff0c\u4e0d\u628a\u90a3\u4e9b \u738b\u516b\u86cb\u7b11\u5f97\u6ee1\u5730\u627e\u7259\u624d\u602a\u3002\u201d \u3000\u3000\u963f\u6839\u5374\u7b11\u4e0d\u51fa\u6765\uff0c\u53ea\u662f\u5583\u5583\u7684\u8bf4\uff1a\u201c\u8981\u662f\u5353\u5148\u751f\u5728\u65c1\u8fb9\uff0c\u8001\u603b\u5c31\u4e0d\u4f1a\u559d\u9189\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u538b\u4f4e\u58f0\u97f3\u95ee\uff1a\u201c\u5353\u5148\u751f\u5462\uff1f\u8fd9\u6b21\u4e3a\u4ec0\u4e48\u6ca1\u8ddf\u8001\u603b\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8ddf\u6211\u5728\u4e00\u8d77\uff1f\u201d\u53f8\u9a6c\u4e0d\u505c\u7684\u7b11\uff1a\u201c\u4ed6\u662f\u4ed6\uff0c\u6211\u662f\u6211\uff0c\u4ed6\u624d\u662f\u771f\u6b63\u7684\u5927\u82f1 \u96c4\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u72d7\u718a\u800c\u5df2\uff0c\u4ed6\u6ca1\u6709\u628a\u6211\u7684\u8111\u888b\u780d\u4e0b\u6765\u5f53\u591c\u58f6\uff0c\u5df2\u7ecf\u5f88\u5bf9\u5f97\u8d77\u6211\u4e86\u3002\u201d \u3000\u3000\u963f\u6839\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5bf8\u989e\u98a5\u7740\u95ee\uff1a\u201c\u96be\u9053\u5353\u5148\u751f\u4e5f\u53cd\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u53cd\u4e86\uff1f\u4ed6\u53cd\u4ec0\u4e48\uff1f\u2019\u53f8\u9a6c\u8fd8\u5728\u7b11\uff1a\u201c\u5927\u9556\u5c40\u672c\u6765\u5c31\u662f\u4ed6\u7684\uff0c\u6211\u7b97\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u963f\u6839\u770b\u7740\u4ed6\uff0c\u773c\u6cea\u7ec8\u4e8e\u6d41\u4e0b\uff0c\u5ffd\u7136\u8dea\u4e0b\u6765\uff0c\u201c\u549a\u549a\u4e1c\u201d\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\u3002 \u3000\u3000\u201c\u963f\u6839\u8be5\u6b7b\uff0c\u963f\u6839\u5bf9\u4e0d\u8d77\u8001\u603b\u3002\u201d \u3000\u3000\u201c\u4f60\u6ca1\u6709\u5bf9\u4e0d\u8d77\u6211\uff0c\u5929\u4e0b\u53ea\u6709\u4e00\u4e2a\u4eba\u5bf9\u4e0d\u8d77\u6211\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6709\u4e9b\u4e8b\u8001\u603b\u8fd8\u4e0d\u77e5\u9053\uff0c\u963f\u6839\u5b81\u613f\u88ab\u8001\u603b\u6253\u6b7b\uff0c\u4e5f\u8981\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u963f\u6839\u6ca1\u6709\u8ddf\u5728\u8001\u603b\u8eab\u8fb9\uff0c\u53ea\u56e0\u4e3a\u5353\u5148\u751f\u4e00\u5b9a\u8981\u6d3e\u6211\u5230\u6d1b\u9633\u96c4\u72ee\u5802\u53bb\u5367\u5e95\uff0c \u800c\u4e14\u8fd8\u8981\u6211\u7792\u7740\u8001\u603b\u3002\u201d\u963f\u6839\u8bf4\uff1a\u201c\u5353\u5148\u751f\u77e5\u9053\u8001\u603b\u4e00\u5411\u662f\u4e2a\u5149\u660e\u78ca\u843d\u7684\u4eba\uff0c\u8fd9\u79cd\u4e8b\u4e00\u5411\u90fd \u4e0d\u8ba9\u8001\u603b\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6b63\u597d\u6211\u4e5f\u4e0d\u60f3\u77e5\u9053\uff0c\u201d\u53f8\u9a6c\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff1a\u201c\u6731\u731b\u90a3\u4e2a\u6df7\u5c0f\u5b50\u5927\u6982\u4e5f\u4e0d\u4f1a\u77e5\u9053\u4ed6\u624b\u4e0b \u7a76\u7adf\u6709\u591a\u5c11\u4eba\u662f\u5353\u4e1c\u6765\u6d3e\u53bb\u7684\uff0c\u4ed6\u5927\u6982\u4e5f\u8ddf\u6211\u4e00\u6837\uff0c\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u6df7\u86cb\u3002\u201d \u3000\u3000\u963f\u6839\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff0c\u773c\u775b\u91cc\u5ffd\u7136\u6709\u79cd\u5947\u602a\u7684\u5149\u8292\u95ea\u52a8\uff0c\u5ffd\u7136\u95ee\u53f8\u9a6c\uff1a\u201c\u8001\u603b\u60f3\u4e0d\u60f3 \u53bb\u89c1\u90a3\u4e2a\u6df7\u86cb\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u7684\u773c\u775b\u91cc\u4e5f\u95ea\u51fa\u4e86\u5149\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u54ea\u4e2a\u6df7\u86cb\uff1f\u201d\u4ed6\u63d0\u9ad8\u4e86\u55d3\u95e8\u95ee\uff1a\u201c\u662f\u4e0d\u662f\u8ddf\u6211\u4e00 \u6837\u7684\u90a3\u4e2a\u6df7\u86cb\u6731\u731b\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u201d\u53f8\u9a6c\u53c8\u95ee\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u4ed6\u76ef\u7740\u963f\u6839\uff1a\u201c\u96be\u9053\u4f60\u4e5f\u662f\u8fd9\u6b21\u8ddf\u7740\u4ed6\u6765\u6b7b\u7684\u90a3\u516b\u5341\u516d\u4e2a\u4eba\u5176\u4e2d\u4e4b \u3000\u3000\u963f\u6839\u53c8\u8dea\u4e0b\uff1a\u201c\u963f\u6839\u8be5\u6b7b\uff0c\u963f\u6839\u5bf9\u4e0d\u8d77\u8001\u603b\uff0c\u53ef\u662f\u6731\u731b\u5b9e\u5728\u4e5f\u8ddf\u8001\u603b\u4e00\u6837\uff0c\u662f\u6761\u6709\u8840\u6027 \u6709\u4e49\u6c14\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u963f\u6839\u5b9e\u5728\u4e0d\u5fcd\u5728\u8fd9\u65f6\u5019\u518d\u51fa\u5356\u4ed6\u4e86\uff0c\u6240\u4ee5\u963f\u6839\u8fd9\u6b21\u6765\uff0c\u4e5f\u5df2\u7ecf\u51c6\u5907\u966a\u4ed6 \u6b7b\u5728\u957f\u5b89\u3002\u201d \u3000\u3000\u4ed6\u4ee5\u5934\u78b0\u5730\uff0c\u6ee1\u9762\u6d41\u8840\uff1a\u201c\u963f\u6839\u8be5\u6b7b\uff0c\u963f\u6839\u867d\u7136\u80cc\u53db\u4e86\u5927\u9556\u5c40\uff0c\u53ef\u662f\u5fc3\u91cc\u4ece\u6765\u4e5f\u6ca1\u6709\u5bf9 \u8001\u603b\u5b58\u4e00\u70b9\u6076\u610f\uff0c\u5426\u5219\u53eb\u963f\u6839\u6b7b\u4e86\u4e5f\u53d8\u4f5c\u755c\u7272\u3002\u201d \u3000\u3000\u53f8\u9a6c\u4eff\u4f5b\u542c\u5f97\u5446\u695e\u4e86\uff0c\u5ffd\u7136\u4ef0\u9762\u9762\u7b11\uff1a\u201c\u597d\uff0c\u597d\u6731\u731b\u3002\u4f60\u80fd\u8981\u5353\u4e1c\u6765\u6d3e\u5927\u7684\u5978\u7ec6\u90fd\u6b7b\u5fc3 \u584c\u5730\u7684\u8ddf\u7740\u4f60\uff0c\u5b9e\u5728\u662f\u4eb2\u597d\u6c49\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u9053\uff1a\u201c\u9489\u978b\u548c\u963f\u6839\u4e5f\u662f\u597d\u6c49\uff0c\u6bd4\u8d77\u4f60\u4eec\u6765\uff0c\u6211\u53f8\u9a6c\u8d85\u7fa4\u5b9e\u5728\u8fde\u72d7\u5c41\u90fd\u4e0d\u5982\u3002\u201d \u3000\u3000\u4ed6\u7684\u7b11\u58f0\u5636\u54d1\u800c\u60b2\u67aa\uff0c\u4f46\u662f\u4ed6\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u786e\u5b9e\u6ca1\u6709\u3002 \u4e94 \u3000\u3000\u6731\u731b\u4e5f\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u773c\u770b\u7740\u9489\u978b\u4e3a\u4ed6\u6218\u6b7b\uff0c\u653e\u5728\u4ed6\u6000\u62b1\u4e2d\u7684\u65f6\u5019\uff0c\u4ed6\u90fd\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u90a3\u65f6\u4ed6\u6d41\u7684\u662f\u8840\u3002 \u3000\u3000\u867d\u7136\u662f\u4ece\u773c\u4e2d\u6d41\u4e0b\u6765\u7684\uff0c\u6d41\u4e0b\u6765\u7684\u4e5f\u662f\u8840\u3002 \u3000\u3000\u8776\u821e\u4e00\u5b9a\u8fd8\u5728\u4e0d\u505c\u7684\u6d41\u8840\uff0c\u4e16\u754c\u4e0a\u5df2\u7ecf\u6ca1\u6709\u4eba\u80fd\u6b62\u4f4f\u5979\u7684\u8840\u3002 \u3000\u3000\u56e0\u4e3a\u4ece\u5979\u4f24\u53e3\u4e2d\u6d41\u51fa\u6765\u7684\u5df2\u7ecf\u4e0d\u662f\u8840\uff0c\u800c\u662f\u821e\u8005\u7684\u7cbe\u9b42\u3002 \u3000\u3000\u800c\u821e\u8005\u7684\u7cbe\u9b42\u5df2\u5316\u4e3a\u8774\u8776\u3002 \u3000\u3000\u2014\u2014\u6709\u8c01\u89c1\u8fc7\u8774\u8776\u6d41\u8840\uff1f\u6709\u8c01\u77e5\u9053\u8774\u8776\u7684\u8840\u662f\u4ec0\u4e48\u989c\u8272\uff1f \u3000\u3000\u6d41\u8840\uff0c\u4eba\u4eec\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u6d41\u8840\uff1f\u4e3a\u4ec0\u4e48\u603b\u662f\u4e0d\u77e5\u9053\u8fd9\u662f\u4ef6\u591a\u4e48\u4e11\u6076\u7684\u4e8b\uff1f \u3000\u3000\u53ef\u662f\u8774\u8776\u77e5\u9053\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u751f\u547d\u5b9e\u5728\u592a\u7f8e\u4e3d\u3001\u592a\u77ed\u4fc3\uff0c\u5df2\u7ecf\u4e0d\u5bb9\u4eba\u518d\u770b\u5230\u5979\u4e11\u964b\u7684\u4e00\u9762\u3002 \u3000\u3000\u201c\u66ff\u6211\u76d6\u4e0a\u88ab\uff0c\u76d6\u4f4f\u6211\u7684\u817f\uff0c\u6211\u4e0d\u8981\u522b\u4eba\u770b\u89c1\u6211\u7684\u817f\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u8776\u821e\u7b2c\u56db\u6b21\u6655\u8ff7\u524d\u6240\u8bf4\u7684\u6700\u540e\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u5176\u5b9e\u5979\u5df2\u7ecf\u6ca1\u6709\u817f\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u5979\u5df2\u7ecf\u6ca1\u6709\u817f\uff0c\u6240\u4ee5\u5bf8\u4e0d\u613f\u88ab\u4eba\u770b\u89c1\uff0c\u5982\u679c\u8fd8\u6709\u4eba\u5fcd\u5fc3\u8bf4\u8fd9\u4e5f\u662f\u4e00\u79cd\u8bbd\u523a\uff0c\u4e5f\u662f \u4eba\u7c7b\u7684\u5f31\u70b9\u4e4b\u4e00\uff0c\u90a3\u4e48\u8fd9\u4e2a\u4eba\u7684\u5fc3\u80a0\u4e00\u5b9a\u5df1\u88ab\u9b3c\u706b\u70bc\u6210\u94c1\u77f3\u3002 \u3000\u3000\u53c8\u539a\u53c8\u91cd\u7684\u68c9\u88ab\u76d6\u5728\u8776\u821e\u8eab\u4e0a\uff0c\u5c31\u597d\u50cf\u66b4\u98ce\u96e8\u524d\u7684\u4e00\u7247\u4e4c\u4e91\u5ffd\u7136\u63a9\u53bb\u4e86\u9633\u5149\u3002 \u3000\u3000\u8776\u821e\u7684\u8138\u4e0a\u5df2\u7ecf\u6ca1\u6709\u4e00\u4e1d\u5149\u6cfd\uff0c\u4e00\u4e1d\u8840\u8272\uff0c\u5c31\u50cf\u662f\u5c0f\u5c4b\u91cc\u672c\u684c\u4e0a\u90a3\u76cf\u706f\u6cb9\u5df2\u5c06\u71c3\u5c3d\u7684\u660f \u706f\u4e00\u6837\u3002 \u3000\u3000\u6731\u731b\u4e00\u76f4\u5728\u706f\u4e0b\u5b88\u7740\u5979\uff0c\u6ca1\u6709\u52a8\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6ca1\u6709\u559d\u8fc7\u4e00\u6ef4\u6c34\uff0c\u4e5f\u6ca1\u6709\u6d41\u8fc7\u4e00\u6ef4\u6cea\u3002 \u3000\u3000\u5c0f\u5c4b\u91cc\u9634\u6e7f\u800c\u5bd2\u51b7\u3002 \u3000\u3000\u4ed6\u5c5e\u4e0b\u4ec5\u5b58\u7684\u5341\u4e09\u4e2a\u4eba\u4e5f\u50cf\u4ed6\u5b88\u7740\u8776\u821e\u4e00\u6837\u5728\u5b88\u7740\u4ed6\u3002\u4ed6\u4eec\u5fc3\u91cc\u4e5f\u548c\u4ed6\u540c\u6837\u60b2\u4f24\u7edd\u671b\uff0c \u53ef\u662f\u4ed6\u4eec\u8fd8\u8bdd\u7740\u3002 \u3000\u3000\u2014\u2014\u51fa\u53bb\u66ff\u4ed6\u4eec\u6253\u542c\u6d88\u606f\u91c7\u4e70\u7cae\u98df\u7684\u4f55\u963f\u6839\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u56de\u6765\uff1f \u3000\u3000\u963f\u6839\u56de\u6765\u65f6\uff0c\u53f8\u9a6c\u8d85\u7fa4\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u770b\u89c1\u963f\u6839\u5e26\u4e86\u4e00\u4e2a\u4eba\u56de\u6765\uff0c\u4e00\u4e2a\u5f88\u9ad8\u5927\u7684\u964c\u751f\u4eba\uff0c\u53d1\u9afb\u5df1\u4e71\u4e86\uff0c\u8863\u886b\u5df2\u7834\u788e\uff0c \u8eab\u4e0a\u8fd8\u5e26\u7740\u4f24\uff0c\u624b\u8fb9\u5374\u6ca1\u6709\u5e26\u6b66\u5668\u3002 \u3000\u3000\u53ef\u662f\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u8fd8\u662f\u4e0d\u5e94\u8be5\u5e26\u8fd9\u4e48\u6837\u4e00\u4e2a\u964c\u751f\u4eba\u5230\u8fd9\u91cc\u6765\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e2a\u843d\u9b44\u7684\u964c\u751f\u4eba\u770b\u6765\u867d\u7136\u5df2\u50cf\u662f\u6761\u6b63\u5728\u88ab\u730e\u4eba\u8ffd\u6355\u5f97\u65e0\u8def\u53ef\u8d70\u7684\u731b\u517d\uff0c\u4f46\u662f\u731b\u517d \u6bd5\u7adf\u8fd8\u662f\u731b\u517d\uff0c\u8fd8\u662f\u5145\u6ee1\u4e86\u5371\u9669\uff0c\u8fd8\u662f\u4e00\u6837\u53ef\u4ee5\u4f24\u4eba\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u8eab\u8fb9\u867d\u7136\u6ca1\u6709\u5e26\u6b66\u5668\uff0c\u5374\u5e26\u7740\u79cd\u6bd4\u5200\u950b\u5251\u5203\u8fd8\u9510\u5229\u903c\u4eba\u7684\u6c14\u52bf\u3002 \u3000\u3000\u5c0f\u5c4b\u4e2d\u6bcf\u4e2a\u4eba\u7684\u624b\u7acb\u523b\u90fd\u63e1\u7d27\u4e86\u4ed6\u4eec\u5df2\u4e0b\u5b9a\u51b3\u5fc3\u81f3\u6b7b\u4e0d\u79bb\u7684\u5927\u5200\u3002 \u3000\u3000\u6bcf\u4e00\u628a\u5200\u90fd\u5df2\u5c06\u51fa\u9798\u3002 \u3000\u3000\u53ea\u6709\u6731\u731b\u8fd8\u662f\u5750\u5728\u90a3\u91cc\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5374\u53d1\u4e0b\u4e86\u4e00\u9053\u4ed6\u7684\u5c5e\u4e0b\u5168\u90e8\u65e0\u6cd5\u4e86\u89e3\u7684\u547d\u4ee4\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u547d\u4ee4\u4ed6\u7684\u5c5e\u4e0b\uff1a\u201c\u638c\u706f\u3001\u71c3\u706b\u3001\u70b9\u70db\u3002\u201d\u6731\u731b\u7684\u547d\u4ee4\u76f4\u63a5\u7b80\u5355\u800c\u5947\u602a\uff0c\u201c\u628a\u6240\u6709 \u80fd\u70b9\u71c3\u7684\u4e1c\u897f\u90e8\u70b9\u8d77\u6765\u3002\u201d \u3000\u3000\u6ca1\u6709\u4eba\u660e\u767d\u6731\u731b\u7684\u610f\u601d\uff0c\u53ef\u662f\u53f8\u9a6c\u8d85\u7fa4\u660e\u767d\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u89c1\u8fc7\u6731\u731b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e00\u8d70\u8fdb\u8fd9\u95f4\u660f\u6697\u9634\u6e7f\u7834\u65e7\u7684\u5c0f\u5c4b\uff0c\u4e00\u770b\u5230\u90a3\u4e2a\u5c31\u50cf\u662f\u5757\u5df2\u7ecf\u88ab\u98ce\u5316\u4fb5\u8680\u4e86\u7684\u5ca9\u77f3 \u822c\u5750\u5728\u5927\u7095\u65c1\u7684\u6731\u731b\uff0c\u5c31\u77e5\u9053\u4ed6\u5df2\u7ecf\u770b\u5230\u4e86\u4ed6\u8fd9\u4e00\u751f\u4e2d\u6700\u60f3\u770b\u89c1\u5374\u4ece\u672a\u770b\u89c1\u8fc7\u7684\u4eba\u3002 \u3000\u3000\u5c0f\u5c4b\u91cc\u672c\u6765\u53ea\u6709\u4e00\u76cf\u660f\u706f\u3002 \u3000\u3000\u706f\u706b\u5149\u660e\u90fd\u662f\u5c5e\u4e8e\u6b22\u4e50\u7684\uff0c\u672c\u6765\u5df2\u7ecf\u5982\u6b64\u60b2\u60e8\u7684\u60c5\u51b5\uff0c\u518d\u4eae\u7684\u706f\u5149\u4e5f\u6ca1\u6709\u7528\u4e86\u3002 \u3000\u3000\u53ef\u662f\u6731\u731b\u73b0\u5728\u5374\u5429\u5490\uff1a\u201c\u628a\u6240\u6709\u7684\u706f\u70db\u706b\u628a\u90fd\u70b9\u8d77\u6765\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4f4e\u6c89\u800c\u5636\u54d1\uff0c\u201c\u8ba9\u6211\u6765\u770b\u770b\u8fd9\u4f4d\u8d35\u5bbe\u3002\u201d \u3000\u3000\u706f\u706b\u7acb\u523b\u71c3\u8d77\uff0c\u6731\u731b\u8bf4\u7684\u8bdd\u901a\u5e38\u90fd\u662f\u7edd\u5bf9\u6709\u6548\u7684\u547d\u4ee4\u3002 \u3000\u3000\u4e09\u76cf\u706f\u3001\u4e03\u6839\u70db\u3001\u4e94\u652f\u706b\u628a\uff0c\u5df2\u8db3\u591f\u628a\u8fd9\u5c0f\u5c4b\u7167\u4eae\u5982\u767d\u663c\u3002\u4e5f\u5df2\u8db3\u591f\u5c06\u8fd9\u5c0f\u5c4b\u91cc\u6bcf\u4e2a\u4eba \u8138\u4e0a\u7684\u6bcf\u4e00\u6761\u4f24\u75d5\u76b1\u7eb9\u90fd\u7167\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u56e0\u60b2\u82e6\u54c0\u75db\u4ec7\u6068\u6124\u6012\u800c\u751f\u51fa\u7684\u76b1\u7eb9\uff0c\u7adf\u4f3c\u6bd4\u5229\u5203\u5200\u950b\u5212\u7834\u7684\u4f24\u75d5\u66f4\u6df1\u3002 \u3000\u3000\u6731\u731b\u7ec8\u4e8e\u6162\u6162\u7684\u7ad9\u8d77\u6765\uff0c\u6162\u6162\u7684\u8f6c\u8fc7\u8eab\uff0c\u7ec8\u4e8e\u9762\u5bf9\u4e86\u53f8\u9a6c\u8d85\u7fa4\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u9ed8\u9ed8\u7684\u76f8\u5bf9\uff0c\u9ed8\u9ed8\u7684\u76f8\u89c6\uff0c\u5927\u5730\u95f4\u4eff\u4f5b\u53ea\u5269\u4e0b\u706b\u7130\u95ea\u52a8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5929\u5730\u95f4\u4eff\u4f5b\u4e5f\u5df2\u7ecf\u53ea\u5269\u4e0b\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u6ee1\u8eab\u5e26\u7740\u4f24\u75d5\uff0c\u6ee1\u5fc3\u5145\u6ee1\u60b2\u75db\u7684\u843d\u9b44\u4eba\uff0c\u4e24\u4e2a\u90fd\u5df2\u5f7b\u5e95\u5931\u8d25\u4e86\u7684\u4eba\u3002 \u3000\u3000\u53ef\u662f\u5929\u5730\u95f4\u8fd8\u662f\u53ea\u6709\u4ed6\u4eec\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u4e24\u4e2a\u4eba\u9762\u5bf9\u9762\u7684\u7ad9\u5728\u90a3\u91cc\u65f6\uff0c\u4e16\u4e0a\u522b\u7684\u4eba\u4eff\u4f5b\u90fd\u5df2\u4e0d\u518d\u5b58 \u3000\u3000\u201c\u4f60\u5c31\u662f\u53f8\u9a6c\u8d85\u7fa4\uff1f\u201d \u3000\u3000\u201c\u4f60\u770b\u6211\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u4f60\u5b9e\u5728\u4e0d\u50cf\uff0c\u82f1\u96c4\u65e0\u6545\u7684\u53f8\u9a6c\u8d85\u7fa4\u5b9e\u5728\u4e0d\u5e94\u8be5\u50cf\u662f\u4f60\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u3002\u201d\u6731\u731b\u8bf4\uff1a \u201c\u4f46\u662f\u6211\u77e5\u9053\u4f60\u5c31\u662f\u53f8\u9a6c\u8d85\u7fa4\uff0c\u4e00\u5b9a\u662f\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u9664\u4e86\u53f8\u9a6c\u8d85\u7fa4\u5916\uff0c\u5929\u4e0b\u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u4f1a\u50cf\u4f60\u8fd9\u4e2a\u6837\u5b50\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u4f60\u7684\u6837 \u5b50\u770b\u8d77\u6765\u5c31\u597d\u50cf\u5219\u624d\u4e00\u4e0b\u5b50\u6d3b\u6d3b\u89c1\u5230\u4e86\u516b\u767e\u516b\u5341\u516b\u4e2a\u51a4\u6b7b\u9b3c\u3002\u201d \u3000\u3000\u53f8\u9a6c\u5c45\u7136\u540c\u610f\u3002 \u3000\u3000\u201c\u80fd\u591f\u4e00\u4e0b\u5b50\u80fd\u89c1\u5230\u516b\u767e\u516b\u5341\u516b\u4e2a\u51a4\u6b7b\u9b3c\u7684\u4eba\u786e\u5b9e\u4e0d\u591a\uff0c\u53ef\u662f\u4e5f\u4e0d\u6b62\u4e00\u4e2a\u3002\u201d \u3000\u3000\u201c\u9664\u4e86\u4f60\u4e4b\u5916\u8fd8\u6709\u8c01\uff1f\u201d\u6731\u731b\u95ee\uff1a\u201c\u662f\u4e0d\u662f\u8fd8\u6709\u4e2a\u59d3\u6731\u53eb\u6731\u731b\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u6731\u731b\u5927\u7b11\u3002 \u3000\u3000\u4ed6\u7684\u786e\u662f\u5728\u5927\u7b11\uff0c\u4ed6\u5e73\u65f6\u542c\u5230\u8fd9\u79cd\u8bdd\u7684\u65f6\u5019\u4e00\u5b9a\u7b11\u7684\uff0c\u4ed6\u7684\u7b11\u58f0\u6709\u65f6\u8fde\u5341\u91cc\u5916\u90fd\u53ef\u4ee5\u542c \u5f97\u5230\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4e5f\u5728\u7b11\uff0c\u53ea\u4e0d\u8fc7\u8138\u4e0a\u8fde\u4e00\u70b9\u7b11\u610f\u90fd\u6ca1\u6709\uff0c\u7b11\u58f0\u8fde\u7ad9\u5728\u4ed6\u65c1\u8fb9\u7684\u4eba\u90fd\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6839\u672c\u8fde\u4e00\u70b9\u58f0\u97f3\u90e8\u6ca1\u6709\u7b11\u51fa\u6765\u3002 \u3000\u3000\u6ca1\u6709\u7b11\u58f0\uff0c\u4e5f\u6ca1\u6709\u54ed\u58f0\uff0c\u522b\u7684\u4eba\u975e\u4f46\u7b11\u4e0d\u51fa\uff0c\u8fde\u54ed\u90fd\u54ed\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u773c\u91cc\u90fd\u5df2\u6709\u70ed\u6cea\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u4eec\u65e2\u4e0d\u662f\u6731\u731b\uff0c\u4e5f\u4e0d\u662f\u53f8\u9a6c\u8d85\u7fa4\uff0c\u6240\u4ee5\u4ed6\u4eec\u53ef\u4ee5\u6d41\u6cea\u3002 \u3000\u3000\u53ef\u4ee5\u6d41\u8840\uff0c\u4e5f\u53ef\u4ee5\u6d41\u6cea\u3002 \u3000\u3000\u4ed6\u4eec\u5269\u4e0b\u7684\u4e5f\u53ea\u6709\u6ee1\u8154\u8840\u6cea\u3002 \u3000\u3000\u6731\u731b\u73af\u987e\u8fd9\u4e9b\u81f3\u6b7b\u90fd\u4e0d\u4f1a\u518d\u79bb\u5f00\u4ed6\u7684\u597d\u7537\u513f\uff0c\u4e00\u53cc\u5e03\u6ee1\u8840\u4e1d\u7684\u5927\u773c\u4e2d\u4eff\u4f5b\u53c8\u6709\u9c9c\u8840\u5c06\u8981 \u8ff8\u51fa\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u6b21\u6211\u4eec\u8d25\u4e86\uff0c\u5f7b\u5e95\u8d25\u4e86\uff0c\u201d\u4ed6\u5636\u58f0\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u8d25\u5f97\u4e0d\u670d\uff0c\u6b7b\u4e5f\u4e0d\u670d\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u201d\u53f8\u9a6c\u8d85\u7fa4\u9eef\u7136\uff1a\u201c\u4f60\u4eec\u7684\u4e8b\u627e\u5df2\u7ecf\u5168\u90fd\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u6765\u7684\u65f6\u5019\uff0c\u4f60\u5e76\u4e0d\u5728\u957f\u5b89\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u90a3\u65f6\u5019\u6211\u4e0d\u5728\u3002\u201d\u53f8\u9a6c\u957f\u53f9\uff1a\u201d\u6211\u4e0d\u77e5\u9053\u4f60\u4f1a\u6765\u5f97\u8fd9\u4e48\u5feb\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u5355\u9a91\u53bb\u4e86\u6d1b\u9633\uff1f\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u60f3\u8d76\u53bb\u5355\u72ec\u89c1\u4f60\u4e00\u9762\uff0c\u628a\u6211\u4eec\u4e4b\u95f4\u7684\u4e8b\u5f7b\u5e95\u89e3\u51b3\u3002\u201d\u53f8\u9a6c\u900d\uff0c\u201c\u7531\u6211\u4eec\u4e24\u4eba\u81ea \u5df1\u89e3\u51b3\u3002\u201d \u3000\u3000\u201c\u4f60\u771f\u7684\u8fd9\u4e48\u60f3\uff1f\u201d \u3000\u3000\u201c\u771f\u7684\u3002\u201d \u3000\u3000\u6731\u731b\u5ffd\u7136\u4e5f\u957f\u957f\u53f9\u606f\uff1a\u201c\u6211\u6ca1\u6709\u770b\u9519\u4f60\uff0c\u6211\u5c31\u77e5\u9053\u5f53\u65f6\u4f60\u82e5\u5728\u957f\u5b89\u3002\u81f3\u5c11\u4e5f\u4f1a\u7ed9\u6211\u4eec\u4e00 \u4e2a\u673a\u4f1a\uff0c\u5802\u5802\u6b63\u6b63\u7684\u51b3\u4e00\u6b7b\u6218\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u91cc\u5145\u6ee1\u60b2\u6124\uff1a\u201c\u6211\u4eec\u672c\u6765\u5c31\u662f\u6765\u6b7b\u7684\uff0c\u8981\u6211\u4eec\u6b7b\u5728\u8fd9\u79cd\u5351\u9119\u7684\u9634\u8c0b\u8be1\u8ba1\u4e2d\uff0c\u6211 \u4eec\u6b7b\u5f97\u5b9e\u5728\u4e0d\u670d\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u6211\u5e76\u4e0d\u602a\u4f60\uff0c\u5f53\u65f6\u4f60\u82e5\u5728\u957f\u5b89\uff0c\u7edd\u4e0d\u4f1a\u505a\u51fa\u8fd9\u79cd\u5351\u9119\u65e0\u803b\u7684\u4e8b\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8083\u7136\u9053\uff1a\u201c\u4e0d\u7ba1\u5f53\u65f6\u6211\u5728\u4e0d\u5728\uff0c\u8fd9\u4ef6\u4e8b\u90fd\u662f\u6211\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u90a3\u65f6\u5019\u6211\u8fd8\u662f\u5927\u9556\u5c40\u7684\u603b\u74e2\u628a\u5b50\uff0c\u53ea\u8981\u662f\u5927\u5ad6\u5c40\u5c5e\u4e0b\u505a\u7684\u4e8b\uff0c\u6211\u90fd\u8d1f\u5168\u8d23\u3002\u201d\u53f8 \u9a6c\u8d85\u7fa4\u9053\uff1a\u201c\u51a4\u6709\u5934\uff0c\u503a\u6709\u738b\uff0c\u8fd9\u7b14\u503a\u8fd8\u662f\u5e94\u8be5\u7531\u6211\u6765\u8fd8\u3002\u201d \u3000\u3000\u201c\u4eca\u65e5\u4f60\u5c31\u662f\u6765\u8fd8\u503a\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u8fd9\u7b14\u503a\u4f60\u80fd\u8fd8\u5f97\u6e05\uff1f\u201d\u6731\u731b\u5389\u58f0\u95ee\uff0c\u201c\u4f60\u600e\u4e48\u6837\u624d\u80fd\u8fd8\u5f97\u6e05\uff1f\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u6e05\u4e5f\u8981\u8fd8\uff0c\u201d\u53f8\u9a6c\u8d85\u7fa4\u9053\uff1a\u201c\u4f60\u8981\u6211\u600e\u4e48\u8fd8\uff0c\u6211\u5c31\u600e\u4e48\u8fd8\u3002\u5426\u5219\u6211\u53c8\u4f55\u5fc5\u6765\uff1f\u201d \u3000\u3000\u6731\u731b\u76ef\u7740\u4ed6\uff0c\u4ed6\u4e5f\u76ef\u7740\u6731\u731b\uff0c\u5947\u602a\u7684\u662f\uff0c\u4e24\u4e2a\u4eba\u7684\u773c\u775b\u975e\u4f46\u6ca1\u6709\u4ec7\u6068\u6028\u6bd2\uff0c\u53cd\u800c\u5145\u6ee1\u4e86 \u5c0a\u656c\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u4f60\u90a3\u65f6\u5019\u8fd8\u662f\u5927\u9556\u5c40\u7684\u603b\u74e2\u628a\u5b50\u3002\u201d\u6731\u731b\u5ffd\u7136\u95ee\u53f8\u9a6c\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u65e0\u8bba\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u90fd\u8ddf\u8fd9\u4ef6\u4e8b\u5168\u65e0\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u8fd8\u662f\u6731\u731b\uff0c\u6211\u8fd8\u662f\u53f8\u9a6c\u8d85\u7fa4\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u5728\u522b\u4eba\u773c\u4e2d\u770b\u6765\u5df2\u7ecf\u5f7b\u5e95\u5931\u8d25\u4e86\u7684\u4eba\uff0c\u795e\u60c5\u4e2d\u5ffd\u7136\u53c8\u9732\u51fa\u4e86\u5e1d\u738b\u822c\u4e0d\u53ef\u4fb5\u72af\u7684\u5c0a \u4e25\u3002\u201c\u4eca\u65e5\u6211\u8981\u6765\u8fd8\u8fd9\u7b14\u503a\uff0c\u5c31\u56e0\u4e3a\u4f60\u662f\u6731\u731b\uff0c\u6211\u662f\u53f8\u9a6c\u8d85\u7fa4\uff0c\u8fd9\u4e00\u70b9\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\u90fd \u4e0d\u4f1a\u53d8\u7684\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u5c31\u7b97\u5934\u65ad\u8840\u6d41\uff0c\u5bb6\u6bc1\u4eba\u4ea1\uff0c\u8fd9\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u53d8\u3002\u201d \u3000\u3000\u2014\u2014\u662f\u7684\uff0c\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u2014\u2014\u5934\u53ef\u65ad\uff0c\u8840\u53ef\u6d41\uff0c\u7cbe\u795e\u5374\u6c38\u8fdc\u4e0d\u80fd\u5c48\u670d\uff0c\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u6bc1\u706d\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u6c5f\u6e56\u7537\u513f\u7684\u4e49\u6c14\uff0c\u8fd9\u5c31\u662f\u6c5f\u7322\u7537\u513f\u7684\u8840\u6027\u3002 \u3000\u3000\u6731\u731b\u51dd\u89c6\u7740\u53f8\u9a6c\u8d85\u7fa4\uff0c\u795e\u60c5\u4e2d\u4e5f\u5145\u6ee1\u4e86\u4e0d\u53ef\u4fb5\u72af\u7684\u5c0a\u4e25\u3002 \u3000\u3000\u201c\u4f60\u662f\u6211\u4e00\u751f\u7684\u6b7b\u654c\uff0c\u4f60\u6211\u51a4\u4ec7\u76f8\u7ed3\u5df2\u6df1\uff0c\u5df2\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u56e0\u6b64\u800c\u6b7b\uff0c\u201d\u6731\u731b\u8bf4\uff1a\u201c\u4e3a \u4e86\u8fd9\u4e9b\u5c48\u6b7b\u7684\u51a4\u9b42\uff0c\u4f60\u6211\u4e5f\u5df2\u52bf\u96be\u5e76\u5b58\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u6211\u6731\u731b\u7eb5\u6a2a\u6c5f\u6e56\u4e00\u751f\uff0c\u6325\u5200\u6740\u4eba\uff0c\u5feb\u610f\u601d\u4ec7\uff0c\u4ece\u672a\u628a\u4efb\u4f55\u4eba\u770b\u5728\u773c\u91cc\u3002\u201d\u6731\u731b\u8bf4\uff1a \u201c\u53ea\u6709\u4f60\uff0c\u4f60\u53f8\u9a6c\u8d85\u7fa4\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5df2\u56e0\u6fc0\u52a8\u800c\u98a4\u6296\uff1a\u201c\u4f60\u53f8\u9a6c\u8d85\u7fa4\u4eca\u65e5\u8bf7\u53d7\u6211\u6731\u731b\u4e00\u62dc\u3002\u201d \u3000\u3000\u4ed6\u771f\u7684\u62dc\u5012\u3002\u8fd9\u4e2a\u6c38\u4e0d\u5c48\u819d\u7684\u7537\u5b50\u6c49\u7adf\u771f\u7684\u62dc\u5012\u5728\u5730\u4e0b\uff0c\u62dc\u5012\u5728\u53f8\u9a6c\u8d85\u7fa4\u9762\u524d\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u4e5f\u62dc\u5012\u3002 \u3000\u3000\u201c\u6211\u62dc\u4f60\u662f\u4e2a\u771f\u6b63\u7684\u82f1\u96c4\uff0c\u662f\u6761\u771f\u6b63\u7684\u7537\u5b50\u53cc\u3002\u201d\u6731\u731b\u5636\u58f0\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u8fd9\u4e00\u62dc\u4e4b\u540e\u4f60 \u6211\u4fbf\u5c06\u6c38\u8bc0\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u8fd8\u662f\u4f1a\u6740\u4f60\uff0c\u6211\u522b\u65e0\u9009\u62e9\u4f59\u5730\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8083\u7136\u9053\uff1a\u201c\u662f\u7684\u3002\u4eba\u5728\u6c5f\u7322\uff0c\u672c\u6765\u5c31\u662f\u8fd9\u6837\u7684\u3002\u4f60\u6211\u90fd\u5df2\u522b\u65e0\u9009\u62e9\u4f59\u5730\u3002\u201d \u3000\u3000\u201c\u4f60\u660e\u767d\u5c31\u597d\u3002\u201d\u6731\u731b\u7684\u58f0\u97f3\u66f4\u5636\u54d1\uff0c\u201c\u4f60\u660e\u767d\u5c31\u597d\u3002\u201d \u3000\u3000\u4ed6\u7ad9\u8d77\u6765\uff0c\u518d\u6b21\u73af\u987e\u4ed6\u7684\u5c5e\u4e0b\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u5c31\u662f\u53f8\u9a6c\u8d85\u7fa4\uff0c\u5c31\u662f\u6bc1\u4e86\u6211\u4eec\u96c4\u72ee\u5802\u7684\u4eba\u3002\u201d\u6731\u731b\u8bf4\u5f97\u4f4e\u6c89\u800c\u7f13\u6162\uff1a\u201c\u5c31\u4e3a\u4e86 \u8fd9\u4e2a\u4eba\u8981\u9020\u6210\u4ed6\u7a7a\u524d\u7684\u9738\u4e1a\uff0c\u6211\u4eec\u7684\u5144\u5f1f\u5df2\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u60e8\u6b7b\u5728\u8857\u5934\uff0c\u8fde\u5c38\u9aa8\u90fd\u65e0\u6cd5\u5b89\u846c\uff0c \u6211\u4eec\u7684\u59d0\u59b9\u5df2\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u505a\u4e86\u5be1\u5987\uff0c\u6709\u7684\u4eba\u4e3a\u4e86\u8981\u5403\u996d\uff0c\u751a\u81f3\u5df2\u7ecf\u6ca6\u843d\u5230\u8981\u53bb\u505a\u5a4a\u5b50\u3002\u201d \u3000\u3000\u5927\u5bb6\u9ed8\u9ed8\u7684\u542c\u7740\uff0c\u6cea\u773c\u4e2d\u90fd\u66b4\u51fa\u4e86\u8840\u4e1d\uff0c\u62f3\u5934\u4e0a\u90fd\u51f8\u8d77\u4e86\u9752\u7b4b\u3002 \u3000\u3000\u201c\u6211\u4eec\u6bcf\u4e2a\u4eba\u90fd\u66fe\u5728\u5fc3\u91cc\u53d1\u8fc7\u6bd2\u8a93\uff0c\u4e0d\u53d6\u4e0b\u4ed6\u7684\u5934\u9885\uff0c\u8a93\u4e0d\u56de\u6545\u4e61\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u5c31\u7b97 \u6211\u4eec\u5168\u90fd\u6218\u6b7b\uff0c\u4e5f\u8981\u5316\u505a\u5389\u9b3c\u6765\u593a\u4ed6\u7684\u9b42\u9b44\u3002\u201d \u3000\u3000\u4ed6\u6307\u7740\u53f8\u9a6c\u8d85\u7fa4\uff1a\u201c\u73b0\u5728\u4ed6\u5df2\u7ecf\u6765\u4e86\uff0c\u4ed6\u8bf4\u7684\u8bdd\u4f60\u4eec\u90fd\u5df2\u7ecf\u542c\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u6731\u731b\u9053\uff1a\u201c\u4ed6\u662f\u8fd8\u503a\u6765\u7684\uff0c\u8840\u503a\u4e00\u5b9a\u8981\u7528\u8840\u6765\u8fd8\u3002\u201d \u3000\u3000\u4ed6\u7684\u76ee\u5149\u5200\u950b\u822c\u4ece\u4ed6\u7684\u5c5e\u4e0b\u8138\u4e0a\u626b\u8fc7\uff1a\u201c\u4ed6\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u4ed6\u4e5f\u50cf\u6211\u4eec\u4e00\u6837\uff0c\u5df2\u7ecf\u4f17\u53db\u4eb2 \u79bb\u3001\u5bb6\u7834\u4eba\u4ea1\uff0c\u4f46\u662f\u6211\u4eec\u6700\u5c11\u8fd8\u6709\u8fd9\u4e9b\u5144\u5f1f\uff0c\u6211\u4eec\u8981\u62a5\u4ec7\uff0c\u73b0\u5728\u5c31\u662f\u6700\u597d\u7684\u673a\u4f1a\uff0c\u4ed6\u4e00\u4e2a\u4eba \u7edd\u4e0d\u662f\u6211\u4eec\u8fd9\u4e9b\u4eba\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u6731\u731b\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u7684\u624b\u91cc\u90fd\u6709\u5200\uff0c\u73b0\u5728\u5c31\u53ef\u4ee5\u62d4\u5200\u800c\u8d77\uff0c\u5c06\u4ed6\u4e71\u5200\u65a9\u6740\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u6ca1\u6709\u4eba\u62d4\u5200\u3002 \u3000\u3000\u5927\u5bb6\u8fd8\u662f\u9ed8\u9ed8\u7684\u542c\u7740\uff0c\u751a\u81f3\u8fde\u770b\u90fd\u6ca1\u6709\u53bb\u770b\u53f8\u9a6c\u8d85\u7fa4\u4e00\u773c\u3002 \u3000\u3000\u6731\u731b\u5927\u559d\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u52a8\u624b\uff0c\u96be\u9053\u4f60\u4eec\u7684\u624b\u90fd\u5df2\u8f6f\u4e86\uff1f\u96be\u9053\u4f60\u4eec\u5df2\u7ecf\u5fd8\u4e86\u600e\u4e48\u6837 \u6740\u4eba\uff1f\u201d \u3000\u3000\u963f\u6839\u5ffd\u7136\u51b2\u8fc7\u6765\uff0c\u4f0f\u5012\u5728\u53f8\u9a6c\u548c\u6731\u731b\u9762\u524d\uff0c\u4e94\u4f53\u6295\u5730\u3002 \u3000\u3000\u201c\u8001\u603b\uff0c\u6211\u77e5\u9053\u4f60\u8ddf\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u662f\u51c6\u5907\u6765\u6b7b\u7684\uff0c\u201d\u963f\u6839\u8bf4\uff1a\u201c\u8001\u603b\uff0c\u4f60\u6c42\u4ec1\u5f97\u4ec1\uff0c \u6b7b\u800c\u65e0\u61be\uff0c\u4f60\u6b7b\u4e86\u4e4b\u540e\uff0c\u963f\u6839\u4e00\u5b9a\u4f1a\u5148\u5b89\u6392\u597d\u4f60\u7684\u540e\u4e8b\uff0c\u7136\u540e\u518d\u8ddf\u7740\u4f60\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d94\u7fa4\u5927\u7b11\uff1a\u201c\u597d\uff0c\u597d\u5144\u5f1f\uff0c\u201d\u4ed6\u5927\u7b11\u9053\uff0c\u201c\u597d\u4e00\u4e2a\u6c42\u4ec1\u5f97\u4ec1\uff0c\u6b7b\u800c\u65e0\u61be\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u201c\u54e8\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4e00\u628a\u5200\u4ece\u4e00\u4e2a\u4eba\u624b\u91cc\u8dcc\u4e0b\u6765\uff0c\u8dcc\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6731\u731b\u5bf9\u7740\u8fd9\u4e2a\u4eba\uff0c\u5389\u58f0\u95ee\uff1a\u201c\u86ee\u725b\uff0c\u4f60\u4e00\u5411\u662f\u6761\u597d\u6c49\uff0c\u6740\u4eba\u4ece\u6765\u4e5f\u6ca1\u6709\u624b\u8f6f\u8fc7\uff0c\u73b0\u5728\u600e \u4e48\u8fde\u5200\u90fd\u63e1\u4e0d\u4f4f\u4e86\uff1f\u201d \u3000\u3000\u86ee\u725b\u5782\u4e0b\u5934\uff0c\u6ee1\u9762\u8840\u6cea\u3002 \u3000\u3000\u201c\u5802\u4e3b\uff0c\u4f60\u77e5\u9053\u4ffa\u672c\u672a\u505a\u68a6\u90fd\u60f3\u628a\u8fd9\u4e2a\u4eba\u7684\u8111\u888b\u5272\u4e0b\u6765\uff0c\u53ef\u662f\u73b0\u5728\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\u600e\u4e48\u6837\uff1f\u201d\u6731\u731b\u7684\u58f0\u97f3\u66f4\u51c4\u5389\uff1a\u201c\u73b0\u5728\u4f60\u96be\u9053\u4e0d\u60f3\u6740\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4ffa\u8fd8\u662f\u60f3\uff0c\u53ef\u662f\u53eb\u4ffa\u8fd9\u4e48\u6837\u5c31\u6740\u4e86\u4ed6\uff0c\u4ffa\u5b9e\u5728\u6ca1\u6cd5\u5b50\u52a8\u624b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ffa\u4e5f\u4e0d\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d\u86ee\u725b\u4e5f\u8dea\u4e0b\u6765\uff0c\u7528\u529b\u6253\u81ea\u5df1\u7684\u8033\u5149\uff0c\u6253\u5f97\u6ee1\u8138\u662f\u8840\uff1a\u201c\u4ffa \u8be5\u6b7b\uff0c\u4ffa\u662f\u4e2a\u8be5\u6b7b\u7684\u5b6c\u79cd\uff0c\u4ffa\u5fc3\u91cc\u867d\u7136\u77e5\u9053\uff0c\u53ef\u662f\u5802\u4e3b\u82e5\u662f\u53eb\u4ffa\u8bf4\u51fa\u6765\uff0c\u4ffa\u5374\u8bf4\u4e0d\u51fa\u3002\u201d \u3000\u3000\u201c\u4f60\u5b6c\u79cd\uff0c\u4f60\u8bf4\u4e0d\u51fa\uff0c\u6211\u8bf4\u5f97\u51fa\uff0c\u201d\u6731\u731b\u9053\uff1a\u201c\u4f60\u6ca1\u6cd5\u5b50\u52a8\u624b\uff0c\u53ea\u56e0\u4e3a\u4f60\u5ffd\u7136\u53d1\u73b0\u54b1\u4eec \u5929\u5929\u60f3\u8981\u4ed6\u547d\u7684\u8fd9\u4e2a\u4eba\u662f\u6761\u597d\u6c49\uff0c\u4ed6\u65e2\u7136\u6709\u79cd\u4e00\u4e2a\u4eba\u6765\u89c1\u54b1\u4eec\uff0c\u54b1\u4eec\u4e5f\u5e94\u8be5\u4ee5\u597d\u6c49\u6765\u5bf9\u5f85 \u4ed6\uff0c\u54b1\u4eec\u82e5\u662f\u8fd9\u4e48\u6837\u6740\u4e86\u4ed6\uff0c\u5c31\u7b97\u62a5\u4e86\u4ec7\uff0c\u4e5f\u6ca1\u6709\u8138\u518d\u53bb\u89c1\u5929\u4e0b\u82f1\u96c4\u3002\u201d \u3000\u3000\u4ed6\u95ee\u86ee\u725b\uff1a\u201c\u4f60\u8bf4\uff0c\u4f60\u5fc3\u91cc\u662f\u4e0d\u662f\u8fd9\u4e48\u6837\u60f3\u7684\uff1f\u201d \u3000\u3000\u86ee\u725b\u4ee5\u5934\u78b0\u5730\uff0c\u8138\u4e0a\u5df2\u8840\u6cea\u6a21\u7cca\u3002 \u3000\u3000\u6731\u731b\u5200\u950b\u822c\u7684\u76ee\u5149\u53c8\u4e00\u6b21\u4ece\u4ed6\u5c5e\u4e0b\u4eec\u7684\u8138\u4e0a\u626b\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u4f60\u4eec\u5462\uff1f\u201d\u4ed6\u95ee\u4ed6\u8fd9\u4e9b\u5df2\u7ecf\u8ddf\u7740\u4ed6\u8eab\u7ecf\u767e\u6218\u4e5d\u6b7b\u4e00\u751f\u3001\u9664\u4e86\u4e00\u6761\u547d\u5916\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\u7684 \u5144\u5f1f\u4eec\uff1a\u201c\u4f60\u4eec\u5fc3\u91cc\u600e\u4e48\u60f3\u7684\uff1f\u201c \u3000\u3000\u6ca1\u6709\u4eba\u56de\u7b54\u3002 \u3000\u3000\u53ef\u662f\u6bcf\u4e2a\u4eba\u63e1\u5200\u7684\u624b\u90fd\u53d7\u4f24\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u5df2\u5931\u53bb\u4e00\u5207\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u5931\u53bb\u4ed6\u4eec\u7684\u8840\u6c14\u4e49\u6c14\u548c\u52c7\u6c14\u3002 \u3000\u3000\u6731\u731b\u770b\u7740\u4ed6\u4eec\uff0c\u4e00\u4e2a\u4e2a\u770b\u8fc7\u53bb\uff0c\u4e00\u53cc\u75b2\u5026\u65e0\u795e\u7684\u5927\u773c\u4e2d\u5ffd\u7136\u53c8\u6709\u4e86\u5149\uff0c\u5ffd\u7136\u4ef0\u9762\u800c\u8bf4\uff1a \u201c\u597d\uff0c\u8fd9\u624d\u662f\u597d\u5144\u5f1f\uff0c\u8fd9\u624d\u662f\u6731\u731b\u7684\u597d\u5144\u5f1f\uff0c\u6731\u731b\u80fd\u4ea4\u5230\u4f60\u4eec\u8fd9\u6837\u7684\u5144\u5f1f\uff0c\u6b7b\u4e86\u4e5f\u4e0d\u51a4\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8138\u53bb\u95ee\u53f8\u9a6c\u8d85\u7fa4\uff0c\u201c\u4f60\u770b\u89c1\u4e86\u5427\uff0c\u6211\u6731\u731b\u7684\u5144\u5f1f\u662f\u4e9b\u4ec0\u4e48\u6837\u7684\u5144\u5f1f\uff1f\u6709\u6ca1\u6709\u4e00\u4e2a\u662f \u5b6c\u79cd\u7684\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7684\u773c\u775b\u5df2\u7ecf\u7ea2\u4e86\uff0c\u65e9\u5c31\u7ea2\u4e86\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u6807\u67aa\u822c\u7ad9\u5728\u90a3\u91cc\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u6731\u731b\uff0c\u6211\u4e0d\u5982\u4f60\uff0c\u8fde\u66ff\u4f60 \u64e6\u5c41\u80a1\u90fd\u4e0d\u914d\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211\u6ca1\u6709\u4f60\u8fd9\u6837\u7684\u5144\u5f1f\u3002\u201d \u3000\u3000\u8fd9\u53e5\u6d3b\u4e0d\u662f\u522b\u4eba\u8bf4\u51fa\u6765\u7684\uff0c\u8fd9\u53e5\u6d3b\u662f\u53f8\u9a6c\u8d85\u7fa4\u8bf4\u51fa\u6765\u7684\u3002 \u3000\u3000\u5929\u4e0b\u65e0\u53cc\u7684\u82f1\u96c4\u53f8\u9a6c\u8d85\u7fa4\u3002 \u3000\u3000\u6731\u731b\u773c\u4e2d\u5374\u6ca1\u6709\u4e1d\u6beb\u5f97\u610f\u4e4b\u8272\uff0c\u53cd\u800c\u5145\u6ee1\u4e86\u60b2\u4f24\uff0c\u4eff\u4f5b\u6b63\u5728\u5fc3\u91cc\u95ee\u81ea\u5df1\uff1a \u3000\u3000\u2014\u2014\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u662f\u670b\u53cb\u800c\u662f\u4ec7\u654c\uff1f \u3000\u3000\u8fd9\u53e5\u8bdd\u5f53\u7136\u662f\u4e0d\u4f1a\u8bf4\u51fa\u6765\u7684\uff0c\u6731\u731b\u53ea\u8bf4\uff1a\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u4f60\u5bf9\u5f97\u8d77\u6211\u4eec\uff0c\u6211\u4eec\u4e5f\u7edd\u4e0d\u4f1a\u5bf9 \u4e0d\u8d77\u4f60\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u53ea\u53ef\u60dc\u6709\u4e00\u70b9\u8fd8\u662f\u4e0d\u4f1a\u53d8\u7684\u3002\u201d \u3000\u3000\u4ed6\u63e1\u7d27\u53cc\u62f3\uff1a\u201c\u6211\u8fd8\u662f\u6731\u731b\uff0c\u4f60\u8fd8\u662f\u53f8\u9a6c\u8d85\u7fa4\uff0c\u6240\u4ee5\u6211\u8fd8\u662f\u8981\u6740\u4f60\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u4e00\u80a1\u6c14\uff0c\u5c31\u50cf\u662f\u6c38\u751f\u4e0d\u6e1d\u7684\u7231\u60c5\u4e00\u6837\uff0c\u6d77\u53ef\u67af\uff0c\u77f3\u53ef\u70c2\uff0c\u8fd9\u80a1\u6c14\u5374\u6c38\u8fdc\u5b58\u5728\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u6709\u8fd9\u80a1\u6c14\uff0c\u6240\u4ee5\u8fd9\u4e9b\u4ec0\u4e48\u90fd\u6ca1\u6709\u8fde\u6839\u90fd\u6ca1\u6709\u7684\u6c5f\u6e56\u7537\u513f\u624d\u80fd\u6c38\u8fdc\u6d3b\u5728\u6709\u8840\u6027\u7684\u4eba \u4eec\u5fc3\u91cc\u3002 \u3000\u3000\u6731\u731b\u53c8\u9053\uff1a\u201c\u4f60\u521a\u624d\u4e5f\u8bf4\u8fc7\uff0c\u8fd9\u672c\u6765\u5c31\u662f\u6211\u4eec\u4e24\u4e2a\u4eba\u7684\u4e8b\uff0c\u672c\u6765\u5c31\u5e94\u8be5\u7531\u6211\u4eec\u81ea\u5df1\u89e3 \u51b3\u3002\u201d \u3000\u3000\u4ed6\u95ee\u53f8\u9a6c\u8d85\u7fa4\uff1a\u201c\u73b0\u5728\u662f\u4e0d\u662f\u5df2\u7ecf\u5230\u65f6\u5019\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u6731\u731b\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u8bf4\uff1a\u201c\u7ed9\u53f8\u9a6c\u5927\u4fa0\u4e00\u628a\u5200\u3002\u201d \u3000\u3000\u86ee\u725b\u7acb\u523b\u62fe\u8d77\u4e86\u5730\u4e0a\u7684\u5200\uff0c\u7528\u53cc\u4e0b\u9001\u8fc7\u53bb\uff0c\u4e00\u628a\u767e\u70bc\u7cbe\u94a2\u94f8\u6210\u7684\u5927\u5200\uff0c\u5200\u53e3\u4e0a\u5df2\u7ecf\u6709\u597d \u513f\u4e2a\u5730\u65b9\u780d\u7f3a\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u628a\u5200\u4e0d\u662f\u597d\u5200\uff0c\u201d\u6731\u731b\u8bf4\uff1a\u201c\u53ef\u662f\u5728\u53f8\u9a6c\u8d85\u7fa4\u624b\u4e0a\uff0c\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u5200\u90fd\u4e00\u6837\u53ef\u4ee5\u6740 \u4eba\u3002\u201d \u3000\u3000\u201c\u662f\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8f7b\u629a\u5200\u950b\u4e0a\u7684\u5377\u7f3a\u5904\uff1a\u201c\u8fd9\u628a\u5200\u672c\u6765\u5c31\u662f\u6740\u4eba\u7684\u5200\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u53ea\u60f3\u8981\u4f60\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u80fd\u6740\u6211\uff0c\u5200\u4e0b\u5343\u4e07\u4e0d\u8981\u7559\u60c5\u3002\u201d\u6731\u731b\u7684\u58f0\u97f3\u53c8\u53d8\u4e3a\u51c4\u5389\uff1a\u201c\u5426\u5219\u6211\u5c31\u7b97\u6740\u4e86 \u4f60\uff0c\u4e5f\u5fc5\u5c06\u62b1\u61be\u7ec8\u751f\u3002\u201d \u3000\u3000\u4ed6\u5389\u58f0\u95ee\u53f8\u9a6c\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u8981\u6211\u6731\u731b\u4e3a\u4f60\u62b1\u61be\u7ec8\u751f\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7684\u56de\u7b54\u5f88\u660e\u767d\uff1a\u201c\u627e\u82e5\u80fd\u4e00\u5200\u6740\u4e86\u4f60\uff0c\u4f60\u7edd\u4e0d\u4f1a\u770b\u5230\u6211\u7684\u7b2c\u4e8c\u5200\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u201d\u6731\u731b\u8bf4\uff1a\u201c\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u5200\u5149\u4e00\u95ea\uff0c\u6731\u731b\u62e8\u5200\u3002 \u3000\u3000\u5c0f\u5ba4\u4e2d\u6240\u6709\u7684\u4eba\u90fd\u907f\u5f00\u4e86\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f\u6731\u731b\u751f\u6b7b\u4e0e\u5171\u7684\u597d\u5144\u5f1f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u90fd\u907f\u5f00\u4e86\u3002 \u3000\u3000\u4eba\u751f\u81ea\u53e4\u8c01\u65e0\u6b7b\uff0c\u6b7b\uff0c\u6b7b\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff1f\u4f46\u662f\u7537\u5b50\u6c49\u7684\u5c0a\u4e25\u548c\u4e49\u6c14\uff0c\u5374\u662f\u7edd\u5bf9\u4e0d\u5bb9\u4efb\u4f55 \u4eba\u635f\u4f24\u7684\u3002 \u3000\u3000\u6731\u731b\u6a2a\u5200\u5411\u53f8\u9a6c\uff1a\u201c\u6211\u82e5\u5140\u5728\u4f60\u7684\u5200\u4e0b\uff0c\u6211\u7684\u5144\u5f1f\u7edd\u4e0b\u4f1a\u518d\u627e\u4f60\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u6731\u731b\u80fd\u6b7b\u5728\u53f8\u9a6c\u8d85\u7fa4\u7684\u5200\u4e0b\uff0c\u6b7b\u4ea6\u65e0\u61be\u3002\u201d \u3000\u3000\u53ef\u662f\u4ed6\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u56de\u5934\u53bb\u770b\u8776\u821e\u4e00\u773c\uff0c\u8fd9\u4e00\u773c\u4e5f\u8bb8\u5c31\u662f\u4ed6\u6700\u540e\u4e00\u773c\u3002 \u3000\u3000\u2014\u2014\u6211\u82e5\u6b7b\u5728\u4f60\u7684\u5200\u4e0b\uff0c\u53ea\u5e0c\u671b\u4f60\u80fd\u66ff\u6211\u7167\u987e\u5979\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u662f\u4e0d\u4f1a\u8bf4\u51fa\u6765\u7684\u3002\u6731\u731b\u53ea\u8bf4\uff1a\u201c\u4f60\u82e5\u6b7b\u5728\u6211\u7684\u5200\u4e0b\uff0c\u6211\u4e00\u5b9a\u4f1a\u597d\u597d\u7167\u987e\u4f60\u7684\u59bb \u5b50\u513f\u5973\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u59bb\u5b50\u513f\u5973\uff1f\u201d\u53f8\u9a6c\u8d85\u7fa4\u60e8\u7b11\uff0c\u201c\u6211\u7684\u59bb\u5b50\u513f\u5973\u6050\u6015\u53ea\u6709\u7b49\u6211\u6b7b\u5728\u4f60\u7684\u5200\u4e0b\u540e\u624d\u80fd \u53bb\u7167\u987e\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u6731\u731b\u5fc3\u6c89\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\u4ed6\u624d\u53d1\u89c9\u53f8\u9a6c\u7684\u60b2\u4f24\u75db\u82e6\u4e5f\u8bb8\u8fdc\u6bd4\u4ed6\u66f4\u91cd\u66f4\u6df1\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5df2\u62d4\u5200\u3002\u5200\u5df2\u6a2a\u3002 \u3000\u3000\u5fc3\u4e5f\u5df2\u6a2a\u4e86\u3002 \u3000\u3000\u751f\u6b7b\u5df2\u5728\u4e00\u77ac\u95f4\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6050\u6015\u5df2\u7ecf\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u963b\u6b62\u4ed6\u4eec\u8fd9\u751f\u6b7b\u4e00\u6218\u3002 \u3000\u3000\u4f46\u662f\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\u4e00 \u3000\u3000\u201c\u6731\u731b\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u542c\u89c1\u6709\u4eba\u5728\u547c\u5524\uff0c\u58f0\u97f3\u4eff\u4f5b\u662f\u90a3\u4e48\u9065\u8fdc\uff0c\u90a3\u4e48\u9065\u8fdc\u3002 \u3000\u3000\u53ef\u662f\u547c\u5524\u4ed6\u7684\u4eba\u5c31\u5728\u4ed6\u8eab\u8fb9\u3002\u4e00\u4e2a\u968f\u65f6\u90fd\u53ef\u4ee5\u8981\u4ed6\u53bb\u4e3a\u5979\u800c\u6b7b\u7684\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u4ed6\u5728\u68a6\u9b42\u4e2d\u90fd\u65e0\u6cd5\u5fd8\u8bb0\u7684\u4eba\u3002 \u3000\u3000\u53bb\u8005\u5df2\u53bb\uff0c\u6b64\u60c5\u672a\u7edd\uff1b \u3000\u3000\u4e3a\u541b\u4e00\u821e\u3002\u5316\u4f5c\u8774\u8776\u3002 \u3000\u3000\u6731\u731b\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u4ed6\u7684\u5200\u5df2\u5728\u624b\uff0c\u4ed6\u7684\u6b7b\u654c\u5df2\u5728\u4ed6\u5200\u950b\u524d\u3002\u4ed6\u7684\u5144\u5f1f\u90fd\u5728\u770b\u7740\u4ed6\u3002\u4ed6\u5df2\u4e0d\u80fd\u56de\u5934\uff0c\u4ed6\u5df2\u4e49 \u65e0\u53cd\u987e\u3002 \u3000\u3000\u201c\u6731\u731b\uff0c\u201d\u547c\u5524\u58f0\u4e49\u54cd\u8d77\uff1a\u201c\u6731\u731b\u3002\u201d \u3000\u3000\u90a3\u4e48\u9065\u8fdc\u7684\u547c\u5524\u58f0\uff0c\u53c8\u90a3\u4e48\u8fd1\u3002 \u3000\u3000\u90a3\u4e48\u8fd1\u7684\u547c\u58f0\uff0c\u53c8\u90a3\u4e48\u8fdc\uff0c\u8fdc\u5165\u6d6a\u5b50\u68a6\u9b42\u4e2d\u7684\u5f52\u5bbf\u3002 \u3000\u3000\u6d6a\u4e8e\u7684\u5f52\u5bbf\u8fdc\u5728\u6df1\u6df1\u7684\u6df1\u6df1\u7684\u4f24\u75db\u4e2d\u3002 \u3000\u3000\u6731\u731b\u56de\u5934\u3002 \u3000\u3000\u53c8\u662f\u201c\u5f53\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u6731\u731b\u56de\u5934\uff0c\u56de\u5934\u65f6\u5200\u5df2\u843d\u4e0b\uff0c\u56de\u5934\u65f6\u8776\u821e\u6b63\u5728\u770b\u7740\u4ed6\u3002 \u3000\u3000\u5979\u770b\u89c1\u7684\u53ea\u6709\u4ed6\uff0c\u4ed6\u770b\u89c1\u7684\u4e5f\u53ea\u6709\u5979\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u6240\u6709\u7684\u4eba\u90fd\u5df2\u4e0d\u5b58\u5728\uff0c\u6240\u6709\u7684\u4e8b\u4e5f\u90fd\u5df2\u4e0d\u5b58\u5728\u4e86\u3002 \u3000\u3000\u6240\u6709\u7684\u4e00\u5207\u6069\u6028\u4ec7\u6068\u6124\u6012\u60b2\u54c0\u90fd\u5df2\u5316\u4f5c\u4e86\u8774\u8776\u3002 \u3000\u3000\u8774\u8776\u98de\u53bb\u3002 \u516d \u3000\u3000\u8774\u8776\u98de\u53bb\u53c8\u98de\u6765\uff0c\u662f\u6765\uff1f\u662f\u53bb\uff1f\u662f\u4eba\uff1f\u662f\u8776\uff1f \u3000\u3000\u201c\u6731\u731b\uff0c\u6731\u731b\uff0c\u4f60\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u201c\u6211\u5728\uff0c\u6211\u5728\uff0c\u6211\u4e00\u76f4\u90fd\u5728\u3002\u201d \u3000\u3000\u4ed6\u5728\u3002 \u3000\u3000\u5b9d\u5200\u4e0d\u5728\uff0c\u96c4\u72ee\u4e0d\u5728\uff0c\u53f1\u54a4\u4e0d\u53ef\u4e00\u4e16\u7684\u82f1\u96c4\u4e5f\u5df2\u4e0d\u5728\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5728\u3002 \u3000\u3000\u53ea\u8981\u5979\u5728\uff0c\u4ed6\u5c31\u5728\u3002 \u3000\u3000\u201c\u6731\u731b\uff0c\u6211\u9519\u4e86\uff0c\u4f60\u4e5f\u9519\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u6211\u662f\u9519\u4e86\u3002\u201d \u3000\u3000\u201c\u6731\u731b\uff0c\u6211\u4e3a\u4ec0\u4e48\u603b\u662f\u4e0d\u660e\u767d\u4f60\u5fc3\u91cc\u662f\u600e\u4e48\u6837\u5bf9\u6211\u7684\uff1f\u4f60\u4e3a\u4ec0\u4e48\u603b\u662f\u4e0d\u8ba9\u6211\u77e5\u9053\uff1f\u201d\u8776 \u821e\u8bf4\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u603b\u662f\u4e0d\u8ba9\u6211\u77e5\u9053\u4f60\u662f\u591a\u4e48\u559c\u6b22\u6211\uff1f\u6211\u4e3a\u4ec0\u4e48\u603b\u662f\u4e0d\u8ba9\u4f60\u77e5\u9053\u6211\u662f\u591a\u4e48\u9700\u8981 \u4e00\u4e2a\u559c\u6b22\u6211\u7684\u4eba\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\uff0c\u6709\u4e9b\u4e8b\u603b\u662f\u6ca1\u6709\u56de\u7b54\u7684\uff0c\u56e0\u4e3a\u5b83\u6839\u672c\u5c31\u6ca1\u6709\u7b54\u6848\u3002 \u3000\u3000\u201c\u6731\u731b\uff0c\u6211\u8981\u6b7b\u4e86\uff0c\u4f60\u4e0d\u8981\u6b7b\u3002\u201d\u8776\u821e\u8bf4\uff1a\u201c\u6211\u53ef\u4ee5\u6b7b\uff0c\u4f60\u4e0d\u53ef\u4ee5\u6b7b\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5c31\u5982\u96fe\u4e2d\u7684\u6e38\u4e1d\u3002 \u3000\u3000\u201c\u6211\u5df2\u4e0d\u80fd\u518d\u4e3a\u4f60\u800c\u821e\u4e86\uff0c\u4f46\u662f\u6211\u8fd8\u53ef\u4ee5\u4e3a\u4f60\u800c\u5531\u3002\u201d\u8776\u821e\u8bf4\uff1a\u201c\u6211\u5531\uff0c\u4f60\u542c\uff0c\u6211\u4e00\u5b9a \u8981\u5531\uff0c\u4f60\u4e00\u5b9a\u8981\u542c\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u4f60\u5531\uff0c\u6211\u542c\u3002\u201d \u3000\u3000\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u4eba\uff0c\u6ca1\u6709\u6028\uff0c\u6ca1\u6709\u4ec7\u6068\uff0c\u9664\u4e86\u5979\u8981\u5531\u7684\u6b4c\u58f0\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u5979\u5531\u3002 \u3000\u3000\u201c\u5b9d\u9afb\u5306\u5306\u68b3\u5c31\uff0c\u94c5\u534e\u6de1\u6de1\u5986\u6210\uff1b \u3000\u3000\u9752\u70df\u7d2b\u96fe\u7f69\u8f7b\u76c8\uff0c\u98de\u7d6e\u6e38\u4e1d\u65e0\u5b9a\u3002 \u3000\u3000\u76f8\u89c1\u4e0d\u5982\u4e0d\u89c1\uff0c\u6709\u60c5\u4f55\u4f3c\u65e0\u60c5\uff1b \u3000\u3000\u7b19\u6b4c\u6563\u540e\u9152\u521d\u9192\uff0c\u6df1\u9662\u6708\u659c\u4eba\u9759\u3002\u201d \u3000\u3000\u6e38\u4e1d\u6e10\u8d70\u66f4\u8fdc\u66f4\u505c\u3002 \u3000\u3000\u5979\u5531\uff0c\u5979\u5df2\u5531\u8fc7\u3002 \u3000\u3000\u5979\u505c\u3002 \u3000\u3000\u5929\u5730\u95f4\u6240\u6709\u7684\u4e00\u5207\u90fd\u5df2\u505c\u6b62\uff0c\u81f3\u5c11\u5728\u8fd9\u4e00\u77ac\u95f4\u90fd\u5df2\u505c\u6b62\u3002 \u3000\u3000\u4eba\u95f4\u5df2\u4e0d\u518d\u6709\u821e\uff0c\u4e5f\u4e0d\u5189\u6709\u6b4c\uff0c\u4eba\u95f4\u4ec0\u4e48\u90fd\u5df2\u4e0d\u518d\u6709\u3002\u8fde\u6cea\u90fd\u4e0d\u518d\u6709\u3002 \u3000\u3000\u53ea\u6709\u8840\u3002 \u3000\u3000\u6731\u731b\u75f4\u75f4\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u75f4\u75f4\u7684\u770b\u770b\u5979\uff0c\u5ffd\u7136\u4e00\u53e3\u9c9c\u8840\u5410\u4e86\u51fa\u6765\u3002 \u7b2c\u5341\u4e03\u7ae0\u3000\u4e00\u5251\u5149\u5bd2>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u5341\u4e03\u7ae0\u3000\u4e00\u5251\u5149\u5bd2 \u4e00 \u3000\u3000\u4e8c\u6708\u4e8c\u5341\u516d\u3002 \u3000\u3000\u957f\u5b89\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u5728\u7b49\u3002 \u3000\u3000\u90d1\u8bda\u544a\u8bc9\u4ed6\uff1a\u201c\u5353\u5148\u751f\u6682\u65f6\u8fd8\u4e0d\u80fd\u89c1\u4f60\uff0c\u4f46\u4ed6\u8bf4\u4f60\u53ef\u4ee5\u5728\u8fd9\u91cc\u7b49\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5fae\u7b11\uff1a\u201c\u6211\u4f1a\u7b49\u7684\u3002\u201d\u4ed6\u7684\u7b11\u5bb9\u6e29\u548c\u5e73\u9759\uff1a\u201c\u6211\u53ef\u4ee5\u5411\u4f60\u4fdd\u8bc1\uff0c\u4f60\u4e00\u5b9a\u4ece\u6765\u90fd\u6ca1\u6709 \u89c1\u8fc7\u50cf\u6211\u8fd9\u4e48\u6837\u4f1a\u7b49\u4eba\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u6bd4\u8c01\u90fd\u6709\u8010\u6027\uff0c\u4e5f\u8bb8\u6bd4\u4e00\u4e2a\u516b\u5341\u5c81\u7684\u8001\u5934\u5b50\u8fd8\u6709\u8010\u6027\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u4ece\u5c0f\u4f4f \u5728\u6df1\u5c71\u91cc\uff0c\u6709\u4e00\u6b21\u4e3a\u4e86\u7b49\u7740\u770b\u4e00\u6735\u5c71\u8336\u5f00\u82b1\uff0c\u4f60\u731c\u6211\u7b49\u4e86\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u4f60\u7b49\u4e86\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u6211\u8db3\u8db3\u7b49\u4e86\u4e09\u5929\u3002\u201d \u3000\u3000\u201c\u7136\u540e\u4f60\u5c31\u628a\u90a3\u6735\u82b1\u6458\u4e0b\u6765\u63d2\u5728\u8863\u895f\u4e0a\u4e86\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\uff0c\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u7b49\u5230\u82b1\u5f00\u4e86\uff0c\u6211\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u7b49\u4e86\u4e09\u5929\uff0c\u5c31\u4e3a\u4e86\u8981\u770b\u82b1\u5f00\u65f6\u90a3\u4e00\u77ac\u95f4\u7684\u60c5\u51b5\uff1f\u201d \u3000\u3000\u90d1\u8bda\u81ea\u5df1\u4e5f\u662f\u4e2a\u5f88\u6709\u8010\u6027\u7684\u4eba\uff0c\u800c\u4e14\u597d\u50cf\u80fd\u591f\u660e\u767d\u5c0f\u9ad8\u7684\u610f\u601d\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u4f60\u5728\u7b49\u7684\u662f\u4ec0\u4e48\uff0c\u901a\u5e38\u90fd\u4e0d\u4f1a\u6ca1\u6709\u76ee\u7684\u3002\u201d\u4ed6\u5bf9\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4f60\u867d\u7136\u6ca1\u6709\u628a\u90a3\u6735\u82b1 \u6458\u4e0b\u6765\uff0c\u53ef\u662f\u4f60\u7684\u76ee\u7684\u4e00\u5b9a\u5df2\u8fbe\u5230\uff0c\u800c\u4e14\u4f60\u7684\u76ee\u7684\u7edd\u4e0d\u662f\u4ec5\u4ec5\u4e3a\u4e86\u8981\u770b\u4e00\u6735\u5c71\u8336\u82b1\u5f00\u800c \u5df2\u3002\u201d \u3000\u3000\u201c\u6211\u4f1a\u6709\u4ec0\u4e48\u522b\u7684\u76ee\u7684\uff1f\u201d \u3000\u3000\u201c\u4e00\u6735\u82b1\u4e5f\u662f\u4e00\u4e2a\u751f\u547d\uff0c\u5728\u90a3\u6735\u82b1\u5f00\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4e5f\u5c31\u662f\u751f\u547d\u8bde\u751f\u7684\u65f6\u5019\uff0c\u201d\u90d1\u8bda\u8bf4\uff1a \u201c\u4e00\u4e2a\u751f\u547d\u5728\u5929\u5730\u5b55\u80b2\u4e2d\u8bde\u751f\uff0c\u5176\u4e2d\u53d8\u5316\u4e4b\u7cbe\u5fae\u5947\u5999\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u6bd4\u5f97\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u5c0f\u9ad8\uff1a\u201c\u6240\u4ee5\u6211\u60f3\u4f60\u90a3\u4e09\u5929\u65f6\u95f4\u5e76\u6ca1\u865a\u8017\uff0c\u7ecf\u8fc7\u90a3\u6b21\u89c2\u5bdf\u540e\uff0c\u4f60\u7684\u5251\u6cd5\u4e00\u5b9a\u7cbe \u8fdb\u4e0d\u5c11\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u8fd9\u4e2a\u957f\u7740\u4e00\u5f20\u5e73\u5e73\u51e1\u51e1\u7684\u56db\u65b9\u8138\u7684\u5e74\u8f7b\u4eba\uff0c\u8fdc\u6bd4\u4ed6\u770b\u8d77\u6765\u7684\u6837\u5b50\u806a \u660e\u5f97\u591a\u3002 \u3000\u3000\u201c\u7b49\u4eba\u66f4\u4e0d\u4f1a\u6ca1\u6709\u76ee\u7684\uff0c\u4f60\u5f53\u7136\u4e5f\u4e0d\u4f1a\u7b49\u5230\u5353\u5148\u751f\u4e00\u6765\u5c31\u8d70\u7684\u3002\u201d\u90d1\u8bda\u6de1\u6de1\u7684\u95ee\u5c0f\u9ad8\uff1a \u201c\u4f60\u8fd9\u6b21\u7684\u76ee\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u5c0f\u9ad8\u5f00\u53e3\uff0c\u53c8\u8bf4\uff1a\u201c\u8fd9\u4e2a\u95ee\u9898\u4f60\u7528\u4e0d\u7740\u56de\u7b54\u6211\uff0c\u6211\u4e5f\u4e0d\u60f3\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4f60\u81ea\u5df1\u95ee\u6211\u7684\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u8981\u6211\u56de\u7b54\uff0c\u53c8\u4e0d\u60f3\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4e00\u4e2a\u4eba\u77e5\u9053\u7684\u4e8b\u8d8a\u5c11\u8d8a\u597d\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u6839\u672c\u4e0d\u60f3\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u95ee\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u4e0d\u8fc7\u5728\u63d0\u9192\u4f60\uff0c\u6211\u65e2\u7136\u4f1a\u8fd9\u4e48\u8bf4\uff0c\u5353\u5148\u751f\u4e00\u5b9a\u4e5f\u4f1a\u8fd9\u4e48\u60f3\u7684\u3002\u201d \u3000\u3000\u90d1\u8bda\u8bf4\uff1a\u201c\u7b49\u5230\u5353\u5148\u751f\u95ee\u4f60\u8fd9\u4e2a\u95ee\u9898\u65f6\uff0c\u4f60\u6700\u597d\u6709\u4e00\u4e2a\u5f88\u597d\u7684\u7406\u7531\u56de\u7b54\u4ed6\uff0c\u800c\u4e14\u80fd\u591f\u8ba9 \u4ed6\u6ee1\u610f\uff0c\u5426\u5219\u4f60\u6700\u597d\u5c31\u4e0d\u8981\u518d\u7b49\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5f88\u4e25\u8083\u800c\u8bda\u6073\uff1a\u201c\u8ba9\u5353\u5148\u751f\u89c9\u5f97\u4e0d\u6ee1\u610f\u7684\u4eba\uff0c\u73b0\u5728\u8fd8\u80fd\u591f\u6d3b\u7740\u7684\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u5c31\u8d70\u4e86\uff0c\u4ed6\u5e76\u4e0d\u60f3\u7b49\u7740\u770b\u5c0f\u9ad8\u5bf9\u4ed6\u8bf4\u7684\u8fd9\u53e5\u8bdd\u6709\u4ec0\u4e48\u53cd\u5e94\u3002 \u3000\u3000\u53ef\u662f\u8d70\u5230\u95e8\u53e3\uff0c\u4ed6\u53c8\u56de\u8fc7\u5934\uff0c\u201c\u8fd8\u6709\u4ef6\u4e8b\u6211\u5fd8\u4e86\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5353\u5148\u751f\u8fd8\u5429\u5490\u8fc7\u6211\uff0c\u4f60\u8981\u4ec0\u4e48\uff0c\u5c31\u7ed9\u4f60\u4ec0\u4e48\uff0c\u4e0d\u7ba1\u4f60\u8981\u4ec0\u4e48\u90fd\u884c\u3002\u201d \u3000\u3000\u201c\u4ed6\u771f\u7684\u662f\u8fd9\u4e48\u6837\u8bf4\u7684\uff1f\u201d \u3000\u3000\u201c\u771f\u7684\u662f\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7b11\u4e86\uff0c\u7b11\u5f97\u975e\u5e38\u6109\u5feb\uff1a\u201c\u90a3\u5c31\u597d\u6781\u4e86\uff0c\u771f\u7684\u597d\u6781\u4e86\u3002\u201d \u4e8c \u3000\u3000\u5353\u4e1c\u6765\u53ec\u89c1\u90d1\u8bda\u65f6\uff0c\u5df2\u7ecf\u63a5\u8fd1\u6b63\u5348\u3002\u90d1\u8bda\u5b8c\u5168\u770b\u4e0d\u51fa\u4ed6\u548c\u5e73\u65f6\u6709\u4ec0\u4e48\u4e0d\u540c\u7684\u5730\u65b9\uff0c\u5c31\u5728 \u6628\u5929\u4e00\u65e5\u95ee\u53d1\u751f\u7684\u90a3\u4e9b\u60b2\u60e8\u800c\u53ef\u6015\u7684\u4e8b\uff0c\u770b\u6765\u5c31\u597d\u50cf\u8ddf\u4ed6\u8fde\u4e00\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\uff0c\u5353\u9752\u5df2\u7ecf\u505a\u51fa \u4e9b\u4ec0\u4e48\u4e8b\u6765\u62a5\u590d\u4ed6\uff1f\u4ed6\u4e5f\u7edd\u53e3\u4e0d\u95ee\u3002 \u3000\u3000\u4ed6\u53ea\u95ee\u90d1\u8bda\uff0c\u201c\u9ad8\u6e10\u98de\u662f\u4e0d\u662f\u8fd8\u5728\u7b49\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u4ed6\u8fd8\u5728\u7b49\u3002\u201d\u90d1\u8bda\u8bf4\uff1a\u201c\u4f46\u662f\u4ed6\u8981\u7684\u4e1c\u897f\u6211\u5374\u6ca1\u6cd5\u5b50\u5b8c\u5168\u66ff\u4ed6\u627e\u5230\u3002\u201d \u3000\u3000\u201c\u4ed6\u8981\u7684\u662f\u4ec0\u4e48\uff0c\u8fde\u4f60\u90fd\u627e\u4e0d\u5230\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8981\u6211\u5728\u4e00\u4e2a\u65f6\u8fb0\u91cc\u66ff\u4ed6\u51c6\u5907\u4e8c\u5341\u684c\u6700\u597d\u7684\u9152\u83dc\uff0c\u800c\u4e14\u9650\u5b9a\u8981\u957f\u5b89\u5c45\u548c\u660e\u6e56\u6625\u4e24\u4e2a\u5730 \u65b9\u7684\u53a8\u5b50\u6765\u505a\u3002\u201d\u90d1\u8bda\u8bf4\uff1a\u201c\u4ed6\u8fd8\u8981\u6211\u5728\u4e00\u4e2a\u65f6\u8fb0\u91cc\u628a\u57ce\u91cc\u6240\u6709\u7684\u7ea2\u59d1\u5a18\u90fd\u627e\u6765\u966a\u4ed6\u559d \u9152\u3002\u201d \u3000\u3000\u201c\u4f60\u66ff\u4ed6\u627e\u6765\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u66ff\u4ed6\u627e\u6765\u4e03\u5341\u4e09\u4e2a\uff0c\u5176\u4e2d\u6709\u4e00\u5927\u534a\u90fd\u662f\u4ece\u522b\u7684\u7537\u4eba\u88ab\u7a9d\u91cc\u62c9\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5c45\u7136\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u5728\u90a3\u4e2a\u65f6\u5019\uff0c\u88ab\u7a9d\u91cc\u6ca1\u6709\u7537\u4eba\u7684\u59d1\u5a18\uff0c\u4e5f\u5c31\u4e0d\u80fd\u7b97\u7ea2\u59d1\u5a18\u4e86\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4f60\u529e \u5f97\u5df2\u7ecf\u5f88\u4e0d\u9519\uff0c\u4eca\u5929\u65e9\u4e0a\u6211\u4eec\u8fd9\u5730\u65b9\u4e00\u5b9a\u5f88\u70ed\u95f9\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u70ed\u95f9\u6781\u4e86\uff0c\u8fde\u9556\u5c40\u91cc\u4f1a\u559d\u9152\u7684\u5f1f\u5144\u4eec\uff0c\u90fd\u88ab\u4ed6\u62c9\u53bb\u966a\u4ed6\u559d\u9152\u3002\u201d\u90d1\u8bda\u9053\uff1a\u201c\u4ed6\u4e00 \u5b9a\u8981\u6bcf\u4e2a\u4eba\u90fd\u597d\u597d\u7684\u4e3a\u4ed6\u5e86\u795d\u4e00\u756a\u3002\u201d \u3000\u3000\u201c\u5e86\u795d\uff1f\u5e86\u795d\u4ec0\u4e48\uff1f\u201d\u5353\u4e1c\u6765\u95ee\uff1a\u201c\u4eca\u5929\u6709\u4ec0\u4e48\u503c\u5f97\u4ed6\u5e86\u795d\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6ca1\u8bf4\u3002\u201d\u90d1\u8bda\u9053\uff1a\u201c\u53ef\u662f\u6211\u4ee5\u524d\u542c\u8bf4\u8fc7\uff0c\u6709\u5f88\u591a\u4eba\u5728\u77e5\u9053\u81ea\u5df1\u5feb\u8981\u6b7b\u7684\u65f6\u5019\u90fd\u4f1a\u8fd9 \u6837\u505a\u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6c89\u601d\u7740\uff0c\u77b3\u5b54\u5ffd\u7136\u53c8\u5f00\u59cb\u6536\u7f29\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u8bf4\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u77e5\u9053\u4ed6\u6682\u65f6\u8fd8\u6b7b\u4e0d \u4e86\u3002\u201d \u4e09 \u3000\u3000\u9152\u5df2\u9189\uff0c\u5ba2\u5df2\u6563\uff0c\u524d\u9762\u7684\u82b1\u5385\u548c\u8d70\u5eca\u4e0a\uff0c\u9664\u4e86\u6563\u6ee1\u4e00\u5730\u65ad\u9497\u843d\u73af\u3001\u8170\u5e26\u7f57\u889c\u548c\u51e0\u4e2a\u8dcc\u788e \u4e86\u7684\u9f3b\u70df\u58f6\u548c\u80ed\u8102\u76d2\u5916\uff0c\u8fd8\u6709\u4e9b\u8ba9\u4eba\u8fde\u60f3\u90fd\u60f3\u4e0d\u5230\u7684\u4e1c\u897f\uff0c\u597d\u50cf\u7279\u5730\u8981\u5411\u4e3b\u4e49\u8bc1\u660e\uff0c\u4ed6\u4eec\u7684 \u786e\u90fd\u5df2\u9189\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u4e3b\u4eba\u5462\uff1f \u3000\u3000\u4e3b\u4eba\u4e0d\u9189\uff0c\u5ba2\u4eba\u600e\u4e48\u80fd\u5c3d\u6b22\uff1f \u3000\u3000\u5c0f\u9ad8\u5c31\u50cf\u662f\u4e2a\u6b7b\u4eba\u4e00\u6837\uff0c\u4f46\u7740\u809a\u5b50\u8eba\u5728\u4e00\u5f20\u8f6f\u69bb\u4e0a\uff0c\u53ef\u662f\u7b49\u5230\u5353\u4e1c\u6765\u8d70\u5230\u4ed6\u9762\u524d\u65f6\uff0c\u8fd9 \u4e2a\u6b7b\u4eba\u5ffd\u7136\u95f4\u5c31\u9192\u4e86\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u7b49\u5230\u66f2\u7ec8\u4eba\u6563\u624d\u6765\uff1f\u96be\u9053\u4f60\u5929\u751f\u5c31\u4e0d\u559c\u6b22\u770b\u5230\u522b\u4eba\u5f00\u5fc3\u7684\u6837\u5b50\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u6211\u7684\u786e\u4e0d\u559c\u6b22\uff0c\u9192\u773c\u770b\u9189\u4eba\uff0c\u5e76\u4e0d\u662f\u4ef6\u5f88\u6709\u8da3\u7684 \u4e8b\u2026\u2026\u201d \u3000\u3000\u4ed6\u76ef\u7740\u5c0f\u9ad8\u7684\u773c\u775b\uff1a\u201c\u5e78\u597d\u4f60\u8fd8\u6ca1\u6709\u9189\uff0c\u9189\u7684\u662f\u522b\u4eba\uff0c\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u773c\u775b\u91cc\u8fde\u4e00\u70b9\u9152\u610f\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u201c\u6211\u770b\u5f97\u51fa\u4f60\u8fd8\u5f88\u6e05\u9192\uff0c\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6bd4\u4e09\u6708\u5929\u7684\u5154\u5b50\u8fd8\u6e05\u9192\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7b11\u4e86\uff0c\u5927\u7b11\u3002 \u3000\u3000\u201c\u4f60\u6ca1\u6709\u770b\u9519\uff0c\u786e\u5b9e\u6ca1\u6709\u770b\u9519\u3002\u201d\u4ed6\u5927\u7b11\u9053\uff1a\u201c\u4f60\u7684\u773c\u775b\u7b80\u76f4\u6bd4\u4e5d\u6708\u5929\u7684\u72d0\u72f8\u8fd8\u5229\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u522b\u4eba\u9189\uff0c\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e0d\u9189\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u72d0\u72f8\u8fdf\u65e9\u4f1a\u6765\u7684\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6709\u72d0\u72f8\u8981\u6765\uff0c\u5154\u5b50\u600e\u4e48\u80fd\u4e0d\u4fdd\u6301\u6e05\u9192\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u72d0\u72f8\u6765\u4e86\uff0c\u5154\u5b50\u518d\u6e05\u9192\u4e5f\u6ca1\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u77e5\u9053\u6709\u72d0\u72f8\u8981\u6765\uff0c\u514d\u5b50\u5c31\u5e94\u8be5\u8d76\u5feb\u9003\u8d70\u624d\u5bf9\u3002\u201d\u5353\u4e1c\u6765\u7b11\u9053\uff1a\u201c\u9664\u975e\u8fd9\u4e2a\u5154\u5b50\u6839 \u672c\u5c31\u4e0d\u6015\u72d0\u72f8\uff01\u201d \u3000\u3000\u201c\u5154\u5b50\u600e\u4e48\u4f1a\u4e0d\u6015\u72d0\u72f8\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5b83\u540e\u9762\u8fd8\u6709\u4e00\u6839\u62a2\uff0c\u8fd9\u6839\u67aa\u5df2\u7ecf\u5bf9\u51c6\u4e86\u72d0\u72f8\u7684\u5fc3\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u523a\u8fdb\u53bb\u3002\u201d \u3000\u3000\u201c\u67aa\uff1f\u201d\u5c0f\u9ad8\u7728\u4e86\u7728\u773c\uff1a\u201c\u54ea\u91cc\u6765\u7684\u67aa\uff1f\u201d \u3000\u3000\u5353\u4e1c\u672a\u7b11\u4e86\u7b11\uff1a\u201c\u5f53\u7136\u662f\u4ece\u4e00\u53e3\u7bb1\u5e72\u91cc\u6765\u7684\uff0c\u4e00\u53e3\u5931\u800c\u590d\u5f97\u7684\u7bb1\u5b50\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u4e0d\u7b11\u4e86\uff0c\u773c\u775b\u4e5f\u4e0d\u518d\u7728\uff0c\u800c\u4e14\u9732\u51fa\u4e86\u4e00\u79cd\u4ece\u5fc3\u91cc\u5c31\u89c9\u5f97\u5f88\u4f69\u670d\u7684\u8868\u60c5\u3002 \u3000\u3000\u201c\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\uff1f\u201d\u4ed6\u95ee\u5353\u4e1c\u6765\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u6211\u77e5\u9053\u4e86\u4ec0\u4e48\uff1f\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u77e5\u9053\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6709\u79cd\u4eba\uff0c\u5982\u679c\u5403\u4e86\u522b \u4eba\u4e00\u6b21\u4e8f\uff0c\u5c31\u4e00\u5b9a\u4f1a\u60f3\u6cd5\u5b50\u52a0\u5341\u500d\u53bb\u8ba8\u56de\u6765\uff0c\u6211\u53ea\u4e0d\u8fc7\u77e5\u9053\u8427\u6cea\u8840\u6070\u5de7\u5c31\u662f\u8fd9\u79cd\u4eba\uff0c\u800c\u4e14\u6070 \u5de7\u627e\u5230\u4e86\u4f60\u3002\u201d \u3000\u3000\u4ed6\u53c8\u7b11\u4e86\u7b11\uff1a\u201c\u6211\u77e5\u9053\u7684\u53ea\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff0c\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u8fd9\u5df2\u7ecf\u4e0d\u662f\u5982\u6b64\u800c\u5df2\u4e86\uff0c\u5df2\u7ecf\u591f\u591a\u4e86\u3002\u201d\u4ed6\u53f9\u606f\u7740\u9053\uff1a\u201c\u96be\u602a\u8427\u6cea\u8840\u544a\u8bc9\u6211\uff0c\u80fd\u591f\u548c \u5353\u5148\u751f\u8c08\u751f\u610f\u7edd\u5bf9\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\uff0c\u56e0\u4e3a\u6709\u4e9b\u4e8b\u4f60\u6839\u672c\u4e0d\u5fc5\u8bf4\u51fa\u6765\uff0c\u4ed6\u5df2\u7ecf\u5b8c\u5168\u77e5\u9053\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u5fae\u7b11\u4eff\u4f5b\u5df2\u53d8\u4e3a\u82e6\u7b11\uff1a\u201c\u53ef\u60dc\u6211\u81ea\u5df1\u8fd8\u4e0d\u77e5\u9053\u81ea\u5df1\u7a76\u7adf\u5df2\u7ecf\u77e5\u9053\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u6b21\u662f\u8427\u6cea\u8840\u8981\u6211\u6765\u7684\uff1f\u201d\u5c0f\u9ad8\u81ea\u5df1\u56de\u7b54\u4e86\u8fd9\u95ee\u9898\u3002\u201c\u4f60\u5f53\u7136\u5df2\u7ecf\u77e5\u9053\uff0c \u800c\u4e14\u4f60\u4e00\u5b9a\u5df2\u7ecf\u77e5\u9053\u4ed6\u8981\u6211\u6765\u8ddf\u4f60\u8c08\u7684\u7edd\u4e0d\u662f\u4ec0\u4e48\u597d\u4e8b\u3002\u201d \u3000\u3000\u201c\u4e0d\u597d\u7684\u4e8b\u4e5f\u6709\u5f88\u591a\u79cd\u3002\u201d\u5353\u4e1c\u6765\u95f4\uff1a\u201c\u4ed6\u8981\u4f60\u6765\u8c08\u7684\u662f\u54ea\u4e00\u79cd\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u6700\u4e0d\u597d\u7684\u4e00\u79cd\u3002\u201d\u5c0f\u9ad8\u53c8\u5728\u53f9\u606f\uff1a\u201c\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u6211\u6b20\u4ed6\u4e00\u70b9\u60c5\uff0c\u8fd9\u79cd\u4e8b\u8fde\u6211 \u90fd\u4e0d\u613f\u610f\u6765\u8ddf\u4f60\u8c08\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff01\u201d\u5353\u4e1c\u6765\u5c45\u7136\u53c8\u5728\u5fae\u7b11\uff1a\u201c\u8fd9\u4e00\u70b9\u4f60\u9519\u4e86\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u201c\u5728\u67d0\u4e00\u65b9\u9762\u6765\u8bf4\uff0c\u6700\u597d\u7684\u4e8b\u5f80\u5f80\u90fd\u662f\u6700\u4e0d\u597d\u7684\u4e8b\uff0c\u6240\u4ee5\u5728\u53e6\u4e00\u65b9\u9762\u6765\u8bf4\uff0c\u6700\u4e0d\u597d\u7684\u4e8b \u672c\u6765\u5c31\u662f\u6700\u597d\u7684\u4e8b\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4eba\u95f4\u4e8b\u5f80\u5f80\u5c31\u6709\u5f88\u591a\u7686\u5982\u662f\u3002\u201d \u3000\u3000\u4ed6\u53c8\u89e3\u91ca\uff1a\u201c\u5982\u679c\u8427\u5148\u751f\u6839\u672c\u5c31\u4e0d\u8981\u4eba\u6765\u8ddf\u6211\u8c08\uff0c\u5374\u5728\u591c\u534a\u65e0\u4eba\u65f6\u63d0\u7740\u4ed6\u7684\u90a3\u53e3\u7bb1\u5b50\u6765 \u627e\u6211\uff0c\u90a3\u79cd\u4e8b\u624d\u662f\u6700\u4e0d\u597d\u7684\u4e00\u79cd\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4e0d\u7ba1\u4ed6\u8981\u6211\u672a\u8ddf\u4f60\u8c08\u7684\u662f\u4ec0\u4e48\u4e8b\uff0c\u4f60\u90fd\u4e0d\u4f1a\u89c9\u5f97\u4e0d\u592a\u6109\u5feb\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u53ef\u662f\u5c0f\u9ad8\u7684\u8868\u60c5\u5374\u5ffd\u7136\u53d8\u5f97\u5f88\u4e25\u8083\uff0c\u4eff\u6548\u7740\u5353\u4e1c\u6765\u7684\u53e3\u6c14\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u4ed6\u8981 \u6211\u6765\u63a5\u66ff\u53f8\u9a6c\u8d77\u7fa4\u7684\u4f4d\u7f6e\uff0c\u6765\u63a5\u62ff\u5927\u7f25\u5c40\u7684\u4ee4\u7b26\uff0c\u5f53\u5927\u9556\u5c40\u7684\u603b\u5c40\u4e3b\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u65e0\u8bba\u8c01\u90fd\u8ba4\u4e3a\u5353\u4e1c\u6765\u4e00\u5b9a\u4f1a\u8df3\u8d77\u6765\u7684\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u970e\u4e00\u970e\uff0c\u53ea\u6de1\u6de1\u7684\u95ee\u5c0f\u9ad8\uff1a\u201c\u8fd9\u771f\u662f\u8427\u5148\u751f\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53cd\u95ee\u5353\u4e1c\u6765\uff1a\u201c\u4f60\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u8fde\u8003\u8651\u90fd\u6ca1\u6709\u8003\u8651\uff0c\u5c31\u7b80\u7b80\u5355\u5355\u7684\u8bf4\u51fa\u4e86\u4e24\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\uff1f\u201d\u5c0f\u9ad8\u53cd\u800c\u89c9\u5f97\u5f88\u60ca\u8bb6\uff1a\u201c\u5f88\u597d\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u5fae\u7b11\uff0c\u5411\u5c0f\u9ad8\u97a0\u8eac\u3002 \u3000\u3000\u201c\u5f88\u597d\u7684\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u73b0\u5728\u9601\u4e0b\u5df2\u7ecf\u662f\u5927\u9556\u5c40\u7684\u7b2c\u4e00\u53f7\u9996\u8111\uff0c\u5df2\u7ecf\u5750\u4e0a\u5927\u9576\u5c40\u7684\u7b2c\u4e00\u628a \u4ea4\u6905\u4e86\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u6014\u4f4f\u3002 \u3000\u3000\u5353\u4e1c\u6765\u5bf9\u4ed6\u7684\u6001\u5ea6\u5df2\u7ecf\u5f00\u59cb\u53d8\u5f97\u5f88\u606d\u656c\u3002 \u3000\u3000\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u5927\u9556\u5c40\u5c5e\u4e0b\u7684\u4e09\u5341\u516d\u8def\u597d\u6c49\uff0c\u5df2\u7ecf\u5168\u90e8\u5c5e\u4e8e\u4f60\u7684\u7edf\u8f96\u4e4b\u4e0b\uff0c\u5982\u679c\u6709\u4eba\u4e0d \u670d\uff0c\u5353\u4e1c\u6765\u613f\u4e3a\u5148\u950b\uff0c\u5c06\u4ed6\u7acb\u65a9\u4e8e\u5200\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u7528\u4ed6\u90a3\u53cc\u6697\u7070\u8272\u7684\u773c\u775b\u6b63\u89c6\u770b\u5c0f\u9ad8\uff1a\u201c\u53ef\u662f\u4ece\u4eca\u4ee5\u540e\uff0c\u4f60\u4e5f\u662f\u5927\u9556\u5c40\u7684\u4eba\u4e86\uff0c\u5927\u9556\u5c40 \u552f\u4f60\u9a6c\u9996\u662f\u77bb\uff0c\u4f60\u4e5f\u8981\u4e3a\u5927\u9556\u5c40\u5c3d\u5fe0\u5c3d\u529b\uff0c\u5927\u9556\u5c40\u7684\u56f0\u96be\uff0c\u5c31\u662f\u4f60\u7684\u56f0\u96be\uff0c\u5927\u9556\u5c40\u7684\u4ec7\u654c\uff0c \u4e5f\u5c31\u662f\u4f60\u7684\u4ec7\u654c\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7ec8\u4e8e\u5410\u51fa\u53e3\u6c14\u3002 \u3000\u3000\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u82e6\u7b11\uff1a\u201c\u672c\u6765\u6211\u8fd8\u4e0d\u660e\u767d\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u7b54\u5e94\u5f97\u8fd9\u4e48\u5feb\uff0c\u73b0\u5728\u6211\u603b\u7b97\u660e\u767d\u4f60\u7684\u610f\u601d \u4e86\u3002\u201d \u3000\u3000\u201c\u4e8b\u60c5\u672c\u6765\u5c31\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u6b63\u5982\u5b9d\u5251\u7684\u53cc\u950b\u4e00\u6837\u3002\u201d\u5353\u4e1c\u672a\u7684\u58f0\u97f3\u4e25\u8083\u9762\u5e73\u9759\uff1a\u201c\u8981\u6709 \u6240\u6536\u83b7\uff0c\u5c31\u5fc5\u9700\u4ed8\u51fa\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5ffd\u7136\u53d8\u5f97\u6709\u4e9b\u5636\u54d1\uff1a\u201c\u6211\u60f3\u4f60\u4e00\u5b9a\u4e5f\u77e5\u9053\u53f8\u9a6c\u8d85\u7fa4\u66fe\u7ecf\u4ed8\u51fa\u8fc7\u4ec0\u4e48\u6837\u7684\u4ee3 \u4ef7\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u5c0f\u9ad8\u5ffd\u7136\u95ee\u4ed6\uff1a\u201c\u4f60\u4ed8\u51fa\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u6211\u4ed8\u51fa\u8fc7\u4ec0\u4e48\uff1f\u6211\u53c8\u5f97\u5230\u4ec0\u4e48\uff1f\u201d\u4ed6\u7684\u7b11\u5bb9\u4e2d\u7adf\u7136\u5145\u6ee1\u4f24\u611f\uff1a\u201c\u8fd9\u4e2a\u95ee\u9898\u6211\u6050\u6015\u4e0d\u80fd \u56de\u7b54\u4f60\uff0c\u56e0\u4e3a\u6211\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\uff0c\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u4e0d\u662f\u8c0e\u8bdd\uff0c\u800c\u4e14\u8bf4\u5f97\u786e\u5b9e\u6709\u70b9\u611f\u4f24\uff0c\u751a\u81f3\u8fde\u5c0f\u9ad8\u90fd\u5f00\u59cb\u6709\u70b9\u540c\u60c5\u4ed6\u4e86\u3002 \u3000\u3000\u5e78\u597d\u5353\u4e1c\u6765\u7acb\u523b\u6062\u590d\u4e86\u5ca9\u77f3\u822c\u7684\u51b7\u9759\uff0c\u800c\u4e14\u7acb\u523b\u63d0\u51fa\u4e86\u4e00\u4e2a\u6bd4\u5200\u950b\u66f4\u5c16\u9510\u7684\u95ee\u9898\u3002 \u3000\u3000\u201c\u6211\u613f\u610f\u62e5\u7acb\u4f60\u4e3a\u9556\u5c40\u4e4b\u4e3b\uff0c\u6211\u4e5f\u613f\u610f\u4e3a\u4f60\u6548\u5fe0\u6548\u529b\u3002\u6211\u76f8\u4fe1\u6211\u4eec\u5f7c\u6b64\u90fd\u5df2\u7ecf\u5f88\u4e86\u89e3\uff0c \u8fd9\u6837\u505a\u5bf9\u6211\u4eec\u90fd\u6709\u597d\u5904\uff01\u201d\u4ed6\u95ee\u5c0f\u9ad8\uff1a\u201c\u53ef\u662f\u522b\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u522b\u4eba\uff1f\u201d \u3000\u3000\u201c\u5927\u9556\u5c40\u5c5e\u4e0b\u7684\u4e09\u5341\u516d\u8def\u4eba\u9a6c\uff0c\u6ca1\u6709\u4e00\u4e2a\u662f\u597d\u60f9\u7684\u89d2\u8272\uff0c\u8981\u4ed6\u4eec\u8bda\u5fc3\u62e5\u6234\u4f60\u4e3a\u603b\u74e2\u628a \u5b50\uff0c\u5f88\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u53c8\u95ee\u5c0f\u9ad8\uff1a\u201c\u4f60\u51c6\u5907\u600e\u4e48\u505a\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u6211\u5e94\u8be5\u600e\u4e48\u505a\uff1f\u201d \u3000\u3000\u201c\u5148\u8981\u6709\u5a01\uff0c\u624d\u80fd\u6709\u4fe1\uff0c\u6709\u4e86\u5a01\u4fe1\uff0c\u624d\u80fd\u53f7\u4eca\u7fa4\u96c4\uff0c\u624d\u80fd\u8ba9\u522b\u4eba\u670d\u4e8e\u4f60\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff0c \u201c\u4f60\u8eab\u5c45\u6b64\u4f4d\uff0c\u5f53\u7136\u8981\u5148\u7acb\u5a01\u3002\u201d \u3000\u3000\u201c\u7acb\u5a01\uff1f\u201d\u5c0f\u9ad8\u95ee\uff1a\u201c\u8981\u600e\u6837\u7acb\u5a01\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u53f8\u9a6c\u548c\u6211\u5df2\u51b3\u88c2\uff0c\u4ed6\u5df2\u7ecf\u8d1f\u6c14\u800c\u53bb\uff0c\u4e0d\u77e5\u53bb\u5411\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u201d \u3000\u3000\u201c\u4e0d\u4f46\u4f60\u77e5\u9053\uff0c\u6211\u76f8\u4fe1\u8fd8\u6709\u5f88\u591a\u522b\u7684\u4eba\u4e5f\u77e5\u9053\u4e86\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5353\u9752\u4e34\u6b7b\u4e4b\u524d\uff0c\u4e00\u5b9a \u4e0d\u4f1a\u5fd8\u8bb0\u6d3e\u4eba\u628a\u8fd9\u4e2a\u6d88\u606f\u4f20\u51fa\u53bb\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u80fd\u591f\u62a5\u590d\u4f60\uff0c\u800c\u4e14\u662f\u4ed6\u80fd\u591f\u505a\u5230\u7684\u4e8b\uff0c\u6211\u76f8\u4fe1\u4ed6\u8fde\u4e00\u4ef6\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\u505a\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u4e5f\u76f8\u4fe1\u4ed6\u80fd\u505a\u5230\u7684\u4e8b\u4e00\u5b9a\u5f88\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u542c\u5230\u8427\u5148\u751f\u8981\u6211\u6765\u63a5\u638c\u9556\u5c40\uff0c\u8fde\u4e00\u70b9\u53cd\u5bf9\u7684\u610f\u601d\u90fd\u6ca1\u6709\u3002\u201d\u5c0f\u9ad8\u82e6\u7b11\uff1a\u201c\u56e0\u4e3a \u4f60\u4e5f\u5f88\u9700\u8981\u6211\u6765\u5e2e\u4f60\u6536\u62fe\u6b8b\u5c40\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\u5353\u4e1c\u6765\u5c45\u7136\u4e5f\u4e0d\u5426\u8ba4\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u4eec\u7684\u60c5\u51b5\u7684\u786e\u4e0d\u592a\u7a33\u5b9a\uff0c\u8427\u5148\u751f\u60f3\u5fc5\u4e5f\u5f88\u660e\u767d\u8fd9\u79cd\u60c5\u51b5\u3002\u6240\u4ee5\u624d\u4f1a\u8981\u4f60\u6765\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u8427\u5148\u751f\u548c\u6211\u4e4b\u95f4\u5f7c\u6b64\u4e5f\u5f88\u4e86\u89e3\uff0c\u4e5f\u7b97\u51c6\u6211\u7edd\u4e0d\u4f1a\u62d2\u7edd\u7684\u3002\u201d \u3000\u3000\u4ed6\u76ef\u7740\u9ad8\u6e10\u98de\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u4f60\u8981\u7acb\u5a01\uff0c\u5f53\u7136\u8981\u7528\u6700\u76f4\u63a5\u6709\u6548\u7684 \u6cd5\u5b50\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u4e5f\u5728\u76ef\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u95ee\uff0c\u201c\u4f60\u662f\u4e0d\u662f\u8981\u6211\u6740\u6731\u731b\u6765\u7acb\u5a01\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4f60\u7684\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u6761\u4ef6\uff0c\u800c\u662f\u5927\u52bf\u3002\u201d\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u5927\u52bf\u5982\u6b64\uff0c\u4f60\u6211\u90fd\u5df2\u522b\u65e0\u9009\u62e9\u7684\u4f59 \u5730\u3002\u201d \u3000\u3000\u9ad8\u6e10\u98de\u970d\u7136\u7ad9\u8d77\uff0c\u8d70\u5230\u7a97\u53e3\u3002 \u3000\u3000\u7a97\u5916\u79ef\u96ea\u672a\u6eb6\uff0c\u5929\u6c14\u5374\u5df2\u6674\u4e86\uff0c\u5927\u5730\u4ecd\u7136\u662f\u4e00\u7247\u94f6\u767d\uff0c\u592d\u8272\u5374\u5df2\u8f6c\u4e3a\u6e5b\u84dd\u3002\u8fdc\u65b9\u5ffd\u7136\u6709 \u4e00\u7247\u767d\u4e91\u98de\u6765\uff0c\u5ffd\u7136\u505c\u4e0b\uff0c\u53c8\u5ffd\u7136\u98de\u53bb\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u4e45\uff0c\u5353\u4e1c\u6765\u624d\u8f7b\u8f7b\u7684\u53f9\u606f\u3002 \u3000\u3000\u201c\u6211\u4e86\u89e3\u4f60\u4eec\uff0c\u4f60\u548c\u6731\u731b\u90fd\u662f\u6c5f\u6e56\u4eba\uff0c\u91cd\u5e94\u8bfa\u800c\u8f7b\u751f\u6b7b\uff0c\u56e0\u4e3a\uff0c\u751f\u6b7b\u4e4b\u95f4\u672c\u6765\u5c31\u53ea\u4e0d\u8fc7 \u662f\u5f39\u6307\u95ee\u7684\u4e8b\u3002\u201d\u4ed6\u8bf4\u5f97\u5f88\u8bda\u6073\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u840d\u6c34\u76f8\u9022\uff0c\u60fa\u60fa\u76f8\u60dc\uff0c\u4fbf\u80fd\u4ee5\u751f\u6b7b\u76f8\u8bb8\u3002\u201d \u3000\u3000\u4ed6\u7684\u53f9\u606f\u58f0\u4e2d\u7684\u786e\u6709\u4e9b\u611f\u6168\uff1a\u201c\u5728\u90a3\u4e9b\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u2018\u670b\u53cb\u2019\u4e3a\u4f55\u7269\u7684\u541b\u5b50\u5148\u751f\u773c\u4e2d\u770b \u6765\uff0c\u4f60\u4eec\u4e5f\u8bb8\u6839\u672c\u5c31\u4e0d\u80fd\u7b97\u670b\u53cb\uff0c\u4f46\u662f\u6211\u4e86\u89e3\u4f60\u4eec\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6240\u4ee5\u6211\u4e5f\u4e86\u89e3\uff0c\u8981\u4f60\u53bb\u6740\u6731\u731b\uff0c\u7684\u786e\u662f\u4ef6\u5f88\u60b2\u54c0\u7684\u4e8b\uff0c\u4e0d\u4ec5\u662f\u4f60\u7684\u60b2\u54c0\uff0c \u4e5f\u4e0d\u4ec5\u662f\u4ed6\u7684\uff0c\u800c\u662f\u6211\u4eec\u5927\u5bb6\u5171\u6709\u7684\u60b2\u54c0\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u65e0\u8bed\u3002 \u3000\u3000\u201c\u6240\u4ee5\u6211\u4e5f\u5e0c\u671b\u4f60\u80fd\u4e86\u89e3\u4e00\u4ef6\u4e8b\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4f60\u4e0d\u53bb\u6740\u6731\u731b\uff0c\u4e5f\u4e00\u6837\u6709\u4eba\u4f1a\u53bb\u6740\u4ed6 \u7684\uff0c\u4ed6\u4e0d\u6b7b\u5728\u4f60\u624b\u91cc\uff0c\u4e5f\u4e00\u6837\u4f1a\u6b7b\u5728\u522b\u4eba\u624b\u91cc\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u79e6\u5931\u5176\u9e7f\uff0c\u5929\u4e0b\u5171\u9010\u4e4b\uff0c\u53f8\u9a6c\u8d85\u7fa4\u5931\u53bb\u4e86\u4ed6\u7684\u5730\u4f4d\uff0c\u60c5\u51b5\u4e5f\u4e00\u6837\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6240 \u4ee5\u6731\u731b\u7684\u5934\u9885\uff0c\u73b0\u5728\u5df2\u6210\u4e3a\u5927\u9556\u5c40\u5c5e\u4e0b\u4e09\u5341\u516d\u8def\u8c6a\u6770\u9010\u9e7f\u7684\u5bf9\u8c61\u3002\u201d \u3000\u3000\u4ed6\u53c8\u89e3\u91ca\uff1a\u201c\u56e0\u4e3a\u6731\u731b\u4e5f\u662f\u4e00\u4e16\u4e4b\u96c4\uff0c\u800c\u4e14\u662f\u5927\u9556\u5c40\u7684\u6b7b\u654c\uff0c\u5927\u9556\u5c40\u4e2d\u65e0\u8bba\u8c01\u80fd\u53d6\u4e0b\u4ed6 \u7684\u5934\u9885\uff0c\u90fd\u53ef\u4ee5\u501f\u6b64\u7acb\u5a01\u4e8e\u8bf8\u8def\u82f1\u8c6a\u95f4\uff0c\u53d6\u53f8\u9a6c\u4e4b\u4f4d\u800c\u4ee3\u4e4b\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5176\u4e2d\u6700\u5c11\u6709\u4e09\u4e2a\u4eba\u6709\u5e0c\u671b\u3002\u201d \u3000\u3000\u201c\u4f60\u6015\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u6211\u6015\u7684\u4e0d\u662f\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e0d\u53d6\u800c\u4ee3\u4e4b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u4e5f\u4e0d\u6015\u4f60\uff0c\u53ef\u662f\u518d\u52a0\u4e0a\u8427\u5148\u751f\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u654c\u3002\u201d \u3000\u3000\u8fd9\u6b21\u4ed6\u8bf4\u7684\u4e5f\u662f\u5b9e\u8bdd\u3002 \u3000\u3000\u201c\u4ee5\u524d\u6211\u4e0d\u6740\u6731\u731b\uff0c\u662f\u4e3a\u4e86\u8981\u5c06\u4ed6\u7559\u7ed9\u53f8\u9a6c\uff0c\u800c\u8fd9\u6b21\u6211\u4e0d\u6740\u6731\u731b\uff0c\u662f\u4e3a\u4e86\u8981\u5c06\u4ed6\u7559\u7ed9 \u4f60\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e0e\u5176\u8ba9\u522b\u4eba\u6740\u4e86\u4ed6\uff0c\u5c31\u4e0d\u5982\u8ba9\u4ed6\u6b7b\u5728\u4f60\u624b\u91cc\u4e86\uff0c\u53cd\u6b63\u4ed6\u8fdf\u65e9\u90fd\u5df2\u5fc5\u6b7b\u65e0 \u7591\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u970d\u7136\u8f6c\u8eab\uff0c\u76ef\u7740\u4ed6\uff0c\u773c\u4e2d\u5e03\u6ee1\u8840\u4e1d\uff0c\u8138\u4e0a\u5374\u8fde\u4e00\u70b9\u8840\u8272\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u201c\u4f60\u521a\u624d\u8bf4\u7684\u90a3\u4e09\u4e2a\u4eba\uff0c\u73b0\u5728\u662f\u4e0d\u662f\u4e5f\u5230\u4e86\u957f\u5b89\uff1f\u201d\u5c0f\u9ad8\u95ee\u5353\u4e1c\u6765\u3002 \u3000\u3000\u201c\u5f88\u53ef\u80fd\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u63a8\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u53e3\u65e0\u60c5\u7684\u5251\uff0c\u4e00\u67c4\u593a\u547d\u7684\u67aa\uff0c\u548c\u4e00\u888b\u89c1\u8840\u5c01\u5589\u7684\u6697\u5668\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6bcf\u4e00\u79cd\u90fd \u6709\u8d44\u683c\u5217\u5165\u5929\u4e0b\u6700\u53ef\u6015\u7684\u4e03\u5341\u4ef6\u6b66\u5668\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u6211\u95ee\u7684\u662f\u4ed6\u4eec\u7684\u4eba\uff0c\u4e0d\u662f\u4ed6\u4eec\u7684\u6b66\u5668\uff0c\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u4eba\u90fd\u662f\u6740\u4eba\u7684\u4eba\uff0c\u5728\u957f\u5b89\u90fd\u6709\u773c\u7ebf\uff0c\u90fd\u80fd\u5728\u4e00\u4e24\u4e2a\u65f6\u8fb0\u4e2d\u627e\u5230\u6731\u731b\u3002\u201d\u5353\u4e1c\u6765 \u8bf4\uff1a\u201c\u4f60\u53ea\u8981\u77e5\u9053\u8fd9\u4e9b\u5c31\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u51fa\u4ed6\u4eec\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u77e5\u9053\u4ed6\u4eec\u7684\u540d\u5b57\u4e4b\u540e\uff0c\u5f88\u53ef\u80fd\u4f1a\u5f71\u54cd\u5230\u4f60\u7684\u6597\u5fd7\u548c\u5fc3\u60c5\u3002\u201d\u201c\u6211\u4eec\u80fd\u4e0d\u80fd\u5728\u4ed6 \u4eec\u4e4b\u524d\u627e\u5230\u6731\u731b\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u80fd\uff0c\u6211\u80fd\u3002\u201d \u3000\u3000\u201c\u6731\u731b\u6b64\u523b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u6211\u7684\u638c\u63e1\u4e2d\u3002\u201d\u5353\u4e1c\u6765\u60a0\u7136\u9053\uff1a\u201c\u4ed6\u4e00\u76f4\u90fd\u5728\u6211\u7684\u638c\u63e1\u4e2d\u3002\u201d \u56db \u3000\u3000\u66ae\u4e91\u56db\u5408\uff0c\u7fa4\u5c71\u5728\u82cd\u832b\u7684\u66ae\u8272\u4e2d\uff0c\u6731\u731b\u4e5f\u5728\uff0c\u5728\u4e00\u576f\u9ec4\u571f\u524d\u3002 \u3000\u3000\u4e00\u576f\u65b0\u5806\u8d77\u7684\u9ec4\u571f\uff0c\u5893\u4e0a\u7684\u6625\u8349\u72b9\u672a\u751f\uff0c\u5893\u524d\u77f3\u7891\u4e5f\u672a\u7acb\uff0c\u56e0\u4e3a\u5893\u4e2d\u7684\u4eba\u53ef\u80fd\u5df2\u5316\u4f5c\u8774 \u8776\u98de\u53bb\u3002 \u3000\u3000\u5893\u4e2d\u57cb\u846c\u7740\u7684\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u662f\u4e00\u6bb5\u901d\u53bb\u7684\u82f1\u96c4\u5c81\u6708\uff0c\u548c\u4e00\u6bb5\u6c38\u8fdc\u4e0d\u4f1a\u6d88\u901d\u7684\u513f\u5973\u67d4\u60c5\u800c \u5df2\u3002 \u3000\u3000\u4f46\u662f\u6731\u731b\u4ecd\u5728\u3002\u53f8\u9a6c\u4ecd\u5728\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u4e4b\u95f4\u7ea0\u7f20\u9519\u7efc\u7684\u6069\u6028\u6e05\u4ec7\u4e5f\u4ecd\u5728\uff0c\u4ed6\u4eec\u4e4b\u95f4\u8fd9\u4e2a\u7ed3\u672c\u6765\u5c31\u662f\u4efb\u4f55\u4eba\u90fd\u89e3\u4e0d\u5f00 \u7684\u3002 \u3000\u3000\u66ae\u8272\u6e10\u6df1\u3002 \u3000\u3000\u6731\u731b\u75f4\u75f4\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u5df2\u4e0d\u77e5\u7ad9\u4e86\u591a\u4e45\uff0c\u4ed6\u4ec5\u5b58\u7684\u5341\u4f59\u5144\u5f1f\u75f4\u75f4\u7684\u770b\u7740\u4ed6\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053 \u4ed6\u5fc3\u91cc\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7684\u5144\u5f1f\u4eec\u5fc3\u91cc\u662f\u4ec0\u4e48\u6ecb\u5473\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u95e8\u81ea\u5df1\u5fc3\u91cc\u90fd\u77e5\u9053\uff0c\u5982\u679c\u4eba\u751f\u771f\u7684\u5982\u620f\uff0c\u5982\u679c\u4ed6\u7684\u8fd9\u4e00\u751f\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e00\u51fa\u620f\u800c \u5df2\uff0c\u90a3\u4e48\u8fd9\u51fa\u620f\u65e0\u7591\u5df2\u5c06\u5230\u843d\u5e55\u7684\u65f6\u5019\u3002 \u3000\u3000\u65e0\u8bba\u8fd9\u51fa\u620f\u591a\u4e48\u60e8\u70c8\u60b2\u58ee\u8f70\u52a8\uff0c\u73b0\u5728\u90fd\u5df2\u5c06\u5230\u4e86\u843d\u5e55\u7684\u65f6\u5019\u3002 \u3000\u3000\u8776\u821e\u53ea\u4e0d\u8fc7\u5148\u8d70\u4e86\u4e00\u6b65\uff0c\u4ed6\u4eec\u5374\u8fd8\u8981\u628a\u6700\u540e\u8fd9\u6bb5\u8def\u8d70\u5b8c\u3002 \u3000\u3000\u4e0d\u7ba1\u591a\u8270\u82e6\u90fd\u800d\u8d70\u5b8c\uff0c\u4ed6\u4eec\u53ea\u5e0c\u671b\u80fd\u628a\u4ec7\u4eba\u7684\u8840\u6d12\u6ee1\u4ed6\u4eec\u7684\u5f52\u9014\u3002 \u3000\u3000\u6731\u731b\u7ec8\u4e8e\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u7740\u4ed6\u8fd9\u73ed\u751f\u6b7b\u4e0e\u5171\u7684\u5144\u5f1f\uff0c\u7528\u4ed6\u90a3\u53cc\u6ee1\u5e03\u8840\u4e1d\u7684\u5927\u773c\u770b\u7740\u4ed6\u4eec\uff0c \u4ece\u4ed6\u4eec\u8138\u4e0a\u4e00\u4e2a\u4eba\u4e00\u4e2a\u4eba\u770b\u8fc7\u53bb\uff0c\u5728\u6bcf\u4e2a\u4eba\u7684\u8138\u4e0a\u90fd\u505c\u7559\u4e86\u5f88\u4e45\uff0c\u5c31\u597d\u50cf\u770b\u8fc7\u8fd9\u4e00\u773c\u540e\u5c31\u6c38 \u8fdc\u4e0d\u4f1a\u518d\u89c1\u4e86\u3002 \u3000\u3000\u7136\u540e\u4ed6\u624d\u7528\u6c99\u54d1\u7684\u58f0\u97f3\u8bf4\uff1a \u3000\u3000\u201c\u4eba\u751f\u4ece\u6765\u4e5f\u6ca1\u6709\u6c38\u8fdc\u4e0d\u6563\u7684\u7b75\u5e2d\uff0c\u5c31\u7b97\u513f\u5b50\u8ddf\u8001\u5b50\uff0c\u4e5f\u603b\u6709\u5206\u624b\u7684\u65f6\u5019\uff0c\u73b0\u5728\u5c31\u5df2\u7ecf \u5230\u4e86\u6211\u4eec\u5206\u624b\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u4ed6\u7684\u5144\u5f1f\u4eec\u8138\u8272\u5df2\u53d8\u4e86\uff0c\u6731\u731b\u88c5\u4f5c\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u201c\u6240\u4ee5\u73b0\u5728\u6211\u5c31\u8981\u4f60\u4eec\u8d70\uff0c\u6700\u597d\u5206\u6210\u51e0\u8def\u8d70\uff0c\u4e0d\u8981\u8d85\u8fc7\u4e24\u4eba\u4e00\u8def\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u56e0\u4e3a\u6211 \u8981\u4f60\u4eec\u6d3b\u4e0b\u53bb\uff0c\u53ea\u8981\u4f60\u4eec\u8fd8\u6709\u4e00\u4e2a\u4eba\u80fd\u6d3b\u4e0b\u53bb\uff0c\u96c4\u72ee\u5802\u5c31\u8fd8\u6709\u518d\u8d77\u7684\u5e0c\u671b\u3002\u201d \u3000\u3000\u6ca1\u6709\u4eba\u8d70\uff0c\u6ca1\u6709\u4eba\u52a8\u3002 \u3000\u3000\u6731\u731b\u8df3\u8d77\u6765\uff0c\u5636\u58f0\u5927\u543c\uff0c \u3000\u3000\u201c\u6211\u64cd\u4f60\u4eec\u7684\u7956\u5b97\uff0c\u4f60\u4eec\u96be\u9053\u6ca1\u542c\u89c1\u8001\u5b50\u5728\u8bf4\u4ec0\u4e48\uff1f\u4f60\u4eec\u96be\u9053\u5e0c\u671b\u96c4\u72ee\u5802\u7684\u4eba\u90fd\u6b7b\u5c3d\u6b7b \u5149\u6b7b\u7edd\uff1f\u201d \u3000\u3000\u8fd8\u662f\u6ca1\u6709\u4eba\u52a8\uff0c\u4e5f\u6ca1\u6709\u4eba\u5f00\u53e3\u3002 \u3000\u3000\u6731\u731b\u7528\u529b\u62bd\u4e0b\u4e86\u8170\u4e0a\u4e00\u6761\u5df4\u638c\u5bbd\u7684\u76ae\u677f\u5e26\uff0c\u5f80\u4ed6\u4eec\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u4f60\u4eec\u4e0d\u8d70\uff0c\u4f60\u4eec\u8981\u6b7b\uff0c\u597d\uff0c\u8001\u5b50\u5c31\u5148\u628a\u4f60\u4eec\u6d3b\u6d3b\u62bd\u6b7b\u5728\u8fd9\u91cc\uff0c\u514d\u5f97\u60f9\u8001\u5b50\u751f\u6c14\u3002\u201d \u3000\u3000\u677f\u5e26\u62bd\u4e0b\uff0c\u4e00\u677f\u5e26\u4e00\u6761\u9752\u7d2b\uff0c\u4e00\u677f\u5e26\u4e00\u6761\u8840\u75d5\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fd9\u4e9b\u65e2\u4e0d\u77e5\u6b7b\u6d3b\u4e5f\u4e0d\u77e5\u75bc\u75db\u7684\u5144\u5f1f\u4eec\uff0c\u53ea\u662f\u95ed\u7740\u5634\uff0c\u54ac\u7740\u7259\uff0c\u8fd9\u4e00\u52a8\u90fd\u4e0d\u52a8\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8fdc\u8fdc\u7684\u7ad9\u7740\uff0c\u8fdc\u8fdc\u7684\u770b\u7740\uff0c\u597d\u50cf\u8fde\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u5634\u89d2\u5df2\u7ecf\u6709\u4e00\u4e1d\u9c9c\u8840\u6c81\u51fa\u3002 \u3000\u3000\u4ed6\u7684\u7259\u9f7f\u54ac\u5f97\u592a\u7d27\uff0c\u5df2\u54ac\u51fa\u4e86\u8840\u3002 \u3000\u3000\u8d77\u4e86\u98ce\uff0c\u4e0d\u77e5\u9053\u5728\u4ec0\u4e48\u65f6\u5019\u5ffd\u7136\u522e\u8d77\u4e86\u98ce\u3002\u522e\u5728\u4eba\u8eab\u4e0a\u597d\u50cf\u5c0f\u5200\u5b50\u4e00\u6837\u7684\u90a3\u79cd\u51b7\u98ce\u3002 \u3000\u3000\u6731\u731b\u7684\u624b\u7ec8\u4e8e\u5782\u843d\u3002 \u3000\u3000\u201c\u597d\u3002\u4f60\u4eec\u8981\u7559\u4e0b\u6765\u966a\u6211\u4e00\u8d77\u6b7b\uff0c\u6211\u5c31\u8ba9\u4f60\u4eec\u7559\u4e0b\u6765\uff0c\u201d\u4ed6\u5389\u58f0\u8bf4\uff1a\u201c\u53ef\u662f\u4f60\u4eec\u4e00\u5b9a\u8981 \u8bb0\u4f4f\uff0c\u4e0d\u7ba1\u6211\u8ddf\u53f8\u9a6c\u8d85\u7fa4\u8fd9\u4e00\u6218\u662f\u8c01\u80dc\u51c6\u8d1f\uff0c\u90fd\u8ddf\u4f60\u4eec\u4e00\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\uff0c\u4f60\u4eec\u7edd\u4e0d\u80fd\u52a8 \u4ed6\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d77\u7fa4\u5ffd\u7136\u51b7\u7b11\u3002 \u3000\u3000\u201c\u6ca1\u6709\u7528\u7684\uff0c\u4e0d\u7ba1\u4f60\u60f3\u7528\u4ec0\u4e48\u6cd5\u5b50\u6765\u611f\u52a8\u6211\u90fd\u6ca1\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u6731\u731b\u5636\u58f0\u95ee\uff1a\u201c\u4f60\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u59a5\u4f60\u660e\u767d\uff0c\u73b0\u5728\u6211\u867d\u7136\u5df2\u7ecf\u5bb6\u7834\u4eba\u4ea1\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u6545\u610f\u6210\u5168\u4f60\uff0c\u6545\u610f\u8ba9\u4f60\u6740 \u4e86\u6211\uff0c\u8ba9\u4f60\u62ff\u6211\u7684\u5934\u9885\u53bb\u91cd\u632f\u4f60\u7684\u58f0\u6210\uff0c\u91cd\u632f\u96c4\u72ee\u5802\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u7684\u58f0\u97f3\u4e5f\u5df2\u5b8c\u5168\u5636\u54d1\uff1a \u201c\u4f60\u82e5\u60f3\u8981\u6211\u9888\u4e0a\u8fd9\u9897\u4eba\u5934\uff0c\u8fd8\u662f\u8981\u62ff\u51fa\u771f\u529f\u592b\u6765\u3002\u201d \u3000\u3000\u201c\u653e\u4f60\u5a18\u7684\u72d7\u5c41\u3002\u201d\u6731\u731b\u66b4\u6012\uff0c\u201c\u8c01\u60f3\u8981\u4f60\u6545\u610f\u653e\u8001\u5b50\u8fd9\u4e00\u9a6c\uff1f\u8001\u5b50\u672c\u6765\u8fd8\u628a\u4f60\u5f53\u4f5c\u4e00 \u4e2a\u4eba\uff0c\u8c01\u77e5\u9053\u4f60\u653e\u7684\u5374\u662f\u72d7\u5c41\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u9a82\u5f97\u597d\u3002\u201d\u53f8\u9a6c\u4ef0\u9762\u800c\u7b11\uff1a\u201c\u4f60\u6709\u79cd\u5c31\u8fc7\u6765\u5427\uff01\u201d \u3000\u3000\u6731\u731b\u672c\u6765\u5df2\u7ecf\u51c6\u5907\u6251\u8fc7\u53bb\uff0c\u5ffd\u7136\u53c8\u505c\u4e0b\uff0c\u90a3\u79cd\u96f7\u9706\u822c\u7684\u66b4\u6012\u5c45\u7136\u4e5f\u5ffd\u7136\u5e73\u606f\uff0c\u5ffd\u7136\u7528\u4e00 \u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u770b\u7740\u53f8\u9a6c\u8d85\u7fa4\uff0c\u5c31\u597d\u50cf\u4ed6\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u8fd9\u4e2a\u4eba\u4e00\u6837\u3002 \u3000\u3000\u201c\u4f60\u600e\u4e48\u4e0d\u6562\u8fc7\u6765\u4e86\uff1f\u201d\u53f8\u9a6c\u53c8\u5728\u6311\u8845\uff0c\u201c\u96be\u9053\u4f60\u53ea\u6709\u80c6\u5b50\u5bf9\u4ed8\u4f60\u81ea\u5df1\u7684\u5144\u5f1f\uff1f\u96be\u9053 \u2018\u96c4\u72ee\u2019\u6731\u731b\u7adf\u662f\u4e2a\u8fd9\u6837\u7684\u5b6c\u79cd\uff1f\u201d \u3000\u3000\u6731\u731b\u5ffd\u7136\u4e5f\u7b11\u4e86\uff0c\u4ef0\u9762\u72c2\u7b11\u3002 \u3000\u3000\u201c\u597d\uff0c\u9a82\u5f97\u597d\uff0c\u9a82\u5f97\u771f\u4ed6\u5a18\u7684\u597d\u6781\u4e86\u3002\u201d\u4ed6\u7684\u7b11\u58f0\u5982\u733f\u557c\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u8fd9\u4e48\u6837\u505a\u4e5f\u6ca1\u6709 \u7528\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d\u53f8\u9a6c\u8d85\u7fa4\u8fd8\u5728\u51b7\u7b11\uff0c\u201c\u4f60\u653e\u7684\u662f\u4ec0\u4e48\u5c41\uff01\u201d \u3000\u3000\u8fd9\u6b21\u6731\u731b\u975e\u4f46\u6ca1\u6709\u53d1\u6012\uff0c\u53cd\u800c\u957f\u957f\u53f9\u606f\uff1a\u201c\u53f8\u9a6c\u8d85\u7fa4\uff0c\u4f60\u662f\u6761\u597d\u6c49\u3002\u6211\u6731\u731b\u7eb5\u6a2a\u4e00\u751f\uff0c \u4ece\u672a\u670d\u4eba\uff0c\u5374\u5df2\u7ecf\u6709\u70b9\u4f69\u670d\u4f60\u3002\u201d\u4ed6\u8bf4\uff0c\u201c\u53ef\u662f\u4f60\u82e5\u8ba4\u4e3a\u6211\u6731\u731b\u53ea\u4e0d\u8fc7\u662f\u6761\u4e0d\u77e5\u597d\u6b79\u7684\u83bd\u6c49 \u800c\u5df2\uff0c\u4f60\u5c31\u9519\u4e86\uff0c\u4f60\u7684\u610f\u601d\u6211\u8fd8\u662f\u660e\u767d\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u7528\u4e0d\u7740\u6fc0\u6211\u53bb\u6740\u4f60\uff0c\u4e5f\u7528\u4e0d\u7740\u7528\u8fd9\u79cd\u6cd5\u5b50\u6765\u6fc0\u6211\u7684\u706b\u6c14\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u6211\u867d\u7136\u5df2\u7ecf \u57ae\u4e86\uff0c\u800c\u65e6\u4e3a\u4e86\u4e00\u4e2a\u5973\u4eba\u5c31\u53d8\u5f97\u50cf\u767d\u75f4\u4e00\u6837\u5931\u9b42\u843d\u9b44\uff0c\u53d8\u5f97\u6bd4\u6b7b\u4e86\u4eb2\u5a18\u8fd8\u4f24\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7528\u529b\u4e00\u62cd\u80f8\u819b\uff1a\u201c\u53ef\u662f\u53ea\u8981\u6211\u6731\u731b\u8fd8\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u5c31\u4e00\u5b9a\u4f1a\u62fc\u5230\u5e95\u7684\uff0c\u7528\u4e0d\u7740\u4f60 \u672a\u6fc0\u6211\uff0c\u6211\u4e5f\u4f1a\u62fc\u5230\u5e95\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6731\u731b\u9888\u4e0a\u8fd9\u9897\u4eba\u5934\u4e5f\u4e0d\u662f\u968f\u4fbf\u5c31\u4f1a\u8ba9\u4eba\u62ff\u8d70\u7684\uff0c\u4e5f\u4e0d\u4f1a\u6210\u5168\u4f60\u3002\u201d\u6731\u731b\u5389\u58f0\u9053\uff0c\u201c\u53ef \u662f\u6211\u4e5f\u4e0d\u8981\u4f60\u6765\u6210\u5168\u6211\u3002\u201d \u3000\u3000\u4ed6\u4ee5\u5927\u773c\u903c\u89c6\u53f8\u9a6c\uff1a\u201c\u4eca\u65e5\u4f60\u6211\u4e00\u6218\uff0c\u751f\u6b7b\u80dc\u8d1f\u672c\u6765\u5c31\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u6839\u672c\u5c31\u6ca1\u6709\u653e \u5728\u5fc3\u4e0a\uff0c\u53ef\u662f\u4f60\u82e5\u6709\u4e00\u70b9\u610f\u601d\u8981\u6210\u5168\u6211\uff0c\u201d\u6731\u731b\u7684\u58f0\u97f3\u66f4\u60e8\u5389\uff1a\u201c\u53ea\u8981\u4f60\u6709\u3002\u4e00\u70b9\u8fd9\u79cd\u610f \u601d\uff0c\u4f60\u53f8\u9a6c\u8d85\u7fa4\u5c31\u4e0d\u662f\u4eba\u751f\u7236\u6bcd\u517b\u7684\uff0c\u5c31\u662f\u4e2a\u72d7\u517b\u7684\u6742\u79cd\uff0c\u53ea\u8981\u4f60\u8ba9\u4e86\u6211\u4e00\u62db\u4e00\u5f0f\uff0c\u6211\u5c31\u9a6c \u4e0a\u6b7b\u5728\u4f60\u9762\u524d\uff0c\u5316\u4e3a\u5389\u9b3c\u4e5f\u4e0d\u9976\u4f60\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u770b\u7740\u4ed6\uff0c\u770b\u7740\u4ed6\u90a3\u53cc\u5e03\u6ee1\u8840\u4e1d\u7684\u5927\u773c\uff0c\u770b\u7740\u8fd9\u4f4d\u867d\u7136\u5df2\u5f62\u9500\u9aa8\u7acb\u5374\u4ecd\u6709\u96c4\u72ee\u822c \u6c14\u6982\u7684\u4eba\uff0c\u8fc7\u4e86\u5f88\u4e45\u4e4b\u540e\u624d\u8bf4\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\u4f60\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4eca\u65e5\u6211\u90fd\u4f1a\u65bd\u5c3d\u5168\u529b\u4e0e\u4f60\u51b3\u4e2a \u6b7b\u6218\u3002\u201d \u3000\u3000\u6731\u731b\u4e5f\u6b63\u770b\u7740\u4ed6\uff0c\u770b\u7740\u8fd9\u4e2a\u66fe\u7ecf\u88ab\u5f53\u4e16\u5929\u4e0b\u82f1\u8c6a\u6367\u5728\u5929\u4e0a\u800c\u4eca\u5374\u5df2\u843d\u5165\u6ce5\u6d82\u7684\u82f1\u96c4\u5076 \u50cf\uff0c\u5ffd\u7136\u4ef0\u5929\u957f\u53f9\uff1a\u201c\u4f60\u6211\u4eca\u4e16\u5df2\u6ce8\u5b9a\u4e3a\u654c\uff0c\u6211\u6731\u731b\u4f46\u613f\u80fd\u6709\u6765\u751f\u800c\u5df2\uff0c\u4f46\u613f\u6765\u751f\u6211\u4eec\u80fd\u4ea4 \u4e2a\u670b\u53cb\uff0c\u4e0d\u7ba1\u4eca\u65e5\u8fd9\u4e00\u6218\u662f\u8c01\u80dc\u8c01\u8d1f\u8c01\u751f\u8c01\u6b7b\u90fd\u5982\u6b64\u3002\u201d \u4e94 \u3000\u3000\u98ce\u66f4\u51b7\u3002 \u3000\u3000\u8fdc\u5c71\u5df2\u51b7\uff0c\u9752\u5bb6\u5df2\u51b7\uff0c\u4eba\u4e5f\u5728\u51b7\u98ce\u4e2d\uff0c\u53ef\u662f\u80f8\u4e2d\u5374\u90fd\u6709\u4e00\u822c\u70ed\u8840\u3002 \u3000\u3000\u8fd9\u80a1\u70ed\u8840\u662f\u6c38\u8fdc\u51b7\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e2a\u4e16\u754c\u4e0a\u8fd8\u6709\u4e00\u4e9b\u4eba\u80f8\u4e2d\u6709\u8fd9\u4e48\u6837\u4e00\u80a1\u6c38\u8fdc\u51b7\u4e0d\u4e86\u7684\u70ed\u8840\uff0c\u6240\u4ee5\u6211\u4eec\u5fc3\u4e2d\u5c31\u5e94\u8be5 \u6c38\u65e0\u754f\u60e7\uff0c\u56e0\u4e3a\u6211\u4eec\u5e94\u8be5\u77e5\u9053\u53ea\u8981\u4eba\u4eec\u80f8\u4e2d\u8fd8\u6709\u8fd9\u4e00\u822c\u70ed\u8840\u5b58\u5728\uff0c\u6b63\u4e49\u5c31\u5fc5\u7136\u5e38\u5b58\u3002 \u3000\u3000\u8fd9\u4e00\u70b9\u5fc5\u5b9a\u8981\u5f3a\u8c03\uff0c\u56e0\u4e3a\u8fd9\u5c31\u662f\u4e49\u7684\u7cbe\u795e\u3002 \u3000\u3000\u66ae\u8272\u4e5f\u66f4\u6df1\u4e86\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u548c\u6731\u731b\u4e24\u4e2a\u4eba\u5728\u66ae\u8272\u4e2d\u770b\u6765\uff0c\u5df2\u7ecf\u53d8\u5f97\u53ea\u4e0d\u8fc7\u662f\u4e24\u6761\u6726\u80e7\u6a21\u7cca\u7684\u4eba\u5f71\u800c\u5df2\u3002 \u3000\u3000\u53ef\u662f\u5728\u8fd9\u4e9b\u70ed\u8840\u6cb8\u817e\u7684\u597d\u6c49\u4eec\u773c\u4e2d\u770b\u6765\uff0c\u8fd9\u4e24\u6761\u6726\u80e7\u6a21\u7cca\u7684\u4eba\u5f71\uff0c\u5374\u8fdc\u6bd4\u4e16\u4e0a\u4efb\u4f55\u4e00\u4e2a \u4eba\u7684\u5f62\u8c61\u90fd\u8981\u9c9c\u660e\u5f3a\u70c8\u4f1f\u5927\u5f97\u591a\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u4e89\u7684\u5e76\u4e0d\u662f\u751f\u6b7b\u8363\u8fb1\u6210\u8d25\u80dc\u8d1f\u3002 \u3000\u3000\u4ed6\u4eec\u5c06\u4e16\u4eba\u4eec\u4e0d\u80fd\u820d\u5f03\u7684\u751f\u6b7b\u8363\u8fb1\u90fd\u7f6e\u4e4b\u5ea6\u5916\uff0c\u4ed6\u4eec\u53ea\u4e0d\u8fc7\u662f\u5728\u505a\u4e00\u4ef6\u4ed6\u4eec\u81ea\u5df1\u8ba4\u4e3a\u81ea \u5df1\u5fc5\u987b\u8981\u505a\u7684\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u662f\u4ed6\u4eec\u505a\u4eba\u7684\u539f\u5219\u3002 \u3000\u3000\u5934\u53ef\u65ad\u3001\u8840\u53ef\u6d41\uff0c\u5bcc\u8d35\u8363\u534e\u53ef\u4ee5\u5f03\u5982\u655d\u5c50\uff0c\u8fd9\u4e00\u70b9\u539f\u5219\u5374\u7edd\u4e0d\u53ef\u5f03\u3002 \u3000\u3000\u2014\u2014\u4ed6\u4eec\u8fd9\u4e48\u6837\u505a\uff0c\u662f\u4e0d\u662f\u4f1a\u6709\u4eba\u8ba4\u4e3a\u4ed6\u4eec\u5927\u611a\u8822\uff1f \u3000\u3000\u2014\u2014\u5982\u679c\u6709\u4eba\u8ba4\u4e3a\u4ed6\u4eec\u592a\u611a\u8822\uff0c\u90a3\u79cd\u4eba\u662f\u79cd\u4ec0\u4e48\u6837\u7684\u4eba\uff1f \u516d \u3000\u3000\u6731\u731b\u8083\u7acb\uff0c\u4e0e\u53f8\u9a6c\u8d85\u7fa4\u8083\u7136\u5bf9\u7acb\uff0c\u751f\u6b7b\u5df2\u51b3\u5b9a\u4e8e\u4e00\u77ac\u95f4\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u6392\u65a5\u6fc0\u8361\u4e8e\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e4b\u95f4\u7684\u90a3\u4e00\u80a1\u6c14\u5e76\u4e0d\u662f\u4ec7\u6068\uff0c\u800c\u662f\u4e00\u80a1\u8840\u6c14\u3002 \u3000\u3000\u6731\u731b\u5ffd\u7136\u95ee\uff1a\u201c\u8fd1\u5341\u5e74\u6765\uff0c\u4f60\u6218\u65e0\u4e0d\u80dc\uff0c\u4ece\u672a\u9047\u8fc7\u5bf9\u624b\uff0c\u4f60\u514b\u654c\u65f6\u7528\u7684\u662f\u4e0d\u662f\u4e00\u53e3\u5343\u9524 \u5927\u94c1\u5251\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u5251\u5462\uff1f\u201d \u3000\u3000\u201c\u5251\u4e0d\u5728\uff0c\u53ef\u662f\u6211\u7684\u4eba\u5728\uff0c\u201d\u53f8\u9a6c\u8d85\u6837\u8bf4\uff1a\u201c\u4f60\u8981\u6218\u7684\u5e76\u4e0d\u662f\u6211\u7684\u5251\uff0c\u800c\u662f\u6211\u7684\u4eba\uff0c\u6240 \u4ee5\u53ea\u8981\u6211\u7684\u4eba\u5728\u5c31\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u6765\u773c\u6211\u62fc\u751f\u6b7b\u51b3\u80dc\u8d1f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5e26\u4f60\u7684\u5251\u6765\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8d64\u624b\u4e5f\u4e00\u6837\u53ef\u4ee5\u640f\u6740\u72ee\u864e\u3002\u201d \u3000\u3000\u6731\u731b\u6162\u6162\u7684\u628a\u4ed6\u7684\u677f\u5e26\u7cfb\u5728\u8170\u4e0a\uff0c\u4e5f\u53ea\u5269\u4e0b\u4e00\u53cc\u7a7a\u62f3\u8d64\u624b\u3002 \u3000\u3000\u201c\u6211\u6731\u731b\u4e00\u751f\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u5feb\u610f\u6069\u4ec7\uff0c\u65e0\u60c5\u65e0\u4e49\u65e0\u5ec9\u65e0\u803b\u7684\u5c0f\u4eba\u5df2\u4e0d\u77e5\u6709\u591a\u5c11\u88ab\u6211\u523a\u6740\u4e8e \u5200\u4e0b\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u6740\u4eba\u65f6\u7528\u7684\u901a\u5e38\u90fd\u662f\u4e00\u67c4\u5927\u626b\u5200\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u5200\u5462\uff1f\u201d \u3000\u3000\u201c\u5200\u5728\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u6211\u7684\u5200\u5728\u3002\u201d \u3000\u3000\u4ed6\u4f38\u51fa\u624b\uff0c\u5c31\u6709\u4eba\u628a\u4ed6\u90a3\u67c4\u80fd\u5728\u5343\u519b\u4e07\u9a6c\u4e2d\u53d6\u654c\u5e05\u9996\u7ea7\u7684\u5927\u626b\u5200\u9001\u4e86\u6765\u3002 \u3000\u3000\u201c\u597d\u5200\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u5927\u58f0\u8bf4\uff1a\u201c\u8fd9\u624d\u662f\u6740\u4eba\u7684\u5200\u3002\u201d \u3000\u3000\u201c\u8fd9\u7684\u786e\u662f\u628a\u6740\u4eba\u7684\u597d\u5200\u3002\u201d\u6731\u731b\u8f7b\u629a\u5200\u950b\uff1a\u201c\u53ea\u4e0d\u8fc7\u8fd9\u628a\u5200\u6740\u7684\u4e00\u5411\u90fd\u662f\u5c0f\u4eba\uff0c\u4e0d\u662f \u82f1\u96c4\u3002\u201d \u3000\u3000\u5200\u5728\u4ed6\u7684\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u5de6\u624b\u63e1\u5200\u67c4\uff0c\u53f3\u624b\u62d7\u5200\u950b\uff0c\u201c\u5623\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4e00\u67c4\u5200\u4ecd\u5728\u4ed6\u624b\u91cc\uff0c\u5374\u5df2\u88ab\u62d7\u6210\u4e24\u622a\u3002 \u3000\u3000\u65ad\u5200\u5316\u4e3a\u98de\u8679\uff0c\u98de\u5165\u66f4\u6df1\u66f4\u6d53\u66f4\u6697\u66f4\u8fdc\u7684\u66ae\u8272\u4e2d\uff0c\u98de\u5f97\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6731\u731b\u7684\u58f0\u97f3\u867d\u7136\u66f4\u5636\u54d1\uff0c\u51e0\u4e4e\u5df2\u4e0d\u80fd\u6210\u58f0\uff0c\u53ef\u662f\u8c6a\u6c14\u4ecd\u5728\uff1a\u201c\u53f8\u9a6c\u8d85\u7fa4\u53ef\u4ee5\u7528\u4e00\u53cc\u8d64\u624b \u640f\u6740\u72ee\u864e\uff0c\u6211\u6731\u731b\u53c8\u4f55\u5c1d\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u4ed6\u7d27\u63e1\u53cc\u62f3\uff0c\u4ed6\u7684\u62f3\u5982\u94c1\uff0c\u53f8\u9a6c\u8d85\u7fa4\u7684\u4e00\u53cc\u94c1\u62f3\u4e5f\u5229\u5982\u5200\u950b\u3002 \u3000\u3000\u201c\u4f60\u8fdc\u6765\uff0c\u4f60\u662f\u5ba2\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u6211\u4e0d\u8ba9\u4f60\uff0c\u53ef\u662f\u4f60\u5e94\u5148\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d \u3000\u3000\u542c\u5230\u6731\u731b\u8bf4\u51fa\u8fd9\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u86ee\u725b\u5c31\u77e5\u9053\u81ea\u5df1\u5feb\u8981\u5b8c\u4e86\u3002 \u4e03 \u3000\u3000\u201c\u86ee\u725b\u201d\u662f\u4e2a\u4eba\uff0c\u662f\u6761\u597d\u6c49\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6709\u7684\u65f6\u5019\u957f\u5f97\u5c31\u50cf\u662f\u6761\u725b\u4e00\u6837\uff0c\u725b\u4e00\u6837\u7684\u813e\u6c14\uff0c\u725b\u4e00\u6837\u7684\u5014\u5f3a\uff0c\u6bd4\u91ce\u725b\u8fd8\u91ce\uff0c\u6bd4 \u86ee\u725b\u8fd8\u86ee\uff0c\u4e00\u8eab\u94dc\u7b4b\u94c1\u9aa8\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u6761\u94c1\u725b\u3002 \u3000\u3000\u53ef\u60dc\u8fd9\u6761\u94c1\u725b\u7684\u5fc3\uff0c\u5374\u50cf\u662f\u74f7\u5668\u505a\u7684\uff0c\u78b0\u90fd\u78b0\u4e0d\u5f97\uff0c\u4e00\u78b0\u5c31\u788e\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e00\u76f4\u90fd\u5750\u5f97\u6700\u8fdc\u3002 \u3000\u3000\u522b\u4eba\u90fd\u7ad9\u7740\uff0c\u4ed6\u5750\u7740\uff0c\u56e0\u4e3a\u4ed6\u6015\u81ea\u5df1\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u6709\u5f88\u591a\u4e8b\u4ed6\u5374\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u4ed6\u6700\u53d7\u4e0d\u4e86\u90a3\u79cd\u51fa\u5356\u670b\u53cb\u7684\u5c0f\u4eba\uff0c\u78b0\u5230\u90a3\u79cd\u4eba\uff0c\u4ed6\u968f\u65f6\u90fd\u53ef\u4ee5\u7528\u4ed6\u552f\u4e00\u7684\u4e00\u6761\u547d\u53bb\u62fc\u4e00 \u62fc\u3002 \u3000\u3000\u4ed6\u4e5f\u53d7\u4e0d\u4e86\u90a3\u79cd\u5bf9\u670b\u53cb\u592a\u591f\u4e49\u6c14\u7684\u4eba\uff0c\u56e0\u4e3a\u78b0\u5230\u8fd9\u79cd\u4eba\uff0c\u4ed6\u4e5f\u968f\u65f6\u90fd\u4f1a\u628a\u81ea\u5df1\u552f\u4e00\u7684\u4e00 \u6761\u547d\u62ff\u53bb\u5356\u7ed9\u4ed6\u3002\u6beb\u65e0\u6761\u4ef6\u7684\u5356\u51fa\u53bb\uff0c\u7edd\u4e0d\u540e\u6094\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e00\u542c\u89c1\u6731\u731b\u8bf4\u201c\u597d\u201d\uff0c\u4e00\u770b\u89c1\u6731\u731b\u4e00\u62f3\u51fb\u51fa\uff0c\u4ed6\u5c31\u77e5\u9053\u81ea\u5df1\u5feb\u8981\u5b8c\u4e86\uff0c\u5c31\u597d\u50cf\u9489 \u978b\u770b\u89c1\u6731\u731b\u5df2\u7ecf\u7ad9\u5230\u5c0f\u9ad8\u8eab\u65c1\u7684\u60c5\u51b5\u4e00\u6837\u3002\u9664\u4e86\u6b7b\u4e4b\u5916\uff0c\u4ed6\u5df2\u7ecf\u6ca1\u6709\u7b2c\u4e8c\u6761\u8def\u597d\u8d70\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u80fd\u591f\u5728\u4e34\u6b7b\u4e4b\u524d\u770b\u5230\u6731\u731b\u51fb\u5012\u53f8\u9a6c\u8d85\u7fa4\u3002\u53ea\u5e0c\u671b\u5728\u4e34\u6b7b\u4e4b\u524d\u8fd8\u80fd\u8ddf\u968f\u7740\u6731\u731b\uff0c \u5230\u5927\u9556\u5c40\u53bb\u8ddf\u5353\u4e1c\u6765\u62fc\u4e00\u62fc\u3002 \u3000\u3000\u53ea\u8981\u80fd\u505a\u5230\u8fd9\u4e00\u70b9\uff0c\u8001\u5929\u7237\u5c31\u662f\u5f85\u4ed6\u4e0d\u8584\u4e86\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5df2\u6b7b\u800c\u65e0\u6028\u3002 \u3000\u3000\u5343\u53e4\u8270\u96be\u552f\u4e00\u6b7b\uff0c\u4ed6\u73b0\u5728\u5df2\u7ecf\u51c6\u5907\u6b7b\u4e86\uff0c\u8fd9\u4e00\u70b9\u8981\u6c42\u5e94\u8be5\u4e0d\u7b97\u8fc7\u5206\u3002 \u3000\u3000\u53ef\u60dc\u8001\u5929\u7237\u504f\u504f\u4e0d\u80af\u7b54\u5e94\u4ed6\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u770b\u5230\u6731\u731b\u4eff\u4f5b\u53c8\u56de\u590d\u4e86\u5f80\u65e5\u7684\u96c4\u98ce\uff0c\u6325\u52a8\u94c1\u62f3\uff0c\u7740\u7740\u62a2\u653b\u65f6\uff0c\u5ffd\u7136\u6709\u4e00\u6761\u9ed1\u8272\u7684 \u7ede\u7d22\u8f7b\u8f7b\u67d4\u67d4\u7684\u4ece\u540e\u9762\u98de\u6765\uff0c\u5957\u4f4f\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u86ee\u725b\u60f3\u6323\u624e\u53cd\u6297\u547c\u558a\u65f6\uff0c\u5df2\u7ecf\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u7ede\u7d22\u5df2\u7ecf\u6536\u7d27\uff0c\u5d4c\u5165\u4e86\u4ed6\u7684\u5589\u7ed3\uff0c\u4ed6\u53ea\u89c9\u5f97\u5168\u8eab\u7684\u529b\u91cf\u5ffd\u7136\u6d88\u5931\u3002\u5168\u8eab\u7684\u808c\u8089\u5ffd\u7136\u677e \u5f1b\uff0c\u6240\u6709\u7684\u6392\u6cc4\u7269\u5ffd\u7136\u540c\u65f6\u6d41\u51fa\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u6731\u731b\u548c\u53f8\u9a6c\u72b9\u5728\u82e6\u6218\uff0c\u522b\u7684\u4eba\u6b63\u5728\u805a\u7cbe\u4f1a\u795e\u7684\u770b\u7740\u4ed6\u4eec\u8fd9\u4e00\u6218\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u5df2 \u7ecf\u6b7b\u4e86\uff0c\u4e5f\u6ca1\u6709\u4eba\u53e3\u8fc7\u5934\u6765\u770b\u4e00\u773c\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u4e48\u6837\u4e00\u6761\u94c1\u725b\u822c\u7684\u597d\u6c49\uff0c\u5c31\u8fd9\u6837\u9759\u6084\u6084\u7684\u79bb\u522b\u4e86\u4eba\u4e16\u3002 \u3000\u3000\u4ed6\u6b7b\u5f97\u5b9e\u5728\u6bd4\u9489\u978b\u66f4\u60e8\u3002 \u516b \u3000\u3000\u9ad8\u624b\u76f8\u4e89\uff0c\u5f80\u5f80\u662f\u4e00\u62db\u95f4\u7684\u4e8b\uff0c\u751f\u6b7b\u80dc\u8d1f\u5f80\u5f80\u5c31\u51b3\u5b9a\u5728\u4e00\u77ac\u95f4\u3002 \u3000\u3000\u53f8\u9a6c\u548c\u6731\u731b\u8fd9\u4e00\u808c\u5374\u4e0d\u540c\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u6253\u5f97\u5f88\u82e6\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u5df2\u5f88\u75b2\u5026\uff0c\u4e0d\u4f46\u5fc3\u795e\u4ea4\u7601\uff0c\u800c\u4e14\u7cbe\u75b2\u529b\u7aed\u3002 \u3000\u3000\u90a3\u4e9b\u672c\u6765\u5728\u7738\u606f\u95f4\u5c31\u53ef\u4ee5\u81f4\u4eba\u4e8e\u6b7b\u7684\u62db\u5f0f\uff0c\u5728\u4ed6\u4eec\u624b\u91cc\u5df2\u7ecf\u53d1\u6325\u4e0d\u51fa\u539f\u6709\u7684\u5a01\u529b\u6765\u3002 \u3000\u3000\u6709\u65f6\u5019\u53f8\u9a6c\u660e\u660e\u4e00\u4e3e\u5c31\u53ef\u4ee5\u5c06\u6731\u731b\u51fb\u5012\u7684\uff0c\u53ef\u662f\u4e00\u638c\u51fb\u51fa\u540e\uff0c\u529b\u91cf\u548c\u90e8\u4f4d\u90fd\u5dee\u4e86\u4e24\u5206\u3002 \u3000\u3000\u6731\u731b\u7684\u60c5\u51b5\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u770b\u7740\u4e24\u4f4d\u53f1\u54a4\u6c5f\u6e56\u4e0d\u53ef\u4e00\u4e16\u7684\u5f53\u4e16\u82f1\u96c4\uff0c\u5982\u4eca\u7adf\u50cf\u4e24\u4f59\u91ce\u517d\u822c\u4f5c\u6b8a\u6b7b\u4e4b\u6597\uff0c\u5b9e\u5728\u662f\u4ef6\u5f88 \u60b2\u54c0\u7684\u4e8b\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u6731\u731b\u7684\u90a3\u4e9b\u5144\u5f1f\u4eec\u7adf\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6709\u65f6\u6731\u731b\u88ab\u4e00\u638c\u51fb\u5012\uff0c\u518d\u6323\u624e\u7740\u722c\u8d77\uff0c\u4ed6\u4eec\u4e5f\u5b8c\u5168\u6ca1\u6709\u53cd\u5e94\uff0c\u7adf\u4f3c\u5b8c\u5168\u65e0\u52a8\u4e8e\u8877\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u88ab\u5bf9\u65b9\u51fb\u5012\u8fc7\u3002\u53ea\u8981\u5012\u4e0b\u53bb\u4e4b\u540e\u8fd8\u80fd\u7ad9\u8d77\u6765\uff0c\u88ab\u51fb\u5012\u4e5f\u6ca1\u4ec0\u4e48\u4e86\u4e0d\u8d77\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u6b21\u53f8\u9a6c\u5012\u4e0b\u53bb\u65f6\uff0c\u773c\u4e2d\u5374\u5ffd\u7136\u9732\u51fa\u79cd\u8bf4\u4e0d\u51fa\u7684\u6050\u60e7\uff0c\u5ffd\u7136\u5728\u5730\u4e0a\u7ffb\u8eab\u4e00\u6eda\uff0c\u6eda \u8fc7\u53bb\u62b1\u4f4f\u4e86\u6731\u731b\u7684\u817f\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u7edd\u4e0d\u662f\u82f1\u96c4\u597d\u6c49\u6240\u7528\u7684\u62db\u5f0f\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u7eb5\u6a2a\u4e00\u751f\uff0c\u4ece\u672a\u7528\u8fc7\u8fd9\u6837\u7684\u62db\u5f0f\uff0c\u6731\u731b\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u7528\u51fa\u6765\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e00\u4e0b\u5b50\u5c31\u88ab\u62d6\u5012\uff0c\u4e24\u4e2a\u4eba\u540c\u65f6\u6eda\u5728\u5730\u4e0a\uff0c\u6731\u731b\u7684\u706b\u6c14\u5df2\u7ecf\u4e0a\u6765\u4e86\u3002\u201c\u7830\u201d\u7684\u4e00 \u62f3\uff0c\u64c2\u5728\u53f8\u9a6c\u7684\u540e\u80cc\u4e0a\u3002 \u3000\u3000\u53f8\u9a6c\u5374\u8fd8\u662f\u7d27\u7d27\u62b1\u4f4f\u4ed6\u4e0d\u653e\uff0c\u5374\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u58f0\u97f3\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\uff1a\u201c\u4f60\u7684\u5144\u5f1f\u4eec\u5927 \u6982\u5df2\u7ecf\u5168\u90fd\u6b7b\u4e86\u3002\u53ef\u662f\u6211\u4eec\u4e00\u5b9a\u8981\u88c5\u4f5c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6731\u731b\u5927\u60ca\uff0c\u6b63\u60f3\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u51fa\u4e00\u4e2a\u5b57\uff0c\u56e0\u4e3a\u4ed6\u7684\u5634\u5df2\u7ecf\u88ab\u53f8\u9a6c\u5835\u4f4f\u3002\u53c8\u5728\u4ed6\u8033\u8fb9\u8bf4\uff1a\u201c\u6211\u4eec\u8fd8\u8981\u7ee7\u7eed\u62fc\u4e0b \u53bb\uff0c\u8ba9\u522b\u4eba\u4ee5\u4e3a\u6211\u4eec\u5df2\u7ecf\u5feb\u8981\u4e24\u8d25\u4ff1\u4f24\uff0c\u540c\u5f52\u4e8e\u5c3d\u4e86\u3002\u201d \u3000\u3000\u6731\u731b\u5e76\u4e0d\u662f\u53ea\u4f1a\u901e\u5339\u592b\u4e4b\u52c7\u7684\u83bd\u6c49\u3002 \u3000\u3000\u4ed6\u4e5f\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u4e5f\u5df2\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u53d1\u73b0\u4e86\u60c5\u52bf\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u7684\u5144\u5f1f\u4eec\u867d\u7136\u8fd8\u5728\u90a3\u91cc\uff0c\u53ef\u662f\u6bcf\u4e2a\u4eba\u7684\u8116\u5b50\u90fd\u5df2\u8f6f\u8f6f\u7684\u5782\u4e0b\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u55c5\u5230\u4e00\u79cd\u4ee4\u4eba\u4ece\u5fc3\u91cc\u4f5c\u5455\u7684\u6076\u81ed\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u82e6\u6218\u65f6\uff0c\u5df2\u7ecf\u6709\u4eba\u5728\u65e0\u58f0\u65e0\u606f\u4e2d\u62d7\u65ad\u4e86\u4ed6\u8fd9\u4e9b\u5144\u5f1f\u7684\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u8fd9\u4e9b\u8eab\u7ecf\u767e\u6545\u7684\u5144\u5f1f\uff0c\u771f\u80fd\u4f1a\u5982\u6b64\u8f7b\u6613\u5c31\u6b7b\u5728\u522b\u4eba\u624b\u91cc\uff1f \u3000\u3000\u6731\u731b\u4e0d\u4fe1\uff0c\u4e0d\u80fd\u76f8\u4fe1\u4e5f\u4e0d\u613f\u76f8\u4fe1\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5168\u8eab\u90fd\u5df2\u51c9\u900f\u3002 \u3000\u3000\u53f8\u9a6c\u5c45\u7136\u4e58\u673a\u4e00\u7ffb\u538b\u5728\u4ed6\u8eab\u4e0a\uff0c\u6325\u62f3\u75db\u51fb\u4ed6\u7684\u8f6f\u80c1\u548c\u808b\u9aa8\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6253\u5f97\u5e76\u4e0d\u91cd\uff0c\u58f0\u97f3\u66f4\u8f7b\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u6211\u4eec\u7a76\u7adf\u662f\u654c\u662f\u53cb\uff0c\u8fd9\u4e00\u6b21\u8981\u542c\u6211\u7684\u6d3b\uff0c\u5426\u5219\u4f60\u6211\u90fd\u6b7b\u4e0d\u7791\u76ee\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u8d70\uff0c\u4e00\u8d77\u8d70\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u9053\uff1a\u201c\u6211\u8bf4\u8d70\u7684\u65f6\u5019\uff0c\u6211\u4eec\u5c31\u8df3\u8d77\u6765\u4e00\u8d77\u8d70\u3002\u201d \u3000\u3000\u5ffd\u7136\u6709\u4eba\u7b11\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u9634\u9633\u602a\u6c14\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u5c0f\u53f8\u9a6c\u679c\u7136\u8fd8\u6709\u70b9\u513f\u806a\u660e\uff0c\u53ea\u53ef\u60dc\u5bf9\u6731\u731b\u8fd8\u662f\u6ca1\u6709\u7528\u7684\u3002\u201d \u8fd9\u4e2a\u4eba\u9634\u607b\u607b\u7684\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u53ea\u6709\u6740\u5934\u7684\u6731\u731b\uff0c\u6ca1\u6709\u9003\u8d70\u7684\u6731\u731b\u3002\u201d \u3000\u3000\u53f8\u9a6c\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u8f7b\u53f1\u4e00\u58f0\uff1a\u201c\u8d70\u3002\u201d \u4e5d \u3000\u3000\u591c\uff0c\u5bd2\u51b7\u800c\u9ed1\u6697\uff0c\u5c31\u7b97\u662f\u4e00\u4e2a\u76ee\u4e3a\u7ecf\u8fc7\u4e25\u683c\u826f\u597d\u8bad\u7ec3\u7684\u4eba\uff0c\u90fd\u5f88\u96be\u770b\u5f97\u6e05\u8fd1\u5728\u54ab\u5c3a\u7684\u6811 \u6728\u548c\u5ca9\u77f3\u3002\u5f53\u7136\u66f4\u65e0\u6cd5\u5206\u522b\u8def\u9014\u548c\u65b9\u5411\u3002 \u3000\u3000\u4f55\u51b5\u8fd9\u91cc\u6839\u672c\u6ca1\u6709\u8def\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5982\u679c\u5df2\u7ecf\u8d70\u5230\u6ca1\u6709\u8def\u7684\u5730\u65b9\uff0c\u901a\u5e38\u5c31\u662f\u8bf4\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u5230\u4e86\u65e0\u8def\u53ef\u8d70\u7684\u65f6\u5019\u4e86\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5728\u5598\u606f\uff0c\u4ed6\u7684\u80ba\u90e8\u867d\u7136\u51e0\u4e4e\u5df2\u5c06\u7206\u88c2\uff0c\u5374\u8fd8\u662f\u5c3d\u91cf\u6291\u5236\u7740\u81ea\u5df1\u7684\u5598\u606f\u58f0\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u7684\u6bcf\u4e00\u6839\u9aa8\u9abc\u6bcf\u4e00\u5757\u808c\u8089\u90e8\u597d\u50cf\u5df2\u6446\u5728\u5c60\u592b\u7684\u8089\u6848\u4e0a\uff0c\u5728\u88ab\u4eba\u7528\u5c0f\u5200\u5207\u5272\u3002 \u3000\u3000\u6731\u731b\u7684\u60c5\u51b5\u4e5f\u4e0d\u6bd4\u4ed6\u597d\u3002\u4e24\u4e2a\u4eba\u80a9\u9760\u7740\u80a9\uff0c\u7ad9\u5728\u8fd9\u4e00\u7247\u8352\u5bd2\u7684\u9ed1\u6697\u4e2d\uff0c\u4e0d\u505c\u7684\u5598\u606f\u7740\uff0c \u867d\u7136\u542c\u4e0d\u89c1\u730e\u4eba\u7684\u5f13\u5f26\u548c\u811a\u6b65\u58f0\uff0c\u5374\u5df2\u7ecf\u53ef\u4ee5\u611f\u89c9\u5230\u91ce\u517d\u8d1f\u4f24\u540e\u8fd8\u5728\u88ab\u730e\u4eba\u8ffd\u6355\u65f6\u90a3\u79cd\u7edd\u671b \u7684\u6c89\u75db\u4e0e\u60b2\u4f24\u3002 \u3000\u3000\u201c\u4f60\u77e5\u9053\u521a\u624d\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u4ed6\u4eec\u6765\u7684\u4e0d\u6b62\u4e00\u4e2a\u4eba\uff0c\u5176\u4e2d\u7684\u4efb\u4f55\u4e00\u4e2a\u4e5f\u8bb8\u90fd\u5df2\u7ecf\u8db3\u591f\u5bf9\u4ed8\u6211 \u4eec\u3002\u201d \u3000\u3000\u6731\u731b\u51b7\u7b11\uff1a\u201c\u60f3\u4e0d\u5230\u5929\u4e0b\u65e0\u53cc\u7684\u53f8\u9a6c\u8d85\u7fa4\u4e5f\u4f1a\u8bf4\u51fa\u8fd9\u79cd\u6cc4\u6c14\u8bdd\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u662f\u6cc4\u6c14\u8bdd\uff0c\u201d\u53f8\u9a6c\u8bf4\uff0c\u201c\u8fd9\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u6731\u731b\u6c89\u9ed8\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u9eef\u7136\u9053\uff1a\u201c\u662f\u7684\uff0c\u8fd9\u662f\u5b9e\u8bdd\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u91cc\u5145\u6ee1\u60b2\u4f24\uff1a\u201c\u53f8\u9a6c\u5df2 \u975e\u6614\u65e5\u4e4b\u53f8\u9a6c\uff0c\u6731\u731b\u5df2\u7ecf\u4e0d\u662f\u4ee5\u524d\u90a3\u4e2a\u6731\u731b\u4e86\uff0c\u5426\u5219\u600e\u4e48\u4f1a\u88ab\u4eba\u50cf\u91ce\u72d7\u822c\u8ffd\u5f97\u65e0\u8def\u53ef\u8d70\uff1f\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u4f60\u672c\u6765\u5b81\u6b7b\u4e5f\u4e0d\u4f1a\u9003\u8d70\u7684\uff0c\u4e16\u4e0a\u53ea\u6709\u6740\u5934\u7684\u6731\u731b\uff0c\u6ca1\u6709\u9003\u8d70\u7684\u6731 \u731b\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u53ef\u662f\u4f60\u4e3a\u4ec0\u4e48\u8981\u628a\u4f60\u8fd9\u9897\u5927\u597d\u5934\u9885\u9001\u7ed9\u4e00\u4e2a\u5351\u9119\u65e0\u803b\u7684\u5c0f\u4eba\uff1f\u4e3a\u4ec0\u4e48 \u8981\u8ba9\u4ed6\u63d0\u7740\u6211\u4eec\u7684\u5934\u9885\u53bb\u6362\u53d6\u4ed6\u7684\u58f0\u540d\u8363\u8000\u7f8e\u9152\u9ad8\u6b4c\u6b22\u5531\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d\u6731\u731b\u5389\u58f0\u9053\uff1a\u201c\u5c31\u7b97\u662f\u6211\u4eec\u8981\u628a\u8fd9\u9897\u5934\u9885\u9001\u4eba\uff0c\u4e5f\u8981\u9009\u4e00\u4e2a\u503c \u5f97\u6211\u4eec\u9001\u7684\u4eba\uff0c\u7edd\u4e0d\u80fd\u9001\u7ed9\u5353\u4e1c\u6765\u3002\u201d \u3000\u3000\u9ed1\u6697\u4e2d\u5ffd\u7136\u6709\u4eba\u5728\u9f13\u638c\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u8bf4\u5f97\u5bf9\u6781\u4e86\u3002\u201d \u3000\u3000\u53c8\u662f\u90a3\u4e2a\u9634\u9633\u602a\u6c14\u7684\u4eba\uff0c\u53c8\u662f\u90a3\u79cd\u9634\u607b\u607b\u7684\u7b11\u58f0\uff1a\u201c\u8fd9\u4e48\u597d\u7684\u4e24\u9897\u5934\u9885\uff0c\u600e\u4e48\u80fd\u9001\u7ed9\u5353 \u672a\u6765\u90a3\u79cd\u5927\u574f\u86cb\uff1f\u6211\u770b\u4f60\u4eec\u4e0d\u5982\u8fd8\u662f\u9001\u7ed9\u6211\u5427\u3002\u201d \u3000\u3000\u4ed6\u7684\u7b11\u58f0\u5ffd\u8fdc\u5ffd\u8fd1\uff0c\u5ffd\u5de6\u5ffd\u53f3\uff0c\u8ba9\u4eba\u6839\u672c\u542c\u4e0d\u51fa\u4ed6\u8fd9\u4e2a\u4eba\u7a76\u7adf\u5728\u54ea\u91cc\u3002 \u3000\u3000\u6731\u731b\u7684\u5168\u8eab\u90fd\u5df2\u50f5\u786c\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u662f\u5353\u4e1c\u6765\uff0c\u5374\u6bd4\u5353\u4e1c\u6765\u66f4\u53ef\u6015\uff0c\u6731\u731b\u8fd9\u4e00\u751f\u4e2d\u8fd8\u6ca1\u9047\u5230\u8fc7\u8f7b\u529f\u5982\u6b64\u53ef\u6015\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u7b80\u76f4\u4e0d\u80fd\u76f8\u4fe1\u4e16\u4e0a\u7adf\u6709\u4eba\u80fd\u7ec3\u6210\u8fd9\u822c\u9b3c\u9b45\u822c\u98d8\u5ffd\u6765\u53bb\u81ea\u5982\u7684\u8f7b\u529f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5f88\u5feb\u5c31\u53c8\u6062\u590d\u4e86\u9547\u5b9a\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u542c\u89c1\u53f8\u9a6c\u8d85\u7fa4\u7684\u8033\u8bed\uff1a\u201c\u8bf4\u8bdd\u7684\u4e0d\u662f\u4e00\u4eba\uff0c\u662f \u631b\u751f\u5144\u5f1f\u4e24\u4e2a\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201c\u53ea\u8981\u6211\u4eec\u80fd\u6c89\u4f4f\u6c14\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u7684\uff0c\u6240\u4ee5\u6211\u4eec\u7edd \u4e0d\u80fd\u8ba9\u4ed6\u770b\u51fa\u6211\u4eec\u7684\u865a\u5b9e\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u4ed6\u4eec\u4e24\u4e2a\u4eba\u7684\u8138\u5ffd\u7136\u88ab\u7167\u4eae\u4e86\uff0c\u8138\u4e0a\u7684\u6bcf\u4e00\u6839\u76b1\u7eb9\u6bcf\u4e00\u9053\u4f24\u75d5\u6bcf\u4e00\u79cd\u8868 \u60c5\u90fd\u88ab\u7167\u4eae\u4e86\u3002 \u3000\u3000\u6700\u5c11\u6709\u4e09\u5341\u76cf\u5de7\u624b\u7cbe\u5236\u7684\u5b54\u660e\u706f\uff0c\u4e09\u5341\u9053\u5f3a\u70c8\u7684\u706f\u5149\u4ece\u56db\u9762\u516b\u65b9\u7167\u8fc7\u6765\uff0c\u7167\u5728\u4ed6\u4eec\u8eab \u4e0a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u4eec\u7684\u8eab\u5b50\u5df2\u7ecf\u7ad9\u5f97\u7b14\u76f4\uff0c\u8138\u4e0a\u5df2\u7ecf\u5168\u65e0\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u8fd8\u662f\u770b\u4e0d\u89c1\u5bf9\u65b9\u7684\u4eba\u5728\u54ea\u91cc\uff0c\u53ef\u662f\u4ed6\u4eec\u4e5f\u6ca1\u6709\u8ba9\u5bf9\u65b9\u770b\u51fa\u4ed6\u4eec\u7684\u75b2\u4e4f\u4f24\u75db\u548c\u6050 \u60e7\u3002 \u3000\u3000\u4e24\u4e2a\u8eab\u7ecf\u767e\u6218\u3001\u767e\u70bc\u6210\u94a2\u7684\u4eba\uff0c\u4e24\u6761\u6c38\u4e0d\u5c48\u670d\u7684\u547d\uff0c\u65e0\u8bba\u8c01\u60f3\u8981\u4ed6\u4eec\u9888\u4e0a\u7684\u4eba\u5934\u90fd\u5f88\u4e0d \u5bb9\u6613\u3002 \u3000\u3000\u706f\u5149\u867d\u4eae\uff0c\u8fdc\u65b9\u7684\u9ed1\u6697\u4ecd\u7136\u662f\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5ffd\u7136\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u516c\u5b59\u516c\u5b59\uff0c\u522b\u6765\u65e0\u6059\uff1f\u201d\u4ed6\u5fae\u7b11\u7740\u9053\uff1a\u201c\u6211\u4e00\u5411\u77e5\u9053\u4f60\u4eec\u90fd\u662f\u5f88\u77e5\u9053\u597d\u6b79\u7684\u4eba\uff0c\u5982\u679c \u6211\u6210\u5168\u4e86\u4f60\u4eec\uff0c\u6210\u5c31\u4e86\u4f60\u4eec\u7684\u9738\u4e1a\uff0c\u4f60\u4eec\u4e00\u5b9a\u4f1a\u628a\u6211\u4eec\u8fd9\u5177\u6ca1\u6709\u5934\u7684\u5c38\u4f53\u597d\u597d\u5b89\u846c\uff0c\u6bcf\u5230\u6625 \u79cb\u796d\u65e5\uff0c\u4e00\u5b9a\u4f1a\u4ee5\u9999\u82b1\u7f8e\u9152\u4f9b\u5949\u5728\u6211\u4eec\u7684\u575f\u524d\u3002\u201d \u3000\u3000\u9ed1\u6697\u4e2d\u53c8\u7acb\u523b\u54cd\u8d77\u4e86\u638c\u58f0\u548c\u7b11\u58f0\uff0c\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u8bf4\u5f97\u5bf9\u6781\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6b21\u7b11\u58f0\u4ece\u5de6\u53f3\u4e24\u8fb9\u540c\u65f6\u54cd\u8d77\u6765\u7684\uff0c\u7136\u540e\u5c31\u6709\u4e24\u4e2a\u4eba\u4ece\u5de6\u53f3\u4e24\u8fb9\u540c\u65f6\u7531\u9ed1\u6697\u4e2d\u8d70\u5165\u4e86 \u706f\u5149\u53ef\u4ee5\u7167\u5f97\u5230\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e24\u4e2a\u770b\u8d77\u6765\u5b8c\u5168\u4e0d\u540c\u7684\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5934\u6234\u73e0\u51a0\uff0c\u8170\u675f\u7389\u5e26\uff0c\u5e26\u4e0a\u60ac\u957f\u5251\uff0c\u5251\u4e0a\u7f00\u5b9d\u7389\uff0c\u8863\u7740\u534e\u4e3d\u5982\u8d35\u516c\u5b50\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u5374\u597d\u50cf\u662f\u4e2a\u4e5e\u4e10\uff0c\u624b\u91cc\u62c4\u7740\u6839\u957f\u6728\u6756\u7684\u8ddb\u8db3\u4e5e\u4e10\u3002 \u3000\u3000\u53ef\u662f\u5982\u679c\u4f60\u4ed4\u7ec6\u53bb\u770b\uff0c\u8fd9\u4e24\u4e2a\u4eba\u7684\u8eab\u6750\u5bb9\u8c8c\u5374\u662f\u5b8c\u5168\u4e00\u6837\u7684\u3002 \u3000\u3000\u4e00\u4e00\u4e00\u516c\u5b59\u516c\u5b59\u3002 \u3000\u3000\u2014\u2014\u5b6a\u751f\u5144\u5f1f\u3002 \u3000\u3000\u6731\u731b\u5ffd\u7136\u60f3\u8d77\u4e86\u4e24\u4e2a\u4eba\uff0c\u4e24\u4e2a\u4ed6\u672c\u6765\u4e00\u76f4\u8ba4\u4e3a\u5b8c\u5168\u6ca1\u6709\u5173\u7cfb\u7684\u4eba\u3002 \u3000\u3000\u2014\u2014\u603b\u9886\u5173\u4e1c\u4e8c\u5341\u4e03\u5927\u5be8\uff0c\u949f\u545c\u9f0e\u98df\uff0c\u996e\u98df\u8d77\u5c45\u6bd4\u738b\u4faf\u8d35\u516c\u66f4\u8bb2\u7a76\u7684\u201c\u5bcc\u8d35\u516c\u5b50\u201d\u516c\u5b59 \u5b9d\u5251\u3002 \u3000\u3000\u2014\u2014\u6d6a\u8ff9\u5929\u6daf\uff0c\u4e09\u9910\u4e0d\u7ee7\uff0c\u7ecf\u5e38\u9189\u5367\u5728\u6c9f\u6e20\u4e2d\uff0c\u8fde\u4e10\u5e2e\u5374\u4e0d\u80af\u6536\u7559\u7684\u516c\u5b59\u4e5e\u513f\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4eec\u662f\u5144\u5f1f\uff0c\u800c\u4e14\u662f\u631b\u751f\u5144\u5f1f\u3002 \u3000\u3000\u65e2\u7136\u662f\u4eb2\u751f\u7684\u5144\u5f1f\uff0c\u4e3a\u4ec0\u4e48\u8981\u8ba9\u5176\u4e2d\u4e00\u4e2a\u9526\u8863\u7389\u98df\uff0c\u53e6\u4e00\u4e2a\u5374\u81ea\u7518\u8d2b\u8d31\uff1f \u3000\u3000\u6731\u731b\u8fd8\u6ca1\u6709\u60f3\u901a\u8fd9\u79cd\u9053\u7406\uff0c\u5374\u60f3\u5230\u4e86\u53e6\u5916\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u5230\u4e86\u53f8\u9a6c\u8d85\u7fa4\u548c\u5353\u4e1c\u6765\u3002 \u3000\u3000\u4e00\u4e00\u5353\u4e1c\u6765\u4e3a\u4ec0\u4e48\u8981\u5c06\u53f8\u9a6c\u8d85\u7fa4\u6367\u6210\u5929\u4e0b\u82f1\u96c4\u7684\u5076\u50cf\uff1f \u3000\u3000\u8fd9\u5176\u4e2d\u7684\u9053\u7406\uff0c\u65e2\u590d\u6742\u53c8\u7b80\u5355\uff0c\u867d\u7b80\u5355\u5374\u590d\u6742\uff0c\u975e\u4f46\u6731\u731b\u5728\u4e00\u65f6\u95f4\u60f3\u4e0d\u901a\uff0c\u522b\u4eba\u4e5f\u540c\u6837 \u60f3\u4e0d\u901a\u3002 \u3000\u3000\u53ef\u662f\u6731\u731b\u603b\u7b97\u60f3\u901a\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u5982\u679c\u53f8\u9a6c\u8d85\u7fa4\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u5b6a\u751f\u5144\u5f1f\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u8ba4\u4e3a\u516c\u5b59\u5b9d\u5251\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u8f7b\u529f\u9ad8 \u624b\uff0c\u542c\u5230\u90a3\u79cd\u9b3c\u9b45\u822c\u7684\u7b11\u58f0\u540e\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u88ab\u4ed6\u4eec\u9707\u6151\uff0c\u5c31\u597d\u50cf\u6731\u731b\u81ea\u5df2\u521a\u624d\u7684\u60c5\u51b5\u4e00\u6837\u3002 \u3000\u3000\u73b0\u5728\u6731\u731b\u5df2\u660e\u767d\uff0c\u90a3\u53ea\u4e0d\u8fc7\u662f\u4e00\u79cd\u70df\u5e55\u800c\u5df2\u3002 \u3000\u3000\u5728\u91d1\u543e\u4e0d\u7981\u7684\u5143\u5bb5\u591c\uff0c\u7687\u5bab\u5927\u5185\u4e2d\u65bd\u653e\u7684\u70df\u706b\u4e5f\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u770b\u6765\u8f89\u714c\u707f\u70c2\uff0c\u5343\u53d8\u4e07 \u5e7b\uff0c\u5982\u4e03\u5b9d\u697c\u53f0\uff0c\u5982\u9c7c\u9f99\u66fc\u884d\u3002 \u3000\u3000\u5176\u5b9e\u5374\u90fd\u662f\u5047\u7684\uff0c\u7a7a\u7684\uff0c\u5728\u4e00\u77ac\u95f4\u5c31\u5316\u4f5c\u4e86\u865a\u65e0\u7a7a\u5047\uff0c\u7a7a\u5047\u865a\u65e0\u3002 \u3000\u3000\u4f46\u662f\u5b83\u5374\u638c\u63e1\u4e86\u90a3\u4e00\u77ac\u95f4\u7684\u8f89\u714c\u5149\u5f69\u3002 \u3000\u3000\u5728\u67d0\u4e9b\u4eba\u5fc3\u65e5\u4e2d\uff0c\u80fd\u638c\u63e1\u8fd9\u4e00\u77ac\u95f4\u7684\u8f89\u714c\uff0c\u5c31\u5df2\u8db3\u6c38\u6052\u3002 \u3000\u3000\u5982\u679c\u8bf4\u4eba\u751f\u672c\u5982\u9006\u65c5\uff0c\u90a3\u4e48\u5728\u8fd9\u60a0\u60a0\u4e0d\u53d8\u7684\u5929\u5730\u95f4\uff0c\u201c\u4e00\u77ac\u201d\u548c\u201c\u6c38\u6052\u201d\u53c8\u6709\u4ec0\u4e48\u533a \u522b\uff1f \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u4e4b\u4e2d\u6709\u4e00\u4e2a\u4eba\u5b81\u613f\u4e3a\u4e00\u4e2a\u4eba\u53bb\u727a\u7272\uff0c\u800c\u4e14\u6beb\u65e0\u6028\u5c24\u3002 \u3000\u3000\u552f\u4e00\u7684\u95ee\u9898\u662f\u2014\u2014 \u3000\u3000\u771f\u6b63\u88ab\u727a\u7272\u7684\u662f\u8c01\uff1f\u771f\u6b63\u5f97\u5230\u6ee1\u8db3\u7684\u53c8\u662f\u8c01\uff1f \u3000\u3000\u8fd9\u95ee\u9898\u6731\u731b\u975e\u4f46\u66f4\u60f3\u4e0d\u901a\uff0c\u73b0\u5728\u7684\u60c5\u51b5\u4e5f\u4e0d\u5bb9\u4ed6\u518d\u60f3\u8fd9\u4e9b\u4e8b\u3002 \u3000\u3000\u4ed6\u542c\u5230\u53f8\u9a6c\u8d85\u7fa4\u6b63\u5728\u5bf9\u516c\u5b59\u5144\u5f1f\u8bf4\uff1a \u3000\u3000\u201c\u5176\u5b9e\u6211\u65e9\u5c31\u77e5\u9053\u4e24\u4f4d\u4f1a\u6765\u7684\u3002\u201d\u53f8\u9a6c\u4ecd\u5728\u5fae\u7b11\uff1a\u201c\u591a\u5e74\u4e4b\u524d\uff0c\u4e24\u4f4d\u5c31\u5df2\u60f3\u5c06\u6211\u9a71\u51fa\u5927 \u9556\u5c40\uff0c\u53ea\u4e0d\u8fc7\u4e00\u76f4\u6ca1\u6709\u628a\u63e1\u800c\u5df2\uff0c\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\uff0c\u4e24\u4f4d\u81ea\u7136\u4e0d\u4f1a\u505a\u7684\uff0c\u6240\u4ee5\u624d\u4f1a\u7b49\u5230\u4eca \u65e5\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u53ef\u662f\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u4e24\u4f4d\u600e\u4e48\u4f1a\u6765\u5f97\u5982\u6b64\u5feb\u3002\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u60f3\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u516c\u5b59\u5b9d\u5251\u8bf4\uff1a\u201c\u50cf\u4eca\u65e5\u8fd9\u6837\u7684\u673a\u4f1a\uff0c\u6211\u5df2\u7b49\u4e86\u5f88\u4e45\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\u673a\u4f1a\u5df2\u7ecf\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u51e0\u65f6\u77e5\u9053\u7684\uff1f\u201d\u53f8\u9a6c\u8d85\u7fa4\u8bf4\uff1a\u201d\u6211\u77e5\u9053\u4f60\u7684\u9a6c\u53a9\u4e2d\u4e0d\u4e4f\u5343\u91cc\u826f\u9a79\uff0c\u53ef\u662f\u5c31\u7b97\u4f60\u80fd\u65e5 \u884c\u5343\u91cc\uff0c\u6700\u5feb\u4e5f\u8981\u7a77\u56db\u4e94\u65e5\u4e4b\u529b\u624d\u80fd\u8d76\u6765\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4ed6\u95ee\u516c\u5b59\u5b9d\u5251\uff0c\u201c\u96be\u9053\u4f60\u5728\u4e94\u5929\u4e4b\u524d\uff0c\u5c31\u5df2\u7b97\u51c6\u4e86\u4f1a\u6709\u6628\u65e5\u4e4b\u4e8b\u53d1\u751f\uff1f\u96be\u9053\u4f60\u5728\u4e94\u5929\u4e4b \u524d\u5c31\u5df2\u7b97\u51c6\u4e86\u6211\u4f1a\u548c\u5353\u4e1c\u6765\u53cd\u76ee\u6210\u4ec7\uff0c\u62d4\u5200\u76f8\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u4e5f\u8bb8\u6211\u5728\u5927\u9556\u5c40\u4e2d\u4e5f\u6709\u5367\u5e95\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u5230\u8fc7\uff0c\u53ef\u662f\u90a3\u4e5f\u6ca1\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u6ca1\u6709\u7528\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4e94\u5929\u4e4b\u524d\uff0c\u8fde\u6211\u81ea\u5df1\u90fd\u6ca1\u6709\u60f3\u5230\u4f1a\u6709\u4eca\u65e5\uff0c\u522b\u4eba\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u5353\u4e1c\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e5f\u60f3\u4e0d\u5230\u7684\u3002\u201d\u53f8\u9a6c\u7684\u58f0\u97f3\u4e2d\u5df2\u6709\u4e86\u611f\u4f24\uff1a\u201c\u76f4\u5230\u6211\u62d4\u5200\u4e4b\u524d\uff0c\u4ed6\u8fd8\u4e0d\u4fe1\u6211\u771f\u7684\u4f1a \u62d4\u5200\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u90a3\u65f6\u4ed6\u5df1\u60f3\u5230\uff0c\u4e5f\u4e0d\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0e\u4ed6\u6570\u5341\u5e74\u4ea4\u60c5\uff0c\u867d\u7136\u5df2\u6bc1\u4e8e\u4e00\u77ac\u95f4\uff0c\u53ef\u662f\u5f53\u4eca\u4e16\u4e0a\uff0c\u8fd8\u662f\u6ca1\u6709\u4eba\u6bd4\u6211\u66f4\u4e86\u89e3 \u4ed6\u3002\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u5c31\u7b97\u4ed6\u8981\u51fa\u5356\u6211\uff0c\u4e5f\u4e0d\u4f1a\u5356\u7ed9\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u8fd8\u4e0d\u914d\uff0c\u201d\u53f8\u9a6c\u8d85\u7fa4\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u5728\u5353\u4e1c\u4eb2\u773c\u4e2d\uff0c\u9601\u4e0b\u4e24\u5144\u5f1f\u52a0\u8d77\u6765\u8fd8\u4e0d\u503c\u4e00 \u6587\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u901a\u4f60\u600e\u4e48\u80fd\u5728\u4eca\u65e5\u8d76\u5230\u8fbe\u91cc\uff0c\u9664\u975e\u4f60\u771f\u7684\u6709\u90a3\u79cd\u672a \u535c\u5148\u77e5\u7684\u672c\u4e8b\u3002\u201d \u3000\u3000\u516c\u5b59\u4e5e\u513f\u5ffd\u7136\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u867d\u7136\u6ca1\u6709\u672a\u535c\u5148\u77e5\u7684\u672c\u4e8b\uff0c\u53ef\u89c1\u6211\u5df2\u7ecf\u60f3\u5230\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b9d\u5251\u7acb\u523b\u95ee\u4ed6\u7684\u5144\u5f1f\uff0c\u201c\u4f60\u60f3\u5230\u4e86\uff1f\u4f60\u60f3\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u5ffd\u7136\u60f3\u5230\u4f60\u5b9e\u5728\u4e5f\u5e94\u8be5\u8ddf\u6211\u4e00\u6837\uff0c\u591a\u5230\u6c5f\u6e56\u4e2d\u6765\u8d70\u52a8\u8d70\u52a8\u7684\uff0e\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u5982\u679c\u4e5f\u8ddf\u6211\u4e00\u6837\u8001\u597d\u5de8\u733e\uff0c\u4f60\u5c31\u4f1a\u660e\u767d\u4ed6\u7684\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7684\u610f\u601d\u53ea\u4e0d\u8fc7\u662f\u8981\u6211\u4eec\u591a\u966a\u4ed6\u804a\u804a\u5929\uff0c\u8bf4\u8bf4\u8bdd\u3002\u201d\u516c\u5b59\u4e5e\u513f\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u7684\u80c6\u5df2 \u4e27\uff0c\u6c14\u5df2\u9981\uff0c\u529b\u5df2\u7aed\uff0c\u6b63\u597d\u5229\u7528\u6211\u4eec\u966a\u4ed6\u8bf4\u8bdd\u7684\u65f6\u5019\u6062\u590d\u6062\u590d\u5143\u6c14\uff0c\u7b49\u6211\u4eec\u51fa\u624b\u65f6\uff0c\u8bf4\u4e0d\u5b9a \u8fd8\u53ef\u4ee5\u62db\u67b6\u4e00\u4e24\u4e0b\u5b50\u3002\u201d \u3000\u3000\u4ed6\u6447\u5934\u53f9\u606f\uff1a\u201c\u4e0d\u5230\u9ec4\u6cb3\u5fc3\u4e0d\u6b7b\uff0c\u4e0d\u89c1\u68fa\u6751\u4e0d\u6389\u6cea\uff0c\u4e0d\u7b49\u5230\u8111\u888b\u771f\u7684\u88ab\u780d\u4e0b\u6765\u65f6\uff0c\u6211\u4eec \u7684\u5c0f\u53f8\u9a6c\u662f\u7edd\u4e0d\u4f1a\u6b7b\u5fc3\u7684\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5ffd\u7136\u7b11\u4e86\uff0c\u6731\u731b\u4e5f\u7b11\u4e86\uff0c\u4e24\u4e2a\u4eba\u5c45\u7136\u540c\u65f6\u5927\u7b11\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u8bf4\u5f97\u5bf9\u6781\u4e86\u3002\u201d \u3000\u3000\u6731\u731b\u5927\u7b11\u7740\u5411\u4e5e\u513f\u62db\u624b\uff1a\u201c\u672a\u6765\u6765\uff0c\u4f60\u8d76\u5feb\u8fc7\u6765\uff0c\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u8fc7\u53bb\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6731\u5927\u592a\u7237\u5df2\u7ecf\u770b\u4e0a\u4f60\u8fd9\u4e2a\u8001\u597d\u5de8\u733e\u7684\u5c0f\u738b\u516b\u7f94\u5b50\u4e86\uff0c\u5f88\u60f3\u628a\u8001\u5b50\u8fd9\u4e2a\u8111\u888b\u9001\u7ed9 \u4f60\uff0c\u53ea\u770b\u4f60\u6709\u6ca1\u6709\u672c\u4e8b\u80fd\u62ff\u5f97\u8d70\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5927\u7b11\u7740\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u3002 \u3000\u3000\u201c\u597d\u3002\u8fd9\u4e2a\u5c0f\u738b\u516b\u7f94\u5b50\u5c31\u7ed9\u4f60\uff0c\u90a3\u4e2a\u6bd4\u4ed6\u5927\u4e00\u70b9\u7684\u738b\u516b\u7f94\u5b50\u5f52\u6211\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u529e\u3002\u201d\u6731\u731b\u7684\u7b11\u58f0\u8c6a\u6c14\u5982\u4e91\uff1a\u201c\u82e5\u662f\u51ed\u54b1\u4eec\u4e24\u4e2a\u8fd8\u5bf9\u4ed8\u4e0d\u4e86\u8fd9\u4e24\u4e2a\u5c0f\u738b\u516b \u86cb\uff0c\u90a3\u4e48\u54b1\u4eec\u4e0d\u5982\u8d76\u5feb\u53bb\u4e70\u5757\u8c46\u8150\u6765\u4e00\u5934\u649e\u6b7b\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u5e76\u80a9\u800c\u7acb\uff0c\u7eb5\u58f0\u5927\u7b11\uff0c\u4ec0\u4e48\u53eb\u201c\u751f\u201d\uff0c\u4ec0\u4e48\u53eb\u201c\u6b7b\u201d\uff0c\u90fd\u88ab\u4ed6\u4eec\u7b11\u5f97\u6eda\u5230\u4e00\u8fb9\u53bb \u4e86\u3002 \u3000\u3000\u516c\u5b59\u5144\u5f1f\u7684\u8138\u8272\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u7684\u8138\u8272\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u53d8\u7684\uff0c\u8138\u4e0a\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u65b0\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u4eec\u5144\u5f1f\u5c31\u662f\u8fd9\u79cd\u4eba\uff0c\u53ea\u4e0d\u8fc7\u516c\u5b59\u4e5e\u513f\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u53f9\u7740\u6c14\u95ee\u4ed6\u7684\u5144\u5f1f\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u542c \u89c1\u90a3\u4f4d\u4ec1\u5144\u8bf4\u7684\u8bdd\uff1f\u201d \u3000\u3000\u201c\u6211\u542c\u89c1\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4f4d\u4ec1\u5144\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u96c4\u72ee\u5802\u7684\u6731\u731b\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u5427\uff0c\u4e0d\u4f1a\u662f\u6731\u731b\u5427\u3002\u201d\u516c\u5b59\u4e5e\u513f\u8bf4\uff1a\u201c\u96c4\u72ee\u5802\u7684\u6731\u731b\u662f\u6761\u6069\u6028\u5206\u660e\u7684\u597d\u6c49\uff0c\u548c\u5927 \u9556\u5c40\u7684\u5c0f\u53f8\u9a6c\u4e00\u76f4\u662f\u4e0d\u5171\u6234\u5929\u7684\u6b7b\u654c\uff0c\u73b0\u5728\u4ed6\u4eec\u4e24\u4e2a\u4eba\u600e\u4e48\u4f1a\u5ffd\u7136\u53d8\u5f97\u7a7f\u8d77\u4e00\u6761\u88e4\u5b50\u6765 \u4e86\uff1f\u201d \u3000\u3000\u6731\u731b\u5ffd\u7136\u7528\u529b\u63e1\u4f4f\u53f8\u9a6c\u8d85\u7fa4\u7684\u81c2\uff0c\u6c89\u58f0\u95ee\uff1a\u201c\u90a3\u4e5e\u513f\u8bf4\u7684\u8bdd\u4f60\u53ef\u66fe\u542c\u5230\uff1f\u201d \u3000\u3000\u201c\u6211\u542c\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u4e5e\u513f\u8bf4\u7684\u6d3b\u867d\u7136\u603b\u5e26\u7740\u4e9b\u4e5e\u513f\u6c14\uff0c\u5374\u4e5f\u4e00\u8bed\u9053\u7834\u4e86\u4f60\u6211\u4eca\u65e5\u7684\u5904\u5883\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u4f60 \u6211\u672c\u662f\u4e00\u4e16\u4e4b\u6b7b\u654c\uff0c\u8c01\u80fd\u60f3\u5f97\u5230\u4eca\u65e5\u7adf\u6210\u4e3a\u540c\u751f\u5171\u6b7b\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u5df2\u7ecf\u662f\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u6731\u731b\u5927\u58f0\u9053\uff1a\u201c\u4ece\u4eca\u65e5\u8d77\uff0c\u4f60\u6211\u4e0d\u59a8\u5c06\u6614\u65e5\u7684\u6028\u4ec7\u4e00\u7b14\u52fe\u9500\u3002\u201d \u3000\u3000\u53f8\u9a6c\u5927\u7b11\u3002 \u3000\u3000\u201c\u597d\uff0c\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u6211\u4e00\u65e5\u4e3a\u53cb\uff0c\u7ec8\u751f\u4e3a\u53cb\u3002\u201d\u6731\u731b\u5389\u58f0\u9053\uff1a\u201c\u53ea\u8981\u6211\u6731\u731b\u4e0d\u6b7b\uff0c\u5982\u8fdd\u6b64\u7ea6\uff0c\u4eba\u795e\u5171 \u6b9b\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u53ea\u89c9\u80f8\u4e2d\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4eec\u90fd\u6b7b\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u8fd9\u80a1\u70ed\u8840\u5c31\u50cf\u662f\u4e00\u80a1\u706b\u7130\uff0c\u53c8\u71c3\u8d77\u4e86\u4ed6\u4eec\u7684\u8c6a\u6c14\uff0c\u8fde\u4ed6\u4eec\u751f\u547d\u4e2d\u6700\u540e\u4e00\u5206\u6f5c\u529b\u90fd\u5df2\u5f7c\u5f15 \u53d1\u71c3\u70e7\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5df2\u7ecf\u77e5\u9053\uff0c\u4ed6\u4eec\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5e76\u4e0d\u5bc2\u5bde\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u81f3\u5c11\u8fd8\u6709\u4e00\u4e2a\u670b\u53cb\uff0c\u4e00\u4e2a\u540c\u751f\u5171\u6b7b\u3001\u751f\u6b7b\u4e0d\u6e1d\u7684\u670b\u53cb\u3002 \u3000\u3000\u4eba\u751f\u81f3\u6b64\uff0c\u6b7b\u6709\u4f55\u61be\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e92\u76f8\u7528\u529b\u4e00\u63e1\u5bf9\u65b9\u7684\u624b\uff0c\u53ea\u89c9\u5f97\u8fd9\u80a1\u70ed\u8840\u5df2\u5e26\u4e00\u80a1\u795e\u5947\u7684\u529b\u91cf\uff0c\u81ea\u80f8\u4e2d\u5954\u6cfb\u800c \u51fa\uff0c\u8fde\u8138\u4e0a\u90fd\u7115\u53d1\u51fa\u8f89\u714c\u7684\u5149\u91c7\u3002 \u3000\u3000\u516c\u5b59\u5144\u5f1f\u7684\u8138\u8272\u5374\u53d8\u4e86\u3002 \u3000\u3000\u6731\u731b\u4e0e\u53f8\u9a6c\u540c\u65f6\u8f6c\u8eab\uff0c\u4ee5\u80cc\u9760\u80cc\u3002 \u3000\u3000\u201c\u4f60\u4eec\u6765\u5427\u3002\u201d\u53f8\u9a6c\u8d85\u7fa4\u5389\u58f0\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u4eec\u6709\u591a\u5c11\u4eba\uff0c\u90fd\u4e00\u8d77\u6765\u5427\u3002\u201d \u3000\u3000\u5915\u9633\u5df2\u6ca1\u4e8e\u897f\u5c71\uff0c\u82f1\u96c4\u5df2\u5230\u4e86\u672b\u8def\uff0c\u516c\u5b59\u5144\u5f1f\u672c\u6765\u5df2\u5c06\u4ed6\u4eec\u5f53\u4f5c\u91dc\u4e2d\u7684\u9c7c\uff0c\u7827\u4e0a\u7684\u8089\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u8fd9\u5144\u5f1f\u4e24\u4eba\u5374\u4e0d\u7ea6\u800c\u540c\u540e\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u624d\u77e5\u9053\uff0c\u82f1\u96c4\u867d\u7136\u5df2\u81f3\u672b\u8def\uff0c\u4ecd\u7136\u8fd8\u662f\u82f1\u96c4\uff0c\u4ecd\u7136\u4e0d\u53ef\u8f7b\u4fae\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u5929\u8272\u66f4\u6697\u4e86\uff0c\u4eff\u4f5b\u5df2\u5230\u4e86\u9ece\u660e\u524d\u6700\u9ed1\u6697\u7684\u90a3\u6bb5\u65f6\u5019\u3002 \u3000\u3000\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\u4e2d\uff0c\u5ffd\u7136\u54cd\u8d77\u4e86\u4e00\u9635\u51c4\u51b7\u7684\u8427\u58f0\uff0c\u4e00\u4e2a\u54c0\u5a49\u67d4\u7f8e\u7684\u5c11\u5973\u58f0\u97f3\uff0c\u4f34\u7740\u8427\u58f0 \u66fc\u58f0\u5531\u8d77\u4e86\u4e00\u66f2\u4ee4\u4eba\u6c38\u96be\u5fd8\u6000\u7684\u60b2\u6b4c\u3002 \u3000\u3000\u6b4c\u58f0\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f \u3000\u3000\u5728\u4e00\u4e2a\u5982\u6b64\u5bd2\u51b7\u9ed1\u6697\u7684\u665a\u4e0a\uff0c\u5982\u6b64\u8352\u51c9\u8083\u6740\u7684\u6df1\u5c71\u91cc\uff0c\u600e\u4e48\u4f1a\u6709\u4eba\u5531\u8fd9\u66f2\u4eca\u4eba\u5fc3\u788e\u7684\u60b2 \u6b4c\uff1f \u7b2c\u5341\u516b\u7ae0\u3000\u82f1\u96c4\u4e0d\u6b7b>> \u53e4\u9f99\u300a\u82f1\u96c4\u65e0\u6cea\u300b \u7b2c\u5341\u516b\u7ae0\u3000\u82f1\u96c4\u4e0d\u6b7b \u4e00 \u3000\u3000\u4e8c\u6708\u4e8c\u5341\u4e03\u65e5\u3002 \u3000\u3000\u957f\u5b89\u57ce\u5916\uff0c\u8352\u91ce\u7a77\u5c71\u3002 \u3000\u3000\u8ddd\u79bb\u5929\u4eae\u8fd8\u6709\u6bb5\u65f6\u5019\uff0c\u5929\u5730\u95f4\u4ecd\u662f\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u5728\u6570\u5341\u76cf\u5b54\u660e\u706f\u7167\u5c04\u4e0b\u7684\u5149\u5f71\u5916\uff0c\u6709\u4e24\u6761\u4eba\u5f71\u968f\u7740\u6b4c\u58f0\u5982\u5e7d\u9b42\u822c\u51fa\u73b0\uff0c\u4e00\u4eba\u62b1\u7435\u7436\uff0c\u4e00 \u4eba\u5439\u6d1e\u8427\u3002 \u3000\u3000\u4eba\u5f71\u6726\u80e7\uff0c\u6b4c\u58f0\u51c4\u5a49\uff0c\u5728\u4f59\u5149\u53cd\u6620\u4e2d\uff0c\u4f9d\u7136\u53ef\u4ee5\u5206\u8fa8\u51fa\u4ed6\u4eec\u5c31\u662f\u90a3\u4e00\u591c\u5728\u957f\u5b89\u5c45\u7b2c\u4e00\u697c \u697c\u5934\u5356\u5531\u7684\u76f2\u76ee\u767d\u5934\u4e50\u5e08\uff0c\u4f34\u7740\u4ed6\u7684\u4f9d\u7136\u662f\u90a3\u4e2a\u8ba9\u4eba\u4e00\u770b\u89c1\u5c31\u4f1a\u5fc3\u788e\u7684\u778e\u773c\u5c0f\u5973\u5b69\u3002 \u3000\u3000\u4ed6\u4eec\u600e\u4e48\u4f1a\u5ffd\u7136\u5728\u8fd9\u91cc\u51fa\u73b0\uff1f\u662f\u4e0d\u662f\u6709\u4eba\u7279\u5730\u8981\u4ed6\u4eec\u5230\u8fd9\u91cc\u6765\u5531\u8fd9\u66f2\u60b2\u6b4c\uff1f \u3000\u3000\u201c\u5b9d\u9afb\u5306\u5306\u68b3\u5c31\uff0c \u3000\u3000\u94c5\u534e\u6de1\u6de1\u5986\u6210\uff0c \u3000\u3000\u9752\u70df\u7d2b\u96fe\u7f69\u8f7b\u76c8\uff0c \u3000\u3000\u98de\u7d6e\u6e38\u4e1d\u65e0\u5b9a\u3002\u201d \u3000\u3000\u6625\u8695\u5df2\u6b7b\uff0c\u4e1d\u72b9\u672a\u5c3d\u3002\u8721\u70ac\u5df2\u6b8b\uff0c\u6cea\u72b9\u6765\u5e72\u3002 \u3000\u3000\u6731\u731b\u6ee1\u8138\u7684\u70ed\u8840\u4e0e\u8c6a\u6c14\uff0c\u5ffd\u7136\u95f4\u5c31\u5df2\u5316\u6210\u4e86\u65e0\u5b9a\u7684\u6e38\u4e1d\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u53c8\u770b\u89c1\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u5ffd\u7136\u53c8\u6709\u4e00\u4e2a\u4eba\u51fa\u73b0\u4e86\uff0c\u5c31\u50cf\u662f\u68a6\u4e2d\u8774\u8776\u7684\u5e7d\u7075\uff0c\u4ee5\u8f7b\u7eb1\u8499\u9762\uff0c\u7a7f\u4e00\u8eab\u7fbd\u8749\u822c\u7684 \u8f7b\u7eb1\u821e\u8863\u3002 \u3000\u3000\u821e\u8863\u98d8\u8d77\u3002 \u3000\u3000\u201c\u76f8\u89c1\u4e0d\u5982\u4e0d\u89c1\uff0c \u3000\u3000\u6709\u60c5\u6070\u4f3c\u65e0\u60c5\uff1b \u3000\u3000\u7b19\u6b4c\u6563\u540e\u9152\u521d\u9192\u3002 \u3000\u3000\u5ead\u9662\u6708\u659c\u4eba\u9759\u3002\u201d \u3000\u3000\u821e\u8863\u98d8\u98d8\u5982\u8774\u8776\uff0c\u821e\u8005\u4e5f\u5982\u8774\u8776\u3002 \u3000\u3000\u6731\u731b\u6ca1\u6709\u6d41\u6cea\uff0c\u6731\u731b\u5df2\u65e0\u6cea\u3002\u751a\u81f3\u70ed\u8840\u90fd\u4f3c\u5df2\u6d41\u5e72\u4e86\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5979\u4e0d\u662f\u8776\u821e\uff0c\u53ef\u662f\u5979\u7684\u821e\u5374\u53c8\u628a\u4ed6\u5e26\u5165\u4e86\u8774\u8776\u7684\u68a6\u5883\u3002\u4f3c\u771f\u975e\u771f\uff0c\u4f3c\u5e7b\u975e\u5e7b\u3002 \u3000\u3000\u7a76\u7adf\u6302\u771f\u662f\u5e7b\uff1f \u3000\u3000\u662f\u771f\u53c8\u5982\u4f55\uff1f\u662f\u5e7b\u4e49\u5982\u4f55\uff1f\u5982\u6b64\u77ed\u6682\u7684\u751f\u547d\uff0c\u5982\u6b64\u73cd\u8d35\u7684\u611f\u60c5\uff0c\u53c8\u4f55\u5fc5\u592a\u8ba4\u771f\uff1f \u3000\u3000\u5c31\u8ba9\u4ed6\u53bb\u5427\uff01\u4ec0\u4e48\u4e8b\u90fd\u8ba9\u4ed6\u53bb\u5427\uff01\u968f\u8774\u8776\u800c\u53bb\uff0c\u53bb\u4e86\u6700\u597d\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u73b0\u5728\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u5728\u62d4\u5251\u95f4\u5c06\u4ed6\u523a\u6740\uff0c\u53ef\u662f\u4ed6\u5df2\u7ecf\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u51c6\u5907\u653e\u5f03\u4e00\u5207\u3002 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5374\u4e0d\u8ba9\u4ed6\u653e\u5f03\uff0c\u6b4c\u8005\u4ecd\u5728\u6b4c\uff0c\u821e\u8005\u4ecd\u5728\u821e\uff0c\u53f8\u9a6c\u8d85\u7fa4\u5ffd\u7136\u732b\u4e00\u822c\u6251\u8fc7\u53bb\uff0c\u8981\u628a \u8fd9\u53ea\u8774\u8776\u6251\u6740\u5728\u4ed6\u7684\u5229\u722a\u4e0b\u3002 \u3000\u3000\u821e\u8005\u975e\u4f46\u6ca1\u6709\u95ea\u907f\uff0c\u53cd\u800c\u8fce\u4e86\u4e0a\u53bb\uff0c\u4ee5\u4e00\u79cd\u65e0\u6bd4\u8f7b\u76c8\u7684\u821e\u59ff\u8fce\u4e86\u4e0a\u53bb\uff0c\u5148\u95ea\u8fc7\u4e86\u4ed6\u8fd9\u4e00 \u51fb\uff0c\u5ffd\u7136\u5728\u4ed6\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u51fa\u4e86\u4e24\u4e2a\u5b57\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u542c\u5f97\u89c1\u5979\u8bf4\u7684\u662f\u4e24\u4e2a\u4ec0\u4e48\u5b57\uff0c\u53ef\u662f\u6bcf\u4e2a\u4eba\u90fd\u770b\u5230\u4e86\u53f8\u9a6c\u8d85\u7fa4\u7684\u53d8\u5316\u3002 \u3000\u3000\u201c\u540c\u540c\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u5979\u8bf4\u7684\u90a3\u4e24\u4e2a\u5b57\uff0c\u4e24\u4e2a\u5b8c\u5168\u6ca1\u6709\u4efb\u4f55\u610f\u4e49\u7684\u5b57\u3002 \u3000\u3000\u201c\u540c\u540c\u3002\u201d \u3000\u3000\u65e0\u8bba\u8c01\u542c\u5230\u8fd9\u4e24\u4e2a\u5b57\u90fd\u4e0d\u4f1a\u6709\u4efb\u4f55\u53cd\u5e94\u7684\uff0c\u53ef\u662f\u5bf9\u53f8\u9a6c\u8d85\u7fa4\u6765\u8bf4\u3002\u8fd9\u4e24\u4e2a\u5b57\u5374\u50cf\u662f\u4e00\u9053 \u5ffd\u7136\u81ea\u534a\u7a7a\u4e2d\u51fb\u4e0b\u7684\u95ea\u7535\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u6240\u6709\u7684\u52a8\u4f5c\u5ffd\u7136\u505c\u6b62\uff0c\u4ed6\u7684\u8eab\u4f53\u56db\u80a2\u4e5f\u5ffd\u7136\u50f5\u786c\uff0c\u773c\u4e2d\u5ffd\u7136\u5145\u6ee1\u4e86\u60ca \u8bb6\u4e0e\u6050\u60e7\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u4e00\u6b65\u6b65\u5f80\u540e\u9000\u3002 \u3000\u3000\u201c\u540c\u540c\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u5b57\u5c31\u50cf\u662f\u67d0\u79cd\u795e\u79d8\u7684\u9b54\u5492\uff0c\u5728\u4e00\u77ac\u95f4\u5c31\u5df2\u6444\u53bb\u4e86\u53f8\u9a6c\u8d85\u7fa4\u7684\u9b42\u9b44\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u5b50\uff1f \u3000\u3000\u4e00\u4e2a\u8c01\u4e5f\u4e0d\u77e5\u9053\u5979\u662f\u8c01\u3001\u4e5f\u4e0d\u77e5\u9053\u5979\u662f\u4ece\u54ea\u91cc\u6765\u7684\u821e\u8005\uff0c\u4e24\u4e2a\u4efb\u4f55\u4eba\u542c\u8d77\u6765\u90e8\u8ba4\u4e3a\u6beb\u65e0 \u610f\u4e49\u7684\u5b57\uff0c\u4e3a\u4ec0\u4e48\u80fd\u8ba9\u53f8\u9a6c\u8d85\u7fa4\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u89e3\u91ca\u8fd9\u4ef6\u4e8b\uff0c\u53ef\u662f\u53e6\u5916\u4e00\u4ef6\u4e8b\u5374\u662f\u6bcf\u4e2a\u4eba\u90fd\u80fd\u770b\u5f97\u51fa\u6765\u7684\u3002 \u3000\u3000\u2014\u2014\u53f8\u9a6c\u8d85\u7fa4\u548c\u6731\u731b\u90fd\u5df2\u7ecf\u5b8c\u4e86\uff0c\u4ed6\u4eec\u7684\u5934\u9885\u5728\u8f6c\u77ac\u95f4\u5c31\u5c06\u8981\u88ab\u4eba\u63d0\u5728\u624b\u91cc\u3002 \u3000\u3000\u778e\u773c\u7684\u767d\u5934\u4e50\u5e08\uff0c\u867d\u7136\u4ec0\u4e48\u90fd\u770b\u4e0d\u51fa\uff0c\u53ef\u662f\u4ed6\u7684\u8427\u58f0\u91cc\u4e5f\u5df2\u9690\u9690\u6709\u4e86\u79cd\u82cd\u51c9\u7684\u8083\u6740\u4e4b \u610f\u3002 \u3000\u3000\u5929\u5730\u95f4\u5ffd\u7136\u5145\u6ee1\u4e86\u6740\u673a\uff0c\u8fde\u706f\u5149\u90fd\u53d8\u5f97\u82cd\u767d\u800c\u60e8\u70c8\uff0c\u7167\u5728\u53f8\u9a6c\u548c\u6731\u731b\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u4e5f\u7167 \u4eae\u4e86\u516c\u5b59\u5b9d\u5251\u63e1\u5251\u7684\u624b\u3002 \u3000\u3000\u5b9d\u5251\u5df2\u5c06\u51fa\u9798\uff0c\u4eba\u5934\u5df2\u5c06\u843d\u5730\u3002 \u3000\u3000\u60e8\u70c8\u7684\u706f\u5149\u5ffd\u7136\u95ea\u4e86\u95ea\uff0c\u95ea\u52a8\u7684\u706f\u5149\u4e2d\u4eff\u4f5b\u5ffd\u7136\u53c8\u95ea\u8d77\u4e86\u4e00\u9053\u6bd4\u706f\u5149\u66f4\u60e8\u70c8\u7684\u5149\u8292\u3002 \u3000\u3000\u5149\u8292\u4e00\u95ea\u800c\u6ca1\uff0c\u4e00\u5251\u7a7f\u80f8\u800c\u8fc7\u3002 \u3000\u3000\u516c\u5b59\u5b9d\u5251\u638c\u4e2d\u7684\u5251\u72b9\u672a\u51fa\u9798\uff0c\u5df2\u7ecf\u88ab\u4e00\u67c4\u5251\u9489\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u5e76\u4e0d\u662f\u5ffd\u7136\u4ece\u5929\u5916\u98de\u6765\u7684\uff0c\u662f\u4e00\u4e2a\u4eba\u98de\u8eab\u523a\u8fc7\u6765\u7684\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u8fd9\u4e2a\u4eba\u548c\u8fd9\u67c4\u5251\u90fd\u6765\u5f97\u592a\u5feb\u4e86\uff0c\u4eba\u4e0e\u5251\u4eff\u4f5b\u5df2\u5316\u4e3a\u4e00\u4f53\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u662f\u8fd9\u4e2a\u4eba\u98de\u8eab\u523a\u8fc7\u6765\u7684\uff1f\u6291\u6216\u8fd9\u4e2a\u4eba\u662f\u4e58\u7740\u8fd9\u4e00\u5251\u98de\u8fc7\u6765\u7684\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5206\u5f97\u51fa\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u770b\u6e05\u695a\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e2a\u4eba\u5927\u5bb6\u90fd\u5df2\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u4e00\u773c\u770b\u8fc7\u53bb\uff0c\u8fd9\u4e2a\u4eba\u5c31\u597d\u50cf\u662f\u5c11\u5e74\u65f6\u7684\u53f8\u9a6c\u8d85\u7fa4\uff0c\u82f1\u633a\uff0c\u9880\u957f\uff0c\u98ce\u795e\u79c0\u6717\uff0c\u6c14\u6982\u5a01\u6b66\uff0c \u7a7f\u4e00\u8eab\u526a\u88c1\u6781\u5408\u8eab\u3001\u8d28\u6599\u6781\u9ad8\u8d35\u3001\u8272\u5f69\u6781\u660e\u7684\u8863\u88f3\u3002\u53d1\u4eae\u7684\u773c\u775b\u4e2d\u5145\u6ee1\u81ea\u4fe1\u3002 \u3000\u3000\u4e00\u773c\u770b\u8fc7\u53bb\uff0c\u51e0\u4e4e\u6ca1\u6709\u4eba\u80fd\u8ba4\u5f97\u51fa\u4ed6\u5c31\u662f\u6614\u65e5\u90a3\u4e2a\u843d\u62d3\u6c5f\u6e56\u7684\u65e0\u540d\u5251\u5ba2\u9ad8\u6e10\u98de\u3002 \u4e8c \u3000\u3000\u4e50\u58f0\u5df2\u65ad\uff0c\u821e\u5df2\u505c\uff0c\u821e\u8005\u87cb\u4f0f\u5728\u5730\uff0c\u4eff\u4f5b\u518d\u4e5f\u4e0d\u6562\u62ac\u5934\u53bb\u770b\u8fd9\u79cd\u6740\u4eba\u6d41\u8840\u7684\u4e8b\u3002 \u3000\u3000\u5c0f\u9ad8\u62d4\u51fa\u4e86\u4ed6\u7684\u5251\uff0c\u79cb\u6c34\u822c\u7684\u957f\u5251\u4e0a\u6ca1\u6709\u4e00\u4e1d\u9c9c\u8840\uff0c\u53ea\u6709\u4e00\u70b9\u6cea\u75d5\u3002 \u3000\u3000\u516c\u5b59\u4e5e\u51e0\u5403\u60ca\u7684\u770b\u7740\u8fd9\u4e2a\u4eba\u548c\u8fd9\u67c4\u5251\uff0c\u638c\u4e2d\u7684\u957f\u68cd\u867d\u7136\u5df2\u6446\u51fa\u4e86\u957f\u67aa\u523a\u51fb\u4e4b\u52bf\uff0c\u5374\u5df2\u6ca1 \u6709\u52c7\u6c14\u523a\u51fa\u53bb\u3002 \u3000\u3000\u6731\u731b\u548c\u53f8\u9a6c\u8d85\u7fa4\u5c45\u7136\u8fd8\u75f4\u75f4\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u597d\u50cf\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u516c\u5b59\u4e5e\u513f\u5ffd\u7136\u5927\u559d\uff1a\u201c\u4eba\u5462\uff1f\u4f60\u4eec\u8fd9\u4e9b\u4eba\u96be\u9053\u90fd\u6b7b\u5149\u4e86\uff0c\u4e3a\u4ec0\u4e48\u90fd\u4e0d\u8fc7\u6765\uff1f\u201d \u3000\u3000\u5149\u5f71\u5916\uff0c\u4e00\u4e2a\u4eba\u7528\u4e00\u79cd\u5f88\u6e29\u548c\u7684\u58f0\u97f3\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u4f60\u8bf4\u5f97\u5bf9\uff0c\u4f60\u7684\u4eba\u7684\u786e\u90fd\u5df2\u6b7b\u5149\u4e86\uff0c \u63d0\u706f\u7684\u90fd\u5df2\u6362\u4e0a\u6211\u7684\u4eba\u3002\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u7740\u534e\u8863\u3001\u62e5\u8c82\u88d8\uff0c\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u65bd\u65bd\u7136\u81ea\u9ed1\u6697\u4e2d\u8d70\u4e86\u8fc7\u6765\uff1a\u8d70\u8def\u7684\u59ff\u6001\u5b89\u8be6\u800c\u4f18 \u96c5\uff0c\u6ca1\u6709\u4eba\u80fd\u770b\u5f97\u51fa\u4ed6\u4f1a\u662f\u4e2a\u8ddb\u8db3\u7684\u6b8b\u5e9f\u3002 \u3000\u3000\u516c\u5b59\u4e5e\u513f\u8138\u8272\u53d8\u4e86\uff1a\u201c\u5353\u4e1c\u6765\uff0c\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u662f\u6211\uff0c\u5f53\u7136\u662f\u6211\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u60a0\u7136\u9053\uff1a\u201c\u53ea\u6709\u6211\u624d\u4f1a\u7528\u4f60\u5bf9\u4ed8\u522b\u4eba\u7684\u6cd5\u5b50\u5bf9\u4ed8\u4f60\uff0c\u6731\u731b\u7684\u624b\u4e0b\u662f\u600e\u4e48\u6b7b\u7684\uff0c\u4f60 \u7684\u5c5e\u4e0b\u4e5f\u662f\u600e\u4e48\u6b7b\uff1b\u4f60\u8981\u600e\u4e48\u6837\u6740\u4eba\uff0c\u6211\u4e5f\u5c31\u600e\u4e48\u6740\u4f60\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\uff1a\u201c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u6211\u505a\u4e8b\u4e00\u5411\u516c\u5e73\u5f97\u5f88\u3002\u201d \u3000\u3000\u516c\u5b59\u4e5e\u513f\u8eab\u5b50\u5ffd\u7136\u5411\u524d\u6ed1\u51fa\uff0c\u957f\u68cd\u4ee5\u4e39\u51e4\u5f0f\u76f4\u523a\u5353\u4e1c\u6765\u7684\u7709\u76ee\u3002 \u3000\u3000\u957f\u68cd\u5411\u524d\u98de\u523a\u800c\u51fa\u65f6\uff0c\u68cd\u5df2\u79bb\u624b\uff0c\u4ed6\u7684\u4eba\u5df2\u5411\u540e\u7ffb\u8d77\uff0c\u51cc\u7a7a\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u5c31\u5df2\u5230\u4e86\u5149 \u5f71\u5916\uff0c\u773c\u770b\u5c31\u8981\u6ca1\u5165\u9ed1\u6697\u4e2d\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u53cd\u5e94\u4e4b\u7094\uff0c\u5e94\u53d8\u80fd\u529b\u4e4b\u5f3a\uff0c\u6b63\u662f\u4ed6\u4e00\u751f\u4e2d\u7ecf\u9a8c\u6b66\u529f\u548c\u667a\u6167\u7684\u7cbe\u534e\u7d2f\u79ef\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u8fd8\u662f\u6162\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u4e86\u7ffb\u8dc3\u65f6\uff0c\u5c31\u5df2\u770b\u5230\u6709\u4e00\u9053\u8000\u773c\u5251\u5149\u60ca\u8679\u822c\u98de\u8d77\uff0c\u5ffd\u7136\u95f4\u5c31\u5df2\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u68ee\u5bd2 \u7684\u5251\u5149\u523a\u5f97\u4ed6\u8fde\u773c\u775b\u90fd\u5f20\u4e0d\u5f00\u4e86\u3002 \u3000\u3000\u7b49\u5230\u4ed6\u80fd\u591f\u5f20\u5f00\u773c\u65f6\uff0c\u5df2\u7ecf\u770b\u4e0d\u5230\u8fd9\u9053\u5251\u5149\uff0c\u53ea\u770b\u89c1\u4e86\u4e00\u6bb5\u5251\u67c4\uff0c\u5c31\u50cf\u5ffd\u7136\u4ece\u4ed6\u8eab\u5b50\u91cc \u957f\u51fa\u6765\u7684\u4e00\u6837\uff0c\u957f\u5728\u4ed6\u7684\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u7684\u8eab\u5b50\u50cf\u77f3\u5757\u822c\u8dcc\u5728\u5730\u4e0a\u65f6\uff0c\u4ed6\u8fd8\u5728\u770b\u7740\u8fd9\u6bb5\u5251\u67c4\uff0c\u773c\u4e2d\u5145\u6ee1\u4e86\u60ca\u8bb6\u4e0e\u6050\u60e7\uff0c\u597d \u50cf\u8fd8\u4e0d\u660e\u767d\u4ed6\u81ea\u5df1\u7684\u80f8\u819b\u4e0a\u600e\u4e48\u4f1a\u5ffd\u7136\u591a\u51fa\u8fd9\u4e48\u6bb5\u5251\u67c4\u6765\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5df2\u7ecf\u77e5\u9053\u8fd9\u67c4\u5251\u7684\u5251\u950b\u5728\u54ea\u91cc\u4e86\u3002 \u3000\u3000\u5251\u950b\u5df2\u9f50\u6839\u6ca1\u5165\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u8131\u624b\u4e00\u5251\uff0c\u4e00\u5251\u81f4\u547d\u3002 \u3000\u3000\u201c\u597d\u5feb\u7684\u5251\uff0c\u597d\u5feb\u7684\u51fa\u624b\uff01\u201d\u5353\u4e1c\u6765\u5411\u5c0f\u9ad8\u8eac\u8eab\u793a\u656c\uff1a\u201c\u5c31\u53ea\u51ed\u8fd9\u4e00\u5251\u4e4b\u5a01\uff0c\u5df2\u7ecf\u8db3\u591f \u7edf\u9886\u5927\u9556\u5c40\u4e86\u3002\u201d \u3000\u3000\u201c\u7edf\u9886\u5927\u9556\u5c40\uff1f\u201d \u3000\u3000\u6731\u731b\u4eff\u4f5b\u5ffd\u7136\u81ea\u68a6\u4e2d\u60ca\u9192\uff0c\u6162\u6162\u7684\u8f6c\u8fc7\u8eab\uff0c\u7528\u4e00\u53cc\u76ee\u7736\u4f3c\u5df2\u5c06\u88c2\u7684\u5927\u773c\u770b\u7740\u5c0f\u9ad8\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u5df2\u7ecf\u7edf\u9886\u4e86\u5927\u9556\u5c40\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u6c89\u9ed8\u3002 \u3000\u3000\u201c\u597d\uff0c\u597d\u4e00\u4e2a\u9ad8\u6e10\u98de\u3002\u201d\u6731\u731b\u5927\u7b11\uff1a\u201c\u73b0\u5728\u4f60\u679c\u7136\u5df2\u6e10\u6e10\u98de\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u7b11\u58f0\u5982\u88c2\u5e1b\u3002 \u3000\u3000\u201c\u4f60\u82e5\u662f\u6765\u53d6\u6211\u9888\u4e0a\u8fd9\u9897\u5934\u9885\u7684\uff0c\u4f60\u53ea\u7ba1\u62ff\u53bb\u3002\u201d\u6731\u731b\u5636\u58f0\u800c\u7b11\uff1a\u201c\u6211\u65e9\u5c31\u60f3\u628a\u5b83\u9001\u7ed9 \u4eba\u4e86\uff0c\u9001\u7ed9\u4f60\u603b\u6bd4\u9001\u7ed9\u522b\u4eba\u597d\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u6ca1\u6709\u7b11\uff0c\u4e5f\u6ca1\u6709\u53cd\u5e94\uff0c\u5c31\u5728\u8fd9\u77ed\u77ed\u6570\u65e5\u4e4b\u95f4\uff0c\u4ed6\u5c31\u5df2\u5c06\u81ea\u5df1\u8bad\u7ec3\u6210\u4e00\u4e2a\u5ca9\u77f3\u822c\u7684 \u4eba\uff0c\u751a\u81f3\u8fde\u8138\u4e0a\u90fd\u6ca1\u6709\u4e1d\u6beb\u8868\u60c5\u3002 \u3000\u3000\u6731\u731b\u5927\u559d\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8fc7\u6765\uff0c\u8fd8\u5728\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u6025\uff0c\u4f60\u4f55\u5fc5\u6025\uff1f\u201d\u5c0f\u9ad8\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u6211\u613f\u610f\u7b49\uff0c\u4f60\u4e5f\u5e94\u8be5\u53ef\u4ee5\u7b49\u7684\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\u9762\u5bf9\u53f8\u9a6c\u8d85\u7fa4\uff0c\u201c\u4f60\u5f53\u7136\u66f4\u5e94\u8be5\u77e5\u9053\u6211\u5728\u7b49\u4ec0\u4e48\u3002\u201d \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u53f8\u9a6c\u624d\u6162\u6162\u7684\u62ac\u8d77\u5934\uff0c\u5c31\u597d\u50cf\u7b2c\u4e00\u6b21\u770b\u5230\u8fd9\u4e2a\u4eba\u4e00\u6837\u3002\u5c31\u597d\u50cf\u5df2\u7ecf\u5c06\u8fc7\u53bb\u6240 \u6709\u7684\u4eba\u548c\u4e8b\u90fd\u5df2\u5b8c\u5168\u5fd8\u8bb0\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u58f0\u97f3\u95ee\u5c0f\u9ad8\u3002 \u3000\u3000\u201c\u4f60\u5728\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7b49\u7740\u7b97\u4f60\u6211\u4e4b\u95f4\u7684\u4e00\u7b14\u65e7\u8d26\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u5f88\u597d\u3002\u201d\u53f8\u9a6c\u6b67\u7fa4\u7684\u58f0\u97f3\u4e2d\u7adf\u4f3c\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u60b2\u4f24\uff1a\u201c\u73b0\u5728\u7684\u786e\u5df2\u7ecf\u5230\u4e86\u6838\u7b97 \u8d26\u7684\u65f6\u5019\uff0c\u4eba\u6b20\u6211\u7684\uff0c\u6211\u6b20\u4eba\u7684\uff0c\u73b0\u5728\u90fd\u8be5\u7b97\u6e05\u4e86\u3002\u201d \u3000\u3000\u201c\u4ee5\u4f60\u73b0\u5728\u7684\u60c5\u51b5\uff0c\u6211\u672c\u4e0d\u8be5\u903c\u4f60\u51fa\u624b\u3002\u201d\u9ad8\u6e10\u98de\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u4e0a\u6b21\u4f60\u51fb\u8d25\u6211\u65f6\uff0c \u6211\u7684\u60c5\u51b5\u4e5f\u5e76\u4e0d\u6bd4\u4f60\u73b0\u5728\u597d\u591a\u5c11\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u5c45\u7136\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u201c\u6211\u6839\u672c\u6ca1\u6709\u602a\u4f60\uff0c\u4f60\u53c8\u4f55\u5fc5\u8bf4\u5f97\u5927\u591a\uff1f\u201d \u3000\u3000\u201c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u6731\u731b\u5ffd\u7136\u53c8\u5927\u559d\uff0c\u201c\u96be\u9053\u4f60\u73b0\u5728\u5c31\u5df2\u5fd8\u4e86\u4f60\u6211\u4e4b\u7ea6\uff1f\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u6c89\u4e0b\u4e86\u8138\u3002 \u3000\u3000\u201c\u4f60\u6700\u597d\u8d70\u8fdc\u4e9b\uff0c\u8fd9\u662f\u6211\u8ddf\u9ad8\u6e10\u98de\u4e24\u4e2a\u4eba\u7684\u4e8b\uff0c\u8c01\u8981\u6765\u63d2\u624b\uff0c\u6211\u552f\u6709\u4e00\u6b7b\u800c\u5df2\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8f7b\u8f7b\u7684\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u82f1\u96c4\u867d\u7136\u5df2\u5230\u672b\u8def\uff0c\u6bd5\u7adf\u8fd8\u662f\u82f1\u96c4\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6731\u5802\u4e3b\uff0c\u4f60\u4e5f\u662f\u4e00\u4e16\u4e4b\u82f1\u96c4\uff0c\u4f60\u4e5f\u5e94 \u8be5\u77e5\u9053\u4ed6\u7684\u60f3\u6cd5\uff0c\u4e3a\u4ec0\u4e48\u8981\u8ba9\u4ed6\u4e00\u4e16\u82f1\u540d\u626b\u5730\uff1f\u201d \u3000\u3000\u4ed6\u8fde\u770b\u90fd\u4e0d\u518d\u770b\u6731\u731b\u4e00\u773c\uff0c\u8d70\u8fc7\u53bb\u62d4\u8d77\u4e86\u516c\u5b59\u4e5e\u513f\u80f8\u819b\u4e0a\u7684\u5251\u3002 \u3000\u3000\u5251\u4e0a\u8fd8\u662f\u6ca1\u6709\u8840\uff0c\u53ea\u6709\u4e00\u70b9\u6cea\u75d5\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4ee5\u5de6\u624b\u7684\u62c7\u6307\u4e0e\u98df\u6307\u634f\u4f4f\u5251\u5c16\uff0c\u5c06\u5251\u67c4\u5f80\u9ad8\u6e10\u98de\u9762\u524d\u9001\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4f60\u7684\u5251\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5e76\u6ca1\u6709\u4f38\u624b\u53bb\u63a5\u5251\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u8fd9\u662f\u6211\u7684\u5251\uff0c\u4f46\u662f\u6211\u4e5f\u77e5\u9053\u4ed6\u6ca1\u6709\u5251\u3002\u201d \u3000\u3000\u201c\u4ed6\u6ca1\u6709\uff0c\u4f60\u6709\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7b11\u4e86\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ed6\u6ca1\u6709\uff0c\u6211\u6709\uff0c\u73b0\u5728\u7684\u60c5\u51b5\u597d\u50cf\u5c31\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u539f\u6765\u5c31\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4f60\u7684\u610f\u601d\u6211\u5df2\u7ecf\u5b8c\u5168\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u4f38\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u7ec8\u4e8e\u63e1\u4f4f\u4e86\u4ed6\u7684\u5251\u67c4\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u8138\u4e0a\u7684\u7b11\u5bb9\u5ffd\u7136\u6d88\u5931\uff0c\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u6740\u673a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\u4ed6\u5df2\u5c06\u8fd9\u67c4\u5251\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5251\u5c16\u8ddd\u79bb\u5353\u4e1c\u6765\u7684\u80f8\u819b\u7edd\u4e0d\u4f1a\u8d85\u8fc7\u4e00\u5c3a\uff0c\u5251\u5c16\u672c\u6765\u5c31\u5bf9\u51c6\u4e86\u4ed6\u81ea\u5df1\u7684\u5fc3\u810f\u3002\u4ed6\u5c45\u7136\u53ea\u7528 \u4e24\u6839\u624b\u6307\u634f\u4f4f\uff0c\u5c45\u7136\u5c06\u5251\u67c4\u4ea4\u7ed9\u4e86\u522b\u4eba\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u72af\u8fd9\u79cd\u9519\uff0c\u72af\u4e86\u8fd9\u79cd\u9519\u7684\u4eba\u5fc5\u5b9a\u90fd\u5df2\u6b7b\u5728\u522b\u4eba\u5251\u4e0b\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4e5f\u4e0d\u80fd\u4f8b\u5916\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u5382\uff0c\u4ed6\u6839\u672c\u5df2\u5b8c\u5168\u6ca1\u6709\u9632\u907f\u62db\u67b6\u7684\u4f59\u5730\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u4e00\u76f4\u5728\u7b49\uff0c\u7b49\u7684\u5c31\u662f\u8fd9\u4e48\u6837\u4e00\u4e2a\u673a\u4f1a\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u4e00\u76f4\u5728\u76ef\u7740\u5353\u4e1c\u6765\u7684\u8138\u3002\u56e0\u4e3a\u4ed6\u505a\u7684\u6bcf\u4e00\u4ef6\u4e8b\u90fd\u662f\u4e3a\u4e86\u5728\u7b49\u8fd9\u4e00\u5239\u90a3\u3002 \u3000\u3000\u5251\u950b\u523a\u5165\u5353\u4e1c\u6765\u5fc3\u810f\u65f6\u7684\u4e00\u5239\u90a3\u3002 \u3000\u3000\u2014\u2014\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u7684\u8138\u4e0a\u4f1a\u6709\u4ec0\u4e48\u6837\u7684\u8868\u60c5\uff1f \u3000\u3000\u5353\u4e1c\u6765\u7684\u8138\u4e0a\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u56e0\u4e3a\u6bcf\u4e00\u4ef6\u4e8b\u90fd\u5728\u4ed6\u9884\u6599\u4e4b\u4e2d\uff0c\u8fd9\u4e00\u5251\u523a\u6765\u65f6\uff0c\u4ed6\u7684\u8eab\u5b50\u5df2\u968f\u9996\u540e\u9000\u3002 \u3000\u3000\u5251\u52bf\u4e0d\u505c\uff0c\u518d\u5f80\u524d\u523a\u3002 \u3000\u3000\u4ed6\u518d\u5f80\u540e\u9000\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u5df2\u7528\u5c3d\u5168\u529b\uff0c\u4f59\u529b\u7ef5\u7ef5\u4e0d\u5730\u3002 \u3000\u3000\u4ed6\u518d\u9000\u3002 \u3000\u3000\u5251\u5c16\u8fd8\u662f\u88ab\u4ed6\u7528\u4e24\u6839\u624b\u6307\u634f\u4f4f\uff0c\u8fd8\u662f\u548c\u4ed6\u7684\u80f8\u819b\u4fdd\u6301\u7740\u540c\u6837\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u5c0f\u9ad8\u505c\u4e0b\u3002 \u3000\u3000\u4ed6\u505c\u4e0b\u6765\u65f6\u8863\u88f3\u5df2\u6e7f\u900f\u3002 \u3000\u3000\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u7528\u4e00\u79cd\u65e2\u6e29\u548c\u53c8\u51b7\u6de1\u7684\u58f0\u97f3\u5bf9\u4ed6\u8bf4\uff1a\u201c\u8fd9\u4e00\u6b21\u5b9e\u5728\u8f9b\u82e6\u4e86\u4f60\u3002\u201d \u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e3a\u4e86\u8981\u7b49\u8fd9\u4e48\u6837\u4e00\u4e2a\u673a\u4f1a\uff0c\u4f60\u7684\u786e\u8d39\u4e86\u5f88\u591a\u5fc3\u673a\uff0c\u51fa\u4e86\u5f88\u591a\u529b\uff0c\u4f60\u5b9e\u5728\u5df2\u7ecf\u505a \u5f97\u5f88\u597d\u4e86\uff0c\u6211\u5b9e\u5728\u5e94\u8be5\u8ba9\u4f60\u6740\u4e86\u6211\u7684\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4e2d\u4e95\u6ca1\u6709\u4ec0\u4e48\u8ba5\u8bee\u4e4b\u610f\uff0c\u56e0\u4e3a\u4ed6\u8bf4\u7684\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4ef6\u4e8b\u5b9e\u800c\u5df2\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u4e00\u5b9a\u8981\u4f60\u77e5\u9053\uff0c\u8981\u6740\u6211\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\uff0c\u6211\u4e0d\u80fd\u8ba9\u4f60\u5f97\u4e4b\u5927 \u6613\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4f55\u51b5\u4f60\u5c31\u7b97\u6740\u4e86\u6211\u4e5f\u6ca1\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u9ad8\u6e10\u98de\u4e00\u76f4\u5728\u542c\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u542c\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u6bcf\u4e2a\u4eba\u90fd\u53ea\u6709\u542c\u5353\u4e1c\u6765\u4e00\u4e2a\u4eba\u8bf4\uff0c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u522b\u4eba\u80fd\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u5ffd\u7136\u8bf4\u51fa\u4e00\u53e5\u8bdd\uff0c\u8ba9\u6bcf\u4e2a\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u201c\u5982\u679c\u4f60\u6740\u4e86\u6211\uff0c\u4f60\u4e5f\u6b7b\u5b9a\u4e86\u3002\u201d\u5353\u4e1c\u6765\u5bf9\u5c0f\u9ad8\u8bf4\uff1a\u201c\u5982\u679c\u4f60\u90a3\u4e00\u5251\u771f\u523a\u5165\u4e86\u6211\u80f8\u819b\uff0c \u5c31\u5728\u90a3\u4e00\u77ac\u95f4\uff0c\u4f60\u4e5f\u5fc5\u6b7b\u65e0\u7591\uff0c\u800c\u4e14\u5f88\u53ef\u80fd\u6bd4\u6211\u6b7b\u5f97\u8fd8\u5feb\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u4e00\u5411\u662f\u4e2a\u5f88\u5c11\u8bf4\u8c0e\u7684\u4eba\uff0c\u53ef\u662f\u8fd9\u4e00\u6b21\u4ed6\u8bf4\u7684\u8bdd\u5374\u5b9e\u5728\u5f88\u96be\u8ba9\u4eba\u76f8\u4fe1\u3002 \u3000\u3000\u5c0f\u9ad8\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8bf4\u5982\u679c\u6211\u90a3\u4e00\u5251\u523a\u6740\u4e86\u4f60\uff0c\u6211\u6b7b\u5f97\u53cd\u800c\u4f1a\u6bd4\u4f60\u8fd8\u5feb\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u4e16\u4e0a\u6700\u5c11\u6709\u4e94\u79cd\u6697\u5668\u662f\u7684\u786e\u80fd\u89c1\u8840\u5c01\u5589\uff0c\u80fd\u591f\u5728\u4e00\u77ac\u95f4\u5c31\u81f4\u4eba\u4e8e\u6b7b\u3002\u201d\u5353 \u4e1c\u6765\u8bf4\uff1a\u201c\u6c5f\u6e56\u4e2d\u6700\u5c11\u6709\u4e09\u4e2a\u4eba\u4f1a\u4f7f\u7528\u8fd9\u4e00\u7c7b\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u6700\u91cd\u8981\u7684\u4e00\u70b9\u662f\uff0c\u6211\u4e5f\u77e5\u9053\u8fd9\u4e09\u4e2a\u4eba\u4e4b\u4e2d\u5df2\u7ecf\u6709\u4e00\u4e2a\u4eba\u5230\u4e86\u8fd9\u91cc\uff0c\u5df2\u7ecf\u7528\u90a3\u4e94\u79cd\u6697\u5668 \u4e4b\u4e2d\u7684\u4e00\u79cd\u5bf9\u51c6\u4e86\u4f60\u7684\u80cc\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5982\u679c\u4f60\u90a3\u4e00\u5251\u523a\u4e86\u6211\u80f8\u819b\uff0c\u90a3\u65f6\u4e00\u5b9a\u4f1a\u9ad8\u5174\u6781\u4e86\uff0c\u5f97\u610f\u6781\u4e86\uff0c\u65e0\u8bba\u8c01\u5728\u90a3 \u79cd\u65f6\u5019\u90fd\u96be\u514d\u4f1a\u758f\u5ffd\u5927\u610f\u7684\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u4f8b\u5916\u3002\u201d \u3000\u3000\u8fd9\u65e0\u7591\u4e5f\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u201c\u5c31\u5728\u4f60\u6700\u9ad8\u5174\u6700\u5f97\u610f\u7684\u65f6\u5019\uff0c\u4f60\u5c31\u4f1a\u5ffd\u7136\u89c9\u5f97\u540e\u80cc\u4e0a\u597d\u50cf\u88ab\u866b\u5b50\u54ac\u4e86\u4e00\u53e3\uff0c\u201d\u5353\u4e1c\u6765 \u8bf4\uff0c\u201c\u4f60\u5c31\u4f1a\u5ffd\u7136\u5012\u4e86\u4e0b\u53bb\uff0c\u4f60\u5012\u4e0b\u53bb\u65f6\u5fc3\u8df3\u5c31\u5df2\u505c\u6b62\uff0c\u90a3\u65f6\u5019\u6211\u5927\u6982\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u80cc\u4e0a\u5df2\u7ecf\u5728\u6d41\u51b7\u6c57\u3002 \u3000\u3000\u5353\u4e1c\u6765\u60a0\u7136\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u4f60\u5df2\u7ecf\u53ef\u4ee5\u653e\u5fc3\u4e86\uff0c\u56e0\u4e3a\u73b0\u5728\u6211\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4ed6\u5927\u6982\u6682\u65f6\u8fd8\u4e0d \u6562\u51fa\u624b\uff0c\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u4e5f\u8ddf\u6211\u4eec\u4e00\u6837\uff0c\u4e00\u5411\u4e0d\u592a\u613f\u610f\u505a\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u8981\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u5c31\u5f97\u5148\u60f3\u901a\u4e09\u4ef6\u4e8b\u3002\u201d\u5353\u4e1c\u6765\u5bf9\u5c0f\u9ad8\u8bf4\u3002 \u3000\u3000\u201c\u4e09\u4ef6\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u516c\u5b59\u5144\u5f1f\u600e\u4e48\u80fd\u672a\u535c\u5148\u77e5\uff0c\u5728\u4e94\u5929\u524d\u5c31\u5df2\u77e5\u9053\u5927\u9556\u5c40\u91cc\u8981\u53d1\u751f\u8fd9\u4e48\u91cd\u5927\u7684\u53d8 \u5316\uff0c\u53ca\u65f6\u8d76\u6765\u8fd9\u91cc\uff1f\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u7b2c\u4e8c\uff0c\u8fd9\u4f4d\u4ee5\u8f7b\u7eb1\u8499\u9762\u7684\u821e\u8005\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u53f8\u9a6c\u8d85\u7fa4 \u672c\u6765\u8981\u4e3a\u6731\u731b\u6740\u4e86\u5979\uff0c\u4e3a\u4ec0\u4e48\u542c\u5979\u8bf4\u4e86\u4e24\u4e2a\u5b57\u5c31\u9000\u4e86\u4e0b\u53bb\uff1f\u800c\u4e14\u597d\u50cf\u53d8\u4e86\u4e00\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u60f3\u4e0d\u901a\uff0c\u4e24\u4ef6\u4e8b\u90fd\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u5353\u4e1c\u6765\u53c8\u70b9\u9192\u4ed6\uff1a\u201c\u5176\u5b9e\u8fd9\u4e24\u4ef6\u4e8b\u4e5f\u53ef\u4ee5\u7b97\u505a\u4e00\u4ef6\u4e8b\uff01\u5c31\u597d\u50cf\u4e00\u95f4\u5c4b\u5b50\u867d\u7136\u6709\u4e24\u4e2a\u95e8\uff0c \u53ef\u662f\u53ea\u8981\u7528\u4e00\u628a\u94a5\u5319\u5c31\u53ef\u4ee5\u6253\u5f00\u4e86\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u82e6\u7b11\uff0c\u201c\u53ef\u60dc\u6211\u6ca1\u6709\u8fd9\u628a\u94a5\u5319\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u8981\u5230\u54ea\u91cc\u53bb\u627e\u3002\u201d \u3000\u3000\u201c\u94a5\u5319\u901a\u5e38\u90fd\u5728\u6d3b\u4eba\u8eab\u4e0a\uff0c\u4eba\u6b7b\u4e86\uff0c\u5c31\u7528\u4e0d\u7740\u5e26\u94a5\u5319\u4e86\u3002\u201d\u5353\u4e1c\u6765\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u4f60 \u8981\u627e\u8fd9\u628a\u94a5\u5319\uff0c\u5374\u4e0d\u59a8\u5230\u6b7b\u4eba\u8eab\u4e0a\u53bb\u627e\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u6b7b\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u516c\u5b59\u5144\u5f1f\u65e2\u4e0d\u80fd\u672a\u535c\u5148\u77e5\uff0c\u4ed6\u4eec\u80fd\u53ca\u65f6\u8d76\u6765\uff0c\u5f53\u7136\u662f\u6709\u4eba\u8981\u4ed6\u4eec\u6765\u7684\u3002\u201d\u5353\u4e1c\u6765\u95ee\uff1a \u201c\u53ef\u662f\u53c8\u6709\u4ec0\u4e48\u4eba\u80fd\u5728\u4e94\u5929\u4e4b\u524d\u5c31\u5df2\u7b97\u51c6\u6211\u4e0e\u53f8\u9a6c\u4e09\u5341\u5e74\u7684\u4ea4\u60c5\u4f1a\u6bc1\u4e8e\u4e00\u77ac\u4e4b\u95f4\u5462\uff1f\u201d \u3000\u3000\u4ed6\u81ea\u5df1\u56de\u7b54\u4e86\u8fd9\u4e2a\u95ee\u9898\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u4eba\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u4e0e\u53f8\u9a6c\u53cd\u76ee\uff0c\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e2a \u4eba\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u662f\u4e2a\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u672c\u6765\u5e94\u8be5\u662f\u4e2a\u6b7b\u4eba\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5979\u77e5\u9053\u5979\u6b7b\u4e86\u4e4b\u540e\u53f8\u9a6c\u4e00\u5b9a\u4e0d\u4f1a\u653e\u8fc7 \u6211\uff0c\u56fa\u4e3a\u5979\u6d3b\u7740\u7684\u65f6\u5019\u5c31\u5df2\u7ecf\u5728\u6211\u4eec\u4e4b\u95f4\u6446\u4e0b\u4e86\u4e00\u5427\u6bd2\u5200\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u7684\u773c\u775b\u91cc\u5ffd\u7136\u95f4\u51fa\u4e86\u5149\uff0c\u5ffd\u7136\u95ee\u5353\u4e1c\u6765\uff1a \u3000\u3000\u201c\u4e00\u4e2a\u5973\u4eba\u96be\u9053\u80fd\u628a\u53e6\u5916\u4e00\u4e2a\u5973\u4eba\u626e\u6210\u5979\u81ea\u5df1\uff0c\u96be\u9053\u80fd\u7792\u5f97\u8fc7\u5979\u81ea\u5df1\u7684\u4e08\u592b\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u5979\u6d3b\u7740\uff0c\u5f53\u7136\u7792\u4e0d\u8fc7\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201d\u53ef\u662f\u5982\u679c\u5979\u5df2\u6b7b\u4e86\u51e0\u5929\uff0c\u60c5\u51b5\u5c31\u4e0d\u540c \u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u4e00\u4e2a\u4eba\u6b7b\u4e86\u51e0\u5929\u4e4b\u540e\uff0c\u808c\u8089\u5df2\u626d\u66f2\u50f5\u786c\uff0c\u5bb9\u8c8c\u672c\u6765\u5c31\u4f1a\u6539\u53d8\uff0c\u5982\u679c\u5979\u662f\u88ab\u540a\u6b7b \u7684\uff0c\u6539\u53d8\u5f97\u5f53\u7136\u66f4\u591a\uff0c\u66f4\u53ef\u6015\uff0c\u65e0\u8bba\u4ec0\u4e48\u4eba\u90fd\u4f1a\u88ab\u5979\u7792\u8fc7\u53bb\u7684\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u4e00\u4e2a\u4eba\u56de\u5bb6\u65f6\u5982\u679c\u9aa4\u7136\u53d1\u73b0\u81ea\u5df1\u7684\u59bb\u5b50\u513f\u5973\u90fd\u5df2\u60e8\u6b7b\uff0c\u65e0\u8bba\u5bf9\u4ec0\u4e48\u4e8b \u5927\u6982\u90fd\u4e0d\u4f1a\u770b\u5f97\u592a\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u95ee\uff1a\u201c\u5982\u679c\u4ed6\u5ffd\u7136\u53c8\u53d1\u73b0\u4ed6\u7684\u59bb\u5b50\u5e76\u6ca1\u6709\u6b7b\uff0c\u4ed6\u4f1a\u53d8\u5f97\u600e\u4e48 \u6837\uff1f\u201d \u3000\u3000\u201c\u8fd9\u65f6\u5019\u4ed6\u5927\u6982\u5c31\u4f1a\u5ffd\u7136\u53d8\u5f97\u597d\u50cf\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u53c8\u957f\u58f0\u53f9\u606f\uff1a\u201c\u8fd9\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u4e00\u4e2a\u5973\u4eba\u600e\u4e48\u80fd\u72e0\u5f97\u4e0b\u8fd9\u79cd\u5fc3\uff0c\u600e\u4e48\u80fd\u505a\u5f97 \u51fa\u8fd9\u79cd\u4e8b\u60c5\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u672c\u6765\u5c31\u6709\u79cd\u4eba\u662f\u4ec0\u4e48\u4e8b\u90fd\u505a\u5f97\u51fa\u7684\uff0c\u4e0d\u7ba1\u4ed6\u662f\u7537\u662f\u5973\u90fd\u4e00\u6837\uff0c\u201d\u5353\u4e1c\u6765 \u8bf4\uff1a\u201c\u4f60\u60f3\u4e0d\u901a\uff0c\u53ea\u56e0\u4e3a\u4f60\u4e0d\u662f\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u5c0f\u9ad8\u95ee\u5353\u4e1c\u6765\uff0c\u201c\u4f60\u662f\u4e0d\u662f\u8fd9\u79cd\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u3002\u201d \u4e09 \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u60e8\u767d\u7684\u8138\u4e0a\u5df2\u5168\u65e0\u8840\u8272\uff0c\u8fde\u6731\u731b\u770b\u4e86\u90fd\u4e3a\u4ed6\u96be\u53d7\u5f97\u8981\u547d\u3002 \u3000\u3000\u90a3\u9500\u9b42\u7684\u821e\u8005\u5374\u4ecd\u4f0f\u5728\u5730\u4e0a\uff0c\u5c31\u597d\u50cf\u6839\u672c\u6ca1\u6709\u542c\u89c1\u5353\u4e1c\u6765\u5728\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u5353\u4e1c\u6765\u51b7\u51b7\u7684\u770b\u7740\u5979\uff1a\u201c\u5176\u5b9e\u6211\u5e76\u4e0d\u602a\u4f60\uff0c\u56e0\u4e3a\u6211\u4eec\u672c\u6765\u5c31\u662f\u540c\u4e00\u79cd\u4eba\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a \u201c\u4f60\u5f53\u7136\u4e5f\u65e9\u5df2\u770b\u51fa\u6765\uff0c\u5927\u9556\u5c40\u6709\u4e09\u4e2a\u4eba\u4e00\u76f4\u548c\u6211\u4e0d\u5bf9\u7684\uff0c\u4e5f\u53ea\u6709\u4ed6\u4eec\u4e09\u4e2a\u4eba\u80fd\u5bf9\u4ed8\u6211\uff0c\u6240 \u4ee5\u4f60\u65e9\u5c31\u5728\u6697\u4e2d\u548c\u4ed6\u4eec\u6697\u901a\u58f0\u606f\uff0c\u6240\u4ee5\u73b0\u5728\u4f60\u624d\u80fd\u628a\u4ed6\u4eec\u53ca\u65f6\u627e\u6765\u3002\u201d \u3000\u3000\u821e\u8005\u65e0\u8bed\u3002 \u3000\u3000\u201c\u4f60\u8fd9\u4e48\u6837\u505a\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u4fdd\u62a4\u4f60\u81ea\u5df1\u800c\u5df2\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u6211\u672c\u6765\u7edd\u5bf9\u4e0d\u4f1a\u56e0\u6b64\u800c \u5bf9\u4f60\u4e0b\u6bd2\u624b\u7684\uff0c\u53ea\u53ef\u60dc\u4f60\u8d70\u9519\u4e86\u4e00\u6b65\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u7ade\u5ffd\u7136\u53c8\u53d8\u4e86\uff0c\u53c8\u7528\u4ed6\u90a3\u79cd\u72ec\u7279\u7684\u8bed\u8c03\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u4e0d\u7ba1\u4f60\u4e3a\u4ec0\u4e48\uff0c \u4f60\u90fd\u4e0d\u5e94\u8be5\u8fd9\u4e48\u6837\u5bf9\u53f8\u9a6c\u8d85\u7fa4\u3002\u201d \u3000\u3000\u4ece\u5916\u8868\u770b\u8d77\u6765\uff0c\u5353\u4e1c\u6765\u5e76\u4e0d\u662f\u4e00\u4e2a\u51f6\u66b4\u6076\u6bd2\u7684\u4eba\uff0c\u53ef\u662f\u6bcf\u5f53\u4ed6\u7528\u8fd9\u79cd\u53e3\u6c14\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c \u65e0\u8bba\u8c01\u542c\u89c1\u90fd\u4f1a\u89c9\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u4e0d\u5bd2\u800c\u7c9f\u3002 \u3000\u3000\u6700\u4e86\u89e3\u4ed6\u7684\u5f53\u7136\u8fd8\u662f\u53f8\u9a6c\u8d85\u7fa4\u3002 \u3000\u3000\u6bcf\u6b21\u4ed6\u542c\u89c1\u4ed6\u7528\u8fd9\u79cd\u53e3\u6c14\u5bf9\u4e00\u4e2a\u4eba\u8bf4\u8bdd\u65f6\uff0c\u90a3\u4e2a\u4eba\u5c31\u7b49\u4e8e\u5df2\u7ecf\u88ab\u5224\u4e86\u6b7b\u5211\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u80fd\u52a8\u5979\u3002\u201d \u3000\u3000\u53f8\u9a6c\u5ffd\u7136\u7eb5\u8eab\u4e00\u63a0\uff0c\u7528\u81ea\u5df1\u7684\u8eab\u5b50\u6321\u5728\u90a3\u795e\u79d8\u7684\u821e\u8005\u4e4b\u524d\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4e0d\u7ba1\u5979\u505a\u4e86\u4ec0 \u4e48\uff0c\u6211\u90fd\u4e0d\u602a\u5979\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4e00\u76f4\u662f\u6211\u5bf9\u4e0d\u8d77\u5979\uff0c\u5c31\u7b97\u6211\u6b7b\u5728\u5979\u624b\u91cc\uff0c\u6211\u4e5f\u4e0d\u8bb8\u4f60\u52a8\u5979\u6beb \u53d1\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u8138\u8272\u5ffd\u7136\u53d8\u4e86\uff0c\u77b3\u5b54\u5ffd\u7136\u6536\u7f29\uff0c\u5ffd\u7136\u5927\u543c\uff1a\u201c\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u7684\u8b66\u544a\u8fd8\u662f\u8fdf\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u5730\u4e0a\u7684\u821e\u8005\u5df2\u8dc3\u8d77\uff0c\u5389\u58f0\u800c\u547c\uff1a\u201c\u4f60\u8981\u6b7b\uff0c\u4f60\u5c31\u53bb\u6b7b\u5427\u3002\u201d \u3000\u3000\u547c\u58f0\u4e2d\uff0c\u4e09\u70b9\u5bd2\u661f\u66b4\u5c04\u800c\u51fa\uff0c\u98de\u51fb\u53f8\u9a6c\u7684\u80cc\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7528\u5de6\u811a\u52fe\u5012\u53f8\u9a6c\uff0c\u4ee5\u53f3\u638c\u6a2a\u5207\u5c0f\u9ad8\u7684\u8f6f\u80c1\uff0c\u5c0f\u9ad8\u64a4\u5251\u67c4\uff0c\u5353\u4e1c\u6765\u7528\u4e00\u76f4\u634f\u4f4f\u5251\u5c16 \u7684\u5de6\u624b\u5c06\u957f\u5251\u4e00\u5e26\uff0c\u5251\u67c4\u5df2\u5230\u4e86\u4ed6\u53f3\u624b\u91cc\u3002 \u3000\u3000\u8fd9\u51e0\u4e2a\u52a8\u4f5c\u51e0\u4e4e\u90fd\u662f\u5728\u540c\u4e00\u5239\u90a3\u95f4\u5b8c\u6210\u7684\uff0c\u5feb\u5f97\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u53c8\u8fdf\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u53f8\u9a6c\u7684\u8eab\u5b50\u867d\u7136\u88ab\u52fe\u5012\uff0c\u4e09\u4ef6\u6697\u5668\u4e2d\u867d\u7136\u6709\u4e24\u4ef6\u6253\u6b6a\u4e86\uff0c\u5176\u4e2d\u8fd8\u662f\u6709\u4e00\u4ef6\u6253\u5165\u4e86\u4ed6\u5de6\u80a9 \u4e0b\u7684\u81c2\u3002 \u3000\u3000\u5353\u4e1c\u6765\u8fde\u8003\u8651\u90fd\u6ca1\u6709\u8003\u8651\uff0c\u6325\u624b\u4e00\u5251\u524a\u51fa\uff0c\u5251\u5149\u4e00\u95ea\u95f4\uff0c\u5df2\u7ecf\u5c06\u53f8\u9a6c\u8fd9\u6761\u624b\u81c2\u8fde\u80a9\u524a\u4e86 \u4e0b\u672a\u3002 \u3000\u3000\u876e\u86c7\u566c\u624b\uff0c\u58ee\u58eb\u65ad\u8155\u3002 \u3000\u3000\u5c0f\u9ad8\u4e5f\u77e5\u9053\u6697\u5668\u4e2d\u5fc5\u6709\u5267\u6bd2\uff0c\u8981\u963b\u6b62\u6bd2\u6027\u8513\u5ef6\uff0c\u8981\u6551\u53f8\u9a6c\u7684\u547d\uff0c\u8fd9\u662f\u552f\u4e00\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u8fd8\u662f\u8981\u95ee\u81ea\u5df1\uff0c\u4e00\u5982\u679c\u4ed6\u662f\u5353\u4e1c\u6765\uff0c\u80fd\u4e0d\u80fd\u5728\u8fd9\u4e00\u77ac\u95f4\u4e0b\u5f97\u4e86\u8fd9\u79cd\u51b3\u65ad\uff0c\u662f\u4e0d\u662f \u80fd\u4e0b\u5f97\u4e86\u624b\uff1f \u3000\u3000\u5251\u98ce\u8361\u8d77\u4e86\u821e\u8005\u8499\u9762\u7684\u8f7b\u7eb1\uff0c\u9732\u51fa\u4e86\u5979\u7684\u8138\u3002 \u3000\u3000\u5434\u5a49\u3002 \u3000\u3000\u8fd9\u4e2a\u795e\u79d8\u7684\u821e\u8005\u679c\u7136\u662f\u5434\u5a49\u3002 \u56db \u3000\u3000\u65ad\u81c2\u843d\u4e0b\uff0c\u9c9c\u8840\u98de\u6e85\uff0c\u53f8\u9a6c\u8d85\u7fa4\u7684\u8eab\u5b50\u5374\u4ecd\u5982\u6807\u67aa\u822c\u7ad9\u5728\u90a3\u91cc\uff0c\u5c79\u7acb\u4e0d\u5012\u3002 \u3000\u3000\u5251\u5149\u53c8\u4e00\u95ea\uff0c\u76f4\u53d6\u5434\u8155\u3002 \u3000\u3000\u53f8\u9a6c\u7adf\u7528\u4e00\u53ea\u6ca1\u6709\u65ad\u7684\u624b\uff0c\u8d64\u624b\u53bb\u593a\u5353\u4e1c\u6765\u7684\u5251\u950b\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u80fd\u52a8\u5979\u3002\u201d\u53f8\u9a6c\u7684\u58f0\u97f3\u51c4\u60e8\u5636\u54d1\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u4e0d\u7ba1\u6211\u6b7b\u6d3b\uff0c\u4f60\u90fd\u4e0d\u80fd\u52a8\u5979\u3002\u201d \u3000\u3000\u4ed6\u7684\u81c2\u5df2\u65ad\uff0c\u6c14\u5374\u672a\u65ad\u3002 \u3000\u3000\u5353\u4e1c\u6765\u8fd9\u4e00\u5251\u7adf\u4f3c\u88ab\u4ed6\u8fd9\u80a1\u6c14\u903c\u4f4f\u4e86\uff0c\u518d\u4e5f\u65e0\u6cd5\u51fa\u624b\u3002 \u3000\u3000\u201c\u5434\u5a49\u3002\u6211\u8fd8\u662f\u4e0d\u602a\u4f60\uff0c\u201d\u53f8\u9a6c\u8bf4\uff1a\u201c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u5434\u5a49\u770b\u7740\u4ed6\uff0c\u7528\u4e00\u79cd\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u7684\u773c\u795e\u770b\u7740\u5979\u7684\u4e08\u592b\u3002 \u3000\u3000\u201c\u662f\u7684\uff0c\u6211\u8981\u8d70\u4e86\uff0c\u201d\u5979\u8f7b\u8f7b\u7684\u8bf4\uff1a\u201c\u6211\u672c\u6765\u5c31\u5e94\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u53ef\u662f\u5979\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u5979\u5ffd\u7136\u6251\u8fc7\u53bb\uff0c\u62b1\u4f4f\u4e86\u4ed6\uff0c\u628a\u5979\u7684\u8138\u8d34\u5728\u4ed6\u7684\u65ad\u81c2\u4e0a\uff0c\u7528\u5979\u7684\u8138\u963b\u4f4f\u4e86\u4ed6\u4f24\u53e3\u6d41\u51fa\u6765\u7684 \u8840\u3002 \u3000\u3000\u8840\u6d41\u5728\u5979\u8138\u4e0a\uff0c\u6cea\u4e5f\u5df2\u6d41\u4e0b\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u8fd9\u4e00\u751f\u5df2\u7ecf\u8d70\u9519\u4e86\u4e00\u6b65\uff0c\u5df2\u7ecf\u4e0d\u80fd\u518d\u9519\uff0c\u201d\u5434\u5a49\u8bf4\uff1a\u201c\u8fd9\u4e00\u6b21\u6211\u7edd\u4e0d\u4f1a\u518d\u8d70\u9519 \u7684\u3002\u201d \u3000\u3000\u5979\u5df2\u7ecf\u9009\u597d\u4e86\u5979\u8981\u8d70\u7684\u8def\u3002 \u3000\u3000\u552f\u4e00\u7684\u4e00\u6761\u8def\u3002 \u3000\u3000\u5353\u4e1c\u6765\u624b\u4e2d\u7684\u5251\u4ecd\u5728\u3002 \u3000\u3000\u5434\u5a49\u5ffd\u7136\u7d27\u62b1\u7740\u5979\u7684\u4e08\u592b\uff0c\u5411\u5251\u5c16\u4e0a\u649e\u4e86\u8fc7\u53bb\uff0c\u5251\u950b\u7acb\u523b\u523a\u5165\u4e86\u5979\u7684\u540e\u80cc\uff0c\u7a7f\u8fc7\u4e86\u5979\u7684 \u5fc3\u810f\uff0c\u518d\u523a\u5165\u53f8\u9a6c\u7684\u5fc3\u810f\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u672c\u6765\u5c31\u662f\u65e0\u6bd4\u950b\u5229\u7684\u5b9d\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u5c31\u7a7f\u900f\u4e86\u4e24\u9897\u5fc3\u3002 \u3000\u3000\u201c\u540c\u540c\uff0c\u201d\u5434\u5a49\u547b\u541f\u4f4e\u8bed\uff1a\u201c\u540c\u540c\uff0c\u6211\u4eec\u603b\u7b97\u662f\u540c\u5e74\u540c\u6708\u540c\u65e5\u540c\u65f6\u6b7b\u7684\uff0c\u603b\u7b97\u6b7b\u5728\u4e00\u8d77 \u4e86\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u5979\u8fd9\u4e00\u751f\u4e2d\u8bf4\u7684\u6700\u540e\u4e00\u53e5\u6d3b\u3002 \u3000\u3000\u201c\u5b9d\u5251\u65e0\u60c5\uff0c\u82f1\u96c4\u65e0\u6cea\u3002\u201d \u3000\u3000\u53f8\u9a6c\u8d85\u7fa4\u8fd8\u662f\u6807\u67aa\u822c\u7ad9\u5728\u90a3\u91cc\uff0c\u8fd8\u662f\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u4ed6\u81f3\u6b7b\u90fd\u6ca1\u6709\u5012\u4e0b\uff0c\u4ed6\u81f3\u6b7b\u90fd\u6ca1\u6709\u6d41\u6cea\u3002 \u4e94 \u3000\u3000\u82f1\u96c4\u7684\u6cea\u5df2\u5316\u4f5c\u78a7\u8840\u3002 \u3000\u3000\u5251\u4e0a\u5374\u4ecd\u7136\u6ca1\u6709\u8840\uff0c\u53ea\u6709\u4e00\u70b9\u6cea\u75d5\uff0c\u53ef\u662f\u73b0\u5728\u8fde\u8fd9\u4e00\u70b9\u795e\u79d8\u7684\u6cea\u75d5\u90fd\u4eff\u4f5b\u5df2\u88ab\u82f1\u96c4\u7684\u78a7 \u8840\u67d3\u7ea2\u4e86\u3002 \u3000\u3000\u5251\u4ecd\u5728\u5353\u4e1c\u6765\u624b\u91cc\uff0c\u5353\u4e1c\u6765\u5728\u51dd\u89c6\u7740\u5251\u4e0a\u7684\u6cea\u75d5\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u53bb\u770b\u53f8\u9a6c\uff0c\u4e5f\u6ca1\u6709\u53bb\u770b\u5434\u5a49\u3002 \u3000\u3000\u4ed6\u7684\u773c\u4e2d\u66f4\u4e0d\u4f1a\u6709\u6cea\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e00\u76f4\u90fd\u5728\u75f4\u75f4\u7684\u770b\u7740\u8fd9\u4e00\u70b9\u6cea\u75d5\uff0c\u5c31\u50cf\u5ffd\u7136\u53d1\u73b0\u4e86\u8fd9\u4e00\u70b9\u6cea\u75d5\u4e2d\u6709\u4e00\u79cd\u795e\u79d8\u800c\u90aa \u6076\u7684\u529b\u91cf\uff0c\u6240\u6709\u7684\u4e0d\u5e78\u90fd\u662f\u88ab\u5b83\u9020\u6210\u7684\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u5ffd\u7136\u8bf4\uff1a\u201c\u4eca\u5929\u6765\u7684\u4e09\u4e2a\u4eba\uff0c\u771f\u6b63\u53ef\u6015\u7684\u5e76\u4e0d\u662f\u516c\u5b59\u5144\u5f1f\uff0c\u800c\u662f\u7b2c \u4e09\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7684\u58f0\u97f3\u51b0\u51b7\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u672c\u6765\u662f\u4e0d\u8be5\u6b7b\u7684\uff0c\u56e0\u4e3a\u4ed6\u592a\u806a\u660e\u3001\u592a\u5389\u5bb3\uff0c\u4ed6\u7684\u6697\u5668\u548c\u6613\u5bb9\u672f\u90fd\u5f88\u5c11\u6709\u4eba\u80fd\u6bd4 \u5f97\u4e0a\u4ed6\uff0c\u5982\u679c\u4ed6\u521a\u624d\u6d88\u6d88\u7684\u8d70\u4e86\uff0c\u6211\u4e5f\u8bb8\u4f1a\u88c5\u4f5c\u4e0d\u77e5\u9053\u7684\uff0c\u56e0\u4e3a\u6211\u4ee5\u540e\u4e00\u5b9a\u8fd8\u4f1a\u7528\u5f97\u5230 \u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u8fd8\u6ca1\u6709\u8d70\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6ca1\u6709\u8d70\uff0c\u201d\u65f1\u4e1c\u6765\u8bf4\uff0c\u201c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u4ed6\u5df2\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\uff0c\u6211\u5df2\u7ecf\u4e0d\u4f1a\u8ba9\u4ed6\u8d70 \u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\uff0c\u9762\u5bf9\u90a3\u767d\u5934\u76f2\u773c\u7684\u8001\u4e50\u5e08\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u8ba1\u5148\u751f\uff0c\u96be\u9053\u4f60\u771f\u7684\u4ee5 \u4e3a\u6211\u8ba4\u4e0d\u51fa\u4f60\u6765\u4e86\uff1f\u201d \u3000\u3000\u767d\u5934\u4e50\u5e08\u4e00\u76f4\u7ad9\u5728\u706f\u5149\u4e0e\u9ed1\u6697\u4e4b\u95f4\u7684\u90a3\u4e00\u7247\u6726\u80e7\u4e2d\uff0c\u5149\u4e5f\u6726\u80e7\uff0c\u4eba\u4e5f\u6726\u80e7\u3002 \u3000\u3000\u90a3\u4e2a\u68b3\u7740\u8fab\u5b50\u7684\u5c0f\u5973\u5b69\uff0c\u4e5f\u4e00\u76f4\u62b1\u7740\u7435\u7436\u7ad9\u5728\u4ed6\u8eab\u8fb9\uff0c\u82cd\u767d\u7684\u8138\u4e0a\u65e2\u6ca1\u6709\u60b2\u4f24\u4e4b\u8272\uff0c\u4e5f \u6ca1\u6709\u6050\u60e7\u4e4b\u610f\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u56e0\u4e3a\u5979\u6839\u672c\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\uff0c\u8fd8\u662f\u56e0\u5979\u5df2\u7ecf\u5b8c\u5168\u9ebb\u6728\u3002 \u3000\u3000\u767d\u5934\u4e50\u5e08\u4e00\u53ea\u624b\u6301\u6d1e\u8427\uff0c\u4e00\u53ea\u624b\u6276\u7740\u5979\u7684\u80a9\uff0c\u8138\u4e0a\u4e5f\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u201c\u8ba1\u5148\u751f\uff0c\u201d\u5353\u4e1c\u6765\u53c8\u5bf9\u4ed6\u8bf4\uff1a\u201c\u4e09\u661f\u593a\u547d\uff0c\u4e24\u6b65\u6613\u5f62\uff0c\u4e00\u8ba1\u7edd\u6237\uff0c\u8ba1\u5148\u751f\uff0c\u4f60\u7684\u6613\u5bb9 \u4e4b\u672f\u7684\u786e\u9ad8\u660e\uff0c\u4f60\u7684\u624b\u6bb5\u66f4\u9ad8\uff0c\u201d \u3000\u3000\u767d\u5934\u4e50\u5e08\u5c45\u7136\u5f00\u53e3\u8bf4\u8bdd\u4e86\uff0c\u5c45\u7136\u8bf4\uff1a\u201c\u591a\u8c22\u5938\u5956\uff0c\u591a\u8c22\u591a\u8c22\u3002\u201d \u3000\u3000\u201c\u8ba1\u5148\u751f\uff0c\u4f60\u8981\u5434\u5a49\u6765\u4f5c\u8776\u821e\u4e4b\u821e\uff0c\u5728\u4e00\u77ac\u95f4\u5c31\u628a\u96c4\u72ee\u5802\u7684\u6731\u5802\u4e3b\u548c\u53f8\u9a6c\u8d85\u7fa4\u4e24\u4e2a\u4eba\u7684 \u6597\u5fd7\u5168\u90fd\u6bc1\u4e86\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u8fd9\u4e00\u7740\u4f60\u505a\u5f97\u771f\u9ad8\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u591a\u8c22\u3002\u201d \u3000\u3000\u201c\u767d\u5934\u7684\u4e50\u5e08\u4f34\u7740\u4ed6\u695a\u695a\u52a8\u4eba\u7684\u5c0f\u5b59\u5973\u5356\u5531\u4e8e\u8857\u5934\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u4ed4\u7ec6\u53bb\u770b\u8fd9\u4e2a\u778e\u4e86\u773c\u7684\u767d \u53d1\u8001\u7fc1\u3002\u6240\u4ee5\u4f60\u5c31\u626e\u6210\u4e86\u4ed6\uff0c\u5e26\u7740\u4ed6\u7684\u5b59\u5973\u5230\u8fd9\u91cc\u6765\uff0c\u7528\u76f2\u8005\u7684\u6b4c\u6765\u63a9\u9970\u886c\u6258\u5434\u5a49\u7684\u821e\uff0c\u7528 \u5979\u7684\u821e\u6765\u5438\u5f15\u522b\u4eba\u7684\u6ce8\u610f\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u90a3\u4f4d\u767d\u5934\u4e50\u5e08\u7684\u5bb9\u8c8c\u867d\u7136\u6ca1\u6709\u4eba\u4f1a\u53bb\u5206\u8fa8\uff0c\u4ed6\u7684\u8427\u58f0\u8fdc\u975e\u4f60\u7684\u8427\u58f0\u80fd\u53ca\uff0c \u8fd9\u662f\u5927\u5bb6\u90fd\u53ef\u4ee5\u5206\u8fa8\u5f97\u51fa\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u53ea\u4e0d\u8fc7\u5728\u5f53\u65f6\u90a3\u79cd\u6084\u51b5\u4e0b\uff0c\u4e5f\u6ca1\u6709\u4eba\u4f1a\u53bb\u6ce8\u610f \u8fd9\u4e00\u70b9\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u201d\u8ba1\u5148\u751f\u5c45\u7136\u627f\u8ba4\uff1a\u201c\u6211\u7684\u60f3\u6cd5\u786e\u5b9e\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u8ba1\u5148\u751f\uff0c\u4f60\u5b9e\u5728\u662f\u4f4d\u4eba\u624d\uff0c\u4e86\u4e0d\u8d77\u7684\u4eba\u624d\uff0c\u6211\u4e00\u76f4\u90fd\u5f88\u4f69\u670d\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u6e29\u548c\u5ba2\u6c14\u7684\u8bed\u58f0\u5ffd\u7136\u53c8\u53d8\u4e86\uff0c\u53c8\u7528\u4ed6\u90a3\u79cd\u72ec\u7279\u7684\u53e3\u6c14\u8bf4\u3002\u201c\u53ef\u662f\u4f60\u5b9e\u5728\u4e0d\u5e94\u8be5\u628a \u4f60\u7684\u7edd\u6237\u9488\u4ea4\u7ed9\u5434\u5a49\u7684\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u5b9e\u5728\u505a\u9519\u4e86\u3002\u201d \u3000\u3000\u8ba1\u5148\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u7528\u4e00\u79cd\u5145\u6ee1\u4e86\u60b2\u4f24\u4e0e\u540e\u6094\u7684\u58f0\u97f3\u53f9\u606f\u7740\u9053\uff1a\u201c\u6211\u627f\u8ba4\u6211\u9519\u4e86\uff0c\u867d\u7136\u6211 \u4ece\u672a\u60f3\u5230\u5434\u5a49\u4f1a\u7528\u5b83\u53bb\u5bf9\u4ed8\u53f8\u9a6c\uff0c\u4f46\u53f8\u9a6c\u5374\u5df2\u56e0\u6b64\u800c\u6b7b\u3002\u6211\u65e9\u5c31\u5e94\u8be5\u60f3\u5230\u5353\u5148\u751f\u4e00\u5b9a\u4f1a\u628a\u8fd9 \u7b14\u8d26\u7b97\u5728\u6211\u8eab\u4e0a\u7684\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4f60\u5f53\u65f6\u53ea\u60f3\u5230\u8981\u522b\u4eba\u7684\u547d\uff0c\u5374\u5fd8\u4e86\u90a3\u4e5f\u662f\u4f60\u81ea\u5df1\u9632\u8eab\u62a4\u547d\u7684\u5229\u5668\u3002\u201d \u3000\u3000\u8ba1\u5148\u751f\u4e5f\u627f\u8ba4\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u90fd\u4e0d\u8be5\u628a\u90a3\u7b52\u9488\u62ff\u53bb\u7ed9\u522b\u4eba\u7684\u3002\u201d\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\u3002\u7528\u4e00\u79cd\u8033\u8bed\u822c\u7684\u58f0 \u97f3\u544a\u8bc9\u5353\u4e1c\u6765\uff1a\u201c\u5e78\u597d\u6211\u81ea\u5df1\u8fd8\u6709\u51e0\u7b52\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u5c31\u597d\u50cf\u5728\u5bf9\u4e00\u4e2a\u77e5\u5fc3\u7684\u670b\u53cb\u53d9\u8bf4\u4ed6\u5fc3\u91cc\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4e00\u5b9a\u8981\u5f88\u6ce8\u610f\u7684\u53bb\u542c\u624d\u80fd\u542c\u5f97\u5230\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u542c\u7684\u65f6\u5019\uff0c\u8ba1\u5148\u751f\u7684\u7edd\u6237\u9488\u5df2\u7ecf\u6253\u51fa\u6765\u4e86\uff0c\u5206\u522b\u4ece\u4ed6\u7684\u53cc\u624b\u8863\u8896\u548c\u4ed6\u624b\u91cc\u90a3\u7ba1\u6d1e \u8427\u91cc\u6253\u51fa\u6765\uff0c\u8fd9\u4e09\u7b52\u9488\u5df2\u8db3\u591f\u5c06\u5353\u4e1c\u6765\u6240\u6709\u7684\u9000\u8def\u5168\u90e8\u5c01\u6b7b\u3002 \u3000\u3000\u4e00\u7b52\u4e09\u9488\uff0c\u5df2\u8db3\u8ffd\u9b42\u593a\u547d\uff0c\u4f55\u51b5\u662f\u4e09\u7b52\uff1f \u3000\u3000\u4f55\u51b5\u5b83\u7684\u9488\u7b52\u548c\u673a\u5668\u90fd\u662f\u7ecf\u8fc7\u7279\u522b\u8bbe\u8ba1\u7684\uff0c\u901f\u5ea6\u4e5f\u8fdc\u6bd4\u4e16\u4e0a\u5927\u591a\u6570\u6697\u5668\u5feb\u5f97\u591a\u3002 \u3000\u3000\u53ef\u60dc\u5353\u4e1c\u6765\u66f4\u5feb\u3002\u4ed6\u6839\u672c\u6ca1\u95ea\u907f\uff0c\u4f46\u662f\u4ed6\u624b\u4e0a\u7684\u5251\u5df1\u5212\u51fa\u4e86\u4e00\u9053\u5149\u8292\u8000\u773c\u7684\u5706\u5f27\u3002\u5251\u6c14 \u6fc0\u8361\u56de\u65cb\uff0c\u5c31\u597d\u50cf\u6d51\u6c34\u4e2d\u5ffd\u7136\u6d8c\u51fa\u7684\u4e00\u4e2a\u529b\u91cf\u6781\u5f3a\u5927\u7684\u6f29\u6da1\u3002 \u3000\u3000\u4e5d\u70b9\u5bd2\u661f\u5728\u4e00\u5239\u90a3\u95f4\u5c31\u5df2\u88ab\u8fd9\u80a1\u529b\u91cf\u5377\u5165\u4e86\u8fd9\u4e2a\u6f29\u6da1\uff0c\u7b49\u5230\u5251\u5149\u6d88\u5931\u65f6\uff0c\u4e09\u7b52\u9488\u4e5f\u4e0d\u89c1 \u4e86\u3002 \u3000\u3000\u8ba1\u5148\u751f\u7684\u5fc3\u4e5f\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u662f\u5b66\u5251\u7684\u4eba\uff0c\u5df2\u7ecf\u5fcd\u4e0d\u4f4f\u8981\u5927\u58f0\u79f0\u8d5e\u3002 \u3000\u3000\u201c\u597d\u5251\u6cd5\uff01\u201d \u3000\u3000\u5353\u4e1c\u6765\u5fae\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u7684\u5251\u4e5f\u662f\u628a\u597d\u5251\uff0c\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u8f6c\u8138\u53bb\u95ee\u8ba1\u5148\u751f\u3002 \u3000\u3000\u201c\u521a\u624d\u6211\u8bf4\u8bdd\u7684\u65f6\u5019\u4e5f\u662f\u4e2a\u597d\u673a\u4f1a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u4e58\u673a\u628a\u4f60\u5269\u4e0b\u7684\u90a3\u7b52\u9488\u6253\u51fa\u6765\uff1f\u201d \u3000\u3000\u8ba1\u5148\u751f\u7684\u624b\u63e1\u7d27\uff0c\u63e1\u4f4f\u4e86\u6ee1\u628a\u51b7\u6c57\u3002 \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u8fd8\u6709\u4e24\u7b52\u9488\uff0c\u4f60\u8fde\u6211\u6709\u51e0\u7b52\u9488\u90fd\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u4e8b\u6211\u5927\u6982\u90fd\u77e5\u9053\u4e00\u70b9\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5927\u6982\u6bd4\u4f60\u60f3\u8c61\u4e2d\u8fd8\u8981\u591a\u4e00\u70b9\u3002\u201d \u3000\u3000\u8ba1\u5148\u751f\u53c8\u5f00\u59cb\u53f9\u606f\u3002 \u3000\u3000\u201c\u5353\u5148\u751f\uff0c\u4f60\u7684\u786e\u6bd4\u6211\u5f3a\uff0c\u6bd4\u6240\u6709\u7684\u4eba\u90fd\u5f3a\uff0c\u4f60\u7684\u786e\u5e94\u8be5\u6210\u529f\u7684\u3002\u201d\u4ed6\u9eef\u7136\u9053\uff1a\u201c\u4ece\u4eca \u4ee5\u540e\uff0c\u6211\u7edd\u4e0d\u4f1a\u518d\u53db\u4f60\u3002\u201d \u3000\u3000\u201c\u4ece\u4eca\u4ee5\u540e\uff1f\u201d\u5353\u4e1c\u6765\u4eff\u4f5b\u5f88\u8be7\u5f02\uff1a\u201c\u96be\u9053\u4f60\u771f\u7684\u8ba4\u4e3a\u4f60\u8fd8\u6709\u2018\u4ee5\u540e\u2019\uff1f\u201d \u3000\u3000\u8ba1\u5148\u751f\u7684\u8138\u8272\u6ca1\u6709\u53d8\uff0c\u4e00\u4e2a\u4eba\u7ecf\u8fc7\u6613\u5bb9\u540e\u8138\u8272\u662f\u4e0d\u4f1a\u53d8\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5168\u8eab\u4e0a\u4e0b\u7684\u6837\u5b50\u90fd\u53d8\u4e86\uff0c\u5c31\u50cf\u662f\u4e00\u6761\u9aa4\u7136\u9762\u5bf9\u4ed9\u9e64\u7684\u6bd2\u86c7\u4e00\u6837\uff0c\u53d8\u5f97\u7d27\u5f20\u800c\u626d \u66f2\u3002 \u3000\u3000\u201c\u4f60\u8981\u6211\u600e\u4e48\u6837\uff1f\u201d\u4ed6\u95ee\u5353\u4e1c\u6765\uff1a\u201c\u968f\u4fbf\u4f60\u8981\u6211\u600e\u4e48\u6837\u90fd\u884c\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u60f3\u8981\u4f60\u600e\u4e48\u6837\uff0c\u53ea\u4e0d\u8fc7\u8981\u4f60\u505a\u4e00\u4ef6\u6700\u7b80\u5355\u7684\u4e8b\u800c\u5df2\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u8fd9\u4ef6\u4e8b\u662f\u4eba\u4eba \u90fd\u4f1a\u505a\u7684\u3002\u201d \u3000\u3000\u8ba1\u5148\u751f\u5c45\u7136\u6ca1\u6709\u53d1\u73b0\u4ed6\u7684\u77b3\u5b54\u5df2\u6536\u7f29\uff0c\u5c45\u7136\u8fd8\u5728\u95ee\u4ed6\uff1a\u201c\u4f60\u8981\u6211\u53bb\u505a\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5353\u4e1c\u6765\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u6211\u8981\u4f60\u53bb\u6b7b\u3002\u201d \u3000\u3000\u6b7b\uff0c\u6709\u65f6\u7684\u786e\u662f\u4ef6\u5f88\u7b80\u5355\u7684\u4e8b\u3002 \u3000\u3000\u8ba1\u5148\u751f\u5f88\u5feb\u5c31\u6b7b\u4e86\uff0c\u5c31\u5728\u5353\u4e1c\u6765\u638c\u4e2d\u7684\u5251\u5149\u53c8\u5f00\u59cb\u95ee\u8d77\u5149\u8292\u65f6\uff0c\u4ed6\u5c31\u6b7b\u4e86\u3002 \u3000\u3000\u5251\u5149\u53ea\u4e00\u95ea\uff0c\u5c31\u5df2\u523a\u4eba\u4e86\u4ed6\u54bd\u5589\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u53c8\u4e0d\u7981\u51fa\u58f0\u800c\u8d5e\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u8fd9\u4e00\u5251\u597d\u5feb\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u5fae\u7b11\uff1a\u201c\u4f60\u7684\u5251\u4e5f\u662f\u628a\u597d\u5251\uff0c\u8fdc\u6bd4\u6211\u60f3\u8c61\u4e2d\u66f4\u597d\uff0c\u6211\u597d\u50cf\u5df2\u7ecf\u6709\u70b9\u820d\u4e0d\u5f97\u8fd8\u7ed9 \u4f60\u4e86\u3002\u201d \u516d \u3000\u3000\u6731\u731b\u4e00\u76f4\u6ca1\u6709\u52a8\uff0c\u800c\u4e14\u4e00\u76f4\u5f88\u6c89\u9ed8\u3002 \u3000\u3000\u4ed6\u672c\u6765\u7edd\u4e0d\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u53f8\u9a6c\u7684\u6b7b\u672c\u6765\u4e00\u5b9a\u4f1a\u8ba9\u4ed6\u70ed\u8840\u6cb8\u817e\u3001\u632f\u81c2\u72c2\u547c\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u52a8\uff0c\u5c31\u56e0\u4e3a\u53f8\u9a6c\u7684\u6b7b\u5ffd\u7136\u8ba9\u4ed6\u60f3\u8d77\u4e86\u8bb8\u591a\u4e8b\uff0c\u6bcf\u4ef6\u4e8b\u90fd\u50cf\u662f\u6746\u957f\u67aa\u4e00\u6837\u523a\u4eba\u4e86\u4ed6 \u7684\u5fc3\u3002 \u3000\u3000\u2014\u2014\u5434\u5a49\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u6837\u505a\uff1f\u662f\u4e3a\u4e86\u62a5\u590d\uff1f\u8fd8\u662f\u4e3a\u4e86\u4fdd\u62a4\u81ea\u5df1\uff1f \u3000\u3000\u4e00\u4e2a\u4eba\u81ea\u5df1\u505a\u9519\u4e86\u4e8b\uff0c\u5374\u5c06\u9519\u8bef\u53d1\u751f\u7684\u539f\u56e0\u5f52\u548e\u5230\u522b\u4eba\u8eab\u4e0a\uff0c\u81ea\u5df1\u5fc3\u91cc\u975e\u4f46\u6ca1\u6709\u6094\u759a\u53cd \u800c\u5145\u6ee1\u4e86\u4ec7\u6068\uff0c\u53cd\u800c\u8981\u53bb\u5bf9\u522b\u4eba\u62a5\u590d\u3002\u8fd9\u79cd\u884c\u4e3a\u672c\u6765\u5c31\u662f\u4eba\u7c7b\u6700\u539f\u59cb\u7684\u5f31\u70b9\u4e4b\u4e00\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u4e3a\u4e86\u81ea\u5df1\u505a\u9519\u4e86\u4e8b\uff0c\u800c\u53bb\u4f24\u5bb3\u522b\u4eba\u6765\u4fdd\u62a4\u81ea\u5df1\uff0c\u8fd9\u79cd\u5fc3\u7406\u4e5f\u662f\u4e00\u6837\u7684\u3002 \u3000\u3000\u81ea\u79c1\uff0c\u5c31\u8fde\u5723\u8d24\u4ed9\u4f5b\u90e8\u5f88\u96be\u52d8\u7834\u8fd9\u4e00\u5173\uff0c\u4f55\u51b5\u51e1\u4eba\u3002 \u3000\u3000\u4f46\u662f\u6731\u731b\u7684\u60f3\u6cd5\u5374\u4e0d\u540c\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u5230\u5434\u5a49\u8fd9\u6837\u505a\u5f88\u53ef\u80fd\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u6df1\u7231\u53f8\u9a6c\uff0c\u5df2\u7ecf\u7231\u5f97\u8eab\u4e0d\u7531\u5df1\uff0c\u65e0\u53ef\u5948\u4f55 \u4e86\u3002 \u3000\u3000\u7231\u5230\u4e86\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u7231\u6210\u4e86\u8fd9\u79cd\u65b9\u5f0f\uff0c\u7231\u5230\u7ec8\u6781\u65f6\u5c31\u662f\u6bc1\u706d\u3002 \u3000\u3000\u6240\u4ee5\u5979\u5c31\u81ea\u5df1\u6bc1\u4e86\uff0c\u4e0d\u4f46\u6bc1\u4e86\u81ea\u5df1\uff0c\u4e5f\u8981\u6bc1\u706d\u5979\u6240\u7231\u7684\u3002 \u3000\u3000\u53f8\u9a6c\u80fd\u4e86\u89e3\u8fd9\u4e00\u70b9\uff0c\u6240\u4ee5\u81f3\u6b7b\u90fd\u4e0d\u6028\u5979\u3002 \u3000\u3000\u8776\u821e\u5462\uff1f \u3000\u3000\u5728\u5353\u4e1c\u6765\u547d\u4ee4\u4ed6\u7684\u5c5e\u4e0b\u591c\u88ad\u96c4\u72ee\u5802\u65f6\uff0c\u8776\u821e\u4e3a\u4ec0\u4e48\u8981\u9003\u8d70\uff1f\u5b81\u53ef\u88ab\u5353\u4e1c\u6765\u5229\u7528\u4e5f\u8981\u9003 \u8d70\uff1f \u3000\u3000\u5979\u662f\u4e3a\u4e86\u201c\u7231\u201d\u800c\u8d70\u7684\uff1f\u8fd8\u662f\u4e3a\u4e86\u201c\u4e0d\u7231\u201d\u800c\u8d70\u7684\uff1f \u3000\u3000\u5982\u679c\u5979\u4e5f\u50cf\u5434\u5a49\u6df1\u7231\u53f8\u9a6c\u4e00\u6837\u7231\u6731\u731b\uff0c\u5374\u8ba4\u4e3a\u6731\u731b\u5bf9\u5979\u5168\u4e0d\u5728\u4e4e\uff0c\u5979\u5f53\u7136\u8981\u8d70\u3002 \u3000\u3000\u5982\u679c\u5979\u6839\u672c\u4e0d\u7231\u6731\u731b\uff0c\u5f53\u7136\u66f4\u8981\u8d70\u3002 \u3000\u3000\u53ef\u662f\u5979\u5982\u679c\u771f\u7684\u4e0d\u7231\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u5bf9\u6731\u731b\u90a3\u4e48\u5728\u4e4e\uff1f\u4e3a\u4ec0\u4e48\u8981\u6b7b\uff1f \u3000\u3000\u4e0d\u7231\u5c31\u662f\u6068\uff0c\u7231\u6781\u4e86\u4e5f\u4f1a\u53d8\u6210\u6068\uff0c\u7231\u6068\u4e4b\u95f4\uff0c\u672c\u6765\u5c31\u53ea\u4e0d\u8fc7\u662f\u4e00\u7ebf\u4e4b\u522b\u800c\u5df2\u3002 \u3000\u3000\u7a76\u7adf\u662f\u7231\u662f\u6068\uff1f\u6709\u8c01\u80fd\u5206\u5f97\u6e05\uff1f\u8fd9\u79cd\u4e8b\u53c8\u6709\u8c01\u80fd\u60f3\u5f97\u901a\uff1f \u3000\u3000\u6731\u731b\u5ffd\u7136\u72c2\u7b11\u3002 \u3000\u3000\u201c\u53f8\u9a6c\u8d85\u7fa4\uff0c\u4f60\u6b7b\u5f97\u597d\uff0c\u6b7b\u5f97\u597d\u6781\u4e86\u3002\u201d\u4ed6\u7684\u7b11\u58f0\u51c4\u5389\u5982\u733f\u557c\uff1a\u201c\u4f60\u672c\u6765\u5c31\u5e94\u8be5\u6b7b\u7684\uff0c \u56e0\u4e3a\u4f60\u672c\u6765\u5c31\u662f\u4e2a\u65e0\u53ef\u6551\u836f\u7684\u5446\u5b50\u3002\u201d \u3000\u3000\u7b49\u4ed6\u7b11\u5b8c\u4e86\uff0c\u5353\u4e1c\u6765\u624d\u51b7\u51b7\u7684\u95ee\uff0c\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u6bd4\u4ed6\u66f4\u8be5\u6b7b\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u6211\u65e9\u5c31\u60f3\u628a\u5934\u9885\u9001\u7ed9\u522b\u4eba\u53ea\u53ef\u60dc\u522b\u4eba\u4e0d\u8981\uff0c\u5374\u8981\u6211\u6b7b\u5728 \u4f60\u624b\u91cc\uff0c\u6211\u6b7b\u5f97\u5b9e\u5728\u4e0d\u7518\u5fc3\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4f60\u6b7b\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u4ed6\u4e00\u6b65\u5c31\u7a9c\u4e86\u8fc7\u6765\uff0c\u548c\u6731\u731b\u5e76\u80a9\u800c\u7acb\uff0c\u7528\u529b\u63e1\u4f4f\u4e86\u6731\u731b\u7684\u81c2\uff1a\u201c\u8c01\u8981\u52a8\u4ed6\uff0c\u5c31\u5f97\u5148\u6740\u4e86 \u6211\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u770b\u770b\u5c0f\u9ad8\uff0c\u5c31\u597d\u50cf\u5728\u770b\u7740\u4e00\u4e2a\u88ab\u81ea\u5df1\u5ba0\u574f\u4e86\u7684\u5b69\u5b50\u4e00\u6837\uff0c\u867d\u7136\u6709\u70b9\u751f\u6c14\uff0c\u5374\u8fd8\u662f \u5145\u6ee1\u601c\u501f\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u4f60\u600e\u4e48\u5bf9\u6211\uff0c\u6211\u4e00\u76f4\u90fd\u6ca1\u6709\u52a8\u4f60\uff0c\u4f60\u8981\u6211\u6b7b\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u6ca1\u6709\u52a8\u4f60\u3002\u201d\u5353\u4e1c\u6765 \u8bf4\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u5df2\u7ecf\u5e94\u8be5\u660e\u767d\u6211\u7684\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u4e0d\u80fd\u5426\u8ba4\uff01 \u3000\u3000\u201c\u6211\u5f53\u7136\u660e\u767d\uff0c\u201d\u4ed6\u8bf4\uff1a\u201c\u4f60\u8981\u628a\u6211\u9020\u6210\u7b2c\u4e8c\u4e2a\u53f8\u9a6c\u8d85\u7fa4\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u9eef\u7136\u53f9\u606f\u3002 \u3000\u3000\u201c\u4ed6\u662f\u6211\u8fd9\u4e00\u751f\u4e2d\u552f\u4e00\u7684\u670b\u53cb\uff0c\u4e0d\u7ba1\u4ed6\u600e\u4e48\u6837\u5bf9\u6211\uff0c\u6211\u5bf9\u4ed6\u90c1\u6ca1\u6709\u53d8\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u4f60\u4fe1\u4e0d\u4fe1\u6211\u968f\u65f6\u90fd\u53ef\u4ee5\u6740\u4e86\u4f60\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u6b66\u529f\u5251\u6cd5\u4e4b\u9ad8\uff0c\u6211\u7684\u786e\u6bd4\u4e0d\u4e0a\uff0c\u4f60\u7684\u5fc3\u8ba1\uff0c\u5929\u4e0b\u66f4\u65e0\u4eba\u80fd\u53ca\u201d\uff0c\u9ad8\u6e10\u98de\u8bf4\uff1a\u201c\u4f60 \u521a\u624d\u8bf4\u90a3\u4f4d\u8ba1\u5148\u751f\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\u624d\uff0c\u5176\u5b9e\u771f\u6b63\u4e86\u4e0d\u8d77\u7684\u5e76\u4e0d\u662f\u4ed6\uff0c\u800c\u662f\u4f60\uff0c\u8c01\u4e5f\u4e0d\u80fd\u4e0d\u4f69 \u670d\u3002\u201d \u3000\u3000\u4ed6\u76ef\u7740\u5353\u4e1c\u6765\uff0c\u5ffd\u7136\u4e5f\u7528\u5353\u4e1c\u672a\u90a3\u79cd\u72ec\u7279\u7684\u53e3\u6c14\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\uff1a\u201c\u53ef\u662f\u4f60\u5c31\u7b97\u6740\u4e86 \u6211\u4e5f\u6ca1\u6709\u7528\u7684\uff0c\u6211\u5c31\u7b97\u6b7b\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u52a8\u6731\u731b\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u4f55\u51b5\u6211\u8fd8\u6709\u4e00\u80a1\u6c14\uff0c\u53ea\u8981\u6211\u8fd9\u80a1 \u6c14\u8fd8\u5728\uff0c\u4f60\u8fd8\u672a\u5fc5\u80fd\u80dc\u5f97\u4e86\u6211\u3002\u201d \u3000\u3000\u4e00\u80a1\u6c14\uff1f \u3000\u3000\u8fd9\u4e00\u80a1\u6c14\u662f\u4e00\u80a1\u4ec0\u4e48\u6837\u7684\u6c14\uff1a\u662f\u6b63\u6c14\uff1f\u662f\u4fa0\u6c14\uff1f\u662f\u52c7\u6c14\uff1f\u662f\u4e49\u6c14\uff1f\u8fd8\u662f\u628a\u8fd9\u51e0\u79cd\u6c14\u7528\u7537 \u513f\u7684\u8840\u6027\u6df7\u5408\u6210\u7684\u4e00\u80a1\u8840\u6c14\uff1f \u3000\u3000\u5353\u4e1c\u6765\u7684\u77b3\u5b54\u53c8\u6e10\u6e10\u5f00\u59cb\u6536\u7f29\u3002 \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u4f60\u7684\u786e\u6709\u4e00\u80a1\u6c14\u5728\u3002\u201d\u4ed6\u95ee\u5c0f\u9ad8\uff1a\u201c\u53ef\u662f\u4f60\u7684\u5251\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u4f60\u624b\u91cc\u3002\u201d \u3000\u3000\u201c\u5728\u6211\u624b\u91cc\uff0c\u5c31\u662f\u6211\u7684\u4e86\u3002\u201d\u5353\u4e1c\u6765\u53c8\u95ee\uff1a\u201c\u4f60\u8fd8\u6709\u6ca1\u6709\u5251\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u7b11\u4e86\uff1a\u201c\u4f60\u6ca1\u6709\uff0c\u6211\u6709\u3002\u201d \u3000\u3000\u6709\u5251\u5728\u624b\uff0c\u5251\u5df2\u51fa\u9798\u3002 \u3000\u3000\u5251\u662f\u4e00\u67c4\u5439\u6bdb\u65ad\u53d1\u7684\u5229\u5668\uff0c\u624b\u4e5f\u662f\u4e00\u53cc\u53ef\u6015\u7684\u624b\uff0c\u751a\u81f3\u6bd4\u5251\u66f4\u53ef\u6015\u3002 \u3000\u3000\u8fd9\u53cc\u624b\u6740\u8fc7\u4eba\u540e\uff0c\u975e\u4f46\u770b\u4e0d\u89c1\u8840\uff0c\u8fde\u4e00\u70b9\u75d5\u8ff9\u90e8\u6ca1\u6709\u3002 \u3000\u3000\u201c\u5982\u679c\u4f60\u4e00\u5b9a\u8981\u8fd9\u4e48\u6837\u505a\uff0c\u4f60\u5c31\u8fd9\u4e48\u6837\u505a\u5427\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u4e5f\u8bb8\u8fd9\u5c31\u662f\u4f60\u7684\u547d\u8fd0\uff0c\u4e00 \u4e2a\u4eba\u7684\u547d\u8fd0\u662f\u8c01\u4e5f\u6ca1\u6cd5\u5b50\u6539\u53d8\u7684\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u8fd9\u53cc\u624b\uff0c\u4ed6\u8fd9\u628a\u5251\uff0c\u786e\u5b9e\u53ef\u4ee5\u5728\u4e00\u77ac\u95f4\u51b3\u5b9a\u4e00\u4e2a\u4eba\u7684\u751f\u6b7b\u548c\u547d\u8fd0\u3002 \u3000\u3000\u6731\u731b\u5ffd\u7136\u53c8\u4ef0\u9762\u800c\u7b11\uff1a\u201c\u5927\u4e08\u592b\u751f\u6709\u4f55\u6b22\uff0c\u6b7b\u6709\u4f55\u60e7\uff1f\u8fd9\u4e24\u53e5\u8bdd\u7684\u610f\u601d\uff0c\u6211\u6731\u731b\u76f4\u5230\u4eca \u65e5\u624d\u603b\u7b97\u660e\u767d\u4e86\u3002\u201d\u4ed6\u7684\u7b11\u58f0\u6e10\u4f4e\uff1a\u201c\u9ad8\u6e10\u98de\uff0c\u6211\u6731\u731b\u80fd\u4ea4\u5230\u4f60\u8fd9\u4e2a\u670b\u53cb\uff0c\u6b7b\u5f97\u603b\u7b97\u4e0d\u51a4\uff0c \u53ef\u662f\u4f60\u8fd8\u5e74\u8f7b\uff0c\u4f60\u72af\u4e0d\u7740\u4e3a\u6211\u62fc\u547d\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u5ffd\u7136\u7528\u811a\u5c16\u6311\u8d77\u516c\u5b59\u5b9d\u5251\u843d\u5728\u5730\u4e0a\u7684\u90a3\u628a\u5251\uff0c\u4e00\u624b\u6284\u4f4f\uff0c\u66f2\u81c2\u52fe\u5728\u4ed6\u7684\u540e \u9888\u4e0a\uff0c\u53ea\u8981\u4ed6\u7684\u624b\u4e00\u7528\u529b\uff0c\u4ed6\u7684\u4eba\u5934\u5c31\u8981\u843d\u5730\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7684\u624b\u5df2\u7ecf\u88ab\u5c0f\u9ad8\u63e1\u4f4f\uff0c\u53c8\u7528\u53e6\u4e00\u53ea\u624b\u63e1\u4f4f\u4e86\u5251\u950b\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4e00\u67c4\u5251\u5df2\u88ab \u4ed6\u4ece\u5251\u9537\u5904\u9f50\u67c4\u62d7\u65ad\u3002 \u3000\u3000\u6731\u731b\u77a7\u7740\u4ed6\u5389\u58f0\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u6b7b\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6b7b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u6d3b\u4e0b\u53bb\uff0c\u201d\u6731\u731b\u8bf4\uff1a\u201c\u6211\u672c\u6765\u65e9\u5c31\u5e94\u8be5\u6b7b\u7684\uff0c\u6211\u6b7b\u4e86\u540e\uff0c\u4f60\u5c31\u7528\u4e0d\u7740\u518d\u53bb \u8ddf\u5353\u4e1c\u6765\u62fc\u547d\uff0c\u6211\u4e5f\u53ef\u4ee5\u7b97\u6b7b\u5f97\u5176\u65f6\uff0c\u6b7b\u800c\u65e0\u61be\uff0c\u4e5f\u4e0b\u7b97\u767d\u6d3b\u4e86\u8fd9\u4e00\u8f88\u5b50\u3002\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\u3002\u201d\u9ad8\u6e10\u98de\u8bf4\uff1a\u201c\u73b0\u5728\u4f60\u662f\u6b7b\u662f\u6d3b\uff0c\u5df2\u7ecf\u4e0e\u6211\u4eec\u4eca\u65e5\u8fd9\u4e00\u6218\u5168\u65e0\u5173\u7cfb\uff0c\u4e0d\u7ba1\u4f60 \u662f\u6b7b\u662f\u6d3b\uff0c\u8fd9\u4e00\u6218\u5df2\u52bf\u5728\u5fc5\u884c\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u73b0\u5728\u5353\u4e1c\u6765\u5df2\u7ecf\u4e0d\u4f1a\u653e\u8fc7\u6211\uff0c\u201d\u9ad8\u6e10\u98de\u8bf4\uff1a\u201c\u6211\u82e5\u4e0d\u6b7b\uff0c\u4ed6\u5c31\u8981\u6b7b\u5728\u6211\u624b\u91cc\uff0c\u82e5 \u662f\u6211\u6b64\u523b\u5c31\u80fd\u6740\u4e86\u4ed6\uff0c\u5c31\u7edd\u4e0d\u4f1a\u9976\u4ed6\u6d3b\u5230\u65e5\u51fa\u65f6\u3002\u201d \u3000\u3000\u4ed6\u7528\u529b\u63e1\u7d27\u6731\u731b\u7684\u624b\uff1a\u201c\u4f60\u521a\u5bf8\u8bf4\u7684\u4e24\u7538\u6d3b\u4e5f\u9519\u4e86\uff0c\u5927\u4e08\u592b\u65e2\u751f\u4e8e\u4e16\uff0c\u8981\u6d3b\uff0c\u5c31\u8981\u6d3b\u5f97 \u5feb\u5feb\u4e50\u4e50\uff0c\u8981\u6b7b\uff0c\u4e5f\u8981\u6b7b\u5f97\u6709\u4ef7\u503c\u3002\u201d\u9ad8\u6e10\u98de\u8bf4\uff1a\u201c\u73b0\u5728\u4f60\u82e5\u6b7b\u4e86\uff0c\u53ea\u4e0d\u8fc7\u767d\u767d\u966a\u6211\u9001\u7ed9\u522b \u4eba\u4e00\u6761\u547d\u800c\u5df2\uff0c\u6b7b\u5f97\u5b9e\u5728\u4e00\u6587\u4e0d\u503c\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u5ffd\u7136\u7b11\u4e86\u7b11\uff1a\u201c\u4ed6\u8bf4\u5f97\u5bf9\uff0c\u7b49\u4ed6\u6b7b\u4e86\uff0c\u4f60\u518d\u6b7b\u4e5f\u4e0d\u8fdf\uff0c\u4e3a\u4ec0\u4e48\u8981\u6025\u7740\u628a\u8fd9\u6761\u547d\u9001 \u51fa\u53bb\uff1f\u96be\u9053\u4f60\u4ee5\u4e3a\u6211\u4f1a\u8c22\u8c22\u4f60\uff1f\u201d \u3000\u3000\u6731\u731b\u7684\u624b\u653e\u677e\u4e86\uff0c\u5c0f\u9ad8\u5374\u628a\u4ed6\u7684\u624b\u63e1\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u201c\u4eca\u65e5\u6211\u82e5\u4e0d\u6b7b\uff0c\u6211\u4e0d\u4f46\u8981\u52a9\u4f60\u91cd\u632f\u96c4\u72ee\u5802\uff0c\u800c\u4e14\u8fd8\u8981\u6574\u987f\u5927\u9556\u5c40\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u4eec \u6765\u65e5\u65b9\u957f\uff0c\u8fd8\u5927\u6709\u53ef\u4e3a\uff0c\u53ea\u8981\u6211\u4eec\u8fd8\u6d3b\u7740\uff0c\u5c31\u5343\u4e07\u4e0d\u8981\u8f7b\u8a00\u2018\u6b7b\u2019\u5b57\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u53c8\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u8fd9\u53e5\u8bdd\u4ed6\u4e5f\u8bf4\u5f97\u5bf9\uff0c\u4eba\u6d3b\u7740\u4e3a\u4ec0\u4e48\u8981\u6b7b\uff1f\u4e3a\u4ec0\u4e48\u8981\u628a\u81ea\u5df1\u7684\u6027\u547d \u770b\u5f97\u5982\u6b64\u8f7b\u8d31\uff1f\u201d\u4ed6\u53f9\u606f\u7740\u8bf4\uff1a\u201c\u53ea\u53ef\u60dc\u5230\u4e86\u975e\u6b7b\u4e0d\u53ef\u7684\u65f6\u5019\uff0c\u8c01\u90fd\u96be\u514d\u4e00\u6b7b\uff0c\u65e0\u8bba\u8c01\u90fd\u4e0d \u80fd\u4f8b\u5916\u3002\u201d \u3000\u3000\u4ed6\u770b\u7740\u5c0f\u9ad8\uff0c\u77b3\u5b54\u5df2\u6536\u7f29\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u5c31\u5df2\u5230\u4e86\u975e\u6b7b\u4e0d\u53ef\u7684\u65f6\u5019\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u56e0\u4e3a\u4f60\u53c8\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f60\u5219\u624d\u4e0d\u8be5\u5c06\u90a3\u67c4\u5251\u62d7\u65ad\u7684\u3002\u201d\u5353\u4e1c\u6765\u8bf4\uff1a\u201c\u5982\u679c\u6709\u5251\u5728\u624b\uff0c\u4f60\u5927\u6982\u8fd8\u53ef\u4ee5\u62b5\u6321\u6211\u4e09 \u5341\u62db\uff0c\u53ef\u662f\u73b0\u5728\u6211\u5728\u5341\u62db\u95f4\u5c31\u80fd\u53d6\u4f60\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4ed6\u521a\u8bf4\u5b8c\uff0c\u5c31\u542c\u89c1\u4e00\u4e2a\u4eba\u7528\u4e00\u79cd\u51b7\u6de1\u800c\u9ad8\u505a\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u8fd9\u4e00\u6b21\u9519\u7684\u6050\u6015\u662f\u4f60 \u4e86\u3002\u201d \u4e03 \u3000\u3000\u66d9\u8272\u6e10\u4e34\uff0c\u4f7f\u5f97\u706f\u5149\u6e10\u611f\u9eef\u6de1\uff0c\u8352\u5c71\u95f4\u5df2\u6709\u4e00\u771f\u4e73\u767d\u8272\u7684\u6668\u96fe\u5347\u8d77\u3002 \u3000\u3000\u8ff7\u96fe\u4e2d\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u4e2a\u96fe\u4e00\u822c\u4e0d\u53ef\u6349\u6478\u7684\u4eba\uff0c\u624b\u91cc\u8fd8\u63d0\u7740\u53e3\u6bd4\u4ed6\u8fd9\u4e2a\u4eba\u66f4\u795e\u79d8\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u201c\u8427\u6cea\u8840\uff0c\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u662f\u6211\u3002\u201d\u8427\u6cea\u8840\u51b7\u51b7\u6de1\u6de1\u7684\u8bf4\uff1a\u201c\u4f60\u5927\u6982\u4ee5\u4e3a\u6211\u5df2\u7ecf\u4e0b\u4f1a\u6765\u4e86\uff0c\u56e0\u4e3a\u4f60\u5bf9\u4f60\u7684\u541b\u5b50\u9999 \u4e00\u5b9a\u5f88\u6709\u628a\u63e1\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u5176\u5b9e\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\uff0c\u50cf\u8fd9\u6837\u7684\u541b\u5b50\u901a\u5e38\u90fd\u662f\u4e0d\u592a\u53ef\u9760\u7684\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u957f\u957f\u53f9\u606f\uff1a\u201c\u8427\u6cea\u8840\uff0c\u8427\u5148\u751f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u5728\u4e0d\u8be5\u51fa\u73b0\u7684\u65f6\u5019\u51fa\u73b0\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u56e0\u4e3a\u6211\u5929\u751f\u5c31\u662f\u8fd9\u79cd\u4eba\u5427\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u559c\u6b22\u8fd9\u79cd\u4eba\uff0c\u5f88\u4e0d\u559c\u6b22\u3002\u201d\u5353\u4e1c\u6765\u7684\u58f0\u97f3\u5df2\u6062\u590d\u51b7\u9759\uff1a\u201c\u627e\u4ee5\u524d\u4e5f\u66fe\u9047\u5230\u8fc7\u8fd9\u79cd \u4eba\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4ed6\u4eec\u662f\u4e0d\u662f\u90fd\u5df2\u6b7b\u5728\u4f60\u624b\u91cc\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u6fc0\u6211\u51fa\u624b\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u9762\u5bf9\u970e\u4e2d\u7684\u4eba\u5f71\uff0c\u5c45\u7136\u5b8c\u5168\u6ca1\u6709\u4e00\u70b9\u754f\u60e7\u4e4b\u610f\u3002 \u3000\u3000\u201c\u6211\u8bf4\u8fc7\uff0c\u5982\u679c\u5230\u4e86\u975e\u6b7b\u4e0d\u53ef\u7684\u65f6\u5019\uff0c\u8c01\u4e5f\u9003\u4e0d\u8fc7\u7684\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u542c\u6765\u5c45\u7136\u4e5f\u548c\u8427\u6cea\u8840 \u4e00\u6837\uff0c\u4e00\u6837\u51b7\u6de1\u800c\u9ad8\u50b2\uff1a\u201c\u53ef\u662f\u6211\u4e5f\u76f8\u4fe1\uff0c\u4f60\u81ea\u5df1\u6050\u6015\u4e5f\u672a\u5fc5\u6709\u628a\u63e1\u80fd\u65ad\u5b9a\uff0c\u4eca\u65e5\u7a76\u7adf\u662f\u8c01 \u8981\u6b7b\u5728\u8c01\u624b\u91cc\u3002\u201d \u3000\u3000\u6731\u731b\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u5c31\u597d\u50cf\u4ece\u6765\u90fd\u6ca1\u6709\u770b\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u4e00\u6837\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4ece\u6765\u90fd\u6ca1\u6709\u60f3\u5230\u5353\u4e1c\u6765\u6700\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e48\u9a84\u505a\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e5f\u4e0d\u77e5\u9053\u4e00\u4e2a\u4eba\u7684\u5185\u5fc3\u5982\u679c\u5145\u6ee1\u4e86\u81ea\u5351\uff0c\u5f80\u5f80\u5c31\u4f1a\u53d8\u6210\u4e00\u4e2a\u6700\u9a84\u50b2\u7684\u4eba\u3002 \u3000\u3000\u4f55\u51b5\u5353\u4e1c\u6765\u7684\u624b\u91cc\u8fd8\u6709\u201c\u6cea\u75d5\u201d\u3002 \u3000\u3000\u6709\u7684\u4eba\u76f8\u4fe1\u547d\u8fd0\uff0c\u6709\u7684\u4eba\u4e0d\u4fe1\u3002 \u3000\u3000\u53ef\u662f\u5927\u591a\u6570\u4eba\u90fd\u627f\u8ba4\uff0c\u51a5\u51a5\u4e2d\u786e\u5b9e\u884c\u4e00\u79cd\u51b7\u9177\u9762\u65e0\u60c5\u7684\u795e\u79d8\u529b\u91cf\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\u786e\u5b9e\u6709\u4e9b \u65e0\u6cd5\u89e3\u91ca\u7684\u4e8b\u7adf\u662f\u56e0\u4e3a\u8fd9\u79cd\u529b\u91cf\u800c\u53d1\u751f\u7684\u3002 \u3000\u3000\u2014\u2014\u5b9d\u5251\u521d\u51fa\uff0c\u5df2\u7ecf\u88ab\u795e\u9b3c\u5171\u5ac9\uff0c\u8981\u5c06\u94f8\u5251\u8005\u7684\u4e00\u4e2a\u4eb2\u4eba\u4f5c\u4e3a\u8fd9\u67c4\u5251\u7684\u796d\u793c\uff0c\u4e00\u5b9a\u8981\u7528 \u8fd9\u4e2a\u4eba\u7684\u9c9c\u8840\uff0c\u624d\u80fd\u6d17\u6389\u94f8\u5251\u8005\u6ef4\u843d\u5728\u5251\u4e0a\u7684\u6cea\u75d5\uff0c\u624d\u80fd\u5316\u53bb\u8fd9\u67c4\u5251\u7684\u66b4\u623e\u51f6\u715e\u4e4b\u6c14\u3002 \u3000\u3000\u94f8\u5251\u7684\u8427\u5927\u5e08\u65e0\u7591\u662f\u4e2a\u76f8\u4fe1\u547d\u8fd0\u7684\u4eba\uff0c\u6240\u4ee5\u4ed6\u624d\u4f1a\u5728\u5251\u4e0a\u6d41\u4e0b\u90a3\u70b9\u6cea\u75d5\u3002 \u3000\u3000\u8427\u6cea\u8840\u5462\uff1f \u3000\u3000\u4ed6\u76f8\u4fe1\u4e0d\u76f8\u4fe1\u5462\uff1f \u3000\u3000\u96fe\u4e2d\u7684\u4eba\u8fd8\u662f\u50cf\u96fe\u4e00\u822c\u4e0d\u53ef\u6349\u6f20\uff0c\u8c01\u4e5f\u731c\u4e0d\u51fa\u4ed6\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u5ffd\u7136\u95ee\u5c0f\u9ad8\uff1a\u201c\u9ad8\u6e10\u98de\uff0c\u4f60\u7684\u5251\u8fd8\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5728\u4e86\uff0c\u6211\u5df2\u7ecf\u6ca1\u6709\u5251\u3002\u201d\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u6ca1\u6709\uff0c\u4ed6\u6709\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4f60\u7684\u7075\u673a\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u4f60\u5931\u5374\u4f60\u7684\u5251\uff0c\u662f\u4f60\u7684\u8fd0\u6c14\uff0c\u4f60\u62d7\u65ad\u90a3\u67c4\u5251\uff0c\u662f\u4f60 \u7684\u7075\u673a\u3002\u201d \u3000\u3000\u201c\u7075\u673a\uff1f\u4e3a\u4ec0\u4e48\u662f\u6211\u7684\u7075\u673a\uff1f\u201d\u9ad8\u6e10\u98de\u8bf4\uff1a\u201c\u6211\u4e0d\u61c2\uff0c\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u53ea\u80af\u5c06\u6211\u7684\u7834\u5251\u4e4b\u672f\u4f20\u7ed9\u6ca1\u6709\u5251\u7684\u4eba\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u4f60\u7684\u624b\u91cc\u5982\u679c\u8fd8\u6709\u5251\uff0c \u5982\u679c\u4f60\u6ca1\u6709\u62d7\u65ad\u90a3\u67c4\u5251\uff0c\u6211\u4e5f\u4e0d\u80af\u4f20\u7ed9\u4f60\u3002\u201d \u3000\u3000\u201c\u4f20\u7ed9\u6211\u4ec0\u4e48\uff1f\u7834\u5251\u4e4b\u672f\uff0c\u201d\u5c0f\u9ad8\u8fd8\u662f\u4e0d\u61c2\uff0c\u201c\u4ec0\u4e48\u53eb\u7834\u5251\u4e4b\u672f\uff1f\u201d \u3000\u3000\u201c\u5929\u4e0b\u6ca1\u6709\u7834\u4e0d\u4e86\u7684\u5251\u6cd5\uff0c\u4e5f\u6ca1\u6709\u62c6\u4e0d\u65ad\u7684\u5251\uff0c\u66f4\u6ca1\u6709\u4e0d\u8d25\u7684\u5251\u5ba2\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u5982 \u679c\u4f60\u7528\u7684\u5175\u5668\u548c\u62db\u5f0f\u9002\u5f53\uff0c\u53ea\u8981\u9047\u5230\u4f7f\u5251\u7684\u4eba\uff0c\u4f60\u5c31\u80fd\u7834\u5176\u6cd5\u6298\u5176\u5251\u6740\u5176\u4eba\uff0c\u8fd9\u5c31\u53eb\u7834\u5251\u4e4b \u672f\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4eff\u4f5b\u4e5f\u5145\u6ee1\u4e00\u79cd\u795e\u79d8\u7684\u529b\u91cf\u3002 \u3000\u3000\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u6211\u5c06\u5929\u4e0b\u4f7f\u5251\u7684\u540d\u5bb6\u90fd\u89c6\u5982\u86c7\u874e\u731b\u517d\uff0c\u53ef\u662f\u73b0\u5728\uff0c\u6211\u5374\u5df2\u5c06\u4ed6\u4eec\u89c6\u5982\u7caa \u571f\u3002\u201d\u8427\u6cea\u8840\u8bf4\uff1a\u201c\u73b0\u5728\u4ed6\u4eec\u5728\u6211\u773c\u4e2d\u770b\u6765\uff0c\u90fd\u5df2\u4e0d\u582a\u4e00\u51fb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u95ee\u5c0f\u9ad8\uff1a\u201c\u9ad8\u6e10\u98de\uff0c\u4f60\u7684\u7075\u673a\u8fd8\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u8fd8\u5728\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u5353\u4e1c\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u53ef\u4ee5\u7b49\u4e00\u7b49\uff0c\u6211\u4e0d\u4f1a\u8ba9\u4ed6\u7b49\u591a\u4e45\u7684\u3002\u201d \u516b \u3000\u3000\u5353\u4e1c\u6765\u770b\u7740\u5c0f\u9ad8\u8d70\u8fc7\u53bb\uff0c\u975e\u4f46\u6ca1\u6709\u963b\u62e6\uff0c\u800c\u4e14\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\uff0c\u5c31\u597d\u50cf\u4ed6\u5f88\u613f\u610f\u7b49\uff0c \u7b49\u5c0f\u9ad8\u7ec3\u6210\u90a3\u79cd\u7834\u5251\u4e4b\u672f\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u4e00\u5b9a\u7ec3\u4e0d\u6210\u7684\uff0c\u5353\u4e1c\u6765\u544a\u8bc9\u81ea\u5df1\uff1a\u5c31\u7b97\u8427\u6cea\u8840\u771f\u7684\u6709\u7834\u5251\u4e4b\u672f\uff0c\u4e5f\u7edd\u4e0d\u662f\u77ed\u77ed\u7247 \u523b\u95f4\u5c31\u53ef\u4ee5\u7ec3\u5f97\u6210\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e4b\u95f4\u4e5f\u8bb8\u7684\u786e\u6709\u79cd\u795e\u79d8\u800c\u4e0d\u53ef\u89e3\u91ca\u7684\u5173\u7cfb\u5b58\u5728\uff0c\u80fd\u591f\u4f7f\u4ed6\u4eec\u7684\u5fc3\u7075\u6c9f \u901a\u3002 \u3000\u3000\u4e5f\u8bb8\u5c0f\u9ad8\u771f\u7684\u80fd\u7528\u90a3\u4e00\u70b9\u7075\u673a\u9886\u4f1a\u5230\u7834\u5251\u4e4b\u672f\u7684\u5965\u79d8\u3002 \u3000\u3000\u5353\u4e1c\u6765\u867d\u7136\u4e00\u76f4\u5728\u5b89\u6170\u81ea\u5df1\uff0c\u5fc3\u91cc\u5374\u8fd8\u662f\u611f\u5230\u6709\u4e00\u79cd\u5de8\u5927\u7684\u538b\u529b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5bf9\u8427\u6cea\u8840\u8fd9\u4e2a\u4eba\u4e00\u76f4\u90fd\u6709\u79cd\u65e0\u6cd5\u89e3\u91ca\u7684\u6050\u60e7\uff0c\u603b\u89c9\u5f97\u8fd9\u4e2a\u4eba\u597d\u50cf\u5929\u751f\u5c31\u6709\u4e00\u79cd\u80fd \u591f\u514b\u5236\u4ed6\u7684\u80fd\u529b\u2014\u2014\u4e00\u79cd\u5df2\u7ecf\u88ab\u8bf8\u795e\u8bf7\u9b54\u795d\u798f\u8bc5\u5492\u8fc7\u7684\u795e\u79d8\u80fd\u529b\uff0c\u4e00\u79cd\u53c8\u7384\u5999\u53c8\u90aa\u6076\u7684\u80fd \u529b\u3002 \u3000\u3000\u8427\u6cea\u8840\u5df2\u7ecf\u6253\u5f00\u4e86\u4ed6\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u5929\u5df2\u4eae\u4e86\uff0c\u65ed\u65e5\u521a\u521a\u5347\u8d77\uff0c\u4e1c\u65b9\u7684\u4e91\u5806\u4e2d\u521a\u521a\u6709\u4e00\u7ebf\u9633\u5149\u5c04\u51fa\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u53ea\u542c\u89c1\u201c\u683c\uff0c\u683c\uff0c\u683c\uff0c\u683c\u201d\u56db\u58f0\u54cd\uff0c\u8427\u6cea\u8840\u624b\u91cc\u5df2\u7ecf\u51fa\u73b0\u4e86\u4e00\u4ef6\u795e\u5947\u7684 \u6b66\u5668\u3002 \u3000\u3000\u81ea\u4e1c\u65b9\u7167\u5c04\u8fc7\u6765\u7684\u7b2c\u4e00\u7ebf\u9633\u5149\uff0c\u4e5f\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u521a\u547c\u7167\u5728\u8fd9\u4ef6\u6b66\u5668\u4e0a\uff0c\u4f7f\u5f97\u5b83\u5ffd\u7136\u95ee \u8d77\u4e00\u79cd\u53c8\u7384\u5999\u53c8\u90aa\u6076\u7684\u5149\u5f69\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u89c1\u8fc7\u8fd9\u79cd\u6b66\u5668\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u5b83\u7a76\u7adf\u6709\u4ec0\u4e48\u5de7\u5999\u4e4b\u5904\u3002 \u3000\u3000\u53ef\u662f\u6bcf\u4e2a\u770b\u5230\u5b83\u7684\u4eba\uff0c\u90fd\u4f1a\u611f\u89c9\u5230\u5b83\u90a3\u79cd\u5947\u5999\u9762\u90aa\u6076\u7684\u529b\u91cf\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7684\u773c\u775b\u91cc\u5ffd\u7136\u4e5f\u53d1\u51fa\u4e86\u5149\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5fc3\u91cc\u5ffd\u7136\u4e5f\u6709\u4e00\u70b9\u7075\u673a\u89e6\u53d1\uff0c\u5ffd\u7136\u95f4\u5c31\u5df2\u7ecf\u60f3\u5230\u4e86\u4e00\u4e2a\u5341\u62ff\u4e5d\u7a33\u7684 \u6cd5\u5b50\uff0c\u7edd\u5bf9\u53ef\u4ee5\u5728\u77ac\u606f\u95f4\u5c06\u9ad8\u6e10\u98de\u7f6e\u4e4b\u6b7b\u5730\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u4f53\u91cc\u5ffd\u7136\u95ee\u5c31\u5145\u6ee1\u4e86\u4fe1\u5fc3\u548c\u529b\u91cf\u3002\u4e00\u79cd\u4ed6\u4ece\u6765\u672a\u66fe\u6709\u8fc7\u7684\u5de8\u5927\u529b\u91cf\uff0c\u8fde\u4ed6\u8089\u5df1\u90fd \u88ab\u9707\u64bc\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u5c31\u597d\u50cf\u5ffd\u7136\u4e5f\u6709\u67d0\u79cd\u795e\u7075\u5e26\u7740\u5bf9\u751f\u547d\u7684\u8bc5\u5492\u964d\u4e34\u5230\u4ed6\u8eab\u4e0a\uff0c\u8981\u501f\u4ed6\u7684\u624b\uff0c\u628a\u4e00 \u4e2a\u4eba\u4ece\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5f7b\u5e95\u6d88\u706d\u3002 \u3000\u3000\u8fd9\u53e3\u7bb1\u5b50\u91cc\u672c\u6765\u5c31\u597d\u50cf\u9501\u7740\u4e2a\u52fe\u9b42\u593a\u547d\u7684\u6076\u9b3c\uff0c\u53ea\u8981\u7bb1\u5b50\u4e00\u5f00\uff0c\u5c31\u4e00\u5b9a\u6709\u4e00\u4e2a\u4eba\u7684\u6027\u547d \u4f1a\u88ab\u593a\u8d70\uff0c\u4e5f\u88ab\u9501\u5165\u8fd9\u53e3\u7bb1\u5b50\u91cc\uff0c\u4e07\u52ab\u4e0d\u590d\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4e00\u5411\u4e0d\u4fe1\u795e\u9b3c\u4ed9\u4f5b\uff0c\u53ef\u662f\u4ed6\u76f8\u4fe1\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u6b63\u5982\u4ed6\u76f8\u4fe1\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u786e\u6709\u67d0\u79cd\u4eba \u7c7b\u65e0\u6cd5\u89e3\u91ca\u7684\u529b\u91cf\u5b58\u5728\u3002 \u3000\u3000\u56e0\u4e3a\u73b0\u5728\u4ed6\u81ea\u5df1\u4e5f\u5df2\u7ecf\u611f\u89c9\u5230\u8fd9\u79cd\u529b\u91cf\u3002 \u3000\u3000\u8427\u6cea\u8840\u5df2\u7ecf\u628a\u624b\u91cc\u7684\u6b66\u5668\u4ea4\u7ed9\u4e86\u5c0f\u9ad8\u3002 \u3000\u3000\u201c\u73b0\u5728\u4f60\u4e0d\u59a8\u53bb\u5427\uff0c\u53bb\u628a\u5353\u5148\u751f\u7684\u547d\u5e26\u56de\u6765\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u8fd9\u4ef6\u6b66\u5668\u81f3\u4eca\u8fd8\u6ca1\u6709\u5728\u4e16\u4e0a\u51fa \u73b0\u8fc7\uff0c\u4ee5\u540e\u6050\u6015\u4e5f\u4e0d\u4f1a\u518d\u51fa\u73b0\u4e86\u3002\u201d \u3000\u3000\u8427\u6cea\u8840\u7684\u58f0\u97f3\u4e5f\u50cf\u662f\u6765\u81ea\u5e7d\u51a5\u7684\u6076\u5492\uff1a\u201c\u56e0\u4e3a\u4e0a\u5929\u8981\u6211\u521b\u51fa\u8fd9\u4ef6\u6b66\u5668\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u7528\u5b83 \u6765\u5bf9\u4ed8\u5353\u5148\u751f\u7684\uff0c\u5b83\u51fa\u73b0\u7684\u65f6\u5019\uff0c\u5c31\u662f\u5353\u5148\u751f\u7684\u6b7b\u5f7b\uff0c\u4e0d\u7ba1\u5b83\u5728\u8c01\u7684\u624b\u91cc\u90fd\u4e00\u6837\uff0c\u90fd\u4e00\u6837\u80fd \u8981\u4ed6\u7684\u547d\u3002\u201d \u4e5d \u3000\u3000\u5bc6\u5bc6\u7684\u4e91\u5c42\u53c8\u906e\u4f4f\u4e86\u9633\u5149\uff0c\u8fde\u706f\u5149\u4e5f\u5df2\u5ab3\u706d\uff0c\u5929\u8272\u9634\u6c89\uff0c\u6740\u673a\u5df2\u52a8\uff0c\u8fd9\u79cd\u9b3c\u90fd\u65e0\u6cd5\u633d \u56de\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u5df2\u98de\u9e1f\u822c\u63a0\u8fc7\u6765\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7684\u773c\u775b\u9525\u5b50\u822c\u76ef\u7740\u4ed6\u624b\u91cc\u7684\u6b66\u5668\uff0c\u5ffd\u7136\u628a\u624b\u91cc\u7684\u201c\u6cea\u75d5\u201d\u5411\u5c0f\u9ad8\u63b7\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4f60\u7684\u5251\uff0c\u6211\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u60f3\u5f97\u5230\u4ed6\u8fd9\u4e00\u7740\uff0c\u5c0f\u9ad8\u4e5f\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u5df2\u8ddf\u968f\u4ed6\u591a\u5e74\uff0c\u59cb\u7ec8\u90fd\u5728\u4ed6\u8eab\u8fb9\uff0c\u5df2\u7ecf\u53d8\u6210\u4ed6\u751f\u547d\u4e2d\u6781\u91cd\u8981\u7684\u4e00\u90e8\u4efd\uff0c\u751a\u81f3\u53ef\u4ee5 \u8bf4\u5df2\u7ecf\u53d8\u6210\u4ed6\u8eab\u4f53\u7684\u4e00\u90e8\u4efd\uff0c\u5df2\u7ecf\u548c\u4ed6\u7684\u9aa8\u8089\u8840\u8109\u7ed3\u6210\u4e00\u4f53\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u8fde\u60f3\u90fd\u6ca1\u6709\u60f3\uff0c\u5c31\u63a5\u4e0b\u4e86\u8fd9\u67c4\u5251\u2014\u2014\u7528\u4ed6\u63e1\u5251\u7684\u624b\u63a5\u4e0b\u4e86\u8fd9\u67c4\u5251\uff0c\u5c31\u867e\u50cf\u5df2\u7ecf\u5b8c \u5168\u5fd8\u8bb0\u4ed6\u8fd9\u53ea\u624b\u91cc\u672c\u6765\u5df2\u7ecf\u63e1\u4f4f\u4e86\u4e00\u4ef6\u7834\u5251\u7684\u6b66\u5668\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u597d\u50cf\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u601d\u60f3\uff0c\u5b8c\u5168\u4e0d\u80fd\u63a7\u5236\u81ea\u5df1\u3002 \u3000\u3000\u56e0\u4e3a\u4e00\u4e2a\u6709\u7406\u6027\u7684\u4eba\u53ea\u6709\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u624d\u4f1a\u505a\u51fa\u8fd9\u4e48\u611a\u8822\u7684\u4e8b\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7b11\u4e86\u3002 \u3000\u3000\u73b0\u5728\u5c0f\u9ad8\u53c8\u6709\u4e86\u5251\uff0c\u53ef\u662f\u7834\u5251\u7684\u6b66\u5668\u5374\u5df2\u7ecf\u88ab\u4ed6\u593a\u5728\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u667a\u6167\u6781\u9ad8\u7684\u4eba\uff0c\u773c\u775b\u4e5f\u6bd4\u522b\u4eba\u5229\uff0c\u8427\u6cea\u8840\u8bf4\u7684\u8bdd\u53c8\u5927\u591a\u4e86\u4e00\u70b9\uff0c\u8ba9\u4ed6\u6709\u8db3\u591f\u7684\u65f6 \u95f4\u628a\u8fd9\u4ef6\u5f62\u5f0f\u6784\u9020\u90fd\u6781\u5947\u7279\u7684\u6b66\u5668\u770b\u5f97\u5f88\u6e05\u695a\u3002\u800c\u4e14\u5df2\u7ecf\u770b\u51fa\u4e86\u8fd9\u4ef6\u6b66\u5668\u786e\u5b9e\u6709\u5f88\u591a\u5730\u65b9\u53ef \u4ee5\u514b\u5236\u4f4f\u5bf9\u65b9\u7684\u5251\uff0c\u751a\u81f3\u5df2\u7ecf\u770b\u51fa\u4e86\u8fd0\u7528\u5b83\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u65e0\u8bba\u4ed6\u7684\u5bf9\u624b\u662f\u8c01\u90fd\u4e00\u6837\u3002 \u3000\u3000\u53ea\u6709\u8427\u6cea\u8840\u8fd9\u6837\u7684\u4eba\u624d\u80fd\u521b\u51fa\u8fd9\u6837\u7684\u6b66\u5668\uff0c\u53ea\u6709\u5353\u4e1c\u6765\u8fd9\u6837\u7684\u4eba\u624d\u80fd\u628a\u8fd9\u4e48\u6837\u4e00\u4ef6\u4e8b\u505a \u5f97\u8fd9\u4e48\u7edd\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u770b\u6765\u5b8c\u5168\u4e0d\u540c\u7684\u4eba\uff0c\u5728\u67d0\u4e9b\u65b9\u9762\u610f\u89c1\u5374\u5b8c\u5168\u76f8\u540c\uff0c\u5c31\u8fde\u601d\u60f3\u90fd\u4eff\u4f5b\u80fd\u4e92\u76f8\u6c9f\u901a\u3002 \u3000\u3000\u6731\u731b\u7684\u8138\u8272\u60e8\u53d8\u3002 \u3000\u3000\u4ed6\u60f3\u4e0b\u5230\u5c0f\u9ad8\u4f1a\u505a\u51fa\u8fd9\u4e48\u7b28\u7684\u4e8b\uff0c\u4ee5\u540e\u7684\u53d8\u5316\u5374\u8ba9\u4ed6\u66f4\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u5ffd\u7136\u53c8\u98de\u9e1f\u822c\u98de\u63a0\u800c\u8d77\uff0c\u6296\u8d77\u4e86\u4e00\u56e2\u5251\u82b1\uff0c\u5411\u5353\u4e1c\u6765\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4e0d\u8be5\u5148\u51fa\u624b\u7684\uff0c\u53ef\u662f\u4ed6\u4e00\u5b9a\u8981\u5728\u5353\u4e1c\u6765\u8fd8\u6ca1\u6709\u6478\u6e05\u8fd9\u4ef6\u6b66\u5668\u7684\u6784\u9020\u548c\u6548\u7528\u65f6\u53d6\u5f97 \u5148\u673a\u3002 \u3000\u3000\u4ed6\u65e0\u7591\u4e5f\u4f4e\u4f30\u4e86\u5353\u4e1c\u6765\u7684\u667a\u6167\u548c\u670d\u529b\u3002 \u3000\u3000\u8000\u773c\u7684\u5251\u5149\u4e2d\u9632\u4f5b\u80b2\u65e0\u6570\u5251\u5f71\u95ea\u52a8\uff0c\u53ef\u662f\u5251\u53ea\u6709\u4e00\u67c4\u3002 \u3000\u3000\u8fd9\u65e0\u6570\u9053\u5251\u5f71\u4e2d\uff0c\u5f53\u7136\u53ea\u6709\u4e00\u62db\u662f\u5b9e\u3002 \u3000\u3000\u5353\u4e1c\u6765\u4e00\u773c\u5c31\u770b\u51fa\u4e86\u54ea\u4e00\u62db\u662f\u5b9e\u62db\uff0c\u5bf9\u8fd9\u79cd\u4ee5\u865a\u62db\u63a9\u62a4\u5b9e\u62db\u7684\u653b\u51fb\u6280\u672f\uff0c\u4ed6\u8fdc\u6bd4\u4e16\u4e0a\u5927 \u591a\u6570\u4eba\u90fd\u4e86\u89e3\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u4e5f\u770b\u51fa\u4e86\u8fd9\u4ef6\u6b66\u5668\u4e0a\u6700\u5c11\u6709\u56db\u4e94\u4e2a\u90e8\u4efd\u7684\u7ed3\u6784\uff0c\u90fd\u53ef\u4ee5\u628a\u5bf9\u65b9\u7684\u5251\u52bf\u5c01\u9501\uff0c\u751a\u81f3\u53ef\u4ee5 \u4e58\u52bf\u628a\u5bf9\u65b9\u7684\u5251\u593a\u4e0b\u6765\uff0c\u7136\u540e\u518d\u8fdb\u51fb\u65f6\u5c31\u662f\u81f4\u547d\u7684\u4e00\u51fb\u4e86\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5e76\u4e0d\u60f3\u505a\u5f97\u8fd9\u4e48\u7edd\u3002 \u3000\u3000\u5bf9\u4e8e\u8fd0\u7528\u8fd9\u4ef6\u6b66\u5668\u7684\u6280\u5de7\uff0c\u4ed6\u8fd8\u4e0d\u7eaf\u719f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5148\u501f\u5c0f\u9ad8\u7684\u5251\u6765\u7ec3\u4e60\u7ec3\u4e60\uff1f \u3000\u3000\u4ed6\u5df2\u7ecf\u6709\u7edd\u5bf9\u7684\u628a\u63e1\uff0c\u53ef\u4ee5\u968f\u65f6\u8981\u5c0f\u9ad8\u7684\u547d\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e00\u70b9\u90fd\u4e0d\u6025\u3002 \u3000\u3000\u5c0f\u9ad8\u7684\u5251\u523a\u6765\uff0c\u4ed6\u4e5f\u628a\u638c\u4e2d\u7684\u6b66\u5668\u8fce\u4e0a\u53bb\uff0c\u8bd5\u63a2\u7740\u7528\u4e0a\u9762\u7684\u4e00\u4e2a\u94a9\u73af\u53bb\u9501\u5c0f\u9ad8\u7684\u5251\u3002 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u5251\u4e0e\u94a9\u76f8\u51fb\uff0c\u8fd9\u4ef6\u6b66\u5668\u7adf\u7a81\u7136\u53d1\u51fa\u4e86\u4efb\u4f55\u4eba\u90fd\u6599\u60f3\u4e0d\u5230\u7684\u5999\u7528\uff0c\u7a81\u7136\u7adf \u6709\u4e00\u90e8\u4efd\u7ed3\u6784\u5f39\u51fa\uff0c\u548c\u8fd9\u4e2a\u73af\u94a9\u914d\u5408\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u94b3\u5b50\u4e00\u6837\uff0c\u4e00\u4e0b\u5b50\u5c31\u628a\u5c0f\u9ad8\u7684\u5251\u94b3\u4f4f\u3002 \u3000\u3000\u5353\u4e1c\u6765\u53c8\u60ca\u53c8\u559c\uff0c\u4ed6\u5b9e\u5728\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4ef6\u6b66\u5668\u7adf\u6709\u8fd9\u4e48\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u8ba9\u4ed6\u66f4\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u5c0f\u9ad8\u7684\u8fd9\u67c4\u5251\u7adf\u7136\u53c8\u4ece\u8fd9\u4ef6\u6b66\u5668\u4e2d\u7a7f\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u672c\u6765\u662f\u7edd\u5bf9\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002 \u3000\u3000\u6784\u9020\u8fd9\u4e48\u590d\u6742\u5de7\u5999\u7684\u6b66\u5668\uff0c\u600e\u4e48\u53ef\u80fd\u8ba9\u5bf9\u65b9\u7684\u5251\u4ece\u4e2d\u95f4\u7a7f\u8fc7\u6765\uff1f \u3000\u3000\u96be\u9053\u8fd9\u4ef6\u6b66\u5668\u7684\u7ed3\u6784\uff0c\u672c\u6765\u5c31\u6545\u610f\u56fe\u4e0b\u4e86\u4e00\u4e2a\u521a\u597d\u53ef\u4ee5\u8ba9\u4e00\u67c4\u5251\u7a7f\u8fc7\u53bb\u7684\u7a7a\u9699\uff1f\u5c0f\u9ad8\u6545 \u610f\u8ba9\u81ea\u5df1\u7684\u5251\u88ab\u9501\u4f4f\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u5229\u7528\u8fd9\u81f4\u547d\u7684\u4e00\u7740\uff1f \u3000\u3000\u5353\u4e1c\u6765\u5df2\u7ecf\u4e0d\u80fd\u53bb\u60f3\u8fd9\u4ef6\u4e8b\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u822c\u7684\u4e00\u5239\u90a3\u95ee\uff0c\u5c0f\u9ad8\u7684\u5251\u5df1\u523a\u5165\u4e86\u4ed6\u7684\u5fc3\u53e3\uff0c\u53ea\u523a\u5165\u4e86\u4e00\u5bf8\u4e03\u5206\uff0c\u56e0\u4e3a \u8fd9\u67c4\u5251\u53ea\u6709\u8fd9\u4e48\u957f\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e48\u957f\u5c31\u5df2\u8db3\u591f\uff0c\u4e00\u5bf8\u4e03\u5206\u521a\u597d\u5df2\u7ecf\u8fbe\u5230\u53ef\u4ee5\u81f4\u547d\u7684\u6df1\u5ea6\uff0c\u521a\u597d\u523a\u5165\u4e86\u5353\u4e1c\u6765\u7684\u5fc3 \u810f\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4ef6\u6b66\u5668\u672c\u6765\u5c31\u662f\u7279\u5730\u521b\u51fa\u6765\u5bf9\u4ed8\u5353\u4e1c\u6765\u7684\u3002 \u3000\u3000\u2014\u2014\u56e0\u4e3a\u53ea\u6709\u5353\u4e1c\u6765\u624d\u80fd\u5728\u90a3\u7247\u523b\u95f4\u770b\u51fa\u8fd9\u4ef6\u6b66\u5668\u7684\u6784\u9020\uff0c\u53ea\u6709\u5353\u4e1c\u6765\u624d\u4f1a\u7528\u81ea\u5df1\u638c\u4e2d \u7684\u5251\u53bb\u6362\u8fd9\u4ef6\u6b66\u5668\uff0c\u522b\u7684\u4eba\u975e\u4f46\u505a\u4e0d\u5230\uff0c\u8fde\u60f3\u90fd\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u2014\u2014\u4e0d\u5e78\u7684\u662f\uff0c\u5353\u4e1c\u6765\u80fd\u60f3\u5230\u7684\uff0c\u8427\u6cea\u8840\u4e5f\u5168\u90fd\u5148\u66ff\u4ed6\u60f3\u5230\u4e86\uff0c\u800c\u4e14\u65e9\u5df2\u7b97\u51c6\u4e86\u4ed6\u4f1a\u8fd9 \u4e48\u505a\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4ef6\u6b66\u5668\u672c\u6765\u5c31\u662f\u8427\u6cea\u8840\u7279\u5730\u5e03\u7f6e\u4e0b\u7684\u9677\u8bb2\uff0c\u7b49\u7740\u5353\u4e1c\u6765\u81ea\u5df1\u4e00\u811a\u8e0f\u8fdb\u53bb\u3002 \u3000\u3000\u73b0\u5728\u5353\u4e1c\u6765\u7ec8\u4e8e\u660e\u767d\u4e86\u3002 \u3000\u3000\u201c\u8427\u6cea\u8840\uff0c\u8427\u5148\u751f\uff0c\u6211\u679c\u7136\u6ca1\u6709\u770b\u9519\uff0c\u4f60\u679c\u7136\u662f\u6211\u7684\u51f6\u715e\uff0c\u6211\u65e9\u5c31\u7b97\u63a8\u6211\u8fdf\u65e9\u8981\u6b7b\u4e8e\u4f60 \u624b\u3002\u201d\u4ed6\u60e8\u7136\u9053\uff1a\u201c\u5426\u5219\u6211\u600e\u4e48\u4f1a\u4e0a\u4f60\u8fd9\u4e2a\u5f53\uff1f\u201d \u3000\u3000\u8427\u6cea\u8840\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff1a\u201c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\u6211\u8bf4\u8fc7\uff0c\u65e0\u8bba\u8fd9\u4ef6\u6b66\u5668\u5728\u8c01\u624b\u91cc\uff0c\u90fd\u53ef\u4ee5\u81f4\u4f60\u4e8e\u6b7b \u5730\uff0c\u5c31\u7b97\u5728\u4f60\u81ea\u5df1\u624b\u91cc\u4e5f\u4e00\u6837\uff01\u201d\u4ed6\u7684\u58f0\u97f3\u66f4\u51b7\u6f20\u3002\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u8bf4\u7684\u4e00\u5411\u90fd\u662f\u5b9e\u6d3b\u3002\u201d \u3000\u3000\u5353\u4e1c\u6765\u60e8\u7b11\u3002 \u3000\u3000\u4ed6\u7684\u7b11\u9707\u52a8\u4e86\u4ed6\u7684\u5fc3\u8109\uff0c\u4e5f\u9707\u52a8\u4e86\u5251\u950b\uff0c\u4ed6\u5ffd\u7136\u53c8\u89c9\u5f97\u5fc3\u5934\u4e00\u9635\u523a\u75db\uff0c\u56e0\u4e3a\u5251\u950b\u53c8\u523a\u6df1 \u4e86\u4e00\u5206\uff0c\u4ed6\u7684\u751f\u547d\u8ddd\u79bb\u6b7b\u4ea1\u4e5f\u53ea\u6709\u4e00\u7ebf\u4e86\u3002 \u3000\u3000\u5c0f\u9ad8\u8f7b\u8f7b\u7684\u628a\u8fd9\u67c4\u5251\u62d4\u4e86\u51fa\u6765\uff0c\u90a3\u4ef6\u6b66\u5668\u4e5f\u8f7b\u8f7b\u7684\u4ece\u5251\u4e0a\u6ed1\u843d\u3002 \u3000\u3000\u4e91\u5c42\u5ffd\u53c8\u518d\u5f00\uff0c\u9633\u5149\u53c8\u7a7f\u4e91\u800c\u51fa\uff0c\u521a\u597d\u7167\u5728\u8fd9\u67c4\u5251\u4e0a\u3002 \u3000\u3000\u5353\u4e1c\u6765\u770b\u7740\u8fd9\u67c4\u5251\uff0c\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u6050\u6016\u4e4b\u6781\u7684\u8868\u60c5\u3002 \u3000\u3000\u201c\u6cea\u75d5\u5462\uff1f\u201d\u4ed6\u5636\u58f0\u5411\uff0c\u201c\u5251\u4e0a\u7684\u6cea\u75d5\u600e\u4e48\u4e0d\u89c1\u4e86?\u96be\u9053\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u51fa\u8fd9\u4e2a\u8ba9\u4ed6\u6b7b\u4e5f\u4e0d\u80fd\u7791\u76ee\u7684\u95ee\u9898\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u4ed6\u4e5f\u662f\u8427\u5927\u5e08\u7684\u4eb2\u4eba\uff0c\u96be\u9053\u4ed6\u90a3\u4e2a\u4ece\u672a\u89c1\u8fc7\u9762\u7684\u7236\u4eb2\u5c31\u662f\u8427\u5927\u5e08\uff1f\u6240\u4ee5\u4ed6\u4e00\u6b7b \u5728\u5251\u4e0b\uff0c\u6cea\u75d5\u4e5f\u540c\u65f6\u6d88\u5931\uff1f \u3000\u3000\u2014\u2014\u6291\u6216\u662f\u9b3c\u795e\u4e4b\u8bf4\u6bd5\u7adf\u4e0d\u53ef\u4fe1\uff0c\u5251\u4e0a\u8fd9\u4e00\u70b9\u6cea\u75d5\u5ffd\u7136\u6d88\u5931\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6b64\u523b\u521a\u597d\u5230\u4e86 \u5b83\u5e94\u8be5\u6d88\u5931\u7684\u65f6\u5019\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u8fd9\u95ee\u9898\uff0c\u4e5f\u8bb8\u90a3\u4ead\u4e2d\u7684\u8001\u4eba\u672c\u6765\u53ef\u4ee5\u56de\u7b54\u7684\uff0c\u53ea\u53ef\u60dc\u8001\u4eba\u5df2\u6b7b\u5728\u5353\u4e1c\u6765\u624b \u91cc\u3002 \u3000\u3000\u8427\u6cea\u8840\u8981\u53bb\u95ee\u8fd9\u4e2a\u8001\u4eba\u7684\uff0c\u4e5f\u8bb8\u5c31\u662f\u8fd9\u4ef6\u4e8b\uff0c\u5982\u679c\u8001\u4eba\u5c06\u7b54\u6848\u544a\u8bc9\u4e86\u4ed6\uff0c\u4ed6\u4e5f\u8bb8\u5c31\u4e0d\u4f1a \u5c06\u5353\u4e1c\u6765\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u53ef\u60dc\u73b0\u5728\u4e00\u5207\u90fd\u5df2\u5927\u8fdf\u4e86\u3002 \u3000\u3000\u5353\u4e1c\u6765\u7684\u5fc3\u8109\u5df2\u65ad\uff0c\u81f3\u6b7b\u90fd\u4e0d\u660e\u767d\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u8fd9\u6837\u7684\u7ed3\u5c40\uff0c\u5c82\u975e\u4e5f\u662f\u4ed6\u81ea\u5df1\u9020\u6210\u7684\uff1f \u5341 \u3000\u3000\u5728\u9633\u5149\u4e0b\u770b\u6765\uff0c\u5251\u8272\u6f84\u6e05\u5982\u79cb\u6c34\uff0c\u5251\u4e0a\u7684\u6cea\u75d5\u679c\u7136\u5df2\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u75f4\u75f4\u7684\u770b\u7740\u8fd9\u67c4\u5251\uff0c\u5fc3\u91cc\u4e5f\u5728\u60f3\u7740\u8fd9\u4e9b\u4e8b\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u660e\u767d\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u624d\u60f3\u5230\u8981\u53bb\u95ee\u8427\u6cea\u8840\u3002 \u3000\u3000\u8427\u6cea\u8840\u5374\u4e0d\u5728\uff0c\u5353\u4e1c\u6765\u7684\u5c38\u4f53\u548c\u90a3\u4ef6\u6b66\u5668\u4e5f\u5df2\u4e0d\u5728\u3002 \u3000\u3000\u6731\u731b\u544a\u8bc9\u5c0f\u9ad8\uff1a\u201c\u8427\u5148\u751f\u5df2\u7ecf\u8d70\u4e86\uff0c\u5e26\u7740\u5353\u4e1c\u6765\u4e00\u8d77\u8d70\u7684\u3002\u201d\u4ed6\u5fc3\u91cc\u65e0\u7591\u4e5f\u5145\u6ee1\u9707\u60ca\u548c \u7591\u60d1\u3002\u201d\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u9065\u671b\u7740\u8fdc\u65b9\uff0c\u8fdc\u65b9\u662f\u4e00\u7247\u6674\u7a7a\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u73b0\u5728\u90fd\u5df2\u7ecf\u6ca1\u5173\u7cfb\u4e86\u3002\u201d\u5c0f\u9ad8\u60a0\u60a0\u7684\u8bf4\uff1a\u201c\u4ece\u4eca\u800c\u540e\uff0c\u6211\u4eec\u5927\u6982 \u4e5f\u4e0d\u4f1a\u518d\u89c1\u5230\u8427\u5148\u751f\u3002\u201d \u3000\u3000\u706f\u5149\u5df2\u706d\uff0c\u63d0\u706f\u7684\u4eba\u4e5f\u5df2\u6563\u53bb\uff0c\u53ea\u5269\u4e0b\u90a3\u4e2a\u778e\u4e86\u773c\u7684\u5c0f\u5973\u5b69\u8fd8\u62b1\u7740\u7435\u7436\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u9633\u5149\u867d\u7136\u5df2\u666e\u7167\u5927\u5730\uff0c\u53ef\u662f\u5979\u773c\u524d\u5374\u4ecd\u7136\u8fd8\u662f\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u9ad8\u6e10\u98de\u5fc3\u91cc\u5ffd\u7136\u53c8\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u611f\u4f24\uff0c\u5fcd\u4e0d\u4f4f\u8d70\u8fc7\u53bb\u95ee\u8fd9\u4e2a\u5c0f\u5973\u5b69\uff1a \u3000\u3000\u201c\u4f60\u7237\u7237\u5462\uff1f\u4f60\u7237\u7237\u8fd8\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u5979\u82cd\u767d\u7684\u8138\u4e0a\u5b8c\u5168\u662f\u4e00\u7247\u7a7a\u767d\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u8fde\u60b2\u4f24\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u53ef\u662f\u65e0\u8bba\u8c01\u770b\u5230\u5979\u5fc3\u91cc\u90fd\u4f1a\u88ab\u523a\u75db\u7684\u3002 \u3000\u3000\u201c\u4f60\u7684\u5bb6\u5728\u54ea\u91cc\uff1f\u201d\u5c0f\u9ad8\u53c8\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u5bb6\uff1f\u5bb6\u91cc\u8fd8\u6709\u6ca1\u6709\u522b\u7684\u4eb2\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u5973\u5b69\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff0c\u5374\u7d27\u7d27\u7684\u62b1\u4f4f\u4e86\u5979\u7684\u7435\u7436\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u6eba\u6c34\u7684\u4eba\u62b1\u4f4f\u4e86\u4e00\u6839\u6d6e \u6728\u4e00\u6837\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u5979\u8fd9\u4e00\u751f\u4e2d\u552f\u4e00\u771f\u6b63\u5c5e\u4e8e\u5979\u6240\u6709\u7684\u5c31\u662f\u8fd9\u628a\u7435\u7436\uff1f \u3000\u3000\u201c\u73b0\u5728\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u5c0f\u9ad8\u95ee\uff1a\u201c\u4ee5\u540e\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u95ee\u51fa\u4e86\u8fd9\u53e5\u6d3b\uff0c\u4ed6\u5c31\u5df2\u7ecf\u5728\u540e\u6094\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u4ed6\u5b9e\u5728\u4e0d\u8be5\u95ee\u7684\uff0c\u4e00\u4e2a\u65e0\u4eb2\u65e0\u6545\u65e0\u4f9d\u65e0\u9760\u7684\u5c0f\u5973\u5b69\uff0c\u600e\u4e48\u4f1a\u60f3\u5230\u4ee5\u540e\u7684\u4e8b\uff1f \u3000\u3000\u5979\u600e\u4e48\u80fd\u53bb\u60f3\uff1f\u600e\u4e48\u6562\u53bb\u60f3\uff1f\u4f60\u8ba9\u5979\u600e\u4e48\u95ee\u7b54\uff1f \u3000\u3000\u60f3\u4e0d\u5230\u8fd9\u4e2a\u6c38\u8fdc\u53ea\u80fd\u6d3b\u5728\u9ed1\u6697\u4e2d\u7684\u5c0f\u5973\u5b69\uff0c\u5374\u5ffd\u7136\u7528\u4e00\u79cd\u5f88\u660e\u4eae\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u4ee5\u540e\u6211\u8fd8 \u8981\u5531\u3002\u201d\u5979\u8bf4\uff1a\u201c\u6211\u8981\u4e00\u76f4\u5531\u4e0b\u53bb\uff0c\u5531\u5230\u6211\u6b7b\u7684\u65f6\u5019\u4e3a\u6b62\u3002\u201d \u5341\u4e00 \u3000\u3000\u9ed8\u9ed8\u7684\u770b\u7740\u88ab\u4ed6\u4eec\u9001\u56de\u6765\u7684\u5c0f\u5973\u5b69\u62b1\u7740\u7435\u7436\u8d70\u8fdb\u4e86\u957f\u5b89\u5c45\uff0c\u5c0f\u9ad8\u548c\u6731\u731b\u7684\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f \u4ec0\u4e48\u6ecb\u5473\uff1f \u3000\u3000\u201c\u6211\u76f8\u4fe1\u5979\u4e00\u5b9a\u4f1a\u5531\u4e0b\u53bb\u7684\u3002\u201d\u6731\u731b\u8bf4\uff1a\u201c\u53ea\u8981\u5979\u4e0d\u6b7b\uff0c\u5c31\u4e00\u5b9a\u4f1a\u5531\u4e0b \u3000\u3000\u201c\u6211\u4e5f\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5c0f\u9ad8\u8bf4\uff1a\u201c\u6211\u4e5f\u76f8\u4fe1\u5982\u679c\u6709\u4eba\u4e0d\u8ba9\u5979\u5531\u4e0b\u53bb\uff0c\u5979\u5c31\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u56e0\u4e3a\u5979\u662f\u6b4c\u8005\uff0c\u6240\u4ee5\u5979\u8981\u5531\uff0c\u5531\u7ed9\u522b\u4eba\u542c\u3002\u7eb5\u7136\u5979\u5531\u5f97\u603b\u662f\u90a3\u4e48\u60b2\u4f24\uff0c\u603b\u662f\u4f1a\u8ba9\u4eba\u6d41 \u6cea\uff0e\u53ef\u662f\u4e00\u4e2a\u4eba\u5982\u679c\u4e0d\u77e5\u9053\u60b2\u4f24\u7684\u6ecb\u5473\u53c8\u600e\u4e48\u4f1a\u4e86\u89e3\u6b22\u4e50\u7684\u771f\u8c1b\uff1f\u53c8\u600e\u4e48\u4f1a\u5bf9\u751f\u547d\u73cd\u60dc\uff1f \u3000\u3000\u6240\u4ee5\u5979\u867d\u7136\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u8fd8\u662f\u4f1a\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u5982\u679c\u5979\u4e0d\u80fd\u5531\u4e86\uff0c\u5979\u7684\u751f\u547d\u5c31\u4f1a\u53d8\u5f97\u6beb\u65e0\u610f\u4e49\u3002 \u3000\u3000\u201c\u6211\u4eec\u5462\uff1f\u201d \u3000\u3000\u6731\u731b\u5ffd\u7136\u95ee\u5c0f\u9ad8\uff1a\u201c\u6211\u4eec\u4ee5\u540e\u5e94\u8be5\u600e\u4e48\u6837\u505a\uff1f\u201d \u3000\u3000\u5c0f\u9ad8\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u8fd8\u6ca1\u6709\u60f3\u51fa\u5e94\u8be5\u600e\u4e48\u6837\u56de\u7b54\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5ffd\u7136\u770b\u89c1\u4e86\u9633\u5149\u7684\u707f\u70c2\uff0c\u5927\u5730\u7684\u8f89\u714c\u3002 \u3000\u3000\u201c\u6211\u4eec\u5f53\u7136\u4e5f\u8981\u5531\u4e0b\u53bb\u3002\u201d\u9ad8\u6e10\u98de\u5ffd\u7136\u633a\u8d77\u80f8\u819b\u5927\u58f0\u8bf4\uff1a\u201c\u867d\u7136\u6211\u4eec\u5531\u7684\u8ddf\u5979\u4e0d\u540c\uff0c\u53ef \u662f\u6211\u4eec\u4e00\u5b9a\u4e5f\u8981\u5531\u4e0b\u53bb\uff0c\u4e00\u76f4\u5531\u5230\u6b7b\u3002\u201d \u3000\u3000\u6b4c\u5973\u7684\u6b47\uff0c\u821e\u8005\u7684\u821e\uff0c\u5251\u5ba2\u7684\u5251\uff0c\u6587\u4eba\u7684\u7b14\uff0c\u82f1\u96c4\u7684\u6597\u5fd7\uff0c\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u53ea\u8981\u662f\u4e0d \u6b7b\uff0c\u5c31\u4e0d\u80fd\u653e\u5f03\u3002 \u3000\u3000\u671d\u9633\u521d\u5347\uff0c\u6625\u96ea\u5df2\u6eb6\uff0c\u4e00\u4e2a\u4eba\u63d0\u7740\u4e00\u53e3\u7bb1\u5b50\uff0c\u9ed8\u9ed8\u7684\u79bb\u5f00\u4e86\u957f\u5b89\u53e4\u57ce\u3002 \u3000\u3000\u4e00\u4e2a\u6c89\u9ed8\u5e73\u51e1\u7684\u4eba\uff0c\u4e00\u53e3\u9648\u65e7\u5e73\u51e1\u7684\u7bb1\u5b50\u3002 \uff08\u300a\u82f1\u96c4\u65e0\u6cea\u300b\u5b8c\uff09"}),showOnHomepage:!0}]}}]);